"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[28397],{92988:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u60c5\u5251\u65e0\u5203",content:(0,t.jsx)(t.Fragment,{children:"\u7b2c \u4e00 \u56de\u3000\u9ad8\u4eba\u6d1e\u4ed9 \u3000\u3000\u4e4c\u4e91\u853d\u65e5\u2014\u2014 \u3000\u3000\u867d\u662f\u767d\u5929\uff0c\u5374\u5927\u5730\u4e00\u7247\u65e0\u5149\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u5904\u70df\u4e91\u6c24\u6c32\u7684\u832b\u832b\u5c71\u91ce\u3002 \u3000\u3000\u5728\u9ad8\u53ef\u63d2\u5929\u7684\u72ec\u79c0\u5cf0\u4e0b\uff0c\u6b63\u5954\u9a70\u7740\u4e24\u6761\u4eba\u5f71\u3002 \u3000\u3000\u90a3\u4e24\u6761\u4eba\u5f71\uff0c\u5954\u884c\u4e4b\u901f\uff0c\u6709\u5982\u4e24\u9053\u8f7b\u70df\uff0c\u76f4\u5230\u4ed6\u4eec\u5728\u8ddd\u5cf0\u5dc5\u4e0d\u8fdc\u5904\u7684\u4e00\u68f5\u866c\u677e\u4e0b\u505c\u4f4f\u811a \u6b65\u3002 \u3000\u3000\u540c\u65f6\u4e5f\u770b\u6e05\u90a3\u7537\u7684\u8eab\u4e0a\u8fd8\u80cc\u8d1f\u7740\u4e00\u4e2a\u5341\u5c81\u5de6\u53f3\u7684\u7537\u5b69\u3002 \u3000\u3000\u8fd9\u4e00\u7537\u4e00\u5973\uff0c\u5e74\u7eaa\u90fd\u5728\u4e09\u5341\u5de6\u53f3\uff0c\u5973\u7684\u5e74\u7eaa\u53ef\u80fd\u8fd8\u8981\u66f4\u8f7b\u4e00\u4e9b\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u7136\u98ce\u5c18\u4ec6\u4ec6\uff0c\u4e00\u671b\u4fbf\u77e5\u662f\u7ecf\u8fc7\u957f\u671f\u8dcb\u6d89\u5954\u6ce2\uff0c\u5374\u4ecd\u63a9\u4e0d\u4f4f\u4e24\u4eba\u7684\u4e0d\u51e1\u795e\u91c7\u3002 \u3000\u3000\u7537\u7684\u4e30\u795e\u4fca\u9038\uff0c\u79c0\u633a\u82f1\u62d4\uff0c\u5973\u7684\u4ed9\u8d44\u7389\u8d28\uff0c\u4eea\u6001\u4e07\u5343\uff0c\u6070\u4f3c\u4e00\u5bf9\u795e\u4ed9\u7737\u4fa3\u3002 \u3000\u3000\u5df2\u88ab\u653e\u5728\u5730\u4e0a\u7684\u5b69\u7ae5\uff0e\u66f4\u662f\u79c0\u6717\u5982\u7389\u3001\u795e\u91c7\u593a\u4eba\u3002 \u3000\u3000\u90a3\u7537\u7684\u671b\u4e86\u5973\u7684\u4e00\u773c\u9053\uff1a\u201c\u79d8\u56fe\u4e0a\u7684\u4f4d\u7f6e\uff0c\u5927\u7ea6\u5c31\u5728\u6b64\u5904\uff0c\u628a\u98de\u513f\u653e\u5728\u8fd9\u91cc\uff0e\u6211\u4eec\u5c31\u53ef \u4ee5\u52a8\u8eab\u4e86\u3002\u201d \u3000\u3000\u5973\u7684\u5411\u56db\u4e0b\u6253\u91cf\u4e86\u8bb8\u4e45\uff0c\u9053\uff1a\u201c\u4e8b\u5173\u91cd\u5927\uff0c\u5343\u4e07\u5931\u8bef\u4e0d\u5f97\uff0c\u4f60\u53ef\u4ed4\u7ec6\u770b\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u7537\u7684\u8bed\u6c14\u9887\u4e3a\u80af\u5b9a\u7684\u9053\uff1a\u201c\u4e0d\u4f1a\u9519\uff0c\u8fd9\u91cc\u6211\u66fe\u6765\u8fc7\u591a\u6b21\uff0c\u600e\u4f1a\u5f04\u4e0d\u6e05\u4f4d\u7f6e\u3002\u201d \u3000\u3000\u5973\u7684\u970e\u65f6\u795e\u8272\u51dd\u91cd\uff0c\u5927\u6709\u751f\u79bb\u6b7b\u522b\u4e4b\u6168\uff0c\u8f7b\u8f7b\u62cd\u4e86\u62cd\u90a3\u5b69\u7ae5\u80a9\u8180\uff0c\u8bed\u8c03\u51c4\u51c9\u7684\u9053\uff1a\u201c\u98de \u513f\uff0c\u6211\u4eec\u5c31\u628a\u4f60\u7559\u5728\u8fd9\u91cc\u4e86\uff0c\u4e0d\u5fc5\u5bb3\u6015\uff0c\u4e0d\u4e45\u4e4b\u540e\uff0c\u81ea\u4f1a\u6709\u4eba\u63a5\u4f60\uff01\u201d \u3000\u3000\u5979\u8bf4\u7740\uff0e\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u6cea\u6c34\u7531\u51e4\u7738\u4e2d\u593a\u7736\u800c\u51fa\u3002 \u3000\u3000\u7537\u7684\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u65e2\u7136\u54b1\u4eec\u548c\u4ed6\u7ec8\u987b\u4e00\u522b\uff0c\u4f24\u5fc3\u53c8\u6709\u4f55\u7528\uff01\u201d \u3000\u3000\u63a5\u7740\u4e5f\u62ac\u624b\u62cd\u62cd\u90a3\u53eb\u98de\u513f\u7684\u5b69\u7ae5\u80a9\u8180\u9053\uff1b\u201c\u98de\u513f\uff0c\u6211\u4eec\u8d70\u4e86\uff01\u201d \u3000\u3000\u98de\u513f\u770b\u6765\u5341\u5206\u575a\u5f3a\uff0c\u4f46\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u201c\u54c7\u201d\u7684\u4e00\u58f0\u54ed\u51fa\u58f0\u6765\u9053\uff1a\u201c\u7239\uff01\u5a18\uff01\u6211\u4eec\u4ec0\u4e48\u65f6\u5019 \u518d\u89c1\u9762\u5462\uff1f\u201d \u3000\u3000\u90a3\u7537\u7684\u795e\u8272\u5145\u6ee1\u65e0\u5948\u4e0e\u611f\u6168\uff0c\u5f3a\u5fcd\u7740\u60b2\u51c4\uff0c\u4f46\u8fd8\u662f\u6324\u51fa\u4e00\u4e1d\u7b11\u5bb9\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u53ea\u7ba1\u8010 \u5fc3\u7684\u7b49\u7740\uff0c\u6211\u4eec\u5f88\u5feb\u5c31\u4f1a\u56de\u6765\u63a5\u4f60\uff01\u201d \u3000\u3000\u4ed6\u8fb9\u8bf4\u8fb9\u53c8\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u4e2a\u5c0f\u5c0f\u9ec4\u7ee2\u5e03\u5305\u9053\uff1a\u201c\u8fd9\u6837\u4e1c\u897f\uff0c\u8981\u597d\u597d\u4fdd\u7ba1\u73cd\u85cf\u7740\uff0c\u4e0d\u53ef\u8ba9 \u4efb\u4f55\u4eba\u6253\u5f00\u770b\u3002\u201d \u3000\u3000\u98de\u513f\u554a\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u7239\uff0c\u8fd9\u91cc\u9762\u88c5\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u7537\u7684\u9053\uff1a\u201c\u662f\u6211\u8ddf\u4f60\u5a18\u5c06\u6765\u548c\u4f60\u8054\u7edc\u7684\u6697\u8bb0\u548c\u4fe1\u7269\u3002\u201d \u3000\u3000\u98de\u513f\u832b\u7136\u628a\u90a3\u5c0f\u5c0f\u9ec4\u7ee2\u5305\u88f9\u63e3\u5165\u6000\u4e2d\uff0c\u53cc\u76ee\u5c04\u5149\uff0c\u7d27\u76ef\u7740\u90a3\u7537\u7684\u548c\u5973\u7684\u9053\uff1b\u201c\u7239\uff01\u5a18\uff01 \u4f60\u4eec\u771f\u7684\u8fd9\u5c31\u8981\u8d70\u4e48\uff1f\u201d \u3000\u3000\u90a3\u7537\u7684\u82e6\u7b11\u9053\uff1a\u201c\u6211\u548c\u4f60\u5a18\u8981\u53bb\u529e\u4e00\u4ef6\u5927\u4e8b\uff0c\u5f53\u7136\u662f\u771f\u7684\u8981\u8d70\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u628a\u5b69\u513f\u4e00\u8d77\u5e26\u7740\u4e86\u201d \u3000\u3000\u201c\u6211\u548c\u4f60\u5a18\u4e0d\u662f\u65e9\u5bf9\u4f60\u8bf4\u4e86\u4e48\uff0c\u8fd9\u662f\u4e00\u4ef6\u5f88\u5371\u9669\u7684\u4e8b\uff0c\u5e26\u7740\u4f60\u53cd\u800c\u66f4\u52a0\u5371\u9669\uff01\u201d \u3000\u3000\u201c\u5b69\u513f\u4e0d\u6015\uff01\u201d \u3000\u3000\u5973\u7684\u63a5\u53e3\u9053\uff1a\u201c\u98de\u513f\uff0c\u4f60\u867d\u7136\u4e0d\u6015\uff0c\u53ef\u662f\u6211\u4eec\u6015\uff0c\u6211\u4eec\u4e0d\u80fd\u8ba9\u4f60\u4e5f\u8ddf\u7740\u767d\u767d\u727a\u7272\u3002\u201d \u3000\u3000\u98de\u513f\u7acb\u523b\u611f\u5230\u4e00\u9635\u751f\u79bb\u6b7b\u522b\u7684\u523a\u75db\uff0c\u6fc0\u52a8\u65e0\u6bd4\u7684\u6b63\u8981\u4e0a\u524d\u626f\u4f4f\u90a3\u5973\u7684\u8863\u895f\uff0c\u90a3\u7537\u7684\u5df2\u98d8 \u8eab\u800c\u53bb\uff0c\u4e00\u9762\u56de\u5934\u53eb\u9053\uff1a\u201c\u5feb\u8d70\uff01\u201d \u3000\u3000\u5973\u7684\u4e5f\u968f\u5373\u7eb5\u8eab\u79bb\u53bb\u3002 \u3000\u3000\u5728\u8fd9\u970e\u90a3\uff0c\u98de\u513f\u5df2\u6014\u4f4f\u4e86\u3002 \u3000\u3000\u4ed6\u5446\u5446\u7684\u7ad9\u5728\u539f\u5730\uff0c\u8111\u5b50\u91cc\u9664\u4e86\u6005\u60d8\u3001\u65e0\u4f9d\uff0c\u51e0\u4e4e\u662f\u4e00\u7247\u7a7a\u767d\u3002 \u3000\u3000\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5f53\u98de\u513f\u7531\u6005\u60d8\u8ff7\u5931\u4e2d\u6e05\u9192\u8fc7\u6765\u65f6\uff0c\u90a3\u4e24\u6761\u4eba\u5f71\uff0c\u65e9\u5df2\u5728\u832b\u832b\u5c71\u91ce\u4e2d\u6d88\u5931 \u65e0\u8e2a\u3002 \u3000\u3000\u4ed6\u8c28\u8bb0\u7236\u6bcd\u53ee\u549b\uff0c\u5fc5\u987b\u5728\u866c\u677e\u4e0b\u9759\u9759\u7b49\u5019\uff0c\u7b49\u5019\u90a3\u6765\u63a5\u81ea\u5df1\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u6839\u672c\u5f04\u4e0d\u6e05\u6765\u63a5\u4ed6\u7684\u4eba\u662f\u4ec0\u4e48\u8eab\u4efd\u6765\u5386\uff0c\u8fde\u5bf9\u65b9\u59d3\u4ec0\u4e48\u53eb\u4ec0\u4e48\u4ee5\u53ca\u591a\u5927\u5e74\u7eaa\u957f\u7684\u4ec0\u4e48 \u6a21\u6837\uff0c\u4e5f\u4e00\u6982\u4e0d\u77e5\u3002 \u3000\u3000\u4ed6\u811a\u4e0b\u6b63\u662f\u72ec\u79c0\u5cf0\u7684\u5cf0\u5dc5\u7565\u4e0b\u65b9\uff0c\u4fef\u8eab\u5411\u4e0b\u671b\u53bb\uff0c\u5c71\u52bf\u5ced\u62d4\u5982\u524a\uff0c\u81ea\u5df1\u7ad9\u7acb\u7684\u4f4d\u7f6e\uff0c\u79f0 \u5f97\u4e0a\u662f\u60ca\u9669\u4e07\u72b6\uff0e\u4e00\u4e0d\u5c0f\u5fc3\uff0c\u5f88\u53ef\u80fd\u5c31\u4f1a\u8dcc\u4e0b\u4e07\u4e08\u6df1\u8c37\u3002 \u3000\u3000\u4f46\u4ed6\u5e76\u4e0d\u8fc7\u4efd\u60ca\u6050\u3002 \u3000\u3000\u56e0\u4ed6\u81ea\u56db\u4e94\u5c81\u8d77\uff0c\u4fbf\u968f\u7236\u6bcd\u4e60\u827a\uff0c\u4e94\u516d\u5e74\u4e0b\u6765\uff0c\u6b66\u529f\u5df2\u8db3\u53ef\u4e0e\u4e00\u6d41\u9ad8\u624b\u76f8\u6bd4\uff0c\u53ea\u662f\u4ed6\u4ece \u672a\u4e0e\u4eba\u52a8\u624b\u8fc7\u62db\uff0c\u81ea\u5df1\u5e76\u4e0d\u6e05\u695a\u800c\u5df2\uff0c \u3000\u3000\u5426\u5219\uff0c\u4ed6\u7684\u7236\u6bcd\u600e\u4f1a\u628a\u4e00\u4e2a\u5e74\u4ec5\u5341\u5c81\u5de6\u53f3\u7684\u5b69\u5b50\uff0c\u629b\u5728\u5c71\u91ce\u95f4\u4e0d\u987e\u5462\u3002 \u3000\u3000\u5927\u7ea6\u987f\u996d\u5de5\u592b\u8fc7\u53bb\uff0c\u4ed6\u5df2\u7b49\u5f97\u6709\u4e9b\u5fc3\u7126\u96be\u5b89\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u8eab\u540e\u4e0d\u8fdc\u5904\u53d1\u51fa\u4e86\u811a\u6b65\u58f0\u3002 \u3000\u3000\u6025\u6025\u8f6c\u8eab\u671b\u53bb\uff0c\u53ea\u89c1\u6765\u4eba\u662f\u4e2a\u8eab\u7a7f\u84dd\u8272\u88e4\u8902\u5341\u516b\u4e5d\u5c81\u7684\u5c11\u5e74\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u867d\u7136\u8863\u9970\u4e0d\u751a\u8bb2\u7a76\uff0c\u4f46\u5374\u733f\u81c2\u5cf0\u8170\u3001\u7709\u6e05\u76ee\u79c0\uff0c\u770b\u6765\u5341\u5206\u82f1\u4fca\u800c\u53c8\u673a\u4f36\u3002 \u3000\u3000\u98de\u513f\u7acb\u65f6\u5fc3\u91cc\u6709\u4e86\u6570\uff0c\u8fd9\u5c11\u5e74\u51b3\u4e0d\u662f\u9644\u8fd1\u519c\u5bb6\u5b50\u5f1f\uff0c\u4e00\u5b9a\u4e5f\u8eab\u8d1f\u4e0a\u4e58\u6b66\u529f\uff0c\u5426\u5219\u5c82\u80fd\u6765 \u5230\u72ec\u79c0\u5cf0\u5dc5\uff1f \u3000\u3000\u5982\u6b64\u770b\u6765\uff0c\u8fd9\u5c11\u5e74\u4e00\u5b9a\u662f\u6765\u63a5\u81ea\u5df1\u7684\u4eba\u4e86\u3002 \u3000\u3000\u4ed6\u987f\u611f\u4e00\u9635\u5174\u594b\uff0c\u6beb\u4e0d\u72b9\u8c6b\u7684\u8fce\u4e0a\u524d\u53bb\uff0c\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u54e5\u53ef\u662f\u6765\u63a5\u6211\u7684\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u6123\u4e86\u4e00\u6123\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u98de\u513f\u53ea\u9053\u5bf9\u65b9\u6ca1\u542c\u6e05\u695a\uff0c\u8fde\u5fd9\u53c8\u62f1\u4e86\u62f1\u624b\uff0c\u518d\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u7b49\u4e00\u4e2a\u4eba\u6765\u63a5\uff0c\u8bf7\u95ee\u5927\u54e5 \u662f\u4e0d\u662f\u6765\u63a5\u6211\u7684\u90a3\u4eba\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u8138\u4e0a\u5148\u662f\u4e00\u7247\u60ca\u6115\u4e4b\u8272\uff0c\u63a5\u7740\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u600e\u4f1a\u662f\u6765\u63a5\u4f60\u7684\u4eba\uff0c\u5c0f\u5144\u5f1f\u4e00\u5b9a\u8ba4 \u9519\u4eba\u4e86\uff01\u201d \u3000\u3000\u98de\u513f\u7acb\u65f6\u7531\u6ee1\u6000\u5e0c\u671b\u53d8\u4e3a\u5931\u671b\uff0c\u8f6c\u4e86\u8f6c\u773c\u73e0\u9053\uff1a\u201c\u5927\u54e5\u65e2\u7136\u4e0d\u662f\u6765\u63a5\u6211\u7684\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u5230 \u8fd9\u91cc\u6765\u5462\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u6211\u521a\u624d\u5728\u5c71\u8170\u770b\u5230\u6709\u4e2a\u4eba\u7ad9\u5728\u8fd9\u91cc\uff0c\u89c9\u5f97\u5f88\u5947\u602a\uff0c\u6240\u4ee5\u624d\u4e0a\u6765\u770b\u770b\uff0c\u60f3\u4e0d \u5230\u662f\u4f60\uff0c\u5c0f\u5144\u5f1f\uff0c\u4f60\u600e\u4e48\u4f1a\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u98de\u513f\u4e24\u773c\u970e\u970e\u5730\u53cd\u95ee\u9053\uff1a\u201c\u96be\u9053\u8fd9\u91cc\u4e0d\u80fd\u6765\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u80fd\u6765\uff0c\u800c\u662f\u4e00\u822c\u4eba\u4e0d\u53ef\u80fd\u6500\u767b\u5230\u8fd9\u6837\u9661\u5ced\u7684\u5c71\u5dc5\uff0c\u5c24\u5176\u5c0f\u5144\u5f1f\u53ea\u662f \u4e00\u4e2a\u5b69\u5b50\uff0c\u4f60\u7a76\u7adf\u662f\u600e\u6837\u722c\u4e0a\u6765\u7684\uff1f\u201d \u3000\u3000\u98de\u513f\u4e0d\u613f\u8bf4\u51fa\u771f\u60c5\uff0c\u5fae\u5fae\u4e00\u987f\u9053\uff1a\u201c\u6211\u5c0f\u65f6\u5019\u722c\u5c71\u722c\u60ef\u4e86\uff0c\u8fd9\u5ea7\u5c71\u8fd8\u96be\u4e0d\u5012\u6211\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u5e26\u4e9b\u5403\u60ca\u6a21\u6837\u9053\uff1a\u201c\u4f60\u80fd\u722c\u4e0a\u72ec\u79c0\u5cf0\uff0c\u5728\u4e60\u6b66\u7684\u4eba\u6765\u8bf4\uff0c\u4e5f\u7b97\u5f97\u4e00\u6d41\u8f7b\u529f\u4e86\uff01\u201d \u3000\u3000\u98de\u513f\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u4ec0\u4e48\u8f7b\u529f\u4e0d\u8f7b\u529f\uff0c\u6211\u53ea\u662f\u722c\u4e0a\u6765\u7b49\u4e00\u4e2a\u4eba\u6765\u63a5\u6211\u3002\u201d \u3000\u3000\u201c\u662f\u8c01\u8981\u4f60\u722c\u5230\u8fd9\u91cc\u6765\u7b49\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u7239\u548c\u6211\u5a18\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5462\uff1f\u201d \u3000\u3000\u201c\u5230\u522b\u5904\u53bb\u529e\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4f60\u5728\u8fd9\u91cc\u7b49\u4e0d\u5230\u4eba\u6765\u63a5\uff0c\u5929\u9ed1\u4e86\u4ee5\u540e\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u7684\uff0c\u4e00\u5b9a\u6709\u4eba\u6765\u63a5\u6211\uff0c\u6211\u7239\u5a18\u600e\u4f1a\u9a97\u6211\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u671b\u671b\u5929\u8272\u9053\uff1a\u201c\u518d\u8fc7\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5929\u5c31\u9ed1\u4e86\uff0c\u4f60\u4e00\u4e2a\u5c0f\u5b69\u5b50\u72ec\u81ea\u5728\u8fd9\u4e48\u9ad8\u7684\u8352\u5c71\u4e0a\uff0c \u4e0d\u662f\u95f9\u7740\u73a9\u7684\uff0c\u5982\u679c\u4f60\u613f\u610f\uff0c\u4e0d\u59a8\u8ddf\u6211\u8d70\uff0c\u7b49\u8fc7\u4e86\u4eca\u665a\u518d\u8bf4\u3002\u201d \u3000\u3000\u98de\u513f\u4e24\u53ea\u6c34\u7075\u7075\u7684\u5927\u773c\u775b\u7728\u52a8\u4e86\u4e00\u9635\u9053\uff1a\u201c\u5927\u54e5\u7684\u5bb6\u5c31\u5728\u9644\u8fd1\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5c31\u5728\u5c71\u8170\u91cc\uff0c\u79bb\u8fd9\u91cc\u4e0d\u8fc7\u4e00\u4e24\u91cc\u8def\u3002\u201d \u3000\u3000\u98de\u513f\u6709\u4e9b\u5fc3\u52a8\uff0c\u771f\u60f3\u8ddf\u7740\u4ed6\u8d70\u3002 \u3000\u3000\u4f46\u518d\u4e00\u60f3\u81ea\u5df1\u662f\u5728\u8fd9\u91cc\u7b49\u4eba\u7684\uff0c\u82e5\u81ea\u5df1\u4e00\u8d70\uff0c\u4e07\u4e00\u6709\u4eba\u6765\u63a5\uff0c\u5c82\u4e0d\u9519\u5931\u673a\u4f1a\uff0c\u90a3\u6837\u4e00\u6765\uff0c \u4ee5\u540e\u53c8\u5982\u4f55\u548c\u7236\u6bcd\u8054\u7edc\uff1f \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0e\u4ed6\u6447\u6447\u624b\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u6211\u4e0d\u80fd\u8ddf\u7740\u4f60\u53bb\uff0c\u4f60\u82e5\u6709\u4e8b\uff0c\u53ea\u7ba1\u81ea\u5df1\u8d70\u5427\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u867d\u7136\u96be\u514d\u4e3a\u98de\u513f\u62c5\u5fc3\uff0c\u5374\u4e5f\u4e0d\u80fd\u52c9\u5f3a\uff0c\u53ea\u597d\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u866c\u677e\u4e0b\u53c8\u5269\u4e0b\u98de\u513f\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u8010\u5fc3\u7684\u7b49\u7740\u3002 \u3000\u3000\u8d77\u521d\uff0c\u4ed6\u4f3c\u662f\u9887\u6709\u4fe1\u5fc3\u4e00\u5b9a\u4f1a\u6709\u4eba\u6765\u63a5\uff0c\u4f46\u65f6\u95f4\u5728\u4e0d\u77e5\u4e0d\u89c9\u95f4\u8fc7\u53bb\uff0c\u5929\u8272\u5df2\u6e10\u6e10\u6697\u4e86\u4e0b \u6765\u3002 \u3000\u3000\u7a7a\u5c71\u5bc2\u5bc2\uff0c\u9664\u4e86\u611f\u5230\u9635\u9635\u51c9\u610f\uff0c\u4f3c\u4e4e\u4e5f\u589e\u6dfb\u4e86\u51e0\u5206\u6050\u6016\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u624d\u611f\u5230\u5df2\u662f\u9965\u80a0\u8f98\u8f98\u3002 \u3000\u3000\u4ed6\u4e0d\u6028\u4e3a\u4ec0\u4e48\u5230\u8fd9\u65f6\u8fd8\u65e0\u4eba\u6765\u63a5\uff0c\u53ea\u6094\u65b9\u624d\u4e3a\u4ec0\u4e48\u4e0d\u968f\u90a3\u5c11\u5e74\u5230\u5c71\u8170\u6682\u4f4f\u4e00\u5bb5\u518d\u8bf4\u3002 \u3000\u3000\u73b0\u5728\u5462\uff0c\u66ae\u8272\u82cd\u832b\uff0c\u53c8\u5230\u54ea\u91cc\u53bb\u627e\u6816\u8eab\u4e4b\u5904\u5462\uff1f \u3000\u3000\u5c71\u4e0a\u8d8a\u6765\u8d8a\u51b7\uff0c\u4ed6\u9884\u6599\u6765\u63a5\u7684\u4eba\u4e0d\u53ef\u80fd\u6765\u4e86\uff0c\u8d81\u5929\u8272\u5c1a\u672a\u5168\u6697\uff0c\u5fc5\u987b\u8fd9\u5c31\u4e0b\u5c71\uff0c\u90a3\u5c11\u5e74 \u65e2\u7136\u4f4f\u5728\u5c71\u8170\uff0c\u5929\u665a\u5fc5\u6709\u706f\u5149\u5c04\u51fa\uff0c\u4e5f\u8bb8\u4e0d\u96be\u627e\u5230\u3002 \u3000\u3000\u521a\u8981\u4e0b\u5c71\uff0c\u53ea\u89c1\u4e00\u6761\u4eba\u5f71\uff0e\u75be\u5954\u800c\u6765\uff0c\u6b63\u662f\u5148\u524d\u6765\u8fc7\u7684\u90a3\u4f4d\u5c11\u5e74\u3002 \u3000\u3000\u4ed6\u4e0d\u89c9\u559c\u51fa\u671b\u5916\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5f88\u5feb\u4fbf\u6765\u5230\u8ddf\u524d\uff0c\u5341\u5206\u5173\u5207\u7684\u95ee\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u98de\u513f\u9053\uff1a\u201c\u6211\u6b63\u8981\u53bb\u627e\u5927\u54e5\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u662f\u6765\u63a5\u4f60\u7684\u3002\u201d \u3000\u3000\u98de\u513f\u201c\u54e6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u83ab\u975e\u5927\u54e5\u5c31\u662f\u6765\u63a5\u6211\u7684\u90a3\u4eba\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u6447\u5934\u9053\uff1a\u201c\u6211\u53ea\u662f\u5949\u547d\u6765\u63a5\u4f60\u6682\u65f6\u5230\u5c71\u4e0b\u4f4f\u4e00\u665a\u7684\u3002\u201d \u3000\u3000\u201c\u5927\u54e5\u662f\u5949\u8c01\u4e4b\u547d\uff1f\u201d \u3000\u3000\u201c\u5bb6\u5e08\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6ca1\u8bf7\u95ee\u5927\u54e5\u4e0a\u59d3\u9ad8\u540d\uff1f\u201d \u3000\u3000\u201c\u6211\u53eb\u8881\u5c0f\u9e64\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u662f\u8881\u5927\u54e5\uff0c\u518d\u8bf7\u95ee\u4ee4\u5e08\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u5bb6\u5e08\u59d3\u5e90\u540d\u4e91\uff0c\u4eba\u79f0\u5723\u624b\u4e66\u751f\uff0c\u5f53\u5e74\u5728\u6b66\u6797\u4e2d\u5927\u540d\u9f0e\u9f0e\uff0c\u4e0d\u8fc7\u8fd1\u5341\u5e74\u6765\uff0c\u4ed6\u8001\u4eba\u5bb6\u5df2 \u5f88\u5c11\u518d\u5728\u5916\u9762\u8d70\u52a8\uff0c\u51e0\u4e4e\u5b8c\u5168\u8fc7\u7740\u9690\u5c45\u751f\u6d3b\uff0c\u4ee5\u5c0f\u5144\u5f1f\u8fd9\u79cd\u5e74\u7eaa\uff0c\u4e0d\u53ef\u80fd\u542c\u5230\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u98de\u513f\u867d\u7136\u7236\u6bcd\u4e5f\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u4f46\u5374\u7684\u786e\u6ca1\u542c\u8bf4\u8fc7\u5e90\u4e91\u8fd9\u4e2a\u540d\u5b57\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u53c8\u9053\uff1a\u201c\u5c71\u8def\u9669\u5cfb\uff0c\u5c0f\u5144\u5f1f\u8981\u4e0d\u8981\u6211\u5e2e\u5fd9\u80cc\u4f60\u4e00\u7a0b\uff1f\u201d \u3000\u3000\u98de\u513f\u9053\uff1a\u201c\u591a\u8c22\u4e86\uff0c\u6211\u5148\u524d\u5bf9\u5927\u54e5\u8bf4\u8fc7\uff0c\u81ea\u5c0f\u722c\u5c71\u722c\u60ef\u4e86\uff0c\u8fd9\u79cd\u5c71\u8def\uff0c\u8fd8\u96be\u4e0d\u5012\u6211\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4ece\u548c\u98de\u513f\u4e00\u89c1\u9762\u8d77\uff0c\u5c31\u89c9\u5f97\u4ed6\u50cf\u4e2a\u5927\u4eba\u3002\u4e0d\u8bba\u8c08\u5410\u548c\u4e3e\u6b62\uff0c\u90fd\u8d85\u8fc7\u4ed6\u7684\u5e74\u9f84\u5f88\u591a\uff0c \u56e0\u4e4b\uff0c\u4e5f\u5e76\u4e0d\u52c9\u5f3a\uff0c\u51b3\u5b9a\u4efb\u7531\u4ed6\u81ea\u5df1\u8d70\u8def\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u8d70\u5728\u524d\u9762\uff0c\u8fd8\u662f\u4e0d\u5f97\u4e0d\u653e\u7f13\u811a\u6b65\uff0c\u4ee5\u514d\u98de\u513f\u8ddf\u4e0d\u4e0a\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u4ed6\u54ea\u91cc\u77e5\u9053\uff0c\u98de\u513f\u7684\u8f7b\u529f\uff0c\u51b3\u4e0d\u5728\u4ed6\u4e4b\u4e0b\u3002 \u3000\u3000\u5230\u8fbe\u5c71\u8170\uff0c\u5929\u8272\u5df2\u665a\uff0c\u4f46\u4ecd\u80fd\u770b\u6e05\u8881\u5c0f\u9e64\u5e08\u5f92\u6240\u4f4f\u4e4b\u5904\uff0c\u662f\u4e2a\u4e09\u5408\u9662\u7684\u8305\u820d\uff0c\u5916\u9762\u56f4\u7740 \u4e00\u9053\u6bd4\u4eba\u8fd8\u9ad8\u7684\u7af9\u7bf1\u3002 \u3000\u3000\u8fdb\u5165\u7bf1\u95e8\uff0c\u53ea\u6709\u4e1c\u8fb9\u53a2\u623f\u91cc\u5c04\u51fa\u706f\u5149\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u5411\u4e1c\u53a2\u4e00\u6307\u9053\uff1a\u201c\u6211\u5c31\u4f4f\u5728\u90a3\u91cc\uff0c\u5c0f\u5144\u5f1f\u4eca\u665a\u5c31\u4f4f\u897f\u53a2\u5427\uff01\u201d \u3000\u3000\u8fdb\u5165\u897f\u53a2\uff0c\u8881\u5c0f\u9e64\u70b9\u4e0a\u706f\uff0c\u53ea\u89c1\u5ba4\u5185\u5c18\u4e1d\u4e0d\u67d3\uff0c\u5e8a\u4e0a\u88ab\u8925\u4ff1\u5168\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u5e08\u5f92\u5bf9\u964c\u751f\u4eba\u5982\u6b64\u70ed\u60c5\u62db\u5f85\uff0c\u98de\u513f\u81ea\u662f\u611f\u6fc0\u4e07\u5206\uff0c\u4f55\u51b5\u4ed6\u53c8\u53ea\u662f\u4e2a\u5341\u5c81\u7684\u5b69\u5b50\u3002 \u3000\u3000\u98de\u513f\u5e26\u7740\u611f\u6fc0\u7684\u8bed\u6c14\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0c\u4f60\u53bb\u7761\u5427\uff0c\u4e0d\u5fc5\u518d\u62db\u5f85\u6211\u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u5403\u996d\uff0c\u4e00\u5b9a\u997f\u4e86\uff0c\u53a8\u623f\u91cc\u6709\u73b0\u6210\u7684\u996d\u83dc\uff0c\u6211\u53bb\u70ed\u4e00\u70ed\u66ff\u4f60\u9001\u6765\u3002\u201d \u3000\u3000\u4ed6\u53bb\u540e\u4e0d\u4e45\uff0c\u5c31\u7aef\u7740\u4e00\u76d8\u70ed\u817e\u817e\u7684\u996d\u83dc\u8fdb\u6765\uff0c\u653e\u5728\u684c\u4e0a\u9053\uff1a\u201c\u8d81\u70ed\u5403\uff0c\u5403\u4e86\u5c31\u7761\u89c9\uff01\u201d \u3000\u3000\u98de\u513f\u6839\u672c\u987e\u4e0d\u5f97\u5ba2\u6c14\uff0c\u72fc\u541e\u864e\u54bd\u7684\u5403\u8fc7\u4e4b\u540e\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0c\u5c0f\u5f1f\u73b0\u5728\u8be5\u53bb\u62dc\u89c1\u4ee4\u5e08\u4e86\u3002\u201d \u3000\u3000\u4ed6\u867d\u7136\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5374\u9887\u77e5\u793c\u8c8c\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff0c\u201c\u4e0d\u5fc5\uff0c\u5bb6\u5e08\u5df2\u7ecf\u7761\u7740\uff0c\u7b49\u660e\u5929\u4e00\u65e9\uff0c\u6211\u518d\u5e26\u4f60\u53bb\u89c1\u4ed6\u3002\u201d \u3000\u3000\u5f7c\u6b64\u53c8\u8c08\u4e86\u51e0\u53e5\uff0c\u8881\u5c0f\u9e64\u4fbf\u544a\u8f9e\u800c\u53bb\u3002 \u3000\u3000\u5ba4\u5185\u53ea\u5269\u4e0b\u98de\u513f\u4e00\u4e2a\u4eba\uff0c\u4ed6\u601d\u5ff5\u7236\u6bcd\uff0c\u53c8\u62c5\u5fc3\u6765\u63a5\u4ed6\u7684\u4eba\u627e\u4e0d\u5230\u4ed6\uff0c\u8eba\u4e0a\u5e8a\u540e\uff0c\u6839\u672c\u65e0 \u6cd5\u5165\u7761\u3002 \u3000\u3000\u6ca1\u5948\u4f55\uff0c\u5e72\u8106\u8d77\u8eab\u4e0b\u5e8a\uff0c\u5c31\u5728\u5ba4\u5185\u6084\u6084\u5730\u7ec3\u8d77\u529f\u592b\u6765\u3002 \u3000\u3000\u4ed6\u81ea\u5e7c\u968f\u7236\u6bcd\u4e60\u827a\uff0c\u5df2\u7ecf\u5c3d\u5f97\u7236\u6bcd\u771f\u4f20\uff0c\u53ea\u662f\u56e0\u5e74\u5c81\u592a\u5c0f\uff0c\u5185\u529b\u4e0d\u8db3\uff0c\u65e0\u6cd5\u5c3d\u60c5\u53d1\u6325\u800c \u5df2\u3002\u82e5\u4ee5\u4ed6\u76ee\u524d\u6240\u4e60\u5f97\u7684\u6b66\u529f\u62db\u5f0f\uff0c\u7b49\u957f\u5927\u540e\uff0c\u4e0d\u5fc5\u518d\u884c\u62dc\u5e08\uff0c\u5df2\u8db3\u53ef\u8dfb\u8eab\u7edd\u9876\u9ad8\u624b\u4e4b\u6797\u3002 \u3000\u3000\u8db3\u8db3\u7ec3\u4e86\u4e00\u4e2a\u65f6\u8fb0\u4ee5\u4e0a\uff0c\u5df2\u662f\u6ee1\u8eab\u5927\u6c57\uff0c\u4ed6\u624d\u6e10\u611f\u75b2\u60eb\uff0c\u4e0d\u4e0d\u5f97\u4e0d\u4e0a\u5e8a\u4f11\u606f\u3002 \u3000\u3000\u4e00\u89c9\u9192\u6765\uff0c\u5929\u5df2\u5927\u4eae\u3002 \u3000\u3000\u4ed6\u660e\u767d\uff0c\u4f4f\u5728\u522b\u4eba\u5bb6\u91cc\uff0c\u5929\u4e00\u4eae\u5c31\u5fc5\u987b\u8d77\u5e8a\u3002 \u3000\u3000\u521a\u62ab\u8863\u4e0b\u5e8a\uff0c\u8881\u5c0f\u9e64\u4fbf\u628a\u65e9\u9910\u9001\u6765\u3002 \u3000\u3000\u5982\u6b64\u70ed\u60c5\u62db\u5f85\uff0c\u5f53\u771f\u8ba9\u4ed6\u8fc7\u610f\u4e0d\u53bb\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u773c\u770b\u4ed6\u5403\u5b8c\u65e9\u9910\u540e\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4e0d\u662f\u8981\u89c1\u5bb6\u5e08\u4e48\uff1f\u8fd9\u5c31\u968f\u6211\u6765\u5427\uff01\u201d \u3000\u3000\u98de\u513f\u6574\u4e86\u6574\u8863\u51a0\uff0c\u7acb\u5373\u968f\u8881\u5c0f\u9e64\u6765\u5230\u6b63\u5c4b\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u5e74\u5728\u56db\u65ec\u5f00\u5916\uff0c\u7559\u7740\u4e94\u7efa\u957f\u9aef\u3001\u7709\u6e05\u76ee\u6717\uff0c\u6e29\u6587\u5c14\u96c5\uff0c\u8eab\u7a7f\u85cf\u9752\u8272\u957f\u888d\u7684\u4e2d\u5e74 \u4eba\uff0c\u6b63\u5750\u5728\u4e00\u5f20\u6728\u6905\u4e0a\uff0c\u624b\u91cc\u8fd8\u62ff\u7740\u4e00\u672c\u4e66\u5728\u89c2\u770b\u3002 \u3000\u3000\u98de\u513f\u53ea\u89c9\u8fd9\u4eba\u867d\u662f\u7709\u6e05\u76ee\u6717\uff0c\u4f46\u5374\u9762\u8272\u82cd\u767d\uff0c\u795e\u91c7\u4e2d\u66f4\u9690\u6cdb\u7740\u4e00\u80a1\u6092\u90c1\u4e4b\u6c14\uff0c\u50cf\u662f\u4e0d\u751a \u5f00\u5fc3\u7684\u6a21\u6837\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u7d27\u884c\u51e0\u6b65\uff0e\u606d\u606d\u656c\u656c\u7684\u4e0a\u524d\u6df1\u601d\u4e00\u793c\u9053\uff1a\u201c\u665a\u8f88\u98de\u513f\u62dc\u89c1\u5e90\u524d\u8f88\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u51fa\u81ea\u4e00\u4e2a\u5341\u5c81\u5b69\u5b50\u95e8\u4e2d\uff0c\u4f3c\u4e4e\u683c\u5916\u5f15\u4eba\u6ce8\u610f\u3002 \u3000\u3000\u5723\u624b\u4e66\u751f\u5e90\u4e91\u5c06\u4e66\u629b\u5411\u8336\u51e0\uff0c\u53cc\u76ee\u795e\u5149\u70af\u70af\uff0c\u51dd\u6ce8\u5728\u98de\u513f\u8138\u4e0a\uff0c\u597d\u4e00\u9635\uff0c\u624d\u7f13\u7f13\u95ee\u9053\uff1a \u201c\u542c\u5c0f\u9e64\u8bf4\uff0c\u4f60\u662f\u5728\u72ec\u79c0\u5cf0\u4e0a\u7b49\u4e00\u4e2a\u6765\u63a5\uff0c\u8fd9\u4eba\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u98de\u513f\u6b49\u7136\u9053\uff1a\u201c\u665a\u8f88\u53ea\u662f\u542c\u5bb6\u7236\u6bcd\u8bf4\u6709\u4eba\u6765\u63a5\uff0c\u5e76\u4e0d\u6e05\u695a\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5e90\u4e91\u4e0d\u52a8\u58f0\u8272\uff0c\u518d\u95ee\u9053\uff1a\u201c\u4ee4\u5c0a\u4ee4\u5802\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u98de\u513f\u9053\uff1a\u201c\u5bb6\u7236\u5bb6\u6bcd\u8981\u53bb\u529e\u4e00\u4ef6\u5927\u4e8b\uff0c\u4ed6\u4eec\u628a\u665a\u8f88\u7559\u5728\u72ec\u79c0\u5cf0\u4e0a\u4e00\u68f5\u5927\u677e\u6811\u4e0b\u5c31\u8d70\u4e86\u3002\u201d \u3000\u3000\u5e90\u4e91\u5bf9\u98de\u513f\u7684\u56de\u7b54\uff1f\u4f3c\u662f\u9887\u4e3a\u7559\u610f\uff0c\u53cc\u76ee\u4e2d\u5f02\u91c7\u5ffd\u73b0\u9053\uff1a\u201c\u4ee4\u5c0a\u4ee4\u5802\u53bb\u529e\u7684\u662f\u4ec0\u4e48\u5927 \u4e8b\uff1f\u201d \u3000\u3000\u98de\u513f\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u5e90\u4e91\u7d27\u8ddf\u7740\u518d\u95ee\uff1a\u201c\u4f60\u662f\u771f\u7684\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u98de\u513f\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u5e90\u4e91\u5927\u5927\u4e0d\u4ee5\u4e3a\u7136\u7684\u54fc\u4e86\u4e00\u58f0\uff1a\u201c\u4ee4\u5c0a\u4ee4\u5802\u505a\u4e8b\u600e\u53ef\u5982\u6b64\u8f7b\u7387\uff0c\u7ade\u628a\u50cf\u4f60\u8fd9\u6837\u5927\u7684\u2014\u4e2a \u5b69\u5b50\u72ec\u81ea\u7559\u5728\u72ec\u79c0\u5cf0\u4e0a\uff0c\u5b9e\u5728\u4e0d\u8be5\uff01\u201d \u3000\u3000\u4ed6\u987f\u4e86\u2014\u987f\uff0c\u518d\u9053\uff1a\u201c\u4ee4\u5c0a\u4ee4\u5802\u7684\u540d\u8bb3\u600e\u6837\u79f0\u547c\uff1f\u201d \u3000\u3000\u5c82\u77e5\u8fd9\u2014\u95ee\uff0c\u98de\u513f\u7adf\u7136\u4e45\u4e45\u7b54\u4e0d\u4e0a\u6765\u3002 \u3000\u3000\u8fd9\u60c5\u666f\u4e0d\u4f46\u4f7f\u5f97\u5e90\u4e91\u5927\u611f\u8be7\u5f02\uff0c\u8fde\u7ad9\u5728\u4e00\u65c1\u7684\u8881\u5c0f\u9e64\u4e5f\u6709\u4e9b\u900f\u7740\u5947\u602a\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u5fcd\u4e0d\u4f4f\u63d2\u53e3\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u5bb6\u5e08\u95ee\u4f60\u7684\u8bdd\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u56de\u7b54\uff1f\u201d \u3000\u3000\u98de\u513f\u671b\u4e86\u5e90\u4e91\u4e00\u773c\uff0c\u7ec8\u4e8e\u55eb\u55eb\u7740\u9053\uff1a\u201c\u524d\u8f88\u8bf7\u76f8\u4fe1\u665a\u8f88\u7684\u8bdd\uff0c\u665a\u8f88\u76f4\u5230\u73b0\u5728\uff0e\u5e76\u4e0d\u6e05\u695a \u5bb6\u7236\u6bcd\u59d3\u4ec0\u4e48\uff0c\u53eb\u4ec0\u4e48\u3002\u201d \u3000\u3000\u5e90\u4e91\u987f\u611f\u4e00\u6014\uff0c\u591a\u65f6\uff0c\u624d\u53c8\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u59d3\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u98de\u513f\u7a98\u5f97\u4f4e\u4e0b\u4e86\u5934\uff1a\u201c\u665a\u8f88\u65e2\u4e0d\u6e05\u695a\u5bb6\u7236\u59d3\u4ec0\u4e48\uff0c\u5f53\u7136\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u59d3\u4ec0\u4e48\u3002\u201d \u3000\u3000\u8fd9\u56de\u7b54\u7b80\u76f4\u4ee4\u5e90\u4e91\u6709\u4e9b\u557c\u7b11\u7686\u975e\uff0c\u4ed6\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e16\u4e0a\u8fd8\u6709\u8fd9\u6837\u7684\u4e8b\uff0c \u90a3\u4e48\u4f60\u73b0\u5728\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u201d \u3000\u3000\u98de\u513f\u9053\uff1a\u201c\u665a\u8f88\u53ea\u60f3\u4eca\u5929\u518d\u5230\u72ec\u79c0\u5cf0\u7b49\u4eba\u6765\u63a5\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u5bf9\u65b9\u518d\u4e0b\u6765\u63a5\u5462\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u51b3\u5b9a\u4e00\u8fde\u7b49\u4e0a\u4e09\u5929\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4e09\u5929\u4e4b\u5185\uff0c\u4ecd\u7136\u65e0\u4eba\u6765\u63a5\u5462\uff1f\u201d \u3000\u3000\u98de\u513f\u88ab\u95ee\u5f97\u7b54\u4e0d\u535c\u8bdd\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\u4e00\u4e2a\u5341\u5c81\u7684\u5b69\u5b50\uff0c\u5fc3\u610f\u80fd\u5982\u6b64\u575a\u5b9a\uff0c\u5df2\u7ecf\u5f88\u96be\u5f97\u4e86\u3002 \u3000\u3000\u5e90\u4e91\u7684\u89c6\u7ebf\u7d27\u76ef\u7740\u98de\u513f\uff0c\u53ea\u89c9\u5f97\u8fd9\u5b69\u5b50\u9aa8\u5947\u795e\u6e05\uff0c\u79f0\u5f97\u4e0a\u662f\u4e00\u5757\u6d51\u91d1\u749e\u7389\uff0c\u82e5\u80fd\u597d\u597d\u52a0 \u4ee5\u8c03\u6559\uff0c\u5fc5\u53ef\u6210\u4e3a\u6587\u6b66\u5168\u624d\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u4ece\u6728\u6905\u4e0a\u7ad9\u8d77\u8eab\u6765\uff0c\u62cd\u62cd\u98de\u513f\u80a9\u8180\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u7528\u4e0d\u7740\u53d1\u6101\uff0c\u6211\u5c31\u8ba9\u4f60\u5728\u72ec\u79c0 \u5cf0\u4e0a\u7b49\u4e09\u5929\uff0c\u82e5\u4e09\u5929\u5185\u65e0\u4eba\u6765\u63a5\uff0c\u4f60\u5c31\u7559\u5728\u6211\u8fd9\u91cc\uff0c\u613f\u4e0d\u613f\u610f\uff0c\u53ea\u7ba1\u5b9e\u8bf4\uff01\u201d \u3000\u3000\u98de\u513f\u90a3\u6709\u4e0d\u613f\u610f\u7684\u9053\u7406\uff0c\u8fde\u5fd9\u53cc\u819d\u8dea\u5730\u9053\uff1a\u201c\u591a\u8c22\u524d\u8f88\u539a\u6069\uff01\u201d \u3000\u3000\u5e90\u4e91\u671b\u5411\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5e26\u4ed6\u5230\u72ec\u79c0\u5cf0\u53bb\uff01\u201d \u3000\u3000\u98de\u513f\u8d77\u8eab\u9053\uff1a\u201c\u4e0d\u5fc5\u9ebb\u70e6\u8881\u5927\u54e5\u4e86\uff0c\u665a\u8f88\u81ea\u5df1\u80fd\u627e\u5230\u90a3\u5730\u65b9\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u62dc\u522b\u5e90\u4e91\uff0c\u968f\u5373\u4e0a\u5c71\u800c\u53bb\u3002 \u3000\u3000\u5230\u4e86\u5cf0\u5dc5\u4e0b\u65b9\u90a3\u68f5\u866c\u677e\u5904\uff0c\u56db\u987e\u832b\u832b\uff0c\u5cf0\u5ce6\u76f8\u63a5\uff0c\u6839\u672c\u8fde\u4e2a\u4eba\u5f71\u90fd\u770b\u4e0d\u89c1\u3002 \u3000\u3000\u8fd9\u4e5f\u96be\u602a\uff0c\u5728\u8fd9\u79cd\u5730\u65b9\uff0c\u65e2\u4e0d\u53ef\u80fd\u6709\u519c\u7530\uff0c\u4e5f\u4e0d\u53ef\u80fd\u6709\u679c\u56ed\uff0c\u666e\u901a\u4eba\u53ef\u671b\u800c\u4e0d\u53ef\u53ca\uff0c\u600e \u4f1a\u6709\u4eba\u4e0a\u6765\u5462\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u8fd8\u662f\u8010\u5fc3\u7b49\u5f85\u7740\u3002 \u3000\u3000\u65f6\u95f4\u5728\u7b49\u4eba\u65f6\u663e\u5f97\u7279\u522b\u957f\uff0c\u770b\u770b\u5230\u4e86\u4e2d\u5348\uff0c\u8179\u5185\u53c8\u5f00\u59cb\u9965\u997f\u3002 \u3000\u3000\u597d\u5728\u8fd9\u65f6\u8881\u5c0f\u9e64\u5df2\u628a\u5348\u9910\u9001\u6765\u3002 \u3000\u3000\u996d\u540e\uff0c\u7b49\u5230\u5929\u8272\u5c06\u665a\uff0c\u4f9d\u7136\u65e0\u4eba\u6765\u63a5\u3002 \u3000\u3000\u4ed6\u53ea\u597d\u518d\u56de\u5230\u8305\u820d\u8fc7\u591c\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u548c\u7b2c\u4e09\u5929\uff0c\u4ed6\u4e0d\u613f\u518d\u9ebb\u70e6\u8881\u5c0f\u9e64\u5230\u5c71\u4e0a\u9001\u996d\uff0c\u4fbf\u81ea\u884c\u5e26\u4e86\u5e72\u7cae\u4e0a\u5c71\u3002 \u3000\u3000\u5230\u7b2c\u4e09\u5929\u4e0b\u5348\u4ecd\u65e0\u4eba\u6765\u63a5\uff0c\u4ed6\u5df2\u5f00\u59cb\u5931\u53bb\u5e0c\u671b\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u5374\u5728\u4e00\u5757\u5e73\u6ed1\u7684\u5927\u9752\u77f3\u4e0a\u7559\u4e0b\u4e00\u53e5\u8bdd\uff0c\u523b\u4e86\u8fd9\u6837\u7684\u51e0\u4e2a\u5b57\uff1a\u201c\u98de\u513f\u5728\u5c71\u8170\u8305\u5c4b \u91cc\u201d\u3002 \u3000\u3000\u6709\u4e86\u8fd9\u51e0\u4e2a\u5b57\uff0c\u603b\u7b97\u7a0d\u7a0d\u653e\u5fc3\u3002 \u3000\u3000\u56de\u5230\u5c71\u8170\u8305\u5c4b\uff0c\u5929\u8272\u5df2\u665a\u3002 \u3000\u3000\u8fdb\u5165\u7bf1\u95e8\uff0c\u53ea\u89c1\u5e90\u4e91\u6b63\u5728\u7bf1\u5185\u9662\u5b50\u91cc\u80cc\u624b\u800c\u7acb\uff0c\u8881\u5c0f\u9e64\u968f\u4f8d\u5728\u4fa7\u3002 \u3000\u3000\u73b0\u5728\uff0c\u5e90\u4e91\u548c\u8881\u5c0f\u9e64\u5c31\u662f\u4ed6\u7684\u4eb2\u4eba\u4e86\uff0c\u4eca\u540e\u4ed6\u5fc5\u987b\u548c\u4ed6\u4eec\u751f\u6d3b\u5728\u4e00\u8d77\u3002\u81f3\u4e8e\u8fd9\u6bb5\u76f8\u5904\u7684 \u65f6\u95f4\u6709\u591a\u4e45\uff0c\u8c01\u4e5f\u65e0\u6cd5\u9884\u6599\u3002 \u3000\u3000\u4e0d\u7b49\u4ed6\u4e0a\u524d\u89c1\u793c\uff0c\u5e90\u4e91\u4fbf\u8f6c\u8fc7\u8eab\u6765\u9053\uff1a\u201c\u98de\u513f\uff0c\u968f\u6211\u5230\u91cc\u9762\u6765\uff01\u201d \u3000\u3000\u98de\u513f\u4e0d\u77e5\u8981\u53d1\u751f\u4ec0\u4e48\u4e8b\uff0c\u53ea\u6709\u6014\u6014\u5730\u8ddf\u7740\u8fdb\u5165\u6b63\u5c4b\u3002 \u3000\u3000\u5e90\u4e91\u5750\u4e0b\u9053\uff1a\u201c\u770b\u6837\u5b50\u4e0d\u53ef\u80fd\u6709\u4eba\u6765\u63a5\u4f60\u4e86\uff0c\u4f60\u662f\u5426\u51b3\u5b9a\u5c31\u7559\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u98de\u513f\u8083\u5bb9\u9053\uff1b\u201c\u832b\u832b\u5929\u6daf\uff0c\u4e3e\u76ee\u65e0\u4eb2\uff0c\u665a\u8f88\u5f53\u7136\u613f\u610f\u7559\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u5e90\u4e91\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u4f60\u81f3\u4eca\u8fde\u59d3\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\uff0c\u662f\u5426\u60f3\u5230\u8be5\u6709\u4e00\u4e2a\u6b63\u5f0f\u7684\u59d3\u540d\u5462\uff1f\u201d \u3000\u3000\u98de\u513f\u5bf9\u5e90\u4e91\uff0c\u4e00\u89c1\u5c31\u611f\u4ef0\u6155\uff0c\u4ed6\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u5982\u679c\u524d\u8f88\u80af\u6536\u98de\u513f\u4e3a\u4e49\u5b50\uff0c\u6211\u5c31\u6682\u65f6\u59d3 \u5e90\u5427\uff01\u201d \u3000\u3000\u5e90\u4e91\u634b\u4e86\u634b\u4e94\u7efa\u957f\u987b\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u5fc5\uff0c\u5982\u679c\u4f60\u613f\u610f\uff0c\u5c31\u7531\u6211\u6682\u65f6\u4e3a\u4f60\u51b3\u5b9a\u4e00\u4e2a \u59d3\uff0c\u7b49\u5c06\u6765\u548c\u4ee4\u5c0a\u76f8\u89c1\u540e\uff0c\u518d\u8ba4\u59d3\u5f52\u5b97\u3002\u201d \u3000\u3000\u201c\u4e0d\u77e5\u524d\u8f88\u4e3a\u665a\u8f88\u53d6\u7684\u4ec0\u4e48\u59d3\uff1f\u201d \u3000\u3000\u201c\u4f60\u53eb\u98de\u513f\uff0c\u540d\u5b57\u4e0d\u59a8\u5c31\u53eb\u5c0f\u98de\uff0c\u7528\u4e0d\u7740\u6539\u4e86\uff0c\u81f3\u4e8e\u59d3\u4ec0\u4e48\uff0c\u5728\u4e2d\u56fd\u4ee5\u98de\u4e3a\u540d\u7684\u4eba\u5f88\u591a\uff0c \u6211\u60f3\u5c31\u5728\u8fd9\u4e9b\u540d\u4eba\u5f53\u4e2d\u53d6\u4e00\u4e2a\u59d3\u3002\u201d \u3000\u3000\u201c\u524d\u8f88\u8bf7\u8bf4\uff01\u201d \u3000\u3000\u201c\u5728\u524d\u4eba\u4e2d\u4ee5\u98de\u4e3a\u540d\u5b57\uff0c\u6700\u6709\u540d\u7684\u4fbf\u662f\u5f20\u98de\u548c\u5cb3\u98de\uff0c\u4ed6\u4eec\u4e0d\u4f46\u662f\u540d\u5c06\uff0c\u800c\u4e14\u662f\u76d6\u4e16\u5fe0 \u826f\u2026\u2026\u201d \u3000\u3000\u5ffd\u542c\u8881\u5c0f\u9e64\u63d2\u5634\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u5c0f\u5144\u5f1f\u5c31\u53eb\u5f20\u5c0f\u98de\u5427\uff01\u201d \u3000\u3000\u5e90\u4e91\u77aa\u4e86\u8881\u5c0f\u9e64\u4e00\u773c\u9053\uff1a\u201c\u4f60\u61c2\u4ec0\u4e48\uff1f\u63d2\u7684\u4ec0\u4e48\u5634\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u7acb\u523b\u4fef\u9996\u8eac\u8eab\uff0c\u4e0d\u6562\u542d\u58f0\u3002 \u3000\u3000\u5e90\u4e91\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u770b\u4f60\u7684\u6a21\u6837\uff0c\u7709\u6e05\u76ee\u79c0\uff0c\u82f1\u6c14\u723d\u723d\uff0c\u5728\u60f3\u8c61\u4e2d\uff0c\u5012\u6709\u4e9b\u8fd1\u4f3c\u5cb3\u98de\u4e4b\u540e\uff0c \u5c31\u53eb\u5cb3\u5c0f\u98de\u5982\u4f55\uff1f\u201d \u3000\u3000\u98de\u513f\u65e9\u5c31\u542c\u8bf4\u8fc7\u5cb3\u98de\u7684\u6545\u4e8b\uff0c\u5bf9\u8fd9\u4f4d\u7cbe\u5fe0\u62a5\u56fd\u7684\u4e00\u4ee3\u5fe0\u826f\u6700\u5d07\u62dc\uff0c\u542c\u5e90\u4e91\u4e3a\u81ea\u5df1\u53d6\u540d\u5cb3 \u5c0f\u98de\uff0c\u5f53\u7136\u6700\u9ad8\u5174\u4e0d\u8fc7\uff0c\u8fde\u5fd9\u53c8\u62dc\u5012\u5728\u5730\u9053\uff1a\u201c\u665a\u8f88\u591a\u8c22\u524d\u8f88\u8d50\u59d3\u547d\u540d\uff01\u201d \u3000\u3000\u5e90\u4e91\u62ac\u624b\u6276\u8d77\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u60f3\u6765\u4ee4\u5c0a\u4ee4\u5802\uff0c\u5fc5\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u4ece\u660e\u5929\u8d77\uff0c\u4f60\u5c31\u5728\u8fd9\u91cc\uff0c\u4e00 \u8fb9\u8bfb\u4e66\uff0c\u4e00\u8fb9\u4e60\u6b66\uff0c\u4e0d\u51fa\u4e09\u5e74\uff0c\u6211\u5b9a\u4f1a\u8ba9\u4f60\u51fa\u4eba\u5934\u5730\uff01\u201d \u3000\u3000\u7ad9\u5728\u4e00\u65c1\u7684\u8881\u5c0f\u9e64\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u53eb\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u8fd8\u4e0d\u5feb\u5feb\u53e9\u8c22\u5e08\u7236\uff01\u201d \u3000\u3000\u5c82\u77e5\u5e90\u4e91\u987f\u73b0\u9762\u8272\u51dd\u91cd\uff0c\u6025\u6025\u4f38\u624b\u4e00\u62e6\u9053\uff1b\u201c\u6211\u5df2\u7acb\u8a93\u4eca\u751f\u4e0d\u518d\u6536\u5f92\uff0c\u6211\u867d\u7136\u7b54\u5e94\u6559\u4f60 \u6587\u4e8b\u6b66\u529f\uff0c\u4f46\u5374\u51b3\u4e0d\u505a\u4f60\u7684\u5e08\u7236\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u89c1\u5e90\u4e91\u8bed\u6c14\u575a\u5b9a\u800c\u53c8\u90d1\u91cd\uff0c\u53ea\u597d\u53eb\u4e86\u4e00\u58f0\u201c\u5e90\u4f2f\u4f2f\u201d\uff0c\u4e0d\u6562\u786c\u884c\u8ba4\u5e08\u3002 \u3000\u3000\u4ed6\u4e4b\u6240\u4ee5\u6539\u53e3\u5bf9\u5e90\u4e91\u4ee5\u4f2f\u4f2f\u76f8\u79f0\uff0c\u5f53\u7136\u662f\u89c9\u5f97\u8fd9\u6837\u6bd4\u8f83\u4eb2\u5207\u3002 \u3000\u3000\u539f\u6765\u5e90\u4e91\u65b9\u624d\u8bf4\u4e0d\u51fa\u4e09\u5e74\uff0c\u4fbf\u80fd\u628a\u5cb3\u5c0f\u98de\u8c03\u6559\u5f97\u51fa\u4eba\u5934\u5730\uff0c\u662f\u6709\u6839\u636e\u7684\uff0c\u4ed6\u6253\u548c\u5cb3\u5c0f\u98de \u4e00\u89c1\u9762\u8d77\uff0c\u4fbf\u89c9\u51fa\u8fd9\u5b69\u5b50\u9aa8\u5947\u795e\u6e05\uff0c\u662f\u5757\u7ec3\u6b66\u7684\u96be\u5f97\u4e4b\u624d\uff0c\u5c24\u5176\u667a\u6167\u9ad8\u4eba\u4e00\u7b49\uff0c\u8d85\u8fc7\u4ed6\u7684\u5e74 \u9f84\u751a\u591a\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u5f53\u524d\u4e09\u5929\u5cb3\u5c0f\u98de\u6bcf\u6b21\u65bd\u5c55\u8f7b\u529f\u6500\u767b\u72ec\u79c0\u5cf0\u65f6\uff0c\u4ed6\u90fd\u5728\u80cc\u540e\u7ec6\u5fc3\u89c2\u5bdf\uff0c\u5df2\u77e5\u4ed6\u4e0d\u4f46 \u65e9\u6709\u6b66\u529f\u6839\u57fa\uff0c\u800c\u4e14\u76ee\u524d\u5c31\u8db3\u53ef\u4ee5\u8dfb\u8eab\u4e00\u6d41\u9ad8\u624b\u4e4b\u6797\uff0c\u82e5\u518d\u52a0\u4ee5\u8c03\u6559\uff0c\u5f53\u7136\u5c31\u4e8b\u534a\u529f\u500d\u4e86\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5f00\u59cb\u968f\u5723\u624b\u4e66\u751f\u4fee\u6587\u4e60\u6b66\uff0c\u4ed6\u7684\u53d1\u6124\u7528\u529f\u7a0b\u5ea6\uff0c\u8fde\u5e90\u4e91\u4e5f\u4e3a\u4e4b\u60ca\u4f69\u4e0d\u5df2\u3002 \u3000\u3000\u5e90\u4e91\u6240\u4ee5\u80fd\u88ab\u6b66\u6797\u4e2d\u4eba\u79f0\u4e3a\u5723\u624b\u4e66\u751f\uff0c\u5f53\u7136\u9664\u4e86\u6b66\u529f\u9ad8\u7edd\u4e4b\u5916\uff0c\u5728\u6587\u4e8b\u65b9\u9762\uff0c\u4e5f\u662f\u535a\u5b66 \u591a\u624d\uff0c\u7434\u3001\u68cb\u3001\u4e66\u3001\u753b\uff0c\u53ef\u8c13\u65e0\u4e00\u4e0d\u7cbe\u3002\u50cf\u8fd9\u6837\u6587\u6b66\u517c\u8d44\u7684\u9ad8\u4eba\uff0c\u5728\u6b66\u6797\u4e2d\u79f0\u5f97\u4e0a\u662f\u767e\u5e74\u96be \u89c1\u3002 \u3000\u3000\u4f46\u6709\u8c01\u77e5\u9053\uff0c\u8fd1\u5341\u5e74\u6765\uff0c\u4ed6\u5374\u4e00\u76f4\u5728\u542b\u57a2\u5fcd\u8fb1\u3001\u6092\u90c1\u96be\u4f38\u7684\u72b6\u6001\u4e2d\u8fc7\u7740\u75db\u82e6\u4e0d\u582a\u7684\u9690\u5c45 \u751f\u6d3b\u3002 \u3000\u3000\u5927\u7ea6\u4e09\u4e2a\u6708\u540e\uff0c\u4e00\u5929\u665a\u9910\u521a\u8fc7\uff0c\u5cb3\u5c0f\u98de\u6b63\u5728\u5ba4\u5185\u72ec\u5750\u6c89\u601d\uff0c\u8881\u5c0f\u9e64\u5ffd\u7136\u95ef\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u8fd9\u4e09\u4e2a\u6708\u6765\uff0c\u5df2\u548c\u5cb3\u5c0f\u98de\u4eb2\u82e5\u5144\u5f1f\uff0c\u4ed6\u95ef\u8fdb\u51fa\u5cb3\u5c0f\u98de\u5ba4\u5185\u672c\u6765\u662f\u5e38\u4e8b\uff0c\u4f46\u4eca\u665a\u5374 \u663e\u5f97\u795e\u8272\u5927\u5927\u4e0d\u540c\u3002 \u3000\u3000\u201c\u8881\u5927\u54e5\u6709\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e0d\u7b54\u53cd\u95ee\uff1a\u201c\u5c0f\u98de\uff0c\u4f60\u60f3\u4e0d\u60f3\u5ff5\u4f60\u7684\u7236\u6bcd\uff1f\u60f3\u4e0d\u60f3\u77e5\u9053\u4f60\u7684\u8eab\u4e16\uff1f\u201d \u3000\u3000\u8fd9\u7a81\u5982\u5176\u6765\u7684\u95ee\u8bdd\uff0c\u4f3c\u4e4e\u628a\u5cb3\u5c0f\u98de\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u4e3a\u4eba\u5b50\u54ea\u6709\u4e0d\u601d\u5ff5\u7236\u6bcd\u4eb2\u7684\uff0c\u8fd9\u51e0\u4e2a\u6708\u6765\uff0c\u867d\u7136\u5e90\u4e91\u548c\u8881\u5c0f\u9e64\u5f85\u4ed6\u4eb2\u82e5\u7236\u5144\uff0c\u8ba9\u4ed6\u8fc7\u5f97 \u5f88\u5feb\u4e50\uff0c\u4f46\u6bcf\u4e00\u601d\u53ca\u7236\u6bcd\uff0c\u4ed6\u4ecd\u96be\u514d\u6697\u5730\u843d\u6cea\uff0c\u5c24\u5176\u591c\u6df1\u4eba\u9759\u72ec\u5904\u5ba4\u5185\uff0c\u66f4\u662f\u7ecf\u5e38\u4ee5\u6cea\u6d17\u9762\u3002 \u3000\u3000\u5982\u4eca\u8881\u5c0f\u9e64\u8fd9\u6ca1\u5934\u6ca1\u8111\u7684\u8bdd\uff0c\u4ed6\u771f\u7684\u4e0d\u77e5\u662f\u60ca\u662f\u559c\u3002 \u3000\u3000\u201c\u8881\u5927\u54e5\uff0c\u83ab\u975e\u5e90\u4f2f\u4f2f\u5df2\u7ecf\u6709\u4e86\u6211\u7239\u5a18\u7684\u6d88\u606f\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6447\u5934\u9053\uff1a\u201c\u5e08\u7236\u6839\u672c\u4e0d\u77e5\u9053\u4ee4\u5c0a\u4ee4\u5802\u662f\u51c6\uff0c\u600e\u4f1a\u6709\u4ec0\u4e48\u6d88\u606f\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8d8a\u53d1\u6478\u4e0d\u7740\u5934\u8111\uff1a\u201c\u90a3\u4e48\u4f60\u521a\u624d\u7684\u8bdd\uff1f\u2026\u2026\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u6211\u662f\u8bf4\u5e08\u7236\u8981\u5e26\u4f60\u53bb\u89c1\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4eba\u5fc5\u5b9a\u77e5\u9053\u4ee4\u5c0a\u4ee4\u5802\u662f\u8c01\uff0c\u77e5\u9053\u4e86\u4ee4\u5c0a \u4ee4\u5802\u662f\u8c01\uff0c\u4f60\u7684\u8eab\u4e16\uff0c\u4e0d\u662f\u81ea\u7136\u5c31\u660e\u767d\u4e86\u5417\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6025\u6025\u95ee\u9053\uff1b\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6d1e\u4ed9\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53c8\u662f\u4e00\u6123\uff1a\u201c\u4ec0\u4e48\u53eb\u6d1e\u4ed9\uff1f\u201d \u3000\u3000\u201c\u6d1e\u4ed9\u5c31\u662f\u4f4f\u5728\u5c71\u6d1e\u7684\u795e\u4ed9\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8d8a\u53d1\u88ab\u5f04\u7cca\u6d82\u4e86\uff1a\u201c\u4eba\u4e16\u95f4\u771f\u7684\u6709\u795e\u4ed9\uff1f\u201d \u3000\u3000\u201c\u4ed6\u662f\u4e0d\u662f\u771f\u795e\u4ed9\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u4e0d\u8fc7\u4eba\u5bb6\u90fd\u8fd9\u6837\u53eb\u4ed6\u3002\u201d \u3000\u3000\u201c\u4ed6\u6709\u540d\u6709\u59d3\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4eba\u8bba\u5e74\u7eaa\u662f\u4e03\u8001\u516b\u5341\u4e86\uff0c\u66f4\u6709\u4eba\u8bf4\u4ed6\u53ef\u80fd\u5df2\u662f\u4e0a\u767e\u9ad8\u9f84\uff0c\u4f46\u81f3\u4eca\u5374\u90fd\u4e0d\u77e5\u9053\u4ed6\u59d3\u4ec0 \u4e48\u53eb\u4ec0\u4e48\u3002\u201d \u3000\u3000\u201c\u4ed6\u53c8\u600e\u80fd\u8ba4\u8bc6\u5bb6\u7236\u5bb6\u6bcd\u5462\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u89c1\u4e86\u4ee4\u5c0a\u4ee4\u5802\uff0c\u4ed6\u4e5f\u8bb8\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u7684\u8bdd\uff0c\u7b80\u76f4\u4ee4\u4eba\u83ab\u6d4b\u9ad8\u6df1\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6b63\u8981\u95ee\uff0c\u8881\u5c0f\u9e64\u518d\u9053\uff1a\u201c\u4f60\u95ee\u6211\uff0c\u6211\u4e5f\u8bf4\u4e0d\u6e05\u695a\uff0c\u6211\u4e0d\u6253\u6405\u4e86\uff0c\u597d\u597d\u7761\u4e00\u89c9\uff0c\u4e5f \u8bb8\u660e\u5929\u5e08\u7236\u5c31\u4f1a\u5e26\u4f60\u53bb\u89c1\u6d1e\u4ed9\uff0c\u5230\u4e86\u65f6\u95f4\uff0c\u4f60\u5c31\u4e00\u5207\u90fd\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e00\u591c\uff1f\u5cb3\u5c0f\u6b63\u53cd\u800c\u6ca1\u5f97\u597d\u7761\u3002 \u3000\u3000\u4e0d\u96be\u60f3\u8c61\uff0c\u4ed6\u662f\u88ab\u8881\u5c0f\u9e64\u7684\u8bdd\uff0c\u5f04\u5f97\u5fc3\u91cc\u4e03\u4e0a\u516b\u4e0b\u3002 \u3000\u3000\u4f46\u4e0d\u7ba1\u5982\u4f55\uff0c\u5728\u4ed6\u6765\u8bf4\uff0c\u603b\u662f\u6709\u4e86\u4e00\u4efd\u5e0c\u671b\u3002 \u3000\u3000\u6b21\u65e5\u65e9\u9910\u540e\uff0c\u5e90\u4e91\u679c\u7136\u5bf9\u4ed6\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u4eca\u5929\u4e0d\u5fc5\u8bfb\u4e66\u7ec3\u529f\u4e86\uff0c\u6211\u8981\u5e26\u4f60\u53bb\u89c1\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1b\u201c\u662f\u4e0d\u662f\u53bb\u89c1\u6d1e\u4ed9\uff1f\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u662f\u8881\u5927\u54e5\u6628\u665a\u544a\u8bc9\u6211\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u63d0\u524d\u544a\u8bc9\u4e86\u4f60\u4e5f\u597d\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u5e76\u4e0d\u6e05\u695a\u6d1e\u4ed9\u600e\u80fd\u77e5\u9053\u6211\u7684\u7236\u6bcd\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u5f88\u96be\u8bf4\uff0c\u6211\u60f3\u4ed6\u81f3\u5c11\u6709\u4e03\u516b\u6210\u628a\u63e1\uff0c\u73b0\u5728\u5c31\u52a8\u8eab\u5427\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u968f\u5373\u5e26\u4e0a\u4eca\u5929\u4e00\u65e9\u5c31\u51c6\u5907\u597d\u7684\u5e72\u7cae\u3002 \u3000\u3000\u5e90\u4e91\u672c\u6765\u51c6\u5907\u8981\u8881\u5c0f\u9e64\u8d1f\u8d23\u770b\u5bb6\uff0c\u4f46\u7981\u4e0d\u4f4f\u8881\u5c0f\u9e64\u82e6\u82e6\u8877\u6c42\uff0c\u53ea\u597d\u4e5f\u5e26\u4ed6\u540c\u884c\u3002 \u3000\u3000\u8def\u4e0a\uff0c\u5cb3\u5c0f\u98de\u4e0d\u6562\u5411\u5e90\u4e91\u591a\u95ee\uff0c\u53ea\u597d\u95ee\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0c\u6709\u591a\u8fdc\u7684\u8def\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5927\u7ea6\u5929\u665a\u53ef\u4ee5\u8d76\u5230\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u5e26\u5e72\u7cae\u5462\uff0c\u96be\u9053\u8fde\u8352\u6751\u91ce\u5e97\u4e5f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e00\u8def\u5168\u662f\u5c71\u8def\uff0c\u6839\u672c\u770b\u4e0d\u5230\u4eba\u7c7b\uff0c\u4e0d\u5e26\u5e72\u7cae\u600e\u4e48\u6210\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u518d\u591a\u95ee\u3002 \u3000\u3000\u4e2d\u5348\uff0c\u4e09\u4eba\u5c31\u5728\u5c71\u91ce\u95f4\u5403\u4e86\u4e00\u9910\u5e72\u7cae\uff0c\u7136\u540e\u7ee7\u7eed\u8d76\u8def\u3002 \u3000\u3000\u4e00\u8def\u4e4b\u4e0a\uff0c\u679c\u5982\u8881\u5c0f\u9e64\u6240\u8a00\uff0c\u8fc7\u4e00\u5c71\uff0c\u53c8\u4e00\u5cad\uff0c\u5c42\u5ce6\u53e0\u5d82\uff0c\u65e0\u6daf\u65e0\u5c3d\uff0c\u82e5\u4e0d\u662f\u5e90\u4e91\u5728\u524d \u5e26\u8def\uff0c\u8fde\u66fe\u7ecf\u8d70\u8fc7\u6b64\u8def\u7684\u8881\u5c0f\u9e64\u90fd\u6478\u4e0d\u6e05\u65b9\u5411\u3002 \u3000\u3000\u770b\u770b\u5929\u8272\u5c06\u665a\uff0c\u5cb3\u5c0f\u98de\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u8881\u5927\u54e5\uff0c\u8fd8\u6709\u591a\u8fdc\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6f2b\u58f0\u5e94\u9053\uff1a\u201c\u5927\u6982\u5c31\u5feb\u5230\u4e86\u3002\u201d \u3000\u3000\u663e\u7136\uff0c\u8881\u5c0f\u9e64\u4e5f\u771f\u7684\u6655\u5934\u8f6c\u5411\u3002 \u3000\u3000\u76f4\u5230\u5e90\u4e91\u5728\u4e00\u5904\u5c71\u58c1\u524d\u505c\u4e0b\uff0c\u8881\u5c0f\u9e64\u624d\u60ca\u559c\u7684\u671b\u4e86\u5cb3\u5c0f\u98de\u4e00\u773c\u9053\uff1a\u201c\u5230\u4e86\uff0c\u5c31\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6e38\u76ee\u56db\u987e\uff0c\u5374\u5e76\u672a\u53d1\u73b0\u4efb\u4f55\u6d1e\u53e3\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4e0d\u4fbf\u53d1\u95ee\uff0c\u4ee5\u514d\u88ab\u4eba\u8ba4\u4e3a\u5c11\u89c1\u591a\u602a\u3002 \u3000\u3000\u53ea\u89c1\u5e90\u4e91\u62ac\u624b\u5728\u77f3\u58c1\u4e0a\u63a5\u8fde\u6572\u4e86\u4e09\u4e0b\u3002 \u3000\u3000\u5f88\u5feb\u4fbf\u9690\u9690\u542c\u5230\u7531\u5c71\u58c1\u5185\u4f20\u6765\u811a\u6b65\u58f0\u3002 \u3000\u3000\u58f0\u97f3\u5c45\u7136\u7531\u5c71\u58c1\u5185\u4f20\u51fa\uff0c\u8fd9\u5728\u5cb3\u5c0f\u98de\u6765\u8bf4\uff0c\u600e\u80fd\u4e0d\u611f\u5230\u60ca\u5947\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u662f\u4e0d\u4fbf\u591a\u95ee\u3002 \u3000\u3000\u811a\u6b65\u58f0\u8d8a\u6765\u8d8a\u8fd1\uff0c\u800c\u4e14\u542c\u6765\u6b65\u5c65\u5341\u5206\u8f7b\u5feb\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4e00\u9635\u8f67\u8f67\u4e4b\u58f0\u8fc7\u540e\uff0c\u90a3\u77f3\u58c1\u5c45\u7136\u88c2\u5f00\u4e00\u4e2a\u6d1e\u95e8\u3002\u6d1e\u95e8\u4e2d\u51fa\u73b0\u4e86\u4e00\u4e2a\u5934\u624e\u53cc\u8fab\u7684\u9752 \u8863\u7ae5\u5b50\u3002 \u3000\u3000\u9752\u8863\u7ae5\u5b50\u5b9a\u4e86\u5b9a\u795e\uff0c\u7acb\u523b\u53eb\u9053\uff1a\u201c\u539f\u6765\u662f\u5e90\u5927\u4fa0\uff0c\u60a8\u600e\u4e48\u6709\u7a7a\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u5e90\u4e91\u7b11\u9053\uff1b\u201c\u6211\u5230\u8fd9\u91cc\u6765\u4e5f\u4e0d\u662f\u4e00\u6b21\u4e24\u6b21\u4e86\uff0c\u8001\u4ed9\u7fc1\u5728\u6d1e\u5e9c\u4e48\uff1f\u201d \u3000\u3000\u9752\u8863\u7ae5\u5b50\u8fb9\u95ea\u8eab\u8083\u5ba2\u8fb9\u9053\uff1a\u201c\u60a8\u8001\u4eba\u5bb6\u4ec0\u4e48\u65f6\u5019\u6765\u65f6\u89c1\u4ed6\u4e0d\u5728\u6d1e\u4e2d\u8fc7\uff1f\u201d \u3000\u3000\u5e90\u4e91\u9053\uff1a\u201c\u4ed6\u5728\u6d1e\u91cc\u5c31\u597d\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u8ff3\u81ea\u8fdb\u6d1e\u5f53\u5148\u5e26\u5934\u524d\u8fdb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u968f\u8eab\u5728\u540e\u3002 \u3000\u3000\u6d1e\u9053\u5185\u5e76\u65e0\u706f\u706b\uff0c\u8fde\u767d\u5929\u90fd\u9690\u6697\u5982\u6f06\uff0c\u6b64\u523b\u5929\u8272\u5df2\u665a\uff0c\u66f4\u662f\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\u3002 \u3000\u3000\u8fd9\u6761\u5730\u9053\u5f53\u771f\u6df1\u9083\uff0c\u76f4\u5230\u8fdb\u4e86\u5341\u4f59\u4e08\uff0c\u4ecd\u7136\u672a\u5230\u5c3d\u5934\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u96be\u60f3\u5230\uff0c\u8fd9\u6761\u5730\u9053\u5982\u679c\u7531\u4eba\u5de5\u5f00\u51ff\uff0c\u4e5f\u7b97\u5f97\u4e0a\u662f\u9879\u6d69\u5927\u5de5\u7a0b\u4e86\u3002 \u3000\u3000\u5c82\u77e5\u53c8\u524d\u8fdb\u4e86\u5341\u51e0\u4e08\uff0c\u8fd8\u662f\u672a\u5230\u5c3d\u5934\u3002 \u3000\u3000\u4f30\u8ba1\u7740\u6015\u53ea\u662f\u5df2\u8fdb\u5165\u4e86\u5c71\u8179\u5730\u5e95\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u867d\u4e0d\u80fd\u770b\u6e05\u6d1e\u5185\u60c5\u666f\uff0c\u5374\u611f\u89c9\u5f97\u51fa\u5730\u9053\u5185\u5341\u5206\u5bbd\u9614\uff0c\u5373\u4fbf\u4e09\u56db\u4eba\u5e76\u80a9\u800c\u884c\uff0c\u4e5f\u6beb \u65e0\u963b\u788d\u3002 \u3000\u3000\u4e0d\u4e45\u4e4b\u540e\uff0c\u603b\u7b97\u5230\u4e86\u5c3d\u5934\uff0c\u4f46\u5e90\u4e91\u5374\u53c8\u5de6\u8f6c\u524d\u884c\u3002 \u3000\u3000\u539f\u6765\u5e76\u975e\u5c3d\u5934\uff0c\u800c\u662f\u6298\u8f6c\u4e86\u4e00\u4e2a\u65b9\u5411\u3002 \u3000\u3000\u5728\u611f\u89c9\u4e0a\u53c8\u662f\u5f88\u4e45\u5f88\u4e45\uff0c\u624d\u8fdb\u5165\u4e86\u4e00\u95f4\u5de8\u5927\u77f3\u5ba4\u3002 \u3000\u3000\u5e90\u4e91\u505c\u4e0b\u811a\u6b65\u9053\uff1a\u201c\u9752\u513f\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u70b9\u4e0a\u706f\u5462\uff1f\u201d \u3000\u3000\u9752\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u8001\u7237\u5b50\u4e0d\u51c6\uff0c\u70b9\u4e0a\u706f\u4f1a\u5f71\u54cd\u4ed6\u8001\u4eba\u5bb6\u7684\u6e05\u4fee\u3002\u201d \u3000\u3000\u9752\u8863\u7ae5\u5b50\u8bf4\u662f\u8fd9\u6837\u8bf4\uff0c\u4f46\u56e0\u5e90\u4e91\u662f\u8001\u7237\u5b50\u7684\u719f\u5ba2\uff0c\u72b9\u8c6b\u4e86\u534a\u664c\uff0c\u8fd8\u662f\u4e3b\u52a8\u628a\u706f\u70b9\u4e0a\u4e86\u3002 \u3000\u3000\u706f\u4e00\u4eae\uff0c\u7167\u89c1\u8fd9\u77f3\u5ba4\u7684\u786e\u5f88\u5927\uff0c\u8db3\u6709\u4e03\u516b\u4e08\u65b9\u5706\u3002 \u3000\u3000\u77f3\u5ba4\u5185\u684c\u6905\u4ff1\u5168\uff0c\u8336\u51e0\u4e0a\u8fd8\u6446\u7740\u73b0\u6210\u7684\u8336\u58f6\u8336\u7897\u3002 \u3000\u3000\u9752\u8863\u7ae5\u5b50\u4e3a\u5ba2\u4eba\u5404\u5012\u4e86\u4e00\u7897\u8336\uff0c\u7136\u540e\u7ad9\u5728\u4e00\u65c1\uff0c\u5e76\u6765\u5f00\u53e3\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u770b\u4e0d\u89c1\u6d1e\u4ed9\uff0c\u5cb3\u5c0f\u98de\u5f53\u7136\u6709\u4e9b\u7eb3\u95f7\u3002 \u3000\u3000\u597d\u5728\u4ed6\u5f88\u5feb\u4fbf\u53d1\u73b0\u77f3\u58c1\u4e0a\u6709\u597d\u51e0\u5904\u53ef\u4ee5\u5f00\u542f\u7684\u6d1e\u95e8\uff0c\u4e0d\u6d88\u8bf4\uff0c\u6d1e\u95e8\u5185\u53e6\u6709\u77f3\u5ba4\uff0c\u6d1e\u4ed9\u4e00 \u5b9a\u5728\u5176\u4e2d\u4e00\u95f4\u77f3\u5ba4\u91cc\u3002 \u3000\u3000\u5e90\u4e91\u559d\u4e86\u53e3\u8336\uff0c\u8f7b\u58f0\u95ee\u9053\uff1b\u201c\u8001\u4ed9\u7fc1\u662f\u5728\u91cc\u9762\u6253\u5750\uff1f\u8fd8\u662f\u7761\u7740\u4e86\uff1f\u201d \u3000\u3000\u4e0d\u7b49\u9752\u8863\u7ae5\u5b50\u7b54\u8bdd\uff0c\u4fbf\u7531\u4e00\u95f4\u865a\u63a9\u7684\u6d1e\u95e8\u5185\u4f20\u51fa\u4e86\u8bdd\u58f0\u3002 \u3000\u3000\u5176\u5b9e\u8fd9\u58f0\u97f3\u4e0d\u8db3\u8bf4\u6d3b\uff0c\u800c\u662f\u5728\u541f\u5531\u7740\u4e00\u9996\u8bd7\uff1a \u3000\u3000\u201c\u6d1e\u4e2d\u96be\u89c1\u65e5\uff0c \u3000\u3000\u663c\u591c\u7686\u4e00\u822c\u3002 \u3000\u3000\u4e0d\u8bba\u5750\u4e0e\u5367\uff0e \u3000\u3000\u540c\u6837\u662f\u795e\u4ed9\u3002\u201d \u3000\u3000\u8fd9\u9996\u8bd7\u4fe1\u53e3\u541f\u6765\uff0c\u5f88\u6709\u70b9\u50cf\u5f53\u5e74\u5218\u5907\u4e09\u987e\u8305\u5e90\u65f6\u8bf8\u845b\u4eae\u5728\u8349\u5ba4\u7761\u5367\u65b9\u8d77\u6240\u541f\u7684\u8bd7\u53e5\uff0c\u800c \u4ed6\u8fd9\u8bd7\u5f00\u5934\u548c\u6700\u540e\u4e24\u4e2a\u5b57\u6070\u597d\u662f\u201c\u6d1e\u4ed9\u201d\uff0c\u7b49\u4e8e\u8868\u660e\u4e86\u8eab\u4efd\uff0c\u4e5f\u53ef\u77e5\u4ed6\u5df2\u542c\u5230\u4e86\u6709\u4eba\u6765\u8bbf\u3002 \u3000\u3000\u5e90\u4e91\u8fde\u5fd9\u7ad9\u8d77\u8eab\u6765\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u8001\u4ed9\u7fc1\uff0c\u665a\u8f88\u5e90\u4e91\u4e0d\u63e3\u5192\u6627\uff0c\u6253\u6405\u60a8\u7684\u6e05\u4fee\u4e86\uff01\u201d \u3000\u3000\u6d1e\u95e8\u5185\u53c8\u4f20\u51fa\u6d1e\u4ed9\u7684\u58f0\u97f3\u9053\uff1b\u201c\u542c\u58f0\u97f3\u4f60\u4eec\u6765\u7684\u597d\u50cf\u4e0d\u53ea\u4e00\u4e2a\uff0c\u53e6\u5916\u4fe9\u4eba\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u5e90\u4e91\u8f7b\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u665a\u8f88\u7684\u665a\u8f88\uff0c\u7b97\u4e0d\u5f97\u5ba2\u4eba\u3002\u201d \u3000\u3000\u6d1e\u4ed9\u9053\uff1a\u201c\u90a3\u5c31\u8bf7\u4f60\u4eec\u7b49\u2014\u7b49\uff0c\u8001\u673d\u8fd8\u6709\u9053\u529f\u8bfe\u6ca1\u6709\u4f5c\u5b8c\u3002\u201d \u3000\u3000\u5927\u77f3\u6d1e\u5185\u5f00\u59cb\u6c89\u5bc2\uff0e\u4f3c\u4e8e\u8c01\u90fd\u4e0d\u6562\u518d\u53d1\u51fa\u534a\u70b9\u58f0\u97f3\u3002 \u3000\u3000\u8db3\u8db3\u76cf\u8336\u5de5\u592b\u8fc7\u53bb\uff0c\u4f9d\u7136\u4e0d\u89c1\u6d1e\u4ed9\u51fa\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5b9e\u5728\u7981\u4e0d\u4f4f\u597d\u5947\uff0c\u4fbf\u8f7b\u8f7b\u8d70\u5230\u90a3\u534a\u63a9\u6d1e\u95e8\u7684\u6d1e\u53e3\u5916\u3002 \u3000\u3000\u91cc\u9762\u6d1e\u5185\u867d\u672a\u70b9\u706f\uff0c\u4f46\u56e0\u6709\u5916\u9762\u706f\u5149\u5c04\u8fdb\uff0c\u518d\u52a0\u4ed6\u76ee\u529b\u5f02\u4e8e\u5e38\u4eba\uff0c\u89e6\u76ee\u4e4b\u4e0b\uff0c\u4f7f\u4ed6\u9669\u4e9b \u60ca\u547c\u51fa\u58f0\u3002 \u3000\u3000\u53ea\u89c1\u91cc\u9762\u7adf\u662f\u505c\u653e\u7740\u4e00\u53e3\u5de8\u5927\u7684\u68fa\u6750\u3002 \u3000\u3000\u90a3\u68fa\u6750\u56e0\u4e3a\u5e95\u90e8\u5df2\u7ecf\u57ab\u8d77\uff0c\u867d\u7136\u68fa\u76d6\u6253\u5f00\uff0c\u5cb3\u5c0f\u98de\u5374\u65e0\u6cd5\u770b\u5230\u91cc\u9762\u7684\u4e00\u5207\u3002 \u3000\u3000\u8c01\u77e5\u5728\u8fd9\u65f6\uff0c\u7a81\u89c1\u68fa\u5185\u5192\u7740\u4e00\u4e2a\u76f4\u633a\u633a\u8eba\u7740\u7684\u4eba\u6765\u3002 \u3000\u3000\u4e00\u4e2a\u4eba\u5c45\u7136\u80fd\u7531\u68fa\u5185\u50cf\u50f5\u5c38\u822c\u5e73\u5347\u800c\u8d77\uff0c\u8fd9\u4efd\u529f\u529b\uff0c\u771f\u591f\u5f97\u4e0a\u662f\u795e\u4ed9\u4e86\u3002\u5cb3\u5c0f\u98de\u5982\u679c\u4e0d \u662f\u5df2\u77e5\u662f\u6d1e\u4ed9\uff0c\u5b9a\u4f1a\u4ee5\u4e3a\u662f\u5c38\u53d8\u6216\u5996\u9b54\u51fa\u73b0\u3002 \u3000\u3000\u4ed6\u6765\u4e0d\u53ca\u518d\u770b\u6d1e\u4ed9\u5982\u4f55\u843d\u5730\uff0c\u8fde\u5fd9\u5feb\u6b65\u8d70\u56de\u539f\u5904\uff0c\u5728\u5e90\u4e91\u8eab\u65c1\u5750\u4e0b\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u534a\u664c\uff0c\u7ec8\u4e8e\u7531\u6d1e\u95e8\u5185\u65e0\u58f0\u65e0\u606f\u7684\u8d70\u51fa\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u4eba\u5934\u5927\u5982\u6597\uff0c\u6ee1\u5934\u767d\u53d1\uff0c\u76f4\u5782\u53cc\u80a9\uff0c\u8fde\u9762\u5b54\u4e5f\u63a9\u53bb\u4e00\u5927\u534a\uff0c\u52a0\u4e0a\u4e24\u9053\u767d\u7709\u4e5f\u957f\u6709 \u6570\u5bf8\uff0c\u8ba9\u4eba\u8d8a\u53d1\u770b\u4e0d\u6e05\u4ed6\u7684\u9762\u8c8c\uff0c\u5f53\u7136\uff0c\u66f4\u770b\u4e0d\u51fa\u4ed6\u7a76\u7adf\u6709\u591a\u5927\u5e74\u7eaa\u3002 \u3000\u3000\u81f3\u4e8e\u4ed6\u7684\u8eab\u4e0a\uff0c\u5219\u662f\u4e00\u88ad\u5bbd\u5927\u7684\u9ed1\u888d\uff0c\u8170\u7cfb\u7740\u9ec4\u8272\u4e1d\u7f15\uff0c\u811a\u4e0b\u7a7f\u4e00\u53cc\u591a\u8033\u9ebb\u978b\u3002 \u3000\u3000\u8fd9\u6a21\u6837\u7b80\u76f4\u50cf\u662f\u65e0\u5e38\u9b3c\u51fa\u73b0\uff0c\u82e5\u4e0d\u77e5\u9053\u4ed6\u662f\u6d1e\u4ed9\uff0c\u76f8\u4fe1\u4efb\u4f55\u4eba\u89c1\u4e86\u4e5f\u4f1a\u6709\u6bdb\u9aa8\u609a\u7136\u4e4b\u611f\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u5e90\u4e91\u7acb\u523b\u7ad9\u8eab\u800c\u8d77\uff0c\u663e\u51fa\u4e00\u526f\u6bd5\u606d\u6bd5\u656c\u795e\u6001\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\uff0c\u4e5f\u90fd\u7d27\u968f\u7740\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u6d1e\u4ed9\u811a\u4e0b\u65e0\u58f0\u65e0\u606f\uff0c\u4ed6\u7f13\u7f13\u8d70\u51fa\u51e0\u6b65\uff0c\u5728\u4e00\u5f20\u77f3\u6905\u4e0a\u5750\u4e0b\u3002 \u3000\u3000\u5e90\u4e91\u8fde\u5fd9\u518d\u4e0a\u524d\u89c1\u793c\u3002 \u3000\u3000\u6d1e\u4ed9\u6478\u4e86\u6478\u5634\u5df4\u9053\uff1a\u201c\u539f\u6765\u4f60\u5e26\u6765\u7684\u662f\u4e24\u4e2a\u5c0f\u5b50\uff0c\u4e00\u4e2a\u662f\u4f60\u5f92\u5f1f\uff0c\u6211\u89c1\u8fc7\u3002\u53e6\u4e00\u4e2a\u597d\u50cf \u662f\u8d44\u8d28\u5f88\u662f\u4e0d\u9519\uff0c\u662f\u8c01\u5bb6\u7684\u5b69\u5b50\uff1f\u201d \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u5cb3\u5c0f\u98de\u53ea\u611f\u5230\u8fd9\u602a\u8001\u4eba\u7684\u6d53\u7709\u4e0b\u548c\u53d1\u9699\u95f4\uff0c\u4f3c\u662f\u5c04\u51fa\u4e24\u9053\u51b7\u7535\uff0c\u76f4\u80fd\u770b\u7a7f\u81ea \u5df1\u7684\u5fc3\u80ba\u4e00\u822c\u3002 \u3000\u3000\u5e90\u4e91\u91cd\u65b0\u843d\u5ea7\uff0c\u6b20\u8eab\u9053\uff1a\u201c\u665a\u8f88\u6b63\u662f\u4e3a\u8fd9\u5b69\u5b50\u6765\u62dc\u89c1\u8001\u4ed9\u7fc1\u7684\u3002\u201d \u3000\u3000\u6d1e\u4ed9\u201c\u55ef\u201d\u4e86\u58f0\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e8b\uff0c\u4f60\u5c31\u76f4\u8bf4\u5427\uff01\u201d \u3000\u3000\u201c\u8fd9\u5b69\u5b50\u4e0d\u77e5\u9053\u4ed6\u7684\u7236\u6bcd\u662f\u51c6\uff0c\u665a\u8f88\u60f3\u6c42\u8001\u4ed9\u7fc1\u66ff\u4ed6\u6307\u5f15\u4e00\u4e0b\u3002\u201d \u3000\u3000\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4e3a\u4eba\u5728\u4e16\uff0c\u54ea\u6709\u8fde\u7236\u6bcd\u90fd\u4e0d\u77e5\u9053\u662f\u8c01\u7684\uff0c\u9664\u975e\u751f\u4e0b\u6765\u4fbf\u88ab\u7236\u6bcd\u629b\u5f03\u3002\u201d \u3000\u3000\u201c\u4e0d\uff0c\u8fd9\u5b69\u5b50\u548c\u4ed6\u7684\u7236\u6bcd\u5206\u522b\u624d\u4e0d\u8fc7\u51e0\u4e2a\u6708\u3002\u201d \u3000\u3000\u201c\u8fd9\u5b69\u5b50\u591a\u5927\u4e86\uff1f\u201d \u3000\u3000\u201c\u5341\u5c81\u3002\u201d \u3000\u3000\u201c\u5c82\u6709\u6b64\u7406\uff0c\u6211\u770b\u8fd9\u5b69\u5b50\u7edd\u9876\u806a\u660e\uff0c\u51b3\u4e0d\u4f1a\u662f\u4e2a\u50bb\u74dc\u3002\u201d \u3000\u3000\u5e90\u4e91\u968f\u5373\u628a\u5176\u4e2d\u539f\u56e0\u8bf4\u4e86\u4e00\u904d\uff0c\u6700\u540e\u53c8\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u5b69\u5b50\u7684\u7236\u6bcd\uff0c\u5fc5\u5b9a\u4e5f\u662f\u6b66\u6797\u4e2d\u51fa\u7c7b \u62d4\u8403\u7684\u4eba\u7269\uff0c\u5982\u679c\u8001\u4ed9\u7fc1\u80fd\u65bd\u6cd5\u8ba9\u4ed6\u4eec\u73b0\u51fa\u8eab\u5f62\uff0c\u4e5f\u8bb8\u80fd\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u6d1e\u4ed9\u6c89\u541f\u4e86\u8bb8\u4e45\u9053\uff1b\u201c\u8fd9\u5b69\u5b50\u4e3a\u4ec0\u4e48\u4e0d\u95ee\u4ed6\u7684\u7236\u6bcd\uff1f\u201d \u3000\u3000\u4e0d\u7b49\u5e90\u4e91\u95ee\u7b54\uff0c\u5cb3\u5c0f\u98de\u4fbf\u62a2\u7740\u8eac\u8eab\u4e00\u793c\u9053\uff1a\u201c\u4e0d\u662f\u665a\u8f88\u6ca1\u95ee\u8fc7\u5bb6\u7236\u6bcd\uff0c\u800c\u662f\u5bb6\u7236\u6bcd\u4e0d\u80af \u8bb2\u3002\u201d \u3000\u3000\u6d1e\u4ed9\u9053\uff1a\u201c\u96be\u9053\u4ece\u4f60\u8bb0\u4e8b\u8d77\uff0c\u5c31\u6ca1\u4eba\u5230\u5e9c\u4e0a\u62dc\u8bbf\u4f60\u7684\u7236\u6bcd\uff1f\u4f60\u5c3d\u53ef\u95ee\u95ee\u522b\u4eba\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u518d\u665a\u548c\u7236\u6bcd\u5bb6\u4f4f\u6df1\u5c71\uff0c\u56db\u65e0\u4eba\u70df\uff0c\u5728\u8bb0\u5fc6\u4e2d\u867d\u7136\u6709\u4eba\u5230\u820d\u4e0b\u53bb\u8fc7\uff0c\u4f46\u5bb6\u7236 \u6bcd\u5374\u90fd\u4e0d\u80af\u8ba9\u518d\u665a\u548c\u5ba2\u4eba\u76f8\u89c1\u3002\u201d \u3000\u3000\u6d1e\u4ed9\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u771f\u662f\u602a\u4e8b\uff01\u602a\u4e8b\uff01\u7b80\u76f4\u602a\u5f97\u79bb\u8c31\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u8bdd\uff0c\u53c8\u5f00\u59cb\u6c89\u5bc2\u3002 \u3000\u3000\u8bb8\u4e45\uff0c\u624d\u53c8\u63a5\u9053\uff1a\u201c\u7eb5\u7136\u6211\u65bd\u6cd5\u8ba9\u4ed6\u7684\u7236\u6bcd\u73b0\u51fa\u5f62\u76f8\uff0c\u6050\u6015\u4e5f\u65e0\u6d4e\u4e8e\u4e8b\u3002\u201d \u3000\u3000\u5e90\u4e91\u8eac\u8eab\u9053\uff1b\u201c\u8001\u4ed9\u7fc1\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u6d1e\u4ed9\u9053\uff1a\u201c\u8001\u673d\u6570\u5341\u5e74\u4e0d\u66fe\u79bb\u5f00\u6d1e\u5e9c\uff0c\u5f53\u7136\u6ca1\u89c1\u8fc7\u4ed6\u7684\u7236\u6bcd\uff0c\u4f60\u5e90\u8001\u5f1f\u8fd1\u5341\u5e74\u6765\u4e5f\u751a\u5c11 \u518d\u5728\u5916\u9762\u884c\u8d70\uff0c\u96be\u9053\u5c31\u4f1a\u8ba4\u8bc6\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u8001\u4ed9\u7fc1\u80fd\u65bd\u6cd5\u903c\u51fa\u5f62\u76f8\uff0c\u665a\u8f88\u4e5f\u8bb8\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5e90\u8001\u5f1f\u975e\u8981\u8001\u673d\u65bd\u6cd5\u4e0d\u53ef\uff0c\u8001\u673d\u5c31\u52c9\u5f3a\u8bd5\u8bd5\u770b\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u8f6c\u5411\u5cb3\u5c0f\u98de\uff1a\u201c\u628a\u4ee4\u5c0a\u4ee4\u5802\u7684\u8eab\u6750\u5f62\u8c8c\u4ee5\u53ca\u5728\u72ec\u79c0\u5cf0\u65f6\u7684\u7a7f\u7740\u4ed4\u7ec6\u544a\u8bc9\u8001\u673d\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5f04\u4e0d\u6e05\u6d1e\u4ed9\u7a76\u7adf\u8981\u5982\u4f55\u65bd\u6cd5\uff0c\u867d\u5185\u5fc3\u65e2\u60ca\u6115\u53c8\u7eb3\u95f7\uff0c\u8fd8\u662f\u4f9d\u8a00\u8be6\u7ec6\u63cf\u8ff0\u4e86\u4e00\u904d\u3002 \u3000\u3000\u6d1e\u4ed9\u70b9\u4e86\u70b9\u5934\uff0c\u7acb\u5373\u5429\u5490\u9752\u8863\u7ae5\u5b50\uff1a\u201c\u628a\u706f\u706b\u7184\u53bb\uff01\u201d \u3000\u3000\u706f\u706b\u4e00\u7184\uff0c\u504c\u5927\u7684\u4e00\u95f4\u77f3\u5ba4\uff0c\u987f\u65f6\u50cf\u9ed1\u6f06\u822c\u660f\u6697\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53ef\u80fd\u53ea\u6709\u5e90\u4e91\u548c\u9752\u8863\u7ae5\u5b50\u5fc3\u91cc\u6709\u6570\uff0c\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u5219\u6709\u5982\u5760\u5165\u4e94\u91cc\u96fe\u4e2d\uff0c\u6839\u672c \u4e0d\u77e5\u9053\u4e0b\u4e00\u6b65\u4f1a\u6709\u4ec0\u4e48\u53d1\u5c55\u3002 \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u5c31\u5728\u4e0d\u4e45\u4e4b\u540e\uff0c\u7a81\u7136\u6709\u4e00\u9053\u5149\u67f1\uff0c\u5c04\u5411\u4e86\u5bf9\u9762\u7684\u77f3\u58c1\u3002 \u3000\u3000\u90a3\u5149\u67f1\u7167\u5c04\u7684\u8303\u56f4\uff0c\u8d77\u521d\u5f88\u5c0f\uff0e\u53ea\u6709\u8336\u676f\u53e3\u822c\u5927\uff0c\u4f46\u5374\u8d8a\u6269\u8d8a\u5927\uff0c\u6700\u540e\u8db3\u6709\u5927\u578b\u78e8\u76d8\u822c \u5927\u3002 \u3000\u3000\u63a5\u7740\uff0c\u5149\u5f71\u4e2d\u663e\u793a\u51fa\u4e00\u5ea7\u5c71\u5cf0\uff0c\u8d6b\u7136\u662f\u72ec\u79c0\u5cf0\u7684\u5f62\u76f8\u3002 \u3000\u3000\u90a3\u5c71\u5cf0\u6e10\u6e10\u7f29\u5c0f\u8fdc\u53bb\uff0c\u7ee7\u4e4b\u51fa\u73b0\u4e86\u4e00\u68f5\u866c\u677e\u3002 \u3000\u3000\u73b0\u5728\uff0e\u753b\u9762\u4e0a\u53ea\u5269\u4e0b\u866c\u677e\u5468\u8fd1\u7684\u8303\u56f4\u3002 \u3000\u3000\u6b64\u523b\u7684\u5cb3\u5c0f\u98de\uff0c\u7b80\u76f4\u8fde\u5927\u6c14\u4e5f\u4e0d\u6562\u5598\uff0c\u51e0\u4e4e\u8981\u88ab\u8feb\u95ed\u4f4f\u547c\u5438\uff0c\u4e16\u4e0a\u5c45\u7136\u6709\u5982\u6b64\u795e\u5947\u83ab\u6d4b \u7684\u4e8b\u60c5\uff0c\u56e0\u4e3a\u8fd9\u666f\u8c61\u5b8c\u5168\u548c\u5b9e\u9645\u4e00\u822c\u65e0\u4e8c\uff0c\u600e\u4e0d\u4ee4\u4ed6\u77a0\u76ee\u7ed3\u820c\u3002 \u3000\u3000\u84e6\u5730\uff0c\u866c\u677e\u4e0b\u73b0\u4e86\u4e09\u4e2a\u4eba\u5f71\uff0c\u5176\u4e2d\u4e24\u4eba\u6b63\u662f\u4ed6\u7684\u7236\u6bcd\uff0c\u53e6\u4e00\u4e2a\u662f\u5cb3\u5c0f\u98de\u81ea\u5df1\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u4e0d\u4f46\u5f62\u8c8c\u903c\u771f\uff0c\u8fde\u8863\u9970\u4e5f\u76f8\u5f53\u65f6\u534a\u70b9\u4e0d\u5dee\u3002 \u3000\u3000\u5c24\u5176\u4ee4\u4eba\u9a87\u5f02\u7684\uff0c\u662f\u4e09\u4eba\u7684\u52a8\u4f5c\u8868\u60c5\uff0c\u4e5f\u5b8c\u5168\u662f\u5f53\u65f6\u7684\u60c5\u5f62\uff0c\u53ea\u662f\u542c\u4e0d\u89c1\u8bf4\u8bdd\u7684\u58f0\u97f3\u800c \u5df2\u3002 \u3000\u3000\u4f17\u4eba\u5728\u51fa\u795e\u4e4b\u9645\uff0c\u5ffd\u5730\u5149\u4e00\u655b\uff0c\u5ba4\u5185\u53c8\u6062\u590d\u4e86\u5982\u6f06\u822c\u660f\u6697\u3002 \u3000\u3000\u53ea\u542c\u6d1e\u4ed9\u9053\uff1a\u201c\u628a\u706f\u518d\u70b9\u8d77\u6765\uff01\u201d \u3000\u3000\u9752\u8863\u7ae5\u5b50\u70b9\u4e0a\u706f\u3002 \u3000\u3000\u4f17\u4eba\u60c5\u4e0d\u81ea\u7981\u50cf\u5927\u68a6\u521d\u9192\u822c\u671b\u5411\u6d1e\u4ed9\u3002 \u3000\u3000\u53ea\u89c1\u6d1e\u4ed9\u8eab\u5b50\u5fae\u5fae\u6296\u52a8\uff0c\u5782\u5728\u9762\u90e8\u7684\u767d\u53d1\u548c\u767d\u7709\u4e0a\uff0c\u4e5f\u95ea\u70c1\u8815\u52a8\u7740\u4e0d\u5c11\u6c34\u73e0\uff0c\u663e\u7136\u90a3\u662f \u6ef4\u4e0b\u7684\u6c57\u6c34\uff0c\u540c\u65f6\u4e5f\u53ef\u77e5\u9053\u4eba\u8fd9\u4e00\u65bd\u6cd5\uff0c\u8017\u53bb\u4e86\u4e0d\u5c11\u529f\u529b\u3002 \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c \u4e8c \u56de\u3000\u9006\u5f92\u6deb\u5987 \u3000\u3000\u77f3\u5ba4\u5185\u53c8\u9759\u8c27\u4e86\u5f88\u4e45\uff0c\u6d1e\u4ed9\u624d\u5f00\u53e3\u9053\uff1b\u201c\u521a\u624d\u7684\u666f\u8c61\uff0c\u4f60\u4eec\u53ef\u90fd\u770b\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u5e90\u4e91\u8eac\u8eab\u7b54\u9053\uff1a\u201c\u665a\u8f88\u770b\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u8ba4\u8bc6\u662f\u8c01\uff1f\u201d \u3000\u3000\u5e90\u4e91\u5fc3\u77e5\u6d1e\u4ed9\u8017\u53bb\u4e86\u4e0d\u5c11\u771f\u5143\uff0c\u4e0d\u89c9\u5927\u611f\u6b49\u610f\u9053\uff1a\u201c\u770b\u4ed6\u4eec\u4e24\u4eba\uff0c\u5e74\u7eaa\u90fd\u5f88\u8f7b\uff0c\u4e0d\u8fc7\u4e09 \u5341\u5de6\u53f3\uff0c\u665a\u8f88\u8fd1\u5341\u5e74\u6765\u751a\u5c11\u5728\u5916\u8d70\u52a8\uff0c\u679c\u7136\u5e76\u4e0d\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u6d1e\u4ed9\u957f\u957f\u5401\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u4e0d\u59a8\u4e8b\uff0c\u4e5f\u8bb8\u5c31\u5728\u6700\u8fd1\u4e00\u4e24\u5929\uff0c\u6709\u4f4d\u5e38\u5728\u6b66\u6797\u4e2d\u8d70\u52a8\u7684\u670b\u53cb\u8981 \u6765\u770b\u6211\uff0c\u4ed6\u5bf9\u76ee\u524d\u6b66\u6797\u4eba\u7269\uff0c\u6ca1\u6709\u4e0d\u77e5\u9053\u7684\uff0c\u5c31\u7b49\u4ed6\u6765\u518d\u8bf4\uff0c\u5e90\u8001\u5f1f\u957f\u9014\u8dcb\u6d89\u4e0d\u4fbf\uff0c\u4e0d\u59a8\u5c31 \u7559\u5728\u8001\u673d\u6d1e\u5e9c\u4f4f\u4e0a\u4e00\u4e24\u5929\u3002\u201d \u3000\u3000\u5e90\u4e91\u5bf9\u6d1e\u4ed9\u7684\u4e00\u756a\u5fc3\u610f\uff0c\u81ea\u662f\u4e07\u5206\u611f\u6fc0\uff0c\u8fde\u5fd9\u8eac\u8eab\u9053\u8c22\u3002 \u3000\u3000\u6d1e\u4ed9\u518d\u9053\uff1a\u201c\u4f60\u4eec\u957f\u9014\u8dcb\u6d89\uff0c\u4e00\u5b9a\u8fd8\u6ca1\u7528\u665a\u9910\uff0c\u9752\u513f\uff0c\u62db\u5f85\u5ba2\u4eba\u5403\u996d\u53bb\uff0c\u6055\u6211\u4e0d\u966a\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u8bdd\uff0c\u8ff3\u81ea\u56de\u6d1e\u800c\u53bb\u3002 \u3000\u3000\u9752\u513f\u5fd9\u9053\uff1a\u201c\u5e90\u5927\u4fa0\u548c\u4e24\u4f4d\u5c0f\u54e5\u54e5\u8bf7\u7a0d\u5f85\uff0c\u996d\u597d\u4e86\u6211\u9a6c\u4e0a\u6765\u62db\u547c\u4e09\u4f4d\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u4e5f\u7531\u77f3\u58c1\u7684\u53e6\u4e00\u6247\u95e8\u8fdb\u5165\u6d1e\u4e2d\uff0c\u60f3\u5fc5\u90a3\u662f\u53a8\u623f\u3002 \u3000\u3000\u4e3a\u4e86\u4e0d\u60ca\u6270\u6d1e\u4ed9\uff0c\u5e90\u4e91\u548c\u8881\u5c0f\u9e64\u5cb3\u5c0f\u98de\u90fd\u4e0d\u4fbf\u8bb2\u8bdd\uff0c\u5168\u90fd\u5728\u9ed8\u9ed8\u67af\u7b49\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\u4ed6\u4eec\u6b64\u523b\u4ecd\u5728\u4e3a\u521a\u624d\u90a3\u4e00\u5e55\u800c\u8ff7\u60d1\uff0c\u8c01\u4e5f\u987e\u4e0d\u5f97\u8bb2\u4ec0\u4e48\u3002 \u3000\u3000\u67af\u7b49\u7684\u65f6\u95f4\u5728\u611f\u89c9\u4e2d\u7279\u522b\u957f\uff0c\u5c31\u50cf\u73b0\u5728\u6d41\u884c\u7684\u4e3a\u67d0\u4f4d\u4eba\u7269\u9ed8\u7977\u4e09\u5206\u949f\u4e00\u6837\uff0c\u4e00\u5206\u949f\u5c31\u591f \u53d7\u7684\u3002 \u3000\u3000\u597d\u4e0d\u5bb9\u6613\uff0c\u9752\u8863\u7ae5\u5b50\u7ec8\u4e8e\u51fa\u6765\u62db\u547c\u3002 \u3000\u3000\u5e90\u4e91\u77e5\u9053\u8fd9\u91cc\u7684\u89c4\u77e9\uff0c\u968f\u5373\u8d77\u8eab\u8ddf\u7740\u9752\u8863\u7ae5\u5b50\u8fdb\u5165\u6d1e\u5185\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u5f53\u7136\u4e5f\u8981\u8ddf\u7740\u8d70\u3002 \u3000\u3000\u62db\u5f85\u5ba2\u4eba\u5403\u996d\u4e0d\u5728\u5ba2\u5385\u800c\u5728\u53a8\u623f\uff0c\u5c24\u5176\u4e3b\u4eba\u8fde\u966a\u90fd\u4e0d\u966a\uff0c\u8fd9\u662f\u5c11\u6709\u7684\u4e8b\uff0c\u4f46\u56e0\u4e3b\u4eba\u662f\u6d1e \u4ed9\uff0c\u8c01\u4e5f\u4e0d\u6562\u62b1\u6028\u3002 \u3000\u3000\u53a8\u623f\u7684\u77f3\u6d1e\u5f88\u6df1\uff0c\u5e76\u5728\u6d1e\u58c1\u4e0a\u5f00\u4e86\u4e00\u4e2a\u5c0f\u6d1e\uff0c\u70df\u53ef\u4ee5\u5192\u5230\u5916\u9762\u53bb\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u597d\u5947\u7684\u95ee\uff1a\u201c\u5c0f\u9752\u8001\u5f1f\uff0c\u8fd9\u70df\u5192\u5230\u5916\u9762\uff0c\u4e0d\u662f\u5c31\u88ab\u5916\u4eba\u770b\u5230\u4e86\u4e48\uff1f\u542c\u8bf4\u7edd\u5c11\u6709\u4eba \u77e5\u9053\u8001\u4ed9\u7fc1\u4f4f\u5728\u8fd9\u91cc\u7684\u3002\u201d \u3000\u3000\u9752\u8863\u7ae5\u5b50\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u6d1e\u6b63\u5728\u8c37\u58c1\u4e0a\uff0c\u79bb\u8c37\u5e95\u8db3\u6709\u5341\u51e0\u4e08\u9ad8\uff0c\u5c31\u662f\u6709\u4eba\u53d1\u73b0\uff0c\u4e5f\u65e0\u6cd5\u4e0a \u6765\u5bdf\u770b\uff0c\u4f55\u51b5\u6211\u505a\u996d\u90fd\u5728\u665a\u4e0a\uff0c\u53c8\u600e\u4f1a\u6709\u4eba\u770b\u5230\u5192\u70df\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u54bd\u4e0b\u4e00\u53e3\u553e\u6db2\uff0c\u518d\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u62ff\u5230\u5927\u77f3\u5ba4\u90a3\u8fb9\u5403\uff1f\u201d \u3000\u3000\u4ed6\u56e0\u89c1\u8fd9\u91cc\u867d\u79bb\u5927\u77f3\u5ba4\u751a\u8fdc\uff0c\u8bf4\u8bdd\u4e0d\u53ef\u80fd\u88ab\u6d1e\u4ed9\u542c\u5230\uff0c\u6240\u4ee5\u624d\u6562\u8fd9\u6837\u53d1\u95ee\u3002 \u3000\u3000\u9752\u8863\u7ae5\u5b50\u9053\uff1a\u201c\u8001\u7237\u5b50\u4e0d\u8fd1\u8364\u8165\uff0c\u4eca\u665a\u6211\u7ed9\u5404\u4f4d\u714e\u4e86\u4e00\u76d8\u9c7c\uff0c\u82e5\u5728\u5927\u77f3\u5ba4\u5403\uff0c\u5fc5\u5b9a\u4f1a\u88ab \u8001\u7237\u5b50\u95fb\u5230\uff0c\u90a3\u600e\u4e48\u5f97\u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u4ed9\u7fc1\u5e73\u5e38\u90fd\u5403\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u8d8a\u6e05\u6de1\u8d8a\u597d\uff0c\u6709\u65f6\u4ed6\u8001\u4eba\u5bb6\u80fd\u4e94\u516d\u5929\u7c92\u7c73\u4e0d\u8fdb\uff0c\u7cbe\u795e\u8fd8\u662f\u597d\u7684\u5f88\u3002\u201d \u3000\u3000\u201c\u90a3\u771f\u7684\u662f\u795e\u4ed9\u4e86\uff01\u201d \u3000\u3000\u201c\u6240\u4ee5\u4ed6\u8001\u4eba\u5bb6\u4e0d\u6765\u966a\u5404\u4f4d\uff0c\u5404\u4f4d\u4e5f\u5343\u4e07\u522b\u89c1\u602a\u3002\u201d \u3000\u3000\u201c\u4eba\u600e\u6562\u89c1\u795e\u4ed9\u7684\u602a\uff01\u201d \u3000\u3000\u9752\u8863\u7ae5\u5b50\u4e0d\u518d\u8a00\u8bed\uff0c\u8fc5\u5feb\u628a\u996d\u83dc\u6446\u5728\u4e00\u5904\u77f3\u677f\u4e0a\uff0c\u679c\u7136\u9664\u4e86\u4e00\u76d8\u9c7c\uff0c\u5176\u4f59\u7684\u83dc\u90fd\u662f\u6e05\u6de1 \u7684\u3002 \u3000\u3000\u9752\u8863\u7ae5\u5b50\u63a5\u7740\u53c8\u62ff\u51fa\u4e00\u7f50\u9152\u6765\u3002 \u3000\u3000\u4f17\u4eba\u65e9\u5df3\u997f\u5f97\u53d1\u614c\uff0c\u867d\u7136\u83dc\u8272\u5f88\u6de1\uff0c\u4f46\u5374\u90fd\u5403\u5f97\u6d25\u6d25\u6709\u5473\u3002 \u3000\u3000\u53ea\u662f\u90a3\u7f50\u9152\u559d\u5f97\u5f88\u5c11\uff0c\u8bd5\u60f3\u841d\u535c\u9752\u83dc\uff0c\u5982\u4f55\u80fd\u5f15\u8d77\u9152\u5174\u3002 \u3000\u3000\u996d\u540e\uff0c\u9752\u8863\u7ae5\u5b50\u628a\u4e09\u4eba\u5f15\u8fdb\u53e6\u4e00\u95f4\u77f3\u5ba4\u3002 \u3000\u3000\u8fd9\u77f3\u5ba4\u6b63\u597d\u6709\u4e09\u5f20\u77f3\u677f\u5e8a\uff0c\u4e5f\u6709\u88ab\u8925\u3002 \u3000\u3000\u9752\u8863\u7ae5\u5b50\u4ea4\u4ee3\u8fc7\u540e\uff0c\u8ff3\u81ea\u79bb\u53bb\u3002 \u3000\u3000\u5306\u5306\u4e00\u591c\u8fc7\u53bb\uff0c\u6b21\u65e5\u8d77\u5e8a\u540e\uff0c\u5e90\u4e91\u9053\uff1a\u201c\u6709\u6211\u4e00\u4eba\u7559\u5728\u8fd9\u91cc\u5c31\u591f\u4e86\u3002\u4f60\u4eec\u4e24\u4eba\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u56e0\u4f4f\u7740\u4e0d\u4e60\u60ef\uff0c\u6b63\u597d\u4e0d\u613f\u610f\u5728\u8fd9\u91cc\u4e45\u7559\uff0c\u542c\u4e86\u5f53\u7136\u9ad8\u5174\u3002 \u3000\u3000\u5e90\u4e91\u518d\u5bf9\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4f60\u53ea\u7ba1\u653e\u5fc3\uff0c\u8001\u4ed9\u7fc1\u7684\u6cd5\u529b\u4f60\u662f\u89c1\u8fc7\u4e86\uff0c\u53ea\u8981\u90a3\u4f4d\u5e38\u5728\u6b66\u6797\u8d70\u52a8 \u7684\u670b\u53cb\u5230\u6765\uff0c\u4e00\u5b9a\u53ef\u4ee5\u8ba4\u51fa\u4ee4\u5c0a\u4ee4\u5802\u662f\u8c01\uff0c\u4f60\u968f\u5c0f\u9e64\u56de\u53bb\u7b49\u6211\u7684\u597d\u6d88\u606f\u5427\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5e08\u7236\u51c6\u5907\u4ec0\u4e48\u65f6\u5019\u56de\u53bb\uff1f\u201d \u3000\u3000\u5e90\u4e91\u9053\uff1a\u201c\u4e8b\u60c5\u529e\u5b8c\u4e86\uff0c\u6211\u5c31\u9a6c\u4e0a\u56de\u53bb\u3002\u201d \u3000\u3000\u8fd9\u65f6\u9752\u8863\u7ae5\u5b50\u5df2\u7ecf\u8fdb\u6765\u63a2\u671b\uff0c\u968f\u5373\u7531\u4ed6\u628a\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u5e26\u51fa\u6d1e\u53bb\u3002\u56e0\u4e3a\u53ea\u6709\u4ed6\u624d\u77e5\u9053 \u5f00\u542f\u6d1e\u95e8\u7684\u673a\u5173\u3002 \u3000\u3000\u51fa\u5f97\u6d1e\u6765\uff0c\u624d\u77e5\u9053\u5929\u8fd8\u672a\u4eae\uff0c\u624d\u4e0d\u8fc7\u521a\u4ea4\u4e94\u9f13\u3002 \u3000\u3000\u8fd9\u4e5f\u96be\u602a\u3002\u4f4f\u5728\u6d1e\u4e2d\uff0c\u5728\u90a3\u4e2a\u5c1a\u65e0\u8ba1\u65f6\u5668\u53ef\u7528\u7684\u65f6\u4ee3\uff0c\u53c8\u542c\u4e0d\u5230\u66f4\u9f13\uff0c\u4e0d\u51fa\u6d1e\u5916\uff0c\u6839\u672c \u5f88\u96be\u5206\u663c\u591c\u3002 \u3000\u3000\u4ed6\u4eec\u5012\u4e5f\u5fd8\u8bb0\u5c1a\u672a\u65e9\u9910\uff0c\u53ea\u987e\u6025\u6025\u8d76\u8def\u3002 \u3000\u3000\u7531\u4e8e\u5e90\u4e91\u4e0d\u5728\u8eab\u8fb9\uff0e\u4e24\u4e2a\u5e74\u8f7b\u4eba\u5f53\u7136\u8981\u8d81\u673a\u65bd\u5c55\u8f7b\u529f\u3002 \u3000\u3000\u53c8\u52a0\u6025\u7740\u8d76\u56de\u53bb\u5403\u996d\uff0e\u4e24\u4eba\u4e00\u8def\u4e0d\u66fe\u7a0d\u505c\u3002 \u3000\u3000\u82e5\u8bba\u8f7b\u529f\uff0c\u8881\u5c0f\u9e64\u56fa\u7136\u4e0d\u9519\uff0c\u4f46\u5cb3\u5c0f\u98de\u5374\u66f4\u80dc\u4e00\u7b79\uff0c\u53ea\u662f\u4ed6\u4e0d\u8bba\u8f7b\u529f\u6b66\u529f\uff0c\u4ece\u4e0d\u5728\u8881\u5c0f \u9e64\u9762\u524d\u70ab\u9732\uff0c\u56e0\u4e4b\uff0c\u8881\u5c0f\u9e64\u4e5f\u4e00\u76f4\u6478\u4e0d\u6e05\u4ed6\u7684\u5e95\u7ec6\uff0c\u53ea\u8ba4\u4e3a\u4ed6\u8fd9\u6bb5\u8def\u52c9\u5f3a\u53ef\u4ee5\u8ddf\u4e0a\u81ea\u5df1\u800c\u5df2\u3002 \u3000\u3000\u770b\u770b\u5348\u65f6\u65b9\u8fc7\uff0c\u5c1a\u672a\u5230\u672a\u65f6\uff0c\u4fbf\u5df2\u8fdc\u8fdc\u671b\u89c1\u72ec\u79c0\u5cf0\uff0c\u4e5f\u671b\u89c1\u4e86\u5cf0\u8170\u7684\u8305\u5c4b\u3002 \u3000\u3000\u5c31\u5728\u8ddd\u79bb\u8305\u5c4b\u53ea\u6709\u5341\u51e0\u4e08\u8fdc\u65f6\uff0c\u7a81\u89c1\u7af9\u7bf1\u5916\u95ea\u51fa\u4e00\u6761\u4eba\u5f71\u3002 \u3000\u3000\u90a3\u4eba\u5f71\u7a7f\u4e00\u8eab\u7d2b\u8272\u52b2\u88c5\uff0c\u770b\u8d77\u6765\u662f\u4e2a\u58ee\u5e74\u4eba\u7269\uff0e\u8eab\u6cd5\u751a\u662f\u5feb\u6377\u8f7b\u7075\uff0c\u4e00\u671b\u4fbf\u77e5\u5177\u6709\u4e0a\u4e58 \u8f7b\u529f\u3002 \u3000\u3000\u201c\u8881\u5927\u54e5\uff0c\u54b1\u4eec\u5feb\u8fc7\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7684\u8bdd\u521a\u8bf4\u5b8c\uff0c\u6b63\u8981\u7eb5\u8eab\u51b2\u51fa\uff0c\u5374\u88ab\u8881\u5c0f\u9e64\u8fc5\u5feb\u7684\u63a2\u624b\u62c9\u4f4f\uff0c\u63a5\u7740\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u7981 \u58f0\uff0c\u5feb\u5728\u6697\u5904\u85cf\u8d77\u6765\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8bdd\u672a\u8bf4\u5b8c\uff0c\u4fbf\u5f3a\u62c9\u7740\u5cb3\u5c0f\u98de\u8eb2\u5230\u4e00\u68f5\u5927\u6811\u7684\u540e\u9762\u571f\u5761\u4e0b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5e76\u4e0d\u6e05\u600e\u4e48\u95ee\u4e8b\uff0c\u6115\u7136\u95ee\u9053\uff1a\u201c\u8fd9\u4eba\u597d\u50cf\u8981\u5230\u8305\u5c4b\u91cc\u9762\u53bb\uff0c\u4e5f\u8bb8\u662f\u5e90\u4f2f\u4f2f\u7684\u719f\u4eba\uff0c \u8881\u5927\u54e5\u5e94\u8be5\u8fc7\u53bb\u62db\u547c\u4e00\u4e0b\u624d\u5bf9\uff0c\u4e3a\u4ec0\u4e48\u53cd\u8981\u8eb2\u8d77\u6765\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4f9d\u7136\u58f0\u97f3\u6781\u4f4e\uff1a\u201c\u4f60\u4e0d\u660e\u767d\uff0c\u8fd9\u4eba\u6211\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u8ba4\u8bc6\uff0c\u5c31\u66f4\u8be5\u8fc7\u53bb\u3002\u201d \u3000\u3000\u201c\u6682\u65f6\u522b\u8bb2\u8bdd\uff0c\u770b\u770b\u4ed6\u7a76\u7adf\u8981\u505a\u4ec0\u4e48\u3002\u201d \u3000\u3000\u679c\u7136\uff0c\u8fd9\u65f6\u90a3\u7d2b\u8863\u4eba\u5df2\u8fdb\u5165\u7af9\u7bf1\uff0c\u5728\u7af9\u7bf1\u5185\u7565\u4e00\u505c\u987f\uff0c\u4fbf\u53c8\u76f4\u5954\u6b63\u5c4b\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u8fd9\u624d\u60f3\u8d77\u4e34\u8d70\u65f6\u5404\u623f\u95e8\u4e0d\u66fe\u52a0\u9501\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u8fd9\u4eba\u5b9e\u5728\u5510\u7a81\uff0c\u5c4b\u91cc\u6ca1\u4eba\uff0c\u7adf\u6562\u81ea\u884c\u8fdb\u53bb\uff0c\u8881\u5927\u54e5\u4e3a\u4f55\u8fd8\u4e0d\u5feb\u8fdb\u53bb \u770b\u770b\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u770b\u770b\u4ed6\u4ec0\u4e48\u65f6\u5019\u51fa\u6765\uff0c\u51fa\u6765\u65f6\u518d\u548c\u4ed6\u7167\u9762\u4e5f\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u77e5\u6709\u5f02\uff0c\u4fbf\u4e0d\u518d\u52a0\u5165\u610f\u89c1\u3002 \u3000\u3000\u8db3\u8db3\u987f\u996d\u5de5\u592b\u8fc7\u53bb\u3002\u90a3\u7d2b\u8863\u4eba\u624d\u5306\u5306\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5f53\u4ed6\u8d70\u51fa\u7bf1\u95e8\u5916\uff0c\u5374\u53c8\u6e38\u76ee\u56db\u987e\uff0c\u50cf\u5728\u5e0c\u671b\u53d1\u73b0\u4ec0\u4e48\u3002 \u3000\u3000\u7a81\u89c1\u8881\u5c0f\u9e64\u8dc3\u8fc7\u571f\u5761\uff0e\u76f4\u5411\u90a3\u7d2b\u8863\u4eba\u8d70\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u968f\u5373\u5728\u540e\u8ddf\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8ddd\u79bb\u6e10\u6e10\u8d70\u8fd1\uff0c\u5cb3\u5c0f\u98de\u5df2\u770b\u6e05\u7d2b\u8863\u4eba\u53ea\u6709\u4e8c\u5341\u4e03\u516b\u5c81\u5e74\u7eaa\uff0c\u8eab\u6750\u9ad8\u5927\u9b41\u68a7\uff0c\u76ae\u80a4\u767d\u7699\u3001 \u9762\u8c8c\u9887\u4e3a\u4fca\u7f8e\uff0c\u53ea\u662f\u7709\u5b87\u95f4\u5374\u9690\u6cdb\u7740\u4e00\u80a1\u96be\u8a00\u7684\u9634\u9669\uff0c\u6deb\u90aa\u4e4b\u6c14\u3002 \u3000\u3000\u5f53\u4ed6\u53d1\u73b0\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u5c71\u4e0a\u5761\u540e\u73b0\u8eab\u800c\u51fa\uff0c\u5e76\u672a\u5f00\u53e3\u8bf4\u8bdd\uff0c\u53ea\u662f\u628a\u89c6\u7ebf\u7d27\u76ef\u5728\u4e24\u4eba\u8eab \u4e0a\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u8d70\u524d\u51e0\u6b65\uff0c\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u539f\u6765\u662f\u5927\u5e08\u5144\uff0c\u5c0f\u5f1f\u96be\u5f97\u5728\u8fd9\u91cc\u770b\u5230\u4f60\uff0c\u6709\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u7d2b\u8863\u4eba\u4e0d\u7b54\u53cd\u95ee\uff1a\u201c\u8001\u5934\u5b50\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u5e08\u7236\u5916\u51fa\u8bbf\u53cb\u53bb\u4e86\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8bf4\u5b8c\u8bdd\u624d\u89c9\u51fa\u4e0d\u5bf9\uff0c\u7d27\u63a5\u7740\u53c8\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u4f60\u628a\u5e08\u7236\u79f0\u4e3a\u8001\u5934\u5b50\uff0c\u672a\u514d\u6709\u4e9b\u8fc7 \u4efd\u4e86\u5427\uff01\u201d \u3000\u3000\u8c01\u77e5\u7d2b\u8863\u4eba\u5374\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u6837\u79f0\u547c\uff0c\u5df2\u7ecf\u7b97\u5ba2\u6c14\u4e86\uff0c\u6211\u662f\u88ab\u4ed6\u9010\u51fa\u95e8\u5899\u7684\uff0c\u548c\u4ed6\u65e9 \u5c31\u6beb\u65e0\u74dc\u845b\uff0c\u83ab\u4e0d\u6210\u8ba9\u6211\u53eb\u4ed6\u4e00\u58f0\u8001\u6df7\u86cb\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e0d\u89c9\u6c14\u5f80\u4e0a\u51b2\uff0c\u53cc\u7709\u626c\u4e86\u4e00\u626c\uff0c\u4f46\u77ac\u95f4\u5374\u53c8\u5fcd\u4e86\u4e0b\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u8bf7\u6055\u5c0f\u5f1f\u6597\u80c6 \u8bf4\u53e5\u8bdd\uff0c\u5982\u679c\u4f60\u80af\u9075\u5b88\u5e08\u95e8\u89c4\u8beb\uff0c\u600e\u4f1a\u88ab\u5e08\u7236\u9010\u51fa\u95e8\u5899\uff0c\u5b9e\u5728\u662f\u4f60\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u4e5f\u592a\u4e0d\u5e94\u8be5 \u4e86\uff01\u201d \u3000\u3000\u7d2b\u8863\u4eba\u987f\u65f6\u4e24\u592a\u9633\u7a74\u9752\u7b4b\u66b4\u8d77\uff0c\u7709\u5b87\u95f4\u6cdb\u73b0\u6740\u673a\uff0c\u66b4\u58f0\u559d\u9053\uff1a\u201c\u4f60\u6562\u6559\u8bad\u6211\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u5e26\u7740\u60f6\u6050\uff0e\u4f4e\u4e0b\u58f0\u97f3\u9053\uff1a\u201c\u5c0f\u5f1f\u600e\u6562\u5192\u72af\uff0c\u53ea\u6c42\u5927\u5e08\u5144\u79bb\u5f00\u5e08\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u56de\u5934\u671b\u4e86\u5cb3\u5c0f\u98de\u4e00\u773c\uff0c\u4f3c\u4e4e\u4e0d\u613f\u518d\u63a5\u4e0b\u53bb\uff0c\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u82e5\u5927\u5e08\u5144\u4e0d\u80af \u542c\u5c0f\u5f1f\u7684\u529d\u544a\uff0c\u53ea\u6015\u4fbf\u65e0\u6cd5\u5728\u6b66\u6797\u4e2d\u7acb\u8db3\uff0c\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u4e5f\u5c06\u6c38\u8fdc\u65e0\u989c\u9762\u5bf9\u6b66\u6797\u6545\u65e7\uff0c\u4f60\u4e0d \u96be\u60f3\u5230\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u8fd9\u5341\u51e0\u5e74\u6765\u5728\u5185\u5fc3\u53d7\u5230\u591a\u5927\u7684\u4f24\u5bb3\uff0c\u8fd8\u671b\u5927\u5e08\u5144\u4e09\u601d\uff01\u201d \u3000\u3000\u201c\u4f4f\u5634\uff01\u201d\u7d2b\u8863\u4eba\u5927\u559d\uff1a\u201c\u6211\u9a6c\u662d\u96c4\u82e5\u4e0d\u770b\u5728\u66fe\u548c\u4f60\u6709\u8fc7\u540c\u95e8\u4e4b\u8c0a\uff0c\u73b0\u5728\u5c31\u8981\u4f60\u6b7b\u5728\u6211 \u7684\u9762\u524d\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u5e26\u7740\u54c0\u80af\u7684\u795e\u8272\uff0c\u9053\uff1a\u201c\u5927\u5e08\u5144\u606f\u6012\uff0c\u5c0f\u5f1f\u5b8c\u5168\u662f\u8bf4\u5fc3\u91cc\u7684\u8bdd\uff0c\u603b\u5e0c\u671b\u5927\u5e08\u5144\u80fd \u542c\u5f97\u8fdb\u53bb\uff01\u201d \u3000\u3000\u5ffd\u89c1\u9a6c\u662d\u96c4\u6740\u673a\u4e00\u655b\uff0c\u7b11\u563b\u563b\u7684\u76f4\u5411\u8881\u5c0f\u9e64\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u5fc3\u5934\u4e00\u9707\uff0c\u4e0d\u77e5\u4ed6\u8981\u505a\u4ec0\u4e48\uff0c\u6025\u6025\u5411\u540e\u8fde\u9000\u51e0\u6b65\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u7684\u4e00\u53ea\u624b\u5374\u8f7b\u8f7b\u642d\u4e0a\u4e86\u8881\u5c0f\u9e64\u7684\u80a9\u8180\uff0c\u970e\u65f6\u58f0\u97f3\u53d8\u5f97\u5341\u5206\u67d4\u548c\uff1a\u201c\u5c0f\u9e64\uff0c\u5927\u5e08\u5144 \u5bf9\u4f60\u6709\u4e2a\u8981\u6c42\uff0c\u4e0d\u77e5\u4f60\u80af\u4e0d\u80af\u7b54\u5e94\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e24\u773c\u53d1\u76f4\uff0c\u60c5\u4e0d\u81ea\u7981\u53c8\u9000\u540e\u2014\u6b65\uff1a\u201c\u5927\u5e08\u5144\u6709\u8bdd\u8bf7\u8bb2\uff0c\u53ea\u8981\u5c0f\u5f1f\u505a\u5f97\u5230\u7684\u2026\u2026\u201d \u3000\u3000\u9a6c\u662d\u96c4\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4f60\u81ea\u7136\u505a\u5f97\u5230\uff0c\u5982\u679c\u505a\u4e0d\u5230\uff0c\u6211\u4e5f\u4e0d\u4f1a\u8981\u6c42\u4f60\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5927\u5e08\u5144\u8fd8\u6ca1\u8bb2\u660e\u767d\uff1f\u201d \u3000\u3000\u201c\u5f88\u7b80\u5355\uff0c\u79bb\u5f00\u8001\u5934\u5b50\uff0c\u8ddf\u6211\u8d70\uff0c\u6211\u51b3\u4e0d\u4f1a\u4e8f\u5f85\u4f60\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6447\u4e86\u6447\u5934\uff0c\u6b63\u8272\u9053\uff1a\u201c\u5927\u5e08\u5144\u8981\u6c42\u5c0f\u5f1f\u4ec0\u4e48\u4e8b\u90fd\u53ef\u4ee5\uff0c\u8981\u6211\u8fd9\u6837\u505a\uff0c\u529e\u4e0d\u5230\uff01\u201d \u3000\u3000\u201c\u90a3\u4e48\u6211\u8981\u4f60\u6740\u4e86\u8001\u5934\u5b50\uff0c\u529e\u4e0d\u529e\u5f97\u5230\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8138\u8272\u4e00\u53d8\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u600e\u80fd\u8bf4\u51fa\u8fd9\u6837\u7684\u8bdd\uff01\u4f60\u5df2\u7ecf\u6b3a\u5e08\u706d\u7956\u4e86\uff0c\u4f46\u5c0f\u5f1f\u5374\u51b3\u4e0d \u505a\u5e08\u95e8\u53db\u8d3c\uff01\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u8bf4\u5f97\u591f\u91cd\u4e86\uff0c\u8881\u5c0f\u9e64\u5728\u8bf4\u8fc7\u4e4b\u540e\uff0c\u4fbf\u6697\u4e2d\u5168\u795e\u6212\u5907\uff0c\u4ee5\u9632\u5bf9\u65b9\u7a81\u65bd\u6740\u624b\u3002 \u3000\u3000\u8c01\u77e5\u9a6c\u662d\u96c4\u7adf\u5e76\u4e0d\u5728\u610f\uff0c\u62cd\u4e86\u62cd\u8881\u5c0f\u9e64\u80a9\u5934\u9053\uff1a\u201c\u5c0f\u9e64\uff0c\u8001\u5934\u5b50\u80fd\u8c03\u6559\u51fa\u4f60\u8fd9\u6837\u4e00\u4e2a\u5f92 \u5f1f\uff0c\u4ed6\u4e5f\u8be5\u5fc3\u6ee1\u610f\u8db3\u4e86\uff0c\u4e0d\u8fc7\u6211\u603b\u5e0c\u671b\u4f60\u597d\u597d\u8003\u8651\u8003\u8651\uff0c\u4e0b\u6b21\u6211\u518d\u6765\u5e26\u4f60\u8d70\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\uff0c\u5927\u5e08\u5144\u6700\u597d\u65ad\u4e86\u8fd9\u79cd\u5ff5\u5934\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u5ffd\u7136\u89c6\u7ebf\u53c8\u8f6c\u5411\u5cb3\u5c0f\u98de\uff1a\u201c\u8fd9\u5b69\u5b50\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u987a\u53e3\u9053\uff1a\u201c\u662f\u5e08\u7236\u4eb2\u65b0\u8fd1\u6536\u4e0b\u7684\u5f1f\u5b50\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u563f\u563f\u7b11\u9053\uff1a\u201c\u8001\u5934\u5b50\u91ce\u5fc3\u8fd8\u771f\u4e0d\u5c0f\uff0c\u7adf\u7136\u53c8\u6536\u4e86\u5f1f\u5b50\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\uff1a\u201c\u5927\u5e08\u5144\u9519\u4e86\uff0c\u5e08\u7236\u6536\u5f1f\u5b50\u53ea\u662f\u4e3a\u6b66\u6797\u6b63\u4e49\u7684\u4e0b\u4e00\u4ee3\uff0c\u600e\u4f1a\u626f\u5230\u91ce\u5fc3\u4e0a\u53bb\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6765\u8fd9\u91cc\u591a\u4e45\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4e45\uff0c\u53ea\u6709\u51e0\u4e2a\u6708\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u6709\u4e00\u5929\u6211\u628a\u8001\u5934\u5b50\u5bb0\u4e86\uff0c\u53ea\u6015\u4ed6\u5c31\u5b66\u4e0d\u6210\u6b66\u827a\u4e86\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u867d\u5185\u5fc3\u6012\u706b\u72c2\u70bd\uff0c\u4f46\u5374\u4e0d\u6562\u548c\u5bf9\u6253\u786c\u78b0\u786c\uff0c\u7d22\u6027\u8f6c\u8fc7\u8138\u53bb\uff0c\u4e0d\u518d\u5f00\u53e3\u3002 \u3000\u3000\u5ffd\u542c\u9a6c\u662d\u96c4\u76ae\u7b11\u8089\u4e0d\u7b11\u7684\u9053\uff1a\u201c\u5c0f\u9e64\uff0c\u8fd9\u4e9b\u5e74\u4f60\u8ddf\u7740\u8001\u5934\u5b50\u4e60\u827a\uff0c\u6b66\u529f\u4e00\u5b9a\u5927\u6709\u8fdb\u5883\uff0c \u8bf4\u4e0d\u5b9a\u8d85\u8fc7\u4e86\u6211\u9a6c\u662d\u96c4\uff0c\u53ef\u4e0d\u53ef\u4ee5\u9732\u51e0\u624b\u7ed9\u6211\u77a7\u77a7\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8bed\u8c03\u51b0\u51b7\u7684\u9053\uff1a\u201c\u5c0f\u5f1f\u5929\u8d44\u611a\u9c81\uff0c\u5e74\u7eaa\u53c8\u8f7b\uff0c\u600e\u6562\u548c\u5927\u5e08\u5144\u76f8\u63d0\u5e76\u8bba\uff01\u201d \u3000\u3000\u201c\u7528\u4e0d\u7740\u5ba2\u6c14\uff0e\u9732\u51e0\u62db\u8ba9\u6211\u770b\u770b\uff0c\u5982\u679c\u9700\u8981\u627e\u4e2a\u5bf9\u624b\uff0c\u5927\u5e08\u5144\u662f\u73b0\u6210\u7684\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u518d\u9000\u540e\u4e00\u6b65\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5927\u5e08\u5144\u7528\u4e0d\u7740\u8fd9\u6837\uff0c\u5c0f\u5f1f\u4e0d\u6562\u732e\u4e11\u3002\u201d \u3000\u3000\u4f46\u89c1\u9a6c\u662d\u96c4\u53f3\u624b\u4e00\u626c\uff0c\u95ea\u7535\u822c\u5411\u8881\u5c0f\u9e64\u6251\u51fa\u4e00\u638c\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u6ca1\u6599\u5230\u5bf9\u65b9\u4f1a\u8f6c\u53d8\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u6839\u672c\u6765\u4e0d\u53ca\u95ea\u8eb2\uff0c\u4e00\u58f0\u8106\u54cd\u4e4b\u540e\uff0c\u5de6\u988a\u987f\u65f6\u6d6e\u4e86 \u4e94\u6761\u6307\u75d5\uff0c\u540c\u65f6\u4eba\u4e5f\u8e09\u8dc4\u5411\u540e\u6454\u51fa\u4e86\u597d\u51e0\u6b65\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u51b7\u68ee\u68ee\u7b11\u9053\uff1a\u201c\u522b\u7ed9\u8138\u4e0d\u8981\u8138\uff0c\u4f60\u82e5\u6562\u4e0d\u542c\u6211\u7684\u5429\u5490\uff0c\u5c31\u522b\u602a\u6211\u624b\u4e0b\u65e0\u60c5\u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u88ab\u6fc0\u8d77\u771f\u706b\uff0c\u6342\u7740\u9762\u988a\u9ad8\u58f0\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u4f60\u82e5\u518d\u82e6\u82e6\u76f8\u903c\uff0c\u53ea\u6015\u5c0f\u5f1f\u5c31\u65e0\u6cd5\u518d \u5fcd\u4e86\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u8038\u7709\u7b11\u9053\uff1a\u201c\u4f60\u672c\u6765\u5c31\u7528\u4e0d\u7740\u5fcd\u3002\u201d \u3000\u3000\u4ed6\u8fb9\u8bf4\u8fb9\u89e3\u4e0b\u8170\u95f4\u7684\u957f\u5251\uff0c\u629b\u5728\u5730\u4e0a\uff0c\u63a5\u9053\uff1a\u201c\u4e0d\u8fc7\u4f60\u653e\u5fc3\uff0c\u4f60\u65e2\u7136\u672a\u5e26\u5175\u5c3a\uff0c\u6211\u4e5f\u51b3 \u5b9a\u4e0d\u7528\u5251\uff1f\u540c\u65f6\uff0c\u4f60\u7eb5\u7136\u8d25\u4e86\uff0c\u6211\u4e5f\u51b3\u4e0d\u53d6\u4f60\u6027\u547d\uff0c\u8fd9\u6837\u4f60\u603b\u8be5\u6ee1\u610f\u4e86\u5427\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u65e9\u77e5\u81ea\u5df1\u7684\u6b66\u529f\uff0c\u65e0\u6cd5\u4e0e\u5bf9\u65b9\u76f8\u6bd4\uff0c\u6240\u4ee5\u5148\u524d\u4ed6\u4e0d\u5f97\u4e0d\u4e00\u5fcd\u518d\u5fcd\uff0c\u6b64\u523b\u89c1\u5df2\u65e0\u6cd5 \u8eb2\u8fc7\uff0c\u4e5f\u53ea\u6709\u786c\u8d77\u5934\u76ae\u4e00\u62da\u4e86\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u4ed6\u6697\u6697\u8fd0\u8d77\u529f\u529b\uff0c\u62b1\u4e86\u62b1\u62f3\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5927\u5e08\u5144\u5c31\u8bf7\u8d50\u62db\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u51b7\u7b11\u9053\uff1a\u201c\u6211\u82e5\u5148\u51fa\u624b\uff0c\u90a3\u5c31\u4ee5\u5927\u538b\u5c0f\u4e86\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u82e5\u5c0f\u5f1f\u5148\u51fa\u624b\uff0c\u90a3\u5c31\u4ee5\u5c0f\u72af\u4e0a\u4e86\uff0c\u5c0f\u5f1f\u62c5\u4e0d\u8d77\u8fd9\u4e2a\u7f6a\u540d\uff01\u201d \u3000\u3000\u201c\u54ea\u91cc\u5b66\u6765\u8fd9\u822c\u7f57\u55e6\uff0c\u770b\u6765\u6211\u662f\u4e0d\u5f97\u4e0d\u5148\u51fa\u624b\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u8bdd\u58f0\u672a\u6bd5\uff0c\u817f\u4e0d\u5c48\u819d\uff0c\u80a9\u4e0d\u6643\u52a8\uff0c\u4eba\u5df2\u6b3a\u81f3\u8881\u5c0f\u9e64\u8eab\u524d\uff0c\u53ea\u89c1\u4ed6\u5de6\u81c2\u4e00\u5708\uff0c\u638c\u626c \u8d77\u540e\u7a81\u7136\u5e7b\u5316\u51fa\u5341\u51e0\u4e2a\u638c\u5f71\uff0c\u95ea\u7535\u822c\u5411\u8881\u5c0f\u9e64\u53c8\u9876\u7f69\u53bb\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u5fc3\u5934\u4e00\u51db\uff0c\u53ea\u611f\u773c\u82b1\u7f2d\u4e71\u4e0d\u6562\u786c\u63a5\uff0c\u60f6\u9a87\u4e2d\u7acb\u5373\u5411\u540e\u95ea\u9000\u3002 \u3000\u3000\u5c82\u77e5\u9a6c\u662d\u96c4\u7684\u638c\u52bf\uff0c\u5982\u5f71\u968f\u5f62\uff0c\u8881\u5c0f\u9e64\u521a\u9000\u51fa\u4e24\u6b65\uff0c\u5de6\u80a9\u5934\u4fbf\u88ab\u638c\u52bf\u51fb\u4e2d\u3002 \u3000\u3000\u597d\u5728\u8fd9\u4e00\u638c\u4efd\u91cf\u4e0d\u91cd\uff0c\u663e\u7136\u662f\u9a6c\u662d\u96c4\u6545\u610f\u7559\u4e86\u51e0\u5206\u60c5\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u5728\u53c8\u7f9e\u53c8\u607c\u4e4b\u4e0b\uff0c\u5df2\u987e\u4e0d\u5f97\u4e00\u5207\uff0c\u4e00\u54ac\u7259\uff0c\u4e5f\u53cc\u638c\u9f50\u51fa\u3002 \u3000\u3000\u4ed6\u968f\u5e90\u4e91\u4e60\u827a\u5341\u5e74\uff0c\u82e5\u8bba\u9020\u8be3\uff0c\u5df2\u79f0\u5f97\u4e0a\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u5728\u62da\u547d\u65bd\u5c55\u4e4b\u4e0b\uff0c\u653b\u52bf\u987f\u65f6 \u9510\u4e0d\u53ef\u6321\uff0c\u5927\u6709\u96f7\u9706\u4e07\u94a7\u4e4b\u6982\u3002 \u3000\u3000\u82e5\u5bf9\u65b9\u662f\u4e00\u822c\u9ad8\u624b\uff0c\u5fc5\u5b9a\u96be\u4ee5\u62db\u67b6\uff0c\u4f46\u9a6c\u662d\u96c4\u65e5\u524d\u7684\u6b66\u529f\u9020\u8be3\uff0c\u5df2\u662f\u51fa\u795e\u5165\u5316\uff0c\u4ed6\u5728\u5fae \u5fae\u4e00\u6115\u4e4b\u540e\uff0c\u5f88\u5feb\u53c8\u628a\u8881\u5c0f\u9e64\u903c\u5f97\u4e0d\u5f97\u4e0d\u5411\u540e\u9000\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u53ea\u611f\u81ea\u5df1\u7684\u6bcf\u4e00\u51fa\u624b\uff0c\u90fd\u5728\u5bf9\u65b9\u7684\u63a7\u5236\u4e4b\u4e2d\uff0c\u5c31\u50cf\u5bf9\u65b9\u5df2\u9884\u77e5\u4ed6\u5982\u4f55\u51fa\u5f0f\u5982\u4f55\u51fa \u62db\u4e00\u822c\u3002 \u3000\u3000\u5b9e\u9645\u4e0a\uff0c\u8881\u5c0f\u9e64\u7684\u51fa\u624b\u62db\u5f0f\uff1f\u6b63\u662f\u88ab\u9a6c\u662d\u96c4\u903c\u51fa\u6765\u7684\uff0c\u903c\u5f97\u4ed6\u975e\u8fd9\u6837\u51fa\u624b\u4e0d\u53ef\uff0c\u6240\u4ee5\u8881 \u5c0f\u9e64\u5728\u611f\u89c9\u4e0a\u4ee5\u4e3a\u5df2\u88ab\u5bf9\u65b9\u9884\u77e5\u4e00\u822c\u3002 \u3000\u3000\u6b66\u5bb6\u8fc7\u62db\uff0c\u8d35\u5728\u62a2\u5360\u5148\u673a\uff0c\u9a6c\u662d\u96c4\u5c3d\u5f97\u5148\u673a\uff0c\u8881\u5c0f\u9e64\u5c82\u6709\u4e0d\u8d25\u4e4b\u7406\u3002 \u3000\u3000\u679c\u7136\uff0c\u5c31\u5728\u4e03\u516b\u62db\u8fc7\u540e\uff0c\u9a6c\u662d\u96c4\u5728\u98de\u8d77\u4e00\u811a\u4e4b\u540e\uff0c\u8881\u5c0f\u9e64\u76f4\u88ab\u8e22\u98de\u4e00\u4e08\u591a\u9ad8\uff0c\u7136\u540e\u6454\u4e86 \u4e2a\u5012\u683d\u8471\uff0c\u76f4\u6253\u4e86\u4e09\u4e2a\u6eda\u7ffb\uff0c\u624d\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u6c14\u4e0d\u51fa\u58f0\uff0c\u9762\u4e0d\u6539\u8272\uff0c\u6413\u4e86\u6413\u4e24\u624b\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u4f60\u7684\u786e\u6ca1\u88ab\u8001\u5934\u5b50\u767d\u6559\uff0c\u5c45 \u7136\u5728\u6211\u624b\u4e0b\u8d70\u8fc7\u4e03\u516b\u62db\uff01\u201d \u3000\u3000\u4ed6\u7684\u53e3\u6c14\uff0c\u662f\u591a\u4e48\u81ea\u5927\uff0c\u591a\u4e48\u72c2\u50b2\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u7ea2\u7740\u8138\u9053\uff1a\u201c\u5927\u5e08\u5144\u9ad8\u660e\uff0c\u5c0f\u5f1f\u8fd9\u70b9\u5fae\u672b\u4e4b\u6280\uff0c\u5982\u4f55\u6bd4\u5f97\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u563f\u563f\u7b11\u9053\uff1a\u201c\u7528\u4e0d\u7740\u5ba2\u6c14\uff0c\u770b\u4f60\u65b9\u624d\u7684\u8eab\u5b50\uff0c\u5df2\u7ecf\u5f88\u96be\u5f97\u4e86\uff0c\u4e0d\u8fc7\u4f60\u8be5\u5fc3\u91cc\u6709\u6570\uff0c \u6211\u5df2\u5bf9\u4f60\u5927\u5927\u624b\u4e0b\u7559\u60c5\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9f3b\u5b54\u91cc\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u591a\u8c22\u5927\u5e08\u5144\u8fd8\u5ff5\u7740\u540c\u95e8\u4e4b\u8c0a\uff01\u201d \u3000\u3000\u201c\u65e2\u77e5\u540c\u95e8\uff1f\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8ddf\u7740\u6211\u8d70\uff1f\u201d \u3000\u3000\u201c\u5927\u5e08\u5144\u53c8\u4e3a\u4ec0\u4e48\u4e0d\u56de\u5934\u5411\u5584\uff0c\u91cd\u8fd4\u5e08\u95e8\uff1f\u201d \u3000\u3000\u201c\u5927\u80c6\uff0c\u4f60\u53ef\u662f\u8981\u627e\u6b7b\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u5927\u5e08\u5144\u5b9e\u5728\u5bb9\u4e0d\u4e0b\u5c0f\u5f1f\uff0c\u5c3d\u53ef\u628a\u5c0f\u5f1f\u6740\u6b7b\u5728\u4f60\u9762\u524d\u3002\u201d \u3000\u3000\u201c\u597d\u786c\u7684\u5634\uff0c\u90a3\u662f\u73b0\u5728\u8fd8\u6ca1\u5230\u65f6\u5019\uff0c\u5fc5\u8981\u65f6\u6211\u4e5f\u8bb8\u8001\u7684\u5c0f\u7684\u4e00\u8d77\u6210\u5168\u3002\u201d \u3000\u3000\u63a5\u7740\u53c8\u6307\u4e86\u6307\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fde\u8fd9\u4e2a\u5c0f\u5b50\u4e5f\u522b\u60f3\u7559\u4e0b\u6d3b\u53e3\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u8bdd\uff0c\u521a\u8981\u4fef\u8eab\u53d6\u8d77\u653e\u5728\u5730\u4e0a\u7684\u957f\u5251\uff0c\u5ffd\u542c\u2014\u4e2a\u6717\u6717\u7684\u58f0\u97f3\u9053\uff1a\u201c\u59d3\u9a6c\u7684\uff0c\u4f60\u4e0d\u59a8 \u73b0\u5728\u5c31\u6765\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u6123\u4e86\u4e00\u6123\uff0c\u518d\u671b\u5411\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7a33\u7ad9\u5f53\u5730\uff0c\u53cc\u624b\u63e1\u62f3\u9053\uff1a\u201c\u4f60\u7684\u8033\u6735\u5e94\u8be5\u4e0d\u804b\uff0c\u83ab\u975e\u585e\u8fdb\u9a74\u6bdb\u53bb\u4e86\uff1f\u201d \u3000\u3000\u800c\u5bf9\u4e00\u4e2a\u5e74\u4ec5\u5341\u5c81\u7684\u5b69\u5b50\uff0c\u9a6c\u662d\u96c4\u7684\u786e\u6123\u591a\u4e8e\u60ca\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u597d\u50cf\u662f\u4e70\u78b1\u9c7c\u653e\u751f\u2014\u2014\u4e0d \u77e5\u6b7b\u6d3b\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6602\u7136\u9053\uff1a\u201c\u4e0d\u77e5\u6b7b\u6d3b\u5e94\u8be5\u662f\u4f60\u8fd9\u53db\u5e08\u706d\u7956\u7684\u6b66\u6797\u8d25\u7c7b\uff01\u201d \u3000\u3000\u6b64\u65f6\u8881\u5c0f\u9e64\u5374\u662f\u5927\u611f\u60ca\u9a87\uff0c\u4ed6\u4e07\u6ca1\u6599\u5230\u5cb3\u5c0f\u98de\u7adf\u4f1a\u81ea\u627e\u9ebb\u70e6\u6345\u9a6c\u8702\u7a9d\u3002 \u3000\u3000\u539f\u6765\u4ed6\u548c\u5cb3\u5c0f\u98de\u76f8\u5904\u4e09\u6708\u6253\u4f59\uff0c\u5374\u5e76\u4e0d\u6e05\u695a\u5cb3\u5c0f\u98de\u4e0d\u8bba\u5728\u6b66\u529f\u76f8\u8f7b\u529f\u65b9\u9762\uff0c\u90fd\u5df2\u8fbe\u4e0a\u4e58 \u706b\u5019\uff0c\u5982\u4eca\u89c1\u4ed6\u95ef\u4e0b\u7978\uff0c\u600e\u80fd\u4e0d\u611f\u60ca\u9a87\uff0c\u4e00\u65e6\u9a6c\u662d\u96c4\u5bf9\u5cb3\u5c0f\u98de\u4e0b\u4e86\u6bd2\u624b\uff0c\u4ed6\u5982\u4f55\u5411\u5e08\u7236\u4ea4\u5f85\uff1f \u81ea\u5df1\u53c8\u5982\u4f55\u5fc3\u5b89\uff1f\uff0c\u60c5\u6025\u4e4b\u4e0b\uff0c\u7acb\u523b\u8f6c\u5934\u559d\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u4f60\u771f\u7684\u60f3\u627e\u6b7b\u4e48\uff1f\u201d \u3000\u3000\u8fb9\u8bf4\u8fb9\u53c8\u6321\u5728\u4e24\u4eba\u4e2d\u95f4\uff0c\u671b\u7740\u9a6c\u662d\u96c4\u62f1\u624b\u4e00\u793c\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u60a8\u5343\u4e07\u522b\u8ddf\u4ed6\u4e00\u822c\u89c1\u8bc6\uff0c \u4ed6\u662f\u4e2a\u5c0f\u5b69\u5b50\uff0c\u4e0d\u61c2\u793c\u8c8c\uff0c\u5192\u72af\u4e86\u5927\u5e08\u5144\uff0c\u5c0f\u5f1f\u60c5\u613f\u66ff\u4ed6\u8d54\u7f6a\uff01\u201d \u3000\u3000\u53ea\u542c\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0c\u4f60\u4e0d\u5fc5\u7ba1\uff0c\u597d\u6c49\u4e00\u4eba\u505a\u4e8b\u4e00\u4eba\u5f53\uff0c\u4f60\u53ef\u4ee5\u53d7\u4ed6\u7684\u6c14\uff0c\u5c0f\u5f1f\u5374 \u5b9e\u5728\u770b\u4e0d\u8fc7\u53bb\uff01\u201d \u3000\u3000\u7ecf\u8881\u5c0f\u9e64\u8bf4\u4e86\u597d\u8bdd\u8d54\u7f6a\uff0c\u9a6c\u662d\u96c4\u672c\u5df2\u4e0d\u60f3\u518d\u8ba1\u8f83\uff0c\u4f46\u5cb3\u5c0f\u98de\u5374\u53c8\u706b\u4e0a\u52a0\u6cb9\uff0c\u6559\u4ed6\u54ea\u91cc\u5fcd \u5f97\u4e0b\u8fd9\u53e3\u6c14\uff0c\u5f53\u4e0b\uff0c\u4e00\u628a\u62e8\u5f00\u8881\u5c0f\u9e64\uff0c \u3000\u3000\u76f4\u5411\u5cb3\u5c0f\u98de\u8d70\u53bb\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u5e74\u7eaa\u8f7b\u8f7b\u5c31\u53d1\u4e86\u75af\uff0c\u8001\u5b50\u73b0\u5728\u5c31\u5e72\u8106\u628a\u4f60\u9001\u4e0a\u897f\u5929\uff01\u201d \u3000\u3000\u5728\u4ed6\u7684\u60f3\u6cd5\uff0c\u522b\u8bf4\u5cb3\u5c0f\u98de\u4e60\u827a\u4e86\u4e09\u4e94\u4e2a\u6708\uff0c\u5373\u4fbf\u4ed6\u4e00\u51fa\u5a18\u80ce\u5c31\u5f00\u59cb\u7ec3\u529f\uff0c\u4e00\u638c\u4e5f\u8db3\u4ee5\u628a \u4ed6\u62cd\u6210\u8089\u9171\u3002 \u3000\u3000\u5c82\u77e5\u4ed6\u521a\u8d70\u51fa\u4e24\u4e09\u6b65\uff0c\u5bf9\u65b9\u4fbf\u5df2\u5931\u53bb\u6240\u5728\uff0c\u63a5\u7740\u4e00\u9053\u9ed1\u5f71\uff0c\u4ece\u534a\u7a7a\u91cc\u76f4\u5411\u5934\u9876\u6251\u6c34\uff0c\u4eba \u672a\u5230\u4e24\u9053\u9510\u5229\u7684\u638c\u98ce\uff0c\u4fbf\u76f4\u88ad\u8111\u95e8\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u5f53\u771f\u88ab\u5f04\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u60c5\u6025\u4e4b\u95f4\u53cc\u638c\u9f50\u51fa\uff0c\u731b\u5411\u7a7a\u4e2d\u8fce\u53bb\u3002 \u3000\u3000\u53cc\u65b9\u62f3\u638c\u4e00\u63a5\uff0c\u201c\u6251\u201d\u7684\u4e00\u58f0\u6c89\u54cd\uff0c\u90a3\u9ed1\u5f71\u7acb\u5373\u53c8\u53cd\u5f39\u4e0a\u53bb\u3002 \u3000\u3000\u663e\u7136\uff0c\u5cb3\u5c0f\u98de\u7684\u5185\u529b\u65e0\u6cd5\u4e0e\u9a6c\u662d\u96c4\u76f8\u6bd4\u3002 \u3000\u3000\u4f46\u4ed6\u8eab\u5b50\u867d\u88ab\u9707\u8d77\uff0c\u5728\u7a7a\u4e2d\u5374\u4f9d\u7136\u80fd\u63a7\u5236\u81ea\u5982\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u5728\u534a\u7a7a\u6253\u4e86\u4e2a\u56de\u65cb\uff0c\u7adf\u53c8\u4fef\u51b2\u5411\u4e0b\uff0c\u6b63\u597d\u53c8\u6251\u5411\u9a6c\u662d\u96c4\u5934\u9876\uff0c\u65b9\u4f4d\u62ff\u634f\u5f97\u5206\u6beb\u4e0d \u5dee\u3002 \u3000\u3000\u7ad9\u5728\u4e00\u65c1\u89c2\u770b\u7684\u8881\u5c0f\u9e64\uff0c\u6b64\u523b\u5df2\u7531\u60ca\u9a87\u53d8\u4e3a\u60ca\u5f02\uff0c\u4ed6\u505a\u68a6\u4e5f\u6ca1\u6599\u5230\uff0c\u5cb3\u5c0f\u98de\u7adf\u5177\u6709\u5947\u5999 \u7edd\u9876\u7684\u8eab\u624b\uff0c\u4ec5\u51ed\u4ed6\u51cc\u7a7a\u4e0d\u5760\u98de\u65cb\u81ea \u3000\u3000\u5982\u7684\u8eab\u6cd5\uff0c\u5c31\u4ee4\u81ea\u5df1\u671b\u5c18\u83ab\u53ca\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u672c\u6ca1\u62ff\u4ed6\u5f53\u56de\u4e8b\uff0c\u73b0\u5728\u4e5f\u88ab\u8feb\u4e0d\u5f97\u4e0d\u5168\u795e\u8fce\u654c\u3002 \u3000\u3000\u53cc\u65b9\u62f3\u638c\u63a5\u8fde\u4ea4\u63a5\u4e86\u51e0\u6b21\uff0c\u9a6c\u662d\u96c4\u7684\u638c\u529b\u867d\u7136\u521a\u731b\u65e0\u4fe6\uff0c\u5728\u5e73\u5730\u8db3\u53ef\u5f00\u7891\u88c2\u77f3\uff0c\u504f\u504f\u5cb3 \u5c0f\u98de\u4eba\u5728\u7a7a\u4e2d\uff0c\u4ed6\u53ea\u80fd\u628a\u5bf9\u65b9\u9707\u8d77\u534a\u7a7a\uff0c\u800c\u5cb3\u5c0f\u98de\u5374\u501f\u7740\u8eab\u5b50\u4e0a\u5347\uff0c\u65e0\u5f62\u4e2d\u5378\u53bb\u4e86\u9a6c\u662d\u96c4\u51fb \u51fa\u7684\u638c\u529b\u3002 \u3000\u3000\u5c31\u8fd9\u6837\u4e00\u4eba\u5728\u4e0a\u4e00\u4eba\u5728\u4e0b\u53c8\u62da\u6597\u4e86\u597d\u51e0\u62db\u3002\u9a6c\u662d\u96c4\u5728\u6012\u6781\u4e4b\u4e0b\uff0c\u731b\u5438\u4e00\u53e3\u771f\u6c14\uff0c\u968f\u5373\u4e5f \u8dc3\u8d77\u7a7a\u4e2d\uff0c\u62da\u5c3d\u5168\u529b\uff0c\u6b63\u597d\u4e00\u638c\u51fb\u4e2d\u5cb3\u5c0f\u98de\u524d\u80f8\u3002 \u3000\u3000\u987f\u65f6\uff0c\u5cb3\u5c0f\u98de\u7684\u8eab\u5b50\u50cf\u65ad\u7ebf\u98ce\u7b5d\uff0c\u76f4\u5411\u8fdc\u5904\u5e73\u98de\u800c\u53bb\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u8881\u5c0f\u9e64\u60c5\u4e0d\u81ea\u7981\u53d1\u51fa\u4e00\u58f0\u60ca\u547c\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u4e5f\u77e5\u9053\u5cb3\u5c0f\u98de\u843d\u5730\u4e4b\u540e\uff0e\u5373\u4fbf\u5fc3\u8109\u4e0d\u88ab\u638c\u529b\u51fb\u65ad\uff0c\u81f3\u5c11\u4e5f\u8981\u6454\u4e2a\u534a\u6b7b\u3002 \u3000\u3000\u90a3\u77e5\u5cb3\u5c0f\u98de\u7adf\u7136\u98d8\u843d\u5728\u4e00\u68f5\u5927\u6811\u7684\u679d\u6860\u4e0a\uff0c\u795e\u8272\u5982\u5e38\uff0c\u7adf\u7136\u534a\u70b9\u6ca1\u53d7\u635f\u4f24\uff0c\u800c\u4e14\u8fd8\u5728\u62db \u624b\u9053\uff1a\u201c\u59d3\u9a6c\u7684\uff0c\u4f60\u8981\u8ffd\u53ea\u7ba1\u8ffd\u8fc7\u6765\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u65e9\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u82e5\u5355\u8bba\u8f7b\u529f\uff0c\u6bd4\u8d77\u8fd9\u5b69\u5b50\uff0c\u4ed6\u7167\u6837\u4e5f\u662f\u671b\u5c18\u83ab\u53ca\u3002 \u3000\u3000\u4ed6\u8f6c\u8fc7\u8eab\u6765\u9053\uff1a\u201c\u5c0f\u9e64\uff0c\u5bf9\u5927\u5e08\u5144\u8bf4\u5b9e\u8bdd\uff0c\u8fd9\u5b69\u5b50\u7a76\u7adf\u968f\u8001\u5934\u5b50\u4e60\u827a\u591a\u4e45\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5c0f\u5f1f\u65b9\u624d\u8bf4\u8fc7\uff0c\u5e08\u7236\u6536\u4ed6\uff0c\u4e0d\u8fc7\u624d\u4e09\u4e94\u4e2a\u6708\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u201c\u5927\u5e08\u5144\u4e0d\u4fe1\uff0c\u5c0f\u5f1f\u4e5f\u6ca1\u6709\u529e\u6cd5\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4ed6\u662f\u4ec0\u4e48\u8eab\u4efd\u6765\u5386\uff1f\u201d \u3000\u3000\u201c\u82e5\u95ee\u4ed6\u7684\u8eab\u4efd\u6765\u5386\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u80e1\u8bf4\uff01\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u53e5\u53e5\u5b9e\u8a00\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u51b7\u54fc\u9053\uff1a\u201c\u4f60\u4e0d\u80af\u8bb2\u4e5f\u6ca1\u5173\u7cfb\uff1f\u7b49\u6211\u4e0b\u6b21\u628a\u8fd9\u5c0f\u5b50\u6349\u5230\u624b\uff0c\u4e0d\u6015\u4ed6\u4e0d\u8bf4\u5b9e\u8bdd\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53eb\u9053\uff1a\u201c\u59d3\u9a6c\u7684\uff0c\u4f60\u4f55\u59a8\u73b0\u5728\u5c31\u6765\u6349\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u563f\u563f\u7b11\u9053\uff1a\u201c\u8001\u5b50\u73b0\u5728\u8fd8\u6709\u8981\u7d27\u7684\u4e8b\u8981\u529e\uff0c\u6ca1\u90a3\u4e48\u591a\u95f2\u529f\u592b\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u3002\u53d6\u8d77\u5730\u4e0a\u7684\u5251\uff0c\u626c\u957f\u800c\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8fd9\u624d\u7531\u6811\u4e0a\u8dc3\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u8d74\u524d\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u771f\u6ca1\u60f3\u5230\uff0c\u4f60\u4f1a\u8fd9\u6837\u7684\u4e86\u4e0d\u8d77\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5176\u5b9e\u5c0f\u5f1f\u4e5f\u53ea\u662f\u4ed7\u7740\u8f7b\u529f\uff0c\u82e5\u548c\u4ed6\u786c\u78b0\u786c\u7684\u6b63\u9762\u76f8\u62da\uff0c\u53ea\u6015\u4e0d\u51fa\u4e09\u62db\uff0c\u4fbf \u8981\u5403\u4e0a\u4ed6\u7684\u5927\u4e8f\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\uff1b\u201c\u5373\u4fbf\u90a3\u6837\uff0c\u6211\u4eca\u5929\u4e5f\u7b97\u5f00\u4e86\u773c\u754c\u5566\uff0c\u4ee5\u540e\uff0c\u6211\u7684\u6b66\u529f\uff0e\u5012\u8be5\u5411\u4f60\u8bf7\u6559\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u6562\u5f53\uff0c\u8881\u5927\u54e5\u5ba2\u6c14\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u987e\u4e0d\u5f97\u591a\u8bf4\uff0c\u5fd9\u9053\uff1a\u201c\u73b0\u5728\u5148\u8fdb\u5c4b\u53bb\u505a\u996d\u5403\u8981\u7d27\uff0c\u7b49\u5403\u8fc7\u996d\u6211\u518d\u5411\u4f60\u8ba8\u6559\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u521a\u624d\u90a3\u59d3\u9a6c\u7684\u597d\u50cf\u5728\u5e90\u4f2f\u4f2f\u623f\u91cc\u5f88\u4e45\uff0c\u54b1\u4eec\u8be5\u9a6c\u4e0a\u8fdb\u53bb\u770b\u770b\uff0c\u522b\u88ab\u4ed6\u6109\u8d70 \u4e86\u4ec0\u4e48\u4e1c\u897f\u3002\u201d \u3000\u3000\u8fdb\u5165\u6b63\u5c4b\u5e90\u4e91\u623f\u95f4\uff0c\u679c\u7136\u5e8a\u4e0a\u548c\u5730\u4e0b\u4e00\u7247\u96f6\u4e71\uff0c\u8fde\u5e8a\u4e0b\u4e5f\u4f3c\u4e4e\u88ab\u641c\u67e5\u8fc7\uff0c\u53ea\u56e0\u8881\u5c0f\u9e64\u4e0d \u77e5\u5e08\u7236\u623f\u4e2d\u85cf\u6709\u673a\u5bc6\u4e1c\u897f\uff0c\u5373\u4fbf\u771f\u6709\u4e1c\u897f\u88ab\u9a6c\u662d\u96c4\u76d7\u8d70\uff0c\u4ed6\u4e5f\u65e0\u4ece\u67e5\u8d77\u3002 \u3000\u3000\u4e24\u4eba\u5306\u5306\u628a\u5ba4\u5185\u6574\u7406\u4e86\u4e00\u904d\uff0c\u8881\u5c0f\u9e64\u7acb\u5373\u8fdb\u5165\u53a8\u623f\u70e7\u996d\u3002 \u3000\u3000\u4ed6\u4eec\u4ece\u6628\u665a\u5230\u73b0\u5728\u7c92\u7c73\u672a\u8fdb\uff0c\u5f53\u7136\u65e9\u5df2\u9965\u80a0\u8f98\u8f98\uff0c\u4e00\u987f\u996d\u5403\u5f97\u4e0d\u4e9a\u72fc\u541e\u864e\u54bd\u3002 \u3000\u3000\u9910\u540e\uff0c\u4e24\u4eba\u5404\u81ea\u56de\u623f\u91cc\u7761\u4e86\u4e00\u89c9\u3002 \u3000\u3000\u5f53\u9192\u672b\u540e\u4e24\u4eba\u518d\u89c1\u9762\u65f6\uff0c\u5929\u8272\u53c8\u5df2\u665a\u4e86\uff0c\u8881\u5c0f\u9e64\u53c8\u5f97\u4e0b\u53a8\u505a\u996d\uff0c\u5cb3\u5c0f\u98de\u5219\u5e2e\u7740\u70e7\u706b\u3002 \u3000\u3000\u5728\u8fd1\u5341\u5e74\u6765\uff0c\u70e7\u996d\u4e00\u76f4\u662f\u8881\u5c0f\u9e64\u8d1f\u8d23\uff0c\u4ed6\u5df2\u7ec3\u5f97\u4e00\u624b\u597d\u70f9\u8c03\u5de5\u592b\u3002\u5728\u8305\u5c4b\u524d\u540e\uff0c\u79cd\u4e86\u4e0d \u5c11\u852c\u83dc\uff0c\u800c\u4e14\u8fd8\u517b\u7740\u9e21\uff0c\u5e73\u5e38\u8881\u5c0f\u9e64\u53ea\u662f\u6bcf\u9694\u51e0\u5929\uff0c\u5230\u5c71\u4e0b\u53bb\u4e70\u4e00\u6b21\u7c73\u548c\u9c7c\u8089\uff0c\u8fd9\u4e9b\u5de5\u4f5c\u5df2 \u7ecf\u6210\u4e86\u4ed6\u7684\u4efd\u5185\u4e4b\u4e8b\u3002 \u3000\u3000\u665a\u4e0a\uff0c\u4ed6\u4eec\u9884\u6599\u5e90\u4e91\u4e0d\u53ef\u80fd\u56de\u6765\uff0c\u53c8\u62c5\u5fc3\u9a6c\u662d\u96c4\u518d\u56de\u6765\u627e\u78b4\uff0c\u4e24\u4eba\u4fbf\u5728\u7af9\u7bf1\u5185\u7684\u9662\u5b50\u91cc \u804a\u5929\u3002 \u3000\u3000\u597d\u5728\u517b\u7740\u4e00\u6761\u5927\u9ec4\u72d7\uff0c\u4e00\u6709\u52a8\u9759\uff0c\u90a3\u5927\u9ec4\u72d7\u5fc5\u5148\u60ca\u89c9\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8d81\u673a\u95ee\u8d77\u6709\u5173\u9a6c\u662d\u96c4\u7684\u4e8b\u3002 \u3000\u3000\u201c\u8881\u5927\u54e5\uff0c\u90a3\u59d3\u9a6c\u7684\u771f\u662f\u4f60\u7684\u5927\u5e08\u5144\u4e48\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5f53\u7136\u662f\uff0c\u90a3\u600e\u4e48\u4f1a\u5047\u5462\u3002\u201d \u3000\u3000\u201c\u5e90\u4f2f\u4f2f\u4e00\u5171\u6536\u4e86\u51e0\u4f4d\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u201c\u9664\u4e86\u4ed6\uff0c\u5c31\u662f\u6211\u4e86\u3002\u201d \u3000\u3000\u201c\u4ed6\u65e2\u7136\u54c1\u5fb7\u4e0d\u597d\uff0c\u5e90\u4f2f\u4f2f\u5f53\u521d\u4e3a\u4ec0\u4e48\u8981\u6536\u4ed6\uff1f\u201d \u3000\u3000\u201c\u77e5\u4eba\u77e5\u9762\u4e0d\u77e5\u5fc3\uff0c\u5f53\u4ed6\u521d\u8fdb\u5e08\u95e8\u65f6\uff0c\u4e0d\u4f46\u5929\u8d44\u806a\u660e\uff0c\u800c\u4e14\u53c8\u52e4\u594b\u597d\u5b66\uff0c\u5e08\u7236\u975e\u5e38\u559c\u6b22 \u4ed6\uff0c\u8ba4\u4e3a\u7ec8\u4e8e\u5f97\u5230\u4e86\u8863\u94b5\u4f20\u4eba\u3002\u201d \u3000\u3000\u201c\u4ed6\u53c8\u4e3a\u4ec0\u4e48\u88ab\u5e90\u4f2f\u4f2f\u9010\u51fa\u95e8\u5899\uff1f\u201d \u3000\u3000\u201c\u5176\u5b9e\u4ed6\u662f\u81ea\u5df1\u53db\u79bb\u5e08\u95e8\u7684\uff0c\u5e76\u6ca1\u7b49\u5e08\u7236\u9010\u4ed6\uff0c\u4ed6\u5c31\u8d70\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u7d27\u8e59\u7709\u5934\uff0c\u663e\u793a\u51fa\u6781\u5ea6\u75db\u82e6\u6a21\u6837\uff0c\u4e00\u770b\u4fbf\u77e5\u5fc5\u6709\u96be\u8a00\u4e4b\u9690\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u4f7f\u5f97\u5cb3\u5c0f\u98de\u8d8a\u53d1\u6025\u4e8e\u77e5\u9053\uff0c\u60c5\u4e0d\u81ea\u7981\u50ac\u4fc3\u7740\u9053\uff1a \u3000\u3000\u201c\u8881\u5927\u54e5\u600e\u4e48\u4e0d\u8bf4\u4e86\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u54bd\u4e0b\u4e00\u53e3\u553e\u6db2\u9053\uff1a\u201c\u5bb6\u4e11\u4e0d\u53ef\u5916\u626c\uff0c\u6559\u6211\u5b9e\u5728\u96be\u4ee5\u542f\u9f7f\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5c0f\u5f1f\u73b0\u5728\u548c\u5e90\u4f2f\u4f2f\u3001\u8881\u4eba\u54e5\u96be\u9053\u8fd8\u4e0d\u7b97\u4e00\u5bb6\u4eba\u4e48\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u8fd9\u79cd\u4e8b\u6211\u4e0d\u5e0c\u671b\u4f60\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u672a\u514d\u592a\u89c1\u5916\u4e86\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u987f\u4e86\u987f\uff0c\u7ec8\u4e8e\u518d\u53f9\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u544a\u8bc9\u4f60\uff0c\u4ed6\u628a\u5e08\u5a18\u62d0\u8d70\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u50cf\u88ab\u4e00\u58f0\u5de8\u96f7\u9707\u4f4f\uff0c\u8bb8\u4e45\uff0c\u624d\u6115\u7136\u95ee\u9053\uff1a\u201c\u4ed6\u548c\u5e90\u4f2f\u6bcd\u5e74\u7eaa\u4e00\u5b9a\u76f8\u5dee\u5f88\u591a\uff0c\u600e\u4f1a \u53d1\u751f\u8fd9\u79cd\u4e8b\u60c5\uff0c\u83ab\u975e\u5e90\u4f2f\u6bcd\u662f\u5fc3\u7518\u60c5\u613f\u7684\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9eef\u7136\u6447\u5934\u9053\uff1a\u201c\u8bf4\u8d77\u6765\u4ed6\u548c\u5e08\u5a18\u7684\u5e74\u7eaa\u76f8\u5dee\u4e5f\u5e76\u4e0d\u5f88\u591a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u53c8\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5e08\u7236\u4e09\u5341\u51e0\u5c81\u624d\u548c\u5e08\u5a18\u6210\u5a5a\u7684\uff0c\u5f53\u65f6\u5e08\u5a18\u8fd8\u4e0d\u5230\u4e8c\u5341\u5c81\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u4ed6\u4eec\u592b\u5987\u4ece\u6765\u5c31\u4e0d\u66fe\u6069\u7231\u8fc7\uff1f\u201d \u3000\u3000\u201c\u4e0d\uff0c\u5f53\u521d\u90a3\u513f\u5e74\u636e\u8bf4\u4ed6\u4eec\u8fc7\u4e86\u4e00\u6bb5\u5f88\u751c\u871c\u7684\u751f\u6d3b\uff0c\u4e24\u4eba\u7ec8\u53e3\u5f62\u5f71\u4e0d\u79bb\uff0c\u7b80\u76f4\u79f0\u5f97\u4e0a\u53ea \u7fa1\u9e33\u9e2f\u4e0d\u7fa1\u4ed9\u4e86\uff0c\u8fde\u6b66\u6797\u4e2d\u90fd\u4f20\u4e3a\u4f73\u8bdd\uff0c \u3000\u3000\u5f53\u7136\uff0c\u90a3\u65f6\u5019\u627e\u8fd8\u672a\u88ab\u5e08\u7236\u6536\u5f52\u95e8\u4e0b\uff0c\u5e76\u6ca1\u4eb2\u773c\u89c1\u8fc7\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u66fe\u89c1\u8fc7\u5e90\u4f2f\u6bcd\uff1f\u201d \u3000\u3000\u201c\u5979\u662f\u6211\u88ab\u5e08\u7236\u6536\u5f52\u95e8\u4e0b\u7684\u7b2c\u4e8c\u5e74\u624d\u79bb\u5f00\u5e08\u7236\uff0c\u5f53\u7136\u89c1\u8fc7\uff0c\u4e0d\u8fc7\u90a3\u65f6\u5979\u548c\u9a6c\u662d\u96c4\u5f88\u53ef\u80fd \u5df2\u7ecf\u6697\u4e2d\u6765\u5f80\uff0c\u548c\u5e08\u7236\u4f3c\u4e4e\u5df2\u8c8c\u5408\u795e\u79bb\u4e86\u3002\u201d \u3000\u3000\u201c\u5e90\u4f2f\u6bcd\u957f\u5f97\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u76ee\u5149\u4e00\u4eae\uff0c\u7acb\u5373\u7838\u7740\u5634\u9053\uff1a\u201c\u5979\u957f\u5f97\u5b9e\u5728\u592a\u7f8e\u4e86\uff0c\u6211\u81f3\u4eca\u5c1a\u672a\u89c1\u8fc7\u4e16\u4e0a\u6709\u90a3\u4e48\u7f8e \u7684\u5973\u4eba\uff0c\u7b80\u76f4\u50cf\u4ed9\u5973\u4e0b\u51e1\u4e00\u822c\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9ed8\u4e86\u2014\u9ed8\uff0c\u518d\u95ee\uff1a\u201c\u90a3\u65f6\u5e90\u4f2f\u4f2f\u662f\u5426\u4e5f\u4f4f\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6447\u5934\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u5e08\u5a18\u548c\u9a6c\u662d\u96c4\u79c1\u5954\u540e\uff0c\u5e08\u7236\u624d\u5e26\u7740\u6211\u8fc1\u8fc7\u6765\u7684\uff0c\u7b97\u8d77\u6765\u5230\u73b0\u5728 \u4e5f\u5c06\u8fd1\u5341\u5e74\u4e86\u3002\u201d \u3000\u3000\u201c\u5e90\u4f2f\u4f2f\u4e3a\u4ec0\u4e48\u8981\u642c\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u5e08\u7236\u5df2\u65e0\u989c\u518d\u89c1\u6c5f\u6e56\u6545\u4eba\uff0c\u6240\u4ee5\u624d\u642c\u5230\u8fd9\u51e0\u4e4e\u4e0e\u4e16\u9694\u7edd\u7684\u5c71\u4e0a\u6765\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u76ee\u524d\u6b66\u6797\u4e2d\u5e76\u65e0\u4eba\u77e5\u9053\u5e90\u4f2f\u4f2f\u9690\u5c45\u5728\u8fd9\u5730\u65b9\u4e86\uff1f\u201d \u3000\u3000\u201c\u7eb5\u7136\u6709\u4eba\u77e5\u9053\uff0c\u4e5f\u662f\u7edd\u5bf9\u5c11\u6570\uff0c\u5728\u6211\u7684\u8bb0\u5fc6\uff0c\u5e08\u7236\u4ece\u524d\u8bbf\u5ba2\u4e0d\u65ad\uff0c\u81ea\u4ece\u642c\u5230\u8fd9\u91cc\u5c06\u8fd1 \u5341\u5e74\uff0c\u6765\u8bbf\u8fc7\u4ed6\u7684\uff0c\u597d\u50cf\u4e0d\u8d85\u8fc7\u4e09\u4e2a\u3002\u201d \u3000\u3000\u201c\u662f\u5426\u5305\u62ec\u6d1e\u4ed9\u5728\u5185\uff1f\u201d \u3000\u3000\u201c\u6d1e\u4ed9\u662f\u4ece\u4e0d\u51fa\u6d1e\u7684\uff0c\u53ea\u6709\u5e08\u7236\u53bb\u8bbf\u4ed6\uff0c\u4ed6\u4ece\u6ca1\u6765\u8bbf\u5e08\u7236\u3002\u201d \u3000\u3000\u6b63\u8bf4\u5230\u8fd9\u91cc\uff1f\u5ffd\u542c\u5927\u9ec4\u72d7\u53d1\u51fa\u72c2\u5420\u3002 \u3000\u3000\u4e24\u4eba\u6025\u6025\u8d77\u8eab\uff0c\u7531\u7bf1\u7f1d\u5185\u5411\u5916\u671b\u53bb\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u53ea\u89c1\u5341\u4f59\u4e08\u5916\u4f3c\u662f\u4e00\u5bf9\u7537\u5973\u7684\u4e24\u6761\u4eba\u5f71\uff0c\u6b63\u671d\u7740\u8305\u5c4b\u8fc5\u5feb\u800c\u6765\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u5446\u4e00\u5446\uff0c\u7acb\u5373\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u597d\u50cf\u662f\u9a6c\u662d\u96c4\u548c\u5e08\u5a18\u3001\u5c0f\u98de\uff0c\u4f60\u5feb\u5feb\u8eb2\u5f00\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u611f\u5fc3\u5934\u4e00\u51db\uff0c\u5fd9\u9053\uff1a\u201c\u8881\u5927\u54e5\u8be5\u8eb2\u8d77\u6765\u624d\u662f\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u6211\u5fc5\u987b\u786c\u8d77\u5934\u76ae\u6765\u5e94\u4ed8\u4ed6\u4eec\uff0c\u5426\u5219\u4ed6\u4eec\u628a\u706b\u628a\u8305\u5c4b\u70e7\u4e86\uff0c\u8ba9\u6211\u600e\u6837 \u5411\u5e08\u7236\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u7559\u5728\u8fd9\u91cc\uff0c\u4e0d\u662f\u5f88\u5371\u9669\uff1f\u4e07\u4e00\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u4f1a\u7684\uff0c\u4ed6\u4eec\u5bf9\u6211\uff0c\u5e76\u6ca1\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\uff0c\u5c0f\u98de\uff0c\u4f60\u5feb\u8d70\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u987e\u4e0d\u5f97\u518d\u529d\u8881\u5c0f\u9e64\uff0c\u8fde\u5fd9\u5954\u5411\u5c4b\u540e\uff0c\u518d\u98de\u8eab\u4e0a\u4e86\u6b63\u5c4b\u5c4b\u9762\uff0c\u7531\u5c4b\u810a\u5904\u63a2\u5934\u89c2\u671b\uff0c \u4ee5\u4fbf\u770b\u4e2a\u7a76\u7adf\u3002 \u3000\u3000\u5f53\u7136\uff0c\u6700\u4e3b\u8981\u7684\uff0c\u4ed6\u662f\u60f3\u770b\u6e05\u8fd9\u4f4d\u548c\u5f1f\u5b50\u6deb\u5954\u7684\u5e90\u4f2f\u6bcd\u7a76\u7adf\u957f\u5f97\u4ec0\u4e48\u6a21\u6837\uff0c\u56e0\u4e3a\u65b9\u624d\u8881 \u5c0f\u9e64\u5bf9\u5979\u7684\u5f62\u5bb9\uff0c\u4e0d\u80fd\u4e0d\u5f15\u8d77\u4ed6\u7684\u597d\u5947\u3002 \u3000\u3000\u679c\u7136\uff0c\u90a3\u4e24\u6761\u4eba\u5f71\uff0c\u5f88\u5feb\u4fbf\u6765\u5230\u7bf1\u95e8\u5916\uff0c\u800c\u4e14\u6b63\u662f\u4e00\u7537\u4e00\u5973\u3002 \u3000\u3000\u90a3\u7537\u7684\u4e5f\u6b63\u662f\u9a6c\u662d\u96c4\u3002 \u3000\u3000\u5973\u7684\u4e0d\u6d88\u8bf4\u4e5f\u6b63\u662f\u5f03\u592b\u6deb\u5954\u4e71\u4f26\u8d25\u5fb7\u7684\u51e4\u5ae3\u7ea2\u4e86\u3002 \u3000\u3000\u53ea\u56fa\u8ddd\u79bb\u8f83\u8fdc\uff0c\u90a3\u5973\u4eba\u53c8\u9762\u7f69\u4e00\u5c42\u8584\u7eb1\uff0c\u867d\u6709\u6708\u5149\uff0c\u5cb3\u5c0f\u98de\u4e5f\u65e0\u6cd5\u770b\u6e05\u5979\u7684\u9762\u8c8c\u3002 \u3000\u3000\u4e0d\u8fc7\u770b\u5979\u8eab\u6750\u5a40\u5a1c\uff0c\u884c\u8fdb\u95f4\u6447\u6446\u751f\u59ff\uff0c\u6709\u5982\u98ce\u6446\u6768\u67f3\u4e00\u822c\uff0c\u4fbf\u4e0d\u96be\u60f3\u5230\u5979\u662f\u600e\u6837\u4e00\u4e2a\u5973 \u4eba\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u8881\u5c0f\u9e64\u65e9\u5df3\u7ad9\u5728\u7af9\u7bf1\u95e8\u53e3\uff0c\u8eac\u8eab\u65bd\u4e86\u4e00\u793c\u9053\uff1b\u201c\u5f1f\u5b50\u8881\u5c0f\u9e64\u8fce\u63a5\u5e08\u5a18\u3001\u5927\u5e08\u5144\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u548c\u9a6c\u662d\u96c4\u5e76\u672a\u8a00\u8bed\uff0c\u8ff3\u81ea\u8fdb\u5165\u7bf1\u95e8\u3002 \u3000\u3000\u4e24\u4eba\u4e1c\u5f20\u897f\u671b\u4e86\u534a\u664c\uff0c\u51e4\u5ae3\u7ea2\u624d\u54cd\u8d77\u5a07\u6ef4\u6ef4\u6709\u5982\u71d5\u556d\u83ba\u5456\u822c\u7684\u58f0\u97f3\u9053\uff1b\u201c\u8001\u5934\u5b50\u5462\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8c28\u58f0\u7b54\u9053\uff1a\u201c\u5e08\u7236\u5916\u51fa\u672a\u56de\uff0c\u5f1f\u5b50\u5148\u524d\u5df2\u5bf9\u5927\u5e08\u5144\u8bb2\u8fc7\u4e86\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u51b7\u7b11\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u6700\u8fd1\u5341\u5e74\uff0c\u5df2\u51e0\u4e4e\u4e0e\u5c18\u4e16\u9694\u7edd\uff0c\u73b0\u5728\u5c45\u7136\u53c8\u5916\u51fa\u8bbf\u53cb\uff0e\u8fde\u665a\u4e0a \u90fd\u4e0d\u56de\u6765\uff0c\u770b\u6837\u5b50\u597d\u50cf\u53c8\u6d3b\u8dc3\u8d77\u6765\u4e86\uff0c\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u8bbf\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u53ea\u8bf4\u8981\u5916\u51fa\u8bbf\u53cb\uff0c\u522b\u7684\u4ec0\u4e48\u90fd\u6ca1\u8bf4\u3002\u201d \u3000\u3000\u201c\u53ef\u77e5\u9053\u4ed6\u4ec0\u4e48\u65f6\u5019\u56de\u6765\uff1f\u201d \u3000\u3000\u201c\u5f1f\u5b50\u4e5f\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u5c0f\u9e64\uff0c\u4f60\u53ef\u662f\u8ddf\u6211\u88c5\u50bb\uff1f\u201d \u3000\u3000\u201c\u5f1f\u5b50\u4e0d\u6562\u3002\u201d \u3000\u3000\u53ea\u542c\u9a6c\u662d\u96c4\u9053\uff1a\u201c\u522b\u8ddf\u4ed6\u7f57\u55e6\uff0c\u5148\u627e\u5230\u90a3\u5c0f\u5b50\u518d\u8bf4\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u53c8\u56db\u4e0b\u671b\u4e86\u4e00\u773c\uff0c\u624d\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u5e08\u7236\u65b0\u8fd1\u53c8\u6536\u4e0b\u4e00\u540d\u5f1f\u5b50\uff0c\u5e74\u7eaa\u5f88\u8f7b\uff0c\u672c\u9886\u5374 \u5927\u5f97\u5f88\uff0c\u4ed6\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u65e9\u6709\u51c6\u5907\uff0c\u4e0d\u614c\u4e0d\u5fd9\u9053\uff1a\u201c\u4ed6\u5728\u4e0b\u5348\u5192\u72af\u4e86\u5927\u5e08\u5144\uff0c\u5fc3\u91cc\u5bb3\u6015\uff0c\u5403\u8fc7\u665a\u996d\u5c31\u6e9c\u5230 \u5916\u9762\u8eb2\u4e86\uff0c\u5230\u73b0\u5728\u8fd8\u6ca1\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u4ed6\u8eb2\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u5f1f\u5b50\u65b9\u624d\u8fd8\u627e\u8fc7\uff0c\u5c71\u8fd9\u4e48\u5927\uff0c\u6839\u672c\u65e0\u6cd5\u627e\u5230\uff0c\u5e73\u5e38\u5f1f\u5b50\u5929\u4e00\u9ed1\u5c31\u7761\u89c9\uff0c\u4eca\u665a\u5230\u8fd9\u65f6\u8fd8 \u6ca1\u7761\uff0c\u5c31\u662f\u56e0\u4e3a\u627e\u4ed6\u803d\u8bef\u4e86\u3002\u201d \u3000\u3000\u5ffd\u542c\u9a6c\u662d\u96c4\u9690\u68ee\u68ee\u7684\u58f0\u97f3\u9053\uff1a\u201c\u4f60\u522b\u542c\u4ed6\u7684\uff0c\u90a3\u5c0f\u5b50\u80c6\u5b50\u6bd4\u5929\u5927\uff0c\u4ed6\u5f53\u65f6\u90fd\u4e0d\u77e5\u9053\u5bb3\u6015\uff0c \u6211\u8d70\u4e86\u4ed6\u4f1a\u5bb3\u6015\uff0c\u7b80\u76f4\u662f\u7b11\u8bdd\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6b63\u8272\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u4ed6\u7684\u786e\u5413\u8dd1\u4e86\uff0c\u5c0f\u5f1f\u600e\u6562\u9a97\u5e08\u5a18\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u9053\uff1a\u201c\u90a3\u6211\u53ef\u8981\u641c\u4e86\uff0c\u4ed6\u82e5\u73b0\u5728\u81ea\u52a8\u51fa\u6765\uff0c\u4ec0\u4e48\u4e8b\u4e5f\u6ca1\u6709\uff0c\u82e5\u7b49\u641c\u51fa\u6765\uff0c\u90a3\u5c31\u4e0d \u4e00\u6837\u4e86\uff01\u201d \u3000\u3000\u201c\u5927\u5e08\u5144\u8981\u641c\u53ea\u7ba1\u641c\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u6b63\u8981\u91c7\u53d6\u884c\u52a8\uff0c\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u5c31\u8fd9\u4e48\u51e0\u95f4\u7834\u623f\u5b50\uff1f\u4ed6\u5f53\u7136\u4e0d\u4f1a\u8eb2\u5728\u91cc\u9762 \u8ba9\u4f60\u641c\u3002\u201d \u3000\u3000\u63a5\u7740\u53c8\u4fa7\u8fc7\u8138\u6765\u9053\uff1a\u201c\u5c0f\u9e64\uff0c\u662d\u96c4\u4e0b\u5348\u8981\u5e26\u4f60\u8d70\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u7b54\u5e94\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u987f\u4e86\u987f\u9053\uff1a\u201c\u5f1f\u5b50\u53ea\u89c9\u6b66\u529f\u4e0d\u6d4e\uff0c\u73b0\u5728\u8fd8\u4e0d\u914d\u5728\u6b66\u6797\u884c\u8d70\u3002\u201d \u3000\u3000\u201c\u8001\u5934\u5b50\u5df2\u7ecf\u8c03\u6559\u4f60\u5341\u51e0\u5e74\u4e86\uff0c\u4f60\u73b0\u5728\u7684\u6b66\u529f\u5e94\u8be5\u4e0d\u9519\u624d\u5bf9\u3002\u201d \u3000\u3000\u201c\u90fd\u56e0\u4e3a\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6\u8fd9\u4e9b\u5e74\u5fc3\u60c5\u4e0d\u597d\uff0c\u5f88\u5c11\u518d\u6559\u5f1f\u5b50\u6b66\u529f\uff0c\u4eca\u5929\u4e0b\u5348\u5927\u5e08\u5144\u5df2\u7ecf\u8bd5\u8fc7 \u5f1f\u5b50\uff0c\u5e08\u5a18\u4e0d\u4fe1\u53ef\u4ee5\u95ee\u5927\u5e08\u5144\u3002\u201d \u3000\u3000\u201c\u90a3\u5b69\u5b50\u7684\u6b66\u529f\u4e3a\u4ec0\u4e48\u90a3\u6837\u9ad8\uff1f\u201d \u3000\u3000\u201c\u4ed6\u662f\u5728\u672a\u6295\u5e08\u4ee5\u524d\uff0c\u5c31\u5177\u6709\u4e00\u8eab\u7edd\u9876\u8f7b\u529f\uff0c\u4f46\u6b66\u529f\u5374\u77e5\u9053\u7684\u4e0d\u591a\u3002\u201d \u3000\u3000\u201c\u4ed6\u7684\u8f7b\u529f\u53c8\u662f\u600e\u4e48\u5b66\u7684\uff1f\u201d \u3000\u3000\u201c\u636e\u8bf4\u4ed6\u81ea\u5c0f\u5728\u5c71\u4e0a\uff0c\u662f\u6574\u65e5\u722c\u5c71\u4e0a\u6811\u81ea\u5df1\u7ec3\u6210\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u662f\u4ec0\u4e48\u6765\u5386\uff1f\u201d \u3000\u3000\u201c\u4ed6\u65e0\u5bb6\u53ef\u5f52\uff0c\u5728\u4e00\u4e2a\u5076\u7136\u7684\u673a\u4f1a\u88ab\u5e08\u7236\u6536\u7559\uff0c\u548c\u6b66\u6797\u6839\u672c\u626f\u4e0d\u4e0a\u5173\u7cfb\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9ed8\u4e86\u534a\u664c\uff0c\u5374\u53c8\u8f6c\u53d8\u8bdd\u9898\u95ee\u9053\uff1a\u201c\u4f60\u5e08\u7236\u4f4f\u54ea\u95f4\u623f\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e0d\u6562\u9690\u7792\uff0c\u5411\u6b63\u5c4b\u6307\u4e86\u4e00\u6307\u9053\uff1a\u201c\u5c31\u662f\u90a3\u95f4\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u6211\u60f3\u8fdb\u53bb\u770b\u770b\uff0c\u53ef\u4ee5\u4e48\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u600e\u6562\u8bf4\u4e2a\u4e0d\u5b57\uff0c\u55eb\u55eb\u7740\u9053\uff1a\u201c\u5e08\u5a18\u8981\u8fdb\u53bb\u770b\uff0c\u5f53\u7136\u53ef\u4ee5\u3002\u4e0d\u8fc7\u5e08\u7236\u73b0\u5728\u7684\u5c45\u5ba4\u7b80 \u964b\u5f97\u5f88\uff0c\u5b9e\u5728\u6ca1\u4ec0\u4e48\u503c\u5f97\u770b\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u53ea\u662f\u8981\u8fdb\u53bb\u770b\u770b\uff0c\u8c01\u7ba1\u4ed6\u7b80\u964b\u4e0d\u7b80\u964b\uff0c\u4f60\u5148\u8fdb\u53bb\u628a\u706f\u70b9\u4e0a\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4f9d\u8a00\u8bb2\u5165\u5e90\u4e91\u623f\u95f4\uff0c\u628a\u706f\u70b9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u706f\u4eae\u4e0d\u4e45\uff0c\u51e4\u5ae3\u7ea2\u548c\u9a6c\u662d\u96c4\u5c31\u8ddf\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u5c0f\u9e64\uff0c\u4f60\u51fa\u53bb\uff0c\u8981\u8d70\u5230\u7af9\u7bf1\u4ee5\u5916\uff0c\u4e0d\u53ef\u9760\u8fd1\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6123\u4e86\u6123\u9053\uff1a\u201c\u83ab\u975e\u5e08\u5a18\u4eca\u665a\u8981\u4f4f\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u79cd\u5730\u65b9\uff0c\u6211\u8fd8\u4e0d\u5c51\u4f4f\uff0c\u4e0d\u5fc5\u591a\u95ee\uff0c\u5feb\u51fa\u53bb\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e0d\u5f97\u4e0d\u51fa\u53bb\uff0c\u800c\u4e14\u771f\u7684\u8eb2\u5230\u7af9\u7bf1\u4e4b\u5916\u3002 \u3000\u3000\u84e6\u5730\uff0c\u4ed6\u4e0b\u610f\u8bc6\u60f3\u5230\u4e00\u4ef6\u4e8b\uff0c\u83ab\u975e\u8fd9\u4e00\u7537\u4e00\u5973\uff0c\u662f\u8981\u5728\u5e08\u7236\u5e8a\u4e0a\u529e\u90a3\u89c1\u4e0d\u5f97\u4eba\u7684\u82df\u4e14\u4e4b \u4e8b\uff1f \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u4ed6\u4e0d\u7981\u6068\u5f97\u8fde\u5934\u8111\u90fd\u6709\u4e9b\u53d1\u660f\uff0c\u82e5\u5e08\u7236\u7684\u5e8a\uff0c\u88ab\u505a\u4e86\u8fd9\u5bf9\u65e0\u803b\u7537\u5973\u7684\u82df\u5408\u4e4b \u5730\uff0c\u90a3\u771f\u662f\u8981\u7a9d\u56ca\u5230\u4e0d\u80fd\u518d\u7a9d\u56ca\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\u5f88\u5feb\u4ed6\u5c31\u53c8\u89c9\u51fa\u53ef\u80fd\u4e0d\u662f\uff0c\u540c\u4e3a\u7ecf\u8fc7\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u5e08\u7236\u623f\u95f4\u7684\u706f\u8fd8\u662f\u4eae\u7740\uff0c\u4ed6\u4eec \u603b\u4e0d\u80fd\u65e0\u803b\u5230\u4eae\u7740\u706f\u505a\u90a3\u79cd\u4e8b\u5427\u3002 \u3000\u3000\u4f46\u4ed6\u4eec\u53c8\u5728\u505a\u4ec0\u4e48\u5462\uff1f \u3000\u3000\u5374\u53c8\u4e0d\u6562\u8fd1\u524d\u5bdf\u770b\u3002 \u3000\u3000\u8db3\u8db3\u534a\u4e2a\u65f6\u8fb0\u8fc7\u53bb\uff0c\u51e4\u5ae3\u7ea2\u548c\u9a6c\u662d\u96c4\u624d\u7531\u91cc\u800c\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u968f\u5373\u4e5f\u8fdb\u4e86\u7bf1\u95e8\u3002 \u3000\u3000\u53ea\u542c\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u5c0f\u9e64\uff0c\u6211\u4eec\u8d70\u4e86\uff0c\u4f60\u4e5f\u8be5\u7761\u4e86\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u642d\u6c55\u7740\u95ee\uff1a\u201c\u8fd9\u4e48\u665a\u4e86\uff0c\u9644\u8fd1\u53c8\u6ca1\u57ce\u9547\uff0c\u5e08\u5a18\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u7b11\u9053\uff1a\u201c\u8fd9\u79cd\u5730\u65b9\u54ea\u91cc\u8fd8\u6709\u6211\u4f4f\u7684\uff0c\u4f60\u82e5\u771f\u7684\u60f3\u77e5\u9053\u6211\u8981\u5230\u54ea\u91cc\u53bb\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8ddf \u7740\u6211\u8d70\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u88ab\u95ee\u5f97\u7b54\u4e0d\u4e0a\u8bdd\uff0c\u773c\u770b\u7740\u8fd9\u5bf9\u65e0\u803b\u7537\u5973\u6d88\u5931\u5728\u591c\u8272\u4e2d\u3002 \u3000\u3000\u73b0\u5728\u8881\u5c0f\u9e64\u7684\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u4fbf\u662f\u8fdb\u5165\u5e08\u7236\u623f\u4e2d\u5bdf\u770b\u3002 \u3000\u3000\u5f53\u4ed6\u521a\u8fdb\u5165\u4e0d\u4e45\uff0c\u5cb3\u5c0f\u98de\u4e5f\u8ddf\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5c71\u4e4e\u4e24\u4eba\u610f\u6599\u4e4b\u5916\u7684\uff0c\u623f\u5185\u4e00\u5207\u5982\u6545\uff0c\u50cf\u6839\u672c\u4e0d\u66fe\u906d\u52ab\u8fc7\u3002 \u3000\u3000\u90a3\u4e48\u8fd9\u6574\u6574\u534a\u4e2a\u65f6\u8fb0\uff0c\u51e4\u5ae3\u7ea2\u548c\u9a6c\u662d\u96c4\u53c8\u5728\u505a\u4ec0\u4e48\u52fe\u5f53\u5462\uff1f \u3000\u3000\u591a\u60f3\u4e5f\u6ca1\u6709\u7528\uff0c\u4e24\u4eba\u53ea\u597d\u5404\u81ea\u56de\u623f\u5c31\u5bdd\u3002 \u3000\u3000\u591c\u91cc\u5e76\u65e0\u4efb\u4f55\u52a8\u9759\u3002 \u3000\u3000\u6b21\u53e3\u508d\u665a\uff0c\u5e90\u4e91\u56de\u6765\u4e86\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u62b1\u7740\u83ab\u5927\u5e0c\u671b\uff0c\u56e0\u4e3a\u4ed6\u9884\u6599\u5e90\u4e91\u5f88\u53ef\u80fd\u5df2\u5f97\u77e5\u4ed6\u7684\u7236\u6bcd\u662f\u8c01\u3002 \u3000\u3000\u665a\u9910\u65f6\uff0c\u5cb3\u5c0f\u98de\u8feb\u4e0d\u53ca\u5f85\u7684\u95ee\u8d77\u3002 \u3000\u3000\u8c01\u77e5\u5e90\u4e91\u5374\u6447\u6447\u5934\uff0c\u63a5\u7740\u957f\u957f\u5401\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u8ba9\u4f60\u5931\u671b\u4e86\u3002\u4ecd\u7136\u6ca1\u5f04\u6e05\u695a\u4ee4\u5c0a\u4ee4 \u5802\u662f\u8c01\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6005\u7136\u518d\u95ee\uff1a\u201c\u83ab\u975e\u6d1e\u4ed9\u8001\u524d\u8f88\u7684\u90a3\u4f4d\u670b\u53cb\u4e0d\u66fe\u5230\u6d1e\u5e9c\u53bb\uff1f\u201d \u3000\u3000\u5e90\u4e91\u9053\uff1a\u201c\u6d1e\u4ed9\u7684\u90a3\u4f4d\u670b\u53cb\uff0c\u4f9d\u7ea6\u800c\u6765\uff0c\u6d1e\u4ed9\u4e5f\u65bd\u51fa\u4e86\u548c\u4e0a\u6b21\u540c\u6837\u7684\u6cd5\u672f\uff0c\u4f46\u4ed6\u7684\u90a3\u4f4d \u670b\u53cb\u5374\u5e76\u4e0d\u8ba4\u8bc6\u4ee4\u5c0a\u4ee4\u5802\u3002\u201d \u3000\u3000\u4ed6\u89c1\u5cb3\u5c0f\u98de\u4e00\u526f\u5931\u795e\u843d\u9b44\u6a21\u6837\uff0c\u53ea\u5f97\u53c8\u5b89\u6170\u7740\u9053\uff1a\u201c\u4e0d\u5fc5\u96be\u8fc7\uff0c\u76f8\u4fe1\u603b\u6709\u4e00\u5929\u4f1a\u67e5\u51fa\u4ee4 \u5c0a\u4ee4\u5802\u662f\u8c01\uff0c\u4f60\u548c\u4ed6\u4eec\u5fc5\u5b9a\u6709\u56e2\u5706\u91cd\u805a\u7684\u2014\u5929\u3002\u3000\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9ed8\u7136\u4f4e\u4e0b\u5934\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\u3002\u201d \u3000\u3000\u996d\u540e\uff0c\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u77e5\u9053\u5e90\u4e91\u5fc5\u5b9a\u5f88\u7d2f\uff0c\u9700\u8981\u63d0\u524d\u4f11\u606f\uff0c\u4fbf\u81ea\u52a8\u8f9e\u51fa\u3002 \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c \u4e09 \u56de\u3000\u89c2\u5149\u96a7\u9053 \u3000\u3000\u6b21\u65e5\u4e00\u65e9\uff0c\u5e90\u4e91\u4fbf\u628a\u8881\u5c0f\u9e64\u548c\u5c0f\u98de\u53eb\u5230\u623f\u95f4\u91cc\u53bb\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u5fc3\u91cc\u6709\u6570\uff0c\u4e8b\u60c5\u4e00\u5b9a\u4e0d\u5999\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6628\u665a\u5e76\u672a\u628a\u51e4\u5ae3\u7ea2\u548c\u9a6c\u662d\u96c4\u6765\u8fc7\u7684\u4e8b\u5411\u5e08\u7236\u7980\u62a5\u3002 \u3000\u3000\u8fd9\u5012\u5e76\u975e\u4ed6\u6709\u610f\u9690\u533f\u4e0d\u62a5\uff0c\u800c\u662f\u4ed6\u4e0d\u613f\u518d\u5f15\u8d77\u5e08\u7236\u7684\u4f24\u5fc3\u3002 \u3000\u3000\u679c\u7136\uff0c\u5f53\u4e24\u4eba\u8fdb\u5165\u6b63\u5c4b\u540e\uff0c\u5e90\u4e91\u7684\u795e\u8272\u73b0\u51fa\u4ece\u672a\u6709\u8fc7\u7684\u51dd\u91cd\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u4e00\u9897\u5fc3\u7acb\u523b\u50cf\u5341\u4e94\u53ea\u540a\u6876\u5728\u6253\u6c34\u2014\u2014\u4e03\u4e0a\u516b\u4e0b\uff0c\u4ed6\u6218\u6218\u5162\u5162\u6765\u5230\u5e90\u4e91\u9762\u524d\u9053\uff1a \u201c\u5e08\u7236\u6709\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u5e90\u4e91\u58f0\u97f3\u51b0\u51b7\u7684\u9053\uff1a\u201c\u6709\u4ef6\u4e8b\u4e3a\u4ec0\u4e48\u6628\u665a\u4e0d\u5411\u6211\u7980\u62a5\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u786c\u7740\u5934\u76ae\u9053\uff1a\u201c\u5e08\u7236\u8bf4\u7684\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5e90\u4e91\u559d\u9053\uff1a\u201c\u600e\u4e48\uff1f\u8fd8\u60f3\u4e0d\u8bf4\u5b9e\u8bdd\uff0c\u4f60\u4eec\u56de\u6765\u540e\uff0c\u6709\u4ec0\u4e48\u4eba\u6765\u8fc7\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u5fc3\u5934\u4e00\u9707\uff0c\u4f4e\u4e0b\u5934\u9053\uff1a\u201c\u83ab\u975e\u5e08\u7236\u5df2\u7ecf\u770b\u51fa\u4ec0\u4e48\u5730\u65b9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u770b\u51fa\u4e0d\u5bf9\uff0c\u5feb\u8bf4\uff0c\u6709\u4ec0\u4e48\u4eba\u6765\u8fc7\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u53ea\u5f97\u628a\u98ce\u5ae3\u7ea2\u548c\u9a6c\u662d\u96c4\u524d\u6765\u7684\u7ecf\u8fc7\uff0c\u8be6\u7ec6\u7980\u544a\u4e00\u904d\u3002 \u3000\u3000\u5e90\u4e91\u8138\u8272\u53d1\u9752\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4ed6\u4eec\u662f\u505a\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u5185\u5fc3\u4e00\u9635\u5fd0\u5fd1\uff0c\u6447\u5934\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u77e5\u3002\u201d \u3000\u3000\u5e90\u4e91\u51b7\u7b11\u9053\uff1a\u201c\u73b0\u5728\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u4ed6\u4eec\u662f\u6765\u76d7\u53d6\u4e3a\u5e08\u7684\u4e00\u6837\u4e1c\u897f\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u614c\u5fd9\u8dea\u5012\u5728\u5730\u9053\uff1a\u201c\u5f1f\u5b50\u8be5\u6b7b\uff0c\u5e08\u7236\u4e0d\u5728\u5bb6\uff0c\u5f1f\u5b50\u7adf\u7136\u758f\u4e8e\u804c\u5b88\u3002\u201d \u3000\u3000\u5e90\u4e91\u53f9\u53e3\u6c14\uff0c\u8bed\u8c03\u5374\u6e10\u8d8b\u7f13\u548c\uff1a\u201c\u8d77\u6765\uff0c\u5176\u5b9e\u8fd9\u4e0d\u662f\u4f60\u7684\u9519\uff0c\u4f60\u7684\u6b66\u529f\u6bd4\u4ed6\u4eec\u76f8\u5dee\u5f88\u591a\uff0c \u6839\u672c\u65e0\u6cd5\u963b\u6b62\u4ed6\u4eec\u7684\u884c\u52a8\uff0c\u4f60\u80fd\u4fdd\u5168\u6027\u547d\uff0e\u5df2\u7ecf\u662f\u5f88\u96be\u5f97\u4e86\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4f9d\u8a00\u7ad9\u4e86\u8d77\u6765\u9053\uff1a\u201c\u5e08\u7236\u7a76\u7adf\u4e22\u4e86\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u201c\u70bc\u5fc3\u5927\u6cd5\u79d8\u7b08\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u5982\u95fb\u6674\u5927\u9739\u96f3\uff0c\u4ed6\u867d\u672a\u89c1\u8fc7\u201c\u70bc\u5fc3\u5927\u6cd5\u79d8\u7b08\u201d\uff0c\u5374\u77e5\u9053\u8fd9\u662f\u6b66\u529f\u4e2d\u6700\u9ad8\u5883\u754c\u7684\u5947 \u4e66\uff0c\u540c\u65f6\u4e5f\u7ec8\u4e8e\u660e\u767d\u4e86\u6628\u665a\u98ce\u5ae3\u7ea2\u548c\u9a6c\u662d\u96c4\u5728\u623f\u4e2d\u5446\u4e86\u90a3\u4e48\u4e45\u7684\u539f\u56e0\u3002 \u3000\u3000\u53ea\u542c\u5e90\u4e91\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u2018\u70bc\u5fc3\u5927\u6cd5\u79d8\u7b08\u2019\u662f\u5341\u51e0\u5e74\u524d\u6d1e\u4ed9\u8d60\u7ed9\u4e3a\u5e08\u7684\uff0c\u6211\u81f3\u4eca\u5c1a\u672a\u5b8c\u5168 \u7ec3\u6210\uff0c\u60f3\u4e0d\u5230\u5374\u88ab\u8fd9\u4e00\u5bf9\u65e0\u803b\u7537\u5973\u76d7\u8d70\u4e86\uff0c\u8fd9\u6559\u6211\u5c06\u6765\u5982\u4f55\u5411\u6d1e\u4ed9\u8001\u524d\u8f88\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u4ed6\u957f\u53f9\u4e00\u58f0\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\u79d8\u7b08\u4e2d\u7684\u6587\u5b57\u548c\u56fe\u89e3\u6211\u5168\u5df2\u8bb0\u5728\u5fc3\u4e2d\uff0c\u5373\u4fbf\u628a\u5b83\u6bc1\u6389\u4e5f \u7b97\u4e0d\u5f97\u4ec0\u4e48\u635f\u5931\uff0c\u4f46\u5982\u4eca\u843d\u5728\u8fd9\u5bf9\u65e0\u803b\u7537\u5973\u624b\u4e2d\uff0c\u5374\u5b9e\u5728\u662f\u6211\u7684\u5929\u5927\u7f6a\u8fc7\uff0c\u540c\u4e3a\u4ed6\u4eec\u2014\u65e6\u628a \u70bc\u5fc3\u5927\u6cd5\u4e60\u6210\uff0c\u6b66\u6797\u4e2d\u5c31\u96be\u514d\u8981\u5174\u8d77\u4e00\u573a\u8165\u98ce\u8840\u96e8\u7684\u6d69\u52ab\u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u987f\u4e86\u987f\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5c31\u8ba9\u5f1f\u5b50\u8bbe\u6cd5\u518d\u628a\u90a3\u518c\u79d8\u7b08\u593a\u56de\u5982\u4f55\uff1f\u201d \u3000\u3000\u5e90\u4e91\u60e8\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5c31\u51ed\u4f60\u73b0\u5728\u7684\u6b66\u529f\uff0c\u5982\u4f55\u80fd\u8ddf\u4ed6\u4eec\u76f8\u6297\uff0c\u90a3\u5c82\u4e0d\u7b49\u4e8e\u98de\u86fe\u6251\u706b\uff0c\u81ea \u5bfb\u6b7b\u8def\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u633a\u4e86\u633a\u80f8\u9053\uff1a\u201c\u665a\u8f88\u53ef\u4ee5\u76f8\u52a9\u8881\u5927\u54e5\u4e00\u81c2\u4e4b\u529b\uff01\u201d \u3000\u3000\u5e90\u4e91\u6447\u6447\u5934\u9053\uff1a\u201c\u4f60\u7684\u8eab\u624b\uff0c\u867d\u7136\u4e0d\u5728\u5c0f\u9e64\u4e4b\u4e0b\uff0c\u603b\u662f\u5e74\u7eaa\u592a\u5c0f\uff0c\u60f3\u8ddf\u4ed6\u4eec\u5bf9\u6297\uff0c\u8c08\u4f55 \u5bb9\u6613\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u55eb\u5685\u7740\u9053\uff1a\u201c\u53ef\u662f\u6211\u4eec\u603b\u4e0d\u8be5\u660e\u77e5\u90a3\u79d8\u7b08\u5df2\u88ab\u4ed6\u4eec\u76d7\u53bb\u800c\u4e0d\u53bb\u91c7\u53d6\u884c\u52a8\uff01\u201d \u3000\u3000\u5e90\u4e91\u6c89\u5fd6\u534a\u54cd\u9053\uff1a\u201c\u4ece\u73b0\u5728\u8d77\uff0c\u4f60\u4eec\u90fd\u5fc5\u987b\u5fc3\u65e0\u65c1\u52a1\uff0c\u4e13\u5fc3\u4e00\u5fd7\u4e60\u7ec3\u6b66\u529f\uff0c\u4e09\u5e74\u4ee5\u540e\uff0c \u6211\u5c31\u51b3\u5b9a\u8ba9\u4f60\u4eec\u4e0b\u5c71\uff0c\u8bbe\u6cd5\u4ece\u90a3\u5bf9\u65e0\u803b\u7537\u5973\u624b\u4e2d\u628a\u79d8\u7b08\u593a\u56de\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u832b\u7136\u9053\uff1a\u201c\u5982\u679c\u518d\u7b49\u4e09\u5e74\uff0c\u4ed6\u4eec\u5c82\u4e0d\u65e9\u5df2\u628a\u70bc\u5fc3\u5927\u6cd5\u4e60\u6210\uff1f\u201d \u3000\u3000\u5e90\u4e91\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\uff0c\u8981\u60f3\u4e60\u6210\u70bc\u5fc3\u5927\u6cd5\uff0c\u5373\u4fbf\u6b66\u529f\u5df2\u8fbe\u767b\u5cf0\u9020\u6781\u7684\u4eba\u7269\uff0e\u81f3\u5c11\u4e5f\u987b\u4e09\u5e74 \u65f6\u95f4\uff0c\u5373\u4ee5\u4e3a\u5e08\u6765\u8bf4\uff0c\u672c\u53ef\u5728\u4e09\u56db\u5e74\u5185\uff0c \u3000\u3000\u628a\u70bc\u5fc3\u5927\u6cd5\u4e60\u6210\uff0c\u4f46\u56e0\u8fd1\u5341\u5e74\u6765\u4e00\u76f4\u5fc3\u7eea\u96f6\u4e71\uff0c\u6240\u4ee5\u5230\u73b0\u5728\u4ecd\u672a\u5230\u5927\u6210\u9636\u6bb5\u3002\u201d \u3000\u3000\u201c\u5e08\u7236\u4e0d\u662f\u8bf4\u8fc7\u4ed6\u4eec\u4e24\u4eba\u7684\u6b66\u529f\u5df2\u7ecf\u63a5\u8fd1\u767b\u5cf0\u9020\u6781\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u53ef\u662f\u4ed6\u4eec\u4e24\u4eba\u90fd\u5fc3\u672f\u4e0d\u6b63\uff0c\u800c\u4e14\u5fc3\u6709\u65c1\u52a1\uff0c\u4e09\u5e74\u5185\u6839\u672c\u4e0d\u53ef\u80fd\u6709\u4ec0\u4e48\u6210\u5c31\uff0c\u8bf4 \u4e0d\u5b9a\u4f1a\u56e0\u800c\u8d70\u706b\u5165\u9b54\uff0c\u4ed6\u4eec\u867d\u7136\u5f97\u5230\u90a3\u518c\u79d8\u7b08\uff0c\u53cd\u800c\u662f\u8eab\u53d7\u5176\u5bb3\u4e86\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8fd9\u624d\u7a0d\u7a0d\u653e\u4e0b\u5fc3\u6765\u3002 \u3000\u3000\u5e90\u4e91\u53c8\u9053\uff1a\u201c\u6ca1\u4e8b\u4e86\uff0c\u4f60\u4eec\u56de\u53bb\u5427\uff0c\u4ece\u4eca\u5929\u8d77\uff0c\u4e3a\u5e08\u51b3\u5b9a\u6253\u8d77\u7cbe\u795e\u6765\u628a\u751f\u5e73\u6240\u5b66\uff0c\u5b8c\u5168 \u4f20\u7ed9\u4f60\u4eec\u3002\u201d \u3000\u3000\u8f6c\u77ac\u4e09\u5e74\u8fc7\u53bb\u3002 \u3000\u3000\u5728\u8fd9\u4e09\u5e74\u91cc\uff0c\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\uff0c\u5728\u5e90\u4e91\u7684\u6089\u5fc3\u6559\u5bfc\u4e0b\uff0c\u679c\u7136\u4e13\u5fc3\u81f4\u5fd7\uff0c\u591c\u4ee5\u7ee7\u65e5\uff0c\u52e4\u4e60 \u6b66\u529f\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5bf9\u4e8e\u548c\u7236\u6bcd\u8054\u7edc\uff0c\u4ecd\u5b58\u7740\u4e00\u7ebf\u5e0c\u671b\uff0c\u4ed6\u66fe\u591a\u6b21\u5077\u5077\u5230\u8fc7\u72ec\u79c0\u5cf0\u90a3\u68f5\u866c\u677e\u4e0b\uff0c\u5e0c\u671b \u80fd\u5947\u8ff9\u822c\u7684\u5f97\u5230\u8baf\u606f\uff0c\u53ef\u60dc\u6bcf\u6b21\u603b\u662f\u5931\u671b\u800c\u5f52\u3002 \u3000\u3000\u2014\u5929\u508d\u665a\uff0c\u5e90\u4e91\u53c8\u628a\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u53eb\u5230\u623f\u4e2d\u3002 \u3000\u3000\u539f\u6765\u4ed6\u5df2\u51b3\u5b9a\u660e\u5929\u5c31\u8981\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u4e0b\u5c71\uff0c\u4ee5\u4fbf\u5411\u98ce\u5ae3\u7ea2\u548c\u9a6c\u662d\u96c4\u8ba8\u56de\u201c\u70bc\u5fc3\u5927\u6cd5\u79d8 \u7b08\u201d\u3002\uff08OCR\uff1a\u522b\u4eba\u4e09\u5e74\u5185\u90fd\u8bb0\u719f\u4e86\uff0c\u8ba8\u56de\u4e0d\u5982\u6740\u4e86\u3002\uff09 \u3000\u3000\u53e6\u5916\uff0c\u5cb3\u5c0f\u98de\u4e5f\u53ef\u8d81\u4e0b\u5c71\u5728\u5916\u884c\u8d70\u4e4b\u4fbf\uff0c\u67e5\u5bfb\u7236\u6bcd\u7684\u4e0b\u843d\u3002 \u3000\u3000\u8fd9\u5728\u5cb3\u5c0f\u98de\u5f53\u7136\u662f\u559c\u51fa\u671b\u5916\u3002 \u3000\u3000\u4f46\u8881\u5c0f\u9e64\u53cd\u800c\u89c9\u5f97\u4e00\u65f6\u4e4b\u95f4\u6f2b\u65e0\u5934\u7eea\uff0c\u832b\u832b\u5929\u6daf\uff0c\u5230\u54ea\u91cc\u53bb\u627e\u51e4\u5ae3\u7ea2\u548c\u9a6c\u662d\u96c4\u5462\uff1f\u56e0\u4e3a \u5728\u8fd9\u4e09\u5e74\u91cc\uff0c\u8fd9\u5bf9\u65e0\u803b\u7537\u5973\uff0c\u5e76\u672a\u518d\u8fc7\u6765\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u8881\u5c0f\u9e64\u4e0d\u5f97\u4e0d\u6709\u6240\u8bf7\u793a\uff1a\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u8be5\u5230\u4ec0\u4e48\u5730\u65b9\u624d\u80fd\u627e\u5230\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u5e90\u4e91\u4f3c\u5df2\u80f8\u6709\u6210\u7af9\uff0e\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u5bf9\u65e0\u803b\u7537\u5973\u7684\u884c\u5f84\uff0e\u65e9\u5df2\u88ab\u6b66\u6797\u540c\u9053\u6240\u4e0d\u9f7f\uff0c\u6240\u4ee5 \u4ed6\u4eec\u53ef\u80fd\u4e0d\u65b9\u4fbf\u5728\u4e2d\u539f\u6d3b\u52a8\uff0c\u4e5f\u8bb8\u5230\u79e6\u5cad\u7ec8\u5357\u4e00\u5e26\u53ef\u4ee5\u627e\u5230\u4ed6\u4eec\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1b\u201c\u5e08\u7236\u53ef\u662f\u5df2\u7ecf\u5f97\u5230\u4ed6\u4eec\u7684\u6d88\u606f\uff1f\u201d \u3000\u3000\u5e90\u4e91\u9053\uff1a\u201c\u6211\u867d\u7136\u6ca1\u5f97\u5230\u4ed6\u4eec\u7684\u6d88\u606f\uff0c\u4f46\u5374\u542c\u8bf4\u8fd1\u5e74\u6765\u5728\u79e6\u5cad\u7ec8\u5357\u4e00\u5e26\uff0c\u6709\u4e00\u4e2a\u65b0\u5174\u7684 \u5e9e\u5927\u7ec4\u7ec7\uff0c\u4e0d\u5c11\u6b66\u6797\u9ad8\u624b\uff0c\u90fd\u671b\u98ce\u76f8\u6295\uff0c\u56e0\u4e3a\u8fd9\u4e00\u5e9e\u5927\u7ec4\u7ec7\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u4e0d\u4f46\u5947\u7279\uff0c\u800c\u4e14\u4e5f \u4e0d\u5149\u660e\u6b63\u5927\uff0c\u5982\u679c\u6211\u6240\u6599\u4e0d\u5dee\uff0c\u8fd9\u5bf9\u65e0\u803b\u7537\u5973\uff0c\u5f88\u53ef\u80fd\u5df2\u52a0\u5165\u4e86\u8fd9\u4e00\u7ec4\u7ec7\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9ed8\u4e86\u4e00\u9ed8\uff0c\u518d\u95ee\uff1a\u201c\u5e08\u7236\u65e2\u7136\u547d\u4ee4\u5f1f\u5b50\u4e0b\u5c71\uff0c\u5fc5\u5b9a\u8ba4\u4e3a\u5f1f\u5b50\u73b0\u5728\u7684\u6b66\u529f\u5df2\u8db3\u53ef\u4e0e \u4ed6\u4eec\u6297\u8861\uff0c\u83ab\u975e\u5f1f\u5b50\u7684\u8eab\u624b\u5df2\u771f\u53ef\u8d76\u4e0a\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u5e90\u4e91\u6447\u5934\u9053\uff1a\u201c\u82e5\u8bba\u6b66\u529f\uff0c\u4f60\u6050\u6015\u6c38\u8fdc\u65e0\u6cd5\u4e0e\u9a6c\u662d\u96c4\u76f8\u6bd4\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u90a3\u4e48\u5f1f\u5b50\u53c8\u600e\u80fd\u8ba8\u56de\u90a3\u518c\u79d8\u7b08\u5462\uff1f\u201d \u3000\u3000\u5e90\u4e91\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u5df2\u4e60\u5f97\u4e86\u5251\u6cd5\u4e0a\u7684\u2018\u98ce\u96f7\u4e09\u5f0f\u2019\u548c\u62f3\u638c\u4e2d\u7684\u2018\u640f\u9f99\u4e09\u62ff\u2019\uff0c\u8fd9\u4e24\u79cd\u6b66 \u529f\uff0c\u5f53\u521d\u6211\u5e76\u672a\u4f20\u7ed9\u9a6c\u662d\u96c4\uff0c\u4f60\u82e5\u6218\u4ed6\u4e0d\u8fc7\uff0c\u5fc5\u987b\u7acb\u523b\u65bd\u51fa\u2018\u98ce\u96f7\u4e09\u5f0f\u2019\u548c\u2018\u640f\u9f99\u4e09\u62ff\u2019\uff0c \u4e5f\u8bb8\u80fd\u628a\u4ed6\u5236\u4f4f\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u832b\u7136\u518d\u95ee\uff1a\u201c\u5f1f\u5b50\u5728\u5e08\u7236\u95e8\u4e0b\u4e60\u827a\uff0c\u8bba\u65f6\u95f4\u5df2\u7ecf\u8d85\u8fc7\u9a6c\u662d\u96c4\uff1f\u4e3a\u4ec0\u4e48\u6b66\u529f\u4ecd\u7136\u8d76 \u4e0d\u4e0a\u4ed6\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u7684\u5929\u8d4b\u8d44\u8d28\u65e0\u6cd5\u548c\u4ed6\u76f8\u6bd4\uff0c\u800c\u4e14\u8fd9\u4e9b\u5e74\u6765\uff0c\u4ed6\u4e00\u5b9a\u4e5f\u66f4\u6709\u8fdb\u5883\u3002\u201d \u3000\u3000\u5e90\u4e91\u53f9\u53e3\u6c14\uff0e\u63a5\u7740\u53c8\u9053\uff1a\u201c\u82e5\u9a6c\u662d\u96c4\u80fd\u4e0d\u5931\u6b63\u9053\uff0c\u51ed\u4ed6\u7684\u5929\u8d4b\u8d44\u8d28\uff0c\u5c06\u6765\u4e00\u5b9a\u5fc5\u6709\u5927\u6210\uff0c \u53ef\u60dc\u4ed6\u5374\u81ea\u5df1\u6bc1\u706d\u4e86\u5927\u597d\u524d\u9014\uff0c\u8bf4\u8d77\u6765\u5b9e\u5728\u662f\u4ef6\u4ee4\u4eba\u627c\u8155\u7684\u4e8b\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u642d\u8baa\u7740\u9053\uff1a\u201c\u90a3\u4e48\u5c0f\u98de\u7684\u5929\u8d4b\u8d44\u8d28\uff0c\u5e94\u8be5\u53ef\u4ee5\u6bd4\u5f97\u4e0a\u4ed6\u4e86\uff1f\u201d \u3000\u3000\u5e90\u4e91\u9894\u9996\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u540e\u8d77\u4e00\u8f88\u4e2d\uff0c\u4e5f\u53ea\u6709\u5c0f\u98de\u53ef\u4ee5\u4e0e\u4ed6\u76f8\u6bd4\uff0c\u751a\u81f3\u72b9\u6709\u8fc7\u4e4b\uff0c\u4f46\u5c0f \u98de\u603b\u662f\u5e74\u7eaa\u592a\u8f7b\uff0c\u529f\u529b\u65e0\u6cd5\u8d76\u4e0a\u4ed6\u7684\u6df1\u539a\uff0c\u867d\u7136\u5982\u6b64\uff0c\u76ee\u524d\u7684\u5c0f\u98de\uff0c\u4e5f\u8db3\u53ef\u4e0e\u4ed6\u4e00\u62da\u3002\u201d \u3000\u3000\u201c\u5f1f\u5b50\u548c\u5c0f\u98de\u4e0b\u5c71\u4ee5\u540e\uff0c\u5e08\u7236\u8fd8\u5b88\u5728\u8fd9\u91cc\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e3a\u5e08\u5f53\u7136\u8fd8\u8981\u5b88\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u5e08\u7236\u4e3a\u4ec0\u4e48\u4e0d\u4eb2\u81ea\u5e26\u5f1f\u5b50\u548c\u5c0f\u98de\u4e00\u540c\u4e0b\u5c71\uff1f\u201d \u3000\u3000\u5e90\u4e91\u9eef\u7136\u4e00\u53f9\u9053\uff1a\u201c\u81ea\u5df1\u7684\u59bb\u5b50\u548c\u5f1f\u5b50\u505a\u51fa\u8fd9\u6837\u65e0\u803b\u4e71\u4f26\u7684\u4e8b\uff0c\u6211\u8fd8\u6709\u4f55\u9762\u76ee\u9762\u5bf9\u6614\u65e5 \u6b66\u6797\u9053\u4e0a\u7684\u6545\u4eba\uff0c\u800c\u4e14\u2026\u2026\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e5f\u4e0d\u6562\u591a\u95ee\u3002 \u3000\u3000\u5e90\u4e91\u4ea4\u4ee3\u5b8c\u6bd5\uff0c\u9053\uff1a\u201c\u73b0\u5728\u8be5\u5230\u53a8\u623f\u505a\u996d\u4e86\uff0c\u4e5f\u7b97\u4e3a\u5e08\u66ff\u4f60\u4eec\u4e24\u4eba\u996f\u884c\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u76f8\u5cb3\u5c0f\u98de\u6765\u5230\u53a8\u623f\uff0c\u53ea\u89c1\u53a8\u623f\u91cc\u6446\u4e86\u4e0d\u5c11\u9e21\u9e2d\u9c7c\u8089\uff0c\u53e6\u884c\u4e00\u7f50\u9152\u3002 \u3000\u3000\u665a\u9910\uff0c\u867d\u7136\u53ea\u6709\u4e09\u4e2a\u4eba\uff0c\u5374\u6446\u4e86\u6ee1\u6ee1\u7684\u4e00\u684c\u83dc\uff0c\u7684\u786e\u4e8b\u76db\u5f97\u5f88\u3002 \u3000\u3000\u679c\u7136\u90fd\u5403\u5f97\u9152\u9189\u996d\u9971\uff0c\u968f\u5373\u5404\u81ea\u56de\u623f\u5b89\u5bdd\u3002 \u3000\u3000\u8fd9\u4e00\u591c\uff0c\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u90fd\u4e0d\u66fe\u7761\u597d\uff0c\u8f97\u8f6c\u53cd\u4fa7\uff0c\u96be\u4ee5\u6210\u7720\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u60f3\u8d77\u5373\u5c06\u548c\u6069\u5e08\u522b\u79bb\uff0c\u4e0d\u77e5\u4f55\u5e74\u4f55\u6708\uff0c\u624d\u5f97\u91cd\u805a\u3002\u5c24\u5176\u4ed6\u548c\u5cb3\u5c0f\u98de\u8d70\u4e86\u4ee5\u540e\uff0e\u8305 \u5c4b\u91cc\u53ea\u5269\u4e0b\u5e08\u7236\u4e00\u4eba\uff0c\u90a3\u662f\u4f55\u7b49\u7684\u5bc2 \u3000\u3000\u5bde\uff0e\u800c\u4e14\u5e08\u7236\u4eca\u540e\u8fde\u996d\u90fd\u8981\u81ea\u5df1\u505a\uff0c\u53c8\u662f\u4f55\u7b49\u7684\u8f9b\u82e6\u3002 \u3000\u3000\u81f3\u4e8e\u5cb3\u5c0f\u98de\uff0c\u5bf9\u5e90\u4e91\u7167\u6837\u4e5f\u6709\u7740\u4f9d\u4f9d\u96be\u820d\u7684\u611f\u60c5\uff0c\u8fd9\u4e09\u5e74\u591a\u82e5\u4e0d\u662f\u4ed6\u8001\u4eba\u5bb6\u6536\u7559\uff0c\u8bf4\u4e0d \u5b9a\u81ea\u5df1\u5df2\u6ca6\u4e3a\u4e00\u540d\u65e0\u5bb6\u53ef\u5f52\u7684\u4e5e\u513f\u3002 \u3000\u3000\u6b21\u65e5\u4e00\u65e9\uff0c\u4e24\u4eba\u4fbf\u8f9e\u522b\u4e86\u5e90\u4e91\u4e0b\u5c71\u3002 \u3000\u3000\u5e90\u4e91\u5e76\u672a\u76f8\u9001\uff0c\u53ea\u662f\u5404\u8d60\u4e86\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u4e00\u67c4\u957f\u5251\uff0c\u4e5f\u7ed9\u4e86\u4ed6\u4eec\u4e00\u4e9b\u76d8\u7f20\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5ea7\u72ec\u79c0\u5cf0\u662f\u5728\u4e94\u53f0\u5c71\u3002 \u3000\u3000\u7531\u4e94\u53f0\u5c71\u5230\u79e6\u5cad\u7ec8\u5357\u5c71\uff0c\u662f\u5f80\u897f\u5357\u800c\u884c\u3002 \u3000\u3000\u5927\u7ea6\u534a\u6708\u540e\uff0c\u4e24\u4eba\u5df2\u5230\u8fbe\u9655\u897f\u534e\u9634\u3002 \u3000\u3000\u534e\u9634\u4e4b\u5357\uff0e\u4fbf\u662f\u534e\u5c71\u3002 \u3000\u3000\u4e24\u4eba\u51b3\u5b9a\u7531\u534e\u9634\u7ecf\u534e\u53bf\u3001\u6e2d\u5357\uff0c\u4e34\u6f7c\u81f3\u957f\u5b89\uff0c\u518d\u7531\u957f\u5b89\u8fdb\u5165\u7ec8\u5357\u5c71\u3002 \u3000\u3000\u8fd9\u6837\u8d70\u6cd5\uff0c\u867d\u7136\u8def\u7a0b\u8fdc\u4e00\u70b9\uff0c\u4f46\u56e0\u9760\u8fd1\u9ec4\u6cb3\u548c\u6e2d\u6c34\u6cbf\u5cb8\uff0c\u5730\u52bf\u5e73\u5766\uff0c\u6bd4\u8d70\u5c71\u8def\u8981\u65b9\u4fbf\u5f88 \u591a\u3002 \u3000\u3000\u5230\u8fbe\u534e\u9634\u65f6\u5929\u8272\u5df2\u665a\uff0c\u4ed6\u4eec\u4fbf\u5728\u57ce\u90ca\u627e\u4e86\u4e00\u5904\u5ba2\u6808\u4f4f\u4e0b\u3002 \u3000\u3000\u534a\u4e2a\u591a\u6708\u4ee5\u6765\uff0c\u4ed6\u4eec\u4e00\u8def\u5954\u6ce2\uff0c\u867d\u7136\u4e5f\u66fe\u7559\u610f\u6253\u542c\uff0c\u8fd8\u662f\u534a\u70b9\u51e4\u5ae3\u7ea2\u548c\u9a6c\u662d\u96c4\u7684\u6d88\u606f\u4e5f \u6ca1\u6709\u3002 \u3000\u3000\u81f3\u4e8e\u5cb3\u5c0f\u98de\u7684\u7236\u6bcd\uff0c\u56e0\u4e3a\u65e0\u540d\u65e0\u59d3\uff0c\u90a3\u5c31\u66f4\u4e0d\u5bb9\u6613\u6253\u542c\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u987e\u4e0d\u5f97\u5728\u8857\u4e0a\u6e38\u901b\uff0c\u5728\u5ba2\u6808\u7528\u8fc7\u665a\u9910\uff0c\u4fbf\u51c6\u5907\u8fdb\u5165\u4e0a\u623f\u4f11\u606f\u3002 \u3000\u3000\u5c31\u5728\u5cb3\u5c0f\u98de\u9910\u540e\u51c6\u5907\u56de\u623f\u4f11\u606f\u65f6\uff0c\u7a81\u7136\u4ece\u53e6\u4e00\u4e2a\u4e0a\u623f\u8d70\u51fa\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u9ad8\u5934\u5927\u9a6c\uff0c\u6ee1\u9762\u866c\u987b\uff0c\u8170\u60ac\u4e00\u53e3\u539a\u80cc\u9b3c\u5934\u5200\uff0c\u795e\u6001\u9887\u4e3a\u7c97\u72c2\u800c\u53c8\u96c4\u731b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u867d\u4e0d\u8ba4\u8bc6\u6b64\u4eba\uff0c\u4f46\u5728\u8fd9\u5239\u90a3\uff0c\u5374\u7acb\u5373\u773c\u775b\u4e3a\u4e4b\u4e00\u4eae\u3002 \u3000\u3000\u539f\u56e0\u662f\u8fd9\u4eba\u80f8\u524d\u62c4\u4e86\u4e00\u5757\u7389\u4f69\uff0c\u800c\u4f7f\u5cb3\u5c0f\u98de\u4e3a\u4e4b\u7559\u610f\u7684\uff0c\u4e5f\u6b63\u662f\u8fd9\u5757\u7389\u4f69\u3002 \u3000\u3000\u4e00\u4e2a\u7c97\u58ee\u7537\u5b50\u80f8\u524d\u6302\u7740\u7389\u4f69\uff0c\u672c\u6765\u5c31\u4e0d\u5927\u76f8\u79f0\uff0c\u4f55\u51b5\u8fd9\u7389\u4f69\u6b63\u662f\u5cb3\u5c0f\u98de\u6240\u6700\u719f\u6089\u7684\u4e1c\u897f\u3002 \u3000\u3000\u539f\u6765\u4e09\u5e74\u524d\u5f53\u5cb3\u5c0f\u98de\u6b63\u5728\u72ec\u79c0\u5cf0\u90a3\u68f5\u866c\u677e\u4e0b\u548c\u7236\u6bcd\u5206\u624b\u65f6\uff0c\u4ed6\u7684\u7236\u4eb2\u66fe\u4ea4\u7ed9\u4ed6\u4e00\u4e2a\u5c0f\u9ec4 \u7ee2\u5305\u88f9\uff0c\u544a\u8bc9\u4ed6\u91cc\u9762\u6709\u5c06\u6765\u5f7c\u6b64\u8054\u7edc\u7684\u6697\u8bb0\u548c\u4fe1\u7269\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u540e\u6765\u7ecf\u8fc7\u5bdf\u770b\uff0c\u5305\u88f9\u5185\u53ea\u4e0d\u8fc7\u662f\u4e00\u5757\u7389\u4f69\uff0c\u800c\u90a3\u7389\u4f69\u548c\u73b0\u5728\u866c\u987b\u5927\u6c49\u80f8\u524d\u6240\u4f69\u7684\uff0c \u4e0d\u8bba\u5927\u5c0f\u5f62\u72b6\uff0c\u7adf\u662f\u5b8c\u5168\u4e00\u6837\uff0c\u53c8 \u3000\u3000\u600e\u80fd\u4e0d\u4f7f\u4ed6\u5927\u5927\u7559\u610f\u3002 \u3000\u3000\u4ed6\u987e\u4e0d\u5f97\u662f\u5426\u5192\u6627\uff0c\u7acb\u523b\u8d8b\u524d\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u5144\u53f0\uff0c\u8bf7\u6055\u5c0f\u5f1f\u65e0\u793c\uff0c\u53ef\u4e0d\u53ef\u4ee5\u501f\u4e00\u6b65\u8bf4 \u8bdd\uff1f\u201d \u3000\u3000\u866c\u987b\u5927\u6c49\u89c1\u5bf9\u65b9\u53ea\u662f\u4e2a\u534a\u5927\u4e0d\u5c0f\u7684\u5b69\u5b50\uff0c\u5148\u662f\u2014\u6123\uff0c\u4f46\u63a5\u7740\u53c8\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u53ef \u662f\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5c0f\u5f1f\u60f3\u8bf7\u5144\u53f0\u5230\u5c0f\u5f1f\u623f\u4e2d\u5750\u5750\uff01\u201d \u3000\u3000\u866c\u987b\u5927\u6c49\u968f\u5373\u968f\u5cb3\u5c0f\u98de\u6765\u5230\u623f\u4e2d\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4eb2\u5207\u7684\u62db\u5f85\u4ed6\u5750\u4e0b\uff0c\u53c8\u4e3a\u4ed6\u5012\u4e86\u4e00\u676f\u8336\u5949\u4e0a\u3002 \u3000\u3000\u866c\u987b\u4eba\u6d41\u4e24\u773c\u76f4\u7728\u7684\u9053\uff1a\u201c\u5728\u4e0b\u548c\u5c0f\u5144\u5f1f\u597d\u8c61\u5e76\u4e0d\u8ba4\u8bc6\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5374\u671b\u5411\u866c\u987b\u5927\u6c49\u80f8\u524d\u90a3\u5757\u7389\u4f69\u9053\uff1a\u201c\u5c0f\u5f1f\u7684\u786e\u548c\u5144\u53f0\u7d20\u4e0d\u76f8\u8bc6\uff0c\u53ea\u662f\u60f3\u95ee\u95ee\u5144\u53f0 \u8fd9\u5757\u7389\u4f69\uff1f\u2026\u2026\u201d \u3000\u3000\u866c\u987b\u5927\u6c49\u805a\u73b0\u60ca\u559c\u4e4b\u8272\uff0c\u554a\u4e86\u4e00\u58f0\u9053\uff1b\u3000\u201c\u83ab\u975e\u5c0f\u5144\u5f1f\u662f\uff1f\u2026\u2026\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5c0f\u5f1f\u4e5f\u6709\u540c\u6837\u7684\u4e00\u5757\u7389\u4f69\uff0c\u56e0\u4e3a\u592a\u5de7\u5408\u4e86\uff0c\u6240\u4ee5\u624d\u8981\u8bf7\u95ee\u5144\u53f0\u2026\u2026\u201d \u3000\u3000\u866c\u987b\u5927\u6c49\u50cf\u94dc\u94c3\u822c\u7684\u4e24\u773c\uff0c\u8d8a\u53d1\u7728\u52a8\u5f97\u5386\u5bb3\uff1a\u201c\u6211\u60f3\u95ee\u95ee\uff0c\u5c0f\u5144\u5f1f\u4eca\u5e74\u591a\u5927\u5e74\u7eaa\u4e86\uff1f\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u4eca\u5e74\u5341\u4e09\u5c81\u3002\u201d \u3000\u3000\u866c\u987b\u5927\u6c49\u731b\u5730\u7531\u5ea7\u4f4d\u7ad9\u4e86\u8d77\u6765\uff0c\u63a2\u81c2\u62c9\u4f4f\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u516c\u5b50\uff0c\u6211\u4e01\u6d9b\u5230\u5904\u627e\u4e86\u4e00\u5e74\u591a\uff0c \u7ec8\u4e8e\u627e\u5230\u4f60\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8feb\u4e0d\u53ca\u5f85\u7684\u6025\u6025\u95ee\u9053\uff1a\u201c\u4e01\u5927\u54e5\uff0c\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u8bf7\u5feb\u544a\u8bc9\u5c0f\u5f1f\uff01\u201d \u3000\u3000\u4ed6\u867d\u7136\u8fd9\u6837\u95ee\uff0c\u5176\u5b9e\u5fc3\u91cc\u5df2\u660e\u767d\uff0c\u8fd9\u4f4d\u53eb\u4e01\u6d9b\u7684\u5927\u6c49\uff0c\u5fc5\u662f\u548c\u81ea\u5df1\u8054\u7edc\u7684\u4eba\u3002 \u3000\u3000\u4e01\u6d9b\u7d27\u7d27\u63e1\u7740\u5cb3\u5c0f\u98de\u7684\u624b\u9053\uff1a\u201c\u6709\u4e00\u7537\u4e00\u5973\u4e24\u4f4d\u5927\u4fa0\uff0c\u5429\u5490\u6211\u5bfb\u8bbf\u516c\u5b50\uff0c\u6211\u5bfb\u8bbf\u4e86\u5e74\u591a\uff0c \u603b\u7b97\u7687\u5929\u4e0d\u8d1f\u82e6\u5fc3\u4eba\uff0e\u4eca\u5929\u9047\u4e0a\u4e86\u516c\u5b50\uff0c\u6ca1\u8f9c\u8d1f\u4e24\u4f4d\u5927\u4fa0\u7684\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u201c\u4e01\u5927\u54e5\u53ef\u77e5\u9053\u90a3\u4e24\u4f4d\u5927\u4fa0\u59d3\u4ec0\u4e48\uff1f\u53eb\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u6447\u6447\u5934\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u5f53\u65f6\u5f53\u7136\u95ee\u8fc7\uff0c\u53ef\u662f\u4ed6\u4eec\u5374\u4e0d\u80af\u900f\u9732\uff0c\u53ea\u4ea4\u7ed9\u5728\u4e0b\u4e00\u5757\u7389 \u4f69\uff0c\u8981\u5728\u4e0b\u5bfb\u8bbf\u4ed6\u4eec\u7684\u516c\u5b50\uff0c\u65e2\u7136\u516c\u5b50\u4e5f\u6709\u4e00\u5757\u540c\u6837\u7684\u7389\u4f69\uff0c\u90a3\u5c31\u4e0d\u4f1a\u9519\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u624d\u53c8\u89c9\u51fa\u4e0d\u5bf9\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u65e2\u7136\u89c1\u5230\u4e86\u516c\u5b50\uff0c \u3000\u3000\u516c\u5b50\u53c8\u4f55\u5fc5\u95ee\u4ee4\u5c0a\u4ee4\u5802\u59d3\u4ec0\u4e48\u53eb\u4ec0\u4e48\uff1f\u5bf9\u4e86\uff0c\u90a3\u4e24\u4f4d\u5927\u4fa0\u4e0d\u80af\u8bf4\u51fa\u59d3\u540d\uff0c\u516c\u5b50\u603b\u8be5\u544a\u8bc9 \u6211\u4f60\u7684\u5c0a\u59d3\u5927\u540d\u5427\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5c0f\u5f1f\u5cb3\u5c0f\u98de\u3002\u201d \u3000\u3000\u866c\u987b\u5927\u6c49\u9053\uff1a\u201c\u90a3\u5c31\u5bf9\u4e86\uff0c\u4ee4\u5c0a\u5c31\u662f\u5cb3\u5927\u4fa0\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u867d\u660e\u77e5\u7236\u4eb2\u5e76\u4e0d\u53ef\u80fd\u59d3\u5cb3\uff0c\u5374\u53c8\u4e0d\u4fbf\u89e3\u91ca\uff0c\u4ee5\u514d\u53c8\u95f9\u51fa\u7b11\u8bdd\u3002 \u3000\u3000\u4ed6\u63a5\u7740\u95ee\u9053\uff1a\u201c\u4e01\u5927\u54e5\u65e2\u7136\u8fde\u5bb6\u7236\u59d3\u4ec0\u4e48\u53eb\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\uff0c\u53c8\u600e\u4f1a\u5bf9\u5bb6\u7236\u548c\u5c0f\u5f1f\u8fd9\u6837\u5e2e \u5fd9\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u6b63\u8d77\u795e\u8272\u9053\uff1a\u201c\u4e0d\u662f\u5728\u4e0b\u5e2e\u5cb3\u5927\u4fa0\u7684\u5fd9\uff0c\u800c\u662f\u5cb3\u5927\u4fa0\u5e2e\u4e86\u5728\u4e0b\u4e00\u6b21\u5927\u5fd9\u3002\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6551\u4e86\u5728\u4e0b\u4e00\u547d\uff0c\u5982\u679c\u6ca1\u6709\u4ed6\uff0c\u5728\u4e0b\u54ea\u80fd\u6d3b\u5230\u73b0\u5728\uff0c\u6240\u4ee5\u5728\u4e0b\u5c31\u662f\u4e00\u8f88\u5b50\u4e0d\u505a\u522b\u7684\uff0c \u4e5f\u8981\u5b8c\u6210\u4ed6\u7684\u4e00\u756a\u9644\u6258\u3002\u201d \u3000\u3000\u201c\u5bb6\u7236\u548c\u5bb6\u6bcd\u90fd\u5bf9\u4e01\u5927\u54e5\u8bf4\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5cb3\u5927\u4fa0\u4ea4\u7ed9\u5728\u4e0b\u8fd9\u5757\u7389\u4f69\uff0c\u8981\u5728\u4e0b\u8bbe\u6cd5\u548c\u516c\u5b50\u8054\u7edc\u3002\u201d \u3000\u3000\u201c\u5bb6\u7236\u53ef\u4ea4\u4ee3\u8fc7\u4e01\u5927\u54e5\u5230\u4e94\u53f0\u5c71\u72ec\u79c0\u5cf0\u53bb\u627e\u5c0f\u5f1f\uff1f\u201d \u3000\u3000\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u5cb3\u5927\u4fa0\u662f\u8fd9\u6837\u4ea4\u4ee3\u8fc7\u3002\u201d \u3000\u3000\u201c\u4e01\u5927\u54e5\u5230\u8fc7\u90a3\u5730\u65b9\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u5230\u8fc7\uff0c\u90a3\u5cf0\u9876\u6709\u4e00\u68f5\u5927\u677e\u6811\uff0c\u6211\u5728\u677e\u6811\u4e0b\u76f4\u5750\u4e86\u4e00\u5929\uff0c\u8fde\u4e2a\u4eba\u5f71\u90fd\u6ca1\u770b\u5230\uff0c\u53ea\u597d \u8fde\u591c\u4e0b\u5c71\uff0c\u540e\u6765\u89c9\u5f97\u5929\u4e0b\u50cf\u516c\u5b50\u8fd9\u822c\u5e74\u7eaa\u7684\u4eba\u592a\u591a\uff0c\u603b\u4e0d\u80fd\u89c1\u5230\u4e00\u4e2a\u95ee\u4e00\u4e2a\uff0c\u6240\u4ee5\u5e72\u8106\u5c31\u628a \u7389\u4f69\u6302\u5728\u80f8\u524d\uff0c\u82e5\u516c\u5b50\u89c1\u4e86\uff0c\u4e00\u5b9a\u4f1a\u4e3b\u52a8\u627e\u6211\uff0c\u679c\u7136\u8fd9\u529e\u6cd5\u5f88\u597d\uff0c\u8981\u4e0d\u7136\uff0c\u771f\u4e0d\u77e5\u54ea\u4e00\u5929\u624d \u80fd\u627e\u5230\u516c\u5b50\u3002\u201d \u3000\u3000\u201c\u4e01\u5927\u54e5\u5f53\u5e74\u662f\u5728\u4ec0\u4e48\u5730\u65b9\u770b\u5230\u5bb6\u7236\u5bb6\u6bcd\u7684\uff1f\u201d \u3000\u3000\u201c\u5c31\u5728\u534e\u5c71\u548c\u7ec8\u5357\u5c71\u4e4b\u95f4\u7684\u84dd\u5173\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5f88\u5feb\u4fbf\u60f3\u8d77\u97e9\u6108\u7684\u4e00\u9996\u4e03\u8a00\u5f8b\u8bd7\uff1a\u201c\u4e00\u5c01\u671d\u594f\u4e5d\u91cd\u5929\uff0c\u5915\u8d2c\u6f6e\u5dde\u8def\u516b\u5343\u3002\u6b32\u4e3a\u5723 \u671d\u9664\u5f0a\u653f\uff0c\u80af\u5c06\u8870\u673d\u60dc\u6b8b\u5e74\u3002\u4e91\u6a2a\u79e6\u5cad\u5bb6\u4f55\u5728\uff0c\u96ea\u62e5\u84dd\u5173\u9a6c\u4e0d\u524d\u3002\u77e5\u6c5d\u8fdc\u6765\u5e94\u6709\u610f\uff0c\u597d\u6536\u543e \u9aa8\u57cb\u6c5f\u8fb9\u3002\u201d \u3000\u3000\u63a5\u7740\uff0c\u4ed6\u4f3c\u4e4e\u4e5f\u6709\u79cd\u4e0d\u7965\u7684\u9884\u611f\u88ad\u4e0a\u5fc3\u5934\u3002 \u3000\u3000\u5728\u4ece\u524d\uff0c\u4ed6\u5728\u611f\u89c9\u4e0a\uff0c\u4f3c\u4e4e\u89c9\u5f97\u84dd\u5173\u8fdc\u5728\u5929\u8fb9\uff0c\u5b9e\u9645\u4e0a\u73b0\u5728\u6765\u5230\u534e\u9634\uff0c\u5df2\u8ddd\u84dd\u5173\u4e0d\u8fdc\u4e86\u3002 \u3000\u3000\u4ed6\u5173\u5207\u800c\u53c8\u6fc0\u52a8\u7684\u95ee\u9053\uff1a\u201c\u5bb6\u7236\u548c\u5bb6\u6bcd\u53ef\u66fe\u4ea4\u4ee3\u4e01\u5927\u54e5\u5e26\u7ed9\u5c0f\u5f1f\u4ec0\u4e48\u8a00\u8bed\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4ed6\u4eec\u53ea\u4ea4\u4ee3\u5728\u4e0b\u544a\u8bc9\u516c\u5b50\uff0c\u4ed6\u4eec\u5f88\u597d\uff0c\u8bf7\u516c\u5b50\u4e0d\u5fc5\u6302\u5fc3\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u7981\u53c8\u662f\u4e00\u9635\u6005\u7136\uff0c\u7236\u6bcd\u6258\u4eba\u5bfb\u627e\u81ea\u5df1\uff0c\u6240\u5e26\u6765\u7684\u7adf\u662f\u8fd9\u4e48\u77ed\u77ed\u7684\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u201c\u90a3\u4e48\u4e01\u5927\u54e5\u53ef\u77e5\u9053\u5bb6\u7236\u6bcd\u53bb\u4e86\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u5f53\u65f6\u4e5f\u66fe\u95ee\u8fc7\uff0c\u53ef\u662f\u5cb3\u5927\u4fa0\u4e0d\u80af\u900f\u9732\uff0c\u770b\u4ed6\u7684\u795e\u8272\uff0c\u597d\u50cf\u662f\u6709\u4ec0\u4e48\u8feb\u4e0d\u5f97\u5df2\u7684\u82e6 \u8877\u3002\u201d \u3000\u3000\u4e01\u6d9b\u8bf4\u5230\u8fd9\u91cc\uff0e\u5ffd\u7136\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u516c\u5b50\u8bf7\u522b\u79bb\u5f00\uff0c\u5f85\u6211\u518d\u53bb\u53eb\u4e00\u4e2a\u4eba\u6765\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u832b\u7136\u9053\uff1a\u201c\u4e01\u5927\u54e5\u8981\u53bb\u53eb\u8c01\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4e00\u4e2a\u53eb\u7518\u9716\u7684\uff0c\u6211\u548c\u4ed6\u4e0d\u8bba\u8d70\u5230\u54ea\u91cc\uff0c\u90fd\u5728\u4e00\u8d77\uff0c\u5cb3\u5927\u4fa0\u6551\u8fc7\u6211\u7684\u547d\uff0c\u7167\u6837 \u4e5f\u6551\u4e86\u4ed6\u7684\u4e00\u547d\uff0c\u8fd9\u4e09\u5e74\u6765\uff0c\u4ed6\u548c\u6211\u90fd\u662f\u5728\u5230\u5904\u5bfb\u8bbf\u516c\u5b50\u7684\uff0c\u73b0\u5728\u65e2\u7136\u627e\u5230\u4e86\uff0c\u5f53\u7136\u8981\u544a\u8bc9 \u4ed6\uff0c\u4e5f\u8ba9\u4ed6\u9ad8\u5174\u4e00\u4e0b\uff01\u201d \u3000\u3000\u539f\u6765\u4e01\u6d9b\u548c\u7518\u9716\u4e5f\u662f\u4f4f\u5728\u8fd9\u5bb6\u5ba2\u6808\u7684\u3002 \u3000\u3000\u4e01\u6d9b\u8d70\u540e\u4e0d\u4e45\uff0c\u4fbf\u5e26\u7740\u53e6\u5916\u4e00\u4e2a\u5927\u6c49\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\u3002\u53e6\u5916\u8fd9\u5927\u6c49\u4fbf\u662f\u7518\u9716\u4e86\u3002 \u3000\u3000\u7518\u9716\u53c8\u662f\u53e6\u4e00\u79cd\u957f\u76f8\uff0c\u4e00\u5f20\u524a\u7626\u7684\u9a6c\u8138\uff0c\u8eab\u6750\u7ec6\u9ad8\uff0c\u76ae\u80a4\u767d\u5f97\u50cf\u6f02\u8fc7\uff0c\u6709\u5982\u80ba\u75e8\u75c5\u9b3c\u4e00 \u822c\uff0c\u518d\u600e\u4e48\u770b\u4e5f\u4e0d\u50cf\u4ed6\u7684\u540d\u5b57\uff0c\u548c\u4e01\u6d9b\u8d70\u5728\u4e00\u8d77\uff0c\u6070\u6210\u5f3a\u70c8\u7684\u5bf9\u6bd4\u3002 \u3000\u3000\u7518\u9716\u4e00\u8fdb\u95e8\u5c31\u62b1\u62f3\u53eb\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u4f60\u597d\uff0c\u603b\u7b97\u627e\u5230\u4f60\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8bf7\u4e8c\u4eba\u91cd\u65b0\u5165\u5ea7\uff0c\u5e76\u53e6\u4e3a\u7518\u9716\u5012\u4e86\u4e00\u676f\u8336\u3002 \u3000\u3000\u53ea\u89c1\u4e01\u6d9b\u671b\u7740\u7518\u9716\u9053\uff1a\u201c\u8001\u7518\uff0c\u539f\u6765\u5cb3\u516c\u5b50\u8fd8\u4e0d\u6e05\u695a\u5cb3\u5927\u4fa0\u592b\u5987\u5728\u54ea\u91cc\uff0c\u8fd9\u6559\u4ed6\u4eec\u5168\u5bb6 \u600e\u4e48\u56e2\u805a\uff1f\u201d \u3000\u3000\u7518\u9716\u7ffb\u7740\u4e00\u5bf9\u6b7b\u9c7c\u773c\u9053\uff1a\u201c\u4f9d\u6211\u770b\u5cb3\u5927\u4fa0\u592b\u5987\u5b9a\u662f\u8fdb\u4e86\u9b3c\u95e8\u5173\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u542c\u5f97\u5927\u5403\u4e00\u60ca\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u83ab\u975e\u5bb6\u7236\u5bb6\u6bcd\u5df2\u7ecf\u906d\u9047\u4e86\u4ec0\u4e48\u4eba\u7684\u6bd2\u624b\u6697\u5bb3\uff1f\u201d \u3000\u3000\u7518\u9716\u5c34\u5c2c\u4e00\u7b11\u9053\uff1a\u201c\u539f\u6765\u5cb3\u516c\u5b50\u4e0d\u6653\u5f97\u4ec0\u4e48\u53eb\u9b3c\u95e8\u5173\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53d8\u7740\u8138\u8272\u9053\uff1a\u201c\u5c0f\u5f1f\u600e\u4e48\u6ca1\u542c\u8bf4\u8fc7\u4ec0\u4e48\u53eb\u9b3c\u95e8\u5173\uff0c\u6d3b\u4eba\u53c8\u600e\u4f1a\u8fdb\u4e86\u9b3c\u95e8\u5173\uff1f\u201d \u3000\u3000\u7518\u9716\u53c8\u662f\u5c34\u5c2c\u4e00\u7b11\u9053\uff1a\u201c\u54b1\u8bf4\u7684\u9b3c\u95e8\u5173\uff0c\u53ea\u662f\u4e00\u4e2a\u5730\u540d\uff0c\u636e\u8bf4\u5728\u6700\u8fd1\u51e0\u5e74\uff0c\u4e0d\u5c11\u6b66\u6797\u7edd \u9876\u9ad8\u624b\uff0c\u90fd\u60f3\u8fdb\u9b3c\u95e8\u5173\uff0c\u8fde\u4e2d\u539f\u4e00\u5e26\u7684\u6b66\u6797\u4eba\u7269\uff0c\u4e5f\u90fd\u6155\u540d\u800c\u6765\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u867d\u7565\u7565\u653e\u4e0b\u5fc3\uff0c\u5374\u8fd8\u662f\u900f\u7740\u4e0d\u89e3\uff1a\u201c\u8fd9\u53c8\u662f\u4e3a\u4ec0\u4e48\u5462\uff1f\u83ab\u975e\u9b3c\u95e8\u5173\u8fd8\u6709\u4ec0\u4e48\u5438\u5f15 \u4eba\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u7518\u9716\u4f3c\u4e4e\u6765\u4e86\u5174\u8da3\uff0c\u8bf4\u5f97\u53e3\u6cab\u6a2a\u98de\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u7528\u8bf4\u4e86\uff0c\u5c31\u4ee5\u54b1\u548c\u8001\u4e01\u6765\u8bf4\u5427\uff0c\u7167\u6837\u4e5f \u5fc3\u91cc\u75d2\u75d2\u7684\uff0c\u5e0c\u671b\u80fd\u53bb\u4e00\u8d9f\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u7518\u5927\u4fa0\u8fd8\u6ca1\u8bf4\u51fa\u90a3\u7a76\u7adf\u6709\u4ec0\u4e48\u5730\u65b9\u5438\u5f15\u4eba\uff1f\u201d \u3000\u3000\u7518\u9716\u7838\u5634\u9053\uff1a\u201c\u636e\u8bf4\u91cc\u9762\u4ec0\u4e48\u5947\u5947\u602a\u602a\u7684\u666f\u7269\u90fd\u6709\uff0c\u79f0\u5f97\u4e0a\u662f\u5343\u5e74\u96be\u5f97\u4e00\u89c1\uff0c\u800c\u4e14\u5982\u679c \u8fd0\u6c14\u597d\uff0c\u8fd8\u53ef\u4ee5\u505a\u795e\u4ed9\u3002\u201d \u3000\u3000\u201c\u7518\u5927\u4fa0\u662f\u542c\u8c01\u8bf4\u7684\uff1f\u201d \u3000\u3000\u201c\u8fde\u5343\u767e\u91cc\u5916\u7684\u4eba\u90fd\u77e5\u9053\uff0c\u54b1\u4eec\u53c8\u600e\u80fd\u4e0d\u77e5\u9053\u5462\uff0c\u6211\u548c\u8001\u4e01\u5c31\u5728\u592a\u767d\u5c71\uff0c\u79bb\u7ec8\u5357\u5c71\u4e0d\u8fc7 \u4e24\u4e09\u767e\u91cc\u8def\u3002\u201d \u3000\u3000\u201c\u9b3c\u95e8\u5173\u5728\u54ea\u91cc\u5462\uff1f\u201d \u3000\u3000\u201c\u5c31\u5728\u7ec8\u5357\u5c71\u3002\u201d \u3000\u3000\u201c\u7518\u5927\u4fa0\u80fd\u4e0d\u80fd\u627e\u5230\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u53ef\u4ee5\u627e\u5230\uff0c\u5982\u679c\u516c\u5b50\u60f3\u53bb\uff0c\u6211\u548c\u8001\u4e01\u4e00\u5b9a\u5949\u966a\uff0c\u4ee4\u5c0a\u5cb3\u5927\u4fa0\u66fe\u6551\u8fc7\u6211\u4eec\u4e24\u4eba\u4e00\u547d\uff0c \u6211\u4eec\u6b63\u8be5\u5e2e\u5fd9\u516c\u5b50\u505a\u70b9\u4e8b\u60c5\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6697\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5e90\u4f2f\u4f2f\u8981\u8881\u5927\u54e5\u548c\u6211\u5230\u79e6\u5cad\u7ec8\u5357\u5c71\u4e00\u5e26\u6765\u627e\u98ce\u5ae3\u7ea2\u548c\u9a6c\u662d\u96c4\uff0c\u8bf4 \u4e0d\u5b9a\u4ed6\u5df2\u542c\u8bf4\u8fc7\u9b3c\u95e8\u5173\u7684\u4e8b\u5427\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u8881\u5c0f\u9e64\u7531\u5916\u9762\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u4f4f\u5728\u4e00\u5ba2\u623f\uff0c\u4ed6\u56e0\u521a\u624d\u5230\u5916\u9762\u529e\u4e86\u70b9\u4e8b\uff0c\u6240\u4ee5\u56de\u6765\u8f83\u665a\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8fde\u5fd9\u4e3a\u53cc\u65b9\u4ecb\u7ecd\u3002 \u3000\u3000\u5f53\u4e01\u6d9b\u548c\u7518\u9716\u5f97\u77e5\u8881\u5c0f\u9e64\u662f\u5723\u624b\u5e90\u4e91\u7684\u9ad8\u8db3\uff0c\u90fd\u4e0d\u514d\u793c\u656c\u6709\u52a0\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u81ea\u7136\u5927\u4e3a\u5f97\u610f\uff1a\u201c\u539f\u6765\u4e24\u4f4d\u4e5f\u8ba4\u8bc6\u5bb6\u5e08\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u6414\u6414\u8111\u888b\u9053\uff1a\u201c\u54ea\u91cc\uff0e\u6211\u4eec\u600e\u591f\u8d44\u683c\u76f8\u5e90\u5927\u4fa0\u4ea4\u60c5\uff0c\u4ed6\u8001\u4eba\u5bb6\u5f53\u5e74\u5728\u6b66\u6797\u4e2d\u9f0e\u9f0e\u5927 \u540d\uff0c\u50cf\u96f7\u9707\u8033\u6735\uff0c\u8c01\u4eba\u4e0d\u77e5\uff0c\u54ea\u4e2a\u4e0d\u6653\uff01\u201d \u3000\u3000\u7518\u9716\u7d27\u63a5\u7740\u9053\uff1a\u201c\u5bf9\u5566\u3002\u6700\u8fd1\u8fd9\u5341\u5e74\uff0c\u5e90\u5927\u4fa0\u5df2\u5f88\u5c11\u5728\u5916\u9732\u9762\uff0c\u82e5\u4e0d\u662f\u4eca\u5929\u78b0\u5230\u8881\u5c0f\u4fa0\uff0c \u6211\u4eec\u8fd8\u76f4\u4e0d\u77e5\u9053\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\u5462\uff1f\u201d \u3000\u3000\u7531\u4e01\u3001\u7518\u4e24\u4eba\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53ef\u77e5\u4ed6\u4eec\u5e76\u4e0d\u6e05\u695a\u51e4\u5ae3\u7ea2\u548c\u9a6c\u662d\u96c4\u7684\u4e00\u6bb5\u4e11\u4e8b\uff0c\u8881\u5c0f\u9e64\u5f53\u7136\u4e5f \u4e0d\u4fbf\u63d0\u8d77\u3002\u4e0d\u8fc7\uff0c\u4ed6\u5df2\u660e\u767d\u5e08\u7236\u5e90\u4e91\u5f53\u5e74\u7684\u786e\u662f\u4f4d\u4e86\u4e0d\u8d77\u7684\u4eba\u7269\u3002 \u3000\u3000\u5f53\u4ed6\u518d\u542c\u5230\u6709\u5173\u9b3c\u95e8\u5173\u7684\u4e8b\u65f6\uff0c\u66f4\u662f\u559c\u5c0f\u81ea\u80dc\uff0c\u5fd9\u9053\uff1a\u201c\u5c31\u8bf7\u4e24\u4f4d\u5f53\u5bb6\u7684\u66ff\u5728\u4e0b\u548c\u5cb3\u516c \u5b50\u5e26\u8def\uff0c\u4ee5\u4fbf\u89c1\u8bc6\u89c1\u8bc6\u9b3c\u95e8\u5173\u7a76\u7adf\u6709\u4ec0\u4e48\u7a00\u5947\u3002\u201d \u3000\u3000\u6b21\u65e5\u4e00\u65e9\uff0c\u56db\u4eba\u4fbf\u8d77\u7a0b\u7ed3\u4f34\u8d76\u8def\u3002 \u3000\u3000\u4e24\u5929\u4e4b\u540e\uff0c\u5230\u8fbe\u957f\u5b89\u3002 \u3000\u3000\u5728\u957f\u5b89\u4f4f\u4e86\u4e00\u591c\uff0c\u5357\u884c\u4fbf\u662f\u7ec8\u5357\u5c71\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u5e26\u662f\u79e6\u5cad\u5c71\u8109\uff0c\u7ef5\u4e98\u6570\u5343\u91cc\uff0c\u4e1c\u6709\u534e\u5c71\uff0c\u897f\u6709\u592a\u767d\u5c71\u3002\u5c71\u8def\u7684\u9669\u5cfb\uff0c\u4e0d\u96be\u60f3\u89c1\u3002 \u3000\u3000\u5728\u4f20\u8bf4\u4e2d\uff0c\u6709\u4e0d\u5c11\u795e\u4ed9\uff0c\u90fd\u9690\u5c45\u5728\u7ec8\u5357\u5c71\uff0c\u8fd9\u5f53\u7136\u662f\u56e0\u4e3a\u5c71\u52bf\u592a\u6df1\uff0c\u7edd\u5c11\u4eba\u70df\uff0c\u6240\u4ee5\u4eba \u4eec\u624d\u6709\u79cd\u79cd\u7684\u7a7f\u51ff\u9644\u4f1a\u3002 \u3000\u3000\u6b63\u540c\u4e3a\u5c71\u4e0a\u7edd\u5c11\u4eba\u70df\uff0c\u6240\u4ee5\u4ed6\u4eec\u5728\u957f\u5b89\u52a8\u8eab\u65f6\uff0c\u7279\u5730\u5e26\u4e86\u4e0d\u5c11\u5e72\u7cae\uff0c\u4ee5\u5907\u8def\u4e0a\u98df\u7528\u3002 \u3000\u3000\u770b\u770b\u5929\u5df2\u8fc7\u5348\uff0c\u624d\u4e0d\u8fc7\u6765\u5230\u5c71\u811a\u3002 \u3000\u3000\u5230\u508d\u665a\u65f6\uff0c\u597d\u4e0d\u5bb9\u6613\u53d1\u73b0\u4e00\u5904\u6c11\u5bb6\u3002 \u3000\u3000\u56db\u4eba\u4fbf\u5728\u6c11\u5bb6\u501f\u5bbf\u4e00\u665a\u3002 \u3000\u3000\u6b21\u65e5\u8d77\u884c\uff0c\u5947\u602a\u7684\u662f\u4e00\u8def\u4e0a\u5374\u9047\u5230\u4e0d\u5c11\u4eba\u7269\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u90e8\u4f69\u5200\u5e26\u5251\uff0c\u800c\u4e14\u6b65\u5c65\u77eb\u5065\uff0c\u4e00\u770b\u4fbf\u77e5\u90fd\u662f\u4e9b\u6b66\u6797\u540c\u9053\u3002 \u3000\u3000\u4ed6\u4eec\u6240\u8c08\u8bba\u7684\uff0c\u4e5f\u6b63\u662f\u6709\u5173\u9b3c\u95e8\u5173\u7684\u4e8b\u3002 \u3000\u3000\u5185\u4e2d\u96be\u514d\u4e5f\u6709\u4eba\u5411\u4e01\u6d9b\u548c\u7518\u9716\u6253\u62db\u547c\uff0c\u90fd\u79f0\u4ed6\u4eec\u4e3a\u201c\u592a\u767d\u53cc\u96c4\u201d\uff0c\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u8fd9\u624d \u77e5\u9053\u4e01\u6d9b\u548c\u7518\u9716\u539f\u6765\u4e5f\u662f\u6c5f\u6e56\u4e0a\u7684\u6210\u540d\u4eba\u7269\u3002 \u3000\u3000\u770b\u770b\u5230\u4e86\u4e0b\u5348\uff0c\u7ec8\u4e8e\u5230\u8fbe\u4e86\u9b3c\u95e8\u5173\u7684\u5165\u53e3\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5165\u53e3\u4e5f\u6b63\u662f\u4e00\u6761\u8c37\u9053\u7684\u5165\u53e3\uff0c\u4f46\u89c1\u8c37\u9053\u4e24\u4fa7\uff0c\u5ced\u58c1\u9ad8\u63d2\u4e91\u8868\uff0c\u800c\u4e14\u7b14\u76f4\u5982\u524a\uff0c\u51e0 \u4e4e\u662f\u96c0\u9e1f\u96be\u6e21\u3002 \u3000\u3000\u4f46\u8c37\u5e95\u5374\u53ea\u6709\u4e24\u4e09\u4e08\u5bbd\uff0c\u5f62\u6210\u4e86\u4e00\u5e45\u5929\u7136\u5947\u89c2\u3002 \u3000\u3000\u76f8\u4fe1\u4efb\u4f55\u4eba\u89c1\u4e86\uff0c\u4e5f\u4e0d\u5f97\u4e0d\u8d5e\u53f9\u9020\u7269\u4e4b\u5947\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u4e0d\u7981\u95ee\u9053\uff1a\u201c\u4e24\u4f4d\u5f53\u5bb6\u7684\uff0c\u82e5\u4e0d\u7ecf\u8fc7\u8fd9\u8c37\u53e3\uff0c\u662f\u5426\u5c31\u65e0\u6cd5\u8fdb\u5165\u9b3c\u95e8\u5173\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8881\u5c11\u4fa0\u5e94\u8be5\u770b\u5f97\u5230\uff0c\u8fd9\u8c37\u58c1\u9ad8\u6709\u51e0\u5341\u4e08\uff0c\u4e0d\u7ecf\u8c37\u53e3\uff0c\u6709\u8c01\u80fd\u8fdb\u5f97\u53bb\u5462\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u662f\u5426\u8fd8\u6709\u53e6\u5916\u7684\u8def\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u6447\u5934\u9053\uff1b\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u636e\u8bf4\u6709\u4e0d\u5c11\u4eba\u60f3\u627e\u53e6\u5916\u7684\u8def\uff0c\u53ef\u60dc\u6ca1\u6709\u4e00\u4e2a\u80fd\u627e\u5230\u3002\u201d \u3000\u3000\u8fd9\u65f6\u4ed6\u4eec\u5df2\u6e10\u8d70\u8fd1\u8c37\u53e3\uff0c\u53ea\u89c1\u8c37\u58c1\u4e0a\u5ffd\u7136\u51fa\u73b0\u4e86\u201c\u89c2\u5149\u96a7\u9053\u201d\u56db\u4e2a\u5927\u5b57\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u5b57\u662f\u7528\u7ea2\u6f06\u5199\u7684\uff0c\u6bcf\u5b57\u90fd\u6709\u78e8\u76d8\u5927\u5c0f\uff0c\u770b\u6765\u5341\u5206\u9192\u76ee\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u6123\u4e86\u4e00\u4e0b\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u89c2\u5149\u96a7\u9053\uff1f\u201d \u3000\u3000\u4ed6\u5f53\u7136\u662f\u95ee\u4e01\u6d9b\u548c\u7518\u9716\u7684\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u90fd\u662f\u7c97\u4eba\uff0c\u53ef\u80fd\u8fde\u8fd9\u56db\u4e2a\u5b57\u90fd\u4e0d\u4e00\u5b9a\u5168\u8ba4\u8bc6\uff0c\u81ea\u7136\u96be\u4ee5\u89e3\u91ca\uff0c\u95ee\u4e86\u4e5f\u662f\u767d\u95ee\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u8c37\u53e3\u5ffd\u7136\u51fa\u73b0\u4e86\u4e00\u540d\u8001\u4eba\u548c\u4e00\u540d\u5c11\u5973\u3002 \u3000\u3000\u90a3\u8001\u4eba\u8eab\u7a7f\u9ed1\u888d\uff0c\u4e00\u5f20\u50cf\u98ce\u5e72\u6a58\u5b50\u76ae\u822c\u7684\u9a6c\u8138\uff0c\u9894\u4e0b\u7559\u7740\u4e00\u64ae\u5c71\u7f8a\u80e1\u5b50\uff0c\u4e24\u773c\u867d\u5c0f\u5f97\u50cf \u8c46\uff0c\u5374\u94c4\u94c4\u53d1\u5149\u3002 \u3000\u3000\u90a3\u5c11\u5973\u7ea2\u8863\u7ea2\u88d9\uff0c\u8138\u86cb\u5341\u5206\u751c\u7f8e\u3002\u800c\u4e14\u7b11\u9762\u8fce\u4eba\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u7ad9\u5728\u4e00\u8d77\uff0e\u7167\u6837\u4e5f\u6784\u6210\u4e86\u4e00\u5e45\u9887\u4e3a\u9192\u76ee\u7684\u753b\u9762\uff0c\u56e0\u4e3a\u8fd9\u753b\u9762\u770b\u6765\u5341\u5206\u4e0d\u8c03\u548c\u3002 \u3000\u3000\u56db\u4eba\u6b63\u8981\u7ee7\u7eed\u524d\u8fdb\uff0c\u90a3\u9a6c\u8138\u8001\u4eba\u548c\u7ea2\u8863\u5c11\u5973\u5df2\u6a2a\u8eab\u62e6\u4f4f\u53bb\u8def\u3002 \u3000\u3000\u4e01\u6d9b\u4e0d\u7531\u6c89\u4e0b\u58f0\u97f3\u9053\uff1a\u201c\u8001\u5934\u513f\uff0c\u51ed\u4ec0\u4e48\u6321\u6211\u4eec\u7684\u8def\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8001\u4eba\u5475\u5475\u4e00\u7b11\u9053\uff1a\u201c\u8001\u592b\u60f3\u95ee\u95ee\uff0c\u56db\u4f4d\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u5f53\u7136\u662f\u8981\u5230\u9b3c\u95e8\u5173\u53bb\u3002\u201d \u3000\u3000\u9ed1\u8863\u8001\u4eba\u53c8\u5475\u5475\u7b11\u9053\uff1a\u201c\u679c\u7136\u662f\u6709\u7f18\u4eba\uff0c\u4e0d\u7136\u4e0d\u4f1a\u627e\u5230\u8fd9\u91cc\uff0c\u9b3c\u95e8\u5173\u5c31\u5728\u91cc\u9762\uff0c\u8fc7\u4e86\u9b3c \u95e8\u5173\u5c31\u662f\u5929\u8c37\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u968f\u5373\u4f38\u51fa\u4e00\u53ea\u624b\u6765\u3002 \u3000\u3000\u4e01\u6d9b\u832b\u7136\u95ee\u9053\uff1a\u201c\u8fd9\u7b97\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8001\u4eba\u9053\uff1b\u201c\u8981\u8fdb\u9b3c\u95e8\u5173\uff0c\u7b2c\u4e00\u7ad9\u662f\u8001\u592b\u8d1f\u8d23\u770b\u5b88\u7684\u2018\u89c2\u5149\u96a7\u9053\u2019\uff0c\u6240\u4ee5\u5fc5\u987b\u5411\u5404\u4f4d \u6536\u94b1\u3002\u201d \u3000\u3000\u4e01\u6d9b\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8def\u662f\u4eba\u4eba\u53ef\u8d70\u7684\uff0c\u96be\u9053\u4f60\u8fd9\u8001\u5bb6\u4f19\u8fd8\u60f3\u8981\u4e70\u8def\u94b1\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8001\u4eba\u795e\u79d8\u4e00\u7b11\u9053\uff1a\u201c\u56db\u4f4d\u7684\u94b1\u4e0d\u4f1a\u767d\u82b1\uff0c\u2018\u89c2\u5149\u96a7\u9053\u2019\u91cc\u6709\u5f88\u591a\u5947\u5947\u602a\u602a\u7684\u4e1c\u897f\uff0c \u8c01\u770b\u4e86\u4e5f\u4f1a\u5927\u5f00\u773c\u754c\uff0c\u6240\u4ee5\u8001\u592b\u8981\u7684\u5e94\u8be5\u662f\u5165\u95e8\u7968\uff0c\u5e76\u975e\u4e70\u8def\u94b1\u3002\u201d \u3000\u3000\u4e01\u6d9b\u987f\u4e86\u987f\u9053\uff1a\u201c\u8981\u591a\u5c11\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8001\u4eba\u9053\uff1a\u201c\u6bcf\u4eba\u4e94\u4e24\u94f6\u5b50\uff0c\u56db\u4f4d\u4e00\u5171\u4e8c\u5341\u4e24\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4e24\u773c\u77aa\u5f97\u50cf\u94c3\u94db\uff1a\u201c\u54ea\u6709\u8fd9\u4e48\u8d35\u7684\uff0c\u4e61\u4e0b\u4eba\u4e00\u4e2a\u6708\u90fd\u4e0d\u89c1\u5f97\u80fd\u8d5a\u4e94\u4e24\u94f6\u5b50\uff0c\u8001\u5934\u513f\uff0c \u83ab\u975e\u4f60\u662f\u5403\u4eba\u7684\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8001\u4eba\u6478\u7740\u5c71\u7f8a\u80e1\u5b50\u9053\uff1a\u201c\u56db\u4f4d\u90fd\u4e0d\u53ef\u80fd\u662f\u4e61\u4e0b\u4eba\uff0c\u4e00\u5206\u94f6\u5b50\u4e00\u5206\u8d27\uff0c\u56db\u4f4d\u770b\u8fc7\u4ee5\u540e\uff0c \u5b9a\u4f1a\u8ba4\u4e3a\u4e00\u70b9\u4e5f\u4e0d\u8d35\u3002\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u7ea2\u8863\u5c11\u5973\u4e5f\u542b\u7b11\u5f00\u4e86\u53e3\uff1a\u201c\u6211\u4eec\u8001\u638c\u67dc\u8bf4\u5f97\u5bf9\uff0c\u4f60\u4eec\u56db\u4f4d\u7237\u4eec\u770b\u8fc7\u4e86\u4e00\u5b9a\u8fd8\u60f3\u518d \u770b\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6211\u8981\u5148\u95ee\u95ee\u4f60\u4eec\uff0c\u90a3\u56db\u4e2a\u5b57\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8001\u4eba\u9053\uff1a\u201c\u518d\u5f80\u524d\u8d70\u4e0d\u4e45\uff0c\u4fbf\u8981\u7a7f\u8fc7\u4e00\u6761\u5c71\u6d1e\uff0c\u90a3\u5c71\u6d1e\u5f53\u7136\u5c31\u662f\u96a7\u9053\uff0c\u81f3\u4e8e\u89c2\u5149\u4e24 \u4e2a\u5b57\uff0c\u5c31\u8bf7\u6211\u4eec\u4e2b\u5934\u8bb2\u7ed9\u4f60\u542c\u5427\uff01\u201d \u3000\u3000\u4e01\u6d9b\u7acb\u5373\u671b\u5411\u7ea2\u8863\u5c11\u5973\u3002 \u3000\u3000\u7ea2\u8863\u5c11\u5973\u76c8\u76c8\u4e00\u7b11\u9053\uff1a\u201c\u89c2\u5149\u5c31\u662f\u5149\u6e9c\u6e9c\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4e00\u76b1\u7709\u773c\u5934\u9053\uff1a\u201c\u5149\u6e9c\u6e9c\u7684\u4e1c\u897f\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u5a9a\u773c\u4e00\u629b\u9053\uff1a\u201c\u96be\u9053\u5973\u4eba\u8131\u5149\u4e86\u8863\u670d\u4f60\u4e5f\u4e0d\u60f3\u770b\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u6765\u7acb\u523b\u5f15\u8d77\u4e86\u4e01\u6d9b\u7684\u5174\u8da3\u3002 \u3000\u3000\u7518\u9716\u4e5f\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u4f46\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u5374\u90fd\u5728\u4e00\u65c1\u80c0\u7ea2\u4e86\u8138\u3002\u4ed6\u4eec\u771f\u4e0d\u76f8\u4fe1\u90a3\u7ea2\u8863\u5c11\u5973\u7adf\u8fde\u8fd9\u79cd\u8bdd\u4e5f\u8bf4\u5f97\u51fa \u6765\u3002 \u3000\u3000\u4e01\u6d9b\u5658\u5507\u9053\uff1a\u201c\u539f\u6765\u89c2\u5149\u5c31\u662f\u8fd9\u4e2a\uff0c\u4e94\u4e24\u94f6\u5b50\u7684\u786e\u503c\u5f97\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u4fbf\u8981\u63a2\u624b\u5165\u6000\u638f\u94f6\u5b50\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5fd9\u9053\uff1a\u201c\u4e01\u4eba\u54e5\uff0c\u8fd9\u6837\u7684\u89c2\u5149\uff0c\u4e0d\u89c2\u4e5f\u7f62\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u516c\u5b50\u4e5f\u8ddf\u7740\u5f00\u5f00\u773c\u754c\uff0c\u4eca\u5929\u7b97\u6211\u8bf7\u5ba2\u3002\u201d \u3000\u3000\u90a3\u9ed1\u8863\u8001\u4eba\u4e5f\u5728\u65c1\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\uff0c\u7684\u786e\u662f\u8fdb\u2018\u89c2\u5149\u96a7\u9053\u2019\u6700\u5e74\u8f7b\u7684\u4e00\u4f4d\uff0c\u4e0d\u8fc7\u4f60 \u4eec\u65e2\u7136\u8981\u5230\u9b3c\u95e8\u5173\uff0c\u4e0d\u53ef\u80fd\u4e0d\u7ecf\u8fc7\u2018\u89c2\u5149\u96a7\u9053\u2019\uff0c\u5982\u679c\u771f\u4e0d\u60f3\u770b\uff0c\u4e0d\u59a8\u95ed\u7740\u773c\u775b\u8d70\u8fc7\u53bb\u3002\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u54af\u54af\u7b11\u9053\uff1a\u201c\u8001\u638c\u67dc\u7684\uff0c\u4f60\u600e\u4e48\u8fd9\u6837\u8bf4\u8bdd\uff0c\u4eba\u5bb6\u82b1\u4e86\u94b1\uff0c\u5f53\u7136\u5fc5\u987b\u770b\u4e0a\u4e00\u770b\u3002\u201d \u3000\u3000\u8fd9\u65f6\u4e01\u6d9b\u5df2\u638f\u51fa\u4e00\u5f20\u94f6\u7968\uff0c\u4ea4\u7ed9\u9ed1\u8863\u8001\u4eba\u9053\uff1a\u201c\u770b\u4e00\u770b\uff0c\u8fd9\u662f\u94f6\u7968\u4e8c\u5341\u4e24\u3002\u201d \u3000\u3000\u9ed1\u8863\u8001\u4eba\u6536\u4e86\u94f6\u7968\uff0c\u5374\u4ece\u6000\u4e2d\u6478\u51fa\u56db\u652f\u706b\u71a0\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u4eec\u56db\u4f4d\u7684\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8981\u8fd9\u4e2a\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ed1\u8863\u8001\u4eba\u9053\uff1a\u201c\u96a7\u9053\u91cc\u5f88\u6697\uff0c\u4e0d\u7528\u8fd9\u4e2a\uff0c\u4f60\u4eec\u8fd8\u89c2\u7684\u4ec0\u4e48\u5149\u3002\u201d \u3000\u3000\u4e01\u6d9b\u7acb\u5373\u6536\u4e0b\uff0c\u5e76\u6bcf\u4eba\u5206\u4e00\u652f\u3002 \u3000\u3000\u9ed1\u8863\u8001\u4eba\u9053\uff1a\u201c\u73b0\u5728\u56db\u4f4d\u53ef\u4ee5\u8fdb\u53bb\u4e86\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u6211\u8981\u8fd9\u4f4d\u59d1\u5a18\u5e26\u8def\uff01\u201d \u3000\u3000\u9ed1\u8863\u8001\u4eba\u9053\uff1a\u201c\u53ea\u8981\u5f80\u91cc\u8d70\uff0c\u5f88\u5feb\u5c31\u5230\uff0c\u54ea\u7528\u5f97\u7740\u5e26\u8def\u3002\u201d \u3000\u3000\u4e01\u6d9b\u6447\u5934\u9053\uff1a\u201c\u4e00\u5b9a\u8981\u5979\u5e26\u8def\uff0c\u5728\u4e0b\u591a\u7ed9\u94f6\u5b50\u90fd\u53ef\u4ee5\u3002\u201d \u3000\u3000\u4e0d\u7b49\u9ed1\u8863\u8001\u4eba\u518d\u8bf4\u8bdd\uff0c\u7ea2\u8863\u5c11\u5973\u5df2\u8106\u751f\u751f\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u54e5\u522b\u51f6\uff0c\u5e26\u8def\u5c31\u5e26\u8def\uff0c\u73b0\u5728\u5c31 \u8ddf\u6211\u8d70\u5427\uff01\u201d \u3000\u3000\u4e8e\u662f\u56db\u4eba\u8ddf\u5728\u7ea2\u8863\u5c11\u5973\u8eab\u540e\u5f00\u59cb\u524d\u8fdb\u3002 \u3000\u3000\u7ea2\u8863\u5c11\u5973\u67f3\u8170\u6b3e\u6446\uff0c\u83b2\u6b65\u751f\u59ff\uff0c\u51e0\u4e4e\u6bcf\u8d70\u2014\u6b65\uff0c\u90fd\u64a9\u4eba\u9050\u601d\u3002 \u3000\u3000\u5979\u8fb9\u8d70\u8fb9\u55f2\u7740\u58f0\u97f3\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u54e5\uff0c\u4f60\u4e3a\u4ec0\u4e48\u975e\u8981\u6211\u5e26\u8def\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u5403\u5403\u7684\u9053\uff1a\u201c\u8001\u5b50\u7684\u94b1\u4e0d\u80fd\u767d\u82b1\uff0c\u6709\u4f60\u8d70\u5728\u524d\u9762\uff0c\u770b\u7740\u4e5f\u8212\u670d\u4e9b\u3002\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u6251\u54e7\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u820d\u4e0d\u5f97\u4e8c\u5341\u4e24\u94f6\u5b50\uff0c\u6211\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff0c\u5f85\u4f1a\u513f\u770b\u89c1\u90a3\u4e9b \u4e0d\u7a7f\u8863\u670d\u7684\uff0c\u90a3\u624d\u771f\u6b63\u597d\u770b\u5462\u3002\u201d \u3000\u3000\u4e01\u6d9b\u5e72\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6211\u60f3\u770b\u4f60\u8131\u5149\uff0c\u4f60\u80af\u4e0d\u80af\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u7b11\u5f97\u51e0\u4e4e\u5f2f\u4e86\u8170\uff0c\u9053\uff1a\u201c\u4eca\u5929\u6ca1\u8f6e\u5230\u6211\u8131\uff0c\u4f60\u82e5\u4e0b\u6b21\u518d\u6765\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u770b\u5f97\u5230\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u524d\u9762\u679c\u7136\u5df2\u662f\u4e00\u5904\u5c71\u6d1e\uff0c\u7ea2\u8863\u5c11\u5973\u9053\uff1a\u201c\u5230\u5566\uff0c\u4f60\u4eec\u81ea\u5df1\u8fdb\u53bb\u5427\uff01\u201d \u3000\u3000\u8fd9\u65f6\u4e01\u6d9b\u5df2\u987e\u4e0d\u5f97\u518d\u548c\u7ea2\u8863\u5c11\u5973\u8c03\u7b11\uff0c\u62c9\u7740\u7518\u9716\u4fbf\u5411\u91cc\u8d70\u3002 \u3000\u3000\u521a\u8fdb\u5165\u96a7\u9053\uff0c\u4ecd\u80fd\u89c1\u7269\uff0c\u6e10\u6e10\u7684\u8d8a\u6df1\u8d8a\u6697\uff0c\u5230\u540e\u6765\u5df2\u662f\u4e00\u7247\u6f06\u9ed1\uff0c\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\u3002 \u3000\u3000\u53c8\u524d\u8fdb\u6570\u4e08\uff0c\u4e01\u6d9b\u548c\u7518\u9716\u624d\u53d1\u89c9\u5df2\u7ecf\u4e0d\u89c1\u4e86\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u4eec\u660e\u767d\uff0c\u51fa\u4e86\u96a7\u9053\u4ee5\u540e\uff0c\u4e00\u5b9a\u53ef\u4ee5\u4f1a\u5408\u3002 \u3000\u3000\u4e01\u6d9b\u50cf\u662f\u81ea\u8a00\u767d\u8bed\u9053\uff1a\u201c\u4ed6\u5a18\u7684\uff0c\u83ab\u4e0d\u6210\u88ab\u4ed6\u4eec\u9a97\u4e86\uff01\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u71c3\u4eae\u706b\u71a0\u5b50\uff0c\u600e\u77e5\u9053\u88ab\u4ed6\u4eec\u9a97\u4e86\u3002\u201d \u3000\u3000\u4e01\u6d9b\u8fde\u5fd9\u4eae\u8d77\u706b\u71a0\u3002 \u3000\u3000\u53ea\u89c1\u4e24\u65c1\u6d1e\u58c1\u4e0a\uff0c\u5f00\u51ff\u4e86\u4e0d\u5c11\u6d1e\u95e8\uff0c\u4e0d\u7528\u8bf4\uff0c\u597d\u770b\u7684\u5c31\u5728\u6d1e\u95e8\u91cc\u9762\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u7acb\u5373\u8fdb\u5165\u4e00\u5904\u6d1e\u95e8\u3002 \u3000\u3000\u6d1e\u95e8\u5185\u662f\u4e00\u5904\u77f3\u5ba4\uff0c\u4e24\u4eba\u7684\u773c\u795e\uff0c\u4e0d\u7531\u9f50\u9f50\u4e3a\u4e4b\u4e00\u4eae\u3002 \u3000\u3000\u679c\u7136\u5230\u4e86\u89c2\u5149\u6240\u5728\uff0c\u4e00\u4e2a\u5168\u88f8\u7684\u5999\u9f84\u5c11\u5973\uff0c\u5c31\u5728\u9760\u8fd1\u77f3\u5ba4\u7684\u540e\u58c1\u7ad9\u7740\u3002 \u3000\u3000\u90a3\u5973\u90ce\u7b11\u8138\u8fce\u4eba\uff0c\u5168\u8eab\u808c\u80a4\u5149\u6d01\u6676\u83b9\uff0c\u6709\u5982\u7c89\u96d5\u7389\u51ff\u4e00\u822c\uff0c\u8eab\u6750\u4e0d\u80d6\u4e2a\u7626\uff0c\u5c24\u5176\u53cc\u5cf0\u9ad8 \u8038\uff0c\u7389\u817f\u4fee\u957f\uff0c\u7ad9\u5728\u90a3\u91cc\uff0c\u540d\u7b26\u5176\u5b9e\u7684\u53ef\u4ee5\u62ff\u201c\u4ead\u4ead\u4e47\u7acb\u201d\u56db\u5b57\u5f62\u5bb9\u3002 \u3000\u3000\u5979\u4e00\u817f\u5728\u524d\uff0c\u4e00\u817f\u5728\u540e\uff0c\u90a3\u7d27\u8981\u4e4b\u5904\uff0c\u4f3c\u9690\u4f3c\u73b0\uff0c\u59ff\u6001\u64a9\u4eba\u81f3\u6781\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u4e01\u6d9b\u548c\u7518\u9716\uff0c\u90fd\u60c5\u4e0d\u81ea\u7981\u6709\u4e9b\u547c\u5438\u6025\u4fc3\uff0c\u8840\u8109\u6269\u5f20\uff0c\u4e24\u773c\u4e5f\u90fd\u5728\u53d1\u76f4\u3002 \u3000\u3000\u90a3\u5973\u90ce\u5728\u4e01\u6d9b\u548c\u7518\u9716\u8fdb\u6765\u4e4b\u540e\uff0c\u59ff\u52bf\u867d\u7136\u672a\u52a8\uff0c\u4e00\u5bf9\u7738\u5b50\u5374\u52fe\u9b42\u6444\u9b44\u822c\u4e0d\u505c\u7684\u79cb\u6ce2\u6697\u9001\u3002 \u3000\u3000\u8fd9\u5728\u4e01\u6d9b\u548c\u7518\u9716\u6765\u8bf4\uff0c\u94f6\u5b50\u679c\u7136\u6ca1\u6709\u767d\u82b1\uff0c\u4ec5\u4ec5\u8fd9\u4e00\u6d1e\uff0c\u5c31\u503c\u56de\u7968\u4ef7\u3002 \u3000\u3000\u4e01\u6d9b\u88c2\u5634\u7b11\u9053\uff1b\u201c\u8fd9\u599e\u513f\u771f\u4e0d\u8d56\uff0c\u8001\u7518\uff0c\u54b1\u4eec\u53ef\u8981\u4ed4\u7ec6\u77a7\u77a7\uff01\u201d \u3000\u3000\u4e24\u4eba\u672c\u6765\u51c6\u5907\u518d\u8d70\u5230\u90a3\u88f8\u5973\u7684\u8eab\u540e\u77a7\u77a7\uff0c\u5374\u56e0\u5979\u540e\u80cc\u5df2\u9760\u8fd1\u6d1e\u58c1\uff0c\u65e0\u6cd5\u770b\u5230\u5979\u7684\u524d\u9762\u3002 \u3000\u3000\u7518\u9716\u5e72\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u597d\u662f\u597d\uff0c\u53ef\u60dc\u5979\u4e0d\u80af\u5f00\u53e3\u8bb2\u8bdd\u3002\u201d \u3000\u3000\u4e01\u6d9b\u968f\u5373\u95ee\u9053\uff1a\u201c\u599e\u513f\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8bb2\u8bdd\uff1f\u201d \u3000\u3000\u90a3\u88f8\u5973\u771f\u7684\u4e0d\u80af\u5f00\u53e3\uff0c\u5374\u8d8a\u53d1\u671b\u7740\u4e01\u6d9b\u548c\u7518\u9716\u5d4b\u7b11\u3002 \u3000\u3000\u4e01\u6d9b\u5b9e\u5728\u6309\u637a\u4e0d\u4f4f\uff0c\u4f38\u624b\u4fbf\u5411\u90a3\u88f8\u5973\u7684\u53cc\u4e73\u4e0a\u6478\u53bb\u3002 \u3000\u3000\u90a3\u77e5\u4ed6\u7684\u624b\u5c1a\u672a\u89e6\u53ca\u5bf9\u65b9\u808c\u80a4\uff0c\u8033\u9645\u4fbf\u54cd\u8d77\u4e00\u4e2a\u7c97\u58ee\u7684\u58f0\u97f3\u9053\uff1a\u201c\u670b\u53cb\uff0c\u53ea\u51c6\u770b\uff0c\u4e0d\u51c6 \u6478\uff0c\u4f60\u4eec\u53ea\u662f\u89c2\u5149\uff0c\u4e0d\u662f\u6478\u5149\uff01\u201d \u3000\u3000\u4e01\u6d9b\u6536\u56de\u624b\u6765\uff0c\u56de\u5934\u671b\u53bb\uff0c\u5374\u770b\u4e0d\u89c1\u53d1\u8bdd\u7684\u4eba\u85cf\u5728\u54ea\u91cc\u3002 \u3000\u3000\u7518\u9716\u8ffd\u51fa\u77f3\u5ba4\u4e4b\u5916\uff0c\u4e5f\u672a\u53d1\u73b0\u4eba\u5f71\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u90fd\u662f\u4e45\u8d70\u6c5f\u6e56\u7684\uff0c\u4e0d\u96be\u60f3\u89c1\u8fd9\u77f3\u5ba4\u5185\u5fc5\u5b9a\u8bbe\u6709\u673a\u5173\u3002 \u3000\u3000\u4e01\u6d9b\u672c\u6765\u8fd8\u60f3\u518d\u770b\uff0c\u4f46\u7518\u9716\u5374\u50ac\u4fc3\u7740\u9053\uff1a\u201c\u5176\u4ed6\u7684\u77f3\u5ba4\u5185\u4e00\u5b9a\u8fd8\u6709\uff0c\u82e5\u628a\u706b\u71a0\u70b9\u7184\u4e86\uff0c \u54b1\u4eec\u5c82\u4e0d\u662f\u53cd\u800c\u5403\u4e8f\u3002\u201d \u3000\u3000\u4e00\u8bed\u63d0\u9192\u4e01\u6d9b\uff0c\u53ea\u597d\u9000\u51fa\u8fd9\u4e00\u77f3\u5ba4\uff0c\u6301\u7740\u706b\u71a0\u53c8\u8fdb\u5165\u53e6\u4e00\u77f3\u5ba4\u3002 \u3000\u3000\u53e6\u4e00\u77f3\u5ba4\u679c\u7136\u53c8\u662f\u4e00\u540d\u88f8\u5973\u3002 \u3000\u3000\u8fd9\u88f8\u5973\u6bd4\u5148\u524d\u7684\u66f4\u7f8e\uff0c\u4e0d\u8fc7\u59ff\u52bf\u5374\u53d8\u4e86\uff0c\u662f\u5750\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5979\u4e00\u817f\u642d\u5728\u53e6\u4e00\u817f\u4e0a\uff0c\u521a\u597d\u628a\u7d27\u8981\u4e4b\u5904\u63a9\u4f4f\u3002 \u3000\u3000\u4e01\u6d9b\u54bd\u4e0b\u4e00\u53e3\u553e\u6db2\u9053\uff1a\u201c\u599e\u513f\uff0c\u53ef\u4e0d\u53ef\u4ee5\u628a\u4e0a\u9762\u90a3\u6761\u817f\u653e\u5f00\uff1f\u201d \u3000\u3000\u8fd9\u6b21\u90a3\u88f8\u5973\u8bb2\u8bdd\u4e86\uff1a\u201c\u89c4\u5b9a\u5c31\u662f\u8fd9\u6837\uff0c\u8c01\u6562\u4e0d\u9075\u3002\u201d \u3000\u3000\u4e01\u6d9b\u7b11\u9053\uff1a\u201c\u8001\u5b50\u662f\u82b1\u94b1\u7684\u5927\u7237\uff0c\u4f60\u8981\u542c\u8001\u5b50\u7684\u89c4\u5b9a\u3002\u201d \u3000\u3000\u90a3\u88f8\u5973\u9053\uff1a\u201c\u4f60\u60f3\u770b\uff0c\u522b\u5916\u7684\u5730\u65b9\u8fd8\u5f88\u591a\uff0c\u6211\u8fd9\u91cc\u5c31\u662f\u8fd9\u6837\uff01\u201d \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u968f\u5373\u53c8\u8d76\u5230\u53e6\u4e00\u77f3\u5ba4\u3002 \u3000\u3000\u8fd9\u65f6\u4e01\u6d9b\u7684\u706b\u71a0\u5df2\u7ecf\u71c3\u5b8c\uff0c\u4e3e\u7740\u706b\u71a0\u7684\u662f\u7518\u9716\u3002 \u3000\u3000\u8fd9\u4e00\u77f3\u5ba4\u7684\u88f8\u5973\u59ff\u52bf\u53c8\u6539\u53d8\u4e86\uff0c\u7adf\u662f\u4fa7\u5367\u5728\u4e00\u5757\u5927\u77f3\u677f\u4e0a\u3002 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u60f3\u770b\u7684\u8fd8\u662f\u770b\u4e0d\u5230\u3002 \u3000\u3000\u4ed6\u4eec\u4e24\u4eba\u62c5\u5fc3\u706b\u71a0\u71c3\u5b8c\uff0c\u4e0d\u6562\u591a\u803d\u8bef\uff0c\u5f88\u5feb\u7684\u518d\u8fdb\u5165\u4e0b\u4e00\u95f4\u77f3\u5ba4\u3002 \u3000\u3000\u5f53\u771f\u662f\u4e00\u5904\u53c8\u80dc\u4e00\u5904\uff0c\u8fd9\u95f4\u77f3\u5ba4\u5185\u7adf\u662f\u6446\u4e86\u4e00\u5f20\u5e8a\uff0c\u6709\u4e24\u540d\u88f8\u5973\u6b63\u7d27\u7d27\u62e5\u62b1\u7740\u53e0\u5728\u5e8a\u4e0a\uff0c \u5f88\u663e\u7136\u7684\uff0c\u5979\u4eec\u662f\u6b63\u5728\u626e\u6f14\u7740\u4e00\u5bf9\u8fdb\u884c\u90a3\u79cd\u4e8b\u60c5\u7684\u7537\u5973\uff0c\u53ea\u662f\u5979\u4eec\u4ec5\u4ec5\u6446\u4e86\u59ff\u52bf\uff0c\u5374\u5e76\u65e0\u52a8 \u4f5c\u3002 \u3000\u3000\u4e24\u4eba\u4e24\u773c\u53d1\u76f4\u7684\u770b\u4e86\u534a\u664c\uff0c\u4e01\u6d9b\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u8fd9\u7b97\u4ec0\u4e48\uff0c\u5e72\u8106\u8ba9\u6211\u4eec\u4e24\u4e2a \u4e5f\u4e0a\u5e8a\uff0c\u90a3\u624d\u591f\u8da3\uff01\u201d \u3000\u3000\u4e24\u540d\u88f8\u5973\u8fd8\u662f\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u7d27\u8981\u5173\u5934\uff0c\u504f\u504f\u706b\u71a0\u5df2\u7ecf\u7184\u4e86\u3002 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u771f\u6068\u4e0d\u5f97\u518d\u56de\u5230\u8c37\u53e3\u4e70\u51e0\u652f\u706b\u71a0\u3002 \u3000\u3000\u597d\u5728\u79bb\u5f00\u77f3\u5ba4\u4e4b\u540e\uff0c\u4ed6\u4eec\u8fd8\u662f\u5fcd\u4e86\u3002 \u3000\u3000\u7ee7\u7eed\u524d\u8fdb\uff0c\u9ed1\u6697\u4e2d\u4ecd\u80fd\u770b\u51fa\u4e24\u65c1\u6d1e\u58c1\u4ecd\u6709\u4e0d\u5c11\u6d1e\u53e3\uff0c\u60f3\u50cf\u4e2d\u91cc\u9762\u7684\u60c5\u666f\u4e00\u5b9a\u66f4\u4e3a\u7cbe\u5f69\uff0c \u4eec\u706b\u71a0\u5df2\u7ecf\u7528\u5b8c\uff0c\u518d\u7cbe\u5f69\u4e5f\u662f\u767d\u642d\u3002 \u3000\u3000\u5927\u7ea6\u53c8\u8d70\u51fa\u5341\u51e0\u4e08\uff0c\u624d\u51fa\u4e86\u96a7\u9053\uff0c\u773c\u524d\u4e5f\u968f\u4e4b\u4eae\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5c71\u4e86\u96a7\u9053\u4e4b\u540e\uff0c\u524d\u9762\u4ecd\u662f\u8c37\u9053\uff0c\u4e24\u65c1\u7684\u5ca9\u58c1\uff0c\u7167\u6837\u4e5f\u9ad8\u53ef\u63d2\u5929\u3002 \u3000\u3000\u5f88\u5feb\u7684\uff0c\u4ed6\u4eec\u5c31\u53d1\u73b0\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u5728\u5ca9\u58c1\u4e0b\u4f11\u606f\u3002 \u3000\u3000\u5176\u5b9e\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u5e76\u975e\u4f11\u606f\uff0c\u800c\u662f\u5728\u7b49\u5019\u4e01\u6d9b\u548c\u7518\u9716\u3002 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u52a0\u5feb\u811a\u6b65\uff0c\u8ddf\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6765\u5230\u8ddf\u524d\uff0c\u4e01\u6d9b\u8feb\u4e0d\u53ca\u5f85\u7684\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u89c2\u8fc7\u5149\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u60f3\u770b\uff0c\u6240\u4ee5\u624d\u5728\u8fd9\u91cc\u7b49\u4f60\u4eec\u4e24\u4f4d\u3002\u201d \u3000\u3000\u4e01\u6d9b\u731b\u4e00\u8dfa\u811a\u9053\uff1a\u201c\u4f60\u4eec\u4e5f\u771f\u662f\uff0c\u8fd9\u79cd\u673a\u4f1a\u9519\u8fc7\uff0c\u4ee5\u540e\u54ea\u91cc\u53bb\u627e\uff0c\u3000\u4e8c\u4f4d\u7684\u706b\u71a0\u5462\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u5404\u81ea\u628a\u706b\u71a0\u62ff\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e01\u6d9b\u4e00\u628a\u62a2\u4e86\u8fc7\u6765\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u8bf7\u5728\u8fd9\u91cc\u518d\u7b49\u4e00\u4e0b\uff01\u201d \u3000\u3000\u63a5\u7740\u62c9\u4e86\u7518\u9716\u4e00\u628a\u9053\uff1a\u201c\u8d70\uff0c\u54b1\u4eec\u518d\u56de\u53bb\u770b\uff01\u201d \u3000\u3000\u7518\u9716\u5f53\u7136\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002 \u3000\u3000\u4e24\u4eba\u5f88\u5feb\u4fbf\u53c8\u56de\u5230\u201c\u89c2\u5149\u96a7\u9053\u201d\u3002 \u3000\u3000\u5c82\u6599\u5f53\u71c3\u4eae\u706b\u71a0\u540e\uff0c\u4e24\u4eba\u7adf\u5168\u6014\u5728\u5f53\u573a\u3002 \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u90a3\u6d1e\u58c1\u4e24\u65c1\u7684\u77f3\u5ba4\uff0c\u7adf\u5b8c\u5168\u4e0d\u89c1\u4e86\uff0c\u8fde\u534a\u70b9\u75d5\u8ff9\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u2026\u2026\u2026 \u3000\u3000\u4e24\u4eba\u8d77\u521d\u4e0d\u6b7b\u5fc3\uff0c\u8fc5\u5373\u4e14\u8d70\u5411\u6d1e\u58c1\u7528\u529b\u6478\u53bb\u3002 \u3000\u3000\u4f46\u4e5f\u6478\u4e0d\u51fa\u6709\u4ec0\u4e48\u5f02\u6837\u3002 \u3000\u3000\u4e16\u4e0a\u504f\u6709\u8fd9\u4e48\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u4eec\u53ea\u597d\u518d\u8fd4\u56de\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u505c\u8eab\u4e4b\u5904\u3002 \u3000\u3000\u201c\u4e8c\u4f4d\u5f53\u5bb6\u7684\u600e\u4e48\u8fd9\u6837\u5feb\u5c31\u56de\u6765\u4e86\uff1f\u201d\u8881\u5c0f\u9e64\u4e5f\u89c9\u5f97\u5947\u602a\u3002 \u3000\u3000\u4e01\u6d9b\u6123\u6123\u7684\u6447\u5934\u9053\uff1a\u201c\u602a\u4e8b\u5e74\u5e74\u6709\uff0c\u5f80\u5e74\u4e0d\u53ca\u4eca\u5e74\u591a\uff0c\u522b\u63d0\u4e86\uff0c\u8d70\u5427\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e5f\u4e0d\u4fbf\u518d\u95ee\u3002 \u3000\u3000\u56db\u4eba\u7ee7\u7eed\u524d\u884c\u3002 \u3000\u3000\u5927\u7ea6\u53c8\u8d70\u4e86\u4e00\u91cc\u591a\u8def\uff0c\u53c8\u8fdb\u5165\u4e00\u5904\u96a7\u9053\u3002 \u3000\u3000\u8fd9\u6761\u96a7\u9053\u4f3c\u4e4e\u4e0d\u957f\uff0c\u800c\u4e14\u548c\u201c\u89c2\u5149\u96a7\u9053\u201d\u5927\u4e0d\u4e00\u6837\uff0c\u56e0\u4e3a\u6d1e\u9876\u60ac\u7740\u4e0d\u5c11\u7eb1\u706f\uff0c\u91cc\u9762\u5df2\u4e0d \u518d\u5e7d\u6697\u3002 \u3000\u3000\u521a\u8fdb\u5165\u96a7\u9053\u4e0d\u8fdc\uff0c\u53f3\u8fb9\u6d1e\u58c1\u4fbf\u6709\u4e00\u95f4\u77f3\u5ba4\uff0c\u77f3\u5ba4\u95e8\u5916\u7ad9\u7740\u4e00\u540d\u767d\u8863\u8001\u8005\u3002 \u3000\u3000\u8fd9\u767d\u8863\u8001\u8005\u548c\u5148\u524d\u8c37\u53e3\u90a3\u540d\u9ed1\u8863\u8001\u8005\u5f62\u50cf\u5b8c\u5168\u4e0d\u540c\uff0c\u6148\u7709\u5584\u76ee\uff0c\u4e00\u526f\u548c\u972d\u53ef\u4eb2\u6a21\u6837\u3002 \u3000\u3000\u767d\u8863\u8001\u8005\u671b\u7740\u56db\u4eba\u62f1\u4e86\u62f1\u624b\uff0c\u62db\u547c\u7740\u9053\uff1a\u201c\u56db\u4f4d\u8bf7\u5230\u91cc\u9762\u6765\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6211\u4eec\u6b63\u8981\u8d76\u8def\uff0c\u8001\u5148\u751f\u4e3a\u4ec0\u4e48\u53c8\u8981\u6211\u4eec\u5230\u91cc\u9762\u53bb\uff0c \u3000\u3000\u4f60\u4e0d\u770b\u5929\u90fd\u5feb\u9ed1\u4e86\uff01\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u7b11\u5bb9\u53ef\u63ac\u7684\u9053\uff1a\u201c\u8bf8\u4f4d\u5728\u524d\u9762\u8fc7\u4e86\u2018\u89c2\u5149\u96a7\u9053\u2019\u4e00\u5173\uff0c\u73b0\u5728\u8fd9\u91cc\u53c8\u662f\u4e00\u9053\u5173\u53e3\uff0c \u4e0d\u8fc7\u8fd9\u4e00\u5173\uff0c\u5c31\u4e0d\u53ef\u80fd\u518d\u5f80\u91cc\u8d70\u3002\u201d \u3000\u3000\u4e01\u6d9b\u7ffb\u7740\u773c\u73e0\u95ee\u9053\uff1a\u201c\u8fd9\u4e00\u5173\u8981\u591a\u5c11\u94b1\uff1f\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u5173\u4e0d\u5fc5\u82b1\u94b1\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4e00\u6123\u9053\uff1a\u201c\u4e0d\u8981\u94f6\u5b50\u8fd8\u6709\u4ec0\u4e48\u770b\u5934\uff01\u201d \u3000\u3000\u767d\u8863\u8001\u8005\uff0c\u7b11\u5f97\u8fde\u773c\u775b\u90fd\u772f\u4e0a\u4e86\uff1a\u201c\u8fd9\u4e00\u5173\u4e0d\u89c2\u5149\uff0c\u5f53\u7136\u6ca1\u4ec0\u4e48\u770b\u5934\u3002\u201d \u3000\u3000\u201c\u6ca1\u770b\u5934\u8981\u6211\u4eec\u8fdb\u53bb\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u8981\u56db\u4f4d\u8fc7\u5173\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u8fd9\u4e00\u5173\u662f\u4ec0\u4e48\u5173\uff1f\u201d \u3000\u3000\u201c\u6587\u5173\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6765\u4e01\u6d9b\u8d8a\u53d1\u6123\u4e86\uff1a\u201c\u6587\u5173\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u4e0d\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u5e94\u8be5\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u597d\u5427\uff0c\u6587\u5173\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u6587\u5173\u662f\u8bb2\u7a76\u6587\u7684\uff0c\u5c31\u662f\u8981\u8003\u8003\u5404\u4f4d\u7684\u6587\u624d\u3002\u201d \u3000\u3000\u4e01\u6d9b\u5f20\u53e3\u7ed3\u820c\u9053\uff1a\u201c\u8001\u5148\u751f\u771f\u4f1a\u5f00\u73a9\u7b11\uff0c\u6211\u548c\u8fd9\u4f4d\u8001\u7518\u8fde\u4e66\u90fd\u6ca1\u5ff5\u8fc7\uff0c\u54ea\u91cc\u6765\u7684\u6587\u624d\uff1f\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u677f\u8d77\u9762\u5b54\u9053\uff1a\u201c\u82e5\u8fc7\u4e0d\u4e86\u6587\u5173\uff0c\u5404\u4f4d\u5c31\u53ea\u6709\u56de\u5934\u8d70\u4e86\uff01\u201d \u3000\u3000\u4e01\u6d9b\u7684\u7751\u53d1\u4e86\u7eff\uff0c\u671b\u7740\u7518\u9716\u9053\uff1a\u201c\u4f60\u770b\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u7518\u9716\u5012\u662f\u4e0d\u592a\u5728\u4e4e\uff1a\u201c\u7ba1\u4ed6\u5462\uff0c\u8bd5\u8bd5\u770b\u518d\u8bf4\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8fde\u4e66\u90fd\u6ca1\u5ff5\u8fc7\uff0c\u8fd8\u8bd5\u7684\u4ec0\u4e48\u8bd5\uff0c\u82e5\u80fd\u8bd5\uff0c\u6211\u65e9\u5c31\u53bb\u8003\u72b6\u5143\u4e86\u3002\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u6ca1\u5ff5\u4e66\u4e5f\u4e0d\u89c1\u5f97\u5c31\u662f\u6ca1\u5b66\u95ee\uff0c\u6709\u53e5\u8bdd\u8bf4\uff1a\u3000\u2018\u884c\u4e07\u91cc\u8def\uff0c\u80dc\u8bfb\u4e07\u5377\u4e66\u2019\uff0c\u54b1 \u4eec\u4e24\u4e2a\u8d70\u7684\u8def\u4f55\u6b62\u4e07\u91cc\uff0c\u4f55\u51b5\u54b1\u4eec\u4e5f\u66fe\u548c\u6709\u5b66\u95ee\u7684\u4eba\u5728\u4e00\u8d77\u8fc7\uff0c\u5b9e\u5728\u4e0d\u4e00\u5b9a\u4f1a\u88ab\u4ed6\u4eec\u8003\u5012\u3002\u201d \u3000\u3000\u4e01\u6d9b\u7ec8\u4e8e\u88ab\u8bf4\u52a8\u4e86\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u597d\u5427\uff0c\u5c31\u8bd5\u8bd5\u770b\uff01\u201d \u3000\u3000\u8f6c\u5934\u518d\u5411\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u8001\u5148\u751f\u8bf7\u522b\u6545\u610f\u4e3a\u96be\uff0c\u5f85\u4f1a\u513f\u51fa\u9898\u5343\u4e07\u8981\u51fa\u7b80\u5355\u4e9b\uff01\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u5f85\u4f1a\u513f\u6211\u4f1a\u4ea4\u5f85\u4e3b\u8003\u5b98\uff0c\u5c3d\u91cf\u5e2e\u4f60\u4eec\u7684\u5fd9\u3002\u201d \u3000\u3000\u4e01\u6d9b\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u539f\u6765\u4e3b\u8003\u5b98\u4e0d\u662f\u4f60\uff1f\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u4e3b\u8003\u5b98\u5c31\u5728\u91cc\u9762\uff0c\u5927\u5bb6\u8bf7\u8fdb\u6765\u5427\uff01\u201d \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c \u56db \u56de\u3000\u5404\u663e\u5176\u957f \u3000\u3000\u8fd9\u95f4\u77f3\u5ba4\u9887\u5927\uff0c\u5e03\u7f6e\u5f97\u4e5f\u5f88\u96c5\u81f4\uff0c\u56db\u9762\u58c1\u95f4\u90fd\u6302\u7740\u540d\u4eba\u5b57\u753b\uff0c\u5f88\u6709\u4e9b\u4e66\u9999\u6c14\u606f\u3002 \u3000\u3000\u9760\u91cc\u8fb9\u653e\u7740\u4e00\u5f20\u684c\u5b50\uff0c\u684c\u4e0a\u653e\u7f6e\u6587\u623f\u56db\u5b9d\uff0c\u53e6\u6709\u51e0\u672c\u4e66\u548c\u4e00\u53e0\u5377\u5b97\u3002 \u3000\u3000\u4e3b\u8003\u5b98\u5c31\u5750\u5728\u684c\u5b50\u540e\u9762\u3002 \u3000\u3000\u4e3b\u8003\u5b98\u4e5f\u662f\u4e2a\u8001\u5934\u513f\uff0c\u6234\u7740\u7ea2\u9876\u74dc\u76ae\u5c0f\u5e3d\uff0c\u9f3b\u6881\u4e0a\u67b6\u7740\u4e00\u526f\u8001\u82b1\u773c\u955c\uff0c\u9f3b\u5934\u7ea2\u7ea2\u7684\u7279\u522b \u5927\uff0c\u9894\u4e0b\u4e5f\u662f\u7559\u7740\u4e00\u64ae\u5c71\u7f8a\u80e1\u5b50\uff0c\u8eab\u7a7f\u84dd\u8272\u957f\u888d\uff0c\u5916\u7f69\u9ed1\u7f0e\u9a6c\u8902\uff0c\u811a\u4e0b\u662f\u7c89\u5e95\u7070\u5e2e\u5462\u978b\u3002 \u3000\u3000\u8fd9\u79cd\u6253\u635e\uff0c\u5f88\u50cf\u620f\u5267\u4e2d\u201c\u6625\u9999\u95f9\u5b66\u201d\u91cc\u7684\u90a3\u4f4d\u8001\u5b66\u7a76\u587e\u5e08\uff0c\u53ea\u662f\u4ed6\u624b\u91cc\u8fd8\u62ff\u7740\u4e00\u6839\u957f\u6746 \u65f1\u70df\u888b\u3002 \u3000\u3000\u56db\u4eba\u8fdb\u6765\u7ad9\u5b9a\u540e\uff0c\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u6768\u5144\uff0c\u4ed6\u4eec\u56db\u4f4d\u662f\u6765\u5e94\u8bd5\u8fc7\u5173\u7684\uff0c\u4f60\u5c31\u51fa\u9898\u5427\uff01\u201d \u3000\u3000\u4e0d\u7b49\u4e3b\u8003\u5b98\u6768\u8001\u5934\u5f00\u53e3\uff0c\u4e01\u6d9b\u4fbf\u62a2\u7740\u4e0d\u4f4f\u5411\u767d\u8863\u8001\u8005\u4f7f\u773c\u8272\u3002 \u3000\u3000\u767d\u8863\u8001\u8005\u4f1a\u610f\uff0c\u8fde\u5fd9\u518d\u9053\uff1a\u201c\u6768\u5144\uff0c\u4ed6\u4eec\u4e24\u4f4d\u5e74\u7eaa\u5927\u4e9b\u7684\u53ef\u80fd\u6ca1\u8bfb\u8fc7\u4ec0\u4e48\u4e66\uff0c\u6700\u597d\u51fa\u9898 \u7b80\u5355\u4e9b\uff0c\u522b\u4e3a\u96be\u4ed6\u4eec\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u8f7b\u63c9\u7740\u70df\u888b\u9505\u91cc\u7684\u70df\u4e1d\uff0c\u70b9\u4e0a\u706b\uff0c\u5438\u4e86\u4e00\u53e3\uff0c\u5410\u51fa\u70df\u96fe\uff0c\u624d\u6162\u6761\u65af\u7406\u7684\u8bf4\uff1a \u201c\u5f53\u7136\u53ef\u4ee5\uff0c\u8001\u673d\u4e0d\u903c\u4ed6\u4eec\u505a\u6587\u7ae0\uff0c\u51fa\u51e0\u4e2a\u5b57\u8ba9\u4ed6\u4eec\u5bf9\u5bf9\u5c31\u6210\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4e3a\u4e86\u5148\u8fc7\u5173\uff0c\u62a2\u524d\u4e00\u6b65\u9053\uff1a\u201c\u90a3\u5c31\u8ba9\u6211\u5148\u6765\u5427\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u900f\u8fc7\u955c\u7247\u77a5\u4e86\u4e01\u6d9b\u4e00\u773c\u9053\uff1a\u201c\u4f60\u4f1a\u5bf9\u5bf9\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4f60\u5148\u6559\u4e00\u6559\u6211\uff0c\u6211\u4e00\u5b66\u5c31\u4f1a\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u9053\uff1b\u201c\u53ef\u4ee5\uff0c\u5929\u5bf9\u5730\uff0c\u96e8\u5c31\u98ce\uff0c\u5927\u9646\u5bf9\u957f\u7a7a\uff0c\u61c2\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u8f6c\u7ffb\u7740\u773c\u73e0\u9053\uff1a\u201c\u8bf7\u518d\u6253\u4e2a\u6bd4\u65b9\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u4f3c\u662f\u9887\u6709\u8010\u6027\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8b6c\u5982\u6211\u8bf4\u5de6\uff0c\u4f60\u5c31\u5bf9\u53f3\uff1b\u6211\u8bf4\u4e1c\uff0c\u4f60\u5c31\u5bf9\u897f\uff1b\u6211 \u8bf4\u4e0a\uff0c\u4f60\u5c31\u5bf9\u4e0b\uff1b\u6211\u8bf4\u72d7\uff0c\u4f60\u5c31\u5bf9\u732b\uff1b\u6211\u8bf4\u4e4c\u9e26\uff0c\u4f60\u5c31\u5bf9\u9ebb\u96c0\uff1b\u6211\u8bf4\u9f99\uff0c\u4f60\u5c31\u5bf9\u51e4\uff1b\u6211\u8bf4\u5403 \u996d\uff0c\u4f60\u5c31\u5bf9\u559d\u6c34\uff1b\u6211\u8bf4\u9ed1\uff0c\u4f60\u5c31\u65f6\u767d\uff1b\u6211\u8bf4\u6349\u9e1f\uff0c\u4f60\u5c31\u5bf9\u6355\u9c7c\uff1b\u6211\u8bf4\u8f66\uff0c\u4f60\u5c31\u5bf9\u8239\uff0c\u6211\u8bf4\u5f88 \u5927\uff0c\u4f60\u5c31\u5bf9\u6781\u5c0f\uff1b\u6211\u8bf4\u5feb\uff0c\u4f60\u5c31\u5bf9\u6162\uff1b\u6211\u8bf4\u5438\u70df\uff0c\u4f60\u5c31\u5bf9\u559d\u9152\uff1b\u6211\u8bf4\u9ad8\u5174\uff0c\u4f60\u5c31\u5bf9\u82e6\u607c\uff1b\u6211 \u8bf4\u6d47\u6c34\uff0c\u4f60\u5c31\u5bf9\u70b9\u706b\u3002\u8001\u673d\u6bd4\u65b9\u7684\u591f\u591a\u4e86\uff0c\u542c\u660e\u767d\u6ca1\u6709\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u5658\u5634\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u53ea\u9053\u5bf9\u5bf9\u5b50\u5f88\u96be\uff0c\u539f\u6765\u8fd9\u4e48\u7b80\u5355\u5bb9\u6613\uff0c\u8001\u5148\u751f\uff0c\u73b0\u5728\u5c31\u5f00\u59cb\uff0c\u4f60 \u8bf7\u51fa\u9898\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u6211\u51fa\u4e2a\u53e3\u5b57\uff0c\u4f60\u5bf9\uff01\u201d \u3000\u3000\u4e01\u6d9b\u54a7\u4e86\u54a7\u5634\u9053\uff1a\u201c\u53e3\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u4e5f\u4e24\u773c\u76f4\u7728\u7684\u9053\uff1a\u201c\u4f60\u8fde\u8fd9\u4e48\u7b80\u5355\u7684\u4e00\u4e2a\u5b57\u90fd\u4e0d\u61c2\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u90a3\u8fd8\u5bf9\u7684\u4ec0\u4e48 \u5bf9\uff0c\u53e3\u5c31\u662f\u5634\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u8be5\u76f4\u63a5\u4e86\u5f53\u7684\u8bf4\u5634\uff0c\u4e0d\u8be5\u8bf4\u53e3\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u9053\uff1a\u201c\u53e3\u548c\u5634\u672c\u6765\u5c31\u662f\u4e00\u4e2a\u610f\u601d\uff0c\u53ea\u602a\u4f60\u6ca1\u6709\u8bfb\u8fc7\u4e66\uff0c\u7adf\u8fde\u53e3\u7684\u610f\u601d\u90fd\u4e0d\u61c2\uff01\u201d \u3000\u3000\u4e01\u6d9b\u5927\u4e0d\u540c\u610f\u7684\u9053\uff1a\u201c\u4f60\u7684\u8bdd\u6211\u4e0d\u670d\u6c14\uff0c\u6211\u8bf4\u53e3\u548c\u5634\u610f\u601d\u5b8c\u5168\u4e0d\u4e00\u6837\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u8010\u7740\u6027\u5b50\u9053\uff1a\u201c\u4f60\u8bf4\u51fa\u9053\u7406\u8001\u673d\u542c\u542c\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u54b1\u4eec\u4e2d\u56fd\u6709\u5f88\u591a\u5730\u540d\u90fd\u5e26\u4e2a\u53e3\u5b57\uff0c\u8fd9\u4e9b\u5730\u65b9\u6211\u4e5f\u5230\u8fc7\uff0c\u50cf\u5f20\u5bb6\u53e3\u3001\u53e4\u5317\u53e3\u3001 \u559c\u5cf0\u53e3\u3001\u5927\u6cbd\u53e3\u3002\u5982\u679c\u628a\u8fd9\u4e9b\u5730\u65b9\u8bf4\u6210\u5f20\u5bb6\u5634\u3001\u53e4\u5317\u5634\u3001\u559c\u5cf0\u5634\u3001\u5927\u6cbd\u5634\u3001\u8bf7\u95ee\u53ef\u4ee5\u4e0d\u53ef \u4ee5\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u4e07\u6ca1\u6599\u5230\u4ed6\u7adf\u6709\u8fd9\u4e48\u4e00\u756a\u6b6a\u7406\uff0c\u4f46\u6b6a\u7406\u4e5f\u662f\u7406\uff0c\u60f3\u628a\u4ed6\u9a73\u5012\uff0c\u8fd8\u771f\u4e0d\u5bb9\u6613\uff0c\u53ea \u597d\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u4e48\u8001\u673d\u5c31\u628a\u53e3\u6539\u6210\u5634\uff0c\u4f60\u5bf9\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4e0d\u5047\u601d\u7d22\u9053\uff1a\u201c\u5c41\u80a1\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u5927\u611f\u4e00\u6123\uff0c\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u5634\u600e\u4e48\u80fd\u5bf9\u4e0a\u5c41\u80a1\uff1f\u4f55\u51b5\u6211\u51fa\u7684\u9898\u662f\u4e00\u4e2a\u5b57\uff0c\u4f60 \u5bf9\u7684\u5374\u662f\u4e24\u4e2a\u5b57\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8001\u5148\u751f\u522b\u53d1\u7765\u6c14\uff0c\u4f60\u521a\u624d\u6253\u6bd4\u65b9\u65f6\u5c31\u8bf4\u8fc7\u4e0a\u5c31\u8be5\u5bf9\u4e0b\uff0c\u5634\u5728\u4e0a\uff0c\u5c41\u80a1\u5728\u4e0b\u9762\uff0c \u6211\u662f\u5b8c\u5168\u8ddf\u4f60\u5b66\u7684\uff0c\u600e\u4e48\u53c8\u4e0d\u5bf9\u4e86\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u5dee\u70b9\u6ca1\u88ab\u4e00\u53e3\u70df\u545b\u4f4f\uff0c\u6446\u7740\u624b\u9053\uff1a\u201c\u7b97\u4e86\u7b97\u4e86\uff0c\u7b80\u76f4\u662f\u673d\u6728\u4e2a\u53ef\u96d5\u4e5f\uff01\u7caa\u4e0a\u4e4b\u5899 \u4e0d\u53ef\u6c61\u4e5f\uff01\u201d \u3000\u3000\u4e01\u6d9b\u867d\u542c\u4e0d\u61c2\u6768\u8001\u5934\u513f\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u4f46\u5374\u89c9\u5f97\u51fa\u5fc5\u5b9a\u4e0d\u662f\u597d\u8bdd\uff0c \u3000\u3000\u5fd9\u9053\uff1a\u201c\u8001\u5148\u751f\uff0c\u4f60\u82e5\u4e0d\u540c\u610f\u3002\u5c31\u7531\u6211\u51fa\u4e2a\u9898\u8bf7\u4f60\u5bf9\u600e\u6837\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u8138\u8272\u4e00\u6574\u9053\uff1a\u201c\u8001\u673d\u662f\u4e3b\u8003\u5b98\uff0c\u4f60\u662f\u5b66\u751f\uff0c\u54ea\u6709\u5b66\u751f\u51fa\u9898\u8003\u8001\u5e08\u7684\u4e86\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8fd9\u6837\u770b\u6765\uff0e\u4f60\u7684\u5b66\u95ee\u5fc5\u5b9a\u4e5f\u4e0d\u600e\u4e48\u6837\uff0c\u8981\u4e0d\u7136\u8fd8\u6015\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u679c\u771f\u88ab\u4e01\u6d9b\u6fc0\u51fa\u4e86\u771f\u706b\uff0c\u5802\u5802\u4e3b\u8003\u5b98\uff0c\u6ee1\u8179\u7ecf\u4f26\uff0c\u5c82\u80fd\u5728\u5b66\u751f\u9762\u524d\u51fa\u4e11\uff0c\u4e0d\u7531 \u4e00\u62cd\u684c\u5b50\u9053\uff1a\u201c\u597d\uff0c\u8001\u673d\u5c31\u7531\u4f60\u51fa\u9898\uff01\u201d \u3000\u3000\u4e01\u6d9b\u60f3\u90fd\u6ca1\u60f3\uff0c\u9053\uff1a\u201c\u809a\u8110\u773c\uff01\u8001\u5148\u751f\u8bf7\u5bf9\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u7709\u5934\u4e00\u76b1\uff0c\u6697\u81ea\u9a82\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u771f\u6ca1\u51fa\u606f\uff0c\u7adf\u7136\u51fa\u8fd9\u79cd\u9898\u76ee\uff0c\u53ef\u8c13\u6df7\u5e10\u900f\u9876\uff01\u201d \u3000\u3000\u4ed6\u867d\u5728\u6697\u9a82\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u8fd8\u771f\u60f3\u4e0d\u51fa\u8be5\u7528\u90a3\u4e09\u4e2a\u5b57\u6765\u5bf9\uff0c\u6ca1\u5948\u4f55\uff0c\u53ea\u597d\u4ee5\u731b\u5438\u65f1\u70df\u6765\u6362 \u53d6\u601d\u8003\u65f6\u95f4\u3002 \u3000\u3000\u8db3\u8db3\u534a\u76cf\u70ed\u8336\u5de5\u592b\u8fc7\u53bb\uff0c\u5f53\u771f\u628a\u6768\u8001\u5934\u513f\u96be\u5012\u4e86\u3002 \u3000\u3000\u4e01\u6d9b\u65e9\u770b\u51fa\u6768\u8001\u5934\u513f\u5341\u5206\u5c34\u5c2c\uff0c\u4e0d\u89c9\u563f\u563f\u7b11\u9053\uff1a\u201c\u8001\u5148\u751f\uff0c\u600e\u4e48\u6837\uff1f\u4f60\u7684\u5b66\u95ee\u867d\u7136\u5927\uff0c \u7167\u6837\u4e5f\u6709\u5403\u762a\u7684\u65f6\u5019\uff0c\u5982\u679c\u771f\u5bf9\u4e0d\u4e0a\uff0c\u4e0d\u5982\u7531\u6211\u6765\u5bf9\u5427\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u55b7\u4e86\u4e00\u53e3\u70df\u96fe\u9053\uff1a\u201c\u4f60\u5bf9\uff01\u8001\u673d\u4e0d\u4fe1\u4f60\u80fd\u5bf9\u4e0a\uff01\u201d \u3000\u3000\u4e01\u6d9b\u7b11\u9053\uff1a\u201c\u4e5f\u662f\u809a\u8110\u773c\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u543c\u53eb\u9053\uff1a\u201c\u8d8a\u53d1\u80e1\u8bf4\uff0c\u809a\u8110\u773c\u600e\u4e48\u80fd\u5bf9\u809a\u8110\u773c\uff1f\u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u4e01\u6d9b\u4e0d\u614c\u4e0d\u5fd9\u9053\uff1a\u201c\u8001\u5148\u751f\u8bf7\u6d88\u6d88\u6c14\uff0c\u6211\u65e2\u7136\u80fd\u5bf9\u4e0a\uff0c\u5f53\u7136\u6709\u7406\u7531\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u7406\u7531\uff1f\u4f60\u8bb2\uff01\u201d \u3000\u3000\u201c\u597d\u6bd4\u4e00\u7537\u4e00\u5973\u4e0a\u4e86\u5e8a\uff0c\u4e0d\u662f\u809a\u8110\u773c\u5bf9\u809a\u8110\u773c\u662f\u4ec0\u4e48\uff1f\u6211\u8fd9\u6837\u5bf9\uff0c\u4e0d\u4f46\u5bf9\u5f97\u597d\uff0c\u800c\u4e14\u8fd8 \u51c6\u5f97\u5f88\u5462\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u5dee\u70b9\u53c8\u88ab\u4e00\u53e3\u70df\u545b\u6b7b\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u6eda\u5f00\uff0c\u8001\u673d\u4e0d\u60f3\u518d\u8003\u4f60\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u4e0d\u4f46\u7518\u9716\u7b49\u4eba\u90fd\u5dee\u70b9\u7b11\u51fa\u58f0\u6765\uff0c\u8fde\u767d\u8863\u8001\u8005\u4e5f\u5fcd\u4fca\u4e0d\u7981\u3002 \u3000\u3000\u4e01\u6d9b\u8fd8\u771f\u542c\u8bdd\uff0c\u5658\u5634\u7b11\u7740\u9000\u56de\u4e00\u65c1\u3002 \u3000\u3000\u6768\u8001\u5934\u513f\u76f4\u5438\u4e86\u4e00\u888b\u70df\uff0c\u628a\u6c14\u6d88\u5f97\u5dee\u4e0d\u591a\u4e86\uff0c\u624d\u5411\u7518\u9716\u62db\u62db\u624b\u9053\uff1a\u201c\u73b0\u5728\u8be5\u8003\u4f60\u4e86\uff0c\u7ad9 \u8fc7\u6765\uff01\u201d \u3000\u3000\u7518\u9716\u4f9d\u8a00\u5411\u524d\u8d70\u4e86\u51e0\u6b65\uff0c\u8eac\u8eab\u65bd\u4e86\u4e00\u793c\u9053\uff1a\u201c\u5927\u4e3b\u8003\u8fd9\u6b21\u8981\u51fa\u4ec0\u4e48\u7c7b\u9898\u76ee\uff1f\u201d \u3000\u3000\u4ed6\u867d\u7136\u591a\u5e74\u6765\u548c\u4e01\u6d9b\u671d\u5915\u4e0d\u79bb\uff0c\u4f46\u6027\u60c5\u5e76\u4e0d\u76f8\u540c\uff0c\u540c\u65f6\u4ed6\u4e5f\u5ff5\u8fc7\u51e0\u5e74\u4e66\uff0c\u5934\u8111\u6bd4\u8f83\u51b7\u9759\uff0c \u5f85\u4eba\u65b9\u9762\uff0c\u793c\u8c8c\u4e5f\u6bd4\u4e01\u6d9b\u5468\u5230\u4e9b\u3002 \u3000\u3000\u6768\u8001\u5934\u513f\u89c1\u7518\u9716\u4e3e\u6b62\u548c\u8a00\u8c08\u90fd\u4e2d\u89c4\u4e2d\u77e9\uff0c\u5c24\u5176\u628a\u81ea\u5df1\u79f0\u547c\u5927\u4e3b\u8003\uff0c\u66f4\u662f\u9ad8\u5174\uff0c\u4e0d\u89c9\u6478\u7740 \u5c71\u7f8a\u80e1\u5b50\u5475\u5475\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u5fc5\u505a\u6587\u7ae0\uff0c\u5bf9\u51e0\u4e2a\u5bf9\u5b50\u5c31\u53ef\u4ee5\u8fc7\u5173\u3002\u201d \u3000\u3000\u7518\u9716\u8eac\u8eab\u53c8\u662f\u4e00\u793c\u9053\uff1a\u201c\u5b66\u751f\u4e66\u8bfb\u7684\u4e0d\u591a\uff0c\u5927\u4e3b\u8003\u53ef\u5426\u6307\u5b9a\u4e00\u4e2a\u8303\u56f4\uff0c\u7ed9\u5b66\u751f\u4e00\u70b9\u65b9 \u4fbf\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u56e0\u4e3a\u5fc3\u91cc\u9ad8\u5174\uff0c\u8d8a\u53d1\u4e0d\u60f3\u4e3a\u96be\u7518\u9716\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4f60\u7684\u8981\u6c42\u6211\u7b54\u5e94\uff0c\u5c31\u7531\u4f60\u9650 \u5b9a\u8303\u56f4\u597d\u4e86\u3002\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u5b66\u751f\u4e66\u8bfb\u7684\u867d\u7136\u4e0d\u591a\uff1f\u4f46\u8def\u5374\u8d70\u5f97\u4e0d\u5c11\uff0c\u5927\u4e3b\u8003\u6700\u597d\u51fa\u5730\u540d\u7ed9\u5b66\u751f\u5bf9\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u9053\uff1a\u201c\u53ef\u4ee5\uff0c\u884c\u4e07\u91cc\u8def\u80dc\u8bfb\u4e07\u5377\u4e66\uff0c\u770b\u8d77\u6765\u4f60\u7684\u5b66\u95ee\u5df2\u7ecf\u4e0d\u5c0f\u4e86\u3002\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u5b66\u751f\u8fd8\u6709\u4e00\u4e2a\u8981\u6c42\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u9053\uff1a\u201c\u8fd8\u8981\u6c42\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u54b1\u4eec\u4e2d\u56fd\u7684\u5730\u65b9\u592a\u5927\u4e86\uff0c\u5730\u540d\u5343\u5343\u4e07\u4e07\uff0c\u5b66\u751f\u4e0d\u77e5\u9053\u7684\u4e00\u5b9a\u8fd8\u5f88\u591a\uff0c\u662f\u5426\u53ef \u4ee5\u628a\u8303\u56f4\u518d\u7f29\u5c0f\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u987a\u6c34\u63a8\u821f\u9053\uff1a\u201c\u4e5f\u53ef\u4ee5\uff0c\u8fd8\u662f\u7531\u4f60\u81ea\u5df1\u6307\u5b9a\u597d\u4e86\u3002\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u5b66\u751f\u5c0f\u65f6\u5019\u5728\u53f0\u6e7e\u4f4f\u8fc7\uff0c\u5bf9\u90a3\u91cc\u7684\u5730\u540d\u6bd4\u8f83\u719f\u6089\uff0c\u5c31\u4ee5\u53f0\u6e7e\u7684\u5730\u540d\u4e3a\u8303\u56f4\u600e \u6837\uff1f\u201d \u3000\u3000\u53ea\u89c1\u6768\u8001\u5934\u513f\u4e24\u773c\u76f4\u7728\u7684\u60ca\u559c\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u4e5f\u4f4f\u8fc7\u53f0\u6e7e\uff1f\u90a3\u592a\u597d\u4e86\uff0c\u6b63\u597d\u8001\u673d\u4e5f\u5728\u53f0 \u6e7e\u4f4f\u8fc7\u51e0\u5e74\u3002\u201d \u3000\u3000\u7518\u9716\u4e0d\u89c9\u559c\u51fa\u671b\u5916\uff0c\u4ed6\u539f\u5148\u53ea\u9053\u8bf4\u4e86\u4e5f\u662f\u767d\u8bf4\uff0c\u56e0\u4e3a\u5bf9\u65b9\u82e5\u4e0d\u77e5\u9053\u53f0\u6e7e\u5730\u540d\uff0c\u53c8\u600e\u80fd\u4ee5 \u53f0\u6e7e\u5730\u540d\u51fa\u9898\u5462\uff1f \u3000\u3000\u4ed6\u8fd9\u4e00\u9ad8\u5174\uff0c\u8fde\u6b7b\u9c7c\u773c\u4e5f\u50cf\u6d3b\u4e86\u8d77\u6765\uff1a\u201c\u5927\u4e3b\u8003\u5c31\u8bf7\u51fa\u9898\u597d\u4e86\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u513f\u5df4\u5527\u5df4\u5527\u7684\u5438\u4e86\u51e0\u53e3\u70df\u9053\uff1a\u201c\u8001\u673d\u5728\u53f0\u6e7e\uff0c\u90a3\u662f\u51e0\u5341\u5e74\u4ee5\u524d\u7684\u4e8b\u4e86\uff0c\u5bf9\u90a3\u91cc\u7684 \u5730\u540d\uff0c\u5fc5\u987b\u73b0\u5728\u597d\u597d\u60f3\u4e00\u60f3\u3002\u201d \u3000\u3000\u4e0d\u8fc7\u4ed6\u5f88\u5feb\u5c31\u60f3\u8d77\u6765\u4e86\uff1a\u201c\u542c\u7740\uff0c\u8001\u673d\u73b0\u5728\u5c31\u51fa\u9898\u4e86\uff01\u201d \u3000\u3000\u7518\u9716\u5782\u624b\u8eac\u8eab\u9053\uff1a\u201c\u5927\u4e3b\u8003\u8bf7\u8bb2\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u65b0\u5e84\uff0c\u5bf9\uff01\u201d \u3000\u3000\u7518\u9716\u60f3\u90fd\u6ca1\u60f3\uff1a\u201c\u65e7\u9986\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u677f\u6865\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u6728\u6805\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u9e6d\u9e1f\u6f6d\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u8759\u8760\u6d1e\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u9f99\u6f6d\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u51e4\u5c71\u3002\u201d\uff0e \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u6c34\u91cc\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u571f\u57ce\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u672c\u6765\u6253\u7b97\u95ee\u4e2a\u4e09\u3001\u4e24\u9898\u5c31\u7b97\u4e86\uff0c\u56e0\u89c1\u7518\u9716\u4e0d\u4f46\u5bf9\u7b54\u5982\u6d41\uff0c\u800c\u4e14\u5730\u540d\u548c\u5730\u540d\u5bf9\u5f97\u975e \u5e38\u5de5\u6574\u8d34\u5207\uff0c\u5fc3\u91cc\u4e00\u9ad8\u5174\uff0c\u9898\u76ee\u5c31\u8d8a\u51fa\u8d8a\u591a\u3002 \u3000\u3000\u53e6\u5916\uff0c\u56de\u5fc6\u8d77\u5f53\u5e74\u5728\u53f0\u6e7e\u7684\u90a3\u6bb5\u7ecf\u8fc7\uff0c\u5982\u4eca\u4ee5\u5730\u540d\u5bf9\uff0c\u4e5f\u9887\u6709\u65e7\u5730\u91cd\u6e38\u4e4b\u611f\uff0c\u5c24\u5176\u9762\u5bf9 \u4e5f\u5230\u8fc7\u53f0\u6e7e\u7684\u7518\u9716\uff0c\u7b80\u76f4\u50cf\u4ed6\u4e61\u9047\u6545\u77e5\u4e00\u822c\u3002 \u3000\u3000\u5374\u56e0\u4ed6\u79bb\u5f00\u53f0\u6e7e\u592a\u4e45\uff0c\u4e00\u65f6\u4e4b\u95f4\u60f3\u4e0d\u8d77\u6765\u592a\u591a\u5730\u540d\uff0c\u53ea\u597d\u6682\u65f6\u6253\u4f4f\u3002 \u3000\u3000\u7518\u9716\u4e5f\u9887\u5f97\u610f\uff0c\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u5927\u4e3b\u8003\u662f\u4e0d\u662f\u5df2\u7ecf\u8003\u597d\u4e86\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u6162\u541e\u541e\u7684\u9053\uff1a\u201c\u7b49\u8001\u673d\u60f3\u60f3\u518d\u8003\u4f60\u51e0\u9898\u3002\u201d \u3000\u3000\u6ca1\u9694\u591a\u4e45\uff0c\u4ed6\u5c31\u53c8\u5f00\u59cb\u51fa\u9898\uff1a\u201c\u82b1\u575b\uff0c\u5bf9\uff01\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u8349\u5c6f\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u89c2\u97f3\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u5f25\u9640\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u94dc\u9523\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u94bb\u77f3\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u4e09\u4e49\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u516b\u5fb7\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u6885\u5c71\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u7af9\u5d0e\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u4e09\u91cd\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u4e94\u7ed3\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u4e94\u80a1\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u516b\u5835\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u96fe\u5cf0\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u6fb3\u5e95\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u5927\u6eaa\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u5c0f\u6e2f\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u9999\u5c71\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u6d4a\u6c34\u3002\u201d \u3000\u3000\u6768\u8001\u53c8\u9053\uff1a\u201c\u5c16\u77f3\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u5706\u5c71\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u65b0\u5c4b\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u65e7\u793e\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u540e\u91cc\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u524d\u9547\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u9e7f\u8c37\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u9f9f\u5c71\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u9f99\u4e95\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u9c7c\u6c60\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u793e\u5934\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u7530\u5c3e\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u4e8c\u6c34\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u4e09\u6e7e\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u7af9\u5c71\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u6885\u6797\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u6c34\u4e0a\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u7530\u4e2d\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u9ebb\u56ed\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u83dc\u5bee\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u6843\u56ed\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u67f3\u8425\u3002\u201d \u3000\u3000\u6768\u8001\u4e08\u9053\uff1a\u201c\u5357\u5dde\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u5317\u6e2f\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u897f\u6e56\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u4e1c\u6cb3\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u7389\u91cc\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u77f3\u95e8\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u9ec4\u6ce5\u5858\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u7ea2\u53f6\u6751\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u6210\u529f\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u5f52\u4ec1\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u4e0d\u518d\u63a5\u4e0b\u53bb\u95ee\uff0c\u5374\u76b1\u7709\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u524d\u9762\u5bf9\u5f97\u90fd\u4e0d\u9519\uff0c\u8fd9\u4e00\u9898\u6709\u95ee\u9898\u3002\u201d \u3000\u3000\u7518\u9716\u4f3c\u4e4e\u4e0d\u5927\u670d\u6c14\uff0c\u7ffb\u7740\u6b7b\u9c7c\u773c\u9053\uff1a\u201c\u6210\u529f\u5f53\u7136\u6700\u597d\u5bf9\u5931\u8d25\uff0c\u53ef\u662f\u53f0\u6e7e\u6ca1\u8fd9\u5730\u540d\uff0c\u5b66\u751f \u5c31\u53ea\u6709\u5bf9\u5f52\u4ec1\u4e86\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4f60\u8bf4\u8bf4\u6709\u4ec0\u4e48\u7406\u7531\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u8bb0\u4e0d\u5f97\u662f\u8c01\u8bf4\u8fc7\u4e00\u53e5\u4e0d\u6210\u529f\u4fbf\u6210\u4ec1\u7684\u8bdd\uff0c\u5f52\u4ec1\u5f53\u7136\u5c31\u662f\u6210\u4ec1\uff0c\u8fd9\u6837\u770b\u6765\uff0c\u5f52 \u4ec1\u5bf9\u6210\u529f\u4e0d\u80fd\u8bf4\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u867d\u662f\u5f3a\u8bcd\u593a\u7406\uff0c\u4e5f\u7b97\u8bf4\u5f97\u8fc7\u53bb\uff0c\u8001\u673d\u518d\u95ee\u4f60\u6700\u540e\u4e00\u9898\u3000\uff0c\u5927\u7532\uff0c \u5bf9\uff01\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u9f9f\u5c71\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u4e24\u773c\u4e00\u76f4\u9053\uff1a\u201c\u8d8a\u53d1\u4e0d\u50cf\u8bdd\uff0c\u9f9f\u5c71\u600e\u80fd\u8010\u4e0a\u5927\u7532\uff0c\u7b80\u76f4\u9a74\u5507\u4e0d\u5bf9\u9a6c\u5634\uff01\u201d \u3000\u3000\u7518\u9716\u966a\u7b11\u7740\u9053\uff1a\u201c\u5b66\u751f\u6ca1\u89e3\u91ca\uff0c\u4f60\u8001\u4eba\u5bb6\u5f53\u7136\u4e0d\u660e\u767d\u5176\u4e2d\u9053\u7406\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u518d\u89e3\u91ca\u4e5f\u6ca1\u7528\uff0c\u6839\u672c\u98ce\u9a6c\u725b\u4e0d\u76f8\u53ca\uff01\u201d \u3000\u3000\u201c\u4f60\u8001\u4eba\u5bb6\u8fd8\u6ca1\u542c\u5b66\u751f\u89e3\u91ca\uff0c\u600e\u77e5\u6ca1\u5bf9\u5f97\u597d\uff1f\u201d \u3000\u3000\u201c\u597d\uff0c\u4f60\u5c31\u89e3\u91ca\u89e3\u91ca\uff01\u201d \u3000\u3000\u8c01\u77e5\u7518\u9716\u5374\u4e0d\u7b54\u53cd\u95ee\u9053\uff1a\u201c\u8bf7\u95ee\u5927\u4e3b\u8003\uff0c\u9f9f\u8eab\u4e0a\u6709\u7532\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u5f53\u7136\u6709\u3002\u201d \u3000\u3000\u201c\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u9f9f\u76d6\u5c31\u662f\u7532\uff0c\u4ece\u524d\u7684\u7532\u9aa8\u6587\u5c31\u662f\u7528\u9f9f\u7532\u548c\u517d\u9aa8\u523b\u6210\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u5bf9\u4e86\uff0c\u90a3\u4e4c\u9f9f\u50cf\u5c71\u4e00\u6837\u5927\uff0c\u5b83\u7684\u7532\u600e\u80fd\u4e0d\u5927\uff1f\u9f9f\u5c71\u4e0d\u662f\u5927\u7532\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u756a\u6b6a\u7406\uff0c\u76f4\u628a\u6768\u8001\u5934\u5f04\u5f97\u557c\u7b11\u7686\u975e\uff0c\u534a\u54cd\uff0c\u624d\u6446\u6446\u624b\u9053\uff1a\u201c\u597d\u4e86\u597d\u4e86\uff0c\u524d\u9762\u90a3\u4e9b\u9898\uff0c \u672c\u6765\u662f\u4e00\u767e\u5206\uff0c\u6700\u540e\u4e24\u9898\u6263\u4e8c\u5341\u5206\uff0c\u7b97\u4f60\u516b\u5341\u5206\uff0c\u5df2\u7ecf\u53ca\u683c\uff0c\u8fc7\u4e86\u5173\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u76ee\u5149\u8f6c\u5411\u8881\u5c0f\u9e64\uff0c\u4e0d\u7531\u8bf4\u4e0b\u4e00\u4f4d\u8be5\u662f\u8881\u5c0f\u9e64\u4e86\u3002 \u3000\u3000\u4f46\u4e01\u6d9b\u7684\u52a8\u4f5c\u771f\u591f\u5feb\uff0c\u4e0d\u7b49\u8881\u5c0f\u9e64\u53cd\u5e94\u4fbf\u62a2\u7740\u51b2\u4e86\u51fa\u6765\u3002 \u3000\u3000\u539f\u6765\u5728\u8fd9\u6bb5\u65f6\u95f4\u91cc\uff0c\u4ed6\u5df2\u6253\u597d\u4e86\u4e3b\u610f\uff0c\u51b3\u5b9a\u5b66\u7740\u7518\u9716\u5c3d\u91cf\u5bf9\u6768\u8001\u5934\u793c\u8c8c\u4e00\u4e9b\uff0c\u540c\u65f6\u89c9\u5f97 \u82e5\u8fc7\u4e0d\u4e86\u5173\uff0c\u9762\u5b50\u4e0a\u5b9e\u5728\u4e0d\u597d\u770b\uff0c\u6240\u4ee5\u8bf4\u4ec0\u4e48\u4e5f\u5fc5\u987b\u6df7\u8fc7\u53bb\u3002 \u3000\u3000\u6768\u8001\u5934\u4e24\u773c\u7728\u4e86\u7728\u9053\uff1a\u201c\u4f60\u600e\u4e48\u53c8\u6765\u4e86\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u5fd9\u6bd5\u606d\u6bd5\u656c\u7684\u8eac\u8eab\u4e00\u793c\u9053\uff1a\u201c\u665a\u8f88\u5f53\u7136\u662f\u5e0c\u671b\u8fc7\u5173\uff0c\u82e5\u8fc7\u4e0d\u4e86\u5173\uff0c\u4e22\u4eba\u53ef\u5c31\u4e22\u5927 \u4e86\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1b\u201c\u4f60\u8fd9\u4eba\u72d7\u5c41\u4e0d\u901a\uff0c\u51ed\u4ec0\u4e48\u80fd\u8fc7\u5173\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u53c8\u662f\u6df1\u6df1\u4e00\u793c\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u6837\uff0c\u4f46\u6c42\u5927\u4e3b\u8003\u518d\u8003\u6211\u4e00\u6b21\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u89c1\u4e01\u6d9b\u5ffd\u7136\u53d8\u5f97\u5f6c\u5f6c\u6709\u793c\uff0c\u4e0d\u514d\u5bf9\u4ed6\u6e10\u751f\u597d\u611f\uff0c\u6478\u4e86\u6478\u5634\u5df4\u9053\uff1a\u201c\u770b\u6765\u4f60\u5df2\u4e0d\u662f \u673d\u6728\u548c\u7caa\u571f\u4e4b\u5899\u4e86\uff0c\u65e2\u7136\u5b7a\u5b50\u53ef\u6559\uff0c\u518d\u8003\u8003\u4f60\u4e5f\u6210\uff0c\u4e0d\u8fc7\u82e5\u518d\u4e71\u5bf9\u5bf9\u5b50\uff0c\u7167\u6837\u6ca1\u4ec0\u4e48\u60c5\u9762\u597d \u8bb2\u7684\u3002\u201d \u3000\u3000\u4e01\u6d9b\u54a7\u4e86\u54a7\u5634\u9053\uff1a\u201c\u665a\u8f88\u6ca1\u8bfb\u8fc7\u4e66\uff0c\u54ea\u4f1a\u5bf9\u5bf9\u5b50\uff0c\u521a\u624d\u4e0d\u8fc7\u662f\u778e\u80e1\u95f9\uff0c\u4f60\u8001\u4eba\u5bb6\u5343\u4e07\u4e0d \u8981\u8bb0\u5728\u5fc3\u4e0a\uff0c\u73b0\u5728\u665a\u8f88\u4e5f\u60f3\u63d0\u51fa\u4e00\u4e2a\u8981\u6c42\uff0c\u4f60\u8001\u4eba\u5bb6\u5148\u524d\u66fe\u7b54\u5e94\u7518\u9716\u7684\u8981\u6c42\uff0c\u82e5\u4e0d\u7b54\u5e94\u665a\u8f88 \u7684\u8981\u6c42\uff0c\u90a3\u5c31\u662f\u4e0d\u516c\u4e86\uff0c\u76f8\u4fe1\u4f60\u8001\u4eba\u5bb6\u51b3\u4e0d\u4f1a\u505a\u4e0d\u516c\u5e73\u7684\u4e8b\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u7b49\u4e8e\u5835\u4f4f\u4e86\u6768\u8001\u5934\u7684\u5634\u3002\u6768\u8001\u5934\u82e6\u7b11\u7740\u9053\uff1a\u201c\u597d\u5427\uff0c\u8001\u673d\u4e5f\u7167\u6837\u7b54\u5e94\u4f60\u7684\u8981 \u6c42\uff0c\u4f60\u60f3\u8981\u6c42\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u60f3\u5bf9\u5bf9\u5b50\uff0c\u6073\u6c42\u5927\u4e3b\u8003\u6362\u4e2a\u522b\u7684\u9898\u76ee\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u505a\u6587\u7ae0\uff1f\u201d \u3000\u3000\u201c\u5f00\u73a9\u7b11\u5566\uff0c\u665a\u8f88\u8fde\u5bf9\u5bf9\u5b50\u90fd\u4e0d\u80fd\u5bf9\uff0c\u54ea\u80fd\u505a\u6587\u7ae0\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u505a\u8bd7\u505a\u8bcd\uff01\u201d \u3000\u3000\u201c\u8d8a\u53d1\u5f00\u73a9\u7b11\u5566\u3002\u201d \u3000\u3000\u201c\u4f60\u4ec0\u4e48\u90fd\u4e0d\u4f1a\uff0c\u8ba9\u8001\u673d\u600e\u8003\uff0c\u603b\u4e0d\u80fd\u8ba9\u4f60\u514d\u8bd5\u8fc7\u5173\uff01\u201d \u3000\u3000\u201c\u665a\u8f88\u6709\u4e00\u6837\u4f1a\u7684\u3002\u201d \u3000\u3000\u201c\u4f1a\u4ec0\u4e48\uff1f\u4f60\u8bf4\uff01\u201d \u3000\u3000\u201c\u540a\u574e\u513f\u3002\u201d \u3000\u3000\u8fd9\u201c\u540a\u574e\u513f\u201d\u4e09\u4e2a\u5b57\u628a\u6768\u8001\u5934\u5f04\u5f97\u6ee1\u5934\u96fe\u6c34\uff0c\u76b1\u8d77\u7709\u5934\u9053\uff1a \u3000\u3000\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u8001\u673d\u6839\u672c\u542c\u4e0d\u61c2\uff01\u201d \u3000\u3000\u7ad9\u5728\u4e00\u65c1\u7684\u767d\u8863\u8001\u8005\u5fd9\u642d\u8154\u9053\uff1a\u201c\u6768\u5144\uff0c\u4ed6\u8bf4\u7684\u662f\u53e5\u65b9\u8a00\uff0c\u6211\u61c2\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u671b\u5411\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u4f60\u8bf7\u8bf4\u8bf4\u770b\uff01\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u540a\u574e\u513f\u5c31\u662f\u4fd7\u6d3b\u8bf4\u7684\u6b47\u540e\u8bed\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4e48\u56de\u4e8b\u3002\u201d \u3000\u3000\u63a5\u7740\u89c6\u7ebf\u53c8\u8f6c\u5411\u4e01\u6d9b\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u8001\u673d\u8bf4\u51fa\u6b47\u540e\u8bed\u7684\u4e0a\u4e00\u53e5\uff0c\u4f60\u5c31\u63a5\u4e0b\u4e00\u53e5\uff0c\u7b49\u4e8e\u6211 \u51fa\u8ff7\u9898\u4f60\u731c\u8ff7\u5e95\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u7b11\u7740\u54a7\u51fa\u4e24\u9897\u5927\u677f\u7259\uff0c\u4e0d\u4f4f\u70b9\u5934\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u665a\u8f88\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u5475\u5475\u7b11\u9053\uff1a\u201c\u8fd9\u79cd\u8003\u8bd5\u65b9\u6cd5\u8001\u673d\u8fd8\u662f\u7b2c\u4e00\u6b21\u7528\uff0c\u4e0d\u8fc7\u7684\u786e\u5f88\u6709\u610f\u601d\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u5c31\u8bf7\u5927\u4e3b\u8003\u73b0\u5728\u5f00\u59cb\u597d\u4e0d\u597d\uff01\u201d \u3000\u3000\u6768\u8005\u5934\u9053\uff1a\u201c\u5f53\u7136\u5c31\u8981\u73b0\u5728\u5f00\u59cb\uff0c\u4f60\u542c\u7740\uff1a\u5e3d\u5b50\u70c2\u4e86\u8fb9\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u6beb\u4e0d\u8fdf\u7591\u7684\u7b54\u9053\uff1a\u201c\u9876\u597d\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u68c9\u82b1\u5e97\u91cc\u5931\u706b\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1b\u201c\u514d\u8c08\uff08\u5f39\uff09\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u5a4a\u5b50\u5377\u884c\u674e\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4e0d\u641e\u4e86\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u571f\u5305\u5b50\u4e70\u68fa\u6750\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8bd5\u8bd5\u770b\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u68fa\u6750\u7834\u4e86\u5e95\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4e22\u4eba\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u57ce\u5899\u4e0a\u62c9\u5c4e\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1b\u201c\u597d\u9ad8\u7684\u8ddf\u513f\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u72ec\u773c\u9f99\u5750\u4e0a\u9a6c\u6876\u6253\u778c\u553e\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u7741\u4e00\u53ea\u773c\u95ed\u4e00\u53cc\u773c\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u9f3b\u5c16\u4e0a\u653e\u7cd6\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u770b\u5230\u5403\u4e0d\u5230\u3002\u201d \u3000\u3000\u6768\u8001\u53c8\u9053\uff1a\u201c\u81ed\u6c34\u6c60\u91cc\u683d\u8377\u82b1\u2014\u2014\u3000\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u53ea\u987e\u4e0a\u9762\u597d\u770b\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u8001\u9f20\u62c9\u9ec4\u5305\u8f66\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u5927\u5934\u5728\u540e\u9762\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u5305\u516c\u517b\u4e86\u4e2a\u9ed1\u5b69\u5b50\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8c01\u7684\u79cd\u50cf\u8c01\u3002\u201d \u3000\u3000\u6768\u8001\u53c8\u9053\uff1a\u201c\u7caa\u5751\u91cc\u8eb2\u96e8\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6015\u6e7f\u4e0d\u6015\u81ed\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u4e61\u4e0b\u4eba\u7a7f\u5927\u8902\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u5fc5\u6709\u6b63\u4e8b\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u548c\u5c1a\u5e99\u5bf9\u7740\u5c3c\u59d1\u5eb5\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6ca1\u4e8b\u4e5f\u6709\u4e8b\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u732a\u516b\u6212\u6389\u8fdb\u998a\u6c34\u6876\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6709\u5403\u6709\u559d\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u4e00\u53e3\u6c14\u95ee\u4e86\u8fd9\u4e48\u591a\uff0c\u4e01\u6d9b\u5bf9\u7b54\u5982\u6d41\uff0c\u4f7f\u5f97\u4ed6\u8fd8\u771f\u6709\u4e9b\u5403\u60ca\uff0c\u6697\u8fdf\uff1a\u201c\u8fd9\u5c0f\u5b50\u8fd8\u771f \u5c41\u80a1\u5934\u4e0a\u6302\u94a5\u5319\u2014\u2014\u4e13\u7ba1\u4e00\u95e8\u3002\u201d \u3000\u3000\u4ed6\u867d\u5174\u8da3\u4e0d\u51cf\uff0c\u5374\u60f3\u4e0d\u8d77\u8be5\u518d\u95ee\u4ec0\u4e48\uff0c\u968f\u5373\u671b\u5411\u767d\u8863\u8001\u8005\uff1a\u201c\u5218\u5144\uff0c\u4f60\u65e2\u7136\u77e5\u9053\u4ec0\u4e48\u53eb \u540a\u574e\u513f\uff0c\u5fc5\u5b9a\u4e5f\u662f\u8fd9\u65b9\u9762\u7684\u4e13\u5bb6\uff0c\u5c31\u8bf7\u7ee7\u7eed\u4ee3\u6211\u8003\u4ed6\u51e0\u9898\u600e\u6837\uff1f\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u65e9\u5c31\u60f3\u63d2\u4e0a\u4e00\u811a\uff0c\u5982\u4eca\u6768\u8001\u5934\u4e3b\u52a8\u627e\u4ed6\u4ee3\u8003\uff0c\u6b63\u4e2d\u4e0b\u6000\uff0c\u5fd9\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u8001\u673d \u5c31\u6597\u80c6\u4ee3\u52b3\u4e86\u3002\u201d \u3000\u3000\u63a5\u7740\u63d0\u9ad8\u55d3\u95e8\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u5f1f\u542c\u7740\uff0c\u73b0\u5728\u7b2c\u4e8c\u5802\u8bfe\u5f00\u59cb\uff0c\u738b\u516b\u5543\u897f\u74dc\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u56de\u7b54\u5f97\u4f9d\u7136\u65e2\u6c89\u7740\u53c8\u654f\u6377\uff1a\u201c\u6eda\u7684\u6eda\uff0c\u722c\u7684\u722c\u3002\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u540a\u6b7b\u9b3c\u64e6\u7c89\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6b7b\u8981\u9762\u5b50\u3002\u201d \u3000\u3000\u767d\u8863\u8001\u8096\u9053\uff1a\u201c\u79c3\u5b50\u6253\u4f1e\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u65e0\u6cd5\uff08\u53d1\uff09\u65e0\u5929\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u5b55\u5987\u8fc7\u72ec\u6728\u6865\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u633a\u800c\u8d70\u9669\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u6728\u5320\u6253\u8001\u5a46\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6709\u5c3a\u5bf8\u7684\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u53a8\u5b50\u6234\u53e3\u7f69\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4e0d\u5403\u9999\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u70df\u888b\u9505\u91cc\u7092\u829d\u9ebb\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1b\u201c\u6ca1\u591a\u5927\u6cb9\u6c34\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u9b3c\u95e8\u5173\u4e0a\u5f39\u7435\u7436\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4f5c\u4e50\u4e0d\u770b\u5730\u70b9\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u6000\u80ce\u5341\u6708\u653e\u5c41\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6709\u70b9\u5a03\u5a03\u6c14\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u5341\u516b\u5c81\u7684\u59d1\u5a18\u8fdb\u8336\u9986\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u94b1\u4e5f\u82b1\u4e86\uff0c\u4eba\u4e5f\u4e22\u4e86\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u59d3\u4f55\u7684\u5ac1\u7ed9\u59d3\u90d1\u7684\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6b63\u5408\u9002\uff08\u90d1\u4f55\u6c0f\uff09\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u5149\u7740\u5c41\u80a1\u722c\u7af9\u7aff\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4e00\u8282\u4e00\u8282\u7684\u4e22\u4eba\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u5a03\u5a03\u6389\u5728\u6eda\u6c64\u91cc\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u719f\u4eba\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u72ee\u5b50\u6349\u8001\u9f20\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u5927\u6750\u5c0f\u7528\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u778e\u5b50\u5531\u82b1\u8138\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u773c\u4e0d\u89c1\u4e3a\u51c0\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u6234\u7740\u5b5d\u5e3d\u770b\u620f\u4e00\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4e50\u4ee5\u5fd8\u5fe7\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u7a7f\u7740\u7834\u978b\u8ba8\u996d\u5403\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u7a77\u5230\u5e95\u5566\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u9e21\u6bdb\u6572\u9f13\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6ca1\u6709\u56de\u97f3\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u516b\u4ed9\u684c\u4e0a\u6446\u591c\u58f6\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4e0d\u662f\u76db\u9152\u7684\u5bb6\u4f19\u3002\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u51fa\u4e86\u8fd9\u4e48\u591a\u9898\u90fd\u6ca1\u96be\u5012\u4e01\u6d9b\uff0c\u6700\u540e\u81ea\u5df1\u4e5f\u6ca1\u7684\u95ee\u4e86\u3002 \u3000\u3000\u4ed6\u671b\u7740\u6768\u8001\u5934\u62f1\u62f1\u624b\u9053\uff1b\u201c\u4ed6\u53ef\u79f0\u5f97\u4e0a\u662f\u540a\u574e\u513f\u7684\u4e13\u5bb6\uff0c\u518d\u8003\u4e5f\u96be\u5f97\u8003\u5012\u4ed6\uff0c\u7b97\u5566\uff0c\u7ed9 \u4ed6\u4e00\u767e\u5206\u8fc7\u5173\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4e50\u5f97\u8fde\u5634\u90fd\u95ed\u4e0d\u62e2\uff0c\u62b1\u4e86\u62b1\u62f3\u9053\uff1a\u201c\u591a\u8c22\u5927\u4e3b\u8003\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u9000\u56de\u4e00\u65c1\uff0c\u8fd8\u4e0d\u4f4f\u5411\u7518\u9716\u6324\u7709\u5f04\u773c\uff0e\u8868\u793a\u4ed6\u662f\u5c41\u80a1\u4e0a\u753b\u7709\u6bdb\u2014\u2014\u9762\u5b50\u591f\u5927\u3002 \u3000\u3000\u8fd9\u65f6\u8881\u5c0f\u9e64\u5df2\u4e3b\u52a8\u8d70\u8fd1\u684c\u5b50\u524d\uff0c\u4e5f\u662f\u606d\u606d\u656c\u656c\u5730\u65bd\u4e86\u4e00\u793c\u9053\uff1a \u3000\u3000\u201c\u665a\u8f88\u8bf7\u5927\u4e3b\u8003\u51fa\u9898\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u89c1\u8881\u5c0f\u9e64\u957f\u5f97\u7709\u6e05\u76ee\u79c0\uff0c\u4e00\u8868\u4eba\u624d\uff0c\u5fc3\u91cc\u65e9\u5c31\u5bf9\u4ed6\u5177\u6709\u597d\u611f\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u524d\u9762 \u4e24\u4f4d\u90fd\u6709\u8981\u6c42\uff0c\u4f60\u6709\u6ca1\u6709\u8981\u6c42\uff1f\u201d \u3000\u3000\u663e\u7136\u4ed6\u662f\u6709\u5fc3\u8ba9\u8881\u5c0f\u9e64\u8f7b\u677e\u8fc7\u5173\u3002 \u3000\u3000\u4f46\u8881\u5c0f\u9e64\u5374\u4e0d\u9886\u8fd9\u4efd\u60c5\u3002 \u3000\u3000\u4ed6\u8ddf\u968f\u5723\u624b\u4e66\u751f\u5e90\u4e91\u5341\u51e0\u5e74\uff0c\u65e2\u4e60\u6587\u53c8\u4e60\u6b66\uff0c\u5373\u4fbf\u4f5c\u7bc7\u6587\u7ae0\u4e5f\u96be\u4e0d\u5012\u4ed6\uff0c\u4f55\u51b5\u53ea\u662f\u53e3\u8bd5\u3002 \u56e0\u4e4b\uff0c\u4e5f\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\u6709\u4ec0\u4e48\u8981\u6c42\uff0c\u4f46\u51ed\u5927\u4e3b\u8003\u968f\u610f\u51fa\u9898\uff01\u201d \u3000\u3000\u7b80\u76f4\u662f\u8682\u8681\u5439\u5587\u53ed\uff0c\u53e3\u6c14\u4e0d\u5c0f\u3002 \u3000\u3000\u6768\u8001\u5934\u603b\u7b97\u660e\u767d\u4e86\u8fd9\u5e74\u8f7b\u4eba\u548c\u5148\u524d\u4e24\u4e2a\u4e0d\u4e00\u6837\uff0c\u809a\u5b50\u91cc\u5fc5\u5b9a\u6709\u4e0d\u5c11\u58a8\u6c34\uff0c\u4fbf\u70b9\u70b9\u5934\u9053\uff1a \u201c\u5982\u679c\u8981\u8001\u673d\u968f\u610f\u51fa\u9898\uff0c\u8001\u673d\u4e0d\u514d\u5c31\u8981\u51fa\u70b9\u6bd4\u8f83\u96be\u7684\u9898\u76ee\uff0c\u4f60\u6709\u610f\u89c1\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5927\u4e3b\u8003\u662f\u8001\u5e08\uff0c\u665a\u8f88\u662f\u5b66\u751f\uff0c\u5b66\u751f\u5bf9\u8001\u5e08\u7684\u51b3\u5b9a\uff0c \u3000\u3000\u600e\u6562\u6709\u610f\u89c1\u3002\u201d \u3000\u3000\u542c\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u6768\u8001\u5934\u5bf9\u8881\u5c0f\u9e64\u8d8a\u53d1\u8d5e\u8d4f\uff0c\u542b\u7b11\u9053\uff1a\u201c\u65e2\u7136\u8fd9\u6837\uff0c\u8001\u673d\u5c31\u6bcf\u6837\u90fd\u8003\u8003 \u4f60\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u4e0d\u8fc7\u5927\u4e3b\u8003\u5343\u4e07\u522b\u540a\u574e\u513f\uff0c\u8fd9\u65b9\u9762\u665a\u8f88\u6ca1\u5b66\u8fc7\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u5fcd\u4e0d\u4f4f\u7b11\u51fa\u58f0\u6765\u9053\uff1a\u201c\u90a3\u672c\u6765\u5c31\u4e0d\u7b97\u4ec0\u4e48\u5b66\u95ee\uff0c\u4e0d\u8fc7\u662f\u8dd1\u6c5f\u6e56\u7684\u7528\u6765\u800d\u800d\u5634\u76ae\u5b50 \u800c\u5df2\u3002\u201d \u3000\u3000\u4ed6\u53ef\u80fd\u6ca1\u60f3\u5230\uff0c\u8fd9\u51e0\u53e5\u8bdd\uff0c\u7b49\u4e8e\u628a\u4e01\u6d9b\u7ed9\u6253\u4e86\u4e24\u8bb0\u5634\u5df4\u5b50\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u591a\u8c22\u5927\u4e3b\u8003\u7ed9\u665a\u8f88\u65b9\u4fbf\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u90a3\u5c31\u5148\u5bf9\u53e5\uff0c\u4e0d\u8fc7\u6bd4\u521a\u624d\u7684\u53ef\u8981\u6df1\u5f97\u591a\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u665a\u8f88\u6597\u80c6\u4e00\u8bd5\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u53d6\u8d77\u684c\u4e0a\u7684\u4e66\uff0c\u7ffb\u9605\u4e86\u51e0\u9875\u9053\uff1a\u201c\u542c\u7740\uff0c\u5173\u5e1d\u5e99\u91cc\u6709\u526f\u5bf9\u8054\uff1f\u4e0a\u8054\u662f\u2018\u5e08\u5367\u9f99\uff0c \u53cb\u5b50\u9f99\uff0c\u9f99\u5e08\u9f99\u53cb\u2019\uff0c\u5bf9\u4e0b\u8054\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u7565\u4e00\u601d\u5fd6\uff0c\u9053\uff1a\u201c\u5144\u7384\u5fb7\uff0c\u5f1f\u7ffc\u5fb7\uff0c\u5fb7\u5144\u5fb7\u5f1f\u3002\u201d \u3000\u3000\u6768\u8001\u4eba\u7838\u7740\u5634\u5567\u5567\u6709\u58f0\u7684\u9053\uff1a\u201c\u679c\u7136\u9ad8\u624d\uff0c\u7adf\u548c\u539f\u6709\u7684\u4e0b\u8054\u5b8c\u5168\u4e00\u6837\uff0c\u518d\u5bf9\u4e00\u8054\uff1a\u6587\u6b66 \u5e99\u91cc\u884c\u526f\u5bf9\u8054\uff0c\u4e0a\u8054\u662f\u2018\u5b54\u592b\u5b50\uff0c\u5173\u592b\u5b50\uff0c\u4e24\u4f4d\u592b\u5b50\u2019\u3002\u5bf9\u4e0b\u8054\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u7565\u7565\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u7740\u6625\u79cb\uff0c\u8bfb\u6625\u79cb\uff0c\u4e00\u90e8\u6625\u79cb\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u5927\u4e3a\u60ca\u53f9\u9053\uff1a\u201c\u4e86\u4e0d\u5f97\uff0c\u7b80\u76f4\u53ef\u4ee5\u5f53\u72b6\u5143\u3002\u518d\u6765\u4e00\u8054\uff0c\u4e0a\u8054\u662f\u2018\u4e24\u6761\u7389\u81c2\u5343\u4eba \u6795\u2019\uff0c\u5bf9\u4e0b\u8054\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u76b1\u8d77\u7709\u5934\u9053\uff1a\u201c\u8bf7\u95ee\u5927\u4e3b\u8003\uff0c\u8fd9\u662f\u5f62\u5bb9\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u8138\u4e0a\u4e00\u70ed\uff0c\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u8fd9\u662f\u6e38\u620f\u4e4b\u4f5c\uff0c\u5f62\u5bb9\u5993\u5973\u7684\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e0d\u5047\u601d\u7d22\u9053\uff1a\u201c\u5b66\u751f\u60f3\u5bf9\u2018\u4e00\u70b9\u6731\u5507\u4e07\u5ba2\u5c1d\u2019\uff0c\u8fd8\u8bf7\u4f60\u8001\u4eba\u5bb6\u6307\u6559\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u62cd\u4e86\u4e00\u4e0b\u5927\u817f\u9053\uff1a\u201c\u592a\u597d\u4e86\uff0c\u5c31\u662f\u8981\u8ba9\u8001\u673d\u5bf9\uff0c\u4e5f\u4e0d\u53ef\u80fd\u5bf9\u5f97\u8fd9\u4e48\u597d\uff01\u518d\u6765\u4e00\u4e2a\uff0c \u4e0a\u8054\u662f\u2018\u6b64\u6728\u4e3a\u67f4\u5c71\u5c71\u51fa\u2019\uff0c\u5bf9\u4e0b\u8054\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u56e0\u706b\u6210\u70df\u5915\u5915\u591a\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u51bb\u96e8\u6d12\u7a97\uff0c\u4e1c\u4e8c\u70b9\uff0c\u897f\u4e09\u70b9\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5207\u74dc\u5206\u7247\uff0c\u4e0a\u4e03\u5200\uff0c\u4e0b\u516b\u5200\u3002\u201d \u3000\u3000\u6b64\u523b\u6768\u8001\u5934\u7b80\u76f4\u5df2\u628a\u8881\u5c0f\u9e64\u60ca\u4e3a\u5343\u53e4\u5947\u624d\uff0c\u820c\u5934\u8fde\u5482\u9053\uff1a\u201c\u73b0\u5728\u4ee5\u8ff7\u5c04\u5b57\uff0c\u8c1c\u9762\u662f\u2018\u98ce \u6765\u79be\u4e0b\u9e1f\u98de\u53bb\uff0c\u9a6c\u5230\u5e90\u8fb9\u8349\u4e0d\u751f\u2019\uff0c\u5c04\u4e24\u5b57\uff0c\u8bf7\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u7565\u4e00\u601d\u7d22\u9053\uff1a\u201c\u79c3\u9a74\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u53c8\u662f\u4e00\u60ca\uff0c\u518d\u9053\uff1a\u201c\u8fd9\u6b21\u6362\u6210\u8c10\u97f3\u683c\u5c04\u8c1c\uff0c\u6709\u56db\u53e5\u8bd7\uff0c\u6bcf\u53e5\u5c04\u4e00\u79cd\u6c34\u679c\uff0c\u56db\u53e5\u8bd7 \u662f\u2018\u5218\u79c0\u6253\u9a6c\u51fa\u57ce\u897f\uff0c\u897f\u695a\u9738\u738b\u522b\u865e\u59ec\uff0c\u5b54\u660e\u4e8c\u66f4\u628a\u5175\u70b9\uff0c\u76c2\u5fb7\u62c9\u4f4f\u4e91\u957f\u8863\u2019\uff0c\u8bf7\u8bf4\u51fa\u8ff7 \u5e95\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u601d\u8003\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u665a\u8f88\u731c\u51fa\u6765\u4e86\uff0c\u53ea\u662f\u5e76\u4e0d\u4e00\u5b9a\u5bf9\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u8bf4\u770b\uff01\u201d \u3000\u3000\u201c\u7b2c\u4e00\u53e5\u542b\u4e49\u662f\u2018\u9003\u2019\uff0c\u8c10\u97f3\u8be5\u662f\u2018\u6843\u2019\uff1b\u7b2c\u4e8c\u53e5\u542b\u4e49\u662f\u2018\u79bb\u2019\uff0c\u8c10\u97f3\u8be5\u662f\u2018\u68a8\u2019\uff1b\u7b2c \u4e8c\u53e5\u542b\u4e49\u662f\u2018\u65e9\u2019\uff0c\u8c10\u97f3\u8be5\u662f\u2018\u67a3\u2019\uff1b\u7b2c\u56db\u53e5\u542b\u4e49\u662f\u2018\u4f7f\u7559\u2019\uff0c\u8c10\u97f3\u8be5\u662f\u2018\u77f3\u69b4\u2019\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u6311\u8d77\u5927\u62c7\u6307\u9053\uff1a\u201c\u9ad8\u624d\u9ad8\u624d\uff0c\u5b8c\u5168\u5c04\u5bf9\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\u77f3\u5ba4\u5185\u7684\u4eba\uff0c\u4e01\u6d9b\u662f\u4f3c\u61c2\u975e\u61c2\uff0c\u4e00\u76f4\u5728\u5f20\u53e3\u7ed3\u820c\u3002 \u3000\u3000\u7518\u9716\u53ea\u80fd\u7b97\u61c2\u4e86\u4e00\u534a\uff0c\u867d\u672a\u5f20\u53e3\u7ed3\u820c\uff0c\u5185\u5fc3\u5374\u65e9\u5df2\u5bf9\u8881\u5c0f\u9e64\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5219\u5728\u4e00\u65c1\u4e0d\u52a8\u58f0\u8272\u3002 \u3000\u3000\u767d\u8863\u8001\u8005\u4e5f\u5185\u5fc3\u76f4\u8bf4\u8fd9\u5c0f\u4f19\u5b50\u771f\u4e0d\u7b80\u5355\u3002 \u3000\u3000\u53ea\u542c\u6768\u8001\u5934\u9053\uff1a\u201c\u73b0\u5728\u6539\u6210\u5c04\u4eba\u540d\uff1a\u2018\u53f8\u9a6c\u708e\u7be1\u4f4d\u2019\u5c04\u4e09\u56fd\u4eba\u540d\u4e00\uff0c\u8bf7\u5c04\u8ff7\u5e95\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u65e9\u5c31\u770b\u8fc7\u4e09\u56fd\u6f14\u4e49\uff0c\u60f3\u90fd\u6ca1\u60f3\u9053\uff1b\u201c\u66f9\u4f11\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u6cb9\u70b8\u8c46\u8150\uff0c\u5c04\u4e24\u4f4d\u4eba\u540d\uff0c\u5176\u4e2d\u4e00\u4eba\u662f\u8c10\u97f3\uff0c\u8bf7\u5c04\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1b\u201c\u4e00\u4f4d\u662f\u9ec4\u76d6\uff0c\u3000\u4e00\u4f4d\u662f\u674e\uff08\u91cc\uff09\u767d\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u62cd\u7740\u624b\u9053\uff1a\u201c\u4e86\u4e0d\u5f97\uff01\u4e0d\u5f97\u4e86\uff01\u5929\u4e0b\u7b2c\u4e00\u5947\u624d\uff0c\u4eca\u5929\u7adf\u88ab\u8001\u673d\u6709\u5e78\u9047\u4e0a\u4e86\uff0c\u548c\u8001 \u673d\u6bd4\u8d77\u6765\uff0c\u7b80\u76f4\u662f\u9752\u51fa\u4e8e\u84dd\uff0c\u66f4\u80dc\u4e8e\u84dd\uff01\u201d \u3000\u3000\u4ed6\u4f3c\u662f\u610f\u72b9\u672a\u5c3d\uff0c\u53c8\u671b\u7740\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u8fd9\u6837\u7684\u9ad8\u624d\uff0c\u5982\u4eca\u6765\u5230\u6211\u4eec\u8fd9\u91cc\uff0c\u8bf4\u4e0d\u5b9a\u628a\u5f53 \u4eca\u7684\u7687\u5e1d\u6c14\u6b7b\uff0c\u4f9d\u8001\u673d\u770b\uff0c\u5373\u4fbf\u5929\u4e0b\u6709 \u3000\u3000\u4eba\u7684\u5b66\u95ee\u80fd\u8d76\u4e0a\u4ed6\uff0c\u4ecd\u7136\u65e0\u6cd5\u548c\u4ed6\u76f8\u6bd4\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u5e26\u4e9b\u4e0d\u89e3\u9053\uff1b\u201c\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u4f4d\u8001\u5f1f\u662f\u6587\u6b66\u53cc\u5168\uff0c\u81ea\u53e4\u4ee5\u6765\uff0c\u54ea\u6709\u72b6\u5143\u662f\u6587\u6b66\u53cc\u5168\u7684\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u4f60\u8bf4\u9519\u4e86\uff0c\u6709\u4e00\u4e2a\u72b6\u5143\uff0c\u4e5f\u662f\u6587\u6b66\u53cc\u5168\u7684\u3002\u201d \u3000\u3000\u201c\u8c01\uff1f\u201d \u3000\u3000\u201c\u9ec4\u5de2\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u9ec4\u5de2\u7684\u4eba\u54c1\uff0c\u53ef\u5c31\u6bd4\u4e0d\u4e0a\u8fd9\u4f4d\u8001\u5f1f\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u54b1\u4eec\u5c31\u8be5\u628a\u4eca\u5929\u53d1\u73b0\u5947\u624d\u7684\u7ecf\u8fc7\u8f6c\u594f\u6559\u4e3b\uff0c\u8bf4\u4e0d\u5b9a\u54b1\u4eec\u4e24\u4eba\u90fd\u80fd\u72ec\u8bb0\u4e00\u6b21\u5927 \u529f\uff01\u201d \u3000\u3000\u201c\u6682\u65f6\u522b\u6025\uff0c\u4ed6\u4eec\u8fd8\u6709\u5f88\u591a\u5173\u53e3\u8981\u8fc7\uff0c\u5982\u679c\u80fd\u5b8c\u5168\u901a\u8fc7\uff0c\u518d\u659f\u914c\u60c5\u5f62\u5904\u7406\u4e5f\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u548c\u767d\u8863\u8001\u8005\u4e00\u5bf9\u4e00\u7b54\uff0c\u4f3c\u4e4e\u5df2\u5fd8\u8bb0\u8fd8\u6709\u4e00\u4eba\u4e0d\u66fe\u5e94\u8bd5\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7b49\u5f97\u6709\u70b9\u4e0d\u8010\u70e6\uff0c\u81ea\u52a8\u8d70\u51fa\u51e0\u6b65\u9053\uff1a\u201c\u5927\u4e3b\u8003\uff0c\u8fd8\u6709\u4e00\u4eba\u6ca1\u8003\u5462\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u8fd9\u624d\u5bf9\u5cb3\u5c0f\u98de\u7559\u4e0a\u4e86\u610f\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u4e30\u59ff\u5982\u7389\uff0c\u9aa8\u5947\u795e\u6e05\uff0c\u8bba\u4eea\u8868\u6c14\u8d28\u4f3c\u4e4e\u80dc\u8fc7\u8881\u5c0f\u9e64\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4e0d\u8ba4\u4e3a\u5cb3\u5c0f\u98de\u7684\u5b66\u95ee\u80fd\u6bd4\u8881\u5c0f\u9e64\u5927\uff0c\u56e0\u4e3a\u4ed6\u603b\u662f\u5e74\u7eaa\u592a\u5c0f\uff0c\u5373\u4f7f\u8bfb\u8fc7\u4e0d\u5c11\u4e66\uff0c\u6839 \u57fa\u4e5f\u4e0d\u4f1a\u592a\u6df1\u3002 \u3000\u3000\u6768\u8001\u5934\u56e0\u4e3a\u5fc3\u91cc\u9ad8\u5174\uff0c\u6709\u610f\u653e\u5cb3\u5c0f\u98de\u4e00\u9a6c\uff0c\u5373\u4fbf\u4e0d\u8003\u4e5f\u8ba9\u4ed6\u8fc7\u5173\uff0c\u968f\u5373\u7b11\u5475\u5475\u7684\u95ee\u9053\uff1a \u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u8bfb\u8fc7\u4e66\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u597d\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u6768\u8001\u5934\u6446\u6446\u624b\u9053\uff1a\u201c\u8bfb\u8fc7\u4e66\u5c31\u7b97\u4e86\uff0c\u7528\u4e0d\u7740\u518d\u8003\u3002\u201d \u3000\u3000\u4ed6\u672c\u6765\u4ee5\u4e3a\u5cb3\u5c0f\u98de\u4f1a\u65e2\u60ca\u559c\u53c8\u611f\u6fc0\uff0c\u5c82\u77e5\u5cb3\u5c0f\u98de\u5374\u6b63\u8d77\u795e\u8272\u9053\uff1b\u201c\u5927\u4e3b\u8003\u662f\u5426\u89c1\u665a\u8f88\u5e74 \u7eaa\u5c0f\uff0c\u77a7\u4e0d\u8d77\uff0c\u6240\u4ee5\u624d\u4e0d\u5c51\u4e00\u8bd5\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u5927\u611f\u4e00\u6123\u9053\uff1b\u201c\u8001\u673d\u6ca1\u6709\u8fd9\u610f\u601d\uff0c\u8fde\u8fd9\u4f4d\u6ca1\u8bfb\u4e66\u7684\u4eba\u90fd\u80fd\u901a\u8fc7\uff0c\u4f60\u5df2\u8bfb\u8fc7\u4e66\uff0c\u9884 \u6599\u5fc5\u53ef\u901a\u8fc7\uff0c\u90a3\u5c31\u7528\u4e0d\u7740\u518d\u9ebb\u70e6\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u51b7\u4e00\u7b11\u9053\uff1b\u201c\u5927\u4e3b\u8003\u8fd9\u756a\u597d\u610f\uff0c\u665a\u8f88\u5341\u5206\u611f\u6fc0\uff0c\u4f46\u514d\u8bd5\u8fc7\u5173\uff0c\u665a\u8f88\u603b\u89c9\u5f97\u4e0d\u8db3 \u4e00\u4ef6\u5149\u8363\u7684\u4e8b\u60c5\u3002\u201d \u3000\u3000\u8fd9\u65f6\u8881\u5c0f\u9e64\u4e5f\u5e2e\u8154\u9053\uff1a\u201c\u5927\u4e3b\u8003\uff0c\u82e5\u8bba\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u7684\u5b66\u95ee\uff0c\u53ef\u6bd4\u665a\u8f88\u5f3a\u591a\u4e86\uff0c\u4f60\u8001\u9519\u8fc7 \u7504\u8bd5\u9ad8\u624d\u7684\u597d\u673a\u4f1a\uff0c\u5b9e\u5728\u662f\u4ef6\u61be\u4e8b\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8fd9\u513f\u53e5\u8bdd\uff0c\u786e\u662f\u51fa\u81ea\u80ba\u8151\u4e4b\u8a00\uff0c\u56e0\u4ed6\u77e5\u9053\u5cb3\u5c0f\u98de\u4e0d\u8bba\u6587\u3001\u6b66\uff0c\u90fd\u8d85\u8fc7\u81ea\u5df1\u5f88\u591a\uff0c \u800c\u65b9\u624d\u81ea\u5df1\u5df2\u5c3d\u98ce\u5934\uff0c\u82e5\u4e0d\u8ba9\u6768\u8001\u5934\u89c1\u8bc6\u89c1\u8bc6\u5cb3\u5c0f\u98de\u7684\u624d\u534e\uff0c\u5bf9\u5cb3\u5c0f\u98de\u6765\u8bf4\uff0c\u5b9e\u5728\u662f\u4e00\u79cd\u59d4 \u5c48\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u6709\u8fd9\u79cd\u60f3\u6cd5\uff0c\u53ef\u89c1\u4ed6\u5bf9\u80dc\u8fc7\u81ea\u5df1\u7684\u5cb3\u5c0f\u98de\uff0c\u6beb\u65e0\u5ac9\u5992\u4e4b\u5fc3\u3002\u5f53\u7136\uff0c\u8fd9\u4e5f\u662f\u5cb3\u5c0f\u98de \u5bf9\u4ed6\u809d\u80c6\u76f8\u7167\u6240\u6362\u6765\u7684\u3002 \u3000\u3000\u6768\u8001\u5934\u4e00\u542c\u8881\u5c0f\u9e64\u8fd9\u6837\u8bf4\uff0c\u4e0d\u7531\u637b\u7740\u5c71\u7f8a\u80e1\u5b50\u9053\uff1a\u201c\u8001\u673d\u76f8\u4fe1\u4ed6\u7684\u5b66\u95ee\u53ef\u80fd\u4e5f\u4e0d\u9519\uff0c\u4f46 \u82e5\u8bf4\u80fd\u80dc\u8fc7\u4f60\uff0c\u90a3\u5c31\u672a\u514d\u592a\u5938\u5f20\u4e86\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u662f\u5426\u56e0\u4e3a\u4ed6\u5e74\u7eaa\u5c0f\uff0c\u6240\u4ee5\u624d\u6709\u8fd9\u79cd\u60f3\u6cd5\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u9886\u9996\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5b66\u95ee\u662f\u968f\u7740\u5e74\u9f84\u7d2f\u79ef\u7684\uff0c\u5c31\u50cf\u4f60\u4eec\u5b66\u6b66\u529f\u4e00\u6837\uff0c\u5e74\u7eaa\u8d8a\u5927\uff0c\u529f \u529b\u706b\u5019\u8d8a\u6df1\uff0c\u8fd9\u4f4d\u5c0f\u8001\u5f1f\u5c31\u662f\u5929\u8d44\u518d\u9ad8\uff0c\u603b\u662f\u5c81\u6570\u592a\u5c0f\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6447\u5934\u9053\uff1b\u201c\u5927\u4e3b\u8003\u7684\u770b\u6cd5\u56fa\u7136\u6709\u7406\uff0c\u4f46\u5374\u4e0d\u80fd\u4e00\u6982\u800c\u8bba\uff0c\u5f53\u5e74\u7518\u7f57\u5341\u4e8c\u5c81\u4fbf\u88ab\u5c01 \u505a\u4e0a\u537f\uff0c\u5b54\u592b\u5b50\u4ee5\u4e03\u5c81\u7684\u9879\u6a50\u4e3a\u5e08\uff0c\u8fd9\u53c8\u505a\u4f55\u89e3\u91ca\u5462\uff1f\u4f55\u51b5\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u5df2\u7ecf\u5341\u4e09\u56db\u5c81\u4e86\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u7b11\u9053\uff1b\u201c\u4f60\u8bf4\u7684\u7167\u6837\u4e5f\u4e0d\u80fd\u4e00\u6982\u800c\u8bba\uff0c\u7518\u7f57\u548c\u9879\u6a50\u662f\u54b1\u4eec\u4e2d\u56fd\u51e0\u5343\u5e74\u6765\u4e0d\u4e16\u51fa\u7684 \u5947\u624d\uff0c\u867d\u6240\u8c13\u795e\u7ae5\u4e0d\u8fc7\u5982\u4e5f\uff0c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u7eb5\u7136\u806a\u660e\u7edd\u9876\uff0c\u4e5f\u51b3\u4e0d\u53ef\u80fd\u548c\u4ed6\u4eec\u4e24\u4eba\u76f8\u6bd4\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5728\u4e3b\u8003\u6ca1\u8003\u8bd5\u8fc7\u4ed6\uff0c\u53c8\u600e\u77e5\u4ed6\u4e0d\u80fd\u548c\u524d\u9762\u6240\u8bf4\u7684\u4e24\u4eba\u76f8\u6bd4\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u4e0d\u613f\u518d\u8fa9\uff0c\u53ea\u597d\u8fde\u58f0\u9053\uff1a\u201c\u597d\u4e86\u597d\u4e86\uff01\u8001\u673d\u8003\u8003\u4ed6\u5c31\u662f\u3002\u201d \u3000\u3000\u4ed6\u867d\u7136\u8fd9\u6837\u8bf4\uff0c\u5374\u4e0d\u60f3\u628a\u9898\u76ee\u51fa\u5f97\u592a\u96be\uff0c\u4ee5\u514d\u5cb3\u5c0f\u98de\u56de\u7b54\u4e0d \u3000\u3000\u4e0a\u800c\u8ba9\u8881\u5c0f\u9e64\u6ca1\u9762\u5b50\uff0c\u56e0\u4e3a\u4ed6\u73b0\u5728\u5df2\u628a\u8881\u5c0f\u9e64\u89c6\u4e3a\u6700\u5f97\u610f\u7684\u95e8\u751f\u3002 \u3000\u3000\u4ed6\u968f\u5373\u671b\u5411\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8001\u673d\u60f3\u5148\u6d4b\u9a8c\u4f60\u7684\u667a\u529b\uff0c\u4e0b\u96e8\u5929\uff0c\u65e2\u4e0d\u6253\u4f1e\uff0c\u53c8\u4e0d\u6234\u5e3d\uff0c\u7ed3\u679c \u5934\u4e0a\u548c\u8eab\u4e0a\u5168\u6e7f\u4e86\uff0c\u5c31\u662f\u6ca1\u6e7f\u5934\u53d1\uff0c\u6709 \u3000\u3000\u8fd9\u79cd\u53ef\u80fd\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5176\u5b9e\u8fd9\u9898\u76ee\u8fd8\u771f\u4e0d\u7b80\u5355\uff0c\u82e5\u8003\u4e01\u6d9b\u548c\u7518\u9716\uff0c\u51c6\u662f\u7b54\u4e0d\u4e0a\u6765\u3002 \u3000\u3000\u8fde\u8881\u5c0f\u9e64\u4e5f\u96be\u514d\u4e3a\u5cb3\u5c0f\u98de\u7740\u6025\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u60f3\u90fd\u6ca1\u60f3\u9053\uff1a\u201c\u5f53\u7136\u6709\u8fd9\u79cd\u53ef\u80fd\uff0c\u548c\u5c1a\u548c\u5c3c\u59d1\u5c31\u505a\u5f97\u5230\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u7167\u6837\u4e5f\u662f\u4e00\u60ca\uff0c\u4e0d\u4f4f\u70b9\u5934\u9053\uff1a\u201c\u7684\u786e\u53cd\u5e94\u591f\u5feb\u3002\u73b0\u5728\u518d\u51fa\u4e2a\u8ff7\u9762\u8364\u4e9b\u7684\uff1a\u4e0a\u4e0b\u90fd \u6709\u6bdb\uff0c\u4e0a\u5e8a\u4e4b\u540e\u6bdb\u5bf9\u6bdb\uff0c\u8fd9\u662f\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u8fd9\u9898\u76ee\u82e5\u8ba9\u4e01\u6d9b\u7b54\uff0c\u90a3\u5c31\u975e\u8364\u4e0d\u53ef\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5927\u4e3b\u8003\u95ee\u7684\u53ef\u662f\u773c\u775b\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u53c8\u5403\u4e00\u60ca\u9053\uff1a\u201c\u4e86\u4e0d\u5f97\uff0c\u4ecd\u7136\u4e0d\u66fe\u96be\u5012\u4f60\u3002\u518d\u51fa\u4e2a\u8ff7\uff0c \u3000\u3000\u8ff7\u9762\u662f\u91cd\u7537\u8f7b\u5973\uff0c\u6253\u4e00\u5730\u540d\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8d35\u9633\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u4e00\u4e2a\u5b57\uff0c\u4e0b\u96ea\u7684\u96ea\uff0c\u6253\u4e24\u4e2a\u5730\u540d\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5929\u6c34\uff0c\u6a2a\u5c71\u3002\u201d \u3000\u3000\u6b64\u523b\u6768\u8001\u5934\u5df2\u77e5\u5cb3\u5c0f\u98de\u7684\u786e\u9ad8\u624d\uff0c\u53cd\u800c\u4e0d\u518d\u5403\u60ca\uff0c\u63a5\u9053\uff1a\u201c\u725b\u5934\u864e\u5c3e\uff0c\u6253\u4e00\u4e2a\u5b57\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5148\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u64e6\u4e86\u4e0b\u989d\u89d2\u7684\u6c57\u6c34\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u5bf9\u56db\u4e66\u4e00\u5b9a\u5f88\u719f\u5427\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8c08\u4e0d\u4e0a\u719f\uff0c\u53ea\u662f\u8bfb\u8fc7\u800c\u5df2\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u7528\u56db\u53e5\u8bd7\u5c04\u8bba\u8bed\u56db\u53e5\uff0c\u542c\u7740\uff1a\u5f53\u4eca\u7687\u4e0a\u53bb\u5077\u725b\uff0c\u6587\u6b66\u767e\u5b98\u722c\u5899\u5934\uff0c\u516c\u516c\u6402 \u7740\u5ab3\u5987\u7761\uff0c\u513f\u5b50\u6253\u7834\u7239\u7684\u5934\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u541b\u4e0d\u541b\uff0c\u81e3\u4e0d\u81e3\uff0c\u7236\u4e0d\u7236\uff0c\u5b50\u4e0d\u5b50\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u4ece\u73b0\u5728\u8d77\uff0c\u8001\u673d\u51c6\u5907\u6458\u56db\u4e66\u7ae0\u53e5\uff0c\u8001\u673d\u8bfb\u4e0a\u53e5\uff0c \u3000\u3000\u4f60\u63a5\u4e0b\u53e5\uff0c\u4e0d\u51c6\u601d\u8003\uff0c\u5fc5\u987b\u9a6c\u4e0a\u7b54\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5927\u4e3b\u8003\u8bf7\u5ff5\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u5b66\u800c\u4e0d\u601d\u5219\u7f54\uff0c\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u601d\u800c\u4e0d\u5b66\u5219\u6b86\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u8d28\u80dc\u6587\u5219\u91ce\uff0c\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6587\u80dc\u8d28\u5219\u53f2\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u541b\u5b50\u6cf0\u800c\u4e0d\u9a84\uff0c\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5c0f\u4eba\u9a84\u800c\u4e0d\u6cf0\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u53ef\u4e0e\u8a00\uff0c\u800c\u4e0d\u4e0e\u4e4b\u8a00\uff0c\u5931\u4eba\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e0d\u53ef\u4e0e\u8a00\uff0c\u800c\u4e0e\u4e4b\u8a00\uff0c\u5931\u8a00\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u4ec1\u8005\u4ee5\u8d22\u53d1\u8eab\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e0d\u4ec1\u8005\u4ee5\u8eab\u53d1\u8d22\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u60a0\u8fdc\u5219\u535a\u539a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u535a\u539a\u5219\u9ad8\u660e\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u89c1\u5176\u751f\uff0c\u4e0d\u5fcd\u89c1\u5176\u6b7b\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u95fb\u5176\u58f0\uff0c\u4e0d\u5fcd\u98df\u5176\u8089\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u5f92\u5584\u4e0d\u8db3\u4ee5\u4e3a\u653f\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5f92\u6cd5\u4e0d\u80fd\u4ee5\u81ea\u884c\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u517b\u5176\u5c0f\u8005\u4e3a\u5c0f\u4eba\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u517b\u5176\u5927\u8005\u4e3a\u5927\u4eba\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u5929\u4e0b\u6709\u9053\uff0c\u4ee5\u9053\u6b89\u8eab\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5929\u4e0b\u65e0\u9053\uff0c\u4ee5\u8eab\u6b89\u9053\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u89c1\u59cb\u7ec8\u65e0\u6cd5\u5c06\u5bf9\u65b9\u8003\u5012\uff0c\u5fc3\u91cc\u4e5f\u96be\u514d\u4e0d\u662f\u5473\u9053\u3002 \u3000\u3000\u8fd9\u5012\u5e76\u975e\u4ed6\u5992\u624d\uff0c\u800c\u662f\u89c9\u5f97\u5148\u524d\u4ed6\u4e0d\u66fe\u628a\u5bf9\u65b9\u770b\u5f97\u592a\u9ad8\uff0c\u82e5\u4e00\u76f4\u8fde\u4e2a\u5c0f\u5b69\u90fd\u96be\u4e0d\u5012\uff0c\u81ea \u5df2\u7684\u9762\u5b50\u4e0a\u603b\u662f\u6709\u4e9b\u4e0d\u597d\u770b\uff0c\u540c\u65f6\u4ed6 \u3000\u3000\u53c8\u4e0d\u613f\u5cb3\u5c0f\u98de\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u6043\u624d\u800c\u9a84\uff0c\u56e0\u4e4b\u4fbf\u51b3\u5b9a\u6765\u4e2a\u6b6a\u7684\u5236\u670d\u5bf9\u65b9\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u679c\u7136\u662f\u4f4d\u795e\u7ae5\uff0e\u4ee4\u4eba\u4f69\u670d\uff0c \u3000\u3000\u8001\u673d\u6700\u540e\u518d\u51fa\u53e6\u5916\u4e00\u9898\uff0c\u5b54\u95e8\u5f1f\u5b50\u4e03\u5341\u4e8c\u8d24\u4f60\u662f\u77e5\u9053\u7684\uff0c\u4f60\u662f\u5426\u77e5\u9053\u4e03\u5341\u4e8c\u8d24\u5f53\u4e2d\uff0c\u6709 \u51e0\u4e2a\u662f\u5a36\u8fc7\u5ab3\u5987\u7684\uff1f\u6709\u51e0\u4e2a\u662f\u6253\u5149\u68cd\u7684\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u9898\u679c\u7136\u628a\u5cb3\u5c0f\u98de\u96be\u5012\uff0c\u4ed6\u5446\u5b50\u4e00\u5446\u9053\uff1a\u201c\u665a\u8f88\u624d\u758f\u5b66\u6d45\uff0c\u4e0d\u66fe\u5728\u4e66\u4e0a\u770b\u5230\u6709\u8fd9\u79cd\u8bb0 \u8f7d\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u4e2a\u52a8\u58f0\u8272\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u6e05\u6e05\u695a\u695a\u8bb0\u8f7d\u5728\u8bba\u8bed\u4e0a\uff0c\u4f60\u4e0d\u66fe\u770b\u5230\uff0c\u53ef\u89c1\u4f60\u8fd8\u662f\u6ca1\u628a\u8bba \u6d6f\u8bfb\u901a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u5934\u4e00\u9707\uff0c\u6697\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u8bfb\u7684\u8bba\u6d6f\u548c\u6211\u8bfb\u7684\u4e0d\u4e00\u6837\uff1f\u3000\u2026\u2026\u201d \u3000\u3000\u6768\u8001\u5934\u89c1\u5cb3\u5c0f\u98de\u6123\u5728\u90a3\u91cc\uff0c\u7b11\u9053\uff1b\u201c\u5c31\u5728\u8bba\u8bed\u2018\u5148\u8fdb\u7b2c\u5341\u4e00\u2019 \u3000\u3000\u90a3\u4e00\u7bc7\u4e0a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5927\u4e3b\u8003\u6240\u8bf4\u7684\u662f\u9c81\u8bba\uff1f\u9f50\u8bba\uff1f\u8fd8\u662f\u53e4\u8bba\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u5f53\u7136\u662f\u4e8c\u5341\u7bc7\u7684\u9c81\u8bba\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6682\u9489\u622a\u94c1\u822c\u7684\u9053\uff1a\u201c\u82e5\u662f\u9c81\u8bba\uff0c\u665a\u8f88\u53ef\u4ee5\u5012\u80cc\u5982\u6d41\uff0c\u7edd\u6ca1\u6709\u8fd9\u79cd\u8bb0\u8f7d\uff01\u201d \u3000\u3000\u201c\u4f60\u6562\u6253\u8d4c\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6562\uff01\u201d \u3000\u3000\u201c\u8d4c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u968f\u5927\u4e3b\u8003\u7684\u610f\uff0c\u5373\u4fbf\u6458\u4e0b\u665a\u8f88\u9879\u4e0a\u4eba\u5934\uff0c\u665a\u8f88\u4e5f\u51b3\u4e0d\u76b1\u4e00\u4e0b\u7709\u5934\uff01\u201d \u3000\u3000\u201c\u6ca1\u90a3\u4e48\u4e25\u91cd\uff0c\u53ea\u8981\u4f60\u62dc\u5728\u8001\u673d\u95e8\u4e0b\u505a\u8001\u673d\u4e00\u540d\u95e8\u751f\u5c31\u591f\u4e86\u3002\u201d \u3000\u3000\u201c\u5927\u4e3b\u8003\u8bf7\u8bf4\u51fa\u8fd9\u79cd\u8bb0\u8f7d\u5728\u2018\u5148\u8fdb\u7b2c\u5341\u4e00\u2019\u7684\u90a3\u53e5\u8bdd\u91cc\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u6447\u5934\u6643\u8111\u7684\u9053\uff1a\u201c\u2018\u83ab\u6625\u8005\uff0c\u6625\u670d\u65e2\u6210\uff0c\u51a0\u8005\u4e94\u516d\u4eba\uff0c\u7ae5\u5b50\u516d\u4e03\u4eba\u2026\u2026\u2019\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5f53\u7136\u8bfb\u8fc7\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u563f\u563f\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u8bb0\u8f7d\uff0c\u6240\u8c13\u51a0\u8005\uff0c\u5c31\u662f\u5a36\u8fc7\u5ab3\u5987\u7684\uff0c\u51a0\u8005\u4e94\u516d\u4eba\uff0c\u4e94\u516d\u4e09\u5341\uff0c \u5c31\u662f\u8bf4\u5a36\u8fc7\u5ab3\u5987\u7684\u6709\u4e09\u5341\u4eba\uff1b\u6240\u8c13\u7ae5\u5b50\uff0c\u5c31\u662f\u6ca1\u5a36\u8fc7\u5ab3\u5987\u7684\uff0c\u7ae5\u5b50\u516d\u4e03\u4eba\uff0c\u516d\u4e03\u56db\u5341\u4e8c\uff0c\u5c31 \u662f\u6ca1\u5a36\u5ab3\u5987\u7684\u6709\u56db\u5341\u4e8c\u4eba\u3002\u4e09\u5341\u4eba\u548c\u52a0\u56db\u5341\u4e8c\u4eba\uff0c\u6b63\u597d\u662f\u4e03\u5341\u4e8c\u4eba\u3002\u5c0f\u5144\u5f1f\uff0c\u8fd8\u6709\u4ec0\u4e48\u8bdd \u8bb2\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u660e\u77e5\u8fd9\u662f\u4e00\u756a\u6b6a\u7406\uff0c\u5374\u53c8\u4e0d\u4fbf\u5c31\u51a0\u8005\u548c\u7ae5\u5b50\u7684\u610f\u4e49\u548c\u6768\u8001\u5934\u4e89\u6267\uff0c\u53ea\u662f\u6de1\u7136\u4e00\u7b11 \u9053\uff1a\u201c\u5927\u4e3b\u8003\uff0c\u665a\u8f88\u627f\u8ba4\u4f60\u8bf4\u7684\u5e76\u975e\u6ca1\u9053\u7406\u2014\u2014\u201d \u3000\u3000\u7ad9\u5728\u65c1\u8fb9\u7684\u8881\u5c0f\u9e64\u5fc3\u91cc\u4e00\u6025\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u5927\u4e3b\u8003\u8bf4\u7684\u660e\u660e\u662f\u5f3a\u8bcd\u593a\u7406\uff0c\u8fde\u6211\u90fd\u53ef\u4ee5\u9a73\u5012 \u4ed6\uff0c\u4f60\u600e\u4e48\u627f\u8ba4\u4ed6\u8bf4\u5bf9\u4e86\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5927\u4e3b\u8003\u8bf4\u7684\u7684\u786e\u5bf9\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6709\u4e9b\u6b7b\u6c14\u618b\u88c2\u7684\u9053\uff1a\u201c\u4f60\u82e5\u627f\u8ba4\u4ed6\u5bf9\uff0c\u5c31\u5f97\u62dc\u4ed6\u4e3a\u5e08\uff0c\u54b1\u4eec\u600e\u53ef\u4e71\u62dc\u5e08\u7236\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u4f3c\u4e4e\u6709\u4e9b\u7b49\u4e0d\u5f97\uff0c\u5927\u58f0\u62db\u547c\u7740\u9053\uff1a\u201c\u65e2\u7136\u627f\u8ba4\u8001\u673d\u7684\u8bdd\u6709\u7406\uff0c\u8fd8\u4e0d\u62dc\u5e08\u7b49\u4ec0\u4e48\uff01\u201d \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c \u4e94 \u56de\u3000\u7279\u6b8a\u4f18\u5f85 \u3000\u3000\u8fd9\u65f6\u6700\u7740\u6025\u7684\uff0c\u5e76\u975e\u5cb3\u5c0f\u98de\uff0c\u800c\u662f\u8881\u5c0f\u9e64\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u65e9\u5c31\u542c\u8bf4\u8fc7\uff1a\u6b66\u6797\u4eba\u7269\uff0c\u4e0d\u53ef\u968f\u4fbf\u62dc\u5e08\u3002\u5cb3\u5c0f\u98de\u5bf9\u5723\u624b\u4e66\u751f\u5e90\u4e91\u90fd\u6ca1\u62dc\u8fc7\u5e08\uff0c\u600e \u53ef\u548c\u6768\u8001\u5934\u521a\u89c1\u9762\u5c31\u62dc\u5e08\uff0c\u4f55\u51b5\u6768\u8001 \u3000\u3000\u5934\u4e5f\u51b3\u4e0d\u53ef\u80fd\u662f\u4ec0\u4e48\u6b63\u6d3e\u4eba\u7269\u3002 \u3000\u3000\u6768\u8001\u5934\u672c\u6765\u5bf9\u8881\u5c0f\u9e64\u9887\u5177\u597d\u611f\uff0c\u5982\u4eca\u89c1\u4ed6\u7adf\u62e6\u963b\u5cb3\u5c0f\u98de\u62dc\u5e08\uff0c\u4e0d\u7531\u4e5f\u52a8\u4e86\u6c14\uff0c\u77aa\u4e86\u8881\u5c0f \u9e64\u4e00\u773c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u53cd\u800c\u6253\u6405\u8d77\u6765\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6b63\u8272\u9053\uff1a\u201c\u5e76\u975e\u665a\u8f88\u6253\u6405\uff0c\u800c\u662f\u5927\u4e3b\u8003\u90a3\u756a\u89e3\u91ca\uff0c\u6839\u672c\u4e0d\u662f\u9053\u7406\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u4e24\u773c\u76f4\u7728\u9053\uff1a\u201c\u8001\u673d\u6240\u8bf4\u7684\u54ea\u91cc\u6ca1\u6709\u9053\u7406\uff0c\u4f60\u8bb2\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u6240\u8c13\u51a0\u8005\uff0c\u4e0d\u8fc7\u6307\u7684\u662f\u5927\u4eba\uff0c\u53e4\u65f6\u4ee5\u4e8c\u5341\u5c81\u4e3a\u5f31\u51a0\uff0c\u8fd9\u4e0e\u5a36\u5ab3\u626a\u6839\u672c\u8c08\u4e0d \u4e0a\u5173\u7cfb\u3002\u81f3\u4e8e\u7ae5\u5b50\uff0c\u6307\u7684\u6839\u672c\u5c31\u662f\u5c0f\u5b69\u5b50\uff0c\u665a\u8f88\u60f3\u95ee\u4e00\u53e5\uff0c\u5927\u4e3b\u8003\u5a36\u8fc7\u5ab3\u5987\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u8138\u4e0a\u4e00\u70ed\u9053\uff1a\u201c\u60ed\u6127\uff0c\u8001\u673d\u81f3\u4eca\u8fd8\u72ec\u8eab\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6de1\u7136\u2014\u7b11\u9053\uff1a\u201c\u5982\u679c\u7167\u5927\u4e3b\u8003\u7684\u8bf4\u6cd5\uff0c\u4f60\u8001\u4eba\u5bb6\u73b0\u5728\u8fd8\u662f\u4e2a\u7ae5\u5b50\uff0c\u82e5\u665a\u8f88\u4ece\u73b0\u5728 \u8d77\u5c31\u79f0\u547c\u4f60\u8001\u4eba\u5bb6\u4e3a\u7ae5\u5b50\uff0c\u4f60\u80af\u4e0d\u80af\u7b54\u5e94\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u987f\u65f6\u7ea2\u4e86\u8138\u7b54\u4e0d\u4e0a\u8bdd\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u7ee7\u7eed\u9053\uff1a\u201c\u518d\u8bf4\u4e94\u516d\u4eba\u5c31\u662f\u4e94\u516d\u4eba\uff0c\u516d\u4e03\u4eba\u5c31\u662f\u516d\u4e03\u4eba\uff0c\u96be\u9053\u5b54\u592b\u5b50\u8bf4\u7684\u8bdd\u8fd8\u5e26 \u52a0\u51cf\u4e58\u9664\u7684\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u88ab\u5f04\u5f97\u8d8a\u53d1\u54d1\u53e3\u65e0\u8a00\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u5cb3\u5c0f\u98de\u5374\u8f6c\u8fc7\u5934\u6765\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0c\u522b\u8bf4\u4e86\uff0c\u5927\u4e3b\u8003\u7684\u89e3\u91ca\u5f88\u5bf9\uff0c\u56e0\u4e3a\u54b1 \u4eec\u7684\u5b66\u95ee\u90fd\u4e0d\u5982\u4ed6\uff0c\u7ae5\u5b50\u5c31\u662f\u6ca1\u5a36\u5ab3\u5987\u7684\uff0c\u4f60\u73b0\u5728\u867d\u7136\u4e8c\u5341\u51fa\u5934\u4e86\uff0c\u4ecd\u662f\u4e2a\u7ae5\u5b50\uff0c\u5927\u4e3b\u8003\u4e5f \u662f\u7ae5\u5b50\uff0c\u4f60\u770b\u4ed6\u54ea\u4e00\u70b9\u4e0d\u50cf\u7ae5\u5b50\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6025\u5f97\u4e00\u62cd\u5927\u817f\u9053\uff1a\u201c\u83ab\u975e\u4f60\u662f\u6709\u610f\u5411\u4ed6\u62dc\u5e08\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u867d\u7136\u8138\u8272\u80c0\u5f97\u66f4\u7ea2\uff0c\u5374\u7d27\u63a5\u7740\u9053\uff1a\u201c\u8fde\u4ed6\u81ea\u5df1\u90fd\u627f\u8ba4\u8001\u673d\u8bf4\u7684\u5bf9\uff0c\u4f60\u8fd8\u63d2\u7684\u4ec0\u4e48 \u5634\uff1f\u6765\uff0c\u5feb\u6765\u62dc\u5e08\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u5927\u58f0\u9053\uff1a\u201c\u5927\u4e3b\u8003\uff0c\u4f60\u65e2\u7136\u662f\u4e2a\u7ae5\u5b50\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u81ea\u79f0\u8001\u673d\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u547c\u5438\u4e00\u7a92\uff0c\u5e72\u8106\u4e0d\u7406\u4f1a\u8881\u5c0f\u9e64\uff0c\u518d\u5ea6\u53eb\u9053\uff1a\u201c\u542c\u89c1\u6ca1\u6709\uff0c\u5feb\u6765\u62dc\u5e08\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u62ac\u624b\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u6162\u6761\u65af\u7406\u7684\u9053\uff1a\u201c\u665a\u8f88\u8033\u6735\u4e0d\u804b\uff0c\u7ae5\u5b50\u7684\u58f0\u97f3\u8fd9\u4e48\u5927\uff0c\u600e\u4f1a\u542c \u4e0d\u89c1\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u987e\u4e0d\u5f97\u5cb3\u5c0f\u98de\u4e5f\u79f0\u4ed6\u4e3a\u7ae5\u5b50\u7684\u96be\u582a\uff0c\u62ac\u624b\u62cd\u7740\u684c\u5b50\u543c\u9053\uff1a\u201c\u542c\u89c1\u4e86\u4e3a\u4ec0\u4e48\u4e0d\u6765 \u62dc\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53cd\u800c\u7a33\u5f97\u5f88\uff0c\u4e0d\u614c\u4e0d\u5fd9\u9053\uff1a\u201c\u7ae5\u4e8e\u8bf7\u522b\u8fd9\u6837\u6025\u8e81\uff0c\u6211\u8fd8\u6709\u8bdd\u8981\u8bf4\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u6c14\u6025\u8d25\u574f\u9053\uff1a\u201c\u597d\u554a\uff0c\u628a\u8001\u673d\u7531\u5927\u4e3b\u8003\u6539\u79f0\u7ae5\u5b50\uff0c\u4f60\u81ea\u5df1\u5374\u7531\u665a\u8f88\u6539\u6210\u4e86\u6211\uff0c\u8fde \u4e00\u70b9\u793c\u8c8c\u90fd\u6ca1\u6709\u4e86\u4e48\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5148\u524d\u627e\u8fd8\u4ee5\u4e3a\u4f60\u662f\u51a0\u8005\uff0c\u6240\u4ee5\u624d\u81ea\u79f0\u665a\u8f88\uff0c\u73b0\u5728\u77e5\u9053\u4f60\u4e5f\u662f\u7ae5\u5b50\uff0c\u5f7c\u6b64\u90fd \u662f\u4e00\u6837\uff0c\u8fd8\u54ea\u91cc\u6765\u7684\u665a\u8f88\uff1f\u4f60\u4e3a\u4ec0\u4e48\u548c\u6211\u8bf4\u8bdd\u4e0d\u81ea\u79f0\u665a\u8f88\u5462\uff1f\u4f60\u521a\u624d\u5df2\u7ecf\u8d5a\u4e86\u6211\u4e0d\u5c11\u4fbf\u5b9c\uff0c \u8fde\u6211\u90fd\u4e0d\u8ba1\u8f83\uff0c\u4f60\u53cd\u800c\u8ba1\u8f83\uff0c\u540a\u4e2a\u574e\u513f\uff0c\u8fd9\u4e0d\u662f\u732a\u516b\u6212\u4e0b\u5c71\u2014\u2014\u5012\u6253\u4e00\u8019\u4e48\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u6c14\u5f97\u8138\u8272\u7531\u7ea2\u53d8\u7d2b\uff0c\u4e9a\u8d5b\u732a\u809d\uff0c\u7528\u529b\u7684\u6446\u7740\u624b\u9053\uff1a\u201c\u597d\u4e86\u597d\u4e86\uff0c\u522b\u8bf4\u9898\u5916\u8bdd\uff0c\u53ea \u628a\u4f60\u4e0d\u62dc\u8001\u673d\u7684\u73b0\u7531\u8bf4\u51fa\u6765\u5c31\u597d\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u5148\u8bf7\u95ee\u6768\u7ae5\u5b50\u4e00\u4ef6\u4e8b\uff0c\u5a36\u5ab3\u5987\u7684\u5e74\u9f84\u6709\u6ca1\u6709\u9650\u5236\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u89c1\u5bf9\u65b9\u4e0d\u4f46\u4ecd\u79f0\u81ea\u5df1\u4e3a\u7ae5\u5b50\uff0c\u800c\u4e14\u8fde\u59d3\u4e5f\u52a0\u4e0a\u53bb\uff0c\u6c14\u5f97\u4ed6\u7b80\u76f4\u8981\u53d1\u660f\uff0c\u4f46\u4ed6\u987e\u53ca \u8eab\u4efd\uff0c\u4e0d\u80fd\u592a\u8fc7\u5931\u6001\uff0c\u53ea\u6253\u6781\u529b\u9690\u5fcd\u7740\uff0c\u800c\u4e14\u731b\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u501f\u4ee5\u7a33\u5b9a\u60c5\u7eea\uff0c\u53c8\u5e72\u54b3\u4e86\u4e00\u58f0 \u624d\u8bf4\uff1a\u201c\u5f53\u7136\u6ca1\u6709\u9650\u5236\uff0c\u5c31\u4ee5\u8001\u673d\u6765\u8bf4\uff0c\u5feb\u4e03\u5341\u4e86\uff0c\u5982\u679c\u6709\u5408\u9002\u7684\uff0c\u8fd8\u4e0d\u662f\u7167\u6837\u53ef\u4ee5\u5a36\u4e00\u4e2a\uff0c \u8fde\u516b\u4e5d\u5341\u5c81\u5a36\u59bb\u7684\u4e5f\u4e0d\u7b97\u7a00\u5947\uff0c\u5c31\u662f\u7687\u5e1d\u8001\u513f\uff0c\u4e5f\u4e0d\u80fd\u9650\u5236\u522b\u4eba\u5a36\u8001\u5a46\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u9886\u6559\u4e86\uff0c\u518d\u95ee\u6768\u7ae5\u5b50\u4e00\u4ef6\u4e8b\uff0c\u5b54\u95e8\u5f1f\u5b50\u4e03\u5341\u4e8c\u8d24\uff0c\u5e74\u9f84\u662f\u5426\u90fd\u6bd4\u5b54\u5b50\u5927\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u9664\u4e86\u66fe\u5b50\u7684\u7236\u4eb2\u66fe\u70b9\u636e\u8bf4\u6bd4\u5b54\u5b50\u7684\u5e74\u9f84\u7a0d\u5927\uff0c\u5176\u4f59\u7684\u5b66\u751f\uff0c\u5e74 \u9f84\u5f53\u7136\u90fd\u6bd4\u8001\u5e08\u5c0f\uff0c\u8fde\u989c\u56de\u7684\u7236\u4eb2\u989c\u8def\uff0c\u8fd8\u6bd4\u5b54\u5b50\u5c0f\u516d\u5c81\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fd9\u5c31\u5bf9\u4e86\uff0c\u989c\u56de\u6b7b\u7684\u65f6\u5019\u662f\u4e8c\u5341\u4e8c\u5c81\uff0c\u518d\u8fc7\u4e24\u5e74\uff0c\u5b54\u5b50\u53bb\u4e16\uff0c\u5b54\u5b50\u6d3b\u4e86\u4e03 \u5341\u4e09\uff0c\u53ef\u89c1\u5b54\u5b50\u8981\u6bd4\u989c\u56de\u5927\u4e86\u5c06\u8fd1\u56db\u5341\u5c81\uff0c\u5728\u4e03\u5341\u4e8c\u8d24\u4e2d\uff0c\u5fc5\u5b9a\u8fd8\u6709\u6bd4\u989c\u56de\u5e74\u9f84\u66f4\u5c0f\u7684\uff0c\u5bf9 \u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u9053\uff1b\u201c\u90a3\u5f53\u7136\u4f1a\u6709\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1b\u201c\u518d\u95ee\u6768\u7ae5\u5b50\uff0c\u4f60\u53ef\u77e5\u9053\u5b54\u592b\u5b50\u8bf4\u2018\u5f31\u51a0\u4e94\u516d\u4eba\uff0c\u7ae5\u5b50\u516d\u4e03\u4eba\u2019\u90a3\u53e5\u8bdd\u65f6\u662f \u591a\u5927\u5e74\u7eaa\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u6123\u4e86\u6123\u9053\uff1a\u201c\u8bba\u8bed\u4e0a\u6ca1\u8bb0\u8f7d\uff0c\u8001\u673d\u600e\u53ef\u4e71\u731c\uff0c\u4e8b\u60c5\u9694\u4e86\u4e24\u5343\u591a\u5e74\uff0e\u5373\u4fbf\u627e\u4e13\u5bb6\u67e5 \u4e5f\u67e5\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u53c8\u8bf4\u5bf9\u4e86\uff0c\u7531\u4e66\u4e0a\u53ef\u4ee5\u770b\u51fa\uff0c\u5b54\u5b50\u662f\u5174\u81f4\u52c3\u52c3\u7684\u8981\u53bb\u8e0f\u9752\uff0c\u9884\u6599 \u4e2d\u4e00\u5b9a\u4e0d\u662f\u665a\u5e74\uff0c\u4e5f\u8bb8\u662f\u56db\u4e94\u5341\u5c81\u5de6\u53f3\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u4e0d\u77e5\u5df2\u88ab\u5bf9\u65b9\u6b65\u6b65\u5957\u7262\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e5f\u8bb8\u5c31\u662f\u8fd9\u79cd\u5e74\u7eaa\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u518d\u9053\uff0c\u201c\u5b54\u592b\u5b50\u5728\u56db\u4e94\u5341\u5c81\u65f6\uff0c\u4ed6\u7684\u5b66\u751f\u5f53\u7136\u5e74\u7eaa\u66f4\u5c0f\uff0c\u5927\u7684\u4e5f\u4e0d\u8fc7\u4e8c\u4e09\u5341\u5c81\u5230 \u56db\u5341\u5c81\uff0c\u5c0f\u7684\u591a\u534a\u662f\u4e03\u516b\u5c81\u5230\u5341\u51e0\u5c81\uff0c\u6240\u4ee5\u90a3\u65f6\u5728\u4ed6\u8001\u4eba\u5bb6\u4e03\u5341\u4e8c\u4f4d\u5f97\u610f\u5f1f\u5b50\u4e2d\uff0c\u4e00\u70b9\u4e5f\u4e0d \u9519\u662f\u5a36\u5ab3\u5987\u7684\u4e09\u5341\u4f4d\uff0c\u6ca1\u5a36\u5ab3\u5987\u7684\u56db\u5341\u4e8c\u4f4d\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u542c\u5f97\u7531\u6012\u8f6c\u559c\uff0c\u7acb\u523b\u53eb\u9053\uff1b\u201c\u4f60\u65e2\u7136\u627f\u8ba4\u8001\u673d\u7684\u8bdd\u6ca1\u9519\uff0c\u9664\u4e86\u62dc\u5e08\uff0c\u8fd8\u6709\u4ec0\u4e48\u53ef \u89e3\u91ca\u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u77a5\u4e86\u6768\u8001\u5934\u4e00\u773c\u9053\uff1a\u201c\u6768\u7ae5\u5b50\u8bf7\u51b7\u9759\u4e00\u4e0b\uff0c\u521a\u624d\u6240\u8c08\u7684\uff0c\u662f\u5b54\u592b\u5b50\u5728\u8bf4\u90a3\u53e5\u8bdd\u7684 \u5f53\u65f6\u60c5\u5f62\uff0c\u53ef\u662f\u8fd9\u56db\u5341\u4e8c\u4f4d\u7ae5\u5b50\u4ee5\u540e\u5fc5\u5b9a\u4f1a\u957f\u5927\uff0c\u957f\u5927\u4e86\u5c31\u4f1a\u5a36\u5ab3\u5987\uff0c\u7eb5\u7136\u4e5f\u6709\u50cf\u4f60\u8fd9\u6837\u4e03 \u5341\u5c81\u8fd8\u6253\u5149\u68cd\u7684\uff0c\u90a3\u4e5f\u53ea\u662f\u5c11\u6570\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u4f60\u521a\u624d\u6240\u8bf4\u7684\u4e09\u5341\u51a0\u8005\uff0c\u56db\u5341\u4e8c\u7ae5\u5b50\uff0c\u5c82\u4e0d\u5c31 \u5b8c\u5168\u63a8\u7ffb\u4e86\uff0c\u65e2\u7136\u4f60\u8bf4\u7684\u4e0d\u5bf9\uff0c\u6211\u8fd8\u62dc\u7684\u4ec0\u4e48\u5e08\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u987f\u65f6\u50bb\u4e86\u773c\uff0c\u50f5\u5728\u5f53\u573a\u534a\u5929\uff0c\u624d\u543c\u7740\u9053\uff1a\u201c\u5b54\u592b\u5b50\u90a3\u4e24\u53e5\u8bdd\u662f\u4e34\u6b7b\u7684\u65f6\u5019\u8bf4\u7684\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b11\u9053\uff1a\u201c\u5c31\u7b97\u662f\u5b54\u592b\u5b50\u901d\u4e16\u524d\u8bf4\u7684\uff0c\u7167\u6837\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u201c\u4e03\u5341\u4e8c\u8d24\u6709\u597d\u51e0\u4f4d\u662f\u5728\u5b54\u5b50\u6b7b\u524d\u53bb\u4e16\u7684\uff0c\u50cf\u5b54\u5b50\u7684\u513f\u5b50\u4f2f\u9c7c\uff0c\u4ee5\u53ca\u989c\u56de\u548c\u5b50\u8def\u5728\u4e66\u4e0a \u90fd\u6709\u8bb0\u8f7d\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u90a3\u5c31\u6ca1\u6709\u4e03\u5341\u4e8c\u4f4d\u4e86\uff0c\u53c8\u54ea\u91cc\u6765\u7684\u4e09\u5341\u548c\u56db\u5341\u4e8c\uff0c\u96be\u9053\u4e07\u4e16\u5e08\u8868\u53c8\u7cbe \u901a\u793c\u3001\u4e50\u3001\u5c04\u3001\u5fa1\u3001\u4e66\u3001\u6570\u516d\u827a\u7684\u5b54\u592b\u5b50\uff0c\u8fde\u8fd9\u6837\u7b80\u5355\u7684\u6570\u76ee\u90fd\u7b97\u4e0d\u6e05\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u88ab\u903c\u6025\u4e86\uff0c\u53c8\u4e0d\u80af\u8ba4\u8f93\uff0c\u53e3\u4e0d\u62e9\u8a00\u9053\uff1a\u201c\u5b54\u592b\u5b50\u53bb\u4e16\u524d\u5df2\u7ecf\u5934\u8111\u4e0d\u6e05\uff0c\u5f53\u7136\u4e5f\u5c31 \u7b97\u4e0d\u6e05\u6570\u76ee\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53cc\u76ee\u5c04\u5149\uff0c\u6717\u58f0\u9053\uff1a\u201c\u6211\u770b\u4f60\u624d\u662f\u5934\u8111\u4e0d\u6e05\uff0c\u5c82\u6b62\u4e0d\u6e05\uff0c\u7b80\u76f4\u5df2\u7ecf\u53d1\u660f\u4e86\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u556e\u7259\u88c2\u5634\u7684\u987f\u4e86\u987f\u9053\uff1a\u201c\u8001\u673d\u8981\u6c42\u66f4\u6b63\uff0c\u5b54\u5b50\u6ca1\u7b97\u9519\u6570\u76ee\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u6b7b\u53bb\u7684\u90a3\u51e0\u4f4d\u5462\uff1f\u201d \u3000\u3000\u201c\u6b7b\u4e00\u4e2a\u8865\u4e00\u4e2a\uff0c\u4ed6\u4eec\u662f\u6709\u902e\u8865\u5236\u5ea6\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e5f\u4e0d\u662f\u4ec0\u4e48\u4ee3\u8868\uff0c\u54ea\u91cc\u6765\u7684\u902e\u8865\u5236\u5ea6\uff1f\u4f60\u6768\u7ae5\u5b50\u902e\u8865\u8fdb\u53bb\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u867d\u65e9\u5df3\u7406\u5c48\u8bcd\u7a77\uff0c\u5374\u5c31\u662f\u4e0d\u80af\u8ba4\u8f93\uff0c\u6240\u4ee5\u624d\u4f1a\u72d7\u6025\u8df3\u5899\u822c\u7684\u80e1\u626f\u4e00\u901a\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\u4ed6\u4e5f\u5e76\u975e\u4e0d\u60f3\u627e\u673a\u4f1a\u4e0b\u53f0\uff0c\u4f46\u504f\u504f\u6ca1\u673a\u4f1a\uff0c\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u53c8\u600e\u80fd\u4e0d\u765e\u86e4\u87c6\u57ab\u684c \u5b50\u817f\u2014\u4e00\u786c\u6491\u3002 \u3000\u3000\u8fd8\u597d\uff0c\u5c31\u5728\u4ed6\u53c8\u6025\u53c8\u7f9e\u53c8\u6068\u4e0d\u5f97\u5730\u4e0a\u6709\u6d1e\u94bb\u8fdb\u53bb\u7684\u65f6\u5019\uff0c\u767d\u8863\u8001\u8005\u5df2\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u767d\u8863\u8001\u8005\u62cd\u62cd\u4ed6\u7684\u80a9\u8180\u9053\uff1a\u201c\u51e4\u5802\u5144\uff0c\u8be5\u8ba4\u8f93\u7684\u65f6\u5019\u5c31\u8981\u8ba4\u8f93\uff0c \u3000\u3000\u4f60\u5148\u524d\u672c\u6765\u5c31\u662f\u4e00\u756a\u6b6a\u7406\uff0c\u6b6a\u7406\u6ca1\u5173\u7cfb\uff0c\u5b9e\u5728\u4e0d\u8be5\u8bf4\u5b54\u592b\u5b50\u5934\u8111\u4e0d\u6e05\uff0c\u4f60\u4e5f\u662f\u4e2a\u8bfb\u4e66\u4eba\uff0c \u8bfb\u4e66\u4eba\u4e0d\u77e5\u5c0a\u5e08\u91cd\u9053\uff0c\u5c31\u662f\u5927\u9006\u4e0d\u9053\uff0c\u8fde\u5144\u5f1f\u6211\u542c\u4e86\u4f60\u521a\u624d\u90a3\u513f\u53e5\u8bdd\u90fd\u6709\u6c14\uff0c\u4f60\u53ef\u4ee5\u4e0d\u656c\u5b54 \u5b50\uff0c\u6211\u53ef\u4e0d\u80fd\u4e0d\u656c\u5b54\u5b50\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u771f\u6ca1\u6599\u5230\u8fde\u767d\u8863\u8001\u8005\u4e5f\u5728\u8bad\u4ed6\uff0c\u4e0d\u8fc7\u4ed6\u5e76\u672a\u518d\u607c\uff0c\u53cd\u800c\u611f\u6fc0\u767d\u8863\u8001\u8005\u4e3a\u4ed6\u89e3\u56f4\uff0c \u5f53\u5373\u4e00\u62cd\u5927\u817f\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u54ea\u91cc\u6562\u9a82\u5b54\u5b50\uff0c\u90a3\u662f\u88ab\u4ed6\u903c\u6025\u624d\u95e8\u4e0d\u62e9\u8a00\u7684\u3002\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u653e\u7f13\u6d6f\u6c14\u9053\uff1a\u201c\u4f60\u80af\u8ba4\u9519\u5c31\u597d\u4e86\uff0c\u8fd9\u624d\u79f0\u5f97\u4e0a\u662f\u8c26\u8c26\u541b\u5b50\u3002\u201d \u3000\u3000\u8bf4\u7740\u8f6c\u5934\u9053\uff1a\u201c\u4f60\u4eec\u56db\u4f4d\u5df2\u7ecf\u8fc7\u4e86\u5173\uff0c\u4e0d\u8d70\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u3002\u8881\u5c0f\u9e64\u3001\u4e01\u6d9b\u3001\u7518\u9716\u7b49\u4eba\u4e5f\u4e0d\u60f3\u518d\u7ed9\u6768\u8001\u5934\u96be\u582a\uff0c\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u4e00\u58f0\u4e0d\u54cd\u7684\u8d70 \u4e86\u51fa\u53bb\u3002 \u3000\u3000\u6768\u8001\u5934\u773c\u770b\u56db\u4eba\u8d70\u51fa\u4e86\u77f3\u5ba4\uff0c\u624d\u53c8\u62cd\u4e86\u4e00\u4e0b\u684c\u5b50\u9053\uff1a\u201c\u683d\u5728\u4e00\u4e2a\u4e73\u81ed\u672a\u5e72\u7684\u6bdb\u5934\u5c0f\u5b50\u624b \u91cc\uff0c\u6211\u6768\u51e4\u5802\u4eca\u540e\u8fd8\u6709\u4f55\u9762\u76ee\u518d\u89c1\u6c5f\u4e1c\u7236\u8001\uff01\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6ca1\u9762\u76ee\u89c1\u6c5f\u4e1c\u7236\u8001\u4f60\u5c31\u5230\u4e4c\u6c5f\u53bb\u81ea\u520e\u7b97\u4e86\uff0c\u6211\u770b\u4f60\u4e5f\u6ca1\u6709\u90a3\u4efd \u52c7\u6c14\uff01\u201d \u3000\u3000\u6768\u8001\u5934\u53c8\u53f9\u53e3\u6c14\uff0c\u7d22\u6027\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u767d\u8863\u8001\u8005\u518d\u62cd\u4e86\u4ed6\u4e00\u4e0b\u80a9\u8180\u9053\uff1a\u201c\u7528\u4e0d\u7740\u628a\u8fd9\u4e8b\u653e\u5728\u5fc3\u91cc\uff0c\u4f60\u65e2\u7136\u81ea\u8ba4\u8fd8\u662f\u4e2a\u7ae5\u5b50\uff0c\u5c31 \u8be5\u548c\u7ae5\u5b50\u4e00\u6837\uff0c\u522b\u8bf4\u53ea\u662f\u8ba9\u4eba\u5bb6\u8bb2\u4e86\u51e0\u53e5\uff0c\u5373\u4fbf\u88ab\u4eba\u6253\u4e86\u88ab\u4eba\u9a82\u4e86\uff0c\u8fd8\u4e0d\u7167\u6837\u4e00\u8f6c\u773c\u5c31\u6ca1\u4e8b \u4e86\u3002\u201d \u3000\u3000\u6768\u8001\u5934\u543c\u9053\uff1a\u201c\u8fd8\u7ae5\u5b50\uff1f\u90fd\u5feb\u4e03\u5341\u4e86\uff0c\u4f60\u4ec0\u4e48\u65f6\u5019\u89c1\u8fc7\u4e03\u5341\u5c81\u7684\u7ae5\u5b50\uff1f\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u4eba\u751f\u4e03\u5341\u624d\u5f00\u59cb\uff0c\u4f60\u61c2\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u6768\u8001\u5934\u731b\u8dfa\u4e00\u811a\u9053\uff1a\u201c\u5f00\u59cb\u4ec0\u4e48\uff1f\u4eca\u5e74\u5f00\u59cb\uff0c\u660e\u5e74\u5c31\u7fd8\u8fab\u5b50\u4e86\uff01\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u4f60\u65e2\u7136\u77e5\u9053\u5df2\u7ecf\u6d3b\u4e0d\u4e86\u51e0\u5e74\uff0c\u5c31\u66f4\u4e0d\u5e94\u8be5\u751f\u6c14\uff0c\u6700\u540e\u7684\u5149\u9634\uff0c\u5fc5\u987b\u597d\u597d \u628a\u63e1\uff0c\u505a\u4e2a\u6c14\u6b7b\u9b3c\u591a\u5212\u4e0d\u6765\uff0c\u4f60\u8bf4\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b49\u56db\u4eba\u8d70\u51fa\u96a7\u9053\uff0c\u624d\u77e5\u9053\u5929\u8272\u5df2\u665a\uff0c\u56e0\u4e3a\u77f3\u5ba4\u548c\u96a7\u9053\u90fd\u70b9\u7740\u706f\uff0c\u4f7f\u4ed6\u4eec\u51e0\u4e4e\u5fd8\u4e86 \u662f\u4ec0\u4e48\u65f6\u95f4\u3002 \u3000\u3000\u65e2\u7136\u5929\u5df2\u5165\u591c\uff0c\u4ed6\u4eec\u4e0d\u5f97\u4e0d\u653e\u7f13\u811a\u6b65\u524d\u8fdb\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u60f3\u4e0d\u5230\u4f60\u8fd8\u771f\u4f1a\u5356\u5173\u5b50\uff0c\u5148\u524d\u628a\u6211\u6025\u5f97\u4e0d\u5f97\u4e86\uff0c\u539f\u6765\u4f60\u7684\u597d\u620f\u7adf\u5728 \u540e\u9762\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1b\u201c\u8881\u5927\u54e5\uff0c\u4f60\u8ba4\u4e3a\u6768\u8001\u5934\u513f\u7684\u5b66\u95ee\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u90a3\u8001\u5c0f\u5b50\u7684\u786e\u535a\u5b66\u591a\u624d\uff0c\u4e66\u8bfb\u5f97\u4e0d\u5c11\u3002\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u5374\u8ba4\u4e3a\u4ed6\u662f\u8bfb\u4e66\u4e0d\u6c42\u751a\u89e3\u3002\u201d \u3000\u3000\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u201c\u4ed6\u628a\u8bba\u8bed\u4e0a\u90a3\u51e0\u53e5\u8bdd\u5f53\u6210\u662f\u5b54\u5b50\u8bb2\u7684\uff0c\u4e00\u5f00\u59cb\u5c31\u5927\u9519\u7279\u9519\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u539f\u6765\u90a3\u51e0\u53e5\u8bdd\u4e0d\u662f\u5b54\u5b50\u8bb2\u7684\uff1f\u90a3\u53c8\u662f\u8c01\u8bb2\u7684\u5462\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u66fe\u53c2\u7684\u7236\u4eb2\u66fe\u54f2\uff0c\u4e5f\u5c31\u662f\u66fe\u70b9\u8bb2\u7684\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u5c34\u5c2c\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u8fde\u6211\u4e5f\u662f\u8bfb\u4e66\u4e0d\u6c42\u751a\u89e3\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8881\u5927\u54e5\u53ef\u80fd\u5df2\u7ecf\u5f88\u4e45\u6ca1\u518d\u770b\u8bba\u8bed\uff0c\u96be\u514d\u4f1a\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u201c\u4f60\u4e0d\u59a8\u628a\u8bba\u8bed\u4e0a\u7684\u60c5\u8282\u518d\u63d0\u4e00\u63d0\uff0c\u514d\u5f97\u6211\u4ee5\u540e\u8bf4\u9519\u4e86\uff01\u201d \u3000\u3000\u201c\u5f53\u65f6\u662f\u5b50\u8def\u3001\u66fe\u7699\u3001\u5189\u6709\u3001\u516c\u897f\u534e\u56db\u4eba\u966a\u4f8d\u5b54\u5b50\uff0c\u5b54\u5b50\u8981\u4ed6\u4eec\u5404\u81ea\u8bf4\u51fa\u81ea\u5df1\u7684\u5fd7\u613f\u6216 \u5fd7\u8da3\u3002\u5f53\u65f6\u4ed6\u8bf4\u51fa\u4e4b\u540e\uff0c\u5b54\u5b50\u72ec\u5bf9\u66fe\u7699\u7684\u90a3\u6bb5\u8bdd\u8868\u793a\u8d5e\u8d4f\u3002\u201d \u3000\u3000\u201c\u7167\u8fd9\u6837\u8bf4\uff0c\u6768\u8001\u5934\u513f\u628a\u90a3\u6bb5\u8bdd\u5f53\u6210\u662f\u5b54\u5b50\u8bf4\u7684\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u5927\u9519\uff0c\u56e0\u4e3a\u8bba\u8bed\u4e0a\u7684\u8bdd\uff0c\u5927 \u534a\u662f\u5b54\u5b50\u8bf4\u7684\uff0c\u8c01\u80fd\u8bb0\u5f97\u90a3\u4e48\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u90a3\u7ec8\u7a76\u662f\u66fe\u7699\u8bf4\u7684\uff0c\u5e76\u975e\u51fa\u4e8e\u5b54\u5b50\u53e3\u4e2d\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5f53\u573a\u6307\u7834\u6768\u8001\u5934\u513f\u8bf4\u9519\u4e86\u5462\uff1f\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u4e00\u5f00\u59cb\u4e0d\u77e5\u9053\u6768\u8001\u5934\u513f\u662f\u6545\u610f\u6574\u4eba\uff0c\u4e3a\u4e86\u7ed9\u4ed6\u7559\u9762\u5b50\uff0c \u3000\u3000\u600e\u597d\u5f53\u9762\u6307\u51fa\u4ed6\u7684\u9519\u8bef\u3002\u201d \u3000\u3000\u201c\u540e\u6765\u5c31\u8be5\u6307\u7834\u624d\u5bf9\uff01\u201d \u3000\u3000\u201c\u540e\u6765\u5df2\u7ecf\u9519\u5230\u5e95\u4e86\uff0c\u82e5\u518d\u6307\u51fa\uff0c\u5c82\u4e0d\u8fde\u5c0f\u5f1f\u4e5f\u53d8\u6210\u4e86\u540e\u77e5\u540e\u89c9\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8bed\u6c14\u7a0d\u6b47\uff0c\u53c8\u9053\uff1a\u201c\u5176\u5b9e\u8bfb\u4e66\u4e0d\u6c42\u751a\u89e3\u7684\u592a\u591a\u4e86\uff0c\u5c31\u4ee5\u56db\u4e66\u4e0a\u53e6\u5916\u7684\u4e00\u53e5\u6d3b\u6765\u8bf4 \u5427\uff0c\u81f3\u5c11\u5341\u4e2a\u4eba\u4e5d\u4e2a\u4f1a\u628a\u8bf4\u8fd9\u8bdd\u7684\u4eba\u5f04\u9519\uff0c\u800c\u4e14\u9519\u5f97\u4e0d\u53ef\u539f\u8c05\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u54ea\u4e00\u53e5\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4eba\u4eba\u90fd\u77e5\u9053\u7684\u4e00\u53e5\u8bdd\uff0c\u5c31\u662f\u2018\u98df\u8272\u6027\u4e5f\u2019\u8fd9\u53e5\u8bdd\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u6211\u5f53\u662f\u54ea\u53e5\uff0c\u98df\u8272\u6027\u4e5f\u8fd9\u56db\u4e2a\u5b57\uff0c\u8c01\u4e0d\u77e5\u9053\u662f\u5b54\u5b50\u8bf4\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u7b11\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0c\u4f60\u7167\u6837\u4e5f\u5f04\u9519\u4e86\uff0c\u5b54\u5b50\u4ec0\u4e48\u65f6\u5019\u8bf4\u8fc7\u8fd9\u6837\u7684\u8bdd\uff1f\u4f60\u4e0d\u59a8\u518d\u8bfb \u4e00\u6b21\u8bba\u8bed\uff0c\u8bba\u8bed\u4e0a\u7edd\u5bf9\u627e\u4e0d\u51fa\u8fd9\u53e5\u8bdd\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6123\u6123\u7684\u9053\uff1b\u201c\u90a3\u4e48\u8fd9\u53e5\u8bdd\u662f\u51fa\u5728\u56db\u4e66\u7684\u90a3\u4e00\u672c\u4e66\u4e0a\uff1f\u201d \u3000\u3000\u201c\u5b5f\u5b50\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u5b5f\u5b50\u8bf4\u7684\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u662f\u3002\u201d \u3000\u3000\u201c\u5230\u5e95\u662f\u51c6\uff1f\u201d \u3000\u3000\u201c\u544a\u5b50\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u662f\u544a\u5b50\u8bf4\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5f53\u7136\u8fd9\u53e5\u8bdd\u4e5f\u662f\u8a00\u4e4b\u6210\u7406\uff0c\u4f46\u5b54\u5b50\u5374\u7edd\u5bf9\u4e0d\u5c51\u8bf4\uff0c\u5723\u4eba\u548c\u4e00\u822c\u4eba\u5dee\u522b\u5c31\u5728\u8fd9 \u91cc\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u8eab\u540e\u54cd\u8d77\u6025\u4fc3\u7684\u811a\u6b65\u58f0\u3002 \u3000\u3000\u63a5\u7740\u4e00\u4e2a\u719f\u6089\u7684\u58f0\u97f3\u558a\u9053\uff1a\u201c\u56db\u4e2a\u6162\u8d70\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b49\u56db\u4eba\u6b62\u6b65\u56de\u8eab\u3002 \u3000\u3000\u867d\u7136\u591c\u91cc\u770b\u4e0d\u6e05\u6765\u4eba\uff0c\u4f46\u4ed6\u4eec\u5374\u542c\u51fa\u662f\u767d\u8863\u8001\u8005\u3002 \u3000\u3000\u767d\u8863\u8001\u8005\u5f88\u5feb\u4fbf\u6765\u5230\u8ddf\u524d\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u62a2\u5148\u95ee\u9053\uff1a\u201c\u8001\u5148\u751f\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u8001\u673d\u5148\u524d\u5fd8\u8bb0\u544a\u8bc9\u5404\u4f4d\uff0c\u8fd9\u4e00\u8c37\u9053\u91cc\u7684\u6bcf\u4e00\u5173\u5361\uff1f\u5929\u9ed1\u4e4b\u540e\uff0c\u5173\u5361\u7684\u5927 \u95e8\u5fc5\u5b9a\u5173\u95ed\uff0c\u56db\u4f4d\u65e0\u6cd5\u8fdb\u5173\uff0c\u4fbf\u5fc5\u987b\u9732\u5bbf\u5728\u8c37\u9053\u91cc\uff0c\u90a3\u6ecb\u5473\u4e0d\u662f\u597d\u53d7\u7684\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u83ab\u975e\u8001\u5148\u751f\u662f\u8981\u5e2e\u6211\u4eec\u53eb\u5f00\u524d\u9762\u5173\u5361\u7684\u5927\u95e8\uff1f\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u6447\u5934\u9053\uff1a\u201c\u82e5\u8981\u5df2\u7ecf\u5173\u95ed\u7684\u5173\u5361\u5f00\u5173\uff0c\u5fc5\u987b\u6709\u6700\u9ad8\u6559\u4e3b\u7684\u4ee4\u8c15\uff0c\u8001\u673d\u54ea\u6709\u8fd9\u79cd \u6743\u5a01\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u8001\u5148\u751f\u53c8\u6765\u62db\u547c\u6211\u4eec\u505a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u8001\u673d\u662f\u8981\u5404\u4f4d\u518d\u56de\u5230\u5148\u524d\u90a3\u91cc\u4f4f\u4e00\u591c\uff0c\u7b49\u660e\u5929\u5929\u4eae\u540e\u518d\u8fc7\u4e0b\u4e00\u5173\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u5fd9\u62e2\u624b\u9053\uff1a\u201c\u8fd9\u6837\u4e0d\u59a5\uff0c\u665a\u8f88\u4eec\u4e0d\u60f3\u56de\u53bb\uff01\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u6123\u4e86\u4e0b\u9053\uff1a\u201c\u8001\u673d\u662f\u4e00\u756a\u597d\u610f\u624d\u6765\u627e\u4f60\u4eec\u56de\u53bb\uff0c\u96be\u9053\u4f60\u4eec\u613f\u610f\u81ea\u627e\u7f6a\u53d7\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u662f\u8fd9\u610f\u601d\uff0c\u800c\u662f\u4e0d\u60f3\u518d\u8ba9\u90a3\u4f4d\u5927\u4e3b\u8003\u96be\u582a\uff0c\u8bd5\u60f3\u6211\u4eec\u56de\u53bb\u548c\u4ed6\u4f4f\u5728 \u4e00\u8d77\uff0c\u90a3\u4f4d\u5927\u4e3b\u8003\u6709\u591a\u522b\u626d\u3002\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u7b11\u9053\uff1a\u201c\u539f\u6765\u4e3a\u7684\u662f\u8fd9\u4e2a\uff0c\u5176\u5b9e\u4f60\u4eec\u56de\u53bb\uff0c\u6839\u672c\u7528\u4e0d\u7740\u548c\u4ed6\u89c1\u9762\u3002\u201d \u3000\u3000\u201c\u90a3\u8fb9\u4e0d\u662f\u53ea\u6709\u4e00\u95f4\u77f3\u5ba4\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f4f\u7684\u5730\u65b9\u591a\u5f97\u5f88\uff0c\u82e5\u53ea\u6709\u4e00\u95f4\u77f3\u5ba4\uff0c\u90a3\u8fd8\u529e\u7684\u4ec0\u4e48\u4e8b\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u665a\u8f88\u660e\u660e\u770b\u5230\u53ea\u6709\u4e00\u95f4\u77f3\u5ba4\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u8ddf\u8001\u673d\u6765\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u534a\u4fe1\u534a\u7591\uff0c\u65e2\u7136\u6709\u5730\u65b9\u4f4f\uff0c\u5f53\u7136\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u4e8e\u662f\uff0c\u767d\u8863\u8001\u8005\u5e26\u8def\uff0c\u56db\u4eba\u8ddf\u5728\u540e \u9762\u3002 \u3000\u3000\u5f88\u5feb\u4fbf\u56de\u5230\u5148\u524d\u90a3\u6bb5\u96a7\u9053\u3002 \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u8fd9\u65f6\u4e24\u8fb9\u6d1e\u58c1\u4e0a\u7adf\u591a\u4e86\u597d\u51e0\u5904\u6d1e\u95e8\u3002 \u3000\u3000\u767d\u8863\u8001\u8005\u968f\u624b\u63a8\u5f00\u4e00\u5904\u6d1e\u95e8\u9053\uff1a\u201c\u56db\u4f4d\u4eca\u665a\u5c31\u7761\u5728\u8fd9\u91cc\u5427\uff0c\u5148\u770b\u770b\u4e2d\u610f\u4e0d\u4e2d\u610f\uff0c\u4e0d\u4e2d\u610f \u53ef\u4ee5\u518d\u6362\u4e00\u95f4\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u7b49\u53eb\u4eba\u8fdb\u53bb\u4e00\u770b\uff0c\u771f\u60f3\u4e0d\u5230\uff0c\u5c71\u6d1e\u91cc\u7adf\u7136\u6709\u5982\u6b64\u8c6a\u534e\u7684\u5e03\u7f6e\u3002 \u3000\u3000\u53ea\u89c1\u56db\u58c1\u90fd\u8d34\u7740\u7eff\u5e95\u7ee3\u82b1\u4e1d\u7ed2\u58c1\u6bef\uff0c\u5730\u4e0a\u5219\u662f\u5927\u7ea2\u5730\u6bef\uff0c\u56db\u89d2\u6446\u7740\u56db\u5f20\u5e8a\uff0c\u4e0d\u4f46\u5e8a\u4e0a\u94fa \u7740\u9526\u88ab\u7ee3\u8925\uff0c\u800c\u4e14\u8fd8\u6709\u4ece\u6d1e\u9876\u5782\u7f69\u4e0b\u6765\u7684\u7c89\u7ea2\u8272\u7eb1\u5e10\u3002 \u3000\u3000\u5728\u6d1e\u5185\u6b63\u4e2d\uff0c\u653e\u7740\u4e00\u5f20\u6a80\u6728\u5706\u684c\uff0c\u56db\u5468\u6446\u7740\u56db\u4e2a\u7ee3\u58a9\uff0c\u4e24\u76cf\u5927\u578b\u5782\u7a23\u5bab\u706f\uff0c\u9ad8\u60ac\u4e0a\u65b9\uff0c \u7167\u5f97\u6d1e\u5185\u660e\u4eae\u5982\u663c\u3002 \u3000\u3000\u5982\u6b64\u7684\u8c6a\u534e\u6446\u8bbe\uff0c\u5373\u4fbf\u5728\u5927\u57ce\u9547\u7684\u5ba2\u6808\u91cc\u4e5f\u96be\u5f97\u4e00\u89c1\uff0c\u600e\u4e0d\u4ee4\u8881\u5c0f\u9e64\u7b49\u56db\u4eba\u90fd\u5927\u611f\u60ca\u5f02 \u83ab\u540d\u3002 \u3000\u3000\u53ea\u542c\u4e01\u6d9b\u6b49\u7136\u7b11\u9053\uff1a\u201c\u597d\u4ec0\u4e48\u597d\uff0c\u6211\u4eec\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u4e0d\u61c2\u793c\u8c8c\uff0c\u5dee\u70b9\u513f\u628a\u4e3b\u8003\u5b98\u6c14\u6b7b\u3002\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u90a3\u662f\u4ed6\u81ea\u5df1\u627e\u7684\uff0c\u6574\u4eba\u8005\u4eba\u4ea6\u6574\u4e4b\uff0c\u6c14\u6b7b\u4e5f\u662f\u6d3b\u8be5\uff0c\u5012\u661f\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u771f \u4e0d\u6127\u662f\u5929\u4e0b\u5947\u624d\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u62b1\u4e86\u62b1\u62f3\u9053\uff1a\u201c\u8001\u5148\u751f\u8fc7\u5956\u4e86\uff01\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u56db\u4f4d\u5fc5\u5b9a\u997f\u4e86\uff0c\u8981\u4e0d\u8981\u5403\u70b9\u4e1c\u897f\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u4e0d\u6562\u6253\u6405\uff0c\u665a\u8f88\u4eec\u81ea\u5df1\u5e26\u6709\u5e72\u7cae\u3002\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u4f60\u4eec\u7684\u5e72\u7cae\u4ece\u5c71\u4e0b\u5e26\u5230\u8fd9\u91cc\uff0c\u5fc5\u5b9a\u65e9\u5df2\u53c8\u51b7\u53c8\u786c\uff0c\u600e\u80fd\u518d\u5403\u3002\u201d \u3000\u3000\u4e01\u6d9b\u62a2\u7740\u9053\uff1a\u201c\u5982\u679c\u8001\u5148\u751f\u6709\u9152\uff0c\u62ff\u4e00\u58f6\u6765\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u6709\u9152\u54ea\u80fd\u6ca1\u83dc\uff0c\u4f60\u4eec\u4e0d\u5fc5\u5ba2\u6c14\u4e86\uff0c\u8001\u673d\u9a6c\u4e0a\u4e3a\u56db\u4f4d\u628a\u9152\u996d\u9001\u6765\uff01\u201d \u3000\u3000\u4e01\u6d9b\u8f6c\u7740\u725b\u773c\u9053\uff1a\u201c\u8fd9\u91cc\u54ea\u6765\u7684\u53a8\u623f\uff1f\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u6ca1\u6709\u53a8\u623f\uff0c\u54ea\u80fd\u505a\u83dc\u505a\u996d\u3002\u201d \u3000\u3000\u4e01\u6d9b\u5fd9\u9053\uff1a\u201c\u65e2\u7136\u6709\u53a8\u623f\uff0c\u4e0d\u597d\u610f\u601d\u9ebb\u70e6\u8001\u5148\u751f\u4e3a\u6211\u4eec\u8f9b\u82e6\uff0c \u3000\u3000\u5c31\u7531\u6211\u4eec\u81ea\u5df1\u52a8\u624b\u5427\uff01\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u7b11\u9053\uff1a\u201c\u53a8\u623f\u7684\u4e8b\uff0c\u81ea\u7136\u6709\u53a8\u623f\u91cc\u7684\u4eba\u7ba1\uff0c\u8001\u673d\u53ea\u8981\u5429\u5490\u4e00\u58f0\u5c31\u6210\u4e86\uff0c\u54ea\u7528\u81ea \u5df1\u52a8\u624b\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8001\u5148\u751f\u628a\u8fd9\u6837\u597d\u7684\u5730\u65b9\u7528\u6765\u62db\u5f85\u6211\u4eec\uff0c\u53c8\u7ba1\u5403\u7ba1\u559d\uff0c \u3000\u3000\u6211\u4eec\u5b9e\u5728\u8fc7\u610f\u4e0d\u53bb\uff0c\u8981\u591a\u5c11\u94f6\u5b50\uff0c\u6211\u4eec\u4e0d\u60f3\u767d\u5403\u767d\u4f4f\u3002\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u6478\u7740\u4e0b\u5df4\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4eec\u8981\u51fa\u94f6\u5b50\uff0c\u8001\u673d\u82e5\u4e0d\u6536\uff0c\u53cd\u800c\u662f\u89c1\u5916\u4e86\uff0c\u8bf4\u5b9e\u8bdd\uff0c\u8fd9 \u91cc\u7684\u8d39\u7528\u6bd4\u5916\u9762\u8981\u8d35\u5f97\u591a\uff0c\u4e00\u5bbf\u4e00\u9910\uff0c\u6bcf\u4eba\u81f3\u5c11\u8981\u5341\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u8fd9\u6570\u76ee\u6bd4\u5728\u5916\u9762\u5ba2\u6808\u91cc\u8981\u9ad8\u51fa\u6570\u500d\uff0c\u8881\u5c0f\u9e64\u542c\u540e\u96be\u514d\u6709\u4e9b\u5fc3\u614c\uff0c\u56da\u4e3a\u4ed6\u548c\u5cb3\u5c0f\u98de\u7684\u76d8\u7f20\uff0c \u5df2\u7ecf\u6240\u5269\u4e0d\u591a\uff0c\u5fc5\u987b\u8282\u7701\u70b9\u7528\u3002 \u3000\u3000\u53ea\u89c1\u4e01\u6d9b\u62cd\u4e86\u62cd\u80f8\u9053\uff1a\u201c\u6bcf\u4eba\u5341\u4e24\uff0c\u56db\u4eba\u4e0d\u8fc7\u56db\u5341\u4e24\uff0c\u4f4f\u8fd9\u79cd\u5730\u65b9\uff0c\u6709\u8fd9\u79cd\u4eab\u53d7\uff0c\u4e00\u767e \u4e24\u90fd\u503c\u5f97\uff0c\u54b1\u6000\u91cc\u7684\u94f6\u7968\u591a\u5f97\u5f88\uff0c\u81f3\u5c11\u8fd8\u6709\u4e24\u5343\u4e24\u5b58\u5728\u8eab\u4e0a\u3002\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u7b11\u9053\uff1a\u201c\u5982\u679c\u8fd9\u4f4d\u8001\u5f1f\u8981\u591a\u7ed9\uff0c\u8001\u673d\u4e5f\u53ea\u6709\u6127\u9886\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u8f6c\u8eab\u51fa\u6d1e\uff0c\u5e76\u56de\u5934\u9053\uff1a\u201c\u5404\u4f4d\u8bf7\u7a0d\u5f85\uff0c\u9152\u996d\u9a6c\u4e0a\u5c31\u6765\u3002\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u521a\u4e00\u51fa\u95e8\uff0c\u7518\u9716\u5c31\u9a82\u9053\uff0c\u201c\u4f60\u8fd9\u5c0f\u5b50\u521a\u624d\u80e1\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u832b\u7136\u9053\uff1a\u201c\u6211\u8bf4\u4ec0\u4e48\u6765\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u6709\u9053\u662f\u8d22\u4e0d\u9732\u767d\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u628a\u8eab\u4e0a\u6709\u591a\u5c11\u94f6\u5b50\u544a\u8bc9\u4ed6\uff0c\u7b80\u76f4\u662f\u72d7\u809a\u5b50\u88c5\u4e0d \u4e86\u56db\u4e24\u6cb9\u6c34\uff01\u201d \u3000\u3000\u4e01\u6d9b\u5658\u5634\u7b11\u9053\uff1a\u201c\u539f\u6765\u4e3a\u7684\u8fd9\u4e2a\uff0c\u8001\u5b50\u5c31\u662f\u628a\u94f6\u5b50\u653e\u5728\u684c\u4e0a\uff0c \u3000\u3000\u90a3\u8001\u5bb6\u4f19\u8fd8\u6562\u62a2\u4e0d\u6210\uff01\u201d \u3000\u3000\u7518\u9716\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u6211\u770b\u4f60\u662f\u6ca1\u5403\u8fc7\u4e8f\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u5982\u679c\u8fde\u4e00\u4e2a\u8001\u5934\u5b50\u4e5f\u6015\uff0c\u54b1\u4eec\u8981\u8fd9\u4e00\u8eab\u6b66\u529f\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u8fd8\u6709\uff0c\u4eba\u5bb6\u53ea\u8981\u56db\u5341\u4e24\uff0c\u4f60\u5374\u8981\u7ed9\u4e00\u767e\u4e24\uff0c\u6211\u5c31\u6ca1\u89c1\u8fc7\u4e16\u4e0a\u8fd8\u6709\u4f60\u8fd9\u79cd\u4eba\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u51fa\u624b\u5927\u65b9\u624d\u6709\u6d3e\u5934\uff0c\u4f60\u61c2\u4e0d\u61c2\uff01\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u6211\u61c2\uff0c\u7b49\u6709\u4e00\u5929\u8eab\u4e0a\u8fde\u4e2a\u8e66\u5b50\u90fd\u6ca1\u6709\uff0c\u6211\u770b\u4f60\u8fd8\u8981\u7684\u4ec0\u4e48\u6d3e\u5934\uff01\u201d \u3000\u3000\u4e24\u4eba\u6b63\u5728\u4e89\u5435\u95f4\uff0c\u4e0d\u60f3\u767d\u8863\u8001\u8005\u53c8\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u7518\u9716\u548c\u4e01\u6d9b\u987f\u65f6\u89c9\u5f97\u6709\u4e9b\u5c34\u5c2c\uff0c\u82e5\u521a\u624d\u5435\u7684\u90a3\u4e9b\u8bdd\u88ab\u5bf9\u65b9\u542c\u5230\uff0c\u5b9e\u5728\u4e0d\u597d\u610f\u601d\u3002 \u3000\u3000\u53ea\u542c\u767d\u8863\u8001\u8005\u5411\u6d1e\u5916\u53eb\u9053\uff1a\u201c\u7aef\u8fdb\u6765\uff01\u201d \u3000\u3000\u5916\u9762\u4eba\u8fd8\u672a\u5230\uff0c\u6251\u9f3b\u7684\u83dc\u9999\u548c\u9152\u9999\u5df2\u4f20\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u63a5\u7740\uff0c\u56db\u4eba\u90fd\u611f\u773c\u775b\u4e00\u4eae\u3002 \u3000\u3000\u4ed6\u4eec\u672c\u4ee5\u4e3a\u7aef\u83dc\u6367\u6d12\u7684\u5fc5\u662f\u51e0\u4e2a\u7c97\u624b\u7c97\u811a\u7684\u6c49\u5b50\uff0c\u8c01\u77e5\u7adf\u662f\u53eb\u540d\u5a09\u5a77\u59a9\u5a9a\u3001\u7ef0\u7ea6\u591a\u59ff\u7684 \u7f8e\u8273\u5c11\u5973\u3002 \u3000\u3000\u8fd9\u56db\u540d\u5c11\u5973\u7a7f\u7740\u4e00\u5f0f\u7684\u7eff\u8863\u7eff\u88d9\uff0c\u542b\u7f9e\u5e26\u7b11\uff0c\u5a07\u6001\u64a9\u4eba\u3002 \u3000\u3000\u5979\u4eec\u5728\u8fd9\u6df1\u5c71\u5e7d\u8c37\u7684\u77f3\u5ba4\u51fa\u73b0\uff0c\u7b80\u76f4\u5c31\u50cf\u4ed9\u5973\u4e0b\u51e1\u4e00\u822c\u3002 \u3000\u3000\u5979\u4eec\u524d\u4e09\u540d\u5404\u7aef\u7740\u4e00\u4e2a\u7ea2\u6f06\u98df\u76d2\uff0c\u6700\u540e\u4e00\u540d\u5219\u6367\u7740\u4e00\u7f50\u9152\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u4e01\u6d9b\u548c\u7518\u9716\u867d\u7136\u4e24\u773c\u770b\u5f97\u76ee\u4e0d\u8f6c\u775b\uff0c\u4f46\u8fd8\u4e0d\u7b97\u592a\u8fc7\u5931\u6001\u3002 \u3000\u3000\u767d\u8863\u8001\u8005\u53c8\u5429\u5490\u9053\uff1a\u201c\u6446\u5e2d\uff01\u201d \u3000\u3000\u4e00\u540d\u5c11\u5973\u628a\u9152\u7f50\u653e\u5728\u9760\u58c1\u5904\u7684\u8336\u51e0\u4e0a\uff0c\u5e76\u62c6\u5f00\u6ce5\u5c01\u3002 \u3000\u3000\u53e6\u4e09\u540d\u5c11\u5973\u5219\u6253\u5f00\u98df\u76d2\u76d6\uff0e\u52a8\u4f5c\u8fc5\u5feb\u7684\u4e00\u76d8\u4e00\u76d8\u7aef\u4e0a\u6a80\u6728\u5706\u684c\u3002 \u3000\u3000\u987f\u65f6\uff0c\u9152\u9999\u3001\u83dc\u9999\uff0c\u6ee1\u5ba4\u751f\u9999\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\uff0c\u4fbf\u6446\u4e86\u6ee1\u6ee1\u4e00\u684c\uff0c\u6709\u5982\u6ee1\u6c49\u5168\u5e2d\uff0c\u53e6\u6709\u4e00\u5927\u76d8\u70ed\u817e\u817e\u7684\u5305\u5b50\u548c\u4e00\u76d8\u9992\u5934\u3002 \u3000\u3000\u8fd9\u684c\u9152\u5e2d\uff0c\u76f8\u4fe1\u8c01\u770b\u4e86\u4e5f\u8981\u6d8e\u6c34\u76f4\u6d41\u3002 \u3000\u3000\u679c\u7136\uff0c\u4e01\u6d9b\u548c\u7518\u9716\u65e9\u5df2\u5fcd\u4e0d\u4f4f\u553e\u6db2\u76f4\u5f80\u809a\u5b50\u54bd\u3002 \u3000\u3000\u56db\u540d\u7eff\u8863\u7edd\u8272\u5c11\u5973\u5728\u628a\u7b75\u5e2d\u6446\u597d\u4e4b\u540e\uff0c\u4fbf\u81ea\u884c\u79bb\u53bb\u3002 \u3000\u3000\u767d\u8863\u8001\u8005\u62db\u547c\u7740\u9053\uff1a\u201c\u62db\u5f85\u4e0d\u5468\u4e4b\u5904\uff0c\u5343\u4e07\u522b\u89c1\u602a\uff0c\u5404\u4f4d\u5feb\u8bf7\u5165\u5e2d\uff0c\u8001\u673d\u544a\u4e2a\u4fbf\uff0c\u4e0d\u966a \u4e86\u3002\u201d \u3000\u3000\u4e01\u6d9b\u5fd9\u9053\uff1a\u201c\u8001\u5148\u751f\u4e3a\u4ec0\u4e48\u4e0d\u548c\u6211\u4eec\u4e00\u8d77\u5403\uff1f\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u8001\u673d\u8fd8\u6709\u70b9\u4e8b\u5f85\u529e\uff0c\u5b9e\u5728\u4e0d\u80fd\u5949\u966a\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u53c8\u62f1\u4e86\u62f1\u624b\uff0c\u8ff3\u81ea\u79bb\u53bb\u3002 \u3000\u3000\u5f53\u56db\u4eba\u5165\u5e2d\u4e4b\u540e\uff0c\u53c8\u662f\u4e00\u60ca\u3002 \u3000\u3000\u53ea\u89c1\u5341\u51e0\u76d8\u83dc\u80b4\uff0c\u5168\u662f\u7528\u73cd\u79bd\u5f02\u517d\u70f9\u8c03\uff0c\u4ed6\u4eec\u4ece\u524d\u4e0d\u4f46\u6ca1\u5403\u8fc7\uff0c\u8fde\u770b\u90fd\u4e0d\u66fe\u770b\u8fc7\u3002 \u3000\u3000\u8fd9\u4e00\u9910\uff0c\u5f53\u771f\u662f\u5927\u5feb\u4eba\u610f\u3002 \u3000\u3000\u82e5\u4f9d\u7740\u4e01\u6d9b\u7684\u5fc3\u610f\uff0c\u5b9a\u8981\u628a\u4e00\u7f50\u9152\u559d\u5149\uff0c\u53ea\u56e0\u8881\u5c0f\u9e64\u63d0\u9192\u4ed6\u660e\u5929\u8fd8\u8981\u8fc7\u5173\uff0c\u4e01\u6d9b\u53ea\u597d\u9002 \u53ef\u800c\u6b62\u3002 \u3000\u3000\u9152\u7b75\u540e\uff0c\u4f30\u8ba1\u65f6\u95f4\u5df2\u5230\u4e8c\u66f4\uff0c\u4e01\u6d9b\u6478\u4e86\u6478\u5634\u9053\uff1a\u201c\u8fd9\u987f\u9152\u996d\uff0c\u7b97\u662f\u4e00\u767e\u4e24\u94f6\u5b50\u4e5f\u4e0d\u51a4 \u6789\uff01\u201d \u3000\u3000\u5f53\u5cb3\u5c0f\u98de\u3001\u8881\u5c0f\u9e64\u3001\u7518\u9716\u4e09\u4eba\u90fd\u4e0a\u4e86\u5e8a\uff0c\u4e01\u6d9b\u8fd8\u5750\u5728\u90a3\u91cc\u3002 \u3000\u3000\u539f\u6765\u4ed6\u662f\u5728\u7b49\u90a3\u56db\u540d\u7edd\u8272\u5c11\u5973\u6765\u6536\u62fe\u7897\u76d8\uff0c\u9664\u4e86\u518d\u591a\u770b\u51e0\u773c\uff0e \u3000\u3000\u66f4\u60f3\u627e\u673a\u4f1a\u548c\u5979\u4eec\u804a\u804a\u3002 \u3000\u3000\u8c01\u77e5\u8fdb\u6765\u6536\u62fe\u7897\u76d8\u7684\u5374\u662f\u4e24\u540d\u864e\u80cc\u718a\u8170\u7684\u7c97\u58ee\u5927\u6c49\u3002 \u3000\u3000\u4e01\u6d9b\u50bb\u4e86\u534a\u664c\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u5148\u524d\u8fdb\u6765\u9001\u996d\u83dc\u7684\u597d\u50cf\u4e0d\u662f\u4f60\u4eec\u4e24\u4f4d\uff1f\u201d \u3000\u3000\u5176\u4e2d\u4e00\u540d\u5927\u6c49\u8fde\u770b\u90fd\u6ca1\u770b\u4e01\u6d9b\u4e00\u773c\uff0c\u8fb9\u6536\u62fe\u7897\u76d8\u8fb9\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u4fa0\u597d\u773c\u529b\uff0c\u60a8\u770b\u6211\u4eec\u90a3 \u4e00\u70b9\u50cf\u5979\u4eec\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u672c\u4ee5\u4e3a\u62d0\u5f2f\u62b9\u89d2\u8bb2\u8bdd\u5f88\u6709\u6280\u5de7\uff0c\u60f3\u4e0d\u5230\u5374\u78b0\u4e86\u4e00\u9f3b\u5b50\u7070\uff0c\u4e0d\u89c9\u5e72\u7b11\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u90a3 \u4e48\u5979\u4eec\u5462\uff1f\u201d \u3000\u3000\u90a3\u5927\u6c49\u4ecd\u672a\u62ac\u5934\u9053\uff1a\u201c\u5979\u4eec\u90fd\u4e0d\u4e60\u60ef\u71ac\u591c\uff0c\u7761\u89c9\u53bb\u4e86\u3002\u201d \u3000\u3000\u4e24\u540d\u5927\u6c49\u6536\u62fe\u597d\uff0c\u4e5f\u8ff3\u81ea\u8d70\u4e86\u3002 \u3000\u3000\u4e01\u6d9b\u81ea\u8ba8\u6ca1\u8da3\uff0c\u4e5f\u4e0a\u4e86\u5e8a\u3002 \u3000\u3000\u8fd9\u4e00\u591c\uff0c\u7eb5\u7136\u7761\u5728\u8212\u9002\u65e0\u6bd4\u7684\u5e8a\u4e0a\uff0c\u56db\u4eba\u5374\u90fd\u4e0d\u66fe\u7761\u597d\u3002 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u8111\u6d77\u91cc\u4e00\u76f4\u8426\u7ed5\u7740\u201c\u89c2\u5149\u96a7\u9053\u201d\u91cc\u7684\u65d6\u65ce\u98ce\u5149\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u8111\u6d77\u91cc\u5219\u5145\u6ee1\u4e86\u53e6\u5916\u7684\u60ca\u5947\u3002 \u3000\u3000\u9996\u5148\uff0c\u4ed6\u4eec\u60ca\u5f02\u4e8e\u8fd9\u6761\u5c71\u8c37\u91cc\u5404\u4e2a\u96a7\u9053\u3001\u77f3\u5ba4\u5de5\u7a0b\u7684\u6d69\u5927\u3002\u5c24\u5176\uff0c\u4ed6\u4eec\u73b0\u5728\u6240\u4f4f\u7684\u8fd9\u95f4 \u77f3\u5ba4\uff0c\u4e3a\u4f55\u8981\u5e03\u7f6e\u5f97\u5982\u6b64\u8c6a\u534e\uff1f\u90a3\u4e9b\u7f8e\u8273\u5c11\u5973\u53c8\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u5979\u4eec\u6765\u8fd9\u91cc\u53c8\u662f\u505a\u4ec0\u4e48\uff1f \u3000\u3000\u53e6\u5916\uff0c\u8d70\u8fdb\u8fd9\u6761\u5c71\u8c37\uff0c\u5c45\u7136\u8fd8\u8981\u7ecf\u8fc7\u6587\u573a\u8003\u8bd5\uff0c\u4ed6\u4eec\u8003\u8bd5\u7684\u65e5\u7684\u4f55\u5728\uff1f \u3000\u3000\u8fd9\u4e00\u8fde\u4e32\u7684\u7591\u95ee\uff0e\u5b9e\u5728\u65e0\u6cd5\u89e3\u91ca\u3002 \u3000\u3000\u5927\u7ea6\u76f4\u5230\u4e09\u66f4\u8fc7\u540e\uff0c\u56db\u4e2a\u4eba\u624d\u5404\u81ea\u6726\u80e7\u7761\u53bb\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u5929\u4eae\u540e\uff0c\u56db\u4eba\u521a\u8d77\u5e8a\u4e0d\u4e45\uff0c\u90a3\u56db\u540d\u7edd\u8272\u5c11\u5973\u53c8\u8fdb\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u6b21\u662f\u6765\u9001\u6dae\u6d17\u7528\u5177\u548c\u7528\u6c34\u3002 \u3000\u3000\u4e0d\u8fc7\u5979\u4eec\u6ca1\u7ed9\u4e01\u6d9b\u501f\u673a\u642d\u8baa\u7684\u673a\u4f1a\uff0c\u4e1c\u897f\u653e\u4e0b\u540e\u4fbf\u5306\u5306\u79bb\u53bb\u3002 \u3000\u3000\u521a\u6dae\u6d17\u5b8c\u6bd5\uff0c\u767d\u8863\u8001\u8005\u4fbf\u8fdb\u6765\u4e86\uff0c\u4ed6\u8eab\u540e\u53c8\u5e26\u7740\u90a3\u56db\u540d\u5c11\u5973\u3002 \u3000\u3000\u539f\u6765\u8fd9\u6b21\u662f\u9001\u65e9\u9910\u6765\u7684\u3002 \u3000\u3000\u5f53\u90a3\u56db\u540d\u5c11\u5973\u6446\u597d\u65e9\u9910\u79bb\u53bb\u540e\uff0c\u767d\u8863\u8001\u8005\u5374\u5e76\u672a\u8d70\u5f00\u3002 \u3000\u3000\u56db\u4eba\u5fc3\u91cc\u90fd\u6709\u6570\uff0c\u767d\u8863\u8001\u8005\u4e00\u5b9a\u7b49\u7740\u6536\u94b1\u7684\u3002 \u3000\u3000\u4e01\u6d9b\u5fc3\u91cc\u6697\u9a82\u9053\uff1a\u201c\u8fd9\u8001\u5c0f\u5b50\u62c5\u7684\u4ec0\u4e48\u5fc3\uff0c\u96be\u9053\u6211\u4eec\u8fd8\u4f1a\u5403\u4e86\u5c31\u5077\u5077\u6e9c\u6389\uff01\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u62db\u547c\u7740\u9053\uff1a\u201c\u56db\u4f4d\u6700\u597d\u5feb\u5feb\u7528\u65e9\u9910\uff0c\u524d\u9762\u7684\u4e00\u5173\u5df2\u7ecf\u5f00\u4e86\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8001\u5148\u751f\u4f55\u4e0d\u8ddf\u6211\u4eec\u4e00\u8d77\u7528\uff1f\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u8001\u673d\u5df2\u7ecf\u7528\u8fc7\u4e86\u3002\u201d \u3000\u3000\u9910\u540e\uff0c\u4e01\u6d9b\u5012\u662f\u5927\u65b9\u5f97\u5f88\uff0c\u638f\u51fa\u4e00\u5f20\u94f6\u7968\u9053\uff1a\u201c\u8001\u5148\u751f\uff0c\u8fd9\u662f\u4e00\u767e\u4e24\u94f6\u7968\u3002\u591a\u8c22\u4f60\u8001\u4eba \u5bb6\u7684\u62db\u5f85\uff01\u201d \u3000\u3000\u8c01\u77e5\u767d\u8863\u8001\u8005\u53cc\u624b\u4e00\u63a8\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u56db\u4f4d\u7684\u94f6\u5b50\u8001\u673d\u4e0d\u6562\u6536\uff01\u201d \u3000\u3000\u4e01\u6d9b\u725b\u773c\u8fde\u7728\u51e0\u7728\u9053\uff1a\u201c\u8001\u5148\u751f\u62db\u5f85\u5ba2\u4eba\u4e0d\u6536\u94b1\uff0c\u5c82\u4e0d\u8d54\u4e86\u8001\u672c\uff1f\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u7b11\u9053\uff1a\u201c\u8001\u673d\u4e0d\u662f\u5f00\u5e97\u7684\uff0c\u4f60\u8001\u5f1f\u8fd9\u6837\u8bf4\u8bdd\uff0c\u90a3\u5c31\u89c1\u5916\u4e86\u3002\u201d \u3000\u3000\u4f46\u4e01\u6d9b\u4e0d\u80af\u767d\u5403\uff0c\u624b\u91cc\u90a3\u5f20\u94f6\u7968\uff0c\u76f4\u5f80\u767d\u8863\u8001\u8005\u8eab\u4e0a\u585e\u3002 \u3000\u3000\u767d\u8863\u8001\u8005\u4f3c\u4e4e\u88ab\u903c\u4e0d\u8fc7\uff0c\u624d\u6574\u4e86\u6574\u8138\u9053\uff1a\u201c\u5b9e\u5728\u5bf9\u4f60\u8001\u5f1f\u8bf4\uff0c \u3000\u3000\u8001\u673d\u662f\u5949\u547d\u62db\u5f85\u4f60\u4eec\u7684\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8fde\u5fd9\u62a2\u5148\u95ee\u9053\uff1a\u201c\u8001\u5148\u751f\u662f\u5949\u8c01\u4e4b\u547d\uff1f\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u6b49\u7136\u2014\u7b11\u9053\uff1b\u201c\u56db\u4f4d\u90fd\u8bf7\u539f\u8c05\uff0c\u5e76\u975e\u8001\u673d\u4e0d\u80af\u660e\u8a00\u3002\u800c\u662f\u5949\u547d\u6682\u65f6\u4e0d\u80fd\u900f\u9732\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u90a3\u4f4d\u4ea4\u5f85\u8001\u5148\u751f\u62db\u5f85\u665a\u8f88\u4eec\u7684\u4eba\uff0c\u8eab\u4efd\u5730\u4f4d\u4e00\u5b9a\u5f88\u9ad8\u4e86\uff1f\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9053\uff1a\u201c\u867d\u7136\u4e0d\u662f\u6700\u9ad8\u7684\uff0c\u81f3\u5c11\u8fd9\u6761\u8c37\u9053\u91cc\u7684\u5404\u5173\u5404\u5361\uff0c\u90fd\u4e0d\u6562\u4e0d\u542c\u5979\u7684\uff0c\u5c31\u4ee5 \u8fd9\u95f4\u77f3\u5ba4\u6765\u8bf4\uff0c\u5e73\u65f6\u8fde\u8001\u673d\u90fd\u4e0d\u80fd\u8fdb\u6765\uff0c\u82e5\u6ca1\u6709\u4ed6\u7684\u4ea4\u5f85\uff0c\u8001\u673d\u600e\u6562\u968f\u4fbf\u6253\u5f00\u62db\u5f85\u5404\u4f4d\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u7d27\u76b1\u7740\u53cc\u7709\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u4f4d\u9ad8\u4eba\u4e00\u5b9a\u662f\u8ba4\u8bc6\u6211\u4eec\u4e86\uff0c\u53ef\u662f\u665a\u8f88\u81ea\u5c0f\u4e5f\u662f\u4f4f \u5728\u6df1\u5c71\uff0c\u4ece\u4e0d\u66fe\u548c\u4efb\u4f55\u5916\u4eba\u4ea4\u5f80\uff0c\u8fd9\u4f4d\u9ad8\u4eba\u53c8\u600e\u4f1a\u961f\u8bc6\u6211\u4eec\u5462\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1b\u201c\u5979\u53ef\u80fd\u662f\u8ba4\u8bc6\u6211\u548c\u8001\u7518\u4e24\u4eba\uff0c\u65e2\u7136\u5979\u6682\u65f6\u4e0d\u80af\u900f\u9732\u8eab\u4efd\uff0c\u90a3\u5c31\u8bf7\u8001\u5148\u751f\u4ee3\u6211 \u4eec\u5411\u5979\u81f4\u8c22\uff01\u201d \u3000\u3000\u767d\u8863\u8001\u8005\u9894\u9996\u9053\uff0c\u201c\u8001\u673d\u4e00\u5b9a\u7167\u529e\uff01\u201d \u3000\u3000\u4e01\u6d9b\u987f\u65f6\u6709\u4e9b\u6d0b\u6d0b\u81ea\u5f97\u6a21\u6837\uff0c\u56e0\u4e3a\u5c45\u7136\u6709\u4eba\u5bf9\u4ed6\u548c\u7518\u9716\u5982\u6b64\u9686\u91cd\u62db\u5f85\uff0c\u8fde\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f \u9e64\u4e5f\u8ddf\u7740\u6cbe\u4e86\u5149\uff0c\u8fd9\u4e48\u6709\u9762\u5b50\uff0c\u6559\u4ed6\u600e\u80fd\u4e0d\u4e50\u3002 \u3000\u3000\u56db\u4e2a\u968f\u5373\u8f9e\u522b\u4e86\u767d\u8863\u8001\u8005\uff0c\u5411\u4e0b\u4e00\u5173\u524d\u8fdb\u3002 \u3000\u3000\u51fa\u4e86\u5c71\u6d1e\uff0c\u7518\u9716\u77aa\u4e86\u4e01\u6d9b\u4e00\u773c\u9053\uff1a\u201c\u522b\u5c3d\u5f80\u8138\u4e0a\u8d34\u91d1\uff0c\u4f60\u600e\u77e5\u9053\u90a3\u4f4d\u9ad8\u4eba\u8ba4\u8bc6\u6211\u4eec\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u4e5f\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u90a3\u4f4d\u9ad8\u4eba\u65e2\u7136\u4e0d\u53ef\u80fd\u8ba4\u8bc6\u5cb3\u516c\u5b50\u548c\u8881\u5c11\u4fa0\uff0c\u5f53\u7136\u5c31\u662f\u8ba4\u8bc6\u6211\u4eec\u4e86\uff0c \u6211\u6709\u5f88\u591a\u7406\u7531\uff0c\u53ef\u4ee5\u8bc1\u660e\u6211\u7684\u60f3\u6cd5\u9519\u4e0d\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u4ec0\u4e48\u7406\u7531\uff1f\u201d \u3000\u3000\u201c\u7b2c\u4e00\uff0c\u54b1\u4eec\u4e24\u4e2a\u5c31\u4f4f\u5728\u592a\u767d\u5c71\uff0c\u592a\u5c71\u5c71\u79bb\u8fd9\u91cc\u4e0d\u8fc7\u4e24\u4e09\u767e\u91cc\u8def\uff0c\u8fd9\u4f4d\u9ad8\u4eba\u5f53\u7136\u5bb9\u6613\u8ba4 \u8bc6\u6211\u4eec\u3002\u201d \u3000\u3000\u201c\u7b2c\u4e8c\u5462\uff1f\u201d \u3000\u3000\u201c\u7b2c\u4e8c\uff0c\u5c31\u662f\u54b1\u4eec\u4e24\u4e2a\u53f7\u79f0\u2018\u592a\u767d\u53cc\u96c4\u2019\uff0c\u5728\u5f53\u4eca\u6b66\u6797\uff0c\u4e5f\u7b97\u662f\u6709\u5934\u6709\u8138\u7684\u4eba\u7269\uff1f\u7531\u4e8e \u76ee\u6807\u592a\u5927\uff0c\u4f60\u4e0d\u8ba4\u8bc6\u522b\u4eba\uff0c\u522b\u4eba\u5374\u8ba4\u8bc6\u4f60\uff0c \u3000\u3000\u4f60\u5e72\u561b\u81ea\u5df1\u77a7\u4e0d\u8d77\u81ea\u5df1\uff1f\u201d \u3000\u3000\u7518\u9716\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u522b\u5c4e\u87d1\u8782\u6234\u82b1\u81ed\u7f8e\u5566\uff01\u201d \u3000\u3000\u4e01\u6d9b\u4f3c\u662f\u4e5f\u4e0d\u613f\u8001\u5728\u8fd9\u4ef6\u4e8b\u4e0a\u6253\u8f6c\uff0c\u5e72\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u54b1\u4eec\u521a\u624d\u5fd8\u8bb0\u95ee\u95ee\u90a3\u4f4d\u8001\u5148 \u751f\u524d\u9762\u662f\u4ec0\u4e48\u5173\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u95ee\u4ec0\u4e48\u95ee\uff0c\u5230\u65f6\u5019\u81ea\u7136\u5c31\u77e5\u9053\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u6587\u5173\u5df2\u8fc7\uff0c\u4e0d\u6d88\u8bf4\u4e0b\u4e00\u5173\u8be5\u662f\u6b66\u5173\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u90a3\u662f\u8bf4\u54b1\u4eec\u8981\u5404\u51ed\u6b66\u529f\u8fc7\u5173\u4e86\uff1f\u201d \u3000\u3000\u8881\u4e2a\u9e64\u9053\uff1a\u201c\u90a3\u662f\u5f53\u7136\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4e00\u62cd\u80f8\u812f\uff0c\u54a7\u5634\u7b11\u9053\uff1a\u201c\u6628\u5929\u8fc7\u6587\u5173\u65f6\uff0c\u4f60\u4eec\u4e24\u4f4d\u7684\u5b66\u95ee\uff0c \u3000\u3000\u5b9e\u5728\u6ca1\u6709\u8bdd\u8bb2\uff0c\u6211\u548c\u8001\u7518\u5728\u4e00\u65c1\u90fd\u542c\u50bb\u4e86\u773c\uff0c\u4f60\u4eec\u548c\u6768\u8001\u5934\u8bb2\u7684\uff0c \u3000\u3000\u54b1\u662f\u5341\u53e5\u542c\u4e0d\u61c2\u4e24\u53e5\uff0c\u4eca\u5929\u8fc7\u6b66\u5173\uff0c\u603b\u7b97\u8f6e\u5230\u6211\u548c\u8001\u7518\u626c\u7709\u5410\u6c14\u7684\u65f6\u5019\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u4e01\u6d9b\u867d\u65e9\u5df2\u77e5\u9053\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u662f\u8ddf\u968f\u5723\u624b\u4e66\u751f\u5e90\u4e91\u4e60\u827a\u7684\uff0c\u8eab\u624b\u4e00\u5b9a\u4e0d\u9519\uff0c\u4f46\u5374 \u53c8\u8ba4\u4e3a\u4ed6\u4eec\u4e24\u4eba\u5e74\u7eaa\u592a\u8f7b\uff0c\u5c24\u5176\u5cb3\u5c0f\u98de\u624d\u53ea\u6709\u5341\u4e09\u56db\u5c81\uff0c\u529f\u529b\u706b\u5019\u4e00\u5b9a\u8fd8\u6d45\u5f97\u5f88\uff0c\u51b3\u4e0d\u53ef\u80fd \u4e0e\u4ed6\u548c\u7518\u9716\u5728\u6b66\u6797\u4e2d\u884c\u8d70\u591a\u5e74\u5df2\u95ef\u51fa\u5b57\u53f7\u7684\u201c\u592a\u767d\u53cc\u96c4\u201d\u76f8\u6bd4\uff0c\u7531\u4e8e\u4ed6\u6709\u8fd9\u79cd\u60f3\u6cd5\uff0c\u6240\u4ee5\u624d \u80fd\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\u3002 \u3000\u3000\u53ea\u542c\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u5230\u4e86\u6b66\u5173\uff0c\u5728\u4e0b\u8fd8\u771f\u62c5\u5fc3\u8fc7\u4e0d\u4e86\uff0c\u5982\u679c\u8fc7\u4e0d\u4e86\u6b66\u5173\uff0c\u5b66\u95ee\u518d\u597d \u4e5f\u6ca1\u7528\uff0c\u770b\u6765\u8fd8\u662f\u4e01\u5f53\u5bb6\u7684\u6b66\u529f\u5b9e\u7528\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u4fd7\u8bdd\u8bf4\uff1a\u3000\u2018\u79c0\u624d\u89c1\u4e86\u5175\uff0c\u6709\u7406\u8bf4\u4e0d\u6e05\u2019\uff0c\u5230\u90a3\u65f6\u6587\u7684\u786e\u5b9e\u8d76\u4e0d \u4e0a\u6b66\u7684\u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1b\u201c\u90a3\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u53c8\u62cd\u62cd\u80f8\u812f\u9053\uff1a\u201c\u8881\u5c11\u4fa0\u653e\u5fc3\uff0e\u4e00\u5207\u6709\u6211\u548c\u8001\u7518\uff0c\u6211\u4eec\u4f1a\u4fdd\u4f60\u4eec\u4e24\u4f4d\u8fc7\u5173\uff0c\u4e24\u4f4d\u53ea \u8981\u8ddf\u7740\u6211\u4eec\u8d70\uff0c\u4fdd\u7ba1\u6ca1\u9519\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u6211\u4eec\u73b0\u5728\u4e0d\u6b63\u662f\u8ddf\u7740\u4e01\u5f53\u5bb6\u7684\u8d70\u4e48\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u90a3\u5c31\u6ca1\u9519\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\u524d\u9762\u7684\u666f\u8c61\u5ffd\u7136\u4e00\u53d8\u3002 \u3000\u3000\u4ed6\u4eec\u811a\u4e0b\u7684\u8c37\u9053\uff0c\u672c\u6765\u53ea\u6709\u4e24\u4e08\u5de6\u53f3\u5bbd\uff0c\u4f46\u524d\u9762\u5374\u5ffd\u7136\u53d8\u5f97\u5f00\u6717\uff0c\u51fa\u73b0\u4e86\u4e00\u5904\u5341\u51e0\u4e08\u65b9 \u5706\u7684\u5927\u5e7f\u573a\u3002 \u3000\u3000\u4e0d\u8fc7\u5e7f\u573a\u7684\u53e6\u4e00\u7aef\uff0c\u5374\u53c8\u53d8\u6210\u4e86\u6781\u7a84\u7684\u5c71\u8c37\uff0c\u50cf\u4e00\u5904\u74f6\u9888\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u5fc3\u91cc\u90fd\u6709\u6570\uff0c\u8fd9\u5e7f\u573a\u5fc5\u5b9a\u662f\u6bd4\u6b66\u573a\u5730\uff0c\u53ea\u8981\u6709\u6b66\u529f\u9ad8\u5f3a\u7684\u4eba\u5b88\u5728\u90a3\u74f6\u9888 \u5730\u5e26\uff0c\u7684\u786e\u65e0\u6cd5\u901a\u8fc7\u3002 \u3000\u3000\u679c\u7136\uff0c\u5c31\u5728\u5feb\u8fdb\u5165\u5e7f\u573a\u7684\u8c37\u58c1\u4e0a\uff0c\u8d6b\u7136\u51fa\u73b0\u4e86\u201c\u6b66\u5173\u201d\u4e24\u4e2a\u5927\u5b57\u3002 \u3000\u3000\u4e01\u6d9b\u603b\u7b97\u8ba4\u8bc6\u8fd9\u4e24\u4e2a\u5b57\uff0c\u5658\u5634\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u73b0\u5728\u8be5\u770b\u54b1\u548c\u8001\u7518\u7684\u4e86\uff01\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5e7f\u573a\u5bf9\u9762\u51fa\u73b0\u4e86\u4e00\u4e2a\u7070\u8863\u9a7c\u80cc\u8001\u4eba\u3002 \u3000\u3000\u90a3\u8001\u4eba\u4f3c\u4e4e\u672c\u6765\u8eab\u6750\u4e0d\u9ad8\uff0c\u518d\u52a0\u4e0a\u9a7c\u80cc\uff0c\u8d8a\u53d1\u77ee\u5f97\u591f\u4e0d\u4e0a\u666e\u901a\u4eba\u80a9\u8180\uff0c\u82e5\u548c\u4e01\u6d9b\u3001\u7518\u9716 \u8fd9\u79cd\u4eba\u9ad8\u9a6c\u5927\u7684\u4eba\u7269\u7ad9\u5728\u4e00\u8d77\uff0c\u90a3\u5c31\u66f4\u4e0d\u8d77\u773c\u4e86\u3002 \u3000\u3000\u4e01\u6d9b\u60c5\u4e0d\u81ea\u7981\u563f\u563f\u7b11\u9053\uff1a\u201c\u51fa\u6765\u7684\u7adf\u662f\u8fd9\u4e48\u5757\u6599\uff0c\u8001\u5b50\u4e00\u811a\u80fd\u628a\u4ed6\u8e22\u4e0a\u897f\u5929\uff01\u201d \u3000\u3000\u5ffd\u542c\u90a3\u9a7c\u80cc\u8001\u4eba\u558a\u9053\uff1a\u201c\u4f60\u4eec\u56db\u4e2a\u90fd\u7ed9\u8001\u592b\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u4e0d\u4f46\u4e01\u6d9b\u548c\u7518\u9716\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u4e5f\u5168\u611f\u5fc3\u5934\u9707\u8361\u3002 \u3000\u3000\u539f\u6765\u662f\u9a7c\u80cc\u8001\u4eba\u7684\u8fd9\u58f0\u558a\u53eb\uff0c\u7adf\u5982\u7126\u96f7\u9aa4\u53d1\uff0c\u9707\u5f97\u4eba\u8033\u9f13\u90fd\u55e1\u55e1\u4f5c\u54cd\u3002 \u3000\u3000\u6b64\u523b\u53cc\u65b9\u76f8\u8ddd\u81f3\u5c11\u4e03\u516b\u5341\u4e08\u8fdc\uff0e\u82e5\u7ad9\u5728\u9762\u524d\uff0c\u58f0\u97f3\u4e4b\u5927\uff0c\u53ea\u6015\u66f4\u662f\u60ca\u4eba\u3002 \u3000\u3000\u4e01\u6d9b\u6123\u4e86\u4e00\u6123\u9053\uff1b\u201c\u82e5\u540a\u4e2a\u574e\u513f\uff0c\u9a7c\u5b50\u653e\u5c41\u2014\u2014\u5f2f\u5f2f\u66f2\u66f2\uff0c\u8bf4\u8bdd\u7531\u4e39\u7530\u53d1\u58f0\uff0c\u5e94\u5f53\u66f4\u5f2f \u66f2\u624d\u5bf9\uff0c\u4ed6\u7adf\u8fd9\u4e48\u5927\u7684\u58f0\u97f3\uff0c\u83ab\u975e\u8fd9\u8001\u5c0f\u5b50\u662f\u9752\u86d9\u53d8\u7684\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4ed6\u672c\u6765\u8fd8\u8981\u7ee7\u7eed\u5f80\u524d\u8d70\uff0c\u5374\u88ab\u7518\u9716\u4e00\u628a\u62c9\u4f4f\u9053\uff1a\u201c\u6ca1\u542c\u4eba\u5bb6\u8bf4\u8981\u54b1\u4eec\u7ad9\u4f4f\u4e48\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u4f60\u8fd9\u72d7\u718a\u7765\u6c14\u6539\u6539\u597d\u4e0d\u597d\uff0c\u4e0d\u7ba1\u5982\u4f55\uff0c\u770b\u770b\u529d\u9759\u518d\u8bf4\u3002\u201d \u3000\u3000\u4f46\u4ed6\u5374\u4e0d\u7518\u793a\u5f31\uff0c\u4e5f\u4eae\u8d77\u55d3\u95e8\u558a\u53eb\u9053\uff1a\u201c\u8def\u662f\u4eba\u4eba\u8d70\u7684\uff1f\u4f60\u51ed\u4ec0\u4e48\u8ba9\u6211\u4eec\u7ad9\u4f4f\uff1f\u201d \u3000\u3000\u4ed6\u7528\u5c3d\u4e39\u7530\u4e4b\u6c14\u558a\u51fa\u8fd9\u4e24\u53e5\u8bdd\uff0c\u81ea\u4ee5\u4e3a\u58f0\u97f3\u4e5f\u591f\u5927\u4e86\uff0c\u5c82\u77e5\u548c\u5bf9\u65b9\u76f8\u6bd4\uff0c\u5b9e\u5728\u662f\u5c0f\u5deb\u89c1 \u5927\u5deb\u3002 \u3000\u3000\u53ea\u542c\u9a7c\u80cc\u8001\u4eba\u9707\u8033\u6b32\u804b\u7684\u58f0\u97f3\u53c8\u4f20\u8fc7\u6765\u9053\uff1a\u201c\u53eb\u4f60\u7ad9\u4f4f\u5c31\u7ad9\u4f4f\uff0c\u90a3\u6709\u90a3\u4e48\u591a\u7684\u7406\u7531\u8bb2\uff01\u201d \u3000\u3000\u4ed6\u7684\u58f0\u97f3\u867d\u5927\uff0c\u4f46\u542c\u6765\u53d1\u8bdd\u65f6\u5374\u5e76\u4e0d\u5403\u529b\u3002 \u3000\u3000\u4e01\u6d9b\u81ea\u77e5\u55d3\u95e8\u65e0\u6cd5\u548c\u5bf9\u65b9\u76f8\u6bd4\uff0c\u7d22\u6027\u4e0d\u518d\u7b54\u8bdd\u3002 \u3000\u3000\u5012\u662f\u8881\u5c0f\u9e64\u8fd9\u65f6\u53eb\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u53cc\u65b9\u9694\u5f97\u8fd9\u4e48\u8fdc\uff0c\u4f60\u65b9\u4fbf\u8ddf\u6211\u4eec\u8bb2\u8bdd\uff0c\u6211\u4eec\u5374\u4e0d\u65b9\u4fbf \u8ddf\u4f60\u8bb2\u8bdd\uff0c\u662f\u5426\u53ef\u4ee5\u51c6\u8bb8\u6211\u4eec\u9760\u8fd1\u4e9b\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u9053\uff1a\u201c\u90a3\u5c31\u518d\u9760\u8fd1\u4e9b\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u3001\u5cb3\u5c0f\u98de\u3001\u4e01\u6d9b\u3001\u7518\u9716\u968f\u5373\u7ee7\u7eed\u524d\u8fdb\u3002 \u3000\u3000\u4ed6\u4eec\u76f4\u8d70\u5230\u8ddd\u9a7c\u80cc\u8001\u4eba\u4e0d\u8db3\u4e24\u4e08\u8fdc\uff0c\u624d\u505c\u4e0b\u6b65\u6765\u3002 \u3000\u3000\u9694\u5f97\u8fd1\u4e86\uff0c\u624d\u89c9\u51fa\u5148\u524d\u8fd8\u628a\u9a7c\u80cc\u8001\u4eba\u7684\u8eab\u9ad8\u9ad8\u4f30\u4e86\u3002\u5b9e\u9645\u4e0a\u4ed6\u7684\u8eab\u9ad8\u53ea\u80fd\u53ca\u5cb3\u5c0f\u98de\u8033\u6735\uff0c \u6d3b\u8131\u8131\u7684\u5c31\u662f\u6c34\u6d52\u4f20\u91cc\u7684\u6b66\u5927\u90ce\u3002 \u3000\u3000\u8bf4\u4ed6\u50cf\u6b66\u5927\u90ce\uff0c\u8fd8\u662f\u628a\u4ed6\u9ad8\u62ac\u4e86\uff0c\u56e0\u4e3a\u6b66\u5927\u90ce\u53ea\u662f\u6bd4\u5e38\u4eba\u77ee\u4e9b\uff0c\u5e76\u672a\u9a7c\u80cc\u3002\u8fd9\u8001\u4eba\u5c82\u4f46 \u9a7c\u80cc\uff0c\u8fde\u4e24\u817f\u90fd\u662f\u5f2f\u7684\uff0c\u8138\u4e0a\u4e5f\u6d3b\u4f3c \u3000\u3000\u765e\u86e4\u87c6\u76ae\uff0c\u4e24\u9053\u82cd\u767d\u7684\u516b\u5b57\u7709\uff0c\u914d\u4e0a\u4e0b\u5782\u7684\u4e09\u89d2\u773c\uff0c\u7b80\u76f4\u5c31\u50cf\u540a\u6b7b\u9b3c\u3002\u518d\u52a0\u4e0a\u9f3b\u5b50\u548c\u5634 \u5df4\u90fd\u6709\u70b9\u6b6a\uff0c\u4e24\u53ea\u8033\u6735\u5374\u53c8\u5927\u5f97\u8db3\u53ef\u6247\u98ce\u3002 \u3000\u3000\u8fd9\u526f\u5c0a\u5bb9\uff0c\u53ef\u8c13\u96c6\u5929\u4e0b\u4e4b\u4e11\u4e8e\u4e00\u8eab\u3002 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u5728\u4ed4\u7ec6\u7aef\u8be6\u4e86\u5bf9\u65b9\u4e00\u773c\u540e\uff0c\u90fd\u4e0d\u7981\u5185\u5fc3\u6697\u7b11\u3002 \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8001\u5bb6\u4f19\uff0c\u4f60\u8bf4\u8bdd\u90a3\u4e48\u5927\u58f0\u505a\u4ec0\u4e48\uff0c\u6211\u4eec\u53c8\u4e0d\u662f\u804b\u5b50\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u6015\u4e48\uff1f\u90a3\u4e48\u8001\u592b\u5c31\u5c0f\u58f0\u4e9b\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u7b11\u8fd8\u597d\uff0c\u8fd9\u4e00\u7b11\uff0c\u8138\u8089\u62c9\u52a8\u5f97\u9f3b\u5b50\u773c\u775b\u5634\u5df4\u4e00\u9f50\u626d\u66f2\uff0c\u8fde\u8033\u6735\u4e5f\u8ddf\u7740\u6247\u98ce\uff0c\u4f7f\u5f97\u4e01 \u6d9b\u548c\u7518\u9716\u90fd\u4e0d\u7981\u4e5f\u7b11\u51fa\u58f0\u6765\u3002 \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u4f3c\u662f\u5f04\u4e0d\u6e05\u695a\u8fd9\u4e24\u4eba\u4e3a\u4f55\u53d1\u7b11\uff0c\u6123\u4e01\u6123\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u597d\u7b11\u7684\uff1f\u83ab\u975e\u62a2\u5230\u7b11\u5e3d\u5b50 \u4e86\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u4e3a\u4e86\u8981\u9017\u4ed6\u2014\u9017\uff0c\u51b3\u5b9a\u6536\u8d77\u6bdb\u8e81\u6027\u5b50\uff0c\u563f\u563f\u7b11\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u8fdc\u770b\u4e00\u8868\u4eba\u624d\uff0c \u8fd1\u770b\u66f4\u662f\u4eba\u624d\u4e00\u8868\uff0c\u5728\u4e0b\u770b\u5f97\u9ad8\u5174\u4e86\uff0c\u5f53\u7136\u8981\u7b11\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u6478\u4e86\u6478\u4e0b\u5df4\u9053\uff1a\u201c\u4f60\u80fd\u53d1\u73b0\u8001\u592b\u7684\u957f\u5904\uff0c\u603b\u7b97\u4e0d\u9519\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u53ef\u4e0d\u53ef\u4ee5\u518d\u7b11\u4e00\u6b21\uff0c\u8ba9\u6211\u4eec\u56db\u4eba\u597d\u597d\u5f00\u6b21\u773c\u754c\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u9053\uff1a\u201c\u5f53\u7136\u53ef\u4ee5\uff0c\u4e0d\u8fc7\u8981\u5148\u529e\u5b8c\u6b63\u4e8b\u518d\u8bf4\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u6b63\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8001\u5927\u60f3\u95ee\u95ee\u4f60\u4ec0\u4e48\u53eb\u6b63\u4e8b\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u8bf4\u8fd9\u8bdd\u65f6\uff0c\u84e6\u5730\u4e24\u773c\u5c04\u51fa\u51b7\u7535\u822c\u7684\u5149\u8292\uff0c\u50cf\u5229\u5203\u822c\u523a\u8fdb\u4e86\u4e01\u6d9b\u7684\u5fc3\u80ba\u3002 \u3000\u3000\u9976\u662f\u4e01\u6d9b\u5148\u524d\u6ca1\u628a\u5bf9\u65b9\u653e\u5728\u5fc3\u4e0a\uff0c\u6b64\u523b\u4e5f\u60c5\u4e0d\u81ea\u7981\u6253\u4e86\u4e2a\u51b7\u98a4\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u62c5\u5fc3\u628a\u4e8b\u60c5\u95f9\u7ffb\uff0c\u7acb\u5373\u63a5\u8fc7\u53e3\u53bb\u9053\uff1a\u201c\u524d\u8f88\uff0c\u662f\u60a8\u8001\u4eba\u5bb6\u8981\u6211\u4eec\u7ad9\u4f4f\uff0c\u6709\u4ec0\u4e48\u4e8b \u60c5\uff0c\u5e94\u8be5\u4f60\u5148\u4ea4\u4ee3\u6211\u4eec\u624d\u662f\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u5fae\u5fae\u9894\u9996\u9053\uff1a\u201c\u8fd9\u8fd8\u50cf\u53e5\u4eba\u8bdd\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u524d\u8f88\u6709\u8bdd\u73b0\u5728\u5c31\u8bf7\u4ea4\u4ee3\uff01\u201d \u3000\u3000\u201c\u8001\u592b\u8981\u5148\u95ee\u95ee\uff0c\u6628\u5929\u508d\u665a\u6709\u56db\u540d\u901a\u8fc7\u6587\u5173\u8003\u8bd5\u7684\u53ef\u662f\u4f60\u4eec\uff1f\u201d \u3000\u3000\u201c\u6b63\u662f\u665a\u8f88\u7b49\u56db\u4eba\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u8138\u8089\u4e00\u626d\uff0c\u4e94\u5b98\u7686\u52a8\uff0c\u7b11\u9053\uff1a\u201c\u8001\u592b\u542c\u8bf4\u4f60\u4eec\u662f\u8fd9\u4e9b\u5e74\u6765\u901a\u8fc7\u6587\u5173\u6d4b\u8bd5\u6210\u7ee9\u6700 \u597d\u7684\u4e00\u6279\uff0c\u5341\u5206\u96be\u5f97\uff0c\u4e0d\u77e5\u4f60\u4eec\u5f53\u4e2d\u90a3\u4e00\u4e2a\u6210\u7ee9\u6700\u597d\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6beb\u4e0d\u8fdf\u7591\u7684\u6307\u6307\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u6210\u7ee9\u6700\u597d\uff01\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u53cc\u76ee\u4e2d\u53c8\u5c04\u51fa\u7535\u4e00\u822c\u7684\u51b7\u8292\uff0c\u51dd\u6ce8\u5728\u5cb3\u5c0f\u98de\u8138\u4e0a\u534a\u664c\uff0c\u624d\u9053\uff1b\u201c\u4f9d\u8001\u592b\u770b\uff0c\u6700 \u597d\u7684\u4e5f\u8be5\u662f\u4ed6\uff0c\u6b63\u6240\u8c13\u6709\u624d\u4e0d\u5728\u5e74\u9ad8\u3002\u201d \u3000\u3000\u63a5\u7740\u76ee\u5149\u53c8\u77a5\u5728\u4e01\u6d9b\u8138\u4e0a\uff1a\u201c\u8fd9\u5c0f\u5b50\u5e74\u7eaa\u867d\u5927\uff0c\u5374\u662f\u4e2a\u5730\u9053\u7684\u8349\u5305\uff0c\u8001\u592b\u771f\u6000\u7591\u4ed6\u662f\u600e \u6837\u8fc7\u7684\u5173\uff01\u201d \u3000\u3000\u4e01\u6d9b\u672c\u6765\u5df2\u5fcd\u4e0b\u6027\u5b50\uff0c\u8fd9\u4e00\u6765\u518d\u4e5f\u6309\u637a\u4e0d\u4f4f\uff0c\u543c\u9053\uff1a\u201c\u8001\u5bb6\u4f19\uff0c\u5c11\u80e1\u8bf4\u516b\u9053\uff0c\u5728\u4e0b\u6628\u5929 \u5bf9\u7b54\u5982\u6d41\uff0e\u8fde\u90a3\u4f4d\u5927\u4e3b\u8003\u90fd\u6ca1\u6709\u8bdd\u8bb2\uff0c\u4f60\u7b97\u662f\u90a3\u4e00\u9053\u5c71\u7684\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u8001\u592b\u662f\u6b66\u5173\u9053\u4e0a\u7684\u3002\u5c0f\u5b50\uff0c\u4f60\u5c31\u7528\u8fd9\u79cd\u6001\u5ea6\u5bf9\u8001\u592b\u8bb2\u8bdd\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u66b4\u58f0\u9053\uff1a\u201c\u8001\u5b50\u5bf9\u4f60\u8fd9\u6837\u8bb2\u8bdd\uff0c\u5df2\u7ecf\u591f\u5ba2\u6c14\u4e86\uff1f\u201d \u3000\u3000\u5c82\u77e5\u4ed6\u7684\u8bdd\u6700\u540e\u4e00\u4e2a\u5b57\u5c1a\u5728\u820c\u5c16\u4e0a\u6253\u8f6c\uff0c\u7a81\u95fb\u4e24\u58f0\u8106\u54cd\uff0c\u53cc\u988a\u5df2\u4e2d\u4e86\u4e24\u8bb0\u91cd\u63b4\uff0c\u6253\u5f97\u4ed6 \u773c\u524d\u91d1\u661f\u76f4\u5192\u3002 \u3000\u3000\u8bf4\u5b9e\u5728\u7684\uff0c\u4e01\u6d9b\u771f\u5f04\u4e0d\u6e05\u8fd9\u4e24\u8bb0\u8033\u5149\u662f\u8c01\u63b4\u7684\uff0c\u56e0\u4e3a\u5f53\u4ed6\u5b9a\u775b\u5bdf\u770b\u65f6\uff0c\u9a7c\u80cc\u8001\u4eba\u4f9d\u7136\u7ad9 \u4f4f\u2014\u4e08\u5f00\u5916\uff0c\u548c\u5148\u524d\u7684\u5f62\u8c61\u534a\u70b9\u672a\u53d8\uff0c \u3000\u3000\u5c31\u50cf\u6839\u672c\u6ca1\u53d1\u751f\u8fc7\u4ec0\u4e48\u4e8b\u4e00\u822c\u3002 \u3000\u3000\u4e01\u6d9b\u5927\u611f\u4e00\u6014\uff0c\u6342\u7740\u9762\u988a\u559d\u9053\uff1a\u201c\u8001\u5c0f\u5b50\uff0c\u521a\u624d\u53ef\u662f\u4f60\u6253\u5728\u4e0b\u7684\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u6478\u4e86\u6478\u4e0b\u5df4\u9053\uff1a\u201c\u4f60\u95ee\u95ee\u4ed6\u4eec\uff0c\u662f\u5426\u4ed6\u4eec\u4e09\u4e2a\u6253\u7684\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u8138\u4e0a\u9752\u7b4b\u66b4\u8d77\uff0c\u4e00\u65b9\u9762\u4e5f\u662f\u6012\u6781\u800c\u751f\uff0c\u82e5\u4ed6\u80fd\u7406\u667a\u4e00\u70b9\uff0c\u5e94\u5f53\u77e5\u9053\u5bf9\u65b9\u7684\u6b66\u529f\u6bd4\u81ea \u5df1\u9ad8\u51fa\u592a\u591a\uff0c\u504f\u504f\u4ed6\u5df2\u7ecf\u6012\u6781\u4e0d\u987e\u4e00\u5207\uff0c \u53cd\u7579\u89e3\u4e0b\u539a\u80cc\u9b3c\u5934\u5200\uff0c\u5927\u559d\u9053\uff1a\u201c\u8001\u5c0f\u5b50\uff0c\u8001\u5b50\u4e00\u5200\u628a\u4f60\u5288\u6210\u4e24\u5f00\uff01\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u53cd\u800c\u4e0d\u518d\u52a8\u6012\uff0c\u6b6a\u5634\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u53ea\u7ba1\u8fc7\u6765\uff0c\u8001\u592b\u811a\u4e0b\u82e5\u52a8\u4e00\u52a8\uff0c\u5c31\u7b97 \u8f93\u4e86\u3002\u201d \u3000\u3000\u6b64\u523b\u4e01\u6d9b\u867d\u5df2\u89c9\u51fa\u9762\u524d\u8fd9\u5c0f\u8001\u5934\u513f\u8eab\u624b\u4e0d\u51e1\uff0c\u4f46\u5374\u4e0d\u76f8\u4fe1\u4ed6\u5728\u8fce\u6218\u65f6\u80fd\u811a\u4e0b\u4e0d\u52a8\uff0c\u81ea\u5df1 \u7684\u539a\u80cc\u9b3c\u5934\u5200\u91cd\u6709\u56db\u4e94\u5341\u65a4\uff0c\u4e00\u5200\u4e0b\u53bb\uff0c\u52bf\u5982\u96f7\u9706\u4e07\u94a7\uff0c\u5373\u4fbf\u5bf9\u65b9\u662f\u795e\u4ed9\uff0c\u4e5f\u51b3\u4e0d\u53ef\u80fd\u6beb\u4e0d \u79fb\u52a8\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u4ed6\u6b64\u523b\u867d\u4e0d\u6562\u5b58\u6709\u80dc\u8fc7\u5bf9\u65b9\u4e4b\u5fc3\uff0c\u81f3\u5c11\u6709\u4fe1\u5fc3\u53ef\u4ee5\u628a\u5bf9\u65b9\u903c\u5f97\u811a\u4e0b\u79fb\u52a8\u3002 \u3000\u3000\u4f46\u7518\u9716\u5374\u603b\u6bd4\u4ed6\u6709\u5934\u8111\uff0c\u6025\u6025\u62c9\u4f4f\u4e01\u6d9b\u9053\uff1a\u201c\u4f7f\u4e0d\u5f97\uff0c\u522b\u81ea\u8ba8\u82e6\u5403\u4e86\uff01\u201d \u3000\u3000\u4e01\u6d9b\u5de6\u81c2\u4e00\u7529\uff0c\u62e8\u5f00\u7518\u9716\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4f7f\u4e0d\u5f97\uff0c\u6211\u53ea\u8981\u903c\u4ed6\u79fb\u52a8\u5c31\u6210\u4e86\uff0c\u9664\u975e\u4ed6\u662f\u94c1\u6253 \u7f57\u6c49\uff0c\u94dc\u94f8\u91d1\u521a\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u521a\u8bf4\u5b8c\uff0c\u4fbf\u4e00\u4e2a\u864e\u8df3\uff0c\u534a\u7a7a\u4e2d\u624b\u8d77\u5200\u843d\uff0c\u95ea\u7535\u822c\u731b\u5411\u9a7c\u80cc\u8001\u4eba\u5934\u9876\u5288\u4e0b\u3002 \u3000\u3000\u4ed6\u672c\u6765\u8eab\u5f62\u6709\u5982\u4e00\u5ea7\u94c1\u5854\uff0c\u62a1\u5200\u5288\u4e0b\uff0c\u5a01\u52bf\u5f53\u771f\u5413\u4eba\u3002 \u3000\u3000\u800c\u9a7c\u80cc\u8001\u4eba\uff0c\u8eab\u9ad8\u4e0d\u8fc7\u56db\u5c3a\uff0c\u76f8\u5f62\u4e4b\u4e0b\uff0c\u5c31\u50cf\u731b\u864e\u640f\u5154\u4e00\u822c\uff0c \u3000\u3000\u5176\u7ed3\u679c\u5982\u4f55\uff0c\u8fde\u7518\u9716\u4e5f\u4f3c\u4e4e\u96be\u4ee5\u9884\u6599\u3002 \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c \u516d \u56de\u3000\u8fc7\u5173\u6597\u201c\u5c06\u201d \u3000\u3000\u5c31\u5728\u4e01\u6d9b\u4eba\u5728\u534a\u7a7a\uff0c\u5200\u52bf\u5c1a\u672a\u843d\u4e0b\u4e4b\u9645\uff0c\u7a81\u89c1\u9a7c\u80cc\u8001\u4eba\u8eab\u5b50\u5fae\u5fae\u4e00\u4ef0\uff0c\u53cc\u638c\u659c\u659c\u63a8\u51fa\u3002 \u3000\u3000\u53ea\u542c\u5176\u58f0\u5982\u51fb\u8d25\u9769\uff0c\u4e01\u6d9b\u7684\u5e9e\u5927\u8eab\u8eaf\uff0c\u7adf\u7136\u4e0d\u8fdb\u53cd\u9000\uff0c\u5411\u540e\u5012\u6454\u51fa\u53bb\uff0c\u6709\u5982\u65ad\u7ebf\u98ce\u7b5d\u822c\uff0c \u76f4\u5012\u98de\u51fa\u4e24\u4e09\u4e08\uff0c\u624d\u518d\u629b\u843d\u5730\u9762\uff0c\u8fde\u6253\u597d\u51e0\u4e2a\u6eda\u7ffb\uff0c\u603b\u7b97\u7070\u5934\u571f\u8138\u7684\u52c9\u5f3a\u722c\u8d77\uff0c\u4f46\u90a3\u539a\u80cc\u9b3c \u5934\u5200\uff0c\u5374\u65e9\u5df2\u4e0d\u5728\u624b\u4e2d\u4e86\u3002 \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u8fd9\u53cc\u638c\u63a8\u51fa\u7684\u5a01\u529b\uff0c\u5c82\u6b62\u5413\u4eba\uff0c\u7b80\u76f4\u591f\u5f97\u4e0a\u662f\u60ca\u5929\u52a8\u5730\u3002 \u3000\u3000\u597d\u5728\u4ed6\u662f\u5411\u4e0a\u63a8\u51fa\uff0c\u82e5\u504f\u5411\u4e0b\u65b9\uff0c\u7ad9\u5728\u9644\u8fd1\u7684\u7518\u9716\uff0c\u4e00\u5b9a\u4e5f\u4f1a\u906d\u6b83\u3002 \u3000\u3000\u8fd9\u65f6\u4e01\u6d9b\u65e9\u5df2\u5168\u8eab\u9aa8\u9165\u8089\u9ebb\uff0c\u82e5\u4e0d\u662f\u7518\u9716\u53ca\u65f6\u8ddf\u8fc7\u53bb\u6276\u4f4f\uff0c\u7ad9\u8d77\u540e\u4ecd\u8981\u5012\u4e0b\u3002 \u3000\u3000\u7518\u9716\u6276\u7740\u4e01\u6d9b\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u8fd9\u4e0d\u662f\u81ea\u627e\u82e6\u5403\u4e48\uff1f\u5148\u524d\u6328\u90a3\u4e24\u8033\u5149\uff0c\u4f60\u5c31\u8be5\u77e5\u9053\u8fd9\u4f4d\u8001\u524d \u8f88\u662f\u4e16\u6240\u7f55\u89c1\u7684\u7edd\u9876\u9ad8\u4eba\uff0c\u597d\u6c49\u4e0d\u5403\u773c\u524d\u4e8f\uff0c\u4f55\u5fc5\u81ea\u8ba8\u82e6\u5403\uff01\u201d \u3000\u3000\u6709\u4e86\u8fd9\u7b2c\u4e8c\u6b21\u5207\u8eab\u6559\u8bad\uff0c\u4e01\u6d9b\u603b\u7b97\u4e0d\u6562\u518d\u901e\u5f3a\uff0c\u4e0d\u8fc7\u4ed6\u5fc3\u91cc\u5374\u5728\u8ff7\u60d1\uff0c\u81ea\u5df1\u95ef\u8361\u6c5f\u6e56\u5341 \u51e0\u5e74\uff0c\u867d\u7136\u4e5f\u9047\u5230\u6b66\u529f\u8d85\u8fc7\u81ea\u5df1\u7684\u9ad8\u624b\uff0c\u4f46\u81f3\u5c11\u4e5f\u662f\u62da\u4e86\u51e0\u62db\u624d\u843d\u8d25\u7684\uff0c\u5982\u4eca\u600e\u4f1a\u8fde\u4e00\u62db\u90fd \u6ca1\u65bd\u51fa\u4fbf\u8d25\u5f97\u5982\u6b64\u72fc\u72c8\uff0c\u83ab\u975e\u8fd9\u8001\u5c0f\u5b50\u662f\u795e\uff1f\u4e0d\u662f\u4eba\uff1f\u597d\u5728\u9a7c\u80cc\u8001\u4eba\u89c1\u597d\u5c31\u6536\u3002 \u3000\u3000\u53ef\u4ee5\u60f3\u89c1\uff0c\u4ed6\u662f\u4e0d\u5c51\u518d\u548c\u4e01\u6d9b\u52a8\u624b\uff0c\u82e5\u4e00\u518d\u52a8\u624b\uff0c\u4e01\u6d9b\u6709\u547d\u4e48\uff1f \u3000\u3000\u7518\u9716\u53c8\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u5750\u4e0b\u6765\u8c03\u606f\u4e00\u4e0b\uff0c\u7528\u4e0d\u7740\u786c\u6491\u3002\u201d \u3000\u3000\u4e01\u6d9b\u672c\u6765\u8fd8\u8981\u901e\u5f3a\u4e0d\u5750\u3002\u4f46\u4e24\u817f\u762b\u8f6f\uff0c\u5168\u8eab\u4e4f\u529b\u3001\u4e5f\u7531\u4e0d\u5f97\u4ed6\u4e0d\u5750\u3002 \u3000\u3000\u7518\u9716\u56de\u8fc7\u8eab\u6765\uff0c\u518d\u8d70\u56de\u539f\u4f4d\uff0c\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u65b9\u624d\u591a\u8499\u8001\u524d\u8f88\u624b\u4e0b\u7559\u60c5\uff0c\u8ba9\u6211\u4eec\u8fd9\u4f4d\u540c \u4f34\u8fd8\u80fd\u4fdd\u4f4f\u4e00\u547d\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u9053\uff1a\u201c\u8001\u592b\u7684\u8fd9\u4e00\u5173\u3002\u53ea\u5728\u6d4b\u9a8c\u6765\u4eba\u7684\u8eab\u624b\u662f\u5426\u6709\u8d44\u683c\u8fdb\u5173\uff0c\u5949\u547d\u4e0d\u5f97\u4f24\u4eba\uff0c \u5426\u5219\u5c82\u80fd\u7559\u4ed6\u6d3b\u547d\uff01\u201d \u3000\u3000\u7518\u9716\u62b1\u62f3\u53c8\u4e00\u793c\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u665a\u8f88\u4eec\u90fd\u8be5\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u7ffb\u4e86\u7ffb\u5012\u4e09\u89d2\u773c\u9053\uff1a\u201c\u4f60\u4eec\u597d\u4e0d\u5bb9\u6613\u6765\u5230\u8fd9\u91cc\uff0c\u4e3a\u4ec0\u4e48\u8981\u56de\u53bb\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u8001\u524d\u8f88\u628a\u5173\uff0c\u4ee5\u665a\u8f88\u4eec\u8fd9\u70b9\u5fae\u672b\u4e4b\u6280\uff0c\u8981\u8fc7\u5173\u4e0d\u662f\u7b49\u4e8e\u5984\u60f3\u4e48\uff0c\u665a\u8f88 \u4eec\u5c82\u6562\u81ea\u4e0d\u91cf\u529b\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u5c0f\u5b50\u8fd9\u4e24\u53e5\u8bdd\uff0c\u8001\u592b\u542c\u6765\u8fd8\u6709\u4e9b\u8212\u670d\u3002\u201d \u3000\u3000\u7518\u9716\u8d81\u673a\u9053\uff1a\u201c\u8001\u524d\u8f88\u8212\u670d\uff0c\u665a\u8f88\u4eec\u5374\u4e0d\u8212\u670d\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u4eec\u8fc7\u4e0d\u4e86\u5173\uff0c\u600e\u80fd\u8212\u670d\u5f97\u8d77\u6765\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u6478\u7740\u5634\u5df4\uff0c\u62bd\u52a8\u7740\u8138\u8089\u5475\u5475\u7b11\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u5b9e\u5bf9\u4f60\u8bf4\uff0c\u82e5\u662f\u7531\u8001\u592b\u628a\u5173\uff0c\u666e\u5929 \u4e4b\u4e0b\uff0c\u53ea\u6015\u6ca1\u4e00\u4e2a\u4eba\u8fdb\u5f97\u4e86\u5173\u7684\uff0c\u4e0d\u8fc7\u8001\u592b\u662f\u5949\u547d\u62db\u5175\u4e70\u9a6c\uff0c\u5c82\u80fd\u5b8c\u5168\u62d2\u4eba\u4e8e\u5343\u91cc\u4e4b\u5916\u3002\u201d \u3000\u3000\u7518\u9716\u542c\u5f97\u4f3c\u61c2\u975e\u61c2\uff0c\u4ed6\u77e5\u9053\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u5b66\u95ee\u6bd4\u81ea\u5df1\u597d\uff0c\u800c\u4e14\u5148\u524d\u9a7c\u80cc\u8001\u4eba\u8fd8\u5bf9\u5cb3\u5c0f \u98de\u52a0\u4ee5\u8d5e\u8d4f\u8fc7\uff0c\u4fbf\u8f6c\u5934\u5411\u5cb3\u5c0f\u98de\u671b\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u89c9\u51fa\u7518\u9716\u662f\u8981\u81ea\u5df1\u548c\u5bf9\u65b9\u63a5\u8c08\uff0c\u8fde\u5fd9\u4e5f\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u60a8\u662f\u8bf4\u53ef\u4ee5\u901a\u878d \u6211\u4eec\u4e00\u4e0b\uff0c\u5bf9\u4e48\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u679c\u7136\u5bf9\u5cb3\u5c0f\u98de\u9887\u5177\u597d\u611f\uff0c\u970e\u65f6\u8138\u4e0a\u6709\u4e86\u7b11\u5bb9\uff0c\u4e0d\u8fc7\u4ed6\u6700\u597d\u522b\u6709\u7b11\u5bb9\uff0c\u6709\u4e86\u7b11\u5bb9 \u53cd\u800c\u66f4\u96be\u770b\u3002 \u3000\u3000\u4ed6\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u53ea\u80fd\u7b97\u5bf9\u4e86\u4e00\u534a\u3002\u201d \u3000\u3000\u201c\u8001\u524d\u8f88\u53ef\u5426\u8bf4\u660e\u767d\u4e9b\uff1f\u201d \u3000\u3000\u201c\u597d\uff0c\u73b0\u5728\u8ba9\u4f60\u4eec\u542c\u660e\u767d\uff0c\u8001\u592b\u81ea\u4ece\u5949\u547d\u628a\u5b88\u6b66\u5173\u4ee5\u6765\uff0c\u5df2\u6709\u6570\u4e0d\u6e05\u7684\u6b66\u6797\u4eba\u7269\u8fc7\u4e86\u5173\uff0c \u4f46\u4ed6\u4eec\u7684\u6b66\u529f\uff0c\u90fd\u4e0d\u5982\u8001\u592b\uff0c\u4ed6\u4eec\u662f\u600e\u6837\u8fc7\u4e86\u5173\uff0c\u4f60\u731c\u731c\u770b\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u8001\u524d\u8f88\u624b\u4e0b\u7559\u60c5\uff0c\u6545\u610f\u653e\u4e86\u4ed6\u4eec\u4e00\u9a6c\uff1f\u201d \u3000\u3000\u201c\u4e0d\u662f\uff0c\u82e5\u8001\u592b\u624b\u4e0b\u7559\u60c5\uff0c\u5c82\u4e0d\u7b49\u4e8e\u81ea\u5df1\u8f93\u4e86\uff0c\u8001\u592b\u4ec0\u4e48\u65f6\u5019\u8f93\u8fc7\u4eba\u6765\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u665a\u8f88\u5c31\u731c\u4e0d\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u7531\u8001\u592b\u76f4\u8bf4\u4e86\u5427\uff0c\u8001\u592b\u4e00\u5411\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u529b\uff0c\u53ea\u8981\u88ab\u8001\u592b\u770b\u4e2d\uff0c\u6709\u65f6\u7528\u4e0d\u7740\u6d4b\u8bd5 \u5c31\u76f4\u63a5\u653e\u4ed6\u8fdb\u5173\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4ee5\u524d\u8fc7\u4e86\u5173\u7684\u4eba\u90fd\u662f\u6ca1\u7ecf\u8fc7\u6d4b\u8bd5\u7684\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u662f\uff0c\u5982\u679c\u8001\u592b\u770b\u4e0d\u900f\u7684\uff0c\u90a3\u5c31\u975e\u6d4b\u8bd5\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u201c\u4ee5\u8001\u524d\u8f88\u8d85\u51e1\u7edd\u4fd7\u7684\u6b66\u529f\uff0c\u82e5\u7ecf\u6d4b\u8bd5\uff0c\u6839\u672c\u4e0d\u53ef\u80fd\u6709\u8fc7\u5f97\u4e86\u5173\u7684\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u5475\u5475\u7b11\u9053\uff1a\u201c\u7684\u786e\u5982\u6b64\uff0c\u5982\u679c\u53ea\u6709\u80dc\u8fc7\u8001\u592b\u7684\u624d\u80fd\u8fc7\u5173\uff0c\u90a3\u5c31\u6c38\u8fdc\u65e0\u6cd5\u62db\u5175\u4e70 \u9a6c\u4e86\uff0c\u6240\u4ee5\u8001\u592b\u4e0d\u5f97\u53e6\u5916\u8ba2\u51fa\u4e00\u4e2a\u6807\u51c6\uff0c\u90a3\u5c31\u662f\u7531\u8001\u592b\u7684\u624b\u4e0b\u4eba\u8d1f\u8d23\u6d4b\u8bd5\uff0c\u6765\u4eba\u53ea\u8981\u80fd\u80dc\u8fc7 \u8001\u592b\u7684\u624b\u4e0b\u4eba\uff0c\u4fbf\u53ef\u8fc7\u5173\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5c31\u8bf7\u8001\u524d\u8f88\u628a\u60a8\u90a3\u4f4d\u624b\u4e0b\u4eba\u8bf7\u51fa\u6765\u5982\u4f55\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u53c8\u662f\u4e00\u7b11\u9053\uff1a\u201c\u8001\u592b\u90fd\u4e0d\u6025\uff0c\u4f60\u6025\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u665a\u8f88\u662f\u5e0c\u671b\u901f\u6218\u901f\u51b3\uff0c\u80fd\u8fc7\u5f97\u4e86\u5173\u5c31\u5c3d\u65e9\u8fc7\uff0c\u8fc7\u4e0d\u4e86\u4e5f\u597d\u5feb\u4e9b\u56de\u53bb\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1b\u201c\u4f60\u4eec\u4e24\u4e2a\u5e74\u8f7b\u7684\u7528\u4e0d\u7740\u6d4b\u8bd5\u4e86\uff0c\u8001\u592b\u76f8\u4fe1\u4f60\u4eec\u80fd\u8fc7\u4e86\u5173\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5012\u4e0d\u60f3\u9886\u8fd9\u5206\u4eba\u60c5\uff0c\u6717\u58f0\u9053\uff1a\u201c\u627f\u8499\u8001\u524d\u8f88\u539a\u7231\uff0c\u665a\u8f88\u611f\u6fc0\u4e0d\u5c3d\uff0c\u4f46\u665a\u8f88\u82e5\u4e0d\u7ecf \u6d4b\u8bd5\u8fc7\u5173\uff0c\u5c06\u6765\u88ab\u4eba\u770b\u51fa\u6b66\u529f\u592a\u5dee\uff0c\u5c82\u4e0d\u5bf9\u8001\u524d\u8f88\u4e5f\u6709\u5f71\u54cd\uff1f\u201d \u3000\u3000\u201c\u8001\u592b\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u529b\u3002\u201d \u3000\u3000\u201c\u8001\u524d\u8f88\u53ef\u60f3\u5230\u603b\u6709\u770b\u8d70\u773c\u7684\u65f6\u5019\uff1f\u201d \u3000\u3000\u201c\u5373\u4fbf\u8001\u592b\u770b\u8d70\u4e86\u773c\uff0c\u4e5f\u5fc5\u987b\u8ba9\u4f60\u4eec\u4e24\u4e2a\u8fc7\u5173\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u8001\u592b\u5df2\u63a5\u5230\u524d\u9762\u6587\u5173\u5218\u7edf\u9886\u7684\u901a\u62a5\uff0c\u8bf4\u4f60\u4eec\u4e24\u4e2a\u5e74\u8f7b\u7684\u6587\u79d1\u6210\u7ee9\u4f18\u5f02\uff0c\u662f\u96be\u5f97\u4e00 \u89c1\u7684\u4eba\u624d\uff0c\u8001\u592b\u4e00\u5411\u6700\u662f\u7231\u624d\uff0c\u5c82\u6709\u628a\u4eba\u624d\u653e\u8d70\u4e3a\u5916\u4eba\u6240\u7528\u7684\u9053\u7406\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6307\u4e86\u6307\u7518\u9716\u548c\u4e01\u6d9b\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u4f4d\u6628\u5929\u6587\u79d1\u6d4b\u8bd5\u65f6\uff0c\u7167\u6837\u4e5f\u662f\u5bf9\u7b54\u5982\u6d41\uff0c\u6ca1\u5403\u534a \u4e2a\u87ba\u4e1d\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u90a3\u7b97\u4ec0\u4e48\u5b66\u95ee\uff0c\u4e00\u4e2a\u53ea\u4f1a\u540a\u574e\u513f\uff0c\u4e00\u4e2a\u77e5\u9053\u51e0\u4e2a\u5730\u540d\uff0c\u82e5\u8fd9\u6837 \u5c31\u7b97\u6709\u5b66\u95ee\uff0c\u5927\u5bb6\u5c3d\u53ef\u4ee5\u4e0d\u5fc5\u8bfb\u4e66\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u4ed6\u4eec\u4e24\u4f4d\u8fd8\u8981\u6d4b\u8bd5\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u4e2a\u53ea\u9700\u6d4b\u8bd5\u4e00\u4e2a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u53ea\u6d4b\u8bd5\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u9a7c\u8f88\u8001\u4eba\u9053\uff1a\u201c\u5148\u524d\u88ab\u8001\u592b\u4e00\u638c\u51fb\u98de\u7684\u90a3\u4e2a\u6d51\u5c0f\u5b50\u7528\u4e0d\u7740\u6d4b\u8bd5\uff0c\u8001\u592b\u51b3\u5b9a\u4e0d\u7528\u4ed6\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6b63\u8272\u9053\uff1a\u201c\u8001\u524d\u8f88\u8fd9\u6837\u505a\uff0c\u672a\u514d\u592a\u4e0d\u516c\u5e73\u4e86\uff01\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u4e0d\u516c\u5e73\uff1f\u4ed6\u7684\u6b66\u529f\u6839\u672c\u4e0d\u80fd\u4e0e\u8001\u592b\u7684\u90a3\u540d\u624b\u4e0b\u76f8\u6bd4\uff0c\u8bd5\u4e86\u4e5f\u662f\u767d\u8bd5\uff0c\u53ea\u4e0d\u8fc7\u591a \u6d6a\u8d39\u65f6\u95f4\u800c\u5df2\u3002\u201d \u3000\u3000\u5c82\u77e5\u9a7c\u80cc\u8001\u4eba\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5df2\u4f7f\u5f97\u5728\u65c1\u7684\u7518\u9716\u5fc3\u60ca\u80c6\u98a4\uff0c\u56e0\u4e3a\u4ed6\u7684\u6b66\u529f\uff0c\u548c\u4e01\u6d9b\u5728\u4f2f\u4ef2\u4e4b \u95f4\uff0c\u4e01\u6d9b\u4e0d\u662f\u9a7c\u80cc\u8001\u4eba\u624b\u4e0b\u7684\u5bf9\u624b\uff0c\u81ea\u5df1\u7167\u6837\u4e5f\u5fc5\u843d\u8d25\u3002 \u3000\u3000\u53e6\u5916\uff0c\u4ed6\u4e00\u5411\u548c\u4e01\u6d9b\u7126\u4e0d\u79bb\u5b5f\uff0c\u5b5f\u4e0d\u79bb\u7126\uff0c\u5373\u4fbf\u81ea\u5df1\u8fc7\u4e86\u5173\uff0c\u4e5f\u8981\u548c\u4e01\u6d9b\u5206\u624b\uff0c\u60f3\u5230\u8fd9 \u91cc\uff0c\u5927\u6709\u96be\u4ee5\u81ea\u5904\u4e4b\u6982\u3002 \u3000\u3000\u4f46\u4e0d\u7ba1\u5982\u4f55\uff0c\u6b64\u523b\u4ed6\u53ea\u6709\u765e\u86e4\u87c6\u57ab\u684c\u817f\u2014\u2014\u786c\u6491\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6b63\u8981\u518d\u5f00\u95e8\uff0c\u9a7c\u80cc\u8001\u4eba\u5df2\u56de\u5934\u558a\u9053\uff1a\u201c\u94c1\u5934\u51fa\u6765\uff01\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u521a\u8fc7\uff0c\u8eab\u540e\u74f6\u9888\u822c\u7684\u72ed\u8c37\u58c1\u4e0a\u7adf\u5192\u51fa\u4e00\u622a\u94c1\u7985\u6756\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7b49\u4eba\u5230\u8fd9\u65f6\u624d\u6ce8\u610f\u5230\u539f\u6765\u90a3\u8c37\u58c1\u7684\u72ed\u7a84\u90e8\u4efd\u4e5f\u6709\u51e0\u5904\u6d1e\u95e8\uff0c\u4e0d\u8fc7\u7531\u6d1e\u95e8\u53ea\u5192\u51fa\u4e00 \u622a\u7985\u6756\u800c\u4e0d\u89c1\u4eba\uff0c\u603b\u662f\u6709\u4e9b\u5947\u602a\u3002 \u3000\u3000\u53ea\u542c\u9a7c\u80cc\u8001\u4eba\u518d\u558a\u9053\uff1a\u201c\u94c1\u5934\uff0c\u4f60\u5728\u91cc\u9762\u78e8\u53e8\u4ec0\u4e48\uff01\u201d \u3000\u3000\u7ec8\u4e8e\u5728\u94c1\u7985\u6756\u4e4b\u540e\uff0c\u7d27\u8ddf\u7740\u8d70\u51fa\u4e00\u4e2a\u5947\u5f62\u602a\u72b6\u7684\u77ee\u80d6\u548c\u5c1a\u3002 \u3000\u3000\u90a3\u548c\u5c1a\u867d\u7136\u4e0d\u9ad8\uff0c\u4f46\u94c1\u7985\u6756\u5374\u8db3\u6709\u4e00\u4e08\u957f\uff0c\u800c\u4e14\u7c97\u5982\u624b\u81c2\uff0c\u602a\u4e0d\u5f97\u4ed6\u51fa\u6d1e\u65f6\u5148\u89c1\u94c1\u7985\u6756 \u800c\u4e0d\u51fa\u4eba\u3002 \u3000\u3000\u8fd9\u548c\u5c1a\u8d70\u8d77\u8def\u6765\u6447\u6447\u6446\u6446\uff1f\u6d3b\u50cf\u9e2d\u5b50\u822c\uff0c\u4e00\u6b65\u4e00\u8e4b\uff0c\u53c8\u50cf\u5f97\u4e86\u67d0\u79cd\u82b1\u67f3\u75c5\uff0c\u4e24\u817f\u65e0\u6cd5\u9760 \u62e2\u3002\u56e0\u4e4b\u90a3\u8d70\u8def\u59ff\u52bf\uff0c\u7b80\u76f4\u4ee4\u4eba\u53d1\u5671\u3002 \u3000\u3000\u4ed6\u672c\u6765\u8eab\u6750\u77ee\u80d6\uff0c\u4e24\u817f\u5947\u77ed\uff0e\u8fd9\u6837\u4e00\u6765\uff0c\u5c31\u8d8a\u53d1\u663e\u5f97\u77ee\u4e86\u3002 \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u6709\u8fd9\u79cd\u624b\u4e0b\uff0c\u79f0\u5f97\u4e0a\u662f\u6b66\u5927\u90ce\u73a9\u591c\u732b\u5b50\u2014\u2014\u4ec0\u4e48\u4eba\u73a9\u4ec0\u4e48\u9e1f\u3002 \u3000\u3000\u81f3\u4e8e\u8bf4\u4ed6\u5947\u5f62\u602a\u72b6\uff0c\u90a3\u4e5f\u51b3\u4e0d\u8fc7\u4efd\uff0e\u8fd9\u548c\u5c1a\u4eba\u867d\u77ee\uff0c\u8111\u888b\u548c\u809a\u5b50\u5374\u5947\u5927\u3002 \u3000\u3000\u4ed6\u90a3\u8111\u888b\uff0c\u5176\u5927\u5982\u6597\uff0c\u800c\u4e14\u5929\u7136\u6cdb\u7740\u6df1\u9752\u8272\uff0c\u7684\u786e\u50cf\u94c1\u6253\u7684\u5dee\u4e0d\u591a\uff0c\u602a\u4e0d\u5f97\u9a7c\u80cc\u8001\u4eba\u53eb \u4ed6\u94c1\u5934\u3002 \u3000\u3000\u81f3\u4e8e\u4ed6\u90a3\u809a\u5b50\uff0c\u5c31\u50cf\u4e00\u9762\u9f13\u3002\u53ef\u80fd\u4e3a\u4e86\u663e\u793a\u4ed6\u7684\u80d6\uff0c\u7279\u522b\u628a\u7070\u8272\u50e7\u888d\u64a9\u8d77\u6765\u522b\u5728\u8eab\u540e\u3002 \u3000\u3000\u522b\u5916\uff0c\u4ed6\u8fd8\u6545\u610f\u628a\u88e4\u5e26\u675f\u5f97\u5f88\u4f4e\uff0c\u4ee5\u4f7f\u4ed6\u7684\u809a\u5b50\u66f4\u663e\u7a81\u51fa\u3002\u4e0d\u8fc7\u8fd9\u6837\u4e00\u6765\uff0c\u5c31\u5b9e\u5728\u6709\u4e9b \u4e0d\u582a\u5165\u76ee\uff0c\u56e0\u4e3a\u8fde\u5c0f\u8179\u5e95\u4e0b\u90a3\u64ae\u6bdb\u90fd\u9732\u4e86\u51fa\u6765\uff0c\u82e5\u88e4\u5e26\u518d\u5f80\u4e0b\u4f4e\u4e00\u5bf8\uff0c\u53ea\u6015\u5c31\u8981\u5927\u548c\u5c1a\u5c0f\u548c \u5c1a\u4e00\u9f50\u73b0\u5f62\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u7518\u9716\u548c\u4e1a\u5df2\u8c03\u606f\u5b8c\u6bd5\u7ad9\u8d77\u6765\u7684\u4e01\u6d9b\uff0c\u5374\u4e0d\u89c9\u8138\u8272\u5927\u53d8\uff0c\u56e0\u4e3a\u4ed6\u4eec\u5df2\u8ba4\u51fa\u8fd9\u548c \u5c1a\u8d6b\u7136\u662f\u5f53\u5e74\u5728\u4e2d\u539f\u6b66\u6797\u4ee4\u4eba\u95fb\u540d\u4e27\u80c6\u7684\u94c1\u5934\u9640\u3002 \u3000\u3000\u63d0\u8d77\u94c1\u5934\u9640\uff0c\u81f3\u4eca\u7edd\u5c11\u6709\u4eba\u77e5\u9053\u4ed6\u7684\u8eab\u5bb6\u59d3\u540d\uff0c\u636e\u8bf4\u4ed6\u539f\u662f\u5c11\u6797\u5f1f\u5b50\uff0c\u56e0\u72af\u4e86\u4f5b\u95e8\u6212\u89c4 \u800c\u88ab\u9010\u51fa\u95e8\u5899\uff0c\u4ece\u6b64\u4fbf\u5728\u5927\u6c5f\u5357\u5317\u3001\u4e09\u5c71\u4e94\u5cb3\u5230\u5904\u95ef\u8361\uff0c\u6b7b\u5728\u4ed6\u624b\u4e0b\u7684\u6b66\u6797\u4eba\u7269\uff0c\u591a\u5f97\u4e0d\u53ef \u80dc\u8ba1\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u5e76\u975e\u5b8c\u5168\u9ed1\u9053\u4eba\u7269\uff0c\u6709\u65f6\u8def\u89c1\u4e0d\u5e73\uff0c\u4e5f\u505a\u4e9b\u597d\u4e8b\uff0c\u53ef\u8bf4\u662f\u4ea6\u6b63\u4ea6\u90aa\u3002 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u5728\u5341\u51e0\u5e74\u524d\u521d\u51fa\u9053\u65f6\uff0c\u66fe\u89c1\u8fc7\u4ed6\u51e0\u6b21\uff0c\u6240\u4ee5\u5370\u8c61\u9887\u6df1\u3002\u53ea\u662f\u8fd9\u94c1\u5934\u9640\u5374\u4e8e\u4e03 \u516b\u5e74\u524d\u5728\u6c5f\u6e56\u4e0a\u9500\u58f0\u533f\u8ff9\uff0c\u6709\u7684\u8bf4\u4ed6\u5df2\u7ecf\u6b7b\u5728\u4e5d\u5bab\u5c71\uff0c\u6709\u7684\u8bf4\u4ed6\u53bb\u4e86\u897f\u57df\uff0c\u4e07\u60f3\u4e0d\u5230\u4eca\u5929\u7adf \u7136\u8fd9\u91cc\u51fa\u73b0\u3002 \u3000\u3000\u6b64\u523b\uff0c\u7518\u9716\u548c\u4e01\u6d9b\u4e00\u89c1\u51fa\u6765\u6d4b\u8bd5\u4ed6\u4eec\u6b66\u529f\u7684\u7adf\u662f\u94c1\u5934\u9640\uff0c\u54ea\u80fd\u4e0d\u5927\u60ca\u5931\u8272\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u672a\u548c\u94c1\u5934\u9640\u4ea4\u8fc7\u624b\uff0c\u4f46\u5728\u60f3\u50cf\u4e2d\u4e5f\u4e0d\u96be\u9884\u6599\u81ea\u5df1\u7684\u6b66\u529f\uff0c\u548c\u5bf9\u65b9\u5fc5\u5b9a\u76f8\u5dee\u60ac\u6b8a\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u7518\u9716\u548c\u4e01\u6d9b\u5df2\u4e0d\u7981\u5446\u5728\u5f53\u573a\u3002 \u3000\u3000\u81f3\u4e8e\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\uff0c\u4ed6\u4eec\u4ece\u524d\u5f53\u7136\u6ca1\u89c1\u8fc7\u94c1\u5934\u9640\uff0c\u8fde\u542c\u4e5f\u6ca1\u542c\u8bf4\u8fc7\uff0c\u4ed6\u4eec\u73b0\u5728\u53ea\u662f\u89c9 \u5f97\u8fd9\u548c\u5c1a\u65e2\u80ae\u810f\u53c8\u53ef\u7b11\u3002 \u3000\u3000\u94c1\u5934\u9640\u6765\u5230\u9a7c\u80cc\u8001\u4eba\u8ddf\u524d\uff0c\u4e00\u624b\u7ad6\u8d77\u94c1\u7985\u6756\uff0c\u3000\u4e00\u624b\u5355\u638c\u7acb\u80f8\u9053\uff1a\u201c\u7edf\u9886\u5524\u6d12\u5bb6\uff0c\u6709\u4e8b \u4e48\uff1f\u201d \u3000\u3000\u522b\u770b\u4ed6\u4e2a\u5b50\u4e0d\u9ad8\uff0c\u51fa\u58f0\u4e5f\u662f\u50cf\u9752\u86d9\u4e00\u822c\u7684\u54cd\u4eae\u3002 \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u54fc\u4e86\u2014\u58f0\u9053\uff1a\u201c\u521a\u624d\u662f\u4e0d\u662f\u53c8\u5728\u704c\u9ec4\u6c64\uff1f\u201d \u3000\u3000\u94c1\u5934\u9640\u563f\u563f\u7b11\u9053\uff1a\u201c\u95f2\u7740\u6ca1\u4e8b\uff0c\u4e0d\u559d\u4e24\u76c5\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u5e72\u7684\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u9053\uff1a\u201c\u73b0\u5728\u5c31\u6709\u4e8b\u4e86\uff01\u201d \u3000\u3000\u94c1\u5934\u9640\u6d53\u7709\u95ea\u4e86\u51e0\u95ea\uff0c\u626b\u89c6\u4e86\u5cb3\u5c0f\u98de\u7b49\u56db\u4eba\u4e00\u773c\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u4eec\u56db\u4e2a\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u6ca1\u597d\u6c14\u7684\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\u4eec\u56db\u4e2a\u8fd8\u662f\u8001\u592b\u4e0d\u6210\uff1f\u201d \u3000\u3000\u94c1\u5934\u9640\u54a7\u5634\u50bb\u7b11\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u5c5e\u4e0b\u4e0d\u6562\uff01\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u9053\uff1a\u201c\u6682\u65f6\u522b\u52a8\uff0c\u8001\u592b\u8fd8\u6709\u8bdd\u8ddf\u4ed6\u4eec\u8bb2\u3002\u201d \u3000\u3000\u53ea\u89c1\u94c1\u5934\u9640\u63e1\u6756\u7684\u53f3\u624b\u4e00\u65cb\uff0c\u90a3\u94c1\u7985\u6756\u7acb\u523b\u5165\u5730\u534a\u5c3a\uff0c\u6ca1\u89c1\u4ed6\u5982\u4f55\u4f5c\u52bf\uff0c\u4eba\u5df2\u817e\u7a7a\u800c\u8d77\uff0c \u7adf\u7136\u8f7b\u98d8\u98d8\u7684\u843d\u5728\u94c1\u7985\u6756\u9876\u7aef\uff0c\u7ad9\u5728\u4e0a\u9762\uff0c\u5c45\u7136\u7eb9\u98ce\u4e0d\u52a8\u3002 \u3000\u3000\u5c31\u51ed\u4ed6\u8fd9\u4e00\u624b\u5de5\u592b\uff0c\u5b9e\u5728\u5df2\u591f\u5f97\u4e0a\u4ee4\u4eba\u77a0\u76ee\u7ed3\u820c\uff0c\u5c24\u5176\u7518\u9716\u548c\u4e01\u6d9b\uff0c\u53ea\u770b\u5f97\u8fde\u547c\u5438\u90fd\u51e0 \u4e4e\u95ed\u4f4f\u3002 \u3000\u3000\u53ea\u542c\u9a7c\u80cc\u8001\u4eba\u9053\uff1a\u201c\u5728\u8fd9\u6b66\u5173\u4e4b\u4e0a\uff0c\u8001\u592b\u6709\u5341\u540d\u624b\u4e0b\uff0c\u4ed6\u4eec\u8f6e\u6d41\u8d1f\u8d23\u6d4b\u8bd5\u51c6\u5907\u8fc7\u5173\u7684\u5404 \u5730\u6b66\u6797\u540c\u9053\uff0c\u73b0\u5728\u6b63\u597d\u8f6e\u5230\u4e86\u94c1\u5934\u2026\u2026\u201d \u3000\u3000\u7518\u9716\u60c5\u4e0d\u81ea\u7981\u53eb\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u8fd9\u6837\u672a\u514d\u592a\u5931\u516c\u5e73\uff0c\u8fd9\u4f4d\u94c1\u5934\u6b66\u529f\u592a\u9ad8\u4e86\uff0c\u662f\u5426\u53ef\u4ee5\u53e6 \u5916\u6362\u2014\u4f4d\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u7b11\u9053\uff1a\u201c\u8001\u592b\u7684\u5341\u540d\u624b\u4e0b\u4e2d\uff0c\u94c1\u5934\u7684\u6b66\u529f\uff0c\u53ea\u7b97\u662f\u4e2d\u7b49\uff0c\u53ea\u6709\u4ee5\u4ed6\u6765\u6d4b\u5ea6\u624d\u6700 \u516c\u5e73\uff0c\u4f60\u4eec\u65e2\u4e0d\u8d5a\u4fbf\u5b9c\uff0c\u4e5f\u4e0d\u5403\u4e8f\uff0c\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u7518\u9716\u672c\u6765\u80a4\u8272\u5c31\u767d\uff0c\u8fd9\u4e00\u6765\u8138\u8272\u66f4\u767d\uff0c\u6839\u672c\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u4ef0\u8138\u559d\u9053\uff1a\u201c\u9a6c\u4e0a\u5c31\u8981\u5f00\u59cb\u52a8\u624b\u4e86\uff0c\u4f60\u8fd8\u5728\u4e0a\u9762\u8981\u7684\u4ec0\u4e48\u72d7\u718a\uff01\u201d \u3000\u3000\u94c1\u5934\u9640\u8eab\u5b50\u4e00\u503e\uff0c\u968f\u5373\u843d\u4e0b\u5730\u6765\u3002 \u3000\u3000\u4ed6\u672c\u6765\u80a5\u5f97\u50cf\u732a\uff0c\u4f46\u843d\u5730\u65f6\u5374\u50cf\u98de\u82b1\u4e00\u822c\uff0c\u70b9\u5c18\u4e0d\u60ca\u3002 \u3000\u3000\u94c1\u5934\u9640\u62d4\u8d77\u94c1\u7985\u6756\uff0c\u5411\u524d\u626b\u4e86\u773c\u9053\uff1a\u201c\u7edf\u9886\uff0c\u6d12\u5bb6\u8be5\u5148\u8bd5\u90a3\u4e2a\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u624b\u6307\u7518\u9716\u9053\uff1a\u201c\u90a3\u6839\u767d\u7af9\u7aff\uff01\u201d \u3000\u3000\u94c1\u5934\u9640\u5411\u524d\u8d70\u51fa\u51e0\u6b65\uff0c\u4e5f\u4e00\u6307\u7518\u9716\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u53d1\u7684\u4ec0\u4e48\u6123\uff0c \u3000\u3000\u8fd8\u4e0d\u8fc7\u6765\u63a5\u62db\uff0c\u6d12\u5bb6\u53ea\u51c6\u5907\u653b\u4f60\u4e09\u62db\uff0c\u4e09\u62db\u4e00\u8fc7\uff0c\u5c31\u7b97\u4f60\u53ca\u683c\u4e86\uff01\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u63a5\u9053\uff1a\u201c\u4e0d\u5fc5\u6015\uff0c\u8001\u592b\u65e9\u5df2\u89c4\u5b9a\u8fc7\u94c1\u5934\uff0c\u8bd5\u5f52\u8bd5\uff0c\u51b3\u4e0d\u4f24\u4eba\uff0c\u8fd9\u4e9b\u5e74\u6765\u5230\u672c\u5173 \u53d7\u6d4b\u7684\uff0c\u5c11\u8bf4\u4e5f\u6709\u51e0\u767e\uff0c\u4ed6\u4eec\u80fd\u8fc7\u5173\u5c31\u8fc7\u5173\uff0c\u4e0d\u80fd\u8fc7\u5173\u5c31\u56de\u53bb\uff0c\u6ca1\u4e00\u4e2a\u6b7b\u5728\u8fd9\u91cc\u7684\uff0c\u8001\u592b\u7684 \u5341\u540d\u624b\u4e0b\u51fa\u624b\uff0c\u540a\u4e2a\u574e\u513f\uff0c\u6728\u5320\u6253\u8001\u5a46\u2014\u2014\u81ea\u6709\u5206\u5bf8\u3002\u201d \u3000\u3000\u7518\u9716\u542c\u8bf4\u5bf9\u65b9\u5e76\u4e0d\u4f24\u4eba\uff0c\u5fc3\u60c5\u7ec8\u4e86\u7a0d\u7a0d\u653e\u677e\uff0c\u800c\u4e14\u5728\u4f17\u76ee\u668c\u668c\u4e4b\u4e0b\uff0c\u82e5\u592a\u8fc7\u80c6\u602f\uff0c\u8fd8\u6709 \u4ec0\u4e48\u989c\u9762\u89c1\u4eba\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u4e0d\u89c9\u786c\u8d77\u5934\u76ae\u5411\u524d\u8d70\u51fa\u51e0\u6b65\uff0c\u53cc\u624b\u62b1\u62f3\u8fc7\u989d\u9053\uff1a\u201c\u5728\u4e0b\u8fd8\u6c42\u94c1\u5934\u5927\u5e08\u624b\u4e0b\u7559 \u60c5\uff01\u201d \u3000\u3000\u94c1\u5934\u9640\u89c1\u7518\u9716\u6267\u793c\u751a\u606d\uff0c\u53c8\u53e3\u79f0\u81ea\u5df1\u94c1\u5934\u5927\u5e08\uff0c\u5fc3\u91cc\u4e0d\u89c9\u4e00\u4e50\uff0c\u4e24\u773c\u7ffb\u4e86\u51e0\u7ffb\u9053\uff1a\u201c\u4f60 \u597d\u50cf\u8ba4\u8bc6\u6d12\u5bb6\uff1f\u201d \u3000\u3000\u7518\u9716\u5c82\u80fd\u653e\u5f03\u8d81\u673a\u62cd\u9a6c\u7684\u673a\u4f1a\uff0c\u62b1\u62f3\u53c8\u662f\u4e00\u793c\u9053\uff1a\u201c\u5927\u5e08\u5927\u540d\u9f0e\u9f0e\uff0c\u5f53\u5e74\u5a01\u9707\u6b66\u6797\uff0c\u9ed1 \u767d\u4e24\u9053\uff0c\u54ea\u6709\u4e0d\u77e5\u9053\u60a8\u662f\u9664\u6076\u9504\u5978\u7684\u94c1\u5934\u5927\u5e08\uff0c\u5728\u4e0b\u65e9\u5c31\u60f3\u62dc\u8bc6\u5927\u5e08\uff0c\u53ef\u60dc\u8fd9\u4e9b\u5e74\u6765\uff0c\u5df2\u65e0 \u4eba\u518d\u77e5\u5927\u5e08\u7684\u4fa0\u5f71\u4ed9\u8e2a\uff0c\u60f3\u4e0d\u5230\u4eca\u5929\u80fd\u5728\u8fd9\u91cc\u6709\u5e78\u9082\u9005\u3002\u201d \u3000\u3000\u94c1\u5934\u9640\u4e50\u5f97\u4e24\u773c\u772f\u6210\u4e00\u6761\u7f1d\uff0c\u6478\u5634\u7b11\u9053\uff1a\u201c\u65e2\u7136\u8ba4\u8bc6\u6d12\u5bb6\uff0c\u60f3\u6765\u4f60\u4e5f\u5fc5\u662f\u6709\u5934\u6709\u8138\u7684\u4eba \u7269\uff1f\u201d \u3000\u3000\u7518\u9716\u56de\u5934\u671b\u4e86\u4e01\u6d9b\u4e00\u773c\u9053\uff1a\u201c\u5728\u4e0b\u548c\u8fd9\u4f4d\u59d3\u4e01\u7684\u4e00\u5411\u4f4f\u5728\u592a\u767d\u5c71\u3002\u201d \u3000\u3000\u94c1\u5934\u9640\u773c\u73e0\u4e00\u8f6c\u9053\uff1a\u201c\u83ab\u975e\u2018\u592a\u767d\u53cc\u96c4\u2019\u5c31\u662f\u4f60\u4eec\u4e24\u4e2a\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1b\u201c\u4e0d\u6562\uff0c\u4e0d\u8fc7\u662f\u6c5f\u6e56\u670b\u53cb\u8fd9\u6837\u79f0\u547c\u800c\u5df2\u3002\u201d \u3000\u3000\u94c1\u5934\u9640\u9053\uff1a\u201c\u65e2\u7136\u80fd\u88ab\u6d12\u5bb6\u95fb\u540d\uff0c\u90a3\u4e00\u5b9a\u4e0d\u9519\uff0c\u522b\u803d\u8bef\u65f6\u95f4\uff0c \u3000\u3000\u5148\u653b\u4e00\u62db\u8ba9\u6d12\u5bb6\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u7518\u9716\u7ffb\u51fa\u63d2\u5728\u80cc\u540e\u7684\u4e24\u652f\u94c1\u7b14\uff0c\u811a\u4e0b\u731b\u529b\u4e00\u70b9\uff0c\u4eba\u5df2\u51cc\u7a7a\u800c\u8d77\uff0c\u76f4\u5411\u94c1\u5934\u9640\u5934\u9876\u51b2\u53bb\uff0c \u7136\u540e\u53cc\u7b14\u9f50\u51fa\uff0c\u6233\u5411\u94c1\u5934\u9640\u4e24\u773c\u3002 \u3000\u3000\u94c1\u5934\u9640\u5c45\u7136\u52a8\u4e5f\u4e0d\u52a8\uff0c\u53f3\u624b\u7684\u94c1\u7985\u6756\u4f9d\u7136\u7ad6\u5728\u5730\u4e0a\uff0c\u76f4\u5230\u7518\u9716\u98de\u4e34\u5934\u9876\u4e24\u679d\u7b14\u731b\u70b9\u800c\u4e0b \u65f6\uff0c\u624d\u62ac\u8d77\u5de6\u624b\uff0c\u5de6\u53f3\u5404\u70b9\u4e00\u70b9\uff0c\u53c8 \u3000\u3000\u5411\u524d\u5fae\u5fae\u4e00\u63a8\u3002 \u3000\u3000\u7518\u9716\u987f\u611f\u4e24\u7f15\u6307\u98ce\uff0c\u5c04\u5411\u5de6\u53f3\u53cc\u8155\uff0c\u63a5\u7740\u524d\u80f8\u4e5f\u51b2\u6765\u4e86\u80a1\u5947\u5927\u65e0\u6bd4\u7684\u6697\u52b2\uff0c\u8feb\u5f97\u4ed6\u8eab\u4e0d \u7531\u5df2\u7684\u53c8\u5012\u98de\u56de\u6765\u3002 \u3000\u3000\u5f53\u4ed6\u843d\u5730\u4e4b\u540e\uff0c\u53cc\u8155\u548c\u524d\u80f8\u867d\u4ecd\u5fae\u5fae\u4f5c\u75db\uff0c\u4f46\u5374\u5e76\u4e0d\u5f71\u54cd\u65bd\u5c55\u6b66\u529f\u3002 \u3000\u3000\u663e\u7136\uff0c\u94c1\u5934\u9640\u662f\u5bf9\u4ed6\u5927\u5927\u7559\u60c5\u3002\u82e5\u5bf9\u65b9\u628a\u94c1\u63b8\u6756\u821e\u52a8\u8d77\u6765\uff0c\u540e\u679c\u5c31\u96be\u4ee5\u8bbe\u60f3\u4e86\u3002 \u3000\u3000\u94c1\u5934\u9640\u62db\u62db\u624b\u9053\uff1a\u201c\u518d\u6765\u7b2c\u4e8c\u62db\uff01\u201d \u3000\u3000\u7518\u9716\u5fc3\u77e5\u5bf9\u65b9\u6709\u610f\u653e\u4ed6\u4e00\u9a6c\uff0c\u7565\u4e00\u5598\u606f\uff0c\u53c8\u6b3a\u8eab\u6251\u4e0a\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u4e0d\u518d\u51cc\u7a7a\u4e0b\u51fb\uff0c\u800c\u662f\u63a5\u8fd1\u4e4b\u540e\uff0c\u4e00\u7b14\u6233\u5411\u9762\u988a\uff0c\u4e00\u7b14\u6233\u5411\u5c0f\u8179\u3002 \u3000\u3000\u5c82\u77e5\u94c1\u5934\u9640\u53ea\u628a\u94c1\u7985\u6756\u5411\u524d\u4e00\u5012\uff0c\u521a\u597d\u628a\u4e0a\u4e0b\u88ad\u6765\u7684\u4e24\u53ea\u94c1\u7b14\u8361\u5f00\uff0c\u65f6\u95f4\u65b9\u4f4d\u62ff\u634f\u4e4b\u51c6\uff0c \u7b80\u76f4\u4ee4\u4eba\u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u800c\u4ed6\u5012\u4e0b\u94c1\u7985\u6756\u7684\u90a3\u4e00\u52a8\u4f5c\uff0c\u4e5f\u50cf\u662f\u6beb\u4e0d\u7ecf\u610f\u7684\u4e00\u822c\uff0c\u6839\u672c\u4e0d\u4f3c\u662f\u5728\u8fce\u654c\u3002 \u3000\u3000\u660e\u773c\u4eba\u8c01\u90fd\u53ef\u4ee5\u770b\u5f97\u51fa\uff0c\u53cc\u65b9\u529f\u529b\u5dee\u5f97\u592a\u8fc7\u60ac\u6b8a\u3002 \u3000\u3000\u8fd9\u65f6\u94c1\u5934\u9640\u5374\u5df2\u628a\u94c1\u7985\u6756\u63d2\u5728\u5730\u4e0a\uff0c\u6413\u4e86\u6413\u4e24\u624b\u9053\uff1a\u201c\u518d\u6765\uff0c\u6700\u597d\u76f4\u6233\u6d12\u5bb6\u7684\u53cc\u624b\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u8bdd\uff0c\u7adf\u771f\u628a\u53cc\u81c2\u5e73\u4f38\u800c\u51fa\uff0c\u4f46\u53cc\u638c\u5374\u662f\u7acb\u7740\u7684\uff0c\u4ee5\u4fbf\u4e3a\u7518\u9716\u52a0\u5927\u76ee\u6807\u3002 \u3000\u3000\u7518\u9716\u6765\u4e0d\u53ca\u591a\u60f3\uff0c\u7acb\u5373\u53c8\u7eb5\u8eab\u6251\u4e0a\uff0c\u53cc\u7b14\u76f4\u6233\u5bf9\u65b9\u638c\u5fc3\u3002 \u3000\u3000\u8fd9\u6b21\u53cc\u65b9\u56db\u53ea\u624b\u8fd8\u771f\u662f\u63a5\u4e0a\u4e86\u3002 \u3000\u3000\u4f46\u7518\u9716\u7f29\u56de\u624b\u6765\u65f6\uff0e\u90a3\u4e24\u679d\u94c1\u7b14\u5374\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u539f\u6765\u94c1\u7b14\u5df2\u88ab\u94c1\u5934\u9640\u63e1\u5728\u624b\u4e2d\u3002 \u3000\u3000\u94c1\u5934\u9640\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e24\u679d\u7b14\uff0c\u7528\u6765\u5199\u5b57\u8fd8\u5dee\u4e0d\u591a\uff0c\u62ff\u6765\u4f24\u4eba\uff0c\u7528\u5904\u4e0d\u5927\u3002\u201d \u3000\u3000\u7518\u9716\u50bb\u6123\u6123\u7684\u50f5\u5728\u90a3\u91cc\uff0c\u9762\u5b54\u65e9\u5df2\u80c0\u5f97\u8840\u7ea2\uff0c\u8fde\u8116\u5b50\u90fd\u7ea2\u4e86\u534a\u622a\u3002 \u3000\u3000\u5176\u5b9e\u8fd9\u4e24\u679d\u94c1\u7b14\uff0c\u5404\u957f\u4e00\u5c3a\u6709\u4f59\uff0c\u6709\u5982\u5c0f\u578b\u68d2\u9524\uff0c\u5c24\u5176\u5c16\u7aef\u950b\u5229\u65e0\u6bd4\uff0c\u666e\u901a\u4eba\u60f3\u62ff\u8d77\u6765 \u90fd\u5341\u5206\u5403\u529b\uff0c\u5982\u4eca\u5728\u94c1\u5934\u9640\u624b\u4e2d\uff0c\u7684\u786e\u770b\u8d77\u6765\u5c31\u50cf\u73a9\u5177\u4e00\u822c\u3002 \u3000\u3000\u4ed6\u8bf4\u5b8c\u8bdd\uff0c\u53cc\u8155\u4e00\u6296\uff0c\u4e24\u679d\u7b14\u7acb\u5373\u51fa\u624b\u629b\u51fa\uff0c\u4e00\u9762\u53eb\u9053\uff1a\u201c\u63a5\u4efb\uff01\u201d \u3000\u3000\u7518\u9716\u4f38\u51fa\u53cc\u624b\u63a5\u4f4f\uff0c\u4ed6\u4e0d\u613f\u62ff\u5728\u624b\u4e2d\u663e\u5f97\u4e22\u4eba\uff0c\u968f\u5373\u63d2\u5411\u80cc\u540e\u3002 \u3000\u3000\u94c1\u5934\u9640\u7b11\u9053\uff1a\u201c\u4e8c\u62db\u8fc7\u4e86\uff0c\u4f60\u611f\u89c9\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u7518\u9716\u54ea\u91cc\u8fd8\u7b54\u5f97\u4e0a\u8bdd\u3002 \u3000\u3000\u4ed6\u7684\u786e\u4e0d\u660e\u767d\u8fd9\u6837\u5230\u5e95\u7b97\u4e0d\u7b97\u8fc7\u4e86\u5173\uff0c\u82e5\u8bf4\u4e0d\u7b97\uff0c\u5374\u5df2\u4ea4\u624b\uff0c\u82e5\u8bf4\u7b97\uff0c\u53c8\u62db\u62db\u51fa\u5c3d\u6d0b\u76f8\u3002 \u3000\u3000\u53ea\u542c\u9a7c\u80cc\u8001\u4eba\u9053\uff1b\u201c\u4f60\u8fd8\u7ad9\u5728\u90a3\u91cc\u78e8\u83c7\u4ec0\u4e48\uff1f\u8fd8\u4e0d\u8fc7\u6765\uff01\u201d \u3000\u3000\u7518\u9716\u6123\u4e86\u6123\u9053\uff1b\u201c\u4f60\u8001\u4eba\u5bb6\u8981\u665a\u8f88\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u9053\uff1b\u201c\u94c1\u5934\u5df2\u8bd5\u4e86\u4f60\u4e09\u62db\uff0c\u5f53\u7136\u4f60\u5df2\u7b97\u901a\u8fc7\u6d4b\u8bd5\u3002\u201d \u3000\u3000\u7518\u9716\u8d8a\u53d1\u8138\u7ea2\uff0c\u4f46\u5bf9\u65b9\u65e2\u5df2\u901a\u878d\uff0e\u8fd9\u4efd\u4eba\u60c5\u53c8\u600e\u80fd\u4e0d\u9886\u3002 \u3000\u3000\u4e01\u6d9b\u773c\u89c1\u7518\u9716\u8d70\u4e86\u8fc7\u53bb\uff0c\u4e0d\u89c9\u5927\u53eb\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u665a\u8f88\u4e5f\u8981\u8bd5\uff0c\u4f60\u4e0d\u80fd\u628a\u6211\u4eec\u62c6\u5f00\uff01\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u8fde\u4e00\u62db\u90fd\u515c\u4e0d\u4f4f\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u8bd5\u7684\uff01\u201d \u3000\u3000\u63a5\u7740\u8f6c\u5934\u671b\u5411\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u4e5f\u8fc7\u6765\uff01\u201d \u3000\u3000\u8c01\u60f3\u8881\u5c0f\u9e64\u5374\u62b1\u4e86\u62f3\u9053\uff1a\u201c\u665a\u8f88\u5bf9\u8001\u524d\u8f88\u7684\u4e00\u756a\u539a\u7231\uff0c\u5fc3\u9886\u4e86\uff01\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u76ee\u73b0\u5f02\u5149\uff0c\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u7684\u610f\u601d\u8fd8\u60f3\u8bd5\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u665a\u8f88\u5bf9\u4efb\u4f55\u4e8b\u60c5\uff0c\u4ece\u4e0d\u60f3\u4e0d\u52b3\u800c\u83b7\uff0c\u4e5f\u4ece\u4e0d\u5fc3\u5b58\u4fa5\u5e78\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u77aa\u7740\u4e24\u773c\u9053\uff1a\u201c\u4f60\u518d\u628a\u7406\u7531\u8bf4\u6e05\u695a\u4e9b\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5c31\u50cf\u505a\u5b98\u4e00\u6837\uff0c\u5fc5\u987b\u901a\u8fc7\u8003\u8bd5\uff0c\u82e5\u53ea\u51ed\u8d70\u540e\u95e8\u6216\u5176\u4ed6\u4eba\u4e8b\u5173\u7cfb\uff0c\u90a3\u5c31\u662f\u9ed1 \u5b98\uff0c\u5b98\u505a\u5f97\u518d\u5927\uff0c\u4e5f\u662f\u4e3a\u4eba\u6240\u4e0d\u9f7f\uff0c\u8fd8\u6709\u4e9b\u51ed\u7740\u6709\u94b1\u6350\u5b98\u7684\uff0c\u90a3\u79cd\u5b98\u7167\u6837\u4ee4\u4eba\u55e4\u4e4b\u4ee5\u9f3b\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u7b11\u9053\uff1a\u201c\u6709\u5fd7\u6c14\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u8001\u592b\u4e5f\u4e0d\u5f97\u4e0d\u6210\u5168\u4f60\u7684\u5fc3\u613f\u3002\u201d \u3000\u3000\u8bf4\u7740\u8f6c\u8fc7\u5934\u53bb\u9053\uff1a\u201c\u94c1\u5934\u5c31\u8bd5\u8bd5\u4ed6\uff0c\u4e0d\u59a8\u70b9\u5230\u4e3a\u6b62\u3002\u201d \u3000\u3000\u94c1\u5934\u9640\u65e9\u77e5\u9a7c\u80cc\u8001\u4eba\u5fc3\u610f\uff0c\u5f53\u7136\u4e5f\u5fc3\u5b58\u901a\u878d\uff0c\u53f3\u624b\u4ecd\u628a\u94c1\u7985\u6756\u7ad6\u5728\u5730\u4e0a\uff0c\u62ac\u8d77\u5de6\u624b\u62db\u4e86 \u62db\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u6d12\u5bb6\u7167\u6837\u4e5f\u8ba9\u4f60\u4e09\u62db\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u7ffb\u8155\u62d4\u51fa\u957f\u5251\uff0c\u6697\u8fd0\u5185\u529b\uff0c\u84e6\u5730\u6fc0\u5c04\u800c\u8d77\uff0c\u4e5f\u5411\u94c1\u5934\u9640\u5934\u9876\u98de\u53bb\u3002 \u3000\u3000\u94c1\u5934\u9640\u4ecd\u60f3\u6545\u6280\u91cd\u65bd\uff0c\u51c6\u5907\u4ee5\u6307\u98ce\u548c\u638c\u529b\u628a\u5bf9\u65b9\u903c\u9000\u3002 \u3000\u3000\u5c82\u77e5\u4ed6\u8fd9\u6b21\u9519\u4f30\u4e86\u5bf9\u65b9\u5b9e\u529b\uff0c\u4ed6\u90a3\u6307\u98ce\u548c\u638c\u529b\u6839\u672c\u65e0\u6cd5\u5c06\u8881\u5c0f\u9e64\u903c\u9000\uff0c\u53ea\u89c9\u8881\u5c0f\u9e64\u7684\u5251 \u52bf\u6709\u5982\u95ea\u7535\u5954\u96f7\uff0c\u201c\u5636\u201d\u7684\u4e00\u58f0\uff0c\u5df2\u7a7f\u7834\u4ed6\u7684\u638c\u98ce\uff0c\u5251\u52bf\u5a01\u529b\u4e0d\u51cf\u7684\u76f4\u524a\u5411\u4ed6\u7684\u5de6\u7897\u3002\u94c1\u5934 \u9640\u5fc3\u5934\u4e00\u9707\uff0c\u6025\u6025\u7f29\u8155\u504f\u5934\u3002 \u3000\u3000\u53ea\u542c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u5251\u76f4\u5288\u5728\u7ad6\u8d77\u7684\u94c1\u63b8\u6756\u4e0a\u3002 \u3000\u3000\u94c1\u5934\u9640\u5e76\u672a\u63e1\u7d27\u94c1\u7985\u6756\uff0c\u53c8\u56e0\u5251\u52bf\u6765\u5f97\u592a\u5feb\u592a\u731b\uff0c\u8feb\u5f97\u94c1\u5934\u9640\u53f3\u624b\u4e00\u677e\uff0c\u90a3\u94c1\u7985\u6756\u7adf\u7136 \u88ab\u780d\u5012\u5730\u4e0a\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u5e76\u4e0d\u8d81\u673a\u65bd\u88ad\uff0c\u987a\u7740\u524d\u51b2\u4e4b\u52bf\uff0c\u4eba\u5df2\u98d8\u843d\u5728\u94c1\u5934\u9640\u8eab\u540e\u3002 \u3000\u3000\u8fd9\u65f6\u4e0d\u4f46\u94c1\u5934\u9640\u9707\u60ca\uff0c\u8fde\u9a7c\u80cc\u8001\u4eba\u4e5f\u5927\u611f\u610f\u5916\u3002 \u3000\u3000\u81f3\u4e8e\u5df2\u7ecf\u8fc7\u4e86\u5173\u7684\u7518\u9716\u548c\u6025\u7740\u60f3\u8fc7\u5173\u7684\u4e01\u6d9b\uff0c\u66f4\u662f\u770b\u5f97\u5f20\u53e3\u7ed3\u820c\u3002 \u3000\u3000\u4ed6\u4eec\u5148\u524d\u53ea\u4ee5\u4e3a\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u53ea\u662f\u5b66\u95ee\u80dc\u8fc7\u81ea\u5df1\uff0c\u8bba\u6b66\u7684\u603b\u8981\u9760\u540e\u4e00\u70b9\uff0c\u5230\u8fd9\u65f6\u624d\u77e5 \u4eba\u5bb6\u662f\u6837\u6837\u80dc\u8fc7\u81ea\u5df1\u3002\u4e0d\u4f46\u80dc\u8fc7\u81ea\u5df1\uff0c\u800c\u4e14\u80dc\u8fc7\u592a\u591a\u3002 \u3000\u3000\u4e0d\u8fc7\u8881\u5c0f\u9e64\u5fc3\u91cc\u6709\u6570\uff0c\u8fd9\u7b2c\u4e00\u62db\u81ea\u5df1\u867d\u80dc\u5374\u80dc\u5f97\u6709\u4e9b\u6295\u673a\u53d6\u5de7\uff0c\u56e0\u4e3a\u90a3\u662f\u5bf9\u65b9\u592a\u8fc7\u5927\u610f\u3002 \u3000\u3000\u94c1\u5934\u9640\u4fef\u8eab\u62fe\u8d77\u94c1\u7985\u6756\uff0c\u73b0\u5728\u4ed6\u5df2\u4e0d\u6562\u518d\u6389\u4ee5\u8f7b\u5fc3\uff0c\u7ad9\u597d\u67b6\u52bf\uff0c\u8138\u8272\u618b\u5f97\u6709\u5982\u732a\u809d\u822c\u9053\uff1a \u201c\u5c0f\u4f19\u5b50\uff0c\u5f53\u771f\u662f\u771f\u4eba\u4e0d\u9732\u76f8\uff0c\u6d12\u5bb6\u8fd8\u662f\u7b2c\u4e00\u6b21\u9047\u5230\u50cf\u4f60\u8fd9\u6837\u7684\u9ad8\u624b\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u62b1\u5251\u4e00\u793c\u9053\uff1a\u201c\u53ea\u80fd\u8bf4\u662f\u5728\u4e0b\u4fa5\u5e78\uff0c\u82e5\u5927\u5e08\u6709\u5907\uff0c\u5728\u4e0b\u54ea\u6709\u5e78\u80dc\u5f97\u4e86\uff01\uff1f\u201d \u3000\u3000\u94c1\u5934\u9640\u543c\u9053\uff1b\u201c\u5c11\u8bf4\u5e9f\u8bdd\uff0c\u518d\u4e0a\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8fd9\u6b21\u4e0d\u518d\u8dc3\u8d77\uff0c\u5374\u6b3a\u8eab\u75be\u8fdb\uff0c\u957f\u5251\u76f4\u523a\u5bf9\u65b9\u5fc3\u7a9d\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u4eba\u5c1a\u672a\u5230\uff0c\u94c1\u7985\u6756\u5df2\u631f\u7740\u9510\u98ce\uff0c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u6a2a\u626b\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u94c1\u7985\u6756\u6a2a\u626b\u7684\u5a01\u529b\uff0c\u4e0d\u4e9a\u4e8e\u6392\u5c71\u5012\u6d77\uff0c\u8881\u5c0f\u9e64\u5f53\u7136\u4e0d\u6562\u6b63\u9762\u76f8\u6297\u3002 \u3000\u3000\u4ed6\u60c5\u6025\u95f4\u817e\u8eab\u4e00\u8dc3\uff0c\u5728\u534a\u7a7a\u4e2d\u6f14\u51fa\u4e00\u5f0f\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u94c1\u7985\u6756\u521a\u597d\u5728\u8eab\u4e0b\u8d34\u80cc\u626b\u8fc7\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u843d\u5730\u540e\uff0c\u8d81\u5bf9\u65b9\u4e0b\u4e00\u6756\u5c1a\u672a\u626b\u6765\uff0c\u4e00\u5251\u518d\u5411\u94c1\u5934\u9640\u5c0f\u8179\u523a\u53bb\u3002 \u3000\u3000\u94c1\u5934\u9640\u867d\u7136\u7b2c\u4e8c\u6756\u53c8\u5df2\u626b\u51fa\uff0c\u4f46\u773c\u89c1\u5251\u950b\u903c\u8fd1\uff0c\u4e5f\u4e0d\u5f97\u4e0d\u5411\u540e\u8dc3\u9000\u4e09\u5c3a\u6709\u4f59\u3002 \u3000\u3000\u5f85\u4ed6\u7b2c\u4e8c\u6756\u626b\u5230\uff0c\u8881\u5c0f\u9e64\u5374\u5df2\u8d34\u5730\u7ffb\u51fa\uff0c\u63a5\u7740\u4e00\u4e2a\u4ef0\u8eab\u5012\u7eb5\uff0c\u4eba\u5df2\u9000\u51fa\u4e08\u4f59\u5f00\u5916\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u7d27\u63a5\u7740\u53c8\u4e00\u4e2a\u5012\u7ffb\uff0c\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u6c14\u4e0d\u51fa\u58f0\uff0c\u9762\u4e0d\u6539\u8272\uff0c\u62b1\u4e86\u62b1\u62f3\u9053\uff1a\u201c\u5728\u4e0b\u6709\u5e78\uff0c\u5e94\u8be5\u53ef\u4ee5\u8fc7\u5173\u4e86\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u9053\uff1a\u201c\u4f60\u65e9\u5c31\u53ef\u4ee5\u8fc7\u5173\u4e86\uff0c\u4f55\u5fc5\u518d\u95ee\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u53ea\u9053\u5cb3\u5c0f\u98de\u5fc5\u5b9a\u548c\u8881\u5c0f\u9e64\u4e00\u8d77\u8fc7\u53bb\uff0c\u5c82\u77e5\u5cb3\u5c0f\u98de\u7adf\u662f\u7ad9\u5728\u539f\u5730\u672a\u52a8\u3002 \u3000\u3000\u201c\u4f60\u600e\u4e48\u8fd8\u4e0d\u8fc7\u6765\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6b63\u8272\u9053\uff1a\u201c\u665a\u8f88\u6ca1\u7ecf\u6d4b\u8bd5\uff0c\u600e\u6562\u968f\u4fbf\u8fc7\u53bb\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u5bf9\u5cb3\u5c0f\u98de\u6709\u8fd9\u79cd\u5fd7\u6c14\uff0c\u5012\u662f\u9887\u4e3a\u8d4f\u8bc6\uff0c\u6545\u610f\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u83ab\u975e\u4f60\u4e5f\u548c\u4ed6\u4e00\u6837\u4e0d \u80af\u9886\u60c5\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u665a\u8f88\u4ece\u4e0d\u505a\u6295\u673a\u53d6\u5de7\u7684\u4e8b\uff0c\u5c24\u5176\u548c\u8001\u524d\u8f88\u4e00\u5411\u65e0\u4eb2\u65e0\u6545\uff0c\u53c8\u6709\u4ec0\u4e48\u60c5\u597d \u9886\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u968f\u4f60\u7684\u4fbf\uff0c\u60f3\u8bd5\u5c31\u8bd5\u5427\uff0c\u8001\u592b\u867d\u770b\u51fa\u4f60\u8d44\u8d28\u4e0d\u51e1\uff0c\u4f46\u4f60\u5374\u603b\u662f\u5e74 \u7eaa\u592a\u5c0f\uff0c\u529f\u592b\u706b\u5019\u51b3\u4e0d\u80fd\u4e0e\u94c1\u5934\u76f8\u6297\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u665a\u8f88\u65e9\u77e5\u8f93\u662f\u8f93\u5b9a\u4e86\u7684\uff0e\u4f46\u5374\u4e0d\u613f\u56e0\u4e3a\u6015\u8f93\u5c31\u754f\u7f29\u4e0d\u524d\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u4e0d\u613f\u518d\u591a\u8d39\u5507\u820c\uff0c\u8f6c\u5934\u9053\uff1a\u201c\u94c1\u5934\u5c31\u8bd5\u4ed6\u4e00\u8bd5\uff0c\u4e0d\u8fc7\u5bf9\u4ed8\u5c0f\u5b69\u5b50\u548c\u5bf9\u4ed8\u5927\u4eba\u4e0d \u4e00\u6837\uff0c\u51fa\u624b\u8981\u591a\u659f\u914c\u4e9b\u3002\u201d \u3000\u3000\u94c1\u5934\u9640\u867d\u5148\u524d\u56e0\u592a\u8fc7\u5927\u610f\uff0c\u6218\u8881\u5c0f\u9e64\u51e0\u4e4e\u8f7d\u4e86\u7b4b\u6597\uff0c\u4f46\u73b0\u5728\u5374\u4e0d\u76f8\u4fe1\u5cb3\u5c0f\u98de\u4e5f\u80fd\u50cf\u8881\u5c0f \u9e64\u4e00\u6837\uff0c\u5c24\u5176\u53c8\u6709\u9a7c\u80cc\u8001\u4eba\u7684\u6697\u793a\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u4ecd\u628a\u94c1\u63b8\u6756\u7ad6\u5728\u5730\u4e0a\uff0c\u8001\u89c4\u77e9\uff0c\u62db\u4e86\u62db\u624b\u9053\uff1a\u201c\u5c0f\u670b\u53cb\uff0c\u4e0a\u5427\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u65e9\u5df3\u624b\u63e1\u957f\u5251\uff0c\u811a\u4e0b\u5fae\u4e00\u70b9\u5730\uff0c\u7adf\u7136\u5e73\u5c04\u800c\u51fa\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u95ea\u7535\u822c\u6251\u5411\u5bf9\u65b9\u3002 \u3000\u3000\u4ed6\u8fd9\u98de\u6251\u4e4b\u52bf\uff0c\u5b9e\u5728\u6765\u5f97\u592a\u5feb\uff0c\u94c1\u5934\u9640\u8fde\u773c\u90fd\u6765\u4e0d\u53ca\u7728\uff0c\u5bf9\u65b9\u7684\u5251\u5934\u5df2\u903c\u8fd1\u989d\u5934\u3002 \u3000\u3000\u94c1\u5934\u9640\u5fc3\u5934\u4e00\u51db\uff0e\u6025\u6025\u62da\u529b\u63a8\u51fa\u4e00\u638c\uff0c\u4f46\u5251\u950b\u8fd8\u662f\u7531\u5934\u76ae\u64e6\u8fc7\uff0c\u82e5\u5251\u950b\u518d\u4f4e\u534a\u5bf8\uff0c\u4ed6\u7684 \u5934\u9876\u5fc5\u5b9a\u591a\u4e86\u4e00\u6761\u69fd\u3002 \u3000\u3000\u4f46\u5f53\u4ed6\u62da\u529b\u63a8\u51fa\u4e00\u638c\u4e4b\u540e\uff0c\u5cb3\u5c0f\u98de\u7684\u4eba\u5f71\u5374\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u94c1\u5934\u9640\u6b63\u5728\u60ca\u8be7\u4e4b\u9645\uff0c\u8eab\u540e\u5df2\u54cd\u8d77\u5cb3\u5c0f\u98de\u7684\u58f0\u97f3\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\uff0c\u6b63\u7b49\u94c1\u5934\u5927\u5e08\u6765\u653b\uff01\u201d \u3000\u3000\u539f\u6765\u5cb3\u5c0f\u98de\u8d81\u5bf9\u65b9\u53d1\u638c\u65f6\uff0c\u4ee5\u529b\u501f\u529b\uff0c\u7adf\u817e\u4e0a\u534a\u7a7a\uff0c\u96be\u602a\u94c1\u5934\u9640\u5df2\u770b\u4e0d\u89c1\u4ed6\u3002 \u3000\u3000\u4ed6\u5347\u4e0a\u534a\u7a7a\u4e4b\u540e\uff0c\u518d\u98d8\u843d\u94c1\u5934\u9640\u8eab\u540e\uff0c\u603b\u4e4b\uff0c\u8fd9\u4e00\u8fde\u4e32\u7684\u52a8\u4f5c\uff0c\u90fd\u5feb\u5f97\u4e0d\u80fd\u518d\u5feb\uff0c\u800c\u4e14 \u53c8\u65e0\u58f0\u65e0\u606f\uff0c\u5426\u5219\u5982\u4f55\u80fd\u7792\u5f97\u8fc7\u94c1\u5934\u9640\u7684\u8033\u76ee\u3002 \u3000\u3000\u94c1\u5934\u9640\u8d8a\u52a0\u51db\u9a87\uff0c\u56e0\u4e3a\u82e5\u65b9\u624d\u5cb3\u5c0f\u98de\u5728\u4ed6\u8eab\u540e\u8d81\u673a\u65bd\u88ad\uff0c\u540e\u679c\u4e0d\u582a\u8bbe\u60f3\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4ed6\u5df2\u4e0d\u5f97\u4e0d\u53cc\u624b\u53d6\u8d77\u94c1\u7985\u6756\uff0c\u8ddf\u8fdb\u4e24\u6b65\uff0c\u4e00\u8bb0\u201c\u6a2a\u626b\u5343\u91cc\u201d\uff0c\u76f4\u5411\u5cb3\u5c0f\u98de\u62e6\u8170\u626b \u53bb\u3002 \u3000\u3000\u5c82\u77e5\u5f53\u4ed6\u626b\u51fa\u4e4b\u540e\uff0c\u90a3\u94c1\u7985\u6756\u5374\u9aa4\u7136\u589e\u52a0\u4e86\u91cd\u529b\u3002 \u3000\u3000\u539f\u6765\u5cb3\u5c0f\u98de\u6b64\u523b\u5df2\u7ad9\u5728\u65cb\u52a8\u5982\u98de\u7684\u94c1\u7985\u6756\u4e0a\u3002 \u3000\u3000\u597d\u5728\u94c1\u5934\u9640\u529b\u5927\u65e0\u7a77\u3002\u94c1\u7985\u6756\u4e0a\u7ad9\u7740\u4e00\u4e2a\u8eab\u4f53\u5e76\u4e0d\u91cd\u5927\u7684\u4eba\uff0c\u5bf9\u4ed6\u5e76\u65e0\u591a\u5927\u59a8\u788d\u3002 \u3000\u3000\u4f46\u81ea\u5df1\u7684\u5175\u5668\u7adf\u88ab\u5bf9\u65b9\u7ad9\u4e0a\uff0c\u800c\u4e14\u65cb\u90fd\u65cb\u4e0d\u4e0b\u6765\uff0c\u5374\u5b9e\u5728\u5927\u5931\u989c\u9762\u3002 \u3000\u3000\u4ed6\u5e76\u4e0d\u4fe1\u90aa\uff0c\u4e0d\u76f8\u4fe1\u4f1a\u7529\u4e0d\u4e0b\u6765\u5cb3\u5c0f\u98de\uff0e\u4e8e\u662f\u62da\u529b\u4e00\u6296\uff0c\u5cb3\u5c0f\u98de\u679c\u7136\u4e00\u5934\u683d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u77e5\u5cb3\u5c0f\u98de\u867d\u7136\u683d\u4e86\u4e0b\u6765\uff0c\u5374\u5728\u4ed6\u80f8\u524d\u7a0d\u7a0d\u4e00\u95ea\uff0c\u8d81\u94c1\u7985\u6756\u5c1a\u672a\u6765\u5f97\u53ca\u53d8\u62db\uff0c\u5374\u53c8\u5012\u7eb5 \u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u94c1\u5934\u9640\u7a81\u89c9\u4e0b\u8eab\u4e00\u51c9\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u8fd9\u8fd8\u4e86\u5f97\uff0c\u6025\u6025\u629b\u53bb\u94c1\u7985\u6756\uff0c\u62a2\u7740\u63d0\u88e4\u5b50\u3002 \u3000\u3000\u539f\u6765\u8fd9\u65f6\u4ed6\u7684\u88e4\u5e26\u7adf\u88ab\u6311\u65ad\uff0c\u88e4\u5b50\u5df2\u843d\u5230\u811a\u4e0a\uff0c\u679c\u7136\u5927\u548c\u5c1a\u5c0f\u548c\u5c1a\u4e00\u9f50\u73b0\u5f62\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u8fd9\u662f\u5cb3\u5c0f\u98de\u4ece\u94c1\u7985\u6756\u4e0a\u683d\u4e0b\u4e4b\u540e\uff0c\u7ed9\u4ed6\u7528\u5251\u6311\u65ad\u7684\uff0c\u82e5\u5cb3\u5c0f\u98de\u7684\u5251\u518d\u524d\u79fb\u4e00\u70b9\uff0c \u51c6\u4f1a\u8ba9\u4ed6\u809a\u7834\u80a0\u6d41\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u4e0d\u4ec5\u7518\u9716\u3001\u4e01\u6d9b\u3001\u8881\u5c0f\u9e64\u7981\u4e0d\u4f4f\u7b11\u51fa\u58f0\u6765\uff0c\u8fde\u9a7c\u80cc\u8001\u4eba\u4e5f\u63a9\u53e3\u6697\u7b11\u4e0d\u5df2\u3002 \u3000\u3000\u94c1\u5934\u9640\u56e0\u88e4\u5e26\u5df2\u65ad\uff0c\u4e0d\u5f97\u4e0d\u53cc\u624b\u63d0\u7740\uff0c\u54ea\u91cc\u8fd8\u987e\u5f97\u518d\u6253\u3002 \u3000\u3000\u53ea\u542c\u9a7c\u80cc\u8001\u4eba\u9053\uff1a\u201c\u771f\u7ed9\u8001\u592b\u4e22\u4eba\uff0c\u8fd8\u4e0d\u5feb\u5feb\u56de\u53bb\uff0c\u4ee5\u540e\u8bb0\u4f4f\u88e4\u5e26\u675f\u9ad8\u4e9b\uff01\u201d \u3000\u3000\u94c1\u5934\u9640\u53ea\u6709\u4e00\u624b\u63d0\u88e4\uff0c\u4e00\u624b\u53d6\u8d77\u94c1\u7985\u6756\uff0c\u72fc\u72c8\u56de\u6d1e\u800c\u53bb\u3002 \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u8f6c\u58f0\u53eb\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u8fd8\u4e0d\u8fc7\u6765\uff01\u201d \u3000\u3000\u8c01\u77e5\u5cb3\u5c0f\u98de\u5374\u6447\u6447\u5934\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u665a\u8f88\u4e0d\u51c6\u5907\u8fc7\u53bb\u4e86\uff01\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u5927\u611f\u4e00\u6123\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u7230\u624d\uff0c\u5982\u4eca\u62db\u6536\u5230\u8fd9\u6837\u96be\u5f97\u4e00\u89c1\u7684\u5947\u624d\uff0c\u5373\u4fbf\u62ac\u4e5f\u8981\u628a\u4ed6\u62ac\u8fc7\u53bb\uff0c\u4e8e\u662f\u4e5f\u987e\u4e0d\u5f97 \u8eab\u4efd\uff0c\u6025\u6b65\u8d70\u8fc7\u53bb\u4e00\u628a\u62c9\u4f4f\u5cb3\u5c0f\u98de\u9053\uff1a \u3000\u3000\u201c\u5a03\u513f\uff0c\u4f60\u72af\u4e86\u4ec0\u4e48\u6bdb\u75c5\uff1f\u4e3a\u4ec0\u4e48\u7adf\u8bf4\u51fa\u8fd9\u79cd\u8bdd\uff1f\u201d \u3000\u3000\u4ed6\u53e3\u79f0\u5cb3\u5c0f\u98de\u5a03\u513f\uff0c\u800c\u4ed6\u7684\u8eab\u91cf\uff0c\u5374\u53ea\u80fd\u53ca\u5f97\u5cb3\u5c0f\u98de\u7684\u8033\u6735\uff0c\u65c1\u89c2\u8005\u770b\u8d77\u6765\u4e5f\u5b9e\u5728\u89c9\u5f97 \u597d\u7b11\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8bed\u6c14\u90d1\u91cd\u7684\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u82e5\u95ee\u665a\u8f88\u4e3a\u4ec0\u4e48\u4e0d\u8fc7\u53bb\uff0c\u60a8\u5fc3\u91cc\u5c31\u5e94\u5f53\u6bd4\u665a\u8f88\u66f4\u6e05 \u695a\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u6839\u672c\u6478\u4e0d\u6e05\u5934\u8111\uff0c\u9053\uff1a\u201c\u8001\u592b\u600e\u80fd\u6e05\u695a\u4f60\u7684\u4e8b\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u665a\u8f88\u4eec\u540c\u6765\u7684\u662f\u56db\u4eba\uff0c\u73b0\u5728\u60a8\u5374\u53ea\u51c6\u4e09\u4eba\u8fc7\u53bb\uff0c\u60a8\u53ef\u77e5\u9053\u90a3\u4f4d\u4e01\u5f53\u5bb6\u82e5\u8fc7 \u4e0d\u4e86\u5173\uff0c\u5fc5\u5b9a\u4f1a\u81ea\u6740\uff0c\u665a\u8f88\u548c\u4ed6\u662f\u670b\u53cb\uff0c\u600e\u80fd\u773c\u770b\u4ed6\u8fd9\u6837\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u5e72\u54b3\u4e24\u58f0\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u4e0d\u591f\u8d44\u683c\u8fc7\u53bb\uff0c\u8001\u592b\u600e\u53ef\u5f87\u79c1\u821e\u5f0a\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u94c1\u5934\u5927\u5e08\u5bf9\u7518\u5f53\u5bb6\u7684\u5df2\u7ecf\u653e\u8fc7\u6c34\uff0e\u4f60\u8001\u4eba\u5bb6\u65e9\u5df2\u7ecf\u5f87\u79c1\u821e\u5f0a\u4e86\uff01\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u7565\u4e00\u72b9\u8c6b\uff0c\u8fd8\u662f\u6447\u5934\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u8001\u592b\u51b3\u5b9a\u4e0d\u7b54\u5e94\u4ed6\u8fc7\u5173\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6ca1\u5173\u7cfb\uff0c\u8fd9\u6837\u8bf4\u5927\u5bb6\u90fd\u4e0d\u8fc7\u5173\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u8fb9\u8bf4\u8fb9\u671b\u7740\u5bf9\u9762\u53eb\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0e\u7518\u5f53\u5bb6\u7684\uff0c\u4f60\u4eec\u4e24\u4f4d\u5982\u679c\u8fd8\u8bb2\u670b\u53cb\u4e49\u6c14\uff0c\u5c31\u8be5\u56de\u6765\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u548c\u7518\u9716\u5f53\u7136\u77e5\u9053\u8fd9\u662f\u5cb3\u5c0f\u98de\u7684\u8ba1\u7b56\uff0c\u4e0d\u7ea6\u800c\u540c\u9f50\u9f50\u7531\u5bf9\u9762\u8d70\u4e86\u56de\u6765\u3002 \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u679c\u7136\u88ab\u8feb\u65e0\u5948\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u7b97\u8001\u592b\u6597\u4e0d\u8fc7\u4f60\u4eec\uff0c\u5c31\u7b97\u4ed6\u8fc7\u5173\u4e86\u5427\uff01\u201d \u3000\u3000\u6b64\u523b\u4e01\u6d9b\u771f\u6068\u4e0d\u5f97\u7ed9\u5cb3\u5c0f\u98de\u53e9\u4e09\u4e2a\u54cd\u5934\uff0c\u4ed6\u60f3\u5230\u5cb3\u5c0f\u98de\u7684\u7236\u4eb2\u6551\u4e86\u4ed6\u4e00\u547d\uff0c\u5982\u4eca\u5cb3\u5c0f\u98de \u53c8\u6210\u5168\u4e86\u4ed6\u8fc7\u5173\u7684\u5fc3\u613f\uff0c\u5927\u6069\u5927\u5fb7\uff0c\u771f\u662f\u820d\u751f\u96be\u62a5\u3002 \u3000\u3000\u4f46\u4ed6\u5e76\u6ca1\u771f\u8dea\u4e0b\u78d5\u5934\uff0c\u53ea\u662f\u4f4e\u7740\u5934\u968f\u5728\u4e09\u4eba\u8eab\u540e\u3002 \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u9053\uff1a\u201c\u8fc7\u4e86\u8001\u592b\u8fd9\u6b66\u5173\uff0c\u524d\u9762\u53c8\u662f\u4e00\u5173\uff0c\u4f60\u4eec\u5fc5\u987b\u5403\u9971\u4e86\u996d\u624d\u6210\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u665a\u8f88\u4eec\u81ea\u5df1\u5e26\u4e86\u5e72\u7cae\u3002\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u9053\uff1a\u201c\u5149\u5403\u5e72\u7cae\u4e0d\u6210\uff0c\u603b\u8981\u8fdb\u4e9b\u6c64\u6c34\uff0c\u8001\u592b\u4eca\u5929\u96be\u5f97\u9047\u5230\u4f60\u4eec\u4e24\u540d\u96be\u5f97\u7684\u4eba\u624d\uff0c \u51b3\u5b9a\u8bf7\u4f60\u4eec\u5403\u4e00\u987f\u518d\u8d70\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u56db\u4eba\u5f53\u7136\u6c42\u4e4b\u4e0d\u5f97\u3002 \u3000\u3000\u6765\u5230\u8c37\u9053\u7684\u74f6\u9888\u5904\uff0c\u679c\u7136\u4e24\u65c1\u6709\u513f\u5904\u6d1e\u53e3\u3002 \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u628a\u56db\u4eba\u5e26\u8fdb\u4e00\u5904\u6d1e\u5185\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u9910\u5385\uff0c\u4f60\u4eec\u7b49\u7740\uff0c\u8001\u592b\u9a6c\u4e0a\u6d3e\u4eba\u628a\u996d\u83dc\u9001 \u6765\uff01\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u8d70\u540e\uff0c\u7518\u9716\u548c\u4e01\u6d9b\u53ea\u9053\u9001\u996d\u83dc\u7684\u4e5f\u662f\u56db\u540d\u7f8e\u8273\u5c11\u5973\uff0c\u8c01\u77e5\u4e0d\u5927\u4e00\u4f1a\u513f\u63d0\u7740\u98df\u76d2 \u8fdb\u6765\u7684\uff0c\u5374\u662f\u4e24\u540d\u6b6a\u9f3b\u90aa\u773c\u7c97\u624b\u7c97\u811a\u7684\u58ee\u6c49\u3002 \u3000\u3000\u996d\u83dc\u6446\u51fa\u6765\u4e4b\u540e\uff0c\u4e5f\u4ec5\u662f\u4e09\u83dc\u4e00\u6c64\uff0c\u53ea\u662f\u591a\u4e86\u4e00\u58f6\u8336\u800c\u5df2\u3002 \u3000\u3000\u4e01\u6d9b\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u91cc\u7684\u59d1\u5a18\u5462\uff1f\u201d \u3000\u3000\u4e00\u540d\u659c\u773c\u5927\u6c49\u77aa\u4e86\u4e01\u6d9b\u4e00\u773c\u9053\uff1b\u201c\u83ab\u975e\u5e9c\u4e0a\u5f00\u7a91\u5b50\u9986\uff1f\u4e0d\u5f00\u7a91\u5b50\u9986\u90a3\u6765\u7684\u59d1\u5a18\uff1f\u201d \u3000\u3000\u82e5\u5728\u4ee5\u5f80\uff0c\u4e01\u6d9b\u4f1a\u8df3\u8d77\u6765\u5f53\u573a\u63cd\u4eba\uff0c\u4f46\u73b0\u5728\u4ed6\u5374\u4e0d\u6562\uff0c\u53ea\u80fd\u56de\u77aa\u4e00\u773c\u800c\u5df2\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u4e5f\u6ca1\u5403\u4e8f\uff0c\u56e0\u90a3\u6c49\u5b50\u662f\u659c\u773c\uff0c\u867d\u7136\u65b9\u624d\u77aa\u7684\u662f\u4e01\u6d9b\uff0c\u4f46\u8138\u5374\u662f\u671d\u5411\u5750\u5728\u4e01\u6d9b\u4e00\u65c1 \u7684\u7518\u9716\u3002 \u3000\u3000\u56db\u4eba\u5306\u5306\u7528\u8fc7\uff0c\u7acb\u5373\u51fa\u6d1e\u51c6\u5907\u8d76\u8def\u3002 \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u6b63\u7ad9\u5728\u6d1e\u5916\uff0c\u6478\u7740\u5634\u5df4\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u5403\u9971\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5403\u9971\u4e86\uff0c\u591a\u8c22\u8001\u524d\u8f88\uff01\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u671b\u4e86\u4e01\u6d9b\u4e00\u773c\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u867d\u7531\u8001\u592b\u9ad8\u62ac\u8d35\u624b\u653e\u4e86\u4e00\u9a6c\uff0c\u524d\u9762\u90a3\u4e00\u5173\uff0c\u5341\u6709 \u516b\u4e5d\u8fd8\u662f\u8981\u88ab\u6253\u56de\u6765\uff01\u201d \u3000\u3000\u4e01\u6d9b\u6839\u672c\u4e0d\u6562\u542d\u6c14\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u95ee\u9053\uff1b\u201c\u8bf7\u95ee\u8001\u524d\u8f88\uff0c\u524d\u9762\u662f\u4ec0\u4e48\u5173\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u9053\uff1a\u201c\u5230\u4e86\u4ee5\u540e\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u987f\u4e86\u4e00\u987f\uff0c\u518d\u95ee\uff1a\u201c\u8fd8\u6709\u591a\u8fdc\uff1f\u201d \u3000\u3000\u9a7c\u80cc\u8001\u4eba\u9053\uff0c\u201c\u8d70\u7740\u770b\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u65e2\u7136\u95ee\u4e0d\u51fa\u6240\u4ee5\u7136\uff0e\u56db\u4eba\u53ea\u80fd\u5411\u9a7c\u80cc\u8001\u4eba\u62f1\u624b\u544a\u522b\u3002 \u3000\u3000\u6b64\u523b\u7684\u7518\u9716\u548c\u4e01\u6d9b\uff0c\u65e9\u5df2\u5bf9\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u9a6c\u9996\u662f\u77bb\uff0c\u82e5\u975e\u6709\u8fd9\u4e24\u4f4d\u8eab\u6000\u7edd\u6280\u7684\u5e74\u8f7b\u4eba \u540c\u884c\uff0c\u4ed6\u4eec\u8fd8\u771f\u6ca1\u6709\u52c7\u6c14\u518d\u5f80\u524d\u884c\u3002 \u3000\u3000\u4f46\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u4e5f\u6709\u4ed6\u4eec\u7684\u5fc3\u4e8b\uff0c\u4ed6\u4eec\u60f3\u5230\u5728\u8fd9\u6761\u8c37\u9053\u91cc\uff0c \u3000\u3000\u5c45\u7136\u53d8\u6210\u4e86\u53e6\u5916\u4e00\u4e2a\u4e16\u754c\uff0c\u800c\u7edf\u7387\u5168\u8c37\u7684\u4e3b\u4eba\uff0c\u53c8\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\u7269\u5462\uff0c\u8fde\u9a7c\u80cc\u8001\u4eba \u8fd9\u6837\u6b66\u529f\u7edd\u4e16\u7684\u4eba\u90fd\u4e56\u4e56\u7684\u4fef\u9996\u542c\u547d\uff0c\u4ee4\u4eba\u53ef\u4ee5\u60f3\u89c1\u8fd9\u4eba\u7684\u4e0d\u51e1\uff0c\u800c\u4ed6\u62db\u6536\u8fd9\u4e48\u591a\u6b66\u6797\u9ad8\u624b \u8fdb\u6765\uff0c\u53c8\u662f\u4f55\u5c45\u5fc3\uff1f\u2026\u2026 \u3000\u3000\u4e0d\u60f3\u8fd8\u597d\uff0c\u8d8a\u60f3\u53cd\u800c\u8d8a\u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u4e01\u6d9b\u89c1\u5404\u4eba\u90fd\u4e0d\u8bb2\u8bdd\uff0c\u53ea\u987e\u95f7\u5934\u8d70\u8def\uff0c\u81ea\u7136\u89c9\u5f97\u4e0d\u662f\u5473\u9053\u3002\u4ed6\u4e00\u5411\u4e0d\u7518\u5bc2\u5bde\uff0c\u4e0d\u89c9\u642d\u8baa \u7740\u95ee\u9053\uff1a\u201c\u8001\u7518\uff0c\u4f60\u8bf4\u4f60\u66fe\u53bb\u8fc7\u53f0\u6e7e\uff0c\u4e3a\u4ec0\u4e48\u4ece\u6765\u4e0d\u66fe\u5bf9\u6211\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u90a3\u662f\u5f88\u4e45\u4ee5\u524d\u7684\u4e8b\uff0c\u6839\u672c\u6ca1\u6709\u5fc5\u8981\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u201c\u53f0\u6e7e\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5728\u4e1c\u5357\u65b9\u7684\u6d77\u4e0a\u3002\u201d \u3000\u3000\u4e01\u6d9b\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\u4f60\u4f1a\u53bb\u8fc7\u90a3\u91cc\u3002\u201d \u3000\u3000\u7518\u971c\u9053\uff1a\u201c\u6211\u82e5\u6ca1\u53bb\u8fc7\u90a3\u91cc\uff0c\u4e3a\u4ec0\u4e48\u77e5\u9053\u90a3\u91cc\u7684\u5730\u540d\uff1f\u201d \u3000\u3000\u201c\u6628\u5929\u4f60\u8ddf\u90a3\u59d3\u6768\u7684\u8001\u5c0f\u5b50\uff0c\u6839\u672c\u662f\u5728\u80e1\u626f\uff0c\u4e0d\u8fc7\u4f60\u4eec\u4e24\u4eba\u505a\u620f\u5012\u662f\u505a\u5f97\u5f88\u903c\u771f\u3002\u201d \u3000\u3000\u7518\u9716\u6025\u5f97\u4e00\u8dfa\u811a\u9053\uff1a\u201c\u96be\u9053\u4f60\u8981\u6211\u62ff\u51fa\u8bc1\u636e\u6765\u624d\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u8bf7\u62ff\u51fa\u6765\u5427\uff01\u201d \u3000\u3000\u201c\u9694\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u6211\u5230\u54ea\u91cc\u62ff\u8bc1\u636e\uff1f\u8fd9\u4e0d\u662f\u903c\u7740\u6d3b\u4eba\u4e0a\u540a\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e0a\u540a\u7684\u5f53\u7136\u90fd\u662f\u6d3b\u4eba\uff0c\u540a\u4e0a\u4ee5\u540e\u624d\u80fd\u53d8\u6210\u6b7b\u4eba\uff0c\u4e0d\u8fc7\u60f3\u8bc1\u660e\u4e5f\u5f88\u7b80\u5355\uff0c\u4f60\u53ea\u8981\u8bf4\u51e0\u53e5 \u90a3\u8fb9\u7684\u8bdd\uff0c\u6211\u5c31\u77e5\u9053\u4f60\u53bb\u8fc7\u53f0\u6e7e\u6ca1\u6709\u3002\u201d \u3000\u3000\u7518\u9716\u662f\u5c0f\u65f6\u5019\u4f4f\u8fc7\u53f0\u6e7e\uff0c\u5982\u4eca\u9694\u4e86\u5c06\u8fd1\u4e09\u5341\u5e74\uff0c\u4ed6\u5b9e\u5728\u5df2\u8bb0\u4e0d\u5f97\u51e0\u53e5\u8bdd\uff0c\u4e0d\u8fc7\u9a82\u4eba\u548c\u96be \u767b\u5927\u96c5\u7684\u8bdd\uff0c\u4ed6\u8fd8\u8bb0\u5f97\u51e0\u53e5\u3002 \u3000\u3000\u4ed6\u563f\u563f\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u6211\u8bf4\u51fa\u6765\u4f60\u542c\u4e0d\u61c2\u4e5f\u662f\u778e\u5b50\u70b9\u706f\u2014\u2014\u767d\u8d39\u8721\u70db\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4e5f\u5658\u5634\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u53ea\u7ba1\u8bf4\uff0c\u6211\u662f\u5404\u5730\u65b9\u8a00\u6837\u6837\u7cbe\u901a\uff0c\u6ca1\u6709\u542c\u4e0d\u61c2\u7684\u8bdd\u3002\u201d \u3000\u3000\u7518\u9716\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u73b0\u5728\u5c31\u8bf4\u90a3\u8fb9\u7684\u8bdd\u4e86\uff0c\u9a7e\u5d29\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u6211\u867d\u7136\u6ca1\u8bfb\u8fc7\u4ec0\u4e48\u4e66\uff0c\u4f46\u5374\u77e5\u9053\u7687\u5e1d\u8001\u513f\u6b7b\u4e86\u53eb\u9a7e\u5d29\uff0c\u524d\u4e9b\u5929\u7687\u5e1d\u8001 \u513f\u7fd8\u4e86\u8fab\u5b50\uff0c\u6ee1\u7b49\u90fd\u558a\u9a7e\u5d29\u3002\u201d \u3000\u3000\u7518\u9716\u5f53\u7136\u4e0d\u8ddf\u4ed6\u89e3\u91ca\uff0c\u518d\u9053\uff1a\u201c\u68d2\u8d5b\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8fd9\u662f\u4e2a\u65b0\u5174\u7684\u73a9\u610f\u513f\uff0c\u4e0d\u77e5\u4ec0\u4e48\u4eba\u53d1\u660e\u4e86\u4e00\u79cd\u7528\u68d2\u5b50\u6253\u7684\u5c0f\u7403\uff0c\u68d2\u8d5b\u5f53\u7136\u5c31 \u662f\u6253\u90a3\u79cd\u7403\u6bd4\u8d5b\u3002\u201d \u3000\u3000\u7518\u9716\u7b80\u76f4\u8981\u55b7\u51fa\u996d\u6765\uff0c\u5f3a\u5fcd\u7740\u53c8\u9053\uff1a\u201c\u83ab\u5bb0\u7f8a\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u83ab\u5bb0\u7f8a\u5f53\u7136\u662f\u522b\u6740\u7f8a\uff0c\u8fde\u4e09\u5c81\u7684\u5b69\u5b50\u4e5f\u61c2\uff0c\u4f60\u4ed6\u59d0\u59d0\u7684\u60f3\u62ff\u8fd9\u79cd\u8bdd\u6765\u96be\u6211\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u9760\u80cc\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8fd9\u5c31\u66f4\u7b80\u5355\u4e86\uff0c\u6bd4\u65b9\u54b1\u4eec\u8d70\u7d2f\u4e86\uff0c\u5750\u4e0b\u6765\u4f11\u606f\uff0c\u628a\u80cc\u9760\u5728\u5899\u4e0a\u6216\u6905\u80cc\u4e0a\uff0c\u4e0d \u662f\u8212\u670d\u591a\u4e86\u4e48\uff1f\u201d \u3000\u3000\u7518\u9716\u563f\u563f\u4e00\u7b11\uff0c\u5e72\u8106\u6765\u4e2a\u8364\u7684\uff0c\u9053\uff1a\u201c\u70c2\u8549\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u60f3\u90fd\u6ca1\u60f3\u9053\uff1a\u201c\u542c\u8bf4\u53f0\u6e7e\u51fa\u4ea7\u9999\u8549\uff0c\u70c2\u8549\u5f53\u7136\u5c31\u662f\u70c2\u9999\u8549\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u4e0d\u60f3\u5403\uff1f\u201d \u3000\u3000\u201c\u90a3\u6562\u60c5\u597d\uff0c\u4e0d\u8fc7\u73b0\u5728\u7259\u53e3\u597d\uff0c\u7528\u4e0d\u7740\u5403\u70c2\u7684\uff0c\u7b49\u5c06\u6765\u8001\u4e86\u7259\u9f7f\u6389\u5149\u4e86\uff0c\u4e0d\u5403\u70c2\u8549\u4e5f\u5f97 \u5403\u70c2\u8549\u3002\u201d \u3000\u3000\u7518\u9716\u4e0d\u7981\u5403\u5403\u800c\u7b11\u3002 \u3000\u3000\u4e01\u6d9b\u6123\u4e86\u4e0b\u9053\uff1a\u201c\u7b11\u4ec0\u4e48\uff1f\u96be\u9053\u6211\u8bf4\u7684\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u5f88\u5bf9\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8fd9\u5c31\u8bc1\u660e\u4f60\u6ca1\u5230\u8fc7\u53f0\u6e7e\uff0c\u8bf4\u7684\u90fd\u662f\u4e9b\u4eba\u4eba\u542c\u5f97\u61c2\u7684\u8bdd\uff0c\u6211\u521a\u624d\u8bf4\u5404\u5730\u65b9\u8a00\u90fd \u542c\u5f97\u61c2\u5f97\uff0c\u4e0d\u8fc7\u662f\u6545\u610f\u8bc8\u4f60\u3002\u201d \u3000\u3000\u7518\u9716\u4e5f\u4e0d\u548c\u4ed6\u8fa9\uff0c\u56e0\u4e3a\u5230\u6ca1\u5230\u8fc7\u53f0\u6e7e\uff0c\u5728\u4ed6\u73b0\u5728\u6765\u8bf4\uff0c\u6839\u672c\u4e0d\u7b97\u4e00\u56de\u4e8b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u4e5f\u53ea\u662f\u542c\u7740\uff0c\u4e0d\u7ba1\u61c2\u4e0d\u61c2\uff0c\u4ed6\u4eec\u90fd\u6ca1\u6709\u63d2\u5634\u7684\u5fc5\u8981\u3002 \u3000\u3000\u524d\u8fdb\u5927\u7ea6\u4e00\u91cc\u5de6\u53f3\uff0c\u524d\u9762\u7adf\u53c8\u662f\u4e00\u5904\u96a7\u9053\u3002 \u3000\u3000\u8fd9\u6761\u8c37\u9053\uff0c\u5f53\u771f\u5947\u602a\uff0c\u7167\u7406\u8bf4\u9047\u5230\u5c71\u6d1e\uff0c\u5fc5\u662f\u8c37\u9053\u5df2\u5230\u5c3d\u5934\uff0c\u4f46\u5374\u504f\u504f\u4e0d\u7136\uff0c\u7a7f\u51fa\u5c71\u6d1e \u4e4b\u540e\uff0c\u90a3\u5f62\u52bf\u7adf\u7136\u53c8\u662f\u5148\u524d\u8c37\u9053\u7684\u5ef6\u4f38\uff0c\u5c31\u50cf\u8fd9\u5c71\u6d1e\u662f\u4eba\u5de5\u7531\u8c37\u9053\u4e0a\u7b51\u76d6\u7684\u4e00\u822c\u3002 \u3000\u3000\u679c\u771f\u5982\u6b64\uff0c\u90a3\u5de5\u7a0b\u7684\u6d69\u5927\uff0c\u4e5f\u5c31\u53ef\u60f3\u800c\u77e5\u4e86\u3002 \u3000\u3000\u7ea6\u83ab\u63a5\u8fd1\u96a7\u9053\u53e3\u53ea\u6709\u56db\u4e94\u4e08\u5904\uff0c\u7a81\u7136\u95f4\u95ea\u51fa\u56db\u540d\u7ea2\u8863\u7ea2\u88d9\u7684\u5c11\u5973\u3002 \u3000\u3000\u8fd9\u56db\u540d\u7ea2\u8863\u5c11\u5973\uff0c\u4e0d\u4f46\u8863\u670d\u989c\u8272\u5982\u4e94\u6708\u69b4\u82b1\uff0c\u663e\u5f97\u8273\u4e3d\u65e0\u6bd4\uff0c\u800c\u4e14\u4f53\u6001\u8f7b\u76c8\uff0c\u5a40\u5a1c\u591a\u59ff\uff0c \u770b\u5f97\u4e01\u6d9b\u548c\u7518\u9716\u4e24\u4eba\uff0c\u987f\u65f6\u6709\u4e9b\u5fc3\u733f\u610f\u9a6c\u8d77\u6765\u3002 \u3000\u3000\u5c24\u5176\u4e01\u6d9b\uff0c\u4f3c\u4e4e\u5df2\u5fd8\u8bb0\u5728\u6b66\u5173\u65f6\u7684\u72fc\u72c8\u7a9d\u56ca\uff0c\u53c8\u663e\u51fa\u4ed6\u90a3\u4e0e\u751f\u60e7\u6765\u7684\u7537\u5b50\u6c14\u6982\u3002 \u3000\u3000\u53ea\u542c\u4e3a\u9996\u90a3\u540d\u5c11\u5973\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u5a07\u6ef4\u6ef4\u7684\uff0c\u5b9e\u5728\u7f8e\u5999\u81f3\u6781\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u4e00\u5411\u505a\u4e8b\u89c4\u77e9\uff0c\u4f9d\u8a00\u505c\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u4e5f\u4e0d\u5f97\u4e0d\u8ddf\u7740\u7ad9\u4f4f\u3002 \u3000\u3000\u90a3\u4e3a\u9996\u7684\u5c11\u5973\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u662f\u95ef\u8fc7\u6b66\u5173\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u59d1\u5a18\u672a\u514d\u591a\u6b64\u4e00\u95ee\uff0c\u82e5\u4e0d\u95ef\u6b66\u5173\uff0c\u6211\u4eec\u600e\u4f1a\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u90a3\u4e3a\u9996\u5c11\u5973\u9053\uff1a\u201c\u65e2\u80fd\u95ef\u8fc7\u6b66\u5173\uff0c\u53ef\u89c1\u56db\u4f4d\u5fc5\u5b9a\u8eab\u624b\u4e86\u5f97\uff0c\u4e0d\u8fc7\u8981\u95ef\u8fc7\u773c\u4e0b\u7684\u8fd9\u4e00\u5173\uff0c \u5c31\u4e0d\u662f\u90a3\u4e48\u5bb9\u6613\u7684\u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1b\u201c\u59d1\u5a18\u8fd9\u4e00\u5173\u662f\u4ec0\u4e48\u5173\uff1f\u201d \u3000\u3000\u4e3a\u9996\u7684\u5c11\u5973\u9053\uff1a\u201c\u56db\u4f4d\u8bf7\u7a0d\u5f85\uff0c\u6211\u8bf7\u7edf\u9886\u51fa\u6765\uff01\u201d \u3000\u3000\u8bf4\u7740\u8f6c\u8eab\u8083\u5bb9\u53eb\u9053\uff1a\u201c\u7980\u7edf\u9886\uff0c\u4ed6\u4eec\u56db\u4f4d\u5230\u4e86\uff01\u201d \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c \u4e03 \u56de\u3000\u667a\u8fc7\u706b\u5173 \u3000\u3000\u7acb\u523b\uff0c\u7531\u96a7\u9053\u524d\u8c37\u58c1\u4e0a\u7684\u6d1e\u95e8\u5185\uff0c\u8885\u8885\u5a1c\u5a1c\u8d70\u51fa\u4e00\u540d\u4e2d\u5e74\u5973\u5b50\u3002 \u3000\u3000\u8fd9\u5973\u5b50\u770b\u6765\u5e74\u5728\u4e09\u5341\u4ee5\u4e0a\uff0c\u4e5f\u662f\u7ea2\u8863\u7ea2\u88d9\uff0c\u867d\u7136\u5e74\u7eaa\u6bd4\u90a3\u56db\u540d\u5c11\u5973\u5927\u5f97\u591a\uff0c\u4f46\u9762\u8c8c\u59e3\u597d\uff0c \u6c14\u8d28\u9ad8\u96c5\uff0c\u98ce\u97f5\u695a\u695a\uff0c\u6bd4\u8d77\u90a3\u56db\u540d \u3000\u3000\u5c11\u5973\uff0c\u66f4\u4e3a\u52a8\u4eba\u3002 \u3000\u3000\u5979\u59d7\u59d7\u6765\u5230\u8fd1\u524d\uff0c\u51dd\u51e4\u76ee\uff0c\u542f\u6731\u5507\uff0c\u53d1\u51fa\u71d5\u8bed\u83ba\u5456\u822c\u7684\u58f0\u97f3\u9053\uff1a\u201c\u672c\u7edf\u9886\u5df2\u7ecf\u5f97\u5230\u6b66\u5173 \u7edf\u9886\u7684\u901a\u77e5\uff0c\u77e5\u9053\u4f60\u4eec\u56db\u4eba\u5f53\u4e2d\uff0c\u90a3\u4e24\u4eba\u6b66\u529f\u5341\u5206\u51fa\u8272\uff0c\u53e6\u4e24\u4eba\u53ea\u80fd\u7b97\u662f\u6df7\u51fa\u6b66\u5173\u7684\uff0c\u5bf9 \u4e48\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u7edf\u9886\u8a00\u5dee\u4e86\uff0c\u5176\u5b9e\u6211\u4eec\u56db\u4eba\u7684\u6b66\u529f\u90fd\u5dee\u4e0d\u591a\uff0c\u6b66\u5173\u4e0a\u90a3\u4f4d\u9a7c\u80cc\u7edf\u9886\u6267\u6cd5\u4e25 \u5f97\u5f88\uff0c\u4e0d\u53ef\u80fd\u5bf9\u4efb\u4f55\u4eba\u653e\u6c34\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8fd9\u6837\u8bf4\u6d3b\uff0c\u9664\u4e86\u4e3a\u4e01\u3001\u7518\u4e24\u4eba\u7559\u9762\u5b50\uff0c\u53e6\u5916\u7684\u7528\u610f\uff0c\u4e0d\u5916\u662f\u5e0c\u671b\u56db\u4eba\u4ecd\u80fd\u4e00\u8d77\u8fc7 \u5173\u3002 \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u8f6c\u5934\u671b\u5411\u90a3\u56db\u540d\u5c11\u5973\u9053\uff1a\u201c\u4eba\u4e0d\u53ef\u8c8c\u76f8\uff0c\u6c34\u4e0d\u53ef\u6597\u91cf\uff0c\u4f60\u4eec\u731c\u731c\u770b\uff0c\u4ed6\u4eec\u56db\u4eba \u5f53\u4e2d\uff1f\u54ea\u4e24\u4e2a\u6b66\u529f\u9ad8\u5f3a\uff1f\u54ea\u4e24\u4e2a\u6b66\u529f\u5e73\u5e38\uff1f\u201d \u3000\u3000\u4e00\u540d\u773c\u775b\u5927\u5927\u7684\u7ea2\u8863\u5c11\u5973\u9053\uff1a\u201c\u5f53\u7136\u662f\u90a3\u4e24\u4e2a\u5e74\u7eaa\u5927\u3001\u4eba\u4e5f\u957f\u5f97\u9ad8\u5934\u5927\u9a6c\u7684\u6b66\u529f\u9ad8\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u9053\uff1a\u201c\u522b\u770b\u4f60\u773c\u775b\u5927\uff0e\u773c\u5149\u5374\u4e0d\u2014\u5b9a\u51c6\u3002\u201d \u3000\u3000\u63a5\u7740\u53c8\u95ee\u90a3\u4e3a\u9996\u7684\u7ea2\u8863\u5c11\u5973\u9053\uff1a\u201c\u4f60\u8bf4\uff01\u201d \u3000\u3000\u4e3a\u9996\u7684\u7ea2\u8863\u5c11\u5973\u9053\uff1a\u201c\u6b66\u5173\u7edf\u9886\u5fc5\u5b9a\u5df2\u7ecf\u544a\u8bc9\u4e86\u4f60\u8001\u4eba\u5bb6\uff0c\u4f60\u8001\u4eba\u5bb6\u4f55\u4e0d\u76f4\u63a5\u8bf4\u51fa\u6765\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u7b11\u9053\uff1a\u201c\u8fd8\u662f\u4f60\u8fd9\u4e2b\u5934\u673a\u4f36\uff0c\u7528\u4e0d\u7740\u731c\u5566\uff0c\u5f85\u4f1a\u513f\u4f60\u4eec\u5c31\u770b\u5230\u4e86\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u62b1\u4e86\u62b1\u62f3\u9053\uff1a\u201c\u8bf7\u95ee\u7edf\u9886\uff0c\u8fd9\u91cc\u5230\u5e95\u662f\u4ec0\u4e48\u5173\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u2018\u706b\u5173\u2019\u3002\u201d \u3000\u3000\u201c\u706b\u5173\uff1f\u201d\u8881\u5c0f\u9e64\u5fc3\u5c16\u4e00\u9707\uff1a\u201c\u706b\u5173\u8981\u600e\u6837\u8fc7\u6cd5\uff1f\u201d \u3000\u3000\u201c\u706b\u5173\u4e0d\u987b\u52a8\u624b\u8fc7\u62db\uff0e\u4e3b\u8981\u662f\u51ed\u4ed7\u8f7b\u529f\uff0c\u53ea\u8981\u8f7b\u529f\u9ad8\u7edd\uff0c\u4fbf\u53ef\u4ee5\u8fc7\u5f97\u53bb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u542c\u5f97\u6697\u559c\uff0c\u56e0\u4e3a\u4ed6\u7684\u6b66\u529f\u5df2\u9ad8\u8fc7\u8881\u5c0f\u9e64\uff0c\u82e5\u8bba\u8f7b\u529f\uff0c\u8d8a\u53d1\u662f\u4ed6\u6700\u62ff\u624b\u7684\u7edd\u6d3b\u3002 \u3000\u3000\u8fd9\u65f6\u4e01\u6d9b\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u5982\u679c\u8f7b\u529f\u5dee\u4e9b\uff0c\u8fc7\u4e0d\u53bb\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u8fc7\u706b\u5173\u6bd4\u8fc7\u6b66\u5173\u66f4\u96be\u7684\u539f\u56e0\uff0c\u6b66\u5173\u8fc7\u4e0d\u53bb\u8fd8\u53ef\u4ee5\u56de\u53bb\uff0c\u706b\u5173\u8fc7\u4e0d \u53bb\u5c31\u5f88\u5c11\u6709\u56de\u53bb\u7684\u53ef\u80fd\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u8fc7\u4e0d\u53bb\u706b\u5173\uff0c\u5341\u6709\u516b\u4e5d\u4f1a\u88ab\u70e7\u6b7b\uff0c\u65e2\u7136\u88ab\u70e7\u6b7b\uff0c\u8fd8\u600e\u80fd\u518d\u56de\u53bb\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u8f7b\u529f\u4e0d\u6d4e\u7684\uff0c\u90a3\u5c31\u975e\u6b7b\u4e0d\u53ef\u4e86\uff1f\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u4e00\u5b9a\uff0c\u82e5\u81ea\u5df1\u89c9\u5f97\u6ca1\u628a\u63e1\uff0c\u5c3d\u53ef\u4ee5\u4e0d\u8fc7\uff0c\u672c\u7edf\u9886\u51b3\u4e0d\u903c\u4f60\u4eec\u975e\u8fc7\u4e0d\u53ef\u3002\u201d \u3000\u3000\u201c\u706b\u5173\u7684\u706b\u5728\u90a3\u91cc\uff0c\u4f60\u8be5\u8ba9\u6211\u4eec\u5148\u770b\u770b\uff0c\u518d\u51b3\u5b9a\u8fc7\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\u8981\u8ba9\u4f60\u4eec\u770b\uff0c\u5426\u5219\u82e5\u903c\u4f60\u4eec\u975e\u8fc7\u4e0d\u53ef\uff0c\u53ea\u6015\u6211\u8fd9\u706b\u5173\u6210\u7acb\uff0c\u81f3\u5c11\u8981\u70e7\u6b7b\u51e0\u767e\u4eba \u4e86\u3002\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u8fb9\u8bf4\u8fb9\u5429\u5490\u4e3a\u9996\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6253\u5f00\u706b\u5173\u8ba9\u4ed6\u4eec\u77a7\u77a7\uff01\u201d \u3000\u3000\u90a3\u4e3a\u9996\u5c11\u5973\u7acb\u523b\u8fdb\u5165\u8c37\u58c1\u4e0a\u7684\u6d1e\u53e3\uff0c\u4e0d\u5927\u4e00\u4f1a\uff0c\u4fbf\u53c8\u8d70\u4e86\u51fa\u6765\uff0c\u8eac\u8eab\uff1a\u201c\u7980\u7edf\u9886\uff0c\u5df2\u7ecf \u53d1\u52a8\u4e86\uff01\u201d \u3000\u3000\u8fd9\u65f6\u5cb3\u5c0f\u98de\u3001\u8881\u5c0f\u9e64\u3001\u4e01\u6d9b\u3001\u7518\u9716\u90fd\u4e24\u773c\u7728\u4e5f\u4e0d\u7728\u7684\u671b\u5411\u96a7\u9053\u53e3\u3002 \u3000\u3000\u679c\u7136\uff0c\u987f\u65f6\u7531\u96a7\u9053\u5185\u4f20\u6765\u4e86\u4e00\u9635\u9686\u9686\u9707\u8033\u4e4b\u58f0\uff0c\u63a5\u7740\uff0c\u96a7\u9053\u4e24\u4fa7\u7684\u5e95\u90e8\u5ffd\u7136\u88c2\u4e86\u5f00\u6765\uff0c \u88c2\u5f00\u4e00\u6761\u8db3\u5728\u4e09\u5c3a\u9ad8\u7684\u7f1d\u9699\u3002 \u3000\u3000\u81f3\u4e8e\u8fd9\u4e24\u6761\u7f1d\u9699\u6709\u591a\u957f\uff0c\u867d\u7136\u770b\u4e0d\u771f\u5207\uff0c\u4f46\u636e\u4f30\u8ba1\u5c11\u8bf4\u4e5f\u6709\u4e94\u516d\u4e08\u9ad8\u3002 \u3000\u3000\u84e6\u5730\uff0c\u4e00\u58f0\u96f7\u9707\u822c\u7684\u5de8\u54cd\u4e4b\u540e\uff0c\u90a3\u96a7\u9053\u4e24\u8fb9\u7684\u7f1d\u9699\u7adf\u7136\u5192\u51fa\u706b\u6765\u3002 \u3000\u3000\u706b\u82d7\u8d77\u521d\u5e76\u4e0d\u5927\uff0c\u6e10\u6e10\u706b\u7130\u8d8a\u6765\u8d8a\u65fa\uff0c\u5230\u540e\u6765\u7adf\u7136\u5f62\u6210\u4e00\u7247\u706b\u6d77\uff0c\u800c\u4e14\u4e24\u8fb9\u7684\u706b\u4ea4\u4f1a\u4e00 \u8d77\u3002\u540c\u65f6\u53c8\u98ce\u58f0\u547c\u547c\uff0c\u98ce\u52a9\u706b\u52bf\uff0c\u706b \u3000\u3000\u663e\u98ce\u5a01\uff0c\u8fde\u5cb3\u5c0f\u98de\u7b49\u7ad9\u5728\u96a7\u9053\u53e3\u7684\u6570\u4e08\u4e4b\u5916\uff0c\u90fd\u611f\u5230\u7099\u70ed\u96be\u5f53\u3002 \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u968f\u5373\u6325\u4e86\u6325\u624b\u9053\uff1a\u201c\u7184\u706b\uff01\u201d \u3000\u3000\u90a3\u4e3a\u9996\u7684\u5c11\u5973\u5e94\u58f0\u53c8\u8fdb\u5165\u6d1e\u5185\u3002 \u3000\u3000\u706b\u52bf\u4e5f\u968f\u4e4b\u7184\u53bb\u3002 \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u706b\u5173\uff0c\u706b\u53d1\u4e4b\u540e\uff0c\u4f60\u4eec\u53ea\u8981\u8c01\u80fd\u901a\u8fc7\uff0c\u90a3\u5c31\u5927\u529f\u544a\u6210\u3002\u201d \u3000\u3000\u6b64\u65f6\u4e01\u6d9b\u548c\u7518\u9716\u65e9\u5df2\u5446\u5728\u5f53\u573a\u3002 \u3000\u3000\u8bb8\u4e45\uff0c\u4e01\u6d9b\u624d\u5482\u7740\u5634\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\u6709\u4eba\u8fc7\u5f97\u4e86\u706b\u5173\uff0c\u53ea\u6015\u51b2\u8fdb\u53bb\u4e0d\u5230\u4e24\u6b65\uff0c\u4e0d\u88ab\u70e7\u6210 \u7126\u70ad\u624d\u602a\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u81ea\u77e5\u8fc7\u4e0d\u53bb\uff0c\u5f53\u7136\u53ef\u4ee5\u4e0d\u8fc7\uff0c\u672c\u7edf\u9886\u51b3\u4e0d\u52c9\u5f3a\u3002\u201d \u3000\u3000\u4e01\u6d9b\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u5bf9\u4e86\uff0c\u6211\u4eec\u56db\u4eba\u4e2d\uff0c\u90a3\u4f4d\u5cb3\u516c\u5b50\u548c\u8881\u5c11\u4fa0\u624d\u662f\u771f\u6b63\u7684\u82f1 \u96c4\u4eba\u7269\uff0c\u6211\u867d\u7136\u8fc7\u4e0d\u53bb\uff0c\u4f46\u5374\u5e0c\u671b\u4ed6\u4eec\u8fc7\u53bb\u3002\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u671b\u5411\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u81ea\u4fe1\u80fd\u8fc7\u5f97\u4e86\u706b\u5173\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u773c\u770b\u65b9\u624d\u7684\u706b\u52bf\uff0e\u5f53\u7136\u4e5f\u77e5\u9053\u65e0\u6cd5\u8fc7\u53bb\uff0c\u4e0d\u8fc7\u4ed6\u4eec\u5374\u5728\u6000\u7591\uff0c\u4ee5\u5f80\u901a\u8fc7 \u706b\u5173\u7684\u90a3\u4e9b\u4eba\uff0c\u4ed6\u4eec\u53c8\u662f\u600e\u6837\u901a\u8fc7\u7684\u5462\uff1f\u56e0\u4e3a\u8fd9\u6839\u672c\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u7edf\u9886\u4e3a\u4ec0\u4e48\u628a\u706b\u7184\u4e86\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u8fd9\u706b\u5173\u4e00\u5f00\u52a8\uff0c\u8981\u7528\u53bb\u591a\u5c11\u7684\u785d\u78f7\u6cb9\u8102\uff0c\u82e5\u5f00\u52a8\u4e45\u4e86\uff0c\u8fd9\u4e9b\u4e1c \u897f\u4f9b\u5e94\u4e0d\u53ca\uff0c\u706b\u5173\u7684\u4efb\u52a1\uff0c\u5c82\u4e0d\u5c31\u9677\u5165\u505c\u987f\uff1f\u201d \u3000\u3000\u5979\u89c1\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u9ed8\u4e0d\u542d\u58f0\uff0c\u4fbf\u50ac\u4fc3\u7740\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u7a76\u7adf\u8981\u4e0d\u8981\u8fc7\uff0e\u5fc5\u987b\u9a6c\u4e0a\u8868\u793a \u610f\u89c1\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7a81\u7136\u7075\u673a\u4e00\u52a8\uff0e\u95ee\u9053\uff1a\u201c\u8bf7\u95ee\u7edf\u9886\uff0c\u4ee5\u5f80\u6709\u6ca1\u6709\u8fc7\u5f97\u53bb\u7684\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u9053\uff1a\u201c\u5f53\u7136\u6709\uff1f\u800c\u4e14\u4e3a\u6570\u4e0d\u5c11\uff0c\u4e0d\u7136\u5929\u8c37\u8fd8\u62db\u6536\u7684\u4ec0\u4e48\u4eba\u9a6c\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6447\u5934\u4e00\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u201c\u600e\u6837\u4f60\u624d\u80fd\u76f8\u4fe1\u5462\uff1f\u201d \u3000\u3000\u201c\u5f88\u7b80\u5355\uff0c\u53ea\u8981\u8ba9\u6211\u4eb2\u773c\u770b\u5230\u6709\u4eba\u8fc7\u53bb\uff0e\u90a3\u5c31\u4e0d\u76f8\u4fe1\u4e5f\u5f97\u76f8\u4fe1\u3002\u201d \u3000\u3000\u201c\u4f60\u76f8\u4fe1\u4e86\u4ee5\u540e\u5462\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u522b\u4eba\u8fc7\u5f97\u53bb\uff0c\u6211\u5c31\u4e00\u5b9a\u8fc7\u5f97\u53bb\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u53e3\u6c14\u4e0d\u5c0f\uff1f\u201d \u3000\u3000\u201c\u4e0d\u6562\u3002\u201d \u3000\u3000\u201c\u5c0f\u5144\u5f1f\uff0c\u8bf4\u8bdd\u53ef\u8981\u7b97\u6570\uff1f\u201d \u3000\u3000\u201c\u4e00\u8a00\u65e2\u51fa\uff0c\u9a77\u9a6c\u96be\u8ffd\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u7acb\u5373\u5429\u5490\u4e3a\u9996\u7684\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6843\u82b1\uff0c\u4f60\u5c31\u8fc7\u4e00\u8d9f\u706b\u5173\u8ba9\u4ed6\u4eec\u770b\u770b\uff01\u201d \u3000\u3000\u518d\u5429\u5490\u53e6\u4e00\u540d\u5c11\u5973\u9053\uff1a\u201c\u4f60\u5230\u91cc\u9762\u8d1f\u8d23\u53d1\u52a8\u673a\u5173\uff01\u201d \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\uff0c\u96a7\u9053\u5185\u7684\u70c8\u706b\u53c8\u5df2\u718a\u718a\u5927\u4f5c\u3002 \u3000\u3000\u90a3\u53eb\u6843\u82b1\u7684\u7ea2\u8863\u5c11\u5973\uff0c\u817e\u7a7a\u800c\u8d77\uff0c\u6709\u5982\u5f69\u51e4\u5f52\u5de2\u822c\u76f4\u5411\u706b\u7a74\u5185\u7eb5\u8eab\u800c\u5165\uff0c\u970e\u65f6\u6d88\u5931\u4e0d\u89c1\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u5cb3\u5c0f\u98de\u7b49\u56db\u4eba\u90fd\u4e0d\u7981\u76ee\u77aa\u53e3\u5446\uff0c\u7b80\u76f4\u50cf\u9677\u5165\u68a6\u5883\u4e00\u822c\u3002\u83ab\u975e\u90a3\u53eb\u6843\u82b1\u7684\u5c11\u5973 \u7adf\u80fd\u50cf\u9f50\u5929\u5927\u5723\u822c\u5728\u592a\u4e0a\u8001\u541b\u7684\u70bc\u4e39\u516b\u5366\u7089\u953b\u70bc\u4e86\u4e03\u4e03\u56db\u4e5d\u65e5\u4ee5\u81f3\u6210\u4e86\u6c34\u706b\u4e0d\u4fb5\u4e4b\u4f53\uff1f\u76f4\u5f85 \u706b\u52bf\u7184\u706d\u540e\uff0c\u56db\u4eba\u624d\u7531\u68a6\u4e2d\u9192\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u53ea\u542c\u4e01\u6d9b\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u7edf\u9886\uff0c\u4f60\u7684\u624b\u6bb5\u672a\u514d\u592a\u72e0\u6bd2\u4e86\u5427\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u8138\u8272\u4e00\u53d8\uff0c\u53f1\u9053\uff1a\u201c\u4f60\u8bf4\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4f60\u4e3a\u4e86\u8ba9\u6211\u4eec\u76f8\u4fe1\uff0c\u4e0d\u60dc\u9001\u6389\u4e00\u4f4d\u59d1\u5a18\u7684\u547d\uff0c\u90a3\u4f4d\u59d1\u5a18\u4e5f\u662f\u7236\u6bcd\u517b\u7684\uff0c\u6a21\u6837 \u53c8\u90a3\u4e48\u7f8e\uff0c\u5c31\u8fd9\u6837\u8ba9\u5979\u767d\u767d\u6b7b\u4e86\uff0e\u591a\u53ef\u60dc\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u8f6c\u989c\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u8fd9\u6837\uff0c\u4f60\u600e\u80fd\u6599\u5b9a\u5979\u4e00\u5b9a\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u53ef\u662f\u4f60\u53c8\u600e\u80fd\u8bc1\u660e\u5979\u6ca1\u6b7b\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u7acb\u5373\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6843\u82b1\u56de\u6765\uff01\u201d \u3000\u3000\u8bdd\u58f0\u521a\u6bd5\uff0c\u6843\u82b1\u679c\u7136\u7531\u96a7\u9053\u6df1\u5904\u73b0\u4e86\u8eab\u3002 \u3000\u3000\u5f85\u5979\u6765\u5230\u773c\u524d\uff0c\u9664\u4e86\u88d9\u89d2\u88ab\u70e7\u53bb\u5c11\u8bb8\uff0c\u5176\u4ed6\u7adf\u7136\u6beb\u53d1\u672a\u4f24\u3002 \u3000\u3000\u5982\u679c\u8fd9\u4e0d\u662f\u5947\u8ff9\uff0e\u771f\u4e0d\u77e5\u5929\u4e0b\u8fd8\u6709\u4ec0\u4e48\u624d\u7b97\u5947\u8ff9\u3002 \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u76ee\u6ce8\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e00\u8a00\u65e2\u51fa\uff0c\u9a77\u9a6c\u96be\u8ffd\uff0c\u8fd9\u8bdd\u521a\u624d\u53ef\u662f\u4f60\u8bf4\u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6beb\u4e0d\u754f\u602f\uff0c\u633a\u4e86\u633a\u80f8\u9053\uff1a\u201c\u8bdd\u65e2\u7136\u51fa\u53e3\uff0c\u5f53\u7136\u4e0d\u80fd\u4e0d\u627f\u8ba4\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u73b0\u5728\u8be5\u600e\u4e48\u505a\uff1f\u201d \u3000\u3000\u201c\u7edf\u9886\u53ea\u7ba1\u628a\u706b\u5173\u5f00\u52a8\u8d77\u6765\uff0c\u6211\u9a6c\u4e0a\u95ef\u5173\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8bf4\u7740\u4fbf\u4f5c\u52bf\u5f85\u6251\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u6025\u6025\u4e00\u628a\u62c9\u4f4f\uff0c\u6d6f\u6c14\u6fc0\u52a8\u7684\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u4f60\u60f3\u627e\u6b7b\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4f3c\u4e4e\u6beb\u4e0d\u5728\u610f\u9053\uff1a\u201c\u8881\u5927\u54e5\u522b\u62c9\uff0c\u5c0f\u5f1f\u4ec0\u4e48\u65f6\u5019\u5728\u4eba\u524d\u8bf4\u8bdd\u4e0d\u7b97\u8bdd\u8fc7\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e24\u773c\u53d1\u8d64\uff0c\u5927\u58f0\u9053\uff1a\u201c\u53ef\u662f\u4f60\u4e0d\u80fd\u660e\u77e5\u6b7b\u8def\u4e00\u6761\uff0c\u5374\u504f\u8981\u53bb\u6b7b\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6b63\u8272\u9053\uff1a\u201c\u4e3a\u4e86\u4e0d\u88ab\u4eba\u8ba5\u7b11\u8a00\u800c\u65e0\u4fe1\uff0c\u6b7b\u4e5f\u6b7b\u7684\u503c\u5f97\uff01\u201d \u3000\u3000\u8fd9\u65f6\u4e01\u6d9b\u548c\u7518\u9716\u4e5f\u5927\u4e3a\u7d27\u5f20\uff0c\u4ed6\u4eec\u4e0d\u80fd\u773c\u770b\u5cb3\u5c0f\u98de\u5c31\u8fd9\u6837\u767d\u767d\u727a\u7272\uff0c\u4e5f\u968f\u5373\u56f4\u8fc7\u6765\u628a\u5cb3 \u5c0f\u98de\u5835\u4f4f\u3002 \u3000\u3000\u53ea\u542c\u4e2d\u5e74\u5973\u5b50\u9053\uff1b\u201c\u706b\u5173\u5df2\u7ecf\u5f00\u52a8\u4e86\uff0c\u5982\u679c\u5f53\u771f\u6015\u6b7b\uff0c\u6211\u4e5f\u5e76\u4e0d\u52c9\u5f3a\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e5f\u7d27\u8ddf\u7740\u9053\uff1b\u201c\u5c0f\u98de\uff0c\u542c\u5230\u6ca1\u6709\uff0c\u4eba\u5bb6\u7edf\u9886\u5df2\u7ecf\u4e0d\u8ffd\u7a76\u4f60\u8bf4\u7684\u90a3\u53e5\u8bdd\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u521a\u521a\u8bf4\u5b8c\uff0c\u7a81\u89c9\u62c9\u4f4f\u5cb3\u5c0f\u98de\u90a3\u53ea\u624b\u4e94\u6307\u4e00\u677e\uff0c\u5cb3\u5c0f\u98de\u7adf\u5df2\u8131\u8eab\u800c\u51fa\uff0c\u53ea\u4e24\u4e2a\u8d77\u843d\uff0c \u4fbf\u5df2\u5230\u5728\u96a7\u9053\u53e3\uff0c\u7136\u540e\u7eb5\u8eab\u4e00\u8dc3\uff0c\u4eba\u5f71\u7acb\u5373\u5728\u70c8\u7130\u6d53\u70df\u4e2d\u6d88\u5931\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u51b2\u81f3\u706b\u5173\u5f7c\u7aef\uff0c\u867d\u7136\u8863\u670d\u4e0b\u6446\u4e5f\u7834\u70e7\u6389\u4e00\u622a\uff0c\u5168\u8eab\u7099\u70ed\u5982\u711a\uff0c\u4f46\u4e0d\u4e45\u4e4b\u540e\uff0c\u4fbf\u5df2 \u4e00\u5207\u5982\u5e38\u3002 \u3000\u3000\u539f\u6765\u4ed6\u5df2\u770b\u51fa\u7531\u96a7\u9053\u4e24\u4fa7\u55b7\u51fa\u6765\u7684\u70c8\u7130\uff0c\u79bb\u5730\u53ea\u6709\u4e09\u56db\u5c3a\u9ad8\uff0c\u800c\u8fd9\u96a7\u9053\u7531\u5e95\u81f3\u9876\u5374\u9ad8\u6709 \u4e24\u4e08\uff0c\u53ea\u8981\u8d34\u7740\u6d1e\u9876\u98de\u884c\uff0c\u4e5f\u8bb8\u80fd\u4e00\u53e3\u6c14\u63a0\u8fc7\u706b\u5173\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u96be\u4ee5\u9884\u6599\u7684\uff0c\u662f\u706b\u5173\u7a76\u6709\u591a\u8fdc\u8ddd\u79bb\uff0e\u53ea\u8981\u5728\u4e94\u516d\u4e08\u4e4b\u5185\uff0c\u4ed6\u76f8\u4fe1\u53ef\u4e00\u53e3\u6c14\u63a0\u8fc7\uff1f \u82e5\u8fc7\u8fdc\u800c\u4e0d\u80fd\u4e00\u53e3\u6c14\u63a0\u8fc7\uff0c\u90a3\u5c31\u5fc5\u6b7b\u65e0\u7591\u4e86\u3002 \u3000\u3000\u597d\u5728\u8fd9\u706b\u5173\u7684\u8ddd\u79bb\u53ea\u6709\u56db\u4e94\u4e08\u8fdc\uff0c\u518d\u52a0\u4e0a\u4ed6\u5728\u751f\u6b7b\u4e00\u53d1\u95f4\u800c\u6fc0\u53d1\u51fa\u7684\u795e\u529b\uff0c\u5373\u4fbf\u518d\u52a0\u957f \u4e24\u4e09\u4e08\u4e5f\u7167\u6837\u53ef\u4ee5\u4e00\u53e3\u6c14\u63a0\u8fc7\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u6700\u62c5\u5fc3\u7684\u662f\u8881\u5c0f\u9e64\u548c\u4e01\u6d9b\u3001\u7518\u9716\u4e09\u4eba\u3002\u8881\u5c0f\u9e64\u4e5f\u8bb8\u53ef\u4ee5\u52c9\u5f3a\u901a\u8fc7\uff0c\u4f46\u5374\u5f88\u96be\u4e0d \u88ab\u707c\u6210\u91cd\u4f24\u3002 \u3000\u3000\u81f3\u4e8e\u4e01\u6d9b\u548c\u7518\u9716\uff0c\u90a3\u662f\u7edd\u5bf9\u65e0\u6cd5\u901a\u8fc7\u7684\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u4eec\u4e09\u4eba\uff0c\u6700\u597d\u662f\u77e5\u96be\u800c\u9000\u3002 \u3000\u3000\u4f46\u90a3\u6837\u4e00\u6765\uff0c\u4ed6\u5c82\u4e0d\u5931\u53bb\u4f19\u4f34\u800c\u53ea\u5269\u4e0b\u81ea\u5df1\u4e00\u4eba\u4e86\uff01 \u3000\u3000\u5411\u5bf9\u9762\u671b\u53bb\uff0c\u706b\u7130\u5df2\u7ecf\u7184\u706d\u4f46\u5374\u770b\u4e0d\u5230\u8881\u5c0f\u9e64\u7b49\u4e09\u4eba\u662f\u5426\u4ecd\u5728\u539f\u5904\uff0c\u56e0\u4e3a\u8fd9\u6761\u96a7\u9053\u5e76\u975e \u76f4\u7ebf\uff0c\u6545\u800c\u65e0\u6cd5\u4ece\u8fd9\u5934\u770b\u5230\u90a3\u5934\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u4e00\u9635\u6025\u4fc3\u800c\u53c8\u6742\u4e71\u7684\u811a\u6b65\u58f0\u7531\u96a7\u9053\u7684\u53e6\u4e00\u7aef\u4f20\u6765\uff0c\u5f88\u5feb\u7684\u4fbf\u51fa\u73b0\u4e09\u540d\u5954 \u901f\u5982\u98de\u7684\u4eba\u5f71\u3002 \u3000\u3000\u8fd9\u4e09\u4eba\u6b63\u662f\u8881\u5c0f\u9e64\u3001\u4e01\u6d9b\u548c\u7518\u9716\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u6765\u5230\u8ddf\u524d\uff0c\u8881\u5c0f\u9e64\u6025\u6025\u62c9\u4f4f\u5cb3\u5c0f\u98de\u7684\u624b\uff0c\u60ca\u559c\u800c\u53c8\u6fc0\u52a8\u7684\u53eb\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u539f\u6765\u4f60 \u8fd8\u6d3b\u7740\uff01\u201d \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u5954\u51fa\u96a7\u9053\u53e3\u540e\uff0e\u5168\u90fd\u6251\u5012\u5730\u4e0a\uff0c\u7136\u540e\u5750\u8d77\u6765\u53cc\u624b\u62b1\u811a\uff0c\u54ac\u7259\u54a7\u5634\u7684\u60e8\u547c\u4e0d\u5df2\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u987e\u4e0d\u5f97\u95ee\u5cb3\u5c0f\u98de\u56e0\u4f55\u5e78\u751f\u7684\u539f\u56e0\uff0c\u671b\u7740\u4e01\u6d9b\u548c\u7518\u9716\u53eb\u9053\uff0c\u201c\u4e24\u4f4d\u5f53\u5bb6\u7684\uff0c\u54b1\u4eec\u597d \u4e0d\u5bb9\u6613\u8d81\u706b\u5173\u7184\u706d\u65f6\u5077\u6e21\u8fc7\u6765\uff0c\u8fd8\u4e0d\u5feb\u8dd1\uff0c\u4e0d\u7136\u88ab\u5979\u4eec\u8ffd\u4e0a\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u539f\u6765\u521a\u624d\u706b\u5173\u867d\u5df2\u7184\u706b\uff0c\u4f46\u5730\u4e0a\u5374\u5df2\u70e7\u5f97\u7126\u7ea2\uff0c\u6e29\u5ea6\u5e76\u672a\u7a0d\u9000\uff0c\u4e01\u6d9b\u548c\u7518\u9716\u56e0\u8f7b\u529f\u4e0d\u6d4e\uff0c \u5fc5\u987b\u53cc\u811a\u70b9\u5730\u5954\u8dd1\uff0c\u7adf\u628a\u53cc\u811a\u70eb\u574f\uff0c\u6839\u672c\u65e0\u6cd5\u518d\u8d70\u8def\u4e86\u3002 \u3000\u3000\u4e01\u6d9b\u60e8\u53eb\u7740\u628a\u4e00\u53ea\u811a\u5403\u529b\u7684\u4f38\u51fa\u6765\u9053\uff1a\u201c\u8881\u5c0f\u4fa0\uff0c\u4f60\u770b\u770b\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u540c\u65f6\u770b\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u4e01\u6d9b\u8fde\u978b\u5e95\u90fd\u70e4\u900f\u4e86\uff0c\u4f24\u70ed\u4e4b\u91cd\uff0c\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u5927\u4e3a\u7740\u6025\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u529e\uff1f\u5979\u4eec\u5fc5\u5b9a\u9a6c\u4e0a\u8ffd\u8fc7\u6765\uff0c\u90a3\u54b1\u4eec\u4e0d\u5c31\u767d\u6295\u673a\u3001\u767d\u5192\u9669\u4e86 \u4e48\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0c\u54b1\u4eec\u4e00\u4eba\u80cc\u7740\u4e00\u4e2a\u8dd1\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u53eb\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u6211\u4eec\u8fd9\u811a\uff0c\u82e5\u4e0d\u9a6c\u4e0a\u7597\u6cbb\uff0c\u53ea\u6015\u534a\u6708\u4e8c\u5341\u5929\u4e5f\u597d\u4e0d\u4e86\uff0c\u600e\u4e48\u80fd\u8ba9\u5cb3 \u516c\u5b50\u548c\u8881\u5c0f\u4fa0\u4e00\u76f4\u80cc\u7740\uff1f\u518d\u8bf4\u4fbf\u8fc7\u4e86\u8fd9\u4e00\u5173\uff0c\u4e0b\u4e00\u5173\u53c8\u600e\u4e48\u8fc7\u5462\uff1f\u201d \u3000\u3000\u8fd9\u5012\u771f\u8ba9\u5cb3\u5c0f\u98de\u5982\u8881\u5c0f\u9e64\u5927\u5927\u4e3a\u96be\u4e86\u3002 \u3000\u3000\u6b63\u5728\u614c\u6025\u800c\u53c8\u65e0\u8ba1\u53ef\u65bd\u65f6\uff0e\u90a3\u4e2d\u5e74\u5973\u5b50\u5df2\u5e26\u56db\u540d\u7ea2\u8863\u5c11\u5973\u8ddf\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4eba\u8fd9\u7b97\u4ec0\u4e48\u8fc7\u706b\u5173\uff1f\u201d \u3000\u3000\u5979\u5634\u91cc\u8bf4\u8bdd\uff0c\u4e24\u773c\u5374\u65e9\u671b\u5411\u5cb3\u5c0f\u98de\uff0c\u8138\u4e0a\u6ee1\u662f\u60ca\u5f02\u4e4b\u8272\u3002 \u3000\u3000\u90a3\u56db\u540d\u7ea2\u8863\u5c11\u5973\u4e5f\u90fd\u773c\u7741\u7741\u5168\u628a\u89c6\u7ebf\u96c6\u4e2d\u5728\u5cb3\u5c0f\u98de\u8138\u4e0a\uff0c\u4e00\u4e2a\u4e2a\u4e5f\u90fd\u60ca\u6115\u83ab\u540d\u3002 \u3000\u3000\u4e01\u6d9b\u8fd9\u65f6\u5df2\u4ec0\u4e48\u90fd\u4e0d\u5728\u4e4e\uff0c\u54a7\u7740\u6ee1\u53e3\u9ec4\u7259\u9053\uff1a\u201c\u7edf\u9886\uff0c\u4e0d\u7b97\u8fc7\u5173\u4e5f\u6ca1\u5173\u7cfb\uff0c\u6211\u4eec\u51b3\u5b9a\u56de \u53bb\uff0c\u53ea\u5e0c\u671b\u4f60\u80fd\u628a\u6211\u4eec\u62a4\u9001\u5230\u8c37\u53e3\u5c31\u6210\u4e86\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u8fc7\u4e86\u5173\uff0c\u4e3a\u4ec0\u4e48\u53c8\u8981\u56de\u53bb\uff1f\u524d\u529f\u5c3d\u5f03\uff0c\u90a3\u591a\u53ef\u60dc\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u53c8\u52c9\u5f3a\u62ac\u4e86\u62ac\u811a\u9053\uff1a\u201c\u4f60\u4e5f\u8bb8\u89c1\u8fc7\u70e4\u732a\u8e44\u70e4\u718a\u8e44\u8fd9\u9053\u83dc\uff0c\u53ef\u89c1\u8fc7\u70e4\u4eba\u811a\u7684\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u201c\u6251\u54e7\u201d\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u628a\u811a\u70e4\u574f\u4e86\uff0c\u8fd9\u53eb\u5077\u9e21\u4e0d\u7740\u8680\u628a\u7c73\uff0c\u7b97\u662f\u7ed9\u4e86\u4f60\u4e00\u6b21 \u6559\u8bad\uff01\u201d \u3000\u3000\u4e01\u6d9b\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u8fd9\u6b21\u6559\u8bad\u4e0d\u6253\u7d27\uff0c\u7adf\u628a\u6211\u4eec\u7684\u811a\u6559\u8bad\u6ca1\u6709\u4e86\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u8138\u8272\u4e00\u6c89\uff0c\u53f1\u9053\uff1a\u201c\u4f60\u4eec\u81ea\u5df1\u6295\u673a\u53d6\u5de7\uff0c\u5982\u4eca\u5403\u4e86\u4e8f\uff0c\u53cd\u6765\u6028\u6211\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4e0d\u6028\u4f60\u6028\u8c01\uff0c\u4f60\u5f53\u65f6\u4e3a\u4ec0\u4e48\u4e0d\u62e6\u7740\uff1f\u201d \u3000\u3000\u53ea\u542c\u6843\u82b1\u9053\uff1a\u201c\u6211\u4eec\u7edf\u9886\u5c31\u662f\u8981\u7ed9\u4f60\u4eec\u4e00\u70b9\u6559\u8bad\uff0c\u6240\u4ee5\u624d\u4e0d\u62e6\u4f60\u4eec\uff0c\u4f60\u60f3\u4e16\u4e0a\u54ea\u6709\u90a3\u4e48 \u4fa5\u5e78\u6210\u529f\u7684\u4e8b\uff0c\u4eba\u5bb6\u90a3\u4f4d\u516c\u5b50\uff0c\u624d\u662f\u771f\u6b63\u7684\u82f1\u96c4\uff01\u201d \u3000\u3000\u4e01\u6d9b\u4f3c\u4e4e\u800d\u4e0a\u4e86\u8d56\uff0c\u54ac\u7740\u7259\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u4ec0\u4e48\u90fd\u597d\uff0c\u6211\u4e0d\u7ba1\u5566\uff1f\u5373\u4fbf\u73b0\u5728\u6740\u4e86\u6211\uff0c\u4e5f\u6bd4 \u8fd9\u6837\u6d3b\u53d7\u7f6a\u597d\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u4e01\u5f53\u5bb6\u7684\uff0c\u5fcd\u7740\u70b9\uff0c\u7531\u5728\u4e0b\u6765\u548c\u5979\u4eec\u8bb2\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u9053\uff0c\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u597d\u8bb2\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u5148\u8bf7\u95ee\u7edf\u9886\uff0c\u6211\u4eec\u4e09\u4e2a\u7b97\u4e0d\u7b97\u8fc7\u4e86\u706b\u5173\uff1f\u201d \u3000\u3000\u201c\u4f60\u81ea\u5df1\u8bf4\u5462\uff1f\u201d \u3000\u3000\u201c\u4f60\u7684\u610f\u601d\u90a3\u662f\u4e0d\u7b97\u4e86\uff0c\u65e2\u7136\u4e0d\u7b97\uff0c\u6211\u4eec\u51b3\u5b9a\u56de\u53bb\uff0c\u4f60\u8be5\u6ca1\u8bdd\u8bb2\u4e86\u5427\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u8981\u56de\u53bb\uff0c\u5f53\u7136\u53ef\u4ee5\uff0c\u53ef\u662f\u73b0\u5728\u4ed6\u4eec\u4e24\u4eba\u5df2\u7ecf\u4e0d\u80fd\u884c\u52a8\u4e86\uff0c\u4f60\u4eec\u53c8\u600e\u6837\u56de\u53bb\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u53ef\u4ee5\u8d1f\u8d23\u80cc\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u4f60\u53ea\u80fd\u80cc\u4e00\u4e2a\uff0c\u53e6\u4e00\u4e2a\u5462\uff1f\u201d \u3000\u3000\u53ea\u542c\u5cb3\u5c0f\u98de\u6717\u58f0\u9053\uff1a\u201c\u53e6\u4e00\u4e2a\u6211\u80cc\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u4e00\u6123\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u8fc7\u4e86\u5173\uff0c\u53ea\u80fd\u524d\u8fdb\uff0c\u4e0d\u80fd\u540e\u9000\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u60c5\u613f\u653e\u5f03\u65e2\u5f97\u7684\u6743\u5229\uff0c\u4e0d\u518d\u524d\u8fdb\u4e86\uff0c\u966a\u7740\u4ed6\u4eec\u4e00\u8d77\u56de\u53bb\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u6709\u4e9b\u5403\u60ca\uff0c\u663e\u7136\u5979\u662f\u4e0d\u80fd\u5931\u53bb\u8fd9\u6837\u96be\u5f97\u4e00\u89c1\u7684\u4eba\u624d\uff0c \u3000\u3000\u6025\u6025\u53eb\u9053\uff1a\u201c\u4e0d\u53ef\u4ee5\uff1f\u4f60\u4e0d\u80fd\u56de\u53bb\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u7136\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u53ef\u4ee5\uff1f\u6211\u73b0\u5728\u8fd8\u662f\u81ea\u7531\u4e4b\u8eab\uff0c\u5e76\u4e0d\u53d7\u4efb\u4f55\u4eba\u7684\u63a7\u5236\uff0c\u4f60\u82e5\u975e \u62e6\u963b\u4e0d\u53ef\uff0c\u6211\u5c31\u51b3\u5b9a\u548c\u4f60\u62da\u4e00\u6b7b\u6218\uff0c\u7edf\u9886\uff0c\u54b1\u4eec\u73b0\u5728\u5c31\u53ef\u4ee5\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u4e0d\u89c9\u8138\u8272\u5927\u53d8\uff0c\u5979\u4e07\u6ca1\u6599\u5230\u8fd9\u5c11\u5e74\u7adf\u662f\u5982\u6b64\u521a\u70c8\u3002\u5979\u5df2\u542c\u6b66\u5173\u7edf\u9886\u9a7c\u80cc\u8001\u4eba\u6d3e \u4eba\u8bf4\u8fc7\u5cb3\u5c0f\u98de\u7684\u6b66\u529f\uff0c\u65b9\u624d\u53c8\u4eb2\u773c\u770b\u5230\u4ed6\u901a\u8fc7\u706b\u5173\uff0c\u81ea\u5fd6\u8fde\u81ea\u5df1\u4e5f\u672a\u5fc5\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u4f46\u8eab\u4e3a \u706b\u5173\u7edf\u9886\uff0c\u5374\u53c8\u4e0d\u80fd\u683d\u5728\u4e00\u4e2a\u5c0f\u5b69\u5b50\u624b\u91cc\u3002 \u3000\u3000\u53e6\u5916\uff0c\u5979\u4e5f\u7edd\u5bf9\u4e0d\u80fd\u8ba9\u5cb3\u5c0f\u98de\u56de\u53bb\uff0c\u56e0\u4e3a\u5949\u547d\u628a\u5b88\u706b\u5173\uff0c\u5176\u4efb\u52a1\u5c31\u662f\u4e3a\u4e0a\u7ea7\u5438\u6536\u4eba\u624d\u7684\uff0c \u5982\u4eca\u9047\u89c1\u8fd9\u6837\u96be\u5f97\u4e00\u89c1\u7684\u5c11\u5e74\u5947\u624d\uff0c \u3000\u3000\u82e5\u8ba9\u4ed6\u6765\u800c\u590d\u53bb\uff0c\u5c82\u4e0d\u6709\u8d1f\u4e0a\u7ea7\u6258\u4ed8\uff1f\u82e5\u4e00\u65e6\u88ab\u4e0a\u7ea7\u67e5\u77e5\uff0c\u8fd9\u9879\u653e\u8d70\u4eba\u624d\u7684\u5927\u7f6a\uff0c\u5979\u5c31 \u627f\u53d7\u4e0d\u8d77\u3002 \u3000\u3000\u539f\u6765\u8fd9\u8c37\u9053\u7684\u6bcf\u4e00\u5173\uff1f\u90fd\u5fc5\u987b\u4fdd\u6301\u6a2a\u7684\u548c\u7eb5\u7684\u8054\u7cfb\u3002 \u3000\u3000\u6a2a\u7684\u8054\u7cfb\u662f\u524d\u4e00\u5173\u5fc5\u987b\u628a\u8fc7\u5173\u8005\u7684\u60c5\u5f62\u8fc5\u901f\u901a\u77e5\u540e\u9762\u4e00\u5173\u3002 \u3000\u3000\u7eb5\u7684\u8054\u7cfb\u662f\u6bcf\u5173\u90fd\u5fc5\u987b\u628a\u8fc7\u5173\u8005\u4e2d\u7684\u7a81\u51fa\u4eba\u624d\u76f4\u63a5\u62a5\u544a\u603b\u575b\u3002 \u3000\u3000\u5c31\u4ee5\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u6765\u8bf4\uff1f\u6587\u5173\u548c\u6b66\u5173\u90fd\u5df2\u76f4\u63a5\u62a5\u5230\u4e86\u603b\u575b\uff0c \u3000\u3000\u8fd9\u706b\u5173\u7edf\u9886\u7684\u4e2d\u5e74\u5973\u5b50\u53c8\u600e\u6562\u8ba9\u4ed6\u4eec\u65e0\u7f18\u65e0\u6545\u7684\u56de\u53bb\uff0c\u9664\u975e\u4ed6\u4eec\u8fc7\u4e0d\u4e86\u5173\u3002 \u3000\u3000\u4f46\u5979\u8eab\u4e3a\u4e00\u5173\u7edf\u9886\uff0c\u5374\u53c8\u4e0d\u80fd\u5728\u4e00\u4e2a\u5c0f\u5b69\u5b50\u9762\u524d\u793a\u602f\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\u706b\u5173\u53ea\u8d1f\u8d23\u6d4b\u8bd5\u6765\u4eba\u662f\u5426\u80fd\u8fc7\u5173\uff1f\u5e76\u65e0\u548c\u6765\u4eba\u52a8\u624b\u8fc7\u62db\u62da\u6597\u7684\u4efb\u52a1\uff0c\u56e0\u4e4b\uff0c\u706b\u5173 \u91cc\u9762\uff0c\u4e5f\u5c31\u7528\u4e0d\u7740\u5b89\u6392\u4ec0\u4e48\u7edd\u9876\u9ad8\u624b\uff0c \u3000\u3000\u6b64\u523b\u5cb3\u5c0f\u98de\u5c45\u7136\u5f00\u53e3\u6311\u6218\uff0c\u8fd9\u5728\u4e2d\u5e74\u5973\u5b50\u6765\u8bf4\uff0c\u5949\u547d\u628a\u5b88\u706b\u5173\u597d\u51e0\u5e74\uff0c\u8fd8\u662f\u7834\u5929\u8352\u7b2c\u4e00 \u6b21\u78b0\u4e0a\u3002 \u3000\u3000\u5979\u6781\u529b\u4fdd\u6301\u9547\u5b9a\uff0c\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u672a\u514d\u592a\u5927\u80c6\u4e86\uff0c\u5c45\u7136\u6562\u5728\u6211\u8fd9\u706b\u5173\u4e0a\u516c\u7136 \u6311\u6218\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u90a3\u53ea\u80fd\u602a\u4f60\u6b3a\u4eba\u592a\u751a\uff0c\u6211\u4eec\u56db\u4e2a\u4eba\u5df2\u7ecf\u7ea6\u597d\u540c\u8fdb\u540c\u9000\uff0c\u5982\u4eca\u6709\u4e24\u4eba\u53cc\u811a\u70e7 \u6210\u91cd\u4f24\uff0c\u6211\u8981\u80cc\u4ed6\u51fa\u53bb\uff0c\u4f60\u53c8\u4e0d\u7b54\u5e94\uff0c\u6839\u672c\u4e0d\u901a\u534a\u70b9\u4eba\u60c5\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u53ea\u6709\u51b3\u5b9a\u548c\u4f60\u62da \u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u4e00\u5b9a\u53ef\u4ee5\u80dc\u5f97\u4e86\u6211\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4e89\u4e00\u53e3\u6c14\uff0c\u4e3a\u5bf9\u670b\u53cb\u5c3d\u4e49\uff1f\u5c31\u662f\u8f93\u7ed9\u4f60\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u5728\u4e0b\u5b81\u53ef\u8ba9\u4eba\u6253\u6b7b\uff0c\u51b3\u4e0d\u88ab \u4eba\u5413\u6b7b\uff01\u201d \u3000\u3000\u8fd9\u4e00\u6765\u4e2d\u5e74\u5973\u5b50\u8fd8\u771f\u96be\u4ee5\u4e0b\u53f0\uff0c\u53ea\u597d\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u8981\u5236\u670d\u7528\u4e0d\u7740\u672c\u7edf\u9886\u4eb2\u81ea\u52a8\u624b\uff0c\u4e0d \u59a8\u7531\u6211\u8fd9\u56db\u540d\u624b\u4e0b\u548c\u4f60\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u671b\u4e86\u56db\u540d\u7ea2\u8863\u5c11\u5973\u4e00\u773c\u9053\uff1a\u201c\u5979\u4eec\u56db\u4f4d\u662f\u4e00\u4e2a\u4e00\u4e2a\u7684\u6765\uff1f\u8fd8\u662f\u4e00\u9f50\u4e0a\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u9053\uff1a\u201c\u6211\u65e9\u77e5\u4f60\u7684\u6b66\u529f\u9ad8\u5f3a\uff0c\u5979\u4eec\u65e0\u6cd5\u548c\u4f60\u5355\u6253\u72ec\u6597\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\u5979\u4eec\u4e00\u9f50\u4e0a\u4e86\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u968f\u5373\u89e3\u4e0b\u957f\u5251\uff0c\u4ea4\u7ed9\u4e86\u8881\u5c0f\u9e64\uff0c\u56de\u8eab\u9053\uff1a\u201c\u5979\u4eec\u56db\u4f4d\u59d1\u5a18\u90fd\u672a\u643a\u5e26\u5175\u5203\uff0c\u6211\u82e5\u7528 \u5251\uff0c\u5c31\u4e0d\u516c\u5e73\u4e86\uff0c\u56db\u4f4d\u59d1\u5a18\uff0c\u4f60\u4eec\u4e00\u9f50\u4e0a\u5427\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e0d\u613f\u628a\u4e8b\u60c5\u95f9\u5927\uff0c\u6025\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u4f7f\u4e0d\u5f97\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u7b11\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4f7f\u4e0d\u5f97\u7684\uff1f\u5979\u60f3\u773c\u770b\u7740\u4e01\u3001\u7518\u4e24\u4f4d\u5f53\u5bb6\u7684\u6b7b\u5728\u8fd9\u91cc\uff0c\u62da\u4e0a\u4e00\u62da\uff0c \u4e5f\u8bb8\u8fd8\u6709\u751f\u8def\uff0c\u5c0f\u5f1f\u8fd9\u662f\u8feb\u4e0d\u5f97\u5df2\uff0c\u8881\u5927\u54e5\u7528\u4e0d\u7740\u62e6\u963b\uff0c\u5426\u5219\u54b1\u4eec\u90fd\u662f\u6b7b\u8def\u4e00\u6761\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u89c1\u5cb3\u5c0f\u98de\u8bf4\u5f97\u7406\u76f4\u6c14\u58ee\uff0c\u53c8\u89c1\u4e01\u6d9b\u548c\u7518\u9716\u4f24\u70ed\u4e25\u91cd\uff0c\u89c9\u5f97\u5c0f\u98de\u8fd9\u6837\u505a\uff0c\u5e76\u975e\u6ca1\u6709 \u9053\u7406\uff0c\u4e5f\u5c31\u4e0d\u518d\u542d\u58f0\u3002 \u3000\u3000\u90a3\u56db\u540d\u7ea2\u8863\u5c11\u5973\u867d\u65e9\u77e5\u5cb3\u5c0f\u98de\u6b66\u529f\u9ad8\u7edd\uff0c\u4f46\u89c1\u4ed6\u4e0d\u8fc7\u662f\u4e2a\u5341\u4e09\u56db\u5c81\u7684\u5b69\u5b50\uff0c\u53c8\u52a0\u4e0a\u81ea\u5df1 \u65b9\u9762\u4eba\u591a\uff0c\u4e5f\u5c31\u6ca1\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u4e0d\u8fc7\u5979\u4eec\u4e5f\u4e0d\u60f3\u4f24\u5bb3\u5cb3\u5c0f\u98de\uff0c\u56e0\u4e3a\u8fd9\u5e74\u8f7b\u4eba\u7f8e\u5982\u51a0\u7389\uff0c\u79c0\u62d4\u82f1\u633a\uff0c\u5979\u4eec\u7231\u6155\u8fd8\u6765\u4e0d\u53ca\uff0c \u53c8\u600e\u80af\u4f24\u4ed6\uff1f \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u65e9\u62ff\u5b9a\u4e3b\u610f\uff0c\u56db\u540d\u7ea2\u8863\u5c11\u5973\u4e0e\u81ea\u5df1\u65e0\u6028\u65e0\u4ec7\uff0c\u53c8\u4e00\u4e2a\u4e2a\u5a07\u6ef4\u6ef4\u7684\u4eba\u89c1\u4eba\u601c\uff0e\u82e5 \u8ba9\u5979\u4eec\u8840\u6ca5\u5f53\u573a\uff0c\u4e0d\u4f46\u8fc7\u610f\u4e0d\u53bb\uff0c\u4e5f\u672a\u514d\u592a\u715e\u98ce\u666f\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u4ed6\u8d70\u51fa\u51e0\u6b65\uff0c\u8fdc\u79bb\u8881\u5c0f\u9e64\u7b49\u4eba\uff0c\u4e00\u9762\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u52a8\u624b\uff1f\u201d \u3000\u3000\u56db\u540d\u5c11\u5973\u970e\u65f6\u98de\u8eab\u6563\u5f00\uff0c\u5404\u53d6\u65b9\u4f4d\uff0c\u628a\u5cb3\u5c0f\u98de\u56f4\u5728\u6838\u5fc3\uff1f \u3000\u3000\u5cb3\u5c0f\u98de\u53eb\u9053\uff1a\u201c\u5feb\u4e0a\uff01\u201d \u3000\u3000\u56db\u540d\u5c11\u5973\u7acb\u5373\u4e00\u62e5\u653b\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u613f\u4f24\u5979\u4eec\uff0c\u53ea\u597d\u4e00\u5f0f\u201c\u65f1\u5730\u62d4\u8471\u201d\uff0c\u539f\u5730\u76f4\u8dc3\u8d77\u4e24\u4e08\u591a\u9ad8\u3002 \u3000\u3000\u56db\u540d\u5c11\u5973\u6ca1\u6599\u5230\u653b\u81f3\u8fd1\u524d\uff0c\u5df2\u5931\u53bb\u76ee\u6807\uff0c\u82e5\u975e\u6536\u52bf\u5f97\u5feb\uff0c\u9669\u4e9b\u649e\u6210\u4e00\u56e2\u3002 \u3000\u3000\u5f85\u5979\u4eec\u5faa\u58f0\u671b\u53bb\uff0c\u5cb3\u5c0f\u98de\u65e9\u5df2\u7ad9\u5728\u4e24\u4e08\u5916\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u547c\u5578\u4e00\u58f0\uff0c\u53c8\u662f\u4e00\u62e5\u800c\u4e0a\u3002 \u3000\u3000\u8fd9\u6b21\u5cb3\u5c0f\u98de\u7ec8\u4e8e\u63a5\u4e86\u62db\u3002 \u3000\u3000\u4f46\u4ed6\u63a5\u7684\u5374\u662f\u865a\u62db\uff0c\u662f\u4ee5\u9694\u7a7a\u6253\u7a74\u624b\u6cd5\u628a\u4e00\u540d\u5c11\u5973\u70b9\u5728\u5f53\u5730\u4e0d\u52a8\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u552f\u6709\u8fd9\u6837\u624d\u80fd\u4e0d\u4f7f\u5bf9\u65b9\u53d7\u4f24\u3002 \u3000\u3000\u4ed6\u7684\u70b9\u7a74\u624b\u6cd5\u5341\u5206\u602a\u5f02\uff0c\u7adf\u80fd\u628a\u4eba\u70b9\u5f97\u7ad9\u5728\u539f\u5730\u7acb\u5373\u4e0d\u52a8\uff1f\u800c\u90a3\u88ab\u70b9\u5c11\u5973\u7684\u59ff\u6001\u53c8\u5341\u5206 \u7f8e\u5999\uff0c\u4e00\u811a\u5728\u524d\uff0c\u4e00\u811a\u5728\u540e\uff0c\u53cc\u624b\u4e94\u6307\u5fae\u66f2\uff0c\u72b6\u82e5\u5170\u82b1\uff0c\u4f3c\u8981\u5411\u524d\u6251\u51fb\u7684\u6a21\u6837\u3002 \u3000\u3000\u53e6\u4e09\u540d\u5c11\u5973\u4e00\u89c1\u8fd9\u60c5\u5f62\uff0c\u4e2a\u4e2a\u9707\u9a87\u4e0d\u5df2\uff0c\u4f46\u5979\u4eec\u8fd8\u662f\u8dc3\u9000\u8fc7\u540e\uff0c\u518d\u5ea6\u6251\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u6b21\u5cb3\u5c0f\u98de\u53cc\u624b\u9f50\u51fa\uff0c\u51e0\u4e4e\u5728\u540c\u4e00\u65f6\u95f4\uff0c\u53c8\u70b9\u4e2d\u4e86\u4e24\u4e2a\u3002 \u3000\u3000\u6843\u82b1\u603b\u7b97\u8eab\u624b\u8f83\u5feb\uff0c\u4e0d\u66fe\u88ab\u70b9\u4e2d\uff0c\u4f46\u5979\u5374\u5df2\u4e0d\u6562\u518d\u6218\uff0c\u8fc5\u5feb\u7684\u9000\u5230\u4e2d\u5e74\u5973\u5b50\u8eab\u8fb9\u3002 \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u9762\u5b54\u80c0\u5f97\u7eef\u7ea2\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u679c\u7136\u662f\u4f4d\u96be\u5f97\u4e00\u89c1\u7684\u5c11\u5e74\u82f1\u96c4\u4eba\u7269\uff0c\u8fde\u6211\u4e5f\u7b97\u5f00 \u4e86\u773c\u754c\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5c31\u50cf\u65b9\u624d\u6ca1\u505a\u8fc7\u4ec0\u4e48\u4e8b\u7684\u4e00\u822c\uff0c\u5411\u524d\u8d70\u4e86\u51e0\u6b65\u9053\uff1a\u201c\u73b0\u5728\u8f6e\u5230\u4f60\u8fd9\u4f4d\u7edf\u9886\u4e86\uff0c\u7528 \u4e0d\u7740\u5ba2\u6c14\uff0c\u5728\u4e0b\u606d\u5019\u8d50\u62db\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u957f\u957f\u5401\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u4eec\u4e0d\u5fc5\u56de\u53bb\u4e86\uff0c\u3000\u4e00\u4eba\u80cc\u4e00\u4e2a\uff1f\u5411\u524d\u8d70\u5427\uff01\u201d \u3000\u3000\u8c01\u77e5\u5cb3\u5c0f\u98de\u5374\u5f97\u7406\u4e0d\u8ba9\u4eba\uff0c\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u73b0\u5728\u4f60\u8ba9\u6211\u4eec\u5f80\u524d\u8d70\uff0c\u6211\u4eec\u4e5f\u4e0d\u51c6\u5907\u5411\u524d\u8d70 \u4e86\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u51c6\u5907\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5411\u540e\u8d70\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e24\u4f4d\u7684\u811a\uff0c\u5fc5\u987b\u9a6c\u4e0a\u533b\u6cbb\uff0e\u5426\u5219\u5c31\u4f1a\u70c2\u6389\uff0c\u5f80\u524d\u8d70\u8c01\u7ed9\u4ed6\u4eec\u533b\u811a\uff1f\u201d \u3000\u3000\u201c\u83ab\u975e\u5f80\u540e\u8d70\u5c31\u6709\u4eba\u66ff\u4ed6\u4eec\u533b\uff1f\u201d \u3000\u3000\u201c\u81f3\u5c11\u5230\u4e86\u957f\u5b89\u4ee5\u540e\uff0c\u53ef\u4ee5\u627e\u5230\u540d\u533b\uff0c\u96be\u9053\u8981\u773c\u770b\u4ed6\u4eec\u628a\u4e24\u811a\u70c2\u6389\uff1f\u201d \u3000\u3000\u539f\u6765\u5cb3\u5c0f\u98de\u8fd9\u6837\u505a\u662f\u53e6\u6709\u7528\u5fc3\uff0c\u4ed6\u9884\u6599\u4e2d\u5e74\u5973\u5b50\u8fd9\u706b\u5173\u4e0a\u5fc5\u5b9a\u5907\u6709\u7597\u4f24\u836f\u7269\uff0c\u56e0\u4e3a\u50cf\u4e01 \u6d9b\u548c\u7518\u9716\u7684\u8fd9\u79cd\u60c5\u5f62\uff0c\u4ee5\u5f80\u53ea\u6015\u4e0d\u5728\u5c11\u6570\u3002 \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u679c\u7136\u88ab\u4ed6\u6fc0\u4e2d\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u96be\u9053\u9664\u4e86\u957f\u5b89\uff0c\u522b\u7684\u5730\u65b9\u5c31\u6ca1\u6709\u533b\u811a\u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6697\u559c\u9053\uff1a\u201c\u82e5\u9644\u8fd1\u5c31\u6709\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u9053\uff1a\u201c\u4f60\u5148\u628a\u5979\u4eec\u4e09\u4e2a\u7684\u7a74\u9053\u89e3\u5f00\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4f9d\u8a00\u89e3\u5f00\u4e86\u4e09\u540d\u7ea2\u8863\u5c11\u5973\u7684\u7a74\u9053\u3002 \u3000\u3000\u4e2d\u5e74\u5973\u5b50\u5429\u5490\u6843\u82b1\u9053\uff1a\u201c\u628a\u4ed6\u4eec\u56db\u4eba\u5e26\u5230\u7597\u4f24\u6d1e\u53bb\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u968f\u5373\u4e00\u4eba\u6276\u7740\u4e01\u6d9b\uff0c\u4e00\u4eba\u80cc\u7740\u7518\u9716\u968f\u6843\u82b1\u8fdb\u5165\u8c37\u58c1\u4e00\u5904\u6d1e\u7a74\u3002 \u3000\u3000\u8fd9\u5904\u6d1e\u7a74\u53ef\u8bf4\u662f\u7eaf\u7cb9\u4e00\u4e2a\u539f\u91ce\u5c71\u6d1e\uff0c\u91cc\u9762\u4e1d\u6beb\u6ca1\u6709\u534a\u70b9\u9648\u8bbe\uff0c \u3000\u3000\u53ea\u662f\u51e0\u5757\u4f9b\u4eba\u5750\u7528\u7684\u77f3\u5934\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u832b\u7136\u95ee\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u8fd9\u91cc\u5c31\u53eb\u7597\u4f24\u6d1e\u4e48\uff1f\u201d \u3000\u3000\u6843\u82b1\u9053\uff0c\u201c\u4e0d\u9519\uff0c\u8fd9\u5c31\u662f\u7597\u4f24\u6d1e\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u56db\u987e\u4e00\u773c\u9053\uff1b\u201c\u53ef\u662f\u4e00\u70b9\u836f\u7269\u90fd\u6ca1\u6709\uff0c\u62ff\u4ec0\u4e48\u7597\u4f24\uff1f\u201d \u3000\u3000\u6843\u82b1\u9053\uff1a\u201c\u836f\u7269\u6b63\u5728\u8c03\u5236\uff0c\u9a6c\u4e0a\u5c31\u5230\u4e86\u3002\u201d \u3000\u3000\u5927\u7ea6\u987f\u996d\u5de5\u592b\u540e\uff0c\u679c\u7136\u6709\u4e24\u540d\u5c11\u5973\uff0c\u5404\u7aef\u7740\u4e00\u4e2a\u6728\u76c6\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u540d\u5c11\u5973\u867d\u7136\u4e5f\u662f\u7ea2\u8863\u7ea2\u88d9\uff0c\u4f46\u5374\u4e0d\u662f\u5148\u524d\u6240\u89c1\u90a3\u4e09\u4f4d\u5c11\u5973\u5f53\u4e2d\u7684\uff0c\u7167\u6837\u4e5f\u6731\u5507\u7693\u9f7f\uff0c \u7ef0\u7ea6\u591a\u59ff\u3002 \u3000\u3000\u6843\u82b1\u6307\u7740\u4e01\u6d9b\u548c\u7518\u9716\u9053\uff1b\u201c\u5c31\u662f\u4ed6\u4eec\u4e24\u4f4d\uff01\u201d \u3000\u3000\u4e24\u540d\u5c11\u5973\u5404\u81ea\u628a\u4e24\u4e2a\u76c6\u653e\u5728\u4e01\u6d9b\u548c\u7518\u9716\u9762\u524d\u3002 \u3000\u3000\u5230\u8fd9\u65f6\u4f17\u4eba\u624d\u770b\u6e05\u6728\u76c6\u91cc\u662f\u70ed\u6c14\u817e\u817e\u7684\u6eda\u6c34\uff0c\u5947\u602a\u7684\u662f\u6c34\u8272\u6697\u7ea2\uff0c\u5f88\u50cf\u4e24\u76c6\u732a\u8840\u3002 \u3000\u3000\u8fd9\u65f6\u4e01\u6d9b\u548c\u7518\u9716\u4ecd\u5728\u54ac\u7259\u54a7\u5634\u7684\u8fde\u54fc\uff0c\u53ef\u89c1\u75db\u5f97\u4e0d\u8f7b\u3002 \u3000\u3000\u90a3\u4e3a\u4e01\u6d9b\u7aef\u76c6\u7684\u5c11\u5973\u8e72\u4e86\u4e0b\u6765\uff0c\u4ef0\u8138\u671b\u7740\u4e01\u6d9b\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u53d4\uff0c\u8bf7\u628a\u811a\u653e\u5230\u76c6\u91cc\u6765\uff01\u201d \u3000\u3000\u4e01\u6d9b\u4e24\u773c\u6709\u4e9b\u53d1\u76f4\u9053\uff1a\u201c\u76c6\u91cc\u662f\u6eda\u6c34\uff0c\u90a3\u4e0d\u8d8a\u70eb\u574f\u4e86\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1b\u201c\u4f60\u653e\u5fc3\uff0c\u4e0d\u5403\u70b9\u82e6\uff0c\u600e\u628a\u811a\u4f24\u6cbb\u597d\uff1f \u3000\u3000\u4e01\u6d9b\u53cc\u624b\u8fde\u6446\u9053\uff1a\u201c\u5f00\u73a9\u7b11\uff0c\u518d\u70eb\u90a3\u5c31\u8d8a\u53d1\u4e0d\u597d\u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u76b1\u7709\u95ee\u9053\uff1b\u201c\u59d1\u5a18\uff0c\u6c34\u8272\u6697\u7ea2\uff0c\u91cc\u9762\u7a76\u7adf\u653e\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u90a3\u836f\u7269\u662f\u4ec0\u4e48\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u53ea\u77e5\u9053\u7528\u8fd9\u79cd\u836f\u6c34\u7597\u4f24\uff0c\u4e00\u6cbb\u5c31\u597d\uff0c\u795e\u6548\u65e0 \u6bd4\uff0c\u4ec5\u4ec5\u5728\u6211\u624b\u91cc\uff0c\u4fbf\u5df2\u6cbb\u597d\u4e86\u51e0\u5341\u4f4d\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u53ef\u662f\u73b0\u5728\u6c34\u8fd8\u6eda\u70eb\uff0c\u600e\u53ef\u8ba9\u4ed6\u628a\u811a\u653e\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u7b49\u5230\u6c34\u4e00\u51c9\u5c31\u4e0d\u7075\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\u518d\u4ef0\u8138\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u53d4\uff0c\u4f60\u5fcd\u7740\u70b9\u513f\uff0c\u75db\u4e5f\u4e0d\u8fc7\u662f\u75db\u4e00\u4e0b\u5b50\uff0c\u4f60\u4e00\u5b9a\u53d7\u5f97\u4e86\u3002\u201d \u3000\u3000\u4e01\u6d9b\u6b63\u5728\u72b9\u8c6b\uff0c\u7a81\u89c1\u90a3\u5c11\u5973\u7693\u8155\u4e00\u63a2\uff0c\u5df2\u6293\u4f4f\u4e01\u6d9b\u5de6\u811a\u811a\u8e1d\uff0c\u63a5\u7740\u4fbf\u5411\u6728\u76c6\u91cc\u6309\u53bb \u3000\u3000\u4e01\u6d9b\u5148\u662f\u60e8\u53eb\u4e00\u58f0\uff0c\u4f46\u4e0d\u4e45\u4e4b\u540e\uff0c\u4e0d\u4f46\u4e0d\u518d\u558a\u53eb\uff0c\u53cd\u800c\u6709\u4e50\u4e0d\u53ef\u652f\u7684\u8868\u60c5\uff0c\u81ea\u52a8\u628a\u53e6\u4e00 \u53ea\u811a\u4e5f\u4f38\u8fdb\u4e86\u6728\u76c6\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4ec5\u662f\u75db\u4e86\u4e00\u4e0b\u4fbf\u4e0d\u518d\u75db\uff0c\u540c\u65f6\u90a3\u5c11\u5973\u7684\u4e00\u53cc\u7ea4\u7ea4\u7389\u624b\u5728\u4e3a\u4ed6\u4e0d\u4f4f\u63c9\u6413\u629a\u6478\uff0c\u53cd\u800c \u89c9\u5f97\u8212\u7545\u65e0\u6bd4\u3002 \u3000\u3000\u4ed6\u957f\u4e86\u8fd9\u4e48\u5927\uff0c\u51e0\u66fe\u6709\u8fc7\u5a07\u6ef4\u6ef4\u7684\u5c11\u5973\u4e3a\u4ed6\u6d17\u811a\uff0c\u800c\u4e14\u8fd8\u8fde\u6478\u5e26\u63c9\u7684\uff0c\u522b\u8bf4\u73b0\u5728\u5df2\u7ecf\u4e0d \u75db\uff0c\u5373\u4fbf\u75db\u70b9\u4e5f\u662f\u8212\u670d\u7684\u3002 \u3000\u3000\u53e6\u4e00\u65c1\u7684\u7518\u9716\uff0c\u89c1\u4e86\u8fd9\u60c5\u5f62\uff0c\u4e5f\u81ea\u52a8\u628a\u4e24\u811a\u4f38\u8fdb\u6728\u76c6\u3002 \u3000\u3000\u8db3\u8db3\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u4e24\u540d\u5c11\u5973\u624d\u628a\u6728\u76c6\u79fb\u5f00\uff0c\u7136\u540e\u518d\u7528\u5e72\u5e03\u4e3a\u4ed6\u4eec\u64e6\u811a\u3002\u4e00\u9762\u53c8\u95ee\u7518\u9716 \u548c\u4e01\u6d9b\u75db\u4e0d\u75db\uff1f\u201d \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u4e24\u4eba\u7684\u811a\uff0c\u8fd8\u771f\u4e0d\u75db\u4e86\uff0c\u90a3\u70e7\u7834\u7684\u76ae\uff0c\u4e5f\u6709\u9010\u6e10\u75ca\u5408\u7684\u6a21\u6837\u3002 \u3000\u3000\u4e24\u540d\u5c11\u5973\u8d70\u540e\uff0c\u6843\u82b1\u9053\uff1a\u201c\u4e24\u4f4d\u7684\u811a\uff0c\u5fc5\u987b\u4f11\u606f\u4e00\u591c\uff0c\u624d\u80fd\u5b8c\u5168\u590d\u539f\uff0c\u82e5\u73b0\u5728\uff0c\u9a6c\u4e0a\u884c \u52a8\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u6e83\u70c2\u3002\u201d \u3000\u3000\u4e01\u6d9b\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u90a3\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u6843\u82b1\u9053\uff1a\u201c\u6211\u53bb\u5411\u7edf\u9886\u8bf7\u793a\u4e00\u4e0b\uff0c\u6700\u597d\u628a\u56db\u4f4d\u7559\u5728\u8fd9\u91cc\u5bbf\u4e00\u665a\u518d\u8d70\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u89c1\u6b64\u523b\u5929\u8272\u5df2\u7ecf\u5c06\u665a\uff0c\u4e0b\u4e00\u5173\u53c8\u4e0d\u77e5\u662f\u4ec0\u4e48\u5173\uff0c\u5f53\u7136\u4e5f\u5e0c\u671b\u4f11\u606f\u4e00\u665a\uff0c \u7b49\u7518\u9716\u548c\u4e86\u6d9b\u7684\u811a\u75ca\u6108\u4e86\u518d\u8d70\u3002 \u3000\u3000\u6843\u82b1\u53bb\u540e\u4e0d\u4e45\uff0c\u4fbf\u9762\u5e26\u7b11\u5bb9\u7684\u56de\u6765\u4e86\u3002 \u3000\u3000\u4e01\u6d9b\u62a2\u7740\u95ee\u9053\uff1a\u201c\u7edf\u9886\u7b54\u5e94\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6843\u82b1\u9053\uff1a\u201c\u5979\u8001\u4eba\u5bb6\u7b54\u5e94\u4e86\uff0c\u56db\u4f4d\u8bf7\u5230\u53e6\u5916\u4e00\u4e2a\u6d1e\u91cc\u53bb\uff0c\u5403\u8fc7\u665a\u996d\uff0c\u4fbf\u53ef\u4ee5\u63d0\u524d\u4f11\u606f\u3002\u201d \u3000\u3000\u56db\u4eba\u968f\u7740\u6843\u82b1\uff0c\u53c8\u6765\u5230\u53e6\u5916\u4e00\u6d1e\u3002 \u3000\u3000\u8fd9\u6d1e\u662f\u7ecf\u8fc7\u8def\u6574\u7684\uff0c\u91cc\u9762\u6709\u4e0d\u5c11\u684c\u6905\uff0c\u5f88\u50cf\u4e00\u5904\u9910\u5385\u3002\u6d1e\u58c1\u4e0a\u53c8\u6709\u56db\u4e2a\u6d1e\u95e8\uff0c\u6843\u82b1\u6307\u7740 \u90a3\u4e9b\u6d1e\u95e8\u9053\uff1a\u201c\u56db\u4f4d\u665a\u4e0a\u4f11\u606f\u65f6\uff0c\u5c31\u8bf7\u4e00\u4eba\u4e00\u6d1e\uff0c\u91cc\u9762\u6709\u73b0\u6210\u7684\u5e8a\u69bb\u88ab\u8925\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u6211\u4eec\u5171\u4f4f\u4e00\u5ba4\u6216\u8005\u4e24\u4eba\u5408\u4f4f\u4e00\u6d1e\uff1f\u201d \u3000\u3000\u6843\u82b1\u9053\uff1a\u201c\u56e0\u4e3a\u90a3\u6d1e\u91cc\u8303\u56f4\u5f88\u5c0f\uff0c\u53ea\u80fd\u6446\u4e0b\u4e00\u5f20\u5e8a\uff0c\u4e00\u4eba\u4f4f\u4e00\u6d1e\uff0c\u4e0d\u662f\u5f88\u8212\u670d\u4e48\uff1f\u201d \u3000\u3000\u53c8\u8c08\u4e86\u4e00\u9635\uff0c\u5df2\u6709\u4e24\u540d\u5c11\u5973\u9001\u6765\u665a\u9910\u3002 \u3000\u3000\u996d\u540e\uff0c\u56e0\u4e3a\u5404\u4eba\u90fd\u5f88\u7d2f\uff0c\u4e01\u6d9b\u548c\u7518\u9716\u53c8\u5fc5\u987b\u4e0a\u5e8a\u517b\u4f24\uff0c\u56db\u4eba\u4fbf\u5404\u81ea\u56de\u5230\u81ea\u5df1\u7684\u6d1e\u91cc\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8fdb\u5165\u81ea\u5df1\u7684\u5bdd\u5904\uff0c\u624d\u53d1\u89c9\u8fd9\u6d1e\u7684\u7a74\u9053\u5f88\u6df1\u3002 \u3000\u3000\u4ed6\u8fdb\u5165\u65f6\u91cc\u5c71\uff0c\u5df2\u7ecf\u70b9\u4e0a\u706f\uff0c\u7167\u89c1\u679c\u7136\u6709\u4e00\u5f20\u5e8a\u69bb\uff0c\u88ab\u8925\u867d\u5df2\u9648\u65e7\uff0c\u4f46\u5374\u6d17\u5f97\u5f88\u5e72\u51c0\u3002 \u3000\u3000\u4e0a\u5e8a\u4e4b\u540e\uff0c\u5439\u7184\u706f\uff0c\u5374\u53c8\u8001\u662f\u7761\u4e0d\u7740\u3002 \u3000\u3000\u4f30\u8ba1\u7740\u81ea\u5df1\u4e00\u66f4\u4ee5\u540e\uff0c\u624d\u6709\u4e86\u6726\u80e7\u7761\u610f\u3002 \u3000\u3000\u8c01\u77e5\u521a\u4e00\u5408\u773c\uff0c\u7a74\u9053\u7684\u53e6\u4e00\u7aef\uff0c\u5374\u4f20\u6765\u811a\u6b65\u58f0\uff0c\u800c\u4e14\u6b63\u662f\u5411\u7740\u4ed6\u7684\u5bdd\u5904\u8d70\u6765\u3002 \u3000\u3000\u4ed6\u77e5\u9053\uff0c\u9664\u4e86\u8881\u5c0f\u9e64\uff0c\u4e0d\u4f1a\u6709\u7b2c\u4e8c\u4e2a\u4eba\u3002 \u3000\u3000\u76f4\u5230\u811a\u6b65\u58f0\u6765\u5230\u5e8a\u524d\uff0c\u4ed6\u624d\u51fa\u58f0\u95ee\u9053\uff1b\u201c\u8881\u5927\u54e5\uff0c\u6709\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u4f46\u5e94\u58f0\u7684\u5374\u662f\u4e2a\u5a07\u6ef4\u6ef4\u7684\u58f0\u97f3\uff1a\u201c\u4e3a\u4ec0\u4e48\u8fde\u706f\u4e5f\u7184\u4e86\uff1f\u201d \u3000\u3000\u539f\u6765\u662f\u6843\u82b1\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u96be\u514d\u6709\u4e9b\u60ca\u5f02\uff1b\u201c\u662f\u6843\u82b1\u59d1\u5a18\uff1f\u8fd9\u65f6\u5019\u4f60\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6843\u82b1\u9053\uff1a\u201c\u7ed9\u516c\u5b50\u9001\u4e1c\u897f\u6765\u4e86\uff0c\u5feb\u628a\u706f\u70b9\u4e0a\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u672c\u662f\u548c\u8863\u800c\u5367\uff0c\u7acb\u5373\u5750\u8d77\u8eab\u70b9\u4e0a\u4e86\u706f\u3002 \u3000\u3000\u6843\u82b1\u624b\u91cc\u62ff\u7684\u5374\u662f\u4e00\u5957\u8863\u670d\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u832b\u7136\u9053\uff1a\u201c\u8c01\u7684\u8863\u670d\uff0c\u4e3a\u4ec0\u4e48\u9001\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u6d1e\u5185\u8303\u56f4\u592a\u5c0f\uff0c\u5e76\u65e0\u5750\u5904\uff0c\u6843\u82b1\u53ea\u597d\u5750\u4e0a\u4e86\u5e8a\uff0c\u7b11\u9053\uff1b\u201c\u5f53\u7136\u662f\u516c\u5b50\u7684\uff0e\u7edf\u9886\u770b\u4f60\u7684\u8863 \u670d\u5df2\u7ecf\u70e7\u574f\u4e86\u4e0b\u6446\uff0c\u6240\u4ee5\u6253\u53d1\u6211\u66ff\u4f60\u9001\u4e00\u5957\u65b0\u7684\u6765\uff0c\u4ee5\u4fbf\u660e\u5929\u6362\u4e0a\uff0c\u53e6\u5916\u8fd8\u6709\u4e00\u53cc\u65b0\u978b\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u706b\u5173\u4e0a\u90a3\u6765\u7684\u7537\u4eba\u8863\u670d\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u603b\u575b\u53d1\u4e0b\u6765\u7684\uff0c\u56e0\u4e3a\u8fc7\u706b\u5173\u7684\u70e7\u574f\u8863\u670d\u548c\u978b\u5b50\u662f\u5e38\u4e8b\uff0c\u5fc5\u987b\u66ff\u4ed6\u4eec\u8865\u5145\u3002\u201d \u3000\u3000\u201c\u5c31\u8bf7\u59d1\u5a18\u4ee3\u6211\u8c22\u8c22\u7edf\u9886\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u672c\u4ee5\u4e3a\u6843\u82b1\u653e\u4e0b\u8863\u978b\u4f1a\u9a6c\u4e0a\u79bb\u53bb\uff0c\u5c82\u77e5\u5979\u5750\u5728\u5e8a\u4e0a\uff0c\u5374\u6ca1\u6709\u8981\u8d70\u7684\u6837\u5b50\u3002 \u3000\u3000\u597d\u5728\u5cb3\u5c0f\u98de\u6b64\u523b\u7761\u4e0d\u7761\u90fd\u65e0\u6240\u8c13\uff0c\u800c\u4e14\u4ed6\u4e5f\u6b63\u60f3\u7531\u6843\u82b1\u53e3\u4e2d\u63a2\u67e5\u4e00\u4e9b\u4e8b\u60c5\u3002 \u3000\u3000\u4ed6\u7565\u4e00\u6c89\u541f\uff0c\u9053\uff1a\u201c\u6843\u82b1\u59d1\u5a18\uff0c\u6211\u60f3\u95ee\u4f60\u4e00\u4ef6\u4e8b\uff0c\u636e\u4f60\u6240\u77e5\uff0c\u95ef\u8fc7\u706b\u5173\u7684\uff0c\u7a76\u7adf\u6709\u591a\u5c11 \u4eba\uff1f\u201d \u3000\u3000\u6843\u82b1\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u5927\u7ea6\u6709\u4e24\u4e09\u767e\u4eba\u5427\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5403\u4e86\u4e00\u60ca\u9053\uff1b\u201c\u771f\u662f\u4eba\u5916\u6709\u4eba\uff0c\u5929\u5916\u6709\u5929\uff0c\u4ece\u524d\u6211\u603b\u4ee5 \u3000\u3000\u4e3a\u81ea\u5df1\u7684\u8f7b\u529f\u5df2\u7ecf\u5f88\u96be\u5f97\u4e86\uff0c\u60f3\u4e0d\u5230\u7adf\u6709\u8fd9\u4e48\u591a\u4eba\u80fd\u95ef\u8fc7\u706b\u5173\uff01\u201d \u3000\u3000\u6843\u82b1\u795e\u79d8\u4e00\u7b11\u9053\uff1b\u201c\u5b9e\u5bf9\u516c\u5b50\u8bf4\uff0c\u771f\u6b63\u80fd\u51ed\u672c\u9886\u95ef\u8fc7\u706b\u5173\u7684\uff0c \u3000\u3000\u4f60\u8be5\u662f\u7b2c\u56db\u4e2a\u4eba\uff0c\u5176\u4f59\u7684\u4e0d\u8fc7\u662f\u6295\u673a\u53d6\u5de7\u6df7\u8fc7\u53bb\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u600e\u6837\u6df7\u8fc7\u53bb\u7684\uff1f\u201d \u3000\u3000\u201c\u5c31\u50cf\u8881\u5c0f\u4fa0\u548c\u59d3\u4e01\u7684\u59d3\u7518\u7684\u4e00\u6837\uff0c\u516c\u5b50\u4e0d\u662f\u4eb2\u773c\u770b\u5230\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u7edf\u9886\u5c31\u627f\u8ba4\u4ed6\u95ef\u8fc7\u4e86\u706b\u5173\uff1f\u201d \u3000\u3000\u201c\u4e0d\u627f\u8ba4\u53c8\u600e\u4e48\u6837\uff0c\u603b\u575b\u662f\u5e0c\u671b\u5438\u6536\u4eba\u624d\u8d8a\u591a\u8d8a\u597d\uff0c\u7edf\u9886\u4e5f\u53ea\u6709\u7741\u4e00\u53ea\u773c\u95ed\u4e00\u53ea\u773c\uff0c\u82e5 \u771f\u8981\u4e25\u683c\u6267\u884c\uff0c\u90a3\u4e48\u8fd9\u51e0\u5e74\u6765\uff0c\u8fde\u4f60\u4e5f\u4e0d\u8fc7\u53ea\u80fd\u5438\u6536\u56db\u4eba\uff0c\u90a3\u8fd8\u79f0\u7684\u4ec0\u4e48\u62db\u5175\u4e70\u9a6c\uff1f\u201d \u3000\u3000\u5979\u9ed8\u4e86\u4e00\u9ed8\uff0c\u53c8\u9053\uff1a\u201c\u516c\u5b50\uff0c\u5176\u5b9e\u5728\u8fd9\u51ed\u771f\u672c\u4e8b\u95ef\u8fc7\u706b\u5173\u7684\u56db\u4eba\u4e2d\uff0c\u76f4\u6b63\u53d7\u77a9\u76ee\u7684\uff0c\u4f60 \u8be5\u662f\u7b2c\u4e00\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u5934\u4e00\u52a8\u9053\uff1a\u201c\u8fd9\u53c8\u600e\u4e48\u8bf4\u5462\uff1f\u201d \u3000\u3000\u6843\u82b1\u9053\uff1a\u201c\u56e0\u4e3a\u524d\u9762\u7684\u4e09\u4eba\uff0c\u5374\u662f\u6210\u540d\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u800c\u4f60\u5374\u53ea\u6709\u5341\u51e0\u5c81\uff0c\u5c06\u6765\u7684\u6210\u5c31\uff0c \u5f53\u7136\u66f4\u5728\u4ed6\u4eec\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5185\u5fc3\u53c8\u662f\u4e00\u52a8\uff0c\u8d81\u673a\u95ee\u9053\uff1a\u201c\u524d\u9762\u4e09\u4f4d\u95ef\u8fc7\u706b\u5173\u7684\u524d\u8f88\uff0c\u59d1\u5a18\u53ef\u66fe\u4eb2\u773c\u770b\u8fc7\uff1f\u201d \u3000\u3000\u6843\u82b1\u9053\uff1a\u201c\u6211\u662f\u7ecf\u5e38\u968f\u4f8d\u5728\u7edf\u9886\u8eab\u8fb9\u7684\u4eba\uff0c\u5f53\u7136\u89c1\u8fc7\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u90fd\u6709\u591a\u5927\u5e74\u7eaa\uff1f\u957f\u7684\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u201c\u5176\u4e2d\u4e00\u4eba\uff0c\u6211\u5f53\u65f6\u4e0d\u66fe\u7559\u610f\uff0c\u4f46\u6709\u540c\u6765\u7684\u4e00\u7537\u4e00\u5973\uff0c\u6211\u5374\u770b\u5f97\u975e\u5e38\u6e05\u695a\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u4e3a\u4ec0\u4e48\u8981\u7279\u522b\u6ce8\u610f\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u90a3\u4e00\u7537\u4e00\u5973\uff0c\u7537\u7684\u4e30\u795e\u4fca\u9038\uff0c\u7fe9\u7fe9\u6f47\u6f47\uff0c\u5973\u7684\u4ed9\u59ff\u7389\u8d28\uff0c\u98ce\u534e\u7edd\u4ee3\uff0c\u53c8\u6709\u8c01\u4e0d\u60f3 \u5bf9\u4ed6\u4eec\u591a\u770b\u51e0\u773c\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u53ef\u8bb0\u5f97\u8fd9\u662f\u591a\u4e45\u4ee5\u524d\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u7b97\u8d77\u6765\u8be5\u662f\u4e09\u5e74\u524d\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4e00\u7537\u4e00\u5973\u6709\u591a\u5927\u5e74\u7eaa\uff1f\u201d \u3000\u3000\u201c\u7537\u7684\u5f53\u65f6\u4e0d\u8fc7\u4e09\u5341\u5de6\u53f3\uff0c\u5973\u7684\u53ef\u80fd\u8fd8\u8981\u5e74\u8f7b\u4e00\u4e9b\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u518d\u4e5f\u7981\u4e0d\u4f4f\u5185\u5fc3\u7684\u6fc0\u52a8\uff0c\u8fd9\u4e24\u4eba\u5206\u660e\u662f\u81ea\u5df1\u7684\u7236\u6bcd\uff0c\u8fd9\u6837\u770b\u6765\uff0c\u4ed6\u5e72\u5c71\u4e07\u6c34\u6765\u5230 \u7ec8\u5357\uff0c\u603b\u7b97\u6ca1\u6709\u767d\u6765\u3002 \u3000\u3000\u4ed6\u5f3a\u81ea\u6291\u5236\u7740\u5185\u5fc3\u7684\u6fc0\u52a8\uff1f\u4ee5\u4fbf\u4e0d\u4f7f\u5bf9\u65b9\u611f\u5230\u53ef\u7591\uff0c\u518d\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u77e5\u9053\u4ed6\u4eec\u59d3\u4ec0\u4e48\uff1f \u53eb\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6843\u82b1\u6447\u5934\u9053\uff1a\u201c\u4e0d\u6e05\u695a\uff0c\u4ed6\u4eec\u95ef\u8fc7\u5173\u5c31\u7ee7\u7eed\u524d\u8fdb\uff0c\u8fde\u7edf\u9886\u90fd\u6ca1\u6765\u5f97\u53ca\u95ee\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u6587\u5173\u548c\u6b66\u5173\u5e94\u8be5\u77e5\u9053\u4ed6\u4eec\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u6587\u5173\u548c\u6b66\u5173\u53ea\u8d1f\u8d23\u6d4b\u9a8c\uff0c\u5e76\u4e0d\u8fc7\u95ee\u4ed6\u4eec\u7684\u8eab\u4e16\u6765\u5386\u548c\u59d3\u540d\uff0c\u516c\u5b50\u4e5f\u662f\u6587\u5173\u6b66\u5173\u7684\u8fc7\u6765 \u4eba\uff0c\u96be\u9053\u8fd8\u4e0d\u6e05\u695a\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u56de\u60f3\u5728\u6587\u5173\u548c\u6b66\u5173\u65f6\uff0c\u679c\u7136\u5bf9\u65b9\u4e0d\u66fe\u67e5\u95ee\u8fd9\u4e9b\u3002 \u3000\u3000\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u518d\u9053\uff1a\u201c\u53ef\u662f\u50cf\u4ed6\u4eec\u90a3\u6837\u6b66\u529f\u8d85\u51e1\u7edd\u4fd7\u7684\u4eba\u7269\uff0c\u5230\u4e86\u603b\u575b\uff0c\u4e0d\u4f1a\u518d\u5c31\u6ca1\u6709 \u6d88\u606f\u4e86\u5427\uff1f\u201d \u3000\u3000\u6843\u82b1\u9053\uff1a\u201c\u5373\u4fbf\u6253\u6d88\u606f\u6211\u4e5f\u4e0d\u53ef\u80fd\u77e5\u9053\u3002\u201d \u3000\u3000\u5979\u8bf4\u5230\u8fd9\u91cc\uff0c\u624d\u89c9\u51fa\u6709\u4e9b\u53ef\u7591\uff0c\u4e24\u773c\u7728\u7728\u7684\u53cd\u95ee\u9053\uff1b\u201c\u516c\u5b50\u4e3a\u4ec0\u4e48\u8ffd\u95ee\u5f97\u8fd9\u4e48\u4ed4\u7ec6\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u88c5\u505a\u82e5\u65e0\u5176\u4e8b\u9053\uff1a\u201c\u50cf\u90a3\u6837\u51fa\u7c7b\u62d4\u8403\u7684\u524d\u8f88\u4eba\u7269\uff0c\u5f53\u7136\u8c01\u90fd\u5e0c\u671b\u77e5\u9053\u4ed6\u4eec\u662f\u51c6\uff0c \u5c31\u4ee5\u6211\u6765\u8bf4\u5427\uff0c\u5c06\u6765\u6709\u5e78\u5230\u4e86\u603b\u575b\uff0c\u82e5\u80fd\u591a\u5411\u4ed6\u4eec\u8bf7\u76ca\uff0c\u5bf9\u81ea\u5df1\u603b\u662f\u6709\u5f88\u5927\u5e2e\u52a9\u7684\u3002\u201d \u3000\u3000\u6843\u82b1\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u516c\u5b50\u5230\u4e86\u603b\u575b\uff0c\u4e00\u5b9a\u4f1a\u5f97\u5230\u91cd\u7528\uff0c\u5230\u90a3\u65f6\u6211\u8fd8\u771f\u5e0c\u671b\u4f60\u80fd\u63d0\u62d4\u63d0\u62d4 \u6211\uff0c\u628a\u6211\u8c03\u5230\u603b\u575b\u53bb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u90a3\u662f\u59d1\u5a18\u592a\u5ba2\u6c14\uff0c\u5982\u679c\u771f\u6709\u90a3\u4e48\u4e00\u5929\uff0c\u59d1\u5a18\u53ea\u8981\u7528\u5f97\u7740\uff0c\u6211\u4e00\u5b9a\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u6843\u82b1\u88e3\u887d\u2014\u793c\u9053\uff1a\u201c\u6211\u5c31\u5148\u8c22\u8c22\u516c\u5b50\u4e86\uff0c\u4f60\u660e\u5929\u4e00\u65e9\u8fd8\u8981\u8fc7\u5173\uff0c\u5fc5\u987b\u597d\u597d\u4f11\u606f\uff0c\u6211\u4e0d\u6253 \u6405\u4e86\uff01\u201d \u3000\u3000\u6b21\u65e5\u5929\u4eae\u540e\uff0c\u706b\u5173\u7edf\u9886\u5c11\u4e0d\u5f97\u53c8\u8981\u62db\u5f85\u4e00\u987f\u65e9\u9910\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8fd9\u624d\u53d1\u73b0\uff0c\u8fde\u8881\u5c0f\u9e64\u3001\u4e01\u6d9b\u3001\u7518\u9716\u7b49\u4e09\u4eba\u4e5f\u90fd\u6362\u4e0a\u4e86\u65b0\u8863\u65b0\u978b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u95ee\u4e01\u6d9b\u548c\u7518\u9716\u4e24\u4eba\u811a\u4f24\u662f\u5426\u5df2\u7ecf\u75ca\u6108\u3002 \u3000\u3000\u4e01\u6d9b\u7ffb\u7740\u4e00\u5bf9\u725b\u773c\u9053\uff1a\u201c\u771f\u662f\u602a\u4e8b\uff0c\u4ec5\u4ec5\u4e00\u665a\uff0c\u7adf\u7136\u6062\u590d\u5f97\u548c\u4ece\u524d\u5b8c\u5168\u4e00\u6837\u3002\u201d \u3000\u3000\u996d\u540e\uff0c\u56db\u4eba\u7ee7\u7eed\u8d76\u8def\u3002 \u3000\u3000\u4ed6\u4eec\u672c\u6765\u60f3\u5411\u706b\u5173\u7edf\u9886\u9053\u8c22\uff0c\u4f46\u90a3\u4e2d\u5e74\u5973\u5b50\u5374\u5e76\u672a\u51fa\u73b0\uff0c\u53ea\u6d3e\u6843\u82b1\u524d\u6765\u7167\u6599\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u95ee\u9053\uff1a\u201c\u6843\u82b1\u59d1\u5a18\uff0c\u524d\u9762\u53c8\u662f\u4ec0\u4e48\u5173\uff1f\u201d \u3000\u3000\u6843\u82b1\u7b11\u9053\uff1a\u201c\u5929\u673a\u4e0d\u53ef\u6cfb\u9732\u3002\u4e0d\u8fc7\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\u4eec\uff0c\u524d\u9762\u90a3\u4e00\u5173\uff0e\u8bf4\u96be\u95ef\u4e5f\u4e0d\u96be\uff0c\u8bf4\u4e0d \u96be\u95ef\u8fd8\u771f\u96be\uff0c\u603b\u4e4b\uff0c\u6bd4\u95ef\u706b\u5173\u53ef\u80fd\u8981\u5bb9\u6613\u4e9b\u3002\u201d \u3000\u3000\u8fd9\u7b49\u4e8e\u6253\u4e86\u4e00\u4e2a\u54d1\u8ff7\uff0c\u53eb\u4eba\u5f53\u7136\u4e5f\u4e0d\u4fbf\u8ffd\u95ee\u3002 \u3000\u3000\u6843\u82b1\u66fe\u9001\u4e86\u4e00\u6bb5\u8def\u3002 \u3000\u3000\u8def\u4e0a\uff0c\u5979\u552f\u72ec\u5bf9\u5cb3\u5c0f\u98de\u8868\u73b0\u5f97\u683c\u5916\u4eb2\u5207\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u91cc\u6709\u6570\uff0c\u5979\u7684\u76ee\u7684\uff0c\u4e0d\u5916\u662f\u5e0c\u671b\u81ea\u5df1\u5c06\u6765\u80fd\u5bf9\u5979\u5e2e\u5fd9\u3002 \u3000\u3000\u6843\u82b1\u53ea\u9001\u4e86\u534a\u91cc\u8def\uff0c\u4fbf\u81ea\u884c\u56de\u53bb\u3002 \u3000\u3000\u53ea\u542c\u4e01\u6d9b\u9053\uff1a\u201c\u8fd9\u6d0b\u8fc7\u4e86\u4e00\u5173\u53c8\u4e00\u5173\uff0c\u771f\u4e0d\u77e5\u9053\u8fd8\u6709\u591a\u5c11\u5173\u597d\u8fc7\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u8bdd\u867d\u6ca1\u5f15\u8d77\u4efb\u4f55\u4eba\u63a5\u8154\uff0c\u4f46\u5927\u5bb6\u90fd\u4e0d\u96be\u60f3\u5230\u4e00\u4ef6\u4e8b\uff0c\u90a3\u5c31\u662f\u51e1\u662f\u6700\u540e\u80fd\u901a\u8fc7\u6240\u6709\u5173 \u95e8\u7684\uff0c\u5fc5\u662f\u4e00\u4f4d\u51fa\u7c7b\u62d4\u8403\u7684\u6587\u6b66\u5168\u624d\u3002 \u3000\u3000\u4e24\u8fb9\u7684\u8c37\u58c1\uff0c\u76f4\u662f\u7b14\u76f4\u5982\u524a\uff0c\u9ad8\u53ef\u63d2\u5929\uff0c\u8c01\u4e5f\u4e0d\u80fd\u9884\u6599\u5230\u6700\u540e\u7a76\u7adf\u662f\u4e00\u4e2a\u4ec0\u4e48\u4e16\u754c\u3002 \u3000\u3000\u5ffd\u542c\u8881\u5c0f\u9e64\u53eb\u9053\uff1a\u201c\u524d\u9762\u53c8\u662f\u4e00\u5173\u4e86\uff01\u201d \u3000\u3000\u4f17\u4eba\u5411\u524d\u671b\u53bb\uff0c\u679c\u7136\u53c8\u662f\u4e00\u5904\u96a7\u9053\u3002 \u3000\u3000\u4ed6\u4eec\u52a0\u7d27\u811a\u6b65\uff0c\u6765\u5230\u8ddf\u524d\uff0c\u53ea\u89c1\u96a7\u9053\u53e3\u6b63\u7ad9\u7740\u4e00\u4f4d\u7eff\u8863\u5973\u90ce\u3002 \u3000\u3000\u53ea\u662f\u96a7\u9053\u53e3\u5374\u88ab\u4e00\u5e45\u5de8\u5927\u7684\u7eff\u8272\u5e37\u5e54\u906e\u4f4f\uff0c\u770b\u4e0d\u6e05\u91cc\u9762\u7684\u60c5\u666f\u3002 \u3000\u3000\u90a3\u7eff\u8863\u5973\u90ce\u770b\u6765\u53ea\u6709\u5341\u516b\u4e5d\u5c81\uff0c\u80a4\u767d\u4f3c\u96ea\uff0c\u9edb\u7709\u5982\u753b\uff0c\u7f8e\u5f97\u6e05\u65b0\u3001\u7f8e\u5f97\u8131\u4fd7\uff0c\u6bd4\u5148\u524d\u6587 \u5173\u548c\u706b\u5173\u6240\u89c1\u7684\u4efb\u4f55\u4e00\u540d\u5c11\u5973\uff0c\u66f4\u8981\u51fa\u8272\u7684\u591a\uff0c\u4ee4\u4eba\u51e0\u7591\u662f\u51fa\u73b0\u5728\u7fa4\u7389\u5c71\u5934\u7684\u5929\u4e0a\u4ed9\u5973\u4e0b\u51e1\u3002 \u3000\u3000\u56db\u4eba\u6b64\u523b\u65e9\u77e5\u8fc7\u5173\u7684\u89c4\u77e9\uff0c\u6765\u5230\u96a7\u9053\u53e3\u524d\u4e08\u4f59\u5de6\u53f3\u4fbf\u81ea\u884c\u505c\u4f4f\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u62f1\u624b\u4e00\u793c\u9053\uff1a\u201c\u8bf7\u95ee\u59d1\u5a18\uff0e\u4f60\u53ef\u662f\u5728\u8fd9\u91cc\u628a\u5173\u7684\uff1f\u201d \u3000\u3000\u7eff\u8863\u5973\u5029\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5982\u679c\u4e0d\u662f\u628a\u5173\uff0c\u6211\u53c8\u7ad9\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5979\u8fd9\u7efd\u5507\u4e00\u7b11\uff0c\u8fde\u4e00\u5411\u77e5\u793c\u800c\u53c8\u5e84\u91cd\u7684\u8881\u5c0f\u9e64\uff0c\u90fd\u4e0d\u7981\u6709\u4e9b\u5fc3\u795e\u8361\u6f3e\uff0c\u81f3\u4e8e\u4e01\u6d9b\u548c\u7518\u9716\uff0c \u66f4\u5982\u540c\u89e6\u4e0a\u7535\u6d41\u2014\u822c\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u642d\u8baa\u7740\u518d\u95ee\u9053\uff1a\u201c\u8fd9\u91cc\u5c31\u662f\u59d1\u5a18\u4e00\u4eba\u628a\u5173\u4e48\uff1f\u201d \u3000\u3000\u7eff\u8863\u5973\u53c8\u662f\u98ce\u81f4\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u4f55\u5fc5\u4eba\u591a\uff0c\u6709\u6211\u4e00\u4eba\u5c31\u591f\u4e86\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6000\u7591\u7eff\u8863\u5973\u505a\u4e0d\u4e86\u4e3b\uff0c\u53c8\u9053\uff1a\u201c\u8d35\u5173\u7684\u7edf\u9886\u5c71\u4ec0\u4e48\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u7eff\u8863\u5973\u4e0d\u52a8\u58f0\u8272\u53cd\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u7edf\u9886\u51fa\u6765\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u6211\u4eec\u5728\u524d\u9762\u7ecf\u8fc7\u597d\u51e0\u5173\uff0c\u90fd\u662f\u7edf\u9886\u4eb2\u81ea\u51fa\u9762\u3002\u201d \u3000\u3000\u7eff\u8863\u5973\u7b11\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u5c31\u662f\u8fd9\u91cc\u7684\u7edf\u9886\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u95fb\u8a00\u51e0\u4e4e\u5446\u4f4f\u3002 \u3000\u3000\u8fde\u5cb3\u5c0f\u98de\u3001\u4e01\u6d9b\u3001\u7518\u9716\u4e5f\u90fd\u96be\u4ee5\u76f8\u4fe1\u3002 \u3000\u3000\u56e0\u4e3a\u6587\u5173\u548c\u6b66\u5173\u7edf\u9886\u90fd\u662f\u767d\u53d1\u82cd\u82cd\u7684\u8001\u5934\u5b50\uff0c\u706b\u5173\u4e5f\u662f\u4f4d\u4e2d\u5e74\u5973\u5b50\uff0c\u8fd9\u7eff\u8863\u5973\u770b\u6765\u53ea\u6709 \u5341\u516b\u4e5d\u5c81\uff0c\u600e\u4f1a\u8fd9\u6837\u5e74\u8f7b\u5c31\u5f53\u4e0a\u7edf\u9886\u7684\u804c\u4f4d\uff1f \u3000\u3000\u7eff\u8863\u5973\u6d45\u7b11\u7740\u9053\uff1a\u201c\u4f60\u4eec\u56db\u4f4d\u597d\u50cf\u4e0d\u4fe1\uff1f\u5f7c\u6b64\u521d\u89c1\u9762\uff0c\u6211\u6709\u548c\u5404\u4f4d\u5f00\u73a9\u7b11\u7684\u5fc5\u8981\u5417\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u53ea\u597d\u53c8\u9053\uff1a\u201c\u8bf7\u95ee\u8fd9\u91cc\u662f\u4ec0\u4e48\u5173\uff1f\u201d \u3000\u3000\u7eff\u8863\u5973\u9053\uff1a\u201c\u6c34\u5173\u201d \u3000\u3000\u8881\u5c0f\u9e64\u5e76\u672a\u611f\u5230\u5947\u602a\uff0c\u56e0\u4e3a\u65e2\u6709\u706b\u5173\uff0c\u987a\u7406\u6210\u7ae0\u7684\u4e5f\u5fc5\u6709\u6c34\u5173\uff0c\u53ea\u662f\u6c34\u5173\u53c8\u662f\u4e00\u79cd\u4ec0\u4e48 \u60c5\u5f62\u5462\uff1f\u96a7\u9053\u662f\u4f9b\u4eba\u901a\u884c\u7684\uff0c\u53c8\u54ea\u91cc\u6765\u7684\u6c34\uff1f \u3000\u3000\u53ea\u542c\u7eff\u8863\u5973\u9ad8\u58f0\u558a\u9053\uff1a\u201c\u64a4\u5e18\uff01\u201d \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u7eff\u8863\u5973\u558a\u8fc7\u4e00\u58f0\u4e4b\u540e\uff0c\u90a3\u906e\u4f4f\u96a7\u9053\u53e3\u7684\u5de8\u5e45\u5e37\u5e54\u7adf\u81ea\u52a8\u6536\u81f3\u4e24\u8fb9\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u7b49\u56db\u4eba\u9f50\u9f50\u5411\u96a7\u9053\u5185\u671b\u53bb\uff0c\u5374\u5e76\u672a\u770b\u5230\u90a3\u91cc\u6709\u6c34\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u4e0d\u7981\u95ee\u9053\uff1a\u201c\u6c34\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u7eff\u8863\u5973\u9ad8\u558a\u9053\uff1a\u201c\u5f00\u5173\uff01\u201d \u3000\u3000\u5ffd\u542c\u4e00\u9635\u9707\u8033\u6b32\u804b\u7684\u9686\u9686\u4e4b\u58f0\uff0c\u90a3\u96a7\u9053\u7684\u5730\u9762\u7adf\u7136\u88c2\u4e86\u5f00\u6765\u3002 \u3000\u3000\u6562\u60c5\u90a3\u5730\u9762\u7adf\u662f\u4e00\u5c42\u94c1\u677f\uff0c\u90a3\u94c1\u677f\u5c45\u7136\u80fd\u6536\u7f29\u8fdb\u6d1e\u58c1\u5185\u3002 \u3000\u3000\u94c1\u677f\u4e0d\u89c1\u4e4b\u540e\uff0c\u4e0b\u9762\u4fbf\u7acb\u523b\u73b0\u51fa\u4e00\u7247\u7247\u6c6a\u6c6a\u6d2a\u6c34\uff0c\u4ee5\u76ee\u89c6\u4f30\u8ba1\uff0c\u8fd9\u6c34\u7531\u6b64\u7aef\u5230\u5f7c\u7aef\u8db3\u6709 \u5341\u51e0\u4e08\u7684\u8ddd\u79bb\uff0c\u4efb\u4f55\u8f7b\u529f\u9ad8\u8d85\u7684\u4eba\u4e5f\u65e0\u6cd5\u4e00\u8dc3\u800c\u8fc7\u3002 \u3000\u3000\u6b64\u523b\uff0c\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u90fd\u4e0d\u7981\u5fc3\u60c5\u7d27\u5f20\uff0c\u56e0\u4e3a\u4ed6\u4eec\u4e24\u4eba\u90fd\u4e0d\u4e60\u6c34\u6027\u3002 \u3000\u3000\u4f46\u4e01\u6d9b\u548c\u7518\u9716\u5374\u53cd\u800c\u5e76\u4e0d\u5728\u4e4e\uff0c\u539f\u6765\u4ed6\u4eec\u90fd\u6709\u4e0d\u9519\u7684\u6cf3\u672f\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u7d27\u8e59\u53cc\u7709\u95ee\u9053\uff1a\u201c\u8fd9\u6c34\u6709\u591a\u6df1\uff1f\u201d \u3000\u3000\u7eff\u8863\u5973\u9053\uff1a\u201c\u6700\u6d45\u7684\u5730\u65b9\u4e24\u4e08\uff0c\u6700\u6df1\u7684\u5730\u65b9\u5927\u7ea6\u4e09\u4e08\u591a\u3002\u201d \u3000\u3000\u5bf9\u4e00\u4e2a\u4e0d\u4f1a\u6e38\u6cf3\u7684\u4eba\u6765\u8bf4\uff0c\u53ea\u8981\u516d\u4e03\u5c3a\u6df1\u7684\u6c34\u4fbf\u53ef\u706d\u9876\u4e86\uff0c\u600e\u80fd\u4e0d\u4f7f\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u66f4 \u52a0\u7d27\u5f20\u3002 \u3000\u3000\u5ffd\u542c\u4e01\u6d9b\u95ee\u9053\uff1a\u201c\u8fd9\u6c34\u810f\u4e0d\u810f\uff1f\u201d \u3000\u3000\u7eff\u8863\u5973\u9053\uff1b\u201c\u4f60\u95ee\u8fd9\u4e9b\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6211\u548c\u8fd9\u4f4d\u59d3\u7518\u7684\u90fd\u6e38\u6cf3\uff0c\u522b\u8bf4\u6c34\u5173\u53ea\u6709\u5341\u51e0\u4e08\u8fdc\uff0c\u5373\u4fbf\u5341\u91cc\u516b\u91cc\u8fdc\uff0c\u4e5f\u96be\u4e0d \u5012\u54b1\u4eec\uff0c\u6015\u7684\u662f\u6c34\u810f\uff0c\u5f04\u5f97\u6ee1\u8eab\u81ed\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u7518\u9716\u4e5f\u7d27\u8ddf\u7740\u9053\uff1a\u201c\u662f\u554a\uff0c\u5982\u679c\u8fd9\u6c34\u5173\u91cc\u50cf\u7caa\u5751\uff0c\u53ea\u8981\u4e00\u4e08\u8fdc\u5c31\u591f\u4eba\u53d7\u7684\u4e86\uff01\u201d \u3000\u3000\u7eff\u8863\u5973\u5fcd\u4e0d\u4f4f\u201c\u6251\u54e7\u201d\u4e00\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u7751\u4e0a\u53ef\u6709\u9f3b\u5b50\uff1f\u201d \u3000\u3000\u7518\u9716\u4f3c\u4e4e\u8fd8\u6ca1\u4f1a\u8fc7\u610f\u6765\uff0c\u832b\u7136\u9053\uff1a\u201c\u59d1\u5a18\u95ee\u8fd9\u4e2a\u5e72\u4ec0\u4e48\uff0c\u4eba\u54ea\u6709\u6ca1\u9f3b\u5b50\u7684\uff1f\u201d \u3000\u3000\u7eff\u8863\u5973\u9053\uff1a\u201c\u65e2\u7136\u6709\u9f3b\u5b50\uff0c\u5982\u679c\u771f\u6709\u8fd9\u4e48\u4e00\u5904\u5927\u7caa\u5751\uff0c\u4f60\u4eec\u603b\u8be5\u95fb\u5f97\u51fa\u6765\uff01\u201d \u3000\u3000\u7518\u9716\u8138\u4e0a\u4e00\u70ed\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u5982\u679c\u8fd9\u6c34\u91cc\u4e0b\u4e86\u6bd2\u5462\uff1f\u201d \u3000\u3000\u7eff\u8863\u5973\u8106\u751f\u751f\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u7591\u795e\u7591\u9b3c\uff0c\u6bdb\u75c5\u53ef\u771f\u591a\uff01\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u5e76\u975e\u6211\u4eec\u7591\u5fc3\u5927\uff0c\u800c\u662f\u4e0d\u5f97\u4e0d\u9632\u3002\u201d \u3000\u3000\u201c\u8fd9\u6c34\u5173\u91cc\uff0c\u8fde\u9c7c\u90fd\u6709\uff0c\u5982\u679c\u4e0b\u4e86\u6bd2\uff0c\u54ea\u91cc\u4f1a\u6709\u9c7c\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u4eec\u770b\u4e0d\u89c1\uff1f\u201d \u3000\u3000\u7eff\u8863\u5973\u7acb\u523b\u53c8\u558a\u9053\uff1b\u201c\u5f00\u5149\u7167\u660e\uff01\u201d \u3000\u3000\u84e6\u5730\uff0c\u7531\u6d1e\u9876\u53ca\u4e24\u4fa7\u5c04\u51fa\u6570\u5341\u9053\u5f3a\u70c8\u5149\u675f\uff0c\u7167\u8000\u5f97\u96a7\u9053\u5185\u5982\u540c\u767d\u663c\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u4e01\u6d9b\u3001\u7518\u9716\u3001\u8881\u5c0f\u9e64\uff0c\u5cb3\u5c0f\u98de\u90fd\u4e0d\u514d\u4eba\u4e3a\u9707\u60ca\uff0c\u6d1e\u9876\u548c\u6d1e\u58c1\u4e24\u4fa7\u5c45\u7136\u80fd\u5728\u540c \u4e00\u65f6\u95f4\u5c04\u51fa\u5f3a\u5149\u675f\uff0c\u7b80\u76f4\u662f\u532a\u5937\u6240\u601d\u3002 \u3000\u3000\u7eff\u8863\u5973\u9053\uff1a\u201c\u4f60\u4eec\u4ed4\u7ec6\u770b\u770b\uff0c\u6709\u8272\u6ca1\u6709\uff1f\u201d \u3000\u3000\u679c\u7136\uff0c\u8fd9\u6c34\u7adf\u662f\u6e05\u6f88\u89c1\u5e95\uff0c\u800c\u4e14\u6709\u6f7a\u6f7a\u6d41\u52a8\u4e4b\u58f0\uff0c\u663e\u7136\u5730\u4e0b\u5c31\u662f\u6c34\u6e90\uff0c\u7adf\u662f\u5929\u7136\u7684\u6c34\u6d41\u3002 \u3000\u3000\u518d\u4e00\u7ec6\u770b\uff0c\u4e01\u6d9b\u7adf\u662f\u6068\u4e0d\u5f97\u7acb\u5373\u6251\u4e0b\u6c34\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u65f6\u6b63\u6709\u4e00\u7fa4\u91d1\u8272\u9ca3\u9c7c\u6e38\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u7fa4\u9ca3\u9c7c\uff0c\u8db3\u6709\u5341\u51e0\u5c3e\uff0c\u6bcf\u5c3e\u90fd\u6709\u597d\u51e0\u65a4\u91cd\uff0c\u770b\u4e86\u7740\u5b9e\u8bf1\u4eba\u3002 \u3000\u3000\u63a5\u7740\uff0c\u53c8\u53d1\u73b0\u6c34\u5e95\u6709\u4e0d\u5c11\u5de8\u5927\u7684\u9f99\u867e\uff0c\u66f4\u6709\u597d\u51e0\u53ea\u5927\u4e4c\u9f9f\u6d6e\u5728\u6c34\u9762\u6e38\u52a8\u3002 \u3000\u3000\u8fd9\u7b80\u76f4\u662f\u4e00\u5904\u6c34\u65cf\u9986\u4e86\u3002 \u3000\u3000\u53ea\u542c\u7eff\u8863\u5973\u9053\uff1a\u201c\u4f60\u60f3\u4e0b\u53bb\u6349\u9c7c\u5417\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8fd9\u6837\u9c9c\u7f8e\u7684\u5927\u9ca3\u9c7c\uff0c\u8c01\u4e0d\u60f3\u6349\uff0e\u53ea\u662f\u6349\u4e0a\u6765\u4ee5\u540e\u6ca1\u5730\u65b9\u716e\uff0c\u4f60\u53ef\u4e0d\u53ef\u4ee5\u5e2e\u6211 \u4eec\u716e\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u7eff\u8863\u5973\u9053\uff1a\u201c\u8fd8\u6709\u6bd4\u8fd9\u91d1\u8272\u9ca4\u9c7c\u66f4\u597d\u7684\u9c7c\uff0c\u4f60\u60f3\u4e0d\u60f3\u8981\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u60f3\u8981\uff0c\u90a3\u662f\u4ec0\u4e48\u9c7c\uff1f\u201d \u3000\u3000\u201c\u7f8e\u4eba\u9c7c\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u7f8e\u4eba\u9c7c\uff0c\u90a3\u53ea\u662f\u4f20\u8bf4\u4e2d\u7684\uff0c\u8c01\u4e5f\u6ca1\u89c1\u8fc7\u201d \u3000\u3000\u201c\u60f3\u4e0d\u60f3\u89c1\u8bc6\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u60f3\uff0c\u53ef\u60dc\u6ca1\u6cd5\u89c1\u5230\u3002\u201d \u3000\u3000\u7eff\u8863\u5973\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u73b0\u5728\u5c31\u8ba9\u4f60\u89c1\u8bc6\u89c1\u8bc6\uff01\u201d \u3000\u3000\u5979\u8bf4\u5b8c\u8bdd\uff0c\u968f\u5373\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u7f8e\u4eba\u9c7c\u6e38\u8fc7\u6765\uff01\u201d \u3000\u3000\u7b80\u76f4\u4ee4\u4eba\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u773c\u775b\uff0c\u5c31\u5728\u8fd9\u65f6\u7adf\u771f\u7684\u6709\u597d\u51e0\u540d\u7f8e\u5973\u7531\u6d1e\u58c1\u4e00\u4fa7\u6e38\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4e0d\u8fc7\u90a3\u4e0d\u662f\u7f8e\u4eba\u9c7c\uff0c\u800c\u662f\u771f\u6b63\u7684\u7f8e\u4e3d\u5c11\u5973\u3002 \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c \u516b \u56de\u3000\u5982\u6b64\u6c34\u5173 \u3000\u3000\u90a3\u597d\u51e0\u540d\u5c11\u5973\u3002\u4e0a\u8eab\u90fd\u4ec5\u662f\u7a7f\u4e86\u4e00\u4ef6\u809a\u515c\u513f\uff0c\u4e0b\u8eab\u5219\u662f\u4e00\u6761\u77ed\u5f97\u4e0d\u80fd\u518d\u77ed\u7684\u77ed\u88e4\u3002\u90a3\u5ae9 \u85d5\u822c\u7684\u53cc\u81c2\uff0c\u51dd\u8102\u6413\u9165\u822c\u800c\u53c8\u5149\u6ed1\u6676\u83b9\u7684\u4fee\u957f\u7389\u817f\uff0c\u90fd\u88f8\u9732\u5728\u5916\uff0c\u5c24\u5176\u53cc\u81c2\u548c\u53cc\u817f\u4e0d\u505c\u7684\u4f38 \u5c48\u5212\u4e71\uff0c\u53ea\u770b\u5f97\u4e01\u6d9b\u548c\u7518\u9716\u90fd\u4e0d\u7981\u76ee\u76f4\u795e\u9a70\u3002 \u3000\u3000\u4ed6\u4eec\u957f\u4e86\u8fd9\u4e48\u4e45\uff0c\u51e0\u66fe\u89c1\u8ba8\u5982\u6b64\u4ee4\u4eba\u5fc3\u52a8\u7684\u60c5\u666f\u3002 \u3000\u3000\u7eff\u8863\u5973\u56de\u7738\u671b\u7740\u4e01\u6d9b\u9053\uff1a\u201c\u8fd9\u6837\u7684\u7f8e\u4eba\u4eec\uff0c\u4f60\u60f3\u4e0d\u60f3\u4e0b\u6c34\u53bb\u6349\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u76f4\u7740\u4e24\u773c\u9053\uff1a\u201c\u5f53\u7136\u60f3\uff0c\u53ea\u662f\u4e0d\u77e5\u9053\u4f60\u80af\u4e0d\u80af\u8ba9\u6211\u6349\uff1f\u201d \u3000\u3000\u7eff\u8863\u5973\u9053\uff1a\u201c\u4f60\u4e0d\u4f46\u53ef\u4ee5\u6349\uff0c\u800c\u4e14\u6211\u7b54\u5e94\u6349\u5230\u4e86\u5c31\u662f\u4f60\u7684\u3002\u201d \u3000\u3000\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u201c\u8ddf\u4f60\u5f00\u7684\u4ec0\u4e48\u73a9\u7b11\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u54ea\u987e\u4e00\u5207\uff0c\u8fde\u5fd9\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u5305\u7528\u6cb9\u7eb8\u5305\u7740\u7684\u4e1c\u897f\uff0c\u4ea4\u4e0e\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u8fd9 \u662f\u4e00\u53e0\u94f6\u7968\uff0c\u4f60\u66ff\u6211\u62ff\u7740\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u63a5\u8fc7\u63e3\u5728\u6000\u91cc\u3002 \u3000\u3000\u4e01\u6d9b\u63a5\u7740\u5c31\u8981\u8131\u8863\u3002 \u3000\u3000\u7eff\u8863\u5973\u9053\uff1a\u201c\u5728\u6211\u9762\u524d\uff0c\u4e0d\u51c6\u8131\u8863\u670d\uff01\u201d \u3000\u3000\u4e01\u6d9b\u518d\u5411\u6c34\u91cc\u671b\u53bb\uff0c\u90a3\u4e9b\u5c11\u5973\uff1f\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u7adf\u6e38\u8d70\u5f97\u53ea\u5269\u4e0b\u4e00\u540d\u3002 \u3000\u3000\u8fd9\u53ea\u5269\u4e0b\u7684\u4e00\u540d\uff0c\u7adf\u4f0f\u5728\u6c34\u5e95\u4e0d\u52a8\u3002 \u3000\u3000\u4e01\u6d9b\u6115\u7136\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u4e3a\u4ec0\u4e48\u4e0d\u52a8\u4e86\uff1f\u201d \u3000\u3000\u7eff\u8863\u5973\u795e\u79d8\u4e00\u7b11\u9053\uff1a\u201c\u662f\u6211\u8981\u5979\u6682\u65f6\u4e0d\u52a8\u7684\uff0c\u8fd9\u4f6f\u624d\u65b9\u4fbf\u4f60\u4e0b\u53bb\u6349\u3002\u201d \u3000\u3000\u201c\u6211\u82e5\u6349\u5230\u4e86\uff0c\u771f\u7684\u7ed9\u6211\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u4f60\u80fd\u628a\u5979\u5e26\u8d70\uff0c\u5f53\u7136\u5c31\u662f\u4f60\u7684\u4eba\u3002\u201d \u3000\u3000\u201c\u5e26\u7740\u4eba\u4e0d\u65b9\u4fbf\uff0c\u6211\u53ea\u60f3\u628a\u5979\u7559\u2014\u4e2a\u665a\u4e0a\u5c31\u591f\u4e86\u3002\u201d \u3000\u3000\u201c\u522b\u591a\u8bf4\u5e9f\u6d3b\uff0c\u5feb\u4e0b\u53bb\u6349\uff0c\u5426\u5219\u5979\u5728\u6c34\u91cc\u618b\u6b7b\u4e86\uff0c\u4f60\u6349\u5230\u4e86\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u201c\u6251\u901a\u201d\u4e00\u58f0\uff0c\u4e01\u6d9b\u5df2\u6251\u8fdb\u6c34\u53bb\u3002 \u3000\u3000\u4ed6\u7684\u6c34\u6027\u7684\u786e\u4e0d\u9519\uff0c\u5f88\u5feb\u4fbf\u94bb\u5165\u6c34\u5e95\uff0c\u6293\u4f4f\u4e86\u90a3\u5c11\u5973\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u90a3\u5c11\u5973\u7adf\u7136\u52a8\u90fd\u4e0d\u52a8\uff0c\u4e00\u4efb\u4e01\u6d9b\u6355\u6349\u3002 \u3000\u3000\u4e01\u6d9b\u65e9\u5df2\u6b32\u5ff5\u5927\u53d1\uff0c\u5b58\u5fc3\u6765\u4e2a\u5148\u884c\u4e00\u5c1d\u751c\u5934\u4e3a\u5feb\uff0c\u6349\u4f4f\u90a3\u5c11\u5973\u540e\uff0c\u4fbf\u7d27\u7d27\u6402\u4e2a\u6ee1\u6000\u3002 \u3000\u3000\u987f\u65f6\uff0c\u4ed6\u51e0\u4e4e\u5168\u8eab\u7684\u9aa8\u5934\u90fd\u6839\u6839\u9165\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u96be\u514d\u4f7f\u4ed6\u545b\u4e86\u597d\u51e0\u53e3\u6c34\u3002 \u3000\u3000\u5f53\u4ed6\u628a\u90a3\u5c11\u5973\u62b1\u4e0a\u5cb8\u6765\uff0c\u8fd8\u8fde\u8fde\u5598\u7740\u6c14\u9053\uff1a\u201c\u522b\u88c5\u849c\u4e86\uff0c\u4eca\u591c\u4f60\u5c31\u662f\u6211\u7684\u4eba\uff01\u201d \u3000\u3000\u5c82\u77e5\u90a3\u5c11\u5973\u653e\u5230\u5730\u4e0a\u4ee5\u540e\uff0c\u5979\u5374\u771f\u7684\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u4e01\u6d9b\u5927\u5403\u4e00\u60ca\uff0c\u558a\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u5979\u5df2\u7ecf\u6ca1\u4e86\u6c14\uff01\u201d \u3000\u3000\u5c45\u7136\u51fa\u4e86\u4eba\u547d\uff0c\u600e\u4e0d\u4ee4\u4ed6\u60f6\u9a87\u83ab\u540d\u3002\u4e01\u6d9b\u7acb\u523b\u53c8\u53eb\u9053\uff1a\u201c\u5973\u7edf\u9886\uff0c\u8fd9\u4e0d\u80fd\u6028\u6211\uff0c\u662f\u5979\u81ea \u5df1\u5728\u6c34\u91cc\u618b\u6b7b\u7684\uff01\u201d \u3000\u3000\u7eff\u8863\u5973\u7b11\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e0d\u4f1a\u8981\u4f60\u7ed9\u5979\u507f\u547d\uff0c\u4f60\u53ea\u7ba1\u653e\u5fc3\u3002\u201d \u3000\u3000\u63a5\u7740\uff0c\u76ee\u5149\u8f6c\u5411\u90a3\u4e1a\u5df2\u505c\u6b62\u547c\u5438\u7684\u5c11\u5973\u9a82\u9053\uff1a\u201c\u6ca1\u7528\u7684\u4e1c\u897f\uff0c\u53ea\u8fd9\u4e48\u2014\u4f1a\u513f\u7684\u5de5\u592b\u5c31\u618b \u6b7b\uff0c\u5f80\u65e5\u6f5c\u6c34\u7684\u672c\u9886\u54ea\u91cc\u53bb\u4e86\uff01\u201d \u3000\u3000\u8fb9\u8bf4\u8fb9\u4e00\u62ac\u817f\uff0c\u7acb\u5373\u628a\u90a3\u5c11\u5973\u53c8\u8e22\u4e0b\u6c34\u53bb\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4e01\u6d9b\u548c\u7518\u9716\u90fd\u5df2\u770b\u5f97\u6c57\u6bdb\u76f4\u7ad6\u3002 \u3000\u3000\u7eff\u8863\u5973\u518d\u53eb\u9053\uff1a\u201c\u6765\u4eba\u628a\u5979\u62d6\u56de\u53bb\uff01\u201d \u3000\u3000\u4f46\u6c34\u5e95\u679c\u7136\u53c8\u6e38\u51fa\u4e00\u540d\u5c11\u5973\uff0c\u6293\u4f4f\u90a3\u6b7b\u53bb\u5c11\u5973\u7684\u4e00\u6761\u817f\uff0c\u5f88\u5feb\u4fbf\u6e38\u8fd1\u6d1e\u58c1\u9699\u5165\u6df1\u5904\u4e0d\u89c1\u3002 \u3000\u3000\u8fd9\u4e00\u5e55\u7ecf\u8fc7\uff0c\u7eff\u8863\u5973\u7adf\u7136\u50cf\u82e5\u65e0\u5176\u4e8b\uff0c\u76ee\u5149\u624e\u89c6\u8fc7\u4e01\u6d9b\u548c\u7518\u9716\u9053\uff1a\u201c\u4e0d\u5fc5\u518d\u601d\u521a\u624d\u7684\u4e8b\uff0c \u4f60\u4eec\u81ea\u4fe1\u80fd\u8fc7\u5f97\u4e86\u5173\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u5728\u4e0b\u521a\u624d\u7684\u6e38\u6cf3\u6280\u672f\uff0c\u4f60\u5df2\u89c1\u8fc7\uff0c\u8fd9\u4e48\u8fd1\u7684\u4e00\u6bb5\u6c34\uff0c\u5f53\u7136\u53ef\u4ee5\u8fc7\u5173\u3002\u201d \u3000\u3000\u7eff\u8863\u5973\u800c\u6cdb\u4e0d\u5c51\u795e\u8272\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u4eec\u4e24\u4eba\u7edd\u5bf9\u8fc7\u4e0d\u53bb\u3002\u201d \u3000\u3000\u201c\u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u201c\u5982\u679c\u4f60\u4eec\u771f\u80fd\u8fc7\u53bb\uff0c\u6211\u60c5\u613f\u518d\u8d54\u4e24\u4e2a\u59d1\u5a18\u7ed9\u4f60\u4eec\u3002\u201d \u3000\u3000\u201c\u6b7b\u59d1\u5a18\u51c6\u8981\uff01\u201d \u3000\u3000\u201c\u4e0d\u662f\u6b7b\u7684\uff0c\u662f\u6d3b\u7684\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4e00\u62c9\u7518\u9716\u9053\uff1a\u201c\u5feb\u4e0b\u6c34\uff0c\u4eca\u5929\u665a\u4e0a\u603b\u7b97\u53c8\u6709\u59d1\u5a18\u966a\u5bbf\u4e86\uff01\u201d \u3000\u3000\u63a5\u7740\u201c\u6251\u901a\u201d\u201c\u6251\u901a\u201d\u4e24\u58f0\uff0c\u4e01\u6d9b\u548c\u7518\u9716\u7acb\u5373\u8dc3\u4e0b\u6c34\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u6e38\u5f97\u8fd8\u771f\u5feb\uff0c\u4e0d\u5927\u4e00\u4f1a\u4fbf\u63a5\u8fd1\u5f7c\u5cb8\u3002 \u3000\u3000\u5c82\u77e5\u5c31\u5728\u5373\u5c06\u63a5\u8fd1\u5bf9\u5cb8\u65f6\uff0c\u84e6\u5730\u7531\u6c34\u5e95\u6765\u4e86\u80a1\u5947\u5927\u7684\u6fc0\u6d41\uff0c\u51b2\u6fc0\u5f97\u4e24\u4eba\u4e0d\u8fdb\u53cd\u9000\uff0c\u773c\u770b \u79bb\u5bf9\u5cb8\u53ea\u6709\u4e0d\u8db3\u4e00\u4e08\u7684\u8ddd\u79bb\uff0c\u5c31\u662f\u65e0\u6cd5\u51b2\u8fc7\u53bb\u3002 \u3000\u3000\u4f46\u4e24\u4eba\u5e76\u4e0d\u6b7b\u5fc3\uff0c\u62da\u5c3d\u5168\u529b\u5411\u524d\u731b\u6e38\u3002 \u3000\u3000\u4f46\u5374\u8d8a\u6e38\u79bb\u5bf9\u5cb8\u8d8a\u8fdc\uff0c\u53c8\u56e0\u6fc0\u6d41\u6e85\u8d77\u7684\u6c34\u82b1\u6025\u901f\u6d8c\u6765\uff0c\u4f7f\u5f97\u4ed6\u4eec\u8fde\u8fde\u545b\u6c34\uff0c\u51e0\u4e4e\u5df2\u628a\u809a \u5b50\u704c\u6ee1\u3002 \u3000\u3000\u6700\u540e\uff0c\u4ed6\u4eec\u90fd\u5df2\u7b4b\u75b2\u529b\u5c3d\uff0c\u6ca1\u5948\u4f55\uff0c\u9664\u4e86\u8fd4\u8eab\u6e38\u56de\uff0c\u5b9e\u5728\u6ca1\u6709\u522b\u7684\u529e\u6cd5\u3002 \u3000\u3000\u5f53\u4e24\u4eba\u4e0a\u4e86\u5cb8\uff0c\u8fd8\u5728\u52fe\u7740\u8116\u5b50\u545b\u54b3\u4e0d\u5df2\uff0c\u597d\u4e45\u597d\u4e45\u624d\u7f13\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u7eff\u8863\u5973\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u4eec\u8fc7\u4e0d\u4e86\u5173\uff0c\u8fd9\u8bdd\u5e76\u4e0d\u5047\u5427\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u618b\u5f97\u8138\u4e0a\u9752\u7b4b\u76f4\u5192\uff0c\u6839\u672c\u8bf4\u4e0d\u51fa\u4ec0\u4e48\u8bdd\u6765\u3002 \u3000\u3000\u7eff\u8863\u5973\u518d\u671b\u5411\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u73b0\u5728\u770b\u4f60\u4eec\u4e24\u4f4d\u7684\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u77a5\u4e86\u77a5\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0e\u4f60\u770b\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4f3c\u4e5f\u662f\u4e00\u7b79\u83ab\u5c55\u3002 \u3000\u3000\u4e0d\u96be\u60f3\u89c1\uff1f\u8fde\u5cb3\u5c0f\u98de\u90fd\u6ca1\u529e\u6cd5\uff0c\u8881\u5c0f\u9e64\u7684\u8f7b\u529f\uff0c\u672c\u6765\u5c31\u4e0d\u80fd\u4e0e\u5cb3\u5c0f\u98de\u76f8\u6bd4\uff0c\u8fd8\u4f1a\u6709\u4ec0\u4e48 \u529e\u6cd5\u3002 \u3000\u3000\u7eff\u8863\u5973\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u4eec\u4e24\u4eba\u8fde\u8fc7\u6587\u5173\u6b66\u5173\u706b\u5173\uff0c\u90fd\u8868\u73b0\u5f97\u975e\u5e38\u51fa\u8272\uff0c\u82e5\u95ef\u4e0d\u8fc7 \u6c34\u5173\uff0c\u90a3\u5c31\u529f\u4e8f\u4e00\u7bd1\uff0c\u672a\u514d\u592a\u53ef\u60dc\u4e86\uff01\u201d \u3000\u3000\u7eff\u8863\u5973\u8fd9\u51e0\u53e5\u8bdd\uff0c\u672c\u6765\u662f\u4e3a\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u60cb\u60dc\uff0c\u4f46\u542c\u5728\u4e24\u4eba\u8033\u6735\u91cc\uff0c\u5374\u4eff\u4f5b\u5bf9\u65b9\u662f\u5728 \u6709\u610f\u8ba5\u8bbd\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u9893\u7136\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u5982\u679c\u54b1\u4eec\u6709\u5c11\u6797\u8fbe\u6469\u7956\u5e08\u90a3\u79cd\u767b\u840d\u6e21\u6c34\u4e00\u82c7\u6e21\u6c5f\u7684\u672c \u9886\uff0c\u8fd8\u7528\u53d1\u4ec0\u4e48\u6101\u5462\uff1f\u201d \u3000\u3000\u5c82\u77e5\u4ed6\u8fd9\u4e24\u53e5\u8bdd\uff0c\u7b49\u4e8e\u628a\u5cb3\u5c0f\u98de\u70b9\u9192\uff0c\u4ed6\u4ec0\u4e48\u8bdd\u90fd\u6ca1\u8bf4\uff0c\u5374\u8f6c\u8eab\u5411\u540e\u8d70\u53bb\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u8bb6\u7136\u53eb\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u4f60\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5e76\u672a\u7b54\u8bdd\uff0c\u4e0d\u5927\u4e00\u4f1a\uff0c\u624b\u91cc\u4fbf\u62ff\u7740\u4e09\u5757\u6728\u677f\u56de\u6765\u3002 \u3000\u3000\u539f\u6765\u4ed6\u5148\u524d\u7ecf\u8fc7\u4e0d\u8fdc\u5904\u7684\u8c37\u9053\u65f6\u4e8b\u66fe\u53d1\u73b0\u8def\u4fa7\u6709\u597d\u51e0\u5757\u6728\u677f\u3002 \u3000\u3000\u4ed6\u7ad9\u5728\u6c34\u5173\u524d\uff0c\u62ff\u634f\u51c6\u8ddd\u79bb\uff0c\u626c\u8155\u5c06\u4e09\u5757\u6728\u677f\u629b\u4e0b\u6c34\u53bb\u3002 \u3000\u3000\u4ed6\u6254\u5f97\u5341\u5206\u51c6\u786e\uff0c\u4e09\u5757\u6728\u677f\uff0e\u6b63\u597d\u5728\u4e24\u5cb8\u95f4\u5404\u53d6\u540c\u7b49\u8ddd\u79bb\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u4e24\u773c\u76f4\u7728\u7684\u9053\uff1a\u201c\u4f60\u60f3\u4ee5\u6728\u677f\u505a\u843d\u811a\u70b9\u8dc3\u8fc7\u53bb\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fd9\u662f\u552f\u4e00\u7684\u529e\u6cd5\u4e86\uff0c\u6210\u529f\u4e0e\u5426\uff0c\u5b8c\u5168\u5728\u6b64\u4e00\u4e3e\u3002\u201d \u3000\u3000\u4e8b\u5230\u5982\u4eca\uff0c\u8881\u5c0f\u9e64\u4e5f\u53ea\u6709\u4efb\u7531\u5cb3\u5c0f\u98de\u5192\u9669\u4e00\u8bd5\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u633d\u8d77\u957f\u886b\u4e0b\u6446\uff0c\u731b\u8fd0\u4e00\u53e3\u771f\u6c14\uff0c\u968f\u5373\u5411\u524d\u5e73\u5c04\u800c\u51fa\u3002 \u3000\u3000\u4ed6\u672c\u6709\u4e00\u8dc3\u4e03\u516b\u4e08\u7684\u8f7b\u529f\uff0c\u6b64\u523b\u7b2c\u4e00\u5757\u6728\u677f\u79bb\u5cb8\u4e0d\u8fc7\u56db\u4e08\u5de6\u53f3\uff0c\u81ea\u7136\u8e0f\u4e0a\u53bb\u5e76\u4e0d\u5403\u529b\u3002 \u3000\u3000\u90a3\u77e5\u8e0f\u4e0a\u4e4b\u540e\uff0c\u987f\u65f6\u9677\u4e0b\u53bb\u4e00\u5c3a\u591a\u6df1\uff0c\u8fde\u819d\u76d6\u90fd\u6ca1\u8fdb\u6c34\u53bb\u3002 \u3000\u3000\u4ed6\u731b\u7136\u518d\u5438\u4e00\u53e3\u771f\u6c14\uff0c\u594b\u529b\u4e0a\u51b2\uff0c\u521a\u597d\u53cc\u624b\u5df2\u89e6\u5230\u6d1e\u9876\uff0c\u7136\u540e\u501f\u53cc\u624b\u63a8\u5411\u6d1e\u9876\u53cd\u5f39\u4e4b\u529b\uff0c \u518d\u843d\u4e0a\u7b2c\u4e8c\u5757\u6728\u677f\u3002 \u3000\u3000\u539f\u6765\u4ed6\u867d\u6709\u5e73\u5730\u4e00\u8dc3\u4e03\u516b\u4e08\u7684\u8f7b\u529f\uff0c\u4f46\u9677\u8fdb\u6c34\u53bb\u4e4b\u540e\u62d4\u8eab\u518d\u8dc3\uff0c\u529b\u91cf\u5df2\u5927\u6253\u6298\u6263\uff0c\u6545\u800c \u624d\u60c5\u6025\u751f\u667a\uff0c\u91c7\u7528\u8fd9\u79cd\u4ee5\u529b\u501f\u529b\u7684\u529e\u6cd5\u3002 \u3000\u3000\u5f53\u4ed6\u843d\u4e0a\u7b2c\u4e8c\u5757\u6728\u677f\uff0c\u4ecd\u7136\u7528\u540c\u6837\u7684\u529e\u6cd5\u3002 \u3000\u3000\u4e0d\u8fc7\u8fd9\u6b21\u811a\u8e0f\u4e0a\u6728\u677f\u540e\uff0c\u5411\u4e0b\u6c89\u7684\u66f4\u6df1\u3002 \u3000\u3000\u4ed6\u7528\u5c3d\u5e73\u751f\u4e4b\u529b\uff0c\u7ec8\u4e8e\u590d\u53c8\u4ece\u6c34\u4e2d\u8dc3\u8d77\uff0c\u53cc\u624b\u518d\u642d\u4e0a\u6d1e\u9876\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u5e76\u672a\u518d\u8e0f\u7b2c\u4e09\u5757\u6728\u677f\uff0c\u7adf\u7136\u6293\u4f4f\u4e86\u6d1e\u9876\u50cf\u949f\u4e73\u77f3\u822c\u7a81\u51fa\u7684\u5c16\u77f3\uff0c\u7136\u540e\u53cc\u817f\u731b\u63d0\uff0c \u8e6c\u4e0a\u4e86\u6d1e\u9876\u3002 \u3000\u3000\u8fd9\u4e00\u8e6c\uff0c\u4e0d\u96be\u60f3\u5230\uff0c\u529b\u9053\u5947\u5927\uff0c\u4ed6\u7adf\u4ef0\u8eab\u5012\u7eb5\u800c\u51fa\uff0c\u76f4\u5c04\u5411\u96a7\u9053\u53e3\u5916\uff0c\u518d\u4e00\u4e2a\u7ffb\u8f6c\uff0c\u4fbf \u8f7b\u98d8\u98d8\u7684\u843d\u4e0b\u5730\u6765\u3002 \u3000\u3000\u4ed6\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff1f\u5947\u8ff9\u822c\u7684\u5c45\u7136\u901a\u8fc7\u4e86\u6c34\u5173\u3002 \u3000\u3000\u8fd9\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4f3c\u4e4e\u9887\u611f\u610f\u5916\u3002 \u3000\u3000\u4ed6\u7565\u4e00\u8c03\u606f\uff0c\u968f\u5373\u5411\u5bf9\u9762\u558a\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0c\u5c31\u7167\u5c0f\u5f1f\u8fd9\u529e\u6cd5\u8fc7\u6765\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9752\u7740\u8138\u8272\u53eb\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u4f60\u505a\u5f97\u5230\uff0c\u6211\u5374\u81ea\u6599\u6ca1\u6cd5\u505a\u5230\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5927\u4e3a\u7740\u6025\u9053\uff1a\u201c\u522b\u72b9\u8c6b\uff0c\u4f60\u82e5\u4e0d\u8fc7\u6765\uff0c\u6211\u4fe9\u5c82\u4e0d\u4ece\u6b64\u5c31\u8981\u5206\u624b\u4e86\uff0c\u8881\u5927\u54e5\uff0c\u6211\u4eec \u53c8\u600e\u53ef\u5206\u624b\u5462\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u5b9e\u5728\u8fc7\u4e0d\u53bb\uff01\u201d \u3000\u3000\u201c\u53ea\u8981\u62ff\u51fa\u52c7\u6c14\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u7a81\u751f\u795e\u529b\uff0c\u4f60\u82e5\u5fc3\u91cc\u5148\u602f\uff0c\u90a3\u5c31\u4ec0\u4e48\u4e8b\u90fd\u529e\u4e0d\u6210\u4e86\uff01\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u7ec8\u4e8e\u6fc0\u8d77\u4e86\u8881\u5c0f\u9e64\u7684\u52c7\u6c14\u3002 \u3000\u3000\u4ed6\u594b\u529b\u4e00\u8dc3\uff0c\u5df2\u8e0f\u4e0a\u7b2c\u4e00\u5757\u6728\u677f\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u5411\u4e0b\u9677\u5f97\u66f4\u6df1\uff0c\u51e0\u4e4e\u6c89\u5230\u4e86\u5927\u817f\u6839\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u662f\u62da\u51fa\u5403\u5976\u7684\u52b2\uff0c\u817e\u8eab\u5347\u8d77\uff0c\u53cc\u624b\u4e5f\u89e6\u5230\u4e86\u6d1e\u9876\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u5cb3\u5c0f\u98de\u51e0\u4e4e\u4e0d\u6562\u518d\u770b\uff0c\u53ea\u597d\u54ac\u7740\u7259\u95ed\u8d77\u773c\u6765\u3002 \u3000\u3000\u4f46\u5f53\u4ed6\u7741\u5f00\u773c\uff0c\u771f\u60f3\u4e0d\u5230\uff0c\u8881\u5c0f\u9e64\u5df2\u7ecf\u5230\u4e86\u7b2c\u4e09\u5757\u6728\u677f\uff0c\u79bb\u5cb8\u8fb9\u53ea\u6709\u4e09\u56db\u4e08\u4e86\u3002 \u3000\u3000\u5c82\u77e5\u8fd9\u4e09\u56db\u4e08\uff0c\u7adf\u7136\u6b63\u662f\u4ed6\u7684\u6027\u547d\u4ea4\u5173\u6240\u5728\u3002 \u3000\u3000\u539f\u56e0\u662f\u4ed6\u7684\u6c14\u529b\u5df2\u7ecf\u5b8c\u5168\u7528\u5c3d\uff0c\u6839\u672c\u5df2\u518d\u65e0\u529b\u6323\u624e\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u773c\u89c1\u8881\u5c0f\u9e64\u4eba\u5728\u6c34\u91cc\uff0c\u8d8a\u5f80\u4e0a\u7a9c\u4fbf\u8d8a\u5f80\u4e0b\u6c89\uff0c\u770b\u770b\u6c34\u5df2\u6df9\u8fc7\u8170\u9645\u3002 \u3000\u3000\u4ed6\u5fc3\u5934\u5927\u6025\uff0c\u60c5\u4e0d\u81ea\u7981\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u76f4\u5411\u8881\u5c0f\u9e64\u8eab\u524d\u6c34\u4e2d\u63a8\u53bb\u3002 \u3000\u3000\u4ed6\u771f\u6ca1\u6599\u5230\u8fd9\u6743\u638c\u4e00\u63a8\uff0c\u7adf\u7136\u771f\u7684\u5e2e\u4e86\u8881\u5c0f\u9e64\u5927\u5fd9\uff0c\u56e0\u4e3a\u90a3\u638c\u529b\u76f4\u900f\u6c34\u5e95\uff0c\u6c34\u5e95\u7684\u6c34\u88ab \u51b2\u6fc0\u5411\u4e0a\u7ffb\u6d8c\uff0c\u5c45\u7136\u628a\u8881\u5c0f\u9e64\u6258\u5230\u6c34\u9762\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u62c5\u5fc3\u8881\u5c0f\u9e64\u518d\u6c89\u4e0b\u53bb\uff0c\u53cc\u638c\u518d\u5ea6\u594b\u529b\u5411\u524d\u63a8\u53bb\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u811a\u4e0b\u88ab\u638c\u98ce\u6240\u6258\uff0c\u8eab\u5b50\u7acb\u5373\u7a33\u4f4f\uff0c\u63a5\u7740\u731b\u5730\u5411\u524d\u4e00\u6251\uff0c\u5df2\u5230\u4e86\u5cb8\u8fb9\u3002 \u3000\u3000\u4ed6\u5230\u4e86\u5cb8\u4e0a\uff0c\u7adf\u5982\u540c\u505a\u4e86\u573a\u68a6\u4e00\u822c\uff0c\u72c2\u5598\u4e86\u534a\u664c\uff0c\u624d\u5b9a\u795e\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u73b0\u5728\uff0c\u6700\u6025\u7684\u4eba\uff0c\u8be5\u662f\u4e01\u6d9b\u548c\u7518\u9716\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u5b9e\u5728\u65e0\u8ba1\u53ef\u65bd\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u53c8\u534a\u70b9\u5e2e\u4e0d\u4e0a\u5fd9\u3002 \u3000\u3000\u7eff\u8863\u5973\u51b7\u7b11\u9053\uff1a\u201c\u770b\uff0c\u4eba\u5bb6\u4e24\u4e2a\u90fd\u8fc7\u53bb\u4e86\uff0c\u4f60\u4eec\u4e0d\u80fd\u5728\u8fd9\u91cc\u8001\u5019\u7740\uff01\u201d \u3000\u3000\u4e01\u6d9b\u54fc\u4e86\u5362\u9053\uff1a\u201c\u4f60\u660e\u77e5\u6211\u4eec\u8fc7\u4e0d\u53bb\uff0c\u7528\u4e0d\u7740\u62ff\u8bdd\u8bbd\u523a\uff01\u201d \u3000\u3000\u7eff\u8863\u5973\u5e76\u4e0d\u89c1\u55d4\uff0c\u53cd\u800c\u7b11\u9053\uff1a\u201c\u53ef\u662f\u6211\u603b\u4e0d\u80fd\u8001\u7b49\u7740\u4f60\u4eec\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u7b49\uff0c\u6211\u4eec\u4e0d\u80fd\u5f80\u524d\u8d70\uff0c\u5f80\u540e\u8d70\u603b\u6210\u5427\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u63d0\u8d77\u55d3\u95e8\u53eb\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u90a3\u4e24\u5343\u591a\u4e24\u94f6\u7968\uff0c\u5c31\u8bf7\u4f60\u4ee3\u4e3a\u4fdd\u7ba1\uff0c\u5982\u679c\u4f60\u8981\u7528\uff0c\u53ea \u7ba1\u7528\uff0c\u7528\u5149\u4e86\u4e5f\u6ca1\u5173\u7cfb\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u9ad8\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u522b\u6015\uff0c\u4e8c\u4f4d\u90fd\u4e60\u6c34\u6027\uff0c\u5374\u4fbf\u6389\u8fdb\u6c34\u53bb\u4e5f\u6ca1\u4ec0\u4e48\u53ef\u6015\u7684\uff01\u201d \u3000\u3000\u4e01\u6d9b\u6446\u7740\u624b\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u5b9e\u5728\u6ca1\u529e\u6cd5\uff0c\u5982\u679c\u5c06\u6765\u6709\u673a\u4f1a\uff0c\u6211\u4eec\u4e00\u5b9a\u518d\u8ffd\u968f\u4f60\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u8bdd\uff1f\u62cd\u62cd\u7518\u9716\u80a9\u8180\u9053\uff1b\u201c\u8001\u7518\uff0c\u8fd8\u6123\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff0c\u53bb\u4ed6\u5a18\u7684\uff0c\u54b1\u4eec\u56de\u5934\u8d70\uff01\u201d \u3000\u3000\u7518\u9716\u4ec0\u4e48\u8bdd\u4e5f\u6ca1\u8bb2\uff0c\u56de\u8eab\u8ddf\u7740\u4e01\u6d9b\u4fbf\u8d70\u3002 \u3000\u3000\u90a3\u77e5\u4ed6\u4eec\u521a\u8d70\u51fa\u51e0\u6b65\uff0c\u4fbf\u542c\u8eab\u540e\u7eff\u8863\u5973\u53eb\u9053\uff1a\u201c\u56de\u6765\uff01\u201d \u3000\u3000\u4e24\u4eba\u867d\u7136\u6b62\u6b65\u56de\u8eab\uff0c\u4e01\u6d9b\u5374\u51b7\u58f0\u51b7\u6c14\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u53ef\u7f57\u55e6\u7684\uff1f\u201d \u3000\u3000\u7eff\u8863\u5973\u9053\uff1a\u201c\u4f60\u4eec\u5c31\u8fd9\u6837\u8d70\u4e86\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1b\u201c\u4e0d\u8fd9\u6837\u8d70\u96be\u9053\u8fd8\u8981\u722c\u7740\u8d70\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u8bf4\u4f60\u4eec\u4e24\u4eba\u65e2\u7136\u8fde\u706b\u5173\u90fd\u95ef\u8fc7\u4e86\uff0c\u82e5\u8fd9\u6837\u56de\u53bb\uff0c\u672a\u514d\u592a\u53ef\u60dc\u4e86\uff01\u201d \u3000\u3000\u201c\u6211\u4eec\u5f53\u7136\u77e5\u9053\u53ef\u60dc\uff0c\u4f46\u8fc7\u4e0d\u53bb\u53c8\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u4e2a\u59a8\u518d\u6e38\u2014\u6b21\uff0c\u4e5f\u8bb8\u53ef\u4ee5\u8fc7\u53bb\u3002\u201d \u3000\u3000\u4e01\u6d9b\u6b63\u8981\u5f00\u53e3\uff0c\u7518\u9716\u5df2\u62a2\u7740\u9053\uff1a\u201c\u8001\u4e01\uff01\u65e2\u7136\u5982\u6b64\uff0c\u54b1\u4eec\u5c31\u518d\u6e38\u4e00\u6b21\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u518d\u6e38\u4e5f\u662f\u767d\u6e38\uff0c\u4f55\u5fc5\u591a\u6b64\u4e00\u4e3e\uff1f\u201d \u3000\u3000\u7518\u9716\u62c9\u7740\u4e01\u6d9b\u9053\uff1a\u201c\u8bd5\u8bd5\u770b\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u4ed6\u628a\u4e01\u6d9b\u5f3a\u62c9\u81f3\u96a7\u9053\u95e8\uff0c\u7acb\u5373\u81ea\u5df1\u5148\u8dc3\u4e0b\u4e86\u6c34\uff0c\u5f53\u6e38\u5230\u5f7c\u5cb8\u9644\u8fd1\uff0c\u5c45\u7136\u5df2\u4e0d\u518d\u6709\u6c34\u52bf\u53cd \u51b2\u3002 \u3000\u3000\u7518\u9716\u6beb\u4e0d\u5403\u529b\u7684\u4e0a\u5cb8\uff0c\u62db\u624b\u9ad8\u53eb\u9053\uff1a\u201c\u8001\u4e01\uff0c\u8fd8\u6123\u5728\u90a3\u91cc\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u968f\u540e\u4e5f\u6e38\u4e86\u8fc7\u53bb\uff0c\u4e0a\u5cb8\u540e\u7acb\u5373\u832b\u7136\u95ee\u9053\uff1a \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u80fd\u6e38\u8fc7\u6765\uff1f\u201d \u3000\u3000\u7518\u9716\u7b11\u9053\uff1a\u201c\u4f60\u7b80\u76f4\u6d51\u7403\uff0c\u96be\u9053\u8fd8\u770b\u4e0d\u51fa\u6765\u5979\u521a\u624d\u662f\u5728\u6709\u610f\u653e\u6c34\uff01\u201d \u3000\u3000\u4e01\u6d9b\u6478\u4e86\u6478\u8111\u888b\uff0c\u5c34\u5c2c\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u4ed6\uff0a\uff0a\uff0a\u786e\u5b9e\u6bd4\u54b1\u806a\u660e\uff01\u201d \u3000\u3000\u4e00\u9635\u9686\u9686\u9707\u8033\u4e4b\u58f0\u540e\uff0c\u90a3\u7f29\u8fdb\u4e24\u8fb9\u6d1e\u58c1\u7684\u94c1\u677f\uff0c\u53c8\u4f38\u5c55\u51fa\u6765\uff0c \u3000\u3000\u5f88\u5feb\u4fbf\u76d6\u4f4f\u4e86\u96a7\u9053\u5185\u7684\u6c34\uff0c\u7531\u6d1e\u9876\u548c\u6d1e\u58c1\u5c04\u51fa\u7684\u5f3a\u5149\uff0c\u4e5f\u968f\u4e4b\u7184\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u770b\u4e0d\u89c1\u7eff\u8863\u5973\uff0c\u5374\u5954\u8fc7\u6765\u53e6\u4e00\u540d\u7d2b\u8863\u5c11\u5973\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u3001\u8881\u5c0f\u9e64\u3001\u4e01\u6d9b\u3001\u7518\u9716\u6b63\u8981\u7ee7\u7eed\u524d\u8fdb\uff0c\u90a3\u7d2b\u8863\u5c11\u5973\u5df2\u6765\u5230\u8ddf\u524d\u558a\u9053\uff1a\u201c\u56db\u4f4d\u8bf7\u6162 \u8d70\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u662f\u6709\u4ec0\u4e48\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u7d2b\u8863\u5c11\u5973\u9053\uff1a\u201c\u73b0\u5728\u8d70\u592a\u65e9\u4e86\uff0c\u524d\u9762\u662f\u2018\u5bb5\u5173\u2019\uff0c\u5fc5\u987b\u8981\u7b49\u5230\u5929\u665a\u540e\u624d\u6253\u5f00\u5173\u95e8\uff0c\u4f60\u4eec \u5230\u4e86\u4ee5\u540e\uff0c\u4eba\u5bb6\u4e0d\u5f00\u95e8\u53c8\u600e\u80fd\u8fdb\u53bb\u5462\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4f30\u8ba1\u65f6\u95f4\uff0c\u6b64\u523b\u79bb\u4e2d\u5348\u8fd8\u65e9\uff0c\u79bb\u5929\u665a\u66f4\u6709\u5927\u534a\u5929\u7684\u5149\u9634\uff0c\u4e0d\u89c9\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u4ec0 \u4e48\u53eb\u5bb5\u5173\uff1f\u201d \u3000\u3000\u7d2b\u8863\u5c11\u5973\u9053\uff1a\u201c\u7531\u5b57\u9762\u5c31\u53ef\u4ee5\u60f3\u5230\uff0c\u5bb5\u662f\u665a\u4e0a\uff0c\u5bb5\u5173\u53ea\u6709\u665a\u4e0a\u624d\u53ef\u4ee5\u8fdb\u53bb\u7684\u5173\uff0c\u5c31\u50cf\u591c \u5e02\u4e00\u6837\uff0c\u53ea\u505a\u591c\u95f4\u7684\u751f\u610f\u3002\u201d \u3000\u3000\u7d2b\u8863\u5c11\u5973\u7684\u8fd9\u4e00\u63cf\u8ff0\uff0e\u96be\u514d\u5f15\u8d77\u8881\u5c0f\u9e64\u7684\u5174\u8da3\u4e0e\u597d\u5947\uff1a\u201c\u90a3\u4e48\u5bb5\u5173\u91cc\u90fd\u662f\u4e9b\u4ec0\u4e48\u666f\u8c61\uff0c \u8981\u95ef\u8fc7\u5bb5\u5173\u5bb9\u4e0d\u5bb9\u6613\uff1f\u201d \u3000\u3000\u7d2b\u8863\u5c11\u5973\u9053\uff1a\u201c\u8981\u95ef\u5bb5\u5173\uff0e\u636e\u8bf4\u5168\u4ed7\u968f\u673a\u5e94\u53d8\uff0c\u8bf4\u5bb9\u6613\u5f88\u5bb9\u6613\uff0c\u8bf4\u56f0\u96be\u8fd8\u771f\u6709\u70b9\u56f0\u96be\uff0c \u81f3\u4e8e\u91cc\u9762\u662f\u4ec0\u4e48\u666f\u8c61\uff0c\u6211\u4e5f\u4e0d\u5927\u6e05\u695a\u3002\u201d \u3000\u3000\u5c82\u77e5\u8fd9\u65f6\u4e01\u6d9b\u7684\u597d\u5947\u548c\u5174\u8da3\u66f4\u5927\uff0c\u4ed6\u4e0b\u610f\u8bc6\u60f3\u5230\u5bb5\u5173\u5185\u5fc5\u5b9a\u98ce\u5149\u65d6\u65ce\uff0c\u6625\u8272\u65e0\u8fb9\uff0c\u53ea\u662f \u4ed6\u6b64\u523b\u5df2\u6210\u843d\u6c64\u9e21\uff0c\u4e0d\u4fbf\u63d2\u5634\u7f62\u4e86\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u9ed8\u4e86\u534a\u664c\uff0c\u518d\u9053\uff1a\u201c\u53ef\u662f\u6211\u4eec\u73b0\u5728\u4e0d\u8d70\uff0c\u8001\u5446\u5728\u8fd9\u91cc\u4e5f\u4e0d\u662f\u529e\u6cd5\u3002\u201d \u3000\u3000\u7d2b\u8863\u5c11\u5973\u9053\uff1a\u201c\u6211\u5c31\u662f\u5949\u547d\u6765\u63a5\u5f85\u4f60\u4eec\u7684\uff0c\u968f\u6211\u6765\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u7b49\u56db\u4eba\u8ddf\u7740\u8fdb\u53bb\u540e\uff0c\u53ea\u89c1\u8fd9\u5c71\u6d1e\u867d\u5927\uff0c\u91cc\u9762\u5374\u4e00\u65e0\u9648\u8bbe\u3002\u53ea\u662f\u5728\u9760\u58c1\u5904\u5806\u4e86\u4e00\u5806 \u5e72\u67f4\u3002 \u3000\u3000\u7d2b\u8863\u5c11\u5973\u53d6\u706b\u628a\u5e72\u67f4\u71c3\u8d77\u9053\uff1a\u201c\u73b0\u5728\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u5404\u4f4d\u5148\u628a\u8863\u670d\u70e4\u5e72\u518d\u8bf4\u3002\u201d \u3000\u3000\u56db\u4eba\u672c\u6765\u90fd\u6709\u4e9b\u51bb\u5f97\u53d1\u6296\uff0c\u71c3\u8d77\u5e72\u67f4\u4e4b\u540e\uff0c\u7acb\u5373\u6709\u4e86\u6696\u70d8\u70d8\u7684\u611f\u89c9\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u4eec\u6c34\u5173\u7684\u7edf\u9886\u662f\u8c01\uff1f\u201d \u3000\u3000\u7d2b\u8863\u5c11\u5973\u4e0d\u89c9\u7728\u7740\u661f\u7738\u63a9\u4f4f\u6a31\u53e3\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u65b9\u624d\u4e0d\u662f\u2014\u76f4\u548c\u6211\u4eec\u7edf\u9886\u5728\u4e00\u8d77\u4e48\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u5979\u771f\u662f\u6c34\u5173\u7684\u7edf\u9886\uff1f\u201d \u3000\u3000\u7d2b\u8863\u5c11\u5973\u9053\uff1a\u201c\u5979\u5f53\u7136\u662f\u7edf\u9886\uff0c\u90a3\u8fd8\u6253\u4ec0\u4e48\u5047\u7684\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5730\u770b\u6765\u8fd8\u4e0d\u5230\u4e8c\u5341\u5c81\uff0c\u4ee5\u8fd9\u6837\u7684\u5e74\u7eaa\uff0c\u600e\u80fd\u5f53\u4e0a\u7edf\u9886\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u6559\u4e3b\uff0c\u7528\u4eba\u552f\u624d\uff0c\u5979\u6709\u624d\u534e\uff0c\u6b66\u529f\u53c8\u9ad8\u4e0d\u53ef\u6d4b\uff0c\u867d\u7136\u5e74\u7eaa\u8f7b\uff0c\u4e5f\u7167\u6837\u53ef\u4ee5\u5f53\u7edf\u9886\uff0c \u6211\u4eec\u6c34\u5173\u4e0a\u6709\u5341\u51e0\u4e2a\u5b50\u59b9\uff0c\u7537\u7684\u4e5f\u6709\u597d\u51e0\u4e2a\u3002\u8c01\u4e0d\u5bf9\u5979\u5fc3\u670d\u53e3\u670d\u3002\u201d \u3000\u3000\u5ffd\u542c\u4e01\u6d9b\u95ee\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u60f3\u95ee\u4f60\u4e00\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u7d2b\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u95ee\u5c31\u95ee\u5427\uff0c\u4f55\u5fc5\u8fd8\u8981\u5148\u6253\u62db\u547c\uff0c\u6211\u770b\u4f60\u8fd9\u4f4d\u5927\u53d4\u597d\u50cf\u6709\u70b9\u6123\u5934\u6123\u8111 \u7684\u3002\u201d \u3000\u3000\u4e01\u6d9b\u8138\u4e0a\u4e00\u70ed\uff0c\u4f46\u5374\u5e76\u672a\u751f\u6c14\uff1a\u201c\u521a\u624d\u5728\u6c34\u91cc\u6e38\u7684\u51e0\u4f4d\u59d1\u5a18\uff0c\u5979\u4eec\u662f\u600e\u6837\u4e0b\u6c34\u7684\uff1f\u201d \u3000\u3000\u7d2b\u8863\u5c11\u5973\u53c8\u662f\u62bf\u5634\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u672c\u6765\u662f\u79d8\u5bc6\uff0c\u4f46\u4f60\u4eec\u65e2\u7136\u5df2\u7ecf\u8fc7\u4e86\u5173\uff0c\u4e5f\u5c31\u4e0d\u59a8\u516c\u5f00\uff0c \u56e0\u4e3a\u6d1e\u58c1\u4e0a\u53e6\u6709\u5c71\u6d1e\uff0c\u4eba\u5728\u77f3\u6d1e\u91cc\uff0c\u53ef\u4ee5\u76f4\u63a5\u4e0b\u6c34\u6e38\u5230\u5916\u9762\u53bb\uff0c\u5f53\u7136\u4e5f\u53ef\u4ee5\u7531\u5916\u9762\u518d\u6e38\u56de\u77f3 \u6d1e\uff0c\u8fd9\u4f4d\u5927\u53d4\uff0c\u4f60\u53ef\u770b\u6e05\u695a\u521a\u624d\u6e38\u6c34\u7684\u90a3\u4e9b\u59d1\u5a18\u90fd\u662f\u4ec0\u4e48\u6a21\u6837\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u5482\u7740\u820c\u5934\u9053\uff1a\u201c\u90a3\u51e0\u4f4d\u59d1\u5a18\u771f\u662f\u592a\u7f8e\u4e86\uff0c\u7b80\u76f4\u50cf\u5929\u4ed9\u4e0b\u754c\uff0c\u5982\u679c\u6211\u80fd\u518d\u770b\u5230\u5979\u4eec\uff0c \u5373\u4fbf\u770b\u4e0a\u4e09\u5929\u4e09\u591c\u4e5f\u770b\u4e0d\u591f\uff01\u201d \u3000\u3000\u7d2b\u8863\u5c11\u5973\u8138\u4e0a\u5148\u662f\u4e00\u7ea2\uff0c\u63a5\u7740\u5374\u53c8\u201c\u54e7\u201d\u7684\u4e00\u58f0\u7b11\u9053\uff1a\u201c\u591a\u8c22\u5927\u53d4\u8d5e\u7f8e\uff0c\u6211\u5c31\u662f\u90a3\u4e9b\u6e38 \u6c34\u59d1\u5a18\u5176\u4e2d\u7684\u2014\u4e2a\uff0c\u53ea\u662f\u6211\u5374\u4e0d\u5e0c\u671b\u88ab\u4f60\u770b\u4e0a\u4e09\u5929\u4e09\u591c\u3002\u201d \u3000\u3000\u4e01\u6d9b\u679c\u7136\u6709\u70b9\u4e0d\u597d\u610f\u601d\uff0e\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u3000\u201c\u53ef\u662f\u2026\u2026\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u90a3\u4f4d\u5728\u6c34\u91cc\u618b\u6b7b\u7684\u59d1\u5a18\uff0c\u6b7b\u5f97\u5b9e\u5728\u592a\u53ef\u60dc\uff01\u201d \u3000\u3000\u7d2b\u8863\u5c11\u5973\u4e0d\u7981\u53c8\u7b11\u51fa\u58f0\u97f3\u6765\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u5927\u53d4\u679c\u7136\u771f\u662f\u6123\u5934\u6123\u8111\uff0c\u8fde\u4e2a\u4eba\u90fd\u8ba4\u4e0d\u51fa\u6765\uff01\u201d \u3000\u3000\u201c\u5047\u4eba\uff1f\u201d\u4e01\u6d9b\u4e24\u773c\u53d1\u76f4\uff1a\u201c\u90a3\u662f\u5047\u7684\uff0c\u600e\u4e48\u53ef\u80fd\uff0c\u8fde\u5927\u817f\u548c\u809a\u5b50\u90fd\u662f\u53c8\u67d4\u53c8\u8f6f\u7684\uff01\u201d \u3000\u3000\u7518\u9716\u549b\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u5979\u7684\u5927\u817f\u548c\u809a\u5b50\u90fd\u662f\u8f6f\u7684\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6211\u6402\u8fc7\uff0c\u62b1\u8fc7\uff0c\u53c8\u6478\u8fc7\uff0c\u5f53\u7136\u77e5\u9053\uff01\u201d \u3000\u3000\u7518\u9716\u9a82\u9053\uff1a\u201c\u90a3\u4e48\u77ed\u7684\u65f6\u95f4\uff0c\u4f60\u5c31\u53c8\u6402\u53c8\u62b1\u53c8\u6478\uff0c\u8fd9\u8981\u6709\u4e2a\u597d\u59d1\u5a18\u966a\u4f60\u7761\u4e00\u665a\u4e0a\uff0c\u8c01\u53d7 \u5f97\u4e86\uff1f\u201d \u3000\u3000\u53ea\u542c\u7d2b\u8863\u5c11\u5973\u5c0f\u8138\u7ea2\u5f97\u50cf\u719f\u900f\u7684\u67ff\u5b50\u822c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u5927\u53d4\u8d8a\u8bf4\u8d8a\u96be\u542c\u554a\uff1f\u518d\u8fd9\u6837\u6211 \u5c31\u4e0d\u966a\u4f60\u4eec\u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e3a\u4e0d\u4f7f\u573a\u9762\u8fc7\u4efd\u5c34\u5c2c\uff0c\u5fd9\u63a5\u8fc7\u8bdd\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u60f3\u518d\u95ee\u4f60\u4e00\u4ef6\u4e8b\uff0c\u6c34\u5173\u7a76\u7adf\u662f\u600e \u6837\u9020\u6210\u7684\uff1f\u90a3\u7531\u6d1e\u9876\u548c\u6d1e\u58c1\u5c71\u6765\u7684\u5f3a\u5149\uff0c\u53c8\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u7d2b\u8863\u5c11\u6587\u6447\u6447\u5934\u9053\uff1a\u201c\u4f60\u8981\u95ee\u8fd9\u4e2a\uff0c\u6211\u5b9e\u5728\u65e0\u53ef\u5949\u544a\uff0c\u6211\u4eec\u53ea\u77e5\u9053\u5982\u4f55\u53d1\u52a8\u673a\u5173\uff0c\u81f3\u4e8e \u90a3\u4e9b\u4e1c\u897f\u662f\u600e\u6837\u9020\u6210\u7684\uff0c\u5c31\u5b8c\u5168\u4e0d\u6e05\u695a\u4e86\uff01\u201d \u3000\u3000\u201c\u90a3\u4e48\u5f53\u521d\u8bbe\u8ba1\u5efa\u9020\u6c34\u5173\u7684\u4eba\uff0c\u4f60\u603b\u542c\u8bf4\u8fc7\u662f\u8c01\u5427\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u6559\u4e3b\u8bf7\u6765\u9ad8\u4eba\u5efa\u9020\u7684\uff0c\u4e0d\u8fc7\u6211\u662f\u6c34\u5173\u9020\u597d\u4ee5\u540e\u624d\u6765\u7684\uff0c\u53c8\u600e\u4f1a\u77e5\u9053\u90a3\u4e9b\u9ad8\u4eba\u662f \u8c01\uff1f\u201d \u3000\u3000\u201c\u59d1\u5a18\u4e3a\u4ec0\u4e48\u4e0d\u6253\u542c\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u201c\u7edf\u9886\u65e9\u6709\u8c15\u793a\uff0c\u8fd9\u4e9b\u4e8b\u4e0d\u51c6\u6253\u542c\uff0c\u800c\u4e14\u6211\u4e5f\u4ece\u6765\u6ca1\u60f3\u5230\u6253\u542c\u8fd9\u4e9b\u4e8b\u3002\u201d \u3000\u3000\u5927\u7ea6\u534a\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0c\u56db\u4eba\u7684\u8863\u670d\u4fbf\u5df2\u70e4\u5e72\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u968f\u5373\u628a\u4ee3\u4e3a\u4fdd\u7ba1\u7684\u94f6\u7968\u4ea4\u8fd8\u4e86\u4e01\u6d9b\u3002 \u3000\u3000\u7d2b\u8863\u5c11\u5973\u51fa\u53bb\u4e0d\u4e45\u3002\u4fbf\u628a\u5348\u996d\u63d0\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u996d\u540e\uff0c\u8881\u5c0f\u9e64\u95ee\u9053\uff1a\u201c\u6211\u4eec\u73b0\u5728\u8be5\u8d70\u4e86\u5427\uff1f\u201d \u3000\u3000\u7d2b\u8863\u5c11\u5973\u9053\uff1a\u201c\u8fd8\u65e9\u5f97\u5f88\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u8be5\u4ec0\u4e48\u65f6\u5019\u8d70\u624d\u597d\uff1f\u201d \u3000\u3000\u201c\u7b49\u6709\u4eba\u6765\u63a5\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8c01\u6765\u63a5\uff1f\u201d \u3000\u3000\u7d2b\u8863\u5c11\u5973\u9053\uff1a\u201c\u5f53\u7136\u662f\u5bb5\u5173\u6d3e\u4eba\u6765\u63a5\uff0c\u8fd9\u662f\u89c4\u77e9\uff0c\u6bcf\u6b21\u901a\u8fc7\u6211\u4eec\u6c34\u5173\u7684\u4eba\uff0c\u90fd\u7531\u9704\u5173\u6d3e \u4eba\u6765\u63a5\u3002\u201d \u3000\u3000\u201c\u90a3\u4eba\u4ec0\u4e48\u65f6\u5019\u53ef\u4ee5\u5230\u8fbe\uff1f\u201d \u3000\u3000\u201c\u4e0d\u4e00\u5b9a\uff0c\u6709\u65f6\u662f\u665a\u996d\u524d\uff0c\u6709\u65f6\u662f\u665a\u996d\u540e\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u662f\u665a\u996d\u524d\uff0c\u6211\u4eec\u665a\u4e0a\u5c82\u4e0d\u8981\u7a7a\u7740\u809a\u5b50\u8fc7\u591c\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u665a\u996d\u524d\u6765\u63a5\uff0c\u4f60\u4eec\u6b63\u8be5\u9ad8\u5174\u624d\u5bf9\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u8868\u793a\u5bb5\u5173\u8981\u62db\u5f85\u4f60\u4eec\uff0c\u4ed6\u4eec\u62db\u5f85\u7684\u6bd4\u6211\u4eec\u8fd9\u91cc\u597d\u591a\u4e86\uff0c \u3000\u3000\u6d12\u83b1\u90fd\u662f\u4e0a\u7b49\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u90a3\u6837\u5927\u65b9\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u62ff\u4e86\u6559\u4e3b\u62e8\u4e0b\u7684\u5f85\u5ba2\u4e13\u6b3e\uff0c\u4e0d\u82b1\u81ea\u5df1\u7684\u94b1\uff0c\u6709\u4ec0\u4e48\u5927\u65b9\u4e0d\u5927\u65b9\uff0c\u81f3\u4e8e\u6211\u4eec\u8fd9\u91cc\uff0c \u53ea\u80fd\u7528\u666e\u901a\u996d\u83dc\uff0c\u82e5\u94fa\u5f20\u4e86\uff0c\u7edf\u9886\u5c31\u8981\u81ea\u5df1\u51fa\u94b1\u3002\u201d \u3000\u3000\u4e01\u6d9b\u62a2\u7740\u9053\uff1a\u201c\u6211\u4eec\u5c31\u7b49\u5bb5\u5173\u62db\u5f85\u597d\u4e86\uff01\u201d \u3000\u3000\u7d2b\u8863\u5c11\u5973\u9053\uff1b\u201c\u82e5\u5bb5\u5173\u4e0d\u62db\u5f85\uff0c\u4f60\u53c8\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u6447\u5934\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u7d2b\u8863\u5c11\u5973\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u665a\u9910\u6211\u4eec\u8fd9\u91cc\u7167\u6837\u51c6\u5907\uff0c\u53ea\u4e0d\u8fc7\u8981\u51c6\u5907\u5f97\u665a\u4e00\u70b9\uff0e\u90a3\u6837\u624d\u4e0d\u4f1a\u6d6a \u8d39\u3002\u201d \u3000\u3000\u4e8e\u662f\uff0c\u56db\u4e2a\u4eba\u53ea\u6709\u5728\u6d1e\u91cc\u67af\u7b49\u4e86\u3002 \u3000\u3000\u597d\u5728\u7d2b\u8863\u5c11\u5973\u4e00\u76f4\u966a\u7740\uff0c\u81f3\u5c11\u4e01\u6d9b\u548c\u7518\u9716\u4e24\u4eba\u5e76\u4e0d\u5bc2\u5bde\u3002 \u3000\u3000\u770b\u770b\u5929\u8272\u5c06\u665a\uff0c\u5bb5\u5173\u7684\u4eba\u4ecd\u7136\u672a\u5230\u3002 \u3000\u3000\u7d2b\u8863\u5c11\u5973\u53ea\u597d\u53c8\u51c6\u5907\u56de\u53bb\u62ff\u996d\u3002 \u3000\u3000\u90a3\u77e5\u5979\u521a\u4e00\u51fa\u6d1e\uff0c\u5bb5\u5173\u7684\u4eba\u4fbf\u6765\u4e86\u3002 \u3000\u3000\u6765\u4eba\u662f\u2014\u4f4d\u7070\u8863\u8001\u8005\uff0c\u6148\u7709\u5584\u76ee\uff0c\u4e00\u56e2\u548c\u6c14\uff0c\u5f88\u50cf\u6587\u5173\u90a3\u4f4d\u59d3\u5218\u7684\u767d\u8863\u8001\u4eba\u3002 \u3000\u3000\u7d2b\u8863\u5c11\u5973\u5f53\u5373\u4e3a\u8881\u5c0f\u9e64\u7b49\u56db\u4eba\u5f15\u89c1\u3002 \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8001\u5148\u751f\uff0c\u6211\u4eec\u8fd8\u6ca1\u6709\u7528\u665a\u9910\u5462\uff01\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u7b11\u9053\uff1a\u201c\u56db\u4f4d\u653e\u5fc3\uff0c\u665a\u9910\u7531\u6211\u4eec\u5bb5\u5173\u62db\u5f85\u3002\u201d \u3000\u3000\u542c\u8bf4\u5bb5\u5173\u6709\u62db\u5f85\uff0c\u56db\u4eba\u5f53\u7136\u9ad8\u5174\uff0c\u968f\u5373\u8ddf\u7740\u7070\u8863\u8001\u4eba\u51fa\u4e86\u6d1e\u3002 \u3000\u3000\u7d2b\u8863\u5c11\u5973\u7565\u9001\u4e86\u4e00\u6bb5\u8def\uff0c\u4fbf\u81ea\u884c\u56de\u53bb\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u56e0\u89c1\u767d\u8863\u8001\u4eba\u662f\u6587\u5173\u7edf\u9886\uff0c\u5fc3\u60f3\u8fd9\u7070\u8863\u8001\u4eba\u5f88\u53ef\u80fd\u5c31\u662f\u5bb5\u5173\u7edf\u9886\uff0c\u4e0d\u80fd\u4e0d\u95ee\u6e05\u695a\uff0c \u4ee5\u514d\u5931\u793c\uff0c\u8fb9\u8d70\u8fb9\u95ee\u9053\uff1a\u201c\u6562\u95ee\u8001\u5148\u751f\u53ef\u662f\u5bb5\u5173\u7edf\u9886\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u5475\u5475\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u9ad8\u62ac\u8001\u673d\u4e86\uff0c\u5f15\u5bfc\u6b66\u6797\u670b\u53cb\u8fdb\u5173\u7684\u4e8b\uff0c\u4e00\u5411\u662f\u8001\u673d\u7684\u804c\u8d23\uff0c \u5bb5\u5173\u5728\u5404\u4e2a\u5173\u95e8\u4e2d\uff0c\u7f16\u7ec4\u6700\u5927\uff0c\u5206\u5b50\u4e5f\u6700\u590d\u6742\uff0c\u7edf\u9886\u90a3\u80fd\u4eb2\u81ea\u6765\u8fce\u63a5\u5404\u4f4d\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5bb5\u5173\u91cc\u9762\u90fd\u6709\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u4f9d\u7136\u7b11\u5475\u5475\u9053\uff1a\u201c\u5bb5\u5173\u4e0d\u6bd4\u5176\u4ed6\u5404\u5173\uff0c\u91cc\u9762\u4e94\u82b1\u516b\u95e8\uff0c \u3000\u3000\u4ec0\u4e48\u90fd\u6709\uff0c\u4e0d\u8fc7\u5404\u4f4d\u4eca\u665a\u4e0d\u53ef\u80fd\u5168\u90e8\u770b\u5230\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u53ef\u80fd\u5168\u90e8\u770b\u5230\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5bb5\u5173\u91cc\u7684\u5404\u79cd\u8282\u65e5\uff0c\u6bcf\u665a\u90fd\u6709\u53d8\u6362\uff0c\u6bcf\u6b21\u53ea\u80fd\u51fa\u73b0\u56db\u4e94\u4e2a\u8282\u76ee\u3002\u201d \u3000\u3000\u201c\u8bf7\u95ee\u4eca\u665a\u662f\u4ec0\u4e48\u8282\u76ee\uff1f\u201d \u3000\u3000\u201c\u8001\u673d\u4e5f\u4e0d\u592a\u6e05\u695a\uff0c\u8fdb\u5173\u4e4b\u540e\uff0c\u81ea\u7136\u5c31\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u4e0d\u77e5\u4e0d\u89c9\u524d\u9762\u53c8\u51fa\u73b0\u4e86\u4e00\u5904\u96a7\u9053\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u77e5\u9053\u8fd9\u91cc\u4e00\u5b9a\u662f\u5bb5\u5173\u3002 \u3000\u3000\u4f46\u8fd9\u5904\u96a7\u9053\u5374\u4e0e\u4ee5\u524d\u6240\u7ecf\u8fc7\u7684\u5b8c\u5168\u4e0d\u540c\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u91cc\u5f88\u50cf\u4e00\u5ea7\u57ce\u5821\uff0c\u800c\u4e14\u57ce\u697c\u4e0a\u8fd8\u6709\u4eba\u5f71\u6643\u52a8\uff0c\u663e\u7136\u90a3\u662f\u8d1f\u8d23\u4e86\u671b\u503c\u591c\u7684\u3002 \u3000\u3000\u57ce\u95e8\u65c1\u4e5f\u6709\u4e24\u540d\u6267\u5251\u7684\u5927\u6c49\u7ad9\u5728\u90a3\u91cc\u3002 \u3000\u3000\u7070\u8863\u8001\u4eba\u8f6c\u5934\u95ee\u9053\uff1a\u201c\u91cc\u9762\u7684\u8282\u76ee\u51c6\u5907\u597d\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5176\u4e2d\u4e00\u540d\u79c3\u5934\u5927\u6c49\u9053\uff1a\u201c\u51c6\u5907\u597d\u4e86\uff01\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u518d\u95ee\uff1a\u201c\u4eca\u665a\u4e00\u5171\u51e0\u4e2a\u8282\u76ee\uff1f\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u79c3\u5934\u5927\u6c49\u9053\uff1a\u201c\u4e00\u5171\u56db\u4e2a\uff0c\u5206\u522b\u662f\u2018\u6148\u6bcd\u671b\u513f\u2019\u3001\u2018\u5be1\u5987\u76fc\u5927\u2019\u3001\u2018\u7a91\u59d0\u62c9\u5ba2\u2019\u548c\u2018\u767d \u5403\u5305\u5b50\u2019\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u7b11\u9053\uff1a\u201c\u8fd9\u56db\u4e2a\u8282\u76ee\u90fd\u5f88\u7b80\u5355\uff0c\u7b97\u4ed6\u4eec\u56db\u4f4d\u8d70\u8fd0\u3002\u201d \u3000\u3000\u8fd9\u65f6\u8881\u5c0f\u9e64\u7b49\u56db\u4eba\u90fd\u542c\u5f97\u5982\u5760\u4e94\u91cc\u96fe\u4e2d\uff0c\u4e01\u6d9b\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u8001\u5148\u751f\uff0c\u521a\u624d\u4f60\u548c\u90a3\u4f4d\u8001 \u5144\u90fd\u8bf4\u7684\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4eca\u665a\u56db\u4f4d\u8981\u901a\u8fc7\u7684\u56db\u9053\u6d4b\u8bd5\u7ad9\u3002\u201d \u3000\u3000\u4ed6\u5e76\u4e0d\u8be6\u7ec6\u89e3\u91ca\uff0c\u5e26\u5934\u5f80\u5173\u5185\u8d70\u53bb\u3002 \u3000\u3000\u8fd9\u9053\u57ce\u95e8\u5f88\u957f\uff0c\u51fa\u4e86\u5bf9\u9762\u6d1e\u53e3\uff0c\u8881\u5c0f\u9e64\u7b49\u56db\u4eba\u9669\u4e9b\u8981\u60ca\u547c\u51fa\u58f0\u3002 \u3000\u3000\u4ed6\u4eec\u4e8b\u5148\u8c01\u4e5f\u6599\u60f3\u4e0d\u5230\uff0c\u773c\u524d\u7adf\u662f\u8c41\u7136\u5f00\u6717\uff1f\u90a3\u4e24\u8fb9\u7684\u8c37\u4fa7\uff0c\u5df2\u7ecf\u4e0d\u89c1\uff0c\u524d\u9762\u51fa\u73b0\u7684\uff0c \u8d6b\u7136\u662f\u4e00\u5904\u6709\u5982\u57ce\u9547\u822c\u7684\u5de8\u5927\u6751\u843d\uff0c\u4f30\u8ba1\u7740\u4e0d\u4e0b\u6570\u767e\u5bb6\u4e4b\u591a\u3002 \u3000\u3000\u7531\u4e8e\u6bcf\u5bb6\u95e8\u524d\uff0c\u90fd\u60ac\u7740\u4e00\u76cf\u706f\u7b3c\uff0c\u7167\u89c1\u8857\u4e0a\u7537\u7537\u5973\u5973\uff0c\u7199\u6765\u6518\u5f80\uff0c\u597d\u4e0d\u70ed\u95f9\uff0c\u5982\u679c\u628a\u4e00 \u4e2a\u4eba\u8499\u7740\u773c\u775b\u9001\u5230\u8fd9\u91cc\uff0c\u73b0\u5728\u8ba9\u4ed6\u518d \u3000\u3000\u770b\u5230\u773c\u524d\u7684\u666f\u8c61\uff0c\u8fd9\u4eba\u7edd\u5bf9\u4e0d\u4f1a\u76f8\u4fe1\u662f\u5728\u7ec8\u5357\u6df1\u5904\u7684\u5c71\u91ce\u91cc\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u4ee5\u4e3a\u6765\u5230\u957f\u5b89\u57ce \u5185\u3002 \u3000\u3000\u7070\u8863\u8001\u4eba\u5e26\u7740\u56db\u4eba\u4e0d\u8d70\u70ed\u95f9\u8857\u9053\uff0c\u5374\u6765\u5230\u4e00\u5904\u51b7\u50fb\u7684\u5c0f\u5df7\u3002 \u3000\u3000\u7070\u8863\u8001\u4eba\u505c\u4e0b\u811a\u6b65\u9053\uff1a\u201c\u8fd9\u91cc\u5c31\u662f\u2018\u6148\u6bcd\u671b\u513f\u2019\u6d4b\u8bd5\u7ad9\u3002\u201d \u3000\u3000\u4e01\u6d9b\u731b\u7ffb\u7740\u725b\u773c\u9053\uff1a\u201c\u8981\u6d4b\u8bd5\u8c01\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u7b11\u9053\uff1a\u201c\u5f53\u7136\u6d4b\u8bd5\u4f60\u4eec\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4f9d\u7136\u832b\u7136\u4e0d\u89e3\u9053\uff1a\u201c\u600e\u6837\u6d4b\u8bd5\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u56db\u4f4d\u8001\u592a\u592a\uff0c\u90fd\u662f\u5728\u95e8\u53e3\u7b49\u513f\u5b50\u56de\u5bb6\u7684\uff0c\u4f60\u4eec\u56db\u4f4d\u53ea\u8981\u80fd\u8bbe\u6cd5\u8ba9\u5979\u4eec \u4e0d\u518d\u7b49\uff0c\u90a3\u5c31\u7b97\u6210\u529f\u4e86\u3002\u4e0d\u8fc7\u5148\u51b3\u7684\u6761\u4ef6\u5c31\u662f\u4e0d\u80fd\u548c\u5979\u4eec\u5435\u67b6\uff0c\u66f4\u4e0d\u80fd\uff0a\u5979\u4eec\u751f\u6c14\uff0c\u6700\u597d\u662f \u5979\u4eec\u9ad8\u9ad8\u5174\u5174\u5fc3\u7518\u60c5\u613f\u7684\u4e0d\u518d\u7b49\u513f\u5b50\u3002\u201d \u3000\u3000\u4e01\u6d9b\u54a7\u5634\u7b11\u9053\uff1a\u201c\u539f\u6765\u4e16\u8fd9\u4e48\u4e00\u56de\u4e8b\uff0c\u90a3\u5f88\u7b80\u5355\uff0c\u8c01\u5148\u6d4b\u8bd5\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u8c01\u5148\u8c01\u540e\uff0c\u4f60\u4eec\u56db\u4f4d\u81ea\u5df1\u5546\u8bae\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u90a3\u5c31\u7531\u6211\u6253\u5934\u9635\u5427\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u5927\u6b65\u8d70\u5230\u4e00\u5904\u95e8\u53e3\uff0c\u505c\u4e0b\u540e\u4e24\u773c\u6b7b\u77aa\u7740\u8001\u592a\u592a\uff0c\u5374\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u90a3\u8001\u592a\u592a\u6123\u6123\u7684\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u54e5\uff0c\u4f60\u8981\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6211\u60f3\u95ee\u8001\u592a\u592a\u5728\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8001\u592a\u592a\u9053\uff1a\u201c\u8001\u8eab\u662f\u4f4f\u8fd9\u91cc\u7b49\u513f\u5b50\u56de\u6765\uff1f\u56e0\u4e3a\u4ed6\u5bc4\u4fe1\u6765\u8bf4\u4eca\u5929\u8981\u56de\u5bb6\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4f38\u624b\u62c9\u4f4f\u8001\u592a\u592a\u7684\u624b\u8155\uff0c\u54a7\u5634\u7b11\u9053\uff1a\u201c\u5a18\uff0c\u6211\u5c31\u662f\u4f60\u513f\u5b50\uff0c \u3000\u3000\u4f60\u600e\u4e48\u8fde\u81ea\u5df1\u7684\u513f\u5b50\u90fd\u4e0d\u8ba4\u8bc6\u4e86\uff1f\u5feb\u56de\u5c4b\u5750\u53bb\uff0c\u522b\u5728\u95e8\u53e3\u7b49\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8fb9\u8bf4\u8fb9\u628a\u8001\u592a\u592a\u5f80\u91cc\u9762\u62c9\u3002 \u3000\u3000\u53ea\u89c1\u8001\u592a\u592a\u8138\u8272\u5927\u53d8\uff0c\u5c16\u53eb\u9053\uff1a\u201c\u4f60\u662f\u90a3\u6765\u7684\u91ce\u5c0f\u5b50\uff1f\u6d51\u5145\u8001\u8eab\u513f\u5b50\uff0c\u8001\u8eab\u6ca1\u778e\uff0c\u600e\u4f1a \u8fde\u81ea\u5df1\u7684\u513f\u5b50\u90fd\u8ba4\u4e0d\u51fa\u6765\uff01\u201d \u3000\u3000\u4e01\u6d9b\u6709\u4e9b\u7740\u614c\u9053\uff1a\u201c\u8001\u592a\u592a\uff1f\u6211\u771f\u662f\u4f60\u7684\u513f\u5b50\uff01\u201d \u3000\u3000\u8001\u592a\u592a\u60ca\u53eb\u9053\uff1a\u201c\u5feb\u677e\u624b\uff0c\u518d\u8fd9\u6837\u8001\u8eab\u5c31\u8981\u558a\u4e86\uff0c\u5230\u90a3\u65f6\u628a\u4f60\u6346\u7ed1\u5230\u5b98\u5e9c\uff0c\u770b\u4f60\u8fd8\u6562\u4e0d \u6562\u80e1\u95f9\uff01\u201d \u3000\u3000\u53ea\u542c\u7070\u8863\u8001\u8005\u53eb\u9053\uff1a\u201c\u4e01\u8001\u5f1f\u56de\u6765\uff0c\u7528\u4e0d\u7740\u518d\u6d4b\u8bd5\u4e86\uff01\u201d \u3000\u3000\u4e01\u6d9b\u8d70\u56de\u6765\u5c34\u5c2c\u7b11\u9053\uff1a\u201c\u8001\u5148\u751f\uff0c\u6211\u7b97\u4e0d\u7b97\u6d4b\u9a8c\u53ca\u683c\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4eca\u665a\u6bcf\u4eba\u5404\u6d4b\u56db\u6b21\uff0c\u6210\u7ee9\u7b49\u5168\u90e8\u6d4b\u8bd5\u5b8c\u6bd5\u4e00\u8d77\u7b97\u3002\u201d \u3000\u3000\u7518\u9716\u6478\u4e86\u6478\u8111\u888b\u9053\uff1a\u201c\u73b0\u5728\u8be5\u6211\u53bb\u4e86\uff01\u201d \u3000\u3000\u4ed6\u6765\u5230\u7b2c\u4e8c\u5bb6\u95e8\u53e3\uff0c\u65bd\u4e0b\u4e00\u793c\u9053\uff1a\u201c\u8bf7\u95ee\u8001\u592a\u592a\u3002\u4f60\u5728\u7b49\u8c01\uff1f\u201d \u3000\u3000\u8001\u592a\u4eba\u9053\uff1a\u201c\u7b49\u6211\u513f\u5b50\u3002\u201d \u3000\u3000\u201c\u4f60\u513f\u5b50\u79bb\u5bb6\u591a\u4e45\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5230\u957f\u5b89\u505a\u751f\u610f\uff0c\u5df2\u7ecf\u534a\u5e74\u6ca1\u56de\u5bb6\u4e86\u3002\u201d \u3000\u3000\u201c\u8001\u592a\u592a\u4e0d\u5fc5\u7b49\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u8ddf\u4f60\u513f\u5b50\u662f\u597d\u670b\u53cb\uff0c\u5728\u957f\u5b89\u4e00\u8d77\u505a\u751f\u610f\uff0c\u4ed6\u6628\u5929\u5f97\u4e86\u66b4\u75c5\u3002\u8fd8\u662f\u6211\u628a\u4ed6\u9001\u5230\u5927\u592b\u90a3 \u7684\uff0c\u867d\u7136\u6b7b\u4e0d\u4e86\uff0c\u81f3\u5c11\u4e5f\u5f97\u534a\u4e2a\u6708\u624d\u80fd\u597d\uff0c\u6211\u73b0\u5728\u5c31\u662f\u7279\u5730\u7ed9\u8001\u592a\u592a\u9001\u4fe1\u513f\u6765\u7684\u3002\u201d \u3000\u3000\u8001\u592a\u592a\u95fb\u8a00\uff0c\u201c\u54c7\u201d\u7684\u2014\u58f0\u54ed\u4e86\uff0c\u63a5\u7740\u63a9\u9762\u5411\u95e8\u5185\u5954\u53bb\u3002 \u3000\u3000\u7518\u9716\u5927\u4e3a\u5f97\u610f\uff0c\u8fd4\u8eab\u8d70\u4e86\u56de\u6765\u9053\uff1a\u201c\u8001\u5148\u751f\uff0c\u5728\u4e0b\u8fd9\u4e00\u7740\u4e0d\u9519\u5427\uff1f\u8001\u592a\u592a\u679c\u7136\u4e0d\u7b49\u513f\u5b50 \u4e86\u3002\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u4e00\u8d77\u8ba1\u7b97\u6210\u7ee9\u3002\u201d \u3000\u3000\u8fd9\u65f6\u8881\u5c0f\u9e64\u5df2\u548c\u5cb3\u5c0f\u98de\u5728\u4e92\u76f8\u63a8\u8ba9\uff0c\u6700\u540e\u8fd8\u662f\u7531\u8881\u5c0f\u9e64\u5148\u6d4b\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u6765\u5230\u7b2c\u4e09\u5bb6\u95e8\u53e3\uff0e\u4e5f\u606d\u606d\u656c\u656c\u7684\u65bd\u4e86\u4e00\u793c\u9053\uff1a\u201c\u8001\u592a\u592a\u53ef\u662f\u5728\u7b49\u4ee4\u90ce\uff1f\u201d \u3000\u3000\u8001\u592a\u592a\u9887\u4e3a\u60ca\u559c\u7684\u95ee\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u8001\u592a\u592a\u771f\u662f\u8d35\u4eba\u591a\u5fd8\u4e8b\uff0c\u6211\u548c\u4ee4\u90ce\u662f\u597d\u53cb\uff0c\u4ece\u524d\u66fe\u5230\u5e9c\u4e0a\u6765\u8fc7\uff0c\u4eca\u5929\u4e5f\u662f \u548c\u4ee4\u90ce\u4e00\u8d77\u6765\u7684\u3002\u201d \u3000\u3000\u8001\u592a\u592a\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4ed6\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e0d\u7b54\u53cd\u95ee\u9053\uff1a\u201c\u8bf7\u95ee\u5e9c\u4e0a\u53ef\u6709\u540e\u95e8\uff1f\u201d \u3000\u3000\u8001\u4eba\u592a\u9053\uff1a\u201c\u6709\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u90a3\u5c31\u96be\u602a\u4e86\uff0c\u6211\u521a\u624d\u548c\u4ed6\u5206\u624b\uff0c\u4ed6\u8bf4\u8981\u7ed9\u8001\u592a\u592a\u4e00\u4e2a\u60ca\u559c\uff0c\u4ece\u540e\u95e8\u56de\u5bb6\uff0c \u770b\u6837\u5b50\u4ed6\u4e00\u5b9a\u4ece\u540e\u95e8\u56de\u5230\u5e9c\u4e0a\u4e86\u3002\u201d \u3000\u3000\u8001\u592a\u592a\u65e2\u5174\u594b\u53c8\u6709\u4e9b\u7740\u607c\u7684\u561f\u56b7\u7740\u9053\uff1a\u201c\u963f\u72d7\u8fd9\u5b69\u5b50\u4e5f\u771f\u662f\uff0c\u90fd\u5feb\u4e09\u5341\u5c81\uff0c\u8fd8\u50cf\u4e2a\u5b69\u5b50 \u4f3c\u7684\uff0c\u8fde\u56de\u5bb6\u4e5f\u8981\u8ddf\u5a18\u6349\u8ff7\u85cf\uff0c\u591a\u8c22\u5c0f\u5144\u5f1f\uff0c\u8001\u8eab\u8fd9\u5c31\u5230\u91cc\u9762\u770b\u770b\uff0c\u770b\u4ed6\u80fd\u8eb2\u5230\u54ea\u91cc\u53bb\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u56de\u5230\u539f\u5904\uff0c\u5374\u5e76\u672a\u8a00\u8bed\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1b\u201c\u8881\u5927\u54e5\uff0c\u4f60\u771f\u884c\uff0c\u8bba\u6210\u7ee9\u5fc5\u5b9a\u7b2c\u4e00\uff0c\u6709\u4f60\u5728\u5148\uff0c\u5c0f\u5f1f\u7adf\u6709\u4e9b\u4e0d\u6562\u8fc7\u53bb\u4e86\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u7b11\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u4f60\u5904\u5904\u90fd\u9ad8\u8fc7\u6211\uff0c\u4f55\u5fc5\u5f00\u6211\u7684\u73a9\u7b11\u3002\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u4e8c\u4f4d\u522b\u53ea\u987e\u8bf4\u8bdd\uff0c\u540e\u9762\u8fd8\u6709\u4e09\u5173\u5462\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u968f\u5373\u6765\u5230\u7b2c\u56db\u5bb6\u95e8\u53e3\uff0c\u4e5f\u6df1\u65bd\u4e00\u793c\u9053\uff1a\u201c\u6211\u77e5\u9053\u8001\u592a\u592a\u662f\u5728\u7b49\u4ee4\u90ce\u56de\u5bb6\uff0c\u4e0d\u5fc5\u7b49 \u4e86\uff01\u201d \u3000\u3000\u8001\u592a\u592a\u5fae\u5fae\u4e00\u60ca\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u83ab\u975e\u51fa\u4e8b\u4e86\uff1f\u5c0f\u5144\u5f1f\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6b49\u7136\u4e00\u7b11\u9053\uff1a\u201c\u8001\u592a\u592a\u600e\u4e48\u60f3\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u6211\u662f\u8bf4\u4ee4\u90ce\u5728\u5916\u9762\u53d1\u4e86\u5927\u8d22\u3002\u201d \u3000\u3000\u8001\u592a\u592a\u6025\u6025\u95ee\u9053\uff1a\u201c\u4ed6\u53d1\u4e86\u8d22\u4e3a\u4ec0\u4e48\u4e0d\u56de\u6765\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4ed6\u6b63\u662f\u8981\u56de\u5bb6\uff0c\u56e0\u4e3a\u91d1\u94f6\u8d22\u5b9d\u592a\u591a\u62ff\u4e0d\u52a8\uff0c\u6240\u4ee5\u624d\u8981\u6211\u5148\u8d76\u6765\u5411\u8001\u592a\u592a\u62a5 \u4fe1\u3002\u201d \u3000\u3000\u201c\u62a5\u4ec0\u4e48\u4fe1\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u4ed6\u7684\u597d\u53cb\uff0c\u4eca\u5929\u662f\u5e2e\u4ed6\u4e00\u8d77\u5e26\u7740\u91d1\u94f6\u8d22\u5b9d\u56de\u6765\u7684\uff0c\u8d70\u5230\u524d\u9762\uff0c\u56e0\u4e3a\u5b9e\u5728\u8d70\u4e0d\u52a8\uff0c \u53ea\u597d\u53bb\u96c7\u9aa1\u8f66\uff0c\u4ed6\u6015\u8001\u592a\u7740\u6025\uff0c\u5c31\u8981\u6211\u5148\u6765\u5411\u8001\u592a\u592a\u62a5\u4fe1\uff0c\u4ed6\u5927\u7ea6\u8fd8\u8981\u4e00\u4e2a\u591a\u65f6\u8fb0\u624d\u80fd\u5230\u5bb6\uff0c \u8fd9\u4e48\u4e45\u7684\u65f6\u95f4\uff0c\u8001\u592a\u592a\u7528\u4e0d\u7740\u518d\u5728\u95e8\u53e3\u7b49\uff0c\u514d\u5f97\u4f60\u8001\u4eba\u5bb6\u7ad9\u7740\u5403\u4e0d\u6d88\u3002\u201d \u3000\u3000\u8001\u592a\u592a\u987f\u65f6\u4e50\u5f97\u8fde\u5634\u90fd\u5408\u4e0d\u4e0a\uff1a\u201c\u963f\u72d7\u5728\u5bb6\u65f6\uff0c\u8001\u8eab\u8001\u662f\u9a82\u4ed6\u6ca1\u51fa\u606f\uff0c\u771f\u60f3\u4e0d\u5230\u4ed6\u53d1\u4e86 \u5927\u8d22\uff0c\u83ab\u4e0d\u662f\u7956\u4e0a\u79ef\u8fc7\u9634\u5fb7\uff0c\u70e7\u8fc7\u9ad8\u9999\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8001\u592a\u592a\uff0c\u4eba\u4e0d\u53ef\u8c8c\u76f8\uff0c\u6d77\u6c34\u4e0d\u53ef\u6597\u91cf\uff0c\u963f\u72d7\u5144\u5728\u957f\u5b89\u76f8\u8fc7\u9762\uff0c\u4ed6\u4e0d\u4f46\u80fd\u53d1 \u5927\u8d22\uff0c\u5c06\u6765\u4e00\u5b9a\u8fd8\u80fd\u505a\u5927\u5b98\uff0c\u4f60\u8001\u4eba\u5bb6\u771f\u662f\u6709\u798f\u4e86\uff01\u201d \u3000\u3000\u8001\u592a\u592a\u7b11\u7740\u5f80\u5bb6\u91cc\u8d70\uff0c\u4e00\u8fb9\u53c8\u9053\uff1a\u201c\u65e2\u7136\u8fd8\u6709\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u8001\u8eab\u5c31\u7528\u4e0d\u7740\u5728\u95e8\u53e3\u7b49\u4e86\uff0c \u5c0f\u5144\u5f1f\uff0c\u8981\u4e0d\u8981\u8fdb\u6765\u559d\u676f\u8336\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u5148\u56de\u53bb\u5e2e\u7740\u963f\u72d7\u5144\u628a\u91d1\u94f6\u8d22\u5b9d\u8fd0\u56de\u6765\u8981\u7d27\uff0c\u5f85\u4f1a\u513f\u4e0d\u4f46\u8981\u559d\u4f60\u8001\u4eba\u5bb6\u7684 \u8336\uff0c\u5c11\u4e0d\u5f97\u8fd8\u8981\u5728\u5e9c\u4e0a\u5403\u996d\u5462\uff01\u201d \u3000\u3000\u4ed6\u8d70\u56de\u539f\u5904\uff0c\u7070\u8863\u8001\u4eba\u5728\u90a3\u91cc\u7b11\u7740\u76f4\u70b9\u5934\u3002 \u3000\u3000\u8fd9\u79cd\u6d4b\u8bd5\u6cd5\uff0c\u5728\u4e01\u6d9b\u548c\u7518\u9716\u6765\u8bf4\uff0c\u6bd4\u6587\u5173\u548c\u6b66\u5173\u3001\u706b\u5173\u3001\u6c34\u5173\u5b9e\u5728\u662f\u8f7b\u677e\u591a\u4e86\uff0c\u800c\u4e14\u8fd8 \u9887\u5bcc\u8da3\u5473\uff0c\u56e0\u4e4b\uff0c\u4e24\u4eba\u90fd\u5174\u81f4\u52c3\u52c3\u3002 \u3000\u3000\u4e01\u6d9b\u62a2\u7740\u95ee\u9053\uff1a\u201c\u4e0b\u4e00\u7ad9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u4f60\u5148\u524d\u4e0d\u662f\u5df2\u542c\u8bf4\u8fc7\u4e48\uff1f\u600e\u4e48\u8bb0\u6027\u8fd9\u6837\u4e0d\u6d4e\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u5e72\u54b3\u4e24\u58f0\u9053\uff1a\u201c\u8c01\u80fd\u8bb0\u90a3\u4e48\u591a\uff0c\u4f60\u8001\u4eba\u5bb6\u518d\u8bf4\u4e00\u904d\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u6c34\u9053\uff1a\u201c\u5f53\u7136\u8981\u5bf9\u5927\u5bb6\u8bf4\u6e05\u695a\uff0c\u4e0d\u7136\u5982\u4f55\u6d4b\u6cd5\u3002\u201d \u3000\u3000\u4ed6\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u4e0b\u4e00\u7ad9\u662f\u5be1\u5987\u76fc\u592b\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4e00\u6123\u9053\uff1a\u201c\u65e2\u7136\u662f\u5be1\u5987\uff0c\u54ea\u91cc\u6765\u7684\u592b\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u4f60\u542c\u8001\u673d\u8bb2\uff0e\u5176\u5b9e\u5979\u4eec\u4e0d\u4e00\u5b9a\u662f\u5be1\u5987\uff0c\u53ea\u56e0\u4e08\u592b\u51fa\u95e8\u591a\u5e74\u672a\u56de\uff0c\u800c\u4e14\u8fde \u6d88\u606f\u4e5f\u6ca1\u6709\uff0c\u8fd9\u65f6\u96be\u514d\u5c31\u6709\u5f88\u591a\u4f20\u8bf4\u3002\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u4f20\u8bf4\uff1f\u201d \u3000\u3000\u201c\u6709\u7684\u8bf4\u5979\u4eec\u7684\u4e08\u592b\u5df2\u7ecf\u6b7b\u5728\u5916\u4e61\uff0c\u6216\u8005\u662f\u53c8\u53e6\u5a36\u4e86\u522b\u7684\u5973\u4eba\uff0c\u6216\u8005\u662f\u4ece\u4e86\u519b\u5230\u8fb9\u7586\u6253 \u4ed7\u53bb\u4e86\u3002\u603b\u4e4b\uff0c\u5979\u4eec\u7684\u4e08\u592b\u56de\u6765\u7684\u5e0c \u3000\u3000\u671b\u5df2\u7ecf\u4e0d\u5927\uff0c\u800c\u5979\u4eec\u5374\u504f\u4e0d\u6b7b\u5fc3\uff0c\u5929\u5929\u591c\u665a\u5728\u95e8\u53e3\u76fc\u671b\u7740\u4e08\u592b\u80fd\u5947\u8ff9\u822c\u7684\u56de\u6765\u56e2\u805a\u3002\u201d \u3000\u3000\u7518\u9716\u4e0d\u7981\u53f9\u4e86\u95e8\u6c14\u9053\uff1b\u201c\u8fd9\u6837\u8bf4\u8fd9\u4e9b\u5973\u4eba\u8fd8\u771f\u662f\u602a\u53ef\u601c\u7684\uff01\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9894\u9996\u9053\uff1a\u201c\u8c01\u8bf4\u4e0d\u662f\uff0c\u6240\u4ee5\u8fd9\u4e00\u7ad9\u8fd8\u771f\u4e0d\u597d\u6d4b\u8bd5\u3002\u201d \u3000\u3000\u4e01\u6d9b\u53c8\u63a5\u8fc7\u8bdd\u6765\u9053\uff1a\u201c\u6211\u4eec\u6d4b\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u5f53\u7136\u8981\u529d\u5979\u4eec\uff0c\u53ea\u8981\u80fd\u628a\u5979\u4eec\u529d\u56de\u53bb\uff0c\u90a3\u5c31\u6210\u529f\u4e86\u3002\u201d \u3000\u3000\u4e01\u6d9b\u76b1\u8d77\u6d53\u7709\u9053\uff1a\u201c\u8001\u5148\u751f\uff0c\u6211\u4eec\u771f\u4e0d\u61c2\uff0c\u5728\u5bb5\u5173\u6d4b\u8bd5\u8fd9\u56db\u7ad9\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u7528\u610f\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u5f53\u7136\u6709\u7528\u610f\uff0e\u4f60\u5148\u8bf4\u8bf4\u524d\u9762\u95ef\u90a3\u51e0\u5173\u662f\u4ec0\u4e48\u7528\u610f\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6587\u5173\u662f\u6d4b\u8bd5\u6587\u624d\uff0c\u6b66\u5173\u662f\u6d4b\u8bd5\u6b66\u529f\uff0c\u706b\u5173\u662f\u6d4b\u9a8c\u8f7b\u529f\uff0c\u81f3\u4e8e\u6c34\u5173\uff0c\u9664\u4e86\u6d4b\u8bd5 \u8f7b\u529f\u4ee5\u5916\uff0c\u66f4\u8981\u6d4b\u8bd5\u662f\u4e0d\u662f\u8c19\u7ec3\u6c34\u6027\u3002\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9894\u9996\u9053\uff1b\u201c\u8bb2\u7684\u5f88\u5bf9\uff0c\u81f3\u4e8e\u5bb5\u5173\uff0c\u6d4b\u8bd5\u7684\u662f\u673a\u667a\u53cd\u5e94\uff0c \u3000\u3000\u5982\u679c\u4e00\u4e2a\u4eba\u6587\u624d\uff0c\u6b66\u529f\u3001\u8f7b\u529f\u3001\u6c34\u6027\u548c\u673a\u667a\u53cd\u5e94\u6837\u6837\u5177\u5907\uff0c\u90a3\u5c31\u662f\u4e00\u4e2a\u5341\u5168\u5341\u7f8e\u7684\u4eba\u4e86\uff0c \u6211\u4eec\u6559\u4e3b\uff0c\u6700\u5e0c\u671b\u5f97\u5230\u7684\uff0c\u5c31\u662f\u8fd9\u6837\u7684\u4eba\u624d\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8fd9\u6837\u7684\u4eba\u624d\uff0c\u5f53\u7136\u8c01\u90fd\u5e0c\u671b\u5f97\u5230\uff0c\u53ea\u662f\u8d35\u5173\u8fd9\u51e0\u7ad9\u7684\u6d4b\u8bd5\uff0c\u4f3c\u4e4e\u6709\u70b9\u5f00\u73a9\u7b11 \u4e00\u822c\uff01\u201d \u3000\u3000\u7070\u8863\u8001\u8005\u7b11\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u50cf\u5f00\u73a9\u7b11\u4e00\u822c\uff0c\u624d\u80fd\u5f15\u8d77\u53d7\u6d4b\u8005\u7684\u5174\u8da3\uff0c\u5426\u5219\u7ed9\u4f60\u6362\u70b9\u522b\u7684\u600e \u4e48\u6837\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u5fd9\u6446\u624b\u9053\uff1a\u201c\u4e0d\u8981\u4e0d\u8981\uff0c\u8fd9\u6837\u6700\u597d\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u7070\u8863\u8001\u8005\u9053\uff1a\u201c\u90a3\u5c31\u522b\u7f57\u55e6\uff0c\u968f\u8001\u673d\u8d70\u5427\uff01\u201d \u3000\u3000\u8f6c\u5f2f\u62b9\u89d2\uff0c\u7070\u8863\u8001\u8005\u4e13\u8d70\u51b7\u50fb\u5c0f\u5df7\u3002 \u3000\u3000\u8bb8\u4e45\u4e4b\u540e\uff0c\u624d\u53c8\u5728\u4e00\u5904\u5df7\u53e3\u505c\u4f4f\u3002 \u3000\u3000\u4ed6\u8f7b\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u91cc\u5c31\u662f\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b49\u56db\u4eba\u5f80\u5df7\u5185\u671b\u53bb\uff0c\u53c8\u662f\u4e00\u6392\u56db\u6237\u4eba\u5bb6\uff0c\u6bcf\u5bb6\u95e8\u53e3\u90fd\u501a\u5899\u7ad9\u7740\u4e00\u4e2a\u795e\u8272\u8ff7\u60d8\u6ee1\u6000 \u5fc3\u4e8b\u7684\u5973\u4eba\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u5973\u4eba\uff0c\u957f\u5f97\u90fd\u9887\u6709\u51e0\u5206\u59ff\u8272\uff0c\u8bba\u5e74\u7eaa\u6700\u5927\u7684\u4e5f\u4e0d\u8fc7\u4e09\u5341\u5f00\u5916\uff0c\u6709\u7684\u53ea\u6709\u4e8c\u5341\u4e03\u516b\uff0c \u5f90\u5a18\u672a\u8001\uff0c\u98ce\u97f5\u5f53\u7136\u66f4\u597d\u3002 \u3000\u3000\u53ea\u56da\u5979\u4eec\u5404\u5bb6\u95e8\u524d\u4e5f\u6302\u4e86\u4e00\u76cf\u706f\u7b3c\uff0c\u6240\u4ee5\u624d\u80fd\u770b\u5f97\u8fd9\u6837\u6e05\u695a\u3002 \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u6b21\u90a3\u4e00\u4f4d\u5148\u8fc7\u53bb\u53d7\u6d4b\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u521a\u624d\u662f\u6211\u5728\u5148\uff0c\u8fd9\u6b21\u5f53\u7136\u4e5f\u8981\u5728\u524d\u9762\uff0c\u5927\u5bb6\u8bf7\u7b49\u7740\uff0c\u770b\u6211\u7684\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u5927\u6b65\u5411\u7b2c\u4e00\u5bb6\u95e8\u53e3\u8d70\u53bb\u3002 \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c \u4e5d \u56de\u3000\u8fde\u4e0b\u4e09\u57ce \u3000\u3000\u7b2c\u4e00\u5bb6\u95e8\u53e3\u7684\u5be1\u5987\uff0c\u9762\u5bf9\u4e00\u4e2a\u7c97\u58ee\u5927\u6c49\u7ad9\u5728\u95e8\u524d\uff0c\u7adf\u7136\u534a\u70b9\u4e0d\u66fe\u7406\u776c\uff0c\u5e72\u8106\u628a\u8138\u504f\u8fc7\u4e00 \u65b9\uff0c\u88c5\u505a\u6ca1\u89c1\u3002 \u3000\u3000\u663e\u7136\uff0c\u8fd9\u5be1\u5987\u6709\u70b9\u8001\u6cb9\u6761\u5473\u9053\u3002 \u3000\u3000\u4e01\u6d9b\u4f4e\u5934\u770b\u53bb\uff0c\u8fd9\u5973\u4eba\u59ff\u8272\u679c\u7136\u4e0d\u9519\uff0c\u4ed6\u771f\u6068\u4e0d\u5f97\u7acb\u523b\u62b1\u4e2a\u6ee1\u6000\uff0c\u5f53\u573a\u4eb2\u70ed\u4eb2\u70ed\u3002 \u3000\u3000\u90a3\u5be1\u5987\u518d\u6cb9\u6761\u4e5f\u662f\u5973\u4eba\uff0c\u89c1\u4e01\u6d9b\u4e00\u76f4\u7ad9\u7740\u4e0d\u8d70\uff0c\u7ec8\u4e8e\u6574\u4e86\u6574\u8138\u8272\uff0c\u51b7\u58f0\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u597d \u65e0\u804a\uff0e\u8001\u7ad9\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u5148\u524d\u5192\u5145\u8001\u592a\u592a\u7684\u513f\u5b50\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9053\u5403\u4e86\u4e8f\uff0c\u6b64\u65f6\u4ed6\u60f3\u5230\u82e5\u80fd\u5192\u5145\u8fd9\u5be1\u5987\u7684\u4e08 \u592b\uff0c\u5c82\u4e0d\u53c8\u53ef\u628a\u4fbf\u5b9c\u8d5a\u56de\u6765\u3002 \u3000\u3000\u4ed6\u62ff\u5b9a\u4e86\u4e3b\u610f\uff0c\u968f\u5373\u5658\u4e86\u5658\u5634\u9053\uff1a\u201c\u5927\u5ac2\uff0c\u4f60\u53ef\u662f\u5728\u7b49\u4f60\u7684\u4e08\u592b\uff1f\u201d \u3000\u3000\u5be1\u5987\u53cc\u76ee\u95ea\u51fa\u5f02\u5149\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u662f\u8981\u773c\u5598\u6c14\uff0c\u5f53\u7136\u4e00\u770b\u5c31\u770b\u5f97\u51fa\u6765\uff0c\u5973\u4eba\u76fc\u4e08\u592b\uff0c\u5c31\u662f\u50cf\u4f60\u8fd9\u79cd\u6a21 \u6837\u3002\u201d \u3000\u3000\u5be1\u5987\u6487\u6487\u5507\u9053\uff1b\u201c\u5c31\u7b97\u88ab\u4f60\u770b\u51fa\u6765\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u5927\u4e0d\u4e86\uff0c\u90a3\u4e2a\u5973\u4eba\u4e0d\u5e0c\u671b\u79bb\u5bb6\u7684\u4e08\u592b\u56de \u6765\uff01\u201d \u3000\u3000\u201c\u4f60\u4e08\u592b\u79bb\u5bb6\u591a\u4e45\u4e86\uff1f\u201d \u3000\u3000\u201c\u5341\u5e74\u3002\u201d \u3000\u3000\u201c\u4f60\u548c\u4ed6\u53ef\u662f\u65b0\u5a5a\u4e0d\u4e45\u5c31\u5206\u5f00\u7684\uff1f\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u89c1\u65f6\u673a\u5df2\u5230\uff0c\u4e00\u628a\u642d\u4e0a\u5be1\u5987\u9999\u80a9\u9053\uff1a\u201c\u90a3\u5c31\u522b\u7b49\u5566\uff0c\u4f60\u4e08\u592b\u5df2\u7ecf\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u5be1\u5987\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u6025\u62e8\u5f00\u4e01\u6d9b\u7684\u624b\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u597d\u4e0d\u8981\u8138\uff0c\u7adf\u6562\u5f53\u8857\u8c03\u620f\u826f\u5bb6\u5987\u5973\uff01\u201d \u3000\u3000\u4e01\u6d9b\u563b\u563b\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u8fde\u5728\u4e00\u4e2a\u88ab\u7a9d\u7761\u89c9\u90fd\u7761\u8fc7\uff0c\u73b0\u5728\u4e3a\u4ec0\u4e48\u5ffd\u7136\u88c5\u8d77\u5047\u6b63\u7ecf\u6765\uff1f\u201d \u3000\u3000\u90a3\u5be1\u5987\u626c\u8d77\u7ea4\u624b\uff0c\u731b\u7136\u5411\u4e01\u6d9b\u9762\u988a\u7529\u53bb\u3002 \u3000\u3000\u4e01\u6d9b\u56e0\u672a\u7559\u610f\uff0c\u6b63\u88ab\u4e00\u638c\u63b4\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u522b\u770b\u8fd9\u5c0f\u5be1\u5987\u4e0d\u4f1a\u6b66\u529f\uff0c\u4f46\u5728\u6c14\u6781\u4e4b\u4e0b\u51fa\u624b\uff0c\u7167\u6837\u628a\u4e01\u6d9b\u6253\u5f97\u9762\u988a\u706b\u8fa3\uff0c\u773c\u524d\u91d1\u661f\u76f4\u5192\u3002 \u3000\u3000\u4e01\u6d9b\u6342\u7740\u9762\u988a\u53eb\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6253\u4eba\uff1f\u201d \u3000\u3000\u5be1\u5987\u53f1\u9053\uff1a\u201c\u6ca1\u6495\u4e86\u4f60\u7684\u5634\u5c31\u5df2\u7ecf\u5f88\u597d\u4e86\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u53ef\u4ee5\uff0c\u6211\u662f\u4f60\u4e08\u5927\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u201c\u653e\u5c41\uff0c\u6211\u4e08\u592b\u600e\u4f1a\u662f\u4f60\uff1f\u201d \u3000\u3000\u201c\u54b1\u4eec\u65b0\u5a5a\u4e0d\u4e45\u6211\u5c31\u79bb\u5bb6\u4e86\uff0c\u5982\u4eca\u9694\u4e86\u5341\u5348\uff0e\u4f60\u5f53\u7136\u8ba4\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u653e\u4f60\u5a18\u90a3\u4e03\u5341\u4e8c\u4e2a\u8fde\u73af\u5c41\uff01\u201d \u3000\u3000\u201c\u4f60\u542c\u6211\u89e3\u91ca\uff0c\u5148\u95ee\u95ee\u4f60\uff0c\u77e5\u4e0d\u77e5\u9053\u5f53\u5e74\u738b\u5b9d\u948f\u82e6\u5b88\u5bd2\u7a91\u5341\u516b\u5e74\u6545\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6545\u4e8b\u8fde\u4e09\u5c81\u7684\u5b69\u5b50\u90fd\u77e5\u9053\uff0c\u4f60\u95ee\u8fd9\u4e2a\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5f53\u5e74\u859b\u5e73\u8d35\u4ece\u897f\u51c9\u56fd\u56de\u6765\uff0c\u7389\u5b9d\u948f\u4e5f\u4e0d\u8ba4\u8bc6\uff0c\u7ecf\u8fc7\u859b\u5e73\u8d35\u4e00\u89e3\u91ca\uff0c\u4ed6\u4eec\u7acb\u523b\u5c31\u592b\u59bb\u76f8 \u8ba4\u4e86\u3002\u201d \u3000\u3000\u5be1\u5987\u51b7\u7b11\u9053\uff1a\u201c\u6211\u95ee\u4f60\uff0c\u5f53\u5e74\u738b\u5b9d\u948f\u4e3a\u4ec0\u4e48\u4e0d\u8ba4\u8bc6\u859b\u5e73\u8d35\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4ed6\u4eec\u5206\u522b\u4e86\u5341\u516b\u5e74\uff0c\u859b\u5e73\u8d35\u56de\u6765\u65f6\u4eba\u8001\u4e86\uff0c\u6a21\u6837\u513f\u4e5f\u53d8\u4e86\uff0c\u80e1\u5b50\u4e5f\u957f\u51fa\u6765\u4e86\uff0c \u6240\u4ee5\u5f53\u65f6\u738b\u5b9d\u948f\u624d\u4e0d\u8ba4\u8bc6\u4ed6\u3002\u201d \u3000\u3000\u5be1\u5987\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u4f60\u4e5f\u662f\u6a21\u6837\u53d8\u4e86\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u90a3\u8fd8\u7528\u8bf4\uff0c\u5f53\u5e74\u6211\u4e5f\u662f\u4e2a\u5c0f\u767d\u8138\uff0c\u7ecf\u8fc7\u8fd9\u5341\u5e74\uff0c\u53c8\u600e\u80fd\u4e0d\u53d8\u9ed1\uff1f\u201d \u3000\u3000\u5be1\u5987\u53f1\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u6211\u4e08\u592b\u662f\u4e2a\u5c0f\u77ee\u5b50\uff0c\u600e\u4f1a\u53d8\u5f97\u50cf\u4f60\u8fd9\u6837\u9ad8\uff1f\u4eba\u5bb6\u859b\u5e73\u8d35\u53ea\u662f\u6a21\u6837 \u53d8\u4e86\uff0c\u8eab\u91cf\u53c8\u600e\u80fd\u53d8\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8eab\u91cf\u662f\u957f\u51fa\u6765\u7684\uff0c\u4f60\u600e\u77e5\u8fd9\u5341\u5e74\u6211\u4e0d\u4f1a\u957f\u9ad8\uff1f\u201d \u3000\u3000\u5be1\u5987\u9053\uff1a\u201c\u6211\u4e08\u592b\u79bb\u5bb6\u5341\u5e74\u591a\u4e86\uff0c\u90a3\u6709\u4e8c\u5341\u51e0\u5c81\u8fd8\u4f1a\u518d\u957f\u9ad8\u7684\uff1f \u3000\u3000\u6b64\u5916\u80fd\u957f\u9ad8\u4e9b\uff0c\u4e5f\u4e0d\u53ef\u80fd\u957f\u5f97\u6bd4\u4ece\u524d\u9ad8\u51fa\u4e24\u4e2a\u5934\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u6025\u5f97\u55d3\u5b50\u773c\u91cc\u76f4\u54b3\u9053\uff1a\u201c\u6211\u662f\u552f\u4e00\u4f8b\u5916\u7684\u3002\u201d \u3000\u3000\u5be1\u5987\u55d4\u76ee\u6012\u77aa\u7740\u4e01\u6d9b\u9053\uff1a\u201c\u518d\u544a\u8bc9\u6211\uff0c\u4f60\u59d3\u4ec0\u4e48\uff1f\u5982\u679c\u8bf4\u5bf9\u4e86\uff0c\u6211\u5c31\u627f\u8ba4\u4f60\u662f\u6211\u4e08\u592b\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6765\u4e01\u6d9b\u771f\u6025\u4e86\uff0c\u6025\u5f97\u4ed6\u6293\u8033\u6414\u816e\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u8bdd\uff1f\u96be\u9053\u8fde\u81ea\u5df1\u59d3\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u6211\u59d3\u2026\u2026\u59d3\u4e01\u3002\u201d \u3000\u3000\u5be1\u5987\u4e00\u53e3\u553e\u6db2\u5550\u5230\u4e01\u6d9b\u8138\u4e0a\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c\u897f\uff0c\u6562\u6765\u5360\u8001\u5a18\u7684\u4fbf\u5b9c\uff0c\u8001\u5a18\u4eca\u665a\u771f\u6666\u6c14\uff0c\u65e0 \u7f18\u65e0\u6545\u78b0\u5230\u4e86\u9b3c\uff0c\u5feb\u6eda\u51fa\u53bb\uff01\u201d \u3000\u3000\u5979\u8bf4\u5b8c\u8bdd\uff0e\u53c8\u5550\u4e86\u4e00\u53e3\uff0c\u8f6c\u8eab\u8fdb\u5165\u95e8\u5185\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u4fbf\u628a\u5927\u95e8\u5173\u4e0a\u3002 \u3000\u3000\u4e01\u6d9b\u72fc\u72c8\u4e0d\u582a\u7070\u5934\u571f\u8138\u7684\u8d70\u4e86\u56de\u6765\u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff0c\u78b0\u5566\uff01\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u50cf\u4f60\u90a3\u6837\u8bb2\u8bdd\uff0c\u54ea\u6709\u4e0d\u78b0\u7684\u3002\u201d \u3000\u3000\u4e01\u6d9b\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u6bd4\u6211\u9ad8\u660e\u4e0d\u4e86\u591a\u5c11\uff0c\u4e0d\u4fe1\u5c31\u53bb\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u6211\u5f53\u7136\u8981\u8bd5\uff0c\u5373\u4fbf\u8bd5\u4e0d\u597d\uff0c\u4e5f\u51b3\u4e0d\u4f1a\u4e22\u4f60\u90a3\u79cd\u4eba\uff01\u201d \u3000\u3000\u7518\u9716\u6765\u5230\u7b2c\u4e8c\u5bb6\u95e8\u53e3\uff0c\u90a3\u5be1\u5987\u4e5f\u662f\u4e0d\u7406\u4e0d\u776c\u3002 \u3000\u3000\u7518\u9716\u975e\u5e38\u61c2\u793c\u8c8c\uff0c\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u5927\u5ac2\u5b50\u53ef\u662f\u5728\u7b49\u4eba\uff1f\u201d \u3000\u3000\u90a3\u5be1\u5987\u89c1\u6765\u4eba\u8868\u73b0\u5f97\u5f6c\u5f6c\u6709\u793c\uff0c\u4e0d\u5f97\u4e0d\u7406\uff0c\u70b9\u4e86\u4e0b\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u5728\u7b49\u4eba\u3002\u201d \u3000\u3000\u201c\u7b49\u8c01\uff1f\u201d \u3000\u3000\u201c\u6211\u4e08\u592b\u3002\u201d \u3000\u3000\u201c\u5927\u5ac2\u5b50\u4e0d\u5fc3\u7b49\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u5fc5\u7b49\uff1f\u201d \u3000\u3000\u201c\u6211\u5c31\u662f\u6765\u9001\u4fe1\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u4f4d\u5927\u54e5\u9001\u4ec0\u4e48\u4fe1\uff1f\u201d \u3000\u3000\u201c\u6211\u548c\u4f60\u4e08\u592b\u662f\u751f\u6b7b\u4e4b\u4ea4\uff0c\u4ed6\u524d\u4e9b\u5929\u5631\u6258\u6211\u4e00\u4ef6\u4e8b\uff0c\u6211\u957f\u9014\u8dcb\u6d89\u5343\u8f9b\u4e07\u82e6\u627e\u5230\u5e9c\u4e0a\uff0c\u5c31 \u662f\u8981\u4e0d\u8d1f\u4ed6\u7684\u4ea4\u6258\u3002\u201d \u3000\u3000\u90a3\u5be1\u5987\u4e24\u773c\u7728\u7728\u7684\u9053\uff1a\u201c\u8fd9\u4f4d\u5927\u54e5\uff0c\u5230\u5e95\u662f\u4ec0\u4e48\u4e8b\uff1f\u4f60\u600e\u4e48\u59cb\u7ec8\u4e0d\u8bf4\u51fa\u6765\uff1f\u201d \u3000\u3000\u7518\u9716\u8e59\u7709\u53f9\u53e3\u6c14\u7684\u9053\uff1a\u201c\u5929\u6709\u4e0d\u6d4b\u98ce\u4e91\uff0c\u4eba\u6709\u65e6\u5915\u7978\u798f\uff0c\u4f60\u4e08\u592b\u6b7b\u4e86\uff01\u201d \u3000\u3000\u90a3\u5be1\u5987\u7acb\u5373\u63a9\u9762\u75db\u54ed\u8d77\u6765\u3002 \u3000\u3000\u7518\u9716\u5b89\u6170\u7740\u9053\uff1a\u201c\u5927\u5ac2\u5b50\u8981\u8282\u54c0\u987a\u53d8\uff0c\u4eba\u6b7b\u4e0d\u80fd\u590d\u6d3b\uff0c\u4f60\u8fd8\u5e74\u8f7b\uff0c\u6765\u65e5\u65b9\u957f\uff0c\u4fdd\u91cd\u8eab\u5b50 \u8981\u7d27\uff01\u201d \u3000\u3000\u90a3\u5be1\u5987\u62bd\u564e\u7740\u6025\u6025\u8f6c\u8eab\u5411\u91cc\u8dd1\u53bb\u3002 \u3000\u3000\u7518\u9716\u751a\u4e3a\u5f97\u610f\uff0c\u633a\u7740\u80f8\u6661\u8d70\u4e86\u56de\u6765\u9053\uff1a\u201c\u8001\u4e01\uff0c\u4f60\u770b\u600e\u4e48\u6837\uff0c\u4e09\u8a00\u4e24\u8bed\u5c31\u628a\u5979\u6253\u53d1\u56de\u53bb \u4e86\u3002\u201d \u3000\u3000\u4e01\u6d9b\u659c\u4e86\u7518\u9716\u4e00\u773c\u9053\uff1a\u201c\u4f60\u8981\u8ba9\u5979\u9ad8\u9ad8\u5174\u5174\u56de\u53bb\u624d\u7b97\u672c\u4e8b\uff01\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u6bd4\u4f60\u5f3a\u591a\u4e86\uff0c\u81f3\u5c11\u6ca1\u6328\u8033\u5149\uff0c\u4e5f\u6ca1\u8ba9\u4eba\u628a\u53e3\u6c34\u5410\u5230\u8138\u4e0a\u3002\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u522b\u5435\u4e86\uff0c\u73b0\u5728\u8be5\u54ea\u4e00\u4f4d\u53bb\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u8fd8\u662f\u7531\u665a\u8f88\u7b2c\u4e09\u4e2a\u53d7\u6d4b\u597d\u4e86\uff01\u201d \u3000\u3000\u4ed6\u6765\u5230\u7b2c\u4e09\u5bb6\u95e8\u53e3\uff0c\u9887\u6709\u793c\u8c8c\u7684\u63d6\u4e86\u4e00\u63d6\u9053\uff1a\u201c\u5927\u5ac2\u5b50\u53ef\u662f\u5728\u76fc\u671b\u5927\u54e5\uff1f\u201d \u3000\u3000\u90a3\u5be1\u5987\u8138\u4e0a\u4e00\u559c\u9053\uff1a\u201c\u8fd9\u4f4d\u5148\u751f\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5c0f\u5f1f\u597d\u4e0d\u5bb9\u6613\u624d\u627e\u5230\u5e9c\u4e0a\uff0c\u7279\u6765\u7ed0\u5927\u5ac2\u9001\u4fe1\u513f\u3002\u201d \u3000\u3000\u90a3\u5be1\u5987\u53cc\u76ee\u7a81\u73b0\u5947\u5149\uff1a\u201c\u83ab\u975e\u4f60\u6709\u4ed6\u7684\u6d88\u606f\uff0c\u4ed6\u5728\u54ea\u91cc\uff1f\u8fd9\u4e9b\u5e74\u53ef\u597d\u4e48\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u5c0f\u5f1f\u4e00\u76f4\u548c\u4ed6\u5728\u4e00\u8d77\uff0c\u600e\u4f1a\u4e0d\u77e5\u4ed6\u7684\u6d88\u606f\uff1f\u4ed6\u5728\u4e4c\u9c81\u6728\u9f50\u3002\u201d \u3000\u3000\u201c\u5728\u4e4c\u9c81\u6728\u9f50\uff1f\u4ed6\u600e\u4f1a\u79bb\u5bb6\u90a3\u4e48\u8fdc\uff1f\u602a\u4e0d\u5f97\u8fd9\u591a\u5e74\u6ca1\u6709\u97f3\u4fe1\u3002\u201d \u3000\u3000\u201c\u5c31\u662f\u561b\uff0c\u4ed6\u60f3\u5bb6\uff0c\u66f4\u60f3\u5927\u5ac2\uff0c\u5982\u679c\u56de\u5bb6\u65b9\u4fbf\uff0c\u65e9\u5c31\u56de\u5bb6\u4e86\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u8fd9\u4e48\u8fdc\u56de\u6765\u62a5\u4fe1\uff0c\u771f\u4e0d\u597d\u610f\u601d\uff01\u201d \u3000\u3000\u201c\u6211\u662f\u6709\u4e8b\u5230\u957f\u5b89\u6765\uff0c\u987a\u4fbf\u5230\u5e9c\u4e0a\u62a5\u4fe1\u513f\u3002\u201d \u3000\u3000\u201c\u4ed6\u53ef\u8bf4\u8fc7\u4ec0\u4e48\u65f6\u5019\u56de\u6765\uff1f\u201d \u3000\u3000\u201c\u5927\u5ac2\u5b50\u6e05\u653e\u5fc3\uff0c\u4eca\u5e74\u4e2d\u79cb\u8282\uff0c\u4ed6\u4e00\u5b9a\u56de\u5bb6\u56e2\u5706\u3002\u201d \u3000\u3000\u201c\u771f\u662f\u8c22\u5929\u8c22\u5730\uff0c\u66f4\u611f\u8c22\u4f60\u8001\u8fdc\u6765\u9001\u4fe1\u513f\uff01\u201d \u3000\u3000\u201c\u90a3\u4e48\u5927\u5ac2\u5b50\u73b0\u5728\u5c31\u4e0d\u5fc5\u5728\u95e8\u5916\u76fc\u671b\u4e86\uff0c\u4e2d\u79cb\u4e00\u5230\uff0c\u5c0f\u5f1f\u4fdd\u8bc1\u4ed6\u56de\u6765\uff01\u201d \u3000\u3000\u90a3\u5be1\u5987\u7709\u5f00\u773c\u7b11\u7684\uff0c\u518d\u5411\u8881\u5c0f\u9e64\u9053\u4e86\u8c22\uff0c\u624d\u8f6c\u8eab\u56de\u4e86\u5bb6\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u4e5f\u7b11\u5475\u5475\u7684\u56de\u5230\u539f\u5904\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6765\u5230\u7b2c\u56db\u5bb6\u95e8\u53e3\uff0c\u5de6\u53f3\u7aef\u8be6\u4e86\u597d\u4e00\u4f1a\uff0c\u53c8\u5411\u95e8\u5185\u671b\u4e86\u671b\uff0c\u624d\u8bed\u6c14\u5174\u594b\u7684\u9053\uff1a\u201c\u603b \u7b97\u7ed9\u6211\u627e\u5230\u4e86\uff01\u201d \u3000\u3000\u90a3\u5be1\u5987\u60c5\u4e0d\u81ea\u7981\u95ee\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u627e\u5230\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u627e\u5230\u4e86\u6211\u90a3\u7ed3\u62dc\u5927\u54e5\u7684\u5bb6\uff01\u201d \u3000\u3000\u90a3\u5be1\u5987\u60ca\u559c\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u90a3\u7ed3\u62dc\u7684\u5927\u54e5\u53eb\u5218\u4e09\u5347\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u5403\u60ca\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\uff0c\u4ed6\u5c31\u53eb\u5218\u4e09\u5347\uff0c\u79bb\u5f00\u5bb6\u597d\u591a\u5e74\u4e86\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u90a3\u5be1\u5987\u9053\uff1a\u201c\u8c01\u8bf4\u4e0d\u662f\uff0c\u4ed6\u79bb\u5f00\u5bb6\u5df2\u7ecf\u5feb\u5341\u5e74\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u4f60\u5c31\u662f\u5218\u5ac2\u5b50\u4e86\uff1f\u201d \u3000\u3000\u90a3\u5be1\u5987\u8138\u8272\u5fae\u7ea2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u6b63\u662f\u4ed6\u7684\u59bb\u5b50\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u559c\u9053\uff1a\u201c\u771f\u5de7\uff0c\u5728\u95e8\u5916\u9047\u5230\u4e86\u5218\u5ac2\u5b50\uff0c\u5c0f\u5f1f\u5931\u793c\uff0c\u8fd8\u6ca1\u5411\u5ac2\u5b50\u95ee\u5b89\uff01\u201d \u3000\u3000\u90a3\u5be1\u5987\u8bed\u6c14\u4eb2\u5207\u7684\u5fd9\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u5feb\u522b\u8fd9\u4e48\u5ba2\u6c14\uff0c\u4f60\u8fd8\u6ca1\u544a\u8bc9\u6211\u4e09\u5347\u5728\u5916\u9762\u7684\u72b6\u51b5\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5218\u5927\u54e5\u771f\u662f\u65f6\u6765\u8fd0\u8f6c\uff0c\u524d\u4e9b\u5e74\u5728\u5916\u9762\u6df7\u5f97\u5e76\u4e0d\u597d\uff0c\u6240\u4ee5\u4ed6\u624d\u4e0d\u597d\u610f\u601d\u56de\u5bb6\uff0c \u53ef\u662f\u4ece\u524d\u5e74\u8d77\uff0c\u4ed6\u5c31\u53d1\u4e86\u5927\u8d22\u5566\uff0c\u4ed6\u6700\u8fd1\u5c31\u8981\u56de\u5bb6\uff0c\u771f\u53ef\u79f0\u5f97\u4e0a\u8863\u9526\u8363\u5f52\uff01\u201d \u3000\u3000\u90a3\u5be1\u5987\u4e5f\u60ca\u559c\u5f97\u8fde\u58f0\u9053\uff1a\u201c\u771f\u7684\uff0c\u603b\u7b97\u6211\u6ca1\u767d\u76fc\u671b\uff0c\u5c0f\u5144\u5f1f\uff0c\u5feb\u8bf7\u5230\u91cc\u9762\u559d\u676f\u8336\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u62f1\u62f1\u624b\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u518d\u559d\uff0c\u5c0f\u5f1f\u8fd8\u6709\u53e6\u4e00\u4ef6\u4e8b\u5fc5\u987b\u9a6c\u4e0a\u53bb\u529e\uff0c\u5929\u8fd9\u4e48\u665a\u4e86\uff0c\u5ac2\u5b50 \u4e5f\u8bf7\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u90a3\u5be1\u5987\u4e34\u8fdb\u95e8\u65f6\uff0c\u8fd8\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u5f85\u4f1a\u513f\u5c0f\u5144\u5f1f\u4e00\u5b9a\u8981\u6765\uff0c\u6211\u7b49\u7740\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5e76\u672a\u8868\u73b0\u51fa\u6d0b\u6d0b\u5f97\u610f\u6a21\u6837\uff0c\u8d70\u56de\u539f\u5904\u9053\uff1a\u201c\u8001\u5148\u751f\uff0c\u7b2c\u4e8c\u7ad9\u5df2\u7ecf\u6d4b\u5b8c\uff0c\u8be5\u5230\u4e0b\u4e00 \u7ad9\u53bb\u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u62a2\u7740\u4e00\u62c9\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u4f60\u4e00\u5411\u89c4\u89c4\u77e9\u77e9\uff0c\u809a\u5b50\u91cc\u597d\u50cf\u5e76\u6ca1\u6709\u4ec0\u4e48\u602a\u70b9\u5b50\uff0c \u600e\u4e48\u5ffd\u7136\u95f4\u602a\u70b9\u5b50\u7adf\u90a3\u4e48\u591a\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5176\u5b9e\u5c0f\u5f1f\u4e5f\u4e0d\u613f\u8fd9\u4f6f\u505a\uff0c\u4f46\u4e0d\u8fd9\u6837\u505a\u5c31\u65e0\u6cd5\u901a\u8fc7\u6d4b\u8bd5\uff0c\u53ea\u80fd\u8bf4\u662f\u903c\u51fa\u6765\u7684 \u7f62\u4e86\u3002\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u73b0\u5728\u8001\u673d\u5c31\u5ba3\u5e03\u4e0b\u4e00\u7ad9\uff0c\u4e0b\u4e00\u7ad9\u53eb\u2018\u7a91\u59d0\u62c9\u5ba2\u2019\u3002\u201d \u3000\u3000\u4e01\u6d9b\u7b11\u563b\u563b\u7684\u53eb\u9053\uff1a\u201c\u90a3\u592a\u597d\u4e86\uff01\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u77e5\u9053\uff0c\u7a91\u59d0\u513f\u5c31\u662f\u5993\u5973\uff0c\u90a3\u7ad9\u5728\u95e8\u5916\u7684\u5993\u5973\uff0c\u51e0\u4e4e\u6ca1\u6709\u4e00 \u4e2a\u4e0d\u5411\u8fc7\u8def\u7537\u4eba\u7ea0\u7f20\u7684\uff0c\u8fd9\u5c31\u53eb\u7a91\u59d0\u62c9\u5ba2\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u90a3\u7a91\u59d0\u62c9\u5ba2\uff0c\u62c9\u5230\u8c01\u8c01\u5c31\u8fdb\u53bb\uff0c\u3000\u4e00\u4e2a\u8981\u94b1\uff0c\u4e00\u4e2a\u8981\u8d27\uff0c\u90a3\u4e0d\u662f\u6b63\u597d\u4e48\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u6447\u5934\u9053\uff1a\u201c\u82e5\u88ab\u5979\u62c9\u8fdb\u53bb\uff0c\u6d4b\u8bd5\u6210\u7ee9\u5c31\u662f\u96f6\u5206\u3002\u201d \u3000\u3000\u4e01\u6d9b\u7728\u7740\u725b\u773c\u9053\uff0c\u201c\u600e\u6837\u624d\u80fd\u5f97\u5230\u9ad8\u5206\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u5230\u4e86\u90a3\u6761\u5df7\u5b50\uff0c\u5df7\u5b50\u4e24\u65c1\u7684\u95e8\u53e3\uff0c\u90fd\u662f\u7a91\u59d0\u513f\uff0c\u5979\u4eec\u89c1\u4e86\u7537\u4eba\u5c31 \u62c9\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u90a3\u5f53\u7136\u8981\u62c9\uff0c\u51e1\u662f\u5230\u90a3\u91cc\u53bb\u7684\uff0c\u54ea\u4e00\u4e2a\u4e0d\u60f3\u89e3\u51b3\u95ee\u9898\uff0c\u59d1\u5a18\u4eec\u62a2\u5ba2\u4eba\uff0c\u7406\u6240 \u5f53\u7136\u3002\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u90a3\u6761\u5df7\u5b50\u5f88\u7a84\uff0c\u7a91\u59d0\u513f\u4e00\u4f38\u624b\u5c31\u53ef\u4ee5\u6293\u5230\u5ba2\u4eba\uff0c\u4f60\u4eec\u8981\u505a\u7684\uff0c\u5c31\u662f\u8981\u5979 \u4eec\u4e0d\u62c9\uff0c\u987a\u987a\u5229\u5229\u7684\u901a\u8fc7\uff0c\u4f46\u53c8\u4e0d\u80fd\u5bf9\u5979\u4eec\u51f6\uff0c\u751a\u81f3\u4e0d\u5f97\u7f6a\u5979\u4eec\uff0c\u80fd\u505a\u5230\u8fd9\u6837\uff0c\u90a3\u5c31\u8fbe\u5230\u8981 \u6c42\u4e86\u3002\u201d \u3000\u3000\u4e01\u6d9b\u8e59\u8d77\u7709\u5934\u9053\uff1a\u201c\u8fd9\u597d\u50cf\u5f88\u96be\uff01\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u505a\u4e0d\u5230\u5c31\u522b\u60f3\u8981\u6210\u7ee9\u3002\u201d \u3000\u3000\u4e01\u6d9b\u6447\u6447\u5934\u9053\uff1a\u201c\u90a3\u5c31\u8bd5\u8bd5\u770b\u5427\uff01\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u5e26\u7740\u56db\u4eba\u53c8\u4e00\u9635\u8f6c\u5f2f\u62b9\u89d2\uff0c\u5f88\u5feb\u4fbf\u5230\u4e86\u4e00\u5904\u7a84\u5df7\u3002 \u3000\u3000\u8fd8\u6ca1\u5230\u5df7\u53e3\uff0c\u5c31\u5df2\u542c\u5230\u5431\u5431\u55b3\u55b3\uff0c\u6253\u60c5\u9a82\u4fcf\u7684\u58f0\u97f3\u4e00\u56e2\u4e71\u7cdf\u7cdf\u7684\u3002 \u3000\u3000\u53ca\u81f3\u5230\u4e86\u5df7\u53e3\uff0c\u53ea\u89c1\u5df7\u9053\u4e24\u65c1\uff0c\u6709\u4e0d\u5c11\u95e8\u6237\u6392\u5217\u7740\uff0c\u95e8\u4e0a\u90fd\u6302\u7740\u4e00\u76cf\u706f\uff0c\u8db3\u6709\u5341\u513f\u540d\u6253 \u626e\u5f97\u5996\u5996\u5a06\u5a06\u82b1\u679d\u62db\u5c55\u7684\u7a91\u59d0\u513f\uff0c\u90fd \u3000\u3000\u6b6a\u6b6a\u659c\u659c\u7684\u7ad9\u5728\u95e8\u53e3\uff0c\u6709\u7684\u6b63\u5728\u5411\u6765\u5f80\u7684\u5bfb\u82b3\u5ba2\u4eec\u62c9\u62c9\u626f\u626f\uff0c\u518d\u52a0\u4e0a\u6492\u5a07\u5356\u55f2\uff0c\u6784\u6210\u2014 \u5e45\u4e0d\u582a\u5165\u76ee\u7684\u753b\u9762\u3002 \u3000\u3000\u6240\u8c13\u4e0d\u582a\u5165\u76ee\uff0c\u5728\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u773c\u4e2d\uff0c\u7684\u786e\u5982\u6b64\uff0c\u4f46\u5728\u4e01\u6d9b\u548c\u7518\u9716\u773c\u4e2d\uff0c\u5374\u5c31\u5b8c\u5168\u4e0d \u540c\u4e86\uff0c\u4ed6\u4eec\u770b\u6765\u53cd\u800c\u5341\u5206\u9017\u8da3\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u51e0\u66fe\u770b\u5230\u8fd9\u7b49\u9635\u4ed7\uff0c\u4ed6\u4eec\u867d\u60f3\u6389\u5934\u800c\u53bb\uff0c\u4f46\u8fd9\u91cc\u504f\u504f\u662f\u4e00\u5904\u6d4b\u8bd5\u7ad9\uff0e\u4e3a \u4e86\u8fc7\u5173\uff0c\u4ed6\u4eec\u53c8\u4e0d\u5f97\u4e0d\u786c\u8d77\u5934\u76ae\u52c9\u5f3a \u3000\u3000\u4e00\u8bd5\u3002 \u3000\u3000\u7070\u8863\u8001\u4eba\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u770b\u6e05\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u6211\u4eec\u5f53\u7136\u770b\u5f97\u6e05\uff0c\u53ea\u6015\u8fde\u778e\u5b50\u4e5f\u8981\u7741\u5f00\u773c\u770b\u770b\uff01\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u73b0\u5728\u7531\u8c01\u5148\u901a\u8fc7\u8fd9\u6761\u5df7\u9053\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u524d\u4e24\u7ad9\u90fd\u662f\u6211\u6253\u5934\u9635\uff0c\u8fd9\u4e00\u7ad9\u4e5f\u8ba9\u6211\u5148\u6765\u5427\uff01\u201d \u3000\u3000\u5ffd\u542c\u7518\u9716\u9053\uff1a\u201c\u8001\u5148\u751f\uff0c\u901a\u8fc7\u4ee5\u540e\uff0c\u662f\u5426\u8fd8\u8981\u518d\u56de\u6765\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u901a\u8fc7\u540e\u5c31\u5728\u5bf9\u9762\u627e\u5904\u9690\u79d8\u7684\u5730\u65b9\u7b49\u7740\uff0c\u7136\u540e\u4e00\u4e2a\u4e2a\u5728\u90a3\u91cc\u76f8\u4f1a\u3002\u201d \u3000\u3000\u4e01\u6d9b\u89c1\u7070\u8863\u8001\u4eba\u4ea4\u4ee3\u5b8c\u6bd5\uff0c\u7acb\u5373\u633a\u7740\u80f8\u812f\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8bf4\u5b9e\u5728\u7684\uff0c\u4ed6\u771f\u60f3\u7a91\u59d0\u4eec\u6765\u62c9\uff0c\u5fc3\u91cc\u4e0d\u4f4f\u5600\u5495\u7740\u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff0c\u7ecf\u8fc7\u8fd9\u79cd\u5730\u65b9\u4e0d\u8ba9\u5979 \u4eec\u6765\u62c9\uff0c\u7b80\u76f4\u4e0d\u901a\u4eba\u6027\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u91cc\u60f3\u662f\u8fd9\u6837\u60f3\uff0c\u4f46\u4e3a\u4e86\u4e89\u53d6\u6210\u7ee9\uff0c\u8fd8\u662f\u4e0d\u5f97\u4e0d\u7167\u7070\u8863\u8001\u4eba\u7684\u89c4\u5b9a\u53bb\u505a\u3002 \u3000\u3000\u8fd8\u6ca1\u63a5\u8fd1\u90a3\u4e9b\u7a91\u59d0\u513f\uff0c\u7a91\u59d0\u513f\u4eec\u5f53\u4e2d\u65e9\u5c31\u6709\u4eba\u5403\u60ca\u7684\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u54ce\u5440\uff0c\u8fd9\u4eba\u4e2a\u5b50\u597d \u5927\uff01\u201d \u3000\u3000\u7d27\u8ddf\u7740\u53c8\u6709\u4eba\u558a\uff1a\u201c\u8fd9\u5927\u4e2a\u5b50\u597d\u50cf\u662f\u7b2c\u4e00\u6b21\u6765\uff01\u201d \u3000\u3000\u53c8\u6709\u7a91\u59d0\u53eb\u9053\uff1a\u201c\u662f\u554a\uff01\u4ece\u524d\u6ca1\u89c1\u8fc7\uff01\u201d \u3000\u3000\u63a5\u7740\uff0c\u4e00\u4e0b\u5b50\u4fbf\u662f\u597d\u51e0\u4e2a\u7a91\u59d0\u513f\u4e00\u62e5\u800c\u4e0a\uff0c\u6709\u7684\u6293\u624b\uff0c\u6709\u7684\u62c9\u8863\u670d\uff0e\u6709\u7684\u8df3\u8d77\u6765\u62cd\u80a9\u8180\uff0c \u66f4\u6709\u7684\u626f\u88e4\u8170\u5e26\u3002 \u3000\u3000\u6709\u7684\u56b7\u9053\uff1a\u201c\u5927\u4e2a\u5b50\uff1f\u5230\u6211\u623f\u91cc\u53bb\uff0e\u6211\u4f1a\u597d\u597d\u62db\u5f85\u4f60\uff01\u201d \u3000\u3000\u6253\u7684\u558a\u9053\uff1a\u201c\u5927\u4e2a\u5b50\uff0c\u8fd8\u662f\u6211\u90a3\u91cc\u597d\uff01\u201d \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0e\u5431\u5431\u55b3\u55b3\uff0e\u4f60\u4e89\u6211\u593a\uff0c\u628a\u4e2a\u4e01\u6d9b\u9677\u5165\u8102\u7c89\u9635\u4e2d\u3002 \u3000\u3000\u6b64\u523b\u4e01\u6d9b\u771f\u662f\u5929\u4eba\u4ea4\u6218\uff0c\u6700\u540e\u4ed6\u8fd8\u662f\u51b3\u5b9a\u8981\u6210\u7ee9\uff0c\u7b49\u901a\u8fc7\u8fd9\u4e00\u7ad9\u540e\uff0c\u5c3d\u53ef\u4e0b\u6b21\u518d\u6765\u3002\u4f46 \u4ed6\u53c8\u5b9e\u5728\u65e0\u6cd5\u8131\u8eab\u3002 \u3000\u3000\u60c5\u6025\u4e4b\u4e0b\uff0c\u54ea\u91cc\u8fd8\u987e\u5f97\u4e00\u5207\uff0c\u7acb\u523b\u62d4\u51fa\u539a\u80cc\u9b3c\u5934\u5200\uff0c\u5927\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u8eb2\u5f00\uff0c\u8c01\u8dd1\u6162 \u4e86\u8001\u5b50\u5c31\u6740\u8c01\uff01\u201d \u3000\u3000\u90a3\u4e9b\u7a91\u59d0\u4eec\u987f\u65f6\u82b1\u5bb9\u5931\u8272\uff0c\u5413\u5f97\u5c41\u6eda\u5c3f\u6d41\uff0c\u6492\u817f\u5c31\u8dd1\u3002 \u3000\u3000\u5c82\u6b62\u5982\u6b64\u3002\u8fde\u6df7\u5728\u5df7\u9053\u91cc\u7684\u5bfb\u82b3\u5ba2\uff0c\u4e5f\u7eb7\u7eb7\u9003\u907f\u3002 \u3000\u3000\u4e01\u6d9b\u4e3a\u4e86\u8868\u793a\u5a01\u98ce\uff0c\u7d22\u6027\u628a\u539a\u80cc\u9b3c\u5934\u5200\u6325\u821e\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u8fd9\u539a\u80cc\u9b3c\u5934\u5200\u91cd\u6709\u56db\u4e94\u5341\u65a4\uff0c\u957f\u5ea6\u4e5f\u6709\u56db\u5c3a\u4ee5\u4e0a\uff0c\u53ea\u56e0\u5df7\u9053\u4eba\u7a84\uff0c\u8fd9\u4e00\u821e\u52a8\uff0c\u96be\u514d\u5c31 \u780d\u4e0a\u4e86\u4e24\u8fb9\u7684\u5899\u58c1\u3002 \u3000\u3000\u4ed6\u529b\u5927\u65e0\u7a77\uff0c\u8fd9\u4e00\u780d\u4e0d\u6253\u7d27\uff0c\u8fde\u5899\u4e0a\u7684\u7816\u90fd\u780d\u5f97\u4e00\u5757\u5757\u7684\u7eb7\u7eb7\u5d29\u843d\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u5df7\u9053\u5185\u8fde\u4e2a\u4eba\u5f71\u90fd\u4e0d\u89c1\uff0c\u8fde\u95e8\u6237\u90fd\u5168\u6570\u7d27\u95ed\uff0c\u4e01\u6d9b\u633a\u7740\u80f8\u6661\u5c31\u6beb\u65e0\u963b\u788d\u7684\u8d70\u4e86\u8fc7 \u53bb\u3002 \u3000\u3000\u7ad9\u5728\u8fd9\u8fb9\u5df7\u53e3\u7684\u7518\u9716\u76b1\u7740\u7709\u6447\u5934\u9053\uff1a\u201c\u7a91\u59d0\u4eec\u90fd\u88ab\u5413\u8dd1\u4e86\uff0c\u522b\u4eba\u8fd8\u600e\u4e48\u6d4b\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u6478\u7740\u5634\u4e0d\u7d27\u4e0d\u6162\u7684\u9053\uff1a\u201c\u7a0d\u5f85\u4e00\u4f1a\u513f\uff0c\u5979\u4eec\u9a6c\u4e0a\u5c31\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u679c\u7136\u662f\u7ecf\u9a8c\u4e4b\u8c08\uff0c\u534a\u76cf\u70ed\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u5404\u9f50\u5bb6\u7684\u95e8\u53c8\u5f00\u4e86\u3002 \u3000\u3000\u7a91\u59d0\u4eec\u8d77\u5148\u8fd8\u63a2\u5934\u5411\u5df7\u9053\u4e24\u8fb9\u77a7\u77a7\uff0c\u76f4\u5230\u786e\u5b9a\u90a3\u821e\u5200\u7684\u75af\u5927\u6c49\u5df2\u7ecf\u4e0d\u5728\uff0c\u624d\u53c8\u90fd\u62e5\u4e86\u51fa \u6765\u3002 \u3000\u3000\u7070\u8863\u8001\u4eba\u671b\u4e86\u7518\u9716\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7518\u9716\u5e72\u54b3\u4e86\u4e24\u58f0\uff0c\u7acb\u523b\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u4ed6\u6b64\u523b\u8d70\u8def\u7684\u59ff\u6001\u548c\u5f80\u5e38\u5927\u4e0d\u76f8\u540c\uff0c\u7adf\u662f\u53cc\u624b\u6342\u4f4f\u5c0f\u8179\uff0c\u5f2f\u8170\u9a7c\u80cc\uff0c\u5916\u5e26\u5f20\u7259 \u54a7\u5634\uff0c\u4e00\u526f\u75db\u82e6\u96be\u5f53\u7684\u6a21\u6837\u3002 \u3000\u3000\u90a3\u4e9b\u7a91\u59d0\u4eec\u56e0\u4e3a\u9694\u5f97\u8fdc\uff0c\u770b\u4e0d\u6e05\u4ed6\u5728\u505a\u4ec0\u4e48\uff0c\u5f85\u4ed6\u6e10\u6e10\u8d70\u8fd1\u65f6\uff0c\u7acb\u523b\u53c8\u4e00\u62e5\u800c\u4e0a\uff0c\u4e03\u624b \u516b\u811a\u7684\u62c9\u626f\u8d77\u6765\u3002 \u3000\u3000\u8c01\u77e5\u7518\u9716\u8fd9\u65f6\u5374\u662f\u58f0\u58f0\u60e8\u53eb\u3002 \u3000\u3000\u7a91\u59d0\u4eec\u6709\u7684\u770b\u51fa\u4e0d\u5bf9\uff0c\u7acb\u523b\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u7518\u9716\u62ac\u624b\u64a9\u5f00\u8863\u670d\uff0c\u9732\u51fa\u5c0f\u8179\u3002 \u3000\u3000\u4f17\u7a91\u59d0\u4eec\u89e6\u76ee\u4e4b\u4e0b\uff0c\u4e0d\u7531\u9f50\u9f50\u60ca\u53eb\u8d77\u6765\uff0c\u53ea\u89c1\u7518\u9716\u7684\u5c0f\u8179\u8d6b\u7136\u9732\u51fa\u4e86\u597d\u51e0\u622a\u80a0\u5b50\u3002 \u3000\u3000\u6709\u7684\u60ca\u95ee\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u597d\u50cf\u88ab\u4eba\u6740\u4e86\u4e00\u5200\uff1f\u201d \u3000\u3000\u7518\u9716\u5168\u8eab\u731b\u6296\u9053\uff1a\u201c\u6211\u88ab\u521a\u624d\u90a3\u4e2a\u75af\u5927\u6c49\u5728\u809a\u5b50\u4e0a\u6740\u4e86\u4e00\u5200\uff01\u201d \u3000\u3000\u4f17\u7a91\u59d0\u987f\u65f6\u773c\u77aa\u820c\u5410\uff0c\u9f50\u5411\u4e24\u8fb9\u8eb2\u5f00\uff0c\u6709\u8c01\u6562\u548c\u88ab\u6740\u7684\u4eba\u63a5\u8fd1\u5462\uff1f \u3000\u3000\u7518\u9716\u5c31\u8fd9\u6837\u6367\u7740\u809a\u5b50\u76f4\u8d70\u51fa\u5df7\u9053\uff0c\u8f6c\u8fc7\u5df7\u53e3\u4fbf\u548c\u4e01\u6d9b\u4f1a\u5408\u3002 \u3000\u3000\u539f\u6765\u7518\u9716\u8170\u675f\u6761\u7ea2\u5e26\uff0c\u4ed6\u628a\u7ea2\u5e26\u4e24\u5934\u6545\u610f\u6253\u4e86\u51e0\u4e2a\u7ed3\uff0c\u9732\u5728\u5c0f\u8179\u4e0a\uff0c\u591c\u665a\u5149\u7ebf\u751a\u6697\uff0c\u731b \u4e00\u6380\u5f00\u8863\u670d\uff0c\u8c01\u4e5f\u76f8\u4fe1\u90a3\u662f\u80a0\u5b50\u6d41\u4e86\u51fa\u6765\uff0c\u8fd9\u529e\u6cd5\u81f3\u5c11\u6bd4\u4e01\u6d9b\u9ad8\u660e\u591a\u4e86\u3002 \u3000\u3000\u773c\u89c1\u7518\u9716\u8fc7\u53bb\u4e4b\u540e\uff0c\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0c\u770b\u4f60\u7684\u4e86\uff01\u201d \u3000\u3000\u7a81\u89c1\u8881\u5c0f\u9e64\u75af\u72c2\u822c\u7684\u76f4\u5411\u524d\u5954\u53bb\u3002 \u3000\u3000\u4ed6\u8fd9\u52a8\u4f5c\uff0c\u4e0d\u4f46\u7a91\u59d0\u4eec\u5403\u60ca\uff0c\u8fde\u5cb3\u5c0f\u98de\u548c\u7070\u8863\u8001\u4eba\u4e5f\u900f\u7740\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u4f17\u7a91\u59d0\u4eec\u89c1\u4e00\u4eba\u72c2\u5954\u800c\u6765\uff0c\u81ea\u7136\u4e0d\u4fbf\u4e0a\u524d\u62c9\u626f\uff0c\u53cd\u800c\u4e0d\u5f97\u4e0d\u95ea\u8eab\u8ba9\u8def\u3002 \u3000\u3000\u6709\u7684\u60c5\u4e0d\u767d\u7981\u60ca\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u4ed6\u75af\u4e86\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8fb9\u8dd1\u8fb9\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u5feb\u8eb2\uff0c\u90a3\u62ff\u5200\u7684\u75af\u6c49\uff0c\u7ed5\u5230\u540e\u9762\u5df7\u53e3\u53c8\u8ffd\u8fc7\u6765\u4e86\u3002\u201d \u3000\u3000\u987f\u65f6\uff0c\u4f17\u7a91\u59d0\u4eec\u53c8\u5df2\u5927\u4e71\uff0c\u518d\u5954\u56de\u95e8\u5185\u5173\u4e0a\u4e86\u95e8\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u901a\u8fc7\u5df7\u9053\uff0c\u79f0\u5f97\u4e0a\u662f\u6700\u5feb\u7684\u4e00\u4e2a\u3002 \u3000\u3000\u8fd9\u6b21\u7070\u8863\u8001\u4eba\u4e3b\u52a8\u53eb\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u522b\u6025\uff0c\u5979\u4eec\u5f88\u5feb\u5c31\u4f1a\u518d\u51fa\u6765\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8010\u5fc3\u7684\u7b49\u7740\uff0c\u76f4\u5230\u5df7\u9053\u5185\u53c8\u6062\u590d\u4e86\u70ed\u95f9\uff0c\u624d\u4e5f\u6367\u7740\u809a\u5b50\u5411\u524d\u8d70\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u7a91\u59d0\u513f\u4eec\u5df2\u770b\u5230\u6765\u4eba\u662f\u4e2a\u7709\u6e05\u76ee\u79c0\u534a\u5927\u4e0d\u5c0f\u7684\u5b69\u5b50\u3002 \u3000\u3000\u9a6c\u4e0a\u6709\u7a91\u59d0\u513f\u53eb\u9053\uff1a\u201c\u54df\uff0c\u600e\u4e48\u8fd8\u6709\u8fd9\u4e48\u5c0f\u7684\u5230\u54b1\u4eec\u8fd9\u91cc\u6765\uff01\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u53eb\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u4e2a\u7ae5\u5b50\u9e21\uff0c\u6211\u8981\uff01\u201d \u3000\u3000\u5979\u4eec\u521a\u8981\u6324\u4e0a\u524d\u6765\u62c9\u626f\uff0c\u5df2\u542c\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u8eab\u4e0a\u5df2\u7ecf\u4e2d\u4e86\u5267\u6bd2\uff0c\u8c01\u78b0\u4e0a\u8c01\u5c31\u6ca1\u547d\uff01\u201d \u3000\u3000\u90a3\u4e9b\u7a91\u59d0\u513f\u4eec\u4e0d\u89c9\u4e00\u5446\u3002 \u3000\u3000\u5979\u4eec\u867d\u672a\u5c3d\u4fe1\uff0c\u5374\u53c8\u4e0d\u6562\u4e0d\u4fe1\u3002 \u3000\u3000\u5f53\u5979\u4eec\u60f3\u5230\u8fd9\u5c0f\u5b69\u5b50\u53ef\u80fd\u662f\u9a97\u4eba\u65f6\uff0c\u5cb3\u5c0f\u98de\u65e9\u5df2\u8f6c\u8fc7\u4e86\u5df7\u53e3\u3002 \u3000\u3000\u7070\u8863\u8001\u4eba\u968f\u540e\u5927\u6447\u5927\u6446\u7684\u8d70\u4e86\u8fc7\u6765\uff0c\u7a91\u59d0\u513f\u4eec\u6709\u7684\u8ba4\u8bc6\u4ed6\u662f \u3000\u3000\u5bb5\u5173\u4e0a\u7684\u8981\u4eba\uff0c\u4e0d\u8ba4\u8bc6\u7684\u89c1\u4ed6\u662f\u4e2a\u8001\u5934\u5b50\uff0c\u8c01\u4e5f\u6ca1\u5174\u8da3\u4e0a\u524d\u62c9\u626f\u3002 \u3000\u3000\u7070\u8863\u8001\u4eba\u548c\u4e01\u6d9b\u7b49\u56db\u4eba\u4f1a\u5408\u540e\u9053\uff1a\u201c\u56db\u7ad9\u5df2\u7ecf\u6d4b\u8bd5\u4e86\u4e09\u7ad9\uff0c\u53ea\u5269\u4e0b\u6700\u540e\u4e00\u7ad9\u4e86\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6211\u53c8\u5fd8\u8bb0\uff0c\u6700\u540e\u4e00\u7ad9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u767d\u5403\u5305\u5b50\u3002\u201d \u3000\u3000\u4e01\u6d9b\u51e0\u4e4e\u8981\u6d41\u51fa\u998b\u6d8e\u6765\uff0c\u6478\u6478\u5634\u5df4\u9053\uff1a\u201c\u767d\u5403\u5305\u5b50\u662f\u600e\u6837\u767d\u5403\u6cd5\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u7ecf\u8fc7\u4e00\u5bb6\u996d\u9986\u95e8\u524d\uff0c\u5305\u5b50\u5c31\u653e\u5728\u95e8\u53e3\u7684\u53f0\u5b50\u4e0a\uff0c\u4f60\u4eec\u56db\u4f4d\u53ea\u8981\u4e0d \u82b1\u94b1\u80fd\u5403\u5230\u5305\u5b50\uff0c\u800c\u53c8\u4e0d\u9700\u8981\u548c\u996d\u9986\u7684\u4f19\u8ba1\u4eec\u4e89\u5435\uff0c\u90a3\u5c31\u8fc7\u4e86\u5173\u3002\u201d \u3000\u3000\u4e01\u6d9b\u53c8\u95ee\uff1a\u201c\u8981\u5403\u51e0\u4e2a\u624d\u80fd\u53ca\u683c\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u4e2a\u5bb9\u6613\u3002\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u968f\u5373\u5e26\u7740\u56db\u4eba\u518d\u5f80\u524d\u8d70\u3002 \u3000\u3000\u8f6c\u8fc7\u51e0\u6761\u8857\u5df7\uff0c\u5df7\u53e3\u8fb9\u679c\u7136\u51fa\u73b0\u4e00\u95f4\u996d\u9986\uff0e\u4e00\u5927\u7b50\u70ed\u817e\u817e\u7684\u5305\u5b50\uff0c\u6b63\u653e\u5728\u95e8\u524d\u7684\u6728\u53f0\u4e0a\u3002 \u3000\u3000\u7070\u8863\u8001\u4eba\u8001\u8fdc\u5c31\u548c\u4e01\u6d9b\u7b49\u53eb\u4eba\u505c\u4e0b\u811a\u6b65\uff0c\u63a5\u7740\u5429\u5490\u4e01\u6d9b\u9053\uff1a \u3000\u3000\u201c\u8fd8\u662f\u7531\u4f60\u5148\u53bb\uff01\u201d \u3000\u3000\u4e01\u6d9b\u5b9e\u5728\u60f3\u4e0d\u51fa\u4ec0\u4e48\u597d\u529e\u6cd5\uff0c\u51b3\u5b9a\u8fd8\u662f\u52a8\u786c\u7684\u3002 \u3000\u3000\u4ed6\u659c\u4f69\u7740\u539a\u80cc\u9b3c\u5934\u5200\u5927\u6a21\u5927\u6837\u7684\u6765\u5230\u996d\u9986\u95e8\u524d\uff0c\u4f38\u624b\u6293\u4e86\u4e24\u4e2a\u5305\u5b50\u4fbf\u8d70\u3002 \u3000\u3000\u7acb\u523b\uff0c\u4e00\u540d\u4f19\u8ba1\u6014\u4e86\u6014\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u600e\u4e48\u8fd9\u6837\u4e0d\u8bb2\u7406\uff0c\u6211\u4eec\u662f\u505a\u751f\u610f\u7684\uff0c\u5403\u5305\u5b50\u600e\u80fd\u767d \u5403\uff1f\u201d \u3000\u3000\u201c\u8001\u5b50\u4eca\u5929\u8eab\u4e0a\u6ca1\u5e26\u94b1\uff0c\u5148\u8d4a\u7740\uff0c\u6539\u65e5\u518d\u7ed9\u94b1\uff01\u201d \u3000\u3000\u201c\u4e0d\u884c\uff0c\u6ca1\u94b1\u5c31\u653e\u4e0b\u5305\u5b50\uff01\u201d\u90a3\u4f19\u6c41\u8fb9\u8bf4\u8fb9\u4e0a\u524d\u593a\u5305\u5b50\u3002 \u3000\u3000\u4e01\u6d9b\u84e6\u5730\u4e24\u773c\u4e00\u77aa\uff0c\u4e9a\u8d5b\u94dc\u94c3\uff0c\u63a5\u7740\u539a\u80cc\u9b3c\u5934\u5200\u51fa\u9798\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u60f3\u627e\u6b7b\uff1f\u201d \u3000\u3000\u90a3\u4f19\u8ba1\u987f\u65f6\u50bb\u4e86\u773c\uff0c\u4e0d\u89c9\u5446\u5728\u5f53\u573a\u3002 \u3000\u3000\u53ea\u542c\u5e97\u91cc\u638c\u67dc\u7684\u53eb\u9053\uff1a\u201c\u5c0f\u4e09\u5b50\uff0c\u56de\u6765\u5427\uff0c\u5c31\u7b97\u6253\u53d1\u4e86\u8981\u996d\u7684\uff0c\u522b\u8ddf\u4ed6\u8ba1\u8f83\uff01\u201d \u3000\u3000\u90a3\u4f19\u8ba1\u5f80\u5730\u4e0a\u5410\u4e86\u4e00\u53e3\uff0c\u53ea\u597d\u6124\u6124\u5730\u8d70\u56de\u996d\u9986\u3002 \u3000\u3000\u7518\u9716\u968f\u5373\u88c5\u6a21\u505a\u6837\u7684\u4e5f\u6765\u5230\u996d\u9986\u95e8\u524d\u3002 \u3000\u3000\u4ed6\u770b\u6765\u5341\u5206\u60a0\u95f2\uff0c\u4e24\u773c\u671b\u5411\u5e97\u5185\u5899\u58c1\u3002\u4f3c\u662f\u5728\u7ec6\u77a7\u5899\u58c1\u4e0a\u8d34\u7684\u5404\u79cd\u996e\u98df\u4ef7\u76ee\u8868\u3002\u7136\u540e\u8d81 \u5e97\u91cc\u4eba\u4e0d\u6ce8\u610f\uff0c\u987a\u624b\u6293\u4e86\u4e24\u4e2a\u5305\u5b50\uff0c\u6284\u5728\u8896\u5b50\u91cc\uff0c\u53c8\u5927\u6a21\u5927\u6837\u7684\u8d70\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u7740\uff0c\u5e72\u5f97\u65e0\u58f0\u65e0\u606f\uff0c\u5f88\u50cf\u662f\u4f4d\u95ef\u51fa\u5b57\u53f7\u7684\u6252\u624b\u8001\u884c\u5bb6\uff0c\u8fde\u7070\u8863\u8001\u4eba\u3001\u5cb3\u5c0f\u98de\u3001\u8881 \u5c0f\u9e64\u4e09\u4eba\u90fd\u5fcd\u4e0d\u4f4f\u60f3\u7b11\u3002 \u3000\u3000\u73b0\u5728\u8f6e\u5230\u8881\u5c0f\u9e64\uff0c\u4ed6\u5c45\u7136\u76f4\u63a5\u8d70\u8fdb\u4e86\u996d\u9986\uff0c\u627e\u4e86\u4e00\u526f\u684c\u5934\u5750\u4e0b\uff0c\u5e76\u4ea4\u4ee3\u6765\u4e00\u7897\u7f8a\u8089\u9762\u3002 \u3000\u3000\u8c01\u77e5\u4ed6\u521a\u4ea4\u4ee3\u8fc7\u4e0d\u4e45\uff0c\u5374\u53c8\u518d\u5429\u5490\u9053\uff1a\u201c\u4f19\u8ba1\uff0c\u6211\u6709\u8981\u7d27\u7684\u4e8b\u8981\u529e\u3002\u5403\u9762\u6765\u4e0d\u53ca\uff0c\u6362\u2014 \u76d8\u5305\u5b50\u597d\u4e86\uff01\u201d \u3000\u3000\u4f19\u8ba1\u5f88\u5feb\u4fbf\u7aef\u4e86\u4e00\u76d8\u5305\u5b50\u4e0a\u6765\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u5306\u5306\u5403\u5b8c\uff0c\u62b9\u7740\u5634\u5c31\u51fa\u4e86\u5e97\u95e8\u3002 \u3000\u3000\u90a3\u4f19\u8ba1\u8ffd\u4e0a\u6765\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u4f60\u8fd8\u6ca1\u4ed8\u5e10\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e00\u6123\u9053\uff1a\u201c\u4f60\u8981\u6211\u4ed8\u4ec0\u4e48\u94b1\uff1f\u201d \u3000\u3000\u90a3\u4f19\u8ba1\u4e5f\u4e00\u6123\u9053\uff1a\u201c\u4f60\u5403\u4e86\u5305\u5b50\uff0c\u5f53\u7136\u8981\u4ed8\u5305\u5b50\u94b1\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8f6c\u7740\u773c\u73e0\u9053\uff1a\u201c\u6211\u5403\u4e86\u5305\u5b50\u4e0d\u9519\uff0c\u90a3\u53ef\u662f\u7528\u7f8a\u8089\u9762\u6362\u7684\u3002\u201d \u3000\u3000\u90a3\u4f19\u8ba1\u4e00\u5446\uff0c\u54a7\u7740\u5634\u9053\uff1a\u201c\u90a3\u5c31\u8be5\u4ed8\u7f8a\u8089\u9762\u7684\u5e10\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e24\u773c\u4e00\u77aa\u9053\uff1a\u201c\u5c82\u6709\u4e9b\u7406\uff0c\u6211\u6839\u672c\u6ca1\u5403\u7f8a\u8089\u9762\uff0c\u4e3a\u4ec0\u4e48\u8981\u4ed8\u5e10\uff1f\u201d \u3000\u3000\u90a3\u4f19\u8ba1\u8d8a\u53d1\u6123\u4f4f\u3002 \u3000\u3000\u5f85\u4ed6\u8111\u5b50\u91cc\u8f6c\u8fc7\u5f2f\u6765\uff0c\u8881\u5c0f\u9e64\u65e9\u5df2\u8d70\u5f97\u4e0d\u77e5\u53bb\u5411\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6beb\u4e0d\u8fdf\u7591\uff0c\u968f\u540e\u4e5f\u5927\u5927\u65b9\u65b9\u8d70\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u7684\u786e\u5927\u65b9\u7684\u5f88\uff0c\u7adf\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u6293\u4e86\u4e24\u4e2a\u5305\u5b50\u4fbf\u8d70\u3002 \u3000\u3000\u4f19\u8ba1\u7acb\u523b\u8ddf\u4e86\u8fc7\u6765\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u600e\u4e48\u4e0d\u58f0\u4e0d\u54cd\u7684\u62ff\u4e86\u5305\u5b50\u5c31\u8d70\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u56de\u8f6c\u8eab\uff0c\u671b\u7740\u968f\u540e\u800c\u6765\u7684\u7070\u8863\u8001\u4eba\u6307\u4e86\u4e00\u6307\u9053\uff1a\u201c\u662f\u8fd9\u4f4d\u8001\u5148\u751f\u8981\u6211\u62ff\u7684\uff0c\u4ed6\u4f1a \u66ff\u6211\u4ed8\u94b1\u3002\u201d \u3000\u3000\u90a3\u4f19\u8ba1\u4e00\u89c1\u540e\u9762\u6765\u7684\u662f\u7070\u8863\u8001\u4eba\uff0c\u9a6c\u4e0a\u5806\u4e0b\u7b11\u8138\uff0c\u54c8\u7740\u8170\u9053\uff1a \u3000\u3000\u201c\u539f\u6765\u662f\u5f20\u526f\u7edf\u9886\uff0c\u60a8\u8001\u5feb\u8bf7\u91cc\u9762\u5750\uff01\u201d \u3000\u3000\u7ecf\u4f19\u8ba1\u8fd9\u4e48\u4e00\u56b7\uff0c\u638c\u67dc\u7684\u8fde\u5fd9\u8fc7\u6765\u966a\u7b11\u6253\u62db\u547c\u3002 \u3000\u3000\u7070\u8863\u8001\u4eba\u6325\u624b\u8fde\u6446\u9053\uff1a\u201c\u8001\u673d\u4eca\u665a\u6709\u4e8b\u5fd9\uff0c\u4e0b\u6b21\u6709\u7a7a\u4e00\u5b9a\u6765\uff01\u201d \u3000\u3000\u90a3\u638c\u67dc\u7684\u8fde\u5fd9\u53c8\u6293\u4e86\u51e0\u4e2a\u5305\u5b50\uff0c\u8ffd\u4e0a\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u521a\u624d\u4e0d\u77e5\u9053\u4f60\u662f\u5f20\u526f\u7edf\u9886\u7684 \u4eb2\u621a\uff0c\u591a\u6709\u5f97\u7f6a\uff0c\u6765\uff0c\u8981\u5403\u5c31\u591a\u62ff\u51e0\u4e2a\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5e76\u672a\u63a5\uff0c\u4e5f\u6447\u7740\u624b\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u5305\u5b50\u5f88\u5927\uff0c\u4e24\u4e2a\u5c31\u591f\u4e86\uff0c\u638c\u67dc\u7684\u522b\u5ba2\u6c14\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u5bb5\u5173\u56e0\u4e3a\u7f16\u7ec4\u5927\uff0c\u7279\u522b\u52a0\u8bbe\u4e00\u4f4d\u526f\u7edf\u9886\uff0c\u7070\u8863\u8001\u4eba\u6b63\u662f\u5bb5\u5173\u7684\u7b2c\u4e8c\u53f7\u4eba\u7269\u3002\u4ed6\u56e0 \u65e0\u5bb6\u65e0\u7737\uff0c\u53c8\u559c\u6b22\u5403\u559d\uff0c\u662f\u8fd9\u5bb6\u996d\u9986\u7684\u8001\u987e\u5ba2\uff0c\u518d\u52a0\u4e0a\u8eab\u4efd\u5730\u4f4d\u7279\u6b8a\uff0c\u638c\u67dc\u548c\u4f19\u8ba1\u90a3\u80fd\u4e0d\u5bf9 \u4ed6\u53e6\u773c\u770b\u5f85\u3002 \u3000\u3000\u7070\u8863\u8001\u4eba\u5e26\u7740\u5cb3\u5c0f\u98de\u548c\u53e6\u5916\u4e09\u4eba\u4f1a\u5408\u4e4b\u540e\uff0c\u56e0\u4e3a\u56db\u7ad9\u5df2\u6d4b\u8bd5\u5b8c\u6bd5\uff0c\u5927\u5bb6\u7684\u5fc3\u60c5\u4e5f\u90fd\u8f7b\u677e \u4e86\u3002 \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8001\u5148\u751f\uff0c\u73b0\u5728\u65f6\u5019\u4e0d\u65e9\u4e86\uff0c\u6211\u4eec\u4eca\u665a\u4f4f\u5728\u54ea\u91cc\uff1f\u8fd8\u6709\uff0c\u6ca1\u5403\u665a\u996d\uff0e\u809a\u5b50\u91cc\u5b9e \u5728\u997f\u5f97\u614c\uff01\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u7b11\u9053\uff1a\u201c\u96be\u9053\u4f60\u4eec\u5404\u4f4d\u5403\u5305\u5b50\u8fd8\u6ca1\u5403\u9971\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4e24\u4e2a\u5305\u5b50\uff0c\u53ea\u591f\u585e\u7259\u7f1d\u513f\u7684\uff01\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u5728\u524d\u9762\u5e26\u8def\u9053\uff1a\u201c\u968f\u8001\u673d\u8d70\u5427\uff0c\u7ba1\u6559\u4f60\u4eec\u56db\u4f4d\u4eca\u665a\u6709\u5403\u6709\u4f4f\uff0c\u8212\u8212\u670d\u670d\u3002\u201d \u3000\u3000\u4eba\u7ea6\u8d70\u4e86\u534a\u91cc\u591a\u8def\uff0c\u5df2\u6765\u5230\u8857\u9547\u4e4b\u5916\uff0c\u9762\u524d\u51fa\u73b0\u4e86\u4e00\u5ea7\u5360\u5730\u6781\u5e7f\u7684\u5dcd\u5ce8\u5b85\u9662\uff0c\u56db\u9762\u56f4\u7740 \u4e08\u4f59\u9ad8\u7684\u767d\u77f3\u56f4\u5899\uff0c\u5927\u95e8\u5916\u6709\u4e24\u540d\u4f69 \u3000\u3000\u5200\u5927\u6c49\u5728\u5b88\u536b\u7740\u3002 \u3000\u3000\u4e24\u540d\u4f69\u5200\u5927\u6c49\u89c1\u4e86\u7070\u8863\u8001\u4eba\u90fd\u8eac\u8eab\u65bd\u793c\u3002 \u3000\u3000\u7070\u8863\u8001\u4eba\u5e26\u7740\u56db\u4eba\u76f4\u63a5\u8fdb\u5165\u5927\u95e8\u3002 \u3000\u3000\u4f46\u89c1\u91cc\u9762\u4ead\u53f0\u697c\u69ad\uff0c\u82b1\u6728\u6276\u758f\uff0e\u7f8e\u4e0d\u80dc\u6536\u3002 \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u8fd9\u91cc\u5c31\u662f\u7edf\u9886\u548c\u8001\u673d\u7684\u4f4f\u5904\u4e86\uff0c\u56db\u4f4d\u8bf7\u7a0d\u5f85\u4e00\u4e0b\uff1f\u6211\u53bb\u7980\u62a5\u7edf\u9886\u3002\u201d \u3000\u3000\u4ed6\u6765\u5230\u4e00\u5904\u5efa\u7b51\u5f97\u5341\u5206\u7cbe\u81f4\u7684\u697c\u9601\u7a97\u5916\uff0c\u91cc\u9762\u7684\u706f\u5149\u5c1a\u672a\u7184\u706d\uff0c\u7070\u8863\u8001\u4eba\u8f7b\u54b3\u4e00\u58f0\uff0c\u53eb \u9053\uff1a\u201c\u7980\u7edf\u9886\uff0c\u4ed6\u4eec\u56db\u4f4d\u5df2\u7ecf\u901a\u8fc7\u6d4b\u8bd5\uff0c \u3000\u3000\u73b0\u5728\u90fd\u5df2\u7ecf\u5230\u4e86\uff01\u201d \u3000\u3000\u53ea\u542c\u91cc\u9762\u4f20\u51fa\u5a07\u6ef4\u6ef4\u602f\u751f\u751f\u6709\u5982\u73e0\u6eda\u7389\u76d8\u7684\u58f0\u97f3\u9053\uff1a\u201c\u6211\u4eca\u665a\u4e0d\u592a\u8212\u670d\uff0c\u4e0d\u80fd\u63a5\u89c1\u4ed6\u4eec\uff0c \u4e00\u5207\u5168\u8bf7\u526f\u7edf\u9886\u66ff\u6211\u4ee3\u52b3\u5427\uff01\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u518d\u9053\uff1a\u201c\u90a3\u4e48\u660e\u5929\u65e9\u4e0a\u7edf\u9886\u662f\u5426\u8fd8\u8981\u89c1\u89c1\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u90a3\u5a07\u6ef4\u6ef4\u7684\u58f0\u97f3\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u4ecd\u65e7\u7531\u526f\u7edf\u9886\u4ee3\u52b3\u3002\u201d \u3000\u3000\u5f53\u7070\u8863\u8001\u4eba\u8f6c\u8fc7\u8eab\u6765\uff0c\u624d\u53d1\u73b0\u5cb3\u5c0f\u98de\u5c31\u5728\u8eab\u540e\u4e0d\u8fdc\u3002 \u3000\u3000\u539f\u6765\u5cb3\u5c0f\u98de\u4e00\u65f6\u597d\u5947\uff0c\u4e0d\u514d\u8ddf\u5728\u7070\u8863\u8001\u4eba\u540e\u9762\u3002 \u3000\u3000\u800c\u6b64\u523b\u7684\u5cb3\u5c0f\u98de\uff0c\u5374\u6b63\u5728\u6014\u5fe1\u4e0d\u5b9a\uff0c\u56e0\u4e3a\u4ed6\u5df2\u542c\u51fa\u8fd9\u4f4d\u5973\u7edf\u9886\u7684\u58f0\u97f3\u9887\u4e3a\u8033\u719f\uff0e\u5374\u4e00\u65f6 \u4e4b\u95f4\u60f3\u4e0d\u8d77\u6765\u6b64\u4eba\u662f\u8c01\u3002 \u3000\u3000\u7070\u8863\u8001\u4eba\u5e76\u672a\u8d23\u602a\u5cb3\u5c0f\u98de\uff0c\u56de\u5230\u539f\u5904\uff0c\u628a\u56db\u4eba\u9886\u5230\u4e00\u5904\u5927\u5385\u3002 \u3000\u3000\u5927\u5385\u5185\u706f\u706b\u8f89\u714c\uff0c\u6709\u56db\u4f4d\u660e\u7738\u7693\u9f7f\uff0c\u7b11\u9762\u8fce\u4eba\u7684\u9ec4\u8863\u5c0f\u5a62\u7ad9\u5728\u90a3\u91cc\u3002 \u3000\u3000\u7070\u8863\u8001\u4eba\u62db\u547c\u56db\u4eba\u5750\u4e0b\u3002 \u3000\u3000\u9ec4\u8863\u5c0f\u5a62\u62a2\u7740\u732e\u4e0a\u8336\u3002 \u3000\u3000\u7070\u8863\u8001\u4eba\u95ee\u9053\uff1a\u201c\u9152\u7b75\u51c6\u5907\u597d\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u4e00\u540d\u5c0f\u5a62\u9053\uff1a\u201c\u65e9\u5c31\u51c6\u5907\u597d\u4e86\u3002\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u9053\uff1a\u201c\u9a6c\u4e0a\u4e0a\u8336\u5f00\u7b75\uff01\u201d \u3000\u3000\u56db\u540d\u9ec4\u8863\u5c0f\u5a62\u5e94\u58f0\u800c\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u8881\u5c0f\u9e64\u3001\u4e01\u6d9b\u3001\u7518\u9716\u4e09\u4eba\u4e5f\u5df2\u77e5\u9053\u7070\u8863\u8001\u4eba\u662f\u5bb5\u5173\u526f\u7edf\u9886\uff0c\u4e0d\u7531\u8bf4\u8fd9\u662f\u5cb3\u5c0f\u98de\u544a\u8bc9 \u4ed6\u4eec\u7684\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u642d\u8baa\u7740\u95ee\u9053\uff1b\u201c\u8001\u5148\u751f\u771f\u80fd\u4fdd\u5bc6\uff0c\u665a\u8f88\u4eec\u5230\u73b0\u5728\u624d\u77e5\u9053\u60a8\u662f\u8fd9\u91cc\u7684\u526f\u7edf\u9886\uff0c\u4f46\u4e0d \u77e5\u5c0a\u59d3\u5927\u540d\uff0c\u53ef\u5426\u8d50\u544a\uff1f\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u7565\u4e00\u72b9\u8c6b\u9053\uff1a\u201c\u8881\u5c0f\u4fa0\u95ee\u8fd9\u4e9b\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u665a\u8f88\u4eec\u867d\u7136\u8fc7\u4e86\u5173\uff0c\u4eca\u540e\u548c\u8001\u5148\u751f\u89c1\u9762\u7684\u673a\u4f1a\u4e00\u5b9a\u8fd8\u5f88\u591a\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u8981\u62dc \u8bbf\u60a8\uff0c\u600e\u53ef\u4e0d\u77e5\u5c0a\u59d3\u5927\u540d\u3002\u201d \u3000\u3000\u7070\u8863\u8001\u4eba\u987f\u4e86\u987f\u9053\uff1a\u201c\u544a\u8bc9\u5404\u4f4d\u4e5f\u65e0\u59a8\uff0c\u8001\u673d\u59d3\u5f20\uff0c\u8349\u5b57\u51e4\u9e23\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7acb\u523b\u6539\u53e3\u76f8\u79f0\u9053\uff1a\u201c\u5f20\u526f\u7edf\u9886\uff0c\u665a\u8f88\u65b9\u624d\u542c\u60a8\u548c\u7edf\u9886\u8bb2\u8bdd\uff0c\u597d\u50cf\u7edf\u9886\u662f\u4f4d\u5973\u7684\uff0c \u800c\u4e14\u5e74\u7eaa\u5f88\u8f7b\u3002\u201d \u3000\u3000\u4ed6\u56e0\u89c9\u5f97\u90a3\u5973\u7edf\u9886\u7684\u58f0\u97f3\u5341\u5206\u8033\u719f\uff0c\u81ea\u7136\u5e0c\u671b\u77e5\u9053\u7a76\u7adf\u662f\u8c01\u3002 \u3000\u3000\u5f20\u51e4\u9e23\u6478\u7740\u4e0b\u5df4\u7684\u5c71\u7f8a\u80e1\u5b50\u9053\uff1a\u201c\u7edf\u9886\u7684\u786e\u662f\u4f4d\u5764\u9053\uff0c\u5e74\u7eaa\u4e5f\u5f88\u8f7b\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5f20\u526f\u7edf\u9886\u5e74\u9ad8\u5fb7\u662d\uff0c\u53d7\u4e00\u4f4d\u5e74\u8f7b\u5973\u4eba\u7684\u8282\u5236\uff0c\u5b9e\u5728\u59d4\u5c48\u4e86\u4e9b\uff01\u201d \u3000\u3000\u5f20\u51e4\u9e23\u6b63\u8272\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u600e\u4e48\u8fd9\u6837\u8bf4\u8bdd\uff0c\u4e0d\u8bba\u7537\u5973\uff0c\u6709\u624d\u4fbf\u53ef\u8eab\u5c45\u4e0a\u4f4d\uff0c\u672c\u5173\u8fd9\u4f4d\u5973\u7edf \u9886\uff0c\u827a\u9ad8\u624d\u5927\uff0c\u8001\u673d\u5bf9\u5979\uff0c\u53ef\u8bf4\u5fc3\u670d\u53e3\u670d\uff0c\u600e\u4f1a\u8c08\u5230\u59d4\u5c48\u3002\u201d \u3000\u3000\u9ed8\u4e86\u9ed8\uff0c\u53c8\u9053\uff1a\u201c\u4ee5\u5730\u7684\u6587\u624d\u6b66\u7565\uff0c\u5f53\u4e00\u540d\u5bb5\u5173\u7edf\u9886\uff0c\u8bf4\u8d77\u6765\u8fd8\u592a\u5927\u624d\u5c0f\u7528\uff0c\u4e0d\u8fc7\u636e\u8001 \u673d\u6240\u77e5\uff0c\u7edf\u9886\u53ef\u80fd\u6700\u8fd1\u5c31\u4f1a\u9ad8\u5347\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8d81\u673a\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u5973\u7edf\u9886\u4e0a\u59d3\u82b3\u540d\uff1f\u201d \u3000\u3000\u5f20\u51e4\u9e23\u8138\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u63a5\u7740\u6447\u6447\u5934\u9053\uff1a\u201c\u5c31\u662f\u8bf4\u51fa\u6765\uff0c\u5c0f\u5144\u5f1f\u4e5f\u4e0d\u4f1a\u77e5\u9053\uff0c\u800c\u4e14\u8001\u673d\u4e5f \u4e0d\u65b9\u4fbf\u628a\u7edf\u9886\u7684\u540d\u5b57\u968f\u4fbf\u793a\u4eba\u3002\u201d \u3000\u3000\u53ea\u542c\u4e01\u6d9b\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u4e0d\u5fc5\u95ee\u4e86\uff0c\u5973\u7edf\u9886\u54b1\u4eec\u4e0d\u662f\u6ca1\u89c1\u8fc7\uff0c\u6c34\u5173\u548c\u706b\u5173\uff0c\u8fd8\u4e0d\u5168\u662f\u5973\u7edf \u9886\uff0c\u5c24\u5176\u90a3\u4f4d\u6c34\u5173\u7edf\u9886\uff0c\u5e74\u7eaa\u597d\u50cf\u8fd8\u4e0d\u5230\u4e8c\u5341\u5c81\u3002\u201d \u3000\u3000\u8fd9\u65f6\uff0c\u9ec4\u8863\u5c0f\u5a62\u4eec\u65e9\u5df2\u628a\u6d12\u996d\u7aef\u4e86\u8fdb\u6765\uff0c\u6446\u4e86\u6ee1\u6ee1\u7684\u4e00\u684c\uff0c\u5404\u79cd\u83dc\u80b4\uff0c\u867d\u7136\u6bd4\u4e0d\u4e0a\u5728\u6587 \u5173\u5403\u7684\u90a3\u4e00\u987f\uff0e\u4f46\u4e5f\u591f\u5c71\u73cd\u7f8e\u5473\u4e86\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u771f\u540e\u6094\u4e0d\u8be5\u5728\u996d\u9986\u5403\u4e86\u4e00\u76d8\u5305\u5b50\uff0c\u73b0\u5728\u60f3\u5403\u4e5f\u65e0\u6cd5\u591a\u5403\u3002 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u98df\u91cf\u5927\uff0c\u4ed6\u4eec\u867d\u5404\u5403\u4e86\u4e24\u4e2a\u5305\u5b50\uff0c\u5e76\u4e0d\u5f71\u54cd\u98df\u6b32\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5219\u662f\u4e24\u4e2a\u5305\u5b50\u5230\u73b0\u5728\u8fd8\u4e0d\u66fe\u5403\u3002 \u3000\u3000\u5f20\u51e4\u9e23\u4eb2\u81ea\u4e3a\u56db\u4eba\u659f\u6ee1\u4e86\u9152\uff0c\u4e3e\u8d77\u676f\u9053\uff1a\u201c\u6765\uff0c\u8001\u673d\u5148\u656c\u5404\u4f4d\uff0c\u5404\u4f4d\u987a\u5229\u8fde\u8fc7\u4e94\u5173\uff0c\u5c06 \u5f52\u9644\u672c\u6559\uff0c\u524d\u9014\u4e0d\u53ef\u9650\u91cf\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u56de\u656c\u5f20\u51e4\u9e23\u9053\uff1a\u201c\u627f\u8499\u5f20\u526f\u7edf\u9886\u76db\u5bb4\u6b3e\u5f85\uff0c\u665a\u8f88\u5b9e\u4e0d\u6562\u5f53\uff01\u201d \u3000\u3000\u5f20\u51e4\u9e23\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u672c\u6559\u7684\u89c4\u5b9a\uff0c\u51e1\u662f\u8fdb\u5165\u8c37\u9053\u8fde\u8fc7\u4e94\u5173\u7684\uff0c \u3000\u3000\u5fc5\u987b\u7531\u5bb5\u5173\u8d1f\u8d23\u8bbe\u7b75\u6b3e\u5f85\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u8001\u673d\u966a\u5ba2\u81f3\u5c11\u966a\u4e86\u767e\u6b21\u4ee5\u4e0a\uff0c\u4ec0\u4e48\u597d\u4e1c\u897f\u90fd\u5403\u8fc7\uff0c \u8bf4\u8d77\u6765\u8be5\u662f\u6211\u6cbe\u4e86\u5404\u4f4d\u7684\u5149\uff0c\u82e5\u6ca1\u6709\u5404\u4f4d\u670b\u53cb\u8fdb\u8c37\uff0c\u8001\u673d\u4e5f\u4e0d\u53ef\u80fd\u7ecf\u5e38\u5927\u5403\u5927\u559d\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u7edf\u9886\u662f\u5426\u4e5f\u5e38\u6765\u966a\u5ba2\uff1f\u201d \u3000\u3000\u5f20\u51e4\u9e23\u9053\uff1a\u201c\u7edf\u9886\u4e0d\u5e38\u6765\uff0c\u591a\u534a\u662f\u8001\u673d\u4ee3\u5979\u63a5\u5ba2\uff0c\u81f3\u4e8e\u4eca\u665a\uff0c\u5979\u662f\u8eab\u4f53\u4e0d\u8212\u670d\uff0c\u60f3\u6765\u4e5f \u4e0d\u80fd\u6765\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u84e6\u5730\u5fc3\u5934\u4e00\u9707\uff0c\u7ec8\u4e8e\u60f3\u8d77\u521a\u624d\u90a3\u5973\u7edf\u9886\u7684\u58f0\u97f3\uff0c\u8d6b\u7136\u662f\u5f03\u592b\u548c\u5f1f\u5b50\u6deb\u5954\u7684\u51e4\u5ae3\u7ea2\u3002 \u96be\u602a\u5979\u6258\u75c5\u4e0d\u6765\u3002\u4e0d\u6d88\u8bf4\u5979\u5df2\u77e5\u9053 \u3000\u3000\u4eca\u665a\u8981\u62db\u5f85\u7684\u56db\u4eba\u662f\u8c01\u3002 \u3000\u3000\u4ed6\u662f\u591a\u4e48\u60f3\u7acb\u523b\u544a\u77e5\u8881\u5c0f\u9e64\uff0c\u4f46\u5f53\u7740\u5f20\u51e4\u9e23\u548c\u4e01\u6d9b\u3001\u7518\u9716\u7684\u9762\uff0c\u5374\u53c8\u5192\u5931\u4e0d\u5f97\uff0c\u800c\u4e14\u8fd8 \u5fc5\u987b\u88c5\u51fa\u82e5\u65e0\u5176\u4e8b\u7684\u6a21\u6837\u3002 \u3000\u3000\u9152\u8fc7\u4e09\u5de1\uff0c\u83dc\u8fc7\u4e94\u5473\uff0c\u8881\u5c0f\u9e64\u95ee\u9053\uff1a\u201c\u665a\u8f88\u7b49\u4eba\u8fc7\u4e86\u4e94\u5173\u4e4b\u540e\uff0c\u524d\u9762\u662f\u5426\u8fd8\u8981\u8fc7\u5176\u4ed6\u7684 \u5173\uff1f\u201d \u3000\u3000\u5f20\u51e4\u9e23\u9053\uff1a\u201c\u6682\u65f6\u4e0d\u5fc5\uff0c\u5404\u4f4d\u80fd\u8fde\u95ef\u4e94\u5173\uff0c\u5df2\u7ecf\u662f\u5927\u5927\u4e0d\u6613\uff0c\u653e\u773c\u5f53\u4eca\u6b66\u6797\uff0c\u5404\u4f4d\u5df2\u8db3 \u4ee5\u626c\u7709\u5410\u6c14\u4e86\uff01\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u5e76\u4e0d\u89c9\u5f97\u600e\u6837\uff0c\u4f46\u4e01\u6d9b\u548c\u7518\u9716\u5374\u5df2\u81ea\u89c9\u8eab\u4ef7\u500d\u589e\uff0c\u5927\u6709\u4e0d\u53ef \u4e00\u4e16\u4e4b\u6982\uff0c\u4ed6\u4eec\u6839\u672c\u6ca1\u60f3\u60f3\u8fd9\u4e94\u5173\u81ea \u3000\u3000\u5df1\u662f\u600e\u6837\u95ef\u8fc7\u7684\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u518d\u6b21\u95ee\u9053\uff1a\u201c\u665a\u8f88\u4eec\u4eca\u540e\u8fd8\u8981\u505a\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5f20\u51e4\u9e23\u9053\uff1a\u201c\u5404\u4f4d\u4eca\u665a\u5c31\u4f4f\u5728\u8fd9\u91cc\uff0c\u660e\u5929\u65e9\u996d\u540e\uff0c\u7531\u8001\u673d\u518d\u628a\u5404\u4f4d\u9001\u5230\u62db\u8d24\u9986\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u91cc\u5c45\u7136\u6709\u62db\u8d24\u9986\uff1f\u201d \u3000\u3000\u5f20\u51e4\u9e23\u7b11\u9053\uff1a\u3000\u201c\u5404\u4f4d\u4f55\u5fc5\u81ea\u8c26\uff0c\u80fd\u8fde\u95ef\u4e94\u5173\uff0c\u81ea\u7136\u79f0\u5f97\u4e0a\u2018\u8d24\u2019\uff0c\u6559\u4e3b\u8981\u6b66\u6797\u670b\u53cb\u8fdb \u8c37\u7684\u76ee\u7684\uff0c\u5c31\u662f\u8981\u62db\u8d24\uff0c\u56e0\u4e4b\uff0c\u53ea\u8981\u95ef\u8fc7\u4e94\u5173\u7684\u4eba\uff0c\u90fd\u53ef\u4ee5\u8fdb\u5165\u62db\u8d24\u9986\u63a5\u53d7\u6b3e\u5f85\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u8fdb\u5165\u62db\u8d24\u9986\u5c31\u4e0d\u9700\u8981\u518d\u505a\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u8981\u505a\u4e8b\uff0c\u4e0d\u505a\u4e8b\u53c8\u4f55\u5fc5\u62db\u8d24\uff1f\u201d \u3000\u3000\u201c\u526f\u7edf\u9886\u53ef\u5426\u544a\u77e5\u90fd\u8981\u505a\u4e9b\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5fc5\u987b\u5148\u7ecf\u8fc7\u4e00\u6bb5\u7ee7\u7eed\u8003\u6838\u65f6\u95f4\uff0c\u7136\u540e\u89c6\u624d\u4efb\u7528\uff0c\u4eba\u624d\u5fc5\u6709\u5927\u7528\uff0c\u5c0f\u624d\u5219\u53ef\u5c0f\u7528\uff0c\u603b\u4e4b\uff0e \u6559\u4e3b\u4e0d\u53ef\u80fd\u517b\u95f2\u4eba\uff0c\u4eba\u4eba\u90fd\u8981\u4e3a\u672c\u6559\u5c3d\u4e9b\u529b\u91cf\u3002\u201d \u3000\u3000\u201c\u6559\u4e3b\u8fd9\u6837\u505a\uff0c\u6700\u7ec8\u7684\u76ee\u7684\u53c8\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u79d8\u5bc6\uff0c\u8001\u673d\u4e0d\u6562\u968f\u4fbf\u900f\u9732\uff0c\u5176\u5b9e\u8001\u673d\u8fd9\u65b9\u9762\u77e5\u9053\u7684\u4e5f\u4e0d\u591a\uff0c\u5404\u4f4d\u4e0d\u59a8\u81ea\u5df2\u60f3\u60f3\uff0c \u5f53\u5e74\u695a\u6c49\u76f8\u4e89\uff0c\u5218\u90a6\u66fe\u547d\u8427\u4f55\u8bbe\u4e0b\u62db\u8d24\u9986\u3002\u62db\u6765\u97e9\u4fe1\uff0c\u5793\u4e0b\u4e00\u6218\uff0c\u9879\u7fbd\u81ea\u520e\u4e4c\u6c5f\uff0c\u4ece\u6b64\u5960\u5b9a \u4e86\u5927\u6c49\u56db\u767e\u5e74\u57fa\u4e1a\uff0c\u6559\u4e3b\u96c4\u624d\u5927\u7565\uff0c\u4e0d\u5728\u5218\u90a6\u4e4b\u4e0b\uff0c\u8bbe\u7acb\u62db\u8d24\u9986\u7528\u610f\u4f55\u5728\uff0c\u4e5f\u5c31\u53ef\u60f3\u800c\u77e5 \u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u542c\u5f97\u987f\u611f\u5fc3\u795e\u9707\u8361\u3002 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u5374\u4f3c\u662f\u5e76\u672a\u8fc7\u4efd\u5403\u60ca\uff0c\u56e0\u4e3a\u4ed6\u4eec\u5bf9\u5f20\u51e4\u9e23\u7684\u4e00\u756a\u8bdd\uff0c\u53ea\u80fd\u7b97\u662f\u4e00\u77e5\u534a\u89e3\u3002 \u3000\u3000\u6563\u5e2d\u540e\uff0c\u5f20\u51e4\u9e23\u968f\u5373\u628a\u5cb3\u5c0f\u98de\u7b49\u56db\u4eba\u5e26\u5230\u5927\u5385\u53f3\u4fa7\u7684\u4e00\u95f4\u5341\u5206\u5bbd\u655e\u7684\u5367\u623f\u3002 \u3000\u3000\u6b63\u56e0\u4e3a\u8fd9\u5367\u623f\u751a\u5927\uff0c\u91cc\u9762\u6446\u7740\u56db\u5f20\u5e8a\u69bb\uff0c\u4ee5\u81f3\u56db\u4e2a\u4eba\u5fc5\u987b\u540c\u5c45\u4e00\u5ba4\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u5cb3\u5c0f\u98de\u4ecd\u4e0d\u4fbf\u628a\u5bb5\u5173\u7edf\u9886\u662f\u51e4\u5ae3\u7ea2\u7684\u4e8b\u544a\u8bc9\u8881\u5c0f\u9e64\u3002 \u3000\u3000\u597d\u5728\u4ed6\u60f3\u5230\u4e0d\u5fc5\u6025\u5728\u4e00\u65f6\uff0c\u5373\u4fbf\u8881\u5c0f\u9e64\u6b64\u523b\u77e5\u9053\u4e86\u8fd9\u4e8b\uff0c\u4e5f\u4e0d\u4fbf\u5373\u523b\u91c7\u53d6\u4ec0\u4e48\u884c\u52a8\u3002 \u3000\u3000\u5f20\u51e4\u9e23\u628a\u56db\u4eba\u9001\u5230\u8fd9\u91cc\u4e4b\u540e\uff0c\u4fbf\u544a\u8f9e\u800c\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u51e0\u4e4e\u4e00\u591c\u4e0d\u66fe\u7761\u597d\uff0c\u6b21\u65e5\u5929\u4e00\u4eae\u4fbf\u72ec\u81ea\u5077\u5077\u8d77\u4e86\u5e8a\uff0c\u88c5\u505a\u5728\u5ead\u9662\u6563\u6b65\u6a21\u6837\uff0c\u76ee\u7684 \u662f\u60f3\u770b\u770b\u51e4\u5ae3\u7ea2\u662f\u5426\u4e5f\u5728\u5c45\u5ba4\u9644\u8fd1\u8d70\u52a8\u3002 \u3000\u3000\u4f46\u4ed6\u5931\u671b\u4e86\uff0c\u51e4\u5ae3\u7ea2\u7684\u4f4f\u5904\uff0c\u4e00\u76f4\u95e8\u7a97\u7d27\u95ed\u3002\u8fde\u670d\u4f8d\u5979\u7684\u4e0b\u4eba\u4e5f\u4e0d\u89c1\u4e00\u4e2a\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u6562\u8fc7\u4e8e\u66b4\u9732\u5f62\u8ff9\uff0c\u4ee5\u514d\u5f15\u4eba\u751f\u7591\uff0c\u53ea\u597d\u518d\u56de\u5230\u5367\u5ba4\u3002\u5f53\u5367\u5ba4\u91cc\u7684\u4eba\u8d77\u5e8a\u4e0d\u4e45\uff0c \u5c31\u6709\u51e0\u540d\u5c0f\u5a62\u9001\u6765\u6dae\u6d17\u7528\u5177\u548c\u7528\u6c34\u3002 \u3000\u3000\u63a5\u7740\u4fbf\u53c8\u88ab\u8bf7\u5230\u5927\u5385\u7528\u65e9\u9910\uff0c\u5f20\u51e4\u9e23\u65e9\u5df2\u7b49\u4faf\u5728\u90a3\u91cc\u3002 \u3000\u3000\u8fd9\u4f4d\u4e0a\u4e86\u5e74\u7eaa\u7684\u526f\u7edf\u9886\uff0c\u5bf9\u62db\u5f85\u5ba2\u4eba\u8fd8\u771f\u6709\u4e00\u5957\uff0c\u5cb3\u5c0f\u98de\u7b49\u56db\u4eba\uff0c\u5927\u6709\u5bbe\u81f3\u5982\u5f52\u4e4b\u611f\u3002 \u3000\u3000\u5f53\u996d\u540e\u51fa\u4e86\u8fd9\u5ea7\u5dcd\u5ce8\u5efa\u7b51\u7684\u5927\u95e8\uff0c\u5cb3\u5c0f\u98de\u7b49\u4eba\u624d\u53d1\u89c9\u9762\u524d\u7adf\u662f\u4e00\u7247\u5e73\u5766\u65f7\u91ce\u3002 \u3000\u3000\u8fd9\u7247\u5e73\u5766\u65f7\u91ce\uff0c\u8db3\u6709\u6570\u5341\u91cc\u65b9\u5706\uff0c\u800c\u65f7\u91ce\u56db\u5468\uff0c\u4f9d\u7136\u662f\u5ced\u58c1\u5982\u524a\uff0c\u5cf0\u5ce6\u63a5\u5929\uff0c\u4f7f\u5f97\u8fd9\u6570 \u5341\u91cc\u7684\u65f7\u91ce\uff0c\u5f62\u6210\u4e00\u4e2a\u6781\u5927\u7684\u8c37\u5730\u3002 \u3000\u3000\u8c01\u4e5f\u4e0d\u96be\u60f3\u89c1\uff0c\u5916\u4eba\u82e5\u60f3\u6765\u5230\u8fd9\u7247\u8c37\u5730\uff0c\u4ecd\u5fc5\u987b\u7ecf\u8fc7\u8bbe\u6709\u4e94\u5173\u7684\u8c37\u9053\u8fdb\u5165\uff0c\u82e5\u4f01\u56fe\u7531\u56db \u5468\u7684\u5c71\u58c1\u76f4\u63a5\u964d\u4e0b\uff0c\u5373\u4fbf\u6709\u767b\u5cf0\u9020\u6781 \u3000\u3000\u7684\u8f7b\u529f\uff0c\u4e5f\u5fc5\u96be\u5f97\u901e\uff0c\u5929\u5730\u95f4\u9020\u7269\u4e4b\u5947\uff0c\u600e\u4e0d\u4ee4\u4eba\u4e3a\u4e4b\u9707\u6817\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u95ee\u9053\uff1a\u201c\u7531\u8fd9\u91cc\u5230\u62db\u8d24\u9986\u6709\u591a\u8fdc\uff1f\u201d \u3000\u3000\u5f20\u51e4\u9e23\u62ac\u624b\u4e00\u6307\u9053\uff1a\u201c\u5927\u7ea6\u4e03\u516b\u91cc\u8def\uff0c\u518d\u8d70\u76cf\u8336\u4e0b\u592b\uff0c\u4fbf\u53ef\u770b\u5230\u3002\u201d \u3000\u3000\u4e00\u8def\u4e0a\uff0c\u4f46\u89c1\u8fd9\u7247\u8c37\u5730\u4e4b\u5185\uff0c\u522b\u5904\u90fd\u6709\u623f\u820d\uff0c\u5c31\u50cf\u661f\u7f57\u68cb\u5e03\u7684\u6751\u843d\u4e00\u822c\uff0c\u800c\u4e14\u623f\u820d\u7684\u5f62 \u5f0f\uff0c\u5404\u6709\u4e0d\u540c\uff0c\u5947\u602a\u7684\u662f\u5f88\u96be\u89c1\u5230\u884c\u4eba\u3002 \u3000\u3000\u4e03\u516b\u91cc\u7684\u8def\u7a0b\uff0c\u4e0d\u8db3\u534a\u4e2a\u65f6\u8fb0\u4fbf\u5230\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7b49\u4eba\u53ea\u9053\u62db\u8d24\u9986\u5fc5\u662f\u4e00\u5904\u5dcd\u5ce8\u5efa\u7b51\uff0c\u5c82\u77e5\u7adf\u662f\u51e0\u5341\u5e62\u72ec\u95e8\u72ec\u9662\u7684\u5e73\u623f\uff0c\u5c31\u50cf\u8fdb\u5165 \u516b\u5366\u9635\u4e00\u822c\uff0c\u82e5\u65e0\u4eba\u5e26\u8def\uff0c\u6839\u672c\u4e0d\u77e5\u9053\u5230\u54ea\u91cc\u53bb\u627e\u63a5\u5f85\u7684\u4eba\u3002 \u3000\u3000\u5f20\u51e4\u9e23\u505c\u4e0b\u811a\u6b65\u9053\uff1a\u201c\u5404\u4f4d\u5c31\u8bf7\u5728\u8fd9\u91cc\u7a0d\u7b49\u4e00\u4e0b\uff0c\u9a6c\u4e0a\u4fbf\u6709\u4eba\u51fa\u6765\u8fce\u63a5\u3002\u201d \u3000\u3000\u53ef\u89c1\u5f20\u51e4\u9e23\u4e5f\u4e0d\u6e05\u695a\u8be5\u76f4\u63a5\u628a\u4eba\u9886\u5230\u4f55\u5904\u3002 \u3000\u3000\u8fd8\u597d\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u679c\u7136\u6709\u4e94\u540d\u9ed1\u8863\u6c49\u5b50\u5411\u8fd9\u8fb9\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5f20\u51e4\u9e23\u5fd9\u5411\u90a3\u4e3a\u9996\u7684\u62f1\u62f1\u624b\u9053\uff1a\u201c\u5434\u8001\u5f1f\uff0c\u8001\u673d\u628a\u4ed6\u4eec\u56db\u4f4d\u5e26\u6765\u4e86\uff01\u201d \u3000\u3000\u59d3\u5434\u7684\u6c49\u5b50\u5658\u5634\u4e00\u7b11\u9053\uff1a\u201c\u5f20\u526f\u7edf\u9886\u8f9b\u82e6\u4e86\uff0c\u4eba\u65e2\u7136\u5230\u4e86\uff0c\u60a8\u8001\u5c31\u8bf7\u56de\u5427\uff01\u201d \u3000\u3000\u5f20\u51e4\u9e23\u671b\u7740\u5cb3\u5c0f\u98de\u7b49\u56db\u4eba\u62f1\u62f1\u624b\u9053\uff1a\u201c\u5404\u4f4d\u7684\u4e8b\uff0c\u8fd9\u4f4d\u5434\u8001\u5f1f\u81ea\u6709\u5b89\u6392\uff0c\u8001\u673d\u4e0d\u4fbf\u518d\u966a\uff0c \u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u5f20\u51e4\u9e23\u8d70\u540e\uff0c\u59d3\u5434\u7684\u6c49\u5b50\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u5f20\u540d\u5355\uff0c\u770b\u4e86\u4e00\u904d\u9053\uff1a \u3000\u3000\u201c\u56db\u4f4d\u4e00\u4f4d\u53eb\u5cb3\u5c0f\u98de\uff0c\u4e00\u4f4d\u53eb\u8881\u5c0f\u9e64\uff0c\u4e00\u4f4d\u53eb\u4e01\u6d9b\uff0c\u4e00\u4f4d\u53eb\u7518\u9716\uff0c\u6ca1\u9519\u5427\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b49\u4eba\u5e76\u672a\u7b54\u8bdd\uff0c\u4ed6\u4eec\u5bf9\u5bf9\u65b9\u76f4\u547c\u81ea\u5df1\u59d3\u540d\uff0c\u96be\u514d\u90fd\u6709\u4e9b\u4e0d\u662f\u6ecb\u5473\u3002 \u3000\u3000\u81f3\u4e8e\u4ed6\u4eec\u56db\u4eba\u7684\u59d3\u540d\u5bf9\u65b9\u5982\u4f55\u77e5\u9053\uff0c\u90a3\u662f\u56e0\u4e3a\u6628\u665a\u5f20\u51e4\u9e23\u66fe\u7ecf\u95ee\u8fc7\uff1f\u5f53\u7136\u662f\u7531\u5bb5\u5173\u4f20\u8fc7 \u6765\u7684\u3002 \u3000\u3000\u59d3\u5434\u7684\u6c49\u5b50\u518d\u9053\uff1a\u201c\u65e2\u7136\u6ca1\u9519\uff0c\u73b0\u5728\u56db\u4f4d\u5c31\u8bf7\u5206\u522b\u5230\u5206\u914d\u7684\u4f4f\u6240\u53bb\u3002\u201d \u3000\u3000\u4e01\u6d9b\u6123\u4e86\u4e00\u4e0b\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u96be\u9053\u6211\u4eec\u56db\u4eba\u4e0d\u4f4f\u5728\u4e00\u8d77\uff1f\u201d \u3000\u3000\u59d3\u5434\u7684\u6c49\u5b50\u6b49\u7136\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u672c\u9986\u9986\u4e3b\u7684\u89c4\u5b9a\uff0c\u5404\u4f4d\u6765\u5230\u540e\uff0c\u5fc5\u987b\u5404\u4eba\u5230\u9f50\u4eba\u6307\u5b9a\u7684 \u5730\u65b9\u53bb\u3002\u201d \u3000\u3000\u4e01\u6d9b\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u50cf\u4ec0\u4e48\u8bdd\uff1f\u8001\u5b50\u548c\u8fd9\u4f4d\u59d3\u7518\u7684\u4e00\u5411\u4e0d\u5206\u5bb6\uff0c\u4f60\u51ed\u4ec0\u4e48\u628a\u6211\u4eec\u5206 \u5f00\uff1f\u201d \u3000\u3000\u59d3\u5434\u7684\u6c49\u5b50\u89c1\u4e01\u6d9b\u51fa\u8a00\u4e0d\u900a\uff0c\u4f3c\u662f\u8981\u53d1\u4f5c\uff0c\u4f46\u6700\u540e\u8fd8\u662f\u5fcd\u4e86\uff0c \u3000\u3000\u563f\u563f\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u4e01\u8001\u5144\u7528\u4e0d\u7740\u53d1\u7765\u6c14\uff0c\u8fd9\u662f\u9986\u4e3b\u7684\u89c4\u5b9a\uff0c\u9986\u4e3b\u4e5f\u662f\u5c0a\u5949\u6559\u4e3b\u7684\u4ee4\u8c15 \u884c\u4e8b\uff0c\u5144\u5f1f\u66f4\u662f\u5949\u547d\u884c\u4e8b\uff0c\u4f60\u8ddf\u5144\u5f1f\u53d1\u813e\u8d77\uff0c\u5b9e\u5728\u662f\u53d1\u9519\u4e86\u5bf9\u8c61\uff01\u201d \u3000\u3000\u4e01\u6d9b\u543c\u9053\uff1a\u201c\u4f60\u4eec\u9986\u4e3b\u662f\u8c01\uff0c\u8001\u5b50\u8981\u4f1a\u4f1a\u4ed6\uff01\u201d \u3000\u3000\u59d3\u5434\u7684\u6c49\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4e01\u8001\u5144\u65e2\u7136\u6765\u4e86\uff0c\u5f53\u7136\u4f1a\u89c1\u5230\u4ed6\uff0c\u4f55\u5fc5\u8fd9\u6837\u6027\u6025\uff01\u201d \u3000\u3000\u4e01\u6d9b\u6b63\u8981\u518d\u95f9\uff0c\u7518\u9716\u5df2\u4e00\u628a\u62c9\u4f4f\u4ed6\u4e00\u6761\u624b\u81c2\u9053\uff1a\u201c\u51b7\u9759\u4e00\u70b9\uff0c\u5165\u5883\u968f\u4fd7\uff0c\u518d\u95ee\u4e0b\u53bb\uff0c\u5403 \u4e8f\u7684\u8fd8\u662f\u4f60\u81ea\u5df1\uff01\u201d \u3000\u3000\u59d3\u5434\u7684\u6c49\u5b50\u968f\u5373\u5429\u5490\u624b\u4e0b\u56db\u540d\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u7167\u7740\u521a\u624d\u7684\u5206\u914d\u4f4d\u7f6e\uff0c\u7ed9\u4ed6\u4eec\u56db\u4f4d\u5e26\u8def\uff01\u201d \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c \u4e5d \u56de\u3000\u8fde\u4e0b\u4e09\u57ce \u3000\u3000\u5cb3\u5c0f\u98de\u968f\u7740\u4e00\u540d\u9ed1\u8863\u4eba\u5f80\u897f\u884c\u53bb\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u4e00\u8def\u4e4b\u4e0a\uff0c\u4e00\u76f4\u4e0d\u66fe\u5f00\u53e3\u8bf4\u6d3b\uff0c\u5c31\u50cf\u54d1\u5df4\u4e00\u822c\u3002\u5cb3\u5c0f\u98de\u4e5f\u61d2\u5f97\u591a\u95ee\u3002 \u3000\u3000\u4e00\u76f4\u5feb\u8d70\u5230\u6240\u6709\u623f\u820d\u7684\u897f\u8fb9\u5c3d\u5934\uff0c\u9ed1\u8863\u4eba\u624d\u5728\u4e00\u5904\u95e8\u53e3\u505c\u4e0b\u3002\u5e76\u62ac\u624b\u6572\u4e86\u6572\u95e8\u3002 \u3000\u3000\u5f00\u95e8\u7684\u662f\u4e2a\u5341\u4e03\u516b\u5c81\u7684\u5c11\u5e74\uff0c\u4eba\u957f\u5f97\u8fd8\u5341\u5206\u79c0\u6c14\uff0c\u7a7f\u7740\u4e00\u8eab\u84dd\u8863\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u4eba\u5230\u8fd9\u65f6\u624d\u5f00\u4e86\u53e3\uff1a\u201c\u5c0f\u6797\uff0c\u4eba\u5e26\u5230\u4e86\u3002\u5f85\u4f1a\u513f\u5fc5\u987b\u5148\u5bf9\u4ed6\u8bf4\u660e\u8fd9\u91cc\u7684\u4e00\u5207\u89c4 \u5b9a\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u5927\u53d4\u653e\u5fc3\uff0c\u51b3\u4e0d\u4f1a\u8bef\u4e8b\uff0c\u60a8\u8bf7\u5230\u91cc\u9762\u5750\u5750\uff01\u201d \u3000\u3000\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4e8b\uff0c\u8fd9\u5c31\u8d70\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u76f4\u671b\u7740\u9ed1\u8863\u4eba\u8d70\u8fdc\uff0c\u624d\u56de\u8fc7\u5934\u6765\u62db\u547c\u5cb3\u5c0f\u98de\u3002 \u3000\u3000\u4ed6\u731b\u89c1\u9762\u524d\u7ad9\u7740\u7684\u7adf\u662f\u4e2a\u5341\u4e09\u56db\u5c81\u7684\u5b69\u5b50\uff0c\u987f\u73b0\u4e00\u8138\u8bb6\u5f02\u4e4b\u8272\uff0c\u56e0\u4e3a\u51e1\u662f\u80fd\u8fdb\u5165\u62db\u8d24\u9986 \u7684\uff0c\u90fd\u662f\u6587\u6b66\u517c\u8d44\u7684\u9ad8\u624b\uff0c\u600e\u53ef\u80fd\u4f1a \u3000\u3000\u662f\u4e2a\u534a\u5927\u4e0d\u5c0f\u7684\u5b69\u5b50\uff1f \u3000\u3000\u4f46\u8fd9\u5b69\u5b50\u660e\u660e\u662f\u9ed1\u8863\u4eba\u4eb2\u81ea\u9001\u6765\u7684\uff0c\u53c8\u51b3\u4e0d\u53ef\u80fd\u9001\u9519\uff0c\u4ed6\u5728\u6123\u8fc7\u4e4b\u540e\uff0c\u8fd8\u662f\u4e0d\u5f97\u4e0d\u4ee5\u793c \u76f8\u5f85\uff0c\u95ea\u8fc7\u4e00\u65c1\uff0c\u966a\u7740\u7b11\u9053\uff1a\u201c\u516c\u5b50\u8bf7\u8fdb\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fc8\u6b65\u8fdb\u53bb\uff0c\u8fd9\u624d\u770b\u6e05\u91cc\u9762\u662f\u4e00\u5e62\u56db\u5408\u9662\u5efa\u7b51\uff0c\u9662\u5b50\u91cc\u8fd8\u79cd\u7740\u4e0d\u5c11\u82b1\u6728\uff0c\u6574\u7406\u5f97\u5341 \u5206\u6e05\u6d01\u3002 \u3000\u3000\u4ed6\u7565\u7565\u56db\u987e\u4e86\u4e00\u773c\u9053\uff1a\u201c\u8fd9\u91cc\u8fd8\u4f4f\u7740\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u54c8\u7740\u8170\u9053\uff1a\u201c\u53ea\u6709\u5c0f\u7684\u4e00\u4e2a\uff0c\u5982\u4eca\u516c\u5b50\u6765\u4e86\uff0c\u4e5f\u4e0d\u8fc7\u4e24\u4e2a\u4eba\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fd9\u6837\u5927\u7684\u4e00\u5e62\u623f\u820d\uff0c\u5373\u4fbf\u6709\u5341\u4e2a\u4e8c\u5341\u4e2a\u4eba\u4e5f\u4f4f\u5f97\u4e0b\u3002\u4e3a\u4ec0\u4e48\u8ba9\u5b83\u7a7a\u7740\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u8fd9\u662f\u9986\u4e3b\u7684\u5206\u914d\uff0c\u4ed6\u8001\u4eba\u5bb6\u4e3a\u4e86\u793c\u8d24\u4e0b\u58eb\uff0c\u5fc5\u987b\u8ba9\u4f4f\u7684\u5730\u65b9\u5c3d\u91cf\u5bbd\u655e\u8212\u9002\uff0c \u624d\u80fd\u663e\u51fa\u5f85\u5ba2\u7684\u8bda\u610f\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6697\u9053\uff1a\u201c\u82e5\u8ba9\u8881\u5927\u54e5\u548c\u4e01\u3001\u7518\u4e24\u4f4d\u5f53\u5bb6\u7684\u4e5f\u4f4f\u5728\u8fd9\u91cc\uff0c\u90a3\u8be5\u591a\u597d\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u516c\u5b50\u7531\u5bb5\u5173\u5230\u8fd9\u91cc\u6765\uff0c\u5fc5\u5b9a\u8d70\u7d2f\u4e86\uff0c\u5148\u8bf7\u8fdb\u623f\u559d\u676f\u8336\u4f11\u606f\u4e00\u4e0b\uff01\u201d \u3000\u3000\u4ed6\u628a\u5cb3\u5c0f\u98de\u76f4\u63a5\u5f15\u5165\u540e\u9762\u6b63\u623f\u3002 \u3000\u3000\u6b63\u623f\u4e00\u6392\u4e94\u95f4\uff0c\u4e2d\u95f4\u53ea\u6709\u4e00\u95e8\u901a\u884c\uff0c\u4e00\u8fdb\u53bb\u5148\u662f\u5ba2\u5385\uff0c\u5de6\u53f3\u4ff1\u4e3a\u5367\u5ba4\uff0c\u5367\u5ba4\u5185\u4e24\u7aef\u662f\u5957 \u623f\u3002 \u3000\u3000\u8fdb\u5165\u5ba2\u5385\uff0c\u5e03\u7f6e\u5f97\u5176\u4e3a\u96c5\u6d01\uff0c\u5cb3\u5c0f\u98de\u5750\u4e0b\u540e\uff0c\u90a3\u5c11\u5348\u7acb\u523b\u6c8f\u4e0a\u8336\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u559d\u4e86\u53e3\u8336\u9053\uff1a\u201c\u8fd8\u6ca1\u8bf7\u95ee\uff0c\u4f60\u5728\u8fd9\u91cc\u662f\u8d1f\u8d23\u505a\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1b\u201c\u5c0f\u7684\u8d1f\u8d23\u662f\u5e62\u623f\u5b50\u7684\u770b\u7ba1\u4e0e\u6574\u7406\uff0c\u4ee5\u53ca\u9644\u8fd1\u73af\u5883\u7684\u6e05\u6d01\uff0c\u4f46\u6700\u4e3b\u8981\u7684\uff0c\u662f \u670d\u4f8d\u4f4f\u5728\u8fd9\u91cc\u7684\u8d35\u5ba2\uff0c\u73b0\u5728\u6765\u8bf4\uff0c\u5c31\u662f\u670d\u4f8d\u516c\u5b50\u4e86\u3002\u201d \u3000\u3000\u201c\u6211\u8be5\u77e5\u9053\u4f60\u59d3\u4ec0\u4e48\u53eb\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u53eb\u6797\u91d1\u5b9d\u3002\u201d \u3000\u3000\u201c\u5728\u6211\u6765\u4e4b\u524d\uff0c\u8fd9\u91cc\u662f\u8c01\u4f4f\u8fc7\uff1f\u201d \u3000\u3000\u201c\u5728\u6211\u6765\u4e4b\u524d\uff0c\u6709\u7684\u4f4f\u4e24\u4e09\u5929\u4fbf\u8d70\uff0c\u6700\u957f\u4e5f\u6709\u4f4f\u4e24\u4e09\u4e2a\u6708\u7684\u3002\u201d \u3000\u3000\u201c\u662f\u5426\u6bcf\u6b21\u90fd\u662f\u4e00\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6bcf\u6b21\u90fd\u662f\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u79bb\u5f00\u8fd9\u91cc\u4e4b\u540e\uff0c\u53c8\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u4e5f\u4e0d\u592a\u6e05\u695a\uff0c\u636e\u8bf4\u90fd\u662f\u5206\u914d\u5230\u6b63\u5f0f\u5de5\u4f5c\uff0c\u5230\u522b\u7684\u5730\u65b9\u4e3a\u6559\u4e3b\u6548\u529b\u3002\u201d \u3000\u3000\u201c\u5728\u6211\u6ca1\u6765\u4e4b\u524d\uff0c\u8fd9\u623f\u5b50\u662f\u5426\u7a7a\u8fc7\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5df2\u7ecf\u7a7a\u4e86\u5341\u51e0\u5929\uff0c\u6ca1\u6709\u8d35\u5ba2\u4f4f\u8fdb\u6765\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u6211\u60f3\u518d\u5230\u95e8\u5916\u770b\u770b\uff0c\u521a\u624d\u90a3\u4eba\u4e0d\u662f\u4ea4\u4ee3\u8fc7\u4f60\u628a\u8fd9\u91cc\u7684\u89c4\u5b9a\u544a\u8bc9 \u4e48\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u516c\u5b50\u82e5\u5230\u95e8\u5916\uff0c\u5c0f\u7684\u6b63\u597d\u53ef\u4ee5\u628a\u8fd9\u91cc\u7684\u89c4\u5b9a\u5411\u60a8\u8bf4\u660e\u3002\u201d \u3000\u3000\u6765\u5230\u95e8\u5916\uff0c\u5411\u4e1c\u671b\u53bb\uff0c\u50cf\u8fd9\u6837\u7684\u72ec\u95e8\u9662\u5efa\u7b51\uff0c\u6809\u6bd4\u9cde\u6b21\uff0c\u4e00\u671b\u65e0\u9645\uff0c\u4e0d\u4e0b\u6570\u5341\u4ea9\u7684\u8303\u56f4\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fd9\u4e48\u591a\u623f\u5b50\uff0c\u597d\u50cf\u5efa\u7b51\u7684\u5f62\u5f0f\u90fd\u5dee\u4e0d\u591a\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5b8c\u5168\u4e00\u6837\uff0c\u4e00\u5171\u662f\u4e03\u5341\u4e8c\u5e62\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e03\u5341\u4e8c\u5e62\uff1f\u800c\u4e14\u5f62\u5f0f\u53c8\u5b8c\u5168\u4e00\u6837\uff0c\u5916\u4eba\u627e\u8d77\u6765\u5c82\u4e0d\u662f\u5f88\u4e0d\u65b9\u4fbf\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u5c82\u53ea\u5916\u4eba\uff0c\u8fde\u5c0f\u7684\u6d3e\u5230\u8fd9\u91cc\u4e24\u4e09\u5e74\uff0c\u6709\u65f6\u5019\u4e5f\u5e38\u6655\u5934\u8f6c\u5411\uff0c\u4e0d\u8fc7\u6bcf\u5e62\u623f\u5b50 \u90fd\u6709\u7f16\u53f7\uff0c\u987a\u7740\u7f16\u53f7\u627e\uff0c\u5c31\u6bd4\u8f83\u5bb9\u6613\u4e9b\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u91cc\u662f\u7b2c\u51e0\u53f7\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u8fd9\u5e62\u623f\u5b50\u662f\u7b2c\u4e09\u5341\u516d\u53f7\uff0c\u5c31\u53eb\u4e09\u5341\u516d\u53f7\u5206\u9986\u3002\u201d \u3000\u3000\u201c\u662f\u5426\u8fd8\u6709\u603b\u9986\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6709\uff0c\u9986\u4e3b\u4f4f\u7684\u5730\u65b9\u5c31\u662f\u603b\u9986\uff0c\u603b\u9986\u5916\u9762\u780c\u7740\u56f4\u5899\uff0c\u91cc\u9762\u6709\u4e0a\u767e\u95f4\u623f\u820d\uff0c\u800c\u4e14\u8fd8\u6709 \u697c\u623f\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6781\u76ee\u671b\u53bb\uff0c\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u6211\u597d\u50cf\u6ca1\u53d1\u73b0\u603b\u9986\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u62ac\u624b\u4e00\u6307\u9053\uff1a\u201c\u5c31\u5728\u90a3\u8fb9\uff0c\u56e0\u4e3a\u6709\u9053\u5c71\u5761\u6321\u7740\uff0c\u6811\u6728\u53c8\u591a\uff0c\u5728\u8fd9\u91cc\u65e0\u6cd5\u770b\u5230\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7565\u4e00\u6c89\u541f\u9053\uff1b\u201c\u521a\u624d\u5e26\u6211\u6765\u7684\u90a3\u540d\u9ed1\u8863\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1b\u201c\u90a3\u662f\u603b\u9986\u7684\u9986\u5dee\uff0c\u603b\u9986\u9986\u5dee\u6709\u4e8c\u4e09\u5341\u4e2a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9ed8\u4e86\u4e00\u9ed8\uff0c\u518d\u95ee\uff1a\u201c\u6211\u7531\u5bb5\u5173\u6765\u65f6\uff0c\u4e00\u5171\u662f\u56db\u4eba\uff0c\u5148\u524d\u6709\u4f4d\u5e26\u9886\u56db\u540d\u9986\u5dee\u53bb\u63a5\u7684\uff0c \u90a3\u4eba\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u90a3\u4eba\u662f\u4e0d\u662f\u59d3\u5434\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u59d3\u5434\u3002\u201d \u3000\u3000\u201c\u90a3\u4e00\u5b9a\u662f\u603b\u9986\u7684\u5434\u526f\u603b\u7ba1\uff0c\u522b\u770b\u4ed6\u53ea\u662f\u4e2a\u526f\u603b\u7ba1\uff0c\u813e\u6c14\u6bd4\u603b\u7ba1\u90fd\u5927\uff0c\u8fde\u5c0f\u7684\u90fd\u7ecf\u5e38\u6328 \u8fc7\u4ed6\u9a82\u3002\u201d \u3000\u3000\u201c\u4f60\u4e5f\u53d7\u4ed6\u7ba1\u8f96\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u5566\uff0e\u6211\u4eec\u4e03\u5341\u4e8c\u5206\u9986\uff0c\u6bcf\u5206\u9986\u90fd\u6709\u4e00\u4e2a\u50cf\u5c0f\u7684\u8fd9\u6837\u7684\u4eba\u5728\u5b88\u7740\uff0c\u5206\u9986\u662f\u5c5e\u4e8e\u603b\u7ba1 \u7684\uff0c\u53c8\u600e\u6562\u4e0d\u542c\u4ed6\u7684\u5462\uff0c\u5c31\u8fde\u603b\u9986\u7684\u9986\u5dee\uff1f\u7167\u6837\u53ef\u4ee5\u5bf9\u5206\u9986\u7684\u4eba\u6253\u5b98\u8154\uff0c\u89c4\u77e9\u53ef\u5927\u5f97\u5f88\u54ea\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5411\u524d\u8d70\u4e86\u51e0\u6b65\u9053\uff1a\u201c\u73b0\u5728\u4f60\u8be5\u8bf4\u8bf4\u8fd9\u91cc\u7684\u89c4\u77e9\u4e86\uff01\u201d \u3000\u3000\u6797\u91d1\u5b9d\u4f3c\u662f\u6709\u4e9b\u96be\u4ee5\u5f00\u53e3\uff0c\u53e3\u9f7f\u542f\u52a8\u4e86\u597d\u4e00\u9635\uff0c\u624d\u9053\uff1a\u201c\u516c\u5b50\u5343\u4e07\u522b\u89c1\u602a\uff0c\u8bf4\u51fa\u6765\u2014\u5b9a \u8ba9\u60a8\u4e0d\u8212\u670d\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6de1\u7136\u4e00\u7b11\uff1a\u201c\u4f60\u53ea\u7ba1\u8bf4\uff01\u201d \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u6240\u8c13\u89c4\u5b9a\uff0c\u4e5f\u5c31\u662f\u516c\u5b50\u6765\u5230\u8fd9\u91cc\uff0c\u5728\u884c\u52a8\u4e0a\u5fc5\u987b\u53d7\u5230\u5f88\u591a\u9650\u5236\uff0c\u7b49\u4e8e\u5931\u53bb \u4e86\u81ea\u7531\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8bb6\u7136\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u90a3\u5c31\u662f\u516c\u5b50\u4ece\u73b0\u5728\u8d77\uff0c\u5fc5\u987b\u6574\u5929\u5446\u5728\u5206\u9986\u91cc\uff0c\u4e0d\u80fd\u5230\u5904\u4e71\u8dd1\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u96be\u9053\u6211\u8fde\u5230\u95e8\u5916\u8d70\u8d70\u90fd\u4e0d\u6210\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b85\u54bd\u4e86\u2014\u53e3\u553e\u6db2\u9053\uff1a\u201c\u5230\u95e8\u5916\u8d70\u8d70\u5f53\u7136\u53ef\u4ee5\uff0c\u4f46\u5374\u53ea\u4ee5\u5206\u9986\u56db\u5468\u4e09\u5341\u6b65\u4e3a\u9650\uff0c\u8d85\u8fc7 \u4e09\u5341\u6b65\uff0c\u90a3\u5c31\u662f\u8fdd\u72af\u89c4\u5b9a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u7b11\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u8fd9\u91cc\u65e2\u662f\u62db\u8d24\u9986\uff0c\u53c8\u628a\u4f4f\u8fdb\u6765\u7684 \u3000\u3000\u4eba\u4ee5\u8d35\u5bbe\u76f8\u5f85\uff0c\u5374\u53c8\u50cf\u56da\u72af\u4e00\u822c\u7684\u9650\u5236\u884c\u52a8\uff0c\u8fd9\u4f4d\u9986\u4e3b\u7a76\u7adf\u662f\u5b89\u7684\u4ec0\u4e48\u5fc3\u5462\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u82eb\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u9986\u4e3b\u4e5f\u662f\u5949\u4e86\u6559\u4e3b\u7684\u4ee4\u8c15\u8fd9\u6837\u505a\uff0c\u8fde\u5c0f\u7684\u4e5f\u89c9\u5f97\u4e0d\u5408\u7406\uff0c\u4f46\u5374\u4e0d \u6562\u4e0d\u628a\u8fd9\u89c4\u5b9a\u544a\u8bc9\u516c\u5b50\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u518d\u5ea6\u51b7\u7b11\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u9075\u5b88\u8fd9\u89c4\u5b9a\uff0c\u4ed6\u4eec\u53c8\u6562\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u7acb\u523b\u7751\u8272\u5927\u53d8\uff0c\u60c5\u4e0d\u81ea\u7981\u6253\u7740\u54c6\u55e6\u9053\uff1a\u201c\u516c\u5b50\u5343\u4e07\u4e0d\u80fd\u4e0d\u5b88\u89c4\u5b9a\uff0c\u90a3\u6837\u4e00\u6765\uff0c\u5c0f \u7684\u7b2c\u4e00\u4e2a\u906d\u6b83\uff01\u201d \u3000\u3000\u201c\u8fd9\u662f\u6211\u4e2a\u4eba\u7684\u4e8b\uff0c\u53c8\u600e\u4f1a\u8fde\u7d2f\u5230\u4f60\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u5f53\u7136\u4f1a\u53d7\u5230\u7275\u8fde\uff0c\u516c\u5b50\u662f\u806a\u660e\u4eba\uff0c\u4e0d\u9700\u5c0f\u7684\u591a\u89e3\u91ca\uff0c\u4e8c\u4e2a\u6708\u4ee5\u524d\uff0c\u4e8c\u5341\u56db\u53f7\u5206\u9986 \u5ba2\u4eba\u56e0\u4e3a\u6ca1\u9075\u5b88\u89c4\u5b9a\uff0c\u7ed3\u679c\u90a3\u540d\u50cf\u5c0f\u7684\u4e00\u6837\u7684\u770b\u5b88\u5206\u9986\u7684\u4eba\uff0c\u4fbf\u88ab\u5434\u526f\u603b\u7ba1\u6d3b\u6d3b\u6253\u6b64\uff01\u201d \u3000\u3000\u201c\u90a3\u4f4d\u8d35\u5bbe\u5462\uff1f\u201d \u3000\u3000\u201c\u4e5f\u88ab\u9010\u51fa\u5206\u9986\uff0c\u540e\u7c73\u4e00\u76f4\u6ca1\u6709\u6d88\u606f\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7ec8\u4e8e\u5fc3\u91cc\u6709\u6570\uff0c\u8fd9\u5206\u9986\u7684\u770b\u5b88\u8005\uff0c\u9664\u4e86\u670d\u4f8d\u4f4f\u8fdb\u6765\u7684\u5ba2\u4eba\uff0c\u8fd8\u8d1f\u6709\u76d1\u89c6\u5ba2\u4eba\u7684\u8d23 \u4efb\uff0c\u8fd9\u6837\u770b\u6765\uff0c\u81ea\u5df1\u7684\u786e\u4e0d\u80fd\u592a\u4efb\u6027\uff0c \u3000\u3000\u4ed6\u53ef\u4ee5\u4e0d\u987e\u81ea\u5df1\u7684\u5b89\u5371\uff0c\u4f46\u5374\u4e0d\u80fd\u56e0\u800c\u4f7f\u6797\u91d1\u5b9d\u53d7\u5230\u8fde\u7d2f\u3002 \u3000\u3000\u518d\u56de\u5230\u5ba2\u5385\uff0c\u6797\u91d1\u5b9d\u53c8\u6c8f\u4e0a\u8336\u3002 \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u516c\u5b50\u8be5\u5230\u5367\u623f\u4f11\u606f\u4e86\uff0c\u5c0f\u7684\u8fd9\u5c31\u53bb\u62ff\u996d\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6123\u4e86\u4e0b\u9053\uff1a\u201c\u5230\u54ea\u91cc\u53bb\u62ff\u996d\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u5206\u9986\u6ca1\u6709\u53a8\u623f\uff0c\u4e0d\u5f00\u4f19\uff0c\u6bcf\u987f\u996d\u90fd\u662f\u5230\u603b\u9986\u53bb\u62ff\u3002\u201d \u3000\u3000\u201c\u603b\u9986\u5230\u8fd9\u91cc\u6709\u591a\u8fdc\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7b97\u592a\u8fdc\uff0c\u6765\u56de\u518d\u52a0\u4e0a\u6253\u996d\u6253\u83dc\u65f6\u95f4\uff0c\u6700\u591a\u534a\u4e2a\u65f6\u8fb0\u3002\u201d \u3000\u3000\u201c\u90a3\u672a\u514d\u592a\u8f9b\u82e6\u4f60\u4e86\uff01\u201d \u3000\u3000\u201c\u5c0f\u7684\u5e72\u7684\u5c31\u662f\u8fd9\u4e2a\uff0c\u54ea\u80fd\u7b97\u662f\u8f9b\u82e6\uff01\u201d \u3000\u3000\u6797\u91d1\u5b9d\u8d70\u540e\uff0c\u5cb3\u5c0f\u98de\u968f\u5373\u8fdb\u5165\u91cc\u9762\u7684\u5367\u5ba4\u3002 \u3000\u3000\u5367\u5ba4\u91cc\u7684\u5e03\u7f6e\uff0c\u5012\u662f\u4ee4\u4ed6\u5341\u5206\u6ee1\u610f\uff0c\u5e8a\u69bb\u4e0a\u9526\u88ab\u7ee3\u8925\uff0c\u800c\u4e14\u90fd\u662f\u5d2d\u65b0\u7684\uff0c\u4f3c\u662f\u4ee5\u524d\u65e0\u4eba \u7528\u8fc7\u800c\u7279\u522b\u4e3a\u4ed6\u51c6\u5907\u7684\u3002 \u3000\u3000\u684c\u524d\u6446\u653e\u7740\u7ea2\u6f06\u684c\u51e0\uff0c\u4e0a\u9762\u653e\u7740\u7cbe\u81f4\u7684\u767d\u74f7\u58f6\u4ee5\u53ca\u8336\u76d8\u8336\u676f\uff0c\u5e76\u6709\u4e00\u76cf\u9020\u5f62\u9887\u4e3a\u522b\u81f4\u7684 \u7f69\u706f\u3002 \u3000\u3000\u5e8a\u69bb\u5bf9\u9762\u9760\u58c1\uff0c\u6709\u4e24\u5f20\u592a\u5e08\u6905\uff0c\u6905\u57ab\u548c\u6905\u7f69\u4e5f\u90fd\u662f\u9526\u7ee3\u7684\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u89e3\u4e0b\u957f\u5251\uff0c\u4ef0\u8eab\u8eba\u4e0a\u5e8a\u53bb\u3002\u4ed6\u672c\u60f3\u6e05\u9759\u4e00\u4e0b\uff0c\u4f46\u8111\u6d77\u91cc\u601d\u7eea\u8d77\u4f0f\uff0c\u5374\u518d\u4e5f\u6e05\u9759\u4e0d \u5f97\u3002 \u3000\u3000\u9996\u5148\uff0c\u4ed6\u611f\u5230\u6b64\u523b\u6709\u5982\u8eab\u5728\u6c99\u6f20\uff0c\u832b\u832b\u65e0\u6240\u9002\u4ece\u3002 \u3000\u3000\u53e6\u5916\uff0c\u4ed6\u4e5f\u6000\u5ff5\u7740\u8881\u5c0f\u9e64\u3001\u4e01\u6d9b\u548c\u7518\u9716\uff0c\u4e0d\u77e5\u4ed6\u4eec\u7a76\u7adf\u5206\u914d\u5230\u54ea\u4e00\u5904\u5206\u9986\u3002 \u3000\u3000\u5c24\u5176\uff0c\u4ed6\u4e0d\u5f97\u4e0d\u4e3a\u4e01\u6d9b\u62c5\u5fc3\uff0c\u56e0\u4e3a\u4ee5\u4e01\u6d9b\u7684\u6027\u5b50\uff0c\u4f4f\u8fdb\u5206\u9986\u4e4b\u540e\uff0c\u5fc5\u5b9a\u4e0d\u7518\u5fc3\u884c\u52a8\u53d7\u5230 \u9650\u5236\uff0c\u5f88\u53ef\u80fd\u4f1a\u95f9\u51fa\u7978\u4e8b\uff0c\u90a3\u540e\u679c\u5b9e \u3000\u3000\u5728\u4e0d\u582a\u8bbe\u60f3\u3002 \u3000\u3000\u8fd8\u6709\uff0c\u56e0\u4e3a\u89c1\u4e0d\u5230\u8881\u5c0f\u9e64\uff0c\u65e0\u6cd5\u544a\u77e5\u4ed6\u5bb5\u5173\u7edf\u9886\u4fbf\u662f\u4ed6\u6b64\u6765\u8981\u627e\u7684\u51e4\u5ae3\u7ea2\u3002\u4e5f\u5b9e\u5728\u662f\u4ef6 \u5927\u5927\u7684\u61be\u4e8b\u3002 \u3000\u3000\u4f46\u6700\u4f7f\u4ed6\u65e0\u6cd5\u7406\u89e3\u7684\uff0c\u662f\u597d\u4e0d\u5bb9\u6613\u8fde\u95ef\u4e94\u5173\u8fdb\u5165\u62db\u8d24\u9986\u540e\uff0c\u7adf\u843d\u5f97\u5982\u6b64\u4e0b\u573a\uff0c\u5bf9\u65b9\u7a76\u7adf \u662f\u5b89\u7684\u4ec0\u4e48\u5fc3\u5462\uff1f \u3000\u3000\u4ed6\u56e0\u601d\u7eea\u8d8a\u6765\u8d8a\u4e71\uff0c\u7d22\u6027\u8d77\u8eab\u6765\u5230\u5929\u4e95\uff0c\u4e00\u8fb9\u8e31\u7740\u6b65\u5b50\uff0c\u4e00\u8fb9\u6b23\u8d4f\u9662\u4e2d\u7684\u5404\u79cd\u82b1\u6728\uff0c\u804a \u4ee5\u6563\u5fc3\u89e3\u95f7\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u513f\uff0c\u6797\u91d1\u5b9d\u63d0\u7740\u98df\u76d2\u56de\u6765\u4e86\u3002 \u3000\u3000\u5f00\u996d\u5c31\u5728\u9910\u5385\u3002 \u3000\u3000\u5f53\u6253\u5f00\u98df\u76d2\u4e4b\u540e\uff0c\u5cb3\u5c0f\u98de\u53d1\u73b0\u6797\u91d1\u5b9d\u7684\u996d\u83dc\u548c\u81ea\u5df1\u7684\u4e0d\u4e00\u6837\uff0c\u81ea\u5df1\u7684\u662f\u4e0a\u7b49\u996d\u83dc\uff0c\u6797\u91d1 \u5b9d\u7684\u2014\u4efd\uff0c\u5219\u662f\u666e\u901a\u7684\u3002 \u3000\u3000\u6797\u91d1\u5b9d\u672c\u6765\u8981\u628a\u81ea\u5df1\u7684\u996d\u83dc\u62ff\u5230\u81ea\u5df1\u623f\u91cc\u5403\uff0c\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u54b1\u4eec\u4e24\u4e2a\u5728\u4e00\u8d77 \u5403\uff01\u201d \u3000\u3000\u522b\u770b\u8fd9\u4e00\u53e5\u8bdd\uff0c\u5728\u6797\u91d1\u5b9d\u6765\u8bf4\uff0c\u5374\u662f\u7b2c\u4e00\u6b21\u542c\u5230\uff0c\u4ed6\u6123\u4e86\u6123\u9053\uff1a\u201c\u516c\u5b50\uff0c\u5c0f\u7684\u662f\u4e0b\u4eba\uff0c \u548c\u60a8\u8eab\u4efd\u5730\u4f4d\u4e0d\u540c\uff0c\u600e\u6562\u548c\u60a8\u5728\u4e00\u8d77\u7528\u996d\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u522b\u5ba2\u6c14\uff0c\u6211\u53eb\u4f60\u5728\u8fd9\u91cc\u5403\uff0c\u4f60\u5c31\u5728\u8fd9\u91cc\u5403\u3002\u201d \u3000\u3000\u6797\u91d1\u5b9d\u5f53\u771f\u53d7\u5ba0\u82e5\u60ca\uff0c\u56e0\u4e3a\u5728\u4ece\u524d\uff0c\u4f4f\u5728\u8fd9\u91cc\u7684\u8d35\u5bbe\uff0c\u4ece\u6ca1\u4e00\u4e2a\u5bf9\u4ed6\u5982\u6b64\u53e6\u773c\u770b\u5f85\u8fc7\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u4ed6\u4e5f\u5c31\u4e0d\u518d\u5ba2\u6c14\uff0c\u628a\u4e24\u4efd\u996d\u83dc\u4e00\u8d77\u6446\u4e0a\uff0c\u5bf9\u9762\u800c\u5750\u7528\u8d77\u9910\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u53c8\u9053\uff1a\u201c\u5148\u5403\u6211\u8fd9\u4e00\u4efd\uff0c\u4e0d\u591f\u65f6\u518d\u5403\u4f60\u90a3\u4e00\u4efd\uff0c\u6211\u7684\u996d\u91cf\u5c0f\uff0c\u8981\u5269\u522b\u5269\u6211\u7684\u3002\u201d \u3000\u3000\u6797\u91d1\u5b9d\u8d8a\u53d1\u611f\u52a8\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8fb9\u5403\u8fb9\u9053\uff1a\u201c\u6211\u7a76\u7adf\u8981\u5f85\u5728\u8fd9\u91cc\u591a\u4e45\uff0c\u4ed6\u4eec\u603b\u4e0d\u80fd\u4e0d\u95fb\u4e0d\u95ee\u5427\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u516c\u5b50\u8981\u8010\u5fc3\u7b49\u5f85\u3002\u7b49\u5230\u6709\u4e00\u5929\u9986\u4e3b\u53ec\u89c1\u65f6\uff0c\u90a3\u5c31\u5dee\u4e0d\u591a\u662f\u51fa\u5934\u7684\u673a\u4f1a\u5230 \u4e86\u3002\u201d \u3000\u3000\u8fd9\u201c\u9986\u4e3b\u53ec\u89c1\u201d\u56db\u5b57\uff0c\u5728\u5cb3\u5c0f\u98de\u542c\u6765\uff0c\u5b9e\u5728\u4e0d\u662f\u5473\u9053\uff0c\u5982\u679c\u5bf9\u65b9\u771f\u662f\u793c\u8d24\u4e0b\u58eb\uff0c\u5c31\u8be5\u4eb2 \u81ea\u5230\u5206\u9986\u6765\u4f9d\u793c\u62dc\u8bbf\uff0c\u600e\u53ef\u7528\u201c\u53ec \u3000\u3000\u89c1\u201d\u6765\u5bf9\u5f85\u8d35\u5bbe\u5462\uff1f \u3000\u3000\u4f46\u4ed6\u5e76\u672a\u5f62\u4e4b\u4e8e\u8272\uff0c\u518d\u95ee\u9053\uff1a\u201c\u5982\u679c\u9986\u4e3b\u4e0d\u53ec\u89c1\u5462\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u5c34\u5c2c\u53c8\u9053\uff1a\u201c\u8fd9\u662f\u4e0d\u53ef\u80fd\u7684\uff0c\u51e1\u662f\u8fdb\u4e86\u62db\u8d24\u9986\uff0c\u90fd\u662f\u6559\u4e3b\u51c6\u5907\u4efb\u7528\u7684\u4eba\uff0c\u53c8\u600e\u80fd \u767d\u767d\u4f9b\u517b\u5728\u8fd9\u91cc\u800c\u4e0d\u95fb\u4e0d\u95ee\uff0c\u6240\u4e0d\u540c\u7684\uff0c \u3000\u3000\u53ea\u662f\u65f6\u95f4\u65e9\u665a\u800c\u5df2\uff0c\u603b\u4e4b\uff0c\u516c\u5b50\u5fc5\u987b\u8981\u6709\u8010\u5fc3\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u65f6\u95f4\u6709\u65e9\u665a\uff0c\u90a3\u53c8\u662f\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u53ea\u8981\u53ec\u89c1\uff0c\u5fc5\u5b9a\u5206\u914d\u5de5\u4f5c\uff0c\u5982\u679c\u6ca1\u6709\u5de5\u4f5c\u53ef\u5206\u914d\uff0c\u5f53\u7136\u5c31\u8981\u665a\u4e00\u4e9b\u65f6\u95f4\u53ec \u89c1\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u518d\u591a\u95ee\u3002 \u3000\u3000\u7528\u8fc7\u9910\u540e\uff0c\u624d\u4e0d\u8fc7\u662f\u4e2d\u5348\uff0c\u4ee5\u5f80\u548c\u8881\u5c0f\u9e64\u3001\u4e01\u6d9b\u3001\u7518\u9716\u7b49\u4eba\u5728\u4e00\u8d77\uff0c\u4ece\u4e0d\u611f\u5230\u65f6\u95f4\u8fc7\u5f97 \u6162\uff0c\u800c\u73b0\u5728\u5462\uff0c\u65f6\u95f4\u8fd8\u771f\u4e0d\u597d\u6253\u53d1\u3002 \u3000\u3000\u4e0b\u5348\uff0c\u4ed6\u5728\u623f\u95f4\u5c0f\u7761\u4e86\u2014\u4f1a\u513f\uff0c\u9192\u6765\u540e\uff0c\u53ea\u6709\u518d\u5728\u5929\u4e95\u6e9c\u8fbe\uff0c \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u76f4\u89c9\u7684\u611f\u5230\u81ea\u5df1\u5df2\u6210\u4e86\u7b3c\u4e2d\u4e4b\u9e1f\uff0c\u4e95\u5e95\u4e4b\u86d9\uff0c\u4eba\u751f\u5982\u6b64\uff0c\u771f\u662f\u4e86\u65e0\u751f\u8da3\u3002 \u3000\u3000\u508d\u665a\uff0c\u8d81\u7740\u6797\u91d1\u5b9d\u53c8\u5230\u603b\u9986\u62ff\u996d\uff0c\u4ed6\u4e0d\u77e5\u4e0d\u89c9\u53c8\u5230\u4e86\u5927\u95e8\u5916\u3002 \u3000\u3000\u4ed6\u8c28\u5b88\u7740\u4e0d\u8fdc\u79bb\u5206\u9986\u4e09\u5341\u6b65\u7684\u89c4\u5b9a\uff0c\u5c31\u7ad9\u5728\u95e8\u53e3\uff0c\u53ea\u4e0d\u8fc7\u662f\u5411\u5916\u773a\u671b\u773a\u671b\u800c\u5df2\u3002 \u3000\u3000\u95e8\u524d\u5927\u7ea6\u4e94\u516d\u5341\u6b65\u5916\uff0c\u4fbf\u662f\u4e00\u6761\u7f8a\u80a0\u5c0f\u5f84\u3002 \u3000\u3000\u84e6\u5730\uff0c\u4ed6\u7684\u773c\u775b\u4e00\u4eae\uff0c\u53ea\u89c1\u5c0f\u5f84\u4e0a\u6b63\u6709\u4e00\u540d\u5a40\u5a1c\u591a\u59ff\u7684\u7ea2\u8863\u5973\u90ce\u5728\u5306\u5306\u8def\u8fc7\u3002 \u3000\u3000\u4ed6\u8ba4\u5f97\u51fa\u8fd9\u5973\u90ce\u6b63\u662f\u706b\u5173\u4e0a\u7684\u6843\u82b1\u3002 \u3000\u3000\u82e5\u5728\u4ee5\u5f80\uff0c\u4ed6\u5b9e\u5728\u4e0d\u60f3\u548c\u5979\u62db\u547c\uff0c\u4f46\u73b0\u5728\u5fc3\u60c5\u5bc2\u5bde\uff0c\u53c8\u900f\u7740\u597d\u5947\uff0c\u5982\u80fd\u548c\u5979\u8c08\u8c08\uff0c\u5012\u662f \u6c42\u4e4b\u4e0d\u5f97\u3002 \u3000\u3000\u521a\u8981\u51fa\u58f0\u547c\u53eb\uff0c\u6843\u82b1\u5374\u5df2\u53d1\u73b0\u4e86\u4ed6\uff0c\u81ea\u52a8\u7684\u5411\u8fd9\u8fb9\u5feb\u6b65\u800c\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u60f3\u8d77\u5728\u706b\u5173\u65f6\uff0c\u6843\u82b1\u66fe\u5bf9\u81ea\u5df1\u8868\u793a\u6bb7\u52e4\uff0c\u5e0c\u671b\u81ea\u5df1\u5c06\u6765\u80fd\u5e2e\u5979\u8c03\u6362\u4e00\u4e2a\u4f4d\u7f6e\uff0c\u5982 \u4eca\u5979\u4e00\u53d1\u73b0\u81ea\u5df1\u4fbf\u4e3b\u52a8\u5954\u6765\uff0c\u5f53\u7136\u662f \u3000\u3000\u4e3a\u7684\u90a3\u4ef6\u4e8b\uff0c\u4f46\u5979\u54ea\u91cc\u77e5\u9053\u81ea\u5df1\u73b0\u5728\u8fde\u672c\u8eab\u90fd\u4e00\u7b79\u83ab\u5c55\u5462\u3002 \u3000\u3000\u6843\u82b1\u50cf\u2014\u9635\u98ce\u4f3c\u7684\u5f88\u5feb\u6765\u5230\u8ddf\u524d\u3002 \u3000\u3000\u201c\u5cb3\u516c\u5b50\uff0c\u539f\u6765\u4f60\u88ab\u62db\u5f85\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u8fd9\u91cc\u662f\u62db\u8d24\u5206\u9986\uff0c\u6211\u5f53\u7136\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u201c\u6211\u662f\u8bf4\u62db\u8d24\u9986\u6709\u4e03\u5341\u4e8c\u5206\u9986\uff0c\u82e5\u5728\u5e73\u65f6\uff0c\u60f3\u627e\u5230\u4f60\u90fd\u4e0d\u5bb9\u6613\uff0c\u73b0\u5728\u5c45\u7136\u80fd\u5728\u65e0\u610f\u4e2d\u76f8 \u9047\uff0c\u5b9e\u5728\u662f\u592a\u5de7\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u4f1a\u6765\u5230\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u5949\u7edf\u9886\u4e4b\u547d\uff0c\u5230\u603b\u575b\u529e\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4e8b\u60c5\u529e\u597d\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u529e\u597d\u4e86\uff0c\u73b0\u5728\u6b63\u8981\u56de\u53bb\uff0c\u516c\u5b50\uff0c\u53ef\u4e0d\u53ef\u4ee5\u8ba9\u6211\u5230\u91cc\u9762\u5750\u5750\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u662f\u8981\u8d76\u56de\u53bb\u4e48\uff1f\u201d \u3000\u3000\u201c\u6ca1\u5173\u7cfb\uff0c\u6211\u53ef\u4ee5\u56de\u53bb\u665a\u4e00\u70b9\uff0c\u6211\u8eab\u4e0a\u6709\u901a\u884c\u8170\u724c\uff0c\u8def\u4e0a\u4e0d\u4f1a\u6709\u4eba\u62e6\u963b\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u968f\u6211\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u628a\u6843\u82b1\u62db\u5f85\u5728\u5ba2\u5385\u91cc\uff0c\u5e76\u4e3a\u5979\u6c8f\u4e86\u676f\u8336\u3002 \u3000\u3000\u6843\u82b1\u56db\u4e0b\u671b\u4e86\u4e00\u773c\u9053\uff1a\u201c\u4f60\u90a3\u4e09\u4f4d\u4f19\u4f34\u5462\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e0d\u6e05\u695a\uff0c\u6211\u548c\u4ed6\u4eec\uff0c\u6765\u5230\u8fd9\u91cc\u5c31\u5206\u624b\u4e86\u3002\u201d \u3000\u3000\u6843\u82b1\u53cc\u7738\u7728\u7728\u7684\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u8fd9\u4e9b\u623f\u5b50\u662f\u62db\u8d24\u9986\u7684\u5206\u9986\uff0c\u4ece\u6765\u6ca1\u673a\u4f1a\u8fdb\u6765\uff0c\u82e5\u4e0d\u662f\u4eca \u5929\u9047\u5230\u516c\u5b50\u628a\u6211\u5e26\u8fdb\u6765\uff0c\u8fd9\u6837\u4e00\u5e62\u5927\u623f\u5b50\uff0c\u96be\u9053\u53ea\u6709\u516c\u5b50\u4e00\u4e2a\u4eba\u4f4f\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5401\u53e3\u6c14\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4f4d\uff0c\u662f\u8fd9\u91cc\u7684\u9986\u50ee\u3002\u201d \u3000\u3000\u6843\u82b1\u5ffd\u7136\u4f4e\u4e0b\u4e86\u5934\uff0c\u5e26\u70b9\u7f9e\u7b54\u7b54\u7684\u6a21\u6837\u9053\uff1a\u201c\u5728\u706b\u5173\u65f6\uff0c\u6211\u66fe\u6c42\u8fc7\u516c\u5b50\u5e2e\u5fd9\uff0c\u4f60\u6ca1\u5fd8\u8bb0 \u5427\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u59d1\u5a18\u7684\u8bdd\uff0c\u6211\u5f53\u7136\u8bb0\u5f97\u3002\u201d \u3000\u3000\u6843\u82b1\u5a07\u9765\u4e0a\u6cdb\u8d77\u4e24\u6735\u7edb\u971e\uff0c\u8109\u8109\u77a5\u4e86\u5cb3\u5c0f\u98de\u4e00\u773c\u9053\uff1b\u201c\u90a3\u4e48\u516c\u5b50\u73b0\u5728\u5c31\u8be5\u5e2e\u5fd9\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u82e6\u7b11\u9053\uff1a\u201c\u6211\u73b0\u5728\u81ea\u987e\u4e0d\u6687\uff0c\u54ea\u80fd\u5e2e\u4e0a\u59d1\u5a18\u7684\u5fd9\uff1f\u201d \u3000\u3000\u6843\u82b1\u5934\u5782\u5f97\u66f4\u4f4e\uff1a\u201c\u4f60\u53ef\u4ee5\u8bbe\u6cd5\u628a\u6211\u8c03\u5230\u8fd9\u91cc\u6765\uff0c\u6211\u8d1f\u8d23\u670d\u4f8d\u4f60\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6b49\u7136\u4e00\u7b11\u9053\uff1a\u201c\u6843\u82b1\u59d1\u5a18\uff0c\u4f60\u628a\u6211\u770b\u5f97\u592a\u9ad8\u4e86\uff0c\u8fde\u6211\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u4f55\u53bb\u4f55\u4ece\uff0c\u53c8 \u600e\u80fd\u5e2e\u5f97\u4e0a\u4f60\u7684\u5fd9\uff1f\u201d \u3000\u3000\u6843\u82b1\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0e\u521d\u5230\u62db\u8d24\u9986\u7684\u4eba\u90fd\u662f\u8fd9\u6837\uff0c\u4f46\u4ee5\u516c\u5b50\u7684\u5e74\u7eaa\uff0c\u5728\u6587\u624d\u6b66\u529f\u65b9 \u9762\u80fd\u6709\u8fd9\u6837\u7684\u6210\u5c31\uff0c\u5df2\u7ecf\u79f0\u5f97\u4e0a\u662f\u7b2c\u4e00\u4eba\u4e86\uff0c\u5c06\u6765\u53d7\u5230\u6559\u4e3b\u7684\u91cd\u7528\uff0c\u90a3\u662f\u4e00\u5b9a\u7684\uff0c\u5230\u90a3\u65f6\u4f60 \u4e00\u5b9a\u53ef\u4ee5\u5e2e\u4e0a\u6211\u7684\u5fd9\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7f04\u9ed8\u4e86\u8bb8\u4e45\uff0c\u624d\u642d\u6c55\u7740\u95ee\u9053\uff1a\u201c\u59d1\u5a18\u5728\u706b\u5173\uff0c\u4e0d\u662f\u5f88\u597d\u4e48\uff0c\u4e3a\u4ec0\u4e48\u53c8\u60f3\u51cb\u79bb\u90a3 \u91cc\uff1f\u201d \u3000\u3000\u6843\u82b1\u7ea2\u7740\u8138\u9053\uff1a\u201c\u6211\u6709\u5f88\u591a\u7406\u7531\uff0c\u4e0d\u613f\u5728\u706b\u5173\uff0c\u53ea\u662f\u4e0d\u65b9\u4fbf\u5bf9\u516c\u5b50\u8bb2\u7f62\u4e86\uff0c\u5982\u679c\u516c\u5b50\u80fd \u628a\u6211\u8c03\u5230\u8fd9\u91cc\uff1f\u53ea\u6709\u6211\u4eec\u4e24\u4e2a\u4eba\uff0c\u4f4f\u8fd9\u6837\u4e00\u5e62\u5927\u623f\u5b50\uff0c\u90a3\u8be5\u591a\u597d\uff0c\u800c\u4e14\u6211\u670d\u4f8d\u4f60\uff0c\u4e00\u5b9a\u6bd4\u8fd9 \u91cc\u7684\u9986\u50ee\u5468\u5230\u5f97\u591a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u65e0\u5948\u7684\u6447\u6447\u5934\u9053\uff1a\u201c\u96be\u9053\u4f60\u8fd8\u4e0d\u660e\u767d\uff0c\u6211\u4e0d\u53ef\u80fd\u6c38\u8fdc\u4f4f\u5728\u8fd9\u91cc\uff0c\u6211\u8d70\u4e86\u4f60\u53c8\u600e\u4e48 \u529e\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u5c31\u8ddf\u7740\u4f60\u8d70\uff0c\u6211\u8981\u6c38\u8fdc\u7559\u5728\u4f60\u7684\u8eab\u8fb9\u3002\u201d \u3000\u3000\u201c\u4f60\u8ddf\u7740\u6211\u8d70\u4f1a\u6709\u4ec0\u4e48\u51fa\u606f\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u4f60\u6709\u51fa\u606f\uff0c\u6211\u5c31\u6709\u51fa\u606f\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5ffd\u7136\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u6211\u6709\u4e00\u4e8b\u4e0d\u89e3\uff0c\u59d1\u5a18\u7684\u6b66\u529f\u6bd4\u6211\u9ad8\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u53d7\u5230\u6559\u4e3b\u7684\u91cd \u7528\uff1f\u201d \u3000\u3000\u6843\u82b1\u6115\u7136\u9053\uff1a\u201c\u516c\u5b50\u8fd9\u8bdd\u4ece\u4f55\u8bf4\u8d77\uff0c\u6211\u867d\u7136\u4e5f\u5b66\u8fc7\u6b66\u529f\uff0c\u5374\u53c8\u600e\u80fd\u548c\u4f60\u76f8\u6bd4\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff0c\u201c\u90a3\u5929\u6211\u4eec\u56db\u4eba\u8fc7\u706b\u5173\u4e4b\u524d\uff0c\u4f60\u66fe\u5148\u8bd5\u8fc7\u4e00\u6b21\u7ed9\u6211\u4eec\u770b\uff0c\u4f60\u80fd\u95ef\u8fc7\u706b\u5173\uff0c\u4e14 \u5168\u8eab\u6beb\u65e0\u635f\u4f24\uff0c\u6b66\u529f\u600e\u8bf4\u4e0d\u9ad8\u5462\uff1f\u201d \u3000\u3000\u6843\u82b1\u4e0d\u89c9\u5a07\u7f9e\u4e00\u7b11\u9053\uff1a\u201c\u539f\u6765\u516c\u5b50\u6ca1\u770b\u51fa\uff0c\u90a3\u4e0d\u8fc7\u662f\u9a97\u4f60\u4eec\u7684\u3002\u201d \u3000\u3000\u201c\u9a97\u6211\u4eec\u7684\uff1f\u5f53\u65f6\u660e\u660e\u706b\u5173\u5df2\u7ecf\u5f00\u52a8\uff0e\u600e\u80fd\u9a97\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u706b\u5173\u5165\u53e3\u5904\uff0c\u6709\u4e00\u4e2a\u6d1e\u7a74\uff0c\u53ef\u4ee5\u76f4\u901a\u6211\u4eec\u6240\u4f4f\u7684\u77f3\u5ba4\uff0c\u4f60\u4eec\u8001\u8fdc\u89c2\u770b\uff0c\u53ea\u9053\u6211\u5df2\u95ef \u8fdb\u706b\u5173\uff0c\u5176\u5b9e\u6211\u662f\u8dc3\u8fdb\u90a3\u6d1e\u7a74\u91cc\u53bb\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u706b\u5173\u7184\u706d\u4e4b\u540e\uff0c\u4f60\u5374\u5728\u96a7\u9053\u5bf9\u9762\u51fa\u73b0\uff0c\u8fd9\u53c8\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u5c45\u4f4f\u7684\u77f3\u5ba4\uff0c\u53e6\u6709\u96a7\u9053\u53ef\u4ee5\u901a\u5230\u5bf9\u9762\uff0c\u6211\u8dc3\u8fdb\u6d1e\u7a74\u4e4b\u540e\uff0c\u7acb\u523b\u7531\u90a3\u96a7\u9053\u8dd1\u5230\u5bf9\u9762\uff0c \u8fd9\u4e0d\u5c31\u5728\u5bf9\u9762\u51fa\u73b0\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u539f\u6765\u662f\u8fd9\u4e48\u56de\u4e8b\uff0c\u4f60\u4e0d\u8bf4\u6211\u8fd8\u771f\u4ee5\u4e3a\u4f60\u7684\u8f7b\u529f\u6bd4\u6211\u8fd8\u8981\u9ad8\u51fa\u5f88\u591a\u3002\u201d \u3000\u3000\u201c\u5b9e\u5bf9\u516c\u5b50\u8bf4\uff0c\u95ef\u8fc7\u706b\u5173\u7684\uff0c\u867d\u7136\u6709\u51e0\u767e\u4eba\u4e4b\u591a\uff0c\u4f46\u771f\u6b63\u51ed\u672c\u9886\u901a\u8fc7\u7684\uff0c\u4e00\u5171\u4e5f\u4e0d\u5230\u4e09 \u4e94\u4e2a\u3002\u201d \u3000\u3000\u4e24\u4eba\u53c8\u8c08\u4e86\u4e00\u4f1a\u513f\uff0c\u5cb3\u5c0f\u98de\u62c5\u5fc3\u88ab\u6797\u91d1\u5b9d\u56de\u6765\u770b\u5230\uff0c\u53ea\u6709\u50ac\u4fc3\u6843\u82b1\u56de\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u628a\u5979\u9001\u51fa\u5927\u95e8\u5916\uff0c\u6843\u82b1\u8d70\u51fa\u8001\u8fdc\uff0c\u4f9d\u7136\u9891\u9891\u56de\u9996\u76f8\u671b\u3002 \u3000\u3000\u521a\u597d\uff0c\u6843\u82b1\u79bb\u5f00\u4e0d\u4e45\uff0c\u6797\u91d1\u5b9d\u4fbf\u62ff\u7740\u996d\u56de\u6765\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u4ecd\u5728\u5ba2\u5385\u7528\u9910\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u56fd\u4e0d\u6e05\u695a\u62db\u8d24\u5206\u9986\u662f\u5426\u53ef\u4ee5\u63a5\u5f85\u719f\u4eba\uff0c\u4fbf\u6545\u610f\u95ee\u9053\uff1a\u201c\u6211\u60f3\u627e\u51e0\u4f4d\u670b\u53cb\u6765\u805a\u805a\uff0c \u662f\u5426\u53ef\u4ee5\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u4f3c\u4e4e\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u516c\u5b50\u60f3\u627e\u4ec0\u4e48\u670b\u53cb\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u544a\u8bc9\u4f60\uff0c\u4eca\u5929\u548c\u6211\u540c\u6765\u7684\uff0c\u4e00\u5171\u56db\u4eba\uff0c\u6211\u5f88\u5e0c\u671b\u80fd\u628a\u4ed6\u4eec\u4e09\u4f4d\u627e\u5230 \u8fd9\u91cc\u6765\uff0c\u56e0\u4e3a\u6211\u4eec\u90fd\u662f\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u6797\u91d1\u5b9d\u6447\u5934\u82e6\u7b11\u9053\uff1b\u201c\u516c\u5b50\u65e2\u7136\u77e5\u9053\u8fd9\u91cc\u7684\u89c4\u5b9a\uff0c\u5c31\u8be5\u77e5\u9053\u4f60\u90a3\u4e09\u4f4d\u670b\u53cb\u4e5f\u4e0d\u80fd\u79bb\u5f00\u4ed6 \u4eec\u6240\u4f4f\u7684\u5206\u9986\u4e09\u5341\u6b65\uff0c\u53c8\u600e\u4e48\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u80fd\u5426\u6253\u542c\u51fa\u4ed6\u4eec\u4e09\u4eba\u4f4f\u5728\u54ea\u51e0\u53f7\u5206\u9986\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u8d8a\u53d1\u9762\u6709\u96be\u8272\u9053\uff1a\u201c\u4ed6\u4eec\u4e09\u4eba\u4f4f\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u53ea\u6709\u526f\u603b\u7ba1\u4ee5\u4e0a\u7684\u4eba\u624d\u80fd\u77e5\u9053\uff0c\u8fd9 \u79cd\u4e8b\u5c0f\u7684\u600e\u6562\u6253\u542c\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u91cc\u4e00\u52a8\uff0c\u53c8\u95ee\uff1a\u201c\u4e03\u5341\u4e8c\u5206\u9986\u91cc\uff0c\u6709\u6ca1\u6709\u5973\u9986\u61a7\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u554a\u4e86\u58f0\u9053\uff1a\u201c\u516c\u5b50\u4e3a\u4ec0\u4e48\u95ee\u8fd9\u4e2a\uff1f\u83ab\u975e\u4f60\u770b\u5c0f\u7684\u4e0d\u597d\uff0c\u60f3\u6362\u4e2a\u5973\u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u600e\u4e48\u60f3\u5230\u90a3\u91cc\u53bb\u4e86\uff0c\u6211\u4e0d\u8fc7\u968f\u4fbf\u95ee\u95ee\u3002\u201d \u3000\u3000\u6797\u91d1\u5b9d\u8fd9\u624d\u653e\u4e86\u5fc3\uff1a\u201c\u90a3\u6211\u5c31\u544a\u8bc9\u516c\u5b50\uff0c\u4e03\u5341\u4e8c\u5206\u9986\u91cc\uff1f\u6ca1\u6709\u4e00\u4e2a\u5973\u9986\u50ee\uff0c\u8fde\u603b\u9986\u90fd\u4e0d \u51c6\u6709\u5973\u4eba\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u89e3\u9053\uff1a\u201c\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u5c0f\u7684\u4e5f\u4e0d\u6e05\u695a\uff0c\u542c\u8bf4\u4e0d\u4e45\u524d\u6709\u4f4d\u5206\u9986\u7684\u5ba2\u4eba\u628a\u5973\u7684\u62db\u5230\u5206\u9986\u6765\uff0c\u7ed3\u679c\u2026\u2026\u201d \u3000\u3000\u201c\u7ed3\u679c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u90a3\u770b\u5b88\u5206\u9986\u7684\u9986\u4ffa\u88ab\u6253\u4e2a\u534a\u6b7b\u3002\u201d \u3000\u3000\u201c\u90a3\u4f4d\u8d35\u5ba2\u5462\uff1f\u201d \u3000\u3000\u201c\u4e5f\u88ab\u6253\u8fdb\u80b2\u5316\u57ce\u53d7\u5c3d\u82e6\u5211\uff0c\u6709\u7684\u8bf4\u5df2\u7ecf\u88ab\u6298\u78e8\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u80b2\u5316\u57ce\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u53ea\u77e5\u9053\u90a3\u91cc\u662f\u53d7\u7f6a\u7684\uff0c\u51e1\u662f\u72af\u4e86\u6559\u89c4\u6216\u8005\u4e0d\u5fe0\u4e8e\u6559\u4e3b\u4ee5\u53ca\u4f01\u56fe\u4e0d\u8f68\u7684\uff0c\u4e00\u5b9a\u4f1a\u6253 \u8fdb\u80b2\u5316\u57ce\u53d7\u6298\u78e8\uff0c\u7f6a\u8f7b\u7684\u4e5f\u8bb8\u5c06\u6765\u8fd8\u6709\u51fa\u6765\u7684\u673a\u4f1a\uff0c\u7f6a\u91cd\u7684\u5c31\u8981\u6298\u78e8\u5230\u6b7b\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u987f\u65f6\u5fc3\u5934\u731b\u9707\uff0c\u5927\u611f\u4e0d\u5b89\u8d77\u6765\uff0c\u4e07\u4e00\u6843\u82b1\u65b9\u624d\u8fdb\u6765\u7684\u4e8b\u88ab\u4eba\u53d1\u73b0\uff0c\u5c82\u4e0d\u9a6c\u4e0a\u5c31\u8981 \u5927\u7978\u4e34\u5934\uff1f \u3000\u3000\u597d\u5728\u4e0d\u4e45\u4e4b\u540e\uff0c\u4ed6\u7684\u5fc3\u60c5\u6e10\u6e10\u5e73\u9759\u4e0b\u6765\uff0c\u56e0\u4e3a\u65b9\u624d\u6843\u82b1\u6765\u65f6\uff0c\u5df2\u662f\u508d\u665a\uff0c\u53c8\u4e0d\u53ef\u80fd\u6709\u4ed6 \u4eba\u770b\u5230\uff0c\u4f55\u5fc5\u56e0\u800c\u81ea\u4e71\u5fc3\u7eea\u3002 \u3000\u3000\u591c\u665a\uff0c\u5cb3\u5c0f\u98de\u8eba\u5728\u5e8a\u4e0a\uff0c\u4f9d\u7136\u662f\u8f97\u8f6c\u96be\u4ee5\u6210\u7720\u3002 \u3000\u3000\u4f4f\u5728\u8fd9\u5e62\u5206\u9986\u91cc\uff0c\u504c\u5927\u5ead\u9662\uff0c\u53ea\u6709\u4e24\u4eba\uff0c\u5b9e\u5728\u9759\u5f97\u5f88\uff0c\u7b80\u76f4\u9759\u5f97\u53ef\u6015\u3002 \u3000\u3000\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5c31\u5728\u4ed6\u5373\u5c06\u6726\u80e7\u5165\u7761\u65f6\uff0c\u5ffd\u542c\u7a97\u6237\u53d1\u51fa\u8f7b\u5fae\u7684\u8863\u8882\u98d8\u98ce\u4e4b\u58f0\uff0c\u63a5\u7740\u7a97\u6237 \u8f7b\u8f7b\u54cd\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u609a\u7136\u7531\u5e8a\u4e0a\u5750\u8d77\uff0c\u5306\u5306\u7a7f\u597d\u8863\u670d\uff0c\u7531\u7a97\u9699\u5411\u5916\u671b\u53bb\uff0c\u6708\u5149\u4e0b\uff0c\u7167\u89c1\u897f\u53a2\u5c4b\u810a\u4e0a\uff0c \u8d6b\u7136\u6709\u6761\u4eba\u5f71\u5728\u90a3\u91cc\u6643\u52a8\u3002 \u3000\u3000\u5206\u9986\u91cc\u53ea\u4f4f\u7740\u4ed6\u548c\u6797\u91d1\u5b9d\u4e24\u4eba\uff0c\u4f46\u6797\u91d1\u5b9d\u7eb5\u7136\u4e5f\u4e60\u8fc7\u6b66\u529f\uff0c\u5374\u7edd\u5bf9\u4e0d\u4f1a\u6709\u5982\u6b64\u9ad8\u8d85\u7684\u8eab \u624b\uff0c\u800c\u4e14\u4ed6\u4e5f\u4e0d\u53ef\u80fd\u6709\u8fd9\u79cd\u884c\u52a8\u3002 \u3000\u3000\u5982\u6b64\u770b\u6765\uff0c\u8fd9\u4eba\u8be5\u662f\u8881\u5c0f\u9e64\u4e86\uff0c\u56e0\u4e3a\u4ece\u6b64\u4eba\u7684\u884c\u52a8\u770b\u6765\uff0c\u5bf9\u81ea\u5df1\u7edd\u65e0\u6076\u610f\uff0e\u5176\u7528\u610f\u4e0d\u5916 \u662f\u628a\u81ea\u5df1\u5f15\u51fa\u53bb\u5728\u6697\u4e2d\u4f1a\u9762\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u4ed6\u968f\u5373\u4e5f\u7a7f\u7a97\u800c\u51fa\u3002 \u3000\u3000\u5f53\u4ed6\u6765\u5230\u5929\u4e95\uff0c\u90a3\u5c4b\u810a\u4e0a\u7684\u4eba\u5f71\uff0c\u7acb\u523b\u5411\u4ed6\u62db\u4e86\u62db\u624b\uff0c\u7136\u540e\u817e\u8eab\u98d8\u843d\u5899\u5916\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u968f\u4e4b\u7531\u5c4b\u9876\u63a0\u51fa\u5899\u5916\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4eba\u8eab\u7740\u84dd\u8863\u6b63\u7ad9\u5728\u5899\u811a\u8fb9\u7684\u836b\u5f71\u4e0b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6765\u5230\u8ddf\u524d\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u5f53\u4ed6\u770b\u6e05\u90a3\u4eba\u9762\u8c8c\uff0c\u8fc7\u5ea6\u7684\u60ca\u559c\uff0c\u7adf\u4f7f\u4ed6\u5446\u4e86\u4e00\u5446\uff0c\u63a5\u7740 \u614c\u5fd9\u8dea\u5012\u5728\u5730\uff0c\u5931\u58f0\u53eb\u9053\uff0c\u201c\u5b69\u513f\u62dc\u89c1\u7236\u4eb2\uff01\u201d \u3000\u3000\u5f53\u771f\u4ed6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\uff0c\u8fd9\u84dd\u8863\u4eba\u7adf\u662f\u4ed6\u9614\u522b\u4e09\u5e74\u6773\u65e0\u97f3\u8baf\u904d\u5bfb\u4e0d\u9047\u7684\u7236\u4eb2\u3002 \u3000\u3000\u90a3\u84dd\u8863\u4eba\u4e5f\u6709\u7740\u65e0\u6bd4\u7684\u6fc0\u52a8\uff0c\u6025\u6025\u6276\u8d77\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5feb\u8d77\u6765\uff0c\u544a\u8bc9\u6211\uff0c\u8fd9\u4e09\u5e74\u591a\uff0c\u4f60\u90fd \u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u7236\u5b50\u91cd\u9022\uff0c\u5728\u8fd9\u5239\u90a3\uff0c\u5cb3\u5c0f\u98de\u65e9\u5df2\u6cea\u6c34\u76c8\u7738\uff0c\u62bd\u564e\u4e86\u597d\u4e00\u9635\uff0c\u624d\u62ed\u53bb\u6cea\u6c34\u9053\uff1b\u201c\u5b69\u513f\u662f \u88ab\u4e00\u4f4d\u5e90\u4f2f\u4f2f\u6536\u7559\uff0c\u53c8\u8ddf\u4ed6\u4e60\u827a\u4e09\u5e74\uff0c\u53e6\u5916\u53c8\u968f\u4ed6\u8bfb\u4e86\u4e0d\u5c11\u4e66\uff0c\u4ed6\u8001\u4eba\u5bb6\u5f85\u5b69\u513f\u53ef\u8c13\u6069\u91cd\u5982 \u5c71\uff01\u201d \u3000\u3000\u84dd\u8863\u4eba\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u90a3\u4f4d\u5e90\u4f2f\u4f2f\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u4ed6\u662f\u4e3a\u4ec0\u4e48\u6536\u7559\u4f60\u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5e90\u4f2f\u4f2f\u53eb\u5e90\u4e91\uff0c\u5c31\u4f4f\u5728\u72ec\u79c0\u5cf0\u4e0b\uff0c\u90a3\u5929\u5b69\u513f\u5728\u72ec\u79c0\u5cf0\u4e0b\u4e45\u7b49\u65e0\u4eba\u6765\u63a5\uff0c\u540e \u6765\u88ab\u5e90\u4f2f\u4f2f\u7684\u5f1f\u5b50\u8881\u5927\u54e5\u770b\u5230\uff0c\u4fbf\u628a\u6211\u5e26\u5230\u5e90\u4f2f\u4f2f\u90a3\u91cc\u3002\u201d \u3000\u3000\u84dd\u8863\u4eba\u5931\u58f0\u9053\uff1a\u201c\u539f\u6765\u662f\u5723\u624b\u4e66\u751f\u5e90\u5927\u4fa0\uff1f\u4ed6\u5df2\u606f\u9690\u6c5f\u6e56\u591a\u5e74\uff0c\u60f3\u4e0d\u5230\u7adf\u662f\u9690\u5c45\u4f4f\u5728\u72ec \u79c0\u5cf0\u4e0b\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1b\u201c\u7236\u4eb2\u53ef\u8ba4\u8bc6\u4ed6\uff1f\u201d \u3000\u3000\u84dd\u8863\u4eba\u9053\uff1b\u201c\u4ed6\u5728\u6b66\u6797\uff0c\u5f53\u5e74\u5927\u540d\u9f0e\u9f0e\uff0c\u4e3a\u7236\u65e9\u5c31\u60f3\u62dc\u8bc6\u4ed6\uff0c\u53ef\u60dc\u59cb\u7ec8\u6ca1\u6709\u673a\u4f1a\u3002\u201d \u3000\u3000\u4ed6\u987f\u4e86\u4e00\u987f\u53c8\u9053\uff1a\u201c\u5176\u5b9e\u5e76\u975e\u6ca1\u6709\u673a\u4f1a\uff0c\u800c\u662f\u56e0\u4ed6\u6bd4\u6211\u5e74\u957f\u5341\u591a\u5c81\uff0c\u7b49\u6211\u5728\u6b66\u6797\u4e2d\u7a0d\u7a0d \u6df7\u51fa\u4e00\u70b9\u540d\u58f0\u65f6\uff0c\u4ed6\u5374\u5df2\u7ecf\u5c01\u5251\u5f52\u9690\u4e86\u3002\u201d \u3000\u3000\u84dd\u8863\u4eba\u987f\u611f\u4e00\u9635\u6b49\u7136\uff0c\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u63d0\u8d77\u8fd9\u4e8b\uff0c\u4e3a\u7236\u5b9e\u5728\u5bf9\u4e0d\u4f4f\u4f60\uff0c\u56e0\u4e3a\u4e3a\u7236\u4e8b\u5148\u65e9 \u548c\u90a3\u4eba\u7ea6\u597d\uff0c\u8981\u4ed6\u6309\u65f6\u5230\u72ec\u79c0\u5cf0\u63a5\u4f60\uff0c\u8c01\u60f3\u5f53\u6211\u548c\u4f60\u5a18\u8fdb\u5165\u2018\u5929\u8c37\u2019\u4e4b\u540e\uff0c\u624d\u77e5\u9053\u90a3\u4eba\u5df2\u88ab \u2018\u7687\u5143\u6559\u4e3b\u2019\u6253\u5165\u80b2\u5316\u57ce\u2026\u2026\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u62a2\u7740\u622a\u4f4f\u8bdd\u95ee\u9053\uff1a\u201c\u5929\u8c37\u5728\u54ea\u91cc\uff1f\u7687\u5143\u6559\u4e3b\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u84dd\u8863\u4eba\u9053\uff1b\u201c\u8fd9\u7247\u6570\u5341\u91cc\u65b9\u5706\u7684\u8c37\u5730\u5c31\u53eb\u5929\u8c37\uff0c\u7687\u5143\u6559\u4e3b\u5c31\u662f\u8fd9\u91cc\u7684\u6559\u4e3b\uff0c\u4f60\u7531\u8c37\u5916\u8fde \u95ef\u4e94\u5173\u6765\u5230\u8fd9\u91cc\uff0c\u96be\u9053\u6ca1\u542c\u8bf4\u8fc7\u8fd9\u91cc\u81f3\u9ad8\u65e0\u4e0a\u7684\u7edf\u6cbb\u8005\u5c31\u662f\u6559\u4e3b\uff1f\u201d \u3000\u3000\u201c\u5b69\u513f\u53ea\u542c\u8bf4\u8fc7\u6559\u4e3b\uff0c\u5e76\u6ca1\u542c\u8bf4\u8fc7\u7687\u5143\u6559\u4e3b\u3002\u201d \u3000\u3000\u201c\u6240\u8c13\u7687\u5143\u6559\uff0c\u5168\u79f0\u8be5\u662f\u2018\u7687\u5929\u5143\u771f\u5723\u6559\u2019\uff0c\u662f\u7531\u6559\u4e3b\u4e00\u624b\u521b\u7acb\u7684\uff0c\u7b80\u79f0\u2018\u7687\u5143\u6559\u2019\uff0c \u6240\u4ee5\u6559\u4e3b\u4e5f\u5c31\u662f\u2018\u7687\u5143\u6559\u4e3b\u2019\u201d\u3002 \u3000\u3000\u201c\u5f53\u7236\u4eb2\u5f97\u77e5\u90a3\u4f4d\u63a5\u5b69\u513f\u7684\u4eba\u5df2\u88ab\u6559\u4e3b\u6253\u5165\u80b2\u5316\u57ce\u4e4b\u540e\u5462\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6700\u62c5\u5fc3\u7684\u5c31\u662f\u4f60\u7684\u4e0b\u843d\uff0c\u5f53\u5e74\u6211\u5728\u8c37\u5916\u6551\u4e86\u2018\u592a\u767d\u53cc\u96c4\u2019\u4e01\u6d9b\u548c\u7518\u9716\u4e00\u547d\uff0c\u5f53\u65f6 \u5c31\u4ea4\u7ed9\u4ed6\u4eec\u4e00\u5757\u7389\u4f69\uff0c\u8981\u4ed6\u4eec\u8bbe\u6cd5\u627e\u4f60\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5b69\u513f\u6b63\u662f\u7531\u4e94\u53f0\u5c71\u897f\u6765\u7684\u8def\u4e0a\u548c\u4ed6\u4eec\u76f8\u9047\uff0c\u8fd9\u6b21\u80fd\u8fdb\u5165\u5929\u8c37\uff0c\u4e5f\u662f\u4ed6\u4eec\u4e24\u4f4d\u5e26 \u8def\u7684\uff0c\u53ef\u662f\u7236\u4eb2\u53c8\u600e\u77e5\u5b69\u513f\u5df2\u6765\u5230\u8fd9\u91cc\u5462\uff1f\u201d \u3000\u3000\u201c\u4e3a\u7236\u56e0\u4e3a\u770b\u5230\u4f60\u4eec\u56db\u4eba\u8fde\u95ef\u4e94\u5173\u5df2\u6765\u5230\u62db\u8d24\u9986\u7684\u540d\u5355\uff0c\u53d1\u73b0\u5176\u4e2d\u6709\u4e01\u6d9b\u548c\u7518\u9716\u4e24\u4eba\uff0c \u53c8\u53d1\u73b0\u5176\u4e2d\u6709\u4e2a\u5341\u4e09\u56db\u5c81\u7684\u5b69\u5b50\u53eb\u5cb3\u5c0f\u98de\uff0c\u5c31\u65ad\u5b9a\u53ef\u80fd\u662f\u4f60\uff0c\u540e\u6765\u53c8\u77e5\u9053\u4f60\u88ab\u5206\u914d\u5230\u4e09\u5341\u516d \u53f7\u5206\u9986\uff0c\u6240\u4ee5\u624d\u51b3\u5b9a\u5728\u591c\u95f4\u5077\u5077\u6765\u770b\u4f60\uff0c\u679c\u7136\u4f60\u6b63\u662f\u98de\u513f\uff0c\u8fd9\u5cb3\u5c0f\u98de\u7684\u59d3\u540d\uff0c\u662f\u522b\u4eba\u4e3a\u4f60\u53d6 \u7684\uff0c\u8fd8\u662f\u81ea\u5df1\u53d6\u7684\uff1f\u201d \u3000\u3000\u201c\u662f\u5e90\u4f2f\u4f2f\u4e3a\u5b69\u513f\u53d6\u7684\uff0c\u56e0\u5b69\u513f\u5f53\u65f6\u53ea\u77e5\u9053\u81ea\u5df1\u53eb\u98de\u513f\uff0c\u5e90\u4f2f\u4f2f\u4fbf\u628a\u5b69\u513f\u4ee5\u5c0f\u98de\u4e3a\u540d\uff0c \u7531\u98de\u60f3\u5230\u4e86\u5cb3\u6b66\u7a46\u738b\uff0c\u4fbf\u53c8\u4ee5\u5cb3\u4e3a\u59d3\u3002\u201d \u3000\u3000\u84dd\u8863\u4eba\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u540d\u5b57\u53d6\u5f97\u5f88\u597d\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u60c5\u4e0d\u81ea\u7981\u95ee\u9053\uff1b\u201c\u53ef\u662f\u5b69\u513f\u7a76\u7adf\u59d3\u4ec0\u4e48\uff0c\u7236\u4eb2\u73b0\u5728\u603b\u8be5\u544a\u8bc9\u5b69\u513f\u4e86\uff0c\u8fd8\u6709\u4f60\u8001\u4eba \u5bb6\u7684\u540d\u8bb3\uff0c\u5b69\u513f\u4e5f\u5fc5\u987b\u77e5\u9053\uff1f\u201d \u3000\u3000\u84dd\u8863\u4eba\u4f3c\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u72b9\u8c6b\u4e86\u534a\u664c\u624d\u9053\uff1a\u201c\u597d\uff0c\u4e3a\u7236\u544a\u8bc9\u4f60\uff0c\u6211\u53eb\u82b1\u7389\u9e9f\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fd9\u9053\uff1a\u201c\u90a3\u4e48\u5b69\u513f\u4ece\u73b0\u5728\u8d77\uff0c\u4e5f\u8be5\u6539\u540d\u53eb\u82b1\u5c0f\u98de\u4e86\uff1f\u201d \u3000\u3000\u5c82\u77e5\u82b1\u7389\u9e9f\u5374\u6447\u6447\u5934\uff0c\u8bed\u6c14\u90d1\u91cd\u7684\u9053\uff1a\u201c\u4e0d\u53ef\u3002\u4f60\u5fc5\u987b\u4ecd\u53eb\u5cb3\u5c0f\u98de\uff0c\u800c\u4e14\u4ee5\u540e\u82e5\u6709\u673a\u4f1a \u5728\u4eba\u524d\u548c\u6211\u76f8\u9047\uff0c\u4e5f\u5fc5\u987b\u88c5\u505a\u5f7c\u6b64\u5e76\u4e0d\u76f8\u8bc6\uff0c\u7edd\u5bf9\u4e0d\u53ef\u8ba9\u4eba\u770b\u51fa\u4f60\u6211\u662f\u7236\u5b50\u5173\u7cfb\uff0c\u9047\u89c1\u4f60\u6bcd \u4eb2\u4e5f\u662f\u2014\u6837\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6014\u4e86\u6014\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u795e\u8272\u9aa4\u7136\u51dd\u91cd\u9053\uff1a\u201c\u4e3a\u7236\u662f\u8feb\u4e0d\u5f97\u5df2\uff0c\u81f3\u4e8e\u4e3a\u4ec0\u4e48\uff0c\u6682\u65f6\u4f60\u4e5f\u7528\u4e0d\u7740\u591a\u95ee\uff0c\u65e5\u540e \u81ea\u7136\u660e\u767d\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9ed8\u4e86\u4e00\u9ed8\uff0c\u642d\u8baa\u7740\u518d\u95ee\u9053\uff1a\u201c\u83ab\u975e\u7236\u4eb2\u73b0\u5728\u4e5f\u505a\u4e86\u6559\u4e3b\u7684\u5c5e\u4e0b\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u51e1\u662f\u8fdb\u5165\u5929\u8c37\u7684\uff0c\u53c8\u6709\u8c01\u80fd\u4e0d\u4e3a\u6559\u4e3b\u6548\u529b\uff1f\u201d \u3000\u3000\u201c\u7236\u4eb2\u4e3a\u4ec0\u4e48\u4e5f\u8981\u8fdb\u5165\u5929\u8c37\u5462\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u4e5f\u662f\u8feb\u4e0d\u5f97\u5df2\u3002\u201d \u3000\u3000\u201c\u4f60\u8001\u4eba\u5bb6\u53ef\u5426\u544a\u77e5\u5b69\u513f\u76ee\u524d\u5728\u7687\u5143\u6559\u62c5\u4efb\u4f55\u804c\uff1f\u201d \u3000\u3000\u201c\u4f60\u53ef\u4ee5\u731c\u5f97\u51fa\uff0c\u4e3a\u7236\u65e2\u80fd\u770b\u5230\u4f60\u4eec\u56db\u4eba\u7684\u540d\u5355\uff0c\u5f53\u7136\u662f\u5728\u62db\u8d24\u9986\u4efb\u804c\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u4f60\u8001\u4eba\u5bb6\u662f\u62db\u8d24\u9986\u7684\u9986\u4e3b\uff1f\u201d \u3000\u3000\u201c\u62db\u8d24\u9986\u4e3b\u5728\u7687\u5143\u6559\u4e2d\u8eab\u4efd\u5730\u4f4d\u6781\u4e3a\u5d07\u9ad8\uff0c\u4e3a\u7236\u8fdb\u5165\u5929\u8c37\u4e0d\u5230\u4e09\u5e74\uff0c\u600e\u53ef\u80fd\u53d7\u5230\u6559\u4e3b\u5982 \u6b64\u91cd\u7528\uff0c\u4e0d\u8fc7\uff0c\u5728\u62db\u8d24\u9986\u603b\u9986\uff0e\u4e3a\u7236\u603b\u8fd8\u6709\u4e9b\u5206\u91cf\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6697\u9053\uff1a\u201c\u83ab\u975e\u7236\u4eb2\u662f\u62db\u8d24\u9986\u603b\u9986\u7684\u603b\u7ba1\uff1f\u2026\u2026\u201d \u3000\u3000\u4f46\u4ed6\u5374\u53c8\u4e0d\u4fbf\u518d\u95ee\u3002 \u3000\u3000\u53ea\u542c\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u90a3\u56db\u4eba\u540d\u5355\u4e2d\u7684\u8881\u5c0f\u9e64\uff0c\u662f\u5426\u5c31\u662f\u5723\u624b\u4e66\u751f\u5e90\u5927\u4fa0\u7684\u9ad8\u8db3\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c0f\u9e64\u5927\u54e5\u5f85\u5b69\u513f\u4eb2\u82e5\u5144\u5f1f\uff0c\u5c06\u6765\u9986\u4e3b\u53ec\u89c1\u65f6\uff0c\u8fd8\u671b\u7236\u4eb2\u80fd\u4e3a\u4ed6\u7f8e\u8a00 \u51e0\u53e5\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u6211\u4f1a\u7684\uff0c\u65f6\u95f4\u4e0d\u65e9\uff0c\u4e3a\u7236\u8be5\u8d70\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fd9\u9053\uff1a\u201c\u7236\u4eb2\u8fd8\u6ca1\u544a\u77e5\u5a18\u7684\u8fd1\u51b5\uff1f\u5979\u8001\u4eba\u5bb6\u597d\u4e48\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u51c4\u51c9\u4e00\u53f9\u9053\uff1a\u201c\u5979\u5f88\u597d\uff0c\u53ea\u662f\u5979\u76ee\u524d\u4e0d\u5728\u62db\u8d24\u9986\uff0c\u4e3a\u7236\u548c\u5979\u89c1\u9762\u7684\u673a\u4f1a\u4e0d\u591a\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u5a18\u7a76\u7adf\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u4e5f\u5728\u5929\u8c37\uff0c\u4e0d\u5fc5\u591a\u95ee\uff0c\u6211\u8be5\u9a6c\u4e0a\u56de\u53bb\u4e86\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u4e0d\u7b49\u5cb3\u5c0f\u98de\u518d\u8bf4\u4ec0\u4e48\uff0c\u8bf4\u5b8c\u8bdd\uff0c\u8f7b\u98ce\u98d2\u7136\uff0c\u5f88\u5feb\u4fbf\u6d88\u5931\u5728\u591c\u8272\u4e2d\u3002 \u3000\u3000\u6b21\u65e5\u5929\u4eae\uff0c\u6797\u91d1\u5b9d\u4e3a\u5cb3\u5c0f\u98de\u9001\u6765\u6dae\u6d17\u7528\u6c34\u540e\uff0c\u5373\u523b\u4fbf\u5230\u603b\u9986\u62ff\u996d\u3002 \u3000\u3000\u6797\u91d1\u5b9d\u6628\u65e5\u66fe\u5bf9\u5cb3\u5c0f\u98de\u8bf4\u8fc7\uff0c\u5f80\u8fd4\u603b\u9986\uff0c\u6700\u591a\u4e0d\u8d85\u8fc7\u534a\u4e2a\u65f6\u8fb0\uff0c\u8c01\u77e5\u4ed6\u8fd9\u4e00\u53bb\uff0c\u7adf\u7136\u8db3 \u8db3\u4e00\u4e2a\u65f6\u8fb0\u8fd8\u4e0d\u89c1\u56de\u6765\u3002 \u3000\u3000\u5978\u4e0d\u5bb9\u6613\u7b49\u5230\u6797\u91d1\u5b9d\u56de\u6765\uff0c\u5cb3\u5c0f\u98de\u7acb\u523b\u53d1\u73b0\u60c5\u5f62\u4e0d\u5bf9\u3002\u53ea\u89c1\u6797\u91d1\u5b9d\u4e24\u8fb9\u800c\u988a\uff0c\u6ee1\u662f\u8840\u75d5\uff0c \u8fde\u773c\u775b\u90fd\u662f\u7ea2\u80bf\u7684\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u91d1\u5b9d\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u83ab\u975e\u548c\u4eba\u6253\u67b6\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u987f\u65f6\u6cea\u6c34\u593a\u7736\u800c\u51fa\uff0c\u54ed\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u5bb3\u4e86\u5c0f\u7684\u4e86\uff0c\u8fde\u4f60\u81ea\u5df1\u4e5f\u2026\u2026\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6115\u7136\u95ee\u9053\uff1a\u201c\u5230\u5e95\u600e\u4e48\u56de\u4e8b\uff1f\u5feb\u8bf4\uff01\u201d \u3000\u3000\u522b\u770b\u6797\u91d1\u5b9d\u5df2\u7ecf\u5341\u4e03\u516b\u5c81\uff0c\u5374\u8fd8\u50cf\u4e2a\u5b69\u5b50\uff0c\u8fb9\u54ed\u8fb9\u9053\uff1a\u201c\u516c\u5b50\u6628\u5929\u508d\u665a\u8d81\u5c0f\u7684\u5230\u603b\u9986\u62ff \u996d\u65f6\uff0c\u662f\u5426\u66fe\u628a\u4e00\u4e2a\u5973\u7684\u5e26\u5230\u623f\u91cc\u6765 \u3000\u3000\u8fc7\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u662f\u4ec0\u4e48\u4eba\u53d1\u73b0\u7684\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u63e9\u62ed\u7740\u6cea\u6c34\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\u53d1\u73b0\u7684\uff0c\u5c0f\u7684\u4e0d\u6e05\u695a\uff0c\u4f46\u662f\u5374\u8ba9\u5434\u526f\u603b\u7ba1\u77e5\u9053\u4e86\uff0c\u5c0f \u7684\u521a\u624d\u5230\u603b\u9986\u62ff\u996d\u6240\u4ee5\u665a\u56de\u6765\uff0c\u5c31\u662f\u88ab\u5434\u526f\u603b\u7ba1\u53eb\u53bb\u62f7\u95ee\u3002\u201d \u3000\u3000\u201c\u4f60\u8138\u4e0a\u5c31\u662f\u88ab\u5434\u526f\u603b\u7ba1\u6253\u7684\uff1f\u201d \u3000\u3000\u201c\u8fd9\u8fd8\u7b97\u6253\u5f97\u5f88\u8f7b\uff0c\u82e5\u4e0d\u662f\u8fd8\u9700\u5c0f\u7684\u62ff\u996d\u56de\u6765\uff0c\u5c0f\u7684\u53ea\u6015\u5c31\u6ca1\u6709\u547d\u4e86\uff0c\u542c\u8bf4\u5f85\u4f1a\u513f\u4ed6\u8fd8 \u8981\u628a\u5c0f\u7684\u53eb\u53bb\u7ee7\u7eed\u62f7\u95ee\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u89c9\u6c14\u5f80\u4e0a\u51b2\uff0c\u540c\u65f6\u4e5f\u5bf9\u6797\u91d1\u5b9d\u4e3a\u8fd9\u4e8b\u53d7\u7d2f\u611f\u5230\u6b49\u610f\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c \u8fd9\u4f4d\u5434\u526f\u603b\u7ba1\u4e5f\u672a\u514d\u592a\u53ef\u6076\u4e86\uff0c\u54b1\u4eec\u9a6c\u4e0a\u5403\u996d\uff0c\u5403\u5b8c\u996d\u540e\u5e26\u6211\u53bb\u627e\u4ed6\uff01\u201d \u3000\u3000\u6797\u91d1\u5b9d\u6253\u4e86\u4e2a\u51b7\u98a4\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u8fd8\u6562\u53bb\u627e\u4ed6\uff0c\u5c31\u8fde\u4f60\u81ea\u5df1\u4e5f\u514d\u4e0d\u4e86\u53d7\u4ed6\u7684\u6574\u6cbb\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u522b\u6015\uff0c\u770b\u8c01\u6574\u6cbb\u5f97\u4e86\u8c01\uff0c\u6211\u5cb3\u5c0f\u98de\u5c31\u662f\u62da\u7740\u8111\u888b\u4e0d\u8981\uff0c\u4e5f\u8981\u66ff\u4f60\u51fa \u8fd9\u53e3\u6c14\uff01\u201d \u3000\u3000\u6797\u91d1\u5b9d\u5413\u5f97\u6253\u5fc3\u5e95\u76f4\u5192\u51b7\u6c14\uff0c\u9053\uff1a\u201c\u516c\u5b50\uff0c\u5343\u4e07\u4f7f\u4e0d\u5f97\uff0c\u5c0f\u7684\u53d7\u8d23\u7f5a\u662f\u5e94\u8be5\u7684\uff0c\u4f60\u5343\u4e07 \u5192\u72af\u4ed6\u4e0d\u5f97\uff0c\u5434\u526f\u603b\u7ba1\u624b\u6bb5\u6bd2\u8fa3\u662f\u51fa\u4e86\u540d\u7684\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u542c\u5f97\u4e24\u9053\u5251\u7709\u5012\u7ad6\uff1a\u201c\u6211\u5cb3\u5c0f\u98de\u504f\u8981\u770b\u770b\u4ed6\u624b\u6bb5\u6bd2\u8fa3\u5230\u4ec0\u4e48\u7a0b\u5ea6\uff0c\u5e26\u5973\u4eba\u8fdb\u6765 \u662f\u6211\u7684\u4e8b\uff0c\u6211\u81ea\u5df1\u505a\u81ea\u5df1\u627f\u62c5\uff0c\u4f60\u600e\u8bf4\u4f60\u53d7\u8d23\u7f5a\u662f\u5e94\u8be5\u7684\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u4e0d\u6562\u518d\u8bf4\u4ec0\u4e48\uff0c\u542b\u6cea\u5c06\u996d\u83dc\u62ff\u51fa\u6765\u6446\u597d\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6c14\u5f52\u6c14\uff0c\u4f46\u5374\u4e0d\u80fd\u56e0\u4e3a\u6c14\u5c31\u4e0d\u5403\u996d\uff0c\u90a3\u5c82\u4e0d\u8d8a\u53d1\u5403\u4e8f\uff0c\u4ed6\u8fb9\u5403\u8fb9\u9053\uff1b\u201c\u6211\u82e5\u4e0d\u4e3a \u4f60\u51fa\u8fd9\u53e3\u6c14\uff0c\u5c31\u7b49\u4e8e\u6211\u683d\u5728\u4ed6\u624b\u91cc\uff0c\u4ed6\u6253\u4f60\uff0c\u8ddf\u6253\u6211\u6839\u672c\u6ca1\u6709\u5206\u522b\uff0c\u4f60\u6015\u4ed6\u662f\u4f60\u7684\u4e8b\uff0c\u6211\u6015 \u4ed6\u662f\u4e3a\u4e86\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u5fc3\u77e5\u82e5\u81ea\u5df1\u518d\u5f00\u53e3\u7b49\u4e8e\u706b\u4e0a\u52a0\u6cb9\uff0c\u5e72\u8106\u4e00\u76f4\u4fdd\u6301\u7740\u4e0d\u542d\u58f0\u3002 \u3000\u3000\u521a\u521a\u628a\u996d\u5403\u5b8c\uff0c\u7a81\u89c1\u56db\u4e94\u540d\u9ed1\u8863\u5927\u6c49\uff0c\u6a2a\u7709\u7ad6\u76ee\u7531\u5927\u95e8\u5916\u95ef\u8fdb\u4e86\u5929\u4e95\u3002 \u3000\u3000\u63a5\u7740\u53c8\u8fdb\u6765\u4e00\u4eba\uff0c\u8d6b\u7136\u662f\u5434\u526f\u603b\u7ba1\u5434\u6709\u6743\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u6797\u91d1\u5b9d\u76ee\u77aa\u53e3\u5446\uff0c\u50cf\u662f\u5df2\u88ab\u5413\u6389\u4e86\u9b42\uff0c\u6839\u672c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u90a3\u5434\u526f\u603b\u7ba1\u5434\u6709\u6743\uff0c\u751f\u6765\u4e00\u5f20\u9a6c\u7751\uff0c\u516b\u5b57\u7709\uff0c\u4e09\u89d2\u773c\uff0c\u9e70\u9f3b\u8584\u5507\uff0c\u795e\u8272\u9634\u6674\u4e0d\u5b9a\u3002\u4ed6\u7684 \u8fd9\u526f\u957f\u76f8\uff0c\u4ee4\u5cb3\u5c0f\u98de\u4e00\u89c1\u5c31\u6709\u4e9b\u624b\u75d2\uff0c\u4f55\u51b5\u5cb3\u5c0f\u98de\u65e9\u5df2\u6012\u6c14\u653b\u5fc3\u3002 \u3000\u3000\u4f46\u5cb3\u5c0f\u98de\u5374\u51b3\u5b9a\u6682\u65f6\u4fdd\u6301\u4e0d\u52a8\u58f0\u8272\uff0c\u5426\u5219\uff0c\u4e00\u5f00\u59cb\u4e3b\u52a8\u628a\u4ed6\u6559\u8bad\u4e86\uff0c\u5c06\u6765\u8ffd\u7a76\u8d77\u6765\u81ea\u5df1 \u96be\u514d\u5c31\u6709\u4e9b\u7406\u5c48\u3002 \u3000\u3000\u8c01\u77e5\u5434\u6709\u6743\u4e5f\u6682\u65f6\u4e0d\u5f00\u95e8\uff0c\u5374\u7531\u5176\u5c0f\u4e00\u540d\u5200\u75a4\u8138\u5927\u6c49\u559d\u9053\uff1a\u201c\u6797\u91d1\u5b9d\uff0c\u4f60\u4ed6\uff0a\uff0a\uff0a\u8fd8\u4e0d \u51fa\u6765\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u4f53\u4f3c\u7b5b\u7ce0\uff0c\u8fde\u5634\u5507\u90fd\u5413\u5f97\u53d1\u4e86\u9752\uff0c\u53ef\u80fd\u817f\u90fd\u8f6f\u4e86\uff0c\u4f3c\u4e4e\u60f3\u8d70\u90fd\u8d70\u4e0d\u51fa\u53bb\u3002 \u3000\u3000\u5200\u75a4\u75d5\u4e24\u773c\u4e00\u77aa\uff0c\u518d\u559d\u9053\uff1a\u201c\u6797\u91d1\u5b9d\uff0c\u6211\u8bb2\u7684\u8bdd\u542c\u5230\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u731b\u6253\u7740\u98a4\uff0c\u7ec8\u4e8e\u58f0\u97f3\u6296\u52a8\u7740\u5f00\u4e86\u53e3\uff1a\u201c\u9ad8\u5927\u53d4\uff0c\u60a8\u2026\u2026\u60a8\u8981\u6211\u2026\u2026\u51fa\u53bb\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5200\u75a4\u8138\u54a7\u5634\u563f\u563f\u4e00\u7b11\u9053\uff1a\u201c\u5f53\u7136\u662f\u8981\u95ee\u4f60\u8bdd\uff0c\u518d\u4e0d\u51fa\u6765\uff0c\u90a3\u5c31\u522b\u602a\u8001\u5b50\u8fdb\u53bb\u62d6\u4e86\uff01\u201d \u3000\u3000\u53ea\u542c\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u53ea\u7ba1\u51fa\u53bb\uff0c\u770b\u4ed6\u4eec\u6562\u628a\u4f60\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u7ec8\u4e8e\u786c\u8d77\u5934\u76ae\u5411\u524d\u8d70\u51fa\u51e0\u6b65\uff0c\u5728\u5ba2\u5385\u95e8\u53e3\u505c\u4f4f\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u968f\u5373\u8ddf\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5200\u75a4\u8138\u4f3c\u662f\u6839\u672c\u4e0d\u5c51\u770b\u5cb3\u5c0f\u98de\u4e00\u773c\uff0c\u563f\u563f\u7b11\u7740\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\uff0c\u5df2\u7ecf\u4e24\u4e09\u5e74\u4e86\uff0c\u96be\u9053\u8fde \u4e0d\u51c6\u5973\u4eba\u8fdb\u6765\u7684\u89c4\u5b9a\u90fd\u4e0d\u77e5\u9053\uff1f\u4f60\u8fd9\u5c0f\u5b50\u53ef\u662f\u627e\u6b7b\u5417\uff1f\u201d \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u5341\u4e00\u56de\u3000\u6012\u72af\u6212\u5f8b \u3000\u3000\u6797\u91d1\u5b9d\u90a3\u91cc\u7b54\u5f97\u4e0a\u8bdd\uff0c\u8138\u8272\u7531\u9752\u53d8\u7eff\uff0c\u60c5\u4e0d\u81ea\u7981\u8f6c\u5934\u671b\u5411\u5cb3\u5c0f\u98de\u3002 \u3000\u3000\u5200\u75a4\u8138\u9634\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u4f60\u5f80\u54ea\u91cc\u770b\uff0c\u8c01\u4e5f\u6551\u4e0d\u4e86\u4f60\uff01\u201d \u3000\u3000\u4ed6\u8bdd\u521a\u8bf4\u5b8c\uff0c\u4f38\u624b\u4fbf\u5411\u6797\u91d1\u5b9d\u6293\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u628a\u5c06\u6797\u91d1\u5b9d\u62e8\u5230\u540e\u9762\uff0c\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c\u897f\uff01\u5c45\u7136\u5728\u6211\u9762\u524d\u653e\u8086\uff0c\u4f60\u4e0d\u8fc7\u62db\u8d24\u9986 \u4e00\u540d\u72d7\u817f\u5b50\uff0c\u592a\u65e0\u6cd5\u65e0\u5929\u4e86\uff01\u201d \u3000\u3000\u5200\u75a4\u8138\u4f38\u624b\u518d\u5411\u5cb3\u5c0f\u98de\u6293\u6765\uff0c\u4e00\u9762\u6c89\u58f0\u9053\uff1a\u201c\u4f60\u6562\u600e\u6837\uff1f\u201d \u3000\u3000\u4ed6\u7684\u6700\u540e\u4e00\u5b57\u5c1a\u672a\u51fa\u58f0\uff0c\u7d27\u63a5\u7740\u4e00\u58f0\u60e8\u53eb\uff0c\u76f4\u98de\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u7136\u540e\u50cf\u5929\u5916\u98de\u77f3\u822c\u5411\u540e\u6454 \u53bb\u3002 \u3000\u3000\u8fd8\u597d\uff0c\u82e5\u4e0d\u662f\u5434\u6709\u6743\u53ca\u65f6\u8dc3\u8eab\u63a5\u4f4f\uff0c\u5200\u75a4\u8138\u4e0d\u6b7b\u4e5f\u5fc5\u91cd\u4f24\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u6240\u6709\u7684\u4eba\uff0c\u5305\u62ec\u6797\u91d1\u5b9d\u5728\u5185\uff0c\u5374\u8c01\u90fd\u6ca1\u770b\u6e05\u5cb3\u5c0f\u98de\u662f\u600e\u6837\u51fa\u624b\u7684\u3002 \u3000\u3000\u5434\u6709\u6743\u7b49\u4eba\u867d\u7136\u77e5\u9053\u51e1\u662f\u80fd\u95ef\u8fc7\u4e94\u5173\u6765\u5230\u62db\u8d24\u9986\u7684\u4eba\u5fc5\u5b9a\u8eab\u624b\u4e0d\u51e1\uff0c\u4f46\u5374\u6ca1\u6599\u5230\u4e00\u4e2a\u5341 \u4e09\u56db\u5c81\u7684\u5b69\u7ae5\uff0c\u7adf\u6709\u5982\u6b64\u60ca\u4e16\u9a87\u4fd7\u7684\u6b66\u529f\u3002 \u3000\u3000\u5176\u5b9e\u5cb3\u5c0f\u98de\u8fde\u624b\u90fd\u6ca1\u52a8\uff0c\u53ea\u662f\u8f7b\u8f7b\u98de\u817f\u8e6c\u51fa\u4e00\u811a\u3002 \u3000\u3000\u4ed6\u77e5\u9053\uff0c\u82e5\u8e6c\u5f97\u8fc7\u91cd\uff0c\u5200\u75a4\u8138\u52bf\u5fc5\u5f53\u573a\u809a\u7834\u80a0\u6d41\uff0c\u4ed6\u4e3a\u4e86\u4e0d\u628a\u4e8b\u60c5\u95f9\u5927\uff0c\u624d\u51b3\u5b9a\u6682\u7559\u5200 \u75a4\u8138\u4e00\u6761\u72d7\u547d\u3002 \u3000\u3000\u5434\u6709\u6743\u653e\u4e0b\u5200\u75a4\u8138\uff0c\u5927\u6b65\u76f4\u51b2\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u5982\u4f55\u5fcd\u5f97\u4e0b\u8fd9\u53e3\u6c14\uff0c\u4e09\u89d2\u773c\u51f6\u5149\u66b4\u95ea\uff0c\u659c\u7784\u4e86\u51e0\u7784\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff0c\u5bb9 \u5f97\u4f60\u6492\u91ce\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6781\u529b\u9690\u5fcd\u7740\uff0c\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u6797\u91d1\u5b9d\u72af\u4e86\u4ec0\u4e48\u7f6a\u8fc7\uff0c\u4f60\u4eec\u8981\u8fd9\u6837\u5bf9\u4ed8\u4ed6\uff1f\u201d \u3000\u3000\u201c\u5206\u9986\u91cc\u8fdb\u6765\u5973\u4eba\uff0c\u96be\u9053\u4e0d\u662f\u4ed6\u7684\u7f6a\u8fc7\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u5728\u4e0b\u653e\u8fdb\u6765\u7684\uff0c\u4e0e\u4ed6\u65e0\u5e72\uff0c\u4f60\u4eec\u5c31\u8be5\u76f4\u63a5\u627e\u6211\uff01\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u628a\u5973\u4eba\u653e\u8fdb\u6765\uff1f\u201d \u3000\u3000\u201c\u5973\u4eba\u4e5f\u662f\u4eba\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u8ba9\u5979\u8fdb\u6765\uff1f\u201d \u3000\u3000\u201c\u4f60\u5e94\u5f53\u542c\u6797\u91d1\u5b9d\u544a\u8bc9\u8fc7\u8fd9\u91cc\u7684\u89c4\u5b9a\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4ed6\u662f\u5bf9\u5728\u4e0b\u8bb2\u8fc7\uff0c\u4f46\u8fd9\u79cd\u4e0d\u5408\u7406\u7684\u89c4\u5b9a\uff0c\u5728\u4e0b\u5374\u6ca1\u6709\u5fc5\u8981\u9075\u5b88\u3002\u201d \u3000\u3000\u5434\u6709\u6743\u4e24\u592a\u9633\u7a74\u6025\u5267\u62bd\u52a8\u4e86\u51e0\u4e0b\uff0c\u5927\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u5e74\u7eaa\u4e0d\u5927\uff0c\u5374\u5df2\u79c3\u5b50\u6253\u4f1e\u2014\u2014\u65e0 \u6cd5\u65e0\u5929\uff0c\u6211\u770b\u4f60\u662f\u778e\u5b50\u95fb\u81ed\u2014\u2014\u60f3\u627e\u6b7b\uff08\u5c4e\uff09\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u51c6\u5907\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5434\u6709\u6743\u95ea\u7535\u822c\u76f4\u5411\u5cb3\u5c0f\u98de\u5de6\u624b\u8155\u8109\u6293\u6765\uff0c\u4e00\u8fb9\u559d\u9053\uff1b\u201c\u5148\u968f\u672c\u526f\u603b\u7ba1\u5230\u603b\u9986\u53bb\uff0c\u518d\u6cbb\u4f60 \u7684\u7f6a\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5c82\u80fd\u5bb9\u4ed6\u6293\u4f4f\uff0c\u5de6\u624b\u4e00\u7ffb\uff0c\u53cd\u5411\u5bf9\u65b9\u8155\u8109\u4e0a\u6263\u4f4f\u3002\u5434\u6709\u6743\u6ca1\u6599\u5230\u5bf9\u65b9\u52a8\u4f5c\u5982\u6b64\u4e4b \u5feb\uff0c\u867d\u672a\u88ab\u6263\u4f4f\uff0c\u4f46\u5374\u88ab\u5cb3\u5c0f\u98de\u7684\u624b\u6307\u89e6\u53ca\u3002 \u3000\u3000\u4ed6\u53ea\u611f\u5bf9\u65b9\u7684\u6307\u5c16\uff0c\u7adf\u6709\u5982\u4e94\u628a\u94a2\u9525\uff0c\u867d\u7136\u8f7b\u8f7b\u4e00\u89e6\uff0c\u5374\u5df2\u75db\u5f7b\u5fc3\u80ba\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u51b7\u7b11\u9053\uff1b\u201c\u59d3\u5434\u7684\uff0c\u4f60\u82e5\u77e5\u8da3\uff0c\u5c31\u8d81\u65e9\u6eda\u56de\u53bb\uff0c\u5426\u5219\u522b\u602a\u6211\u5cb3\u5c0f\u98de\u5bf9\u4f60\u4e0d\u8d77\uff01\u201d \u3000\u3000\u5434\u6709\u6743\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u5c82\u80fd\u793a\u5f31\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1b\u201c\u8001\u5b50\u4eca\u5929\u82e5\u8fde\u4f60\u90fd\u5236\u670d\u4e0d\u4e86\uff0c\u5c31\u628a \u5434\u5b57\u8ba9\u4f60\u5012\u5199\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8038\u4e86\u8038\u7709\u9053\uff1b\u201c\u5728\u4e0b\u65e9\u5c31\u542c\u8bf4\u4f60\u8fd9\u72d7\u817f\u5b50\u4e0d\u662f\u4e1c\u897f\uff0c\u5982\u679c\u975e\u8981\u627e\u5012\u9709\u4e0d\u53ef\uff0c\u90a3\u6211 \u4e5f\u6b63\u597d\u53ef\u4ee5\u8d81\u8fd9\u673a\u4f1a\u6559\u8bad\u6559\u8bad\u4f60\uff0c\u4f60\u53ea\u7ba1\u8fc7\u6765\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u5434\u6709\u6743\u9661\u5730\u4e00\u4e2a\u864e\u8df3\uff0c\u53cc\u62f3\u9f50\u51fa\uff0c\u76f4\u5411\u5cb3\u5c0f\u98de\u9762\u95e8\u6363\u6765\uff0c\u4e0b\u9762\u540c\u65f6\u53c8\u8e22\u51fa\u4e00\u811a\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u4fa7\u8eab\uff0c\u8ba9\u8fc7\u6765\u52bf\uff0c\u53f3\u638c\u75be\u5411\u5434\u6709\u6743\u540e\u80cc\u62cd\u53bb\u3002 \u3000\u3000\u53ea\u542c\u5434\u6709\u6743\u4e00\u58f0\u95f7\u54fc\uff0c\u51cc\u7a7a\u76f4\u5411\u524d\u683d\u53bb\uff0c\u6b63\u597d\u4fef\u6454\u5728\u5ba2\u5385\u95e8\u69db\u4e0a\u3002 \u3000\u3000\u90a3\u95e8\u69db\u79bb\u5730\u8db3\u6709\u534a\u5c3a\u9ad8\uff0c\u6a2a\u9876\u5728\u5434\u6709\u6743\u5c0f\u8179\u4e0a\uff0c\u5176\u7ed3\u679c\u4e0d\u95ee\u53ef\u77e5\u3002 \u3000\u3000\u5434\u6709\u6743\u52c9\u5f3a\u722c\u4e86\u8d77\u6765\uff0c\u53cc\u624b\u6367\u7740\u5c0f\u8179\uff0c\u53ea\u987e\u54ac\u7259\u54a7\u5634\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4f59\u6012\u672a\u606f\uff0c\u8ddf\u7740\u5de6\u53f3\u5f00\u5f13\uff0c\u5288\u51fa\u4e24\u638c\u3002 \u3000\u3000\u987f\u65f6\uff0c\u5434\u6709\u6743\u53cc\u988a\u5df2\u76ae\u5f00\u8089\u7efd\uff0c\u8fde\u5634\u5df4\u548c\u9f3b\u5b50\u90fd\u51e0\u4e4e\u6253\u6b6a\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u518d\u6293\u4f4f\u5434\u6709\u6743\u8863\u9886\uff0c\u4e94\u6307\u4e00\u65cb\uff0c\u628a\u4eba\u65cb\u4e86\u4e2a\u5012\u8f6c\u4e00\u5708\uff0c\u7136\u540e\u98de\u8d77\u4e00\u811a\uff0c\u518d\u8e4b\u4e0a\u5bf9 \u65b9\u524d\u80f8\u3002 \u3000\u3000\u5434\u6709\u6743\u201c\u5662\u201d\u7684\u4e00\u58f0\uff0c\u968f\u5373\u5411\u5927\u95e8\u65b9\u5411\u5012\u51fa\u53bb\u3002 \u3000\u3000\u90a3\u51e0\u540d\u9ed1\u8863\u5927\u6c49\u9f50\u9f50\u8dc3\u8eab\u51c6\u5907\u628a\u5434\u6709\u6743\u63a5\u4f4f\uff0c\u5c82\u77e5\u5434\u6709\u6743\u5411\u540e\u5012\u649e\u7684\u51b2\u529b\u592a\u5927\uff0c\u90a3\u51e0\u4e2a \u9ed1\u8863\u5927\u6c49\u867d\u7136\u628a\u4eba\u63a5\u4f4f\uff0c\u5374\u90fd\u5168\u88ab\u649e\u5012\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8f7b\u8f7b\u677e\u677e\u7684\u51fa\u624b\u51e0\u62db\uff0c\u7adf\u628a\u5434\u6709\u6743\u6253\u5f97\u534a\u70b9\u8fd8\u624b\u4e4b\u529b\u90fd\u6ca1\u6709\uff0c\u600e\u4e0d\u4ee4\u5728\u573a\u6240\u6709\u7684 \u4eba\u90fd\u5927\u60ca\u5931\u8272\u3002 \u3000\u3000\u51e0\u540d\u9ed1\u8863\u5927\u6c49\u722c\u8d77\u8eab\u6765\uff0c\u518d\u628a\u5434\u6709\u6743\u6276\u8d77\uff0c\u5374\u90fd\u53ea\u80fd\u50f5\u5728\u90a3\u91cc\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u7b11\u9053\uff1a\u201c\u59d3\u5434\u7684\uff0c\u4f60\u4e0d\u662f\u8981\u628a\u5728\u4e0b\u62ff\u5230\u603b\u9986\u6cbb\u7f6a\u5417\uff0c\u6211\u770b\u4f60\u600e\u6837\u62ff\u6cd5\uff01\u201d \u3000\u3000\u5434\u6709\u6743\u72c2\u5598\u5982\u725b\uff0c\u5472\u7259\u54a7\u5634\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6709\u79cd\u5c31\u522b\u8dd1\uff0c\u5434\u67d0\u62ff\u4e0d\u4f4f\u4f60\uff0c\u603b\u8fd8\u6709\u4eba\u62ff\u5f97\u4f4f \u4f60\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6de1\u7136\u7b11\u9053\uff1b\u201c\u6211\u7b49\u7740\uff0c\u770b\u4f60\u4eec\u54ea\u4e2a\u6562\u6765\u62ff\uff01\u201d \u3000\u3000\u5176\u4e2d\u4e00\u540d\u9ed1\u8863\u5927\u6c49\u9053\uff1a\u201c\u526f\u603b\u7ba1\uff0c\u4f60\u522b\u8bf4\u4e86\uff0c\u5148\u56de\u53bb\u8981\u7d27\uff01\u201d \u3000\u3000\u4e8b\u5230\u5982\u4eca\uff0c\u5434\u6709\u6743\u9664\u4e86\u56de\u53bb\uff0c\u6839\u672c\u6ca1\u6709\u7b2c\u4e8c\u6761\u8def\u597d\u8d70\u3002 \u3000\u3000\u8c01\u77e5\u4ed6\u521a\u6b6a\u6b6a\u659c\u659c\u7684\u8d70\u51fa\u56db\u4e94\u6b65\uff0c\u4fbf\u5df2\u8eab\u4e0d\u7531\u5df1\u7684\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u90a3\u51e0\u540d\u5927\u6c49\uff0c\u53ea\u597d\u62ac\u5934\u7684\u62ac\u5934\uff0c\u62ac\u811a\u7684\u62ac\u811a\uff0c\u6324\u6210\u4e00\u5806\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u628a\u5434\u6709\u6743\u62ac\u51fa\u5927\u95e8\uff0c \u7136\u540e\u518d\u62ac\u7740\u5f80\u603b\u9986\u65b9\u5411\u800c\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u56de\u8fc7\u8eab\u6765\uff0c\u53ea\u89c1\u6797\u91d1\u5b9d\u4ecd\u76ee\u77aa\u53e3\u5446\u7684\u6123\u5728\u90a3\u91cc\u4e0d\u77e5\u6240\u63aa\u3002 \u3000\u3000\u201c\u6797\u91d1\u5b9d\uff0c\u4ed6\u4eec\u5df2\u7ecf\u8d70\u4e86\uff0c\u8fd8\u6015\u7684\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u8fd9\u624d\u6253\u4e86\u54c6\u55e6\uff0c\u5982\u68a6\u65b9\u9192\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6253\u4e86\u5434\u526f\u603b\u7ba1\uff0c\u5728\u4ed6\u6765\u8bf4\uff0c\u5f53\u7136\u662f\u5927\u5feb\u4eba\u5fc3\uff0c\u4f46\u4ed6\u5374\u4e0d\u80fd\u4e0d\u60f3\u5230\u7ee7\u4e4b\u800c\u6765\u7684\u5c06\u662f \u4ec0\u4e48\u540e\u679c\u3002 \u3000\u3000\u84e6\u5730\uff0c\u4ed6\u7ffb\u8eab\u8dea\u5012\u5728\u5730\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8fd9\u7978\u4e8b\u95ef\u5f97\u5927\u4e86\uff0c\u73b0\u5728\u60a8\u552f\u6709\u5feb\u5feb\u9003\u8d70\uff0c\u624d\u662f\u552f\u4e00 \u7684\u529e\u6cd5\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u62c9\u8d77\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u597d\u6c49\u505a\u4e8b\u597d\u6c49\u5f53\uff0c\u6211\u82e5\u6015\uff0c\u5c31\u7528\u4e0d\u7740\u6253\u59d3\u5434\u7684\u4e86\uff0c\u4f55\u51b5\u6211\u73b0\u5728 \u8eab\u5728\u5929\u8c37\uff0c\u53c8\u80fd\u9003\u5230\u54ea\u91cc\u53bb\uff0c\u800c\u4e14\u6211\u9003\u8d70\uff0c\u4f60\u53c8\u600e\u4e48\u529b\uff0c\uff1f\u201d \u3000\u3000\u201c\u516c\u5b50\u73b0\u5728\u5df2\u662f\u81ea\u8eab\u96be\u4fdd\uff0c\u6839\u672c\u5df2\u4e0d\u53ef\u80fd\u987e\u5f97\u4e86\u5c0f\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u653e\u5fc3\uff0c\u53ea\u8981\u6709\u6211\u5728\uff0c\u8c01\u4e5f\u522b\u60f3\u52a8\u4f60\u4e00\u6839\u6c57\u6bdb\u3002\u201d \u3000\u3000\u201c\u516c\u5b50\uff0c\u8fd8\u662f\u542c\u5c0f\u7684\u8bdd\uff0c\u9003\u8d70\u8981\u7d27\uff0c\u5929\u8c37\u8fd9\u6837\u5927\uff0c\u4e5f\u8bb8\u4ecd\u6709\u85cf\u8eab\u4e4b\u5730\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u518d\u7406\u4f1a\u6797\u91d1\u5b9d\uff0c\u7d22\u6027\u56de\u5230\u5ba2\u5385\uff0c\u81ea\u5df1\u5012\u4e86\u676f\u8336\uff0c\u5750\u4e0a\u6905\u5b50\uff0c\u6162\u6162\u559d\u4e86\u8d77\u6765\uff0c\u90a3 \u795e\u60c5\u770b\u6765\u8fd8\u5341\u5206\u6f47\u6d12\u3002 \u3000\u3000\u6797\u91d1\u5b9d\u8ddf\u4e86\u8fc7\u6765\u9053\uff1a\u201c\u516c\u5b50\uff0c\u60a8\u603b\u8981\u6709\u4e2a\u6253\u7b97\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u7684\u6253\u7b97\u5c31\u662f\u7b49\u7740\u770b\u770b\u7b2c\u4e8c\u6279\u53c8\u6709\u4ec0\u4e48\u4eba\u6765\uff0c\u82e5\u9986\u4e3b\u4eb2\u81ea\u6765\u4e86\uff0c\u6b63\u597d\u53ef\u4ee5 \u63d0\u524d\u89c1\u4ed6\u3002\u201d \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u4ee5\u9986\u4e3b\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u4e0d\u592a\u53ef\u80fd\u4eb2\u81ea\u6765\uff0c\u4e5f\u8bb8\u662f\u603b\u7ba1\u6765\u3002\u201d \u3000\u3000\u201c\u603b\u7ba1\u59d3\u4ec0\u4e48\uff1f\u53eb\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u603b\u7ba1\u59d3\u97e9\uff0c\u53eb\u97e9\u5fb7\u8d77\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u89c9\u5fc3\u5934\u4e00\u51c9\uff0c\u4ed6\u672c\u6765\u4ee5\u4e3a\u7236\u4eb2\u5fc5\u662f\u603b\u7ba1\uff0c\u5982\u4eca\u542c\u8bf4\u603b\u7ba1\u53e6\u6709\u5176\u4eba\uff0c\u90a3\u4e48\u7236\u4eb2\u5728 \u62db\u8d24\u9986\u7684\u804c\u4f4d\uff0c\u663e\u7136\u662f\u5fae\u4e0d\u8db3\u9053\u4e86\uff0c\u600e\u4e0d\u4ee4\u4ed6\u4e3a\u4e4b\u9eef\u7136\u3002 \u3000\u3000\u201c\u8fd9\u4f4d\u97e9\u603b\u7ba1\u7684\u4e3a\u4eba\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u97e9\u603b\u7ba1\u6bd4\u5434\u526f\u603b\u7ba1\u597d\u5f97\u591a\u4e86\uff0c\u4f46\u516c\u5b50\u95ef\u4e0b\u8fd9\u4e48\u5927\u7684\u7978\u4e8b\uff0c\u53ea\u6015\u4ed6\u518d\u597d\u4e5f\u597d\u4e0d\u8d77\u6765\u3002\u201d \u3000\u3000\u201c\u6211\u5e76\u4e0d\u60f3\u6c42\u4ed6\uff0c\u4ed6\u5982\u679c\u4e5f\u50cf\u5434\u6709\u6743\u4e00\u6837\u6765\u72e0\u7684\uff0c\u6211\u7167\u6837\u4e5f\u4e0d\u4f1a\u5bf9\u4ed6\u5ba2\u6c14\uff0c\u4e8b\u60c5\u65e2\u7136\u95f9 \u51fa\u6765\u4e86\uff0c\u90a3\u5c31\u5e72\u8106\u4e00\u4e0d\u4f5c\u4e8c\u4e0d\u4f11\u95f9\u5f97\u518d\u5927\u4e00\u70b9\uff0c\u53cd\u6b63\u540e\u679c\u90fd\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53ea\u542c\u5f97\u6797\u91d1\u5b9d\u53c8\u5f00\u59cb\u76ee\u77aa\u53e3\u5446\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6de1\u7136\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u6015\uff0c\u552f\u6709\u6211\u628a\u4e8b\u60c5\u95f9\u5927\uff0c\u4f60\u53cd\u800c\u53ef\u4fdd\u65e0\u4e8b\u3002\u201d \u3000\u3000\u6797\u91d1\u5b9d\u7f13\u8fc7\u4e00\u53e3\u6c14\u6765\u9053\uff1a\u201c\u516c\u5b50\u8fd9\u8bdd\u5c0f\u7684\u542c\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u90a3\u65f6\u4f60\u5c31\u5c3d\u53ef\u628a\u4e00\u5207\u8d23\u4efb\u63a8\u5f97\u5e72\u51c0\uff0c\u8fde\u603b\u7ba1\u5230\u526f\u603b\u7ba1\u90fd\u5948\u4f55\u6211\u4e0d\u4e86\uff0c\u6211\u628a \u5973\u4eba\u9886\u5230\u91cc\u9762\u6765\uff0c\u4f60\u53c8\u600e\u80fd\u7981\u6b62\u5f97\u4e86\u5462\uff1f\u201d \u3000\u3000\u8fd9\u8bdd\u786e\u662f\u8a00\u4e4b\u6709\u7406\uff0c\u4f46\u6797\u91d1\u5b9d\u5374\u51b3\u4e0d\u5e0c\u671b\u5cb3\u5c0f\u98de\u518d\u548c\u97e9\u603b\u7ba1\u95f9\u8d77\u6765\uff0c\u4ed6\u5446\u4e86\u534a\u664c\u624d\u9053\uff1a \u201c\u82e5\u603b\u7ba1\u6ca1\u4eba\u6765\uff0c\u90a3\u5f53\u7136\u6700\u597d\uff0c\u53ea\u662f\u5c0f \u3000\u3000\u7684\u62b1\u6b49\uff0c\u4eca\u5929\u4e2d\u5348\u516c\u5b50\u53ea\u6015\u5c31\u6ca1\u996d\u5403\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\u4ed6\u4eec\u8981\u65ad\u6211\u7684\u7cae\uff1f\u201d \u3000\u3000\u201c\u4e0d\uff0c\u662f\u5c0f\u7684\u4e0d\u6562\u518d\u5230\u603b\u9986\u62ff\u996d\u3002\u201d \u3000\u3000\u6797\u91d1\u5b9d\u8bf4\u7684\u4e0d\u9519\uff0c\u5434\u6709\u6743\u7684\u6028\u6c14\u6b63\u65e0\u5904\u53d1\u6cc4\uff0c\u82e5\u88ab\u4ed6\u9047\u4e0a\uff0c\u8bf4\u4e0d\u5b9a\u6797\u91d1\u5b9d\u5c31\u6ca1\u4e86\u547d\uff0c\u5cb3 \u5c0f\u98de\u4e0d\u80fd\u4e0d\u4e3a\u6797\u91d1\u5b9d\u7740\u60f3\uff1a\u201c\u4e00\u987f\u996d\u4e0d\u5403\u65e0\u6240\u8c13\uff0c\u82e5\u4e0b\u5348\u4ecd\u6ca1\u6709\u52a8\u9759\uff0c\u4f60\u4e0d\u59a8\u5e26\u8def\u7531\u6211\u53bb \u62ff\u3002\u201d \u3000\u3000\u5927\u7ea6\u534a\u4e2a\u65f6\u8fb0\u8fc7\u540e\uff0c\u5f53\u771f\u603b\u7ba1\u97e9\u5fb7\u8d77\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u4eba\u5e74\u5728\u56db\u65ec\u4e0a\u4e0b\uff0c\u8eab\u6750\u5341\u5206\u9b41\u68a7\uff0c\u6d53\u7709\u5927\u773c\uff0c\u6ee1\u8138\u866c\u987b\uff0c\u795e\u6001\u9887\u4e3a\u5a01\u731b\u3002 \u3000\u3000\u4ed6\u53ea\u5e26\u7740\u4e24\u540d\u968f\u4ece\uff0c\u8fdb\u95e8\u4e4b\u540e\uff0c\u4fbf\u671b\u7740\u5cb3\u5c0f\u98de\u62f1\u4e86\u62f1\u624b\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4f4d\u6562\u662f\u5cb3\u516c\u5b50\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u89c1\u5bf9\u65b9\u4ee5\u793c\u76f8\u5f85\uff0c\u968f\u5373\u4e5f\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u6b63\u662f\u5728\u4e0b\u3002\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u4f3c\u7b11\u975e\u7b11\u9053\uff1a\u201c\u65b9\u624d\u5434\u526f\u603b\u7ba1\u591a\u6709\u5192\u72af\uff0c\u516c\u5b50\u51fa\u624b\u6559\u8bad\u4e86\u4ed6\uff0c\u90a3\u662f\u4ed6\u548e\u7531\u767d\u53d6\uff0c \u97e9\u67d0\u7279\u5730\u524d\u6765\u5411\u516c\u5b50\u8d54\u7f6a\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u65b9\u624d\u662f\u5728\u4e0b\u5931\u793c\uff0c\u97e9\u603b\u7ba1\u8bf7\u5750\uff01\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u53c8\u4e00\u62f1\u624b\u9053\uff1a\u201c\u97e9\u67d0\u9664\u4e86\u5411\u516c\u5b50\u8d54\u7f6a\uff0c\u53e6\u5916\u8fd8\u5949\u547d\u8bf7\u516c\u5b50\u5230\u603b\u9986\u6709\u8d9f\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9762\u4e0d\u6539\u8272\uff0c\u53c8\u7b11\u7b11\u9053\uff1b\u201c\u4e0d\u77e5\u662f\u4ec0\u4e48\u4eba\u53ec\u89c1\u5728\u4e0b\uff1f\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u9053\uff1a\u201c\u516c\u5b50\u592a\u5ba2\u6c14\uff0c\u60a8\u662f\u8d35\u5ba2\uff0c\u97e9\u67d0\u662f\u5949\u9986\u4e3b\u4e4b\u547d\u76f8\u8bf7\uff0c\u600e\u53ef\u8bf4\u6210\u53ec\u89c1\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u90a3\u6700\u597d\u4e0d\u8fc7\uff0c\u5728\u4e0b\u4e5f\u6b63\u60f3\u627e\u673a\u4f1a\u62dc\u89c1\u62dc\u89c1\u9986\u4e3b\u3002\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u9053\uff1b\u201c\u5c31\u8bf7\u516c\u5b50\u8fd9\u5c31\u79fb\u9a7e\u5427\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u521a\u8d70\u51fa\u51e0\u6b65\uff0c\u5ffd\u542c\u6797\u91d1\u5b9d\u53eb\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4f60\u8fd8\u56de\u4e0d\u56de\u6765\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u53eb\u558a\uff0c\u542c\u5f97\u5cb3\u5c0f\u98de\u6709\u4e9b\u557c\u7b11\u7686\u975e\uff0c\u4e0d\u8fc7\u4ed6\u660e\u767d\uff0c\u8fd9\u662f\u6797\u91d1\u5b9d\u60c5\u6025\u95f4\u8131\u53e3\u800c\u51fa\uff0c\u7528 \u610f\u662f\u5bf9\u81ea\u5df1\u7684\u5b89\u5371\u8868\u793a\u5173\u5207\u3002 \u3000\u3000\u97e9\u5fb7\u8d77\u56de\u5934\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u56b7\u4ec0\u4e48\uff0c\u5cb3\u516c\u5b50\u662f\u8d35\u5ba2\uff0c\u5f53\u7136\u8981\u56de\u6765\uff0c\u5982\u679c\u4e0d\u56de\u6765\uff0c\u90a3\u5c31 \u662f\u53e6\u6709\u9ad8\u5c31\u4e86\u3002\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u7684\u786e\u5f88\u591f\u793c\u8c8c\uff0c\u4e00\u51fa\u95e8\u4fbf\u8ba9\u5cb3\u5c0f\u98de\u8d70\u5728\u524d\u9762\u3002 \u3000\u3000\u8fd9\u4e03\u5341\u4e8c\u5206\u9986\uff0c\u661f\u7f57\u68cb\u5e03\u5728\u4e00\u7247\u4e18\u9675\u5730\u4e0a\uff0c\u5cb3\u5c0f\u98de\u8fb9\u8d70\u8fb9\u7559\u610f\u56db\u4e0b\u52a8\u9759\uff0c\u53ea\u89c1\u5404\u5206\u9986\u95e8 \u5916\uff0c\u770b\u4e0d\u5230\u534a\u4e2a\u4eba\u5f71\uff0c\u4ed6\u662f\u591a\u4e48\u60f3\u80fd \u3000\u3000\u9047\u5230\u8881\u5c0f\u9e64\u4ee5\u53ca\u4e01\u6d9b\u3001\u7518\u9716\u7b49\u4eba\uff0c\u53ef\u60dc\u6362\u6765\u7684\u53ea\u662f\u5931\u671b\u3002\u4ed6\u6709\u4e00\u642d\u6ca1\u4e00\u642d\u7684\u95ee\u9053\uff1a\u201c\u97e9 \u603b\u7ba1\uff0c\u8fd9\u4e03\u5341\u4e8c\u5206\u9986\uff0c\u4e3a\u4ec0\u4e48\u4e00\u4e2a\u4eba\u90fd\u770b\u4e0d\u5230\uff1f\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u9053\uff1a\u201c\u6bcf\u5206\u9986\u4e0d\u8fc7\u4f4f\u4e86\u4e24\u4eba\uff0c\u6709\u534a\u6570\u5206\u9986\u6839\u672c\u6ca1\u6709\u8d35\u5ba2\u5728\u5185\uff0c\u518d\u52a0\u4e0a\u4e0d\u5f97\u8fdc\u79bb\u4e09 \u5341\u6b65\u7684\u9650\u5236\uff0c\u5f53\u7136\u516c\u5b50\u4e0d\u5bb9\u6613\u770b\u5230\u4eba\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u518d\u9053\uff1a\u201c\u548c\u5728\u4e0b\u540c\u6765\u7684\u4e00\u5171\u56db\u4eba\uff0c\u53e6\u5916\u4e09\u4eba\u4f4f\u5728\u54ea\u51e0\u53f7\u5206\u9986\uff0c\u97e9\u603b\u7ba1\u2014\u5b9a\u77e5\u9053 \u4e86\uff1f\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u6b49\u7136\u7b11\u9053\uff1b\u201c\u8fd9\u4e48\u591a\u5206\u9986\uff0c\u97e9\u67d0\u5b9e\u5728\u8bb0\u4e0d\u6e05\u695a\uff0c\u5fc5\u987b\u56de\u53bb\u67e5\u67e5\u540d\u518c\u624d\u6210\uff0c\u4e0d\u8fc7\u516c \u5b50\u548c\u4ed6\u4eec\u4e09\u4f4d\u65e5\u524d\u90fd\u53d7\u5230\u884c\u52a8\u4e0a\u7684\u9650\u5236\uff0c \u3000\u3000\u5373\u4f7f\u77e5\u9053\u5bf9\u65b9\u4f4f\u5728\u54ea\u91cc\u4e5f\u4e0d\u53ef\u80fd\u8054\u7edc\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u5f7c\u6b64\u5c31\u6c38\u8fdc\u4e0d\u80fd\u89c1\u9762\u4e86\uff1f\u201d \u3000\u3000\u201c\u7b49\u5230\u5404\u4eba\u5206\u914d\u804c\u52a1\u4ee5\u540e\uff0c\u5f53\u7136\u5c31\u6709\u673a\u4f1a\u89c1\u9762\u3001\u4e0d\u8fc7\u5206\u914d\u804c\u52a1\u6709\u65e9\u665a\uff0c\u4e0d\u66fe\u5206\u914d\u5230\u804c\u52a1 \u7684\uff0c\u884c\u52a8\u8fd8\u662f\u8981\u53d7\u5230\u9650\u5236\u3002\u201d \u3000\u3000\u201c\u8d35\u9986\u7684\u8fd9\u79cd\u89c4\u5b9a\uff0c\u5728\u4e0b\u603b\u89c9\u5f97\u5e76\u975e\u5f85\u5ba2\u4e4b\u9053\uff0c\u800c\u4e14\u4e5f\u4e0d\u592a\u5408\u7406\u3002\u201d \u3000\u3000\u201c\u97e9\u67d0\u548c\u516c\u5b50\u7684\u770b\u6cd5\u4e00\u6837\uff0c\u53ef\u60dc\u8fd9\u662f\u6559\u4e3b\u7684\u89c4\u5b9a\uff0c\u62db\u8d24\u9986\u4e0d\u5f97\u4e0d\u9075\u7167\u6267\u884c\u3002\u201d \u3000\u3000\u8f6c\u8fc7\u4e00\u9053\u77ee\u5761\uff0c\u773c\u524d\u666f\u8c61\u4e00\u65b0\uff0c\u4e00\u5e62\u5360\u5730\u6570\u4ea9\u7684\u5dcd\u5ce8\u5efa\u7b51\uff0c\u5728\u6797\u6728\u63a9\u6620\u4e2d\u9690\u7ea6\u53ef\u89c1\u3002 \u3000\u3000\u97e9\u5fb7\u8d77\u5411\u524d\u4e00\u6307\u9053\uff1a\u201c\u90a3\u5c31\u662f\u603b\u9986\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1b\u201c\u8fd8\u6ca1\u8bf7\u95ee\u8d35\u9986\u9986\u4e3b\u7684\u5c0a\u59d3\u5927\u540d\uff1f\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u9053\uff1a\u201c\u655d\u9986\u4e3b\u59d3\u8427\uff0c\u5355\u540d\u4e00\u4e2a\u6e58\u5b57\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u540d\u5b57\u542c\u8d77\u6765\u5f88\u987a\uff0c\u53ea\u662f\u4e0d\u5bb9\u6613\u5206\u51fa\u662f\u7537\u662f\u5973\uff1f\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u7b11\u9053\uff1a\u201c\u62db\u8d24\u9986\u4e0d\u51c6\u6709\u5973\u4eba\u8fdb\u5165\uff0c\u9986\u4e3b\u5f53\u7136\u4e0d\u53ef\u80fd\u662f\u5973\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u8d77\u7236\u4eb2\u548c\u6bcd\u4eb2\u5f53\u521d\u8fdb\u5165\u5929\u8c37\u65f6\uff0c\u5fc5\u5b9a\u4e5f\u662f\u8fde\u95ef\u4e94\u5173\uff0c\u90a3\u4e48\u6bcd\u4eb2\u4e0d\u4f4f \u62db\u8d24\u9986\u53c8\u4f4f\u54ea\u91cc\u5462\uff1f \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u968f\u53e3\u95ee\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4e9b\u5e74\u6765\uff0c\u5c31\u6ca1\u6709\u4e00\u4f4d\u5973\u7684\u8fde\u95ef\u4e94\u5173\u8fdb\u5165\u5929\u8c37\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6709\u3002\u201d \u3000\u3000\u201c\u5979\u4eec\u53c8\u4f4f\u5728\u54ea\u91cc\u5462\uff1f\u201d \u3000\u3000\u201c\u5973\u7684\u4e0d\u5fc5\u4f4f\u8fdb\u62db\u8d24\u9986\uff0c\u6559\u4e3b\u53e6\u6709\u5b89\u6392\u201d \u3000\u3000\u5f88\u5feb\u4fbf\u8fdb\u5165\u62db\u8d24\u9986\u5927\u95e8\u3002 \u3000\u3000\u5927\u95e8\u5185\u9694\u7740\u4e00\u9053\u4ead\u53f0\u6c34\u69ad\u4fbf\u662f\u5927\u5385\u3002 \u3000\u3000\u97e9\u5fb7\u8d77\u628a\u5cb3\u5c0f\u98de\u5f15\u8fdb\u5927\u5385\uff0c\u65e9\u6709\u4eba\u732e\u4e0a\u8336\u6765\u3002 \u3000\u3000\u5927\u5385\u5e03\u7f6e\u5f97\u9ad8\u96c5\u81f3\u6781\uff0c\u56db\u58c1\u6302\u6ee1\u4e86\u540d\u4eba\u5b57\u753b\uff0c\u5899\u89d2\u8fb9\u5219\u6446\u653e\u7740\u5404\u79cd\u5947\u82b1\u5f02\u6728\u7684\u76c6\u666f\uff0c\u7684 \u786e\u5f88\u50cf\u62db\u8d24\u58eb\u805a\u4f1a\u7684\u6240\u5728\u3002 \u3000\u3000\u540e\u58c1\u6b63\u4e2d\u4e0a\u65b9\u9ad8\u60ac\u7740\u4e00\u65b9\u9ed1\u5e95\u91d1\u5b57\u7684\u533e\u989d\uff0c\u4e0a\u5199\u201c\u7fa4\u8d24\u6bd5\u81f3\u201d\u56db\u4e2a\u5927\u5b57\uff0c\u663e\u5f97\u975e\u5e38\u8000\u773c\u3002 \u3000\u3000\u97e9\u5fb7\u8d77\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u8bf7\u7a0d\u5f85\uff0c\u97e9\u67d0\u9a6c\u4e0a\u53bb\u8bf7\u9986\u4e3b\u6765\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u4e0b\u96be\u514d\u4e0d\u5feb\uff0c\u5982\u679c\u771f\u6b63\u662f\u201c\u62db\u8d24\u201d\uff0c\u5c82\u6709\u5ba2\u4eba\u53cd\u800c\u7b49\u5f85\u4e3b\u4eba\u5230\u6765\u4e4b\u7406\u3002 \u3000\u3000\u8fd9\u4e00\u7b49\uff0c\u5c45\u7136\u7b49\u5f97\u8db3\u8db3\u4e00\u76cf\u70ed\u8336\u7684\u5de5\u592b\u8fc7\u53bb\u3002 \u3000\u3000\u8fd8\u597d\uff0c\u5927\u95e8\u5916\u7ec8\u4e8e\u6709\u4e86\u811a\u6b65\u58f0\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u9053\u62db\u8d24\u9986\u9986\u4e3b\u8427\u6e58\u5df2\u5230\uff0c\u8c01\u77e5\u8fdb\u6765\u7684\u90a3\u4eba\u5374\u662f\u88ab\u4e24\u540d\u9ed1\u8863\u6c49\u5b50\u67b6\u7740\u3002 \u3000\u3000\u5f53\u771f\u51fa\u4eba\u610f\u6599\uff0c\u6765\u4eba\u8d6b\u7136\u662f\u526f\u603b\u7ba1\u5434\u6709\u6743\u3002 \u3000\u3000\u5434\u6709\u6743\u4ecd\u662f\u6ee1\u8138\u8840\u6c61\uff0c\u800c\u4e14\u53cc\u988a\u80bf\u5f97\u66f4\u9ad8\uff0c\u88ab\u4e24\u4eba\u67b6\u8fdb\u4e4b\u540e\uff0c \u3000\u3000\u4fbf\u5750\u5728\u9760\u5916\u7684\u4e00\u5f20\u9760\u80cc\u6905\u4e0a\uff0c\u90a3\u4e24\u540d\u9ed1\u8863\u6c49\u5b50\u5e76\u672a\u79bb\u53bb\uff0c\u4e0d\u65f6\u5411\u5750\u5728\u53e6\u2014\u8fb9\u7684\u5cb3\u5c0f\u98de\u6012 \u76ee\u800c\u89c6\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u7531\u80f8\u4e2d\u6012\u706b\u76f4\u51b2\uff0c\u5434\u6709\u6743\u662f\u6765\u5f53\u573a\u5bf9\u8d28\u7684\uff0c\u4e0d\u6d88\u8bf4\u62db\u8d24\u9986\u4e3b\u8427\u6e58\u5fc5\u5b9a\u8981\u4ee5\u5347\u5802 \u5ba1\u6848\u7684\u59ff\u6001\u51fa\u73b0\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u4ed6\u771f\u60f3\u62d4\u817f\u5c31\u8d70\uff0c\u4f46\u6700\u540e\u8fd8\u662f\u5fcd\u4e86\u3002 \u3000\u3000\u53c8\u662f\u5f88\u957f\u4e00\u6bb5\u65f6\u95f4\u8fc7\u53bb\uff0c\u624d\u542c\u5927\u95e8\u5916\u6709\u4eba\u558a\u9053\uff1a\u201c\u9986\u4e3b\u9a7e\u5230\uff01\u201d \u3000\u3000\u5750\u5728\u5bf9\u9762\u7684\u5434\u6709\u6743\uff0c\u7acb\u523b\u5472\u7259\u54a7\u5634\u6323\u624e\u7740\u7ad9\u4e86\u8d77\u6765\uff0c\u5b8c\u5168\u662f\u4e00\u526f\u5c0f\u4eba\u9022\u8fce\u4e0a\u7ea7\u7684\u4e11\u6001\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5374\u662f\u7a33\u5750\u6905\u4e0a\uff0c\u52a8\u4e5f\u6ca1\u52a8\u3002 \u3000\u3000\u62db\u8d24\u9986\u4e3b\u8427\u6e58\u8fc8\u6b65\u8fdb\u5165\u5927\u5385\u3002 \u3000\u3000\u4ed6\u7684\u8eab\u540e\uff0c\u7d27\u8ddf\u7740\u603b\u7ba1\u97e9\u5fb7\u8d77\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u8427\u6e58\u5728\u4e94\u65ec\u5de6\u53f3\uff0c\u4e09\u7efa\u957f\u987b\uff0c\u7709\u6e05\u76ee\u6717\uff0c\u8eab\u6750\u9002\u4e2d\uff0c\u6c14\u5ea6\u96cd\u5bb9\uff0c \u8fd8\u771f\u6709\u70b9\u5112\u96c5\u98ce\u97f5\u3002\u7687\u5143\u6559\u4e3b\u80fd\u5b89\u6392 \u3000\u3000\u6b64\u4eba\u62c5\u4efb\u62db\u8d24\u9986\u4e3b\uff0c\u7684\u786e\u9887\u6709\u773c\u5149\u3002 \u3000\u3000\u8427\u6e58\u8fc8\u7740\u6f47\u6d12\u7684\u6b65\u5c65\uff0c\u76ee\u4e0d\u659c\u89c6\uff0c\u76f4\u6765\u5230\u5f53\u4e2d\u5ea7\u4f4d\u5750\u4e0b\uff0c\u7acb\u523b\u6709\u4eba\u732e\u4e0a\u8336\u6765\u3002 \u3000\u3000\u8427\u6e58\u559d\u4e86\u4e00\u53e3\u8336\u9053\uff1b\u201c\u54ea\u4e00\u4f4d\u662f\u5cb3\u516c\u5b50\uff1f\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u5411\u5cb3\u5c0f\u98de\u4e00\u6307\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4f4d\uff01\u201d \u3000\u3000\u6b64\u65f6\u5cb3\u5c0f\u98de\u53cd\u611f\u8d8a\u751a\uff0c\u5927\u5385\u5185\u53ea\u6709\u4ed6\u4e00\u4eba\u5750\u7740\uff0c\u4e5f\u53ea\u6709\u4ed6\u4e00\u4e2a\u662f\u751f\u4eba\uff0c\u5bf9\u65b9\u5c45\u7136\u8fd8\u8981\u95ee \u4eba\u5728\u54ea\u91cc\uff0c\u5bf9\u5ba2\u4eba\u9119\u89c6\u5230\u8fd9\u79cd\u7a0b\u5ea6\uff0c\u600e\u4e0d\u4ee4\u4ed6\u706b\u5192\u4e09\u4e08\u3002 \u3000\u3000\u8427\u6e58\u89c1\u5cb3\u5c0f\u98de\u4e0d\u4f46\u6ca1\u5f00\u53e3\uff0c\u8fde\u5bf9\u81ea\u5df1\u770b\u90fd\u6ca1\u770b\u4e00\u773c\uff0c\u53ea\u597d\u653e\u4e0b\u8336\u676f\uff0c\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u8427 \u67d0\u6709\u5931\u8fdc\u8fce\uff0c\u5cb3\u516c\u5b50\u4f11\u602a\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u80fd\u518d\u4e0d\u7406\uff0c\u53ea\u597d\u4e5f\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u5728\u4e0b\u89c1\u8fc7\u9986\u4e3b\uff01\u201d \u3000\u3000\u4ed6\u8fde\u8eab\u5b50\u4e5f\u6ca1\u52a8\uff0c\u67b6\u5b50\u548c\u8427\u6e58\u4e00\u822c\u5927\u3002 \u3000\u3000\u8fd9\u4e3e\u52a8\u53ea\u770b\u5f97\u97e9\u5fb7\u8d77\u76f4\u76b1\u7709\u3002 \u3000\u3000\u5728\u97e9\u5fb7\u8d77\u7684\u7ecf\u5386\u4e2d\uff0c\u62db\u8d24\u9986\u81ea\u6210\u7acb\u81f3\u4eca\uff0c\u4ece\u65e0\u4e00\u4eba\u5728\u548c\u9986\u4e3b\u89c1\u9762\u65f6\u6001\u5ea6\u5982\u6b64\u5028\u50b2\u7684\uff0c\u56e0 \u4e3a\u4ed6\u4eec\u4e3a\u4e86\u80fd\u5f97\u5230\u597d\u7684\u804c\u4f4d\uff0c\u9022\u8fce\u8fd8 \u3000\u3000\u6765\u4e0d\u53ca\uff0c\u4f55\u51b5\u5cb3\u5c0f\u98de\u53c8\u4ec5\u662f\u4e00\u4e2a\u5341\u4e09\u56db\u5c81\u7684\u5b69\u5b50\u3002 \u3000\u3000\u8427\u6e58\u4e1d\u6beb\u4e0d\u89c1\u6120\u8272\uff0c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u4f4f\u8fdb\u4e09\u5341\u516d\u53f7\u5206\u9986\u4ee5\u540e\uff0c\u8fc7\u5f97\u8fd8\u4e60\u60ef\u5417\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u4e0d\u4e60\u60ef\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4ee5\u8fd9\u4e09\u4e2a\u5b57\u56de\u7b54\uff0c\u4e0d\u4f46\u97e9\u5fb7\u8d77\u5927\u60ca\u5931\u8272\uff0c\u8fde\u90a3\u6276\u6301\u5434\u6709\u6743\u7684\u4e24\u540d\u9ed1\u8863\u6c49\u5b50\u548c\u5927\u5385 \u5185\u8d1f\u8d23\u6c8f\u8336\u7684\u4e0b\u4eba\uff0c\u4e5f\u90fd\u77a0\u76ee\u7ed3\u820c\u3002 \u3000\u3000\u4f46\u8427\u6e58\u5374\u4ecd\u672a\u73b0\u51fa\u6012\u610f\uff0c\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4e0d\u77e5\u5cb3\u516c\u5b50\u6709\u54ea\u4e9b\u4e0d\u4e60\u60ef\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u9650\u5236\u592a\u591a\uff0c\u4f7f\u5728\u4e0b\u5931\u53bb\u884c\u52a8\u81ea\u7531\uff0c\u8d35\u9986\u540d\u4e3a\u62db\u8d24\uff0c\u5374\u8ba9\u8d24\u8005\u53d7\u5230\u5982\u6b64\u5f85\u9047\uff0c \u672a\u514d\u592a\u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u8427\u6e58\u62c2\u987b\u563f\u563f\u7b11\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u8a00\u4e4b\u6210\u7406\uff0c\u4f46\u8fd9\u662f\u6559\u4e3b\u7684\u89c4\u5b9a\uff0c\u8427\u67d0\u5fc5\u987b\u6309\u5f8b\u6267\u884c\uff0c\u5cb3\u516c \u5b50\u59d4\u5c48\u4e4b\u5904\uff0c\u4e5f\u53ea\u6709\u591a\u8bf7\u539f\u8c05\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7ad9\u8d77\u8eab\u9053\uff1b\u201c\u9986\u4e3b\u8981\u5728\u4e0b\u5230\u8fd9\u91cc\u6765\uff0c\u5c31\u662f\u4e3a\u4e86\u8bf4\u8fd9\u53e5\u8bdd\uff1f\u679c\u771f\u5982\u6b64\uff0c\u5728\u4e0b\u5f53\u7136\u53ef \u4ee5\u539f\u8c05\uff0c\u73b0\u5728\u6211\u8be5\u8d70\u4e86\uff01\u201d \u3000\u3000\u8427\u6e58\u4e24\u592a\u9633\u7a74\u5fae\u5fae\u62bd\u6410\u4e86\u51e0\u4e0b\uff0c\u62ac\u624b\u6307\u5411\u5434\u6709\u6743\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u53ef\u8ba4\u8bc6\u8fd9\u4eba\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6628\u5929\u89c1\u8fc7\u4e00\u9762\u5370\u8c61\u4e0d\u6df1\uff0c\u4eca\u5929\u65e9\u4e0a\u5728\u4e09\u5341\u516d\u53f7\u5206\u9986\uff0c\u603b\u7b97\u52a0\u6df1\u4e86\u5370\u8c61\uff0c\u4ed6 \u597d\u50cf\u662f\u8fd9\u91cc\u7684\u526f\u603b\u7ba1\uff0c\u59d3\u5434\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u8427\u6e58\u563f\u563f\u7b11\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u88ab\u4eba\u6253\u4e86\uff0c\u6253\u6210\u8fd9\u6837\uff0c\u5cb3\u516c\u5b50\u8bf7\u770b\u770b\uff0c\u4f24\u52bf\u4e0d\u8f7b\u5427\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u7b11\u4e86\u8d77\u6765\u9053\uff1a\u201c\u4e0d\u5fc5\u770b\u4e86\uff0c\u662f\u5728\u4e0b\u4eb2\u624b\u6253\u7684\uff0c\u6211\u77e5\u9053\u7684\u6bd4\u8c01\u90fd\u65e9\uff0c\u4f55\u5fc5\u518d\u770b\uff01\u201d \u3000\u3000\u201c\u516c\u5b50\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u7adf\u6709\u5982\u6b64\u9ad8\u7edd\u7684\u8eab\u624b\uff0c\u8427\u67d0\u5b9e\u5728\u4f69\u670d\uff01\u201d \u3000\u3000\u201c\u522b\u5ba2\u6c14\uff0c\u82e5\u4e0d\u770b\u5728\u9986\u4e3b\u9762\u4e0a\uff0c\u4ed6\u53ef\u80fd\u4f24\u5f97\u66f4\u91cd\uff0c\u4e5f\u5f88\u53ef\u80fd\u5728\u5929\u8c37\u5185\u5c11\u4e86\u8fd9\u4e2a\u4eba\uff0c\u66ff\u9986 \u4e3b\u7701\u70b9\u7cae\u98df\u3002\u201d \u3000\u3000\u201c\u5cb3\u516c\u5b50\u6253\u4e86\u4ed6\uff0c\u603b\u8be5\u6709\u70b9\u7406\u7531\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6709\u8fdd\u6559\u4e3b\u62db\u8d24\u672c\u610f\uff0c\u5728\u4e0b\u6253\u4e86\u4ed6\uff0c\u7b49\u4e8e\u66ff\u7687\u5143\u6559\u9664\u53bb\u5bb3\u7fa4\u4e4b\u9a6c\uff0c\u8fd9\u4e00\u5207\u81f3\u5c11\u8be5\u53d7\u5230 \u6559\u4e3b\u7684\u5956\u52b1\uff01\u201d \u3000\u3000\u201c\u4f60\u53ef\u77e5\u9053\u4ed6\u6b63\u662f\u9075\u5949\u672c\u6559\u89c4\u5f8b\u884c\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6559\u4e3b\u53ef\u89c4\u5b9a\u8fc7\u4ed6\u5bf9\u8d24\u58eb\u4e0d\u656c\uff1f\u201d \u3000\u3000\u201c\u5cb3\u516c\u5b50\u4e3a\u4f55\u4e0d\u95ee\u95ee\u81ea\u5df1\uff0c\u4e3a\u4f55\u5192\u72af\u89c4\u5f8b\u628a\u5973\u4eba\u5f15\u8fdb\u5206\u9986\uff0c\u662f\u5426\u9986\u91cc\u7684\u9986\u50ee\u6ca1\u5bf9\u5cb3\u516c\u5b50 \u8bb2\u8fc7\u4e86\u201d \u3000\u3000\u201c\u4ed6\u5728\u5728\u4e0b\u8fdb\u5206\u9986\u65f6\u5c31\u8bb2\u8fc7\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u8bb2\u8fc7\uff0c\u5cb3\u516c\u5b50\u8fd9\u6837\u505a\uff0c\u5c82\u4e0d\u662f\u660e\u77e5\u6545\u72af\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u8ba4\u4e3a\u8fd9\u89c4\u5b9a\u4e0d\u5408\u7406\uff0c\u6ca1\u6709\u9075\u5b88\u7684\u5fc5\u8981\uff01\u201d \u3000\u3000\u8427\u6e58\u7ec8\u4e8e\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5c45\u7136\u505a\u51fa\u52fe\u5f15\u5973\u4eba\u4e4b\u4e8b\uff0c\u8fd9\u7b97\u4ec0\u4e48\u8d24\u58eb\uff1f\u8427\u67d0 \u4e0d\u89e3\uff0c\u5cb3\u516c\u5b50\u662f\u7531\u8c37\u5916\u8fdb\u6765\u7684\uff0c\u77ed\u77ed\u8fd9 \u3000\u3000\u4e48\u51e0\u5929\uff0c\u600e\u4f1a\u6709\u5973\u4eba\u548c\u4f60\u8ba4\u8bc6\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u8fde\u95ef\u4e94\u5173\uff0c\u4e94\u5173\u4e4b\u5185\uff0c\u9664\u4e86\u5728\u6b66\u5173\u6ca1\u89c1\u8fc7\u5973\u4eba\uff0c\u5176\u4f59\u5404\u5173\uff0c \u4e0d\u4f46\u6709\u5973\u4eba\uff0c\u800c\u4e14\u8fd8\u4e0d\u5728\u5c11\u6570\uff0c\u5979\u4eec\u89c1\u8fc7\u5728\u4e0b\uff0c\u5f53\u7136\u8ba4\u8bc6\u3002\u201d \u3000\u3000\u8427\u6e58\u6b63\u8981\u518d\u95ee\uff0c\u5ffd\u542c\u5916\u9762\u6709\u4eba\u9ad8\u58f0\u558a\u9053\uff1a\u201c\u526f\u9986\u4e3b\u9a7e\u5230\uff01\u201d \u3000\u3000\u63a5\u7740\u6602\u7136\u9614\u6b65\uff0c\u8fdb\u6765\u4e00\u540d\u4e30\u795e\u4fca\u6717\u7684\u6f47\u6d12\u4eba\u7269\uff0c\u8d6b\u7136\u662f\u82b1\u7389\u9e9f\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u5cb3\u5c0f\u98de\u5185\u5fc3\u53c8\u559c\u53c8\u7a98\u53c8\u6fc0\u52a8\uff0c\u4ed6\u4e0d\u80fd\u7236\u5b50\u76f8\u8ba4\uff0c\u53ea\u6709\u4f4e\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u8427\u6e58\u6b20\u4e86\u6b20\u8eab\u9053\uff1a\u201c\u526f\u5ea7\u8bf7\u5750\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u4f9d\u8a00\u5728\u4e00\u65c1\u5750\u4e0b\uff0c\u6545\u505a\u832b\u7136\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u8427\u6e58\u63a0\u4e86\u5cb3\u5c0f\u98de\u773c\u9053\uff1a\u201c\u8fd9\u4eba\u5c31\u662f\u6628\u5929\u521a\u5230\u7684\u5cb3\u5c0f\u98de\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u4e5f\u98d8\u8fc7\u6765\u4e00\u773c\u9053\uff1a\u201c\u539f\u6765\u4ed6\u53ea\u662f\u4e00\u4e2a\u5b69\u5b50\uff0c\u636e\u524d\u9762\u4e94\u5173\u4f20\u6765\u7684\u6d88\u606f\uff0c\u5728\u540c\u6765\u7684\u56db \u4e2a\u5f53\u4e2d\uff0c\u4e0d\u8bba\u6587\u624d\u6b66\u529f\uff0c\u90fd\u662f\u4ee5\u4ed6\u4e3a\u9996\uff0c\u96be\u5f97\u4ed6\u5c0f\u5c0f\u5e74\u7eaa\u4fbf\u6709\u5982\u6b64\u6210\u5c31\uff0c\u6559\u4e3b\u80fd\u62db\u5f97\u8fd9\u6837\u7f55 \u89c1\u7684\u9ad8\u624b\uff0c\u7b97\u5f97\u4e0a\u662f\u54b1\u4eec\u7687\u5143\u6559\u4e00\u5927\u5e78\u4e8b\u4e86\uff01\u201d \u3000\u3000\u8427\u6e58\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u526f\u5ea7\u53ea\u77e5\u5176\u4e00\uff0e\u4e0d\u77e5\u5176\u4e8c\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u505a\u8fc7\u4ec0\u4e48\u4e0d\u8be5\u505a\u7684\u4e8b\uff1f\u201d \u3000\u3000\u8427\u6e58\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5c82\u6b62\u5982\u6b64\uff0c\u4ed6\u7b80\u76f4\u5c31\u662f\u80c6\u5927\u5305\u5929\uff0c\u516c\u7136\u53db\u9006\u6297\u547d\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u4f3c\u662f\u5403\u4e86\u2014\u60ca\u9053\uff1a\u201c\u9986\u4e3b\u4e0d\u59a8\u8bf4\u51fa\u6765\u8ba9\u82b1\u67d0\u542c\u542c\uff01\u201d \u3000\u3000\u8427\u6e58\u56de\u987e\u4e86\u7ad9\u5728\u8eab\u65c1\u7684\u97e9\u5fb7\u8d77\u4e00\u773c\u9053\uff1b\u201c\u4f60\u544a\u8bc9\u526f\u9986\u4e3b\uff01\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u9053\uff1a\u201c\u7980\u526f\u9986\u4e3b\uff0c\u59d3\u5cb3\u7684\u660e\u77e5\u6545\u72af\uff0c\u65e0\u7aef\u628a\u5973\u4eba\u5f15\u8fdb\u5206\u9986\uff0c\u65e9\u4e0a\u5434\u526f\u603b\u7ba1\u5949\u547d\u53bb \u67e5\u95ee\uff0c\u7adf\u53c8\u88ab\u4ed6\u6253\u6210\u8fd9\u6837\uff0c\u526f\u9986\u4e3b\uff0c\u5434\u526f\u603b\u7ba1\u5c31\u5728\u8fd9\u91cc\uff0c\u60a8\u4e0d\u59a8\u4ed4\u7ec6\u770b\u770b\u4ed6\u4f24\u6210\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u4f3c\u662f\u4e5f\u4e3a\u5cb3\u5c0f\u98de\u628a\u5973\u4eba\u5f15\u8fdb\u5206\u9986\u4e0d\u89e3\uff0c\u4ed6\u6de1\u7136\u626b\u8fc7\u5434\u6709\u6743\u4e00\u773c\u9053\uff1a\u201c\u90a3\u4e5f\u8bb8\u5434\u526f \u603b\u7ba1\u6709\u5bf9\u4ed6\u8fc7\u4efd\u4e4b\u5904\uff0c\u5426\u5219\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u6253\u522b\u4eba\uff1f\u636e\u6211\u6240\u77e5\uff0c\u5434\u526f\u603b\u7ba1\u5bf9\u5f85\u5404\u5206\u9986\u7684\u8d35\u5ba2\uff0c\u7ecf \u5e38\u50b2\u6162\u65e0\u793c\uff0c\u751a\u81f3\u72d0\u5047\u864e\u5a01\uff0c\u72d7\u4ed7\u4eba\u52bf\uff0c\u4ed6\u5230\u4eca\u5929\u624d\u88ab\u4eba\u5bb6\u6253\u5b50\uff0c\u6211\u8fd8\u8ba4\u4e3a\u662f\u665a\u4e86\u4e9b\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u65e2\u7136\u8bf4\u51fa\u8fd9\u6837\u7684\u8bdd\uff0c\u5728\u573a\u7684\u9664\u5cb3\u5c0f\u98de\uff0c\u5176\u4ed6\u6240\u6253\u7684\u4eba\uff0c\u65e0\u4e0d\u5927\u611f\u610f\u5916\uff0c\u5c24\u5176\u5434\u6709 \u6743\uff0c\u7b80\u76f4\u6123\u5728\u5f53\u5730\uff0c\u5374\u53c8\u6562\u6012\u800c\u4e0d\u6562\u8a00\u3002 \u3000\u3000\u97e9\u5fb7\u8d77\u4e0d\u7981\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u526f\u9986\u4e3b\u8fd9\u8bdd\u672a\u514d\u8fc7\u4efd\u4e86\u4e9b\uff0c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u5434\u526f\u603b\u7ba1\u662f\u88ab\u59d3 \u5cb3\u7684\u6253\u4e86\uff0c\u800c\u4e14\u6253\u5f97\u90a3\u4e48\u91cd\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u90a3\u662f\u4ed6\u6253\u4e0d\u8fc7\u4eba\u5bb6\uff0c\u82e5\u4ed6\u7684\u6b66\u529f\u6bd4\u522b\u4eba\u9ad8\uff0c\u53ea\u6015\u4ed6\u4f1a\u628a\u522b\u4eba\u6253\u5f97\u66f4\u91cd\uff01\u201d \u3000\u3000\u5434\u6709\u6743\u7ec8\u4e8e\u54ac\u7259\u5f00\u4e0b\u53e3\uff1a\u201c\u526f\u9986\u4e3b\uff0c\u60a8\u2026\u2026\u201d \u3000\u3000\u82b1\u7389\u9e9f\u51b7\u7b11\u9053\uff1a\u201c\u6211\u600e\u4e48\u6837\uff1f\u6211\u82e5\u4e0d\u770b\u4f60\u4f24\u5f97\u91cd\uff0c\u8bf4\u4e0d\u5b9a\u73b0\u5728\u4e5f\u63cd\u4f60\u4e00\u987f\u3002\u5434\u6709\u6743\uff0c\u4f60 \u628a\u54b1\u4eec\u62db\u8d24\u9986\u7684\u4eba\u5168\u4e22\u5149\u4e86\uff0c\u7b97\u5f97\u4e0a\u6df7 \u3000\u3000\u5e10\u900f\u9876\uff0c\u8fd8\u6709\u8138\u5728\u9986\u4e3b\u9762\u524d\u544a\u72b6\uff0e\u4e5f\u4e0d\u6492\u6ce1\u5c3f\u7167\u7167\uff0c\u4f60\u7a76\u7adf\u7b97\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u671b\u5411\u8427\u6e58\u9053\uff1a\u201c\u9986\u4e3b\uff0c\u7b49\u8fd9\u6df7\u86cb\u4f24\u52bf\u597d\u4e86\u4ee5\u540e\uff0c\u82b1\u67d0\u8fd8\u8981\u5904\u5206\u4ed6\uff01\u201d \u3000\u3000\u8427\u6e58\u8138\u8272\u5fae\u5fae\u4e00\u53d8\u9053\uff1a\u201c\u526f\u5ea7\uff0c\u5434\u6709\u6743\u5df2\u7ecf\u591f\u59d4\u5c48\u4e86\uff0c\u4f60\u4e0d\u5b89\u6170\u4ed6\u51e0\u53e5\uff0c\u53cd\u800c\u7ed9\u4ed6\u72d7\u8840 \u55b7\u5934\u4e00\u9635\u5927\u9a82\uff0c\u8427\u67d0\u4e0d\u89e3\u4f60\u8fd9\u662f\u4ec0\u4e48\u7528\u5fc3\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u4ed6\u662f\u81ea\u53d6\u5176\u8fb1\uff0c\u540c\u65f6\u4e5f\u7b49\u4e8e\u7838\u4e86\u62db\u8d24\u9986\u7684\u62db\u724c\uff0c\u82b1\u67d0\u6ca1\u518d\u60e9\u7f5a\u4ed6\uff0c\u5df2\u7ecf\u7b97 \u662f\u5f88\u5ba2\u6c14\uff0c\u9986\u4e3b\u5982\u4eca\u53cd\u800c\u95ee\u82b1\u67d0\u4ec0\u4e48\u7528\u5fc3\uff0c\u82b1\u67d0\u5012\u60f3\u77e5\u9053\u9986\u4e3b\u662f\u4ec0\u4e48\u7528\u5fc3\uff1f\u201d \u3000\u3000\u8427\u6e58\u4e24\u592a\u9633\u7a74\u6025\u5267\u7684\u62bd\u6410\u4e86\u51e0\u4e0b\uff0c\u603b\u7b97\u5fcd\u4e86\u4e0b\u6765\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4f9d\u526f\u5ea7\u7684\u610f\u601d\u8be5\u600e\u4e48 \u529e\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u9986\u4e3b\u5475\u95ee\u6e05\u4ed6\u4e3a\u4ec0\u4e48\u628a\u5973\u4eba\u5f15\u5230\u5206\u9986\u4e2d\uff1f\u90a3\u5973\u7684\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u8427\u67d0\u5f53\u7136\u95ee\u8fc7\uff0c\u4ed6\u8bf4\u662f\u5173\u4e0a\u7684\u3002\u201d \u3000\u3000\u201c\u54ea\u4e00\u5173\uff0c\u662f\u4ed6\u628a\u5973\u4eba\u5f15\u8fdb\u5206\u9986\u7684\uff1f\u8fd8\u662f\u90a3\u5973\u4eba\u81ea\u5df1\u95ef\u8fdb\u53bb\u7684\uff1f\u82e5\u8fde\u8fd9\u4e2a\u90fd\u6ca1\u5f04\u6e05\u695a\uff0c \u5982\u4f55\u65ad\u5b9a\u4ed6\u662f\u72af\u4e86\u89c4\u5f8b\uff1f\u201d \u3000\u3000\u8427\u6e58\u8fd9\u65f6\u65e9\u5df2\u5bf9\u82b1\u7389\u9e9f\u5bf9\u81ea\u5df1\u5028\u50b2\u65e0\u793c\u800c\u6012\u706b\u76f4\u51b2\uff0c\u53ea\u662f\u5f53\u7740\u4f17\u4eba\u7684\u9762\u4e0d\u4fbf\u5f53\u573a\u53d1\u4f5c\u800c \u5df2\u3002\u4ed6\u6781\u529b\u6309\u637a\u7740\u6124\u6012\u518d\u671b\u5411\u5cb3\u5c0f\u98de\u9053\uff1b\u201c\u662f\u54ea\u4e00\u5173\u4e0a\u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u706b\u5173\u3002\u201d \u3000\u3000\u5c82\u77e5\u201c\u706b\u5173\u201d\u4e8c\u5b57\u4e00\u51fa\uff0c\u8427\u6e58\u8138\u8272\u7acb\u5373\u53d8\u4e86\u989c\u8272\uff0c\u7ec8\u4e8e\u6c89\u4e0b\u58f0\u97f3\u559d\u9053\uff1a\u201c\u53ef\u77e5\u9053\u5979\u53eb\u4ec0 \u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u7b11\u9053\uff1b\u201c\u9986\u4e3b\u5bf9\u5728\u4e0b\u8bf4\u8bdd\uff0c\u5b9e\u5728\u72af\u4e0d\u7740\u7528\u8fd9\u79cd\u53e3\u6c14\uff0c\u4f60\u5e94\u5f53\u77e5\u9053\u5728\u4e0b\u662f\u5ba2\u4eba\uff0c \u4e0d\u662f\u4f60\u7684\u5c5e\u4e0b\uff01\u201d \u3000\u3000\u8427\u6e58\u731b\u5730\u4e00\u62cd\u8336\u51e0\u9053\uff1a\u201c\u5c11\u8bb2\u5e9f\u8bdd\uff0c\u8bf4\uff0c\u5979\u53eb\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6717\u58f0\u9053\uff1a\u201c\u5979\u53eb\u6843\u82b1\uff01\u201d \u3000\u3000\u201c\u5979\u600e\u4e48\u8ba4\u8bc6\u4f60\uff01\u201d \u3000\u3000\u201c\u5728\u4e0b\u5148\u524d\u65e9\u5c31\u5bf9\u4f60\u8bf4\u8fc7\uff0c\u5728\u4e0b\u5728\u706b\u5173\u66fe\u4f4f\u8fc7\u4e00\u665a\uff0c\u5f53\u65f6\u662f\u7531\u5979\u8d1f\u8d23\u7167\u6599\uff0e\u5979\u5c31\u662f\u8fd9\u6837 \u8ba4\u8bc6\u5728\u4e0b\u7684\u3002\u201d \u3000\u3000\u201c\u5979\u5728\u706b\u5173\uff0c\u4f60\u5728\u4e09\u5341\u516d\u53f7\u5206\u9986\uff0c\u76f8\u8ddd\u5341\u51e0\u4e8c\u5341\u91cc\u8def\uff0c\u5979\u53c8\u600e\u80fd\u8fdb\u5230\u5206\u9986\u53bb\uff1f\u201d \u3000\u3000\u201c\u5979\u5949\u547d\u5230\u603b\u575b\u529e\u4e8b\uff0c\u6628\u5929\u508d\u665a\u56de\u7a0b\u65f6\u7ecf\u8fc7\u4e09\u5341\u516d\u53f7\u5206\u9986\u95e8\u524d\u8def\u4e0a\uff0c\u90a3\u65f6\u5728\u4e0b\u6b63\u597d\u5728\u95e8 \u5916\uff0c\u719f\u4eba\u76f8\u89c1\uff0c\u5979\u968f\u5373\u5230\u5206\u9986\u91cc\u5c0f\u5750\u4e86\u4e00\u4f1a\u513f\uff0c\u8bf7\u95ee\u8fd9\u53c8\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u201c\u4f60\u5e94\u5f53\u77e5\u9053\u4e0d\u51c6\u5973\u4eba\u8fdb\u9986\u7684\u89c4\u5b9a\uff1f\u201d \u3000\u3000\u201c\u4f46\u662f\u6211\u5374\u4e0d\u80fd\u62d2\u7edd\u5979\u5165\u95e8\uff0c\u8fd9\u662f\u4eba\u4e4b\u5e38\u60c5\uff0c\u5c0a\u9a7e\u8eab\u4e3a\u62db\u8d24\u9986\u4e3b\uff0c\u96be\u9053\u8fde\u8fd9\u70b9\u5f85\u5ba2\u4e4b\u9053 \u90fd\u4e0d\u660e\u767d\uff1f\u201d \u3000\u3000\u8427\u6e58\u8f6c\u5934\u671b\u4e86\u97e9\u5fb7\u8d77\u4e00\u773c\u9053\uff1a\u201c\u672c\u9986\u4e3b\u4e0d\u5fc5\u518d\u8ddf\u4ed6\u591a\u8d39\u53e3\u820c\u4e86\uff01\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u8eac\u8eab\u9053\uff1a\u201c\u5c31\u8bf7\u9986\u4e3b\u6309\u5f8b\u6cbb\u4ed6\u7684\u7f6a\uff0c\u5c5e\u4e0b\u4e5f\u8ba4\u4e3a\u6ca1\u6709\u518d\u548c\u4ed6\u7f57\u55e6\u7684\u5fc5\u8981\u3002\u201d \u3000\u3000\u8427\u6e58\u9634\u68ee\u68ee\u4e00\u7b11\u9053\uff1a\u201c\u6253\u5165\u6b7b\u7262\uff01\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u5e94\u4e86\u4e00\u58f0\uff0c\u7acb\u5373\u5927\u6b65\u5411\u5cb3\u5c0f\u98de\u8d70\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u542c\u8bf4\u8981\u628a\u81ea\u5df1\u6253\u5165\u6b7b\u7262\uff0c\u65e2\u7136\u975e\u6b7b\u4e0d\u53ef\uff0c\u5c31\u4e0d\u5982\u5148\u884c\u5927\u5e72\u4e00\u573a\uff0c\u80fd\u5bb0\u4e0a\u51e0\u4e2a\u603b\u662f \u8d5a\u7684\u3002 \u3000\u3000\u4ed6\u624b\u6309\u5251\u628a\uff0c\u6b63\u8981\u62d4\u5251\uff0c\u5374\u53d1\u73b0\u82b1\u7389\u9e9f\u5df2\u5728\u6697\u4f7f\u773c\u8272\u5236\u6b62\u3002 \u3000\u3000\u4ed6\u4e0d\u6562\u6297\u8fdd\u7236\u547d\uff0c\u53ea\u597d\u4efb\u7531\u97e9\u5fb7\u8d77\u524d\u6765\u52a8\u624b\u3002 \u3000\u3000\u97e9\u5fb7\u8d77\u4e00\u628a\u6293\u4f4f\u5cb3\u5c0f\u98de\u624b\u8155\uff0c\u559d\u9053\uff1a\u201c\u59d3\u5cb3\u7684\uff0c\u8d70\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4f60\u8981\u6211\u8d70\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u6c89\u58f0\u9053\uff1a\u201c\u6b7b\u7262\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6697\u8fd0\u4e00\u53e3\u771f\u6c14\uff0c\u529b\u8d2f\u53f3\u81c2\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5427\uff0c\u5728\u4e0b\u60c5\u613f\u8ba9\u4f60\u5e26\u8d70\uff01\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u731b\u7136\u7528\u529b\u4e00\u5e26\uff0c\u5cb3\u5c0f\u98de\u7684\u8eab\u5b50\u7adf\u7136\u53ea\u662f\u8f7b\u5fae\u7684\u79fb\u52a8\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u5cb3\u5c0f\u98de\u5df2\u7528\u4e0a\u4e86\u201c\u5343\u65a4\u5760\u201d\uff0c\u7acb\u5373\u559d\u4ee4\u6276\u6301\u5434\u6709\u6743\u7684\u4e24\u540d\u5927\u6c49\u9053\uff1a\u201c\u8fc7\u6765\uff0c\u628a\u8fd9 \u5c0f\u5b50\u62ac\u5230\u6b7b\u7262\u53bb\uff01\u201d \u3000\u3000\u5ffd\u89c1\u82b1\u7389\u9e9f\u7ad9\u8d77\u8eab\uff0c\u5927\u6b65\u5411\u524d\u8d70\u6765\uff0c\u559d\u9053\uff1a\u201c\u97e9\u603b\u7ba1\uff0c\u677e\u624b\uff01\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u6014\u4e86\u6014\u9053\uff1a\u201c\u526f\u9986\u4e3b\uff0c\u8fd9\u662f\u9986\u4e3b\u7684\u547d\u4ee4\uff0c\u97e9\u67d0\u4e0d\u6562\u4e0d\u9075\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u6211\u73b0\u5728\u8981\u4f60\u677e\u624b\uff0c\u8fd9\u4e5f\u662f\u547d\u4ee4\uff0c\u4f60\u9075\u4e0d\u9075\u3002\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u5982\u679c\u9986\u4e3b\u6ca1\u6709\u547d\u4ee4\u5728\u5148\uff0c\u97e9\u67d0\u4e0d\u5f97\u4e0d\u9075\uff0c\u73b0\u5728\u4f60\u4eec\u4e24\u4f4d\u90fd\u6709\u547d\u4ee4\uff0c \u97e9\u67d0\u5f53\u7136\u5e94\u8be5\u542c\u9986\u4e3b\u7684\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u5411\u524d\u8d70\u51fa\u4e00\u6b65\u9053\uff1a\u201c\u4f60\u518d\u8bf4\u4e00\u904d\uff01\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u9053\uff1a\u201c\u526f\u9986\u4e3b\u5e94\u8be5\u5df2\u7ecf\u542c\u6e05\u4e86\u624d\u5bf9\uff01\u201d \u3000\u3000\u201c\u6211\u6ca1\u542c\u6e05\u695a\uff0c\u4f60\u53ea\u7ba1\u518d\u8bf4\u4e00\u904d\uff01\u201d \u3000\u3000\u201c\u97e9\u67d0\u804c\u8d23\u6240\u5728\uff0c\u8981\u542c\u9986\u4e3b\u7684\uff01\u201d \u3000\u3000\u201c\u90a3\u662f\u4e0d\u60f3\u542c\u6211\u7684\u4e86\uff01\u201d \u3000\u3000\u201c\u8bdd\u4e0d\u80fd\u8fd9\u4e48\u8bf4\uff01\u201d \u3000\u3000\u201c\u73b0\u5728\u6211\u53ea\u8981\u4f60\u542c\u6211\u7684\uff0c\u82e5\u6562\u4e0d\u542c\uff0c\u90a3\u5c31\u522b\u602a\u6211\u624b\u4e0b\u65e0\u60c5\uff01\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u5f53\u7136\u4e0d\u542c\uff0c\u63a2\u8155\u518d\u5ea6\u5411\u5cb3\u5c0f\u98de\u8863\u9886\u6293\u53bb\u3002 \u3000\u3000\u7a81\u89c1\u82b1\u7389\u9e9f\u626c\u624b\u4e00\u638c\uff0c\u95ea\u7535\u822c\u5411\u97e9\u5fb7\u8d77\u9762\u988a\u63b4\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u529b\u9053\u5947\u91cd\uff0c\u628a\u97e9\u5fb7\u8d77\u63b4\u5f97\u6574\u4e2a\u8eab\u5b50\u90fd\u98de\u649e\u51fa\u53bb\uff0c\u76f4\u6454\u5230\u4e08\u4f59\u5916\u7684\u5899\u89d2\uff0c\u624d\u7531\u5899\u58c1 \u518d\u53cd\u5f39\u56de\u6765\uff0c\u5f85\u4ed6\u52c9\u5f3a\u722c\u8d77\u8eab\u6765\uff0c\u5df2\u662f\u5934\u7834\u8840\u6d41\u3002 \u3000\u3000\u8427\u6e58\u84e6\u5730\u7531\u5ea7\u6905\u4e0a\u76f4\u8df3\u8d77\u6765\uff0c\u55d4\u76ee\u5927\u559d\u9053\uff1a\u201c\u82b1\u7389\u9e9f\uff0c\u4f60\u53ef\u662f\u8981\u9020\u53cd\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u201c\u5237\u201d\u7684\u4e00\u58f0\uff0c\u957f\u5251\u5df2\u7ecf\u51fa\u9798\uff0c\u559d\u9053\uff1a\u201c\u73b0\u5728\u82b1\u67d0\u8981\u4ee3\u6559\u4e3b\u6e05\u9664\u53db\u9006\uff01\u201d \u3000\u3000\u8427\u6e58\u8138\u8272\u618b\u5f97\u9752\u4e2d\u5e26\u7d2b\uff0c\u4ed6\u5fc3\u91cc\u6709\u6570\uff0c\u82e5\u8bba\u6b66\u529f\uff0c\u81ea\u5df1\u7edd\u5bf9\u4e0d\u662f\u82b1\u7389\u9e9f\u7684\u654c\u624b\uff0c\u4f46\u5374\u4e0d \u5f97\u4e0d\u518d\u6b21\u559d\u9053\uff1a\u201c\u82b1\u7389\u9e9f\uff0c\u4f60\u8bf4\u8c01\u662f\u53db\u9006\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u5251\u5c16\u6307\u5230\u8427\u6e58\u9f3b\u5b50\u4e0a\u9053\uff1a\u201c\u4f60\u5c31\u662f\u53db\u9006\uff1f\u201d \u3000\u3000\u8427\u6e58\u5927\u58f0\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u4e8b\u60c5\u6446\u5728\u800c\u524d\uff0c\u5bb9\u4e0d\u5f97\u4f60\u72e1\u8fa9\uff01\u201d \u3000\u3000\u201c\u4f60\u8bf4\uff01\u6211\u4e3a\u4ec0\u4e48\u662f\u53db\u9006\uff1f\u201d \u3000\u3000\u201c\u6559\u4e3b\u8bbe\u4e0b\u62db\u8d24\u9986\uff0c\u4e3a\u7684\u5c31\u662f\u62db\u8d24\u7eb3\u58eb\uff0c\u5149\u5927\u672c\u6559\u5b9e\u529b\uff0c\u5cb3\u516c\u5b50\u662f\u767e\u5e74\u96be\u5f97\u4e00\u89c1\u7684\u5c11\u5e74 \u82f1\u96c4\uff0c\u4f60\u5374\u5904\u5fc3\u79ef\u8651\u9677\u5bb3\u4ed6\uff0c\u8981\u628a\u4ed6\u6253\u5165\u6b7b\u7262\uff0c\u50cf\u4f60\u8fd9\u6837\u5751\u6740\u4eba\u624d\uff0c\u4e0d\u662f\u53db\u9006\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u8bf4\u5f97\u4e49\u6b63\u8f9e\u4e25\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u4f7f\u5f97\u8427\u6e58\u96be\u4ee5\u7b54\u8bdd\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u4ed7\u5251\u8d70\u5230\u5927\u95e8\u53e3\uff0c\u6717\u58f0\u9053\uff1a\u201c\u4eca\u5929\u4f60\u4eec\u4e00\u4e2a\u4e2a\u90fd\u522b\u60f3\u6d3b\u7740\u51fa\u53bb\uff0c\u82b1\u67d0\u6740\u4e86\u4f60\u4eec\u8fd9 \u4e9b\u53db\u9006\u4e4b\u540e\uff0c\u7acb\u523b\u5c31\u5230\u603b\u575b\u5411\u6559\u4e3b\u7980\u62a5\u7ecf\u8fc7\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u95e8\u5916\u53c8\u6709\u4eba\u558a\u9053\uff1b\u201c\u5927\u516c\u4e3b\u9a7e\u5230\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u95fb\u8a00\u968f\u5373\u95ea\u8eab\u4e00\u65c1\uff0c\u8fd8\u5251\u5165\u9798\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u81ea\u52a8\u7531\u5ea7\u4f4d\u4e0a\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f46\u89c1\u4e00\u540d\u70df\u53d1\u96fe\u53d1\uff0c\u957f\u88d9\u66f3\u5730\uff0c\u7f8e\u5982\u5929\u4eba\u7684\u5973\u90ce\uff0c\u67ad\u67ad\u5a1c\u5a1c\u7684\u8d70\u8fdb\u5927\u5385\u3002 \u3000\u3000\u5728\u5927\u516c\u4e3b\u8eab\u540e\uff0c\u7d27\u968f\u7740\u4e00\u540d\u5e74\u5728\u4e09\u5341\u5de6\u53f3\uff0c\u7709\u6e05\u76ee\u79c0\uff0c\u7fe9\u7fe9\u5112\u96c5\u770b\u6765\u53c8\u9887\u5bcc\u673a\u667a\u7684\u6587\u58eb \u6a21\u6837\u7684\u7537\u5b50\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u8427\u6e58\u548c\u82b1\u7389\u9e9f\u90fd\u9f50\u8eab\u65bd\u793c\u3002 \u3000\u3000\u5927\u516c\u4e3b\u51b7\u51b7\u626b\u4e86\u5927\u5385\u6240\u6709\u7684\u4eba\u4e00\u773c\u9053\uff1a\u201c\u771f\u60f3\u4e0d\u5230\uff0c\u4f60\u4eec\u7adf\u95f9\u51fa\u7a9d\u91cc\u53cd\uff0c\u81ea\u76f8\u6b8b\u6740\uff0c\u6210 \u4ec0\u4e48\u4f53\u7edf\uff01\u201d \u3000\u3000\u5979\u867d\u7136\u8bed\u6c14\u51b0\u51b7\uff0c\u4f46\u6d3b\u58f0\u5374\u4f9d\u7136\u50cf\u73e0\u6eda\u7389\u76d8\uff0c\u542c\u6765\u60a6\u8033\u6e05\u5fc3\u3002 \u3000\u3000\u8427\u6e58\u8d8b\u524d\u4e00\u6b65\uff0c\u8eac\u8eab\u8bf4\u9053\uff1a\u201c\u7980\u5927\u516c\u4e3b\uff0c\u82b1\u526f\u9986\u4e3b\u516c\u7136\u72af\u4e0a\uff0c \u3000\u3000\u7ed9\u4e86\u5351\u804c\u8fd9\u6837\u5927\u7684\u96be\u582a\uff0c\u671b\u5927\u516c\u4e3b\u5fc5\u987b\u6309\u5f8b\u6cbb\u7f6a\uff0c\u4ee5\u656c\u6548\u5c24\uff0c\u5426\u5219\u5351\u804c\u4eca\u540e\u8fd8\u5982\u4f55\u505a \u4eba\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u5728\u4e2d\u95f4\u5ea7\u6905\u843d\u4e86\u5ea7\uff0c\u6446\u6446\u624b\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u4e3a\u6559\u4e3b\u6548\u529b\uff0c\u4eca\u5929\u7684\u4e8b\uff0c\u8c01\u90fd\u6709\u9519\uff0c \u4e5f\u8c01\u90fd\u6ca1\u9519\uff01\u201d \u3000\u3000\u8427\u6e58\u8138\u8272\u4e00\u70ed\u9053\uff1a\u201c\u4eca\u5929\u7684\u4e8b\uff0c\u5351\u804c\u5fc5\u987b\u8be6\u7ec6\u5411\u5927\u516c\u4e3b\u7980\u62a5\u4e00\u904d\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u53c8\u6446\u6446\u624b\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u6574\u4e2a\u4e8b\u60c5\u7684\u7ecf\u8fc7\uff0c\u65b9\u624d\u516c\u5b59\u5148\u751f\u5df2\u5411\u6211\u8bb2\u8fc7\uff0c\u800c\u4e14\u65b9\u624d \u6211\u5728\u7a97\u5916\u4e5f\u505c\u7559\u4e86\u5f88\u4e45\uff0c\u7b49\u4e8e\u5728\u573a\u4eb2\u773c\u770b\u5230\uff0c\u4f60\u4eec\u90fd\u5750\u4e0b\uff01\u201d \u3000\u3000\u8427\u6e58\u3001\u82b1\u7389\u9e9f\u3001\u5cb3\u5c0f\u98de\u4f9d\u7136\u5750\u5728\u4e00\u65c1\u3002 \u3000\u3000\u90a3\u4f4d\u6587\u58eb\u6a21\u6837\u7684\u4eba\uff0c\u4e5f\u968f\u7740\u843d\u4e86\u5ea7\u3002 \u3000\u3000\u97e9\u5fb7\u8d77\u5219\u8001\u8fdc\u7ad9\u5728\u58c1\u89d2\uff0c\u5434\u6709\u6743\u5472\u7259\u54a7\u5634\u7684\u88ab\u4e24\u540d\u5927\u6c49\u67b6\u4e86 \u3000\u3000\u8d77\u6765\uff0c\u56e0\u4e3a\u4ed6\u4eec\u4e24\u4eba\u8fd8\u4e0d\u591f\u5750\u4e0b\u7684\u8eab\u4efd\u3002 \u3000\u3000\u5927\u516c\u4e3b\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5cb3\u516c\u5b50\uff0c\u4ed6\u8fde\u95ef\u4e94\u5173\u7684\u6587\u624d\u6b66\u529f\uff0c\u79f0\u5f97\u4e0a\u662f\u5f53\u4e16\u5c11\u89c1\uff0c\u8fde\u6559\u4e3b \u90fd\u6709\u610f\u7834\u4f8b\u76f4\u63a5\u53ec\u89c1\u4ed6\uff0c\u8427\u9986\u4e3b\u7adf\u51c6\u5907\u628a\u4ed6\u6253\u5165\u6b7b\u7262\uff0c\u8fd9\u82e5\u8ba9\u6559\u4e3b\u77e5\u9053\uff0c\u4f60\u8fd9\u62db\u8d24\u9986\u4e3b\u8fd8\u60f3 \u5e72\u4e48\uff1f\u201d \u3000\u3000\u8427\u6e58\u7ea2\u7740\u8138\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u5927\u516c\u4e3b\u53c8\u9053\uff1a\u201c\u82b1\u526f\u9986\u4e3b\u521a\u624d\u7684\u4e3e\u52a8\uff0c\u7684\u786e\u662f\u6297\u547d\u72af\u4e0a\uff0c\u6309\u5f8b\u4e5f\u8bb8\u8981\u5904\u4ee5\u6781\u5211\uff0c\u4f46\u4ed6\u662f \u60c5\u6025\u95f4\u8feb\u4e0d\u5f97\u5df2\uff0c\u7528\u5fc3\u4ecd\u662f\u4e3a\u6559\u4e3b\u6548\u5fe0\uff0c \u3000\u3000\u6240\u4ee5\u4ed6\u60c5\u6709\u53ef\u539f\uff0c\u6211\u521a\u624d\u8bf4\u4f60\u4eec\u90fd\u6709\u9519\uff0c\u4e5f\u90fd\u6ca1\u6709\u9519\uff0c\u5c31\u662f\u8fd9\u610f\u601d\u3002\u201d \u3000\u3000\u8427\u6e58\u987f\u4e86\u4e00\u987f\u9053\uff1b\u201c\u53ef\u662f\u8fd9\u5cb3\u516c\u5b50\u64c5\u81ea\u628a\u5973\u4eba\u5e26\u8fdb\u5206\u9986\uff0c\u603b\u662f\u89e6\u72af\u4e86\u6212\u5f8b\uff0c\u5351\u804c\u804c\u8d23\u6240 \u5728\uff0c\u4e0d\u80fd\u4e0d\u7ba1\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u5c31\u7b97\u4ed6\u89e6\u72af\u4e86\u6212\u5f8b\uff0c\u53c8\u4f55\u81f3\u4e8e\u6253\u8fdb\u6b7b\u7262\uff1f\u201d \u3000\u3000\u8427\u6e58\u5e72\u54b3\u4e00\u58f0\u9053\uff1a\u201c\u90a3\u662f\u5927\u516c\u4e3b\u6ca1\u770b\u5230\u4ed6\u5982\u4f55\u7684\u50b2\u6162\u65e0\u793c\uff0c\u5434\u526f\u603b\u7ba1\u53c8\u88ab\u4ed6\u6253\u6210\u4ec0\u4e48\u6837 \u5b50\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u51b7\u51b7\u2014\u7b11\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5148\u68c0\u8ba8\u81ea\u5df1\u5462\uff1f\u4f60\u4eec\u5bf9\u4ed6\u53c8\u662f\u4ec0\u4e48\u6001\u5ea6\uff1f\u8981\u77e5\u9053\u62db\u8d24 \u9986\u662f\u62db\u8d24\u7eb3\u58eb\u7684\uff0c\u4f60\u4eec\u9996\u5148\u5fc5\u987b\u793c\u8d24\u4e0b\u58eb\uff0c\u4ece\u524d\u5218\u90a6\u547d\u8427\u4f55\u7acb\u62db\u8d24\u9986\uff0c\u97e9\u4fe1\u6155\u540d\u800c\u6765\uff0c\u8427\u4f55 \u5f53\u65f6\u8eab\u4e3a\u9996\u76f8\uff0c\u672a\u66fe\u4e0b\u4f4d\u76f8\u8fce\uff0c\u97e9\u4fe1\u7acb\u523b\u51c6\u5907\u62c2\u8896\u800c\u53bb\uff0c\u4f7f\u5f97\u8427\u4f55\u4e0d\u5f97\u4e0d\u6539\u5bb9\u76f8\u89c1\uff0c\u8fd9\u6bb5\u6545 \u4e8b\u4f60\u5e94\u5f53\u77e5\u9053\u624d\u5bf9\uff1f\u201d \u3000\u3000\u8427\u6e58\u8d6b\u7136\u8eac\u8eab\u7b54\u9053\uff1a\u201c\u5351\u804c\u77e5\u9053\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u5f53\u521d\u672c\u6559\u6210\u7acb\u62db\u8d24\u9986\uff0c\u6559\u4e3b\u5c31\u662f\u56e0\u4e3a\u4f60\u4e5f\u59d3\u8427\uff0c \u3000\u3000\u5e0c\u671b\u4f60\u80fd\u6709\u4e43\u7956\u4e4b\u98ce\uff0c\u6240\u4ee5\u624d\u6d3e\u4f60\u62c5\u4efb\u9986\u4e3b\uff0c\u4f60\u4e0d\u59a8\u68c0\u8ba8\u4e00\u4e0b\uff0c\u662f\u5426\u6709\u534a\u70b9\u50cf\u5f53\u5e74\u7684\u8427 \u4f55\uff1f\u201d \u3000\u3000\u8427\u6e58\u6c57\u989c\u7b54\u9053\uff1a\u201c\u5351\u804c\u9a7d\u9a80\u5eb8\u9c81\uff0c\u600e\u6562\u548c\u655d\u5148\u7956\u76f8\u63d0\u5e76\u8bba\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u65e2\u7136\u81ea\u77e5\u60ed\u6127\uff0c\u4e5f\u8bb8\u4ee5\u540e\u4e0d\u96be\u6539\u8fdb\uff0c\u4f46\u4f60\u7684\u624b\u4e0b\u4eba\u5374\u826f\u83a0\u4e0d\u9f50\uff0c\u8fde\u6211\u90fd \u7ecf\u5e38\u542c\u5230\u4ed6\u7684\u52a3\u8ff9\uff0c\u5cb3\u516c\u5b50\u6253\u4e86\u4ed6\uff0c\u4f9d\u6211\u770b\u6765\uff0c\u5df2\u7ecf\u7b97\u662f\u624b\u4e0b\u7559\u60c5\u4e86\uff01\u201d \u3000\u3000\u8427\u6e58\u8eac\u8eab\u8fde\u58f0\u9053\uff1a\u201c\u5927\u516c\u4e3b\u8bf4\u5f97\u5bf9\uff0c\u5351\u804c\u4ee5\u540e\u5bf9\u4e0b\u5c5e\u4e00\u5b9a\u52e4\u52a0\u7763\u5bfc\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u6211\u60f3\u518d\u4e3e\u4e00\u4f8b\uff0c\u5f53\u5e74\u5218\u5907\u4e3a\u4e86\u8bbf\u8d24\uff0c\u4e0d\u60dc\u4e09\u987e\u8305\u5e90\uff0c\u76f8\u8bf7\u8bf8\u845b\u4eae\u51fa\u5c71\uff0c\u73b0 \u5728\u5462\uff1f\u8d24\u58eb\u81ea\u5df1\u9001\u5230\u95e8\u4e0a\uff0c\u4f60\u4eec\u53cd\u800c\u76f8\u5e94\u4e0d\u7406\uff0c\u4f60\u4eec\u4f55\u6b62\u8be5\u60ed\u6127\uff0c\u7b80\u76f4\u8be5\u4e00\u5934\u649e\u6b7b\uff01\u201d \u3000\u3000\u8427\u6e58\u54ea\u91cc\u8fd8\u80fd\u8bf4\u4ec0\u4e48\uff0c\u8bb8\u4e45\uff0c\u624d\u52c9\u5f3a\u8fdb\u51fa\u4e00\u53e5\u6765\u9053\uff1a\u201c\u90a3\u4e48\u5cb3\u516c\u5b50\u5e26\u5973\u4eba\u8fdb\u5206\u9986\u7684\u4e8b\uff0c \u5351\u804c\u5c31\u4e0d\u518d\u8ffd\u7a76\u4e86\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u53ea\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u82e5\u88ab\u6559\u4e3b\u5f97\u77e5\uff0c\u5351\u804c\u603b\u662f\u96be\u8131\u4e0d\u5c3d\u804c\u4e4b\u5acc\u3002\u201d \u3000\u3000\u201c\u4f60\u65e9\u5c31\u4e0d\u5c3d\u804c\u4e86\uff01\u201d \u3000\u3000\u201c\u5927\u516c\u4e3b\u8fd9\u8bdd\uff0c\u5351\u804c\u6709\u4e9b\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u201c\u6211\u4e14\u95ee\u4f60\uff0c\u738b\u5b50\u72af\u6cd5\u662f\u5426\u4e0e\u6c11\u540c\u7f6a\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u5f53\u7136\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u54fc\u4e86\u4e00\u58f0\u9053\uff1b\u201c\u90a3\u4e48\u6211\u7ecf\u5e38\u5230\u4f60\u62db\u8d24\u9986\u6765\uff0c\u6211\u4e5f\u662f\u5973\u4eba\uff0c \u3000\u3000\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u7980\u62a5\u6559\u4e3b\u628a\u6211\u6cbb\u7f6a\u201d \u3000\u3000\u8427\u6e58\u5446\u4e86\u4e00\u5446\uff0c\u6839\u672c\u7b54\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u65e2\u7136\u4e0d\u6562\u62ff\u6211\u6cbb\u7f6a\uff0c\u5374\u8981\u62ff\u522b\u4eba\u6cbb\u7f6a\uff0c\u8fd9\u5c31\u662f\u4e0d\u516c\uff0c\u4e5f\u770b\u51fa\u4f60\u505a\u4e0d\u5230\u4e0d \u754f\u6743\u52bf\u8fd9\u56db\u4e2a\u5b57\uff0c\u6211\u7684\u8bdd\u4e0d\u4f1a\u51a4\u6789\u4f60\u5427\uff1f\u201d \u3000\u3000\u8427\u6e58\u8138\u8272\u6709\u5982\u732a\u809d\uff0e\u55eb\u5685\u8bf4\u9053\uff1a\u201c\u5927\u516c\u4e3b\u8bf4\u5f97\u5bf9\uff01\u8bf4\u5f97\u5bf9\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u76f4\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u5973\u4eba\u4e0d\u51c6\u8fdb\u5165\u62db\u8d24\u9986\u867d\u662f\u6559\u4e3b\u89c4\u5b9a\u7684\uff0c\u4f46\u6211\u5374\u4e0d\u8ba4\u4e3a\u5bf9\uff0c\u5973\u4eba\u4e5f \u662f\u4eba\uff0c\u4e3a\u4ec0\u4e48\u5c31\u4e0d\u80fd\u548c\u7537\u4eba\u5e73\u7b49\uff0c\u7b49\u6709\u4e86\u673a\u4f1a\uff0c\u6211\u5b9a\u8981\u5411\u6559\u4e3b\u5efa\u8bae\uff0c\u8bf7\u6559\u4e3b\u53d6\u6d88\u8fd9\u9879\u4e0d\u5408\u7406 \u7684\u89c4\u5b9a\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u628a\u8427\u6e58\u8bad\u4e86\u8001\u534a\u5929\uff0c\u4f3c\u4e4e\u4e5f\u6709\u4e9b\u8fc7\u610f\u4e0d\u53bb\uff0c\u6700\u540e\u653e\u7f13\u8bed\u6c14\u9053\uff1a\u201c\u8427\u9986\u4e3b\uff0c\u6211\u521a\u624d \u8fd9\u756a\u8bdd\uff0c\u4e5f\u8bb8\u8bf4\u91cd\u4e86\u4e9b\uff0c\u4f46\u5374\u5b8c\u5168\u662f\u51fa\u81ea\u80ba\u8151\u4e4b\u8a00\uff0c\u5e0c\u671b\u4f60\u5343\u4e07\u4e0d\u8981\u4ecb\u610f\uff01\u201d \u3000\u3000\u8427\u6e58\u8fde\u8fde\u9894\u9996\u9053\uff1a\u201c\u5927\u516c\u4e3b\u6559\u8bad\u5f97\u662f\uff0c\u5351\u804c\u611f\u6fc0\u8fd8\u6765\u4e0d\u53ca\uff0c\u54ea\u91cc\u8fd8\u6562\u4ecb\u610f\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u4e0d\u613f\u518d\u4f7f\u8427\u6e58\u96be\u582a\uff0c\u76ee\u5149\u8fd9\u624d\u79fb\u5411\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u4f60\u80fd\u51ed\u771f\u672c\u4e8b\u8fde\u95ef\u4e94\u5173\uff0c \u5728\u6765\u5230\u5929\u8c37\u7684\u51e0\u767e\u4eba\u5f53\u4e2d\uff0c\u524d\u9762\u53ea\u6709\u4e09\u4eba\u582a\u4e0e\u4f60\u76f8\u6bd4\uff0c\u5728\u573a\u7684\u82b1\u526f\u9986\u4e3b\u4fbf\u662f\u4e00\u4f4d\uff0c\u4f46\u4ed6\u4eec\u7684 \u5e74\u7eaa\u5374\u90fd\u6bd4\u4f60\u5927\u5f97\u591a\uff0c\u8fd9\u6837\u770b\u6765\uff0c\u4f60\u5c06\u6765\u7684\u6210\u5c31\uff0c\u4e00\u5b9a\u6bd4\u4ed6\u4eec\u66f4\u5927\uff0c\u8fde\u6211\u4e5f\u4e0d\u5f97\u4e0d\u4f69\u670d\u4f60\u662f \u4f4d\u5c11\u5e74\u5947\u624d\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fde\u5fd9\u4e5f\u8eac\u8eab\u9053\uff1a\u201c\u5927\u516c\u4e3b\u8fc7\u5956\uff0c\u665a\u8f88\u6127\u4e0d\u6562\u5f53\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8fc7\u8c26\uff0c\u56de\u5230\u5206\u9986\u540e\uff0c\u8010\u5fc3\u5f85\u547d\uff0c\u6559\u4e3b\u51b3\u4e0d\u4f1a\u8f9c\u8d1f\u4f60\u8fd9\u4f4d\u4eba\u624d\uff0c\u82e5\u6709 \u4ec0\u4e48\u8981\u6c42\uff0c\u73b0\u5728\u5c3d\u7ba1\u5bf9\u6211\u8bb2\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6b63\u8272\u9053\uff1a\u201c\u665a\u8f88\u6240\u4f4f\u7684\u5206\u9986\uff0c\u662f\u4e00\u540d\u53eb\u6797\u91d1\u5b9d\u7684\u5e74\u8f7b\u4eba\u8d1f\u8d23\u770b\u5b88\uff0c\u665a\u8f88\u5e0c\u671b\u4ed6\u4e0d \u8981\u53d7\u5230\u4efb\u4f55\u5904\u5206\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u539f\u6765\u53ea\u662f\u8fd9\u4e48\u70b9\u5c0f\u4e8b\uff0c\u8fde\u4f60\u90fd\u6ca1\u6709\u4e8b\uff0c\u4ed6\u5f53\u7136\u66f4\u4e0d\u4f1a\u6709\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u53ef\u662f\u603b\u7ba1\u65b9\u9762\uff0c\u4e5f\u8bb8\u4f1a\u6709\u4eba\u5bf9\u4ed6\u8fc7\u4e0d\u53bb\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u77a5\u4e86\u8427\u6e58\u4e00\u773c\u9053\uff1a\u201c\u8427\u9986\u4e3b\u542c\u7740\uff0c\u6211\u5df2\u7ecf\u7b54\u5e94\u5cb3\u516c\u5b50\u4e86\uff0c\u82e5\u4f60\u7684\u624b\u4e0b\u4eba\u6709\u6562\u5bf9\u90a3 \u59d3\u6797\u7684\u5e74\u8f7b\u4eba\u51cc\u8fb1\u8d23\u7f5a\u7684\uff0c\u6211\u5c31\u4ee5\u6297\u547d\u8bba\u5904\uff01\u201d \u3000\u3000\u8427\u6e58\u82e6\u7b11\u9053\uff1a\u201c\u5927\u516c\u4e3b\u7684\u547d\u4ee4\uff1f\u8c01\u6562\u4e0d\u9075\uff0c\u4ed6\u4eec\u90a3\u513f\u4e5f\u6ca1\u957f\u4e24\u4e2a\u8111\u888b\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u957f\u957f\u5401\u4e00\u53e3\u6c14\u9053\uff1b\u201c\u6211\u5f88\u4e3a\u4f60\u4eec\u4eca\u5929\u9986\u4e3b\u526f\u9986\u4e3b\u53cd\u76ee\u76f8\u5411\u7684\u4e8b\u611f\u5230\u9057\u61be\uff1f\u82e5\u81ea\u5bb6 \u4eba\u4e0d\u5408\uff0c\u5c06\u662f\u6211\u4eec\u7687\u5143\u6559\u7684\u5927\u4e0d\u5e78\uff01\u201d \u3000\u3000\u5979\u8bf4\u7740\u4ea4\u4ee3\u90a3\u6587\u58eb\u6a21\u6837\u7684\u4eba\u9053\uff1a\u201c\u516c\u5b59\u5148\u751f\u8bf7\u901a\u77e5\u53a8\u623f\uff0c\u4e2d\u5348\u529e\u4e00\u684c\u9152\u5e2d\uff0c\u6211\u51b3\u5b9a\u7559\u5728 \u8fd9\u91cc\u4f5c\u966a\uff0c\u628a\u9152\u8a00\u6b22\uff0c\u4e3a\u4f60\u4eec\u4e24\u5bb6\u548c\u597d\uff01\u201d \u3000\u3000\u63a5\u7740\u518d\u671b\u5411\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u8fd9\u5c31\u56de\u5206\u9986\u53bb\uff0c\u5c31\u5728\u8fd9\u4e24\u5929\uff0c\u6211\u4f1a\u6d3e\u516c\u5b59\u5148\u751f\u53bb\u770b\u4f60\uff0c \u5982\u679c\u6709\u4ec0\u4e48\u8981\u6c42\uff0c\u5c3d\u7ba1\u63d0\u51fa\u6765\uff0c\u516c\u5b59\u5148\u751f\u8f6c\u8fbe\u6211\u4e4b\u540e\uff0c\u6211\u4e00\u5b9a\u4e3a\u4f60\u89e3\u51b3\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u56de\u5230\u4e09\u5341\u516d\u53f7\u5206\u9986\uff0c\u6797\u91d1\u5b9d\u6b63\u5750\u5728\u5927\u95e8\u5916\u53d1\u5446\u3002 \u3000\u3000\u731b\u89c1\u5cb3\u5c0f\u98de\u5e73\u5b89\u65e0\u4e8b\u7684\u56de\u6765\uff0c\u6797\u91d1\u5b9d\u8fd8\u771f\u4ee5\u4e3a\u662f\u5728\u505a\u68a6\u3002 \u3000\u3000\u4ed6\u6025\u6025\u7ad9\u4e86\u8d77\u6765\uff0c\u60ca\u559c\u7684\u95ee\u9053\uff1a\u201c\u516c\u5b50\uff0c\u60a8\u771f\u7684\u6ca1\u4e8b\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u9884\u6599\u4e0d\u4f1a\u6709\u4e8b\uff0c\u4f60\u600e\u4e48\u8fd8\u5728\u5927\u60ca\u5c0f\u602a\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u5c34\u5c2c\u7b11\u9053\uff1a\u201c\u6ca1\u4e8b\u5c31\u597d\uff0c\u5c0f\u7684\u5c31\u7528\u4e0d\u7740\u518d\u62c5\u5fc3\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u671b\u4e86\u671b\u5929\u8272\u9053\uff1a\u201c\u4f60\u8be5\u5230\u603b\u575b\u62ff\u996d\u4e86\uff01\u201d \u3000\u3000\u6797\u91d1\u5b9d\u7acb\u523b\u53c8\u6709\u4e86\u60ca\u6050\u4e4b\u8272\uff1a\u201c\u5982\u679c\u5c0f\u7684\u88ab\u2026\u2026\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u7528\u4e0d\u7740\u5bb3\u6015\uff0c\u4e00\u5207\u90fd\u5305\u5728\u6211\u8eab\u4e0a\uff0c\u7edd\u6ca1\u4eba\u6562\u6b3a\u8d1f\u4f60\u3002\u201d \u3000\u3000\u6797\u91d1\u5b9d\u53ea\u662f\u534a\u500d\u534a\u7591\uff0c\u4f46\u53c8\u4e0d\u80fd\u4e0d\u53bb\u62ff\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7b49\u6797\u91d1\u5b9d\u8d70\u540e\uff0c\u4e00\u4e2a\u4eba\u5c31\u5728\u5ba2\u5385\u559d\u8d77\u8336\u6765\u3002 \u3000\u3000\u60f3\u8d77\u65b9\u624d\u5728\u603b\u9986\u53d1\u751f\u7684\u4e8b\uff0c\u603b\u611f\u5230\u592a\u7a81\u5140\uff0c\u800c\u4e14\u6709\u4e9b\u5730\u65b9\u4e5f\u51fa\u4eba\u60f3\u50cf\u3002 \u3000\u3000\u4ed6\u60f3\u5230\u7236\u4eb2\u4e3a\u4e86\u81ea\u5df1\uff0c\u7adf\u4e0d\u60dc\u548c\u8427\u6e58\u95f9\u7ffb\uff0c\u751a\u81f3\u5175\u620e\u76f8\u5411\uff0c\u4f3c\u4e4e\u8fc7\u4e8e\u51b2\u52a8\u800c\u53c8\u6b20\u7406\u667a\uff0c \u53ca\u81f3\u5927\u516c\u4e3b\u5230\u6765\uff0c\u4ed6\u672c\u6765\u4ee5\u4e3a\u7236\u4eb2\u5fc5\u4f1a\u53d7\u5230\u4e25\u5386\u8bad\u65a5\uff0c\u751a\u81f3\u4f1a\u5f53\u573a\u6253\u5165\u6b7b\u7262\uff0c\u56e0\u4e3a\u6309\u7167\u89c4\u5f8b\uff0c \u8eab\u5728\u9ad8\u4f4d\u7684\u4eba\uff0c\u603b\u662f\u4f1a\u5b98\u5b98\u76f8\u62a4\uff0c\u51e1\u662f\u4e0b\u7ea7\u548c\u4e0a\u7ea7\u8d77\u4e86\u51b2\u7a81\uff0c\u4e00\u65e6\u67e5\u7a76\u8d77\u6765\uff0c\u5403\u4e8f\u7684\u5fc5\u662f\u4e0b \u7ea7\u3002 \u3000\u3000\u4f46\u4eca\u5929\u504f\u504f\u4e8b\u5b9e\u4e0d\u7136\uff0c\u5927\u516c\u4e3b\u5230\u6765\u540e\uff0c\u53ea\u8bad\u65a5\u8427\u6e58\uff0c\u534a\u70b9\u4e0d\u602a\u7f6a\u7236\u4eb2\uff0c\u8fd9\u5c82\u4e0d\u662f\u51fa\u4e8e\u5e38 \u7406\u4e4b\u5916\u3002 \u3000\u3000\u6b63\u56e0\u5982\u6b64\uff0c\u4ed6\u5bf9\u5927\u516c\u4e3b\u7684\u4e3a\u4eba\uff0c\u81ea\u4e00\u89c1\u9762\u8d77\uff0c\u4fbf\u656c\u4f69\u4e0d\u5df2\uff0c\u7687\u5143\u6559\u7684\u9ad8\u9636\u5c42\u5c45\u7136\u80fd\u51fa\u73b0 \u5982\u6b64\u8fbe\u60c5\u660e\u7406\u7684\u4eba\uff0c\u5b9e\u5728\u662f\u4ef6\u96be\u5f97\u7684\u4e8b\u3002 \u3000\u3000\u534a\u4e2a\u65f6\u8fb0\u4e4b\u540e\uff0e\u6797\u91d1\u5b9d\u56de\u6765\u4e86\uff0c\u4ed6\u53bb\u65f6\u662f\u5fc3\u60ca\u80c6\u98a4\uff0c\u4e00\u8138\u60f6\u6050\uff0c\u6b64\u523b\u56de\u6765\uff0c\u5219\u662f\u559c\u6c14\u6d0b \u6d0b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6545\u610f\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u603b\u9986\u7684\u4eba\u6709\u6ca1\u6709\u6b3a\u8d1f\u4f60\u7684\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u6447\u7740\u5934\u9053\uff1a\u201c\u771f\u5947\u602a\uff0c\u4ed6\u4eec\u5bf9\u5c0f\u7684\u4e0d\u4f46\u6ca1\u6b3a\u8d1f\uff0c\u53cd\u800c\u683c\u5916\u5ba2\u6c14\uff0c\u800c\u4e14\u4ece\u6765\u6ca1\u6709\u8fd9 \u6837\u4eb2\u5207\u8fc7\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u90a3\u5c31\u597d\u4e86\uff01\u201d \u3000\u3000\u6797\u91d1\u5b9d\u6253\u5f00\u98df\u76d2\uff0c\u7b11\u7740\u53c8\u9053\uff1a\u201c\u8fd8\u6709\uff0c\u4eca\u5929\u7684\u83dc\u4e5f\u7279\u522b\u597d\uff0c\u8fd8\u6709\u9152\u5462\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u91cc\u6709\u6570\uff0c\u8fd9\u662f\u56e0\u4e3a\u4e2d\u5348\u529e\u4e86\u4e00\u684c\u9152\u98df\uff0c\u65e0\u5f62\u4e2d\u81ea\u5df1\u4e5f\u6cbe\u4e86\u5149\u3002 \u3000\u3000\u5403\u996d\u65f6\uff0c\u5cb3\u5c0f\u98de\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u542c\u8bf4\u6709\u4f4d\u5927\u516c\u4e3b\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u7687\u5143\u6559\u91cc\u8c01\u4e0d\u77e5\u9053\u5927\u516c\u4e3b\uff0c\u8fde\u5c0f\u7684\u6709\u4e00\u6b21\u8fd8\u5728\u603b\u9986\u89c1\u8fc7\u5979\u5462\uff0c\u5927\u516c\u4e3b\u957f\u5f97 \u771f\u50cf\u4ed9\u5973\u4e0b\u51e1\u4e00\u822c\uff0c\u516c\u5b50\u4e3a\u4ec0\u4e48\u5ffd\u7136\u95ee\u8d77\u8fd9\u4e2a\u6765\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u4eca\u5929\u4e5f\u5728\u603b\u9986\u9047\u89c1\u8fc7\u5979\uff0c\u5927\u516c\u4e3b\u8be5\u662f\u7687\u4e0a\u7684\u5343\u91d1\uff0c\u8fd9\u91cc\u6ca1\u6709\u7687\u4e0a\uff0c\u54ea\u91cc \u6765\u7684\u5927\u516c\u4e3b\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u7687\u4e0a\uff0c\u5c31\u662f\u6559\u4e3b\uff0c\u6559\u4e3b\u81ea\u79f0\u7687\u5143\u6559\u4e3b\uff0c \u3000\u3000\u4e0d\u662f\u7687\u4e0a\u662f\u4ec0\u4e48\uff1f\u6559\u4e3b\u7684\u5927\u5343\u91d1\u5c31\u662f\u5927\u516c\u4e3b\uff0c\u8c01\u90fd\u8ba4\u4e3a\u6ca1\u4ec0\u4e48\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u201c\u5927\u516c\u4e3b\u5e38\u5e38\u5230\u603b\u9986\u6765\u5750\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u4e5f\u611f\u5230\u5947\u602a\uff0c\u542c\u8bf4\u5927\u516c\u4e3b\u5f88\u5c11\u5230\u522b\u7684\u5730\u65b9\u53bb\uff0c\u504f\u504f\u5979\u5e38\u5e38\u559c\u6b22\u5230\u62db\u8d24\u9986\u6765\uff0c\u771f\u4e0d \u77e5\u662f\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u60f3\u8d77\u5927\u516c\u4e3b\u66fe\u8bf4\u8981\u6d3e\u516c\u5b59\u5148\u751f\u6765\u770b\u81ea\u5df1\uff0c\u8d81\u673a\u518d\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u6709\u4f4d\u516c\u5b59\u5148\u751f\uff0c\u4f60 \u53ef\u77e5\u9053\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u5728\u62db\u8d24\u9986\uff0c\u8c01\u4e0d\u77e5\u9053\u516c\u5b59\u5148\u751f\uff0c\u4ed6\u662f\u62db\u8d24\u9986\u7684\u519b\u5e08\uff0c\u5b66\u95ee\u53ef\u597d\u5f97\u5f88\u5462\uff01\u8fde \u9986\u4e3b\u90fd\u4e0d\u5f97\u4e0d\u656c\u4ed6\u4e09\u5206\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u6211\u5728\u603b\u9986\uff0e\u770b\u5230\u4ed6\u548c\u5927\u516c\u4e3b\u8d70\u5728\u4e00\u8d77\uff0c\u53ea\u9053\u4ed6\u662f\u5927\u516c\u4e3b\u8eab\u8fb9\u7684\u4eba \u5462\uff01\u201d \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u8bf4\u516c\u5b59\u5148\u751f\u662f\u5927\u516c\u4e3b\u8eab\u8fb9\u7684\u4eba\u4e5f\u4e0d\u4e3a\u8fc7\uff0c\u56e0\u4e3a\u62db\u8d24\u9986\u8d1f\u8d23\u548c\u603b\u575b\u65b9\u9762\u5954\u8d70 \u8054\u7cfb\u7684\uff0c\u5c31\u662f\u516c\u5b59\u5148\u751f\uff0c\u4ed6\u548c\u603b\u575b\u65b9\u5411\u7684\u6bcf\u4e00\u4e2a\u4eba\u90fd\u5f88\u719f\uff0c\u5f53\u7136\u548c\u5927\u516c\u4e3b\u8d70\u5f97\u5f88\u8fd1\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u770b\u6765\uff0c\u516c\u5b59\u5148\u751f\u7b97\u662f\u62db\u8d24\u9986\u7684\u7ea2\u4eba\u4e86\uff1f\u201d \u3000\u3000\u201c\u90a3\u8fd8\u7528\u8bb2\uff0c\u521a\u624d\u5c0f\u7684\u4e0d\u662f\u8bf4\u8fc7\uff0c\u8fde\u9986\u4e3b\u90fd\u656c\u4ed6\u4e09\u5206\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u5728\u603b\u575b\u53c8\u4f1a\u89c1\u4e86\u82b1\u526f\u9986\u4e3b\uff0c\u82b1\u526f\u9986\u4e3b\u4e3a\u4eba\u5982\u4f55\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u5ffd\u7136\u7ad6\u8d77\u5927\u62c7\u6307\u9053\uff1a\u201c\u4eba\u5bb6\u82b1\u526f\u9986\u4e3b\u624d\u771f\u662f\u4e00\u4f4d\u4e86\u4e0d\u8d77\u7684\u4eba\u7269\uff0c\u542c\u8bf4\u4ed6\u4e09\u5e74\u524d\u95ef \u4e94\u5173\u65f6\uff0c\u5206\u6570\u6700\u9ad8\uff0c\u5b8c\u5168\u51ed\u771f\u672c\u4e8b\uff0c\u6240\u4ee5\u664b\u5347\u5f97\u4e5f\u666f\u5feb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1b\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u4ed6\u505a\u62db\u8d24\u9986\u4e3b\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u90a3\u603b\u8981\u51ed\u4e00\u70b9\u8d44\u5386\uff0c\u73b0\u5728\u7684\u9986\u4e3b\uff0c\u662f\u7687\u5143\u6559\u7684\u8001\u4eba\uff0c\u8ddf\u968f\u6559\u4e3b\u591a\u5e74\uff0c\u6559\u4e3b \u5f53\u7136\u4e0d\u4f1a\u8ba9\u82b1\u526f\u9986\u4e3b\u4e00\u4e0b\u5b50\u5c31\u722c\u5230\u4ed6\u7684\u5934\u4e0a\uff0c\u5176\u5b9e\u4e0d\u8bba\u6587\u624d\u548c\u6b66\u529f\uff0c\u4ed6\u90fd\u6bd4\u82b1\u526f\u9986\u4e3b\u5dee\u5f97 \u8fdc\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e24\u4eba\u662f\u5426\u76f8\u5904\u5f97\u5f88\u878d\u6d3d\uff1f\u201d \u3000\u3000\u201c\u5f88\u96be\u8bf4\uff0c\u8868\u9762\u4e0a\u8c01\u4e5f\u770b\u4e0d\u51fa\u4ec0\u4e48\uff0c\u81f3\u4e8e\u5230\u5e95\u600e\u4e48\u6837\uff0c\u53ea\u6709\u4ed6\u4eec\u4e24\u4eba\u5fc3\u91cc\u6709\u6570\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u5230\u603b\u9986\u62ff\u996d\uff0c\u53ef\u89c1\u8fc7\u97e9\u603b\u7ba1\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u5ffd\u7136\u8138\u8272\u4e00\u53d8\uff0c\u4f4e\u4e0b\u58f0\u97f3\u9053\uff1a\u201c\u516c\u5b50\uff0c\u542c\u8bf4\u97e9\u603b\u7ba1\u53c8\u88ab\u4eba\u6253\u4e86\uff0c\u662f\u4e0d\u662f\u53c8\u88ab\u60a8\u6253 \u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b11\u9053\uff1a\u201c\u6211\u600e\u4f1a\u5230\u5904\u6253\u4eba\uff0c\u4f55\u51b5\u627e\u5230\u4e86\u603b\u9986\uff0c\u81ea\u8eab\u96be\u4fdd\uff0c\u54ea\u91cc\u8fd8\u6562\u6253\u4eba\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u54a7\u4e86\u54a7\u5634\u9053\uff1a\u201c\u5c0f\u7684\u4e00\u76f4\u611f\u5230\u5947\u602a\uff0c\u60a8\u628a\u5434\u526f\u603b\u7ba1\u6253\u6210\u90a3\u6837\u5b50\uff0c\u7adf\u7136\u4e00\u70b9\u6ca1\u4e8b\u7684 \u5e73\u5b89\u56de\u6765\uff0c\u5b9e\u5728\u4e0d\u7b80\u5355\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b11\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u5947\u602a\u7684\uff0c\u516c\u9053\u81ea\u5728\u4eba\u5fc3\uff0c\u53ea\u8981\u6211\u6253\u5f97\u5bf9\uff0c\u8c01\u90fd\u4e0d\u4f1a\u8ba4\u4e3a\u6709\u4e8b\u3002\u201d \u3000\u3000\u6797\u91d1\u5b9d\u5472\u7259\u9053\uff1a\u201c\u90a3\u592a\u5bf9\u4e86\uff0c\u771f\u662f\u5927\u5feb\u4eba\u5fc3\uff0c\u4e0d\u8fc7\u5c0f\u7684\u771f\u6ca1\u6599\u5230\u516c\u5b50\u7684\u6b66\u529f\u7adf\u7136\u9ad8\u5f97\u8fd9 \u6837\u51fa\u5947\uff0c\u5434\u526f\u603b\u7ba1\u7684\u6b66\u529f\u4e5f\u4e0d\u7b97\u5dee\uff0c\u9047\u5230\u516c\u5b50\uff0c\u5374\u8fde\u8fd8\u624b\u7684\u529b\u91cf\u90fd\u6ca1\u6709\uff0c\u6bd4\u8d77\u516c\u5b50\u6765\uff0c\u4ed6\u7b80 \u76f4\u5929\u4e0a\u5dee\u5230\u5730\u4e0b\u53bb\u4e86\uff01\u201d \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u5341\u4e8c\u56de\u3000\u4e49\u6551\u6843\u82b1 \u3000\u3000\u53c8\u8fc7\u4e86\u4e00\u665a\u3002 \u3000\u3000\u6b64\u523b\u5cb3\u5c0f\u98de\u6700\u76fc\u671b\u7684\uff0c\u4fbf\u662f\u90a3\u4f4d\u516c\u5b59\u5148\u751f\u80fd\u5feb\u4e9b\u6765\uff0c\u81f3\u5c11\uff0c\u4ed6\u5e0c\u671b\u80fd\u77e5\u9053\u7236\u4eb2\u8fd9\u4e00\u5929\u6765 \u7684\u8fd1\u51b5\u3002 \u3000\u3000\u504f\u504f\u76f4\u5230\u5929\u5c06\u508d\u665a\uff0c\u4f9d\u7136\u4e0d\u89c1\u516c\u5b59\u5148\u751f\u5230\u6765\u3002 \u3000\u3000\u5728\u6797\u91d1\u5b9d\u5230\u603b\u9986\u62ff\u996d\u65f6\uff0c\u4ed6\u4e00\u4e2a\u4eba\u53c8\u6765\u5230\u5206\u9986\u95e8\u5916\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4ed6\u5df2\u4e0d\u5728\u4e4e\u4e0d\u5f97\u8d85\u51fa\u5206\u9986\u4e09\u5341\u6b65\u7684\u89c4\u5b9a\uff0c\u4fe1\u6b65\u76f4\u5411\u95e8\u524d\u5c0f\u8def\u4e0a\u8d70\u53bb\u3002 \u3000\u3000\u7a81\u7136\uff0c\u53c8\u662f\u4e00\u540d\u7ea2\u8863\u5c11\u5973\u8fdc\u8fdc\u8fce\u9762\u800c\u6765\uff0c\u89e6\u76ee\u4e4b\u4e0b\uff0c\u53ea\u89c9\u4e5f\u6709\u70b9\u4f3c\u66fe\u76f8\u8bc6\uff0c \u3000\u3000\u90a3\u7ea2\u8863\u5c11\u5973\u5f88\u5feb\u4fbf\u5230\u8ddf\u524d\uff1f \u3000\u3000\u5f53\u5730\u53d1\u73b0\u5cb3\u5c0f\u98de\uff0c\u4e0d\u89c9\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u5cb3\u516c\u5b50\u5417\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u201c\u54e6\u201d\u4e86\u58f0\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u662f\u706b\u5173\u4e0a\u7684\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5a62\u5b50\u53eb\u73ab\u7470\uff0c\u771f\u5de7\uff0c\u6211\u672c\u6765\u5c31\u60f3\u627e\u516c\u5b50\uff0c\u4e0d\u60f3\u4e00\u6765\u5c31\u78b0\u4e0a \u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u832b\u7136\u9053\uff1a\u201c\u73ab\u7470\u59d1\u5a18\u627e\u6211\u6709\u4e8b\uff1f\u201d \u3000\u3000\u73ab\u7470\u9053\uff1a\u201c\u5a62\u5b50\u662f\u5949\u547d\u5230\u603b\u575b\u5448\u9001\u6587\u4ef6\u7684\uff0c\u53d7\u6843\u82b1\u59d0\u59d0\u6240\u6258\uff0c\u66ff\u5979\u5e26\u4e2a\u4fe1\u513f\u7ed9\u516c\u5b50\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u5934\u4e00\u9707\u9053\uff1a\u201c\u83ab\u975e\u5979\u51fa\u4e86\u4e8b\uff1f\u201d \u3000\u3000\u73ab\u7470\u9053\uff1a\u201c\u539f\u6765\u516c\u5b50\u4e5f\u60f3\u5230\u5979\u4f1a\u51fa\u4e8b\uff0c\u5979\u771f\u7684\u51fa\u4e8b\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u7531\u5927\u4e3a\u5173\u5207\uff0c\u6025\u6025\u95ee\u9053\uff1a\u201c\u5979\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff0c\u5feb\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u73ab\u7470\u795e\u8272\u51c4\u7136\uff0c\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5979\u4e24\u5929\u524d\u662f\u4e0d\u662f\u5230\u5206\u9986\u6765\u548c\u516c\u5b50\u89c1\u8fc7\u9762\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u5979\u662f\u5426\u544a\u8bc9\u8fc7\u516c\u5b50\u5979\u4e0d\u613f\u7559\u5728\u706b\u5173\uff0c\u8bf7\u516c\u5b50\u5e2e\u5fd9\u628a\u5979\u8c03\u5230\u8fd9\u8fb9\u6765\uff1f\u201d \u3000\u3000\u201c\u5979\u786e\u66fe\u8fd9\u6837\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c31\u5bf9\u4e86\uff0c\u8fd9\u4e8b\u5c45\u7136\u88ab\u7edf\u9886\u77e5\u9053\uff0c\u7edf\u9886\u4e00\u6012\u4e4b\u4e0b\uff0c\u5f53\u573a\u628a\u5979\u6253\u4e2a\u534a\u6b7b\uff0c\u7136\u540e\u5173\u5728\u4e00\u95f4 \u77f3\u5ba4\u91cc\uff0c\u6bcf\u9910\u53ea\u51c6\u5979\u5403\u2014\u7f50\u76d0\u6c34\u996d\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u542c\u5f97\u4e00\u9635\u5fc3\u75db\uff0c\u6025\u6025\u518d\u95ee\u9053\uff1a\u201c\u8fd9\u4e8b\u662f\u4ec0\u4e48\u65f6\u5019\u53d1\u751f\u7684\uff1f\u201d \u3000\u3000\u73ab\u7470\u9053\uff1a\u201c\u5c31\u5728\u6628\u5929\u4e0b\u5348\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7acb\u523b\u660e\u767d\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u6beb\u65e0\u7591\u95ee\uff0c\u8fd9\u6d88\u606f\u662f\u7531\u62db\u8d24\u9986\u5230\u706b\u5173\u53bb\u7684\uff0c\u4f46\u62db\u8d24\u9986\u4e3a\u4ec0 \u4e48\u8981\u628a\u8fd9\u4e8b\u4f20\u5230\u706b\u5173\uff0c\u5374\u5b9e\u5728\u4ee4\u4eba\u4e0d \u3000\u3000\u89e3\uff1f \u3000\u3000\u4ed6\u9ed8\u7136\u8bb8\u4e45\uff0c\u5401\u53e3\u6c14\u9053\uff1a\u201c\u5979\u662f\u59d4\u6258\u59d1\u5a18\u5411\u6211\u5e26\u4fe1\u7684\uff1f\u201d \u3000\u3000\u73ab\u7470\u9053\uff1a\u201c\u6843\u82b1\u59d0\u59d0\u4e00\u5411\u5f85\u6211\u5f88\u597d\uff0c\u6211\u4eca\u5929\u4e00\u65e9\u5077\u5077\u53bb\u63a2\u671b\u5979\uff0c\u5979\u6258\u6211\u5982\u679c\u6709\u673a\u4f1a\u51fa\u6765\uff0c \u4e00\u5b9a\u8981\u5e2e\u5979\u5fd9\u628a\u8fd9\u4e8b\u901a\u77e5\u516c\u5b50\uff0c\u6b63\u597d\u6211\u4eca\u5929\u4e2d\u5348\u5949\u547d\u5230\u603b\u575b\u5448\u9001\u6587\u4ef6\uff0c\u53c8\u8fd9\u4e48\u5de7\u5c31\u9047\u89c1\u4e86\u516c \u5b50\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6b64\u523b\u5185\u5fc3\u7684\u7126\u8651\u53ef\u60f3\u800c\u77e5\uff0c\u4ed6\u60f3\u5230\u6843\u82b1\u7adf\u7136\u628a\u81ea\u5df1\u770b\u6210\u4e86\u65e0\u6240\u4e0d\u80fd\u7684\u6551\u661f\uff0c\u96be\u514d \u6709\u4e9b\u557c\u7b11\u7686\u975e\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u4ed6\u60f3\u8d77\u5927\u516c\u4e3b\u66fe\u8bf4\u8fc7\uff0c\u6709\u4ec0\u4e48\u56f0\u96be\uff0c\u5c3d\u53ef\u4ee5\u6258\u516c\u5b59\u5148\u751f\u8f6c\u8fbe\uff0c\u5979\u4e00\u5b9a\u4f1a\u8bbe\u6cd5\u89e3\u51b3\uff0c \u8fd9\u4e0d\u6b63\u662f\u81ea\u5df1\u7684\u5e0c\u671b\u548c\u51ed\u85c9\u3002 \u3000\u3000\u201c\u5c31\u8bf7\u59d1\u5a18\u8f6c\u544a\u6843\u82b1\u59d1\u5a18\uff0c\u8981\u5979\u6682\u4e14\u5fcd\u4e00\u65f6\u4e4b\u82e6\uff0c\u8010\u5fc3\u7b49\u7740\uff0c\u6211\u4e00\u5b9a\u4f1a\u8bbe\u6cd5\u4e3a\u5979\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u73ab\u7470\u9891\u4e86\u9891\u9edb\u7709\u9053\uff1a\u201c\u516c\u5b50\u5fc5\u987b\u5f88\u5feb\u4e9b\u529e\uff0c\u6843\u82b1\u59d0\u59d0\u76ee\u524d\u53d7\u7684\u90a3\u79cd\u7f6a\uff0c\u5b9e\u5728\u591f\u53ef\u601c\u7684\uff0c \u5979\u771f\u7684\u662f\u5ea6\u65e5\u5982\u5e74\uff0c\u9664\u4e86\u516c\u5b50\uff0c\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u80fd\u6551\u5f97\u4e86\u5979\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u82e6\u7b11\u9053\uff1a\u201c\u53ef\u662f\u59d1\u5a18\u5e94\u5f53\u660e\u767d\uff0c\u6211\u73b0\u5728\u53ea\u662f\u62db\u8d24\u9986\u7684\u4e00\u540d\u5bbe\u5ba2\uff0c\u4ec0\u4e48\u804c\u4f4d\u90fd\u6ca1\u5206 \u6d3e\uff0c\u603b\u4e0d\u80fd\u8ba9\u6211\u5355\u5200\u5339\u9a6c\u6740\u4e86\u4f60\u4eec\u7684\u7edf\u9886\u6559\u51fa\u5979\u6765\u5427\uff01\u201d \u3000\u3000\u73ab\u7470\u9053\uff1a\u201c\u5a62\u5b50\u660e\u767d\uff0c\u4f46\u6843\u82b1\u59d0\u59d0\u5374\u4e0d\u8fd9\u4e48\u60f3\uff0c\u5979\u8ba4\u5b9a\u4f60\u5fc5\u4f1a\u53d7\u5230\u6559\u4e3b\u7684\u91cd\u7528\uff0c\u6240\u4ee5\u624d \u628a\u4e00\u5207\u7684\u5e0c\u671b\u5bc4\u6258\u5728\u60a8\u8eab\u4e0a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u53ef\u662f\u6211\u73b0\u5728\u5c1a\u672a\u53d7\u5230\u91cd\u7528\uff0c\u603b\u4e4b\uff0c\u6211\u62dc\u6258\u59d1\u5a18\u5343\u4e07\u591a\u5b89\u6170\u5979\uff0c\u4e0d\u7ba1\u5982\u4f55\uff0c \u6211\u4e00\u5b9a\u4f1a\u5c3d\u529b\u7684\u3002\u201d \u3000\u3000\u73ab\u7470\u70b9\u70b9\u5934\u9053\uff1a\u201c\u65e2\u7136\u6843\u82b1\u59d0\u59d0\u76f8\u4fe1\u516c\u5b50\uff0c\u5a62\u5b50\u5f53\u7136\u4e5f\u76f8\u4fe1\u516c\u5b50\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u671b\u671b\u5929\u8272\u9053\uff1a\u201c\u672c\u6765\u6211\u8be5\u7559\u4f60\u5230\u91cc\u9762\u5750\u5750\uff0c\u559d\u676f\u8336\u4f11\u606f\u4f11\u606f\uff0c\u4f46\u8fd9\u91cc\u6709\u89c4\u5b9a\uff0c\u5973 \u4eba\u4e0d\u80fd\u8fdb\u53bb\uff0c\u6843\u82b1\u59d1\u5a18\u51fa\u4e86\u4e8b\u60c5\uff0c\u6b63\u662f\u548c\u4e0a\u6b21\u66fe\u5230\u91cc\u9762\u53bb\u8fc7\u6709\u5173\u3002\u201d \u3000\u3000\u73ab\u7470\u9053\uff1a\u201c\u5a62\u5b50\u77e5\u9053\uff0c\u6211\u73b0\u5728\u5c31\u56de\u53bb\u4e86\uff0c\u4f46\u613f\u516c\u5b50\u522b\u8d1f\u4e86\u6843\u82b1\u59d0\u59d0\u5bf9\u60a8\u7684\u4e00\u7247\u5fc3\uff01\u201d \u3000\u3000\u671b\u7740\u73ab\u7470\u8d70\u8fdc\u7684\u80cc\u5f71\uff0c\u5cb3\u5c0f\u98de\u4e0d\u89c9\u9690\u5165\u4e00\u7247\u6014\u60d8\u5f53\u4e2d\u3002\u73ab\u7470\u6700\u540e\u7684\u90a3\u53e5\u8bdd\uff0c\u7a76\u7adf\u4ee3\u8868\u7684 \u662f\u4ec0\u4e48\uff1f \u3000\u3000\u96be\u9053\u6843\u82b1\u7adf\u662f\u5bf9\u81ea\u5df1\u52a8\u4e86\u7537\u5973\u4e4b\u60c5\uff1f \u3000\u3000\u8fd9\u662f\u4e0d\u53ef\u80fd\u7684\uff0c\u81ea\u5df1\u53ea\u662f\u5341\u4e09\u5c81\u7684\u5b69\u5b50\uff0c\u6843\u82b1\u81f3\u5c11\u4e5f\u8be5\u662f\u4f4d\u5341\u4e03\u516b\u5c81\u7684\u5927\u59d1\u5a18\uff0c\u4ee5\u5f7c\u6b64 \u7684\u5e74\u9f84\u800c\u8bba\uff0c\u600e\u4f1a\u53d1\u751f\u8fd9\u6837\u7684\u4e8b\uff1f \u3000\u3000\u4f46\uff0c\u4ed6\u786e\u5df2\u611f\u89c9\u51fa\u6843\u82b1\u5bf9\u81ea\u5df1\u7684\u611f\u60c5\u539f\u4e0d\u5bfb\u5e38\uff0c\u518d\u52a0\u4e0a\u73ab\u7470\u4e34\u8d70\u65f6\u7684\u90a3\u51e0\u53e5\u8bdd\uff0c\u53c8\u786e\u5b9e \u900f\u7740\u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u95ee\u5230\u5ba2\u5385\u4e0d\u4e45\uff0c\u6797\u91d1\u5b9d\u4fbf\u5df3\u62ff\u996d\u56de\u6765\u3002 \u3000\u3000\u665a\u9910\u7684\u83dc\u8272\u4f9d\u7136\u6bd4\u4ee5\u524d\u597d\u5f97\u591a\uff0c\u8fde\u6797\u91d1\u5b9d\u4e5f\u8bf4\u4ece\u524d\u6ca1\u6709\u8fc7\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u771f\u5e0c\u671b\u4f4f\u5728\u5404\u5206\u9986\u7684\u4eba\u90fd\u80fd\u5982\u6b64\uff0c\u82e5\u603b\u9986\u53ea\u662f\u5bf9\u4ed6\u4e2a\u4eba\u53e6\u773c\u76f8\u770b\uff0c\u53c8\u6709\u4f55\u610f\u4e49\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u8d8a\u53d1\u5e0c\u671b\u516c\u5b59\u5148\u751f\u80fd\u65e9\u4e9b\u6765\u8bbf\uff0c\u4ee5\u4fbf\u642d\u6551\u6843\u82b1\uff0c\u665a\u9910\u65f6\u5c11\u4e0d\u5f97\u8be2\u95ee\u6797\u91d1\u5b9d\u662f\u5426 \u89c1\u8fc7\u516c\u5b59\u5148\u751f\uff0c\u6797\u91d1\u5b9d\u7684\u56de\u7b54\u662f\u6447\u6447 \u3000\u3000\u5934\u3002 \u3000\u3000\u665a\u4e0a\uff0c\u5cb3\u5c0f\u98de\u51e0\u4e4e\u5f7b\u5e95\u672a\u7720\u3002 \u3000\u3000\u6b21\u65e5\u5929\u4eae\uff0c\u4ed6\u771f\u6068\u4e0d\u5f97\u76f4\u63a5\u95ef\u8fdb\u603b\u575b\u53bb\u89c1\u516c\u5b59\u5148\u751f\uff0c\u4f46\u6700\u540e\u8fd8\u662f\u89c9\u5f97\u90a3\u6837\u505a\u592a\u5510\u7a81\uff0c\u4e5f \u592a\u83bd\u649e\u3002 \u3000\u3000\u770b\u770b\u53c8\u5230\u4e86\u4e0b\u5348\uff0c\u4ed6\u771f\u559c\u51fa\u671b\u5916\uff0c\u516c\u5b59\u5148\u751f\u771f\u7684\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u5df2\u4ece\u6797\u91d1\u5b9d\u90a3\u91cc\u6253\u542c\u51fa\u516c\u5b59\u5148\u751f\u53eb\u516c\u5b59\u7389\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u628a\u516c\u5b59\u7389\u8bf7\u81f3\u5ba2\u5385\u5750\u4e0b\uff0c\u4eb2\u81ea\u6c8f\u4e0a\u8336\u3002 \u3000\u3000\u516c\u5b59\u7389\u559d\u4e86\u53e3\u8336\u9053\uff1b\u201c\u6211\u662f\u5949\u5927\u516c\u4e3b\u4e4b\u547d\u6765\u770b\u671b\u5cb3\u516c\u5b50\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5bf9\u516c\u5b59\u7389\u6267\u793c\u751a\u606d\uff0c\u8eac\u8eab\u9053\uff1b\u201c\u665a\u8f88\u627f\u5927\u516c\u4e3b\u5782\u9752\uff0c\u5b9e\u5728\u4e0d\u6562\u5f53\uff0c\u800c\u4e14\u8fdb\u5165\u5929\u8c37 \u5bf8\u529f\u672a\u7acb\u5c31\u8499\u5927\u516c\u4e3b\u5956\u8d4f\uff0c\u5185\u5fc3\u66f4\u662f\u8fc7\u610f\u4e0d\u53bb\u3002\u201d \u3000\u3000\u516c\u5b59\u7389\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u662f\u4f4d\u96be\u5f97\u7684\u5c11\u5e74\u5947\u624d\uff0c\u5927\u516c\u4e3b\u6700\u662f\u7231\u624d\uff0c\u5bf9\u4f60\u53e6\u773c\u76f8\u770b\uff0c \u662f\u7406\u6240\u5f53\u7136\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u4fbf\u7acb\u523b\u63d0\u8d77\u6843\u82b1\u4e4b\u4e8b\uff0c\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u4e3a\u4e86\u665a\u8f88\uff0c\u4f7f\u5f97\u82b1\u526f\u9986\u4e3b\u548c\u8427\u9986\u4e3b\u7adf\u7136 \u53cd\u76ee\u76f8\u5411\uff0c\u5185\u5fc3\u4e00\u76f4\u4e0d\u5b89\uff0c\u665a\u8f88\u5f88\u4e3a\u82b1\u526f\u9986\u4e3b\u62c5\u5fc3\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u7b11\u9053\uff1b\u201c\u5cb3\u516c\u5b50\u806a\u660e\u7edd\u9876\uff0c\u5e94\u8be5\u53ef\u4ee5\u770b\u5f97\u51fa\uff0e\u82b1\u526f\u9986\u4e3b\u82e5\u975e\u6709\u6240\u51ed\u6043\uff0c\u4ed6\u53c8\u600e\u6562 \u548c\u8427\u9986\u4e3b\u51b2\u7a81\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5185\u5fc3\u4e00\u52a8\uff0c\u5fd9\u9053\uff1a\u201c\u82b1\u526f\u9986\u4e3b\u6709\u4ec0\u4e48\u51ed\u6043\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u4f3c\u662f\u4e0d\u613f\u76f4\u8bf4\uff0c\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u5728\u603b\u575b\u6709\u540e\u53f0\u9760\u5c71\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8d8a\u53d1\u5fc3\u52a8\uff1a\u201c\u83ab\u975e\u82b1\u526f\u9986\u4e3b\u662f\u6559\u4e3b\u7684\u5fc3\u8179\u4eba\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u9ed8\u4e86\u534a\u664c\uff0c\u7ec8\u4e8e\u653e\u7f13\u4e86\u8bed\u6c14\u9053\uff1a\u201c\u5176\u5b9e\u8fd9\u4e8b\u8ba9\u5cb3\u516c\u5b50\u77e5\u9053\u4e5f\u65e0\u59a8\uff0c\u82b1\u526f\u9986\u4e3b\u51ed\u6043 \u7684\u5c31\u662f\u5927\u516c\u4e3b\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u53c8\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u5927\u516c\u4e3b\u867d\u7136\u5df2\u7ecf\u5e74\u8fc7\u7968\u6885\uff0c\u4f46\u5374\u4f9d\u7136\u4e91\u82f1\u672a\u5ac1\uff0c\u5f85\u5b57\u95fa\u4e2d\uff0c\u800c\u82b1\u526f\u9986\u4e3b\u5219 \u662f\u4e00\u4f4d\u6587\u6b66\u517c\u8d44\u624d\u8bc6\u8fc7\u4eba\u7684\u82f1\u96c4\u4eba\u7269\uff0c\u5c24\u5176\u4ed6\u4e30\u59ff\u4fca\u6717\uff0c\u4eea\u8868\u51fa\u4f17\uff0c\u56e0\u4e4b\uff0c\u5927\u516c\u4e3b\u65e9\u5c31\u5bf9\u4ed6 \u4e00\u89c1\u949f\u60c5\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u987f\u65f6\u5fc3\u5934\u5982\u53d7\u7535\u51fb\uff0c\u4ed6\u60f3\u5230\u7236\u4eb2\u6709\u5927\u516c\u4e3b\u505a\u540e\u53f0\u9760\u5c71\uff0c\u56fa\u7136\u662f\u4ef6\u597d\u4e8b\uff0c\u4f46\u6bcd\u4eb2\u53c8 \u5c06\u600e\u4e48\u529e\u5462\uff1f\u2026\u2026 \u3000\u3000\u597d\u5728\u4ed6\u5c1a\u80fd\u6781\u529b\u4fdd\u6301\u9547\u5b9a\uff0c\u4e0d\u4f7f\u5bf9\u65b9\u770b\u51fa\u3002 \u3000\u3000\u53ea\u542c\u516c\u5b59\u7389\u53c8\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u524d\u5929\u82b1\u526f\u9986\u4e3b\u6562\u548c\u8427\u9986\u4e3b\u51e0\u4e4e\u5175\u620c\u76f8\u89c1\u7684\u539f\u56e0\uff0c\u800c\u8427\u9986\u4e3b\u4e5f \u4e0d\u5f97\u4e0d\u5fcd\u4e0b\u8fd9\u53e3\u6c14\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u82b1\u526f\u9986\u4e3b\u548c\u5927\u516c\u4e3b\u662f\u5426\u5df2\u7ecf\u2026\u2026\u201d \u3000\u3000\u201c\u76ee\u524d\u8fd8\u6ca1\u6709\uff0c\u636e\u6211\u6240\u77e5\uff0c\u5927\u516c\u4e3b\u65e9\u5df2\u628a\u5fc3\u610f\u7980\u62a5\u4e86\u6559\u4e3b\uff0c\u6559\u4e3b\u4e00\u5411\u505a\u4e8b\u8c28\u614e\uff0c\u6682\u65f6\u8fd8 \u4e0d\u4f1a\u7b54\u5e94\uff0c\u4e5f\u8bb8\u8fd8\u5728\u5bf9\u82b1\u526f\u9986\u4e3b\u8003\u6838\u4e4b\u4e2d\uff0c\u5927\u516c\u4e3b\u7ecf\u5e38\u5230\u62db\u8d24\u9986\u6765\uff0c\u4e0d\u5916\u662f\u501f\u673a\u548c\u82b1\u526f\u9986\u4e3b \u63a5\u8fd1\u3002\u201d \u3000\u3000\u201c\u516c\u5b59\u5148\u751f\u8ba4\u4e3a\u82b1\u526f\u9986\u4e3b\u548c\u5927\u516c\u4e3b\u7684\u4e8b\u60c5\u53ef\u4ee5\u6210\u529f\u5417\uff1f\u201d \u3000\u3000\u201c\u4e8b\u60c5\u5df2\u7ecf\u5230\u4e86\u8fd9\u79cd\u5730\u6b65\uff0c\u5341\u6709\u516b\u4e5d\u662f\u4e0d\u4f1a\u6709\u95ee\u9898\u7684\uff0c\u53ea\u662f\u65f6\u95f4\u65e9\u665a\u800c\u5df2\uff0c\u5927\u516c\u4e3b\u6240\u4ee5 \u5230\u73b0\u5728\u8fd8\u5f85\u5b57\u95fa\u4e2d\uff0c\u4e0d\u5916\u662f\u773c\u5149\u592a\u9ad8\uff0c\u5bf9\u8c61\u96be\u6c42\uff0c\u5728\u5929\u8c37\u6765\u8bf4\uff0c\u5979\u53c8\u54ea\u91cc\u80fd\u627e\u5230\u50cf\u82b1\u526f\u9986\u4e3b \u8fd9\u6837\u7684\u7b2c\u4e8c\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8d8a\u53d1\u5fc3\u5982\u9524\u51fb\uff0c\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u516c\u5b59\u5148\u751f\u53ef\u6e05\u695a\u82b1\u526f\u9986\u4e3b\u7684\u4e3a\u4eba\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u6211\u5bf9\u82b1\u526f\u9986\u4e3b\uff0c\u4e00\u76f4\u5b58\u7740\u611f\u6069\u672a\u62a5\u4e4b\u5fc3\uff0c\u53c8\u5c82\u6b62\u6e05\u695a\u4ed6\u7684\u4e3a\u4eba\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u516c\u5b59\u5148\u751f\u65e9\u5c31\u8ba4\u8bc6\u82b1\u526f\u9986\u4e3b\uff1f\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u8fc7\u4e09\u5e74\u591a\uff0c\u5f53\u65f6\u6211\u4eec\u540c\u95ef\u4e94\u5173\uff0c\u9664\u4e86\u6587\u5173\u548c\u5bb5\u5173\u6211\u80fd\u8f7b\u6613\u901a\u8fc7\u5916\uff0c\u5176\u4f59\u8fd8\u5f97\u529b\u4e8e \u82b1\u526f\u9986\u4e3b\u7684\u534f\u52a9\uff0c\u5982\u679c\u6ca1\u6709\u4ed6\uff0c\u6211\u53c8\u600e\u80fd\u5230\u5929\u8c37\u6765\uff1f\u53c8\u600e\u80fd\u5728\u62db\u8d24\u9986\u4efb\u804c\u519b\u5e08\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6697\u9053\uff1a\u201c\u65e2\u7136\u4ed6\u5f53\u5e74\u548c\u7236\u4eb2\u4e00\u8d77\u8fdb\u5165\u5929\u8c37\uff0c\u5fc5\u5b9a\u4e5f\u89c1\u5230\u6bcd\u4eb2\uff0c\u4ed6\u65e2\u7136\u77e5\u9053\u7236\u4eb2\u5df2 \u6709\u59bb\u5ba4\uff0c\u53c8\u548c\u7236\u4eb2\u76f8\u4ea4\u83ab\u9006\uff0c\u4e3a\u4ec0\u4e48\u542c\u4ed6\u65b9\u624d\u7684\u8bed\u6c14\uff0c\u4f3c\u4e4e\u53c8\u4e50\u89c1\u7236\u4eb2\u548c\u5927\u516c\u4e3b\u7684\u5a5a\u4e8b\u5f97\u9042 \u5462\uff1f\u201d \u3000\u3000\u7136\u800c\u4ed6\u53c8\u4e0d\u6562\u63d0\u8d77\u8fd9\u4e8b\uff0c\u56e0\u4e3a\u8fd9\u6837\u4e00\u6765\uff0c\u52bf\u5fc5\u5f15\u8d77\u516c\u5b59\u7389\u5bf9\u81ea\u5df1\u8eab\u4efd\u7684\u6000\u7591\u3002\u4ed6\u4e0d\u80fd\u4e0d \u4e25\u5b88\u7236\u4eb2\u7684\u90d1\u91cd\u4ea4\u4ee3\uff1a\u51b3\u4e0d\u80fd\u8ba9\u4efb\u4f55 \u3000\u3000\u4eba\u77e5\u9053\u4ed6\u4eec\u662f\u7236\u5b50\u5173\u7cfb\u3002 \u3000\u3000\u516c\u5b59\u7389\u89c1\u5cb3\u5c0f\u98de\u6c89\u541f\u4e0d\u8bed\uff0c\u4f3c\u4e4e\u4e0d\u6b32\u591a\u4f5c\u505c\u7559\uff0c\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u5982\u679c\u6ca1\u6709\u522b\u7684\u4e8b\uff0c \u6211\u8be5\u8d70\u4e86\u3002\u5982\u679c\u6709\u4e8b\uff0c\u4e5f\u7528\u4e0d\u7740\u5ba2\u6c14\uff1f \u3000\u3000\u53ea\u7ba1\u76f4\u8bb2\uff0c\u6211\u80fd\u5e2e\u5fd9\u7684\u5fc5\u5b9a\u5f53\u573a\u7b54\u590d\uff0c\u65e0\u6cd5\u89e3\u51b3\u7684\uff0c\u6211\u4f1a\u5411\u5927\u516c\u4e3b\u8f6c\u8fbe\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5c82\u80fd\u5931\u53bb\u673a\u4f1a\uff0c\u5fd9\u9053\uff1a\u201c\u516c\u5b59\u5148\u751f\u8bb2\u5750\uff0c\u665a\u8f88\u6b63\u6709\u4e00\u4e8b\u5949\u544a\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u590d\u53c8\u5750\u4e86\u4e0b\u6765\uff0c\u4e24\u9053\u76ee\u5149\uff0c\u6df1\u6ce8\u5728\u5cb3\u5c0f\u98de\u8138\u4e0a\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u53ea\u7ba1\u8bb2\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e0a\u6b21\u665a\u8f88\u8bef\u72af\u89c4\u5f8b\u628a\u5973\u4eba\u5f15\u8fdb\u5206\u9986\uff0c\u90a3\u5973\u7684\u662f\u8c01\uff0c\u516c\u5b59\u5148\u751f\u4e00\u5b9a\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u662f\u706b\u5173\u4e0a\u4e00\u540d\u53eb\u6843\u82b1\u7684\u59d1\u5a18\uff0c\u5bf9\u5417\uff1f\u4e8b\u60c5\u5df2\u7ecf\u8fc7\u53bb\u4e86\uff0c\u5cb3\u516c\u5b50\u4e3a\u4ec0\u4e48\u8fd8\u8981 \u518d\u63d0\uff1f\u201d \u3000\u3000\u201c\u5e76\u975e\u665a\u8f88\u6709\u610f\u518d\u63d0\uff0c\u800c\u662f\u53c8\u51fa\u4e86\u4e8b\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u53c8\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u968f\u5373\u628a\u6843\u82b1\u60e8\u906d\u60e9\u7f5a\u7684\u7ecf\u8fc7\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u516c\u5b59\u7389\u542c\u540e\u51b7\u51b7\u2014\u7b11\u9053\uff1a\u201c\u4e0d\u96be\u60f3\u89c1\uff0c\u8fd9\u662f\u8427\u9986\u4e3b\u901a\u77e5\u706b\u5173\u7684\uff0c\u8427\u9986\u4e3b\u8fd9\u6837\u505a\uff0c\u5c82\u4e0d\u662f \u65e0\u4e8b\u627e\u4e8b\uff0c\u53c8\u662f\u4f55\u82e6\uff01\u201d \u3000\u3000\u63a5\u7740\u53c8\u9053\uff1a\u201c\u5982\u679c\u90a3\u4f4d\u59d1\u5a18\u4e0d\u662f\u706b\u5173\u7684\u4eba\uff0c\u90a3\u5c31\u6ca1\u4e8b\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u89c9\u4e00\u6123\u9053\uff1a\u201c\u8fd9\u53c8\u662f\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u706b\u5173\u7edf\u9886\uff0c\u6b63\u662f\u8427\u9986\u4e3b\u7684\u80de\u59b9\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u5982\u6b64\uff0c\u90a3\u4f4d\u706b\u5173\u7edf\u9886\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u201c\u5979\u53eb\u8427\u7476\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u8427\u9986\u4e3b\u5728\u7687\u5143\u6559\u91cc\uff0c\u52bf\u529b\u8fd8\u771f\u4e0d\u5c0f\uff01\u201d \u3000\u3000\u201c\u8427\u9986\u4e3b\u548c\u4ed6\u7684\u80de\u59b9\u591a\u5e74\u8ffd\u968f\u6559\u4e3b\uff0c\u79f0\u5f97\u4e0a\u662f\u672c\u6559\u7684\u5143\u8001\u529f\u81e3\uff0c\u4e00\u822c\u4eba\u600e\u80fd\u548c\u4ed6\u76f8\u6bd4\uff0c \u8981\u4e0d\u7136\u4ed6\u54ea\u4e00\u70b9\u80fd\u6bd4\u5f97\u82b1\u526f\u9986\u4e3b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u6843\u82b1\u59d1\u5a18\u7684\u4e8b\uff0c\u8fd8\u8981\u8bf7\u516c\u5b59\u5148\u751f\u8bbe\u6cd5\u5e2e\u5fd9\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u4f3c\u662f\u9762\u6709\u96be\u8272\uff0c\u7d27\u76b1\u53cc\u7709\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u8fd8\u5b9e\u5728\u4e0d\u597d\u5904\u7406\uff0c\u9664\u975e\u8fd9\u4eba\u51fa\u9762\uff0c\u4f46\u6211\u5374 \u53c8\u4e0d\u65b9\u4fbf\u6c42\u4ed6\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8feb\u4e0d\u53ca\u5f85\u95ee\u9053\uff1a\u201c\u516c\u5b59\u5148\u751f\u6240\u8bf4\u7684\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u5f53\u7136\u662f\u8427\u9986\u4e3b\uff0c\u706b\u5173\u4e0e\u62db\u8d24\u9986\u4e92\u4e0d\u96b6\u5c5e\uff0c\u53ea\u6709\u8427\u9986\u4e3b\u624d\u6709\u8fd9\u79cd\u79c1\u4eba\u5173\u7cfb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6b63\u8272\u9053\uff1a\u201c\u4e8b\u60c5\u662f\u4ed6\u901a\u77e5\u706b\u5173\u7684\uff0c\u5f53\u7136\u4e0d\u80fd\u518d\u6c42\u4ed6\u3002\u201d \u3000\u3000\u516c\u5b59\u7389\u82e6\u7b11\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u51c6\u5907\u6c42\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u516c\u5b59\u5148\u751f\u662f\u5949\u5927\u516c\u4e3b\u4e4b\u547d\u800c\u6765\uff0c\u5c31\u8be5\u60f3\u5230\u5e94\u8be5\u6c42\u5927\u516c\u4e3b\u89e3\u51b3\u3002\u201d \u3000\u3000\u4ed6\u7684\u51e0\u53e5\u8bdd\uff0c\u663e\u7136\u5df2\u5bf9\u516c\u5b59\u7389\u8868\u793a\u4e0d\u6ee1\u3002 \u3000\u3000\u5176\u5b9e\u516c\u5b59\u7389\u5e76\u975e\u6ca1\u60f3\u5230\u5927\u516c\u4e3b\uff0c\u800c\u662f\u5728\u4ed6\u5fc3\u76ee\u4e2d\uff0c\u5e76\u4e0d\u8ba4\u4e3a\u8fd9\u662f\u4e00\u4ef6\u91cd\u8981\u5927\u4e8b\uff0c\u4ed6\u54ea\u91cc \u77e5\u9053\uff0c\u8fd9\u4e8b\u5728\u5cb3\u5c0f\u98de\u5fc3\u76ee\u4e2d\uff0c\u5374\u662f\u6700 \u3000\u3000\u6025\u5207\u4e0d\u8fc7\u7684\u3002 \u3000\u3000\u516c\u5b59\u7389\u4e5f\u53ea\u597d\u70b9\u5934\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u6211\u51b3\u5b9a\u5411\u5927\u516c\u4e3b\u8f6c\u62a5\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u665a\u8f88\u6073\u6c42\u516c\u5b59\u5148\u751f\u80fd\u9a6c\u4e0a\u8f6c\u62a5\uff0c\u5fc5\u987b\u5c3d\u65e9\u628a\u6843\u82b1\u59d1\u5a18\u89e3\u6551\u51fa\u6765\uff0c\u665a\u8f88\u867d\u7136 \u4e0d\u662f\u6843\u82b1\u59d1\u5a18\uff0c\u4f46\u5374\u53ef\u4ee5\u60f3\u89c1\u5979\u6b64\u523b\u6240\u53d7\u7684\u75db\u82e6\u3002\u201d \u3000\u3000\u9001\u8d70\u516c\u5b59\u7389\uff0c\u5bf9\u4e8e\u642d\u6551\u6843\u82b1\u7684\u4e8b\uff0c\u5cb3\u5c0f\u98de\u5185\u5fc3\u8f7b\u677e\u4e86\u4e0d\u5c11\u3002\u4f46\u53e6\u4e00\u65b9\u9762\uff0c\u4ed6\u5374\u53c8\u4e3a\u7236\u4eb2 \u548c\u5927\u516c\u4e3b\u7684\u4e8b\uff0c\u5fc3\u5934\u5982\u5760\u91cd\u94c5\u3002 \u3000\u3000\u8c01\u77e5\u5306\u5306\u4e24\u5929\u8fc7\u53bb\uff0c\u516c\u5b59\u7389\u7adf\u7136\u4e0d\u66fe\u518d\u6765\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u771f\u662f\u4e94\u5185\u5982\u711a\uff0c\u540c\u65f6\u4e5f\u5bf9\u516c\u5b59\u7389\u5927\u611f\u4e0d\u6ee1\u3002 \u3000\u3000\u76f4\u5230\u7b2c\u4e09\u5929\u4e2d\u5348\uff0c\u4ed6\u5b9e\u5728\u65e0\u6cd5\u518d\u5fcd\uff0c\u53ea\u597d\u4e0b\u5b9a\u51b3\u5fc3\uff0c\u4eb2\u81ea\u5230\u603b\u9986\u4e00\u8d9f\u3002 \u3000\u3000\u4ed6\u89c9\u5f97\u9664\u6b64\u4e4b\u5916\uff0c\u5df2\u7ecf\u6ca1\u6709\u7b2c\u4e8c\u6761\u8def\u597d\u8d70\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u6b63\u597d\u6797\u91d1\u5b9d\u8981\u5230\u603b\u9986\u62ff\u996d\uff0c\u4ed6\u558a\u6765\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u8d70\uff0c\u6211\u548c\u4f60\u4e00\u8d77\u5230\u603b\u9986\u53bb\uff01\u201d \u3000\u3000\u6797\u91d1\u5b9d\u5927\u611f\u4e00\u6123\u9053\uff1a\u201c\u516c\u5b50\u4ec0\u4e48\u4e8b\u8981\u5230\u603b\u9986\u53bb\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u51c6\u5907\u5230\u603b\u9986\u89c1\u516c\u5b59\u5148\u751f\uff01\u201d \u3000\u3000\u6797\u91d1\u5b9d\u987f\u73b0\u60ca\u614c\u4e4b\u8272\u9053\uff1a\u201c\u53ef\u662f\u516c\u5b50\u73b0\u5728\u8fd8\u4e0d\u80fd\u81ea\u7531\u884c\u52a8\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5e76\u975e\u6ca1\u5230\u8fc7\u603b\u9986\uff0e\u4e0a\u6b21\u53ef\u4ee5\u53bb\uff0c\u73b0\u5728\u4e3a\u4ec0\u4e48\u4e0d\u53ef\u53bb\uff1f\u201d \u3000\u3000\u201c\u4e0a\u6b21\u516c\u5b50\u662f\u88ab\u97e9\u603b\u7ba1\u5e26\u53bb\u7684\uff0c\u73b0\u5728\u5e76\u4e0d\u66fe\u6709\u4eba\u6765\u5e26\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u5c31\u7531\u4f60\u5e26\uff0c\u4e0d\u662f\u4e00\u6837\u5417\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u2026\u2026\u5c0f\u7684\u600e\u591f\u8d44\u683c\u5e26\uff1f\u201d \u3000\u3000\u201c\u97e9\u603b\u7ba1\u662f\u62db\u8d24\u9986\u7684\u4eba\uff0c\u4f60\u4e5f\u662f\u62db\u8d24\u9986\u7684\u4eba\uff0c\u4e3a\u4ec0\u4e48\u6ca1\u6709\u8d44\u683c\u5e26\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u8bba\u8eab\u4efd\u5730\u4f4d\uff0c\u5c0f\u7684\u600e\u80fd\u548c\u97e9\u603b\u7ba1\u6bd4\uff1f\u800c\u4e14\u4e0a\u6b21\u97e9\u603b\u7ba1\u5949\u7684\u662f\u9986\u4e3b\u4e4b\u547d\u2026\u2026\u201d \u3000\u3000\u201c\u6ca1\u4ec0\u4e48\u8bf4\u7684\uff0c\u4f60\u4e0d\u5e26\u6211\u53ef\u4ee5\u81ea\u5df1\u53bb\uff01\u201d \u3000\u3000\u6797\u91d1\u5b9d\u89c1\u5cb3\u5c0f\u98de\u5df2\u529d\u4e86\u6012\uff0c\u53c8\u60f3\u8d77\u5cb3\u5c0f\u6b63\u4e0a\u6b21\u7531\u603b\u9986\u65e0\u4e8b\u5f52\u6765\uff0c\u4e5f\u5c31\u4e0d\u6562\u518d\u575a\u6301\uff0e\u54a7\u5634 \u82e6\u7b11\u4e86\u58f0\u9053\uff1a\u201c\u90a3\u4e48\u516c\u5b50\u5c31\u8bf7\u548c\u5c0f\u7684\u4e00\u8d77\u8d70\u5427\uff01\u201d \u3000\u3000\u5230\u4e86\u603b\u9986\u5927\u95e8\u5916\uff0c\u6797\u91d1\u5b9d\u7ad9\u4f4f\u811a\u9053\uff1a\u201c\u5c0f\u7684\u62ff\u996d\uff0c\u90fd\u662f\u8d70\u540e\u95e8\u76f4\u63a5\u5230\u53a8\u623f\u53bb\u7684\uff0c\u516c\u5b50\u662f \u4e0d\u662f\u4e5f\u8981\u8d70\u540e\u95e8\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fcd\u4e0d\u4f4f\u8981\u7b11\uff0c\u6325\u4e86\u6325\u624b\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u53bb\u5427\uff0c\u6211\u662f\u4ece\u6765\u4e0d\u8d70\u540e\u95e8\u7684\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u8fc8\u6b65\u5411\u5927\u95e8\u8d70\u53bb\u3002 \u3000\u3000\u5927\u95e8\u5916\u6709\u4e24\u540d\u5b88\u62a4\uff0c\u90fd\u662f\u4f69\u5200\u5e26\u5251\u7684\u5f6a\u5f62\u9ed1\u8863\u5927\u6c49\u3002 \u3000\u3000\u4e0d\u7b49\u4ed6\u8d70\u8fd1\uff0c\u4e00\u540d\u5927\u6c49\u5df2\u51fa\u58f0\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u62ff\u901a\u884c\u8170\u724c\u51fa\u6765\u67e5\u9a8c\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u60f3\u518d\u95f9\u4e8b\uff0c\u81ea\u52a8\u505c\u4e0b\u811a\u6b65\u9053\uff1a\u201c\u5728\u4e0b\u662f\u4f4f\u5728\u4e09\u5341\u516d\u53f7\u5206\u9986\u7684\uff0c\u6709\u4e8b\u6c42\u5427\u516c\u5b59\u5148 \u751f\uff01\u201d \u3000\u3000\u90a3\u5927\u6c49\u671b\u7740\u53e6\u4e00\u540d\u5927\u6c49\u9053\uff1a\u201c\u8fd9\u4e48\u5c0f\u5c31\u80fd\u95ef\u8fc7\u4e94\u5173\u8fdb\u5165\u5206\u9986\uff0c\u6050\u6015\u4e0d\u5bf9\u5427\uff1f\u201d \u3000\u3000\u53e6\u4e00\u5927\u6c49\u9053\uff1b\u201c\u4e0d\u7ba1\u5bf9\u4e0d\u5bf9\uff0c\u5148\u67e5\u9a8c\u4e86\u901a\u884c\u8170\u724c\u518d\u8bf4\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u7528\u4e0d\u7740\u67e5\uff0c\u5728\u4e0b\u6ca1\u6709\u901a\u884c\u8170\u724c\u3002\u201d \u3000\u3000\u539f\u5148\u90a3\u5927\u6c49\u987f\u65f6\u4e24\u773c\u4e00\u77aa\uff0c\u559d\u9053\uff1a\u201c\u65e2\u7136\u6ca1\u6709\u901a\u884c\u8170\u724c\uff0c\u80c6\u6562\u5f80\u62db\u8d24\u9986\u5927\u95e8\u91cc\u95ef\uff0c\u4f60\u60f3 \u627e\u6b7b\uff01\u201d \u3000\u3000\u53e6\u4e00\u5927\u6c49\u5fd9\u9053\uff1a\u201c\u8001\u65b9\uff0c\u5148\u522b\u8fd9\u4e48\u51f6\uff0c\u524d\u4e9b\u5927\u54b1\u4eec\u603b\u7ba1\u5df2\u7ecf\u4ea4\u4ee3\u8fc7\uff0c\u6709\u751f\u4eba\u8fdb\u6765\uff0c\u8981\u5c3d \u91cf\u5ba2\u6c14\u70b9\u3002\u201d \u3000\u3000\u539f\u5148\u90a3\u5927\u6c49\u603b\u7b97\u6536\u8d77\u6251\u514b\u9762\u5b54\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8001\u5b50\u4e0d\u60f3\u8ffd\u7a76\u4f60\uff1f\u65e2\u7136\u6ca1\u6709\u901a\u884c\u8170\u724c\uff0c \u90a3\u5c31\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u867d\u7136\u4e0d\u60f3\u95f9\u4e8b\uff0c\u4f46\u89c1\u4ed6\u53e3\u79f0\u8001\u5b50\uff0c\u5374\u518d\u4e5f\u6309\u637a\u4e0d\u4f4f\uff0c\u4e0d\u89c9\u53cc\u7709\u76f4\u8038\uff0c\u559d\u9053\uff1a\u201c\u5bf9 \u5728\u4e0b\u8bf4\u8bdd\uff0c\u4f60\u6562\u5634\u91cc\u4e0d\u5e72\u4e0d\u51c0\uff1f\u201d \u3000\u3000\u90a3\u5927\u6c49\u987f\u65f6\u4e00\u6123\uff0c\u4ed6\u672c\u6765\u51c6\u5907\u653e\u4eba\uff0c\u4e0d\u60f3\u8fd9\u5c0f\u5b69\u5b50\u5374\u5012\u627e\u9ebb\u70e6\uff0c\u7acb\u5373\u6309\u4e0a\u4e86\u5200\u9798\uff0c\u4e24\u53ea \u725b\u86cb\u8ddf\u5706\u7741\u9053\uff1a\u201c\u54a6\uff1f\u8fd9\u5c0f\u5b50\u662f\u732a\u516b\u6212 \u3000\u3000\u4e0b\u5c71\uff0c\u8fd8\u60f3\u5012\u6253\u8001\u5b50\u4e00\u94af\uff0c\u7b80\u76f4\u662f\u4e70\u78b1\u9c7c\u653e\u725b\u2014\u2014\u4e0d\u77e5\u6b7b\u6d3b\uff01\u201d \u3000\u3000\u5c82\u77e5\u4ed6\u7684\u8bdd\u521a\u521a\u51fa\u53e3\uff0c\u7a81\u89c9\u8033\u6839\u6709\u5982\u96f7\u9707\uff0c\u63a5\u7740\u4e24\u773c\u4e00\u9ed1\uff0c\u8eab\u5b50\u5df2\u817e\u7a7a\u800c\u8d77\uff0c\u76f4\u98de\u51fa\u4e24 \u4e09\u4e08\uff0c\u624d\u6454\u843d\u5728\u95e8\u65c1\u7684\u4e00\u68f5\u5927\u6811\u4e0b\uff0c\u7136\u540e\u7ffb\u4e86\u4e24\u7ffb\uff0c\u660f\u8e76\u8fc7\u53bb\u3002 \u3000\u3000\u53e6\u4e00\u5927\u6c49\u89c1\u5cb3\u5c0f\u98de\u8f7b\u63cf\u6de1\u5199\u7684\u4e00\u51fa\u624b\u5c31\u6709\u5982\u6b64\u60ca\u4eba\u5a01\u529b\uff0c\u53ea\u5413\u5f97\u4e5f\u51e0\u4e4e\u660f\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6717\u58f0\u9053\uff1a\u201c\u7528\u4e0d\u7740\u6015\uff0c\u5230\u91cc\u9762\u627e\u4f60\u4eec\u603b\u7ba1\u6216\u526f\u603b\u7ba1\u51fa\u6765\u5c31\u6ca1\u4f60\u7684\u4e8b\u4e86\uff01\u201d \u3000\u3000\u53e6\u4e00\u5927\u6c49\u8fd9\u624d\u50cf\u6e05\u9192\u8fc7\u6765\uff0c\u4e00\u58f0\u4e0d\u54cd\u7684\u5feb\u6b65\u5f80\u5927\u95e8\u5185\u5954\u53bb\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u513f\uff0c\u4fbf\u6709\u4e00\u4eba\u5954\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8ba4\u4e86\u534a\u664c\uff0c\u624d\u770b\u51fa\u661f\u603b\u7ba1\u97e9\u5fb7\u8d77\u3002 \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u4ed6\u8138\u4f24\u672a\u6108\uff0c\u534a\u8fb9\u8138\u7528\u7eb1\u5e03\u5305\u7740\uff0c\u81ea\u7136\u4e0d\u6613\u8fa8\u8ba4\u3002 \u3000\u3000\u97e9\u5fb7\u8d77\u7acb\u5373\u62f1\u62f1\u624b\u9053\uff1a\u201c\u539f\u6765\u662f\u5cb3\u516c\u5b50\uff0c\u6709\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5982\u679c\u6ca1\u6709\u4e8b\uff0c\u5728\u4e0b\u4f55\u5fc5\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u6ca1\u8bf4\u4ec0\u4e48\uff0c\u5374\u8f6c\u5934\u5411\u5927\u6811\u4e0b\u671b\u53bb\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u4ed6\u662f\u8981\u770b\u770b\u90a3\u4f4d\u5b88\u95e8\u5f1f\u5144\u88ab\u6253\u6210\u4ec0\u4e48\u6837\u5b50\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4e0d\u5fc5\u770b\uff0c\u5927\u6982\u8fd8\u6b7b\u4e0d\u4e86\uff01\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u4e00\u8fb9\u592a\u9633\u7a74\u7684\u9752\u7b4b\u5192\u4e86\u51e0\u5192\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u4e3a\u4ec0\u4e48\u628a\u4ed6\u6253\u6210\u90a3\u79cd\u6837\u5b50\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u7b11\u9053\uff1a\u201c\u4e0a\u6b21\u5434\u526f\u603b\u7ba1\u4e3a\u4ec0\u4e48\u88ab\u5728\u4e0b\u6253\u6210\u90a3\u6837\u5b50\uff0c\u8fd9\u6b21\u7684\u60c5\u5f62\u4e5f\u5dee\u4e0d\u591a\uff01\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u5fc3\u91cc\u6709\u6570\uff0c\u6b64\u523b\u603b\u9986\u5185\u80fd\u5236\u670d\u5cb3\u5c0f\u98de\u7684\uff0c\u53ea\u6709\u82b1\u7389\u9e9f\u4e00\u4eba\uff0e\u4f46\u82b1\u7389\u9e9f\u504f\u504f\u53c8\u62a4\u7740 \u8fd9\u5c0f\u5b50\uff0c\u770b\u6765\u8fd8\u662f\u4e0d\u80fd\u52a8\u786c\u7684\u3002 \u3000\u3000\u4ed6\u53ea\u597d\u53c8\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u7a76\u7adf\u6709\u4ec0\u4e48\u4e8b\uff1f\u4e0d\u59a8\u544a\u8bc9\u97e9\u67d0\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u60f3\u89c1\u89c1\u516c\u5b59\u5148\u751f\uff01\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u4e24\u773c\u4e00\u970e\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u6765\u5f97\u4e0d\u5de7\uff0c\u516c\u5b59\u5148\u751f\u4e00\u65e9\u5c31\u51fa\u53bb\u4e86\uff0c\u5230\u73b0\u5728\u8fd8\u6ca1\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u4f60\u603b\u8be5\u77e5\u9053\u4e86\u201d \u3000\u3000\u201c\u97e9\u67d0\u4e0d\u6e05\u695a\uff0c\u516c\u5b59\u5148\u751f\u5916\u51fa\u516c\u5e72\uff0c\u4e0d\u9700\u5411\u97e9\u67d0\u4ea4\u4ee3\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd9\u8bdd\u5230\u5e95\u662f\u771f\u7684\u8fd8\u662f\u5047\u7684\uff1f\u201d \u3000\u3000\u201c\u97e9\u67d0\u600e\u6709\u5fc5\u8981\u9a97\u5cb3\u516c\u5b50\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u603b\u9986\u91cc\u8fd8\u6709\u8c01\u5728\uff1f\u201d \u3000\u3000\u201c\u82b1\u526f\u9986\u4e3b\u4e5f\u51fa\u53bb\u4e86\uff0c\u53ea\u6709\u9986\u4e3b\u5728\u3002\u201d \u3000\u3000\u201c\u4e5f\u597d\uff0c\u5728\u4e0b\u5c31\u89c1\u89c1\u8427\u9986\u4e3b\uff01\u201d \u3000\u3000\u8bf4\u5b9e\u5728\u7684\uff0c\u5cb3\u5c0f\u98de\u672c\u4e0d\u6b32\u89c1\u8427\u6e58\uff0c\u4f46\u6b64\u523b\u516c\u5b59\u7389\u4e0d\u5728\uff0c\u4ed6\u5728\u6025\u4e8e\u6c42\u5f97\u89e3\u51b3\u95ee\u9898\u4e4b\u4e0b\uff0c\u4e5f \u53ea\u6709\u89c1\u4e0a\u8427\u6e58\u4e00\u89c1\u4e86\u3002 \u3000\u3000\u97e9\u5fb7\u8d77\u9762\u6709\u96be\u8272\uff0c\u4f46\u8bdd\u5df2\u51fa\u53e3\uff0c\u5374\u53c8\u4e0d\u80fd\u518d\u8bf4\u9986\u4e3b\u4e0d\u5728\u3002 \u3000\u3000\u65e0\u5948\u4f55\uff0c\u53ea\u5f97\u95ea\u8fc7\u4e00\u65c1\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u8bf7\u8fdb\uff01\u201d \u3000\u3000\u4ed6\u628a\u5cb3\u5c0f\u98de\u5f15\u8fdb\u5927\u5385\uff0c\u518d\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u8bf7\u7a0d\u5f85\uff0c\u97e9\u67d0\u8fd9\u5c31\u53bb\u6e05\u9986\u4e3b\u6765\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u76f4\u7b49\u4e86\u8db3\u8db3\u987f\u996d\u5de5\u592b\uff0c\u624d\u89c1\u97e9\u5fb7\u8d77\u966a\u7740\u8427\u6e58\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8427\u6e58\u9762\u65e0\u8868\u60c5\u3002 \u3000\u3000\u97e9\u5fb7\u8d77\u5219\u4e00\u8138\u54ed\u4e27\u6a21\u6837\uff0c\u4e0d\u96be\u60f3\u89c1\u4ed6\u662f\u53d7\u4e86\u8427\u6e58\u7684\u8d23\u9a82\uff0c\u8d23\u9a82\u4ed6\u4e0d\u8be5\u544a\u8bc9\u5cb3\u5c0f\u98de\u9986\u4e3b\u5728\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u56e0\u4e3a\u6709\u6c42\u4e8e\u8427\u6e58\uff0c\u800c\u4e14\u793c\u8c8c\u4e0d\u53ef\u5e9f\uff0c\u4e0d\u7b49\u5bf9\u65b9\u8fdb\u5165\uff0c\u5c31\u7ad9\u4e86\u8d77\u6765\uff0c\u62b1\u62f3\u4e00\u793c\u9053\uff1a \u201c\u5728\u4e0b\u89c1\u8fc7\u8427\u9986\u4e3b\uff01\u201d \u3000\u3000\u8427\u6e58\u53ea\u662f\u70b9\u4e86\u70b9\u5934\uff0c\u968f\u5373\u5728\u4e2d\u95f4\u592a\u5e08\u6905\u4e0a\u5750\u4e0b\uff0c\u8138\u4e0a\u5145\u6ee1\u4e86\u51b7\u6f20\u548c\u4e0d\u5c51\u795e\u8272\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u51e0\u6b21\u60f3\u542f\u9f7f\uff0c\u4f46\u770b\u4e86\u5bf9\u65b9\u90a3\u6a21\u6837\uff0c\u8bdd\u5230\u5507\u8fb9\uff0c\u53c8\u5b9e\u5728\u4e0d\u5f97\u4e0d\u54bd\u56de\u53bb\u3002 \u3000\u3000\u6700\u540e\uff0c\u4ed6\u5e72\u8106\u76f8\u5bf9\u65b9\u8017\u7740\uff0c\u53cd\u6b63\u4ed6\u6709\u7684\u662f\u65f6\u95f4\uff0c\u770b\u8c01\u7740\u6025\u3002 \u3000\u3000\u8fd9\u79cd\u5c34\u5c2c\u5c40\u9762\u628a\u97e9\u5fb7\u8d77\u4e5f\u5f04\u5f97\u50f5\u5728\u90a3\u91cc\u4e2a\u77e5\u6240\u63aa\u3002 \u3000\u3000\u5c82\u77e5\u8427\u6e58\u5df2\u4e0d\u8010\u70e6\uff0c\u8d77\u8eab\u4fbf\u8981\u79bb\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u60a0\u7136\u7ad9\u8eab\u800c\u8d77\uff0c\u9ad8\u58f0\u9053\uff1b\u201c\u8427\u9986\u4e3b\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u8427\u6e58\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u8427\u67d0\u542c\u8bf4\u5cb3\u516c\u5b50\u6709\u4e8b\uff0e\u6240\u4ee5\u624d\u653e\u4e0b\u516c\u52a1\u8d76\u5230\u5927\u5385\u6765\uff0c\u73b0\u5728\u89c1\u5cb3\u516c\u5b50\u6ca1 \u4e8b\uff0c\u90a3\u5c31\u53ea\u6709\u8bf7\u6055\u4e0d\u80fd\u76f8\u966a\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5f3a\u5fcd\u7740\u6012\u6c14\u9053\uff1a\u201c\u8427\u9986\u4e3b\u600e\u77e5\u5728\u4e0b\u6ca1\u4e8b\uff1f\u201d \u3000\u3000\u8427\u6e58\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u5750\u5728\u90a3\u91cc\u4e00\u8a00\u4e0d\u53d1\uff0c\u53ef\u50cf\u6709\u4e8b\u7684\uff1f\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u8fd8\u771f\u628a\u5cb3\u5c0f\u98de\u95ee\u4f4f\u4e86\uff0c\u4ed6\u53ea\u597d\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u90a3\u4e48\u8427\u9986\u4e3b\u8bf7\u5750\uff0c\u5728\u4e0b\u73b0\u5728\u5c31\u6709\u8bdd \u5949\u544a\u3002\u201d \u3000\u3000\u8427\u6e58\u91cd\u53c8\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u80fd\u518d\u7b49\uff0c\u4ee5\u514d\u53c8\u7ed9\u5bf9\u65b9\u501f\u53e3\uff0c\u8f7b\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u4e0a\u6b21\u628a\u5973\u4eba\u5f15\u8fdb\u5206\u9986\uff0c\u8427 \u9986\u4e3b\u60f3\u5fc5\u77e5\u9053\u90a3\u4e2a\u5973\u7684\u662f\u8c01\u5427\uff1f\u201d \u3000\u3000\u8427\u6e58\u5f04\u4e0d\u6e05\u5bf9\u65b9\u7528\u610f\uff0c\u62c2\u53d1\u51b7\u51b7\u4e00\u7b11\u9053\uff1b\u201c\u4e0a\u6b21\u4f60\u5df2\u7ecf\u8bf4\u8fc7\uff0c \u3000\u3000\u8427\u67d0\u8fd8\u6ca1\u5fd8\uff0c\u96be\u9053\u5cb3\u516c\u5b50\u81ea\u5df1\u53cd\u800c\u8bb0\u4e0d\u8d77\u90a3\u5973\u7684\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u5f53\u7136\u8bb0\u5f97\uff0c\u90a3\u5973\u7684\u662f\u706b\u5173\u7684\u4eba\uff0c\u540d\u53eb\u6843\u82b1\u3002\u201d \u3000\u3000\u8427\u6e58\u6545\u505a\u4e00\u6123\u9053\uff1a\u201c\u65e2\u7136\u5cb3\u516c\u5b50\u8bb0\u5f97\u8fd9\u6837\u6e05\u695a\uff0c\u4e3a\u4ec0\u4e48\u7adf\u7136\u95ee\u8d77\u8427\u67d0\u6765\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5728\u4e0b\u8fd9\u6837\u505a\uff0c\u4e0d\u8fc7\u662f\u60f3\u52a0\u6df1\u8427\u9986\u4e3b\u7684\u5370\u8c61\u800c\u5df2\u3002\u201d \u3000\u3000\u8427\u6e58\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5728\u4e0b\u542c\u8bf4\u90a3\u4f4d\u6843\u82b1\u59d1\u5a18\u5df2\u88ab\u706b\u5173\u7edf\u9886\u56da\u7981\u8d77\u6765\uff0c \u3000\u3000\u800c\u4e14\u53d7\u5230\u4e25\u5211\u62f7\u6253\uff0c\u5728\u4e0b\u4eca\u5929\u6765\u5230\u603b\u9986\u62dc\u89c1\u8427\u9986\u4e3b\uff0c\u5c31\u662f\u8981\u6c42\u8427\u9986\u4e3b\u901a\u77e5\u706b\u5173\uff0c\u628a\u6843\u82b1 \u59d1\u5a18\u91ca\u653e\u51fa\u6765\u3002\u201d \u3000\u3000\u8427\u6e58\u563f\u563f\u4e00\u9635\u5927\u7b11\u9053\uff1b\u201c\u5cb3\u516c\u5b50\u8fd9\u8bdd\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\uff0c\u62db\u8d24\u9986\u548c\u706b\u5173\u5e76\u65e0\u4ece\u5c5e\u5173\u7cfb\uff0c\u8427\u67d0 \u54ea\u6709\u8d44\u683c\u901a\u77e5\u706b\u5173\u653e\u4eba\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u8427\u9986\u4e3b\u5fd2\u8c26\u4e86\uff0c\u542c\u8bf4\u706b\u5173\u7edf\u9886\u662f\u8427\u9986\u4e3b\u7684\u80de\u59b9\uff0c\u5c31\u51ed\u8fd9\u70b9\u5173\u7cfb\uff0c\u8427\u9986\u4e3b\u4e5f\u5e2e\u5f97\u4e0a \u5fd9\u3002\u201d \u3000\u3000\u8427\u6e58\u5bf9\u5cb3\u5c0f\u98de\u5c45\u7136\u77e5\u9053\u81ea\u5df1\u548c\u706b\u5173\u7edf\u9886\u662f\u5144\u59b9\uff0c\u96be\u514d\u6709\u4e9b\u8bb6\u5f02\uff0c\u4f46\u53c8\u4e0d\u4fbf\u5f53\u573a\u76d8\u95ee\uff0c\u7565 \u2014\u6c89\u541f\u9053\uff1a\u201c\u8427\u67d0\u8eab\u4e3a\u62db\u8d24\u9986\u4e3b\uff0c\u5c82\u53ef\u516c\u79c1\u4e0d\u5206\uff0c\u706b\u5173\u7edf\u9886\u867d\u662f\u820d\u59b9\uff0c\u4f46\u5979\u79c9\u516c\u5904\u4e8b\uff0c\u8427\u67d0 \u600e\u80fd\u5984\u52a0\u5e72\u6d89\uff01\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u8427\u9986\u4e3b\u662f\u4e0d\u80af\u5e2e\u8fd9\u4e2a\u5fd9\u4e86\uff1f\u201d \u3000\u3000\u201c\u8427\u67d0\u5e76\u975e\u4e0d\u80af\u5e2e\u5fd9\uff0c\u800c\u662f\u5b9e\u5728\u65e0\u80fd\u4e3a\u529b\uff0c\u8427\u67d0\u4e0d\u89e3\uff0c\u6843\u82b1\u4e0d\u8fc7\u662f\u706b\u5173\u4e00\u540d\u5973\u5175\uff0e\u600e\u503c \u5f97\u5cb3\u516c\u5b50\u5982\u6b64\u5173\u5fc3\u3002\u201d \u3000\u3000\u201c\u8427\u9986\u4e3b\u8bdd\u4e0d\u80fd\u8fd9\u4e48\u8bf4\uff0c\u6843\u82b1\u88ab\u56da\u7981\uff0c\u662f\u56e0\u5728\u4e0b\u800c\u8d77\uff0c\u81f3\u4e8e\u5979\u662f\u4ec0\u4e48\u8eab\u4efd\uff1f\u90a3\u53c8\u662f\u53e6\u5916 \u4e00\u56de\u4e8b\uff0c\u5373\u4f7f\u8d35\u5982\u7687\u5e1d\uff0c\u4e5f\u662f\u4e00\u4e2a\u4eba\uff0c\u5728\u5728\u4e0b\u773c\u4e2d\uff0c\u7687\u5e1d\u4e0e\u5e73\u6c11\uff1f\u5b9e\u5728\u6ca1\u4ec0\u4e48\u591a\u5927\u5206\u522b\uff01\u201d \u3000\u3000\u8427\u6e58\u8138\u4e0a\u4e0d\u5e26\u4efb\u4f55\u8868\u60c5\uff0c\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u8bf7\u539f\u8c05\uff0c\u8427\u67d0\u4e0d\u6562\u4ee5\u79c1\u5fc3\u5e9f\u5236\uff0c\u8fd9\u4ef6\u4e8b \u5b9e\u5728\u5e2e\u4e0d\u4e0a\u5fd9\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u968f\u5373\u8d77\u8eab\uff0c\u4e5f\u62f1\u62f1\u624b\u9053\uff1a\u201c\u90a3\u662f\u5728\u4e0b\u6253\u6270\u4e86\uff0c\u544a\u8f9e\uff01\u201d \u3000\u3000\u6b64\u65f6\uff0c\u4ed6\u771f\u6709\u4e9b\u8d70\u6295\u65e0\u8def\u7684\u611f\u89c9\u3002 \u3000\u3000\u8fde\u5e26\u7684\uff0c\u4e5f\u5bf9\u516c\u5b59\u7389\u5927\u8d77\u53cd\u611f\u3002 \u3000\u3000\u79bb\u5f00\u603b\u9986\uff0c\u6b63\u8d70\u5230\u534a\u8def\uff0c\u610f\u5916\u7684\u5374\u5ffd\u542c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u4f60\u5230\u54ea\u91cc\u53bb\uff0c\u54b1\u603b\u7b97\u770b\u5230 \u4f60\u4e86\uff01\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u662f\u591a\u4e48\u719f\u6089\uff0c\u591a\u4e48\u4eb2\u5207\uff0c\u5cb3\u5c0f\u98de\u771f\u662f\u559c\u51fa\u671b\u5916\uff0c\u8f6c\u5934\u671b\u53bb\uff0c\u4e01\u6d9b\u6b63\u4ece\u5de6\u4fa7\u56db\u4e94\u4e08 \u8fdc\u98de\u5954\u800c\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u505c\u4e0b\u811a\u6b65\u7b49\u4e01\u6d9b\u6765\u5230\u8ddf\u524d\uff0c\u8feb\u4e0d\u53ca\u5f85\u7684\u95ee\u9053\uff1a\u201c\u4f60\u662f\u600e\u4e48\u51fa\u6765\u7684\uff1f\u83ab\u975e\u5df2\u7ecf\u5206\u6d3e \u4e86\u5de5\u4f5c\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4ec0\u4e48\u5206\u6d3e\u5de5\u4f5c\uff0c\u6211\u662f\u8981\u5230\u603b\u9986\u627e\u9986\u4e3b\u7406\u8bba\u53bb\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6115\u7136\u9053\uff1a\u201c\u4f60\u8981\u7406\u8bba\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u6012\u51b2\u51b2\u7684\u9053\uff1a\u201c\u62db\u8d24\u9986\u4e3b\u8fd9\u4e2a\u738b\u516b\u86cb\uff0c\u5b9e\u5728\u53ef\u6076\uff0c\u628a\u4eba\u56f0\u5728\u5206\u9986\u91cc\uff0c\u95f7\u90fd\u95f7\u6b7b\u4e86\uff0c \u60f3\u51fa\u53bb\u770b\u770b\uff0c\u53c8\u4e0d\u51c6\u79bb\u5f00\u4e09\u5341\u6b65\uff0c\u516c\u5b50\u60f3\u60f3\u770b\uff0c\u4ed6\u628a\u54b1\u4eec\u8fd8\u5f53\u4eba\u5f85\u5417\uff1f\u7b80\u76f4\u8fde\u56da\u72af\u90fd\u4e0d\u5982\uff01\u201d \u3000\u3000\u201c\u4f60\u5230\u603b\u9986\uff0c\u5c31\u662f\u4e3a\u4e86\u8fd9\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e8b\u8fd8\u4e0d\u591f\u4e45\u5417\uff1f\u6211\u8981\u95ee\u95ee\u90a3\u9986\u4e3b\u6b63\u516b\u86cb\uff0c\u4ed6\u7a76\u7adf\u5b58\u7684\u4ec0\u4e48\u5fc3\u610f\uff1f\u201d \u3000\u3000\u201c\u4f60\u90a3\u5206\u9986\u7684\u9986\u50ee\u53ef\u540c\u610f\u4f60\u8dd1\u51fa\u6765\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5f53\u7136\u4e0d\u80af\uff0c\u6211\u5df2\u7ecf\u628a\u4ed6\u6346\u4e86\u8d77\u6765\u9501\u5728\u623f\u91cc\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4e2d\u5348\u8c01\u53bb\u62ff\u996d\uff1f\u201d \u3000\u3000\u201c\u5230\u4e86\u603b\u9986\uff0c\u8fd8\u6101\u6ca1\u6709\u996d\u5403\uff0c\u5373\u4f7f\u4e00\u987f\u4e0d\u5403\uff0c\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u628a\u62c9\u4f4f\u4e01\u6d9b\u9053\uff1a\u201c\u6211\u529d\u4f60\u8fd8\u662f\u522b\u53bb\u7684\u597d\uff0c\u5426\u5219\u60f9\u51fa\u7978\u4e8b\uff0c\u5403\u4e8f\u7684\u662f\u4f60\u81ea\u5df1\uff01\u201d \u3000\u3000\u4ed6\u4e0d\u80fd\u4e0d\u4e3a\u4e01\u6d9b\u987e\u8651\uff0c\u56e0\u4e3a\u4e01\u6d9b\u4e0d\u80fd\u548c\u4ed6\u81ea\u5df1\u6bd4\uff0c\u5230\u4e86\u603b\u9986\uff0c \u3000\u3000\u8bf4\u4e0d\u5b9a\u8427\u6e58\u7acb\u523b\u5c31\u4f1a\u628a\u4ed6\u6253\u5165\u6b7b\u7262\uff0c\u5373\u4f7f\u52a8\u8d77\u624b\u6765\uff0c\u4ee5\u4e01\u6d9b\u7684\u8eab\u624b\uff0c\u5fc5\u5b9a\u5f88\u5feb\u4fbf\u4f1a\u88ab\u603b \u9986\u91cc\u7684\u4eba\u5236\u670d\uff0c\u90a3\u6837\u4e00\u6765\uff0c\u540e\u679c\u5c31\u8d8a\u53d1\u4e0d\u582a\u8bbe\u60f3\u4e86\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u4ed6\u5fc5\u987b\u529d\u963b\u4e01\u6d9b\u7684\u9c81\u83bd\u884c\u52a8\u3002 \u3000\u3000\u53ea\u89c1\u4e01\u6d9b\u7ffb\u4e86\u7ffb\u725b\u773c\u9053\uff1a\u201c\u90a3\u4e48\u516c\u5b50\u662f\u5230\u54ea\u91cc\u53bb\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u662f\u4ece\u603b\u9986\u521a\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u516c\u5b50\u80fd\u53bb\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u53bb\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u597d\u9a97\u4ed6\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u662f\u8499\u9986\u4e3b\u53ec\u89c1\u7684\u3002\u201d \u3000\u3000\u4e01\u6d9b\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u83ab\u975e\u516c\u5b50\u5df2\u7ecf\u5206\u6d3e\u5de5\u4f5c\u4e86\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5927\u6982\u5feb\u5566\uff0c\u6240\u4ee5\u6211\u529d\u4f60\u6682\u65f6\u8fd8\u662f\u5fcd\u8010\u4e9b\uff0c\u7b49\u6211\u4e0b\u6b21\u6709\u673a\u4f1a\u89c1\u5230\u9986\u4e3b\u65f6\uff0c\u4e00 \u5b9a\u5411\u4ed6\u63d0\u4e00\u63d0\u4f60\uff0c\u8bf7\u4ed6\u5c3d\u65e9\u7ed9\u4f60\u5206\u6d3e\u5de5\u4f5c\u3002\u201d \u3000\u3000\u4e01\u6d9b\u5bf9\u5cb3\u5c0f\u98de\u7684\u8bdd\uff0c\u5012\u662f\u5341\u5206\u80af\u542c\uff0c\u540c\u65f6\u4ed6\u66f4\u77e5\u9053\u81ea\u5df1\u4e0d\u8bba\u90a3\u4e00\u65b9\u9762\uff0c\u90fd\u4e0d\u80fd\u548c\u5cb3\u5c0f\u98de \u76f8\u6bd4\uff0c\u5cb3\u5c0f\u98de\u80fd\u5148\u8499\u9986\u4e3b\u53ec\u89c1\uff0c\u4e5f\u662f \u3000\u3000\u7406\u6240\u5f53\u7136\u3002 \u3000\u3000\u4ed6\u53ea\u597d\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0e\u6211\u542c\u516c\u5b50\u7684\uff0c\u6682\u65f6\u5c31\u4e0d\u53bb\u627e\u9986\u4e3b\u4e86\uff0c \u3000\u3000\u516c\u5b50\u6b64\u523b\u53c8\u8981\u5230\u54ea\u91cc\u5360\u5462\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u9986\u4e3b\u53ec\u89c1\u5b8c\u6bd5\uff0c\u5f53\u7136\u8fd8\u8981\u56de\u5230\u5206\u9986\u3002\u201d \u3000\u3000\u201c\u516c\u5b50\u4f4f\u54ea\u4e00\u53f7\u5206\u9986\uff1f\u201d \u3000\u3000\u201c\u4e09\u5341\u516d\u53f7\u3002\u201d \u3000\u3000\u4e01\u6d9b\u731b\u4e00\u8dfa\u811a\uff0c\u9a82\u9053\uff1a\u201c\u4ed6\uff0a\uff0a\uff0a\uff0c\u628a\u4eba\u8fd8\u8981\u7f16\u53f7\uff0c\u7b80\u76f4\u628a\u54b1\u4eec\u5f53\u6210\u7a91\u59d0\u513f\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fd9\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u5206\u9986\u6709\u4e03\u5341\u4e8c\u53f7\uff0c\u623f\u5b50\u7684\u5f62\u5f0f\u90fd\u662f\u4e00\u6837\u7684\uff0c\u53ea\u6709\u7f16\u51fa\u53f7\u7801\uff0c \u627e\u8d77\u6765\u624d\u8f83\u4e3a\u65b9\u4fbf\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9ed8\u4e86\u2014\u9ed8\u9053\uff1a\u201c\u516c\u5b50\u8fd9\u51e0\u5929\u770b\u89c1\u8881\u5c11\u4fa0\u548c\u7518\u9716\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6447\u5934\u9053\uff1a\u201c\u6211\u9664\u4e86\u8fd9\u6b21\u9986\u4e3b\u53ec\u89c1\uff0c\u4e5f\u662f\u4e0d\u80fd\u8fdc\u79bb\u5206\u9986\uff0c\u53c8\u600e\u4f1a\u770b\u5230\u4ed6\u4eec\u5462\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u53c8\u4e00\u8dfa\u811a\uff0c\u518d\u5ea6\u9a82\u9053\uff1a\u201c\u62db\u8d24\u9986\u4e3b\u8fd9\u72d7\u5a18\u517b\u7684\uff0c\u5b9e\u5728\u4e0d\u591f\u4eba\u60c5\uff0c\u6211\u548c\u7518\u9716\uff0c\u4e00\u5411\u662f \u5f62\u5f71\u4e0d\u79bb\uff0c\u73b0\u5728\u5374\u88ab\u4ed6\u62c6\u6563\u5f97\u597d\u51e0\u5929\u4e0d\u7740\u9762\uff0c\u8fd9\u738b\u516b\u86cb\u600e\u4e48\u8fd9\u6837\u53ef\u6076\uff01\u201d \u3000\u3000\u63a5\u7740\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u4e9b\u5929\u6211\u8fde\u4f5c\u68a6\u4e5f\u60f3\u7740\u8001\u7518\uff0c\u5f53\u7136\u4e5f\u60f3\u5ff5\u516c\u5b50\u548c\u8881\u5c11\u4fa0\uff0c\u82e5\u518d\u8fc7\u51e0 \u5929\u89c1\u4e0d\u5230\u9762\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u628a\u4eba\uff0a\u75af\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u529d\u4f60\u5fc5\u987b\u591a\u5fcd\u8010\uff0c\u4e3a\u4eba\u5728\u4e16\uff0c\u4e0d\u53d7\u70b9\u714e\u71ac\u662f\u96be\u4ee5\u51fa\u5934\u7684\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u8981\u5fcd\u8010\u5230\u4ec0\u4e48\u65f6\u5019\u5462\uff1f\u201d \u3000\u3000\u201c\u5f88\u5feb\uff0c\u4f60\u4f4f\u54ea\u91cc\uff0c\u8fc7\u51e0\u5929\u6211\u53bb\u770b\u4f60\u3002\u201d \u3000\u3000\u201c\u6211\u4f4f\u5341\u4e09\u53f7\uff0c\u73b0\u5728\u5c31\u5148\u5230\u516c\u5b50\u90a3\u91cc\u53bb\u5750\u4f1a\u513f\u5427\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u867d\u5e0c\u671b\u4e01\u6d9b\u53bb\u5750\u5750\uff0c\u4f46\u53c8\u62c5\u5fc3\u56e0\u800c\u518d\u51fa\u7eb0\u6f0f\uff0c\u53ea\u597d\u5a49\u8a00\u529d\u4ed6\u56de\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u5206\u624b\u540e\uff0c\u5cb3\u5c0f\u98de\u56de\u5230\u5206\u9986\uff0c\u6797\u91d1\u5b9d\u65e9\u5df2\u62ff\u996d\u56de\u6765\u4e86\u3002 \u3000\u3000\u6797\u91d1\u5b9d\u4e0d\u6562\u95ee\u5cb3\u5c0f\u98de\u5728\u603b\u9986\u505a\u4e86\u4e9b\u4ec0\u4e48\uff0c\u4e0d\u8fc7\u5374\u6253\u5fc3\u5e95\u4f69\u670d\u5cb3\u5c0f\u98de\u795e\u901a\u5e7f\u5927\uff0c\u8eab\u65e0\u901a\u544a \u8170\u724c\u5c45\u7136\u80fd\u6765\u53bb\u81ea\u5982\uff0c\u8fd9\u662f\u5728\u4ece\u524d\u6839\u672c\u6ca1\u6709\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4e0d\u77e5\u9053\u5cb3\u5c0f\u98de\u6b64\u523b\u5185\u5fc3\u662f\u591a\u4e48\u7126\u8e81\u3002 \u3000\u3000\u53c8\u71ac\u8fc7\u4e00\u591c\uff0c\u516c\u5b59\u7389\u4ecd\u672a\u5230\u6765\u3002 \u3000\u3000\u76f4\u5230\u6b21\u65e5\u65e9\u996d\u540e\uff0c\u516c\u5b59\u7389\u603b\u7b97\u6765\u4e86\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u5f97\u4e0d\u5f3a\u5fcd\u7740\u6124\u95f7\u4e4b\u6c14\u628a\u4ed6\u63a5\u5230\u5ba2\u5385\uff0c\u4f46\u795e\u8272\u95f4\u5374\u4e0d\u518d\u8868\u793a\u4eb2\u5207\u3002 \u3000\u3000\u5750\u4e0b\u540e\uff0c\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u665a\u8f88\u8fd8\u4ee5\u4e3a\u516c\u5b59\u5148\u751f\u4e0d\u518d\u6765\u4e86\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u6ca1\u60f3\u5230\u5cb3\u5c0f\u98de\u4f1a\u662f\u5982\u6b64\u6001\u5ea6\uff0c\u8138\u4e0a\u4e00\u70ed\u9053\uff1a\u201c\u6211\u77e5\u9053\u5cb3\u516c\u5b50\u5fc3\u91cc\u7740\u6025\uff0c\u5176\u5b9e\u6211\u53c8 \u4f55\u5c1d\u4e0d\u6025\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5982\u679c\u516c\u5b59\u5148\u751f\u4e0d\u6025\uff0c\u4e0d\u77e5\u8be5\u4ec0\u4e48\u65f6\u5019\u6765\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u4e0d\u89c9\u8138\u8272\u4e00\u53d8\uff0c\u4f46\u6700\u540e\u8fd8\u662f\u5fcd\u4e86\u4e0b\u6765\u9053\uff1a\u201c\u4e5f\u96be\u602a\u5cb3\u516c\u5b50\u4f1a\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\uff0c\u4e8b\u60c5 \u662f\u8fd9\u6837\u7684\uff0c\u5728\u4e0b\u53d7\u516c\u5b50\u4e4b\u6258\uff0c\u5f53\u5929\u5c31\u8d76\u5230\u603b\u575b\u51c6\u5907\u5411\u5927\u516c\u4e3b\u800c\u62a5\uff0c\u5c82\u77e5\u5927\u516c\u4e3b\u6709\u4e8b\u5230\u8c37\u5916\u53bb \u4e86\uff0c\u76f4\u5230\u6628\u5929\u4e0b\u5348\u624d\u56de\u6765\uff0c\u4e8b\u60c5\u5c31\u662f\u8fd9\u6837\u803d\u8bef\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u65e0\u6cd5\u65ad\u5b9a\u5bf9\u65b9\u8fd9\u756a\u8bf4\u8bcd\u662f\u771f\u662f\u5047\uff0c\u53ea\u597d\u62b1\u62f3\u4e00\u793c\u9053\uff1a \u3000\u3000\u201c\u665a\u8f88\u62b1\u6b49\uff0c\u9519\u602a\u4e86\u516c\u5b59\u5148\u751f\uff0c\u4e0d\u8fc7\u516c\u5b59\u5148\u751f\u4e0d\u96be\u60f3\u89c1\u665a\u8f88\u8fd9\u4e24\u5929\u5185\u5fc3\u662f\u591a\u4e48\u7740\u6025\uff0c\u5927 \u516c\u4e3b\u6709\u4ec0\u4e48\u5904\u7f6e\uff0c\u8fd8\u8bf7\u5feb\u5feb\u8d50\u544a\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u5927\u516c\u4e3b\u6700\u521d\u672c\u60f3\u4ea4\u4ee3\u8427\u9986\u4e3b\u901a\u77e5\u706b\u5173\u7edf\u9886\u4e0d\u5f97\u5904\u5206\u6843\u82b1\uff0c\u4f46\u6211\u8ba4\u4e3a\u8fd9\u6837\u505a \u4e0d\u59a5\u5f53\uff0c\u5927\u516c\u4e3b\u987a\u4ece\u6211\u7684\u610f\u601d\u53c8\u6539\u53d8\u4e86\u4e3b\u610f\u3002\u201d \u3000\u3000\u201c\u516c\u5b59\u5148\u751f\u8ba4\u4e3a\u90a3\u6837\u505a\u6709\u4ec0\u4e48\u4e0d\u59a5\u5f53\uff1f\u201d \u3000\u3000\u201c\u8427\u9986\u4e3b\u662f\u4e2a\u57ce\u5e9c\u6df1\u6c89\u7684\u4eba\uff0c\u4e07\u4e00\u4ed6\u56e0\u8fd9\u4e8b\u607c\u7f9e\u6210\u6012\uff0c\u6697\u4e2d\u4ea4\u4ee3\u706b\u5173\u7edf\u9886\u628a\u6843\u82b1\u59d1\u5a18\u5148 \u884c\u5904\u6b7b\u600e\u4e48\u529e\u4e86\u5cb3\u516c\u5b50\u53ef\u66fe\u60f3\u5230\u8fd9\u4e00\u6b65\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6562\u8fd9\u6837\u505a\u4e48\uff1f\u90a3\u53c8\u5982\u4f55\u5411\u5927\u516c\u4e3b\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u201c\u5f88\u7b80\u5355\uff0c\u4ed6\u53ef\u8c0e\u62a5\u6843\u82b1\u59d1\u5a18\u662f\u754f\u7f6a\u81ea\u6740\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u89c9\u5fc3\u5934\u4e00\u9707\uff0c\u679c\u7136\u516c\u5b59\u7389\u8bbe\u60f3\u5468\u5230\uff0c\u89c1\u5730\u9ad8\u51fa\u81ea\u5df1\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u771f\u540e\u6094\u6628\u65e5\u4e0d\u8be5\u53bb\u6c42\u8427\u6e58\uff0c\u4e07\u2014\u6843\u82b1\u56e0\u800c\u906d\u4e86\u6bd2\u624b\uff0c\u5c82\u4e0d\u7b49\u4e8e\u662f\u81ea\u5df1\u6789\u9001\u6843\u82b1\u4e00 \u547d\uff1f\u2026\u2026 \u3000\u3000\u4ed6\u771f\u7684\u4e0d\u6562\u518d\u5f80\u4e0b\u60f3\uff0c\u540c\u65f6\u4e5f\u4e0d\u4fbf\u628a\u6c42\u8fc7\u8427\u6e58\u7684\u4e8b\u8ba9\u516c\u5b59\u7389\u77e5\u9053\u3002 \u3000\u3000\u516c\u5b59\u7389\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1b\u201c\u5927\u516c\u4e3b\u542c\u4ece\u4e86\u6211\u7684\u5efa\u8bae\uff0c\u6d3e\u6211\u5230\u706b\u5173\u5f53\u9762\u544a\u77e5\u8427\u7edf\u9886\u4e0d\u53ef\u518d\u5904\u5206 \u6843\u82b1\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fd9\u6837\u56fa\u7136\u5f88\u597d\uff0c\u4f46\u6843\u82b1\u59d1\u5a18\u4ece\u4eca\u540e\u5728\u706b\u5173\u5374\u5fc5\u7136\u8fc7\u5f97\u8d8a\u53d1\u75db\u82e6\u4e86\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u9894\u9996\u9053\uff1a\u201c\u8fd9\u662f\u5fc5\u7136\u7684\uff0c\u706b\u5173\u7edf\u9886\u8427\u7476\uff0c\u636e\u8bf4\u548c\u5979\u54e5\u54e5\u8427\u9986\u4e3b\u540c\u6837\u7684\u9634\u6c89\u9669\u8bc8\uff0c \u5979\u867d\u7136\u4e0d\u5f97\u4e0d\u653e\u51fa\u6843\u82b1\u6765\uff0c\u4f46\u6843\u82b1\u4eca\u540e\u7684\u65e5\u5b50\u786e\u5b9e\u4e0d\u4f1a\u597d\u8fc7\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6c89\u601d\u4e86\u534a\u664c\u9053\uff1a\u201c\u5927\u516c\u4e3b\u82e5\u771f\u662f\u4e3a\u6843\u82b1\u7740\u60f3\uff0c\u5c31\u8be5\u628a\u5979\u8c03\u5230\u603b\u575b\u6216\u53e6\u5916\u7684\u5730\u65b9 \u53bb\u3002\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u5230\u8fd9\u4e00\u5c42\uff0c\u4f46\u5f53\u65f6\u5374\u4e0d\u65b9\u4fbf\u8bb2\u51fa\u6765\uff0c\u6843\u82b1\u53ea\u4e0d\u8fc7\u706b\u5173\u7684\u4e00\u540d\u5973\u5175\uff0c \u6211\u82e5\u8fd9\u6837\u5efa\u8bae\uff0c\u5927\u516c\u4e3b\u5f88\u53ef\u80fd\u4f1a\u602a\u6211\u5c0f\u9898\u5927\u4f5c\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fd9\u6b21\u4e0d\u80fd\u602a\u516c\u5b59\u7389\uff0c\u7ad9\u5728\u516c\u5b59\u7389\u7684\u7acb\u573a\uff0c\u8fd9\u6837\u5411\u5927\u516c\u4e3b\u5efa\u8bae\uff0c\u7684\u786e\u6709\u4e9b\u5c0f\u9898\u5927\u4f5c\u3002 \u3000\u3000\u516c\u5b59\u7389\u4f3c\u662f\u60f3\u8bd5\u63a2\u5cb3\u5c0f\u98de\u7684\u5fc3\u610f\uff0e\u6c89\u541f\u4e86\u534a\u54cd\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u8fd8\u6709\u4ec0\u4e48\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u65e2\u7136\u5927\u516c\u4e3b\u8fd9\u6837\u5429\u5490\u4e0b\u6765\uff0c\u5df2\u7ecf\u7b97\u5f88\u7167\u987e\u665a\u8f88\u4e86\uff0c \u3000\u3000\u665a\u8f88\u73b0\u5728\u6240\u5e0c\u671b\u7684\uff0c\u662f\u5426\u80fd\u968f\u516c\u5b59\u5148\u751f\u4e00\u8d77\u5230\u706b\u5173\u53bb\u4e00\u8d9f\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u4e0d\u7f6e\u53ef\u5426\u7684\u95ee\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u5fc5\u8981\u5417\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u665a\u8f88\u89c9\u5f97\u552f\u6709\u4eb2\u773c\u89c1\u6843\u82b1\u4e00\u9762\uff0c\u624d\u80fd\u5fc3\u5b89\u3002\u201d \u3000\u3000\u516c\u5b59\u7389\u8e59\u8d77\u53cc\u7709\uff0c\u8bb8\u4e45\u624d\u9053\uff1b\u201c\u4e5f\u597d\uff0c\u6b63\u597d\u5927\u516c\u4e3b\u7ed9\u4e86\u6211\u4e24\u679a\u901a\u884c\u8170\u724c\uff0c\u8bf4\u4e0d\u5b9a\u5979\u4e5f\u65e9 \u6709\u6b64\u610f\uff0c\u53ea\u662f\u5f53\u65f6\u4e0d\u66fe\u660e\u8a00\u7f62\u4e86\u3002\u201d \u3000\u3000\u201c\u516c\u5b59\u5148\u751f\u5728\u7687\u5143\u6559\u4e2d\u8eab\u4efd\u5df2\u7ecf\u4e0d\u4f4e\uff0c\u96be\u9053\u5916\u51fa\u4e5f\u8981\u901a\u884c\u8170\u724c\uff1f\u201d \u3000\u3000\u201c\u5728\u5929\u8c37\u4e2d\u98df\u82e5\u6ca1\u6709\u901a\u884c\u8170\u724c\uff0c\u53ef\u8bf4\u5bf8\u6b65\u96be\u884c\uff0c\u6211\u5728\u62db\u8d24\u9986\u5730\u533a\u5185\u884c\u52a8\uff0c\u5f53\u7136\u7528\u4e0d\u7740\u8170 \u724c\uff0c\u5230\u603b\u575b\u5c31\u5fc5\u987b\u901a\u884c\u603b\u575b\u7684\u8170\u724c\uff0c\u81f3\u4e8e\u5230\u4e94\u5173\u53bb\uff0c\u5728\u62db\u8d24\u9986\u9664\u9986\u4e3b\u526f\u9986\u4e3b\u5916\uff0c\u5176\u6709\u4f59\u7684\u4eba \u90fd\u5fc5\u987b\u53e6\u5907\u901a\u884c\u4e94\u5173\u7684\u8170\u724c\u3002\u6211\u81ea\u7136\u4e5f\u4e0d\u4f8b\u5916\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u671b\u671b\u5929\u8272\u9053\uff1b\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u73b0\u5728\u5c31\u8bf7\u516c\u5b59\u5148\u751f\u5e26\u665a\u8f88\u540c\u53bb\u5982\u4f55\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u7ad9\u8d77\u8eab\uff0c\u4ea4\u7ed9\u5cb3\u5c0f\u98de\u4e00\u5757\u8170\u724c\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u8bf7\u4ea4\u4ee3\u8fd9\u91cc\u7684\u9986\u50ee\u6797\u91d1\u5b9d\uff0c\u4ed6\u5230\u603b\u575b \u62ff\u996d\u65f6\uff0c\u5343\u4e07\u4e0d\u53ef\u63d0\u8d77\u4f60\u6211\u5230\u706b\u5173\u7684\u4e8b\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u968f\u5373\u4ea4\u4ee3\u8fc7\u6797\u91d1\u5b9d\u3002 \u3000\u3000\u7531\u8fd9\u91cc\u5230\u706b\u5173\uff0c\u5927\u7ea6\u5341\u51e0\u91cc\u8def\uff0c\u4e2d\u95f4\u5c1a\u987b\u7ecf\u8fc7\u5bb5\u5173\u548c\u6c34\u5173\u3002 \u3000\u3000\u5230\u8fbe\u5bb5\u5173\uff0c\u867d\u7136\u7ecf\u8fc7\u4e0d\u5c11\u5e02\u8857\uff0c\u8857\u4e0a\u5374\u51b7\u6e05\u6e05\uff0c\u5f88\u5c11\u770b\u5230\u884c\u4eba\uff0c\u5f53\u771f\u540d\u526f\u5176\u5b9e\u7684\u5bb5\u5173\uff1f \u5fc5\u987b\u5230\u665a\u4e0a\u624d\u80fd\u70ed\u95f9\u8d77\u6765\u3002 \u3000\u3000\u4e0d\u8fc7\u5de7\u5f97\u5f88\uff0c\u504f\u504f\u53ea\u9047\u5230\u4e24\u4eba\uff0c\u5cb3\u5c0f\u98de\u7adf\u5bf9\u8fd9\u4e24\u4eba\u5168\u8ba4\u8bc6\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u4f4d\u662f\u8001\u592a\u592a\uff0c\u4e00\u4f4d\u662f\u5c11\u5987\uff0c\u5979\u4eec\u6b63\u662f\u201c\u6148\u6bcd\u671b\u513f\u201d\u548c\u201c\u5be1\u5987\u76fc\u592b\u201d\u4e24\u7ad9\u6d4b\u8bd5\u8fc7 \u5cb3\u5c0f\u98de\u7684\u3002 \u3000\u3000\u8001\u592a\u592a\u548c\u5c11\u5987\u4e5f\u662f\u597d\u8bb0\u6027\uff0c\u89c1\u4e86\u5cb3\u5c0f\u98de\uff0c\u5374\u9732\u51fa\u4f1a\u5fc3\u5fae\u7b11\uff0c\u5f04\u5f97\u5cb3\u5c0f\u98de\u53cd\u800c\u6709\u4e9b\u4e0d\u597d\u610f \u601d\u3002 \u3000\u3000\u5bb5\u5173\u7684\u57ce\u95e8\uff0c\u767d\u5929\u662f\u5173\u7740\u7684\uff0c\u6709\u8170\u724c\u7684\u4eba\uff0c\u53ef\u4ece\u65c1\u8fb9\u4e00\u5904\u72ed\u7a84\u7684\u4fa7\u95e8\u901a\u884c\u3002 \u3000\u3000\u51fa\u4e86\u57ce\uff0c\u5cb3\u5c0f\u98de\u642d\u8baa\u7740\u95ee\u9053\uff1b\u201c\u516c\u5b59\u5148\u751f\u53ef\u8ba4\u8bc6\u5bb5\u5173\u7684\u7edf\u9886\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u5f53\u7136\u89c1\u8fc7\uff0c\u53ea\u662f\u5e76\u4e0d\u592a\u719f\uff0e\u4e5f\u662f\u4e00\u4f4d\u5973\u7684\uff0c\u53eb\u51e4\u5ae3\u7ea2\u3002\u201d \u3000\u3000\u201c\u542c\u8bf4\u4e94\u5173\u4e4b\u4e2d\uff0c\u4ee5\u5bb5\u5173\u7ba1\u7684\u4eba\u6700\u591a\uff0c\u5730\u9762\u4e5f\u6700\u5927\uff0c\u5973\u7684\u80fd\u5f53\u4e0a\u7edf\u9886\uff0c\u53ef\u89c1\u5fc5\u662f\u6709\u4e9b\u6765 \u5934\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u81ea\u7136\uff0c\u636e\u8bf4\u8fd9\u4f4d\u51e4\u7edf\u9886\u662f\u6559\u4e3b\u4eb2\u4fe1\uff0c\u5341\u5206\u5f97\u5ba0\uff0c\u518d\u52a0\u4e0a\u6709\u4e00\u8eab\u7edd\u9ad8\u7684\u6b66\u529f\uff0c\u51ed\u7740 \u8fd9\u4e24\u79cd\u5173\u7cfb\uff0e\u88ab\u6d3e\u4efb\u5bb5\u5173\u7edf\u9886\uff0c\u5c31\u4e0d\u8db3\u4e3a\u5947\u4e86\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u987f\u4f7f\u5cb3\u5c0f\u98de\u5fc3\u60c5\u53c8\u5927\u4e3a\u6df1\u91cd\uff0c\u5982\u679c\u51e4\u5ae3\u7ea2\u5df2\u5f97\u5ba0\u7687\u5143\u6559\u4e3b\uff0c\u8981\u60f3\u8ba8\u56de\u5979\u548c\u9a6c \u662d\u96c4\u76d7\u8d70\u7684\u201c\u70bc\u5fc3\u5927\u6cd5\u79d8\u7b08\u201d\uff0c\u90a3\u5c31\u5341\u5206\u68d8\u624b\u4e86\uff0c\u540c\u65f6\u4e5f\u96be\u514d\u8981\u4e3a\u8881\u5c0f\u9e64\u62c5\u5fc3\u3002 \u3000\u3000\u4ed6\u642d\u8baa\u7740\u518d\u95ee\u9053\uff1b\u201c\u8fd9\u4f4d\u51e4\u7edf\u9886\u6709\u4e08\u592b\u6ca1\u6709\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u8fd9\u65b9\u9762\u6211\u4e0d\u5927\u6e05\u695a\uff0c\u56e0\u4e3a\u6559\u4e3b\u6709\u89c4\u5b9a\uff0c\u592b\u5987\u4e0d\u53ef\u4f4f\u5728\u4e00\u8d77\uff0c\u542c\u8bf4\u5979\u548c\u4e00\u4e2a \u59d3\u9a6c\u7684\u5173\u7cfb\u5bc6\u5207\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u90a3\u59d3\u9a6c\u7684\u4e0d\u5728\u5bb5\u5173\u4e86\uff1f\u201d \u3000\u3000\u201c\u53ef\u80fd\u4e0d\u5728\uff0c\u81f3\u4e8e\u5728\u54ea\u91cc\uff0c\u5f88\u5c11\u6709\u4eba\u63d0\u8d77\u3002\u201d \u3000\u3000\u663e\u7136\uff0c\u516c\u5b59\u7389\u5e76\u4e0d\u6e05\u695a\u51e4\u5ae3\u7ea2\u539f\u662f\u5723\u624b\u4e66\u751f\u7684\u59bb\u5b50\uff0c\u4e5f\u4e0d\u6e05\u695a\u5979\u548c\u9a6c\u662d\u96c4\u7684\u771f\u6b63\u5173\u7cfb\u3002 \u3000\u3000\u4ed6\u4e0d\u4fbf\u518d\u95ee\uff0c\u4ee5\u514d\u516c\u5b59\u7389\u751f\u7591\u3002 \u3000\u3000\u5f88\u5feb\u66f4\u5df2\u5230\u8fbe\u6c34\u5173\u3002 \u3000\u3000\u6c34\u5173\u5728\u65e0\u4eba\u53d7\u6d4b\u65f6\uff0c\u53ea\u662f\u4e00\u6761\u96a7\u9053\uff0c\u5b88\u5173\u7684\u4e5f\u4ec5\u662f\u4e00\u540d\u7eff\u8863\u5c11\u5973\u3002 \u3000\u3000\u90a3\u7eff\u8863\u5c11\u5973\u8ba4\u51fa\u662f\u5cb3\u5c0f\u98de\uff0c\u8fd8\u4e3b\u52a8\u4e0a\u524d\u6253\u62db\u547c\u3002 \u3000\u3000\u5c31\u5728\u8ddd\u706b\u5173\u4e0d\u8db3\u4e00\u91cc\u65f6\uff0c\u4e00\u540d\u7ea2\u8863\u5c11\u5973\u8fce\u9762\u800c\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8001\u8fdc\u5c31\u8ba4\u51fa\u8fd9\u5c11\u5973\u662f\u73ab\u7470\uff0c\u4fbf\u6025\u6025\u8fce\u4e0a\u524d\u53bb\u3002 \u3000\u3000\u73ab\u7470\u4e00\u89c1\u5cb3\u5c0f\u98de\uff0c\u7acb\u5373\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u4f60\u600e\u4e48\u73b0\u5728\u624d\u6765\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u89c9\u51fa\u4e0d\u5999\uff0c\u6115\u7136\u95ee\u9053\uff1b\u201c\u83ab\u975e\u6843\u82b1\u59d1\u5a18\uff1f\u2026\u2026\u201d \u3000\u3000\u73ab\u7470\u89c1\u516c\u5b59\u7389\u5728\u65c1\uff0c\u4e0d\u4fbf\u8bb2\u8bdd\uff0c\u7acb\u523b\u628a\u5cb3\u5c0f\u98de\u62c9\u5230\u4e00\u8fb9\u3002 \u3000\u3000\u516c\u5b59\u7389\u77e5\u9053\u73ab\u7470\u7684\u7528\u610f\uff0c\u4e3b\u52a8\u8fdc\u8fdc\u8eb2\u5f00\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6025\u6025\u95ee\u9053\uff1a\u201c\u6843\u82b1\u59d1\u5a18\u600e\u4e48\u4e86\uff1f\u4f60\u5feb\u8bf4\uff01\u201d \u3000\u3000\u73ab\u7470\u970e\u65f6\u7738\u5b50\u91cc\u8fdb\u51fa\u4e86\u6cea\u6c34\uff1a\u201c\u7edf\u9886\u5df2\u7ecf\u4e24\u5929\u591a\u6ca1\u7ed9\u6843\u82b1\u59d0\u59d0\u996d\u5403\u4e86\uff0c\u6843\u82b1\u59d0\u59d0\u73b0\u5728\u5df2 \u7ecf\u4e0d\u6210\u4eba\u5f62\uff0e\u542c\u8bf4\u7edf\u9886\u51b3\u5b9a\u8981\u628a\u5979\u997f\u6b7b\uff0c \u3000\u3000\u5c24\u5176\u4ece\u6628\u5929\u665a\u4e0a\u8fd8\u628a\u5979\u8131\u6389\u8863\u670d\u540a\u5728\u4e00\u6839\u67f1\u5b50\u4e0a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u5982\u706b\u711a\uff0c\u4ed6\u731c\u5f97\u51fa\uff0c\u5fc5\u662f\u62db\u8d24\u9986\u4e3b\u8427\u6e58\u53c8\u4f20\u6765\u4ed6\u66fe\u8981\u6c42\u653e\u4eba\u7684\u6d88\u606f\u624d\u4f7f\u5f97\u6843\u82b1 \u6b7b\u524d\u8fd8\u8981\u88ab\u540a\u8d77\u6765\uff0c\u53ef\u89c1\u516c\u5b59\u7389\u679c\u7136 \u3000\u3000\u9884\u6599\u4e0d\u5dee\u3002 \u3000\u3000\u4ed6\u53c8\u6025\u6025\u518d\u95ee\uff1a\u201c\u4f60\u8fd9\u4e24\u5929\u53ef\u66fe\u89c1\u8fc7\u5979\uff1f\u201d \u3000\u3000\u73ab\u7470\u62ed\u7740\u6cea\u6c34\u9053\uff1a\u201c\u8fde\u996d\u90fd\u4e0d\u518d\u9001\u4e86\uff0c\u6d1e\u95e8\u5df2\u7ecf\u4e0a\u9501\uff0c\u5a62\u5b50\u54ea\u80fd\u8fd8\u518d\u89c1\u5230\u5979\uff01\u201d \u3000\u3000\u201c\u4f60\u73b0\u5728\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u8fd8\u662f\u5949\u547d\u5230\u603b\u575b\u53bb\u529e\u4e8b\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u73b0\u5728\u5c31\u53bb\u770b\u5979\uff0c\u4f60\u53bb\u4f60\u7684\uff0c\u514d\u5f97\u5728\u8fd9\u91cc\u7ad9\u4e45\u4e86\u88ab\u4f60\u4eec\u7edf\u9886\u770b\u5230\u3002\u201d \u3000\u3000\u73ab\u7470\u4e34\u8d70\u65f6\u53c8\u9053\uff1a\u201c\u516c\u5b50\u4e0d\u4f46\u8981\u6551\u5979\u3002\u800c\u4e14\u6700\u597d\u628a\u5730\u5e26\u8d70\uff0c\u4e0d\u7136\u5979\u8fdf\u65e9\u8fd8\u662f\u8981\u88ab\u5904\u6b7b\uff01\u201d \u3000\u3000\u201c\u6211\u660e\u767d\uff0c\u4f60\u5feb\u8d70\uff01\u201d \u3000\u3000\u6b64\u523b\u5cb3\u5c0f\u98de\u65e9\u5df2\u6012\u706b\u586b\u81ba\uff0c\u56de\u5934\u53eb\u9053\uff1a\u201c\u516c\u5b59\u5148\u751f\uff0c\u6211\u4eec\u5feb\u8d70\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u4e5f\u9690\u7ea6\u542c\u51fa\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u3000\u7acb\u5373\u548c\u5cb3\u5c0f\u98de\u6025\u6b65\u5411\u706b\u5173\u8d76\u53bb\u3002 \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u5341\u4e09\u56de\u3000\u5927\u95f9\u603b\u9986 \u3000\u3000\u8fd9\u65f6\u7684\u706b\u5173\uff0c\u56e0\u65e0\u4eba\u53d7\u6d4b\uff0e\u4e5f\u53ea\u662f\u4e00\u6761\u666e\u901a\u96a7\u9053\u800c\u5df2\u3002 \u3000\u3000\u96a7\u9053\u4e24\u5934\uff0c\u5404\u6709\u4e00\u540d\u7ea2\u8863\u5973\u90ce\u5728\u8d1f\u8d23\u628a\u5b88\uff0c\u53ea\u8981\u6709\u8170\u724c\uff0c\u4fbf\u53ef\u901a\u884c\u65e0\u963b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u77e5\u9053\u706b\u5173\u7edf\u9886\u5728\u8c37\u9053\u5165\u53e3\u90a3\u4e00\u7aef\uff0c\u56e0\u4e4b\uff0c\u5bf9\u8fd1\u5904\u8d1f\u8d23\u628a\u5b88\u7684\u7ea2\u8863\u5973\u90ce\u8fde\u95ee\u90fd\u6ca1\u95ee\uff0c \u76f4\u63a5\u5feb\u6b65\u5954\u5230\u90a3\u4e00\u5934\u3002 \u3000\u3000\u53e6\u5916\u4e00\u7aef\u7684\u5973\u90ce\u8ba4\u51fa\u5cb3\u5c0f\u98de\u662f\u4e0d\u4e45\u524d\u624d\u95ef\u8fc7\u5173\u7684\uff0c\u4e3b\u52a8\u8fce\u4e0a\u6765\u95ee\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u5230\u54ea\u91cc\u53bb\uff0c \u4f60\u8fd9\u4e48\u5feb\u5c31\u5206\u6d3e\u4e86\u5de5\u4f5c\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u6709\u6025\u4e8b\u89c1\u8d35\u5173\u8427\u7edf\u9886\uff0c\u70e6\u52b3\u59d1\u5a18\u5feb\u5feb\u901a\u62a5\uff01\u201d \u3000\u3000\u4ed6\u56e0\u4e3a\u5f04\u4e0d\u6e05\u706b\u5173\u7edf\u9886\u8427\u7476\u4f4f\u5728\u54ea\u91cc\uff0c\u4e0d\u5f97\u4e0d\u8981\u4eba\u901a\u62a5\uff0c\u5426\u5219\uff0c\u4ed6\u65e9\u5c31\u81ea\u884c\u95ef\u8fdb\u53bb\u4e86\u3002 \u3000\u3000\u7ea2\u8863\u5c11\u5973\u6123\u4e86\u6123\u9053\uff1a\u201c\u516c\u5b50\u6709\u4ec0\u4e48\u6025\u4e8b\u8981\u89c1\u6211\u4eec\u7edf\u9886\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8981\u7d27\u7684\u4e8b\uff0c\u59d1\u5a18\u4e0d\u5fc5\u591a\u95ee\u3002\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u7acb\u5373\u5f80\u96a7\u9053\u4e00\u5904\u58c1\u6d1e\u800c\u53bb\uff0c\u4e0d\u5927\u4e00\u4f1a\uff0c\u4fbf\u5954\u4e86\u51fa\u6765\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u6211\u4eec\u7edf\u9886\u5728 \u91cc\u9762\u6b63\u6709\u6025\u4e8b\u5f85\u529e\uff0c\u8bf7\u516c\u5b50\u5728\u5916\u9762\u7b49\u4e00\u4f1a\u513f\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5df2\u987e\u4e0d\u5f97\u518d\u4fdd\u6301\u793c\u8c8c\uff0c\u559d\u9053\uff1a\u201c\u5979\u4ec0\u4e48\u6025\u4e8b\u8fd8\u6709\u6bd4\u73b0\u5728\u7684\u4e8b\u66f4\u8981\u7d27\u7684\uff1f\u518d\u4e0d\u51fa\u6765 \u6211\u5c31\u76f4\u63a5\u95ef\u8fdb\u53bb\u4e86\uff01\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u731b\u5403\u4e86\u4e00\u60ca\uff0c\u7acb\u523b\u53c8\u5954\u4e86\u56de\u53bb\u3002 \u3000\u3000\u516c\u5b59\u7389\u6015\u628a\u5c40\u9762\u95f9\u50f5\uff0c\u8fd1\u524d\u51e0\u6b65\u9053\uff1a\u201c\u54b1\u4eec\u65e2\u7136\u6765\u4e86\uff0c\u5979\u5f53\u7136\u8981\u51fa\u6765\u76f8\u89c1\uff0c\u5cb3\u516c\u5b50\u5fc5\u987b \u4fdd\u6301\u51b7\u9759\uff0c\u5f85\u4f1a\u513f\u7531\u6211\u51fa\u9762\u548c\u5979\u4ea4\u6d89\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u660e\u767d\u516c\u5b59\u7389\u662f\u5949\u5927\u516c\u4e3b\u4e4b\u547d\u800c\u6765\uff0c\u7684\u786e\u8be5\u7531\u4ed6\u51fa\u9762\uff0c\u81ea\u5df1\u53ea\u662f\u968f\u884c\u800c\u6765\uff0c\u4e0d\u8be5 \u55a7\u5bbe\u593a\u4e3b\uff0c\u53ea\u597d\u5f3a\u5fcd\u7740\u706b\u6c14\u9053\uff1a\u201c\u665a\u8f88\u9075\u547d\uff01\u201d \u3000\u3000\u597d\u4e00\u9635\u5de5\u592b\u8fc7\u53bb\uff0c\u706b\u5173\u7edf\u9886\u8427\u7476\u624d\u7531\u58c1\u6d1e\u4e2d\u7f13\u7f13\u8d70\u4e86\u51fa\u6765\u3002\u516c\u5b59\u7389\u968f\u5373\u8fce\u4e0a\u524d\u53bb\uff0c\u62f1\u624b \u4e00\u63d6\u9053\uff1a\u201c\u5728\u4e0b\u516c\u5b59\u7389\uff0c\u7279\u6765\u62dc\u89c1\u8427\u7edf\u9886\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u662f\u62db\u8d24\u9986\u7684\u519b\u5e08\uff0c\u8427\u7476\u5f53\u7136\u65e9\u5c31\u8ba4\u8bc6\uff0c\u4e5f\u8fd8\u4e86\u4e00\u793c\uff0c\u7b11\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u98ce\u628a\u516c\u5b59\u5148 \u751f\u5439\u5230\u8fd9\u91cc\u7684\uff1f\u8bf7\u5230\u91cc\u9762\u5750\uff01\u201d \u3000\u3000\u8fdb\u5165\u58c1\u6d1e\uff0c\u91cc\u9762\u5e03\u7f6e\u5f97\u9887\u4e3a\u5bcc\u4e3d\u5802\u7687\uff0c\u5750\u4e0b\u540e\uff0c\u516c\u5b59\u7389\u552f\u6050\u5cb3\u5c0f\u98de\u7740\u6025\uff0c\u4fbf\u5f00\u95e8\u89c1\u5c71\u7684 \u9053\uff1a\u201c\u542c\u8bf4\u8d35\u5c5e\u6709\u4f4d\u6843\u82b1\u59d1\u5a18\uff0c\u56e0\u4e3a\u72af\u4e86\u9519\u88ab\u8427\u7edf\u9886\u56da\u7981\u8d77\u6765\uff0c\u53ef\u6709\u8fd9\u56de\u4e8b\u5417\uff1f\u201d \u3000\u3000\u8427\u7476\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u8fd9\u4e8b\u516c\u5b59\u5148\u751f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u6b49\u7136\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u96be\u514d\u6709\u4eba\u4f20\u51fa\u53bb\uff0c\u5144\u5f1f\u4e5f\u4e0d\u8fc7\u662f\u8033\u95fb\u800c\u5df2\u3002\u201d \u3000\u3000\u8427\u7476\u4f3c\u662f\u9762\u7f69\u5bd2\u971c\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u655d\u5173\u7684\u5bb6\u52a1\u4e8b\uff0c\u60f3\u4e0d\u5230\u7adf\u52b3\u70e6\u516c\u5b59\u5148\u751f\u5173\u6000\uff0c\u516c\u5b59 \u5148\u751f\u5ffd\u7136\u95ee\u8d77\u8fd9\u4e8b\u3002\u4e00\u5b9a\u6709\u4ec0\u4e48\u539f\u56e0\u5427\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u966a\u7b11\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u5728\u4e0b\u662f\u5949\u5927\u516c\u4e3b\u4e4b\u547d\u800c\u6765\u3002\u201d \u3000\u3000\u8427\u7476\u8138\u8272\u53c8\u662f\u53d8\u9053\uff1a\u201c\u8fd9\u5c31\u5947\u602a\u4e86\uff0c\u96be\u9053\u5927\u516c\u4e3b\u4e5f\u77e5\u9053\u8fd9\u4ef6\u4e8b\u4e86\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u5728\u4e0b\u6b63\u662f\u7531\u5927\u516c\u4e3b\u90a3\u91cc\u77e5\u9053\u8fd9\u4ef6\u4e8b\u7684\u3002\u201d \u3000\u3000\u201c\u771f\u662f\u4ee4\u4eba\u601d\u89e3\u4e0d\u900f\uff0c\u655d\u5173\u7684\u8fd9\u4e48\u4e00\u70b9\u5c0f\u4e8b\uff0c\u5c45\u7136\u88ab\u5927\u516c\u4e3b\u77e5\u9053\u4e86\uff0c\u5927\u516c\u4e3b\u53ef\u662f\u8981\u516c\u5b59 \u5148\u751f\u6765\u8c03\u67e5\u6b64\u4e8b\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u6562\uff01\u5728\u4e0b\u53ea\u662f\u4f20\u8fbe\u5927\u516c\u4e3b\u7684\u4ee4\u8c15\uff0c\u8bf7\u8427\u7edf\u9886\u628a\u90a3\u4f4d\u6843\u82b1\u59d1\u5a18\u91ca\u653e\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u771f\u662f\u4e0d\u5de7\u5f97\u5f88\uff0c\u516c\u5b59\u5148\u751f\u6765\u8fdf\u4e86\u4e00\u6b65\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u8427\u7edf\u9886\u8fd9\u8bdd\u600e\u8bb2\uff1f\u201d \u3000\u3000\u201c\u6843\u82b1\u5df2\u7ecf\u6b7b\u4e86\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u987f\u65f6\u6014\u5728\u5f53\u573a\uff0c\u591a\u65f6\u624d\u900f\u8fc7\u4e00\u53e3\u6c14\u6765\u95ee\u9053\uff1a\u201c\u5979\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u8427\u7476\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u754f\u7f6a\u81ea\u5c3d\u7684\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fd9\u65f6\u518d\u4e5f\u6309\u637a\u4e0d\u4f4f\uff0c\u970d\u5730\u7ad9\u8eab\u800c\u8d77\uff0c\u53cc\u76ee\u5c04\u5149\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8427\u7edf\u9886\uff0c\u6843\u82b1\u59d1\u5a18\u72af \u4e86\u4ec0\u4e48\u7f6a\uff1f\u201d \u3000\u3000\u8427\u7476\u4e0d\u5c51\u7684\u77a5\u8fc7\u4e00\u773c\u9053\uff1a\u201c\u4f60\u53ef\u662f\u524d\u4e9b\u5927\u624d\u8fc7\u5173\u7684\u5cb3\u516c\u5b50\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u633a\u4e86\u80f8\u9053\uff1a\u201c\u4f60\u65e2\u7136\u8ba4\u8bc6\uff0c\u4f55\u5fc5\u591a\u95ee\uff01\u201d \u3000\u3000\u8427\u7476\u4e0d\u89c9\u674f\u7738\u51dd\u5a01\uff0c\u79c0\u7709\u5e26\u715e\uff0e\u51b7\u51b7\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u4e0d\u77e5\u6b7b\u6d3b\u7684\u4e1c\u897f\uff0c\u4f60\u6765\u5230\u5929 \u8c37\u624d\u51e0\u5929\uff0c\u5c45\u7136\u6562\u5bf9\u672c\u7edf\u9886\u8fd9\u6837\u8bf4\u8bdd\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6012\u706b\u653b\u5fc3\uff0c\u90a3\u987e\u4e00\u5207\uff0e\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u957f\u5251\u5df2\u7ecf\u51fa\u9798\uff0c \u3000\u3000\u5927\u559d\u9053\uff1a\u201c\u8427\u7476\uff0c\u5982\u679c\u6843\u82b1\u59d1\u5a18\u771f\u7684\u6b7b\u4e86\uff0c\u4f60\u4e5f\u522b\u60f3\u6d3b\u547d\uff01\u201d \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u7ad9\u5728\u8427\u7476\u8eab\u540e\u7684\u51e0\u540d\u7ea2\u8863\u5c11\u5973\uff0c\u5168\u90fd\u53d8\u4e86\u989c\u8272\u3002 \u3000\u3000\u8427\u7476\u731b\u7684\u4e00\u62cd\u8336\u51e0\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u7ed9\u6211\u628a\u8fd9\u5c0f\u5b50\u62ff\u4e0b\uff01\u201d \u3000\u3000\u90a3\u51e0\u540d\u7ea2\u8863\u5c11\u5973\u65e9\u77e5\u9053\u5cb3\u5c0f\u98de\u8eab\u624b\u5389\u5bb3\uff0c\u4f46\u7edf\u9886\u7684\u547d\u4ee4\u4e0d\u6562\u4e0d\u9075\uff0c\u53ea\u597d\u5404\u81ea\u4eae\u51fa\u5200\u5251\uff0c \u4e00\u9635\u8863\u8882\u98d8\u98ce\uff0c\u628a\u5cb3\u5c0f\u98de\u56e2\u56e2\u56f4\u4f4f\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u970d\u5730\u4e00\u4e2a\u5927\u8f6c\u8eab\uff0c\u6717\u58f0\u9053\uff1a\u201c\u4f17\u4f4d\u59d1\u5a18\uff0c\u6ca1\u6709\u4f60\u4eec\u7684\u4e8b\uff0c\u5728\u4e0b\u4e0d\u60f3\u4f24\u5bb3\u65e0\u8f9c\uff0c\u82e5 \u4f60\u4eec\u4e0d\u987e\u6b7b\u6d3b\uff0c\u90a3\u4e5f\u5c31\u522b\u602a\u5728\u4e0b\u624b\u4e0b\u65e0\u60c5\u4e86\uff01\u201d \u3000\u3000\u4f17\u7ea2\u8863\u5c11\u5973\u679c\u7136\u8c01\u90fd\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u3002 \u3000\u3000\u8fd9\u65f6\u516c\u5b59\u7389\u4e5f\u79bb\u4e86\u5ea7\u4f4d\uff0c\u614c\u5fd9\u53eb\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u5343\u4e07\u4f7f\u4e0d\u5f97\uff01\u201d \u3000\u3000\u53ea\u542c\u8427\u7476\u51b7\u53f1\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u52a8\u624b\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4f17\u7ea2\u8863\u5c11\u5973\u7acb\u65f6\u5200\u5251\u5e76\u4e3e\uff0c\u9f50\u9f50\u56f4\u653b\u4e0a\u6765\u3002 \u3000\u3000\u5979\u4eec\u4e00\u4e2a\u4e2a\u4ff1\u90fd\u8eab\u624b\u4e0d\u5f31\uff0c\u5200\u5149\u5251\u5f71\uff0c\u6377\u5982\u95ea\u7535\uff0c\u53ea\u53ef\u60dc\u5979\u4eec\u7684\u5bf9\u624b\uff0c\u9047\u4e0a\u4e86\u5cb3\u5c0f\u98de\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u51b3\u5b9a\u4e0d\u4f24\u5979\u4eec\uff0c\u957f\u5251\u7ed5\u7740\u4e00\u8f6c\uff0c\u53ea\u542c\u4e00\u9635\u91d1\u94c1\u4ea4\u51fb\u4e4b\u58f0\u8fc7\u540e\uff0c\u90a3\u88ad\u6765\u7684\u5200\u5251\uff0c\u5168 \u88ab\u8361\u4e86\u5f00\u53bb\uff0c \u3000\u3000\u5f53\u4ed6\u7b2c\u4e8c\u62db\u5251\u52bf\u65cb\u5f00\u65f6\uff0c\u4f17\u7ea2\u8863\u5c11\u5973\u7684\u5175\u5203\uff0c\u7adf\u5168\u8131\u624b\u98de\u53bb\u3002 \u3000\u3000\u4f17\u5c11\u5973\u5931\u53bb\u5175\u5203\uff0c\u4e00\u4e2a\u4e2a\u82b1\u5bb9\u5931\u8272\u4e4b\u4e0b\uff0c\u53ea\u597d\u5168\u9000\u5230\u58c1\u89d2\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5251\u4e00\u6307\u8427\u7476\u9053\uff1a\u201c\u8427\u5973\u8d3c\uff0c\u73b0\u5728\u8be5\u8f6e\u5230\u4f60\u4e86\uff01\u201d \u3000\u3000\u8427\u7476\u867d\u660e\u77e5\u4e0d\u662f\u5cb3\u5c0f\u98de\u7684\u5bf9\u624b\uff0c\u4f46\u8eab\u4e3a\u706b\u5173\u7edf\u9886\uff0c\u5374\u53c8\u4e0d\u80fd\u793a\u5f31\uff0c\u7a81\u89c1\u5979\u53f3\u7897\u5728\u80f8\u524d\u2014 \u65cb\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u6761\u91d1\u86c7\u8f6f\u97ad\u3002\u63a5\u7740\u201c\u55d6\u201d\u7684\u4e00\u58f0\u3002\u91d1\u86c7\u8f6f\u97ad\u95ea\u7535\u822c\u76f4\u5411\u5cb3\u5c0f\u98de\u9762\u95e8\u659c\u63a0\u800c \u4e0b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u53f3\u8155\u4e00\u7ffb\uff0c\u5251\u950b\u7167\u51c6\u97ad\u52bf\u4e0a\u8fce\u53bb\u3002 \u3000\u3000\u7acb\u523b\uff0c\u97ad\u68a2\u7f20\u4f4f\u4e86\u5251\u8eab\u3002 \u3000\u3000\u8427\u7476\u4e00\u54ac\u7259\uff0c\u5c06\u91d1\u86c7\u8f6f\u97ad\u731b\u5411\u6000\u91cc\u5e26\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u811a\u4e0b\u4e00\u6ed1\uff0c\u7adf\u88ab\u5e26\u52a8\u5f97\u76f4\u5411\u524d\u8e09\u8dc4\u8e0f\u51fa\u4e24\u4e09\u6b65\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u6fc0\u5f97\u5cb3\u5c0f\u98de\u706b\u6027\u5927\u53d1\uff0c\u8fd0\u6c14\u7a33\u4f4f\u8eab\u5b50\u4e4b\u540e\uff0c\u4e5f\u731b\u529b\u5411\u540e\u5e26\u5251\u3002 \u3000\u3000\u8427\u7476\u7167\u6837\u7ad9\u811a\u4e0d\u4f4f\uff0c\u4e5f\u88ab\u8feb\u8fce\u7740\u5cb3\u5c0f\u98de\u649e\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5f85\u5bf9\u65b9\u8eab\u5b50\u4e34\u8fd1\uff0c\u98de\u8d77\u4e00\u811a\u76f4\u8e22\u5c0f\u8179\u3002\u8427\u7476\u82b3\u5fc3\u5927\u9707\uff0c\u5979\u5fc5\u987b\u907f\u5f00\u8fd9\u4e00\u811a\uff0c\u5426\u5219 \u52bf\u5fc5\u5f53\u573a\u51fa\u4e11\uff0c\u56e0\u4e4b\uff0c\u8feb\u5f97\u5979\u4e0d\u5f97\u4e0d\u677e\u624b\u5411\u4e00\u4fa7\u75be\u8dc3\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u7529\u5251\uff0c\u90a3\u6761\u91d1\u86c7\u8f6f\u97ad\u76f4\u88ab\u629b\u51fa\u6d1e\u5916\u3002 \u3000\u3000\u4ea4\u624b\u4ec5\u4ec5\u4e24\u62db\uff0c\u8427\u7476\u548c\u5979\u7684\u5c5e\u4e0b\u4e00\u6837\uff0c\u4e5f\u843d\u5f97\u5175\u5203\u8131\u624b\u3002 \u3000\u3000\u597d\u5728\u8fd9\u65f6\u516c\u5b59\u7389\u5df2\u6a2a\u62e6\u5cb3\u5c0f\u98de\u8eab\u524d\uff0c\u793a\u610f\u89c1\u597d\u5c31\u6536\u3002 \u3000\u3000\u5176\u5b9e\u5cb3\u5c0f\u98de\u4e5f\u4e0d\u6562\u628a\u4e8b\u60c5\u7ee7\u7eed\u95f9\u5927\uff0c\u82e5\u4ed6\u6740\u4e86\u8427\u7476\uff0c\u53ea\u6015\u5927\u516c\u4e3b\u4e5f\u62a4\u4e0d\u4e86\u4ed6\u3002 \u3000\u3000\u516c\u5b59\u7389\u53eb\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u4e8b\u60c5\u5230\u6b64\u4e3a\u6b62\uff0c\u90fd\u662f\u81ea\u5df1\u4eba\uff0c\u82e5\u8fc7\u4e8e\u4f24\u4e86\u548c\u6c14\uff0c\u533a\u533a\u4e0d\u624d\u5b9e\u5728 \u96be\u4ee5\u5411\u5927\u516c\u4e3b\u4ea4\u4ee3\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4f9d\u7136\u624b\u6301\u957f\u5251\u9053\uff1a\u201c\u516c\u5b59\u5148\u751f\uff0c\u4ec0\u4e48\u53eb\u4e8b\u60c5\u5230\u6b64\u4e3a\u6b62\uff1f\u665a\u8f88\u4e5f\u4e0d\u60f3\u96be\u4e3a\u5979\uff0c\u53ea\u8981 \u5979\u7b54\u5e94\u4e00\u4ef6\u4e8b\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u8981\u8427\u7edf\u9886\u7b54\u5e94\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6843\u82b1\u59d1\u5a18\u6b7b\u5728\u54ea\u91cc\uff0c\u665a\u8f88\u5fc5\u987b\u4eb2\u81ea\u770b\u770b\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u8fd9\u5f88\u7b80\u5355\uff0c\u8427\u7edf\u9886\u5f53\u7136\u4e0d\u4f1a\u4e0d\u7b54\u5e94\u3002\u201d \u3000\u3000\u8bf4\u7740\u8f6c\u8fc7\u5934\u53bb\u9053\uff1a\u201c\u8427\u7edf\u9886\uff0c\u5c31\u8bf7\u4f60\u5e26\u5cb3\u516c\u5b50\u53bb\u770b\u770b\u6843\u82b1\u59d1\u5a18\u7684\u5c38\u4f53\uff01\u201d \u3000\u3000\u8427\u7476\u8138\u8272\u618b\u5f97\u9752\u4e2d\u5e26\u7d2b\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5df2\u7ecf\u57cb\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6717\u58f0\u9053\uff1a\u201c\u57cb\u4e86\u4e5f\u8981\u6398\u51fa\u6765\u770b\uff01\u201d \u3000\u3000\u201c\u4eba\u6b7b\u5165\u571f\u4e3a\u5b89\uff0c\u57cb\u4e86\u600e\u80fd\u518d\u884c\u6398\u51fa\uff1f\u201d \u3000\u3000\u201c\u6843\u82b1\u8fd9\u79cd\u6b7b\u6cd5\uff0c\u5165\u4ec0\u4e48\u4e5f\u4e0d\u80fd\u5b89\uff0c\u4f60\u82e5\u771f\u6562\u4e0d\u5e26\u5728\u4e0b\u53bb\uff0c\u5728\u4e0b\u5e72\u8106\u4e5f\u5c31\u8ba9\u4f60\u5165\u571f\u4e3a \u5b89\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fb9\u8bf4\u8fb9\u62a2\u5251\u5411\u8427\u7476\u8d70\u53bb\u3002 \u3000\u3000\u516c\u5b59\u7389\u2026\u89c1\u4e0d\u5999\uff0c\u6025\u6025\u53c8\u62a2\u524d\u4e00\u6b65\u62e6\u4f4f\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u4e0d\u53ef\u83bd\u649e\uff1f\u4e00\u5207\u6709\u6211\uff0c\u82e5\u5979\u771f\u4e0d\u5e26 \u4f60\u770b\uff0c\u90a3\u65f6\u518d\u7531\u4f60\u5904\u7f6e\u3002\u201d \u3000\u3000\u516c\u5b59\u7389\u8bf4\u5b8c\u8bdd\u518d\u671b\u5411\u8427\u7476\u9053\uff1a\u201c\u8427\u7edf\u9886\uff0c\u5cb3\u516c\u5b50\u8bf4\u5f97\u51fa\u505a\u5f97\u51fa\uff0c \u3000\u3000\u4e3a\u4e86\u606f\u4e8b\u5b81\u4eba\uff0c\u5e0c\u671b\u4f60\u522b\u518d\u575a\u6301\uff0c\u5426\u5219\u540e\u679c\u7531\u4f60\u81ea\u8d1f\u3002\u201d \u3000\u3000\u8427\u7476\u7ec8\u4e8e\u4e0d\u5f97\u4e0d\u8f6f\u4e0b\u6765\uff0c\u54ac\u4e86\u54ac\u7259\u9053\uff1b\u201c\u8ddf\u6211\u6765\uff01\u201d \u3000\u3000\u90a3\u51e0\u540d\u7ea2\u8863\u5c11\u5973\u6b63\u8981\u8ddf\u7740\u8d70\uff0c\u5374\u88ab\u8427\u7476\u53f1\u9000\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5176\u4e2d\u6709\u4e00\u540d\u7ea2\u8863\u5c11\u5973\u7adf\u5411\u5cb3\u5c0f\u98de\u6697\u4f7f\u773c\u8272\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f3c\u4e4e\u6709\u4e9b\u660e\u767d\uff0c\u7acb\u5373\u7d27\u8ddf\u5728\u8427\u7476\u8eab\u4fa7\u3002 \u3000\u3000\u8427\u7476\u4fa7\u8138\u51b7\u58f0\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8ddf\u5f97\u8fd9\u6837\u7d27\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4f60\u662f\u5426\u53c8\u6015\u4e86\uff1f\u653e\u5fc3\uff0c\u5728\u4e0b\u8fd8\u4e0d\u60f3\u628a\u4f60\u600e\u4e48\u6837\u3002\u201d \u3000\u3000\u51fa\u4e86\u8fd9\u5904\u58c1\u6d1e\uff0c\u53c8\u6765\u5230\u53e6\u5916\u4e00\u5904\u58c1\u6d1e\u95e8\u5916\u3002 \u3000\u3000\u8fd9\u6d1e\u95e8\u5c45\u7136\u662f\u9501\u7740\u7684\u3002 \u3000\u3000\u8427\u7476\u6253\u5f00\u6d1e\u95e8\u3002\u53ea\u89c1\u6d1e\u5185\u71c3\u7740\u6cb9\u706f\uff0c\u5728\u6d1e\u5185\u9760\u5de6\u58c1\u5904\u7ad6\u7740\u4e00\u6839\u6728\u6869\uff0c\u4e00\u540d\u62ab\u5934\u6563\u53d1\u7684\u5973 \u5b50\uff0c\u6b63\u540a\u5728\u6728\u6869\u4e0a\u3002 \u3000\u3000\u8427\u7476\u7a81\u7136\u52a0\u5feb\u811a\u6b65\uff0c\u63a2\u624b\u75be\u5411\u90a3\u5973\u5b50\u7684\u524d\u80f8\u62cd\u53bb\u3002 \u3000\u3000\u5c82\u77e5\u5979\u521a\u521a\u51fa\u624b\uff0c\u7a81\u89c9\u89c1\u4e00\u80a1\u5947\u5927\u65e0\u6bd4\u7684\u6697\u52b2\u5c06\u53f3\u81c2\u51fb\u504f\uff0c\u63a5\u7740\u81ea\u5df1\u7684\u4fa7\u8170\u7adf\u5148\u4e2d\u4e86\u4e00 \u638c\u3002 \u3000\u3000\u8fd9\u4e00\u638c\uff0c\u51fb\u5f97\u8427\u7476\u76f4\u6454\u56de\u6d1e\u95e8\u53e3\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u56de\u8eab\u5927\u559d\u9053\uff1a\u201c\u81ed\u5a46\u5a18\uff0c\u6211\u5cb3\u5c0f\u98de\u5c82\u80fd\u8ba9\u4f60\u5f97\u624b\uff0c\u5c31\u5728\u90a3\u8fdc\u8fdc\u7ad9\u7740\uff0c\u82e5\u6562\u518d\u8fdb\u4e00 \u6b65\uff0c\u8fd9\u91cc\u5c31\u662f\u4f60\u7684\u6b7b\u5730\uff01\u201d \u3000\u3000\u539f\u6765\u65b9\u624d\u90a3\u7ea2\u8863\u5c11\u5973\u4f7f\u6765\u773c\u8272\uff0c\u5cb3\u5c0f\u98de\u5c31\u4f1a\u610f\u6843\u82b1\u53ef\u80fd\u8fd8\u6d3b\u7740\uff0c\u56e0\u4e4b\uff0c\u5979\u624d\u7d27\u8ddf\u7740\u8427\u7476\uff0c \u4ee5\u514d\u5979\u731d\u7136\u51fa\u624b\u5c06\u6843\u82b1\u7f6e\u4e8e\u6b7b\u5730\u3002 \u3000\u3000\u679c\u7136\uff0c\u8427\u7476\u8fdb\u5165\u6d1e\u5185\u4e4b\u540e\uff0c\u7adf\u771f\u7684\u8981\u5c06\u6843\u82b1\u5904\u4e8e\u6b7b\u5730\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u65e9\u5df2\u6709\u5907\uff0c\u5f53\u7136\u4e0d\u80fd\u8ba9\u5979\u5f97\u624b\u3002 \u3000\u3000\u516c\u5b59\u7389\u4f55\u7b49\u673a\u667a\uff0c\u5230\u8fd9\u65f6\u4e5f\u77e5\u9053\u4e86\u662f\u600e\u4e48\u56de\u4e8b\u3002 \u3000\u3000\u4ed6\u968f\u5373\u4e5f\u6a2a\u62e6\u8427\u7476\u8eab\u524d\u9053\uff1a\u201c\u8427\u7edf\u9886\uff0c\u4f60\u8fd9\u6837\u505a\uff0c\u672a\u514d\u5c31\u5927\u5927\u4e0d\u8be5\u4e86\uff0c\u8fde\u533a\u533a\u4e0d\u624d\u4e5f\u770b \u4e0d\u8fc7\u53bb\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5306\u5306\u628a\u6843\u82b1\u89e3\u4e0b\u6728\u6869\uff0c\u63a5\u7740\u518d\u62e8\u5f00\u5979\u8986\u5728\u9762\u524d\u7684\u4e71\u53d1\u3002 \u3000\u3000\u53ea\u89c1\u6843\u82b1\u8138\u8272\u60e8\u767d\u5982\u7eb8\uff0c\u8fde\u773c\u7736\u90fd\u9677\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u7d27\u95ed\u53cc\u7738\uff0c\u53e3\u9f3b\u95f4\u53ea\u6709\u5fae\u5f31\u7684\u6c14\u606f\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u611f\u89c9\u4e00\u9897\u5fc3\u50cf\u9525\u624e\u5200\u6233\u822c\u7684\u523a\u75db\uff0e\u60c5\u4e0d\u81ea\u7981\u6447\u52a8\u7740\u5979\u7684\u624b\u81c2\u5927\u53eb\u9053\uff1a\u201c\u6843\u82b1\u59d1 \u5a18\uff0c\u6211\u6765\u4e86\uff01\u4f60\u82e5\u542c\u89c1\uff0c\u5feb\u5feb\u7741\u5f00\u773c\u6765\uff01\u201d \u3000\u3000\u6843\u82b1\u679c\u7136\u7f13\u7f13\u5f20\u5f00\u773c\u76ae\u3002\u5f53\u5979\u53d1\u73b0\u9762\u524d\u6276\u6301\u7740\u5979\u7684\u662f\u5cb3\u5c0f\u98de\uff0c \u3000\u3000\u7738\u5b50\u91cc\u7acb\u5373\u51fa\u73b0\u5947\u5f02\u7684\u5149\u91c7\uff0c\u63a5\u7740\u5634\u5507\u8815\u52a8\uff0c\u4f3c\u662f\u6cdb\u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u4f46\u5374\u6ca1\u8bf4\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u660e\u767d\uff0c\u5979\u5df2\u4e24\u4e09\u5929\u7c92\u7c73\u6ef4\u6c34\u672a\u8fdb\uff0c\u82e5\u8fd9\u65f6\u80fd\u7ed9\u5979\u704c\u676f\u70ed\u8336\uff0c\u5fc5\u5b9a\u80fd\u5927\u5927\u63d0\u632f\u7cbe\u795e\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4e0d\u80fd\u547d\u4ee4\u8427\u7476\u9001\u676f\u8336\u6765\uff1f\u56e0\u4e3a\u82e5\u8427\u7476\u5728\u8336\u4e2d\u4e0b\u4e86\u6bd2\uff0c\u5c82\u4e0d\u5f04\u5de7\u6210\u62d9\u3002 \u3000\u3000\u4ed6\u6682\u65f6\u628a\u6843\u82b1\u653e\u5750\u5728\u5730\u4e0a\uff0c\u4e00\u9762\u95ee\u516c\u5b59\u7389\u9053\uff1a\u201c\u516c\u5b59\u5148\u751f\uff0c\u4f60\u770b\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u5374\u8f6c\u5934\u9053\uff1a\u201c\u8427\u7edf\u9886\uff0c\u4f60\u73b0\u5728\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u8427\u7476\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u65e2\u7136\u5979\u8fd8\u6ca1\u6b7b\uff0c\u4e0d\u6b63\u968f\u4e86\u4f60\u4eec\u7684\u5fc3\u610f\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u51b7\u7740\u58f0\u97f3\u9053\uff1a\u201c\u5728\u4e0b\u662f\u95ee\u4f60\u6253\u7b97\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u8427\u7476\u9053\uff1a\u201c\u5979\u6ca1\u6b7b\u5c31\u662f\u547d\u5927\uff0c\u65e2\u7136\u547d\u5927\uff0c\u90a3\u5c31\u4e0d\u4f1a\u518d\u6b7b\uff0c\u73b0\u5728\u4f60\u4eec\u8be5\u653e\u5fc3\u4e86\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u662f\u8981\u8427\u7edf\u9886\u8bf4\u8fd9\u4e9b\u8bdd\uff0c\u662f\u95ee\u95ee\u4f60\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u201d \u3000\u3000\u8427\u7476\u9053\uff1a\u201c\u6843\u82b1\u662f\u6211\u706b\u5173\u4e0a\u7684\u4eba\uff0c\u4f60\u4eec\u5df2\u7ecf\u89c1\u7740\u5979\u4e86\uff0c\u800c\u4e14\u4e5f\u770b\u5230\u5979\u8fd8\u6d3b\u7740\uff0c\u90a3\u5c31\u8be5\u8d70 \u4e86\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u4eec\u8d70\u4e86\u4ee5\u540e\uff0c\u8c01\u77e5\u4f60\u53c8\u628a\u5979\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u672c\u7edf\u9886\u81ea\u5df1\u7684\u5c5e\u4e0b\u4eba\uff0c\u5f53\u7136\u8981\u597d\u597d\u7167\u987e\uff0c\u4f55\u52b3\u516c\u5b59\u5148\u751f\u62c5\u5fc3\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u53ea\u597d\u518d\u8f6c\u5934\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54fc\u4e86\u4e00\u58f0\u9053\uff1b\u201c\u516c\u5b59\u5148\u751f\u521a\u624d\u548c\u5979\u8fd8\u6709\u4ec0\u4e48\u597d\u8bb2\u7684\uff1f\u82e5\u628a\u6843\u82b1\u59d1\u5a18\u4ecd\u7559\u5728\u706b\u5173\uff0e \u4f60\u60f3\u5979\u8fd8\u6253\u6d3b\u547d\u5417\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e5f\u60f3\u5230\u8fd9\u4e00\u5c42\uff0c\u4f46\u6843\u82b1\u7ec8\u7a76\u662f\u706b\u5173\u7684\u4eba\uff0c\u53c8\u600e\u80fd\u4e0d\u7559\u5728\u706b\u5173\u4e0a\uff1f\u201d \u3000\u3000\u201c\u516c\u5b59\u5148\u751f\u662f\u5426\u60f3\u5230\u6211\u4eec\u5fc5\u987b\u628a\u5979\u5e26\u8d70\uff1f\u201d \u3000\u3000\u201c\u5cb3\u516c\u5b50\u51c6\u5907\u628a\u5979\u5e26\u5230\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u82e5\u6211\u4eec\u4e0d\u5e26\u5979\u8d70\uff0c\u5979\u662f\u5fc5\u6b7b\u65e0\u7591\uff0c\u81f3\u4e8e\u5e26\u5230\u90a3\u91cc\uff0c\u90a3\u5c31\u53ea\u6709\u8d70\u4e00\u6b65\u7b97\u4e00\u6b65\u4e86\uff01\u201d \u3000\u3000\u5ffd\u542c\u8427\u7476\u51b7\u7b11\u9053\uff1b\u201c\u6843\u82b1\u662f\u6211\u7684\u4eba\uff0c\u662f\u6559\u4e3b\u62e8\u7ed9\u6211\u7684\uff0c\u4f60\u4eec\u8c01\u6562\u628a\u5979\u5e26\u8d70\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5c31\u6562\u628a\u5979\u5e26\u8d70\uff0c\u4f60\u82e5\u4e0d\u6015\u6b7b\uff0e\u53ea\u7ba1\u51fa\u624b\u62e6\u963b\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u4ed6\u8fb9\u8bf4\u8fb9\u5de6\u624b\u6258\u8d77\u6843\u82b1\uff0c\u53f3\u624b\u6a2a\u5251\u76f4\u5411\u6d1e\u5916\u8d70\u53bb\u3002 \u3000\u3000\u8427\u7476\u5df2\u9886\u6559\u8fc7\u5cb3\u5c0f\u98de\u7684\u5386\u5bb3\uff0c\u8fd8\u771f\u4e0d\u6562\u51fa\u624b\u62e6\u963b\uff0c\u53ea\u662f\u9762\u5b54\u618b\u5f97\u8840\u7ea2\uff0c\u4f3c\u4e4e\u6709\u751f\u4ee5\u6765\u4ece \u6ca1\u5403\u8fc7\u8fd9\u6837\u7684\u762a\u3002 \u3000\u3000\u5012\u662f\u516c\u5b59\u7389\u6709\u4e9b\u8fc7\u610f\u4e0d\u53bb\uff0c\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u8427\u7edf\u9886\uff0c\u591a\u6709\u5f97\u7f6a\uff0c\u5cb3\u516c\u5b50\u8fd9\u662f\u8feb\u4e0d\u5f97\u5df2\uff0c \u53ea\u8981\u4f60\u4ee5\u540e\u5bf9\u5f85\u4e0b\u5c5e\u4ec1\u6148\u4e00\u70b9\uff0c\u8fd9\u79cd\u4e8b\u51b3\u4e0d\u53ef\u80fd\u518d\u53d1\u751f\u3002\u201d \u3000\u3000\u8427\u7476\u53ea\u6c14\u5f97\u5168\u8eab\u53d1\u6296\uff0c\u54ea\u91cc\u8fd8\u80fd\u8bf4\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u53e3\u6c14\u51fa\u4e86\u96a7\u9053\uff0c\u624d\u653e\u7f13\u811a\u6b65\u3002 \u3000\u3000\u5f53\u96a7\u9053\u53e3\u90a3\u540d\u5b88\u62a4\u7684\u7ea2\u8863\u5c11\u5973\u89c1\u5cb3\u5c0f\u98de\u6258\u7740\u6843\u82b1\u7ecf\u8fc7\uff0c\u4e5f\u77e5\u9053\u5cb3\u5c0f\u98de\u662f\u6551\u6843\u82b1\u7684\uff0c\u5979\u9ad8 \u5174\u8fd8\u6765\u4e0d\u53ca\uff0c\u5f53\u7136\u5e76\u4e0d\u8fc7\u95ee\u3002 \u3000\u3000\u5176\u5b9e\u5979\u8fc7\u95ee\u53c8\u6709\u4f55\u7528\uff1f\u8fde\u5979\u4eec\u7684\u7edf\u9886\u90fd\u62e6\u963b\u4e0d\u4e86\uff0c\u5979\u5c82\u4e0d\u7b49\u4e8e\u804b\u5b50\u7684\u8033\u6735\uff0c\u53ea\u662f\u6446\u5728\u90a3 \u91cc\u597d\u770b\u800c\u5df2\u3002 \u3000\u3000\u8fd9\u65f6\u516c\u5b59\u7389\u5df2\u8ddf\u4e86\u4e0a\u6765\u9053\uff1a\u201c\u4f60\u5230\u5e95\u51c6\u5907\u628a\u5979\u5e26\u5230\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5230\u4e86\u5bb5\u5173\u5c31\u6709\u996d\u9986\uff0c\u8ba9\u5979\u559d\u70b9\u6c34\u5403\u70b9\u4e1c\u897f\uff0c\u53ea\u8981\u5979\u7684\u7cbe\u795e\u4f53\u529b\u80fd\u7a0d\u7a0d\u6062\u590d \u5c31\u597d\u529e\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u4e0d\u5fc5\u8d70\u90a3\u4e48\u591a\uff0c\u6c34\u5173\u7edf\u9886\u6211\u8ba4\u8bc6\uff0c\u5230\u4e86\u6c34\u5173\uff0c\u5148\u8ba8\u676f\u8336\u7ed9\u5979\u559d\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u559c\u9053\uff1a\u201c\u90a3\u5c31\u66f4\u597d\u4e0d\u8fc7\u4e86\u3002\u201d \u3000\u3000\u5230\u4e86\u6c34\u5173\uff0c\u516c\u5b59\u7389\u679c\u7136\u8ba8\u4e86\u676f\u6c34\u7ed9\u6843\u82b1\u559d\u4e0b\u3002 \u3000\u3000\u6843\u82b1\u4e24\u4e09\u5929\u7c92\u7c73\u6ef4\u6c34\u672a\u8fdb\uff1f\u559d\u4e0b\u4e00\u676f\u8336\u540e\uff0c\u679c\u7136\u987f\u89c1\u8d77\u8272\uff0c\u800c\u4e14\u5df2\u6e10\u6e10\u80fd\u5f00\u53e3\u8bf4\u8bdd\u3002 \u3000\u3000\u5979\u771f\u611f\u6fc0\u5f97\u4e0d\u77e5\u8bf4\u4ec0\u4e48\u597d\uff0c\u6709\u6c14\u65e0\u529b\u7684\u9053\uff1a\u201c\u516c\u5b50\uff0c\u60a8\u7684\u5927\u6069\u5927\u5fb7\uff0c\u5a62\u5b50\u771f\u4e0d\u77e5\u8be5\u600e\u6837 \u62a5\u7b54\uff0c\u82e5\u60a8\u4eca\u5929\u518d\u4e0d\u6765\uff0c\u5a62\u5b50\u5fc5\u5b9a\u5df2\u7ecf\u6ca1\u6709\u547d\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u4f53\u529b\u5c1a\u672a\u5b8c\u5168\u6062\u590d\uff0c\u4e0d\u5fc5\u591a\u8bf4\u8bdd\uff0c\u8bf4\u8d77\u6765\u8be5\u662f\u6211\u8fde\u7d2f\u4e86\u4f60\uff0c\u6211\u65e0 \u8bba\u5982\u4f55\u4e5f\u8981\u628a\u4f60\u6551\u51fa\u6765\uff01\u201d \u3000\u3000\u6843\u82b1\u88ab\u5cb3\u5c0f\u98de\u6258\u7740\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u5a07\u7f9e\u7684\u4f4e\u58f0\u9053\uff1a\u201c\u516c\u5b50\u8fd8\u662f\u628a\u6211\u653e\u4e0b\uff0c\u6276\u7740\u6211\u8d70\u5c31\u884c \u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u5426\u8d70\u5f97\u52a8\uff1f\u201d \u3000\u3000\u201c\u8d70\u4e0d\u52a8\u53ef\u4ee5\u8d70\u6162\u4e9b\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4f9d\u8a00\u628a\u5979\u653e\u4e0b\uff0c\u4e00\u8fb9\u6276\u6301\u7740\u3002 \u3000\u3000\u6843\u82b1\u53c8\u4f4e\u58f0\u9053\uff1b\u201c\u90a3\u4f4d\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u90a3\u4f4d\u662f\u516c\u5b59\u5148\u751f\uff0c\u82e5\u6ca1\u6709\u4ed6\u5728\u5927\u516c\u4e3b\u90a3\u91cc\u9886\u6765\u901a\u884c\u8170\u724c\uff0c\u6211\u53c8\u600e\u80fd\u6765\u5230\u706b \u5173\u6551\u4f60\u3002\u201d \u3000\u3000\u6843\u82b1\u554a\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u539f\u6765\u5927\u516c\u4e3b\u4e5f\u77e5\u9053\u8fd9\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u82e5\u6ca1\u6709\u5927\u516c\u4e3b\u505a\u4e3b\uff0c\u6211\u4eec\u7167\u6837\u4e5f\u6ca1\u6cd5\u5230\u706b\u5173\u6765\u6551\u4f60\u3002\u201d \u3000\u3000\u6843\u82b1\u539f\u5148\u96be\u514d\u4e3a\u5cb3\u5c0f\u98de\u8fd9\u6b21\u884c\u52a8\u62c5\u5fc3\uff0c\u6b64\u523b\u542c\u8bf4\u6709\u5927\u516c\u4e3b\u505a\u4e3b\uff0c\u5185\u5fc3\u8d8a\u53d1\u5145\u6ee1\u611f\u52a8\uff0c\u540c \u65f6\u4e5f\u4e0d\u518d\u4e3a\u540e\u679c\u62c5\u5fc3\u3002 \u3000\u3000\u5176\u5b9e\u5cb3\u5c0f\u98de\u53ea\u662f\u4e3a\u4e86\u5b89\u6170\u6843\u82b1\uff0c\u81f3\u4e8e\u5e72\u4e0b\u8fd9\u4ef6\u4e8b\u7684\u540e\u679c\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u6562\u591a\u60f3\u3002 \u3000\u3000\u5230\u4e86\u5bb5\u5173\uff0c\u5f88\u5feb\u4fbf\u5728\u8857\u4e0a\u627e\u5230\u4e86\u4e00\u5bb6\u996d\u9986\u3002 \u3000\u3000\u5403\u8fc7\u996d\u540e\uff0c\u6843\u82b1\u7684\u7cbe\u795e\u4f53\u529b\u53c8\u6062\u590d\u4e86\u5f88\u591a\u3002 \u3000\u3000\u516c\u5b59\u7389\u89c1\u6843\u82b1\u7684\u8863\u670d\u51e0\u5929\u5df2\u7ecf\u5f04\u5f97\u5f88\u810f\uff0c\u800c\u4e14\u66fe\u88ab\u62f7\u6253\u8fc7\uff0c\u67d3\u4e86\u4e0d\u5c11\u8840\u6e0d\uff0c\u4fbf\u53c8\u5230\u8857\u4e0a \u4e3a\u5979\u4e70\u4e86\u4e00\u5957\u65b0\u8863\u670d\u3002 \u3000\u3000\u6843\u82b1\u771f\u662f\u611f\u6fc0\u83ab\u540d\uff0c\u8fde\u8fde\u5411\u516c\u5b59\u7389\u9053\u8c22\u3002 \u3000\u3000\u79bb\u5f00\u5bb5\u5173\uff0c\u516c\u5b59\u7389\u548c\u5cb3\u5c0f\u98de\u624d\u60f3\u8d77\u4e0b\u4e00\u6b65\u7684\u96be\u9898\u3002 \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u73b0\u5728\u8be5\u51b3\u5b9a\u7a76\u7adf\u8981\u628a\u6843\u82b1\u59d1\u5a18\u5b89\u7f6e\u5728\u54ea\u91cc\u7684\u4e8b\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u552f\u4e00\u7684\u529e\u6cd5\uff0c\u53ea\u6709\u52b3\u9a7e\u516c\u5b59\u5148\u751f\u628a\u5979\u9001\u5230\u603b\u575b\u518d\u8bf7\u5927\u516c\u4e3b\u5b89\u7f6e\u3002\u201d \u3000\u3000\u516c\u5b59\u7389\u8e59\u8d77\u53cc\u7709\u9053\uff1a\u201c\u7684\u786e\u8fd9\u662f\u552f\u4e00\u7684\u529e\u6cd5\uff0c\u4f46\u6211\u5374\u5fc5\u987b\u5148\u7980\u62a5\u8fc7\u5927\u516c\u4e3b\u518d\u628a\u5979\u5e26\u53bb\uff0c \u82e5\u76f4\u63a5\u628a\u4eba\u5e26\u53bb\u603b\u662f\u4e0d\u59a5\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u82e6\u601d\u4e86\u534a\u664c\u9053\uff1a\u201c\u90a3\u5c31\u8bf7\u516c\u5b59\u5148\u751f\u6682\u65f6\u628a\u6843\u82b1\u59d1\u5a18\u5b89\u7f6e\u5728\u603b\u9986\u3002\u201d \u3000\u3000\u516c\u5b59\u7389\u6447\u5934\u9053\uff1a\u201c\u8fd9\u5982\u4f55\u4f7f\u5f97\uff0c\u6211\u82e5\u628a\u6843\u82b1\u59d1\u5a18\u5e26\u5230\u603b\u9986\uff0c\u8427\u9986\u4e3b\u5fc5\u5b9a\u9a6c\u4e0a\u77e5\u9053\uff0c\u4e0d\u4f46 \u72af\u4e86\u89c4\u6212\uff0c\u800c\u4e14\u6843\u82b1\u59d1\u5a18\u662f\u4ed6\u80de\u59b9\u5173\u4e0a\u7684\u4eba\uff0c\u5c82\u4e0d\u53c8\u8981\u51fa\u4e8b\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u4eec\u603b\u4e0d\u80fd\u628a\u6843\u82b1\u59d1\u5a18\u4e22\u5728\u91ce\u5916\u8fc7\u591c\uff1f\u201d \u3000\u3000\u201c\u73b0\u5728\u5df2\u662f\u4e07\u4e0d\u5f97\u5df2\uff0c\u53ea\u6709\u8ba9\u5979\u5728\u4f60\u90a3\u5206\u9986\u91cc\u4f4f\u4e00\u591c\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4e0d\u7167\u6837\u8fdd\u72af\u89c4\u5b9a\uff1f\u201d \u3000\u3000\u201c\u54b1\u4eec\u4e0d\u59a8\u5728\u5bb5\u5173\u591a\u5f85\u4e00\u4f1a\u513f\uff0c\u7b49\u5929\u665a\u4e86\u518d\u56de\u53bb\uff0c\u8fd9\u6837\u5c31\u4e0d\u5927\u53ef\u80fd\u88ab\u4eba\u53d1\u73b0\uff0c\u53ea\u8981\u5206\u9986 \u7684\u9986\u50ee\u4e0d\u5411\u5916\u6cc4\u6f0f\uff0c\u4e0d\u4f1a\u6709\u4eba\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6c89\u601d\u4e86\u534a\u664c\uff0c\u8fd9\u7684\u786e\u662f\u552f\u4e00\u7684\u529e\u6cd5\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u4e09\u4eba\u6362\u4e86\u4e00\u5bb6\u8336\u9986\uff0c\u5230\u508d\u665a\u53c8\u5230\u996d\u9986\u5403\u4e86\u665a\u996d\uff0c\u624d\u7ee7\u7eed\u8d76\u8def\u3002 \u3000\u3000\u8def\u4e0a\uff0c\u679c\u7136\u5e76\u6ca1\u78b0\u5230\u719f\u4eba\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u662f\u6307\u516c\u5b59\u7389\u800c\u8a00\uff0c\u81f3\u4e8e\u5cb3\u5c0f\u98de\uff0c\u521d\u6765\u5929\u8c37\uff0c\u6839\u672c\u6ca1\u4ec0\u4e48\u719f\u4eba\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4e0d\u8ba4\u8bc6 \u522b\u4eba\uff0c\u522b\u4eba\u5374\u6709\u4e0d\u5c11\u8ba4\u8bc6\u4ed6\u7684\u3002 \u3000\u3000\u56de\u5230\u4e09\u5341\u516d\u53f7\u5206\u9986\uff0c\u6797\u91d1\u5b9d\u6b63\u5728\u95e8\u53e3\u82e6\u7b49\u5cb3\u5c0f\u98de\uff0c\u56e0\u4e3a\u4ed6\u8eab\u4e3a\u9986\u50ee\uff0c\u5cb3\u5c0f\u98de\u5230\u8fd9\u65f6\u8fd8\u6ca1 \u56de\u6765\uff0c\u54ea\u6709\u4e0d\u7740\u6025\u7684\u9053\u7406\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u89c1\u4e0d\u4f46\u5cb3\u5c0f\u98de\u56de\u6765\u4e86\uff0c\u800c\u4e14\u8fd8\u6709\u516c\u5b59\u7389\uff0c\u5c24\u5176\u5e26\u56de\u4e00\u4f4d\u5a07\u6ef4\u6ef4\u7684\u5927\u59d1\u5a18\uff0c\u5f04\u5f97\u7b80 \u76f4\u5982\u5760\u4e94\u91cc\u96fe\u4e2d\u3002 \u3000\u3000\u4e8c\u4eba\u6765\u5230\u5ba2\u5385\uff0c\u6797\u91d1\u5b9d\u5fd9\u9053\uff1a\u201c\u516c\u5b59\u5148\u751f\uff0c\u516c\u5b50\uff0c\u665a\u996d\u8fd8\u6ca1\u52a8\uff0c\u8981\u4e2a\u8981\u9a6c\u4e0a\u5c31\u5403\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff1f\u6211\u4eec\u90fd\u5df2\u7528\u8fc7\u3002\u201d \u3000\u3000\u6797\u91d1\u5b9d\u53c8\u9053\uff1a\u201c\u90a3\u4e48\u8fd9\u4f4d\u59d1\u5a18\u7528\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b11\u9053\uff1a\u201c\u516c\u5b59\u5148\u751f\u548c\u6211\u7528\u8fc7\uff0c\u5f53\u7136\u8fd9\u4f4d\u59d1\u5a18\u4e0d\u4f1a\u997f\u7740\u3002\u201d \u3000\u3000\u516c\u5b59\u7389\u5c0f\u5750\u4e86\u4e00\u4f1a\u513f\u4fbf\u8d77\u8eab\u9053\uff1a\u201c\u6211\u8be5\u56de\u603b\u9986\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fd8\u8bf7\u516c\u5b59\u5148\u751f\u660e\u5929\u80fd\u5c3d\u65e9\u7980\u62a5\u5927\u516c\u4e3b\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u653e\u5fc3\uff0c\u8fd9\u4e0d\u662f\u4ef6\u5c0f\u4e8b\uff0c\u4f55\u51b5\u8fd8\u6709\u6211\u53c2\u4e0e\uff0c\u660e\u5929\u4e00\u65e9\u6211\u5c31\u8d76\u5230\u603b\u575b\u5411 \u5927\u516c\u4e3b\u53bb\u7980\u62a5\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u53cc\u7eff\u8272\u7389\u74f6\uff0c\u5012\u51fa\u4e24\u7c92\u4e39\u4e38\uff0c\u53c8\u638f\u51fa\u4e00\u5305\u836f\u7c89\uff0c\u653e\u5728\u8336\u51e0\u4e0a\u9053\uff1a \u201c\u6843\u82b1\u59d1\u5a18\u65e2\u6709\u5185\u4f24\u53c8\u6709\u5916\u4f24\uff0c\u8fd9\u4e24\u7c92\u4e39\u836f\uff0c\u5f85\u4f1a\u513f\u5c31\u4e3a\u5979\u670d\u4e0b\uff0c\u90a3\u836f\u7c89\u5219\u662f\u6577\u5728\u4f24\u53e3\u4e0a\u7528 \u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u6843\u82b1\u8fde\u5fd9\u8c22\u8fc7\u516c\u5b59\u7389\uff0c\u53c8\u9001\u51fa\u5927\u95e8\u5916\uff0c\u624d\u518d\u56de\u6765\u3002\u6797\u91d1\u5b9d\u957f\u4e86\u8fd9\u4e48\u5927\uff0c\u6839\u672c\u6ca1 \u770b\u5230\u8fc7\u51e0\u4e2a\u5973\u4eba\uff0c\u5982\u4eca\u4e0d\u4f46\u770b\u5230\uff0c \u3000\u3000\u800c\u4e14\u5c31\u5728\u773c\u524d\uff0c\u60f3\u591a\u770b\u53c8\u4e0d\u597d\u610f\u601d\uff0c\u53ea\u6709\u5077\u5077\u7684\u77a7\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u5df2\u4e0d\u518d\u62c5\u5fc3\u5cb3\u5c0f\u98de\u72af\u4e86\u89c4\u6212\uff0c\u56e0\u4e3a\u8fd9\u4e8b\u6709\u603b\u9986\u519b\u5e08\u516c\u5b59\u7389\u53c2\u4e0e\u5176\u95f4\uff0c\u53c8\u542c\u8bf4\u6709\u5927 \u516c\u4e3b\u505a\u4e3b\uff0c\u5f53\u7136\u5c31\u6709\u6043\u65e0\u6050\u4e86\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u770b\u51fa\u6797\u91d1\u5b9d\u6709\u4e9b\u795e\u9b42\u98a0\u5012\uff0c\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u6843\u82b1\u59d1\u5a18\uff0c\u5979\u4eca\u665a\u5c31\u4f4f\u5728\u54b1\u4eec \u8fd9\u91cc\uff0c\u5feb\u5feb\u5c06\u623f\u95f4\u6574\u7406\u51fa\uff0c\u518d\u70e7\u58f6\u70ed\u8336\u6765\u3002\u201d \u3000\u3000\u8981\u6797\u91d1\u5b9d\u670d\u4f8d\u6843\u82b1\uff0c\u4ed6\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u8fde\u5fd9\u5e94\u58f0\u800c\u53bb\u3002 \u3000\u3000\u6843\u82b1\u88ab\u6797\u91d1\u5b9d\u770b\u5f97\u4e00\u76f4\u5a07\u7f9e\u4e0d\u5df2\uff0c\u6b64\u523b\u624d\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4ed6\u53eb\u6797\u91d1\u5b9d\uff0c\u662f\u8fd9\u5904\u5206\u9986\u91cc\u7684\u9986\u50ee\uff0c\u522b\u770b\u4ed6\u957f\u4e86\u8fd9\u4e48\u5927\uff0c\u4e16\u9762\u89c1\u5f97\u5e76\u4e0d\u591a\uff0c \u6240\u4ee5\u65b9\u624d\u624d\u4e0d\u514d\u591a\u770b\u4e86\u4f60\u51e0\u773c\uff0c\u5176\u5b9e\u4ed6\u4eba\u5f88\u8001\u5b9e\uff0c\u662f\u4e2a\u9053\u9053\u5730\u5730\u7684\u597d\u4eba\u3002\u201d \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\uff0c\u6797\u91d1\u5b9d\u5c31\u63d0\u4e86\u4e00\u58f6\u8336\u6765\uff0c\u5e76\u54c8\u7740\u8170\u9053\uff1a\u201c\u516c\u5b50\uff0c\u6843\u82b1\u59d1\u5a18\u7684\u623f\u95f4\u6536\u62fe\u597d\u4e86\u3002\u201d \u3000\u3000\u63a5\u7740\u8fde\u5fd9\u6c8f\u4e86\u4e24\u676f\u8336\uff0c\u4e00\u676f\u606d\u606d\u656c\u656c\u7684\u9001\u7ed9\u5cb3\u5c0f\u98de\uff0c\u53e6\u4e00\u676f\u606d\u606d\u656c\u656c\u7684\u9001\u7ed9\u6843\u82b1\u3002 \u3000\u3000\u6843\u82b1\u5728\u706b\u5173\u4e00\u76f4\u662f\u670d\u4f8d\u4eba\u7684\uff0c\u5982\u4eca\u6709\u7537\u4eba\u670d\u4f8d\u81ea\u5df1\uff0c\u771f\u50cf\u7531\u5730\u72f1\u5ffd\u7136\u8fdb\u4e86\u5929\u5802\u4e00\u822c\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u773c\u770b\u6843\u82b1\u670d\u4e0b\u4e39\u4e38\uff0e\u81f3\u4e8e\u836f\u7c89\uff0c\u662f\u6577\u5728\u8eab\u4e0a\u7684\uff0c\u5f53\u7136\u4e0d\u80fd\u5728\u8fd9\u91cc\u52a8\u624b\uff0c\u53c8\u5750\u4e86\u4e00 \u4f1a\u4fbf\u9053\uff1a\u201c\u59d1\u5a18\u8fd9\u4e9b\u5929\u5403\u4e86\u4e0d\u5c11\u82e6\uff0c\u63d0\u524d\u4f11\u606f\u8981\u7d27\u3002\u201d \u3000\u3000\u6797\u91d1\u5b9d\u5f53\u771f\u6bb7\u52e4\uff0c\u5fd9\u9053\uff1a\u201c\u6843\u82b1\u59d1\u5a18\uff0c\u6211\u5e26\u4f60\u5230\u623f\u95f4\u53bb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5219\u76f4\u7b49\u5230\u6843\u82b1\u7761\u4e0b\u540e\uff0c\u81ea\u5df1\u624d\u56de\u623f\u5c31\u5bdd\u3002\u4e00\u591c\u8fc7\u53bb\u3002 \u3000\u3000\u5f53\u5cb3\u5c0f\u98de\u65e9\u4e0a\u9192\u6765\u5230\u5929\u4e95\u540e\uff0c\u6843\u82b1\u4e5f\u5df2\u8d77\u8eab\u3002 \u3000\u3000\u6843\u82b1\u4e00\u5411\u670d\u4f8d\u522b\u4eba\uff0c\u4e60\u60ef\u65e9\u8d77\uff0c\u5982\u4eca\u4e00\u4e2a\u5973\u4eba\u5bb6\u4f4f\u5728\u5206\u9986\uff0c\u5f53\u7136\u66f4\u4e0d\u80fd\u665a\u8d77\u3002 \u3000\u3000\u6797\u91d1\u5b9d\u53bb\u62ff\u996d\u65f6\u95f4\u9053\uff1b\u201c\u516c\u5b50\uff0c\u62ff\u4e24\u4e2a\u4eba\u8fd8\u662f\u4e09\u4e2a\u4eba\u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5f53\u7136\u662f\u4e24\u4e2a\u4eba\u7684\uff0c\u4e0d\u8fc7\u6700\u597d\u996d\u83dc\u591a\u6253\u4e9b\u3002\u201d \u3000\u3000\u62ff\u996d\u56de\u6765\u540e\uff0c\u6797\u91d1\u5b9d\u771f\u662f\u4f53\u8d34\uff0c\u81ea\u5df1\u4e0d\u6562\u591a\u5403\uff0c\u76f4\u5230\u773c\u770b\u6843\u82b1\u5df2\u7ecf\u5403\u9971\uff0c\u624d\u653e\u5fc3\u628a\u996d\u5403 \u5b8c\u3002 \u3000\u3000\u6b64\u523b\uff0c\u5cb3\u5c0f\u98de\u6700\u76fc\u671b\u7684\u4fbf\u662f\u516c\u5b59\u7389\u53ca\u65e9\u5230\u6765\uff0c\u4ee5\u4fbf\u8ba9\u6843\u82b1\u6b63\u5f0f\u5b89\u5b9a\u4e0b\u6765\u3002\u8c01\u77e5\u5929\u5230\u8fd1\u5348\uff0c \u516c\u5b59\u7389\u4ecd\u4e0d\u89c1\u5230\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u6797\u91d1\u5b9d\u521a\u53bb\u62ff\u996d\u4e0d\u4e45\uff0c\u5cb3\u5c0f\u98de\u6b63\u548c\u6843\u82b1\u5728\u5ba2\u5385\u82e6\u7b49\u516c\u5b59\u7389\uff0c\u5ffd\u89c1\u6797\u91d1\u5b9d\u63d0\u7740\u7a7a\u98df\u76d2 \u614c\u614c\u5f20\u5f20\u4e0a\u6c14\u4e0d\u63a5\u4e0b\u6c14\u7684\u5954\u4e86\u56de\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u89c9\u51fa\u6709\u5f02\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u600e\u4e48\u53c8\u56de\u6765\u4e86\uff0c\u83ab\u975e\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u6253\u7740\u54c6\u55e6\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4e0d\u597d\u4e86\uff01\u4e0d\u597d\u4e86\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\u4e0d\u597d\u4e86\uff0c\u5feb\u8bf4\uff01\u201d \u3000\u3000\u201c\u9986\u4e3b\u5e26\u7740\u4e0d\u5c11\u4eba\uff0c\u6b63\u5411\u54b1\u4eec\u8fd9\u91cc\u6765\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u53ef\u770b\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u53bb\u62ff\u996d\uff0c\u521a\u8d70\u51fa\u4e0d\u5230\u534a\u91cc\u8def\uff0c\u8fdc\u8fdc\u671b\u89c1\u9986\u4e3b\u5e26\u7740\u56db\u4e94\u4e2a\u4eba\uff0c\u5411\u54b1\u4eec\u5206\u9986\u65b9\u5411\u800c\u6765\uff0c \u5c0f\u7684\u4e0d\u6562\u548c\u4ed6\u4eec\u78b0\u4e0a\uff0c\u4fbf\u7acb\u523b\u6389\u5934\u8dd1\u56de\u6765\uff0c\u4ee5\u4fbf\u5411\u516c\u5b50\u62a5\u544a\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u201c\u5c0f\u7684\u731c\u60f3\u4e00\u5b9a\u662f\u5230\u8fd9\u91cc\u6765\u7684\uff0c\u516c\u5b50\u6700\u597d\u9a6c\u4e0a\u505a\u4e2a\u51c6\u5907\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6697\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u4e8b\u53c8\u88ab\u4ed6\u77e5\u9053\u4e86\uff1f\u4ed6\u7a76\u7adf\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u2026\u2026\u201d \u3000\u3000\u4ed6\u6765\u4e0d\u53ca\u591a\u60f3\uff0c\u968f\u5373\u8981\u6843\u82b1\u5148\u8eb2\u5230\u4ed6\u7684\u623f\u95f4\uff0c\u5e76\u5429\u5490\u6797\u91d1\u5b9d\u4e5f\u56de\u623f\u53bb\uff0c\u7136\u540e\u4e00\u4e2a\u4eba\u5728\u5ba2 \u5385\u6545\u505a\u9547\u5b9a\u7684\u559d\u8d77\u8336\u6765\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u513f\uff0c\u95e8\u5916\u679c\u7136\u5c71\u8fdc\u800c\u8fd1\u4f20\u6765\u4e86\u9635\u6742\u4e71\u7684\u811a\u6b65\u58f0\u3002 \u3000\u3000\u63a5\u7740\uff0c\u5f53\u5148\u4e00\u4eba\u8fdb\u5165\u5927\u95e8\uff0c\u6b63\u662f\u62db\u8d24\u9986\u4e3b\u8427\u6e58\u3002 \u3000\u3000\u7d27\u968f\u5728\u8427\u6e58\u8eab\u540e\u7684\uff0c\u662f\u603b\u7ba1\u97e9\u5fb7\u8d77\u3002 \u3000\u3000\u97e9\u5fb7\u8d77\u4ecd\u662f\u534a\u8fb9\u8138\u7f20\u7740\u7eb1\u5e03\u3002 \u3000\u3000\u5728\u97e9\u5fb7\u8d77\u8eab\u540e\u53c8\u662f\u4e24\u4eba\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u9ad8\u4e00\u77ee\uff0c\u9ad8\u7684\u5e74\u5728\u56db\u65ec\u5de6\u53f3\uff0c\u633a\u80f8\u51f9\u809a\uff0c\u53d1\u53d1\u5982\u621f\uff0c\u773c\u8d5b\u94dc\u94c3\uff0c\u53e3\u5982\u8840\u76c6\uff0c\u7b80 \u76f4\u50cf\u51f6\u795e\u6076\u715e\u4e00\u822c\u3002 \u3000\u3000\u77ee\u7684\u4f53\u5f62\u80a5\u58ee\uff0c\u5706\u773c\u9614\u5634\uff0c\u7559\u7740\u4e24\u6487\u516b\u5b57\u80e1\uff0c\u5f88\u6709\u70b9\u6c34\u6d52\u4e2d\u77ee\u811a\u864e\u738b\u82f1\u5473\u9053\uff0c\u9887\u4f3c\u4e00\u95e8 \u5c0f\u94a2\u70ae\u3002 \u3000\u3000\u5c82\u77e5\u518d\u540e\u9762\u53c8\u662f\u4e09\u4eba\uff0c\u8d6b\u7136\u662f\u8881\u5c0f\u9e64\u3001\u4e01\u6d9b\u548c\u7518\u9716\u3002 \u3000\u3000\u597d\u5728\u8881\u5c0f\u9e64\u7b49\u4e09\u4eba\u4e00\u8fdb\u95e8\u5c31\u5411\u5cb3\u5c0f\u98de\u6697\u9012\u773c\u8272\uff0c\u5cb3\u5c0f\u98de\u5f88\u5feb\u4fbf\u4f1a\u8fc7\u610f\u6765\u3002 \u3000\u3000\u539f\u6765\u5cb3\u5c0f\u98de\u6628\u65e5\u5927\u95f9\u706b\u5173\u628a\u6843\u82b1\u5e26\u8d70\u7684\u4e8b\uff0c\u8427\u7476\u968f\u540e\u4fbf\u706b\u901f\u6d3e\u4eba\u901a\u77e5\u4e86\u8427\u6e58\u3002 \u3000\u3000\u8427\u6e58\u9aa4\u95fb\u6b64\u4fe1\uff0c\u9669\u4e9b\u6c14\u660f\uff0c\u672c\u60f3\u5f53\u665a\u5c31\u7387\u4f17\u5305\u56f4\u4e09\u5341\u516d\u53f7\u5206\u9986\u628a\u5cb3\u5c0f\u98de\u62ff\u4e0b\uff0c\u4f46\u53c8\u81ea\u5fd6 \u603b\u9986\u5185\u65e0\u4eba\u662f\u5cb3\u5c0f\u98de\u7684\u654c\u624b\u3002 \u3000\u3000\u5176\u5b9e\u603b\u9986\u5185\u5e76\u975e\u6ca1\u6709\u7edd\u9876\u9ad8\u624b\uff0c\u82e5\u6709\u526f\u9986\u4e3b\u82b1\u7389\u9e9f\u51fa\u9762\uff0c\u81ea\u53ef\u5236\u4f4f\u5cb3\u5c0f\u98de\uff0c\u4f46\u4ed6\u5df2\u77e5\u82b1 \u7389\u9e9f\u662f\u62a4\u7740\u5cb3\u5c0f\u98de\u7684\uff0c\u53c8\u56e0\u4e24\u4eba\u4e4b\u95f4\u5df2\u7ecf\u4ea4\u6076\uff0c\u5f53\u7136\u4ed6\u4e0d\u4f1a\u628a\u8fd9\u4e8b\u544a\u77e5\u82b1\u7389\u9e9f\u3002 \u3000\u3000\u51e0\u7ecf\u6df1\u601d\u719f\u8651\uff0c\u518d\u7ecf\u97e9\u5fb7\u8d77\u732e\u7b56\uff0c\u4ed6\u7adf\u60f3\u5230\u5229\u7528\u76ee\u524d\u5206\u9986\u5185\u7684\u4e94\u540d\u9ad8\u624b\u5408\u529b\u5bf9\u4ed8\u5cb3\u5c0f\u98de\uff0c \u4ee5\u4e94\u4eba\u4e4b\u529b\u64d2\u4f4f\u5cb3\u5c0f\u98de\uff0c\u90a3\u662f\u4e07\u65e0\u4e00\u5931\u7684\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4ed6\u5bf9\u8fd9\u4e94\u4eba\u4e8b\u5148\u5df2\u6709\u627f\u8bfa\uff0c\u90a3\u5c31\u662f\u4e8b\u6210\u4e4b\u540e\uff0c\u9664\u91cd\u8d4f\u5916\uff0c\u5e76\u8bb8\u4ee5\u63d0\u524d\u4fdd\u8bc1\u5206\u6d3e\u5de5 \u4f5c\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u7b49\u4e09\u4eba\u7ecf\u8fc7\u5546\u91cf\uff0c\u51b3\u5b9a\u5c06\u8ba1\u5c31\u8ba1\uff0c\u865a\u4e0e\u59d4\u86c7\uff0c\u4e0d\u8fc7\u4ed6\u4eec\u6700\u4e3b\u8981\u7684\uff0c\u8fd8\u662f\u60f3\u501f\u6b64\u673a \u4f1a\u548c\u5cb3\u5c0f\u98de\u89c1\u89c1\u9762\uff0c\u8981\u77e5\u9053\u4ed6\u4eec\u5206\u624b\u4e86\u8fd9\u4e48\u591a\u5929\uff0c\u5b9e\u5728\u601d\u5ff5\u5f97\u7d27\u3002 \u3000\u3000\u81f3\u4e8e\u53e6\u5916\u4e24\u4eba\uff0c\u56e0\u548c\u5cb3\u5c0f\u98de\u6839\u672c\u4e0d\u76f8\u8bc6\uff0c\u80fd\u5f97\u5230\u8fd9\u79cd\u7acb\u529f\u7684 \u3000\u3000\u673a\u4f1a\uff0c\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e2d\uff0c\u9ad8\u7684\u53eb\u88f4\u901a\uff0c\u77ee\u7684\u53eb\u6731\u5f6a\uff0c\u4ed6\u4eec\u5728\u6b66\u6797\u4e2d\u867d\u975e\u7edd \u3000\u3000\u9876\u9ad8\u624b\uff0c\u5374\u4e5f\u7b97\u5f97\u4e0a\u662f\u6210\u540d\u4eba\u7269\u3002 \u3000\u3000\u8427\u6e58\u5e26\u7740\u8fd9\u51e0\u540d\u5982\u72fc\u4f3c\u864e\u7684\u4eba\u7269\u8fdb\u5165\u5206\u9986\u5927\u95e8\u540e\uff0c\u968f\u5373\u5429\u5490\u4ed6\u4eec\u5c31\u5728\u5929\u4e95\u91cc\u6392\u597d\uff0c\u7136\u540e \u671b\u5411\u5ba2\u5385\u5185\u7684\u5cb3\u5c0f\u98de\u559d\u9053\uff1a\u201c\u4f60\u7ed9\u6211\u51fa\u6765\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u614c\u4e0d\u5fd9\uff0c\u6765\u5230\u5ba2\u5385\u95e8\u5916\uff0c\u62f1\u62f1\u624b\u9053\uff1a\u201c\u539f\u6765\u662f\u9986\u4e3b\u5927\u9a7e\u5149\u4e34\uff0c\u597d\u50cf\u6709\u4ec0\u4e48\u8981\u7d27 \u7684\u4e8b\u5427\uff1f\u201d \u3000\u3000\u8427\u6e58\u51b7\u53f1\u9053\uff1a\u201c\u4e8b\u522b\u5982\u4eca\uff0c\u5c45\u7136\u8fd8\u88c5\u75af\u5356\u50bb\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e24\u773c\u7728\u7728\u7684\u9053\uff1a\u201c\u9986\u4e3b\u7a76\u7adf\u4e3a\u4e86\u4ec0\u4e48\uff0c\u5728\u4e0b\u4e0d\u75af\u4e0d\u50bb\uff0c\u4f55\u82e6\u88c5\u75af\u5356\u50bb\uff1f\u201d \u3000\u3000\u201c\u4f60\u6628\u5929\u505a\u7684\u597d\u4e8b\uff01\u201d \u3000\u3000\u201c\u597d\u4e8b\u5e94\u8be5\u8868\u626c\uff0c\u9986\u4e3b\u600e\u4e48\u53cd\u800c\u6012\u6c14\u51b2\u51b2\uff1f\u201d \u3000\u3000\u201c\u706b\u5173\u4e0a\u7684\u6843\u82b1\uff0c\u53ef\u662f\u88ab\u4f60\u5e26\u8d70\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6551\u4eba\u4e00\u547d\uff0c\u80dc\u9020\u4e03\u7ea7\u6d6e\u5c60\uff0c\u5728\u4e0b\u7684\u786e\u6628\u5929\u505a\u4e86\u8fd9\u4e48\u4e00\u4ef6\u597d\u4e8b\u3002\u201d \u3000\u3000\u201c\u80e1\u8bf4\uff01\u7b80\u76f4\u9020\u53cd\u4e86\uff0c\u4eca\u5929\u672c\u9986\u4e3b\u628a\u4f60\u62ff\u56de\u53bb\uff0c\u5f53\u573a\u5c31\u628a\u4f60\u5904\u6b7b\uff0c\u8fd9\u6b21\u51b3\u4e0d\u4f1a\u6709\u4eba\u6765\u6551 \u4f60\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u5728\u4e0b\u505a\u4e86\u597d\u4e8b\u8fd8\u8981\u88ab\u5904\u6b7b\uff0c\u8427\u9986\u4e3b\u8fd8\u6709\u4e00\u70b9\u5929\u7406\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6b64\u523b\u7684\u786e\u6beb\u65e0\u987e\u5fcc\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u6700\u540e\u5fc5\u6709\u5927\u516c\u4e3b\u505a\u4e3b\u3002\u81f3\u4e8e\u8427\u6e58\uff0c\u4ed6\u672c\u4ee5\u4e3a\u81ea\u5df1 \u5e26\u4e86\u4e94\u540d\u9ad8\u624b\u524d\u6765\uff0c\u5cb3\u5c0f\u98de\u5fc5\u5b9a\u4f1a \u3000\u3000\u89c1\u800c\u751f\u754f\uff0c\u4e56\u4e56\u53d7\u7f1a\uff0c\u5c82\u77e5\u4ed6\u7adf\u4f9d\u7136\u662f\u4e00\u526f\u73a9\u4e16\u4e0d\u606d\u6beb\u4e0d\u5728\u610f\u6a21\u6837\uff0c\u600e\u4e0d\u4ee4\u4ed6\u8d8a\u53d1\u6c14\u5f97 \u4e03\u7a8d\u751f\u70df\u3002 \u3000\u3000\u4f46\u4ed6\u4e3a\u4e86\u641c\u8bc1\uff0c\u6682\u65f6\u8fd8\u4e0d\u51c6\u5907\u547d\u4ee4\u4e94\u540d\u9ad8\u624b\u52a8\u624b\uff0c\u8f6c\u5934\u559d\u4ee4\u97e9\u5fb7\u8d77\u9053\uff1a\u201c\u641c\u641c\u706b\u5173\u4e0a\u90a3 \u4e2b\u5934\u5728\u4ec0\u4e48\u5730\u65b9\uff01\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u521a\u8981\u91c7\u53d6\u884c\u52a8\uff0c\u5cb3\u5c0f\u98de\u5df2\u6de1\u7136\u7b11\u9053\uff1a\u201c\u7528\u4e0d\u7740\u641c\uff0c\u597d\u6c49\u505a\u4e8b\u597d\u6c49\u5f53\uff0c\u6843\u82b1\u59d1\u5a18\u5c31 \u5728\u5728\u4e0b\u623f\u91cc\uff01\u201d \u3000\u3000\u63a5\u7740\u6717\u58f0\u53eb\u9053\uff1a\u201c\u6843\u82b1\u59d1\u5a18\uff0c\u51fa\u6765\u8ba9\u4ed6\u4eec\u770b\u770b\uff01\u201d \u3000\u3000\u6843\u82b1\u867d\u7136\u5df2\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u4f46\u81ea\u6599\u8eb2\u4e0d\u8fc7\uff0c\u53ea\u597d\u6218\u6218\u5162\u5162\u7684\u7531\u5ba4\u5185\u8d70\u5230\u5ba2\u5385\u3002 \u3000\u3000\u8427\u6e58\u51b7\u7b11\u9053\uff1a\u201c\u597d\u554a\uff0c\u62db\u8d24\u9986\u65e9\u6709\u89c4\u5b9a\uff0c\u5404\u5206\u9986\u8fde\u5973\u4eba\u8d70\u8fdb\u4e00\u6b65\u90fd\u5728\u7981\u5236\u4e4b\u5217\uff0c\u4f60\u5c45\u7136 \u628a\u5973\u4eba\u5e26\u8fdb\u6765\u8fc7\u591c\uff0c\u4e73\u81ed\u672a\u5e72\u7684\u6bdb\u5934\u5c0f\u5b50\uff0c\u7adf\u505a\u51fa\u52fe\u642d\u5973\u4eba\u7684\u4e8b\uff0c\u771f\u53ef\u79f0\u5f97\u4e0a\u662f\u65e0\u803d\u4e4b\u5c24\uff0c \u50cf\u4f60\u8fd9\u79cd\u4e0b\u6d41\u54c1\u6027\uff0c\u600e\u591f\u8d44\u683c\u4f4f\u8fdb\u62db\u8d24\u9986\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u559d\u9053\uff1a\u201c\u4f4f\u5634\uff01\u6843\u82b1\u59d1\u5a18\u53ea\u662f\u5728\u8fd9\u91cc\u6682\u4f4f\u4e00\u591c\uff0c\u4f60\u5374\u60f3\u5230\u90a3\u65b9\u9762\u53bb\u4e86\uff0c\u65e0\u803d\u4e4b\u5c24 \u7684\u5e94\u8be5\u662f\u4f60\uff01\u201d \u3000\u3000\u201c\u4f60\u82e5\u6ca1\u505a\u51fa\u82df\u4e14\u4e4b\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u8fd9\u4e2b\u5934\u4ece\u4f60\u623f\u91cc\u51fa\u6765\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u56e0\u4e3a\u4f60\u4eec\u6765\u4e86\uff0c\u5728\u4e0b\u6682\u65f6\u8ba9\u5979\u5230\u6211\u623f\u95f4\u8eb2\u8eb2\u3002\u201d \u3000\u3000\u201c\u4e0d\u6253\u81ea\u62db\uff0c\u4f60\u4eec\u4e4b\u95f4\u82e5\u6b63\u5927\u5149\u660e\uff0c\u8fd9\u4e2b\u5934\u8fd8\u8eb2\u7684\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u88ab\u95ee\u5f97\u8fd8\u771f\u6709\u4e9b\u96be\u4ee5\u8fa9\u89e3\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u600e\u4e48\u8bf4\uff0c\u5728\u4e0b\u81ea\u95ee\u6e05\u767d\u5c31\u884c\uff01\u201d \u3000\u3000\u8427\u6e58\u89c9\u51fa\u65f6\u673a\u5df2\u5230\uff0c\u56de\u5934\u9053\uff1a\u201c\u4f60\u4eec\u4e94\u4f4d\uff0c\u54ea\u4f4d\u8fc7\u53bb\u628a\u4ed6\u62ff\u4e0b\uff0c\u54ea\u4f4d\u5c31\u662f\u5934\u529f\uff0c\u82e5\u4e00\u4eba \u529b\u91cf\u4e0d\u591f\uff0c\u4e94\u4eba\u9f50\u4e0a\u4e5f\u53ef\u3002\u201d \u3000\u3000\u53ea\u89c1\u90a3\u77ee\u58ee\u6709\u5982\u632b\u5730\u70ae\u7684\u6731\u5f6a\u50cf\u8783\u87f9\u4f38\u722a\u822c\u5411\u524d\u8d70\u51fa\u51e0\u6b65\u9053\uff1a\u201c\u7980\u9986\u4e3b\uff0c\u6731\u67d0\u5148\u524d\u53ea\u9053 \u662f\u4e2a\u4ec0\u4e48\u6a21\u6837\u7684\u4e09\u5934\u516d\u81c2\u4eba\u7269\uff0c\u539f\u6765\u4e0d\u8fc7\u4e00\u4e2a\u521a\u521a\u8131\u5976\u7684\u5c0f\u841d\u535c\uff0c\u8bf4\u4ec0\u4e48\u4e94\u4e2a\u4eba\u4e00\u8d77\u4e0a\uff0c\u6731 \u67d0\u82e5\u8fde\u4ed6\u90fd\u6536\u62fe\u4e0d\u4e86\uff0c\u8fd8\u6765\u5929\u8c37\u521b\u7684\u4ec0\u4e48\u540d\uff1f\u7acb\u7684\u4ec0\u4e48\u4e1a\uff1f\u201d \u3000\u3000\u8427\u6e58\u9000\u5230\u4e00\u4fa7\u9053\uff1a\u201c\u6731\u58ee\u58eb\u522b\u5c0f\u770b\u4e86\u4ed6\uff0c\u8fd9\u5c0f\u5b50\u5e74\u7eaa\u867d\u8f7b\uff0c\u8eab\u624b\u5374\u5341\u5206\u4e0d\u51e1\uff0c\u4e0a\u6b21\u6253\u5f97 \u672c\u9986\u5434\u526f\u603b\u7ba1\u8fde\u8fd8\u624b\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u6731\u5f6a\u51b7\u7b11\u9053\uff1a\u201c\u9986\u4e3b\uff0c\u90a3\u53ea\u6028\u60a8\u90a3\u5434\u526f\u603b\u9986\u592a\u8113\u5305\uff0c\u7b49\u6731\u67d0\u64d2\u4f4f\u8fd9\u5c0f\u5b50\u540e\uff0c\u53ea\u6c42\u60a8\u9769\u6389 \u5434\u526f\u603b\u7ba1\u7684\u5dee\uff0c\u628a\u54b1\u8865\u4e00\u540d\u526f\u603b\u7ba1\u7684\u5dee\u5c31\u6210\u4e86\u3002\u201d \u3000\u3000\u8427\u6e58\u5c34\u5c2c\u4e00\u7b11\u9053\uff1a\u201c\u6731\u58ee\u58eb\u5148\u628a\u4ed6\u62ff\u4e0b\u518d\u8bf4\u3002\u201d \u3000\u3000\u6731\u5f6a\u7acb\u5373\u89e3\u4e0b\u8170\u95f4\u7684\u4e24\u67c4\u523a\u732c\u9524\u3002 \u3000\u3000\u4ed6\u8fd9\u53cc\u9524\uff0c\u60ac\u5728\u8170\u95f4\u65f6\u539f\u672c\u662f\u5149\u6ed1\u7684\uff0c\u56e0\u4e3a\u8bbe\u6709\u673a\u5173\uff0c\u53ea\u8981\u5728\u628a\u5b50\u4e0a\u4e00\u6309\uff0c\u7acb\u523b\u4fbf\u80c0\u4e86 \u4e00\u500d\uff0c\u53d8\u6210\u4e86\u523a\u732c\u4e00\u822c\uff0c\u6bcf\u6839\u94a2\u523a\u90fd\u950b\u5229\u65e0\u6bd4\uff0c\u65e0\u5f62\u4e2d\u8d8a\u53d1\u589e\u52a0\u4e86\u5a01\u529b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u770b\u5f97\u5012\u6709\u4e9b\u597d\u7b11\uff0c\u56e0\u4e3a\u6b64\u4eba\u672c\u6765\u5c31\u77ee\u5f97\u6709\u4e9b\u50cf\u523a\u732c\uff0c\u4f7f\u7528\u7684\u53c8\u662f\u4e00\u5bf9\u523a\u732c\u9524\uff0c\u540a \u4e2a\u574e\u513f\uff0c\u6b63\u662f\u6b66\u5927\u90ce\u73a9\u591c\u732b\u5b50\u2014\u2014\u4ec0\u4e48\u4eba\u73a9\u4ec0\u4e48\u9e1f\u3002 \u3000\u3000\u6731\u5f6a\u628a\u53cc\u9524\u5728\u624b\u91cc\u6382\u4e86\u4e00\u6382\u9053\uff1a\u201c\u9986\u4e3b\uff0c\u60a8\u662f\u8981\u6b7b\u7684\u8fd8\u662f\u8981\u6d3b\u7684\uff1f\u201d \u3000\u3000\u8427\u6e58\u9053\uff1a\u201c\u968f\u6731\u58ee\u58eb\u7684\u4fbf\uff01\u201d \u3000\u3000\u6731\u5f6a\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u8981\u6b7b\u7684\u8981\u6d3b\u7684\u90fd\u4e00\u6837\u5bb9\u6613\u3002\u201d \u3000\u3000\u5c82\u77e5\u5cb3\u5c0f\u98de\u6b64\u523b\u8fde\u5251\u90fd\u4e0d\u62d4\uff0c\u53ea\u662f\u6c14\u5b9a\u795e\u95f2\u800c\u7acb\uff0c\u6b63\u773c\u4e5f\u6ca1\u770b\u6731\u5f6a\u4e00\u4e0b\u3002 \u3000\u3000\u5b8b\u5f6a\u6014\u4e86\u4e00\u6014\uff0c\u543c\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u600e\u4e48\u52a8\u90fd\u4e0d\u52a8\uff0c\u771f\u662f\u5728\u7b49\u6b7b\u5417\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fd8\u662f\u8fde\u773c\u76ae\u5b50\u4e5f\u6ca1\u642d\u4e00\u4e0b\u3002 \u3000\u3000\u6731\u5f6a\u81ea\u8a00\u81ea\u6d6f\u9053\uff1a\u201c\u8fd9\u6837\u770b\u6765\uff0c\u62ff\u6b7b\u7684\u53cd\u800c\u6bd4\u62ff\u6d3b\u7684\u66f4\u5bb9\u6613\u3002\u201d \u3000\u3000\u53ea\u542c\u90a3\u5927\u4e2a\u5b50\u53eb\u88f4\u901a\u7684\u53eb\u9053\uff1a\u201c\u6731\u8001\u5f1f\uff0c\u4f60\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6731\u5f6a\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4ed6\u4e0d\u52a8\uff01\u201d \u3000\u3000\u88f4\u901a\u9053\uff1a\u201c\u4ed6\u4e0d\u52a8\u4ed6\u7684\uff0c\u4f60\u52a8\u4f60\u7684\u3002\u201d \u3000\u3000\u6731\u5f6a\u731b\u7136\u6b3a\u8eab\u75be\u8fdb\uff0c\u4e24\u9524\u9f50\u51fa\uff0c\u4e0a\u51fb\u9762\u95e8\uff0c\u3000\u4e0b\u64c2\u524d\u80f8\uff0c\u624b\u6cd5\u5feb\u5982\u98ce\u9a70\u7535\u63a3\u3002 \u3000\u3000\u8c01\u90fd\u53ef\u4ee5\u60f3\u5230\uff0e\u53ea\u8981\u88ab\u523a\u732c\u9524\u51fb\u4e2d\uff0c\u4e0d\u8bba\u8138\u4e0a\u8eab\u4e0a\uff0c\u5fc5\u5b9a\u6ee1\u662f\u7a9f\u7abf\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5df2\u62ff\u5b9a\u4e3b\u610f\uff0c\u51b3\u5b9a\u4ee5\u6e38\u6597\u65b9\u5f0f\u8fce\u6218\uff0c\u907f\u514d\u901f\u6218\u901f\u51b3\uff0c\u76ee\u7684\u662f\u62d6\u5ef6\u65f6\u95f4\uff0c\u4ee5\u4fbf\u7b49\u5927 \u516c\u4e3b\u548c\u516c\u5b59\u7389\u8d76\u6765\u3002 \u3000\u3000\u53e6\u5916\uff0c\u4ed6\u4e5f\u4e0d\u60f3\u7f6e\u5bf9\u65b9\u4e8e\u6b7b\u5730\uff0c\u6700\u591a\u4f7f\u4e4b\u53d7\u70b9\u8f7b\u4f24\u3002\u56e0\u4e3a\u8fd9\u4e00\u9ad8\u4e00\u77ee\u4e24\u540d\u5927\u6c49\uff0c\u65e2\u5df2\u8fdb \u4e86\u62db\u8d24\u9986\uff0c\u603b\u662f\u6559\u4e3b\u7684\u4eba\u4e86\uff0c\u4ed6\u82e5\u53d6 \u3000\u3000\u4e86\u4e24\u4eba\u7684\u547d\uff0c\u7b49\u4e8e\u6298\u635f\u4e86\u7687\u5143\u6559\u7684\u5b9e\u529b\uff0c\u90a3\u5c31\u6ca1\u7f6a\u4e5f\u53d8\u6210\u6709\u7f6a\u4e86\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u4ed6\u76f4\u7b49\u53cc\u9524\u5df2\u7ecf\u8fd1\u8eab\uff0c\u624d\u6ed1\u6b65\u65cb\u8eab\uff0c\u95ea\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6731\u5f6a\u773c\u770b\u5c31\u8981\u51fb\u4e2d\uff0c\u77ac\u606f\u95f4\u76ee\u6807\u7adf\u7136\u6d88\u5931\uff0c\u96be\u514d\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u53ea\u542c\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u6731\u5f6a\u8fd9\u624d\u53d1\u73b0\u5bf9\u65b9\u4ecd\u5728\u8eab\u5de6\u4e0d\u8fdc\uff0c\u62db\u5f0f\u4e0d\u53d8\uff0c\u6298\u8f6c\u65b9\u5411\uff0c\u53c8\u64c2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u9524\u5230\u540e\u5bf9\u65b9\u53c8\u5df2\u4e0d\u89c1\u3002 \u3000\u3000\u6b63\u5728\u4e0d\u77e5\u76ee\u6807\u98d8\u5411\u4f55\u65b9\uff0c\u5ffd\u89c9\u540e\u80a9\u88ab\u4eba\u62cd\u4e86\u4e00\u4e0b\uff0c\u63a5\u7740\u53c8\u542c\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5728\u540e\u8fb9\uff01\u201d \u3000\u3000\u6731\u5f6a\u970d\u5730\u4e00\u4e2a\u5927\u8f6c\u8eab\uff0c\u53cc\u9524\u631f\u7740\u9510\u98ce\uff0c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u518d\u62a2\u8fc7\u53bb\u3002 \u3000\u3000\u8eab\u5b50\u521a\u8f6c\u8fc7\uff0c\u4e0d\u60f3\u5bf9\u65b9\u53c8\u5df2\u6d88\u5931\uff0c\u7d27\u8ddf\u7740\u5c41\u80a1\u4e0a\u6328\u4e86\u4e00\u811a\u3002 \u3000\u3000\u8fd9\u4e00\u811a\u867d\u4e0d\u7b97\u91cd\uff0c\u4f46\u52a0\u4e0a\u6731\u5f6a\u81ea\u5df1\u524d\u52a8\u4e4b\u529b\uff0c\u5f62\u6210\u4e86\u529b\u534a\u529f\u500d\uff0c\u8e22\u5f97\u4ed6\u76f4\u5411\u524d\u8e09\u8dc4\u51b2\u53bb\u3002 \u3000\u3000\u8c01\u77e5\u4ed6\u6b63\u8981\u6781\u529b\u628a\u8eab\u5b50\u7a33\u4f4f\uff0c\u5c41\u80a1\u4e0a\u7adf\u53c8\u4e2d\u4e86\u4e00\u811a\u3002 \u3000\u3000\u8fd9\u4e00\u811a\u529b\u9053\u81f3\u5c11\u6bd4\u5148\u524d\u90a3\u4e00\u811a\u589e\u52a0\u4e86\u4e09\u500d\uff0c\u53ea\u8e22\u5f97\u6731\u5f6a\u60c5\u4e0d\u81ea\u7981\u8ff8\u51fa\u4e2a\u58f0\u97f3\u5947\u5927\u7684\u54cd\u5c41\uff0c \u4eba\u4e5f\u817e\u7a7a\u4fef\u8eab\u6454\u51fa\u3002 \u3000\u3000\u53c8\u662f\u4e00\u58f0\u5de8\u54cd\uff0c\u8fd9\u6b21\u4e0d\u662f\u5c41\u58f0\uff0c\u800c\u662f\u6731\u5f6a\u77ee\u80d6\u7684\u8eab\u8eaf\u649e\u4e0a\u4e86\u5730\u9762\u3002 \u3000\u3000\u8fd9\u4e00\u649e\u4e0d\u6253\u7d27\uff0c\u7531\u4e8e\u5634\u5df4\u5148\u7740\u5730\uff0c\u4e0d\u4f46\u649e\u4e86\u4e2a\u72d7\u5403\u5c4e\uff0c\u8fde\u53cc\u9524\u4e5f\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u5f85\u4ed6\u722c\u8d77\u8eab\u6765\uff0c\u4e0d\u4f46\u7070\u5934\u571f\u8138\uff0c\u5c24\u5176\u6ee1\u5634\u662f\u8840\uff0c\u72fc\u72c8\u4e4b\u72b6\uff0c\u4e0d\u5fcd\u5352\u7779\u3002 \u3000\u3000\u8fd9\u65f6\u8881\u5c0f\u9e64\u548c\u4e01\u6d9b\u3001\u7518\u9716\uff0c\u90fd\u5728\u63a9\u53e3\u6697\u7b11\uff0c\u4ed6\u4eec\u7b11\u7684\u662f\u521a\u624d\u6731\u5f6a\u5c41\u58f0\u5b9e\u5728\u592a\u54cd\uff0c\u7b80\u76f4\u95fb \u6240\u672a\u95fb\u3002 \u3000\u3000\u6731\u5f6a\u5230\u8fd9\u65f6\u624d\u7b97\u9886\u6559\u4e86\u5bf9\u65b9\u7684\u5389\u5bb3\uff0c\u81ea\u5df1\u8fde\u653b\u6570\u62db\uff0c\u5c45\u7136\u8fde\u4eba\u5bb6\u7684\u5f71\u5b50\u90fd\u6ca1\u770b\u89c1\uff0c\u4e0e\u4eba \u52a8\u624b\u8fc7\u62db\u6253\u6210\u8fd9\u79cd\u5730\u6b65\uff0c\u4e5f\u662f\u89c1\u6240\u672a\u89c1\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e24\u624b\u8f7b\u8f7b\u6413\u4e86\u51e0\u4e0b\uff0c\u6307\u7740\u88f4\u901a\u9053\uff1a\u201c\u5927\u4e2a\u5b50\uff0c\u73b0\u5728\u8f6e\u5230\u4f60\u4e86\uff01\u201d \u3000\u3000\u88f4\u901a\u539f\u5148\u7684\u9884\u6599\uff0c\u6731\u5f6a\u6536\u62fe\u5cb3\u5c0f\u98de\uff0c\u7edd\u5bf9\u662f\u4ef6\u8f7b\u800c\u6613\u4e3e\u7684\u4e8b\uff0c \u3000\u3000\u4e07\u60f3\u4e0d\u5230\u7adf\u6f14\u53d8\u51fa\u8fd9\u6837\u7684\u540e\u679c\uff0c\u56e0\u4e4b\uff0c\u4ed6\u5df2\u4e0d\u6562\u8f7b\u654c\u3002 \u3000\u3000\u4ed6\u7528\u7684\u662f\u4e24\u628a\u957f\u67c4\u53cc\u5203\u94f2\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u4ed6\u7f13\u7f13\u89e3\u4e0b\u53cc\u94f2\uff0c\u5411\u524d\u8d70\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u6b64\u4eba\u4eba\u9ad8\u9a6c\u5927\uff0c\u53cc\u81c2\u7279\u957f\uff0c\u518d\u52a0\u53cc\u94f2\u53c8\u662f\u957f\u67c4\uff0c\u4f38\u5c55\u5f00\u6765\uff0c\u5373\u4fbf\u811a\u4e0b\u4e0d\u52a8\uff0c\u4e5f\u8db3\u53ef\u51fb\u4e2d \u4e03\u516b\u5c3a\u5916\u7684\u76ee\u6807\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5ea6\u91cf\u60c5\u52bf\uff0c\u4e0d\u5f97\u4e0d\u628a\u957f\u5251\u4eae\u51fa\u3002 \u3000\u3000\u88f4\u901a\u628a\u53cc\u94f2\u6296\u4e86\u4e00\u6296\u9053\uff1a\u201c\u59d3\u5cb3\u7684\u5c0f\u5b50\uff0c\u4f60\u8981\u5c0f\u5fc3\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6de1\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u65e9\u5c31\u5f00\u59cb\u5c0f\u5fc3\uff0c\u96be\u9053\u4f60\u8fd9\u72d7\u4e1c\u897f\u5c31\u4e0d\u5fc5\u5c0f\u5fc3\uff1f\u201d \u3000\u3000\u88f4\u901a\u4e00\u54ac\u7259\uff0c\u84e6\u5730\u53cc\u94f2\u95ea\u7535\u822c\u51cc\u7a7a\u94f2\u4e0b\uff0c\u679c\u7136\u5a01\u52bf\u60ca\u4eba\uff0c\u4e0d\u540c\u51e1\u54cd\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9000\u540e\u4e00\u6b65\uff0c\u4ef0\u8eab\u62a2\u5251\u683c\u53bb\u3002 \u3000\u3000\u201c\u5693\u201d\u3001\u201c\u5693\u201d\u4e24\u8bb0\u64e6\u9707\uff0c\u5cb3\u5c0f\u98de\u53ea\u611f\u5230\u864e\u53e3\u6709\u4e9b\u75b2\u9ebb\uff0c\u663e\u7136\u5bf9\u65b9\u7684\u5185\u529b\u80dc\u8fc7\u81ea\u5df1\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u4e0d\u8fc7\u662f\u85c9\u6b64\u8bd5\u63a2\uff0c\u5e76\u4e0d\u60f3\u4ee5\u8fd9\u79cd\u65b9\u5f0f\u548c\u5bf9\u65b9\u786c\u62da\u3002 \u3000\u3000\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u4eba\u5df2\u817e\u7a7a\u800c\u8d77\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u88f4\u901a\u7684\u53cc\u94f2\u53c8\u5df2\u4ece\u8eab\u4e0b\u88ad\u5230\u3002 \u3000\u3000\u5251\u5c16\u548c\u94f2\u9762\u4e00\u89e6\uff0c\u5cb3\u5c0f\u98de\u53c8\u5347\u9ad8\u4e86\u6570\u5c3a\u3002 \u3000\u3000\u88f4\u901a\u7684\u624b\u81c2\u548c\u94f2\u67c4\u518d\u957f\uff0c\u6b64\u65f6\u5df2\u65e0\u6cd5\u653b\u5230\u5bf9\u65b9\uff0c\u53ea\u6709\u7b49\u7740\u5bf9\u65b9\u8eab\u5b50\u4e0b\u964d\u65f6\u518d\u51fa\u62db\u3002 \u3000\u3000\u5c82\u77e5\u5cb3\u5c0f\u98de\u8eab\u5b50\u5e76\u4e0d\u5168\u843d\uff0c\u53ea\u8981\u5251\u5c16\u4e00\u89e6\u5230\u94f2\u9762\uff0c\u4fbf\u53c8\u5347\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u8fd9\u4f7f\u88f4\u901a\u53ea\u6709\u5e72\u77aa\u773c\u7684\u4efd\u513f\uff0c\u5c24\u5176\u4ed6\u773c\u89c1\u5cb3\u5c0f\u98de\u5982\u6b64\u60ca\u4e16\u9a87\u4fd7\u7684\u6b66\u529f\uff0c\u5c82\u6b62\u5e72\u77aa\u773c\uff0c\u7b80 \u76f4\u5df2\u76ee\u77aa\u53e3\u5446\u3002 \u3000\u3000\u7a81\u89c1\u5cb3\u5c0f\u98de\u8eab\u5b50\u6709\u5982\u5f29\u7bad\u79bb\u5f26\uff0c\u95ea\u7535\u822c\u76f4\u5c04\u800c\u4e0b\uff0c\u88f4\u901a\u8fd8\u6ca1\u6765\u5f97\u53ca\u51fa\u624b\uff0c\u53f3\u80a9\u4fbf\u88ab\u4e00\u5251 \u523a\u7a7f\u3002 \u3000\u3000\u5f85\u88f4\u901a\u614c\u9a87\u95f4\u51fa\u94f2\u53cd\u51fb\u65f6\uff0c\u5cb3\u5c0f\u98de\u65e9\u5df2\u843d\u5728\u4ed6\u7684\u8eab\u540e\u3002 \u3000\u3000\u88f4\u901a\u6025\u6025\u8f6c\u8eab\uff0c\u53cc\u94f2\u731b\u7136\u51fb\u4e0b\u3002 \u3000\u3000\u8c01\u4e5f\u60f3\u4e0d\u5230\uff0c\u4ed6\u7684\u94f2\u5c1a\u672a\u94f2\u51fa\u4e00\u534a\uff0c\u5374\u5ffd\u7136\u5f2f\u4e0b\u8170\u53bb\uff0c\u629b\u94f2\u6342\u4f4f\u5c0f\u8179\u3002 \u3000\u3000\u539f\u6765\u4ed6\u7684\u5c0f\u8179\u5df2\u88ab\u5cb3\u5c0f\u98de\u4e00\u7f15\u6307\u98ce\u9694\u7a7a\u70b9\u4e2d\u3002 \u3000\u3000\u4ed6\u521a\u6342\u4f4f\u5c0f\u8179\uff0c\u4e0d\u6599\u5c41\u80a1\u4e0a\u53c8\u4e2d\u4e86\u4e00\u811a\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u6ca1\u50cf\u6731\u5f6a\u90a3\u6837\u7684\u6454\u51fa\uff0c\u5374\u5f53\u573a\u4e00\u5934\u683d\u4e86\u4e0b\u53bb\u3002\u800c\u5cb3\u5c0f\u98de\u5df2\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u65e9\u5df2\u53c8 \u7ad9\u56de\u5ba2\u5385\u95e8\u53e3\u3002 \u3000\u3000\u4e0d\u5230\u4e00\u76cf\u8336\u7684\u5de5\u592b\uff0c\u6731\u5f6a\u548c\u88f4\u901a\u5168\u5df2\u72fc\u72c8\u4e0d\u582a\u7684\u5931\u53bb\u6218\u6597\u529b\u3002 \u3000\u3000\u8fd9\u65f6\u6700\u611f\u72fc\u72c8\u800c\u53c8\u795e\u8272\u51dd\u91cd\u7684\uff0c\u8be5\u662f\u62db\u8d24\u9986\u4e3b\u8427\u6e58\u3002\u597d\u5728\u8fd8\u6709\u4e09\u4eba\u4e0d\u66fe\u4e0a\u9635\uff0c\u603b\u8fd8\u5b58\u7740 \u4e00\u7ebf\u5e0c\u671b\u3002 \u3000\u3000\u4ed6\u56de\u5934\u671b\u5411\u8881\u5c0f\u9e64\u7b49\u4e09\u4eba\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6731\u58ee\u58eb\u548c\u88f4\u58ee\u58eb\u7adf\u7136\u4e0d\u592a\u4e2d\u7528\uff0c\u73b0\u5728\u8be5\u770b\u4f60\u4eec\u4e09 \u4f4d\u7684\u4e86\uff01\u201d \u3000\u3000\u4e01\u6d9b\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u6211\u4eec\u4e09\u4e2a\u548c\u524d\u9762\u4e24\u4f4d\u4e5f\u5dee\u4e0d\u591a\uff0c\u5176\u5b9e\u4e0d\u662f\u4ed6\u4eec\u4e0d\u4e2d\u7528\uff0c\u800c\u662f\u8fd9\u4f4d \u59d3\u5cb3\u7684\u5c0f\u5144\u5f1f\u8eab\u624b\u592a\u9ad8\u4e86\uff01\u201d \u3000\u3000\u8427\u6e58\u51dd\u7740\u8138\u8272\u9053\uff1a\u201c\u8427\u67d0\u77e5\u9053\u4f60\u4eec\u4e09\u4f4d\u548c\u4ed6\u662f\u4e00\u9053\u6765\u7684\uff0c\u4e5f\u8bb8\u4f60\u4eec\u6709\u4ea4\u60c5\uff0c\u4e0d\u613f\u4f24\u4e86\u548c \u6c14\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u6447\u5934\u9053\uff1a\u201c\u9986\u4e3b\u600e\u53ef\u8fd9\u6837\u8bf4\u8bdd\uff0c\u6211\u4eec\u8ddf\u4ed6\u7eb5\u7136\u76f8\u5904\u8fc7\u4e00\u6bb5\u65e5\u5b50\uff0c\u90a3\u4e5f\u53ea\u80fd\u7b97\u79c1\u60c5\uff0c \u4eca\u5929\u6211\u4eec\u5949\u9986\u4e3b\u4e4b\u547d\u6765\u62ff\u4ed6\u662f\u516c\u4e8b\uff0c\u79c1\u60c5\u90a3\u80fd\u6bd4\u516c\u4e8b\u91cd\u8981\uff0c\u4f60\u8001\u4eba\u5bb6\u53ef\u771f\u5f00\u73a9\u7b11\u5566\uff01\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\uff0c\u542c\u5f97\u8427\u6e58\u5341\u5206\u5f97\u610f\uff0c\u62c2\u987b\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e01\u58ee\u58eb\u8fd8\u662f\u4f4d\u6df1\u660e\u5927\u4e49\u7684\u4eba\uff0c \u4f60\u4eec\u6709\u8fd9\u79cd\u60f3\u6cd5\uff0c\u624d\u662f\u6700\u6b63\u786e\u7684\uff0c\u4ed6\u73b0\u5728\u662f\u53db\u9006\uff0c\u82e5\u518d\u6709\u79c1\u60c5\uff0c\u90a3\u5c31\u5927\u5927\u4e0d\u5bf9\u4e86\u3002\u4f55\u51b5\u4f60\u4eec \u7acb\u4e86\u5927\u529f\uff0c\u5bf9\u81ea\u5df1\u53c8\u6709\u5927\u5927\u7684\u597d\u5904\uff0c\u4e5f\u53ea\u6709\u8427\u67d0\u624d\u80fd\u5e2e\u4f60\u4eec\u7684\u5fd9\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u54a7\u5634\u7b11\u9053\uff1a\u201c\u9986\u4e3b\u7684\u8bdd\u5bf9\u6781\u4e86\uff0c\u6211\u4eec\u6765\u5230\u62db\u8d24\u9986\uff0c\u4e00\u5207\u5168\u4ed7\u4f60\u8001\u4eba\u5bb6\u63d0\u62d4\uff0c\u82e5\u518d\u548c \u59d3\u5cb3\u7684\u8bb2\u611f\u60c5\uff0c\u81ea\u5df1\u53cd\u800c\u5403\u4e8f\u4e86\uff0c\u6211\u4eec\u82e5\u8fde\u8fd9\u70b9\u9053\u7406\u90fd\u4e0d\u61c2\uff0c\u90a3\u4e0d\u662f\u767d\u5403\u8001\u7c73\u996d\uff0c\u6789\u6d3b\u8fd9\u4e48 \u5927\u4e86\uff1f\u201d \u3000\u3000\u8427\u6e58\u8fde\u8fde\u9894\u9996\u9053\uff1a\u201c\u8bf4\u5f97\u597d\uff0c\u8bf4\u5f97\u597d\uff0c\u672c\u9986\u4e3b\u8fd9\u5c31\u653e\u5fc3\u4e86\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8bdd\u662f\u8fd9\u4e48\u6837\uff0c\u4e0d\u8fc7\u80fd\u4e0d\u80fd\u62ff\u4e0b\u4ed6\uff0c\u8c01\u4e5f\u6ca1\u628a\u63e1\u3002\u201d \u3000\u3000\u8427\u6e58\u9053\uff1a\u201c\u4f60\u4e14\u51fa\u624b\u770b\u770b\uff0c\u5982\u679c\u4e0d\u884c\uff0c\u4e0d\u59a8\u4e09\u4f4d\u4e00\u8d77\u4e0a\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u7531\u6211\u5148\u8bd5\u8bd5\u4ed6\uff01\u201d"}),showOnHomepage:!0},{name:"\u7b2c\u5341\u56db\u56de\u3000\u4e94\u5173\u603b\u9547",content:(0,t.jsx)(t.Fragment,{children:"\u4e01\u6d9b\u5148\u5411\u5cb3\u5c0f\u98de\u4f7f\u4e2a\u773c\u8272\uff0c\u7136\u540e\u62b1\u62f3\u62f1\u62f1\u624b\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u5bf9\u4e0d\u8d77\u5566\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4e01\u5f53\u5bb6\u7684\u522b\u5ba2\u6c14\uff0c\u516c\u4e8b\u516c\u529e\uff0c\u8fd9\u624d\u663e\u5f97\u4f60\u5bf9\u8427\u9986\u4e3b\u5fe0\u5fc3\u803f\u803f\uff0c\u90a3 \u6709\u4ec0\u4e48\u5bf9\u4e0d\u8d77\u7684\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u53ef\u662f\u54b1\u4eec\u603b\u662f\u6709\u4ea4\u60c5\u7684\uff0c\u60f3\u5f53\u65e5\uff0c\u4e00\u540c\u8fdb\u8c37\uff0c\u4e00\u540c\u8fc7\u5173\uff0c\u73b0\u5728\u7adf\u7136\u8981\u52a8\u624b\uff0c \u5b9e\u5728\u5c34\u5c2c\u5f97\u5f88\u3002\u201d \u3000\u3000\u201c\u6ca1\u4ec0\u4e48\u5c34\u5c2c\u7684\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u7acb\u529f\u7b2c\u4e00\uff0c\u5982\u679c\u4e0d\u5fcd\u4e0b\u624b\uff0c\u5403\u4e8f\u7684\u8fd8\u662f\u4f60\u81ea\u5df1\u3002\u201d \u3000\u3000\u4e01\u7977\u50bb\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u8fd8\u662f\u6709\u70b9\u4e0d\u597d\u610f\u601d\u3002\u201d \u3000\u3000\u53ea\u542c\u8427\u6e58\u9053\uff1a\u201c\u4e01\u58ee\u58eb\uff0c\u4f60\u548c\u4ed6\u8fd9\u6837\u78e8\u83c7\u4e0b\u53bb\uff0c\u5c82\u4e0d\u53d8\u6210\u4e86\u5929\u6865\u628a\u5f0f\u2014\u2014\u53ea\u8bf4\u4e0d\u7ec3\uff1f\u201d \u3000\u3000\u201c\u597d\uff0c\u73b0\u5728\u5c31\u7ec3\uff01\u201d \u3000\u3000\u4e01\u6d9b\u8fb9\u8bf4\u8fb9\u62bd\u51fa\u539a\u80cc\u9b3c\u5934\u5200\uff0c\u6643\u4e86\u4e00\u6643\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u5c0f\u5fc3\u4e86\uff0c\u54b1\u771f\u7684\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u63a5\u7740\u4e24\u4eba\u5c31\u6253\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e3a\u4e86\u62d6\u5ef6\u65f6\u95f4\uff0c\u6545\u610f\u5e76\u4e0d\u6c42\u80dc\uff0c\u5e76\u6697\u793a\u4e01\u6d9b\uff0c\u8981\u4ed6\u5168\u529b\u731b\u653b\u3002 \u3000\u3000\u4e01\u6d9b\u5fc3\u91cc\u6709\u6570\uff0c\u81ea\u5df1\u5373\u4fbf\u518d\u600e\u4e48\u653b\uff0c\u4e5f\u7edd\u5bf9\u4f24\u4e0d\u5230\u5bf9\u65b9\uff0c\u56e0\u4e4b\uff0c\u4ed6\u8fd8\u771f\u7684\u62ff\u51fa\u770b\u5bb6\u672c\u9886\uff0c \u780d\u6740\u5f97\u6709\u677f\u6709\u773c\uff0c\u8ba9\u8427\u6e58\u534a\u70b9\u770b\u4e0d\u51fa\u5047\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u60f3\u548c\u4e01\u6d9b\u6218\u4e2a\u5e73\u624b\uff0c\u4ee5\u4fbf\u8ba9\u4e01\u6d9b\u5728\u8427\u6e58\u773c\u91cc\u662f\u4f4d\u4e86\u4e0d\u8d77\u7684\u4eba\u7269\uff0c\u5982\u6b64\u5bf9\u4e01\u6d9b\u5c06 \u6765\u5206\u6d3e\u5de5\u4f5c\uff0c\u5f53\u7136\u5927\u6709\u597d\u5904\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u62da\u6218\uff0c\u8db3\u8db3\u62da\u4e86\u987f\u996d\u5de5\u592b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u672c\u6765\u8fd8\u60f3\u628a\u65f6\u95f4\u62d6\u4e0b\u53bb\uff0c\u5c82\u77e5\u4e01\u6d9b\u65e9\u5df2\u7b4b\u75b2\u529b\u5c3d\uff0c\u7d2f\u5f97\u4ed6\u6c14\u5598\u5982\u725b\u3002\u6b32\u5f85\u653e\u7f13\u52a8 \u4f5c\uff0c\u53c8\u6015\u8427\u6e58\u770b\u51fa\u7834\u7efd\uff0c\u53ea\u597d\u529b\u62da\u51e0\u62db\uff0c\u9000\u4e86\u56de\u6765\u3002 \u3000\u3000\u8427\u6e58\u6b63\u770b\u5f97\u5927\u4e3a\u9ad8\u5174\uff0c\u8fd9\u65f6\u4e0d\u7531\u6123\u4e86\u6123\u9053\uff1a\u201c\u4e01\u58ee\u58eb\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u4e00\u9f13\u4f5c\u6c14\u628a\u4ed6\u64d2\u4e0b\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u63e9\u62ed\u7740\u989d\u89d2\u4e0a\u6eda\u6eda\u800c\u4e0b\u7684\u6c57\u73e0\u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\u771f\u96be\u7f20\uff0c\u54b1\u5df2\u7d2f\u6210\u8fd9\u6837\u5b50\uff0c\u96be\u9053\u9986\u4e3b\u6ca1 \u770b\u89c1\uff1f\u201d \u3000\u3000\u8427\u6e58\u9053\uff1a\u201c\u679c\u7136\u4f60\u7d2f\u5f97\u4e0d\u8f7b\uff0c\u4e0d\u8fc7\u4e01\u58ee\u58eb\u7684\u6b66\u529f\uff0c\u5df2\u4e0d\u5728\u4ed6\u4e4b\u4e0b\uff0c\u82e5\u518d\u52a0\u4e9b\u8010\u529b\uff0c\u90a3\u5c31 \u5927\u529f\u544a\u6210\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7740\u671b\u5411\u5cb3\u5c0f\u98de\u4e00\u773c\u9053\uff1a\u201c\u8427\u67d0\u53ea\u9053\u4f60\u7684\u6b66\u529f\u5728\u672c\u9986\u5df2\u7ecf\u65e0\u4eba\u53ef\u654c\uff0c\u8fd9\u4f4d\u4e01\u58ee\u58eb\u5c31\u4e0d\u6bd4 \u4f60\u5dee\u591a\u5c11\uff0c\u4e0b\u9762\u8fd8\u6709\u4e24\u4f4d\uff0c\u4e0d\u6015\u64d2\u4e0d\u4f4f\u4f60\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u88c5\u51fa\u4f53\u529b\u4e0d\u7ee7\u6a21\u6837\uff0c\u5598\u606f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u4f60\u5c31\u8ba9\u4ed6\u4eec\u4e24\u4e2a\u518d\u6765\uff0c\u770b\u4ed6\u4eec\u80fd\u4e0d\u80fd\u62ff \u4e0b\u5728\u4e0b\u3002\u201d \u3000\u3000\u8427\u6e58\u8f6c\u5934\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u54ea\u4f4d\u5148\u4e0a\uff0c\u53ea\u8981\u7528\u8f66\u8f6e\u6218\u6cd5\uff0c\u5f88\u5feb\u4fbf\u53ef\u64d2\u4f4f\u4ed6\uff0c\u4ed6\u73b0\u5728\u5df2\u6210\u5f3a \u5f29\u4e4b\u672b\uff0c\u5f88\u5feb\u5c31\u652f\u6301\u4e0d\u4f4f\u4e86\u3002\u201d \u3000\u3000\u7518\u9716\u62d4\u4e0b\u63d2\u5728\u80cc\u540e\u7684\u4e24\u679d\u94c1\u7b14\u9053\uff1a\u201c\u9986\u4e3b\u653e\u5fc3\uff0c\u6709\u6211\u4eec\u4e24\u4eba\uff0c\u8fd8\u6015\u5236\u670d\u4e0d\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u8427\u6e58\u9053\uff1a\u201c\u5feb\u4e0a\uff0c\u6253\u94c1\u8d81\u70ed\uff0c\u522b\u8ba9\u4ed6\u6709\u5598\u606f\u7684\u65f6\u95f4\u3002\u201d \u3000\u3000\u7518\u9716\u521a\u8981\u63c9\u8eab\u800c\u4e0a\uff0c\u7a81\u542c\u5927\u95e8\u5916\u811a\u6b65\u58f0\u54cd\uff0c\u63a5\u7740\u8427\u6e58\u53eb\u9053\uff1a\u201c\u7518\u58ee\u58eb\u6682\u505c\uff01\u201d \u3000\u3000\u7518\u9716\u8fd8\u6ca1\u5f04\u6e05\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4e00\u540d\u6709\u5982\u5929\u4eba\u4e0b\u51e1\u822c\u7684\u767d\u8863\u5973\u90ce\u5df2\u67ad\u67ad\u5a77\u5a77\u7684\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u6765\u4eba\u6b63\u662f\u5927\u516c\u4e3b\u3002 \u3000\u3000\u5927\u516c\u4e3b\u8eab\u540e\u7d27\u8ddf\u7740\u516c\u5b59\u7389\u3002 \u3000\u3000\u8427\u6e58\u8fde\u5fd9\u8eac\u8eab\u4e00\u793c\u9053\uff1a\u201c\u5351\u804c\u53c2\u89c1\u5927\u516c\u4e3b\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u606d\u606d\u656c\u656c\u7684\u5411\u5927\u516c\u4e3b\u65bd\u4e86\u4e00\u793c\u3002 \u3000\u3000\u5927\u516c\u4e3b\u76ee\u5149\u7f13\u7f13\u626b\u89c6\u8fc7\u5168\u573a\uff0c\u832b\u7136\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u8427\u6e58\u9053\uff1a\u201c\u5728\u4e0b\u6b63\u8981\u7980\u62a5\u5927\u516c\u4e3b\uff0c\u8fd9\u59d3\u5cb3\u7684\u5c0f\u5b50\u505a\u4e0b\u4e86\u5927\u9006\u4e0d\u9053\u7684\u4e8b\uff0c\u95f9\u5f97\u4e5f\u5b9e\u5728\u592a\u4e0d \u50cf\u8bdd\u4e86\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u770b\u6837\u5b50\u4f60\u5e26\u7740\u8fd9\u4e48\u591a\u4eba\u6765\uff0c\u662f\u51c6\u5907\u628a\u4ed6\u62ff\u4e0b\u6cbb\u7f6a\uff1f\u201d \u3000\u3000\u8427\u6e58\u9894\u9996\u9053\uff1a\u201c\u4ed6\u505a\u4e0b\u8fd9\u6837\u65e0\u6cd5\u65e0\u5929\u7684\u4e8b\uff0c\u82e5\u5351\u804c\u4e0d\u628a\u4ed6\u62ff\u4e0b\uff0c\u672c\u9986\u6212\u5f8b\u5982\u4f55\u7ef4\u6301\uff1f\u53c8 \u600e\u6837\u5411\u6559\u4e3b\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u201c\u4f60\u628a\u4ed6\u62ff\u4e0b\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u6b63\u8981\u62ff\u4e0b\uff0c\u4e0d\u60f3\u5927\u516c\u4e3b\u5c31\u6765\u4e86\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u89c6\u7ebf\u518d\u63a0\u8fc7\u6731\u5f6a\u3001\u88f4\u901a\uff0c\u4e01\u6d9b\u7b49\u4e00\u904d\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u90fd\u662f\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u8427\u6e58\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u6295\u6548\u6765\u7684\uff0c\u76ee\u524d\u6b63\u4f4f\u5728\u5404\u5206\u9986\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u53ef\u66fe\u5206\u6d3e\u8fc7\u5de5\u4f5c\uff1f\u201d \u3000\u3000\u201c\u76ee\u524d\u8fd8\u6ca1\u6709\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u51b7\u58f0\u9053\uff1a\u201c\u50cf\u4ec0\u4e48\u8bdd\uff0c\u8fde\u5de5\u4f5c\u90fd\u6ca1\u5206\u6d3e\uff0c\u4fbf\u8981\u4ed6\u4eec\u5230\u522b\u7684\u5206\u9986\u6293\u4eba\uff0c\u4f60\u4eec\u603b\u9986\u7684 \u4eba\u90fd\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u8427\u6e58\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u5351\u804c\u60ed\u6127\uff0c\u603b\u9986\u7684\u9ad8\u624b\uff0c\u5b9e\u5728\u6ca1\u4e00\u4e2a\u80fd\u5236\u4f4f\u4ed6\u7684\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u82b1\u526f\u9986\u4e3b\u4e5f\u4e0d\u662f\u4ed6\u7684\u654c\u624b\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u82b1\u526f\u9986\u4e3b\u4e0a\u6b21\u662f\u62a4\u7740\u4ed6\u7684\uff0c\u5351\u804c\u5b9e\u5728\u4e0d\u4fbf\u901a\u77e5\u82b1\u526f\u9986\u4e3b\u3002\u201d \u3000\u3000\u201c\u4ed6\u7a76\u7adf\u72af\u4e0b\u4ec0\u4e48\u5927\u9519\uff0c\u4f60\u8fd8\u6ca1\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u201c\u5b9e\u5728\u592a\u4e0d\u50cf\u8bdd\uff0c\u4ed6\u6628\u65e5\u79c1\u81ea\u5916\u51fa\uff0c\u4e0d\u4f46\u5927\u95f9\u706b\u5173\uff0c\u5e76\u628a\u706b\u5173\u4e00\u4e2a\u53eb\u6843\u82b1\u7684\u5973\u5175\u5e26\u5230\u5206 \u9986\u6765\uff0c\u800c\u4e14\u8fd8\u4f4f\u4e86\u4e00\u591c\uff0c\u5927\u516c\u4e3b\uff0c\u60a8\u8bf4\u8fd9\u4e0d\u662f\u592a\u65e0\u6cd5\u65e0\u5929\u4e86\u5417\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u98ce\u81f4\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u6211\u5f53\u662f\u4ec0\u5e7a\u4e8b\uff0c\u8fd9\u4e9b\u4e8b\u6211\u65e9\u5df2\u7ecf\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u8427\u6e58\u5927\u611f\u4e00\u6123\u9053\uff1a\u201c\u65e2\u7136\u5927\u516c\u4e3b\u5df2\u7ecf\u77e5\u9053\uff0c\u90a3\u5c31\u8be5\u7531\u5927\u516c\u4e3b\u5904\u7f6e\u4ed6\u4e86\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u6211\u73b0\u5728\u6765\uff0c\u6b63\u662f\u4e3a\u4e86\u5904\u7f6e\u8fd9\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u8427\u6e58\u5fc3\u4e2d\u2014\u559c\u9053\uff1a\u201c\u90a3\u5c31\u628a\u4ed6\u4ea4\u7ed9\u5927\u516c\u4e3b\u4e86\uff0c\u770b\u4ed6\u8fd8\u6562\u4e0d\u6562\u53cd\u6297\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u6211\u662f\u6765\u5904\u7f6e\u6843\u82b1\u7684\u4e8b\uff0c\u770b\u5979\u4f4f\u5728\u8fd9\u91cc\u65b9\u4e0d\u65b9\u4fbf\uff0c\u5982\u679c\u4e0d\u65b9\u4fbf\uff0c\u5c31\u53e6\u60f3\u529e \u6cd5\u3002\u201d \u3000\u3000\u8427\u6e58\u4e24\u773c\u7728\u52a8\u4e86\u4e00\u9635\u9053\uff1a\u201c\u5927\u516c\u4e3b\u8fd9\u8bdd\uff0c\u5351\u804c\u6709\u4e9b\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u884c\u4ec0\u4e48\u4e0d\u61c2\u7684\uff0c\u662f\u6211\u5429\u5490\u5cb3\u516c\u5b50\u548c\u516c\u5b59\u5148\u751f\u628a\u6843\u82b1\u5e26\u5230\u8fd9\u91cc\u7684\uff0c\u73b0\u5728\u8be5 \u61c2\u4e86\u5427\uff1f\u201d \u3000\u3000\u8427\u6e58\u50cf\u5f53\u5934\u6328\u4e86\u2014\u68d2\uff0c\u987f\u4e86\u987f\u9053\uff1b\u201c\u5927\u516c\u4e3b\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4e3a\u7684\u662f\u8981\u6551\u4e00\u6761\u4eba\u547d\uff0c\u4e0d\u80fd\u8ba9\u6843\u82b1\u88ab\u4f60\u59b9\u5b50\u5904\u6b7b\u3002\u201d \u3000\u3000\u201c\u6843\u82b1\u8fd9\u8d31\u4eba\u2026\u2026\u2026\u201d \u3000\u3000\u201c\u5979\u4e0d\u662f\u8d31\u4eba\uff0c\u5979\u867d\u7136\u8eab\u4efd\u5351\u5fae\uff0c\u4f46\u4eba\u5374\u4e0d\u8d31\uff0c\u6211\u4e14\u95ee\u4f60\uff0c\u4f60\u59b9\u5b50\u4e3a\u4ec0\u4e48\u8981\u90a3\u6837\u5904\u7f6e \u5979\uff1f\u201d \u3000\u3000\u201c\u5979\u2026\u2026\u5979\u72af\u4e0b\u4e86\u6212\u5f8b\uff01\u201d \u3000\u3000\u201c\u4f60\u9519\u4e86\uff0c\u72af\u6212\u5f8b\u7684\u4e5f\u8bb8\u662f\u5cb3\u516c\u5b50\uff0c\u6843\u82b1\u8fde\u603b\u575b\u90fd\u80fd\u53bb\uff0c\u96be\u9053\u5c31\u4e0d\u80fd\u5230\u62db\u8d24\u5206\u9986\u6765\uff1f\u4f55 \u51b5\u5979\u5e76\u4e0d\u77e5\u9053\u5973\u4eba\u4e0d\u51c6\u8fdb\u5165\u5206\u9986\u7684\u89c4\u5b9a\uff0c\u8427\u9986\u4e3b\uff0c\u4f60\u7684\u547d\u4ee4\u62db\u8d24\u9986\u7684\u4eba\u4e0d\u5f97\u8fdd\u53cd\u89c4\u5b9a\uff0c\u96be\u9053 \u706b\u5173\u7684\u4eba\u4e5f\u8981\u542c\u4f60\u7684\uff1f\u201d \u3000\u3000\u8427\u6e58\u987f\u65f6\u88ab\u95ee\u5f97\u54d1\u53e3\u65e0\u8a00\uff0c\u8bb8\u4e45\uff0c\u624d\u55eb\u5685\u7740\u9053\uff1a\u201c\u542f\u7980\u5927\u516c\u4e3b\uff0c\u59d3\u5cb3\u7684\u5c0f\u5b50\u53e6\u5916\u4e00\u4ef6\u5927 \u9006\u4e0d\u9053\u4e4b\u4e8b\uff0c\u60a8\u53ef\u80fd\u8fd8\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u54e6\u4e86\u58f0\u9053\uff1b\u201c\u4f60\u8bf4\u8bf4\u770b\uff01\u201d \u3000\u3000\u8427\u6e58\u9053\uff1a\u201c\u4ed6\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5c45\u7136\u5bf9\u5973\u4eba\u6709\u4e86\u90aa\u5fc3\u6b32\u5ff5\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u679c\u771f\u4e00\u6123\u9053\uff1a\u201c\u8fd9\u8bdd\u600e\u4e48\u8bb2\uff1f\u201d \u3000\u3000\u8427\u6e58\u9053\uff0c\u201c\u4ed6\u6628\u591c\u7adf\u548c\u6843\u82b1\u90a3\u8d31\u5973\u4eba\u540c\u5c45\u4e00\u5ba4\uff0c\u540c\u7761\u4e00\u5e8a\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u9edb\u7709\u2014\u8038\uff0c\u661f\u7738\u5c04\u5149\uff0c\u671b\u5411\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u53ef\u6709\u8fd9\u4e8b\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6843\u82b1\u6628\u591c\u4f4f\u5728\u53a2\u623f\u91cc\u3002\u201d \u3000\u3000\u8427\u6e58\u5fd9\u9053\uff1a\u201c\u4ed6\u8fd9\u6837\u8bf4\u96be\u9053\u5927\u516c\u4e3b\u76f8\u4fe1\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5927\u516c\u4e3b\u4e0d\u76f8\u4fe1\u53ef\u4ee5\u95ee\u6843\u82b1\uff0c\u66f4\u53ef\u4ee5\u95ee\u6797\u91d1\u5b9d\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u56db\u4e0b\u671b\u4e86\u4e00\u773c\u9053\uff1a\u201c\u6797\u91d1\u5b9d\u5728\u90a3\u91cc\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u7acb\u523b\u4ece\u4ed6\u4f4f\u7684\u623f\u91cc\u8dd1\u4e86\u51fa\u6765\uff0c\u201c\u6251\u901a\u201d\u4e00\u58f0\uff0c\u8dea\u5728\u5730\u4e0a\u9053\uff1a\u201c\u542f\u684c\u5927\u516c\u4e3b\uff0c\u6843\u82b1 \u59d1\u5a18\u6628\u665a\u4f4f\u5728\u53a2\u623f\u91cc\uff0c\u90a3\u623f\u5b50\u8fd8\u662f\u5c0f\u7684\u6253\u626b\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u8427\u6e58\u559d\u9053\uff1a\u201c\u6797\u91d1\u5b9d\uff0c\u5728\u5927\u516c\u4e3b\u9762\u524d\u53ef\u8981\u8bf4\u5b9e\u8bdd\uff0c\u8981\u4e0d\u7136\uff0c\u5c0f\u5fc3\u8001\u592b\u628a\u4f60\u5904\u6b7b\uff01\u201d \u3000\u3000\u6797\u91d1\u5b9d\u8f6c\u8fc7\u8eab\u6765\u53e9\u5934\u9053\uff1a\u201c\u5c0f\u7684\u8bf4\u7684\u662f\u5b9e\u8bdd\uff0c\u600e\u6562\u6b3a\u9a97\u5927\u516c\u4e3b\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u51b7\u7b11\u9053\uff1a\u201c\u8427\u9986\u4e3b\uff0c\u6700\u597d\u522b\u5728\u6211\u9762\u524d\u800d\u5a01\u98ce\uff01\u201d \u3000\u3000\u8427\u6e58\u8eac\u8eab\u9053\uff1a\u201c\u5351\u804c\u4e0d\u6562\uff0c\u4e0d\u8fc7\u8fd9\u6797\u91d1\u5b9d\u7684\u8bdd\u7eb5\u7136\u662f\u771f\uff0c\u5374\u4e5f\u96be\u4fdd\u59d3\u5cb3\u7684\u90a3\u5c0f\u5b50\u548c\u6843\u82b1 \u90a3\u8d31\u4eba\u505a\u4e0d\u51fa\u4e11\u4e8b\u6765\u3002\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u53c8\u600e\u4e48\u8bb2\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u4ed6\u4eec\u4e0d\u4f4f\u5728\u540c\u4e00\u623f\u95f4\uff0c\u5c31\u505a\u4e0d\u51fa\u90a3\u79cd\u4e8b\u6765\uff1f\u4f55\u51b5\u6797\u91d1\u5b9d\u8fd8\u8981\u5230\u603b\u9986\u53d6\u996d\uff01\u201d \u3000\u3000\u53ea\u542c\u5927\u516c\u4e3b\u51b7\u51b7\u4e00\u9635\u7b11\u9053\uff1a\u201c\u8427\u9986\u4e3b\uff0c\u6211\u60f3\u95ee\u95ee\uff0c\u4f60\u5728\u5cb3\u516c\u5b50\u90a3\u79cd\u5e74\u7eaa\uff0c\u662f\u5426\u4e5f\u66fe\u505a\u51fa \u8fd9\u79cd\u4e8b\u6765\uff1f\u201d \u3000\u3000\u8427\u6e58\u5446\u4e86\u4e00\u5446\u9053\uff1b\u201c\u5927\u516c\u4e3b\u8fd9\u8bdd\uff0c\u5351\u804c\u4e0d\u89e3\uff0c\u5351\u804c\u5e7c\u8bfb\u8bd7\u4e66\uff0c\u7109\u80fd\u4e0d\u77e5\u793c\u4eea\uff0c\u600e\u80fd\u505a\u51fa \u8fd9\u79cd\u4e8b\u6765\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u83ab\u975e\u5cb3\u516c\u5b50\u662f\u4e0d\u66fe\u8bfb\u8fc7\u8bd7\u4e66\uff1f\u4ed6\u82e5\u6ca1\u8bfb\u8fc7\u4e66\uff0c\u7a76\u7adf\u662f\u600e\u6837\u901a\u8fc7\u6587\u5173\u6d4b\u8bd5 \u7684\uff1f\u201d \u3000\u3000\u8427\u6e58\u767b\u65f6\u5446\u5728\u5f53\u573a\u3002 \u3000\u3000\u5927\u516c\u4e3b\u54fc\u4e86\u4e00\u58f0\uff0c\u518d\u9053\uff1a\u201c\u8427\u9986\u4e3b\uff0c\u6700\u597d\u522b\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\u53bb\u5ea6\u541b\u5b50\u4e4b\u8179\uff0c\u6211\u4e0d\u59a8\u518d\u544a\u8bc9\u4f60\uff0c \u90a3\u6843\u82b1\u5df2\u88ab\u4ee4\u59b9\u62f7\u6253\u5f97\u904d\u4f53\u9cde\u4f24\uff0c\u5373\u4fbf\u5979\u6709\u5fc3\u4e3a\u6076\uff0c\u662f\u5426\u80fd\u505a\u51fa\u90a3\u79cd\u4e8b\u6765\uff0c\u4f60\u4e0d\u59a8\u60f3\u60f3\u770b\uff01\u201d \u3000\u3000\u8427\u6e58\u9762\u5b54\u618b\u5f97\u4e9a\u8d5b\u732a\u809d\uff0c\u4f4e\u4e0b\u5934\uff0c\u8fde\u58f0\u9053\uff1a\u201c\u662f\u5351\u804c\u591a\u5fc3\u4e86\uff0c\u8fd8\u8bf7\u5927\u516c\u4e3b\u89c1\u8c05\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u73b0\u5728\u6ca1\u4f60\u7684\u4e8b\u4e86\uff0c\u8be5\u5e26\u7740\u4f60\u7684\u5927\u961f\u4eba\u9a6c\u8d70\u4e86\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u8fd9\u91cc\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u91cc\u6709\u6211\u5904\u7406\uff0c\u4f60\u662f\u5426\u5acc\u6211\u65e0\u6cd5\u5904\u7f6e\u59a5\u5f53\uff1f\u201d \u3000\u3000\u201c\u5927\u516c\u4e3b\u8fd9\u6837\u8bf4\u8bdd\uff0c\u6559\u5351\u804c\u5982\u4f55\u62c5\u5f85\u5f97\u8d77\uff1f\u201d \u3000\u3000\u201c\u65e2\u7136\u6ca1\u6709\u8bdd\u8bf4\uff0c\u8fd8\u4e0d\u8d70\u7b49\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8427\u6e58\u4f3c\u662f\u6028\u6c14\u65e0\u5904\u5f97\u4f38\uff0c\u63a0\u4e86\u516c\u5b59\u7389\u4e00\u773c\u9053\uff1a\u201c\u516c\u5b59\u5148\u751f\uff0c\u4f60\u6628\u65e5\u548c\u59d3\u5cb3\u7684\u5c0f\u5b50\u5230\u706b\u5173 \u53bb\uff0c\u603b\u8be5\u5bf9\u8427\u67d0\u8bb2\u4e00\u58f0\u624d\u5bf9\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u6b49\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5351\u804c\u62b1\u6b49\uff0c\u8fd9\u662f\u5927\u516c\u4e3b\u7684\u4ea4\u4ee3\uff0c\u5351\u804c\u600e\u6562\u4e0d\u9075\u3002\u201d \u3000\u3000\u8427\u6e58\u8d8a\u89c9\u7a9d\u56ca\uff0c\u7eff\u7740\u8138\u8f6c\u8fc7\u8eab\u6765\u9053\uff1a\u201c\u5404\u4f4d\u58ee\u58eb\uff0c\u54b1\u4eec\u8d70\uff01\u201d \u3000\u3000\u8427\u6e58\u7b49\u4eba\u8d70\u540e\uff0c\u6843\u82b1\u8fd9\u624d\u6765\u5230\u9662\u4e2d\u5411\u5927\u516c\u4e3b\u53e9\u62dc\u3002 \u3000\u3000\u5927\u516c\u4e3b\u5bf9\u6843\u82b1\u4f3c\u662f\u9887\u4e3a\u7231\u601c\uff0c\u4eb2\u81ea\u6276\u5979\u8d77\u6765\u9053\uff1a\u201c\u4f60\u53d7\u82e6\u4e86\uff01\u201d \u3000\u3000\u6843\u82b1\u611f\u52a8\u5f97\u70ed\u6cea\u76c8\u7736\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u5927\u516c\u4e3b\u5782\u601c\u8d50\u6551\uff0c\u5a62\u5b50\u53ea\u6015\u6628\u5929\u5c31\u6ca1\u6709\u547d\u4e86\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff0c\u201c\u522b\u96be\u8fc7\uff0c\u4f60\u73b0\u5728\u5df2\u7ecf\u79bb\u5f00\u706b\u5173\uff0c\u4ee5\u540e\u4e0d\u4f1a\u518d\u53d7\u59d4\u5c48\u4e86\uff01\u201d \u3000\u3000\u5979\u8bf4\u7740\uff0c\u7f13\u6b65\u8fdb\u5165\u5ba2\u5385\u5750\u4e0b\u3002 \u3000\u3000\u516c\u5b59\u7389\u3001\u5cb3\u5c0f\u98de\u3001\u6843\u82b1\u4e5f\u8ddf\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u5927\u516c\u4e3b\u9762\u8272\u51dd\u91cd\uff0c\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1b\u201c\u771f\u60f3\u4e0d\u5230\uff0e\u8fd9\u51e0\u5929\u63a5\u4e8c\u8fde\u4e09\u51fa\u4e86\u8fd9\u4e48\u591a\u4e8b\u60c5\uff0c \u8fde\u6211\u5fc3\u91cc\u4e5f\u591f\u70e6\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fde\u5fd9\u65bd\u4e86\u4e00\u793c\u9053\uff1b\u201c\u5168\u662f\u665a\u8f88\u4e0d\u597d\uff0c\u8fd9\u4e9b\u4e8b\uff0c\u53ef\u8bf4\u90fd\u662f\u665a\u8f88\u60f9\u51fa\u6765\u7684\uff0c\u665a\u8f88\u60c5\u613f \u9886\u53d7\u5e94\u5f97\u4e4b\u7f6a\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u6447\u6447\u5934\u9053\uff1a\u201c\u600e\u80fd\u6028\u4f60\uff0c\u6211\u662f\u60f3\u5230\u8be5\u5982\u4f55\u5584\u540e\u3002\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u76ee\u524d\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u4fbf\u662f\u6843\u82b1\u59d1\u5a18\u8be5\u5982\u4f55\u5b89\u7f6e\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u5b89\u7f6e\u6843\u82b1\uff0c\u975e\u5e38\u7b80\u5355\uff0c\u6211\u53ef\u4ee5\u628a\u5979\u5e26\u56de\u603b\u575b\uff0c\u6682\u65f6\u7559\u5728\u6211\u8eab\u8fb9\uff0c\u76ee\u524d\u8ba9\u6211 \u4e3a\u96be\u7684\uff0c\u662f\u8427\u9986\u4e3b\u548c\u82b1\u526f\u9986\u4e3b\u4e4b\u95f4\u7684\u4e8b\u3002\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u5927\u516c\u4e3b\u4e0d\u662f\u5df2\u4e3a\u4ed6\u4eec\u548c\u597d\u4e86\u5417\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u6447\u5934\u6005\u7136\u4e00\u7b11\u9053\uff1a\u201c\u4e8b\u60c5\u90a3\u6709\u90a3\u4e48\u7b80\u5355\u7684\uff0c\u8427\u9986\u4e3b\u662f\u4e00\u4e2a\u57ce\u5e9c\u5f88\u6df1\u7684\u4eba\uff0c\u8fd9\u4e8b\u5728 \u4ed6\u5fc3\u91cc\uff0c\u6c38\u8fdc\u662f\u4e2a\u82a5\u8482\uff0c\u82e5\u4ed6\u4eec\u4e24\u4e2a\u59cb\u7ec8\u8c8c\u53f0\u795e\u79bb\uff0c\u603b\u975e\u54b1\u4eec\u7687\u5143\u6559\u4e4b\u798f\u2026\u2026\u201d \u3000\u3000\u201c\u5927\u516c\u4e3b\u7684\u610f\u601d\u662f\u60f3\u628a\u8427\u9986\u4e3b\u8c03\u79bb\u62db\u8d24\u9986\uff1f\u201d \u3000\u3000\u201c\u6211\u90a3\u6709\u8fd9\u5927\u529b\u91cf\uff0c\u4f55\u51b5\u8427\u9986\u4e3b\u8ddf\u968f\u6559\u4e3b\u8fd9\u4e48\u591a\u5e74\uff0c\u548c\u6559\u4e3b\u7684\u5173\u7cfb\u975e\u5e38\u5bc6\u5207\uff0c\u6211\u867d\u662f\u6559 \u4e3b\u7684\u4eb2\u751f\u5973\u513f\uff0c\u4e5f\u4e0d\u80fd\u628a\u4ed6\u600e\u6837\uff0c\u8fd9\u4e9b\u5929\u6211\u4e24\u6b21\u4e0d\u7ed9\u4ed6\u9762\u5b50\uff0c\u5df2\u7ecf\u6709\u4e9b\u8fc7\u5206\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u5927\u516c\u4e3b\u610f\u601d\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u51c6\u5907\u8ba9\u82b1\u526f\u9986\u4e3b\u79bb\u5f00\u62db\u8d24\u9986\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u8fd9\u8bdd\uff0c\u4f7f\u5f97\u516c\u5b59\u7389\u548c\u5cb3\u5c0f\u98de\u90fd\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u5c24\u5176\u5cb3\u5c0f\u98de\uff0c\u82e5\u7236\u4eb2\u79bb\u5f00\u62db\u8d24\u9986\uff0c\u81ea\u5df1\u7b49\u4e8e\u5931\u53bb\u4f9d\u9760\u3002 \u3000\u3000\u516c\u5b59\u7389\u5927\u4e3a\u5173\u5207\u7684\u95ee\u9053\uff1a\u201c\u5927\u516c\u4e3b\u51c6\u5907\u628a\u82b1\u526f\u9986\u4e3b\u8c03\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u6211\u53ea\u662f\u6709\u8fd9\u79cd\u6253\u7b97\uff0c\u5f53\u7136\u5fc5\u987b\u7980\u62a5\u6559\u4e3b\uff0c\u7531\u6559\u4e3b\u6b63\u5f0f\u4e0b\u4ee4\u3002\u201d \u3000\u3000\u201c\u53ea\u8981\u5927\u516c\u4e3b\u5411\u6559\u4e3b\u5efa\u8bae\uff0c\u6559\u4e3b\u5341\u6709\u516b\u4e5d\u603b\u4f1a\u91c7\u7eb3\uff0c\u4f9d\u5351\u804c\u6765\u8bf4\uff0c\u5f53\u7136\u5e0c\u671b\u82b1\u526f\u9986\u4e3b\u80fd \u6b65\u6b65\u9ad8\u5347\u3002\u201d \u3000\u3000\u201c\u4e00\u5207\u6211\u81ea\u4f1a\u5c3d\u529b\u7684\uff0c\u603b\u4e4b\uff0c\u82b1\u526f\u9986\u4e3b\u5c06\u6765\u8c03\u52a8\u7684\u804c\u4f4d\uff0c\u51b3\u4e0d\u4f1a\u6bd4\u73b0\u5728\u4f4e\u5c31\u662f\u3002\u201d \u3000\u3000\u201c\u5351\u804c\u5e0c\u671b\u5927\u516c\u4e3b\u4e5f\u80fd\u5c3d\u65e9\u4e3a\u5cb3\u516c\u5b50\u5b89\u6392\u5de5\u4f5c\u3002\u201d \u3000\u3000\u201c\u6211\u4f1a\u7684\uff0c\u5cb3\u516c\u5b50\u662f\u4f4d\u5c11\u5e74\u82f1\u96c4\uff0c\u4eba\u624d\u4e0d\u53ef\u591a\u5f97\uff0c\u6211\u600e\u4f1a\u8ba9\u4ed6\u8001\u662f\u95f2\u7740\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u8bf4\u5b8c\u8bdd\uff0c\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u6843\u82b1\uff0c\u968f\u6211\u53bb\u5427\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u548c\u5cb3\u5c0f\u98de\u7acb\u5373\u8d77\u8eab\u76f8\u9001\u3002 \u3000\u3000\u51fa\u4e86\u5927\u95e8\uff0c\u5927\u516c\u4e3b\u505c\u6b65\u56de\u8eab\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u8fd9\u51e0\u5929\u8981\u8010\u5fc3\u7b49\u7740\uff0c\u5343\u4e07\u522b\u518d\u95f9\u4e8b\uff0c\u5bf9\u8427\u9986 \u4e3b\u4e5f\u7edd\u5bf9\u4e0d\u53ef\u5931\u793c\uff0c\u514d\u5f97\u6709\u4ec0\u4e48\u628a\u67c4\u843d\u5728\u4ed6\u624b\u91cc\uff0c\u8427\u9986\u4e3b\u5e76\u975e\u5bbd\u5b8f\u5927\u91cf\u7684\u4eba\uff0c\u82e5\u8fc7\u4e8e\u7ed9\u4ed6\u96be \u582a\uff0c\u4ed6\u76f4\u63a5\u7980\u62a5\u6559\u4e3b\uff0c\u5230\u90a3\u65f6\u53ea\u6015\u6211\u4e5f\u5e2e\u4e0d\u4e0a\u5fd9\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8eac\u8eab\u9053\uff1a\u201c\u665a\u8f88\u9075\u547d\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u8d70\u540e\uff0c\u5cb3\u5c0f\u98de\u5185\u5fc3\u5df2\u5341\u5206\u5e73\u9759\u3002 \u3000\u3000\u5c24\u5176\uff0c\u4eca\u5929\u4ed6\u53c8\u5f97\u89c1\u601d\u5ff5\u591a\u65e5\u7684\u8881\u5c0f\u9e64\uff0c\u66f4\u662f\u611f\u5230\u5feb\u6170\u5f02\u5e38\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u8981\u505a\u7684\uff0c\u53ea\u662f\u8010\u5fc3\u7684\u7b49\u5f85\u3002 \u3000\u3000\u5927\u7ea6\u4e09\u5929\u8fc7\u53bb\uff0c\u516c\u5b59\u7389\u6765\u4e86\uff0c\u4ed6\u4ecd\u662f\u5949\u5927\u516c\u4e3b\u4e4b\u547d\u800c\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u628a\u4ed6\u62db\u5f85\u5728\u5ba2\u5385\u91cc\u3002 \u3000\u3000\u516c\u5b59\u7389\u6ee1\u9762\u6625\u98ce\uff0c\u4e00\u526f\u559c\u6c14\u6d0b\u6d0b\u6a21\u6837\uff0c\u4e00\u770b\u4fbf\u77e5\u5fc5\u5b9a\u5e26\u6765\u597d\u6d88\u606f\u3002 \u3000\u3000\u679c\u7136\uff0c\u4ed6\u4e00\u5750\u4e0b\u4fbf\u8bf4\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u5148\u8be5\u606d\u559c\u4f60\uff0c\u606d\u559c\u4f60\u5df2\u7ecf\u6d3e\u5230\u4e86\u65b0\u804c\uff0c\u800c\u4e14\u4eca\u540e\u8fd8\u80fd \u548c\u82b1\u526f\u9986\u4e3b\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8feb\u4e0d\u53ca\u5f85\u7684\u95ee\u9053\uff1a\u201c\u82b1\u526f\u9986\u4e3b\u771f\u7684\u8c03\u79bb\u4e86\u62db\u8d24\u9986\uff1f\u665a\u8f88\u53c8\u6d3e\u4e86\u4ec0\u4e48\u65b0\u804c\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u4e3a\u4e86\u82b1\u526f\u9986\u4e3b\u7684\u51fa\u8def\uff0c\u5927\u516c\u4e3b\u771f\u662f\u7528\u5fc3\u826f\u82e6\u3002\u5979\u7adf\u5efa\u8bae\u6559\u4e3b\uff0c\u65b0\u6210\u7acb\u4e00\u5904 \u673a\u6784\uff0c\u800c\u6559\u4e3b\u4e5f\u5c45\u7136\u91c7\u7eb3\u4e86\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u65b0\u673a\u6784\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u5728\u4e94\u5173\u4e4b\u4e0a\uff0c\u53c8\u6210\u7acb\u4e86\u4e00\u4e2a\u673a\u6784\uff0c\u4ece\u524d\u7684\u4e94\u5173\uff0c\u662f\u76f4\u5c5e\u603b\u575b\u7684\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u76f4 \u63a5\u5411\u603b\u575b\u53cd\u6620\uff0c\u5927\u516c\u4e3b\u5efa\u8bae\u6559\u4e3b\u65b0\u6210\u7acb\u7684\u673a\u6784\uff0c\u5b9a\u540d\u4e3a\u4e94\u5173\u603b\u9547\uff0c\u987e\u540d\u601d\u8bae\uff0c\u662f\u8d1f\u8d23\u7ba1\u8f96\u4e94 \u5173\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u60ca\u559c\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u82b1\u526f\u9986\u4e3b\u662f\u6d3e\u4efb\u4e94\u5173\u603b\u9547\uff0c\u90a3\u8be5\u662f\u5927\u5927\u9ad8\u5347\u4e86\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u82b1\u526f\u9986\u4e3b\u88ab\u4efb\u547d\u4e3a\u4e94\u5173\u526f\u603b\u9547\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e94\u5173\u603b\u9547\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u662f\u5927\u516c\u4e3b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u5927\u516c\u4e3b\u4e5f\u88ab\u6d3e\u4e86\u804c\uff1f\u201d \u3000\u3000\u201c\u5176\u5b9e\u5927\u516c\u4e3b\u53ea\u662f\u6302\u4e00\u4e2a\u8854\u800c\u5df2\uff0c\u5b9e\u9645\u4e0a\u526f\u603b\u9547\u5c31\u662f\u603b\u9547\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5916\u662f\u56e0\u4e3a\u82b1\u526f\u9986\u4e3b\u8fdb\u5165\u672c\u6559\u8d44\u5386\u8f83\u6d45\uff0c\u800c\u4e94\u5173\u603b\u9547\u53c8\u6743\u9ad8\u4f4d\u91cd\uff0c\u82e5\u4e00\u4e0b\u5b50\u628a\u4ed6\u59d4\u4e3a \u4e94\u5173\u603b\u9547\uff0c\u96be\u514d\u4f1a\u6709\u4eba\u5ac9\u5992\uff0c\u5176\u4e2d\u8427\u8427\u9986\u4e3b\u4fbf\u4e00\u5b9a\u4f1a\u6709\u8fd9\u79cd\u60f3\u6cd5\uff0c\u56e0\u4e4b\uff0c\u6559\u4e3b\u548c\u5927\u516c\u4e3b\u51e0\u7ecf \u659f\u914c\u4e4b\u540e\uff0c\u624d\u51b3\u5b9a\u7531\u5927\u516c\u4e3b\u6302\u540d\u603b\u9547\uff0c\u5b9e\u9645\u8d1f\u8d23\u7684\u4eba\u5219\u662f\u526f\u603b\u9547\uff0c\u8fd9\u6837\u5c31\u4e0d\u53ef\u80fd\u6709\u4eba\u8bb2\u95f2\u8bdd \u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u81ea\u662f\u559c\u4e4b\u4e0d\u5c3d\uff0c\u8fde\u5fd9\u518d\u95ee\u9053\uff1a\u201c\u665a\u8f88\u53c8\u5206\u6d3e\u4e86\u4ec0\u4e48\u5de5\u4f5c\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u7684\u65b0\u804c\uff0c\u4e5f\u662f\u5927\u516c\u4e3b\u5411\u6559\u4e3b\u4fdd\u8bc1\u7684\uff0c\u628a\u4f60\u62e8\u5728\u82b1\u526f\u603b\u9547\u9ebe\u4e0b\uff0c\u62c5\u4efb \u5de1\u5173\u4f7f\u8005\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u66f4\u662f\u559c\u51fa\u671b\u5916\uff0c\u80fd\u968f\u4f8d\u7236\u4eb2\u8eab\u65c1\uff0c\u6b63\u662f\u6210\u5c31\u4e86\u4ed6\u4e00\u5927\u5fc3\u613f\uff0c\u540c\u65f6\uff0c\u4ed6\u5bf9\u5927\u516c\u4e3b\u4e5f \u8d8a\u53d1\u611f\u6fc0\u3002 \u3000\u3000\u516c\u5b59\u7389\u559d\u4e86\u53e3\u8336\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u8bf7\u5b89\u5fc3\u7b49\u5f85\uff0c\u5c31\u5728\u8fd9\u51e0\u5929\uff0c\u4fbf\u53ef\u8d70\u9a6c\u4e0a\u4efb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u516c\u5b59\u5148\u751f\u7684\u804c\u4f4d\uff0c\u662f\u5426\u4e5f\u6709\u6240\u8c03\u52a8\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u9762\u6cdb\u6005\u7136\u4e4b\u8272\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u8fd8\u662f\u8001\u6837\u5b50\uff0c\u4ecd\u7136\u8981\u5728\u62db\u8d24\u9986\u5446\u4e0b\u53bb\uff0c\u53ea\u6015\u4eca\u540e \u7684\u65e5\u5b50\uff0c\u8fd8\u5b9e\u5728\u4e0d\u592a\u597d\u8fc7\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u5e26\u4f60\u5230\u706b\u5173\u6551\u51fa\u6843\u82b1\u7684\u4e8b\uff0c\u65e9\u5df2\u5f15\u8d77\u8427\u9986\u4e3b\u7684\u4e0d\u5feb\uff0c\u4f60\u60f3\u5f7c\u6b64\u95f4\u4eca\u540e\u7684\u76f8\u5904\uff0c\u8fd8\u80fd \u6109\u5feb\u4e48\uff1f\u597d\u5728\u81ea\u6709\u5927\u516c\u4e3b\u5728\uff0c\u4ed6\u8fd8\u4e0d\u6562\u628a\u6211\u600e\u6837\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u603b\u7ba1\u97e9\u5fb7\u8d77\u5ffd\u7136\u95ef\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u97e9\u5fb7\u8d77\u4e0d\u901f\u800c\u6765\uff0c\u516c\u5b59\u7389\u548c\u5cb3\u5c0f\u98de\u90fd\u96be\u514d\u611f\u5230\u610f\u5916\u3002 \u3000\u3000\u97e9\u5fb7\u8d77\u671b\u7740\u516c\u5b59\u7389\u62f1\u62f1\u624b\u9053\uff1a\u201c\u539f\u6765\u516c\u5b59\u5148\u751f\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u97e9\u603b\u7ba1\u53ef\u662f\u6709\u4e8b\u627e\u6211\uff1f\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u9053\uff1a\u201c\u97e9\u67d0\u662f\u5949\u9986\u4e3b\u4e4b\u547d\uff0c\u6765\u901a\u77e5\u5cb3\u516c\u5b50\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u97e9\u603b\u7ba1\u901a\u77e5\u5728\u4e0b\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u97e9\u5fb7\u8d77\u9053\uff1a\u201c\u9986\u4e3b\u8bf7\u5cb3\u516c\u5b50\u508d\u665a\u5230\u603b\u9986\u4e00\u8d9f\uff0c\u81f3\u4e8e\u4ec0\u4e48\u4e8b\uff0c\u97e9\u67d0\u4e5f\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u62f1\u4e86\u62f1\u624b\uff0c\u5e76\u672a\u505c\u7559\uff0c\u968f\u5373\u544a\u8f9e\u800c\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u89c9\u987f\u611f\u7530\u60d1\uff0c\u671b\u7740\u516c\u5b59\u7389\u9053\uff1a\u201c\u516c\u5b59\u5148\u751f\uff0c\u60a8\u53ef\u60f3\u5f97\u51fa\u8427\u9986\u4e3b\u662f\u8981\u665a\u8f88\u53bb\u505a\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u4e0d\u7ba1\u4e3a\u4ec0\u4e48\uff0c\u53bb\u8fd8\u662f\u8981\u53bb\uff0c\u4f60\u73b0\u5728\u5df2\u6709\u4e86\u65b0\u804c\uff0c\u8c05\u4ed6\u4e5f\u4e0d\u4f1a\u628a\u4f60 \u600e\u6837\uff0c\u540c\u65f6\u4e5f\u5343\u4e07\u522b\u518d\u5f97\u7f6a\u4ed6\uff0c\u505a\u4eba\u5c31\u8be5\u5982\u6b64\uff0c\u5f7c\u6b64\u597d\u805a\u597d\u6563\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u665a\u8f88\u548c\u4ed6\u5df2\u7ecf\u7b97\u4e0d\u5f97\u597d\u805a\u4e86\u3002\u201d \u3000\u3000\u201c\u867d\u7136\u5982\u6b64\uff0c\u6700\u540e\u5206\u624b\u65f6\uff0c\u8fd8\u662f\u8be5\u5c3d\u91cf\u4fdd\u6301\u98ce\u5ea6\uff0c\u8981\u77e5\u9053\u4ed6\u603b\u662f\u6559\u4e3b\u7684\u4eb2\u4fe1\uff0c\u80fd\u4e0d\u5f97\u7f6a \u8fd8\u662f\u4e0d\u5f97\u7f6a\u7684\u597d\u3002\u201d \u3000\u3000\u508d\u665a\uff0c\u6797\u91d1\u5b9d\u5230\u603b\u9986\u62ff\u996d\uff0c\u5cb3\u5c0f\u98de\u548c\u4ed6\u4e00\u540c\u524d\u5f80\u3002 \u3000\u3000\u6797\u91d1\u5b9d\u95ee\u9053\uff1a\u201c\u516c\u5b50\u53c8\u53bb\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u4e0a\u5348\u97e9\u603b\u7ba1\u901a\u77e5\u6211\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u516c\u5b50\u7684\u996d\u8981\u4e0d\u8981\u62ff\u4e86\uff1f\u201d \u3000\u3000\u201c\u7167\u62ff\uff0c\u5982\u679c\u6211\u4e0d\u5403\uff0c\u4f60\u53ef\u4ee5\u5403\u6211\u90a3\u4e00\u4efd\u3002\u201d \u3000\u3000\u6797\u91d1\u5b9d\u4e0d\u6562\u518d\u591a\u95ee\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4ed6\u4f1a\u60f3\u5230\u5cb3\u5c0f\u98de\u53c8\u8981\u53bb\u95f9\u4e8b\u3002 \u3000\u3000\u5728\u6797\u91d1\u5b9d\u8bb0\u5fc6\u4e2d\uff0c\u81ea\u4ece\u62db\u8d24\u9986\u6210\u7acb\u4ee5\u540e\uff0c\u95ef\u8fc7\u4e94\u5173\u7684\uff0c\u5cb3\u5c0f\u98de\u4f3c\u662f\u5e74\u7eaa\u6700\u5c0f\u7684\u4e00\u4e2a\uff0c\u800c \u95f9\u4e8b\u6700\u591a\u7684\uff0c\u4f3c\u4e4e\u4e5f\u662f\u4ed6\u3002 \u3000\u3000\u504f\u504f\u4ed6\u662f\u4f4d\u798f\u661f\uff0c\u6bcf\u6b21\u95f9\u4e8b\u6700\u540e\u90fd\u6ca1\u4e8b\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u6797\u91d1\u5b9d\u73b0\u5728\u5df2\u4e0d\u62c5\u5fc3\u5cb3\u5c0f\u98de\u95f9\u4e8b\u3002 \u3000\u3000\u6765\u5230\u603b\u9986\u5927\u95e8\u5916\uff0c\u6797\u91d1\u5b9d\u81ea\u5f80\u540e\u95e8\u800c\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5728\u8fdb\u5165\u5927\u95e8\u65f6\uff0c\u610f\u5916\u7684\u4e0d\u4f46\u6ca1\u4eba\u67e5\u95ee\uff0c\u5b88\u95e8\u7684\u53cd\u800c\u5bf9\u4ed6\u793c\u4e0a\u6709\u52a0\u3002 \u3000\u3000\u521a\u8d70\u5230\u5047\u5c71\u6c34\u69ad\u65c1\uff0c\u97e9\u603b\u7ba1\u4fbf\u7b11\u8138\u8fce\u4eba\u7684\u8fc7\u6765\u8fce\u63a5\u3002 \u3000\u3000\u8fdb\u5165\u5927\u5385\uff0c\u53ea\u89c1\u8881\u5c0f\u9e64\u3001\u4e01\u6d9b\u3001\u7518\u9716\u5df2\u5148\u5728\u90a3\u91cc\uff0c\u4e09\u4eba\u559d\u7740\u8336\u6709\u8bf4\u6709\u7b11\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u89c1\u5cb3\u5c0f\u98de\u5230\u6765\uff0e\u8fde\u5fd9\u8d77\u8eab\u76f8\u8fce\uff0c\u5f7c\u6b64\u771f\u6709\u8bf4\u4e0d\u51fa\u7684\u4eb2\u5207\u3002 \u3000\u3000\u7ecf\u8fc7\u8be2\u95ee\u4e4b\u4e0b\uff0c\u624d\u77e5\u9053\u8881\u5c0f\u9e64\u7b49\u4e09\u4eba\u4e0d\u4f46\u4e5f\u6d3e\u4e86\u65b0\u804c\uff0c\u800c\u4e14\u4e5f\u90fd\u88ab\u5206\u914d\u5728\u4e94\u5173\u603b\u9547\u516c\u8859\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u4e00\u6837\uff0c\u4e5f\u662f\u5de1\u5173\u4f7f\u8005\u3002 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u5219\u662f\u526f\u4f7f\u8005\u3002 \u3000\u3000\u539f\u6765\u4eca\u665a\u662f\u62db\u8d24\u9986\u4e3b\u8427\u6e58\u8bbe\u4e0b\u76db\u7b75\u4e3a\u82b1\u7389\u9e9f\u996f\u884c\uff0c\u987a\u4fbf\u4e5f\u628a\u5cb3\u5c0f\u98de\u7b49\u4e24\u4f4d\u4f7f\u8005\u4e24\u4f4d\u526f\u4f7f \u8005\u9080\u6765\u53c2\u52a0\u3002 \u3000\u3000\u7531\u6b64\u53ef\u89c1\u8427\u6e58\u7684\u786e\u4e0d\u662f\u4f4d\u7b80\u5355\u4eba\u7269\uff0c\u81f3\u5c11\u8868\u9762\u5de5\u4f5c\u505a\u5f97\u6ca1\u6709\u8bdd\u8bb2\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u513f\uff0c\u8427\u6e58\uff0c\u82b1\u7389\u9e9f\u3001\u516c\u5b59\u7389\u4e09\u4eba\u4fbf\u4e00\u540c\u8fdb\u5165\u5927\u5385\u3002 \u3000\u3000\u8427\u6e58\u6ee1\u9762\u5b58\u98ce\uff0c\u8c01\u4e5f\u770b\u4e0d\u51fa\u4ed6\u4e0d\u4e45\u524d\u8fd8\u548c\u82b1\u7389\u9e9f\u53cd\u76ee\u76f8\u5411\u8fc7\u3002 \u3000\u3000\u4ed6\u672c\u6765\u8c26\u8ba9\u82b1\u7389\u9e9f\u4e0a\u5ea7\uff0c\u4f46\u82b1\u7389\u9e9f\u4e0d\u80af\uff0c\u6700\u540e\u8fd8\u662f\u7531\u4ed6\u5750\u4e86\u4e0a\u4f4d\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u548c\u516c\u5b59\u7389\u5206\u5750\u4e24\u65c1\uff0c\u5cb3\u5c0f\u98de\u7b49\u56db\u4eba\u4e5f\u5404\u4f9d\u6b21\u800c\u5750\uff0c\u603b\u7ba1\u97e9\u5fb7\u8d77\u6700\u540e\u8fdb\u6765\u5750\u5728\u672b\u4f4d \u76f8\u966a\u3002 \u3000\u3000\u9152\u83dc\u5f88\u5feb\u4fbf\u6446\u4e86\u4e0a\u6765\uff0c\u5f53\u771f\u662f\u5c71\u73cd\u6d77\u5473\uff0c\u7f8e\u4e0d\u80dc\u6536\uff0c\u4e01\u6d9b\u548c\u7518\u9716\u770b\u4e86\u4fbf\u6d8e\u6c34\u76f4\u6d41\u3002 \u3000\u3000\u53ea\u89c1\u8427\u6e58\u4e3e\u676f\u9053\uff1a\u201c\u81ea\u53e4\u5927\u624d\u5fc5\u6709\u5927\u7528\uff0c\u526f\u9986\u4e3b\u82b1\u7389\u9e9f\u5144\u6b64\u6b21\u8363\u8c03\u4e94\u5173\u526f\u603b\u9547\uff0c\u53ef\u89c1\u6559 \u4e3b\u6167\u773c\u8bc6\u4eba\uff0c\u4e5f\u53ef\u8bf4\u662f\u672c\u9986\u7684\u5149\u8363\uff0c\u8427\u67d0\u8c28\u5907\u6c34\u9152\uff0c\u7b97\u4e0d\u5f97\u4e3a\u7389\u9e9f\u5144\u6d45\u884c\uff0c\u53ea\u662f\u804a\u8868\u5fc3\u610f\u800c \u5df2\uff0c\u8fd8\u671b\u7389\u9e9f\u5144\u4eca\u540e\u5bf9\u8427\u67d0\u4e0d\u65f6\u6307\u6559\uff0c\u5219\u8427\u67d0\u5e78\u751a\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u4ef0\u8d77\u8116\u5b50\uff0c\u4e00\u676f\u9152\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u7acb\u5373\u4e3e\u676f\u56de\u656c\uff0c\u5f53\u7136\u4e5f\u8bf4\u4e86\u4e00\u756a\u5ba2\u5957\u8bdd\u3002 \u3000\u3000\u8427\u6e58\u518d\u4e3e\u676f\u5411\u5cb3\u5c0f\u98de\u7b49\u56db\u4eba\u9053\uff1a\u201c\u56db\u4f4d\u8001\u5f1f\u8fd9\u6837\u5feb\u5c31\u5206\u6d3e\u4e0b\u65b0\u804c\uff0c\u800c\u4e14\u5f97\u80fd\u62e8\u5728\u7389\u9e9f\u5144 \u9ebe\u4e0b\uff0c\u8427\u67d0\u5b9e\u5728\u4e3a\u56db\u4f4d\u9ad8\u5174\uff0c\u4ee5\u56db\u4f4d\u7684\u6587\u624d\u6b66\u529f\uff0c\u5c06\u6765\u4e0d\u96be\u51fa\u4eba\u5934\u5730\uff0c\u524d\u9014\u4e0d\u53ef\u9650\u91cf\uff01\u201d \u3000\u3000\u53c8\u662f\u4e3e\u676f\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7b49\u56db\u4eba\u56e0\u5fc3\u91cc\u9ad8\u5174\uff0c\u66f4\u662f\u793c\u8c8c\u5468\u5230\uff0c\u9f50\u9f50\u8d77\u8eab\u56de\u656c\u3002 \u3000\u3000\u8fd9\u4e00\u987f\u9152\u53ef\u8c13\u5c3d\u6b22\u800c\u6563\uff0c\u81f3\u4e8e\u5fc3\u91cc\u662f\u5426\u5404\u6000\u9b3c\u80ce\uff0c\u90a3\u5c31\u53ea\u6709\u4ed6\u4eec\u81ea\u5df1\u5fc3\u91cc\u6709\u6570\u4e86\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6563\u5e2d\u540e\u56de\u5230\u5206\u9986\uff0c\u5df2\u662f\u8d77\u66f4\u65f6\u5206\uff0c\u60f3\u4e0d\u5230\u6797\u91d1\u5b9d\u5728\u90a3\u91cc\u5b88\u7ad9\u98df\u76d2\u4e0d\u6562\u52a8\u3002 \u3000\u3000\u8fd9\u4f7f\u5cb3\u5c0f\u98de\u5927\u611f\u6b49\u610f\uff0c\u5fd9\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u5403\u996d\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u516c\u5b50\u6ca1\u56de\u6765\uff0c\u5c0f\u7684\u600e\u6562\u5148\u5403\u3002\u201d \u3000\u3000\u201c\u6211\u5df2\u7ecf\u5728\u603b\u9986\u5403\u8fc7\u4e86\uff0c\u4f60\u81ea\u5df1\u5feb\u5403\u3002\u201d \u3000\u3000\u6797\u91d1\u5b9d\u8fd9\u624d\u6253\u5f00\u98df\u76d2\u5403\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u591c\uff0e\u5cb3\u5c0f\u98de\u7761\u4e86\u4e00\u4e2a\u4ece\u672a\u6709\u8fc7\u7684\u597d\u89c9\uff0c\u56e0\u4e3a\u4ece\u4eca\u540e\u4ed6\u4e0d\u4f46\u53ef\u4ee5\u968f\u4f8d\u7236\u4eb2\u5de6\u53f3\uff0c\u66f4\u80fd \u548c\u8881\u5c0f\u9e64\u7b49\u4eba\u91cd\u65b0\u805a\u5408\uff0c\u8fd9\u662f\u4e00\u4ef6\u591a\u4e48\u5feb\u6170\u7684\u4e8b\u3002 \u3000\u3000\u6b21\u65e5\uff0c\u4ed6\u5148\u5230\u603b\u9986\u548c\u8881\u5c0f\u9e64\u7b49\u4e09\u4eba\u4f1a\u5408\uff0c\u7136\u540e\u968f\u540c\u82b1\u7389\u9e9f\u4e00\u540c\u524d\u5f80\u65b0\u6210\u7acb\u7684\u4e94\u5173\u603b\u9547\u6240 \u5728\u5730\u3002 \u3000\u3000\u8427\u6e58\u4eb2\u7387\u516c\u5b59\u7389\u4ee5\u53ca\u6b63\u526f\u603b\u7ba1\u7b49\u4eba\u76f4\u9001\u51fa\u5927\u95e8\u5916\uff0c\u624d\u6bb7\u6bb7\u8bdd\u522b\u3002 \u3000\u3000\u4e94\u5173\u603b\u9547\u7684\u8859\u7f72\u8bbe\u5728\u5bb5\u5173\u5883\u5185\uff0c\u662f\u4e00\u5904\u5e9f\u56ed\uff0c\u6b63\u5728\u5927\u4e8b\u6574\u4fee\uff0c \u3000\u3000\u53c8\u56e0\u5fc5\u987b\u7ecf\u8fc7\u603b\u575b\u5f81\u8c03\u4eba\u624b\uff0c\u771f\u5fd9\u4e86\u5341\u591a\u5929\uff0c\u624d\u6e10\u6e10\u5b89\u7f6e\u5c31\u7eea\u3002 \u3000\u3000\u5728\u8fd9\u5341\u591a\u5929\u91cc\uff0c\u5cb3\u5c0f\u98de\u7b49\u4eba\u4e5f\u8ddf\u7740\u5fd9\uff0c\u8fde\u5230\u5bb5\u5173\u8857\u4e0a\u8d70\u8d70\u7684\u65f6\u95f4\u90fd\u62bd\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u76f4\u60f3\u627e\u673a\u4f1a\u5355\u72ec\u548c\u7236\u4eb2\u89c1\u89c1\uff0c\u56e0\u4e3a\u4ed6\u6709\u5f88\u591a\u8feb\u5207\u60f3\u77e5\u9053\u7684\u4e8b\uff0c\u5fc5\u987b\u5411\u7236\u4eb2\u95ee\u660e \u767d\uff0c\u53e6\u5916\u4e5f\u6709\u5f88\u591a\u8bdd\u8981\u544a\u8bc9\u7236\u4eb2\u3002 \u3000\u3000\u597d\u4e0d\u5bb9\u6613\u5728\u4e00\u4e2a\u591c\u665a\uff0c\u4ed6\u7ec8\u4e8e\u6709\u673a\u4f1a\u5355\u72ec\u8fdb\u5165\u4e86\u82b1\u7389\u9e9f\u623f\u95f4\u3002 \u3000\u3000\u8bf7\u8fc7\u5b89\u540e\uff0c\u4ed6\u8feb\u4e0d\u53ca\u5f85\u7684\u95ee\u9053\uff1a\u201c\u7236\u4eb2\uff0c\u5a18\u5230\u5e95\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u5b69\u513f\u4e3a\u4ec0\u4e48\u4e00\u76f4\u89c1\u4e0d\u5230 \u5979\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u987f\u65f6\u795e\u8272\u9eef\u7136\uff0c\u773c\u7736\u5185\u4e5f\u9aa4\u89c1\u6da6\u6e7f\uff0c\u51c4\u7136\u53f9\u4e86\u53e3\u6c14\u9053\uff1a \u3000\u3000\u201c\u4e8b\u5230\u5982\u4eca\uff0c\u6211\u4e5f\u4e0d\u80fd\u518d\u7792\u4f60\u4e86\uff0c\u4f60\u5a18\u5728\u4e09\u5e74\u524d\u4fbf\u88ab\u6253\u8fdb\u4e86\u80b2\u5316\u57ce\uff0c\u4e3a\u7236\u81f3\u4eca\u6ca1\u89c1\u7740\u5979 \u7684\u9762\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5982\u95fb\u6674\u5929\u9739\u5389\uff0c\u554a\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u8bf7\u7236\u4eb2\u5feb\u628a\u539f\u56e0\u544a\u8bc9\u5b69\u513f\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u53c8\u957f\u957f\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u56e0\u4e3a\u5979\u5f97\u7f6a\u4e86\u5bb5\u5173\u7edf\u9886\u51e4\u5ae3\u7ea2\uff0c\u51e4\u5ae3\u7ea2\u5728\u6559\u4e3b\u9762\u524d\u8fdb\u4e86\u8c17 \u8a00\uff0c\u6559\u4e3b\u4e00\u6012\u4e4b\u4e0b\uff0c\u5c31\u628a\u4f60\u5a18\u6253\u5165\u80b2\u5316\u57ce\u3002\u201d \u3000\u3000\u201c\u80b2\u5316\u57ce\u7a76\u7adf\u662f\u4e00\u4e2a\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u662f\u4e00\u5904\u4eba\u95f4\u5730\u72f1\uff0c\u51e1\u662f\u5bf9\u6559\u4e3b\u4e0d\u5fe0\u6216\u662f\u72af\u4e86\u7687\u5143\u6559\u6212\u5f8b\u7684\uff0c\u9664\u4e86\u5f53\u573a\u5904\u6b7b\u5916\uff0c\u4fbf\u662f\u88ab \u6253\u5165\u80b2\u5316\u57ce\u3002\u201d \u3000\u3000\u201c\u80b2\u5316\u4e8c\u5b57\u505a\u5982\u4f55\u89e3\u91ca\u5462\uff1f\u201d \u3000\u3000\u201c\u80b2\u5316\u57ce\u539f\u5148\u672c\u6765\u53eb\u505a\u5730\u72f1\u57ce\uff0c\u540e\u6765\u6539\u540d\u611f\u8bad\u57ce\uff0c\u4f46\u6709\u4eba\u5efa\u8bae\u8fd9\u540d\u79f0\u4e0d\u597d\u542c\uff0c\u6559\u4e3b\u4fbf\u53c8 \u628a\u5b83\u6539\u540d\u80b2\u5316\u57ce\uff0c\u5c31\u662f\u6559\u80b2\u611f\u5316\u4e4b\u610f\uff0c\u4f46\u8fdb\u5165\u80b2\u5316\u57ce\u7684\u4eba\uff0c\u5b9e\u9645\u5c31\u662f\u5728\u670d\u82e6\u5211\uff0c\u800c\u4e14\u82e6\u4e0d\u582a \u8a00\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u542c\u5230\u8fd9\u91cc\uff0c\u6cea\u6c34\u4e0d\u89c9\u593a\u7736\u800c\u51fa\uff0c\u545c\u54bd\u8bf4\u9053\uff1a\u201c\u7236\u4eb2\u65e2\u7136\u53d7\u5230\u6559\u4e3b\u7684\u91cd\u7528\uff0c\u53c8\u8499\u5927 \u516c\u4e3b\u53e6\u773c\u770b\u5f85\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u628a\u5a18\u6551\u51fa\u6765\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u6447\u5934\u9053\uff1a\u201c\u5e76\u975e\u6211\u4e0d\u60f3\u628a\u4f60\u5a18\u6551\u51fa\uff0c\u800c\u662f\u6211\u6ca1\u6709\u7406\u7531\u5411\u6559\u4e3b\u548c\u5728\u516c\u4e3b\u8fdb\u8a00\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6115\u7136\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u82b1\u4e94\u9e9f\u9053\uff1a\u201c\u56e0\u4e3a\u6559\u4e3b\u548c\u5927\u516c\u4e3b\u5e76\u4e0d\u6e05\u695a\u6211\u548c\u4f60\u5a18\u662f\u592b\u59bb\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u4f3c\u6709\u65e0\u5c3d\u6127\u610f\uff1a\u201c\u56e0\u4e3a\u6211\u548c\u4f60\u5a18\u4e09\u5e74\u524d\u8fdb\u8c37\u65f6\uff0c\u4fbf\u542c\u8bf4\u6559\u4e3b\u4e0d\u559c\u6b22\u592b\u5987\u540c\u6765\uff0c\u6240 \u4ee5\u540e\u6765\u6211\u548c\u4f60\u5a18\u53ea\u597d\u9a97\u8bf4\u5f7c\u6b64\u5e76\u975e\u592b\u5987\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u5b69\u513f\u6597\u80c6\uff0c\u6709\u4ef6\u4e8b\u8fd9\u4e9b\u5929\u4e00\u76f4\u4e8e\u5fc3\u96be\u5b89\uff0c\u5728\u7236\u4eb2\u9762\u524d\uff0c\u4f3c\u4e4e\u4e0d\u5410 \u4e0d\u5feb\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u4e24\u773c\u7d27\u76ef\u7740\u5cb3\u5c0f\u98de\u8138\u8272\u9053\uff1b\u201c\u7a76\u7adf\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u987f\u4e86\u987f\u9053\uff1a\u201c\u542c\u516c\u5b59\u5148\u751f\u8bf4\uff0c\u5927\u516c\u4e3b\u6709\u610f\u5bf9\u7236\u4eb2\u59d4\u4ee5\u7ec8\u8eab\uff0c\u82e5\u771f\u662f\u8fd9\u6837\uff0c\u4e0d\u77e5\u7236 \u4eb2\u5bf9\u5a18\u5982\u4f55\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u4e0d\u89c9\u4f4e\u4e0b\u5934\u53bb\uff0c\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4e3a\u4e86\u628a\u4f60\u5a18\u6551\u51fa\uff0c\u6211\u73b0\u5728\u4e0d\u5f97\u4e0d\u5047\u610f\u5e94\u4ed8\u5927\u516c\u4e3b\uff0c \u82e5\u6211\u4e00\u65e6\u8bf4\u51fa\u81ea\u5df1\u5df2\u6709\u59bb\u5ba4\uff0c\u5728\u5931\u53bb\u5927\u516c\u4e3b\u7684\u52a9\u529b\u4e4b\u540e\uff0c\u53ea\u6015\u53cd\u800c\u6551\u4e0d\u51fa\u4f60\u5a18\u4e86\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u4e07\u4e00\u7236\u4eb2\u771f\u548c\u5927\u516c\u4e3b\u7ed3\u6210\u4e86\u592b\u5987\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u81ea\u4f1a\u5c3d\u529b\u8bbe\u6cd5\u62d6\u5ef6\uff0c\u81f3\u5c11\u5728\u77ed\u671f\u5185\u8fd8\u4e0d\u4f1a\u6210\u771f\u3002\u201d \u3000\u3000\u201c\u5b69\u513f\u53e6\u6709\u4e00\u4e8b\u4e0d\u660e\uff0c\u7236\u4eb2\u548c\u5a18\u4e09\u5e74\u524d\u4e3a\u4ec0\u4e48\u8981\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u7a81\u7136\u8eab\u5b50\u4e00\u98a4\uff0c\u795e\u8272\u4e5f\u663e\u51fa\u6709\u5f02\uff0c\u534a\u664c\uff0c\u624d\u4e00\u5b57\u4e00\u53e5\u7684\u9053\uff1b\u201c\u56e0\u4e3a\u6211\u548c\u4f60\u5a18\u8981\u6551 \u51fa\u53e6\u5916\u4e00\u4e2a\u88ab\u6253\u5165\u80b2\u5316\u57ce\u7684\u4eba\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u4eba\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u795e\u8272\u53c8\u663e\u8083\u7a46\uff0c\u50cf\u662f\u8d39\u5c3d\u6781\u5927\u7684\u6c14\u529b\uff0c\u624d\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u6709\u5927\u6069\u4e8e\u6211\u548c\u4f60\u5a18\u7684\u4eba\uff0c \u6ca1\u6709\u8fd9\u4eba\uff0c\u6211\u548c\u4f60\u5a18\u65e9\u5df2\u4e0d\u4f1a\u6d3b\u5728\u4e16\u4e0a\uff0c\u6211\u548c\u4f60\u5a18\uff0c\u7eb5\u7136\u4e0a\u5200\u5c71\u4e0b\u5251\u6797\uff0c\u4e5f\u5fc5\u987b\u628a\u8fd9\u4eba\u6551\u4e86 \u51fa\u6765\uff01\u201d \u3000\u3000\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u540c\u65f6\u8fd9\u4eba\u548c\u4f60\u4e5f\u6709\u5207\u8eab\u5173\u7cfb\uff0c\u6ca1\u6709\u4ed6\uff0c\u7167\u6837\u4e5f\u4e0d\u4f1a\u6709\u4f60\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u542c\u5f97\u6709\u4e9b\u60ca\u5fc3\u52a8\u9b44\uff0c\u5374\u53c8\u832b\u7136\u4e0d\u77e5\u5176\u89e3\uff0c\u4ed6\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u7236\u4eb2\uff0c\u8fd9\u4eba\u5230\u5e95 \u662f\u8c01\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u51c4\u7136\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u591a\u95ee\uff0c\u7b49\u65f6\u673a\u4e00\u5230\uff0c\u6211\u81ea\u4f1a\u5bf9\u4f60\u8bf4\u660e\uff0c\u82e5\u73b0\u5728\u544a\u8bc9\u4f60\uff0e\u5bf9\u4f60 \u5bf9\u6211\u4ee5\u53ca\u4f60\u5a18\u90fd\u5927\u5927\u4e0d\u5229\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5f3a\u5fcd\u7740\u5185\u5fc3\u7684\u56f0\u60d1\uff0c\u8bb8\u4e45\u624d\u9053\uff1a\u201c\u7236\u4eb2\uff0c\u65e2\u7136\u662f\u5bb5\u5173\u7edf\u9886\u51e4\u5ae3\u7ea2\u9677\u5bb3\u4e86\u6bcd\u4eb2\uff0c\u73b0 \u5728\u60a8\u8001\u4eba\u5bb6\u8eab\u4e3a\u4e94\u5173\u603b\u9547\uff0c\u6b63\u53ef\u4ee5\u7ba1\u8f96\u5230\u5979\uff0c\u5c31\u8be5\u8bbe\u6cd5\u4e3a\u5a18\u62a5\u4ec7\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9eef\u7136\u6447\u5934\u9053\uff1a\u201c\u4e3a\u7236\u73b0\u5728\u867d\u80fd\u7ba1\u8f96\u5230\u5979\uff0c\u4f46\u5374\u65e0\u6cd5\u52a8\u5f97\u4e86\u5979\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u6123\u9053\uff1a\u201c\u8fd9\u53c8\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u56e0\u4e3a\u5979\u662f\u6559\u4e3b\u7684\u5fc3\u8179\uff0c\u6559\u4e3b\u5bf9\u5979\u5ba0\u4fe1\u6709\u52a0\uff0c\u6211\u82e5\u60f3\u6574\u5979\uff0c\u8bf4\u4e0d\u5b9a\u53cd\u800c\u8981\u5403 \u5979\u7684\u4e8f\u3002\u201d \u3000\u3000\u201c\u6559\u4e3b\u4e3a\u4ec0\u4e48\u4f1a\u5bf9\u5979\u90a3\u6837\u5ba0\u4fe1\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5979\u66fe\u4e3a\u6559\u4e3b\u7acb\u4e0b\u5927\u529f\u3002\u201d \u3000\u3000\u201c\u6559\u4e3b\u8fd9\u4e9b\u5e74\u6765\u5904\u5fc3\u79ef\u8651\u7684\u60f3\u5f97\u5230\u7684\u4e00\u90e8\u6b66\u5b66\u79d8\u7b08\uff0c\u4fbf\u662f\u2018\u70bc\u5fc3\u5927\u6cd5\u2019\uff0c\u800c\u504f\u504f\u5979\u80fd\u8bbe \u6cd5\u5f97\u5230\u8fd9\u518c\u6b66\u5b66\u79d8\u7b08\uff0c\u732e\u7ed9\u4e86\u6559\u4e3b\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u89c9\u5fc3\u5934\u4e00\u9707\uff0e\u539f\u6765\u51e4\u5ae3\u7ea2\u548c\u9a6c\u662d\u96c4\u76d7\u5f97\u5723\u624b\u4e66\u751f\u5e90\u4e91\u7684\u2018\u70bc\u5fc3\u5927\u6cd5\u79d8\u7b08\u2019\uff0c\u7adf \u7136\u732e\u7ed9\u4e86\u7687\u5143\u6559\u4e3b\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u8881\u5c0f\u9e64\u5982\u4f55\u80fd\u628a\u8fd9\u518c\u79d8\u7b08\u518d\u65f6\u56de\u6765\uff1f \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u5cb3\u5c0f\u98de\u771f\u6709\u4e9b\u624b\u8db3\u65e0\u63aa\uff0c\u4f24\u5fc3\u6b32\u7edd\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u770b\u51fa\u5cb3\u5c0f\u98de\u795e\u8272\u6709\u5f02\uff0c\u4e0d\u89c9\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u597d\u50cf\u53c8\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5b9a\u4e86\u5b9a\u795e\u9053\uff1a\u201c\u7236\u4eb2\u53ef\u77e5\u9053\u51e4\u5ae3\u7ea2\u7684\u8eab\u4efd\u6765\u5386\u5417\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u76b1\u4e0b\u53cc\u7709\u9053\uff1a\u201c\u5979\u8fdb\u5165\u5929\u8c37\uff0c\u6bd4\u4e3a\u7236\u8981\u65e9\u5f97\u591a\uff0c\u83ab\u975e\u4f60\u77e5\u9053\u5979\u7684\u8eab\u4efd\u6765\u5386\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u968f\u5373\u628a\u51e4\u5ae3\u7ea2\u539f\u662f\u5723\u624b\u4e66\u751f\u5e90\u4e91\u7684\u59bb\u5b50\uff0c\u5341\u5e74\u524d\u548c\u95e8\u4e0b\u5f1f\u5b50\u9a6c\u662d\u96c4\u79c1\u5954\uff0e\u4ee5\u53ca\u5982 \u4f55\u76d7\u8d70\u5e90\u4e91\u7684\u2018\u70bc\u5fc3\u5927\u6cd5\u79d8\u7b08\u2019\u4ece\u5934\u5230\u5c3e\u8bf4\u4e86\u2014\u904d\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u4e0d\u89c9\u5251\u7709\u76f4\u7ad6\u9053\uff1a\u201c\u539f\u6765\u51e4\u5ae3\u7ea2\u662f\u8fd9\u4e48\u4e00\u4e2a\u4e0b\u6d41\u65e0\u803b\u7684\u5973\u4eba\uff0c\u800c\u504f\u504f\u5979\u80fd\u5f97\u5ba0\u4e8e \u6559\u4e3b\uff0c\u4e16\u4e0a\u771f\u662f\u5929\u7406\u4f55\u5728\uff1f\u201d \u3000\u3000\u201c\u7236\u4eb2\u662f\u5426\u77e5\u9053\u9a6c\u662d\u96c4\u76ee\u524d\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u6211\u4e5f\u542c\u8bf4\u8fc7\u6b64\u4eba\uff0c\u81f3\u4e8e\u4ed6\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u65e5\u524d\u8fd8\u5f04\u4e0d\u6e05\u695a\uff0c\u5f53\u7136\uff0c\u56e0\u4e3a\u6559\u4e3b\u4e0d\u559c\u6b22\u592b\u5987 \u540c\u6765\uff0c\u8fd9\u5bf9\u65e0\u803b\u7537\u5973\u60f3\u5fc5\u4e5f\u53ea\u80fd\u6697\u4e2d\u5077\u5077\u6765\u5f80\u3002\u201d \u3000\u3000\u8bdd\u5df2\u8bf4\u4e86\u8fd9\u4e48\u591a\uff0c\u5cb3\u5c0f\u98de\u8be5\u77e5\u9053\u7684\u5168\u5df2\u77e5\u9053\uff0c\u6b63\u8981\u544a\u8f9e\u9000\u51fa\uff0c\u82b1\u7389\u9e9f\u53c8\u9053\uff1a\u201c\u8981\u544a\u8bc9\u8881 \u5c0f\u9e64\uff0c\u4ed6\u6b64\u6765\u5929\u8c37\uff0c\u867d\u7136\u662f\u5949\u5723\u624b\u4e66\u751f\u5e90\u5927\u4fa0\u4e4b\u547d\uff0e\u76ee\u7684\u5728\u8ba8\u56de\u90a3\u518c\u2018\u70bc\u5fc3\u5927\u6cd5\u79d8\u7b08\u2019\u5374\u5df2 \u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\u60c5\u4e86\uff0c\u5c24\u5176\u5bf9\u51e4\u5ae3\u7ea2\uff0c\u5343\u4e07\u4e0d\u80fd\u8f7b\u4e3e\u5984\u52a8\uff0c\u5426\u5219\u5f88\u53ef\u80fd\u81ea\u53d6\u5176\u7978\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5b69\u513f\u9075\u547d\uff01\u201d \u3000\u3000\u79bb\u5f00\u82b1\u7389\u9e9f\u623f\u95f4\uff0c\u5cb3\u5c0f\u98de\u672c\u60f3\u7acb\u5373\u627e\u8881\u5c0f\u9e64\u544a\u77e5\u6b64\u4e8b\uff0c\u53ea\u662f\u65f6\u95f4\u5df2\u665a\uff0c\u8881\u5c0f\u9e64\u65e9\u5df2\u5b89\u6b47\u3002 \u3000\u3000\u56de\u5230\u81ea\u5df1\u623f\u95f4\uff0c\u8eba\u4e0a\u5e8a\uff0c\u8f97\u8f6c\u53cd\u4fa7\uff0c\u59cb\u7ec8\u65e0\u6cd5\u5165\u7761\uff0c\u60f3\u5230\u6bcd\u4eb2\u5df2\u88ab\u6253\u5165\u80b2\u5316\u57ce\uff0c\u800c\u4e14\u53c8 \u662f\u88ab\u51e4\u5ae3\u7ea2\u9677\u5bb3\u7684\uff0c\u771f\u6068\u4e0d\u5f97\u628a\u90a3\u65e0\u803b\u7684\u6d6e\u8d31\u5973\u4eba\u788e\u5c38\u4e07\u6bb5\uff0c\u504f\u504f\u5979\u53c8\u662f\u6559\u4e3b\u7684\u4eb2\u4fe1\u3002 \u3000\u3000\u597d\u4e0d\u5bb9\u6613\u71ac\u5230\u6b21\u65e5\u5929\u4eae\uff0c\u4ed6\u51b3\u5b9a\u5c3d\u901f\u627e\u8881\u5c0f\u9e64\u5546\u91cf\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u4e5f\u662f\u521a\u8d77\u5e8a\uff0c\u4e00\u89c1\u5cb3\u5c0f\u98de\u8fdb\u5165\u623f\u95f4\u4fbf\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u8fd9\u4e48\u65e9\u6765\u627e\u6211\uff0c\u6709\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u81ea\u52a8\u5728\u6905\u5b50\u4e0a\u5750\u4e0b\u9053\uff1a\u201c\u5bb5\u5173\u7edf\u9886\u51e4\u5ae3\u7ea2\uff0c\u8881\u5927\u54e5\u662f\u5df2\u7ecf\u77e5\u9053\u7684\u4e86\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u4f60\u544a\u8bc9\u6211\u7684\uff0c\u6211\u8fd9\u51e0\u5929\u6b63\u51c6\u5907\u627e\u673a\u4f1a\u89c1\u89c1\u5979\u3002\u201d \u3000\u3000\u201c\u8881\u5927\u54e5\u6700\u597d\u6682\u65f6\u522b\u53bb\u89c1\u5979\uff0c\u5373\u4fbf\u4ee5\u540e\u89c1\u7740\u5979\uff0c\u4e5f\u5fc5\u987b\u5c0f\u5fc3\u8c28\u614e\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u5979\u76ee\u524d\u5728\u6559\u4e3b\u9762\u524d\u5341\u5206\u5f97\u5ba0\uff0c\u8fde\u526f\u603b\u9547\u867d\u662f\u5979\u7684\u4e0a\u53f8\uff0c\u4e5f\u4e0d\u6562\u60f9\u5979\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u542c\u5f97\u4e00\u6014\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1b\u201c\u5c0f\u5f1f\u867d\u662f\u4fa7\u9762\u6d88\u606f\uff0c\u4f46\u6d88\u606f\u6765\u6e90\u5374\u5341\u5206\u6b63\u786e\uff0c\u53e6\u5916\uff0c\u5c0f\u5f1f\u8fd8\u8981\u544a\u8bc9\u4f60\u4e00\u4ef6 \u5927\u4e8b\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u5927\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5979\u548c\u9a6c\u662d\u96c4\u76d7\u5f97\u4e86\u5e90\u4f2f\u4f2f\u7684\u2018\u70bc\u5fc3\u5927\u6cd5\u79d8\u7b08\u2019\uff0c\u5e76\u6ca1\u6709\u81ea\u5df1\u7559\u7740\uff0c\u5df2\u7ecf\u732e\u7ed9\u4e86\u6559\u4e3b\uff0c \u5979\u80fd\u5728\u6559\u4e3b\u9762\u524d\u8d70\u7ea2\uff0c\u4e5f\u6b63\u662f\u8fd9\u539f\u56e0\u3002\u201d \u3000\u3000\u201c\u5c0f\u98de\uff0c\u8fd9\u8be5\u662f\u4e00\u4ef6\u6781\u5927\u7684\u673a\u5bc6\uff0c\u4f60\u7a76\u7adf\u662f\u600e\u6837\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u201c\u8881\u5927\u54e5\u53ea\u8981\u660e\u767d\u5c31\u597d\u4e86\uff0c\u4e0d\u5fc5\u95ee\u5c0f\u5f1f\u662f\u600e\u6837\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e0d\u4fbf\u8feb\u95ee\uff0c\u53ea\u6709\u4f4e\u4e0b\u5934\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u518d\u9053\uff1a\u201c\u8881\u5927\u54e5\u5343\u4e07\u8bb0\u4f4f\uff0c\u5bf9\u4e8e\u51e4\u5ae3\u7ea2\uff0c\u5e94\u8be5\u5c3d\u91cf\u907f\u514d\u89c1\u5230\u5979\uff0c\u514d\u5f97\u60f9\u7978\u4e0a\u8eab\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8d70\u540e\uff0c\u8881\u5c0f\u9e64\u9677\u5165\u75db\u82e6\u7684\u6c89\u601d\u4e2d\u3002 \u3000\u3000\u4ed6\u4ece\u72ec\u79c0\u5cf0\u5949\u547d\u897f\u6765\uff0c\u4e3b\u8981\u7684\u4efb\u52a1\u4fbf\u662f\u8ba8\u56de\u2018\u70bc\u5fc3\u5927\u6cd5\u79d8\u7b08\u2019\uff0c\u5982\u4eca\u542c\u5cb3\u5c0f\u98de\u8fd9\u4e48\u4e00\u8bf4\uff0c \u8fe2\u8fe2\u5343\u91cc\u95ef\u5165\u5929\u8c37\uff0c\u5c82\u4e0d\u7b49\u4e8e\u524d\u529f \u3000\u3000\u5c3d\u5f03\uff1f\u5c06\u6765\u53c8\u5982\u4f55\u5411\u5e08\u7236\u4ea4\u4ee3\uff1f\u56e0\u4e4b\uff0c\u4ed6\u4e00\u76f4\u5446\u5750\u623f\u4e2d\uff0c\u4e0d\u4f46\u65e9\u9910\u672a\u7528\uff0c\u8fde\u5348\u996d\u4e5f\u65e0\u5fc3 \u5403\u3002 \u3000\u3000\u5c31\u8fd9\u6837\u4e00\u76f4\u5230\u508d\u665a\uff0c\u4ed6\u5b9e\u5728\u5fcd\u53d7\u4e0d\u4f4f\uff0c\u7adf\u7136\u4e0b\u51b3\u5fc3\u8981\u548c\u51e4\u5ae3\u7ea2\u89c1\u4e0a\u4e00\u9762\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u60f3\u5230\uff0c\u53ea\u6709\u89c1\u5230\u51e4\u5ae3\u7ea2\uff0c\u624d\u80fd\u63a2\u542c\u51fa\u90a3\u518c\u2018\u70bc\u5fc3\u5927\u6cd5\u79d8\u7b08\u2019\uff0c\u7a76\u7adf\u662f\u5426\u5df2\u732e\u7ed9\u4e86 \u6559\u4e3b\uff0c\u8fd9\u8868\u793a\u4ed6\u65f6\u5cb3\u5c0f\u98de\u7684\u8bdd\u5e76\u672a\u5b8c\u5168\u76f8\u4fe1\u3002 \u3000\u3000\u53e6\u5916\uff0e\u4ed6\u4e5f\u6599\u5f97\u51fa\uff0c\u53ea\u8981\u4ed6\u5bf9\u51e4\u5ae3\u7ea2\u80fd\u4fdd\u6301\u4e00\u8d2f\u7684\u793c\u8c8c\uff0c\u51e4\u5ae3\u7ea2\u4e5f\u51b3\u4e0d\u4f1a\u628a\u4ed6\u600e\u4e48\u6837\u3002 \u56e0\u4e3a\u5f53\u521d\u51e4\u5ae3\u7ea2\u548c\u5e90\u4e91\u5c1a\u662f\u592b\u59bb\u65f6\uff0c\u4ed6\u59cb\u7ec8\u5bf9\u5979\u606d\u656c\u6709\u52a0\uff0c\u800c\u4e14\u4ed6\u90a3\u65f6\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u51e4\u5ae3\u7ea2\u5bf9 \u4ed6\u4e5f\u5341\u5206\u559c\u7231\uff0c\u5f7c\u6b64\u95f4\u6839\u672c\u8c08\u4e0d\u4e0a\u4efb\u4f55\u6069\u6028\u3002 \u3000\u3000\u4e3b\u610f\u65e2\u5df2\u6253\u5b9a\uff0c\u4ed6\u7279\u522b\u6362\u4e86\u4e00\u5957\u65b0\u8863\uff0c\u5e76\u4f69\u6234\u4e0a\u5de1\u5173\u4f7f\u8005\u7684\u6807\u8bc6\uff0c\u4e00\u4e2a\u4eba\u5077\u5077\u5f80\u5bb5\u5173\u800c \u6765\u3002 \u3000\u3000\u4e94\u5173\u603b\u9547\u8859\u7f72\u4e5f\u5728\u5bb5\u5173\u5883\u5185\uff0c\u79bb\u5bb5\u5173\u7edf\u9886\u6240\u5728\u5730\u6700\u591a\u4e0d\u8fc7\u4e00\u91cc\u8def\uff0c\u5f88\u5feb\u4fbf\u5230\u8fbe\u5bb5\u5173\u7edf\u9886 \u6240\u5728\u5730\u7684\u5927\u95e8\u5916\u3002 \u3000\u3000\u5b88\u95e8\u4eba\u4e00\u89c1\u8881\u5c0f\u9e64\u4f69\u6709\u5de1\u5173\u4f7f\u8005\u7684\u6807\u8bc6\uff0c\u4e0a\u7ea7\u89c6\u5bdf\u7684\u4eba\u5230\u4e86\uff0c \u3000\u3000\u5f53\u7136\u662f\u5927\u793c\u76f8\u8fce\uff0c\u4e00\u9762\u6025\u6025\u901a\u77e5\u526f\u7edf\u9886\u5f20\u51e4\u9e23\u3002 \u3000\u3000\u5f20\u51e4\u9e23\u5306\u5306\u8fce\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u8fde\u5fd9\u62b1\u62f3\u65bd\u793c\u9053\uff1a\u201c\u665a\u8f88\u8881\u5c0f\u9e64\u7279\u6765\u62dc\u8bbf\u526f\u7edf\u9886\uff01\u201d \u3000\u3000\u5f20\u51e4\u9e23\u6025\u6025\u8fd8\u793c\uff0c\u8fde\u58f0\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u8881\u8001\u5f1f\uff0c\u606d\u559c\u4f60\u4e00\u4e0b\u5b50\u5c31\u8363\u4efb\u4e86\u5de1\u5173\u4f7f\u8005\uff0c\u73b0\u5728 \u8be5\u662f\u672c\u5173\u7684\u4e0a\u53f8\u4e86\uff0c\u5931\u8fce\u5931\u8fce\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e5f\u8fde\u79f0\u4e0d\u6562\u5f53\uff0c\u9053\uff1a\u201c\u524d\u8f88\uff0c\u665a\u8f88\u4eca\u540e\u8fd8\u8981\u627f\u60a8\u591a\u6307\u6559\uff0c\u62dc\u8c12\u6765\u8fdf\uff0c\u4f60\u8001\u4eba\u5bb6\u53ef \u8981\u591a\u539f\u8c05\uff01\u201d \u3000\u3000\u5f20\u51e4\u9e23\u628a\u8881\u5c0f\u9e64\u5f15\u81f3\u5927\u5385\uff0c\u4eb2\u81ea\u6c8f\u4e0a\u8336\uff0c\u671b\u4e86\u671b\u5929\u8272\u9053\uff1b\u201c\u8881\u8001\u5f1f\u4e00\u5b9a\u8fd8\u4e0d\u66fe\u7528\u665a\u996d\uff0c \u8001\u673d\u4eca\u665a\u8981\u597d\u597d\u62db\u5f85\u4f60\uff0c\u4e5f\u7b97\u4e3a\u4f60\u63a5\u98ce\u6d17\u5c18\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u7b11\u9053\uff1a\u201c\u665a\u8f88\u73b0\u5728\u4e5f\u5728\u5bb5\u5173\u5883\u5185\uff0c\u4e0d\u6d88\u7247\u523b\u4fbf\u53ef\u56de\u53bb\uff0c\u600e\u597d\u610f\u601d\u518d\u2018\u767d\u5403\u5305 \u5b50\u2019\uff01\u201d \u3000\u3000\u5f20\u51e4\u9e23\u4e5f\u5475\u5475\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8881\u8001\u5f1f\u8fd8\u771f\u98ce\u8da3\uff0c\u53c8\u628a\u90a3\u56de\u4e8b\u8bb0\u8d77\u6765\u4e86\uff0c\u8bf4\u771f\u7684\uff0c\u4e0a\u6b21\u56db \u4f4d\u901a\u8fc7\u2018\u767d\u5403\u5305\u5b50\u2019\u90a3\u4e00\u7ad9\uff0c\u5b9e\u5728\u5404\u6709\u5999\u5904\uff0c\u8001\u673d\u81f3\u4eca\u60f3\u6765\uff0c\u8fd8\u5fcd\u4fca\u4e0d\u7981\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u613f\u8881\u5c0f\u9e64\u8c26\u8f9e\uff0c\u6b63\u6b32\u5429\u5490\u5927\u5385\u5916\u7684\u624b\u4e0b\u4eba\u4ea4\u4ee3\u53a8\u623f\u51c6\u5907\u9152\u7b75\uff0c\u8881\u5c0f\u9e64\u8fde\u5fd9\u62c9\u4f4f\u4ed6\u9053\uff1a \u201c\u8001\u524d\u8f88\u5343\u4e07\u522b\u9ebb\u70e6\uff0c\u665a\u8f88\u73b0\u5728\u8fd8\u60f3\u53bb\u62dc\u89c1\u4e00\u4e0b\u51e4\u7edf\u9886\uff01\u201d \u3000\u3000\u5f20\u51e4\u9e23\u53ea\u597d\u53c8\u56de\u4e86\u5ea7\u3002 \u3000\u3000\u82e5\u5728\u4ee5\u5f80\uff0c\u8881\u5c0f\u9e64\u60f3\u89c1\u51e4\u5ae3\u7ea2\uff0c\u5f20\u51e4\u9e23\u4e00\u5b9a\u4f1a\u501f\u6545\u62d2\u7edd\uff0c\u4f46\u73b0\u5728\u8881\u5c0f\u9e64\u662f\u5de1\u5173\u4f7f\u8005\uff0c\u8eab \u4efd\u804c\u8d23\u4e0d\u540c\uff0c\u5f20\u51e4\u9e23\u4e0d\u5f97\u4e0d\u4ee3\u4e3a\u8f6c\u8fbe\u3002 \u3000\u3000\u4ed6\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u7edf\u9886\u4e00\u5411\u5f88\u5c11\u63a5\u89c1\u5bbe\u5ba2\uff0c\u82e5\u8881\u8001\u5f1f\u8981\u89c1\uff0c\u90a3\u5c31\u8bf7\u7a0d\u5f85\uff0c\u8001\u673d\u8fd9\u5c31\u8fc7\u53bb\u901a \u77e5\u7edf\u9886\u4e00\u58f0\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u665a\u8f88\u5728\u793c\u8c8c\u4e0a\u5fc5\u987b\u62dc\u89c1\u51e4\u5ae3\u7ea2\u7edf\u9886\uff0c\u5426\u5219\u5c31\u662f\u5931\u793c\u4e86\uff01\u201d \u3000\u3000\u5f20\u51e4\u9e23\u53bb\u540e\u4e0d\u4e45\uff0c\u4fbf\u7b11\u541f\u541f\u7684\u56de\u5230\u5927\u5385\u9053\uff1a\u201c\u8881\u8001\u5f1f\u8fd9\u4f4d\u5de1\u5173\u4f7f\u8005\uff0c\u679c\u7136\u4e0e\u4f17\u4e0d\u540c\uff0c\u6211 \u4eec\u7edf\u9886\u8bf7\u4f60\u9a6c\u4e0a\u8fc7\u53bb\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u771f\u6ca1\u6599\u5230\u51e4\u5ae3\u7ea2\u4f1a\u8fd9\u4e48\u723d\u5feb\u63a5\u89c1\u81ea\u5df1\uff0c\u968f\u5373\u8ddf\u7740\u5f20\u51e4\u9e23\u6765\u5230\u51e4\u5ae3\u7ea2\u7684\u4f4f\u5904\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u680b\u72ec\u95e8\u72ec\u9662\u7684\u7cbe\u820d\uff0c\u5f53\u8fdb\u95e8\u540e\u6765\u5230\u5929\u6251\uff0c\u51e4\u5ae3\u7ea2\u5df2\u50cf\u6735\u76db\u5f00\u7684\u73ab\u7470\u82b1\u822c\u542b\u7b11\u7ad9\u5728 \u5ba2\u5385\u95e8\u53e3\u3002 \u3000\u3000\u8fd9\u5973\u4eba\u5f53\u771f\u9a7b\u989c\u6709\u672f\uff0c\u548c\u5f53\u5e74\u4e1d\u6beb\u6ca1\u6709\u6539\u53d8\uff0c\u4e30\u5bb9\u76db\u524d\uff0c\u5343\u5a07\u767e\u5a9a\uff0c\u770b\u8d77\u6765\u5c31\u50cf\u4e8c\u5341\u5de6 \u53f3\u7684\u5999\u9f84\u5973\u90ce\u4e00\u822c\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u5f53\u7136\u4e0d\u80fd\u8ba9\u5f20\u51e4\u9e23\u5f97\u77e5\u81ea\u5df1\u548c\u51e4\u5ae3\u7ea2\u7684\u5173\u7cfb\uff0c\u5f53\u4e0b\uff0c\u8d8b\u524d\u51e0\u6b65\uff0e\u6df1\u65bd\u4e00\u793c\u9053\uff1a \u201c\u665a\u8f88\u8881\u5c0f\u9e64\u62dc\u89c1\u51e4\u7edf\u9886\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u98ce\u81f4\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u8881\u4f7f\u8005\u7528\u4e0d\u7740\u5ba2\u6c14\uff0c\u5feb\u8bf7\u91cc\u9762\u5750\uff01\u201d \u3000\u3000\u8fdb\u5165\u5ba2\u5385\uff0c\u8881\u5c0f\u9e64\u6697\u4e2d\u7559\u795e\u671b\u53bb\uff0c\u91cc\u9762\u5e03\u7f6e\u5f97\u9ad8\u96c5\u800c\u534e\u4e3d\uff0c\u8fde\u6c14\u6c1b\u4e5f\u900f\u7740\u65d6\u65ce\u98ce\u5149\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u671b\u4e86\u671b\u5f20\u51e4\u9e23\u4e00\u773c\u9053\uff1a\u201c\u526f\u7edf\u9886\u5982\u679c\u4e8b\u60c5\u5fd9\uff0c\u5c31\u4e0d\u5fc5\u5728\u8fd9\u91cc\u4f5c\u966a\u4e86\uff01\u201d \u3000\u3000\u5f20\u51e4\u9e23\u968f\u5373\u544a\u8f9e\u800c\u53bb\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u518d\u5429\u5490\u8eab\u65c1\u4e00\u540d\u5973\u4f8d\u9053\uff1a\u201c\u901a\u77e5\u53a8\u623f\uff0c\u5907\u4e00\u684c\u9152\u98df\uff0c\u76f4\u63a5\u9001\u522b\u8fd9\u91cc\u6765\uff01\u201d \u3000\u3000\u518d\u5429\u5490\u53e6\u4e00\u540d\u5973\u4f8d\u9053\uff1a\u201c\u4f60\u4e5f\u51fa\u53bb\uff0c\u6ca1\u7ecf\u6211\u4f20\u558a\u4e0d\u5fc5\u8fdb\u6765\uff01\u201d \u3000\u3000\u663e\u7136\uff0c\u5979\u662f\u8981\u5355\u72ec\u548c\u8881\u5c0f\u9e64\u8c08\u8c08\uff0c\u800c\u4e14\u4e0d\u613f\u8ba9\u4efb\u4f55\u5916\u4eba\u542c\u5230\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u89c1\u5ba2\u5385\u5185\u53ea\u5269\u4e0b\u51e4\u5ae3\u7ea2\uff0c\u8fd9\u624d\u518d\u5ea6\u6df1\u65bd\u4e00\u793c\u9053\uff1a\u201c\u5f1f\u5b50\u62dc\u89c1\u5e08\u5a18\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u7728\u52a8\u7740\u79cb\u6c34\u822c\u7684\u660e\u7738\uff0c\u89c6\u7ebf\u5728\u8881\u5c0f\u9e64\u8138\u4e0a\u95ea\u4e86\u51e0\u95ea\u9053\uff1a \u3000\u3000\u201c\u5c0f\u9e64\uff0c\u51e0\u5e74\u4e0d\u89c1\uff0c\u4f60\u679c\u771f\u957f\u9ad8\u4e86\uff0c\u800c\u4e14\u51fa\u843d\u5f97\u4e00\u8868\u4eba\u624d\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8138\u4e0a\u4e00\u70ed\uff0c\u4f4e\u4e0b\u5934\u9053\uff1a\u201c\u5f1f\u5b50\u5df2\u7ecf\u4e8c\u5341\u591a\u4e86\uff0c\u81ea\u7136\u65e9\u5df2\u4e0d\u518d\u662f\u5c0f\u5b69\u5b50\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u54af\u54af\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5df2\u7ecf\u662f\u5927\u4eba\uff0c\u5c31\u7528\u4e0d\u7740\u518d\u5bb3\u7f9e\uff0c\u6765\u5230\u6211\u8fd9\u91cc\uff0c\u5c31\u50cf\u56de\u5230\u81ea\u5df1\u7684 \u5bb6\u4e00\u6837\uff0c\u7528\u4e0d\u7740\u5ba2\u6c14\uff0c\u5feb\u8bf7\u5750\uff01\u201d \u3000\u3000\u5979\u4e00\u8fb9\u8bf4\uff0c\u4e00\u8fb9\u4eb2\u81ea\u6c8f\u4e86\u676f\u8336\u7aef\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u8fde\u5fd9\u63a5\u8fc7\u9053\uff1a\u201c\u5e08\u5a18\u8fd9\u6837\u76f8\u5f85\uff0c\u771f\u6298\u715e\u5f1f\u5b50\u4e86\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u4eb2\u4eb2\u5207\u5207\u7684\u9053\uff1a\u201c\u591a\u5e74\u4e0d\u89c1\uff0c\u53c8\u662f\u81ea\u5bb6\u4eba\uff0c\u5e08\u5a18\u5f53\u7136\u8be5\u597d\u597d\u62db\u5f85\u4f60\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u771f\u662f\u53d7\u5ba0\u82e5\u60ca\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u5f1f\u5b50\u56de\u53bb\u5f88\u8fd1\uff0c\u7528\u4e0d\u7740\u6253\u6270\u5e08\u5a18\u51c6\u5907\u9152\u996d\uff0c\u8fd9\u6837\u5f1f \u5b50\u5b9e\u5728\u4e8e\u5fc3\u4e0d\u5b89\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u7b11\u9765\u751f\u6625\u9053\uff1a\u201c\u5c0f\u9e64\uff0c\u4f60\u82e5\u518d\u89c1\u5916\uff0c\u5c31\u7b49\u4e8e\u4e0d\u8ba4\u6211\u8fd9\u4f4d\u5e08\u5a18\u4e86\uff0c\u4f60\u867d\u7136\u79bb\u5f97\u8fd1\uff0c \u6211\u5374\u603b\u4e0d\u80fd\u4e0d\u62db\u5f85\u4f60\uff0c\u800c\u4e14\u6211\u4e5f\u51c6\u5907\u591a\u8ddf\u4f60\u8c08\u8c08\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e0d\u4fbf\u518d\u63a8\u8f9e\uff0c\u559d\u4e86\u53e3\u8336\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u53c8\u4e0d\u77e5\u8be5\u5148\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u5728\u5bf9\u9762\u5750\u4e0b\u9053\uff1a\u201c\u4e94\u5173\u603b\u9547\u5df2\u7ecf\u6210\u7acb\u4e86\u534a\u4e2a\u591a\u6708\uff0c\u5f7c\u6b64\u9694\u5f97\u8fd9\u4e48\u8fd1\uff0c\u4f60\u4e3a\u4ec0\u4e48\u5230 \u73b0\u5728\u624d\u6765\u770b\u6211\uff0c\u662f\u4e0d\u662f\u51c6\u5907\u4e0d\u8ba4\u5e08\u5a18\u4e86\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u7ea2\u7740\u8138\u9053\uff1a\u201c\u5e08\u5a18\u8bf4\u90a3\u91cc\u8bdd\uff0c\u5f1f\u5b50\u76f4\u5230\u6628\u5929\u624d\u77e5\u9053\u5e08\u5a18\u662f\u5bb5\u5173\u7edf\u9886\uff0c\u4e0a\u6b21\u5728\u5bb5\u5173 \u53d7\u6d4b\u53c8\u5728\u8fd9\u91cc\u4f4f\u4e86\u4e00\u591c\uff0c\u9057\u61be\u7684\u662f\u5e76\u6ca1\u89c1\u5230\u5e08\u5a18\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u4e0a\u6b21\u6b63\u597d\u6211\u6709\u4e8b\uff0c\u6ca1\u80fd\u63a5\u89c1\u4f60\u4eec\u56db\u4f4d\uff0c\u4e8b\u540e\u624d\u542c\u5f20\u526f\u7edf\u9886\u63d0\u5230\u4f60\u4eec\uff0c\u672c\u60f3 \u5230\u62db\u8d24\u9986\u53bb\u63a2\u671b\u4f60\uff0c\u53c8\u788d\u4e8e\u90a3\u91cc\u7684\u89c4\u5b9a\u6ca1\u6cd5\u53bb\u6210\u3002\u201d \u3000\u3000\u53c8\u8c08\u4e86\u4e00\u9635\uff0c\u4e24\u540d\u5973\u4f8d\u5df2\u628a\u9152\u996d\u7aef\u4e86\u8fdb\u6765\u3002 \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u5341\u4e94\u56de\u3000\u521d\u5de1\u4e94\u5173 \u3000\u3000\u867d\u7136\u53ea\u6709\u4e24\u4e2a\u4eba\uff0c\u5404\u79cd\u83dc\u80b4\u5374\u6446\u4e86\u6ee1\u6ee1\u4e00\u684c\uff0c\u800c\u4e14\u8fd8\u6709\u4e00\u7f50\u9152\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u53c8\u7684\u628a\u4e24\u540d\u4f8d\u5973\u4f7f\u51fa\u53bb\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u642d\u8baa\u7740\u9053\uff1a\u201c\u5e08\u5a18\u65e2\u7136\u9152\u83dc\u51c6\u5907\u5f97\u8fd9\u6837\u4e30\u76db\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u628a\u5f20\u526f\u7edf\u9886\u8bf7\u6765\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u4eca\u665a\u6211\u4eec\u8981\u8c08\u7684\u662f\u79c1\u8bdd\uff0c\u600e\u53ef\u8bf7\u5916\u4eba\u6765\u3002\u201d \u3000\u3000\u5979\u53c8\u4eb2\u81ea\u4e3a\u8881\u5c0f\u9e64\u659f\u6ee1\u9152\u9053\uff1a\u201c\u6765\uff0c\u5e08\u5a18\u656c\u4f60\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u614c\u5fd9\u4e3e\u676f\u9053\uff1a\u201c\u5e08\u5a18\uff0c\u5f1f\u5b50\u9152\u91cf\u4e0d\u597d\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u7b11\u9053\uff1a\u201c\u4f60\u662f\u7537\u4eba\uff0c\u9152\u91cf\u4e0d\u597d\uff0c\u5c31\u8be5\u591a\u7ec3\u4e60\uff0c\u5c24\u5176\u73b0\u5728\u505a\u4e86\u5de1\u5173\u4f7f\u8005\uff0c\u5230\u4e86\u90a3\u4e00 \u5173\uff0c\u90a3\u4e00\u5173\u5c31\u514d\u4e0d\u4e86\u9152\u7b75\u62db\u5f85\uff0c\u9152\u91cf\u4e0d\u597d\u600e\u4e48\u6210\uff1f\u4f55\u51b5\u4f60\u5c06\u6765\u7684\u5b98\u513f\u8d8a\u505a\u8d8a\u5927\uff0c\u66f4\u8981\u628a\u9152\u91cf \u7ec3\u597d\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u53ea\u5f97\u4e00\u996e\u800c\u5c3d\uff0c\u518d\u56de\u656c\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u5e08\u5a18\uff0c\u5f1f\u5b50\u7684\u786e\u6ca1\u6709\u9152\u91cf\uff0c\u8981\u7ec3\u4e5f\u4e0d\u662f\u90a3\u4e48 \u5feb\u7684\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u4e5c\u659c\u7740\u773c\u513f\u7b11\u9053\uff1a\u201c\u5c0f\u9e64\uff0c\u4f60\u4eca\u665a\u548c\u5e08\u5a18\u89c1\u9762\u4e0d\u6613\uff0c\u5373\u4fbf\u559d\u9189\u4e86\uff0c\u4e5f\u662f\u5e94\u8be5\u7684\uff0c \u53ea\u7ba1\u653e\u5fc3\u559d\u4e0a\u4e00\u9189\uff0c\u5e08\u5a18\u4f1a\u7167\u987e\u4f60\u7684\u3002\u201d \u3000\u3000\u4e0d\u5f97\u5df2\uff0c\u8881\u5c0f\u9e64\u7684\u7b2c\u4e8c\u676f\u9152\u53c8\u662f\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u8fd9\u65f6\u5a07\u9765\u4e5f\u6ee1\u6cdb\u7edb\u971e\uff0c\u62bf\u4e86\u62bf\u5634\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u5e08\u7236\u597d\u5417\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6697\u9053\uff1a\u201c\u5e9f\u8bdd\uff0c\u5e08\u7236\u5728\u4f60\u548c\u9a6c\u662d\u96c4\u6deb\u5954\u4e4b\u540e\uff0c\u600e\u80fd\u597d\u5f97\u8d77\u6765\uff1f\u201d \u3000\u3000\u4f46\u4ed6\u5374\u4e0d\u5f97\u4e0d\u5047\u610f\u5e94\u4ed8\uff1a\u201c\u5e08\u7236\u8fd9\u4e9b\u5e74\u65e5\u5b50\u8fc7\u5f97\u5f88\u6e05\u51c0\uff0c\u5012\u4e5f\u900d\u9065\u81ea\u5728\u3002\u201d \u3000\u3000\u201c\u4ed6\u80fd\u65e5\u5b50\u8fc7\u5f97\u900d\u9065\u81ea\u5728\uff0c\u90a3\u6211\u4e5f\u5c31\u7528\u4e0d\u7740\u6302\u5ff5\u4ed6\u4e86\uff01\u201d \u3000\u3000\u201c\u5e08\u5a18\u5f53\u771f\u5fc3\u91cc\u8fd8\u60f3\u5e08\u7236\uff1f\u201d \u3000\u3000\u201c\u4e00\u65e5\u592b\u59bb\u767e\u65e5\u6069\uff0c\u6211\u5f53\u7136\u4e5f\u65f6\u65f6\u7275\u6302\u7740\u4ed6\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5e08\u5a18\u4e3a\u4ec0\u4e48\u53c8\u8981\u79bb\u5f00\u4ed6\u8001\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u201c\u6211\u6709\u4e0d\u5f97\u5df2\u7684\u82e6\u8877\uff0c\u5c0f\u9e64\uff0c\u4f60\u8ba4\u4e3a\u6211\u8ddf\u4f60\u5927\u5e08\u5144\u662f\u79c1\u5954\u5417\uff1f\u201d \u3000\u3000\u8fd9\u95ee\u8bdd\u5b9e\u5728\u5927\u51fa\u8881\u5c0f\u9e64\u610f\u6599\uff0c\u800c\u4ed6\u4e5f\u5b9e\u5728\u4e0d\u77e5\u8be5\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u5e7d\u5e7d\u4e00\u53f9\u9053\uff1a\u201c\u5c0f\u9e64\uff0c\u522b\u628a\u5e08\u5a18\u770b\u6210\u662f\u4e00\u4e2a\u6deb\u8d31\u7684\u5973\u4eba\uff0c \u3000\u3000\u522b\u4eba\u53ef\u4ee5\u8bef\u4f1a\u6211\uff0c\u4f60\u4e0d\u5e94\u5f53\u8bef\u4f1a\u6211\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u5f53\u7136\u4e0d\u4f1a\u88ab\u5979\u8fd9\u51e0\u53e5\u8bdd\u8ff7\u60d1\uff0c\u8d81\u673a\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u9a6c\u5927\u5e08\u5144\u5462\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9ed8\u7136\u4e00\u7b11\u9053\uff1a\u201c\u8fd8\u63d0\u4ed6\u505a\u4ec0\u4e48\uff1f\u4ed6\u6b7b\u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u771f\u7684\uff1f\u4ec0\u4e48\u65f6\u5019\u6b7b\u7684\uff1f\u6b7b\u5728\u90a3\u91cc\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u4e00\u5e74\u591a\u4e86\uff0c\u5f53\u7136\u662f\u6b7b\u5728\u5929\u8c37\u91cc\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0d\u9700\u8981\u77e5\u9053\u90a3\u4e48\u591a\uff0c\u53ea\u8981\u77e5\u9053\u4ed6\u5df2\u4e0d\u5728\u4eba\u4e16\u5c31\u6210\u4e86\u3002\u201d \u3000\u3000\u201c\u5e08\u5a18\u5fc3\u91cc\u65e2\u7136\u8fd8\u5ff5\u7740\u5e08\u7236\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u56de\u5230\u5e08\u7236\u8eab\u8fb9\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u51c4\u51c9\u4e00\u7b11\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u6240\u8c13\u8986\u6c34\u96be\u6536\uff0c\u6211\u73b0\u5728\u5373\u4fbf\u56de\u53bb\uff0c\u4f60\u5e08\u7236\u53c8\u600e\u80af\u518d\u8981\u6211\uff1f \u4f55\u51b5\uff0c\u73b0\u5728\u6211\u8eab\u5728\u5929\u8c37\uff0c\u7eb5\u7136\u60f3\u56de\u53bb\uff0c \u3000\u3000\u4e5f\u662f\u4e0d\u53ef\u80fd\u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u8fdb\u4e86\u5929\u8c37\u4ee5\u540e\uff0c\u771f\u662f\u4e0d\u80fd\u518d\u56de\u53bb\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u8fdb\u5165\u5929\u8c37\u96be\uff0c\u51fa\u5929\u8c37\u66f4\u96be\uff0c\u5c0f\u9e64\uff0c\u4f60\u7167\u6837\u4e5f\u4e0d\u80fd\u518d\u56de\u53bb\u89c1\u4f60\u5e08\u7236\u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u5446\u4e86\u4e00\u5446\uff0c\u987f\u65f6\u6709\u4e0d\u77e5\u6240\u63aa\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u518d\u4e3a\u4ed6\u659f\u4e86\u4e00\u676f\u9152\u9053\uff1b\u201c\u542c\u8bf4\u90a3\u4e2a\u53eb\u5cb3\u5c0f\u98de\u7684\u4e5f\u6765\u4e86\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c0f\u98de\u4e5f\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u542c\u8bf4\u4ed6\u4e0d\u8bba\u6587\u624d\u6b66\u529f\uff0c\u90fd\u975e\u6bd4\u5bfb\u5e38\uff0c\u53ef\u89c1\u4f60\u5e08\u7236\u771f\u80fd\u8c03\u6559\u51fa\u4eba\u624d\u6765\u3002\u201d \u3000\u3000\u201c\u8fd9\u5e76\u975e\u5b8c\u5168\u5e08\u7236\u4e4b\u529f\uff0c\u5c0f\u98de\u5929\u8d4b\u5f02\u7980\uff0c\u95fb\u4e00\u77e5\u5341\uff0c\u4e0d\u662f\u4e00\u822c\u4eba\u6bd4\u5f97\u4e86\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u4e3a\u4ec0\u4e48\u4eca\u665a\u4e0d\u548c\u4f60\u4e00\u8d77\u6765\uff1f\u201d \u3000\u3000\u201c\u4ed6\u548c\u5e08\u5a18\uff0c\u5e76\u65e0\u6e0a\u6e90\uff0c\u4e0d\u8fc7\u4ed6\u4e5f\u662f\u5de1\u5173\u4f7f\u8005\uff0c\u4ee5\u540e\u603b\u4f1a\u6765\u7684\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u5ffd\u7136\u772f\u8d77\u7738\u5b50\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u9e64\uff0c\u5e08\u5a18\u5bf9\u4f60\u6709\u4e00\u4e2a\u8981\u6c42\uff0c\u4e0d\u77e5\u4f60\u80af\u4e0d\u80af\u7b54\u5e94\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u5fc3\u5c0f\u6026\u7136\u4e00\u9707\uff1a\u201c\u5e08\u5a18\u6709\u4ec0\u4e48\u4e8b\uff0c\u53ea\u7ba1\u660e\u8a00\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u6211\u5e0c\u671b\u4f60\u80fd\u8f9e\u53bb\u5de1\u5173\u4f7f\u8005\uff0c\u5230\u6211\u5bb5\u5173\u6765\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e0d\u89c9\u4e00\u6014\u9053\uff1a\u201c\u5e08\u5a18\u4e3a\u4ec0\u4e48\u6709\u8fd9\u79cd\u60f3\u6cd5\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u5e0c\u671b\u5728\u5bb5\u5173\u80fd\u6709\u81ea\u5df1\u7684\u4eba\u5728\u8eab\u8fb9\uff0c\u8fd9\u6837\u505a\u8d77\u4e8b\u6765\uff0c\u624d\u4f1a\u5f97\u5fc3\u5e94\u624b\u3002 \u4f60\u60f3\uff0c\u5728\u5929\u8c37\u5185\uff0c\u6211\u8fd8\u6709\u8c01\u6bd4\u4f60\u66f4\u4eb2\u8fd1\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u5f1f\u5b50\u521a\u6d3e\u5230\u4e94\u5173\u603b\u8859\u5185\uff0c\u82e5\u9a6c\u4e0a\u8c03\u52a8\uff0c\u53ea\u6015\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u201c\u53ea\u8981\u4f60\u81ea\u5df1\u613f\u610f\uff0c\u6211\u81ea\u6709\u529e\u6cd5\u628a\u4f60\u8c03\u8fc7\u6765\u3002\u201d \u3000\u3000\u201c\u5f1f\u5b50\u6765\u4e86\u4ee5\u540e\uff0c\u53c8\u80fd\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5c31\u6682\u65f6\u7559\u5728\u6211\u8eab\u8fb9\u542c\u7528\uff0c\u7b49\u4e00\u6709\u673a\u4f1a\uff0c\u6211\u4fbf\u4fdd\u4f60\u5347\u4efb\u526f\u7edf\u9886\u3002\u201d \u3000\u3000\u201c\u5f1f\u5b50\u90a3\u6709\u62c5\u4efb\u526f\u7edf\u9886\u7684\u624d\u80fd\uff0c\u4f55\u51b5\u8fd9\u91cc\u53c8\u6709\u5f20\u526f\u7edf\u9886\u3002\u201d \u3000\u3000\u201c\u5f20\u526f\u7edf\u9886\u5c81\u6570\u4eba\u4e86\uff0c\u5df2\u7ecf\u5230\u4e86\u9000\u4f11\u7684\u5e74\u9f84\uff0c\u4ed6\u968f\u65f6\u90fd\u53ef\u80fd\u79bb\u5f00\uff0c\u800c\u4e14\u6211\u4e5f\u6709\u529e\u6cd5\u968f\u65f6 \u8ba9\u4ed6\u8d70\u8def\u3002\u201d \u3000\u3000\u5176\u5b9e\u8881\u5c0f\u9e64\u65b9\u624d\u53ea\u662f\u5e94\u4ed8\u51e4\u5ae3\u7ea2\uff0c\u4ed6\u5f53\u7136\u4e0d\u60f3\u7559\u5728\u8fd9\u540d\u6deb\u8d31\u65e0\u803b\u7684\u5973\u4eba\u8eab\u8fb9\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u4f3c\u4e4e\u4e5f\u770b\u51fa\u8881\u5c0f\u9e64\u7684\u5fc3\u610f\uff0c\u4e5f\u5c31\u4e0d\u518d\u63d0\u8fd9\u4ef6\u4e8b\uff0c\u4f46\u53e6\u4e00\u65b9\u9762\uff0c\u5374\u53c8\u4e0d\u4f4f\u5411\u8881\u5c0f\u9e64 \u529d\u9152\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u7ecf\u4e0d\u4f4f\u5979\u7684\u6bb7\u52e4\u76f8\u529d\uff0c\u53ea\u597d\u52c9\u5f3a\u4e00\u676f\u4e00\u676f\u7684\u5e72\u3002 \u3000\u3000\u4e00\u987f\u9152\u996d\uff0c\u8db3\u8db3\u5403\u5230\u5c06\u8fd1\u4e8c\u66f4\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u53ea\u89c9\u56db\u80a2\u65e0\u529b\uff0c\u5934\u91cd\u811a\u8f7b\uff0c\u8111\u5b50\u91cc\u660f\u660f\u6c89\u6c89\uff0c\u6839\u672c\u5df2\u65e0\u6cd5\u884c\u52a8\uff0c\u60c5\u4e0d\u81ea\u7981\u4f0f\u4e0a\u4e86 \u684c\u5b50\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u968f\u5373\u5173\u4e0a\u5ba2\u5385\u7684\u95e8\uff0c\u504e\u5230\u8881\u5c0f\u9e64\u8eab\u8fb9\uff0c\u4f38\u51fa\u7ea4\u7ea4\u7389\u624b\uff0c \u3000\u3000\u642d\u4e0a\u4e86\u4ed6\u7684\u80a9\u5934\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u9e64\uff0c\u771f\u7684\u559d\u9189\u4e86\u5417\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u53ea\u611f\u6726\u6726\u80e7\u80e7\u7684\uff0c\u53e3\u9f7f\u4e0d\u6e05\u7684\u9053\uff1a\u201c\u5e08\u5a18\uff0c\u5f1f\u5b50\u2026\u2026\u5f1f\u5b50\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u770b\u4f60\u662f\u65e0\u6cd5\u56de\u53bb\u4e86\uff0c\u5c31\u7559\u5728\u8fd9\u91cc\u7761\u5427\uff01\u201d \u3000\u3000\u5979\u8bf4\u7740\uff0c\u5f20\u8d77\u7c89\u81c2\uff0c\u4fbf\u628a\u8881\u5c0f\u9e64\u7d27\u7d27\u6402\u4e2a\u6ee1\u6000\uff0c\u7531\u5ba2\u5385\u5c4f\u98ce\u4e4b\u540e\u7684\u4e00\u9053\u58c1\u95e8\u8fdb\u5165\u53e6\u4e00\u623f \u95f4\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u8eab\u4e0d\u7531\u5df1\u7684\u88ab\u51e4\u5ae3\u7ea2\u6402\u62b1\u7740\uff0c\u4f46\u5bf9\u6b64\u523b\u7684\u4ed6\u6765\u8bf4\uff0c\u4f3c\u5e73\u5df2\u65e0\u591a\u5927\u7684\u7279\u6b8a\u611f\u53d7\u3002 \u3000\u3000\u8fd9\u95f4\u623f\u95f4\u6b63\u662f\u51e4\u5ae3\u7ea2\u7684\u5367\u5ba4\uff0c\u5e03\u7f6e\u5f97\u534e\u4e3d\u800c\u53c8\u65d6\u65ce\uff0c\u7b80\u76f4\u50cf\u60f3\u8c61\u4e2d\u7684\u516c\u4e3b\u7684\u9999\u95fa\u3002 \u3000\u3000\u5979\u628a\u8881\u5c0f\u9e64\u8f7b\u8f7b\u7684\u653e\u4e0a\u4e86\u5e8a\u69bb\uff0c\u53cc\u7738\u4e2d\u5c04\u51fa\u5947\u5f02\u7684\u5149\uff0c\u7c89\u8138\u51e0\u4e4e\u8d34\u4e0a\u4e86\u8881\u5c0f\u9e64\u7684\u9762\u988a\uff0c \u55f2\u7740\u58f0\u97f3\u9053\uff1a\u201c\u5c0f\u9e64\uff0c\u8981\u4e0d\u8981\u559d\u676f\u8336\u9192\u9192\u9152\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u53ea\u80fd\u8ff7\u6726\u7684\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u5012\u4e86\u4e00\u676f\u6e29\u8336\uff0c\u518d\u628a\u8881\u5c0f\u9e64\u4e0a\u8eab\u6276\u4f4f\uff0c\u4e00\u8fb9\u4e3a\u4ed6\u628a\u8336\u704c\u4e0b\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u53c8\u8eba\u4e86\u4e0b\u53bb\uff0c\u534a\u664c\u4e4b\u540e\uff0c\u5934\u8111\u4f3c\u4e4e\u7565\u7565\u6e05\u9192\uff0c\u4f46\u4f53\u5185\u5374\u4f3c\u4e4e\u6709\u4e86\u5947\u5f02\u7684\u611f\u89c9\u3002 \u3000\u3000\u4ed6\u53ea\u611f\u4e00\u80a1\u5947\u5999\u7684\u70ed\u6c14\uff0c\u7531\u5c0f\u8179\u5185\u5347\u8d77\uff0c\u6e10\u6e10\u53ca\u4e8e\u5168\u8eab\uff0c\u5168\u8eab\u5c31\u50cf\u88ab\u65e0\u6570\u4e0d\u77e5\u540d\u7684\u5c0f\u866b \u5728\u5543\u556e\u7740\uff0c\u523a\u75d2\u5f97\u4ed6\u8d77\u4e86\u9635\u9635\u75c9\u631b\u3002 \u3000\u3000\u5c24\u5176\uff0c\u90a3\u80a1\u70ed\u6c14\uff0c\u6e10\u6e10\u53d8\u6210\u4e86\u4e00\u80a1\u706b\uff0c\u53c8\u56de\u51b2\u5230\u5c0f\u8179\u4e4b\u4e0b\uff0c\u7136\u540e\u751f\u7406\u4e0a\u8d77\u4e86\u6781\u5927\u53d8\u5316\uff0c \u4f3c\u4e4e\u5fc5\u987b\u6c42\u5f97\u53d1\u6cc4\uff0c\u624d\u80fd\u4e3a\u4e4b\u4e00\u5feb\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u53ea\u80fd\u54ac\u7740\u7259\u5f3a\u5fcd\u3002 \u3000\u3000\u53ea\u542c\u51e4\u5ae3\u7ea2\u71d5\u8bed\u58f0\u822c\u7684\u58f0\u97f3\u54cd\u8d77\u5728\u8033\u8fb9\u9053\uff1b\u201c\u5c0f\u9e64\uff0c\u4f60\u89c9\u5f97\u600e\u6837\uff0c\u6211\u5c31\u5728\u4f60\u65c1\u8fb9\u3002\u201d \u3000\u3000\u539f\u6765\uff0c\u51e4\u5ae3\u7ea2\u5df2\u5728\u65b9\u624d\u7684\u90a3\u676f\u8336\u4e2d\uff0c\u63ba\u5165\u4e86\u4e00\u79cd\u53eb\u2018\u767e\u82b1\u6563\u2019\u7684\u5f3a\u70c8\u6625\u836f\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u4e24\u773c\u53d1\u76f4\u7684\u8f6c\u5934\u671b\u53bb\u3002 \u3000\u3000\u53ef\u4e0d\u662f\u5417\uff0c\u51e4\u5ae3\u7ea2\u6b63\u7389\u4f53\u6a2a\u9648\u7684\u504e\u5728\u4ed6\u7684\u8eab\u65c1\u3002 \u3000\u3000\u6b64\u523b\u7684\u51e4\u5ae3\u7ea2\uff0c\u5df2\u5378\u53bb\u7f57\u88f3\uff0c\u6362\u4e0a\u4e00\u88ad\u8584\u5982\u8749\u7ffc\u822c\u7684\u8f7b\u7eb1\uff0c\u90a3\u51f9\u51f8\u5206\u660e\u767d\u5982\u7389\u51dd\u5982\u8102\u7684 \u80a1\u80a4\u82e5\u9690\u82e5\u73b0\uff0c\u5c24\u5176\u662f\u661f\u7738\u534a\u95ed\uff0c\u5a07\u9765\u542b\u6625\uff0c\u4f7f\u5f97\u4ed6\u7b80\u76f4\u5c31\u8981\u4e0d\u987e\u4e00\u5207\u7684\u6251\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u4f46\u4ed6\u7ec8\u7adf\u8fd8\u80fd\u54ac\u7d27\u7259\u5173\uff0c\u6781\u529b\u538b\u6291\u7740\u90a3\u80a1\u70e7\u5f97\u8fd1\u4e8e\u75af\u72c2\u7684\u70c8\u706b\uff0c\u5728\u770b\u8fc7\u4e4b\u540e\uff0c\u968f\u5373\u95ed\u4e0a \u773c\u53bb\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u5c45\u7136\u6709\u8fd9\u6837\u5f3a\u5927\u7684\u8010\u529b\uff0c\u8feb\u5f97\u51e4\u5ae3\u7ea2\u4e0d\u5f97\u4e0d\u8fdb\u4e00\u6b65\u52a0\u5f3a\u5bf9\u4ed6\u7684\u8bf1\u611f\u3002 \u3000\u3000\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5979\u706b\u70ed\u7684\u7c89\u988a\uff0c\u5df2\u8d34\u4e0a\u4e86\u8881\u5c0f\u9e64\u7684\u8138\uff0c\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u5c0f\u9e64\uff0c\u7741\u5f00\u773c\u6765\u518d \u770b\u770b\u6211\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u60c5\u4e0d\u81ea\u7981\u7684\u518d\u7741\u5f00\u773c\u6765\uff0c\u4ed6\u7684\u5fc3\u8d8a\u53d1\u8df3\u52a8\u5f97\u5389\u5bb3\uff0c\u53ea\u89c1\u51e4\u5ae3\u7ea2\u8fde\u90a3\u8584\u8584\u7684\u8f7b\u7eb1\u4e5f \u5378\u53bb\u4e86\uff0c\u4e0a\u8eab\u5df2\u5b8c\u5168\u8d64\u88f8\uff0c\u4e0b\u8eab\u4e5f\u4ec5\u662f\u7740\u4e86\u4e00\u6761\u77ed\u5f97\u4e0d\u80fd\u518d\u77ed\u7684\u4eb5\u88e4\u3002 \u3000\u3000\u4ed6\u5168\u8eab\u6296\u52a8\u7684\u4f4e\u558a\u7740\uff1a\u3000\u201c\u5e08\u2026\u2026\u5e08\u5a18\u2026\u2026\u4f60\u8fd9\u662f\u8981\u505a\u4ec0\u4e48\uff1f\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u9e64\uff0c\u96be\u9053\u4f60\u8fd8\u4e0d\u660e\u767d\uff1f\u4eca\u665a\u5e08\u5a18\u5c31\u662f\u4f60\u7684\u4eba\u4e86\uff0c\u4f60\u8981\u600e\u6837\u5c31\u600e\u6837\u3002\u201d \u3000\u3000\u201c\u5e08\u5a18\uff0c\u4f7f\u2026\u2026\u4e0d\u5f97\u2026\u2026\u8fd9\u662f\u2026\u2026\u4e71\u2026\u2026\u4f26\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u9e64\uff0c\u6211\u73b0\u5728\u53ea\u662f\u4e00\u4e2a\u5973\u4eba\uff0c\u4e0d\u662f\u4f60\u7684\u5e08\u5a18\uff01\u201d \u3000\u3000\u201c\u662f\u2026\u2026\u4f60\u662f\u2026\u2026\u4e0d\u8fc7\u2026\u2026\u82e5\u8fd9\u6837\u4e0b\u53bb\u2026\u2026\u4f60\u5c31\u4e0d\u2026\u2026\u4e0d\u914d\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u9e64\uff0c\u73b0\u5728\u53ea\u6709\u6211\u6551\u5f97\u4e86\u4f60\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u7a81\u7136\u4e24\u773c\u4e00\u76f4\uff0c\u5df2\u660f\u8e76\u8fc7\u53bb\u3002 \u3000\u3000\u5f53\u8881\u5c0f\u9e64\u53e3\u5e72\u820c\u71e5\uff0c\u5934\u8111\u80c0\u75db\u6b32\u88c2\u7684\u9192\u6765\u65f6\uff0c\u5ba4\u5185\u7684\u706f\u5149\u65e9\u5df2\u7184\u706d\uff0c\u9ed1\u6f06\u6f06\u7684\u51e0\u5e73\u4f38\u624b \u4e0d\u89c1\u4e94\u6307\u3002 \u3000\u3000\u4ed6\u53ea\u611f\u5168\u8eab\u9165\u8f6f\u817b\u6ed1\u800c\u53c8\u70ed\u70d8\u70d8\u7684\u611f\u89c9\uff0c\u5206\u660e\u662f\u6709\u4e2a\u5973\u4eba\u7d27\u8d34\u7740\u7761\u5728\u4ed6\u8eab\u8fb9\u3002 \u3000\u3000\u4ed6\u5f88\u5feb\u4fbf\u610f\u8bc6\u5230\u8fd9\u5973\u4eba\u662f\u51e4\u5ae3\u7ea2\uff0c\u4e5f\u60f3\u8d77\u4e86\u6628\u665a\u9152\u9189\u540e\u7684\u79cd\u79cd\u7ecf\u8fc7\u3002 \u3000\u3000\u6478\u4e86\u6478\u81ea\u5df1\u8eab\u4e0a\u3002\u8863\u670d\u8fd8\u7a7f\u5f97\u597d\u597d\u7684\uff0c\u5728\u611f\u89c9\u4e0a\u4f3c\u4e4e\u8fd8\u5c1a\u672a\u5931\u8eab\u3002 \u3000\u3000\u5230\u8fd9\u65f6\u4ed6\u624d\u5f7b\u5e95\u660e\u767d\u8fd9\u5973\u4eba\u662f\u591a\u4e48\u7684\u6deb\u8d31\u65e0\u803b\u3002 \u3000\u3000\u5176\u5b9e\u4e5f\u8bb8\u4ed6\u4e0d\u4f1a\u60f3\u5230\uff0c\u76ee\u524d\u4ed6\u5728\u51e4\u5ae3\u7ea2\u7684\u773c\u4e2d\uff0c\u5df2\u7ecf\u80dc\u8fc7\u4e86\u9a6c\u662d\u96c4\u8bb8\u591a\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u4f46\u6bd4 \u9a6c\u662d\u96c4\u66f4\u5e74\u8f7b\uff0c\u800c\u4e14\u8bba\u8bae\u8868\u66f4\u6bd4\u9a6c\u662d\u96c4\u82f1\u633a\u79c0\u62d4\u800c\u51fa\u8272\uff0c\u4f55\u51b5\u60f3\u548c\u9a6c\u662d\u96c4\u805a\u4f1a\u4e0d\u6613\uff0c\u8fdc\u6c34\u89e3 \u4e0d\u4e86\u8fd1\u6e34\u3002 \u3000\u3000\u5982\u679c\u771f\u7684\u5979\u80fd\u628a\u8881\u5c0f\u9e64\u6536\u4e3a\u7981\u5185\uff0c\u5979\u4e00\u5b9a\u4f1a\u4ece\u6b64\u628a\u9a6c\u662d\u96c4\u629b\u5728\u4e5d\u5bb5\u4e91\u5916\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u89c9\u51fa\u51e4\u5ae3\u7ea2\u6b64\u523b\u7761\u5f97\u6b63\u9163\uff0c\u4f7f\u8f7b\u8f7b\u6380\u5f00\u9526\u88ab\uff0c\u7136\u540e\u518d\u8f7b\u8f7b\u7684\u4e0b\u4e86\u5e8a\u3002 \u3000\u3000\u4ed6\u53ea\u611f\u5934\u91cd\u811a\u8f7b\uff0c\u773c\u524d\u91d1\u661f\u76f4\u5192\uff0c\u51e0\u4e4e\u8981\u652f\u6301\u4e0d\u4f4f\u3002 \u3000\u3000\u6240\u5e78\u51e4\u5ae3\u7ea2\u6628\u665a\u4e5f\u662f\u559d\u591a\u4e86\u9152\uff0e\u4e0d\u81f4\u88ab\u8881\u5c0f\u9e64\u4e0b\u5e8a\u7684\u4e9b\u8bb8\u58f0\u97f3\u60ca\u9192\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u6781\u529b\u6323\u624e\u7740\uff0c\u8dcc\u8dcc\u649e\u649e\u7684\uff0c\u63a5\u8fde\u6084\u6084\u7684\u8fde\u6253\u5f00\u4e09\u9053\u95e8\uff0c\u624d\u79bb\u5f00\u51e4\u5ae3\u7ea2\u6240\u4f4f\u7684\u9662\u843d\u3002 \u3000\u3000\u770b\u5929\u8272\uff0c\u7ea6\u83ab\u5df2\u662f\u56db\u66f4\u5de6\u53f3\uff0c\u8fdc\u8fdc\u671b\u89c1\u5927\u95e8\u5916\u4ecd\u6709\u4eba\u628a\u5b88\u3002 \u3000\u3000\u9664\u4e86\u8d70\u5927\u95e8\uff0c\u53ea\u6709\u4ece\u56f4\u5899\u7ffb\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u56f4\u5899\u8db3\u6709\u4e00\u4e08\u591a\u9ad8\uff0c\u82e5\u5728\u4ee5\u524d\uff0c\u4ed6\u8f7b\u8f7b\u4e00\u8dc3\u4fbf\u53ef\u8dc3\u8fc7\uff0c\u4f46\u73b0\u5728\u4ed6\u5374\u53ea\u6709\u8bbe\u6cd5\u722c\u8fc7\u53bb\u3002 \u3000\u3000\u76f4\u5728\u5899\u4e0b\u57ab\u4e86\u4e0d\u5c11\u77f3\u5934\uff0c\u603b\u7b97\u7ffb\u4e86\u8fc7\u53bb\uff0c\u800c\u4e14\u8fd8\u6454\u5f97\u4e0d\u8f7b\u3002 \u3000\u3000\u5c31\u8fd9\u6837\uff0c\u4ed6\u8d94\u8d94\u8d84\u8d84\u7684\u56de\u5230\u4e94\u5173\u603b\u9547\u7f72\u8859\u3002 \u3000\u3000\u5929\u4e00\u4eae\uff0c\u4e01\u6d9b\u548c\u7518\u9716\u4fbf\u6765\u5230\u5cb3\u5c0f\u98de\u623f\u4e2d\u3002 \u3000\u3000\u4e24\u4eba\u8fde\u5750\u90fd\u4e0d\u5750\uff0c\u4e01\u6d9b\u4fbf\u5148\u5f00\u53e3\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\uff0c\u8fd9\u4e9b\u5929\u60a8\u4e0d\u89c9\u5f97\u95f7\u5f97\u614c\u5417\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e01\u5f53\u5bb6\u7684\u5feb\u522b\u8fd9\u6837\u79f0\u547c\uff0c\u4ece\u524d\u600e\u4e48\u53eb\uff0c\u73b0\u5728\u8fd8\u600e\u4e48\u53eb\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u90a3\u4e48\u5cb3\u516c\u5b50\u5230\u5e95\u95f7\u4e0d\u95f7\u5462\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u95f7\u4e5f\u6ca1\u6709\u529e\u6cd5\uff0c\u83ab\u975e\u4e01\u5f53\u5bb6\u7684\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u60a8\u662f\u5de1\u5173\u4f7f\u8005\uff0c\u6211\u4eec\u4e24\u4eba\u662f\u526f\u4f7f\u8005\uff0c\u5403\u8fc7\u65e9\u996d\u540e\uff0c\u5c31\u7531\u60a8\u5e26\u961f\uff0c\u54b1\u4eec\u5230\u5404\u5173 \u53bb\u5de1\u89c6\u5de1\u89c6\uff0c\u4e00\u6765\u662f\u516c\u4e8b\uff0c\u4e8c\u6765\u4e5f\u53ef\u6d88\u6101\u89e3\u95f7\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u65e9\u6709\u610f\u5230\u5916\u9762\u8d70\u8d70\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u597d\u54a7\uff0c\u4f60\u4eec\u4e8c\u4f4d\u53ef\u53bb\u627e\u8fc7\u8881\u5927\u54e5\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u600e\u4e48\u6ca1\u627e\u8fc7\uff0c\u6211\u4eec\u662f\u5148\u627e\u4e86\u4ed6\u518d\u6765\u627e\u60a8\u3002\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5230\u73b0\u5728\u8fd8\u6ca1\u8d77\u5e8a\uff0c\u61d2\u6d0b\u6d0b\u7684\u597d\u50cf\u4e0d\u8212\u670d\uff0c\u4ed6\u8bf4\u4eca\u5929\u4e0d\u60f3\u51fa\u53bb\uff0c\u8981\u6211\u4eec\u6765\u627e\u60a8\u3002\u201d \u3000\u3000\u4e8e\u662f\u4e09\u4eba\u4e00\u540c\u5230\u9910\u5385\u7528\u9910\uff0c\u7528\u9910\u65f6\u4e5f\u672a\u89c1\u8881\u5c0f\u9e64\u9732\u9762\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u653e\u5fc3\u4e0d\u4e0b\uff0c\u672c\u6765\u8981\u5230\u8881\u5c0f\u9e64\u623f\u4e2d\u63a2\u671b\u63a2\u671b\uff0c\u5374\u88ab\u4e01\u6d9b\u62e6\u4f4f\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u8881\u5c0f\u9e64 \u8bf4\u8fc7\uff0c\u4eca\u5929\u8c01\u90fd\u522b\u6253\u6270\u4ed6\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u597d\u4f5c\u7f62\u3002 \u3000\u3000\u65e9\u9910\u540e\uff0c\u4e09\u4eba\u5404\u81ea\u6362\u4e86\u4e00\u5957\u8863\u670d\uff0c\u5e26\u4e0a\u5175\u5203\u4ee5\u53ca\u5de1\u5173\u6807\u8bc6\u3002 \u3000\u3000\u4ed6\u4eec\u51b3\u5b9a\u5148\u7531\u6c34\u5173\u5de1\u5230\u6587\u5173\uff0c\u7136\u540e\u518d\u5230\u5bb5\u5173\u53bb\u3002 \u3000\u3000\u51fa\u4e86\u4e94\u5173\u8859\u7f72\uff0c\u7b2c\u4e00\u7ad9\u4fbf\u662f\u6c34\u5173\u3002 \u3000\u3000\u8def\u4e0a\uff0c\u4e01\u6d9b\u9053\uff1a\u201c\u516c\u5b50\uff0c\u60a8\u73b0\u5728\u771f\u6709\u529e\u6cd5\u5566\uff0c\u4e0d\u4f46\u505a\u4e86\u5de1\u5173\u4f7f\u8005\uff0c\u800c\u4e14\u627e\u5230\u4e86\u4ee4\u5c0a\u82b1\u526f \u603b\u9547\uff0c\u7236\u5b50\u76f8\u4f1a\uff0c\u53c8\u80fd\u5206\u6d3e\u5728\u4e00\u8d77\uff0c\u771f\u662f\u592a\u597d\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5728\u4e0b\u8fd8\u62dc\u6258\u4e8c\u4f4d\uff0c\u6709\u5173\u5728\u4e0b\u548c\u526f\u603b\u9547\u662f\u7236\u5b50\u7684\u4e8b\uff0c\u9664\u8881\u5927\u54e5\u5916\uff0e\u5343\u4e07\u4e0d\u80fd \u8ba9\u4efb\u4f55\u4eba\u77e5\u9053\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u60a8\u653e\u5fc3\uff0c\u8fd9\u4e2a\u6211\u4eec\u65e9\u5c31\u660e\u767d\uff0c\u4e0a\u6b21\u62db\u8d24\u9986\u4e3b\u996f\u884c\u7684\u65f6\u5019\uff0c\u6211\u4eec\u4e0d\u662f\u4e00\u76f4\u88c5\u5f97 \u5f88\u597d\u5417\uff1f\u5b9e\u5bf9\u60a8\u8bf4\uff0c\u6211\u4eec\u4e24\u4e2a\u8fd9\u6b21\u80fd\u8c03\u5230\u4e94\u5173\u603b\u9547\u62c5\u4efb\u526f\u4f7f\u8005\uff0c\u4e5f\u5168\u662f\u82b1\u526f\u603b\u9547\u5e2e\u7684\u5fd9\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4f60\u4eec\u611f\u6fc0\u5bb6\u7236\uff0c\u5bb6\u7236\u4e5f\u7167\u6837\u611f\u6fc0\u4e8c\u4f4d\uff0c\u8fde\u5c0f\u5f1f\u4e5f\u4e0d\u4f8b\u5916\uff0c\u82e5\u4e0d\u662f\u4e8c\u4f4d\u5e26\u8def \u6765\u5230\u5929\u8c37\uff0c\u6211\u4eec\u7236\u5b50\u53c8\u600e\u80fd\u76f8\u4f1a\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\u5df2\u5230\u8fbe\u6c34\u5173\u3002 \u3000\u3000\u5b88\u5173\u7684\u4e00\u540d\u7eff\u8863\u5c11\u5973\u4e00\u89c1\u4e09\u4eba\u4f69\u6234\u7740\u5de1\u5173\u4f7f\u8005\u548c\u526f\u4f7f\u8005\u7684\u6807\u8bc6\uff0c\u8fde\u5fd9\u8fdb\u5185\u901a\u62a5\u3002 \u3000\u3000\u6c34\u5173\u7edf\u9886\u5f88\u5feb\u4fbf\u4eb2\u81ea\u6765\u8fce\u63a5\u3002 \u3000\u3000\u8fd9\u65f6\u5cb3\u5c0f\u98de\u7b49\u4e09\u4eba\u5df3\u77e5\u6c34\u5173\u7edf\u9886\u53eb\u7504\u79c0\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u4f4d\u6700\u5e74\u8f7b\u7684\u7edf\u9886\uff0c\u53ea\u6709\u5341\u516b\u3001\u4e5d\u5c81\uff0c\u4e0d\u4f46\u6c14\u8d28\u9ad8\u96c5\uff0c\u8c8c\u7f8e\u5982\u4ed9\uff0c\u6700\u96be\u80fd\u53ef\u8d35\u7684\u662f \u534a\u70b9\u4e0d\u6446\u67b6\u5b50\uff0c\u524d\u6b21\u5cb3\u5c0f\u98de\u7b49\u56db\u4eba\u95ef\u5173\u65f6\uff0c\u8fd8\u53ea\u5f53\u5979\u662f\u4e00\u4f4d\u666e\u901a\u7684\u7eff\u8863\u5c11\u5973\u5462\u3002 \u3000\u3000\u7504\u79c0\u7b11\u5bb9\u53ef\u63ac\u7684\u8fce\u4e86\u51fa\u6765\u9053\uff1a\u201c\u4e09\u4f4d\u521a\u8fc7\u5173\u4e0d\u4e45\uff0c\u4fbf\u8363\u4efb\u65b0\u804c\uff0c\u771f\u662f\u53ef\u559c\u53ef\u8d3a\uff01\u201d \u3000\u3000\u968f\u5373\u628a\u4e09\u4eba\u8bf7\u5165\u77f3\u5ba4\u5185\uff0c\u4eb2\u81ea\u6c8f\u8336\u62db\u5f85\u3002 \u3000\u3000\u7504\u79c0\u7684\u786e\u5bf9\u5cb3\u5c0f\u98de\u7b49\u4e09\u4eba\u5927\u6709\u597d\u611f\uff0c\u5979\u8f7b\u8f7b\u5e74\u7eaa\uff0c\u5f85\u4eba\u5904\u4e8b\u5b9e\u5728\u6bd4\u706b\u5173\u7edf\u9886\u8427\u7476\u597d\u5f97\u592a \u591a\u4e86\uff0c\u602a\u4e0d\u5f97\u4eba\u5bb6\u4e0d\u5230\u4e8c\u5341\u5c81\u4fbf\u80fd\u5f53\u4e0a\u6c34\u5173\u7edf\u9886\u3002 \u3000\u3000\u5979\u672c\u60f3\u62db\u5f85\u5cb3\u5c0f\u98de\u7b49\u4e09\u4eba\u5728\u6c34\u5173\u5403\u996d\uff0c\u56e0\u4e3a\u79bb\u5348\u65f6\u5c1a\u65e9\uff0c\u53ea\u597d\u4f5c\u7f62\u3002 \u3000\u3000\u6b63\u56e0\u4e3a\u7504\u79c0\u62db\u5f85\u6bb7\u52e4\u4eb2\u5207\uff0c\u5cb3\u5c0f\u98de\u7b49\u53cd\u800c\u4e0d\u597d\u610f\u601d\u591a\u6253\u6270\u3002\u4e0b\u4e00\u5173\u6765\u5230\u706b\u5173\u3002 \u3000\u3000\u706b\u5173\u7edf\u9886\u8427\u7476\u542c\u8bf4\u5cb3\u5c0f\u98de\u6765\u4e86\uff0c\u7d22\u6027\u79f0\u75c5\u4e0d\u51fa\uff0c\u4e0d\u8fc7\u8fd8\u662f\u6d3e\u4e86\u4e00\u540d\u624b\u4e0b\u8d1f\u8d23\u63a5\u5f85\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7b49\u5e76\u672a\u591a\u5446\u4fbf\u8f6c\u5f80\u4e86\u6b66\u5173\u3002 \u3000\u3000\u6b66\u5173\u7edf\u9886\u9a7c\u80cc\u8001\u4eba\u67b6\u5b50\u4e5f\u662f\u5927\u5f97\u5f88\uff0c\u4e0d\u4f46\u767d\u5df1\u6ca1\u51fa\u6765\uff0c\u8fde\u63a5\u5f85\u7684\u4eba\u4e5f\u4e0d\u6d3e\u51fa\uff0c\u5bf9\u4ed6\u4eec\u4e09 \u4f4d\u5de1\u5173\u4f7f\u8005\u3001\u526f\u4f7f\u8005\uff0c\u6839\u672c\u4e0d\u7406\u4e0d\u776c\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u3001\u3000\u4e01\u6d9b\u3001\u7518\u9716\u5bf9\u9a7c\u80cc\u8001\u4eba\u90a3\u4e9b\u4f5c\u6cd5\uff0c\u4e5f\u9887\u80fd\u8c05\u89e3\uff0c\u56e0\u4e3a\u6b66\u5173\u4e4b\u5185\uff0c\u85cf\u9f99\u5367\u864e\uff0c \u90fd\u662f\u6b66\u6797\u4e2d\u8d6b\u8d6b\u6709\u540d\u7684\u524d\u8f88\u4eba\u7269\uff0c\u751a\u81f3\u4e0d\u4e4f\u9ad8\u4eba\u5947\u58eb\uff0c\u4ed6\u4eec\u4e00\u5411\u90fd\u6781\u4e3a\u81ea\u8d1f\uff0c\u5c82\u80af\u628a\u5cb3\u5c0f\u98de \u548c\u7518\u9716\u3001\u4e01\u6d9b\u653e\u5728\u773c\u91cc\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u51b3\u5b9a\u6765\u4e2a\u8fc7\u5173\u4e0d\u5165\uff0c\u76f4\u63a5\u8d76\u5f80\u6587\u5173\u3002 \u3000\u3000\u6765\u5230\u6587\u5173\uff0c\u5b88\u5173\u7684\u662f\u4f4d\u4e2d\u5e74\u5927\u6c49\u3002 \u3000\u3000\u4e2d\u5e74\u5927\u6c49\u2014\u89c1\u4e09\u4eba\u6807\u8bc6\uff0c\u4fbf\u8fce\u4e0a\u524d\u6765\u9053\uff1a\u201c\u4e09\u4f4d\u8bf7\u968f\u5728\u4e0b\u5230\u91cc\u9762\u5750\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b49\u53ea\u9053\u4ed6\u8981\u5e26\u81ea\u5df1\u53bb\u89c1\u7edf\u9886\u5218\u59d3\u8001\u4eba\uff0c\u8c01\u77e5\u5374\u8fdb\u5165\u4e00\u95f4\u5341\u5206\u7b80\u964b\u7684\u77f3\u5ba4\uff0c\u9664\u4e86\u6709 \u51e0\u4e2a\u77f3\u58a9\u53ef\u4ee5\u5750\u4e0b\u4e4b\u5916\uff0c\u8fde\u8336\u6c34\u90fd\u6ca1\u6709\u4f9b\u5e94\u7684\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u5927\u6c49\u966a\u7740\u7b11\u9053\uff1a\u201c\u4e09\u4f4d\u6765\u5f97\u51d1\u5de7\uff0c\u6211\u4eec\u7edf\u9886\u65e0\u6cd5\u4eb2\u81ea\u8fc7\u6765\u62db\u5f85\uff0c\u53ea\u597d\u59d4\u5c48\u5404\u4f4d\u6682 \u65f6\u5728\u8fd9\u91cc\u5750\u5750\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u6123\u9053\uff1a\u201c\u83ab\u975e\u5218\u7edf\u9886\u75c5\u4e86\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u4eba\u6c49\u4e00\u6123\u9053\uff1a\u201c\u522b\u770b\u6211\u4eec\u7edf\u9886\u5e74\u7eaa\u5927\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u6b66\u529f\uff0c\u53ef\u662f\u4ed6\u8001\u4eba\u5bb6\u4ece\u6765\u6ca1\u75c5\u8fc7\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u90a3\u4ed6\u662f\u6709\u4e8b\u5728\u5fd9\u4e86\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u5927\u6c49\u9053\uff1a\u201c\u4e0d\u7792\u5cb3\u4f7f\u8005\uff0c\u4ed6\u662f\u5728\u63a5\u5f85\u53e6\u5916\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u53ea\u542c\u4e01\u6d9b\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u7684\u5927\u4eba\u7269\u8fd9\u6837\u91cd\u8981\uff0c\u96be\u9053\u6211\u4eec\u5c31\u4e0d\u503c\u5f97\u63a5\u5f85\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u5927\u6c49\u53c8\u966a\u7b11\u9053\uff1a\u201c\u4e01\u526f\u4f7f\u8005\u522b\u53d1\u813e\u6c14\uff0c\u6211\u4eec\u7edf\u9886\u73b0\u5728\u62db\u5f85\u7684\u4e0d\u662f\u4f4d\u7b80\u5355\u4eba\u7269\uff0c\u5373\u4fbf \u73b0\u5728\u662f\u4e94\u5173\u7684\u82b1\u526f\u603b\u9547\u6765\uff0c\u4e5f\u5fc5\u987b\u62c5\u5f85\u4e00\u4e8c\u3002\u201d \u3000\u3000\u8fd9\u4e0b\u53ef\u628a\u4e01\u6d9b\u6014\u4f4f\u4e86\uff0c\u987f\u4e86\u987f\u9053\uff1a\u201c\u8fd9\u4eba\u5230\u5e95\u662f\u8c01\uff1f\u4f60\u8bf4\u51fa\u6765\u4e0d\u5c31\u7ed3\u4e86\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5927\u6c49\u9053\uff1a\u201c\u662f\u4e09\u516c\u4e3b\uff01\u201d \u3000\u3000\u4e01\u6d9b\u679c\u771f\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5cb3\u5c0f\u98de\u548c\u7518\u9716\u4e5f\u90fd\u4e3a\u4e4b\u5fc3\u795e\u4e00\u9707\uff0c\u4ed6\u4eec\u53ea\u89c1\u8fc7\u5927\u516c\u4e3b\uff0c\u81f3\u4e8e\u4e09\u516c \u4e3b\uff0c\u8fde\u542c\u8bf4\u4e5f\u6ca1\u542c\u8bf4\u8fc7\u3002 \u3000\u3000\u534a\u664c\uff0c\u5cb3\u5c0f\u98de\u624d\u9053\uff1a\u201c\u4e3b\u8003\u5b98\u6768\u5148\u751f\u53ef\u5728\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u5927\u6c49\u9053\uff1a\u201c\u5728\u4e0b\u5012\u5fd8\u4e86\uff0c\u5c31\u8be5\u628a\u6768\u8001\u5148\u751f\u8bf7\u51fa\u6765\u966a\u966a\u5404\u4f4d\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u8bdd\u521a\u8981\u53bb\u8bf7\u6768\u51e4\u5802\uff0c\u5374\u53c8\u88ab\u4e01\u6d9b\u558a\u4f4f\u9053\uff1a\u201c\u6162\u7740\uff0c\u6768\u8001\u5934\u513f\u73b0\u5728\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u5927\u6c49\u9053\uff1a\u201c\u6768\u8001\u5148\u751f\u6574\u5929\u9664\u4e86\u770b\u4e66\uff0c\u8fd8\u662f\u770b\u4e66\uff0c\u4ed6\u90a3\u77f3\u5ba4\u91cc\uff0c\u56db\u9762\u90e8\u662f\u4e66\u67b6\u5b50\uff0c\u4e0a \u9762\u81f3\u5c11\u6446\u7740\u4e0a\u5343\u672c\u7684\u4e66\uff0c\u6bcf\u6b21\u5230\u4ed6\u623f\u91cc\u53bb\uff0c\u4ed6\u90fd\u54bf\u54bf\u554a\u554a\u7684\u4e0d\u77e5\u5728\u5ff5\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u7b11\u9053\uff1a\u201c\u96be\u9053\u4f60\u4e00\u53e5\u4e5f\u542c\u4e0d\u61c2\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u5927\u6c49\u9053\uff1a\u201c\u542c\u5f97\u61c2\u4f46\u5374\u4e0d\u77e5\u4ec0\u4e48\u610f\u601d\uff0c\u6709\u65f6\u662f\u5b50\u66f0\uff0c\u6709\u65f6\u662f\u8d75\u94b1\u5b59\u674e\uff0c\u6709\u65f6\u662f\u5929\u5730 \u7384\u9ec4\uff0c\u6709\u65f6\u662f\u4eba\u4e4b\u521d\uff0c\u6709\u65f6\u662f\u9ece\u660e\u5373\u8d77\uff0c \u3000\u3000\u6709\u65f6\u662f\u5f1f\u5b50\u89c4\u5723\u4eba\u8bad\uff0c\u6709\u65f6\u662f\u81e3\u4eae\u8a00\uff0c\u6709\u65f6\u662f\u4eba\u751f\u51e0\u4f55\uff0c\u6709\u65f6\u662f\u6625\u7720\u4e0d\u89c9\u6653\uff0c\u6709\u65f6\u661f\u4e91 \u6de1\u98ce\u8f7b\u8fd1\u5348\u5929\uff0c\u6709\u65f6\u662f\u6012\u53d1\u51b2\u51a0\u2026\u2026\u201d \u3000\u3000\u4e01\u6d9b\u6447\u6447\u624b\u9053\uff1a\u201c\u522b\u518d\u8bb2\u5566\uff0c\u4f60\u80fd\u80cc\u8fd9\u4e48\u591a\uff0c\u5df2\u7ecf\u592a\u4e0d\u7b80\u5355\uff0c\u54b1\u7167\u6837\u4e5f\u662f\u4e00\u53e5\u6ca1\u542c\u61c2\uff01\u201d \u3000\u3000\u4e2d\u5e74\u5927\u6c49\u8d70\u540e\uff0c\u7518\u9716\u9053\uff1a\u201c\u8001\u4e86\uff0c\u4f60\u9760\u80cc\u5566\uff01\u201d \u3000\u3000\u4e01\u6d9b\u4e0d\u77e5\u7518\u9716\u7528\u53f0\u6e7e\u8bdd\u9a82\u4eba\uff0c\u56de\u5934\u671b\u4e86\u671b\u9053\uff1a\u201c\u5976\u5976\u7684\uff0c\u8fd9\u4e0d\u662f\u5e9f\u8bdd\u5417\uff1f\u5750\u5728\u77f3\u58a9\u4e0a\uff0c \u90a3\u91cc\u6765\u7684\u9760\u80cc\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u4f60\u9760\u8170\u5566\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9a82\u9053\uff1a\u201c\u4e0d\u80fd\u9760\u80cc\uff0c\u90a3\u91cc\u6765\u7684\u9760\u8170\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u6211\u7ed9\u4f60\u5403\u4e2a\u70c2\u8549\uff01\u201d \u3000\u3000\u4e01\u6d9b\u4f38\u51fa\u4e4e\u638c\u9053\uff1b\u201c\u62ff\u6765\uff0c\u62ff\u4e0d\u51fa\u6765\u4f60\u662f\u738b\u516b\u86cb\uff01\u8001\u5b50\u73b0\u5728\u8fde\u8336\u90fd\u6ca1\u7684\u559d\uff0c\u6765\u4e2a\u6c34\u679c\u5f53 \u7136\u66f4\u597d\uff01\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u8001\u5b50\u68d2\u8d5b\u7ed9\u4f60\u5403\uff01\u201d \u3000\u3000\u4e01\u6d9b\u543c\u9053\uff1a\u201c\u4f60\u4ed6\u5976\u5976\u7684\u75af\u4e86\uff1f\u5e72\u561b\u5ffd\u7136\u8bf4\u8bdd\u98a0\u4e09\u5012\u56db\u7684\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u6211\u662f\u770b\u4f60\u521a\u624d\u5728\u90a3\u4f4d\u8001\u5144\u9762\u524d\u81ea\u4ee5\u4e3a\u5f53\u4e86\u4e00\u540d\u526f\u4f7f\u8005\u5c31\u4e86\u4e0d\u8d77\uff0c\u76f4\u5230\u542c\u8bf4\u62db \u5f85\u7684\u662f\u4e09\u516c\u4e3b\u624d\u50bb\u5b50\u773c\uff0c\u8001\u4e01\uff0c\u6211\u770b\u4f60\u4ee5\u540e\u6700\u597d\u8c26\u865a\u4e00\u4e9b\uff0c\u4eba\u5bb6\u5cb3\u516c\u5b50\u7684\u5b98\u513f\u6bd4\u4f60\u5927\uff0c\u4ed6\u50cf \u4f60\u90a3\u6837\u81ea\u4ee5\u4e3a\u4e86\u4e0d\u8d77\u6ca1\u6709\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u50bb\u6123\u6123\u7684\u9053\uff1a\u201c\u539f\u6765\u662f\u4e3a\u4e86\u8fd9\u4e2a\uff0c\u90a3\u53c8\u4f55\u5fc5\u9760\u80cc\u9760\u8170\u70c2\u8549\u7684\uff0c\u8bf4\u4e86\u4e00\u5927\u5806\u8001\u5b50\u542c\u4e0d \u61c2\u7684\u5e9f\u8bdd\uff0c\u4f60\u521a\u624d\u4e0d\u662f\u53d1\u4e86\u75af\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u6768\u51e4\u5802\u5df2\u624b\u6301\u65f1\u70df\u888b\u6447\u5934\u6643\u8111\u7684\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4ed6\u4ecd\u662f\u4e00\u88ad\u957f\u888d\uff0c\u8eab\u7f69\u9a6c\u8902\uff0c\u5934\u6234\u74dc\u76ae\u5c0f\u5e3d\uff0c\u9f3b\u6881\u4e0a\u67b6\u7740\u4e00\u526f\u8001\u82b1\u773c\u955c\uff0e\u8fb9\u8d70\u8fb9\u541f\u548f\u7740 \u9053\uff1a\u201c\u6b63\u5728\u628a\u4e66\u770b\uff0c\u5ffd\u62a5\u5ba2\u6765\u63a2\uff0c\u539f\u6765\u662f\u4e09\u4f4d\uff0c\u96be\u5f97\u53c8\u76f8\u89c1\uff0c\u524d\u56de\u597d\u6210\u7ee9\uff0c\u4eca\u79d1\u4e2d\u72b6\u5143\uff0c\u8001 \u673d\u76f8\u8fce\u8fdf\uff0c\u4f60\u4eec\u591a\u5305\u6db5\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b49\u4eba\u8fde\u5fd9\u8d77\u8eab\u76f8\u8fce\u3002 \u3000\u3000\u4ed6\u4eec\u4e09\u4eba\u6b64\u523b\u65e9\u5df3\u77e5\u9053\uff0c\u6768\u8001\u5934\u513f\u5728\u6587\u5173\u662f\u4f4d\u8eab\u4efd\u5341\u5206\u7279\u6b8a\u7684\u4eba\u7269\u3002\u4ed6\u867d\u7136\u8bba\u5730\u4f4d\u5728\u7edf \u9886\u4e4b\u4e0b\uff0c\u4f46\u7edf\u9886\u5374\u5bf9\u4ed6\u4ee5\u4e0a\u5bbe\u4e3b\u793c\u76f8\u5f85\uff0c\u56e0\u4e3a\u4ed6\u7684\u5b66\u95ee\uff0e\u5373\u4f7f\u5728\u6574\u4e2a\u5929\u8c37\uff0c\u4e5f\u627e\u4e0d\u51fa\u51e0\u4f4d\uff0c \u4ee5\u6587\u5173\u6765\u8bf4\uff0c\u5b9e\u9645\u4e0a\u662f\u4ed6\u5728\u5531\u4e3b\u89d2\u620f\uff0c\u82e5\u6587\u5173\u6ca1\u6709\u4ed6\uff0c\u6839\u672c\u5c31\u4e0d\u6210\u6587\u5173\u4e86\u3002 \u3000\u3000\u867d\u7136\u6587\u5173\u7edf\u9886\u5218\u7ee7\u5fb7\u4e5f\u662f\u4f4d\u9971\u5b66\u58eb\uff0c\u4f46\u6bd4\u8d77\u4ed6\u6765\uff0c\u603b\u662f\u5dee\u4e86\u4e00\u4e9b\u3002 \u3000\u3000\u6768\u51e4\u5802\u62db\u547c\u7740\u4e09\u4eba\u91cd\u65b0\u5f52\u5ea7\uff0c\u518d\u5ff5\u5ff5\u6709\u8bcd\u9053\uff1a\u201c\u5f97\u610f\u95e8\u751f\u5347\u4e86\u5b98\uff0c\u4f60\u4eec\u7b97\u5f97\u9752\u51fa\u76d1\uff0c\u4eca \u5929\u6765\u7684\u56db\u7f3a\u4e00\uff0c\u59d3\u8881\u90a3\u4e2a\u600e\u4e0d\u89c1\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u6839\u672c\u6ca1\u542c\u51fa\u6768\u51e4\u5802\u5728\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u5cb3\u5c0f\u98de\u7acb\u5373\u62f1\u624b\u7b54\u9053\uff1a\u201c\u591a\u8499\u8001\u5148\u751f\u6302\u6000\uff0c \u90a3\u4f4d\u8881\u4f7f\u8005\uff0c\u56e0\u4e3a\u6709\u4e8b\uff0c\u6ca1\u80fd\u548c\u665a\u8f88\u4e00\u8d77\u6765\u62dc\u89c1\u8001\u5148\u751f\u3002\u201d \u3000\u3000\u4e01\u6d9b\u5927\u611f\u4e00\u6123\uff0c\u671b\u7740\u5cb3\u5c0f\u98de\u4f4e\u58f0\u9053\uff1b\u201c\u5cb3\u516c\u5b50\uff0c\u8001\u5148\u751f\u6839\u672c\u6ca1\u63d0\u8881\u5c11\u4fa0\uff0c\u60a8\u600e\u4e48\u8ddf\u4ed6\u8bf4 \u8fd9\u4e9b\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fd8\u6ca1\u6765\u5f97\u53ca\u5f00\u53e3\uff0c\u6768\u51e4\u5802\u4fbf\u77a5\u4e86\u4e01\u6d9b\u4e00\u773c\u541f\u9053\uff1a\u201c\u4f60\u7684\u8033\u6735\u585e\u4e86\u6bdb\uff0c\u8001\u673d\u541f\u8bd7\u542c \u4e0d\u5230\uff0c\u5cb3\u5c0f\u5144\u5f1f\u53cd\u5e94\u597d\uff0c\u53ef\u89c1\u4ed6\u662f\u6bd4\u4f60\u9ad8\uff01\u201d \u3000\u3000\u8fd9\u6b21\u4e01\u6d9b\u603b\u7b97\u542c\u61c2\u4e86\uff0c\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u5927\u4e3b\u8003\uff0c\u60a8\u8ddf\u6211\u4eec\u8bf4\u8bdd\u597d\u4e0d\u597d\uff0c\u6211\u4eec\u4e0d\u662f\u5230\u5531 \u8bd7\u73ed\u6765\u542c\u60a8\u5531\u8bd7\u7684\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u53c8\u541f\u9053\uff1a\u201c\u541f\u8bd7\u8bf4\u8bdd\u7686\u4e00\u822c\uff0c\u6709\u4e86\u5b66\u95ee\u5c31\u4e0d\u96be\uff0c\u529d\u4f60\u597d\u597d\u628a\u4e66\u5ff5\uff0c\u4ece\u6b64\u4e0d\u505a\u7cca\u6d82 \u86cb\uff01\u201d \u3000\u3000\u7518\u9716\u7b11\u9053\uff1a\u201c\u5927\u4e3b\u8003\uff0c\u60a8\u8001\u4eba\u5bb6\u522b\u518d\u9017\u4ed6\u4e86\uff0c\u4ed6\u672c\u6765\u5c31\u662f\u4e2a\u7cca\u6d82\u86cb\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u4ed6\u5c31\u8d8a \u53d1\u7cca\u6d82\u4e86\uff01\u201d \u3000\u3000\u6768\u51e4\u5802\u7ec8\u4e8e\u89c1\u597d\u5c31\u6536\uff0c\u8f6c\u5934\u5411\u6d1e\u5916\u53eb\u9053\uff1a\u201c\u6253\u8336\u6765\uff01\u201d \u3000\u3000\u6d1e\u5916\u679c\u7136\u6709\u540d\u5927\u6c49\u5e94\u58f0\u3002 \u3000\u3000\u4f46\u4e0d\u5927\u4e00\u4f1a\uff0c\u7aef\u7740\u8336\u76d8\u8fdb\u6765\u7684\uff0c\u5374\u662f\u4e00\u540d\u5a40\u5a1c\u591a\u59ff\u7684\u5c11\u5973\u3002 \u3000\u3000\u90a3\u5c11\u5973\u5728\u9f50\u4eba\u9762\u524d\u653e\u4e86\u4e00\u676f\u8336\uff0c\u4fbf\u4e0d\u58f0\u4e0d\u54cd\u800c\u53bb\u3002 \u3000\u3000\u4e01\u6d9b\u559d\u4e86\u53e3\u8336\uff0c\u5482\u5482\u5634\u9053\uff1a\u201c\u771f\u602a\uff0c\u540c\u6837\u7684\u8336\uff0c\u59d1\u5a18\u4eec\u7aef\u6765\uff0c \u3000\u3000\u5c31\u6bd4\u7b28\u624b\u7b28\u811a\u7684\u7537\u4eba\u4eec\u7aef\u6765\u7684\u597d\u559d\u5f97\u591a\uff01\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u8fd9\u6837\u770b\u6765\uff0c\u4f60\u662f\u6c38\u8fdc\u4e0d\u591f\u8d44\u683c\u7aef\u8336\u4e86\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u543c\u9053\uff1a\u201c\u53bb\u4f60\u5976\u5976\u7684\uff0c\u54b1\u4ec0\u4e48\u65f6\u5019\u7b28\u624b\u7b28\u811a\u6765\uff1f\u201d \u3000\u3000\u7518\u9716\u7b11\u9053\uff1a\u201c\u8bf4\u4f60\u53cd\u5e94\u4e0d\u5feb\uff0c\u8fd9\u6b21\u53cd\u5e94\u7684\u5012\u771f\u5feb\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4e5f\u563f\u563f\u7b11\u9053\uff1a\u201c\u8fd9\u8981\u770b\u5e72\u4ec0\u4e48\uff0c\u5982\u679c\u662f\u540a\u574e\u513f\uff0c\u5927\u4e3b\u8003\u7684\u5b66\u95ee\u867d\u5927\uff0c\u4e5f\u7167\u6837\u96be\u4e0d\u5012 \u6211\u3002\u201d \u3000\u3000\u201c\u4eba\u5bb6\u5927\u4e3b\u8003\u7684\u5b66\u95ee\uff0c\u53ef\u4ee5\u4e2d\u72b6\u5143\uff0c\u53ef\u4ee5\u505a\u5927\u5b98\uff0c\u4f60\u90a3\u540a\u574e\u513f\u7684\u4e00\u5957\uff0c\u53ef\u80fd\u5f53\u996d\u5403\uff1f\u201d \u3000\u3000\u201c\u867d\u7136\u4e0d\u80fd\u5f53\u996d\u5403\uff0c\u7167\u6837\u4e5f\u5728\u6587\u5173\u8fc7\u4e86\u5173\uff0c\u8fde\u5927\u4e3b\u8003\u90fd\u627f\u8ba4\u8fd9\u662f\u5b66\u95ee\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd \u8bb2\uff1f\u201d \u3000\u3000\u5374\u542c\u6768\u51e4\u5802\u9053\uff1a\u201c\u4e01\u8001\u5f1f\uff0c\u54b1\u4eec\u518d\u540a\u540a\u5982\u4f55\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u60a8\u8001\u4eba\u5bb6\u5df2\u7ecf\u8003\u8fc7\u665a\u8f88\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u518d\u8003\uff1f\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u4ece\u90a3\u6b21\u4ee5\u540e\uff0c\u8001\u673d\u7684\u786e\u4e5f\u8ba4\u4e3a\u540a\u574e\u513f\u662f\u95e8\u5b66\u95ee\uff0c\u6240\u4ee5\u8fd1\u4e9b\u5929\u5230\u5904\u6253\u542c\u8fd8\u6709 \u4ec0\u4e48\u574e\u513f\uff0c\u53e6\u5916\u4e5f\u5c3d\u91cf\u641c\u96c6\u8fd9\u4e00\u7c7b\u7684\u53c2\u8003\u4e66\uff0c\u603b\u7b97\u53c8\u77e5\u9053\u4e86\u4e0d\u5c11\uff0c\u73b0\u5728\u54b1\u4eec\u968f\u4fbf\u8bd5\u8bd5\uff0c\u770b\u80fd \u4e0d\u80fd\u8003\u5012\u4f60\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4e0d\u7531\u88ab\u5f15\u8d77\u5174\u8da3\uff0c\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u82e5\u8003\u4e0d\u5012\u5462\uff1f\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u8003\u4e0d\u5012\u8001\u673d\u5c31\u51b3\u5b9a\u6536\u4f60\u4e3a\u5f92\uff0c\u6559\u4f60\u4e00\u4e9b\u771f\u6b63\u7684\u5b66\u95ee\u3002\u201d \u3000\u3000\u4e01\u6d9b\u65e9\u5c31\u81ea\u6068\u5c0f\u65f6\u5019\u6ca1\u8bfb\u4e66\uff0c\u8fde\u7518\u9716\u4e5f\u6bd4\u4e0d\u8fc7\uff0c\u542c\u8bf4\u6768\u51e4\u5802\u8981\u6559\u4ed6\u5b66\u95ee\uff0c\u5f53\u7136\u6c42\u4e4b\u4e0d\u5f97\uff0c \u5fd9\u9053\uff1a\u201c\u90a3\u4e48\u5927\u4e3b\u8003\u5c31\u8bf7\u51fa\u9898\u5427\uff01\u201d \u3000\u3000\u6768\u51e4\u5802\u71c3\u4e0a\u65f1\u70df\uff0c\u5438\u4e86\u51e0\u53e3\u9053\uff1a\u3000\u201c\u542c\u7740\uff0c\u575f\u5893\u5730\u91cc\u5439\u5587\u53ed\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u5435\u6b7b\u4eba\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff0c\u201c\u6728\u5320\u6234\u67b7\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u81ea\u4f5c\u81ea\u53d7\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u843d\u6c34\u72d7\u4e0a\u4e86\u5cb8\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6296\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u8001\u592b\u5c11\u59bb\u5bf9\u9762\u5750\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u5404\u60f3\u5404\u7684\u5fc3\u4e8b\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u7a91\u59d0\u513f\u5f00\u4f1a\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u65e0\u7a3d\u4e4b\u8c08\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u6b6a\u5634\u5439\u5587\u53ed\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4e00\u56e2\u90aa\u6c14\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u80e1\u5b50\u4e0a\u8d34\u818f\u836f\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1b\u201c\u6bdb\u75c5\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u72ec\u773c\u9f99\u770b\u620f\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4e00\u76ee\u4e86\u7136\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u516c\u516c\u66ff\u513f\u5ab3\u5987\u51bc\u88e4\u5b50\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4e0d\u6253\u81ea\u62db\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u65b0\u5b66\u6765\u7684\uff0c\u4e5f\u53ea\u6709\u8fd9\u4e48\u591a\u4e86\u3002\u4ed6\u96be\u4e0d\u5012\u4e01\u6d9b\uff0c\u5b9e\u5728\u4e8e\u5fc3\u4e0d\u7518\uff0c\u82e6\u601d\u4e86\u534a\u664c\uff0c\u51b3\u5b9a \u6765\u4e2a\u81ea\u7f16\u7684\uff0c\u6478\u4e86\u6478\u80e1\u5b50\u9053\uff1a\u201c\u8001\u673d\u653e\u5c41\u2014\u2014\u201d \u3000\u3000\u4e01\u6d9b\u76b1\u4e86\u6d53\u7709\u60f3\u4e86\u5f88\u4e45\u624d\u9053\uff1a\u201c\u4e0d\u81ed\uff01\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u4eba\u5403\u4e94\u8c37\u6742\u7cae\uff0c\u653e\u5c41\u90a3\u80fd\u4e0d\u81ed\uff0c\u4e0d\u4fe1\u8001\u673d\u73b0\u5728\u5c31\u653e\u4e2a\u5c41\uff0c\u4f60\u95fb\u95fb\u770b\uff01\u201d \u3000\u3000\u4e01\u6d9b\u63a9\u7740\u9f3b\u5b50\u9053\uff1a\u201c\u5f88\u81ed\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u6447\u5934\u9053\uff1a\u201c\u8001\u673d\u8fd8\u6ca1\u653e\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u5f88\u81ed\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u90a3\u662f\u8bf4\u81ed\u4e0d\u81ed\uff0c\u8bf4\u4e0d\u81ed\u4e5f\u81ed\uff1f\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u90a3\u6709\u8fd9\u79cd\u5473\u9053\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u518d\u4e0d\u5c31\u662f\u60a8\u8001\u4eba\u5bb6\u80fd\u653e\u9999\u5c41\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u5475\u5475\u7b11\u9053\uff1a\u201c\u8d8a\u53d1\u80e1\u626f\uff0c\u522b\u8bf4\u4eba\uff0c\u795e\u4ed9\u4e5f\u653e\u4e0d\u51fa\u9999\u5c41\u6765\u3002\u201d \u3000\u3000\u4e01\u6d9b\u70b9\u5934\u9053\uff1a\u201c\u8bf4\u5f97\u4e5f\u662f\uff0c\u5982\u679c\u60a8\u771f\u6709\u8fd9\u79cd\u672c\u9886\uff0c\u4ee5\u540e\u5c31\u5230\u5904\u653e\u5c41\uff0c\u5c82\u4e0d\u662f\u5230\u5904\u53d7\u6b22 \u8fce\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u65e2\u7136\u7b54\u4e0d\u51fa\uff0c\u90a3\u5c31\u8be5\u5411\u8001\u673d\u62dc\u5e08\u4e86\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u60a8\u8001\u5148\u751f\u8bf7\u5148\u8bf4\u8bf4\uff0c\u5230\u5e95\u574e\u513f\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u5f88\u7b80\u5355\uff0c\u53ea\u6709\u56db\u4e2a\u4e86\u2014\u2014\u6587\u6c14\u51b2\u5929\u3002\u201d \u3000\u3000\u4e01\u6d9b\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u662f\u6587\u6c14\u51b2\u5929\u5462\uff1f\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u60f3\u60f3\uff0c\u8001\u673d\u4e00\u809a\u5b50\u5168\u662f\u5b66\u95ee\uff0c\u653e\u51fa\u5c41\u6765\uff0c\u4e0d\u662f\u6587\u6c14\u51b2\u5929\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u4e0d\u5f97\u4e0d\u670d\uff0c\u8fde\u5fd9\u8dea\u62dc\u5728\u5730\uff0c\u78d5\u4e86\u4e09\u4e2a\u54cd\u5934\u9053\uff1a\u201c\u5e08\u7236\u5728\u4e0a\uff0c\u5f1f\u5b50\u4e01\u6d9b\u5927\u793c\u53c2\u62dc\uff01\u201d \u3000\u3000\u6768\u51e4\u5802\u4e50\u4e0d\u53ef\u652f\u7684\u5c06\u4e01\u6d9b\u6276\u8d77\uff0c\u4ece\u8896\u5b50\u91cc\u6478\u51fa\u4e00\u652f\u7b14\u6765\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e2a\u505a\u89c1\u9762\u793c\uff0c\u56de\u53bb\u4e4b\u540e\uff0c\u5148\u8bfb\u2018\u4eba\u624b\u8db3\u5200\u5c3a\uff0c\u5c71\u6c34\u7531\uff0c\u72d7\u5e74\u7f8a\uff0c \u3000\u3000\u4e00\u8eab\u4e8c\u624b\uff0c\u5927\u5c71\u5c0f\u77f3\uff0c\u5c0f\u732b\u4e09\u53ea\u56db\u53ea\uff0c\u767d\u5e03\u4e94\u5339\u516d\u5339\u2026\u2026\u2019\u4e0d\u61c2\u7684\u8bf7\u5cb3\u5c0f\u5144\u5f1f\u6559\uff0c\u7b49\u8bfb \u5230\u56db\u4e66\u65f6\uff0c\u518d\u6765\u5411\u8001\u673d\u8bf7\u6559\u3002\u201d \u3000\u3000\u4e01\u6d9b\u606d\u606d\u656c\u656c\u7684\u4e24\u624b\u63a5\u8fc7\uff0c\u63e3\u5728\u6000\u91cc\u9053\uff1a\u201c\u5e08\u7236\u653e\u5fc3\uff0c\u5f1f\u5b50\u4e00\u5b9a\u597d\u597d\u8bfb\u4e66\uff01\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u8001\u5148\u751f\uff0c\u5148\u522b\u8c08\u8fd9\u4e2a\uff0c\u8bf7\u544a\u8bc9\u6211\u4eec\uff0c\u4e09\u516c\u4e3b\u4ec0\u4e48\u65f6\u5019\u8d70\uff0c\u6211\u4eec\u4e5f\u597d\u6709\u673a\u4f1a\u89c1 \u5218\u7edf\u9886\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u8fd9\u4e2a\u5f88\u96be\u8bb2\uff0c\u4e09\u516c\u4e3b\u5728\u4e94\u5173\u4e4b\u4e2d\uff0c\u6700\u559c\u6b22\u5230\u6587\u5173\u6765\uff0c\u6709\u65f6\u8fd8\u5728\u8fd9\u91cc\u8fc7\u591c\uff0c \u8fde\u4f60\u4eec\u51e0\u4f4d\u4e5f\u6cbe\u8fc7\u5979\u7684\u5149\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6014\u4e86\u6014\u9053\uff1a\u201c\u665a\u8f88\u4eec\u6cbe\u8fc7\u4e09\u516c\u4e3b\u7684\u4ec0\u4e48\u5149\uff1f\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u4e0a\u6b21\u4f60\u4eec\u513f\u4f4d\u5728\u9f99\u98ce\u9601\u4f4f\u4e86\u4e00\u591c\uff0c\u53c8\u5403\u4e86\u4e00\u987f\u4e16\u6240\u7f55\u89c1\u7684\u7f8e\u5473\uff0c\u96be\u9053\u5c31\u5fd8 \u4e86\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u83ab\u975e\u90a3\u662f\u8fd9\u4e09\u516c\u4e3b\u4ea4\u4ee3\u5173\u4e0a\u5b89\u6392\u7684\uff1f\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u82e5\u6ca1\u6709\u4e09\u516c\u4e3b\u4ea4\u4ee3\uff0c\u8c01\u6562\u64c5\u81ea\u6253\u5f00\u9f99\u98ce\u9601\u62db\u5f85\u5916\u4eba\uff1f\u8c01\u6562\u529e\u90a3\u4e48\u4e00\u684c\u7f8e \u98df\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u4e3a\u4ec0\u4e48\u8981\u5f85\u6211\u4eec\u8fd9\u6837\u597d\uff1f\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u77e5\u9053\u662f\u600e\u4e48\u56de\u4e8b\u4e86\u3002\u201d \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u5341\u516d\u56de\u3000\u6559\u4e3b\u53ec\u89c1 \u3000\u3000\u4e01\u6d9b\u63d2\u5634\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4e0a\u6b21\u5f1f\u5b50\u4eec\u4f4f\u7684\u90a3\u5730\u65b9\uff0c\u5c31\u53eb\u9f99\u51e4\u9601\uff1f\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u673d\u5728\u5173\u597d\u51e0\u5e74\uff0c\u90a3\u5904\u9f99\u51e4\u9601\u603b\u5171\u4e5f\u6ca1\u5f00\u653e\u8fc7\u51e0\u6b21\uff0c\u53ef\u89c1\u4e09\u516c \u4e3b\u2026\u2026\u201d\uff0c\u6b63\u8bf4\u5230\u8fd9\u91cc\uff0c\u3000\u4e00\u540d\u5927\u6c49\u8fdb\u6765\u9053\uff1a\u201c\u7980\u6768\u8001\u5148\u751f\uff0c\u7edf\u9886\u8bf7\u4e09\u4f4d\u4f7f\u8005\uff0c\u526f\u4f7f\u8005\u8fc7 \u53bb\uff01\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u77e5\u9053\u4e86\uff0c\u8001\u673d\u5c31\u5e26\u4ed6\u4eec\u4e09\u4f4d\u8fc7\u53bb\u3002\u201d \u3000\u3000\u79bb\u5f00\u8fd9\u95f4\u77f3\u5ba4\uff0c\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8001\u5148\u751f\uff0c\u4e0a\u6b21\u665a\u8f88\u591a\u6709\u5f97\u7f6a\uff0c\u60a8\u8001\u4eba\u5bb6\u5343\u4e07\u522b\u4ecb\u610f\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u637b\u7740\u5c71\u7f8a\u80e1\u5b50\u7b11\u9053\uff1a\u201c\u90a3\u91cc\u8bdd\uff0c\u7814\u7a76\u5b66\u95ee\uff0c\u514d\u4e0d\u4e86\u8981\u62ac\u6760\uff0c\u771f\u7406\u6108\u8fa9\u6108\u660e\uff0c\u800c\u4e14 \u4e0a\u6b21\u662f\u9519\u5728\u8001\u673d\u3002\u201d \u3000\u3000\u201c\u4f60\u8001\u5148\u751f\u6709\u4ec0\u4e48\u9519\u5462\uff1f\u201d \u3000\u3000\u201c\u6240\u6e2d\u51a0\u8005\u4e94\u516d\u4eba\uff0c\u7ae5\u5e72\u516d\u4e03\u4eba\uff0c\u52a0\u8d77\u6765\u672c\u6765\u5c31\u662f\u5341\u4e8c\u4e09\u4e2a\u4eba\uff0c \u3000\u3000\u8001\u673d\u4e0d\u8fc7\u662f\u6545\u610f\u552c\u4f60\u4eec\u7f62\u4e86\uff0c\u8fd8\u6709\uff0c\u8fd9\u53e5\u8bdd\u4e5f\u5e76\u975e\u5b54\u8001\u592b\u5b50\u8bb2\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5bf9\u6768\u51e4\u5802\u5c45\u7136\u80af\u81ea\u5df1\u8ba4\u9519\uff0c\u81ea\u7136\u9887\u4e3a\u656c\u4f69\uff0c\u5ffd\u7136\u5fc3\u4e2d\u4e00\u52a8\u9053\uff1a\u201c\u5218\u7edf\u9886\u8981\u6211\u4eec\u8fc7 \u53bb\uff0c\u83ab\u975e\u4e09\u516c\u4e3b\u8d70\u4e86\uff1f\u201d \u3000\u3000\u6768\u51e4\u5802\u6447\u5934\u9053\uff1a\u201c\u636e\u8001\u673d\u5224\u65ad\uff0c\u4e00\u5b9a\u8fd8\u6ca1\u8d70\u3002\u201d \u3000\u3000\u201c\u8001\u5148\u751f\u662f\u600e\u6837\u5224\u65ad\u7684\uff1f\u201d \u3000\u3000\u201c\u5982\u679c\u4e09\u516c\u4e3b\u8d70\u4e86\uff0c\u5218\u7edf\u9886\u5c31\u8be5\u4eb2\u81ea\u6765\u770b\u5404\u4f4d\uff0c\u4ed6\u4ece\u6765\u4e0d\u6446\u67b6\u5b50\uff0c\u7528\u4e0d\u7740\u628a\u5404\u4f4d\u8bf7\u5230\u90a3 \u8fb9\u53bb\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u4e09\u516c\u4e3b\u8981\u53ec\u89c1\u665a\u8f88\u4eec\uff1f\u201d \u3000\u3000\u201c\u6709\u8fd9\u79cd\u53ef\u80fd\u3002\u201d \u3000\u3000\u542c\u8bf4\u4e09\u516c\u4e3b\u8981\u53ec\u89c1\uff0c\u4e01\u6d9b\u548c\u7518\u9716\u987f\u65f6\u6709\u4e9b\u7d27\u5f20\uff0c\u4ed6\u4eec\u8d76\u5fd9\u8fb9\u8d70\u8fb9\u6574\u7406\u8863\u670d\u3002 \u3000\u3000\u4e01\u6d9b\u54a7\u5634\u95ee\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u89c1\u8fc7\u5927\u516c\u4e3b\uff0c\u5e74\u7eaa\u5f88\u8f7b\uff0c\u8fd9\u4f4d\u4e09\u516c\u4e3b\uff0c\u4e00\u5b9a\u5e74\u7eaa\u66f4\u8f7b\u4e86\uff1f\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u5e9f\u8bdd\uff0c\u96be\u9053\u59b9\u59b9\u8fd8\u80fd\u6bd4\u59d0\u59d0\u5e74\u7eaa\u5927\uff1f\u201d \u3000\u3000\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\u5f85\u4f1a\u513f\u89c1\u4e86\u4e09\u516c\u4e3b\uff0c\u4f60\u4eec\u4e09\u4f4d\u53ef\u8981\u5343\u4e07\u5c0f\u5fc3\u5e94\u5bf9\uff0c\u4e09\u516c\u4e3b\u5e74\u7eaa\u867d\u5c0f\uff0c \u5374\u9896\u609f\u8fc7\u4eba\uff0c\u8fde\u8001\u673d\u548c\u5979\u8bb2\u8bdd\uff0c\u4e5f\u8981\u5728\u809a\u5b50\u91cc\u5148\u884c\u6253\u597d\u8349\u7a3f\uff0c\u5426\u5219\uff0c\u51c6\u4f1a\u5403\u4e0d\u4e86\u515c\u7740\u8d70\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4f38\u4e86\u4e00\u4e0b\u820c\u5934\u9053\uff1a\u201c\u8fd9\u4e48\u5386\u5bb3\uff0c\u602a\u4e0d\u5f97\u4eba\u5bb6\u80fd\u5f53\u516c\u4e3b\uff0c\u548c\u51e1\u4eba\u5c31\u662f\u4e0d\u540c\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5df2\u6765\u5230\u4e00\u5904\u6d1e\u95e8\uff0c\u6768\u51e4\u5802\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u91cc\u4e86\uff0c\u4f60\u4eec\u968f\u6211\u8fdb\u53bb\uff01\u201d \u3000\u3000\u521a\u8fdb\u5165\u901a\u9053\uff0c\u6587\u5173\u7edf\u9886\u5218\u7ee7\u5fb7\u4fbf\u8fce\u4e86\u51fa\u6765\uff0c\u7b11\u5475\u5475\u7684\u62f1\u7740\u624b\u9053\uff1a\u201c\u8005\u673d\u521a\u624d\u6709\u4e8b\uff0c\u4e0d\u80fd \u4eb2\u81ea\u63a5\u5f85\u5404\u4f4d\uff0c\u5343\u4e07\u591a\u5305\u6db5\uff0c\u5feb\u8bf7\u5230\u91cc\u9762\u5750\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b49\u4e09\u4eba\u8fd9\u51e0\u6b65\u8def\uff0c\u90fd\u8d70\u5f97\u5f88\u8c28\u614e\uff0c\u56e0\u4e3a\u4ed6\u4eec\u77e5\u9053\u4e09\u516c\u4e3b\u5fc5\u5728\u91cc\u9762\u3002 \u3000\u3000\u5c82\u77e5\u8fdb\u5165\u91cc\u9762\u77f3\u5ba4\u540e\uff0c\u5374\u4e0d\u89c1\u534a\u4e2a\u4eba\u5f71\u3002 \u3000\u3000\u5218\u7ee7\u5fb7\u5148\u4e3a\u4e09\u4eba\u6c8f\u8336\uff0c\u548c\u6768\u51e4\u5802\u5750\u5728\u4e00\u8fb9\u76f8\u966a\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u4e09\u516c\u4e3b\u5728\u8fd9\u91cc\uff0c\u662f\u5426\u5df2\u7ecf\u8d70\u4e86\uff1f\u201d \u3000\u3000\u5218\u7ee7\u5fb7\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u8981\u53ec\u89c1\u4e09\u4f4d\uff0c\u600e\u4f1a\u9a6c\u4e0a\u8d70\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u5728\u54ea\u91cc\uff1f\u665a\u8f88\u8fd9\u5c31\u53bb\u62dc\u89c1\u3002\u201d \u3000\u3000\u5218\u7ee7\u5fb7\u9053\uff1a\u201c\u4e09\u4f4d\u5148\u559d\u5b8c\u8336\u518d\u8bf4\uff0c\u514d\u5f97\u89c1\u4e86\u4e09\u516c\u4e3b\u5fc3\u795e\u614c\u5f20\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u628a\u8336\u4e00\u996e\u800c\u5c3d\uff0c\u4ed6\u5bf9\u5218\u7ee7\u5fb7\u6700\u540e\u4e00\u53e5\u8bdd\uff0c\u5185\u5fc3\u9887\u611f\u4e0d\u662f\u6ecb\u5473\uff0c\u4e09\u516c\u4e3b\u8eab\u4efd\u5730\u4f4d\u518d \u9ad8\uff0c\u4e5f\u662f\u4eba\u4e0d\u662f\u795e\uff0c\u89c1\u4e86\u5979\u6709\u4ec0\u4e48\u597d\u614c\u5f20\u7684\uff0c\u8fde\u5927\u516c\u4e3b\u90fd\u89c1\u8fc7\uff0c\u5979\u603b\u4e0d\u80fd\u6bd4\u5927\u516c\u4e3b\u8fd8\u5927\u3002 \u3000\u3000\u4f46\u4e01\u6d9b\u548c\u7518\u9716\u5374\u6545\u610f\u559d\u5f97\u6162\u4e9b\u3002 \u3000\u3000\u5218\u7ee7\u5fb7\u76f4\u7b49\u4e09\u4eba\u5168\u628a\u8336\u559d\u5b8c\uff0c\u624d\u9053\uff1a\u201c\u5404\u4f4d\u8fd9\u5c31\u8fdb\u53bb\u89c1\u4e09\u516c\u4e3b\u5427\uff01\u201d \u3000\u3000\u8c01\u77e5\u4ed6\u5634\u91cc\u8fd9\u6837\u8bf4\uff0c\u5374\u4e0d\u8d77\u8eab\u5e26\u8def\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8f7b\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u6ca1\u4eba\u5e26\u8def\uff0c\u665a\u8f88\u4eec\u600e\u77e5\u4e09\u516c\u4e3b\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5218\u7ee7\u5fb7\u4ecd\u672a\u8d77\u8eab\uff0c\u53ea\u662f\u62ac\u624b\u4e00\u6307\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u5c31\u5728\u91cc\u9762\uff0c\u5979\u8981\u4e09\u4f4d\u81ea\u884c\u8fdb\u53bb\uff0c\u8bf7\u6055\u8001\u673d \u548c\u6768\u5148\u751f\u65e0\u6cd5\u4f5c\u966a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fd9\u624d\u53d1\u89c9\u539f\u6765\u77f3\u58c1\u4e0a\u53e6\u6709\u4e00\u95e8\uff0c\u4e0d\u6d88\u8bf4\u91cc\u9762\u53e6\u6709\u77f3\u5ba4\uff0c \u3000\u3000\u4ed6\u7ad9\u8d77\u8eab\u62db\u547c\u9053\uff1a\u201c\u4e01\u3001\u7518\u4e24\u4f4d\u526f\u4f7f\u8005\uff1f\u8bf7\u968f\u5728\u4e0b\u8fdb\u53bb\uff01\u201d \u3000\u3000\u8fdb\u53bb\u540e\u901a\u9053\u5f88\u957f\uff0c\u76f4\u8d70\u4e86\u4e03\u3001\u516b\u4e08\uff0c\u624d\u5728\u5de6\u8fb9\u51fa\u73b0\u4e86\u95f4\u9887\u4e3a\u5bbd\u655e\u7684\u77f3\u5ba4\u3002 \u3000\u3000\u77f3\u5ba4\u4e0a\u65b9\u70b9\u7740\u4e24\u76cf\u5782\u82cf\u5bab\u706f\uff0c\u7167\u89c1\u77f3\u5ba4\u5185\u5e03\u7f6e\u5f97\u9ad8\u96c5\u800c\u53c8\u534e\u4e3d\uff0c\u5947\u602a\u7684\u662f\u91cc\u9762\u7adf\u7136\u65e0\u4eba\u3002 \u3000\u3000\u8fd9\u91cc\u5206\u660e\u5df2\u5230\u4e86\u5c3d\u5934\uff0c\u90a3\u4e48\u4e09\u516c\u4e3b\u7adf\u5728\u54ea\u91cc\u5462\uff1f \u3000\u3000\u4e01\u6d9b\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u83ab\u975e\u5218\u7edf\u9886\u5f00\u8d77\u54b1\u4eec\u73a9\u7b11\u6765\u4e86\uff1f\u201d \u3000\u3000\u4e09\u4eba\u6b63\u8981\u56de\u53bb\uff0c\u5ffd\u7136\u8033\u9645\u4f20\u6765\u8106\u751f\u751f\u7684\u58f0\u97f3\u9053\uff1a\u201c\u4f60\u4eec\u5f80\u54ea\u91cc\u8d70\uff1f\u4e09\u516c\u4e3b\u5c31\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u4e09\u4eba\u60bc\u7136\u56de\u8eab\uff0c\u5374\u770b\u4e0d\u5230\u53d1\u8bdd\u4e4b\u4eba\u3002 \u3000\u3000\u8fd9\u77f3\u5ba4\u867d\u7136\u4e0d\u5c0f\uff0c\u5374\u51b3\u4e0d\u53ef\u80fd\u85cf\u7740\u4eba\u800c\u770b\u4e0d\u5230\uff0c\u56e0\u4e3a\u77f3\u5ba4\u5185\u53ea\u6446\u4e86\u51e0\u5f20\u684c\u6905\uff0c\u5728\u5bab\u706f\u7167 \u5c04\u4e0b\uff0c\u53ef\u8bf4\u4e00\u89c8\u65e0\u9057\uff0c\u5373\u4fbf\u4e00\u53ea\u8001\u9f20\u4e5f\u85cf\u4e0d\u4f4f\uff0c\u4f55\u51b5\u662f\u4eba\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5b9a\u4e86\u5b9a\u795e\u9053\uff1a\u201c\u8bf7\u95ee\u4e09\u516c\u4e3b\u5728\u54ea\u91cc\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u73b0\u8eab\u76f8\u89c1\uff1f\u201d \u3000\u3000\u90a3\u80a1\u8106\u751f\u751f\u7684\u58f0\u97f3\u53c8\u4f20\u6765\u9053\uff1a\u201c\u73b0\u5728\u662f\u4e09\u516c\u4e3b\u53ec\u89c1\u4f60\u4eec\uff0c\u4e0d\u662f\u4f60\u4eec\u53ec\u89c1\u4e09\u516c\u4e3b\uff0c\u53ea\u8981\u4e09 \u516c\u4e3b\u770b\u5230\u4f60\u4eec\u5c31\u6210\u4e86\uff0c\u4f55\u5fc5\u4f60\u4eec\u770b\u5230\u4e09\u516c\u4e3b\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u91cc\u6709\u4e86\u6570\uff0c\u8fd9\u95f4\u77f3\u5ba4\u5185\u5fc5\u5b9a\u6709\u7279\u6b8a\u7684\u673a\u5173\u8bbe\u7f6e\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u968f\u5373\u62f1\u624b\u4e00\u793c\u9053\uff1a\u201c\u5de1\u5173\u4f7f\u8005\u5cb3\u5c0f\u98de\u62dc\u89c1\u4e09\u516c\u4e3b\uff01\u201d \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u89c1\u5cb3\u5c0f\u98de\u8fd9\u6837\u505a\uff0c\u4e5f\u968f\u5373\u8ddf\u7740\u5b66\u6837\uff0c \u3000\u3000\u53ea\u542c\u4e00\u4e2a\u6709\u5982\u51fa\u8c37\u9ec4\u83ba\u822c\u7684\u58f0\u97f3\u9053\uff1a\u201c\u4e0d\u5fc5\u591a\u793c\uff0c\u4f60\u4eec\u90fd\u8bf7\u5750\u4e0b\uff01\u201d \u3000\u3000\u6beb\u65e0\u7591\u95ee\uff0c\u8fd9\u6b21\u8bf4\u8bdd\u7684\u662f\u4e09\u516c\u4e3b\u4e86\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7b49\u4e09\u4eba\u5e76\u4e0d\u5ba2\u6c14\uff0c\u4f9d\u8a00\u5728\u6728\u6905\u4e0a\u5750\u4e0b\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u662f\u6b63\u895f\u5371\u5750\u3002\u5750\u59ff\u6bd4\u6709\u4eba\u5728\u9762\u524d\u66f4\u8981\u7aef\u6b63\u3002 \u3000\u3000\u77f3\u5ba4\u91cc\u9759\u9ed8\u5f88\u4e45\uff0c\u624d\u4f20\u6765\u58f0\u97f3\u9053\uff1a\u201c\u90a3\u4e2a\u5927\u4e2a\u5b50\u59d3\u4ec0\u4e48\u53eb\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u4e00\u54a7\u5634\uff0c\u7528\u624b\u8098\u9876\u4e00\u4e0b\u7518\u9716\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u95ee\u4f60\u5566\uff01\u201d \u3000\u3000\u7518\u9716\u6123\u4e0b\u6123\u9053\uff1a\u201c\u4e0d\u662f\u6211\uff0c\u662f\u4f60\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u4f60\u4e0d\u662f\u5927\u4e2a\u5b50\uff1f\u201d \u3000\u3000\u201c\u6211\u867d\u7136\u4e2a\u5b50\u4e5f\u5f88\u9ad8\uff0c\u4f46\u5374\u6ca1\u4f60\u80d6\uff0c\u4e00\u5b9a\u662f\u4f60\uff01\u201d \u3000\u3000\u4e24\u4eba\u6b63\u5728\u4e92\u76f8\u63a8\u8bff\uff0c\u53c8\u54cd\u8d77\u4e09\u516c\u4e3b\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5c31\u662f\u90a3\u4e2a\u80a5\u80d6\u7684\u5927\u4e2a\u5b50\u201d\u3002 \u3000\u3000\u8fd9\u6b21\u4e01\u6d9b\u603b\u7b97\u77e5\u9053\u95ee\u7684\u662f\u81ea\u5df1\uff0c\u633a\u4e86\u633a\u80f8\u9053\uff1a\u201c\u5351\u804c\u59d3\u4e01\uff0c\u4e24\u5212\u7684\u4e01\uff0c\u5355\u5b57\u53eb\u6d9b\uff0c\u3000\u5341 \u4e03\u5212\u7684\u6d9b\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u89e3\u91ca\u8fd8\u597d\uff0c\u4e24\u5212\u7684\u4e01\uff0c\u53ea\u8981\u8bc6\u5b57\u7684\uff0c\u90fd\u77e5\u9053\u662f\u90a3\u4e2a\u5b57\uff0c\u4f46\u5341\u4e03\u5212\u53ef\u5c31\u4ee4\u4eba\u96be\u4ee5\u60f3\u5230 \u662f\u4ec0\u4e48\u5b57\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\u5728\u4e01\u6d9b\u6765\u8bf4\uff0c\u5374\u8ba4\u4e3a\u81ea\u5df1\u5f88\u6709\u5b66\u95ee\u3002 \u3000\u3000\u597d\u5728\u4e09\u516c\u4e3b\u5e76\u672a\u8d23\u602a\uff0c\u518d\u95ee\u9053\uff1a\u201c\u4e0a\u6b21\u5728\u6587\u5173\u53d7\u6d4b\u7684\u65f6\u5019\uff0c\u53ef\u662f\u4f60\u81ea\u5df1\u51ed\u5b66\u95ee\u901a\u8fc7\u7684\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8fd9\u65b9\u9762\u5351\u804c\u5e76\u4e0d\u542b\u7cca\uff0c\u5b8c\u5168\u51ed\u7684\u771f\u672c\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u61c2\u56db\u4e66\u4e94\u7ecf\uff0c\u8bd7\u8bcd\u6b4c\u8d4b\uff1f\u201d \u3000\u3000\u201c\u5351\u804c\u662f\u51ed\u540a\u574e\u513f\u8fc7\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u73b0\u5728\u5c31\u4ee5\u6211\u4e09\u516c\u4e3b\u4e3a\u574e\u513f\uff0c\u4f60\u540a\u4e00\u540a\u770b\uff01\u201d \u3000\u3000\u4e01\u6d9b\u4e07\u60f3\u4e0d\u5230\u4e09\u516c\u4e3b\u4f1a\u51fa\u8fd9\u79cd\u9898\u76ee\u6765\u8003\u81ea\u5df1\uff0c\u987f\u4e86\u4e00\u987f\u9053\uff1a \u3000\u3000\u201c\u5351\u804c\u5c31\u540a\u4e00\u540a\u770b\uff0c\u5927\u516c\u4e3b\u548c\u4e8c\u516c\u4e3b\u7684\u59b9\u59b9\uff0c\u574e\u513f\u5c31\u662f\u4e09\u516c\u4e3b\u4e86\uff01\u201d \u3000\u3000\u4e09\u516c\u4e3b\u54cd\u8d77\u94f6\u94c3\u822c\u7684\u7b11\u58f0\u9053\uff1a\u201c\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u4e01\u6d9b\u6014\u4e86\u6014\u9053\uff1a\u201c\u5351\u804c\u5b9e\u5728\u4e0d\u77e5\u9053\u9519\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u5982\u679c\u8fd8\u6709\u56db\u516c\u4e3b\u5462\uff0c\u5979\u662f\u4e0d\u662f\u5927\u516c\u4e3b\u548c\u4e8c\u516c\u4e3b\u7684\u59b9\u59b9\uff1f\u600e\u80fd\u8bf4\u4e00\u5b9a\u662f\u4e09\u516c \u4e3b\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u88ab\u9a73\u5f97\u54d1\u53e3\u65e0\u8a00\uff0c\u5f53\u7136\uff0c\u8fd9\u662f\u56e0\u4e3a\u9762\u5bf9\u7740\u7684\u662f\u4e09\u516c\u4e3b\uff0c\u5982\u679c\u6362\u4e86\u522b\u4eba\uff0c\u4ed6\u4ecd\u6709\u4e00\u756a \u6b6a\u7406\u3002 \u3000\u3000\u4e09\u516c\u4e3b\u7684\u58f0\u97f3\u53c8\u4f20\u4e86\u8fc7\u6765\u9053\uff1a\u201c\u53e6\u4e00\u4e2a\u5927\u4e2a\u5b50\u59d3\u4ec0\u4e48\u53eb\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7518\u9716\u8fde\u5fd9\u7531\u5ea7\u4f4d\u4e0a\u7ad9\u8d77\u6765\uff0c\u8eac\u8eab\u7b11\u9053\uff1a\u201c\u5c5e\u4e0b\u59d3\u7518\uff0c\u540d\u9716\uff0c\u7518\u662f\u7518\u7684\u7518\uff0c\u9716\u662f\u9716\u7684\u9716\u3002\u201d \u3000\u3000\u53ea\u542c\u4e09\u516c\u4e3b\u5403\u5403\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u7518\u7684\u7518\uff0c\u4ec0\u4e48\u53eb\u9716\u7684\u9716\uff1f\u201d \u3000\u3000\u7518\u9716\u7ea2\u7740\u8138\u9053\uff1a\u201c\u5c31\u597d\u50cf\u5f88\u4e45\u6ca1\u4e0b\u96e8\uff0c\u6811\u4e5f\u67af\u4e86\uff0c\u5730\u4e5f\u88c2\u4e86\uff0c\u7389\u7c73\u548c\u9ea6\u5b50\u90fd\u5e72\u6b7b\u4e86\uff0c\u5ffd \u7136\u4e0b\u4e86\u4e00\u573a\u96e8\uff0c\u5c31\u53eb\u7518\u9716\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u65e2\u7136\u6811\u67af\u4e86\uff0c\u7389\u7c73\u548c\u9ea6\u5b50\u90fd\u5e72\u6b7b\u4e86\uff0c\u7518\u9716\u53c8\u6709\u4ec0\u4e48\u7528\u5462\uff1f\u201d \u3000\u3000\u7518\u9716\u54bd\u4e0b\u4e00\u53e3\u553e\u6cab\u9053\uff1a\u201c\u67af\u6728\u9022\u6625\u53c8\u53d1\u82bd\uff0c\u603b\u662f\u6709\u7528\u7684\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u7b97\u4f60\u80fd\u72e1\u8fa9\uff0c\u3000\u4e0a\u6b21\u4f60\u5728\u6587\u5173\u662f\u600e\u6837\u901a\u8fc7\u7684\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u5c5e\u4e0b\u5168\u4ed7\u53f0\u6e7e\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u8bed\u6c14\u53d8\u5f97\u4e00\u6123\u9053\uff1a\u201c\u4f60\u4eec\u56db\u4e2a\u95ef\u5173\u7684\uff0c\u6ca1\u542c\u8bf4\u6709\u53eb\u53f0\u6e7e\u7684\uff1f\u201d \u3000\u3000\u7518\u9716\u82e6\u7b11\u9053\uff1a\u201c\u53f0\u6e7e\u662f\u5904\u5730\u540d\uff0c\u4e0d\u662f\u4eba\u540d\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u4e0d\u613f\u591a\u95ee\u4e0d\u77e5\u9053\u7684\u4e8b\uff0c\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u4f60\u5750\u4e0b\uff01\u201d \u3000\u3000\u63a5\u7740\u95ee\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u5e9c\u535c\u90fd\u6709\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u89c9\u6697\u751f\u8bb6\u5f02\uff0c\u4e09\u516c\u4e3b\u95ee\u4ed6\u7684\u8bdd\uff0c\u663e\u7136\u548c\u95ee\u4e01\u6d9b\u3001\u7518\u9716\u4e0d\u540c\uff0c\u800c\u4e14\u4e0d\u79f0\u5cb3\u4f7f\u8005\u5374 \u79f0\u5cb3\u516c\u5b50\uff0c\u4e5f\u4e0e\u5e38\u7406\u4e0d\u5408\u3002 \u3000\u3000\u4ed6\u5e76\u672a\u8d77\u8eab\uff0c\u6c14\u5b9a\u795e\u95f2\u7684\u7b54\u9053\uff1a\u201c\u5351\u804c\u5bb6\u6709\u7236\u6bcd\uff0c\u6b64\u5916\u5e76\u65e0\u4ed6\u4eba\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u5c0f\u5c0f\u5e74\u7eaa\u5c31\u79bb\u5bb6\u6765\u5230\u5929\u8c37\uff0c\u4ee4\u5c0a\u548c\u4ee4\u5802\u53ef\u653e\u5fc3\u5417\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u7537\u513f\u5fd7\u5728\u56db\u65b9\uff0c\u5351\u804c\u6b63\u662f\u5949\u5bb6\u7236\u6bcd\u4e4b\u547d\uff0c\u5230\u5916\u9762\u589e\u957f\u4e00\u4e9b\u89c1\u95fb\u9605\u5386\u3002\u201d \u3000\u3000\u201c\u542c\u8bf4\u4f60\u8fde\u95ef\u4e94\u5173\uff0c\u6210\u7ee9\u90fd\u662f\u6700\u4f18\u7684\uff0c\u5b9e\u5728\u4ee4\u4eba\u4f69\u670d\uff01\u201d \u3000\u3000\u201c\u4e09\u516c\u4e3b\u8fc7\u5956\uff0c\u5351\u804c\u53ea\u662f\u5c3d\u529b\u800c\u4e3a\uff0c\u201d \u3000\u3000\u201c\u50cf\u4f60\u8fd9\u6837\u7684\u6587\u6b66\u5168\u624d\uff0c\u5728\u6574\u4e2a\u5929\u8c37\u91cc\u4e5f\u627e\u4e0d\u51fa\u51e0\u4e2a\uff0c\u4f46\u4efb\u5de1\u5173\u4f7f\u8005\uff0c\u4f3c\u4e4e\u592a\u59d4\u5c48\u4f60\u4e86\uff0c \u6211\u6709\u610f\u7980\u62a5\u6559\u4e3b\uff0c\u628a\u4f60\u8c03\u5230\u603b\u575b\u53bb\uff0c\u4f60\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7ec8\u4e8e\u8d77\u8eab\u9053\uff1a\u201c\u591a\u8c22\u4e09\u516c\u4e3b\u7f8e\u610f\uff0c\u5351\u804c\u5e74\u8f7b\u8bc6\u6d45\uff0c\u5fc5\u987b\u591a\u78e8\u78e8\u7ec3\u7ec3\uff0c\u80fd\u62c5\u4efb\u5de1\u5173 \u4f7f\u8005\uff0c\u5df2\u7ecf\u5fc3\u6ee1\u610f\u8db3\u4e86\uff01\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u5426\u8a00\u4e0d\u7531\u8877\uff1f\u201d \u3000\u3000\u201c\u5351\u804c\u5b8c\u5168\u51fa\u80ba\u8151\u4e4b\u8a00\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u4e0d\u518d\u8a00\u8bed\u3002\u591a\u65f6\u624d\u53c8\u4f20\u8fc7\u58f0\u97f3\u6765\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u4f60\u5c31\u6682\u65f6\u7559\u5728\u4e94\u5173\u603b\u9547\u5e9c\uff0c\u4e0d\u8fc7\u6559 \u4e3b\u4e00\u5411\u4e0d\u57cb\u6ca1\u4eba\u624d\uff0c\u8fc7\u4e9b\u65f6\u5019\u6211\u4e00\u5b9a\u8bbe\u6cd5\u628a\u4f60\u8c03\u5230\u603b\u575b\u53bb\u3002\u201d \u3000\u3000\u201c\u5351\u804c\u4e0d\u60f3\u79bb\u5f00\u4e94\u5173\u603b\u9547\uff0c\u8fd8\u671b\u4e09\u516c\u4e3b\u6210\u5168\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u597d\u4eba\u5fc5\u987b\u51fa\u5934\uff0c\u4f60\u613f\u610f\u6c38\u8fdc\u53ea\u505a\u4e00\u540d\u5de1\u5173\u4f7f\u8005\u5417\uff1f\u201d \u3000\u3000\u201c\u5351\u804c\u613f\u610f\u3002\u201d \u3000\u3000\u4e09\u516c\u4e3b\u53d1\u51fa\u4e00\u9635\u5a07\u7b11\u9053\uff1a\u201c\u597d\u5566\u597d\u5566\uff0c\u6211\u8fd8\u662f\u7b2c\u4e00\u6b21\u9047\u5230\u50cf\u4f60\u8fd9\u6837\u4e0d\u6c42\u53d1\u8ff9\u7684\u4eba\uff0c\u4f60\u4eec \u56de\u5230\u524d\u9762\u53bb\u5427\uff0c\u6211\u5df2\u4ea4\u4ee3\u8fc7\u5218\u7edf\u9886\uff0c\u8981\u4ed6\u4e2d\u5348\u518d\u597d\u597d\u6b3e\u5f85\u4f60\u4eec\u4e00\u6b21\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e0a\u6b21\u627f\u4e09\u516c\u4e3b\u5ba0\u9047\uff0c\u8fd9\u6b21\u53c8\u627f\u8d50\u5bb4\uff0c\u5351\u804c\u4eec\u5b9e\u5728\u4e0d\u6562\u5f53\uff01\u201d \u3000\u3000\u4e09\u516c\u4e3b\u9053\uff1a\u201c\u51e1\u662f\u6295\u6548\u5929\u8c37\u7684\uff0c\u5c31\u662f\u81ea\u5df1\u4eba\uff0c\u81ea\u5df1\u4eba\u4f55\u7528\u5ba2\u6c14\uff0c \u3000\u3000\u53ea\u662f\u6211\u65e0\u6cd5\u5949\u966a\uff0c\u522b\u4ecb\u610f\uff0c\u4f60\u4eec\u56de\u5230\u524d\u800c\u53bb\u5427\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b49\u4e09\u4eba\u968f\u5373\u56de\u5230\u524d\u9762\u77f3\u5ba4\uff0c\u53ea\u89c1\u7b75\u5e2d\u65e9\u5df2\u6446\u597d\uff0c\u5218\u7ee7\u5fb7\u548c\u6768\u51e4\u5802\u6b63\u7b49\u5019\u5728\u90a3\u91cc\u3002 \u3000\u3000\u8fd9\u4e00\u9910\u9152\u7b75\uff0c\u53ea\u5403\u5f97\u5bbe\u4e3b\u5c3d\u6b22\uff0c \u3000\u3000\u6563\u5e2d\u540e\uff0c\u4e01\u6d9b\u7981\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u4e3a\u4ec0\u4e48\u5230\u73b0\u5728\u8fd8\u6ca1\u51fa\u6765\u5462\uff1f\u201d \u3000\u3000\u5218\u7ee7\u5fb7\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u65e9\u5df2\u8d70\u4e86\u3002\u201d \u3000\u3000\u4e01\u6d9b\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u4e09\u516c\u4e3b\u8d70\u4e86\uff1f\u4e3a\u4ec0\u4e48\u6ca1\u89c1\u5730\u7ecf\u8fc7\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u5218\u7ee7\u5fb7\u7b11\u9053\uff1a\u201c\u91cc\u9762\u53e6\u6709\u901a\u9053\uff0c\u4f55\u5fc5\u7ecf\u8fc7\u8fd9\u91cc\u3002\u201d \u3000\u3000\u4e01\u6d9b\u5482\u4e86\u5482\u820c\u5934\u9053\uff1a\u201c\u539f\u6765\u662f\u8fd9\u6837\uff0c\u60a8\u8fd9\u91cc\u7684\u673a\u5173\u8bbe\u65bd\u53ef\u771f\u4e86\u4e0d\u5f97\uff0c\u521a\u624d\u5728\u91cc\u9762\uff0c\u548c\u4e09 \u516c\u4e3b\u8bb2\u4e86\u534a\u5929\u8bdd\uff0c\u5374\u8fde\u5979\u7684\u5f71\u5b50\u90fd\u6ca1\u770b\u5230\u3002\u201d \u3000\u3000\u5218\u7ee7\u5fb7\u9053\uff1a\u201c\u4e09\u516c\u4e3b\u548c\u5927\u516c\u4e3b\u4e0d\u4e00\u6837\uff0c\u5979\u5f88\u5c11\u8ba9\u4eba\u770b\u5230\u5e90\u5c71\u771f\u9762\u76ee\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4e24\u773c\u4e00\u7728\u9053\uff1a\u201c\u662f\u4e0d\u662f\u5979\u957f\u5f97\u4e0d\u597d\u770b\uff1f\u201d \u3000\u3000\u5218\u7ee7\u5fb7\u5931\u58f0\u7b11\u9053\uff1a\u201c\u4e01\u8001\u5f1f\u600e\u4f1a\u8fd9\u6837\u60f3\uff0c\u82e5\u8bba\u4e09\u516c\u4e3b\u7684\u5bb9\u8c8c\uff0c\u8fde\u5927\u516c\u4e3b\u90fd\u6bd4\u5979\u4e0d\u4e0a\uff0c\u53ea \u6015\u666e\u5929\u4e4b\u4e0b\uff0c\u4e5f\u627e\u4e0d\u5230\u51e0\u4e2a\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u6447\u5934\u6643\u8111\u7684\u9053\uff1a\u201c\u79f0\u5f97\u4e0a\u662f\u95ed\u6708\u7f9e\u82b1\u4e4b\u5bb9\uff0c\u6c89\u9c7c\u843d\u96c1\u4e4b\u8c8c\uff0c\u82e5\u975e\u7fa4\u7389\u5c71\u5934\u89c1\uff0c\u4f1a \u5411\u7476\u53f0\u6708\u4e0b\u9022\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8d77\u8eab\u9053\uff0c\u201c\u591a\u8c22\u4e24\u4f4d\u524d\u8f88\u76db\u60c5\u6b3e\u5f85\uff0c\u665a\u8f88\u4eec\u8be5\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u5218\u7ee7\u5fb7\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\u4e94\u5173\u53ef\u90fd\u5de1\u67e5\u8fc7\u4e86\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u53ea\u5269\u5bb5\u5173\u6ca1\u53bb\u3002\u201d \u3000\u3000\u5ffd\u542c\u4e01\u6d9b\u95ee\u9053\uff1a\u201c\u8bf7\u95ee\u5218\u524d\u8f88\uff0c\u6211\u4eec\u53ef\u4e0d\u53ef\u4ee5\u5230\u89c2\u5149\u96a7\u9053\u770b\u770b\uff1f\u201d \u3000\u3000\u5218\u7ee7\u5fb7\u9053\uff1a\u201c\u89c2\u5149\u96a7\u9053\u867d\u7136\u4e0d\u662f\u4e94\u5173\u4e2d\u7684\u4efb\u4f55\u4e00\u5173\uff0c\u7167\u7406\u8bf4\u4e5f\u8be5\u53d7\u4e94\u5173\u603b\u9547\u7ba1\u8f96\uff0c\u4f60\u4eec \u5f53\u7136\u53ef\u4ee5\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4e00\u624b\u62c9\u4f4f\u5cb3\u5c0f\u98de\uff0c\u4e00\u624b\u62c9\u4f4f\u7518\u9716\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u8001\u7518\uff0c\u54b1\u4eec\u5230\u89c2\u5149\u96a7\u9053\u53bb\uff01\u201d \u3000\u3000\u6768\u51e4\u5802\u4e24\u773c\u7728\u4e86\u51e0\u773c\u9053\uff1a\u201c\u4e01\u5f92\u513f\uff0c\u4f60\u957f\u4e86\u51e0\u5bf9\u773c\u775b\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u4e0d\u77e5\u6768\u51e4\u5802\u6b64\u8bdd\u7528\u610f\uff0c\u6123\u4e86\u6123\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5f1f\u5b50\u5f53\u7136\u53ea\u6709\u4e00\u5bf9\u773c\u775b\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u9053\uff1a\u201c\u65e2\u7136\u53ea\u6709\u4e00\u5bf9\u773c\u775b\uff0c\u8fd8\u662f\u522b\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5c0f\u5fc3\u4f60\u90a3\u4e00\u5bf9\u773c\u775b\u778e\u6389\uff01\u201d \u3000\u3000\u4e01\u6d9b\u8fde\u5fd9\u53cc\u819d\u8dea\u5012\u9053\uff1a\u201c\u5e08\u7236\u5f00\u6069\uff0c\u5c31\u8ba9\u5f1f\u5b50\u53bb\u8fd9\u4e00\u6b21\uff0c\u5f1f\u5b50\u7684\u773c\u775b\u4fdd\u8bc1\u4e0d\u4f1a\u778e\u3002\u5982\u679c \u4f60\u8001\u4eba\u5bb6\u4e0d\u4fe1\uff0c\u5f1f\u5b50\u56de\u6765\u4ee5\u540e\u60a8\u53ef\u4ee5\u9a8c\uff01\u201d \u3000\u3000\u6768\u51e4\u5802\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u9a8c\u4ec0\u4e48\u9a8c\uff0c\u8001\u673d\u4e5f\u4e0d\u662f\u773c\u79d1\u5927\u592b\uff01\u201d \u3000\u3000\u5218\u7ee7\u5fb7\u5df2\u77e5\u6768\u51e4\u5802\u6536\u4e86\u4e01\u6d9b\u4e3a\u5f92\uff0c\u8fde\u5fd9\u529d\u89e3\u9053\uff1a\u201c\u51e4\u5802\u5144\u5c31\u51c6\u4ed6\u53bb\u8fd9\u2014\u6b21\u5427\uff0c\u9022\u573a\u4f5c\u620f\uff0c \u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u90a3\u5730\u65b9\u8fde\u8001\u673d\u4e5f\u53bb\u8fc7\uff0c\u4f60\u8fd8\u4e0d\u662f\u7167\u6837\u4e5f\u53bb\u53c2\u89c2\u8fc7\u3002\u201d \u3000\u3000\u6768\u51e4\u5802\u6273\u8d77\u9762\u5b54\u9053\uff1a\u201c\u8c01\u8bf4\u8001\u673d\u53bb\u8fc7\uff0c\u8001\u673d\u8bfb\u5723\u8d24\u4e4b\u4e66\uff0c\u96be\u9053\u4e0d\u61c2\u5f97\u975e\u793c\u52ff\u89c6\uff1f\u201d \u3000\u3000\u5218\u7ee7\u5fb7\u9053\uff1a\u201c\u4f60\u82e5\u6ca1\u770b\u8fc7\uff0c\u600e\u77e5\u9053\u770b\u8fc7\u540e\u4f1a\u778e\u773c\u775b\uff1f\u201d \u3000\u3000\u6768\u51e4\u5802\u987f\u65f6\u80c0\u4e86\u7ea2\u8138\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u597d\u5427\uff0c\u968f\u4ed6\u4eec\u7684\u4fbf\uff01\u201d \u3000\u3000\u5218\u7ee7\u5fb7\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4f4d\u53bb\u5427\uff0c\u4e0d\u8fc7\u597d\u620f\u4e0d\u80fd\u591a\u770b\uff0c\u770b\u591a\u4e86\u665a\u4e0a\u7761\u89c9\u4f1a\u5934\u75db\u3002\u201d \u3000\u3000\u5f88\u5feb\u4fbf\u63a5\u8fd1\u89c2\u5149\u96a7\u9053\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8001\u8fdc\u5c31\u6b62\u4f4f\u811a\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u7b49\u7740\uff0c\u4f60\u4eec\u4e8c\u4f4d\u53bb\u5427\uff01\u201d \u3000\u3000\u4e01\u8bd7\u53c8\u2014\u628a\u62c9\u4f4f\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u516c\u5b50\u4e0a\u6b21\u6ca1\u770b\uff0c\u8fd9\u6b21\u4e00\u5b9a\u8981\u770b\uff0c \u3000\u3000\u8fd9\u6837\u597d\u7684\u4e1c\u897f\u4e0d\u770b\uff0c\u4f60\u4f1a\u7ec8\u751f\u9057\u61be\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7529\u5f00\u624b\u9053\uff1a\u201c\u522b\u62c9\u7740\uff0c\u90a3\u662f\u9650\u5e74\u7eaa\u7684\uff0c\u6211\u7684\u5e74\u9f84\u8fd8\u4e0d\u5230\u3002\u201d \u3000\u3000\u7518\u971c\u62cd\u4e86\u4e00\u4e0b\u4e01\u6d9b\u80a9\u8180\u9053\uff1a\u201c\u516c\u5b50\u4e0d\u53bb\u5c31\u522b\u5f3a\u62c9\u4e86\uff0c\u54b1\u4eec\u770b\u54b1\u4eec\u7684\u3002\u201d \u3000\u3000\u4e01\u6d9b\u53ea\u597d\u54a7\u5634\u7b11\u4e0b\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u516c\u5b50\u5c31\u5728\u8fd9\u91cc\u4f11\u606f\u5427\uff0c\u6211\u4eec\u5f88\u5feb\u56de\u6765\u3002\u201d \u3000\u3000\u6765\u5230\u96a7\u9053\u8fb9\uff0c\u4e00\u540d\u7ea2\u8863\u5c11\u5973\u5b88\u5728\u90a3\u91cc\u3002 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u971c\u90fd\u8ba4\u5f97\u51fa\uff0c\u8fd9\u7ea2\u8863\u5c11\u5973\u6b63\u662f\u548c\u4e0a\u6b21\u90a3\u4f4d\u88ab\u79f0\u505a\u8001\u638c\u67dc\u7684\u9ed1\u8863\u8001\u4eba\u4e00\u8d77\u51fa\u73b0\u8fc7 \u7684\uff0c\u4e5f\u6b63\u662f\u89c2\u5149\u96a7\u9053\u7684\u5973\u7ba1\u4e8b\u3002 \u3000\u3000\u4e01\u6d9b\u4e0a\u524d\u51e0\u6b65\uff0c\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u59d1\u5a18\u3002\u8fd8\u8bb0\u5f97\u6211\u4eec\u4e24\u4e2a\u5417\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u63a9\u4f4f\u6a31\u53e3\u4e00\u7b11\u9053\uff1a\u201c\u624d\u4e0d\u8fc7\u9694\u4e86\u534a\u4e2a\u591a\u6708\uff0c\u5f53\u7136\u8ba4\u8bc6\u4f60\u4eec\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8ba4\u8bc6\u6211\u4eec\u5c31\u597d\u529e\u3002\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u53cd\u95ee\u9053\uff1a\u201c\u597d\u529e\u4ec0\u4e48\uff1f\u4f60\u4eec\u65e2\u7136\u5df2\u7ecf\u95ef\u8fc7\u4e94\u5173\uff0c\u4e3a\u4ec0\u4e48\u53c8\u56de\u5230\u8fd9\u91cc\uff1f\u96be\u9053\u4e0d\u77e5 \u9053\u5929\u8c37\u53ea\u80fd\u8fdb\u4e0d\u80fd\u51fa\u5417\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u662f\u60f3\u51fa\u53bb\uff0c\u53ea\u5e0c\u671b\u518d\u89c2\u5149\u4e00\u6b21\u3002\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u7b11\u9053\uff1a\u201c\u5bf9\u4e0d\u8d77\uff0c\u6bcf\u4e2a\u4eba\u53ea\u80fd\u89c2\u5149\u4e00\u6b21\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6211\u4eec\u6bcf\u4e2a\u4eba\u518d\u51fa\u4e94\u4e24\u94f6\u5b50\uff0c\u96be\u9053\u5c31\u4e0d\u80fd\u518d\u770b\u770b\uff1f\u4e3a\u4ec0\u4e48\u5916\u4eba\u80fd\u770b\uff0c\u81ea\u5df1\u4eba\u53cd \u800c\u4e0d\u80fd\u770b\uff1f\u201d \u3000\u3000\u201c\u7ed9\u5916\u4eba\u770b\uff0c\u76ee\u7684\u662f\u628a\u4ed6\u5f15\u8fdb\u6765\u3002\u5f15\u8fdb\u6765\u4ee5\u540e\uff0c\u5c31\u6ca1\u6709\u5fc5\u8981\u518d\u770b\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u53eb\u63a8\u5b8c\u78e8\u6740\u9a74\u3002\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u6740\u9a74\u4e0d\u6740\u9a74\uff0c\u53cd\u6b63\u4e0d\u80fd\u518d\u770b\u5c31\u662f\u4e0d\u80fd\u518d\u770b\uff01\u201d \u3000\u3000\u201c\u771f\u4e0d\u8ba9\u770b\uff1f\u201d \u3000\u3000\u201c\u8c01\u8ddf\u4f60\u5f00\u73a9\u7b11\uff01\u201d \u3000\u3000\u4e01\u6d9b\u6307\u7740\u8eab\u4e0a\u7684\u5de1\u5173\u8170\u724c\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u4e0d\u5c51\u7684\u77a5\u4e0b\u4e00\u773c\u9053\uff1a\u201c\u5de1\u5173\u662f\u5de1\u5173\uff0c\u89c2\u5149\u662f\u89c2\u5149\uff0c\u5982\u679c\u4f60\u4eec\u60f3\u5047\u516c\u6d4e\u79c1\uff0c\u53ea\u8981 \u6211\u4eec\u638c\u67dc\u7684\u5411\u4e0a\u9762\u5012\u544a\u4e00\u72b6\uff0c\u8fde\u4f60\u4eec\u526f\u603b\u9547\u4e5f\u8981\u5403\u4e0d\u4e86\u515c\u7740\u8d70\uff01\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u679c\u7136\u8bf4\u5f97\u4e01\u6d9b\u6709\u4e9b\u80c6\u602f\u8d77\u6765\uff0c\u82e5\u56e0\u800c\u8fde\u7d2f\u4e86\u82b1\u526f\u603b\u9547\uff0c\u81ea\u5df1\u7684\u7f6a\u8fc7\u5c31\u5927\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u5e76\u4e0d\u6b7b\u5fc3\uff0c\u563b\u563b\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4ece\u8ba4\u8bc6\u5230\u73b0\u5728\uff0c\u8fd8\u6ca1\u8bf7\u95ee\u4f60\u4e0a\u59d3\u82b3\u540d\uff1f\u201d \u3000\u3000\u7ea2\u8863\u5c11\u5973\u9053\uff1a\u201c\u6211\u53eb\u4e01\u79c0\u541b\uff0c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u4e24\u773c\u4e00\u772f\u9053\uff1a\u201c\u771f\u5de7\u5f97\u5f88\uff0c\u54b1\u4eec\u662f\u4e00\u5bb6\u5b50\u5462\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u4f60\u4e5f\u59d3\u4e01\uff1f\u201d \u3000\u3000\u201c\u53ef\u4e0d\u662f\uff0c\u5728\u4e0b\u53eb\u4e01\u6d9b\u3002\u59d1\u5a18\uff0c\u54b1\u4eec\u8ba4\u4e2a\u5e72\u5144\u59b9\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u4e01\u79c0\u541b\u679c\u7136\u5bf9\u4e01\u6d9b\u53d8\u5f97\u4eb2\u5207\u8d77\u6765\uff0c\u6d45\u7b11\u76c8\u76c8\u7684\u9053\uff1a\u201c\u4e01\u5927\u53d4\uff0c\u4f60\u82e5\u771f\u60f3\u518d\u89c2\u5149\u4e00\u6b21\uff0c\u6700 \u597d\u627e\u638c\u67dc\u7684\u5546\u91cf\uff0c\u53ea\u8981\u4ed6\u7b54\u5e94\u5c31\u6210\u4e86\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4f60\u4eec\u638c\u67dc\u7684\u59d3\u4ec0\u4e48\u53eb\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u638c\u67dc\u7684\u53eb\u738b\u68a6\u6765\u3002\u201d \u3000\u3000\u201c\u9ebb\u70e6\u4f60\u628a\u4ed6\u8bf7\u51fa\u6765\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u968f\u6211\u6765\uff01\u201d \u3000\u3000\u539f\u6765\u738b\u68a6\u6765\u4f4f\u5728\u7531\u5916\u800c\u5165\u53e3\u7684\u90a3\u4e00\u7aef\u3002 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u5728\u7ecf\u8fc7\u96a7\u9053\u65f6\uff0c\u5374\u60c5\u4e0d\u81ea\u7981\u5411\u4e24\u8fb9\u8c37\u58c1\u5bdf\u770b\uff0c\u5947\u602a\u7684\u662f\u5e76\u672a\u770b\u5230\u4e00\u5904\u6d1e\u95e8\uff0c \u3000\u4e0a\u6b21\u90a3\u4e9b\u89c2\u5149\u6d1e\u7a74\u90fd\u5230\u90a3\u91cc\u53bb\u4e86\u5462\uff1f \u3000\u3000\u6765\u5230\u96a7\u9053\u53e6\u4e00\u7aef\uff0c\u3000\u4e01\u79c0\u541b\u5f88\u5feb\u4fbf\u628a\u738b\u68a6\u6765\u8bf7\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u8fd8\u662f\u7a7f\u7740\u4e00\u8eab\u9ed1\u888d\uff0c \u3000\u3000\u738b\u68a6\u6765\u5012\u662f\u5f88\u5ba2\u6c14\uff0c\u542c\u8fc7\u4e86\u4e01\u79c0\u541b\u8bf4\u660e\u4e00\u5207\u540e\uff0c\u76b1\u7740\u7684\u7709\u9053\uff1a \u3000\u3000\u201c\u4e24\u4f4d\u526f\u4f7f\u80af\u8bf7\u539f\u8c05\uff0c\u8fd9\u89c2\u5149\u96a7\u9053\u53ea\u662f\u4f9b\u521d\u5165\u8c37\u7684\u4eba\u53c2\u89c2\u7684\uff0c\u603b\u575b\u65e9\u6709\u89c4\u5b9a\uff0c\u81ea\u5df1\u7684\u4eba \u4e0d\u5f97\u53c2\u89c2\u3002\u201d \u3000\u3000\u4e01\u6d9b\u54a7\u5634\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u4e5f\u6ca1\u5916\u4eba\uff0c\u4f60\u5c31\u62db\u5f85\u62db\u5f85\u53c8\u6709\u5565\u5173\u7cfb\uff1f\u201d \u3000\u3000\u738b\u68a6\u6765\u9887\u611f\u4e3a\u96be\u7684\u9053\uff1a\u201c\u4e01\u526f\u4f7f\u8005\u4e5f\u8bb8\u4e0d\u6e05\u695a\uff0c\u628a\u89c2\u5149\u96a7\u9053\u5e03\u7f6e\u8d77\u6765\uff0c\u81f3\u5c11\u8981\u4e8c\u3001\u4e09\u5341 \u4f4d\u59d1\u5a18\uff0c\u4e0d\u662f\u8bf4\u770b\u4fbf\u53ef\u770b\u7684\uff0c\u53e6\u5916\uff0c\u6240\u6709\u7684\u673a\u5173\u53d1\u52a8\u4e00\u6b21\uff0c\u4e5f\u800d\u8017\u53bb\u4e0d\u5c11\u539f\u6599\uff0c\u8001\u592b\u53c8\u62ff\u4ec0 \u4e48\u7406\u7531\u5411\u4e0a\u9762\u62a5\u9500\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u6d8e\u7740\u8138\u9053\uff1a\u201c\u6211\u4eec\u60c5\u613f\u591a\u51fa\u94b1\uff0c\u8fd8\u671b\u638c\u67dc\u7684\u591a\u5e2e\u5fd9\uff01\u201d \u3000\u3000\u738b\u68a6\u6765\u8e0c\u8e87\u4e86\u534a\u664c\u9053\uff1a\u201c\u8fd9\u6837\u597d\u4e0d\u597d\uff0c\u89c2\u5149\u6d1e\u7a74\u53ea\u5f00\u4e00\u95f4\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u53ea\u8981\u6709\u5c31\u597d\uff0c\u6700\u597d\u662f\u53cc\u4eba\u7684\uff0c\u6216\u8005\u4e09\u4eba\u7684\uff0c\u4e0d\u8fc7\u5fc5\u987b\u591a\u7ed9\u6211\u4eec\u4e00\u70b9\u53c2\u89c2\u65f6 \u95f4\u3002\u201d \u3000\u3000\u738b\u68a6\u6765\u9053\uff1a\u201c\u90a3\u5f53\u7136\u53ef\u4ee5\uff0c\u5373\u4fbf\u770b\u4e0a\u4e00\u4e2a\u65f6\u8fb0\u4e5f\u65e0\u6240\u8c13\uff0c\u4e0d\u8fc7\u8fd9\u6b21\u662f\u5355\u4e3a\u4e24\u4f4d\u51c6\u5907\u7684\uff0c \u5fc5\u987b\u6bcf\u4eba\u51fa\u4e8c\u5341\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u4e01\u6d9b\u5728\u8fd9\u65b9\u9762\u6700\u4e3a\u6177\u6168\uff0c\u7acb\u5373\u638f\u51fa\u56db\u5341\u4e24\u94f6\u7968\u9053\uff1a\u201c\u94b1\u591a\u5c11\u662f\u5c0f\u4e8b\uff0c\u54b1\u59d3\u4e01\u7684\u522b\u7684\u6ca1\u6709\uff0c \u5c31\u662f\u6709\u94b1\uff0c\u638c\u67dc\u7684\u8bf7\u6536\u4e0b\uff01\u201d \u3000\u3000\u738b\u68a6\u6765\u6536\u4e0b\u94f6\u7968\u9053\uff1a\u201c\u4e01\u59d1\u5a18\u9a6c\u4e0a\u53bb\u53d6\u706b\u647a\u5b50\u6765\uff01\u201d \u3000\u3000\u4e01\u6d9b\u5fd9\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u9650\u5236\u65f6\u95f4\uff0c\u706b\u62db\u5b50\u8981\u591a\u62ff\u51e0\u4e2a\u6765\uff01\u201d \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u513f\uff0c\u3000\u4e01\u79c0\u541b\u679c\u7136\u62ff\u4e86\u4e00\u5927\u628a\u706b\u647a\u6765\u3002 \u3000\u3000\u4e01\u6d9b\u6536\u4e0b\u540e\uff0c\u53c8\u5206\u7ed9\u7518\u9716\u51e0\u679d\u3002 \u3000\u3000\u738b\u68a6\u6765\u9053\uff1a\u201c\u8001\u5927\u8981\u5230\u91cc\u9762\u5e03\u7f6e\u4e86\uff0c\u4f60\u4eec\u4e24\u4f4d\u5c31\u8bf7\u5728\u8fd9\u91cc\u7a0d\u5f85\u3002\u201d \u3000\u3000\u738b\u68a6\u6765\u8d70\u540e\uff0c\u4e01\u79c0\u541b\u7b11\u77a5\u4e86\u4e01\u6d9b\u4e00\u773c\u9053\uff1a\u201c\u4e01\u5927\u53d4\uff0c\u5316\u56db\u5341\u4e24\u94f6\u5b50\uff0c\u53ea\u4e3a\u770b\u770b\u6ca1\u7a7f\u8863\u670d \u7684\u5973\u4eba\uff0c\u503c\u5f97\u5417\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u5658\u7740\u5634\u7b11\u9053\uff1a\u201c\u5f53\u7136\u503c\u5f97\u3002\u201d \u3000\u3000\u4e01\u79c0\u541b\u9053\uff1a\u201c\u4eba\u5bb6\u8bf4\u7a7f\u8863\u670d\u662f\u4e3a\u4e86\u7f8e\u89c2\uff0c\u53ef\u89c1\u4e0d\u7a7f\u8863\u670d\u5f88\u96be\u770b\uff0c \u3000\u3000\u504f\u504f\u4f60\u4eec\u7537\u4eba\u82b1\u94b1\u53bb\u627e\u96be\u770b\u7684\u770b\uff0c\u771f\u6559\u4eba\u4e0d\u61c2\uff01\u201d \u3000\u3000\u4e01\u6d9b\u563f\u563f\u7b11\u9053\uff1a\u201c\u7537\u4eba\u591a\u534a\u90fd\u6253\u8fd9\u79cd\u8d31\u6bdb\u75c5\uff0c\u5982\u679c\u4f60\u73b0\u5728\u8131\u5149\u4e86\uff0c\u6211\u60c5\u613f\u518d\u591a\u51fa\u4e00\u500d\u94f6 \u5b50\u3002\u201d \u3000\u3000\u4e01\u79c0\u541b\u987f\u65f6\u5a07\u9765\u80c0\u5f97\u8840\u7ea2\uff0c\u6487\u4e86\u6487\u5634\u9053\uff1a\u201c\u4f60\u8fd8\u597d\u610f\u601d\u59d3\u4e01\uff0c\u4ec0\u4e48\u4e00\u5bb6\u5b50\uff1f\u542c\u4f60\u8bf4\u8fd9\u79cd \u8bdd\uff0c\u7b80\u76f4\u4e0d\u50cf\u4eba\uff01\u201d \u3000\u3000\u4e01\u6d9b\u867d\u88ab\u9a82\u4e86\uff0c\u5374\u4e0d\u80fd\u53d1\u813e\u6c14\uff0c\u53ea\u597d\u5e72\u7b11\u4e86\u51e0\u7b11\u58f0\u9053\uff1a\u201c\u5bf9\u4e0d\u8d77\uff0c\u54b1\u4e0d\u8fc7\u8ddf\u4f60\u5f00\u5f00\u73a9\u7b11\uff0c \u8c01\u82e5\u8131\u4f60\u7684\u8863\u670d\uff0c\u4f60\u4e01\u5927\u53d4\u5c31\u8ddf\u4ed6\u62da\u4e86\uff01\u201d \u3000\u3000\u8fd9\u513f\u53e5\u8bdd\uff0c\u7adf\u53c8\u628a\u4e01\u79c0\u541b\u9017\u7b11\u3002\u63a5\u7740\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u59d3\u4e01\u7684\u80fd\u51fa\u4f60\u8fd9\u4e48\u4e2a\u4eba\uff0c\u4e0d\u77e5\u662f\u70e7 \u4e86\u9ad8\u9999\u8fd8\u662f\u70e7\u8fc7\u7259\u7caa\uff0c\u65f6\u95f4\u5dee\u4e0d\u591a\u4e86\uff0c\u8981\u770b\u5c31\u968f\u6211\u53bb\u770b\u5427\uff01\u201d \u3000\u3000\u8fdb\u5165\u96a7\u9053\u8bbe\u591a\u4e45\uff0c\u4e01\u79c0\u541b\u5c31\u6307\u7740\u58c1\u95f4\u4e00\u5904\u77f3\u95e8\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u91cc\u9762\uff0c\u4f60\u4eec\u81ea\u5df1\u8fdb\u53bb\u5427\uff01\u201d \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u56e0\u4e3a\u706b\u647a\u5f88\u591a\uff0c\u4e00\u8fdb\u6d1e\u95e8\u5c31\u71c3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u672c\u4ee5\u4e3a\u4e00\u8fdb\u53bb\u5c31\u662f\u89c2\u5149\u6240\u5728\uff0c\u5c82\u77e5\u8fdb\u53bb\u5927\u7ea6\u4e24\u4e09\u4e08\uff0c\u7adf\u7136\u51fa\u73b0\u4e86\u4e09\u53c9\u8def\uff0c\u4f7f\u5f97\u4ed6\u4eec \u4e0d\u77e5\u8d70\u90a3\u6761\u8def\u624d\u5bf9\u3002 \u3000\u3000\u6ca1\u5948\u4f55\uff0c\u53ea\u597d\u968f\u4fbf\u627e\u4e00\u6761\u8def\u8d70\u3002 \u3000\u3000\u8fd9\u6761\u8def\u5c45\u7136\u662f\u5904\u65e0\u5e95\u6d1e\uff0c\u76f4\u5230\u4e00\u6839\u706b\u647a\u71c3\u5b8c\uff0c\u4ecd\u672a\u8d70\u5230\u5c3d\u5934\u3002 \u3000\u3000\u4e01\u6d9b\u5583\u5583\u9a82\u9053\uff1a\u201c\u638c\u67dc\u7684\u8fd9\u4e2a\u8001\u738b\u516b\u86cb\uff0c\u4ed6\u7adf\u800d\u8d77\u8001\u5b50\u4eec\u4e86\uff01\u201d \u3000\u3000\u4e3a\u4e86\u7701\u706b\uff0c\u4ed6\u4eec\u53ea\u597d\u6478\u9ed1\u5f80\u524d\u8d70\u3002 \u3000\u3000\u5c82\u6296\u6d1e\u5185\u8d8a\u8d70\u8d8a\u9ed1\uff0c\u901a\u9053\u53c8\u5f2f\u5f2f\u66f2\u66f2\uff0c\u6beb\u65e0\u89c4\u5219\uff0c\u800c\u4e14\u5730\u4e0a\u53c8\u51f9\u51f8\u4e0d\u5e73\uff0c\u4f7f\u5f97\u4e01\u6d9b\u548c\u7518 \u9716\u4e24\u4eba\uff0c\u4e00\u8def\u6454\u6454\u8dcc\u8dcc\uff0c\u8fde\u989d\u89d2\u90fd\u78b0\u4e86\u597d\u51e0\u4e2a\u5927\u75a4\u3002 \u3000\u3000\u597d\u4e0d\u5bb9\u6613\u770b\u5230\u4e00\u7ebf\u5149\u660e\uff0c\u53ea\u9053\u89c2\u5149\u5904\u6240\u5230\u4e86\uff0c\u539f\u6765\u5374\u662f\u53c8\u8f6c\u56de\u65b9\u624d\u7684\u8fdb\u53e3\u5904\u3002 \u3000\u3000\u4e01\u6d9b\u771f\u6068\u4e0d\u5f97\u628a\u738b\u68a6\u6765\u627e\u6765\u62a5\u63cd\u2014\u987f\u3002\u7ecf\u8fc7\u7518\u9716\u7684\u62e6\u963b\uff0c\u8fd8\u662f\u5fcd\u4e86\u3002 \u3000\u3000\u76f4\u5230\u4e09\u53c9\u8def\u53e3\u53e6\u8d70\u4e00\u6761\u8def\uff0c\u603b\u7b97\u627e\u5230\u4e86\u76ee\u7684\u5730\u3002 \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u4ed6\u4eec\u542c\u5230\u6709\u5973\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u3002 \u3000\u3000\u679c\u7136\uff0c\u5f53\u4ed6\u4eec\u71c3\u8d77\u706b\u647a\u65f6\uff0c\u5de6\u8fb9\u6b63\u662f\u4e00\u95f4\u77f3\u5ba4\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u6b63\u6709\u4e09\u540d\u4e00\u4e1d\u4e0d\u6302\u7684\u5c11\u5973\uff0c\u4e00\u5750\u3001\u4e00\u8dea\u3001\u4e00\u5367\u5728\u9760\u58c1\u5904\u6446\u597d\u59ff\u52bf\u3002 \u3000\u3000\u5979\u4eec\u90fd\u8eab\u6750\u82d7\u6761\uff0c\u80a4\u767d\u4f3c\u96ea\uff0c\u7389\u817f\u4fee\u957f\uff0c\u7c89\u81c2\u6676\u83b9\u3002 \u3000\u3000\u5979\u4eec\u4f3c\u4e4e\u90fd\u53d7\u8fc7\u7279\u6b8a\u8bad\u7ec3\uff0c\u4e0d\u4f46\u59ff\u52bf\u80fd\u4e45\u4e45\u4e0d\u52a8\uff0c\u8fde\u773c\u775b\u4e5f\u4e0d\u7728\u4e00\u4e0b\uff0c\u5f53\u7136\uff0c\u4e5f\u4e1d\u6beb\u6ca1 \u6709\u8868\u60c5\u3002 \u3000\u3000\u4e01\u6d9b\u54a7\u7740\u5634\u563f\u563f\u7b11\u9053\uff1a\u201c\u521a\u624d\u8bf4\u8bdd\u7684\u4e0d\u662f\u4f60\u4eec\u5417\uff1f\u4e3a\u4ec0\u4e48\u73b0\u5728\u53c8\u88c5\u6b7b\u4eba\u53c8\u88c5\u54d1\u5df4\uff1f\u201d \u3000\u3000\u4e09\u540d\u5c11\u5973\u6839\u672c\u4e0d\u95fb\u4e0d\u95ee\u3002 \u3000\u3000\u4e01\u6d9b\u89c1\u5979\u4eec\u4e0d\u7406\u4e0b\u776c\uff0c\u8d70\u8fd1\u4e24\u6b65\u9053\uff1a\u201c\u518d\u4e0d\u5f00\u53e3\uff0c\u8001\u5b50\u5c31\u8981\u6478\u4e86\uff01\u201d \u3000\u3000\u7ec8\u4e8e\u90a3\u8dea\u7740\u7684\u5c11\u5973\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u6015\u5012\u9709\u53ea\u7ba1\u52a8\u624b\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8001\u5b50\u4e0d\u542c\u8fd9\u4e00\u5957\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u679c\u771f\u63a2\u51fa\u624b\u53bb\uff0c\u76f4\u6478\u90a3\u5c11\u5973\u7684\u524d\u80f8\u3002 \u3000\u3000\u89e6\u624b\u4e4b\u4e0b\uff0c\u5bf9\u65b9\u6ca1\u9165\uff0c\u4ed6\u81ea\u5df1\u53cd\u800c\u5168\u8eab\u53c8\u9165\u53c8\u75d2\u3002 \u3000\u3000\u90a3\u5c11\u5973\u6296\u5b50\u6296\u8eab\u5b50\u9053\uff1a\u201c\u4f60\u82e5\u518d\u4e0d\u7f29\u624b\uff0c\u5c31\u771f\u7684\u8981\u5012\u9709\u4e86\uff01\u201d \u3000\u3000\u4e01\u6d9b\u5c82\u4f1a\u88ab\u5979\u559d\u6b62\uff0c\u90a3\u53ea\u624b\u5374\u987a\u7740\u9165\u80f8\uff0c\u76f4\u5411\u4e0b\u6ed1\u53bb\u3002 \u3000\u3000\u4e0d\u7528\u8bf4\uff0c\u8fd9\u65f6\u4ed6\u7684\u8170\u4e5f\u5411\u524d\u52fe\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5ffd\u89c1\u90a3\u5c11\u5973\u5a07\u8eaf\u4e00\u7eb5\uff0c\u626c\u8d77\u7389\u8155\uff0c\u76f4\u5411\u4e01\u6d9b\u9762\u988a\u4e0a\u63b4\u53bb\u3002 \u3000\u3000\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u53ea\u63b4\u5f97\u4e01\u6d9b\u8eab\u4e0d\u7531\u5df1\u5411\u540e\u6454\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u4ed6\u66b4\u6012\u4e4b\u4e0b\uff0c\u731b\u7136\u5411\u524d\u6251\u53bb\u3002 \u3000\u3000\u90a3\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u5730\u9762\u5ffd\u7136\u5411\u4e0b\u9677\u4e86\u4e0b\u53bb\uff0c\u4e0d\u4f46\u90a3\u4e09\u540d\u5c11\u5973\u4e0d\u89c1\uff0c\u8fde\u4e01\u6d9b\u4e5f\u843d\u4e0b\u5751\u53bb\u3002 \u3000\u3000\u7518\u9716\u7ad9\u5f97\u8f83\u8fdc\uff0c\u6b32\u62c9\u4e01\u6d9b\u4e00\u628a\u5df2\u6765\u4e0d\u53ca\uff0c\u5f53\u4ed6\u6301\u7740\u706b\u647a\u5411\u9677\u4e0b\u4e4b\u5904\u7167\u53bb\uff0c\u4e01\u6d9b\u5df2\u7f69\u5728\u4e00 \u526f\u94c1\u7f51\u4e2d\uff0c\u800c\u90a3\u4e09\u540d\u5c11\u5973\u5374\u5df2\u4e0d\u77e5\u53bb\u5411\u3002\u90a3\u94c1\u7f51\u79bb\u5730\u9762\u8db3\u6709\u4e00\u4e08\u591a\u6df1\uff0c\u5373\u4fbf\u4e0d\u7f69\u94c1\u7f51\uff0c\u4e01\u6d9b \u4e5f\u96be\u4ee5\u76f4\u4e0a\u76f4\u4e0b\u7684\u8dc3\u4e0a\u6765\u3002 \u3000\u3000\u53ea\u542c\u4e01\u6d9b\u6323\u624e\u7740\u53eb\u9053\uff1a\u201c\u8001\u7518\uff0c\u5feb\u6765\u6551\u6211\uff01\u201d \u3000\u3000\u7518\u9716\u5e72\u7740\u6025\u9053\uff1a\u201c\u8fd9\u4e48\u6df1\uff0c\u6211\u53c8\u600e\u80fd\u6559\u4f60\uff01\u201d \u3000\u3000\u201c\u60f3\u60f3\u529e\u6cd5\uff01\u201d \u3000\u3000\u201c\u6839\u672c\u6ca1\u529e\u6cd5\u53ef\u60f3\uff01\u201d \u3000\u3000\u201c\u6211\u770b\u4f60\u662f\u6d3b\u4eba\u8ba9\u5c3f\u618b\u6c14\uff0c\u5feb\u627e\u638c\u67dc\u7684\u6216\u4e01\u59d1\u5a18\u53bb\u3002\u201d \u3000\u3000\u7518\u9716\u4f9d\u8a00\u614c\u5fd9\u5411\u6d1e\u5916\u5954\u53bb\u3002 \u3000\u3000\u5978\u4e0d\u5bb9\u6613\u5954\u51fa\u6d1e\u5916\u627e\u5230\u4e86\u4e01\u79c0\u541b\u3002 \u3000\u3000\u7518\u9716\u6c14\u5598\u5982\u725b\u7684\u53eb\u9053\uff1a\u201c\u4e0d\u597d\u5566\uff01\u4e0d\u597d\u5566\uff01\u201d \u3000\u3000\u4e01\u79c0\u541b\u7728\u7740\u661f\u7738\u9053\uff1a\u201c\u4f60\u4eec\u770b\u5230\u4e86\u597d\u770b\u7684\uff0c\u600e\u4e48\u53c8\u4e0d\u597d\u4e86\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u59d3\u4e01\u7684\u5df2\u6389\u8fdb\u94c1\u7f51\u53bb\u4e86\uff01\u201d \u3000\u3000\u4e01\u79c0\u541b\u53c8\u9053\uff1a\u201c\u8fd9\u53eb\u4e50\u6781\u751f\u60b2\uff0c\u4f60\u627e\u6211\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u201c\u59d1\u5a18\u5343\u4e07\u5e2e\u5fd9\uff0c\u6551\u4eba\u8981\u7d27\uff01\u201d \u3000\u3000\u201c\u597d\u5427\uff0c\u968f\u6211\u6765\uff01\u201d \u3000\u3000\u8d76\u5230\u90a3\u95f4\u77f3\u5ba4\uff0c\u518d\u5f80\u4e0b\u7167\u65f6\uff0c\u90a3\u94c1\u7f51\u65e9\u5df2\u6536\u7f29\uff0c\u628a\u4e01\u6d9b\u7b3c\u7f69\u5f97\u7b80\u76f4\u50cf\u53ea\u523a\u732c\u3002 \u3000\u3000\u7518\u9716\u5927\u5403\u4e00\u60ca\u9053\uff1a\u201c\u8001\u4e01\uff0c\u4f60\u73b0\u5728\u611f\u89c9\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u618b\u5f97\u8fde\u58f0\u97f3\u90fd\u662f\u95f7\u7684\u9053\uff1a\u201c\u94c1\u7f51\u8d8a\u6765\u8d8a\u7d27\uff0c\u518d\u4e0d\u6551\u4eba\uff0c\u54b1\u9a6c\u4e0a\u5c31\u5b8c\u86cb\u4e86\uff01\u201d \u3000\u3000\u4e01\u79c0\u541b\u5411\u4e0b\u671b\u4e86\u4e00\u671b\uff0c\u54af\u54af\u7b11\u9053\uff1a\u201c\u4e01\u5927\u53d4\uff0c\u4f60\u4e0d\u662f\u8212\u670d\u5417\uff0c\u73b0\u5728\u600e\u4e48\u53d8\u6210\u8fd9\u6837\u5b50\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u4e0a\u6c14\u4e0d\u63a5\u4e0b\u6c14\u9053\uff1a\u201c\u4e01\u59d1\u5a18\uff0c\u522b\u8bf4\u98ce\u51c9\u8bdd\uff0c\u5feb\u6559\u4eba\u8981\u7d27\uff01\u201d \u3000\u3000\u4e01\u79c0\u541b\u9053\uff1a\u201c\u6551\u4f60\u53ef\u4ee5\uff0c\u4e0d\u8fc7\u5148\u8981\u8bb2\u597d\u4ef7\u94b1\u3002\u201d \u3000\u3000\u201c\u600e\u4e48\uff1f\u53c8\u8981\u94b1\uff1f\u201d \u3000\u3000\u201c\u8981\u6551\u4f60\u5fc5\u987b\u53d1\u52a8\u673a\u5173\uff0c\u53d1\u52a8\u673a\u5173\u5f53\u7136\u8981\u94b1\u3002\u201d \u3000\u3000\u201c\u8981\u591a\u5c11\uff1f\u201d \u3000\u3000\u201c\u4e0d\u591a\uff0c\u4e5f\u662f\u56db\u5341\u4e24\u3002\u201d \u3000\u3000\u201c\u4f60\u60f3\u6572\u7af9\u6760\uff1f\u201d \u3000\u3000\u201c\u90a3\u4f60\u5c31\u5728\u4e0b\u9762\u5446\u7740\u597d\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u56db\u5341\u4e24\u5c31\u56db\u5341\u4e24\uff01\u201d \u3000\u3000\u4e01\u79c0\u541b\u4e0d\u77e5\u7528\u7684\u4ec0\u4e48\u65b9\u6cd5\uff0c\u90a3\u94c1\u7f51\u5ffd\u7136\u7f13\u7f13\u5411\u4e0a\u5347\u4e86\u8d77\u6765\uff0c\u7f51\u53e3\u4e5f\u968f\u4e4b\u677e\u4e86\u5f00\u6765\u3002 \u3000\u3000\u5f85\u5feb\u63a5\u8fd1\u5730\u9762\uff0c\u4e01\u6d9b\u8dc3\u4e86\u4e0a\u6765\u65f6\uff0c\u90a3\u94c1\u7f51\u5ffd\u7136\u4e0d\u89c1\uff0c\u5730\u9762\u4e5f\u6062\u590d\u4e86\u539f\u72b6\u3002 \u3000\u3000\u4e01\u6d9b\u543c\u9053\uff1a\u201c\u4f60\u4eec\u7b80\u76f4\u9a97\u4eba\uff01\u201d \u3000\u3000\u4e01\u79c0\u541b\u7b11\u9053\uff1a\u201c\u4f60\u660e\u660e\u5df2\u7ecf\u89c2\u5149\u8fc7\u4e86\uff0c\u600e\u8bf4\u6211\u4eec\u662f\u9a97\u4eba\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u4e3a\u4ec0\u4e48\u53c8\u628a\u4eba\u5f04\u8fdb\u9677\u4e95\u91cc\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e0a\u6b21\u6765\u8fc7\uff0c\u65e9\u5c31\u77e5\u9053\u6709\u4e0d\u51c6\u52a8\u624b\u7684\u89c4\u5b9a\uff0c\u4f60\u81ea\u5df1\u72af\u4e86\u6212\uff0c\u6028\u51c6\uff1f\u201d \u3000\u3000\u201c\u597d\u5566\u597d\u5566\uff0c\u8001\u5b50\u4eca\u540e\u518d\u4e0d\u6765\u5566\uff01\u201d \u3000\u3000\u201c\u4e0d\u6765\u6700\u597d\uff0c\u6211\u4eec\u8c01\u4e5f\u6ca1\u8bf7\u8fc7\u4f60\u6765\uff01\u201d \u3000\u3000\u4e01\u6d9b\u53ea\u80fd\u54d1\u5df4\u5403\u9ec4\u83b2\u3002 \u3000\u3000\u6765\u5230\u6d1e\u5916\uff0c\u4e01\u79c0\u541b\u4f38\u51fa\u624b\u6765\u9053\uff1a\u201c\u62ff\u6765\uff01\u201d \u3000\u3000\u4e01\u6d9b\u6ca1\u8bdd\u8bb2\uff0c\u53ea\u597d\u53c8\u638f\u51fa\u56db\u5341\u4e24\u94f6\u7968\u3002 \u3000\u3000\u4ed6\u4eec\u6ca1\u518d\u505c\u7559\uff0c\u76f4\u5954\u5cb3\u5c0f\u98de\u7b49\u5f85\u4e4b\u5904\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5e76\u65e0\u4e0d\u8010\u70e6\u6a21\u6837\uff0c\u8f6c\u8eab\u5411\u524d\u8d70\u9053\uff1a\u201c\u770b\u591f\u4e86\u5417\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u90fd\u4e0d\u4fbf\u628a\u4e22\u4eba\u7684\u4e8b\u544a\u8bc9\u5cb3\u5c0f\u98de\uff0c\u53ea\u662f\u95f7\u5934\u8ddf\u7740\u5411\u56de\u8d70\u3002 \u3000\u3000\u4e00\u8def\u8fde\u7ecf\u56db\u5173\uff0c\u56e0\u4e3a\u90fd\u5df2\u5de1\u8fc7\uff0c\u5e76\u6ca1\u6709\u518d\u505c\u7559\u3002 \u3000\u3000\u5230\u8fbe\u5bb5\u5173\uff0c\u592a\u9633\u8fd8\u5f88\u9ad8\uff0c\u4e01\u6d9b\u95ee\u9053\uff1a\u201c\u516c\u5b50\uff0c\u54b1\u4eec\u662f\u56de\u53bb\uff0c\u8fd8\u662f\u518d\u5230\u5bb5\u5173\u770b\u770b\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8981\u5de1\u5c31\u628a\u4e94\u5173\u4e00\u6b21\u5de1\u5b8c\uff0c\u514d\u5f97\u660e\u5929\u518d\u803d\u8bef\u65f6\u95f4\u3002\u201d \u3000\u3000\u5230\u4e86\u5bb5\u5173\uff0c\u53c8\u662f\u526f\u7edf\u9886\u5f20\u51e4\u9e23\u8fce\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u4ecd\u88ab\u62db\u5f85\u5728\u5927\u5385\u3002 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u65e9\u77e5\u9053\u5bb5\u5173\u7edf\u9886\u662f\u5973\u7684\uff0c\u4e5f\u65e9\u60f3\u627e\u673a\u4f1a\u89c1\u89c1\uff0c\u504f\u504f\u51e4\u5ae3\u7ea2\u786c\u662f\u4e0d\u80af\u9732\u9762\u3002 \u3000\u3000\u81f3\u4e8e\u5cb3\u5c0f\u98de\uff0c\u5219\u662f\u5bf9\u51e4\u5ae3\u7ea2\u60f3\u89c1\u53c8\u4e0d\u613f\u89c1\u3002 \u3000\u3000\u5f20\u51e4\u9e23\u53c8\u51c6\u5907\u4e86\u9152\u7b75\u3002 \u3000\u3000\u5165\u5e2d\u540e\uff0c\u4e01\u6d9b\u9053\uff1a\u201c\u526f\u7edf\u9886\uff0c\u4e3a\u4ec0\u4e48\u7edf\u9886\u59cb\u7ec8\u4e0d\u51fa\u6765\u89c1\u9762\uff1f\u201d \u3000\u3000\u5f20\u51e4\u9e23\u9053\uff1a\u201c\u7edf\u9886\u4eca\u5929\u8eab\u5b50\u53c8\u4e0d\u8212\u670d\uff0c\u8001\u673d\u5230\u73b0\u5728\u8fde\u5979\u7684\u9762\u90fd\u6ca1\u89c1\u5230\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4e5f\u5c31\u4e0d\u4fbf\u518d\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u6563\u5e2d\u540e\uff0c\u4e09\u4eba\u6b63\u8981\u544a\u8f9e\uff0c\u5ffd\u89c1\u4e00\u540d\u7d2b\u8863\u5c11\u5973\u6765\u5230\u5927\u5385\uff0c\u5411\u5f20\u51e4\u9e23\u798f\u4e86\u4e00\u798f\u9053\uff1a\u201c\u7980\u526f\u7edf \u9886\uff0c\u7edf\u9884\u60f3\u8bf7\u4e00\u4f4d\u5cb3\u4f7f\u8005\u8fc7\u53bb\uff0c\u4e0d\u77e5\u90a3\u4f4d\u662f\u5cb3\u4f7f\u8005\uff1f\u201d \u3000\u3000\u5f20\u51e4\u9e23\u5fd9\u5411\u5cb3\u5c0f\u98de\u62f1\u62f1\u624b\u9053\uff1a\u201c\u65e2\u7136\u7edf\u9886\u6709\u8bf7\uff0c\u5cb3\u4f7f\u8005\u5c31\u8fc7\u53bb\u5427\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8e0c\u8e87\u4e86\u4e00\u4e0b\uff0c\u53ea\u597d\u4ea4\u4ee3\u4e01\u3001\u7518\u4e8c\u4eba\u9053\uff1a\u201c\u4e8c\u4f4d\u4e0d\u5fc5\u7b49\u6211\uff0c\u5c31\u8bf7\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u7d2b\u8863\u5c11\u5973\u518d\u5411\u5cb3\u5c0f\u98de\u65bd\u4e86\u4e00\u793c\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\u8bf7\uff01\u201d \u3000\u3000\u8bf4\u7f62\u8f6c\u8eab\u5e26\u8def\u3002 \u3000\u3000\u5979\u89c1\u5cb3\u5c0f\u98de\u5e74\u7eaa\u5982\u6b64\u4e4b\u8f7b\uff0c\u96be\u514d\u6709\u4e9b\u60ca\u5f02\u3002 \u3000\u3000\u51fa\u4e86\u5927\u5385\uff0c\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u4eec\u6765\u5230\u7684\u4e00\u5171\u4e09\u4eba\uff0c\u7edf\u9886\u4e3a\u4ec0\u4e48\u5355\u5355\u53ec\u89c1\u6211\uff1f\u201d \u3000\u3000\u7d2b\u8863\u5c11\u5973\u9053\uff1a\u201c\u8fd9\u662f\u7edf\u9886\u4ea4\u4ee3\u7684\uff0c\u5a62\u5b50\u4e5f\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\u3002\u201d \u3000\u3000\u201c\u542c\u8bf4\u7edf\u9886\u8eab\u5b50\u4e0d\u8212\u670d\uff0c\u53ef\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u201c\u5979\u8001\u4eba\u5bb6\u4eca\u5929\u7684\u786e\u4e00\u76f4\u61d2\u6d0b\u6d0b\u7684\uff0c\u5230\u73b0\u5728\u8fde\u996d\u8fd8\u6ca1\u5403\u5462\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\u5df2\u7ecf\u5230\u8fbe\uff0c\u7d2b\u8863\u5c11\u5973\u76f4\u63a5\u628a\u5cb3\u5c0f\u98de\u5f15\u8fdb\u5ba2\u5385\uff0c\u6c8f\u4e0a\u8336\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\u8bf7\u7a0d\u5f85\uff0c\u5a62\u5b50 \u9a6c\u4e0a\u53bb\u8bf7\u7edf\u9886\u3002\u201d \u3000\u3000\u6b64\u523b\u7684\u5cb3\u5c0f\u98de\u5185\u5fc3\u96be\u514d\u6709\u4e9b\u4e0d\u5b89\uff0c\u53ea\u6709\u6781\u529b\u88c5\u51fa\u9547\u5b9a\u6a21\u6837\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u8eab\u540e\u54cd\u8d77\u4e00\u4e2a\u5a07\u6ef4\u6ef4\u7684\u58f0\u97f3\u9053\uff1a\u201c\u4f60\u53ef\u662f\u5c0f\u98de\u5417\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u609a\u7136\u56de\u987e\uff0c\u53ea\u89c1\u51e4\u5ae3\u7ea2\u8d6b\u7136\u7ad9\u5728\u8eab\u540e\u3002 \u3000\u3000\u663e\u7136\u5c4f\u98ce\u540e\u53e6\u6709\u4fa7\u95e8\u53ef\u901a\u3002 \u3000\u3000\u6b64\u523b\u7684\u51e4\u5ae3\u7ea2\uff0c\u8102\u7c89\u4e0d\u65bd\uff0c\u9762\u8272\u786e\u662f\u6709\u4e9b\u6194\u60b4\uff0c\u4f46\u90a3\u80a1\u5996\u51b6\u98ce\u97f5\uff0c\u4f3c\u4e4e\u5a9a\u5728\u9aa8\u5b50\u91cc\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u5f97\u4e0d\u7ad9\u8d77\u8eab\u6765\uff0c\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u665a\u8f88\u62dc\u89c1\u51e4\u7edf\u9886\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u59d7\u59d7\u8fd1\u524d\uff0c\u5728\u6905\u5b50\u4e0a\u5750\u4e0b\uff0c\u6b3e\u6b3e\u4e00\u7b11\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u4f60\u8fd9\u6837\u79f0\u547c\u6211\uff0c\u4e0d\u89c9\u5927\u751f\u5206\u4e00 \u70b9\u5417\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6b63\u8272\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6e05\u695a\u5bf9\u51e4\u7edf\u9886\u8fd8\u8be5\u6709\u4ec0\u4e48\u53e6\u5916\u7684\u79f0\u547c\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u89c6\u7ebf\u76f4\u76ef\u5728\u5cb3\u5c0f\u98de\u8138\u4e0a\uff0c\u9053\uff1a\u201c\u4f60\u5728\u72ec\u79c0\u5cf0\u4e0b\u5e90\u4e91\u90a3\u91cc\u4f4f\u4e86\u4e09\u5e74\u591a\uff0c\u96be\u9053\u8fd8\u4e0d\u77e5 \u6211\u548c\u4f60\u5e90\u4f2f\u4f2f\u7684\u5173\u7cfb\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u665a\u8f88\u5f53\u7136\u542c\u8bf4\u8fc7\uff0c\u4f60\u548c\u5e90\u4f2f\u4f2f\u539f\u662f\u592b\u59bb\uff0c\u53ef\u662f\u540e\u6765\u4f60\u79bb\u4ed6\u800c\u53bb\uff0c\u548c\u5e90\u4f2f\u4f2f \u4e4b\u95f4\uff0c\u5df2\u7ecf\u8c08\u4e0d\u4e0a\u534a\u70b9\u5173\u7cfb\u4e86\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u98a6\u7709\u4e00\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u8ba1\u8f83\u7684\u8fd8\u8fd9\u4e48\u6e05\u695a\uff0c\u5e38\u8a00\u9053\u4e00\u65e5\u592b\u59bb\u767e\u65e5\u6069\uff0c \u4f60\u5e94\u8be5\u79f0\u6211\u5e90\u4f2f\u6bcd\u624d\u5bf9\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\uff1a\u201c\u51e4\u7edf\u9886\u8bf7\u539f\u8c05\uff0c\u665a\u8f88\u5728\u72ec\u79c0\u5cf0\u4e0b\u4e09\u5e74\uff0c\u53ea\u77e5\u5e90\u4f2f\u4f2f\u662f\u72ec\u8eab\u4e00\u4eba\uff0c\u6839\u672c\u4e0d\u660e\u767d \u8c01\u662f\u5e90\u4f2f\u6bcd\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u4f3c\u6b32\u53d1\u4f5c\uff0c\u4f46\u77ac\u606f\u95f4\u5374\u53c8\u6447\u5934\u4e00\u7b11\u9053\uff1a\u201c\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u6027\u5b50\u7adf\u662f\u8fd9\u6837\u5014\u5f3a\uff0c\u56de\u7b54\u6211 \u4e00\u53e5\u8bdd\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6765\u5230\u5929\u8c37\uff0c\u53ef\u662f\u5e90\u4e91\u6559\u5506\u4f60\u6765\u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5373\u4f7f\u662f\u5e90\u4f2f\u4f2f\u8981\u6211\u6765\u7684\uff0c\u4e5f\u4e0d\u8be5\u8bf4\u6210\u662f\u6559\u5506\uff0c\u4f55\u51b5\u8fd9\u5b8c\u5168\u51fa\u4e8e\u6211\u81ea\u5df1\u7684\u5fc3 \u610f\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u4e3a\u4ec0\u4e48\u522b\u5904\u4e0d\u53bb\uff0c\u504f\u504f\u8981\u5230\u5929\u8c37\u6765\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6211\u542c\u8bf4\u5929\u8c37\u6b63\u5728\u62db\u5175\u4e70\u9a6c\uff0c\u6240\u4ee5\u624d\u8bda\u5fc3\u8bda\u610f\u6765\u6295\u6548\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u8bdd\u6050\u6015\u8a00\u4e0d\u7531\u8877\u5427\uff1f\u201d \u3000\u3000\u201c\u51e4\u7edf\u9886\u600e\u77e5\u6211\u8a00\u4e0d\u7531\u8877\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u660e\u767d\uff0c\u4f60\u548c\u5c0f\u9e64\u5230\u5929\u8c37\u6765\uff0c\u4e0d\u5916\u662f\u5949\u5e90\u4e91\u4e4b\u547d\uff0c\u5411\u6211\u8ba8\u56de\u90a3\u518c\u2018\u70bc\u5fc3\u5927\u6cd5\u79d8 \u7b08\u2019\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u795e\u8272\u51db\u7136\u9053\uff1a\u201c\u5982\u679c\u51e4\u7edf\u9886\u8ba4\u4e3a\u5982\u6b64\uff0c\u665a\u8f88\u4e5f\u4e0d\u5426\u8ba4\uff0c \u3000\u3000\u90a3\u518c\u79d8\u7b08\u672c\u6765\u5c31\u662f\u5e90\u4f2f\u4f2f\u6240\u6709\uff0c\u51e4\u7edf\u9886\u7528\u4e0d\u6b63\u5f53\u7684\u624b\u6cd5\u53d6\u56de\uff0c\u96be\u9053\u5e90\u4f2f\u4f2f\u4e0d\u53ef\u4ee5\u8ba8\u56de\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u4e00\u5f20\u5634\uff0c\u4e0d\u9519\uff0c\u90a3\u518c\u79d8\u7b08\u6211\u62ff\u6765\u4e86\uff0c\u53ef\u60dc\u4f60\u4eec\u8c01\u4e5f\u8ba8\u4e0d\u53bb\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u51b7\u7b11\u9053\uff1a\u201c\u665a\u8f88\u660e\u767d\uff0c\u51e4\u7edf\u9886\u662f\u628a\u90a3\u79d8\u7b08\u732e\u7ed9\u6559\u4e3b\u4e86\uff0c\u5bf9\u5417\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u4eec\u8c01\u6562\u5411\u6559\u4e3b\u53bb\u8ba8\uff1f\u201d \u3000\u3000\u5979\u6fc0\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u8138\u8272\u4e00\u53d8\uff0c\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u4e8b\u4f60\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u82e5\u8981\u4eba\u4e0d\u77e5\uff0c\u9664\u975e\u5df2\u83ab\u4e3a\uff0c\u51e4\u7edf\u9886\u65e2\u7136\u80fd\u505a\u5c71\u8fd9\u79cd\u4e8b\u6765\uff0c\u665a\u8f88\u80fd \u77e5\u9053\u4e5f\u662f\u7406\u6240\u5f53\u7136\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u84e6\u5730\u67f3\u7709\u5012\u7ad6\uff0c\u674f\u773c\u5706\u7741\u9053\uff1a\u201c\u5cb3\u5c0f\u98de\uff0c\u83ab\u975e\u4f60\u8fd8\u6562\u627e\u5230\u6559\u4e3b\u90a3\u91cc\u53bb\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6de1\u7136\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u867d\u7136\u4e0d\u6562\u5411\u6559\u4e3b\u8ba8\uff0c\u4f46\u8fd9\u7b14\u5e10\u5374\u8981\u8bb0\u5728\u4f60\u7684\u5934\u4e0a\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u731b\u7684\u4e00\u62cd\u8336\u51e0\u9053\uff1a\u201c\u5cb3\u5c0f\u98de\uff0c\u4f60\u7684\u80c6\u5b50\u4e5f\u672a\u514d\u592a\u5927\uff0c\u4e5f\u592a\u65e0\u6cd5\u65e0\u5929\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u970d\u7136\u7ad9\u8eab\u800c\u8d77\uff0c\u5927\u58f0\u9053\uff1a\u201c\u51e4\u7edf\u9886\uff0c\u8fd9\u5c31\u662f\u4f60\u8981\u6211\u6765\u7684\u76ee\u7684\u5417\uff1f\u201d \u3000\u3000\u201c\u597d\u4e00\u4e2a\u4e0d\u8ba4\u62ac\u4e3e\u7684\u4e1c\u897f\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5c82\u80af\u5f53\u9762\u53d7\u8fb1\uff0c\u624b\u6309\u5251\u628a\u9053\uff1a\u201c\u5728\u4e0b\u867d\u4e0d\u80fd\u5411\u6559\u4e3b\u8ba8\u53d6\u79d8\u7b08\uff0c\u4f46\u5374\u53ef\u4ee5\u5728\u6559\u4e3b\u9762 \u524d\uff0c\u8bf4\u51fa\u4f60\u80cc\u592b\u968f\u5f92\u6deb\u5954\u7684\u4e71\u4f26\u65e0\u803b\u884c\u4e3a\uff0c\u5230\u90a3\u65f6\u6211\u770b\u4f60\u8fd8\u6709\u4f55\u989c\u9762\u4f4f\u5929\u8c37\u6df7\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u7adf\u7136\u771f\u7684\u628a\u51e4\u5ae3\u7ea2\u6151\u4f4f\uff0c\u56e0\u4e3a\u5979\u66fe\u548c\u5e90\u4e91\u662f\u592b\u59bb\uff0c\u4ee5\u53ca\u5055\u9a6c\u662d\u96c4\u6deb\u5954\u7684\u4e8b\uff0c\u6559 \u4e3b\u81f3\u4eca\u6beb\u4e0d\u77e5\u60c5\uff0c\u82e5\u4e00\u4f46\u88ab\u6559\u4e3b\u5f97\u77e5\uff0c\u5979\u4fbf\u5f88\u53ef\u80fd\u4ece\u6b64\u5728\u5929\u8c37\u8eab\u8d25\u540d\u88c2\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u88ab\u6253\u5165 \u80b2\u5316\u57ce\uff0c\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u5979\u90a3\u6709\u4e0d\u7acb\u5373\u8f6f\u4e0b\u6765\u4e4b\u7406\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u597d\u4e00\u53cc\u767d\u5982\u7389\u8f6f\u5982\u7ef5\u7684\u624b\uff0c\u5df2\u7ecf\u8f7b\u8f7b\u642d\u4e0a\u5cb3\u5c0f\u98de\u80a9\u5934\uff0c\u7528\u90a3\u67d4\u5a9a\u800c\u53c8\u4eb2\u5207\u7684 \u58f0\u97f3\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u4f60\u600e\u4e48\u53cd\u800c\u5bf9\u6211\u53d1\u8d77\u813e\u6c14\u6765\u4e86\uff1f\u7eb5\u7136\u4f60\u77a7\u4e0d\u8d77\u6211\u7684\u4e3a\u4eba\uff0c\u4f46\u6211\u628a\u4f60\u603b\u8ba4\u4e3a \u662f\u81ea\u5df1\u4eba\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6211\u6839\u672c\u626f\u4e0d\u4e0a\u534a\u70b9\u5173\u7cfb\u3002\u5982\u679c\u8bf4\u662f\u81ea\u5df1\u4eba\uff0c\u90a3\u4e0d\u662f\u7b11\u8bdd\u5417\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u5e7d\u5e7d\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5343\u4e0d\u8be5\uff0c\u4e07\u4e0d\u8be5\uff0c\u6211\u4e0d\u8be5\u628a\u79d8\u7b08\u732e\u7ed9\u4e86\u6559\u4e3b\uff0c\u8981\u4e0d\u7136\uff0c\u6211\u4e00 \u5b9a\u628a\u5b83\u4ea4\u7ed9\u4f60\u548c\u5c0f\u9e64\uff0c\u8ba9\u4f60\u4eec\u4ea4\u8fd8\u5e90\u4e91\u5b8c\u6210\u5fc3\u613f\u3002\u201d \u3000\u3000\u5979\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4ef6\u4e8b\uff0c\u5fc5\u987b\u8ba9\u4f60\u77e5\u9053\uff0c\u627e\u548c\u9a6c\u662d\u96c4\uff0c\u53ea\u662f\u4e00\u8d77\u51fa\u8d70\uff0c\u5f7c\u6b64 \u4e4b\u95f4\uff0c\u7edd\u6ca1\u505a\u51fa\u4e71\u4e03\u516b\u7cdf\u7684\u4e8b\uff0c\u4f55\u51b5\u4ed6\u73b0\u5728\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7acb\u5373\u95ee\u9053\uff1a\u201c\u4ed6\u6b7b\u4e86\uff0c\u53ef\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u600e\u53ef\u968f\u4fbf\u4e71\u8bf4\uff0c\u4e0d\u4fe1\u4f60\u53ef\u4ee5\u95ee\u95ee\u5bb5\u5173\u4e0a\u7684\u4eba\uff0c\u8c01\u89c1\u8fc7\u6709\u4e2a\u53eb\u9a6c\u662d\u96c4 \u7684\u6765\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u9a6c\u662d\u96c4\u6b7b\u4e86\uff0c\u5f53\u7136\u662f\u4ef6\u597d\u4e8b\uff0c\u6211\u548c\u8881\u5927\u54e5\u90fd\u8be5\u989d\u624b\u79f0\u5e86\uff0c\u5929\u7406\u662d\u7136\uff0c\u5584\u6076 \u5230\u5934\u603b\u662f\u6709\u62a5\u5e94\u7684\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u7728\u7740\u7738\u5b50\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u8fd8\u8ba4\u4e3a\u4ed6\u4e0d\u662f\u597d\u4eba\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5982\u679c\u9a6c\u662d\u96c4\u662f\u597d\u4eba\uff0c\u5929\u4e0b\u5c31\u6ca1\u6709\u574f\u4eba\u4e86\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u5e26\u4e9b\u65e0\u5948\uff0c\u82e6\u7b11\u7740\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u8c08\u8fd9\u4e2a\uff0c\u5c0f\u9e64\u4eca\u5929\u4e3a\u4ec0\u4e48\u4e0d\u6765\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eca\u5929\u4e0d\u8212\u670d\uff0c\u8fde\u65e9\u996d\u90fd\u6ca1\u8d77\u4e00\u5403\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u6e05\u695a\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u8212\u670d\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u662f\u533b\u751f\uff0c\u800c\u4e14\u4eca\u5929\u4e5f\u6ca1\u89c1\u8fc7\u4ed6\uff0c\u600e\u4f1a\u77e5\u9053\u4ed6\u5f97\u7684\u4ec0\u4e48\u75c5\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u603b\u7b97\u7565\u7565\u653e\u4e0b\u5fc3\u6765\uff0c\u82e5\u8881\u5c0f\u9e64\u628a\u6628\u665a\u7684\u4e8b\u5ba3\u626c\u51fa\u53bb\uff0c\u5979\u5b9e\u5728\u4e0d\u77e5\u5e94\u8be5\u5982\u4f55\u81ea\u5904\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u5979\u771f\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\u5ea6\u541b\u5b50\u4e4b\u8179\u4e86\uff0c\u8fd9\u79cd\u4e8b\u8881\u5c0f\u9e64\u600e\u80af\u5bf9\u5916\u5ba3\u626c\u5462\uff1f \u3000\u3000\u5979\u5355\u72ec\u63a5\u89c1\u5cb3\u5c0f\u98de\uff0c\u4e0d\u5916\u662f\u60f3\u6253\u542c\u2014\u4e0b\u8881\u5c0f\u9e64\u6b64\u523b\u7684\u666f\u51b5\u3002 \u3000\u3000\u73b0\u5728\u597d\u5728\u65e2\u5df2\u5f97\u77e5\u4e8b\u60c5\u5e76\u672a\u5f04\u69fd\uff0c\u5f53\u7136\u5df2\u4e0d\u9700\u8981\u518d\u95ee\u4ec0\u4e48\u3002 \u3000\u3000\u4f46\u5979\u5374\u4e0d\u5f97\u4e0d\u5bf9\u5cb3\u5c0f\u98de\u5ba2\u6c14\u548c\u7b3c\u7edc\u4e00\u756a\uff0c\u968f\u5373\u6d45\u6d45\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u73b0\u5728\u662f\u8d35\u5ba2\uff0c\u6211\u8be5\u597d\u597d \u62db\u5f85\u62db\u5f85\u4f60\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u521a\u624d\u5f20\u526f\u7edf\u9886\u5df2\u7ecf\u62db\u5f85\u8fc7\u665a\u9910\u4e86\uff0c\u4e0d\u52b3\u51e4\u7edf\u9886\u8d39\u5fc3\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u6b49\u7136\u9053\uff1a\u201c\u6211\u56e0\u8eab\u4e0a\u4e0d\u8212\u670d\uff0c\u6ca1\u80fd\u4eb2\u81ea\u53bb\u966a\u4f60\uff0c\u4f60\u53ef\u522b\u4ecb\u610f\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u51e4\u7edf\u9886\u7528\u4e0d\u7740\u5ba2\u6c14\uff0c\u665a\u8f88\u80fd\u8499\u4f60\u5355\u72ec\u62db\u5f85\uff0c\u5df2\u7ecf\u7b97\u662f\u5f88\u8363\u5e78\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd9\u5c31\u8981\u8d70\u5417\uff1f\u201d \u3000\u3000\u201c\u65e2\u7136\u5df2\u7ecf\u62dc\u89c1\u8fc7\u51e4\u7edf\u9886\uff0c\u4e0d\u8d70\u8fd8\u7b49\u6c41\u4e48\uff1f\u201d \u3000\u3000\u201c\u52b3\u9a7e\u4f60\u56de\u53bb\u540e\uff0c\u544a\u8bc9\u5c0f\u9e64\u8981\u4ed6\u5230\u8fd9\u6211\u91cc\u6765\u4e00\u9053\uff0c\u4f60\u53ef\u4ee5\u4e0d\u6765\u770b\u6211\uff0c\u4ed6\u4e0d\u80fd\u4e0d\u6765\u770b\u6211\u3002\u201d \u3000\u3000\u201c\u4ed6\u548c\u6211\u6709\u4ec0\u4e48\u4e24\u6837\uff1f\u201d \u3000\u3000\u201c\u4f60\u5728\u72ec\u79c0\u5cf0\u4e0b\u65f6\uff0c\u6211\u5df2\u548c\u5e90\u4e91\u79bb\u5f00\u591a\u5e74\uff0c\u800c\u4ed6\u5374\u548c\u6211\u6709\u4e09\u5e74\u591a\u76f8\u5904\u65f6\u95f4\u3002\u201d \u3000\u3000\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u665a\u8f88\u603b\u5e0c\u671b\u51e4\u7edf\u9886\u7528\u4e0d\u7740\u548c\u8881\u5927\u54e5\u8868\u793a\u4eb2\u8fd1\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8881\u5927\u54e5\u662f\u5e90\u4f2f\u4f2f\u4e00\u624b\u629a\u517b\u5927\u7684\uff0c\u4ed6\u4eec\u4e4b\u95f4\uff0c\u65e2\u662f\u5e08\u5f92\uff0c\u53c8\u6069\u82e5\u7236\u5b50\uff0c\u800c\u4f60\u5374\u662f\u80cc\u5f03\u5e90 \u4f2f\u4f2f\u7684\uff0c\u4f60\u60f3\u4ed6\u5bf9\u4f60\u80fd\u6709\u597d\u611f\u5417\uff1f\u201d \u3000\u3000\u201c\u53ef\u662f\u5f53\u5e74\u6211\u5bf9\u4ed6\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u4ea4\u4ee3\uff0c\u6211\u4f1a\u8f6c\u544a\u8881\u5927\u54e5\u7684\uff0c\u81f3\u4e8e\u4ed6\u4f1a\u4e0d\u4f1a\u6765\u89c1\u4f60\uff0c\u90a3\u5c31\u53ea\u6709\u770b\u4ed6\u7684\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u56de\u5230\u4e94\u5173\u603b\u9547\u5e9c\u540e\uff0c\u5df2\u662f\u4e00\u66f4\u5de6\u53f3\u3002 \u3000\u3000\u4ed6\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u4fbf\u662f\u63a2\u671b\u8881\u5c0f\u9e64\uff0c\u4e00\u6765\u770b\u770b\u4ed6\u662f\u5426\u5df2\u7ecf\u597d\u4e86\uff0c\u4e8c\u6765\u7535\u8981\u628a\u51e4\u5ae3\u7ea2\u7684\u4ea4\u4ee3\u8f6c \u544a\u4ed6\u3002 \u3000\u3000\u504f\u504f\u8881\u5c0f\u9e64\u5df2\u7ecf\u5173\u4e0a\u623f\u95e8\uff0c\u91cc\u9762\u8fde\u706f\u4e5f\u7184\u4e86\uff0c\u4ed6\u53ea\u597d\u4e0d\u518d\u6253\u6270\u3002 \u3000\u3000\u6b21\u65e5\u65e9\u9910\u65f6\uff0c\u4ecd\u4e0d\u89c1\u8881\u5c0f\u9e64\u7684\u5f71\u5b50\uff0c\u5cb3\u5c0f\u98de\u4e0d\u5f97\u4e0d\u518d\u53bb\u770b\u4ed6\u3002 \u3000\u3000\u8fdb\u95e8\uff0c\u4fbf\u89c1\u8881\u5c0f\u9e64\u62ab\u8863\u5750\u5728\u6905\u5b50\uff0c\u4e24\u773c\u65e0\u795e\uff0c\u9762\u8272\u82cd\u767d\u3002 \u3000\u3000\u201c\u8881\u5927\u54e5\uff0c\u4f60\u662f\u600e\u4e48\u75c5\u4e86\u7684\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u52c9\u5f3a\u73b0\u51fa\u7b11\u5bb9\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u5c0f\u6bdb\u75c5\uff0c\u53ea\u662f\u6709\u4e9b\u4e0d\u8212\u670d\u800c\u5df2\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u665a\u4e0a\u7740\u4e86\u51c9\uff1f\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u662f\u5427\u3002\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u60f3\u544a\u8bc9\u8881\u5927\u54e5\u4e00\u4ef6\u4e8b\uff0c\u6628\u5929\u6211\u89c1\u7740\u51e4\u5ae3\u7ea2\u4e86\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u53cc\u76ee\u5ffd\u7136\u2014\u4eae\uff0c\u6025\u6025\u95ee\u9053\uff1a\u201c\u5979\u53ef\u8bf4\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u663e\u7136\uff0c\u8881\u5c0f\u9e64\u662f\u62c5\u5fc3\u51e4\u5ae3\u7ea2\u628a\u524d\u665a\u7684\u4e8b\u5411\u5cb3\u5c0f\u98de\u900f\u9732\uff0c\u50cf\u5979\u90a3\u79cd\u65e0\u803b\u7684\u5973\u4eba\uff0c\u662f\u4e0d\u5728\u4e4e \u8fd9\u4e9b\u7684\uff0c\u4f46\u4ed6\u81ea\u5df1\u5374\u4e0d\u80fd\u4e0d\u5728\u4e4e\uff0c\u4e07\u4e00\u8fd9\u4e8b\u88ab\u5cb3\u5c0f\u98de\u77e5\u9053\uff0c\u90a3\u5c06\u4f55\u4ee5\u81ea\u5904\uff1f \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5979\u8bf4\u90a3\u518c\u2018\u70bc\u5fc3\u5927\u6cd5\u79d8\u7b08\u2019\uff0c\u5df2\u7ecf\u732e\u7ed9\u4e86\u6559\u4e3b\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u8fd9\u4e8b\u5148\u524d\u4f60\u5df2\u7ecf\u544a\u8bc9\u8fc7\u6211\u4e86\uff0c\u5979\u8fd8\u8bf4\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5979\u5e0c\u671b\u4f60\u53bb\u770b\u770b\u5979\uff0c\u542c\u5979\u7684\u8bed\u6c14\uff0c\u597d\u50cf\u5230\u73b0\u5728\u8fd8\u6ca1\u89c1\u5230\u4f60\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8fd9\u624d\u653e\u4e0b\u5fc3\u4e2d\u4e00\u5757\u77f3\u5934\uff0c\u800c\u4e14\u7cbe\u795e\u4e5f\u6e10\u6e10\u632f\u4f5c\u8d77\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7d27\u76ef\u7740\u95ee\u9053\uff1a\u201c\u8881\u5927\u54e5\u60f3\u4e0d\u60f3\u53bb\u89c1\u5979\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u8eab\u4e3a\u5de1\u5173\u4f7f\u8005\uff0c\u968f\u65f6\u90fd\u8981\u5de1\u89c6\u5230\u5bb5\u5173\uff0c\u60f3\u8eb2\u7740\u5979\u90a3\u662f\u4e0d\u53ef\u80fd\u7684\u3002\u4e0d\u8fc7\u6211\u51b3 \u4e0d\u60f3\u7279\u522b\u5355\u72ec\u53bb\u89c1\u5979\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u73b0\u5728\u53bb\u89c1\u5979\uff0c\u6bd4\u4ee5\u524d\u89c1\u5979\uff0c\u5728\u611f\u89c9\u4e0a\u603b\u8981\u7a0d\u597d\u4e9b\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6709\u4e9b\u4e0d\u89e3\uff1a\u201c\u6211\u4e0d\u61c2\u8fd9\u8bdd\u7684\u610f\u601d\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u636e\u5979\u8bf4\u9a6c\u662d\u96c4\u5df2\u7ecf\u6b7b\u4e86\uff0c\u5355\u72ec\u89c1\u5979\u4e00\u4e2a\uff0c\u6bd4\u89c1\u5979\u548c\u9a6c\u521a\u96c4\u5728\u4e00\u8d77\u65f6\uff0c\u4e0d\u662f \u8981\u597d\u8fc7\u5f97\u591a\u5417\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u56e0\u66fe\u542c\u51e4\u5ae3\u7ea2\u8bf4\u8fc7\u9a6c\u662d\u96c4\u5df2\u6b7b\uff0c\u5e76\u672a\u8fc7\u5206\u5728\u610f\uff0c\u6b63\u8981\u518d\u95ee\uff0c\u5ffd\u89c1\u4e01\u6d9b\u51b2\u4e86\u8fdb\u6765\u9053\uff1a \u201c\u5cb3\u516c\u5b50\uff0c\u526f\u603b\u9547\u6709\u8bf7\uff0c\u8981\u4f60\u5230\u5927\u5385\u53bb\uff01\u201d \u3000\u3000\u7236\u4eb2\u76f8\u53ec\uff0c\u800c\u4e14\u53c8\u5728\u5927\u5385\uff0c\u5fc5\u662f\u6709\u5341\u5206\u8981\u7d27\u7684\u4e8b\uff0c\u5cb3\u5c0f\u98de\u522b\u8fc7\u8881\u5c0f\u9e64\uff0c\u7acb\u5373\u6765\u5230\u5927\u5385\u3002 \u3000\u3000\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\u7684\uff0c\u82b1\u7389\u9e9f\u7adf\u662f\u548c\u5927\u516c\u4e3b\u76f8\u5bf9\u800c\u5750\u3002 \u3000\u3000\u53e6\u5916\u5f15\u4eba\u6ce8\u76ee\u7684\u662f\u5927\u516c\u4e3b\u8eab\u540e\u7ad9\u7740\u4e00\u540d\u5343\u5a07\u767e\u5a9a\u7684\u7ea2\u8863\u5c11\u5973\uff0c\u7adf\u662f\u6843\u82b1\u3002 \u3000\u3000\u6843\u82b1\u8001\u8fdc\u5c31\u53cc\u7738\u7d27\u76ef\u7740\u5cb3\u5c0f\u98de\uff0c\u9732\u51fa\u4eb2\u5207\u53ef\u4eba\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6309\u7167\u793c\u6570\u5148\u5411\u5927\u516c\u4e3b\u89c1\u793c\uff0c\u518d\u5411\u7236\u4eb2\u82b1\u7389\u9e9f\u89c1\u793c\u3002 \u3000\u3000\u53ea\u542c\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\uff0c\u4eca\u5929\u5927\u516c\u4e3b\u4eb2\u81ea\u524d\u6765\uff0c\u8981\u628a\u4f60\u5e26\u5230\u603b\u575b\u53bb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u613f\u79bb\u5f00\u7236\u4eb2\uff0c\u5fc3\u5934\u4e00\u9707\u9053\uff1a\u201c\u5351\u804c\u65e9\u5411\u4e09\u516c\u4e3b\u7980\u62a5\u8fc7\uff0c\u4e0d\u60f3\u79bb\u5f00\u4e94\u5173\u603b\u9547\uff0c\u4e3a \u4ec0\u4e48\u53c8\u8981\u8c03\u5230\u603b\u575b\u53bb\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\u653e\u5fc3\uff0c\u5230\u4e86\u603b\u575b\u4ee5\u540e\uff0c\u5f88\u5feb\u5c31\u53ef\u4ee5\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u8981\u53bb\u505a\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u7684\u8363\u5e78\uff0c\u6559\u4e3b\u8981\u53ec\u89c1\u4f60\uff0c\u672c\u6559\u81ea\u6210\u7acb\u4ee5\u540e\uff0c\u8fdb\u5165\u5929\u8c37\u7684\u6b66\u6797\u9ad8\u624b\uff0c \u4e0d\u4e0b\u6570\u767e\u4eba\uff0c\u80fd\u5728\u8fd9\u4e48\u77ed\u7684\u65f6\u95f4\u5185\u8499\u6559\u4e3b\u53ec\u89c1\u7684\uff0c\u4f60\u8fd8\u662f\u7b2c\u4e00\u4e2a\u3002\u201d \u3000\u3000\u53c8\u542c\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\u53ea\u7ba1\u653e\u5fc3\u524d\u53bb\u3002\u5bf9\u4f60\u6765\u8bf4\uff0c\u8fd9\u662f\u4ef6\u5929\u5927\u7684\u597d\u4e8b\uff0c\u4e5f\u662f\u4e94\u5173\u603b\u9547 \u4e0a\u7684\u5149\u8363\u3002\u201d \u3000\u3000\u8bf4\u7740\u671b\u5411\u5927\u516c\u4e3b\u9053\uff1a\u201c\u7b49\u5351\u804c\u6b3e\u5f85\u8fc7\u5927\u516c\u4e3b\u518d\u8d70\u5982\u4f55\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u6211\u9a6c\u4e0a\u5e26\u5cb3\u4f7f\u8005\u56de\u603b\u575b\u53bb\uff01\u201d \u3000\u3000\u5f53\u5cb3\u5c0f\u98de\u56de\u623f\u6362\u8fc7\u4e00\u5957\u8863\u670d\u540e\uff0c\u5927\u516c\u4e3b\u548c\u6843\u82b1\u5df2\u5728\u5927\u5385\u95e8\u53e3\u7b49\u4faf\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u76f4\u9001\u51fa\u5927\u95e8\uff0c\u4e95\u793a\u610f\u5cb3\u5c0f\u98de\u8981\u591a\u8c28\u614e\u5c0f\u5fc3\u3002 \u3000\u3000\u8def\u4e0a\uff0c\u5cb3\u5c0f\u98de\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u5927\u516c\u4e3b\uff0c\u6559\u4e3b\u4e3a\u4ec0\u4e48\u8981\u53ec\u89c1\u5c5e\u4e0b\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u5f53\u7136\u662f\u56e0\u4e3a\u4f60\u6587\u624d\u6b66\u529f\u51fa\u4f17\uff0c\u5c24\u5176\u8fd9\u6837\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5c06\u6765\u7684\u6210\u5c31\u66f4\u662f\u4e0d\u53ef \u9650\u91cf\u3002\u201d \u3000\u3000\u201c\u5927\u516c\u4e3b\u8fc7\u5956\u4e86\uff01\u201d \u3000\u3000\u201c\u4e8b\u5b9e\u4ff1\u5728\uff0c\u4f60\u8fde\u8fc7\u4e94\u5173\u7684\u6210\u7ee9\uff0c\u90fd\u65e9\u5df2\u62a5\u5230\u6559\u4e3b\u90a3\u91cc\uff0c\u8fd8\u5ba2\u6c14\u4ec0\u4e48\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u518d\u671b\u5411\u6843\u82b1\u9053\uff1a\u201c\u606d\u559c\u6843\u82b1\u59d1\u5a18\uff0c\u96be\u5f97\u4f60\u73b0\u5728\u7adf\u80fd\u968f\u4f8d\u5728\u5927\u516c\u4e3b\u8eab\u8fb9\u3002\u201d \u3000\u3000\u6843\u82b1\u8fde\u5fd9\u65bd\u4e86\u4e00\u793c\u9053\uff1a\u201c\u82e5\u975e\u516c\u5b50\u76f8\u6551\uff0c\u5a62\u5b50\u90a3\u6709\u4eca\u5929\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u63a5\u7740\u9053\uff1a\u201c\u6843\u82b1\u4eba\u597d\u53c8\u52e4\u5feb\uff0c\u6211\u5f88\u559c\u6b22\u5979\uff0c\u4eca\u540e\u5cb3\u4f7f\u8005\u5982\u679c\u6709\u4ec0\u4e48\u4e8b\u627e\u6211\uff0c\u6211\u4f1a \u968f\u65f6\u6d3e\u6843\u82b1\u548c\u4f60\u8054\u7edc\u3002\u201d \u3000\u3000\u8d70\u4e86\u4e03\u3001\u516b\u91cc\u8def\u4ee5\u540e\uff0c\u5df2\u5230\u8fbe\u4e09\u5341\u516d\u53f7\u62db\u8d24\u5206\u9986\u95e8\u524d\u7684\u5c0f\u5f84\u3002 \u3000\u3000\u8fd9\u91cc\u662f\u901a\u5f80\u603b\u575b\u7684\u6377\u5f84\uff0c\u4ee5\u524d\u6843\u82b1\u548c\u73ab\u7470\u90fd\u8d70\u8fc7\u8fd9\u91cc\uff0c\u6240\u4ee5\u624d\u4f1a\u88ab\u4f4f\u5728\u4e09\u5341\u516d\u53f7\u5206\u9986\u7684 \u5cb3\u5c0f\u98de\u9047\u4e0a\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u771f\u60f3\u80fd\u89c1\u5230\u6797\u91d1\u5b9d\u3002 \u3000\u3000\u4ed6\u5728\u8fd9\u91cc\u4f4f\u8fc7\u591a\u65e5\uff0c\u5df2\u548c\u6797\u91d1\u5b9d\u5efa\u7acb\u4e86\u4e0d\u9519\u7684\u611f\u60c5\u3002 \u3000\u3000\u8fd8\u771f\u5de7\u5f97\u5f88\uff0c\u6797\u91d1\u5b9d\u6b63\u5728\u5929\u4e95\u91cc\u5750\u7740\u51fa\u795e\uff0c\u4e00\u89c1\u95e8\u5916\u5c0f\u8def\u4e0a\u8d70\u6765\u4e86\u5927\u516c\u4e3b\u3001\u5cb3\u5c0f\u98de\u548c\u6843 \u82b1\uff0c\u8fde\u5fd9\u7531\u91cc\u9762\u5954\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u5148\u62dc\u89c1\u8fc7\u5927\u516c\u4e3b\uff0c\u518d\u62dc\u89c1\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u597d\u4e45\u4e0d\u89c1\u516c\u5b50\u4e86\uff0c\u60a8\u597d\u5417\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u5f88\u597d\uff0c\u8fd9\u91cc\u76ee\u524d\u53ef\u6709\u65b0\u5ba2\u4eba\u6765\uff1f\u201d \u3000\u3000\u6797\u91d1\u5b9d\u6447\u5934\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e9b\u5929\u4f60\u5230\u603b\u9986\u53bb\uff0c\u662f\u5426\u6709\u4eba\u6b3a\u8d1f\u4f60\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u90fd\u5bf9\u5c0f\u7684\u51b7\u6de1\u5f97\u5f88\uff0c\u4f46\u5374\u6ca1\u6b3a\u8d1f\uff0c\u6240\u4ee5\uff0c\u5c0f\u7684\u60f3\u6c42\u516c\u5b50\u5e2e\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u201c\u4f60\u51c6\u5907\u8981\u6211\u5e2e\u4ec0\u4e48\u5fd9\uff1f\u201d \u3000\u3000\u201c\u6c42\u516c\u5b50\u60f3\u529e\u6cd5\u628a\u5c0f\u7684\u4e5f\u8c03\u5230\u4e94\u5173\u603b\u9547\u53bb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u540c\u6837\u6709\u8fd9\u79cd\u60f3\u6cd5\uff0c\u5982\u679c\u80fd\u628a\u6797\u91d1\u5b9d\u4e5f\u8c03\u5230\u4e94\u5173\u603b\u9547\uff0c \u3000\u3000\u81ea\u5df1\u5c31\u7b49\u4e8e\u591a\u4e86\u4e00\u4e2a\u5fc3\u8179\u4eba\uff0c\u53ef\u60dc\u4ed6\u76ee\u524d\u5e76\u6ca1\u6709\u8fd9\u79cd\u80fd\u529b\u3002 \u3000\u3000\u5927\u516c\u4e3b\u65e9\u770b\u51fa\u5cb3\u5c0f\u98de\u7684\u5fc3\u610f\uff0c\u8f7b\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u6797\u91d1\u5b9d\uff0c\u6682\u65f6\u5b89\u4e0b\u5fc3\u6765\u7559\u5728\u8fd9\u91cc\uff0c\u6162\u6162\u7684 \u6211\u518d\u66ff\u4f60\u60f3\u529e\u6cd5\u3002\u201d \u3000\u3000\u6797\u91d1\u5b9d\u559c\u51fa\u671b\u5916\uff0c\u8fde\u5fd9\u8dea\u5730\u5411\u4eba\u516c\u4e3b\u78d5\u4e86\u4e2a\u5934\u9053\uff1a\u201c\u5c0f\u7684\u53e9\u8c22\u5927\u516c\u4e3b\u6069\u5178\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u81ea\u7136\u4e5f\u662f\u559c\u4e4b\u4e0d\u80dc\uff0c\u62c9\u8d77\u6797\u91d1\u5b9d\u9053\uff1a\u201c\u4f60\u8be5\u653e\u5fc3\u4e86\uff0c\u6709\u65f6\u95f4\u6211\u4e5f\u4f1a\u6765\u770b\u4f60\u3002\u201d \u3000\u3000\u6797\u91d1\u5b9d\u518d\u6b21\u6b22\u5929\u559c\u5730\u7684\u5411\u5cb3\u5c0f\u98de\u62dc\u8c22\u3002 \u3000\u3000\u5927\u516c\u4e3b\u7b49\u4e09\u4eba\u7ee7\u7eed\u524d\u8fdb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5927\u516c\u4e3b\uff0c\u7531\u8fd9\u91cc\u5230\u603b\u575b\u8fd8\u6709\u591a\u8fdc\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4e0d\u8fc7\u5341\u91cc\u5de6\u5728\uff0c\u53ea\u662f\u5fc5\u987b\u5230\u8fbe\u4ee5\u540e\u624d\u80fd\u770b\u5230\u3002\u201d \u3000\u3000\u5929\u8c37\u4e4b\u5185\uff0c\u65b9\u5706\u5c06\u8fd1\u767e\u91cc\uff0c\u4f46\u56db\u5730\u52bf\u9ad8\u4f4e\u8d77\u4f0f\uff0c\u53c8\u56e0\u5230\u5904\u90fd\u662f\u53f6\u6797\uff0c\u4e00\u773c\u671b\u51fa\u53bb\uff0c\u5b9e\u5728 \u770b\u4e0d\u591a\u8fdc\uff0c\u5426\u5219\uff0c\u767e\u91cc\u8303\u56f4\u4e4b\u5185\uff0c\u5fc5\u53ef\u4e00\u89c8\u65e0\u9057\u3002 \u3000\u3000\u7531\u4e8e\u5927\u516c\u4e3b\u8d70\u5f97\u751a\u6162\uff0c\u534a\u4e2a\u65f6\u8fb0\u8fc7\u53bb\u4e4b\u540e\uff0c\u773c\u524d\u4ecd\u4e0d\u89c1\u6709\u4ec0\u4e48\u623f\u820d\uff0c\u4f46\u4f30\u8ba1\u8ddd\u79bb\uff0c\u5df2\u8be5 \u5feb\u5230\u603b\u575b\u4e86\u3002 \u3000\u3000\u7a81\u7136\uff0c\u9762\u524d\u51fa\u73b0\u4e00\u7247\u67f3\u6797\uff0c\u90a3\u67f3\u6811\u6797\u682a\u682a\u90fd\u9ad8\u5927\u5f02\u5e38\u3002 \u3000\u3000\u5728\u67f3\u6797\u56db\u5468\uff0c\u73af\u7ed5\u7740\u4e00\u6761\u5c0f\u6cb3\uff0c\u6cb3\u6c34\u6f7a\u6f7a\u6d41\u52a8\uff0c\u6e05\u6f88\u89c1\u5e95\uff0c\u5e76\u6e05\u6670\u7684\u53ef\u53e3\u671b\u89c1\u6c34\u4e2d\u6e38\u9c7c\u3002 \u3000\u3000\u5978\u4e00\u5e45\u6e05\u5e7d\u96c5\u81f4\u7684\u666f\u8272\u3002 \u3000\u3000\u66f4\u4ee4\u4eba\u5f15\u4ee5\u4e3a\u5947\u7684\uff0c\u662f\u6cb3\u6c34\u867d\u4e0d\u6df1\uff0c\u4f46\u6e38\u9c7c\u5374\u90fd\u5f88\u80a5\u5927\uff0c\u800c\u4e14\u9c7c\u8eab\u4e94\u5f69\u7f24\u7eb7\uff0c\u975e\u9ca4\u975e\u9cab\uff0c \u81f3\u5c11\u5cb3\u5c0f\u98de\u4ece\u672a\u89c1\u8fc7\u3002 \u3000\u3000\u6843\u82b1\u5411\u67f3\u6797\u5185\u4e00\u6307\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8fd9\u91cc\u9762\u5c31\u662f\u4e86\uff01\u201d \u3000\u3000\u53ea\u56e0\u88ab\u67f3\u6797\u906e\u4f4f\uff0c\u67f3\u6797\u5185\u7684\u666f\u51b5\uff0c\u5cb3\u5c0f\u98de\u867d\u6781\u76ee\u671b\u53bb\uff0c\u4e5f\u770b\u4e0d\u51fa\u4ec0\u4e48\u3002 \u3000\u3000\u4ed6\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u539f\u6765\u603b\u575b\u5c31\u5728\u67f3\u6797\u91cc\u9762\uff1f\u201d \u3000\u3000\u6843\u82b1\u7b11\u9053\uff1a\u201c\u67f3\u6797\u5185\u662f\u5927\u516c\u4e3b\u4f4f\u7684\u5730\u65b9\uff0c\u53eb\u67f3\u6797\u9662\uff0c\u4e5f\u53eb\u7fe1\u7fe0\u9986\uff0c\u79bb\u603b\u575b\u8fd8\u6709\u4e00\u6bb5\u8ddd\u79bb \u5462\u3002\u201d \u3000\u3000\u901a\u5f80\u67f3\u6797\u7684\uff0c\u662f\u4e00\u5ea7\u6728\u6865\uff0c\u540d\u526f\u5176\u5b9e\u7684\u5c0f\u6865\u6d41\u6c34\u3002 \u3000\u3000\u6765\u5230\u6865\u5934\uff0c\u5927\u516c\u4e3b\u5ffd\u7136\u6b62\u4f4f\u811a\u6b65\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\uff0c\u542c\u8bf4\u4f60\u7684\u8f7b\u529f\uff0c\u5df2\u5230\u843d\u82b1\u98d8\u53f6\u3001\u70b9\u5c18\u4e0d \u60ca\u7684\u5730\u6b65\uff0c\u53ef\u5426\u8ba9\u6211\u5f00\u5f00\u773c\u754c\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6447\u6447\u5934\u9053\uff1a\u201c\u5927\u516c\u4e3b\u542c\u8c01\u8bf4\u7684\uff0c\u5c5e\u4e8e\u6127\u4e0d\u6562\u5f53\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u7528\u4e0d\u7740\u5ba2\u6c14\uff0c\u4f60\u82e5\u6ca1\u884c\u8d85\u51e1\u7edd\u4fd7\u7684\u8f7b\u529f\u3002\u5982\u4f55\u80fd\u95ef\u8fc7\u6c34\u706b\u4e24\u5173\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u5927\u516c\u4e3b\u53ef\u662f\u53c8\u8981\u6d4b\u8bd5\u6d4b\u8bd5\u5c5e\u4e0b\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u7b97\u4e0d\u5f97\u6d4b\u8bd5\uff0c\u73b0\u5728\u6211\u548c\u6843\u82b1\u5148\u8fc7\u6865\u53bb\uff0c\u4f60\u518d\u4ee5\u8f7b\u529f\u7531\u6cb3\u9762\u98de\u8d8a\u8fc7\u53bb\uff0c\u6bd4\u8fc7 \u6c34\u3001\u706b\u4e24\u5173\u5bb9\u6613\u591a\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6253\u91cf\u4e00\u4e0b\u6cb3\u9762\uff0c\u4e24\u5cb8\u76f8\u8ddd\u6700\u8fd1\u5904\u53ea\u6709\u56db\u3001\u4e94\u4e08\uff0c\u6700\u5bbd\u5904\u4e5f\u4e0d\u8fc7\u4e03\u3001\u516b\u4e08\uff0c\u8fd9\u79cd\u8ddd \u79bb\uff0c\u5728\u4ed6\u6765\u8bf4\uff0c\u5f53\u7136\u662f\u8f7b\u800c\u6613\u4e3e\u3002 \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5c5e\u4e0b\u52c9\u5f3a\u53ef\u80fd\u8fc7\u53bb\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u968f\u5373\u548c\u6843\u82b1\u8d70\u5230\u6728\u6865\u5bf9\u9762\uff0c\u62db\u62db\u624b\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\u5c31\u5f00\u59cb\u5427\uff01\u201d \u3000\u3000\u5c82\u77e5\u5cb3\u5c0f\u98de\u7adf\u627e\u4e86\u4e24\u5cb8\u76f8\u8ddd\u6700\u8fdc\u4e4b\u5904\u7ad9\u597d\uff0c\u63a5\u7740\u817e\u8eab\u800c\u8d77\uff0c\u5411\u5bf9\u9762\u63a0\u8d70\u53bb\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u63a0\u51fa\u4e24\u4e09\u4e08\u8fdc\uff0c\u7a81\u7136\u7adf\u4f3c\u63a7\u5236\u4e0d\u4f4f\uff0c\u76f4\u5411\u6c34\u9762\u5760\u53bb\u3002 \u3000\u3000\u5728\u8fd9\u970e\u90a3\uff0c\u5927\u516c\u4e3b\u548c\u6843\u82b1\u90fd\u4e0d\u7531\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u6cb3\u6c34\u867d\u7136\u4e0d\u4e00\u5b9a\u80fd\u6df9\u6b7b\u4eba\uff0c\u4f46\u5cb3\u5c0f\u98de\u5982\u6b64 \u53cd\u5e38\uff0c\u5374\u5b9e\u5728\u662f\u5979\u4eec\u59cb\u6599\u4e0d\u53ca\u3002 \u3000\u3000\u4f46\u5927\u516c\u4e3b\u548c\u6843\u82b1\u7684\u5fc3\u5ff5\u5c1a\u672a\u8f6c\u5b8c\uff0c\u53ea\u89c1\u5cb3\u5c0f\u98de\u8eab\u521a\u4e00\u8d34\u8fd1\u6c34\u9762\uff0c\u4fbf\u53c8\u518d\u5ea6\u5347\u8d77\uff0c\u7136\u540e\u7ee7 \u7eed\u5411\u524d\u5e73\u98de\uff0c\u518d\u8f7b\u8f7b\u7684\u843d\u4e0b\u5730\u6765\u3002 \u3000\u3000\u5979\u4eec\u5230\u8fd9\u65f6\u624d\u53d1\u73b0\u5cb3\u5c0f\u98de\u53cc\u624b\u5df2\u6367\u7740\u4e00\u6761\u8db3\u6709\u4e24\u4e09\u65a4\u91cd\u7684\u91d1\u9cde\u5927\u9c7c\uff0c\u9664\u4e86\u4e24\u8896\u5df2\u6e7f\u5916\uff0c \u8eab\u4e0a\u548c\u978b\u9762\u8fde\u4e00\u6ef4\u6c34\u73e0\u90fd\u6ca1\u6cbe\u4e0a\u3002 \u3000\u3000\u8fd9\u7b49\u7edd\u4fd7\u60ca\u4e16\u7684\u8f7b\u529f\uff0c\u600e\u4e0d\u4ee4\u5927\u516c\u4e3b\u548c\u6843\u82b1\u90fd\u6014\u5728\u5f53\u5730\u3002 \u3000\u3000\u534a\u664c\u4e4b\u540e\uff0c\u5927\u516c\u4e3b\u624d\u5438\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4eca\u5929\u603b\u7b85\u8ba9\u6211\u5f00\u4e86\u773c\u754c\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5c5e\u4e0b\u5fae\u672b\u4e4b\u6280\uff0c\u4e0d\u6562\u52b3\u5927\u516c\u4e3b\u8c2c\u5956\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u8fd9\u6761\u9c7c\u5c31\u5e26\u5230\u67f3\u6797\u9662\u5427\uff0c\u5f85\u4f1a\u513f\u5c31\u70f9\u7ed9\u4f60\u5403\u3002\u201d \u3000\u3000\u987a\u7740\u67f3\u6797\u5185\u7684\u906e\u9633\u5c0f\u5f84\uff0c\u8d70\u4e0d\u591a\u8fdc\uff0c\u7a81\u7136\u773c\u524d\u4e00\u4eae\uff0c\u73b0\u51fa\u4e00\u5e62\u767d\u8272\u5efa\u7b51\u3002 \u3000\u3000\u8fd9\u5e62\u5efa\u7b51\u5360\u5730\u4e0d\u4e0b\u4e00\u4ea9\uff0c\u8fde\u56f4\u5899\u4e5f\u662f\u767d\u8272\u7684\u3002 \u3000\u3000\u8fdb\u5165\u5927\u95e8\uff0c\u4f46\u89c1\u91cc\u9762\u697c\u53f0\u4ead\u69ad\uff0c\u4e00\u7247\u7eaf\u767d\uff0c\u5728\u56db\u5468\u67f3\u6797\u7684\u63a9\u6620\u4e0b\uff0c\u7f8e\u5f97\u8d85\u5c18\u51fa\u4fd7\uff0c\u522b\u6709 \u4e00\u756a\u97f5\u81f4\u3002 \u3000\u3000\u5927\u516c\u4e3b\u628a\u5cb3\u5c0f\u98de\u5f15\u5165\u4e00\u95f4\u7cbe\u820d\uff0c\u5429\u5490\u6843\u82b1\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u966a\u5cb3\u4f7f\u8005\uff0c\u6211\u8fc7\u4e00\u4f1a\u513f\u518d\u6765\u3002\u201d \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u5341\u4e03\u56de\u3000\u5bcc\u56fd\u57ce\u5e9c \u3000\u3000\u6843\u82b1\u8fde\u5fd9\u6c8f\u4e0a\u837c\u3002 \u3000\u3000\u6843\u82b1\u80fd\u6709\u4eca\u5929\uff0c\u53ef\u8bf4\u5b8c\u5168\u5f97\u529b\u4e8e\u5cb3\u5c0f\u98de\u7684\u4ed7\u4e49\u76f8\u52a9\u3002\u5979\u662f\u4e00\u4e2a\u4e0d\u5fd8\u5927\u6069\u7684\u4eba\uff0c\u6b64\u523b\u80fd\u548c \u5cb3\u5c0f\u98de\u5355\u72ec\u76f8\u5904\u3002\u5f53\u7136\u8981\u5c3d\u91cf\u8868\u73b0\u5f97\u6bb7\u52e4\u4e9b\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u8fd1\u4e2d\u5348\uff0c\u5f88\u5feb\u4fbf\u6446\u4e0a\u9152\u98df\u6765\uff0c\u5176\u4e2d\u7684\u4e00\u76d8\uff0c\u6b63\u662f\u4ed6\u6349\u7684\u90a3\u6761\u5927\u9c7c\u3002 \u3000\u3000\u5f53\u5927\u516c\u4e3b\u518d\u6765\u65f6\uff0c\u5df2\u6362\u4e86\u4e00\u8eab\u8863\u670d\uff0c\u4f46\u5374\u8fd8\u662f\u767d\u8272\u7684\u5cb3\u5c0f\u98de\u4e0d\u96be\u5f97\u77e5\u5927\u516c\u4e3b\u6700\u559c\u767d\u8272\uff0c \u56e0\u4e3a\u8fde\u4ed6\u6240\u5728\u7684\u67f3\u6797\u9662\uff0c\u4e5f\u90fd\u662f\u767d\u8272\u7684\u3002 \u3000\u3000\u5979\u7b11\u9768\u751f\u6625\u7684\u9053\uff1a\u201c\u6843\u82b1\u4e5f\u7559\u5728\u8fd9\u91cc\u4e00\u8d77\u5403\u5427\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u80fd\u7531\u5927\u516c\u4e3b\u76f8\u966a\uff0c\u79f0\u5f97\u4e0a\u662f\u79cd\u5ba0\u9047\uff0c\u800c\u6843\u82b1\u80fd\u548c\u5927\u516c\u4e3b\u5171\u684c\u5403\u996d\uff0c\u5c31\u66f4\u662f\u53d7\u5ba0\u82e5 \u60ca\u4e86\u3002 \u3000\u3000\u996d\u540e\u3002\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4e0b\u5348\u5c31\u7531\u6843\u82b1\u966a\u5cb3\u4f7f\u8005\u5404\u5904\u8d70\u8d70\uff0c\u4f46\u5343\u4e07\u4e0d\u53ef\u64c5\u51fa\u5927\u95e8\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u6123\u9053\uff1a\u201c\u5c5e\u4e0b\u4e0d\u662f\u8981\u53bb\u89c1\u6559\u4e3b\u5417\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u521a\u624d\u6709\u62a5\u4f20\u6765\uff0c\u6559\u4e3b\u767d\u5929\u6709\u4e8b\uff0c\u5fc5\u987b\u5230\u5165\u591c\u4e4b\u540e\u624d\u80fd\u53ec\u89c1\u4f60\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u8d70\u540e\uff0c\u6843\u82b1\u968f\u5373\u5e26\u7740\u5cb3\u5c0f\u98de\u5728\u56f4\u5899\u5185\u5404\u5904\u8d70\u4e86\u4e00\u8d70\u3002 \u3000\u3000\u67f3\u6797\u9662\u867d\u7136\u8303\u56f4\u5f88\u5927\uff0c\u4f46\u5404\u5904\u8d70\u9a6c\u89c2\u82b1\u7684\u901b\u4e86\u4e00\u904d\uff0c\u5374\u6ca1\u82b1\u8d39\u591a\u5927\u65f6\u95f4\u3002 \u3000\u3000\u665a\u9910\u4ecd\u7531\u5927\u516c\u4e3b\u4eb2\u81ea\u6765\u966a\u3002 \u3000\u3000\u5927\u7ea6\u4e00\u66f4\u5de6\u53f3\uff0c\u5927\u516c\u4e3b\u9053\uff1a\u201c\u73b0\u5728\u53ef\u4ee5\u52a8\u8eab\u5230\u603b\u575b\u4e86\uff01\u201d \u3000\u3000\u5176\u5b9e\uff0c\u67f3\u6797\u9662\u5df2\u7b97\u662f\u5728\u603b\u575b\u8303\u56f4\u4e4b\u5185\uff0c\u5927\u516c\u4e3b\u6240\u8bf4\u7684\u603b\u575b\uff0c\u4e0d\u8fc7\u6307\u7684\u6559\u4e3b\u6240\u4f4f\u4e4b\u5904\u800c\u5df2\u3002 \u3000\u3000\u51fa\u4e86\u67f3\u6797\u9662\uff0c\u5929\u8272\u6697\u5f97\u6709\u5982\u6cfc\u4e86\u58a8\uff0c\u8fd9\u662f\u56e0\u4e3a\u4e4c\u4e91\u5bc6\u5e03\uff0c\u661f\u6708\u65e0\u5149\u7684\u7f18\u6545\u3002 \u3000\u3000\u6240\u5e78\u5728\u524d\u5e26\u8def\u7684\u5927\u516c\u4e3b\u7a7f\u7740\u4e00\u8eab\u767d\u8863\u767d\u88d9\uff0c\u5426\u5219\u4ee5\u5cb3\u5c0f\u98de\u7684\u76ee\u529b\u4e5f\u96be\u4ee5\u62e9\u8def\u3002 \u3000\u3000\u6843\u82b1\u5219\u8d70\u5728\u6700\u540e\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u4e00\u5ea7\u5c0f\u6728\u6865\uff0c\u4fbf\u51fa\u4e86\u67f3\u6797\u3002 \u3000\u3000\u518d\u8d70\u7ea6\u534a\u91cc\u5de6\u53f3\uff0c\u4e24\u65c1\u73b0\u51fa\u4e00\u6392\u6392\u5de8\u5927\u7684\u6697\u5f71\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u731c\u5f97\u51fa\uff0c\u90a3\u662f\u4e00\u6392\u6392\u6574\u9f50\u800c\u5b8f\u4f1f\u7684\u623f\u820d\u3002 \u3000\u3000\u518d\u524d\u8fdb\u4e86\u6570\u5341\u4e08\uff0c\u9762\u524d\u7adf\u662f\u4e00\u5904\u96a7\u9053\uff0c\u8fdb\u5165\u4e4b\u540e\uff0c\u4f3c\u4e4e\u5728\u91cc\u9762\u4e0d\u4f4f\u515c\u5708\u5b50\uff0c\u6709\u5982\u4f20\u8bf4\u4e2d \u7684\u516b\u5366\u9635\uff0c\u8f6c\u5f97\u4eba\u51e0\u4e4e\u6709\u4e9b\u5934\u6655\u773c\u82b1\u3002 \u3000\u3000\u597d\u4e0d\u5bb9\u6613\u8f6c\u51fa\u4e86\u96a7\u9053\uff0c\u53ea\u542c\u5927\u516c\u4e3b\u9053\uff1a\u201c\u5230\u5566\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7728\u4e86\u7728\u773c\uff0c\u53ea\u89c1\u524d\u9762\u5ffd\u89c1\u4eae\u8d77\u4e86\u706f\u706b\uff0c\u4e0d\u8fdc\u5904\u51fa\u73b0\u4e86\u4e00\u5ea7\u91d1\u78a7\u8f89\u714c\u3001\u5dcd\u5ce8\u8c6a\u534e\u7684 \u5bab\u6bbf\uff0c\u548c\u60f3\u50cf\u4e2d\u7687\u57ce\u5185\u7684\u91d1\u92ae\u5b9d\u6bbf\uff0c\u4f3c\u4e4e\u6ca1\u4ec0\u4e48\u4e24\u6837\u3002 \u3000\u3000\u6240\u4e0d\u540c\u7684\uff0c\u4e39\u5880\u4e0b\uff0c\u5e76\u65e0\u6587\u6b66\u767e\u5b98\uff0c\u4ec5\u662f\u9ad8\u9ad8\u5728\u4e0a\u7684\u5b9d\u5ea7\u524d\u4fa7\u4e24\u65c1\uff0c\u53f3\u9996\u7ad9\u7740\u56db\u540d\u624b\u6367 \u91d1\u76cf\u7684\u9ec4\u8863\u7ae5\u5b50\uff0c\u5de6\u9996\u7ad9\u7740\u56db\u540d\u624b\u6367\u7389\u58f6\u7684\u7eff\u8863\u5c11\u5973\u3002 \u3000\u3000\u5bab\u6bbf\u4e0a\u65b9\uff0c\u4ece\u9636\u4e0b\u5230\u6700\u9ad8\u5904\u7684\u5b9d\u5ea7\uff0c\u9ad8\u60ac\u7740\u4e24\u6392\u5de8\u5927\u7684\u5782\u82cf\u5bab\u706f\uff0c\u6574\u6574\u9f50\u9f50\uff0c\u8db3\u6709\u4e8c\u3001 \u4e09\u5341\u76cf\u4e4b\u591a\uff0c\u7167\u8000\u5f97\u5927\u6bbf\u5185\u5982\u540c\u767d\u663c\u3002 \u3000\u3000\u5927\u516c\u4e3b\u4f4e\u58f0\u4ea4\u4ee3\u6843\u82b1\u9053\uff1a\u201c\u968f\u6211\u6765\uff01\u201d \u3000\u3000\u6b64\u523b\uff0c\u5927\u516c\u4e3b\u7684\u795e\u8272\uff0c\u663e\u5f97\u5341\u5206\u7aef\u5e84\u8083\u7a46\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u5728\u5239\u90a3\u95f4\u5fc3\u7406\u4e0a\u6709\u79cd\u5f02\u6837\u611f\u89c9\uff0c\u771f\u7684\u5c31\u50cf\u81e3\u5b50\u8981\u671d\u89c1\u7687\u5e1d\u4e00\u822c\uff0c\u867d\u7136\u5927\u6bbf\u4e0a\u5e76 \u65e0\u624b\u6267\u5e72\u6208\u7684\u6b66\u58eb\uff0c\u5374\u9690\u9690\u900f\u7740\u4e00\u80a1\u83ab\u53ef\u8a00\u72b6\u7684\u8083\u6740\u4e4b\u6c14\u3002 \u3000\u3000\u6765\u5230\u5b9d\u5ea7\u4e0b\u7ea6\u65b9\u4e08\u4f59\u5904\uff0c\u5927\u516c\u4e3b\u505c\u4e0b\u811a\u6b65\uff0c\u53c8\u4f4e\u58f0\u9053\uff1a\u201c\u5c31\u5728\u6b64\u5904\u606d\u5019\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5077\u5077\u7684\u5411\u4e0a\u671b\u53bb\uff0c\u5b9d\u5ea7\u4e0a\u7adf\u662f\u7a7a\u7740\uff0c\u663e\u7136\u6559\u4e3b\u8fd8\u4e0d\u66fe\u9a7e\u5230\u3002 \u3000\u3000\u518d\u5411\u5b9d\u5ea7\u4e24\u65c1\u56db\u540d\u9ec4\u8863\u7ae5\u5b50\u548c\u56db\u540d\u7eff\u8863\u5c11\u5973\u5077\u5077\u770b\u53bb\uff0c\u7acb\u523b\u4f7f\u4ed6\u7684\u773c\u775b\u4e3a\u4e4b\u4e00\u4eae\uff0c\u56e0\u4e3a \u6b64\u5904\u79bb\u5f97\u751a\u8fd1\uff0c\u4ed6\u5df2\u770b\u6e05\u8fd9\u56db\u7537\u56db\u5973\u3002 \u3000\u3000\u7537\u7684\u4e2a\u4e2a\u7709\u6e05\u76ee\u79c0\uff0c\u7f8e\u5982\u51a0\u7389\uff0c\u5973\u7684\u66f4\u662f\u4eba\u4eba\u660e\u7738\u7693\u9f7f\uff0c\u4ed9\u59ff\u7389\u8d28\uff0c\u7b80\u76f4\u5c31\u50cf\u60f3\u8c61\u4e2d\u7684 \u91d1\u7ae5\u7389\u5973\u4e00\u822c\u3002 \u3000\u3000\u771f\u4e0d\u77e5\u7687\u5143\u6559\u4e3b\u4ece\u90a3\u91cc\u6311\u9009\u6765\u7684\u8fd9\u7b49\u4fca\u7537\u7f8e\u5973\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5927\u6bbf\u540e\u65b9\u4f20\u51fa\u4e00\u7f15\u8106\u800c\u4eae\u7684\u58f0\u97f3\u9053\uff1a\u201c\u6559\u4e3b\u9a7e\u9053\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u521a\u8981\u5411\u5927\u6bbf\u540e\u671b\u53bb\uff0c\u84e6\u5730\uff0c\u90a3\u6570\u5341\u76cf\u5de8\u5927\u7684\u5782\u82cf\u5bab\u706f\uff0c\u7adf\u7136\u540c\u65f6\u65e0\u98ce\u81ea\u7184\uff0c\u4f7f\u6574 \u4e2a\u5927\u6bbf\uff0c\u5239\u65f6\u6f06\u9ed1\u4e00\u7247\uff0c\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\u3002 \u3000\u3000\u7a81\u7136\u95f4\u7684\u53d8\u5316\uff0c\u4f7f\u5f97\u521d\u8fdb\u5929\u8c37\u603b\u575b\u7684\u5cb3\u5c0f\u98de\u600e\u4e0d\u9a87\u5f02\u83ab\u540d\u3002 \u3000\u3000\u5c24\u5176\uff0c\u90a3\u6570\u5341\u76cf\u5de8\u5927\u5782\u82cf\u5bab\u706f\uff0c\u5982\u4f55\u80fd\u5728\u65e0\u4eba\u65e0\u98ce\u4e4b\u4e0b\u540c\u65f6\u7184\u706d\uff0c\u5b9e\u5728\u662f\u4ef6\u532a\u5937\u6240\u601d\u7684 \u4e8b\u3002 \u3000\u3000\u6b64\u523b\uff0c\u5927\u6bbf\u5185\u9e26\u96c0\u65e0\u58f0\uff0c\u51e0\u4e4e\u8fde\u6839\u7ee3\u82b1\u9488\u6389\u5728\u5730\u4e0a\u90fd\u80fd\u542c\u5230\uff0c\u9759\u5f97\u7b80\u76f4\u6709\u70b9\u53ef\u6015\u3002 \u3000\u3000\u504f\u504f\u7ad9\u5728\u4e00\u65c1\u7684\u5927\u516c\u4e3b\uff0c\u4e5f\u4e0d\u8a00\u4e0d\u52a8\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u5927\u6bbf\u4e0a\u65b9\u6240\u6709\u5bab\u706f\uff0c\u7adf\u53c8\u540c\u65f6\u4eae\u4e86\u8d77\u6765\u3002 \u3000\u3000\u65e0\u4eba\u70b9\u71c3\u800c\u706f\u5149\u9f50\u660e\uff0c\u8d8a\u53d1\u4ee4\u4eba\u89c9\u5f97\u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6025\u6025\u5411\u4e0a\u671b\u53bb\uff0c\u4e0d\u89c9\u53c8\u662f\u4e00\u60ca\uff0c \u3000\u3000\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u5728\u5b9d\u5ea7\u524d\u5df2\u5782\u4e0b\u4e00\u9053\u95ea\u95ea\u53d1\u4eae\u7684\u91d1\u8272\u5e37\u5e54\uff0c\u521a\u597d\u628a\u5b9d\u5ea7\u4ee5\u53ca\u5de6\u53f3\u5404\u56db\u540d \u9ec4\u8863\u7ae5\u5b50\u548c\u7eff\u8863\u5c11\u5973\u9694\u5728\u5e37\u5e54\u4e4b\u540e\u3002\u53c8\u56e0\u90a3\u91d1\u8272\u5e37\u5e54\u672c\u8eab\u5c31\u95ea\u95ea\u53d1\u5149\uff0c\u4f7f\u4eba\u6839\u672c\u65e0\u6cd5\u900f\u89c6\u5230 \u91cc\u9762\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u5927\u516c\u4e3b\u606d\u606d\u656c\u656c\u7684\u5411\u4e0a\u6df1\u65bd\u4e00\u793c\uff0c\u8c28\u58f0\u8bf4\u9053\uff1b\u201c\u5973\u513f\u767d\u91d1\u51e4\u53c2\u89c1\u6559\u4e3b\uff01\u201d \u3000\u3000\u5e37\u5e54\u4e4b\u540e\u968f\u5373\u53d1\u51fa\u4e00\u7f15\u5a07\u6ef4\u6ef4\u94f6\u94a4\u822c\u7684\u58f0\u97f3\u9053\uff1a\u201c\u6559\u4e3b\u964d\u8c15\uff0c\u5927\u516c\u4e3b\u514d\u793c\uff0c\u5cb3\u4f7f\u8005\u5e26\u5230 \u6ca1\u6709\uff1f\u201d \u3000\u3000\u6559\u4e3b\u8bf4\u8bdd\uff0c\u5c45\u7136\u7531\u4eba\u4ee3\u4f20\uff0c\u8fde\u7687\u5e1d\u4e5f\u4f3c\u4e4e\u6ca1\u6709\u6446\u8fc7\u8fd9\u79cd\u6d3e\u5934\u7684\u3002 \u3000\u3000\u5927\u516c\u4e3b\u8083\u5bb9\u7980\u9053\uff1a\u201c\u5973\u513f\u8eab\u65c1\u8fd9\u4f4d\u5e74\u8f7b\u4eba\uff0c\u5c31\u662f\u5cb3\u4f7f\u8005\u3002\u201d \u3000\u3000\u5e37\u5e54\u540e\u90a3\u5a07\u6ef4\u6ef4\u7684\u58f0\u97f3\u53c8\u9053\uff1a\u201c\u6559\u4e3b\u964d\u8c15\uff0c\u5cb3\u4f7f\u8005\u62ac\u8d77\u5934\u6765\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u597d\u62ac\u5934\u5f80\u4e0a\u4ef0\u89c6\uff0c\u53ef\u60dc\u9664\u4e86\u95ea\u95ea\u53d1\u5149\u7684\u91d1\u8272\u5e37\u5e54\uff0c\u5176\u4ed6\u4ec0\u4e48\u90fd\u770b\u4e0d\u5230\u3002 \u3000\u3000\u4f46\u4ed6\u5fc3\u91cc\u6709\u6570\uff0c\u5bf9\u65b9\u7531\u5e37\u5e54\u4e4b\u5185\u770b\u4ed6\uff0c\u5374\u5fc5\u5b9a\u80fd\u770b\u5f97\u4e00\u6e05\u4e8c\u695a\u3002 \u3000\u3000\u8bb8\u4e45\uff0c\u624d\u53c8\u54cd\u8d77\u90a3\u5a07\u6ef4\u6ef4\u7684\u58f0\u97f3\u9053\uff1a\u201c\u6559\u4e3b\u95ee\u5cb3\u4f7f\u8005\uff0c\u8ba2\u8fc7\u4eb2\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8083\u5bb9\u7b54\u9053\uff1b\u201c\u5c5e\u4e0b\u5e74\u7eaa\u5c1a\u8f7b\uff0c\u5e76\u672a\u8ba2\u8fc7\u4eb2\u4e8b\u3002\u201d \u3000\u3000\u5e37\u5e54\u540e\u90a3\u5a07\u6ef4\u6ef4\u7684\u58f0\u97f3\u518d\u9053\uff1a\u201c\u6559\u4e3b\u95ee\u5cb3\u4f7f\u8005\u591a\u5927\u5e74\u7eaa\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5c5e\u4e0b\u8fd8\u6ca1\u6ee1\u5341\u56db\u5c81\u3002\u201d \u3000\u3000\u5927\u6bbf\u4e0a\u9759\u8c27\u4e86\u5f88\u4e45\uff0c\u53c8\u54cd\u8d77\u90a3\u5a07\u6ef4\u6ef4\u7684\u58f0\u97f3\u9053\uff1b\u201c\u597d\u5566\uff0c\u6559\u4e3b\u6ca1\u4e8b\u5566\uff0c\u5927\u516c\u4e3b\u8bf7\u5e26\u5cb3\u4f7f \u8005\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u606d\u606d\u656c\u656c\u5411\u4e0a\u65bd\u4e86\u4e00\u793c\uff0c\u5e94\u4e86\u4e00\u58f0\u201c\u662f\u201d\u3002 \u3000\u3000\u5f53\u5cb3\u5c0f\u98de\u968f\u7740\u5927\u516c\u4e3b\u8d70\u5230\u9636\u4e0b\uff0c\u518d\u56de\u5934\u65f6\uff0c\u4e0d\u4f46\u90a3\u5de8\u5927\u7684\u91d1\u8272\u5e37\u5e54\u5df2\u7ecf\u4e0d\u89c1\uff0c\u8fde\u5b9d\u5ea7\u4e0a \u4ee5\u53ca\u4e24\u65c1\u7684\u516b\u540d\u4fca\u7537\u7f8e\u5973\u4e5f\u90fd\u5931\u53bb\u8e2a\u5f71\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5148\u524d\u53ea\u9053\u6559\u4e3b\u53ec\u89c1\uff0c\u5fc5\u5b9a\u8981\u95ee\u5f88\u591a\u8bdd\uff0c\u4e07\u6ca1\u60f3\u5230\u4e09\u8a00\u4e24\u8bed\u4fbf\u544a\u7ed3\u675f\u3002 \u3000\u3000\u5c24\u5176\uff0c\u4ed6\u4e0d\u4f46\u6ca1\u770b\u5230\u6559\u4e3b\u4ec0\u4e48\u6a21\u6837\uff0c\u8fde\u6559\u4e3b\u662f\u7537\u662f\u5973\uff0c\u4e5f\u534a\u70b9\u5e95\u7ec6\u6ca1\u6478\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u6843\u82b1\u5df2\u7531\u9636\u4e0b\u8fce\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u6843\u82b1\u4e5f\u832b\u7136\u95ee\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u6837\u5feb\u5c31\u53ec\u89c1\u5b8c\u4e86\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1b\u201c\u4e0d\u5fc5\u95ee\uff0c\u9a6c\u4e0a\u56de\u53bb\uff01\u201d \u3000\u3000\u8c01\u77e5\u521a\u8d70\u51fa\u51e0\u6b65\uff0c\u4e00\u540d\u7eff\u8863\u5c11\u5973\u7531\u4eea\u95e8\u5954\u4e86\u51fa\u6765\u9053\uff1a\u201c\u6559\u4e3b\u6709\u8c15\uff0c\u8bf7\u5927\u516c\u4e3b\u5230\u5185\u5bab\u76f8 \u89c1\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u53ea\u597d\u76fc\u5490\u9053\uff1a\u201c\u6843\u82b1\uff0c\u5c31\u7531\u4f60\u628a\u5cb3\u4f7f\u8005\u5e26\u56de\u53bb\uff0c\u4e0d\u5fc5\u7b49\u6211\u3002\u201d \u3000\u3000\u6843\u82b1\u9053\uff1a\u201c\u7980\u5927\u516c\u4e3b\uff0c\u628a\u5cb3\u4f7f\u8005\u5e26\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4eca\u665a\u5c31\u628a\u5cb3\u4f7f\u8005\u5b89\u7f6e\u5728\u67f3\u6797\u9662\u8fc7\u591c\uff0c\u8fd9\u4e48\u665a\u4e86\uff0c\u5f53\u7136\u4e0d\u80fd\u518d\u8981\u4ed6\u56de\u4e94\u5173\u603b \u9547\u5e9c\u53bb\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u4fbf\u548c\u90a3\u7eff\u8863\u5c11\u5973\u8fdb\u5165\u4e86\u4eea\u95e8\u3002 \u3000\u3000\u6843\u82b1\u62c9\u4e86\u62c9\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u516c\u5b50\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u5979\u5728\u65e0\u4eba\u5904\u4ecd\u79f0\u5cb3\u5c0f\u98de\u516c\u5b50\uff0c\u4e0d\u5916\u662f\u89c9\u5f97\u8fd9\u6837\u8f83\u4e3a\u4eb2\u5207\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1b\u201c\u4f60\u8ba4\u5f97\u8def\u5417\uff1f\u201d \u3000\u3000\u6843\u82b1\u9053\uff1a\u201c\u5a62\u5b50\u8ba4\u5f97\u8def\uff0c\u591c\u665a\u4e4b\u95f4\uff0c\u5fc5\u987b\u5c0f\u5fc3\u9ed8\u8ba4\uff0c\u516c\u5b50\u4e5f\u8bf7\u7559\u610f\u4e9b\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5929\u8272\u8fd9\u6837\u6697\uff0c\u7b80\u76f4\u4ee4\u4eba\u6ca1\u6cd5\u7559\u610f\u3002\u201d \u3000\u3000\u5f53\u8d70\u5f97\u79bb\u5f00\u5927\u6bbf\u8f83\u8fdc\u540e\uff0c\u6843\u82b1\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u516c\u5b50\uff0c\u65b9\u624d\u6559\u4e3b\u90fd\u95ee\u8fc7\u60a8\u4ec0\u4e48\u8bdd\uff1f\u597d\u50cf\u6ca1 \u8bb2\u51e0\u53e5\u5427\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u7684\u786e\u6ca1\u8bb2\u51e0\u53e5\uff0c\u53ea\u95ee\u6211\u591a\u5927\u5e74\u7eaa\uff0c\u53c8\u95ee\u8ba2\u8fc7\u4eb2\u6ca1\u6709\uff0c\u5c31\u8fd9\u4e48\u7b80\u5355\u3002\u201d \u3000\u3000\u6843\u82b1\u7b11\u9053\uff1a\u201c\u5a62\u5b50\u8981\u606d\u559c\u516c\u5b50\uff0c\u6559\u4e3b\u53ef\u80fd\u8981\u4e3a\u516c\u4e3b\u9009\u5973\u5a7f\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u76b1\u7709\u5934\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u6211\u8fd9\u79cd\u5e74\u7eaa\uff0c\u600e\u4e48\u80fd\u914d\u516c\u4e3b\uff1f\u201d \u3000\u3000\u6843\u82b1\u9053\uff1a\u201c\u5a62\u5b50\u8bf4\u7684\u5f53\u7136\u4e0d\u662f\u5927\u516c\u4e3b\uff0c\u4e5f\u4e0d\u662f\u4e8c\u516c\u4e3b\uff0c\u662f\u4e09\u516c\u4e3b\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54e6\u4e86\u58f0\u9053\uff1b\u201c\u4f60\u89c1\u8fc7\u4e09\u516c\u4e3b\uff1f\u5979\u6709\u591a\u5927\u5e74\u7eaa\uff1f\u201d \u3000\u3000\u6843\u82b1\u9053\uff1b\u201c\u5a62\u5b50\u6ca1\u89c1\u8fc7\uff0c\u4e0d\u8fc7\u5374\u542c\u8bf4\u4e09\u516c\u4e3b\u4eca\u5e74\u53ea\u6709\u5341\u56db\u5c81\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u89c9\u5fc3\u5934\u4e00\u9707\uff0c\u4ed6\u8ba4\u4e3a\u6843\u82b1\u7684\u8bdd\u5e76\u975e\u6ca1\u6709\u9053\u7406\uff0c\u5426\u5219\u6559\u4e3b\u4f55\u5fc5\u8981\u95ee\u81ea\u5df1\u591a\u5927\u5e74\u7eaa \u548c\u8ba2\u8fc7\u4eb2\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u771f\u540e\u6094\uff0c\u5f53\u65f6\u4e3a\u4ec0\u4e48\u4e0d\u8c0e\u62a5\u8ba2\u8fc7\u4eb2\u4e86\u3002 \u3000\u3000\u4ed6\u5e76\u975e\u8ba4\u4e3a\u6559\u4e3b\u9009\u5a7f\u662f\u4ef6\u574f\u4e8b\uff0c\u800c\u662f\u60f3\u5230\u5927\u516c\u4e3b\u5df2\u5bf9\u7236\u4eb2\u82b1\u7389\u9e9f\u6709\u610f\uff0c\u82e5\u81ea\u5df1\u771f\u88ab\u6559\u4e3b \u9009\u4e0a\uff0c\u5c82\u4e0d\u5f62\u6210\u4e86\u4e71\u4f26\uff1f \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u4ed6\u771f\u7684\u4e0d\u6562\u518d\u5f80\u4e0b\u60f3\u4e86\u3002 \u3000\u3000\u53ea\u542c\u6843\u82b1\u9053\uff1a\u201c\u5982\u679c\u516c\u5b50\u80fd\u88ab\u6559\u4e3b\u9009\u4e0a\uff0c\u7b97\u5f97\u4e0a\u662f\u4ef6\u5929\u5927\u559c\u4e8b\uff0c\u4ece\u4eca\u4ee5\u540e\u53c8\u4f55\u6101\u4e0d\u80fd\u6b65 \u6b65\u9ad8\u5347\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u8c08\u8fd9\u4e9b\u597d\u5417\uff0c\u6843\u82b1\u59d1\u5a18\uff0c\u6211\u60f3\u95ee\u4f60\uff0c\u6559\u4e3b\u5230\u5e95\u662f\u7537\u7684\u8fd8\u662f\u5973\u7684\uff1f\u201d \u3000\u3000\u6843\u82b1\u8bb6\u7136\u9053\uff1a\u201c\u60a8\u521a\u624d\u4e0d\u662f\u548c\u6559\u4e3b\u8bf4\u8fc7\u6d3b\u5417\uff1f\u201d \u3000\u3000\u201c\u6559\u4e3b\u7684\u8bdd\uff0c\u662f\u7531\u4eba\u4ee3\u4f20\u7684\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u662f\u8fd9\u6837\uff0c\u8bf7\u539f\u8c05\uff0c\u5a62\u5b50\u4e5f\u4e0d\u77e5\u9053\u6559\u4e3b\u662f\u7537\u7684\u8fd8\u662f\u5973\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u6559\u4e3b\u672a\u514d\u592a\u795e\u79d8\u4e86\uff01\u201d \u3000\u3000\u201c\u5929\u8c37\u672c\u6765\u662f\u5904\u795e\u79d8\u5730\u65b9\uff0c\u5373\u4fbf\u4f4f\u5728\u5929\u8c37\u9644\u8fd1\u7684\u4eba\uff0c\u4e5f\u5f88\u5c11\u6709\u4eba\u77e5\u9053\u5929\u8c37\u5728\u90a3\u91cc\uff0c\u751a\u81f3 \u8fde\u5929\u8c37\u8fd9\u4e24\u4e2a\u5b57\u90fd\u6ca1\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u5230\u8fbe\u67f3\u6797\u9662\uff0c\u5df2\u662f\u4e8c\u66f4\u8fc7\u540e\uff0c\u6843\u82b1\u7d22\u6027\u5c31\u628a\u81ea\u5df1\u7684\u5367\u5ba4\u8ba9\u7ed9\u5cb3\u5c0f\u98de\uff0c\u5979\u81ea\u5df1\u5219\u7761\u5230\u53e6\u5916 \u4e00\u540d\u4f8d\u5a62\u7684\u623f\u95f4\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5bf9\u6843\u82b1\u5982\u6b64\u76f8\u5f85\uff0c\u81ea\u7136\u4e5f\u5341\u5206\u611f\u6fc0\u3002 \u3000\u3000\u6b21\u65e5\u9192\u6765\uff0c\u65e9\u9910\u65f6\u6843\u82b1\u9053\uff1a\u201c\u516c\u5b50\u8bf7\u7a0d\u5f85\u4f1a\u513f\uff0c\u5927\u516c\u4e3b\u8fd8\u8981\u4eb2\u81ea\u6765\u966a\u4f60\u3002\u201d \u3000\u3000\u201c\u5927\u516c\u4e3b\u662f\u4ec0\u4e48\u65f6\u5019\u56de\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u6628\u665a\u4e8c\u66f4\u5c31\u56de\u6765\u4e86\uff0c\u90a3\u65f6\u516c\u5b50\u5df2\u7ecf\u7761\u7740\uff0c\u5927\u516c\u4e3b\u6ca1\u8ba9\u5a62\u5b50\u518d\u6253\u6270\u4f60\u3002\u201d \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u513f\uff0c\u5927\u516c\u4e3b\u679c\u7136\u6765\u4e86\u3002 \u3000\u3000\u7528\u8fc7\u65e9\u9910\u540e\uff0c\u5cb3\u5c0f\u98de\u6b63\u8981\u544a\u8f9e\u8fd4\u56de\u4e94\u5173\u603b\u9547\uff0c\u5927\u516c\u4e3b\u5374\u9053\uff1a \u3000\u3000\u201c\u5cb3\u4f7f\u8005\uff0c\u606d\u559c\u4f60\uff0c\u6559\u4e3b\u6628\u665a\u53ec\u89c1\u6211\uff0c\u5f53\u573a\u8868\u793a\u5bf9\u4f60\u5341\u5206\u4e2d\u610f\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6709\u5982\u5f53\u5934\u88ab\u51fb\u4e0b\u4e00\u68d2\uff0c\u5446\u4e86\u5446\u9053\uff1a\u201c\u6559\u4e3b\u5df2\u7ecf\u59d4\u6d3e\u5c5e\u4e0b\u4e3a\u5de1\u5173\u4f7f\u8005\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e2d \u610f\u4e0d\u4e2d\u610f\u5462\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u201c\u55e4\u201d\u7684\u4e00\u58f0\u7b11\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\uff0c\u4f60\u7ec8\u7a76\u662f\u4e2a\u5b69\u5b50\uff0c\u96be\u9053\u53ea\u5f53\u4e00\u540d\u5de1\u5173\u4f7f\u8005\u5c31\u77e5\u8db3 \u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u6559\u4e3b\u60f3\u8981\u5c5e\u4e0b\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6682\u65f6\u4e0d\u544a\u8bc9\u4f60\uff0c\u4e0d\u8fc7\u5fc5\u987b\u8ba9\u4f60\u9a6c\u4e0a\u77e5\u9053\u7684\uff0c\u662f\u4f60\u6682\u65f6\u4e0d\u5fc5\u56de\u4e94\u5173\u603b\u9547\u4e86\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u8981\u5c5e\u4e0b\u7559\u5728\u5927\u516c\u4e3b\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u6559\u4e3b\u7279\u51c6\u4f60\u6e38\u89c8\u2018\u5bcc\u56fd\u57ce\u2019\uff0c\u4e00\u822c\u4eba\u5728\u5929\u8c37\u591a\u5e74\uff0c\u8fde\u2018\u5bcc\u56fd\u57ce\u2019\u5728\u4ec0\u4e48\u5730 \u65b9\u90fd\u4e0d\u77e5\u9053\uff0c\u800c\u4f60\u5374\u80fd\u8499\u6069\u6e38\u89c8\uff0c\u79f0\u5f97\u4e0a\u662f\u5929\u5927\u7684\u6b8a\u8363\u3002\u201d \u3000\u3000\u201c\u5bcc\u56fd\u57ce\u5728\u54ea\u91cc\uff0c\u91cc\u9762\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u95ee\uff0c\u53bb\u5230\u5c31\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u201c\u662f\u8c01\u5e26\u5c5e\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u6211\u3002\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u77e5\u9053\u5927\u516c\u4e3b\u4e8b\u60c5\u5f88\u5fd9\uff0c\u600e\u6562\u518d\u52b3\u52a8\u4f60\u8001\u4eba\u5bb6\u3002\u201d \u3000\u3000\u201c\u5728\u4f60\u6240\u8ba4\u8bc6\u7684\u4eba\u4e2d\uff0c\u9664\u4e86\u6211\uff0c\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u80fd\u5e26\u4f60\u5230\u5bcc\u56fd\u57ce\u53bb\u7684\u3002\u201d \u3000\u3000\u201c\u53ef\u6709\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u53ea\u6709\u6211\u624d\u53ef\u4ee5\u81ea\u7531\u8fdb\u51fa\u5bcc\u56fd\u57ce\uff0c\u5176\u4ed6\u7684\u4eba\uff0c\u5373\u4fbf\u8427\u9986\u4e3b\u548c\u82b1\u526f\u603b\u9547\uff0c\u82e5\u8bf7\u4e0d\u5230\u6559 \u4e3b\u4eb2\u81ea\u9881\u53d1\u7684\u901a\u544a\u4ee4\u724c\uff0c\u4e5f\u662f\u65e0\u6cd5\u8fdb\u51fa\u5bcc\u56fd\u57ce\u7684\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u8bf4\u7740\uff0c\u8d77\u8eab\u5429\u5490\u9053\uff1b\u201c\u4f60\u5728\u8fd9\u91cc\u7a0d\u5f85\u2014\u4f1a\u513f\uff0c\u6211\u6362\u8fc7\u8863\u670d\uff0c\u9a6c\u4e0a\u5c31\u5e26\u4f60\u51fa\u53d1\u3002\u201d \u3000\u3000\u5f53\u5927\u516c\u4e3b\u518d\u8fdb\u5165\u5ba2\u5385\u65f6\uff0c\u867d\u7136\u4ecd\u662f\u767d\u8863\u767d\u88d9\uff0c\u4f46\u4e0a\u8863\u7684\u5468\u8fb9\u4ee5\u53ca\u9886\u53e3\u4e0a\uff0c\u5374\u90fd\u9576\u7740\u58a8\u7eff \u53d1\u5149\u7684\u9526\u8fb9\uff0c\u80f8\u524d\u4e5f\u7ee3\u7740\u5170\u82b1\u56fe\u6848\uff0c\u4e8e\u7eaf\u6d01\u9ad8\u60df\u4e4b\u4e2d\uff0c\u53c8\u591a\u4e86\u4e00\u4efd\u5e7d\u81f4\u76ce\u7136\u7684\u795e\u97f5\u3002 \u3000\u3000\u6843\u82b1\u5e26\u7740\u4f01\u76fc\u7684\u8bdd\u6c14\u95ee\u9053\uff1a\u201c\u5927\u516c\u4e3b\uff0c\u5a62\u5b50\u662f\u5426\u53ef\u4ee5\u968f\u4f8d\u540c\u53bb\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4f60\u5f53\u7136\u8981\u8ddf\u7740\u53bb\uff0c\u4e00\u8def\u4e4b\u4e0a\uff0c\u9664\u4e86\u7167\u987e\u6211\uff0c\u4e5f\u8981\u7167\u987e\u5cb3\u4f7f\u8005\u3002\u201d \u3000\u3000\u6843\u82b1\u771f\u662f\u559c\u51fa\u671b\u5916\u3002 \u3000\u3000\u51fa\u4e86\u67f3\u6797\uff0c\u5e76\u4e0d\u5411\u603b\u575b\u65b9\u5411\u884c\u8fdb\uff0c\u5374\u7531\u53e6\u5916\u4e00\u6761\u8def\uff0c\u8fdb\u5165\u4e00\u6761\u8c37\u9053\u3002 \u3000\u3000\u8fd9\u8c37\u9053\u4e24\u65c1\u7684\u5d16\u58c1\u90fd\u4e0d\u9ad8\uff0c\u6700\u9ad8\u5904\u4e5f\u4e0d\u8fc7\u4e09\u3001\u4e94\u4e08\u800c\u5df2\u3002 \u3000\u3000\u82e5\u4e0d\u662f\u6709\u5927\u516c\u4e3b\u548c\u6843\u82b1\u5728\u65c1\uff0c\u5cb3\u5c0f\u98de\u771f\u60f3\u8dc3\u4e0a\u5d16\u9876\uff0c\u770b\u770b\u524d\u9762\u662f\u4ec0\u4e48\u666f\u8c61\u3002 \u3000\u3000\u5c82\u77e5\u4e24\u8fb9\u5d16\u58c1\u867d\u7136\u4e0d\u9ad8\uff0c\u4f46\u8c37\u9053\u5374\u5f88\u957f\uff0c\u800c\u4e14\u5f88\u50cf\u662f\u7531\u4eba\u5de5\u5f00\u51ff\u51fa\u6765\u7684\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u81ea\u4fe1\u8fd9\u60f3\u6cd5\u4e0d\u4f1a\u9519\uff0c\u56e0\u4e3a\u5728\u4e24\u8fb9\u5d16\u58c1\u4e4b\u5916\uff0c\u5e76\u975e\u9ad8\u5c71\uff0c\u5f88\u53ef\u80fd\u6709\u4e0d\u5c11\u673a\u5bc6\u8bbe\u65bd\uff0c \u4e0d\u4fbf\u88ab\u4eba\u7aa5\u7834\uff0c\u800c\u884c\u8d70\u5728\u8c37\u9053\u4ee5\u5185\u7684\u4eba\uff0c\u53c8\u8c01\u90fd\u4e0d\u4fbf\u8dc3\u4e0a\u5d16\u9876\u3002 \u3000\u3000\u4eba\u7ea6\u8db3\u8db3\u8d70\u4e86\u4e09\u3001\u56db\u91cc\u8def\uff0c\u4e00\u76f4\u6ca1\u8d70\u51fa\u8c37\u9053\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u8fd9\u8c37\u9053\u7a76\u7adf\u6709\u591a\u957f\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u5feb\u5230\u5934\u4e86\uff0c\u51fa\u8c37\u53e3\u4e0d\u8fdc\uff0c\u524d\u9762\u4fbf\u662f\u5bcc\u56fd\u57ce\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5728\u8c37\u9053\u8d70\u4e86\u8fd9\u4e48\u4e45\uff0c\u600e\u4e48\u8fde\u4e00\u4e2a\u5f80\u6765\u7684\u4eba\u90fd\u770b\u4e0d\u5230\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u8fd9\u8c37\u9053\u4e0d\u662f\u4e00\u822c\u4eba\u53ef\u4ee5\u8fdb\u51fa\u7684\uff0c\u5f53\u7136\u4e0d\u5bb9\u6613\u770b\u5230\u6709\u4eba\u6765\u5f80\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5165\u53e3\u5904\u4ee5\u53ca\u4e00\u8def\u4e4b\u4e0a\uff0c\u5e76\u6ca1\u9047\u5230\u5c97\u54e8\u76d8\u67e5\uff1f\u201d \u3000\u3000\u201c\u5b9e\u5bf9\u4f60\u8bf4\uff0c\u8fd9\u6761\u8c37\u9053\u5185\u4e00\u5171\u6709\u5341\u4e8c\u5904\u5c97\u54e8\u8d1f\u8d23\u76d1\u89c6\uff0c\u53ea\u662f\u4f60\u65e0\u6cd5\u770b\u5230\u7f62\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u89c9\u6697\u81ea\u9707\u60ca\uff0c\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4ed6\u4eec\u4e0d\u76d8\u67e5\u6211\u4eec\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u6709\u6211\u5e26\u8def\uff0c\u4ed6\u4eec\u8fd8\u6709\u4ec0\u4e48\u597d\u76d8\u67e5\u7684\uff1f\u5982\u679c\u4f60\u5355\u72ec\u95ef\u8fdb\u6765\u8bd5\u8bd5\u770b\uff0c\u5373\u4fbf\u4f60 \u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u95ef\u4e0d\u8fc7\u53bb\u3002\u201d \u3000\u3000\u51fa\u4e86\u8c37\u53e3\uff0c\u4e24\u65c1\u5374\u53c8\u662f\u5939\u9053\u5bc6\u6797\u3002 \u3000\u3000\u5bc6\u6797\u8303\u56f4\u751a\u5e7f\uff0c\u9053\u8def\u4f3c\u4e4e\u5728\u5bc6\u6797\u5f53\u4e2d\uff0c\u56e0\u4e4b\uff0c\u6839\u672c\u770b\u4e0d\u900f\u6797\u5916\u662f\u4ec0\u4e48\u6700\u8c61\u3002 \u3000\u3000\u4e0d\u4f46\u5982\u6b64\uff0c\u8fde\u4e0a\u7a7a\u4e5f\u88ab\u6811\u9634\u906e\u4f4f\uff0c\u82e5\u662f\u76db\u590f\u5b63\u8282\uff0c\u8d70\u5728\u8fd9\u91cc\uff0c\u5fc5\u5b9a\u723d\u5feb\u65e0\u6bd4\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u642d\u8baa\u7740\u9053\uff1a\u201c\u5927\u516c\u4e3b\uff0c\u8fd9\u6761\u8def\u771f\u7b97\u5f97\u4e0a\u662f\u5929\u4e0b\u5947\u666f\uff0c\u590f\u5929\u5230\u8fd9\u91cc\u6765\u7eb3\u51c9\u7684\u4eba\u4e00\u5b9a \u5f88\u591a\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u591f\u8d44\u683c\u8d70\u5728\u8fd9\u6761\u8def\u4e0a\u7684\u4eba\u5b9e\u5728\u4e0d\u591a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u83ab\u975e\u9644\u8fd1\u4ecd\u6709\u7981\u5236\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e24\u65c1\u6797\u5185\u5230\u5904\u90fd\u662f\u7981\u5236\uff0c\u6709\u7684\u662f\u673a\u5173\u5e03\u8bbe\uff0c\u6709\u7684\u662f\u6697\u693f\u57cb \u4f0f\uff0c\u53ea\u662f\u5916\u4eba\u65e0\u6cd5\u770b\u51fa\u800c\u5df2\u3002\u201d \u3000\u3000\u51fa\u4e86\u5bc6\u6797\uff0c\u5cb3\u5c0f\u98de\u4e0d\u7531\u5fc3\u5934\u4e00\u9707\uff0c\u4f46\u89c1\u800c\u524d\u7adf\u662f\u4e00\u9053\u6570\u5341\u4e08\u9ad8\u800c\u4e14\u7b14\u76f4\u5982\u524a\u7684\u7edd\u58c1\uff0c\u7edd \u58c1\u4e0b\u51fa\u73b0\u4e86\u4e00\u5ea7\u57ce\u95e8\u3002 \u3000\u3000\u90a3\u57ce\u95e8\u5c31\u662f\u5f00\u5728\u7edd\u58c1\u5e95\u90e8\uff0c\u800c\u7edd\u58c1\u4e5f\u6b63\u597d\u505a\u4e86\u5929\u7136\u57ce\u5899\u3002\u5982\u6b64\u7684\u57ce\u5899\uff0c\u771f\u53ef\u79f0\u5f97\u4e0a\u662f\u4e00 \u592b\u5f53\u5173\uff0c\u4e07\u592b\u83ab\u5165\u3002 \u3000\u3000\u6765\u5230\u79bb\u57ce\u95e8\u4e0d\u8fdc\u5904\uff0c\u4fbf\u671b\u89c1\u57ce\u95e8\u4e0a\u65b9\u5199\u7740\u4e09\u4e2a\u5de8\u5927\u7684\u91d1\u5b57\u2014\u2014\u5bcc\u56fd\u57ce\u3002 \u3000\u3000\u57ce\u95e8\u4e0b\u96c4\u7ea0\u7ea0\u6c14\u6602\u6602\u7684\u7ad9\u7740\u56db\u540d\u6267\u5200\u4f69\u5251\u7684\u5b88\u536b\u3002 \u3000\u3000\u4ed6\u4eec\u89c1\u4e86\u5927\u516c\u4e3b\uff0c\u9f50\u9f50\u8eac\u8eab\u65bd\u793c\u3002 \u3000\u3000\u5927\u516c\u4e3b\u4e00\u4e00\u7b54\u793c\uff0c\u5e26\u7740\u5cb3\u5c0f\u98de\u548c\u6843\u82b1\u7a7f\u8fc7\u57ce\u95e8\u3002 \u3000\u3000\u8fc7\u4e86\u57ce\u95e8\uff0c\u5cb3\u5c0f\u98de\u53c8\u662f\u4e00\u60ca\u3002 \u3000\u3000\u4ed6\u53ea\u9053\u57ce\u95e8\u5185\u5fc5\u5b9a\u8fdb\u5165\u5c71\u8179\uff0c\u5c82\u77e5\u5374\u53e6\u5916\u53c8\u662f\u4e00\u756a\u5929\u5730\u3002 \u3000\u3000\u539f\u6765\u8fd9\u505a\u4e3a\u57ce\u5899\u7684\u8038\u7acb\u5c71\u58c1\uff0c\u91cc\u9762\u53c8\u662f\u6570\u91cc\u65b9\u5706\u7684\u539f\u91ce\uff0c\u5f62\u6210\u5929\u8c37\u4e4b\u5916\u53e6\u4e00\u7247\u8c37\u5730\u3002 \u3000\u3000\u8fdb\u57ce\u4e0d\u8fdc\uff0c\u8def\u6709\u4fbf\u662f\u4e00\u5904\u5efa\u7b51\u5dcd\u5ce8\u96c4\u4f1f\u7684\u5e9c\u7b2c\uff0c\u5927\u95e8\u4e0a\u65b9\u9ad8\u60ac\u7740\u4e00\u65b9\u2018\u5bcc\u56fd\u57ce\u5e9c\u2019\u7684\u5de8 \u578b\u533e\u989d\u3002 \u3000\u3000\u5e9c\u7b2c\u95e8\u5916\u4e5f\u6709\u4e00\u540d\u5e26\u5200\u5b88\u62a4\u3002 \u3000\u3000\u90a3\u5b88\u62a4\u5411\u5927\u516c\u4e3b\u89c1\u8fc7\u793c\u540e\uff0c\u95ee\u9053\uff1a\u201c\u5927\u516c\u4e3b\u53ef\u662f\u8981\u89c1\u57ce\u4e3b\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u70e6\u4f60\u7980\u62a5\u57ce\u4e3b\uff0c\u5c31\u8bf4\u6211\u6765\u62dc\u89c1\uff01\u201d \u3000\u3000\u90a3\u5b88\u62a4\u5e94\u58f0\u800c\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u832b\u7136\u95ee\u9053\uff1a\u201c\u5927\u516c\u4e3b\uff0c\u5bcc\u56fd\u57ce\u4e3b\u597d\u50cf\u8eab\u4efd\u5730\u4f4d\u5f88\u9ad8\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u5c82\u53ea\u5f88\u9ad8\uff0c\u8fde\u6559\u4e3b\u90fd\u8981\u656c\u4ed6\u4e09\u5206\uff0c\u8b6c\u5982\u6211\u5230\u62db\u8d24\u9986\u6216\u8005\u4e94\u5173\u53bb\uff0c\u62db\u8d24\u9986\u4e3b \u548c\u4e94\u5173\u7edf\u9886\u90fd\u8981\u606d\u606d\u656c\u656c\u7684\u51fa\u6765\u8fce\u63a5\uff0c\u4f46\u6765\u5230\u5bcc\u56fd\u57df\u3002\u5374\u5fc5\u987b\u6211\u5148\u62dc\u89c1\u5bcc\u56fd\u57ce\u4e3b\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u7531\u60f3\u8d77\u4e94\u5173\u4e4b\u4e2d\uff0c\u60df\u6709\u6b66\u5173\u7edf\u9886\u90a3\u77ee\u5c0f\u7684\u9a7c\u80cc\u8001\u4eba\uff0c\u4e0a\u6b21\u5bf9\u4ed6\u548c\u4e01\u6d9b\u3001\u7518\u9716\u5de1 \u89c6\u65f6\u4e0d\u4f46\u4e0d\u7406\u4e0d\u776c\uff0c\u8fde\u4eba\u5f71\u90fd\u6ca1\u770b\u89c1\uff0c\u968f\u5373\u8d81\u673a\u95ee\u9053\uff1a\u201c\u5927\u516c\u4e3b\uff0c\u90a3\u4f4d\u6b66\u5173\u7edf\u9886\uff0c\u597d\u50cf\u67b6\u5b50 \u5947\u5927\uff0c\u5bf9\u4eba\u4e0d\u592a\u793c\u8c8c\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u5ce8\u4e86\u58f0\u9053\uff1a\u201c\u521a\u624d\u6211\u8bf4\u7684\u4e94\u5173\u7edf\u9886\uff0c\u6b66\u5173\u7edf\u9886\u5e94\u8be5\u9664\u5916\uff0c\u90a3\u4f4d\u8001\u524d\u8f88\u7684\u6027\u5b50\u4e56\u8f9f \u662f\u51fa\u4e86\u540d\u7684\uff0c\u8fde\u6559\u4e3b\u90fd\u8981\u987a\u7740\u4ed6\uff0c\u522b\u8bf4\u4e0d\u7406\u4f60\u4eec\uff0c\u5c31\u662f\u6211\u5230\u4e86\u6b66\u5173\uff0c\u4e5f\u4e0d\u6562\u5bf9\u4ed6\u5192\u72af\uff0c\u4ed6\u813e \u6c14\u6765\u4e86\uff0c\u8fde\u6211\u4e5f\u7167\u6837\u9a82\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6115\u7136\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u539f\u56e0\uff0c\u4ed6\u4e0d\u8fc7\u4e00\u540d\u4e94\u5173\u7edf\u9886\uff0c\u7adf\u7136\u8fd9\u6837\u5028\u50b2\u9738\u9053\uff0c\u96be\u9053\u7687\u5143 \u6559\u5c31\u6ca1\u6709\u6559\u89c4\u548c\u6212\u5f8b\u4e86\u5417\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u6447\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6cd5\u5b50\uff0c\u6559\u4e3b\u597d\u4e0d\u5bb9\u6613\u628a\u4ed6\u8bf7\u51fa\u5c71\u6765\uff0c\u8fd8\u987a\u7740\u4ed6\u3002\u4ed6\u8fd8\u7ecf\u5e38\u53d1\u7262\u9a9a \u5462\uff0c\u800c\u4e14\u9664\u4e86\u4ed6\uff0c\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u53ef\u4ee5\u5f53\u5f97\u4e86\u6b66\u5173\u7edf\u9886\uff0c\u5c31\u4ee5\u82b1\u526f\u603b\u9547\u6765\u8bf4\uff0c\u4ed6\u53ef\u4ee5\u5f53\u4e94\u5173\u526f \u603b\u9547\uff0c\u4f46\u5374\u65e0\u6cd5\u505a\u6b66\u5173\u7edf\u9886\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u6b66\u5173\u6709\u5341\u5927\u9b54\u5934\uff0c\u5341\u4eba\u9b54\u5934\u53ea\u670d\u4ed6\uff0c\u4e0d\u670d\u4efb\u4f55\u4eba\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u552f\u6709\u4ed6\u624d\u80fd\u628a\u5341\u5927 \u9b54\u5934\u7edf\u5fa1\u5f97\u670d\u670d\u8d34\u5e16\u3002\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u53ea\u77e5\u9053\u90a3\u4f4d\u9a7c\u80cc\u8001\u4eba\u6b66\u529f\u9ad8\u4e0d\u53ef\u6d4b\uff0c\u4f46\u5374\u4e0d\u6e05\u695a\u4ed6\u4ee5\u524d\u7684\u8eab\u4efd\u6765\u5386\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e2a\u662f\u8d3a\u5170\u5c71\u7684\u4e00\u4f4d\u5f02\u4eba\uff0c\u6ca1\u4eba\u80fd\u77e5\u9053\u4ed6\u7684\u5e08\u627f\u95e8\u6d3e\uff0c\u516d\u5341\u5e74\u524d\uff0c\u66fe\u4e09\u5ea6\u5230\u8fc7\u4e2d\u539f\uff0c \u4e2d\u539f\u6b66\u6797\uff0c\u7adf\u6ca1\u4e00\u4eba\u662f\u4ed6\u654c\u624b\uff0c\u56e0\u4e4b\u5f53\u65f6\u7684\u9ed1\u767d\u4e24\u9053\uff0c\u5bf9\u4ed6\u65e0\u4e0d\u95fb\u540d\u4e27\u80c6\uff0c\u4f46\u4ed6\u5728\u4e2d\u539f\u5e76\u672a \u5927\u5f00\u6740\u6212\uff0c\u5f88\u5feb\u4fbf\u53c8\u56de\u5230\u4e86\u8d3a\u5170\u5c71\uff0c\u56e0\u4e4b\u5f53\u5e74\u7684\u4e2d\u539f\u6b66\u6797\u3002\u53ea\u77e5\u9053\u897f\u5317\u65b9\u6709\u4f4d\u6b66\u529f\u76d6\u4e16\u7684\u9a7c \u80cc\u77ee\u4eba\uff0c\u7167\u6837\u8c01\u4e5f\u5f04\u4e0d\u6e05\u4ed6\u7684\u8eab\u4efd\u6765\u5386\u3002\u201d \u3000\u3000\u201c\u4ed6\u5230\u5e95\u59d3\u4ec0\u4e48\u53eb\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u4ed6\u59d3\u5e9e\u540d\u821f\uff0c\u6709\u4e2a\u7ef0\u53f7\u53eb\u591a\u80cc\u5929\u7fc1\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u554a\u4e86\u58f0\u9053\uff1b\u201c\u591a\u80cc\u5929\u7fc1\uff0c\u3000\u8fd9\u7ef0\u53f7\u7684\u786e\u53d6\u5f97\u5f88\u6709\u610f\u601d\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u6211\u4e5f\u8ba4\u4e3a\u66ff\u4ed6\u53d6\u8fd9\u7ef0\u53f7\u7684\u4eba\u5f88\u6709\u5b66\u95ee\uff0c\u628a\u9a7c\u80cc\u53eb\u591a\u80cc\uff0c\u77ee\u5b50\u5374\u53eb\u5929\u7fc1\uff0c\u5b9e \u5728\u6709\u4e9b\u610f\u601d\u3002\u201d \u3000\u3000\u201c\u4ed6\u516d\u5341\u5e74\u524d\u5c31\u5230\u8fc7\u4e2d\u539f\uff0c\u90a3\u4e48\u73b0\u5728\u7a76\u7adf\u6709\u591a\u5927\u5e74\u7eaa\u5462\uff1f\u201d \u3000\u3000\u201c\u636e\u8bf4\u4ed6\u5df2\u7ecf\u63a5\u8fd1\u767e\u5c81\u9ad8\u9f84\u4e86\u3002\u201d \u3000\u3000\u201c\u6559\u4e3b\u4e3a\u4ec0\u4e48\u8981\u628a\u4ed6\u8bf7\u51fa\u5c71\u6765\uff1f\u201d \u3000\u3000\u201c\u6559\u4e3b\u5728\u6210\u7acb\u4e94\u5173\u4e4b\u521d\uff0c\u6700\u5148\u8bbe\u6cd5\u627e\u5230\u90a3\u5341\u5927\u9b54\u5934\uff0c\u4f46\u5341\u5927\u9b54\u5934\u5374\u8c01\u90fd\u4e0d\u670d\u8c01\uff0c\u5728\u4e07\u4e0d \u5f97\u5df2\u4e4b\u4e0b\uff0c\u624d\u60f3\u5c3d\u4e00\u5207\u529e\u6cd5\uff0c\u628a\u591a\u80cc\u5929\u7fc1\u5e9e\u821f\u8bf7\u5230\u5929\u8c37\u6765\uff0c\u7531\u4ed6\u9886\u5bfc\u5341\u9b54\uff0c\u5341\u9b54\u8fd9\u624d\u670d\u670d\u8d34 \u8d34\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u9ed8\u4e86\u4e00\u9ed8\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\u7edf\u5fa1\u5341\u9b54\u5f88\u7b80\u5355\uff0c\u53ea\u8981\u80fd\u627e\u5230\u6b66\u529f\u6bd4\u4ed6\u4eec\u9ad8\u7684\uff0c\u4ed6 \u4eec\u5c31\u4f1a\u5fc3\u670d\u53e3\u670d\u3002\u201d \u3000\u3000\u201c\u5927\u516c\u4e3b\u53ef\u77e5\u9053\u591a\u80cc\u5929\u7fc1\u7684\u6b66\u529f\u7a76\u7adf\u6709\u591a\u9ad8\uff1f\u96be\u9053\u5728\u5929\u8c37\u4e4b\u4e2d\uff0c\u5c31\u627e\u4e0d\u5230\u7b2c\u4e8c\u4e2a\u6b66\u529f\u548c \u4ed6\u4e00\u6837\u9ad8\u7684\uff1f\u201d \u3000\u3000\u201c\u5929\u8c37\u4e4b\u4e2d\u50cf\u4ed6\u90a3\u6837\u6b66\u529f\u7684\uff0c\u4e0d\u80fd\u8bf4\u6ca1\u6709\uff0c\u6559\u4e3b\u7684\u6b66\u529f\u5c31\u4e0d\u5728\u4ed6\u4e4b\u4e0b\uff0c\u4f46\u6559\u4e3b\u53c8\u5982\u4f55\u80fd \u518d\u62c5\u4efb\u4e94\u5173\u7edf\u9886\u5462\uff1f\u201d \u3000\u3000\u6b63\u8bf4\u5230\u8fd9\u91cc\uff0c\u90a3\u540d\u5b88\u536b\u5927\u6c49\u5df2\u5954\u4e86\u51fa\u6765\u9053\uff1a\u201c\u57ce\u4e3b\u6709\u8bf7\u5927\u516c\u4e3b\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u7387\u9886\u5cb3\u5c0f\u98de\u548c\u6843\u82b1\u76f4\u63a5\u8fdb\u5165\u4e00\u95f4\u5927\u5385\u3002 \u3000\u3000\u5927\u5385\u5185\u7684\u9648\u8bbe\uff0c\u4ee4\u5cb3\u5c0f\u98de\u4e3a\u4e4b\u5403\u60ca\u3002 \u3000\u3000\u53ea\u89c1\u91cc\u9762\u6446\u8bbe\u7684\u684c\u6905\uff0c\u5c45\u7136\u90fd\u662f\u7fe0\u7389\u51ff\u6210\uff0c\u540e\u9762\u7684\u4e00\u9762\u56db\u6247\u8fde\u7ed3\u7684\u5c4f\u98ce\uff0c\u4e5f\u662f\u6a80\u6728\u9576\u7389\uff0c \u800c\u4e14\u8fd8\u9540\u4e86\u91d1\uff0c\u5176\u4ed6\u6240\u6709\u6446\u8bbe\uff0c\u4e5f\u5168\u975e\u91d1\u5373\u7389\uff0c\u8c6a\u534e\u5f97\u7b80\u76f4\u4ee4\u4eba\u4e0d\u6562\u60f3\u8c61\u3002 \u3000\u3000\u5750\u4e0b\u540e\uff0c\u4e24\u540d\u8273\u88c5\u5c11\u5973\u7acb\u5373\u8d8b\u524d\u732e\u8336\u3002 \u3000\u3000\u4e09\u4eba\u7b49\u4e86\u534a\u664c\uff0c\u624d\u542c\u89c1\u5c4f\u540e\u4f20\u6765\u4e00\u58f0\u54b3\u55fd\uff0c\u5927\u516c\u4e3b\u8fde\u5fd9\u7ad9\u8d77\u8eab\u6765\uff0c\u671b\u7740\u53d1\u58f0\u4e4b\u5904\u8eac\u8eab\u65bd \u793c\u9053\uff1a\u201c\u5f1f\u5b50\u767d\u91d1\u51e4\u62dc\u89c1\u9ec4\u5e08\u4f2f\uff01\u201d \u3000\u3000\u5c4f\u98ce\u540e\u5f88\u5feb\u4fbf\u8f6c\u51fa\u4e00\u4f4d\u9ec4\u8863\u8001\u8005\u3002 \u3000\u3000\u9ec4\u8863\u8001\u8005\u5e74\u5728\u516d\u65ec\u5de6\u53f3\uff0c\u80a5\u5934\u5927\u8033\uff0c\u6ee1\u9762\u7ea2\u5149\uff0c\u9894\u4e0b\u98d8\u7740\u4e09\u7efa\u957f\u987b\u3002 \u3000\u3000\u522b\u770b\u4ed6\u5e74\u5df2\u516d\u65ec\u5de6\u53f3\uff0c\u4f46\u9762\u76ae\u5374\u5341\u5206\u7ec6\u5ae9\uff0c\u770b\u4e0d\u51fa\u534a\u70b9\u76b1\u7eb9\uff0c\u663e\u7136\u662f\u517b\u5c0a\u5904\u4f18\u60ef\u4e86\u7684\u3002 \u3000\u3000\u9ec4\u8863\u8001\u8005\u7f13\u7f13\u6765\u5230\u6b63\u4f4d\u5750\u4e0b\u9053\uff1a\u201c\u91d1\u51e4\uff0c\u4f60\u600e\u4e48\u6709\u65f6\u95f4\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u8c28\u58f0\u9053\uff1a\u201c\u5f1f\u5b50\u662f\u5949\u6559\u4e3b\u4e4b\u547d\uff0c\u5e26\u8fd9\u4f4d\u5cb3\u516c\u5b50\u6765\u53c2\u89c2\u53c2\u89c2\u5bcc\u56fd\u57ce\u3002\u201d \u3000\u3000\u9ec4\u8863\u8001\u8005\u77a5\u4e86\u5cb3\u5c0f\u98de\u4e00\u773c\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u662f\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4ed6\u662f\u8fd9\u6b21\u8fde\u8fc7\u4e94\u5173\u6210\u7ee9\u6700\u4f18\u7684\uff0c\u6559\u4e3b\u975e\u5e38\u559c\u6b22\u4ed6\uff0c \u3000\u3000\u6628\u665a\u66fe\u4e88\u4eb2\u81ea\u53ec\u89c1\uff0c\u5e76\u7279\u51c6\u4ed6\u53c2\u89c2\u5bcc\u56fd\u57ce\u3002\u201d \u3000\u3000\u9ec4\u8863\u8001\u8005\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u5f53\u771f\u662f\u82f1\u96c4\u51fa\u5c11\u5e74\uff0c\u53ef\u4e3a\u4ed6\u5b89\u6392\u4e86\u5dee\u4e8b\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4ed6\u76ee\u524d\u5df2\u5728\u65b0\u6210\u7acb\u7684\u4e94\u5173\u603b\u9547\u62c5\u4efb\u5de1\u5173\u4f7f\u8005\u3002\u201d \u3000\u3000\u9ec4\u8863\u8001\u8005\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u5927\u624d\u5c0f\u7528\u4e86\u5417\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u6559\u4e3b\u662f\u8ba9\u4ed6\u591a\u78e8\u7ec3\u78e8\u7ec3\uff0c\u4e0d\u4e45\u4e4b\u540e\uff0c\u81ea\u6709\u8c03\u52a8\u3002\u201d \u3000\u3000\u9ec4\u8863\u8001\u8005\u7565\u2014\u6c89\u541f\u9053\uff1a\u201c\u5982\u679c\u4e0d\u597d\u5b89\u6392\uff0c\u4e0d\u59a8\u628a\u4ed6\u51cb\u5230\u8001\u592b\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4e0d\u52b3\u9ec4\u5e08\u4f2f\u8d39\u5fc3\uff0c\u6559\u4e3b\u4e00\u5b9a\u65e9\u6709\u5b89\u6392\u3002\u201d \u3000\u3000\u9ec4\u8863\u8001\u8005\u5411\u540e\u4e00\u62db\u624b\u9053\uff1a\u201c\u53d6\u4e09\u9762\u4ee4\u724c\u8fc7\u6765\uff01\u201d \u3000\u3000\u7acb\u5373\u6253\u4e00\u540d\u9526\u8863\u5c11\u5973\u6367\u7740\u4e09\u9762\u4ee4\u724c\u606d\u606d\u656c\u656c\u7684\u4ea4\u7ed9\u4e86\u5927\u516c\u4e3b\u3002 \u3000\u3000\u9ec4\u8863\u8001\u8005\u968f\u5373\u8d77\u8eab\u9053\uff1a\u201c\u8001\u592b\u4e0d\u803d\u8bef\u4f60\u4eec\u65f6\u95f4\uff0c\u4f60\u4eec\u73b0\u5728\u5c31\u53bb\u53c2\u89c2\u5427\uff01\u201d \u3000\u3000\u8bf4\u5b8c\u6d3b\uff0c\u81ea\u884c\u5f80\u5c4f\u98ce\u540e\u9762\u800c\u53bb\u3002 \u3000\u3000\u5927\u516c\u4e3b\u628a\u7fe0\u7389\u4ee4\u724c\u5206\u7ed9\u5cb3\u5c0f\u98de\u548c\u6843\u82b1\u5404\u4e00\u9762\uff0c\u5e76\u5429\u5490\u9053\uff1a\u201c\u597d\u597d\u7cfb\u5728\u80f8\u524d\uff0c\u5343\u4e07\u522b\u78b0\u649e \u7740\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u7fe0\u7389\u4ee4\u724c\u662f\u5bcc\u56fd\u57ce\u7684\u901a\u544a\u6807\u8bc6\uff0c\u9664\u5bcc\u56fd\u57ce\u5185\u7684\u4eba\u53e6\u6709\u6807\u8bc6\u5916\uff0c\u5176\u4ed6\u5373\u4fbf\u603b\u575b\u7684\u4eba \u524d\u6765\u53c2\u89c2\uff0c\u4e5f\u5fc5\u987b\u5148\u9886\u5230\u7fe0\u7389\u4ee4\u724c\u624d\u80fd\u901a\u884c\u65e0\u963b\u3002 \u3000\u3000\u4e09\u4eba\u5404\u81ea\u7cfb\u597d\u7fe0\u7389\u4ee4\u724c\uff0c\u79bb\u5f00\u5bcc\u56fd\u57ce\u5e9c\uff0c\u5927\u516c\u4e3b\u9053\uff1a\u201c\u5bcc\u56fd\u57ce\u91cc\u9762\u5206\u4e09\u5be8\u4e00\u5173\uff0c\u5cb3\u516c\u5b50 \u51c6\u5907\u5148\u53c2\u89c2\u54ea\u4e00\u90e8\u95e8\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u76b1\u7709\u5934\u9053\uff1b\u201c\u5c5e\u4e0b\u6839\u672c\u4e0d\u6e05\u695a\u4e09\u5be8\u4e00\u5173\u662f\u4ec0\u4e48\uff1f\u8fd8\u662f\u7531\u5927\u516c\u4e3b\u81ea\u884c\u51b3\u5b9a\u5427\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u6240\u8c13\u4e09\u5be8\uff0c\u5c31\u662f\u2018\u5f00\u53d1\u5927\u5be8\u2019\u3001\u3000\u2018\u901a\u8fd0\u5927\u5be8\u2019\u548c\u2018\u5f18\u519c\u5927\u5be8\u2019\uff0c\u81f3\u4e8e\u4e00 \u5173\uff0c\u53eb\u505a\uff0c\u2018\u7f8e\u4eba\u5173\u2019\uff0c\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u3000\u2018\u7f8e\u4eba\u5173\u2019\u4e0d\u53c2\u89c2\u4e5f\u7f62\uff0c\u65e2\u7136\u7531\u6211\u51b3\u5b9a\u6b21\u5e8f\uff0c\u90a3\u5c31 \u5148\u770b\u2018\u5f00\u53d1\u5927\u5be8\u2019\u5427\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u8fb9\u8bf4\u8fb9\u8d70\uff0c\u5927\u7ea6\u524d\u884c\u91cc\u8bb8\u4e4b\u540e\uff0c\u5c94\u8def\u5f88\u591a\u3002 \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u8fd9\u91cc\u5c31\u662f\u5f00\u53d1\u5927\u5be8\u4e86\uff0c\u5927\u7ea6\u6709\u4e24\u4e09\u767e\u4eba\u5728\u91cc\u9762\u5f00\u91c7\u77ff\u7269\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u539f\u6765\u8fd9\u91cc\u662f\u4e00\u5904\u7164\u77ff\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u5982\u679c\u662f\u7164\u77ff\uff0c\u8fd8\u6709\u4ec0\u4e48\u503c\u5f97\u5f00\u91c7\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u4ec0\u4e48\u77ff\u5462\uff1f\u201d \u3000\u3000\u201c\u91d1\u77ff\u3001\u94f6\u77ff\u3001\u7389\u77ff\u3001\u8fd8\u6709\u73e0\u5b9d\u77ff\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5403\u60ca\u9053\uff1a\u201c\u5c71\u6d1e\u91cc\u90a3\u4f1a\u6709\u8fd9\u4e48\u591a\u5b9d\u7269\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u7ecf\u8fc7\u5730\u8d28\u4e13\u5bb6\u52d8\u5bdf\uff0c\u8fd9\u5c71\u8179\u5185\u662f\u4e00\u5904\u65e0\u7a77\u65e0\u5c3d\u7684\u5b9d\u77ff\uff0c\u5341\u5e74\u524d\u6559\u4e3b\u4fbf\u6d3e\u4eba \u5f00\u91c7\uff0c\u534a\u5e74\u540e\u679c\u7136\u5148\u6316\u5230\u94f6\u77ff\uff0c\u63a5\u7740\u53c8\u6316\u5230\u91d1\u77ff\uff0c\u4ee5\u540e\u7389\u77ff\u548c\u73e0\u5b9d\u77ff\u4fbf\u76f8\u7ee7\u88ab\u53d1\u73b0\u3002\u76ee\u524d\u6bcf \u5929\u53ef\u5f00\u91c7\u5230\u767d\u94f6\u4e94\u5341\u6597\uff0c\u9ec4\u91d1\u5341\u6597\uff0c\u58c1\u7389\u4e09\u8f66\uff0c\u5176\u4ed6\u73e0\u5b9d\u81f3\u5c11\u4e5f\u5728\u767e\u65a4\u4ee5\u4e0a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u542c\u5f97\u6709\u4e9b\u76ee\u77aa\u53e3\u5446\uff0c\u4eba\u4eec\u5e38\u8bf4\u7684\u201c\u65e5\u8fdb\u6597\u91d1\u201d\uff0c\u90a3\u5df2\u7ecf\u662f\u5f88\u4e86\u4e0d\u8d77\u4e86\uff0c\u5982\u4eca\u5929 \u8c37\u5185\u6bcf\u65e5\u80fd\u5f00\u91c7\u51fa\u8fd9\u4e48\u591a\u91d1\u94f6\u73e0\u5b9d\uff0c\u90a3\u5c82\u4e0d\u662f\u5bcc\u53ef\u654c\u56fd\u4e86\u3002 \u3000\u3000\u5927\u516c\u4e3b\u4e0d\u7b49\u5cb3\u5c0f\u98de\u5f00\u53e3\u3002\u53c8\u9053\uff1b\u201c\u5929\u8c37\u5185\u5c06\u8fd1\u4e07\u4eba\uff0c\u82e5\u6ca1\u6709\u5de8\u5927\u8d44\u8d22\uff0c\u5982\u4f55\u80fd\u517b\u5f97\u8d77\uff1f \u4f55\u51b5\u8fd8\u5fc5\u987b\u50a8\u5907\u5927\u91cf\u8d44\u91d1\uff0c\u4ee5\u4fbf\u4e0d\u4e45\u4e4b\u540e\u8fdb\u519b\u4e2d\u539f\u4e4b\u9700\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5927\u5403\u4e00\u60ca\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u6559\u4e3b\u8fd8\u8981\u8fdb\u519b\u4e2d\u539f\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u5230\u73b0\u5728\u624d\u77e5\u9053\uff0c\u6559\u4e3b\u8bbe\u7acb\u62db\u8d24\u9986\u62db\u5175\u4e70\u9a6c\uff0c\u8fd9\u51e0\u5e74\u6765\uff0c\u9664\u539f\u6709\u4eba \u624b\u5916\uff0c\u4e2d\u539f\u6b66\u6797\u9ad8\u624b\u524d\u6765\u6295\u6548\u7684\uff0c\u5df2\u6709\u597d\u51e0\u767e\u4eba\u4e4b\u591a\uff0c\u5373\u4fbf\u4ee5\u73b0\u5728\u7684\u5b9e\u529b\u800c\u8bba\uff0c\u53ea\u6015\u6b66\u6797\u4e2d \u4e5d\u5927\u95e8\u6d3e\u4ee5\u53ca\u5404\u4e2a\u6b66\u6797\u4e16\u5bb6\u52a0\u8d77\u6765\uff0c\u4e5f\u96be\u4ee5\u548c\u6211\u4eec\u7687\u5143\u6559\u76f8\u6bd4\u3002\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u6559\u4e3b\u5f53\u7136\u5e94 \u8be5\u7edf\u4e00\u4e2d\u539f\u6b66\u6797\uff0c\u767b\u4e0a\u552f\u6211\u72ec\u5c0a\u7684\u76df\u4e3b\u5b9d\u5ea7\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u867d\u7136\u81ea\u8fdb\u5165\u5929\u8c37\u540e\uff0c\u65e9\u5c31\u542c\u8fc7\u8fd9\u6837\u7684\u4f20\u8a00\uff0c\u5374\u4e00\u76f4\u672a\u4fbf\u6df1\u4fe1\uff0c\u5982\u4eca\u8fd9\u8bdd\u51fa\u81ea\u5927\u516c \u4e3b\u53e3\u4e2d\u3002\u4f7f\u4ed6\u4e0d\u4fe1\u4e5f\u5f97\u4fe1\u4e86\u3002 \u3000\u3000\u8fd9\u4f7f\u4ed6\u6839\u672c\u6ca1\u6709\u7f6e\u5544\u7684\u4f59\u5730\uff0c\u4ed6\u5e72\u8106\u95f7\u7740\u5934\u4e0d\u518d\u5f00\u53e3\u3002 \u3000\u3000\u5927\u516c\u4e3b\u968f\u4fbf\u5f80\u2014\u6761\u5c94\u8def\u5185\u8d70\u53bb\uff0c\u5f88\u5feb\u4fbf\u542c\u5230\u8fdc\u5904\u4f20\u6765\u53ee\u53ee\u5f53\u5f53\u7684\u6316\u51ff\u58f0\u97f3\uff0c\u4ee5\u53ca\u8f66\u8f86\u8f98 \u8f98\u58f0\u3002 \u3000\u3000\u6e10\u6e10\uff0c\u5c71\u8179\u5730\u9053\u4e2d\u5df2\u4eba\u6765\u4eba\u5f80\uff0c\u6709\u7684\u63a8\u8f66\uff0c\u6709\u7684\u62c5\u62c5\u5b50\uff0c\u8f66\u5b50\u548c\u62c5\u5b50\u91cc\u3002\u4e0d\u662f\u9ec4\u6f84\u6f84\uff0c \u4fbf\u662f\u767d\u82b1\u82b1\uff0c\u518d\u4e0d\u4fbf\u662f\u6676\u4eae\u8000\u773c\u7684\u7fe0\u7389\u5b9d\u77f3\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u90fd\u662f\u4ec5\u7a7f\u4e86\u2014\u4ef6\u7d27\u8eab\u77ed\u88e4\uff0c\u800c\u4e14\u5c71\u6d1e\u4e2d\u6bcf\u9694\u4e00\u6bb5\u8ddd\u79bb\uff0c \u3000\u3000\u4fbf\u6709\u4eba\u5728\u6697\u4e2d\u76d1\u89c6\uff0c\u76d1\u89c6\u7684\u4eba\uff0c\u4e5f\u662f\u53ea\u80fd\u7a7f\u4e00\u6761\u77ed\u88e4\u3002\u663e\u7136\u8fd9\u662f\u9632\u6b62\u6709\u4eba\u79c1\u85cf\u5939\u5e26\u3002 \u3000\u3000\u5927\u516c\u4e3b\u5e76\u672a\u8d70\u5230\u5c3d\u5934\uff0c\u4fbf\u6b62\u4f4f\u811a\u6b65\u9053\uff1a\u201c\u4e0d\u5fc5\u770b\u4ed6\u4eec\u5b9e\u9645\u5f00\u91c7\u7684\u60c5\u51b5\u4e86\uff0c\u800c\u4e14\u5404\u6761\u5c94\u8def \u7684\u60c5\u5f62\u90fd\u5dee\u4e0d\u591a\uff0c\u8fd8\u8981\u7ec6\u770b\u53ea\u6015\u4e00\u5929\u90fd\u770b\u4e0d\u5b8c\uff0c\u73b0\u5728\u5c31\u51fa\u53bb\uff0c\u518d\u770b\u53e6\u5916\u4e00\u4e2a\u5be8\u3002\u201d \u3000\u3000\u51fa\u4e86\u5f00\u53d1\u5927\u5be8\uff0c\u6cbf\u7740\u5c71\u58c1\u524d\u8fdb\uff0c\u4e0d\u4e45\u4e4b\u540e\uff0c\u53c8\u51fa\u73b0\u4e86\u4e00\u6761\u6781\u4e3a\u5bbd\u655e\u7684\u5c71\u8179\u96a7\u9053\uff0c \u3000\u3000\u5927\u516c\u4e3b\u6307\u7740\u5c71\u8179\u96a7\u9053\u9053\uff1a\u201c\u8fd9\u91cc\u9762\u4fbf\u662f\u901a\u8fd0\u5927\u5be8\uff0c\u6211\u4eec\u4e0d\u5fc5\u8fdb\u53bb\uff0c\u53ea\u7ad9\u5728\u6d1e\u53e3\u5916\u770b\u770b\u5c31 \u591f\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u832b\u7136\u9053\uff1a\u201c\u7531\u6d1e\u53e3\u80fd\u770b\u5230\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u9a6c\u4e0a\u5c31\u770b\u5230\u4e86\uff01\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u96a7\u9053\u5185\u8db3\u6709\u51e0\u5341\u4eba\u6d8c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u6709\u7684\u8d76\u7740\u725b\u8f66\uff0c\u9aa1\u8f66\u3001\u9a74\u8f66\uff0c\u6709\u7684\u5219\u6311\u7740\u62c5\u5b50\uff0c\u4e0d\u8bba\u8f66\u91cc\u6216\u62c5\u5b50\u91cc\uff0c\u4e1c\u897f\u90fd\u88c5\u5f97 \u6ee1\u6ee1\u7684\u3002 \u3000\u3000\u7531\u4e8e\u7ad9\u5f97\u5f88\u8fd1\uff0c\u5cb3\u5c0f\u98de\u5df2\u6e05\u695a\u7684\u770b\u5230\uff0c\u8f66\u91cc\u548c\u62c5\u5b50\u91cc\uff0c\u88c5\u7684\u6709\u732a\u3001\u6709\u7f8a\u3001\u6709\u9e21\u3001\u6709\u9e2d\u3001 \u6709\u9c7c\uff0c\u8fd8\u6709\u5404\u8272\u5404\u6837\u7684\u852c\u83dc\uff0c\u751a\u81f3\u8fde\u7ef8\u7f0e\u5e03\u5339\u4ee5\u53ca\u5404\u79cd\u73a9\u7269\u5bb6\u5177\u90fd\u6709\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5927\u4e3a\u60ca\u5947\u9053\uff1b\u201c\u8fd9\u4e9b\u4e1c\u897f\u90fd\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u81ea\u7136\u662f\u82b1\u94b1\u4e70\u6765\u7684\uff0c\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8d8a\u53d1\u5403\u60ca\u9053\uff1b\u201c\u5c71\u8179\u5185\u600e\u4f1a\u6709\u5356\u8fd9\u4e9b\u4e1c\u897f\u7684\u5462\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u5c71\u8179\u5185\u5f53\u7136\u6ca1\u6709\u8fd9\u4e9b\u4e1c\u897f\uff0c\u4ed6\u4eec\u662f\u4ece\u957f\u5b89\u6216\u5c71\u5916\u7684\u6751\u9547\u4e70\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u8fd9\u6761\u96a7\u9053\u53ef\u4ee5\u76f4\u901a\u957f\u5b89\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6740\u96a7\u9053\u6709\u5341\u51e0\u91cc\u957f\uff0c\u53ef\u4ee5\u76f4\u901a\u5230\u5c71\u5916\uff0c\u4ed6\u4eec\u518d\u5230\u957f\u5b89\u548c\u9644\u8fd1\u6751\u9547\u91c7\u8d2d\uff0c\u8981\u4e0d\u7136\u5929\u8c37 \u91cc\u7684\u4e0a\u4e07\u4eba\u5403\u4ec0\u4e48\uff1f\u7a7f\u4ec0\u4e48\uff1f\u7528\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6837\u4e00\u6765\uff0c\u5929\u8c37\u5c82\u4e0d\u5c31\u6ca1\u6709\u79d8\u5bc6\u53ef\u8a00\u4e86\uff1f\u5916\u4eba\u4e5f\u53ef\u4e0d\u7ecf\u4e94\u5173\u968f\u4fbf\u6df7\u8fdb\u6765\uff1f\u201d \u3000\u3000\u201c\u7528\u4e0d\u7740\u4f60\u591a\u8651\uff0c\u8fd9\u96a7\u9053\u7684\u51fa\u95e8\u5904\uff0c\u76d6\u4e86\u4e0d\u5c11\u623f\u820d\uff0c\u5916\u4eba\u53ea\u77e5\u90a3\u91cc\u662f\u4e00\u5904\u5927\u6237\u4eba\u5bb6\uff0c\u6839 \u672c\u4e0d\u6e05\u695a\u6709\u96a7\u9053\u53ef\u4ee5\u901a\u5230\u5929\u8c37\u5185\uff0c\u800c\u4e14\u5728\u90a3\u4e9b\u623f\u820d\u91cc\uff0c\u4f4f\u884c\u5341\u51e0\u540d\u7edd\u9876\u9ad8\u624b\uff0c\u4efb\u4f55\u6b66\u6797\u4eba\u7269 \u4e5f\u95ef\u4e0d\u8fdb\u6765\u3002\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u53c8\u6709\u4e00\u6279\u8f66\u8f86\u548c\u6311\u62c5\u5b50\u7684\u7531\u96a7\u9053\u5185\u6d8c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5927\u516c\u4e3b\u518d\u9053\uff1a\u201c\u5c31\u8fd9\u6837\u5ddd\u6d41\u4e0d\u606f\u7684\u5411\u91cc\u9762\u901a\u8fd0\u5404\u79cd\u8d27\u7269\uff0c\u5929\u8c37\u5185\u6839\u672c\u6ca1\u4ec0\u4e48\u4e1c\u897f\u7f3a\u4e4f \u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fd9\u4e9b\u4e1c\u897f\u90fd\u8fd0\u5230\u90a3\u91cc\u53bb\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u5148\u8fd0\u5230\u57ce\u5185\u5927\u4ed3\u5e93\u91cc\uff0c\u518d\u7531\u5bcc\u56fd\u57ce\u4e3b\u5206\u914d\u5230\u8c37\u5185\u5404\u5730\u53bb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fd9\u91cc\u7528\u4e0d\u7740\u770b\u4e86\uff0c\u518d\u53bb\u53c2\u89c2\u4e00\u4e0b\u5f18\u519c\u5927\u5be8\u5427\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u968f\u5373\u5728\u524d\u5e26\u8def\u3002 \u3000\u3000\u5f18\u519c\u5927\u5be8\u9664\u6809\u6bd4\u9cde\u6b21\u7684\u623f\u820d\u5916\uff0c\u662f\u4e00\u671b\u65e0\u9645\u7684\u5e73\u5766\uff0c\u91cc\u9762\u5168\u662f\u519c\u7530\u548c\u679c\u56ed\uff0c\u7537\u7537\u5973\u5973\u4e0d \u4e0b\u6570\u767e\u4eba\uff0c\u90fd\u5728\u7530\u56ed\u91cc\u57cb\u5934\u5de5\u4f5c\uff0c\u6709\u7684\u9504\u8349\uff0c\u6709\u7684\u704c\u6e89\uff0c\u6709\u7684\u6536\u5272\uff0c\u7adf\u50cf\u5230\u4e86\u53e6\u4e00\u4e2a\u4e16\u754c\uff0c \u5b8c\u5168\u662f\u519c\u6751\u98ce\u5149\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u89e3\u7684\u95ee\u9053\uff1a\u201c\u65e2\u7136\u4e1c\u897f\u53ef\u4ee5\u7531\u5916\u9762\u8fd0\u6765\uff0c\u53c8\u4f55\u5fc5\u8981\u81ea\u5df1\u79cd\u690d\u519c\u7530\u679c\u56ed\u5462\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u8fd9\u662f\u4ee5\u5907\u4e0d\u65f6\u4e4b\u9700\uff0c\u4e07\u4e00\u901a\u8fd0\u5be8\u51fa\u4e86\u95ee\u9898\uff0c\u81ea\u5df1\u4e5f\u53ef\u4ee5\u4f9b\u7ed9\u98df\u7c73\u548c\u852c\u83dc\uff0c \u4f55\u51b5\u8fd9\u91cc\u6536\u6210\u7684\u852c\u83dc\u548c\u6c34\u679c\uff0c\u6bd4\u7531\u5916\u800c\u8fd0\u6765\u7684\u65b0\u9c9c\u5f97\u591a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5f18\u519c\u5927\u5be8\u8303\u56f4\u592a\u5927\uff0c\u65e2\u7136\u77e5\u9053\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u60c5\uff0c\u4e5f\u7528\u4e0d\u7740\u5f80\u91cc\u518d\u8d70\u4e86\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u90a3\u4e48\u73b0\u5728\u53ea\u5269\u4e0b\u7f8e\u4eba\u5173\u4e86\uff0c\u4f60\u8981\u4e0d\u8981\u53bb\u770b\u770b\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8e59\u8d77\u53cc\u7709\u9053\uff1a\u201c\u7f8e\u4eba\u5173\u662f\u505a\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u5a07\u9765\u4e0a\u5fae\u73b0\u7ea2\u6655\u9053\uff1a\u201c\u987e\u540d\u601d\u4e49\uff0c\u7f8e\u4eba\u5173\u91cc\u9762\uff0c\u4f4f\u7684\u5f53\u7136\u90fd\u662f\u7f8e\u5973\u3002\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u662f\u95ee\u90a3\u4e48\u591a\u7f8e\u5973\u4f4f\u5728\u91cc\u9762\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u7684\u53cc\u988a\u6108\u89c1\u6cdb\u7ea2\uff0c\u987f\u4e86\u4e00\u987f\u624d\u9053\uff1a\u201c\u5b9e\u5bf9\u4f60\u8bf4\u4e5f\u65e0\u59a8\uff0c\u7f8e\u4eba\u5173\u4e0d\u8fc7\u662f\u8bf4\u6765\u542c\u7f62\u4e86\uff0c \u5b9e\u9645\u4e0a\u5b83\u662f\u4e00\u5904\u5927\u578b\u5993\u9662\uff0c\u91cc\u9762\u6709\u59d1\u5a18\u4e00\u767e\u591a\u4eba\uff0c\u4e2a\u4e2a\u90fd\u8c8c\u7f8e\u5982\u82b1\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u554a\u4e86\u58f0\u9053\uff1a\u201c\u5728\u5bcc\u56fd\u57ce\u91cc\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u5f00\u5993\u9662\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4e09\u5927\u5be8\u7684\u5f1f\u5144\u4eec\u6574\u5929\u8f9b\u82e6\u5de5\u4f5c\uff0c\u8eab\u5fc3\u5f53\u7136\u5fc5\u987b\u6709\u6240\u8c03\u5242\uff0c\u5426\u5219\u751f\u6d3b\u5c82\u4e0d\u592a\u67af\u71e5\u65e0 \u5473\uff0c\u8bbe\u7acb\u7f8e\u4eba\u5173\uff0c\u4e0d\u5916\u662f\u7ed9\u4ed6\u4eec\u8c03\u5242\u8eab\u5fc3\u7684\uff0c\u540c\u65f6\u4e5f\u53ef\u4ee5\u63d0\u9ad8\u5de5\u4f5c\u6548\u7387\u3002\u201d \u3000\u3000\u201c\u90a3\u4e9b\u7f8e\u5973\u90fd\u662f\u4ece\u54ea\u91cc\u6765\u7684\u5462\uff1f\u201d \u3000\u3000\u201c\u6709\u7684\u662f\u4ece\u5916\u9762\u4e70\u8fdb\u6765\u7684\uff0c\u6709\u7684\u662f\u4ece\u4e94\u5173\u8fdb\u6765\u7684\u5973\u6027\u4eba\u7269\uff0c\u56e0\u4e3a\u72af\u4e86\u672c\u6559\u89c4\u8beb\u800c\u88ab\u6253\u8fdb \u53bb\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u60c5\u4e0d\u81ea\u7981\u6253\u4e86\u4e00\u4e2a\u51b7\u98a4\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u672c\u80fd\u7684\u60f3\u5230\u6bcd\u4eb2\uff0c\u4ed6\u60f3\u5230\u6bcd\u4eb2\u672c\u6765\u662f\u4e2a\u5bb9\u8c8c\u51fa\u4f17\u7684\u5973\u5b50\uff0c\u8bba\u5e74\u7eaa\u4e5f\u4e0d\u8fc7\u4e09\u5341\u521a\u521a \u51fa\u5934\uff0c\u4e07\u4e00\u662f\u88ab\u6253\u8fdb\u7f8e\u4eba\u5173\u2026\u2026\u2026 \u3000\u3000\u4ed6\u5b9e\u5728\u4e0d\u6562\u518d\u5f80\u4e0b\u60f3\u3002 \u3000\u3000\u672c\u6765\uff0c\u4ed6\u5e76\u6ca1\u6709\u53c2\u89c2\u7f8e\u4eba\u5173\u7684\u5ff5\u5934\uff0c\u5982\u4eca\uff0c\u4ed6\u5012\u771f\u5e0c\u671b\u80fd\u8fdb\u53bb\u770b\u770b\uff0c\u770b\u770b\u662f\u5426\u80fd\u9047\u5230\u6bcd \u4eb2\u3002\u5982\u679c\u5f53\u771f\u6bcd\u4eb2\u5728\u5185\uff0c\u5373\u4fbf\u62da\u7740\u6b7b\uff0c\u4ed6\u4e5f\u8981\u628a\u6bcd\u4eb2\u6551\u51fa\u6765\uff0c\u81f3\u5c11\uff0c\u4ed6\u53ef\u4ee5\u5411\u5927\u516c\u4e3b\u6c42\u60c5\u3002 \u3000\u3000\u5927\u516c\u4e3b\u89c1\u5cb3\u5c0f\u98de\u6c89\u541f\u4e0d\u8bed\uff0c\u4f3c\u5728\u51fa\u795e\uff0c\u8f7b\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\uff0c\u4f60\u7a76\u7adf\u60f3\u4e0d\u60f3\u53bb\u770b \u770b\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e3b\u610f\u65e2\u5b9a\uff0c\u6beb\u4e0d\u8fdf\u7591\u7684\u9053\uff1a\u201c\u5c5e\u4e0b\u5f88\u60f3\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u8fd9\u56de\u7b54\u5927\u51fa\u516c\u4e3b\u610f\u6599\uff0c\u4e0d\u89c9\u9891\u8d77\u7fe0\u7709\u9053\uff1a\u201c\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u4e3a\u4ec0\u4e48\u8981\u53c2\u89c2\u90a3\u79cd\u5730\u65b9\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e0d\u8bba\u4ec0\u4e48\u5730\u65b9\uff0c\u770b\u4e86\u540e\u603b\u662f\u591a\u957f\u4e00\u4e9b\u89c1\u8bc6\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u867d\u7136\u6709\u4e9b\u4e0d\u60a6\uff0c\u5374\u4e5f\u4e0d\u4fbf\u7981\u6b62\uff0c\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u90a3\u5c31\u8d70\u5427\uff01\u201d \u3000\u3000\u4e0d\u8db3\u4e00\u76cf\u70ed\u8336\u5de5\u592b\uff0c\u4fbf\u8fdb\u5165\u4e00\u5904\u6781\u5927\u7684\u56f4\u5899\u5185\u3002 \u3000\u3000\u8fd9\u91cc\u867d\u662f\u4e00\u5904\u5927\u5993\u9662\uff0c\u4f46\u8857\u9053\u4e0a\u5374\u7eff\u67f3\u6210\u836b\uff0c\u800c\u4e14\u8fd8\u79cd\u690d\u7740\u4e0d\u5c11\u6843\u6811\u548c\u6885\u6811\u3002 \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u8fd9\u5993\u9662\u5171\u5206\u4e09\u5904\u5927\u9662\uff0c\u4e5f\u5c31\u662f\u6309\u59ff\u8272\u5206\u6210\u4e0a\u3001\u4e2d\u3001\u4e0b\u4e09\u7b49\uff0c\u4e09\u5927\u5be8\u8fdb\u6765\u5bfb \u82b3\u7684\u4eba\uff0c\u4e5f\u662f\u6309\u7167\u8eab\u4efd\u5730\u4f4d\u5206\u6210\u4e09\u7b49\uff0c\u51b3\u4e0d\u4f1a\u4e71\u4e86\u6b21\u5e8f\u3002\u201d \u3000\u3000\u5979\u8bf4\u7740\uff0c\u968f\u5373\u505c\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5927\u516c\u4e3b\u4e3a\u4ec0\u4e48\u4e0d\u8d70\u4e86\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u7ea2\u7740\u8138\u9053\uff1b\u201c\u6211\u548c\u6843\u82b1\u8fdb\u53bb\u4e0d\u592a\u65b9\u4fbf\uff0c\u4f60\u81ea\u5df1\u81ea\u884c\u53c2\u89c2\u5427\uff0c\u6211\u5728\u8fd9\u91cc\u7b49\u7740\u4f60\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5e76\u672a\u72b9\u8c6b\uff0c\u8ff3\u81ea\u5411\u4e00\u5904\u5927\u95e8\u5185\u8d70\u53bb\u3002 \u3000\u3000\u522b\u770b\u8fd9\u662f\u4e00\u5904\u5993\u9662\uff0c\u5ead\u9662\u5185\u7684\u5e03\u7f6e\u5374\u5341\u5206\u5e7d\u7a1a\uff0c\u65e2\u6709\u5047\u5c71\uff0c\u53c8\u6709\u6c34\u69ad\uff0c\u66f2\u5f84\u56de\u5eca\uff0c\u82b1\u6728 \u6276\u758f\u3002 \u3000\u3000\u5ead\u9662\u5185\u6709\u51e0\u4e2a\u9f9f\u5974\u6a21\u6837\u7684\u7537\u4eba\u5728\u95f2\u5750\u804a\u5929\uff0c\u4ed6\u4eec\u89c1\u8fdb\u6765\u7684\u5c45\u7136\u662f\u4e2a\u5341\u4e09\u3002\u56db\u5c81\u7684\u5c0f\u5b69\uff0c \u96be\u514d\u89c9\u5f97\u597d\u5947\u3002 \u3000\u3000\u597d\u5728\u5cb3\u5c0f\u98de\u80f8\u524d\u60ac\u7740\u57ce\u4e3b\u53d1\u4e0b\u7684\u7fe0\u7389\u4ee4\u724c\uff0c\u603b\u7b97\u660e\u767d\u8fd9\u5c0f\u5b69\u5b50\u4e00\u5b9a\u6765\u5386\u4e0d\u51e1\u3002 \u3000\u3000\u4e00\u4e2a\u77ee\u80d6\u7684\u9f9f\u5974\u8fce\u4e0a\u6765\uff0c\u3000\u4e00\u9762\u5658\u5634\u7b11\u95ee\u9053\uff1a\u201c\u516c\u5b50\u53ef\u662f\u6765\u627e\u59d1\u5a18\u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5728\u4e0b\u53ea\u662f\u60f3\u6765\u53c2\u89c2\u53c2\u89c2\uff0c\u600e\u4e48\u770b\u4e0d\u89c1\u59d1\u5a18\uff1f\u201d \u3000\u3000\u90a3\u9f9f\u5974\u7b11\u9053\uff1a\u201c\u73b0\u5728\u662f\u767d\u5929\uff0c\u4e09\u5be8\u7684\u4eba\u90fd\u5728\u5de5\u4f5c\uff0c\u56db\u4e3a\u6ca1\u6709\u5ba2\u4eba\u6765\uff0c\u59d1\u5a18\u4eec\u90fd\u5728\u623f\u91cc\u4f11 \u606f\uff0c\u4e0d\u8fc7\u516c\u5b50\u82e5\u60f3\u627e\uff0c\u968f\u4fbf\u90a3\u4e2a\u59d1\u5a18\u7684\u623f\u95f4\u90fd\u53ef\u8fdb\u53bb\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6765\u53cd\u800c\u4f7f\u5cb3\u5c0f\u98de\u5927\u5931\u6240\u671b\uff0c\u4ed6\u603b\u4e0d\u80fd\u6bcf\u4e00\u4f4d\u59d1\u5a18\u7684\u623f\u95f4\u90fd\u53bb\u5bdf\u770b\u4e00\u4e0b\uff0c\u90a3\u6837\u4e00\u6765\uff0c \u5c06\u6765\u4f20\u51fa\u53bb\u5c82\u4e0d\u6210\u4e86\u5927\u5927\u7b11\u8bdd\u3002 \u3000\u3000\u65e0\u5948\u4f55\uff0c\u53ea\u597d\u518d\u5230\u7b2c\u4e8c\u5904\u5927\u9662\u3002 \u3000\u3000\u4e0d\u96be\u9884\u6296\uff0c\u7b2c\u4e8c\u5bb6\u5927\u9662\u7684\u60c5\u5f62\u4e5f\u662f\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u53ea\u597d\u4e0d\u518d\u8fdb\u7b2c\u4e09\u5bb6\u5927\u9662\uff0c\u5f88\u5feb\u4fbf\u56de\u5230\u5927\u516c\u4e3b\u548c\u6843\u82b1\u7b49\u5f85\u7684\u5730\u65b9\u3002 \u3000\u3000\u5927\u516c\u4e3b\u5a07\u7b11\u95ee\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u4e48\u5feb\u5c31\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u80fd\u5b9e\u8bdd\u5b9e\u8bf4\uff1a\u201c\u767d\u5929\u6ca1\u6709\u5ba2\u4eba\u6765\u3002\u59d1\u5a18\u4eec\u90fd\u8eb2\u5728\u623f\u91cc\u4f11\u606f\uff0c\u5c5e\u4e0b\u770b\u4e0d\u5230\u4ec0\u4e48\uff0c \u53ea\u6709\u4e0d\u770b\u4e86\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\uff0c\u4f60\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u4e0d\u8be5\u60f3\u5230\u5973\u8272\u4e0a\u53bb\uff0c\u8981\u77e5\u9053\u6559\u4e3b\u5bf9\u4f60\u7684\u671f\u671b\u5f88\u5927\uff0c \u8fde\u6211\u5c71\u5e0c\u671b\u4f60\u4e00\u76f4\u80fd\u505a\u4e2a\u89c4\u89c4\u77e9\u77e9\u7684\u4eba\uff0c \u3000\u3000\u552f\u6709\u5982\u6b64\uff0c\u4f60\u624d\u4f1a\u6709\u4e0d\u53ef\u9650\u91cf\u7684\u524d\u9014\u3002\u201d \u3000\u3000\u8fd9\u6559\u5cb3\u5c0f\u98de\u5982\u4f55\u89e3\u91ca\uff0c\u4ed6\u53ea\u80fd\u6de1\u7136\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u5c5e\u4e0b\u53ea\u662f\u4e3a\u4e86\u597d\u5947\uff0c\u600e\u4f1a\u5f53\u771f\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u671b\u4e86\u671b\u5929\u8272\u9053\uff1a\u201c\u5df2\u7ecf\u5feb\u4e2d\u5348\u4e86\uff0c\u54b1\u4eec\u5411\u57ce\u4e3b\u7f34\u56de\u4ee4\u724c\u540e\uff0c\u5403\u8fc7\u5348\u996d\uff0c\u5c31\u8be5\u56de\u53bb \u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6697\u9053\uff1a\u201c\u65e2\u7136\u5728\u7f8e\u4eba\u5173\u65e0\u6cd5\u770b\u5230\u6bcd\u4eb2\uff0c\u4f55\u4e0d\u518d\u6c42\u5927\u516c\u4e3b\u5230\u80b2\u5316\u57ce\u770b\u770b\uff0c\u7236\u4eb2\u66fe\u8bf4 \u6bcd\u4eb2\u88ab\u6253\u5165\u80b2\u5316\u57ce\uff0c\u4eca\u5929\u5c82\u53ef\u5931\u53bb\u5230\u80b2\u5316\u57ce\u7684\u673a\u4f1a\u2026\u2026\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u5cb3\u5c0f\u98de\u642d\u8baa\u7740\u9053\uff1a\u201c\u7980\u5927\u516c\u4e3b\uff0c\u4f60\u8001\u4eba\u5bb6\u53ef\u5426\u518d\u5e26\u5c5e\u4e0b\u5230\u80b2\u5316\u57ce\u53c2\u89c2\u53c2\u89c2\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u60f3\u5230\u80b2\u5316\u57ce\u4e86\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fd9\u91cc\u4e00\u5b9a\u79bb\u80b2\u5316\u57ce\u4e0d\u8fdc\uff0c\u82e5\u4eca\u5929\u4e0d\u80fd\u770b\u770b\uff0c\u4ee5\u540e\u90a3\u91cc\u8fd8\u6709\u673a\u4f1a\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u80b2\u5316\u57ce\u90fd\u662f\u4e9b\u53d7\u5211\u6216\u5728\u611f\u5316\u4e2d\u7684\u4eba\uff0c\u5b9e\u5728\u6ca1\u4ec0\u4e48\u597d\u770b\u3002\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u5c31\u662f\u60f3\u770b\u770b\u4ed6\u4eec\u7a76\u7adf\u53d7\u7684\u4ec0\u4e48\u5211\uff1f\u5c0f\u65f6\u5019\u770b\u8fc7\u4e00\u672c\u2018\u5584\u4e66\u2019\uff0c\u91cc\u9762\u6709\u5341\u516b\u5c42\u5730\u72f1\uff0c \u4ec0\u4e48\u4e0a\u5200\u5c71\u4e0b\u6cb9\u9505\uff0c\u8fd8\u6709\u559d\u8ff7\u9b42\u6c64\uff0c\u8d70\u5948\u4f55\u6865\u7684\uff0c\u82e5\u80fd\u5728\u80b2\u5316\u57ce\u770b\u5230\u771f\u5b9e\u666f\u51b5\uff0c\u4e5f\u53ef\u4ee5\u548c\u4e66 \u4e0a\u5bf9\u7167\u4e00\u4e0b\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u80b2\u5316\u57ce\u867d\u7136\u4ece\u524d\u53eb\u5730\u72f1\u57ce\uff0c\u4f46\u5374\u6ca1\u90a3\u4e48\u6b8b\u9177\uff0c\u600e\u4f1a\u770b\u5230\u4e0a\u5200\u5c71\u4e0b\u6cb9\u9505\u5462\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u53ea\u6c42\u5927\u516c\u4e3b\u5e26\u5c5e\u4e0b\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u4f9d\u7136\u7b11\u7740\u9053\uff1a\u201c\u770b\u4f60\u7ec8\u7adf\u662f\u4e2a\u5c0f\u5b69\u5b50\uff0c\u4ec0\u4e48\u4e8b\u90fd\u597d\u5947\uff0c\u65e2\u7136\u771f\u60f3\u770b\uff0c\u6211\u5c31\u53ea\u6709\u5e26 \u4f60\u53bb\u4e86\u3002\u201d \u3000\u3000\u4e09\u4eba\u56de\u5230\u5bcc\u56fd\u57ce\u5e9c\uff0c\u5411\u57ce\u4e3b\u7f34\u4e86\u4ee4\u724c\uff0c\u53c8\u5403\u8fc7\u5348\u996d\uff0c\u4fbf\u5f80\u80b2\u5316\u57ce\u800c\u6765\u3002 \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u5341\u516b\u56de\u3000\u80b2\u5316\u57ce\u5e9c \u3000\u3000\u79bb\u5f00\u5bcc\u56fd\u57ce\uff0c\u5cb3\u5c0f\u98de\u95ee\u9053\uff1a\u201c\u7531\u8fd9\u91cc\u5230\u80b2\u5316\u57ce\u8fd8\u6709\u591a\u8fdc\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4ee5\u73b0\u5728\u7684\u8d70\u6cd5\uff0c\u5927\u7ea6\u8981\u534a\u4e2a\u65f6\u8fb0\u5de6\u53f3\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5ffd\u7136\u5fc3\u4e2d\u4e00\u52a8\u9053\uff1a\u201c\u5929\u8c37\u4e2d\u8fd9\u4e48\u591a\u79d8\u5bc6\uff0c\u96be\u9053\u5c31\u4e0d\u6015\u5916\u6cc4\u5417\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u7528\u4e0d\u7740\u62c5\u5fc3\uff0c\u5929\u8c37\u4e2d\u7684\u79d8\u5bc6\uff0c\u6839\u672c\u4e0d\u53ef\u80fd\u5916\u6cc4\u3002\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u4e0d\u4fe1\uff0c\u5c31\u4ee5\u901a\u8fd0\u5927\u5be8\u6765\u8bf4\u5427\uff0c\u6bcf\u5929\u6709\u4e0d\u5c11\u4eba\u51fa\u53bb\uff0c\u8fdc\u7684\u751a\u81f3\u5230\u957f\u5b89\uff0c\u4e07\u4e00\u5176\u4e2d\u6709 \u8dd1\u6389\u4e0d\u56de\u6765\u7684\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u8c01\u90fd\u4e0d\u6562\u8dd1\u6389\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u5728\u51fa\u53d1\u4e4b\u524d\u90fd\u5fc5\u987b\u5148\u670d\u4e0b\u4e00\u79cd\u836f\u7269\uff0c\u65f6\u95f4\u4e00\u5230\uff0c\u975e\u56de\u6765\u4e0d\u53ef\uff0c\u5426\u5219\u6ca1\u6709\u89e3\u836f\uff0c\u5c31 \u975e\u6b7b\u4e0d\u53ef\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4ed6\u4eec\u5728\u5916\u9762\uff0c\u603b\u4f1a\u628a\u8fd9\u91cc\u7684\u79d8\u5bc6\u900f\u9732\u7ed9\u522b\u4eba\u7684\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u51fa\u53bb\uff0c\u90fd\u6709\u4e00\u5b9a\u7684\u7f16\u7ec4\uff0c\u6bcf\u7ec4\u90fd\u6709\u57ce\u4e3b\u7684\u5fc3\u8179\u4eba\u8d1f\u8d23\u76d1\u89c6\uff0c\u800c\u8fd9\u76d1\u89c6\u4eba\u4e5f\u662f\u6b66\u529f \u6700\u9ad8\u7684\uff0c\u4f55\u51b5\uff0c\u51e1\u662f\u80fd\u5728\u901a\u8fd0\u5927\u5be8\u5de5\u4f5c\u7684\uff0c\u90fd\u662f\u7ecf\u8fc7\u4e25\u683c\u8003\u6838\u6311\u9009\u51fa\u6765\u7684\uff0c\u4ed6\u4eec\u672c\u8eab\u5c31\u4e2a\u4e2a \u5fe0\u4e8e\u6559\u4e3b\u3002\u201d \u3000\u3000\u8fd9\u65f6\u524d\u8fdb\u7684\u65b9\u5411\uff0c\u6b63\u5bf9\u7740\u4e00\u5904\u9ad8\u53ef\u63d2\u5929\u7684\u5c71\u58c1\uff0c\u51e0\u4e4e\u8fde\u9633\u5149\u4e5f\u96be\u4ee5\u7167\u5c04\u4e0b\u6765\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u80b2\u5316\u57ce\u539f\u540d\u5730\u72f1\u57ce\uff0c\u5fc5\u5728\u6697\u65e0\u5929\u65e5\u7684\u5730\u65b9\u3002 \u3000\u3000\u679c\u7136\uff0c\u5f88\u5feb\u4fbf\u5728\u5c71\u58c1\u4e0b\u51fa\u73b0\u4e86\u5ea7\u57ce\u95e8\uff0c\u57ce\u95e8\u4e0a\u65b9\u5199\u7740\u201c\u80b2\u5316\u57ce\u201d\u4e09\u4e2a\u5927\u9ed1\u5b57\u3002 \u3000\u3000\u628a\u5b88\u57ce\u95e8\u7684\u4e24\u540d\u5927\u6c49\uff0c\u4e5f\u662f\u5168\u8eab\u9ed1\u8863\u3002 \u3000\u3000\u8fdb\u5165\u57ce\u95e8\uff0c\u867d\u7136\u521a\u8fc7\u4e2d\u5348\u4e0d\u4e45\uff0c\u5374\u662f\u4e00\u7247\u5e7d\u6697\uff0c\u5c31\u50cf\u5df2\u7ecf\u5230\u4e86\u9ec4\u660f\uff0c\u7b80\u76f4\u548c\u4f20\u8bf4\u7684\u90ba\u90fd \u57ce\u6709\u4e9b\u76f8\u4f3c\uff0c\u4ee4\u4eba\u4e0d\u671f\u7136\u6709\u79cd\u9634\u68ee\u68ee\u7684\u611f\u89c9\u3002 \u3000\u3000\u5927\u516c\u4e3b\u7167\u4f8b\u5148\u5230\u80b2\u5316\u57ce\u62dc\u89c1\u57ce\u4e3b\u3002 \u3000\u3000\u57ce\u4e3b\u662f\u4f4d\u7ea2\u8863\u8001\u8005\uff0c\u5927\u516c\u4e3b\u4ecd\u79f0\u4ed6\u5e08\u4f2f\u3002 \u3000\u3000\u6b64\u4eba\u770b\u6765\u5f88\u51b7\u9177\uff0c\u8138\u4e0a\u4e0d\u5e26\u534a\u70b9\u8868\u60c5\uff0c\u5728\u542c\u8fc7\u5927\u516c\u4e3b\u8981\u6c42\u53c2\u89c2\u540e\uff0c\u4e5f\u547d\u4eba\u53d6\u6c42\u4e09\u9762\u9ed1\u7389 \u5236\u6210\u7684\u901a\u544a\u4ee4\u724c\u3002 \u3000\u3000\u5927\u516c\u4e3b\u521a\u8981\u544a\u8f9e\uff0c\u5374\u542c\u80b2\u5316\u57ce\u4e3b\u9053\uff1b\u201c\u6162\u7740\uff0c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4ed6\u53eb\u5cb3\u5c0f\u98de\u3002\u201d \u3000\u3000\u80b2\u5316\u57ce\u4e3b\u4e24\u8ddf\u7cbe\u8292\u66b4\u5c04\uff0c\u76f4\u671b\u7740\u5cb3\u5c0f\u98de\u5168\u8eab\u6253\u91cf\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u5cb3\u5c0f\u98de\u53ea\u611f\u5bf9\u65b9\u7684\u773c\u795e\uff0c\u6709\u5982\u5229\u5203\u822c\uff0c\u4f3c\u4e4e\u76f4\u80fd\u523a\u900f\u81ea\u5df1\u7684\u5fc3\u5e95\uff0c\u60c5\u4e0d\u81ea\u7981 \u6697\u6697\u6253\u4e86\u4e2a\u5bd2\u5664\u3002 \u3000\u3000\u591a\u65f6\uff0c\u80b2\u5316\u57ce\u4e3b\u624d\u79fb\u5f00\u76ee\u5149\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u6709\u591a\u5927\u5e74\u7eaa\u4e86\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4ed6\u8fd8\u4e0d\u6ee1\u5341\u56db\u5c81\u3002\u201d \u3000\u3000\u80b2\u5316\u57ce\u4e3b\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u6837\u5c0f\u5c0f\u7684\u5e74\u7eaa\uff0c\u4e3a\u4ec0\u4e48\u5e26\u4ed6\u5230\u8fd9\u91cc\u6765\u53c2\u89c2\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u8fd9\u662f\u6559\u4e3b\u6301\u51c6\u7684\uff0c\u6628\u665a\u6559\u4e3b\u66fe\u53ec\u89c1\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u201c\u6559\u4e3b\u4e3a\u4ec0\u4e48\u8981\u53ec\u89c1\u8fd9\u4e48\u4e00\u4e2a\u5b69\u5b50\uff1f\u53c8\u7279\u51c6\u4ed6\u53c2\u89c2\u80b2\u5316\u57ce\uff1f\u201d \u3000\u3000\u201c\u4e25\u5e08\u4f2f\u6709\u6240\u4e0d\u77e5\uff0c\u4ed6\u867d\u7136\u5e74\u7eaa\u5c0f\uff0c\u4f46\u6587\u624d\u6b66\u529f\uff0c\u5374\u51fa\u7c7b\u62d4\u8403\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u80fd\u51ed\u771f\u672c\u9886 \u901a\u8fc7\u4e94\u5173\u7684\uff0c\u53ea\u4e0d\u8fc7\u4e09\u3001\u56db\u4e2a\u4eba\uff0c\u800c\u4ed6\u4fbf\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u82e5\u518d\u5047\u4ee5\u65f6\u65e5\uff0c\u524d\u9014\u5b9e\u5728\u4e0d\u53ef\u9650\u91cf\u3002\u201d \u3000\u3000\u201c\u54e6\uff0c\u8fd9\u5012\u662f\u5341\u5206\u96be\u5f97\uff0c\u5f85\u4f1a\u513f\u5c31\u5728\u8fd9\u91cc\u5403\u665a\u996d\uff0c\u8001\u592b\u8981\u597d\u597d\u62db\u5f85\u62db\u5f85\u4ed6\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u56de\u8fc7\u8eab\u6765\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\uff0c\u8fd8\u4e0d\u5feb\u5feb\u8c22\u8fc7\u57ce\u4e3b\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fde\u5fd9\u8eac\u8eab\u4e00\u793c\u9053\uff1a\u201c\u591a\u8c22\u57ce\u4e3b\u6069\u5178\uff01\u201d \u3000\u3000\u80b2\u5316\u57ce\u4e3b\u7ec8\u4e8e\u73b0\u51fa\u7b11\u5bb9\uff0c\u6325\u6325\u624b\u9053\uff1a\u201c\u4f60\u4eec\u53bb\u5427\uff0c\u672c\u57ce\u6ca1\u4ec0\u4e48\u597d\u770b\u7684\uff0c\u8981\u65e9\u70b9\u513f\u56de\u6765\uff01\u201d \u3000\u3000\u79bb\u5f00\u80b2\u5316\u57ce\u5e9c\uff0c\u5927\u516c\u4e3b\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\uff0c\u4f60\u4eca\u5929\u53ef\u79f0\u5f97\u5230\u4e86\u6b8a\u8363\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5927\u4e3a\u4e0d\u89e3\u9053\uff1a\u201c\u5c5e\u4e0b\u90a3\u6709\u4ec0\u4e48\u6b8a\u8363\u53ef\u8a00\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1b\u201c\u80b2\u5316\u57ce\u4e3b\u4e00\u5411\u5f85\u4eba\u51b7\u6f20\uff0c\u8fde\u6211\u4ee5\u524d\u6765\uff0c\u4e5f\u7edd\u5c11\u5f97\u5230\u4ed6\u62db\u5f85\u5403\u996d\u7684\uff0c\u4f60\u4e0d\u8fc7 \u4e00\u4e2a\u5916\u4eba\uff0c\u53c8\u53ea\u662f\u4e2a\u5b69\u5b50\uff0c\u7b2c\u4e00\u6b21\u4ed6\u5c31\u8981\u62db\u5f85\uff0c\u4e0d\u662f\u6b8a\u8363\u662f\u4ec0\u4e48\uff0c\u770b\u6765\u4eca\u5929\u6211\u548c\u6843\u82b1\uff0c\u53cd\u800c \u662f\u6cbe\u4e86\u4f60\u7684\u5149\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u5c5e\u4e0b\u6cbe\u4e86\u5927\u516c\u4e3b\u7684\u5149\uff0c\u4e5f\u8bb8\u4eca\u5929\u57ce\u4e3b\u6709\u4ec0\u4e48\u7279\u522b\u9ad8\u5174\u7684\u4e8b\u5427\uff01\u201d \u3000\u3000\u201c\u8fd8\u6709\uff0c\u4ed6\u5c45\u7136\u5bf9\u4f60\u7b11\u4e86\u4e00\u7b11\uff0c\u8981\u77e5\u9053\u8fde\u6211\u4e5f\u6ca1\u89c1\u4ed6\u7b11\u8fc7\u51e0\u6b21\u3002\u201d \u3000\u3000\u201c\u8fd9\u6b63\u662f\u5c5e\u4e0b\u521a\u624d\u6240\u8bf4\uff0c\u4ed6\u4eca\u5929\u7279\u522b\u9ad8\u5174\u7684\u7f18\u6545\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u522b\u8bf4\u4e86\uff0c\u53cd\u6b63\u4ed6\u4eca\u5929\u7684\u8868\u73b0\uff0c\u4f3c\u4e4e\u663e\u5f97\u9887\u4e0d\u5e73\u51e1\u3002 \u3000\u3000\u5cb3\u4f7f\u8005\uff0c\u4f11\u8981\u5148\u770b\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u65e2\u7136\u6765\u4e86\uff0c\u5c5e\u4e0b\u5f53\u7136\u60f3\u5230\u5904\u90fd\u770b\u770b\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u5176\u5b9e\u8fd9\u91cc\u5b9e\u5728\u4e5f\u6ca1\u4ec0\u4e48\u597d\u770b\u7684\uff0c\u8fdb\u5165\u8fd9\u91cc\u7684\u4eba\uff0c\u591a\u534a\u5728\u53d7\u82e6\u53d7\u96be\uff0c\u770b\u4e86\u4ee5 \u540e\uff0c\u5fc3\u91cc\u53cd\u800c\u8d8a\u53d1\u4e0d\u8212\u670d\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u90a3\u91cc\u77e5\u9053\uff0c\u5979\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53e5\u53e5\u90fd\u50cf\u9488\u4e00\u822c\u5728\u7a7f\u523a\u7740\u5cb3\u5c0f\u98de\u7684\u5fc3\uff0c\u56e0\u4e3a\u5cb3\u5c0f\u98de\u660e\u77e5 \u6bcd\u4eb2\u662f\u5728\u8fd9\u91cc\u53d7\u82e6\u53d7\u96be\u3002 \u3000\u3000\u60f3\u8d77\u6bcd\u4eb2\uff0c\u5cb3\u5c0f\u98de\u771f\u662f\u5fcd\u7740\uff0c\u4f46\u5374\u53c8\u4e0d\u80fd\u4e0d\u770b\uff0c\u4e0d\u770b\u53c8\u5982\u4f55\u80fd\u6551\u5f97\u51fa\u6bcd\u4eb2\uff1f \u3000\u3000\u53ea\u542c\u5927\u516c\u4e3b\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u770b\u5012\u4e0d\u5982\u53bb\u53c2\u89c2\u53c2\u89c2\u7075\u5802\uff0c\u4f60\u662f\u5c0f\u5b69\u5b50\uff0c\u4e00\u5b9a\u559c\u6b22\u770b\u90a3\u4e9b\u6050 \u6016\u7684\u666f\u8c61\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u5934\u4e00\u9707\u9053\uff1a\u201c\u4ec0\u4e48\u7075\u5802\uff1f\u662f\u8c01\u7684\u7075\u5802\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4e0d\u662f\u4e00\u4e2a\u4eba\u7684\u7075\u5802\uff0c\u662f\u5f88\u591a\u4eba\u7684\u7075\u5802\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8d8a\u53d1\u5403\u60ca\u9053\uff1a\u201c\u90fd\u662f\u54ea\u4e9b\u4eba\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u5982\u679c\u95ee\u90fd\u662f\u54ea\u4e9b\u4eba\uff0c\u4e0d\u4f46\u4f60\u4e0d\u6e05\u695a\uff0c\u8fde\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u90fd\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u3000\u3000\u201c\u6709\u7684\u662f\u75c5\u6b7b\u7684\uff0c\u6709\u7684\u662f\u53d7\u5211\u800c\u6b7b\u7684\uff0c\u4e5f\u6709\u7684\u662f\u81ea\u6740\u7684\uff0c\u4e0d\u8fc7\u5176\u4e2d\u6709\u4e00\u90e8\u5206\u662f\u4f8b\u5916\u3002\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u4f8b\u5916\uff1f\u201d \u3000\u3000\u201c\u6709\u4e9b\u4eba\u5e76\u6ca1\u6b7b\uff0c\u53ea\u662f\u88c5\u5728\u68fa\u6750\u91cc\u7f62\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u53c8\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e9b\u4eba\u76ee\u524d\u6d3b\u7740\u65e0\u7528\uff0c\u6b7b\u4e86\u53c8\u53ef\u60dc\uff0c\u4fbf\u7ed9\u4ed6\u4eec\u670d\u4e0b\u4e00\u79cd\u836f\u7269\u88c5\u8fdb\u68fa\u6750\u91cc\uff0c\u7b49\u7528\u7740\u4ed6\u4eec \u7684\u65f6\u5019\uff0c\u518d\u8ba9\u4ed6\u4eec\u6d3b\u8fc7\u6765\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u542c\u5f97\u6253\u5fc3\u5e95\u76f4\u5192\u51b7\u6c14\uff0c\u9ed8\u4e86\u4e00\u9ed8\uff0c\u624d\u95ee\u9053\uff1a\u201c\u4e16\u754c\u4e0a\u90a3\u6709\u8fd9\u79cd\u836f\u7269\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u522b\u5904\u6ca1\u6709\uff0c\u5929\u8c37\u91cc\u90fd\u6709\uff0c\u8fd9\u4e9b\u4eba\u5728\u68fa\u6750\u91cc\uff0c\u8bf4\u6b7b\u4e0d\u6b7b\uff0c\u8bf4\u6d3b\u4e0d\u6d3b\uff0c\u800c\u4e14\u4e5f \u4e0d\u996e\u4e0d\u98df\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u8fd8\u6709\u4e00\u53e3\u6c14\u547c\u5438\u4e86\uff1f\u4ed6\u4eec\u5728\u68fa\u6750\u91cc\uff0c\u662f\u5426\u6709\u4e00\u5b9a\u7684\u671f\u9650\u5462\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u4e0d\u8d85\u8fc7\u4e09\u5e74\u90fd\u53ef\u4ee5\u6551\u6d3b\u3002\u201d \u3000\u3000\u201c\u6551\u6d3b\u4e86\u4ee5\u540e\uff0c\u7cbe\u795e\u4f53\u529b\u662f\u5426\u80fd\u548c\u4ee5\u524d\u2014\u6837\uff1f\u6b66\u529f\u662f\u5426\u5df2\u7ecf\u6d88\u5931\uff1f\u201d \u3000\u3000\u201c\u548c\u4ece\u524d\u5b8c\u5168\u4e00\u6837\uff0c\u6b66\u529f\u4e5f\u4e0d\u4f1a\u6d88\u5931\uff0c\u201d \u3000\u3000\u8fd9\u5b9e\u5728\u592a\u7384\u4e86\uff0c\u4e5f\u5b9e\u5728\u4e0d\u53ef\u601d\u8bae\u3002 \u3000\u3000\u5927\u516c\u4e3b\u63a5\u7740\u518d\u9053\uff1b\u201c\u5f53\u7136\uff0c\u51e1\u662f\u68fa\u6750\u91cc\u7684\u8fd9\u79cd\u4eba\uff0c\u90fd\u662f\u6709\u5934\u6709\u8138\u5c06\u6765\u53c8\u6709\u5229\u7528\u4ef7\u503c\u7684\u4eba \u7269\uff0c\u56e0\u4e3a\u8fd9\u79cd\u836f\u7269\u70bc\u5236\u4e0d\u6613\uff0c\u666e\u901a\u4eba\u662f\u4e0d\u591f\u8d44\u683c\u7528\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8feb\u4e0d\u53ca\u5f85\u7684\u9053\uff1b\u201c\u90a3\u5c31\u8bf7\u5927\u516c\u4e3b\u5feb\u5e26\u5c5e\u4e0b\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u4e0d\u518d\u8bf4\u8bdd\uff0c\u5e26\u7740\u5cb3\u5c0f\u98de\u5f80\u4e00\u5904\u5c71\u58c1\u65b9\u5411\u8d70\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u6843\u82b1\u4e5f\u662f\u5728\u53c8\u6014\u53c8\u6123\uff0c\u5979\u8ddf\u968f\u5927\u516c\u4e3b\u4e0d\u4e45\uff0c\u4e0d\u7528\u8bf4\u4e5f\u662f\u7b2c\u4e00\u6b21\u5230\u8fd9\u79cd\u5730\u65b9\u6765\u3002\u53ea\u662f \u56e0\u4e3a\u81ea\u89c9\u8eab\u4efd\u5351\u5fae\uff0c\u4e0d\u6562\u5411\u5927\u516c\u4e3b\u8be2\u95ee\u7f62\u4e86\u3002 \u3000\u3000\u6765\u5230\u5c71\u58c1\u4e0b\uff0c\u5c71\u58c1\u4e0a\u5f00\u7740\u4e00\u4e2a\u6d1e\u95e8\uff0c\u6709\u4e2a\u9ed1\u8863\u4eba\u5b88\u5728\u6d1e\u95e8\u5916\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u89c1\u5927\u516c\u4e3b\u7b49\u4e09\u4eba\u90fd\u4f69\u6709\u57ce\u4e3b\u53d1\u4e0b\u7684\u6807\u8bc6\uff0c\u5e76\u672a\u67e5\u95ee\u3002 \u3000\u3000\u521a\u8fdb\u53bb\u4e4b\u540e\uff0c\u5f53\u771f\u662f\u9ed1\u6d1e\u6d1e\u7684\uff0c\u51e0\u4e4e\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\u3002 \u3000\u3000\u8d70\u4e86\u56db\u4e94\u4e08\u540e\uff0c\u6d1e\u9876\u4e0a\u65b9\u5df2\u60ac\u6709\u7af9\u7b3c\u3002 \u3000\u3000\u53c8\u8f6c\u4e86\u513f\u5f2f\uff0c\u9762\u524d\u8c41\u7136\u5f00\u6717\uff0c\u7adf\u662f\u4e00\u4e2a\u8db3\u6709\u6570\u5341\u4e08\u65b9\u5706\u7684\u5de8\u5927\u7a7a\u6d1e\u3002 \u3000\u3000\u89e6\u76ee\u4e4b\u4e0b\uff0c\u5cb3\u5c0f\u98de\u771f\u6709\u4e9b\u6bdb\u9aa8\u609a\u7136\u4e4b\u611f\u3002 \u3000\u3000\u53ea\u89c1\u5de8\u5927\u7684\u6d1e\u5ba4\u5185\uff0c\u6446\u653e\u4e86\u8db3\u6709\u4e0a\u767e\u53e3\u7684\u7ea2\u6f06\u68fa\u6728\u3002 \u3000\u3000\u90a3\u4e9b\u68fa\u6728\uff0c\u6392\u653e\u5f97\u6574\u6574\u9f50\u9f50\uff0c\u7eb5\u770b\u6210\u884c\uff0c\u6a2a\u770b\u6210\u5217\u3002\u6bcf\u53e3\u68fa\u6750\u7684\u4e00\u7aef\uff0c\u90fd\u653e\u7740\u4e00\u5f20\u5c0f\u5706 \u51f3\uff0c\u5c0f\u5706\u51f3\u4e0a\u70b9\u7740\u4e00\u76cf\u7eff\u5149\u95ea\u95ea\u7684\u6cb9\u706f\uff0c\u7167\u5f97\u90a3\u4e9b\u68fa\u6728\u4e5f\u7ea2\u4e2d\u6cdb\u7eff\uff0c\u8d8a\u53d1\u589e\u52a0\u4e86\u9634\u68ee\u6050\u6016\u611f\u3002 \u3000\u3000\u5728\u6d1e\u58c1\u7684\u56db\u5468\uff0c\u5404\u7ad9\u7740\u4e00\u540d\u7a7f\u9ed1\u8863\u7684\u5b88\u62a4\u4eba\u5458\uff0c\u5176\u4e2d\u7537\u5973\u5404\u4e24\u540d\u3002 \u3000\u3000\u8fd9\u56db\u540d\u7537\u5973\uff0c\u53ef\u80fd\u662f\u957f\u65f6\u95f4\u4e0d\u89c1\u9633\u5149\u4e4b\u6545\uff0c\u4e00\u4e2a\u4e2a\u4e5f\u7626\u9aa8\u5d99\u5ccb\uff0c\u9762\u8272\u60e8\u767d\uff0c\u5728\u7eff\u8272\u706f\u5149 \u7684\u95ea\u95ea\u6620\u7167\u4e0b\uff0c\u5c31\u50cf\u6b7b\u4e86\u4e09\u5929\u6ca1\u57cb\u7684\u50f5\u5c38\u4e00\u822c\uff0c\u4ec5\u662f\u770b\u5230\u8fd9\u4e24\u7537\u4e24\u5973\uff0c\u5c31\u4ee4\u4eba\u6709\u4e9b\u6c57\u6bdb\u76f4\u7ad6\u3002 \u3000\u3000\u5927\u516c\u4e3b\u6307\u7740\u90a3\u4e9b\u68fa\u6728\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\u5e94\u5f53\u770b\u5f97\u51fa\uff0c\u540e\u9762\u9760\u53f3\u7684\u4e8c\u5341\u51e0\u53e3\u91cc\u9762\u88c5\u7684\u90fd\u662f\u6d3b \u4eba\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6014\u6014\u7684\u9053\uff1a\u201c\u5927\u516c\u4e3b\u600e\u4e48\u770b\u5f97\u51fa\u6765\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u90a3\u4e8c\u5341\u51e0\u53e3\u68fa\u6728\uff0c\u68fa\u76d6\u90fd\u6ca1\u5168\u76d6\uff0c\u800c\u4e14\u68fa\u6728\u4e24\u4fa7\u4e5f\u51ff\u6709\u6d1e\u5b54\uff0c\u662f\u4f9b\u4eba\u547c\u5438 \u7528\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1b\u201c\u53ef\u4e0d\u53ef\u4ee5\u8fd1\u524d\u5f80\u91cc\u9762\u770b\u770b\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u6447\u5934\u9053\uff1a\u201c\u90a3\u600e\u4e48\u53ef\u4ee5\uff0c\u9664\u4e86\u57ce\u4e3b\u548c\u5b88\u62a4\u7075\u5802\u7684\u5934\u76ee\uff0c\u8c01\u90fd\u4e0d\u80fd\u6253\u5f00\u68fa\u76d6\u5bdf\u770b\u7684\uff0c \u5c31\u4ee5\u6211\u6765\u8bf4\uff0c\u4e5f\u53ea\u80fd\u5728\u4e00\u65c1\u770b\u770b\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fd9\u56db\u4f4d\u5b88\u62a4\u7075\u5802\u7684\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8138\u4e0a\u4e00\u70b9\u8840\u8272\u90fd\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u7f5a\u5728\u8fd9\u91cc\u5b88\u62a4\u7684\uff0c\u8c01\u90fd\u4e0d\u80fd\u51fa\u53bb\uff0c\u4e0d\u89c1\u65e5\u5149\uff0c\u8138\u4e0a\u53c8\u90a3\u91cc\u6765\u7684\u8840 \u8272\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u4ed6\u4eec\u4e5f\u4e0d\u6362\u73ed\uff1f\u201d \u3000\u3000\u201c\u636e\u8bf4\u4ed6\u4eec\u4e00\u5171\u5341\u4e8c\u4eba\uff0c\u6bcf\u5929\u5206\u4e09\u73ed\u8f6e\u6362\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u4f4f\u90a3\u91cc\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u7075\u5802\u540e\u9762\u7684\u6d1e\u58c1\u4e0a\uff0c\u53e6\u6709\u4e24\u95f4\u77f3\u5ba4\uff0c\u4ed6\u4eec\u5c31\u4f4f\u5728\u90a3\u91cc\uff0c\u7537\u7684\u4f4f\u4e00\u95f4\uff0c\u5973\u7684\u4f4f\u4e00\u95f4\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u6362\u73ed\u7684\u65f6\u95f4\u5df2\u5230\uff0c\u679c\u7136\u7531\u5bf9\u9762\u8d70\u51fa\u4e24\u7537\u4e24\u5973\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u5cb3\u5c0f\u98de\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df2\u7684\u773c\u775b\uff0c\u53c8\u597d\u4f3c\u8eab\u5728\u68a6\u5883\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u53d1\u73b0\u5728\u90a3\u4e24\u540d\u9ed1\u8863\u5973\u5b50\u4e2d\uff0c\u5176\u4e2d\u4e00\u540d\uff0c\u8d6d\u7136\u662f\u4ed6\u7684\u6bcd\u4eb2\u3002 \u3000\u3000\u867d\u7136\u90a3\u5973\u5b50\u662f\u5728\u5bf9\u9762\uff0c\u79bb\u4ed6\u5f88\u8fdc\uff0c\u4e0d\u6613\u8fa9\u8bc6\uff0c\u4f46\u5979\u7684\u8eab\u6750\u3001\u9762\u90e8\u8f6e\u5ed3\u4ee5\u53ca\u8d70\u8def\u59ff\u6001\uff0c\u5bf9 \u4ed6\u6765\u8bf4\uff0c\u5374\u662f\u518d\u4e5f\u719f\u6089\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u6b64\u523b\uff0c\u5cb3\u5c0f\u98de\u7684\u4e00\u9897\u5fc3\uff0c\u6fc0\u52a8\u5f97\u51e0\u4e4e\u8981\u8df3\u51fa\u80f8\u8154\uff0c\u771f\u6068\u4e0d\u5f97\u7acb\u523b\u8dc3\u8eab\u8fc7\u53bb\u548c\u6bcd\u4eb2\u76f8\u8bc6\u3002 \u3000\u3000\u4f46\u662f\uff0c\u4ed6\u4e0d\u80fd\uff0c\u4ed6\u5fc5\u987b\u5f3a\u81ea\u6291\u5236\u7740\u5185\u5fc3\u7684\u6fc0\u52a8\uff0c\u56e0\u4e3a\u5927\u516c\u4e3b\u5c31\u5728\u8eab\u8fb9\uff0c\u5c24\u5176\u56db\u5468\u90fd\u6709\u5b88 \u62a4\uff0c\u6212\u5907\u68ee\u4e25\uff0c\u4e00\u65e6\u95f9\u51fa\u4e8b\u6765\uff0c\u5c82\u4e0d\u540e\u6094\u83ab\u53ca\u3002 \u3000\u3000\u4ed6\u5446\u5446\u7684\u8fdc\u671b\u7740\u6bcd\u4eb2\uff0c\u6bcd\u4eb2\u7684\u8138\u8272\u4e5f\u662f\u6194\u60b4\u65e0\u6bd4\uff0c\u82e5\u975e\u5979\u521a\u624d\u662f\u8d70\u7740\u51fa\u6765\u7684\uff0c\u800c\u662f\u8001\u65e9 \u7ad9\u5728\u90a3\u91cc\uff0c\u53ea\u6015\u4ed6\u5df2\u65e0\u6cd5\u8ba4\u51fa\u90a3\u5c31\u662f\u6bcd\u4eb2\u3002 \u3000\u3000\u81f3\u4e8e\u4ed6\u7684\u6bcd\u4eb2\uff0c\u5374\u662f\u4f4e\u7740\u5934\u9760\u5728\u5bf9\u9762\u7684\u5899\u89d2\u91cc\uff0c\u6839\u672c\u6ca1\u5bf9\u5cb3\u5c0f\u98de\u6ce8\u89c6\uff0c\u4e8b\u5b9e\u4e0a\u5979\u7eb5\u7136\u770b \u5230\u4e86\u5cb3\u5c0f\u98de\uff0c\u4e5f\u51b3\u4e0d\u53ef\u80fd\u60f3\u5230\u5cb3\u5c0f\u98de\u4f1a\u6765\u5230\u5929\u8c37\uff0c\u66f4\u60f3\u4e0d\u5230\u4ed6\u4f1a\u6765\u53c2\u89c2\u7075\u5802\uff0c \u3000\u3000\u4f55\u51b5\uff0c\u706f\u5149\u6697\u6de1\uff0c\u76f8\u9694\u53c8\u8fdc\uff0c\u6839\u672c\u4e5f\u6ca1\u6cd5\u770b\u5f97\u6e05\u695a\u3002 \u3000\u3000\u53ea\u542c\u5927\u516c\u4e3b\u9053\uff1a\u201c\u54b1\u4eec\u73b0\u5728\u8be5\u51fa\u53bb\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53c8\u671b\u4e86\u671b\u6bcd\u4eb2\u51e0\u773c\uff0c\u624d\u968f\u5927\u516c\u4e3b\u51fa\u4e86\u7075\u5802\u3002 \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\uff0c\u8fd8\u60f3\u5230\u4ec0\u4e48\u5730\u65b9\u770b\uff1f\u201d \u3000\u3000\u82e5\u672a\u9047\u5230\u6bcd\u4eb2\uff0c\u5cb3\u5c0f\u98de\u5f53\u7136\u60f3\u5230\u5904\u770b\u770b\uff0c\u5982\u4eca\uff0c\u4ed6\u90a3\u91cc\u8fd8\u6709\u5fc3\u60c5\u518d\u770b\u522b\u7684\uff0c\u957f\u957f\u5401\u4e00\u53e3 \u6c14\uff0c\u9053\uff1a\u201c\u4e0d\u77e5\u600e\u7684\uff0c\u5c4a\u4e0b\u611f\u5230\u5f88\u7d2f\uff0c\u4ec0\u4e48\u4e5f\u4e0d\u60f3\u518d\u770b\u4e86\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u5e26\u4e9b\u5403\u60ca\u7684\u795e\u8272\u9053\uff1a\u201c\u8fde\u6843\u82b1\u90fd\u6ca1\u8bf4\u7d2f\uff0c\u4f60\u7684\u6b66\u529f\u8fd9\u6837\u597d\uff0c\u600e\u4e48\u5c31\u7d2f\u4e86\u5462\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u7d2f\u4e0d\u7d2f\u4f3c\u4e4e\u4e0e\u6b66\u529f\u65e0\u5173\uff0c\u5c5e\u4e0b\u6628\u665a\u53ef\u80fd\u53d7\u4e86\u70b9\u98ce\u5bd2\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u53e5\u8bdd\u4e0d\u6253\u7d27\uff0c\u6843\u82b1\u7adf\u6709\u4e9b\u7740\u614c\u4e86\uff0c\u5fd9\u9053\uff1a\u201c\u516c\u5b50\uff0c\u53ef\u662f\u5a62\u5b50\u6ca1\u670d\u4f8d\u597d\uff1f\u201d \u3000\u3000\u6843\u82b1\u7684\u8bdd\u51fa\u53e3\u540e\uff0c\u624d\u89c9\u51fa\u8fd9\u6d3b\u6709\u6bdb\u75c5\uff0c\u4e0d\u89c9\u7c89\u8138\u4e00\u9635\u70ed\u8fa3\u8fa3\u7684\u611f\u89c9\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u600e\u4e48\u80fd\u602a\u59d1\u5a18\uff0c\u4f60\u623f\u91cc\u6628\u665a\u4e5f\u6ca1\u4f60\uff0c\u53ea\u80fd\u602a\u81ea\u5df1\u6ca1\u7167\u987e\u597d\u81ea\u5df1\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u65e2\u7136\u8eab\u4e0a\u4e0d\u8212\u670d\uff0c\u90a3\u5c31\u56de\u80b2\u5316\u57ce\u5e9c\u4f11\u606f\u5427\uff0c\u4eba\u5bb6\u57ce\u4e3b\u8fd8\u8981\u62db\u5f85\u4f60\u5403\u996d\u5462\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8d8b\u673a\u95ee\u9053\uff1a\u201c\u5927\u516c\u4e3b\uff0c\u8fd9\u4f4d\u57ce\u4e3b\u59d3\u4ec0\u4e48\uff1f\u53eb\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4ed6\u53eb\u4e25\u5bd2\uff0c\u662f\u6559\u4e3b\u7684\u5927\u5e08\u5144\uff0c\u4e5f\u662f\u6211\u7684\u5927\u5e08\u4f2f\u3002\u201d \u3000\u3000\u56de\u5230\u80b2\u5316\u57ce\u5e9c\uff0c\u5148\u7f34\u56de\u9ed1\u7389\u4ee4\u724c\uff0c\u56e0\u4e3a\u665a\u9910\u65f6\u95f4\u4e0d\u5230\uff0c\u53ea\u6709\u5728\u5927\u5385\u559d\u8336\u4f11\u606f\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u513f\uff0c\u80b2\u5316\u57ce\u4e3b\u4e25\u5bd2\u51fa\u6765\u4e86\u3002 \u3000\u3000\u5728\u5927\u516c\u4e3b\u773c\u4e2d\uff0c\u4e25\u5bd2\u4eca\u5929\u7684\u786e\u4e0e\u5f80\u65e5\u4e0d\u540c\uff0c\u663e\u5f97\u6ee1\u9762\u6625\u98ce\uff0c\u4e0d\u518d\u6273\u8d77\u4ed6\u7684\u8138\u5b54\u3002 \u3000\u3000\u4ed6\u4e00\u51fa\u6765\uff0c\u5927\u516c\u4e3b\u8fde\u5fd9\u5e26\u7740\u5cb3\u5c0f\u98de\u4e0a\u524d\u89c1\u793c\u3002 \u3000\u3000\u4e25\u5bd2\u5750\u4e0b\u540e\uff0c\u5148\u559d\u4e86\u53e3\u8336\uff0c\u624d\u6162\u6761\u65af\u7406\u7684\u9053\uff1a\u201c\u91d1\u51e4\uff0c\u4f60\u521a\u624d\u8bf4\u8fd9\u5b69\u5b50\u4e0d\u5230\u5341\u56db\u5c81\u5c31\u6709 \u8fd9\u4e48\u597d\u7684\u6587\u624d\u548c\u6b66\u529f\uff0c\u4f60\u53ef\u4eb2\u773c\u89c1\u8fc7\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u5f1f\u5b50\u867d\u6ca1\u4eb2\u773c\u89c1\u8fc7\uff0c\u4f46\u4e94\u5173\u62a5\u5230\u603b\u575b\u7684\u6210\u7ee9\u5374\u51b3\u4e0d\u4f1a\u5047\u3002\u201d \u3000\u3000\u4e25\u5bd2\u5475\u5475\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u8001\u592b\u5012\u60f3\u4eb2\u773c\u770b\u770b\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u7728\u4e86\u7728\u661f\u7738\u9053\uff1a\u201c\u8fd9\u91cc\u6ca1\u6709\u4e94\u5173\uff0c\u5927\u5e08\u4f2f\u600e\u80fd\u770b\u5f97\u5230\u5462\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u62c2\u4e86\u62c2\u9894\u4e0b\u7684\u987b\u9aef\u9053\uff1a\u201c\u96be\u9053\u8001\u592b\u5c31\u4e0d\u53ef\u4ee5\u8bd5\u8bd5\u4ed6\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u5403\u60ca\u9053\uff1a\u201c\u4ee5\u5927\u5e08\u4f2f\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u600e\u53ef\u548c\u4ed6\u52a8\u624b\u8fc7\u62db\uff0c\u800c\u4e14\u4ed6\u53c8\u600e\u6562\u548c\u5927\u5e08\u4f2f\u8f83 \u91cf\u5462\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u5475\u5475\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e2b\u5934\u60f3\u5230\u90a3\u91cc\u53bb\u4e86\u5462\uff1f\u96be\u9053\u6211\u4e0d\u53ef\u4ee5\u627e\u4eba\u548c\u4ed6\u6bd4\u5212\u6bd4\u5212\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u660e\u767d\u4e86\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u5012\u4f7f\u5f97\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u4e0d\u8fc7\u8001\u592b\u5fc5\u987b\u5148\u89c4\u5b9a\u597d\uff0c\u53cc\u65b9\u90fd\u662f\u70b9\u5230\u4e3a\u6b62\uff0c\u7edd\u5bf9\u4e0d\u53ef\u4f24\u4eba\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u5ffd\u7136\u76b1\u8d77\u7fe0\u7709\u9053\uff1a\u201c\u7980\u5e08\u4f2f\uff0c\u5cb3\u516c\u5b50\u521a\u624d\u8bf4\u8eab\u4e0a\u6709\u4e9b\u4e0d\u8212\u670d\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u4f60\u53ef\u662f\u62c5\u5fc3\u4ed6\u5403\u4e8f\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u8eab\u4e0a\u4e0d\u8212\u670d\uff0c\u603b\u662f\u5bf9\u8eab\u624b\u6709\u4e9b\u5f71\u54cd\u7684\u3002\u201d \u3000\u3000\u4e25\u5bd2\u7b11\u9053\uff1a\u201c\u8001\u592b\u65b9\u624d\u5df2\u7ecf\u8bf4\u8fc7\uff0c\u53ea\u662f\u70b9\u5230\u4e3a\u6b62\uff0c\u8c01\u4e5f\u4e0d\u53ef\u4f24\u8c01\uff0c\u6240\u8c13\u884c\u5bb6\u4e00\u51fa\u624b\uff0c\u4fbf \u77e5\u6709\u6ca1\u6709\uff0c\u51ed\u8001\u592b\u7684\u773c\u529b\uff0c\u53ea\u8981\u4ed6\u4e00\u51fa\u624b\uff0c\u4fbf\u4e0d\u96be\u6382\u51fa\u4ed6\u6709\u591a\u5927\u5206\u91cf\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u8bdd\uff0c\u56de\u5934\u53eb\u9053\uff1a\u201c\u5230\u524d\u9762\u6765\uff01\u201d \u3000\u3000\u7acb\u523b\uff0c\u4e00\u540d\u9ed1\u8863\u4eba\u7531\u5c4f\u98ce\u540e\uff0c\u80a9\u4e0d\u6643\u52a8\uff0c\u817f\u4e0d\u5c48\u819d\u7684\u98d8\u4e86\u51fa\u6765\uff0c \u3000\u3000\u5947\u602a\u7684\u662f\u8fd9\u540d\u9ed1\u8863\u4eba\u5c45\u7136\u9762\u7f69\u9ed1\u7eb1\uff0c\u6839\u672c\u770b\u4e0d\u51fa\u4ed6\u7684\u9762\u8c8c\u548c\u5e74\u7eaa\u3002 \u3000\u3000\u8fd9\u4e14\u4e0d\u8bf4\uff0c\u9ed1\u8863\u4eba\u8fde\u53cc\u624b\u4e5f\u5e26\u7740\u624b\u5957\uff0c\u5168\u8eab\u4e0a\u4e0b\uff0c\u9664\u4e86\u9732\u51fa\u773c\u775b\uff0c\u8fde\u76ae\u80a4\u4e5f\u4e0d\u8ba9\u4eba\u770b\u51fa \u534a\u70b9\u3002 \u3000\u3000\u5927\u516c\u4e3b\u832b\u7136\u95ee\u9053\uff1a\u201c\u5927\u5e08\u4f2f\uff0c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u7528\u4e0d\u7740\u95ee\u4ed6\u8db3\u51c6\uff0c\u53ea\u662f\u770b\u4ed6\u4eec\u6bd4\u8bd5\u5c31\u6210\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4eba\u4e3a\u4ec0\u4e48\u4e0d\u9732\u51fa\u771f\u9762\u76ee\u6765\uff1f\u201d \u3000\u3000\u201c\u4ed6\u957f\u5f97\u5f88\u96be\u770b\uff0c\u81ea\u5df1\u4e0d\u60f3\u8ba9\u5916\u4eba\u770b\u5230\uff0c\u8001\u592b\u4e5f\u4e0d\u4fbf\u52c9\u5f3a\u3002\u201d \u3000\u3000\u201c\u4ed6\u76ee\u524d\u53ef\u662f\u5e9c\u4e0a\u7684\u4eba\uff1f\u201d \u3000\u3000\u201c\u5f88\u96be\u8bf4\uff0c\u8001\u592b\u542c\u8bf4\u4ed6\u7684\u6b66\u529f\u4e0d\u9519\uff0c\u6709\u610f\u628a\u4ed6\u7559\u5728\u8eab\u8fb9\u62c5\u4efb\u4f8d\u536b\uff0c\u81f3\u4e8e\u4ed6\u80fd\u5426\u80dc\u4efb\uff0c\u7b49 \u4ed6\u8bd5\u8fc7\u8eab\u624b\u4e4b\u540e\uff0c\u9a6c\u4e0a\u4fbf\u77e5\u5206\u6653\u3002\u201d \u3000\u3000\u6b64\u523b\uff0c\u5cb3\u5c0f\u98de\u4e24\u773c\u4e00\u76f4\u5728\u9ed1\u8863\u4eba\u8eab\u4e0a\u6253\u8f6c\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u6b64\u4eba\u4e3e\u6b62\u4e4b\u95f4\uff0c\u8eab\u4e0a\u4f3c\u662f\u5341\u5206\u8f7b\u76c8\u7075\u6d3b\uff0c\u8eab\u6750\u867d\u7136\u5e76\u4e0d\u9ad8\u5927\uff0c\u4f46\u7ad9\u5728\u90a3\u91cc\uff0c\u5374\u522b \u6709\u4e00\u79cd\u5438\u5f15\u4eba\u7684\u529b\u91cf\u3002 \u3000\u3000\u53ea\u542c\u4e25\u5bd2\u53c8\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4eec\u4e8c\u4eba\u90fd\u4e0d\u66fe\u643a\u5e26\u5175\u5203\uff0c\u90a3\u5c31\u5f92\u624b\u5bf9\u640f\u597d\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u671b\u9ed1\u8863\u4eba\u53cc\u624b\u4e00\u62b1\u62f3\u9053\uff1a\u201c\u5144\u53f0\u8bf7\u5148\u8d50\u62db\uff01\u201d \u3000\u3000\u9ed1\u8863\u4eba\u4e5f\u62f1\u4e86\u62f1\u624b\uff0c\u5374\u5e76\u672a\u5f00\u53e3\u8bf4\u8bdd\u3002 \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u7528\u4e0d\u7740\u591a\u8bf4\u8bdd\uff0c\u5c0f\u5144\u5f1f\u5c31\u5148\u8fdb\u62db\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53c8\u4e00\u62f1\u624b\u9053\uff1a\u201c\u8bf7\u6055\u5728\u4e0b\u65e0\u793c\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u8bdd\uff0c\u4e00\u4e2a\u7bad\u6b65\uff0c\u7eb5\u4e0a\u524d\u53bb\uff0c\u62a1\u62f3\u76f4\u5411\u9ed1\u8863\u4eba\u80a9\u90e8\u6363\u53bb\u3002 \u3000\u3000\u8c01\u90fd\u53ef\u4ee5\u770b\u5f97\u51fa\uff0c\u4ed6\u8fd9\u4e00\u62db\u53ea\u662f\u8bd5\u63a2\u6027\u8d28\u3002 \u3000\u3000\u679c\u7136\uff0c\u9ed1\u8863\u4eba\u5e76\u4e0d\u5728\u610f\uff0c\u76f4\u5230\u62f3\u52bf\u5373\u5c06\u88ad\u53ca\uff0c\u624d\u5fae\u5fae\u4e00\u4fa7\u8eab\uff0c \u3000\u3000\u7136\u540e\u6b3a\u8eab\u75be\u8fdb\uff0c\u5de6\u638c\u53c9\u5f00\u4e94\u6307\uff0c\u95ea\u7535\u822c\u6293\u5411\u5cb3\u5c0f\u98de\u524d\u80f8\u5927\u7a74\u3002 \u3000\u3000\u4ed6\u7684\u52a8\u4f5c\u5341\u5206\u8f7b\u5de7\uff0c\u4f46\u5374\u8fc5\u5982\u95ea\u7535\u60ca\u96f7\uff0c\u5cb3\u5c0f\u98de\u4e00\u65f6\u5927\u610f\uff0c\u9669\u4e9b\u88ab\u5bf9\u65b9\u51fb\u4e2d\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u89c9\u51fa\u5bf9\u65b9\u8eab\u624b\u679c\u7136\u4e0d\u5f31\uff0c\u4fbf\u51b3\u5b9a\u653e\u624b\u67aa\u653b\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4ed6\u5e76\u4e0d\u60f3\u8ba9\u5bf9\u65b9\u53d7\u4f24\uff0c\u56e0\u4e3a\u4e25\u5bd2\u5df2\u6709\u610f\u6536\u6b64\u4eba\u4e3a\u8fd1\u8eab\u4f8d\u536b\uff0c\u603b\u8be5\u6210\u4eba\u4e4b\u7f8e\uff0c\u81ea\u5df1 \u53ea\u8981\u7565\u80dc\u4e00\u7b79\uff0c\u4e5f\u5c31\u591f\u4e86\u3002 \u3000\u3000\u5c82\u77e5\u9ed1\u8863\u4eba\u5374\u662f\u5f97\u7406\u4e0d\u8ba9\u4eba\uff0c\u62db\u52bf\u843d\u7a7a\u4e4b\u540e\uff0c\u63c9\u8eab\u518d\u8fdb\uff0c\u5de6\u638c\u53f3\u62f3\uff0c\u540c\u65f6\u5bf9\u4f4f\u5cb3\u5c0f\u98de\u4e0a \u4e0b\u4e24\u51fb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u88ab\u4e00\u8fde\u9000\u540e\u4e24\u6b65\uff0c\u84e6\u5730\u4e00\u8bb0\u5206\u6c34\u8dfa\u5b50\u811a\uff0c\u65cb\u8eab\u5012\u8e22\u51fa\u53bb\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u6025\u6025\u6536\u52bf\uff0c\u63a5\u7740\u4e00\u7f15\u6307\u98ce\uff0c\u76f4\u5411\u5cb3\u5c0f\u98de\u540e\u80cc\u80a9\u4e95\u7a74\u65f6\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u50cf\u80cc\u540e\u751f\u7740\u773c\u775b\uff0c\u6ed1\u6b65\u4e00\u4e2a\u6025\u65cb\uff0c\u4e0d\u4f46\u8ba9\u5f00\u4e86\u6307\u98ce\uff0c\u800c\u4e14\u53f3\u811a\u53c8\u8e6c\u5411\u9ed1\u8863\u4eba\u4fa7\u8170\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5c31\u5730\u65bd\u51fa\u4e00\u8bb0\u94c1\u677f\u6865\uff0c\u7136\u540e\u2014\u4e2a\u5012\u7eb5\uff0c\u7adf\u8f7b\u8f7b\u98d8\u98d8\u5730\u843d\u5230\u4e86\u6881\u67f1\u4e0a\u3002 \u3000\u3000\u4e24\u4eba\u4ea4\u624b\u51e0\u5408\uff0c\u5f88\u96be\u4ee4\u4eba\u5206\u51fa\u8c01\u80dc\u8c01\u8d1f\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7a33\u7ad9\u539f\u5730\u4e0d\u52a8\uff0c\u82e5\u8bba\u8f7b\u529f\uff0c\u4ed6\u6839\u672c\u4e0d\u4f1a\u628a\u5bf9\u65b9\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u7a81\u7136\uff0c\u9ed1\u8863\u4eba\u7531\u7cb1\u4e0a\u2014\u8dc3\u800c\u4e0b\uff0c\u76f4\u5411\u5cb3\u5c0f\u98de\u5934\u9876\u6251\u53bb\u3002 \u3000\u3000\u5c82\u77e5\u5cb3\u5c0f\u98de\u8fd9\u65f6\u4e5f\u817e\u8eab\u800c\u8d77\uff0c\u4e24\u4eba\u521a\u597d\u5728\u7a7a\u4e2d\u76f8\u9047\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\u7684\u5de5\u592b\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u5feb\u5f97\u4e0d\u80fd\u518d\u5feb\u7684\u8f7b\u54cd\uff0c\u4e24\u4eba\u5df2\u5728\u7a7a\u4e2d\u4ea4\u63a5\u4e86\u4e94\u516d\u62db\u3002 \u3000\u3000\u8fd9\u4e94\u516d\u62db\u5168\u662f\u8d77\u8eab\u76f8\u89e6\u7684\u77ed\u62db\uff0c\u9664\u4e86\u5f53\u4e8b\u4eba\u5fc3\u91cc\u6709\u6570\u5916\uff0c\u5728\u573a\u7684\u4e25\u5bd2\u548c\u5927\u516c\u4e3b\uff0c\u8c01\u4e5f\u6ca1 \u770b\u6e05\u4ed6\u4eec\u662f\u600e\u6837\u51fa\u624b\u7684\u3002 \u3000\u3000\u4f46\u4e25\u5bd2\u548c\u5927\u516c\u4e3b\u521a\u5b9a\u4e0b\u795e\u6765\uff0c\u5cb3\u5c0f\u98de\u548c\u9ed1\u8863\u4eba\u5df2\u65e9\u98d8\u8eab\u843d\u5730\u3002 \u3000\u3000\u8fd9\u6b21\u4e25\u5bd2\u548c\u5927\u516c\u4e3b\u603b\u7b97\u770b\u6e05\u695a\u4e86\uff0c\u5cb3\u5c0f\u98de\u505c\u8eab\u4e4b\u5904\uff0c\u4ecd\u5728\u539f\u5730\uff0c\u800c\u9ed1\u8863\u4eba\u5374\u5df2\u5728\u4e08\u4f59\u4e4b \u5916\u3002 \u3000\u3000\u518d\u770b\u5cb3\u5c0f\u98de\uff0c\u9762\u4e0d\u6539\u8272\uff0c\u6c14\u4e0d\u51fa\u58f0\u3002 \u3000\u3000\u9ed1\u8863\u4eba\u5219\u80f8\u8179\u4e4b\u95f4\uff0c\u4e0d\u4f4f\u8d77\u4f0f\uff0c\u90a3\u8499\u9762\u9ed1\u7eb1\u7684\u989d\u89d2\u90e8\u4f4d\uff0c\u4e5f\u4e00\u6ef4\u4e00\u6ef4\u7684\u6ef4\u4e0b\u6c57\u73e0\u3002 \u3000\u3000\u663e\u7136\uff0c\u4e24\u4eba\u4e4b\u95f4\u5df2\u7ecf\u5206\u51fa\u4e86\u80dc\u8d1f\u3002 \u3000\u3000\u4f46\u9ed1\u8863\u4eba\u5374\u4f3c\u4e0d\u7518\u5c31\u6b64\u8ba4\u8f93\uff0c\u7565\u4e00\u5598\u6c14\uff0c\u4fbf\u53c8\u6251\u8eab\u653b\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u8fd9\u6b21\u5cb3\u5c0f\u98de\u4e0d\u518d\u907f\u8ba9\uff0c\u4ea4\u624b\u4e09\u62db\uff0c\u90a3\u9ed1\u8863\u4eba\u7adf\u53c8\u4ef0\u6454\u51fa\u53bb\u3002 \u3000\u3000\u4e0d\u8fc7\u9ed1\u8863\u4eba\u4f3c\u662f\u5e76\u672a\u53d7\u4f24\uff0c\u6454\u51fa\u4e4b\u540e\uff0c\u4ecd\u80fd\u62ff\u693f\u7ad9\u7a33\uff0c\u53ea\u662f\u5df2\u5fae\u5fae\u53ef\u95fb\u5598\u606f\u4e4b\u58f0\u3002 \u3000\u3000\u4e25\u5bd2\u7acb\u5373\u6717\u58f0\u53eb\u9053\uff1a\u201c\u597d\u4e86\u597d\u4e86\uff0c\u53cc\u65b9\u5df2\u7136\u5206\u51fa\u9ad8\u4e0b\uff0c\u4e0d\u5fc5\u518d\u6253\u4e86\uff01\u201d \u3000\u3000\u63a5\u7740\u8f6c\u5934\u671b\u5411\u9ed1\u8863\u4eba\u9053\uff1a\u201c\u8fd8\u4e0d\u5feb\u9000\u4e0b\uff01\u201d \u3000\u3000\u9ed1\u8863\u4eba\u4f9d\u8a00\u9000\u56de\u5c4f\u98ce\u4e4b\u540e\u3002 \u3000\u3000\u4e25\u5bd2\u56de\u8fc7\u5934\u6765\u9053\uff1a\u201c\u5cb3\u5c0f\u5144\u5f1f\u679c\u7136\u662f\u4f4d\u6b66\u6797\u5c11\u89c1\u7684\u5947\u624d\uff0c\u8fde\u8001\u592b\u4e5f\u4e0d\u5f97\u4e0d\u522e\u76ee\u76f8\u770b\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u62b1\u62f3\u62f1\u624b\u9053\uff1b\u201c\u57ce\u4e3b\u8fc7\u5956\uff0c\u665a\u8f88\u6127\u4e0d\u6562\u5f53\uff0c\u5176\u5b9e\u65b9\u624d\u8fd9\u4f4d\u5144\u53f0\u7684\u8eab\u624b\uff0c\u665a\u8f88\u7167\u6837 \u4e5f\u662f\u4f69\u670d\u5f97\u5f88\uff01\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u4ed6\u56fa\u7136\u7b97\u5f97\u662f\u540d\u9ad8\u624b\uff0c\u4f46\u548c\u5c0f\u5144\u5f1f\u6bd4\u8d77\u6765\uff0c\u6b63\u5e94\u4e86\u5f3a\u4e2d\u66f4\u6709\u5f3a\u4e2d\u624b\u7684\u4e00\u53e5 \u8bdd\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u7acb\u5373\u5429\u5490\u6446\u9152\u4e0a\u83dc\u3002 \u3000\u3000\u4e25\u5bd2\u4eca\u5929\u5174\u81f4\u7279\u522b\u597d\uff0c\u5e76\u628a\u4ed6\u7684\u592b\u4eba\u4e5f\u8bf7\u51fa\u6765\u4f5c\u966a\u3002 \u3000\u3000\u996d\u540e\uff0c\u53c8\u8c08\u4e86\u5f88\u4e45\uff0c\u4ed6\u4eec\u4e24\u592b\u5987\u624d\u4eb2\u81ea\u628a\u5927\u516c\u4e3b\u548c\u5cb3\u5c0f\u98de\u9001\u51fa\u5927\u95e8\u3002 \u3000\u3000\u56de\u5230\u67f3\u6797\u9662\uff0c\u5927\u516c\u4e3b\u672c\u51c6\u5907\u4ecd\u7559\u5cb3\u5c0f\u98de\u4f4f\u4e00\u665a\u518d\u8d70\uff0c\u4f46\u5cb3\u5c0f\u98de\u575a\u6301\u8981\u56de\u53bb\uff0c\u5927\u516c\u4e3b\u4e5f\u5c31 \u4e0d\u4fbf\u575a\u6301\uff0c\u5e76\u7279\u5730\u5429\u5490\u6843\u82b1\u9001\u4e86\u4e00\u7a0b\u3002 \u3000\u3000\u5230\u8fbe\u4e94\u5173\u603b\u9547\u5e9c\uff0c\u5df2\u662f\u8d77\u66f4\u65f6\u5206\u3002 \u3000\u3000\u867d\u7136\u65f6\u95f4\u5df2\u665a\uff0c\u4ed6\u4ecd\u6025\u7740\u5148\u89c1\u7236\u4eb2\u9762\u62a5\u4e00\u5207\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u6b63\u5728\u623f\u5185\uff0c\u4ed6\u7167\u6837\u4e5f\u76fc\u7740\u4ed6\u65e9\u4e9b\u56de\u6765\u3002 \u3000\u3000\u5f53\u5cb3\u5c0f\u98de\u628a\u4e00\u5207\u7ecf\u8fc7\u8be6\u7ec6\u53d9\u8ff0\u4e00\u904d\u540e\uff0c\u82b1\u7389\u9e9f\u51c4\u7136\u53f9\u9053\uff1a\u201c\u4e3a\u7236\u771f\u662f\u4e0d\u77e5\u9053\uff0c\u4f60\u6bcd\u4eb2\u7adf \u7136\u662f\u88ab\u6253\u5728\u80b2\u5316\u57ce\u91cc\u5b88\u7075\uff0c\u90a3\u79cd\u6697\u65e0\u5929\u65e5\u7684\u751f\u6d3b\uff0c\u5979\u662f\u5982\u4f55\u5fcd\u53d7\u5f97\u4e86\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u89c9\u6cea\u5782\u53cc\u988a\u9053\uff1a\u201c\u7236\u4eb2\u603b\u8be5\u60f3\u529e\u6cd5\u6551\u51fa\u6bcd\u4eb2\u624d\u662f\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u65e0\u9650\u75db\u82e6\u7684\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u5df2\u77e5\u9053\u5929\u8c37\u6709\u592b\u5987\u4e0d\u53ef\u540c\u6765\u7684\u89c4\u5b9a\uff0c\u4e07\u4e00\u6211 \u548c\u4f60\u6bcd\u4eb2\u592b\u5987\u76f8\u8bc6\uff0c\u53ea\u6015\u8fde\u81ea\u8eab\u4e5f\u96be\u4ee5\u4fdd\u5168\uff0c\u53c8\u5982\u4f55\u6551\u5f97\u4e86\u4f60\u6bcd\u4eb2\uff1f\u201d \u3000\u3000\u4ed6\u4ef0\u8d77\u5934\u6765\uff0c\u7f04\u9ed8\u8bb8\u4e45\uff0c\u5ffd\u7136\u7075\u673a\u4e00\u52a8\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u6551\u4f60\u6bcd\u4eb2\uff0c\u4e5f\u8bb8\u80fd\u5e94\u5728\u4f60\u8eab\u4e0a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8bb6\u7136\u9053\uff1a\u201c\u5b69\u513f\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1b\u201c\u7167\u4f60\u521a\u624d\u6240\u8bf4\uff0c\u80b2\u5316\u57ce\u4e3b\u5fc5\u5b9a\u975e\u5e38\u559c\u6b22\u4f60\uff0c\u82e5\u5c06\u6765\u6709\u673a\u4f1a\u518d\u89c1\u5230\u4ed6\uff0c\u4f60\u4e0d \u59a8\u5927\u80c6\u63d0\u51fa\u3002\u201d \u3000\u3000\u201c\u5b69\u513f\u53ef\u5426\u8bf4\u51fa\u548c\u6bcd\u4eb2\u7684\u771f\u6b63\u5173\u7cfb\uff1f\u201d \u3000\u3000\u201c\u53ef\u4ee5\uff0c\u53ea\u8981\u4f60\u548c\u6211\u7684\u5173\u7cfb\u80fd\u7792\u4f4f\u4ed6\u4eec\u5c31\u6210\u4e86\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u6bcd\u4eb2\u5230\u5e95\u59d3\u4ec0\u4e48\u53eb\u4ec0\u4e48\uff0c\u7236\u4eb2\u5e94\u8be5\u544a\u8bc9\u5b69\u513f\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f60\u6bcd\u4eb2\u53eb\u4f55\u6167\u4ed9\uff0c\u8fd9\u540d\u5b57\u5373\u4f7f\u5728\u4e2d\u539f\u6b66\u6797\uff0c\u4e5f\u7edd\u5c11\u6709\u4eba\u77e5\u9053\uff0c \u3000\u3000\u6240\u4ee5\u8fd9\u4e09\u5e74\u6765\u7687\u5143\u6559\u4e3b\u624d\u67e5\u4e0d\u51fa\u6211\u548c\u5979\u7684\u771f\u6b63\u5173\u7cfb\u3002\u201d \u3000\u3000\u201c\u53ea\u662f\u5b69\u513f\u62c5\u5fc3\u4eca\u540e\u4e0d\u53ef\u80fd\u518d\u6709\u673a\u4f1a\u5230\u80b2\u5316\u57ce\u53bb\uff0c\u7236\u4eb2\u53ef\u8ba4\u8bc6\u80b2\u5316\u57ce\u4e3b\uff1f\u201d \u3000\u3000\u201c\u80b2\u5316\u57ce\u548c\u5bcc\u56fd\u57ce\u4e24\u4f4d\u57ce\u4e3b\uff0c\u90fd\u662f\u6559\u4e3b\u7684\u5e08\u5144\uff0c\u5728\u7687\u5143\u6559\u4e2d\u7b49\u4e8e\u7687\u4eb2\u56fd\u621a\uff0c\u6211\u867d\u7136\u89c1\u8fc7 \u4ed6\u4eec\uff0c\u4f46\u5374\u6500\u4e0d\u4e0a\u4ea4\u60c5\uff0c\u800c\u4e14\u4ee5\u4ed6\u4eec\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u8fde\u6559\u4e3b\u90fd\u8981\u656c\u4ed6\u4eec\u4e09\u5206\uff0c\u4e5f\u6839\u672c\u4e0d\u4f1a\u628a\u4e3a \u7236\u653e\u5728\u773c\u91cc\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u5b69\u513f\u7a76\u7adf\u8981\u5982\u4f55\u8bbe\u6cd5\u63a5\u8fd1\u4ed6\uff1f\u201d \u3000\u3000\u201c\u4e5f\u8bb8\u53ea\u80fd\u6162\u6162\u7b49\u673a\u4f1a\u4e86\u3002\u201d \u3000\u3000\u6b21\u65e5\u65e9\u4e0a\u5403\u996d\u65f6\uff0c\u4e01\u6d9b\u548c\u7518\u9716\u90fd\u56f4\u6765\u95ee\u957f\u95ee\u77ed\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u544a\u8bc9\u4ed6\u4eec\u66fe\u89c1\u8fc7\u6559\u4e3b\uff0c\u56e0\u4e3a\u90a3\u5929\u5927\u516c\u4e3b\u6765\u5230\u603b\u575b\u53bb\u7684\u4e8b\uff0c\u4e01\u6d9b\u548c\u7518\u9716\u90fd\u5df2\u77e5\u9053\u3002 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u5bf9\u5cb3\u5c0f\u98de\u80fd\u88ab\u6559\u4e3b\u53ec\u89c1\uff0c\u5185\u5fc3\u53ea\u6709\u7fa1\u6155\uff0c\u5e76\u65e0\u534a\u70b9\u5ac9\u5992\uff0c\u81ea\u5df1\u7684\u6b66\u529f\u6bd4\u8d77\u4eba \u5bb6\u76f8\u5dee\u592a\u8fdc\uff0c\u5cb3\u5c0f\u98de\u80fd\u88ab\u6559\u4e3b\u53ec\u89c1\uff0c\u4ed6\u4eec\u8ba4\u4e3a\u90a3\u662f\u7406\u6240\u5f53\u7136\u7684\u4e8b\u3002 \u3000\u3000\u81f3\u4e8e\u53c2\u89c2\u8fc7\u5bcc\u56fd\u57ce\u548c\u80b2\u5316\u57ce\uff0c\u5cb3\u5c0f\u98de\u5374\u5e76\u672a\u544a\u8bc9\u4ed6\u4eec\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8bb0\u6302\u7740\u8881\u5c0f\u9e64\uff0c\u996d\u540e\u4fbf\u5230\u4e86\u8881\u5c0f\u9e64\u623f\u4e2d\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u9664\u4e86\u7cbe\u795e\u8fd8\u6709\u70b9\u9893\u5510\uff0c\u5df2\u4e0d\u89c1\u75c5\u5bb9\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0c\u4f60\u4e00\u5411\u6ca1\u75c5\u8fc7\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u5ffd\u7136\u4e0d\u8212\u670d\u8d77\u6765\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u53ea\u5f97\u5411\u5cb3\u5c0f\u98de\u900f\u9732\u2014\u70b9\u6d88\u606f\u3002\u82e6\u7b11\u9053\uff1a\u201c\u6211\u662f\u89c1\u5230\u4e86\u51e4\u5ae3\u7ea2\u624d\u4e0d\u8212\u670d\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u6123\u9053\uff1a\u201c\u89c1\u4e86\u5979\u6709\u4ec0\u4e48\u4e0d\u8212\u670d\u7684\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1b\u201c\u4f60\u4e0a\u6b21\u66fe\u8bf4\u5e08\u7236\u90a3\u518c\u2018\u70bc\u5fc3\u5927\u6cd5\u79d8\u7b08\u2019\uff0c\u5df2\u88ab\u5979\u732e\u7ed9\u4e86\u6559\u4e3b\uff0c\u6211\u53ea\u662f\u534a\u4fe1 \u534a\u7591\uff0c\u53ca\u81f3\u89c1\u4e86\u51e4\u5ae3\u7ea2\uff0c\u5df2\u5b8c\u5168\u5f97\u5230\u8bc1\u5b9e\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u65e5\u540e\u5982\u4f55\u5411\u5e08\u7236\u4ea4\u4ee3\uff1f\u6211\u7684\u4e0d\u8212\u670d\uff0c \u4e5f\u6b63\u662f\u7531\u6b64\u800c\u8d77\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53cd\u800c\u677e\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u539f\u6765\u662f\u4e3a\u8fd9\u7f18\u6545\uff0c\u65e2\u7136\u5df2\u7ecf\u65e0\u6cd5\u8ba8\u56de\u90a3\u518c\u79d8\u7b08\uff0c\u70e6\u607c\u53c8\u6709\u4f55 \u7528\uff1f\u81ea\u5df1\u7684\u8eab\u5b50\u8981\u7d27\uff0c\u76ee\u524d\u53ea\u6709\u591a\u5fcd\u8010\uff0c \u3000\u3000\u800c\u4e14\u5e90\u4f2f\u4f2f\u5c06\u6765\u4e5f\u51b3\u4e0d\u4f1a\u56e0\u4e3a\u8fd9\u4ef6\u4e8b\u6028\u4f60\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u679c\u7136\u5fc3\u60c5\u5f00\u6717\u4e00\u4e9b\uff0c\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u4e5f\u89c1\u8fc7\u98ce\u5ae3\u7ea2\uff1f\u201d \u3000\u3000\u201c\u8881\u5927\u54e5\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u201c\u4e01\u6d9b\u548c\u7518\u9716\u4e24\u4f4d\u8bf4\u7684\uff0c\u4ed6\u4eec\u8bf4\u5927\u524d\u5929\u665a\u4e0a\u4f60\u4eec\u4e09\u4eba\u5230\u5bb5\u5173\u5de1\u89c6\uff0c\u540e\u6765\u51e4\u5ae3\u7ea2\u5355\u72ec\u53ec\u89c1 \u4f60\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u6211\u662f\u89c1\u8fc7\u4e86\u5979\u3002\u201d \u3000\u3000\u201c\u5979\u8ddf\u4f60\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5979\u4e5f\u8bf4\u8fc7\u90a3\u518c\u79d8\u7b08\u5df2\u732e\u7ed9\u4e86\u6559\u4e3b\uff0c\u53c8\u8bf4\u9a6c\u662d\u96c4\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u201c\u5979\u4e5f\u5bf9\u6211\u63d0\u8d77\u9a6c\u662d\u96c4\u5df2\u6b7b\u7684\u4e8b\uff0c\u4e0d\u8fc7\u6211\u5e76\u672a\u5b8c\u5168\u76f8\u4fe1\u3002\u201d \u3000\u3000\u201c\u8881\u5927\u54e5\u8ba4\u4e3a\u9a6c\u662d\u96c4\u8fd8\u6d3b\u7740\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u4ed6\u4e0d\u53ef\u80fd\u662f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u6b63\u8c08\u5230\u8fd9\u91cc\uff0c\u5ffd\u89c1\u4e01\u6d9b\u5306\u5306\u5fd9\u5fd9\u7684\u5954\u4e86\u8fdb\u6765\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u82b1\u526f\u603b\u9547\u6709\u8bf7\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u5f97\u5411\u8881\u5c0f\u9e64\u544a\u8f9e\uff0c\u6765\u5230\u82b1\u7389\u9e9f\u623f\u4e2d\u3002 \u3000\u3000\u53ea\u89c1\u82b1\u7389\u9e9f\u795e\u8272\u9887\u6709\u559c\u60a6\u4e4b\u60c5\uff0c\u4e00\u89c1\u5cb3\u5c0f\u98de\u5230\u6765\uff0c\u4fbf\u9053\uff1a\u201c\u98de\u513f\uff0c\u771f\u6ca1\u60f3\u5230\u673a\u4f1a\u6765\u5f97\u8fd9 \u4e48\u5feb\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u673a\u4f1a\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u4f60\u548c\u80b2\u5316\u57ce\u4e3b\u89c1\u9762\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u201c\u7236\u4eb2\u662f\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u201c\u80b2\u5316\u57ce\u4e3b\u6d3e\u4e86\u5927\u5c0f\u59d0\u4eb2\u81ea\u524d\u6765\uff0c\u8bf4\u662f\u57ce\u4e3b\u60f3\u518d\u89c1\u89c1\u4f60\u3002\u201d \u3000\u3000\u201c\u5927\u5c0f\u59d0\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u6211\u628a\u5979\u62db\u5f85\u5728\u5ba2\u5385\u65f6\uff0c\u518d\u56de\u6765\u5148\u8ddf\u4f60\u5546\u8bae\u4e00\u4e0b\uff0c\u4ee5\u4fbf\u4f60\u5728\u5fc3\u7406\u4e0a\u5148\u6709\u51c6\u5907\u3002\u201d \u3000\u3000\u8fd9\u6d88\u606f\u5bf9\u5cb3\u5c0f\u98de\u6765\u8bf4\uff0c\u8fd8\u771f\u6709\u4e9b\u7a81\u5982\u5176\u6765\uff0c\u4ed6\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u5927\u5c0f\u59d0\u7684\u6765\u610f\u5df2\u7ecf\u5411\u7236\u4eb2 \u8bf4\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u611f\u5230\u610f\u5916\uff0c\u4e0d\u8fc7\u6211\u770b\u5f97\u51fa\uff0c\u80b2\u5316\u57ce\u4e3b\u4e24\u592b\u5987\u5fc5\u5b9a\u662f\u5bf9\u4f60\u7279 \u522b\u8d4f\u8bc6\uff0c\u603b\u4e4b\uff0c\u8fd9\u662f\u6551\u4f60\u6bcd\u4eb2\u8131\u79bb\u82e6\u6d77\u7684\u6700\u597d\u673a\u4f1a\uff0c\u5343\u4e07\u8981\u597d\u597d\u628a\u63e1\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4f4e\u5934\u6c89\u541f\u4e00\u9635\u9053\uff1a\u201c\u4e07\u4e00\u80b2\u5316\u57ce\u4e3b\u5bf9\u5b69\u513f\u63d0\u51fa\u4ec0\u4e48\u6761\u4ef6\u5462\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u4f3c\u4e4e\u4e5f\u65e9\u60f3\u5230\u8fd9\u4e00\u5c42\uff0c\u4e0d\u89c9\u8e59\u8d77\u5251\u7709\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u4ec0\u4e48\u6761\u4ef6\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8b6c\u5982\u4ed6\u82e5\u6709\u610f\u628a\u5b69\u513f\u8c03\u5230\u80b2\u5316\u57ce\u53bb\uff0c\u6216\u8005\u5176\u4ed6\u65b9\u9762\u7684\u3002\u201d \u3000\u3000\u201c\u6709\u8fd9\u79cd\u53ef\u80fd\u5417\uff1f\u201d \u3000\u3000\u201c\u5b69\u513f\u90a3\u5929\u5148\u5230\u7684\u662f\u5bcc\u56fd\u57ce\uff0c\u5bcc\u56fd\u57ce\u4e3b\u5f53\u65f6\u5c31\u6709\u610f\u628a\u5b69\u513f\u8c03\u53bb\uff0c\u80b2\u5316\u57ce\u4e3b\u96be\u514d\u4e0d\u4f1a\u6709\u8fd9 \u79cd\u610f\u601d\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u4ed6\u65e2\u7136\u559c\u6b22\u4f60\uff0c\u4e0d\u7ba1\u4ec0\u4e48\u6761\u4ef6\uff0c\u5bf9\u4f60\u6765\u8bf4\uff0c\u4f3c\u4e4e\u90fd\u65e0\u6240\u8c13\uff0c\u4e3a\u4e86 \u6551\u4f60\u6bcd\u4eb2\uff0c\u5c3d\u7ba1\u7b54\u5e94\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u3002\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u522b\u8ba9\u5979\u7b49\u5f97\u592a\u4e45\u4e86\uff0c\u73b0\u5728\u5c31\u968f\u6211\u5230\u5927\u5385\u53bb\uff01\u201d \u3000\u3000\u6765\u5230\u5927\u5385\uff0c\u5cb3\u5c0f\u98de\u4e0d\u89c9\u773c\u775b\u4e00\u4eae\uff0c \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u5341\u516b\u4e5d\u5c81\u7684\u5973\u90ce\uff0c\u6b63\u9759\u9759\u5730\u72ec\u5750\u5728\u90a3\u91cc\u3002 \u3000\u3000\u8fd9\u5973\u90ce\u679c\u771f\u662f\u7f8e\u5f97\u51fa\u5947\uff0c\u7a7f\u7740\u4e00\u8eab\u7d2b\u8863\u7d2b\u88d9\uff0c\u4e91\u53d1\u9ad8\u633d\uff0c\u5f2f\u5f2f\u7684\u67f3\u53f6\u7709\uff0c\u4e00\u53cc\u9ed1\u767d\u5206\u660e \u7684\u5927\u773c\u775b\uff0c\u5507\u7ea2\u9f7f\u767d\uff0c\u53cc\u988a\u6709\u4e24\u4e2a\u8ff7\u4eba\u7684\u9152\u7a9d\uff0c\u80a4\u767d\u4f3c\u96ea\uff0c\u4e30\u59ff\u7ef0\u7ea6\uff0c\u663e\u5f97\u90a3\u4e48\u6e05\u65b0\u800c\u8131\u4fd7\u3002 \u3000\u3000\u4e0d\u7528\u8bf4\uff0c\u8fd9\u5c31\u662f\u80b2\u5316\u57ce\u7684\u5927\u5343\u91d1\u4e86\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u6709\u52b3\u5927\u5c0f\u59d0\u4e45\u5019\u4e86\uff01\u201d \u3000\u3000\u5927\u5c0f\u59d0\u7ad9\u8d77\u8eab\u6765\uff0c\u4e5f\u8fd8\u4e86\u4e00\u793c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u5cb3\u4f7f\u8005\u5417\uff1f\u201d \u3000\u3000\u5979\u8bed\u58f0\u5a07\u8106\u3002\u6709\u5982\u51fa\u8c37\u9ec4\u83ba\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5cb3\u4f7f\u8005\uff0c\u5feb\u89c1\u8fc7\u5927\u5c0f\u59d0\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u62b1\u4e86\u62b1\u62f3\u9053\uff1a\u201c\u5351\u804c\u89c1\u8fc7\u5927\u5c0f\u59d0\uff01\u201d \u3000\u3000\u5927\u5c0f\u59d0\u98ce\u81f4\u5ae3\u7136\u7684\u7b11\u9053\uff1a\u201c\u679c\u7136\u662f\u4e00\u8868\u4eba\u624d\uff0c\u602a\u4e0d\u5f97\u5bb6\u7236\u6628\u5929\u521a\u89c1\u8fc7\u4ed6\uff0c\u4eca\u5929\u53c8\u60f3\u89c1 \u4ed6\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u5927\u59d1\u5a18\u96be\u5f97\u5230\u8fd9\u91cc\u6765\uff0c\u7b49\u4e2d\u5348\u63a5\u53d7\u82b1\u67d0\u8bbe\u5bb4\u6b3e\u5f85\u518d\u8d70\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u5927\u5c0f\u59d0\u9053\uff1a\u201c\u82b1\u526f\u603b\u9547\u7684\u76db\u610f\uff0c\u665a\u8f88\u5fc3\u9886\u4e86\uff0c\u73b0\u5728\u79bb\u5348\u65f6\u8fd8\u65e9\u5f97\u5f88\uff0c\u665a\u8f88\u4e0d\u6562\u6253\u6270\uff0c\u8fd9 \u5c31\u8d70\u4e86\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u518d\u6b32\u5f3a\u7559\u3002\u5927\u5c0f\u59d0\u53c8\u9053\uff1a\u201c\u5bb6\u7236\u6bcd\u6025\u4e8e\u89c1\u4ed6\uff0c\u665a\u8f88\u5fc5\u987b\u73b0\u5728\u5c31\u8d70\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u53ea\u597d\u4eb2\u81ea\u9001\u51fa\u5927\u95e8\uff0c\u5e76\u4ea4\u4ee3\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5230\u4e86\u80b2\u5316\u57ce\uff0c\u5343\u4e07\u6ce8\u610f\u793c\u8c8c\uff0c\u5c3d\u91cf\u65e9\u53bb \u65e9\u56de\uff01\u201d \u3000\u3000\u8def\u4e0a\uff0c\u5927\u5c0f\u59d0\u8d70\u5728\u524d\u9762\u3002 \u3000\u3000\u671b\u7740\u5979\u90a3\u5029\u5029\u7f8e\u597d\u7684\u80cc\u5f71\uff0c\u83b2\u6b65\u751f\u59ff\uff0c\u522b\u6709\u4e00\u756a\u98ce\u81f4\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7a76\u7adf\u5e74\u7eaa\u592a\u5c0f\uff0c\u6709\u5f88\u591a\u8bdd\uff0c\u60f3\u95ee\u53c8\u4e0d\u4fbf\u95ee\u3002 \u3000\u3000\u5012\u662f\u5927\u5c0f\u59d0\u5148\u6253\u7834\u4e86\u5bc2\u5bde\uff0c\u5979\u56de\u5934\u671b\u4e86\u5cb3\u5c0f\u98de\u4e00\u773c\uff0c\u5ae3\u7136\u7b11\u9053\uff1b\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d \u8bf4\u8bdd\uff1f\u73b0\u5728\u53ea\u6709\u4f60\u6211\u4e24\u4eba\uff0c\u7528\u4e0d\u7740\u5bb3\u7f9e\u3002\u201d \u3000\u3000\u5979\u5bf9\u5cb3\u5c0f\u98de\u4e0d\u79f0\u5cb3\u4f7f\u8005\u800c\u79f0\u5c0f\u5144\u5f1f\uff0c\u663e\u7136\u662f\u5728\u8868\u793a\u4eb2\u5207\uff0c\u501f\u4ee5\u62c9\u8d77\u5f7c\u6b64\u95f4\u7684\u5173\u7cfb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8f7b\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u5351\u804c\u4e0d\u660e\u767d\uff0c\u548c\u57ce\u4e3b\u6628\u5929\u624d\u89c1\u8fc7\uff0c\u4ed6\u8001\u4eba\u5bb6\u4e3a\u4ec0\u4e48\u4eca\u5929\u53c8\u8981\u5ba3\u53ec \u5351\u804c\u53bb\uff1f\u201d \u3000\u3000\u201c\u5728\u6211\u9762\u524d\uff0c\u7528\u4e0d\u7740\u81ea\u79f0\u5351\u804c\uff0c\u8fd8\u628a\u6211\u770b\u6210\u50cf\u59d0\u59d0\u4e00\u822c\uff0c\u4e0d\u597d\u5417\uff1f\u201d \u3000\u3000\u201c\u5f7c\u6b64\u8eab\u4efd\u4e0d\u540c\uff0c\u90a3\u4e0d\u662f\u592a\u6298\u6211\u4e86\u5417\uff1f\u201d \u3000\u3000\u201c\u5c0f\u5144\u5f1f\u82e5\u518d\u5ba2\u6c14\uff0c\u5c31\u662f\u89c1\u5916\u4e86\uff01\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u5c31\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u624d\u5bf9\uff0c\u4f60\u521a\u624d\u95ee\u7684\uff0c\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u56e0\u4e3a\u5bb6\u7236\u5f88\u559c\u6b22\u4f60\uff0c\u4eca\u5929\u60f3\u5355\u72ec\u548c\u4f60\u89c1\u89c1\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u6628\u5929\u57ce\u4e3b\u627e\u4e86\u4e00\u4f4d\u8499\u9762\u4eba\u6765\u8bd5\u6211\u7684\u6b66\u529f\uff0c\u8fd9\u53c8\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u5927\u59d1\u5a18\u7b11\u9053\uff1a\u201c\u90a3\u662f\u4ed6\u542c\u8bf4\u4f60\u8fde\u95ef\u4e94\u5173\u7684\u6210\u7ee9\u592a\u597d\u4e86\uff0c\u4e00\u65f6\u5174\u4e4b\u6240\u81f3\uff0c\u624d\u51b3\u5b9a\u8981\u4eb2\u773c\u770b \u770b\u4f60\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u90a3\u4f4d\u8499\u9762\u4eba\u7684\u8eab\u624b\u5982\u4f55\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u7b97\u5f97\u4e0a\u662f\u4e00\u6d41\u9ad8\u624b\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u90a3\u4eba\u6700\u540e\u8fd8\u662f\u8d25\u5728\u4f60\u7684\u624b\u4e0b\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u56e0\u4e3a\u6211\u6700\u540e\u4e0d\u5f97\u4e0d\u5168\u529b\u5e94\u4ed8\uff0c\u5426\u5219\u8d25\u7684\u4e00\u65b9\u53ef\u80fd\u662f\u6211\u3002\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u4f60\u603b\u662f\u6700\u540e\u7684\u80dc\u5229\u8005\u3002\u201d \u3000\u3000\u201c\u90a3\u8499\u9762\u4eba\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u3000\u3000\u5927\u5c0f\u59d0\u5ffd\u7136\u56de\u5934\u5bf9\u4ed6\u795e\u79d8\u4e00\u7b11\uff1a\u201c\u96be\u9053\u4f60\u4e00\u70b9\u770b\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7ec8\u4e8e\u5fc3\u6709\u6240\u609f\uff1a\u201c\u6211\u89c9\u5f97\u90a3\u4eba\u597d\u50cf\u662f\u4e2a\u5973\u7684\u3002\u201d \u3000\u3000\u5927\u5c0f\u59d0\u9053\uff1a\u201c\u4f60\u4ece\u4ec0\u4e48\u5730\u65b9\u770b\u51fa\u662f\u4e2a\u5973\u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u90a3\u4eba\u7684\u4e3e\u624b\u6295\u8db3\uff0c\u90fd\u663e\u5f97\u975e\u5e38\u8f7b\u67d4\uff0c\u6709\u597d\u51e0\u6b21\u6211\u770b\u5230\u4e86\u5979\u7684\u624b\u8155\uff0c\u76ae\u80a4\u662f \u90a3\u4e48\u767d\u5ae9\u67d4\u7ec6\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u5462\uff1f\u201d \u3000\u3000\u201c\u4e00\u822c\u6765\u8bf4\uff0c\u8499\u9762\u4eba\u7528\u4e0d\u7740\u518d\u6234\u624b\u5957\uff0c\u800c\u5979\u5374\u504f\u504f\u6234\u7740\u624b\u5957\uff0c\u4f3c\u4e4e\u60f3\u8ba9\u5bf9\u65b9\u770b\u4e0d\u5230\u5979\u8eab \u4f53\u7684\u4efb\u4f55\u90e8\u5206\uff0c\u5c24\u5176\u5979\u81ea\u59cb\u81f3\u7ec8\u6ca1\u8bb2\u53e5\u8bdd\uff0c\u90a3\u5f53\u7136\u4e5f\u662f\u4e0d\u60f3\u8ba9\u5bf9\u65b9\u542c\u5230\u5979\u7684\u58f0\u97f3\uff1f\u201d \u3000\u3000\u5927\u5c0f\u59d0\u70b9\u70b9\u5934\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u5bf9\u90a3\u4eba\u89c2\u5bdf\u5f97\u5982\u6b64\u7ec6\u5bc6\uff0c\u90a3\u4eba\u7684\u786e\u662f\u4e2a\u5973\u7684\uff0c\u4f60\u731c\u5979\u662f\u51c6 \u5462\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6447\u5934\u9053\uff1a\u201c\u6211\u6839\u672c\u4e0d\u53ef\u80fd\u8ba4\u8bc6\u5979\uff0c\u53c8\u600e\u80fd\u77e5\u9053\u5979\u662f\u8c01\uff1f\u201d \u3000\u3000\u5927\u5c0f\u59d0\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u73b0\u5728\u4e00\u5b9a\u8ba4\u8bc6\u5979\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd9\u9053\uff1a\u201c\u83ab\u975e\u5c31\u662f\u5927\u5c0f\u59d0\uff1f\u201d \u3000\u3000\u5927\u5c0f\u59d0\u5a07\u9765\u5fae\u9161\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53ef\u60dc\u6211\u8fd8\u662f\u6253\u4e0d\u8fc7\u4f60\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fde\u5fd9\u62f1\u62f1\u624b\uff0c\u6b49\u7136\u966a\u7b11\u9053\uff1a\u201c\u5931\u793c\u5931\u793c\uff0c\u5927\u5c0f\u59d0\u5343\u4e07\u539f\u8c05\uff01\u201d \u3000\u3000\u5927\u5c0f\u59d0\u8d8a\u53d1\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff1a\u201c\u60ed\u6127\uff0c\u6211\u4e00\u5411\u81ea\u4ee5\u4e3a\u6b66\u529f\u5df2\u7ecf\u5f88\u4e86\u4e0d\u8d77\uff0c\u6240\u4ee5\u5f53\u6628\u5929\u5bb6\u7236 \u60f3\u8bd5\u8bd5\u4f60\u7684\u8eab\u624b\u65f6\uff0c\u6211\u81ea\u544a\u594b\u52c7\u8981\u548c\u4f60\u8bd5\u8bd5\uff0c\u6280\u4e0d\u5982\u4eba\uff0c\u6211\u8d25\u5f97\u5b9e\u5728\u6ca1\u6709\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u672c\u6765\uff0c\u5cb3\u5c0f\u98de\u60f3\u518d\u8bf4\u51e0\u53e5\u5b89\u6170\u7684\u8bdd\uff0c\u4f46\u5374\u60f3\u4e0d\u8d77\u8be5\u8bf4\u4ec0\u4e48\u624d\u597d\uff0c\u56e0\u4e3a\u82e5\u8bf4\u5f97\u4e0d\u6070\u5f53\uff0c\u53cd \u800c\u8d8a\u53d1\u589e\u52a0\u5bf9\u65b9\u7684\u96be\u582a\u3002 \u3000\u3000\u5230\u8fbe\u80b2\u5316\u57ce\uff0c\u8fd8\u4e0d\u5230\u4e2d\u5348\u3002 \u3000\u3000\u8fdb\u5165\u80b2\u5316\u57ce\u5e9c\u5927\u5385\u4e0d\u4e45\uff0c\u80b2\u5316\u57ce\u4e3b\u4e25\u5bd2\u4fbf\u548c\u4ed6\u7684\u592b\u4eba\u4e00\u8d77\u5230\u4e86\u5927\u5385\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u66fe\u542c\u5927\u516c\u4e3b\u8bf4\u8fc7\uff0c\u4e25\u5bd2\u7684\u4e3a\u4eba\uff0c\u548c\u4ed6\u7684\u540d\u5b57\u4e00\u6837\uff0c\u65e2\u51b7\u9177\u53c8\u4e0d\u82df\u8a00\u7b11\uff0c\u4f46\u5cb3\u5c0f\u98de \u6240\u89c1\u5230\u7684\uff0c\u5374\u5b8c\u5168\u4e0d\u662f\u8fd9\u6837\u5b50\uff0c\u4e0d\u4f46\u6ee1\u9762\u6625\u98ce\uff0c\u800c\u4e14\u7b11\u5bb9\u53ef\u63ac\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u4e5f\u8bb8\u662f\u56e0\u4eba\u800c\u5f02\u7684\u7f18\u6545\uff0c\u4ed6\u4e3b\u52a8\u628a\u5cb3\u5c0f\u98de\u53ec\u6765\uff0c\u603b\u4e0d\u80fd\u6273\u7740\u9762\u5b54\u5427\u3002 \u3000\u3000\u81f3\u4e8e\u4e25\u592b\u4eba\uff0c\u5374\u662f\u4f4d\u9053\u9053\u5730\u5730\u548c\u853c\u800c\u53c8\u6148\u7965\u7684\u8d35\u5987\u4eba\u3002 \u3000\u3000\u5f88\u5feb\u4fbf\u6446\u4e0a\u9152\u7b75\uff0c\u8fd9\u6b21\u4f5c\u966a\u7684\uff0c\u9664\u4e25\u5bd2\u5916\uff0c\u53c8\u589e\u52a0\u4e86\u4e25\u5927\u4eba\u548c\u5927\u5c0f\u59d0\u3002 \u3000\u3000\u5e2d\u95f4\uff0c\u4ed6\u4eec\u4e09\u4eba\u628a\u5cb3\u5c0f\u98de\u62db\u5f85\u5f97\u50cf\u4e00\u5bb6\u4eba\u90a3\u822c\u4eb2\u5207\uff0c\u628a\u4e2a\u5cb3\u5c0f\u98de\u5f04\u5f97\u7b80\u76f4\u53d7\u5ba0\u82e5\u60ca\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5f53\u7136\u5fc3\u91cc\u9ad8\u5174\uff0c\u56e0\u4e3a\u4ed6\u7684\u76ee\u7684\uff0c\u53ea\u662f\u8981\u6551\u6bcd\u4eb2\u4f55\u6167\u4ed9\u8131\u79bb\u82e6\u6d77\u3002 \u3000\u3000\u996d\u540e\uff0c\u64a4\u53bb\u676f\u76d8\uff0c\u56db\u4eba\u53e6\u6362\u5ea7\u4f4d\uff0c\u7ee7\u7eed\u996e\u8336\u8c08\u5929\u3002 \u3000\u3000\u53ea\u542c\u4e25\u5bd2\u9053\uff1a\u201c\u5cb3\u5c0f\u5144\u5f1f\uff0c\u4f60\u7684\u6b66\u529f\uff0c\u6628\u5929\u8001\u592b\u5df2\u7ecf\u89c1\u8fc7\u4e86\uff0c\u679c\u7136\u662f\u96be\u5f97\u4e00\u89c1\u7684\u5c11\u5e74\u5947 \u624d\uff0c\u53ea\u662f\u8001\u592b\u8fd8\u60f3\u8bd5\u8bd5\u4f60\u7684\u6587\u624d\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5230\u8fd9\u65f6\u624d\u660e\u767d\uff0c\u539f\u6765\u80b2\u5316\u57ce\u4e3b\u4eca\u5929\u628a\u81ea\u5df1\u53ec\u6765\uff0c\u662f\u8981\u8003\u8bd5\u6587\u624d\u7684\uff0c\u4e0d\u89c9\u6697\u5fd6\u9053\uff1a \u201c\u6211\u5df2\u7ecf\u901a\u8fc7\u4e94\u5173\uff0c\u4e3a\u4ec0\u4e48\u80b2\u5316\u57ce\u8981\u518d\u52a0\u6d4b\u8bd5\uff1f\u4ed6\u8fd9\u6837\u505a\uff0c\u5c82\u975e\u591a\u6b64\u4e00\u4e3e\uff0c\u4e94\u5173\u662f\u6559\u4e3b\u8bbe\u7acb \u7684\uff0c\u96be\u9053\u4ed6\u8fde\u6559\u4e3b\u90fd\u4fe1\u4e0d\u8fc7\uff1f\u800c\u4e14\u4ed6\u8fd9\u6837\u505a\uff0c\u5bf9\u6559\u4e3b\u4e5f\u9887\u4e0d\u793c\u8c8c\uff0c\u5c82\u4e0d\u5f62\u6210\u4ed6\u7684\u5a01\u529b\u6bd4\u6559\u4e3b \u8fd8\u5927\uff1f\u201d \u3000\u3000\u4f46\u80b2\u5316\u57ce\u4e3b\u65e2\u7136\u8fd9\u6837\u8bf4\u4e86\uff0c\u5cb3\u5c0f\u98de\u4e5f\u53ea\u597d\u9759\u542c\u5b89\u6392\u4e86\u3002 \u3000\u3000\u4e25\u5bd2\u89c1\u5cb3\u5c0f\u98de\u5e76\u65e0\u53cd\u5e94\uff0c\u4e0d\u89c9\u76b1\u8d77\u7709\u5934\u9053\uff1a\u201c\u4e0d\u77e5\u5c0f\u5144\u5f1f\u662f\u5426\u540c\u610f\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8eac\u8eab\u9053\uff1a\u201c\u65e2\u7136\u57ce\u4e3b\u5429\u5490\u4e0b\u6765\uff0c\u665a\u8f88\u6562\u4e0d\u4ece\u547d\u3002\u201d \u3000\u3000\u4e25\u5bd2\u968f\u5373\u5429\u5490\u7ad9\u5728\u8eab\u540e\u7684\u2014\u540d\u4e0b\u4eba\u9053\uff1a\u201c\u53bb\u628a\u6587\u5148\u751f\u8bf7\u6765\uff01\u201d \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u513f\uff0c\u7531\u5916\u9762\u8d70\u8fdb\u4e00\u4f4d\u987b\u53d1\u534a\u767d\u7684\u8001\u5148\u751f\u3002 \u3000\u3000\u8fd9\u4f4d\u8001\u5148\u751f\uff0c\u5e74\u5728\u516d\u65ec\u5de6\u53f3\uff0c\u5934\u6234\u7ea2\u9876\u74dc\u76ae\u5c0f\u5e3d\uff0c\u8138\u4e0a\u5e72\u762a\u5f97\u50cf\u98ce\u5e72\u6a58\u5b50\u76ae\uff0c\u9894\u4e0b\u7559\u4e00 \u64ae\u5c71\u7f8a\u80e1\u5b50\uff0c\u84dd\u957f\u888d\uff0c\u9ed1\u9a6c\u8902\uff0c\u624b\u91cc\u8fd8\u62ff\u7740\u4e00\u6839\u957f\u6746\u65f1\u70df\u888b\uff0c\u5f88\u50cf\u4e00\u4f4d\u8001\u5b66\u7a76\uff0c\u4f46\u6a21\u6837\u548c\u795e \u60c5\u5374\u663e\u5f97\u975e\u5e38\u6ed1\u7a3d\u3002 \u3000\u3000\u6587\u8001\u5148\u751f\u8fdb\u6765\u4ee5\u540e\uff0c\u671b\u7740\u4e25\u5bd2\u9053\uff1a\u201c\u4e1c\u7fc1\u76f8\u53ec\u6587\u67d0\u6709\u4e8b\u5417\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u5bf9\u6587\u8001\u5148\u751f\u9887\u4e3a\u793c\u8c8c\uff0c\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u6587\u5148\u751f\u8bf7\u5750\uff0c\u8fd9\u4f4d\u5c31\u662f\u8001\u592b\u5bf9\u4f60\u8bf4\u8fc7\u7684\u5cb3\u5c0f \u5144\u5f1f\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fde\u5fd9\u5411\u6587\u5148\u751f\u89c1\u793c\u3002 \u3000\u3000\u4e25\u5bd2\u518d\u5bf9\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u820d\u4e0b\u7684\u4efb\u897f\u5e2d\uff0c\u59d3\u6587\u540d\u4ea6\u5947\uff0c\u6587\u5148\u751f\u6ee1\u8179\u7ecf\u4f26\uff0c\u548c\u6587\u5173\u7684 \u4e3b\u8003\u6768\u51e4\u5802\u6bd4\u8d77\u6765\uff0c\u5e76\u4e0d\u591a\u8ba9\uff0c\u5f53\u5e74\u6559\u4e3b\u672c\u6765\u6709\u610f\u8ba9\u6587\u5148\u751f\u5230\u6587\u5173\u62c5\u4efb\u4e3b\u8003\uff0c\u662f\u8001\u592b\u628a\u4ed6\u575a \u7559\u5728\u820d\u4e0b\u4efb\u897f\u5e2d\u7684\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u559d\u4e86\u53e3\u8336\u9053\uff1a\u201c\u4e1c\u7fc1\u53ef\u662f\u6709\u610f\u8ba9\u6587\u67d0\u6765\u8003\u8003\u8fd9\u4f4d\u5cb3\u5c0f\u5144\u5f1f\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9894\u9996\u9053\uff1a\u201c\u5cb3\u5c0f\u5144\u5f1f\u7684\u6b66\u529f\uff0c\u8001\u592b\u5df2\u7ecf\u89c1\u8fc7\uff0c\u5f53\u7136\u4e5f\u5e0c\u671b\u4eb2\u773c\u89c1\u8bc6\u89c1\u8bc6\u4ed6\u7684\u6587\u624d\u3002 \u3000\u3000\u6587\u4ea6\u5947\u71c3\u4e0a\u4e00\u888b\u70df\uff0c\u5438\u4e86\u51e0\u53e3\u9053\uff1a\u201c\u4ed6\u5728\u6587\u5173\uff0c\u6768\u5144\u51e4\u5802\u5fc5\u5b9a\u5df2\u7ecf\u8003\u8fc7\u4e86\u4ed6\uff0c\u60f3\u6765\u662f\u4e0d \u9519\u7684\u3002\u201d \u3000\u3000\u201c\u6587\u5148\u751f\u4f55\u59a8\u518d\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u6587\u4ea6\u5947\u77a5\u4e86\u5cb3\u5c0f\u98de\u4e00\u773c\u9053\uff1a\u201c\u5cb3\u5c0f\u5144\u5f1f\u5728\u6587\u5173\u8003\u7684\u5185\u5bb9\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u65e0\u975e\u662f\u56db\u4e66\u4e94\u7ecf\uff0c\u8bd7\u8bcd\u6b4c\u8d4b\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u9053\uff1a\u201c\u90a3\u4e48\u73b0\u5728\u54b1\u4eec\u5c31\u8003\u70b9\u522b\u7684\u3002\u201d \u3000\u3000\u201c\u4f46\u8bf7\u6587\u5148\u751f\u547d\u9898\u3002\u201d \u3000\u3000\u5c82\u77e5\u6587\u4ea6\u5947\u5374\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u5cb3\u5c0f\u5144\u5f1f\u770b\u8fc7\u4e09\u56fd\u6f14\u4e49\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u7531\u5730\u5927\u611f\u60ca\u5947\uff0c\u6587\u5148\u751f\u4e3a\u4ec0\u4e48\u95ee\u8d77\u8fd9\u4e2a\u6765\u5462\uff1f\u53ea\u597d\u6123\u6123\u7684\u7b54\u9053\uff1a\u201c\u665a\u8f88\u770b\u8fc7\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u6478\u7740\u5c71\u7f8a\u80e1\u5b50\uff0c\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u90a3\u5c31\u597d\u529e\uff0c\u54b1\u4eec\u5c31\u8003\u8003\u4e09\u56fd\u5427\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u88ab\u5f04\u5f97\u679c\u771f\u53c8\u5403\u4e86\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u8003\u4e09\u56fd\uff1f\u771f\u662f\u7834\u5929\u8352\u7b2c\u4e00\u6b21\u542c\u5230\u4e16\u4e0a\u6709\u8fd9\u79cd\u4e8b \u60c5\u2026\u2026\u201d \u3000\u3000\u4ed6\u90a3\u91cc\u77e5\u9053\uff0c\u539f\u6765\u6587\u4ea6\u5947\u662f\u4e09\u56fd\u8ff7\uff0c\u8fd9\u90e8\u624d\u5b50\u4e66\uff0c\u4ed6\u81f3\u5c11\u4e5f\u770b\u8fc7\u51e0\u5341\u904d\uff0c\u66f9\u5b5f\u5fb7\u7684\u6a2a\u69ca \u8d4b\u8bd7\u4ee5\u53ca\u8bf8\u845b\u4eae\u7684\u524d\u540e\u51fa\u5e08\u8868\uff0c\u4ed6\u80fd\u5012\u80cc\u5982\u6d41\u3002 \u3000\u3000\u504f\u504f\u4ed6\u7684\u4e1c\u7fc1\u80b2\u5316\u57ce\u4e3b\u4e25\u5bd2\u4e5f\u662f\u4f4d\u4e09\u56fd\u8ff7\uff0c\u56e0\u4e4b\u4e24\u4eba\u624d\u80fd\u4e00\u89c1\u5982\u6545\uff0c\u4ed6\u548c\u4e25\u5bd2\u6ca1\u4e8b\u65f6\u5728 \u4e00\u8d77\u804a\u5929\uff0c\u6839\u672c\u79bb\u4e0d\u5f00\u4e09\u56fd\uff0c\u6709\u65f6\u4e00\u804a\u5c31\u662f\u5927\u534a\u5929\uff0c\u4f3c\u4e4e\u8c08\u4e09\u56fd\u6bd4\u5403\u996d\u7761\u89c9\u66f4\u91cd\u8981\u3002 \u3000\u3000\u5982\u4eca\u6587\u4ea6\u5947\u63d0\u8bae\u8003\u5cb3\u5c0f\u98de\u7684\u4e09\u56fd\uff0c\u6b63\u662f\u5408\u4e86\u4e25\u5bd2\u7684\u5fc3\u610f\u3002 \u3000\u3000\u4ed6\u53c8\u559d\u4e86\u53e3\u8336\uff0c\u518d\u5e72\u54b3\u51e0\u58f0\uff0c\u626b\u6e05\u55d3\u95e8\u9053\uff1a\u201c\u5cb3\u5c0f\u5144\u5f1f\uff0c\u4f60\u7684\u4e09\u56fd\u8bfb\u7684\u719f\u4e0d\u719f\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u665a\u8f88\u53ea\u770b\u8fc7\u4e24\u904d\uff0c\u91cd\u8981\u60c5\u8282\u5f53\u7136\u4e0d\u4f1a\u5fd8\u8bb0\uff0c\u4f46\u7ec6\u7410\u4e8b\u60c5\uff0c\u53ef\u80fd\u5c31\u8bb0\u4e0d\u6e05\u695a \u4e86\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u9053\uff1a\u201c\u90a3\u5c31\u8003\u70b9\u7b80\u5355\u7684\uff0c\u4e0d\u8fc7\u8001\u673d\u5fc5\u987b\u544a\u8bc9\u4f60\uff0c\u8003\u7684\u8d8a\u6df1\uff0c\u6210\u7ee9\u8d8a\u9ad8\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u521a\u8981\u51fa\u9898\uff0c\u4e25\u5bd2\u5374\u6446\u6446\u624b\u9053\uff1a\u201c\u7ed9\u4ed6\u70b9\u513f\u65f6\u95f4\uff0c\u8ba9\u4ed6\u56de\u5fc6\u4e00\u4e0b\uff0c\u82e5\u9a6c\u4e0a\u5c31\u8003\uff0c\u53ef \u80fd\u5f71\u54cd\u6210\u7ee9\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u9053\uff1a\u201c\u8fd9\u4e09\u56fd\u53ef\u662f\u4e0d\u7b80\u5355\u7684\uff0c\u5982\u679c\u8003\u4e09\u56fd\u80fd\u8003\u5230\u6ee1\u5206\uff0c\u4e2d\u72b6\u5143\u4e00\u5b9a\u4e0d\u4f1a\u6709\u95ee\u9898\u3002\u201d \u3000\u3000\u4e25\u5bd2\u563f\u563f\u7b11\u9053\uff1b\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u8003\u72b6\u5143\uff1f\u201d \u3000\u3000\u6587\u4ea6\u5947\u5e72\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u8003\u5566\uff0c\u6587\u67d0\u6700\u62c5\u5fc3\u7684\uff0c\u5c31\u662f\u6015\u8003\u4e2d\u72b6\u5143\u3002\u201d \u3000\u3000\u4e25\u5bd2\u6115\u7136\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u6587\u4ea6\u5947\u9053\uff1a\u201c\u7406\u7531\u5f88\u7b80\u5355\uff0c\u4e2d\u4e86\u72b6\u5143\u5341\u6709\u516b\u4e5d\u4f1a\u88ab\u62db\u4e3a\u9a78\u9a6c\uff0c\u90a3\u6709\u8fd9\u79cd\u5e74\u7eaa\u624d\u5f53\u9a78\u9a6c\u7684\uff0c \u5c82\u4e0d\u8bef\u4e86\u4eba\u5bb6\u516c\u4e3b\u7684\u9752\u6625\uff0c\u53c8\u600e\u80fd\u5bf9\u5f97\u4f4f\u4e07\u5c81\u7237\uff01\u201d \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u5341\u4e5d\u7ae0\u3000\u6697\u62e9\u4f73\u5a7f \u3000\u3000\u5927\u7ea6\u7b49\u4e86\u4e0d\u5230\u987f\u996d\u65f6\u95f4\uff0c\u6587\u4ea6\u5947\u5df2\u6709\u4e9b\u6025\u5f97\u71ac\u4e0d\u4f4f\u3002 \u3000\u3000\u8003\u751f\u4e0d\u6025\u4e3b\u8003\u5b98\u6025\uff0c\u8fd9\u79cd\u73b0\u8c61\uff0c\u8bf4\u8d77\u6765\u8fd8\u771f\u5c11\u89c1\u3002 \u3000\u3000\u90a3\u662f\u56e0\u4e3a\u6587\u4ea6\u5947\u6025\u4e8e\u5356\u5f04\u5b66\u95ee\uff0c\u4ee5\u4fbf\u663e\u51fa\u4ed6\u662f\u4f4d\u4e09\u56fd\u901a\uff0c\u5c24\u5176\u5f53\u7740\u4e1c\u7fc1\u4e25\u5bd2\u7684\u9762\uff0c\u4e0d\u80fd \u4e0d\u51fa\u51fa\u98ce\u5934\uff0c\u8c01\u90fd\u77e5\u9053\uff0c\u82e5\u672c\u8eab\u4e0d\u901a\u4e09\u56fd\uff0c\u53c8\u5982\u4f55\u80fd\u8003\u522b\u4eba\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u795e\u8272\u6de1\u7136\uff0c\u4f3c\u4e4e\u5e76\u6ca1\u62ff\u53d7\u6d4b\u5f53\u56de\u4e8b\uff0c\u6f2b\u58f0\u5e94\u9053\uff1a\u201c\u51c6\u5907\u597d\u4e86\uff01\u201d \u3000\u3000\u6587\u4ea6\u5947\u9996\u5148\u6765\u4e2a\u5f00\u573a\u767d\u9053\uff1a\u201c\u4e09\u56fd\u6f14\u4e49\u8fd9\u90e8\u624d\u5b50\u4e66\uff0c\u662f\u4ece\u6c49\u7075\u5e1d\u5f00\u59cb\u5199\u8d77\uff0c\u5148\u6709\u201c\u5341\u5e38 \u4f8d\u201d\u4e4b\u4e71\uff0c\u7136\u540e\u5f15\u8d77\u9ec4\u5dfe\u8d3c\u9020\u53cd\uff0c\u63a5\u7740\u5218\u3001\u5173\u3001\u5f20\u6843\u56ed\u7ed3\u4e49\uff0c\u4e2d\u95f4\u7ecf\u8fc7\u4e86\u51e0\u5341\u5e74\uff0c\u76f4\u5230\u66f9\u4e15 \u7be1\u6c49\u79f0\u5e1d\uff0c\u624d\u7b97\u662f\u771f\u6b63\u4e09\u56fd\u65f6\u4ee3\u5f00\u59cb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6587\u5148\u751f\u662f\u5426\u51c6\u5907\u4ece\u66f9\u4e15\u7be1\u4f4d\u540e\u5f00\u59cb\u8003\u8d77\uff1f\u201d \u3000\u3000\u6587\u4ea6\u5947\u6447\u5934\u9053\uff1a\u201c\u4e0d\uff0c\u4ece\u4e09\u56fd\u6f14\u4e49\u4e00\u5f00\u59cb\u5c31\u7b97\uff0c\u4e8b\u5b9e\u4e0a\u4e09\u56fd\u6f14\u4e49\u4e0a\u7684\u7cbe\u5f69\u6545\u4e8b\uff0c\u591a\u534a\u5728 \u4e09\u56fd\u6210\u7acb\u4e4b\u524d\uff0c\u5982\u864e\u7262\u5173\u4e09\u82f1\u6218\u5415\u5e03\u3001\u8f95\u95e8\u5c04\u7bad\u3001\u767d\u95e8\u697c\u3001\u767d\u9a6c\u5761\u3001\u8fc7\u4e94\u5173\u3001\u53e4\u57ce\u4f1a\u3001\u4e09\u987e \u8305\u5e90\u3001\u957f\u5742\u5761\u3001\u706b\u70e7\u8d64\u58c1\u3001\u534e\u5bb9\u9053\u3001\u6218\u957f\u6c99\u3001\u5355\u5200\u8d74\u4f1a\u3001\u522e\u9aa8\u7597\u6bd2\u3001\u6c34\u6df9\u4e03\u519b\u3001\u5b9a\u519b\u5c71\u7b49\uff0c \u90fd\u662f\u5728\u4e09\u56fd\u6210\u7acb\u4e4b\u524d\uff0c\u8fde\u5173\u516c\u4e5f\u662f\u4e1c\u6c49\u7684\u4eba\uff0c\u800c\u4e0d\u662f\u4e09\u56fd\u65f6\u4ee3\u7684\u4eba\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u4e00\u8fde\u8bf4\u4e86\u8fd9\u4e48\u591a\uff0c\u65e0\u975e\u5728\u70ab\u8000\u4ed6\u786e\u662f\u719f\u8bfb\u8fc7\u4e09\u56fd\u6f14\u4e49\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6587\u5148\u751f\u4e0d\u5fc5\u591a\u4ecb\u7ecd\uff0c\u5c31\u8bf7\u5f00\u59cb\u8003\u8bd5\u5427\uff01\u201d \u3000\u3000\u6587\u4ea6\u5947\u9894\u9996\u9053\uff1a\u201c\u5173\u3001\u5f20\u3001\u8d75\u3001\u9a6c\u3001\u9ec4\u662f\u8700\u6c49\u7684\u4e94\u864e\u4e0a\u5c06\uff0c\u5cb3\u5c0f\u5144\u5f1f\u8bf7\u8bf4\u51fa\u4ed6\u4eec\u5404\u4eba\u6240 \u7528\u7684\u5175\u5668\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5173\u516c\u7528\u7684\u9752\u9f99\u5043\u6708\u5200\uff0c\u5f20\u98de\u662f\u4e08\u516b\u86c7\u77db\uff0c\u8d75\u4e91\u3001\u9a6c\u8d85\u90fd\u7528\u957f\u67aa\uff0c\u9ec4\u5fe0\u4f7f\u7684 \u662f\u5927\u5200\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u6709\u5339\u6700\u6709\u540d\u7684\u9a6c\uff0c\u53eb\u4ec0\u4e48\u9a6c\uff1f\u8c01\u9a91\u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fd9\u9a6c\u53eb\u8d64\u5154\u9a6c\uff0c\u6700\u5148\u662f\u5415\u5e03\u7684\u6218\u9a6c\uff0c\u5415\u5e03\u6b7b\u540e\uff0c\u5f52\u66f9\u64cd\u6240\u6709\uff0c\u66f9\u64cd\u53c8\u8d60\u7ed9 \u5173\u516c\uff0c\u5173\u516c\u5f52\u5929\u540e\uff0c\u8d64\u5154\u9a6c\u4e0d\u98df\u8349\u6599\u800c\u6b7b\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e94\u864e\u4e0a\u5c06\u7684\u6218\u888d\uff0c\u90fd\u662f\u4ec0\u4e48\u989c\u8272\u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5173\u516c\u7a7f\u7eff\u888d\uff0c\u5f20\u98de\u9ed1\u888d\uff0c\u8d75\u4e91\u3001\u9a6c\u8d85\u662f\u767d\u888d\uff0c\u9ec4\u5fe0\u9ec4\u888d\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u5173\u516c\u65a9\u8fc7\u56db\u5458\u6709\u540d\u4e0a\u5c06\uff0c\u90fd\u6ca1\u8d85\u8fc7\u4e09\u56de\u5408\uff0c\u8fd9\u56db\u5458\u4e0a\u5c06\u90fd\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u6e29\u9152\u65a9\u534e\u96c4\uff0c\u767d\u9a6c\u5761\u65a9\u989c\u826f\u8bdb\u6587\u4e11\uff0c\u53e4\u57ce\u4e4b\u524d\u65a9\u8fc7\u8521\u9633\u7684\u5934\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u4e2d\u6709\u51e0\u4f4d\u7f8e\u5973\uff1f\u201d \u3000\u3000\u8fd9\u9898\u76ee\u51fa\u5f97\u592a\u7b3c\u7edf\uff0c\u8fd8\u771f\u8ba9\u5cb3\u5c0f\u98de\u96be\u4ee5\u56de\u7b54\uff0c\u4ed6\u987f\u4e86\u4e00\u987f\u9053\uff1a \u3000\u3000\u201c\u4e09\u56fd\u65f6\u7684\u7f8e\u5973\u592a\u591a\u4e86\uff0c\u6587\u5148\u751f\u53ef\u5426\u628a\u9898\u76ee\u8bf4\u5f97\u660e\u767d\u4e9b\uff1f\u201d \u3000\u3000\u6587\u4ea6\u5947\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u5f53\u7136\u662f\u4e66\u4e0a\u6709\u7684\uff0c\u800c\u4e14\u66fe\u8ff7\u604b\u8fc7\u4eba\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u90a3\u5c31\u7b80\u5355\u4e86\uff0c\u8c82\u8749\u66fe\u8ff7\u604b\u8fc7\u8463\u5353\u3001\u5415\u5e03\uff0c\u5f20\u8083\u7684\u5be1\u5987\u90d1\u6c0f\u8ff7\u604b\u8fc7\u66f9\u64cd\uff0c\u8ff7 \u604b\u8fc7\u66f9\u4e15\u3001\u66f9\u690d\uff0c\u5927\u4e54\u4e8c\u4e54\u66fe\u8ff7\u604b\u8fc7\u66f9\u64cd\uff0c\u5b59\u5c1a\u9999\u66fe\u8ff7\u604b\u8fc7\u5218\u5907\u3002\u4e0d\u8fc7\u5176\u4e2d\u66f9\u64cd\u8ff7\u604b\u5927\u4e54\u4e8c \u4e54\uff0c\u53ea\u4e0d\u8fc7\u89c1\u4e8e\u94dc\u96c0\u53f0\u8d4b\uff0c\u4e8b\u5b9e\u4e0a\u4ed6\u4eec\u6839\u672c\u8fde\u9762\u90fd\u6ca1\u89c1\u8fc7\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u5218\u5907\u624b\u4e0b\u4e09\u5927\u519b\u5e08\uff0c\u4ed6\u4eec\u90fd\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4f9d\u5148\u540e\u987a\u5e8f\uff0c\u662f\u5f90\u5eb6\u3001\u8bf8\u845b\u4eae\u548c\u5e9e\u7edf\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u9053\uff1a\u201c\u4eba\u6240\u5171\u77e5\uff0c\u8bf8\u845b\u4eae\u662f\u5927\u519b\u4e8b\u5bb6\u3001\u5927\u653f\u6cbb\u5bb6\uff0c\u8fd8\u6709\u5176\u4ed6\u7684\u5bb6\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u6709\uff0c\u8fd8\u662f\u5927\u6587\u5b66\u5bb6\uff0c\u4ed6\u7684\u524d\u540e\u51fa\u5e08\u8868\u4f20\u8bf5\u5343\u53e4\uff0c\u4e5f\u662f\u5927\u53d1\u660e\u5bb6\uff0c\u4ed6\u53d1\u660e\u8fc7 \u6728\u725b\u6d41\u9a6c\uff0c\u66f4\u662f\u5927\u5929\u6587\u5bb6\uff0c\u4ed6\u719f\u77e5\u661f\u8c61\uff0c\u501f\u8fc7\u4e1c\u98ce\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u6709\u4f4d\u5927\u4e66\u6cd5\u5bb6\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\u201c\u949f\u5b59\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u6709\u4f4d\u624d\u5973\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u8521\u6587\u59ec\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u6709\u4f4d\u5927\u533b\u5b66\u5bb6\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u534e\u9640\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u6709\u4f4d\u8eab\u6750\u6700\u77ee\u5c0f\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u5f20\u677e\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u7684\u96c4\u8fa9\u5bb6\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u524d\u6709\u8bf8\u845b\u4eae\uff0c\u540e\u6709\u79e6\u5b93\u3002\u524d\u8005\u820c\u6218\u7fa4\u5112\uff0c\u540e\u8005\u96be\u5012\u5f20\u6e29\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u8bf8\u845b\u4eae\u820c\u6218\u7fa4\u5112\u65f6\uff0c\u7fa4\u5112\u4e2d\u6709\u4f4d\u662f\u5c5e\u4e8e\u4e8c\u5341\u56db\u6559\u4e4b\u4e00\u7684\uff0c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u9646\u7eed\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u4e2d\u706b\u653b\u6700\u6210\u529f\u7684\uff0c\u6709\u90a3\u51e0\u6b21\u6218\u5f79\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u6700\u6709\u540d\u7684\u662f\u8d64\u58c1\u4e4b\u6218\uff0c\u5176\u6b21\u662f\u8bf8\u845b\u4eae\u706b\u70e7\u85e4\u7532\u519b\uff0c\u4ee5\u53ca\u706b\u70e7\u846b\u82a6\u5cea\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u8bf8\u845b\u4eae\u7684\u7ee7\u627f\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u653f\u6cbb\u662f\u848b\u742c\uff0c\u519b\u4e8b\u662f\u59dc\u7ef4\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u4e2d\u6c34\u6218\u6700\u6210\u529f\u7684\u662f\u90a3\u4e00\u5f79\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u5173\u4e91\u957f\u6c34\u6df9\u4e03\u519b\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u4e2d\u4e24\u5927\u5978\u81e3\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u524d\u6709\u8463\u5353\uff0c\u540e\u6709\u66f9\u64cd\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u53e6\u6709\u4e00\u5978\u81e3\uff0c\u6bd4\u8463\u5353\u3001\u66f9\u64cd\u66f4\u4ee4\u4eba\u75db\u6068\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u534e\u6b46\u3002\u4e0d\u8fc7\u665a\u8f88\u6709\u4e00\u89e3\u91ca\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u6709\u4ec0\u4e48\u89e3\u91ca\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u8fd9\u6b63\u662f\u4e09\u56fd\u6f14\u4e49\u4e0e\u4e09\u56fd\u5fd7\u4e0d\u540c\u4e4b\u5904\uff0c\u636e\u4e09\u56fd\u5fd7\u6240\u8ff0\uff0c\u534e\u97f5\u53cd\u800c\u662f\u4e2a\u597d\u5f97\u4e0d \u80fd\u518d\u597d\u7684\u4eba\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u90a3\u662f\u9648\u5bff\u7684\u6b3a\u4eba\u4e4b\u8c08\uff0c\u4f55\u51b5\u8001\u673d\u8003\u7684\u662f\u4e09\u56fd\u6f14\u4e49\uff0c\u4e0d\u662f\u4e09\u56fd\u5fd7\u3002\u201d \u3000\u3000\u8fd9\u662f\uff0c\u4ed6\u5bf9\u5cb3\u5c0f\u98de\uff0c\u65e9\u5df2\u5927\u52a0\u8d4f\u8bc6\u3002 \u3000\u3000\u81f3\u4e8e\u5750\u5728\u4e00\u65c1\u7684\u4e25\u5bd2\u3001\u4e25\u592b\u4eba\u4ee5\u53ca\u5927\u5c0f\u59d0\uff0c\u4e5f\u90fd\u6697\u6697\u79f0\u7fa1\uff0c\u5c24\u5176\u4e25\u5bd2\uff0c\u66f4\u662f\u6fc0\u8d4f\u4e0d\u5df2\uff0c \u7b80\u76f4\u8981\u628a\u5cb3\u5c0f\u98de\u89c6\u4e3a\u795e\u7ae5\u3002 \u3000\u3000\u4f46\u8eab\u4e3a\u4e3b\u8003\u7684\u6587\u4ea6\u5947\uff0c\u5374\u603b\u5e0c\u671b\u80fd\u628a\u5cb3\u5c0f\u98de\u8003\u5012\uff0c\u5426\u5219\u81ea\u5df1\u4f3c\u4e4e\u6ca1\u6709\u9762\u5b50\u3002 \u3000\u3000\u4ed6\u6c89\u541f\u4e86\u534a\u54cd\u9053\uff1a\u201c\u4e09\u56fd\u6709\u4e09\u96c4\u3001\u9b4f\u3001\u8700\u3001\u5434\u5404\u6709\u4e00\u96c4\uff0c\u4ed6\u4eec\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4f3c\u662f\u795e\u8272\u6709\u4e9b\u4e0d\u60a6\uff0c\u5f88\u4e45\u4e4b\u540e\u624d\u9053\uff1a\u201c\u4e09\u56fd\u5c82\u53ea\u4e09\u96c4\uff0c\u5373\u4fbf\u53ea\u6709\u4e09\u96c4\uff0c\u4e5f\u4e0d\u4e00\u5b9a \u8700\u3001\u9b4f\u3001\u5434\u5404\u6709\u4e00\u4e2a\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u89c9\u51fa\u5cb3\u5c0f\u98de\u4f3c\u4e4e\u5df2\u5feb\u88ab\u96be\u5012\uff0c\u4e0d\u7531\u5475\u5475\u7b11\u9053\uff1a\u201c\u83ab\u975e\u5c0f\u5144\u5f1f\u56de\u7b54\u4e0d\u4e0a\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u7136\u9053\uff1a\u201c\u6587\u5148\u751f\u53ef\u8ba4\u4e3a\u66f9\u64cd\u662f\u5978\u96c4\uff1f\u5218\u5907\u662f\u67ad\u96c4\uff1f\u5b59\u6743\u662f\u82f1\u96c4\uff1f\u201d \u3000\u3000\u6587\u4ea6\u5947\u9053\uff1a\u201c\u672c\u6765\u5c31\u662f\u5982\u6b64\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u7b11\u9053\uff1a\u201c\u6587\u5148\u751f\u4e0d\u8fc7\u662f\u4eba\u4e91\u4ea6\u4e91\uff0c\u4f46\u665a\u8f88\u5374\u4e0d\u4ee5\u4e3a\u7136\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u7728\u52a8\u7740\u4e00\u5bf9\u8001\u9f20\u773c\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u4e0d\u59a8\u8bf4\u51fa\u4f60\u7684\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u66f9\u64cd\u662f\u5978\u96c4\uff0c\u665a\u8f88\u5b8c\u5168\u540c\u610f\uff0c\u4f46\u5218\u5907\u662f\u67ad\u96c4\u5c31\u5f62\u5bb9\u8fc7\u5206\u3002\u201d \u3000\u3000\u201c\u54ea\u91cc\u8fc7\u5206\uff1f\u201d \u3000\u3000\u201c\u5218\u5907\u4ee5\u7ef4\u62a4\u6c49\u7edf\u4e3a\u5fd7\u4e1a\uff0c\u4e3a\u4eba\u5bbd\u5b8f\u5927\u5ea6\uff0c\u884c\u4ec1\u4e49\u4e8e\u5929\u4e0b\uff0c\u5728\u8d64\u58c1\u4e4b\u6218\u524d\u643a\u6c11\u6e21\u6c5f\uff0c\u662f \u4f55\u7b49\u4ec1\u6c11\u7231\u7269\u80f8\u895f\uff0c\u600e\u80fd\u8bf4\u4ed6\u662f\u67ad\u96c4\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u5c0f\u5144\u5f1f\u5fc3\u76ee\u4e2d\u7684\u82f1\u96c4\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u4e09\u56fd\u82f1\u96c4\u4e0d\u53ef\u80dc\u6570\uff0c\u4f46\u6700\u5177\u4ee3\u8868\u6027\u7684\u4eba\u7269\uff0c\u8be5\u662f\u5173\u516c\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u975e\u8981\u5728\u4e1c\u5434\u6311\u51fa\u4e00\u4f4d\u82f1\u96c4\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u5e94\u8be5\u662f\u5b59\u7b56\uff0c\u800c\u51b3\u975e\u662f\u5b59\u6743\u3002\u201d \u3000\u3000\u201c\u5c0f\u5144\u5f1f\u8ba4\u4e3a\u5b59\u6743\u4e0d\u914d\u79f0\u82f1\u96c4\u7684\u7406\u7531\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u201c\u5b59\u6743\u6258\u7236\u7684\u4f59\u836b\u800c\u6709\u4e1c\u5434\u4e4b\u5730\uff0c\u5e76\u975e\u672c\u8eab\u521b\u4e1a\uff0c\u5e76\u4e14\u4ed6\u505a\u4e86\u4e00\u4ef6\u6700\u5927\u7684\u6076\u4e8b\uff0c\u4fbf\u662f\u6740 \u5bb3\u5173\u516c\u3002\u201d \u3000\u3000\u201c\u8fd8\u6709\u522b\u7684\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6709\uff0c\u90a3\u4fbf\u662f\u4ed6\u7684\u5351\u9119\u65e0\u803b\uff0c\u5f53\u5218\u5907\u4f10\u5434\u65f6\uff0c\u4ed6\u7adf\u5411\u66f9\u4e15\u4fef\u9996\u79f0\u81e3\uff0c\u518d\u52a0\u4e0a\u4ed6\u751f\u6027 \u591a\u7591\uff0c\u5fcd\u5fc3\u6740\u622e\uff0c\u5c24\u5176\u5230\u4e86\u665a\u5e74\uff0c\u542c\u4fe1\u8c17\u8a00\uff0c\u5e9f\u5b50\u7acb\u5eb6\uff0c\u4ee5\u81f4\u4ed6\u7684\u51e0\u4e2a\u513f\u5b50\u4eec\u5f7c\u6b64\u503e\u8f67\uff0c\u9aa8 \u8089\u76f8\u6b8b\uff0c\u5929\u4e0b\u53ef\u6709\u8fd9\u6837\u7684\u82f1\u96c4\uff1f\u201d \u3000\u3000\u5728\u2014\u65c1\u9759\u542c\u7684\u4e25\u5bd2\uff0c\u8fd9\u65f6\u4e5f\u62cd\u4e86\u4e00\u4e0b\u5927\u817f\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\u8fd9\u8bdd\u7684\u786e\u9ad8\u8bba\uff0c\u8001\u592b\u6253\u5fc3\u91cc\u540c\u610f \u4ed6\u7684\u89c1\u89e3\uff01\u201d \u3000\u3000\u6587\u4ea6\u5947\u5c34\u5c2c\u7b11\u9053\uff1a\u201c\u65e2\u7136\u4e1c\u7fc1\u4e5f\u8ba4\u4e3a\u5cb3\u5c0f\u5144\u5f1f\u8bf4\u5f97\u5bf9\uff0c\u90a3\u5c31\u662f\u82f1\u96c4\u6240\u89c1\u7565\u540c\u4e86\uff01\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8fd9\u6837\u8bf4\uff0c\u5fc3\u91cc\u5374\u662f\u4e0d\u670d\u6c14\uff0c\u603b\u8ba4\u4e3a\u95ee\u4e0d\u5012\u4e00\u4e2a\u4e73\u81ed\u672a\u5e72\u7684\u5b69\u5b50\uff0c\u662f\u81ea\u5df1\u7684\u803b\u8fb1\uff0c \u56e0\u4e4b\uff0c\u71c3\u8d77\u4e00\u888b\u70df\u6765\u518d\u9053\uff1a\u201c\u521a\u624d\u53ea\u7b97\u8003\u4e86\u4e00\u534a\uff0c\u4e0b\u9762\u8fd8\u8981\u518d\u8003\u3002\u201d \u3000\u3000\u201c\u6587\u5148\u751f\u7528\u4e0d\u7740\u5ba2\u6c14\uff01\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u6709\u4e00\u4e2a\u88ab\u9a82\u6b7b\u7684\uff0c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u738b\u6717\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u6709\u4e00\u4e2a\u4e0d\u5fe0\u4e8e\u4e3b\u51fa\u5356\u56fd\u5bb6\u673a\u5bc6\u7684\u4eba\uff0c\u7167\u8bf4\u8be5\u662f\u5978\u81e3\uff0c\u4f46\u7edd\u5927\u591a\u6570\u770b\u4e09\u56fd\u7684 \u8bfb\u8005\u5374\u90fd\u8ba4\u4e3a\u4ed6\u662f\u597d\u4eba\uff0c\u4ed6\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u5f20\u677e\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u6253\u4e2a\u81ea\u5df1\u5403\u81ea\u5df1\u773c\u775b\u7684\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u590f\u5019\u60c7\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u6709\u4f4d\u6293\u8d77\u5175\u58eb\u4e24\u6761\u817f\u6495\u5f00\u6765\u505a\u5175\u5668\u7684\u5927\u5c06\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u5178\u97e6\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u6709\u4f4d\u6740\u59bb\u5f85\u5ba2\u7684\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u5218\u5b89\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u6700\u6709\u540d\u7684\u51fa\u5bb6\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u666e\u51c0\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u55d3\u95e8\u6700\u5927\u7684\u5927\u662f\u8c01\uff1f\u6709\u4f55\u4e3a\u8bc1\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u5f20\u98de\uff0c\u4ed6\u559d\u65ad\u4e86\u5f53\u9633\u6865\uff0c\u8fde\u6c34\u90fd\u5012\u6d41\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u7b2c\u4e00\u52c7\u5c06\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u5415\u5e03\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u8a00\u8fc7\u5176\u5b9e\u4e0d\u53ef\u5927\u7528\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u9a6c\u8c21\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u91cc\u6709\u53cd\u9aa8\u7684\u5927\u5c06\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u9b4f\u5ef6\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u7bad\u6cd5\u6700\u597d\u7684\u5927\u5c06\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u5415\u5e03\u548c\u9ec4\u5fe0\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u6709\u540d\u65e0\u59d3\u7684\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u8c82\u8749\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u6709\u59d3\u65e0\u540d\u7684\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u4e54\u56fd\u8001\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u4e0e\u4eba\u4ea4\u950b\u6700\u5e72\u8106\u4ece\u4e0d\u62d6\u6ce5\u5e26\u6c34\u7684\u5927\u5c06\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u5173\u516c\uff0c\u4ed6\u8001\u4eba\u5bb6\u591a\u534a\u662f\u4ea4\u624b\u4e0d\u5230\u51e0\u56de\u5408\u4fbf\u628a\u5bf9\u65b9\u65a9\u4e8e\u9a6c\u4e0b\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u4e2d\u6b7b\u540e\u663e\u5723\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u5173\u516c\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u4e2d\u88ab\u5218\u5907\u8a89\u4e3a\u4e00\u8eab\u662f\u80c6\u7684\u5927\u5c06\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u8d75\u4e91\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u4e09\u56fd\u4e2d\u6700\u82f1\u4fca\u5a01\u6b66\u7684\u5927\u5c06\u662f\u8c01\uff1f\u4e66\u4e2d\u5bf9\u4ed6\u4eec\u6709\u4ec0\u4e48\u5f62\u5bb9\u8bcd\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u524d\u6709\u5415\u5e03\uff0c\u6709\u6240\u8c13\u4eba\u4e2d\u5415\u5e03\uff0c\u9a6c\u4e2d\u8d64\u5154\u3002\u540e\u6709\u9a6c\u8d85\uff0c\u4eba\u79f0\u9526\u9a6c\u8d85\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u6709\u4f4d\u7741\u773c\u7761\u89c9\u7684\u5927\u5c06\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u5f20\u98de\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u66f9\u64cd\u8d25\u5f97\u6700\u60e8\u7684\u6709\u54ea\u51e0\u6b21\u6218\u5f79\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1b\u201c\u6218\u6fee\u9633\u8d25\u4e8e\u5415\u5e03\uff0c\u516b\u5341\u4e09\u4e07\u4eba\u9a6c\uff0c\u51e0\u4e4e\u6298\u635f\u6b86\u5c3d\u3002\u8d25\u8d70\u534e\u5bb9\u9053\uff0c\u66f4\u662f\u4e22\u4eba \u73b0\u773c\uff0c\u72fc\u72c8\u4e0d\u582a\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u5218\u5907\u5728\u672a\u5f97\u897f\u5ddd\u524d\uff0c\u66fe\u4f9d\u9644\u8fc7\u54ea\u4e9b\u4eba\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u6700\u5148\u4f9d\u9644\u516c\u5b59\u8d5e\uff0c\u4ee5\u540e\u53c8\u4f9d\u9644\u8fc7\u66f9\u64cd\u3001\u5415\u5e03\u3001\u8881\u7ecd\u3001\u5218\u8868\u3001\u5218\u748b\u7b49\u4eba\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\uff1a\u201c\u5728\u8d64\u58c1\u4e4b\u6218\u524d\uff0c\u8bf8\u4faf\u4e2d\u5175\u529b\u6700\u96c4\u539a\uff0c\u5b9e\u529b\u6700\u5f3a\u4eba\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b54\uff1a\u201c\u6cb3\u5317\u8881\u7ecd\u3002\u201d \u3000\u3000\u6587\u4ea6\u5947\u95ee\u5230\u8fd9\u91cc\uff0c\u5df2\u77e5\u65e0\u6cd5\u96be\u5012\u5bf9\u65b9\uff0c\u7075\u673a\u4e00\u52a8\uff0c\u4e0d\u7531\u60f3\u51fa\u4e86\u6b6a\u70b9\u5b50\uff0c\u4ed6\u5438\u4e86\u51e0\u53e3\u70df\uff0c \u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u5218\u5907\u3001\u5173\u516c\u3001\u5f20\u98de\u4e09\u4eba\u4e2d\uff0c\u8c01\u90fd\u77e5\u9053\u5218\u5907\u662f\u8001\u5927\uff0c\u5173\u516c\u662f\u8001\u4e8c\u3001\u5f20\u98de\u662f\u8001\u4e09\uff0c \u8fd9\u6b21\u5e8f\u662f\u4ee5\u4ec0\u4e48\u505a\u6839\u636e\u6392\u5217\u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u8fd9\u95ee\u9898\u95ee\u5f97\u5cb3\u5c0f\u98de\u5927\u611f\u4e00\u6123\uff0c\u8bb6\u7136\u95ee\u9053\uff1b\u201c\u5f53\u7136\u662f\u4ee5\u5e74\u9f84\u7684\u957f\u5e7c\u6392\u5217\u51fa\u6765\u7684\uff0c\u6587\u5148\u751f\u8fd9 \u95ee\u9898\u95ee\u5f97\u5b9e\u5728\u4ee4\u4eba\u8d39\u89e3\uff1f\u201d \u3000\u3000\u5c82\u77e5\u6587\u4ea6\u5947\u5374\u6447\u5934\u7728\u773c\u4e00\u7b11\u9053\uff1b\u201c\u4e0d\u5bf9\uff0c\u4ed6\u4eec\u4e0d\u662f\u4ee5\u5e74\u9f84\u5927\u5c0f\u6392\u5217\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8d8a\u611f\u5403\u60ca\uff1a\u201c\u6587\u5148\u751f\u8ba4\u4e3a\u662f\u4ee5\u4ec0\u4e48\u505a\u6839\u636e\u6392\u5217\u7684\u5462\uff1f\u201d \u3000\u3000\u6587\u4ea6\u5947\u637b\u7740\u5c71\u7f8a\u80e1\u5b50\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4ee5\u722c\u6811\u6392\u5217\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u554a\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6587\u5148\u751f\u4e0d\u59a8\u628a\u8fd9\u6bb5\u7ecf\u8fc7\u8bf4\u8bf4\u770b\uff01\u201d \u3000\u3000\u6587\u4ea6\u5947\u9053\uff1a\u201c\u4ed6\u4eec\u4e09\u4f4d\u5728\u6843\u56ed\u7ed3\u4e49\u524d\uff0c\u8c01\u90fd\u4e0d\u80af\u8bf4\u51fa\u5b9e\u9645\u5e74\u9f84\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\u201c\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6587\u4ea6\u5947\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4eec\u90fd\u60f3\u505a\u5927\u54e5\u3002\u201d \u3000\u3000\u201c\u722c\u6811\u53c8\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8fd9\u529e\u6cd5\u662f\u5f20\u98de\u63d0\u8bae\u7684\uff0c\u56e0\u4e3a\u5f20\u98de\u52a8\u4f5c\u5feb\uff0c\u8f7b\u529f\u53c8\u597d\uff0c\u6240\u4ee5\u63d0\u8bae\u4e09\u4eba\u6bd4\u8d5b\u722c\u6811\uff0c\u722c\u5f97\u6700 \u5feb\u7684\u662f\u8001\u5927\uff0c\u6700\u6162\u7684\u4fbf\u662f\u8001\u4e09\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u662f\u8fd9\u6837\uff0c\u5218\u5907\u4e00\u5b9a\u4e0d\u540c\u610f\u3002\u201d \u3000\u3000\u201c\u504f\u504f\u5218\u5907\u4e0d\u4f46\u540c\u610f\uff0c\u800c\u4e14\u662f\u6b23\u7136\u540c\u610f\u3002\u201d \u3000\u3000\u201c\u4ee5\u540e\u5462\uff1f\u201d \u3000\u3000\u201c\u4e8e\u662f\u4e09\u4eba\u4e00\u540c\u6bd4\u8d5b\u722c\u6811\u3002\u201d \u3000\u3000\u201c\u7ed3\u679c\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u7ed3\u679c\u4e0d\u96be\u60f3\u89c1\uff0c\u5f20\u98de\u722c\u5f97\u6700\u9ad8\uff0c\u5173\u516c\u7b2c\u4e8c\uff0c\u5218\u5907\u5728\u6811\u4e0b\u6839\u672c\u6ca1\u722c\u4e0a\u53bb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u90a3\u4e0d\u53d8\u6210\u5f20\u98de\u662f\u5927\u54e5\u4e86\uff1f\u201d \u3000\u3000\u6587\u4ea6\u5947\u9053\uff1a\u201c\u5f20\u98de\u5f53\u7136\u4ee5\u4e3a\u81ea\u5df1\u662f\u5927\u54e5\uff0c\u4f46\u5218\u5907\u5374\u8bf4\u4ed6\u8be5\u662f\u5927\u54e5\u3002\u201d \u3000\u3000\u201c\u8fd9\u53c8\u662f\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u201c\u5218\u5907\u8bf4\u6811\u662f\u7531\u6839\u957f\u8d77\uff0c\u4ed6\u5728\u88c9\u4e0a\uff0c\u5f53\u7136\u662f\u5927\u54e5\uff0c\u5173\u516c\u722c\u5230\u6811\u5e72\uff0c\u5e94\u5f53\u662f\u8001\u4e8c\uff0c\u5f20\u98de\u5728 \u6811\u68a2\u4e0a\uff0c\u5fc5\u5b9a\u662f\u8001\u4e09\u3002\u201d \u3000\u3000\u201c\u5f20\u98de\u80af\u5417\uff1f\u201d \u3000\u3000\u201c\u7531\u4e8e\u5173\u516c\u540c\u610f\u5218\u5907\u7684\u8bf4\u6cd5\uff0c\u5f20\u98de\u4e5f\u53ea\u6709\u5c11\u6570\u670d\u4ece\u591a\u6570\u4e86\u3002\u201d \u3000\u3000\u53ea\u542c\u4e25\u5bd2\u9053\uff1a\u201c\u6587\u5148\u751f\uff0c\u4f60\u662f\u4ece\u54ea\u91cc\u5b66\u6765\u7684\u8fd9\u4e00\u5957\uff1f\u201d \u3000\u3000\u6587\u4ea6\u5947\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6587\u67d0\u4e0d\u8fc7\u8ddf\u5cb3\u5c0f\u5144\u5f1f\u5f00\u4e2a\u73a9\u7b11\uff0c\u8bf4\u8bf4\u73a9\u513f\u7f62\u4e86\u3002\u201d \u3000\u3000\u63a5\u7740\u6b63\u8272\u7ad6\u8d77\u5927\u62c7\u6307\u9053\uff1a\u201c\u5cb3\u5c0f\u5144\u5f1f\u679c\u7136\u9ad8\u624d\uff0c\u6587\u67d0\u5b9e\u5728\u4f69\u670d\u3002 \u3000\u3000\u9057\u61be\u7684\u662f\u4ed6\u6ca1\u53bb\u8003\u72b6\u5143\uff0c\u8be5\u662f\u7687\u5bb6\u7684\u4e00\u5927\u635f\u5931\uff01\u201d \u3000\u3000\u4e25\u5bd2\u5475\u5475\u7b11\u9053\uff1a\u201c\u7687\u5bb6\u7684\u635f\u5931\u7b97\u4ec0\u4e48\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u662f\u54b1\u4eec\u7687\u5143\u6559\u7684\u4e00\u5927\u6536\u83b7\uff0c\u4eca\u665a\u54b1\u4eec \u5c31\u5927\u5927\u7684\u5e86\u8d3a\u4e00\u756a\uff01\u201d \u3000\u3000\u5230\u4e86\u508d\u665a\uff0c\u679c\u7136\u53c8\u662f\u4e00\u987f\u76db\u7b75\uff0c\u8fd9\u6b21\u9664\u4e86\u4e25\u5bd2\u592b\u5987\u548c\u5927\u5c0f\u59d0\u5916\uff0c\u66f4\u52a0\u4e0a\u4e86\u6587\u4ea6\u5947\u505a\u966a\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u76f4\u60f3\u63d0\u6bcd\u4eb2\u5728\u7075\u5802\u5b88\u7075\u4e4b\u4e8b\uff0c\u4f46\u5f53\u7740\u8fd9\u4e48\u591a\u4eba\uff0c\u53c8\u5b9e\u5728\u627e\u4e0d\u5230\u5f00\u53e3\u7684\u673a\u4f1a\u3002 \u3000\u3000\u9152\u7b75\u6563\u540e\uff0c\u5929\u8fd8\u5e76\u672a\u5168\u9ed1\uff0c\u4e25\u5bd2\u8d77\u8eab\u9053\uff1a\u201c\u5cb3\u5c0f\u5144\u5f1f\u5c31\u8bf7\u5728\u5927\u5385\u5750\u4e00\u5750\uff0c\u8001\u592b\u6682\u65f6\u5931\u966a \u4e86\u3002\u201d \u3000\u3000\u4e25\u5bd2\u8d70\u540e\uff0c\u4e25\u592b\u4eba\u3001\u5927\u5c0f\u59d0\u548c\u6587\u4ea6\u5947\u4e5f\u90fd\u76f8\u7ee7\u79bb\u53bb\u3002 \u3000\u3000\u5927\u5385\u5185\u53ea\u5269\u4e0b\u5cb3\u5c0f\u98de\u4e00\u4eba\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u96be\u514d\u6709\u4e9b\u832b\u7136\u4e0d\u89e3\uff0c\u4e3a\u4ec0\u4e48\u6240\u6709\u7684\u4eba\u90fd\u8d70\u5149\u4e86\uff0c\u53ea\u7559\u4e0b\u4ed6\u72ec\u81ea\u4e00\u4e2a\u5462\uff1f\u65e2\u7136\u4e25\u5bd2 \u5bf9\u81ea\u5df1\u9887\u4e3a\u8d4f\u8bc6\uff0c\u5c31\u4e0d\u8be5\u4ee5\u5982\u6b64\u6001\u5ea6\u76f8\u5f85\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u610f\u5ff5\u5c1a\u672a\u8f6c\u5b8c\u4e4b\u9645\uff0c\u5927\u5385\u5916\u5df2\u54cd\u8d77\u811a\u6b65\u58f0\uff0c\u63a5\u7740\uff0c\u4e00\u4e2a\u5e74\u5728\u56db\u5341\u5de6\u53f3\u8eab\u6750\u9ad8\u5927\u9b41 \u68a7\uff0c\u770b\u6765\u9887\u4e3a\u5a01\u731b\u7684\u4e2d\u5e74\u4eba\u8fc8\u6b65\u800c\u5165\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u4eba\u4e00\u8fdb\u95e8\u5c31\u671b\u7740\u5cb3\u5c0f\u98de\u62b1\u4e86\u62b1\u62f3\u9053\uff1a\u201c\u8fd9\u4f4d\u53ef\u662f\u5cb3\u516c\u5b50\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u79bb\u5ea7\u8fd8\u793c\u9053\uff1a\u201c\u5728\u4e0b\u6b63\u662f\uff0c\u8bf7\u95ee\u5927\u9a7e\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u4eba\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u5728\u4e0b\u5468\u6d77\u5c71\uff0c\u662f\u80b2\u5316\u57ce\u5e9c\u603b\u7ba1\uff0c\u5949\u57ce\u4e3b\u4e4b\u547d\u6765\u966a\u516c\u5b50\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8d8a\u611f\u8be7\u5f02\uff0c\u7eb5\u7136\u57ce\u4e3b\u4e0d\u6765\uff0c\u5c3d\u53ef\u6b62\u4e25\u592b\u4eba\u6216\u8005\u5927\u5c0f\u59d0\u751a\u81f3\u6587\u5148\u751f\u6765\u966a\uff0c\u600e\u4f1a\u6d3e\u603b \u7ba1\u6765\u966a\u5462\uff1f \u3000\u3000\u53ea\u542c\u5468\u6d77\u5c71\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4e3a\u4e86\u8d76\u65f6\u95f4\uff0c\u54b1\u4eec\u73b0\u5728\u5c31\u8d70\u5427\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u5468\u603b\u7ba1\u8981\u5728\u4e0b\u5230\u54ea\u91cc\u53bb\uff1f\u53ef\u662f\u9001\u5728\u4e0b\u56de\u4e94\u5173\u603b\u9547\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u7b11\u9053\uff1a\u201c\u5468\u67d0\u662f\u5949\u57ce\u4e3b\u4e4b\u547d\uff0c\u966a\u516c\u5b50\u5230\u4e00\u8d9f\u5bcc\u56fd\u57ce\u3002\u201d \u3000\u3000\u201c\u5230\u5bcc\u56fd\u57ce\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u516c\u5b50\u4e0d\u5fc5\u591a\u95ee\uff0c\u53bb\u4e86\u5c31\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u6709\u95f7\u5728\u5fc3\u91cc\uff0c\u4e0d\u4fbf\u518d\u95ee\uff0c\u4e3a\u4e86\u6551\u51fa\u6bcd\u4eb2\uff0c\u5373\u4fbf\u4efb\u4eba\u6446\u5e03\uff0c\u4e5f\u5fc5\u987b\u5fcd\u53d7\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u5728\u5468\u6d77\u5c71\u966a\u540c\u4e0b\uff0c\u5f80\u5bcc\u56fd\u57ce\u8d76\u53bb\u3002 \u3000\u3000\u4e24\u57ce\u76f8\u8ddd\u4e0d\u8db3\u5341\u91cc\uff0c\u4e0d\u6d88\u534a\u4e2a\u65f6\u8fb0\u4fbf\u5230\u3002 \u3000\u3000\u7531\u5468\u6d77\u5c71\u7684\u884c\u52a8\u4e2d\uff0c\u5cb3\u5c0f\u98de\u4fbf\u5df2\u770b\u5f97\u51fa\u4ed6\u662f\u65e9\u884c\u51c6\u5907\uff0c\u56e0\u4e3a\u4ed6\u5c45\u7136\u5e26\u7740\u5bcc\u56fd\u57ce\u7684\u7fe0\u7389\u901a \u884c\u4ee4\u724c\uff0c\u5e76\u53d1\u7ed9\u5cb3\u5c0f\u98de\u4e00\u9762\u3002 \u3000\u3000\u8fdb\u5165\u5bcc\u56fd\u57ce\u540e\uff0c\u7531\u524d\u8fdb\u7684\u65b9\u5411\uff0c\u4f7f\u5f97\u5cb3\u5c0f\u98de\u8d8a\u6765\u8d8a\u611f\u5403\u60ca\u3002 \u3000\u3000\u4ed6\u4f9d\u7a00\u8bb0\u5f97\uff0c\u8fd9\u6761\u8def\u5206\u660e\u662f\u901a\u5f80\u7f8e\u4eba\u5173\u7684\u3002 \u3000\u3000\u60c5\u52bf\u903c\u5f97\u4ed6\u4e0d\u5f97\u4e0d\u5f00\u53e3\uff0c\u4ed6\u8f7b\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u5468\u603b\u7ba1\uff0c\u6ca1\u8d70\u9519\u5427\uff0c\u8fd9\u6761\u8def\u597d\u50cf\u662f\u5f80\u7f8e\u4eba\u5173 \u53bb\u7684\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u539f\u6765\u516c\u5b50\u5230\u8fc7\u7f8e\u4eba\u5173\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8138\u4e0a\u4e00\u70ed\u9053\uff1a\u201c\u5728\u4e0b\u6628\u5929\u624d\u968f\u5927\u516c\u4e3b\u5230\u8fc7\u5bcc\u56fd\u57ce\u53c2\u89c2\uff0c\u7f8e\u4eba\u5173\u867d\u7136\u5230\u8fc7\uff0c\u4f46\u5374\u4e0d \u66fe\u8fdb\u5230\u91cc\u9762\u53bb\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u7b11\u9053\uff1b\u201c\u90a3\u6b63\u597d\uff0c\u73b0\u5728\u6b63\u662f\u7f8e\u4eba\u5173\u91cc\u6700\u70ed\u95f9\u7684\u65f6\u523b\uff0c\u8fdb\u53bb\u770b\u770b\uff0c\u7b49\u4e8e\u5f00\u6b21\u773c\u754c\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u9887\u4f24\u5cb3\u5c0f\u98de\u7684\u81ea\u5c0a\u5fc3\uff0c\u4ed6\u8138\u8272\u5fae\u53d8\uff0c\u7ad9\u4f4f\u811a\u9053\uff1a\u201c\u5468\u603b\u7ba1\uff0c\u5f88\u62b1\u6b49\uff0c\u90a3\u5730\u65b9\u6211\u4e0d \u60f3\u53bb\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u4e00\u6123\u9053\uff1a\u201c\u516c\u5b50\u4e3a\u4ec0\u4e48\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u53bb\uff1f\u7f8e\u4eba\u5173\u91cc\u4e0d\u7ba1\u591a\u4e48\u597d\u770b\uff0c\u603b\u662f\u4e0d\u6b63\u5e38\u7684\u573a\u6240\uff0c\u5728\u4e0b\u6ca1\u6709\u5fc5 \u8981\u5f00\u90a3\u79cd\u773c\u754c\uff01\u201d \u3000\u3000\u201c\u516c\u5b50\uff0c\u8fd9\u662f\u57ce\u4e3b\u5429\u5490\u5468\u67d0\u5e26\u60a8\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u5e0c\u671b\u4f60\u80fd\u8bf4\u51fa\u57ce\u4e3b\u8981\u4f60\u5e26\u6211\u6765\u7684\u7406\u7531\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u5468\u6d77\u5c71\u5e26\u70b9\u541e\u541e\u5410\u5410\uff0c\u8bb8\u4e45\u6ca1\u63a5\u4e0a\u4e0b\u6587\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u7136\u9053\uff1a\u201c\u5728\u4e0b\u4ece\u6ca1\u60f3\u5230\u5e72\u5bfb\u82b1\u95ee\u67f3\u7684\u4e8b\uff0c\u4f55\u51b5\u4ee5\u5728\u4e0b\u8fd9\u79cd\u5e74\u7eaa\u5230\u90a3\u79cd\u5730\u65b9\u53bb\uff0c \u8d8a\u53d1\u5bb9\u6613\u5f15\u8d77\u4eba\u8a00\u7269\u8bae\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u966a\u7740\u7b11\u9053\uff1a\u201c\u516c\u5b50\u522b\u607c\uff0c\u4e0d\u7ba1\u5982\u4f55\uff0c\u60a8\u8fd8\u662f\u8d70\u8d9f\uff0c\u4e0d\u7136\u5468\u67d0\u56de\u53bb\u65e0\u6cd5\u5411\u57ce\u4e3b\u4ea4 \u5dee\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u5426\u5e26\u6211\u6765\u8fc7\uff0c\u57ce\u4e3b\u53c8\u600e\u4e48\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u201c\u7f8e\u4eba\u5173\u7684\u8001\u9e28\u548c\u8336\u58f6\u4eec\uff0c\u90a3\u4e00\u4e2a\u4e0d\u8ba4\u8bc6\u5468\u67d0\uff0c\u57ce\u4e3b\u53ea\u8981\u6d3e\u4eba\u4e00\u95ee\u5c31\u77e5\u9053\u5468\u67d0\u6ca1\u6765\u8fc7 \u4e86\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5c31\u7531\u6211\u5728\u8fd9\u91cc\u7b49\u7740\uff0c\u5468\u603b\u7ba1\u4e00\u4eba\u8fdb\u53bb\u5c31\u6210\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u6210\uff0c\u57ce\u4e3b\u82e5\u6d3e\u4eba\u95ee\uff0c\u5fc5\u5b9a\u4f1a\u95ee\u5468\u67d0\u662f\u5426\u966a\u7740\u4e00\u4f4d\u5e74\u8f7b\u516c\u5b50\u6765\u8fc7\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5468\u603b\u7ba1\u603b\u8be5\u8bf4\u51fa\u57ce\u4e3b\u8981\u4f60\u5e26\u6211\u6765\u7684\u7406\u7531\uff01\u201d \u3000\u3000\u201c\u5468\u67d0\u73b0\u5728\u4e0d\u4fbf\u8bb2\uff0c\u5f88\u5feb\u4f60\u5c31\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u65e0\u5948\uff0c\u53ea\u597d\u968f\u7740\u5468\u6d77\u5c71\u8d70\u3002 \u3000\u3000\u5468\u6d77\u5c71\u8fb9\u8d70\u8fb9\u9053\uff1a\u201c\u516c\u5b50\u662f\u4ece\u4e0a\u5f80\u4e0b\u770b\uff1f\u8fd8\u662f\u4ece\u4e0b\u5f80\u4e0a\u770b\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fd9\u8bdd\u600e\u4e48\u8bb2\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u7a91\u59d0\u4eec\u5206\u4e0a\u3001\u4e2d\u3001\u4e0b\u4e09\u7b49\uff0c\u4e5f\u53eb\u7532\u3001\u4e59\u3001\u4e19\u4e09\u7b49\uff0c\u5206\u4f4f\u5728\u4e09\u5904\u5927\u9662\uff0c\u4ece\u4e0a \u5f80\u4e0b\u770b\uff0c\u5c31\u662f\u5148\u770b\u7532\u7b49\uff0c\u518d\u770b\u4e59\u7b49\uff0c\u6700\u540e\u770b\u4e19\u7b49\u3002\u7531\u4e0b\u5f80\u4e0a\u770b\u662f\u6309\u76f8\u53cd\u6b21\u5e8f\u89c2\u770b\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u65e2\u7136\u8eb2\u4e0d\u8fc7\uff0c\u53ea\u597d\u968f\u53e3\u9053\uff1a\u201c\u5c31\u7531\u4f60\u51b3\u5b9a\u597d\u4e86\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u5e26\u7740\u795e\u79d8\u4e00\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u5982\u679c\u53ea\u60f3\u770b\u770b\uff0c\u4e19\u7b49\u7684\u53cd\u800c\u6700\u597d\u770b\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u540c\u4e3a\u4e19\u7b49\u7684\u8863\u670d\u7a7f\u5f97\u6700\u5c11\u3002\u201d \u3000\u3000\u201c\u7a7f\u8863\u670d\u9664\u4e86\u4fdd\u6696\u4e4b\u5916\uff0c\u4e5f\u662f\u4e3a\u6c42\u7f8e\u89c2\uff0c\u8863\u670d\u7a7f\u5f97\u5c11\u4e0d\u662f\u66f4\u96be\u770b\u5417\uff1f\u201d \u3000\u3000\u201c\u516c\u5b50\u521a\u597d\u8bb2\u9519\u4e86\uff0c\u5728\u7537\u4eba\u4eec\u7684\u773c\u4e2d\uff0c\u5973\u4eba\u7684\u8863\u670d\u7a7f\u5f97\u8d8a\u5c11\u8d8a\u597d\uff0c\u6700\u597d\u662f\u4e0d\u7a7f\u3002\u201d \u3000\u3000\u201c\u90a3\u4e0e\u79bd\u517d\u4f55\u5f02\uff0c\u8fd9\u79cd\u7537\u4eba\uff0c\u3000\u4e00\u5b9a\u662f\u773c\u775b\u6709\u95ee\u9898\u3002\u201d \u3000\u3000\u201c\u504f\u504f\u73b0\u5728\u7684\u7537\u4eba\uff0c\u5341\u4e2a\u6709\u4e5d\u4e2a\u50cf\u516c\u5b50\u6240\u8bf4\u7684\u773c\u775b\u6709\u95ee\u9898\u3002\u201d \u3000\u3000\u201c\u5468\u603b\u7ba1\u4e0d\u5fc5\u591a\u8bf4\uff0c\u5c31\u7531\u4f60\u968f\u4fbf\u5e26\u5427\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\u5df2\u8fdb\u5165\u4e00\u5904\u5927\u9662\u3002 \u3000\u3000\u597d\u70ed\u95f9\uff0c\u7b80\u76f4\u662f\u4eba\u6324\u4eba\uff0c\u4eba\u78b0\u4eba\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u662f\u6307\u7684\u8fdb\u95e8\u5904\uff0c\u81f3\u4e8e\u8fdb\u53bb\u4ee5\u540e\uff0c\u7531\u4e8e\u5ead\u9662\u5f88\u5927\uff0c\u5bfb\u82b3\u5ba2\u4eec\u81ea\u7136\u5206\u6563\u4e86\u5f88\u591a\u3002 \u3000\u3000\u5468\u6d77\u5c71\u679c\u7136\u662f\u4f4d\u663e\u773c\u7684\u4eba\u7269\uff0c\u3000\u4e00\u8fdb\u5927\u95e8\u4fbf\u6709\u51e0\u4e2a\u9f9f\u5974\u56f4\u62e2\u4e0a\u6765\u6253\u62db\u547c\u3002 \u3000\u3000\u4e00\u4e2a\u7350\u5934\u9f20\u76ee\u7684\u72ec\u773c\u9f9f\u5974\u54a7\u5634\u54c8\u8170\u8d8b\u524d\u53eb\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u98ce\u628a\u5468\u5927\u603b\u7ba1\u5439\u4e86\u6765\uff0c\u5f53\u771f\u7a00\u5ba2\uff0c \u8981\u627e\u90a3\u4f4d\u59d1\u5a18\uff0c\u5c0f\u7684\u7ed9\u60a8\u53eb\u53bb\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u563f\u563f\u7b11\u9053\uff1a\u201c\u8001\u5434\u522b\u5ba2\u6c14\uff0c\u6211\u662f\u966a\u8fd9\u4f4d\u516c\u5b50\u6765\u7684\u3002\u201d \u3000\u3000\u72ec\u773c\u9f9f\u5974\u76ee\u5149\u4e00\u89e6\u5230\u5cb3\u5c0f\u98de\uff0c\u5148\u662f\u6123\u4e86\u4e0b\uff0c\u63a5\u7740\u4e00\u54c8\u8170\uff0c\u9732\u51fa\u4e24\u53ea\u5e26\u7740\u9ec4\u57a2\u7684\u5927\u677f\u7259\u9053\uff1a \u201c\u8fd9\u4f4d\u5c11\u7237\u770b\u597d\u4e86\u90a3\u4f4d\u59d1\u5a18\uff0c\u53ea\u7ba1\u8bb2\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u5728\u4e0b\u53ea\u662f\u968f\u4fbf\u770b\u770b\uff0c\u4e0d\u60f3\u5355\u627e\u90a3\u4e00\u4e2a\u3002\u201d \u3000\u3000\u72ec\u773c\u9f9f\u5974\u77a5\u4e86\u5468\u6d77\u5c71\u4e00\u773c\u9053\uff1a\u201c\u5927\u603b\u7ba1\uff0c\u8fd9\u4f4d\u5c11\u7237\u662f\u90a3\u6765\u7684\u8d35\u5ba2\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u662f\u6211\u4eec\u57ce\u4e3b\u7684\u4eb2\u621a\u3002\u201d \u3000\u3000\u72ec\u773c\u9f9f\u5974\u4f3c\u4e4e\u5413\u4e86\u4e00\u8df3\uff0c\u5634\u54a7\u5f97\u66f4\u5927\uff0c\u5fd9\u9053\uff1a\u201c\u90a3\u8fb9\u4ead\u5b50\u4e0b\u9762\u6709\u51e0\u4f4d\u59d1\u5a18\uff0c\u73a9\u4e0d\u73a9\u6ca1\u5173 \u7cfb\uff0c\u5c0f\u7684\u5e26\u5c11\u7237\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u7a7f\u8fc7\u5047\u5c71\u6c34\u69ad\uff0c\u5728\u4e00\u5904\u4ead\u5b50\u4e0b\uff0c\u679c\u7136\u6709\u4e94\u5927\u540d\u59d1\u5a18\uff0c\u6b63\u5728\u966a\u7740\u51e0\u4e2a\u5bfb\u82b3\u5ba2\u8c08\u5929\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u642d\u773c\u5c31\u89c9\u5f97\u90a3\u60c5\u666f\u5b9e\u5728\u4e0d\u582a\u5165\u76ee\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4e9b\u59d1\u5a18\u4eec\uff0c\u5168\u8eab\u53ea\u6709\u4e00\u4ef6\u809a\u515c\u548c\u4e00\u4ef6\u4eb5\u88e4\uff0c\u5176\u4f59\u90e8\u5206\uff0c\u6574\u4e2a\u5149\u6e9c\u6e9c\u7684\u9732\u5728\u5916\u9762\uff0c \u5c31\u50cf\u8fdb\u4e86\u725b\u8089\u573a\u4e00\u6837\u3002 \u3000\u3000\u5979\u4eec\u7684\u8138\u4e0a\uff0c\u90fd\u62b9\u7740\u539a\u5f97\u4e0d\u80fd\u518d\u539a\u7684\u8102\u7c89\uff0c\u5728\u706f\u5149\u6620\u7167\u4e0b\uff0c\u7b80\u76f4\u50cf\u5996\u602a\uff0c\u6839\u672c\u770b\u4e0d\u51fa\u662f \u7f8e\u662f\u4e11\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u8fd9\u8981\u5168\u770b\u5404\u4eba\u7684\u773c\u5149\uff0c\u6709\u7684\u4eba\u770b\u4e86\u5979\u4eec\u4f1a\u6076\u5fc3\uff0c\u4f46\u4e5f\u6709\u4eba\u628a\u5979\u4eec\u770b\u6210\u7f8e\u82e5\u5929\u4ed9\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u5979\u4eec\u7684\u8eab\u6750\u5374\u90fd\u4e0d\u9519\uff0c\u5927\u817f\u4fee\u957f\uff0c\u53cc\u81c2\u67d4\u5ae9\uff0c\u6643\u52a8\u5728\u706f\u5149\u4e0b\uff0c\u81c2\u6ce2\u4e73\u6d6a\uff0c\u8fd8\u786e\u5b9e \u6709\u90a3\u4e48\u70b9\u513f\u8bf1\u4eba\u60f3\u5165\u975e\u975e\u7684\u5473\u9053\u3002 \u3000\u3000\u72ec\u773c\u9f9f\u5974\u5728\u76f8\u9694\u4e08\u4f59\u5916\u4fbf\u5c71\u58f0\u558a\u9053\uff1a\u201c\u4f60\u4eec\u522b\u95f9\u5566\uff0c\u73b0\u5728\u6765\u4e86\u4e00\u4f4d\u8d35\u5ba2\uff01\u201d \u3000\u3000\u90a3\u4e94\u516d\u4f4d\u59d1\u5a18\u7acb\u5373\u4e0d\u7ea6\u800c\u540c\u5f80\u8fd9\u8fb9\u671b\u6765\u3002 \u3000\u3000\u5979\u4eec\u6709\u7684\u8ba4\u8bc6\u5468\u6d77\u5c71\uff0c\u4f46\u89c6\u7ebf\u5374\u4e0d\u7ea6\u800c\u540c\u96c6\u4e2d\u5728\u5cb3\u5c0f\u98de\u8eab\u4e0a\u3002 \u3000\u3000\u5979\u4eec\u96be\u514d\u5947\u602a\u4e00\u4e2a\u5341\u4e09\u56db\u5c81\u7684\u5b69\u5b50\u4f1a\u5230\u8fd9\u79cd\u5730\u65b9\u6765\uff0c\u4f46\u5374\u53c8\u7981\u4e0d\u4f4f\u60ca\u7fa1\u8fd9\u5c11\u5e74\u4eba\u7269\u7adf\u662f \u5982\u6b64\u7684\u82f1\u59ff\u7115\u53d1\uff0c\u4eea\u8868\u51fa\u4f17\u3002 \u3000\u3000\u53ea\u542c\u72ec\u8ddf\u9f9f\u5974\u518d\u9053\uff1a\u201c\u4eca\u5929\u6765\u627e\u4f60\u4eec\u7684\uff0c\u4e0d\u662f\u5468\u5927\u603b\u7ba1\uff0c\u662f\u8fd9\u4f4d\u5c11\u7237\uff0c\u8fd9\u4f4d\u5c11\u7237\u662f\u80b2\u5316 \u57ce\u4e25\u57ce\u4e3b\u7684\u4eb2\u621a\uff0c\u5e76\u975e\u4e00\u822c\u5ba2\u4eba\uff0c\u4ed6\u82e5\u770b\u597d\u4e86\u8c01\uff0c\u8c01\u5c31\u7b49\u4e8e\u4e2d\u4e86\u5927\u5956\uff0c\u5fc5\u987b\u62ff\u51fa\u771f\u5de5\u592b\u771f\u672c \u4e8b\u6765\u62db\u5f85\u3002\u201d \u3000\u3000\u90a3\u4e9b\u59d1\u5a18\u4eec\u4e00\u542c\u4e4b\u4e0b\uff0c\u7acb\u523b\u4e00\u6d8c\u800c\u4e0a\uff0c\u50cf\u8089\u5c4f\u98ce\u822c\u628a\u5cb3\u5c0f\u98de\u56f4\u4e86\u4e2a\u6c34\u6cc4\u4e0d\u901a\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6709\u751f\u4ee5\u6765\uff0c\u90a3\u7ecf\u8fc7\u8fd9\u7b49\u9635\u4ed7\uff0c\u7b80\u76f4\u8fde\u547c\u5438\u90fd\u6709\u4e9b\u7a92\u606f\u4e4b\u611f\u3002 \u3000\u3000\u5c82\u77e5\u90a3\u4e9b\u59d1\u5a18\u56f4\u62e2\u4e0a\u6765\u4e0d\u6253\u7d27\uff0c\u800c\u4e14\u8fd8\u52a8\u624b\u52a8\u811a\uff0c\u6709\u7684\u8981\u6478\u5cb3\u5c0f\u98de\u7684\u8138\uff0c\u66f4\u6709\u7684\u5476\u7740\u7ea2 \u5507\u8981\u548c\u5cb3\u5c0f\u98de\u4eb2\u5634\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u60c5\u6025\u4e4b\u4e0b\uff0c\u53c8\u4e0d\u4fbf\u51fa\u624b\u6253\u4eba\uff0c\u53ea\u597d\u9ad8\u53eb\u5468\u6d77\u5c71\u9053\uff1a\u201c\u5468\u603b\u7ba1\uff0c\u53eb\u5979\u4eec\u8d70\u8fdc\u4e00\u4e9b\uff01\u201d \u3000\u3000\u5468\u6d77\u5c71\u518d\u53eb\u72ec\u773c\u9f9f\u5974\u9053\uff1a\u201c\u8001\u5434\uff0c\u59d1\u5a18\u4eec\u8fd9\u6837\u62e5\u4e0a\u6765\uff0c\u8ba9\u6211\u4eec\u516c\u5b50\u600e\u4e48\u6311\u9009\uff0c\u516c\u5b50\u8fd8\u5e74 \u8f7b\uff0c\u603b\u4e0d\u80fd\u7167\u5355\u5168\u6536\u5427\uff01\u201d \u3000\u3000\u72ec\u773c\u9f9f\u5974\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5927\u5bb6\u522b\u8fd9\u6837\uff0c\u79bb\u5f00\u4e00\u70b9\uff0c\u8ba9\u516c\u5b50\u6311\uff0c\u53cd\u6b63\u53ea\u6709\u4e00\u4e2a\u4e2d\u5956\u7684\u3002\u201d \u3000\u3000\u4f46\u5979\u4eec\u7684\u773c\u775b\u548c\u5634\u5df4\u5374\u90fd\u6ca1\u95f2\u7740\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4e2a\u5634\u89d2\u6709\u9ed1\u75e3\u7684\u9053\uff1a\u201c\u8fd9\u4f4d\u5c11\u7237\u51c6\u662f\u4f4d\u7ae5\u5b50\u9e21\uff0c\u5e72\u54b1\u4eec\u8fd9\u884c\u7684\uff0c\u4e00\u8f88\u5b50\u4e5f\u4e0d\u4e00\u5b9a \u80fd\u9047\u4e0a\u4e00\u4e2a\uff0c\u5e0c\u671b\u4f60\u4eec\u5927\u5bb6\u8ba9\u7ed9\u6211\uff0c\u6211\u660e\u5929\u4e00\u5b9a\u8bf7\u4f60\u4eec\u7684\u5ba2\uff01\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u5de6\u988a\u6709\u9152\u7a9d\u7684\u9053\uff1a\u201c\u4f60\u522b\u4e13\u60f3\u597d\u4e8b\uff0c\u8ba9\u7ed9\u6211\uff0c\u6211\u53ef\u4ee5\u8fde\u8bf7\u5927\u5bb6\u4e24\u6b21\u5ba2\uff01\u201d \u3000\u3000\u53c8\u6709\u4e00\u4e2a\u53f3\u7709\u4e0b\u5e26\u70b9\u75a4\u773c\u7684\u9053\uff1a\u201c\u8c01\u90fd\u522b\u4e89\uff0c\u8fd9\u516c\u5b50\u4eca\u665a\u548c\u6211\u6709\u7f18\uff0c\u8be5\u662f\u6211\u7684\uff0c\u4e0d\u8fc7\u6211 \u660e\u5929\u4e5f\u7167\u6837\u8bf7\u5ba2\u3002\u201d \u3000\u3000\u518d\u2014\u4e2a\u53d1\u8fb9\u63d2\u7740\u4e00\u6735\u82b1\u7684\u9053\uff1a\u201c\u597d\u4e1c\u897f\u8c01\u90fd\u60f3\u5403\uff0c\u4f60\u4eec\u518d\u4e89\u4e5f\u6ca1\u7528\uff0c\u6211\u5012\u6709\u516c\u5e73\u529e\u6cd5\u3002\u201d \u3000\u3000\u90a3\u5de6\u988a\u6709\u9152\u7a9d\u7684\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u516c\u5e73\u529e\u6cd5\uff1f\u201d \u3000\u3000\u53d1\u8fb9\u63d2\u82b1\u7684\u9053\uff1b\u201c\u54b1\u4eec\u62bd\u7b3a\uff0c\u8c01\u62bd\u4e2d\u4e86\u5c31\u662f\u8c01\u7684\u3002\u201d \u3000\u3000\u90a3\u75a4\u773c\u7684\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u90a3\u6837\u62bd\u4e0d\u4e2d\u7684\u8fd8\u662f\u843d\u7a7a\uff0c\u6211\u6709\u4e2a\u66f4\u597d\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u5de6\u503e\u6709\u9152\u7a9d\u7684\u9053\uff1a\u201c\u54b1\u4eec\u4eca\u665a\u90fd\u966a\u8fd9\u4f4d\u516c\u5b50\u7761\uff0c\u8fd9\u6837\u8c01\u90fd\u4e0d\u5403\u4e8f\uff0c\u660e\u5929\u4e5f\u8c01\u90fd\u7528\u4e0d\u7740\u518d \u8bf7\u5ba2\u3002\u201d \u3000\u3000\u6b64\u8bed\u4e00\u51fa\uff0c\u81f3\u5c11\u4e24\u4e09\u4e2a\u62cd\u624b\u8d5e\u6210\u3002 \u3000\u3000\u53e6\u4e00\u4e2a\u9053\uff1a\u201c\u8001\u5434\uff0c\u6211\u4eec\u5c31\u8fd9\u6837\u51b3\u5b9a\u4e86\uff0c\u73b0\u5728\u8be5\u548c\u516c\u5b50\u8bf4\u4e86\uff01\u201d \u3000\u3000\u72ec\u773c\u8001\u5434\u5472\u5472\u7259\uff0c\u671b\u7740\u5cb3\u5c0f\u98de\u4e00\u54c8\u8170\u9053\uff1a\u201c\u516c\u5b50\uff0c\u59d1\u5a18\u4eec\u7684\u8bdd\u60a8\u542c\u89c1\u4e86\uff0c\u662f\u62bd\u7b3a\u597d\uff1f\u8fd8 \u662f\u5979\u4eec\u4e00\u8d77\u966a\u60a8\u597d\uff1f\u60a8\u8bf7\u5429\u5490\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u73b0\u5728\u6211\u662f\u5ba2\u4eba\uff0c\u6211\u60f3\u8981\u8c01\u5c31\u8981\u8c01\uff0c\u600e\u80fd\u7531\u59d1\u5a18\u4eec\u81ea\u5df1\u62bd\u7b3a\u51b3\u5b9a\uff1f\u201d \u3000\u3000\u72ec\u773c\u8001\u5434\u563f\u563f\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u516c\u5b50\u662f\u8ba9\u5979\u4eec\u4e00\u8d77\u966a\u4e86\uff0c\u96e8\u9732\u5747\u6cbe\uff0c\u6b63\u662f\u5979\u4eec\u6240\u5e0c\u671b\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u53ea\u6015\u4f60\u4eec\u6ca1\u6709\u8fd9\u6837\u5927\u7684\u5e8a\u94fa\u3002\u201d \u3000\u3000\u72ec\u773c\u8001\u5434\u7b11\u9053\uff1a\u201c\u8bbe\u5173\u7cfb\uff0c\u5927\u5e8a\u53ef\u4ee5\u7761\u4e09\u4e2a\u4eba\uff0c\u516c\u5b50\u5728\u4e2d\u95f4\uff0c\u4ed6\u5404\u4e00\u4e2a\uff0c\u5176\u4f59\u7684\u4e34\u65f6\u7761 \u5728\u5e8a\u4e0b\u5730\u4e0a\uff0c\u53ea\u8981\u6362\u4e24\u6b21\u73ed\u6bcf\u4e2a\u4eba\u90fd\u6709\u673a\u4f1a\u670d\u4f8d\u516c\u5b50\u3002\u201d \u3000\u3000\u6b64\u523b\uff0c\u59d1\u5a18\u4eec\u89c6\u7ebf\u5168\u76ef\u5728\u5cb3\u5c0f\u98de\u8138\u4e0a\uff0c\u6025\u5f85\u7740\u4ed6\u70b9\u5934\u7b54\u5e94\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u7136\u6447\u6447\u5934\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u5728\u4e0b\u4eca\u665a\u53ea\u60f3\u6765\u53c2\u89c2\u53c2\u89c2\uff0c \u3000\u3000\u56e0\u4e3a\u6211\u5fc5\u987b\u9a6c\u4e0a\u56de\u80b2\u5316\u57ce\u53bb\u3002\u201d \u3000\u3000\u6b64\u8bed\u4e00\u51fa\uff0c\u987f\u65f6\u4f7f\u90a3\u4e9b\u59d1\u5a18\u4eec\u51c9\u4e86\u534a\u622a\u3002 \u3000\u3000\u72ec\u773c\u8001\u5434\u4e5f\u6123\u5728\u5f53\u573a\uff0c \u3000\u3000\u5ffd\u542c\u5468\u6d77\u5c71\u9053\uff1a\u201c\u516c\u5b50\u653e\u5fc3\uff0c\u57ce\u4e3b\u4ea4\u4ee3\u8fc7\uff0c\u5982\u679c\u60a8\u770b\u4e2d\u4e86\u90a3\u4f4d\u59d1\u5a18\uff0c\u53ea\u7ba1\u7559\u4e0b\uff0c\u660e\u5929\u5468 \u67d0\u518d\u6765\u63a5\u60a8\u3002\u201d \u3000\u3000\u72ec\u773c\u8001\u5434\u4e5f\u7d27\u63a5\u7740\u9053\uff1a\u201c\u516c\u5b50\u5343\u4e07\u4e0d\u80fd\u8d70\uff0c\u8fd9\u51e0\u4f4d\u59d1\u5a18\uff0c\u5e73\u5e38\u5bf9\u5ba2\u4eba\u90fd\u662f\u6311\u4e09\u62e3\u56db\u7684\uff0c \u4eca\u665a\u5979\u4eec\u5bf9\u60a8\u8fd9\u6837\u70ed\u60c5\uff0c\u53ef\u8bf4\u5b9e\u5728\u5c11\u89c1\uff0c\u516c\u5b50\u4e0d\u80fd\u8ba9\u5979\u4eec\u5931\u671b\uff01\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4ead\u8fb9\u4e00\u4e2a\u7d2b\u8863\u5927\u6c49\uff0c\u659c\u77aa\u7740\u773c\u8d94\u8d94\u8d84\u8d84\u7684\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u59d1\u5a18\u4eec\u4e00\u89c1\u8fd9\u4eba\uff0c\u8167\u4e0a\u4f3c\u4e4e\u90fd\u843d\u4e86\u989c\u8272\u3002 \u3000\u3000\u72ec\u773c\u8001\u5434\u4e5f\u4f3c\u662f\u89c9\u51fa\u4e0d\u5999\uff0c\u5fd9\u8f6c\u8fc7\u8eab\u53bb\u966a\u7b11\u9053\uff1a\u201c\u848b\u526f\u7edf\u9886\uff0c\u60a8\u559d\u9189\u4e86\uff0c\u968f\u4fbf\u5230\u90a3\u4f4d\u59d1 \u5a18\u623f\u91cc\u4f11\u606f\u53bb\u5427\uff01\u201d \u3000\u3000\u7d2b\u8863\u5927\u6c49\u8038\u8d77\u4e24\u9053\u6d53\u7709\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8c01\u559d\u9189\u4e86\uff1f\u4f60\u7956\u5b97\u624d\u559d\u9189\u4e86\uff1f\u5434\u5c0f\u5b50\uff0c\u6709\u4ef6\u4e8b \u54b1\u770b\u4e0d\u987a\u773c\uff0c\u73b0\u5728\u5c31\u8ddf\u4f60\u8ba8\u8bba\u8ba8\u8bba\u3002\u201d \u3000\u3000\u72ec\u773c\u8001\u5434\u5472\u51fa\u9ec4\u677f\u7259\u9053\uff1a\u201c\u8c01\u6562\u8ba9\u60a8\u848b\u526f\u7edf\u9886\u770b\u7740\u4e0d\u987a\u8ddf\uff0c\u60a8\u8fd8\u662f\u627e\u4e2a\u5730\u65b9\u4f11\u606f\u7684\u597d\u3002\u201d \u3000\u3000\u7d2b\u8863\u5927\u6c49\u51b7\u7b11\u9053\uff1a\u201c\u627e\u5730\u65b9\u4f11\u606f\uff1f\u4f60\u4eec\u8fd9\u4e9b\u59d1\u5a18\u89c1\u4e86\u54b1\uff0c\u8868\u9762\u5949\u627f\uff0c\u80cc\u5730\u91cc\u6068\u4e0d\u5f97\u54ac\u7259 \u628a\u54b1\u5492\u6b7b\uff0c\u504f\u504f\u4eca\u665a\u5979\u4eec\u89c1\u4e86\u4e00\u4e2a\u521a\u8131\u5976\u7684\u5b69\u5b50\uff0c\u5c31\u50cf\u82cd\u8747\u78b0\u4e0a\u8702\u871c\u822c\uff0c\u4f60\u8bf4\u8fd9\u4e8b\u8ba9\u54b1\u597d\u6c14 \u4e0d\u597d\u6c14\uff0c\u54b1\u957f\u4e86\u8fd9\u4e48\u5927\uff0c\u8bba\u8eab\u4efd\u5730\u4f4d\u4e5f\u662f\u4e2a\u526f\u7edf\u9886\uff0c\u7adf\u8fde\u4e2a\u6bdb\u5934\u5c0f\u5b50\u90fd\u6bd4\u4e0d\u4e0a\uff0c\u59d3\u5434\u7684\uff0c\u54b1 \u4eec\u8ba8\u8bba\u7684\u5c31\u662f\u8fd9\u4ef6\u4e8b\uff0c\u4f60\u770b\u516c\u5e73\u4e0d\u516c\u5e73\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u5bb6\u4f19\u662f\u51b2\u7740\u5cb3\u5c0f\u98de\u6765\u4e86\u3002 \u3000\u3000\u72ec\u773c\u8001\u5434\u96be\u514d\u7740\u4e86\u614c\uff0c\u4e24\u8fb9\u4ed6\u90a3\u2014\u8fb9\u90fd\u60f9\u4e0d\u8d77\uff0c\u5c24\u5176\u5cb3\u5c0f\u98de\u662f\u80b2\u5316\u57ce\u4e3b\u7684\u4eb2\u621a\uff0c\u800c\u4e14\u8fd8 \u662f\u7531\u80b2\u5316\u57ce\u7684\u5927\u603b\u7ba1\u966a\u7740\uff0c\u82e5\u8ba9\u4ed6\u5f53\u573a\u53d7\u8fb1\uff0c\u5982\u4f55\u62c5\u5f85\u5f97\u4e86\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u65e0\u6cd5\u529d\u963b\u5f97\u4e86\uff0c\u53ea\u597d\u4ee5\u6073\u6c42\u7684\u76ee\u5149\u671b\u5411\u5468\u6d77\u5c71\u3002 \u3000\u3000\u5468\u6d77\u5c71\u5fd9\u62b1\u62f3\u62f1\u624b\u9053\uff1a\u201c\u848b\u526f\u7edf\u9886\u662f\u4e3a\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u7d2b\u8863\u5927\u6c49\u5f53\u7136\u8bc6\u5468\u6d77\u5c71\uff0c\u4f46\u56e0\u5f7c\u6b64\u5206\u5c5e\u4e24\u57ce\uff0c\u7528\u4e0d\u7740\u5356\u5468\u6d77\u5c71\u7684\u5e10\uff0c\u518d\u52a0\u4e0a\u5df2\u6709\u4e03\u516b\u5206 \u9189\u610f\uff0c\u66f4\u4e0d\u628a\u5468\u6d77\u5c71\u653e\u5728\u773c\u91cc\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u8fd8\u662f\u4e5f\u62b1\u4e86\u62b1\u62f3\u9053\uff1a\u201c\u96be\u5f97\uff0c\u5468\u5927\u603b\u7ba1\u4e5f\u5230\u8fd9\u91cc\u6765\u4e86\uff0c\u4f60\u6765\u4e86\u6b63\u597d\uff0c\u54b1\u4eec\u8bc4\u8bc4 \u7406\uff01\u201d \u3000\u3000\u5468\u6d77\u5c71\u5e72\u54b3\u4e86\u58f0\u9053\uff1a\u201c\u848b\u526f\u7edf\u9886\u8a00\u91cd\u4e86\uff0c\u5230\u8fd9\u79cd\u5730\u65b9\u6765\uff0c\u4e0d\u8fc7\u9022\u573a\u4f5c\u620f\uff0c\u6709\u4ec0\u4e48\u7406\u597d\u8bc4 \u7684\u3002\u201d \u3000\u3000\u201c\u5468\u5927\u603b\u7ba1\u975e\u8bc4\u7406\u4e0d\u53ef\uff01\u201d \u3000\u3000\u201c\u4f60\u8bf4\u8bf4\u770b\uff0c\u8bc4\u7684\u4ec0\u4e48\u7406\uff1f\u201d \u3000\u3000\u201c\u4e8b\u60c5\u5f88\u7b80\u5355\uff0c\u59d1\u5a18\u4eec\u89c1\u4e86\u54b1\u848b\u67d0\u4eba\uff0c\u50cf\u89c1\u4e86\u9b3c\u4e00\u822c\uff0c\u8eb2\u4e4b\u552f\u6050\u4e0d\u53ca\uff0c\u4eca\u665a\u89c1\u4e86\u8fd9\u5c0f\u5b50\uff0c \u5374\u53c8\u50cf\u86c6\u89c1\u5230\u5c4e\uff0c\u5230\u5e95\u662f\u4ec0\u4e48\u9053\u7406\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u4e0d\u7531\u51dd\u8d77\u8138\u8272\u9053\uff1a\u201c\u848b\u526f\u7edf\u9886\uff0c\u8bf4\u8bdd\u6700\u597d\u5634\u91cc\u5e72\u51c0\u4e00\u70b9\uff0c\u4f60\u53ef\u77e5\u9053\u8fd9\u4f4d\u516c\u5b50\u662f \u8c01\uff1f\u201d \u3000\u3000\u7d2b\u8863\u5927\u6c49\u8fde\u54fc\u4e24\u58f0\u9053\uff1a\u201c\u54b1\u7ba1\u4ed6\u662f\u8c01\uff0c\u603b\u4e0d\u4f1a\u662f\u5f53\u4eca\u7684\u7687\u592a\u5b50\u5427\uff01\u201d \u3000\u3000\u5468\u6d77\u5c71\u5df2\u88ab\u6fc0\u8d77\u6012\u610f\uff0c\u4ed6\u662f\u4e00\u57ce\u7684\u603b\u7ba1\uff0c\u8bba\u5730\u4f4d\u6bd4\u7d2b\u8863\u5927\u6c49\u7684\u526f\u7edf\u9886\u9ad8\u51fa\u5f88\u591a\uff0c\u82e5\u5355\u4eba \u524d\u6765\uff0c\u5728\u5bf9\u65b9\u9152\u9189\u4e4b\u4e0b\uff0c\u8fd8\u53ef\u5c3d\u91cf\u5bb9 \u3000\u3000\u5fcd\uff0c\u4f46\u73b0\u5728\u662f\u5949\u547d\u966a\u5cb3\u5c0f\u98de\uff0c\u4ed6\u5f53\u7136\u5fc5\u987b\u7ef4\u62a4\u5cb3\u5c0f\u98de\u7684\u5b89\u5168\u4ee5\u53ca\u81ea\u5df1\u7684\u989c\u9762\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u5468\u6d77\u5c71\u4e0d\u7531\u4e24\u773c\u4e00\u77aa\uff0c\u559d\u9053\uff1a\u201c\u848b\u6d77\u9f99\uff0c\u522b\u7ed9\u8138\u4e0d\u8981\u8138\uff0c\u4f60\u6562\u5728\u5468\u67d0\u9762\u524d\u800d\u6a2a\uff01\u201d \u3000\u3000\u848b\u6d77\u9f99\u4e00\u633a\u80f8\uff0c\u53cc\u76ee\u51f6\u5149\u66b4\u5c04\uff0c\u559d\u9053\uff1a\u201c\u59d3\u5468\u7684\uff0c\u4f60\u60f3\u505a\u4ec0\u4e48\u3002\u5728\u80b2\u5316\u57ce\u6709\u4eba\u6015\u4f60\uff0c\u6765 \u5230\u5bcc\u56fd\u57ce\uff0c\u6700\u597d\u628a\u62db\u5b50\u653e\u4eae\u4e00\u70b9\uff0c\u514d\u5f97\u8d70\u4e0d\u52a8\u722c\u7740\u56de\u53bb\uff01\u201d \u3000\u3000\u4e8b\u60c5\u95f9\u5230\u8fd9\u79cd\u5730\u6b65\uff0c\u770b\u6765\u4e24\u4eba\u975e\u5200\u5175\u76f8\u89c1\u4e0d\u53ef\u4e86\u3002 \u3000\u3000\u72ec\u773c\u8001\u5434\u5df2\u5413\u5f97\u624b\u8db3\u65e0\u63aa\u3002 \u3000\u3000\u90a3\u4e9b\u59d1\u5a18\u4eec\u90fd\u82b1\u5bb9\u5931\u8272\uff0c\u7eb7\u7eb7\u5411\u540e\u9000\u907f\u3002 \u3000\u3000\u4e0d\u8fc7\u5979\u4eec\u5e76\u672a\u8d70\u8fdc\uff0c\u4ecd\u7136\u5927\u4e3a\u5173\u5fc3\u7684\u671b\u5411\u8fd9\u8fb9\uff0c\u4eba\u4eba\u90fd\u4e3a\u5cb3\u5c0f\u98de\u634f\u4e00\u628a\u6c57\uff0c\u751f\u6015\u4ed6\u5403\u4e86 \u4e8f\uff0c\u867d\u7136\u5cb3\u5c0f\u98de\u5e76\u672a\u7b54\u5e94\u627e\u5979\u4eec\u4efb\u4f55\u2014\u4e2a\u966a\u5bbf\uff0c\u4f46\u5979\u4eec\u5374\u65e9\u628a\u5cb3\u5c0f\u98de\u770b\u6210\u662f\u81ea\u5df1\u6700\u5173\u5fc3\u7684\u4eba\u3002 \u3000\u3000\u7a81\u89c1\u5cb3\u5c0f\u98de\u4e00\u628a\u63a8\u5f00\u5468\u6d77\u5c71\u9053\uff1a\u201c\u5468\u603b\u7ba1\u8bf7\u9000\u540e\uff0c\u4ed6\u662f\u51b2\u7740\u5728\u4e0b\u6765\u7684\uff0c\u4e8b\u60c5\u8be5\u7531\u5728\u4e0b\u81ea \u5df1\u89e3\u51b3\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u679c\u7136\u4e56\u4e56\u7684\u95ea\u5230\u4e00\u65c1\uff0c\u56e0\u4e3a\u4ed6\u65e9\u542c\u8bf4\u5cb3\u5c0f\u98de\u6b66\u529f\u9ad8\u4e0d\u53ef\u6d4b\uff0c\u8fde\u5927\u5c0f\u59d0\u90fd\u8d25\u5728\u4ed6\u7684 \u624b\u4e0b\uff0c\u5bf9\u4ed8\u848b\u6d77\u9f99\uff0c\u6839\u672c\u4e0d\u4f1a\u6709\u95ee\u9898\u3002 \u3000\u3000\u4f46\u90a3\u4e9b\u59d1\u5a18\u4eec\u5374\u8d8a\u53d1\u7740\u614c\uff0c\u5979\u4eec\u773c\u89c1\u5cb3\u5c0f\u98de\u548c\u848b\u6d77\u9f99\u7ad9\u5728\u4e00\u8d77\uff0c\u53cc\u65b9\u5dee\u4e86\u4e00\u4e2a\u5934\u4ee5\u4e0a\uff0c \u82e5\u52a8\u8d77\u624b\u6765\uff0c\u540e\u679c\u7b80\u76f4\u4e0d\u582a\u60f3\u8c61\u3002 \u3000\u3000\u5176\u5b9e\u5cb3\u5c0f\u98de\u867d\u7136\u53ea\u6709\u5341\u56db\u5c81\uff0c\u4f46\u4e2a\u5934\u5374\u5e76\u4e0d\u592a\u77ee\uff0c\u51e0\u4e4e\u53ef\u4ee5\u8d76\u4e0a\u4e00\u822c\u6210\u5e74\u4eba\u4e86\uff0c\u4ed6\u6240\u4ee5 \u6bd4\u848b\u6d77\u9f99\u77ee\u4e86\u90a3\u4e48\u591a\uff0c\u662f\u56e0\u4e3a\u848b\u6d77\u9f99\u672c\u6765\u5c31\u8eab\u6750\u7c97\u58ee\u9ad8\u5927\uff0c\u7b80\u76f4\u5c31\u50cf\u6c34\u6d52\u4f20\u91cc\u7684\u848b\u95e8\u795e\u4e00\u822c\u3002 \u3000\u3000\u6b64\u523b\uff0c\u848b\u6d77\u9f99\u89c1\u5cb3\u5c0f\u98de\u5c45\u7136\u6562\u4e3b\u52a8\u51b2\u5411\u81ea\u5df1\uff0c\u96be\u514d\u6709\u4e9b\u5403\u60ca\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7a33\u7ad9\u5f53\u5730\uff0c\u6717\u58f0\u9053\uff1b\u201c\u59d3\u848b\u7684\uff0c\u4f60\u53ef\u662f\u5bf9\u5728\u4e0b\u770b\u4e0d\u987a\u773c\uff1f\u201d \u3000\u3000\u848b\u6d77\u9f99\u65e9\u5df3\u63e1\u8d77\u6597\u5927\u62f3\u5934\uff0c\u53cc\u988a\u9752\u7b4b\u66b4\u8d77\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u5b50\u5c31\u770b\u4f60\u4e0d\u987a\u773c\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8138\u8272\u4e00\u5bd2\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u4eec\u4e00\u89c1\u4f60\u5c31\u8eb2\uff0c\u90a3\u662f\u56e0\u4e3a\u4f60\u957f\u5f97\u6839\u672c\u4e0d\u50cf\u4eba\uff0c\u4f60\u8be5\u56de\u5bb6\u95ee \u95ee\u4f60\u7684\u7236\u6bcd\uff0c\u5f53\u521d\u4e3a\u4ec0\u4e48\u8fd9\u6837\u4e0d\u957f\u773c\u775b\uff0c \u3000\u3000\u73b0\u5728\u627e\u5230\u5728\u4e0b\uff0c\u5728\u4e0b\u53ef\u662f\u4e00\u70b9\u529e\u6cd5\u6ca1\u6709\uff01\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u4e0d\u557b\u706b\u4e0a\u52a0\u6cb9\u3002 \u3000\u3000\u848b\u6d77\u9f99\u90a3\u91cc\u5fcd\u5f97\u4e0b\u8fd9\u53e3\u6c14\uff0c\u9661\u7136\u6b3a\u8eab\u75be\u8fdb\uff0c\u201c\u547c\u201d\u7684\u4e00\u62f3\uff0c\u76f4\u5411\u5cb3\u5c0f\u98de\u9762\u95e8\u6363\u53bb\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u90a3\u4e9b\u59d1\u5a18\u4eec\u4e0d\u7531\u9f50\u9f50\u53d1\u51fa\u2014\u58f0\u5c16\u53eb\u60ca\u547c\u3002 \u3000\u3000\u679c\u7136\u51fb\u4e2d\u4e86\uff0c\u53ea\u542c\u201c\u4e52\u201d\u201c\u4e53\u201d\u4e24\u58f0\u8106\u54cd\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u7adf\u662f\u848b\u6d77\u9f99\u8e09\u8dc4\u5411\u540e\u5012\u649e\u56de\u53bb\u3002 \u3000\u3000\u800c\u5cb3\u5c0f\u98de\u5374\u4f9d\u7136\u52a8\u4e5f\u4e0d\u52a8\u7684\u7ad9\u5728\u539f\u5730\uff0c\u5c31\u50cf\u521a\u624d\u6839\u672c\u6ca1\u53d1\u751f\u4efb\u4f55\u4e8b\u4e00\u6837\u3002 \u3000\u3000\u90a3\u4e9b\u59d1\u5a18\u4eec\u548c\u72ec\u773c\u8001\u5434\u8fd9\u624d\u4f1a\u8fc7\u610f\u6765\uff0c\u539f\u6765\u65b9\u624d\u662f\u848b\u6d77\u9f99\u88ab\u5cb3\u5c0f\u98de\u7529\u4e24\u8bb0\u8033\u5149\u3002 \u3000\u3000\u7136\u800c\u8fd9\u4e24\u8bb0\u8033\u5149\u7a76\u7adf\u662f\u600e\u4e48\u6253\u7684\uff0c\u5374\u8c01\u4e5f\u534a\u70b9\u6ca1\u770b\u6e05\u695a\u3002 \u3000\u3000\u5230\u8fd9\u65f6\u5019\u5979\u4eec\u81f3\u5c11\u5df2\u653e\u4e86\u5fc3\uff0c\u540c\u65f6\u56e0\u4e3a\u5403\u4e8f\u7684\u662f\u848b\u6d77\u9f99\uff0c\u4e5f\u6697\u6697\u79f0\u5feb\u3002 \u3000\u3000\u848b\u6d77\u9f99\u53cc\u988a\u88ab\u63b4\u5f97\u706b\u8fa3\u8fa3\u7684\uff0c\u773c\u524d\u91d1\u661f\u76f4\u5192\uff0c\u5374\u8fd8\u4e0d\u76f8\u4fe1\u8fd9\u4e24\u638c\u662f\u5cb3\u5c0f\u98de\u6253\u7684\u3002 \u3000\u3000\u4f46\u65b9\u624d\u548c\u4ed6\u7ad9\u5f97\u6700\u8fd1\u7684\uff0c\u660e\u660e\u53ea\u6709\u5cb3\u5c0f\u98de\u4e00\u4eba\uff0c\u4e0d\u662f\u4ed6\u6253\u7684\u53c8\u662f\u8c01\u6253\u7684\u5462\uff1f \u3000\u3000\u771f\u628a\u4eba\u5f04\u7cca\u6d82\u4e86\u3002 \u3000\u3000\u83ab\u975e\u6709\u9b3c\u4e0d\u6210\uff1f \u3000\u3000\u848b\u6d77\u9f99\u662f\u4e0d\u4fe1\u90aa\u7684\uff0c\u7565\u4e00\u5598\u606f\uff0c\u518d\u5ea6\u8dc3\u8eab\u653b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u6b21\u4ed6\u662f\u53cc\u62f3\u9f50\u51fa\uff0c\u4e0b\u9762\u53c8\u594b\u529b\u8e22\u51fa\u4e00\u811a\u3002 \u3000\u3000\u53c8\u662f\u201c\u4e52\u201d\u201c\u4e53\u201d\u4e24\u58f0\u66b4\u54cd\uff0c\u51e0\u4e4e\u548c\u4e0a\u6b21\u5b8c\u5168\u4e00\u6837\uff0c\u848b\u6d77\u9f99\u4ecd\u8e09\u8dc4\u5012\u6454\u56de\u6765\u3002 \u3000\u3000\u4e0d\u8fc7\u8fd9\u6b21\u6bd4\u4e0a\u6b21\u6454\u5f97\u66f4\u91cd\uff0c\u8fd1\u4e4e\u5012\u683d\u4e0b\u53bb\u3002 \u3000\u3000\u5176\u5b9e\u5cb3\u5c0f\u98de\u662f\u4e0d\u60f3\u5728\u8fd9\u79cd\u5730\u65b9\u95f9\u4e8b\uff0c\u6545\u800c\u51fa\u624b\u6781\u8f7b\uff0c\u76ee\u7684\u662f\u8ba9\u5bf9\u65b9\u77e5\u96be\u800c\u9000\uff0c\u5426\u5219\uff0c\u848b \u6d77\u9f99\u65e9\u5df2\u4e0d\u662f\u73b0\u5728\u8fd9\u6837\u5b50\u4e86\u3002 \u3000\u3000\u4ed6\u7eb9\u98ce\u4e0d\u52a8\u7684\u7ad9\u5728\u539f\u5730\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u59d3\u848b\u7684\uff0c\u4f60\u8be5\u56de\u53bb\u4f11\u606f\u4e86\u5427\uff1f\u201d \u3000\u3000\u848b\u6d77\u9f99\u603b\u7b97\u6709\u4e9b\u660e\u767d\uff0c\u9152\u4e5f\u9192\u4e86\u5927\u534a\uff0c\u5472\u7259\u54a7\u5634\u9053\uff1a\u201c\u521a\u624d\u53ef\u662f\u4f60\u6253\u54b1\u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fcd\u7740\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u5728\u4e0b\u6253\u7684\uff0c\u662f\u8fc7\u5f80\u795e\u7075\u6253\u7684\u3002\u201d \u3000\u3000\u848b\u6d77\u9f99\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u90a3\u5c31\u96be\u602a\uff0c\u53cd\u6b63\u4e0d\u8bba\u662f\u795e\u662f\u9b3c\uff0c\u603b\u662f\u6709\u5e2e\u4f60\u5fd9\u7684\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u5426\u8fd8\u6ca1\u6709\u6328\u591f\uff1f\u201d \u3000\u3000\u201c\u8001\u5b50\u8fd8\u662f\u4e0d\u4fe1\u90aa\uff01\u201d \u3000\u3000\u201c\u4e0d\u59a8\u518d\u8fc7\u6765\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u201c\u8c01\u8fd8\u6015\u4e86\u4f60\uff01\u201d \u3000\u3000\u8fd9\u6b21\u5cb3\u5c0f\u98de\u4e0d\u7b49\u4ed6\u8fd1\u8eab\uff0c\u5de6\u8155\u4e00\u6296\uff0c\u4e00\u7f15\u6307\u98ce\u76f4\u5c04\u5411\u848b\u6d77\u9f99\u80f8\u524d\u5927\u7a74\u3002 \u3000\u3000\u848b\u6d77\u9f99\u521a\u8d70\u5c71\u534a\u6b65\uff0c\u4fbf\u539f\u5730\u4e0d\u52a8\u7684\u50f5\u5728\u90a3\u91cc\uff0c\u90a3\u59ff\u52bf\u770b\u6765\u5341\u5206\u6ed1\u7a3d\u53ef\u7b11\u3002 \u3000\u3000\u8fd9\u60c5\u666f\u53cd\u800c\u4f7f\u90a3\u4e9b\u59d1\u5a18\u4eec\u90fd\u5927\u611f\u60ca\u5947\uff0c\u5979\u4eec\u8fd8\u4ee5\u4e3a\u5cb3\u5c0f\u98de\u4f1a\u65bd\u6cd5\u672f\u5462\u3002 \u3000\u3000\u72ec\u773c\u8001\u5434\u5f53\u7136\u660e\u767d\u848b\u6d77\u9f99\u662f\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u62db\u547c\u90a3\u4e9b\u59d1\u5a18\u9053\uff1a\u201c\u4f60\u4eec\u8981\u770b\uff0c\u4e0d\u59a8\u8fd1\u524d\u770b\uff01\u201d \u3000\u3000\u4e00\u540d\u59d1\u5a18\u9053\uff1a\u201c\u53bb\u4f60\u7684\uff0c\u6211\u4eec\u82e5\u8fd1\u524d\u770b\uff0c\u848b\u526f\u7edf\u9886\u4e00\u5b9a\u4f1a\u6253\u4eba\u7684\uff01\u201d \u3000\u3000\u72ec\u773c\u8001\u5434\u563f\u563f\u7b11\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u8fde\u52a8\u90fd\u4e0d\u80fd\u52a8\u4e86\uff0c\u8fd8\u60f3\u6253\u4eba\uff0c\u6253\u4e2a\u72d7\u5c41\uff01\u201d \u3000\u3000\u201c\u8001\u5434\uff0c\u4f60\u8bf4\u6211\u4eec\u662f\u72d7\u5c41\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u4f60\u4eec\u8fde\u770b\u5230\u6b7b\u4eba\u4e5f\u6015\uff1f\u77a7\u6211\u7684\uff01\u201d \u3000\u3000\u72ec\u773c\u8001\u5434\u5e73\u65f6\u56e0\u53d7\u848b\u6d77\u9f99\u7684\u6c14\u592a\u591a\uff0c\u65e9\u5df2\u6068\u5f97\u4ed6\u7259\u6839\u53d1\u75d2\uff0c\u4eca\u665a\u597d\u4e0d\u5bb9\u6613\u902e\u5230\u4e86\u673a\u4f1a\uff0c \u4ed6\u8bf4\u7740\u5f53\u771f\u6765\u5230\u848b\u6d77\u9f99\u9762\u524d\uff0c\u6307\u7684\u9f3b\u5b50\u5927\u9a82\u9053\uff1a\u201c\u848b\u6d77\u9f99\uff0c\u8001\u5b50\u5e72\u4e86\u4f60\u7684\u7956\u5b97\uff0c\u770b\u4f60\u8fd8\u795e\u6c14 \u4e0d\u795e\u6c14\uff01\u201d \u3000\u3000\u90a3\u4e9b\u59d1\u5a18\u8fd9\u624d\u77e5\u9053\u848b\u6d77\u9f99\u5df2\u5931\u77e5\u89c9\uff0c\u7acb\u523b\u2014\u7a9d\u8702\u822c\u62e5\u4e86\u4e0a\u6765\uff0c \u3000\u3000\u6709\u7684\u5410\u53e3\u6c34\uff0c\u4e5f\u6709\u7684\u6307\u7740\u9f3b\u5b50\u9a82\u3002 \u3000\u3000\u5979\u4eec\u53ef\u8bf4\u662f\u65e2\u60ca\u5947\uff0c\u53c8\u89c9\u5f97\u597d\u73a9\u3002 \u3000\u3000\u72ec\u773c\u8001\u5434\u4e3a\u4e86\u5728\u59d1\u5a18\u800c\u524d\u5145\u82f1\u96c4\uff0c\u5e72\u8106\u5728\u848b\u6d77\u9f99\u80f8\u524d\u6363\u4e86\u4e00\u62f3\u3002 \u3000\u3000\u5c82\u77e5\u8fd9\u4e00\u4e3e\u6363\u4e0b\u53bb\uff0c\u7adf\u628a\u848b\u6d77\u9f99\u6253\u5012\u4e86\uff0c\u53ea\u542c\u201c\u549a\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u7740\u7740\u5b9e\u5b9e\u6454\u4e86\u4e2a\u56db\u5e73\u516b \u7a33\u3002 \u3000\u3000\u6454\u4e0b\u53bb\u4e4b\u540e\uff0c\u5df2\u65e0\u6cd5\u518d\u6276\u8d77\u6765\uff0c\u56e0\u4e3a\u6276\u8d77\u6765\u4e5f\u7ad9\u4e0d\u4f4f\u3002 \u3000\u3000\u72ec\u773c\u8001\u5434\u548c\u59d1\u5a18\u4eec\u6b63\u5728\u4e03\u624b\u516b\u811a\u5fd9\u4e71\u4e4b\u9645\uff0c\u53ea\u542c\u4e00\u540d\u59d1\u5a18\u558a\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u90a3\u4f4d\u516c\u5b50\u548c\u5468 \u5927\u603b\u7ba1\u90fd\u8d70\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u5468\u6d77\u5c71\u5f53\u7136\u662f\u5df2\u79bb\u5f00\u4e86\u8fd9\u5904\u5927\u9662\u3002 \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u516c\u5b50\uff0c\u6211\u4eec\u521a\u624d\u53ea\u901b\u4e86\u4e00\u5bb6\uff0c\u8fd8\u6709\u4e24\u5bb6\u6ca1\u770b\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff0c\u201c\u521a\u624d\u8fd9\u4e00\u5bb6\u662f\u54ea\u4e00\u7b49\u7684\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u672c\u6765\u8981\u4ece\u4e19\u7b49\u5f80\u4e0a\u770b\uff0c\u56e0\u4e3a\u8fd9\u5bb6\u8def\u8fd1\uff0c\u624d\u5148\u770b\u8fd9\u5bb6\u4e59\u7b49\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4e19\u7b49\u7684\u53c8\u662f\u4ec0\u4e48\u6837\u5b50\u5462\uff1f\u201d \u3000\u3000\u201c\u4e19\u7b49\u7684\u4e0a\u8eab\u8fde\u515c\u90fd\u6ca1\u6709\uff0c\u53ea\u7a7f\u4e00\u6761\u77ed\u88e4\uff0c\u82e5\u8bba\u98ce\u5149\uff0c\u6bd4\u770b\u4e59\u7b49\u7684\u66f4\u723d\u3002\u201d \u3000\u3000\u201c\u7532\u7b49\u7684\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u7532\u7b49\u7684\u7a7f\u85cf\u6bd4\u8f83\u6574\u9f50\uff0c\u59d1\u5a18\u4eec\u4e5f\u6bd4\u8f83\u89c4\u77e9\u4e9b\uff0c\u81f3\u5c11\u5e76\u4e0d\u4e3b\u52a8\u62c9\u5ba2\uff0c\u4e0d\u8fc7\u90a3\u91cc\u4ef7\u7801\u4e5f\u9ad8 \u4e9b\uff0c\u5149\u987e\u7684\u4eba\u4e5f\u90fd\u9ad8\u7ea7\u4e00\u70b9\u3002\u201d \u3000\u3000\u201c\u521a\u624d\u90a3\u4f4d\u59d3\u848b\u7684\uff0c\u4e5f\u7b97\u662f\u6709\u8eab\u4efd\u7684\u4e86\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5230\u4e59\u7b49\u53bb\uff1f\u201d \u3000\u3000\u201c\u516c\u5b50\u6709\u6240\u4e0d\u77e5\uff0c\u8981\u60f3\u73a9\u5f97\u75db\u51b3\uff0c\u8fd8\u662f\u4e59\u7b49\u548c\u4e19\u7b49\u7684\u597d\uff0c\u65e2\u7ecf\u6d4e\u53c8\u5b9e\u60e0\uff0c\u8b6c\u5982\u5230\u4e19\u7b49\u5927 \u9662\u53bb\uff0c\u5373\u4fbf\u770b\u770b\u5979\u4eec\u9732\u7740\u4e0a\u8eab\u548c\u5927\u817f\uff0c\u4e0d\u5fc5\u82b1\u94b1\u4e5f\u591f\u523a\u6fc0\u7684\u4e86\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u65e2\u7136\u4f60\u5df2\u8bf4\u8fc7\u53e6\u4e24\u5904\u5927\u9662\u662f\u4ec0\u4e48\u60c5\u5f62\uff0c\u5728\u4e0b\u5c31\u4e0d\u5fc5\u770b\u4e86\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u8bf4\u662f\u8bf4\uff0c\u770b\u662f\u770b\uff0c\u6709\u9053\u662f\u767e\u95fb\u4e0d\u5982\u4e00\u89c1\uff0c\u516c\u5b50\u600e\u53ef\u4e0d\u770b\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6447\u5934\u9053\uff1a\u201c\u5728\u4e0b\u8bf4\u4e0d\u770b\u5c31\u4e0d\u770b\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u6b63\u7ecf\u516b\u767e\u7684\u9053\uff1a\u201c\u516c\u5b50\uff0c\u8fd9\u662f\u57ce\u4e3b\u4ea4\u4ee3\u7684\uff0c\u5468\u67d0\u4e0d\u80fd\u4e0d\u966a\u60a8\u53bb\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u6b63\u8272\u7684\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u57ce\u4e3b\u4ea4\u4ee3\u7684\uff0c\u6240\u4ee5\u6211\u4e0d\u5f97\u4e0d\u52c9\u5f3a\u770b\u4e86\u4e00\u5bb6\uff0c\u5426\u5219\u6211\u5c82\u80af\u5230 \u8fd9\u79cd\u5730\u65b9\u6765\uff01\u201d \u3000\u3000\u201c\u90a3\u516c\u5b50\u662f\u771f\u7684\u4e0d\u53bb\u4e86\uff1f\u53ef\u662f\u5468\u67d0\u56de\u53bb\u5982\u4f55\u5411\u57ce\u4e3b\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u662f\u5426\u4e09\u5bb6\u5168\u53bb\u8fc7\uff0c\u57ce\u4e3b\u53c8\u5982\u4f55\u77e5\u9053\u5462\uff1f\u201d \u3000\u3000\u201c\u57ce\u4e3b\u53ef\u80fd\u5728\u53e6\u5916\u4e24\u5bb6\u5df2\u5b89\u6392\u4e86\u6697\u4e2d\u5bdf\u770b\u7684\u4eba\uff0c\u6839\u672c\u7792\u4e0d\u8fc7\u4ed6\u8001\u4eba\u5bb6\u3002\u201d \u3000\u3000\u201c\u679c\u771f\u5982\u6b64\uff0c\u4f60\u5c3d\u53ef\u544a\u8bc9\u57ce\u4e3b\uff0c\u662f\u6211\u4e0d\u80af\u53bb\uff0c\u628a\u8d23\u4efb\u63a8\u5230\u6211\u8eab\u4e0a\u597d\u5417\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u8e0c\u8e87\u4e86\u534a\u54cd\uff0c\u53ea\u597d\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u4e24\u4eba\u521a\u8981\u5f80\u56de\u8d70\uff0c\u53ea\u89c1\u5bf9\u9762\u4e00\u4e2a\u4eba\u6b63\u5411\u8fd9\u8fb9\u8d70\u6765\u3002 \u3000\u3000\u90a3\u4eba\u6e10\u8d70\u6e10\u8fd1\uff0c\u6708\u5149\u4e0b\uff0c\u7167\u89c1\u90a3\u4eba\u7684\u8eab\u6750\u9762\u8c8c\uff0c\u5cb3\u5c0f\u98de\u4e0d\u89c9\u5fc3\u5934\u731b\u9707\uff0c\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\uff0c\u8fd9\u4eba\u8d6b\u7136\u662f\u53db\u5e08\u706d\u7956\u5055\u5e08\u5a18\u6deb\u5954\u7684\u9a6c\u662d\u96c4\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u660e\u660e\u8bf4\u8fc7\u9a6c\u662d\u96c4\u5df2\u7ecf\u6b7b\u4e86\uff0c\u7a76\u7adf\u662f\u51e4\u5ae3\u7ea2\u4e0d\u77e5\u4ed6\u4ecd\u6d3b\u7740\uff1f\u8fd8\u662f\u5979\u6545\u610f\u9a97\u4ed6\u548c\u8881\u5c0f \u9e64\u5462\uff1f \u3000\u3000\u4ed6\u6682\u65f6\u4e0d\u60f3\u8ba9\u9a6c\u662d\u96c4\u649e\u4e0a\uff0c\u4e8e\u662f\u53ea\u597d\u544a\u8bc9\u5468\u6d77\u5c71\u8981\u53bb\u5c0f\u89e3\uff0c\u6025\u6025\u95ee\u6697\u5904\u9690\u53bb\u3002 \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u4e8c\u5341\u56de\u3000\u91cd\u89c1\u5929\u65e5 \u3000\u3000\u9a6c\u662d\u96c4\u5f88\u5feb\u4fbf\u6765\u5230\u8ddf\u524d\u3002 \u3000\u3000\u5f53\u4ed6\u53d1\u73b0\u5468\u6d77\u5c71\u65f6\uff0c\u8fde\u5fd9\u62b1\u62f3\u62f1\u624b\u9053\uff1a\u201c\u539f\u6765\u5468\u603b\u7ba1\u5728\u8fd9\u91cc\uff01\u201d \u3000\u3000\u5468\u6d77\u5c71\u7b11\u9053\uff1a\u201c\u9a6c\u9999\u4e3b\u600e\u4e48\u6709\u65f6\u95f4\u5230\u8fd9\u91cc\u6765\u5462\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u4e5f\u563f\u563f\u5e72\u7b11\u9053\uff1a\u3000\u201c\u5144\u5f1f\u5e72\u7684\u8fd9\u4e00\u884c\uff0c\u3000\u60a8\u5468\u603b\u7ba1\u4e0d\u662f\u4e0d\u77e5\u9053\uff0c\u3000\u6574\u5e74\u5230\u5934\u6697\u65e0 \u5929\u65e5\uff0c\u3000\u65e2\u82e6\u53c8\u95f7\uff0c\u3000\u90a3\u80fd\u4e0d\u627e\u673a\u4f1a\u51fa\u6765\u8c03\u5242\u8c03\u5242\u3002\u201d \u3000\u3000\u201c\u9a6c\u9999\u4e3b\u51c6\u5907\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u9664\u4e86\u4e19\u7b49\u5927\u9662\uff0c\u5c31\u662f\u4e59\u7b49\u5927\u9662\uff0c\u5144\u5f1f\u4ece\u6765\u4e0d\u5230\u7532\u7b49\u5927\u9662\u53bb\u3002\u201d \u3000\u3000\u201c\u60a8\u5728\u8fd9\u91cc\u4e00\u5b9a\u6709\u8001\u76f8\u597d\u5427\uff1f\u201d \u3000\u3000\u201c\u4ec0\u4e48\u8001\u76f8\u597d\u4e0d\u8001\u76f8\u597d\uff0c\u53ea\u8981\u6709\u94b1\uff0c\u54ea\u4e2a\u4e0d\u80fd\u73a9\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u5feb\u53bb\u5427\uff0c\u5468\u67d0\u4e0d\u4fbf\u803d\u8bef\u4f60\u7684\u5b9d\u8d35\u65f6\u95f4\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u62f1\u4e86\u62f1\u624b\uff0c\u6025\u6b65\u800c\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7acb\u523b\u56de\u5230\u539f\u5904\u3002 \u3000\u3000\u4ed6\u6b64\u523b\u5fc3\u60c5\u7684\u6fc0\u52a8\uff0c\u4e0d\u95ee\u53ef\u77e5\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u5fc5\u987b\u5c3d\u91cf\u4fdd\u6301\u9547\u5b9a\uff0c\u4e0d\u9732\u58f0\u8272\uff0c\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u521a\u624d\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1b\u201c\u5468\u67d0\u521a\u624d\u548c\u4ed6\u6240\u8bb2\u7684\u8bdd\uff0c\u516c\u5b50\u90fd\u542c\u89c1\u4e86\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u591c\u665a\u4eba\u9759\uff0c\u4f60\u4eec\u7684\u58f0\u97f3\u53c8\u5927\uff0c\u5f53\u7136\u542c\u5f97\u5230\u3002\u201d \u3000\u3000\u201c\u4ed6\u53eb\u9a6c\u662d\u96c4\uff0c\u662f\u4e00\u4f4d\u9999\u4e3b\u3002\u201d \u3000\u3000\u201c\u4e0d\u7528\u8bf4\u4ed6\u662f\u5bcc\u56fd\u57ce\u7684\u9999\u4e3b\u4e86\uff1f\u201d \u3000\u3000\u201c\u516c\u5b50\u731c\u9519\u4e86\uff0c\u4ed6\u662f\u54b1\u4eec\u80b2\u5316\u57ce\u7684\u9999\u4e3b\u3002\u201d \u3000\u3000\u201c\u80b2\u5316\u57ce\u7684\u9999\u4e3b\uff0c\u600e\u53ef\u968f\u4fbf\u5230\u8fd9\u8fb9\u6765\uff1f\u201d \u3000\u3000\u201c\u4ed6\u548c\u5bcc\u56fd\u57ce\u5b88\u95e8\u7684\u5f1f\u5144\u90fd\u8ba4\u8bc6\uff0c\u800c\u4e14\u53ea\u662f\u5230\u4e09\u5927\u9662\u6765\uff0c\u53c8\u662f\u591c\u665a\uff0c\u5f53\u7136\u53ef\u4ee5\u6765\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u4ed6\u662f\u5e38\u5e38\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c0f\u5b50\u8272\u5fc3\u5f88\u5927\uff0c\u5982\u679c\u4e09\u5929\u4e0d\u6765\uff0c\u51c6\u4f1a\u628a\u4ed6\u8e69\u6b7b\u3002\u201d \u3000\u3000\u201c\u4ed6\u5728\u80b2\u5316\u57ce\u90a3\u4e00\u90e8\u95e8\u5de5\u4f5c\uff1f\u201d \u3000\u3000\u201c\u542c\u8bf4\u5927\u516c\u4e3b\u6628\u5929\u66fe\u5e26\u516c\u5b50\u53c2\u89c2\u8fc7\u7075\u5802\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u4ed6\u5c31\u5728\u7075\u5802\u62c5\u4efb\u9999\u4e3b\uff0c\u90a3\u8fb9\u7684\u4e8b\uff0c\u7531\u4ed6\u8d1f\u603b\u8d23\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u50cf\u88ab\u5f53\u5934\u6572\u4e86\u4e00\u68d2\uff0c\u5dee\u70b9\u6655\u53a5\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u60f3\u5230\u6bcd\u4eb2\u6b63\u662f\u5728\u7075\u5802\u62c5\u4efb\u5b88\u7075\u5974\uff0c\u4e5f\u6b63\u662f\u5f52\u9a6c\u662d\u96c4\u6240\u7ba1\u8f96\uff0c\u6bcd\u4eb2\u7684\u5bb9\u8c8c\uff0c\u79f0\u5f97\u4e0a\u662f\u4ed9 \u8d44\u7389\u8d28\uff0c\u800c\u504f\u504f\u9a6c\u662d\u96c4\u53c8\u597d\u8272\u5982\u547d\uff0c\u4e07\u4ed6\u771f\u4e0d\u6562\u518d\u5f80\u4e0b\u60f3\uff0c\u53ea\u611f\u5230\u4e24\u773c\u53d1\u9ed1\uff0c\u8fde\u811a\u6b65\u90fd\u6709\u4e9b \u4e0d\u7a33\u3002 \u3000\u3000\u597d\u5728\u6b64\u523b\u662f\u665a\u4e0a\uff0c\u8fd9\u60c5\u5f62\u5e76\u672a\u5f15\u8d77\u5468\u6d77\u5c71\u7684\u6ce8\u610f\u3002 \u3000\u3000\u4ed6\u5f3a\u5fcd\u7740\u5401\u4e86\u53e3\u6c14\uff0c\u6545\u610f\u50cf\u6f2b\u4e0d\u7ecf\u5fc3\u7684\u9053\uff1a\u201c\u5728\u4e0b\u6628\u5929\u53c2\u89c2\u7075\u5802\uff0c\u89c1\u5b88\u62a4\u7684\u6709\u7537\u6709\u5973\uff0c \u5973\u7684\u6709\u4e9b\u8fd8\u957f\u5f97\u5f88\u4e0d\u9519\uff0c\u8fd9\u4f4d\u9a6c\u9999\u4e3b\u5bf9\u5979\u4eec\u53ef\u4e0d\u53ef\u80fd\u4e0d\u89c4\u77e9\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u59d3\u9a6c\u7684\u8fd9\u5c0f\u5b50\u5f53\u7136\u4e0d\u60f3\u653e\u8fc7\u5979\u4eec\uff0c\u5176\u4e2d\u4e24\u4e2a\u53eb\u4f55\u6167\u4ed9\u7684\uff0c\u4eba\u957f\u5f97 \u6700\u7f8e\u2026\u2026\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u542c\u5230\u8fd9\u91cc\uff0c\u50cf\u88ab\u5229\u5203\u523a\u8fdb\u4e86\u5fc3\u80f8\uff0c\u51e0\u4e4e\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5468\u6d77\u5c71\u7ee7\u7eed\u9053\uff1b\u201c\u9a6c\u662d\u96c4\u6700\u60f3\u67d3\u6307\u7684\uff0c\u5c31\u662f\u90a3\u4e2a\u4f55\u6167\u4ed9\uff0c\u53ef\u60dc\u4ed6\u59cb\u7ec8\u6ca1\u8fbe\u6210\u5fc3\u613f\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u603b\u7b97\u7f13\u8fc7\u4e00\u53e3\u6c14\uff0c\u5374\u60c5\u4e0d\u81ea\u7981\u95ee\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8fbe\u4e0d\u6210\u5fc3\u613f\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u90a3\u4f55\u6167\u4ed9\u7b97\u5f97\u4e0a\u662f\u4f4d\u8d1e\u8282\u70c8\u5973\uff0c\u800c\u4e14\u53c8\u6709\u4e00\u8eab\u4e0d\u51e1\u7684\u6b66\u529f\uff0c\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c \u9a6c\u9999\u4e3b\u5f53\u7136\u65e0\u6cd5\u5f97\u901e\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u201c\u4e0d\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e0d\u8fc7\u5979\u5374\u53d7\u4e86\u6bd4\u522b\u4eba\u66f4\u591a\u7684\u6298\u78e8\uff0c\u6709\u4e24\u6b21\u9a6c\u9999\u4e3b\u6545\u610f\u7ed9\u5979\u52a0\u4e2a\u7f6a\u540d\u3002\u7528\u97ad\u5b50\u6253\u5f97\u5979\u904d \u4f53\u9cde\u4f24\uff0c\u4f24\u52bf\u5341\u5929\u534a\u6708\u624d\u597d\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u542c\u5f97\u5fc3\u5982\u5200\u624e\uff0c\u771f\u6068\u4e0d\u5f97\u7acb\u523b\u628a\u9a6c\u662d\u96c4\u788e\u5c38\u4e07\u6bb5\u3002\u4ed6\u5f3a\u5fcd\u60b2\u6124\u9053\uff1a\u201c\u540e\u6765\u5462\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u540e\u6765\u8fd9\u4e8b\u88ab\u6211\u77e5\u9053\u4e86\uff0c\u662f\u6211\u770b\u4e0d\u8fc7\u53bb\uff0c\u544a\u8beb\u9a6c\u9999\u4e3b\u4ee5\u540e\u4e0d\u5f97\u518d\u6253\u5979\uff0c\u9a6c\u662d \u96c4\u8fd9\u624d\u4e0d\u6562\u518d\u5bf9\u4f55\u6167\u4ed9\u600e\u4e48\u6837\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u987f\u65f6\u5bf9\u5468\u6d77\u5c71\u6709\u7740\u96be\u8a00\u7684\u611f\u6fc0\uff0c\u7531\u6b64\u53ef\u89c1\uff0c\u5468\u6d77\u5c71\uff0c\u8fd8\u4e0d\u5931\u662f\u4e2a\u597d\u4eba\u3002 \u3000\u3000\u4ed6\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u90a3\u4f4d\u4f55\u6167\u4ed9\u662f\u4e3a\u4ec0\u4e48\u88ab\u6253\u5230\u7075\u5802\u53bb\u7684\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u76b1\u8d77\u7709\u5934\u9053\uff1a\u201c\u63d0\u8d77\u4f55\u6167\u4ed9\uff0c\u4e09\u5e74\u524d\u4e5f\u662f\u901a\u8fc7\u4e94\u5173\u800c\u8fdb\u5165\u5929\u8c37\u7684\uff0c\u636e\u8bf4\u5979\u7684\u6210\u7ee9 \u8fd8\u597d\u7684\u4e0d\u5f97\u4e86\uff0c\u81f3\u4e8e\u4e3a\u4ec0\u4e48\u88ab\u6253\u8fdb\u80b2\u5316\u57ce\u53d7\u7f6a\uff0c\u636e\u8bf4\u548c\u5bb5\u5173\u7684\u98ce\u7edf\u9886\u6709\u5173\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u5bb5\u5173\u7684\u98ce\u7edf\u9886\u662f\u4f4d\u5973\u7684\uff0c\u6211\u56e0\u5de1\u5173\u5173\u7cfb\uff0c\u548c\u5979\u89c1\u8fc7\u597d\u51e0\u6b21\uff0c\u5979\u548c\u90a3 \u4f4d\u4f55\u6167\u4ed9\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e8b\u5468\u67d0\u5c31\u4e0d\u6e05\u695a\u4e86\uff0c\u53ef\u80fd\u662f\u4f55\u6167\u4ed9\u5f97\u7f6a\u4e86\u98ce\u7edf\u9886\uff0c\u636e\u8bf4\u98ce\u7edf\u9886\u662f\u6559 \u4e3b\u624b\u4e0b\u7684\u7ea2\u4eba\uff0c\u5979\u5728\u6559\u4e3b\u90a3\u91cc\u8bf4\u4ec0\u4e48\u8bdd\u90fd\u5f88\u7ba1\u7528\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9ed8\u4e86\u9ed8\u9053\uff1a\u201c\u5982\u679c\u660e\u5929\u6709\u65f6\u95f4\uff0c\u6211\u5f88\u60f3\u5230\u7075\u5802\u53bb\u89c1\u90a3\u4f4d\u4f55\u6167\u4ed9\uff0c\u5468\u603b\u7ba1\u80fd\u5426\u5e2e\u5fd9 \u7ed9\u6211\u4e2a\u673a\u4f1a\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u832b\u7136\u95ee\u9053\uff1a\u201c\u5728\u6211\u6765\u8bf4\uff0c\u5e26\u516c\u5b50\u5230\u7075\u5802\u8d70\u8d70\uff0c\u5f53\u7136\u5f88\u7b80\u5355\uff0c\u53ef\u662f\u516c\u5b50\u4e3a\u4ec0\u4e48\u8981\u53bb \u89c1\u5979\u5462\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u56e0\u4e3a\u636e\u4f60\u521a\u624d\u6240\u8bf4\uff0c\u90a3\u4f4d\u4f55\u6167\u4ed9\u4e0d\u4f46\u662f\u4f4d\u8d1e\u8282\u70c8\u5973\uff0c\u800c\u4e14\u53c8\u6253\u4e00\u8eab\u4e86\u4e0d\u8d77 \u7684\u6b66\u529f\uff0c\u50cf\u8fd9\u6837\u7684\u4eba\uff0c\u5728\u4e0b\u5f53\u7136\u5e0c\u671b\u6709\u673a\u4f1a\u89c1\u89c1\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u4f46\u770b\u57ce\u4e3b\u660e\u5929\u600e\u6837\u5b89\u6392\uff0c\u5982\u679c\u65f6\u95f4\u4e0d\u51b2\u7a81\uff0c\u6211\u5c31\u5e26\u516c\u5b50\u53bb\u8d9f\u7075\u5802\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8d81\u673a\u95ee\u9053\uff1a\u201c\u5468\u603b\u7ba1\u53ef\u77e5\u9053\u57ce\u4e3b\u53ec\u6211\u5230\u80b2\u5316\u57ce\u7a76\u7adf\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u7b11\u9053\uff1a\u201c\u5f53\u7136\u662f\u597d\u4e8b\uff0c\u81f3\u4e8e\u4ec0\u4e48\u597d\u4e8b\uff0c\u8bf7\u6055\u5468\u67d0\u73b0\u5728\u8fd8\u4e0d\u4fbf\u660e\u8a00\uff0c\u603b\u4e4b\uff0c\u4e5f\u8bb8\u660e \u5929\u516c\u5b50\u5c31\u6709\u597d\u6d88\u606f\u3002\u201d \u3000\u3000\u4e24\u4eba\u4e00\u8def\u8fb9\u8d70\u8fb9\u8c08\uff0c\u4e0d\u5230\u4e8c\u66f4\uff0c\u4fbf\u5df2\u5230\u8fbe\u80b2\u5316\u57ce\u5e9c\u3002 \u3000\u3000\u8fd9\u65f6\u5019\uff0c\u4e25\u5bd2\uff0c\u4e25\u592b\u4eba\u548c\u5927\u5c0f\u59d0\u7b49\u4eba\uff0c\u5f53\u7136\u90fd\u5df2\u7761\u7740\u3002 \u3000\u3000\u5468\u6d77\u5c71\u76f4\u63a5\u628a\u5cb3\u5c0f\u98de\u5f15\u8fdb\u4e00\u5904\u8de8\u9662\u3002 \u3000\u3000\u4f46\u89c1\u6b63\u5c4b\u5185\u706f\u5149\u660e\u4eae\uff0c\u4e00\u540d\u9ed1\u8863\u6c49\u5b50\u8fce\u4e0a\u524d\u6765\u9053\uff1a\u201c\u7980\u603b\u7ba1\uff0c\u5cb3\u516c\u5b50\u7684\u5367\u5ba4\u5df2\u7ecf\u6574\u7406\u597d \u4e86\uff01\u201d \u3000\u3000\u5468\u6d77\u5c71\u6325\u6325\u624b\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4f60\u56de\u53bb\u4f11\u606f\u5427\uff01\u201d \u3000\u3000\u7136\u540e\u4ed6\u81ea\u5df1\u4e5f\u62f1\u62f1\u624b\u9053\uff1a\u201c\u65f6\u95f4\u4e0d\u65e9\uff0c\u516c\u5b50\u4e5f\u8be5\u4f11\u606f\uff0c\u8bf7\u6055\u5468\u67d0\u4e0d\u966a\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fde\u5fd9\u4e5f\u5411\u5468\u6d77\u5c71\u62f1\u624b\u9053\u8c22\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5728\u5929\u4e95\u91cc\u8d70\u4e86\u4e00\u5708\uff0c\u53ea\u89c9\u504c\u5927\u7684\u4e00\u5904\u8de8\u9662\uff0c\u4f3c\u4e4e\u65e0\u4eba\u5c45\u4f4f\uff0c\u663e\u7136\u662f\u7279\u5730\u4e3a\u81ea\u5df1\u6574 \u7406\u51fa\u6765\u7684\u3002 \u3000\u3000\u4ed6\u7ecf\u6b63\u5c4b\u8fdb\u5165\u5367\u5ba4\uff0c\u684c\u4e0a\u9ad8\u70e7\u7740\u4e24\u679d\u7ea2\u8272\u8721\u70db\uff0c\u5ba4\u5185\u5e03\u7f6e\u7684\u5e7d\u9759\u7eda\u70c2\uff0c\u7b80\u76f4\u4ee4\u4ed6\u4e3a\u4e4b\u548b \u820c\uff0c\u6bd4\u8d77\u5728\u6587\u5173\u4f4f\u8fc7\u7684\u90a3\u95f4\u201c\u9f99\u98ce\u9601\u201d\uff0c\u72b9\u80dc\u4e09\u5206\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u7d2b\u6a80\u96d5\u82b1\u5e8a\u4e0a\uff0c\u5c45\u7136\u653e\u7740\u4e24\u53ea\u5206\u7ee3\u7740\u91d1\u9f99\u5f69\u98ce\u7684\u6795\u5934\uff0c\u5f53\u7136\uff0c\u5367\u69bb\u4e5f\u662f\u53cc\u4eba\u7684\uff0c \u518d\u52a0\u4e0a\u5782\u906e\u4e0b\u6765\u7684\u7c89\u7ea2\u7f57\u5e10\uff0c\u4ee5\u53ca\u684c\u4e0a\u9ad8\u70e7\u7684\u4e24\u679d\u7ea2\u70db\uff0c\u7b80\u76f4\u6709\u4e9b\u50cf\u65b0\u5a5a\u4e4b\u591c\u7684\u6d1e\u623f\u3002 \u3000\u3000\u4ed6\u5439\u7184\u4e86\u8721\u70db\uff0c\u5f88\u5feb\u4fbf\u5728\u5e8a\u4e0a\u8eba\u597d\u3002 \u3000\u3000\u867d\u7136\u5df2\u662f\u4e8c\u66f4\uff0c\u4f46\u5374\u8f97\u8f6c\u53cd\u4fa7\uff0c\u518d\u4e5f\u7761\u4e0d\u7740\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5fc3\u5934\u6709\u7740\u592a\u591a\u7684\u4e8b\uff0c\u4e0d\u80fd\u4e0d\u60f3\u3002 \u3000\u3000\u9996\u5148\uff0c\u4ed6\u4e00\u76f4\u601d\u89e3\u4e0d\u900f\u80b2\u5316\u57ce\u4e3b\u53ec\u81ea\u5df1\u524d\u6765\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f \u3000\u3000\u5c24\u5176\uff0c\u4ed6\u547d\u5468\u6d77\u5c71\u5e26\u7740\u81ea\u5df1\u5230\u7f8e\u4eba\u5173\u51b6\u6e38\uff0c\u83ab\u975e\u4e5f\u662f\u6709\u4ec0\u4e48\u7279\u522b\u7684\u7528\u610f\uff1f \u3000\u3000\u5f53\u7136\uff0c\u6700\u4f7f\u4ed6\u6302\u5fc3\u7684\u8fd8\u662f\u6bcd\u4eb2\u3002 \u3000\u3000\u6240\u5e78\u4ed6\u4ece\u5468\u6d77\u5c71\u90a3\u91cc\uff0c\u5df2\u7ecf\u6253\u542c\u51fa\u6bcd\u4eb2\u7684\u4e0d\u5c11\u6d88\u606f\uff0c\u5185\u5fc3\u9664\u4e86\u6df1\u6df1\u611f\u6fc0\u5468\u6d77\u5c71\u5916\uff0c\u5bf9\u9a6c \u662d\u96c4\u5219\u662f\u8d8a\u53d1\u75db\u6068\u3002 \u3000\u3000\u5927\u7ea6\u5feb\u5230\u4e09\u66f4\uff0c\u4ed6\u624d\u6e10\u6709\u7761\u610f\u3002 \u3000\u3000\u6726\u80e7\u95f4\u521a\u4e00\u5408\u773c\uff0c\u5ffd\u542c\u5929\u4e95\u4e2d\u4f3c\u6709\u811a\u6b65\u58f0\u97f3\u3002 \u3000\u3000\u8fd9\u822c\u65f6\u5019\uff0c\u6709\u8c01\u4f1a\u6765\u5462\uff1f \u3000\u3000\u7531\u811a\u6b65\u58f0\u97f3\u751a\u8f7b\u5224\u65ad\uff0c\u51b3\u4e0d\u53ef\u80fd\u662f\u5468\u6d77\u5c71\u3002 \u3000\u3000\u811a\u6b65\u58f0\u5f88\u5feb\u4fbf\u6765\u5230\u623f\u95e8\u5916\uff0c\u63a5\u7740\u623f\u95e8\u8f7b\u8f7b\u54cd\u4e86\u4e09\u54cd\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5750\u8d77\u8eab\u6765\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u95e8\u5916\u56de\u7b54\u7684\u662f\u5a07\u6ef4\u6ef4\u7684\u58f0\u97f3\uff1a\u201c\u662f\u6211\uff0c\u516c\u5b50\u8bf7\u5f00\u95e8\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u867d\u672a\u542c\u51fa\u662f\u8c01\uff0c\u4f46\u5374\u60f3\u5230\u5f88\u53ef\u80fd\u662f\u5927\u5c0f\u59d0\u3002 \u3000\u3000\u5982\u679c\u662f\u5927\u5c0f\u59d0\uff0c\u4ed6\u6ca1\u7406\u7531\u4e0d\u8ba9\u5979\u8fdb\u6765\u3002 \u3000\u3000\u62ab\u8863\u4e0b\u5e8a\uff0c\u5148\u70b9\u4e0a\u8721\u70db\uff0c\u63a5\u7740\u6253\u5f00\u95e8\u6765\u3002 \u3000\u3000\u4ed6\u4e0d\u7531\u4e00\u6014\u3002 \u3000\u3000\u95e8\u5916\u7ad9\u7740\u7684\uff0c\u5374\u662f\u4f4d\u7f9e\u7f9e\u7b54\u7b54\u3001\u5343\u5a07\u767e\u5a9a\u7684\u7ea2\u8863\u5c11\u5973\u3002 \u3000\u3000\u90a3\u5c11\u5973\u7f8e\u5f97\u51fa\u5947\uff0c\u800c\u4e14\u76db\u88c5\uff0c\u6709\u5982\u7c89\u5986\u7389\u7422\u4e00\u822c\uff0c\u76f8\u4fe1\u4efb\u4f55\u4eba\u89c1\u4e86\u4e5f\u8981\u591a\u770b\u5979\u51e0\u773c\u3002 \u3000\u3000\u90a3\u5c11\u5973\u4e0d\u7b49\u5cb3\u5c0f\u98de\u95ee\u8bdd\uff0c\u4fbf\u8f7b\u79fb\u83b2\u6b65\u8de8\u8fdb\u95e8\u69db\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6123\u6123\u5730\u9000\u540e\u2014\u6b65\u95ee\u9053\uff1a\u201c\u59d1\u5a18\u662f\u4ec0\u4e48\u4eba\uff1f\u6709\u4e8b\u5417\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u5e76\u4e0d\u7b54\u8bdd\uff0c\u5374\u8fd4\u8eab\u628a\u623f\u95e8\u5173\u4e0a\uff0c\u7136\u540e\u8f7b\u8f7b\u5750\u4e0a\u4e86\u5e8a\u69bb\uff0c\u4f4e\u5782\u7c89\u9888\uff0c\u4e0d\u53d1\u4e00\u8bed\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8d8a\u611f\u9519\u6115\uff0c\u8f7b\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u59d1\u5a18\u4e09\u66f4\u534a\u591c\u5230\u8fd9\u91cc\u6765\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u5a07\u9765\u4e0a\u7edb\u971e\u4f3c\u6d41\uff0c\u7f9e\u602f\u602f\u5730\u7b54\u9053\uff1a\u201c\u6211\u662f\u5949\u547d\u6765\u966a\u516c\u5b50\u8fc7\u591c\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u5934\u4e00\u9707\uff0c\u4e0d\u7981\u554a\u4e86\u58f0\u9053\uff1b\u201c\u59d1\u5a18\u662f\u5949\u4ec0\u4e48\u4eba\u4e4b\u547d\u6765\u7684\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u57ce\u4e3b\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u89c9\u53c8\u4e00\u6b21\u7684\u9677\u5165\u8ff7\u60d1\u4e2d\uff0c\u57ce\u4e3b\u5148\u547d\u5468\u6d77\u5c71\u5e26\u7740\u81ea\u5df1\u901b\u7f8e\u4eba\u5173\uff0c\u73b0\u5728\u53c8\u6d3e\u8fd9\u4f4d\u79c0 \u7f8e\u53ef\u4eba\u7684\u5973\u90ce\u524d\u6765\u966a\u5bbf\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u7528\u5fc3\u3002 \u3000\u3000\u4ed6\u957f\u957f\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u59d1\u5a18\u53ef\u5426\u544a\u77e5\u82b3\u540d\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u53eb\u79c0\u5170\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6b63\u8272\u9053\uff1a\u201c\u79c0\u5170\u59d1\u5a18\uff0c\u6211\u81ea\u5df1\u53ef\u4ee5\u7167\u987e\u81ea\u5df1\uff0c\u7528\u4e0d\u7740\u4eba\u966a\uff0c\u4f55\u51b5\u8fd9\u91cc\u53ea\u6709\u4e00\u5f20\u5e8a\uff0c \u7537\u5973\u6388\u53d7\u4e0d\u4eb2\uff0c\u600e\u53ef\u7761\u5728\u4e00\u5f20\u5e8a\u4e0a\uff0c\u57ce\u4e3b\u7684\u597d\u610f\uff0c\u6211\u5fc3\u9886\u4e86\uff0c\u4f60\u8fd8\u662f\u8bf7\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u79c0\u5170\u5934\u5782\u5f97\u66f4\u4f4e\u3002\u7f9e\u7b54\u7b54\u7684\u9053\uff1a\u201c\u6211\u662f\u5949\u547d\u670d\u4f8d\u516c\u5b50\u7684\uff0c\u672c\u5c31\u8981\u548c\u516c\u5b50\u7761\u5728\u4e00\u5f20\u5e8a\u4e0a\uff0c \u800c\u65e5\u8fd8\u8981\u7761\u540c\u4e00\u4e2a\u88ab\u7a9d\u91cc\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u514d\u6709\u4e9b\u7740\u614c\uff0c\u5fd9\u9053\uff1a\u201c\u90a3\u4e0d\u6210\uff0c\u6211\u6700\u4e0d\u4e60\u60ef\u548c\u5973\u4eba\u540c\u7761\uff0c\u4f60\u5feb\u8d70\u5427\uff01\u201d \u3000\u3000\u79c0\u5170\u9053\uff1a\u201c\u516c\u5b50\u53ea\u8981\u7531\u6211\u670d\u4f8d\u4e00\u665a\uff0c\u4ee5\u540e\u5c31\u4e60\u60ef\u548c\u5973\u4eba\u540c\u7761\u4e86\u3002\u201d \u3000\u3000\u201c\u5c82\u6709\u6b64\u7406\uff0c\u6211\u7231\u81ea\u5df1\u7684\u540d\u8a89\uff0c\u59d1\u5a18\u66f4\u5e94\u73cd\u60dc\u81ea\u5df1\u7684\u6e05\u767d\u3002\u201d \u3000\u3000\u201c\u516c\u5b50\u7684\u8bdd\uff0c\u6211\u5f53\u7136\u660e\u767d\uff0c\u4f46\u57ce\u4e3b\u7684\u4ee4\u8c15\uff0c\u5374\u8c01\u4e5f\u4e0d\u6562\u4e0d\u9075\uff0c\u6240\u4ee5\u516c\u5b50\u8981\u6211\u56de\u53bb\uff0c\u90a3\u662f \u4e07\u4e07\u529e\u4e0d\u5230\u7684\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4f60\u56de\u53bb\u4e86\uff0c\u5c06\u4f1a\u5f97\u5230\u4ec0\u4e48\u6837\u7684\u5904\u5206\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u53ea\u6709\u6b7b\u8def\u4e00\u6761\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7565\uff0c\u6c89\u541f\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u6211\u5c31\u8ba9\u4f60\u4eca\u665a\u4f4f\u5728\u8fd9\u91cc\uff0c\u59d1\u5a18\u8bf7\u4e0a\u5e8a\u7761\u5427\uff01\u201d \u3000\u3000\u79c0\u5170\u7728\u7740\u661f\u7738\u8f6c\u8fc7\u5934\u6765\u9053\uff1a\u201c\u516c\u5b50\u8bf7\u5148\u4e0a\u5e8a\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5e8a\u5c31\u8ba9\u7ed9\u59d1\u5a18\uff0c\u6211\u60c5\u613f\u7761\u5730\u4e0b\u3002\u201d \u3000\u3000\u79c0\u5170\u5403\u60ca\u9053\uff1a\u201c\u90a3\u600e\u4e48\u6210\uff1f\u82e5\u516c\u5b50\u4e0d\u7761\u5e8a\u4e0a\uff0c\u6211\u600e\u6562\u72ec\u81ea\u7761\u5728\u5e8a\u4e0a\uff1f\u5806\u9053\u516c\u5b50\u8fd8\u6ca1\u770b\u51fa \u4eca\u665a\u8fd9\u623f\u95f4\u91cc\u7684\u5e03\u7f6e\uff1f\u201d \u3000\u3000\u201c\u96be\u9053\u623f\u95f4\u91cc\u7684\u5e03\u7f6e\u6709\u4ec0\u4e48\u7279\u522b\u7528\u610f\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u6709\uff0c\u5e8a\u4e0a\u6709\u4e24\u4e2a\u6795\u5934\uff0c\u5c31\u8868\u793a\u6709\u4eba\u966a\u516c\u5b50\u7761\uff0c\u684c\u4e0a\u4e24\u679d\u7ea2\u70db\u9ad8\u70e7\uff0c\u90a3\u8868\u793a\u516c\u4e8e\u4eca \u665a\u5c31\u50cf\u65b0\u5a5a\u4e4b\u591c\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u59d1\u5a18\u548c\u6211\u5e76\u975e\u592b\u59bb\uff1f\u201d \u3000\u3000\u201c\u516c\u5b50\u4f55\u59a8\u628a\u6211\u770b\u505a\u662f\u4f60\u7684\u59bb\u5b50\uff0c\u662f\u5426\u5acc\u6211\u4e0d\u597d\u770b\uff1f\u201d \u3000\u3000\u6b64\u523b\uff0c\u5cb3\u5c0f\u98de\u5f53\u771f\u5927\u4e3a\u8e0c\u8e87\u8d77\u6765\uff0c\u5982\u679c\u786c\u628a\u5bf9\u65b9\u8d76\u8d70\uff0c\u53c8\u63d0\u5fc3\u5979\u660e\u5929\u771f\u4f1a\u53d7\u5230\u5904\u7f5a\uff0c\u800c \u53cc\u65b9\u5927\u58f0\u5435\u95f9\u8d77\u6765\uff0c\u6df1\u66f4\u534a\u591c\uff0c\u66f4\u4e0d\u6210\u4f53\u7edf\u3002 \u3000\u3000\u65e0\u5948\u4e4b\u4e0b\uff0c\u4ed6\u53ea\u597d\u6682\u65f6\u5750\u5728\u5e8a\u4e0b\u6905\u5b50\u4e0a\uff0c\u5373\u4fbf\u8fd9\u6837\u53cc\u65b9\u5750\u5230\u5929\u4eae\uff0c\u4ed6\u4e5f\u53ea\u6709\u8ba4\u4e86\u3002 \u3000\u3000\u5c82\u77e5\u79c0\u5170\u5e76\u4e0d\u613f\u8001\u5750\u5728\u5e8a\u6cbf\uff0c\u7d22\u6027\u8131\u6389\u7ee3\u978b\u4e0a\u4e86\u5e8a\u3002\u63a5\u7740\uff0c\u5979\u7adf\u5f00\u59cb\u5bbd\u8863\u89e3\u5e26\u3002 \u3000\u3000\u8fd9\u60c5\u5f62\u8feb\u5f97\u5cb3\u5c0f\u98de\u4e0d\u5f97\u4e0d\u8f6c\u8fc7\u5934\u53bb\u3002 \u3000\u3000\u534a\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u4f3c\u4e4e\u5df2\u6ca1\u4e86\u58f0\u97f3\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u9053\u5979\u5df2\u76d6\u8d77\u88ab\u5b50\u8eba\u4e0b\uff0c\u4f46\u5f53\u4ed6\u8f6c\u8fc7\u5934\u6765\u65f6\uff0c\u4e0d\u7531\u4ed6\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u89c1\u79c0\u5170\u5df2\u662f\u8131 \u5f97\u4e00\u4e1d\u4e0d\u6302\uff0c\u800c\u4e14\u6b63\u7389\u4f53\u6a2a\u9648\u6beb\u65e0\u906e\u63a9\u7684\u8eba\u5728\u90a3\u91cc\u3002 \u3000\u3000\u5979\u5168\u8eab\u808c\u8d28\u6676\u83b9\uff0c\u767d\u5982\u51dd\u8102\uff0c\u66f2\u7ebf\u73b2\u73d1\u5206\u660e\uff0c\u8fde\u5cb3\u5c0f\u98de\u5728\u8fd9\u5239\u90a3\uff0c\u4e5f\u4f3c\u4e4e\u9677\u5165\u8ff7\u60d1\u3002 \u3000\u3000\u4ed6\u6025\u6025\u95ed\u4e0a\u773c\u53bb\uff0c\u51dd\u7740\u58f0\u97f3\u9053\uff1a\u201c\u59d1\u5a18\u8fd9\u662f\u8981\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53ea\u542c\u79c0\u5170\u5403\u5403\u7b11\u9053\uff1a\u201c\u516c\u5b50\u662f\u806a\u660e\u7edd\u9876\u7684\u4eba\uff0c\u600e\u4e48\u8fde\u8fd9\u4e2a\u90fd\u4e0d\u660e\u767d\uff0c\u6211\u5728\u7b49\u7740\u4f60\u8fc7\u6765\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u53e3\u6c14\u5439\u7184\u4e86\u706f\u9053\uff1a\u201c\u59d1\u5a18\u8bf7\u5148\u76d6\u4e0a\u88ab\uff0c\u522b\u7740\u4e86\u51c9\uff01\u201d \u3000\u3000\u706f\u65e2\u7136\u7184\u4e86\uff0c\u79c0\u5170\u53ea\u5f97\u76d6\u4e0a\u88ab\u5b50\uff0c\u5979\u55f2\u58f0\u55f2\u6c14\u7684\u53eb\u9053\uff1a\u201c\u516c\u5b50\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u8fc7\u6765\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1b\u201c\u59d1\u5a18\u5148\u7761\u5427\uff0c\u6211\u968f\u540e\u5c31\u4e0a\u5e8a\uff01\u201d \u3000\u3000\u79c0\u5170\u679c\u7136\u542c\u8bdd\uff0c\u4e0d\u518d\u7740\u58f0\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u76f4\u5750\u4e86\u987f\u996d\u5de5\u592b\uff0c\u624d\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u4f46\u4ed6\u5e76\u672a\u4e0a\u5e8a\uff0c\u7adf\u8f7b\u8f7b\u7684\u6253\u5f00\u4e86\u623f\u95e8\u3002 \u3000\u3000\u5c82\u77e5\u79c0\u5170\u5e76\u672a\u7761\u7740\uff0c\u4e00\u9aa8\u788c\u5750\u8d77\u8eab\u6765\u9053\uff1a\u201c\u516c\u5b50\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4f60\u53ea\u7ba1\u7761\u4f60\u7684\uff0c\u6211\u5230\u5916\u9762\u4e00\u4e0b\uff0c\u9a6c\u4e0a\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u4e00\u5b9a\u8981\u9a6c\u4e0a\u56de\u6765\uff01\u201d \u3000\u3000\u201c\u6211\u4f1a\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5148\u5728\u5929\u4e95\u91cc\u6563\u4e86\u4e00\u56de\u6b65\uff0c\u6b32\u5f85\u8d70\u51fa\u8de8\u9662\uff0c\u53c8\u62c5\u5fc3\u88ab\u5916\u9762\u5de1\u591c\u7684\u4eba\u78b0\u4e0a\u3002 \u3000\u3000\u7075\u673a\u4e00\u52a8\uff0c\u4ed6\u60f3\u5230\u8de8\u9662\u91cc\u6709\u8fd9\u4e48\u591a\u7a7a\u623f\u95f4\uff0c\u4f55\u4e0d\u968f\u4fbf\u627e\u4e00\u95f4\u8fdb\u53bb\u6682\u65f6\u71ac\u8fc7\u4e0b\u534a\u591c\u3002 \u3000\u3000\u63a8\u95e8\u8fdb\u5165\u4e00\u95f4\u53a2\u623f\uff0c\u91cc\u9762\u867d\u6709\u5e8a\u69bb\uff0c\u5374\u65e0\u88ab\u8925\uff0c\u53ea\u6709\u4e24\u4e2a\u66f4\u6b21\u4fbf\u53ef\u5929\u4eae\uff0c\u4ed6\u51b3\u5b9a\u5c31\u5728\u6b64 \u5904\u5c06\u5c31\u5c06\u5c31\uff0c\u4e8e\u662f\u590d\u53c8\u628a\u95e8\u8f7b\u8f7b\u5173\u4e0a\uff0c\u7136\u540e\u4e0a\u5e8a\u548c\u8863\u800c\u5367\u3002 \u3000\u3000\u4ed6\u6599\u60f3\u5f97\u5230\uff0c\u79c0\u5170\u662f\u65e0\u6cd5\u627e\u5230\u81ea\u5df1\u7684\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u4fbf\u7761\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u9192\u6765\u65f6\u5929\u5df2\u5927\u4eae\u3002 \u3000\u3000\u4ed6\u51b3\u5b9a\u518d\u56de\u5230\u539f\u6765\u623f\u95f4\uff0c\u73b0\u5728\u662f\u767d\u5929\uff0c\u79c0\u5170\u4e0d\u53ef\u80fd\u518d\u6765\u7ea0\u7f20\u4ed6\u3002 \u3000\u3000\u56de\u5230\u539f\u6765\u623f\u95f4\uff0c\u624d\u77e5\u9053\u79c0\u5170\u65e9\u5df2\u8d70\u4e86\uff0c\u800c\u4e14\u628a\u88ab\u8925\u647a\u53e0\u5f97\u6574\u6574\u9f50\u9f50\u3002 \u3000\u3000\u5f88\u5feb\u4fbf\u6709\u4eba\u9001\u6765\u6dae\u6d17\u7528\u6c34\u548c\u7528\u5177\u3002 \u3000\u3000\u63a5\u7740\uff0c\u53c8\u6709\u4eba\u9001\u6765\u65e9\u9910\u3002 \u3000\u3000\u8a93\u540e\uff0c\u4ed6\u6765\u5230\u5367\u623f\u5916\u9762\u7684\u5c0f\u5ba2\u5385\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u63a5\u7740\u800c\u6765\u7684\uff0c\u5fc5\u662f\u57ce\u4e3b\u4e25\u5bd2\u53ec\u89c1\u3002 \u3000\u3000\u679c\u7136\uff0c\u4e0d\u5927\u2014\u4f1a\u513f\uff0c\u603b\u7ba1\u5468\u6d77\u5c71\u6765\u4e86\u3002 \u3000\u3000\u5468\u6d77\u5c71\u4eca\u5929\u6bd4\u6628\u665a\u8868\u73b0\u5f97\u66f4\u4eb2\u5207\uff0c\u800c\u4e14\u4f3c\u4e4e\u793c\u8c8c\u4e5f\u66f4\u5468\u5230\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u662f\u5426\u57ce\u4e3b\u53ec\u89c1\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u57ce\u4e3b\u4e0a\u5348\u6709\u8981\u7d27\u7684\u4e8b\u60c5\u5fc5\u987b\u9a6c\u4e0a\u5904\u7406\uff0c\u53ef\u80fd\u8981\u5230\u4e0b\u5348\u624d\u80fd\u548c\u516c\u5b50\u89c1\u9762\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4e0a\u4e4d\u5c31\u8bf7\u5468\u603b\u7ba1\u5e26\u5728\u4e0b\u5230\u7075\u5802\u53bb\u89c1\u89c1\u90a3\u4f4d\u4f55\u6167\u4ed9\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u53ef\u4ee5\u3002\u201d \u3000\u3000\u201c\u4e0d\u8fc7\u6211\u60f3\u6c42\u5468\u603b\u7ba1\u53e6\u5916\u5e2e\u4e2a\u5fd9\u3002\u201d \u3000\u3000\u201c\u516c\u5b50\u53ea\u7ba1\u5429\u5490\uff0c\u53ea\u8981\u5468\u67d0\u80fd\u505a\u5230\u7684\u5fc5\u5b9a\u5c3d\u529b\u3002\u201d \u3000\u3000\u201c\u6211\u6682\u65f6\u4e0d\u60f3\u548c\u90a3\u4f4d\u9a6c\u9999\u4e3b\u89c1\u9762\uff0c\u5468\u603b\u7ba1\u662f\u5426\u80fd\u8bbe\u6cd5\u6682\u65f6\u628a\u4ed6\u5f15\u5f00\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u6123\u4e86\u4e0b\u9053\uff1a\u201c\u83ab\u975e\u516c\u5b50\u548c\u9a6c\u9999\u4e3b\u4ece\u524d\u8ba4\u8bc6\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4ece\u524d\u89c1\u8fc7\uff0c\u4f46\u5374\u5e76\u65e0\u4ea4\u5f80\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u516c\u5b50\u548c\u4ed6\u8ba4\u8bc6\uff0c\u4ed6\u8fd9\u4eba\u5728\u6ca1\u8fdb\u5929\u8c37\u524d\u7684\u98ce\u8bc4\u5982\u4f55\uff1f\u201d \u3000\u3000\u201c\u5934\u9876\u957f\u75ae\uff0c\u811a\u5e95\u6d41\u8113\u2014\u2014\u574f\u5230\u5e95\u4e86\u3002\u6240\u4ee5\u6211\u624d\u4e0d\u60f3\u548c\u4ed6\u89c1\u9762\u3002\u201d \u3000\u3000\u201c\u516c\u5b50\u8fd9\u6837\u8bc4\u8bba\u4ed6\u5e76\u4e0d\u8fc7\u5206\uff0c\u5468\u67d0\u4e5f\u65e9\u5c31\u770b\u51fa\u4ed6\u4e0d\u662f\u4e1c\u897f\u3002\u201d \u3000\u3000\u201c\u90a3\u4e3a\u4ec0\u4e48\u8fd8\u8981\u6bd4\u4ed6\u505a\u7075\u5802\u7684\u9999\u4e3b\u5462\uff1f\u201d \u3000\u3000\u201c\u8fd9\u5c0f\u5b50\u4e5f\u9887\u6709\u6765\u5934\uff0c\u4ed6\u662f\u6559\u4e3b\u6d3e\u4e0b\u6765\u7684\uff0c\u8fde\u57ce\u4e3b\u4e5f\u4e0d\u65b9\u4fbf\u52a8\u4ed6\uff0c\u4ee5\u5468\u67d0\u6765\u8bf4\uff0c\u53ef\u4ee5\u4e0d \u7406\u4ed6\uff0c\u5374\u53c8\u4e0d\u80fd\u8fc7\u5206\u5f97\u7f6a\u4ed6\u3002\u201d \u3000\u3000\u4ed6\u63a5\u7740\u53c8\u9053\uff1a\u201c\u516c\u5b50\u8bf7\u7b49\u4e00\u7b49\uff0c\u6211\u56de\u53bb\u53d6\u6837\u4e1c\u897f\u6765\u3002\u201d \u3000\u3000\u5f53\u5468\u6d77\u5c71\u518d\u56de\u6765\u65f6\uff0c\u624b\u91cc\u591a\u4e86\u4e00\u6837\u4e1c\u897f\uff0c\u662f\u7528\u9ec4\u7ee2\u5305\u7740\u3002 \u3000\u3000\u4ed6\u9012\u7ed9\u5cb3\u5c0f\u98de\u9053\uff1b\u201c\u8fd9\u91cc\u9762\u662f\u526f\u4eba\u76ae\u9762\u5177\uff0c\u516c\u5b50\u4e0d\u59a8\u6234\u4e0a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8bb6\u7136\u9053\uff1b\u201c\u6234\u4e0a\u8fd9\u4e2a\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u60f3\u628a\u9a6c\u662d\u96c4\u6682\u65f6\u5f15\u5f00\uff0c\u4e5f\u8bb8\u4f1a\u5f15\u8d77\u4ed6\u7684\u7591\u5fc3\uff0c\u6234\u4e0a\u8fd9\u4e2a\uff0c\u4ed6\u5c31\u8ba4\u4e0d\u51fa\u6765 \u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5927\u559c\uff0c\u4ed6\u867d\u542c\u8bf4\u8fc7\u4eba\u76ae\u9762\u5177\uff0c\u4eca\u5929\u8fd8\u662f\u7b2c\u4e00\u6b21\u770b\u5230\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u7531\u5468\u6d77\u5c71\u534f\u52a9\u6234\u597d\uff0c\u53d6\u8fc7\u955c\u5b50\u4e00\u7167\uff0c\u81ea\u5df1\u679c\u771f\u53d8\u6210\u4e00\u4e2a\u58ee\u5e74\u4eba\u7684\u6a21\u6837\u4e86\uff0c\u4e0d\u8fc7\u5982 \u6b64\u4e00\u6765\uff0c\u96be\u514d\u6709\u4eba\u4f1a\u5acc\u4ed6\u4e2a\u5934\u77ee\u4e86\u4e9b\u3002 \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u73b0\u5728\u5c31\u8d70\u5427\uff01\u201d \u3000\u3000\u5230\u8fbe\u7075\u5802\u5916\u6d1e\u53e3\uff0c\u5b88\u95e8\u4eba\u89c1\u662f\u672c\u57ce\u5927\u603b\u7ba1\uff0c\u656c\u793c\u8fd8\u6765\u4e0d\u53ca\uff0c\u90a3\u91cc\u8fd8\u6562\u67e5\u95ee\u3002 \u3000\u3000\u5468\u6d77\u5c71\u95ee\u9053\uff1a\u201c\u9a6c\u9999\u4e3b\u53ef\u5728\u91cc\u9762\u5417\uff1f\u201d \u3000\u3000\u5b88\u95e8\u4eba\u9053\uff1a\u201c\u9a6c\u9999\u4e3b\u6709\u4e8b\u521a\u51fa\u53bb\u3002\u201d \u3000\u3000\u65e9\u77e5\u5982\u6b64\uff0c\u5cb3\u5c0f\u98de\u7528\u4e0d\u7740\u6234\u4eba\u76ae\u9762\u5177\u4e86\u3002 \u3000\u3000\u5f53\u4e24\u4eba\u8d70\u5165\u901a\u9053\u3002\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u5e0c\u671b\u80fd\u5355\u72ec\u548c\u5979\u89c1\u89c1\uff0c\u6700\u597d\u4e00\u65c1\u65e0\u4eba\u6253\u6270\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u90a3\u5f88\u7b80\u5355\uff0c\u7531\u6211\u6765\u5b89\u6392\u3002\u201d \u3000\u3000\u5230\u8fbe\u7075\u5802\uff0c\u4e00\u89c1\u90a3\u6392\u5217\u7740\u7684\u6570\u5341\u53e3\u68fa\u6750\uff0c\u7acb\u65f6\u7ed9\u4eba\u4e00\u79cd\u9634\u68ee\u6050\u6016\u800c\u53c8\u51c4\u51c9\u4e4b\u611f\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u60f3\u5230\u6bcd\u4eb2\u5728\u8fd9\u79cd\u6697\u65e0\u5929\u65e5\u7684\u5c71\u6d1e\u5185\u5ea6\u8fc7\u4e86\u4e09\u5e74\u591a\u7684\u51c4\u82e6\u751f\u6d3b\uff0c\u9f3b\u5c16\u4e00\u9178\uff0c\u7981\u4e0d\u4f4f \u5c31\u8981\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u4ed6\u5f3a\u81ea\u9547\u5b9a\uff0c\u6e38\u76ee\u5411\u7075\u5802\u56db\u89d2\u671b\u53bb\uff0c\u5e76\u4e0d\u89c1\u6bcd\u4eb2\u7684\u5f71\u5b50\u3002 \u3000\u3000\u5468\u6d77\u5c71\u5e26\u7740\u5cb3\u5c0f\u98de\uff0c\u76f4\u63a5\u8fdb\u5165\u9a6c\u662d\u96c4\u7684\u6d1e\u5ba4\uff0c\u5750\u4e0b\u540e\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u9999\u4e3b\u5ba4\uff0c\u6b63\u597d\u9a6c\u9999\u4e3b \u4e0d\u5728\uff0c\u516c\u5b50\u5c31\u5728\u8fd9\u91cc\u548c\u5979\u89c1\u9762\u5982\u4f55\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e07\u4e00\u9a6c\u9999\u4e3b\u56de\u6765\u4ee5\u540e\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5468\u8bf2\u5c71\u9053\uff1a\u201c\u6211\u5728\u5916\u9762\u66ff\u4f60\u628a\u98ce\uff0c\u4ed6\u82e5\u56de\u6765\uff0c\u6211\u81ea\u6253\u529e\u6cd5\u7f20\u4f4f\u4ed6\uff0c\u540c\u65f6\u6211\u4e5f\u4e0d\u51c6\u5176\u4ed6\u7684 \u4eba\u8fdb\u6765\u3002\u201d \u3000\u3000\u201c\u90a3\u592a\u597d\u4e86\uff0c\u591a\u8c22\u5468\u603b\u7ba1\u8003\u8651\u7684\u5468\u5230\u3002\u201d \u3000\u3000\u8fd9\u65f6\u65e9\u6709\u2014\u540d\u9ed1\u8863\u6c49\u5b50\u8dd1\u4e86\u8fdb\u6765\uff0c\u671b\u7740\u5468\u6d77\u5c71\u8eac\u8eab\u4e00\u793c\u9053\uff1a \u3000\u3000\u201c\u5c0f\u7684\u8fce\u63a5\u5468\u603b\u7ba1\uff0c\u6211\u4eec\u9999\u4e3b\u521a\u51fa\u53bb\u4e0d\u4e45\uff0c\u5468\u603b\u7ba1\u662f\u5426\u6709\u4ec0\u5e7a\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u63a5\u7740\u5e76\u6c8f\u4e86\u4e24\u676f\u8336\u7aef\u4e0a\uff0c \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u672c\u603b\u7ba1\u6ca1\u4ec0\u4e48\u5927\u4e8b\uff0c\u4f60\u53bb\u628a\u90a3\u53eb\u4f55\u6167\u4ed9\u7684\u5b88\u7075\u5974\u53eb\u5230\u8fd9\u91cc\u6765\uff01\u201d \u3000\u3000\u90a3\u6c49\u5b50\u5f04\u4e0d\u6e05\u662f\u4ec0\u4e48\u4e8b\uff0c\u5374\u53c8\u4e0d\u6562\u95ee\uff0c\u7acb\u5373\u5e94\u58f0\u800c\u53bb\u3002 \u3000\u3000\u5468\u8bf2\u5c71\u968f\u540e\u4e5f\u8d77\u8eab\u9053\uff1a\u201c\u6211\u5230\u5916\u9762\u4e3a\u516c\u5b50\u628a\u98ce\u53bb\u3002\u201d \u3000\u3000\u6d1e\u5ba4\u5185\u53ea\u5269\u4e0b\u5cb3\u5c0f\u98de\u4e00\u4eba\uff0c\u4e0d\u96be\u60f3\u89c1\uff0c\u4ed6\u6b64\u523b\u5185\u5fc3\u65e9\u5df2\u6fc0\u52a8\u4e0d\u5df2\u3002 \u3000\u3000\u4ed6\u7559\u610f\u5411\u5ba4\u5185\u5404\u5904\u671b\u53bb\uff0c\u9664\u4e86\u9634\u6e7f\u8f83\u91cd\u6574\u5929\u5230\u665a\u5fc5\u987b\u70b9\u706f\u5916\uff0c\u5e03\u7f6e\u5f97\u5012\u4e5f\u5341\u5206\u534e\u4e3d\u3002 \u3000\u3000\u81ea\u7136\uff0c\u8fd9\u662f\u9999\u4e3b\u7684\u5c45\u5ba4\uff0c\u81f3\u4e8e\u90a3\u4e9b\u5b88\u7075\u5974\u4eec\u7684\u4f4f\u5904\uff0c\u5c31\u4e0d\u6562\u60f3\u8c61\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u4ed6\u4f3c\u4e4e\u542c\u5f97\u89c1\u81ea\u5df1\u7684\u5fc3\u5728\u8df3\u52a8\u3002 \u3000\u3000\u4ed6\u6781\u529b\u4fdd\u6301\u7740\u9547\u5b9a\uff0c\u800c\u53cc\u773c\u76f4\u76f4\u5730\u7d27\u76ef\u7740\u6d1e\u95e8\u3002 \u3000\u3000\u4e0d\u4e45\u4e4b\u540e\uff0c\u6d1e\u95e8\u5916\u54cd\u8d77\u4e86\u811a\u6b65\u58f0\uff0c\u63a5\u7740\u63a8\u95e8\u800c\u5165\u7684\uff0c\u6b63\u662f\u6bcd\u4eb2\u4f55\u6167\u4ed9\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u614c\u5fd9\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u4ed6\u56e0\u6234\u7740\u4eba\u76ae\u9762\u5177\uff0c\u82e5\u9a6c\u4e0a\u76f8\u8ba4\uff0c\u53cd\u4e3a\u4e0d\u5999\uff0c\u6682\u65f6\u53ea\u80fd\u4ee5\u793c\u76f8\u8fce\u3002 \u3000\u3000\u4f55\u6167\u4ed9\u9762\u5bb9\u6194\u60b4\uff0c\u8fde\u8eab\u4e0a\u7a7f\u7684\u90a3\u5957\u9ed1\u8863\uff0c\u4e5f\u7834\u70c2\u4e0d\u582a\uff0c\u5cb3\u5c0f\u98de\u82e5\u975e\u4e0a\u6b21\u5df2\u89c1\u8fc7\uff0c\u7b80\u76f4\u4f1a \u4e0d\u6562\u76f8\u8ba4\u3002 \u3000\u3000\u4ed6\u7acb\u5373\u5411\u524d\u8d70\u51fa\u4e24\u6b65\uff0c\u6df1\u6df1\u65bd\u4e86\u4e00\u793c\uff0c\u5f3a\u5fcd\u7740\u76f4\u6b32\u7834\u7736\u800c\u51fa\u7684\u6cea\u6c34\u9053\uff1a\u201c\u592b\u4eba\u8bf7\u5750\uff01\u201d \u3000\u3000\u4f55\u6167\u4ed9\u4e0d\u7531\u5927\u611f\u8bb6\u5f02\uff0c\u5979\u672c\u6765\u542c\u8bf4\u662f\u672c\u57ce\u7684\u5468\u5927\u603b\u7ba1\u53ec\u89c1\uff0c\u600e\u4e48\u5374\u53c8\u6362\u4e86\u4e00\u4e2a\u4eba\uff1f \u3000\u3000\u5373\u4fbf\u662f\u5468\u5927\u603b\u7ba1\u53ec\u89c1\uff0c\u5728\u5979\u6765\u8bf4\uff0c\u4e5f\u662f\u610f\u60f3\u4e0d\u5230\u7684\u4e8b\u3002 \u3000\u3000\u5982\u4eca\uff0c\u6362\u4e86\u53e6\u5916\u4e00\u4e2a\u4eba\u4e0d\u8bf4\uff0c\u800c\u8fd9\u4eba\u53c8\u5bf9\u81ea\u5df1\u53e3\u79f0\u201c\u592b\u4eba\u201d\uff0c\u600e\u4e0d\u4ee4\u5979\u8d8a\u611f\u5403\u60ca\u3002 \u3000\u3000\u5979\u5e76\u975e\u6ca1\u542c\u8fc7\u522b\u4eba\u79f0\u81ea\u5df1\u4e3a\u592b\u4eba\uff0c\u4e0d\u8fc7\u90a3\u5df2\u662f\u4e09\u5e74\u524d\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u81ea\u4ece\u88ab\u6253\u8fdb\u80b2\u5316\u57ce\uff0c\u522b\u4eba\u80fd\u628a\u5979\u770b\u6210\u4e00\u4e2a\u666e\u901a\u4e0b\u4eba\uff0c\u5979\u4e5f\u5c31\u5fc3\u6ee1\u610f\u8db3\uff0c\u90a3\u91cc\u8fd8\u6562\u5962\u6c42\u3002 \u3000\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u5979\u4e0d\u89c9\u6014\u5728\u90a3\u91cc\uff0c\u56e0\u4e3a\u5979\u5b9e\u5728\u4e0d\u6562\u5750\u4e0b\u3002 \u3000\u3000\u201c\u592b\u4eba\u5feb\u8bf7\u5750\uff01\u5feb\u8bf7\u5750\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fb9\u53eb\u8fb9\u53c8\u6c8f\u4e86\u4e00\u676f\u8336\uff0c\u53cc\u624b\u7aef\u4e0a\u3002 \u3000\u3000\u4f55\u6167\u4ed9\u7ec8\u4e8e\u6014\u6014\u5730\u5750\u4e0b\uff0c\u628a\u8336\u653e\u5728\u4e00\u8fb9\uff0c\u60ca\u5f02\u4e0d\u5b9a\u7684\u832b\u7136\u95ee\u9053\uff0c\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f \u4f60\uff1f\u2026\u2026\u201d \u3000\u3000\u53ea\u542c\u201c\u6251\u549a\u201d\u4e00\u58f0\uff0c\u5cb3\u5c0f\u98de\u65e9\u628a\u53cc\u819d\u8dea\u5012\uff0c\u60b2\u5207\u5207\u53eb\u9053\uff1b\u201c\u5a18\uff01\u6211\u662f\u98de\u513f\uff01\u201d \u3000\u3000\u4f55\u6167\u4ed9\u53cc\u7738\u4e00\u76f4\uff0c\u201c\u554a\u201d\u4e86\u58f0\u9053\uff1b\u201c\u4f60\u662f\u98de\u513f\uff1f\u600e\u4e48\u53ef\u80fd\uff0c\u98de\u513f\u600e\u4f1a\u6709\u4f60\u8fd9\u6837\u5927\u7684\u5e74\u7eaa\uff1f \u800c\u4e14\u6a21\u6837\u513f\u4e5f\u5b8c\u5168\u4e0d\u50cf\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53cc\u624b\u63ed\u53bb\u4eba\u76ae\u9762\u5177\uff0c\u518d\u53eb\u9053\uff1a\u201c\u5a18\uff01\u73b0\u5728\u60a8\u8be5\u8ba4\u8bc6\u5b69\u513f\u4e86\u5427\uff1f\u201d \u3000\u3000\u5728\u8fd9\u77ac\u95f4\u3002\u4f55\u6167\u4ed9\u7b80\u76f4\u6000\u7591\u81ea\u5df1\u662f\u5728\u68a6\u4e2d\u3002 \u3000\u3000\u5979\u987e\u4e0d\u5f97\u518d\u8bf4\u4ec0\u4e48\uff0c\u7d27\u7d27\u628a\u5cb3\u5c0f\u98de\u6402\u8fdb\u6000\u91cc\uff0c\u6bcd\u5b50\u76f8\u62e5\u800c\u6ce3\uff0c\u4e45\u4e45\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u4f46\u65f6\u95f4\u7d27\u8feb\uff0c\u5cb3\u5c0f\u98de\u4e0d\u6562\u591a\u803d\u8bef\uff0c\u7ad9\u8d77\u8eab\u540e\uff0c\u628a\u6bcd\u4eb2\u6276\u56de\u5ea7\u4f4d\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4fbf\u5c06\u8fd9\u4e8c\u5e74\u591a\u6765\u7684\u7ecf\u8fc7\u4ee5\u53ca\u5982\u4f55\u6765\u5230\u5929\u8c37\uff0c\u5927\u7565\u548c\u6bcd\u4eb2\u53d9\u8ff0\u4e86\u4e00\u904d\u3002 \u3000\u3000\u4ed6\u62c5\u5fc3\u4f1a\u6709\u4eba\u95ef\u8fdb\u6765\uff0c\u53ea\u6709\u628a\u4eba\u76ae\u9762\u5177\u518d\u884c\u6234\u597d\u3002 \u3000\u3000\u4f55\u6167\u4ed9\u53ea\u542c\u5f97\u60b2\u559c\u4ea4\u96c6\uff0c\u64e6\u53bb\u6cea\u75d5\u9053\uff1a\u201c\u98de\u513f\uff0c\u57ce\u4e3b\u628a\u4f60\u7531\u4e94\u5173\u603b\u9547\u53eb\u5230\u80b2\u5316\u57ce\u6765\uff0c\u5230 \u5e95\u662f\u4ec0\u4e48\u7528\u5fc3\u5462\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5b69\u513f\u81f3\u4eca\u4e5f\u5f04\u4e0d\u6e05\u695a\u4e3a\u4ec0\u4e48\uff0c\u4e0d\u8fc7\u5b69\u513f\u770b\u5f97\u51fa\uff0c\u4ed6\u5bf9\u6211\u5f88\u597d\uff0c\u6240\u4ee5\u5b69\u513f\u76f8 \u4fe1\u4e00\u5b9a\u53ef\u4ee5\u628a\u5a18\u4ece\u82e6\u96be\u4e2d\u89e3\u6551\u51fa\u6765\u3002\u201d \u3000\u3000\u4f55\u6167\u4ed9\u4e0d\u89c9\u53c8\u6dcc\u4e0b\u6cea\u6c34\u9053\uff1a\u201c\u5176\u5b9e\u751f\u6d3b\u5728\u8fd9\u91cc\u4e09\u5e74\u591a\uff0c\u8fd9\u70b9\u82e6\u5a18\u8fd8\u53d7\u5f97\u4e86\uff0c\u53ef\u6068\u7684\u662f\u9a6c \u662d\u96c4\u4e00\u76f4\u5bf9\u5a18\u5fc3\u6000\u4e0d\u8f68\uff0c\u6709\u597d\u51e0\u6b21\u5a18\u51e0\u4e4e\u5931\u8eab\u4e8e\u4ed6\uff0c\u8bf4\u8d77\u6765\u591a\u4e8f\u5468\u603b\u7ba1\u89c4\u8beb\u8fc7\u4ed6\uff0c\u5426\u5219\uff0c\u5a18 \u771f\u4e0d\u6562\u5f80\u4e0b\u60f3\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54ac\u7259\u5207\u9f7f\u9053\uff1a\u201c\u5b69\u513f\u4e00\u5b9a\u8bbe\u6cd5\u597d\u597d\u6536\u62fe\u4ed6\uff0c\u5176\u5b9e\u4ed6\u53db\u5e08\u706d\u7956\uff0c\u5055\u5e08\u5a18\u6deb\u5954\uff0c\u65e9\u5df2 \u5c31\u5927\u9006\u4e0d\u9053\uff0c\u7f6a\u4e0d\u53ef\u6055\u4e86\uff0c\u82e5\u8ba9\u8fd9\u79cd\u4eba\u900d\u9065\u6cd5\u5916\uff0c\u5929\u7406\u4f55\u5728\uff1f\u201d \u3000\u3000\u4f55\u6167\u4ed9\u9053\uff1a\u201c\u4e0d\u8fc7\u4f60\u5343\u4e07\u522b\u592a\u9c81\u83bd\uff0c\u542c\u8bf4\u9a6c\u662d\u96c4\u5f88\u53ef\u80fd\u5f97\u5230\u6559\u4e3b\u7684\u4fe1\u4efb\uff0c\u975e\u5230\u5fc5\u8981\uff0c\u4e0d \u53ef\u8f7b\u4e3e\u5984\u52a8\uff01\u201d \u3000\u3000\u201c\u5b69\u513f\u6653\u5f97\uff0c\u542c\u8bf4\u5a18\u88ab\u6253\u8fdb\u80b2\u5316\u57ce\uff0c\u662f\u56e0\u4e3a\u5f97\u7f6a\u4e86\u51e4\u5ae3\u7ea2\uff0c\u5a18\u7a76\u7adf\u662f\u600e\u6837\u5f97\u7f6a\u5979\uff1f\u201d \u3000\u3000\u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u65f6\u95f4\u7d27\u8feb\uff0c\u5a18\u6765\u4e0d\u53ca\u5bf9\u4f60\u7ec6\u8bf4\uff0c\u603b\u4e4b\uff0c\u6211\u548c\u5979\u7684\u7ed3\u6028\uff0c\u5e76\u975e\u65e0\u56e0\u3002\u201d \u3000\u3000\u5979\u8bf4\u5230\u8fd9\u91cc\uff0c\u62c5\u5fc3\u9a6c\u662d\u96c4\u95ef\u8fdb\u6765\uff0c\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u5a18\u8be5\u8d70\u4e86\uff0c\u89c1\u5230\u4f60\u7236\u4eb2\u540e\uff0c\u8981\u4ed6\u4e0d\u5fc5 \u6302\u5fc3\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u968f\u5373\u8d77\u7acb\u9053\uff1a\u201c\u5a18\u8bf7\u6682\u65f6\u5fcd\u4e00\u65f6\u4e4b\u82e6\uff0c\u5b69\u513f\u4e00\u5b9a\u6709\u529e\u6cd5\u6551\u60a8\uff01\u201d \u3000\u3000\u6bcd\u5b50\u4f9d\u4f9d\u800c\u522b\u540e\uff0c\u5cb3\u5c0f\u98de\u4ecd\u7559\u5728\u6d1e\u5ba4\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u7684\u60c5\u7eea\u5df2\u5f97\u5230\u53d1\u6cc4\uff0c\u5fc3\u91cc\u8f7b\u677e\u4e86\u5f88\u591a\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u4e0d\u4e45\uff0c\u5468\u6d77\u5c71\u63a8\u95e8\u800c\u5165\uff0c\u559d\u4e86\u676f\u8336\u9053\uff1a\u201c\u516c\u5b50\u548c\u4f55\u6167\u4ed9\u8c08\u7684\u65f6\u95f4\u8fd8\u771f\u4e0d\u77ed\uff0c\u4f60\u548c \u5979\u5e76\u4e0d\u8ba4\u8bc6\uff0c\u54ea\u6709\u8fd9\u4e48\u591a\u8bdd\u5462\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u957f\u957f\u5401\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u5728\u4e0b\u540c\u60c5\u5979\u4e0d\u5e78\u7684\u906d\u9047\uff0c\u66f4\u4e3a\u5979\u53d7\u51e4\u7edf\u9886\u8bec\u9677\u800c\u4e0d\u5e73\uff0c\u5468\u603b \u7ba1\u662f\u5426\u80fd\u8bbe\u6cd5\u6551\u5979\u4e00\u6551\u5462\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u8e59\u8d77\u4e24\u9053\u6d53\u7709\u9053\uff1a\u201c\u6211\u66fe\u5bf9\u5979\u5e2e\u8fc7\u5fd9\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fd9\u4e8b\u5979\u65b9\u624d\u63d0\u8d77\u8fc7\uff0c\u5979\u5bf9\u5468\u603b\u7ba1\u975e\u5e38\u611f\u6fc0\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u6700\u5927\u7684\u4e00\u4e2a\u96be\u9898\uff0c\u5979\u662f\u88ab\u5bb5\u5173\u51e4\u7edf\u9886\u5728\u6559\u4e3b\u9762\u524d\u544a\u8fc7\u72b6\u7684\uff0c\u53ef\u4ee5\u8bf4\u5979\u662f\u88ab \u6559\u4e3b\u60e9\u7f5a\u7684\uff0c\u516c\u5b50\u4e0d\u59a8\u60f3\u60f3\uff0c\u6211\u662f\u5426\u5e2e\u5f97\u4e0a\u5fd9\uff1f\u201d \u3000\u3000\u4ed6\u77e5\u9053\u5cb3\u5c0f\u98de\u65e0\u6cd5\u56de\u7b54\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u504f\u504f\u7075\u5802\u9999\u4e3b\u9a6c\u662d\u96c4\u4e5f\u662f\u6559\u4e3b\u6d3e\u4e0b\u6765\u7684\uff0c\u6211\u7167\u6837 \u4e5f\u52a8\u4ed6\u4e0d\u5f97\uff0c\u5982\u679c\u80fd\u6362\u4e00\u4f4d\u9999\u4e3b\uff0c\u81f3\u5c11\u6211\u53ef\u4ee5\u628a\u4f55\u6167\u4ed9\u6362\u4e00\u5904\u6bd4\u8f83\u8212\u9002\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u201c\u96be\u9053\u5468\u603b\u7ba1\u4e00\u70b9\u522b\u7684\u529e\u6cd5\u4e5f\u60f3\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u201c\u522b\u7684\u529e\u6cd5\u5f53\u7136\u6709\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u529e\u6cd5\uff1f\u5feb\u544a\u8bc9\u5728\u4e0b\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u662f\u6b63\u5f0f\u7980\u62a5\u57ce\u4e3b\uff0c\u57ce\u4e3b\u662f\u6559\u4e3b\u7684\u5e08\u5144\uff0c\u6559\u4e3b\u4e0d\u80fd\u4e0d\u5356\u4ed6\u8001\u4eba\u5bb6\u7684\u5e10\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u5468\u603b\u7ba1\u5c31\u8be5\u7980\u62a5\u57ce\u4e3b\u624d\u662f\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u6447\u5934\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u6211\u7684\u786e\u66fe\u8003\u8651\u8fc7\u5411\u57ce\u4e3b\u7980\u62a5\uff0c\u4f46\u7ecf\u8fc7\u4ed4\u7ec6\u7422\u78e8\uff0c\u603b\u89c9\u5f97\u4e0d\u59a5\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6709\u4e9b\u4e0d\u89e3\u9053\uff1a\u201c\u5728\u4e0b\u60f3\u4e0d\u51fa\u6709\u4ec0\u4e48\u4e0d\u59a5\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u7406\u7531\u5f88\u7b80\u5355\uff0c\u4f55\u6167\u4ed9\u4eba\u957f\u5f97\u5341\u5206\u6807\u81f4\uff0c\u6211\u82e5\u65e0\u7f18\u65e0\u6545\u7684\u4e3a\u5979\u5411\u57ce\u4e3b\u6c42\u60c5\uff0c \u57ce\u4e3b\u5f88\u53ef\u80fd\u6000\u7591\u6211\u548c\u5979\u6709\u4ec0\u4e48\u4e0d\u53ef\u544a\u4eba\u4e4b\u4e8b\uff0c\u5230\u90a3\u65f6\u5373\u4f7f\u8df3\u5230\u9ec4\u6cb3\u4e5f\u6d17\u4e0d\u6e05\uff0c\u516c\u5b50\u4e0d\u59a8\u60f3\u60f3\uff0c \u6211\u6709\u5fc5\u8981\u4e3a\u81ea\u5df1\u6dfb\u9ebb\u70e6\u5417\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u89c1\u5468\u6d77\u5c71\u8bf4\u51fa\u8fd9\u79cd\u8bdd\uff0c\u81ea\u77e5\u65e0\u6cd5\u52c9\u5f3a\uff0c\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a \u3000\u3000\u201c\u90a3\u5c31\u7b49\u6162\u6162\u518d\u8bf4\u5427\uff0c\u73b0\u5728\u8be5\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4ecd\u56de\u5230\u6628\u665a\u6240\u4f4f\u7684\u8de8\u9662\uff0c\u5468\u6d77\u5c71\u4e5f\u4e00\u76f4\u5728\u65c1\u76f8\u966a\u3002 \u3000\u3000\u4e3a\u4e86\u6551\u6bcd\u4eb2\uff0c\u5cb3\u5c0f\u98de\u5f53\u7136\u5e0c\u671b\u80fd\u65e9\u4e9b\u548c\u80b2\u5316\u57ce\u571f\u4e25\u5bd2\u89c1\u9762\uff0c\u504f\u504f\u4ece\u6628\u5929\u665a\u9910\u540e\u5230\u73b0\u5728\uff0c \u59cb\u7ec8\u6ca1\u6253\u548c\u4e25\u5bd2\u518d\u89c1\u9762\u7684\u673a\u4f1a\u3002 \u3000\u3000\u4ed6\u4e0d\u5f97\u4e0d\u5047\u88c5\u8981\u8d70\uff0c\u5411\u5468\u6d77\u5c71\u9053\uff1a\u201c\u5728\u4e0b\u8be5\u56de\u4e94\u5173\u603b\u9547\u4e86\uff0c\u57ce\u4e3b\u90a3\u91cc\uff0c\u5c31\u8bf7\u603b\u7ba1\u4ee3\u4e3a\u8c22 \u8fc7\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u4f3c\u4e4e\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u516c\u5b50\u600e\u4e48\u80fd\u8d70\uff0c\u636e\u5468\u67d0\u6240\u77e5\uff0c\u57ce\u4e3b\u4e00\u5b9a\u8fd8\u6709\u8981\u7d27\u7684\u4e8b\u548c\u4f60\u5546 \u91cf\u3002\u201d \u3000\u3000\u201c\u5728\u4e0b\u53ea\u662f\u4e94\u5173\u603b\u9547\u4e00\u540d\u5c0f\u5c0f\u7684\u5de1\u5173\u4f7f\u8005\uff0c\u57ce\u4e3b\u600e\u4f1a\u6709\u4e8b\u548c\u6211\u5546\u91cf\u5462\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u795e\u79d8\u4e00\u7b11\uff0c\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u516c\u5b50\u8bf7\u5149\u4f11\u606f\u4e00\u4f1a\u513f\uff0c\u5468\u67d0\u73b0\u5728\u5c31\u53bb\u89c1\u57ce\u4e3b\uff0c\u5f85\u4f1a\u513f \u5c31\u56de\u6765\u7ed9\u4f60\u6d88\u606f\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u8d70\u540e\uff0c\u5cb3\u5c0f\u98de\u6ee1\u8111\u5b50\u53c8\u90fd\u662f\u6bcd\u4eb2\u7684\u5f71\u5b50\u3002\u4ed6\u5df2\u7ecf\u544a\u8bc9\u8fc7\u6bcd\u4eb2\uff0c\u4e00\u5b9a\u53ef\u4ee5\u60f3\u529e\u6cd5\u6551 \u5979\u51fa\u6765\uff0c\u4ee5\u73b0\u5728\u7684\u60c5\u5f62\u770b\u6765\uff0c\u9664\u4e86\u6c42\u57ce\u4e3b\u4e25\u5bd2\u5916\uff0c\u4f3c\u4e4e\u6ca1\u6709\u7b2c\u4e8c\u6761\u8def\u53ef\u8d70\u4e86\uff0c\u4ed6\u65e0\u6ca6\u5982\u4f55\u4e5f \u8981\u628a\u63e1\u4f4f\u8fd9\u673a\u4f1a\u3002 \u3000\u3000\u76f4\u5230\u8fd1\u5348\uff0c\u5468\u6d77\u5c71\u679c\u7136\u56de\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u8feb\u4e0d\u53ca\u5f85\u7684\u95ee\u9053\uff1a\u201c\u603b\u7ba1\u89c1\u5230\u57ce\u4e3b\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u5e26\u7740\u559c\u4e0d\u81ea\u80dc\u7684\u795e\u8272\u9053\uff1a\u201c\u606d\u559c\u516c\u5b50\uff0c\u8d3a\u559c\u516c\u5b50\uff0c\u4f60\u8fc7\u5173\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u832b\u7136\u4e0d\u77e5\u6240\u5bf9\u3002 \u3000\u3000\u5468\u6d77\u5c71\u63a5\u9053\uff1a\u201c\u57ce\u4e3b\u4e00\u751f\uff0c\u6700\u5d07\u62dc\u7684\uff0c\u5c31\u662f\u53e4\u4ee3\u7684\u67f3\u4e0b\u60e0\uff0c\u6240\u4ee5\u4ed6\u5bf9\u4e00\u4e2a\u4eba\u7684\u8003\u6838\uff0c\u4e5f \u4ee5\u662f\u5426\u4e0d\u597d\u8272\u4e3a\u4f9d\u636e\uff0c\u73b0\u5728\u5468\u67d0\u4e0d\u59a8\u5b9e\u5bf9\u516c\u5b50\u8bf4\u660e\u767d\uff0c\u6628\u665a\u4ed6\u8981\u5468\u67d0\u5e26\u516c\u5b50\u5230\u5bcc\u56fd\u57ce\u7f8e\u4eba\u5173 \u53bb\uff0c\u6b63\u662f\u5bf9\u4f60\u7684\u4e00\u79cd\u8003\u6838\u3002\u201d \u3000\u3000\u201c\u603b\u7ba1\u662f\u5426\u5df2\u628a\u6628\u665a\u7684\u60c5\u5f62\u544a\u8bc9\u57ce\u4e3b\u4e86\uff1f\u201d \u3000\u3000\u201c\u5468\u67d0\u5728\u6628\u665a\u4e00\u56de\u6765\u4fbf\u628a\u7ecf\u8fc7\u7980\u62a5\u4e86\u57ce\u4e3b\uff0c\u4ed6\u8001\u4eba\u5bb6\u4e3a\u4e86\u518d\u5bf9\u516c\u5b50\u505a\u8fdb\u4e00\u6b65\u7684\u8003\u6838\uff0c\u4fbf \u53c8\u6d3e\u4e86\u4e00\u4e2a\u53eb\u79c0\u5170\u7684\u4e2b\u5934\u6765\u966a\u5bbf\uff0c\u53ef\u6709\u8fd9\u56de\u4e8b\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6628\u665a\u662f\u6709\u2014\u4e2a\u53eb\u79c0\u5170\u7684\u5230\u6211\u623f\u4e2d\u6765\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u7b11\u9053\uff1a\u201c\u79c0\u5170\u4eca\u5929\u4e00\u65e9\uff0c\u4e5f\u628a\u7ecf\u8fc7\u7980\u62a5\u4e86\u57ce\u4e3b\uff0c\u636e\u8bf4\u516c\u5b50\u4e0d\u4f46\u4e0d\u4e3a\u6240\u52a8\uff0c\u800c\u4e14\u8fd8 \u8eb2\u5230\u53a2\u623f\u91cc\u72ec\u5bbf\uff0c\u8001\u5b9e\u8bb2\uff0c\u82e5\u5f53\u65f6\u516c\u5b50\u628a\u6301\u4e0d\u4f4f\u800c\u5f53\u771f\u548c\u79c0\u5170\u540c\u5bbf\uff0c\u90a3\u5c31\u4e00\u5207\u5168\u5b8c\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u76b1\u8d77\u7709\u5934\u9053\uff1a\u201c\u57ce\u4e3b\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u8003\u6838\u5728\u4e0b\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u7b11\u9053\uff1a\u201c\u5f88\u7b80\u5355\uff0c\u5982\u679c\u4e0d\u662f\u628a\u516c\u5b50\u8c03\u5230\u80b2\u5316\u57ce\u62c5\u4efb\u91cd\u8981\u804c\u52a1\uff0c\u4fbf\u662f\u53e6\u5916\u6709\u5176\u4ed6\u7684 \u597d\u4e8b\uff0c\u4eca\u5929\u4e2d\u5348\uff0c\u57ce\u4e3b\u53c8\u8981\u8bbe\u7b75\u62db\u5f85\u4f60\uff0c\u5230\u65f6\u5019\u4ed6\u8001\u4eba\u5bb6\u4e00\u5b9a\u4f1a\u5f53\u9762\u548c\u4f60\u8bf4\u660e\u767d\u3002\u201d \u3000\u3000\u679c\u7136\u5c31\u5728\u4e0d\u4e45\u4e4b\u540e\uff0c\u3000\u2014\u540d\u5927\u6c49\u524d\u6765\u901a\u77e5\uff0c\u8bf4\u662f\u57ce\u4e3b\u8bf7\u5cb3\u516c\u5b50\u5728\u5185\u5e9c\u5ba2\u5385\u89c1\u9762\u3002 \u3000\u3000\u5468\u6d77\u5c71\u5fc3\u91cc\u6709\u6570\uff0c\u51e1\u662f\u80fd\u88ab\u57ce\u4e3b\u8bf7\u8fdb\u5185\u5e9c\u5ba2\u5385\u7684\uff0c\u5fc5\u5b9a\u5173\u7cfb\u4e0d\u540c\u5bfb\u5e38\uff0c\u5cb3\u5c0f\u98de\u80fd\u53d7\u57ce\u4e3b \u5982\u6b64\u770b\u5f85\uff0c\u679c\u7136\u4e0d\u51fa\u4ed6\u7684\u9884\u6599\u3002 \u3000\u3000\u4ed6\u966a\u5cb3\u5c0f\u98de\u6765\u5230\u5185\u5e9c\u5ba2\u5385\uff0c\u4e25\u5bd2\u5df2\u7ecf\u7b49\u5728\u90a3\u91cc\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8fde\u5fd9\u8eac\u8eab\u4e00\u793c\u9053\uff1a\u201c\u665a\u8f88\u62dc\u89c1\u57ce\u4e3b\uff01\u201d \u3000\u3000\u4e25\u5bd2\u7b11\u5bb9\u53ef\u63ac\u7684\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u8bf7\u5750\uff01\u201d \u3000\u3000\u4e00\u9762\u5429\u5490\u5468\u6d77\u5c71\u9053\uff1a\u201c\u4f60\u53bb\u5fd9\u4f60\u7684\u5427\uff0c\u8001\u592b\u8981\u548c\u5cb3\u516c\u5b50\u5355\u72ec\u8c08\u8c08\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6b63\u895f\u5371\u5750\uff0c\u4ed6\u660e\u767d\uff0c\u4e25\u5bd2\u5df2\u5bf9\u81ea\u5df1\u6709\u4e86\u597d\u5370\u8c61\uff0c\u8fd9\u5370\u8c61\u5fc5\u987b\u66f4\u52a0\u4fdd\u6301\uff0c\u4e3a\u4e86\u6551\u6bcd \u4eb2\uff0c\u4ed6\u4e0d\u80fd\u4e0d\u5c3d\u91cf\u8ba8\u597d\u5bf9\u65b9\u3002 \u3000\u3000\u4e25\u5bd2\u559d\u4e86\u53e3\u8336\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u8001\u592b\u5192\u6627\u95ee\u53e5\u8bdd\uff0c\u4f60\u8ba2\u8fc7\u4eb2\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5185\u5fc3\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u4e0a\u6b21\u6559\u4e3b\u53ec\u89c1\u65f6\u4e5f\u662f\u95ee\u7684\u8fd9\u53e5\u8bdd\uff1f\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bb0\u5f97\u4f3c\u4e4e\u6709\u4eba\u5bf9\u4ed6\u8bf4\u8fc7\uff0c\u6559\u4e3b\u5f88\u53ef\u80fd\u51c6\u5907\u628a\u4e09\u516c\u4e3b\u4e0e\u81ea\u5df1\u8ba2\u4eb2\uff0c\u4f46\u8fd9\u53ea\u662f\u731c\u6d4b\u800c\u5df2\uff0c \u6559\u4e3b\u5f53\u65f6\u5e76\u672a\u660e\u8a00\u3002 \u3000\u3000\u6b63\u56e0\u4e3a\u8fd9\u7f18\u6545\uff0c\u6b64\u523b\u4ed6\u53ea\u80fd\u4f4e\u4e0b\u5934\u9053\uff1a\u201c\u665a\u8f88\u8fd8\u4e0d\u66fe\u8ba2\u4eb2\u3002\u201d \u3000\u3000\u4e25\u5bd2\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u542c\u8bf4\u524d\u51e0\u5929\u6559\u4e3b\u4e5f\u66fe\u53ec\u89c1\u4f60\uff0c\u6559\u4e3b\u66fe\u5bf9\u4f60\u8bf4\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6559\u4e3b\u4e5f\u95ee\u8fc7\u540c\u6837\u7684\u8bdd\u3002\u201d \u3000\u3000\u4e25\u5bd2\u4f3c\u662f\u6709\u4e9b\u5403\u60ca\uff0c\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u6559\u4e3b\u662f\u5426\u8bf4\u8fc7\u8981\u628a\u4e09\u516c\u4e3b\u548c\u4f60\u8ba2\u4eb2\u7684\u4e8b\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff0c\u201c\u6559\u4e3b\u95ee\u8fc7\u4e4b\u540e\uff0c\u5c31\u4e0d\u66fe\u518d\u63d0\u4e86\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9762\u73b0\u559c\u8272\u9053\uff1a\u201c\u90a3\u5c31\u597d\uff0c\u8001\u592b\u51b3\u5b9a\u8ba9\u5c0f\u5973\u548c\u4f60\u8ba2\u4eb2\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5148\u524d\u867d\u5df2\u731c\u51fa\u4e25\u5bd2\u7684\u5fc3\u610f\uff0c\u5374\u6ca1\u6599\u5230\u4ed6\u4f1a\u5982\u6b64\u5e72\u8106\u3002 \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u6014\u4e4b\u540e\u9053\uff1a\u201c\u665a\u8f88\u53ea\u6015\u548c\u5927\u5c0f\u59d0\u5e74\u5c81\u4e0d\u5927\u76f8\u5f53\u3002\u201d \u3000\u3000\u4e25\u5bd2\u7b11\u9053\uff1b\u201c\u8001\u592b\u8bf4\u7684\u5f53\u7136\u4e0d\u662f\u4f60\u89c1\u8fc7\u7684\u90a3\u4e2a\uff0c\u8001\u592b\u6709\u5c0f\u5973\u4e24\u4eba\uff0c\u8001\u5927\u5982\u51b0\uff0c\u4f60\u662f\u89c1\u8fc7 \u4e86\uff0c\u6b21\u5973\u5982\u971c\uff0c\u6b63\u597d\u548c\u4f60\u540c\u9f84\uff0c\u82e5\u8bba\u5979\u7684\u54c1\u8c8c\uff0c\u6bd4\u5979\u59d0\u59d0\u5c24\u80dc\u4e09\u5206\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8138\u4e0a\u4e00\u70ed\u9053\uff1a\u201c\u8499\u57ce\u4e3b\u539a\u7231\uff0c\u665a\u8f88\u611f\u6fc0\u4e0d\u5c3d\uff0c\u4f46\u665a\u8f88\u8eab\u4efd\u5351\u5fae\uff0c\u53ea\u6015\u4e0d\u5b9c\u9ad8\u6500\u3002\u201d \u3000\u3000\u4e25\u5bd2\u5475\u5475\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff1f\u8001\u592b\u4e00\u5411\u7231\u7684\u662f\u4eba\u624d\uff0c\u4eba\u4e0e\u4eba\u4e4b\u95f4\u6839\u672c\u6ca1\u6709\u8d35\u8d31\u4e4b\u5206\uff0c \u60f3\u5f53\u5e74\u67f3\u8fce\u6625\u5ac1\u7ed9\u859b\u4ec1\u8d35\u3001\u7389\u5b9d\u948f\u5ac1\u7ed9\u859b\u5e73\u8d35\uff0c\u4f55\u66fe\u5728\u8eab\u4efd\u4e0a\u8ba1\u8f83\uff0c\u8fd8\u4e0d\u7167\u6837\u4f20\u4e3a\u5343\u53e4\u4f73 \u8bdd\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e3a\u4e86\u6551\u6bcd\u4eb2\uff0c\u5f53\u7136\u4e0d\u80fd\u518d\u63a8\u8f9e\uff0c\u6b63\u8981\u8d77\u8eab\u91cd\u65b0\u62dc\u89c1\uff0c\u4e25\u5bd2\u5374\u6447\u6447\u624b\u9053\uff1a\u201c\u65e2\u7136\u4e00 \u8a00\u4e3a\u5b9a\uff0c\u5c0f\u5973\u5982\u971c\u4ee5\u540e\u5c31\u662f\u4f60\u7684\u4eba\u4e86\uff0c\u4e0d\u8fc7\u4f60\u4eec\u76ee\u524d\u5e74\u7eaa\u90fd\u8fd8\u592a\u5c0f\uff0c\u6b63\u5f0f\u6210\u5a5a\uff0c\u5fc5\u987b\u5ef6\u540e\u51e0 \u5e74\uff0c\u81f3\u4e8e\u4f60\u6211\uff0c\u6682\u65f6\u4e5f\u4e0d\u5fc5\u4ee5\u7fc1\u5a7f\u76f8\u79f0\uff0c\u603b\u4e4b\uff0c\u8fd9\u4e8b\u73b0\u5728\u53ea\u6709\u8001\u592b\u53ca\u5185\u4eba\u4ee5\u53ca\u5c0f\u5973\u5982\u51b0\u3001\u5982 \u971c\u51e0\u4eba\u77e5\u9053\uff0c\u4e0d\u5fc5\u5bf9\u5916\u5ba3\u626c\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4e0d\u77e5\u4e8c\u5c0f\u59d0\u662f\u5426\u540c\u610f\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u7b11\u9053\uff1a\u201c\u5f53\u8001\u592b\u5728\u5927\u5385\u62db\u5f85\u4f60\u65f6\uff0c\u5979\u65e9\u5df3\u5728\u5c4f\u98ce\u540e\u770b\u8fc7\u591a\u6b21\u4e86\uff0c\u90a3\u5929\u5982\u51b0\u6539\u626e\u6210\u8499 \u9762\u4eba\u548c\u4f60\u6bd4\u6b66\uff0c\u5979\u770b\u5f97\u6bd4\u8c01\u90fd\u7559\u610f\u3002\u5982\u679c\u4e0d\u662f\u6211\u548c\u5185\u4eba\u4e0d\u51c6\uff0c\u4e5f\u8bb8\u5979\u65e9\u5c31\u81ea\u52a8\u51fa\u6765\u548c\u4f60\u89c1\u9762 \u4e86\u3002\u201d \u3000\u3000\u8bf4\u6d3b\u95f4\u5df2\u6709\u51e0\u540d\u4f8d\u5973\u7aef\u8fdb\u9152\u6765\u3002 \u3000\u3000\u8fd9\u6b21\u53ea\u6709\u4e25\u5bd2\u548c\u5cb3\u5c0f\u98de\u4e24\u4eba\u540c\u5e2d\uff0c\u8fde\u4e25\u5927\u4eba\u548c\u5927\u5c0f\u59d0\u4e5f\u6ca1\u51fa\u6765\uff0c \u3000\u3000\u5e2d\u95f4\uff0c\u5cb3\u5c0f\u98de\u9891\u9891\u5411\u4e25\u5bd2\u656c\u9152\u3002 \u3000\u3000\u4ed6\u7684\u76ee\u7684\uff0c\u4e0d\u5916\u662f\u8d81\u9152\u9163\u8033\u70ed\u4e4b\u9645\uff0c\u4ee5\u4fbf\u63d0\u8d77\u6bcd\u4eb2\u5728\u7075\u5802\u53d7\u82e6\u4e4b\u4e8b\u3002 \u3000\u3000\u4e25\u5bd2\u65e2\u5f97\u4e58\u9f99\u5feb\u5a7f\uff0c\u5fc3\u91cc\u9ad8\u5174\uff0c\u5373\u4f7f\u5cb3\u5c0f\u98de\u4e0d\u529d\u9152\uff0c\u4ed6\u4e5f\u8981\u5f00\u6000\u7545\u996e\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4ed6\u53d1\u73b0\u5cb3\u5c0f\u98de\u5728\u656c\u9152\u65f6\u6697\u81ea\u843d\u4e0b\u773c\u6cea\u3002 \u3000\u3000\u201c\u5cb3\u516c\u5b50\uff0c\u4f60\u2026\u2026\u4f60\u662f\u600e\u4e48\u5566\uff1f\u2026\u2026\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7acb\u523b\u79bb\u5e2d\u53cc\u819d\u8dea\u5012\u5728\u5730\u9053\uff1a\u201c\u665a\u8f88\u6c42\u57ce\u4e3b\u6559\u6551\u5bb6\u6bcd\uff01\u201d \u3000\u3000\u4e25\u5bd2\u6115\u7136\u9053\uff1b\u201c\u5feb\u8d77\u6765\uff0c\u6709\u8bdd\u6162\u6162\u8bb2\uff0c\u4ee4\u5802\u5728\u54ea\u91cc\uff0c\u8001\u592b\u600e\u80fd\u6551\u5f97\u4e86\u5979\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u62ed\u53bb\u6cea\u75d5\uff0c\u91cd\u65b0\u5165\u5e2d\u9053\uff1a\u201c\u57ce\u4e3b\u53ef\u77e5\u9053\u665a\u8f88\u4e0a\u5348\u5230\u8fc7\u7075\u5802\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u8001\u592b\u542c\u5468\u603b\u7ba1\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u201c\u665a\u8f88\u524d\u5929\u968f\u5927\u516c\u4e3b\u6765\u65f6\uff0c\u4e5f\u66fe\u5230\u8fc7\u7075\u5802\uff0c\u65e0\u610f\u4e2d\u53d1\u73b0\u5bb6\u6bcd\u7adf\u7136\u5728\u7075\u5802\u4e2d\u505a\u4e86\u4e00\u540d\u5b88\u7075 \u5974\uff01\u201d \u3000\u3000\u201c\u4ee4\u5802\u662f\u600e\u6837\u8fdb\u5165\u5929\u8c37\u7684\uff1f\u201d \u3000\u3000\u201c\u5bb6\u6bcd\u4e09\u5e74\u524d\u6487\u4e0b\u665a\u8f88\uff0c\u51b3\u5fc3\u6295\u5165\u5929\u8c37\u6548\u5fe0\u6559\u4e3b\uff0c\u5979\u4e5f\u662f\u901a\u8fc7\u4e94\u5173\u8fdb\u6765\u7684\uff0c\u540e\u6765\u4e0d\u77e5\u4e3a \u4ec0\u4e48\u5f97\u7f6a\u4e86\u5bb5\u5173\u7684\u51e4\u7edf\u9886\uff0c\u800c\u88ab\u6253\u8fdb\u80b2\u6bd4\u57ce\u7075\u5802\u91cc\u6765\u3002\u201d \u3000\u3000\u201c\u4ee4\u5802\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u201c\u5bb6\u6bcd\u59d3\u4f55\u8bb3\u6167\u4ed9\u3002\u201d \u3000\u3000\u201c\u4f55\u6167\u4ed9\uff1f\u2026\u2026\u201d\u4e25\u5bd2\u82e5\u6709\u6240\u601d\u7684\u9053\uff1a\u201c\u8001\u592b\u542c\u8bf4\u8fc7\u8fd9\u540d\u5b57\uff0c\u662f\u5468\u603b\u7ba1\u5076\u7136\u63d0\u8d77\u8fc7\u7684\uff0c \u4f60\u4eca\u5929\u4e0a\u5348\u53ef\u548c\u4ee4\u5802\u6b63\u5f0f\u89c1\u8fc7\u9762\uff1f\u201d \u3000\u3000\u201c\u627f\u5468\u603b\u7ba1\u5e2e\u5fd9\uff0c\u665a\u8f88\u548c\u5bb6\u6bcd\u89c1\u8fc7\u3002\u201d \u3000\u3000\u201c\u4ee4\u5c0a\u5462\uff1f\u201d \u3000\u3000\u201c\u5bb6\u7236\u65e9\u5728\u51e0\u5348\u524d\u79bb\u5bb6\u51fa\u8d70\uff0c\u5230\u73b0\u5728\u97f3\u4fe1\u5168\u65e0\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u6c42\u8001\u592b\u4e3a\u4ee4\u5802\u8131\u7f6a\uff0c\u4ee5\u76ee\u524d\u4f60\u6211\u7684\u5173\u7cfb\uff0c \u3000\u3000\u53ea\u662f\u5c0f\u4e8b\u4e00\u4ef6\uff0c\u5f85\u4f1a\u513f\u8001\u592b\u5c31\u4e0b\u4ee4\u8981\u4ee4\u5802\u5230\u8fd9\u91cc\u6765\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u559c\u51fa\u671b\u5916\uff0c\u6b63\u6b32\u518d\u79bb\u4f4d\u62dc\u8c22\uff0c\u4e25\u5bd2\u5df2\u62ac\u624b\u963b\u6b62\u9053\uff1a\u201c\u4e0d\u5fc5\u62dc\u8c22\uff0c\u5426\u5219\u90a3\u5c31\u662f\u89c1\u5916 \u4e86\uff0c\u53ea\u662f\u4ee4\u5802\u79bb\u5f00\u7075\u5802\u4ee5\u540e\u5982\u4f55\u5b89\u6392\uff0c\u4f60\u53ef\u6709\u4ec0\u4e48\u9ad8\u89c1\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u665a\u8f88\u5e0c\u671b\u80fd\u8ba9\u5bb6\u6bcd\u6682\u65f6\u7559\u5728\u57ce\u5e9c\uff0c\u81f3\u4e8e\u4ee5\u540e\u5982\u4f55\u5b89\u7f6e\uff0c\u5f53\u7136\u4ecd\u987b\u4ef0\u4ed7\u57ce\u4e3b \u9f0e\u529b\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9894\u9996\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u4e0d\u8fc7\u4f9d\u8001\u592b\u4e4b\u89c1\uff0c\u6700\u597d\u6682\u65f6\u522b\u8ba9\u4eba\u77e5\u9053\u4f60\u4eec\u662f\u6bcd\u5b50\u5173\u7cfb\uff0c\u4ee5\u514d\u53c8 \u751f\u51fa\u4e0d\u5fc5\u8981\u7684\u679d\u8282\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4f46\u51ed\u57ce\u4e3b\u4f5c\u4e3b\u3002\u201d \u3000\u3000\u4e25\u5bd2\u505a\u4e8b\u679c\u771f\u723d\u5feb\uff0c\u968f\u5373\u6d3e\u4eba\u628a\u603b\u7ba1\u5468\u6d77\u5c71\u627e\u4e86\u6765\u3002 \u3000\u3000\u53ca\u5230\u5468\u6d77\u5c71\u6765\u5230\uff0c\u9152\u83dc\u5df2\u7136\u64a4\u53bb\u3002 \u3000\u3000\u5468\u6d77\u5c71\u6df1\u65bd\u4e00\u793c\u9053\uff1a\u201c\u57ce\u4e3b\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u65b9\u624d\u548c\u8001\u592b\u8c08\u8d77\u7075\u5802\u6709\u4f4d\u5973\u5b88\u62a4\u5974\uff0c\u540d\u53eb\u4f55\u6167\u4ed9\uff0c\u636e\u8bf4\u8fd9\u4eba\u662f\u53d7\u4e86\u51a4 \u5c48\u88ab\u5bb5\u5173\u7684\u51e4\u7edf\u987f\u9677\u5bb3\uff0c\u8001\u592b\u5f88\u540c\u60c5\u5979\u7684\u4e0d\u5e78\u906d\u9047\uff0c\u51b3\u5b9a\u6682\u65f6\u628a\u5979\u8c03\u5230\u5e9c\u91cc\u6765\uff0c\u4f60\u9a6c\u4e0a\u5230\u7075 \u5802\u628a\u5979\u5e26\u5230\u8fd9\u91cc\uff01\u201d \u3000\u3000\u5468\u6d77\u5c71\u4f3c\u662f\u9762\u6709\u96be\u8272\uff0c\u8fd1\u524d\u4e24\u6b65\u9053\uff1a\u201c\u4f9d\u5c5e\u4e0b\u611a\u89c1\uff0c\u6700\u597d\u5148\u628a\u9a6c\u9999\u4e3b\u53eb\u6765\uff0c\u57ce\u4e3b\u5148\u548c\u4ed6 \u8bf4\u660e\u4e4b\u540e\uff0c\u518d\u628a\u4f55\u6167\u4ed9\u53eb\u6765\u8f83\u4e3a\u5408\u9002\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9762\u73b0\u4e0d\u60a6\u4e4b\u8272\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u5148\u77e5\u4f1a\u4ed6\uff1f\u9a6c\u662d\u96c4\u7b97\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u5e26\u70b9\u5c34\u5c2c\u795e\u8272\u966a\u7b11\u9053\uff1a\u201c\u57ce\u4e3b\u4e00\u5b9a\u77e5\u9053\uff0c\u9a6c\u662d\u96c4\u662f\u6559\u4e3b\u76f4\u63a5\u6d3e\u4e0b\u6765\u7684\uff0c\u4f55\u6167\u4ed9\u4e5f \u662f\u6559\u4e3b\u4eb2\u81ea\u88c1\u793a\u6253\u5230\u672c\u57ce\u7075\u5802\u505a\u5b88\u7075\u5974\u7684\u3002\u82e5\u57ce\u4e3b\u4e0d\u7ecf\u9a6c\u662d\u96c4\u76f4\u63a5\u628a\u5979\u8c03\u5230\u5e9c\u91cc\u6765\uff0c\u53ea\u6015\u5bf9 \u6559\u4e3b\u7684\u9762\u5b50\u4e0a\u4e0d\u5927\u597d\u770b\uff0c\u4f55\u51b5\u9a6c\u662d\u96c4\u662f\u4e2a\u5c0f\u4eba\uff0c\u4e07\u4e00\u4ed6\u5728\u6559\u4e3b\u9762\u524d\u6311\u62e8\u662f\u975e\uff0c\u4e5f\u5f88\u5bb9\u6613\u4f24\u4e86 \u57ce\u4e3b\u548c\u6559\u4e3b\u4e4b\u95f4\u7684\u611f\u60c5\u3002\u201d \u3000\u3000\u4e25\u5bd2\u6c89\u541f\u4e86\u534a\u664c\u9053\uff1a\u201c\u8bf4\u7684\u4e5f\u662f\uff0c\u4f60\u5c31\u5148\u4f20\u4ee4\u8981\u9a6c\u662d\u96c4\u5230\u8fd9\u91cc\u6765\uff01\u201d \u3000\u3000\u5468\u6d77\u5c71\u5e94\u58f0\u800c\u4e0a\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8f7b\u541f\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u665a\u8f88\u5f88\u4f69\u670d\u5468\u603b\u7ba1\uff0c\u56e0\u4e3a\u4ed6\u5f88\u6709\u77e5\u4eba\u4e4b\u660e\u3002\u201d \u3000\u3000\u4e25\u5bd2\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u8bdd\u600e\u4e48\u8bb2\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4ed6\u80fd\u77e5\u9053\u9a6c\u662d\u96c4\u662f\u5c0f\u4eba\uff0c\u4fbf\u8db3\u4ee5\u8bc1\u660e\u4ed6\u6709\u77e5\u4eba\u4e4b\u660e\u3002\u201d \u3000\u3000\u4e25\u5bd2\u6123\u4e86\u4e00\u6123\u9053\uff1a\u201c\u83ab\u975e\u4f60\u4e5f\u8ba4\u8bc6\u9a6c\u662d\u96c4\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u4ece\u524d\u548c\u4ed6\u867d\u4ec5\u89c1\u8fc7\u4e00\u9762\uff0c\u4f46\u5bf9\u4ed6\u7684\u4e3a\u4eba\u5374\u77e5\u4e4b\u751a\u8be6\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u8bf4\u770b\uff01\u201d \u3000\u3000\u201c\u4ed6\u662f\u4e2d\u539f\u6b66\u6797\u540d\u5bbf\u5723\u624b\u4e66\u751f\u5e90\u4e91\u7684\u5f1f\u5b50\uff0c\u540e\u6765\u4e0d\u4f46\u53db\u51fa\u5e08\u95e8\uff0c\u800c\u4e14\u505a\u51fa\u4e71\u4f26\u4e4b\u4e8b\uff0c\u7adf \u7136\u548c\u5e90\u4e91\u7684\u59bb\u5b50\u6deb\u5954\u3002\u201d \u3000\u3000\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u5e90\u4e91\u7684\u59bb\u5b50\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u76ee\u524d\u7684\u5bb5\u5173\u7edf\u9886\u51e4\u5ae3\u7ea2\uff01\u201d \u3000\u3000\u4e25\u5bd2\u53ea\u542c\u5f97\u6012\u706b\u76f4\u51b2\u800c\u8d77\uff0c\u4ed6\u4e00\u751f\u6700\u6068\u7684\u5c31\u662f\u597d\u8272\u4e4b\u5f92\uff0c\u800c\u9a6c\u662d\u96c4\u7684\u884c\u4e3a\u5c45\u7136\u662f\u4e71\u4f26\uff0c \u8fd9\u8ba9\u4ed6\u5982\u4f55\u80fd\u5fcd\u5f97\u4e0b\uff0c\u4e0d\u89c9\u84e6\u5730\u4e00\u62cd\u5927\u817f\uff0c\u54ac\u7259\u5207\u9f7f\u7684\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u53ef\u6076\u7684\u4e1c\u897f\uff0c\u5cb3\u516c\u5b50\uff0c \u8fd9\u8be5\u4e0d\u662f\u9a97\u6211\u7684\u5427\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6b63\u8272\u7684\u9053\uff1a\u201c\u4e0d\u7792\u57ce\u4e3b\uff0c\u4e09\u5e74\u524d\u5f53\u5bb6\u6bcd\u6295\u5954\u5929\u8c37\u6709\u610f\u6548\u5fe0\u7687\u5143\u6559\u65f6\uff0c\u5c31\u628a\u665a\u8f88\u6258 \u4ed8\u5723\u624b\u4e66\u751f\u5e90\u5927\u4fa0\u629a\u517b\u7684\uff0c\u5426\u5219\u665a\u8f88\u5bf9\u9a6c\u662d\u96c4\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u600e\u4f1a\u5982\u6b64\u6e05\u695a\uff1f\u50cf\u8fd9\u79cd\u79bd\u517d\u4e0d\u5982 \u7684\u4eba\uff0c\u5982\u4eca\u4e0d\u4f46\u900d\u9065\u6cd5\u5916\uff0c\u800c\u4e14\u5c45\u7136\u53d7\u5230\u6559\u4e3b\u91cd\u7528\uff0c\u600e\u4e0d\u4ee4\u4eba\u4e3a\u4e4b\u6c14\u7ed3\uff01\u201d \u3000\u3000\u4e25\u5bd2\u54ac\u4e86\u54ac\u7259\u9053\uff1a\u201c\u96be\u602a\u8001\u592b\u65e9\u5c31\u770b\u8fd9\u5c0f\u5b50\u4e0d\u987a\u773c\uff0c\u539f\u6765\u4ed6\u6bd4\u8001\u592b\u60f3\u8c61\u7684\u66f4\u574f\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u9a6c\u662d\u96c4\u5230\u6765\u65f6\uff0c\u6700\u597d\u6682\u65f6\u522b\u6b62\u4ed6\u770b\u5230\u665a\u8f88\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u90a3\u662f\u81ea\u7136\uff0c\u5ba2\u5385\u9694\u58c1\uff0c\u5c31\u662f\u8001\u592b\u7684\u4e66\u623f\uff0c\u4f60\u4e0d\u59a8\u6682\u65f6\u5148\u5230\u6211\u4e66\u623f\u91cc\u53bb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u968f\u5373\u7531\u58c1\u95e8\u8fdb\u5165\u4e66\u623f\u3002 \u3000\u3000\u987f\u996d\u5de5\u592b\u4e4b\u540e\uff0c\u5468\u6d77\u5c71\u679c\u7136\u966a\u540c\u9a6c\u662d\u96c4\u8fdb\u5165\u5ba2\u5385\u3002 \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u72d7\u7537\u9e1f\u5973 \u3000\u3000\u9a6c\u662d\u96c4\u867d\u7136\u548c\u5468\u6d77\u5c71\u4e00\u8def\u540c\u6765\uff0c\u5374\u5e76\u4e0d\u6e05\u695a\u57ce\u4e3b\u4f55\u6545\u53ec\u89c1\u4ed6\u3002 \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u5468\u6d77\u5c71\u4e8b\u5148\u5e76\u672a\u5411\u4ed6\u900f\u9732\u4efb\u4f55\u53e3\u98ce\u3002 \u3000\u3000\u6b64\u523b\uff0c\u9a6c\u662d\u96c4\u89c1\u4e25\u5bd2\u8138\u8272\u50b2\u7136\u5750\u5728\u90a3\u91cc\uff0c\u96be\u514d\u6709\u4e9b\u5403\u60ca\u3002 \u3000\u3000\u4ed6\u89c1\u8fc7\u793c\u540e\uff0c\u5782\u624b\u8eac\u8eab\u7ad9\u5728\u4e00\u65c1\uff0c\u7b49\u5019\u57ce\u4e3b\u6709\u4f55\u4ee4\u8c15\u793a\u4e0b\u3002 \u3000\u3000\u4e25\u5bd2\u770b\u4e5f\u6ca1\u770b\u9a6c\u662d\u690e\u4e00\u773c\uff0c\u4e0d\u52a8\u58f0\u8272\u95ee\u9053\uff1a\u201c\u7075\u5802\u91cc\u53ef\u6709\u4e00\u4f4d\u53eb\u4f55\u6167\u4ed9\u7684\u5973\u5974\u5417\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u8c28\u58f0\u9053\uff1a\u201c\u6709\u7684\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u8001\u592b\u6709\u610f\u628a\u5979\u8c03\u5230\u5e9c\u4e0a\u6765\uff0c\u4f60\u53ef\u6709\u4ec0\u4e48\u610f\u89c1\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u8138\u8272\u53d8\u9053\uff1a\u201c\u542f\u7980\u57ce\u4e3b\uff0c\u4f7f\u4e0d\u5f97\uff01\u201d \u3000\u3000\u4e25\u5bd2\u51b7\u7b11\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4f7f\u4e0d\u5f97\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u9053\uff1a\u201c\u4f55\u6167\u4ed9\u662f\u603b\u575b\u6709\u6848\u7684\u5973\u72af\uff0c\u5f53\u521d\u662f\u6559\u4e3b\u4eb2\u81ea\u6279\u793a\u6253\u5230\u672c\u7075\u5802\u7684\uff0c\u800c\u4e14\u5c5e\u4e0b \u5949\u547d\u6765\u672c\u57ce\u7075\u5802\u62c5\u4efb\u9999\u4e3b\u65f6\uff0c\u6559\u4e3b\u8fd8\u66fe\u7279\u522b\u4f20\u8c15\u5bf9\u5979\u8981\u597d\u597d\u770b\u7ba1\uff0c\u82e5\u51fa\u4e86\u5dee\u9519\uff0c\u552f\u5351\u804c\u662f\u95ee\uff0c \u5351\u804c\u4e0d\u5f97\u4e0d\u628a\u8fd9\u6bb5\u7ecf\u8fc7\u7980\u62a5\u57ce\u4e3b\u3002\u201d \u3000\u3000\u4e25\u5bd2\u563f\u563f\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u4f9d\u4f60\u4e4b\u89c1\uff0c\u8001\u592b\u8be5\u600e\u4e48\u624d\u80fd\u628a\u5979\u8c03\u51fa\u6765\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u9053\uff1a\u201c\u4f55\u6167\u4ed9\u672c\u6765\u8eab\u72af\u6b7b\u7f6a\uff0c\u6559\u4e3b\u628a\u5979\u6253\u5165\u7075\u5802\u4e3a\u5974\uff0c\u5df2\u7b97\u662f\u5bf9\u5979\u4ece\u8f7b\u53d1\u843d\u4e86\uff0c \u6ca1\u6709\u73b0\u7531\u628a\u5979\u653e\u51fa\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u8001\u592b\u975e\u8981\u628a\u5979\u8c03\u5230\u5e9c\u91cc\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u8bf7\u6055\u5351\u804c\u4e0d\u6562\u4f5c\u4e3b\uff0c\u57ce\u4e3b\u6700\u597d\u5148\u5411\u6559\u4e3b\u8bf7\u51c6\u624d\u6210\u3002\u201d \u3000\u3000\u201c\u975e\u8fd9\u6837\u9ebb\u70e6\u4e0d\u53ef\u5417\uff1f\u201d \u3000\u3000\u201c\u5e76\u975e\u9ebb\u70e6\u4e0d\u9ebb\u70e6\u7684\u95ee\u9898\uff0c\u800c\u662f\u4e0d\u8fd9\u6837\u505a\u5351\u804c\u4e0d\u6562\u653e\u4eba\u3002\u201d \u3000\u3000\u4e25\u5bd2\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u9a6c\u9999\u4e3b\uff0c\u4f60\u8fd1\u524d\u51e0\u6b65\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u4f9d\u8a00\u6765\u5230\u4e25\u5bd2\u8eab\u524d\u3002 \u3000\u3000\u7a81\u89c1\u4e25\u5bd2\u62ff\u81c2\u75be\u6325\uff0c\u731b\u5730\u4e00\u638c\uff0c\u7529\u4e0a\u9a6c\u662d\u96c4\u9762\u988a\u3002 \u3000\u3000\u66b4\u54cd\u58f0\u4e2d\uff0c\u9a6c\u662d\u96c4\u4e00\u58f0\u95f7\u54fc\uff0c\u76f4\u6454\u5230\u58c1\u89d2\u8fb9\uff0c\u624d\u88ab\u5899\u58c1\u6321\u4f4f\u3002 \u3000\u3000\u5f85\u4ed6\u7a33\u4f4f\u8eab\u5f62\uff0c\u53e3\u9f3b\u95f4\u5df2\u662f\u9c9c\u8840\u76f4\u6d41\u3002 \u3000\u3000\u4e25\u5bd2\u6012\u76ee\u559d\u9053\uff1a\u201c\u6df7\u86cb\uff0c\u8001\u592b\u60f3\u8c03\u4eba\u8fd8\u8981\u7ed9\u4f60\u8bb2\uff1f\u4f60\u7b97\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff1f\u8fd9\u80b2\u5316\u57ce\u5f52\u8001\u592b\u638c \u7ba1\uff0c\u8001\u592b\u8981\u600e\u6837\u5c31\u600e\u4e48\u6837\uff0c\u52a8\u4e0d\u52a8\u5c31\u628a\u6559\u4e3b\u62ac\u51fa\u6765\uff0c\u96be\u9053\u6559\u4e3b\u8fd8\u80fd\u538b\u6b7b\u4eba\u4e0d\u6210\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u6342\u7740\u9762\u988a\uff0c\u6562\u6012\u800c\u4e0d\u6562\u8a00\u3002 \u3000\u3000\u5468\u6d77\u5c71\u5fd9\u9053\uff1a\u201c\u7980\u57ce\u4e3b\uff0c\u4f60\u8001\u4eba\u5bb6\u4f55\u5fc5\u53d1\u8fd9\u4e48\u5927\u813e\u6c14\uff0c\u53eb\u4ed6\u56de\u53bb\u628a\u4f55\u6167\u4ed9\u9001\u5230\u8fd9\u91cc\u6765\u5c31 \u662f\u3002\u201d \u3000\u3000\u4e25\u5bd2\u63a0\u4e86\u9a6c\u662d\u96c4\u4e00\u773c\uff0c\u518d\u559d\u9053\uff1a\u201c\u9a6c\u9999\u4e3b\uff0c\u8001\u592b\u8981\u7279\u522b\u8b66\u544a\u4f60\uff0c\u4ece\u4eca\u540e\u884c\u4e3a\u8981\u597d\u597d\u68c0\u70b9\uff0c \u82e5\u6709\u534a\u70b9\u5dee\u9519\uff0c\u4f11\u602a\u8001\u592b\u5bf9\u4e0d\u4f4f\u4f60\uff0c\u522b\u4ee5\u4e3a\u4f60\u662f\u6559\u4e3b\u8eab\u8fb9\u7684\u7ea2\u4eba\u5c31\u53ef\u4ee5\u4e3a\u6240\u6b32\u4e3a\uff0c\u8001\u592b\u4ece\u6765 \u4e0d\u5403\u8fd9\u4e00\u5957\uff0c\u7b49\u6709\u4e00\u5929\u8001\u592b\u8981\u5904\u7f6e\u4f60\uff0c\u5373\u4f7f\u6559\u4e3b\u8bb2\u60c5\u4e5f\u6ca1\u7528\uff01\u201d \u3000\u3000\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u73b0\u5728\u5c31\u6eda\u56de\u53bb\uff0c\u9a6c\u4e0a\u628a\u4f55\u6167\u4ed9\u9001\u5230\u8fd9\u91cc\u6765\uff0c\u82e5\u803d\u8bef\u4e86\u65f6 \u95f4\uff0c\u5c0f\u5fc3\u4f60\u7684\u72d7\u547d\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u521a\u8d70\uff0c\u5468\u6d77\u5c71\u7acb\u5373\u63d0\u9192\u4e25\u5bd2\u9053\uff1a\u201c\u7980\u57ce\u4e3b\uff0c\u5c5e\u4e0b\u73b0\u5728\u8be5\u8ddf\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u53c8\u8981\u53bb\uff1f\u201d \u3000\u3000\u201c\u9a6c\u9999\u4e3b\u5fc3\u72e0\u624b\u8fa3\uff0c\u4e07\u4e00\u4ed6\u56de\u53bb\u540e\u5bf9\u4f55\u6167\u4ed9\u4e0d\u5229\uff0c\u4e0d\u662f\u95f9\u7740\u73a9\u7684\u3002\u201d \u3000\u3000\u4e25\u5bd2\u88ab\u4e00\u8bed\u63d0\u9192\uff0c\u968f\u5373\u5429\u5490\u9053\uff1a\u201c\u597d\uff0c\u4f60\u9a6c\u4e0a\u8ddf\u53bb\uff01\u201d \u3000\u3000\u65b9\u624d\u7684\u8fd9\u6bb5\u7ecf\u8fc7\uff0c\u5cb3\u5c0f\u98de\u5728\u9694\u58c1\u4e66\u623f\uff0c\u90fd\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4ed6\u9664\u4e86\u6709\u201c\u5927\u5feb\u4eba\u5fc3\u201d\u4e4b\u611f\u5916\uff0c \u66f4\u5bf9\u4e25\u5bd2\u548c\u5468\u6d77\u5c71\u6709\u8bf4\u4e0d\u51fa\u7684\u611f\u6fc0\u3002\u4ed6\u53c8\u6765\u5230\u5ba2\u5385\u3002 \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u4ee4\u5802\u6c42\u65f6\uff0c\u4e3a\u4e86\u4e0d\u4f7f\u522b\u4eba\u770b\u51fa\u4f60\u4eec\u662f\u6bcd\u5b50\u3002 \u3000\u3000\u4f60\u8fd8\u662f\u6682\u65f6\u8eb2\u5230\u9694\u58c1\u4e66\u623f\u53bb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u518d\u4e09\u5411\u4e25\u5bd2\u62dc\u8c22\u3002 \u3000\u3000\u5468\u6d77\u5c71\u5f88\u5feb\u4fbf\u628a\u4f55\u6167\u4ed9\u5e26\u4e86\u6765\u3002 \u3000\u3000\u5728\u8def\u4e0a\uff0c\u4ed6\u7167\u6837\u4e5f\u6ca1\u544a\u8bc9\u4f55\u6167\u4ed9\u57ce\u4e3b\u56e0\u4f55\u53ec\u89c1\u3002 \u3000\u3000\u4e0d\u8fc7\u4f55\u6167\u4ed9\u5374\u5df2\u731c\u5230\u4e86\u516b\u4e5d\u5206\uff0c\u56e0\u4e3a\u5cb3\u5c0f\u98de\u4e0a\u5348\u66fe\u5bf9\u5979\u8bf4\u8fc7\u5c06\u8bbe\u6cd5\u8bf4\u52a8\u57ce\u4e3b\u6551\u5979\u4e4b\u4e8b\uff0c \u53ea\u662f\u6ca1\u6599\u5230\u4f1a\u5982\u6b64\u4e4b\u5feb\u3002 \u3000\u3000\u5728\u4e25\u5bd2\u6765\u8bf4\uff0c\u6b64\u523b\u4f55\u6167\u4ed9\u5df2\u662f\u81ea\u5df1\u7684\u513f\u5973\u4eb2\u5bb6\uff0c\u4ed6\u5fc5\u987b\u5728\u5ba2\u5385\u95e8\u53e3\u76f8\u8fce\uff0c\u5426\u5219\u82e5\u4ecd\u9ad8\u9ad8 \u5728\u4e0a\u5750\u7740\uff0c\u4f55\u6167\u4ed9\u8fdb\u95e8\u4e4b\u540e\u5fc5\u5b9a\u884c\u8dea\u62dc\u5927\u793c\uff0c\u90a3\u5c31\u663e\u5f97\u81ea\u5df1\u592a\u4e0d\u8be5\u4e86\u3002 \u3000\u3000\u4ed6\u6765\u5230\u5ba2\u5385\u95e8\u53e3\uff0c\u521a\u597d\u4f55\u6167\u4ed9\u5df2\u5230\u3002 \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u57ce\u4e3b\uff0c\u8fd8\u4e0d\u5feb\u5feb\u62dc\u89c1\uff01\u201d \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u5468\u6d77\u5c71\u662f\u4e00\u756a\u597d\u610f\u3002 \u3000\u3000\u57ce\u4e3b\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u4ec5\u6b21\u4e8e\u6559\u4e3b\uff0c\u4f55\u6167\u4ed9\u5e73\u65f6\u5728\u9999\u4e3b\u9762\u524d\u90fd\u8981\u5fc5\u606d\u5fc5\u656c\u3002\u5982\u4eca\u89c1\u4e86\u57ce\u4e3b\uff0c \u90a3\u80fd\u4e0d\u5927\u793c\u53c2\u62dc\u3002 \u3000\u3000\u4f46\u4e25\u5bd2\u5374\u7d27\u8ddf\u7740\u8bf4\u9053\uff1a\u201c\u6765\u5230\u8fd9\u91cc\uff0c\u4e0d\u5fc5\u62d8\u793c\uff0c\u7528\u4e0d\u7740\u62dc\u3002\u201d \u3000\u3000\u4ed6\u62c5\u5fc3\u4f55\u6167\u4ed9\u771f\u7684\u8981\u62dc\uff0c\u53c8\u4e0d\u4fbf\u4e0a\u524d\u6276\u6301\uff0c\u8bf4\u8bdd\u95f4\u7528\u624b\u865a\u7a7a\u4e00\u6258\uff0c\u6697\u53d1\u51fa\u4e00\u80a1\u5947\u5927\u65e0\u6bd4 \u7684\u5185\u529b\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u4f55\u6167\u4ed9\u7a81\u611f\u5230\u8eab\u524d\u4f3c\u88ab\u4e00\u9053\u65e0\u5f62\u7684\u6c14\u5899\u6321\u4f4f\uff0c\u5373\u4f7f\u60f3\u62dc\u4e5f\u62dc\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u4e07\u60f3\u4e0d\u5230\uff0c\u57ce\u4e3b\u5f85\u4eba\u7adf\u662f\u5982\u6b64\u4eb2\u5207\u5ba2\u6c14\u3002 \u3000\u3000\u4e25\u5bd2\u76f4\u628a\u4f55\u6167\u4ed9\u8ba9\u5230\u5ea7\u4f4d\u4e0a\u5750\u597d\uff0c\u81ea\u5df1\u624d\u5728\u5bf9\u9762\u5750\u4e0b\uff0c\u4e00\u9762\u5429\u5490\u9053\uff1a\u201c\u5468\u603b\u7ba1\u53ef\u4ee5\u53bb\u4e86\uff0c \u8001\u592b\u8981\u548c\u4f55\u5973\u4fa0\u5355\u72ec\u8c08\u8c08\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u968f\u5373\u79bb\u5f00\u5ba2\u5385\u3002 \u3000\u3000\u4f55\u6167\u4ed9\u89c1\u57ce\u4e3b\u79f0\u81ea\u5df1\u5973\u4fa0\uff0c\u66f4\u662f\u53d7\u5ba0\u82e5\u60ca\uff0c\u8fd9\u79cd\u79f0\u547c\uff0c\u5bf9\u5979\u65e9\u5df2\u5f88\u964c\u751f\u4e86\u3002 \u3000\u3000\u4e25\u5bd2\u5e26\u7740\u6b49\u610f\u7684\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u4e09\u5e74\u591a\u6765\uff0c\u4f55\u5973\u4fa0\u5728\u7075\u5802\u53d7\u82e6\u53d7\u96be\uff0c\u4e25\u67d0\u5b9e\u5728\u4e8e\u5fc3\u96be \u5b89\uff01\u201d \u3000\u3000\u4f55\u6167\u4ed9\u5f3a\u5fcd\u7740\u6cea\u6c34\uff0c\u4f4e\u4e0b\u5934\u9053\uff1a\u201c\u7f6a\u5973\u8499\u57ce\u4e3b\u5982\u6b64\u5173\u6000\uff0c\u8877\u5fc3\u611f\u6fc0\u4e0d\u5c3d\uff01\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u4f55\u5973\u4fa0\u7528\u4e0d\u7740\u5ba2\u6c14\uff0c\u53ea\u602a\u4e25\u67d0\u5230\u4eca\u5929\u624d\u77e5\u9053\u4f60\u7684\u8eab\u4e16\u4ee5\u53ca\u542b\u51a4\u53d7\u5c48\u7ecf\u8fc7\uff0c\u5426 \u5219\u65e9\u5c31\u4e0d\u4f1a\u8ba9\u4f60\u5728\u7075\u5802\u53d7\u82e6\u4e86\uff01\u201d \u3000\u3000\u4ed6\u9ed8\u4e86\u4e00\u9ed8\u63a5\u9053\uff1a\u201c\u542c\u8bf4\u4e0a\u5348\u4f60\u548c\u4ee4\u90ce\u66fe\u4f1a\u8fc7\u9762\uff0c\u4f60\u7684\u8eab\u4e16\u4ee5\u53ca\u542b\u51a4\u53d7\u5c48\u7ecf\u8fc7\uff0c\u6b63\u662f\u4ee4 \u90ce\u544a\u8bc9\u4e25\u67d0\u7684\uff0c\u4f55\u5973\u4fa0\u6559\u5b50\u6709\u65b9\uff0c\u624d\u80fd\u57f9\u80b2\u51fa\u4ee4\u90ce\u8fd9\u79cd\u5c11\u5e74\u82f1\u96c4\uff0c\u4e25\u67d0\u548c\u4ed6\uff0c\u79f0\u5f97\u4e0a\u76f8\u89c1\u6068 \u665a\u3002\u201d \u3000\u3000\u4f55\u6167\u4ed9\u65e2\u611f\u6fc0\u53c8\u6fc0\u52a8\u7684\u9053\uff1a\u201c\u57ce\u4e3b\u8fc7\u5956\uff0c\u5c0f\u5973\u5b50\u6127\u4e0d\u6562\u5f53\uff01\u201d \u3000\u3000\u4e25\u5bd2\u987f\u4e86\u987f\u9053\uff1a\u201c\u4e25\u67d0\u8981\u6b63\u5f0f\u544a\u6c82\u4f55\u5973\u4fa0\uff0c\u6211\u5df2\u7b54\u5e94\u628a\u5c0f\u5973\u5982\u971c\u8bb8\u914d\u7ed9\u4ed6\uff0c\u4e0d\u8fc7\u8fd9\u4e8b\u6682 \u65f6\u8fd8\u4e0d\u80fd\u58f0\u5f20\uff0c\u4f55\u5973\u4fa0\uff0c\u5b9e\u9645\u4e0a\u5f7c\u6b64\u5df2\u7ecf\u662f\u513f\u5973\u4eb2\u5bb6\u4e86\uff0c\u6240\u4ee5\u4f11\u5bf9\u4e25\u67d0\u624d\u7528\u4e0d\u7740\u591a\u793c\u3002\u201d \u3000\u3000\u4e8b\u60c5\u4f1a\u8fdb\u5c55\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u8fd9\u5012\u4f7f\u4f55\u6167\u4ed9\u5927\u611f\u610f\u5916\uff0c\u5979\u8fde\u5fd9\u5728\u539f\u4f4d\u88e3\u887d\u4e00\u793c\u9053\uff1a\u201c\u57ce\u4e3b\u867d \u4e0d\u5acc\u5fae\u8d31\uff0c\u4f46\u5c0f\u5973\u5b50\u5982\u6b64\u548c\u72ac\u5b50\u603b\u5acc\u592a\u9ad8\u6500\u4e86\uff01\u201d \u3000\u3000\u4e25\u5bd2\u6b63\u8272\u9053\uff1a\u201c\u83ab\u975e\u4f55\u5973\u4fa0\u4e0d\u540c\u610f\u8fd9\u95e8\u4eb2\u4e8b\uff1f\u201d \u3000\u3000\u4f55\u6167\u4ed9\u53c8\u88e3\u887d\u4e00\u793c\u9053\uff1a\u201c\u57ce\u4e3b\u4e0d\u5acc\u6211\u4eec\u6bcd\u5b50\u5fae\u8d31\uff0c\u5c0f\u5973\u5b50\u600e\u6562\u89c1\u5916\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u62c2\u987b\u5475\u5475\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u597d\uff0c\u73b0\u5728\u4e25\u67d0\u5c31\u8bf7\u4ee4\u90ce\u51fa\u6765\uff0c\u8ba9\u4f60\u4eec\u6bcd\u5b50\u76f8\u89c1\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u7b49\u4e25\u5bd2\u53ec\u558a\uff0c\u7acb\u5373\u81ea\u52a8\u6765\u5230\u5ba2\u5385\uff0c\u5148\u62dc\u4e25\u5bd2\uff0c\u518d\u62dc\u6bcd\u4eb2\u3002 \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u4ece\u73b0\u5728\u8d77\uff0c\u4f55\u5973\u4fa0\u4e0d\u5fc5\u56de\u7075\u5802\u4e86\uff0c\u5c31\u7559\u5728\u5e9c\u91cc\uff0c\u81f3\u4e8e\u4eca\u540e\u7684\u4e8b\uff0c\u6162\u6162\u518d\u5b89 \u6392\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u968f\u5373\u628a\u592b\u4eba\u548c\u5927\u5c0f\u59d0\u53ec\u6765\u548c\u4f55\u6167\u4ed9\u76f8\u89c1\u3002 \u3000\u3000\u4e25\u592b\u4eba\u5bf9\u5f85\u4f55\u6167\u4ed9\u50cf\u4eb2\u59ca\u59b9\u4e00\u822c\u4eb2\u5207\uff0c\u5fd9\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u6211\u966a\u4eb2\u5bb6\u6bcd\u5148\u5230\u540e\u9762\u6362\u6362\u8863\u670d\uff0c \u518d\u5b89\u6392\u5979\u7684\u4f4f\u5904\uff0c\u665a\u4e0a\u518d\u8bbe\u7b75\u597d\u597d\u4e3a\u5979\u5e86\u8d3a\u5e86\u8d3a\uff01\u201d \u3000\u3000\u4e25\u5bd2\u9894\u9996\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5148\u5e26\u4f55\u5973\u4fa0\u5230\u540e\u9762\u53bb\u3002\u201d \u3000\u3000\u63a5\u7740\u53c8\u9053\uff1a\u201c\u8bb0\u4f4f\uff0c\u4f55\u5973\u4fa0\u548c\u54b1\u4eec\u7684\u5173\u7cfb\uff0c\u76ee\u524d\u9664\u4e86\u4f60\u548c\u5982\u51b0\u5982\u971c\u77e5\u9053\u5916\uff0c\u5bf9\u5176\u4ed6\u7684\u4eba \u7edd\u5bf9\u8981\u4fdd\u5bc6\uff01\u201d \u3000\u3000\u4e25\u592b\u4eba\u7b11\u9053\uff1a\u201c\u7528\u4e0d\u7740\u4f60\u4ea4\u4ee3\uff0c\u6211\u65e9\u60f3\u5230\u8fd9\u4e00\u5c42\u5566\u3002\u201d \u3000\u3000\u5f53\u665a\uff0c\u4e25\u5bd2\u592b\u5987\u5907\u4e0b\u76db\u7b75\uff0c\u5c31\u5728\u5185\u5ba4\u5ba2\u5385\u91cc\u6b3e\u5f85\u4f55\u6167\u4ed9\u3002 \u3000\u3000\u4f5c\u966a\u7684\u9664\u4e25\u5bd2\u592b\u5987\u5916\uff0c\u53ea\u6709\u5cb3\u5c0f\u98de\u548c\u5927\u59d1\u5a18\u4e25\u5982\u51b0\uff0c\u81f3\u4e8e\u4e8c\u59d1\u5a18\u4e25\u5982\u971c\uff0c\u5f53\u7136\u4e0d\u4fbf\u548c\u51c6 \u5a46\u5a46\u4ee5\u53ca\u672a\u5a5a\u592b\u5a7f\u89c1\u9762\u3002 \u3000\u3000\u4f55\u6167\u4ed9\u65e9\u6362\u4e0a\u4e00\u8eab\u9526\u7ee3\u65b0\u8863\uff0c\u518d\u52a0\u4e0a\u4e00\u6253\u626e\uff0c\u548c\u5728\u7075\u5802\u65f6\u5df2\u5224\u82e5\u4e24\u4eba\uff0c\u4f3c\u4e4e\u53c8\u6062\u590d\u4e86\u6614 \u65e5\u4ed9\u59ff\u7389\u8d28\u7684\u5bb9\u989c\u3002 \u3000\u3000\u8fd9\u662f\u5cb3\u5c0f\u98de\u4e00\u751f\u4e2d\u6700\u5174\u594b\u6700\u6109\u5feb\u7684\u4e00\u665a\u3002 \u3000\u3000\u76f8\u4fe1\u4f55\u6167\u4ed9\u4e5f\u4e0d\u4f8b\u5916\u3002 \u3000\u3000\u5e2d\u95f4\uff0c\u4e25\u592b\u4eba\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u4f60\u662f\u5426\u60f3\u5230\u628a\u5c0f\u98de\u4e5f\u8c03\u5230\u54b1\u4eec\u8fd9\u8fb9\u6765\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u5f53\u7136\u60f3\u8fc7\uff0c\u53ea\u662f\u4ed6\u548c\u5982\u971c\u8fd8\u8981\u7b49\u51e0\u5e74\u624d\u80fd\u6210\u4eb2\uff0c\u82e5\u8c03\u5230\u8fd9\u8fb9\u6765\uff0c\u971c\u513f\u548c\u4ed6\u90fd \u6709\u4e9b\u4e0d\u4fbf\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u8fd8\u662f\u8ba9\u4ed6\u6682\u65f6\u7559\u5728\u4e94\u5173\u603b\u9547\u7684\u597d\u3002\u201d \u3000\u3000\u4e25\u592b\u4eba\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u603b\u8be5\u5e38\u56de\u6765\u770b\u770b\u4ed6\u6bcd\u4eb2\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u8fd9\u65b9\u9762\u6211\u4e5f\u65e9\u60f3\u5230\u4e86\uff0c\u6211\u4f1a\u53d1\u7ed9\u4ed6\u4e00\u9762\u672c\u57ce\u7684\u901a\u884c\u4ee4\u724c\uff0c\u53ea\u8981\u4ed6\u6709\u65f6\u95f4\uff0c\u968f \u65f6\u90fd\u53ef\u4ee5\u56de\u6765\u548c\u4f55\u5973\u4fa0\u89c1\u9762\u3002\u201d \u3000\u3000\u8fd9\u987f\u9152\u996d\uff0c\u76f4\u5403\u5230\u4e00\u66f4\u5c06\u5c3d\uff0c\u624d\u5c3d\u6b22\u800c\u6563\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8fd8\u7279\u522b\u8ddf\u5230\u5185\u5ba4\uff0c\u53c8\u966a\u4f8d\u6bcd\u4eb2\u5f88\u4e45\uff0c\u624d\u56de\u5230\u6628\u591c\u4f4f\u8fc7\u7684\u8de8\u9662\u5b89\u6b47\u3002 \u3000\u3000\u8fd9\u4e00\u591c\uff0c\u4ed6\u7761\u5f97\u5f53\u771f\u751c\u871c\u6781\u4e86\uff0c\u4ed6\u60f3\u5230\u5f53\u56de\u5230\u4e94\u5173\u603b\u9547\u628a\u6d88\u606f\u544a\u8bc9\u7236\u4eb2\uff0c\u7236\u4eb2\u5185\u5fc3\u7684\u5feb \u6170\u3002\u53c8\u5c82\u662f\u7b14\u58a8\u6240\u53ef\u5f62\u5bb9\u7684\u3002 \u3000\u3000\u6b21\u65e5\u7684\u65e9\u9910\uff0c\u662f\u4ed6\u72ec\u81ea\u5728\u623f\u95f4\u7528\u7684\u3002 \u3000\u3000\u996d\u540e\uff0c\u4ed6\u5230\u5185\u5ba4\u5411\u6bcd\u4eb2\u4ee5\u53ca\u4e25\u5bd2\u592b\u5987\u8f9e\u522b\u3002 \u3000\u3000\u5947\u602a\u7684\u5374\u59cb\u7ec8\u6ca1\u78b0\u7740\u4e8c\u59d1\u5a18\u4e25\u5982\u971c\uff0c\u867d\u7136\u90a3\u65f6\u5019\u672a\u5a5a\u592b\u59bb\u4e0d\u80fd\u89c1\u9762\uff0c\u4f46\u65e0\u610f\u4e2d\u78b0\u4e0a\u5e94\u662f \u4f8b\u5916\uff0c\u5176\u5b9e\u5728\u4ed6\u7684\u5c0f\u5fc3\u7075\u91cc\uff0c\u53c8\u4f55\u5c1d\u4e0d\u60f3\u548c\u4e25\u5982\u971c\u89c1\u4e0a\u4e00\u9762\u3002 \u3000\u3000\u5f53\u51fa\u57ce\u540e\uff0c\u5cb3\u5c0f\u98de\u624d\u60f3\u8d77\u90a3\u526f\u4eba\u76ae\u9762\u5177\u5c1a\u672a\u4ea4\u8fd8\u5468\u6d77\u5c71\uff0c\u4ed6\u5b9e\u5728\u60f3\u62e5\u6709\u4e00\u526f\u4eba\u76ae\u9762\u5177\uff0c \u4ee5\u4fbf\u7528\u6765\u5bf9\u4ed8\u9a6c\u662d\u96c4\u4e4b\u7528\uff0c\u5374\u53c8\u4e0d\u4fbf\u5411\u5468\u6d77\u5c71\u786c\u8981\u3002 \u3000\u3000\u6ca1\u5948\u4f55\uff0c\u53ea\u5f97\u53d6\u51fa\u9762\u5177\u9053\uff1a\u201c\u665a\u8f88\u8fd8\u5fd8\u8bb0\u5e94\u628a\u9762\u5177\u5949\u8fd8\u603b\u7ba1\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u5e76\u672a\u7acb\u5373\u63a5\u53d6\uff1a\u201c\u5982\u679c\u5cb3\u516c\u5b50\u6709\u7528\uff0c\u4e0d\u59a8\u7559\u7740\u7ee7\u7eed\u4f7f\u7528\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u771f\u662f\u559c\u51fa\u671b\u5916\u3002\u9053\uff1a\u201c\u90a3\u6211\u5c31\u8c22\u603b\u7ba1\u4e86\uff01\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u7528\u4e0d\u7740\u8c22\uff0c\u6211\u8eab\u8fb9\u53e6\u5916\u8fd8\u6709\u4e24\u526f\uff0c\u653e\u7740\u4e5f\u662f\u653e\u7740\u3002\u201d \u3000\u3000\u201c\u603b\u7ba1\u90a3\u6765\u7684\u8fd9\u4e48\u591a\u4eba\u76ae\u9762\u5177\uff1f\u201d \u3000\u3000\u201c\u516c\u5b50\u5728\u7075\u5802\u5df2\u7ecf\u89c1\u8fc7\u90a3\u4e9b\u68fa\u6750\u4e86\uff0c\u68fa\u6750\u91cc\u4e0d\u4e4f\u5947\u4eba\u5f02\u58eb\uff0c\u4ec0\u4e48\u6837\u7684\u6c5f\u6e56\u4eba\u7269\u90fd\u6709\uff0c\u8fd9 \u51e0\u526f\u4eba\u76ae\u9762\u5177\uff0c\u90fd\u662f\u4ece\u4ed6\u4eec\u8eab\u4e0a\u641c\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u9a6c\u662d\u96c4\u5fc5\u5b9a\u4e5f\u6709\u4e86\uff1f\u201d \u3000\u3000\u201c\u5f88\u53ef\u80fd\u4e5f\u6709\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u8bf4\u5230\u8fd9\u91cc\uff0c\u4e0d\u89c9\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u6628\u5929\u57ce\u4e3b\u5f53\u573a\u6559\u8bad\u9a6c\u662d\u96c4\u7684\u4e8b\uff0c\u516c\u5b50\u662f\u5426\u77e5\u9053\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5f53\u65f6\u665a\u8f88\u5728\u9694\u58c1\u4e66\u623f\uff0c\u4e00\u5207\u90fd\u542c\u5f97\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u53f9\u53e3\u6c14\u9053\uff0c\u201c\u9a6c\u662d\u96c4\u662f\u4e2a\u5c0f\u4eba\uff0c\u5c0f\u4eba\u4e4b\u5fc3\uff0c\u4e0d\u53ef\u4e0d\u9632\uff0c\u6211\u5f88\u62c5\u5fc3\u57ce\u4e3b\u4f1a\u5403\u4ed6\u7684 \u4e8f\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5440\u7136\u9053\uff1a\u201c\u4ed6\u4e0d\u8fc7\u4e00\u540d\u5c0f\u5c0f\u9999\u4e3b\uff0c\u5982\u4f55\u80fd\u8ba9\u57ce\u4e3b\u5403\u4e8f\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u8c01\u80fd\u9632\u4ed6\u4e0d\u5728\u6559\u4e3b\u9762\u524d\u6311\u62e8\u79bb\u95f4\uff0c\u5c24\u5176\u57ce\u4e3b\u6628\u5929\u66fe\u8bf4\u8fc7\u6ca1\u628a\u6559\u4e3b\u653e\u5728\u773c\u91cc \u7684\u8bdd\uff0c\u9a6c\u662d\u96c4\u6b63\u597d\u6709\u4e86\u6311\u62e8\u662f\u975e\u7684\u501f\u53e3\uff0c\u6240\u8c13\u6253\u72d7\u770b\u4e3b\u9762\uff0c\u57ce\u4e3b\u5f53\u65f6\u5b9e\u5728\u4e0d\u8be5\u90a3\u6837\u51b2\u52a8\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8d81\u673a\u95ee\u9053\uff1a\u201c\u542c\u5927\u516c\u4e3b\u8bf4\uff0c\u57ce\u4e3b\u4e3a\u4eba\u5f88\u4e25\u8083\uff0c\u4e0d\u82df\u8a00\u7b11\uff0c\u636e\u665a\u8f88\u8fd9\u4e24\u5929\u548c\u4ed6\u8001\u4eba \u5bb6\u7684\u63a5\u89e6\uff0c\u597d\u50cf\u4e0d\u662f\u8fd9\u6837\u5b50\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u53ef\u89c1\u5927\u516c\u4e3b\u6839\u672c\u4e0d\u4e86\u89e3\u57ce\u4e3b\u7684\u4e3a\u4eba\uff0c\u5f53\u57ce\u4e0a\u7b2c\u4e00\u6b21\u548c\u5916\u4eba\u89c1\u9762\u65f6\uff0c\u7684\u786e\u4f1a \u4ee4\u4eba\u89c9\u5f97\u4ed6\u5f88\u4e25\u8083\u3002\u4e0d\u8fc7\u76f8\u4ea4\u4e00\u4e45\uff0c\u5c31\u4f1a\u660e\u767d\u4ed6\u662f\u4e00\u4f4d\u6027\u60c5\u4e2d\u4eba\uff0c\u5c24\u5e76\u4ed6\u5bf9\u81ea\u5df1\u6240\u559c\u6b22\u7684\u4eba\uff0c \u66f4\u662f\u8bda\u5fc3\u76f8\u5f85\uff0c\u4ece\u6765\u4e0d\u505a\u8868\u9762\u6587\u7ae0\uff0c\u4ee5\u6211\u5468\u6d77\u5c71\u6765\u8bf4\uff0c\u80fd\u5728\u4ed6\u624b\u4e0b\u505a\u4e8b\uff0c\u7b97\u662f\u8ddf\u5bf9\u4eba\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7ec6\u60f3\u8fd9\u4e24\u5929\u4e25\u5bd2\u5bf9\u5f85\u81ea\u5df1\u7684\u4e00\u5207\uff0c\u548c\u5468\u6d77\u5c71\u7684\u8bdd\u76f8\u5bf9\u7167\uff0c\u679c\u7136\u4e0d\u5047\uff0c\u5fc3\u4e0b\u5bf9\u4e25\u5bd2 \u8d8a\u53d1\u6709\u7740\u96be\u8a00\u7684\u611f\u6fc0\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u51fa\u57ce\u5f88\u8fdc\uff0c\u5cb3\u5c0f\u98de\u4e0d\u4fbf\u9ebb\u70e6\u5468\u6d77\u5c71\u8fdc\u9001\u3002\u968f\u5373\u62f1\u624b\u544a\u8f9e\u3002 \u3000\u3000\u56de\u5230\u4e94\u5173\u603b\u9547\uff0c\u5cb3\u5c0f\u98de\u7acb\u5373\u628a\u8fd9\u4e24\u5929\u5728\u80b2\u5316\u57ce\u7684\u7ecf\u8fc7\uff0c\u5411\u7236\u4eb2\u82b1\u7389\u9e9f\u7980\u62a5\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u5f97\u77e5\u7231\u59bb\u4f55\u6167\u4ed9\u5df2\u7ecf\u5f97\u6551\uff0c\u771f\u662f\u559c\u4e4b\u4e0d\u5c3d\uff0c\u7d27\u63e1\u7740\u5cb3\u5c0f\u98de\u7684\u624b\u9053\uff1a\u201c\u98de\u513f\uff0c\u4f60\u6bd4 \u4e3a\u7236\u80fd\u5e72\u591a\u4e86\uff0c\u4e3a\u7236\u5728\u5929\u8c37\u4e09\u5e74\u591a\u90fd\u65e0\u6cd5\u5e2e\u4e0a\u4f60\u6bcd\u4eb2\u7684\u5fd9\uff0c\u800c\u4f60\u4e0d\u8fc7\u51e0\u5929\u5de5\u592b\u4fbf\u6551\u4e0b\u4f60\u6bcd\u4eb2\uff0c \u8bf4\u8d77\u6765\u4e3a\u7236\u771f\u8be5\u60ed\u6127\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8083\u5bb9\u9053\uff1a\u201c\u7236\u4eb2\u8fd9\u6837\u8bf4\u8bdd\uff0c\u5c82\u4e0d\u6298\u715e\u4e86\u5b69\u513f\uff0c\u4f60\u8001\u4eba\u5bb6\u6ca1\u6551\u51fa\u6bcd\u4eb2\uff0c\u662f\u56e0\u4e3a\u6709\u5f88 \u591a\u987e\u5fcc\uff0c\u800c\u5b69\u513f\u5728\u5fc3\u7406\u4e0a\uff0c\u5374\u6ca1\u6709\u534a\u70b9\u8fd9\u79cd\u8d1f\u62c5\u3002\u201d \u3000\u3000\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u4e3a\u7236\u5374\u4e0d\u5e94\u4ee5\u8fd9\u79cd\u7406\u7531\u4f5c\u4e3a\u65e0\u6cd5\u6551\u51fa\u4f60\u6bcd\u4eb2\u7684\u501f\u53e3\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u5b69\u513f\u4e5f\u72af\u4e0b\u4e86\u4e00\u9879\u4e0d\u53ef\u9976\u6055\u7684\u5927\u7f6a\uff01\u201d \u3000\u3000\u201c\u4f60\u6307\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5b69\u513f\u6ca1\u7980\u660e\u7236\u6bcd\uff0c\u4fbf\u7b54\u5e94\u4e0b\u4eb2\u4e8b\uff0c\u600e\u80fd\u4e0d\u8bf4\u662f\u4e00\u9879\u5927\u7f6a\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u4f60\u4e0d\u7b54\u5e94\uff0c\u53c8\u5982\u4f55\u6551\u5f97\u4e86\u4f60\u6bcd\u4eb2\uff1f\u4f60\u4e3a\u4e86\u5c3d\u5b5d\u624d\u8fd9\u6837\u505a\uff0c\u4f55\u7f6a\u4e4b\u6709\uff1f\u518d\u8bf4\u4e25\u5bd2\u662f \u4e00\u57ce\u4e4b\u4e3b\uff0c\u4f60\u5c31\u662f\u60f3\u62d2\u7edd\uff0c\u53c8\u80fd\u62d2\u7edd\u5f97\u4e86\u5417\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4f4e\u4e0b\u5934\uff0c\u55eb\u5685\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u7236\u4eb2\u548c\u5927\u516c\u4e3b\u7684\u4e8b\uff0c\u5b69\u513f\u4e0d\u80fd\u5fd8\u8bb0\uff0c\u57ce\u4e3b\u662f\u6559\u4e3b\u7684\u5e08 \u5144\uff0c\u8fd9\u4e8b\u4e00\u65e6\u6210\u771f\uff0c\u53c8\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u7b11\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u653e\u5fc3\uff0c\u4e3a\u7236\u548c\u5927\u516c\u4e3b\u7684\u4e8b\uff0c\u6211\u4f1a\u5c3d\u91cf\u8bbe\u6cd5\u62d6\u5ef6\uff0c\u81f3\u4e8e\u4f60\u548c\u4e25\u57ce \u4e3b\u7684\u4e8c\u5c0f\u59d0\uff0c\u90a3\u662f\u51e0\u5e74\u4ee5\u540e\u7684\u4e8b\u4e86\uff0c\u4e3a\u7236\u8bb0\u5f97\u66fe\u7ecf\u544a\u8bc9\u8fc7\u4f60\uff0c\u53ea\u8981\u6551\u51fa\u8981\u6551\u7684\u4eba\uff0c\u6211\u4eec\u9a6c\u4e0a \u5c31\u79bb\u5929\u8c37\uff0c\u8fd9\u91cc\u5c82\u662f\u4f60\u6211\u7236\u5b50\u53ca\u4f60\u6bcd\u4eb2\u4e45\u5c45\u4e4b\u5730\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7ec8\u4e8e\u7565\u7565\u5bbd\u5fc3\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u4f60\u8f9b\u82e6\u4e86\u4e24\u5929\u591a\uff0c\u4e5f\u8be5\u597d\u597d\u4f11\u606f\u4f11\u606f\u4e86\uff0c\u603b\u4e4b\uff0c\u4f60\u6bcd\u4eb2\u65e2\u7136\u5df2\u7ecf\u5f97\u6551\uff0c\u6211 \u4e5f\u5c31\u653e\u5fc3\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u79bb\u5f00\u7236\u4eb2\u623f\u4e2d\uff0c\u7acb\u523b\u518d\u6765\u5230\u8881\u5c0f\u9e64\u4f4f\u5904\u3002 \u3000\u3000\u7ecf\u8fc7\u8fd9\u51e0\u65e5\u4e4b\u540e\uff0c\u8881\u5c0f\u9e64\u5df2\u6e10\u6e10\u6062\u590d\u6b63\u5e38\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5bf9\u8881\u5c0f\u9e64\u662f\u65e0\u8bdd\u4e0d\u8c08\uff0c\u89c1\u9762\u540e\u4fbf\u628a\u8fd9\u4e24\u5929\u5728\u80b2\u5316\u57ce\u7684\u7ecf\u8fc7\uff0c\u5927\u7565\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u9996\u5148\u4e3a\u5cb3\u5c0f\u98de\u7684\u6bcd\u5b50\u91cd\u9022\u5e86\u8d3a\uff0c\u63a5\u7740\u76b1\u8d77\u7709\u5b87\u9053\uff1a\u201c\u539f\u6765\u9a6c\u662d\u96c4\u4e0d\u4f46\u6ca1\u6b7b\uff0c\u800c\u4e14 \u8fd8\u505a\u4e86\u6559\u4e3b\u7684\u5fc3\u8179\uff0c\u8fd9\u6837\u8bf4\u4ed6\u548c\u51e4\u5ae3\u7ea2\u4ecd\u5fc5\u5e38\u6253\u6697\u4e2d\u6765\u5f80\u4e86\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5f88\u96be\u8bb2\uff0c\u4e0d\u8fc7\u5373\u4f7f\u6709\u6765\u5f80\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u53ef\u80fd\u5e38\u5e38\u89c1\u9762\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u80b2\u5316\u57ce\u7ba1\u5236\u5f88\u4e25\uff0c\u9a6c\u662d\u96c4\u5230\u5bb5\u5173\u5f80\u8fd4\u4e00\u8d9f\u4fbf\u5f97\u534a\u5929\u65f6\u95f4\uff0c\u5bf9\u4ed6\u6765\u8bf4\uff0c\u81ea\u7136\u4e0d\u65b9\u4fbf\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u8fd9\u5bf9\u72d7\u7537\u5973\u603b\u8981\u5e7d\u4f1a\u7684\u3002\u201d \u3000\u3000\u201c\u5bcc\u56fd\u57ce\u6709\u4e00\u5904\u7f8e\u4eba\u5173\uff0c\u5b9e\u9645\u4e0a\u662f\u4e09\u5927\u5993\u9662\uff0c\u636e\u8bf4\u9a6c\u662d\u96c4\u662f\u90a3\u91cc\u7684\u5e38\u5ba2\u3002\u201d \u3000\u3000\u201c\u72d7\u8d70\u5230\u5929\u8fb9\u5fd8\u4e0d\u4e86\u5403\u5c4e\uff0c\u9a6c\u662d\u96c4\u8fd9\u6df7\u5e10\u5b9e\u5728\u592a\u53ef\u6076\u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8bf4\u5230\u8fd9\u91cc\uff0c\u54ac\u4e86\u54ac\u7259\u9053\uff1a\u201c\u5176\u5b9e\u6211\u5012\u5e0c\u671b\u4ed6\u5230\u5bb5\u5173\u6765\u627e\u51e4\u5ae3\u7ea2\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u89e3\u9053\uff1a\u201c\u8881\u5927\u54e5\u4e3a\u4ec0\u4e48\u5ffd\u7136\u53c8\u6709\u8fd9\u79cd\u60f3\u6cd5\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u54b1\u4eec\u662f\u5de1\u5173\u4f7f\u8005\uff0c\u804c\u8d23\u6240\u5728\uff0c\u82e5\u80fd\u5f53\u573a\u6349\u5230\u4ed6\u548c\u51e4\u5ae3\u7ea2\u7684\u5978\u60c5\uff0c\u8bf7\u526f\u603b\u9547 \u62a5\u5230\u603b\u575b\uff0c\u4ed6\u4eec\u7eb5\u7136\u662f\u6559\u4e3b\u624b\u4e0b\u7684\u7ea2\u4eba\uff0c\u4e5f\u5fc5\u5b9a\u8981\u5012\u5927\u9709\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6447\u5934\u9053\uff1a\u201c\u82e5\u60f3\u5f53\u573a\u6349\u5978\uff0c\u8c08\u4f55\u5bb9\u6613\uff0c\u4f9d\u5c0f\u5f1f\u4e4b\u89c1\uff0c\u82e5\u80fd\u548c\u9a6c\u662d\u96c4\u72ed\u8def\u76f8\u9022\uff0c\u5148 \u597d\u597d\u6559\u8bad\u4ed6\u2014\u987f\u624d\u6700\u80fd\u5927\u5feb\u4eba\u5fc3\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e0d\u4ee5\u4e3a\u7136\uff1a\u201c\u82e5\u771f\u662f\u548c\u4ed6\u72ed\u8def\u76f8\u9022\uff0c\u8981\u4e0d\u5e72\u5c31\u4e0d\u5e72\uff0c\u8981\u5e72\u5c31\u5fc5\u987b\u5e72\u6389\u4ed6\u3002\u201d \u3000\u3000\u201c\u4ee5\u76ee\u524d\u6765\u8bf4\uff0c\u5e72\u6389\u4ed6\u53cd\u4e3a\u4e0d\u5999\uff0c\u6559\u8bad\u4ed6\u4e00\u987f\u624d\u662f\u771f\u7684\u3002\u201d \u3000\u3000\u201c\u5c82\u6709\u6b64\u7406\uff0c\u82e5\u4ed6\u4e00\u72b6\u544a\u5230\u6559\u4e3b\u90a3\u91cc\uff0c\u54b1\u4eec\u8fd8\u60f3\u6d3b\u547d\u5417\uff1f\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u4f1a\u8ba9\u4ed6\u8ba4\u4e0d\u51fa\u6765\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u90a3\u662f\u8bf4\u54b1\u4eec\u5fc5\u987b\u6613\u5bb9\uff0c\u53ef\u662f\u54b1\u4eec\u603b\u4e0d\u80fd\u8001\u662f\u6613\u597d\u5bb9\u7b49\u7740\u4ed6\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b11\u9053\uff1a\u201c\u4e0d\u7792\u8881\u5927\u54e5\uff0c\u5c0f\u5f1f\u8fd9\u6b21\u5230\u80b2\u5316\u57ce\uff0c\u6709\u4eba\u9001\u4e86\u4e00\u526f\u4eba\u76ae\u9762\u5177\uff0c\u53ea\u8981\u4e34\u65f6\u6234 \u4e0a\uff0c\u8c01\u80fd\u8ba4\u5f97\u51fa\u6765\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8feb\u4e0d\u53ca\u5f85\u7684\u9053\uff1a\u201c\u5feb\u62ff\u51fa\u6765\u6234\u4e0a\u770b\u770b\uff0c\u6211\u957f\u4e86\u8fd9\u4e48\u5927\uff0c\u8fd8\u6ca1\u89c1\u8fc7\u4eba\u76ae\u9762\u5177\u5462\u3002\u201d \u3000\u3000\u5f53\u5cb3\u5c0f\u98de\u6234\u4e0a\u4e4b\u540e\uff0c\u8881\u5c0f\u9e64\u770b\u4e86\u771f\u662f\u60ca\u5947\u4e0d\u5df2\u3002 \u3000\u3000\u5cb3\u5c0f\u6b63\u9053\uff1a\u201c\u8881\u5927\u54e5\u5982\u679c\u559c\u6b22\uff0c\u5c0f\u5f1f\u4e5f\u8bb8\u80fd\u60f3\u529e\u6cd5\u66ff\u4f60\u627e\u4e00\u526f\uff0c\u73b0\u5728\u5982\u6709\u9700\u8981\uff0c\u53ef\u4ee5\u6682 \u65f6\u5411\u5c0f\u5f1f\u501f\u7528\u3002\u201d \u3000\u3000\u4ed6\u79bb\u5f00\u8881\u5c0f\u9e64\u623f\u95f4\uff0c\u53c8\u548c\u4e01\u6d9b\u3001\u7518\u9716\u5404\u81ea\u6253\u8fc7\u62db\u547c\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u6839\u672c\u4e0d\u77e5\u9053\u5cb3\u5c0f\u98de\u662f\u5230\u90a3\u91cc\u53bb\u4e86\uff0c\u5cb3\u5c0f\u98de\u4e5f\u53ea\u80fd\u968f\u673a\u5e94\u4ed8\u8fc7\u53bb\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u88ab\u4e25\u5bd2\u63b4\u4e86\u4e00\u638c\u53c8\u5927\u52a0\u8d23\u9a82\u4e4b\u540e\u56de\u5230\u7075\u5802\uff0c\u8d8a\u60f3\u8d8a\u4e0d\u662f\u6ecb\u5473\uff0c\u518d\u52a0\u4f55\u6167\u4ed9\u88ab\u5468\u6d77 \u5c71\u5e26\u8d70\uff0c\u66f4\u662f\u96be\u4ee5\u5fcd\u53d7\u3002 \u3000\u3000\u4ed6\u4e00\u5411\u775a\u7726\u5fc5\u62a5\uff0c\u4f55\u51b5\u81ea\u5df1\u53c8\u662f\u6559\u4e3b\u8ddf\u524d\u7684\u7ea2\u4eba\uff0c\u800c\u4e14\u57ce\u4e3b\u8d23\u7f5a\u4ed6\u65f6\u7684\u8bed\u6c14\uff0c\u4f3c\u4e4e\u628a\u6559 \u4e3b\u6ca1\u653e\u5728\u773c\u91cc\uff0c\u53ea\u8981\u81ea\u5df1\u5728\u6559\u4e3b\u9762\u524d\u642c\u5f04\u51e0\u53e5\u662f\u975e\uff0c\u4e0d\u6101\u6273\u4e0d\u5012\u4e25\u5bd2\u3002 \u3000\u3000\u4ed6\u672c\u60f3\u5f53\u665a\u5c31\u5230\u603b\u575b\u9762\u62a5\u6559\u4e3b\uff0c\u4f46\u53c8\u89c9\u5f97\u81ea\u5df1\u4e00\u4eba\u7684\u529b\u91cf\u6050\u6015\u4e0d\u591f\uff0c\u82e5\u80fd\u8054\u5408\u51e4\u5ae3\u7ea2\u52a9 \u4e0a\u4e00\u81c2\u4e4b\u529b\uff0c\u90a3\u5c31\u5dee\u4e0d\u591a\u4e86\uff0c\u56e0\u4e3a\u51e4\u5ae3\u7ea2\u5728\u6559\u4e3b\u9762\u524d\uff0c\u6bd4\u4ed6\u66f4\u8981\u7ea2\u5f97\u591a\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u4e0d\u4fbf\u7acb\u5373\u6210\u884c\uff0c\u4e00\u6765\u521a\u521a\u51fa\u8fd9\u4e8b\u540e\uff0c\u4e0d\u80fd\u4e0d\u62c5\u5fc3\u57ce\u4e3b\u968f\u65f6\u8fd8\u4f1a\u4f20\u89c1\u4ed6\uff0c\u4e8c\u6765\u4ed6\u88ab \u4e25\u5bd2\u4e00\u638c\u63b4\u5f97\u76ae\u5f00\u8089\u7efd\uff0c\u53c8\u4f24\u5728\u95e8\u9762\u4e0a\uff0c\u8fd9\u6837\u5b50\u5982\u4f55\u80fd\u548c\u51e4\u5ae3\u7ea2\u89c1\u9762\u3002 \u3000\u3000\u4ed6\u548c\u51e4\u5ae3\u7ea2\u5df2\u7ecf\u6709\u5f88\u957f\u4e00\u6bb5\u65f6\u95f4\u6ca1\u89c1\u9762\u4e86\uff0c\u51e4\u5ae3\u7ea2\u6700\u7231\u7684\u662f\u5c0f\u767d\u8138\uff0c\u4ed6\u82e5\u4ee5\u73b0\u5728\u6ee1\u8138\u4f24 \u75d5\u7684\u72fc\u72c8\u76f8\u53bb\u89c1\u5979\uff0c\u4e0d\u7acb\u523b\u88ab\u55e4\u4e4b\u4ee5\u9f3b\u8d76\u51fa\u6765\u624d\u602a\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u4ed6\u51b3\u5b9a\u7b49\u8fc7\u51e0\u5929\u9762\u4f24\u597d\u4e86\u518d\u627e\u673a\u4f1a\u548c\u51e4\u5ae3\u7ea2\u76f8\u89c1\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u6b63\u5728\u5bb5\u5173\u5979\u90a3\u7cbe\u81f4\u8c6a\u534e\u7684\u5367\u5ba4\u91cc\u65e0\u804a\u7684\u72ec\u5750\u7740\u3002 \u3000\u3000\u5979\u7684\u786e\u592a\u65e0\u804a\u3002 \u3000\u3000\u5728\u4ee5\u5f80\uff0c\u5979\u51e0\u4e4e\u671d\u671d\u66ae\u66ae\u79bb\u4e0d\u5f00\u7537\u4eba\u3002 \u3000\u3000\u81ea\u4ece\u4e09\u5e74\u524d\u56e0\u8fdb\u732e\u201c\u70bc\u5fc3\u5927\u6cd5\u201d\u79d8\u7b08\u6709\u529f\u88ab\u7687\u5143\u6559\u4e3b\u5f15\u4e3a\u5fc3\u8179\u59d4\u4efb\u5bb5\u5173\u7edf\u9886\u540e\uff0c\u8eab\u4efd\u5730 \u4f4d\u867d\u7136\u9ad8\u4e86\uff0c\u5374\u5c11\u4e86\u966a\u4f34\u5979\u7684\u7537\u4eba\u3002 \u3000\u3000\u5979\u548c\u9a6c\u662d\u96c4\u79c1\u901a\u7684\u4e8b\uff0c\u5f53\u7136\u4e0d\u6562\u8ba9\u6559\u4e3b\u6216\u5929\u8c37\u4e2d\u7684\u4efb\u4f55\u4eba\u77e5\u9053\u3002\u504f\u504f\u9a6c\u662d\u96c4\u53c8\u88ab\u5206\u914d\u5728 \u80b2\u5316\u57ce\u7075\u5802\u62c5\u4efb\u9999\u4e3b\uff0c\u800c\u57ce\u4e3b\u4e25\u5bd2\u53c8\u5bf9\u5c5e\u4e0b\u53d7\u5236\u751a\u4e25\uff0c\u56e0\u4e4b\uff0c\u9a6c\u662d\u96c4\u4e5f\u751a\u5c11\u6765\u548c\u5979\u5e7d\u4f1a\u3002 \u3000\u3000\u5176\u5b9e\u9a6c\u662d\u96c4\u5012\u5e76\u4e0d\u5bc2\u5bde\uff0c\u5bcc\u56fd\u57ce\u7684\u7f8e\u4eba\u5173\u8fd1\u5728\u54ab\u5c3a\uff0c\u4e5f\u968f\u65f6\u90fd\u53ef\u4ee5\u627e\u5230\u6240\u9700\u8981\u7684\u3002 \u3000\u3000\u4f46\u5374\u82e6\u4e86\u51e4\u5ae3\u7ea2\uff0c\u5979\u8eab\u4e3a\u7edf\u9886\uff0c\u81ea\u4e0d\u65b9\u4fbf\u548c\u5c5e\u624b\u4e0b\u53d1\u751f\u82df\u4e14\u4e4b\u4e8b\uff0c\u800c\u4e14\u5bb5\u5173\u7684\u4e00\u4e9b\u7537\u6027 \u5c5e\u4e0b\uff0c\u4e5f\u6ca1\u4e00\u4e2a\u5979\u770b\u5f97\u4e0a\u773c\u7684\u3002 \u3000\u3000\u6700\u80fd\u548c\u5979\u63a5\u8fd1\u7684\u7537\u6027\uff0c\u8be5\u662f\u5173\u4e0a\u7684\u526f\u7edf\u9886\uff0c\u504f\u504f\u526f\u7edf\u9886\u53c8\u662f\u4f4d\u8001\u5bb6\u4f19\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u5373\u4f7f\u526f\u7edf\u9886\u5f20\u51e4\u9e23\u4e0d\u8001\uff0c\u4eba\u5bb6\u4e5f\u662f\u4f4d\u6b63\u6b63\u7ecf\u7ecf\u7684\u4eba\uff0c\u51b3\u4e0d\u4f1a\u548c\u5979\u505a\u51fa\u90a3\u79cd\u4e8b\u6765\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u5979\u4e0a\u6b21\u624d\u60f3\u5c3d\u529e\u6cd5\u52fe\u5f15\u8881\u5c0f\u9e64\uff0c\u5982\u679c\u90a3\u665a\u8881\u5c0f\u9e64\u771f\u7684\u4e0a\u4e86\u94a9\uff0c\u5979\u4e5f\u8bb8\u4f1a\u628a\u9a6c\u662d\u96c4 \u5fd8\u5f97\u4e00\u5e72\u4e8c\u51c0\u3002\u8881\u5c0f\u9e64\u504f\u504f\u4e0d\u5403\u8fd9\u4e00\u5957\uff0c\u4f60\u8bf4\u6c14\u4e0d\u6c14\u4eba\uff1f\u5c31\u5728\u8fd9\u65f6\uff0c\u3000\u4e00\u540d\u4f8d\u5a62\u8fdb\u6765\u7980\u62a5\uff1a \u5927\u95e8\u5916\u4e00\u4f4d\u8001\u5148\u751f\u6c42\u89c1\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u82b3\u5fc3\u4e00\u52a8\uff0c\u7acb\u5373\u5429\u5490\u9053\uff1a\u201c\u628a\u4ed6\u8bf7\u8fdb\u6765\uff01\u201d \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u5de5\u592b\uff0c\u90a3\u4f8d\u5a62\u679c\u7136\u5f15\u9886\u4e00\u540d\u8001\u8005\u8d70\u8fdb\u5ba2\u5385\u3002 \u3000\u3000\u90a3\u8001\u8005\u770b\u6765\u81f3\u5c11\u5df2\u5728\u4e03\u5341\u4ee5\u4e0a\uff0c\u4f46\u8eab\u6750\u5374\u5341\u5206\u9ad8\u5927\u9b41\u68a7\uff0c\u800c\u4e14\u8170\u6746\u633a\u5f97\u5f88\u76f4\uff0c\u6beb\u65e0\u9f99\u949f \u4e4b\u6001\u3002 \u3000\u3000\u8fd9\u65f6\u51e4\u5ae3\u7ea2\u4e5f\u65e9\u8fdb\u4e86\u5ba2\u5385\uff0c\u5979\u4ea4\u4ee3\u90a3\u4f8d\u5a62\u9053\uff1a\u201c\u6ca1\u4f60\u7684\u4e8b\u4e86\uff0c\u6ca1\u6709\u6211\u7684\u62db\u547c\uff0c\u4efb\u4f55\u4eba\u4e0d \u51c6\u8fdb\u6765\uff01\u201d \u3000\u3000\u90a3\u4f8d\u5a62\u8d70\u540e\uff0c\u51e4\u5ae3\u7ea2\u659c\u7768\u4e86\u90a3\u8001\u8005\u4e00\u773c\uff0c\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u522b\u88c5\u6a21\u4f5c\u6837\u4e86\uff0c\u8fd8\u4e0d\u5feb\u73b0\u539f\u5f62\uff01\u201d \u3000\u3000\u90a3\u8001\u8005\u4e3e\u624b\u5f80\u8138\u4e0a\u4e00\u62b9\uff0c\u970e\u65f6\u8138\u5f62\u5927\u53d8\uff0c\u8d6b\u7136\u662f\u9a6c\u662d\u96c4\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u51b7\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u6b7b\u4e86\u5462\uff1f\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u4e45\u624d\u6765\uff1f\u4f60\u90a3\u5fc3\u4e2d\u8fd8\u6709\u6211\u5417\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u6d8e\u8138\u7b11\u9053\uff1a\u201c\u522b\u8fd9\u4e48\u9178\u6e9c\u6e9c\u7684\uff0c\u6211\u82e5\u4e0d\u60f3\u4f60\uff0c\u4e5f\u4e0d\u4f1a\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\uff1f\u60f3\u6211\u5c31\u6765\uff0c\u90a3\u4e48\u4e0d\u60f3\u6211\u5c31\u4e0d\u6765\u4e86\uff0c\u4f60\u8fd9\u4e48\u4e45\u624d\u6765\u4e00\u6b21\uff0c\u662f\u5426\u8868\u793a\u5e73\u65f6\u6839\u672c\u4e0d\u60f3 \u6211\uff1f\u76f4\u5230\u73b0\u5728\u624d\u60f3\u6211\uff1f\u201d \u3000\u3000\u201c\u55e8\uff01\u4f60\u4e0d\u77e5\u9053\uff0c\u57ce\u4e3b\u90a3\u8001\u738b\u516b\u86cb\u6253\u5f97\u7d27\uff0c\u7ba1\u5f97\u4e25\uff0c\u6211\u53c8\u600e\u6562\u968f\u4fbf\u51fa\u6765\u3002\u201d \u3000\u3000\u201c\u4f60\u4eca\u5929\u600e\u4e3b\u662f\u51fa\u6765\u7684\u5462\uff1f\u201d \u3000\u3000\u201c\u4eca\u5929\u6765\u662f\u88ab\u903c\u5f97\u5b9e\u5728\u6ca1\u529e\u6cd5\uff0c\u9664\u4e86\u770b\u4f60\uff0c\u53e6\u6709\u4e00\u4ef6\u91cd\u8981\u5927\u4e8b\u548c\u4f60\u5546\u8bae\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u539f\u6765\u8fd8\u6709\u53e6\u5916\u7684\u4e8b\uff0c\u5426\u5219\u5927\u6982\u4f60\u4e5f\u4e0d\u4f1a\u6765\u4e86\uff0c\u5750\u4e0b\u5427\uff0c\u6211\u5148\u95ee\u95ee\u6709 \u4ec0\u4e48\u6b63\u4e8b\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u5728\u5bf9\u9762\u5750\u4e0b\uff0c\u81ea\u52a8\u5012\u8336\u559d\u4e86\u4e00\u53e3\u9053\uff1a\u201c\u90a3\u4e2a\u53eb\u4f55\u6167\u4ed9\u7684\uff0c\u53ef\u662f\u4f60\u7980\u62a5\u6559\u4e3b\u628a\u5979\u6253 \u5165\u7075\u5802\u505a\u5b88\u7075\u5974\u7684\uff0c\u4e0d\u9519\u5427\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u7728\u7740\u7738\u5b50\u9053\uff1a\u201c\u4f60\u65e2\u7136\u65e9\u5c31\u77e5\u9053\uff0c\u4f55\u5fc5\u518d\u95ee\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u64de\u4e86\u64a4\u5634\u9053\uff1a\u201c\u6211\u73b0\u5728\u5c31\u662f\u8981\u544a\u8bc9\u4f60\uff0c\u4f55\u6167\u4ed9\u5df2\u7ecf\u88ab\u4eba\u653e\u8d70\u4e86\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u8138\u8272\u9aa4\u53d8\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u53ef\u662f\u4f60\u76d1\u7763\u7ba1\u5236\u4e0d\u4e25\uff0c\u88ab\u5979\u79c1\u81ea\u9003\u8dd1\u4e0b\uff1f\u201d \u3000\u3000\u201c\u4f60\u522b\u6025\uff0c\u542c\u6211\u628a\u8bdd\u8bf4\u5b8c\uff0c\u6211\u521a\u624d\u4e0d\u662f\u5df2\u7ecf\u8bf4\u8fc7\u662f\u88ab\u4eba\u653e\u8d70\u7684\u5417\uff1f\u201d \u3000\u3000\u201c\u8c01\u6709\u8fd9\u4e48\u5927\u80c6\uff1f\u9664\u975e\u662f\u6559\u4e3b\u3002\u201d \u3000\u3000\u201c\u5f53\u7136\u4e0d\u662f\u6559\u4e3b\u3002\u201d \u3000\u3000\u201c\u90a3\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u57ce\u4e3b\u4e25\u5bd2\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u5927\u611f\u4e00\u6123\u9053\uff1a\u201c\u4e25\u57ce\u4e3b\u4e3a\u4ec0\u4e48\u8981\u653e\u8d70\u5979\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u6447\u5934\u9053\uff1a\u201c\u4f60\u95ee\u6211\uff0c\u6211\u53c8\u95ee\u8c01\uff1f\u90a3\u5929\u4e3a\u8fd9\u4e8b\uff0c\u6211\u8fd8\u6328\u4e86\u90a3\u738b\u516b\u86cb\u4e00\u638c\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u4f55\u6167\u4ed9\u662f\u6559\u4e3b\u4eb2\u81ea\u4e0b\u4ee4\u6253\u5165\u7075\u5802\u4e3a\u5974\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u8bf4\u4e86\u3002\u201d \u3000\u3000\u201c\u4e25\u57ce\u4e3b\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4ed6\u6743\u672c\u6ca1\u628a\u6559\u4e3b\u653e\u5728\u773c\u91cc\uff0c\u800c\u4e14\u8fde\u6559\u4e3b\u4e00\u8d77\u9a82\u4e86\u3002\u201d \u3000\u3000\u201c\u771f\u6709\u8fd9\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u9a97\u4f60\u3002\u6211\u4eca\u5929\u6765\uff0c\u4e3b\u8981\u5c31\u662f\u4e3a\u4e86\u8fd9\u4e8b\u548c\u4f60\u5546\u8bae\u3002\u201d \u3000\u3000\u201c\u76ee\u524d\u4f55\u6167\u4ed9\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u5df2\u7ecf\u88ab\u8c03\u5230\u57ce\u5e9c\uff0c\u5f88\u53ef\u80fd\u662f\u57ce\u4e3b\u90a3\u8001\u738b\u516b\u86cb\u770b\u4e0a\u4e86\u5979\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u54ac\u4e86\u54ac\u7259\u9053\uff1a\u201c\u597d\u554a\uff0c\u4e25\u5bd2\u90a3\u8001\u8d3c\u8fd9\u6837\u505a\u4e5f\u592a\u4e0d\u50cf\u8bdd\uff0c\u4ed6\u5728\u5929\u8c37\uff0c\u867d\u7136\u8eab\u4efd\u5730\u4f4d \u9ad8\u9ad8\u5728\u4e0a\uff0c\u4f46\u5374\u603b\u4e0d\u80fd\u5927\u8fc7\u6559\u4e3b\uff0c\u6559\u4e3b\u4eb2\u81ea\u4e0b\u4ee4\u6253\u5165\u7075\u5802\u4e3a\u5974\u7684\u4eba\uff0c\u4ed6\u7adf\u64c5\u81ea\u653e\u8d70\uff0c\u6211\u8981\u800c \u89c1\u6559\u4e3b\u53bb\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u9053\uff1a\u201c\u4e3a\u4e86\u8fd9\u4e8b\uff0c\u6211\u672c\u60f3\u81ea\u884c\u53bb\u9762\u62a5\u6559\u4e3b\uff0c\u4f46\u53c8\u89c9\u5f97\u81ea\u5df1\u7684\u529b\u91cf\u4e0d\u591f\uff0c\u6240\u4ee5\u624d\u6765 \u627e\u4f60\uff0c\u5728\u6559\u4e3b\u773c\u4e2d\uff0c\u4f60\u7684\u5206\u91cf\u6bd4\u6211\u66f4\u91cd\u5f97\u591a\uff0c\u6211\u6b63\u662f\u5e0c\u671b\u7531\u4f60\u53bb\u9762\u89c1\u6559\u4e3b\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e48\u5927\u7684\u4e8b\u60c5\uff0c\u6211\u5f53\u7136\u8981\u53bb\uff0c\u82e5\u7b49\u4e25\u57ce\u4e3b\u9020\u4e86\u53cd\u518d\u53bb\uff0c\u90a3\u5c31\u665a\u4e86\uff01\u201d \u3000\u3000\u201c\u5ae3\u7ea2\uff0c\u6211\u81f3\u4eca\u8fd8\u5f04\u4e0d\u6e05\u695a\u4f60\u4e3a\u4ec0\u4e48\u8981\u628a\u4f55\u6167\u4ed9\u6253\u5165\u7075\u5802\u4e3a\u5974\uff1f\u201d \u3000\u3000\u201c\u628a\u5979\u6253\u5165\u7075\u5802\u4e3a\u5974\uff0c\u90a3\u662f\u6551\u4e3b\u5f00\u6069\uff0c\u6211\u5f53\u65f6\u662f\u5e0c\u671b\u6559\u4e3b\u628a\u5979\u5904\u6b7b\u7684\uff0c\u504f\u504f\u6559\u4e3b\u5ff5\u5979\u901a \u8fc7\u4e94\u5173\u65f6\u6210\u7ee9\u4f18\u5f02\uff0c\u4e3a\u4e86\u601c\u624d\u60dc\u624d\uff0c\u624d\u9976\u8fc7\u5979\u4e00\u6b7b\uff0c\u6700\u540e\u51b3\u5b9a\u628a\u5979\u6253\u5165\u7075\u5802\u4e3a\u5974\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u5fd6\u601d\u4e86\u534a\u54cd\u9053\uff1a\u201c\u4f60\u53bb\u9762\u89c1\u6559\u4e3b\u65f6\uff0c\u6211\u8981\u4e0d\u8981\u8ddf\u4f60\u4e00\u8d77\u53bb\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u4f60\u60f3\u627e\u6b7b\u5417\uff1f\u82e5\u54b1\u4eec\u4e00\u8d77\u53bb\uff0c\u6559\u4e3b\u96be\u514d\u5c31\u4f1a\u6000\u7591\u4f60\u6211\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u5230\u90a3\u65f6 \u5c31\u6094\u4e4b\u4e0d\u53ca\u4e86\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u6559\u4e3b\u65e9\u5df2\u77e5\u9053\u4f60\u6211\u4e4b\u95f4\u5173\u7cfb\u4e0d\u9519\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u6307\u7684\u54ea\u4e00\u65b9\u9762\uff1f\u201d \u3000\u3000\u201c\u5f53\u521d\u4f60\u628a\u2018\u70bc\u5fc3\u5927\u6cd5\u2019\u79d8\u7b08\u732e\u7ed9\u6559\u4e3b\u65f6\uff0c\u4e0d\u662f\u8bf4\u8fc7\u6211\u4e5f\u66fe\u5e2e\u8fc7\u5fd9\u5417\uff0c\u5426\u5219\u6559\u4e3b\u5982\u4f55\u80fd \u4fe1\u4efb\u6211\uff1f\u53c8\u5982\u4f55\u80af\u59d4\u4efb\u6211\u505a\u9999\u4e3b\uff1f\u201d \u3000\u3000\u201c\u90a3\u53ea\u662f\u666e\u901a\u5173\u7cfb\uff0c\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u4f60\u8be5\u77e5\u9053\uff0c\u7687\u5143\u6559\u6700\u5fcc\u7684\u5c31\u662f\u624b\u4e0b\u4eba\u4e71\u641e\u7537\u5973\u5173\u7cfb\uff0c \u8fde\u592b\u59bb\u90fd\u4e0d\u51c6\u4e00\u540c\u8fdb\u5165\u5929\u8c37\uff0c\u4f60\u6211\u771f\u6b63\u5173\u7cfb\u82e5\u88ab\u6559\u4e3b\u67e5\u51fa\uff0c\u90a3\u8fd8\u5f97\u4e86\u3002\u201d \u3000\u3000\u201c\u597d\uff0c\u6211\u4e0d\u53bb\u5c31\u4e0d\u53bb\uff0c\u53ef\u662f\u6559\u4e3b\u5fc5\u5b9a\u95ee\u8d77\u4f60\u662f\u600e\u4e48\u77e5\u9053\u8fd9\u4e8b\u7684\uff0c\u4f60\u53c8\u600e\u6837\u7b54\u590d\uff1f\u201d \u3000\u3000\u201c\u6211\u81ea\u6709\u529e\u6cd5\uff0c\u81f3\u4e8e\u4f60\uff0c\u867d\u7136\u4e0d\u5fc5\u4e3b\u52a8\u9762\u89c1\u6559\u4e3b\uff0c\u4f46\u6559\u4e3b\u5374\u4f1a\u4e3b\u52a8\u4f20\u4f60\u53bb\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u7075\u5802\u9999\u4e3b\uff0c\u4f55\u6167\u4ed9\u662f\u53d7\u4f60\u7ba1\u8f96\u7684\u4eba\uff0c\u6559\u4e3b\u5f53\u7136\u4f1a\u628a\u4f60\u53eb\u53bb\u67e5\u8be2\u7ecf\u8fc7\u3002\u201d \u3000\u3000\u201c\u6709\u9053\u7406\uff0c\u8fd8\u662f\u4f60\u60f3\u7684\u6bd4\u6211\u5468\u5230\uff0c\u53ef\u89c1\u6211\u5e76\u6ca1\u767d\u6765\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u5927\u4e8b\u89e3\u51b3\u4e86\uff0c\u4f60\u8fd8\u6709\u522b\u7684\u4e8b\u6ca1\u6709\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u731b\u7136\u7ad9\u8d77\u6765\uff0c\u8d8b\u524d\u4e00\u628a\u5c06\u51e4\u5ae3\u7ea2\u7d27\u7d27\u6402\u4f4f\u9053\uff1a\u201c\u8fd9\u4e48\u4e45\u6ca1\u89c1\u9762\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u662f\u591a \u4e48\u60f3\u4f60\uff01\u201d \u3000\u3000\u63a5\u7740\uff0c\u5658\u5634\u7d27\u8d34\u51e4\u5ae3\u7ea2\u7c89\u988a\uff0c\u5c31\u543b\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u4e00\u7f29\u8eab\u5b50\uff0c\u5a07\u55d4\u9053\uff1a\u201c\u4f60\u82e5\u771f\u60f3\u6211\uff0c\u4e3a\u4ec0\u4e48\u5230\u73b0\u5728\u624d\u6765\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u547c\u5438\u6025\u4fc3\u7684\u9053\uff1a\u201c\u6211\u5148\u524d\u4e0d\u662f\u5df2\u7ecf\u5bf9\u4f60\u8bf4\u8fc7\u4e86\u5417\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u4f38\u51fa\u767d\u5982\u7389\u8f6f\u5982\u7ef5\u7684\u7ea4\u624b\u3002\u6342\u4f4f\u9a6c\u662d\u96c4\u7684\u5634\u5507\u9053\uff1a\u201c\u6b7b\u4eba\uff0c\u4f60\u82e5\u65e9\u8fd9\u4e48\u6025\uff0c\u6211\u4e5f \u7528\u4e0d\u7740\u6574\u5929\u5230\u665a\u8fde\u505a\u68a6\u4e5f\u5728\u76fc\u4f60\u4e86\uff0c\u8fd9\u91cc\u4e0d\u662f\u529e\u90a3\u4e8b\u7684\u5730\u65b9\uff0c\u677e\u5f00\u624b\uff0c\u5230\u5367\u623f\u53bb\u3002\u201d \u3000\u3000\u5ba2\u5385\u65f6\u6709\u9053\u4fa7\u95e8\uff0c\u53ef\u4ee5\u76f4\u901a\u5367\u623f\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u5e76\u6ca1\u677e\u624b\uff0c\u53cd\u800c\u6402\u5f97\u66f4\u7d27\uff0c\u7136\u540e\u628a\u51e4\u5ae3\u7ea2\u6258\u8d77\u76f4\u62b1\u5230\u5367\u623f\u3002 \u3000\u3000\u63a5\u7740\u518d\u628a\u51e4\u5ae3\u7ea2\u6254\u4e0a\u5e8a\uff0c\u8fde\u978b\u5b50\u90fd\u6ca1\u8131\uff0c\u4fbf\u997f\u864e\u6251\u7f8a\u822c\u6251\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u5403\u5403\u7b11\u7740\uff0c\u4e00\u628a\u5c06\u9a6c\u662d\u96c4\u63a8\u5230\u4e00\u4fa7\uff0c\u63a5\u7740\u5728\u4ed6\u9762\u988a\u4e0a\u62e7\u4e86\u4e00\u628a\u9053\uff1a\u201c\u6b7b\u9b3c\uff0c\u600e\u4e48 \u6025\u6210\u8fd9\u6837\u5b50\uff0c\u6162\u4e00\u70b9\u4e0d\u6210\u5417\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u8fd9\u624d\u60f3\u8d77\u5f88\u591a\u969c\u788d\u8fd8\u6ca1\u626b\u9664\u3002 \u3000\u3000\u4ed6\u8df3\u4e0b\u5e8a\u5173\u4e0a\u623f\u95e8\uff0c\u518d\u8df3\u4e0a\u5e8a\uff0c\u52a8\u4f5c\u5feb\u5f97\u4e0d\u80fd\u518d\u5feb\u7684\u8f6c\u77ac\u4fbf\u628a\u81ea\u5df1\u8131\u4e86\u4e2a\u8d64\u6761\u7cbe\u5149\u3002 \u3000\u3000\u8fd9\u5c0f\u5b50\u679c\u7136\u4f53\u683c\u7ed3\u5b9e\uff0c\u5c24\u5176\u80f8\u524d\u4e1b\u751f\u7684\u90a3\u4e00\u64ae\u9ed1\u6bdb\uff0c\u6b63\u7ecf\u4eba\u770b\u4e86\u6076\u5fc3\uff0c\u51e4\u5ae3\u7ea2\u770b\u4e86\u5374\u662f \u6253\u5fc3\u5e95\u559c\u6b22\u3002 \u3000\u3000\u8c01\u77e5\u8fd9\u65f6\u51e4\u5ae3\u7ea2\u5374\u662f\u772f\u7740\u7738\u5b50\u4ef0\u5367\u5728\u90a3\u91cc\uff0c\u597d\u50cf\u534a\u70b9\u4e0d\u6025\u7684\u6a21\u6837\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u4fef\u4e0b\u8eab\u53bb\uff0c\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff0c\u83ab\u975e\u53c8\u4e0d\u60f3\u4e86\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u7684\u58f0\u97f3\uff0c\u50cf\u662f\u4ece\u9f3b\u5b54\u91cc\u54fc\u51fa\u6765\u7684\uff1a\u201c\u51a4\u5bb6\uff0c\u8fd9\u79cd\u4e8b\u60c5\uff0c\u96be\u9053\u8981\u5e08\u5a18\u4eb2\u81ea\u52a8\u624b\uff0c\u4f60 \u6eda\u4e3a\u5e08\u5a18\u670d\u52a1\u670d\u52a1\u624d\u5bf9\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u5982\u5949\u5723\u65e8\uff0c\u5f88\u5feb\u5c31\u628a\u51e4\u5ae3\u7ea2\u7684\u4e0a\u8eab\u5265\u5149\u3002 \u3000\u3000\u90a3\u575a\u633a\u4e30\u8174\u7684\u53cc\u5cf0\uff0c\u65e9\u5df2\u770b\u5f97\u4ed6\u4e24\u773c\u53d1\u76f4\u3002 \u3000\u3000\u4ed6\u987e\u4e0d\u5f97\u4e0b\u4e00\u6b65\uff0c\u6025\u6025\u4fef\u4e0b\u8eab\u53bb\uff0c\u7528\u5634\u8854\u4f4f\u731b\u542e\u4e0b\u53bb\uff0c\u6765\u4e86\u4e2a\u201c\u541b\u5b50\u52a8\u53e3\u4e0d\u52a8\u624b\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u626d\u52a8\u7740\u8eab\u5b50\uff0c\u4e00\u53ea\u624b\u5374\u6309\u4e0a\u4e86\u9a6c\u662d\u96c4\u7684\u5934\uff0c\u53d1\u51fa\u68a6\u5453\u822c\u7684\u9635\u9635\u5a07\u7b11\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u542e\u8fc7\u4e86\u8fd9\u4e00\u8fb9\uff0c\u518d\u542e\u90a3\u4e00\u8fb9\u3002 \u3000\u3000\u53ea\u542c\u51e4\u5ae3\u7ea2\u5403\u5403\u7b11\u9053\uff1a\u201c\u597d\u513f\u5b50\uff0c\u522b\u53ea\u987e\u5403\u5976\uff0c\u8fd8\u6709\u66f4\u597d\u7684\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u8fd9\u624d\u62ac\u8d77\u4e86\u8eab\u5b50\uff0c\u987a\u7740\u51e4\u5ae3\u7ea2\u767d\u5982\u51dd\u8102\u7684\u5c0f\u8179\uff0c\u53cc\u624b\u5f80\u4e0b\u4e00\u6ed1\uff0c\u987f\u65f6\uff0c\u51e4\u5ae3\u7ea2\u5df2 \u4e00\u4e1d\u4e0d\u6302\u7389\u4f53\u6a2a\u9648\u5728\u4ed6\u9762\u524d\u3002 \u3000\u3000\u4ed6\u7684\u53cc\u773c\u672c\u6765\u5df2\u76f4\uff0c\u8fd9\u65f6\u66f4\u50cf\u8981\u5192\u51fa\u706b\u6765\uff0c\u800c\u53e6\u4e00\u80a1\u5947\u70c8\u65e0\u6bd4\u7684\u706b\uff0c\u53c8\u76f4\u51b2\u5c0f\u8179\u4e4b\u4e0b\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u3000\u4e00\u7537\u4e00\u5973\uff0c\u771f\u7684\u5408\u800c\u4e3a\u4e00\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\u3002\u4f3c\u4e4e\u5929\u5728\u65cb\uff0c\u5730\u5728\u9677\uff0c\u800c\u5e8a\u4ecd\u4e5f\u5728\u65e0\u98ce\u81ea\u6447\uff0c\u5939\u6742\u7740\u68a6\u5453\u3001\u547b\u541f\u58f0\u3001\u5a07\u7b11\u58f0\u3001 \u5598\u606f\u58f0\uff0c\u7b80\u76f4\u4ee4\u4eba\u5206\u4e0d\u6e05\u8fd9\u662f\u90a3\u4e00\u9996\u4e50\u7ae0\u91cc\u7684\u63d2\u66f2\u3002 \u3000\u3000\u603b\u4e4b\uff0c\u5c31\u662f\u5982\u6b64\u800c\u5df2\u3002 \u3000\u3000\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u51e4\u5ae3\u7ea2\u548c\u9a6c\u662d\u96c4\u5df2\u7a7f\u597d\u8863\u670d\u65f6\u5750\u5e8a\u4e0a\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u4f3c\u662f\u7cbe\u795e\u5c1a\u672a\u7528\u5b8c\uff0c\u6d8e\u7740\u8138\u9053\uff1a\u201c\u5ae3\u7ea2\u3002\u6211\u770b\u4f60\u662f\u8d8a\u6765\u8d8a\u5389\u5bb3\u4e86\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u659c\u76ee\u779f\u8fc7\u6765\u4e00\u773c\u9053\uff1a\u201c\u96be\u9053\u4f60\u4e0d\u5389\u5bb3\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u54a7\u5634\u7b11\u9053\uff1a\u201c\u6211\u5389\u5bb3\u662f\u6709\u539f\u56e0\u7684\uff0c\u517b\u7cbe\u84c4\u9510\u8fd9\u4e48\u4e45\uff0c\u5f53\u7136\u8981\u6bd4\u4ece\u524d\u5929\u5929\u5728\u4e00\u8d77\u65f6 \u5389\u5bb3\u5f97\u591a\u3002\u8fd9\u53eb\u517b\u5175\u5343\u65e5\uff0c\u7528\u5728\u4e00\u65f6\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u51b7\u7b11\u9053\uff1a\u201c\u6211\u770b\u4f60\u4e0d\u81f3\u4e8e\u8fd9\u6837\u53ef\u601c\u5427\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u8bdd\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u542c\u8bf4\u5bcc\u56fd\u57ce\u90a3\u8fb9\u6709\u5904\u7f8e\u4eba\u5173\uff0c\u5b9e\u9645\u4e0a\u662f\u5f00\u7a91\u5b50\u9986\u7684\uff0c\u72d7\u8d70\u5230\u5929\u8fb9\u5fd8\u4e0d\u4e86\u5403 \u5c4e\uff0c\u4f60\u4e00\u5b9a\u662f\u90a3\u91cc\u7684\u8001\u987e\u5ba2\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u51e0\u4e4e\u8981\u4e3e\u8d77\u624b\u6765\u53d1\u8a93\u9053\uff1a\u201c\u4f60\u522b\u51a4\u6789\u597d\u4eba\uff0c\u6211\u82e5\u6d89\u8db3\u90a3\u79cd\u5730\u65b9\uff0c\u5c31\u4e0d\u5f97\u597d\u6b7b\uff0c\u4f60 \u8bf4\u8fd9\u79cd\u8bdd\uff0c\u7b80\u76f4\u662f\u95e8\u7f1d\u91cc\u77a7\u4eba\uff0c\u628a\u4eba\u770b\u6241\u4e86\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u6ca1\u5230\u8fc7\u90a3\u5730\u65b9\uff0c\u4e5f\u96be\u4fdd\u4e0d\u53e6\u6253\u91ce\u98df\uff1f\u201d \u3000\u3000\u201c\u5c82\u6709\u6b64\u7406\uff0c\u54ea\u91cc\u6709\u91ce\u98df\u53ef\u6253\uff1f\u201d \u3000\u3000\u201c\u7075\u5802\u5750\u5c31\u6709\u73b0\u6210\u7684\uff1f\u201d \u3000\u3000\u201c\u4f60\u8bf4\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u4f55\u6167\u4ed9\u5c31\u591f\u4e86\uff0c\u5979\u957f\u5f97\u4e0d\u6bd4\u6211\u5dee\uff0c\u6211\u4e0d\u76f8\u4fe1\u4f60\u6ca1\u52a8\u8fc7\u5979\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u51e0\u4e4e\u53c8\u8981\u53d1\u8a93\uff1a\u201c\u5ae3\u7ea2\uff0c\u6211\u770b\u4f60\u662f\u8d8a\u6765\u8d8a\u4e0d\u8be5\u4e86\uff0c\u5979\u662f\u4e2a\u7f6a\u72af\uff0c\u6211\u600e\u53ef\u968f\u4fbf\u52a8\u5979\uff0c \u800c\u4e14\u5979\u6709\u4e00\u8eab\u6b66\u529f\uff0c\u4e5f\u4e0d\u662f\u90a3\u4e48\u597d\u52a8\u7684\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u7b11\u9053\uff1a\u201c\u770b\u3002\u8fd9\u4e0d\u662f\u4e0d\u6253\u81ea\u62db\u4e86\u5417\uff1f\u4e0d\u8fc7\u5979\u4e0d\u80af\u4f9d\uff0c\u90a3\u6211\u5012\u76f8\u4fe1\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u5728\u51e4\u5ae3\u7ea2\u5c0f\u8179\u8f7b\u62cd\u4e86\u4e00\u4e0b\u9053\uff1a\u201c\u6211\u770b\u4f60\u624d\u662f\u60f3\u4e71\u6253\u91ce\u98df\u5462\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u7fe0\u7709\u4e00\u8038\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u6211\u8ddf\u8c01\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u9053\uff1a\u201c\u4f60\u5bb5\u5173\u4e0a\u7684\u7537\u4eba\uff0c\u4e5f\u4e0d\u5728\u5c11\u6570\uff0c\u53ea\u8981\u662f\u7537\u4eba\uff0c\u90a3\u4e2a\u4e0d\u53ef\u4ee5\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u4e00\u8033\u5149\u76f4\u5411\u9a6c\u662d\u96c4\u63b4\u6765\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u504f\u5934\u95ea\u8fc7\uff0c\u987a\u624b\u6293\u4f4f\u51e4\u5ae3\u7ea2\u7684\u7389\u8155\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u522b\u6253\u522b\u6253\uff0c\u7b97\u6211\u8bf4\u9519\u4e86\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u5a07\u53f1\u9053\uff1a\u201c\u4f60\u518d\u80e1\u8bf4\u516b\u9053\uff0c\u770b\u6211\u6495\u4e0d\u6495\u4e86\u4f60\u7684\u72d7\u5634\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u5411\u7a97\u5916\u671b\u4e86\u671b\u5929\u8272\u9053\uff1a\u201c\u65f6\u95f4\u4e0d\u65e9\uff0c\u6211\u8be5\u8d70\u5566\uff01\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u5feb\u5c31\u8981\u8d70\uff1f\u201d \u3000\u3000\u201c\u6211\u5f53\u7136\u4e0d\u60f3\u8d70\uff0c\u4f46\u51fa\u4e86\u4e8b\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u4f60\u62c5\u5fc3\u51fa\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e25\u5bd2\u90a3\u8001\u8d3c\u76ef\u5f97\u7d27\uff0c\u5c24\u5176\u8fd9\u513f\u5929\u6b63\u5728\u98ce\u5934\u4e0a\uff0c\u4e07\u4e00\u4ed6\u665a\u4e0a\u6d3e\u5468\u603b\u7ba1\u53bb\u67e5\u591c\u3002\u4ee5\u2018\u591c\u4e0d \u5f52\u57ce\u2019\u7684\u7f6a\u540d\u529e\u6211\uff0c\u90a3\u5c31\u60e8\u5566\uff01\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u4ec0\u4e48\u65f6\u5019\u518d\u6765\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u6211\u5230\u673a\u4f1a\uff0c\u4e00\u5b9a\u5f88\u5feb\u518d\u6765\u770b\u4f60\u3002\u201d \u3000\u3000\u201c\u5e0c\u671b\u4f60\u8bf4\u8bdd\u7b97\u8bdd\uff0c\u522b\u8ba9\u6211\u72ec\u5b88\u7a7a\u5e0f\u82e6\u7b49\uff0c\u4f60\u77e5\u9053\u5e08\u5a18\u95f2\u7740\u591a\u96be\u53d7\uff01\u201d \u3000\u3000\u201c\u653e\u5fc3\u5427\uff0c\u6211\u7684\u5b9d\u8d1d\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u8bf4\u7740\uff0c\u53c8\u5c06\u51e4\u5ae3\u7ea2\u6402\u7d27\uff0c\u5634\u5df4\u50cf\u96e8\u70b9\u822c\u7684\u5728\u51e4\u5ae3\u7ea2\u7c89\u988a\u4e0a\u731b\u543b\u4e71\u543b\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u88ab\u9a6c\u662d\u96c4\u52fe\u8d77\u4e86\u4e8c\u5ea6\u6b32\u5ff5\uff0c\u7adf\u81ea\u52a8\u53c8\u4e3a\u9a6c\u662d\u96c4\u89e3\u5f00\u4e86\u8863\u670d\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u90a3\u91cc\u6309\u5948\u5f97\u4f4f\uff0c\u4e8e\u662f\uff0c\u4e24\u4eba\u53c8\u8fdb\u5165\u4e86\u7b2c\u4e8c\u4e2a\u6625\u5929\u3002\u5f53\u4e24\u4eba\u518d\u5ea6\u7a7f\u597d\u8863\u670d\u65f6\uff0c\u9a6c\u662d \u96c4\u7eb5\u7136\u518d\u7559\u604b\uff0c\u4e5f\u4e0d\u5f97\u4e0d\u8d70\u4e86\u3002 \u3000\u3000\u4ed6\u91cd\u65b0\u6234\u8d77\u4e86\u90a3\u526f\u8001\u4eba\u4eba\u76ae\u9762\u5177\uff0c\u4e0b\u4e86\u5e8a\uff0c\u7531\u91cc\u9762\u518d\u56de\u5230\u5ba2\u5385\u3002\u51e4\u5ae3\u7ea2\u4e5f\u8ddf\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5979\u50cf\u65f1\u7530\u5f97\u5230\u4e86\u7518\u9716\uff0c\u53cd\u800c\u7cbe\u795e\u65fa\u76db\uff0c\u5bb9\u5149\u7115\u53d1\u3002 \u3000\u3000\u4f46\u9a6c\u662d\u96c4\u5374\u6709\u4e9b\u75b2\u60eb\u6a21\u6837\uff0c\u5782\u5934\u4e27\u6c14\uff0c\u50cf\u4e2a\u5927\u5b5d\u5b50\u822c\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u5728\u4ed6\u80a9\u4e0a\u62cd\u4e86\u4e00\u628a\u9053\uff1a\u201c\u51a4\u5bb6\uff0c\u770b\u4f60\u7684\u6837\u5b50\uff0c\u597d\u50cf\u6ca1\u6709\u4ee5\u524d\u4e2d\u7528\u4e86\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u633a\u4e86\u633a\u80f8\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd9\u6837\u8bf4\u8bdd\uff1f\u521a\u624d\u6211\u4e0d\u662f\u5f88\u4e2d\u7528\u7684\u5417\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u53ef\u662f\u770b\u4f60\u73b0\u5728\u8fd9\u6837\u5b50\uff0c\u597d\u50cf\u652f\u6301\u4e0d\u4f4f\u7b2c\u4e09\u56de\u5408\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u8038\u4e86\u8038\u80a9\u9053\uff1a\u201c\u7b97\u4e86\u5427\uff0c\u4e0b\u6b21\u6211\u4f1a\u8ba9\u4f60\u670d\u6c14\u7684\u3002\u8fd9\u4e9b\u5929\u53d7\u5c3d\u4e86\u4e25\u5bd2\u90a3\u8001\u8d3c\u7684\u6c14\uff0c \u60f3\u8d77\u6765\u5c31\u7a9d\u56ca\uff0c\u4f60\u8bf4\u6211\u4e0d\u5982\u4ece\u524d\uff0c\u4e5f\u8bb8\u662f\u771f\u7684\uff0c\u8fd8\u5fd8\u4e86\u95ee\uff0c\u4f60\u51c6\u5907\u4ec0\u4e48\u65f6\u5019\u53bb\u9762\u89c1\u6559\u4e3b\uff1f\u201d \u3000\u3000\u201c\u4f60\u8bf4\u5462\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u8d8a\u5feb\u8d8a\u597d\u3002\u201d \u3000\u3000\u201c\u6211\u542c\u4f60\u7684\uff0c\u8fd9\u4e24\u5929\u4e00\u5b9a\u53bb\u3002\u201d \u3000\u3000\u201c\u53ea\u8981\u6273\u5012\u4e86\u4e25\u5bd2\u8001\u8d3c\u3002\u4ee5\u540e\u54b1\u4eec\u5c31\u53ef\u5e38\u5e38\u89c1\u9762\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u8d70\u51fa\u5bb5\u5173\uff0c\u8111\u6d77\u91cc\u8fd8\u662f\u8426\u7ed5\u7740\u65b9\u624d\u5728\u51e4\u5ae3\u7ea2\u5367\u623f\u91cc\u90a3\u8fde\u8fc7\u4e24\u5173\u7684\u60c5\u666f\uff0c\u5728\u4ed6\u6765\u8bf4\uff0c \u591f\u5f97\u4e0a\u662f\u56de\u5473\u65e0\u7a77\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u592a\u9633\u5df2\u5c06\u4e0b\u5c71\uff0c\u9884\u8ba1\u7740\u8d76\u56de\u80b2\u5316\u57ce\uff0c\u81f3\u5c11\u5df2\u662f\u4e00\u66f4\u5c06\u5c3d\u4e86\u3002 \u3000\u3000\u4ed6\u968f\u5373\u628a\u90a3\u526f\u4eba\u76ae\u9762\u5177\u5378\u4e0b\u4e86\u6765\u63e3\u597d\uff0c\u56e0\u4e3a\u8fd9\u65f6\u5df2\u7528\u4e0d\u7740\u5b83\uff0c\u800c\u4e14\u6234\u4e0a\u5b83\u540e\u628a\u9762\u76ae\u7ef7\u5f97 \u7d27\u7d27\u7684\u4e5f\u4e0d\u8212\u670d\u3002 \u3000\u3000\u5c24\u5176\u4ed6\u4e3a\u4e86\u65bd\u5c55\u8f7b\u529f\u5feb\u4e9b\u8d76\u8def\uff0c\u53d8\u6210\u8001\u4eba\u6a21\u6837\u53cd\u800c\u5bb9\u6613\u5f15\u4eba\u751f\u7591\u3002 \u3000\u3000\u6b63\u8d70\u4e4b\u95f4\uff0c\u84e6\u542c\u8033\u65c1\u54cd\u8d77\u4e00\u58f0\u6c89\u6d51\u6709\u529b\u7684\u51b7\u559d\u9053\uff1a\u201c\u9a6c\u662d\u96c4\uff0c\u4eca\u5929\u770b\u4f60\u8fd8\u5f80\u54ea\u91cc\u8d70\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u731b\u5403\u4e00\u60ca\uff0c\u4ed6\u95fb\u58f0\u6536\u4f4f\u811a\u6b65\uff0c\u53ea\u89c1\u8def\u65c1\u4e09\u4e08\u5916\u7684\u571f\u5761\u4e0a\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\uff0c\u7adf\u7ad9\u7740 \u4e00\u540d\u4e8c\u5341\u5f00\u5916\u4e09\u5341\u4e0d\u5230\u7684\u58ee\u6c49\u3002 \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u75db\u60e9\u6deb\u8d3c \u3000\u3000\u9a6c\u662d\u96c4\u5f53\u7136\u8ba4\u4e0d\u51fa\u8fd9\u4eba\u662f\u8c01\u3002 \u3000\u3000\u56e0\u4e3a\u8fd9\u4eba\u662f\u6234\u7740\u4eba\u76ae\u9762\u5177\u3002 \u3000\u3000\u9762\u5177\u867d\u8db3\u5cb3\u5c0f\u98de\u6240\u6709\uff0c\u6b64\u523b\u6234\u7740\u5b83\u7ad9\u5728\u571f\u5761\u4e0a\u7684\u5374\u662f\u8881\u5c0f\u9e64\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u9a6c\u662d\u96c4\u201c\u5237\u201d\u7684\u4e00\u58f0\uff0c\u62bd\u51fa\u957f\u5251\uff0c\u559d\u9053\uff1a\u201c\u5c0a\u9a7e\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u51b7\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u867d\u7136\u4e0d\u8ba4\u8bc6\u5728\u4e0b\uff0c\u4f46\u5728\u4e0b\u5374\u8ba4\u8bc6\u4f60\u662f\u53db\u5e08\u706d\u7956\u53c8\u517c\u4e71\u4f26\u7684\u6b66\u6797\u8d25 \u7c7b\u9a6c\u662d\u96c4\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8bf4\u8bdd\u65f6\uff0c\u6545\u610f\u618b\u7740\u55d3\u95e8\u4f7f\u58f0\u97f3\u53d8\u8c03\uff0c\u4e0d\u8ba9\u5bf9\u65b9\u542c\u51fa\u662f\u8c01\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u53cc\u76ee\u51f6\u5149\u66b4\u5c04\uff0c\u518d\u5ea6\u559d\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u8c01\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5728\u4e0b\u5c31\u662f\u8981\u4e3a\u6b66\u6797\u9664\u5978\u6740\u6389\u4f60\u8fd9\u8d25\u7c7b\u7684\u4eba\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u989d\u89d2\u9752\u7b4b\u76f4\u5192\uff0c\u543c\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u4e0d\u8bc6\u62ac\u4e3e\u7684\u4e1c\u897f\uff0c\u6211\u770b\u4f60\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u81ea\u5bfb\u6b7b \u8def\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u6ca1\u6709\u4e09\u5206\u4e09\uff0c\u600e\u6562\u4e0a\u6881\u5c71\uff0c\u7528\u4e0d\u7740\u8bf4\u5927\u8bdd\uff0c\u6211\u82e5\u6015\u4e86\u4f60\uff0c\u4eca\u5929\u4e5f\u5c31\u4e0d\u6765 \u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u4eba\u5df2\u7f13\u7f13\u8d70\u4e0b\u571f\u5761\uff0c\u76f4\u5728\u9a6c\u662d\u96c4\u8eab\u524d\u4e03\u516b\u5c3a\u5904\u7ad9\u4f4f\uff0c\u800c\u4e14\u624b\u91cc\u4e5f\u65e9\u5df2\u6a2a\u7740\u4e00\u67c4 \u957f\u5251\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u731b\u4e00\u8dc3\u8eab\uff0c\u957f\u5251\u76f4\u5411\u8881\u5c0f\u9e64\u659c\u80a9\u5e26\u80cc\u5288\u53bb\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u4e0d\u95ea\u4e0d\u8eb2\u3002\u4e00\u54ac\u7259\uff0c\u4e00\u62db\u201c\u6a2a\u67b6\u91d1\u6881\u201d\uff0c\u624b\u4e2d\u957f\u5251\u786c\u662f\u5411\u5bf9\u65b9\u5288\u6765\u5251\u52bf\u4e0a\u786c\u78b0\u786c \u7684\u8fce\u53bb\u3002 \u3000\u3000\u53cc\u523d\u4e00\u63a5\uff0c\u4e00\u58f0\u91d1\u94c1\u5927\u9707\uff0c\u706b\u661f\u8ff8\u98de\u4e0b\uff0c\u5404\u81ea\u88ab\u9707\u9000\u4e09\u5c3a\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u53ea\u89c9\u864e\u53e3\u4e00\u9ebb\uff0c\u773c\u524d\u4e00\u9ed1\uff0c\u9669\u4e9b\u62ff\u693f\u4e0d\u4f4f\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u4e5f\u88ab\u9707\u5f97\u53f3\u81c2\u75b2\u75db\uff0c\u80f8\u53e3\u6c14\u8840\u6d6e\u52a8\u4e0d\u5df2\u3002 \u3000\u3000\u4e24\u4eba\u5728\u63a5\u8fc7\u4e00\u62db\u4e4b\u540e\uff0c\u4f3c\u4e4e\u90fd\u6697\u6697\u5fc3\u60ca\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u6ca1\u6599\u5230\u5bf9\u624b\u7adf\u662f\u5982\u6b64\u5f3a\u786c\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u4e5f\u89c9\u5f97\u81ea\u5df1\u7684\u5185\u529b\uff0c\u4f3c\u4e4e\u4ecd\u4e0d\u5982\u5bf9\u65b9\u3002 \u3000\u3000\u5728\u5de6\u9762\u770b\u6765\uff0c\u8fd9\u4e00\u62db\u7b97\u662f\u5e73\u5206\u79cb\u8272\u3002 \u3000\u3000\u4f46\u5b9e\u9645\u4e0a\u662f\u56e0\u65b9\u624d\u9a6c\u662d\u96c4\u5728\u51e4\u5ae3\u7ea2\u5904\u5df2\u8017\u53bb\u4e0d\u5c11\u4f53\u529b\uff0c\u8eab\u4e0a\u96be\u514d\u8f83\u5e73\u65f6\u865a\u5f31\uff0c\u5426\u5219\uff0c\u8fd9 \u4e00\u62db\u4ed6\u5fc5\u53ef\u5360\u5230\u4e0a\u98ce\u3002 \u3000\u3000\u987b\u77e5\uff0c\u8fd9\u4e09\u5e74\u6765\uff0c\u8881\u5c0f\u9e64\u867d\u7136\u6b66\u529f\u7a81\u98de\u731b\u8fdb\uff0c\u9a6c\u662d\u96c4\u7167\u6837\u4e5f\u6709\u8fdb\u5883\u3002 \u3000\u3000\u4e24\u4eba\u7565\u4e00\u5598\u606f\uff0c\u4fbf\u53c8\u518d\u5ea6\u5404\u81ea\u51fa\u624b\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\uff0c\u53cc\u65b9\u5168\u90fd\u536f\u4e0a\u4e86\u52b2\uff0c\u5404\u81ea\u62ff\u51fa\u770b\u5bb6\u672c\u9886\u8fce\u654c\u3002 \u3000\u3000\u8f6c\u77ac\u95f4\u4fbf\u5df2\u62da\u6597\u4e86\u4e8c\u4e09\u5341\u5408\uff0c\u6e10\u6e10\uff0c\u4e24\u4eba\u5168\u88ab\u4e00\u56e2\u5251\u6c14\u7f69\u4f4f\uff0c\u5175\u5203\u4ea4\u9519\u63a5\u51fb\u4e4b\u58f0\uff0c\u51e0\u4e4e \u5feb\u5f97\u4e0d\u80fd\u518d\u5feb\uff0c\u58f0\u58f0\u9707\u52a8\u7740\u4e24\u4eba\u7684\u5fc3\u5f26\u3002 \u3000\u3000\u5230\u540e\u6765\uff0c\u5251\u6c14\u8d8a\u6765\u8d8a\u76db\uff0c\u5df2\u6839\u672c\u65e0\u6cd5\u770b\u5230\u4e24\u4eba\u7684\u8eab\u5f62\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u89c9\u51fa\u65e0\u6cd5\u53d6\u80dc\uff0c\u5fc3\u795e\u4e00\u52a8\uff0c\u7acb\u65f6\u60f3\u8d77\u5e08\u7236\u5e90\u4e91\u8bf4\u8fc7\uff1a \u3000\u3000\u201c\u5c06\u6765\u82e5\u4e0e\u9a6c\u662d\u96c4\u5bf9\u654c\u65e0\u6cd5\u53d6\u80dc\u65f6\uff0c\u5fc5\u987b\u4ee5\u2018\u98ce\u96f7\u4e09\u5f0f\u2019\u624d\u80fd\u514b\u5236\u4ed6\u3002\u201d\u7684\u8bdd\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u5251\u6cd5\u4e00\u53d8\uff0c\u65bd\u5c55\u51fa\u201c\u98ce\u96f7\u4e09\u5f0f\u201d\u62db\u5f0f\u3002 \u3000\u3000\u679c\u7136\uff0c\u201c\u98ce\u96f7\u4e09\u5f0f\u201d\u65bd\u5c55\u5f00\u6765\uff0c\u5a01\u52bf\u5927\u589e\uff0c\u8feb\u5f97\u9a6c\u662d\u96c4\u4e00\u9635\u624b\u5fd9\u811a\u4e71\uff0c\u8fde\u8fde\u5411\u540e\u76f4\u9000\u51fa \u4e03\u516b\u6b65\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u5185\u5fc3\u6697\u559c\uff0c\u968f\u5373\u4e5f\u6b65\u6b65\u8fdb\u903c\u3002 \u3000\u3000\u5c82\u77e5\u5f53\u4ed6\u7684\u201c\u98ce\u96f7\u4e09\u5f0f\u201d\u5168\u90e8\u65bd\u5c55\u5b8c\u6bd5\u8fc7\u540e\uff0c\u7adf\u53c8\u88ab\u5bf9\u65b9\u7684\u5251\u52bf\u538b\u5236\u4e0b\u6765\uff0c\u8feb\u5f97\u4ed6\u4e5f\u8fde \u8fde\u5411\u540e\u8d25\u9000\u3002 \u3000\u3000\u8f6c\u77ac\u95f4\u53c8\u662f\u51e0\u5341\u56de\u5408\u8fc7\u53bb\uff0c\u8881\u5c0f\u9e64\u5df2\u5927\u6709\u7b4b\u75b2\u529b\u5c3d\u4e4b\u6982\u3002\u4e0d\u5f97\u5df2\uff0c\u5728\u62da\u529b\u653b\u51fa\u51e0\u62db\u4e4b\u540e\uff0c \u968f\u5373\u4e00\u4e2a\u5012\u7eb5\uff0c\u8dc3\u9000\u5411\u6570\u4e08\u4e4b\u5916\u3002 \u3000\u3000\u4ed6\u90a3\u91cc\u77e5\u9053\uff0c\u6b64\u523b\u9a6c\u662d\u96c4\u4e5f\u7167\u6837\u65e0\u529b\u518d\u6218\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u9a6c\u662d\u96c4\u5f53\u7136\u5e76\u4e0d\u8ffd\u88ad\u3002 \u3000\u3000\u53cc\u65b9\u7a33\u4f4f\u4e4b\u540e\uff0c\u90fd\u5df2\u9762\u8272\u53d1\u767d\uff0c\u989d\u89d2\u4e0a\u5192\u7740\u8c46\u5927\u7684\u6c57\u73e0\uff0c\u5404\u81ea\u62c4\u5251\u72c2\u5598\u4e0d\u5df2\u3002 \u3000\u3000\u4f46\u9a6c\u662d\u96c4\u5728\u8868\u9762\u4e0a\u4ecd\u4e0d\u7518\u793a\u5f31\uff0c\u5598\u606f\u8fc7\u540e\uff0c\u632f\u58f0\u559d\u9053\uff1a\u201c\u4f60\u53ef\u662f\u81ea\u8ba4\u4e0d\u662f\u9a6c\u67d0\u5bf9\u624b\uff0c\u6240 \u4ee5\u624d\u4e0d\u6562\u518d\u6218\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5c0a\u9a7e\u82e5\u8fd8\u81ea\u4fe1\u80fd\u529b\u518d\u6218\uff0c\u518d\u4e0b\u7167\u6837\u5949\u966a\u4e0d\u8bef\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4eca\u5929\u6682\u65f6\u9976\u8fc7\u4f60\u4e00\u6761\u72d7\u547d\uff0c\u4e0b\u6b21\u78b0\u4e0a\uff0c\u5c31\u522b\u602a\u9a6c\u67d0\u624b\u4e0b\u65e0\u60c5\u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u90a3\u5c31\u8bf7\u4f60\u6eda\u86cb\u5427\uff0c\u5728\u4e0b\u4eca\u5929\u4e5f\u53d1\u53d1\u6148\u60b2\uff0c\u7559\u4e0b\u4f60\u4e00\u6761\u72d7\u547d\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u6599\u60f3\u518d\u6218\u4e5f\u5fc5\u65e0\u6cd5\u5236\u670d\u5bf9\u65b9\uff0c\u53c8\u6025\u7740\u8d76\u56de\u80b2\u5316\u57ce\uff0c\u53ea\u5f97\u8fd8\u5251\u5165\u9798\uff0c\u8f6c\u8eab\u800c\u53bb\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u89c1\u4ed6\u5df2\u6e10\u8d70\u8fdc\uff0c\u7acb\u5373\u518d\u56de\u5230\u571f\u5761\u4e4b\u540e\u3002 \u3000\u3000\u571f\u5761\u4e4b\u540e\uff0c\u8d6b\u7136\u85cf\u7740\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u5374\u662f\u5cb3\u5c0f\u98de\u3002 \u3000\u3000\u539f\u6765\u8881\u5c0f\u9e64\u5148\u524d\u662f\u548c\u5cb3\u5c0f\u98de\u4e00\u540c\u9690\u8eab\u5728\u571f\u5761\u540e\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u4e24\u4eba\uff0c\u672c\u6765\u662f\u4e00\u540c\u5de1\u5173\uff0c\u65e9\u5c31\u53d1\u73b0\u4e00\u540d\u8001\u8005\u7531\u5bb5\u5173\u5927\u95e8\u51fa\u6765\uff0c\u5f53\u65f6\u5c31\u89c9\u51fa\u8fd9\u8001\u8005\u7684 \u8eab\u4efd\u5f62\u8ff9\u53ef\u7591\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u4fbf\u6697\u6697\u5728\u540e\u8ddf\u8e2a\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u773c\u5c16\uff0c\u5f88\u5feb\u4fbf\u8ba4\u51fa\u8fd9\u8001\u8005\u7684\u4f53\u5f62\u548c\u4e3e\u52a8\u5f88\u50cf\u9a6c\u662d\u96c4\u3002 \u3000\u3000\u679c\u7136\u4e0d\u4e45\u4e4b\u540e\uff0c\u9a6c\u662d\u96c4\u5c31\u5378\u53bb\u4e86\u4eba\u76ae\u9762\u5177\uff0c\u73b0\u51fa\u539f\u5f62\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4e24\u4eba\u7531\u53e6\u4e00\u6761\u5c94\u8def\uff0c\u8d85\u8d8a\u5230\u9a6c\u662d\u96c4\u524d\u9762\uff0c\u7136\u540e\u518d\u6298\u8f6c\u65b9\u5411\u6765\u5230\u9a6c\u662d\u96c4\u5fc5\u7ecf\u4e4b\u8def\u4e00 \u4fa7\u7684\u571f\u5761\u540e\u85cf\u597d\u8eab\u5f62\uff0c\u4ee5\u5f85\u5bf9\u65b9\u5230\u8fbe\u540e\u518d\u91c7\u53d6\u884c\u52a8\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u4e3a\u4e86\u6025\u4e8e\u4e00\u8bd5\u81ea\u5df1\u76ee\u524d\u7684\u8eab\u624b\u662f\u5426\u53ef\u4e0e\u9a6c\u662d\u96c4\u6297\u8861\uff0c\u4fbf\u5148\u6234\u4e86\u4eba\u76ae\u9762\u5177\u8dc3\u51fa\u571f\u5761\u3002 \u3000\u3000\u7ecf\u8fc7\u53cc\u65b9\u4e00\u573a\u6fc0\u70c8\u62da\u640f\u540e\uff0c\u4ed6\u7ec8\u4e8e\u6709\u4e86\u6570\uff0c\u81ea\u5df1\u7684\u8eab\u624b\uff0c\u6bd4\u8d77\u5bf9\u65b9\uff0c\u4f3c\u4e4e\u4ecd\u7a0d\u900a\u4e00\u7b79\u3002 \u3000\u3000\u4ed6\u5378\u4e0b\u4eba\u76ae\u800c\u5177\uff0c\u4ea4\u8fd8\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u73b0\u5728\u8be5\u770b\u4f60\u7684\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8881\u5927\u54e5\u521a\u624d\u548c\u4ed6\u4ea4\u624b\uff0c\u611f\u89c9\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u96be\u9053\u4f60\u6ca1\u770b\u89c1\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5c0f\u5f1f\u4e3a\u9632\u88ab\u4ed6\u53d1\u73b0\uff0c\u53ea\u80fd\u5077\u770b\uff0c\u867d\u6ca1\u770b\u5230\u5168\u90e8\uff0c\u603b\u4e5f\u770b\u4e86\u4e2a\u5927\u6982\u3002\u201d \u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u4eb2\u81ea\u548c\u4ed6\u4ea4\u624b\u7684\uff0c\u5f53\u7136\u66f4\u6e05\u695a\uff0c\u4f9d\u5c0f\u5f1f\u89c2\u5bdf\uff0c\u4ed6\u7684\u529f\u529b\u706b\u5019\uff0c\u4f3c\u4e4e\u6bd4\u8881\u5927\u54e5\u7a0d\u80dc \u4e00\u7b79\uff0c\u4e0d\u8fc7\u4ed6\u4e5f\u5f88\u96be\u4f24\u5230\u4f60\u3002\u201d \u3000\u3000\u201c\u6211\u7684\u611f\u89c9\u548c\u4f60\u5b8c\u5168\u4e00\u6837\uff0c\u4f60\u7684\u6b66\u529f\u6bd4\u6211\u9ad8\u5f97\u591a\uff0c\u73b0\u5728\u8be5\u770b\u4f60\u7684\u4e86\u3002\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u5f53\u7136\u4e0d\u80fd\u8ba9\u4ed6\u8f7b\u8f7b\u677e\u677e\u7684\u56de\u53bb\uff0c\u603b\u8981\u7ed9\u4ed6\u4e00\u70b9\u6559\u8bad\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u6709\u4e00\u4ef6\uff0c\u73b0\u5728\u8fd8\u4e0d\u662f\u6740\u4ed6\u7684\u65f6\u5019\u3002\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u77e5\u9053\uff0c\u6211\u672c\u6765\u5c31\u6ca1\u51c6\u5907\u6740\u4ed6\u3002\u201d \u3000\u3000\u201c\u8fd9\u5c0f\u5b50\u771f\u53ef\u6076\uff0c\u4ed6\u5230\u5bb5\u5173\u6765\uff0c\u4e00\u5b9a\u662f\u548c\u51e4\u5ae3\u7ea2\u5e7d\u4f1a\u3002\u201d \u3000\u3000\u201c\u90a3\u8fd8\u7528\u63d0\u5417\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fd9\u65f6\u65e9\u5df2\u85cf\u597d\u4eba\u76ae\u9762\u5177\uff0c\u518d\u9053\uff1a\u201c\u5c0f\u5f1f\u73b0\u5728\u5c31\u53bb\u8ffd\u4ed6\uff0c\u8881\u5927\u54e5\u8981\u4e0d\u8981\u518d\u53bb\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5f53\u7136\u60f3\u53bb\u770b\u770b\uff0c\u80fd\u770b\u7740\u4f60\u6574\u6cbb\u4ed6\uff0c\u6211\u5fc3\u91cc\u4e5f\u8212\u670d\u4e9b\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u54b1\u4eec\u8fd8\u662f\u8d76\u5728\u4ed6\u524d\u9762\u7684\u597d\uff0c\u4ed6\u4e5f\u7d2f\u5f97\u4e0d\u8f7b\uff0c\u6839\u672c\u8d70\u4e0d\u5feb\u3002\u201d \u3000\u3000\u4e24\u4eba\u53c8\u7531\u53e6\u4e00\u6761\u5c94\u8def\u4e0a\u76f4\u5954\u51fa\u4e09\u56db\u8def\uff0c\u770b\u770b\u518d\u5f80\u524d\u8d70\u5c31\u63a5\u8fd1\u62db\u8d24\u9986\u4e86\uff0c\u624d\u53c8\u6298\u56de\u9a6c\u662d\u96c4 \u5fc5\u7ecf\u7684\u90a3\u6761\u8def\uff0c\u53c8\u627e\u4e86\u4e00\u5904\u571f\u5761\u540e\u85cf\u597d\u3002 \u3000\u3000\u7531\u571f\u5761\u4e0a\u65b9\u63a2\u5934\u5411\u5bb5\u5173\u65b9\u5411\u671b\u53bb\uff0c\u8fd8\u4e0d\u89c1\u9a6c\u662d\u96c4\u7684\u5f71\u5b50\u3002 \u3000\u3000\u9884\u8ba1\u4ed6\u81f3\u5c11\u8fd8\u8981\u76cf\u8336\u5de5\u592b\u624d\u80fd\u5230\u8fbe\u3002 \u3000\u3000\u4e24\u4eba\u53c8\u8c08\u4e86\u534a\u664c\u8bdd\uff0c\u624d\u8fdc\u8fdc\u671b\u89c1\u9a6c\u662d\u96c4\u6b63\u5411\u8fd9\u8fb9\u8d70\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u7b49\u4ed6\u5230\u6765\uff0c\u4fbf\u5df2\u5148\u8dc3\u51fa\u571f\u5761\uff0c\u62e6\u4f4f\u8def\u53e3\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u786e\u5b9e\u7d2f\u4e86\uff0c\u4ed6\u5148\u548c\u51e4\u5ae3\u7ea2\u4e24\u5ea6\u5e8a\u6218\uff0c\u518d\u4e0e\u8881\u5c0f\u9e64\u2014\u756a\u5251\u6597\uff0c\u6b64\u523b\u4e0d\u4f46\u8d70\u5f97\u6781\u6162\uff0c \u8fde\u56db\u5468\u52a8\u9759\uff0c\u4e5f\u61d2\u5f97\u53bb\u6ce8\u610f\u3002 \u3000\u3000\u5f53\u4ed6\u84e6\u4e00\u62ac\u5934\uff0c\u53d1\u73b0\u9762\u524d\u4e0d\u8fdc\u7ad9\u5b9a\u5cb3\u5c0f\u98de\uff0c\u96be\u514d\u5927\u611f\u610f\u5916\u7684\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u4ed6\u6697\u81ea\u5fd6\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u5c0f\u5b50\u53c8\u6765\u4e86\uff1f\u201d \u3000\u3000\u539f\u6765\u4ed6\u628a\u5cb3\u5c0f\u98de\u770b\u6210\u4e86\u65b9\u624d\u90a3\u4eba\u3002 \u3000\u3000\u8fd9\u4e5f\u96be\u602a\uff0c\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u6234\u7684\u662f\u540c\u4e00\u4eba\u76ae\u9762\u5177\uff0c\u504f\u504f\u4eca\u5929\u4ed6\u4eec\u8863\u670d\u7684\u989c\u8272\u4e5f\u5b8c\u5168\u76f8\u540c\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u82e5\u8bba\u8eab\u6750\uff0c\u5cb3\u5c0f\u98de\u8981\u6bd4\u8881\u5c0f\u9e64\u77ee\u4e86\u534a\u4e2a\u5934\uff0c\u4f46\u56e0\u5730\u9762\u4e0d\u5e73\uff0c\u5cb3\u5c0f\u98de\u6240\u7ad9\u7684\u8def\u9762\u53c8 \u8f83\u9ad8\uff0c\u9a6c\u662d\u96c4\u5f88\u96be\u76ee\u6d4b\u51fa\u4ed6\u7684\u9ad8\u77ee\uff0c\u4ed6\u628a\u5cb3\u5c0f\u98de\u8ba4\u6210\u8881\u5c0f\u9e64\uff0c\u5e94\u8be5\u662f\u4ef6\u5f88\u81ea\u7136\u7684\u4e8b\u3002 \u3000\u3000\u4ed6\u968f\u5373\u5728\u5cb3\u5c0f\u98de\u9762\u524d\u4e08\u4f59\u5916\u505c\u4e0b\u811a\u6b65\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u771f\u60f3\u627e\u6b7b\u5417\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u5411\u6700\u64c5\u6a21\u4eff\uff0c\u4ed6\u618b\u7740\u55d3\u5b50\u5b66\u51fa\u8881\u5c0f\u9e64\u7684\u58f0\u97f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u4e0b\u8fd8\u6ca1\u6b7b\u6210\uff0c\u89c9\u5f97 \u5f88\u4e0d\u8212\u670d\uff0c\u6240\u4ee5\u624d\u60f3\u627e\u4f60\u7ee7\u7eed\u8f83\u91cf\u8f83\u91cf\uff0c\u603b\u8981\u5206\u51fa\u4e2a\u9ad8\u4e0b\u624d\u6210\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4f1a\u8dd1\u5230\u524d\u9762\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u4f60\u5728\u8def\u4e0a\u8d70\uff0c\u6211\u5728\u7a7a\u4e2d\u98de\uff0c\u5f53\u7136\u8981\u6bd4\u4f60\u5feb\u5f97\u591a\u3002\u201d \u3000\u3000\u201c\u5c11\u800d\u5634\u76ae\u5b50\uff0c\u9a6c\u67d0\u770b\u4f60\u6700\u597d\u522b\u81ea\u8ba8\u65e0\u8da3\uff0c\u82e5\u975e\u903c\u6211\u52a8\u624b\u4e0d\u53ef\uff0c\u8fd9\u6b21\u53ea\u6015\u4e0d\u4f1a\u50cf\u4e0a\u6b21\u90a3 \u6837\u5bf9\u4f60\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u201c\u539f\u6765\u5c0a\u9a7e\u65b9\u624d\u662f\u5bf9\u6211\u5ba2\u6c14\uff0c\u90a3\u5c31\u591a\u8c22\u4f60\u5566\uff0c\u5e0c\u671b\u4f60\u8fd9\u6b21\u4e0d\u8981\u518d\u5ba2\u6c14\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u771f\u60f3\u627e\u6b7b\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u662f\u89c9\u5f97\u5f7c\u6b64\u6beb\u65e0\u74dc\u845b\uff0c\u4f60\u82e5\u5ba2\u6c14\uff0c\u53cd\u800c\u4f7f\u6211\u6b20\u4f60\u4e00\u4efd\u4eba\u60c5\uff0c\u5728\u4e0b\u662f\u4ece\u6765\u4e0d\u6b20\u522b\u4eba \u60c5\u7684\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u53cc\u7709\u76f4\u7ad6\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u5c31\u51fa\u62db\u5427\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b11\u9053\uff1a\u201c\u6211\u82e5\u5148\u51fa\u62db\uff0c\u5fc5\u5b9a\u5360\u4fbf\u5b9c\uff0c\u5728\u4e0b\u4e5f\u4e0d\u60f3\u5360\u4f60\u7684\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u66b4\u543c\u4e00\u58f0\uff0c\u9ad8\u5927\u7684\u8eab\u8eaf\uff0c\u5e73\u98de\u800c\u8d77\uff0c\u75be\u5982\u95ea\u7535\u822c\uff0c\u5939\u7740\u9510\u5229\u5251\u98ce\uff0c\u76f4\u5411\u5cb3\u5c0f\u98de\u6251 \u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u4e0d\u514d\u6697\u6697\u5403\u60ca\uff0c\u9a6c\u662d\u96c4\u5206\u660e\u5df2\u65bd\u51fa\u4e86\u9a6d\u5251\u672f\u3002 \u3000\u3000\u4ed6\u4e0d\u60f3\u786c\u63a5\uff0c\u52a8\u4f5c\u6bd4\u5bf9\u65b9\u66f4\u5feb\u3002 \u3000\u3000\u5f53\u9a6c\u662d\u96c4\u5c1a\u672a\u6251\u51fb\u5230\u8eab\u524d\uff0c\u5df2\u6765\u4e86\u4e2a\u201c\u4e00\u9e64\u51b2\u5929\u201d\uff0c\u76f4\u4e0a\u76f4\u4e0b\u7684\u76f4\u5347\u8d77\u4e24\u4e08\u591a\u9ad8\uff0c \u3000\u3000\u9a6c\u662d\u96c4\u773c\u89c1\u5c31\u8981\u523a\u4e2d\uff0c\u5374\u4e0d\u60f3\u773c\u524d\u9ed1\u5f71\u4e00\u95ea\uff0c\u5bf9\u65b9\u5df2\u5931\u53bb\u6240\u5728\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u679c\u7136\u8eab\u624b\u5df2\u51fa\u795e\u5165\u5316\uff0c\u5c45\u7136\u80fd\u75be\u51b2\u4e2d\u968f\u5373\u715e\u4f4f\u8eab\u5b50\uff0c\u63a5\u7740\u56de\u5934\u671b\u7a7a\u53cd\u624b\u5288\u51fa\u4e00\u5251\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u5251\u7684\u51fa\u62db\uff0c\u65f6\u95f4\u90e8\u4f4d\u62ff\u634f\u5f97\u6070\u5230\u597d\u5904\uff0c\u4e5f\u6b63\u662f\u5cb3\u5c0f\u98de\u8eab\u5b50\u843d\u4e0b\u51fa\u5251\u5288\u5411\u4ed6\u540e\u80cc\u7684 \u540c\u65f6\u3002 \u3000\u3000\u53ea\u542c\u201c\u545b\u5f53\u201d\u4e00\u58f0\uff0c\u53cc\u5251\u4e00\u63a5\uff0c\u9a6c\u662d\u96c4\u867d\u672a\u88ab\u9707\u9000\uff0c\u4f46\u811a\u4e0b\u5374\u9677\u8fdb\u5730\u9762\u8db3\u6709\u4e24\u5bf8\u3002 \u3000\u3000\u770b\u5cb3\u5c0f\u98de\u65f6\uff0c\u4ed6\u501f\u53cc\u5251\u4ea4\u63a5\u7684\u53cd\u5f39\u4e4b\u529b\uff0c\u4eba\u53c8\u5347\u56de\u534a\u7a7a\u3002 \u3000\u3000\u8fd9\u60c5\u5f62\u53ea\u770b\u5f97\u9a6c\u662d\u96c4\u5927\u751f\u51db\u9a87\uff0c\u4ed6\u4e0b\u610f\u8bc6\u89c9\u51fa\u6b64\u4eba\u7684\u8eab\u624b\u662f\u6bd4\u5148\u524d\u9ad8\u51fa\u4e0d\u5c11\uff0c\u53ef\u89c1\u4ed6\u5148 \u524d\u5fc5\u662f\u6545\u610f\u9690\u7792\u5b9e\u529b\u3002 \u3000\u3000\u679c\u7136\u5982\u6b64\uff0c\u8fd9\u4e00\u6218\u770b\u6765\u5c31\u5927\u5927\u4e0d\u5999\u4e86\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u6b63\u5728\u610f\u5ff5\u5c1a\u672a\u8f6c\u5b8c\uff0c\u7a81\u89c1\u9762\u524d\u9ed1\u5f71\u4e00\u95ea\uff0c\u63a5\u7740\u52b2\u98ce\u6251\u9762\uff0c\u5bf9\u65b9\u7adf\u7136\u5982\u6d77\u71d5\u63a0\u6ce2\u822c \u970e\u65f6\u51b2\u5230\u4ed6\u7684\u5934\u9876\u3002 \u3000\u3000\u4ed6\u60c5\u6025\u4e4b\u4e0b\uff0c\u6765\u4e0d\u53ca\u51fa\u5251\uff0c\u731b\u7136\u4ee5\u5de6\u624b\u63a8\u51fa\u4e00\u638c\u3002 \u3000\u3000\u82e5\u8bba\u4ed6\u7684\u638c\u529b\uff0c\u5df2\u8db3\u53ef\u5f00\u7891\u88c2\u77f3\uff0c\u4e0d\u6101\u4e0d\u628a\u5bf9\u65b9\u9707\u98de\u3002 \u3000\u3000\u5c82\u77e5\u4ed6\u638c\u529b\u521a\u521a\u53d1\u51fa\uff0c\u4e00\u7f15\u5947\u5927\u65e0\u6bd4\u7684\u6697\u52b2\uff0c\u4e0d\u4f46\u7a7f\u7834\u638c\u98ce\uff0c\u800c\u4e14\u76f4\u88ad\u4ed6\u7684\u53f3\u80a9\u3002 \u3000\u3000\u770b\u5bf9\u65b9\u65f6\uff0c\u6b64\u523b\u65e9\u5df2\u7ad9\u5728\u4e24\u4e08\u5f00\u5916\uff0c\u6c14\u4e0d\u51fa\u58f0\uff0c\u9762\u4e0d\u6539\u8272\u3002 \u3000\u3000\u539f\u6765\u5cb3\u5c0f\u98de\u5728\u9a6c\u662d\u96c4\u53d1\u638c\u540c\u65f6\uff0c\u4e5f\u4ee5\u5f39\u6307\u795e\u529f\u88ad\u5411\u5bf9\u65b9\uff0c\u7136\u540e\u518d\u4ef0\u8eab\u8dc3\u9000\u3002 \u3000\u3000\u53ea\u56e0\u4ed6\u8fd9\u6307\u98ce\u7684\u6f5c\u529b\uff0c\u5df2\u88ab\u638c\u98ce\u5378\u53bb\u4e0d\u5c0f\uff0c\u5426\u5219\uff0c\u9a6c\u662d\u96c4\u52bf\u5fc5\u88ab\u70b9\u4e2d\u7a74\u9053\u3002 \u3000\u3000\u867d\u7136\u5982\u6b64\uff0c\u4ed6\u7684\u4e00\u6761\u53f3\u81c2\uff0c\u4e5f\u53d1\u9ebb\u5f97\u51e0\u4e4e\u96be\u4ee5\u52a8\u5f39\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u770b\u51c6\u4e86\u8fd9\u4e00\u70b9\uff0c\u6b64\u523b\u60f3\u6536\u62fe\u9a6c\u662d\u96c4\uff0c\u5df2\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\uff0c\u56e0\u4e3a\u4ed6\u5df2\u51e0\u4e4e\u5728\u77ed\u65f6\u5185\u65e0 \u6cd5\u7528\u5251\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7f13\u7f13\u5411\u524d\u76f4\u8d70\u5230\u8ddd\u79bb\u9a6c\u662d\u96c4\u4e94\u5c3a\u8fdc\uff0c\u624d\u6b62\u4f4f\u811a\u6b65\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u4e5f\u81ea\u77e5\u65e0\u6cd5\u7528\u5251\uff0c\u60c5\u6025\u4e4b\u4e0b\uff0c\u731b\u5730\u53c8\u53d1\u51fa\u5de6\u638c\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6de1\u7136\u4e00\u7b11\uff0c\u8fd9\u6b21\u4ed6\u6839\u672c\u4e0d\u5fc5\u518d\u8eb2\uff0c\u7acb\u5373\u4e5f\u4ee5\u5de6\u638c\u76f8\u8fce\uff0c\u540c\u65f6\u53f3\u624b\u957f\u5251\uff0c\u4e5f\u5411\u5bf9\u65b9 \u80a9\u5934\u538b\u4e0b\u3002 \u3000\u3000\u53cc\u65b9\u638c\u529b\u62b5\u6d88\u4e4b\u540e\uff0c\u9a6c\u662d\u96c4\u4e0d\u80fd\u773c\u770b\u88ab\u5288\u4e2d\u53f3\u80a9\uff0c\u53ea\u597d\u731b\u4e00\u54ac\u7259\uff0c\u62da\u5168\u529b\u8fce\u51fa\u4e00\u5251\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7a81\u7136\u5c06\u529b\u9053\u52a0\u91cd\uff0c\u53ea\u542c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u9a6c\u662d\u96c4\u7684\u957f\u5251\u5df2\u88ab\u51fb\u843d\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u6b63\u60f3\u5411\u540e\u8dc3\u9000\uff0c\u8c01\u60f3\u5bf9\u65b9\u7684\u5251\u5c16\u4e00\u79fb\uff0c\u65e9\u5df2\u62b5\u4e0a\u4e86\u4ed6\u7684\u54bd\u5589\u3002 \u3000\u3000\u987f\u65f6\uff0c\u9a6c\u662d\u96c4\u5df2\u76ee\u77aa\u53e3\u5446\uff0c\u50f5\u5728\u5f53\u573a\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6084\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4f60\u4e0b\u4e00\u6b65\u7684\u547d\u8fd0\u5982\u4f55\uff0c\u4e0d\u59a8\u81ea\u5df1\u731c\u731c\u770b\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u9762\u5982\u571f\u8272\uff0c\u6253\u4e01\u4e2a\u51b7\u98a4\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u600e\u4f1a\u8ba4\u8bc6\u6211\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u7b11\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u79cd\u6b3a\u5e08\u706d\u7956\u7684\u6b66\u6797\u8d25\u7c7b\uff0c\u4eba\u4eba\u5f97\u800c\u8bdb\u4e4b\uff0c\u65e2\u7136\u8981\u6740\u4f60\uff0c\u5f53\u7136\u5fc5 \u5148\u8ba4\u8bc6\u4f60\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u4f3c\u4e4e\u6599\u5230\u6d3b\u547d\u7684\u673a\u4f1a\u4e0d\u5927\uff0c\u7d22\u6027\u95ed\u4e0a\u773c\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u66fe\u7b54\u590d\u6211\u4e0b\u4e00\u6b65\u7684\u547d\u8fd0\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u786c\u54ac\u7740\u7259\uff0c\u5e76\u4e0d\u7b54\u8bdd\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u5c0a\u9a7e\u73b0\u5728\u7684\u4e00\u6761\u72d7\u547d\uff0c\u638c\u63e1\u5728\u5728\u4e0b\u624b\u4e2d\uff0c\u4e0d\u80af\u7b54\u590d\u7b97\u4f60\u806a\u660e\uff0c\u4e0d\u8fc7 \u4f60\u82e5\u80af\u7b54\u5e94\u4e00\u4e2a\u6761\u4ef6\uff0c\u5728\u4e0b\u4e5f\u53ef\u4ee5\u9976\u4f60\u4e0d\u6b7b\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u5f53\u7136\u4e0d\u80af\u6b7b\uff0c\u5c24\u5176\u60f3\u5230\u4e86\u51e4\u5ae3\u7ea2\uff0c\u66f4\u662f\u5fc5\u5f97\u8bbe\u6cd5\u6d3b\u547d\u4e0d\u53ef\u3002 \u3000\u3000\u4ed6\u60c5\u4e0d\u81ea\u7981\u7741\u5f00\u773c\u6765\uff0c\u55eb\u5685\u7740\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\u6761\u4ef6\uff0c\u53ea\u8981\u6211\u505a\u5f97\u5230\u7684\uff0c\u6211\u90fd\u7b54\u5e94\u3002\u201d \u3000\u3000\u201c\u5728\u4e0b\u4ece\u4e0d\u5f3a\u4eba\u6240\u96be\uff0c\u4f60\u5f53\u7136\u505a\u5f97\u5230\uff0c\u5148\u8dea\u5728\u5730\u4e0a\u78d5\u4e09\u4e2a\u54cd\u5934\uff0c\u7136\u540e\u518d\u8001\u8001\u5b9e\u5b9e\u7b54\u590d\u6211 \u51e0\u53e5\u95ee\u8bdd\uff0c\u6761\u4ef6\u5c31\u8fd9\u4e48\u7b80\u5355\u3002\u201d\u3000\u9a6c\u662d\u96c4\u8fd8\u771f\u72b9\u8c6b\u4e86\u3002 \u3000\u3000\u4ed6\u6d3b\u4e86\u8fd9\u4e48\u5927\uff0c\u9664\u4e86\u5f53\u5e74\u66fe\u5411\u5e90\u4e91\u53e9\u8fc7\u5934\uff0c\u540e\u6765\u53c8\u5411\u7687\u5143\u6559\u4e3b\u53e9\u8fc7\u5934\uff0c\u518d\u6ca1\u5411\u7b2c\u4e09\u4eba\u5c48 \u819d\u8fc7\uff0c\u8fd9\u6559\u4ed6\u9762\u5b50\u4e0a\u5982\u4f55\u8fc7\u5f97\u53bb\u3002 \u3000\u3000\u53ea\u542c\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e0d\u5fc5\u4e0d\u597d\u610f\u601d\uff0c\u591c\u8def\u8d70\u591a\u4e86\uff0c\u8fd9\u79cd\u4e8b\u7a00\u677e\u5e73\u5e38\uff0c\u4f55\u51b5\u6b64\u523b\u56db\u4e0b\u65e0\u4eba\uff0c \u9664\u4e86\u6211\u77e5\u9053\uff0c\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u7ec8\u4e8e\u9f13\u8d77\u52c7\u6c14\uff0c\u201c\u5657\u549a\u201d\u4e00\u58f0\uff0c\u8dea\u4e86\u4e0b\u53bb\uff0c\u63a5\u7740\u4e00\u8fde\u78d5\u4e86\u4e09\u4e2a\u5934\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5fcd\u7740\u7b11\u9053\uff1a\u201c\u522b\u5fd9\u7ad9\u8d77\u6765\uff0c\u7b54\u8bdd\u4e5f\u8981\u8dea\u7740\u3002\u201d \u3000\u3000\u201c\u4f60\u5feb\u95ee\uff01\u201d\u73b0\u5728\u662f\u9a6c\u662d\u96c4\u903c\u5cb3\u5c0f\u98de\u65e9\u4e9b\u95ee\u8bdd\u4e86\uff0c\u4ed6\u662f\u5e0c\u671b\u5c11\u8dea\u4e00\u4e9b\u65f6\u95f4\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u8f88\u4e0d\u6162\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u5728\u80b2\u5316\u57ce\uff0c\u4e3a\u4ec0\u4e48\u5230\u5bb5\u5173\u53bb\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u7ea2\u7740\u8138\u4f4e\u4e0b\u5934\u9053\uff1a\u201c\u53bb\u2026\u2026\u53bb\u627e\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u8981\u627e\u7684\u662f\u5426\u51e4\u5ae3\u7ea2\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u70b9\u4e86\u70b9\u5934\uff0c\u4ed6\u56e0\u5bf9\u65b9\u5df2\u77e5\u81ea\u5df1\u7684\u5e95\u7ec6\uff0c\u4e3a\u4e86\u6d3b\u547d\uff0c\u5f53\u7136\u4e0d\u6562\u9690\u7792\u3002 \u3000\u3000\u201c\u627e\u51e4\u5ae3\u7ea2\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5979\u2026\u2026\u5979\u662f\u5728\u4e0b\u7684\u5e08\u5a18\uff0c\u505a\u5f1f\u5b50\u7684\uff0c\u4e0d\u80fd\u4e0d\u627e\u65f6\u95f4\u7ed9\u4ed6\u8001\u4eba\u5bb6\u8bf7\u5b89\u53bb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u4ece\u524d\u662f\u4f60\u5e08\u5a18\uff0c\u53ea\u662f\u73b0\u5728\u4e0d\u662f\u4e86\uff01\u201d \u3000\u3000\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4e00\u65e5\u4e3a\u5e08\uff0c\u7ec8\u8eab\u4e3a\u7236\uff0c\u5e08\u5a18\u5c31\u662f\u5e08\u5a18\uff0c\u600e\u4e48\u8fd8\u4f1a\u6709\u53d8\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6447\u6447\u5934\u9053\uff1a\u201c\u8fd9\u53e5\u8bdd\u51fa\u81ea\u522b\u4eba\u53e3\u4e2d\uff0c\u4e5f\u8bb8\u6211\u4fe1\uff0c\u4f46\u4f60\u662f\u8bf4\u4eba\u8bdd\u4e0d\u529e\u4eba\u4e8b\u7684\uff0c\u6240\u4ee5 \u6211\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u201c\u4f60\u8bf4\u5728\u4e0b\u8ddf\u5979\u662f\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u613f\u63d0\u4ed6\u548c\u51e4\u5ae3\u7ea2\u7684\u80ae\u810f\u4e8b\u60c5\uff0c\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u7b97\u5566\uff0c\u4e0d\u5fc5\u518d\u95ee\uff0c\u4f60\u628a\u90a3\u526f\u8001\u5934 \u4eba\u76ae\u9762\u5177\u7559\u4e0b\uff0c\u5c31\u53ef\u4ee5\u8d70\u4e86\u3002\u201d \u3000\u3000\u8fd9\u6b21\u9a6c\u662d\u96c4\u6beb\u672a\u8fdf\u7591\uff0c\u6025\u6025\u638f\u51fa\u4eba\u76ae\u9762\u5177\uff0c\u53cc\u624b\u9012\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u56e0\u62c5\u4efb\u7075\u5802\u9999\u4e3b\uff0c\u4eba\u76ae\u9762\u5177\u5f04\u4e86\u4e0d\u5c11\uff0c\u5f53\u7136\u5e76\u4e0d\u5728\u4e4e\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u56de\u5230\u80b2\u5316\u57ce\u540e\u3002\u4e0d\u51c6\u518d\u5230\u5bb5\u5173\u6765\uff0c\u5982\u679c\u4e0b\u6b21\u518d\u88ab\u6211\u78b0\u4e0a\uff0c\u4f60\u5c31\u53ea\u6709\u6b7b\u8def\u4e00 \u6761\u4e86\uff0c\u722c\u8d77\u6765\u6eda\u5427\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u722c\u4e86\u8d77\u6765\uff0c\u6361\u8d77\u957f\u5251\uff0c\u6492\u817f\u4fbf\u8d70\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u5f88\u5feb\u4fbf\u8dc3\u8fc7\u571f\u5761\uff0c\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u65b9\u624d\u7684\u4e8b\uff0c\u4e0d\u7528\u8bf4\u4ed6\u5168\u770b\u5230\u542c\u5230\u4e86\u3002 \u3000\u3000\u4ed6\u65e9\u77e5\u5cb3\u5c0f\u98de\u7684\u6b66\u529f\u9ad8\u51fa\u81ea\u5df1\u751a\u591a\uff0c\u56e0\u4e4b\u5bf9\u5cb3\u5c0f\u98de\u5236\u4f4f\u9a6c\u662d\u96c4\uff0c\u5e76\u4e0d\u611f\u5230\u610f\u5916\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0c\u6b63\u597d\u8fd9\u526f\u4eba\u76ae\u9762\u5177\u53ef\u4ee5\u7ed9\u4f60\u4e86\uff0c\u5fc5\u8981\u65f6\u54b1\u4eec\u4e5f\u53ef\u4ea4\u6362\u4f7f\u7528\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u5f53\u7136\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u8fde\u5fd9\u63a5\u8fc7\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4f60\u73b0\u5728\u5c31\u6234\u8d77\u6765\uff0c\u770b\u770b\u662f\u4ec0\u4e48\u6837\u5b50\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4f9d\u8a00\u6234\u4e86\u8d77\u6765\uff0c \u3000\u3000\u5cb3\u5c0f\u98de\u5ffd\u7136\u7075\u673a\u4e00\u52a8\uff0c\u5fd9\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0c\u4f60\u53bb\u505a\u4e00\u4ef6\u4e8b\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6123\u4e86\u4e0b\u9053\uff1a\u201c\u8981\u6211\u53bb\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4f60\u6234\u7740\u8fd9\u526f\u9762\u5177\u53bb\u89c1\u51e4\u5ae3\u7ea2\uff0c\u4e00\u5b9a\u53ef\u4ee5\u4ece\u5979\u5634\u91cc\u63a2\u542c\u51fa\u4e00\u4e9b\u6d88\u606f\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u795e\u8272\u5c34\u5c2c\u7684\u6447\u5934\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u5f00\u73a9\u7b11\u4e48\uff0c\u51e4\u5ae3\u7ea2\u7cbe\u5f97\u5f88\uff0c\u53ea\u6015\u4e09\u8a00\u4e24\u8bed\u4fbf\u4f1a\u88ab\u5979 \u77a7\u51fa\u7834\u7efd\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4f60\u6700\u4f1a\u6a21\u4eff\u522b\u4eba\u8bf4\u8bdd\uff0c\u5b66\u9a6c\u662d\u96c4\u4e00\u5b9a\u4e0d\u6210\u95ee\u9898\uff0c\u800c\u4e14\u4f60\u7684\u8eab\u6750\u548c\u4ed6\u4e5f\u5dee\u4e0d \u591a\uff0c\u6b63\u597d\u8863\u670d\u989c\u8272\u4e5f\u6709\u4e9b\u4e00\u6837\uff0c\u5c24\u5176\u5230\u4e86\u5bb5\u5173\uff0c\u5fc5\u5b9a\u662f\u8d77\u66f4\u65f6\u5206\uff0c\u591c\u665a\u4e4b\u95f4\uff0c\u5979\u5fc5\u5b9a\u66f4\u96be\u8fa9 \u8bc6\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u662f\u731b\u6447\u5934\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u4e00\u5b9a\u7792\u4e0d\u8fc7\u5979\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fd9\u9053\uff1a\u201c\u4f60\u4e0d\u59a8\u5c3d\u91cf\u5c11\u8bf4\u8bdd\uff0c\u53ea\u8981\u542c\u5979\u8bf4\u4ec0\u4e48\u5c31\u591f\u4e86\u3002\u201d \u3000\u3000\u201c\u5979\u80fd\u8bf4\u4e9b\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u60f3\u5979\u4e00\u5b9a\u4f1a\u8bf4\u51fa\u4e00\u4e9b\u673a\u5bc6\u4e8b\u60c5\u6765\uff0c\u800c\u8fd9\u4e9b\u673a\u5bc6\uff0c\u4e5f\u6b63\u662f\u54b1\u4eec\u60f3\u77e5\u9053\u5374\u53c8\u65e0\u6cd5\u77e5\u9053 \u7684\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u7ec8\u4e8e\u88ab\u8bf4\u5f97\u6709\u4e9b\u5fc3\u52a8\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u518d\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0c\u4f60\u628a\u80c6\u5b50\u653e\u5927\u4e00\u70b9\u597d\u4e0d\u597d\uff1f\u5982\u679c\u51e1\u4e8b\u754f\u9996\u754f\u5c3e\uff0c\u4e00\u5b9a\u6210\u4e0d\u4e86\u5927 \u4e8b\uff0c\u4e07\u4e00\u88ab\u5979\u77a7\u51fa\u7834\u7efd\uff0c\u5c3d\u53ef\u8d81\u673a\u6e9c\u6389\uff0c\u5979\u7edd\u5bf9\u4e0d\u53ef\u80fd\u77e5\u9053\u4f60\u662f\u8c01\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u76b1\u7740\u7709\u9053\uff1a\u201c\u8fd9\u6837\u53ef\u4ee5\u5417\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5f53\u7136\u53ef\u4ee5\uff0c\u4f60\u4e00\u76f4\u60f3\u6709\u2014\u526f\u4eba\u76ae\u800c\u5177\uff0c\u73b0\u5728\u6709\u4e86\u5374\u53c8\u4e0d\u6562\u7528\uff0c\u90a3\u5c31\u5e72\u8106\u8fd8 \u7ed9\u5c0f\u5f1f\u597d\u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9f13\u4e86\u9f13\u52c7\u6c14\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u653e\u5927\u80c6\u5192\u4e00\u6b21\u9669\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u90a3\u5c31\u8d76\u5feb\u56de\u5bb5\u5173\u5427\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8131\u4e0b\u9762\u5177\uff0c\u4e24\u4eba\u8f6c\u8eab\u5411\u540e\u8d70\u3002 \u3000\u3000\u5230\u8fbe\u5bb5\u5173\uff0c\u679c\u7136\u5df2\u4ea4\u4e00\u66f4\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8881\u5927\u54e5\u73b0\u5728\u5c31\u53bb\u5427\uff0c\u5c0f\u5f1f\u7b49\u4f60\u7684\u597d\u6d88\u606f\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u201c\u5c0f\u5f1f\u5728\u5927\u95e8\u5916\u8fdc\u8fdc\u7b49\u7740\u4f60\uff0c\u518d\u4e0d\u6211\u5c31\u5148\u56de\u603b\u9547\u5e9c\u53bb\u3002\u201d \u3000\u3000\u201c\u4f60\u8fd8\u662f\u5728\u5927\u95e8\u5916\u8fdc\u8fdc\u7b49\u7740\u6211\u7684\u597d\uff0c\u81f3\u5c11\u4e5f\u7ed9\u6211\u58ee\u58ee\u80c6\u91cf\u3002\u201d \u3000\u3000\u5176\u5b9e\u8881\u5c0f\u9e64\u5e76\u975e\u80c6\u91cf\u4e0d\u591f\uff0c\u800c\u662f\u56e0\u4e3a\u8981\u89c1\u7684\u5bf9\u8c61\u662f\u51e4\u5ae3\u7ea2\uff0c\u4e0d\u7ba1\u5982\u4f55\uff0c\u51e4\u5ae3\u7ea2\u603b\u505a\u8fc7\u4ed6 \u7684\u5e08\u5a18\uff0c\u800c\u4e14\u60f3\u8d77\u4e0a\u6b21\u90a3\u665a\u7684\u4e8b\uff0c\u4ed6\u4e5f\u96be\u514d\u6253\u5fc3\u5e95\u8d77\u7599\u7629\uff0c\u82e5\u5bf9\u8c61\u662f\u522b\u4eba\uff0c\u4ed6\u5c31\u6ca1\u7406\u7531\u80c6\u602f \u4e86\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u4ed6\u91cd\u65b0\u6234\u597d\u4e86\u9762\u5177\uff0c\u786c\u7740\u5934\u76ae\uff0c\u5927\u6447\u5927\u6446\u7684\u76f4\u5411\u5bb5\u5173\u5927\u95e8\u8d70\u53bb\u3002 \u3000\u3000\u5b88\u95e8\u7684\u662f\u4e2a\u9ed1\u7626\u6c49\u5b50\uff0c\u56e0\u4e3a\u5927\u95e8\u53e3\u60ac\u7740\u706f\u7b3c\uff0c\u53cc\u65b9\u90fd\u770b\u5f97\u5f88\u6e05\u695a\u3002 \u3000\u3000\u90a3\u9ed1\u7626\u6c49\u5b50\u4e00\u89c1\u8881\u5c0f\u9e64\uff0c\u8fde\u5fd9\u8d70\u8fd1\u524d\u6765\uff0c\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u8001\u7237\u5b50\u4e0d\u662f\u5df2\u7ecf\u8d70\u4e86\u5417\uff1f\u600e\u4e48 \u53c8\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u56e0\u89c1\u8fd9\u8001\u5934\u513f\u66fe\u88ab\u7edf\u9886\u62db\u5f85\u4e86\u5f88\u4e45\u4e00\u6bb5\u65f6\u95f4\uff0c\u5fc5\u662f\u5f88\u6709\u8eab\u4efd\u5730\u4f4d\uff0c\u6240\u4ee5\u4e0d\u6562\u6020\u6162\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u5e72\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8001\u6c49\u5728\u8def\u4e0a\u53c8\u60f3\u8d77\u4e00\u4ef6\u4e8b\uff0c\u4e0d\u5f97\u4e0d\u518d\u56de\u6765\u544a\u8bc9\u98ce\u7edf\u9886\u3002\u201d \u3000\u3000\u90a3\u9ed1\u7626\u6c49\u5b50\u5fd9\u9053\uff1a\u201c\u8001\u7237\u5b50\u8bf7\u5728\u8fd9\u91cc\u7b49\u4e00\u4e0b\uff0c\u5c0f\u7684\u8fdb\u53bb\u7ed9\u60a8\u901a\u62a5\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e5f\u62f1\u62f1\u624b\u9053\uff1a\u201c\u6709\u52b3\u4e86\uff01\u201d \u3000\u3000\u9ed1\u7626\u6c49\u5b50\u8d70\u540e\uff0c\u7531\u4e8e\u8fd9\u7b2c\u4e00\u5173\u9a97\u5f97\u5f88\u8f7b\u677e\uff0c\u8881\u5c0f\u9e64\u7684\u80c6\u5b50\u4e5f\u4e0d\u514d\u6e10\u6e10\u58ee\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90a3\u9ed1\u7626\u6c49\u5b50\u5f88\u5feb\u4fbf\u5df2\u56de\u6765\uff0c\u8eac\u8eab\u4e00\u793c\u9053\uff1a\u201c\u7edf\u9886\u6709\u8bf7\uff0c\u8001\u7237\u5b50\u8bf7\u81ea\u5df1\u8fdb\u53bb\u597d\u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u7acb\u523b\u53c8\u6709\u4e9b\u80c6\u602f\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u501f\u4ee5\u7f13\u548c\u5185\u5fc3\u7684\u7d27\u5f20\u3002 \u3000\u3000\u597d\u5728\u51e4\u5ae3\u7ea2\u4f4f\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u4ed6\u662f\u77e5\u9053\u7684\u3002 \u3000\u3000\u8fdb\u5165\u91cc\u9762\u9662\u95e8\uff0c\u53ea\u89c1\u5ba2\u5385\u91cc\u706f\u706b\u660e\u4eae\uff0c\u51e4\u5ae3\u7ea2\u6b63\u5750\u5728\u4e00\u5f20\u7ee3\u82b1\u9526\u62ab\u7684\u6a80\u6728\u6905\u4e0a\u3002 \u3000\u3000\u4ed6\u6545\u610f\u4e0d\u5148\u5f00\u53e3\uff0c\u4ee5\u4fbf\u51cf\u5c11\u9732\u51fa\u7834\u7efd\u7684\u673a\u4f1a\u3002 \u3000\u3000\u53ea\u542c\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u6b7b\u9b3c\uff0c\u4f60\u5148\u524d\u4e0d\u662f\u6025\u7740\u56de\u53bb\u5417\uff1f\u600e\u4e48\u53c8\u56de\u6765\u4e86\uff0c\u662f\u4e0d\u662f\u60f3\u8005\u5a18\u60f3\u75af\u4e86\uff0c \u53ef\u89c1\u4f60\u662f\u8272\u80c6\u5305\u5929\uff0c\u8fde\u4e25\u5bd2\u90a3\u8001\u8d3c\u7684\u5904\u5206\u4e5f\u4e0d\u5728\u4e4e\u4e86\uff01\u201d \u3000\u3000\u7531\u8fd9\u51e0\u53e5\u8bdd\uff0c\u8881\u5c0f\u9e64\u4e0d\u96be\u77e5\u9053\u9a6c\u662d\u96c4\u5148\u524d\u548c\u5979\u505a\u4e86\u4e9b\u4ec0\u4e48\u3002\u4ed6\u8fd8\u662f\u4e0d\u5f00\u53e3\uff0c\u5374\u5f2f\u7740\u8170\u53cc \u624b\u7d27\u7d27\u6342\u4f4f\u809a\u5b50\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4f60\u662f\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6545\u610f\u54ac\u7259\u54a7\u5634\u9053\uff1a\u201c\u6211\u8d70\u5728\u8def\u4e0a\uff0c\u5ffd\u7136\u809a\u5b50\u75db\u4e86\u8d77\u6765\uff0c\u53ea\u597d\u518d\u56de\u6765\u4f11\u606f\u4f11\u606f\u3002\u201d \u3000\u3000\u4ed6\u8fb9\u8bf4\u8fb9\u5728\u4e00\u5f20\u6905\u4e0a\u5750\u4e0b\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u6380\u7740\u9f3b\u7fc5\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u4e0d\u4e2d\u7528\u4e86\uff0c\u4f60\u8fd8\u4e0d\u627f\u8ba4\uff0c\u624d\u4e0d\u8fc7\u4e24\u6b21\uff0c\u5c31\u51fa\u4e86\u6bdb \u75c5\uff0c\u8fd9\u8981\u662f\u518d\u591a\u51e0\u6b21\uff0c\u4f60\u4e0d\u5f53\u573a\u6b7b\u5728\u5e8a\u4e0a\u624d\u602a\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u771f\u6ca1\u6599\u5230\u4e00\u89c1\u9762\u5c31\u542c\u4e86\u8fd9\u4e48\u591a\u4e0d\u582a\u5165\u8033\u7684\u8bdd\u3002 \u3000\u3000\u4ed6\u56e0\u88c5\u505a\u809a\u5b50\u75db\uff0c\u773c\u775b\u4e0d\u5fc5\u770b\u51e4\u5ae3\u7ea2\uff0c\u53c8\u6545\u610f\u4f4e\u7740\u5934\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u51e4\u5ae3\u7ea2\u518d\u7cbe\u660e\u4e5f\u4e0d\u6613 \u77a7\u51fa\u7834\u7efd\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u63a5\u7740\u518d\u9053\uff1a\u201c\u8981\u4f11\u606f\u5c31\u8be5\u8fdb\u623f\u4e0a\u5e8a\u4f11\u606f\uff0c\u5982\u679c\u662f\u5750\u7740\u4f11\u606f\uff0c\u8def\u4e0a\u5230\u5904\u53ef\u4ee5\u5750\uff0c\u4f55 \u5fc5\u8001\u8fdc\u8dd1\u56de\u6765\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u547b\u541f\u7740\u9053\uff1a\u201c\u6211\u9700\u8981\u559d\u676f\u8336\uff0c\u7b49\u559d\u4e86\u8336\u518d\u8bf4\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u6c8f\u4e86\u676f\u8336\u7aef\u8fc7\u6765\uff0c\u7136\u540e\u4f38\u624b\u6478\u5411\u8881\u5c0f\u9e64\u5c0f\u8179\u9053\uff1a\u201c\u8ba9\u6211\u6478\u6478\u770b\uff01\u201d \u3000\u3000\u8fd9\u4e00\u6765\u8881\u5c0f\u9e64\u4e0d\u7531\u5927\u4e3a\u7d27\u5f20\uff0c\u4ed6\u4e00\u53ea\u624b\u7d27\u7d27\u62a4\u4f4f\u4e0b\u4f53\uff0c\u4ee5\u514d\u88ab\u51e4\u5ae3\u7ea2\u6478\u4e0a\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u5403\u5403\u7b11\u9053\uff1a\u201c\u4f60\u522b\u6293\u5f97\u90a3\u4e48\u7d27\uff0c\u8bf4\u4e0d\u5b9a\u6bdb\u75c5\u5c31\u51fa\u5728\u90a3\u4e0a\u9762\u3002\u201d \u3000\u3000\u597d\u5728\u5979\u8bf4\u8fc7\u540e\u5c31\u7f29\u56de\u4e86\u624b\uff0c\u53c8\u5750\u56de\u539f\u5904\u9053\uff1a\u201c\u4f60\u4eca\u665a\u5c31\u4e0d\u5fc5\u8d70\u4e86\u5427\uff0c\u4e0d\u7ba1\u4f60\u8fd8\u80fd\u4e0d\u80fd\u529e \u4e8b\uff0c\u7ed9\u6211\u5e8a\u4e0a\u591a\u4e2a\u4eba\u4e5f\u662f\u597d\u7684\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u54ac\u4e86\u54ac\u7259\u9053\uff1a\u201c\u90a3\u8981\u770b\u6211\u80fd\u4e0d\u80fd\u597d\uff0c\u5982\u679c\u9a6c\u4e0a\u597d\u4e86\uff0c\u8fd8\u662f\u8981\u56de\u80b2\u5316\u57ce\u53bb\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u672c\u6765\u6211\u8fd9\u91cc\u6709\u4e39\u836f\uff0c\u670d\u4e0b\u53bb\u5b9a\u53ef\u9a6c\u4e0a\u597d\uff0c\u542c\u4f60\u8fd9\u4e48\u4e00\u8bf4\uff0c\u6211\u5c31\u504f\u504f \u4e0d\u7ed9\u4f60\uff0c\u770b\u4f60\u8fd8\u60f3\u4e0d\u60f3\u56de\u53bb\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5982\u679c\u6709\uff0c\u9a6c\u4e0a\u7ed9\u6211\uff0c\u522b\u8ba9\u6211\u8001\u96be\u53d7\u3002\u201d \u3000\u3000\u4ed6\u5634\u91cc\u8fd9\u6837\u8bf4\uff0c\u5176\u5b9e\u8fd8\u771f\u4e0d\u5e0c\u671b\u51e4\u5ae3\u7ea2\u628a\u836f\u62ff\u6765\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u809a\u5b50\u75db\u662f\u5c0f\u6bdb\u75c5\uff0c\u4e0d\u5403\u836f\u81ea\u5df1\u4e5f\u4f1a\u597d\u7684\uff0c\u6015\u7684\u662f\u4f60\u6ca1\u826f\u5fc3\uff0c\u597d\u4e86\u5c31\u8981\u8d70\uff0c \u4ece\u524d\u662f\u4f60\u6c42\u8001\u5a18\uff0c\u73b0\u5728\u53cd\u800c\u662f\u8001\u5a18\u6c42\u4f60\u4e86\uff01\u201d \u3000\u3000\u53ea\u542c\u8881\u5c0f\u9e64\u54a7\u5634\u53eb\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u5148\u524d\u6211\u90fd\u5bf9\u4f60\u8bb2\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u5148\u662f\u4e00\u6014\uff0c\u63a5\u7740\u5374\u5403\u5403\u7b11\u9053\uff1a\u201c\u6b7b\u4e1c\u897f\uff0c\u4f60\u7684\u786e\u662f\u8d8a\u6765\u8d8a\u4e0d\u4e2d\u7528\uff0c\u8fde\u81ea\u5df1\u8bb2\u8fc7\u7684 \u8bdd\u90fd\u4f1a\u4e0d\u8bb0\u5f97\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u809a\u5b50\u8fd9\u4e00\u75db\uff0c\u628a\u8bb0\u6027\u4e5f\u75db\u56de\u53bb\u4e86\uff0c\u6211\u5f53\u7136\u4e0d\u662f\u5b8c\u5168\u4e0d\u8bb0\u5f97\uff0c\u53ea\u662f\u8981\u63d0\u9192\u4f60 \u4e00\u4e0b\u7f62\u4e86\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u7528\u4e0d\u7740\u63d0\u9192\uff0c\u6211\u660e\u5929\u5c31\u89c1\u6559\u4e3b\u53bb\uff0c\u522b\u770b\u4e25\u5bd2\u90a3\u8001\u8d3c\u4ee5\u6559\u4e3b\u5927\u5e08\u5144\u7684\u8eab\u4efd\u501a \u8001\u5356\u8001\uff0c\u82e5\u628a\u6559\u4e3b\u60f9\u607c\u4e86\uff0c\u4ed6\u7167\u6837\u4f1a\u5012\u5927\u9709\u7684\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u5fc3\u5934\u4e00\u9707\uff0c\u7ec8\u4e8e\u542c\u5230\u4e86\u673a\u5bc6\uff0c\u4e5f\u6697\u6697\u5e86\u5e78\u4e0d\u865a\u6b64\u884c\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5df2\u77e5\u5cb3\u5c0f\u98de\u53d7\u5230\u80b2\u5316\u57ce\u4e3b\u4e25\u5bd2\u7684\u5ba0\u7231\uff0c\u5982\u4eca\u51e4\u5ae3\u7ea2\u8981\u5728\u6559\u4e3b\u9762\u524d\u5bc6\u544a\u80b2\u5316\u57ce\u4e3b\uff0c \u4ed6\u672c\u80fd\u7684\u8054\u60f3\u5230\u8fd9\u4e8b\u5fc5\u4e0e\u5cb3\u5c0f\u98de\u6709\u5173\u3002 \u3000\u3000\u4ed6\u6342\u4f4f\u809a\u5b50\uff0c\u6296\u7740\u55d3\u5b50\u9053\uff1a\u201c\u53ef\u662f\u6211\u62c5\u5fc3\u4f60\u544a\u4e0d\u5012\u4ed6\uff0c\u90a3\u5c31\u53d8\u6210\u6211\u662f\u5012\u5927\u9709\u7684\u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6545\u610f\u6296\u7740\u58f0\u97f3\uff0c\u76ee\u7684\u4e0d\u5916\u662f\u4f7f\u5bf9\u65b9\u542c\u4e0d\u51fa\u6bdb\u75c5\uff0c\u4ed6\u867d\u7136\u5b66\u9a6c\u662d\u96c4\u5b66\u5f97\u5df2\u7ecf\u5f88\u50cf\uff0c \u81ea\u5df1\u5374\u603b\u6709\u4e9b\u5fc3\u865a\uff0c\u56e0\u4e3a\u4e00\u4e2a\u4eba\u5f53\u809a\u5b50\u75db\u5f97\u5f88\u5389\u5bb3\uff0c\u8fde\u58f0\u97f3\u90fd\u53d1\u6296\u65f6\uff0c\u5bf9\u65b9\u5fc5\u5b9a\u4e0d\u6613\u5bdf\u89c9\u6709 \u4ec0\u4e48\u4e0d\u5bf9\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u51b7\u7b11\u9053\uff1a\u201c\u4f55\u6167\u4ed9\u662f\u6559\u4e3b\u4e0b\u4ee4\u6253\u4eba\u7075\u5802\u4e3a\u5974\u7684\u4eba\uff0c\u4ed6\u64c5\u81ea\u653e\u8d70\uff0c\u8fd9\u4e2a\u7f6a\u540d\u8fd8\u8f7b\u5417\uff1f \u5c31\u51ed\u8fd9\u4e00\u70b9\u5c31\u53ef\u4ee5\u53eb\u4ed6\u5403\u4e0d\u4e86\u515c\u7740\u8d70\uff01\u201d \u3000\u3000\u5979\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u60f3\u559d\u8336\uff0c\u4e3a\u4ec0\u4e48\u53c8\u4e0d\u559d\u4e86\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8fde\u5fd9\u6293\u8fc7\u8336\u676f\uff0c\u76b1\u7740\u7709\u5934\u559d\u4e86\u4e24\u53e3\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u7b11\u9053\uff1a\u201c\u4f60\u76b1\u7684\u4ec0\u4e48\u7709\u5934\uff0c\u96be\u9053\u6211\u8fd8\u4f1a\u5728\u8336\u91cc\u4e0b\u6bd2\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u547b\u541f\u7740\u9053\uff1a\u201c\u4f60\u8bf4\u5230\u90a3\u91cc\u53bb\u4e86\uff0c\u6211\u662f\u809a\u5b50\u75db\u5f97\u76b1\u7709\u5934\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u8fd8\u662f\u628a\u4e39\u836f\u7ed9\u4f60\u670d\u4e0b\u5427\uff0c\u53ef\u662f\u4f60\u597d\u4e86\u4ee5\u540e\uff0c\u5fc5\u987b\u7559\u5728\u8fd9\u91cc\uff0c\u8fd9\u4e9b \u5929\u6211\u591c\u665a\u95f2\u7740\u65e0\u804a\uff0c\u7814\u7a76\u51fa\u51e0\u79cd\u65b0\u82b1\u6837\uff0c\u6b63\u597d\u4eca\u5929\u665a\u4e0a\u53ef\u4ee5\u8bd5\u8bd5\u770b\uff0c\u7ba1\u6559\u4f60\u53d8\u6210\u6d3b\u795e\u4ed9\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u5f3a\u5fcd\u7740\u4e00\u53e3\u7a9d\u56ca\u6c14\u9053\uff1a\u201c\u4e39\u836f\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u5f53\u7136\u5728\u6211\u623f\u95f4\u91cc\uff0c\u6765\u5427\uff0c\u8ddf\u6211\u5230\u91cc\u9762\u53bb\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u4f60\u8fdb\u53bb\u62ff\u51fa\u6765\uff0c\u6b63\u597d\u8fd9\u91cc\u6709\u8336\u53ef\u4ee5\u670d\u836f\uff0c\u7b49\u6211\u597d\u4e00\u70b9\u518d\u8fdb\u53bb\u4e0d\u662f\u66f4\u597d\u5417\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u521a\u8981\u8fdb\u53bb\uff0c\u5374\u53c8\u8f6c\u8fc7\u5934\u6765\u9053\uff1a\u201c\u4f60\u8001\u628a\u4e2a\u9762\u5177\u7d27\u7ef7\u7ef7\u7684\u85cf\u5728\u8138\u4e0a\u505a\u4ec0\u4e48\uff1f\u8fd8\u4e0d\u62ff \u4e0b\u6765\uff0c\u6f02\u6f02\u4eae\u4eae\u7684\u4e00\u4e2a\u4eba\u53d8\u6210\u4e2a\u8001\u5934\u5b50\uff0c\u53eb\u4eba\u770b\u7740\u90fd\u4e0d\u8212\u670d\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u7b49\u4f60\u628a\u4e39\u836f\u62ff\u56de\u6765\uff0c\u6211\u5c31\u4e0d\u662f\u8fd9\u6837\u5b50\u4e86\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u8f6c\u8eab\u5f80\u5c4f\u98ce\u540e\u800c\u53bb\u3002 \u3000\u3000\u5728\u8881\u5c0f\u9e64\u6765\u8bf4\uff0c\u6b64\u65f6\u4e0d\u8d70\uff0c\u66f4\u5f85\u4f55\u65f6\uff0c\u4ed6\u6025\u6025\u6e9c\u51fa\u53bb\u4ee5\u540e\uff0c\u4ecd\u5927\u6447\u5927\u6446\u7684\u5c71\u5927\u95e8\u800c\u51fa\u3002 \u3000\u3000\u79bb\u5f00\u5927\u95e8\u767e\u4f59\u6b65\u540e\uff0c\u4e00\u68f5\u5927\u6811\u540e\u8f6c\u51fa\u4e86\u5cb3\u5c0f\u98de\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u8feb\u4e0d\u53ca\u5f85\u7684\u628a\u65b9\u624d\u542c\u5230\u7684\u673a\u5bc6\u544a\u77e5\u4e86\u5cb3\u5c0f\u98de\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u7531\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u4e07\u6ca1\u6599\u5230\u7adf\u4f1a\u53d1\u751f\u8fd9\u6837\u7684\u5927\u4e8b\u3002 \u3000\u3000\u56de\u5230\u603b\u9547\u5e9c\uff0c\u4ed6\u968f\u5373\u628a\u8fd9\u4e8b\u9762\u62a5\u4e86\u7236\u4eb2\u82b1\u7389\u9e9f\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u7167\u6837\u4e5f\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u5728\u7687\u5143\u6559\u4e2d\uff0c\u5c45\u7136\u6709\u4eba\u5bc6\u544a\u80b2\u5316\u57ce\u4e3b\uff0c\u8fd9\u5728\u5929\u8c37\u4e2d\u662f\u4ef6\u7834\u5929\u8352\u7684\u4e8b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8feb\u4e0d\u53ca\u5f85\u7684\u95ee\uff1a\u201c\u7236\u4eb2\u4ee5\u4e3a\u8fd9\u4e8b\u8be5\u600e\u6837\u5904\u7f6e\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u6700\u597d\u7684\u529e\u6cd5\uff0c\u5f53\u7136\u662f\u65a9\u8349\u9664\u6839\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u5b69\u513f\u542c\u4e0d\u61c2\u7236\u4eb2\u8fd9\u8bdd\u7684\u610f\u601d\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u5f88\u7b80\u5355\uff0c\u6740\u4e86\u51e4\u5ae3\u7ea2\uff0c\u5c31\u6ca1\u4eba\u5411\u6559\u4e3b\u5bc6\u544a\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u5934\u4e00\u9707\uff0c\u8c28\u58f0\u9053\uff1a\u201c\u5b69\u513f\u89c9\u5f97\u8fd9\u6837\u4f3c\u4e4e\u4e0d\u59a5\uff1f\u201d \u3000\u3000\u201c\u4f60\u7684\u610f\u601d\u5462\uff1f\u201d \u3000\u3000\u201c\u51e4\u5ae3\u7ea2\u7684\u6b66\u529f\u7167\u6837\u9ad8\u4e0d\u53ef\u6d4b\uff0c\u7236\u4eb2\u867d\u80fd\u6740\u4e86\u5979\uff0c\u4f46\u5374\u4e0d\u662f\u90a3\u4e48\u5bb9\u6613\u7684\uff0c\u4e07\u4e00\u88ab\u5979\u9003\u8131 \u6216\u662f\u88ab\u5916\u4eba\u770b\u5230\uff0c\u7236\u4eb2\u5c82\u4e0d\u4e5f\u906d\u5230\u5929\u7978\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u8e59\u7709\u7f04\u9ed8\u5f88\u4e45\uff0c\u9894\u9996\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u4e5f\u6709\u9053\u7406\uff0c\u4e3a\u7236\u4e0d\u80fd\u4e0d\u9632\u4e07\u4e00\u3002\u201d \u3000\u3000\u7acb\u523b\uff0c\u4ed6\u7684\u53cc\u7709\u4e00\u626c\u9053\uff1a\u201c\u90a3\u5c31\u53e6\u8d70\u7b2c\u4e8c\u6b65\uff0c\u4f60\u660e\u5929\u4e00\u65e9\u5c31\u8d76\u5230\u80b2\u5316\u57ce\uff0c\u628a\u8fd9\u4e8b\u5411\u4e25\u57ce \u4e3b\u7980\u62a5\uff0c\u4e5f\u8bb8\u57ce\u4e3b\u6709\u529e\u6cd5\u5904\u7f6e\uff0c\u5426\u5219\u4ed6\u5fc5\u63aa\u624b\u4e0d\u53ca\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u53ef\u662f\u5b69\u513f\u62c5\u5fc3\u51e4\u5ae3\u7ea2\u4f1a\u8d76\u5728\u524d\u9762\uff0c\u5979\u82e5\u4e5f\u662f\u660e\u5929\u4e00\u65e9\u8d70\uff0c\u603b\u575b\u6bd4\u80b2\u5316\u57ce\u8981 \u8fd1\u5f97\u591a\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u8fd9\u65b9\u9762\u4e0d\u5fc5\u6101\uff0c\u6211\u53ef\u4ee5\u8bbe\u6cd5\u7ba1\u5236\u5979\u7684\u884c\u52a8\u3002\u201d \u3000\u3000\u201c\u7236\u4eb2\u600e\u6837\u7ba1\u5236\u5979\u7684\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u6211\u9a6c\u4e0a\u6d3e\u4eba\u901a\u77e5\u5bb5\u5173\uff0c\u5c31\u8bf4\u660e\u5929\u4e00\u65e9\u8981\u53bb\u89c6\u5bdf\uff0c\u6211\u662f\u5979\u7684\u9876\u5934\u4e0a\u53f8\uff0c\u5979\u5f53\u7136\u5fc5\u987b\u5728\u573a \u5949\u966a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7ec8\u4e8e\u653e\u4e0b\u4e86\u5fc3\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u518d\u9053\uff1a\u201c\u4f60\u9a6c\u4e0a\u56de\u53bb\u4f11\u606f\uff0c\u660e\u5929\u4e00\u65e9\u5c31\u51fa\u53d1\uff0c\u4e0d\u5fc5\u518d\u6765\u89c1\u6211\u3002\u201d \u3000\u3000\u6b21\u65e5\u5929\u8fd8\u4e0d\u4eae\uff0c\u5cb3\u5c0f\u98de\u5c31\u52a8\u8eab\u5f80\u80b2\u5316\u57ce\u51fa\u53d1\u3002 \u3000\u3000\u518d\u8fc7\u4e0d\u4e45\uff0c\u82b1\u7389\u9e9f\u4e5f\u5e26\u7740\u8881\u5c0f\u9e64\u3001\u4e01\u6d9b\u3001\u7518\u9716\u7b49\u4eba\u53bb\u4e86\u5bb5\u5173\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u8def\u6025\u6025\u5954\u884c\uff0c\u5230\u8fbe\u80b2\u5316\u57ce\u540e\uff0c\u624d\u662f\u65e9\u9910\u521a\u8fc7\u7684\u65f6\u95f4\u3002 \u3000\u3000\u4e0d\u96be\u60f3\u89c1\uff0c\u4ed6\u6b64\u523b\u7684\u5fc3\u60c5\u5341\u5206\u6c89\u91cd\uff0c\u60f3\u4e0d\u5230\u6551\u51fa\u6bcd\u4eb2\u4e4b\u540e\uff0c\u5374\u95ef\u4e0b\u4e86\u5927\u7978\u3002 \u3000\u3000\u5c24\u5176\u4ed6\u611f\u5230\u6127\u5bf9\u80b2\u5316\u57ce\u4e3b\u4e25\u5bd2\uff0c\u5982\u679c\u80b2\u5316\u57ce\u4e3b\u4e0d\u80fd\u514d\u53bb\u8fd9\u573a\u7978\u4e8b\uff0c\u53ef\u8bf4\u4e8b\u60c5\u5b8c\u5168\u56e0\u81ea\u5df1 \u800c\u8d77\u3002 \u3000\u3000\u4ed6\u4e0d\u4fbf\u76f4\u63a5\u95ef\u8fdb\u5185\u5e9c\uff0c\u7ecf\u8fc7\u603b\u7ba1\u5468\u6d77\u5c71\u901a\u62a5\u540e\uff0c\u6700\u540e\u8fd8\u662f\u88ab\u5f15\u5165\u5185\u5e9c\u5ba2\u5385\u3002 \u3000\u3000\u4e25\u5bd2\u53ef\u8c13\u8003\u8651\u5468\u5230\uff0c\u4e3a\u4e86\u8ba9\u5cb3\u5c0f\u98de\u80fd\u53ca\u65e9\u89c1\u5230\u6bcd\u4eb2\uff0c\u7279\u5730\u901a\u77e5\u4f55\u6167\u4ed9\u5728\u5185\u5e9c\u5ba2\u5385\u4e00\u8d77\u63a5 \u89c1\u5cb3\u5c0f\u98de\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5148\u5411\u4e25\u5bd2\u89c1\u793c\uff0c\u518d\u62dc\u89c1\u6bcd\u4eb2\u3002 \u3000\u3000\u51e0\u65e5\u4e0d\u89c1\uff0c\u4f55\u6167\u4ed9\u5df2\u662f\u5bb9\u5149\u7115\u53d1\uff0c\u518d\u52a0\u4e0a\u8863\u9970\u5149\u9c9c\uff0c\u66f4\u663e\u5f97\u98ce\u534e\u7edd\u4ee3\u3002 \u3000\u3000\u770b\u4e86\u6bcd\u4eb2\u4e00\u4e0b\u5b50\u7531\u5730\u72f1\u5347\u5230\u5929\u5802\u7684\u6a21\u6837\uff0c\u5cb3\u5c0f\u98de\u672c\u8be5\u9ad8\u5174\uff0c\u4f46\u4ed6\u5374\u518d\u4e5f\u9ad8\u5174\u4e0d\u8d77\u6765\uff0c\u56e0 \u4e3a\u66f4\u5927\u7684\u7978\u4e8b\u9a6c\u4e0a\u5c31\u8981\u6765\u4e34\u3002 \u3000\u3000\u4ed6\u5f88\u5feb\u628a\u4e8b\u60c5\u5411\u4e25\u5bd2\u7980\u62a5\u4e86\u4e00\u904d\u3002 \u3000\u3000\u5374\u89c1\u4e25\u5bd2\u9762\u8272\u94c1\u9752\uff0c\u731b\u5730\u62cd\u4e86\u684c\u5b50\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u9a6c\u662d\u96c4\uff0c\u8fd9\u738b\u516b\u7f94\u5b50\u592a\u53ef\u6076\u4e86\uff0c\u7adf\u6562\u52a8 \u5230\u8001\u592b\u5934\u4e0a\uff0c\u7b80\u76f4\u662f\u8981\u9020\u53cd\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u57ce\u4e3b\u5fc5\u987b\u5c3d\u65e9\u505a\u4e00\u5904\u7f6e\uff01\u201d \u3000\u3000\u4e25\u5bd2\u543c\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u597d\u5904\u7f6e\u7684\uff0c\u53eb\u5bb5\u5173\u51e4\u5ae3\u7ea2\u90a3\u8d31\u5973\u4eba\u544a\u53bb\uff0c\u4e0d\u4fe1\u5979\u80fd\u52a8\u5f97\u4e86\u8001\u592b\u4e00\u6839 \u6c57\u6bdb\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6ca1\u6599\u5230\u4e25\u5bd2\u7adf\u7136\u5bf9\u8fd9\u4e8b\u6beb\u4e0d\u5728\u610f\uff0c\u4e0d\u5f97\u4e0d\u518d\u6b63\u8272\u9053\uff1a\u201c\u7980\u57ce\u4e3b\uff0c\u665a\u8f88\u8ba4\u4e3a\u4f60\u8001\u4eba \u5bb6\u8fd8\u662f\u4e0d\u53ef\u5927\u610f\uff0c\u51e4\u5ae3\u7ea2\u5fc3\u5982\u86c7\u874e\uff0c\u800c\u4e14\u5979\u662f\u6559\u4e3b\u7684\u5fc3\u8179\uff0c\u5bf9\u4e8e\u8fd9\u79cd\u5c0f\u4eba\u4e0d\u5f97\u4e0d\u9632\u3002\u201d \u3000\u3000\u4e25\u5bd2\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5979\u662f\u6559\u4e3b\u7684\u5fc3\u8179\uff0c\u8001\u592b\u5462\uff0c\u8001\u592b\u662f\u6559\u4e3b\u7684\u5927\u5e08\u5144\uff0c\u6559\u4e3b\u53c8\u6562\u628a\u8001\u592b \u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u4f55\u6167\u4ed9\u4e5f\u89c9\u51fa\u4e8b\u6001\u4e25\u91cd\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u8bed\u6c14\u6fc0\u52a8\u7684\u9053\uff1a\u201c\u8fd9\u573a\u7978\u4e8b\uff0c\u53ef\u8bf4\u5b8c\u5168\u56e0\u5c0f\u59b9\u800c\u8d77\uff0c \u5c0f\u59b9\u5bf9\u57ce\u4e3b\u7684\u4e00\u756a\u539a\u6069\uff0c\u4eca\u751f\u62a5\u7b54\u4e0d\u5c3d\uff0c\u4e3a\u4e86\u4e0d\u4f7f\u4e8b\u60c5\u95f9\u5927\uff0c\u5c0f\u59b9\u60c5\u613f\u518d\u56de\u7075\u5802\u4e3a\u5974\uff0c\u4e5f\u8bb8 \u4e8b\u60c5\u5c31\u4f1a\u5e73\u606f\u4e86\uff01\u201d \u3000\u3000\u4e25\u5bd2\u6447\u6447\u624b\u9053\uff1a\u201c\u5927\u59b9\u5b50\u600e\u53ef\u8bf4\u51fa\u8fd9\u79cd\u8bdd\uff0c\u8001\u592b\u65e2\u7136\u628a\u4f60\u4ece\u7075\u5802\u8c03\u51fa\u6765\uff0c\u5c31\u4e0d\u80fd\u8ba9\u4f60\u518d \u56de\u53bb\uff0c\u90a3\u6837\u505a\u6211\u4e25\u5bd2\u7b97\u4e2a\u4ec0\u4e48\u4eba\uff1f\u8981\u77e5\u9053\u54b1\u4eec\u662f\u513f\u5973\u4eb2\u5bb6\uff0c\u6211\u82e5\u8fde\u513f\u5973\u4eb2\u5bb6\u90fd\u4fdd\u4e0d\u4f4f\uff0c\u90a3\u5c31 \u5e72\u8106\u4e0d\u5982\u78b0\u6b7b\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u5934\u5927\u6025\u9053\uff1a\u201c\u53ef\u662f\u4f60\u8001\u4eba\u5bb6\u603b\u8be5\u5148\u505a\u5904\u7f6e\uff01\u201d \u3000\u3000\u201c\u4f60\u7684\u610f\u601d\u8ba9\u8001\u592b\u600e\u4e48\u5904\u7f6e\uff1f\u201d \u3000\u3000\u201c\u6700\u597d\u4f60\u8001\u4eba\u5bb6\u5148\u53bb\u89c1\u6559\u4e3b\uff0c\u8fd9\u6837\u51e4\u5ae3\u7ea2\u518d\u53bb\u5bc6\u544a\uff0c\u4e5f\u5fc5\u5b9a\u5931\u6548\u4e86\u3002\u201d \u3000\u3000\u4f55\u6167\u4ed9\u63a5\u53e3\u9053\uff1a\u201c\u98de\u513f\u8fd9\u8bdd\u4e5f\u6709\u70b9\u9053\u7406\uff0c\u57ce\u4e3b\u5148\u89c1\u6559\u4e3b\u603b\u662f\u597d\u4e9b\u3002\u201d \u3000\u3000\u4e25\u5bd2\u4e24\u592a\u9633\u7a74\u62bd\u6410\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u597d\u5427\uff0c\u8001\u592b\u8fd9\u5c31\u53bb\u89c1\u6559\u4e3b\uff0c\u4f60\u4eec\u6bcd\u5b50\u5c31\u5728\u8fd9\u91cc\u805a\u805a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8eac\u8eab\u9053\uff1a\u201c\u665a\u8f88\u662f\u5426\u7b49\u4f60\u8001\u4eba\u5bb6\u56de\u6765\u518d\u8d70\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u8d77\u8eab\u9053\uff1a\u201c\u4f60\u8d70\u4f60\u7684\uff0c\u7528\u4e0d\u7740\u518d\u7b49\u6211\u7684\u6d88\u606f\uff0c\u5c0f\u4e8b\u4e00\u4ef6\uff0c\u7d27\u5f20\u4ec0\u4e48\u3002\u201d \u3000\u3000\u5c82\u77e5\u4e25\u5bd2\u6839\u672c\u4e0d\u53bb\u89c1\u6559\u4e3b\u3002 \u3000\u3000\u4ed6\u9a97\u4f55\u6167\u4ed9\u548c\u5cb3\u5c0f\u98de\u8bf4\u53bb\u89c1\u6559\u4e3b\uff0c\u4e0d\u8fc7\u662f\u5b89\u6170\u4ed6\u4eec\u6bcd\u5b50\u7f62\u4e86\u3002 \u3000\u3000\u4ed6\u6765\u5230\u524d\u9662\u5927\u5385\uff0c\u53ec\u6765\u603b\u7ba1\u5468\u6d77\u5c71\u5429\u5490\u9053\uff1a\u201c\u9a6c\u4e0a\u5230\u7075\u5802\u628a\u9a6c\u662d\u96c4\u5e26\u5230\u8fd9\u91cc\u6765\uff01\u201d \u3000\u3000\u5468\u6d77\u5c71\u770b\u51fa\u57ce\u4e3b\u8138\u8272\u4e0d\u5bf9\uff0c\u6839\u672c\u4e0d\u6562\u591a\u95ee\uff0c\u5e94\u58f0\u800c\u53bb\u3002 \u3000\u3000\u5927\u5385\u91cc\u53ea\u5269\u4e0b\u4e25\u5bd2\u4e00\u4eba\uff0c\u4ed6\u8dfa\u7740\u811a\u81ea\u8a00\u81ea\u8bed\u543c\u9053\uff1a\u201c\u53ef\u6076\u7684\u6df7\u5e10\u4e1c\u897f\uff0c\u7b80\u76f4\u8981\u9020\u53cd\u4e86\u3002 \u7687\u5143\u6559\u91cc\u5c45\u7136\u6709\u4eba\u6562\u52a8\u8001\u592b\uff0c\u83ab\u975e\u592a\u9633\u6253\u897f\u8fb9\u51fa\u6765\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8d8a\u60f3\u8d8a\u6c14\uff0c\u5e72\u8106\u547d\u4eba\u642c\u8fc7\u4e00\u7f50\u9152\u6765\uff0c\u6765\u4e2a\u65e0\u83dc\u5e72\u559d\u3002\u5468\u6d77\u5c71\u90a3\u6562\u6020\u6162\uff0c\u987f\u996d\u5de5\u592b\u4fbf\u628a \u9a6c\u662d\u96c4\u5e26\u4e86\u6765\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u56e0\u4e3a\u5df2\u548c\u51e4\u5ae3\u7ea2\u7ea6\u597d\u5bc6\u544a\u4e25\u5bd2\uff0c\u9884\u6599\u4e25\u5bd2\u7684\u57ce\u4e3b\u5b9d\u5ea7\u5df2\u7ecf\u4e0d\u7a33\uff0c\u8fd9\u65f6\u53cd\u800c\u6ca1\u628a\u4e25 \u5bd2\u653e\u5728\u773c\u91cc\u3002 \u3000\u3000\u4ed6\u505a\u68a6\u4e5f\u6599\u60f3\u4e0d\u5230\u673a\u5bc6\u5df2\u7ecf\u5916\u6cc4\u3002 \u3000\u3000\u4ed6\u8fdb\u5165\u5927\u5385\uff0c\u5411\u4e25\u5bd2\u65bd\u4e86\u4e00\u793c\uff0c\u7136\u540e\u5927\u6a21\u5927\u6837\u7684\u7ad9\u5728\u4e00\u65c1\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e25\u5bd2\u84e6\u5730\u54cd\u8d77\u4e00\u58f0\u96f7\u9706\u5927\u559d\u9053\uff1a\u201c\u8dea\u4e0b\uff01\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u5927\u559d\uff0c\u7b80\u76f4\u548c\u60f3\u8c61\u4e2d\u5f53\u5e74\u5f20\u98de\u5728\u957f\u5742\u5761\u559d\u65ad\u6865\u6881\u7684\u58f0\u97f3\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u4e0d\u4f46\u9a6c\u662d\u96c4\u5413\u5f97\u8138\u4e0a\u843d\u4e86\u989c\u8272\uff0c\u8fde\u5468\u6d77\u5c71\u4e5f\u8ddf\u7740\u5934\u76ae\u53d1\u9ebb\u3002 \u3000\u3000\u5468\u6d77\u5c71\u8ddf\u968f\u4e25\u5bd2\u8fd9\u4e48\u4e45\uff0c\u867d\u7136\u77e5\u9053\u4ed6\u6027\u5b50\u521a\u70c8\uff0c\u4f46\u53d1\u813e\u6c14\u5230\u8fd9\u79cd\u7a0b\u5ea6\uff0c\u8fd8\u662f\u7b2c\u4e00\u6b21\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u867d\u7136\u5413\u5f97\u53d1\u660f\uff0c\u5374\u5e76\u6ca1\u8dea\u4e0b\uff0c\u4ed6\u8fd8\u4ee5\u4e3a\u53ef\u80fd\u662f\u8981\u5468\u6d77\u5c71\u8dea\u4e0b\u5462\u3002 \u3000\u3000\u4e25\u5bd2\u8fd9\u6b21\u58f0\u97f3\u5c0f\u4e86\uff0c\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u738b\u516b\u7f94\u5b50\uff0c\u53eb\u4f60\u8dea\u4e0b\u542c\u5230\u6ca1\u6709\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u671b\u4e86\u5468\u6d77\u5c71\u4e00\u773c\uff0c\u56de\u5934\u9053\uff1a\u201c\u57ce\u4e3b\u53eb\u8c01\u8dea\u4e0b\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u4e00\u62cd\u684c\u5b50\uff0c\u53c8\u662f\u4e00\u58f0\u5982\u96f7\u5927\u559d\u9053\uff1a\u201c\u5c31\u662f\u4f60\u8fd9\u6df7\u86cb\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u53ea\u597d\u6123\u6123\u7684\u8dea\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u5206\u8fa9\u9053\uff1a\u201c\u7980\u57ce\u4e3b\uff0c\u5351\u804c\u4e0d\u77e5\u72af\u4e86\u90a3\u4e00\u6761\u8beb\u5f8b\uff0c\u53d7\u7f5a\u65e0\u6240\u8c13\uff0c\u603b\u4e0d\u80fd\u88ab\u7f5a\u5f97\u4e0d\u660e \u4e0d\u767d\uff01\u201d \u3000\u3000\u4e25\u5bd2\u4e00\u9635\u51b7\u7b11\u9053\uff1a\u201c\u8001\u592b\u5f53\u7136\u8981\u7ed9\u4f60\u4e00\u660e\u767d\uff0c\u4f60\u6628\u5929\u4e0b\u5348\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u4e86\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u5fc3\u5934\u4e00\u9707\uff0c\u55eb\u5685\u7740\u9053\uff1a\u201c\u5c5e\u4e0b\u5e76\u6ca1\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u3002\u201d \u3000\u3000\u4e25\u5bd2\u559d\u9053\uff1a\u201c\u6df7\u86cb\uff0c\u4f60\u660e\u660e\u5077\u5077\u53bb\u4e86\u5bb5\u5173\uff0c\u5230\u4e8c\u66f4\u5c06\u5c3d\u624d\u56de\u6765\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u5982\u95fb\u6674\u5929\u9739\u96f3\uff0c\u58ee\u7740\u80c6\u5b50\u9053\uff1a\u201c\u8fd9\u4e8b\u57ce\u4e3b\u542c\u8c01\u8bf4\u7684\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u5f53\u7136\u6709\u8bc1\u4eba\uff0c\u8001\u592b\u51b3\u4e0d\u60f3\u8bec\u8d56\u90a3\u4e00\u4e2a\u3002\u53e6\u5916\uff0c\u5bb5\u5173\u7edf\u9886\u51e4\u5ae3\u7ea2\uff0c\u4e5f\u88ab\u8001\u592b\u8fde \u591c\u6d3e\u4eba\u62bc\u4e86\u6765\uff0c\u4f60\u6562\u4e0d\u6562\u548c\u5979\u5bf9\u8d28\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u4e00\u542c\u8fde\u51e4\u5ae3\u7ea2\u4e5f\u88ab\u6349\u4e86\u6765\uff0c\u66f4\u662f\u5413\u5f97\u9b42\u98de\u80c6\u88c2\uff0c\u4e24\u773c\u4e00\u9ed1\uff0c\u8fde\u8111\u888b\u4e5f\u642d\u62c9\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u4e25\u5bd2\u89c1\u9a6c\u662d\u96c4\u5df2\u88ab\u5413\u5f97\u5dee\u4e0d\u591a\u4e86\uff0c\u8bed\u6c14\u53cd\u800c\u7f13\u548c\u4e86\u4e0b\u6765\uff1a\u201c\u9a6c\u9999\u4e3b\uff0c\u8001\u592b\u7a76\u7adf\u90a3\u4e00\u70b9\u5bf9 \u4e0d\u8d77\u4f60\uff0c\u4f60\u7adf\u52a8\u8111\u7b4b\u52a8\u5230\u8001\u592b\u5934\u4e0a\uff0c\u5c45\u7136\u8054\u7edc\u51e4\u5ae3\u7ea2\uff0c\u60f3\u5728\u6559\u4e3b\u9762\u524d\u544a\u6211\u4e00\u72b6\uff0c\u4e5f\u6ca1\u60f3\u60f3\u8001 \u592b\u662f\u6559\u4e3b\u7684\u4ec0\u4e48\u4eba\uff0c\u53ef\u89c1\u4f60\u8fd9\u6df7\u86cb\u8fd8\u662f\u6ca1\u6709\u5934\u8111\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u53e9\u5934\u5982\u6363\u849c\u9053\uff1a\u201c\u5351\u804c\u4e0d\u6562\uff01\u5351\u804c\u4e0d\u6562\uff01\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u4e8b\u60c5\u5df2\u7ecf\u505a\u51fa\u6765\u4e86\uff0c\u8fd8\u4e0d\u627f\u8ba4\uff0c\u5b9e\u5bf9\u4f60\u8bf4\uff0c\u521a\u624d\u51e4\u5ae3\u7ea2\u5728\u8001\u592b\u62f7\u95ee\u4e4b\u4e0b\uff0c\u5df2 \u7ecf\u5b8c\u5168\u62db\u4f9b\uff0c\u4f60\u8fd8\u6562\u62b5\u8d56\u5417\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u60c5\u6025\u95f4\u53eb\u9053\uff1a\u201c\u90a3\u662f\u98ce\u7edf\u9886\u5411\u5351\u804c\u683d\u810f\uff0c\u57ce\u4e3b\u5343\u4e07\u4e0d\u80fd\u8f7b\u4fe1\uff01\u201d \u3000\u3000\u4e25\u5bd2\u563f\u563f\u7b11\u9053\uff1a\u201c\u98ce\u7edf\u9886\u8ddf\u4f60\u597d\u5f97\u53ef\u4ee5\u7a7f\u4e00\u6761\u88e4\u5b50\uff0c\u5979\u4e3a\u4f60\u8131\u7f6a\u8fd8\u6765\u4e0d\u53ca\uff0c\u90a3\u91cc\u6765\u7684\u683d \u810f\u4e00\u8bf4\uff0c\u7531\u6b64\u53ef\u89c1\u4f60\u8fd9\u6df7\u86cb\u5b9e\u5728\u65e2\u5351\u9119\u53c8\u53ef\u6076\uff0c\u4e00\u65e6\u5927\u7978\u4e34\u5934\uff0c\u5c45\u7136\u8fde\u5c71\u76df\u6d77\u8a93\u7684\u5fc3\u4e0a\u4eba\u4e5f \u4e0d\u987e\u4e86\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u4e24\u773c\u53d1\u76f4\u7684\u53eb\u9053\uff1a\u201c\u5351\u804c\u548c\u98ce\u7edf\u9886\u53ea\u662f\u5728\u672a\u5165\u5929\u8c37\u524d\u5c31\u8ba4\u8bc6\uff0c\u6839\u672c\u6ca1\u6709\u534a\u70b9\u4eb2\u5bc6 \u5173\u7cfb\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u6ca1\u6709\u4eb2\u5bc6\u5173\u7cfb\u4e3a\u4f55\u8981\u8054\u5408\u5979\u6765\u544a\u8bc9\u6211\uff1f\u4f60\u4ee5\u4e3a\u8001\u592b\u4e0d\u77e5\u9053\uff0c\u51e4\u5ae3\u7ea2\u672c\u6765\u662f\u5723 \u624b\u4e66\u751f\u5e90\u4e91\u7684\u59bb\u5b50\uff0c\u4f60\u662f\u5e90\u4e91\u7684\u5f1f\u5b50\uff0c\u5e90\u4e91\u7b97\u662f\u778e\u4e86\u773c\uff0c\u6559\u51fa\u4e00\u4e2a\u628a\u81ea\u5df1\u8001\u5a46\u5e26\u8d70\u7684\u79bd\u517d\u4e0d \u5982\u7684\u8d25\u7c7b\uff0c\u9a6c\u662d\u96c4\uff0c\u8001\u592b\u8bf4\u7684\u53ef\u6709\u534a\u70b9\u5dee\u9519\u6ca1\u6709\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u65e9\u5df2\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u4ed6\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\u4e25\u5bd2\u7adf\u80fd\u628a\u4ed6\u7684\u5e95\u7ec6\u8c03\u67e5\u5f97\u5982\u6b64\u6e05\u695a\uff0c\u4f7f\u5f97 \u4ed6\u6839\u672c\u65e0\u4ece\u62b5\u8d56\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4ed6\u4e5f\u65e0\u6cd5\u7b54\u5f97\u4e0a\u8bdd\u3002 \u3000\u3000\u53ea\u542c\u4e25\u5bd2\u559d\u9053\uff1a\u201c\u5468\u603b\u7ba1\uff0c\u7ed9\u8001\u592b\u628a\u4ed6\u8131\u5149\u8863\u670d\u540a\u5230\u6881\u4e0a\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u614c\u4e0d\u8fed\u7684\u53e9\u5934\u9053\u558a\u9053\uff1a\u201c\u57ce\u4e3b\u9976\u547d\uff01\u5351\u804c\u77e5\u7f6a\u4e86\uff01\u201d \u3000\u3000\u4e25\u5bd2\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u77e5\u7f6a\uff0c\u90a3\u5c31\u8868\u793a\u6709\u7f6a\uff0c\u8001\u592b\u5982\u4f55\u9976\u5f97\u4e86\u4f60\uff01\u201d \u3000\u3000\u5468\u6d77\u5c71\u8eac\u8eab\u9053\uff1a\u201c\u7980\u57ce\u4e3b\uff0c\u540a\u5728\u5927\u5385\u91cc\u6050\u6015\u4e0d\u592a\u597d\u770b\u5427\uff01\u201d \u3000\u3000\u4e25\u5bd2\u53f1\u9053\uff1a\u201c\u7b80\u76f4\u662f\u732a\u8111\u5b50\uff0c\u5927\u5385\u540e\u9762\u6709\u5f88\u591a\u7a7a\u5c4b\uff0c\u96be\u9053\u8fd8\u540a\u4e0d\u4e0b\u4ed6\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u62c5\u5fc3\u9a6c\u662d\u96c4\u53cd\u6297\uff0c\u4e00\u6307\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u9a6c\u9999\u4e3b\uff0c\u8bf7\u6055\u5468\u67d0\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u63a2\u624b\u628a\u9a6c\u662d\u96c4\u7531\u5730\u4e0a\u6293\u8d77\uff0c\u4e0d\u7ecf\u524d\u95e8\uff0c\u7531\u5927\u5385\u540e\u65b9\u76f4\u63a5\u628a\u4eba\u67b6\u5230\u4e00\u95f4\u7a7a\u5c4b\uff0c\u7136 \u540e\u518d\u627e\u6765\u4e00\u6839\u7ef3\u5b50\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u867d\u7136\u7a74\u9053\u88ab\u5236\uff0c\u5374\u4ecd\u80fd\u8bf4\u8bdd\uff0c\u4ed6\u54c0\u6c42\u7740\u9053\uff1a\u201c\u603b\u7ba1\u5927\u4eba\uff0c\u60a8\u8fd9\u662f\u4f55\u82e6\uff01\u201d \u3000\u3000\u5468\u6d77\u5c71\u51b7\u7b11\u9053\uff1a\u201c\u5bf9\u4e0d\u8d77\uff0c\u8fd9\u662f\u57ce\u4e3b\u7684\u547d\u4ee4\uff0c\u5468\u67d0\u4e0d\u6562\u4e0d\u9075\u3002\u201d \u3000\u3000\u201c\u5351\u804c\u6c42\u60a8\u5e2e\u5e2e\u5fd9\uff0c\u5c31\u662f\u8981\u540a\uff0c\u4e5f\u522b\u8131\u5149\u8863\u670d\uff0c\u90a3\u6837\u591a\u4e0d\u597d\u770b\u3002\u201d \u3000\u3000\u201c\u90a3\u91cc\u8bdd\uff0c\u8131\u5149\u4e86\u624d\u597d\u770b\uff0c\u4f60\u4e0d\u662f\u4e13\u7231\u7ed9\u5973\u4eba\u8131\u5149\u8863\u670d\u5417\uff0c\u8fde\u5e08\u5a18\u7684\u88e4\u5b50\u90fd\u8131\uff0c\u600e\u4e48\u73b0 \u5728\u8fde\u81ea\u5df1\u90fd\u4e0d\u60f3\u8131\u4e86\uff1f\u201d \u3000\u3000\u201c\u4f60\u522b\u542c\u57ce\u4e3b\u8bf4\uff0c\u90a3\u662f\u51a4\u6789\u6211\u7684\u3002\u201d \u3000\u3000\u201c\u5468\u67d0\u4e0d\u542c\u57ce\u4e3b\u8bf4\u96be\u9053\u8fd8\u542c\u4f60\u8bf4\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u628a\u7ef3\u5b50\u7684\u4e00\u7aef\u642d\u5230\u6881\u4e0a\u540e\uff0c\u4e09\u4e0b\u4e94\u9664\u4e8c\u628a\u9a6c\u662d\u96c4\u8131\u4e86\u4e2a\u7cbe\u5149\u6e9c\u6e9c\u3002 \u3000\u3000\u7136\u540e\u518d\u6765\u4e2a\u56db\u4e2a\u9a6c\u6512\u8e44\u5f0f\u7684\u6346\u7ed1\uff0c\u63a5\u7740\u540a\u4e0a\u4e86\u6881\u3002 \u3000\u3000\u5468\u6d77\u5c71\u65e9\u5c31\u5bf9\u9a6c\u662d\u96c4\u4e0d\u6000\u597d\u611f\uff0c\u56e0\u4e4b\uff0c\u6346\u5f97\u7279\u522b\u7d27\uff0c\u9a6c\u662d\u96c4\u6574\u4e2a\u4eba\u5c31\u50cf\u5265\u4e86\u76ae\u7684\u7cbd\u5b50\u4e00 \u822c\u3002 \u3000\u3000\u4ed6\u62ac\u5934\u5f80\u4e0a\u671b\u4e86\u4e00\u773c\u9053\uff1a\u201c\u9a6c\u9999\u4e3b\uff0c\u73b0\u5728\u662f\u5426\u51c9\u5feb\u4e9b\uff1f\u4e00\u5b9a\u5f88\u723d\u5427\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u53ea\u662f\u5421\u7259\u88c2\u5634\u3002 \u3000\u3000\u5468\u6d77\u5c71\u7b11\u9053\uff1a\u201c\u4f60\u90a3\u5c41\u80a1\u8fd8\u6ee1\u767d\u561b\uff0c\u5c0f\u5fc3\u6709\u4eba\u8d70\u4f60\u7684\u540e\u95e8\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u8d8a\u53d1\u6c14\u5f97\u53d1\u660f\uff0c\u5403\u529b\u7684\u54bd\u4e00\u4e0b\u53e3\u553e\u6cab\u9053\uff1a\u201c\u5468\u603b\u7ba1\uff0c\u60a8\u600e\u4e48\u4e5f\u8bb2\u51fa\u8fd9\u79cd\u8bdd\u6765\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u8981\u8d70\u4f60\u540e\u95e8\u7684\u4e0d\u662f\u6211\u3002\u201d \u3000\u3000\u201c\u8c01\uff1f\u201d \u3000\u3000\u201c\u5f85\u4f1a\u513f\u5c31\u6709\u4eba\u6765\uff0c\u4e0d\u5c11\u5973\u4eba\u90fd\u8ba9\u4f60\u75db\u5feb\u8fc7\uff0c\u8fd9\u6b21\u8f6e\u5230\u4f60\u8ba9\u522b\u4eba\u75db\u5feb\u4e86\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u53ea\u9053\u5468\u6d77\u5c71\u8fd9\u8bdd\u662f\u771f\u7684\uff0c\u6025\u5f97\u4ed6\u62da\u529b\u6323\u624e\uff0c\u8fde\u6881\u67f1\u90fd\u5431\u5431\u4f5c\u54cd\u3002 \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u6025\uff0c\u90a3\u4eba\u9a6c\u4e0a\u5c31\u6765\uff0c\u5148\u6253\u6253\u79cb\u5343\u8fc7\u763e\u4e5f\u597d\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a7a\u5c4b\u5916\u54cd\u8d77\u4e86\u811a\u6b65\u58f0\u3002 \u3000\u3000\u5468\u6d77\u5c71\u987a\u53e3\u9053\uff1a\u201c\u9a6c\u9999\u4e3b\uff0c\u8d70\u4f60\u540e\u95e8\u7684\u4eba\u6765\u4e86\uff01\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u521a\u521a\u8bf4\u5b8c\uff0c\u8fdb\u6765\u7684\u8d6b\u7136\u662f\u4e25\u5bd2\u3002 \u3000\u3000\u5468\u6d77\u5c71\u7acb\u523b\u4f38\u4f38\u820c\u5c16\uff0c\u6253\u4e86\u4e2a\u54c6\u55e6\u3002 \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u5978\u4eba\u544a\u5bc6 \u3000\u3000\u4e25\u5bd2\u624b\u91cc\u8fd8\u62ff\u7740\u4e00\u6839\u76ae\u97ad\u3002 \u3000\u3000\u5468\u6d77\u5c71\u62c5\u5fc3\u521a\u624d\u7684\u8bdd\u88ab\u57ce\u4e3b\u542c\u5230\uff0c\u4e5f\u5413\u5f97\u6709\u4e9b\u4e0d\u77e5\u6240\u63aa\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u56e0\u4e3a\u8eab\u5b50\u88ab\u5b50\u540a\u7740\uff0c\u8138\u671d\u4e0a\uff0c\u6839\u672c\u770b\u4e0d\u5230\u6765\u4eba\u662f\u8c01\uff0c\u6025\u5f97\u4eba\u53eb\u9053\uff1a\u201c\u5343\u4e07\u4e0d\u80fd\u8d70 \u540e\u95e8\uff0c\u90a3\u8981\u5012\u9709\u4e00\u8f88\u5b50\u7684\uff01\u201d \u3000\u3000\u5468\u6d77\u5c71\u60c5\u4e0d\u81ea\u7981\u5927\u559d\u9053\uff1a\u201c\u738b\u516b\u86cb\uff0c\u4f60\u80e1\u8bf4\u4ec0\u4e48\uff01\u201d \u3000\u3000\u521a\u624d\u5468\u6d77\u5c71\u7684\u90a3\u51e0\u53e5\u8bdd\uff0c\u4e25\u5bd2\u5f53\u7136\u5df2\u7ecf\u542c\u5230\uff0c\u4ee5\u4ed6\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u53ea\u80fd\u88c5\u7740\u4e0d\u77e5\u9053\u7684\u6a21\u6837\u3002 \u3000\u3000\u4ed6\u628a\u76ae\u97ad\u4ea4\u7ed9\u5468\u6d77\u5c71\u9053\uff1a\u201c\u7ed9\u6211\u62bd\uff0c\u62bd\u5230\u4ed6\u65ad\u4e86\u6c14\u518d\u8bf4\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u8fd9\u624d\u542c\u51fa\u6765\u4eba\u662f\u57ce\u4e3b\u4e25\u5bd2\uff0c\u5413\u5f97\u6ca1\u9b42\u4f3c\u7684\u5636\u558a\u9053\uff1a\u201c\u57ce\u4e3b\u5f00\u6069\uff01\u57ce\u4e3b\u9976\u547d\uff01\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u5c1a\u672a\u558a\u5b8c\uff0c\u90a3\u76ae\u97ad\u65e9\u5df2\u201c\u5237\u201d\u7684\u62bd\u5230\u5c41\u80a1\u4e0a\u3002 \u3000\u3000\u5468\u6d77\u5c71\u6bcf\u4e00\u97ad\u90fd\u7528\u4e0a\u4e86\u529b\u9053\uff1a\u201c\u9a6c\u662d\u96c4\u5219\u662f\u6bcf\u6328\u4e00\u4e0b\uff0c\u4fbf\u662f\u4e00\u6b21\u7684\u75db\u5f7b\u5fc3\u80ba\uff0c\u6740\u732a\u822c\u7684 \u54c7\u54c7\u5927\u53eb\u3002 \u3000\u3000\u4e09\u4e94\u97ad\u4e0b\u53bb\uff0c\u4fbf\u5df2\u62bd\u5f97\u9a6c\u662d\u96c4\u76ae\u5f00\u8089\u7efd\uff0c\u9c9c\u8840\u2014\u6ef4\u6ef4\u7684\u76f4\u6ef4\u5230\u5730\u4e0a\u3002 \u3000\u3000\u6e10\u6e10\uff0c\u9a6c\u662d\u96c4\u5df2\u75db\u660f\u8fc7\u53bb\uff0c\u60e8\u53eb\u58f0\u4e5f\u8d8a\u6765\u8d8a\u5f31\u3002 \u3000\u3000\u5468\u6d77\u5c71\u8fd9\u624d\u505c\u4e0b\u624b\u6765\u3002 \u3000\u3000\u4e25\u5bd2\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u6ca1\u6709\u8001\u592b\u7684\u547d\u4ee4\uff0c\u4e0d\u51c6\u89e3\u4e0b\u6765\uff0c\u4e5f\u4e0d\u51c6\u9001\u996d\u6765\uff0c\u5148\u997f\u4ed6\u4e09\u5929\u518d\u8bf4\uff01\u201d \u3000\u3000\u8bf4\u5b8c\u8bdd\uff0c\u51b7\u7b11\u8fde\u58f0\u800c\u53bb\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u672c\u6765\u60f3\u6b21\u65e5\u4e00\u65e9\u5c31\u8d76\u5f80\u603b\u575b\u5411\u6559\u4e3b\u5bc6\u544a\u4e25\u5bd2\uff0c\u504f\u504f\u5f53\u665a\u63a5\u5230\u526f\u603b\u9547\u8981\u6765\u89c6\u5bdf\u7684\u901a \u62a5\u3002 \u3000\u3000\u9876\u5934\u4e0a\u53f8\u8981\u6765\u89c6\u5bdf\uff0c\u5979\u5f53\u7136\u4e0d\u80fd\u79bb\u5f00\uff0c\u800c\u4e14\u5979\u4e5f\u4e0d\u60f3\u5931\u53bb\u8fd9\u6b21\u673a\u4f1a\u3002 \u3000\u3000\u539f\u6765\u5979\u65e9\u5c31\u5bf9\u82b1\u7389\u9e9f\u5fc3\u751f\u7231\u6155\uff0c\u6068\u4e0d\u5f97\u627e\u673a\u4f1a\u6295\u6000\u9001\u62b1\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u7fe9\u7fe9\u6f47\u6d12\uff0c\u4eea\u8868\u51fa\u4f17\uff0c\u5979\u7b2c\u4e00\u6b21\u89c1\u5230\u65f6\u5c31\u96be\u514d\u5fc3\u733f\u610f\u9a6c\u3002 \u3000\u3000\u53ef\u60dc\u5f53\u65f6\u82b1\u7389\u9e9f\u6b63\u62c5\u4efb\u62db\u8d24\u9986\u526f\u9986\u4e3b\uff0c\u6839\u672c\u627e\u4e0d\u5230\u7406\u7531\u63a5\u8fd1\u3002 \u3000\u3000\u8fd9\u6b21\u82b1\u7389\u9e9f\u8c03\u4efb\u4e94\u5173\u526f\u603b\u9547\uff0c\u6700\u611f\u5174\u594b\u7684\u5c31\u662f\u5979\u3002 \u3000\u3000\u504f\u504f\u82b1\u7389\u9e9f\u4e00\u76f4\u4e0d\u66fe\u5230\u5bb5\u5173\u89c6\u5bdf\u3002 \u3000\u3000\u5979\u5728\u65e0\u5948\u4e4b\u4e0b\uff0c\u66fe\u6709\u4e24\u4e09\u6b21\u4eb2\u81f3\u603b\u9547\u5e9c\uff0c\u660e\u4e3a\u62dc\u8c12\uff0c\u6697\u662f\u501f\u673a\u63a5\u8fd1\uff0c\u9057\u61be\u7684\u662f\u6bcf\u6b21\u53bb\u90fd \u6251\u4e86\u7a7a\u3002 \u3000\u3000\u6b21\u65e5\uff0c\u5979\u5728\u56db\u66f4\u5929\u5c31\u8d77\u4e86\u5e8a\uff0c\u523b\u610f\u7684\u5316\u5986\u3001\u4fee\u9970\u3001\u6253\u626e\u4e86\u4e00\u756a\u3002 \u3000\u3000\u5176\u5b9e\u51e4\u5ae3\u7ea2\u672c\u6765\u5df2\u7ecf\u591f\u7f8e\u4e86\uff0c\u5373\u4f7f\u4e0d\u6253\u626e\uff0c\u7167\u6837\u5bf9\u7537\u4eba\u6709\u83ab\u5927\u7684\u5438\u5f15\u529b\u3002 \u3000\u3000\u5f53\u5979\u63d0\u524d\u7528\u8fc7\u65e9\u9910\u548c\u526f\u7edf\u9886\u5f20\u51e4\u9e23\u4f1a\u9762\u540e\uff0c\u8fde\u5e74\u8fc7\u82b1\u7532\u7684\u5f20\u51e4\u9e23\u90fd\u88ab\u5979\u7684\u5a07\u8273\u5f04\u5f97\u5fc3\u795e \u4e0d\u5b9a\u3002 \u3000\u3000\u5f20\u51e4\u9e23\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u7edf\u9886\u4eca\u5929\u600e\u4e48\u8d77\u6765\u5f97\u8fd9\u4e48\u65e9\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u526f\u603b\u9547\u8981\u6765\u89c2\u5bdf\uff0c\u54b1\u4eec\u5f53\u7136\u8981\u63d0\u524d\u51c6\u5907\u51c6\u5907\uff0c\u4f60\u4e5f\u8be5\u6362\u4ef6\u65b0\u8863\u670d\u624d\u5bf9\u3002\u201d \u3000\u3000\u5f20\u51e4\u9e23\u5475\u5475\u7b11\u9053\uff1a\u201c\u5351\u804c\u5e74\u7eaa\u5927\u4e86\uff0c\u5e72\u7684\u53c8\u662f\u526f\u5dee\u4e8b\uff0c\u9a6c\u9a6c\u864e\u864e\u5c31\u5e94\u4ed8\u8fc7\u53bb\u4e86\uff0c\u526f\u603b\u9547 \u8981\u6765\u770b\u7684\u5bf9\u8c61\uff0c\u4e3b\u8981\u662f\u7edf\u9886\u60a8\uff0c\u4ed6\u770b\u5230\u7edf\u9886\u60a8\u8fd9\u756a\u6253\u626e\uff0c\u51c6\u4f1a\u7ed9\u54b1\u4eec\u5bb5\u5173\u6253\u4e0a\u4e00\u767e\u5206\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u542b\u63f6\u6986\u7684\u8bdd\uff0c\u51e4\u5ae3\u7ea2\u4e0d\u4f46\u4e0d\u607c\uff0c\u53cd\u800c\u542c\u5f97\u6253\u5fc3\u5e95\u53d7\u7528\uff0c\u540c\u65f6\u4e5f\u6697\u611f\u5f97\u610f\u3002 \u3000\u3000\u5979\u602f\u751f\u751f\u7684\u7b11\u9053\uff1a\u201c\u526f\u7edf\u9886\uff0c\u4f60\u731c\u526f\u603b\u9547\u662f\u4e00\u4e2a\u4eba\u6765\uff1f\u8fd8\u662f\u4f1a\u5e26\u4e0d\u5c11\u4eba\u6765\uff1f\u201d \u3000\u3000\u5f20\u51e4\u9e23\u6342\u7740\u5c71\u7f8a\u80e1\u5b50\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u5f88\u96be\u8bb2\uff0c\u4e5f\u8bb8\u4ed6\u4f1a\u5e26\u51e0\u4f4d\u5de1\u5173\u4f7f\u8005\u6216\u526f\u4f7f\u8005\u6765\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u642d\u8baa\u7740\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u8ddf\u90a3\u4e9b\u4f7f\u8005\u3001\u526f\u4f7f\u8005\u90fd\u5f88\u719f\uff0c\u4ed6\u4eec\u90fd\u6709\u54ea\u51e0\u4f4d\uff1f\u201d \u3000\u3000\u5f20\u51e4\u9e23\u9053\uff1a\u201c\u5176\u5b9e\u5351\u804c\u548c\u4ed6\u4eec\uff0c\u53ea\u80fd\u7b97\u8ba4\u8bc6\u800c\u5df2\uff0c\u636e\u6211\u77e5\u9053\u7684\uff0c\u6709\u4e24\u4f4d\u4f7f\u8005\uff0c\u4e00\u4f4d\u59d3\u5cb3\uff0c \u4e00\u4f4d\u59d3\u8881\u3002\u201d \u3000\u3000\u201c\u4ed6\u4eec\u90fd\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u201c\u90a3\u4f4d\u5cb3\u4f7f\u8005\u5e74\u7eaa\u6700\u8f7b\uff0c\u53ea\u6709\u5341\u4e09\u56db\u5c81\uff0c\u4f46\u5374\u6b66\u529f\u6700\u9ad8\uff0c\u5b66\u95ee\u6700\u597d\uff0c\u662f\u4f4d\u96be\u5f97\u4e00\u89c1\u7684\u5c11 \u5e74\u5947\u624d\uff0c\u81f3\u4e8e\u90a3\u4eec\u8881\u4f7f\u8005\uff0c\u6b66\u529f\u6587\u624d\u4e5f\u662f\u4e00\u6d41\u7684\uff0c\u96be\u5f97\u7684\u662f\u4eba\u5f88\u8001\u5b9e\uff0c\u4e5f\u7b97\u4e0d\u53ef\u591a\u5f97\u7684\u4eba \u624d\u3002\u201d \u3000\u3000\u201c\u526f\u4f7f\u8005\u91cc\u4f60\u6709\u8ba4\u8bc6\u7684\uff1f\u201d \u3000\u3000\u201c\u6709\uff0c\u4e00\u4f4d\u59d3\u4e01\uff0c\u4e00\u4f4d\u59d3\u7518\uff0c\u4ed6\u4eec\u6c5f\u6e56\u9605\u5386\u867d\u5f88\u4e30\u5bcc\uff0c\u4f46\u5374\u662f\u4e00\u5bf9\u7c97\u4eba\uff0c\u4e0d\u8fc7\u4e3a\u4eba\u4e5f\u86ee \u597d\u3002\u201d \u3000\u3000\u5728\u51e4\u5ae3\u7ea2\u7684\u60f3\u6cd5\uff0c\u5f53\u7136\u662f\u5e0c\u671b\u82b1\u7389\u9e9f\u72ec\u81ea\u800c\u6765\uff0c\u8fd9\u6837\u5979\u5c31\u53ef\u4ee5\u628a\u4ed6\u8bf7\u5230\u81ea\u5df1\u7684\u5c0f\u5ba2\u5385\uff0c \u4f7f\u51fa\u5a9a\u529f\uff0c\u732e\u732e\u6bb7\u52e4\uff0c\u8ba9\u4ed6\u6765\u4e2a\u82f1\u96c4\u96be\u8fc7\u7f8e\u4eba\u5173\uff0c\u8bf4\u4e0d\u5b9a\u5f53\u573a\u5c31\u53ef\u6210\u5c31\u597d\u4e8b\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u4f3c\u4e4e\u68a6\u5df2\u6210\u771f\uff0c\u7d22\u6027\u4e5f\u5bf9\u5f20\u98ce\u545c\u8868\u793a\u4e00\u4e0b\u4eb2\u5207\uff0c\u62c9\u62c9\u4ed6\u7684\u8896\u5b50\u9053\uff1a\u201c\u5f20\u8001\uff0c \u54b1\u4eec\u4e00\u8d77\u5230\u5927\u95e8\u53e3\u7b49\u7740\u8fce\u63a5\u5427\uff01\u201d \u3000\u3000\u5f20\u51e4\u9e23\u7b80\u76f4\u53d7\u5ba0\u82e5\u60ca\uff0c\u597d\u5728\u4ed6\u5e74\u7eaa\u5927\u4e86\uff0c\u8fd8\u80fd\u628a\u6301\u5f97\u4f4f\u3002 \u3000\u3000\u6765\u5230\u5927\u95e8\u53e3\uff0c\u5f20\u51e4\u9e23\u6545\u610f\u6ca1\u8bdd\u627e\u8bdd\u9053\uff1a\u201c\u4eca\u5929\u662f\u526f\u603b\u9547\u7b2c\u4e00\u6b21\u5230\u54b1\u4eec\u5bb5\u5173\u6765\uff0c\u5982\u679c\u8981\u8868 \u793a\u9686\u91cd\u4e9b\uff0c\u6700\u597d\u80fd\u6446\u4e0a\u4e2a\u4eea\u961f\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u600e\u4e48\u6ca1\u60f3\u5230\u8fd9\u4e00\u5c42\u5462\uff1f\u90a3\u5c31\u52b3\u9a7e\u9a6c\u4e0a\u901a\u77e5\u4e00\u58f0\uff0c\u8981\u5f1f\u5144\u4eec \u90fd\u6765\u96c6\u5408\u3002\u201d \u3000\u3000\u8c01\u77e5\u5f20\u51e4\u9e23\u5374\u6447\u5934\u9053\uff1a\u201c\u5351\u804c\u7684\u610f\u601d\uff0c\u6700\u597d\u662f\u6446\u51fa\u4e2a\u5973\u5175\u4eea\u961f\uff0c\u8fd9\u6837\u4e0d\u4f46\u663e\u5f97\u65b0\u9c9c\uff0c\u4e5f \u6709\u5438\u5f15\u529b\u3002\u201d \u3000\u3000\u5f20\u51e4\u9e23\u8fd9\u8001\u5bb6\u4f19\u6240\u4ee5\u8981\u51fa\u6b6a\u70b9\u5b50\uff0c\u4e0d\u8fc7\u662f\u60f3\u535a\u53d6\u51e4\u5ae3\u7ea2\u6b22\u5fc3\uff0c\u540c\u65f6\u4e5f\u51fa\u51fa\u5979\u7684\u6d0b\u76f8\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u679c\u7136\u8ba4\u4e3a\u8fd9\u4e3b\u610f\u66f4\u597d\uff0c\u81f3\u5c11\u5bcc\u6709\u5671\u5934\u3002 \u3000\u3000\u5979\u4e3a\u4e86\u96c6\u5408\u5f97\u5feb\uff0c\u4eb2\u81ea\u8dd1\u5230\u540e\u9762\u62db\u547c\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u5de5\u592b\uff0c\u4fbf\u96c6\u5408\u4e86\u4e8c\u5341\u56db\u540d\u5c11\u5973\u3002 \u3000\u3000\u8fd9\u4e9b\u5c11\u5973\u4eec\u4e2a\u4e2a\u8eab\u6750\u82d7\u6761\uff0c\u8138\u86cb\u4fcf\u4e3d\uff0c\u7a7f\u7740\u9752\u4e00\u8272\u7d2b\u8863\u7d2b\u88d9\uff0c\u6392\u5217\u5728\u5927\u95e8\u5916\uff0c\u679c\u7136\u662f\u4e00 \u652f\u5f15\u4eba\u6ce8\u76ee\u7684\u7f8e\u4e3d\u961f\u4f0d\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u4e3a\u4e86\u8ba9\u5979\u4eec\u6709\u597d\u7684\u8868\u73b0\uff0c\u4e34\u65f6\u8fd8\u4eb2\u81ea\u6559\u5bfc\u4e86\u5979\u4eec\u4e00\u4e9b\u5e94\u6709\u7684\u793c\u8282\u548c\u52a8\u4f5c\uff0c\u4f8b\u5982\u5982 \u4f55\u656c\u793c\uff0c\u5982\u4f55\u6446\u5934\uff0c\u5982\u4f55\u6ce8\u76ee\u7b49\uff0c\u90fd\u89c4\u5b9a\u5f97\u8be6\u8be6\u7ec6\u7ec6\uff0c\u4ea4\u4ee3\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u800c\u4e14\u5979\u81ea\u5df1\u53c8\u5f53\u9605 \u5175\u5b98\uff0c\u53cd\u590d\u6f14\u7ec3\u4e86\u597d\u591a\u6b21\u3002 \u3000\u3000\u521a\u521a\u64cd\u7ec3\u5f97\u5dee\u4e0d\u591a\uff0c\u82b1\u7389\u9e9f\u4fbf\u5df2\u5230\u8fbe\u4e86\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u5668\u5b87\u8f69\u6602\u7684\u8d70\u5728\u524d\u9762\uff0c\u8eab\u540e\u7d27\u7d27\u8ddf\u7740\u4e09\u540d\u968f\u5458\uff0c\u6b63\u662f\u8881\u5c0f\u9e64\u3001\u4e01\u6d9b\u548c\u7518\u9716\u3002 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u56e0\u4e3a\u4eca\u5929\u662f\u966a\u4f8d\u526f\u603b\u9547\uff0c\u4e24\u4eba\u633a\u80f8\u51f9\u809a\uff0c\u4e5f\u663e\u5f97\u683c\u5916\u795e\u6c14\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u4eec\u8001\u8fdc\u5c31\u611f\u5230\u60ca\u5947\uff0c\u56e0\u4e3a\u4ed6\u4eec\u65e9\u5c31\u671b\u89c1\u4e86\u5927\u95e8\u5916\u6392\u5217\u4e86\u4e00\u961f\u5343\u5a07\u767e\u5a9a\u7684\u8102\u7c89\u961f\u4f0d\uff0c \u5c31\u50cf\u8981\u7ade\u9009\u4ec0\u4e48\u5c0f\u59d0\u4f3c\u7684\u3002 \u3000\u3000\u8fde\u82b1\u7389\u9e9f\u4e5f\u611f\u5230\u5341\u5206\u60ca\u5947\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u7acb\u523b\u7387\u9886\u5f20\u51e4\u9e23\u5feb\u6b65\u8fce\u4e86\u4e0a\u6765\uff0c\u4e24\u4eba\u5404\u81ea\u5411\u82b1\u7389\u9e9f\u81f4\u656c\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u505c\u4e0b\u811a\u6b65\u9053\uff1a\u201c\u5927\u95e8\u5916\u8fd9\u961f\u5973\u5175\u662f\u505a\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u53d1\u51fa\u5979\u90a3\u71d5\u8bed\u83ba\u5453\u822c\u7684\u58f0\u97f3\u9053\uff1a\u201c\u662f\u5351\u804c\u8ba9\u5979\u4eec\u6765\u6b22\u8fce\u526f\u603b\u9547\u7684\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u4e00\u76b1\u7709\u5b87\u9053\uff1a\u201c\u8fd9\u662f\u4f55\u82e6\uff0c\u8fce\u63a5\u6211\u4f55\u5fc5\u6446\u8fd9\u4e48\u5927\u573a\u9762\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u526f\u603b\u9547\u662f\u7b2c\u4e00\u6b21\u5230\u8fd9\u91cc\u6765\uff0c\u975e\u6bd4\u5bfb\u5e38\uff0c\u8fd9\u662f\u5e94\u8be5\u7684\u3002\u201d \u3000\u3000\u5f20\u51e4\u9e23\u4e5f\u7d27\u8ddf\u7740\u9053\uff1a\u201c\u6211\u4eec\u7edf\u9886\u4e3a\u8fce\u63a5\u526f\u603b\u9547\u9a7e\u4e34\uff0c\u7b97\u5f97\u4e0a\u662f\u82b1\u4e86\u4e00\u756a\u5fc3\u601d\uff0c\u505a\u4e0b\u5c5e\u7684 \u5bf9\u4e0a\u7ea7\uff0c\u5c31\u8be5\u8fd9\u6837\u5b50\uff0c\u526f\u603b\u9547\u5feb\u8bf7\u5230\u91cc\u8fb9\u53bb\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u4e0d\u518d\u5ba2\u6c14\uff0c\u4e3e\u6b65\u7ee7\u7eed\u524d\u8fdb\u3002 \u3000\u3000\u5f53\u884c\u8fd1\u7f8e\u4e3d\u961f\u4f0d\u524d\u65b9\u7ea6\u516d\u4e03\u6b65\u65f6\uff0c\u6392\u5934\u4e00\u58f0\u5a07\u558a\uff0c\u53d1\u51fa\u4e86\u656c\u793c\u53e3\u4ee4\u3002 \u3000\u3000\u63a5\u7740\uff0c\u5979\u4eec\u6574\u9f50\u5212\u4e00\u7684\u5411\u82b1\u7389\u9e9f\u6446\u5934\u884c\u6ce8\u76ee\u793c\uff0c\u800c\u4e14\u8fd8\u76ee\u8fce\u76ee\u9001\u3002 \u3000\u3000\u8fd9\u4e9b\u5973\u5175\u4eec\u4e00\u89c1\u526f\u603b\u9547\u7adf\u662f\u5982\u6b64\u82f1\u59ff\u7115\u53d1\uff0c\u800c\u4e14\u5a01\u4eea\u51fa\u4f17\uff0c\u90fd\u770b\u5f97\u53cc\u7738\u53d1\u76f4\uff0c\u4e00\u4e2a\u4e2a\u82b3 \u5fc3\u4e5f\u8df3\u4e2a\u4e0d\u505c\uff0c\u5373\u4f7f\u8fd9\u65f6\u89c4\u5b9a\u5979\u4eec\u4e0d\u51c6\u770b\uff0c\u5979\u4eec\u4e5f\u975e\u770b\u4e0d\u53ef\u4e86\u3002 \u3000\u3000\u63a5\u7740\u5979\u4eec\u671b\u89c1\u4e86\u8ddf\u5728\u540e\u9762\u7684\u8881\u5c0f\u9e64\u3001\u4e01\u6d9b\u548c\u7518\u9716\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u4e0d\u5c11\u5973\u5175\u90fd\u5fcd\u4e0d\u4f4f\u8981\u5f00\u95e8\u4e86\uff0c\u53ea\u542c\u5176\u4e2d\u6709\u4eba\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u5c0f\u767d\u8138\u662f\u8dd1\u6b65\u9a97\u4eba\u901a\u8fc7 \u7684\uff01\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u55b3\u55b3\u7684\u9053\uff1a\u201c\u8fd9\u7626\u5927\u4e2a\u5b50\u662f\u88c5\u7740\u88ab\u6740\u4e86\u4e00\u5200\uff0c\u6d41\u51fa\u80a0\u5b50\u6765\u7684\uff01\u201d \u3000\u3000\u53c8\u4e00\u4e2a\u4f4e\u53eb\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5927\u5bb6\u770b\uff0c\u8fd9\u9ed1\u5927\u6c49\u5c31\u662f\u8981\u5927\u5200\u8981\u8fc7\u53bb\u7684\u90a3\u4e2a\uff01\u201d \u3000\u3000\u8fd9\u65f6\u8881\u5c0f\u9e64\u3001\u4e01\u6d9b\u548c\u7518\u9716\u4e5f\u89c9\u5f97\u5973\u5175\u961f\u4f0d\u4e2d\uff0c\u6709\u4e0d\u5c11\u4eba\u4f3c\u66fe\u76f8\u8bc6\u3002 \u3000\u3000\u4ed6\u4eec\u5f88\u5feb\u60f3\u8d77\uff0c\u8fd9\u4e9b\u4f3c\u66fe\u76f8\u8bc6\u7684\uff0c\u6b63\u662f\u5f53\u521d\u5728\u5bb5\u5173\u53d7\u6d4b\u65f6\u201c\u7a91\u59d0\u62c9\u5ba2\u201d\u90a3\u4e00\u7ad9\u7684\u7a91\u59d0\u513f\u3002 \u3000\u3000\u539f\u6765\u4e0a\u6b21\u201c\u7a91\u59d0\u62c9\u5ba2\u201d\u90a3\u4e00\u7ad9\u7684\u5993\u5973\uff0c\u6b63\u662f\u8fd9\u4e9b\u5973\u5175\u4eec\u6539\u626e\u7684\uff0c\u5979\u4eec\u5f53\u7136\u5bf9\u8881\u5c0f\u9e64\u7b49\u8bb0 \u5fc6\u72b9\u65b0\u3002 \u3000\u3000\u81f3\u4e8e\u201c\u6148\u6bcd\u671b\u513f\u201d\u90a3\u4e00\u7ad9\u7684\u56db\u4f4d\u8001\u592a\u592a\u4ee5\u53ca\u201c\u5be1\u5987\u76fc\u592b\u201d\u90a3\u4e00\u7ad9\u7684\u56db\u4f4d\u5be1\u5987\uff0c\u5219\u662f\u4e8b\u5148 \u7279\u7ea6\u7684\uff0c\u4e8b\u540e\u518d\u7ed9\u5979\u4eec\u4e00\u70b9\u916c\u52b3\uff0c\u5426\u5219\uff0c\u600e\u4f1a\u90a3\u4e48\u5de7\u56db\u5bb6\u95e8\u95e8\u7ad9\u7740\u56db\u4f4d\u671b\u513f\u7684\u8001\u592a\u5927\u3001\u56db\u5bb6 \u7ad9\u524d\u7ad9\u7740\u56db\u4f4d\u76fc\u592b\u7684\u5be1\u5987\u5462\u3002 \u3000\u3000\u5f53\u7136\uff0c\u90a3\u56db\u4f4d\u8001\u592a\u592a\u548c\u56db\u4f4d\u5be1\u5987\uff0c\u4e5f\u90fd\u662f\u4e8b\u5148\u7ecf\u8fc7\u6392\u7ec3\u7684\u3002 \u3000\u3000\u53e6\u5916\u201c\u767d\u5403\u5305\u5b50\u201d\u90a3\u4e00\u7ad9\uff0c\u5374\u786e\u5b9e\u662f\u771f\u6b63\u7684\u996d\u9986\uff0c\u4f46\u4e8b\u5148\u4e5f\u662f\u7ea6\u597d\u7684\uff0c\u5982\u679c\u771f\u767d\u5403\u4e86\u5305 \u5b50\uff0c\u5bb5\u5173\u5fc5\u5b9a\u7167\u4ef7\u8d54\u507f\u3002 \u3000\u3000\u7ecf\u8fc7\u8fd9\u4e48\u4e00\u8d9f\u5973\u5175\u4eea\u961f\uff0c\u4e01\u6d9b\u548c\u7518\u9716\u8868\u9762\u867d\u88c5\u6a21\u4f5c\u6837\uff0c\u5fc3\u91cc\u5374\u8fde\u547c\u8fc7\u763e\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u7d27\u968f\u5728\u82b1\u7389\u9e9f\u8eab\u540e\uff0c\u671b\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u5979\u771f\u6068\u4e0d\u5f97\u6251\u4e0a\u524d\u53bb\u62e5\u62b1\u4e00\u4e0b\u3002 \u3000\u3000\u6709\u53e5\u8bdd\u8bf4\uff1a\u201c\u4eba\u6bd4\u4eba\uff0c\u6c14\u6b7b\u4eba\u3002\u201d\u4ee5\u5f80\u51e4\u5ae3\u7ea2\u548c\u9a6c\u662d\u96c4\u5728\u2014\u8d77\uff0c\u53ea\u89c9\u9a6c\u662d\u96c4\u5404\u65b9\u9762\u4e5f\u5f88 \u591f\u53ef\u7231\u7684\uff0c\u4f46\u5982\u4eca\u89c1\u4e86\u82b1\u7389\u9e9f\uff0c\u9a6c\u662d\u96c4\u5c31\u5b9e\u5728\u4e0d\u80fd\u6bd4\u4e86\uff0c\u6b64\u523b\u8ddf\u5728\u82b1\u7389\u9e9f\u8eab\u65c1\uff0c\u6559\u5979\u5982\u4f55\u80fd \u4e0d\u5fc3\u8361\u795e\u9a70\uff0c\u82b1\u7389\u9e9f\u4e3a\u4e86\u62d6\u5ef6\u65f6\u95f4\uff0c\u6545\u610f\u8d70\u5f97\u6781\u6162\u3002 \u3000\u3000\u4ed6\u4eec\u5148\u5728\u5927\u5385\u559d\u8fc7\u8336\uff0c\u7136\u540e\u4fbf\u662f\u5230\u5404\u5904\u5de1\u89c6\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u89c1\u627e\u4e0d\u5230\u5355\u72ec\u548c\u82b1\u7389\u9e9f\u76f8\u5904\u7684\u673a\u4f1a\uff0c\u82b3\u5fc3\u4e00\u6025\uff0c\u8ba1\u4e0a\u5fc3\u6765\uff0c\u7acb\u523b\u4ea4\u4ee3\u5f20\u51e4\u9e23\u9053\uff1a \u201c\u6211\u770b\u5206\u5934\u770b\u6bd4\u8f83\u597d\uff0c\u526f\u7edf\u9886\u5c31\u8bf7\u966a\u4ed6\u4eec\u4e09\u4f4d\u5230\u5404\u5904\u8d70\u8d70\uff0c\u6211\u6765\u966a\u526f\u603b\u9547\u3002\u201d \u3000\u3000\u5f20\u51e4\u9e23\u5f53\u7136\u4e0d\u4f1a\u6709\u5f02\u8bae\uff0c\u8881\u5c0f\u9e64\u7b49\u4e09\u4eba\u4e5f\u4e0d\u80fd\u4e0d\u540c\u610f\uff0c\u968f\u5373\u5206\u6210\u4e86\u4e24\u7ec4\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u4e5f\u4e0d\u8868\u793a\u610f\u89c1\uff0c\u4ed6\u5df2\u77e5\u51e4\u5ae3\u7ea2\u6deb\u8361\u6210\u6027\uff0c\u800c\u4e14\u66f4\u548c\u9a6c\u662d\u96c4\u4e71\u4f26\u79c1\u5954\uff0c\u4e5f\u6b63\u60f3\u8bd5\u8bd5 \u8fd9\u5973\u4eba\u5230\u5e95\u6deb\u8361\u5230\u4ec0\u4e48\u7a0b\u5ea6\uff0c\u56e0\u4e3a\u51e4\u5ae3\u7ea2\u7684\u4e11\u95fb\uff0c\u4ed6\u90fd\u662f\u542c\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u8bf4\u7684\uff0c\u662f\u5426\u5f53\u771f \u5982\u6b64\uff0c\u8fd8\u6709\u5f85\u67e5\u8bc1\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u53ea\u966a\u7740\u82b1\u7389\u9e9f\u5404\u5904\u5927\u7565\u8d70\u4e86\u4e00\u8d9f\uff0c\u4fbf\u628a\u82b1\u7389\u9e9f\u5e26\u5230\u81ea\u5df1\u4f4f\u5904\u3002 \u3000\u3000\u8fdb\u5165\u5ba2\u5385\uff0c\u5979\u4e0d\u4f46\u4e0d\u7528\u4f8d\u5a62\uff0c\u53cd\u800c\u628a\u4f8d\u5a62\u8d76\u4e86\u51fa\u53bb\u3002\u81ea\u5df1\u4eb2\u81ea\u6bb7\u52e4\u6c8f\u8336\uff0c\u5f53\u7aef\u8336\u5230\u9762\u524d \u65f6\uff0c\u6545\u610f\u5f80\u82b1\u7389\u9e9f\u8eab\u4e0a\u8f7b\u8f7b\u78b0\u4e86\u4e00\u4e0b\uff0c\u7136\u540e\u518d\u5728\u5bf9\u9762\u843d\u4e86\u5ea7\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u559d\u4e86\u53e3\u8336\u9053\uff1a\u201c\u4f60\u4e0d\u6127\u662f\u4f4d\u80fd\u5e72\u7684\u7edf\u9886\uff0c\u628a\u5bb5\u5173\u6cbb\u7406\u5f97\u6709\u6761\u4e0d\u7d0a\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u542b\u7f9e\u5e26\u7b11\u7684\u5411\u82b1\u7389\u9e9f\u779f\u4e86\u4e00\u773c\u9053\uff1a\u201c\u526f\u603b\u9547\u8fc7\u5956\u4e86\uff0c\u5982\u679c\u60a8\u80fd\u5e38\u5e38\u6765\u89c6\u5bdf\u6307\u5bfc\uff0c \u5bb5\u5173\u4e00\u5b9a\u6bd4\u73b0\u5728\u66f4\u597d\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u6de1\u7136\u4e00\u7b11\u9053\uff1a\u201c\u82b1\u67d0\u521d\u6765\u4e4d\u5230\uff0c\u90a3\u91cc\u8c08\u5f97\u4e0a\u6307\u5bfc\uff0c\u5982\u679c\u8bf4\u662f\u6765\u5b66\u4e60\uff0c\u90a3\u8fd8\u5dee\u4e0d \u591a\u3002\u201d \u3000\u3000\u201c\u526f\u603b\u9547\u8fd9\u6837\u5ba2\u6c14\uff0c\u5351\u804c\u54ea\u91cc\u6562\u5f53\uff0c\u5bf9\u5566\uff0c\u4e94\u5173\u603b\u9547\u5e9c\u6210\u7acb\u5df2\u7ecf\u5feb\u4e00\u4e2a\u6708\u4e86\uff0c\u60a8\u600e\u4e48\u5230 \u73b0\u5728\u624d\u7b2c\u4e00\u6b21\u6765\uff1f\u201d \u3000\u3000\u201c\u8fd9\u673a\u5173\u521a\u521a\u6210\u7acb\uff0c\u4e00\u5207\u5c1a\u672a\u8d70\u4e0a\u8f68\u9053\uff0c\u96be\u514d\u5fd9\u4e9b\uff0c\u4ee5\u540e\u82b1\u67d0\u5c31\u53ef\u5e38\u5e38\u6765\u4e86\u3002\u201d \u3000\u3000\u201c\u5351\u804c\u4e5f\u66fe\u597d\u51e0\u6b21\u5230\u603b\u9547\u5e9c\u53bb\u62dc\u89c1\u60a8\uff0c\u53ef\u60dc\u6bcf\u6b21\u90fd\u6ca1\u9047\u4e0a\u3002\u201d \u3000\u3000\u201c\u82b1\u67d0\u4e5f\u542c\u8bf4\u8fc7\uff0c\u52b3\u4f60\u7a7a\u8dd1\u597d\u51e0\u8d9f\uff0c\u975e\u5e38\u62b1\u6b49\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u89c1\u82b1\u7389\u9e9f\u975e\u5e38\u4eb2\u5207\u968f\u548c\uff0c\u6599\u60f3\u5fc5\u5b9a\u53ef\u4ee5\u4e0a\u94a9\uff0c\u82b3\u5fc3\u6697\u559c\u4e4b\u4e0b\uff0c\u80c6\u5b50\u4e5f\u6e10\u6e10\u5927\u4e86\uff0c \u4e0d\u89c9\u5a9a\u5a9a\u4e00\u7b11\u9053\uff1a\u201c\u526f\u603b\u9547\u8bf4\u90a3\u91cc\u8bdd\uff0c\u53ea\u8981\u80fd\u89c1\u5230\u60a8\uff0c\u5351\u804c\u5c31\u662f\u8dd1\u4e0a\u5341\u8d9f\u516b\u8d9f\uff0c\u4e5f\u662f\u503c\u5f97 \u7684\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u770b\u4e0d\u503c\u5f97\uff0c\u5bb5\u5173\u4e8b\u60c5\u5f88\u591a\uff0c\u5982\u679c\u53ea\u4e3a\u548c\u6211\u89c1\u6b21\u9762\u803d\u8bef\u4e86\u516c\u52a1\uff0c\u90a3 \u4e0d\u662f\u6211\u6240\u613f\u89c1\u7684\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u8138\u4e0a\u4e00\u70ed\uff0c\u63a5\u7740\u53c8\u602f\u751f\u751f\u7b11\u9053\uff1a\u201c\u90a3\u662f\u526f\u603b\u9547\u591a\u8651\u4e86\uff0c\u5351\u804c\u5728\u5bb5\u5173\u597d\u51e0\u5e74\uff0c\u4ec0\u4e48 \u4e8b\u90fd\u5df2\u9a7e\u8f7b\u5c31\u70ed\uff0c\u4f55\u51b5\u66f4\u6709\u4e00\u4f4d\u80fd\u5e72\u7684\u526f\u7edf\u9886\uff0c\u6709\u4ed6\u5728\uff0c\u4ec0\u4e48\u4e8b\u90fd\u5904\u7406\u5f97\u597d\u597d\u7684\u3002\u201d \u3000\u3000\u5979\u987f\u4e86\u987f\uff0c\u53c8\u9053\uff1a\u201c\u5176\u5b9e\u5351\u804c\u53bb\u89c1\u526f\u603b\u9547\uff0c\u4e5f\u5e76\u975e\u767d\u89c1\uff0c\u53ea\u8981\u60a8\u80af\u591a\u6307\u6559\uff0c\u5351\u804c\u5fc5\u5b9a\u83b7 \u76ca\u826f\u591a\uff0c\u95fb\u541b\u4e00\u5e2d\u8bdd\uff0c\u80dc\u8bfb\u5341\u5e74\u4e66\uff0c\u6b63\u662f\u5351\u804c\u6240\u5e0c\u671b\u7684\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u51e4\u7edf\u9886\u672a\u514d\u628a\u82b1\u67d0\u62ac\u5f97\u592a\u9ad8\u4e86\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u629b\u8fc7\u4e00\u4e2a\u5a9a\u773c\u9053\uff1a\u201c\u526f\u603b\u9547\u600e\u4e48\u53c8\u5ba2\u6c14\u8d77\u6765\u4e86\uff1f\u5351\u804c\u65e9\u5c31\u542c\u8bf4\u60a8\u662f\u4f4d\u6587\u6b66\u5168\u624d\u7684 \u5927\u82f1\u96c4\uff0c\u5bf9\u5566\uff0c\u526f\u603b\u9547\u5e73\u65f6\u90fd\u505a\u4e9b\u4ec0\u4e48\u6d88\u9063\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u82b1\u67d0\u4e3a\u6559\u4e3b\u6548\u529b\uff0c\u53ea\u6c42\u628a\u4e8b\u60c5\u505a\u597d\uff0c\u90a3\u91cc\u8fd8\u8c08\u5230\u4ec0\u4e48\u6d88\u9063\u3002\u201d \u3000\u3000\u201c\u4eba\u751f\u5728\u4e16\uff0c\u603b\u8be5\u53ca\u65f6\u884c\u4e50\uff0c\u5351\u804c\u89c9\u5f97\u5929\u8c37\u4ec0\u4e48\u90fd\u597d\uff0c\u5c31\u662f\u6709\u4e00\u6837\u4e0d\u597d\u3002\u201d \u3000\u3000\u201c\u51e4\u7edf\u9886\u6307\u7684\u662f\u90a3\u4e00\u6837\uff1f\u201d \u3000\u3000\u201c\u5351\u804c\u8bf4\u7684\uff0c\u5c31\u662f\u592b\u59bb\u4e0d\u80fd\u540c\u6765\uff0c\u5c31\u4ee5\u526f\u603b\u9547\u6765\u8bf4\uff0c\u5fc5\u5b9a\u65e9\u5df2\u6210\u4eb2\uff0c\u504f\u504f\u5c0a\u592b\u4eba\u4e0d\u80fd\u6765\uff0c \u96be\u9053\u60a8\u5c31\u4e0d\u611f\u5230\u5bc2\u5bde\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u7687\u5143\u6559\u7684\u89c4\u5b9a\uff0c\u5bc2\u5bde\u4e5f\u6ca1\u529e\u6cd5\u3002\u201d \u3000\u3000\u201c\u526f\u603b\u9547\u6ca1\u6709\u529e\u6cd5\uff0c\u81ea\u5df1\u5c31\u8be5\u60f3\u529e\u6cd5\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u89c1\u51e4\u5ae3\u7ea2\u8bf4\u8bdd\u8d8a\u6765\u8d8a\u9732\u9aa8\uff0c\u5374\u6545\u610f\u4e0d\u52a8\u58f0\u8272\uff0c\u957f\u957f\u5401\u4e86\u53e3\u6c14\uff1a\u201c\u82b1\u67d0\u5b9e\u5728\u6ca1\u6709\u529e \u6cd5\u53ef\u60f3\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u7c89\u988a\u6cdb\u7ea2\u4f4e\u4e0b\u5934\u53bb\u9053\uff1a\u201c\u5351\u804c\u4e5f\u8bb8\u6709\u529e\u6cd5\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u7d22\u6027\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u671b\u4e86\u671b\u82b1\u7389\u9e9f\u8138\u8272\uff0c\u58f0\u97f3\u67d4\u67d4\u7684\u9053\uff1a\u201c\u5351\u804c\u8fd9\u91cc\u7684\u4f8d\u5a62\u5f88\u591a\uff0c\u4e0d\u4e4f\u59ff\u8272\u4e0d\u9519\u7684\uff0c \u53ea\u8981\u526f\u603b\u9547\u770b\u5f97\u4e0a\uff0c\u4e0d\u8bba\u9001\u5230\u603b\u9547\u5e9c\uff0c\u6216\u8005\u60a8\u5230\u5173\u4e0a\u6765\uff0c\u90fd\u65b9\u4fbf\u5f97\u5f88\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u51b7\u51b7\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u6837\u7684\u529e\u6cd5\u5417\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u659c\u98d8\u7740\u5a9a\u773c\u7b11\u9053\uff1a\u201c\u5982\u679c\u526f\u603b\u9547\u5acc\u5979\u4eec\u4e0d\u597d\uff0c\u5351\u804c\u8fd8\u6709\u597d\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u672a\u514d\u592a\u9ebb\u70e6\u4f60\u4e86\uff1f\u201d \u3000\u3000\u201c\u505a\u90e8\u4e0b\u7684\u66ff\u4e0a\u9762\u5206\u5fe7\u89e3\u6101\uff0c\u5e94\u8be5\u662f\u4efd\u5185\u4e8b\uff0c\u90a3\u91cc\u6562\u6015\u9ebb\u70e6\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u5ffd\u7136\u7ad9\u8d77\u8eab\u6765\u9053\uff1a\u201c\u6211\u8fd8\u5fd8\u4e86\u6709\u4ef6\u4e8b\u8981\u4ea4\u4ee3\u4ed6\u4eec\u3002\u201d \u3000\u3000\u5728\u8fd9\u79cd\u8282\u9aa8\u773c\u4e0a\uff0c\u82b1\u7389\u9e9f\u5ffd\u7136\u8981\u8d70\uff0c\u51e4\u5ae3\u7ea2\u96be\u514d\u5927\u611f\u610f\u5916\uff0c\u6025\u6025\u4e5f\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u526f\u603b\u9547 \u4ec0\u4e48\u4e8b\u8fd9\u6837\u8981\u7d27\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u81ea\u7136\u662f\u516c\u4e8b\uff0c\u516c\u4e8b\u603b\u8be5\u653e\u5728\u79c1\u4e8b\u524d\u9762\u3002\u201d \u3000\u3000\u201c\u60a8\u4e0d\u59a8\u544a\u8bc9\u5351\u804c\uff0c\u5351\u804c\u6d3e\u4eba\u4ee3\u60a8\u4f20\u8fbe\u4e5f\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u201c\u4e0d\u6210\uff0c\u6211\u5fc5\u987b\u4eb2\u81ea\u4ea4\u4ee3\uff0c\u51e4\u7edf\u9886\u4e0d\u5fc5\u518d\u966a\uff0c\u6211\u8d70\u4e86\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u79bb\u5f00\u51e4\u5ae3\u7ea2\u7684\u5ba2\u5385\uff0c\u5f88\u5feb\u4fbf\u548c\u8881\u5c0f\u9e64\u7b49\u4e09\u4eba\u4f1a\u5408\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u4e0d\u4f46\u5b8c\u5168\u76f8\u4fe1\u4e86\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u7684\u8bdd\uff0c\u800c\u4e14\u6bd4\u4ed6\u9884\u6599\u4e2d\u7684\u8fd8\u8981\u66f4\u751a\u51e0\u5206\u3002 \u3000\u3000\u4e0d\u4e45\u4e4b\u540e\uff0c\u51e4\u5ae3\u7ea2\u53c8\u8ddf\u4e86\u8fc7\u6765\uff0c\u575a\u7559\u4ed6\u4eec\u5728\u5bb5\u5173\u5348\u9910\u3002 \u3000\u3000\u4f9d\u82b1\u7389\u9e9f\u7684\u610f\u601d\uff0c\u672c\u8be5\u4e00\u523b\u4e0d\u7559\u7684\u8d76\u56de\u53bb\uff0c\u4ed6\u4e3a\u4e86\u4f7f\u51e4\u5ae3\u7ea2\u5ef6\u8bef\u9762\u89c1\u6559\u4e3b\u7684\u65f6\u95f4\uff0c\u53ea\u597d \u63a5\u53d7\u5bb5\u5173\u7684\u5348\u5bb4\u6b3e\u5f85\u3002 \u3000\u3000\u5e2d\u4e0a\u56e0\u4e3a\u4eba\u591a\uff0c\u51e4\u5ae3\u7ea2\u9664\u4e86\u5bf9\u82b1\u7389\u9e9f\u7a0d\u732e\u6bb7\u52e4\uff0c\u5e76\u4e0d\u6562\u8868\u73b0\u5f97\u592a\u8fc7\u706b\u3002 \u3000\u3000\u5012\u662f\u8881\u5c0f\u9e64\u6700\u4e0d\u81ea\u5728\uff0c\u60f3\u8d77\u4e0a\u6b21\u591c\u95f4\u7684\u4e8b\uff0c\u5982\u4eca\u9762\u5bf9\u51e4\u5ae3\u7ea2\uff0c\u7b80\u76f4\u4f7f\u4ed6\u8fde\u5934\u4e5f\u4e0d\u6562\u62ac\u3002 \u3000\u3000\u4f46\u4e01\u6d9b\u548c\u7518\u9716\u5374\u662f\u5174\u81f4\u52c3\u52c3\uff0c\u4ed6\u4eec\u8fb9\u5403\u8fb9\u5077\u77a7\u51e4\u5ae3\u7ea2\u7684\u64a9\u4eba\u59ff\u8272\uff0c\u4e0d\u559d\u9152\u4e5f\u591f\u9189\u5f97\u5dee\u4e0d \u591a\u4e86\u3002 \u3000\u3000\u9152\u7b75\u8fc7\u540e\uff0c\u82b1\u7389\u9e9f\u5373\u7387\u8881\u5c0f\u9e64\u7b49\u4eba\u79bb\u5f00\u4e86\u5bb5\u5173\u3002 \u3000\u3000\u9001\u8d70\u82b1\u7389\u9e9f\uff0c\u51e4\u5ae3\u7ea2\u8111\u5b50\u91cc\u4f9d\u7136\u8fd8\u8426\u7ed5\u7740\u82b1\u7389\u9e9f\u7684\u5f71\u5b50\u3002 \u3000\u3000\u5979\u5e76\u672a\u611f\u5230\u5931\u671b\uff0c\u53ea\u662f\u7a0d\u6709\u4e9b\u60c6\u6005\u800c\u5df2\uff0c\u5979\u76f8\u4fe1\u53ea\u8981\u7ed9\u5979\u65f6\u95f4\uff0c\u4e00\u5b9a\u53ef\u4ee5\u5f97\u507f\u5fc3\u613f\u3002 \u3000\u3000\u73b0\u5728\u6ca1\u522b\u7684\uff0c\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u4fbf\u661f\u5230\u603b\u575b\u9762\u89c1\u6559\u4e3b\u4e86\u3002\u5979\u7a0d\u505a\u4f11\u606f\uff0c\u4fbf\u5f00\u59cb\u52a8\u8eab\u3002 \u3000\u3000\u521a\u521a\u8d70\u5230\u62db\u8d24\u9986\u9644\u8fd1\u7684\u5c0f\u8def\u4e0a\uff0c\u8fce\u9762\u6765\u4e86\u4e00\u4eba\uff0c\u8d6b\u7136\u662f\u5cb3\u5c0f\u98de\u3002 \u3000\u3000\u5979\u672c\u6765\u5947\u602a\u82b1\u7389\u9e9f\u4e0a\u5348\u5230\u5bb5\u5173\u4e3a\u4ec0\u4e48\u6ca1\u5e26\u5cb3\u5c0f\u98de\uff0c\u73b0\u5728\u624d\u660e\u767d\u539f\u6765\u4ed6\u662f\u6d3e\u5230\u5916\u9762\u516c\u5e72\u53bb \u4e86\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u672c\u60f3\u8eb2\u8fc7\u51e4\u5ae3\u7ea2\uff0c\u4f46\u773c\u524d\u5c31\u8fd9\u4e48\u4e00\u6761\u8def\uff0c\u53ea\u6709\u786c\u7740\u5934\u76ae\u548c\u5979\u89c1\u9762\u4e86\u3002 \u3000\u3000\u4ed6\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u51e4\u7edf\u9886\u53ef\u662f\u8981\u5230\u603b\u575b\u53bb\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u4ee5\u4e3a\u57ce\u4e3b\u4e25\u5bd2\u65e9\u5728\u4e0a\u5348\u4fbf\u5148\u89c1\u8fc7\u4e86\u57ce\u4e3b\uff0c\u6839\u672c\u4e0d\u6e05\u695a\u4e25\u5bd2\u4e0d\u4f46\u6ca1\u53bb\u603b\u575b\uff0c\u53cd\u800c \u628a\u9a6c\u662d\u96c4\u540a\u8d77\u6765\u6bd2\u6253\u4e00\u987f\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u4ed6\u5bf9\u6b64\u523b\u51e4\u5ae3\u7ea2\u8981\u5230\u603b\u575b\u5bc6\u544a\u4e25\u5bd2\uff0c\u5df2\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u5e76\u672a\u56de\u7b54\uff0c\u5374\u53c8\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u662f\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u5566\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5f53\u7136\u4e0d\u80fd\u544a\u8bc9\u5bf9\u65b9\u5230\u80b2\u5316\u57ce\u4e4b\u4e8b\uff0c\u987a\u53e3\u9053\uff1a\u201c\u665a\u8f88\u5949\u547d\u5230\u603b\u575b\u529e\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u5949\u8c01\u4e4b\u547d\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u4eba\u5728\u4e94\u5173\u603b\u9547\u5e9c\uff0c\u81ea\u7136\u662f\u5949\u526f\u603b\u9547\u4e4b\u547d\u3002\u201d \u3000\u3000\u201c\u5230\u603b\u575b\u53bb\u505a\u4ec0\u4e48\uff1f\u53ef\u89c1\u8fc7\u6559\u4e3b\uff1f\u201d \u3000\u3000\u201c\u4e00\u70b9\u5c0f\u4e8b\uff0c\u4ee5\u665a\u8f88\u8fd9\u79cd\u8eab\u4ef7\uff0c\u90a3\u91cc\u6709\u8d44\u683c\u89c1\u6559\u4e3b\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u5bf9\u5cb3\u5c0f\u98de\uff0c\u7167\u6837\u4e5f\u5b58\u6709\u975e\u5206\u4e4b\u60f3\uff0c\u53ea\u53ef\u60dc\u4ed6\u5e74\u7eaa\u592a\u5c0f\uff0c\u8fd8\u4e0d\u4fbf\u52a8\u4ee5\u7537\u5973\u4e4b\u60c5\uff0c\u5728 \u5979\u5fc3\u76ee\u4e2d\uff0c\u82e5\u518d\u8fc7\u51e0\u5e74\uff0c\u7b80\u76f4\u4f1a\u662f\u7b2c\u4e8c\u4e2a\u82b1\u7389\u9e9f\uff0c\u6bd4\u9a6c\u662d\u96c4\u771f\u4e0d\u77e5\u8981\u80dc\u8fc7\u591a\u5c11\u3002 \u3000\u3000\u5979\u53cc\u7738\u7d27\u76ef\u7740\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u662f\u8981\u5230\u603b\u575b\u7684\uff0c\u4f60\u53ef\u5426\u966a\u6211\u4e00\u8d9f\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u51e4\u7edf\u9886\u81ea\u5df1\u53bb\u4e0d\u662f\u5f88\u597d\u5417\uff0c\u4e3a\u4ec0\u4e48\u8981\u4eba\u966a\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u8d70\u8def\u5f88\u5bc2\u5bde\uff0c\u6709\u4f60\u966a\u7740\uff0c\u54b1\u4eec\u2014\u8def\u4e0a\u53ef\u4ee5\u8bf4\u8bf4\u8bdd\u513f\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e3a\u4e86\u80fd\u4fa6\u5bdf\u5979\u7684\u884c\u52a8\uff0c\u53c8\u53ef\u505a\u4e2a\u987a\u6c34\u4eba\u60c5\uff0c\u6545\u610f\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u518d\u9053\uff1a\u201c\u5982\u679c\u4eca\u665a \u8d76\u4e0d\u56de\u6765\uff0c\u665a\u8f88\u5230\u4e86\u603b\u575b\uff0c\u98df\u5bbf\u90fd\u4e0d\u65b9\u4fbf\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u7b11\u9053\uff1a\u201c\u8ddf\u7740\u6211\u8d70\u90a3\u8ba9\u4f60\u6ca1\u996d\u5403\uff0c\u53ea\u8981\u6211\u6709\u5730\u65b9\u4f4f\uff0c\u4f60\u5c31\u6709\u5730\u65b9\u4f4f\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u665a\u8f88\u5c31\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u5185\u5fc3\u6697\u559c\u3002 \u3000\u3000\u5979\u8fb9\u8d70\u8fb9\u642d\u8baa\u7740\u9053\uff1a\u201c\u4eca\u5929\u4e0a\u5348\u82b1\u526f\u603b\u9547\u5230\u5bb5\u5173\u89c6\u5bdf\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u665a\u8f88\u6628\u665a\u5c31\u542c\u5230\u6709\u4eba\u8bf4\u8fc7\uff0c\u5982\u679c\u4e0d\u662f\u4eca\u5929\u6709\u4e8b\uff0c\u4e5f\u8bb8\u4f1a\u968f\u526f\u603b\u9547\u5230\u8d35\u5173 \u53bb\u3002\u201d \u3000\u3000\u201c\u53ef\u4e0d\u662f\u5417\uff0c\u5c0f\u9e64\u548c\u4e24\u4f4d\u526f\u4f7f\u8005\u90fd\u53bb\u4e86\uff0c\u6211\u8fd8\u7279\u522b\u6d3e\u4e86\u4e2a\u5973\u5175\u4eea\u961f\u8fce\u63a5\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u5973\u5175\u4eea\u961f\uff1f\u201d \u3000\u3000\u201c\u5bb5\u5173\u56e0\u4e3a\u4efb\u52a1\u7279\u6b8a\uff0c\u6240\u4ee5\u5973\u5175\u5f88\u591a\uff0c\u5979\u4eec\u4e00\u4e2a\u4e2a\u90fd\u5f88\u6f02\u4eae\uff0c\u5c0f\u98de\uff0c\u4f60\u60f3\u4e0d\u60f3\u4ea4\u5973\u670b \u53cb\uff1f\u201d \u3000\u3000\u201c\u665a\u8f88\u53ea\u60f3\u6548\u5fe0\u6559\u4e3b\uff0c\u5e74\u7eaa\u5c0f\uff0c\u4ea4\u4ec0\u4e48\u5973\u670b\u53cb\u5462\u3002\u201d \u3000\u3000\u201c\u6548\u5fe0\u6559\u4e3b\u548c\u4ea4\u5973\u670b\u53cb\u6839\u672c\u662f\u4e24\u56de\u4e8b\uff0c\u4f60\u867d\u7136\u5e74\u7eaa\u8f7b\uff0c\u5bf9\u5973\u4eba\u5fc5\u5b9a\u4e5f\u4f1a\u60f3\uff0c\u82e5\u60f3\u4ea4\u5973\u670b \u53cb\uff0c\u5c31\u8be5\u5e38\u5230\u6211\u90a3\u91cc\u53bb\uff0c\u6211\u4e00\u5b9a\u4f1a\u627e\u4e2a\u4ee4\u4f60\u6700\u6ee1\u610f\u7684\u7ed9\u4f60\u3002\u201d \u3000\u3000\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u8fd8\u6709\u5c0f\u9e64\u548c\u4e24\u4f4d\u526f\u4f7f\u8005\uff0c\u4e5f\u53ef\u8f6c\u544a\u4ed6\u4eec\u5e38\u5230\u6211\u90a3\u91cc\u53bb\uff0c\u53ea\u8981\u4ed6\u4eec\u53bb \u627e\u6211\uff0c\u53ea\u6709\u597d\u5904\uff0c\u51b3\u4e0d\u4f1a\u5403\u4e8f\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6697\u81ea\u9a82\u9053\uff1a\u201c\u8d31\u5973\u4eba\uff0c\u7adf\u8fde\u4e01\u6d9b\u548c\u7518\u9716\u90a3\u79cd\u7c97\u4eba\u90fd\u4e0d\u60f3\u653e\u8fc7\uff01\u201d \u3000\u3000\u4ed6\u5b9e\u5728\u4e0d\u613f\u518d\u542c\u5bf9\u65b9\u7684\u65e0\u804a\u8bdd\uff0c\u7d22\u6027\u4e0d\u4e3b\u52a8\u5f00\u53e3\u3002 \u3000\u3000\u5230\u4e86\u603b\u575b\uff0c\u5cb3\u5c0f\u98de\u672c\u60f3\u4ecd\u80fd\u968f\u51e4\u5ae3\u7ea2\u884c\u52a8\uff0c\u4ee5\u4fbf\u76d1\u89c6\u5979\u505a\u4e9b\u4ec0\u4e48\uff0c\u4f46\u51e4\u5ae3\u7ea2\u5374\u628a\u4ed6\u5b89\u7f6e \u5728\u603b\u575b\u5916\u56f4\u4e00\u5904\u5c0f\u697c\u91cc\u3002 \u3000\u3000\u8fd9\u5904\u5c0f\u697c\u53ea\u6709\u4e00\u540d\u8001\u4eba\u8d1f\u8d23\u770b\u5b88\uff0c\u663e\u7136\u51e4\u5ae3\u7ea2\u5bf9\u8fd9\u91cc\u5f88\u70ed\uff0c\u5c31\u50cf\u81ea\u5df1\u7684\u884c\u9986\u4e00\u6837\uff0c\u53ef\u89c1 \u5979\u5728\u603b\u575b\u5f88\u515c\u5f97\u5f00\u3002 \u3000\u3000\u5979\u4ea4\u4ee3\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4f60\u5c31\u5728\u8fd9\u91cc\u7b49\u6211\uff0c\u5343\u4e07\u522b\u79c1\u81ea\u8dd1\u5230\u5916\u9762\u53bb\uff0c\u6709\u4ec0\u4e48\u9700\u8981\uff0c\u53ea\u7ba1\u627e\u90a3 \u540d\u8001\u4ec6\u4eba\uff0c\u90a3\u8005\u4ec6\u4eba\u540d\u53eb\u9ad8\u6765\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u51e4\u7edf\u9886\u9884\u8ba1\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u56de\u6765\u5462\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u4e0d\u4e00\u4f1a\uff0c\u5982\u679c\u6211\u56de\u6765\u665a\u4e86\uff0c\u5403\u4f4f\u65b9\u9762\uff0c\u9ad8\u6765\u81ea\u4f1a\u66ff\u4f60\u6599\u7406\u3002\u201d \u3000\u3000\u5979\u79bb\u5f00\u90a3\u5ea7\u5c0f\u697c\uff0c\u4e0d\u7ecf\u5927\u6bbf\uff0c\u76f4\u8d8b\u6559\u4e3b\u5185\u5bab\u3002 \u3000\u3000\u7ecf\u8fc7\u901a\u62a5\u4e4b\u540e\uff0c\u4f20\u8c15\u51fa\u6765\uff1a\u6559\u4e3b\u5c31\u5728\u5185\u5bab\u63a5\u89c1\u3002 \u3000\u3000\u5185\u5bab\u8b66\u536b\u68ee\u4e25\uff0c\u8fde\u51e4\u5ae3\u7ea2\u8fd9\u79cd\u6559\u4e3b\u7684\u5fc3\u8179\u4eba\uff0c\u4e5f\u662f\u4e00\u8def\u4f4e\u7740\u5934\u8fdb\u53bb\u7684\u3002 \u3000\u3000\u7687\u5143\u6559\u4e3b\u5750\u5728\u7ee3\u6709\u65e5\u6708\u9f99\u98ce\u9526\u62ab\u7684\u5b9d\u5ea7\u4e0a\uff0c\u8eab\u540e\u7ad9\u7740\u4e24\u540d\u7709\u76ee\u4fcf\u4e3d\u7684\u5f69\u8863\u5c11\u5973\u3002 \u3000\u3000\u539f\u6765\u7687\u5143\u6559\u4e3b\u662f\u4e00\u4f4d\u5973\u7684\u3002 \u3000\u3000\u5979\u770b\u4e0a\u53bb\u53ea\u6709\u56db\u5341\u5c81\u5de6\u53f3\uff0c\u9762\u76ee\u59e3\u597d\uff0c\u98ce\u534e\u7edd\u4ee3\uff0c\u795e\u91c7\u4e2d\u5374\u53c8\u9690\u73b0\u7740\u4e00\u80a1\u903c\u4eba\u7684\u5a01\u4eea\u3002 \u3000\u3000\u4ee4\u4eba\u671b\u800c\u751f\u754f\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u4e0d\u6562\u4ef0\u89c6\uff0c\u8d8b\u524d\u51e0\u6b65\uff0c\u76c8\u76c8\u62dc\u4e86\u4e0b\u53bb\u9053\uff1a\u201c\u5c5e\u4e0b\u5bb5\u5173\u7edf\u9886\u51e4\u5ae3\u7ea2\u53c2\u89c1\u6559\u4e3b\uff0c\u671b\u6559 \u4e3b\u4e07\u5c81\uff0c\u4e07\u4e07\u5c81\uff01\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u4e0d\u89c1\u4e1d\u6beb\u8868\u60c5\uff0c\u5f00\u53e3\u9053\uff1a\u201c\u8d77\u6765\u5427\uff0c\u6709\u8bdd\u7ad9\u7740\u8bb2\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u7ad9\u8d77\u8eab\u6765\uff0c\u89c6\u7ebf\u63a0\u8fc7\u7687\u5143\u6559\u4e3b\u8eab\u540e\u4e24\u540d\u5c11\u5973\uff0c\u5374\u53c8\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u7687\u5143\u6559\u4e3b\u9053\uff1a\u201c\u5979\u4eec\u4e24\u4e2a\u90fd\u662f\u670d\u4f8d\u6211\u591a\u5e74\u7684\u4eba\uff0c\u7528\u4e0d\u7740\u907f\u5f00\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u5438\u4e86\u4e00\u53e3\u6c14\u624d\u9053\uff1a\u201c\u4e09\u5e74\u524d\u6709\u4e00\u540d\u95ef\u8fdb\u5929\u8c37\u56fe\u8c0b\u4e0d\u8f68\u540d\u53eb\u4f55\u6167\u4ed9\u7684\u5973\u5b50\uff0c\u88ab\u6559\u4e3b \u4eb2\u81ea\u4e0b\u8c15\u6253\u5165\u80b2\u5316\u57ce\u7075\u5802\u4e3a\u5974\uff0c\u800c\u4e14\u82e5\u65e0\u6559\u4e3b\u624b\u4ee4\u4e0d\u5f97\u5f00\u91ca\uff0c\u8fd9\u4e8b\u6559\u4e3b\u8fd8\u8bb0\u5f97\u5417\uff1f\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u5f53\u7136\u8bb0\u5f97\uff0c\u8fd9\u5973\u5b50\u8fd8\u662f\u7531\u4f60\u5bc6\u544a\u7684\uff0c\u73b0\u5728\u4e3a\u4ec0\u4e48\u53c8\u63d0\u8d77\u5979\u6765\uff1f\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u5f97\u5230\u5bc6\u62a5\uff0c\u76ee\u524d\u8fd9\u5973\u4eba\u5df2\u88ab\u80b2\u5316\u57ce\u4e25\u57ce\u4e3b\u64c5\u81ea\u5f00\u91ca\uff0c\u8d1f\u8d23\u5b88\u62a4\u7075\u5802\u7684\u9a6c\u9999\u4e3b\u804c\u8d23 \u6240\u5728\uff0c\u53ea\u4ed6\u5411\u4e25\u57ce\u4e3b\u7980\u62a5\u90a3\u5973\u4eba\u662f\u6559\u4e3b\u4eb2\u81ea\u4e0b\u8c15\u6253\u5165\u7075\u5802\u7684\u7ecf\u8fc7\uff0c\u5c82\u77e5\u4e25\u57ce\u4e3b\u4e0d\u4f46\u4e0d\u80af\u628a\u90a3 \u5973\u4eba\u4ea4\u56de\u7075\u5802\uff0c\u53cd\u800c\u628a\u9a6c\u9999\u4e3b\u75db\u8d23\u4e86\u4e00\u987f\u3002\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u795e\u8272\u5fae\u53d8\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\u5427\uff0c\u4e25\u57ce\u4e3b\u662f\u6211\u7684\u5927\u5e08\u5144\uff0c\u600e\u4f1a\u8ddf\u6211\u8fc7\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4e25\u57ce\u4e3b\u662f\u6559\u4e3b\u7684\u5927\u5e08\u5144\uff0c\u6240\u4ee5\u4ed6\u624d\u6562\u8fd9\u6837\u505a\uff0c\u800c\u4e14\u4ed6\u5f53\u65f6\u7684\u8bed\u6c14\uff0c \u4f3c\u4e4e\u6839\u672c\u8bbe\u628a\u4f60\u8001\u4eba\u5bb6\u653e\u5728\u773c\u91cc\u3002\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u795e\u8272\u53c8\u662f\u4e00\u53d8\u9053\uff1a\u201c\u51e4\u7edf\u9886\uff0c\u672c\u6559\u4e3b\u548c\u4e25\u57ce\u4e3b\u7684\u5173\u7cfb\u975e\u6bd4\u5bfb\u5e38\uff0c\u4f60\u8fd9\u8bdd\u82e5\u6709\u534a \u70b9\u4e0d\u5b9e\uff0c\u5c0f\u5fc3\u6211\u5148\u62ff\u4f60\u6cbb\u7f6a\uff01\u201d \u3000\u3000\u201c\u8fd9\u6837\u7684\u5927\u4e8b\uff0c\u5c5e\u4e0b\u600e\u6562\u4fe1\u53e3\u5f00\u6cb3\uff0c\u82e5\u5f53\u771f\u6240\u62a5\u4e0d\u5b9e\uff0c\u5373\u4f7f\u88ab\u60a8\u8001\u4eba\u5bb6\u5904\u6b7b\uff0c\u4e5f\u662f\u7f6a\u6709 \u5e94\u5f97\uff0c\u7edd\u65e0\u534a\u53e5\u6028\u8a00\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e8b\u4f60\u662f\u542c\u8c01\u8bf4\u7684\uff1f\u201d \u3000\u3000\u80b2\u5316\u57ce\u90a3\u8fb9\uff0c\u4e5f\u6709\u5c5e\u4e0b\u7684\u719f\u4eba\uff0c\u6559\u4e3b\u53ea\u8981\u628a\u9a6c\u9999\u4e3b\u53eb\u6765\uff0c\u4e00\u95ee\u4fbf\u77e5\u3002\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u4e3a\u4e86\u67e5\u8bc1\uff0c\u968f\u5373\u547d\u4eba\u901f\u5230\u80b2\u5316\u57ce\u4f20\u8c15\u9a6c\u662d\u96c4\u6765\u89c1\u3002 \u3000\u3000\u4e00\u9762\u5429\u5490\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u4f60\u5148\u9000\u4e0b\u4f11\u606f\uff0c\u7b49\u9a6c\u9999\u4e3b\u5230\u6765\u65f6\uff0c\u518d\u4e00\u5e76\u6765\u89c1\u3002\u201d \u3000\u3000\u90a3\u8d1f\u8d23\u4f20\u8c15\u7684\u662f\u4e00\u540d\u62a4\u6cd5\u3002 \u3000\u3000\u5927\u7ea6\u4e00\u4e2a\u65f6\u8fb0\u540e\uff0c\u90a3\u62a4\u6cd5\u795e\u8272\u614c\u5f20\u7684\u56de\u6765\u4e86\u3002 \u3000\u3000\u7687\u5143\u6559\u4e3b\u5347\u5ea7\u95ee\u9053\uff1a\u201c\u9a6c\u9999\u4e3b\u4e3a\u4ec0\u4e48\u6ca1\u6765\uff1f\u201d \u3000\u3000\u90a3\u62a4\u6cd5\u8eac\u8eab\u9053\uff1a\u201c\u7980\u6559\u4e3b\uff0c\u9a6c\u9999\u4e3b\u5df2\u88ab\u4e25\u57ce\u4e3b\u6263\u62bc\u8d77\u6765\uff0c\u800c\u4e14\u662f\u540a\u5728\u6881\u4e0a\uff0c\u88ab\u6253\u5f97\u904d\u4f53 \u9cde\u4f24\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u7687\u5143\u6559\u4e3b\u4e0d\u4fe1\u4e5f\u5f97\u4fe1\u4e86\u3002 \u3000\u3000\u5979\u4e0d\u52a8\u58f0\u8272\u95ee\u9053\uff1a\u201c\u53ef\u95ee\u8fc7\u4e25\u57ce\u4e3b\u4e3a\u4ec0\u4e48\u8981\u6263\u62bc\u9a6c\u9999\u4e3b\uff1f\u201d \u3000\u3000\u90a3\u62a4\u6cd5\u9053\uff1a\u201c\u4e25\u57ce\u4e3b\u4e0d\u80af\u8bb2\uff0c\u770b\u6837\u5b50\u4ed6\u7684\u706b\u6c14\u5f88\u5927\uff0c\u5c5e\u4e0b\u867d\u7136\u662f\u5949\u8c15\u524d\u53bb\uff0c\u4ed6\u5374\u4e00\u526f\u4f3c \u7406\u4e0d\u7406\u7684\u6837\u5b50\u3002\u201d \u3000\u3000\u201c\u4f60\u53ef\u4eb2\u773c\u89c1\u8fc7\u9a6c\u662d\u96c4\uff1f\u201d \u3000\u3000\u201c\u4e25\u57ce\u4e3b\u4e0d\u51c6\u89c1\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4f60\u600e\u77e5\u9a6c\u9999\u4e3b\u88ab\u540a\u5728\u6881\u4e0a\u6253\u5f97\u904d\u4f53\u9cde\u4f24\uff1f\u201d \u3000\u3000\u201c\u662f\u80b2\u5316\u57ce\u603b\u7ba1\u5468\u6d77\u5c71\u8bb2\u7684\uff0c\u8fde\u5468\u6d77\u5c71\u4e5f\u4e0d\u51c6\u5c5e\u4e0b\u8fdb\u53bb\u770b\u3002\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u9ed8\u7136\u8bb8\u4e45\uff0c\u624d\u518d\u8bf4\u9053\uff1a\u201c\u90a3\u5c31\u7531\u4f60\u518d\u5230\u80b2\u5316\u57ce\u4e00\u6b21\uff0c\u53bb\u628a\u4e25\u57ce\u4e3b\u8bf7\u6765\uff01\u201d \u3000\u3000\u90a3\u62a4\u6cd5\u9762\u6709\u96be\u8272\u9053\uff1a\u201c\u4e25\u57ce\u4e3b\u706b\u6c14\u7684\u786e\u5f88\u5927\uff0c\u5c5e\u4e0b\u53ea\u6015\u8bf7\u4e0d\u52a8\u4ed6\u3002\u201d \u3000\u3000\u201c\u8fd9\u662f\u672c\u6559\u4e3b\u7684\u4ee4\u8c15\uff0c\u5e76\u975e\u4f60\u79c1\u4eba\u8bf7\u4ed6\uff0c\u96be\u9053\u4ed6\u8fde\u672c\u6559\u4e3b\u7684\u4ee4\u8c15\u4e5f\u4e0d\u9075\u5b88\uff0c\u5feb\u53bb\uff01\u201d \u3000\u3000\u90a3\u62a4\u6cd5\u8eac\u8eab\u800c\u9000\u3002 \u3000\u3000\u80b2\u5316\u57ce\u4e3b\u5230\u8fbe\u603b\u575b\u65f6\uff0c\u5df2\u662f\u8d77\u66f4\u65f6\u5206\u3002 \u3000\u3000\u8fd9\u65f6\u51e4\u5ae3\u7ea2\u5df2\u5148\u5b88\u5728\u7687\u5143\u6559\u4e3b\u8eab\u8fb9\uff0c\u540c\u65f6\u4e5f\u77e5\u9053\u4e86\u9a6c\u662d\u96c4\u88ab\u540a\u6bd2\u6253\u4e4b\u4e8b\u3002 \u3000\u3000\u9a6c\u662d\u96c4\u7684\u88ab\u540a\u6bd2\u6253\uff0c\u51e4\u5ae3\u7ea2\u5185\u5fc3\u53cd\u800c\u500d\u611f\u9ad8\u5174\u3002\u56e0\u4e3a\u8fd9\u6837\u4e00\u6765\uff0c\u4e25\u5bd2\u7684\u7f6a\u540d\u8d8a\u53d1\u5927\u4e86\uff0c \u81ea\u7136\u4e5f\u8d8a\u53d1\u5bb9\u6613\u544a\u5012\u4ed6\u3002 \u3000\u3000\u4e25\u5bd2\u8fdb\u6765\u4e4b\u540e\uff0c\u5bf9\u7687\u5143\u6559\u4e3b\u53ea\u662f\u62f1\u4e86\u62f1\u624b\u3002 \u3000\u3000\u7687\u5143\u6559\u4e3b\u867d\u672a\u4e0b\u4f4d\u8fce\u63a5\uff0c\u5374\u4e5f\u8fde\u5fd9\u6b20\u8eab\u7b54\u793c\u3002 \u3000\u3000\u4ed6\u4eec\u662f\u5e08\u5144\u59b9\uff0c\u5173\u7cfb\u53ef\u8c13\u4e0e\u4f17\u4e0d\u540c\u3002 \u3000\u3000\u53ea\u542c\u7687\u5143\u6559\u4e3b\u9053\uff1a\u201c\u5927\u5e08\u5144\u8bf7\u5750\uff01\u201d \u3000\u3000\u4e25\u5bd2\u968f\u5373\u5728\u4e00\u65c1\u5750\u4e0b\uff0c\u4f46\u5374\u671b\u7740\u7ad9\u5728\u4e00\u65c1\u7684\u51e4\u5ae3\u7ea2\u6012\u76ee\u800c\u89c6\u3002\u51e4\u5ae3\u7ea2\u88ab\u770b\u5f97\u4e00\u9897\u5fc3\u6026\u6026 \u76f4\u8df3\uff0c\u6025\u6025\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u4e25\u5bd2\u51b7\u51b7\u95ee\u9053\uff1a\u201c\u6559\u4e3b\u628a\u4e25\u67d0\u53eb\u6765\uff0c\u4e0d\u77e5\u6709\u4ec0\u4e48\u793a\u4e0b\uff1f\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u8bed\u6c14\u663e\u5f97\u5341\u5206\u5e73\u548c\uff0c\u9053\uff1a\u201c\u65b9\u624d\u5c0f\u59b9\u6d3e\u8d75\u62a4\u6cd5\u5230\u80b2\u5316\u57ce\u4f20\u89c1\u9a6c\u9999\u4e3b\uff0c\u542c\u8bf4\u9a6c\u9999 \u4e3b\u88ab\u5e08\u5144\u6263\u62bc\u8d77\u6765\uff0c\u800c\u4e14\u8fd8\u540a\u5728\u6881\u4e0a\u53d7\u5211\uff0c\u53ef\u6709\u8fd9\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9894\u9996\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6709\u8fd9\u4e48\u4e00\u56de\u4e8b\u3002\u201d \u3000\u3000\u201c\u9a6c\u9999\u4e3b\u72af\u4e86\u4ec0\u4e48\u57ce\u89c4\uff1f\u5927\u5e08\u5144\u53ef\u5426\u544a\u77e5\u5c0f\u59b9\uff1f\u201d \u3000\u3000\u201c\u4ed6\u64c5\u81ea\u6e9c\u51fa\u80b2\u5316\u57ce\uff0c\u4e00\u53bb\u5927\u534a\u5929\uff0c\u76f4\u5230\u6df1\u591c\u624d\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u5927\u5e08\u5144\u53ef\u77e5\u9053\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u53bb\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u84e6\u5730\u53cc\u76ee\u5706\u7741\uff0c\u624b\u6307\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u6559\u4e3b\u4e0d\u59a8\u95ee\u95ee\u8fd9\u5973\u4eba\uff01\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u8f6c\u5934\u671b\u5411\u51e4\u5ae3\u7ea2\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u8fde\u5fd9\u8dea\u5012\u5728\u5730\u9053\uff1a\u201c\u7980\u6559\u4e3b\uff0c\u5c5e\u4e0b\u4ec0\u4e48\u4e5f\u4e0d\u77e5\u9053\uff0c\u66f4\u4e0d\u660e\u767d\u4e25\u57ce\u4e3b\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u6837 \u8bf4\u8bdd\u3002\u201d \u3000\u3000\u4e25\u5bd2\u55d4\u76ee\u559d\u9053\uff1a\u201c\u4ed6\u5230\u5bb5\u5173\u548c\u4f60\u5e7d\u4f1a\uff0c\u4f60\u8fd9\u8d31\u5973\u4eba\u8fd8\u6562\u8bf4\u4e0d\u77e5\u9053\uff1f\u8001\u592b\u65e9\u5c31\u77e5\u9053\u4f60\u8fd9\u8d31 \u5973\u4eba\u4f5c\u8d3c\u5fc3\u865a\uff0c\u6076\u4eba\u5148\u544a\u72b6\uff0c\u7adf\u8981\u5728\u6559\u4e3b\u9762\u524d\u5bc6\u544a\u8001\u592b\uff0c\u7b80\u76f4\u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u53ea\u5413\u5f97\u80a1\u6447\u80c6\u6817\uff0c\u8fde\u8fde\u53e9\u5934\u9053\uff1a\u201c\u7980\u6559\u4e3b\uff0c\u4e25\u57ce\u4e3b\u8fd9\u4e9b\u8bdd\u5168\u662f\u51a4\u6789\u5c5e\u4e0b\u7684\uff0c\u5c5e\u4e0b \u4e0d\u77e5\u9053\u4ec0\u4e48\u4e8b\u5f97\u7f6a\u4e86\u4e25\u57ce\u4e3b\uff0c\u4ed6\u8001\u4eba\u5bb6\u7adf\u7136\u5728\u6559\u4e3b\u9762\u524d\u8bf4\u51fa\u8fd9\u79cd\u6ca1\u5f71\u513f\u7684\u8bdd\u6765\uff01\u201d \u3000\u3000\u53ea\u89c1\u4e25\u5bd2\u970d\u5730\u817e\u8eab\u800c\u8d77\uff0c\u98de\u8d77\u4e00\u811a\uff0c\u76f4\u5411\u51e4\u5ae3\u7ea2\u8e22\u53bb\u3002\u51e4\u5ae3\u7ea2\u4e00\u58f0\u60e8\u53eb\uff0c\u76f4\u8e22\u5230\u5899\u89d2\u8fb9\u3002 \u3000\u3000\u4e25\u5bd2\u5728\u7687\u5143\u6559\u4e3b\u5b9d\u5ea7\u524d\u5982\u6b64\u4e3e\u52a8\uff0c\u8fd9\u662f\u7687\u5143\u6559\u7acb\u6559\u4ee5\u6765\u4ece\u6ca1\u53d1\u751f\u8fc7\u7684\u4e8b\uff0c\u4ed6\u8e22\u7684\u662f\u51e4\u5ae3 \u7ea2\uff0c\u800c\u96be\u582a\u7684\u5374\u662f\u7687\u5143\u6559\u4e3b\u3002 \u3000\u3000\u7687\u5143\u6559\u4e3b\u7ec8\u4e8e\u53d8\u4e86\u8138\u8272\uff0c\u51b7\u7136\u5927\u58f0\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u6709\u8bdd\u597d\u597d\u8bb2\uff0c\u4f55\u5fc5\u5728\u5c0f\u59b9\u9762\u524d\u53d1\u8fd9\u4e48 \u5927\u7684\u7765\u6c14\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u8d31\u5973\u4eba\u592a\u53ef\u6076\u4e86\uff0c\u63d0\u8d77\u5979\u7684\u4e11\u95fb\uff0c\u4e25\u67d0\u771f\u662f\u8bf4\u4e0d\u51fa\u53e3\uff01\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u53ea\u597d\u6682\u4e14\u653e\u4e0b\u51e4\u5ae3\u7ea2\u7684\u4e8b\u4e0d\u8c08\uff0c\u8bed\u6c14\u518d\u8f6c\u4e3a\u7f13\u548c\u9053\uff1a \u3000\u3000\u201c\u5927\u5e08\u5144\u5230\u5e95\u51c6\u5907\u600e\u6837\u5904\u7f6e\u9a6c\u9999\u4e3b\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u5148\u540a\u4ed6\u4e09\u5929\u4e09\u591c\uff0c\u5982\u679c\u540a\u6b7b\u4e86\uff0c\u5c31\u8ba9\u4ed6\u6b7b\u53bb\u3002\u201d \u3000\u3000\u201c\u5927\u5e08\u5144\uff0c\u9a6c\u9999\u4e3b\u7eb5\u7136\u72af\u4e86\u57ce\u89c4\uff0c\u4e5f\u4e0d\u8fc7\u662f\u64c5\u81ea\u5916\u51fa\uff0c\u5c31\u662f\u5904\u5206\u4ed6\uff0c\u4e5f\u7528\u4e0d\u7740\u5904\u5206\u5f97\u8fd9 \u6837\u91cd\u3002\u201d \u3000\u3000\u201c\u4e25\u67d0\u65e9\u77e5\u9a6c\u662d\u96c4\u662f\u6559\u4e3b\u7684\u4eba\uff0c\u4e0d\u8fc7\u4ed6\u65e2\u7136\u88ab\u5206\u5728\u80b2\u5316\u57ce\u5f53\u5dee\uff0c\u4e25\u67d0\u5c31\u6709\u6743\u5904\u7f6e\u4ed6\uff01\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u77ac\u95f4\u8138\u8272\u53d8\u4e86\u51e0\u53d8\uff0c\u5f3a\u81ea\u6291\u5236\u7740\u6fc0\u52a8\u7684\u60c5\u7eea\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4e5f\u53ef\u4ee5\u6682\u65f6\u4e0d\u8c08\uff0c\u542c \u8bf4\u8d35\u57ce\u7075\u5802\u4e00\u4e2a\u53eb\u4f55\u6167\u4ed9\u7684\u5973\u4eba\uff0c\u5df2\u88ab\u5927\u5e08\u5144\u91ca\u653e\u4e86\uff0c\u53ef\u6709\u8fd9\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e5f\u6709\u8fd9\u56de\u4e8b\u3002\u201d \u3000\u3000\u201c\u5927\u5e08\u5144\u53ef\u77e5\u9053\u90a3\u5973\u4eba\u662f\u5f85\u7f6a\u4e4b\u8eab\uff0c\u5c0f\u59b9\u66fe\u4eb2\u4e0b\u624b\u8c15\u6253\u5165\u7075\u5802\u4e3a\u5974\uff0c\u82e5\u6ca1\u6709\u5c0f\u59b9\u4f20\u8c15\u8c01 \u4e5f\u4e0d\u80fd\u91ca\u653e\u7684\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u201c\u4e25\u67d0\u4e5f\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u201c\u5927\u5e08\u5144\u660e\u660e\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u628a\u5979\u91ca\u653e\u4e86\u5462\uff1f\u201d \u3000\u3000\u201c\u4e25\u67d0\u60f3\u5148\u95ee\u6559\u4e3b\u4e00\u53e5\u8bdd\uff0c\u90a3\u4e2a\u53eb\u4f55\u6167\u4ed9\u7684\u5973\u4eba\u7a76\u7adf\u72af\u4e86\u4ec0\u4e48\u6212\u5f8b\uff1f\u201d \u3000\u3000\u201c\u4e09\u5e74\u524d\u5979\u8fdb\u5165\u5929\u8c37\u7684\u7528\u5fc3\uff0c\u5c31\u662f\u56fe\u8c0b\u4e0d\u8f68\u3002\u201d \u3000\u3000\u201c\u6709\u4ec0\u4e48\u8bc1\u636e\uff1f\u201d \u3000\u3000\u201c\u662f\u51e4\u7edf\u9886\u5411\u5c0f\u59b9\u5bc6\u544a\u7684\uff0c\u51e4\u7edf\u9886\u624b\u8fb9\u5c31\u63e1\u6709\u5979\u7684\u8bc1\u636e\u3002\u201d \u3000\u3000\u4e25\u5bd2\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u6559\u4e3b\u4e5f\u662f\u76f8\u4fe1\u4e86\u5979\u7684\u8c17\u8a00\uff0c\u8fd9\u8d31\u5973\u4eba\u6839\u672c\u8bf4\u4e0d\u51fa\u4e00\u53e5\u4eba\u8bdd\uff0c\u6559\u4e3b\uff0c \u4e25\u67d0\u4e3a\u4e86\u7687\u5143\u6559\uff0c\u4e5f\u4e3a\u4e86\u4f60\uff0c\u4e0d\u5f97\u4e0d\u5949\u529d\u51e0\u53e5\u8bdd\uff0c\u4f46\u613f\u4f60\u80fd\u4eb2\u541b\u5b50\uff0c\u8fdc\u5c0f\u4eba\uff0c\u5fe0\u8a00\u9006\u8033\uff0c\u542c \u4e0d\u542c\u5c31\u5728\u4f60\u4e86\uff01\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u8138\u8272\u53d8\u5f97\u7ea2\u4e2d\u5e26\u7eff\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u591a\u627f\u5927\u5e08\u5144\u6559\u8bf2\uff0c\u5c0f\u59b9\u81ea\u4fe1\u8fd8\u4e0d\u662f\u4f60\u8bf4\u7684\u90a3 \u79cd\u4eba\uff01\u201d \u3000\u3000\u4e25\u5bd2\u970d\u7136\u800c\u8d77\uff0c\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u5982\u679c\u6559\u4e3b\u6ca1\u6709\u522b\u7684\u5429\u5490\uff0c\u4e25\u67d0\u5c31\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u9053\uff1a\u201c\u5927\u5e08\u5144\u8bf7\u7b49\u4e00\u7b49\uff0c\u5c0f\u59b9\u60f3\u518d\u95ee\u95ee\uff0c\u90a3\u53eb\u4f55\u6167\u4ed9\u7684\u5973\u4eba\uff0c\u76ee\u524d\u5728\u4ec0\u4e48\u5730 \u65b9\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u4e25\u67d0\u6682\u65f6\u628a\u5979\u8c03\u5230\u57ce\u5e9c\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u5427\uff0c\u8bf7\u5927\u5e08\u5144\u660e\u5929\u5c31\u6d3e\u4eba\u628a\u90a3\u5973\u4eba\u9001\u5230\u603b\u575b\u6765\u3002\u201d \u3000\u3000\u201c\u83ab\u975e\u6559\u4e3b\u51c6\u5907\u5904\u7f6e\u5979\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u5c0f\u59b9\u7684\u4e8b\uff0c\u5927\u5e08\u5144\u7528\u4e0d\u7740\u591a\u95ee\u3002\u201d \u3000\u3000\u201c\u6559\u4e3b\u7684\u4ee4\u8c15\uff0c\u4e25\u67d0\u4e0d\u6562\u4e0d\u9075\uff0c\u4e0d\u8fc7\u4e25\u67d0\u62c5\u5fc3\u5979\u5728\u8def\u4e0a\u9003\u8131\uff0c\u4e8b\u5173\u91cd\u5927\uff0c\u6240\u4ee5\u6559\u4e3b\u6700\u597d \u6d3e\u4e00\u4f4d\u9ad8\u624b\u5230\u80b2\u5316\u57ce\u628a\u5979\u62bc\u56de\u6765\u3002\u201d \u3000\u3000\u4e25\u67d0\u8bf4\u5b8c\u8bdd\uff0c\u6389\u5934\u800c\u53bb\u3002 \u3000\u3000\u4e25\u5bd2\u8d70\u540e\uff0c\u7687\u5143\u6559\u4e3b\u7684\u8138\u8272\uff0c\u7adf\u662f\u8d8a\u53d8\u8d8a\u51b7\uff0c\u8d8a\u53d8\u8d8a\u51dd\uff0c\u5750\u5728\u90a3\u91cc\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u8fd9\u65f6\u51e4\u5ae3\u7ea2\u867d\u5df2\u722c\u4e86\u8d77\u6765\uff0c\u5374\u53c8\u8d70\u5230\u7687\u5143\u6559\u4e3b\u5b9d\u5ea7\u524d\u8dea\u4e0b\uff0c\u63a9\u9762\u555c\u6ce3\u8d77\u6765\u3002 \u3000\u3000\u7687\u5143\u6559\u4e3b\u51b7\u58f0\u9053\uff1a\u201c\u4f60\u8fd8\u8dea\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u6cea\u6d41\u6ee1\u9762\uff0c\u545c\u54bd\u9053\uff1a\u201c\u5c5e\u4e0b\u6c42\u6559\u4e3b\u660e\u9274\uff0c\u65b9\u624d\u4e25\u57ce\u4e3b\u8bf4\u90a3\u4e00\u756a\u8bdd\uff0c\u5b8c\u6210\u662f\u65e0\u4e2d\u751f \u6709\uff0c\u5c5e\u4e0b\u771f\u4e0d\u6e05\u695a\u7a76\u7adf\u90a3\u4ef6\u4e8b\u5f97\u7f6a\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u9053\uff1a\u201c\u4ed6\u8bf4\u4ed6\u7684\uff0c\u6211\u660e\u767d\u5c31\u597d\uff0c\u96be\u9053\u4ed6\u6253\u4e86\u4f60\uff0c\u4f60\u8fd8\u60f3\u6253\u4ed6\u4e0d\u6210\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u6253\u4e86\u4e2a\u54c6\u55e6\u9053\uff1a\u201c\u5c5e\u4e0b\u600e\u6562\u5b58\u8fd9\u79cd\u5ff5\u5934\uff0c\u4e25\u57ce\u4e3b\u662f\u6559\u4e3b\u7684\u5927\u5e08\u5144\uff0c\u4ed6\u8fde\u6559\u4e3b\u90fd\u6562 \u6559\u8bad\uff0c\u8e22\u5c5e\u4e0b\u4e00\u811a\u7b97\u4ec0\u4e48\u3002\u201d \u3000\u3000\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u8981\u54ed\u54ed\u557c\u557c\u7684\uff0c\u8fd8\u8d56\u5728\u5730\u4e0a\u4e0d\u80af\u8d77\u6765\uff1f\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u89c9\u5f97\u88ab\u6253\u4e8b\u5c0f\uff0c\u5e73\u767d\u53d7\u8fb1\u4e8b\u5927\uff0c\u5973\u4eba\u6d3b\u5728\u4e16\u4e0a\uff0c\u6700\u91cd\u8981\u7684\u4fbf\u662f\u540d\u8282\uff0c\u4e25\u57ce\u4e3b\u7834\u574f \u4e86\u5c5e\u4e0b\u7684\u540d\u8282\uff0c\u5c5e\u4e0b\u8fd8\u6709\u4ec0\u4e48\u8138\u518d\u6d3b\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u201c\u8d77\u6765\uff0c\u522b\u8005\u8bf4\u50bb\u8bdd\uff0c\u4ed6\u8bf4\u7684\u662f\u4e00\u56de\u4e8b\uff0c\u6211\u76f8\u4e0d\u76f8\u4fe1\u53c8\u662f\u4e00\u56de\u4e8b\uff0c\u53ea\u8981\u4f60\u81ea\u5df1\u771f\u505a\u5f97\u6b63\uff0c \u4ed6\u7231\u600e\u4e48\u8bf4\u5c31\u600e\u4e48\u8bf4\uff0c\u4f55\u5fc5\u8ba1\u8f83\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u62ed\u53bb\u4e86\u6cea\u75d5\u722c\u8d77\u6765\u3002\u7a81\u542c\u95e8\u5916\u53d1\u51fa\u4e00\u9635\u563f\u563f\u51b7\u7b11\uff0c\u63a5\u7740\u73e0\u5e18\u4e00\u6380\uff0c\u8d70\u8fdb\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u8fd9\u4eba\u8eab\u7a7f\u4e00\u88ad\u5bbd\u4eba\u7684\u9ed1\u888d\uff0c\u5e74\u7eaa\u7ea6\u4e94\u65ec\u4ee5\u4e0a\uff0c\u80a4\u8272\u6709\u5982\u9ec4\u8721\uff0c\u4e00\u5f20\u7a84\u957f\u7684\u9a6c\u8138\uff0c\u516b\u5b57\u7709\uff0c \u5012\u4e09\u89d2\u773c\uff0c\u9e70\u94a9\u9f3b\uff0c\u8584\u5634\u5507\uff0c\u4e0b\u989a\u5c31\u50cf\u74e2\u628a\u5b50\u822c\uff0c\u7559\u7740\u4e24\u64de\u516b\u5b57\u80e1\uff0c\u5168\u8eab\u7626\u9aa8\u5d99\u5ccb\uff0c\u50cf\u5f31\u4e0d \u7981\u98ce\u6a21\u6837\uff0c\u4f46\u4e24\u773c\u5374\u7cbe\u8292\u6e5b\u6e5b\uff0c\u76f4\u80fd\u6151\u4eba\u9b42\u9b44\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4eba\u662f\u7687\u5143\u6559\u603b\u575b\u603b\u62a4\u6cd5\uff0c\u59d3\u79e6\u540d\u69d0\uff0c\u4e0d\u4f46\u4e00\u8eab\u6b66\u529f\u9ad8\u4e0d\u53ef\u6d4b\uff0c\u6587\u624d\u4e5f\u9887\u51fa\u4f17\uff0c\u7687 \u5143\u6559\u4e3b\u4e00\u5411\u5bf9\u4ed6\u8a00\u542c\u8ba1\u4ece\u3002 \u3000\u3000\u79e6\u69d0\u5728\u7687\u5143\u6559\u603b\u575b\uff0c\u9664\u6559\u4e3b\u5916\uff0c\u79f0\u5f97\u4e0a\u662f\u4ee5\u4ed6\u4e3a\u5c0a\u3002\u82e5\u8bba\u8eab\u4efd\u5730\u4f4d\uff0c\u4e0d\u5728\u57ce\u4e3b\u4e4b\u4e0b\uff0c\u4f46 \u56e0\u4e24\u4f4d\u57ce\u4e3b\u90fd\u662f\u6559\u4e3b\u7684\u5e08\u5144\uff0c\u4f7f\u5f97\u4ed6\u4e0d\u5f97\u4e0d\u5bf9\u4e24\u4f4d\u57ce\u4e3b\u6709\u6240\u987e\u5fcc\u800c\u5df2\u3002 \u3000\u3000\u7687\u5143\u6559\u4e3b\u5148\u884c\u5f00\u53e3\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u503c\u5f97\u603b\u62a4\u6cd5\u4e00\u8def\u51b7\u7b11\uff1f\u201d \u3000\u3000\u79e6\u69d0\u5411\u7687\u5143\u6559\u4e3b\u65bd\u4e86\u4e00\u793c\uff0c\u7136\u540e\u7ad9\u5728\u4e00\u65c1\u9053\uff0c\u201c\u521a\u624d\u7684\u4e8b\u8fd8\u4e0d\u503c\u5f97\u597d\u7b11\u5417\uff1f\u672c\u5ea7\u5b9e\u5728\u4f69 \u670d\u6559\u4e3b\u7684\u6db5\u517b\uff0c\u5c45\u7136\u80fd\u9006\u6765\u987a\u53d7\u628a\u8fd9\u53e3\u6c14\u5fcd\u4e0b\u4e86\uff01\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u6307\u4e86\u6307\u65b9\u624d\u4e25\u5bd2\u5750\u8fc7\u7684\u5ea7\u6905\u9053\uff1a\u201c\u603b\u62a4\u6cd5\u8bf7\u5750\uff0c\u521a\u624d\u53d1\u751f\u7684\u4e8b\u4f60\u90fd\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u79e6\u69d0\u6478\u7740\u516b\u5b57\u80e1\uff0c\u53c8\u563f\u563f\u7b11\u4e86\u8d77\u6765\uff1a\u201c\u65b9\u624d\u672c\u5ea7\u4ece\u7a97\u5916\u7ecf\u8fc7\uff0c\u6b63\u8d76\u4e0a\u4e25\u57ce\u4e3b\u811a\u8e22\u51e4\u7edf\u9886\uff0c \u91cc\u9762\u53d1\u751f\u4e86\u8fd9\u4e48\u5927\u7684\u4e8b\uff0c\u5f53\u7136\u4e0d\u80fd\u4e0d\u505c\u4e0b\u6765\u5077\u542c\u5077\u542c\u3002\u201d \u3000\u3000\u201c\u603b\u62a4\u6cd5\u5bf9\u8fd9\u4ef6\u4e8b\u6709\u4ec0\u4e48\u770b\u6cd5\uff1f\u201d \u3000\u3000\u201c\u672c\u5ea7\u7b97\u5f00\u4e86\u773c\u754c\uff0c\u4e5f\u5f00\u4e86\u8033\u754c\uff0c\u4e0d\u77e5\u54b1\u4eec\u7687\u5143\u6559\uff0c\u7a76\u7adf\u8fd8\u6709\u793c\u7edf\u6ca1\u6709\uff1f\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u5176\u5b9e\u6211\u65b9\u624d\u4f55\u5c1d\u4e0d\u607c\uff0c\u770b\u5728\u4ed6\u662f\u6211\u7684\u5927\u5e08\u5144\uff0c\u4e5f\u53ea\u6709\u5f3a\u5fcd\u4e0b\u4e00 \u53e3\u6c14\u4e86\u3002\u201d \u3000\u3000\u79e6\u69d0\u7728\u52a8\u7740\u5012\u4e09\u89d2\u773c\u9053\uff1a\u201c\u662f\u53ef\u5fcd\uff0c\u5b70\u4e0d\u53ef\u5fcd\uff0c\u6559\u4e3b\u7684\u8010\u5fc3\uff0c\u603b\u8be5\u6709\u4e2a\u9650\u5ea6\u3002\u201d \u3000\u3000\u201c\u603b\u62a4\u6cd5\u7684\u610f\u601d\uff0c\u51c6\u5907\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u4fd7\u8bf4\u758f\u4e0d\u95f4\u4eb2\uff0c\u6bd4\u8d77\u4e25\u57ce\u4e3b\uff0c\u672c\u5ea7\u548c\u6559\u4e3b\u7684\u5173\u7cfb\uff0c\u603b\u662f\u758f\u4e86\u4e00\u5c42\uff0c\u6240\u4ee5\u672c\u5ea7\u4e5f\u5b9e\u5728\u4e0d \u4fbf\u591a\u8bf4\u4ec0\u4e48\uff0c\u514d\u5f97\u4e24\u5934\u4e0d\u8ba8\u597d\uff0c\u7b49\u4e8e\u81ea\u5df1\u642c\u77f3\u5934\u7838\u81ea\u5df1\u7684\u811a\u3002\u201d \u3000\u3000\u201c\u603b\u62a4\u6cd5\u53ea\u7ba1\u8bf4\uff0c\u6211\u4ec0\u4e48\u65f6\u5019\u628a\u4f60\u770b\u6210\u5916\u4eba\u8fc7\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u8bf7\u6055\u672c\u5ea7\u76f4\u8a00\u4e86\uff0c\u6559\u4e3b\u4e0d\u4e45\u4e4b\u540e\u4fbf\u8981\u8fdc\u5f81\u4e2d\u539f\uff0c\u626b\u8361\u4e2d\u539f\u6b66\u6797\uff0c\u8981\u60f3\u65d7\u5f00\u5f97\u80dc\uff0c \u9996\u5148\u5fc5\u5f97\u53f7\u4ee4\u8d2f\u5f7b\uff0c\u90a3\u5c31\u662f\u8981\u5148\u9664\u53bb\u5185\u90e8\u7684\u969c\u788d\uff0c\u672c\u5ea7\u6597\u80c6\u8bf4\u53e5\u8bdd\uff0c\u4e25\u57ce\u4e3b\u6b63\u662f\u672c\u6559\u5185\u90e8\u7684 \u969c\u788d\u3002\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u8bdd\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u79e6\u69d0\u51b7\u7b11\u9053\uff1a\u201c\u672c\u5ea7\u5148\u8bf7\u95ee\u6559\u4e3b\uff0c\u4f60\u8001\u4eba\u5bb6\u662f\u5426\u6709\u628a\u63e1\u80fd\u53f7\u4ee4\u5f97\u52a8\u4ed6\uff1f\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u5bf9\u79e6\u69d0\u8fd9\u4e00\u95ee\uff0c\u4f3c\u662f\u5f88\u96be\u56de\u7b54\u3002 \u3000\u3000\u79e6\u69d0\u63a5\u4e0b\u53bb\u9053\uff1a\u201c\u6559\u4e3b\u65e2\u7136\u53f7\u4ee4\u4e0d\u52a8\u4ed6\uff0c\u5176\u4ed6\u4eba\u4e5f\u5f88\u53ef\u80fd\u7fa4\u8d77\u6548\u5c24\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u540e\u679c\u7684 \u4e25\u91cd\uff0c\u4e5f\u5c31\u53ef\u60f3\u800c\u77e5\u4e86\u3002\u201d \u3000\u3000\u201c\u5176\u4ed6\u7684\u4eba\u8c01\u6562\u7fa4\u8d77\u6548\u5c24\uff1f\u96be\u9053\u4ed6\u4eec\u5c31\u4e0d\u6015\u672c\u6559\u4e3b\u4ee5\u6559\u89c4\u6cbb\u4ed6\u4eec\u7684\u7f6a\uff1f\u201d \u3000\u3000\u201c\u90a3\u9664\u975e\u8fde\u4e25\u57ce\u4e3b\u4e00\u8d77\u6cbb\u7f6a\uff0c\u5426\u5219\u5c31\u8d4f\u7f5a\u4e0d\u516c\uff0c\u4f55\u4ee5\u670d\u4f17\uff1f\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u51dd\u7740\u8138\u8272\uff0c\u9677\u5165\u6c89\u601d\uff0c\u8bb8\u4e45\u4ee5\u540e\u624d\u9053\uff1a\u201c\u5176\u5b9e\u4e25\u57ce\u4e3b\u521a\u624d\u53ef\u80fd\u662f\u60c5\u7eea\u4e0d\u7a33\uff0c\u51b3 \u4e0d\u53ef\u80fd\u5bf9\u6211\u6709\u4e8c\u5fc3\u3002\u201d \u3000\u3000\u79e6\u69d0\u76ae\u7b11\u8089\u4e0d\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u4e00\u5b9a\uff0c\u660e\u5929\u7684\u4e8b\uff0c\u636e\u672c\u5ea7\u9884\u6599\uff0c\u4ed6\u5c31\u5f88\u53ef\u80fd\u7ed9\u4f60\u8001\u4eba\u5bb6\u4e00 \u70b9\u989c\u8272\u77a7\u77a7\uff01\u201d \u3000\u3000\u201c\u603b\u62a4\u6cd5\u6307\u7684\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u201c\u5c31\u662f\u90a3\u4e2a\u53eb\u4f55\u6167\u4ed9\u7684\u5973\u4eba\u7684\u4e8b\u3002\u201d \u3000\u3000\u201c\u4f60\u662f\u8bf4\u4ed6\u4e0d\u80af\u628a\u4eba\u4ea4\u51fa\u6765\uff1f\u201d \u3000\u3000\u201c\u82e5\u4ed6\u80af\u4ea4\u51fa\u4eba\u6765\uff0c\u672c\u5ea7\u521a\u624d\u90a3\u4e9b\u6d3b\u5c31\u7b97\u767d\u8bf4\u4e86\u3002\u201d \u3000\u3000\u201c\u771f\u4f1a\u8fd9\u6837\uff1f\u201d \u3000\u3000\u201c\u672c\u5ea7\u60c5\u613f\u628a\u4eba\u5934\u8d4c\u4e0a\u3002\u82e5\u4ed6\u80af\u4ea4\u51fa\u4eba\u6765\uff0c\u672c\u5ea7\u5c31\u81ea\u884c\u628a\u4eba\u5934\u5272\u4e0b\u6765\u5448\u5230\u6559\u4e3b\u9762\u524d\uff01\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u53cc\u773c\u62bd\u6410\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u603b\u62a4\u6cd5\u8a00\u91cd\u4e86\u3002\u4e07\u4e00\u4ed6\u4e0d\u4ea4\u4eba\uff0c\u53c8\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u79e6\u69d0\u9053\uff1a\u201c\u90a3\u5c31\u662f\u6559\u4e3b\u5fc5\u987b\u4e0b\u51b3\u5fc3\u7684\u65f6\u5019\u4e86\u3002\u201d \u3000\u3000\u79e6\u69d0\u518d\u4fa7\u8fc7\u8138\u6765\u9053\uff1a\u201c\u6559\u4e3b\uff0c\u8be5\u60a8\u62ff\u4e2a\u4e3b\u610f\u4e86\uff0c\u73b0\u5728\u7684\u95ee\u9898\uff0c \u3000\u3000\u91cd\u8981\u7684\u4e0d\u662f\u90a3\u540d\u5973\u72af\u80fd\u5426\u62bc\u56de\u6765\uff0c\u800c\u662f\u4e25\u57ce\u4e3b\u5df2\u6709\u4e8c\u5fc3\uff0c\u516c\u7136\u6297\u547d\uff0c\u4e3a\u4e86\u5927\u5c40\u7740\u60f3\uff0c\u6559 \u4e3b\u4f3c\u4e4e\u6ca1\u5fc5\u8981\u518d\u987e\u8651\u4ec0\u4e48\u60c5\u9762\u3002\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u6c89\u541f\u4e86\u534a\u664c\u9053\uff1a\u201c\u82e5\u73b0\u5728\u91c7\u53d6\u884c\u52a8\uff0c\u53ea\u6015\u592a\u65e9\u4e86\u4e9b\u3002\u201d \u3000\u3000\u79e6\u69d0\u76b1\u8d77\u516b\u5b57\u7709\uff0c\u772f\u8d77\u4e00\u5bf9\u5012\u4e09\u89d2\u773c\uff0c\u63a5\u7740\u9634\u6c89\u6c89\u4e00\u7b11\u9053\uff1a \u3000\u3000\u201c\u90a3\u5c31\u7531\u672c\u5ea7\u4eb2\u5230\u80b2\u6bd4\u57ce\u4e00\u8d9f\u3002\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u4ee5\u662f\u9887\u611f\u610f\u5916\uff0c\u8bb6\u7136\u95ee\u9053\uff1a\u201c\u4f60\u6709\u628a\u63e1\u80fd\u628a\u53eb\u4f55\u6167\u4ed9\u7684\u5973\u4eba\u62bc\u56de\u6765\uff1f\u201d \u3000\u3000\u79e6\u69d0\u9053\uff1a\u201c\u672c\u5ea7\u6b64\u53bb\u7684\u76ee\u7684\uff0c\u662f\u60f3\u4eb2\u81ea\u4e86\u89e3\u4e86\u89e3\u72b6\u51b5\uff0c\u770b\u770b\u4e25\u57ce\u4e3b\u5230\u5e95\u7528\u5fc3\u4f55\u5728\uff1f\u552f\u6709 \u77e5\u5df1\u77e5\u5f7c\uff0c\u624d\u80fd\u767e\u6218\u767e\u80dc\uff0c\u81f3\u4e8e\u80fd\u5426\u628a\u59d3\u4f55\u7684\u5973\u4eba\u62bc\u56de\uff0c\u90a3\u5012\u4e0d\u91cd\u8981\u4e86\u3002\u201d \u3000\u3000\u201c\u627e\u5e0c\u671b\u4f60\u8981\u5c0f\u5fc3\u4e9b\uff0c\u6700\u597d\u4e0d\u8981\u548c\u4ed6\u5f53\u573a\u95f9\u7ffb\u3002\u201d \u3000\u3000\u201c\u6559\u4e3b\u653e\u5fc3\uff0c\u672c\u5ea7\u4e00\u5207\u81ea\u5f53\u89c1\u673a\u884c\u4e8b\u3002\u201d \u3000\u3000\u4e25\u5bd2\u548c\u5468\u6d77\u5c71\u6b63\u5728\u5927\u5385\u672a\u6563\uff0c\u5cb3\u5c0f\u98de\u7a81\u7136\u5306\u5306\u800c\u6765\u3002 \u3000\u3000\u539f\u6765\u5cb3\u5c0f\u98de\u5728\u603b\u575b\u5916\u56f4\u90a3\u5ea7\u5c0f\u697c\u4f4f\u8fc7\u4e00\u591c\u540e\uff0c\u672c\u60f3\u76f4\u63a5\u5230\u80b2\u5316\u57ce\u5411\u4e25\u5bd2\u5bc6\u62a5\uff0c\u56e0\u4e3a\u7531\u603b \u575b\u5230\u80b2\u5316\u57ce\u6bd4\u5bb5\u5173\u5230\u80b2\u5316\u57ce\u8def\u7a0b\u5dee\u4e0d\u591a\u8fd1\u4e86\u4e00\u534a\u3002 \u3000\u3000\u4f46\u4ed6\u4e0d\u80fd\u4e0d\u4f46\u5fc3\u51e4\u5ae3\u7ea2\u751f\u7591\uff0c\u53ea\u597d\u548c\u51e4\u5ae3\u7ea2\u4e00\u9053\u5148\u56de\u5bb5\u5173\u518d\u91cd\u65b0\u8d76\u6765\u3002 \u3000\u3000\u5f53\u4e0b\u3002\u4e25\u5bd2\u4e5f\u628a\u603b\u575b\u66fe\u6765\u62bc\u89e3\u4f55\u6167\u4ed9\u7684\u4e8b\u544a\u77e5\u4e86\u5cb3\u5c0f\u98de\u3002 \u3000\u3000\u4e8b\u60c5\u5df2\u7ecf\u95f9\u5927\u4e86\uff0c\u5cb3\u5c0f\u98de\u5f53\u7136\u5927\u611f\u5fd0\u5fd1\u4e0d\u5b81\uff0c\u540c\u65f6\uff0c\u4ed6\u5bf9\u4e25\u5bd2\u7684\u4e0d\u60dc\u548c\u603b\u575b\u51b3\u88c2\u4ed7\u4e49\u76f8 \u52a9\uff0c\u771f\u662f\u611f\u6fc0\u83ab\u540d\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u540d\u9ed1\u8863\u5927\u6c49\u5306\u5306\u524d\u6765\u7980\u62a5\uff1a\u603b\u62a4\u6cd5\u79e6\u69d0\u5df2\u8fdb\u5165\u4e86\u57ce\u95e8\u3002 \u3000\u3000\u603b\u62a4\u6cd5\u79e6\u69d0\u4eb2\u81ea\u524d\u6765\u80b2\u5316\u57ce\u3002\u4e25\u5bd2\u548c\u5468\u6d77\u5c71\u4e0d\u96be\u60f3\u5230\u4e8b\u60c5\u5df2\u7ecf\u5341\u5206\u4e25\u91cd\u3002 \u3000\u3000\u79e6\u69d0\u662f\u603b\u575b\u7684\u7b2c\u4e8c\u53f7\u4eba\u7269\uff0c\u8fde\u4e25\u5bd2\u5bf9\u4ed6\u4e5f\u4e0d\u80fd\u8fc7\u4e8e\u6020\u6162\uff0c\u7acb\u5373\u5429\u5490\u5468\u6d77\u5c71\u51fa\u8fce\uff0c\u81ea\u5df1\u5219 \u6765\u5230\u5ba2\u5385\u95e8\u53e3\u7b49\u5019\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e3a\u4e86\u80fd\u4eb2\u773c\u770b\u5230\u4e8b\u60c5\u7a76\u7adf\u4e25\u91cd\u5230\u4ec0\u4e48\u7a0b\u5ea6\uff0c\u8eac\u8eab\u7980\u9053\uff1a \u3000\u3000\u201c\u665a\u8f88\u662f\u5426\u80fd\u968f\u4f8d\u57ce\u4e3b\u8eab\u65c1\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u8001\u592b\u867d\u7136\u4e5f\u5e0c\u671b\u4f60\u80fd\u5728\u573a\uff0c\u4f46\u4e07\u4e00\u88ab\u79e6\u69d0\u8ba4\u51fa\uff0c\u53ea\u6015\u8d8a\u53d1\u4e0d\u597d\u529e\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u5468\u603b\u7ba1\u8d50\u4e86\u665a\u8f88\u4e00\u526f\u4eba\u76ae\u9762\u5177\uff0c\u665a\u8f88\u5df2\u5e26\u5728\u8eab\u8fb9\u3002\u201d \u3000\u3000\u4e25\u5bd2\u559c\u9053\uff1a\u201c\u90a3\u5c31\u5feb\u5feb\u5e26\u4e0a\uff0c\u968f\u5728\u8001\u592b\u8eab\u65c1\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u521a\u628a\u9762\u5177\u6234\u597d\u4e0d\u4e45\uff0c\u79e6\u69d0\u5df2\u5728\u5468\u6d77\u5c71\u7684\u966a\u540c\u4e0b\uff0c\u6447\u6447\u6446\u6446\u800c\u6765\u3002 \u3000\u3000\u4e25\u5bd2\u8fde\u5fd9\u62f1\u624b\u9053\uff1a\u201c\u4e07\u60f3\u4e0d\u5230\u79e6\u603b\u5ea7\u4eca\u5929\u4f1a\u9a7e\u4e34\u672c\u57ce\uff0c\u4e25\u67d0\u6709\u5931\u8fdc\u8fce\uff0c\u6055\u7f6a\uff0c\u6055\u7f6a\u3002\u201d \u3000\u3000\u79e6\u69d0\u4e5f\u62f1\u624b\u8fc7\u989d\uff0c\u4e00\u9762\u563f\u563f\u7b11\u9053\uff1a\u201c\u672c\u5ea7\u4e8b\u5148\u672a\u7ecf\u901a\u62a5\uff0c\u5192\u6627\u800c\u6765\uff0c\u8fd8\u671b\u4e25\u57ce\u4e3b\u6d77\u6db5\uff01\u201d \u3000\u3000\u4e24\u4eba\u4e92\u8ba9\u7740\u5206\u5bbe\u4e3b\u5750\u5b9a\u540e\uff0c\u5cb3\u5c0f\u98de\u8fde\u5fd9\u606d\u606d\u656c\u656c\u7684\u5c06\u8336\u7aef\u4e0a\uff0c\u7136\u540e\u53c8\u89c4\u89c4\u77e9\u77e9\u7684\u7ad9\u5728\u4e00 \u8fb9\u3002 \u3000\u3000\u79e6\u69d0\u559d\u4e86\u53e3\u8336\uff0c\u5148\u563f\u563f\u7b11\u4e86\u51e0\u58f0\u624d\u9053\uff1a\u201c\u542c\u8d75\u62a4\u6cd5\u56de\u53bb\u5411\u6559\u4e3b\u7980\u62a5\uff0c\u90a3\u540d\u59d3\u4f55\u7684\u5973\u5b50\u75c5 \u4e86\uff0c\u800c\u4e14\u75c5\u5f97\u5f88\u91cd\uff0c\u53ef\u5426\u8ba9\u672c\u5ea7\u77a7\u77a7\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u636e\u59d3\u4f55\u7684\u5973\u5b50\u8bf4\uff0c\u5979\u8fd9\u75c5\u662f\u79cd\u602a\u75c5\uff0c\u53ea\u8981\u4e00\u72af\uff0c\u5fc5\u987b\u88f8\u4f53\u6ce1\u5728\u6e29\u6c34\u91cc\uff0c\u65e5\u524d \u8fd8\u5728\u6d74\u76c6\u91cc\u6ce1\u7740\uff0c\u79e6\u603b\u5ea7\u60f3\u8fdb\u53bb\u770b\u3002\u53ea\u6015\u4e0d\u592a\u59a5\u4e9b\u3002\u201d \u3000\u3000\u79e6\u69d0\u634b\u7740\u516b\u5b57\u80e1\u5e72\u7b11\u9053\uff1a\u201c\u672c\u5ea7\u8fd8\u4ece\u6ca1\u542c\u8bf4\u8fc7\u4e16\u4e0a\u6709\u8fd9\u79cd\u75c5\uff0c\u5f53\u771f\u5de7\u5f97\u5f88\u3002\u201d \u3000\u3000\u4e25\u5bd2\u4e5f\u5475\u5475\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u4e4b\u5927\uff0c\u65e0\u5947\u4e0d\u6709\uff0c\u79e6\u603b\u5ea7\u7eb5\u7136\u89c1\u591a\u8bc6\u5e7f\uff0c\u4e5f\u96be\u514d\u6709\u6ca1\u542c\u8bf4\u8fc7\u7684 \u4e8b\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u662f\u8fd9\u6837\uff0c\u672c\u5ea7\u7684\u786e\u4e0d\u65b9\u4fbf\u8fdb\u53bb\u3002\u201d \u3000\u3000\u201c\u4e25\u67d0\u4e0d\u89e3\u3002\u6559\u4e3b\u4e3a\u4ec0\u4e48\u8981\u4eba\u8981\u5f97\u8fd9\u6837\u6025\uff0c\u59d3\u4f55\u7684\u5973\u5b50\uff0c\u6253\u5165\u7075\u5802\u4e09\u5e74\u591a\uff0c\u4ece\u6ca1\u89c1\u6559\u4e3b \u95ee\u8fc7\uff0c\u5982\u4eca\u6025\u7740\u8981\u4eba\uff0c\u6559\u4e3b\u662f\u5426\u8bef\u542c\u4e86\u4ec0\u4e48\u4eba\u7684\u8c17\u8a00\uff1f\u201d \u3000\u3000\u201c\u6559\u4e3b\u5723\u660e\u777f\u77e5\uff0c\u600e\u80af\u542c\u4fe1\u8c17\u8a00\uff0c\u60f3\u5fc5\u90a3\u59d3\u4f55\u7684\u5973\u5b50\uff0c\u786e\u662f\u4e00\u540d\u91cd\u72af\uff0c\u5426\u5219\u6559\u4e3b\u51b3\u4e0d\u53ef \u80fd\u975e\u628a\u5979\u62bc\u56de\u603b\u575b\u4e0d\u53ef\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u8bf7\u79e6\u603b\u5ea7\u4ee3\u4e25\u67d0\u4e0a\u590d\u6559\u4e3b\uff0c\u8fc7\u4e24\u5929\u7b49\u5979\u7a0d\u597d\u4e9b\uff0c\u4e25\u67d0\u5fc5\u5b9a\u4eb2\u81ea\u628a\u4eba\u9001\u5230\u603b\u575b\uff0c\u4e0d \u9700\u6559\u4e3b\u52b3\u5fc3\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u672c\u5ea7\u8be5\u4ee3\u6559\u4e3b\u8c22\u8fc7\u4e25\u57ce\u4e3b\u4e86\u3002\u201d \u3000\u3000\u4e25\u5bd2\u6447\u4e86\u6447\u5934\uff0c\u5401\u53e3\u6c14\u9053\uff1a\u201c\u5176\u5b9e\u59d3\u4f55\u7684\u5973\u5b50\u75c5\u5f97\u8fd9\u4e48\u5de7\uff0c\u5e76\u975e\u65e0\u56e0\uff0c\u82e5\u4e0d\u8bf4\u660e\u767d\uff0c\u6559 \u4e3b\u548c\u79e6\u603b\u5ea7\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u5bf9\u4e25\u67d0\u751f\u51fa\u7591\u5fc3\u3002\u201d \u3000\u3000\u79e6\u69d0\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u5979\u8fd9\u75c5\u662f\u5413\u51fa\u6765\u7684\uff0c\u5f53\u5979\u542c\u5230\u6559\u4e3b\u8981\u628a\u5979\u62bc\u5230\u603b\u575b\uff0c\u5fc3\u91cc\u4e00\u6025\u4e00\u6015\uff0c\u591a\u5e74\u6ca1 \u72af\u7684\u8001\u6bdb\u75c5\u5c45\u7136\u590d\u53d1\u3002\u201d \u3000\u3000\u79e6\u69d0\u9894\u9996\u9053\uff1a\u201c\u4e25\u57ce\u4e3b\u4e3a\u4ec0\u4e48\u65e9\u4e0d\u8bf4\u660e\uff0c\u5426\u5219\u4e5f\u8bb8\u771f\u7684\u4f1a\u8ba9\u4eba\u53d1\u751f\u8bef\u4f1a\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u5ffd\u7136\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u672c\u5ea7\u53ef\u5426\u53bb\u770b\u770b\u9a6c\u9999\u4e3b\uff1f\u4ed6\u662f\u4e2a\u7537\u4eba\uff0c\u5373\u4f7f\u4e5f\u6ce1\u5728\u6d74\u76c6\u91cc\uff0c\u4e5f \u4e0d\u81f3\u4e8e\u770b\u4e0d\u5f97\u3002\u201d \u3000\u3000\u4e25\u5bd2\u6ca1\u60f3\u5230\u5bf9\u65b9\u6709\u6b64\u4e00\u7740\uff0c\u60f3\u62d2\u7edd\u53c8\u6ca1\u7406\u7531\uff0c\u53ea\u597d\u4e5f\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u79e6\u603b\u5ea7\u4e3a\u4ec0\u4e48\u60f3\u8d77\u8981 \u770b\u4ed6\uff1f\u83ab\u975e\u4e5f\u662f\u5949\u6559\u4e3b\u4e4b\u547d\uff1f\u201d \u3000\u3000\u79e6\u69d0\u76ae\u7b11\u8089\u4e0d\u7b11\u9053\uff1a\u201c\u65e2\u7136\u6765\u5230\u8d35\u57ce\uff0c\u53ea\u80fd\u8bf4\u662f\u987a\u4fbf\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u90a3\u4e48\u5c31\u8bf7\u79e6\u603b\u5ea7\u968f\u4e25\u67d0\u6765\u3002\u201d \u3000\u3000\u6765\u5230\u90a3\u95f4\u7a7a\u5c4b\uff0c\u5468\u6d77\u5c71\u5148\u884c\u4e0a\u524d\u6253\u5f00\u4e86\u95e8\u3002 \u3000\u3000\u53ea\u89c1\u7cb1\u4e0a\u540a\u7740\u4e00\u4e2a\u4eba\uff0c\u51e0\u4e4e\u8ba4\u4e0d\u51fa\u662f\u9a6c\u662d\u96c4\u3002 \u3000\u3000\u7531\u4e8e\u4e25\u5bd2\u4e00\u76f4\u5230\u73b0\u5728\u4e0d\u66fe\u7ed9\u4ed6\u5403\u996d\uff0c\u4ec5\u4ec5\u4e24\u5929\u7684\u5de5\u592b\uff0c\u9a6c\u662d\u96c4\u5df2\u7ecf\u88ab\u540a\u5f97\u4e0d\u6210\u4eba\u5f62\uff0c\u7b80 \u76f4\u8fd1\u4e4e\u5944\u5944\u4e00\u606f\u3002 \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u9a6c\u9999\u4e3b\uff0c\u603b\u575b\u79e6\u603b\u62a4\u6cd5\u6765\u770b\u4f60\u4e86\uff01\u201d \u3000\u3000\u9a6c\u662d\u96c4\u7f13\u7f13\u7741\u5f00\u773c\u6765\uff0c\u9732\u51fa\u60ca\u559c\u4e4b\u8272\uff0c\u6709\u6c14\u65e0\u529b\u7684\u558a\u9053\uff1a\u201c\u603b\u62a4\u6cd5\uff0c\u5feb\u6551\u6551\u5c5e\u4e0b\uff01\u201d \u3000\u3000\u79e6\u69d0\u54a7\u5634\u5e72\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u540a\u4f60\u7684\u662f\u4e25\u57ce\u4e3b\uff0c\u672c\u5ea7\u5982\u4f55\u6551\u5f97\u4e86\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u518d\u558a\u9053\uff1a\u201c\u4f60\u66ff\u5c5e\u4e0b\u8bb2\u8bb2\u60c5\uff0c\u5c5e\u4e0b\u662f\u51a4\u6789\u7684\u3002\u201d \u3000\u3000\u79e6\u69d0\u9053\uff1a\u201c\u4f60\u51a4\u6789\u4e0d\u51a4\u6789\uff0c\u53ea\u6709\u4e25\u57ce\u4e3b\u5fc3\u91cc\u6709\u6570\uff0c\u8ddf\u672c\u5ea7\u8bb2\u53c8\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u9a6c\u662d\u96c4\u539f\u4ee5\u4e3a\u79e6\u69d0\u662f\u81ea\u5df1\u7684\u6551\u661f\uff0c\u4e07\u6ca1\u6599\u5230\u4ed6\u7684\u6001\u5ea6\u7adf\u662f\u5982\u6b64\u6f20\u4e0d\u5173\u5fc3\u3002 \u3000\u3000\u53ea\u542c\u79e6\u69d0\u53c8\u9053\uff1a\u201c\u5c31\u8fd9\u6837\u540a\u7740\u5427\uff0c\u770b\u8d77\u6765\u8fd8\u86ee\u8212\u670d\u7684\u3002\u201d \u3000\u3000\u9a6c\u662d\u96c4\u62da\u529b\u53eb\u9053\uff1a\u201c\u603b\u62a4\u6cd5\uff0c\u4f60\u8001\u4eba\u5bb6\u600e\u4e48\u8bf4\u51fa\u8fd9\u79cd\u8bdd\u6765\uff1f\u201d \u3000\u3000\u79e6\u69d0\u9053\uff1a\u201c\u4e25\u57ce\u4e3b\u4e0d\u80af\u9976\uff0c\u4f60\u8ba9\u672c\u5ea7\u8bf4\u4ec0\u4e48\u8bdd\uff0c\u4e00\u540d\u5c0f\u5c0f\u9999\u4e3b\uff0c\u6709\u4f60\u4e0d\u591a\uff0c\u6ca1\u4f60\u4e5f\u4e0d\u5c11\uff0c \u5c31\u662f\u6b7b\u4e86\u53c8\u6709\u4ec0\u4e48\u5927\u4e0d\u4e86\uff01\u201d \u3000\u3000\u4ed6\u8bf4\u7740\u8ff3\u81ea\u8d70\u51fa\u7a7a\u5c4b\uff0c\u5411\u4e25\u5bd2\u62f1\u62f1\u624b\u9053\uff1a\u201c\u672c\u5ea7\u8be5\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u79e6\u69d0\u8d70\u540e\uff0c\u4e25\u5bd2\u548c\u5468\u6d77\u5c71\u53c8\u56de\u5230\u5927\u5385\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6b63\u5b88\u5728\u5927\u5385\u91cc\uff0c\u4ed6\u62c5\u5fc3\u88ab\u9a6c\u662d\u96c4\u8ba4\u51fa\uff0c\u5e76\u6ca1\u8ddf\u7740\u53bb\u3002\u5cb3\u5c0f\u98de\u53d6\u4e0b\u9762\u5177\uff0c\u7b49\u4e25\u5bd2\u5750 \u4e0b\u540e\uff0c\u8ddf\u8fc7\u6765\u9053\uff1a\u201c\u57ce\u4e3b\uff0c\u4f60\u8001\u4eba\u5bb6\u5fc5\u5b9a\u770b\u5f97\u51fa\u8fd9\u4f4d\u603b\u62a4\u6cd5\u4e0d\u6000\u597d\u610f\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u8001\u592b\u4e0d\u4f1a\u4e0a\u4ed6\u7684\u5f53\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u665a\u8f88\u62c5\u5fc3\u82e5\u4e0b\u6b21\u6559\u4e3b\u628a\u57ce\u4e3b\u53ec\u5230\u603b\u575b\uff0c\u4f60\u8001\u4eba\u5bb6\u8bf4\u4e0d\u5b9a\u4f1a\u906d\u5230\u610f\u5916\u3002\u201d \u3000\u3000\u4e25\u5bd2\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6253\u8fd9\u79cd\u53ef\u80fd\uff0c\u4e5f\u8bb8\u771f\u4f1a\u50cf\u97e9\u4fe1\u5f53\u5e74\u88ab\u5ba3\u8fdb\u4e86\u672a\u592e\u5bab\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u57ce\u4e3b\u8be5\u5982\u4f55\u5904\u7f6e\uff0c\u5fc5\u987b\u62a8\u505a\u6253\u7b97\uff01\u201d \u3000\u3000\u201c\u8001\u592b\u81ea\u6709\u5e94\u4ed8\u4e4b\u9053\u3002\u201d \u3000\u3000\u79e6\u69d0\u56de\u5230\u603b\u575b\uff0c\u7687\u5143\u6559\u4e3b\u6b63\u5728\u5185\u5bab\u7b49\u5019\u56de\u97f3\u3002 \u3000\u3000\u5f53\u5979\u542c\u8fc7\u79e6\u69d0\u62a5\u544a\u4e4b\u540e\uff0c\u8138\u8272\u5982\u7f69\u5bd2\u971c\uff0c\u7f04\u9ed8\u4e86\u534a\u54cd\u624d\u9053\uff1a \u3000\u3000\u201c\u4e8b\u60c5\u7684\u786e\u5982\u4f60\u6240\u6599\uff0c\u770b\u6765\u6211\u4e5f\u987e\u4e0d\u5f97\u90a3\u4e48\u591a\u4e86\uff01\u201d \u3000\u3000\u79e6\u69d0\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u6559\u4e3b\u53ef\u8981\u9a6c\u4e0a\u5ba3\u53ec\u4ed6\u5230\u603b\u575b\uff0c\u5f53\u573a\u628a\u4ed6\u62ff\u4e0b\uff1f\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u9053\uff1a\u201c\u5f53\u7136\u8fd9\u8be5\u662f\u552f\u4e00\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u79e6\u69d0\u70b9\u5934\u7b11\u9053\uff1a\u201c\u6559\u4e3b\u9a6c\u4e0a\u8fd9\u6837\u505a\uff0c\u4e0d\u89c9\u5f97\u64cd\u4e4b\u8fc7\u6025\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u53ef\u662f\u4f60\u7684\u4e3b\u610f\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u662f\u672c\u5ea7\u7684\u4e3b\u610f\u3002\u4f46\u6559\u4e3b\u5e94\u8be5\u60f3\u5230\u3002\u4e25\u57ce\u4e3b\u5e76\u4e0d\u7cca\u6d82\uff0c\u60a8\u82e5\u73b0\u5728\u9a6c\u4e0a\u53eb\u4ed6\u5230\u603b\u575b \u6765\uff0c\u4ed6\u5728\u6212\u5fc3\u5c1a\u672a\u6d88\u5931\u4e4b\u4e0b\uff0c\u5f88\u53ef\u80fd\u6258\u6545\u4e0d\u6765\uff0c\u800c\u6559\u4e3b\u53c8\u4e0d\u80fd\u628a\u4ed6\u6349\u4e86\u6765\uff0c\u5230\u90a3\u65f6\u4e8b\u60c5\u5c31\u4e0d \u597d\u529e\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u610f\u601d\u8bf4\u5148\u6309\u5175\u4e0d\u52a8\uff1f\u201d \u3000\u3000\u201c\u672c\u5ea7\u6b63\u662f\u8fd9\u4e48\u60f3\uff0c\u5230\u65f6\u5019\u5fc5\u5b9a\u53ef\u4e00\u4e3e\u6210\u529f\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6025\u6025\u5fd9\u5fd9\u8fd4\u56de\u4e94\u5173\u603b\u9547\u5e9c\u3002 \u3000\u3000\u4ed6\u5916\u51fa\u4e24\u5929\u4e00\u591c\uff0c\u5fc5\u987b\u628a\u6240\u6709\u53d1\u751f\u7684\u4e8b\u5c3d\u5feb\u7980\u544a\u7236\u4eb2\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u95f4\u8a00\u5927\u60ca\uff0c\u4ed6\u4e0d\u96be\u60f3\u5230\u4e8b\u6001\u7684\u4e25\u91cd\u3002 \u3000\u3000\u53e6\u5916\uff0c\u4ed6\u4e5f\u7edd\u672a\u6599\u5230\u8eab\u4e3a\u80b2\u5316\u57ce\u4e3b\u7684\u4e25\u5bd2\uff0c\u4e3a\u4e86\u6551\u51fa\u81ea\u5df1\u7684\u7231\u59bb\u4f55\u6167\u4ed9\uff0c\u7adf\u80af\u4ed8\u51fa\u5982\u6b64 \u5927\u5f97\u4e0d\u80fd\u518d\u5927\u7684\u4ee3\u4ef7\u3002 \u3000\u3000\u7531\u8fd9\u4e8b\u770b\u6765\uff0c\u4e25\u5bd2\u548c\u7687\u5143\u6559\u4e3b\u5206\u660e\u5df2\u662f\u6c34\u706b\u65e0\u6cd5\u76f8\u5bb9\u4e86\u3002\u800c\u4e25\u5bd2\u4e3a\u4e86\u81ea\u4fdd\uff0c\u9664\u4e86\u53db\u79bb\u7687 \u5143\u6559\uff0c\u6839\u672c\u6ca1\u6709\u7b2c\u4e8c\u6761\u8def\u53ef\u8d70\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u82b1\u7389\u9e9f\u663e\u5f97\u795e\u8272\u65e0\u6bd4\u90d1\u91cd\u7684\u9053\uff1a\u201c\u4ece\u73b0\u5728\u8d77\uff0c\u4f60\u4e0d\u59a8\u5927\u90e8\u5206\u65f6\u95f4\u7559\u5728\u80b2\u5316\u57ce \u4e3b\u8eab\u8fb9\uff0c\u5fc5\u8981\u65f6\u6211\u4f1a\u8fde\u8881\u5c0f\u9e64\u4e5f\u4e00\u8d77\u6d3e\u53bb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u5934\u4e00\u9707\u9053\uff1a\u201c\u7236\u4eb2\u53ef\u662f\u8ba4\u4e3a\u4e25\u57ce\u4e3b\u968f\u65f6\u90fd\u6709\u5371\u9669\uff0c\u8981\u5b69\u513f\u548c\u8881\u5927\u54e5\u53bb\u4fdd\u62a4\u4ed6\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9894\u9996\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e25\u57ce\u5904\u6839\u672c\u5df2\u4e0d\u53ef\u80fd\u548c\u6559\u4e3b\u8a00\u5f52\u4e8e\u597d\uff0c\u800c\u6559\u4e3b\u4e5f\u7edd\u5bf9\u4e0d\u53ef\u80fd\u5bb9 \u5f97\u4e0b\u4e25\u57ce\u4e3b\uff0c\u4e25\u57ce\u4e3b\u662f\u4e3a\u4e86\u4f60\u6bcd\u4eb2\u624d\u4ed8\u51fa\u5982\u6b64\u91cd\u5927\u7684\u727a\u7272\uff0c\u6211\u4eec\u53c8\u600e\u80fd\u4e0d\u6697\u4e2d\u76f8\u52a9\u3002\u4f55\u51b5\u4e00 \u65e6\u80b2\u5316\u57ce\u4e0d\u4fdd\uff0c\u4f60\u6bcd\u4eb2\u4e5f\u96be\u5e78\u514d\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u5934\u5982\u53d7\u7535\u51fb\uff0c\u987f\u4e00\u4e86\u987f\u9053\uff1a\u201c\u53ef\u662f\u80b2\u5316\u57ce\u7684\u529b\u91cf\uff0c\u5982\u4f55\u80fd\u654c\u5f97\u4f4f\u6559\u4e3b\u7684\u6574\u4e2a\u5929 \u8c37\uff1f\u4e25\u57ce\u7740\u5c82\u4e0d\u773c\u770b\u4f1a\u88ab\u6d88\u706d\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u559f\u7136\u4e00\u53f9\u9053\uff1a\u201c\u4e8b\u60c5\u5df2\u7ecf\u5230\u4e86\u8fd9\u79cd\u5730\u6b65\uff0c\u53ea\u8981\u6211\u4eec\u5c3d\u4e86\u529b\uff0c\u90a3\u91cc\u8fd8\u80fd\u8ba1\u8f83\u540e\u679c\uff0c \u5373\u4f7f\u6211\u4eec\u540c\u5f52\u4e8e\u5c3d\uff0c\u4e5f\u662f\u6709\u4ee3\u4ef7\u7684\uff0c\u80b2\u5316\u57ce\u6240\u6709\u7684\u4eba\u867d\u88ab\u6d88\u706d\uff0c\u4f46\u7687\u5143\u6559\u4e5f\u5fc5\u4ed8\u51fa\u60e8\u91cd\u7684\u4ee3 \u4ef7\uff0c\u81f3\u5c11\u5df2\u7ecf\u6ca1\u6709\u529b\u91cf\u518d\u8fdb\u636e\u4e2d\u539f\uff0c\u5982\u6b64\u770b\u6765\uff0c\u6211\u4eec\u5c82\u4e0d\u662f\u4e3a\u4e2d\u539f\u6b66\u6797\u6d88\u9664\u4e86\u4e00\u573a\u8165\u98ce\u8840\u96e8 \u7684\u6d69\u52ab\u3002\u201d \u3000\u3000\u201c\u7236\u4eb2\u90a3\u65f6\u662f\u5426\u4e5f\u8981\u5230\u80b2\u5316\u57ce\uff1f\u8fd8\u662f\u51c6\u5907\u5728\u6697\u4e2d\u63a5\u5e94\uff1f\u201d \u3000\u3000\u201c\u53ea\u8981\u53cc\u65b9\u6218\u706b\u4e00\u8d77\uff0c\u4e3a\u7236\u5f53\u7136\u8981\u5e26\u9886\u8fd9\u91cc\u7684\u5fc3\u8179\u624b\u4e0b\u8d76\u5230\u80b2\u5316\u57ce\u76f8\u52a9\uff0c\u540c\u65f6\u6211\u4e5f\u8bb8\u4f1a \u5e26\u4e2a\u4eba\u8d28\u53bb\u3002\u201d \u3000\u3000\u201c\u7236\u4eb2\u6240\u8bf4\u7684\u4eba\u8d28\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u5f53\u7136\u662f\u5927\u516c\u4e3b\uff0c\u6709\u4e86\u5927\u516c\u4e3b\u505a\u4eba\u8d28\uff0c\u7687\u5143\u6559\u5fc5\u5b9a\u6295\u9f20\u5fcc\u5668\u3002 \u3000\u3000\u7687\u5143\u6559\u4e3b\u60f3\u6d88\u706d\u80b2\u5316\u57ce\uff0c\u4e5f\u5c31\u96be\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7acb\u523b\u50cf\u6765\u4e86\u4e00\u7ebf\u5e0c\u671b\uff0c\u5982\u80fd\u628a\u5927\u516c\u4e3b\u62ff\u4e0b\u505a\u4eba\u8d28\uff0c\u7684\u786e\u4e0d\u5931\u4e3a\u4e00\u826f\u7b56\u3002 \u3000\u3000\u4ed6\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u662f\u5426\u4e5f\u53ef\u628a\u51e4\u5ae3\u7ea2\u62ff\u4e0b\u505a\u4eba\u8d28\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u6447\u5934\u9053\uff1a\u201c\u5979\u867d\u662f\u6559\u4e3b\u7684\u5fc3\u8179\uff0c\u4f46\u6559\u4e3b\u5374\u6839\u672c\u4e0d\u4f1a\u987e\u5ff5\u5979\u7684\u751f\u6b7b\uff0c\u4ee5\u5979\u505a\u4eba\u8d28\uff0c \u53c8\u6709\u4f55\u7528\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e48\u5b69\u513f\u660e\u5929\u5c31\u518d\u56de\u80b2\u5316\u57ce\u53bb\u3002\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u90a3\u4e48\u6025\uff0c\u4f9d\u6211\u6240\u6599\uff0c\u6559\u4e3b\u76ee\u524d\u5bf9\u4e25\u57ce\u4e3b\uff0c\u5fc5\u5b9a\u6682\u65f6\u6309\u5175\u4e0d\u52a8\uff0c\u4e0d\u59a8\u8fc7\u4e09\u3001\u4e94\u5929\u518d \u53bb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u79bb\u5f00\u82b1\u7389\u9e9f\u623f\u95f4\uff0c\u4fbf\u5fd9\u7740\u53bb\u627e\u8881\u5c0f\u9e64\u3002 \u3000\u3000\u4ed6\u548c\u8881\u5c0f\u9e64\uff0c\u53c8\u662f\u5f88\u4e45\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4ed6\u548c\u8881\u5c0f\u9e64\uff0c\u4eb2\u5982\u4e00\u6bcd\u540c\u80de\uff0c\u4e00\u65f6\u4e0d\u89c1\uff0c\u4fbf\u96be\u514d\u60f3\u5ff5\u3002 \u3000\u3000\u6309\u7740\uff0c\u53c8\u53bb\u627e\u4e01\u6d9b\u548c\u7518\u9716\u3002 \u3000\u3000\u504f\u504f\u770b\u4e0d\u5230\u4ed6\u4eec\u7684\u5f71\u5b50\u3002 \u3000\u3000\u539f\u6765\u4e01\u6d9b\u548c\u7518\u9716\u662f\u53bb\u4e86\u5bb5\u5173\u3002 \u3000\u3000\u4ed6\u4eec\u4e24\u4eba\u81ea\u4ece\u4e0a\u6b21\u968f\u82b1\u7389\u9e9f\u5230\u5bb5\u5173\u89c6\u5bdf\u540e\uff0c\u8fd9\u4e24\u5929\u4e00\u5fc3\u4e00\u610f\u53ea\u5ff5\u7740\u5bb5\u5173\u3002 \u3000\u3000\u56e0\u4e3a\u5bb5\u5173\u7edf\u9886\u4eba\u957f\u5f97\u7f8e\uff0c\u800c\u4e14\u4e5f\u98ce\u9a9a\uff0c\u5c24\u5176\u90a3\u4e8c\u5341\u51e0\u540d\u62c5\u4efb\u4eea\u961f\u7684\u5973\u5175\uff0c\u5373\u4fbf\u770b\u4e00\u773c\u4e5f \u662f\u8212\u670d\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u672c\u6765\u60f3\u7ea6\u8881\u5c0f\u9e64\u4e00\u8d77\u53bb\uff0c\u4f46\u53c8\u89c9\u5f97\u8881\u5c0f\u9e64\u592a\u6b63\u7ecf\uff0c\u53bb\u4e86\u53cd\u800c\u6709\u715e\u98ce\u666f\uff0c\u4e0d\u82e5\u81ea\u5df1\u53bb \u7684\u597d\u3002 \u3000\u3000\u5230\u4e86\u5bb5\u5173\uff0c\u504f\u504f\u5374\u8fde\u2014\u4e2a\u5973\u7684\u90fd\u6ca1\u770b\u5230\u3002 \u3000\u3000\u526f\u7edf\u9886\u5f20\u51e4\u9e23\u53c8\u5916\u51fa\u672a\u5f52\u3002 \u3000\u3000\u5b88\u95e8\u6c49\u5b50\u628a\u4ed6\u4eec\u5f15\u5230\u5927\u5385\uff0c\u5c31\u5fd9\u81ea\u5df1\u7684\u4e86\uff0c\u5f04\u5f97\u4ed6\u4eec\u8fde\u4e2a\u505a\u966a\u7684\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u4e01\u6d9b\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u7518\uff0c\u54b1\u4eec\u76f4\u63a5\u627e\u51e4\u7edf\u9886\u53bb\uff01\u201d \u3000\u3000\u7518\u9716\u54fc\u58f0\u9053\uff1a\u201c\u4e5f\u4e0d\u6492\u6ce1\u5c3f\u7167\u7167\uff0c\u5c31\u51ed\u54b1\u4eec\u80fd\u53bb\u76f4\u63a5\u627e\u4eba\u5bb6\u51e4\u7edf\u9886\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u5658\u5658\u5634\u9053\uff1a\u201c\u90a3\u53ef\u4e0d\u4e00\u5b9a\uff0c\u54b1\u4eec\u597d\u6b79\u4e5f\u662f\u5de1\u5173\u526f\u4f7f\u8005\uff0c\u65e2\u7136\u6765\u4e86\uff0c\u51e4\u7edf\u9886\u5c31\u4e0d\u80fd\u4e0d \u7406\uff0c\u4e0a\u6b21\u4f60\u8be5\u770b\u5230\uff0c\u90a3\u5973\u4eba\u7684\u4e00\u8eab\u9a9a\u52b2\uff0c\u7b80\u76f4\u5a9a\u5230\u4e86\u9aa8\u5b50\u91cc\uff0c\u5f04\u5f97\u8fd9\u4e24\u665a\u7b80\u76f4\u8fde\u89c9\u90fd\u7761\u4e0d \u597d\u3002\u201d \u3000\u3000\u7518\u9716\u77aa\u4e86\u4e00\u773c\u9053\uff1a\u201c\u5976\u5976\u7684\uff0c\u90a3\u662f\u5979\u5bf9\u526f\u603b\u9547\u732e\u6bb7\u52e4\uff0c\u82e5\u770b\u5230\u4f60\uff0c\u5979\u5c31\u8981\u53e6\u6362\u4e00\u526f\u6a21\u6837 \u4e86\uff01\u201d \u3000\u3000\u201c\u4e0d\u7ba1\u5979\u6362\u4e0d\u6362\u6a21\u6837\uff0c\u53cd\u6b63\u90a3\u6a21\u6837\u53eb\u4eba\u4e00\u770b\u5c31\u8212\u670d\uff0c\u54b1\u4eec\u5c31\u53bb\u89c1\u89c1\u5979\uff01\u201d \u3000\u3000\u4e01\u6d9b\u8bf4\u5b8c\u8bdd\uff0c\u4e0d\u7b49\u7518\u9716\u53cd\u5e94\uff0c\u62c9\u7740\u7518\u9716\u5c31\u8d70\u3002 \u3000\u3000\u7518\u9716\u6ca1\u5948\u4f55\uff0c\u53ea\u597d\u8ddf\u7740\u8d70\uff0c\u5176\u5b9e\u4ed6\u5185\u5fc3\u53c8\u4f55\u5c1d\u4e0d\u60f3\u518d\u89c1\u89c1\u98ce\u60c5\u4e07\u79cd\u7684\u51e4\u5ae3\u8679\uff0c\u53ea\u662f\u8868\u73b0 \u5f97\u6bd4\u4e01\u6d9b\u542b\u84c4\u2014\u70b9\u7f62\u4e86\u3002 \u3000\u3000\u521a\u51fa\u4e86\u5927\u5385\uff0c\u4fbf\u6709\u4e00\u540d\u7d2b\u8863\u7d2b\u88d9\u7684\u5c11\u5973\u8fce\u9762\u800c\u6765\u3002 \u3000\u3000\u90a3\u5c11\u5973\u7f9e\u7b54\u7b54\u7684\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u53ef\u662f\u5de1\u5173\u526f\u4f7f\u8005\u5417\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u7b11\u5403\u5403\u7684\u9053\uff1a\u201c\u59d1\u5a18\u600e\u4e48\u8ba4\u8bc6\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u524d\u4e24\u5929\u526f\u603b\u9547\u6765\u65f6\uff0c\u4f60\u4eec\u4e0d\u662f\u5c31\u968f\u5728\u540e\u9762\u5417\uff1f\u201d \u3000\u3000\u201c\u539f\u6765\u59d1\u5a18\u90a3\u5929\u4e5f\u5728\u573a\uff1f\u201d \u3000\u3000\u201c\u90a3\u5929\u6211\u662f\u4eea\u961f\u91cc\u9762\u7684\uff0c\u5f53\u7136\u89c1\u8fc7\u4f60\u4eec\uff0c\u800c\u4e14\u770b\u5230\u4f60\uff0c\u8fde\u773c\u73e0\u90fd\u4e0d\u8f6c\uff0c\u76f4\u76ef\u7740\u6211\u4eec\u3002\u201d \u3000\u3000\u4e01\u6d9b\u5c34\u5c2c\u7b11\u9053\uff1a\u201c\u5f7c\u6b64\u5f7c\u6b64\uff0c\u82e5\u59d1\u5a18\u4e0d\u770b\u6211\uff0c\u53c8\u600e\u77e5\u9053\u6211\u770b\u4f60\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u7ea2\u7740\u8138\u9053\uff1a\u201c\u6211\u4eec\u770b\u4f60\uff0c\u662f\u7edf\u9886\u89c4\u5b9a\u7684\u3002\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6211\u770b\u4f60\u4eec\uff0c\u4e5f\u662f\u526f\u603b\u9547\u89c4\u5b9a\u7684\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u4e0d\u60f3\u548c\u4e01\u6d9b\u800d\u5634\u76ae\u5b50\uff0c\u4f4e\u5934\u5c31\u8981\u8dd1\u5f00\u3002 \u3000\u3000\u4e01\u6d9b\u5fd9\u53eb\u9053\uff1a\u201c\u59d1\u5a18\u522b\u8dd1\uff0c\u8fd8\u6709\u4e8b\u60c5\u62dc\u6258\u4f60\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u9b3c\u4e8b\uff1f\u201d \u3000\u3000\u201c\u62dc\u6258\u4f60\u4ee3\u6211\u4eec\u5411\u7edf\u9886\u901a\u62a5\u2014\u58f0\uff0c\u5c31\u8bf4\u6211\u4eec\u6765\u62dc\u8bbf\u5979\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u53ea\u9053\u662f\u516c\u4e8b\uff0c\u5f53\u7136\u4e0d\u80fd\u4e0d\u7406\uff0c\u968f\u5373\u4f9d\u8a00\u800c\u53bb\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u513f\uff0c\u4fbf\u53e6\u6709\u4e00\u540d\u5c11\u5973\u8fc7\u6765\u62db\u547c\u9053\uff1a\u201c\u7edf\u9886\u8bf7\u4e24\u4f4d\u526f\u4f7f\u8005\u8fdb\u53bb\uff01\u201d \u3000\u3000\u4e01\u3001\u7518\u4e24\u4eba\u771f\u662f\u559c\u51fa\u671b\u5916\u3002 \u3000\u3000\u4ed6\u4eec\u6c42\u89c1\u51e4\u5ae3\u7ea2\uff0c\u53ea\u4e0d\u8fc7\u662f\u8bd5\u63a2\u8bd5\u63a2\uff0c\u60f3\u4e0d\u5230\u7adf\u771f\u8fbe\u5230\u4e86\u76ee\u7684\u3002 \u3000\u3000\u4e24\u4eba\u8ddf\u5728\u90a3\u5c11\u5973\u8eab\u540e\uff0c\u8fdb\u5165\u4e00\u5904\u7cbe\u820d\uff0c\u76f4\u5954\u5ba2\u5385\u3002 \u3000\u3000\u90a3\u5c11\u5973\u8bf7\u4e24\u4eba\u5750\u4e0b\uff0c\u53c8\u7aef\u6765\u4e24\u676f\u8336\uff0c\u4e34\u8d70\u65f6\u9053\uff1a\u201c\u4e24\u4f4d\u526f\u4f7f\u8005\u8bf7\u7a0d\u5f85\uff0c\u6211\u4eec\u7edf\u9886\u9a6c\u4e0a\u5c31 \u51fa\u6765\u3002\u201d \u3000\u3000\u5ba2\u5385\u91cc\u53ea\u5269\u4e0b\u4e01\u6d9b\u548c\u7518\u9716\u4e24\u4eba\uff0c\u4e0d\u514d\u5f00\u59cb\u4e1c\u5f20\u897f\u671b\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u81ea\u7136\u6599\u5f97\u5230\uff0c\u8fd9\u95f4\u5ba2\u5385\u5fc5\u7136\u662f\u51e4\u5ae3\u7ea2\u72ec\u7528\u7684\uff0c\u4f3c\u4e4e\u6bcf\u4e00\u6837\u6446\u8bbe\u90fd\u9999\u55b7\u55b7\u7684\uff0c\u5f88\u5bb9\u6613 \u4ee4\u4eba\u60f3\u5165\u975e\u975e\u3002 \u3000\u3000\u4e24\u4eba\u6b63\u5728\u51fa\u795e\uff0c\u5c4f\u98ce\u540e\u5df2\u54cd\u8d77\u811a\u6b65\u58f0\u3002 \u3000\u3000\u63a5\u7740\uff0c\u51e4\u5ae3\u7ea2\u5e26\u7740\u4e00\u9635\u9999\u98ce\uff0c\u73b0\u8eab\u800c\u51fa\u3002 \u3000\u3000\u53ea\u770b\u5979\u90a3\u70df\u89c6\u5a9a\u884c\u5a07\u5a07\u800c\u6765\u7684\u59ff\u6001\uff0c\u5c31\u591f\u4e01\u6d9b\u548c\u7518\u9716\u4e24\u773c\u53d1\u76f4\u7684\u3002 \u3000\u3000\u4e24\u4eba\u8fde\u5fd9\u7ad9\u8eab\u800c\u8d77\uff0c\u5404\u81ea\u62b1\u62f3\u656c\u793c\uff0c\u5374\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u4e48\u624d\u597d\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u98ce\u81f4\u5ae3\u7136\u7684\u62ac\u4e86\u62ac\u624b\u9053\uff1a\u201c\u4e24\u4f4d\u526f\u4f7f\u8005\u8bf7\u5750\uff0c\u522b\u5ba2\u6c14\u3002\u201d \u3000\u3000\u8bf4\u7740\u81ea\u5df1\u4e5f\u5728\u5bf9\u9762\u5750\u4e0b\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u7b11\u9765\u751f\u6625\u7684\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u4e8c\u4f4d\u4e00\u4f4d\u59d3\u4e01\uff0c\u4e00\u4f4d\u59d3\u7518\uff0c\u4e0d\u77e5\u5927\u540d\u600e\u4e48\u79f0\u547c\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u54b1\u53eb\u6d9b\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff0c\u201c\u662f\u90a3\u4e2a\u5b57\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u5de6\u8fb9\u662f\u7978\u6c34\u7684\u6c34\uff0c\u53f3\u8fb9\u662f\u5bff\u8863\u5e3d\u7684\u5bff\uff0c\u54b1\u7684\u59d3\u5f88\u7b80\u5355\uff0c\u540d\u5b57\u5374\u53c8\u5f88\u9ebb\u70e6\uff0c\u5f53 \u521d\u5199\u4e86\u597d\u51e0\u5929\u624d\u5199\u5bf9\u3002\u201d \u3000\u3000\u7518\u9716\u63a5\u7740\u9053\uff1a\u201c\u6211\u53eb\u9716\uff0c\u4e0a\u9762\u4e00\u4e2a\u65e0\u96e8\u4e0d\u6210\u6c34\u7684\u96e8\uff0c\u4e0b\u9762\u2014\u4e2a\u65e0\u6797\u4e0d\u7740\u706b\u7684\u6797\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u5f3a\u5fcd\u7740\u7b11\uff0c\u9053\uff1a\u201c\u771f\u96be\u5f97\uff0c\u4e8c\u4f4d\u6709\u8fd9\u6837\u4e00\u4e2a\u597d\u540d\u5b57\uff01\u201d \u3000\u3000\u4e01\u6d9b\u5927\u4e3a\u5f97\u610f\u9053\uff1a\u201c\u51e4\u7edf\u9886\u8fc7\u5956\u4e86\u3002\u540d\u5b57\u867d\u7136\u597d\uff0c\u53ef\u60dc\u5b98\u505a\u7684\u4e0d\u5927\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u90a3\u91cc\u8bdd\uff0c\u5de1\u5173\u526f\u4f7f\u8005\u5df2\u7ecf\u4e0d\u7b97\u5c0f\u4e86\uff0c\u4e8c\u4f4d\u4eca\u5929\u53ef\u662f\u5de1\u89c6\u5bb5\u5173\u6765\u7684\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u53ea\u80fd\u8bf4\u95f2\u7740\u6ca1\u4e8b\uff0c\u5230\u8d35\u5173\u6765\u8d70\u8d70\uff0c\u80fd\u8499\u7edf\u9886\u63a5\u89c1\uff0c\u4e5f\u7b97\u6211\u4eec\u7956\u4e0a\u6709 \u5fb7\uff0c\u5c71\u4e0a\u6709\u674f\uff01\u201d \u3000\u3000\u7518\u9716\u6025\u6025\u7528\u624b\u8098\u78b0\u4e86\u4e01\u6d9b\u4e00\u4e0b\u9053\uff1a\u201c\u4ec0\u4e48\u5c71\u4e0a\u6709\u674f\uff0c\u662f\u4e09\u751f\u6709\u5e78\uff01\u201d \u3000\u3000\u4e01\u6d9b\u4f4e\u58f0\u53f1\u9053\uff1a\u201c\u4f60\u522b\u6253\u5c94\uff0c\u672c\u6765\u5c31\u662f\u5c71\u4e0a\u6709\u674f\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u8881\u4f7f\u8005\u548c\u5cb3\u4f7f\u8005\u600e\u4e48\u6ca1\u6765\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8881\u4f7f\u8005\u8fd1\u6765\u597d\u50cf\u5f88\u61d2\uff0c\u4e0d\u5927\u559c\u6b22\u8d70\u52a8\u3002\u5cb3\u4f7f\u8005\u7ecf\u5e38\u5230\u5916\u9762\u516c\u5e72\uff0c\u6709\u65f6\u597d\u513f\u5929 \u90fd\u89c1\u4e0d\u5230\u4ed6\u7684\u9762\u3002\u201d \u3000\u3000\u201c\u5cb3\u4f7f\u8005\u90fd\u5230\u4ec0\u4e48\u5730\u65b9\u516c\u5e72\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e0d\u80af\u8bb2\uff0c\u597d\u50cf\u662f\u5230\u4ec0\u4e48\u57ce\u91cc\u53bb\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u80b2\u5316\u57ce\uff1f\u201d \u3000\u3000\u201c\u4e0d\u77e5\u662f\u4e0d\u662f\u4e00\u5212\u57ce\uff0c\u53cd\u6b63\u6709\u90a3\u4e2a\u57ce\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u663e\u7136\uff0c\u51e4\u5ae3\u7ea2\u5bf9\u5cb3\u5c0f\u98de\u7684\u884c\u52a8\uff0c\u5df2\u7559\u4e0a\u4e86\u610f\u3002\u56e0\u4e3a\u4e0a\u6b21\u5979\u867d\u542c\u5cb3\u5c0f\u98de\u8bf4\u662f\u53bb\u603b\u575b\uff0c\u4f46\u7ecf \u5979\u6253\u542c\uff0c\u603b\u575b\u5374\u65e0\u4eba\u89c1\u8fc7\u4ed6\u3002 \u3000\u3000\u5ffd\u542c\u4e01\u6d9b\u5e72\u54b3\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u7edf\u9886\uff0c\u4e0a\u6b21\u6211\u4eec\u8ddf\u82b1\u526f\u603b\u9547\u6765\uff0c\u770b\u89c1\u60a8\u8fd9\u91cc\u6709\u90a3\u4e48\u591a\u5973\u5175\uff0c \u600e\u4e48\u4eca\u5929\u5f88\u5c11\u89c1\u5230\u5462\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u62bf\u5634\u4e00\u7b11\u9053\uff1a\u201c\u4e01\u526f\u9675\u8005\u95ee\u8fd9\u4e2a\u5e72\u561b\uff0c\u662f\u5426\u8fd8\u60f3\u518d\u770b\u770b\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u5472\u7740\u4e00\u5bf9\u5927\u677f\u7259\u9053\uff1a\u201c\u7537\u4eba\u561b\uff0c\u8c01\u4e0d\u60f3\u770b\u770b\u597d\u770b\u7684\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u5982\u679c\u4f60\u4eec\u60f3\u770b\uff0c\u6211\u5c31\u53eb\u4e24\u4e2a\u6765\uff0c\u966a\u4e8c\u4f4d\u5230\u5404\u5904\u8d70\u8d70\u3002\u201d \u3000\u3000\u4e01\u6d9b\u987f\u611f\u4e50\u4e0d\u53ef\u652f\uff0c\u5658\u5634\u7b11\u9053\uff1a\u201c\u5b9e\u5728\u4e0d\u597d\u610f\u601d\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u8fd9\u662f\u5e94\u8be5\u7684\uff0c\u4f60\u4eec\u4e8c\u4f4d\u8bf7\u5230\u5916\u800c\u4e00\u68f5\u5927\u6811\u4e0b\u7b49\uff0c\u5979\u4eec\u5f88\u5feb\u5c31\u5230\u3002\u201d \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u96be\u514d\u6709\u4e9b\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5728\u5ba2\u5385\u7b49\u800c\u8981\u5728\u5927\u6811\u4e0b\u7b49\u5462\uff1f \u3000\u3000\u4ed6\u4eec\u65e0\u6687\u591a\u60f3\uff0c\u6b22\u5929\u559c\u5730\u7684\u8f9e\u522b\u8fc7\u51e4\u5ae3\u7ea2\u3002\u8ff3\u81ea\u5230\u4e86\u95e8\u5916\u3002\u51e4\u5ae3\u7ea2\u968f\u5373\u53ec\u4e86\u4e24\u540d\u5c11\u5973\u5230 \u5ba2\u5385\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u59ff\u8272\u90fd\u5728\u4e2d\u7b49\u4ee5\u4e0a\uff0c\u4e00\u4e2a\u53eb\u7ea2\u674f\uff0c\u4e00\u4e2a\u53eb\u78a7\u6843\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u4f60\u4eec\u9a6c\u4e0a\u53bb\u966a\u90a3\u4e24\u4e2a\u526f\u4f7f\u8005\uff0c\u8981\u8010\u7740\u6027\u5b50\uff0c\u628a\u4ed6\u4eec\u966a\u5f97\u8212\u8212\u670d\u670d\uff0c\u5f85\u4f1a\u513f \u6253\u8d4f\u3002\u201d \u3000\u3000\u7ea2\u674f\u9053\uff1a\u201c\u7edf\u9886\u53ef\u8981\u5c5e\u4e0b\u4eec\u5728\u4ed6\u4eec\u8eab\u4e0a\u5f97\u5230\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c3d\u91cf\u8bbe\u6cd5\u5f00\u6e05\u695a\u82b1\u526f\u603b\u9547\u4ee5\u53ca\u8881\u4f7f\u8005\u3001\u5cb3\u4f7f\u8005\u7b49\u4eba\u7684\u52a8\u6001\u3002\u201d \u3000\u3000\u539f\u6765\u51e4\u5ae3\u7ea2\u6240\u4ee5\u5982\u6b64\u505a\uff0c\u4e0d\u4f46\u662f\u5979\u81ea\u5df1\u5e0c\u671b\u77e5\u9053\u7684\uff0c\u4e5f\u662f\u7687\u5143\u6559\u4e3b\u7684\u79d8\u5bc6\u4ea4\u4ee3\u3002 \u3000\u3000\u7687\u5143\u6559\u4e3b\u6bcf\u6b21\u53d1\u5e03\u5c5e\u4e0b\u65b0\u4efb\u547d\u540e\u3002\u5fc5\u5b9a\u627e\u4eba\u6697\u4e2d\u8003\u6838\uff0c\u82b1\u7389\u9e9f\u662f\u521d\u4efb\u65b0\u804c\uff0c\u5cb3\u5c0f\u98de\u548c\u8881 \u5c0f\u9e64\u662f\u521a\u521a\u4e3a\u5929\u8c37\u6548\u529b\uff0c\u5979\u5f53\u7136\u8981\u52a0\u4ee5\u8003\u6838\u3002 \u3000\u3000\u7531\u4e8e\u51e4\u5ae3\u7ea2\u662f\u7687\u5143\u6559\u4e3b\u7684\u5fc3\u8179\uff0c\u53c8\u6b63\u597d\u548c\u4e94\u5173\u603b\u9547\u5e9c\u540c\u5728\u5bb5\u5173\uff0c\u5f53\u7136\u8fd9\u9879\u79d8\u5bc6\u4efb\u52a1\uff0c\u5c31 \u843d\u5728\u51e4\u5ae3\u7ea2\u8eab\u4e0a\u3002 \u3000\u3000\u8fd9\u6b21\u51e4\u5ae3\u7ea2\uff0c\u6b63\u662f\u4e00\u4e3e\u4e24\u5f97\u3002\u4e0d\u4f46\u53ef\u4ee5\u62a5\u6548\u7687\u5143\u6559\u4e3b\uff0c\u66f4\u53ef\u501f\u8fd9\u673a\u4f1a\u628a\u82b1\u7389\u9e9f\u548c\u8881\u5c0f\u9e64 \u641e\u5230\u624b\u4e2d\u3002 \u3000\u3000\u8fd9\u65f6\u7ea2\u674f\u548c\u78a7\u6843\u4f3c\u4e4e\u6709\u4e9b\u8e0c\u8e87\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u7ea2\u674f\u55eb\u5685\u7740\u9053\uff1a\u201c\u5c5e\u4e0b\u4eec\u62c5\u5fc3\u4f1a\u5403\u4e86\u90a3\u4e24\u4f4d\u526f\u4f7f\u8005\u7684\u4e8f\uff01\u201d \u3000\u3000\u201c\u4f60\u4eec\u6015\u5403\u4ec0\u4e48\u4e8f\uff1f\u201d \u3000\u3000\u201c\u90a3\u4e24\u4f4d\u526f\u4f7f\u8005\uff0c\u597d\u50cf\u90fd\u5f88\u4e0d\u8001\u5b9e\uff0c\u5c24\u5176\u90a3\u4e2a\u9ed1\u5927\u6c49\uff0c\u66f4\u662f\u51f6\u6765\u516e\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u4f1a\u6765\u4e2a\u9738 \u738b\u786c\u4e0a\u5f13\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u7b11\u9053\uff1a\u201c\u539f\u6765\u4e3a\u8fd9\u4e2a\uff0c\u4ed6\u82e5\u786c\u4e0a\u5f13\uff0c\u4f60\u4eec\u5c31\u7ed9\u4ed6\u4e0a\uff0c\u6b63\u597d\u628a\u4ed6\u4eec\u4f8d\u5019\u75db\u5feb\u4e86\uff0c\u4ed6 \u4eec\u5fc5\u5b9a\u4f1a\u5bf9\u4f60\u4eec\u65e0\u8bdd\u4e0d\u8c08\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u90a3\u6837\u4e00\u6765\u5c5e\u4e0b\u4eec\u5c31\u2026\u2026\u201d \u3000\u3000\u201c\u5c31\u4ec0\u4e48\uff1f\u4f60\u4eec\u8fd8\u60f3\u5f97\u4e00\u5ea7\u8d1e\u8282\u724c\u574a\u4e0d\u6210\uff1f\u4e5f\u5c11\u4e0d\u4e86\u2014\u5757\u8089\uff0c\u8fd9\u79cd\u4e8b\u8bf4\u4e0d\u5b9a\u4f60\u4eec\u4ee5\u540e\u60f3 \u90fd\u60f3\u4e0d\u5230\u5462\uff1f\u5feb\u53bb\uff0c\u522b\u8ba9\u4ed6\u4eec\u4e45\u7b49\u3002\u201d \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u4e8c\u5341\u56db\u56de\u3000\u6743\u5b9c\u4e4b\u8ba1 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u6b63\u5728\u5927\u6811\u4e0b\u7b49\u5f97\u4e0d\u8010\u70e6\uff0c\u7ea2\u674f\u548c\u78a7\u6843\u5df2\u50cf\u4e24\u53ea\u5927\u8774\u8776\u822c\u5e26\u7740\u9999\u98ce\u98d8\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u987f\u65f6\uff0c\u4e24\u4eba\u90fd\u5f00\u59cb\u5fc3\u9b42\u98d8\u8361\u3002 \u3000\u3000\u7ea2\u674f\u548c\u78a7\u6843\u793c\u8282\u5468\u5230\uff0c\u6765\u5230\u9762\u524d\uff0c\u8fd8\u5411\u4e01\u3001\u7518\u4e24\u4eba\u7f9e\u7b54\u7b54\u7684\u65bd\u4e86\u4e00\u793c\u3002 \u3000\u3000\u4e01\u6d9b\u62a2\u7740\u95ee\u9053\uff1a\u201c\u4e24\u4f4d\u59d1\u5a18\u53ef\u662f\u6765\u966a\u6211\u4eec\u7684\uff1f\u201d \u3000\u3000\u7ea2\u674f\u9053\uff1a\u201c\u5f53\u7136\u662f\u966a\u4e24\u4f4d\u526f\u4f7f\u8005\u7684\u3002\u201d \u3000\u3000\u4e01\u6d9b\u8bde\u8138\u9053\uff1a\u201c\u966a\u6211\u4eec\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7ea2\u674f\u9053\uff1a\u201c\u4f60\u60f3\u505a\u4ec0\u4e48\u5c31\u505a\u4ec0\u4e48\u3002\u201d \u3000\u3000\u4e01\u6d9b\u542c\u4e0d\u51fa\u8fd9\u8bdd\u7684\u771f\u6b63\u542b\u610f\uff0c\u54a7\u5634\u7b11\u9053\uff1a\u201c\u771f\u7684\u4e48\uff1f\u201d \u3000\u3000\u7ea2\u674f\u659c\u7768\u4e86\u4e00\u773c\u9053\uff1a\u201c\u4f60\u60f3\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u4e00\u89c1\u6709\u610f\u601d\uff0c\u8fde\u5fd9\u8f6c\u5934\u9053\uff1a\u201c\u8001\u7518\uff0c\u54b1\u4eec\u8be5\u6682\u65f6\u5206\u5206\u624b\uff0c\u8ba9\u5979\u4eec\u4e00\u4e2a\u966a\u4f60\uff0c\u4e00\u4e2a\u966a \u6211\u3002\u201d \u3000\u3000\u7518\u9716\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u9f87\u7259\u2014\u7b11\u9053\uff1a\u201c\u597d\u554a\uff0c\u4f60\u8981\u54ea\u4e2a\u966a\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u6307\u6307\u7ea2\u674f\u9053\uff1a\u201c\u6211\u5c31\u8981\u5979\u3002\u201d \u3000\u3000\u4e8e\u662f\uff0c\u4e24\u4eba\u679c\u7136\u5206\u5934\u8d70\u5f00\u3002 \u3000\u3000\u4e01\u6d9b\u4e34\u8d70\u65f6\u8fd8\u7279\u522b\u4ea4\u4ee3\u9053\uff1a\u201c\u5f85\u4f1a\u513f\u4e8b\u60c5\u529e\u5b8c\u4e86\u4ecd\u5728\u8fd9\u6811\u4e0b\u76f8\u4f1a\u3002\u201d \u3000\u3000\u7ea2\u674f\u8ddf\u5728\u4e01\u6d9b\u8eab\u540e\u8d70\u4e86\u51e0\u6b65\u9053\uff1a\u201c\u526f\u4f7f\u8005\uff0c\u4f60\u521a\u624d\u8bf4\u7684\u529e\u4e8b\uff0c\u662f\u529e\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u54a7\u5634\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u4f60\u8bf4\u5462\uff1f\u201d \u3000\u3000\u7ea2\u674f\u9053\uff1a\u201c\u5f53\u7136\u662f\u966a\u4f60\u89c6\u5bdf\uff0c\u7ed9\u4f60\u5e26\u8def\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4e0d\u4fbf\u64cd\u4e4b\u8fc7\u6025\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u7136\u77e5\u9053\uff0c\u4f55\u5fc5\u518d\u53eb\u3002\u201d \u3000\u3000\u7ea2\u674f\u9053\uff1a\u201c\u6211\u770b\u4f60\u8bf4\u8bdd\u65f6\u9b3c\u9b3c\u795f\u795f\u7684\u3002\u6240\u4ee5\u5f04\u4e0d\u6e05\u695a\u4f60\u60f3\u505a\u4ec0\u4e48\u3002\u201d \u3000\u3000\u201c\u59d1\u5a18\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u201c\u4f60\u60f3\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u201c\u4e0d\u60f3\u53c8\u4f55\u5fc5\u95ee\u3002\u201d \u3000\u3000\u201c\u6211\u53eb\u7ea2\u674f\u3002\u201d \u3000\u3000\u4e01\u6d9b\u771f\u60f3\u628a\u7ea2\u674f\u4e00\u53e3\u5403\u6389\uff0c\u5482\u4e86\u5482\u5634\u9053\uff1a\u201c\u542c\u4f60\u8fd9\u540d\u5b57\uff0c\u4e00\u5b9a\u5f88\u597d\u5403\u3002\u201d \u3000\u3000\u7ea2\u674f\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u540d\u5b57\u600e\u4e48\u80fd\u5403\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6211\u662f\u8bf4\u7ea2\u674f\u597d\u5403\uff0c\u674f\u5b50\u53d1\u7ea2\uff0c\u5f53\u7136\u662f\u70ed\u900f\u4e86\uff0c\u5f53\u7136\u597d\u5403\u3002\u201d \u3000\u3000\u4e24\u4eba\u5230\u5904\u8d70\u4e86\u4e00\u5708\uff0c\u5f88\u5feb\u4fbf\u6765\u5230\u4e00\u5904\u5bc6\u6797\u3002\u5bc6\u6797\u540e\u9762\uff0c\u9690\u9690\u53ef\u89c1\uff0c\u6709\u4e24\u95f4\u7816\u5c4b\u3002 \u3000\u3000\u4e01\u6d9b\u94bb\u8fdb\u5bc6\u6797\uff0c\u76f4\u5411\u90a3\u7816\u5c4b\u8d70\u53bb\u3002 \u3000\u3000\u7ea2\u674f\u5f53\u7136\u6599\u5f97\u5230\uff0c\u9ebb\u70e6\u7684\u4e8b\u9a6c\u4e0a\u5c31\u8981\u6765\u4e86\u3002 \u3000\u3000\u5979\u642d\u8baa\u7740\u95ee\u9053\uff1a\u201c\u526f\u4f7f\u8005\uff0c\u5230\u90a3\u91cc\u800c\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u770b\u770b\u90a3\u7816\u5c4b\u662f\u505a\u4ec0\u4e48\u7528\u7684\u3002\u201d \u3000\u3000\u7ea2\u674f\u9053\uff1a\u201c\u90a3\u662f\u4e24\u95f4\u7a7a\u5c4b\uff0c\u6709\u4ec0\u4e48\u53ef\u770b\u7684\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u6765\u6b63\u4e2d\u4e01\u6d9b\u4e0b\u6000\uff0c\u4ed6\u63a8\u5f00\u95e8\u5411\u91cc\u4e00\u770b\uff0c\u679c\u7136\u662f\u7a7a\u7684\u3002 \u3000\u3000\u4e01\u6d9b\u54ea\u91cc\u8fd8\u6309\u637a\u5f97\u4f4f\uff0c\u56de\u8fc7\u8eab\u6765\uff0c\u4e00\u628a\u4fbf\u5c06\u7ea2\u674f\u62b1\u8d77\uff0c\u8fdb\u5165\u5c4b\u5185\u540e\uff0c\u968f\u5373\u628a\u95e8\u5173\u4e0a\u3002 \u3000\u3000\u7ea2\u674f\u60ca\u53eb\u9053\uff1a\u201c\u526f\u4f7f\u8005\uff0c\u4f60\u8981\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u628a\u7ea2\u674f\u6309\u5012\u5730\u4e0a\u5c31\u4eb2\u5634\uff0c\u3000\u4e00\u8fb9\u54e7\u54e7\u7b11\u9053\uff1a\u201c\u8fd9\u624d\u771f\u6b63\u53eb\u529e\u4e8b\uff01\u201d \u3000\u3000\u7ea2\u674f\u7aed\u529b\u6323\u624e\u7740\uff0c\u53cc\u624b\u76f4\u63a8\u4e01\u6d9b\u7684\u8116\u5b50\uff0c\u5598\u5401\u5401\u7684\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u6211\u8981\u7980\u62a5\u7edf\u987b\uff0c\u8bf4\u4f60\u6b3a \u4fae\u6211\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u6211\u662f\u5de1\u5173\u526f\u4f7f\u8005\uff0c\u5f53\u7136\u8981\u6b3a\u4fae\u4f60\u3002\u201d \u3000\u3000\u5176\u5b9e\u4e01\u6d9b\u4eca\u5929\u7684\u80c6\u5b50\u6240\u4ee5\u8fd9\u4e48\u5927\uff0c\u4e0d\u5916\u662f\u53d7\u4e86\u51e4\u5ae3\u7ea2\u7684\u6697\u793a\u548c\u9f13\u52b1\uff0c\u56e0\u4e3a\u4ed6\u65e9\u770b\u51fa\u51e4\u5ae3 \u7ea2\u662f\u4e2a\u5996\u51b6\u6deb\u8361\u7684\u5973\u4eba\uff0c\u3000\u4e0aL\u6881\u4e0d\u6b63\u4e0b\u6881\u6b6a\uff0c\u82e5\u5230\u4e86\u522b\u5904\uff0c\u4ed6\u7eb5\u7136\u8272\u80c6\u5305\u5929\uff0c\u4e5f\u4e0d\u6562\u5982\u6b64 \u4e71\u6765\u3002 \u3000\u3000\u53ea\u542c\u7ea2\u674f\u5e26\u7740\u54c0\u6c42\u7684\u58f0\u97f3\u9053\uff1a\u201c\u4f60\u522b\u6025\uff0c\u6211\u6709\u4e2a\u6761\u4ef6\uff0c\u53ea\u8981\u4f60\u505a\u5f97\u5230\uff0c\u6211\u4e0d\u4f46\u73b0\u5728\u7b54\u5e94 \u4f60\uff0c\u4ee5\u540e\u7167\u6837\u4e5f\u7b54\u5e94\u4f60\u3002\u201d \u3000\u3000\u201c\u4f60\u6709\u4ec0\u4e48\u6761\u4ef6\uff1f\u201d \u3000\u3000\u201c\u628a\u4f60\u4eec\u90a3\u8fb9\u7684\u4e8b\u3002\u968f\u65f6\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u60f3\u77e5\u9053\u6211\u4eec\u90a3\u8fb9\u7684\u4e8b\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6837\u5c31\u663e\u5f97\u6211\u5f88\u80fd\u5e72\uff0c\u5982\u679c\u6211\u80fd\u5347\u5230\u4f60\u4eec\u90a3\u8fb9\u505a\u526f\u4f7f\u8005\uff0c\u4f60\u4e0d\u5c31\u53ef\u4ee5\u5929\u5929\u548c\u6211\u5728\u4e00\u8d77 \u4e48\uff1f\u201d \u3000\u3000\u201c\u90a3\u5f88\u7b80\u5355\uff0c\u627e\u4e00\u5b9a\u53ef\u4ee5\u5e2e\u4f60\u8fd9\u4e2a\u5fd9\u3002\u201d \u3000\u3000\u4e01\u6d9b\u8bf4\u5b8c\u8bdd\uff0c\u5c31\u8feb\u4e0d\u4ece\u5f85\u7684\u8981\u4e3a\u7ea2\u674f\u89e3\u8863\u3002 \u3000\u3000\u5c82\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u95e8\u5916\u54cd\u8d77\u4e86\u811a\u6b65\u58f0\uff0c\u4f7f\u5f97\u4e01\u6d9b\u4e0d\u5f97\u4e0d\u505c\u6b62\u52a8\u4f5c\u3002 \u3000\u3000\u53ea\u542c\u7518\u9716\u7684\u58f0\u97f3\u9053\uff1a\u201c\u78a7\u6843\u7ad9\u5a18\uff0c\u4f60\u8bf4\u662f\u4e24\u95f4\u7a7a\u5c4b\uff0c\u4e3a\u4ec0\u4e48\u91cc\u9762\u4e0a\u4e86\u95e9\uff1f\u201d \u3000\u3000\u78a7\u6843\u554a\u4e86\u58f0\u9053\uff1a\u201c\u5947\u602a\uff0c\u4e0a\u5348\u95e8\u8fd8\u4e0c\u7740\uff0c\u83ab\u975e\u91cc\u9762\u6709\u4eba\uff1f\u7518\u4f7f\u8005\u4e2a\u5b50\u9ad8\u3002\u4ece\u7a97\u53e3\u5f80\u91cc\u770b \u770b\uff01\u201d \u3000\u3000\u4e01\u6d9b\u8fde\u5fd9\u62c9\u8d77\u7ea2\u674f\uff0c\u8fc5\u5feb\u6765\u5230\u9760\u7a97\u4e00\u8fb9\uff0c\u8d34\u7740\u5899\u58c1\u7ad9\u597d\u3002 \u3000\u3000\u4e00\u9762\u6697\u6697\u9a82\u9053\uff1a\u201c\u7518\u9716\uff0c\u8001\u5b50\u628a\u4f60\u8fd9\u72d7\u5a18\u517b\u7684\uff0c\u4ec0\u4e48\u5730\u65b9\u4e0d\u597d\u627e\uff0c\u504f\u504f\u4e5f\u627e\u5230\u8fd9\u91cc\u6765\uff01\u201d \u3000\u3000\u53ea\u542c\u7518\u9716\u9053\uff1a\u201c\u91cc\u800c\u6839\u672c\u6ca1\u4eba\u3002\u201d \u3000\u3000\u78a7\u6843\u9053\uff1a\u201c\u90a3\u5c31\u602a\u4e86\uff0c\u6ca1\u6709\u4eba\u600e\u4e48\u623f\u95e8\u4f1a\u4e0a\u4e86\u95e9\uff1f\u6d3b\u89c1\u9b3c\uff01\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u90a3\u5c31\u4e0d\u5fc5\u8fdb\u53bb\u4e86\uff0c\u78a7\u6843\uff0c\u6700\u597d\u5efa\u8bae\u7edf\u9886\uff0c\u628a\u8fd9\u4e24\u95f4\u7816\u5c4b\u62c6\u6389\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e3b\u8981\u62c6\u6389\uff1f\u201d \u3000\u3000\u201c\u82e5\u4e0d\u62c6\u6389\uff0c\u4f60\u4eec\u8fd9\u4e9b\u5973\u5175\uff0c\u8fdf\u65e9\u8981\u5403\u4e8f\uff0c\u4e07\u4e00\u6709\u90a3\u4e2a\u72d7\u5a18\u517b\u7684\u738b\u516b\u86cb\u89c1\u8272\u8d77\u610f\uff0c\u628a\u4eba \u63b3\u5230\u8fd9\u5730\u529b\u505a\u574f\u4e8b\uff0c\u53ef\u662f\u65b9\u4fbf\u5f97\u5f88\u3002\u201d \u3000\u3000\u201c\u4e0d\u53ef\u80fd\u5427\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e0d\u53ef\u80fd\uff0c\u4e00\u5b9a\u6709\u8fd9\u79cd\u574f\u79cd\uff01\u201d \u3000\u3000\u6b63\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u542c\u6797\u5916\u53e6\u4e00\u5973\u5b50\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u78a7\u6843\uff0c\u7edf\u9886\u53eb\u4f60\u9a6c\u4e0a\u53bb\uff0c\u8fd8\u6709\u7ea2\u674f\uff0c\u4f60\u770b \u5230\u7ea2\u674f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u78a7\u6843\u7acb\u523b\u5411\u7518\u9716\u65bd\u4e86\u4e00\u793c\u9053\uff1a\u201c\u526f\u4f7f\u8005\uff0c\u5bf9\u4e0d\u8d77\uff0c\u5931\u966a\u4e86\uff01\u201d \u3000\u3000\u7518\u9716\u4e5f\u968f\u5373\u51fa\u4e86\u6811\u6797\u3002 \u3000\u3000\u4e01\u6d9b\u4e5f\u53ea\u597d\u628a\u7ea2\u674f\u653e\u51fa\u53bb\u3002 \u3000\u3000\u7ea2\u674f\u4e34\u51fa\u95e8\u65f6\uff0c\u5411\u540e\u62db\u4e86\u62db\u624b\u9053\uff1a\u201c\u526f\u4f7f\u8005\u522b\u6cc4\u6c14\uff0c\u4e0b\u6b21\u518d\u6765\uff01\u201d \u3000\u3000\u716e\u719f\u7684\u9e2d\u5b50\u98de\u4e86\uff0c\u4e01\u6d9b\u4ece\u6ca1\u8fd9\u6837\u7a9d\u56ca\u8fc7\u3002 \u3000\u3000\u6765\u5230\u90a3\u68f5\u5927\u6811\u4e0b\uff0c\u7518\u9716\u65e9\u5df2\u7b49\u5728\u90a3\u91cc\u3002 \u3000\u3000\u4ed6\u771f\u6068\u4e0d\u5f97\u628a\u7518\u9716\u72e0\u63cd\u4e00\u987f\uff0c\u4f46\u8868\u9762\u4e0a\u8fd8\u662f\u8981\u88c5\u51fa\u4ec0\u4e48\u4e8b\u4e5f\u6ca1\u53d1\u751f\u8fc7\u7684\u6a21\u6837\u3002 \u3000\u3000\u6ca1\u522b\u7684\uff0c\u53ea\u80fd\u548c\u7518\u9716\u4e00\u8d77\u56de\u53bb\u4e86\u3002 \u3000\u3000\u5f88\u5feb\u4fbf\u662f\u5341\u51e0\u5929\u8fc7\u53bb\u3002 \u3000\u3000\u7687\u5143\u6559\u4e3b\u548c\u603b\u62a4\u6cd5\u79e6\u69d0\u7adf\u7136\u6ca1\u5f97\u5230\u80b2\u5316\u57ce\u4e3b\u4e25\u5bd2\u7684\u534a\u70b9\u6d88\u606f\u3002 \u3000\u3000\u6b64\u65f6\u4e24\u4eba\u53c8\u5728\u540e\u5bab\u5bc6\u8bae\u3002 \u3000\u3000\u7687\u5143\u6559\u4e3b\u9053\uff1a\u201c\u770b\u6765\u4ed6\u662f\u7edd\u4e0d\u53ef\u80fd\u628a\u90a3\u5973\u72af\u9001\u5230\u603b\u575b\u6765\u4e86\uff1f\u201d \u3000\u3000\u79e6\u69d0\u563f\u563f\u7b11\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u53ef\u80fd\uff0c\u73b0\u5728\u8be5\u662f\u4e0b\u624b\u7684\u65f6\u5019\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u7684\u610f\u601d\u600e\u6837\u4e0b\u624b\uff1f\u201d \u3000\u3000\u201c\u672c\u5ea7\u65e9\u5df2\u7980\u544a\u8fc7\u6559\u4e3b\uff0c\u6d3e\u4eba\u628a\u4ed6\u53ec\u5230\u603b\u575b\uff0c\u7136\u540e\u5f53\u573a\u6263\u62bc\u8d77\u6765\u3002\u81f3\u4e8e\u4ee5\u540e\u5982\u4f55\u5904\u7f6e\uff0c \u5c31\u7531\u6559\u4e3b\u51b3\u5b9a\u3002\u201d \u3000\u3000\u201c\u6d3e\u4ec0\u4e48\u4eba\u4f20\u4ee4\uff1f\u201d \u3000\u3000\u201c\u672c\u6765\u8d75\u62a4\u6cd5\u53ef\u4ee5\u53bb\uff0c\u4f46\u4ed6\u73b0\u5728\u53ea\u6015\u4e0d\u6562\u518d\u89c1\u4e25\u57ce\u4e3b\uff0c\u4e0d\u59a8\u6539\u5c71\u9ad8\u62a4\u6cd5\u53bb\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u7531\u4f60\u5b89\u6392\u4e86\uff01\u201d \u3000\u3000\u80b2\u5316\u57ce\u6700\u8fd1\u4ee5\u6765\uff0c\u5448\u73b0\u7740\u5916\u5f1b\u5185\u5f20\u7684\u5c40\u9762\u3002\u4e25\u5bd2\u548c\u5468\u6d77\u5c71\uff0c\u90fd\u5728\u968f\u65f6\u51c6\u5907\u5e94\u53d8\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9075\u7236\u4eb2\u82b1\u7389\u9e9f\u4e4b\u547d\uff0c\u5927\u90e8\u5206\u65f6\u95f4\u7559\u5728\u80b2\u5316\u57ce\uff0c\u968f\u4f8d\u4e25\u5bd2\u8eab\u8fb9\u3002 \u3000\u3000\u7531\u4e8e\u7ecf\u5e38\u5728\u5185\u5e9c\u8d70\u52a8\uff0c\u4ed6\u5df2\u89c1\u8fc7\u4e8c\u59d1\u5a18\u4e25\u5982\u971c\uff0c\u53ea\u662f\u5e76\u672a\u4ea4\u6de1\u800c\u5df2\u3002 \u3000\u3000\u4e8c\u59d1\u5a18\u4e25\u5982\u971c\u679c\u7136\u751f\u5f97\u5929\u59ff\u56fd\u8272\uff0c\u6bd4\u8d77\u5979\u7684\u59d0\u59d0\u4e25\u5982\u51b0\u4f3c\u4e4e\u72b9\u80dc\u4e09\u5206\uff0c\u4e0d\u7528\u8bf4\uff0c\u5cb3\u5c0f\u98de \u4e5f\u9887\u4e3a\u4e2d\u610f\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e25\u5bd2\u548c\u5468\u6d77\u5c71\u3001\u5cb3\u5c0f\u98de\u6b63\u5728\u5927\u5385\uff0c\u5ffd\u542c\u5b88\u57ce\u5f1f\u5144\u6765\u62a5\uff1a\u603b\u575b\u62a4\u6cd5\u9ad8\u5e84\u524d\u6765\u62dc\u89c1\u57ce \u4e3b\u3002 \u3000\u3000\u4e25\u5bd2\u968f\u5373\u4ea4\u4ee3\u5468\u6d77\u5c71\uff0c\u6309\u9884\u5b9a\u8ba1\u5212\u8fdb\u884c\uff0c\u7136\u540e\u81ea\u5df1\u907f\u5165\u5185\u5ba4\u3002 \u3000\u3000\u4e0d\u4e45\u4e4b\u540e\uff0c\u603b\u575b\u62a4\u6cd5\u9ad8\u5e84\u679c\u7136\u5df2\u6765\u5230\u5927\u5385\u3002 \u3000\u3000\u5468\u6d77\u5c71\u548c\u5cb3\u5c0f\u98de\u8fde\u5fd9\u4e0b\u9636\u8fce\u63a5\u3002 \u3000\u3000\u6b64\u523b\u7684\u5cb3\u5c0f\u98de\uff0c\u4e0d\u6d88\u8bf4\u65e9\u5df2\u6234\u4e0a\u4eba\u76ae\u9762\u5177\u3002 \u3000\u3000\u8fd9\u4f4d\u62a4\u6cd5\u9ad8\u5e84\uff0c\u5e74\u5728\u56db\u65ec\u4e0a\u4e0b\uff0c\u7ec6\u7709\u7ec6\u773c\uff0c\u770b\u6765\u9887\u4e3a\u9634\u6c89\u3002 \u3000\u3000\u9ad8\u5e84\u5c31\u5750\u540e\uff0c\u5468\u6d77\u5c71\u4eb2\u81ea\u5949\u8336\uff0c\u3000\u2014\u9762\u95ee\u9053\uff1a\u201c\u9ad8\u62a4\u6cd5\u9a7e\u4e34\u672c\u57ce\uff0c\u4e0d\u77e5\u6709\u4ec0\u4e48\u6559\u8c15\uff1f\u201d \u3000\u3000\u9ad8\u5e84\u53cc\u76ee\u7cbe\u5149\u707c\u707c\uff0c\u5728\u5927\u5385\u626b\u89c6\u4e86\u4e00\u773c\u9053\uff1a\u201c\u9ad8\u67d0\u662f\u5949\u547d\u6765\u62dc\u89c1\u4e25\u57ce\u4e3b\u7684\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u89c1 \u4ed6\u8001\u4eba\u5bb6\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u76b1\u7709\u53f9\u4e86\u53e3\u6c14\u9053\uff1b\u201c\u539f\u6765\u9ad8\u62a4\u6cd5\u8fd8\u4e0d\u77e5\u9053\uff0c\u57ce\u4e3b\u4ed6\u8001\u4eba\u5bb6\u75c5\u4e86\u3002\u201d \u3000\u3000\u201c\u75c5\u4e86\uff1f\u201d\u9ad8\u5e84\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u75c5\u7684\uff1f\u201d \u3000\u3000\u201c\u5df2\u7ecf\u4e24\u3001\u4e09\u5929\u4e86\u3002\u201d \u3000\u3000\u201c\u603b\u575b\u600e\u4e48\u4e00\u70b9\u6d88\u606f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8001\u4eba\u5bb6\u751f\u75c5\uff0c\u4ece\u6765\u6ca1\u5411\u603b\u575b\u62a5\u5907\u8fc7\u3002\u201d \u3000\u3000\u9ad8\u5e84\u8dfa\u4e86\u8dfa\u811a\u9053\uff1a\u201c\u771f\u662f\u7cdf\u7cd5\uff0c\u9ad8\u67d0\u662f\u5949\u6559\u4e3b\u4e4b\u547d\uff0c\u8bf7\u4e25\u57ce\u4e3b\u5230\u603b\u575b\u6709\u8981\u4e8b\u76f8\u5546\uff0c\u4ed6\u8fd9 \u4e00\u75c5\uff0c\u9ad8\u67d0\u5c31\u65e0\u6cd5\u56de\u53bb\u7f34\u4ee4\u4e86\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u6ca1\u529e\u6cd5\u7684\u4e8b\uff0c\u9ad8\u62a4\u6cd5\u56de\u53bb\u53ea\u8981\u5b9e\u8bdd\u5b9e\u8bf4\uff0c\u6559\u4e3b\u53c8\u5982\u4f55\u80fd\u89c1\u602a\u60a8\u3002\u201d \u3000\u3000\u9ad8\u5e84\u7565\u4e00\u72b9\u8c6b\u9053\uff1a\u201c\u4e25\u57ce\u4e3b\u75c5\u5f97\u5f88\u91cd\u4e48\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u82e6\u7b11\u9053\uff1a\u201c\u5982\u679c\u4e0d\u91cd\uff0c\u5468\u67d0\u5c31\u5c3d\u53ef\u901a\u62a5\u4ed6\uff0c\u8ba9\u4ed6\u8001\u4eba\u5bb6\u968f\u9ad8\u62a4\u6cd5\u5230\u603b\u575b\u4e86\u3002\u201d \u3000\u3000\u9ad8\u5e84\u8d77\u8eab\u9053\uff1a\u201c\u90a3\u4e48\u9ad8\u67d0\u5e94\u5f53\u5230\u91cc\u9762\u53bb\u63a2\u671b\u63a2\u671b\u4ed6\u8001\u4eba\u5bb6\uff0c\u987a\u4fbf\u7ed9\u4ed6\u8001\u4eba\u5bb6\u8bf7\u4e2a\u5b89\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u4e0d\u514d\u5927\u4e3a\u7d27\u5f20\uff0c\u82e5\u8ba9\u5bf9\u65b9\u5f97\u89c1\u57ce\u4e3b\uff0c\u5c82\u4e0d\u5f53\u573a\u9732\u51fa\u9a6c\u811a\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u8981\u6ca1\u6cd5\u628a\u5bf9 \u65b9\u62e6\u4f4f\u624d\u6210\u3002 \u3000\u3000\u4ed6\u8fde\u5fd9\u62b1\u4e86\u62b1\u62f3\u9053\uff1a\u201c\u591a\u8c22\u9ad8\u6269\u6cd5\u5bf9\u57ce\u4e3b\u7684\u5173\u6000\uff0c\u4e0d\u8fc7\u57ce\u4e3b\u65e9\u6253\u4ea4\u4ee3\uff0c\u75c5\u4e2d\u8c22\u7edd\u4e00\u5207\u8bbf \u5ba2\uff0c\u6700\u597d\u522b\u6253\u6270\u4ed6\u8001\u4eba\u5bb6\u7684\u6e05\u9759\u3002\u201d \u3000\u3000\u9ad8\u5e84\u5927\u4e3a\u4e0d\u60a6\u9053\uff1a\u201c\u5468\u603b\u7ba1\u8fd9\u6837\u8bf4\u8bdd\uff0c\u672a\u514d\u592a\u89c1\u5916\u4e86\uff0c\u9ad8\u67d0\u662f\u53bb\u63a2\u75c5\uff0c\u5e76\u975e\u4e00\u822c\u8bbf\u5ba2\uff0c \u53c8\u6709\u4ec0\u4e48\u89c1\u4e0d\u5f97\u7684\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u6b63\u5728\u65e0\u6cd5\u5e94\u4ed8\uff0c\u5ffd\u7136\u5cb3\u5c0f\u98de\u8d70\u4e0a\u524d\u6765\uff0c\u62f1\u624b\u4e00\u793c\u9053\uff1a \u3000\u3000\u201c\u9ad8\u62a4\u6cd5\u8fd8\u662f\u522b\u53bb\u6253\u6270\u57ce\u4e3b\u7684\u597d\uff0c\u4ed6\u8001\u4eba\u5bb6\u4eca\u5929\u4e00\u65e9\u5c31\u4ea4\u4ee3\u8fc7\u5728\u4e0b\uff0c\u4efb\u4f55\u5ba2\u4eba\u4e00\u6982\u514d \u89c1\uff01\u201d \u3000\u3000\u9ad8\u5e84\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u672c\u62a4\u6cd5\u597d\u50cf\u4ece\u6ca1\u89c1\u5230\u4f60\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6717\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u662f\u57ce\u4e3b\u65b0\u6536\u7684\u9a7e\u524d\u62a4\u536b\uff0c\u4f60\u5f53\u7136\u4e0d\u8ba4\u8bc6\uff0c\u5176\u5b9e\u5728\u4e0b\u53c8\u4f55\u5c1d\u8ba4\u8bc6 \u4f60\uff01\u201d \u3000\u3000\u9ad8\u5e84\u89c1\u5cb3\u5c0f\u98de\u8eab\u6750\u77ee\u5c0f\uff0c\u8bba\u8eab\u4efd\u4e0d\u8fc7\u662f\u4e00\u540d\u5c0f\u5c0f\u62a4\u536b\uff0c\u5bf9\u81ea\u5df1\u8bf4\u8bdd\u7adf\u662f\u5982\u6b64\u8bed\u6c14\uff0c\u5982\u4f55 \u5fcd\u5f97\u4e0b\u8fd9\u53e3\u6c14\uff0c\u5927\u559d\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\u7684\u4e1c\u897f\uff0c\u7adf\u6562\u5728\u672c\u62a4\u6cd5\u9762\u524d\u5982\u6b64\u653e\u8086\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6602\u7136\u9053\uff1a\u201c\u5728\u4e0b\u7684\u804c\u8d23\uff0c\u5c31\u662f\u7ef4\u62a4\u57ce\u4e3b\uff0c\u57ce\u4e3b\u65e2\u7136\u4ea4\u5f85\u4e0b\u6765\u4e0d\u5f97\u6709\u4eba\u6253\u6270\uff0c\u5728\u4e0b \u5c31\u5fc5\u987b\u8d2f\u5f7b\u6267\u884c\uff0c\u4f60\u5374\u504f\u504f\u4e0d\u542c\uff0c\u5230\u5e95\u662f\u54ea\u4e2a\u653e\u8086\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u867d\u62c5\u5fc3\u628a\u4e8b\u60c5\u95f9\u5927\uff0c\u4f46\u4ed6\u56fd\u65e0\u6cd5\u963b\u6b62\u9ad8\u5e84\uff0c\u4e5f\u5c31\u53ea\u6709\u4efb\u7531\u5cb3\u5c0f\u98de\u5904\u7f6e\u4e86\u3002 \u3000\u3000\u7a81\u89c1\u9ad8\u5e84\u626c\u624b\u4e00\u638c\uff0c\u76f4\u5411\u5cb3\u5c0f\u98de\u5f53\u5934\u5288\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u53f3\u81c2\u75be\u63a2\uff0c\u6b63\u597d\u6263\u4f4f\u4e86\u5bf9\u65b9\u8155\u8109\uff0c\u9ad8\u53eb\u9053\uff1a\u201c\u9ad8\u62a4\u6cd5\u3001\u4f60\u60f3\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9ad8\u5e84\u672c\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u4e07\u6ca1\u6599\u5230\u5288\u51fa\u4e00\u638c\uff0c\u4e0d\u4f46\u6ca1\u51fb\u4e2d\uff0c\u53cd\u800c\u88ab\u4eba\u64d2\u4f4f\u8155\u8109\uff0c\u65e2\u60ca\u53c8 \u6124\u4e4b\u4e0b\uff0c\u53f3\u8155\u62da\u529b\u5411\u540e\u4e00\u6296\uff0c\u51c6\u5907\u5148\u64a4\u56de\u624b\u6765\u518d\u8bf4\u3002 \u3000\u3000\u5c82\u77e5\u4ed6\u90a3\u53f3\u8155\uff0c\u7adf\u50cf\u88ab\u6263\u4f4f\u4e00\u9053\u94c1\u7b8d\uff0c\u800c\u4e14\u6709\u8d8a\u6536\u8d8a\u7d27\u4e4b\u52bf\uff0c\u6e10\u6e10\u5df2\u4f7f\u5f97\u4ed6\u4f53\u5185\u6c14\u8840\u9006 \u51b2\uff0c\u5168\u8eab\u7b4b\u529b\u5c3d\u706b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u4e3a\u5df2\u751a\uff0c\u4e00\u6296\u624b\uff0c\u968f\u5373\u677e\u5f00\u4e94\u6307\u3002 \u3000\u3000\u9ad8\u5e84\u7ad9\u811a\u4e0d\u4f4f\uff0c\u8ddf\u8dc4\u5411\u540e\u6454\u51fa\u4e94\u516d\u6b65\uff0c\u624d\u62ff\u6869\u7ad9\u4f4f\u3002 \u3000\u3000\u4ed6\u4f5c\u68a6\u6ca1\u60f3\u5230\uff0c\u8eab\u4e3a\u603b\u575b\u6269\u6cd5\uff0c\u53c8\u5177\u6709\u2014\u8eab\u8d85\u51e1\u7edd\u4fd7\u7684\u6b66\u529f\uff0c\u4eca\u5929\u5c45\u7136\u683d\u5728\u4e00\u4e2a\u8eab\u6750\u77ee \u5c0f\u540d\u4e0d\u89c1\u7ecf\u4f20\u7684\u5c0f\u4eba\u7269\u624b\u91cc\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u59d3\u9ad8\u7684\uff0c\u4f60\u82e5\u77e5\u8da3\uff0c\u5c31\u89c4\u89c4\u77e9\u77e9\u7684\u5f85\u5728\u5927\u5385\u91cc\uff0c\u81f3\u5c11\u8fd8\u6709\u676f\u8336\u7ed9\u4f60\u559d\uff0c\u82e5 \u786c\u8981\u5f80\u91cc\u95ef\uff0c\u90a3\u662f\u81ea\u8ba8\u82e6\u5403\uff01\u201d \u3000\u3000\u9ad8\u5e84\u970d\u5730\u62d4\u51fa\u4f69\u5251\uff0c\u8dc3\u5230\u5927\u5385\u5e7f\u573a\u4e0a\uff0c\u5251\u6307\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u9ad8\u67d0\u4e0d\u4fe1\u80fd\u7531\u5f97\u4f60\u7316\u72c2\uff0c\u6709\u80c6 \u91cf\u5c31\u51fa\u6765\u9886\u6b7b\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u633a\u7740\u80f8\uff0c\u6602\u7136\u51fa\u4e86\u5927\u5385\uff0c\u201c\u545b\u5577\u201d\u4e00\u58f0\uff0c\u957f\u5251\u4e5f\u51fa\u4e86\u9798\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u59d3\u9ad8\u7684\uff0c \u51fa\u62db\u5427\uff01\u201d \u3000\u3000\u9ad8\u5e84\u987f\u65f6\u98de\u8eab\u75be\u6251\u800c\u6765\uff0c\u5251\u51fa\u5982\u7535\uff0c\u76f4\u523a\u5411\u5cb3\u5c0f\u98de\u54bd\u5589\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u62a1\u5251\u659c\u659c\u8fce\u53bb\u3002 \u3000\u3000\u4ed6\u51fa\u62db\u770b\u6765\u5e76\u4e0d\u8fc5\u6377\u3002\u4f46\u5374\u6b63\u597d\u628a\u5bf9\u65b9\u6765\u52bf\u622a\u4f4f\u3002 \u3000\u3000\u53ea\u542c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u65e9\u628a\u523a\u6765\u4e00\u5251\u51fb\u504f\u3002 \u3000\u3000\u5c82\u77e5\u9ad8\u5e84\u5e76\u672a\u540e\u9000\uff0c\u5374\u8d81\u673a\u518d\u62cd\u51fa\u4e00\u638c\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u51b3\u5b9a\u548c\u4ed6\u5bf9\u62da\u638c\u529b\uff0c\u5de6\u624b\u4e5f\u62cd\u51fa\u4e00\u638c\u3002 \u3000\u3000\u53cc\u65b9\u638c\u52bf\u4e00\u63a5\uff0c\u5148\u4f3c\u80f6\u7740\uff0c\u4f46\u77ac\u95f4\u4e4b\u540e\uff0c\u9ad8\u5e84\u5df2\u624e\u6869\u4e0d\u4f4f\uff0c\u76f4\u88ab\u9707\u9000\u4e00\u4e08\u591a\u8fdc\u3002 \u3000\u3000\u8c01\u77e5\u9ad8\u5e84\u5e76\u4e0d\u8c03\u606f\uff0c\u5374\u6324\u7740\u4e00\u53e3\u4f59\u6c14\uff0c\u95ea\u7535\u822c\u53c8\u6251\u4e0a\u6765\uff0c\u5251\u5288\u5411\u5cb3\u5c0f\u98de\u5de6\u80f8\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7a33\u7ad9\u539f\u5730\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u4f3c\u662f\u6beb\u4e0d\u5728\u610f\u822c\u8fce\u51fa\u4e00\u5251\u3002 \u3000\u3000\u4e00\u58f0\u91d1\u94c1\u4ea4\u51fb\uff0c\u9ad8\u5e84\u7684\u5251\uff0c\u751f\u751f\u88ab\u9707\u5f97\u8131\u624b\u98de\u51fa\uff0c\u8fde\u4eba\u4e5f\u6454\u51fa\u53bb\u8db3\u8db3\u4e00\u4e08\u5f00\u5916\u3002 \u3000\u3000\u8fd9\u65f6\u9ad8\u5e84\u7ec8\u4e8e\u5927\u60ca\u5931\u8272\u3002 \u3000\u3000\u5468\u6d77\u5c71\u8fde\u5fd9\u8fc7\u53bb\u6276\u4f4f\u9ad8\u5e84\u9053\uff1a\u201c\u8fd9\u4f4d\u5144\u5f1f\u4e0d\u61c2\u4e8b\uff0c\u9ad8\u62a4\u6cd5\u5343\u4e07\u539f\u8c05\uff01\u201d \u3000\u3000\u63a5\u7740\u53c8\u53bb\u628a\u5730\u4e0a\u7684\u5251\u6361\u8d77\uff0c\u8fd8\u7ed9\u9ad8\u5e84\u3002 \u3000\u3000\u9ad8\u5e84\u90a3\u91cc\u8fd8\u8bf4\u5f97\u51fa\u8bdd\uff0c\u4e00\u8f6c\u8eab\u4fbf\u5feb\u6b65\u800c\u53bb\u3002 \u3000\u3000\u5468\u6d77\u5c71\u76f4\u628a\u4ed6\u9001\u5230\u57ce\u95e8\u53e3\u624d\u56de\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4ecd\u5728\u5927\u5385\u91cc\u3002 \u3000\u3000\u5468\u6d77\u5c71\u57cb\u6028\u9053\uff1a\u201c\u5cb3\u516c\u5b50\uff0c\u4f60\u8fd9\u4e00\u6765\u4e8b\u60c5\u95f9\u5927\u4e86\uff0c\u4ed6\u662f\u603b\u575b\u62a4\u6cd5\uff0c\u6253\u4e86\u603b\u575b\u62a4\u6cd5\uff0c\u5c82\u4e0d \u7b49\u4e8e\u9020\u53cd\u4e00\u6837\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u2014\u4eba\u505a\u4e8b\u4e00\u4eba\u5f53\uff0c\u6211\u7edd\u4e0d\u8fde\u7d2f\u4efb\u4f55\u4eba\uff01\u201d \u3000\u3000\u5468\u6d77\u5c71\u82e6\u7b11\u9053\uff1a\u201c\u8bdd\u867d\u8fd9\u4e48\u8bf4\uff0c\u4f46\u8d23\u4efb\u5374\u5df2\u843d\u5728\u57ce\u4e3b\u8eab\u4e0a\uff0c\u6559\u4e3b\u8981\u627e\u7684\u662f\u57ce\u4e3b\uff0c\u7f6a\u540d\u4e5f \u662f\u57ce\u4e3b\u8eab\u4e0a\uff0c\u7edd\u4e0d\u662f\u4f60\u81ea\u5df1\u51fa\u9762\u5c31\u4e86\u4e8b\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7ec8\u4e8e\u89c9\u51fa\u8fd9\u573a\u7978\u679c\u7136\u95ef\u5927\u4e86\uff0c\u4ed6\u81ea\u5df1\u5373\u4f7f\u9001\u4e0a\u4e00\u547d\u4e5f\u5728\u6240\u4e0d\u60dc\uff0c\u4f46\u53c8\u5982\u4f55\u5411\u4e25\u5bd2 \u4ea4\u4ee3\uff1f \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e25\u5bd2\u5df2\u5728\u5927\u5385\u51fa\u73b0\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8fde\u5fd9\u8eac\u8eab\u65bd\u793c\uff0c\u4f4e\u4e0b\u5934\u9053\uff1a\u201c\u665a\u8f88\u4e3a\u57ce\u4e3b\u95ef\u4e0b\u4e86\u5927\u7978\uff0c\u60c5\u613f\u8bf7\u57ce\u4e3b\u6cbb\u4ee5\u5e94\u5f97\u4e4b \u7f6a\uff01\u201d \u3000\u3000\u4e25\u5bd2\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u65b9\u624d\u4f60\u6253\u4e86\u9ad8\u5e84\u7684\u7ecf\u8fc7\uff0c\u8001\u592b\u5168\u5df2\u77e5\u6653\uff0c\u4e8b\u5230\u5982\u4eca\uff0c\u4f60\u53c8\u4f55\u5fc5\u8bf4\u8fd9 \u79cd\u8bdd\uff0c\u6211\u5373\u4f7f\u6cbb\u4f60\u7684\u7f6a\uff0c\u5927\u5c40\u4e5f\u662f\u65e0\u6cd5\u633d\u6551\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1b\u201c\u57ce\u4e3b\u4e0d\u59a8\u628a\u665a\u8f88\u6346\u7ed1\u8d77\u6765\u89e3\u9001\u603b\u575b\uff0c\u53ea\u8981\u80fd\u4fdd\u57ce\u4e3b\u65e0\u4e8b\uff0c\u665a\u8f88\u5373\u4f7f\u53d7\u4e94\u9a6c \u5206\u5c38\u4e4b\u5211\uff0c\u4e5f\u5fc3\u7518\u60c5\u613f\u3002\u201d \u3000\u3000\u4e25\u5bd2\u957f\u957f\u5401\u53e3\u6c14\u9053\uff1a\u201c\u4f55\u5fc5\u518d\u8bf4\u8fd9\u4e9b\uff0c\u8001\u592b\u5df2\u51b3\u5b9a\u4ece\u73b0\u5728\u8d77\uff0c\u548c\u7687\u5143\u6559\u603b\u575b\u6b63\u5f0f\u5bf9\u6297\uff01\u201d \u3000\u3000\u6b64\u8bed\u4e00\u51fa\uff0c\u5468\u6d77\u5c71\u548c\u5cb3\u5c0f\u98de\u5168\u6709\u76ee\u77aa\u53e3\u5446\u4e4b\u611f\u3002 \u3000\u3000\u591a\u65f6\uff0c\u5468\u6d77\u5c71\u624d\u5411\u524d\u8d70\u4e86\u51e0\u6b65\uff0c\u8bed\u6c14\u5145\u6ee1\u6fc0\u52a8\u9053\uff1a\u201c\u7980\u57ce\u4e3b\uff0c\u4f60\u8001\u4eba\u5bb6\u4e0d\u96be\u60f3\u5230\uff0c\u672c\u57ce \u548c\u603b\u575b\u65b9\u9762\u5b9e\u529b\u76f8\u5dee\u60ac\u6b8a\uff0c\u8fd9\u6837\u505a\u5c82\u4e0d\u773c\u89c1\u8981\u7389\u77f3\u4ff1\u711a\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u4ef0\u8d77\u8138\u6765\uff0c\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5176\u5b9e\u8001\u592b\u5373\u4f7f\u4e0d\u548c\u603b\u575b\u5bf9\u6297\uff0c\u8fdf\u65e9\u4e5f\u8981\u906d\u5230\u5979\u7684\u6bd2\u624b\uff0c \u8d77\u800c\u5bf9\u6297\uff0c\u4e5f\u8bb8\u8fd8\u6709\u6b7b\u91cc\u9003\u751f\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u57ce\u4e3b\u53ef\u662f\u51c6\u5907\u5fc5\u8981\u65f6\u7531\u79d8\u9053\u9000\u51fa\u8c37\u5916\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u672c\u57ce\u5f00\u51ff\u7684\u8fd9\u6761\u79d8\u9053\uff0c\u603b\u575b\u65b9\u9762\u81f3\u4eca\u65e0\u4e00\u4eba\u77e5\u9053\uff0c\u53ea\u8981\u9003\u5230\u8c37\u5916\uff0c\u81f3 \u5c11\u6682\u65f6\u603b\u575b\u65b9\u9762\u8fd8\u97ad\u957f\u83ab\u53ca\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u6c89\u541f\u4e86\u534a\u664c\u9053\uff1a\u201c\u7075\u5802\u91cc\u7684\u90a3\u4e9b\u670d\u8fc7\u836f\u7269\u7684\u6b66\u6797\u4eba\u7269\uff0c\u662f\u5426\u73b0\u5728\u8be5\u628a\u4ed6\u4eec\u5f04\u6d3b\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u90a3\u4e9b\u4eba\u5373\u4f7f\u73b0\u5728\u5f00\u6d3b\uff0c\u77ed\u65f6\u95f4\u5185\u4e5f\u65e0\u6cd5\u5b8c\u5168\u6062\u590d\u529f\u529b\uff0c\u4f55\u51b5\u4ed6\u4eec\u5e76\u4e0d\u4e00\u5b9a\u4f1a \u5e2e\u7740\u8001\u592b\u5bf9\u6297\u603b\u575b\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u4ee5\u5c5e\u4e0b\u9884\u6599\uff0c\u603b\u575b\u65b9\u9762\uff0c\u53ef\u80fd\u9a6c\u4e0a\u8fd8\u6709\u4eba\u6765\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9894\u9996\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u5b9a\u7684\uff0c\u800c\u4e14\u6765\u4eba\u5fc5\u5b9a\u8eab\u4efd\u5730\u4f4d\u6781\u9ad8\u3002\u201d \u3000\u3000\u201c\u6765\u4e86\u4ee5\u540e\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u6682\u65f6\u5148\u6577\u884d\u7740\u4ed6\uff0c\u975e\u5230\u5fc5\u8981\uff0c\u522b\u5f53\u573a\u95f9\u7ffb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u82e5\u4ed6\u4eec\u6b64\u6c42\u662f\u628a\u665a\u8f88\u62bc\u89e3\u5230\u603b\u575b\u4e0a\u5462\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u90a3\u5c31\u975e\u5f53\u573a\u95f9\u7ffb\u4e0d\u53ef\u4e86\uff0c\u5c06\u6765\u548c\u7687\u5143\u6559\u5bf9\u6297\uff0c\u4f60\u662f\u8001\u592b\u624b\u4e0b\u6700\u4e3b\u8981\u7684\u4e00\u5458\u6218 \u5c06\uff0c\u5c82\u80fd\u8ba9\u4ed6\u4eec\u62bc\u89e3\u5230\u603b\u575b\u53bb\u3002\u201d \u3000\u3000\u63a5\u7740\uff0c\u4e25\u5bd2\u53c8\u5490\u5490\u5468\u6d77\u5c71\u4f20\u8c15\u5168\u57ce\u5404\u5904\u52a0\u5f3a\u6212\u5907\uff0c\u7136\u540e\u5219\u53c8\u5e26\u7740\u5cb3\u5c0f\u98de\u4eb2\u81ea\u5230\u5404\u5904\u5de1\u89c6 \u4e86\u4e00\u904d\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u521a\u56de\u6765\u4e0d\u4e45\uff0c\u4fbf\u6709\u4eba\u6765\u62a5\u603b\u62a4\u6cd5\u79e6\u69d0\u4eb2\u7387\u4e24\u540d\u62a4\u6cd5\u8fdb\u57ce\u6765\u4e86\u3002 \u3000\u3000\u4e25\u5bd2\u5429\u5490\u9053\uff1a\u201c\u8001\u592b\u6682\u5728\u4e66\u623f\u9694\u58c1\u5367\u5ba4\u88c5\u75c5\uff0c\u5c31\u7531\u5468\u603b\u7ba1\u62db\u5f85\u4ed6\u4eec\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u4e07\u4e00\u4ed6\u4eec\u8981\u5230\u91cc\u9762\u63a2\u671b\u4f60\u8001\u4eba\u5bb6\u5462\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u5c31\u5e26\u4ed6\u4eec\u5230\u4e66\u623f\u770b\u6211\u53bb\u3002\u201d \u3000\u3000\u518d\u5429\u5490\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5cb3\u516c\u5b50\u6682\u907f\u4e00\u4e0b\uff0c\u7b49\u4ed6\u4eec\u627e\u4f60\u65f6\u518d\u6765\u3002\u201d \u3000\u3000\u4e25\u5bd2\u548c\u5cb3\u5c0f\u98de\u8d70\u540e\uff0c\u5ba2\u5385\u91cc\u53ea\u5269\u4e0b\u5468\u6d77\u5c71\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u4e0d\u4e45\uff0c\u5927\u59d1\u5a18\u4e25\u5982\u51b0\u6765\u5230\u4e86\u5927\u5385\u3002 \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u5927\u59d1\u5a18\u53ef\u662f\u5949\u57ce\u4e3b\u4e4b\u547d\u6765\u4ea4\u4ee3\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u4e25\u5982\u51b0\u9053\uff1a\u201c\u5bb6\u7236\u8981\u6211\u6765\u548c\u5468\u5927\u53d4\u4e00\u8d77\u63a5\u5f85\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4e25\u5982\u51b0\u795e\u8272\u5341\u5206\u51dd\u91cd\uff0c\u663e\u7136\u5979\u5df2\u5f97\u77e5\u80b2\u5316\u57ce\u5373\u5c06\u906d\u5230\u5de8\u53d8\u3002 \u3000\u3000\u603b\u62a4\u6cd5\u79e6\u69d0\u5f88\u5feb\u4fbf\u6025\u5306\u5306\u7684\u6765\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u8138\u8272\u50cf\u7f69\u4e86\u4e00\u5c42\u5bd2\u971c\u3002 \u3000\u3000\u968f\u5728\u79e6\u69d0\u8eab\u540e\u7684\u4e24\u540d\u62a4\u6cd5\uff0c\u9664\u4e86\u521a\u624d\u5403\u8fc7\u4e8f\u72fc\u72c8\u800c\u53bb\u7684\u9ad8\u5e84\u5916\uff0c\u53e6\u4e00\u4e2a\u4e5f\u662f\u65e5\u524d\u6765\u8fc7\u591a \u6b21\u7684\u8d75\u660e\u6708\u3002 \u3000\u3000\u5927\u59d1\u5a18\u4e25\u5982\u51b0\u548c\u603b\u7ba1\u5468\u6d77\u5c71\u8fde\u5fd9\u8fce\u5c06\u4e0a\u53bb\u3002 \u3000\u3000\u79e6\u69d0\u8fde\u793c\u90fd\u672a\u56de\uff0c\u4fbf\u5f84\u81ea\u8fdb\u5165\u5927\u5385\u5750\u4e0b\u3002 \u3000\u3000\u8d75\u660e\u6708\u548c\u9ad8\u5e84\u4e5f\u5750\u5728\u4e00\u65c1\u3002 \u3000\u3000\u79e6\u69d0\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u65b9\u624d\u6253\u4e86\u9ad8\u62a4\u6cd5\u7684\u5c0f\u5b50\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u54c8\u7740\u8170\uff0c\u4f4e\u58f0\u4e0b\u6c14\u7684\u9053\uff1a\u201c\u4ed6\u77e5\u9053\u95ef\u4e86\u5927\u7978\uff0c\u4e0d\u77e5\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u79e6\u69d0\u731b\u5730\u62cd\u4e86\u684c\u5b50\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u7b80\u76f4\u9020\u53cd\u4e86\uff0c\u5c45\u7136\u6709\u4eba\u6562\u6253\u603b\u575b\u6269\u6cd5\uff0c\u4e25\u57ce\u4e3b\u5982\u6b64 \u7eb5\u5bb9\u90e8\u4e0b\uff0c\u53ef\u89c1\u4ed6\u662f\u5f53\u771f\u4e0d\u628a\u6559\u4e3b\u653e\u5728\u773c\u91cc\uff01\u201d \u3000\u3000\u5468\u6d77\u5c71\u966a\u7b11\u9053\uff1a\u201c\u7980\u603b\u62a4\u6cd5\uff0c\u4f60\u8001\u4eba\u5bb6\u4e0d\u80fd\u57cb\u6028\u57ce\u4e3b\uff0c\u53d1\u751f\u8fd9\u4e8b\u65f6\uff0c\u57ce\u4e3b\u4e0d\u5728\u9762\u524d\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u4e25\u57ce\u4e3b\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u5351\u804c\u5f53\u65f6\u5df2\u7ecf\u544a\u8bc9\u8fc7\u9ad8\u62a4\u6cd5\u57ce\u4e3b\u75c5\u5f97\u5f88\u91cd\u3002\u201d \u3000\u3000\u201c\u771f\u5de7\uff0c\u9ad8\u62a4\u6cd5\u4e00\u6765\uff0c\u4f60\u4eec\u57ce\u4e3b\u5c31\u75c5\u4e86\uff01\u201d \u3000\u3000\u201c\u603b\u62a4\u6cd5\u4e0d\u80fd\u8fd9\u6837\u8bb2\u8bdd\uff0c\u4eba\u4ec0\u4e48\u65f6\u5019\u751f\u75c5\uff0c\u54ea\u80fd\u7531\u81ea\u5df1\u51b3\u5b9a\u3002\u201d \u3000\u3000\u79e6\u69d0\u559d\u9053\uff1a\u201c\u4f4f\u5634\uff0c\u4e00\u4e2a\u5c0f\u5c0f\u80b2\u5316\u57ce\u603b\u7ba1\uff0c\u6562\u9876\u649e\u672c\u5ea7\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u9762\u8272\u94c1\u9752\uff0c\u9053\uff1a\u201c\u5351\u804c\u53ea\u662f\u5b9e\u8bdd\u5b9e\u8bf4\uff0c\u600e\u6562\u9876\u649e\u603b\u62a4\u6cd5\u3002\u201d \u3000\u3000\u79e6\u69d0\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u90a3\u4e48\u9ad8\u62a4\u6cd5\u8981\u5230\u91cc\u9762\u770b\u671b\u4e25\u57ce\u4e3b\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u62e6\u963b\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u56e0\u4e3a\u57ce\u4e3b\u5df2\u6709\u4ea4\u4ee3\uff0c\u4e0d\u51c6\u4efb\u4f55\u4eba\u6253\u6270\u3002\u201d \u3000\u3000\u201c\u80e1\u8bf4\uff0c\u63a2\u89c6\u75c5\u4eba\u600e\u80fd\u8bf4\u6210\u6253\u6270\uff1f\u201d \u3000\u3000\u201c\u540e\u6765\u9ad8\u62a4\u6cd5\u975e\u53bb\u4e0d\u53ef\uff0c\u51fa\u624b\u62e6\u963b\u7684\u4e5f\u5e76\u4e0d\u662f\u5351\u804c\u3002\u201d \u3000\u3000\u201c\u672c\u5ea7\u660e\u767d\uff0c\u662f\u6253\u4e86\u9ad8\u62a4\u6cd5\u7684\u90a3\u5c0f\u5b50\uff01\u90a3\u5c0f\u5b50\u5230\u5e95\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u5351\u804c\u9a6c\u4e0a\u6d3e\u4eba\u627e\u53bb\uff0c\u4ed6\u7edd\u4e0d\u53ef\u80fd\u9003\u5c71\u6b64\u57ce\u3002\u201d \u3000\u3000\u79e6\u69d0\u51b7\u51b7\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u4e25\u57ce\u4e3b\u53ef\u662f\u771f\u75c5\u4e86\uff1f\u201d \u3000\u3000\u4e25\u5982\u51b0\u5b9e\u5728\u6c14\u4e0d\u8fc7\u79e6\u69d0\u8fd9\u79cd\u76db\u6c14\u51cc\u4eba\u6a21\u6837\uff0c\u63a5\u8fc7\u8154\u6765\u9053\uff1a\u201c\u603b\u62a4\u6cd5\uff0c\u4f60\u662f\u603b\u575b\u7684\u7b2c\u4e8c\u53f7 \u5927\u4eba\u7269\uff0c\u6211\u4eec\u8c01\u6562\u9a97\u60a8\uff01\u201d \u3000\u3000\u79e6\u69d0\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u4e48\u672c\u5ea7\u60f3\u5230\u91cc\u9762\u63a2\u671b\u63a2\u671b\u4ed6\uff0c\u53ef\u4e0d\u53ef\u4ee5\u5462\uff1f\u201d \u3000\u3000\u4e25\u5982\u51b0\u9053\uff1a\u201c\u5f53\u7136\u53ef\u4ee5\uff0c\u6211\u4eec\u5c31\u662f\u501f\u5929\u505a\u80c6\u5b50\uff0c\u4e5f\u4e0d\u6562\u6321\u60a8\u603b\u62a4\u6cd5\u7684\u9a7e\uff01\u201d \u3000\u3000\u79e6\u69d0\u563f\u563f\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u5927\u59d1\u5a18\u597d\u4e00\u5f20\u4f1a\u8bf4\u8bdd\u7684\u5634\u5df4\uff0c\u79f0\u5f97\u4e0a\u6709\u5176\u7236\u5fc5\u6709\u5176\u5973\u4e86\uff01\u201d \u3000\u3000\u4e25\u5982\u51b0\u9053\uff1a\u201c\u665a\u8f88\u82e5\u4f1a\u8bf4\u8bdd\uff0c\u5c31\u4e0d\u81f3\u4e8e\u5f97\u7f6a\u60a8\u603b\u62a4\u6cd5\u4e86\uff01\u201d \u3000\u3000\u79e6\u69d0\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u7528\u4e0d\u7740\u800d\u5634\u76ae\u5b50\uff0c\u8fd9\u5c31\u5e26\u672c\u5ea7\u770b\u4e25\u57ce\u4e3b\u53bb\uff01\u201d \u3000\u3000\u63a5\u7740\u77a5\u4e86\u8d75\u660e\u6708\u548c\u9ad8\u5e84\u4e00\u773c\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u4e5f\u968f\u672c\u5ea7\u4e00\u8d77\u53bb\uff0c\u6709\u672c\u5ea7\u5728\uff0c\u8fd8\u4e0d\u81f3\u4e8e\u6709\u4eba \u6562\u6321\u9a7e\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u4e3a\u4e86\u5047\u88c5\u627e\u5cb3\u5c0f\u98de\uff0c\u5e76\u672a\u8ddf\u53bb\u3002 \u3000\u3000\u4e25\u5982\u51b0\u628a\u79e6\u69d0\u7b49\u4e09\u4eba\u76f4\u5f15\u5230\u4e66\u623f\uff0c\u518d\u7531\u4fa7\u95e8\u8fdb\u5165\u9694\u58c1\u5367\u5ba4\u3002 \u3000\u3000\u53ea\u89c1\u4e25\u5bd2\u679c\u7136\u8eba\u5728\u5e8a\u4e0a\uff0c\u800c\u4e14\u770b\u6765\u7684\u786e\u6c14\u8272\u4e0d\u4f73\uff0c\u7cbe\u795e\u4e5f\u59d4\u9761\u4e0d\u632f\u3002 \u3000\u3000\u5728\u75c5\u69bb\u4e00\u65c1\uff0c\u5b88\u7740\u4e25\u5927\u4eba\u548c\u4e8c\u59d1\u5a18\u4e25\u5982\u971c\u3002 \u3000\u3000\u539f\u6765\u4e25\u5bd2\u4e3a\u4e86\u505a\u5f97\u903c\u771f\uff0c\u7adf\u6765\u4e86\u4e2a\u5168\u5bb6\u603b\u52a8\u5458\u3002 \u3000\u3000\u4e25\u5bd2\u7531\u592b\u4eba\u6276\u7740\u52c9\u5f3a\u9760\u5899\u5750\u8d77\uff0c\u3000\u4e00\u8fde\u54b3\u4e0b\u51e0\u58f0\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u4e0d\u6562\u5f53\uff0c\u7adf\u7136\u6709\u52b3\u603b\u5ea7 \u524d\u6765\u63a2\u89c6\uff01\u201d \u3000\u3000\u79e6\u69d0\u5728\u69bb\u524d\u5750\u4e0b\uff0c\u4e5f\u5e72\u54b3\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u771f\u662f\u4e0d\u5de7\uff0c\u6559\u4e3b\u6709\u91cd\u8981\u5927\u4e8b\u60f3\u8bf7\u4e25\u57ce\u4e3b\u5230\u603b\u575b\u4f1a \u5546\uff0c\u8c01\u60f3\u4e25\u57ce\u4e3b\u5374\u75c5\u4e86\uff0c\u76ee\u524d\u662f\u5426\u7a0d\u597d\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u82e6\u7b11\u9053\uff1a\u201c\u4e25\u67d0\u8fd9\u75c5\uff0c\u8bf4\u4e0d\u5de7\u8fd8\u771f\u5de7\uff0c\u8bf4\u5de7\u4e48\uff0c\u53c8\u5b9e\u5728\u4e0d\u5de7\uff01\u201d \u3000\u3000\u4e25\u592b\u4eba\u63a5\u53e3\u9053\uff1a\u201c\u6211\u4eec\u8001\u7237\u5b50\u4e00\u5411\u8eab\u4f53\u786c\u6717\u7684\u5f88\uff0c\u8c01\u60f3\u4e00\u75c5\u5c31\u75c5\u6210\u8fd9\u6837\u5b50\uff01\u201d \u3000\u3000\u4e25\u5bd2\u53c8\u54b3\u4e86\u51e0\u58f0\u9053\uff1b\u201c\u6559\u4e3b\u53ec\u4e25\u67d0\u53bb\uff0c\u5230\u5e95\u662f\u4ec0\u4e48\u673a\u5bc6\u5927\u4e8b\uff1f\u201d \u3000\u3000\u79e6\u69d0\u987a\u53e3\u9053\uff1a\u201c\u548c\u4e25\u57ce\u4e3b\u6bd4\u8d77\u6765\uff0c\u5728\u6559\u4e3b\u9762\u524d\u8fde\u672c\u5ea7\u90fd\u662f\u5916\u4eba\uff0c\u6559\u4e3b\u51b3\u5b9a\uff0c\u5fc5\u987b\u4e25\u57ce\u4e3b \u5230\u4e86\u603b\u575b\u624d\u80fd\u8bb2\uff0c\u6240\u4ee5\u6b64\u523b\u672c\u5ea7\u4e5f\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u4e8b\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u603b\u5ea7\u770b\u4e25\u67d0\u75c5\u6210\u8fd9\u6837\u5b50\uff0c\u80fd\u5230\u603b\u575b\u53bb\u4e48\uff1f\u201d \u3000\u3000\u79e6\u69d0\u5475\u5475\u7b11\u9053\uff1a\u201c\u5f53\u7136\u8fd8\u662f\u517b\u75c5\u8981\u7d27\uff0c\u6559\u4e3b\u90a3\u8fb9\u5c31\u662f\u4e8b\u60c5\u518d\u5927\uff0c\u4e5f\u5fc5\u987b\u6682\u65f6\u538b\u4e00\u538b\u3002\u201d \u3000\u3000\u201c\u4e25\u67d0\u5b9e\u5728\u5bf9\u4e0d\u8d77\u6559\u4e3b\u3002\u201d \u3000\u3000\u79e6\u69d0\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u4e0a\u6b21\u4e25\u57ce\u4e3b\u7b54\u5e94\u628a\u90a3\u540d\u4f55\u59d3\u5973\u5b50\u5f88\u5feb\u9001\u5230\u603b\u575b\u53bb\uff0c\u73b0\u5728\u8fc7\u4e86\u8fd9\u4e48\u591a \u5929\uff0c\u600e\u4e48\u8fd8\u6ca1\u6709\u52a8\u9759\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u6447\u5934\u4e00\u53f9\u9053\uff1a\u201c\u5979\u90a3\u75c5\u7684\u786e\u662f\u602a\u75c5\uff0c\u5230\u73b0\u5728\u8fd8\u6574\u5929\u6ce1\u5728\u6d74\u76c6\u91cc\uff0c\u603b\u4e0d\u80fd\u628a\u4eba\u8fde\u6d74\u76c6 \u2014\u8d77\u62ac\u5230\u603b\u575b\u5427\u3002\u201d \u3000\u3000\u79e6\u69d0\u5e72\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u542c\u8bf4\u57ce\u4e3b\u6700\u8fd1\u6536\u4e86\u4e00\u540d\u968f\u8eab\u62a4\u536b\uff0c\u4eba\u975e\u5e38\u80fd\u5e72\uff0c\u6b66\u529f\u53c8\u9ad8\u5f97\u51fa\u5947\uff0c \u53ef\u6709\u8fd9\u56de\u4e8b\u5427\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9887\u4e0d\u81ea\u7136\u7684\u6447\u4e86\u6447\u5934\uff0c\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5b9e\u5728\u62b1\u6b49\uff0c\u542c\u8bf4\u4ed6\u521a\u624d\u5f97\u7f6a\u4e86\u9ad8\u62a4\u6cd5\uff0c\u5c45\u7136 \u5982\u6b64\u65e0\u6cd5\u65e0\u5929\uff0c\u53ea\u80fd\u602a\u8001\u592b\u5fa1\u4e0b\u4e0d\u4e25\u3002\u201d \u3000\u3000\u79e6\u69d0\u9053\uff1a\u201c\u5982\u679c\u602a\uff0c\u8be5\u602a\u9ad8\u62a4\u6cd5\u5b66\u827a\u4e0d\u7cbe\uff0c\u5802\u5802\u7687\u5143\u6559\u603b\u575b\u62a4\u6cd5\uff0c\u5c45\u7136\u8d25\u5728\u4e00\u540d\u62a4\u536b\u624b \u91cc\u3002\u201d \u3000\u3000\u9ad8\u5e84\u660e\u77e5\u79e6\u69d0\u8fd9\u8bdd\u662f\u6545\u610f\u8bf4\u7ed9\u4e25\u5bd2\u542c\u7684\uff0c\u5374\u4e5f\u80c0\u5f97\u9762\u7ea2\u8033\u8d64\u3002 \u3000\u3000\u4e25\u5bd2\u5145\u6ee1\u6b49\u610f\u9053\uff1a\u201c\u4e25\u67d0\u8be5\u5411\u9ad8\u62a4\u6cd5\u9053\u6b49\uff0c\u7b49\u8fc7\u4e9b\u5929\u75c5\u597d\u4e4b\u540e\uff0c\u518d\u628a\u4ed6\u5e26\u5230\u603b\u575b\u9886\u7f6a\u3002\u201d \u3000\u3000\u79e6\u69d0\u5475\u5475\u7b11\u9053\uff1a\u201c\u8fd9\u4ec0\u4e48\u8bdd\uff1f\u8fd9\u4e8b\u521a\u624d\u88ab\u6559\u4e3b\u77e5\u9053\u4ee5\u540e\uff0c\u6559\u4e3b\u4e0d\u4f46\u6ca1\u53d1\u7765\u6c14\uff0c\u53cd\u800c\u5bf9\u8d35 \u57ce\u8fd9\u540d\u62a4\u536b\u5927\u52a0\u8d5e\u8d4f\uff0c\u51b3\u5b9a\u628a\u4ed6\u8c03\u5230\u603b\u575b\u91cd\u7528\u91cd\u8d4f\uff0c\u4e25\u57ce\u4e3b\uff0c\u53ef\u5426\u628a\u8fd9\u4eba\u4ea4\u7ed9\u672c\u5ea7\u5e26\u8d70\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u4ed6\u65b9\u624d\u81ea\u77e5\u95ef\u4e86\u7978\uff0c\u7adf\u4e0d\u77e5\u8eb2\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u7b49\u627e\u5230\u540e\u4e25\u67d0\u4fbf\u5429\u5490\u5468\u603b\u7ba1\u628a\u4ed6 \u9001\u5230\u603b\u575b\u53bb\u3002\u201d \u3000\u3000\u5ffd\u89c1\u5468\u6d77\u5c71\u5306\u5306\u8d70\u4e86\u8fdb\u6765\u9053\uff1a\u201c\u7980\u57ce\u4e3b\uff0c\u627e\u5230\u5173\u5927\u9e4f\u4e86\uff0c\u6b63\u5728\u5927\u5385\u91cc\u3002\u201d \u3000\u3000\u4e25\u5bd2\u5148\u662f\u4e00\u6123\uff0c\u4f46\u5f88\u5feb\u4fbf\u77e5\u9053\u5173\u5927\u9e4f\u5c31\u662f\u5cb3\u5c0f\u98de\u3002 \u3000\u3000\u539f\u6765\u5173\u5927\u9e4f\u8fd9\u5316\u540d\u662f\u5cb3\u5c0f\u98de\u81ea\u5df1\u60f3\u7684\u3002 \u3000\u3000\u4ed6\u60f3\u5230\u5173\u3001\u5cb3\u90fd\u662f\u6b66\u5723\uff0c\u5c0f\u5bf9\u5927\uff0c\u867d\u7136\u5bf9\u4e0d\u4e0a\u98de\uff0c\u4f46\u9e4f\u5374\u6700\u80fd\u98de\uff0c\u60f3\u597d\u4e4b\u540e\uff0c\u4fbf\u8981\u5468\u6d77 \u5c71\u5230\u91cc\u9762\u901a\u62a5\u3002 \u3000\u3000\u4ed6\u7684\u76ee\u7684\uff0c\u662f\u8981\u628a\u79e6\u69d0\u7b49\u4eba\u65e9\u4e9b\u5f15\u79bb\u5185\u5ba4\uff0c\u514d\u5f97\u4ed6\u4eec\u548c\u4e25\u5bd2\u6b7b\u7f20\u4e0d\u4f11\uff0c \u3000\u3000\u79e6\u69d0\u8d77\u8eab\u62f1\u4e86\u62f1\u624b\u9053\uff1a\u201c\u4e25\u57ce\u4e3b\u8bf7\u5b89\u5fc3\u9759\u517b\uff0c\u672c\u5ea7\u5c31\u5230\u5ba2\u5385\u628a\u5173\u62a4\u536b\u5e26\u8d70\u3002\u201d \u3000\u3000\u4e25\u5bd2\u4e5f\u5728\u75c5\u69bb\u4e0a\u62f1\u62f1\u624b\u9053\uff1a\u201c\u6709\u52b3\u4e86\uff01\u201d \u3000\u3000\u79e6\u69d0\u7b49\u4e09\u4eba\u88ab\u5468\u6d77\u5c71\u5e26\u8d70\u540e\uff0c\u4e25\u592b\u4eba\u3001\u4e25\u5982\u51b0\u3001\u4e25\u5982\u971c\u5927\u611f\u5403\u60ca\u3002 \u3000\u3000\u5c24\u5176\u662f\u4e25\u5982\u971c\uff0c\u5cb3\u5c0f\u98de\u662f\u5979\u672a\u6765\u7684\u592b\u5a7f\uff0c\u600e\u80fd\u8fd9\u6837\u5c31\u88ab\u4eba\u5e26\u8d70\uff1f \u3000\u3000\u601b\u5979\u82b3\u5fc3\u5185\u6025\u867d\u6025\uff0c\u5374\u4e0d\u4fbf\u5f00\u53e3\u8bf4\u8bdd\uff0c \u3000\u3000\u4e25\u5982\u51b0\u6025\u6025\u8bf4\u9053\uff1b\u201c\u7239\uff0c\u60a8\u53ef\u662f\u771f\u8ba9\u4ed6\u4eec\u628a\u5c0f\u98de\u5e26\u8d70\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u7b11\u9053\uff1a\u201c\u6211\u5373\u4fbf\u7b54\u5e94\u4ed6\u4eec\u628a\u4eba\u5e26\u8d70\uff0c\u4ed6\u4eec\u53ef\u6709\u529e\u6cd5\u628a\u4ed6\u5e26\u8d70\u4e48\uff1f\u6211\u7b54\u5e94\uff0c\u53ef\u77e5\u9053\u4eba \u5bb6\u5c0f\u98de\u7b54\u4e0d\u7b54\u5e94\uff1f\u201d \u3000\u3000\u4e25\u5982\u51b0\u4e0d\u89c9\u4e5f\u7b11\u4e86\uff0c\u5979\u548c\u5cb3\u5c0f\u98de\u66fe\u4ea4\u8fc7\u624b\uff0c\u5f53\u7136\u65e9\u77e5\u9053\u4ed6\u6b66\u529f\u9ad8\u4e0d\u53ef\u6d4b\u3002 \u3000\u3000\u53ea\u542c\u4e8c\u59d1\u5a18\u4e25\u5982\u971c\u9053\uff1a\u201c\u59d0\uff0c\u6211\u4eec\u770b\u770b\u53bb\uff01\u201d \u3000\u3000\u4e25\u5bd2\u5fd9\u5429\u5490\u9053\uff1a\u201c\u770b\u5f52\u770b\uff0c\u53ef\u662f\u5728\u6697\u5904\u5077\u7740\u770b\uff0c\u82e5\u4ed6\u4eec\u8981\u4f60\u4eec\u5e2e\u5fd9\u62ff\u4eba\uff0c\u4f60\u4eec\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u4e25\u5982\u51b0\u62c9\u7740\u4e25\u5982\u971c\u7684\u624b\uff0c\u968f\u5373\u5411\u5916\u5954\u53bb\u3002 \u3000\u3000\u79e6\u69d0\u7b49\u4e8c\u4eba\u8fdb\u5165\u5927\u5385\uff0c\u53ea\u89c1\u5cb3\u5c0f\u98de\u6b63\u5750\u5728\u90a3\u91cc\uff0c\u50cf\u82e5\u65e0\u5176\u4e8b\u822c\u3002 \u3000\u3000\u9ad8\u5e84\u4e00\u6307\u6234\u7740\u4eba\u76ae\u9762\u5177\u7684\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u7980\u603b\u5ea7\uff0c\u5c31\u662f\u4ed6\uff01\u201d \u3000\u3000\u79e6\u69d0\u539f\u5148\u53ea\u9053\u5cb3\u5c0f\u98de\u662f\u4e2a\u4ec0\u4e48\u4e09\u5934\u516d\u81c2\u4eba\u7269\uff0c\u6b64\u523b\u4e00\u89c1\u4ed6\u8eab\u91cf\u5341\u5206\u77ee\u5c0f\uff0c\u4f7f\u4ed6\u7b80\u76f4\u8981\u6000 \u7591\u9ad8\u5e84\u7684\u4e00\u8eab\u6b66\u529f\u90fd\u54ea\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u8d70\u8fd1\u8eab\u6765\uff0c\u4e0d\u52a8\u58f0\u8272\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u53eb\u5173\u5927\u9e4f\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6839\u672c\u4e0d\u8d77\u5ea7\uff0c\u51b7\u51b7\u7b54\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u79e6\u69d0\u9053\uff1a\u201c\u672c\u5ea7\u60f3\u8d77\u6765\u4e86\uff0c\u4e0a\u6b21\u672c\u5ea7\u6765\u65f6\uff0c\u4f60\u66fe\u968f\u5728\u4e25\u57ce\u4e3b\u8eab\u8fb9\uff0c\u800c\u4e14\u8fd8\u5012\u8fc7\u8336\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u597d\u50cf\u6709\u8fd9\u4e48\u4e00\u56de\u4e8b\uff0c\u4e0d\u8fc7\u5728\u4e0b\u73b0\u5728\u4e0d\u60f3\u7ed9\u57ce\u4e3b\u5f53\u62a4\u536b\u4e86\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u65b9\u624d\u6253\u4e86\u59d3\u9ad8\u7684\uff0c\u66fe\u906d\u5230\u57ce\u4e3b\u8d23\u9a82\uff0c\u6240\u4ee5\u5728\u4e0b\u4e0d\u60f3\u518d\u8fde\u7d2f\u4ed6\u8001\u4eba\u5bb6\u3002\u201d \u3000\u3000\u201c\u542c\u8bf4\u4f60\u7684\u6b66\u529f\u5f88\u597d\uff1f\u201d \u3000\u3000\u201c\u54ea\u91cc\u8bdd\uff0c\u90a3\u662f\u56e0\u4e3a\u59d3\u9ad8\u7684\u6b66\u529f\u592a\u5dee\uff0c\u56e0\u6b64\u663e\u5f97\u5728\u4e0b\u597d\u50cf\u8fd8\u6709\u4e24\u4e0b\u5b50\u3002\u201d \u3000\u3000\u201c\u6559\u4e3b\u8ba4\u4e3a\u4f60\u662f\u4e2a\u4eba\u624d\uff0c\u5e0c\u671b\u672c\u5ea7\u628a\u4f60\u5e26\u5230\u603b\u575b\u53bb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u53bb\u3002\u201d \u3000\u3000\u79e6\u69d0\u5f3a\u6291\u7740\u9707\u6012\u9053\uff1a\u201c\u4f60\u6562\u4e0d\u9075\u6559\u4e3b\u7684\u4ee4\u8c15\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u7687\u5143\u6559\u8bb2\u7a76\u7684\u662f\u5c42\u5c42\u8282\u5236\uff0c\u4e0b\u547d\u4ee4\u7ed9\u6211\u7684\uff0c\u5e94\u8be5\u662f\u57ce\u4e3b\uff0c\u81f3\u4e8e\u6559\u4e3b\u4e0b\u4ee4\uff0c \u5c06\u5728\u5916\u541b\u547d\u6709\u6240\u4e0d\u53d7\uff01\u201d \u3000\u3000\u79e6\u69d0\u4e0d\u6562\u518d\u548c\u5cb3\u5c0f\u98de\u6597\u5634\uff0c\u5f84\u81ea\u5728\u4e00\u5904\u5750\u4e0b\uff0c\u559d\u8d77\u8336\u6765\u3002 \u3000\u3000\u9ad8\u5e84\u662f\u5403\u8fc7\u4e8f\u7684\uff0c\u5f53\u7136\u4e0d\u6562\u51fa\u9762\uff0c\u4e0d\u8fc7\u4ed6\u5947\u602a\u603b\u62a4\u6cd5\u79e6\u69d0\u6b64\u523b\u4e3a\u4ec0\u4e48\u7adf\u8fde\u4e00\u70b9\u813e\u6c14\u90fd\u6ca1 \u6709\u4e86\u3002 \u3000\u3000\u8d75\u660e\u6708\u5374\u662f\u4e0d\u7518\u5bc2\u5bde\uff0c\u6012\u77aa\u7740\u5cb3\u5c0f\u98de\u559d\u9053\uff1a\u201c\u5927\u80c6\uff0c\u5728\u603b\u62a4\u6cd5\u9762\u524d\uff0c\u7adf\u6562\u8fd9\u6837\u653e\u8086\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u90a3\u91cc\u6765\u4e86\u8fd9\u4e48\u4e2a\u8001\u5c0f\u5b50\uff1f\u4f60\u7b97\u4ec0\u4e48\u4e1c\u897f\uff01\u201d \u3000\u3000\u8d75\u660e\u6708\u53ea\u6c14\u5f97\u8138\u4e0a\u9752\u7b4b\u66b4\u8d77\uff0c\u8fd8\u6ca1\u6765\u5f97\u53ca\u8bf4\u8bdd\uff0c\u7a81\u89c1\u79e6\u69d0\u731b\u5730\u4e00\u62cd\u684c\u5b50\u9053\uff1a\u201c\u8fd8\u4e0d\u7ed9\u6211 \u62ff\u4e0b\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fd9\u624d\u7ad9\u4e86\u8d77\u6765\u9053\uff1a\u201c\u8981\u62ff\u5230\u5916\u9762\u62ff\uff0c\u514d\u5f97\u5927\u5385\u91cc\u7684\u684c\u51f3\u906d\u6b83\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u4eba\u5df2\u8dc3\u5230\u5927\u5385\u4e4b\u5916\u3002 \u3000\u3000\u8d75\u660e\u6708\u559d\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u53ef\u662f\u60f3\u8dd1\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b11\u9053\uff0c\u201c\u5c31\u662f\u4f60\u4eec\u6559\u4e3b\u6765\u4e86\uff0c\u5728\u4e0b\u4e5f\u7528\u4e0d\u7740\u8dd1\u3002\u201d \u3000\u3000\u8d75\u660e\u6708\u7528\u7684\u662f\u4e00\u5bf9\u5929\u661f\u7b14\uff0c\u8fd9\u65f6\u53cc\u7b14\u65e9\u5df2\u7d27\u63e1\u5728\u624b\uff0c\u8ddf\u7740\u5c31\u8dc3\u4e86\u51fa\u6765\u3002 \u3000\u3000\u53ea\u770b\u4ed6\u7531\u5927\u5385\u8dc3\u51fa\u7684\u8eab\u6cd5\uff0c\u4e0d\u96be\u77e5\u9053\u4ed6\u6b66\u529f\u5fc5\u5728\u9ad8\u5e84\u4e4b\u4e0a\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u5c51\u5730\u77a5\u4e86\u4e00\u773c\u9053\uff1a\u201c\u4f60\u8fc7\u6765\uff01\u201d \u3000\u3000\u8d75\u660e\u6708\u84e6\u5730\u817e\u8eab\u800c\u8d77\uff0c\u5148\u662f\u201c\u5927\u9e4f\u5c55\u7fc5\u201d\uff0c\u7136\u540e\u201c\u5bd2\u9e26\u5f52\u5de2\u201d\uff0c \u3000\u3000\u8eab\u5f62\u5feb\u5f97\u7b80\u76f4\u4ee4\u4eba\u76ee\u4e0d\u6687\u63a5\uff0c\u5f85\u6251\u8fd1\u5cb3\u5c0f\u98de\u8eab\u524d\uff0c\u7acb\u5373\u53cc\u7b14\u9f50\u51fa\uff0c\u7b14\u70b9\u5411\u201c\u5929\u7075\u7a74\u201d\uff0c \u3000\u4e00\u7b14\u6233\u5411\u54bd\u5589\u3002 \u3000\u3000\u53ea\u542c\u201c\u55b3\u201d\u3001\u201c\u55b3\u201d\u4e24\u58f0\uff0c\u51e0\u4e4e\u5728\u540c\u4e00\u65f6\u95f4\uff0c\u53cc\u7b14\u5df2\u88ab\u5cb3\u5c0f\u98de\u7684\u957f\u5251\u62e8\u504f\u3002 \u3000\u3000\u4f46\u89c1\u94f6\u8292\u4e00\u95ea\uff0c\u90a3\u53bb\u800c\u590d\u8fd4\u7684\u5251\u950b\uff0c\u5374\u53c8\u524a\u8fc7\u6765\u3002 \u3000\u3000\u8d75\u660e\u6708\u5927\u5403\u4e00\u60ca\uff0c\u6025\u6025\u4ef0\u8eab\u504f\u5934\uff0c\u90a3\u5bd2\u8292\u76f4\u6253\u773c\u524d\u63a0\u8fc7\uff0c\u82e5\u975e\u4ed6\u8eb2\u5f97\u5f97\u5feb\uff0c\u53ea\u8981\u518d\u8fd1\u534a \u5bf8\uff0c\u5f88\u53ef\u80fd\u4f1a\u88ab\u524a\u6389\u9f3b\u5b50\u3002 \u3000\u3000\u8fd9\u65f6\u79e6\u69d0\u3001\u9ad8\u5e84\u3001\u5468\u6d77\u5c71\u7b49\u65e9\u4e5f\u6765\u5230\u5927\u5385\u5916\u89c2\u6218\u3002 \u3000\u3000\u8fd8\u6709\u90a3\u8eb2\u5728\u6697\u5904\u7684\u5927\u59d1\u5a18\u4e25\u5982\u51b0\u548c\u4e8c\u59d1\u5a18\u4e25\u5982\u971c\uff0c\u770b\u5f97\u66f4\u662f\u805a\u7cbe\u4f1a\u795e\u3002 \u3000\u3000\u8d75\u660e\u6708\u843d\u5730\u4e4b\u540e\uff0c\u8e0f\u6b65\u63c9\u8eab\uff0c\u518d\u5411\u5cb3\u5c0f\u98de\u653b\u53bb\uff0c\u800c\u4e14\u5c55\u5f00\u751f\u5e73\u7edd\u5b66\uff0c\u53cc\u7b14\u6325\u52a8\u5f97\u6025\u5982\u9aa4 \u96e8\uff0c\u6068\u4e0d\u5f97\u7acb\u523b\u628a\u5bf9\u65b9\u7f6e\u4e8e\u6b7b\u5730\u3002 \u3000\u3000\u4ed6\u7684\u8fd9\u79cd\u62da\u547d\u6253\u6cd5\uff0c\u7adf\u662f\u5fc3\u5b58\u4e09\u79cd\u7528\u610f\u3002 \u3000\u3000\u7b2c\u4e00\uff0c\u4ed6\u662f\u60f3\u5728\u603b\u62a4\u6cd5\u79e6\u69d0\u9762\u524d\u5c3d\u91cf\u663e\u73b0\u8eab\u624b\uff0c\u4ee5\u4fbf\u5c06\u6765\u66f4\u53d7\u91cd\u7528\u3002 \u3000\u3000\u5176\u6b21\uff0c\u4ed6\u4e5f\u662f\u6545\u610f\u8868\u73b0\u7ed9\u9ad8\u5e84\u770b\u7684\uff0c\u4f7f\u9ad8\u5e84\u5728\u4ed6\u800c\u524d\u7518\u62dc\u4e0b\u98ce\u3002 \u3000\u3000\u7b2c\u4e09\uff0c\u4e0d\u5916\u662f\u8981\u901f\u6218\u901f\u51b3\u3002 \u3000\u3000\u4e0d\u77e5\u5cb3\u5c0f\u98de\u662f\u5426\u8981\u6545\u610f\u6210\u5168\u4ed6\u7684\u5fc3\u613f\uff0c\u6b64\u523b\u53cd\u800c\u91c7\u53d6\u4e86\u6e38\u6597\u7684\u6218\u6cd5\uff0c\u867d\u672a\u8d25\u9000\uff0c\u5374\u4e5f\u5e76 \u4e0d\u6c42\u80dc\u3002 \u3000\u3000\u8f6c\u77ac\u95f4\u4e09\u56db\u5341\u62db\u8fc7\u53bb\u3002 \u3000\u3000\u6e10\u6e10\uff0c\u8d75\u660e\u6708\u7684\u653b\u52bf\u5df2\u7ecf\u7f13\u6162\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u4f8b\u5e76\u975e\u8d75\u660e\u6708\u6545\u610f\u5982\u6b64\uff0c\u800c\u662f\u4ed6\u7684\u5185\u529b\u5df2\u6e10\u7528\u5b8c\uff0c\u6839\u672c\u4e0d\u80fd\u7ee7\u7eed\u67aa\u653b\uff0c \u3000\u3000\u4f46\u5cb3\u5c0f\u98de\u5374\u7cbe\u795e\u6108\u6765\u6108\u65fa\uff0c\u5df2\u5f00\u59cb\u903c\u5f97\u8d75\u660e\u6708\u62db\u67b6\u4e0d\u4f4f\uff0c\u4e00\u8def\u5411\u540e\u5012\u9000\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u89c1\u5cb3\u5c0f\u98de\u4e00\u58f0\u51b7\u7b11\uff0c\u7adf\u7136\u53cd\u64d8\u4e00\u5251\uff0c\u5411\u8eab\u540e\u62a1\u53bb\uff0c\u540c\u65f6\u4eba\u4e5f\u5411\u4e00\u4fa7\u8dc3\u5f00\u4e09 \u5c3a\uff0c \u3000\u3000\u53ea\u542c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u3000\u4e00\u67c4\u957f\u5251\u98de\u4e86\u51fa\u53bb\uff0c\u63a5\u7740\u5c31\u662f\u9ad8\u5e84\u7684\u4e00\u58f0\u60e8\u547c\u3002 \u3000\u3000\u539f\u6765\u8fd9\u65f6\u9ad8\u5e84\u5df2\u65e0\u58f0\u65e0\u606f\u7684\u8d81\u673a\u5728\u540e\u5077\u88ad\uff0c\u5cb3\u5c0f\u98de\u5c31\u50cf\u8eab\u540e\u751f\u4e86\u773c\u775b\uff0c\u90a3\u53cd\u81c2\u4e00\u5251\uff0c\u4e0d \u4f46\u5c06\u5bf9\u65b9\u5175\u5203\u78d5\u98de\uff0c\u540c\u65f6\u4e5f\u524a\u53bb\u4e86\u9ad8\u5e84\u4e24\u53ea\u624b\u6307\u3002 \u3000\u3000\u9ad8\u5e84\u6b63\u5728\u9f87\u7259\u54a7\u5634\u7684\u63e1\u624b\u5927\u75db\uff0c\u9762\u524d\u9ed1\u5f71\u4e00\u95ea\uff0c\u66b4\u63a0\u800c\u81f3\uff0c\u524d\u80f8\u65e9\u53c8\u4e2d\u4e86\u4e00\u8bb0\u98de\u811a\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7684\u8fd9\u4e00\u811a\u529b\u9053\u5947\u91cd\uff0c\u628a\u9ad8\u5e84\u8e22\u5f97\u8fde\u8eab\u5b50\u90fd\u98de\u4e86\u8d77\u6765\uff0c\u76f4\u6454\u51fa\u4e24\u4e08\u4e4b\u5916\uff0c\u843d\u5730\u4e4b\u540e\uff0c \u534a\u5929\u722c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u9aa4\u89c1\u6b64\u60c5\uff0c\u8d75\u660e\u6708\u4e5f\u60ca\u5f97\u51e0\u4e4e\u53d1\u5446\uff0c\u6b63\u6b32\u8dc3\u8eab\u540e\u9000\uff0c\u5cb3\u5c0f\u98de\u65e9\u50cf\u4e00\u9053\u8f7b\u70df\u822c\u63a0\u5230\u8eab\u524d\uff0c \u4e24\u58f0\u8fde\u54cd\u4e4b\u540e\uff0c\u4ed6\u53ea\u89c9\u53cc\u624b\u5ffd\u7136\u8f7b\u4e86\u8bb8\u591a\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u624d\u53d1\u73b0\u624b\u4e2d\u7684\u4e24\u679d\u94c1\u7b14\uff0c\u5df2\u7ecf\u4e0d\u89c1\uff0c \u8fde\u88ab\u51fb\u843d\u5728\u4ec0\u4e48\u5730\u65b9\u90fd\u6ca1\u770b\u5230\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5e76\u4e0d\u8ffd\u51fb\uff0c\u53cd\u800c\u9000\u81f3\u4e08\u4f59\u4e4b\u5916\uff0c\u7ad9\u5728\u90a3\u91cc\uff0c\u6c14\u4e0d\u51fa\u58f0\uff0c\u9762\u4e0d\u6539\u8272\u3002 \u3000\u3000\u8fd9\u60c5\u5f62\u5c82\u6b62\u8d75\u660e\u6708\u6014\u5728\u5f53\u573a\uff0c\u79e6\u69d0\u7167\u6837\u4e5f\u6709\u4e9b\u76ee\u8e6c\u53e3\u5446\u3002 \u3000\u3000\u79e6\u69d0\u5947\u602a\u7684\u662f\u80b2\u5316\u57ce\u600e\u4f1a\u6709\u8fd9\u6837\u4e00\u540d\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\u7684\u9ad8\u624b\uff0c\u800c\u4ed6\u4f4d\u5c45\u603b\u575b\u603b\u62a4\u6cd5\uff0c\u5c45\u7136 \u6beb\u4e0d\u77e5\u60c5\u3002 \u3000\u3000\u4ed6\u81ea\u5fd6\u5373\u4f7f\u81ea\u5df1\u4eb2\u81ea\u4e0b\u573a\u51fa\u6218\uff0c\u4e5f\u4e0d\u5bb9\u6613\u8ba8\u5230\u597d\u5904\uff0c\u82e5\u5236\u4e0d\u4f4f\u5bf9\u65b9\uff0c\u53cd\u800c\u843d\u5f97\u88ab\u4eba\u803b\u7b11\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u79e6\u69d0\u53ea\u662f\u634b\u7740\u516b\u5b57\u80e1\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5173\u62a4\u536b\uff0c\u679c\u7136\u8eab\u624b\u4e0d\u5dee\uff0c\u8bf7\u7b54\u590d\u672c\u5ea7\u4e00\u53e5\u8bdd\uff0c \u4f60\u662f\u4ec0\u4e48\u65f6\u5019\u8fdb\u5165\u5929\u8c37\u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5c0a\u9a7e\u522b\u53eb\u5728\u4e0b\u5173\u62a4\u536b\uff0c\u5728\u4e0b\u5148\u524d\u5df2\u7ecf\u58f0\u660e\u8fc7\uff0c\u4ece\u73b0\u5728\u8d77\uff0c\u4e0d\u518d\u505a\u4e25\u57ce\u4e3b\u7684 \u62a4\u536b\u3002\u201d \u3000\u3000\u201c\u4f60\u51c6\u5907\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5728\u5929\u8c37\u81ea\u7acb\u4e3a\u738b\uff0c\u628a\u7687\u5143\u6559\u4e3b\u8d76\u8d70\uff0c\u8981\u4f60\u4eec\u90fd\u542c\u6211\u7684\u3002\u201d \u3000\u3000\u201c\u597d\u5927\u7684\u53e3\u6c14\uff01\u201d \u3000\u3000\u201c\u4eba\u751f\u5728\u4e16\uff0c\u5fc5\u987b\u7acb\u5927\u5fd7\uff0c\u53e3\u6c14\u5927\u624d\u8db3\u4ee5\u81ea\u8c6a\u3002\u201d \u3000\u3000\u201c\u4f60\u6ca1\u8fd8\u7b54\u590d\u672c\u5ea7\u7684\u8bdd\u3002\u201d \u3000\u3000\u201c\u4f60\u8981\u5728\u4e0b\u56de\u7b54\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u4f60\u662f\u4ec0\u4e48\u65f6\u5019\u8fdb\u5165\u5929\u8c37\u7684\uff0c\u4e3a\u4ec0\u4e48\u672c\u5ea7\u4ee5\u524d\u7adf\u7136\u5bf9\u4f60\u6beb\u65e0\u6240\u6089\u3002\u201d \u3000\u3000\u201c\u90a3\u6b63\u8bc1\u660e\u4f60\u6709\u4e8f\u804c\u5b88\uff0c\u81f3\u4e8e\u6211\u4ec0\u4e48\u65f6\u5019\u8fdb\u5165\u5929\u8c37\uff0c\u5b9e\u5728\u6ca1\u884c\u8ba9\u4f60\u77e5\u9053\u7684\u5fc5\u8981\uff0c\u95f2\u8bdd\u5c11 \u8bf4\uff0c\u5728\u4e0b\u73b0\u5728\u8981\u8d70\u4e86\uff01\u201d \u3000\u3000\u201c\u4f60\u8981\u8d70\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u5c0a\u9a7e\u4e0d\u5448\u8bf4\u7687\u5143\u6559\u4e3b\u8981\u8bf7\u6211\u5230\u603b\u575b\u4e48\uff0c\u6211\u73b0\u5728\u5c31\u5230\u4f60\u4eec\u603b\u575b\u53bb\uff0c\u8981\u6ce8\u610f\uff0c\u4eca\u5929\u4e8c\u66f4\u5b50 \u65f6\uff0c\u627e\u4e5f\u8bb8\u4f1a\u53bb\u627e\u4f60\u3002\u201d \u3000\u3000\u4ed6\u7684\u8bdd\u6700\u540e\u4e00\u4e2a\u5b57\u521a\u521a\u843d\u97f3\uff0c\u80a9\u5934\u4e00\u6643\uff0c\u4e00\u5f0f\u201c\u4e00\u9e64\u51b2\u5929\u201d\uff0c\u76f4\u51b2\u8d77\u4e03\u516b\u4e08\u9ad8\uff0c\u7136\u540e\u518d \u5e73\u63a0\u800c\u51fa\uff0c\u7728\u773c\u95f4\u4f7f\u5df2\u4e0d\u89c1\u4eba\u5f71\u3002 \u3000\u3000\u8fd9\u7b49\u60ca\u4e16\u9a87\u4fd7\u7684\u8f7b\u529f\uff0c\u770b\u5f97\u5728\u65c1\u6240\u6709\u7684\u4eba\uff0c\u51e0\u4e4e\u8fde\u6c14\u4e5f\u900f\u4e0d\u8fc7\u6765\u3002 \u3000\u3000\u5c24\u5176\u4ed6\u4e34\u8d70\u524d\u66fe\u8bf4\uff1a\u4eca\u591c\u4e09\u66f4\u5b50\u65f6\u8981\u53bb\u627e\u79e6\u69d0\uff0c\u4f7f\u5f97\u79e6\u69d0\u4e8e\u9707\u60ca\u4e4b\u5916\uff0c\u66f4\u591a\u4e86\u4e00\u5206\u987e\u8651\u3002 \u3000\u3000\u79e6\u69d0\u60ca\u9b42\u752b\u5b9a\u4e4b\u540e\uff0c\u77a5\u4e86\u5468\u6d77\u5c71\u4e00\u773c\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u4ec0\u4e48\u65f6\u5019\u6765\u5230\u5929\u8c37\u7684\uff0c\u4f60\u603b\u8be5\u77e5\u9053\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u5176\u5b9e\u4ed6\u4e5f\u662f\u4ece\u603b\u575b\u62e8\u6765\u7684\uff0c\u53ea\u662f\u4e00\u76f4\u6df1\u85cf\u4e0d\u9732\uff0c\u76f4\u5230\u524d\u4e9b\u5929\u65e0\u610f\u4e2d\u9732\u4e86\u4e00 \u624b\uff0c\u624d\u88ab\u57ce\u4e3b\u770b\u4e0a\uff0c\u628a\u4ed6\u7559\u5728\u8eab\u8fb9\u505a\u62a4\u536b\u3002\u201d \u3000\u3000\u79e6\u69d0\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u4fdd\u5bc6\u5de5\u592b\u505a\u5f97\u53ef\u771f\u5230\u5bb6\uff01\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u4ed6\u8bf4\u5f53\u521d\u6765\u80b2\u5316\u57ce\u65f6\uff0c\u53ea\u4f1a\u51e0\u624b\u4e09\u811a\u732b\u628a\u5f0f\uff0c\u73b0\u5728\u7684\u6b66\u529f\uff0c\u5168 \u662f\u81ea\u5df1\u5077\u5077\u7ec3\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u201c\u5c82\u6709\u6b64\u7406\uff0c\u82e5\u65e0\u540d\u5e08\uff0c\u90a3\u91cc\u6765\u7684\u9ad8\u5f92\uff0c\u5230\u5e95\u662f\u8c01\u6559\u4ed6\u7684\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u662f\u65e0\u5e08\u81ea\u901a\u3002\u201d \u3000\u3000\u201c\u80e1\u8bf4\uff01\u4e0b\u6b21\u4f60\u518d\u8bbe\u6cd5\u628a\u4ed6\u7684\u5e95\u7ec6\u5957\u51fa\u6765\uff0c\u672c\u5ea7\u91cd\u91cd\u6709\u8d4f\uff01\u201d \u3000\u3000\u201c\u4ed6\u6839\u672c\u4e0d\u53ef\u80fd\u518d\u56de\u6765\u4e86\uff0c\u5351\u804c\u54ea\u91cc\u627e\u4ed6\u53bb\uff1f\u201d \u3000\u3000\u79e6\u69d0\u671b\u4e86\u8d75\u660e\u6708\u548c\u9ad8\u5e84\u4e00\u773c\u9053\uff1a\u201c\u54b1\u4eec\u8d70\uff01\u201d \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u5251\u62d4\u5f29\u5f20 \u3000\u3000\u79e6\u69d0\u7b49\u4e09\u4eba\u8d70\u540e\u4e0d\u4e45\uff0c\u5cb3\u5c0f\u98de\u4fbf\u5df2\u56de\u6765\uff0c\u800c\u4e14\u6062\u590d\u4e86\u672c\u6765\u9762\u76ee\u3002 \u3000\u3000\u4ed6\u548c\u5468\u6d77\u5c71\u53ea\u7565\u7565\u6de1\u4e86\u51e0\u53e5\u8bdd\uff0c\u4fbf\u76f4\u63a5\u8fdb\u5165\u4e25\u5bd2\u7684\u4e66\u623f\u3002 \u3000\u3000\u6b63\u597d\u4e66\u623f\u91cc\u53ea\u6709\u4e25\u5bd2\u4e00\u4eba\u3002 \u3000\u3000\u4e25\u5bd2\u4e00\u89c1\u5cb3\u5c0f\u98de\u8fdb\u6765\uff0c\u62db\u547c\u4ed6\u5728\u65c1\u8fb9\u5750\u4e0b\u9053\uff1a\u201c\u4f60\u65b9\u624d\u5728\u5927\u5385\u5916\u548c\u4ed6\u4eec\u4ea4\u624b\u7684\u60c5\u5f62\uff0c\u8001 \u592b\u5df2\u5168\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9887\u4e3a\u4e0d\u5b89\u9053\uff1a\u201c\u665a\u8f88\u7978\u4e8b\u8d8a\u95ef\u8d8a\u5927\uff0c\u4e5f\u66ff\u4f60\u8001\u4eba\u5bb6\u60f9\u6765\u4e0d\u53ef\u6536\u62fe\u7684\u9ebb\u70e6\uff0c\u5185\u5fc3\u5b9e \u5728\u60ed\u6127\u5f97\u5f88\uff01\u201d \u3000\u3000\u4e25\u5bd2\u957f\u957f\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4e8b\u5df2\u5982\u6b64\uff0c\u7528\u4e0d\u7740\u518d\u8c08\u8fd9\u4e9b\u3002\u8001\u592b\u76ee\u524d\u6240\u6101\u7684\uff0c\u4e0d\u5916\u662f\u80b2\u5316\u57ce\u5185 \u53ef\u7528\u4e4b\u4eba\u592a\u5c11\uff0c\u5982\u679c\u80fd\u591a\u6709\u51e0\u540d\u50cf\u4f60\u8fd9\u6837\u7684\u6218\u5c06\uff0c\u4fbf\u4e0d\u6101\u4e0d\u80fd\u548c\u7687\u5143\u6559\u4e3b\u5bf9\u6297\u4e86\uff01\u201d \u3000\u3000\u7a81\u89c1\u5cb3\u5c0f\u98de\u53cc\u819d\u8dea\u4e86\u4e0b\u53bb\u9053\uff1a\u201c\u665a\u8f88\u7f6a\u8be5\u4e07\u6b7b\uff0c\u6709\u4ec0\u4e8b\u60c5\uff0c\u4e00\u76f4\u672a\u5411\u57ce\u4e3b\u7980\u660e\uff01\u201d \u3000\u3000\u4e25\u5bd2\u6709\u5982\u4e08\u4e8c\u91d1\u521a\u6478\u4e0d\u7740\u5934\u8111\uff0c\u8bb6\u7136\u9053\uff1a\u201c\u5feb\u8d77\u6765\uff01\u5230\u5e95\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e8b\u5230\u5982\u4eca\uff0c\u665a\u8f88\u5b9e\u5728\u4e0d\u8be5\u518d\u9690\u7792\u4e0b\u53bb\u4e86\uff0c\u5bb6\u7236\u4e5f\u5728\u5929\u8c37\uff0c\u5fc5\u8981\u65f6\u5bb6\u7236\u5b9a\u53ef \u524d\u6765\u76f8\u52a9\uff0c\u800c\u4e14\u4ed6\u624b\u4e0b\u4e5f\u4e0d\u4e4f\u6b66\u529f\u51fa\u4f17\u7684\u9ad8\u624b\u3002\u201d \u3000\u3000\u4e25\u5bd2\u6025\u6025\u95ee\u9053\uff1a\u201c\u4ee4\u5c0a\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5bb6\u7236\u5c31\u662f\u9a7b\u5b88\u4e94\u5173\u7684\u82b1\u526f\u603b\u9547\u3002\u201d \u3000\u3000\u4e25\u5bd2\u4e0d\u7531\u4e00\u6123\u9053\uff1a\u201c\u4ee4\u5c0a\u4e3a\u4ec0\u4e48\u548c\u4f60\u4e0d\u540c\u59d3\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u5f97\u628a\u539f\u56e0\u4ece\u5934\u81f3\u5c3e\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u4e25\u5bd2\u4e0d\u89c9\u5927\u559c\u9053\uff1a\u201c\u8fd9\u592a\u597d\u4e86\uff0c\u5982\u679c\u80fd\u5f97\u5230\u4ee4\u5c0a\u7387\u540c\u4e94\u5173\u9ad8\u624b\u76f8\u52a9\uff0c\u5bf9\u4ed8\u7687\u5143\u6559\uff0c\u8001\u592b \u5c31\u6709\u6043\u65e0\u6050\u4e86\u3002\u4f60\u5feb\u56de\u4e94\u5173\uff0c\u8981\u4ee4\u5c0a\u5c3d\u901f\u79d8\u5bc6\u524d\u6765\u4e00\u8d9f\uff0c\u8001\u592b\u6709\u5f88\u591a\u4e8b\u60c5\uff0c\u8981\u5148\u884c\u548c\u4ed6\u5546\u8bae \u5546\u8bae\u3002\u201d \u3000\u3000\u201c\u57ce\u4e3b\u4ee5\u524d\u662f\u5426\u89c1\u8fc7\u5bb6\u7236\uff1f\u201d \u3000\u3000\u201c\u4ee4\u5c0a\u4ee5\u524d\u5728\u62db\u8d24\u9986\u62c5\u4efb\u526f\u9986\u4e3b\u65f6\u3001\u8001\u592b\u66fe\u548c\u4ed6\u5728\u603b\u575b\u89c1\u8fc7\u51e0\u6b21\uff0c\u4f46\u4e5f\u53ea\u662f\u76f8\u8bc6\u800c\u5df2\u3002\u201d \u3000\u3000\u4ed6\u987f\u4e86\u4e00\u987f\u53c8\u9053\uff1a\u201c\u4ee4\u5c0a\u4ee4\u5802\u548c\u4f60\u7684\u4fdd\u5bc6\u5de5\u592b\uff0c\u5b9e\u5728\u8ba9\u8001\u592b\u4f69\u670d\uff0c\u82e5\u4f60\u4eca\u5929\u4e0d\u8bf4\u51fa\u6765\uff0c \u53ea\u6015\u4efb\u4f55\u4eba\u90fd\u88ab\u7792\u8fc7\uff0c\u5176\u5b9e\u8fd9\u4e8b\u4e0d\u8bba\u4ee4\u5802\u548c\u4f60\uff0c\u65e9\u5c31\u4e0d\u8be5\u7792\u6211\uff0c\u96be\u9053\u4ee4\u5c0a\u4ee4\u5802\u592b\u59bb\u5206\u79bb\u4e09\u5e74 \u591a\uff0c\u5c31\u4e0d\u60f3\u89c1\u4e0a\u4e00\u9762\u4e48\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u88ab\u8bf4\u5f97\u7ea2\u7740\u8138\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u5176\u5b9e\u4e25\u5bd2\u53ea\u662f\u5634\u91cc\u8fd9\u6837\u8bf4\u8bf4\uff0c\u4ed6\u4f55\u5c1d\u4e0d\u77e5\u9053\u6b64\u4e8b\u5bf9\u82b1\u7389\u9e9f\u592b\u5987\u4ee5\u53ca\u5cb3\u5c0f\u98de\u5173\u7cfb\u91cd\u5927\uff0c\u4e0d \u5230\u6700\u540e\u5173\u5934\uff0c\u8c01\u4e5f\u4e0d\u6562\u6cc4\u6f0f\u673a\u5bc6\u3002 \u3000\u3000\u534a\u664c\u4e4b\u540e\uff0c\u5cb3\u5c0f\u98de\u624d\u9053\uff1a\u201c\u665a\u8f88\u73b0\u5728\u5c31\u8fd4\u56de\u4e94\u5173\u603b\u9547\u5411\u5bb6\u7236\u7980\u62a5\uff0c\u5148\u6c42\u57ce\u4e3b\u53d1\u4e0b\u901a\u884c\u4ee4 \u724c\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u9884\u8ba1\u4e94\u5173\u6709\u591a\u5c11\u9ad8\u624b\u53ef\u4ee5\u5230\u672c\u57ce\u6765\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u9664\u5bb6\u7236\u5916\uff0c\u53e6\u6709\u4e00\u540d\u4f7f\u8005\uff0c\u4e24\u540d\u526f\u4f7f\u8005\uff0c\u4ed6\u4eec\u662f\u4e00\u5b9a\u53ef\u4ee5\u968f\u540c\u884c\u52a8\u7684\u3002\u201d \u3000\u3000\u4e25\u5bd2\u968f\u5373\u5f00\u9501\u6253\u5f00\u4e66\u623f\u5185\u7684\u4e00\u5177\u6a71\u67dc\uff0c\u627e\u51fa\u4e94\u9762\u901a\u884c\u4ee4\u724c\uff0c\u4ea4\u7ed9\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4ee4\u5c0a\u548c\u4ed6 \u4eec\u52a0\u8d77\u6765\u662f\u56db\u4eba\uff0c\u591a\u4e00\u9762\u901a\u884c\u4ee4\u724c\u5185\u4f60\u59a5\u4e3a\u4fdd\u7ba1\uff0c\u5982\u679c\u6709\u4eba\u6765\u65f6\u53ef\u4ee5\u6d3e\u4e0a\u7528\u573a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5f53\u65e5\u4e0b\u5348\u5c31\u8fd4\u56de\u4e94\u5173\u603b\u9547\uff0c\u628a\u7ecf\u8fc7\u4e00\u4e94\u4e00\u5341\u5411\u82b1\u7389\u9e9f\u7980\u62a5\u3002 \u3000\u3000\u80b2\u5316\u57ce\u548c\u7687\u5143\u6559\u7684\u5173\u7cfb\u5df2\u7ecf\u6076\u5316\u5230\u8fd9\u79cd\u5730\u6b65\uff0c\u82b1\u7389\u9e9f\u5f53\u7136\u4e0d\u4f1a\u8d23\u602a\u5cb3\u5c0f\u98de\u6cc4\u5bc6\uff0c\u540c\u65f6\uff0c \u4ed6\u6000\u5ff5\u7231\u59bb\u4f55\u6167\u4ed9\uff0c\u65e9\u5df2\u671b\u773c\u6b32\u7a7f\uff0c\u5230\u4e86\u80b2\u5316\u57ce\uff0c\u9664\u548c\u4e25\u5bd2\u5171\u8bae\u5927\u4e8b\u5916\uff0c\u4e5f\u53ef\u548c\u4f55\u6167\u4ed9\u89c1\u4e0a \u4e00\u9762\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u968f\u5373\u5c06\u4e94\u9762\u80b2\u5316\u57ce\u901a\u884c\u4ee4\u724c\u4e5f\u4ea4\u7ed9\u4e86\u7236\u4eb2\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u8fd9\u4e8b\u4e8b\u5173\u91cd\u5927\uff0c\u6682\u65f6\u4e0d\u80fd\u8ba9\u8881\u5c0f\u9e64\u548c\u4e01\u3001\u7518\u4e8c\u4eba\u77e5\u9053\uff0c\u4f60\u5bf9\u4ed6\u4eec\uff0c\u5fc5\u987b\u4e25 \u5b88\u79d8\u5bc6\uff0c\u4e0d\u80fd\u900f\u9732\u534a\u70b9\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5b69\u513f\u6653\u5f97\uff0c\u7236\u4eb2\u51c6\u5907\u4ec0\u4e48\u65f6\u5019\u5230\u80b2\u5316\u57ce\u53bb\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u591c\u665a\u884c\u52a8\u8f83\u4e3a\u65b9\u4fbf\uff0c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u6211\u665a\u996d\u4e4b\u540e\u5c31\u52a8\u8eab\uff0c\u5728\u8fd9\u6bb5\u65f6\u95f4\uff0c\u5982\u679c\u6709 \u4eba\u6765\u8bbf\uff0c\u4f60\u52a1\u5fc5\u8bbe\u6cd5\u5e94\u4ed8\u8fc7\u53bb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8f9e\u51fa\u7236\u4eb2\u623f\u95f4\uff0c\u53ef\u4ee5\u60f3\u89c1\uff0c\u5728\u8fd9\u79cd\u5173\u5934\uff0c\u5fc3\u60c5\u96be\u514d\u5341\u5206\u7d27\u5f20\u3002 \u3000\u3000\u4ed6\u627e\u5230\u4e86\u8881\u5c0f\u9e64\uff0c\u5374\u4e0d\u89c1\u4e01\u6d9b\u548c\u7518\u9716\u3002 \u3000\u3000\u201c\u8881\u5927\u54e5\uff0c\u4e01\u3001\u7518\u4e24\u4f4d\u526f\u4f7f\u8005\u5462\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u5c34\u5c2c\u4e00\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u4eba\uff0c\u8fd9\u51e0\u5929\u7ecf\u5e38\u4e0d\u7167\u9762\uff0c\u597d\u4e0d\u5bb9\u6613\u6211\u624d\u67e5\u51fa\u6765\uff0c\u539f\u6765\u4ed6\u4eec \u7adf\u662f\u5929\u5929\u8dd1\u5230\u5bb5\u5173\u9b3c\u6df7\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u5934\u6026\u7136\u4e00\u9707\uff1a\u201c\u4ed6\u4eec\u53bb\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u56e0\u4e3a\u5bb5\u5173\u5973\u4eba\u591a\uff0c\u628a\u4ed6\u4eec\u8ff7\u5f97\u6655\u5934\u8f6c\u5411\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u987f\u65f6\u611f\u5230\u4e0d\u5999\uff0c\u6b63\u8d77\u795e\u8272\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0c\u8fd9\u4e8b\u4e0d\u53ef\u7b49\u95f2\u89c6\u4e4b\uff0c\u6211\u4eec\u5fc5\u987b\u8bbe\u6cd5\u7981\u6b62 \u4ed6\u4eec\u4e24\u4eba\u518d\u5230\u5bb5\u5173\u53bb\uff01\u201d \u3000\u3000\u201c\u4f60\u662f\u6015\u4ed6\u4eec\u51fa\u4e8b\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u4ed6\u4eec\u4e24\u4e2a\u867d\u7136\u5fc3\u5730\u4e0d\u574f\uff0c\u4f46\u89c1\u4e86\u5973\u8272\uff0c\u5fc5\u5b9a\u628a\u6301\u4e0d\u4f4f\uff0c\u6211\u4eec\u4e0d\u80fd\u4e0d\u63d0\u9632\u4ed6\u4eec\u4f1a \u88ab\u51e4\u5ae3\u7ea2\u5229\u7528\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4ed6\u4eec\u5230\u5bb5\u5173\uff0c\u5374\u53e3\u79f0\u662f\u516c\u4e8b\uff0c\u54b1\u4eec\u5b9e\u5728\u6ca1\u7406\u7531\u7981\u6b62\u4ed6\u4eec\u3002\u201d \u3000\u3000\u201c\u4e3a\u5927\u5c40\u7740\u60f3\uff0c\u5b9e\u5728\u5df2\u7ecf\u4e0d\u80fd\u987e\u8651\u90a3\u4e48\u591a\uff0c\u4e0d\u59a8\u786c\u6027\u89c4\u5b9a\u4e0d\u51c6\u4ed6\u4eec\u53bb\u3002\u201d \u3000\u3000\u201c\u82e5\u4ed6\u4eec\u4e0d\u542c\u5462\uff1f\u201d \u3000\u3000\u201c\u90a3\u5c31\u7ed9\u4ed6\u4eec\u4e00\u70b9\u6559\u8bad\uff01\u201d \u3000\u3000\u665a\u996d\u65f6\uff0c\u4e01\u6d9b\u548c\u7518\u9716\u4ecd\u672a\u56de\u6765\u3002 \u3000\u3000\u996d\u540e\uff0c\u5cb3\u5c0f\u98de\u8d8a\u60f3\u8d8a\u4e0d\u5bf9\u52b2\uff0c\u53ea\u597d\u8981\u6c42\u8881\u5c0f\u9e64\u5230\u5bb5\u5173\u628a\u4eba\u627e\u56de\u6765\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u6211\u4eec\u4e00\u8d77\u53bb\u5427\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u56e0\u7236\u4eb2\u5df2\u53bb\u4e86\u80b2\u5316\u57ce\uff0c\u81ea\u5df1\u5f53\u7136\u4e0d\u80fd\u79bb\u5f00\uff0c\u504f\u504f\u4ed6\u53c8\u4e0d\u4fbf\u660e\u8a00\u3002\u53ea\u597d\u7f16\u4e86\u4e00\u5957\u4e0d \u80fd\u53bb\u7684\u7406\u7531\u9a97\u8fc7\u8881\u5c0f\u9e64\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u53ea\u597d\u4e00\u4eba\u524d\u53bb\u3002 \u3000\u3000\u5c1a\u672a\u5230\u8fbe\u5bb5\u5173\u95e8\u53e3\uff0c\u771f\u662f\u5de7\u5f97\u5f88\uff0c\u521a\u597d\u4e01\u6d9b\u548c\u7518\u9716\u7531\u91cc\u9762\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u89c1\u8881\u5c0f\u9e64\u8fce\u9762\u800c\u6765\uff0c\u4e01\u6d9b\u8001\u8fdc\u5c31\u54a7\u5634\u7b11\u9053\uff1a\u201c\u8881\u4f7f\u8005\uff0c\u4f60\u4e5f\u6765\u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u505c\u4e0b\u811a\u6b65\uff0c\u5e76\u672a\u8a00\u8bed\u3002 \u3000\u3000\u4e24\u4eba\u6765\u5230\u8ddf\u524d\uff0c\u4e00\u89c1\u8881\u5c0f\u9e64\u8138\u8272\u4e0d\u5bf9\uff0c\u4e01\u6d9b\u5e26\u4e9b\u5403\u60ca\u95ee\u9053\uff1a \u3000\u3000\u201c\u8881\u4f7f\u8005\u600e\u4e48\u5566\uff0c\u83ab\u975e\u53c8\u4e0d\u8212\u670d\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u51b7\u58f0\u9053\uff1a\u201c\u4f60\u4eec\u4e8c\u4f4d\u5230\u5bb5\u5173\u505a\u4ec0\u4e48\u6765\u4e86\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u5f53\u7136\u662f\u5de1\u5173\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u91cc\u6709\u4e94\u5173\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u5929\u5929\u53ea\u5de1\u5bb5\u5173\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u6014\u4e86\u6014\u9053\uff1a\u201c\u8881\u4f7f\u8005\uff0c\u4eca\u5929\u597d\u50cf\u6709\u4eba\u60f9\u7740\u4f60\u4e86\uff1f\u5e72\u561b\u53d1\u813e\u6c14\u53d1\u5230\u6211\u4eec\u5934\u4e0a\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u8f6c\u8fc7\u8eab\u9053\uff1a\u201c\u4e0d\u5fc5\u8bb2\u5e9f\u8bdd\uff0c\u56de\u53bb\u518d\u8bf4\uff01\u201d \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u5f53\u771f\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u5728\u4ed6\u4eec\u7684\u8bb0\u5fc6\u91cc\uff0c\u8881\u5c0f\u9e64\u4e00\u5411\u5bf9\u4ed6\u4eec\u5341\u5206\u5c0a\u91cd\uff0c\u867d\u7136\u8881\u5c0f\u9e64\u7684\u6b66\u529f\u6bd4\u4ed6\u4eec\u9ad8\u5f97\u591a\uff0c\u8eab\u4efd \u4e5f\u5728\u4ed6\u4eec\u4e4b\u4e0a\uff0c\u4f46\u5374\u5ff5\u5728\u4ed6\u4eec\u5e74\u957f\uff0c\u603b\u662f\u4ee5\u524d\u8f88\u4eba\u7269\u770b\u5f85\u3002 \u3000\u3000\u4eca\u5929\u4e3a\u4ec0\u4e48\u53d8\u4e86\u5462\uff1f \u3000\u3000\u4ed6\u4eec\u4e24\u4eba\u672c\u6765\u5728\u5bb5\u5173\u6ce1\u599e\u6ce1\u5f97\u4e50\u4e0d\u53ef\u8a00\uff0c\u5174\u9ad8\u91c7\u70c8\u4e4b\u4e0b\uff0c\u6b64\u523b\u5374\u7b49\u4e8e\u88ab\u6d47\u4e0b\u4e00\u5934\u51b7\u6c34\u3002 \u3000\u3000\u4e00\u8def\u8c01\u90fd\u6ca1\u518d\u8bf4\u4ec0\u4e48\uff0c\u5f88\u5feb\u5c31\u56de\u5230\u4e94\u5173\u603b\u9547\u5e9c\u3002 \u3000\u3000\u521a\u597d\u5cb3\u5c0f\u98de\u6b63\u5728\u5927\u5385\uff0c\u8001\u8fdc\u5c31\u62db\u547c\u7740\u9053\uff1a\u201c\u4e24\u4f4d\u5f53\u5bb6\u7684\u8bf7\u5230\u8fd9\u91cc\u6765\u4e00\u4e0b\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e5f\u8ddf\u7740\u8fdb\u5165\u5927\u5385\u3002 \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\uff0c\u597d\u4e45\u4e0d\u89c1\uff0c\u4f60\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5f53\u4ed6\u8bf4\u5b8c\u8bdd\uff0c\u624d\u53d1\u89c9\u5cb3\u5c0f\u98de\u7684\u8138\u8272\u4e0d\u5bf9\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u8be5\u5148\u95ee\u95ee\u4f60\u4eec\u4e24\u4f4d\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u53c8\u662f\u4e00\u6123\uff0c\u5947\u602a\uff0c\u600e\u4e48\u8fde\u5cb3\u5c0f\u98de\u4e5f\u50cf\u53d7\u4e86\u8881\u5c0f\u9e64\u7684\u4f20\u67d3\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u63a5\u7740\u51b7\u7b11\u9053\uff1a\u201c\u8272\u4e0d\u8ff7\u4eba\u4eba\u81ea\u8ff7\uff0c\u4f60\u4eec\u4e8c\u4f4d\u5c0f\u5fc3\u8ff7\u660f\u4e86\u8111\u888b\uff01\u201d \u3000\u3000\u4e01\u6d9b\u4ecd\u4e0d\u77e5\u8da3\uff0c\u5482\u4e86\u5482\u5634\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u4f7f\u8005\u4eca\u5929\u597d\u50cf\u5403\u9519\u4e86\u836f\uff1f\u522e\u80e1\u5b50\u4e5f\u4e0d\u662f\u8fd9\u79cd\u522e \u6cd5\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u559d\u9053\uff1a\u201c\u4f4f\u5634\uff01\u4f60\u4eec\u5929\u5929\u5f80\u5bb5\u5173\u8dd1\uff0c\u8fd8\u5f53\u522b\u4eba\u4e0d\u77e5\u9053\uff1f \u3000\u3000\u4f60\u4eec\u7a76\u7adf\u505a\u4e86\u4e9b\u4ec0\u4e48\uff0c\u5fc3\u91cc\u2014\u5b9a\u6709\u6570\uff01\u201d \u3000\u3000\u4e01\u6d9b\u4e5f\u77aa\u4e86\u773c\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\uff0c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u6211\u4eec\u7684\u5de5\u4f5c\u5c31\u662f\u5de1\u5173\uff0c\u600e\u80fd\u4e0d\u5230\u5bb5\u5173 \u53bb\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u518d\u4e5f\u5fcd\u4e0d\u8fc7\uff0c\u626c\u624b\u4e00\u638c\uff0c\u76f4\u5411\u4e01\u6d9b\u63b4\u53bb\u3002 \u3000\u3000\u4e01\u6d9b\u90a3\u91cc\u8eb2\u5f97\u8fc7\uff0c\u88ab\u63b4\u5f97\u76f4\u6454\u5230\u5927\u5385\u95e8\u53e3\uff0c\u624d\u88ab\u95e8\u69db\u62e6\u4f4f\u3002 \u3000\u3000\u4ed6\u505a\u68a6\u4e5f\u6ca1\u60f3\u5230\uff0c\u5cb3\u5c0f\u98de\u7adf\u7136\u516d\u4eb2\u4e0d\u8ba4\uff0c\u51fa\u624b\u4f24\u4eba\u3002 \u3000\u3000\u7ad9\u5728\u4e00\u65c1\u7684\u7518\u9716\uff0c\u4e5f\u770b\u50bb\u4e86\u773c\u3002 \u3000\u3000\u8fde\u8881\u5c0f\u9e64\u4e5f\u6ca1\u60f3\u5230\u5cb3\u5c0f\u98de\u4f1a\u51fa\u73b0\u8fd9\u79cd\u4e3e\u52a8\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u4e01\u6d9b\u8fd8\u771f\u4e0d\u6562\u518d\u8bf4\u4ec0\u4e48\uff0c\u4ed6\u5fc3\u91cc\u6709\u6570\uff0c\u81ea\u5df1\u7684\u8eab\u624b\u548c\u5bf9\u65b9\u76f8\u6bd4\uff0c\u76f8\u5dee\u60ac\u6b8a\uff0c\u4e00 \u65e6\u53cd\u6297\uff0c\u90a3\u5c31\u975e\u5403\u5927\u4e8f\u4e0d\u53ef\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u51dd\u7740\u8138\u8272\u9053\uff1a\u201c\u4e01\u5f53\u5bb6\u7684\uff0c\u5728\u4e0b\u5e0c\u671b\u8fd9\u4e00\u638c\u80fd\u628a\u4f60\u4ece\u68a6\u4e2d\u6253\u9192\uff0c\u4ece\u660e\u5929\u8d77\uff0c\u4e0d\u51c6 \u4f60\u4eec\u4e24\u4eba\u518d\u5230\u5bb5\u5173\u53bb\uff01\u201d \u3000\u3000\u7518\u9716\u58ee\u7740\u80c6\u5b50\u9053\uff1a\u201c\u53ef\u662f\u6211\u4eec\u4e0d\u5de1\u5173\u53c8\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8981\u5de1\u5173\u5fc5\u987b\u7531\u5728\u4e0b\u6216\u8881\u4f7f\u8005\u5e26\u7740\u53bb\uff0c\u5426\u5219\u4f60\u4eec\u5c31\u53ea\u7ba1\u95f2\u7740\u3002\u201d \u3000\u3000\u7518\u9716\u987f\u4e86\u987f\u53c8\u9053\uff1a\u201c\u6211\u4eec\u82e5\u95f2\u7740\uff0c\u5982\u679c\u88ab\u526f\u603b\u9547\u95ee\u8d77\u5462\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4f60\u4eec\u5c31\u8bf4\u662f\u6211\u89c4\u5b9a\u7684\uff0c\u8bf7\u526f\u603b\u9547\u627e\u6211\u597d\u4e86\u3002\u201d \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u540d\u5f1f\u5144\u6765\u62a5\u5927\u516c\u4e3b\u6765\u4e86\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7acb\u5373\u5429\u5490\u4e01\u6d9b\u548c\u7518\u9716\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u56de\u53bb\u4f11\u606f\u53bb\uff01\u201d \u3000\u3000\u7136\u540e\u518d\u5bf9\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u8881\u5927\u54e5\u4e5f\u8bf7\u56de\u53bb\uff0c\u5c0f\u5f1f\u548c\u5927\u516c\u4e3b\u8f83\u719f\uff0c\u7531\u5c0f\u5f1f\u4e00\u4eba\u63a5\u5f85\u5979\u5c31\u591f \u4e86\u3002\u201d \u3000\u3000\u5f53\u4ed6\u5306\u5306\u51fa\u4e86\u5927\u5385\u5230\u5916\u9762\u8fce\u63a5\u65f6\uff0c\u5927\u516c\u4e3b\u767d\u91d1\u51e4\u65e9\u5df2\u8fdb\u4e86\u5927\u95e8\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u628a\u5927\u516c\u4e3b\u63a5\u8fdb\u5927\u5385\uff0c\u5949\u4e0a\u8336\u9053\uff1a\u201c\u5927\u516c\u4e3b\u597d\u4e45\u6ca1\u6765\u4e86\uff0c\u4eca\u5929\u96be\u5f97\u6709\u7a7a\u5230\u4e94\u5173\u603b\u9547 \u6765\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u8bed\u6c14\u4f9d\u7136\u662f\u90a3\u6837\u4eb2\u5207\u67d4\u548c\uff0c\u6d45\u6d45\u4e00\u7b11\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u597d\u4e45\u4e0d\u89c1\uff0c\u6240\u4ee5\u624d\u60f3\u8d77\u6765\u8981\u6765 \u770b\u770b\u4f60\u548c\u82b1\u526f\u603b\u9547\uff0c\u600e\u4e48\u82b1\u526f\u603b\u9547\u6ca1\u51fa\u6765\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u672c\u4ee5\u4e3a\u5979\u5728\u8def\u4e0a\u4f1a\u78b0\u89c1\u7236\u4eb2\uff0c\u542c\u8fd9\u8bed\u6c14\uff0c\u5206\u660e\u5728\u8def\u4e0a\u4e0d\u66fe\u78b0\u89c1\uff0c\u968f\u5373\u7b54\u9053\uff1a\u201c\u526f \u603b\u9547\u665a\u9910\u65f6\u8fd8\u5728\uff0c\u996d\u540e\u4ea4\u4ee3\u4e86\u4e00\u58f0\u5c31\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u201c\u4ed6\u5e76\u6ca1\u8bf4\u660e\uff0c\u5f88\u53ef\u80fd\u662f\u5230\u5404\u5173\u5de1\u89c6\u53bb\u4e86\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u8e59\u8d77\u9edb\u7709\u9053\uff1a\u201c\u6211\u6b63\u597d\u6709\u8981\u7d27\u7684\u4e8b\u8f6c\u544a\u4ed6\uff0c\u504f\u504f\u4ed6\u53c8\u4e0d\u5728\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u4e2d\u4e00\u52a8\uff0c\u88c5\u51fa\u6beb\u4e0d\u5728\u610f\u7684\u795e\u8272\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u8981\u7d27\u7684\u4e8b\uff1f\u5927\u516c\u4e3b\u4e0d\u59a8\u544a\u8bc9\u6211\uff0c\u7531 \u6211\u8f6c\u544a\u526f\u603b\u9547\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u8d77\u521d\u4f3c\u662f\u6709\u4e9b\u72b9\u8c6b\uff0c\u4f46\u5f88\u5feb\u5c31\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u4e5f\u4e0d\u662f\u5916\u4eba\uff0c\u8ba9\u4f60\u77e5\u9053\u4e5f\u65e0\u59a8\uff0c\u4e0d \u8fc7\u4f60\u5343\u4e07\u4e0d\u80fd\u900f\u9732\u7ed9\u4efb\u4f55\u4eba\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u96be\u9053\u5927\u516c\u4e3b\u8fd8\u4fe1\u4e0d\u8fc7\u6211\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u53ef\u8bb0\u5f97\uff0c\u4e0a\u6b21\u6211\u66fe\u5e26\u4f60\u5230\u8fc7\u80b2\u5316\u57ce\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u8fd9\u4e8b\u5c31\u5728\u4e0d\u4e45\u4ee5\u524d\uff0c\u6211\u4e0d\u4f46\u8bb0\u5f97\uff0c\u800c\u4e14\u8fd8\u8bb0\u5f97\u6e05\u6e05\u695a\u695a\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u7c89\u988a\u62bd\u6410\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u8c01\u4e5f\u60f3\u4e0d\u5230\uff0c\u80b2\u5316\u57ce\u4e3b\u5c45\u7136\u53db\u79bb\u4e86\u7687\u5143\u6559\uff0c\u73b0\u5728\u7b49\u4e8e\u5df2\u548c \u6559\u4e3b\u6b63\u5f0f\u4e3a\u654c\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6545\u4f5c\u5927\u5403\u4e00\u60ca\uff0c\u60ca\u5f97\u51e0\u4e4e\u6709\u4e9b\u96be\u4ee5\u76f8\u4fe1\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff0c\u5927\u516c\u4e3b\u662f\u6709\u610f\u5f00\u73a9\u7b11 \u968f\u4fbf\u8bf4\u8bf4\u5427\uff01\u201d \u3000\u3000\u5927\u516c\u4e3b\u6b63\u8272\u9053\uff1a\u201c\u8fd9\u6837\u7684\u5927\u4e8b\uff0c\u600e\u53ef\u5f00\u73a9\u7b11\u968f\u4fbf\u8bf4\u8bf4\uff1f\u201d \u3000\u3000\u201c\u83ab\u975e\u5927\u516c\u4e3b\u5df2\u7ecf\u6293\u5230\u4e86\u80b2\u5316\u57ce\u4e3b\u8c0b\u53cd\u7684\u8bc1\u636e\uff1f\u201d \u3000\u3000\u201c\u8fd8\u8981\u4ec0\u4e48\u8bc1\u636e\uff0c\u4ed6\u5df2\u516c\u7136\u6297\u547d\uff0c\u4e0d\u542c\u6559\u4e3b\u8c15\u4ee4\uff0c\u800c\u4e14\u8fde\u4f24\u4e86\u603b\u575b\u4e24\u4f4d\u62a4\u6cd5\u3002\u201d \u3000\u3000\u201c\u8fd9\u786e\u662f\u4e00\u4ef6\u4ee4\u4eba\u9707\u60ca\u7684\u5927\u4e8b\uff0c\u6559\u4e3b\u6253\u7b97\u600e\u4e48\u5904\u7f6e\uff1f\u201d \u3000\u3000\u201c\u672c\u6765\uff0c\u80b2\u5316\u57ce\u4e3b\u662f\u6559\u4e3b\u7684\u5927\u5e08\u5144\uff0c\u6559\u4e3b\u8fd8\u6253\u4e9b\u4e0d\u5fcd\u5bf9\u4ed6\u4e0b\u624b\uff0c\u4f46\u79e6\u603b\u62a4\u6cd5\u5374\u5e0c\u671b\u6559\u4e3b \u4e0d\u80fd\u56e0\u5faa\u59d1\u606f\uff0c\u56e0\u4e4b\uff0c\u6559\u4e3b\u5df2\u51b3\u5b9a\u6293\u5230\u80b2\u5316\u57ce\u4e3b\u540e\uff0c\u628a\u4ed6\u6309\u6559\u89c4\u5f53\u4f17\u5904\u6b7b\u3002\u201d \u3000\u3000\u201c\u6559\u4e3b\u80fd\u6709\u529e\u6cd5\u6349\u4f4f\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u201c\u5fc5\u8981\u65f6\u6559\u4e3b\u53ea\u597d\u8ba9\u80b2\u5316\u57ce\u7389\u77f3\u4ff1\u711a\uff0c\u8ba9\u80b2\u5316\u57ce\u91cc\u7684\u6570\u767e\u4eba\u540c\u5f52\u4e8e\u5c3d\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u672a\u514d\u624b\u6bb5\u592a\u72e0\u4e86\u4e9b\u5427\uff1f\u201d \u3000\u3000\u201c\u8fd9\u662f\u6ca1\u529e\u6cd5\u7684\u4e8b\uff0c\u5373\u4f7f\u5984\u6740\u4e00\u767e\uff0c\u4e5f\u7edd\u4e0d\u80fd\u8ba9\u8be5\u6b7b\u7684\u4eba\u6d3b\u547d\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u542c\u5230\u8fd9\u91cc\uff0c\u5fc3\u60c5\u7684\u7d27\u8feb\u53ef\u60f3\u800c\u77e5\uff0c\u4f46\u4ed6\u8868\u9762\u5374\u80fd\u4e0d\u9732\u58f0\u8272\uff0c\u9ed8\u4e86\u4e00\u9ed8\u9053\uff1a\u201c\u5927\u516c \u4e3b\u4eca\u5929\u6765\uff0c\u5c31\u662f\u8981\u8f6c\u544a\u526f\u603b\u9547\u8fd9\u4ef6\u4e8b\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u662f\u5949\u6559\u4e3b\u4e4b\u547d\uff0c\u79d8\u5bc6\u8f6c\u544a\u672c\u6559\u5404\u9996\u8111\u4eba\u7269\uff0c\u8ba9\u4ed6\u4eec\u5148\u505a\u597d\u5fc3\u7406\u51c6 \u5907\uff0c\u4ee5\u514d\u5403\u4e8f\u4e0a\u5f53\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u968f\u53e3\u95ee\u9053\uff1a\u201c\u5927\u516c\u4e3b\u90fd\u8f6c\u544a\u8fc7\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u50cf\u5bcc\u56fd\u57ce\u4e3b\u4ee5\u53ca\u62db\u8d24\u9986\u4e3b\u7b49\u4eba\uff0c\u6211\u90fd\u5df2\u8f6c\u544a\u8fc7\u4e86\u3002\u201d \u3000\u3000\u201c\u4e94\u5173\u7684\u5404\u7edf\u9886\u77e5\u9053\u8fd9\u4ef6\u4e8b\u5417\uff1f\u201d \u3000\u3000\u201c\u4e94\u5173\u7edf\u9886\u6bd4\u8d77\u4ee5\u4e0a\u5404\u4eba\uff0c\u7b97\u662f\u4f4e\u4e86\u4e00\u7ea7\uff0c\u73b0\u5728\u8fd8\u6ca1\u6709\u5fc5\u8981\u8ba9\u4ed6\u4eec\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e8b\u5927\u516c\u4e3b\u544a\u8bc9\u4e86\u526f\u603b\u9547\uff0c\u662f\u5426\u8fd8\u8981\u526f\u603b\u9547\u91c7\u53d6\u4ec0\u4e48\u884c\u52a8\uff1f\u201d \u3000\u3000\u201c\u6559\u4e3b\u6682\u65f6\u8fd8\u6309\u5175\u4e0d\u52a8\uff0c\u5fc5\u8981\u65f6\u4e5f\u8bb8\u4f1a\u8c03\u52a8\u8fd9\u8fb9\u7684\u4eba\u9a6c\u53bb\u8fdb\u653b\u80b2\u5316\u57ce\uff0c\u5cb3\u516c\u5b50\uff0c\u4e5f\u8bb8\u4f60 \u7acb\u529f\u7684\u673a\u4f1a\u9a6c\u4e0a\u5230\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u83ab\u975e\u662f\u8981\u6211\u4e5f\u53bb\u6253\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u901a\u8fc7\u4e94\u5173\u8fdb\u5165\u5929\u8c37\u7684\uff0c\u8fd9\u51e0\u5e74\u8db3\u6709\u51e0\u767e\u4eba\u4e4b\u591a\uff0c\u6210\u7ee9\u6700\u4f18\u7684\u53ea\u6709\u56db\u4eba\uff0c\u800c \u4f60\u548c\u82b1\u526f\u603b\u9547\u6b63\u662f\u5176\u4e2d\u7684\u4e24\u4f4d\uff0c\u8981\u8fdb\u653b\u80b2\u5316\u57ce\uff0c\u6559\u4e3b\u548c\u603b\u62a4\u6cd5\u5f53\u7136\u4f1a\u6700\u5148\u8003\u8651\u5230\u4f60\u4eec\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u53e6\u4e24\u4f4d\u6210\u7ee9\u597d\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u5e7d\u5e7d\u4e00\u53f9\u9053\uff1a\u201c\u8bf4\u8d77\u6765\u771f\u53ef\u60dc\uff0c\u53e6\u4e24\u4eba\u7adf\u5168\u5728\u80b2\u5316\u57ce\uff0c\u4ed6\u4eec\u4e00\u4e2a\u53eb\u65b9\u5927\u94ce\uff0c\u3000\u4e00 \u4e2a\u53eb\u4f55\u6167\u4ed9\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u60f3\u4e86\u4e00\u60f3\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e8b\u5148\u4e0d\u628a\u4ed6\u4eec\u4e24\u4eba\u8c03\u56de\u603b\u575b\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u53c8\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u90fd\u662f\u56e0\u4e3a\u6709\u7f6a\u624d\u88ab\u6253\u5165\u80b2\u5316\u57ce\uff0c\u4e00\u4f4d\u5728\u53d7\u5211\uff0c\u3000\u4e00\u4f4d\u670d\u4e0b \u836f\u7269\u5165\u4e86\u68fa\uff0c\u4ed6\u4eec\u4e00\u65e6\u51fa\u6765\uff0c\u53ea\u6709\u5e2e\u80b2\u5316\u57ce\u4e3b\uff0c\u600e\u80af\u5e2e\u6559\u4e3b\u5462\uff0c\u4f55\u51b5\u76ee\u524d\u80b2\u5316\u57ce\u65e9\u5df2\u4e0d\u542c\u603b \u575b\u4ee4\u8c15\uff0c\u5373\u4f7f\u6559\u4e3b\u60f3\u8981\u4eba\u4e5f\u8981\u4e0d\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6b64\u523b\u5185\u5fc3\u53cd\u800c\u6709\u4e9b\u5b89\u6170\uff0c\u56e0\u4e3a\u8fdb\u5165\u5929\u8c37\u7684\u56db\u5927\u9ad8\u624b\uff0c\u7adf\u7136\u90fd\u662f\u80b2\u5316\u57ce\u4e3b\u7684\u529b\u91cf\uff0c \u4e0d\u6101\u4e0d\u80fd\u548c\u7687\u5143\u6559\u6297\u8861\u3002 \u3000\u3000\u4ed6\u7f04\u9ed8\u4e86\u8bb8\u4e45\uff0c\u624d\u4e5f\u5401\u53e3\u6c14\u9053\uff1a\u201c\u771f\u60f3\u4e0d\u5230\u5929\u8c37\u4f1a\u53d1\u751f\u8fd9\u6837\u7684\u4e8b\uff0c\u504f\u504f\u53c8\u88ab\u6211\u8d76\u4e0a\u4e86\uff0c \u4e0d\u8fc7\u6700\u4f7f\u5351\u804c\u611f\u52a8\u7684\uff0c\u662f\u5927\u516c\u4e3b\u5c45\u7136\u80af\u628a\u8fd9\u6837\u7684\u673a\u5bc6\u544a\u8bc9\u5351\u804c\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u5ffd\u7136\u7738\u5149\u7d27\u7d27\u51dd\u4f4f\u5728\u5cb3\u5c0f\u98de\u8138\u4e0a\uff0c\u795e\u79d8\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u771f\u662f\u4e2a\u50bb\u5b69\u5b50\uff0c\u96be\u9053\u76f4\u5230\u73b0 \u5728\u5fc3\u91cc\u8fd8\u4e0d\u660e\u767d\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5f53\u7136\u5df2\u660e\u767d\u4e86\u51e0\u5206\uff0c\u5374\u6545\u4f5c\u832b\u7136\u9053\uff1a\u201c\u5351\u804c\u660e\u767d\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u6559\u4e3b\u90a3\u5929\u5355\u72ec\u53ec\u89c1\u4f60\uff0c\u4f60\u8bf4\u662f\u4e3a\u4ec0\u4e48\u5427\uff1f\u201d \u3000\u3000\u201c\u6559\u4e3b\u6ca1\u8bb2\uff0c\u5351\u804c\u600e\u80fd\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u90a3\u6211\u5c31\u63d0\u524d\u544a\u8bc9\u4f60\uff0c\u6559\u4e3b\u5df2\u7ecf\u51b3\u5b9a\u628a\u4f60\u53ec\u4e3a\u4e1c\u5e8a\u5feb\u5a7f\uff0c\u628a\u6211\u90a3\u4e09\u59b9\u7389\u98ce\u5ac1\u7ed9\u4f60\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u88c5\u7740\u5446\u4e86\u4e00\u5446\u9053\uff1a\u201c\u5927\u516c\u4e3b\u4e0d\u662f\u5f00\u73a9\u7b11\u5427\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u6b63\u8272\u9053\uff1a\u201c\u8fd9\u6837\u91cd\u8981\u7684\u4e8b\uff0c\u6211\u600e\u4f1a\u548c\u4f60\u5f00\u73a9\u7b11\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u6559\u4e3b\u8fd9\u6837\u505a\uff0c\u5c31\u7b49\u4e8e\u5f00\u73a9\u7b11\uff0c\u5351\u804c\u8eab\u4efd\u5351\u5fae\uff0c\u600e\u4e48\u80fd\u914d\u4e0a\u4e09\u516c\u4e3b\u5462\uff1f\u201d \u3000\u3000\u201c\u6559\u4e3b\u662f\u53ea\u8ba4\u4eba\u624d\u4e0d\u8ba4\u8eab\u4efd\u7684\uff0c\u800c\u4e14\u6211\u90a3\u4e09\u59b9\u4e5f\u975e\u4f60\u4e0d\u5ac1\uff0c\u4f60\u867d\u7136\u6ca1\u89c1\u8fc7\u5979\uff0c\u5979\u5374\u5df2\u770b \u5230\u4f60\u591a\u6b21\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e8b\u76ee\u524d\u6709\u591a\u5c11\u4eba\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u6559\u4e3b\u8fd8\u6ca1\u6b63\u5f0f\u5ba3\u5e03\u3002\u77e5\u9053\u7684\u4eba\u81ea\u7136\u4e0d\u591a\u3002\u201d \u3000\u3000\u201c\u6559\u4e3b\u4e3a\u4ec0\u4e48\u4e0d\u6b63\u5f0f\u5ba3\u5e03\uff1f\u201d \u3000\u3000\u201c\u56e0\u4e3a\u4f60\u4eec\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u800c\u4e14\u4f60\u65e2\u5165\u4e86\u5929\u8c37\uff0c\u4e5f\u4e0d\u53ef\u80fd\u518d\u51fa\u53bb\uff0c\u7b49\u8fc7\u51e0\u5e74\u4f60\u4eec\u5409\u671f\u5c06\u5230\u4e4b \u524d\u518d\u5ba3\u5e03\uff0c\u4e0d\u662f\u5f88\u597d\u4e48\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u76b1\u4e86\u76b1\u7709\u9053\uff1a\u201c\u539f\u6765\u5c31\u56e0\u4e3a\u8fd9\u5c42\u5173\u7cfb\uff0c\u5927\u516c\u4e3b\u4eca\u665a\u624d\u80af\u628a\u8fd9\u673a\u5bc6\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fde\u4e94\u5173\u7edf\u9886\u90fd\u4e0d\u77e5\u9053\uff0c\u4e0d\u7136\u6211\u600e\u4f1a\u5355\u5355\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u8bf4\u5230\u8fd9\u91cc\uff0c\u5411\u5916\u671b\u4e86\u671b\u5929\u8272\u9053\uff1a\u201c\u82b1\u526f\u603b\u9547\u600e\u4e48\u8fd8\u4e0d\u56de\u6765\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u526f\u603b\u9547\u665a\u996d\u540e\u624d\u51fa\u53bb\uff0c\u54ea\u80fd\u8fd9\u6837\u5feb\u5c31\u56de\u6765\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u4f3c\u662f\u6709\u4e9b\u8e0c\u8e87\u3002\u9887\u4e3a\u7740\u6025\u7684\u95ee\u9053\uff1a\u201c\u4ed6\u5927\u7ea6\u4ec0\u4e48\u65f6\u5019\u53ef\u4ee5\u56de\u6765\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6447\u5934\u9053\uff1a\u201c\u90a3\u8981\u770b\u4ed6\u662f\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\uff0c\u8981\u529e\u7684\u662f\u4ec0\u4e48\u4e8b\uff1f\u5927\u516c\u4e3b\u597d\u50cf\u5f88\u6025\u7684\u6837 \u5b50\uff1f\u201d \u3000\u3000\u5927\u516c\u4e3b\u8e59\u8d77\u7fe0\u7709\u9053\uff1a\u201c\u6211\u5f53\u7136\u6025\uff0c\u56e0\u4e3a\u6211\u5fc5\u987b\u628a\u8fd9\u4e8b\u4eb2\u81ea\u544a\u8bc9\u4ed6\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u96be\u9053\u7531\u5351\u804c\u8f6c\u544a\u4ed6\u8fd8\u4e0d\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u6447\u5934\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u6211\u5fc5\u987b\u7b49\u7740\u4ed6\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u82e5\u526f\u603b\u9547\u5230\u6df1\u591c\u624d\u56de\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u6211\u5c31\u7b49\u4ed6\u5230\u6df1\u591c\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u89c9\u5f00\u59cb\u7d27\u5f20\uff0c\u4ed6\u9884\u8ba1\u7236\u4eb2\u5f80\u8fd4\u80b2\u5316\u57ce\uff0c\u518d\u52a0\u4e0a\u548c\u4e25\u57ce\u4e3b\u8be6\u8c08\u4ee5\u53ca\u548c\u6bcd\u4eb2\u89c1\u9762\uff0c \u8bf4\u4e0d\u5b9a\u8981\u5230\u4e94\u66f4\u5927\u660e\u65f6\u624d\u56de\u6765\uff0c\u82e5\u5927\u516c\u4e3b\u4e5f\u7b49\u5230\u90a3\u65f6\uff0c\u5fc5\u5b9a\u4f1a\u5f15\u8d77\u7591\u5fc3\uff0c\u5c82\u4e0d\u7cdf\u7cd5\u3002 \u3000\u3000\u63a5\u7740\u4ed6\u5185\u5fc3\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u6211\u4f55\u4e0d\u8d81\u8fd9\u65f6\u628a\u5927\u516c\u4e3b\u64d2\u4f4f\uff0c\u8fde\u591c\u9001\u5230\u80b2\u5316\u57ce\u505a\u4eba\u8d28\u2026\u2026\u201d \u3000\u3000\u4f46\u4ed6\u5185\u5fc3\u4ea4\u6218\u518d\u4e09\uff0c\u603b\u89c9\u73b0\u5728\u52a8\u624b\uff0c\u672a\u514d\u592a\u65e9\uff0c\u82e5\u8d38\u7136\u884c\u52a8\uff0c\u5f88\u53ef\u80fd\u53cd\u800c\u574f\u4e86\u5927\u4e8b\uff0c\u5230 \u90a3\u65f6\u5c31\u540e\u6094\u83ab\u53ca\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u5fc5\u987b\u8bbe\u6cd5\u8ba9\u5927\u516c\u4e3b\u65e9\u4e9b\u79bb\u53bb\u3002 \u3000\u3000\u53c8\u5750\u4e86\u4e00\u4f1a\uff0c\u5cb3\u5c0f\u98de\u7ec8\u4e8e\u642d\u8baa\u7740\u9053\uff1a\u201c\u8ba9\u5927\u516c\u4e3b\u4e45\u7b49\uff0c\u53c8\u803d\u8bef\u4e86\u7761\u7720\u65f6\u95f4\uff0c\u5b9e\u5728\u4e0d\u597d\u610f \u601d\uff0c\u4f9d\u5351\u804c\u731c\u60f3\uff0c\u526f\u603b\u9547\u4e5f\u8bb8\u5230\u603b\u575b\u53bb\u4e86\uff0c\u5230\u4e86\u603b\u575b\uff0c\u5fc5\u5b9a\u662f\u89c1\u6559\u4e3b\uff0c\u6559\u4e3b\u4e5f\u5fc5\u5b9a\u544a\u8bc9\u4ed6\u8fd9 \u4e8b\uff0c\u5927\u516c\u4e3b\u8fd8\u662f\u7528\u4e0d\u7740\u7b49\u7684\u597d\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u679c\u7136\u88ab\u4ed6\u8bf4\u52a8\uff0c\u8d77\u8eab\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u8fd9\u5c31\u56de\u53bb\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u76f4\u628a\u5927\u516c\u4e3b\u9001\u51fa\u597d\u51e0\u91cc\u8def\uff0c\u624d\u544a\u522b\u800c\u56de\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u8d76\u5230\u80b2\u5316\u57ce\uff0c\u5df2\u63a5\u8fd1\u4e8c\u66f4\u3002 \u3000\u3000\u4e25\u5bd2\u628a\u4ed6\u76f4\u63a5\u62db\u5f85\u5728\u5185\u5e9c\u5ba2\u5385\u3002 \u3000\u3000\u5728\u82b1\u7389\u9e9f\u6765\u8bf4\uff0c\u4e25\u5bd2\u662f\u524d\u8f88\u4eba\u7269\uff0c\u8eab\u4efd\u5730\u4f4d\u4e5f\u6bd4\u81ea\u5df1\u9ad8\u5f97\u591a\uff0c\u4e0d\u514d\u8981\u5927\u793c\u62dc\u89c1\u3002 \u3000\u3000\u4f46\u4e25\u5bd2\u5374\u53c8\u628a\u4ed6\u4ee5\u4eb2\u5bb6\u7fc1\u770b\u5f85\uff0c\u8ba9\u4ed6\u548c\u81ea\u5df1\u5e73\u8d77\u5e73\u5750\u3002 \u3000\u3000\u4ed6\u4e3a\u4e86\u597d\u597d\u63a5\u5f85\u82b1\u7389\u9e9f\uff0c\u7279\u5730\u5907\u4e86\u9152\u548c\u51e0\u6837\u5c0f\u83dc\uff0c\u591c\u534a\u5bf9\u914c\uff0c\u522b\u6709\u4e00\u756a\u98ce\u5473\u3002 \u3000\u3000\u4e09\u676f\u9152\u4e0b\u809a\uff0c\u4e25\u5bd2\u5df2\u662f\u65e0\u8bdd\u4e0d\u8c08\uff1a\u201c\u82b1\u8001\u5f1f\uff0c\u4e25\u67d0\u5bf9\u4f60\uff0c\u53ef\u79f0\u76f8\u89c1\u6068\u665a\uff0c\u6b64\u756a\u80fd\u5f97\u4f60\u76f8 \u52a9\uff0c\u54b1\u4eec\u5f88\u53ef\u4ee5\u548c\u7687\u5143\u6559\u5927\u5e72\u4e00\u573a\u4e86\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u57ce\u4e3b\u5f85\u82b1\u67d0\u6069\u91cd\u5982\u5c71\uff0c\u8fd9\u6b21\u57ce\u4e3b\u548c\u7687\u5143\u6559\u6f14\u53d8\u6210\u8fd9\u79cd\u5c40\u9762\uff0c\u53ef\u8bf4\u5b8c\u5168\u56e0\u5185 \u5b50\u4e4b\u4e8b\u800c\u8d77\uff0c\u82b1\u67d0\u600e\u80fd\u4e0d\u611f\u6069\u56fe\u62a5\uff0c\u5373\u4fbf\u7c89\u8eab\u788e\u9aa8\uff0c\u4e5f\u5728\u6240\u4e0d\u60dc\u3002\u201d \u3000\u3000\u4e25\u5bd2\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u4eca\u540e\u5bf9\u6297\u7687\u5143\u6559\uff0c\u82b1\u8001\u5f1f\u548c\u5c0a\u592b\u4eba\u4ee5\u53ca\u4ee4\u90ce\uff0c\u6b63\u662f\u672c\u57ce\u7684\u4e3b\u529b\uff0c\u4e25 \u67d0\u7684\u6240\u6709\u5e0c\u671b\uff0c\u53ef\u8bf4\u5b8c\u5168\u5bc4\u6258\u5728\u82b1\u8001\u5f1f\u8eab\u4e0a\u4e86\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u57ce\u4e3b\u8fd9\u8fb9\uff0c\u662f\u5426\u8fd8\u6709\u4e9b\u9700\u8981\u51c6\u5907\u7684\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u82e5\u8bf4\u5fc5\u987b\u51c6\u5907\u7684\u4e8b\uff0c\u4e0d\u5916\u7cae\u8349\u4e0d\u8db3\uff0c\u4e00\u65e6\u7cae\u8349\u4f9b\u5e94\u4e0d\u4e0a\uff0c\u5c82\u4e0d\u4e0d\u6218\u81ea\u6e83\u3002\u201d \u3000\u3000\u201c\u80b2\u5316\u57ce\u76ee\u524d\u6709\u591a\u5c11\u4eba\u9a6c\uff1f\u201d \u3000\u3000\u201c\u76ee\u524d\u81f3\u5c11\u5728\u4e94\u3001\u516d\u767e\u4eba\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u7684\u7cae\u8349\u8fd8\u80fd\u4f9b\u5e94\u591a\u4e45\uff1f\u201d \u3000\u3000\u201c\u5927\u7ea6\u53ea\u80fd\u7ef4\u6301\u4e00\u6708\u5de6\u53f3\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u6c89\u541f\u4e86\u8bb8\u4e45\u9053\uff1a\u201c\u8fd9\u8fb9\u7684\u7cae\u8349\uff0c\u4e0d\u6d88\u8bf4\u662f\u7531\u5bcc\u56fd\u57ce\u4f9b\u5e94\uff0c\u636e\u8bf4\u5bcc\u56fd\u57ce\u4e3b\u662f\u57ce\u4e3b\u7684 \u4e8c\u5e08\u5f1f\uff0c\u53ef\u5426\u73b0\u5728\u5546\u8bf7\u5bcc\u56fd\u57ce\u4e3b\u628a\u7cae\u8349\u63d0\u524d\u62e8\u6765\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u6447\u5934\u82e6\u7b11\u9053\uff1a\u201c\u5bcc\u56fd\u57ce\u4e3b\u9ec4\u4e00\u9053\u662f\u4e25\u67d0\u7684\u5e08\u5f1f\uff0c\u4f46\u4ed6\u5374\u4e00\u76f4\u5fe0\u4e8e\u7687\u5143\u6559\u4e3b\uff0c\u800c\u4e14\u7687 \u5143\u6559\u4e3b\u5fc5\u5b9a\u65e9\u5df2\u901a\u77e5\u4ed6\u505c\u6b62\u672c\u57ce\u7684\u7cae\u8349\u4f9b\u5e94\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u82e5\u771f\u662f\u5982\u6b64\uff0c\u82b1\u67d0\u60c5\u613f\u5e26\u51e0\u540d\u624b\u4e0b\u5230\u5bcc\u56fd\u57ce\u52ab\u7cae\u3002\u201d \u3000\u3000\u4e25\u5bd2\u8003\u8651\u4e86\u8bb8\u4e45\u9053\uff1a\u201c\u8fd9\u529e\u6cd5\u867d\u7136\u53ef\u884c\uff0c\u4f46\u4e25\u67d0\u5374\u5e0c\u671b\u6682\u65f6\u522b\u91c7\u53d6\u884c\u52a8\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u82e5\u60f3\u52ab\u7cae\u6210\u529f\uff0c\u5fc5\u987b\u5728\u53cc\u65b9\u5c1a\u672a\u6b63\u5f0f\u4ea4\u6218\u4e4b\u524d\uff0c\u4e00\u65e6\u53cc\u65b9\u6b63\u5f0f\u542f\u8d77\u6218\u706b\uff0c \u90a3\u5c31\u5f88\u96be\u5f97\u624b\u4e86\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u82b1\u8001\u5f1f\u8fd9\u756a\u8bdd\u56fa\u7136\u6709\u7406\uff0c\u4e0d\u8fc7\u4e25\u67d0\u603b\u89c9\u5f97\u8fd9\u573a\u6218\u4e8b\u4e0d\u53ef\u80fd\u6301\u7eed\u592a\u4e45\uff0c\u4e0d\u8bba\u8c01 \u80dc\u8c01\u8d25\uff0c\u5728\u4e00\u6708\u4e4b\u5185\u5fc5\u6709\u7ed3\u679c\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u5ffd\u7136\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd9\u9053\uff1a\u201c\u82b1\u67d0\u60f3\u5411\u57ce\u4e3b\u6253\u542c\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4eba\u5f88\u53ef\u80fd\u4e5f\u5728\u80b2\u5316\u57ce\u91cc\u3002\u201d \u3000\u3000\u4e25\u5bd2\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u82b1\u8001\u5f1f\u95ee\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u8fd9\u4eba\u53eb\u65b9\u5929\u94ce\uff0c\u4e09\u5e74\u524d\u521d\u5165\u5929\u8c37\u65f6\uff0c\u636e\u8bf4\u901a\u8fc7\u4e94\u5173\u662f\u6210\u7ee9\u6700\u4f18\u7684\u4e00\u4f4d\u3002\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u8fd9\u4eba\u6b63\u5728\u672c\u57ce\uff0c\u82b1\u8001\u5f1f\u4e3a\u4ec0\u4e48\u5ffd\u7136\u63d0\u8d77\u4ed6\u6765\uff1f\u201d \u3000\u3000\u201c\u4e0d\u6562\u76f8\u7792\uff0c\u5341\u5e74\u524d\u82b1\u67d0\u827a\u6ee1\u4e0b\u5c71\uff0c\u521d\u5c65\u6c5f\u6e56\u65f6\uff0c\u66fe\u548c\u53e6\u4e24\u4eba\u4e49\u7ed3\u91d1\u5170\uff0c\u65b9\u5929\u94ce\u6b63\u662f\u82b1 \u67d0\u7ed3\u62dc\u7684\u4e8c\u54e5\uff0c\u82b1\u67d0\u548c\u5185\u5b50\u4e09\u5e74\u524d\u5728\u8fdb\u5165\u5929\u8c37\u4e4b\u524d\uff0c\u672c\u6765\u4ed6\u7b54\u5e94\u4ee3\u4e3a\u629a\u517b\u72ac\u5b50\u5c0f\u98de\uff0c\u4e0d\u60f3\u4ed6 \u5374\u5148\u82b1\u67d0\u592b\u5987\u8fdb\u5165\u5929\u8c37\uff0c\u5f85\u82b1\u67d0\u4e5f\u6765\u5230\u5929\u8c37\u540e\uff0c\u624d\u542c\u8bf4\u4ed6\u5df2\u88ab\u6253\u5165\u80b2\u5316\u57ce\u3002\u201d \u3000\u3000\u4e25\u5bd2\u5927\u559c\u9053\uff1a\u201c\u90a3\u592a\u597d\u4e86\uff0c\u5982\u4eca\u53c8\u6709\u65b9\u5927\u4fa0\u76f8\u52a9\uff0c\u4e25\u67d0\u4f55\u6101\u4e0d\u80fd\u5bf9\u6297\u7687\u5143\u6559\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u6025\u6025\u95ee\u9053\uff1a\u201c\u4ed6\u76ee\u524d\u4eba\u5728\u90a3\u91cc\uff0c\u57ce\u4e3b\u53ef\u5426\u5bb9\u82b1\u67d0\u9a6c\u4e0a\u548c\u4ed6\u89c1\u89c1\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u5374\u53c8\u51c4\u7136\u4e00\u53f9\u9053\uff1a\u201c\u65b9\u5927\u4f19\u4e00\u76f4\u88ab\u6253\u5728\u7075\u5802\uff0c\u800c\u4e14\u670d\u836f\u5165\u68fa\uff0c\u867d\u7136\u73b0\u5728\u5c31\u53ef\u4ee5\u7ed9\u4ed6 \u670d\u836f\u9192\u6765\uff0c\u4f46\u7cbe\u795e\u4f53\u529b\u5374\u975e\u77ed\u65f6\u5185\u53ef\u4ee5\u6062\u590d\u3002\u5373\u4fbf\u8981\u4ed6\u76f8\u52a9\u4e00\u81c2\u4e4b\u529b\uff0c\u81f3\u5c11\u4e5f\u9700\u534a\u4e2a\u6708\u4e4b\u540e \u4e86\u3002\u201d \u3000\u3000\u4ed6\u9ed8\u4e86\u4e00\u9ed8\u53c8\u9053\uff1a\u201c\u672c\u6765\uff0c\u95e8\u524d\u5728\u7075\u5802\u670d\u836f\u5165\u68fa\u7684\uff0c\u4e0d\u4e0b\u5341\u4eba\u4e4b\u591a\uff0c\u8fd9\u4e9b\u4eba\u90fd\u79f0\u5f97\u4e0a\u662f \u6b66\u6797\u4e2d\u7edd\u9876\u9ad8\u624b\uff0c\u4f46\u4e25\u67d0\u8003\u8651\u518d\u4e09\u3001\u6700\u540e\u8fd8\u662f\u51b3\u5b9a\u76ee\u524d\u4e0d\u8ba9\u4ed6\u4eec\u6d3b\u8fc7\u6765\u3002\u201d \u3000\u3000\u201c\u57ce\u4e3b\u4e3a\u4ec0\u4e48\u4e0d\u5229\u7528\u4ed6\u4eec\u6765\u5bf9\u6297\u7687\u5143\u6559\uff0c\u82e5\u8fd9\u4e9b\u4eba\u90fd\u80fd\u6062\u590d\u6b66\u529f\uff0c\u672c\u57ce\u7684\u5b9e\u529b\u5c31\u5927\u5927\u8d85 \u8fc7\u7687\u5143\u6559\u4e86\u3002\u201d \u3000\u3000\u201c\u82b1\u8001\u5f1f\u6709\u6240\u4e0d\u77e5\uff0c\u8fd9\u4e9b\u4eba\u867d\u7136\u4e0d\u4e00\u5b9a\u80af\u6548\u5fe0\u7687\u5143\u6559\u4e3b\uff0c\u4f46\u4e5f\u5fc5\u4e0d\u80af\u6548\u5fe0\u4e25\u67d0\uff0c\u4e00\u65e6\u8ba9 \u4ed6\u4eec\u6d3b\u8fc7\u6765\u6062\u590d\u6b66\u529f\uff0c\u53cd\u800c\u96be\u4ee5\u63a7\u5236\uff0c\u5230\u90a3\u65f6\u672c\u57ce\u5fc5\u5b9a\u9677\u5165\u5927\u4e69\u7684\u5c40\u9762\uff0c\u4e25\u67d0\u5c82\u4e0d\u53cd\u800c\u81ea\u98df \u6076\u679c\uff1f\u81f3\u4e8e\u65b9\u5927\u4fa0\uff0c\u65e2\u7136\u662f\u82b1\u67d0\u8001\u5f1f\u7684\u4e49\u5144\uff0c\u4e25\u67d0\u81ea\u7136\u4f1a\u9a6c\u4e0a\u8ba9\u4ed6\u590d\u6d3b\u3002\u201d \u3000\u3000\u8fd9\u65f6\u82b1\u7389\u9e9f\u771f\u662f\u559c\u4e4b\u4e0d\u80dc\uff0c\u65e0\u610f\u4e2d\u7adf\u53c8\u6551\u51fa\u4e86\u4e49\u5144\u65b9\u5929\u94ce\u3002 \u3000\u3000\u4ed6\u6c89\u9ed8\u4e86\u534a\u664c\u9053\uff1a\u201c\u53e6\u6709\u4e00\u4eba\uff0c\u4e5f\u662f\u5f53\u521d\u82b1\u67d0\u592b\u5987\u60f3\u8fdb\u5165\u5929\u8c37\u6551\u51fa\u7684\u4e00\u4eba\u3002\u4e0d\u8fc7\u4ed6\u8fdb\u5165 \u5929\u8c37\u81f3\u5c11\u5df2\u6709\u5341\u5e74\u4e4b\u4e45\uff0c\u82b1\u67d0\u8fd9\u4e09\u5e74\u591a\u6765\uff0c\u5374\u4e00\u76f4\u6ca1\u542c\u5230\u4ed6\u7684\u6d88\u606f\uff0c\u8fde\u662f\u751f\u662f\u6b7b\uff0c\u4e5f\u96be\u4ee5\u9884 \u6599\u3002\u201d \u3000\u3000\u4e25\u5bd2\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u82b1\u8001\u5f1f\u6240\u8bf4\u7684\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u8fd9\u4eba\u5c31\u662f\u82b1\u67d0\u7684\u7ed3\u4e49\u5927\u54e5\u5173\u7433\u3002\u201d \u3000\u3000\u4e25\u5bd2\u4f3c\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u539f\u6765\u82b1\u8001\u5f1f\u5c31\u662f\u5173\u7433\u7684\u7ed3\u62dc\u4e09\u5f1f\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u89c9\u51fa\u4e25\u5bd2\u50cf\u662f\u65e9\u77e5\u5173\u7433\u4e0b\u843d\uff0c\u60ca\u559c\u4e2d\u6025\u6025\u95ee\u9053\uff1a\u201c\u83ab\u975e\u57ce\u4e3b\u77e5\u9053\u6211\u90a3\u5173\u5927\u54e5\u7684\u4e0b \u843d\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u98a4\u9996\u9053\uff1a\u201c\u5173\u5927\u4fa0\u4e5f\u5728\u672c\u57ce\uff0c\u4e0d\u8fc7\u4ed6\u662f\u88ab\u9501\u5728\u4e00\u5904\u5c71\u6d1e\u5185\u7684\u5730\u7262\u91cc\uff0c\u90a3\u5730\u70b9\u79d8\u5bc6\u7684 \u5f88\uff0c\u9664\u4e25\u67d0\u548c\u5468\u603b\u7ba1\u4ee5\u53ca\u4e00\u540d\u8d1f\u8d23\u9001\u996d\u7684\u5f1f\u5144\u5916\uff0c\u518d\u6ca1\u6709\u4efb\u4f55\u4eba\u77e5\u9053\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u8d8a\u53d1\u559c\u51fa\u671b\u5916\uff0c\u4ed6\u4eec\u592b\u59bb\u6b64\u6b21\u6df7\u5165\u5929\u8c37\u7687\u5143\u6559\uff0c\u8981\u6551\u51fa\u7684\u6b63\u662f\u8fd9\u4e24\u4eba\uff0c\u5982\u4eca\u4e24\u4eba \u90fd\u53ef\u6551\u51fa\uff0c\u7b49\u4e8e\u76ee\u7684\u5df2\u8fbe\uff0c\u5927\u529f\u544a\u6210\uff0c\u6b64\u523b\u6240\u5269\u4e0b\u7684\uff0c\u53ea\u662f\u4e0e\u7687\u5143\u6559\u51b3\u4e00\u6b7b\u6218\u7684\u6700\u540e\u4e00\u5173\u4e86\u3002 \u3000\u3000\u4ed6\u672c\u60f3\u80fd\u7acb\u523b\u5230\u90a3\u79d8\u5bc6\u5c71\u6d1e\u548c\u5173\u7433\u89c1\u4e0a\u4e00\u9762\uff0c\u4f46\u4e25\u5bd2\u5374\u529d\u4ed6\u6682\u65f6\u5fcd\u2014\u65f6\u601d\u5ff5\u4e4b\u82e6\uff0c\u7b49\u4e0b \u6b21\u6765\u65f6\u518d\u89c1\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u63a5\u7740\u518d\u95ee\u9053\uff1a\u201c\u57ce\u4e3b\u51c6\u5907\u4ec0\u4e48\u65f6\u5019\u8981\u82b1\u67d0\u524d\u6765\u62a5\u5230\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u82b1\u8001\u5f1f\u6682\u65f6\u6700\u597d\u8fd8\u662f\u6309\u5175\u4e0d\u52a8\uff0c\u4ee5\u4fbf\u7b49\u5f85\u9002\u5f53\u65f6\u673a\uff0c\u53e6\u5916\u7531\u4ee4\u90ce\u5c45\u4e2d\u8054\u7edc\uff0c \u4e25\u67d0\u4f1a\u968f\u65f6\u7ed9\u4f60\u6d88\u606f\u3002\u201d \u3000\u3000\u4e25\u5bd2\u65e9\u5df2\u5b89\u6392\u597d\u8ba9\u82b1\u7389\u9e9f\u548c\u4f55\u6167\u4ed9\u592b\u59bb\u89c1\u4e0a\u4e00\u9762\uff0c\u5f7c\u6b64\u8ba1\u8bae\u5df2\u5b9a\uff0c\u7acb\u523b\u6d3e\u4eba\u5230\u5185\u5ba4\u8bf7\u51fa \u4f55\u6167\u4ed9\u6765\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u548c\u4f55\u6167\u4ed9\u5206\u79bb\u4e09\u5e74\u591a\uff0c\u4ed6\u4eec\u672c\u6765\u4f09\u4fea\u60c5\u6df1\uff0c\u6b64\u523b\u5f97\u80fd\u91cd\u76f8\u805a\u9996\uff0c\u573a\u9762\u81ea\u662f\u4ee4\u4eba\u611f \u52a8\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u4e0d\u6562\u4e45\u7559\uff0c\u592b\u59bb\u5c0f\u805a\u4e4b\u540e\uff0c\u7acb\u523b\u4fbf\u8fde\u591c\u8d76\u56de\u4e94\u5173\u3002 \u3000\u3000\u5f53\u4ed6\u5230\u8fbe\u4e94\u5173\u65f6\uff0c\u5df2\u662f\u62c2\u6653\u65f6\u5206\u3002 \u3000\u3000\u4e00\u8fde\u4e09\u5929\u8fc7\u53bb\uff0c\u7687\u5143\u6559\u603b\u575b\u5e76\u672a\u518d\u6d3e\u4eba\u5230\u8fc7\u80b2\u5316\u57ce\u3002 \u3000\u3000\u8fd9\u4e09\u5929\u4e00\u76f4\u5728\u98ce\u5e73\u6d6a\u9759\u4e2d\u5ea6\u8fc7\u3002 \u3000\u3000\u4f46\u80b2\u5316\u57ce\u65b9\u9762\uff0c\u4e25\u5bd2\u548c\u5468\u6d77\u5c71\u5fc3\u91cc\u90fd\u6709\u6570\uff0c\u8fd9\u6b63\u662f\u66b4\u98ce\u96e8\u6765\u4e34\u524d\u7684\u5b81\u9759\u3002 \u3000\u3000\u76f4\u5230\u7b2c\u56db\u5929\uff0c\u7ec8\u4e8e\u6709\u4eba\u6765\u8bbf\u3002 \u3000\u3000\u51fa\u4eba\u610f\u6599\u7684\uff0c\u6765\u4eba\u5374\u662f\u5bcc\u56fd\u57ce\u4e3b\u9ec4\u4e00\u9053\uff0c\u800c\u4e14\u53ea\u662f\u5355\u8eab\u4e00\u4eba\u3002 \u3000\u3000\u4e25\u5bd2\u628a\u4ed6\u62db\u5f85\u5728\u524d\u9662\u5927\u5385\uff0c\u4ed6\u8eab\u8fb9\u4e5f\u53ea\u6709\u5468\u6d77\u5c71\u4e00\u4eba\u968f\u4f8d\u3002 \u3000\u3000\u5bcc\u56fd\u57ce\u4e3b\u9ec4\u4e00\u9053\u5750\u4e0b\u540e\uff0c\u4fbf\u5f00\u95e8\u89c1\u5c71\u7684\u9053\uff1a\u201c\u542c\u8bf4\u5927\u5e08\u5144\u548c\u603b\u575b\u95f9\u7ffb\u4e86\uff1f\u5144\u5f1f\u4e4d\u542c\u8fd9\u6d88 \u606f\uff0c\u5927\u4e3a\u9a87\u5f02\uff0c\u7a76\u7adf\u4e3a\u4ec0\u4e48\u4f1a\u53d1\u751f\u8fd9\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u96be\u9053\u767d\u837b\u4e0d\u66fe\u5bf9\u4e8c\u5e08\u5f1f\u8bb2\u8fc7\uff1f\u201d \u3000\u3000\u767d\u837b\u6b63\u662f\u7687\u5143\u6559\u4e3b\u7684\u540d\u5b57\u3002 \u3000\u3000\u9ec4\u4e00\u9053\u8f7b\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u636e\u6559\u4e3b\u548c\u79e6\u603b\u62a4\u6cd5\u8bf4\uff0c\u5927\u5e08\u5144\u7adf\u7136\u4e3a\u4e86\u4e00\u70b9\u5c0f\u4e8b\uff0c\u5bf9\u603b\u575b\u516c\u7136 \u6297\u547d\uff0c\u800c\u4e14\u5f53\u7740\u79e6\u69d0\u7684\u9762\uff0c\u6253\u4e86\u603b\u575b\u4e24\u540d\u62a4\u6cd5\uff0c\u53ef\u6709\u8fd9\u56de\u4e8b\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u3000\u3000\u9ec4\u4e00\u9053\u8e59\u7709\u9053\uff1a\u201c\u5927\u5e08\u5144\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u518d\u5ea6\u51b7\u7b11\u9053\uff1a\u201c\u767d\u837b\u548c\u79e6\u69d0\u6b3a\u4eba\u592a\u751a\uff0c\u4e25\u67d0\u8eab\u4e3a\u80b2\u5316\u57ce\u4e3b\uff0c\u5c45\u7136\u81ea\u5df1\u8fde\u4e00\u70b9\u5c0f\u4e8b\u90fd \u505a\u4e0d\u4e86\u4e3b\uff0c\u5904\u5904\u53d7\u4ed6\u4eec\u7684\u5e72\u6d89\u63a3\u8098\uff0c\u4e25\u67d0\u4e00\u518d\u59d4\u5c48\u6c42\u5168\u800c\u4e0d\u53ef\u5f97\uff0c\u6700\u540e\u5c31\u53ea\u6709\u8d70\u4e0a\u548c\u4ed6\u4eec\u5bf9 \u6297\u7684\u8def\u4e86\u3002\u201d \u3000\u3000\u9ec4\u4e00\u9053\u6447\u6447\u5934\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u4f60\u9519\u4e86\uff0c\u4e0d\u4f46\u9519\u4e86\uff0c\u800c\u4e14\u5927\u9519\u7279\u9519\uff01\u201d \u3000\u3000\u201c\u4e25\u67d0\u5012\u60f3\u542c\u542c\u4e8c\u5e08\u5f1f\u7684\u9ad8\u89c1\uff01\u201d \u3000\u3000\u201c\u767d\u5e08\u59b9\u8eab\u4e3a\u7687\u5143\u6559\u4e3b\uff0c\u5728\u5929\u8c37\u5185\u53ef\u8bf4\u4e07\u4f17\u5f52\u5fc3\uff0c\u4f60\u8d77\u800c\u548c\u5979\u5bf9\u6297\uff0c\u7b49\u4e8e\u4ee5\u5375\u51fb\u77f3\uff0c\u4f55 \u51b5\u4e0d\u65e5\u4e4b\u540e\uff0c\u672c\u6559\u4fbf\u53ef\u8fdb\u519b\u4e2d\u539f\uff0c\u7edf\u4e00\u5929\u4e0b\u6b66\u6797\uff0c\u5230\u90a3\u65f6\u5927\u5e08\u5144\u4ecd\u662f\u7687\u5143\u6559\u7684\u7b2c\u4e8c\u53f7\u4eba\u7269\uff0c \u53ef\u8bf4\u4eab\u4e0d\u5c3d\u7684\u8363\u534e\u5bcc\u8d35\uff0c\u4f60\u73b0\u5728\u8fd9\u79cd\u4f5c\u6cd5\uff0c\u5c82\u4e0d\u662f\u5c0f\u4e0d\u5fcd\u5219\u4e71\u5927\u8c0b\uff1f\u201d \u3000\u3000\u4e25\u5bdf\u65e9\u5df2\u542c\u5f97\u6709\u4e9b\u4e0d\u8010\u70e6\uff0c\u51b7\u51b7\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u4e8c\u5e08\u5f1f\uff0c\u83ab\u975e\u4f60\u662f\u66ff\u767d\u83b7\u505a\u8bf4\u5ba2\u6765\u7684\uff1f\u201d \u3000\u3000\u9ec4\u4e00\u9053\u9053\uff1a\u201c\u5927\u5e08\u5144\u8a00\u91cd\u4e86\uff0c\u5e08\u5f1f\u6765\u6b64\u4e4b\u524d\uff0c\u7684\u786e\u662f\u89c1\u8fc7\u767d\u5e08\u59b9\uff0c\u767d\u5e08\u59b9\u5ff5\u5728\u540c\u95e8\u4e4b\u8c0a\uff0c \u66fe\u7acb\u4e0b\u4fdd\u8bc1\uff0c\u53ea\u8981\u5927\u5e08\u5144\u80af\u628a\u5979\u8981\u7684\u4eba\u4ea4\u51fa\uff0c\u518d\u4eb2\u81ea\u5230\u603b\u575b\u4e00\u8d9f\uff0c\u8fc7\u53bb\u7684\u4e8b\uff0c\u5979\u60c5\u613f\u4e00\u6982\u4e0d \u7a76\u3002\u201d \u3000\u3000\u4e25\u5bd2\u51b7\u7b11\u9053\uff1a\u201c\u4e8c\u5e08\u5f1f\u6700\u597d\u9192\u6765\u8bb2\u8bdd\uff0c\u5979\u82e5\u5ff5\u5728\u540c\u95e8\u4e4b\u8c0a\uff0c\u5c31\u4e0d\u8be5\u542c\u4fe1\u8c17\u8a00\uff0c\u5bf9\u6211\u6709\u6240 \u7591\u5fc3\uff0c\u56de\u60f3\u5f53\u5e74\uff0c\u4e25\u67d0\u5bf9\u5979\u662f\u4f55\u7b49\u7231\u62a4\uff0c\u8fde\u5979\u7684\u5f88\u591a\u6b66\u529f\uff0c\u90fd\u662f\u6211\u4ee3\u5e08\u4f20\u6388\uff0c\u5c31\u4ee5\u5341\u5e74\u524d\u6210 \u7acb\u7687\u5143\u6559\u800c\u8a00\uff0c\u540d\u6b63\u8a00\u987a\u7684\u6211\u5e94\u8be5\u662f\u6559\u4e3b\uff0c\u4f46\u6211\u6700\u540e\u8fd8\u662f\u8ba9\u7ed9\u4e86\u5979\uff0c\u81ea\u5df1\u60c5\u613f\u4ee5\u4e0b\u5c5e\u7684\u8eab\u4efd \u8f85\u4f50\u5979\uff0c\u5979\u82e5\u8fd8\u6709\u70b9\u5929\u7406\u826f\u5fc3\uff0c\u600e\u53ef\u5fd8\u6069\u8d1f\u4e49\u5230\u8fd9\u79cd\u7a0b\u5ea6\uff1f\u201d \u3000\u3000\u9ec4\u4e00\u9053\u88ab\u8bf4\u5f97\u6709\u4e9b\u9762\u7ea2\u8033\u8d64\uff0c\u5e26\u7740\u82e6\u7b11\u7684\u610f\u5473\u9053\uff1a\u201c\u5144\u5f1f\u65e9\u77e5\u5fe0\u8a00\u9006\u8033\uff0c\u65e0\u6cd5\u8bf4\u52a8\u5927\u5e08 \u5144\uff0c\u4f46\u5927\u5e08\u5144\u5e94\u8be5\u60f3\u60f3\uff0c\u4ee5\u4f60\u76ee\u524d\u7684\u529b\u91cf\uff0c\u600e\u53ef\u4e0e\u767d\u5e08\u59b9\u9886\u5bfc\u7684\u7687\u5143\u6559\u6297\u8861\uff0c\u4e0d\u662f\u773c\u89c1\u8981\u5403 \u5927\u4e8f\u4e48\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u795e\u6001\u51db\u7136\u9053\uff1a\u201c\u4e25\u67d0\u4e3a\u4e89\u56de\u4e00\u53e3\u6c14\uff0c\u5df2\u987e\u4e0d\u4e86\u8bb8\u591a\uff0c\u5373\u4fbf\u80b2\u5316\u57ce\u6570\u767e\u4eba\u7684\u6027\u547d\u7389\u77f3 \u4ff1\u711a\uff0c\u4e5f\u5728\u6240\u4e0d\u60dc\uff01\u201d \u3000\u3000\u201c\u53ef\u662f\u5927\u5e08\u5144\u4e0d\u80fd\u4e0d\u4e3a\u5c06\u6765\u8fdb\u519b\u4e2d\u539f\u5927\u5c40\u7740\u60f3\u3002\u201d \u3000\u3000\u201c\u6240\u8c13\u8fdb\u519b\u4e2d\u539f\uff0c\u4e25\u67d0\u65e9\u5c31\u53cd\u5bf9\u6b64\u8bae\uff0c\u767d\u837b\u8fd9\u79cd\u91ce\u5fc3\uff0c\u672c\u5c31\u662f\u5927\u9006\u4e0d\u9053\uff0c\u96be\u9053\u4e8c\u5e08\u5f1f\u975e \u8981\u548c\u5979\u72fc\u72c8\u4e3a\u5978\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u9ec4\u4e00\u9053\u7684\u8138\u8272\u8d8a\u53d1\u96be\u770b\uff0c\u5f3a\u81ea\u9547\u5b9a\u9053\uff1a\u201c\u767d\u5e08\u59b9\u8fdb\u519b\u4e2d\u539f\u7684\u9e3f\u56fe\u5927\u8ba1\uff0c\u5c82\u662f\u5144\u5f1f\u53ef\u4ee5\u6539 \u53d8\u5f97\u4e86\u7684\uff0c\u4f55\u51b5\u5144\u5f1f\u4e5f\u575a\u4fe1\u7687\u5143\u6559\u6709\u8fd9\u4efd\u5b9e\u529b\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u4e25\u67d0\u8fd9\u6b21\u5c31\u51b3\u5b9a\u6253\u7834\u5979\u7684\u68a6\u60f3\uff01\u201d \u3000\u3000\u9ec4\u4e00\u9053\u8138\u8272\u4e00\u53d8\u9053\uff1a\u201c\u83ab\u975e\u5927\u5e08\u5144\u51c6\u5907\uff1f\u2026\u2026\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u767d\u837b\u8fdb\u519b\u4e2d\u539f\u7684\u672c\u94b1\uff0c\u4e0d\u5916\u5728\u672c\u57ce\u7684\u7075\u5802\u91cc\uff0c\u5979\u628a\u4e2d\u539f\u5404\u95e8\u6d3e\u7684\u9876\u5c16 \u4eba\u7269\uff0c\u8bf1\u8fdb\u5929\u8c37\uff0c\u7136\u540e\u670d\u836f\u5165\u68fa\uff0c\u4e25\u67d0\u73b0\u5728\u5df2\u51b3\u5b9a\u90fd\u628a\u4ed6\u4eec\u6551\u6d3b\uff0c\u4ed6\u4eec\u613f\u610f\u7559\u4e0b\u5bf9\u6297\u7687\u5143\u6559 \u7684\u5c31\u7559\u4e0b\u548c\u4e25\u67d0\u5e76\u80a9\u4f5c\u6218\uff0c\u4e0d\u613f\u7684\u5c31\u653e\u4ed6\u4eec\u56de\u5230\u4e2d\u539f\uff0c\u5230\u90a3\u65f6\u770b\u767d\u837b\u5982\u4f55\u80fd\u8fdb\u519b\u4e2d\u539f\uff1f\u201d \u3000\u3000\u9ec4\u4e00\u9053\u4e0d\u89c9\u8138\u8272\u5927\u53d8\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u8fd9\u6837\u505a\u5343\u4e07\u4f7f\u4e0d\u5f97\uff0c\u8fd9\u662f\u54b1\u4eec\u7687\u5143\u6559\u8d39\u4e86\u5341\u51e0\u5e74\u7684 \u5fc3\u8840\uff0c\u624d\u94fa\u597d\u4e86\u8fdb\u519b\u4e2d\u539f\u4e4b\u8def\uff0c\u4f60\u82e5\u8fd9\u6837\u505a\uff0c\u5c82\u4e0d\u628a\u7687\u5143\u6559\u5341\u51e0\u5e74\u7684\u5fc3\u8840\u6240\u5f97\u5230\u7684\u6536\u83b7\u6bc1\u4e8e \u4e00\u65e6\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u51b7\u7b11\u9053\uff1a\u201c\u4e8c\u5e08\u5f1f\u8fd8\u8bf4\u7684\u4ec0\u4e48\u54b1\u4eec\u7687\u5143\u6559\uff0c\u73b0\u5728\u4e25\u67d0\u65e9\u5df2\u4e0d\u662f\u7687\u5143\u6559\u7684\u4eba\u4e86\uff0c\u4e25\u67d0 \u4e0d\u59a8\u544a\u8bc9\u4f60\uff0c\u767d\u837b\u867d\u7136\u806a\u660e\u4e00\u4e16\uff0c\u672a\u514d\u4ecd\u6709\u8003\u8651\u4e0d\u5230\u4e4b\u5904\uff0c\u5979\u628a\u5341\u51e0\u5e74\u5fc3\u8840\u7684\u6536\u83b7\u653e\u5728\u4e25\u67d0 \u7ba1\u8f96\u7684\u80b2\u5316\u57ce\uff0c\u5374\u53c8\u542c\u4fe1\u8c17\u8a00\u7591\u5fc3\u4e8e\u6211\uff0c\u6b63\u662f\u5979\u7684\u5931\u7b56\u4e4b\u5904\uff01\u201d \u3000\u3000\u201c\u5927\u5e08\u5144\u8fd9\u8bdd\u9519\u4e86\uff0c\u8fd9\u6b63\u8868\u793a\u767d\u5e08\u59b9\u5bf9\u4f60\u7684\u6df1\u4fe1\u4e0d\u7591\uff0c\u4f60\u73b0\u5728\u8fd9\u6837\u5bf9\u4ed6\uff0c\u53cd\u800c\u662f\u4f60\u7684\u591a \u5fc3\uff01\u201d \u3000\u3000\u201c\u4e8c\u5e08\u5f1f\uff0c\u7528\u4e0d\u7740\u5728\u6211\u9762\u524d\u518d\u8bb2\u8fd9\u4e9b\uff0c\u4f60\u82e5\u5ff5\u5728\u540c\u95e8\u4e4b\u8c0a\uff0c\u6211\u5012\u5e0c\u671b\u4f60\u80fd\u7b54\u5e94\u6211\u4e00\u4ef6 \u4e8b\u3002\u201d \u3000\u3000\u201c\u5927\u5e08\u5144\u53ef\u662f\u8981\u5144\u5f1f\u4e5f\u8131\u79bb\u7687\u5143\u6559\uff0c\u52a0\u5165\u5230\u4f60\u8fd9\u8fb9\u6765\uff1f\u201d \u3000\u3000\u201c\u4eba\u5404\u6709\u5fd7\uff0c\u8fd9\u65b9\u9762\u6211\u7edd\u4e0d\u60f3\u52c9\u5f3a\u4f60\uff0c\u6211\u53ea\u76fc\u4f60\u80fd\u6697\u4e2d\u63a5\u6d4e\u6211\u4e00\u4e9b\u7cae\u8349\uff0c\u90a3\u5c31\u7b97\u4f60\u5e2e \u6211\u3002\u201d \u3000\u3000\u9ec4\u4e00\u9053\u4f3c\u662f\u7a0d\u611f\u72b9\u8c6b\uff0c\u534a\u664c\u4e4b\u540e\u624d\u9053\uff1a\u201c\u53ef\u5426\u5bb9\u5144\u5f1f\u56de\u53bb\u8ba1\u5212\u4e00\u4e0b\u518d\u91c7\u53d6\u884c\u52a8\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u5176\u5b9e\u6211\u8fd9\u91cc\u7684\u7cae\u8349\u4e5f\u8db3\u591f\u4e00\u6708\u4e4b\u7528\uff0c\u4e8c\u5e08\u5f1f\u7528\u4e0d\u7740\u6025\u4e8e\u884c\u52a8\uff0c\u4f46\u8fd9\u4e8b\u5fc5\u987b\u4fdd \u6301\u7edd\u5bf9\u79d8\u5bc6\uff0c\u627e\u4e0d\u60f3\u8ba9\u4f60\u4e5f\u8ddf\u7740\u53d7\u7d2f\u3002\u201d \u3000\u3000\u9ec4\u4e00\u9053\u51dd\u7740\u8138\u8272\u8d77\u8eab\u9053\uff1a\u201c\u5144\u5f1f\u544a\u8f9e\u4e86\uff01\u201d \u3000\u3000\u9ec4\u4e00\u9053\u79bb\u5f00\u80b2\u5316\u57ce\uff0c\u8fde\u81ea\u5df1\u7684\u5bcc\u56de\u57ce\u90fd\u987e\u4e0d\u5f97\u56de\u53bb\uff0c\u5373\u523b\u8d76\u5230\u7687\u5143\u6559\u603b\u575b\u5411\u6559\u4e3b\u767d\u837b\u590d \u547d\u3002 \u3000\u3000\u7687\u5143\u6559\u4e3b\u767d\u837b\u548c\u603b\u62a4\u6cd5\u79e6\u69d0\u6b63\u5728\u540e\u5bab\u6025\u5f85\u56de\u97f3\u3002 \u3000\u3000\u5f53\u9ec4\u4e00\u9053\u53d9\u8ff0\u8fc7\u548c\u4e25\u5bd2\u7684\u8c08\u6d3b\u5185\u5bb9\u540e\uff0c\u90fd\u5927\u60ca\u5931\u8272\u3002 \u3000\u3000\u4ed6\u4eec\u60ca\u7684\u4e0d\u5916\u662f\u80b2\u5316\u57ce\u7075\u5802\u4e2d\u90a3\u51e0\u5341\u540d\u670d\u836f\u5165\u68fa\u7684\u7edd\u9876\u9ad8\u624b\u7684\u5931\u53bb\u63a7\u5236\uff0c\u90a3\u786e\u662f\u7687\u5143\u6559 \u5341\u51e0\u5e74\u5fc3\u8840\u6240\u5f97\u5230\u7684\u6536\u83b7\uff0c\u82e5\u4e00\u65e6\u88ab\u4e25\u5bd2\u653e\u8d70\u6216\u505a\u5176\u4ed6\u5229\u7528\uff0c\u7687\u5143\u6559\u8fdb\u519b\u4e2d\u539f\u7684\u5e0c\u671b\u5fc5\u5b9a\u843d \u7a7a\u3002 \u3000\u3000\u6b63\u6240\u8c13\u667a\u8005\u5343\u8651\uff0c\u5fc5\u6709\u4e00\u5931\uff0c\u8fd9\u4e8b\u5b9e\u662f\u767d\u83b7\u548c\u79e6\u69d0\u4e8b\u5148\u6240\u672a\u6599\u5230\u7684\u3002 \u3000\u3000\u767d\u837b\u9762\u8272\u9634\u6c89\u7684\u9053\uff1a\u201c\u4e8c\u5e08\u5144\uff0c\u4f60\u770b\u4e25\u5bd2\u7684\u610f\u601d\uff0c\u662f\u5426\u5df2\u7ecf\u7edd\u5bf9\u65e0\u6cd5\u8ba9\u4ed6\u56de\u5fc3\u8f6c\u610f\uff1f\u201d \u3000\u3000\u9ec4\u4e00\u9053\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u4e25\u5bd2\u4e0d\u662f\u597d\u5bf9\u4ed8\u7684\uff0c\u4ed6\u5f53\u7136\u660e\u767d\uff0c\u82e5\u4ed6\u4e00\u65e6\u5230\u603b\u575b\u6765\uff0c\u5fc5\u5b9a\u6ca1\u6709\u6d3b \u547d\u7684\u5e0c\u671b\uff0c\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u53c8\u6709\u8c01\u80fd\u8bf4\u670d\u5f97\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u767d\u837b\u72b9\u8c6b\u4e86\u4e00\u4e0b\u9053\uff1a\u201c\u5c31\u7531\u6211\u4eb2\u81ea\u5230\u80b2\u5316\u57ce\u53bb\u4e00\u8d9f\u5982\u4f55\uff1f\u201d \u3000\u3000\u79e6\u69d0\u563f\u563f\u7b11\u9053\uff1a\u201c\u6559\u4e3b\u8fd9\u8bdd\uff0c\u5b9e\u5728\u6709\u6b20\u8003\u8651\uff0c\u60a8\u82e5\u4e00\u53bb\uff0c\u6b63\u597d\u662f\u7f8a\u5165\u864e\u53e3\uff0c\u5230\u90a3\u65f6\u54b1\u4eec \u7687\u5143\u6559\u53cd\u800c\u771f\u7684\u8981\u5411\u4ed6\u4f4e\u5934\u4e86\u3002\u201d \u3000\u3000\u5ffd\u542c\u9ec4\u4e00\u9053\u9053\uff1a\u201c\u9ec4\u67d0\u5012\u6709\u4e2a\u529e\u6cd5\u3002\u201d \u3000\u3000\u767d\u837b\u5fd9\u9053\uff1a\u201c\u4e8c\u5e08\u5144\u6253\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u3000\u3000\u9ec4\u4e00\u9053\u9053\uff1a\u201c\u636e\u4e25\u5bd2\u521a\u624d\u8bf4\uff0c\u80b2\u5316\u57ce\u5185\u53ea\u6709\u4e00\u6708\u7cae\u8349\uff0c\u4e0d\u59a8\u6d3e\u51fa\u91cd\u5175\uff0c\u5728\u57ce\u5916\u53ea\u5b88\u4e0d\u6218\uff0c \u7b49\u4ed6\u7cae\u8349\u7528\u5c3d\uff0c\u57ce\u5185\u5fc5\u7136\u5927\u4e71\uff0c\u90a3\u65f6\u518d\u6740\u8fdb\u53bb\uff0c\u80b2\u6bd4\u57ce\u5c82\u4e0d\u553e\u624b\u53ef\u5f97\u3002\u201d \u3000\u3000\u767d\u837b\u9053\uff1a\u201c\u8fd9\u529e\u6cd5\u56fa\u7136\u4e0d\u63aa\uff0c\u4f46\u56f0\u5730\u4e00\u6708\uff0c\u65f6\u95f4\u672a\u514d\u592a\u4e45\uff0c\u8bf4\u4e0d\u5b9a\u5176\u4e2d\u53c8\u8d77\u53d8\u5316\uff0c\u4f9d\u5c0f \u59b9\u770b\uff0c\u5fc5\u987b\u901f\u6218\u901f\u51b3\u624d\u597d\u3002\u201d \u3000\u3000\u201c\u5982\u679c\u6559\u4e3b\u5acc\u8fd9\u529e\u6cd5\u65f6\u95f4\u62d6\u5f97\u592a\u591a\uff0c\u9ec4\u67d0\u8fd8\u53e6\u6709\u4e00\u8ba1\u3002\u201d \u3000\u3000\u201c\u4e8c\u5e08\u5144\u6709\u4ec0\u4e48\u5999\u8ba1\uff0c\u8fd8\u8bf7\u8bf4\u51fa\u6765\uff01\u201d \u3000\u3000\u201c\u4e25\u5bd2\u65b9\u624d\u66fe\u6c42\u6211\u6697\u4e2d\u52a9\u4ed6\u7cae\u8349\uff0c\u6240\u4ee5\u54b1\u4eec\u6b63\u597d\u53ef\u4ee5\u5c06\u8ba1\u5c31\u8ba1\u3002\u201d \u3000\u3000\u201c\u4e8c\u5e08\u5144\u8bf7\u8bf4\u660e\u767d\uff01\u201d \u3000\u3000\u201c\u9ec4\u67d0\u7684\u529e\u6cd5\uff0c\u4e0d\u59a8\u5c31\u6697\u4e2d\u63a5\u6d4e\u4ed6\u7cae\u8349\uff0c\u7136\u540e\u5728\u62bc\u89e3\u7cae\u8349\u7684\u4eba\u4e2d\uff0c\u90fd\u6362\u4e0a\u7edd\u9876\u9ad8\u624b\uff0c\u628a \u5175\u5203\u85cf\u5728\u7cae\u8349\u4e4b\u4e2d\uff0c\u6df7\u8fdb\u80b2\u5316\u57ce\u540e\uff0c\u4e00\u58f0\u53f7\u4ee4\uff0c\u9f50\u9f50\u52a8\u624b\u5c55\u5f00\u6495\u6740\uff0c\u4e25\u5bd2\u5fc5\u5b9a\u63aa\u624b\u4e0d\u53ca\uff0c\u95ee \u9898\u5c82\u4e0d\u9a6c\u4e0a\u5c31\u89e3\u51b3\u4e86\u3002\u201d \u3000\u3000\u767d\u837b\u559c\u51fa\u671b\u5916\u9053\uff1a\u201c\u4e8c\u5e08\u5144\u8fd9\u4e00\u8ba1\u679c\u7136\u9ad8\u660e\uff0c\u5c0f\u59b9\u4e5f\u5c31\u7528\u4e0d\u7740\u53d1\u6101\u4e86\u3002\u201d \u3000\u3000\u9ec4\u4e00\u9053\u7565\u4e00\u6c89\u541f\u9053\uff1a\u201c\u529e\u6cd5\u867d\u597d\uff0c\u4f46\u9ec4\u67d0\u90a3\u8fb9\u7684\u9ad8\u624b\uff0c\u53ea\u6015\u529b\u91cf\u8fd8\u4e0d\u591f\uff0c\u5fc5\u987b\u7531\u603b\u575b\u65b9 \u9762\u62bd\u8c03\u652f\u63e1\uff0c\u5426\u5219\u5f88\u96be\u901f\u6218\u901f\u51b3\u3002\u201d \u3000\u3000\u767d\u837b\u9053\uff1a\u201c\u5c0f\u59b9\u5f53\u7136\u8981\u4e3a\u4e8c\u5e08\u5144\u53e6\u5916\u8c03\u96c6\u9ad8\u624b\uff0c\u5c31\u5728\u660e\u5929\u665a\u4e0a\u91c7\u53d6\u884c\u52a8\u5982\u4f55\uff1f\u201d \u3000\u3000\u9ec4\u4e00\u9053\u9053\uff1a\u201c\u4e8b\u60c5\u5f53\u7136\u662f\u8d8a\u5feb\u8d8a\u597d\uff0c\u4f46\u884c\u52a8\u592a\u65e9\u4e86\u5374\u5f88\u5bb9\u6613\u5f15\u8d77\u4e25\u5bd2\u7684\u7591\u5fc3\u3002\u201d \u3000\u3000\u201c\u4e8c\u5e08\u5144\u8fd9\u8bdd\u600e\u4e48\u8bb2\uff1f\u201d \u3000\u3000\u201c\u65b9\u624d\u4e25\u5bd2\u8bb2\u8fc7\uff0c\u80b2\u5316\u57ce\u7684\u7cae\u8349\uff0c\u8db3\u591f\u4e00\u6708\u4e4b\u7528\uff0c\u8981\u63a5\u6d4e\u4ed6\u4e5f\u4e0d\u5fc5\u6025\u5728\u4e00\u65f6\uff0c\u6211\u82e5\u884c\u52a8 \u592a\u65e9\uff0c\u96be\u514d\u4f1a\u5f15\u8d77\u4ed6\u751f\u7591\u3002\u201d \u3000\u3000\u79e6\u69d0\u63a5\u53e3\u9053\uff1a\u201c\u9ec4\u57ce\u4e3b\u7684\u770b\u6cd5\u4e5f\u4e0d\u65e0\u9053\u7406\uff0c\u4f9d\u672c\u5ea7\u4e4b\u89c1\uff0c\u6559\u4e3b\u660e\u5929\u5c31\u968f\u5e26\u9ad8\u624b\uff0c\u53bb\u4e00\u8d9f \u80b2\u6bd4\u57ce\u3002\u201d \u3000\u3000\u767d\u837b\u4e0d\u7531\u4e00\u6123\u9053\uff1a\u201c\u4f60\u65b9\u624d\u4e0d\u662f\u8bf4\u6211\u82e5\u5230\u80b2\u5316\u57ce\uff0c\u7b49\u4e8e\u7f8a\u5165\u864e\u95e8\u4e48\uff1f\u201d \u3000\u3000\u79e6\u69d0\u7b11\u9053\uff1a\u201c\u672c\u5ea7\u5f53\u7136\u4e0d\u662f\u8981\u6559\u4e3b\u5f53\u771f\u8fdb\u5165\u80b2\u5316\u57ce\u53bb\uff0c\u53ea\u662f\u8bd5\u63a2\u4e00\u4e0b\u800c\u5df2\uff0c\u4e0d\u7ba1\u5982\u4f55\uff0c \u6559\u4e3b\u603b\u8be5\u627e\u673a\u4f1a\u518d\u548c\u4e25\u5bd2\u89c1\u6b21\u9762\uff0c\u540c\u65f6\u4e5f\u53ef\u770b\u770b\u90a3\u4e2a\u53eb\u5173\u5927\u9e4f\u7684\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u8eab\u4efd\u6765\u5386\u3002\u201d \u3000\u3000\u767d\u837b\u9894\u9996\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u6709\u6b64\u610f\uff0c\u4e25\u5bd2\u8eab\u8fb9\u7684\u4e00\u540d\u62a4\u536b\uff0c\u5c45\u7136\u8eab\u5b50\u9ad8\u5230\u8fd9\u79cd\u7a0b\u5ea6\uff0c\u6211\u7684\u786e \u5f88\u60f3\u89c1\u89c1\u6b64\u4eba\u3002\u201d \u3000\u3000\u4e8c\u4eba\u8ba1\u8bae\u5b8c\u540e\uff0c\u968f\u5373\u5404\u6563\u3002 \u3000\u3000\u6b21\u65e5\u4e0a\u5348\uff0c\u4e25\u5bd2\u548c\u4e25\u592b\u4eba\u3001\u4e25\u5982\u51b0\u4ee5\u53ca\u4f55\u6167\u4ed9\u3001\u5cb3\u5c0f\u98de\u7b49\u4eba\u6b63\u5728\u5185\u5ead\u5546\u8bae\u4e8b\u60c5\uff0c\u5ffd\u89c1\u5468 \u6d77\u5c71\u5306\u5306\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u7980\u57ce\u4e3b\uff0c\u636e\u6d3e\u51fa\u57ce\u5916\u5de1\u54e8\u7684\u5f1f\u5144\u56de\u62a5\uff0c\u6559\u4e3b\u5e26\u7740\u4e0d\u5c11\u4eba\u6b63\u5411\u54b1\u4eec\u80b2\u5316 \u57ce\u800c\u6765\uff01\u201d \u3000\u3000\u8fd9\u6d88\u606f\u4e0d\u8bba\u5bf9\u5728\u573a\u7684\u4efb\u4f55\u4eba\u6765\u8bf4\uff0c\u90fd\u662f\u4e00\u9879\u610f\u5916\u3002 \u3000\u3000\u4e25\u5bd2\u7acb\u5373\u5429\u5490\u9053\uff1a\u201c\u9a6c\u4e0a\u968f\u6211\u5230\u57ce\u5916\u53bb\uff01\u201d \u3000\u3000\u4e25\u592b\u4eba\u9053\uff1a\u201c\u8001\u7237\u5b50\uff0c\u4f60\u51c6\u5907\u5e26\u90a3\u4e9b\u4eba\u53bb\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u5c0f\u98de\u548c\u5468\u603b\u7ba1\u968f\u6211\u53bb\u5c31\u591f\u4e86\uff0c\u5c0f\u98de\u5feb\u5feb\u6234\u4e0a\u4eba\u76ae\u9762\u5177\u3002\u201d \u3000\u3000\u8fd9\u662f\u4e00\u4ef6\u4eba\u4e8b\uff0c\u4f55\u6167\u4ed9\u56e0\u4eba\u624b\u4e0d\u591f\uff0c\u4e5f\u8ddf\u7740\u524d\u53bb\uff0c\u5979\u660e\u767d\uff0c\u4e00\u5207\u4e8b\u60c5\u5168\u662f\u7531\u5979\u800c\u8d77\uff0c\u5979 \u5f53\u7136\u4e0d\u80fd\u8896\u624b\u65c1\u89c2\u3002 \u3000\u3000\u4f46\u4e25\u5bd2\u5374\u8ba4\u4e3a\u5979\u6682\u65f6\u4e0d\u5b9c\u51fa\u9762\u3002 \u3000\u3000\u4f55\u6167\u4ed9\u4e5f\u53ea\u597d\u4f5c\u7f62\u3002 \u3000\u3000\u6700\u540e\uff0c\u4e25\u5bd2\u53ea\u591a\u5e26\u4e86\u5927\u59d1\u5a18\u4e25\u5982\u51b0\u4e00\u4eba\u3002 \u3000\u3000\u4e0d\u7528\u8bf4\uff0c\u4ed6\u4eec\u4e00\u884c\u56db\u4eba\uff0c\u90fd\u662f\u643a\u5e26\u5175\u5203\u524d\u5f80\u3002 \u3000\u3000\u53e6\u5916\uff0c\u7531\u4e25\u592b\u4eba\u4eb2\u81ea\u5230\u5168\u57ce\u5404\u5904\u6653\u8c15\u52a0\u5f3a\u6212\u5907\uff0c\u968f\u65f6\u51c6\u5907\u8fce\u6218\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u80b2\u5316\u57ce\u6240\u6709\u7684\u4eba\uff0c\u90fd\u5df2\u77e5\u9053\u57ce\u4e3b\u5df2\u8131\u79bb\u7687\u5143\u6559\u7684\u7ecf\u8fc7\uff0c\u4ed6\u4eec\u90fd\u8a93\u8a00\u4e3a\u57ce\u4e3b\u6548\u5fe0\uff0c \u8a93\u6b7b\u5bf9\u6297\u7687\u5143\u6559\u3002 \u3000\u3000\u4e25\u5bd2\u3001\u5cb3\u5c0f\u98de\u3001\u4e25\u5982\u51b0\u3001\u5468\u6d77\u5c71\u56db\u4eba\u6765\u5230\u57ce\u95e8\u5916\u65f6\uff0c\u7687\u5143\u6559\u4e3b\u767d\u837b\u6240\u5e26\u7684\u4eba\u9a6c\u4e5f\u6b63\u597d\u5230 \u8fbe\u3002 \u3000\u3000\u968f\u540c\u767d\u837b\u800c\u6765\u7684\uff0c\u4e0d\u4e0b\u5341\u4f59\u4eba\uff0c\u5305\u62ec\u603b\u62a4\u6cd5\u79e6\u69d0\u3001\u5927\u516c\u4e3b\u767d\u91d1\u51e4\u3001\u603b\u575b\u516b\u5927\u62a4\u6cd5\u4e2d\u7684\u56db \u540d\u62a4\u6cd5\uff0c\u4ee5\u53ca\u53e6\u5916\u516d\u4e03\u540d\u9ad8\u624b\uff0c\u8bba\u58f0\u52bf\u5f53\u7136\u6bd4\u4e25\u5bd2\u8fd9\u8fb9\u8981\u58ee\u5f97\u591a\u3002 \u3000\u3000\u4e25\u5bd2\u6602\u7136\u7ad9\u5728\u57ce\u95e8\u5916\u4e2d\u592e\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7d27\u7d27\u968f\u5728\u4e25\u5bd2\u8eab\u65c1\u3002 \u3000\u3000\u5468\u6d77\u5c71\u548c\u4e25\u5982\u51b0\u5219\u5206\u7acb\u5de6\u53f3\u3002 \u3000\u3000\u4ed6\u4eec\u56db\u4eba\u7684\u9762\u8272\u90fd\u6beb\u65e0\u8868\u60c5\u3002 \u3000\u3000\u5c24\u5176\u4e25\u5bd2\uff0c\u6b64\u65f6\u6b64\u5730\uff0c\u4ed6\u5df2\u7528\u4e0d\u7740\u5bf9\u7687\u5143\u6559\u4e3b\u865a\u4e0e\u59d4\u86c7\u3002 \u3000\u3000\u7687\u5143\u6559\u4e3b\u767d\u837b\u8fd9\u8fb9\uff0c\u7531\u5979\u5c45\u4e2d\uff0c\u767d\u91d1\u51e4\u548c\u79e6\u69d0\u5206\u7ad9\u5de6\u53f3\uff0c\u5176\u4f59\u5219\u4e00\u5b57\u6392\u5f00\u3002 \u3000\u3000\u5012\u662f\u767d\u83b7\u8868\u73b0\u5f97\u9887\u4e3a\u53cb\u5584\uff0c\u6ee1\u9762\u542b\u7b11\u7684\u53eb\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u524d\u4e9b\u5929\u603b\u575b\u65b9\u9762\u5f97\u7f6a\u4e86\u4f60\uff0c\u5c0f \u59b9\u4eca\u5929\u662f\u7279\u5730\u5411\u4f60\u8d54\u793c\u6765\u7684\u3002\u201d \u3000\u3000\u4e25\u5bd2\u6380\u9aef\u51b7\u7b11\u9053\uff1a\u201c\u767d\u837b\uff0c\u7528\u4e0d\u7740\u865a\u60c5\u5047\u610f\uff0c\u96be\u9053\u4e25\u67d0\u8fd8\u4f1a\u4e0a\u4f60\u7684\u5f53\uff01\u201d \u3000\u3000\u53ea\u542c\u79e6\u69d0\u559d\u9053\uff1a\u201c\u5927\u80c6\uff0c\u6559\u4e3b\u7684\u540d\u8bb3\uff0c\u5c82\u662f\u4f60\u968f\u4fbf\u53eb\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u89c1\u79e6\u69d0\u4ee5\u5982\u6b64\u8bed\u6c14\u5bf9\u57ce\u4e3b\u8bb2\u8bdd\uff0c\u54ea\u9a7b\u6309\u637a\u5f97\u4f4f\uff0c\u4e5f\u6012\u76ee\u559d\u9053\uff1a\u201c\u79e6\u69d0\uff0c\u4f60\u7b97\u4e2a\u4ec0 \u4e48\u4e1c\u897f\uff0c\u4f60\u4eec\u6559\u4e3b\u8fd8\u6ca1\u6b7b\uff0c\u54ea\u91cc\u6709\u4f60\u8bb2\u8bdd\u7684\u4f59\u5730\uff1f\u201d \u3000\u3000\u79e6\u69d0\u7b80\u76f4\u88ab\u6c14\u4e86\u4e2a\u53d1\u660f\uff0c\u6068\u4e0d\u5f97\u9a6c\u4e0a\u4e0b\u4ee4\u6240\u6709\u4eba\u51b2\u6740\u8fc7\u53bb\u3002 \u3000\u3000\u5c82\u77e5\u767d\u837b\u5374\u9887\u80fd\u6c89\u5f97\u4f4f\u6c14\uff0c\u4fa7\u8138\u9053\uff1a\u201c\u79e6\u603b\u5ea7\u7528\u4e0d\u7740\u548c\u8fd9\u79cd\u4eba\u8ba1\u8f83\uff0c\u7b49\u672c\u6559\u4e3b\u8bf4\u5b8c\u8bdd\u540e \u518d\u7531\u4f60\u5904\u7f6e\u3002\u201d \u3000\u3000\u5979\u63a5\u7740\u53c8\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u5373\u4f7f\u662f\u5c0f\u59b9\u9519\u4e86\uff0c\u4f60\u4e5f\u603b\u8be5\u5ff5\u5728\u5f53\u5e74\u540c\u95e8\u4e60\u827a\u4e4b\u8c0a\uff0c\u7687 \u5143\u6559\u6210\u7acb\u4e0d\u6613\uff0c\u76ee\u524d\u4f60\u6211\u6b63\u8be5\u540c\u5fc3\u534f\u529b\uff01\u5171\u8c0b\u8fdb\u519b\u4e2d\u539f\u7684\u5927\u4e8b\uff0c\u600e\u53ef\u79bb\u5fc3\u79bb\u5fb7\uff0c\u540c\u5ba4\uff0a\u6208\uff0c \u8fd9\u5c82\u4e0d\u662f\u95f9\u7b11\u8bdd\u7ed9\u522b\u4eba\u770b\u4e48\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u795e\u6001\u51db\u7136\u9053\uff1a\u201c\u767d\u837b\uff0c\u4f60\u4ee5\u4e3a\u8fd9\u513f\u53e5\u8bdd\u5c31\u80fd\u6253\u52a8\u4e25\u67d0\u7684\u5fc3\uff1f\u4e25\u67d0\u5c82\u80fd\u4e3a\u4f60\u8fd9\u4e00\u756a\u82b1 \u8a00\u5de7\u8bed\u6240\u60d1\uff1f\u201d \u3000\u3000\u767d\u837b\u4f9d\u7136\u4e0d\u542b\u6012\u610f\u9053\uff1a\u201c\u90a3\u4e48\u5927\u5e08\u5144\u51c6\u5907\u8ba9\u5c0f\u59b9\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u8bf4\u4ec0\u4e48\u4e5f\u5168\u662f\u5e9f\u8bdd\uff0c\u4f60\u53ea\u8981\u8868\u660e\u4eca\u5929\u7684\u6765\u610f\u5c31\u6210\u4e86\u3002\u201d \u3000\u3000\u767d\u837b\u9053\uff1a\u201c\u5c0f\u59b9\u65b9\u624d\u8bf4\u7684\u90a3\u4e9b\u6d3b\u3002\u5c31\u662f\u4eca\u5929\u7684\u6765\u610f\u3002\u201d \u3000\u3000\u201c\u4e25\u67d0\u529d\u4f60\u8fd8\u662f\u5c11\u8bb2\u5e9f\u8bdd\u7684\u597d\uff0c\u4f60\u867d\u7136\u5e26\u7684\u4eba\u591a\uff0c\u5374\u5e76\u6ca1\u653e\u5728\u8001\u592b\u773c\u91cc\uff0c\u53ea\u7ba1\u8ba9\u4ed6\u4eec\u4e00 \u9f50\u6740\u8fc7\u6765\uff01\u201d \u3000\u3000\u201c\u5927\u5e08\u5144\uff0c\u4f60\u5f53\u771f\u8981\uff0a\u5c0f\u59b9\u548c\u4f60\u5200\u5175\u76f8\u89c1\uff1f\u201d \u3000\u3000\u201c\u8fd9\u6b63\u662f\u4f60\u7684\u6765\u610f\uff0c\u4f55\u5fc5\u8bf4\u6210\u662f\u8001\u592b\u76f8\uff0a\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u62c5\u5fc3\u5bf9\u65b9\u4e00\u9f50\u51b2\u6740\u8fc7\u6765\u65f6\uff0c\u4e25\u5bd2\u548c\u4e25\u5982\u51b0\u3001\u5468\u6d77\u5c71\u7b49\u4eba\u53ef\u80fd\u8981\u5403\u4e8f\uff0c\u8fd9\u65f6\u81ea\u52a8\u5411 \u524d\u8d70\u51fa\u51e0\u6b65\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5982\u679c\u8981\u52a8\u624b\uff0c\u4f55\u7528\u57ce\u4e3b\u4eb2\u81ea\u51fa\u9a6c\uff0c\u53ea\u8981\u5728\u4e0b\u4e00\u4eba\u5c31\u8db3\u53ef\u6536\u62fe\u4f60\u4eec\u7684 \u4e86\uff01\u201d \u3000\u3000\u4ed6\u7684\u7528\u610f\uff0c\u4e0d\u5916\u662f\u4e00\u4e2a\u4e00\u4e2a\u7684\u6536\u62fe\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\uff0c\u5bf9\u65b9\u4e3a\u4e86\u987e\u8651\u8eab\u4efd\uff0c\u7edd\u4e0d\u53ef\u80fd\u8054\u624b\u56f4 \u6740\u4ed6\u4e00\u4eba\u3002 \u3000\u3000\u53ea\u542c\u79e6\u69d0\u9053\uff1a\u201c\u6559\u4e3b\uff0c\u8d75\u62a4\u6cd5\u548c\u9ad8\u62a4\u6cd5\u5c31\u662f\u5403\u4e86\u8fd9\u5c0f\u5b50\u7684\u4e8f\uff0c\u6b64\u523b\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u4fbf\u662f\u5148\u9664 \u53bb\u6b64\u4eba\uff0c\u5426\u5219\u540e\u60a3\u65e0\u7a77\u3002\u201d \u3000\u3000\u767d\u837b\u8e59\u8d77\u5a25\u7709\u9053\uff1a\u201c\u8fd9\u4eba\u8eab\u91cf\u77ee\u5c0f\uff0c\u771f\u662f\u8eab\u624b\u9ad8\u4e0d\u53ef\u6d4b\u4e48\uff1f\u201d \u3000\u3000\u79e6\u69d0\u9053\uff1a\u201c\u6559\u4e3b\u522b\u5c0f\u770b\u4ed6\u8eab\u91cf\u77ee\u5c0f\uff0c\u54b1\u4eec\u6b66\u5173\u7edf\u9886\u591a\u80cc\u5929\u7fc1\u5e9e\u821f\uff0c\u8eab\u91cf\u6bd4\u4ed6\u66f4\u77ee\uff0c\u4f46\u5374 \u662f\u5929\u8c37\u4e4b\u5185\u7684\u7b2c\u4e00\u9ad8\u4eba\u3002\u201d \u3000\u3000\u767d\u837b\u7b11\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u80fd\u548c\u591a\u5929\u80cc\u5929\u7fc1\u5e9e\u8001\u602a\u6bd4\uff0c\u5e9e\u8001\u602a\u7684\u6b66\u529f\uff0c\u5c82\u4e0a\u5929\u8c37\u5185\u7684\u7b2c\u4e00\u4eba\uff0c \u5373\u4fbf\u666e\u5929\u4e4b\u4e0b\uff0c\u4e5f\u7edd\u5bf9\u627e\u4e0d\u51fa\u4ed6\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u79e6\u69d0\u9053\uff1a\u201c\u6559\u4e3b\u4e0d\u59a8\u5148\u53eb\u51fa\u4e00\u4eba\u548c\u4ed6\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u767d\u837b\u4fa7\u76ee\u5de6\u53f3\u626b\u89c6\u4e86\u4e00\u773c\u9053\uff1a\u201c\u4f60\u4eec\u54ea\u4e2a\u613f\u610f\u51fa\u53bb\u628a\u4ed6\u62ff\u4e0b\uff1f\u201d \u3000\u3000\u7acb\u523b\uff0c\u4e00\u540d\u8eab\u6750\u5947\u4f1f\uff0c\u6ee1\u9762\u866c\u9aef\uff0c\u53cc\u624b\u63e1\u9524\u7684\u5927\u6c49\u5e94\u58f0\u800c\u51fa\u3002 \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u4e8c\u5341\u516d\u56de\u3000\u660e\u5e2e\u6697\u52a9 \u3000\u3000\u8fd9\u5927\u6c49\u65b9\u624d\u548c\u4f17\u4eba\u7ad9\u5728\u4e00\u8d77\uff0c\u5e76\u672a\u5f15\u8d77\u5cb3\u5c0f\u98de\u7684\u6ce8\u610f\uff0c\u4f46\u6b64\u523b\u7ad9\u51fa\u6765\uff0c\u5374\u58ee\u5f97\u7b80\u76f4\u50cf\u5ea7 \u94c1\u5854\u3002 \u3000\u3000\u5c24\u5176\u8bba\u8eab\u91cf\uff0c\u8db3\u8db3\u6bd4\u5cb3\u5c0f\u98de\u9ad8\u4e86\u4e00\u4e2a\u5934\u4ee5\u4e0a\u3002 \u3000\u3000\u8fd9\u5927\u6c49\u7684\u4e24\u67c4\u9524\uff0c\u5404\u91cd\u56db\u4e94\u5341\u65a4\uff0c\u5c31\u50cf\u662f\u68d2\u9524\u4e0a\u6311\u7740\u4e24\u4e2a\u5927\u9ed1\u76ae\u897f\u74dc\uff0c\u770b\u4e0a\u53bb\u5c31\u591f\u5413\u4eba \u7684\uff0c\u8db3\u8bc1\u660e\u4ed6\u529b\u5927\u65e0\u7a77\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4eba\u662f\u7687\u5143\u6559\u7684\u62a4\u575b\u4f7f\u8005\uff0c\u59d3\u53e4\u540d\u4eae\u3002 \u3000\u3000\u62a4\u575b\u4f7f\u8005\u7684\u5730\u4f4d\u867d\u5728\u62a4\u6cd5\u4e4b\u4e0b\uff0c\u4f46\u53e4\u4eae\u7684\u4e00\u8eab\u6b66\u529f\uff0c\u5374\u5728\u516b\u5927\u62a4\u6cd5\u4efb\u4f55\u4e00\u4eba\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u4ed6\u53ea\u662f\u4e00\u4e2a\u6d51\u4eba\uff0c\u9664\u6b66\u529f\u4e4b\u5916\uff0c\u5e76\u65e0\u591a\u5927\u5934\u8111\uff0c\u6240\u4ee5\u65e0\u6cd5\u5347\u4efb\u5230\u62a4\u6cd5\u6216\u5176\u4ed6\u804c \u4f4d\u4e0a\u53bb\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u53e4\u4eae\u628a\u53cc\u9524\u5728\u624b\u91cc\u7ffb\u8f6c\u4e00\u6382\uff0c\u53cc\u9524\u5c45\u7136\u5728\u8fd9\u4e00\u6382\u4e4b\u95f4\uff0c\u81f3\u5c11\u6253\u4e86\u4e94\u516d\u8f6c\uff0c\u50cf\u73a9\u9b54 \u672f\u4e00\u822c\uff0c\u4ec5\u51ed\u4ed6\u8fd9\u4f6f\u8f7b\u8f7b\u9732\u5c71\u4e00\u624b\uff0c\u5c31\u8db3\u4ee5\u4f7f\u4eba\u770b\u5f97\u773c\u82b1\u7f2d\u4e71\u3002 \u3000\u3000\u63a5\u7740\uff0c\u54a7\u5634\u4e00\u58f0\u51b7\u7b11\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u542c\u8bf4\u603b\u575b\u4e24\u4f4d\u62a4\u6cd5\u90fd\u683d\u5728\u4f60\u624b\u91cc\uff0c\u54b1\u8fd8\u53ea\u9053\u4f60\u751f\u6765\u4e09 \u5934\u516d\u81c2\uff0c\u73b0\u5728\u4e00\u770b\uff0c\u5374\u771f\u8ba9\u4eba\u7b11\u6389\u5927\u7259\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u624b\u6a2a\u957f\u5251\uff0c\u7ad9\u5728\u539f\u5730\uff0c\u7eb9\u98ce\u4e0d\u52a8\uff0c\u6de1\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5c11\u8bf4\u5e9f\u8bdd\uff0c\u5728\u4e0b\u8ba9\u4f60\u5148\u653b\u3002\u201d \u3000\u3000\u53e4\u4eae\u9f87\u7740\u4e24\u9897\u9ec4\u7259\u9053\uff1a\u201c\u5c0f\u5fc3\u54b1\u8fd9\u4e00\u9524\u4e0b\u53bb\uff0c\u5c31\u8ba9\u4f60\u53d8\u6210\u8089\u9171\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u613f\u548c\u4ed6\u6597\u5634\uff0c\u7d22\u6027\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53cc\u65b9\u6240\u6709\u7684\u4eba\u7684\u76ee\u5149\uff0c\u65e9\u5df2\u96c6\u4e2d\u5728\u5cb3\u5c0f\u98de\u548c\u53e4\u4eae\u4e24\u4eba\u8eab\u4e0a\uff0c\u5176\u4e2d\u9664\u4eb2\u773c\u89c1\u8fc7\u5cb3\u5c0f \u98de\u6b66\u529f\u7684\u4eba\u5916\uff0c\u8c01\u90fd\u8ba4\u4e3a\u4ed6\u7edd\u96be\u63a5\u8fc7\u4e09\u62db\u3002 \u3000\u3000\u53e4\u4eae\u89c1\u5bf9\u65b9\u5c45\u7136\u4e0d\u7406\u4e0d\u776c\uff0c\u4e0d\u89c9\u706b\u5927\u4e86\uff0c\u4e00\u4e2a\u864e\u8dc3\uff0c\u53cc\u9524\u76f4\u5982\u6cf0\u5c71\u538b\u9876\u822c\uff0c\u5411\u5cb3\u5c0f\u98de\u5934 \u4e0a\u7838\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5f53\u7136\u4e0d\u60f3\u548c\u4ed6\u786c\u62da\uff0c\u53e4\u4eae\u7684\u53cc\u9524\u5939\u7740\u52b2\u98ce\u843d\u4e0b\u540e\uff0c\u65e9\u5df2\u98d8\u8eab\u5411\u4e00\u4fa7\u79fb\u5f00\u6570\u5c3a\u3002 \u3000\u3000\u53e4\u4eae\u53cc\u9524\u843d\u7a7a\uff0c\u51e0\u4e4e\u95ea\u4e86\u4e2a\u5927\u7b4b\u6597\u3002 \u3000\u3000\u4ed6\u8eab\u6750\u867d\u7136\u9ad8\u5927\uff0c\u624b\u811a\u5374\u7075\u6d3b\u65e0\u6bd4\uff0c\u5c31\u5730\u5411\u4fa7\u65b9\u4e00\u4e2a\u6ed1\u6b65\uff0c\u7adf\u53c8\u8ffd\u4e0a\u4e86\u5cb3\u5c0f\u98de\uff0c\u53cc\u9524\u4e5f \u5728\u540c\u65f6\u75be\u62a1\u800c\u4e0b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5c31\u52bf\u53c8\u662f\u4e00\u4e2a\u5012\u7eb5\uff0c\u518d\u5ea6\u7ffb\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u53e4\u4eae\u4e24\u51fb\u4e0d\u6210\uff0c\u706b\u6c14\u66f4\u5927\uff0c\u5e72\u8106\u8feb\u7740\u5cb3\u5c0f\u98de\u53cc\u9524\u4ea4\u9519\u4e0b\u624b\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u624b\u7528\u5f97\u5341\u5206\u6709\u6548\uff0c\u4f7f\u5f97\u5cb3\u5c0f\u98de\u9632\u4e0d\u80dc\u9632\uff0c\u5076\u800c\u957f\u5251\u89e6\u4e0a\u5bf9\u65b9\u7684\u9524\uff0c\u51e0\u4e4e\u5c06\u624b\u8155\u9707 \u5f97\u53d1\u9ebb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u51b3\u5b9a\u6539\u5b88\u4e3a\u653b\uff0c\u53cc\u811a\u4e00\u70b9\uff0c\u7acb\u5373\u817e\u7a7a\u800c\u8d77\uff0c\u7136\u540e\u7a7a\u4e2d\u4e00\u4e2a\u5012\u8f6c\uff0c\u53d8\u6210\u5934\u4e0b\u811a\u4e0a\uff0c \u94f6\u8292\u4e00\u95ea\uff0c\u5251\u950b\u76f4\u6307\u53e4\u4eae\u201c\u5929\u7075\u201d\u5927\u7a74\u3002 \u3000\u3000\u53e4\u4eae\u7530\u8f7b\u529f\u4e0d\u6d4e\uff0c\u4e0d\u4e60\u60ef\u8fd9\u79cd\u6253\u6cd5\uff0c\u614c\u5fd9\u4ef0\u8eab\u53cc\u9524\u5411\u4e0a\u67b6\u683c\u3002 \u3000\u3000\u5251\u5c16\u548c\u9524\u4e00\u63a5\uff0c\u5cb3\u5c0f\u98de\u7adf\u53c8\u817e\u8d77\u4e03\u516b\u5c3a\u9ad8\uff0c\u63a5\u7740\u518d\u5ea6\u632f\u5251\u51b2\u4e0b\u3002 \u3000\u3000\u5c31\u8fd9\u6837\u53cc\u529b\u5175\u5203\u4e00\u8fde\u4ea4\u63a5\u4e86\u4e94\u516d\u6b21\uff0c\u5cb3\u5c0f\u98de\u4e5f\u4e00\u8fde\u5728\u7a7a\u4e2d\u5f39\u5347\u4e86\u4e94\u516d\u6b21\uff0c\u4e00\u76f4\u4fdd\u6301\u7740\u51cc \u7a7a\u4e0d\u5760\u3002 \u3000\u3000\u4ec5\u51ed\u5cb3\u5c0f\u98de\u8fd9\u4e00\u8eab\u60ca\u4e16\u9a87\u4fd7\u7684\u8f7b\u529f\uff0c\u5c31\u4f7f\u5f97\u4e24\u65c1\u89c2\u6218\u7684\u4eba\uff0c\u90fd\u4e0d\u7981\u77a0\u76ee\u7ed3\u820c\u3002 \u3000\u3000\u53e4\u4eae\u4e5f\u8d8a\u6218\u8d8a\u60ca\uff0c\u5c31\u8fd9\u4e48\u77ed\u7684\u65f6\u95f4\uff0c\u4fbf\u5df2\u6c57\u6d41\u6d43\u80cc\uff0c\u6e7f\u900f\u8863\u886b\uff0c\u4ee4\u4eba\u5206\u4e0d\u6e05\u4ed6\u8fd9\u8eab\u6c57\u662f \u7d2f\u51fa\u6765\u7684\u8fd8\u662f\u5413\u51fa\u6765\u7684\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u5fae\u4e00\u5931\u795e\u4e4b\u4e0b\uff0c\u7a7a\u4e2d\u7adf\u5df2\u4e0d\u89c1\u4e86\u5cb3\u5c0f\u98de\u7684\u4eba\u5f71\u3002 \u3000\u3000\u53e4\u4eae\u9a87\u7136\u65e0\u63aa\u4e2d\u6b63\u6b32\u8f6c\u8eab\u5bdf\u770b\uff0c\u540e\u80cc\u65e9\u4e2d\u4e86\u5cb3\u5c0f\u98de\u4e00\u8bb0\u51cc\u7a7a\u98de\u8e22\u3002 \u3000\u3000\u8fd9\u4e00\u811a\u7531\u4e8e\u901f\u5ea6\u592a\u5feb\uff0c\u529b\u9053\u4e5f\u5c31\u5947\u91cd\uff0c\u8e22\u5f97\u53e4\u4eae\u4e00\u58f0\u95f7\u54fc\uff0c\u7ad9\u811a\u4e0d\u4f4f\uff0c\u76f4\u5411\u524d\u5954\u6454\u800c\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u53ea\u5954\u51fa\u51e0\u6b65\uff0c\u5cb3\u5c0f\u98de\u7684\u7b2c\u4e8c\u811a\u53c8\u5df2\u8e22\u4e0a\u540e\u80cc\u3002 \u3000\u3000\u8fd9\u6b21\u5cb3\u5c0f\u98de\u662f\u51cc\u7a7a\u4ef0\u8eab\u53cc\u8db3\u9f50\u51fa\uff0c\u8e6c\u5f97\u4ed6\u5f53\u573a\u6251\u6454\u5728\u5730\uff0c\u6f14\u51fa\u4e00\u5f0f\u6807\u51c6\u7684\u201c\u72d7\u5403\u5c4e\u201d\uff0c \u8fde\u4e24\u67c4\u9524\u4e5f\u8131\u8155\u98de\u51fa\uff0c\u90a3\u79cd\u72fc\u72c8\u60c5\u5f62\uff0c\u4e0d\u4eb2\u773c\u770b\u5230\u7684\u7edd\u96be\u60f3\u8c61\u3002 \u3000\u3000\u5176\u5b9e\u5cb3\u5c0f\u98de\u65b9\u624d\u8981\u60f3\u53d6\u53e4\u4eae\u6027\u547d\uff0c\u6613\u5982\u53cd\u638c\uff0c\u53ea\u662f\u4ed6\u60f3\u5230\u4e0d\u5230\u6700\u540e\u5173\u5934\uff0c\u8fd8\u662f\u6682\u65f6\u907f\u514d \u51fa\u73b0\u6e85\u8840\u6a2a\u5c38\u573a\u9762\u8f83\u597d\u3002 \u3000\u3000\u53e4\u4eae\u597d\u4e0d\u5bb9\u6613\u624d\u722c\u4e86\u8d77\u6765\uff0c\u4e0d\u8fc7\u4ed6\u8fd9\u4e00\u722c\u8d77\u6765\uff0c\u5cb3\u5c0f\u98de\u771f\u5fcd\u4e0d\u4f4f\u60f3\u7b11\u3002 \u3000\u3000\u53ea\u89c1\u53e4\u4eae\u5634\u5df4\u5df2\u6b6a\uff0c\u8fde\u9f3b\u5b50\u4e5f\u504f\u5728\u4e00\u8fb9\uff0c\u4ed6\u4e00\u624b\u6258\u4f4f\u4e0b\u816d\uff0c\u90a3\u9f87\u7259\u54a7\u7259\u7684\u6a21\u6837\u53c8\u50cf\u5728\u7b11\uff0c \u800c\u4e14\u8fd8\u52fe\u7740\u8170\u6709\u5982\u4e00\u53ea\u5927\u72d7\u718a\uff0c\u770b\u5f97\u4e25\u5bd2\u8fd9\u8fb9\u7684\u4eba\u90fd\u5fcd\u4fca\u4e0d\u7981\u3002 \u3000\u3000\u4f46\u767d\u837b\u7684\u8138\u8272\u5374\u662f\u96be\u770b\u5f97\u5982\u7f69\u5bd2\u971c\u3002 \u3000\u3000\u968f\u6765\u7684\u56db\u540d\u62a4\u6cd5\uff0c\u5305\u62ec\u8d75\u660e\u6708\u548c\u9ad8\u5e84\u5728\u5185\uff0c\u4ee5\u53ca\u53e6\u5916\u51e0\u540d\u9ad8\u624b\uff0c\u4f3c\u4e4e\u90fd\u4e0d\u613f\u81ea\u884c\u51fa\u6218\u3002 \u3000\u3000\u8fd8\u597d\uff0c\u5927\u516c\u571f\u767d\u91d1\u51e4\u671b\u7740\u767d\u837b\u88e3\u887d\u4e00\u793c\u9053\uff1a\u201c\u7980\u6559\u4e3b\uff0c\u8ba9\u5973\u513f\u51fa\u53bb\u63a5\u4ed6\u51e0\u62db\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u767d\u837b\u51dd\u7740\u8138\u8272\u9053\uff1a\u201c\u8981\u591a\u52a0\u5c0f\u5fc3\uff01\u201d \u3000\u3000\u767d\u91d1\u51e4\u7528\u7684\u662f\u4e00\u6761\u4eae\u94f6\u8f6f\u97ad\uff0c\u7528\u624b\u63e1\u8f6f\u97ad\uff0c\u8885\u8885\u5a1c\u5a1c\u7684\u8d70\u4e0b\u573a\u6765\u3002 \u3000\u3000\u6b64\u523b\u7684\u5cb3\u5c0f\u98de\uff0c\u5b9e\u5728\u5927\u611f\u4e3a\u96be\uff0c\u5728\u7687\u5143\u6559\u603b\u575b\u4e2d\uff0c\u4ed6\u6700\u611f\u6fc0\u7684\u4fbf\u662f\u767d\u91d1\u51e4\u3002 \u3000\u3000\u56de\u60f3\u521d\u5230\u62db\u8d24\u5206\u9986\u65f6\uff0c\u767d\u91d1\u51e4\u66fe\u4ed7\u4e49\u4e3a\u4ed6\u89e3\u9664\u4e86\u4e0d\u5c11\u5371\u96be\uff0c\u8fde\u7236\u4eb2\u4e5f\u5f97\u8fc7\u5979\u4e0d\u5c11\u5e2e\u52a9\uff0c \u5982\u4eca\u4e0d\u4f46\u4e0d\u80fd\u62a5\u7b54\u5979\uff0c\u53cd\u800c\u8981\u5f53\u9762\u53ae\u6740\uff0c\u5185\u5fc3\u600e\u80fd\u8fc7\u610f\u5f97\u53bb\u3002 \u3000\u3000\u56e0\u4e4b\uff0c\u77ac\u606f\u95f4\u4ed6\u5df2\u51b3\u5b9a\u51fa\u624b\u65f6\u5c3d\u91cf\u4e0d\u4f7f\u5bf9\u65b9\u96be\u582a\uff0c\u53ea\u6c42\u81ea\u4fdd\uff0c\u7edd\u4e0d\u6c42\u80dc\uff0c\u5c3d\u91cf\u7ed9\u5bf9\u65b9\u7559 \u70b9\u9762\u5b50\u3002 \u3000\u3000\u4ed6\u968f\u5373\u62b1\u5251\u4e00\u793c\u9053\uff1a\u201c\u82b3\u9a7e\u53ef\u662f\u5927\u516c\u4e3b\u4e48\uff1f\u201d \u3000\u3000\u767d\u91d1\u51e4\u51e4\u76ee\u51dd\u5a01\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u5230\u5e95\u662f\u8c01\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5728\u4e0b\u5173\u5927\u9e4f\u3002\u201d \u3000\u3000\u201c\u4f60\u76ee\u524d\u662f\u4ec0\u4e48\u804c\u4f4d\uff1f\u201d \u3000\u3000\u201c\u57ce\u4e3b\u8eab\u8fb9\u4e00\u540d\u5c0f\u5c0f\u62a4\u536b\u3002\u201d \u3000\u3000\u201c\u6211\u600e\u4e48\u4ece\u6765\u6ca1\u89c1\u8fc7\u4f60\uff1f\u201d \u3000\u3000\u201c\u5927\u516c\u4e3b\u8eab\u5728\u9ad8\u4f4d\uff0c\u600e\u53ef\u80fd\u8ba4\u8bc6\u50cf\u5728\u4e0b\u8fd9\u79cd\u65e0\u540d\u5c0f\u5352\u3002\u201d \u3000\u3000\u201c\u6211\u770b\u4f60\u5f88\u50cf\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u5927\u516c\u4e3b\u771f\u4f1a\u8bf4\u7b11\u8bdd\uff0c\u5728\u4e0b\u672c\u6765\u5c31\u662f\u4eba\u3002\u201d \u3000\u3000\u201c\u6211\u662f\u8bf4\u4f60\u7684\u6a21\u6837\u867d\u4e0d\u50cf\uff0c\u4f46\u8eab\u6750\u5374\u5f88\u50cf\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u89c9\u5fc3\u5934\u4e00\u9707\uff0c\u83ab\u975e\u5df2\u88ab\u77a7\u51fa\u7834\u7efd\uff1f\u201d \u3000\u3000\u4ed6\u6de1\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5927\u516c\u4e3b\u4e0d\u5fc5\u591a\u8bf4\uff0c\u5c31\u8bf7\u8d50\u62db\uff01\u201d \u3000\u3000\u767d\u91d1\u51e4\u53f3\u8155\u4e00\u6296\uff0c\u90a3\u4e94\u516d\u5c3a\u957f\u7684\u4eae\u94f6\u8f6f\u97ad\uff0c\u5bd2\u8292\u95ea\u70c1\uff0c\u6709\u5982\u4e00\u6761\u94f6\u86c7\u822c\uff0c\u5939\u7740\u9510\u98ce\uff0c\u95ea \u7535\u822c\u76f4\u5411\u5cb3\u5c0f\u98de\u62e6\u8170\u626b\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u9762\u8dc3\u8eab\u75be\u9000\uff0c\u4e00\u9762\u6325\u5251\u53cd\u524a\u4e0a\u53bb\u3002 \u3000\u3000\u53ea\u542c\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u97ad\u68a2\u6b63\u597d\u7f20\u4f4f\u4e86\u5251\u8eab\u3002 \u3000\u3000\u767d\u91d1\u51e4\u7acb\u5373\u594b\u529b\u5c06\u97ad\u5f80\u6000\u91cc\u5e26\u53bb\u3002 \u3000\u3000\u5979\u56e0\u97ad\u957f\uff0c\u8fd9\u731b\u529b\u4e00\u5e26\uff0c\u52b2\u9053\u5947\u5927\uff0c\u5cb3\u5c0f\u98de\u4e00\u4e2a\u7ad9\u811a\u4e0d\u4f4f\uff0c\u7adf\u771f\u7684\u88ab\u5e26\u52a8\u5f97\u5411\u524d\u5954\u51fa\u597d \u51e0\u6b65\u3002 \u3000\u3000\u767d\u91d1\u51e4\u679c\u7136\u8eab\u624b\u4e0d\u51e1\uff0c\u8d8b\u52bf\u626c\u8d77\u5de6\u8155\uff0c\u3000\u4e00\u638c\u5411\u5cb3\u5c0f\u98de\u524d\u80f8\u62cd\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6025\u6025\u5c06\u8eab\u5b50\u4e00\u65cb\uff0c\u501f\u8fd9\u4e00\u65cb\u4e4b\u529b\uff0c\u4e0d\u4f46\u5c06\u5251\u8eab\u8131\u79bb\u4e86\u97ad\u68a2\uff0c\u4eba\u4e5f\u5411\u4e00\u4fa7\u79fb\u5f00\u4e86\u6570 \u5c3a\uff0c\u6b63\u597d\u907f\u6765\u4e86\u767d\u91d1\u51e4\u51fb\u6765\u7684\u4e00\u638c\u3002 \u3000\u3000\u53cc\u65b9\u4ea4\u624b\u4e24\u62db\uff0c\u663e\u7136\u662f\u767d\u91d1\u51e4\u5360\u4e86\u4e0a\u98ce\uff0c\u81f3\u5c11\u5979\u662f\u4e00\u76f4\u91c7\u53d6\u653b\u52bf\u7684\u3002 \u3000\u3000\u800c\u5cb3\u5c0f\u98de\u5374\u4f3c\u5e73\u9677\u5165\u88ab\u52a8\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u7687\u5143\u6559\u603b\u575b\u65b9\u9762\u7684\u9ad8\u624b\uff0c\u83ab\u4e0d\u4e3a\u767d\u91d1\u51e4\u6697\u6697\u559d\u5f69\u3002 \u3000\u3000\u767d\u91d1\u51e4\u4f3c\u4e4e\u4e0d\u5bb9\u5bf9\u65b9\u6709\u5598\u606f\u673a\u4f1a\uff0c\u201c\u547c\u201d\u7684\u4e00\u97ad\uff0c\u5982\u5f71\u968f\u5f62\uff0c\u8d34\u5730\u626b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4eae\u94f6\u97ad\u8fc7\u5904\uff0c\u5730\u9762\u6c99\u77f3\u6a2a\u98de\uff0c\u5c18\u571f\u5192\u8d77\uff0c\u8fd9\u4e00\u97ad\u5982\u679c\u626b\u4e2d\u5c0f\u817f\uff0c\u52bf\u5fc5\u5c06\u53cc\u817f\u524a\u6210\u4e24\u622a\u3002 \u3000\u3000\u5c82\u77e5\u5979\u97ad\u521a\u51fa\u624b\uff0c\u5cb3\u5c0f\u98de\u5df2\u7136\u4eba\u5728\u534a\u7a7a\uff0c\u51dd\u5251\u4e0d\u52a8\uff0c\u4e00\u7f15\u6307\u98ce\uff0c\u5c04\u5411\u767d\u91d1\u51e4\u53cc\u80a9\u3002 \u3000\u3000\u767d\u91d1\u51e4\u4e0d\u5931\u773c\u660e\u624b\u5feb\uff0c\u62db\u52bf\u4e0d\u6562\u9012\u6ee1\uff0c\u4fbf\u6025\u6025\u4fa7\u8eab\u95ea\u907f\u3002 \u3000\u3000\u53ea\u89c1\u5cb3\u5c0f\u98de\u6a2a\u5251\u7ad9\u5728\u4e08\u4f59\u5916\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u5927\u516c\u4e3b\uff0c\u6211\u770b\u54b1\u4eec\u7528\u4e0d\u7740\u518d\u6253\u4e86\u3002\u201d \u3000\u3000\u767d\u91d1\u51e4\u6123\u4e86\u6123\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u6253\u4e86\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u56e0\u4e3a\u5728\u4e0b\u4eca\u5929\u8981\u6218\u7684\u5bf9\u8c61\u4e0d\u662f\u4f60\u3002\u201d \u3000\u3000\u767d\u91d1\u51e4\u53f1\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u52a8\u624b\u62da\u640f\uff0c\u54ea\u91cc\u8fd8\u5bb9\u5f97\u4f60\u81ea\u9009\u5bf9\u8c61\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5f53\u7136\u5176\u4e2d\u6709\u539f\u56e0\u3002\u201d \u3000\u3000\u201c\u4ec0\u4e48\u539f\u56e0\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u89c9\u5f97\u82e5\u548c\u5927\u516c\u4e3b\u6253\uff0c\u53ea\u6015\u6253\u4e00\u5929\u4e00\u591c\u4e5f\u5206\u4e0d\u51fa\u80dc\u8d1f\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u53c8\u4f55\u5fc5\u6d6a\u8d39\u65f6 \u95f4\uff1f\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u5206\u4e0d\u51fa\u80dc\u8d1f\uff1f\u201d \u3000\u3000\u201c\u6211\u53ef\u4ee5\u62c5\u4fdd\u3002\u201d \u3000\u3000\u201c\u6211\u504f\u8981\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u201c\u5927\u516c\u4e3b\u82e5\u6267\u610f\u4e0d\u4fe1\uff0c\u5728\u4e0b\u4e5f\u53ea\u6709\u5949\u966a\uff01\u201d \u3000\u3000\u767d\u91d1\u51e4\u4e0d\u518d\u7b54\u8bdd\uff0c\u50cf\u53ea\u5927\u8774\u8776\u822c\uff0c\u51cc\u7a7a\u98de\u6251\u800c\u4e0b\uff0c\u6325\u97ad\u5c55\u5f00\u731b\u653b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u4e0d\u518d\u95ea\u8ba9\uff0c\u62a1\u5251\u4e5f\u5c55\u5f00\u751f\u5e73\u6240\u5b66\u3002 \u3000\u3000\u8fd9\u6b21\u4e24\u4eba\u7684\u51fa\u62db\u63a5\u62db\uff0c\u90fd\u5feb\u5f97\u4e0d\u80fd\u518d\u5feb\uff0c\u7b80\u76f4\u4ee4\u4eba\u76ee\u4e0d\u6687\u63a5\u3002 \u3000\u3000\u5230\u540e\u6765\uff0c\u97ad\u5982\u5343\u767e\u6761\u94f6\u86c7\u6f2b\u7a7a\u98de\u821e\uff0c\u5251\u4f3c\u65e0\u6570\u9053\u5bd2\u5149\u5f53\u5934\u95ea\u5c04\uff0c\u4f46\u95fb\u5530\u5530\u5530\u5632\u5530\u5530\u5530\u5530 \u5530\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u4e08\u4f59\u65b9\u5706\u4e4b\u5185\uff0c\u6709\u5982\u88ab\u4e00\u7247\u5149\u5f71\u7b3c\u7f69\uff0c\u6839\u672c\u770b\u4e0d\u51fa\u5cb3\u5c0f\u98de\u548c\u767d\u91d1\u51e4\u4eba\u5728\u54ea \u91cc\u3002 \u3000\u3000\u8db3\u8db3\u4e00\u76cf\u70ed\u8336\u7684\u5de5\u592b\u8fc7\u53bb\uff0c\u8c01\u4e5f\u6ca1\u6570\u6e05\u4e24\u4eba\u5230\u5e95\u5bf9\u62c6\u4e86\u591a\u5c11\u62db\uff0c\u4f46\u5374\u663e\u800c\u6613\u89c1\uff0c\u767d\u91d1\u51e4 \u7684\u97ad\u52bf\u5df2\u6e10\u6e10\u7f13\u6162\u4e0b\u6765\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u5cb3\u5c0f\u98de\u867d\u5251\u62db\u4ecd\u6c14\u52bf\u5982\u8679\uff0c\u5374\u5e76\u4e0d\u5411\u524d\u8fdb\uff0a\uff0c\u6beb\u65e0\u8fdb\u653b\u6a21\u6837\u3002 \u3000\u3000\u767d\u91d1\u51e4\u7ec8\u4e8e\u88ab\u8feb\u5411\u540e\u8dc3\u51fa\u4e24\u4e08\u4e4b\u5916\u3002 \u3000\u3000\u5f53\u5979\u7a33\u4f4f\u5a07\u8eaf\uff0c\u4f17\u4eba\u624d\u770b\u6e05\u5979\u8138\u8272\u4e00\u7247\u60e8\u767d\uff0c\u5a07\u5598\u5401\u5401\uff0c\u9999\u6c57\u6dcb\u6f13\uff0c\u540e\u8eab\u548c\u80f8\u524d\u7684\u8863\u670d\uff0c \u51e0\u4e4e\u5168\u5df2\u88ab\u6c57\u6c34\u6e17\u900f\u3002 \u3000\u3000\u4f46\u5cb3\u5c0f\u98de\u5374\u4f9d\u7136\u6c14\u5b9a\u795e\u95f2\uff0c\u5c31\u50cf\u65b9\u624d\u6839\u672c\u6ca1\u7ecf\u8fc7\u6fc0\u70c8\u62da\u640f\u4e00\u6837\u3002 \u3000\u3000\u4ed6\u6a2a\u5251\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5927\u516c\u4e3b\uff0c\u5728\u4e0b\u8bf4\u7684\u4e0d\u5047\u5427\uff0c\u53cc\u65b9\u6574\u6574\u5bf9\u62c6\u4e86\u767e\u62db\uff0c\u8c01\u4e5f\u6ca1\u6cbe\u5230\u8c01 \u7684\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u767d\u91d1\u51e4\u8138\u8272\u7ec8\u4e8e\u5728\u60e8\u767d\u4e2d\u6cdb\u51fa\u4e00\u62b9\u7edb\u971e\u3002 \u3000\u3000\u5979\u8fd8\u80fd\u8bf4\u4ec0\u4e48\uff0c\u660e\u660e\u81ea\u5df1\u5df2\u65e0\u529b\u518d\u6218\uff0c\u800c\u5bf9\u65b9\u5374\u6c14\u4e0d\u51fa\u58f0\uff0c\u9762\u4e0d\u6539\u8272\u3002 \u3000\u3000\u5979\u8d25\u5f97\u4e00\u70b9\u4e5f\u6ca1\u6709\u6028\u6068\uff0c\u800c\u4e14\u82b3\u5fc3\u4e2d\u5bf9\u654c\u65b9\u6709\u8bf4\u4e0d\u51fa\u7684\u611f\u6fc0\uff0c\u56e0\u4e3a\u4eba\u5bb6\u5206\u660e\u5904\u5904\u4e3a\u81ea\u5df1 \u7559\u4e0b\u4e86\u4f59\u5730\u3002 \u3000\u3000\u5f53\u5979\u521a\u9000\u56de\u9635\u4e2d\uff0c\u79e6\u69d0\u5df2\u5389\u58f0\u559d\u9053\uff1a\u201c\u591a\u4e0a\u53bb\u51e0\u4e2a\u628a\u4ed6\u62ff\u4e0b\uff01\u201d \u3000\u3000\u56db\u540d\u62a4\u6cd5\uff0c\u679c\u7136\u4e00\u9f50\u51fa\u9635\u3002\u5176\u4e2d\u4e24\u4eba\uff0c\u4ecd\u662f\u6628\u65e5\u8d25\u5728\u5cb3\u5c0f\u98de\u624b\u4e0b\u7684\u9ad8\u5e84\u548c\u8d75\u660e\u6708\u3002 \u3000\u3000\u5176\u5b9e\u8fd9\u56db\u4eba\u5fc3\u91cc\u90fd\u6709\u6570\uff0c\u82e5\u5355\u6253\u72ec\u6597\uff0c\u6839\u672c\u652f\u6301\u4e0d\u4e86\u51e0\u62db\uff0c\u73b0\u5728\u4ed7\u7740\u4eba\u591a\uff0c\u5f53\u7136\u5df2\u65e0\u6240 \u754f\u60e7\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u662f\u8981\u8054\u624b\u5408\u653b\u5bf9\u4ed8\u5728\u4e0b\uff1f\u201d \u3000\u3000\u8d75\u660e\u6708\u5e72\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8d75\u67d0\u81ea\u77e5\u4e00\u4eba\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\uff0c\u4e0d\u5f97\u4e0d\u91c7\u53d6\u8054\u624b\u5408\u653b\u7684\u6253\u6cd5\uff0c\u4f60 \u82e5\u6015\u4e86\uff0c\u6211\u4eec\u4e5f\u5e76\u4e0d\u52c9\u5f3a\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6717\u6717\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u957f\u4e86\u8fd9\u4e48\u5927\uff0c\u4ece\u6765\u4e0d\u6653\u5f97\u4ec0\u4e48\u53eb\u6015\uff0c\u7528\u4e0d\u7740\u5ba2\u6c14\uff0c\u4e0a \u5427\uff01\u201d \u3000\u3000\u8fd9\u6b21\u5cb3\u5c0f\u98de\u867d\u4e0d\u60f3\u4f24\u4eba\uff0c\u4f46\u5374\u51b3\u5b9a\u6765\u4e2a\u901f\u6218\u901f\u51b3\uff0c\u81f3\u5c11\u5e94\u8be5\u628a\u771f\u672c\u9886\u62ff\u51fa\u6765\u8ba9\u5bf9\u65b9\u89c1\u8bc6 \u89c1\u8bc6\u3002 \u3000\u3000\u4f46\u89c1\u8d75\u660e\u6708\u7b49\u56db\u540d\u62a4\u6cd5\u5728\u4e00\u58f0\u6697\u53f7\u4e4b\u4e0b\uff0c\u8eab\u6cd5\u79fb\u52a8\u95f4\uff0c\u5f88\u5feb\u4f7f\u5404\u53d6\u65b9\u4f4d\uff0c\u628a\u5cb3\u5c0f\u98de\u56f4\u5728 \u4e2d\u95f4\u3002 \u3000\u3000\u63a5\u7740\u53c8\u662f\u4e00\u58f0\u6697\u53f7\uff0c\u56db\u4eba\u4e00\u9f50\u95ea\u7535\u822c\u53d1\u52a8\uff0c\u56db\u6837\u5175\u5203\uff0c\u524d\u3001\u540e\u3001\u5de6\u3001\u53f3\u5411\u5cb3\u5c0f\u98de\u653b\u51fb\u8fc7 \u6765\u3002 \u3000\u3000\u8fd9\u56db\u4eba\u4f3c\u662f\u65e9\u6709\u9ed8\u5951\uff0c\u914d\u5408\u5f97\u6070\u5230\u597d\u5904\uff0c\u5a01\u529b\u4e4b\u5927\uff0c\u653b\u52bf\u4e4b\u731b\uff0c\u6709\u5982\u72c2\u98ce\u9aa4\u96e8\u822c\uff0c\u706b\u529b \u96c6\u4e2d\u4e00\u70b9\uff0c\u4f3c\u4e4e\u60f3\u4e00\u51fa\u624b\u5c31\u628a\u5cb3\u5c0f\u98de\u7f6e\u4e8e\u6b7b\u5730\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6599\u5b9a\u56db\u9762\u8fce\u654c\uff0c\u5fc5\u611f\u5403\u529b\uff0c\u4e0d\u7b49\u56db\u9762\u5175\u5203\u88ad\u5230\uff0c\u4e00\u5f0f\u201c\u4e00\u9e64\u51b2\u5929\u201d\uff0c\u4eba\u5df2\u76f4\u4e0a\u76f4 \u4e0b\u7684\u51cc\u7a7a\u800c\u8d77\uff0c\u7136\u540e\u98d8\u843d\u4e00\u4fa7\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u53cd\u800c\u4f7f\u5f97\u56db\u540d\u62a4\u6cd5\u7684\u5175\u5203\uff0c\u51e0\u4e4e\u7ea0\u7f20\u5728\u4e00\u8d77\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u6492\u5f00\u5175\u5203\u6b63\u6b32\u5bfb\u627e\u76ee\u6807\u65f6\uff0c\u53ea\u542c\u4e00\u58f0\u60e8\u547c\uff0c\u8d75\u660e\u6708\u9996\u5148\u88ab\u4e00\u6307\u70b9\u4e2d\u540e\u80cc\uff0c\u4ef0\u8eab\u5012 \u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u5cb3\u5c0f\u98de\u7528\u4e0d\u7740\u56db\u9762\u8fce\u654c\uff0c\u5bf9\u65b9\u4e09\u4eba\uff0c\u54ea\u4e2a\u9760\u81ea\u5df1\u6700\u8fd1\uff0c\u54ea\u4e2a\u5c31\u8981\u5148\u5403\u4e8f\u3002 \u3000\u3000\u679c\u7136\u201c\u5f53\u201d\u7684\u4e00\u58f0\u9707\u54cd\uff0c\u9ad8\u5e84\u7684\u5175\u5203\u9996\u5148\u88ab\u51fb\u98de\uff0c\u5cb3\u5c0f\u98de\u7b2c\u63a5\u7740\u98de\u8d77\u4e00\u811a\uff0c\u76f4\u628a\u9ad8\u5e84\u8e22 \u51fa\u4e24\u4e09\u4e08\u5916\u3002 \u3000\u3000\u53e6\u4e24\u540d\u62a4\u6cd5\u4e00\u89c1\u5927\u9a87\uff0c\u53ea\u6709\u4e5f\u5411\u540e\u6025\u9000\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u5e76\u4e0d\u8ffd\u88ad\uff0c\u6a2a\u5251\u7a33\u7ad9\u539f\u5730\uff0c\u770b\u770b\u8fd8\u6709\u4f55\u4eba\u6765\u653b\u3002 \u3000\u3000\u4ec5\u4ec5\u7247\u523b\u5de5\u592b\uff0c\u4fbf\u5df2\u628a\u56db\u540d\u62a4\u6cd5\u6253\u5f97\u72fc\u72c8\u4e0d\u582a\uff0c\u679c\u7136\u662f\u540d\u526f\u5176\u5b9e\u7684\u901f\u6218\u901f\u51b3\u3002 \u3000\u3000\u5728\u5cb3\u5c0f\u98de\u9884\u6599\uff0c\u4e0b\u9762\u5fc5\u5b9a\u662f\u7687\u5143\u6559\u4e3b\u767d\u837b\u6216\u603b\u62a4\u6cd5\u79e6\u69d0\u4eb2\u81ea\u51fa\u9a6c\u4e86\u3002 \u3000\u3000\u5c82\u77e5\u7687\u5143\u6559\u4e3b\u767d\u837b\u5374\u4e0d\u52a8\u58f0\u8272\u7684\u8f6c\u8eab\u9053\uff1a\u201c\u56de\u603b\u575b\u53bb\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5728\u79e6\u69d0\u3001\u767d\u91d1\u51e4\u4ee5\u53ca\u53e6\u4e24\u540d\u9ad8\u624b\u7684\u7c07\u62e5\u4e0b\uff0c\u8fdb\u5165\u4e00\u9876\u9ec4\u8272\u5c0f\u8f7f\uff0c\u8d77\u8f7f\u4e4b\u540e\uff0c\u7f13\u7f13 \u800c\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u56de\u8eab\u9053\uff1a\u201c\u7980\u57ce\u4e3b\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u628a\u7687\u5143\u6559\u4e3b\u548c\u79e6\u69d0\u62ff\u4e0b\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u5401\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u8981\u62ff\u4e0b\u4ed6\u4eec\u4e24\u4eba\u8c08\u4f55\u5bb9\u6613\uff0c\u8fd9\u4e24\u4eba\u7684\u6b66\u5b66\u9020\u8be3\uff0c\u5df2\u8fbe\u767b\u5cf0\u9020\u6781\u4e4b\u5883\uff0c \u65b9\u624d\u53ea\u662f\u81ea\u987e\u8eab\u4efd\uff0c\u4e0d\u613f\u51fa\u624b\u800c\u5df2\u3002\u201d \u3000\u3000\u4e8e\u662f\uff0c\u4e25\u5bd2\u7387\u9886\u5cb3\u5c0f\u98de\u3001\u4e25\u5982\u51b0\u3001\u5468\u6d77\u5c71\u7b49\u4eba\uff0c\u4ecd\u9000\u56de\u57ce\u4e2d\u3002 \u3000\u3000\u8fd9\u4e00\u6b21\u4f1a\u6218\uff0c\u53cc\u65b9\u7b97\u662f\u725b\u5200\u5c0f\u8bd5\uff0c\u5728\u7687\u5143\u6559\u4e3b\u767d\u83b7\u6765\u8bf4\uff0c\u53ea\u662f\u8bd5\u63a2\u6027\u8d28\u800c\u5df2\u3002 \u3000\u3000\u56de\u5230\u5927\u5385\uff0c\u4e25\u5bd2\u53ea\u628a\u5cb3\u5c0f\u98de\u4e00\u4eba\u7559\u4e0b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5fd6\u5ea6\u60c5\u52bf\uff0c\u63d0\u51fa\u4e86\u81ea\u5df1\u7684\u770b\u6cd5\uff0c\u4ed6\u8bf4\uff1a\u201c\u4ee5\u665a\u8f88\u9884\u6599\uff0c\u5c31\u5728\u6700\u8fd1\u51e0\u5929\uff0c\u7687\u5143\u6559\u4e3b \u5fc5\u5b9a\u8c03\u96c6\u5927\u519b\u548c\u5929\u8c37\u5185\u7cbe\u9510\uff0c\u518d\u5ea6\u524d\u6765\uff0c\u90a3\u65f6\u5c31\u7edd\u4e0d\u60a8\u4eca\u5929\u8fd9\u6837\u5bb9\u6613\u5bf9\u4ed8\uff0c\u57ce\u4e3b\u5fc5\u987b\u5c3d\u65e9\u5ba3 \u793a\u8fce\u654c\u5bf9\u7b56\uff0c\u665a\u8f88\u7b49\u4eba\u4e5f\u53ef\u65e9\u505a\u51c6\u5907\u3002\u201d \u3000\u3000\u4e25\u5bd2\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u672c\u57ce\u76ee\u524d\u53ef\u7528\u4e4b\u5c06\uff0c\u4ee5\u4f60\u548c\u4ee4\u5802\u6b66\u529f\u6700\u9ad8\uff0c\u81f3\u4e8e\u8001\u592b\uff0c\u975e\u5fc5\u8981\u65f6\uff0c\u4e0d \u5b9c\u4eb2\u81ea\u51fa\u9635\u3002\u201d \u3000\u3000\u4ee5\u4e25\u5bd2\u7684\u60f3\u6cd5\uff0c\u4e0d\u5916\u662f\u80fd\u518d\u62d6\u5ef6\u51e0\u5929\uff0c\u53cc\u65b9\u518d\u5c55\u5f00\u51b3\u5b9a\u6027\u7684\u4e00\u6218\u3002 \u3000\u3000\u539f\u56e0\u662f\u4ed6\u5728\u90a3\u665a\u82b1\u7389\u9e9f\u8d70\u540e\uff0c\u5df2\u628a\u5728\u7075\u5802\u4e2d\u670d\u836f\u5165\u68fa\u7684\u65b9\u5929\u94ce\u6551\u9192\uff0c\u53e6\u5916\uff0c\u4e5f\u653e\u51fa\u4e86\u7981 \u9522\u5728\u79d8\u6d1e\u91cc\u5341\u4f59\u5e74\u7684\u5173\u7433\uff0c\u76ee\u524d\u90fd\u5df2\u88ab\u8bf7\u5230\u5185\u5e9c\u4e00\u5904\u8de8\u9662\u91cc\u9759\u517b\u3002 \u3000\u3000\u65b9\u5929\u94ce\u548c\u5173\u7433\uff0c\u867d\u7136\u6b66\u529f\u5df2\u767b\u5cf0\u9020\u6781\uff0c\u4f46\u56e0\u4e00\u4e2a\u670d\u836f\u5165\u68fa\u4e09\u5e74\u591a\uff0c\u7cbe\u795e\u4f53\u529b\u77ed\u65f6\u5185\u90fd\u65e0 \u6cd5\u6062\u590d\uff0c\u4e00\u4e2a\u7981\u9522\u5728\u5c71\u6d1e\u79d8\u5ba4\u5341\u51e0\u5e74\uff0c\u81ea\u7136\u4e5f\u65e0\u6cd5\u7acb\u5373\u4e0a\u9635\u3002 \u3000\u3000\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u4e25\u5bd2\u5f53\u7136\u5e0c\u671b\u80fd\u591a\u62d6\u5ef6\u4e00\u4e9b\u65f6\u65e5\uff0c\u7b49\u5173\u7433\u548c\u65b9\u5929\u94ce\u6b66\u529f\u6062\u590d\u540e\u53cc\u65b9\u518d\u51b3 \u4e00\u6b7b\u6218\uff0c\u5230\u90a3\u65f6\u4fbf\u6709\u8db3\u591f\u7684\u5b9e\u529b\u548c\u7687\u5143\u6559\u5bf9\u6297\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u89c1\u4e25\u5bd2\u8bf4\u8fc7\u8bdd\u540e\uff0c\u4e45\u4e45\u7f04\u9ed8\u4e0d\u8bed\uff0c\u518d\u9053\uff1a\u201c\u665a\u8f88\u662f\u5426\u8be5\u56de\u5230\u4e94\u5173\u8bf7\u5bb6\u7236\u5373\u523b\u7387\u4f17 \u524d\u6765\u52a9\u9635\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u53c8\u6c89\u541f\u4e86\u8bb8\u4e45\u624d\u9053\uff1a\u201c\u6682\u65f6\u4e0d\u5fc5\u6765\uff0c\u4e0d\u8fc7\u8001\u592b\u5374\u5e0c\u671b\u4f60\u4eca\u665a\u56de\u4e94\u5173\u4e00\u8d9f\uff0c\u628a\u8fd9\u8fb9\u7684 \u60c5\u5f62\u544a\u8bc9\u4ee4\u5c0a\uff0c\u8981\u4ed6\u4e5f\u5148\u6709\u5fc3\u7406\u51c6\u5907\u3002\u201d \u3000\u3000\u201c\u57ce\u4e3b\u4e3a\u4ec0\u4e48\u4e0d\u8981\u5bb6\u7236\u5373\u523b\u8d76\u6765\u76f8\u52a9\uff1f\u201d \u3000\u3000\u201c\u7406\u7531\u5f88\u7b80\u5355\uff0c\u4ee4\u5c0a\u6682\u5728\u4e94\u5173\u4e0d\u52a8\uff0c\u8001\u592b\u81f3\u5c11\u53ef\u4ee5\u4ece\u4ed6\u90a3\u91cc\u5f97\u5230\u7687\u5143\u6559\u603b\u575b\u7684\u6d88\u606f\uff0c\u82e5 \u4ee4\u5c0a\u4e00\u6765\uff0c\u8eab\u4efd\u66b4\u9732\uff0c\u9664\u4e86\u6b7b\u5b88\u80b2\u5316\u57ce\uff0c\u5bf9\u4e8e\u5916\u9762\u7684\u6d88\u606f\uff0c\u5c82\u4e0d\u7b49\u4e8e\u5b8c\u5168\u5c01\u9501\u3002\u201d \u3000\u3000\u201c\u57ce\u4e3b\u987e\u8651\u5f97\u662f\uff0c\u665a\u8f88\u4eca\u665a\u5c31\u56de\u4e94\u5173\u5411\u5bb6\u7236\u7980\u62a5\u3002\u201d \u3000\u3000\u201c\u544a\u8bc9\u4ee4\u5c0a\uff0c\u5173\u7433\u548c\u65b9\u5929\u94ce\u4e24\u4f4d\u5927\u4fa0\u90fd\u5df2\u653e\u51fa\uff0c\u76ee\u524d\u6b63\u5728\u4f11\u517b\uff0c\u8981\u4ed6\u653e\u5fc3\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u5927\u4fa0\u90fd\u662f\u4ec0\u4e48\u4eba\uff0c\u4e3a\u4ec0\u4e48\u800d\u628a\u4ed6\u4eec\u7684\u6d88\u606f\u544a\u8bc9\u5bb6\u7236\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u4f60\u53ea\u8981\u544a\u8bc9\u4ee4\u5c0a\u4ee4\u5c0a\u5fc5\u4f1a\u5bf9\u4f60\u8bf4\u51fa\u4ed6\u4eec\u7684\u8eab\u4efd\u6765\u5386\u3002\u201d \u3000\u3000\u4e3a\u4e86\u5c3d\u901f\u8d76\u56de\u4e94\u5173\uff0c\u5cb3\u5c0f\u98de\u5728\u5f53\u65e5\u4e0b\u5348\u4fbf\u52a8\u8eab\u4e0a\u8def\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5df2\u77e5\u9053\u80b2\u5316\u57ce\u901a\u5f80\u4e94\u5173\uff0c\u6709\u4e09\u6761\u8def\u53ef\u884c\uff0c\u4ed6\u5f53\u7136\u662f\u627e\u79bb\u603b\u575b\u8fdc\u4e9b\u7684\u8def\u8d70\u3002 \u3000\u3000\u665a\u996d\u521a\u8fc7\u4e0d\u4e45\uff0c\u4ed6\u5df2\u56de\u5230\u4e94\u5173\u3002 \u3000\u3000\u7528\u8fc7\u9910\u540e\uff0c\u5373\u523b\u53bb\u89c1\u7236\u4eb2\u82b1\u7389\u9e9f\u3002 \u3000\u3000\u5f53\u82b1\u7389\u9e9f\u542c\u89c1\u5cb3\u5c0f\u98de\u7684\u62a5\u544a\u540e\uff0c\u4e5f\u6599\u5b9a\u7687\u5143\u6559\u4e3b\u5fc5\u5b9a\u5728\u6700\u8fd1\u51e0\u5929\u7387\u5927\u519b\u8fdb\u653b\u80b2\u5316\u57ce\uff0c\u800c \u80b2\u5316\u57ce\u76ee\u524d\u7684\u529b\u91cf\u4e5f\u7684\u786e\u96be\u4ee5\u76f8\u6297\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u82b1\u7389\u9e9f\u7075\u673a\u4e00\u52a8\uff0c\u4f3c\u4e4e\u6709\u4e86\u4e3b\u610f\uff1a\u201c\u65e2\u7136\u4e25\u57ce\u4e3b\u5e0c\u671b\u80fd\u62d6\u5ef6\u4e00\u4e9b\u65f6\u65e5\u518d\u548c\u7687\u5143\u6559 \u51b3\u6218\uff0c\u4e3a\u7236\u5012\u60f3\u51fa\u4e00\u4e2a\u529e\u6cd5\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8feb\u4e0d\u53ca\u5f85\u7684\u95ee\u9053\uff1a\u201c\u4e3a\u7236\u6709\u4ec0\u4e48\u826f\u7b56\uff0c\u5b69\u513f\u613f\u610f\u9a6c\u4e0a\u91c7\u53d6\u884c\u52a8\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u4e0d\u59a8\u5728\u7687\u5143\u6559\u5185\u90e8\u5236\u9020\u5185\u4e71\uff0c\u7687\u5143\u6559\u4e3b\u4e3a\u4e86\u6574\u987f\u5185\u90e8\uff0c\u8fd1\u671f\u5185\u5fc5\u5b9a\u987e\u4e0d\u5f97 \u518d\u5411\u80b2\u5316\u57ce\u8fdb\u519b\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u7236\u4eb2\u7684\u610f\u601d\uff0c\u662f\u5426\u60f3\u5148\u5728\u4e94\u5173\u5236\u9020\u53d8\u4e71\uff1f\u8b6c\u5982\u628a\u6c34\u5173\u7834\u574f\uff1f\u6216\u8005\u6697\u6740\u706b\u5173 \u7edf\u9886\u8427\u7476\uff1f\u2026\u2026\u201d \u3000\u3000\u82b1\u7389\u9e9f\u6447\u5934\u9053\uff1a\u201c\u4e94\u5173\u662f\u4e3a\u7236\u6240\u8f96\u4e0b\u7684\u5730\u9762\uff0c\u5982\u679c\u4e94\u5173\u53d1\u751f\u53d8\u4e71\uff0c\u7b2c\u4e00\u4e2a\u5931\u804c\u7684\u4fbf\u662f\u4e3a \u7236\uff0c\u600e\u53ef\u81ea\u5df1\u627e\u81ea\u5df1\u7684\u9ebb\u70e6\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u662f\u5426\u53ef\u4ee5\u628a\u76ee\u6807\u653e\u5728\u62db\u8d24\u9986\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u70b9\u5934\u9053\uff1a\u201c\u6211\u6b63\u662f\u8fd9\u610f\u601d\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u6700\u597d\u4eca\u665a\u5c31\u91c7\u53d6\u884c\u52a8\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u671b\u4e86\u671b\u5929\u8272\u9053\uff1a\u201c\u73b0\u5728\u884c\u52a8\u8fd8\u5acc\u65e9\u4e9b\uff0c\u7b49\u5929\u8272\u5168\u6697\u4e4b\u540e\u518d\u884c\u52a8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8d81\u8fd9\u65f6\u95ee\u9053\uff1a\u201c\u4e25\u57ce\u4e3b\u6240\u8bf4\u7684\u5173\u7433\u3001\u65b9\u5929\u94ce\u4e24\u4f4d\u5927\u4fa0\uff0c\u4ed6\u4eec\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u970e\u65f6\u6d6f\u6c14\u5e26\u4e9b\u6fc0\u52a8\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u4e3a\u7236\u7ed3\u62dc\u7684\u4e24\u4f4d\u4e49\u5144\uff0c\u5173\u7433\u6700\u957f\uff0c\u4e3a\u7236\u662f\u7ed3\u62dc \u4e2d\u7684\u8001\u4e09\uff0c\u8fd9\u4e24\u4eba\u7684\u6b66\u529f\uff0c\u90fd\u5df2\u767b\u5cf0\u9020\u6781\uff0c\u4e25\u57ce\u4e3b\u6240\u4ee5\u8981\u62d6\u5ef6\u4e00\u4e9b\u65f6\u65e5\u518d\u548c\u7687\u5143\u6559\u4e3b\u51b3\u6218\uff0c \u636e\u6211\u9884\u6599\uff0c\u4e0d\u5916\u662f\u5728\u7b49\u5f85\u4ed6\u4eec\u4e8c\u4eba\u6062\u590d\u529f\u529b\u3002\u201d \u3000\u3000\u6709\u4e86\u8fd9\u4e24\u4f4d\u9ad8\u4eba\u76f8\u52a9\uff0c\u5cb3\u5c0f\u98de\u987f\u611f\u5174\u594b\u4e0d\u5df2\uff0c\u987f\u4e86\u4e00\u987f\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u4f2f\u7236\u4e3a\u4ec0\u4e48\u4e5f\u4f1a\u88ab \u56da\u7981\u5728\u80b2\u5316\u57ce\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u590d\u53c8\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u5173\u4f2f\u7236\u5728\u5341\u4e8c\u5e74\u524d\uff0c\u4fbf\u88ab\u7687\u5143\u6559\u4e3b\u56da\u7981\u5728\u5929\u8c37\uff0c\u5176\u4e2d\u539f\u56e0\uff0c \u4e3a\u7236\u5230\u73b0\u5728\u8fd8\u5f04\u4e0d\u6e05\u695a\uff0c\u81f3\u4e8e\u4f60\u65b9\u4f2f\u7236\uff0c\u6b63\u662f\u4e09\u5e74\u524d\u548c\u6211\u7ea6\u597d\u8981\u5230\u72ec\u79c0\u5cf0\u63a5\u4f60\u7684\u90a3\u4eba\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u60c5\u4e0d\u81ea\u7981\u554a\u4e86\u58f0\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u4e5f\u5230\u4e86\u5929\u8c37\u5462\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u4ed6\u548c\u4e3a\u7236\u4e00\u6837\uff0c\u7adf\u7136\u4e5f\u662f\u4e3a\u4e86\u6551\u51fa\u4f60\u5173\u4f2f\u7236\uff0c\u5374\u56e0\u548c\u6211\u4e8b\u5148\u4e0d\u66fe\u8054\u7cfb\u597d\uff0c \u4ed6\u7adf\u5148\u901a\u8fc7\u4e94\u5173\u8fdb\u5165\u5929\u8c37\uff0c\u4ed6\u662f\u5728\u4e09\u5e74\u524d\u901a\u8fc7\u4e94\u5173\u6210\u7ee9\u6700\u4f18\u7684\u4eba\uff0c\u4ee5\u540e\u624d\u662f\u6211\u548c\u4f60\u6bcd\u4eb2\u4ee5\u53ca \u4f60\uff0c\u76ee\u524d\u8fd9\u56db\u4eba\u90fd\u662f\u80b2\u5316\u57ce\u7684\u529b\u91cf\uff0c\u53ea\u8981\u80fd\u62d6\u5ef6\u4e00\u4e9b\u65f6\u65e5\u8ba9\u4f60\u65b9\u4f2f\u7236\u548c\u5173\u4f2f\u7236\u7684\u6b66\u529f\u6062\u590d\uff0c \u6211\u4eec\u5c31\u6709\u8db3\u591f\u7684\u529b\u91cf\u548c\u7687\u5143\u6559\u4e3b\u5bf9\u6297\uff0c\u7eb5\u7136\u4e0d\u80fd\u80dc\u8fc7\u5979\uff0c\u81f3\u5c11\u4e5f\u5dee\u4e0d\u5230\u90a3\u91cc\u53bb\u3002\u201d \u3000\u3000\u7236\u5b50\u53c8\u8c08\u4e86\u4e00\u9635\uff0c\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u73b0\u5728\u65f6\u95f4\u5dee\u4e0d\u591a\uff0c\u6211\u4eec\u53ef\u4ee5\u884c\u52a8\u4e86\u3002\u201d \u3000\u3000\u201c\u7236\u4eb2\u51c6\u5907\u5e26\u90a3\u4e9b\u4eba\u53bb\uff1f\u201d \u3000\u3000\u201c\u5e72\u8fd9\u79cd\u4e8b\u60c5\uff0c\u4eba\u8d8a\u5c11\u8d8a\u597d\uff0c\u4f60\u6211\u7236\u5b50\u4e24\u4eba\u8db3\u591f\u4e86\u3002\u201d \u3000\u3000\u201c\u5173\u4e0a\u7684\u4e8b\uff0c\u8981\u4e0d\u8981\u4ea4\u4ee3\u8881\u5927\u54e5\u4e00\u58f0\uff1f\u201d \u3000\u3000\u201c\u4e0d\u5fc5\uff0c\u5982\u679c\u8fdb\u884c\u5f97\u5feb\uff0c\u4e0d\u5230\u4e09\u66f4\u5c31\u53ef\u4ee5\u8d76\u56de\u6765\uff0c\u4f60\u90a3\u4eba\u76ae\u9762\u5177\uff0c\u53ef\u5e26\u5728\u8eab\u8fb9\uff1f\u201d \u3000\u3000\u201c\u5b69\u513f\u4e00\u76f4\u968f\u8eab\u643a\u5e26\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u5c31\u9a6c\u4e0a\u968f\u7236\u8d70\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5929\u5df2\u5c3d\u9ed1\u3002 \u3000\u3000\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u4eec\u7236\u5b50\u5df2\u5230\u8fbe\u62db\u8d24\u9986\u603b\u9986\u540e\u65b9\u7684\u4e00\u5904\u571f\u5761\u540e\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u5728\u8fd9\u91cc\u66fe\u62c5\u4efb\u8fc7\u4e0d\u5c11\u65e5\u5b50\u7684\u526f\u9986\u4e3b\uff0c\u53ef\u8c13\u8f7b\u8f66\u719f\u8def\uff0c\u5373\u4f7f\u5728\u591c\u665a\u884c\u52a8\uff0c\u5bf9\u6240\u6709\u5730 \u5f62\u4e5f\u6beb\u4e0d\u964c\u751f\u3002 \u3000\u3000\u62db\u8d24\u9986\u56e0\u4e3a\u5e76\u65e0\u4efb\u4f55\u673a\u5bc6\u53ef\u8a00\uff0c\u53c8\u662f\u4f4d\u4e8e\u5929\u8c37\u6838\u5fc3\u5730\u5e26\uff0c\u56e0\u4e4b\u8b66\u6212\u5e76\u4e0d\u4e25\u5bc6\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u5728\u571f\u5761\u540e\u505c\u4e0b\u9053\uff1a\u201c\u628a\u9762\u5177\u5e26\u8d77\u6765\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6234\u597d\u540e\uff0c\u53ea\u9053\u7236\u4eb2\u662f\u8981\u4ee5\u672c\u6765\u9762\u76ee\u51fa\u73b0\uff0c\u4f46\u518d\u8f6c\u8eab\u770b\u65f6\uff0c\u82b1\u7389\u9e9f\u7adf\u7136\u4e5f\u6234\u4e0a\u4e86\u9762 \u5177\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4e24\u4eba\u5f00\u59cb\u884c\u52a8\u3002 \u3000\u3000\u4ed6\u4eec\u7531\u540e\u9762\u56f4\u5899\u7ffb\u5899\u8dc3\u5165\u603b\u9986\u540e\u9662\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u6761\u9ed1\u5f71\uff0c\u8fce\u9762\u800c\u6765\u3002 \u3000\u3000\u90a3\u9ed1\u5f71\u521a\u559d\u95ee\u51fa\u4e00\u4e2a\u201c\u8c01\u201d\uff0c\u82b1\u7389\u9e9f\u53f3\u624b\u4e00\u626c\uff0c\u53ea\u542c\u90a3\u9ed1\u5f71\u4e00\u58f0\u95f7\u54fc\uff0c\u7acb\u5373\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u539f\u6765\u82b1\u7389\u9e9f\u63b7\u51fa\u53bb\u7684\uff0c\u53ea\u662f\u4e00\u7c92\u77f3\u5b50\uff0c\u5c06\u90a3\u4eba\u524d\u80f8\u201c\u7487\u7391\u7a74\u201d\u51fb\u4e2d\uff0c\u9884\u8ba1\u81f3\u5c11\u8981\u4e00\u4e2a\u5bf9 \u65f6\u540e\u624d\u80fd\u9192\u8fc7\u6765\u3002 \u3000\u3000\u63a5\u7740\u82b1\u7389\u9e9f\u5df2\u6765\u5230\u4e00\u5904\u53a2\u623f\uff0c\u8f7b\u8f7b\u5728\u95e8\u4e0a\u6572\u4e86\u51e0\u4e0b\u3002 \u3000\u3000\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u5c4b\u5185\u53d1\u51fa\u559d\u95ee\u58f0\u3002 \u3000\u3000\u201c\u5434\u526f\u603b\u7ba1\uff0c\u662f\u6211\u3002\u201d \u3000\u3000\u201c\u539f\u6765\u662f\u8001\u526f\u9986\u4e3b\uff0c\u597d\u4e45\u4e0d\u89c1\u5566\uff0c\u4f60\u8001\u4eba\u5bb6\u600e\u4e48\u8fd9\u65f6\u5019\u5230\u9986\u91cc\u6765\uff01\u201d \u3000\u3000\u8fde\u5cb3\u5c0f\u98de\u90fd\u542c\u5f97\u51fa\uff0c\u8fd9\u4eba\u662f\u62db\u8d24\u9986\u526f\u603b\u7ba1\u5434\u6709\u6743\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u4f4e\u58f0\u9053\uff1a\u201c\u82b1\u67d0\u6709\u4ec0\u8981\u7d27\u7684\u4e8b\uff0c\u60f3\u95ee\u95ee\u4f60\u3002\u201d \u3000\u3000\u5ba4\u5185\u53d1\u51fa\u4e00\u9635\u549d\u549d\u4e4b\u58f0\uff0c\u60f3\u5fc5\u5728\u7a7f\u8863\u670d\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\uff0c\u623f\u95e8\u6253\u5f00\uff0c\u5434\u6709\u6743\u62ab\u7740\u8863\u670d\uff0c\u51fa\u73b0\u5728\u95e8\u5185\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u8de8\u8fdb\u95e8\u53bb\u9053\uff1a\u201c\u4e0d\u5fc5\u51fa\u53bb\uff0c\u5916\u9762\u98ce\u5927\uff0c\u514d\u5f97\u7740\u4e86\u51c9\uff0c\u6211\u8bf4\u8fc7\u8bdd\u5c31\u8d70\u3002\u201d \u3000\u3000\u5434\u6709\u6743\u5f80\u91cc\u8ba9\u9053\uff1a\u201c\u526f\u9986\u4e3b\u8bf7\u5750\uff0c\u5148\u559d\u676f\u8336\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\u3002\u201d \u3000\u3000\u201c\u526f\u9986\u571f\u7a76\u610f\u6709\u4ec0\u4e48\u4ea4\u4ee3\uff1f\u201d \u3000\u3000\u201c\u6211\u521a\u624d\u53bb\u89c1\u9986\u4e3b\uff0c\u9986\u4e3b\u597d\u50cf\u4e0d\u5728\u3002\u201d \u3000\u3000\u201c\u5728\uff0c\u8fd9\u65f6\u5019\u4ed6\u80fd\u5230\u90a3\u91cc\u53bb\uff0c\u4e00\u5b9a\u5728\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u97e9\u603b\u7ba1\u5462\uff1f\u201d \u3000\u3000\u201c\u4e5f\u5728\uff0c\u665a\u996d\u540e\u6211\u8fd8\u548c\u4ed6\u5728\u4e00\u8d77\uff0c\u201d \u3000\u3000\u201c\u6253\u6270\u4f60\u4e86\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u8bdd\u521a\u8bf4\u5b8c\uff0c\u957f\u5251\u9aa4\u7136\u51fa\u624b\uff0c\u76f4\u5411\u5434\u6709\u6743\u524d\u80f8\u523a\u53bb\u3002 \u3000\u3000\u5434\u6709\u6743\u8fde\u53eb\u90fd\u6ca1\u53eb\u51fa\uff0c\u4fbf\u5012\u5730\u6c14\u7edd\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u62bd\u51fa\u5251\uff0c\u518d\u628a\u95e8\u5e26\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u8fde\u4e32\u7ecf\u8fc7\uff0c\u5cb3\u5c0f\u98de\u867d\u5b88\u5728\u95e8\u5916\u628a\u98ce\uff0c\u5374\u90fd\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u5230\u8fd9\u65f6\u4ed6\u624d\u77e5\u9053\u7236\u4eb2\u662f\u73a9\u771f\u7684\uff0c\u5e76\u975e\u4ec5\u6b62\u9a9a\u6270\u4e00\u4e0b\u800c\u5df2\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u63a5\u7740\u6765\u5230\u603b\u7ba1\u97e9\u5fb7\u8d77\u623f\u95e8\u5916\uff0c\u53eb\u95e8\u5f00\u540e\uff0c\u4f9d\u6837\u753b\u846b\u82a6\uff0c\u4e5f\u65e0\u58f0\u65e0\u606f\u7684\u628a\u4eba\u5bb0\u6389\u3002 \u3000\u3000\u6700\u540e\u6765\u5230\u9986\u4e3b\u8427\u6e58\u623f\u95e8\u5916\u3002 \u3000\u3000\u8fd9\u6b21\u8f83\u9ebb\u70e6\uff0c\u56e0\u4e3a\u8427\u6e58\u623f\u95e8\u5916\u6b63\u6709\u4e00\u540d\u9ed1\u8863\u6c49\u5b50\u5728\u8d1f\u8d23\u8b66\u6212\u3002 \u3000\u3000\u90a3\u9ed1\u8863\u6c49\u5b50\u867d\u88ab\u82b1\u7389\u9e9f\u4e5f\u7528\u77f3\u5b50\u51fb\u4e2d\u7a74\u9053\u660f\u4e86\u8fc7\u53bb\uff0c\u4f46\u90a3\u4e00\u58f0\u53eb\u5374\u628a\u6b63\u5728\u7761\u68a6\u4e2d\u7684\u8427\u6e58 \u60ca\u9192\uff0c\u82b1\u7389\u9e9f\u56e0\u62c5\u5fc3\u88ab\u8427\u6e58\u8ba4\u51fa\u8eab\u5f62\uff0c\u82e5\u4e07\u4e00\u4ed6\u53eb\u51fa\u81ea\u5df1\u540d\u5b57\uff0c\u52bf\u5fc5\u60ca\u52a8\u5176\u4ed6\u7684\u4eba\u3002 \u3000\u3000\u4ed6\u6b64\u6765\u53ea\u60f3\u6740\u8fd9\u4e09\u4e2a\u4e3a\u9996\u800c\u53c8\u8be5\u6740\u7684\u4eba\uff0c\u4e0d\u5fcd\u6ee5\u6740\u65e0\u8f9c\uff0c\u56e0\u4e4b\uff0c\u9000\u95ea\uff0d\u4fa7\uff0c\u793a\u610f\u7531\u5cb3\u5c0f \u98de\u5904\u7406\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7acb\u5373\u6572\u4e86\u6572\u95e8\uff0c\u53eb\u9053\uff1a\u201c\u9986\u4e3b\uff0c\u4e0d\u597d\u5566\uff0c\u6709\u4eba\u628a\u5b88\u536b\u7684\u5f1f\u5144\u6740\u6b7b\u5728\u9662\u5b50\u91cc\uff01\u201d \u3000\u3000\u8427\u6e58\u4e00\u9762\u7a7f\u8863\uff0c\u4e00\u9762\u5927\u4e3a\u5403\u60ca\u7684\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u9986\u4e3b\u5feb\u8bf7\u51fa\u6765\u770b\uff01\u201d \u3000\u3000\u201c\u4f60\u662f\u505a\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u662f\u6765\u63a5\u73ed\u5b88\u536b\u7684\u3002\u201d \u3000\u3000\u5f53\u623f\u95e8\u4e00\u5f00\uff0c\u8427\u6e58\u521a\u9732\u51fa\u8eab\u5f62\uff0c\u5cb3\u5c0f\u98de\u7acb\u5373\u4e00\u5251\u5f53\u80f8\u523a\u53bb\uff0c\u76f4\u7a7f\u4e86\u4e2a\u524d\u540e\u7686\u901a\u3002 \u3000\u3000\u53ea\u542c\u82b1\u7389\u9e9f\u62db\u547c\u9053\uff1a\u201c\u5feb\u8d70\uff01\u201d \u3000\u3000\u7236\u5b50\u4e24\u4eba\uff0c\u4ecd\u7531\u539f\u8def\u8dc3\u5899\u800c\u51fa\uff0c\u8d70\u5230\u5927\u8def\u540e\uff0c\u624d\u628a\u9762\u5177\u5378\u4e0b\u3002 \u3000\u3000\u522b\u770b\u5cb3\u5c0f\u98de\u6b66\u827a\u9ad8\u5f3a\uff0c\u800c\u4e14\u81ea\u5165\u5929\u8c37\u540e\uff0c\u66fe\u4e0e\u4eba\u6253\u6597\u8fc7\u4e0d\u5c11\u6b21\uff0c\u4f46\u6740\u4eba\u5374\u8fd8\u662f\u7b2c\u4e00\u906d\uff0c \u5fc3\u91cc\u96be\u514d\u4e5f\u6709\u4e9b\u5fd0\u5fd1\u4e0d\u5b89\u3002 \u3000\u3000\u8def\u4e0a\uff0c\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5b69\u513f\u662f\u5426\u53ef\u7531\u8fd9\u91cc\u8fd4\u56de\u80b2\u5316\u57ce\u53bb\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd4\u56de\u80b2\u5316\u57ce\uff1f\u201d \u3000\u3000\u201c\u5b69\u513f\u62c5\u5fc3\u90a3\u8fb9\u6709\u4e8b\uff0c\u800c\u4e14\u7531\u8fd9\u91cc\u53bb\uff0c\u8def\u7a0b\u4e5f\u8fd1\u4e9b\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u50bb\u5b69\u5b50\uff0c\u4f60\u73b0\u5728\u6839\u672c\u5c31\u4e0d\u80fd\u5230\u80b2\u5316\u57ce\u53bb\uff0c\u5fc5\u987b\u6682\u65f6\u7559\u5728\u4e94\u5173\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u832b\u7136\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u62db\u8d24\u9986\u51fa\u4e86\u8fd9\u6837\u5927\u7684\u60ca\u4eba\u8840\u6848\uff0c\u7687\u5143\u6559\u603b\u575b\u600e\u80fd\u4e0d\u5230\u5404\u4e0b\u5c5e\u5355\u4f4d\u67e5\u6848\uff0c\u82e5 \u5230\u4e86\u4e94\u5173\u5c06\u6240\u6709\u4eba\u5458\u96c6\u5408\u70b9\u9a8c\uff0c\u4f60\u7684\u4eba\u4e0d\u5728\uff0c\u8ba9\u4e3a\u7236\u600e\u4e48\u56de\u590d\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fd9\u624d\u4e0d\u5f97\u4e0d\u6697\u670d\u7236\u4eb2\u8003\u8651\u7684\u5468\u5230\u3002 \u3000\u3000\u6b21\u65e5\u9192\u6765\uff0c\u82b1\u7389\u9e9f\u548c\u5cb3\u5c0f\u98de\u90fd\u4e00\u5982\u5e73\u5e38\uff0c\u4e1d\u6beb\u770b\u4e0d\u51fa\u4e24\u6837\u3002 \u3000\u3000\u4ed6\u4eec\u53ef\u4ee5\u6599\u5f97\u51fa\uff0c\u603b\u575b\u5373\u4fbf\u6709\u4eba\u6765\u67e5\u6848\uff0c\u4e5f\u4e0d\u4f1a\u5728\u5f97\u8fd9\u4e48\u5feb\u3002 \u3000\u3000\u65e9\u996d\u521a\u8fc7\uff0c\u5cb3\u5c0f\u98de\u6b63\u5728\u5927\u95e8\u524d\u9ed8\u5bdf\u52a8\u9759\uff0c\u5ffd\u89c1\u614c\u614c\u5f20\u5f20\u5954\u6765\u4e00\u4eba\uff0c\u8d6b\u7136\u662f\u62db\u8d24\u9986\u519b\u5e08\u516c \u5b59\u7389\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u89c9\u6697\u53eb\u4e00\u58f0\u4e0d\u5999\uff0c\u6628\u665a\u7684\u884c\u52a8\u867d\u7136\u8fdb\u884c\u5f97\u5341\u5206\u987a\u5229\uff0c\u4f46\u5f53\u65f6\u5374\u6ca1\u987e\u8651\u5230\u516c\u5b59\u7389 \u5fc5\u5c06\u53d7\u5230\u7275\u8fde\u3002 \u3000\u3000\u516c\u5b59\u7389\u4e00\u89c1\u5cb3\u5c0f\u98de\u4fbf\u6025\u6025\u95ee\u9053\uff1a\u201c\u82b1\u526f\u603b\u9547\u53ef\u5728\u5173\u4e0a\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u9762\u7b54\u5e94\uff0c\u4e00\u9762\u76f4\u63a5\u628a\u516c\u5b59\u7389\u5e26\u5230\u7236\u4eb2\u623f\u5185\uff0c\u7136\u540e\u81ea\u884c\u9000\u4e86\u51fa\u6765\u3002 \u3000\u3000\u516c\u5b59\u7389\u4e00\u8fdb\u95e8\u4fbf\u53cc\u80ef\u8dea\u5012\uff0c\u9762\u65e0\u4eba\u8272\u7684\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5feb\u6551\u6551\u5c0f\u5f1f\uff01\u201d \u3000\u3000\u516c\u5b59\u7389\u548c\u82b1\u7389\u9e9f\u56e0\u4e3a\u4e09\u5e74\u524d\u540c\u8fdb\u5929\u8c37\uff0c\u4e8c\u4eba\u610f\u6c14\u76f8\u6295\uff0c\u66fe\u7ed3\u62dc\u4e3a\u5f02\u59d3\u5144\u5f1f\uff0c\u5728\u62db\u8d24\u9986\u65f6 \u4e3a\u4e86\u4e0d\u4f7f\u4eba\u77e5\uff0c\u6240\u4ee5\u5728\u79f0\u547c\u4e0a\u4ecd\u4ee5\u804c\u79f0\u76f8\u79f0\uff0c\u6b64\u523b\u5355\u72ec\u89c1\u9762\uff0c\u624d\u6562\u8868\u9732\u7ed3\u62dc\u8eab\u4efd\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u5f53\u7136\u77e5\u9053\u4ed6\u7684\u6765\u610f\uff0c\u6697\u53eb\u4e00\u58f0\u201c\u60ed\u6127\u201d\uff0c\u8fde\u5fd9\u5c06\u516c\u5b59\u7389\u6276\u8d77\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u4e0d\u4fbf\u9a6c\u4e0a\u8bf4\u51fa\u5b9e\u8bdd\uff0c\u88c5\u51fa\u4e00\u8138\u832b\u7136\u795e\u8272\u95ee\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4f60\u8fd9\u7b97\u4f55\u610f\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u5750\u5b9a\u540e\uff0c\u5374\u4f9d\u7136\u60ca\u9b42\u4e0d\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8bf4\u6765\u4f60\u4e5f\u8bb8\u4e0d\u4fe1\uff0c\u62db\u8d24\u9986\u53d1\u751f\u4e86\u60ca\u4eba\u8840 \u6848\u2026\u2026\u201d \u3000\u3000\u82b1\u7389\u9e9f\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u83ab\u975e\u51fa\u4e86\u4eba\u547d\uff1f\u201d \u3000\u3000\u516c\u5b59\u7389\u9053\uff1a\u201c\u5c82\u6b62\u51fa\u4e86\u4eba\u547d\uff0c\u8427\u9986\u4e3b\u3001\u97e9\u603b\u7ba1\u3001\u5434\u526f\u603b\u7ba1\uff0c\u5168\u90e8\u88ab\u4eba\u6697\u6740\uff0c\u8fd9\u5728\u6574\u4e2a\u5929 \u8c37\u6765\u8bf4\uff0c\u4e5f\u662f\u7834\u5929\u8352\u7684\u5927\u4e8b\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u4e5f\u663e\u5f97\u8138\u8272\u5927\u53d8\uff0c\u9053\uff1a\u201c\u6709\u8fd9\u79cd\u4e8b\uff0c\u53ef\u67e5\u51fa\u662f\u4ec0\u4e48\u4eba\u4e0b\u7684\u6bd2\u624b\uff1f\u201d \u3000\u3000\u201c\u82e5\u67e5\u51fa\u662f\u4ec0\u4e48\u4eba\u4e0b\u624b\u7684\u5c31\u597d\u529e\u4e86\uff0c\u636e\u5de1\u591c\u548c\u5b88\u62a4\u7684\u5f1f\u5144\u8bf4\uff0c\u4ed6\u4eec\u8fde\u4eba\u5f71\u90fd\u8bbe\u770b\u6e05\u4fbf\u88ab \u6253\u660f\u5728\u5730\u3002\u201d \u3000\u3000\u201c\u4f60\u5230\u6211\u8fd9\u91cc\u6765\uff0c\u53c8\u662f\u4ec0\u4e48\u7528\u610f\uff1f\u201d \u3000\u3000\u201c\u5144\u5f1f\u9a97\u4ed6\u4eec\u8bf4\u662f\u5230\u603b\u575b\u62a5\u6848\uff0c\u4fbf\u6025\u6025\u5fd9\u5fd9\u8dd1\u5230\u4f60\u8fd9\u91cc\u6765\u3002\u201d \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u4e0d\u5230\u603b\u575b\u62a5\u6848\uff1f\u201d \u3000\u3000\u201c\u5927\u54e5\uff0c\u4f60\u662f\u806a\u660e\u4eba\uff0c\u62db\u8d24\u9986\u4e3b\u8981\u4eba\u7269\u4e2d\u4e09\u4eba\u88ab\u6740\uff0c\u72ec\u72ec\u5144\u5f1f\u6ca1\u4e8b\uff0c\u8c01\u4e5f\u4f1a\u8bef\u4f1a\u8fd9\u4e8b\u662f \u5144\u5f1f\u5e72\u7684\uff0c\u518d\u4e0d\u5c31\u662f\u548c\u51f6\u624b\u6709\u52fe\u7ed3\uff0c\u82e5\u5144\u5f1f\u53bb\u62a5\u6848\uff0c\u6b63\u662f\u81ea\u6295\u7f57\u7f51\uff0c\u6b7b\u8def\u4e00\u6761\uff01\u201d \u3000\u3000\u201c\u6240\u4ee5\u4f60\u624d\u6765\u627e\u6211\uff1f\u201d \u3000\u3000\u201c\u4e0d\u9519\uff0c\u5c0f\u5f1f\u6c42\u5927\u54e5\u8bbe\u6cd5\u5148\u628a\u6211\u85cf\u8d77\u6765\uff0c\u7b49\u6162\u6162\u518d\u60f3\u529e\u6cd5\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u7f04\u9ed8\u4e86\uff0c\u5bf9\u65b9\u8bf4\u7684\u662f\u5b9e\u60c5\uff0c\u800c\u4e8b\u60c5\u662f\u81ea\u5df1\u5e72\u7684\uff0c\u4ed6\u5c82\u80fd\u4e0d\u987e\u5ff5\u4e49\u5f1f\u7684\u751f\u6b7b\uff0c\u7eb5\u7136 \u6551\u4e86\u4e49\u5f1f\uff0c\u4ed6\u5185\u5fc3\u4ecd\u89c9\u5bf9\u516c\u5b59\u7389\u6b49\u759a\u3002 \u3000\u3000\u6b63\u597d\u4ed6\u7684\u623f\u95f4\u662f\u5957\u623f\uff0c\u5367\u5ba4\u540e\u9762\u4ecd\u6709\u4e00\u95f4\uff0c\u628a\u4eba\u85cf\u5728\u91cc\u9762\uff0c\u4e0d\u81f4\u88ab\u4eba\u53d1\u73b0\u3002 \u3000\u3000\u5f53\u4e0b\u4fbf\u628a\u516c\u5b59\u7389\u5e26\u5230\u540e\u9762\u623f\u95f4\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u4e0d\u7ecf\u4f20\u558a\uff0c\u8c01\u4e5f\u4e0d\u6562\u8fdb\u6765\uff0c\u4f60\u5c31\u6682\u65f6\u85cf\u5728\u8fd9 \u91cc\uff0c\u5f88\u5feb\u6211\u4fbf\u4f1a\u518d\u60f3\u529e\u6cd5\u3002\u201d \u3000\u3000\u4e00\u8fb9\u53c8\u53eb\u6765\u5cb3\u5c0f\u98de\uff0c\u8981\u4ed6\u4eb2\u81ea\u628a\u516c\u5b59\u7389\u7684\u65e9\u9910\u63d0\u5230\u540e\u9762\u623f\u95f4\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u9884\u8ba1\u603b\u575b\u6765\u67e5\u6848\u7684\u53ef\u80fd\u8981\u5230\u4e0b\u5348\uff0c\u56e0\u4e3a\u62a5\u6848\u7684\u516c\u5b59\u7389\u6839\u672c\u6ca1\u53bb\u62a5\uff0c\u603b\u575b\u6b64\u523b\u53ef\u80fd \u8fde\u6d88\u606f\u8fd8\u6ca1\u5f97\u5230\u3002 \u3000\u3000\u679c\u7136\uff0c\u76f4\u5230\u4e0b\u5348\u603b\u575b\u624d\u6765\u4e86\u4eba\u3002 \u3000\u3000\u6765\u4eba\u5c45\u7136\u662f\u603b\u62a4\u6cd5\u79e6\u69d0\u548c\u5927\u516c\u4e3b\u767d\u91d1\u51e4\u3002 \u3000\u3000\u79e6\u69d0\u79bb\u5f00\u603b\u575b\u4eb2\u81ea\u5230\u4e0b\u7ea7\u5355\u4f4d\u6765\uff0c\u6b63\u662f\u6781\u5c11\u89c1\u7684\u4e8b\uff0c\u53ef\u89c1\u8fd9\u662f\u4e00\u4ef6\u591a\u4e48\u4e25\u91cd\u7684\u4e8b\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u4eb2\u81ea\u7387\u5cb3\u5c0f\u98de\u3001\u8881\u5c0f\u9e64\u5230\u5927\u95e8\u5916\u606d\u8fce\u3002 \u3000\u3000\u628a\u79e6\u69d0\u548c\u5927\u516c\u4e3b\u8fce\u81f3\u5927\u5385\uff0c\u79e6\u69d0\u7acb\u5373\u5429\u5490\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u8d70\u5f00\uff0c\u53ea\u7559\u4e0b\u82b1\u7389\u9e9f\u4e00\u4eba\u3002 \u3000\u3000\u4ed6\u8fd9\u6837\u505a\uff0c\u4e0d\u5916\u662f\u8fd9\u4e8b\u53ea\u8ba9\u9996\u8111\u4eba\u7269\u77e5\u9053\uff0c\u5bf9\u5176\u4f59\u7684\u4eba\u4ecd\u5e94\u7136\u9501\u6d88\u606f\uff0c\u4ee5\u514d\u5f71\u54cd\u519b\u5fc3\u58eb \u6c14\u3002 \u3000\u3000\u79e6\u69d0\u548c\u5927\u516c\u4e3b\u90fd\u795e\u8272\u51dd\u91cd\uff0c\u5f53\u79e6\u69d0\u5ba3\u5e03\u8fc7\u62db\u8d24\u9986\u7684\u8840\u6848\u8fc7\u540e\uff0c\u82b1\u7389\u9e9f\u4e5f\u5f88\u5feb\u8138\u8272\u51dd\u91cd\u8d77 \u6765\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u6447\u5934\u4e00\u53f9\u9053\uff1a\u201c\u82b1\u67d0\u524d\u5728\u62db\u8d24\u9986\u5c06\u8fd1\u4e09\u5e74\uff0c\u4e00\u5411\u5e73\u5b89\u65e0\u4e8b\uff0c\u73b0\u5728\u600e\u4f1a\u53d1\u751f\u8fd9\u79cd\u4e8b \u60c5\uff0c\u5b9e\u5728\u4ee4\u4eba\u767e\u601d\u4e0d\u89e3\uff01\u201d \u3000\u3000\u79e6\u69d0\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u6b63\u56e0\u4e3a\u82b1\u526f\u603b\u9547\u5728\u62db\u8d24\u9986\u5f88\u4e45\uff0c\u5bf9\u90a3\u8fb9\u7684\u4eba\u4e8b\u548c\u73af\u5883\u5fc5\u5b9a\u4e86\u82e5\u6307\u638c\uff0c \u4e0d\u59a8\u60f3\u60f3\u770b\uff0c\u54ea\u4e9b\u4eba\u4f1a\u6709\u5acc\u7591\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u636e\u603b\u5ea7\u65b9\u624d\u6240\u8bf4\u7684\u60c5\u5f62\uff0c\u51f6\u5acc\u6b66\u529f\u4e00\u5b9a\u5f88\u9ad8\uff0c\u5426\u5219\u600e\u4f1a\u5728\u6781\u77ed\u65f6\u95f4\u5185\u8fde\u6740 \u4e09\u4eba\uff0c\u800c\u4e14\u4ed6\u4eec\u4e09\u4eba\u6b66\u529f\u4e5f\u90fd\u4e0d\u5f31\u3002\u201d \u3000\u3000\u79e6\u69d0\u9053\uff1a\u201c\u73b0\u5728\u5df2\u7ecf\u67e5\u5230\u4e00\u4eba\u5acc\u7591\u6700\u5927\uff0c\u504f\u504f\u8fd9\u4eba\u53c8\u5f03\u804c\u6f5c\u9003\u3002\u201d \u3000\u3000\u201c\u603b\u5ea7\u6240\u6307\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u516c\u5b59\u7389\u3002\u201d \u3000\u3000\u201c\u603b\u5ea7\u4e3a\u4ec0\u4e48\u4f1a\u60f3\u5230\u662f\u4ed6\uff1f\u201d \u3000\u3000\u201c\u5728\u62db\u8d24\u9986\u8bba\u8eab\u4efd\u5730\u4f4d\uff0c\u4ed6\u4ec5\u6b21\u4e8e\u8427\u9986\u4e3b\u3001\u8fde\u603b\u7ba1\u3001\u526f\u603b\u7ba1\u90fd\u88ab\u6740\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u80fd\u4e00\u4e2a\u4eba \u5e78\u514d\u3002\u201d \u3000\u3000\u201c\u603b\u5ea7\u53ef\u66fe\u95ee\u8fc7\u4ed6\u7684\u8bdd\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4eba\u5df2\u7ecf\u5f03\u804c\u6f5c\u9003\uff0c\u8d8a\u53d1\u53ef\u4ee5\u8bc1\u660e\u4e8b\u60c5\u662f\u4ed6\u5e72\u7684\u3002\u201d \u3000\u3000\u201c\u5929\u8c37\u5c31\u8fd9\u4e48\u5927\uff0c\u4ed6\u80fd\u9003\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u201c\u5929\u8c37\u867d\u7136\u4e0d\u5927\uff0c\u4f46\u5982\u679c\u85cf\u4e00\u4e2a\u4eba\uff0c\u53ea\u6015\u5343\u519b\u4e07\u9a6c\u4e5f\u641c\u7d22\u4e0d\u5230\u3002\u201d \u3000\u3000\u82e5\u79e6\u69d0\u5f53\u771f\u8ba4\u5b9a\u51f6\u624b\u662f\u516c\u5b59\u7389\uff0c\u53cd\u800c\u6b63\u4e2d\u82b1\u7389\u9e9f\u4e0b\u6000\uff0c\u56e0\u4e3a\u4ed6\u5df2\u51b3\u5b9a\u4eca\u665a\u628a\u516c\u5b59\u7389\u9001\u5230 \u80b2\u5316\u57ce\u53bb\uff0c\u90a3\u5c31\u4e0d\u4f1a\u518d\u6709\u81ea\u5df1\u7684\u4e8b\u4e86\u3002 \u3000\u3000\u5374\u542c\u79e6\u69d0\u9053\uff1a\u201c\u4ecd\uff1b\u8fd9\u91cc\u7684\u4eba\u53ef\u90fd\u5728\u5173\u4e0a\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u5168\u5728\u5173\u4e0a\uff0c\u201d \u3000\u3000\u79e6\u69d0\u9053\uff1a\u201c\u597d\uff0c\u73b0\u5728\u5c31\u628a\u6240\u6709\u7684\u4eba\u96c6\u5408\u8d77\u6765\uff01\u201d \u3000\u3000\u679c\u7136\u4e0d\u51fa\u82b1\u7389\u9e9f\u6240\u6599\uff0c\u79e6\u69d0\u662f\u8981\u6e05\u70b9\u4eba\u6570\u3002 \u3000\u3000\u96c6\u5408\u4e4b\u540e\uff0c\u5f1f\u5144\u4eec\u5171\u662f\u5341\u516b\u540d\uff0c\u53e6\u5916\u4fbf\u662f\u5cb3\u5c0f\u98de\u3001\u8881\u5c0f\u9e64\u3001\u4e01\u6d9b\u3001\u7518\u9716\u7b49\u56db\u4eba\u3002 \u3000\u3000\u79e6\u69d0\u62ff\u82b1\u540d\u518c\u4ed4\u7ec6\u67e5\u5bf9\u8fc7\u4ee5\u540e\uff0c\u5148\u8ba9\u5f1f\u5144\u4eec\u89e3\u6563\u56de\u53bb\uff0c\u53ea\u628a\u5cb3\u5c0f\u98de\u7b49\u56db\u4eba\u7559\u4e0b\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u5219\u5728\u4e00\u65c1\u966a\u68c0\u3002 \u3000\u3000\u5927\u516c\u4e3b\u767d\u91d1\u51e4\u5219\u81ea\u59cb\u81f3\u7ec8\u672a\u53d1\u4e00\u8bed\u3002 \u3000\u3000\u79e6\u69d0\u773c\u795e\u5982\u7535\uff0c\u7f13\u7f13\u5728\u5cb3\u5c0f\u98de\u7b49\u56db\u4eba\u8138\u4e0a\u8fde\u626b\u63a0\u8fc7\u597d\u51e0\u904d\uff0c\u624d\u4e0d\u52a8\u58f0\u8272\u7684\u95ee\u9053\uff1a\u201c\u4f60\u4eec \u6628\u665a\u6709\u54ea\u4e2a\u66fe\u51fa\u53bb\u5de1\u8fc7\u5173\uff1f\u201d \u3000\u3000\u56db\u4eba\u9762\u9762\u76f8\u89d1\u4e00\u9635\uff0c\u8c01\u90fd\u6ca1\u5f00\u53e3\u3002 \u3000\u3000\u79e6\u69d0\u518d\u95ee\u201c\u4f60\u4eec\u6700\u8fd1\u7ecf\u5e38\u51fa\u5916\u7684\u90fd\u6709\u54ea\u4e9b\u4eba\u5462\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\uff1a\u201c\u7980\u603b\u62a4\u6cd5\uff0c\u5c5e\u4e0b\u4eec\u90fd\u662f\u5de1\u5173\u4f7f\u8005\u6216\u526f\u4f7f\u8005\uff0c\u7ecf\u5e38\u51fa\u5916\u662f\u514d\u4e0d\u4e86\u7684\u3002\u201d \u3000\u3000\u79e6\u69d0\u9053\uff1a\u201c\u8fd9\u4e2a\u672c\u5ea7\u660e\u767d\uff0c\u672c\u5ea7\u95ee\u7684\u662f\u4f60\u4eec\u9664\u5de1\u5173\u4ee5\u5916\uff0c\u662f\u5426\u8fd8\u5230\u522b\u7684\u5730\u65b9\u53bb\u8fc7\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5c5e\u4e0b\u4eec\u9664\u5de1\u89c6\u4e94\u5173\uff0c\u5e76\u6ca1\u5230\u5176\u4ed6\u4efb\u4f55\u5730\u65b9\u53bb\u8fc7\u3002\u201d \u3000\u3000\u5ffd\u542c\u4e01\u6d9b\u9053\uff1a\u201c\u542f\u795f\u603b\u62a4\u6cd5\uff0c\u6211\u4eec\u5cb3\u4f7f\u8005\u8fd1\u6765\u7ecf\u5e38\u4e0d\u5728\u5173\u4e0a\uff0c\u4e5f\u8bb8\u66fe\u5230\u8fc7\u5176\u4ed6\u5730\u65b9\u3002\u201d \u3000\u3000\u6b64\u8bed\u4e00\u51fa\uff0c\u4e0d\u4f46\u5cb3\u5c0f\u98de\u9707\u60ca\uff0c\u8fde\u82b1\u7389\u9e9f\u548c\u8881\u5c0f\u9e64\u4e5f\u90fd\u5927\u611f\u6115\u7136\u3002 \u3000\u3000\u4e0d\u8fc7\u8881\u5c0f\u9e64\u5fc3\u91cc\u7a0d\u7a0d\u6709\u6570\uff0c\u90a3\u662f\u5cb3\u5c0f\u98de\u66fe\u6beb\u4e0d\u7559\u60c5\u7684\u6559\u8bad\u8fc7\u4e01\u6d9b\u3002 \u3000\u3000\u79e6\u69d0\u4e24\u9053\u70af\u70af\u76ee\u5149\uff0c\u7acb\u5373\u4e0d\u505c\u7684\u5728\u5cb3\u5c0f\u98de\u8eab\u4e0a\u6253\u8f6c\uff0c\u8bb8\u4e45\uff0c\u624d\u7f13\u7f13\u95ee\u9053\uff1a\u201c\u4f60\u5c31\u662f\u5cb3\u4f7f \u8005\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u79e6\u69d0\u9053\uff1a\u201c\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5c45\u7136\u5f53\u4e0a\u4e86\u5de1\u5173\u4f7f\u8005\uff0c\u771f\u4e0d\u5bb9\u6613\u3002\u201d \u3000\u3000\u5927\u516c\u4e3b\u767d\u91d1\u51e4\u5230\u8fd9\u65f6\u624d\u5f00\u4e86\u53e3\uff1a\u201c\u4ed6\u5e74\u7eaa\u867d\u5c0f\uff0c\u4f46\u5374\u662f\u8fd1\u4e09\u5e74\u6765\u901a\u8fc7\u4e94\u5173\u6210\u7ee9\u6700\u4f18\u7684\u4e00 \u4e2a\uff0c\u800c\u4e14\u4e0d\u4e45\u524d\u66fe\u8499\u6559\u4e3b\u53ec\u89c1\u8fc7\u3002\u201d \u3000\u3000\u79e6\u69d0\u563f\u563f\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u66f4\u96be\u5f97\u4e86\uff0c\u5cb3\u4f7f\u8005\uff0c\u4f60\u53ef\u662f\u6700\u8fd1\u7ecf\u5e38\u4e0d\u5728\u5173\u4e0a\uff1f\u90fd\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6001\u5ea6\u5341\u5206\u4ece\u5bb9\uff0c\u9053\uff1a\u201c\u7980\u603b\u62a4\u6cd5\uff0c\u5c5e\u4e0b\u8eab\u4e3a\u5de1\u5173\u4f7f\u8005\uff0c\u5f53\u7136\u662f\u7ecf\u5e38\u5230\u5404\u5904\u5de1\u5173\uff0c \u56e0\u4e3a\u7ecf\u5e38\u4e0d\u66fe\u548c\u4e01\u526f\u4f7f\u8005\u540c\u884c\uff0c\u6240\u4ee5\u4ed6\u4e5f\u5c31\u5f88\u5c11\u89c1\u5230\u5c5e\u4e0b\u5728\u5173\u4e0a\u3002\u201d \u3000\u3000\u79e6\u69d0\u671b\u5411\u4e01\u6d9b\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8bb2\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u53ef\u662f\u5c5e\u4e0b\u6709\u65f6\u8fde\u591c\u95f4\u4e5f\u89c1\u4e0d\u5230\u5cb3\u4f7f\u8005\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u7b49\u79e6\u69d0\u95ee\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u5c5e\u4e0b\u5de1\u5173\u56e0\u4e3a\u7ecf\u5e38\u90fd\u5f88\u665a\u56de\u6765\uff0c\u56de\u6765\u5c31\u4e0a\u5e8a\u7761\u4e86\uff0c\u4f55 \u5fc5\u8ba9\u4e01\u526f\u4f7f\u8005\u770b\u5230\u3002\u201d \u3000\u3000\u79e6\u69d0\u518d\u671b\u5411\u4e01\u6d9b\u3002 \u3000\u3000\u4e01\u6d9b\u518d\u9053\uff1a\u201c\u5c5e\u4e0b\u6709\u4ef6\u4e8b\u60c5\u5b9e\u5728\u4e0d\u61c2\uff0c\u5cb3\u4f7f\u8005\u66fe\u89c4\u5b9a\u5c5e\u4e0b\u5c3d\u91cf\u5c11\u51fa\u53bb\uff0c\u8981\u51fa\u53bb\u5fc5\u987b\u7531\u4ed6 \u6216\u8881\u4f7f\u8005\u5e26\u7740\u540c\u884c\uff0c\u8fd9\u53c8\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u7980\u603b\u62a4\u6cd5\uff0c\u5c5e\u4e0b\u8ba4\u4e3a\u4e94\u5173\u5404\u7edf\u9886\u5728\u672c\u6559\u90fd\u9887\u5177\u8d44\u671b\uff0c\u4ed6\u4eec\u4e24\u4f4d\u526f\u4f7f\u8005\u72ec\u81ea \u53bb\u5de1\u89c6\uff0c\u662f\u4ef6\u4e0d\u592a\u793c\u8c8c\u7684\u4e8b\uff0c\u6240\u4ee5\u624d\u5e0c\u671b\u4ed6\u4eec\u8981\u5de1\u5173\u6700\u597d\u968f\u540c\u8881\u4f7f\u8005\u548c\u5c5e\u4e0b\u4e00\u8d77\u53bb\uff0c\u96be\u9053\u6709 \u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u79e6\u69d0\u76ae\u7b11\u8089\u4e0d\u7b11\u9053\uff1a\u201c\u6709\u9053\u7406\uff0c\u4f60\u4eec\u53ef\u4ee5\u89e3\u6563\u4e86\uff0c\u672c\u5ea7\u8fd8\u8981\u548c\u82b1\u526f\u603b\u9547\u518d\u5355\u72ec\u8c08\u8c08\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u7b49\u56db\u4eba\u5411\u79e6\u69d0\u65bd\u793c\u800c\u9000\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u966a\u7740\u79e6\u69d0\u548c\u767d\u91d1\u51e4\u53c8\u56de\u5230\u5927\u5385\u3002 \u3000\u3000\u79e6\u69d0\u559d\u4e86\u676f\u8336\u9053\uff1a\u201c\u82b1\u526f\u603b\u9547\uff0c\u54b1\u4eec\u7687\u5143\u6559\u51fa\u8fd9\u6837\u7684\u60ca\u4eba\u5927\u4e8b\uff0c\u4f60\u53ef\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u5fc3\u5934\u4e00\u9707\u9053\uff1a\u201c\u5351\u804c\u6700\u76fc\u671b\u7684\uff0c\u662f\u901f\u901f\u7f09\u6355\u51f6\u624b\uff0c\u67e5\u660e\u771f\u76f8\uff0c\u7edd\u4e0d\u80fd\u8ba9\u8fd9\u4e8b\u6210\u4e3a \u60ac\u6848\u3002\u201d \u3000\u3000\u79e6\u69d0\u9894\u9996\u9053\uff1a\u201c\u5f53\u7136\uff0c\u672c\u5ea7\u6709\u628a\u63e1\u5f88\u5feb\u4fbf\u53ef\u7834\u6848\uff0c\u81f3\u4e8e\u4e94\u5173\u8be5\u505a\u4e9b\u4ec0\u4e48\uff0c\u60f3\u6765\u4f60\u5df2\u6709\u51b3 \u5b9a\u4e86\u5427\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u7531\u4e8e\u53d1\u751f\u4e86\u8fd9\u4ef6\u610f\u5916\u4e8b\u6545\uff0c\u60f3\u6765\u62db\u8d24\u9986\u77ed\u671f\u5185\u5fc5\u987b\u5173\u95ed\uff0c\u4ee5\u4fbf\u91cd\u65b0\u6574\u987f\uff0c \u56e0\u4e4b\uff0c\u4e94\u5173\u7684\u4efb\u52a1\uff0c\u6700\u597d\u4e5f\u6682\u65f6\u505c\u987f\u4e00\u4e0b\uff0c\u5426\u5219\u901a\u8fc7\u4e94\u5173\u7684\u4eba\u53c8\u5982\u4f55\u5b89\u7f6e\uff1f\u201d \u3000\u3000\u79e6\u69d0\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6559\u4e3b\u548c\u672c\u5ea7\u6b63\u662f\u987e\u8651\u5230\u8fd9\u4e00\u5c42\uff0c\u51b3\u5b9a\u6682\u65f6\u5c06\u8c37\u53e3\u5173\u95ed\uff0c\u5982\u6709\u901a\u8fc7\u4e94\u5173 \u5c1a\u672a\u5230\u8fbe\u62db\u8d24\u9986\u7684\uff0c\u5c31\u4e34\u65f6\u8ba9\u4ed6\u4eec\u4f4f\u5728\u5bb5\u5173\uff0c\u7136\u540e\u518d\u542c\u5019\u53d1\u843d\u3002\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u662f\u5426\u4ea4\u7531\u5351\u804c\u5904\u7f6e\uff1f\u201d \u3000\u3000\u201c\u4f60\u8eab\u4e3a\u4e94\u5173\u526f\u603b\u9547\uff0c\u5e76\u4ee3\u884c\u603b\u9547\u804c\u6743\uff0c\u5f53\u7136\u8be5\u7531\u4f60\u5904\u7f6e\uff0c\u4e0d\u8fc7\u62db\u8d24\u9986\u53d1\u751f\u7684\u4e8b\uff0c\u4ecd\u987b \u4fdd\u5bc6\uff0c\u4ee5\u514d\u6270\u519b\u5fc3\u58eb\u6c14\u3002\u201d \u3000\u3000\u201c\u662f\u5426\u8be5\u8ba9\u5404\u5173\u7edf\u9886\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u4e0d\u59a8\u7531\u4f60\u659f\u914c\uff0c\u4e0d\u8fc7\u5fc5\u987b\u544a\u8bc9\u706b\u5173\u8427\u7edf\u9886\uff0c\u56e0\u4e3a\u5979\u662f\u62db\u8d24\u9986\u8427\u9986\u4e3b\u7684\u80de\u59b9\uff0c\u4e0d\u80fd\u4e0d\u8ba9 \u5979\u77e5\u9053\u3002\u201d \u3000\u3000\u79e6\u69d0\u4ea4\u4ee3\u8fc7\u540e\uff0c\u4fbf\u548c\u5927\u516c\u4e3b\u767d\u91d1\u51e4\u5306\u5306\u9762\u53bb\u3002 \u3000\u3000\u79e6\u69d0\u8d70\u540e\uff0c\u82b1\u7389\u9e9f\u968f\u5373\u628a\u5cb3\u5c0f\u98de\u5524\u5230\u81ea\u5df1\u623f\u4e2d\u3002 \u3000\u3000\u4ed6\u5bf9\u4e01\u6d9b\u5728\u79e6\u69d0\u9762\u524d\u7684\u4e3e\u52a8\uff0c\u96be\u514d\u5927\u611f\u9a87\u5f02\u3002 \u3000\u3000\u7ecf\u5cb3\u5c0f\u98de\u8bf4\u660e\u540e\uff0c\u82b1\u7389\u9e9f\u624d\u6447\u5934\u4e00\u53f9\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u53ea\u56e0\u4e3a\u7236\u8fd1\u6765\u4e0d\u66fe\u6ce8\u610f\u4ed6\u548c\u7518\u9716 \u7684\u884c\u52a8\uff0c\u7adf\u7136\u4e0d\u77e5\u4ed6\u4eec\u7ecf\u5e38\u5728\u5bb5\u5173\u9b3c\u6df7\uff0c\u8fd9\u79cd\u4eba\u53ef\u8c13\u6210\u4e8b\u4e0d\u8db3\uff0c\u8d25\u4e8b\u6709\u4f59\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5176\u5b9e\u4ed6\u4eec\u597d\u8272\u6210\u6027\uff0c\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\u5927\u6076\uff0c\u6015\u7684\u662f\u4ed6\u4eec\u5df2\u88ab\u51e4\u5ae3\u7ea2\u5229\u7528\u800c\u4e0d \u81ea\u77e5\uff0c\u636e\u5b69\u513f\u9884\u6599\uff0c\u51e4\u5ae3\u7ea2\u5fc5\u5b9a\u5df2\u4ece\u4ed6 \u3000\u3000\u4eec\u5634\u91cc\uff0c\u5957\u53bb\u4e0d\u5c11\u8fd9\u8fb9\u7684\u6d88\u606f\uff0c\u8bf4\u4e0d\u5b9a\u66f4\u6709\u4ec0\u4e48\u628a\u67c4\u843d\u5728\u90a3\u5973\u4eba\u624b\u4e0a\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u5176\u5b9e\u4e01\u6d9b\u548c\u7518\u9716\u4e24\u4eba\u5fc3\u5730\u90fd\u4e0d\u574f\uff0c\u4e01\u6d9b\u4eca\u5929\u5ffd\u7136\u6709\u8fd9\u79cd\u4e3e\u52a8\uff0c\u4e0d\u5916\u662f\u56e0\u4f60 \u66fe\u6253\u8fc7\u4ed6\u4e00\u8033\u5149\u6000\u6068\u5728\u5fc3\u6240\u81f4\uff0c\u4f60\u4e0d\u59a8\u627e\u673a\u4f1a\u548c\u4ed6\u5316\u89e3\u5316\u89e3\uff0c\u514d\u5f97\u5acc\u9699\u8d8a\u79ef\u8d8a\u6df1\uff0c\u5c06\u6765\u53c8\u8981 \u51fa\u4e8b\u3002\u201d \u3000\u3000\u201c\u8fc7\u51e0\u5929\u82e5\u7236\u4eb2\u5230\u80b2\u5316\u57ce\uff0c\u662f\u5426\u8981\u628a\u4ed6\u4eec\u5e26\u53bb\uff1f\u201d \u3000\u3000\u201c\u8fd9\u8981\u89c1\u673a\u884c\u4e8b\uff0c\u80b2\u5316\u57ce\u90a3\u8fb9\uff0c\u591a\u4e00\u4eba\u603b\u662f\u591a\u4e00\u4efd\u529b\u91cf\uff0c\u867d\u7136\u4ed6\u4eec\u7684\u6b66\u529f\u5e76\u4e0d\u8db3\u4ee5\u62c5\u5f53 \u5927\u4efb\uff0c\u4f46\u4e5f\u603b\u7b97\u662f\u4e24\u540d\u9ad8\u624b\u3002\u201d \u3000\u3000\u201c\u7236\u4eb2\u662f\u5426\u9a6c\u4e0a\u5230\u5404\u5173\u53bb\uff1f\u201d \u3000\u3000\u201c\u6211\u9a6c\u4e0a\u5c31\u53bb\uff0c\u6211\u8d70\u540e\u7684\u8fd9\u6bb5\u65f6\u95f4\uff0c\u4f60\u548c\u8881\u5c0f\u9e64\u5343\u4e07\u8981\u8c28\u614e\u5c0f\u5fc3\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u51e0\u4e4e\u7528\u4e86\u4e00\u4e0b\u5348\u65f6\u95f4\u5230\u5404\u5173\u53bb\u3002 \u3000\u3000\u4ed6\u9996\u5148\u5230\u89c2\u5149\u96a7\u9053\uff0c\u4ee3\u6559\u4e3b\u4f20\u8c15\u8d1f\u8d23\u4eba\u738b\u68a6\u6765\uff0c\u5373\u523b\u5c06\u8c37\u53e3\u5c01\u95ed\uff0c\u4f46\u5374\u4e0d\u66fe\u8bf4\u51fa\u662f\u4ec0\u4e48 \u539f\u56e0\u3002 \u3000\u3000\u5230\u4e86\u706b\u5173\uff0c\u518d\u628a\u62db\u8d24\u9986\u4e3b\u6628\u591c\u88ab\u523a\u7684\u6d88\u606f\uff0c\u79d8\u5bc6\u544a\u77e5\u706b\u5173\u7edf\u9886\u8427\u7476\u3002 \u3000\u3000\u8427\u7476\u60ca\u95fb\u5669\u8017\uff0c\u6709\u5982\u7126\u96f7\u51fb\u9876\uff0c\u51e0\u4e4e\u6655\u53a5\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u7acb\u5373\u5411\u82b1\u7389\u9e9f\u8bf7\u4e86\u5047\uff0c\u4ee5\u4fbf\u5954\u5f80\u62db\u8d24\u9986\u5904\u7406\u8427\u6e58\u7684\u4e27\u4e8b\u3002 \u3000\u3000\u81f3\u4e8e\u6587\u5173\u3001\u6b66\u5173\u3001\u6c34\u5173\uff0c\u82b1\u7389\u9e9f\u53ea\u662f\u8d70\u4e86\u4e00\u4e0b\uff0c\u5c24\u5176\u6b66\u5173\uff0c\u7edf\u9886\u591a\u80cc\u5929\u7fc1\u5e9e\u821f\u6709\u5982\u571f\u7687 \u5e1d\uff0c\u8fde\u6559\u4e3b\u90fd\u8981\u793c\u8ba9\u4e09\u5206\uff0c\u82b1\u7389\u9e9f\u4e3a\u514d\u89e6\u9709\u5934\u53d7\u7a9d\u56ca\u6c14\uff0c\u6839\u672c\u4e0d\u66fe\u8fdb\u53bb\u548c\u4ed6\u4f1a\u9762\u3002 \u3000\u3000\u5176\u5b9e\u82b1\u7389\u9e9f\u5bf9\u591a\u80cc\u5929\u7fc1\u5e9e\u821f\uff0c\u6839\u672c\u4e5f\u7528\u4e0d\u7740\u8fc7\u95ee\uff0c\u5e9e\u821f\u96c4\u8e1e\u6b66\u5173\uff0c\u4fe8\u7136\u50cf\u4e00\u5904\u72ec\u7acb\u738b\u56fd\u3002 \u8fde\u603b\u62a4\u6cd5\u79e6\u69d0\u90fd\u4e0d\u6562\u89c6\u5bdf\u4ed6\uff0c\u53c8\u4f55\u51b5\u662f\u82b1\u7389\u9e9f\u3002 \u3000\u3000\u6700\u540e\u5230\u4e86\u5bb5\u5173\uff0c\u4e5f\u662f\u5c0f\u5750\u4e00\u4f1a\u513f\u4fbf\u8d70\uff0c\u56e0\u4e3a\u4ed6\u53d7\u4e0d\u4e86\u51e4\u5ae3\u7ea2\u7684\u7ea0\u7f20\u3002 \u3000\u3000\u5f53\u665a\uff0c\u82b1\u7389\u9e9f\u4fbf\u547d\u5cb3\u5c0f\u98de\u628a\u516c\u5b59\u7389\u9001\u5f80\u80b2\u5316\u57df\u3002 \u3000\u3000\u4ed6\u8eab\u8fb9\u85cf\u6709\u4e94\u679a\u80b2\u5316\u57ce\u7684\u901a\u884c\u4ee4\u724c\uff0c\u81ea\u7136\u4e5f\u7ed9\u4e86\u516c\u5b59\u7389\u4e00\u679a\u3002 \u3000\u3000\u516c\u5b59\u7389\u548c\u5cb3\u5c0f\u98de\u90fd\u5728\u51fa\u53d1\u540e\u6234\u4e0a\u4e86\u4eba\u76ae\u9762\u5177\u3002 \u3000\u3000\u4e34\u884c\u524d\u82b1\u7389\u9e9f\u5631\u5cb3\u5c0f\u98de\u65e9\u53bb\u65e9\u56de\u3002 \u3000\u3000\u8def\u4e0a\uff0c\u5cb3\u5c0f\u98de\u4e0d\u4fbf\u518d\u9690\u7792\uff0c\u4fbf\u628a\u8fd1\u6765\u53d1\u751f\u7684\u4e8b\u60c5\u4ee5\u53ca\u6628\u591c\u523a\u6740\u8427\u6e58\u7684\u7ecf\u8fc7\u5168\u544a\u8bc9\u4e86\u516c\u5b59 \u7389\u3002 \u3000\u3000\u5230\u8fbe\u80b2\u5316\u57ce\u5411\u4e25\u5bd2\u7980\u62a5\u8fc7\u4e00\u5207\u540e\uff0c\u4e25\u5bd2\u5185\u5fc3\u7684\u6b23\u559c\u81ea\u4e0d\u5f85\u8a00\u3002 \u3000\u3000\u4e25\u5bd2\u9884\u6599\u7687\u5143\u6559\u5185\u51fa\u4e86\u8fd9\u6837\u7684\u5927\u4e8b\uff0c\u77ed\u671f\u5185\u7edd\u4e0d\u81f4\u5174\u5175\u653b\u6253\u80b2\u5316\u57ce\uff0c\u4fbf\u547d\u5cb3\u5c0f\u98de\u901f\u53cd\u56de \u4e94\u5173\u3002 \u3000\u3000\u540c\u65f6\uff0c\u4ed6\u89c9\u5f97\u82b1\u7389\u9e9f\u7559\u5728\u4e94\u5173\uff0c\u9664\u4e86\u53ef\u4ee5\u7ee7\u7eed\u6270\u4e71\u7687\u5143\u6559\uff0c\u66f4\u53ef\u91cc\u5e94\u5916\u5408\uff0c\u4e5f\u8981\u5cb3\u5c0f\u98de \u8f6c\u544a\u82b1\u7389\u9e9f\uff0c\u6682\u65f6\u4e0d\u5fc5\u64a4\u5230\u80b2\u5316\u57ce\u6765\u3002 \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u4e00\u5ea6\u6625\u98ce \u3000\u3000\u4e01\u6d9b\u5728\u79e6\u69d0\u89c6\u5bdf\u867d\u7136\u6ca1\u544a\u5012\u5cb3\u5c0f\u98de\uff0c\u5185\u5fc3\u603b\u7b97\u7a0d\u7a0d\u51fa\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u4ed6\u60f3\u5230\u5f53\u521d\u5cb3\u5c0f\u98de\u8fdb\u5165\u5929\u8c37\u65f6\uff0c\u662f\u81ea\u5df1\u5e2e\u5fd9\u5e26\u8def\uff0c\u53c8\u56e0\u800c\u4f7f\u4ed6\u4eec\u7236\u5b50\u76f8\u4f1a\uff0c\u5cb3\u5c0f\u98de\u4e0d\u4f46 \u4e0d\u611f\u6fc0\uff0c\u53cd\u800c\u6012\u63b4\u81ea\u5df1\u7684\u8033\u5149\uff0c\u60f3\u6253\u53c8\u6253\u4e0d\u8fc7\u4eba\u5bb6\uff0c\u8fd9\u53e3\u95f7\u6c14\uff0c\u6559\u4ed6\u5982\u4f55\u5fcd\u5f97\u4e0b\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u628a\u82b1\u7389\u9e9f\u5f53\u5e74\u66fe\u6551\u8fc7\u4ed6\u4e00\u547d\u4ee5\u53ca\u4e94\u4e49\u6d4b\u8bd5\u65f6\u5cb3\u5c0f\u98de\u5bf9\u4ed6\u7684\u8bf8\u591a\u534f\u52a9\uff0c\u5b8c\u5168\u629b\u8bf8\u8111 \u540e\u3002 \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u4e5f\u5e76\u4e0d\u5b8c\u5168\u662f\u4ed6\u7684\u9519\uff0c\u5cb3\u5c0f\u98de\u7529\u4ed6\u90a3\u4e00\u8bb0\u8033\u5149\uff0c\u4e5f\u7684\u786e\u6709\u4e9b\u8fc7\u5206\u3002 \u3000\u3000\u5728\u5f53\u65e5\u665a\u95f4\u4e00\u66f4\u5de6\u53f3\uff0c\u4ed6\u89c1\u526f\u603b\u9547\u82b1\u7389\u9e9f\u5df2\u7531\u5916\u9762\u56de\u6765\u8fdb\u623f\u5b89\u6b47\uff0c\u5cb3\u5c0f\u98de\u4f3c\u4e4e\u53c8\u4e0d\u5728\u5173 \u4e0a\uff0c\u4fbf\u8fde\u7518\u9716\u4e5f\u6ca1\u544a\u8bc9\uff0c\u4e00\u4e2a\u4eba\u5c31\u6084\u6084\u6e9c\u5411\u5bb5\u5173\u3002 \u3000\u3000\u4ed6\u81ea\u88ab\u5cb3\u5c0f\u98de\u7529\u8fc7\u8033\u5149\u540e\uff0c\u5df2\u6709\u597d\u51e0\u5929\u4e0d\u66fe\u5916\u51fa\u3002 \u3000\u3000\u4e0d\u5230\u522b\u5904\u53bb\u65e0\u6240\u6e2d\uff0c\u4e0d\u5230\u5bb5\u5173\u5b9e\u5728\u96be\u7981\u5fc3\u733f\u610f\u9a6c\uff0c\u56e0\u4e3a\u4ed6\u5df2\u548c\u7ea2\u674f\u6253\u5f97\u706b\u70ed\uff0c\u88ab\u7ea2\u674f\u8ff7 \u660f\u4e86\u5934\u3002 \u3000\u3000\u7531\u4e94\u5173\u603b\u9547\u6240\u5728\u5730\u5230\u5bb5\u5173\uff0c\u8fd1\u5728\u54ab\u5c3a\uff0c\u4ed6\u6765\u5230\u5bb5\u5173\u95e8\u5916\u7ea6\u767e\u4f59\u6b65\u5904\uff0c\u6700\u521d\u8fd8\u8eb2\u5728\u6697\u5904\u72b9 \u8c6b\uff0c\u4e45\u4e45\u4e0d\u6562\u8fdb\u53bb\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u62c5\u5fc3\u5cb3\u5c0f\u98de\u4e5f\u5728\u91cc\u9762\uff0c\u82e5\u649e\u4e86\u4e0a\u4e86\u53ea\u6015\u53c8\u8981\u5403\u4e8f\u3002 \u3000\u3000\u539f\u6765\u4ed6\u7adf\u7591\u5fc3\u5cb3\u5c0f\u98de\u4e5f\u5e38\u5230\u5bb5\u5173\u548c\u5973\u5175\u4eec\u53ae\u6df7\uff0c\u4e3a\u9632\u4ed6\u548c\u7518\u9716\u53d1\u89c9\uff0c\u6240\u4ee5\u624d\u7981\u6b62\u4ed6\u548c\u7518 \u9716\u518d\u5230\u5bb5\u5173\u53bb\u3002 \u3000\u3000\u4ed6\u672c\u6765\u90a3\u5929\u88ab\u6253\u540e\u8981\u5411\u82b1\u7389\u9e9f\u544a\u72b6\uff0c\u53c8\u60f3\u5230\u4eba\u5bb6\u662f\u7236\u5b50\u5173\u7cfb\uff0c\u758f\u4e0d\u95f4\u4eb2\uff0c\u544a\u72b6\u5f88\u53ef\u80fd\u53cd \u800c\u5f15\u8d77\u82b1\u7389\u9e9f\u7684\u4e0d\u60a6\uff0c\u6240\u4ee5\u624d\u8d81\u4eca\u5929\u603b\u62a4\u6cd5\u79e6\u69d0\u6765\u65f6\u594f\u4e0a\u4e00\u672c\u3002 \u3000\u3000\u5176\u5b9e\u8fd9\u65f6\u6b63\u662f\u5cb3\u5c0f\u98de\u966a\u540c\u516c\u5b59\u7389\u5077\u5077\u53bb\u4e86\u80b2\u5316\u57ce\uff0c\u6240\u4ee5\u624d\u4e0d\u5728\u5173\u4e0a\u3002 \u3000\u3000\u4e01\u6d9b\u5728\u5916\u9762\u7b49\u4e86\u4e00\u4f1a\uff0c\u770b\u4e0d\u51fa\u4ec0\u4e48\u52a8\u9759\uff0c\u5b9e\u5728\u5fcd\u4e0d\u4f4f\uff0c\u7ec8\u4e8e\u731b\u7740\u80c6\u5b50\u8fdb\u4e86\u5bb5\u5173\u5927\u95e8\u3002 \u3000\u3000\u5b88\u95e8\u4eba\u65e9\u77e5\u4ed6\u662f\u4e94\u5173\u526f\u4f7f\u8005\uff0c\u800c\u4e14\u53c8\u5e38\u5e38\u6765\u5f80\u8fc7\uff0c\u5f53\u7136\u4e5f\u5e76\u4e0d\u76d8\u67e5\u3002 \u3000\u3000\u4e01\u6d9b\u8fdb\u53bb\u540e\uff0c\u9996\u5148\u9047\u4e0a\u4e86\u78a7\u6843\u3002 \u3000\u3000\u78a7\u6843\u867d\u662f\u7518\u9716\u7684\u76f8\u597d\uff0c\u548c\u4e01\u6d9b\u4e5f\u719f\u6089\u5f97\u5f88\uff0c\u4e00\u89c1\u9762\u5c31\u95ee\u9053\uff1a \u3000\u3000\u201c\u4ec0\u4e48\u98ce\u628a\u4e01\u526f\u4f7f\u8005\u5439\u4e86\u6765\uff0c\u8001\u7518\u5462\uff1f\u4e3a\u4ec0\u4e48\u597d\u51e0\u5929\u4e0d\u89c1\u4f60\u4eec\u7684\u9b3c\u5f71\u5b50\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u5658\u5634\u4e00\u7b11\uff0c\u6545\u610f\u9a97\u5979\u9053\uff1a\u201c\u8001\u7518\u90a3\u5c0f\u5b50\u75c5\u4e86\uff0c\u800c\u4e14\u75c5\u5f97\u8fd8\u4e0d\u8f7b\u201d \u3000\u3000\u78a7\u6843\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u8c01\u9a97\u4f60\u5c31\u662f\u72d7\u718a\u3002\u201d \u3000\u3000\u78a7\u6843\u62bf\u4e86\u62bf\u5634\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u75c5\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u7b11\u9053\uff1a\u201c\u4ed6\u75c5\u4ed6\u7684\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e5f\u8981\u75c5\uff1f\u201d \u3000\u3000\u201c\u4f60\u4eec\u4e0d\u662f\u540c\u7a7f\u4e00\u6761\u88e4\u5b50\u4e48\uff1f\u201d \u3000\u3000\u201c\u8ddf\u4ed6\u7a7f\u4e00\u6761\u88e4\u5b50\u7684\u662f\u4f60\uff0c\u8ddf\u6211\u7a7f\u4e00\u6761\u88e4\u5b50\u7684\u662f\u7ea2\u674f\uff0c\u5bf9\u5566\uff0c\u7ea2\u674f\u5462\uff1f\u5feb\u628a\u5979\u627e\u6765\uff01\u201d \u3000\u3000\u201c\u7ea2\u674f\u75c5\u4e86\uff01\u201d \u3000\u3000\u4e01\u6d9b\u6123\u4e86\u6123\u9053\uff1a\u201c\u4f60\u9a97\u4eba\u3002\u201d \u3000\u3000\u78a7\u6843\u9053\uff1a\u201c\u6211\u53ea\u9a97\u72d7\u718a\uff0c\u4e0d\u9a97\u4eba\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5df2\u662f\u4e00\u66f4\u540e\uff0c\u9664\u4e86\u5927\u95e8\u53e3\u6709\u4e2a\u5b88\u95e8\u7684\uff0c\u56db\u4e0b\u6839\u672c\u6ca1\u6709\u5176\u4ed6\u7684\u4eba\uff0c\u800c\u4e14\u4e24\u4eba\u53c8\u6b63\u5728\u5047 \u5c71\u65c1\uff0c\u4e01\u6d9b\u63a2\u624b\u6293\u4f4f\u78a7\u6843\u9999\u80a9\uff0c\u7b11\u54e7\u54e7\u7684\u9053\uff1a\u201c\u5c0f\u5a4a\u5b50\uff0c\u4f60\u6562\u9a82\u54b1\u662f\u72d7\u718a\uff0c\u5982\u679c\u4e0d\u628a\u7ea2\u674f\u627e \u6765\uff0c\u8001\u5b50\u5c31\u8981\u4f60\u4ee3\u66ff\u3002\u201d \u3000\u3000\u78a7\u6843\u626d\u52a8\u7740\u8eab\u5b50\u62e8\u5f00\u4e01\u6d9b\u7684\u624b\u9053\uff1a\u201c\u53bb\u4f60\u7684\uff0c\u5c0f\u5fc3\u8ba9\u8001\u7518\u77e5\u9053\u4e86\u9976\u4e0d\u4e86\u4f60\uff0c\u4f60\u53ef\u77e5\u9053\u670b \u53cb\u59bb\u4e0d\u53ef\u6b3a\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u563f\u563f\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u90a3\u8138\u76ae\u6bd4\u54b1\u66f4\u539a\uff0c\u5728\u4e00\u5757\u75db\u5feb\u8fc7\u51e0\u6b21\u4fbf\u627f\u8ba4\u662f\u4ed6\u7684\u8001\u5a46\uff0c\u4f60\u65e2 \u7136\u80fd\u505a\u4ed6\u7684\u8001\u5a46\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u505a\u6211\u7684\u8001\u5a46\uff1f\u201d \u3000\u3000\u201c\u4e5f\u4e0d\u64a4\u6ce1\u72d7\u5c3f\u7167\u7167\uff0c\u5c31\u51ed\u4f60\u90a3\u5fb7\u6027\uff0c\u9664\u4e86\u7ea2\u674f\u559c\u6b22\uff0c\u53ea\u6015\u732a\u516b\u6212\u7684\u59b9\u59b9\u4e5f\u770b\u4e0d\u4e0a\u773c\uff01\u201d \u3000\u3000\u201c\u90a3\u4f60\u5c31\u8be5\u5e2e\u6211\u53bb\u53eb\u7ea2\u674f\u3002\u201d \u3000\u3000\u201c\u6211\u4e0d\u662f\u8bf4\u8fc7\u7ea2\u674f\u75c5\u4e86\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u4fe1\uff01\u201d \u3000\u3000\u201c\u4fe1\u4e0d\u4fe1\u7531\u4f60\uff0c\u6211\u8d70\u5566\uff01\u201d \u3000\u3000\u78a7\u6843\u521a\u8d70\u51fa\u53bb\u4e24\u6b65\uff0c\u5ffd\u542c\u5047\u5c71\u540e\u4f20\u6765\u4e00\u4e2a\u5a07\u6ef4\u6ef4\u800c\u53c8\u9887\u6709\u5a01\u4eea\u7684\u58f0\u97f3\u9053\uff1a\u201c\u78a7\u6843\uff0c\u4f60\u5728 \u8ddf\u8c01\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u78a7\u6843\u987f\u65f6\u6253\u4e86\u4e2a\u51b7\u98a4\uff0c\u8c28\u58f0\u5e94\u9053\uff1a\u201c\u7980\u7edf\u9886\uff0c\u662f\u4e94\u5173\u4e0a\u7684\u4e01\u526f\u4f7f\u8005\uff01\u201d \u3000\u3000\u539f\u6765\u95ee\u8bdd\u7684\u8d6b\u7136\u662f\u5bb5\u5173\u7edf\u9886\u51e4\u5ae3\u7ea2\u3002 \u3000\u3000\u53ea\u542c\u51e4\u5ae3\u7ea2\u518d\u95ee\u9053\uff1a\u201c\u53ef\u662f\u90a3\u4e2a\u53eb\u4e01\u6d9b\u7684\uff1f\u201d \u3000\u3000\u78a7\u6843\u9053\uff1a\u201c\u5c31\u662f\u4ed6\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u8bed\u6c14\u51b0\u51b7\u7684\u9053\uff1a\u201c\u4ed6\u8fd9\u65f6\u5019\u6765\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u78a7\u6843\u987f\u4e86\u987f\u9053\uff1a\u201c\u4ed6\u8bf4\u662f\u6765\u627e\u7ea2\u674f\u7684\u3002\u201d \u3000\u3000\u201c\u627e\u7ea2\u674f\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u5a62\u5b50\u4e0d\u6e05\u695a\uff0c\u8fd8\u6ca1\u95ee\u4ed6\uff0c\u4ed6\u4e5f\u6ca1\u8bb2\u3002\u201d \u3000\u3000\u201c\u53eb\u4ed6\u5230\u6211\u8fd9\u91cc\u6765\uff0c\u6211\u8981\u597d\u597d\u95ee\u95ee\u4ed6\uff01\u201d \u3000\u3000\u78a7\u6843\u4f4e\u58f0\u9053\uff1a\u201c\u59d3\u4e01\u7684\uff0c\u7edf\u9886\u7684\u8bdd\u4f60\u90fd\u542c\u89c1\u4e86\uff0c\u8fc7\u53bb\u627e\u5979\u5012\u9709\u5427\uff0c\u6211\u8d70\u5566\uff01\u201d \u3000\u3000\u4e01\u6d9b\u867d\u662f\u4e2a\u6d51\u4eba\uff0c\u6b64\u523b\u5374\u5b9e\u5728\u7740\u4e86\u614c\uff0c\u4ed6\u65e9\u77e5\u9053\u51e4\u5ae3\u7ea2\u4e0d\u662f\u597d\u60f9\u7684\uff0c\u800c\u4e14\u81ea\u5df1\u53c8\u884c\u5f84\u4e0d \u6b63\uff0c\u54ea\u80fd\u4e0d\u4f5c\u8d3c\u5fc3\u865a\uff0c\u82e5\u5bf9\u65b9\u5728\u526f\u603b\u9547\u82b1\u7389\u9e9f\u9762\u524d\u544a\u4ed6\u4e00\u72b6\uff0c\u4ed6\u5c31\u975e\u5012\u5927\u9709\u4e0d\u53ef\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u60f3\u9003\u4e5f\u9003\u4e0d\u6389\uff0c\u53ea\u6709\u6218\u6218\u5162\u5162\u7684\u7ed5\u8fc7\u5047\u5c71\uff0c\u6765\u5230\u51e4\u5ae3\u7ea2\u9762\u524d\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u7eb9\u98ce\u4e0d\u52a8\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u53ea\u6709\u5fae\u98ce\u5439\u8d77\u5979\u7684\u88d9\u89d2\u98d8\u52a8\uff0c\u867d\u7136\u76f8\u9694\u4e03\u516b\u5c3a\u8fdc\uff0c\u5374\u5df2\u95fb \u5230\u90a3\u606f\u606f\u9999\u98ce\u3002 \u3000\u3000\u4e01\u6d9b\u65e9\u5c31\u5782\u6d8e\u51e4\u5ae3\u7ea2\u7684\u7f8e\u8272\uff0c\u5927\u6709\u201c\u671d\u5230\u624b\uff0c\u5915\u6b7b\u53ef\u77e3\u201d\u4e4b\u6982\uff0c\u4f46\u6b64\u523b\u4ed6\u5374\u4e0d\u6562\u7a0d\u5b58\u539a \u5206\u4e4b\u60f3\uff0c\u53ea\u5e0c\u671b\u5bf9\u65b9\u80fd\u9ad8\u62ac\u8d35\u624b\uff0c\u653e\u81ea\u5df1\u4e00\u9a6c\uff0c\u5219\u4e8e\u613f\u5df2\u8db3\u3002 \u3000\u3000\u4ed6\u6765\u5230\u8ddf\u524d\uff0c\u7ad9\u4f4f\u811a\uff0c\u62b1\u62f3\u6df1\u6df1\u4e00\u793c\u9053\uff1a\u201c\u4e01\u6d9b\u62dc\u89c1\u51e4\u7edf\u9886\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u4f9d\u7136\u8eab\u5b50\u6ca1\u52a8\u4e00\u52a8\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4e01\u6d9b\uff0c\u8fd9\u4e48\u665a\u4f60\u8dd1\u5230\u5bb5\u5173\u6765\u505a\u4ec0\u4e482\u201d \u3000\u3000\u4e01\u6d9b\u5782\u624b\u8eac\u8eab\u9053\uff1a\u201c\u6765\u2026\u2026\u6765\u5de1\u5173\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u53f1\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u4e09\u66f4\u534a\u591c\u6765\u5de1\u5173\uff0c\u96be\u9053\u6211\u5bb5\u5173\u51fa\u4e86\u4e8b\u6545\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u88ab\u95ee\u5f97\u7b54\u4e0d\u4e0a\u8bdd\u3002 \u3000\u3000\u201c\u51e4\u5ae3\u7ea2\u51b7\u7b11\u4e86\u51e0\u58f0\uff0c\u63a5\u9053\uff1a\u201c\u521a\u624d\u4f60\u8ddf\u78a7\u6843\u8bf4\u7684\u90a3\u4e9b\u8bdd\uff0c\u6211\u5168\u542c\u5230\u4e86\uff0c\u6839\u672c\u6ca1\u6709\u4e00\u53e5 \u4eba\u8bdd\uff0c\u4f60\u8eab\u4e3a\u4e94\u5173\u526f\u4f7f\u8005\uff0c\u5374\u6765\u5230\u5bb5\u5173\u80e1\u641e\uff0c\u5c45\u7136\u548c\u6211\u624b\u4e0b\u7684\u5973\u5175\u901a\u5978\uff0c\u8fd9\u8981\u8ba9\u603b\u575b\u77e5\u9053\uff0c \u4f60\u8fd8\u60f3\u6d3b\u547d\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u8111\u95e8\u50cf\u88ab\u6572\u4e86\u4e00\u68d2\uff0c\u60c5\u4e0d\u81ea\u7981\uff0c\u201c\u6251\u901a\u201d\u4e00\u58f0\uff0c\u8dea\u4e0b\u4e0b\u53bb\uff0c\u78d5\u7740\u54cd\u5934\u9053\uff1a\u201c\u7edf\u9886\u9ad8 \u62ac\u8d35\u624b\uff0c\u9976\u547d\uff0c\u82e5\u5f53\u521d\u60a8\u4e0d\u8ba9\u7ea2\u674f\u59d1\u5a18\u966a\u6211\uff0c\u6211\u2026\u2026\u4e5f\u4e0d\u53ef\u80fd\u505a\u51fa\u90a3\u79cd\u4e8b\u60c5\u6765\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u5389\u58f0\u9053\uff0c\u201c\u4f4f\u5634\uff0c\u6211\u8981\u7ea2\u674f\u966a\u4f60\uff0c\u662f\u966a\u4f60\u5de1\u5173\uff0c\u53ef\u66fe\u8981\u4f60\u548c\u5979\u505a\u51fa\u90a3\u79cd\u4e8b\u6765\u3002\u201d \u3000\u3000\u4e01\u6d9b\u53e9\u9996\u5982\u6363\u849c\u9053\uff1a\u201c\u5c5e\u4e0b\u7f6a\u8be5\u4e07\u6b7b\uff0c\u4e07\u6b7b\uff0c\u4e07\u4e07\u6b7b\uff01\u201d \u3000\u3000\u4ed6\u5e76\u975e\u51e4\u5ae3\u7ea2\u624b\u4e0b\uff0c\u5374\u6539\u53e3\u79f0\u5c5e\u4e0b\uff0c\u4e0d\u5916\u662f\u8ba9\u51e4\u5ae3\u7ea2\u542c\u7740\u8212\u670d\uff0c\u4ee5\u4fbf\u53d1\u53d1\u6148\u60b2\uff0c\u4ece\u8f7b\u53d1 \u843d\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u4f60\u72af\u4e0b\u5929\u8c37\u5927\u6212\uff0c\u7684\u786e\u7f6a\u8be5\u4e07\u6b7b\uff0c\u4f60\u60f3\u600e\u6837\u6b7b\u6cd5\uff0c\u81ea\u5df1\u5149\u8bf4\u8bf4\u770b\uff0c\u4e5f\u8bb8\u6211 \u4f1a\u4f9d\u4f60\uff0c\u201d \u3000\u3000\u4e01\u6d9b\u5168\u8eab\u5192\u7740\u51b7\u6c14\u9053\uff1a\u201c\u5982\u679c\u975e\u6b7b\u4e0d\u53ef\uff0c\u53cd\u6b63\u4e0d\u80fd\u6d3b\uff0c\u600e\u6837\u6b7b\u6cd5\u8fd8\u6709\u4ec0\u4e48\u5206\u522b\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u5206\u522b\u53ef\u5927\u4e86\uff0c\u6211\u53ef\u4ee5\u628a\u4f60\u540a\u6b7b\u3001\u7ede\u6b7b\u3001\u780d\u5934\u3001\u5927\u5378\u516b\u5757\u3001\u51cc\u8fdf\u5904\u6b7b\u3001\u9707\u65ad \u5fc3\u8109\u800c\u6b7b\u3001\u6216\u8005\u70e7\u6b7b\u3001\u6df9\u6b7b\u3001\u4e22\u8fdb\u6cb9\u9505\u70b8\u6b7b\u3001\u4e71\u7bad\u7a7f\u5fc3\u800c\u6b7b\u3001\u4e94\u9a6c\u5206\u5c38\u800c\u6b7b\u3001\u6216\u8005\u628a\u4f60\u6bd2\u6b7b\uff0c \u8fd9\u4e9b\u6b7b\u6cd5\uff0c\u5473\u9053\u5404\u6709\u4e0d\u540c\uff0c\u4f60\u9009\u62e9\u54ea\u4e00\u6837\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u8dea\u5728\u5730\u4e0a\uff0c\u542c\u51e4\u5ae3\u7ea2\u6bcf\u8bf4\u4e00\u6837\uff0c\u4fbf\u50cf\u6b7b\u8fc7\u4e00\u6b21\uff0c\u542c\u5230\u6700\u540e\uff0c\u7b80\u76f4\u5df2\u9b42\u98de\u9b44\u6563\uff0c\u55eb\u5685 \u7740\u9053\uff1a\u201c\u5c5e\u4e0b\u2026\u2026\u5c5e\u4e0b\u2026\u2026\u53ea\u6c42\u4e0d\u6b7b\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u79cd\u79bd\u517d\u4e0d\u5982\u7684\u4eba\uff0c\u6d3b\u5728\u4e16\u4e0a\uff0c\u53ea\u80fd\u574f\u4e8b\uff0c\u4e0d\u80fd\u505a\u4e8b\uff0c\u6b7b\u4e86\u5b9e\u5728\u6bd4\u6d3b \u7740\u597d\u4e9b\uff0c\u81f3\u5c11\u53ef\u4ee5\u7701\u4e0b\u4e00\u4e9b\u7cae\u98df\u5582\u72d7\u3002\u201d \u3000\u3000\u4e01\u6d9b\u6253\u7740\u54c6\u55e6\u9053\uff1a\u201c\u5c5e\u4e0b\u4e0d\u662f\u6ca1\u6709\u7528\uff0c\u5c5e\u4e0b\u5728\u6ca1\u8fdb\u5929\u8c37\u524d\uff0c\u548c\u7518\u9716\u5728\u592a\u767d\u5c71\uff0c\u66fe\u662f\u6709\u540d \u7684\u592a\u767d\u53cc\u96c4\uff0c\u8c01\u4eba\u4e0d\u77e5\uff0c\u90a3\u4e2a\u4e0d\u6653\uff0c\u6765\u5230\u5929\u8c37\uff0c\u56e0\u4e3a\u8fd9\u91cc\u4eba\u624d\u592a\u591a\uff0c\u6240\u4ee5\u5c31\u663e\u4e0d\u51fa\u6765\uff0c\u4e0d\u8fc7\uff0c \u82e5\u7edf\u9886\u9976\u5c5e\u4e0b\u4e0d\u6b7b\uff0c\u5c5e\u4e0b\u7ed9\u60a8\u505a\u725b\u505a\u9a6c\uff0c\u505a\u4ec0\u4e48\u90fd\u53ef\u4ee5\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u4ec0\u4e48\u592a\u767d\u53cc\u96c4\uff1f\u6700\u591a\u53ea\u80fd\u7b97\u592a\u767d\u5c71\u4e0a\u4e24\u53ea\u72d7\u718a\uff0c\u4f60\u8bf4\u80fd\u7ed9\u6211\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u8981\u5c5e\u4e0b\u505a\u4ec0\u4e48\uff0c\u5c5e\u4e0b\u5c31\u505a\u4ec0\u4e48\u3002\u201d \u3000\u3000\u201c\u7ea2\u674f\u662f\u5426\u8bf4\u8fc7\uff0c\u8981\u4f60\u628a\u4e94\u5173\u90a3\u8fb9\u7684\u6d88\u606f\u544a\u8bc9\u5979\uff1f\u201d \u3000\u3000\u201c\u8bf4\u8fc7\uff0c\u5c5e\u4e0b\u5df2\u7ecf\u544a\u8bc9\u4e86\u5979\u4e0d\u5c11\u3002\u201d \u3000\u3000\u201c\u90a3\u4e9b\u6d88\u606f\u90fd\u65e0\u5173\u91cd\u8981\uff0c\u4e0d\u7528\u4f60\u8bf4\u6211\u4e5f\u77e5\u9053\u3002\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u73b0\u5728\u5c31\u6709\u4e00\u4ef6\u91cd\u8981\u7684\u4e8b\u544a\u8bc9\u4f60\u8001\u4eba\u5bb6\uff01\u201d \u3000\u3000\u201c\u4f60\u8bf4\uff01\u201d \u3000\u3000\u201c\u82b1\u526f\u603b\u9547\u548c\u5cb3\u4f7f\u8005\u662f\u7236\u5b50\u5173\u7cfb\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u554a\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u5c5e\u4e0b\u600e\u6562\u9a97\u60a8\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u7acb\u523b\u7f13\u4e0b\u8bed\u6c14\u9053\uff1a\u201c\u8d77\u6765\uff0c\u8ddf\u6211\u5230\u91cc\u9762\u53bb\uff01\u201d \u3000\u3000\u8bf4\u7740\uff0c\u8f6c\u8eab\u5728\u524d\u5e26\u8def\u3002 \u3000\u3000\u4e01\u6d9b\u722c\u8d77\u8eab\u6765\uff0c\u50cf\u53ea\u72d7\u718a\u822c\u8ddf\u5728\u540e\u9762\u3002 \u3000\u3000\u8fdb\u5165\u5185\u9662\u5ba2\u5385\uff0c\u91cc\u9762\u65e9\u4eae\u7740\u706f\uff0c\u56e0\u5df2\u65f6\u81f3\u4e8c\u66f4\uff0c\u4f8d\u5a62\u4eec\u90fd\u5df2\u56de\u623f\u5b89\u606f\uff0c\u5ba2\u5385\u91cc\u5e76\u65e0\u7b2c\u4e09 \u4eba\u5728\u3002 \u3000\u3000\u8fd9\u5ba2\u5385\u4e01\u6d9b\u5df2\u6765\u8fc7\u4e00\u6b21\u3002 \u3000\u3000\u4e0d\u8fc7\u4e0a\u6b21\u4ed6\u548c\u7518\u9716\u88ab\u62db\u5f85\u5728\u8fd9\u91cc\uff0c\u5fc3\u60c5\u662f\u53d7\u5ba0\u82e5\u60ca\uff0c\u8fd9\u6b21\u5374\u662f\u5f85\u7f6a\u7f94\u7f8a\uff0c\u50cf\u56da\u72af\u8fdb\u5165\u5211 \u573a\uff0c\u8fde\u5934\u90fd\u4e0d\u6562\u62ac\uff0c\u6839\u672c\u65e0\u5fc3\u6d4f\u89c8\u5ba2\u5385\u5185\u7684\u65d6\u65ce\u98ce\u5149\u3002 \u3000\u3000\u4ed6\u7ad9\u5728\u90a3\u91cc\uff0c\u5782\u9996\u8eac\u8eab\uff0c\u5c31\u50cf\u5b66\u751f\u88ab\u8001\u5e08\u7f5a\u7acb\u6b63\u4e00\u822c\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u5012\u4e86\u676f\u8336\uff0c\u559d\u4e86\u4e00\u53e3\uff0c\u81ea\u884c\u843d\u4e86\u5ea7\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u521a\u624d\u8fd9\u8bdd\u6709\u4ec0\u4e48\u6839\u636e\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\u8fd9\u6b21\u8fdb\u5165\u5929\u8c37\uff0c\u76ee\u7684\u5c31\u662f\u627e\u4ed6\u7684\u7236\u6bcd\uff0c\u73b0\u5728\u4ed6\u4eec\u660e\u662f\u957f\u5b98\u90e8\u5c5e\uff0c\u5b9e\u662f \u7236\u5b50\uff0c\u53ea\u662f\u4ed6\u4eec\u4e0d\u51c6\u5c5e\u4e0b\u548c\u7518\u9716\u544a\u8bc9\u522b\u4eba\u800c\u5df2\u3002\u201d \u3000\u3000\u63a5\u7740\u53c8\u628a\u4e09\u5e74\u524d\u4ed6\u548c\u7518\u9716\u5982\u4f55\u88ab\u82b1\u7389\u9e9f\u592b\u5987\u6551\u8fc7\u4e00\u547d\u4ee5\u53ca\u5982\u4f55\u966a\u5cb3\u5c0f\u98de\u3001\u8881\u5c0f\u9e64\u8fdb\u5165\u5929 \u8c37\u7684\u4e8b\u4ece\u5934\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u542c\u5f97\u5341\u5206\u4ed4\u7ec6\uff0c\u6700\u540e\u5374\u53c8\u8e59\u8d77\u67f3\u7709\u9053\uff1a\u201c\u53ef\u662f\u4e3a\u4ec0\u4e48\u4ed6\u4eec\u7236\u5b50\u4e0d\u540c\u59d3\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u636e\u5c5e\u4e0b\u6240\u77e5\uff0c\u5f53\u521d\u5cb3\u4f7f\u8005\u6839\u672c\u5f04\u4e0d\u6e05\u4ed6\u7236\u6bcd\u7684\u59d3\u540d\uff0c\u4ed6\u59d3\u5cb3\u662f\u522b\u4eba\u66ff\u4ed6\u53d6\u7684 \u5cb3\uff0c\u81f3\u4e8e\u4ed6\u4eec\u7236\u5b50\u5728\u5929\u8c37\u76f8\u89c1\u540e\u5cb3\u4f7f\u8005\u6ca1\u6539\u59d3\uff0c\u90a3\u662f\u4e3a\u4e86\u4fdd\u5bc6\uff0c\u8ba9\u522b\u4eba\u4e0d\u77e5\u9053\u4ed6\u4eec\u662f\u7236\u5b50\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u7728\u52a8\u7740\u7738\u5b50\uff0c\u7f04\u9ed8\u4e86\u534a\u54cd\u9053\uff1a\u201c\u90a3\u4e48\u5cb3\u4f7f\u8005\u7684\u6bcd\u4eb2\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u9053\uff1a\u201c\u597d\u50cf\u59d3\u4f55\uff0c\u53eb\u4ec0\u4e48\uff0c\u5947\u602a\u7684\u662f\u5c5e\u4e0b\u6765\u5230\u5929\u8c37\u540e\uff0c\u53ea\u89c1\u8fc7\u82b1\u526f\u603b\u9547\uff0c\u5374\u4ece\u6ca1\u89c1 \u8fc7\u4ed6\u7684\u592b\u4eba\uff0c\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u5230\u8fd9\u65f6\u624d\u77e5\u9053\u539f\u6765\u82b1\u7389\u9e9f\u548c\u4f55\u6167\u4ed9\u7adf\u662f\u592b\u5987\uff0c\u4e0d\u89c9\u6697\u9053\uff1a\u82b1\u7389\u9e9f\uff0c\u6211\u51e4\u5ae3\u7ea2\u7ec8\u4e8e \u6293\u5230\u4f60\u7684\u628a\u67c4\u4e86\uff0c\u6709\u4e86\u8fd9\u628a\u67c4\uff0c\u4e0d\u6015\u4f60\u4e0d\u5c31\u8303\uff01\u201d \u3000\u3000\u6b64\u523b\uff0c\u5979\u7684\u8bed\u6c14\u53d8\u5f97\u5341\u5206\u4eb2\u5207\uff0c\u7ea4\u624b\u8f7b\u626c\uff0c\u62db\u547c\u7740\u9053\uff1a\u201c\u4e01\u526f\u4f7f\u8005\uff0c\u4f60\u5750\u4e0b\uff01\u201d \u3000\u3000\u4e01\u6d9b\u7ec8\u4e8e\u5fc3\u91cc\u50cf\u843d\u4e0b\u4e00\u5757\u77f3\u5934\uff0c\u8f7b\u677e\u4e86\u4e0d\u5c11\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u843d\u4e86\u5ea7\u3002 \u3000\u3000\u51fa\u4e4e\u4ed6\u610f\u6599\u7684\uff0c\u51e4\u5ae3\u7ea2\u7adf\u53c8\u5012\u4e86\u4e00\u676f\u8336\uff0c\u4eb2\u81ea\u9001\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e01\u6d9b\u60ca\u5f97\u53c8\u7ad9\u8d77\u6765\uff0c\u53cc\u624b\u76f4\u63a8\u9053\uff1a\u201c\u4f7f\u4e0d\u5f97\uff01\u4f7f\u4e0d\u5f97\uff0c\u5c5e\u4e0b\u5feb\u8981\u6b7b\u7684\u4eba\u4e86\uff0c\u600e\u6562\u8981\u4f60\u8001 \u4eba\u5bb6\u5012\u8336\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u62bf\u5634\u4e00\u7b11\u9053\uff1a\u201c\u522b\u7d27\u5f20\uff0c\u6211\u5df2\u51b3\u5b9a\u9976\u4f60\u4e0d\u6b7b\u4e86\uff0c\u5148\u559d\u676f\u8336\u538b\u538b\u60ca\u3002\u201d \u3000\u3000\u4e01\u6d9b\u771f\u6068\u4e0d\u5f97\u518d\u8dea\u4e0b\u53bb\u53e9\u5934\u8c22\u6069\uff0c\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u4e48\u624d\u597d\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u53c8\u5750\u4e0b\u53bb\uff0c\u4f46\u8fd9\u6b21\u5374\u5750\u5f97\u548c\u4e01\u6d9b\u975e\u5e38\u9760\u8fd1\uff0c\u773c\u6ce2\u6d41\u76fc\u7684\u9053\uff1a\u201c\u4f60\u8fd8\u60f3\u4e0d\u60f3\u7ea2\u674f\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u6014\u4e86\u6014\u9053\uff1a\u201c\u5c5e\u4e0b\u90a3\u91cc\u8fd8\u6562\u518d\u60f3\uff0c\u72af\u4e86\u89c4\u6212\uff0c\u90a3\u662f\u8981\u6740\u5934\u7684\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u4f4e\u58f0\u9053\uff1a\u201c\u5982\u679c\u6211\u4e0d\u6740\u4f60\u5462\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u4e0d\u89c9\u80c6\u6c14\u6e10\u6e10\u58ee\u4e86\uff0c\u54a7\u5634\u9053\uff1a\u201c\u5982\u679c\u4f60\u8001\u4eba\u5bb6\u4e0d\u6740\u5934\uff0c\u5c5e\u4e0b\u5f53\u7136\u8fd8\u662f\u60f3\u5979\u3002\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u90a3\u4e48\u6211\u73b0\u5728\u5c31\u53bb\u628a\u5979\u627e\u6765\u597d\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u7b80\u76f4\u4ee5\u4e3a\u8033\u6735\u51fa\u4e86\u6bdb\u75c5\uff0c\u4e24\u773c\u4e00\u76f4\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u4e0d\u662f\u5f00\u73a9\u7b11\u5427\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u54af\u54af\u7b11\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u5f00\u7684\u4ec0\u4e48\u73a9\u7b11\u3002\u201d \u3000\u3000\u4e01\u8bd7\u54bd\u4e0b\u4e00\u53e3\u53e3\u6c34\u9053\uff1a\u201c\u73b0\u5728\u53bb\u627e\u5979\u65b9\u4fbf\u4e48\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u65b9\u4fbf\uff0c\u6211\u53ef\u4ee5\u628a\u623f\u95f4\u8ba9\u7ed9\u4f60\u4eec\u3002\u201d \u3000\u3000\u4e01\u6d9b\u4e0d\u89c9\u4e24\u773c\u53d1\u76f4\uff0c\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u5a9a\u773c\u4e00\u629b\uff0c\u7b11\u9053\uff1a\u201c\u5b9e\u5bf9\u4f60\u8bf4\uff0c\u5979\u73b0\u5728\u5c31\u5728\u6211\u623f\u95f4\u91cc\u3002\u201d \u3000\u3000\u4e01\u6d9b\u5f04\u4e0d\u6e05\u51e4\u5ae3\u7ea2\u8fd9\u8bdd\u662f\u4f55\u7528\u610f\uff0c\u7adf\u7136\u5446\u5728\u5f53\u573a\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u8d77\u4e86\u5ea7\uff0c\u626d\u52a8\u7740\u50cf\u86c7\u4e00\u822c\u7684\u67f3\u8170\uff0c\u5411\u5c4f\u98ce\u540e\u9762\u8d70\u53bb\uff0c\u518d\u56de\u7738\u4e00\u7b11\uff0c\u5411\u540e\u62db\u62db\u624b\u9053\uff1a \u201c\u6765\uff01\u968f\u6211\u6765\uff01\u201d \u3000\u3000\u4e01\u6d9b\u970e\u65f6\u795e\u9b42\u98d8\u8361\uff0c\u5fc3\u65cc\u6447\u66f3\uff0c\u6025\u6025\u8d77\u8eab\u8ddf\u53bb\u3002 \u3000\u3000\u5c4f\u98ce\u540e\u4fbf\u662f\u4e00\u9053\u4fa7\u95e8\uff0c\u7a7f\u8fc7\u4fa7\u95e8\uff0c\u5c31\u8fdb\u5165\u4e86\u51e4\u5ae3\u7ea2\u7684\u5367\u5ba4\u3002 \u3000\u3000\u5367\u5ba4\u91cc\u70b9\u7740\u706f\u3002 \u3000\u3000\u53ea\u770b\u4e86\u8fd9\u95f4\u5367\u623f\u7684\u5e03\u7f6e\uff0c\u4e01\u6d9b\u4fbf\u5df2\u6309\u637a\u4e0d\u4f4f\u5fc3\u91cc\u90a3\u80a1\u8bf4\u4e0d\u51fa\u7684\u602a\u706b\uff0c\u5373\u4f7f\u4e8b\u60c5\u8fc7\u540e\u9a6c\u4e0a \u6740\u5934\uff0c\u4ed6\u4e5f\u6beb\u4e0d\u5728\u4e4e\u4e86\u3002 \u3000\u3000\u4e01\u6d9b\u4e24\u773c\u706b\u7ea2\u3002\u547c\u5438\u6025\u901f\u7684\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u89c1\u7ea2\u674f\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u626d\u52a8\u7740\u5a07\u8eaf\uff0c\u8f6f\u7ef5\u7ef5\u7684\u5750\u4e0a\u5e8a\u6cbf\uff0c\u54e7\u54e7\u7b11\u9053\uff1a\u201c\u867d\u7136\u4e0d\u89c1\u7ea2\u674f\uff0c\u5374\u6709\u4e00\u4e2a\u6bd4\u7ea2\u674f \u66f4\u597d\u7684\uff0c\u96be\u9053\u4f60\u8fd8\u6ca1\u770b\u89c1\uff01\u201d \u3000\u3000\u4e01\u6d9b\u5148\u662f\u76ee\u77aa\u53e3\u5446\uff0c\u63a5\u7740\u4f7f\u50cf\u997f\u864e\u6251\u7f8a\u822c\uff0c\u6251\u5230\u51e4\u5ae3\u7ea2\u8eab\u4e0a\u53bb\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u88ab\u6251\u5f97\u201c\u5662\u201d\u7684\u4e00\u58f0\u5a07\u547c\uff0c\u968f\u5373\u4ef0\u8eab\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u539f\u6765\u51e4\u5ae3\u7ea2\u81ea\u4ece\u4e0a\u6b21\u548c\u9a6c\u662d\u96c4\u5e7d\u4f1a\u8fc7\u540e\uff0c\u4e00\u76f4\u6625\u5fc3\u8361\u6f3e\uff0c\u4e0d\u80fd\u81ea\u5df1\u3002 \u3000\u3000\u5728\u5979\u6765\u8bf4\uff0c\u8fd9\u662f\u5f88\u81ea\u7136\u7684\u4e8b\uff0c\u5728\u6ca1\u548c\u9a6c\u662d\u96c4\u5e7d\u4f1a\u524d\uff0c\u72ec\u5b88\u7a7a\u5e0f\u65e5\u5b50\u4e45\u4e86\uff0c\u6162\u6162\u4e5f\u80fd\u538b\u4e0b \u90a3\u79cd\u5ff5\u5934\uff0c\u4f46\u4e00\u65e6\u5f00\u4e86\u6212\uff0c\u5374\u518d\u4e5f\u71ac\u4e0d\u8fc7\u53bb\u3002 \u3000\u3000\u5979\u4e0a\u6b21\u767e\u822c\u8bf1\u529d\u82b1\u7389\u9e9f\u4e0d\u6210\uff0c\u4eca\u5929\u4e0b\u5348\u82b1\u7389\u9e9f\u53c8\u6765\u8fc7\uff0c\u672c\u60f3\u518d\u5ea6\u65bd\u51fa\u6d51\u8eab\u89e3\u6570\u4ee5\u507f\u5fc3\u613f\uff0c \u504f\u504f\u82b1\u7389\u9e9f\u8fde\u5750\u90fd\u6ca1\u5750\uff0c\u4ea4\u4ee3\u4e86\u51e0\u53e5\u8bdd\u4fbf\u8d70\uff0c\u4f7f\u5f97\u5979\u7b80\u76f4\u5feb\u8981\u6b32\u706b\u711a\u8eab\u3002 \u3000\u3000\u6b64\u523b\uff0c\u53c8\u6709\u7537\u4eba\u524d\u6765\uff0c\u5979\u5f53\u7136\u4e0d\u4f1a\u653e\u8fc7\u3002 \u3000\u3000\u867d\u7136\u4e01\u6d9b\u7684\u957f\u76f8\uff0c\u5979\u770b\u4e0d\u4e2d\u610f\uff0c\u4f46\u603b\u662f\u4e2a\u7537\u4eba\uff0c\u4f55\u51b5\u4ed6\u7684\u957f\u76f8\u867d\u4e0d\u600e\u4e48\u6837\uff0c\u8eab\u6750\u5374\u9ad8\u5927 \u7ed3\u5b9e\uff0c\u5439\u7184\u4e86\u706f\uff0c\u90a3\u91cc\u8fd8\u7ba1\u4ed6\u522b\u7684\uff0c\u6709\u603b\u6bd4\u6ca1\u6709\u597d\uff0c\u4e5f\u8bb8\u8fd9\u5c31\u53eb\u9965\u4e0d\u62e9\u98df\u5427\u3002 \u3000\u3000\u6b64\u523b\u7684\u4e01\u6d9b\uff0c\u5c31\u50cf\u53d1\u4e86\u75af\u4e00\u822c\uff0c\u628a\u51e4\u5ae3\u7ea2\u538b\u5728\u8eab\u4e0b\uff0c\u8fde\u6c14\u4e5f\u5598\u4e0d\u8fc7\u6765\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u63a8\u7740\u4e01\u6d9b\u9053\uff1a\u201c\u6b7b\u72d7\uff0c\u5c31\u8fd9\u6837\u600e\u4e48\u884c\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u8fd9\u624d\u7ffb\u8eab\u4e0b\u6765\uff0c\u6b7b\u547d\u7684\u62c9\u626f\u51e4\u5ae3\u7ea2\u7684\u4e0b\u8863\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u5de5\u592b\uff0c\u51e4\u5ae3\u7ea2\u5df2\u88ab\u5265\u4e86\u4e2a\u7cbe\u5149\u3002 \u3000\u3000\u521a\u8981\u518d\u6251\u4e0a\u53bb\uff0c\u51e4\u5ae3\u7ea2\u53c8\u63a8\u4e86\u4e00\u628a\u9053\uff1a\u201c\u8fd8\u6709\u4f60\u81ea\u5df1\u3002\u201d \u3000\u3000\u4e01\u6d9b\u5306\u5306\u518d\u89e3\u81ea\u5df1\u7684\u8863\u670d\uff0c\u8fd9\u53ef\u80fd\u662f\u4ed6\u6709\u751f\u4ee5\u6765\u5378\u88c5\u5378\u5f97\u6700\u5feb\u7684\u4e00\u6b21\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u6ca1\u60f3 \u5230\u81ea\u5df1\u7adf\u6709\u8fd9\u6837\u52a8\u4f5c\u8fc5\u901f\u7684\u672c\u9886\u3002 \u3000\u3000\u8c01\u77e5\u5f53\u4ed6\u7b2c\u4e09\u5ea6\u6251\u4e0a\u53bb\uff0c\u7adf\u53c8\u88ab\u51e4\u5ae3\u7ea2\u63a8\u5f00\u3002 \u3000\u3000\u4ed6\u6c14\u5598\u5982\u725b\u7684\u9053\uff1a\u201c\u4f60\u8001\u4eba\u5bb6\u8fd9\u662f\u8981\u505a\u4ec0\u4e48\uff1f\u83ab\u975e\u60a8\u8fd9\u91cc\u8fd8\u6709\u4ec0\u4e48\u53e6\u5916\u7684\u89c4\u77e9\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u563b\u563b\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u505a\u90e8\u4e0b\u7684\u5c31\u8fd9\u6837\u5bf9\u5f85\u4e0a\u7ea7\u4e48\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u7acb\u523b\u53cc\u819d\u8dea\u5728\u5e8a\u4e0a\uff0c\u76f4\u7740\u55d3\u95e8\u53eb\u9053\uff1a\u201c\u7edf\u9886\u5f00\u6069\uff01\u5c5e\u4e0b\u5b9e\u5728\u5bf9\u4e0d\u8d77\u4e86\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u8f7b\u8f7b\u4e00\u638c\u6253\u5728\u4e01\u6d9b\u8138\u4e0a\u9053\uff1a\u201c\u8fd8\u4e0d\u5feb\u5feb\u7184\u706f\uff01\u201d \u3000\u3000\u53e3\u53e3\u3000\u3000\u3000\u3000\u3000\u53e3\u53e3 \u3000\u3000\u5bb5\u5173\u95e8\u5916\u7684\u8def\u4e0a\uff0c\u6643\u52a8\u7740\u4e00\u4e2a\u9ad8\u5927\u7684\u4eba\u5f71\u3002 \u3000\u3000\u8fd9\u4eba\u6b63\u662f\u4e01\u6d9b\u3002 \u3000\u3000\u6b64\u523b\u7684\u4e01\u6d9b\uff0c\u5728\u4ed6\u81ea\u5df1\u7684\u611f\u89c9\u4e0a\uff0c\u7b80\u76f4\u50cf\u795e\u4ed9\u4e00\u822c\u3002 \u3000\u3000\u50cf\u4ed6\u8fd9\u79cd\u8eab\u4efd\u5730\u4f4d\u7684\u4eba\uff0c\u5c45\u7136\u80fd\u4e00\u4eb2\u5bb5\u5173\u7edf\u9886\u82b3\u6cfd\uff0c\u82e5\u975e\u7956\u4e0a\u70e7\u8fc7\u9ad8\u9999\uff0c\u5c31\u662f\u6709\u795e\u7075\u4fdd \u4f51\u3002 \u3000\u3000\u603b\u4e4b\uff0c\u4ed6\u73b0\u5728\u51e0\u4e4e\u5df2\u7ecf\u5fd8\u8bb0\u81ea\u5df1\u59d3\u4ec0\u4e48\u53eb\u4ec0\u4e48\u4e86\u3002 \u3000\u3000\u60f3\u8d77\u65b9\u624d\u5728\u51e4\u5ae3\u7ea2\u5367\u623f\u4e2d\u7684\u90a3\u4e00\u5e55\uff0c\u5c31\u50cf\u505a\u4e86\u4e00\u6b21\u795e\u4ed9\u3002 \u3000\u3000\u771f\u7684\uff0c\u76f4\u5230\u73b0\u5728\u4ed6\u8fd8\u6b32\u4ed9\u6b32\u9189\uff0c\u98d8\u98d8\u7136\u6709\u5982\u5728\u6f2b\u6e38\u592a\u7a7a\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u4e5f\u6709\u4e00\u4ef6\u4e8b\u4f7f\u4ed6\u4e0d\u5927\u7518\u5fc3\uff0c\u90a3\u5c31\u662f\u51e4\u5ae3\u7ea2\u544a\u8beb\u4ed6\u4eca\u665a\u7684\u4e8b\uff0c\u5343\u4e07\u4e0d\u80fd\u8ba9\u4efb\u4f55\u4eba\u77e5 \u9053\uff0c\u5426\u5219\u4e0b\u6b21\u89c1\u4e86\u8fd8\u662f\u8981\u6740\u5934\u3002\u4ed6\u60f3\uff0c\u81f3\u5c11\u4ed6\u8be5\u5728\u7518\u9716\u9762\u524d\u70ab\u8000\u4e00\u756a\uff0c\u4ed6\u76f8\u4fe1\u7518\u9716\u8fde\u7956\u5b97\u516b \u4ee3\u90fd\u4e0d\u53ef\u80fd\u8fd9\u6837\u98ce\u5149\u8fc7\u3002 \u3000\u3000\u6b63\u8d70\u4e4b\u95f4\uff0c\u53e6\u4e00\u6761\u5c94\u8def\u4e0a\uff0c\u84e6\u5730\u51fa\u73b0\u4e86\u4e00\u4e2a\u4eba\u5f71\u3002 \u3000\u3000\u4ed6\u8d77\u5148\u5c1a\u4e0d\u4ee5\u4e3a\u610f\uff0c\u4f46\u5f88\u5feb\u5c31\u5fc3\u795e\u731b\u7136\u9707\u52a8\u8d77\u6765\uff0c\u56e0\u4e3a\u4ed6\u5df2\u770b\u6e05\u8fd9\u4eba\u662f\u5cb3\u5c0f\u98de\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u6b63\u662f\u7531\u80b2\u5316\u57ce\u8fd4\u56de\uff0c\u504f\u504f\u5c31\u9047\u4e0a\u4e86\u4e01\u6d9b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u89c6\u529b\u5f02\u4e8e\u5e38\u4eba\uff0c\u4ed6\u89c1\u4e01\u6d9b\u8fd9\u822c\u65f6\u5019\u7531\u5bb5\u5173\u51fa\u6765\uff0c\u600e\u80fd\u4e0d\u607c\u3002 \u3000\u3000\u518d\u60f3\u8d77\u4e01\u6d9b\u5c45\u7136\u5411\u79e6\u69d0\u544a\u72b6\uff0c\u66f4\u662f\u6c14\u5f80\u4e0a\u51b2\u3002 \u3000\u3000\u867d\u7136\uff0c\u7236\u4eb2\u66fe\u8981\u4ed6\u627e\u673a\u4f1a\u548c\u4e01\u6d9b\u5316\u89e3\u5316\u89e3\uff0c\u6b64\u65f6\u4ed6\u4e5f\u987e\u4e0d\u5f97\u4e86\u3002 \u3000\u3000\u4ed6\u5feb\u8d70\u51e0\u6b65\uff0c\u62e6\u5728\u4e01\u6d9b\u524d\u9762\uff0c\u56de\u8eab\u9053\uff1a\u201c\u4e01\u5f53\u5bb6\u7684\uff0c\u4f60\u5230\u54ea\u91cc\u53bb\u6765\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u65e9\u5df2\u5413\u5446\uff0c\u5e72\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u6211\u2026\u2026\u968f\u4fbf\u51fa\u6765\u8d70\u8d70\u3002\u201d \u3000\u3000\u201c\u4f60\u5206\u660e\u662f\u4ece\u5bb5\u5173\u51fa\u6765\uff01\u201d \u3000\u3000\u201c\u6211\u2026\u2026\u786e\u5b9e\u662f\u968f\u4fbf\u51fa\u6765\u8d70\u8d70\u3002\u201d \u3000\u3000\u201c\u73b0\u5728\u5df2\u662f\u4e0b\u534a\u591c\uff0c\u53ef\u6709\u4e0d\u7761\u89c9\u8fd8\u51fa\u6765\u8d70\u8d70\u7684\uff1f\u201d \u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e5f\u5728\u5916\u9762\uff1f\u201d \u3000\u3000\u201c\u5728\u4e0b\u662f\u5949\u526f\u603b\u9547\u4e4b\u547d\uff0c\u51fa\u6765\u529e\u4e8b\u7684\uff0c\u4f60\u662f\u5949\u8c01\u4e4b\u547d\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u7ec8\u4e8e\u7b54\u4e0d\u4e0a\u8bdd\uff0c\u4ed6\u600e\u6562\u5728\u82b1\u7389\u9e9f\u9762\u524d\u5bf9\u8d28\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7a81\u5730\u626c\u624b\u4e00\u638c\uff0c\u731b\u5411\u4e01\u6d9b\u9762\u988a\u63b4\u53bb\u3002 \u3000\u3000\u4e01\u6d9b\u90a3\u91cc\u8eb2\u5f97\u8fc7\uff0c\u4e24\u773c\u4e00\u9635\u53d1\u9ed1\uff0c\u76f4\u5411\u8def\u65c1\u6454\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u7684\u611f\u89c9\uff0c\u4e0e\u65b9\u624d\u5728\u51e4\u5ae3\u7ea2\u5367\u623f\u4e2d\u76f8\u6bd4\uff0c\u5b9e\u5728\u4ee4\u4ed6\u8fde\u60f3\u90fd\u4e0d\u6562\u60f3\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u559d\u9053\uff1a\u201c\u59d3\u4e01\u7684\uff0c\u4f60\u7ecf\u5e38\u5230\u5bb5\u5173\u9b3c\u6df7\uff0c\u5df2\u7ecf\u662f\u5927\u5927\u4e0d\u8be5\u4e86\uff0c\u5982\u4eca\u7adf\u7136\u53d8\u672c\u52a0\u5389\uff0c \u8fde\u534a\u591c\u4e5f\u5f80\u5bb5\u5173\u8dd1\uff0c\u8fd9\u8fd8\u4e0d\u7b97\uff0c\u66f4\u5728\u603b\u62a4\u6cd5\u9762\u524d\u544a\u6211\u7684\u72b6\uff0c\u4f60\u7a76\u7adf\u7b97\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff01\u201d \u3000\u3000\u4e01\u6d9b\u6342\u7740\u9762\u988a\uff0c\u54ac\u7259\u54a7\u5634\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\uff0c\u6211\u5c31\u662f\u505a\u9519\u4e86\u4e8b\uff0c\u4f60\u4e5f\u4e0d\u8be5\u8fd9\u6837\u4e0d\u8bb2\u60c5\u9762\uff01\u201d \u3000\u3000\u201c\u4f60\u82e5\u8bb2\u60c5\u9762\uff0c\u5c31\u4e0d\u8be5\u5728\u603b\u62a4\u6cd5\u9762\u524d\u9760\u6211\u7684\u72b6\uff01\u201d \u3000\u3000\u201c\u90a3\u4e0d\u662f\u544a\u72b6\u3002\u201d \u3000\u3000\u201c\u4e0d\u662f\u544a\u72b6\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u603b\u62a4\u6cd5\u95ee\u8bdd\uff0c\u6211\u4e0d\u5f97\u4e0d\u7b54\u3002\u201d \u3000\u3000\u201c\u603b\u62a4\u6cd5\u53ef\u66fe\u5355\u72ec\u5411\u4f60\u95ee\u8bdd\uff1f\u201d \u3000\u3000\u201c\u5f53\u65f6\u56db\u4eba\u7ad9\u5728\u90a3\u91cc\uff0c\u6211\u4e2a\u5b50\u6700\u9ad8\uff0c\u603b\u62a4\u6cd5\u95ee\u8bdd\uff0c\u522b\u4eba\u4e0d\u7b54\uff0c\u6211\u4e0d\u80fd\u4e0d\u7b54\u3002\u201d \u3000\u3000\u201c\u89c9\u7740\u5012\u4e0d\u9519\uff0c\u4f60\u867d\u7136\u9ad8\uff0c\u5374\u6ca1\u653e\u5728\u603b\u62a4\u6cd5\u773c\u91cc\uff0c\u4e94\u5173\u7684\u65d7\u6746\u6bd4\u4f60\u9ad8\uff0c\u5b83\u4e3a\u4ec0\u4e48\u4e0d\u7b54 \u8bdd\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u5e72\u54b3\u7740\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\uff0c\u4f60\u600e\u4e48\u8fd9\u6837\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6ca1\u6709\u4f60\u8bf4\u7684\u8bdd\uff0c\u56de\u53bb\u540e\u5728\u4e0b\u5373\u523b\u7980\u62a5\u526f\u603b\u9547\uff0c\u5148\u628a\u4f60\u5173\u8d77\u6765\u518d\u8bf4\u3002\u201d \u3000\u3000\u5927\u7ea6\u4e94\u5929\u4e4b\u540e\u7684\u6df1\u591c\uff0c\u80b2\u5316\u57ce\u7684\u5927\u59d1\u5a18\u4e25\u5982\u51b0\u6084\u7136\u6765\u5230\u4e86\u4e94\u5173\uff0c\u4e3a\u4e86\u79d8\u5bc6\uff0c\u82b1\u7389\u9e9f\u628a\u5979 \u63a5\u5f85\u5728\u5185\u5ba4\uff0c\u5cb3\u5c0f\u98de\u4e5f\u76f8\u968f\u5728\u4fa7\u3002 \u3000\u3000\u5927\u59d1\u5a18\u4e25\u5982\u51b0\u8fd9\u65f6\u5019\u524d\u6765\uff0c\u8c01\u90fd\u53ef\u4ee5\u60f3\u5230\u5fc5\u6709\u91cd\u8981\u5927\u4e8b\u3002 \u3000\u3000\u539f\u6765\u4e25\u5bd2\u5df2\u5f97\u5230\u786e\u5207\u6d88\u606f\uff0c\u7687\u5143\u6559\u4e3b\u5373\u5c06\u5728\u660e\u5929\u5927\u4e3e\u8fdb\u72af\u80b2\u5316\u57ce\uff0c\u5927\u59d1\u5a18\u6b64\u6765\uff0c\u4e0d\u5916\u662f \u8bf7\u6c42\u9ad8\u624b\u652f\u63f4\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u542c\u8fc7\u4e4b\u540e\u9053\uff1a\u201c\u57ce\u4e3b\u5f97\u5230\u8fd9\u6d88\u606f\u662f\u5426\u786e\u5b9e\uff1f\u201d \u3000\u3000\u4e25\u5982\u51b0\u9053\uff1a\u201c\u5bb6\u7236\u6709\u4eba\u5728\u603b\u575b\u5367\u5e95\uff0c\u6d88\u606f\u7edd\u5bf9\u9519\u4e0d\u4e86\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u5982\u679c\u80b2\u5316\u57ce\u90a3\u8fb9\u786e\u5b9e\u60c5\u51b5\u4e25\u91cd\uff0c\u82b1\u67d0\u5c31\u51b3\u5b9a\u4ece\u73b0\u5728\u8d77\u653e\u5f03\u4e94\u5173\uff0c \u8fde\u591c\u7387\u4f17\u8d76\u5230\u80b2\u5316\u57ce\u53bb\u3002\u201d \u3000\u3000\u4e25\u5982\u51b0\u9053\uff1a\u201c\u5bb6\u7236\u7684\u610f\u601d\uff0c\u82b1\u53d4\u53d4\u6682\u65f6\u8fd8\u4e0d\u53ef\u653e\u5f03\u4e94\u5173\uff0c\u53ea\u8981\u6d3e\u51fa\u51e0\u540d\u9ad8\u624b\u76f8\u52a9\u5c31\u53ef\u4ee5 \u4e86\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u6211\u8fd9\u91cc\u82e5\u8bba\u9ad8\u624b\uff0c\u53ea\u6709\u72ac\u5b50\u5c0f\u98de\u548c\u53e6\u4e00\u4f4d\u5de1\u5173\u4f7f\u8005\u8881\u5c0f\u9e64\uff0c\u4ec5\u7531\u4ed6\u4eec\u4e24\u4eba \u524d\u53bb\uff0c\u53ea\u6015\u4e5f\u4e8e\u4e8b\u65e0\u8865\u3002\u201d \u3000\u3000\u4e25\u5982\u51b0\u9053\uff1a\u201c\u5c0f\u98de\u5144\u5f1f\u4e00\u4eba\u8db3\u6321\u5343\u519b\u4e07\u9a6c\uff0c\u518d\u52a0\u4e0a\u4e00\u4f4d\u8881\u4f7f\u8005\uff0c\u90a3\u5c31\u66f4\u597d\u4e86\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u968f\u5373\u547d\u5cb3\u5c0f\u98de\u4eb2\u628a\u8881\u5c0f\u9e64\u627e\u6765\uff0c\u5e76\u4ea4\u5f85\u4ed6\u548c\u5cb3\u5c0f\u98de\u4e24\u4eba\u968f\u5927\u59d1\u5a18\u8fde\u591c\u8d76\u5230\u80b2\u5316\u57ce \u53bb\uff0c\u51c6\u5907\u660e\u5929\u8fce\u51fb\u7687\u5143\u6559\u8fdb\u72af\u3002 \u3000\u3000\u4e25\u5982\u51b0\u4e0d\u5f97\u4e0d\u4e3a\u82b1\u7389\u9e9f\u7684\u5904\u5883\u62c5\u5fc3\uff0c\u5e26\u7740\u4e0d\u5b89\u7684\u795e\u8272\u9053\uff1a\u201c\u82b1\u53d4\u53d4\uff0c\u5982\u679c\u7687\u5143\u6559\u4e3b\u4e5f\u4e0b \u4ee4\u8c03\u5c0f\u98de\u5144\u5f1f\u548c\u8881\u4f7f\u8005\u53c2\u4e0e\u660e\u5929\u8fdb\u653b\u80b2\u5316\u57ce\u7684\u884c\u52a8\uff0c\u4f60\u4ea4\u4e0d\u51fa\u4eba\uff0c\u53c8\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u867d\u660e\u77e5\u4e0d\u6613\u5e94\u4ed8\uff0c\u5374\u4e0d\u5f97\u4e0d\u6545\u505a\u9547\u5b9a\uff0c\u6de1\u7136\u7b11\u9053\uff1a\u201c\u4e25\u59d1\u5a18\u653e\u5fc3\uff0c\u6211\u4e00\u5207\u81ea\u6709\u5b89 \u6392\u3002\u201d \u3000\u3000\u4e25\u5982\u51b0\u4e0d\u4fbf\u4e45\u7559\uff0c\u968f\u5373\u5e26\u7740\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\uff0c\u4e58\u591c\u8d76\u56de\u80b2\u5316\u57ce\u3002 \u3000\u3000\u8def\u4e0a\uff0c\u4e09\u4eba\u90fd\u85cf\u7740\u4eba\u76ae\u9762\u5177\uff0c\u4ee5\u514d\u88ab\u4eba\u8bc6\u7834\u3002 \u3000\u3000\u5230\u8fbe\u80b2\u5316\u57ce\u5df2\u662f\u4e09\u66f4\u8fc7\u540e\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u88ab\u62db\u5f85\u5728\u4e00\u5904\u4e34\u65f6\u4f4f\u6240\u3002 \u3000\u3000\u4e24\u4eba\u521a\u7761\u4e0b\u4e0d\u4e45\uff0c\u4fbf\u5929\u5df2\u5927\u4eae\u3002 \u3000\u3000\u5929\u4eae\u540e\u80b2\u5316\u57ce\u4e3b\u4e25\u5bd2\u4eb2\u81ea\u8fc7\u6765\u63a2\u89c6\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u662f\u7b2c\u4e00\u6b21\u5230\u80b2\u5316\u57ce\uff0c\u548c\u4e25\u5bd2\u4e5f\u662f\u7b2c\u4e00\u6b21\u89c1\u9762\uff0c\u5728\u5cb3\u5c0f\u98de\u5f15\u89c1\u4e0b\uff0c\u5927\u793c\u5411\u4e25\u5bd2\u62dc\u89c1\u3002 \u3000\u3000\u65e9\u9910\u5f00\u5728\u5927\u5385\uff0c\u5173\u7433\u3001\u65b9\u5929\u94ce\u3001\u4f55\u6167\u4ed9\u5168\u5230\u4e86\u3002 \u3000\u3000\u4e25\u5bd2\u5c11\u4e0d\u5f97\u53c8\u5f15\u7740\u5cb3\u5c0f\u98de\u62dc\u89c1\u5173\u7433\u548c\u65b9\u5929\u94ce\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e5f\u5c06\u8881\u5c0f\u9e64\u4e3a\u6bcd\u4eb2\u5f15\u89c1\u3002 \u3000\u3000\u5173\u7433\u548c\u65b9\u5929\u94ce\u867d\u7136\u5df2\u4f11\u517b\u4e86\u597d\u591a\u5929\uff0c\u4f46\u7cbe\u795e\u4f53\u529b\u4ecd\u672a\u590d\u539f\uff0c\u4f46\u4ed6\u4eec\u4e3a\u4e86\u8fce\u6218\u7687\u5143\u6559\uff0c\u4e5f \u51b3\u5b9a\u5728\u5fc5\u8981\u65f6\u52c9\u5f3a\u51fa\u6218\u3002 \u3000\u3000\u80b2\u5316\u57ce\u4ece\u6628\u591c\u8d77\uff0c\u4fbf\u5f00\u59cb\u4e25\u5bc6\u6212\u5907\uff0c\u6574\u5ea7\u57ce\u6c60\uff0c\u5b8c\u5168\u8fdb\u5165\u5907\u6218\u72b6\u6001\u3002 \u3000\u3000\u597d\u5728\u4e25\u5bd2\u6709\u5929\u7136\u5c4f\u969c\u53ef\u51ed\uff0c\u56e0\u4e3a\u80b2\u5316\u57ce\u540e\u9762\u4fbf\u662f\u7edd\u58c1\u5343\u4ede\u7684\u9ad8\u5c71\uff0c\u800c\u4e14\u6709\u4e00\u534a\u662f\u5728\u5f00\u51ff \u8fc7\u7684\u5c71\u8179\u91cc\uff0c\u9732\u5728\u5916\u9762\u7684\uff0c\u4e0d\u53ca\u4e00\u534a\uff0c\u9632\u5b88\u8d77\u6765\u53ef\u4ee5\u51cf\u5c11\u4e0d\u5c11\u5175\u529b\u3002 \u3000\u3000\u5728\u5168\u57ce\u7684\u5404\u579b\u53e3\uff0c\u4ee5\u53ca\u91cd\u8981\u636e\u70b9\uff0c\u90fd\u57cb\u4f0f\u4e86\u5f13\u7bad\u624b\uff0c\u654c\u65b9\u8981\u60f3\u722c\u5899\u8fdb\u88ad\uff0c\u52bf\u5fc5\u4ed8\u51fa\u6781\u4e3a \u60e8\u91cd\u7684\u4ee3\u4ef7\u3002 \u3000\u3000\u65e9\u9910\u8fc7\u540e\uff0c\u57ce\u5185\u6240\u6709\u9ad8\u624b\uff0c\u5728\u4e25\u5bd2\u7684\u7387\u9886\u4e0b\uff0c\u5df2\u6765\u5230\u57ce\u95e8\u5165\u53e3\u5904\u7684\u95e8\u623f\u3002 \u3000\u3000\u8fd9\u91cc\u539f\u662f\u62a4\u57ce\u5f1f\u5144\u4eec\u7684\u4f4f\u5bbf\u548c\u4f11\u606f\u4e4b\u6240\uff0c\u6b64\u523b\u5df2\u6574\u7406\u51fa\u6765\u4f9b\u7fa4\u8c6a\u5f85\u654c\u4e4b\u7528\u3002 \u3000\u3000\u8fd9\u4e9b\u9ad8\u624b\u4eec\uff0c\u5305\u62ec\u57ce\u4e3b\u4e25\u5bd2\uff0c\u4ee5\u53ca\u4e25\u5982\u51b0\u3001\u5468\u6d77\u5c71\u3001\u5173\u7433\u3001\u65b9\u5929\u94ce\u3001\u4f55\u6167\u4ed9\u3001\u8881\u5c0f\u9e64\u3001 \u5cb3\u5c0f\u98de\u548c\u80b2\u5316\u57ce\u539f\u6709\u7684\u51e0\u540d\u7edf\u9886\u4e0e\u9999\u4e3b\u7ea7\u4eba\u7269\u3002\u5728\u80b2\u5316\u57ce\u6765\u8bf4\uff0c\u53ef\u8c13\u7cbe\u9510\u5c3d\u51fa\uff0c\u6beb\u65e0\u4fdd\u7559\u3002 \u3000\u3000\u5f53\u57ce\u5185\u6d3e\u51fa\u5728\u5916\u7684\u76d1\u89c6\u54e8\u64a4\u56de\u65f6\uff0c\u5728\u57ce\u697c\u4e0a\u8d1f\u8d23\u8b66\u6212\u7684\u4e00\u540d\u7edf\u9886\u968f\u5373\u8fdb\u5165\u95e8\u623f\u5411\u4e25\u5bd2\u7980 \u62a5\uff01\u7687\u5143\u6559\u7684\u4eba\u9a6c\u5df2\u7ecf\uff0a\u8fd1\u80b2\u5316\u57ce\u3002 \u3000\u3000\u4e25\u5bd2\u5429\u5490\u6253\u5f00\u57ce\u95e8\uff0c\u5148\u884c\u7387\u9886\u7fa4\u8c6a\u8fce\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5927\u7ea6\u76cf\u8336\u5de5\u592b\u4e4b\u540e\uff0c\u7687\u5143\u6559\u7684\u4eba\u9a6c\uff0c\u679c\u7136\u6d69\u6d69\u8361\u8361\u800c\u6765\uff0c\u5927\u961f\u4eba\u9a6c\uff0c\u4e0d\u4e0b\u767e\u4eba\u4e4b\u591a\uff0c\u4e2d \u95f4\u4e00\u9876\u9ec4\u8272\u5c0f\u8f7f\uff0c\u4e0d\u6d88\u8bf4\u90a3\u662f\u7687\u5143\u6559\u4e3b\u767d\u837b\u3002 \u3000\u3000\u9ec4\u8272\u5c0f\u8f7f\u5728\u8ddd\u80b2\u5316\u57ce\u4eba\u9a6c\u6570\u4e08\u5916\u505c\u4e0b\uff0c\u767d\u837b\u63ed\u5e18\u800c\u51fa\uff0c\u767e\u4f59\u4eba\u9a6c\u5728\u5979\u5de6\u53f3\u53ca\u8eab\u540e\u5c31\u5730\u6563 \u5f00\u3002 \u3000\u3000\u767d\u837b\u9762\u8272\u5982\u7f69\u5bd2\u971c\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4e25\u5bd2\uff0c\u672c\u6559\u4e3b\u7ed9\u4f60\u4e00\u6b21\u6700\u540e\u673a\u4f1a\uff0c\u82e5\u80af\u73b0\u5728\u6295\u964d\uff0c\u8fd8 \u53ef\u4ee5\u514d\u9664\u4e00\u6b7b\uff0c\u4e0d\u7136\uff0c\u672c\u6559\u4e3b\u6325\u519b\u6740\u8fdb\u57ce\u53bb\uff0c\u5fc5\u5b9a\u9e21\u72ac\u4e0d\u7559\uff0c\u7247\u74e6\u65e0\u5b58\uff0c\u5230\u90a3\u65f6\u53ea\u6015\u540e\u6094\u5c31 \u6765\u4e0d\u53ca\u4e86\uff01\u201d \u3000\u3000\u767d\u837b\u76f4\u5230\u8bf4\u5b8c\u8bdd\u540e\uff0c\u624d\u53d1\u73b0\u5bf9\u65b9\u9635\u4e2d\uff0c\u5c45\u7136\u6709\u5173\u7433\u548c\u65b9\u5929\u94ce\u4ee5\u53ca\u4f55\u6167\u4ed9\u5728\u5185\u3002 \u3000\u3000\u8fd9\u51e0\u4eba\u5982\u4eca\u4e3a\u4e25\u5bd2\u6240\u7528\uff0c\u600e\u4e0d\u4ee4\u5979\u4e3a\u4e4b\u6c14\u7ed3\u3002 \u3000\u3000\u5979\u4fa7\u8138\u671b\u4e86\u7ad9\u5728\u8eab\u65c1\u7684\u603b\u62a4\u6cd5\u79e6\u69d0\u4e00\u773c\u9053\uff1a\u201c\u73b0\u5728\u5c31\u7531\u4f60\u8c03\u5175\u9063\u5c06\uff01\u201d \u3000\u3000\u79e6\u69d0\u5148\u671b\u5411\u4e25\u5bd2\u559d\u9053\uff1a\u201c\u4e25\u57ce\u4e3b\uff0c\u521a\u624d\u6559\u4e3b\u7684\u8bdd\u4f60\u542c\u5230\u6ca1\u6709\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u51b7\u7b11\u9053\uff1a\u201c\u8001\u592b\u5e0c\u671b\u5979\u6700\u597d\u5c11\u8bf4\u5e9f\u8bdd\uff01\u201d \u3000\u3000\u79e6\u69d0\u4e24\u592a\u9633\u7a74\u62bd\u6410\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u597d\uff0c\u672c\u5ea7\u7acb\u523b\u5c31\u8981\u4f60\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff01\u201d \u3000\u3000\u4e25\u5bd2\u56de\u8eab\u9053\uff1a\u201c\u5404\u4f4d\u6709\u8c01\u5148\u51fa\u9635\u8fce\u6218\uff1f\u201d \u3000\u3000\u4e00\u540d\u767d\u53d1\u767d\u7709\u7684\u8001\u8005\uff0c\u5e94\u58f0\u800c\u51fa\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4eba\u662f\u6234\u7740\u4eba\u76ae\u9762\u5177\u7684\u8881\u5c0f\u9e64\u3002 \u3000\u3000\u8fd9\u9762\u5177\u539f\u4e3a\u9a6c\u662d\u96c4\u6240\u6709\uff0c\u7531\u5cb3\u5c0f\u98de\u5f97\u5230\u540e\u8d60\u7ed9\u4e86\u8881\u5c0f\u9e64\uff0c\u8881\u5c0f\u9e64\u66fe\u6234\u7740\u8fd9\u9762\u5177\u9a97\u8fc7\u51e4\u5ae3 \u7ea2\u4e00\u6b21\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u5bf9\u65b9\u81ea\u767d\u837b\u4ee5\u4e0b\uff0c\u90fd\u6709\u4e9b\u5403\u60ca\uff0c\u56e0\u4e3a\u4ed6\u4eec\u90fd\u4e0d\u8ba4\u8bc6\u6b64\u4eba\u662f\u8c01\u3002 \u3000\u3000\u5bf9\u65b9\u4e0d\u8ba4\u8bc6\u5cb3\u5c0f\u98de\uff0c\u8fd8\u8bf4\u5f97\u8fc7\u53bb\uff0c\u56e0\u4e3a\u5cb3\u5c0f\u98de\u6234\u4e0a\u5b83\u7684\u9762\u5177\uff0c\u4ecd\u662f\u4e2a\u5e74\u8f7b\u4eba\uff0c\u4ed6\u4eec\u8ba4\u4e3a \u4e5f\u8bb8\u662f\u4e25\u5bd2\u65b0\u57f9\u690d\u8d77\u6765\u7684\u9ad8\u624b\uff0c\u4f46\u50cf\u8881\u5c0f\u9e64\u6b64\u523b\u626e\u6210\u7684\u767d\u53d1\u767d\u7709\u8001\u8005\u4e0d\u8ba4\u8bc6\uff0c\u90a3\u5c31\u672a\u514d\u8bf4\u4e0d \u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u79e6\u69d0\u8fd8\u6ca1\u6765\u5f97\u53ca\u5429\u5490\u7531\u8c01\u51fa\u6218\uff0c\u7a81\u89c1\u4eba\u4e1b\u4e2d\u95ea\u51fa\u4e86\u5bb5\u5173\u7edf\u9886\u51e4\u5ae3\u7ea2\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u624b\u6a2a\u91d1\u6708\u5200\uff0c\u671b\u7740\u79e6\u69d0\u62b1\u5200\u4e00\u793c\u9053\uff1a\u201c\u7980\u603b\u5ea7\uff0c\u8fd9\u4e00\u9635\u5c31\u7531\u5351\u804c\u63a5\u4e0b\u4e86\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e07\u6ca1\u6599\u5230\u5bf9\u65b9\u63a5\u6218\u7684\u7adf\u662f\u51e4\u5ae3\u7ea2\uff0c\u4e0d\u7ba1\u4ed6\u6068\u51e4\u5ae3\u7ea2\u6068\u5230\u4ec0\u4e48\u7a0b\u5ea6\uff0c\u5185\u5fc3\u5374\u5b9e\u5728\u4e0d \u60f3\u548c\u5979\u4ea4\u624b\u3002 \u3000\u3000\u800c\u51e4\u5ae3\u7ea2\u5374\u662f\u56e0\u4e3a\u5979\u5df2\u8ba4\u51fa\u6b64\u4eba\u662f\u9a6c\u662d\u96c4\uff0c\u5728\u5927\u611f\u60ca\u5947\u4e4b\u4e0b\uff0c\u624d\u60c5\u4e0d\u81ea\u7981\u60f3\u4e3b\u52a8\u63a5\u6218\u3002 \u3000\u3000\u5979\u65e9\u5df2\u542c\u8bf4\u9a6c\u662d\u96c4\u88ab\u80b2\u5316\u57ce\u4e3b\u4e25\u5211\u6bd2\u6253\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u597d\u5f97\u8fd9\u4e48\u5feb\uff1f\u80b2\u5316\u57ce\u4e3b\u53c8\u4e3a\u4ec0\u4e48\u80af\u628a \u4ed6\u653e\u51fa\u6765\u8fce\u6218\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u8d70\u8fd1\u8881\u5c0f\u9e64\uff0c\u6545\u610f\u6012\u76ee\u53f1\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7ba1\u8001\u592b\u662f\u4ec0\u4e48\u4eba\uff1f\u8981\u6253\u5c31\u6253\uff0c\u4e0d\u6253\u5c31\u9000\u4e0b\uff0c\u7528\u4e0d\u7740\u591a\u8bb2\u5e9f\u8bdd\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u8fd8\u771f\u662f\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u5979\u4e0d\u6562\u591a\u95ee\uff0c\u4ee5\u514d\u88ab\u6559\u4e3b\u548c\u603b\u62a4\u6cd5\u8ba4\u51fa\u7834\u7efd\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u62a1\u8d77\u91d1\u6708\u5200\u4fbf\u51fa\u62db\u653b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u7acb\u5373\u4ed7\u5251\u8fce\u6218\uff0c \u3000\u3000\u8d77\u521d\uff0c\u51e4\u5ae3\u7ea2\u8fd8\u4e0d\u4fbf\u5c3d\u529b\u65bd\u51fa\uff0c\u4ee5\u514d\u4f24\u4e86\u9a6c\u662d\u96c4\u3002 \u3000\u3000\u4f46\u5341\u51e0\u62db\u8fc7\u53bb\u540e\uff0c\u5979\u7ec8\u4e8e\u89c9\u51fa\u6b64\u4eba\u4e0d\u662f\u9a6c\u662d\u96c4\uff0c\u4f46\u5251\u6cd5\u8def\u6570\u5374\u53c8\u9887\u4e3a\u76f8\u4f3c\u3002 \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u8881\u5c0f\u9e64\u548c\u9a6c\u662d\u96c4\u90fd\u540c\u51fa\u5e90\u4e91\u95e8\u4e0b\u7684\u7f18\u6545\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u8d77\u521d\u4e5f\u662f\u4e0d\u613f\u4f24\u7740\u51e4\u5ae3\u7ea2\uff0c\u4e03\u3001\u516b\u62db\u8fc7\u540e\uff0c\u5df2\uff0a\u5f97\u4ed6\u4e0d\u5f97\u4e0d\u5c3d\u5c55\u6240\u5b66\u65bd\u4e3a\u3002 \u3000\u3000\u53cc\u53cc\u8db3\u8db3\u8fc7\u4e86\u767e\u62db\u4ee5\u4e0a\uff0c\u7adf\u662f\u4e00\u76f4\u5206\u4e0d\u51fa\u80dc\u8d1f\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u53ea\u542c\u79e6\u69d0\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u548c\u51e4\u5ae3\u7ea2\u5404\u81ea\u62da\u529b\u653b\u51fa\u4e09\u62db\uff0c\u7136\u540e\u53cc\u53cc\u5411\u540e\u8dc3\u5f00\u3002 \u3000\u3000\u79e6\u69d0\u56de\u8eab\u53eb\u9053\uff1a\u201c\u4e94\u5173\u7684\u4eba\u9a6c\u5230\u524d\u9762\u6765\uff01\u201d \u3000\u3000\u987f\u65f6\uff0c\u4e0d\u4f46\u8881\u5c0f\u9e64\u5403\u60ca\uff0c\u8fde\u5cb3\u5c0f\u98de\u4e5f\u5728\u8fd9\u4e00\u77ac\u95f4\u5446\u4f4f\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u5df2\u770b\u5230\u82b1\u7389\u9e9f\u7387\u540c\u4e01\u6d9b\u548c\u7518\u9716\u5927\u6b65\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u4eec\u5403\u60ca\u7684\u662f\u82b1\u4e94\u9cde\u6b64\u523b\u7684\u5904\u5883\u3002 \u3000\u3000\u679c\u7136\uff0c\u79e6\u69d0\u7684\u89c6\u7ebf\u7f13\u7f13\u4ece\u82b1\u7389\u9e9f\u7b49\u4eba\u8eab\u4e0a\u626b\u8fc7\uff0c\u63a5\u7740\u4e24\u773c\u7728\u52a8\u4e86\u51e0\u4e0b\u9053\uff1a\u201c\u82b1\u526f\u603b\u9547\uff0c \u8fd8\u6709\u4e24\u540d\u4f7f\u8005\u4e3a\u4ec0\u4e48\u6ca1\u6765\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u5351\u804c\u4eca\u5929\u65e9\u4e0a\u624d\u5949\u8c15\u7387\u9886\u4e94\u5173\u9ad8\u624b\u534f\u653b\u80b2\u5316\u57ce\uff0c\u5f53\u65f6\u53e6\u4e24\u540d\u4f7f\u8005\u6b63\u597d\u5df2\u51fa \u53d1\u5de1\u5173\u53bb\u4e86\uff0c\u6240\u4ee5\u53ea\u80fd\u5e26\u540c\u4e24\u540d\u526f\u4f7f\u8005\u524d\u6765\u3002\u201d \u3000\u3000\u79e6\u69d0\u9053\uff1a\u201c\u8c37\u53e3\u5df2\u7ecf\u5c01\u95ed\uff0c\u8fd8\u5de1\u7684\u4ec0\u4e48\u5173\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u8c37\u53e3\u867d\u7136\u5df2\u7ecf\u5c01\u95ed\uff0c\u4f46\u5404\u5173\u7edf\u9886\uff0c\u591a\u534a\u5df2\u5949\u8c03\u6765\u653b\u57ce\uff0c\u5404\u5173\u5728\u7fa4\u9f99\u65e0\u9996\u4e4b \u4e0b\uff0c\u66f4\u4e0d\u53ef\u5ffd\u89c6\u5de1\u5173\u3002\u201d \u3000\u3000\u79e6\u69d0\u56e0\u89c1\u82b1\u7389\u9e9f\u8bf4\u5f97\u6709\u7406\uff0c\u4e0d\u4fbf\u8d23\u5907\uff0c\u89c6\u7ebf\u79fb\u5230\u4e01\u6d9b\u548c\u7518\u9716\u4e24\u4eba\u8eab\u4e0a\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u7684\u6b66 \u529f\u5982\u4f55\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u4ed6\u4eec\u867d\u53ca\u4e0d\u5f97\u90a3\u4e24\u540d\u4f7f\u8005\uff0c\u4f46\u5728\u6765\u8c37\u524d\uff0c\u4e5f\u662f\u6709\u5934\u6709\u8138\u7684\u4eba\u7269\uff0c\u8eab\u624b\u81ea\u7136 \u4e0d\u5dee\u3002\u201d \u3000\u3000\u79e6\u69d0\u76ae\u7b11\u8089\u4e0d\u7b11\u9053\uff1a\u201c\u5c31\u8ba9\u4ed6\u4eec\u51fa\u9635\u8bd5\u8bd5\uff0c\u6559\u4e3b\u548c\u672c\u5ea7\u4e5f\u53ef\u4eb2\u773c\u770b\u770b\u8fd9\u6279\u65b0\u8fd1\u8fdb\u5165\u5929\u8c37 \u7684\u4eba\u7a76\u7adf\u53ef\u5426\u6d3e\u4e0a\u7528\u573a\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u968f\u5373\u5429\u5490\u9053\uff1a\u201c\u7518\u526f\u4f7f\u8005\u5148\u51fa\u9635\u4f1a\u4e00\u4f1a\uff01\u201d \u3000\u3000\u7518\u9716\u65e9\u770b\u51fa\u8881\u5c0f\u9e64\u7684\u6b66\u529f\u6bd4\u81ea\u5df1\u9ad8\u5f97\u591a\uff0c\u96be\u514d\u602f\u9635\uff0c\u5374\u53c8\u4e0d\u6562\u4e0d\u9075\uff0c\u53ea\u5f97\u62d4\u51fa\u63d2\u5728\u80cc\u540e \u7684\u4e00\u5bf9\u94c1\u7b14\uff0c\u8dc3\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u6b64\u65f6\u53cd\u800c\u6709\u4e9b\u8e0c\u8e87\u8d77\u6765\uff0c\u4ed6\u5b9e\u5728\u4e0d\u5fcd\u4f24\u4e86\u7518\u9716\uff0c\u5f53\u4e0b\uff0c\u53ea\u597d\u6a2a\u5251\u95ee\u9053\uff1a\u201c\u5c0a\u9a7e\u662f \u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u7518\u9716\u731b\u7740\u80c6\u5b50\u9053\uff1a\u201c\u5728\u4e0b\u4e94\u5173\u526f\u4f7f\u8005\u7518\u9716\u4fbf\u662f\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u5de6\u624b\u6325\u4e86\u6325\u9053\uff1a\u201c\u4e00\u540d\u5c0f\u5c0f\u5de1\u5173\u526f\u4f7f\u8005\uff0c\u5c82\u662f\u8001\u592b\u7684\u5bf9\u624b\uff0c\u56de\u53bb\u56de\u53bb\uff01\u201d \u3000\u3000\u7518\u9716\u5e72\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u672c\u526f\u4f7f\u8005\u5949\u547d\u51fa\u6765\u62ff\u4f60\uff0c\u600e\u53ef\u968f\u4fbf\u56de\u53bb\u3002\u201d \u3000\u3000\u201c\u65e2\u7136\u4e0d\u8bc6\u62ac\u4e3e\uff0c\u8001\u592b\u5c31\u7ed9\u4f60\u70b9\u5389\u5bb3\u5c1d\u5c1d\uff01\u201d \u3000\u3000\u7518\u9716\u8dc3\u8eab\u76f4\u5411\u8881\u5c0f\u9e64\u6251\u53bb\uff0c\u4e24\u679d\u94c1\u7b14\uff0c\u4e0a\u6233\u9762\u95e8\uff0c\u4e0b\u622e\u5fc3\u7a9d\uff0c\u52a8\u4f5c\u7167\u6837\u4e5f\u5feb\u5f97\u4ee4\u4eba\u76ee\u4e0d \u6687\u63a5\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u6545\u610f\u5148\u4e0d\u6c42\u80dc\uff0c\u6765\u4e2a\u53ea\u5b88\u4e0d\u653b\uff0c\u800c\u4e14\u8fde\u8fde\u5411\u540e\u907f\u9000\u3002 \u3000\u3000\u4e00\u8fde\u4e03\u516b\u62db\u8fc7\u53bb\uff0c\u7adf\u662f\u4e2a\u4e0d\u80dc\u4e0d\u8d25\u4e4b\u5c40\u3002 \u3000\u3000\u7687\u5143\u6559\u65b9\u9762\u7684\u9ad8\u624b\uff0c\u89c1\u7518\u9716\u4e0d\u8fc7\u662f\u4e94\u5173\u7684\u4e00\u540d\u5de1\u5173\u526f\u4f7f\u8005\uff0c\u5c45\u7136\u6709\u6b64\u8eab\u624b\uff0c\u83ab\u4e0d\u5bf9\u4ed6\u53e6 \u773c\u76f8\u770b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u89c1\u8881\u5c0f\u9e64\u8fd9\u79cd\u6253\u6cd5\uff0c\u4e0d\u89c9\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u8001\u524d\u8f88\u9000\u4e0b\uff0c\u5f85\u5728\u4e0b\u63a5\u4ed6\u51e0\u62db\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u5e94\u58f0\u9000\u56de\u9635\u53bb\u3002 \u3000\u3000\u7518\u9716\u6b63\u5728\u5f97\u610f\u4e4b\u9645\uff0c\u53c8\u89c1\u5cb3\u5c0f\u98de\u8eab\u6750\u77ee\u5c0f\uff0c\u66f4\u4e0d\u628a\u4ed6\u653e\u5728\u773c\u91cc\uff0c\u8dc3\u8eab\u518d\u5411\u5cb3\u5c0f\u98de\u6251\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u4ed6\u865a\u5e94\u4e86\u51e0\u62db\uff0c\u7136\u540e\u4e00\u62db\u201c\u6a2a\u626b\u843d\u53f6\u201d\uff0c\u659c\u5288\u8fc7\u53bb\u3002 \u3000\u3000\u53ea\u542c\u201c\u5f53\u3001\u5f53\u201d\u4e24\u58f0\uff0c\u7518\u9716\u7684\u4e24\u679d\u94c1\u7b14\uff0c\u5168\u5df2\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u51fa\u624b\uff0c\u65f6\u95f4\u90e8\u4f4d\u62ff\u634f\u5f97\u5f53\u771f\u6070\u5230\u597d\u5904\uff0c\u82e5\u5251\u950b\u518d\u524d\u8fdb\u4e09\u5bf8\uff0c\u7518\u9716\u7684\u53cc\u624b\u52bf\u5fc5\u5341\u6307 \u5168\u65ad\u3002 \u3000\u3000\u7518\u9716\u5413\u5f97\u9b42\u98de\u80c6\u88c2\uff0c\u8fde\u5730\u4e0a\u7684\u94c1\u7b14\u90fd\u987e\u4e0d\u5f97\u6361\u8d77\uff0c\u4fbf\u9762\u65e0\u4eba\u8272\u7684\u98de\u8eab\u9003\u4e86\u56de\u53bb\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u518d\u5429\u5490\u9053\uff1a\u201c\u4e01\u526f\u4f7f\u8005\u51fa\u6218\uff01\u201d \u3000\u3000\u4ed6\u4e4b\u6240\u4ee5\u4ecd\u547d\u4e01\u6d9b\u51fa\u6218\uff0c\u81ea\u7136\u662f\u5df2\u77e5\u5cb3\u5c0f\u98de\u4e0d\u81f4\u4f24\u4ed6\u3002 \u3000\u3000\u4e01\u6d9b\u5374\u4f3c\u4e4e\u4e0d\u4fe1\u90aa\uff0c\u62a1\u8d77\u539a\u80cc\u9b3c\u5934\u5200\u4fbf\u8dc3\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u51b7\u7b11\u4e86\u51e0\u58f0\uff0c\u95ee\u9053\uff1a\u201c\u5c0a\u9a7e\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u543c\u9053\uff1a\u201c\u5728\u4e0b\u4e5f\u662f\u4e94\u5173\u526f\u4f7f\u8005\uff0c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u6211\u770b\u4e0d\u600e\u4e48\u6837\uff0c\u4e0a\u5427\uff01\u201d \u3000\u3000\u4e01\u6d9b\u539a\u80cc\u9b3c\u5934\u5200\u4e00\u4e3e\uff0c\u5927\u6709\u96f7\u9706\u4e07\u94a7\u4e4b\u52bf\uff0c\u5939\u7740\u52b2\u98ce\u5f53\u5934\u76f4\u5288\u800c\u4e0b\u3002 \u3000\u3000\u5c82\u77e5\u5f53\u4ed6\u62db\u5f0f\u9012\u6ee1\u4e4b\u540e\uff0c\u5cb3\u5c0f\u98de\u7684\u4eba\u5f71\u5374\u5df2\u4e0d\u89c1\u3002 \u3000\u3000\u4e01\u6d9b\u5927\u9a87\u4e4b\u4e0b\uff0c\u521a\u8981\u56de\u8eab\u627e\u4eba\uff0c\u4e0d\u60f3\u540e\u80cc\u65e9\u4e2d\u4e86\u4e00\u811a\u3002 \u3000\u3000\u8fd9\u4e00\u811a\u529b\u9053\u5947\u91cd\uff0c\u8e22\u5f97\u4e01\u6d9b\u5f53\u573a\u6454\u5728\u5730\u4e0a\uff0c\u63a5\u8fde\u4e24\u4e2a\u201c\u61d2\u9a74\u6253\u6eda\u201d\uff0c\u624d\u52c9\u5f3a\u7a33\u4f4f\u3002 \u3000\u3000\u5f53\u4ed6\u521a\u521a\u722c\u8d77\uff0c\u9762\u988a\u4e0a\u7adf\u53c8\u4e2d\u4e86\u4e24\u8bb0\u91cd\u63b4\u3002 \u3000\u3000\u5947\u602a\u7684\u662f\u4ed6\u4ecd\u672a\u770b\u5230\u5bf9\u65b9\u7684\u4eba\u5f71\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5de6\u53f3\u5f00\u5f13\u7684\u4e24\u638c\uff0c\u5cb3\u5c0f\u98de\u662f\u5728\u4e01\u6d9b\u80cc\u540e\u8dc3\u8eab\u800c\u8d77\u63b4\u4e0b\u7684\u3002 \u3000\u3000\u6b64\u523b\u7684\u4e01\u6d9b\uff0c\u5df2\u662f\u88ab\u6253\u5f97\u8ff7\u8ff7\u7cca\u7cca\uff0c\u6655\u5934\u8f6c\u5411\uff0c\u53ea\u987e\u62a1\u7740\u539a\u80cc\u9b3c\u5934\u5200\uff0c\u76f2\u76ee\u7684\u4e71\u6740\u4e71\u780d\uff0c \u5f88\u50cf\u4e00\u53ea\u53d1\u4e86\u75af\u7684\u72d7\u718a\u3002 \u3000\u3000\u5ffd\u542c\u201c\u556a\u3001\u556a\u201d\u53c8\u662f\u4e24\u58f0\u66b4\u54cd\u3002 \u3000\u3000\u8fd9\u4e24\u638c\u5cb3\u5c0f\u98de\u5c45\u7136\u662f\u5728\u7a7a\u4e2d\u5934\u4e0b\u811a\u4e0a\u63b4\u4e0b\u7684\u3002 \u3000\u3000\u4e01\u6d9b\u88ab\u6253\u5f97\u8d8a\u53d1\u660f\u4e86\u8111\u888b\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u5fc3\u91cc\u6709\u6570\uff0c\u5cb3\u5c0f\u98de\u6240\u4ee5\u628a\u4e01\u6d9b\u6574\u5f97\u5982\u6b64\u4e4b\u60e8\uff0c\u4e0d\u5916\u662f\u4e01\u6d9b\u66fe\u5728\u79e6\u69d0\u9762\u524d\u544a\u72b6\u4e4b\u6545\uff0c \u5f53\u7136\uff0c\u4ed6\u5e76\u4e0d\u77e5\u9053\u4e01\u6d9b\u7adf\u7136\u8fde\u51e4\u5ae3\u7ea2\u90fd\u59d8\u4e0a\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4e24\u65c1\u89c2\u6218\u7684\u4eba\uff0c\u591a\u534a\u5df2\u88ab\u5cb3\u5c0f\u98de\u9b3c\u795e\u83ab\u6d4b\u7684\u8eab\u624b\u60ca\u5f97\u76ee\u77aa\u53e3\u5446\u3002 \u3000\u3000\u4f46\u6700\u60ca\u559c\u7684\uff0c\u83ab\u8fc7\u4e8e\u4f55\u6167\u4ed9\uff0c\u56e0\u4e3a\u8fd9\u662f\u5979\u7b2c\u4e00\u6b21\u770b\u5230\u5cb3\u5c0f\u98de\u6709\u5982\u6b64\u60ca\u4eba\u7684\u8eab\u624b\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u603b\u7b97\u51fa\u4e86\u4e00\u53e3\u6c14\uff0c\u51b3\u5b9a\u5230\u6b64\u4e3a\u6b62\uff0c\u968f\u5373\u5411\u540e\u8dc3\u9000\u4e08\u4f59\u4e4b\u5916\uff0c\u4ed7\u5251\u800c\u7acb\u3002 \u3000\u3000\u79e6\u69d0\u53f1\u9053\uff1a\u201c\u7b80\u76f4\u628a\u7687\u5143\u6559\u7684\u4eba\u5168\u4e22\u5149\u4e86\uff0c\u82b1\u526f\u603b\u9547\u8fd8\u4e0d\u5feb\u53eb\u8fd9\u540d\u59d3\u4e01\u7684\u526f\u4f7f\u8005\u9000\u56de \u6765\uff01\u201d \u3000\u3000\u4e01\u6d9b\u9000\u56de\u540e\u8fb9\uff0c\u79e6\u69d0\u9053\uff1a\u201c\u82b1\u526f\u603b\u9547\uff0c\u73b0\u5728\u8be5\u4f60\u51fa\u624b\u4e86\uff01\u201d \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u4e0d\u4f46\u82b1\u7389\u9e9f\u5927\u611f\u4e3a\u96be\uff0c\u5cb3\u5c0f\u98de\u66f4\u662f\u6709\u4e9b\u4e0d\u77e5\u6240\u63aa\u3002 \u3000\u3000\u81ea\u53e4\u53ca\u4eca\uff0c\u90a3\u6709\u7236\u5b50\u5bf9\u9635\u7684\u9053\u7406\u3002 \u3000\u3000\u597d\u5728\u5c31\u5728\u8fd9\u65f6\uff0c\u7a81\u89c1\u4e00\u540d\u8eab\u6750\u77ee\u80d6\u8179\u633a\u5982\u9f13\u7684\u50e7\u4eba\uff0c\u624b\u6a2a\u94c1\u7985\u6756\u7531\u6570\u4e08\u4e4b\u5916\u50cf\u4ece\u5929\u800c\u964d \u822c\u8dc3\u4e86\u8fc7\u6765\uff0c\u843d\u5730\u540e\u5927\u559d\u9053\uff1a\u201c\u5f85\u6d12\u5bb6\u6765\u4f1a\u4f1a\u8fd9\u5c0f\u5b50\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u4e0d\u7531\u6697\u5403\u4e00\u60ca\uff0c\u7acb\u5373\u8ba4\u51fa\u8fd9\u50e7\u4eba\u662f\u6b66\u5173\u4e0a\u7684\u94c1\u5934\u9640\uff0c\u5f53\u521d\u4ed6\u4e0e\u8881\u5c0f\u9e64\u7b49\u56db\u4eba\u901a\u8fc7 \u6b66\u5173\u65f6\uff0c\u6b63\u662f\u5728\u6b64\u4eba\u624b\u4e0b\u53d7\u6d4b\u3002 \u3000\u3000\u5176\u5b9e\u4ed6\u5e76\u975e\u60e7\u6015\u94c1\u5934\u9640\uff0c\u800c\u662f\u6599\u60f3\u5230\u6b66\u5173\u7edf\u9886\u591a\u80cc\u5929\u7fc1\u5e9e\u821f\u5fc5\u5b9a\u4e5f\u5df2\u524d\u6765\uff0c\u82e5\u5e9e\u821f\u5728\u573a\uff0c \u8fd9\u573a\u5927\u6218\u7684\u7ed3\u679c\uff0c\u90a3\u5c31\u5b9e\u5728\u4e0d\u582a\u8bbe\u60f3\u4e86\u3002 \u3000\u3000\u4f55\u51b5\uff0c\u636e\u4ed6\u6240\u77e5\uff0c\u5728\u5e9e\u821f\u624b\u4e0b\uff0c\u5171\u6709\u5341\u540d\u7edd\u9876\u9ad8\u624b\uff0c\u5176\u4ed6\u4e5d\u540d\uff0c\u6b66\u529f\u90fd\u4e0d\u5728\u94c1\u5934\u9640\u4e4b\u4e0b\uff0c \u751a\u81f3\u6709\u6bd4\u94c1\u5934\u9640\u66f4\u9ad8\u7684\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u521a\u8981\u63a5\u6218\uff0c\u8881\u5c0f\u9e64\u5374\u53c8\u8dc3\u4e86\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5f85\u8001\u592b\u63a5\u4ed6\u51e0\u62db\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u53ea\u5f97\u6682\u65f6\u9000\u4e0b\uff0c\u4e00\u9762\u6717\u58f0\u9053\uff1a\u201c\u8fd9\u5934\u9640\u8eab\u624b\u4e0d\u51e1\uff0c\u8001\u524d\u8f88\u8981\u5c0f\u5fc3\u5bf9\u4ed8\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u518d\u5411\u524d\u8d70\u51fa\u51e0\u6b65\uff0c\u7528\u5251\u5c16\u4e00\u6307\u9053\uff1a\u201c\u4f60\u8fd9\u79c3\u9a74\uff0c\u53ef\u662f\u6b66\u5173\u6765\u7684\uff1f\u201d \u3000\u3000\u94c1\u5934\u9640\u543c\u9053\uff1a\u201c\u4f60\u7ba1\u6d12\u5bb6\u662f\u90a3\u91cc\u6765\u7684\uff0c\u8001\u5c0f\u5b50\uff0c\u6d12\u5bb6\u73b0\u5728\u5c31\u628a\u4f60\u8d85\u6e21\u5230\u897f\u5929\u53bb\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u597d\u79c3\u9a74\uff0c\u5c11\u8bf4\u5927\u8bdd\uff0c\u5c0f\u5fc3\u98ce\u5927\u95ea\u4e86\u4f60\u7684\u820c\u5c16\uff01\u201d \u3000\u3000\u94c1\u5934\u9640\u4e0d\u518d\u7b54\u8bdd\uff0c\u62a1\u8d77\u94c1\u7985\u6756\uff0c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u626b\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u522b\u770b\u94c1\u5934\u9640\u8eab\u9ad8\u4e0d\u6ee1\u4e94\u5c3a\uff0c\u4f46\u90a3\u94c1\u7985\u6756\u5374\u8db3\u6709\u4e00\u4e08\uff0c\u8bba\u91cd\u91cf\u4e5f\u4e0d\u4e0b\u767e\u65a4\uff0c\u626b\u51fa\u4e4b\u540e\uff0c\u76f4 \u5982\u6012\u6d9b\u88c2\u5cb8\uff0c\u5927\u6709\u77f3\u7834\u5929\u60ca\u4e4b\u6982\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u81ea\u7136\u4e0d\u6562\u786c\u63a5\uff0c\u88ab\u8feb\u4e4b\u4e0b\uff0c\u53ea\u6709\u4ef0\u8eab\u75be\u9000\u3002 \u3000\u3000\u94c1\u5934\u9640\u5c82\u6562\u653e\u677e\uff0c\u633a\u7740\u5927\u809a\u5b50\u4e14\u8fdb\u4e14\u626b\uff0c\u8f6c\u77ac\u95f4\u4fbf\u626b\u51fa\u4e09\u6756\uff0c\u6bcf\u626b\u4e00\u6756\uff0c\u5468\u8fd1\u4fbf\u4e00\u9635\u98de \u6c99\u8d70\u77f3\uff0c\u5c31\u50cf\u8fde\u5730\u76ae\u4e5f\u522e\u8d77\u4e00\u822c\u3002 \u3000\u3000\u5982\u6b64\u96f7\u9706\u4e07\u94a7\u7684\u653b\u52bf\uff0c\u8881\u5c0f\u9e64\u53ea\u80fd\u51ed\u7740\u7075\u6d3b\u7684\u8eab\u6cd5\u95ea\u907f\uff0c\u6839\u672c\u4e0d\u6562\u51fa\u5251\u67b6\u683c\uff0c\u5426\u5219\u957f\u5251 \u52bf\u5fc5\u88ab\u9707\u51fa\u624b\u53bb\u3002 \u3000\u3000\u94c1\u5934\u9640\u76f4\u5230\u653b\u51fa\u4e03\u516b\u6756\u4ecd\u672a\u4f24\u5230\u5bf9\u65b9\uff0c\u96be\u514d\u4e5f\u6697\u6697\u79f0\u5947\u3002 \u3000\u3000\u4f46\u4ed6\u529b\u5927\u65e0\u7a77\uff0c\u6709\u5982\u4e00\u5177\u673a\u5668\uff0c\u4e0d\u7ba1\u80fd\u5426\u51fb\u4e2d\uff0c\u8fd8\u662f\u4e00\u5473\u731b\u653b\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u88ab\uff0a\u5f97\u53ea\u80fd\u5728\u56db\u5468\u6e38\u8d70\uff0c\u59cb\u7ec8\u627e\u4e0d\u51fa\u8fd8\u51fb\u7684\u673a\u4f1a\u3002 \u3000\u3000\u5c31\u8fd9\u6837\u94c1\u5934\u9640\u76f4\u653b\u51fa\u4e09\u56db\u5341\u62db\uff0c\u4f9d\u7136\u770b\u4e0d\u51fa\u6709\u4ec0\u4e48\u7ed3\u5c40\u3002 \u3000\u3000\u6e10\u6e10\uff0c\u8881\u5c0f\u9e64\u5df2\u662f\u6c57\u6d41\u6d43\u80cc\uff0c\u6c14\u5598\u5401\u5401\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u8ddf\u89c1\u8881\u5c0f\u9e64\u4f3c\u662f\u96be\u4ee5\u6301\u4e45\uff0c\u3000\u4e00\u4e2a\u4e00\u9e64\u51b2\u5929\uff0c\u76f4\u817e\u5728\u534a\u7a7a\uff0c\u4e00\u9762\u53eb\u9053\uff1a\u201c\u8001\u524d\u8f88 \u5feb\u9000\uff0c\u5f85\u5728\u4e0b\u6765\u6536\u62fe\u8fd9\u79c3\u9a74\u3002\u201d \u3000\u3000\u94c1\u5934\u9640\u96be\u514d\u4e5f\u5bf9\u5cb3\u5c0f\u98de\u7684\u4e00\u8eab\u8f7b\u529f\u5403\u60ca\uff0c\u6756\u52bf\u4e00\u53d8\uff0c\u53cd\u81c2\u5411\u4e0a\u9006\u626b\u4e0a\u53bb\u3002 \u3000\u3000\u8c01\u77e5\u5cb3\u5c0f\u98de\u7684\u8eab\u5b50\u6bd4\u4ed6\u7684\u4ed7\u52bf\u66f4\u5feb\uff0c\u7adf\u4ece\u6756\u9699\u4e2d\u60ca\u9e3f\u822c\u4fef\u51b2\u800c\u4e0b\uff0c\u4e00\u5251\u523a\u5411\u94c1\u5934\u9640\u7684\u54bd \u5589\u3002 \u3000\u3000\u94c1\u5934\u9640\u5927\u5403\u4e00\u60ca\uff0c\u6025\u6025\u6536\u52bf\u540e\u9000\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4e00\u5251\u843d\u7a7a\uff0c\u6b63\u8981\u4e5f\u4ef0\u8eab\u540e\u9000\uff0c\u4ee5\u4f7f\u8131\u79bb\u94c1\u7985\u6756\u7684\u626b\u51fb\u8303\u56f4\u3002 \u3000\u3000\u5c82\u6599\u5c31\u5728\u8fd9\u65f6\uff0c\u94c1\u7985\u6756\u7adf\u771f\u7684\u5df2\u95ea\u7535\u822c\u626b\u6765\u3002 \u3000\u3000\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u4ed6\u5c45\u7136\u843d\u5728\u94c1\u7985\u6756\u4e0a\uff0c\u800c\u4e14\u4f7f\u5f97\u94c1\u5934\u9640\u65e0\u6cd5\u7529\u6389\u3002 \u3000\u3000\u94c1\u5934\u9640\u65e0\u5948\u4e4b\u4e0b\uff0c\u731b\u529b\u5c06\u94c1\u7985\u6756\u4e00\u6296\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u867d\u88ab\u4ed6\u6296\u4e0b\uff0c\u4f46\u5251\u5c16\u5374\u5df2\u523a\u8fd1\u4e86\u94c1\u5934\u9640\u7684\u524d\u80f8\u3002 \u3000\u3000\u94c1\u5934\u9640\u88ab\u8feb\u53ea\u6709\u6492\u624b\u5c06\u94c1\u5934\u7985\u6756\u629b\u51fa\uff0c\u5426\u5219\u52bf\u5fc5\u4e27\u547d\u5251\u4e0b\u4e0d\u53ef\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u4f9d\u7136\u8fde\u4eba\u5e26\u5251\uff0c\u5411\u524d\u75be\u523a\uff0c\u867d\u672a\u523a\u4e2d\u94c1\u5934\u9640\u524d\u80f8\uff0c\u5374\u523a\u8fdb\u4e86\u4ed6\u7684\u5de6\u80a9\u3002 \u3000\u3000\u6b63\u6b32\u62d4\u5251\u518d\u523a\uff0c\u7a81\u611f\u8eab\u4fa7\u6d8c\u6765\u4e00\u80a1\u6709\u5982\u6392\u5c71\u5012\u6d77\u822c\u7684\u6f5c\u529b\uff0c\u76f4\u628a\u4ed6\uff0a\u51fa\u4e00\u4e08\u5f00\u5916\u3002 \u3000\u3000\u53ea\u542c\u8033\u8fb9\u54cd\u8d77\u4e00\u58f0\u5982\u96f7\u5927\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u5c45\u7136\uff0a\u5f97\u8001\u592b\u975e\u51fa\u624b\u4e0d\u53ef\u4e86\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fd9\u624d\u770b\u51fa\uff0c\u573a\u4e2d\u5c45\u7136\u591a\u4e86\u4e00\u4e2a\u8eab\u957f\u4e0d\u6ee1\u56db\u5c3a\u7684\u9a7c\u80cc\u8001\u4eba\uff0c\u8d6b\u7136\u662f\u591a\u80cc\u5929\u7fc1\u5e9e\u821f\u3002 \u3000\u3000\u5e9e\u821f\u7684\u55d3\u95e8\u4e4b\u5927\uff0c\u771f\u662f\u5929\u4e0b\u5c11\u6709\uff0c\u65b9\u624d\u7684\u4e00\u58f0\u5927\u559d\uff0c\u4f7f\u5f97\u4e24\u8fb9\u89c2\u6218\u4e4b\u4eba\uff0c\u8fde\u8033\u819c\u90fd\u51e0\u4e4e \u8981\u88ab\u9707\u7834\u3002 \u3000\u3000\u8fd9\u65f6\u573a\u5185\u6240\u6709\u7684\u4eba\uff0c\u65e0\u4e0d\u5664\u82e5\u5bd2\u8749\u3002 \u3000\u3000\u4ed6\u4eec\u53ea\u77e5\u5e9e\u821f\u6b66\u529f\u5929\u4e0b\u65e0\u6545\uff0c\u5374\u8c01\u90fd\u6ca1\u89c1\u4ed6\u51fa\u624b\u8fc7\uff0c\u8fde\u7687\u5143\u6559\u4e3b\u767d\u837b\u4ee5\u53ca\u603b\u62a4\u6cd5\u79e6\u69d0\uff0c \u4e5f\u53ea\u662f\u8033\u95fb\u800c\u5df2\u3002 \u3000\u3000\u5e9e\u821f\u867d\u5df2\u51fa\u9635\uff0c\u4e24\u624b\u5374\u672a\u63e1\u4efb\u4f55\u5175\u5203\uff0c\u663e\u7136\u4ed6\u6839\u672c\u4e0d\u5c51\u52a8\u7528\u3002 \u3000\u3000\u800c\u5bf9\u5e9e\u821f\uff0c\u5cb3\u5c0f\u98de\u96be\u514d\u4e5f\u6709\u4e9b\u80c6\u602f\uff0c\u4f46\u6b64\u65f6\u6b64\u5730\uff0c\u4ed6\u5374\u7edd\u5bf9\u4e0d\u80fd\u9000\u7f29\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u77e5\u9053\uff0c\u80b2\u5316\u57ce\u65b9\u800c\uff0c\u53ea\u6709\u4ed6\u548c\u6bcd\u4eb2\u4f55\u6167\u4ed9\u4ee5\u53ca\u8881\u5c0f\u9e64\u6700\u582a\u4e00\u6218\uff0c\u5173\u7433\u548c\u65b9\u5929\u94ce\uff0c \u867d\u662f\u7edd\u9876\u9ad8\u4eba\uff0c\u5374\u56e0\u7cbe\u795e\u4f53\u529b\u5c1a\u672a\u6062\u590d\u96be\u4ee5\u53d1\u6325\uff0c\u6b64\u523b\u82e5\u4e0d\u548c\u5e9e\u821f\u786c\u62da\u4e00\u9635\uff0c\u81ea\u5df1\u7684\u4e00\u65b9\uff0c \u5f88\u53ef\u80fd\u4f1a\u4e00\u6e83\u4e0d\u53ef\u6536\u62fe\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u7acb\u523b\u53c8\u817e\u8eab\u800c\u8d77\uff0c\u4ee5\u5fa1\u5251\u4e4b\u672f\uff0c\u731b\u5411\u5bf9\u65b9\u653b\u53bb\u3002 \u3000\u3000\u4f46\u89c1\u5e9e\u821f\u9065\u7a7a\u53cc\u638c\u4e00\u63a8\uff0c\u4e24\u9053\u72c2\u98d9\uff0c\u76f4\u51b2\u800c\u8d77\uff0c\u6b63\u597d\u8fce\u4e0a\u4e86\u5cb3\u5c0f\u98de\u7684\u6765\u52bf\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5168\u8eab\u4e00\u9707\u4e4b\u4e0b\uff0c\u7acb\u523b\u50cf\u65ad\u7ebf\u98ce\u7b5d\uff0c\u88ab\u9707\u5f97\u5411\u540e\u5012\u6454\u56de\u53bb\u3002 \u3000\u3000\u4ed6\u7ad9\u7a33\u811a\u540e\uff0c\u518d\u5ea6\u63a2\u5251\u51cc\u7a7a\u5411\u524d\u75be\u51b2\u3002 \u3000\u3000\u4f46\u4f9d\u7136\u88ab\u5e9e\u821f\u7684\u638c\u98ce\u534a\u8def\u649e\u56de\u3002 \u3000\u3000\u6240\u5e78\u4ed6\u8f7b\u529f\u5df2\u51fa\u795e\u5165\u5316\uff0c\u88ab\u649e\u56de\u4e4b\u540e\uff0c\u4ecd\u80fd\u63a7\u5236\u4f4f\u4e0b\u843d\u4e4b\u52bf\uff0c\u4e0d\u81f3\u53d7\u4f24\uff0c\u82e5\u6362\u4e86\u4e00\u822c\u9ad8 \u624b\uff0c\u53ea\u6015\u7b2c\u4e00\u638c\u5c31\u88ab\u9707\u660f\u3002 \u3000\u3000\u6b64\u523b\uff0c\u5cb3\u5c0f\u98de\u5b9e\u5728\u5df2\u65e0\u8ba1\u53ef\u65bd\uff0c\u81ea\u5df1\u6839\u672c\u65e0\u6cd5\u63a5\u8fd1\u5bf9\u65b9\uff0c\u6b66\u529f\u518d\u9ad8\uff0c\u5251\u6cd5\u518d\u5999\uff0c\u4e5f\u4e2a\u65e0 \u7528\u6b66\u4e4b\u5730\u3002 \u3000\u3000\u82e5\u81ea\u5df1\u4e5f\u4ee5\u638c\u529b\u548c\u6307\u98ce\u4e0e\u5bf9\u65b9\u786c\u62da\uff0c\u90a3\u66f4\u662f\u671b\u5c18\u83ab\u53ca\u3002 \u3000\u3000\u5ffd\u542c\u7687\u5143\u6559\u4e3b\u767d\u837b\u4f20\u8c15\u9053\uff1a\u201c\u6240\u6709\u7684\u4eba\u4e00\u9f50\u6325\u6740\u8fc7\u53bb\uff01\u201d \u3000\u3000\u5979\u8fd9\u4e00\u58f0\u4ee4\u4e0b\uff0c\u987f\u65f6\uff0c\u4e0a\u767e\u540d\u7684\u9ad8\u624b\uff0c\u5404\u4ed7\u5175\u5203\uff0c\u50cf\u6f6e\u6c34\u822c\u9f50\u9f50\u5411\u80b2\u5316\u57ce\u4e3b\u4e25\u5bd2\u7b49\u4eba\u505c \u8eab\u4e4b\u5904\u6d8c\u53bb\u3002 \u3000\u3000\u4e25\u5bd2\u6025\u6025\u5429\u5490\u9000\u56de\u57ce\u4e2d\u3002 \u3000\u3000\u597d\u5728\u53cc\u65b9\u76f8\u8ddd\u6253\u4e03\u516b\u4e08\u8fdc\uff0c\u4e25\u5bd2\u65b9\u9762\u7684\u4eba\u8ddd\u57ce\u95e8\u53c8\u8fd1\uff0c\u603b\u7b97\u5728\u654c\u65b9\u5c1a\u672a\u653b\u5230\u4e4b\u524d\uff0c\u5b89\u7136 \u9000\u56de\u57ce\u4e2d\uff0c\u5e76\u5c06\u57ce\u95e8\u95ed\u8d77\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u57ce\u4e0a\u7684\u6570\u5341\u540d\u5f13\u5f29\u624b\u7bad\u5982\u98de\u8757\u822c\u5c04\u4e0b\uff0c\u8feb\u5f97\u7687\u5143\u6559\u65b9\u9762\u7684\u9ad8\u624b\uff0c\u53ea\u5f97\u4e5f\u5411\u540e\u9000\u3002 \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u4e8c\u5341\u516b\u56de\u3000\u5371\u6025\u65f6\u523b \u3000\u3000\u4e00\u8fde\u534a\u6708\u8fc7\u53bb\u3002 \u3000\u3000\u80b2\u5316\u57ce\u59cb\u7ec8\u57ce\u95e8\u7d27\u95ed\u3002 \u3000\u3000\u57ce\u4e0a\u7684\u5f13\u5f29\u624b\uff0c\u51e0\u4e4e\u662f\u65e5\u591c\u5f85\u547d\u3002 \u3000\u3000\u7687\u5143\u6559\u7684\u4eba\u9a6c\uff0c\u81ea\u4ece\u4e0a\u6b21\u653b\u57ce\u672a\u9042\u64a4\u8d70\u4e4b\u540e\uff0c\u534a\u6708\u6765\u7adf\u65e0\u4efb\u4f55\u52a8\u9759\u3002 \u3000\u3000\u4f46\u80b2\u5316\u57ce\u65b9\u9762\u7684\u9996\u8111\u4eba\u7269\uff0c\u5374\u4e00\u5929\u6bd4\u4e00\u5929\u7126\u8651\u4e0d\u5b89\u3002\u8bba\u53cc\u65b9\u5b9e\u529b\uff0c\u76f8\u5dee\u60ac\u6b8a\uff0c\u80b2\u5316\u57ce\u6839 \u672c\u65e0\u529b\u5bf9\u7687\u5143\u6559\u8fdb\u884c\u53cd\u653b\uff0c\u5bf9\u65b9\u53ea\u8981\u6709\u4e00\u4e2a\u591a\u81c2\u5929\u7fc1\u5e9e\u821f\uff0c\u5c31\u4f7f\u5f97\u80b2\u5316\u57ce\u65b9\u9762\u7fa4\u8c6a\u65e0\u4e00\u6562\u6484 \u5176\u950b\uff0c\u4f55\u51b5\u4ed6\u5c5e\u4e0b\u66f4\u6709\u5341\u5927\u9ad8\u624b\uff0c\u6bcf\u4e00\u9ad8\u624b\uff0c\u90fd\u4e0d\u5728\u8881\u5c0f\u9e64\u7b49\u4eba\u4e4b\u4e0b\u3002 \u3000\u3000\u76ee\u524d\u80b2\u5316\u57ce\u552f\u4e00\u7684\u529e\u6cd5\uff0c\u4fbf\u662f\u575a\u5b88\u4e0d\u51fa\uff0c\u6328\u4e00\u5929\u7b97\u4e00\u5929\u3002 \u3000\u3000\u4f46\u504f\u504f\u7cae\u8349\u53c8\u6e10\u6e10\u7528\u7f44\uff0c\u770b\u6765\u4e0d\u51fa\u5341\u65e5\uff0c\u4fbf\u8981\u53d1\u751f\u9965\u8352\uff0c\u5230\u90a3\u65f6\u53c8\u5982\u4f55\u80fd\u7ee7\u7eed\u575a\u5b88\u57ce\u6c60\uff1f \u3000\u3000\u5c31\u5728\u4e25\u5bd2\u4e00\u7b79\u83ab\u5c55\u4e4b\u9645\uff0c\u5b88\u57ce\u7684\u5f1f\u5144\u524d\u6765\u7980\u62a5\u5bcc\u56fd\u57ce\u4e3b\u9ec4\u4e00\u9053\u6c42\u89c1\u3002 \u3000\u3000\u4e25\u5bd2\u4e0d\u89c9\u559c\u51fa\u671b\u5916\uff0c\u8fde\u5fd9\u6d3e\u5468\u6d77\u5c71\u4eb2\u81ea\u6253\u5f00\u57ce\u95e8\u8fce\u63a5\u3002 \u3000\u3000\u5728\u5927\u5385\u5185\uff0c\u4e25\u5bd2\u548c\u9ec4\u4e00\u9053\u89c1\u4e86\u9762\u3002 \u3000\u3000\u5750\u4e0b\u540e\uff0c\u9ec4\u4e00\u9053\u957f\u957f\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u60f3\u4e0d\u5230\u60a8\u548c\u6559\u4e3b\u6b63\u5f0f\u95f9\u7ffb\uff0c\u4e8b\u5230\u5982\u4eca\uff0c\u60a8\u5fc5 \u5b9a\u5df2\u662f\u5750\u56f0\u5371\u57ce\uff0c\u4e00\u7b79\u83ab\u5c55\u4e86\u5427\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u4e5f\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u6765\u4e86\uff0c\u662f\u5426\u80fd\u66ff\u611a\u5144\u60f3\u60f3\u529e\u6cd5\uff1f\u201d \u3000\u3000\u9ec4\u4e00\u9053\u9053\uff1a\u201c\u5144\u5f1f\u6628\u5929\u4e3a\u8fd9\u4e8b\u66fe\u524d\u5f80\u603b\u575b\u89c1\u8fc7\u6559\u4e3b\u3002\u201d \u3000\u3000\u201c\u5979\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u201c\u5728\u5144\u5f1f\u529d\u8bf4\u4e4b\u4e0b\uff0c\u6559\u4e3b\u603b\u7b97\u8fd8\u987e\u5ff5\u540c\u95e8\u4e4b\u8c0a\uff0c\u5979\u8bf4\u53ea\u8981\u5927\u5e08\u5144\u4e0d\u518d\u4e0e\u5979\u4e3a\u654c\uff0c\u7687\u5143\u6559 \u4fbf\u4e0d\u548e\u65e2\u5f80\uff0c\u4ecd\u8ba9\u5927\u5e08\u5144\u7ee7\u7eed\u505a\u80b2\u5316\u57ce\u4e3b\uff0c\u4ee5\u4fbf\u65e9\u65e5\u540c\u5fc3\u534f\u529b\u8fdb\u519b\u4e2d\u539f\u3002\u201d \u3000\u3000\u201c\u5979\u7684\u610f\u601d\uff0c\u53ef\u662f\u8981\u6211\u5411\u5979\u9053\u6b49\u8c22\u7f6a\uff1f\u201d \u3000\u3000\u201c\u5927\u5e08\u5144\u4f55\u5fc5\u8bf4\u5f97\u8fd9\u4e48\u4e25\u91cd\uff1f\u53ea\u8981\u60a8\u80af\u4ecd\u548c\u6559\u4e3b\u5408\u4f5c\uff0c\u4e8b\u60c5\u4fbf\u4e00\u5207\u5305\u5728\u5144\u5f1f\u8eab\u4e0a\u3002\u201d \u3000\u3000\u201c\u4f60\u6253\u7b97\u600e\u4e48\u505a\uff1f\u201d \u3000\u3000\u201c\u7531\u5144\u5f1f\u5907\u4e0a\u4e00\u684c\u9152\u5e2d\uff0c\u5927\u5e08\u5144\u548c\u6559\u4e3b\u90fd\u8bf7\u5230\u5bcc\u56fd\u57ce\uff0c\u5f7c\u6b64\u5f00\u8bda\u76f8\u89c1\u628a\u8bdd\u8bb2\u5f00\uff0c\u4e0d\u5c31\u4e00 \u5207\u95ee\u9898\u5168\u89e3\u51b3\u4e86\u4e48\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u51b7\u7b11\u9053\uff1a\u201c\u4e8c\u5e08\u5f1f\uff0c\u539f\u6765\u4f60\u6765\u770b\u6211\uff0c\u7adf\u53ea\u662f\u8fd9\u51e0\u53e5\u8bdd\uff0c\u4f60\u7684\u60f3\u6cd5\u672a\u514d\u592a\u5929\u771f\u4e86\uff01\u201d \u3000\u3000\u9ec4\u4e00\u9053\u9762\u8272\u663e\u5f97\u5341\u5206\u5c34\u5c2c\uff0c\u9053\uff1a\u201c\u5144\u5f1f\u8bf4\u7684\u5b8c\u5168\u771f\u5fc3\u8bdd\uff0c\u800c\u4e14\u4e5f\u5b8c\u5168\u4e3a\u4e86\u5927\u5e08\u5144\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u4e8c\u5e08\u5f1f\u80fd\u5728\u8fd9\u65f6\u5019\u6765\u770b\u6211\uff0c\u6211\u5f53\u7136\u5f88\u611f\u6fc0\uff0c\u8db3\u89c1\u6211\u4eec\u5e08\u5144\u5f1f\u4f9d\u7136\u75db\u75d2\u76f8\u5173\u3002 \u4f46\u4e8b\u5b9e\u6446\u5728\u9762\u524d\uff0c\u6211\u4e0e\u767d\u837b\u6839\u672c\u6ca1\u6709\u518d\u590d\u5408\u7684\u53ef\u80fd\uff0c\u90a3\u5973\u4eba\u5fc3\u5982\u86c7\u874e\uff0c\u6211\u5c82\u80fd\u843d\u5165\u5979\u7684\u5708\u5957\uff0c \u8fd9\u4e00\u70b9\u4e8c\u5e08\u5f1f\u4e0d\u4f1a\u770b\u4e0d\u6e05\u695a\u5427\uff1f\u201d \u3000\u3000\u9ec4\u4e00\u9053\u7ea2\u7740\u8138\u9053\uff1a\u201c\u82e5\u5927\u5e08\u5144\u4e0d\u76f8\u4fe1\u5144\u5f1f\u8bf4\u7684\u662f\u771f\u8bdd\uff0c\u5144\u5f1f\u4e5f\u6ca1\u6709\u529e\u6cd5\uff01\u201d \u3000\u3000\u4e25\u5bd2\u7f04\u9ed8\u4e86\u4e00\u4e0b\u9053\uff1a\u201c\u5982\u679c\u4e8c\u5e08\u5f1f\u8fd8\u5ff5\u5728\u540c\u5e08\u4e60\u827a\u4e4b\u60c5\uff0c\u8bda\u5fc3\u4e3a\u5e2e\u52a9\u6211\u800c\u6765\uff0c\u611a\u5144\u786e\u5b9e \u6709\u4ef6\u4e8b\u6b63\u60f3\u8bf7\u4f60\u5e2e\u5fd9\uff01\u201d \u3000\u3000\u201c\u5927\u5e08\u5144\u8bf7\u8bb2\uff0c\u53ea\u8981\u5144\u5f1f\u505a\u5f97\u5230\u7684\uff0c\u65e0\u4e0d\u5c3d\u529b\u3002\u201d \u3000\u3000\u201c\u5176\u5b9e\u6211\u4e0d\u8bb2\u4f60\u4e5f\u5e94\u5f53\u60f3\u5f97\u5230\uff0c\u611a\u5144\u56f0\u5b88\u5b64\u57ce\uff0c\u76ee\u524d\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u5927\u4e8b\uff0c\u662f\u7cae\u8349\u5373\u5c06\u7528 \u7f44\uff0c\u6b63\u597d\u4f60\u90a3\u91cc\u7684\u901a\u8fd0\u5927\u5be8\uff0c\u8d1f\u8d23\u4f9b\u5e94\u5168\u8c37\u7684\u7cae\u8349\u7528\u5ea6\uff0c\u53ef\u5426\u6697\u4e2d\u62e8\u8865\u5230\u80b2\u5316\u57ce\uff0c\u4ee5\u89e3\u611a\u5144 \u71c3\u7709\u4e4b\u6025\uff1f\u201d \u3000\u3000\u9ec4\u4e00\u9053\u76b1\u4e0b\u7709\u5934\u9053\uff1a\u201c\u8fd9\u4e8b\u5144\u5f1f\u81ea\u5e94\u5e2e\u5fd9\uff0c\u4f46\u901a\u8fd0\u5927\u5be8\u7531\u5916\u9762\u8fdb\u5165\u7684\u4e00\u5207\u7269\u8d44\uff0c\u603b\u575b\u5747 \u6709\u5e10\u76ee\u53ef\u67e5\uff0c\u4e00\u65e6\u88ab\u603b\u575b\u67e5\u89c9\u6216\u8fd0\u9001\u8fdb\u57ce\u65f6\u884c\u52a8\u4e0d\u79d8\uff0c\u5144\u5f1f\u5c31\u96be\u514d\u5403\u7f6a\u4e0d\u8d77\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u901a\u8fd0\u5927\u5be8\u7684\u7269\u8d44\u8fdb\u5165\uff0c\u9996\u987b\u7ecf\u8fc7\u5bcc\u56fd\u57ce\uff0c\u4f60\u82e5\u4e0d\u5148\u884c\u767b\u5e10\uff0c\u53c8\u6709\u8c01\u80fd\u67e5\u51fa\uff1f \u81f3\u4e8e\u8fd0\u9001\u81f3\u672c\u57ce\u7684\u884c\u52a8\uff0c\u5f53\u7136\u8981\u9009\u5728\u591c\u95f4\uff0c\u800c\u4e14\u6700\u597d\u662f\u4e0b\u534a\u591c\u3002\u201d \u3000\u3000\u9ec4\u4e00\u9053\u53c8\u6c89\u541f\u4e86\u4e00\u9635\u9053\uff1a\u201c\u4e0d\u8fc7\u82e5\u7531\u6b63\u95e8\u8fdb\u5165\u80b2\u5316\u57ce\uff0c\u603b\u662f\u76ee\u6807\u592a\u5927\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u5f53\u7136\u90a3\u65f6\u6211\u53ef\u6253\u5f00\u504f\u95e8\uff0c\u504f\u95e8\u79bb\u5c71\u58c1\u5f88\u8fd1\uff0c\u4f4d\u7f6e\u9690\u79d8\uff0c\u8f66\u961f\u8fdb\u5165\u5fc5\u4e0d\u81f4\u66b4\u9732 \u884c\u8e2a\u3002\u4f55\u51b5\u80b2\u5316\u57ce\u4e0d\u8fc7\u51e0\u767e\u4eba\uff0c\u53ea\u8981\u7cae\u8349\u8fdb\u5165\u4e09\u4e94\u5341\u8f66\uff0c\u4fbf\u53ef\u8db3\u591f\u4e09\u4e24\u6708\u4e4b\u7528\u3002\u201d \u3000\u3000\u9ec4\u4e00\u9053\u6b63\u8272\u9053\uff1a\u201c\u5144\u5f1f\u65e2\u7136\u8981\u6697\u4e2d\u76f8\u52a9\u5927\u5e08\u5144\uff0c\u5f53\u7136\u662f\u7269\u8d44\u8fd0\u6765\u8d8a\u591a\u8d8a\u597d\uff0c\u4e00\u52b3\u6c38\u9038\uff0c \u514d\u5f97\u4ee5\u540e\u8fd8\u8981\u7ecf\u5e38\u8865\u5145\u3002\u201d \u3000\u3000\u4e25\u5bd2\u4e0d\u89c9\u559c\u9053\uff1a\u201c\u90a3\u5c31\u591a\u8c22\u4e8c\u5e08\u5f1f\u4e86\uff0c\u611a\u5144\u5e0c\u671b\u884c\u52a8\u80fd\u8d8a\u5feb\u8d8a\u597d\u3002\u201d \u3000\u3000\u9ec4\u4e00\u9053\u5fd6\u5ea6\u4e86\u4e00\u4e0b\u9053\uff1a\u201c\u5c31\u5728\u660e\u5929\u665a\u4e0a\u4e09\u66f4\u5982\u4f55\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u5c31\u8fd9\u4e48\u51b3\u5b9a\uff0c\u611a\u5144\u4e0d\u4fbf\u4e45\u7559\uff0c\u4e8c\u5e08\u5f1f\u6700\u597d\u8fd9\u5c31\u56de\u53bb\u51c6\u5907\u3002\u201d \u3000\u3000\u4ed6\u4e3a\u4e86\u8ba9\u9ec4\u4e00\u9053\u719f\u6089\u504f\u95e8\u8def\u7ebf\uff0c\u5e76\u547d\u4eba\u6253\u5f00\u504f\u95e8\uff0c\u628a\u9ec4\u4e00\u9053\u4eb2\u81ea\u9001\u51fa\u57ce\u53bb\u3002 \u3000\u3000\u9ec4\u4e00\u9053\u8d70\u540e\uff0c\u4e25\u5bd2\u8fd9\u4e9b\u5929\u6765\u7684\u7126\u8651\u548c\u4e0d\u5b89\uff0c\u4e5f\u968f\u4e4b\u4e00\u626b\u800c\u5149\u3002 \u3000\u3000\u4ed6\u60f3\u5230\u5e08\u5144\u5f1f\u603b\u662f\u5e08\u5144\u5f1f\uff0c\u9ec4\u4e00\u9053\u867d\u4e0d\u80af\u968f\u4ed6\u4e00\u540c\u53cd\u51fa\u7687\u5143\u6559\uff0c\u5374\u80fd\u5728\u6697\u4e2d\u76f8\u52a9\uff0c\u770b\u6765 \u5df2\u7ecf\u5f88\u591f\u9053\u4e49\u7684\u4e86\u3002 \u3000\u3000\u4f46\u603b\u7ba1\u5468\u6d77\u5c71\u5374\u4e0d\u4ee5\u4e3a\u7136\u3002 \u3000\u3000\u5f53\u4e25\u5bd2\u628a\u6b64\u4e8b\u544a\u8bc9\u5468\u6d77\u5c71\uff0c\u5468\u6d77\u5c71\u7acb\u5373\u63d0\u9192\u4e25\u5bd2\u9053\uff1a\u201c\u7980\u57ce\u4e3b\uff0c\u4f9d\u5351\u804c\u770b\u6765\uff0c\u8fd9\u4e8b\u53ea\u6015 \u5176\u4e2d\u6709\u8bc8\uff01\u201d \u3000\u3000\u4e25\u5bd2\u6447\u5934\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\uff0c\u8001\u592b\u4e0e\u9ec4\u57ce\u4e3b\u8c0a\u5c5e\u540c\u95e8\uff0c\u800c\u4e14\u4e00\u5411\u79c1\u4ea4\u4e0d\u6076\uff0c\u4ed6\u6697\u4e2d\u76f8\u52a9\uff0c\u5fc5 \u5b9a\u51fa\u4e8e\u771f\u5fc3\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u5c5e\u4e0b\u8fd8\u662f\u529d\u4f60\u8001\u4eba\u5bb6\u4e0d\u53ef\u8fc7\u5206\u76f8\u4fe1\u9ec4\u57ce\u4e3b\uff0c\u4ed6\u82e5\u80af\u5e2e\u52a9\u6211\u4eec\uff0c\u5c31\u8be5\u516c\u7136\u548c \u4f60\u91c7\u53d6\u4e00\u81f4\u884c\u52a8\uff0c\u5c31\u7b97\u5351\u804c\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\u5ea6\u541b\u5b50\u4e4b\u8179\uff0c\u57ce\u4e3b\u4e5f\u4e0d\u5f97\u4e0d\u9632\u5176\u4e2d\u6709\u53d8\u3002\u201d \u3000\u3000\u4e25\u5bd2\u4f3c\u6709\u6240\u52a8\u9053\uff1a\u201c\u5373\u4fbf\u5176\u4e2d\u6709\u8bc8\uff0c\u53c8\u80fd\u53d1\u751f\u591a\u5927\u4f5c\u7528\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u9053\uff1a\u201c\u82e5\u4ed6\u628a\u9ad8\u624b\u4eec\u6539\u626e\u6210\u8f66\u592b\uff0c\u8fdb\u57ce\u4e4b\u540e\uff0c\u7acb\u523b\u5c55\u5f00\u53ae\u6740\uff0c\u6216\u8005\u7eb5\u706b\u70e7\u57ce\uff0c\u80b2 \u5316\u57ce\u5c82\u4e0d\u5c31\u8981\u6bc1\u4e4b\u4e00\u65e6\uff1f\u201d \u3000\u3000\u201c\u4f60\u7684\u610f\u601d\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u201c\u5c5e\u4e0b\u60f3\u6700\u597d\u628a\u5cb3\u516c\u5b50\u548c\u8881\u4f7f\u8005\u901f\u901f\u7531\u4e94\u5173\u53ec\u56de\uff0c\u660e\u665a\u52a0\u5f3a\u6212\u5907\uff0c\u5728\u7cae\u8349\u8fdb\u57ce\u524d\uff0c\u5148\u884c \u4ed4\u7ec6\u68c0\u67e5\uff0c\u53d1\u73b0\u53ef\u7591\uff0c\u7acb\u523b\u5148\u53d1\u5236\u4eba\u3002\u201d \u3000\u3000\u201c\u9ec4\u57ce\u4e3b\u6697\u4e2d\u76f8\u52a9\u6211\u4eec\uff0c\u82e5\u6211\u4eec\u53cd\u800c\u8d77\u7591\u5fc3\u52a0\u4ee5\u68c0\u67e5\uff0c\u8fd9\u6837\u505a\u662f\u5426\u4e0d\u592a\u793c\u8c8c\u5462\uff1f\u201d \u3000\u3000\u201c\u4e3a\u4e86\u4e07\u4e00\uff0c\u57ce\u4e3b\u5927\u53ef\u4e0d\u5fc5\u987e\u8651\u90a3\u4e48\u591a\uff0c\u4f55\u51b5\u5982\u679c\u4ed6\u662f\u8bda\u5fc3\u8bda\u610f\u6697\u4e2d\u76f8\u52a9\uff0c\u8fd8\u5728\u4e4e\u7684\u4ec0 \u4e48\u68c0\u67e5\uff1f\u201d \u3000\u3000\u6700\u7ec8\u4e25\u5bd2\u9894\u9996\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u6211\u9a6c\u4e0a\u6d3e\u4eba\u628a\u5cb3\u516c\u5b50\u548c\u8881\u5c0f\u9e64\u8fde\u591c\u8c03\u6765\u3002\u201d \u3000\u3000\u5f53\u665a\uff0c\u7531\u5927\u59d1\u5a18\u4e25\u5982\u51b0\u5077\u5077\u5230\u4e86\u4e94\u5173\uff0c\u4e09\u66f4\u5de6\u53f3\u4fbf\u548c\u5cb3\u5c0f\u98de\u3001\u8881\u5c0f\u9e64\u4e00\u540c\u56de\u5230\u80b2\u5316\u57ce\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u662f\u8d8a\u57ce\u800c\u8fc7\uff0c\u5927\u95e8\u548c\u504f\u95e8\u90fd\u4e0d\u66fe\u6253\u5f00\u3002 \u3000\u3000\u4e25\u5bd2\u4e3a\u4e86\u8ba9\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u517b\u8db3\u7cbe\u795e\uff0c\u7279\u522b\u8ba9\u4ed6\u4eec\u76f4\u7761\u5230\u5c06\u8fd1\u6b21\u65e5\u4e2d\u5348\uff0c\u624d\u53eb\u9192\u5403\u996d\u3002 \u3000\u3000\u5348\u996d\u540e\uff0c\u5cb3\u5c0f\u98de\u76f4\u63a5\u8fdb\u5165\u5185\u5ba4\u548c\u6bcd\u4eb2\u4f55\u6167\u4ed9\u89c1\u9762\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u6709\u4e00\u9879\u4e0d\u89e3\u4e4b\u8c1c\uff0c\u51b3\u5b9a\u8d81\u8fd9\u65f6\u5411\u6bcd\u4eb2\u95ee\u660e\uff0c\u90a3\u5c31\u662f\u5f53\u4e09\u5e74\u524d\u6bcd\u4eb2\u8fdb\u5165\u5929\u8c37\u65f6\uff0c\u4e3a \u4ec0\u4e48\u7adf\u7136\u53d7\u5230\u51e4\u5ae3\u7ea2\u7684\u9677\u5bb3\u3002 \u3000\u3000\u4f55\u6167\u4ed9\u9aa4\u542c\u5cb3\u5c0f\u98de\u95ee\u8d77\u8fd9\u4e8b\uff0c\u5148\u662f\u4e0d\u613f\u5410\u9732\uff0c\u540e\u6765\u7ec8\u4e8e\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u98de\u513f\uff0c\u4f60\u65e2\u7136\u975e\u95ee \u4e0d\u53ef\uff0c\u6211\u4e5f\u53ea\u597d\u5b9e\u5bf9\u4f60\u8bf4\u4e86\uff0c\u56e0\u4e3a\u6211\u548c\u51e4\u5ae3\u7ea2\u65e9\u5c31\u8ba4\u8bc6\uff0c\u4e0d\u4f46\u8ba4\u8bc6\uff0c\u800c\u4e14\u5979\u662f\u6211\u7684\u5e08\u59d0\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u554a\u4e86\u58f0\u9053\uff1a\u201c\u539f\u6765\u5979\u548c\u5a18\u7adf\u6709\u7740\u8fd9\u79cd\u5173\u7cfb\uff1f\u65e2\u7136\u5982\u6b64\uff0c\u5979\u4e3a\u4ec0\u4e48\u53cd\u800c\u8981\u9677\u5bb3\u5a18 \u5462\uff1f\u201d \u3000\u3000\u4f55\u6167\u4ed9\u9053\uff1a\u201c\u56e0\u4e3a\u5728\u8fd9\u4e16\u754c\u4e0a\uff0c\u53ea\u6709\u6211\u77e5\u9053\u5979\u7684\u79c1\u79d8\uff0c\u5979\u62c5\u5fc3\u6211\u5728\u7687\u5143\u6559\u4e3b\u9762\u524d\u544a\u5979\uff0c \u53ea\u6709\u4e3a\u6211\u52a0\u4e0a\u4e00\u9879\u7f6a\u540d\uff0c\u6253\u5165\u80b2\u5316\u57ce\u7075\u5802\u4e3a\u5974\u3002\u201d \u3000\u3000\u201c\u8fd9\u6837\u8bf4\u5979\u603b\u7b97\u8fd8\u6709\u70b9\u826f\u5fc3\uff0c\u672a\u66fe\u7f6e\u5a18\u4e8e\u6b7b\u5730\u3002\u201d \u3000\u3000\u201c\u5979\u80fd\u6709\u70b9\u826f\u5fc3\u5c31\u597d\u4e86\uff0c\u5979\u5f53\u521d\u672c\u6765\u662f\u8981\u7687\u5143\u6559\u4e3b\u6cbb\u6211\u6b7b\u7f6a\uff0c\u8fd8\u662f\u7687\u5143\u6559\u4e3b\u5ff5\u6211\u901a\u8fc7\u4e94 \u5173\u65f6\u6210\u7ee9\u4f18\u5f02\uff0c\u624d\u5f00\u6069\u514d\u4e8e\u4e00\u6b7b\u7684\u3002\u201d \u3000\u3000\u201c\u5979\u7a76\u7adf\u6709\u4ec0\u4e48\u628a\u67c4\u6293\u5728\u5a18\u624b\u4e0a\uff1f\u201d \u3000\u3000\u201c\u90a3\u5973\u4eba\u5fc3\u80a0\u5b9e\u5728\u592a\u72e0\uff0c\u5979\u4eb2\u624b\u4e0b\u6bd2\u6bd2\u6b7b\u4e86\u5e08\u7236\uff01\u201d \u3000\u3000\u201c\u5a18\u770b\u89c1\u4e86\uff1f\u201d \u3000\u3000\u201c\u867d\u4e0d\u66fe\u5f53\u573a\u4eb2\u773c\u770b\u89c1\uff0c\u4f46\u90a3\u676f\u6bd2\u8336\u5374\u662f\u5979\u4eb2\u624b\u9012\u7ed9\u5e08\u7236\u7684\u3002\u201d \u3000\u3000\u201c\u540e\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u5a18\u4e3a\u66ff\u5e08\u7236\u62a5\u4ec7\uff0c\u5f53\u573a\u548c\u5979\u52a8\u4e0a\u4e86\u624b\uff0c\u6211\u7684\u6b66\u529f\u4e0d\u4f46\u4e0d\u8f93\u5979\uff0c\u800c\u4e14\u8fd8\u80dc\u8fc7\u5979\uff0c\u5979\u5728\u6218 \u8d25\u4e4b\u540e\uff0c\u4fbf\u4e00\u53bb\u6ca1\u6709\u4e0b\u843d\u3002\u82e5\u4e0d\u662f\u4f60\u63d0\u8d77\uff0c\u6211\u6839\u672c\u4e0d\u77e5\u9053\u5979\u540e\u6765\u7adf\u5ac1\u7ed9\u5723\u624b\u4e66\u751f\u5e90\u4e91\uff0c\u5f53\u7136 \u4e5f\u66f4\u4e0d\u77e5\u9053\u5979\u5f03\u592b\u548c\u9a6c\u662d\u96c4\u6deb\u5954\u7684\u4e8b\u3002\u201d \u3000\u3000\u542c\u4e86\u8fd9\u6bb5\u7ecf\u8fc7\uff0c\u5cb3\u5c0f\u98de\u771f\u662f\u4e0d\u80dc\u611f\u6168\uff0c\u4ed6\u89c9\u5f97\u6bcd\u4eb2\u5bf9\u51e4\u5ae3\u7ea2\u6240\u77e5\u9053\u7684\u8fd8\u5e76\u4e0d\u7b97\u591a\uff0c\u56e0\u4e3a \u5979\u9664\u4e86\u9a6c\u662d\u96c4\uff0c\u66f4\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u505a\u8fc7\u5165\u5e55\u4e4b\u5bbe\uff0c\u5c31\u8fde\u81ea\u5df1\u4e00\u4e2a\u5341\u4e09\u56db\u5c81\u7684\u5b69\u5b50\uff0c\u5979\u51e0\u4e4e\u90fd\u4e0d \u60f3\u653e\u8fc7\u3002\u4eba\u4e4b\u65e0\u803b\u5230\u4e86\u8fd9\u79cd\u5730\u6b65\uff0c\u4e0e\u79bd\u517d\u4f55\u5f02\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u9ed8\u4e86\u4e00\u9ed8\u95ee\u9053\uff1a\u201c\u5979\u5728\u5a18\u624b\u4e0b\u8d25\u8d70\u4ee5\u540e\uff0c\u5a18\u53ef\u66fe\u53bb\u8ffd\u8e2a\u5979\uff1f\u201d \u3000\u3000\u4f55\u6167\u4ed9\u6447\u5934\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u56e0\u4e3a\u6211\u5fc5\u987b\u534f\u52a9\u5e08\u5a18\u6599\u7406\u5e08\u7236\u7684\u540e\u4e8b\uff0c\u5e08\u7236\u548c\u5e08\u5a18\u65e0\u513f\u65e0\u5973\uff0c \u5e08\u5a18\u5728\u5e08\u7236\u6b7b\u540e\uff0c\u4e5f\u56e0\u8fc7\u4e8e\u60b2\u4f24\u800c\u4e8e\u534a\u5e74\u540e\u4e00\u75c5\u4e0d\u8d77\u3002\u7b49\u6211\u6599\u7406\u8fc7\u5e08\u5a18\u7684\u4e27\u4e8b\u540e\uff0c\u624d\u4e0b\u5c71\u67e5 \u8bbf\u51e4\u5ae3\u7ea2\u51c6\u5907\u4e3a\u5e08\u62a5\u4ec7\uff0c\u4e0d\u4e45\u4fbf\u9047\u4e0a\u4e86\u4f60\u7236\u4eb2\uff0c\u5374\u59cb\u7ec8\u6ca1\u627e\u5230\u51e4\u5ae3\u7ea2\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5a18\u6240\u4ee5\u6ca1\u627e\u5230\u51e4\u5ae3\u7ea2\uff0c\u90a3\u662f\u56e0\u4e3a\u5e90\u4f2f\u4f2f\u548c\u5979\u5a5a\u540e\u4fbf\u5f88\u5c11\u518d\u5728\u6b66\u6797\u884c\u8d70\u3002\u201d \u3000\u3000\u4f55\u6167\u4ed9\u70b9\u70b9\u5934\u9053\uff1a\u201c\u540c\u6837\u7684\uff0c\u6211\u548c\u4f60\u7236\u4eb2\u5a5a\u540e\uff0c\u867d\u7136\u6211\u4eec\u5e74\u7eaa\u90fd\u5f88\u8f7b\uff0c\u6211\u5f53\u65f6\u624d\u5341\u516b\u4e5d \u5c81\uff0c\u4e5f\u5f88\u5c11\u6df7\u8fdb\u6b66\u6797\uff0c\u6240\u4ee5\u4e00\u822c\u6b66\u6797\u4eba\u7269\uff0c\u4e0d\u4f46\u4e0d\u8ba4\u8bc6\u4f60\u7236\u4eb2\u548c\u6211\uff0c\u5c31\u8fde\u540d\u5b57\u4e5f\u6ca1\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5728\u6bcd\u4eb2\u623f\u4e2d\u966a\u4f8d\u4e86\u5f88\u4e45\uff0c\u5f53\u4ed6\u8f9e\u51fa\u540e\uff0c\u5df2\u5feb\u5230\u665a\u996d\u65f6\u95f4\u3002 \u3000\u3000\u665a\u996d\u540e\uff0c\u603b\u7ba1\u5468\u6d77\u5c71\u4fbf\u6765\u8bf7\u793a\u4e25\u5bd2\uff0c\u5e0c\u671b\u4e25\u5bd2\u80fd\u5e26\u6240\u6709\u9ad8\u624b\u5230\u57ce\u90ed\u504f\u95e8\u9644\u8fd1\u53bb\u505a\u89c2\u5bdf\uff0c \u4ee5\u5907\u5e94\u4ed8\u4e0d\u610f\u60c5\u51b5\u3002 \u3000\u3000\u4e25\u5bd2\u867d\u89c9\u5468\u6d77\u5c71\u5bf9\u8fd9\u4e8b\u4f3c\u4e4e\u7591\u8651\u8fc7\u5206\uff0c\u5374\u4e5f\u4e0d\u4fbf\u4e0d\u7406\u4ed6\u7684\u5efa\u8bae\u3002 \u3000\u3000\u6240\u6709\u9ad8\u624b\uff0c\u5f88\u5feb\u4fbf\u5728\u5927\u5385\u5185\u96c6\u5408\u5b8c\u6bd5\uff0c\u5305\u62ec\u5173\u7433\u3001\u65b9\u5929\u94ce\u3001\u4f55\u6167\u4ed9\u3001\u8881\u5c0f\u9e64\u3001\u5cb3\u5c0f\u98de\u7b49 \u5168\u5230\u9f50\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u7684\u5173\u7433\u548c\u65b9\u5929\u94ce\uff0c\u6b66\u529f\u5df2\u65e5\u6e10\u6062\u590d\uff0c\u53c2\u6218\u81ea\u65e0\u95ee\u9898\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u4e25\u5bd2\u7684\u7387\u9886\u4e0b\uff0c\u6cbf\u7740\u57ce\u5899\u5185\u4fa7\uff0c\u4e00\u9762\u89c6\u5bdf\u9632\u52a1\uff0c\u4e00\u9762\u524d\u8fdb\u3002 \u3000\u3000\u6765\u5230\u504f\u95e8\uff0c\u504f\u95e8\u4e0a\u65b9\u6709\u4e00\u57ce\u697c\uff0c\u6240\u6709\u4eba\u90fd\u767b\u4e0a\u57ce\u697c\uff0c\u9ed8\u5bdf\u57ce\u5916\u5730\u7406\u5f62\u52bf\u3002\u5bcc\u56fd\u57ce\u4e3b\u6d3e\u51fa \u7684\u7cae\u8349\u8f8e\u91cd\uff0c\u6cbf\u7740\u5c71\u8fb9\u5411\u8fd9\u8fb9\u63a5\u8fd1\uff0c\u8def\u7ebf\u7684\u786e\u5341\u5206\u9690\u79d8\uff0c\u82e5\u65e0\u5185\u5978\uff0c\u7687\u5143\u6559\u603b\u575b\u7edd\u96be\u67e5\u77e5\u3002 \u3000\u3000\u9884\u5b9a\u7cae\u8349\u8fdb\u57ce\u7684\u65f6\u95f4\u662f\u4e09\u66f4\uff0c\u6b64\u65f6\u624d\u4e0d\u8fc7\u4e00\u66f4\uff0c\u65f6\u95f4\u8fd8\u4e45\u5f97\u5f88\uff0c\u4e25\u5bd2\u968f\u5373\u8bf7\u7fa4\u8c6a\u6682\u65f6\u56de \u53bb\u4f11\u606f\uff0c\u7b49\u4e8c\u66f4\u8fc7\u540e\u518d\u96c6\u5927\u5385\u5206\u914d\u4efb\u52a1\u3002 \u3000\u3000\u5f88\u5feb\u4fbf\u662f\u4e24\u4e2a\u65f6\u8fb0\u8fc7\u53bb\uff0c\u5728\u5927\u5385\u96c6\u4e2d\u5b8c\u540e\uff0c\u4e25\u5bd2\u8bf7\u5173\u7433\u3001\u65b9\u5929\u94ce\u3001\u4f55\u6167\u4ed9\u4e09\u4eba\u8d1f\u8d23\u5728\u57ce \u5185\u5404\u5904\u5de1\u903b\u3002\u4ed6\u81ea\u5df1\u5219\u5e26\u7740\u5468\u6d77\u5c71\u3001\u5cb3\u5c0f\u98de\u3001\u8881\u5c0f\u9e64\u4e09\u4eba\u767b\u4e0a\u4e86\u504f\u95e8\u57ce\u697c\u3002 \u3000\u3000\u57ce\u697c\u4e0a\u5e76\u672a\u70b9\u706f\uff0c\u867d\u5728\u591c\u665a\uff0c\u56e0\u6709\u6708\u5149\uff0c\u89c6\u754c\u4ecd\u751a\u826f\u597d\u3002 \u3000\u3000\u770b\u770b\u4e09\u66f4\u5df2\u5230\uff0c\u679c\u7136\u4e0d\u8fdc\u5904\u6709\u5927\u961f\u9aa1\u8f66\u4ee5\u53ca\u6311\u592b\u7b49\uff0c\u7f13\u7f13\u800c\u6765\u3002 \u3000\u3000\u5927\u961f\u9aa1\u8f66\u548c\u6311\u592b\u5f88\u5feb\u4fbf\u63a5\u8fd1\u57ce\u4e0b\u3002 \u3000\u3000\u5468\u6d77\u5c71\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u56e0\u4e3a\u4ec5\u4ee5\u9aa1\u8f66\u800c\u8bba\uff0c\u81f3\u5c11\u4e5f\u6709\u4e09\u56db\u5341\u8f86\u4e4b\u591a\uff0c\u4f3c\u4e4e\u901a\u8fd0\u5927\u5be8\u7684\u4eba \u9a6c\u5df2\u5168\u90e8\u51fa\u52a8\u3002 \u3000\u3000\u4f46\u76f8\u53cd\u7684\uff0c\u4e25\u5bd2\u5374\u559c\u51fa\u671b\u5916\uff0c\u56e0\u4e3a\u6253\u8fd9\u4e48\u591a\u7cae\u8349\u9001\u6765\uff0c\u8db3\u591f\u80b2\u5316\u57ce\u534a\u5e74\u6240\u9700\u3002 \u3000\u3000\u770b\u770b\u6765\u5230\u5207\u8fd1\uff0c\u5468\u6d77\u5c71\u7acb\u523b\u5411\u4e0b\u53eb\u9053\uff1a\u201c\u9ec4\u57ce\u4e3b\u6765\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u4e00\u540d\u9aa1\u592b\u7b54\u9053\uff1a\u201c\u57ce\u4e3b\u5728\u9aa1\u961f\u4e2d\u95f4\u4eb2\u81ea\u62bc\u961f\u3002\u201d \u3000\u3000\u5468\u6d77\u5c71\u518d\u558a\u9053\uff1a\u201c\u5404\u4f4d\u5c31\u8bf7\u5728\u57ce\u8fb9\u6682\u65f6\u4f11\u606f\u4e00\u4e0b\uff0c\u5feb\u6d3e\u4eba\u5feb\u70b9\u628a\u8d35\u57ce\u4e3b\u8bf7\u6765\uff0c\u6211\u4eec\u57ce\u4e3b \u548c\u4ed6\u6709\u8bdd\u8bb2\uff01\u201d \u3000\u3000\u5468\u6d77\u5c71\u672a\u7ecf\u4e25\u5bd2\u793a\u610f\uff0c\u64c5\u81ea\u5982\u6b64\u5904\u7f6e\uff0c\u663e\u7136\u662f\u5bf9\u8fd9\u884c\u52a8\u4e0d\u5f97\u4e0d\u8fc7\u5206\u5c0f\u5fc3\u3002 \u3000\u3000\u65e2\u7136\u5468\u6d77\u5c71\u8bdd\u5df2\u51fa\u53e3\uff0c\u4e25\u5bd2\u5f53\u7136\u4e5f\u53ea\u6709\u5982\u6b64\u505a\u3002 \u3000\u3000\u4e0d\u8db3\u7247\u523b\uff0c\u9ec4\u4e00\u9053\u4fbf\u6765\u5230\u57ce\u4e0b\uff0c\u5411\u4e0a\u95ee\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u60a8\u53ef\u662f\u627e\u5144\u5f1f\u8bb2\u8bdd\uff01\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u4e8c\u5e08\u5f1f\u8fd0\u6765\u8fd9\u6837\u591a\u7684\u7cae\u8349\uff0c\u53ea\u6015\u4e0d\u5bb9\u6613\u7792\u8fc7\u767d\u837b\u3002\u201d \u3000\u3000\u9ec4\u4e00\u9053\u9053\uff1a\u201c\u5927\u5e08\u5144\u653e\u5fc3\uff0c\u6b63\u597d\u8fd9\u6279\u7cae\u8349\u662f\u65b0\u5230\u7684\uff0c\u53ea\u8981\u4e0d\u767b\u5e10\uff0c\u603b\u575b\u5c31\u65e0\u6cd5\u67e5\u51fa\uff0c\u4f55 \u51b5\u591a\u9001\u4e9b\u7cae\u8349\u6765\uff0c\u5728\u5927\u5e08\u5144\u548c\u5144\u5f1f\u6765\u8bf4\uff0c\u90fd\u662f\u4e00\u52b3\u6c38\u9038\uff0c\u56e0\u4e3a\u4ee5\u540e\u518d\u627e\u673a\u4f1a\uff0c\u6050\u6015\u5c31\u4e0d\u5bb9\u6613 \u4e86\u3002\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u90a3\u5c31\u591a\u8c22\u4e8c\u5e08\u5f1f\uff0c\u611a\u5144\u9a6c\u4e0a\u4e0b\u57ce\u5f00\u95e8\u8fce\u63a5\u3002\u201d \u3000\u3000\u5728\u7531\u57ce\u697c\u4e0b\u6765\u65f6\uff0c\u5468\u6d77\u5c71\u9053\uff1a\u201c\u7980\u57ce\u4e3b\uff0c\u82e5\u4f9d\u5351\u804c\u7684\u610f\u601d\uff0c\u6700\u597d\u662f\u4e00\u8f66\u4e00\u8f66\u7684\u68c0\u67e5\uff0c\u6bcf \u68c0\u67e5\u4e00\u8f66\uff0c\u4fbf\u5f80\u91cc\u653e\u884c\u4e00\u8f66\uff0c\u552f\u6709\u5982\u6b64\uff0c\u624d\u662f\u5b89\u5168\u4e4b\u9053\u3002\u201d \u3000\u3000\u4e25\u5bd2\u6447\u5934\u7b11\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e5f\u60f3\u5230\u8fd9\u4e00\u5c42\uff0c\u4f46\u9ec4\u57ce\u4e3b\u548c\u6211\u4e0d\u662f\u5916\u4eba\uff0c\u5982\u679c\u8fd9\u6837\u505a\uff0c\u5c82\u4e0d\u8f9c \u8d1f\u4ed6\u4e00\u756a\u5fc3\u610f\uff1f\u201d \u3000\u3000\u5468\u6d77\u5c71\u89c1\u4e25\u5bd2\u8bf4\u51fa\u8fd9\u79cd\u8bdd\uff0c\u518d\u60f3\u5230\u81ea\u5df1\u603b\u662f\u4e2a\u4e0b\u4eba\uff0c\u4e5f\u5c31\u4e0d\u4fbf\u575a\u6301\u3002 \u3000\u3000\u6253\u5f00\u57ce\u95e8\u540e\uff0c\u9aa1\u8f66\u548c\u6311\u592b\u9c7c\u8d2f\u800c\u5165\uff0c\u4e0d\u8bba\u8f66\u8f86\u6216\u62c5\u5b50\uff0c\u770b\u6765\u90fd\u662f\u6c89\u7538\u7538\u7684\u3002 \u3000\u3000\u4e25\u5bd2\uff0c\u5468\u6d77\u5c71\u3001\u5cb3\u5c0f\u98de\u3001\u8881\u5c0f\u9e64\u90fd\u7ad9\u5728\u4e00\u65c1\u89c2\u770b\u3002 \u3000\u3000\u4e25\u5bd2\u4e3a\u4e86\u8868\u793a\u8c22\u610f\uff0c\u8fd8\u4e0d\u4f4f\u5411\u8d70\u8fc7\u7684\u9aa1\u8f66\u548c\u6311\u592b\u62f1\u624b\u8fde\u53eb\u201c\u8f9b\u82e6\u201d\u3002 \u3000\u3000\u5c31\u5728\u8f66\u961f\u548c\u6311\u592b\u901a\u8fc7\u57ce\u95e8\u7ea6\u83ab\u4e09\u5206\u4e4b\u4e8c\u65f6\uff0c\u7a81\u7136\u5728\u5bcc\u56fd\u57ce\u4e3b\u4e00\u58f0\u53f7\u4ee4\u4e4b\u4e0b\uff0c\u987f\u65f6\u6740\u58f0\u9707 \u5929\uff0c\u4e0d\u4f46\u8f66\u592b\u4eec\u90fd\u5404\u81ea\u4eae\u51fa\u5175\u5203\u8dc3\u4e0b\u8f66\u6765\uff0c\u8fde\u5404\u4e2a\u9a6e\u8f7f\u5185\u4e5f\u85cf\u7740\u4e0d\u5c11\u9ad8\u4e8e\uff0c\u52a0\u4e0a\u90a3\u4e9b\u626e\u6210\u6311 \u592b\u7684\u9ad8\u624b\uff0c\u51e0\u4e4e\u4e0d\u4e0b\u4e8c\u4e09\u767e\u4eba\u4e4b\u4f17\u3002 \u3000\u3000\u5728\u8fd9\u4e8c\u4e09\u767e\u4eba\u4e4b\u4f17\u4e2d\uff0c\u867d\u975e\u4e2a\u4e2a\u9ad8\u624b\uff0c\u4f46\u5374\u90fd\u662f\u7ecf\u8fc7\u7cbe\u6843\u7ec6\u9009\u80fd\u5f81\u60ef\u6218\u7684\uff0c\u8fd9\u4e00\u5c55\u5f00\u653b \u52bf\uff0c\u5c31\u50cf\u6f6e\u6c34\u906e\u5929\u76d6\u5730\u800c\u6765\u822c\uff0c\u767b\u65f6\u6df9\u6ca1\u4e86\u5168\u57ce\u3002 \u3000\u3000\u53e6\u6709\u4e25\u5bd2\u548c\u5468\u6d77\u5c71\u4e8b\u5148\u9884\u6599\u4e0d\u5230\u7684\uff0c\u662f\u90a3\u4e9b\u9aa1\u5e74\u4e2d\uff0c\u7adf\u5e26\u6765\u4e0d\u5c11\u70df\u785d\u706b\u836f\uff0c\u5f88\u5feb\u5230\u5904\u4fbf \u706b\u5149\u56db\u8d77\uff0c\u90a3\u60c5\u5f62\u7b80\u76f4\u50cf\u5f53\u5e74\u5218\u5907\u4f10\u5434\u65f6\u7684\u706b\u70e7\u8fde\u8425\u3002 \u3000\u3000\u4e25\u5bd2\u5230\u8fd9\u65f6\u624d\u5927\u5927\u540e\u6094\u672a\u542c\u5468\u6d77\u5c71\u7684\u529d\u544a\uff0c\u4ee5\u81f4\u9020\u6210\u6b64\u523b\u65e0\u6cd5\u6536\u62fe\u7684\u5c40\u9762\uff0c\u540c\u65f6\u4e5f\u628a\u9ec4 \u4e00\u9053\u6068\u5230\u4e86\u6781\u70b9\u3002 \u3000\u3000\u60c5\u52bf\u4e71\u5230\u5982\u6b64\u5730\u6b65\uff0c\u4e25\u5bd2\u548c\u5468\u6d77\u5c71\u3001\u5cb3\u5c0f\u98de\u3001\u8881\u5c0f\u9e64\u7b49\u56db\u4eba\uff0c\u6839\u672c\u4e5f\u987e\u4e0d\u5f97\u8c01\u6307\u6325\u8c01\uff0c \u53ea\u80fd\u5404\u81ea\u51b2\u5165\u4e71\u519b\u4e2d\u5c55\u5f00\u53ae\u6740\u3002 \u3000\u3000\u80b2\u5316\u57ce\u539f\u6709\u7684\u4eba\u9a6c\uff0c\u591a\u534a\u53ea\u51c6\u5907\u4e86\u5f13\u7bad\u8fce\u654c\uff0c\u8fd9\u65f6\u56e0\u4e3a\u9ed1\u591c\u95f4\u5df2\u65e0\u6cd5\u5206\u6e05\u654c\u6211\uff0c\u5f13\u7bad\u4e5f \u4e0d\u6562\u4e71\u53d1\u3002 \u3000\u3000\u53cc\u65b9\u5927\u7ea6\u6df7\u6218\u4e86\u5c06\u8fd1\u4e00\u4e2a\u65f6\u8fb0\uff0c\u9ec4\u4e00\u9053\u624d\u5c06\u4eba\u9a6c\u64a4\u51fa\u3002 \u3000\u3000\u53ca\u81f3\u5929\u660e\u540e\uff0c\u4f46\u89c1\u5230\u5904\u5c38\u4f53\u72fc\u85c9\uff0c\u8840\u6c61\u6591\u6591\u3002 \u3000\u3000\u7ecf\u8fc7\u6e05\u67e5\u7edf\u8ba1\uff0c\u654c\u65b9\u7559\u5728\u57ce\u5185\u7684\u5c38\u4f53\uff0c\u4e0d\u4e0b\u767e\u5177\u4e4b\u591a\u3002 \u3000\u3000\u81f3\u4e8e\u80b2\u5316\u57ce\u65b9\u9762\uff0c\u5f1f\u5144\u4eec\u4e5f\u9489\u56db\u4e94\u5341\u4eba\u9635\u4ea1\uff0c\u52a0\u4e0a\u53d7\u4f24\u7684\uff0c\u4e5f\u4e0d\u4e0b\u767e\u4eba\u3002 \u3000\u3000\u53e6\u5916\uff0c\u623f\u820d\u4ee5\u53ca\u91cd\u8981\u8bbe\u65bd\uff0c\u4e5f\u88ab\u70e7\u6bc1\u5341\u51e0\u5904\u3002 \u3000\u3000\u4e0d\u8fc7\u80b2\u5316\u57ce\u65b9\u9762\u4e5f\u6709\u6536\u83b7\uff0c\u6709\u4e0d\u5c11\u9a74\u3001\u9aa1\u672a\u80fd\u9003\u51fa\u3002\u53ef\u60dc\u6536\u83b7\u7684\u9aa1\u8f66\uff0c\u91cc\u9762\u7adf\u7136\u6ca1\u6709\u7cae \u79f8\uff0c\u53ea\u662f\u4e00\u4e9b\u77f3\u5934\u548c\u9ebb\u888b\u4e2d\u88c5\u7684\u9ec4\u571f\u800c\u5df2\uff0c\u602a\u4e0d\u5f97\u770b\u8d77\u6765\u597d\u50cf\u6c89\u7538\u7538\u7684\u3002 \u3000\u3000\u6700\u4ee4\u4e25\u5bd2\u7b49\u4eba\u9707\u60ca\u7684\u4e00\u4ef6\u5927\u4e8b\uff0c\u662f\u4e25\u592b\u4eba\u548c\u4e8c\u5c0f\u59d0\u4e25\u5982\u971c\u7adf\u88ab\u654c\u65b9\u63b3\u8d70\u3002 \u3000\u3000\u56e0\u4e3a\u5f53\u65f6\u80b2\u6bd4\u57ce\u65b9\u800c\u7684\u9ad8\u624b\uff0c\u90fd\u5df2\u8c03\u9063\u5728\u5916\uff0c\u53ea\u6709\u5927\u59d1\u5a18\u4e25\u5982\u51b0\u4e00\u4eba\u5728\u5185\u9662\u8d1f\u8d23\u4fdd\u62a4\u5185 \u7737\uff0c\u504f\u504f\u654c\u65b9\u4e00\u5f00\u59cb\u53ae\u6740\u4fbf\u6709\u6570\u540d\u9ad8\u624b\u76f4\u5954\u5185\u9662\uff0c\u76ee\u7684\u5c31\u5728\u63b3\u4eba\u3002 \u3000\u3000\u5927\u59d1\u5a18\u4e00\u4eba\u529b\u6218\u6570\u540d\u9ad8\u624b\uff0c\u8fde\u81ea\u5df1\u90fd\u8d1f\u4e86\u4f24\uff0c\u53c8\u600e\u80fd\u6551\u5f97\u4e86\u6bcd\u4eb2\u548c\u59b9\u59b9\u3002 \u3000\u3000\u6b21\u65e5\u4e00\u6574\u5929\u7684\u65f6\u95f4\uff0c\u80b2\u5316\u57ce\u65b9\u9762\u53ea\u80fd\u5168\u529b\u5584\u540e\uff0c\u5728\u57ce\u5185\u627e\u5904\u9690\u79d8\u6240\u5728\uff0c\u6316\u4e86\u4e24\u5904\u5927\u5751\uff0c \u5206\u522b\u5c06\u53cc\u65b9\u5c38\u4f53\u57cb\u597d\u3002 \u3000\u3000\u53e6\u5916\uff0c\u88ab\u70e7\u6bc1\u7684\u623f\u820d\u548c\u8bbe\u65bd\uff0c\u80fd\u6574\u7406\u7684\u4e5f\u5168\u529b\u4f7f\u4e4b\u590d\u539f\u3002 \u3000\u3000\u76ee\u524d\uff0c\u4e25\u5bd2\u6700\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u4fbf\u662f\u5982\u4f55\u8bbe\u6cd5\u6551\u51fa\u592b\u4eba\u548c\u4e8c\u5c0f\u59d0\u3002 \u3000\u3000\u4f46\uff0c\u82e5\u60f3\u6551\u56de\u592b\u4eba\u548c\u4e8c\u5c0f\u59d0\uff0c\u4ee5\u73b0\u5728\u7684\u60c5\u52bf\u800c\u8a00\uff0c\u6839\u672c\u662f\u4e0d\u53ef\u80fd\u7684\u4e8b\u3002 \u3000\u3000\u4e25\u5bd2\u5fd6\u5ea6\uff0c\u592b\u4eba\u548c\u4e8c\u5c0f\u59d0\u5fc5\u5b9a\u5df2\u88ab\u9001\u5230\u7687\u5143\u6559\u603b\u575b\uff0c\u5373\u4f7f\u5728\u4ece\u524d\uff0c\u80b2\u5316\u57ce\u4e5f\u65e0\u529b\u8fdb\u653b\u7687 \u5143\u6559\u603b\u575b\uff0c\u4f55\u51b5\u6b64\u523b\u53c8\u5143\u6c14\u5927\u4f24\u3002 \u3000\u3000\u73b0\u5728\uff0c\u4e25\u5bd2\u53ea\u6709\u5750\u56f0\u6101\u57ce\u4e86\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u5728\u80b2\u5316\u57ce\u60e8\u906d\u6d17\u52ab\u7684\u6b21\u65e5\u5f53\u591c\uff0c\u6084\u6084\u56de\u5230\u4e86\u4e94\u5173\u3002 \u3000\u3000\u5f53\u82b1\u7389\u9e9f\u5f97\u77e5\u60c5\u5f62\uff0c\u4e5f\u4e3a\u4e4b\u5927\u60ca\u4e0d\u5df2\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u548c\u5cb3\u5c0f\u98de\u5185\u5fc3\u90fd\u6709\u4e00\u79cd\u60f3\u6cd5\uff0c\u90a3\u5c31\u662f\u8425\u6551\u4e25\u592b\u4eba\u548c\u4e25\u5982\u971c\u7684\u8d23\u4efb\uff0c\u52bf\u5fc5\u843d\u5728\u81ea\u5df1 \u80a9\u4e0a\u3002\u56e0\u4e3a\u4ed6\u4eec\u7236\u5b50\u81f3\u5c11\u8fdb\u5165\u603b\u575b\u5e76\u4e0d\u56f0\u96be\uff0c\u53ea\u8981\u80fd\u8fdb\u5165\u603b\u575b\uff0c\u4fbf\u591a\u5c11\u6709\u4e9b\u5e0c\u671b\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u548c\u5cb3\u5c0f\u98de\u7ecf\u8fc7\u4e00\u756a\u5546\u8bae\uff0c\u51b3\u5b9a\u5148\u5230\u4e94\u5173\u89c6\u5bdf\u4e00\u756a\uff0c\u4ee5\u4fbf\u770b\u770b\u52a8\u9759\u3002 \u3000\u3000\u5982\u679c\u6b66\u5173\u7edf\u9886\u591a\u80cc\u5929\u7fc1\u5e9e\u821f\u548c\u4ed6\u5c5e\u4e0b\u7684\u5341\u540d\u9ad8\u624b\u90fd\u5728\u5173\u4e0a\uff0c\u82b1\u7389\u9e9f\u548c\u5cb3\u5c0f\u98de\u4fbf\u51c6\u5907\u5192\u9669 \u51b2\u5165\u603b\u575b\uff0c\u4e0d\u60dc\u548c\u7687\u5143\u6559\u4e3b\u4e0e\u79e6\u69d0\u5c55\u5f00\u786c\u62da\uff0c\u80fd\u6551\u51fa\u4e25\u592b\u4eba\u548c\u4e25\u5982\u971c\u5f53\u7136\u6700\u597d\uff0c\u5426\u5219\uff0c\u4e5f\u8981 \u628a\u5927\u516c\u4e3b\u6216\u8005\u4e8c\u516c\u4e3b\u63b3\u5230\u624b\u4ee5\u505a\u4eba\u8d28\u3002 \u3000\u3000\u8ba1\u8bae\u5df2\u5b9a\uff0c\u5c31\u5728\u65e9\u996d\u540e\uff0c\u82b1\u7389\u9e9f\u4fbf\u5e26\u7740\u5cb3\u5c0f\u98de\u3001\u8881\u5c0f\u9e64\u3001\u4e01\u6d9b\uff0c\u7518\u9716\u56db\u4eba\u5f00\u59cb\u5de1\u5173\uff0c\u4ed6 \u4eec\u662f\u7531\u8fdc\u800c\u8fd1\u9006\u5e8f\u5de1\u89c6\uff0c\u7b2c\u4e00\u7ad9\u5148\u5230\u4e86\u201c\u89c2\u5149\u96a7\u9053\u201d\u3002 \u3000\u3000\u201c\u89c2\u5149\u96a7\u968f\u201d\u5916\u7684\u8c37\u53e3\u65e9\u5df2\u5c01\u95ed\uff0c\u5916\u9762\u5df2\u65e0\u4eba\u518d\u80fd\u8fdb\u5165\u5929\u8c37\u3002 \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u96a7\u9053\u89c2\u5149\u4e8b\u4e1a\uff0c\u4e5f\u6682\u505c\u4e86\u4e0b\u6765\u3002\u90a3\u4e9b\u59d1\u5a18\u4eec\u95f2\u7740\u6ca1\u4e8b\uff0c\u3000\u4e2a\u4e2a\u53cd\u800c\u663e\u5f97\u61d2\u6d0b\u6d0b \u7684\uff0c\u5c31\u50cf\u7a91\u59d0\u4eec\u6ca1\u5ba2\u4eba\u53ef\u63a5\u4e00\u6837\u7684\u4e0d\u81ea\u5728\uff0c\u8fd9\u5c31\u662f\u6240\u8c13\u7684\u8d31\u9aa8\u5934\u3002 \u3000\u3000\u518d\u6765\u5230\u6587\u5173\uff0c\u6587\u5173\u7edf\u9886\u5218\u7ee7\u5fb7\u548c\u4e3b\u8003\u5b98\u6768\u51e4\u5802\u90fd\u51fa\u6765\u8fce\u63a5\u3002 \u3000\u3000\u8fd9\u4e00\u5173\u76ee\u524d\u662f\u6700\u60a0\u95f2\u7684\uff0c\u7687\u5143\u6559\u5185\u53d1\u751f\u4e86\u5927\u5927\u7684\u4e8b\u60c5\u3002\u90fd\u627e\u4e0d\u5230\u4ed6\u4eec\u5934\u4e0a\u3002 \u3000\u3000\u63a5\u7740\u5230\u8fbe\u6b66\u5173\u3002 \u3000\u3000\u5de1\u89c6\u6b66\u5173\uff0c\u662f\u82b1\u7389\u9e9f\u7b49\u4eba\u53ea\u611f\u5934\u75db\u7684\u4e8b\uff0c\u56e0\u4e3a\u4ed6\u4eec\u5bf9\u5de1\u5173\u7684\u4eba\u6839\u672c\u4e0d\u7406\u4e0d\u776c\uff0c\u8fde\u7687\u5143\u6559 \u4e3b\u548c\u79e6\u69d0\u90fd\u5bf9\u4ed6\u4eec\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u662f\u786c\u7740\u5934\u76ae\u8fdb\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u91cc\u9762\u5e76\u65e0\u591a\u80cc\u5929\u7fc1\u5e9e\u821f\uff0c\u5341\u540d\u9ad8\u624b\u4e2d\u4e5f\u4ec5\u6709\u4e94\u516d\u4eba\u5728\u3002 \u3000\u3000\u8fd9\u4f7f\u82b1\u7389\u9e9f\u987f\u65f6\u5927\u611f\u4e0d\u5b89\uff0c\u56e0\u4e3a\u82e5\u5e9e\u821f\u5e26\u7740\u51e0\u540d\u9ad8\u624b\u7559\u5728\u603b\u575b\uff0c\u90a3\u4e48\u8981\u60f3\u8425\u6551\u4e25\u592b\u4eba\u548c \u4e25\u5982\u971c\uff0c\u5c31\u534a\u70b9\u5e0c\u671b\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u4e0b\u4e00\u7ad9\u4fbf\u662f\u706b\u5173\u3002 \u3000\u3000\u706b\u5173\u7edf\u9886\u8427\u7476\u867d\u4e5f\u51fa\u6765\u8fce\u63a5\uff0c\u4f46\u5374\u795e\u8272\u60b2\u621a\uff0c\u4e00\u526f\u4f24\u5fc3\u6b32\u7edd\u6a21\u6837\u3002 \u3000\u3000\u5979\u7684\u80de\u5144\u62db\u8d24\u9986\u4e3b\u8427\u6e58\u60e8\u906d\u6a2a\u6b7b\uff0c\u81f3\u4eca\u8fde\u51f6\u624b\u90fd\u6ca1\u67e5\u51fa\uff0c\u6559\u5979\u600e\u80fd\u597d\u8fc7\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u56e0\u5728\u62db\u8d24\u9986\u62c5\u4efb\u8fc7\u526f\u9986\u4e3b\uff0c\u6b64\u523b\u4e0d\u5f97\u4e0d\u5047\u610f\u5b89\u6170\u5979\u4e00\u756a\u3002 \u3000\u3000\u8427\u7476\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\uff0c\u6740\u5979\u80de\u5144\u7684\u51f6\u624b\u5c31\u5728\u773c\u524d\u3002 \u3000\u3000\u518d\u5230\u6c34\u5173\u3002 \u3000\u3000\u6c34\u5173\u7edf\u9886\u7504\u79c0\uff0c\u53ea\u6709\u5341\u516b\u4e5d\u5c81\uff0c\u4e0d\u4f46\u4eba\u957f\u5f97\u7f8e\u5982\u5929\u4ed9\uff0c\u5f85\u4eba\u4e5f\u4eb2\u5207\u548c\u853c\uff0c\u82b1\u7389\u9e9f\u7b49\u4eba\u90fd \u5bf9\u5979\u9887\u6709\u597d\u611f\u3002 \u3000\u3000\u6c34\u3001\u706b\u4e24\u5173\uff0c\u76ee\u524d\u81ea\u7136\u4e5f\u662f\u95f2\u7740\uff0c\u4f46\u6c34\u5173\u7edf\u9886\u7504\u79c0\u5374\u5229\u7528\u8fd9\u51e0\u5929\u95f2\u6687\u65f6\u95f4\uff0c\u628a\u6240\u5c5e\u5973\u5175 \u96c6\u5408\u8d77\u6765\u8bfb\u4e66\u4e60\u5b57\uff0c\u82b1\u7389\u9e9f\u7b49\u4eba\u5230\u8fbe\u65f6\uff0c\u5979\u6b63\u5728\u4e0a\u8bfe\uff0c\u600e\u4e0d\u4ee4\u4eba\u79f0\u8d5e\u3002 \u3000\u3000\u5de1\u89c6\u8fc7\u4e94\u5173\uff0c\u5df2\u5c06\u4e2d\u5348\uff0c\u4fbf\u5148\u56de\u5230\u4e94\u5173\u603b\u9547\u5e9c\u5348\u9910\u3002 \u3000\u3000\u4e0b\u5348\uff0c\u518d\u5230\u5bb5\u5173\u3002 \u3000\u3000\u8fd9\u65f6\u3002\u82b1\u7389\u9e9f\u7b49\u4e94\u4eba\u7684\u5fc3\u60c5\u5404\u6709\u4e0d\u540c\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u548c\u5cb3\u5c0f\u98de\u5bf9\u51e4\u5ae3\u7ea2\u662f\u65e2\u9119\u89c6\u53c8\u75db\u6068\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u9664\u9119\u89c6\u75db\u6068\u4e4b\u5916\uff0c\u66f4\u6709\u4e00\u79cd\u7f6a\u6076\u611f\u3002 \u3000\u3000\u7518\u9716\u5219\u662f\u5fc3\u733f\u610f\u9a6c\u800c\u8868\u9762\u4e0a\u5374\u53c8\u663e\u5f97\u5341\u5206\u89c4\u77e9\u3002 \u3000\u3000\u552f\u6709\u4e01\u6d9b\uff0c\u89c1\u4e86\u5979\u7b80\u76f4\u795e\u9b42\u98d8\u8361\uff0c\u53ef\u60dc\u81ea\u4ece\u90a3\u4e00\u6b21\u540e\uff0c\u4ed6\u81f3\u4eca\u672a\u518d\u5c1d\u5230\u7b2c\u4e8c\u6b21\u751c\u5934\u3002 \u3000\u3000\u5230\u8fbe\u5bb5\u5173\uff0c\u56e0\u4e3a\u5e76\u672a\u4e8b\u5148\u901a\u77e5\uff0c\u5f53\u7136\u4e0d\u4f1a\u518d\u6709\u5973\u5175\u4eea\u961f\u76f8\u8fce\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u95fb\u8baf\u8fde\u5fd9\u8fce\u4e86\u51fa\u6765\uff0c\u5f84\u81ea\u628a\u82b1\u7389\u9e9f\u63a5\u5f85\u5728\u5979\u90a3\u4f4f\u5904\u7684\u5c0f\u5ba2\u5385\u91cc\uff0c\u4ee5\u793a\u4eb2\u5207\u3002 \u3000\u3000\u5979\u6697\u4e2d\u7559\u795e\u540c\u6765\u7684\u4e94\u4e2a\u7537\u4eba\uff0c\u771f\u662f\u4e0d\u6015\u4e0d\u8bc6\u8d27\uff0c\u53ea\u6015\u8d27\u6bd4\u8d27\uff0c\u50cf\u4e01\u6d9b\u90a3\u79cd\u5fb7\u6027\uff0c\u7ad9\u5728\u82b1 \u7389\u9e9f\u8eab\u65c1\uff0c\u7b80\u76f4\u662f\u4e0d\u80fd\u770b\u4e86\uff0c\u800c\u81ea\u5df1\u504f\u504f\u90a3\u665a\u65f6\u4ed6\u4e5f\u5e03\u65bd\u96e8\u9732\uff0c\u73b0\u5728\u60f3\u8d77\u6765\u771f\u662f\u7a9d\u56ca\u900f\u9876\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u5f53\u7136\u4e0d\u4f1a\u5728\u51e4\u5ae3\u7ea2\u8fd9\u91cc\u591a\u803d\u8bef\u65f6\u95f4\uff0c\u4f46\u5979\u662f\u5173\u952e\u4eba\u7269\uff0c\u5728\u4e94\u5173\u4e2d\uff0c\u53ea\u6709\u5979\u5bf9\u603b\u575b \u6d88\u606f\u7075\u901a\uff0c\u8981\u60f3\u95ef\u8fdb\u603b\u575b\u6551\u51fa\u4e25\u5927\u4eba\u548c\u4e25\u5982\u971c\uff0c\u8bd5\u63a2\u8bd5\u63a2\u5979\u7684\u8bed\u6c14\u603b\u662f\u6709\u5e2e\u52a9\u7684\u3002 \u3000\u3000\u4ed6\u5750\u4e0b\u540e\u559d\u4e86\u53e3\u8336\u9053\uff1a\u201c\u98ce\u7edf\u9886\u6700\u8fd1\u53ef\u66fe\u5230\u8fc7\u603b\u575b\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u6447\u5934\u9053\uff1a\u201c\u81ea\u4ece\u4e0a\u6b21\u653b\u6253\u80b2\u5316\u57ce\u540e\uff0c\u5351\u804c\u8fd9\u591a\u5929\u4e00\u76f4\u4e0d\u66fe\u5230\u8fc7\u603b\u575b\u3002\u201d \u3000\u3000\u539f\u6765\u51e4\u5ae3\u7ea2\u81ea\u4ece\u7531\u4e01\u6d9b\u90a3\u91cc\u5f97\u77e5\u82b1\u7389\u9e9f\u5c45\u7136\u548c\u4f55\u6167\u4ed9\u662f\u592b\u59bb\u540e\uff0c\u4e5f\u6709\u4e86\u6212\u5fc3\uff0c\u5f53\u7136\u4e5f\u5c31 \u4e0d\u80af\u5bf9\u82b1\u7389\u9e9f\u8bf4\u5b9e\u8bdd\u3002 \u3000\u3000\u82b1\u7389\u9e9f\u642d\u8baa\u7740\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u51e4\u7edf\u9886\u5bf9\u603b\u575b\u65b9\u9762\u7684\u4e8b\u5e76\u4e0d\u6e05\u695a\u4e86\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u4ee5\u524d\u603b\u575b\u548c\u5bb5\u5173\u662f\u76f4\u63a5\u8054\u7cfb\u7684\uff0c\u81ea\u4ece\u4e94\u5173\u603b\u9547\u5e9c\u6210\u7acb\u540e\uff0c\u73b0\u5728\u662f\u5c42\u5c42\u8282\u5236\uff0c \u6709\u5173\u603b\u575b\u65b9\u9762\u7684\u6d88\u606f\uff0c\u8be5\u7531\u603b\u9547\u8f6c\u8fbe\u624d\u5bf9\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u60f3\u4e0d\u5230\u5c45\u7136\u78b0\u5230\u4e86\u51e4\u5ae3\u625b\u7684\u8f6f\u9489\u5b50\uff0c\u4e5f\u5c31\u4e0d\u518d\u8bf4\u4ec0\u4e48\uff0c\u968f\u5373\u79bb\u5f00\u5bb5\u5173\u3002 \u3000\u3000\u665a\u996d\u540e\uff0c\u82b1\u7389\u9e9f\u7ecf\u8fc7\u4ed4\u7ec6\u8003\u8651\uff0c\u51b3\u5b9a\u5192\u9669\u95ef\u4e00\u6b21\u603b\u575b\u3002 \u3000\u3000\u4ed6\u60f3\u5230\uff1a\u4e0d\u5165\u864e\u7a74\uff0c\u7109\u5f97\u864e\u5b50\u3002\u8981\u60f3\u6551\u51fa\u4e25\u592b\u4eba\u548c\u4e8c\u5c0f\u59d0\u4e25\u5982\u971c\uff0c\u6210\u8d25\u5b8c\u5168\u5728\u6b64\u4e00\u4e3e\u4e86\u3002 \u3000\u3000\u4ed6\u5bf9\u4e01\u6d9b\u548c\u7518\u9716\u968f\u4fbf\u4ea4\u4ee3\u4e86\u51e0\u53e5\uff0c\u4fbf\u5e26\u7740\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u76f4\u5954\u603b\u575b\u3002 \u3000\u3000\u7531\u4e94\u5173\u5230\u603b\u575b\u4ec5\u534a\u4e2a\u591a\u65f6\u8fb0\u7684\u8def\u7a0b\u3002 \u3000\u3000\u5230\u8fbe\u603b\u575b\u65f6\u5df2\u662f\u4e00\u66f4\u8fc7\u540e\uff0c\u82b1\u7389\u9e9f\u627e\u4e86\u4e00\u5904\u9690\u79d8\u6240\u5728\u505c\u4e0b\uff0c\u4ee5\u4fbf\u4ea4\u4ee3\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u5e94 \u5982\u4f55\u914d\u5408\u884c\u52a8\u3002 \u3000\u3000\u4ed6\u6307\u7740\u603b\u575b\u5de6\u65b9\u571f\u5761\u540e\u4e00\u68f5\u5927\u6811\u9053\uff1a\u201c\u6211\u5148\u8fdb\u53bb\uff0c\u4e0d\u7ba1\u4e8b\u60c5\u662f\u5426\u6210\u529f\uff0c\u4f60\u4eec\u4e24\u4eba\u90fd\u8981\u5728 \u90a3\u68f5\u5927\u6811\u540e\u7b49\u6211\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5927\u4e3a\u5173\u5207\u7684\u95ee\u9053\uff1a\u201c\u7236\u4eb2\u5927\u7ea6\u4ec0\u4e48\u65f6\u5019\u53ef\u4ee5\u5230\u5927\u6811\u4e0b\u548c\u5b69\u513f\u4eec\u76f8\u4f1a\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u5f88\u96be\u9884\u6599\uff0c\u4e0d\u8fc7\uff0c\u82e5\u4f60\u4eec\u7b49\u5230\u4e00\u4e2a\u65f6\u8fb0\u540e\u4ecd\u4e0d\u89c1\u6211\uff0c\u90a3\u5c31\u8868\u793a\u6211\u5fc5\u5b9a\u51fa\u4e86 \u610f\u5916\uff0c\u90a3\u65f6\u4f60\u4eec\u5c31\u7528\u4e0d\u7740\u518d\u7b49\u4e86\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u82e5\u5f53\u771f\u526f\u603b\u9547\u53d1\u751f\u4e0d\u6d4b\uff0c\u5c5e\u4e0b\u548c\u5c0f\u98de\u8be5\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u4f60\u4eec\u5343\u4e07\u4e0d\u53ef\u518d\u8e48\u7f57\u7f51\uff0c\u4e5f\u4e0d\u53ef\u518d\u56de\u4e94\u5173\uff0c\u8981\u9a6c\u4e0a\u8d76\u5230\u80b2\u5316\u57ce\u53bb\u3002\u201d \u3000\u3000\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u518d\u8981\u8bf4\u8bdd\uff0c\u82b1\u7389\u9e9f\u5df2\u7136\u5934\u4e5f\u4e0d\u56de\u7684\u79bb\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u82b1\u7389\u9e9f\u5fc3\u5ff5\u72b9\u5982\u7535\u8f6c\uff0c\u4ed6\u6839\u672c\u5f04\u4e0d\u6e05\u4e25\u592b\u4eba\u548c\u4e25\u5982\u971c\u88ab\u76d1\u7981\u5728\u4f55\u5904\uff0c\u4e0e\u5176\u5077\u5077\u6478 \u6478\u7684\u884c\u52a8\uff0c\u5b9e\u4e0d\u5982\u5927\u5927\u65b9\u65b9\u7684\u76f4\u63a5\u9762\u89c1\u6559\u4e3b\u3002 \u3000\u3000\u6765\u5230\u603b\u575b\u5bab\u5ba4\u4e4b\u5916\uff0c\u65e9\u6709\u503c\u591c\u7684\u62a4\u575b\u4f7f\u8005\u4e0a\u524d\u67e5\u95ee\u3002 \u3000\u3000\u90a3\u4f7f\u8005\u4e00\u89c1\u662f\u82b1\u7389\u9e9f\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u82b1\u526f\u603b\u9547\u600e\u4e48\u8fd9\u65f6\u5019\u6765\u5230\u603b\u575b\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u82b1\u67d0\u6709\u4e8b\u60f3\u9762\u8c12\u6559\u4e3b\uff0c\u70e6\u52b3\u4ee3\u4e3a\u901a\u62a5\uff01\u201d \u3000\u3000\u90a3\u4f7f\u8005\u53bb\u540e\u4e0d\u4e45\u4fbf\u51fa\u6765\u9053\uff1a\u201c\u6559\u4e3b\u5728\u540e\u5bab\u53ec\u89c1\u82b1\u526f\u603b\u9547\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u76f4\u5954\u540e\u5bab\uff0c\u8fdb\u5165\u4e4b\u540e\uff0c\u53ea\u89c1\u7687\u5143\u6559\u4e3b\u767d\u837b\u65e9\u5df2\u9ad8\u9ad8\u5750\u5728\u5b9d\u5ea7\u4e0a\u3002 \u3000\u3000\u5728\u5979\u8eab\u65c1\uff0c\u9664\u4e86\u603b\u62a4\u6cd5\u79e6\u69d0\u5916\uff0c\u53e6\u6709\u5bcc\u56fd\u57ce\u4e3b\u9ec4\u4e00\u9053\u3002 \u3000\u3000\u4e00\u540d\u4f8d\u5a62\u63ed\u8d77\u73e0\u5ec9\uff0c\u82b1\u7389\u9e9f\u5fc5\u606d\u5fc5\u656c\u7684\u8d70\u4e86\u8fdb\u53bb\uff0c\u8eac\u8eab\u4e00\u793c\u9053\uff1a\u201c\u5351\u804c\u4e94\u5173\u526f\u603b\u9547\u82b1\u7389 \u9e9f\u53c2\u89c1\u6559\u4e3b\uff01\u201d \u3000\u3000\u7687\u5143\u6559\u4e3b\u795e\u8272\u95f4\u4e0d\u5e26\u534a\u70b9\u8868\u60c5\uff0c\u77a5\u8fc7\u82b1\u7389\u9e9f\u4e00\u773c\u9053\uff1a\u201c\u8fd9\u65f6\u5019\u5230\u603b\u575b\u6c42\uff0c\u53ef\u662f\u6709\u8981\u7d27\u7684 \u4e8b\u4e48\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9053\uff1a\u201c\u80b2\u5316\u57ce\u4e45\u4e45\u653b\u6253\u4e0d\u4e0b\uff0c\u5351\u804c\u5bf9\u8fd9\u4e8b\u5341\u5206\u5173\u5fc3\uff0c\u76ee\u524d\u4e94\u5173\u65e0\u4e8b\uff0c\u5351\u804c\u60f3\u5230\u8be5 \u6765\u8d9f\u603b\u575b\uff0c\u4ee5\u4fbf\u542c\u5019\u5dee\u9063\u3002\u201d \u3000\u3000\u767d\u837b\u54fc\u4e86\u58f0\u9053\uff1a\u201c\u653b\u6253\u80b2\u5316\u57ce\uff0c\u672c\u6559\u4e3b\u548c\u603b\u62a4\u6cd5\u81ea\u6709\u5b9a\u593a\uff0c\u7528\u4e0d\u7740\u4f60\u5173\u5fc3\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u4e0d\u89c9\u5fc3\u5934\u4e00\u9707\uff0c\u6697\u611f\u4e0d\u5999\uff0c\u4ed6\u4ece\u524d\u4e5f\u66fe\u591a\u6b21\u8c12\u89c1\u8fc7\u767d\u837b\uff0c\u5bf9\u65b9\u4ece\u4e0d\u66fe\u4ee5\u8fd9\u79cd\u6001\u5ea6 \u548c\u8bed\u6c14\u76f8\u5f85\uff0c\u83ab\u975e\u5176\u4e2d\u6709\u53d8\uff1f\u2026\u2026 \u3000\u3000\u4f46\u4ed6\u4ecd\u80fd\u5341\u5206\u9547\u5b9a\uff0c\u642d\u8baa\u7740\u9053\uff1a\u201c\u5351\u804c\u9664\u4e86\u521a\u624d\u6240\u8bf4\u7684\uff0c\u53e6\u6709\u4e00\u4e8b\uff0c\u4e5f\u60f3\u5411\u6559\u4e3b\u8bf7\u793a\uff01\u201d \u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u4f60\u8bf4\uff01\u201d \u3000\u3000\u201c\u5351\u804c\u4eca\u5929\u66fe\u5230\u540d\u5173\u5de1\u89c6\uff0c\u5f53\u5de1\u522b\u706b\u5173\uff0c\u706b\u5173\u8427\u7edf\u9886\u76ee\u524d\u4ecd\u4f24\u5fc3\u6b32\u7edd\uff0c\u60c5\u7eea\u4e0d\u7a33\uff0c\u4e00\u5fc3 \u53ea\u76fc\u65e9\u65e5\u67e5\u51fa\u884c\u523a\u62db\u8d24\u9986\u4e3b\u7684\u51f6\u624b\uff0c\u56e0\u4e4b\uff0c\u5351\u804c\u5230\u603b\u575b\u6765\uff0c\u4e5f\u60f3\u6253\u542c\u4e00\u4e0b\u51f6\u624b\u662f\u5426\u7f09\u6355\u5230 \u6848\uff1f\u201d \u3000\u3000\u201c\u8fd9\u4e8b\u4e5f\u7528\u4e0d\u7740\u4f60\u7ba1\uff0c\u4e00\u65e6\u902e\u5230\u51f6\u624b\uff0c\u672c\u6559\u4e3b\u4f1a\u76f4\u63a5\u901a\u77e5\u706b\u5173\u8427\u7edf\u9886\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u6599\u60f3\u6b64\u6765\u76ee\u7684\u96be\u8fbe\uff0c\u6b63\u60f3\u544a\u9000\uff0c\u5374\u542c\u767d\u837b\u9053\uff1a\u201c\u82b1\u526f\u603b\u9547\uff0c\u672c\u6559\u4e3b\u60f3\u95ee\u95ee\u4f60\uff0c\u4f60 \u5bf9\u7687\u5143\u6559\u662f\u5426\u771f\u6709\u6548\u5fe0\u4e4b\u5fc3\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u5927\u5403\u4e00\u60ca\uff0c\u5f3a\u505a\u9547\u5b9a\u9053\uff1a\u201c\u5351\u804c\u4e0d\u660e\u767d\u6559\u4e3b\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u6837\u95ee\u8bdd\uff1f\u83ab\u975e\u5351\u804c\u505a\u9519\u4e86 \u4ec0\u4e48\uff1f\u201d \u3000\u3000\u767d\u837b\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u7136\u80fd\u5fe0\u5fc3\u4e8e\u672c\u6559\u4e3b\uff0c\u5c31\u4e0d\u8be5\u6709\u5f88\u591a\u4e8b\u60c5\u7792\u9996\u6211\uff01\u201d \u3000\u3000\u201c\u5351\u804c\u4e0d\u77e5\u7a76\u7adf\u6709\u4ec0\u4e48\u4e8b\u7792\u8fc7\u6559\u4e3b\uff1f\u201d \u3000\u3000\u201c\u8fd8\u6562\u5f3a\u8fa9\uff0c\u4f60\u548c\u6253\u5165\u80b2\u5316\u57ce\u7075\u5802\u4e3a\u5974\u73b0\u5728\u5df2\u88ab\u4e25\u5bd2\u653e\u51fa\u7684\u4f55\u6167\u4ed9\u662f\u5426\u592b\u59bb\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u4e07\u60f3\u4e0d\u5230\u8fd9\u4e8b\u7adf\u88ab\u7687\u5143\u6559\u4e3b\u67e5\u77e5\uff0c\u6b64\u523b\u82e5\u62b5\u8d56\u52bf\u5fc5\u5f04\u5de7\u6210\u62d9\uff0c\u968f\u5373\u70b9\u4e86\u70b9\u5934\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u4f55\u6167\u4ed9\u6b63\u662f\u5351\u804c\u7684\u59bb\u5b50\u3002\u201d \u3000\u3000\u767d\u837b\u559d\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e00\u76f4\u7792\u7740\u6211\uff1f\u201d \u3000\u3000\u201c\u5351\u804c\u56e0\u8fdb\u5165\u5929\u8c37\u540e\uff0c\u624d\u77e5\u592b\u59bb\u4e0d\u80fd\u540c\u6765\uff0c\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u4e3a\u4e86\u6548\u5fe0\u6559\u4e3b\uff0c\u5f53\u7136\u4e0d\u4fbf\u8bf4 \u660e\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u4f60\u8be5\u77e5\u9053\uff0c\u672c\u6559\u4e3b\u65e9\u5c31\u6709\u610f\u628a\u5927\u516c\u4e3b\u91d1\u51e4\u5ac1\u4f60\uff0c\u540c\u65f6\u91d1\u51e4\u4e5f\u5bf9\u4f60\u9887\u6709\u597d\u611f\uff0c\u82e5\u679c \u771f\u6210\u4e3a\u4e8b\u5b9e\uff0c\u96be\u9053\u8981\u672c\u6559\u4e3b\u7684\u5973\u513f\u66ff\u4f60\u4f5c\u59be\u4e0d\u6210\uff1f\u201d \u3000\u3000\u201c\u5173\u4e8e\u8fd9\u4e8b\uff0c\u6559\u4e3b\u548c\u5927\u516c\u4e3b\u4ece\u6765\u4e0d\u66fe\u5bf9\u5351\u804c\u660e\u8a00\uff0c\u5426\u5219\uff0c\u5351\u804c\u81ea\u5f53\u8bf4\u660e\u4e00\u5207\u3002\u201d \u3000\u3000\u201c\u4e0d\u5fc5\u5f3a\u8bcd\u593a\u7406\uff0c\u8fd8\u6709\uff0c\u90a3\u4e2a\u53eb\u5cb3\u5c0f\u98de\u7684\uff0c\u548c\u4f60\u662f\u5426\u7236\u5b50\u5173\u7cfb\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u8d8a\u53d1\u5403\u60ca\uff0c\u7687\u5143\u6559\u4e3b\u7a76\u7adf\u4ece\u54ea\u91cc\u5f97\u77e5\u8fd9\u4e9b\u6d88\u606f\u7684\u5462\uff1f \u3000\u3000\u4ed6\u987f\u4e86\u987f\u9053\uff1a\u201c\u5351\u804c\u867d\u4e0d\u66fe\u7980\u77e5\u5cb3\u5c0f\u98de\u5c31\u662f\u72ac\u5b50\uff0c\u4f46\u4ed6\u540c\u6837\u662f\u4e3a\u6559\u4e3b\u6548\u5fe0\uff0c\u53c8\u4f55\u5fc5\u8bf4\u660e \u4ed6\u548c\u5351\u804c\u662f\u7236\u5b50\u5173\u7cfb\uff1f\u201d \u3000\u3000\u767d\u837b\u5927\u559d\u9053\uff1a\u201c\u5927\u80c6\uff0c\u4f60\u6562\u9876\u649e\u6211\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u8fde\u5fd9\u8eb2\u8eab\u9053\uff1a\u201c\u5351\u804c\u4e0d\u6562\u3002\u201d \u3000\u3000\u53ea\u542c\u79e6\u69d0\u563f\u563f\u7b11\u9053\uff1a\u201c\u82b1\u526f\u603b\u9547\uff0c\u4ece\u73b0\u5728\u8d77\uff0c\u4f60\u7528\u4e0d\u7740\u56de\u4e94\u5173\u4e86\uff0c\u90a3\u8fb9\u7684\u4e8b\uff0c\u6559\u4e3b\u5df2\u51b3 \u5b9a\u4e0b\u4ee4\u7531\u5bb5\u5173\u51e4\u7edf\u9886\u517c\u4ee3\uff0c\u6559\u4e3b\u5ff5\u4f60\u6614\u65e5\u6709\u529f\uff0c\u4e0d\u5fcd\u4ee5\u6559\u60e9\u6cbb\uff0c\u6682\u65f6\u8ba9\u4f60\u5728\u603b\u575b\uff0c\u95ed\u95e8\u601d \u8fc7\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0c\u5411\u5916\u559d\u9053\uff1a\u201c\u6765\u4eba\uff01\u201d \u3000\u3000\u4f46\u89c1\u4e24\u540d\u5927\u6c49\uff0c\u5e94\u58f0\u800c\u5165\u3002 \u3000\u3000\u6b64\u65f6\u6b64\u5730\uff0c\u82b1\u7389\u9e9f\u81ea\u7136\u4e0d\u6562\u53cd\u6297\uff0c\u56e0\u4e3a\u773c\u524d\u7684\u767d\u837b\u3001\u9ec4\u4e00\u9053\u548c\u79e6\u69d0\u4e09\u4eba\uff0c\u4e2a\u4e2a\u6b66\u529f\u9ad8\u4e0d \u53ef\u6d4b\uff0c\u518d\u52a0\u4e0a\u603b\u575b\u5185\u90e8\u9ad8\u624b\u5982\u4e91\uff0c\u82e5\u8f7b\u4e3e\u5984\u52a8\uff0c\u52bf\u5fc5\u6b7b\u8def\u4e00\u6761\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u4ed6\u53ea\u6709\u4e56\u4e56\u7684\u88ab\u4e24\u540d\u5927\u6c49\u62bc\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u79e6\u69d0\u51b7\u7b11\u4e86\u51e0\u58f0\u9053\uff1a\u201c\u6559\u4e3b\uff0c\u73b0\u5728\u60a8\u8be5\u77e5\u9053\u80b2\u5316\u57ce\u90a3\u540d\u8eab\u6750\u77ee\u5c0f\uff0c\u540d\u53eb\u5173\u5927\u9e4f\u7684\u9ad8\u624b\u662f \u8c01\u4e86\u5427\uff1f\u201d \u3000\u3000\u767d\u837b\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u83ab\u975e\u4ed6\u5c31\u662f\u5cb3\u5c0f\u98de\u6539\u626e\u7684\uff1f\u201d \u3000\u3000\u79e6\u69d0\u9053\uff1a\u201c\u9664\u4e86\u4ed6\u53c8\u6709\u8c01\uff0c\u4e0a\u6b21\u653b\u57ce\u65f6\uff0c\u6559\u4e3b\u53ef\u89c1\u8fc7\u82b1\u7389\u9e9f\u8eab\u8fb9\u6709\u5cb3\u5c0f\u98de\uff1f\u7531\u6b64\u53ef\u89c1\uff0c \u82b1\u7389\u9e9f\u548c\u4e25\u5bd2\u65e9\u6253\u52fe\u901a\uff0c\u5426\u5219\uff0c\u4e25\u5bd2\u4e3a\u4ec0\u4e48\u8981\u7518\u5192\u5927\u4e0d\u97ea\u653e\u51fa\u4e86\u4f55\u6167\u4ed9\uff0c\u800c\u4e14\u8fd8\u628a\u5979\u5f85\u4e3a\u4e0a \u5bbe\uff1f\u201d \u3000\u3000\u767d\u837b\u54ac\u4e86\u54ac\u7259\u9053\uff1a\u201c\u539f\u6765\u662f\u8fd9\u4e48\u56de\u4e8b\uff0c\u4f60\u4e0d\u662f\u8bf4\u6211\u8fd8\u771f\u88ab\u4ed6\u8499\u5728\u9f13\u91cc\u3002\u672c\u6765\u6211\u53ea\u60f3\u6682\u65f6 \u628a\u4ed6\u76d1\u7981\u5728\u603b\u575b\uff0c\u4ee5\u89c2\u540e\u6548\uff0c\u8fd9\u4e00\u6765\u90a3\u5c31\u771f\u8981\u6309\u5f8b\u6cbb\u4ed6\u7684\u7f6a\u4e86\uff01\u201d \u3000\u3000\u201c\u6559\u4e3b\u4e0d\u59a8\u9a6c\u4e0a\u6d3e\u4eba\u5230\u4e94\u5173\u770b\u770b\uff0c\u5cb3\u5c0f\u98de\u5b9a\u7136\u4e0d\u5728\uff0c\u8fde\u90a3\u4e2a\u8881\u5c0f\u9e64\u7684\uff0c\u4e5f\u53ef\u80fd\u6295\u5954\u4e86\u80b2 \u5316\u57ce\u3002\u201d \u3000\u3000\u201c\u6211\u5947\u602a\u7684\u662f\u82b1\u7389\u9e9f\u548c\u5cb3\u5c0f\u98de\u4e3a\u4ec0\u4e48\u7236\u5b50\u5e76\u4e0d\u540c\u59d3\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4eec\u82e5\u540c\u59d3\uff0c\u53c8\u5982\u4f55\u7792\u5f97\u8fc7\u5916\u4eba\uff0c\u53ef\u89c1\u4ed6\u4eec\u662f\u65e9\u6709\u9884\u8c0b\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u5728\u82b1\u7389\u9e9f\u8fdb\u5165\u603b\u575b\u540e\uff0c\u7acb\u5373\u4ece\u6697\u5904\u7ed5\u5230\u571f\u5761\u540e\u90a3\u68f5\u5927\u6811\u4e0b\u3002 \u3000\u3000\u4ed6\u4eec\u4e3a\u4e86\u9690\u533f\u8d77\u89c1\uff0c\u5168\u8dc3\u5230\u6811\u5e72\u4e0a\u3002 \u3000\u3000\u90a3\u5927\u6811\u679d\u53f6\u8302\u76db\uff0c\u4eba\u5728\u6811\u4e0a\uff0c\u5373\u4f7f\u6811\u4e0b\u6709\u4eba\u7ecf\u8fc7\uff0c\u4e5f\u4e0d\u6613\u53d1\u73b0\u6811\u4e0a\u6709\u4eba\uff0c\u540c\u65f6\u4eba\u5728\u6811\u4e0a\uff0c \u4e5f\u53ef\u4ee5\u770b\u5f97\u8fdc\u4e9b\u3002 \u3000\u3000\u8db3\u8db3\u7b49\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4ecd\u4e0d\u89c1\u82b1\u7389\u9e9f\u5230\u6765\u3002 \u3000\u3000\u4e24\u4eba\u5fc3\u77e5\u4e0d\u5999\uff0c\u5374\u53c8\u4e0d\u4fbf\u5f84\u81ea\u5954\u5f80\u80b2\u5316\u57ce\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u5c0f\u98de\uff0c\u4f9d\u6211\u770b\u526f\u603b\u9547\u5fc5\u5b9a\u53d1\u751f\u610f\u5916\uff0c\u6211\u4eec\u4e5f\u8be5\u91c7\u53d6\u884c\u52a8\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5fc3\u60ac\u7236\u4eb2\u5b89\u5371\uff0c\u7acb\u5373\u8dc3\u4e0b\u6811\u6765\uff0c\u628a\u4eba\u76ae\u9762\u5177\u6234\u4e0a\uff0c\u53eb\u9053\uff1a\u201c\u8881\u5927\u54e5\uff0c\u4f60\u4e5f\u628a\u9762\u5177 \u4e0a\uff0c\u54b1\u4eec\u5c31\u6740\u8fdb\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u4e8e\u662f\uff0c\u4e24\u4eba\u5404\u81ea\u4ed7\u5251\u5728\u624b\uff0c\u76f4\u5411\u603b\u575b\u51b2\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u4e24\u4eba\u5747\u6765\u5230\u603b\u575b\u5185\u90e8\uff0c\u6765\u5230\u5927\u6bbf\u95e8\u53e3\uff0c\u6839\u672c\u4e0d\u8bc6\u8def\u5f84\u3002 \u3000\u3000\u84e6\u5730\uff0c\u4e24\u540d\u62a4\u575b\u4f7f\u8005\u8fce\u4e0a\u524d\u6765\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff0c\u6562\u5728\u8fd9\u91cc\u4e71\u95ef\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u6717\u58f0\u9053\uff1a\u201c\u5feb\u5feb\u5e26\u6211\u4eec\u4e24\u4eba\u5230\u5185\u5bab\u53bb\uff01\u201d \u3000\u3000\u90a3\u4e24\u540d\u62a4\u4f7f\u8005\u4e00\u89c1\u82d7\u5934\u4e0d\u5bf9\uff0c\u4e0d\u518d\u7b54\u8bdd\uff0c\u633a\u8d77\u5175\u5203\u4fbf\u780d\u6740\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u6b66\u529f\u90fd\u5341\u5206\u4e86\u5f97\uff0c\u867d\u7136\u4e0d\u662f\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u7684\u5bf9\u624b\uff0c\u4f46\u5374\u62e6\u5f97\u4e24\u4eba\u96be\u4ee5\u524d\u8fdb\u3002 \u3000\u3000\u5175\u5203\u4ea4\u63a5\u4e4b\u58f0\u4e00\u8d77\uff0c\u7a81\u7136\u4e24\u8fb9\u4eea\u95e8\u5927\u95e8\uff0c\u970e\u65f6\u4fbf\u51b2\u51fa\u4e8c\u4e09\u5341\u540d\u5927\u6c49\uff0c\u624b\u6301\u5404\u5f0f\u5404\u6837\u5175\u5203\uff0c \u9f50\u5411\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u6740\u6765\u3002 \u3000\u3000\u8fd9\u4e8c\u4e09\u5341\u540d\u5927\u6c49\u4e2d\uff0c\u5c45\u7136\u6709\u6b66\u5173\u4e0a\u5e9e\u821f\u5c5e\u4e0b\u7684\u9ad8\u624b\u5728\u5185\u3002 \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e0d\u4f46\u4e0d\u80fd\u524d\u8fdb\uff0c\u800c\u4e14\u5fc5\u987b\u65bd\u5c55\u5168\u529b\u4ee5\u6c42\u81ea\u4fdd\u3002 \u3000\u3000\u518d\u8fc7\u7247\u523b\uff0c\u767d\u837b\u3001\u79e6\u69d0\u3001\u9ec4\u4e00\u9053\u4e09\u4eba\u4e5f\u5168\u7531\u4eea\u95e8\u800c\u51fa\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u5fc3\u77e5\u5df2\u7ecf\u4e8b\u4e0d\u53ef\u4e3a\uff0c\u62da\u529b\u780d\u5012\u8eab\u524d\u4e24\u4e09\u540d\u5927\u6c49\uff0c\u7136\u540e\u817e\u8eab\u800c\u8d77\uff0c\u51cc\u7a7a\u8dc3 \u51fa\u91cd\u56f4\uff0c\u5f80\u80b2\u5316\u57ce\u65b9\u9762\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u4e24\u4eba\u7684\u8f7b\u529f\uff0c\u90fd\u5df2\u7ecf\u8fbe\u5230\u767b\u5cf0\u9020\u6781\u4e4b\u5883\uff0c\u7687\u5143\u6559\u7684\u4f17\u9ad8\u624b\uff0c\u81ea\u7136\u5f88\u96be\u8ffd\u53ca\uff0c\u5373\u4f7f\u6709 \u51e0\u4e2a\u53ef\u4ee5\u52c9\u5f3a\u4e00\u8ffd\u7684\uff0c\u4e5f\u51db\u4e8e\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u7684\u8eab\u624b\uff0c\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u5230\u8fbe\u80b2\u5316\u57ce\uff0c\u60ca\u52a8\u4e86\u7761\u68a6\u4e2d\u7684\u4e25\u5bd2\u548c\u4f55\u6167\u4ed9\u7b49\u4eba\u3002 \u3000\u3000\u5f53\u4ed6\u4eec\u5f97\u77e5\u82b1\u7389\u9e9f\u4e3a\u8425\u6551\u4e25\u592b\u4eba\u548c\u4e25\u5982\u971c\u800c\u5728\u7687\u5143\u6559\u603b\u575b\u906d\u5230\u610f\u5916\uff0c\u90fd\u60ca\u75db\u4e0d\u5df2\u3002\u5c24\u5176 \u4f55\u6167\u4ed9\uff0c\u66f4\u4e3a\u4e08\u592b\u7684\u751f\u6b7b\u5b89\u5371\u62c5\u5fc3\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u867d\u52b3\u7d2f\u4e86\u5927\u534a\u591c\uff0c\u4f46\u4e0b\u534a\u591c\u5374\u4e00\u76f4\u65e0\u6cd5\u5b89\u7720\u3002 \u3000\u3000\u6b21\u65e5\u9192\u6765\uff0c\u4ed6\u4eec\u7980\u544a\u4e25\u5bd2\u548c\u4f55\u6167\u4ed9\uff0c\u51b3\u5b9a\u518d\u56de\u5230\u4e94\u5173\u4e00\u8d9f\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u4eec\u5fc5\u987b\u56de\u5230\u4e94\u5173\uff0c\u624d\u80fd\u5f97\u5230\u82b1\u7389\u9e9f\u7684\u6d88\u606f\u3002 \u3000\u3000\u4e25\u5bd2\u548c\u4f55\u6167\u4ed9\u7ecf\u8fc7\u4e00\u756a\u8003\u8651\uff0c\u7b54\u5e94\u4e86\u4ed6\u4eec\u7684\u8981\u6c42\u3002 \u3000\u3000\u51fa\u4e8e\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u610f\u5916\u7684\uff0c\u8fde\u4f55\u6167\u4ed9\u4e5f\u8981\u968f\u4ed6\u4eec\u4e00\u8d77\u53bb\uff0c\u4ee5\u4f7f\u6709\u6240\u63a5\u5e94\u3002 \u3000\u3000\u7531\u80b2\u5316\u57ce\u5230\u4e94\u5173\u4e00\u5171\u6709\u4e09\u6761\u8def\uff0c\u4ed6\u4eec\u8d70\u7684\u662f\u6700\u9690\u79d8\u7684\u4e00\u6761\u3002 \u3000\u3000\u4e09\u4eba\u5728\u8def\u90fd\u672a\u6234\u4eba\u76ae\u9762\u5177\uff0c\u56e0\u4e3a\u8fd9\u65f6\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u5c1a\u672a\u516c\u7136\u53db\u79bb\u7687\u5143\u6559\uff0c\u81f3\u4e8e\u4f55\u6167\u4ed9\uff0c \u5219\u751a\u5c11\u6709\u4eba\u8ba4\u8bc6\u5979\u5230\u8fbe\u4e94\u5173\uff0c\u5df2\u8fd1\u4e2d\u5348\uff0c\u4f55\u6167\u4ed9\u6682\u65f6\u9690\u8eab\u5728\u5927\u95e8\u5916\u7684\u6797\u836b\u6df1\u5904\uff0c\u53ea\u7531\u5cb3\u5c0f\u98de \u548c\u8881\u5c0f\u9e64\u8fdb\u53bb\u3002 \u3000\u3000\u521a\u8fdb\u5927\u95e8\uff0c\u4fbf\u89c1\u4e01\u6d9b\u548c\u7518\u9716\u7ad9\u5728\u5927\u5385\u5916\u7684\u7a7a\u5730\u4e0a\uff0c\u4e00\u526f\u6d0b\u6d0b\u81ea\u5f97\u7684\u6a21\u6837\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u89c1\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u56de\u6765\uff0c\u7518\u9716\u4fbf\u8fce\u4e0a\u524d\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u600e\u4e48\u5230\u73b0\u5728\u624d\u56de\u6765\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u6628\u665a\u6211\u548c\u5cb3\u4f7f\u8005\u53c8\u968f\u526f\u603b\u9547\u5de1\u5173\uff0c\u88ab\u7559\u5728\u6587\u5173\u4f4f\u4e86\u4e00\u591c\uff0c\u9192\u6765\u540e \u4e0d\u89c1\u4e86\u526f\u603b\u9547\uff0c\u4ed6\u8001\u4eba\u5bb6\u662f\u5426\u5148\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u7518\u9716\u9762\u5e26\u5403\u60ca\u4e4b\u8272\uff0c\u6447\u5934\u9053\uff1a\u201c\u521a\u603b\u9547\u6ca1\u56de\u6765\uff0c\u597d\u50cf\u662f\u51fa\u4e86\u4e8b\u5566\uff0c\u96be\u9053\u4f60\u4eec\u4e8c\u4f4d\u4f7f\u8005\u8fd8 \u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u867d\u7136\u5fc3\u91cc\u65e9\u5c31\u6709\u6570\uff0c\u5374\u4e5f\u88c5\u7740\u5403\u60ca\u6a21\u6837\uff0c\u9053\uff1a\u201c\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u5bb5\u5173\u7684\u51e4\u7edf\u9886\u65e9\u996d\u540e\u6765\u8fc7\uff0c\u5979\u8bf4\u526f\u603b\u9547\u5df2\u5949\u8c03\u603b\u575b\u9ad8\u5c31\uff0c\u4e94\u5173\u526f\u603b\u9547\u7684\u804c\u52a1\uff0c \u4ece\u73b0\u5728\u8d77\uff0c\u6559\u4e3b\u5df2\u6d3e\u5979\u517c\u4ee3\uff0c\u8fd9\u6837\u7684\u5927\u4e8b\uff0c\u82b1\u526f\u603b\u9547\u6628\u665a\u4e3a\u4ec0\u4e48\u4e0d\u5411\u4e8c\u4f4d\u900f\u9732\u5462\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u7728\u7740\u4e24\u773c\u9053\uff0c\u201c\u5947\u602a\uff0c\u82b1\u526f\u603b\u9547\u786e\u5b9e\u6ca1\u5bf9\u6211\u548c\u5cb3\u4f7f\u8005\u8bb2\u8fc7\uff0c\u4ed6\u8001\u4eba\u5bb6\u771f\u79f0\u5f97\u4e0a\u5b88 \u53e3\u5982\u74f6\u4e86\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u95ee\u9053\uff1a\u201c\u51e4\u7edf\u9886\u65e9\u4e0a\u6765\u65f6\uff0c\u53ef\u66fe\u4ea4\u4ee3\u8fc7\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u5979\u5df2\u517c\u4ee3\u4e94\u5173\u526f\u603b\u9547\uff0c\u66fe\u4ea4\u4ee3\u6211\u4eec\u6682\u65f6\u4e0d\u5fc5\u5de1\u5173\uff0c\u5979\u5f85\u4f1a\u513f\u8fd8\u8981\u6765\uff0c\u5230\u90a3\u65f6 \u518d\u5206\u914d\u5de5\u4f5c\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u5979\u53ef\u95ee\u8fc7\u8881\u4f7f\u8005\u548c\u6211\u600e\u4e48\u4e0d\u5230\uff1f\u201d \u3000\u3000\u7518\u9716\u9053\uff1a\u201c\u5f53\u7136\u95ee\u8fc7\u3002\u201d \u3000\u3000\u201c\u4f60\u600e\u4e48\u56de\u7b54\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u4eec\u53ea\u80fd\u5b9e\u60c5\u5b9e\u62a5\uff0c\u8bf4\u4e24\u4f4d\u4f7f\u8005\u6628\u665a\u53bb\u5de1\u5173\uff0c\u5230\u73b0\u5728\u8fd8\u6ca1\u56de\u6765\u3002\u201d \u3000\u3000\u201c\u5979\u5f53\u65f6\u6709\u4ec0\u4e48\u8868\u793a\uff1f\u201d \u3000\u3000\u201c\u5979\u4ec0\u4e48\u8bdd\u4e5f\u6ca1\u8bf4\u3002\u201d \u3000\u3000\u8fd9\u65f6\u6700\u611f\u5174\u594b\u7684\uff0c\u83ab\u8fc7\u4e8e\u4e01\u6d9b\uff0c\u56e0\u4e3a\u4e94\u5173\u526f\u603b\u9547\u5df2\u6362\u4e86\u51e4\u5ae3\u7ea2\uff0c\u800c\u51e4\u5ae3\u7ea2\u53c8\u548c\u4ed6\u6709\u8fc7\u4e00 \u624b\uff0c\u4ece\u4eca\u540e\uff0c\u8fd1\u6c34\u697c\u53f0\uff0c\u90a3\u65e5\u5b50\u8981\u5bb3\u597d\u8fc7\u5c31\u591a\u597d\u8fc7\uff0c\u53ea\u662f\u5728\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u9762\u524d\uff0c\u4e0d\u4fbf\u8fc7\u5206 \u5f62\u4e4b\u4e8e\u5916\u7f62\u4e86\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u51e4\u5ae3\u7ea2\u5df2\u5728\u51e0\u540d\u4f8d\u5a62\u7684\u966a\u540c\u7531\u5927\u95e8\u5916\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u8fde\u5fd9\u6bd5\u606d\u6bd5\u656c\u7684\u8fc7\u53bb\u8fce\u63a5\uff0c\u4e00\u526f\u9f50\u80a9\u9677\u7b11\u6a21\u6837\uff0c\u5dee\u70b9\u513f\u6ca1\u8ba9\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64 \u5455\u51fa\u6765\u3002 \u3000\u3000\u5c24\u5176\u4e01\u6d9b\uff0c\u9f87\u7740\u6ee1\u5634\u51e0\u4e4e\u53ef\u4ee5\u6ef4\u51fa\u6cb9\u6765\u7684\u9ec4\u7259\uff0c\u8fde\u6d8e\u6c34\u90fd\u51e0\u4e4e\u6d41\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u770b\u4e86\u4ed6\u8fd9\u526f\u5fb7\u6027\uff0c\u518d\u60f3\u8d77\u7236\u4eb2\u751f\u6b7b\u96be\u6599\uff0c\u4e00\u65f6\u6012\u4ece\u5fc3\u4e0a\u8d77\uff0c\u771f\u6068\u4e0d\u5f97\u4e0a\u53bb\u4e09\u62f3\u4e24 \u811a\u628a\u4e01\u6d9b\u6253\u6b7b\u5728\u9762\u524d\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u6839\u672c\u6ca1\u7406\u4f1a\u4e01\u6d9b\u548c\u7518\u9716\u7684\u5351\u8eac\u5c48\u819d\u76f8\u8fce\uff0c\u5374\u76f4\u5411\u5cb3\u5c0f\u98de\uff0c\u8881\u5c0f\u9e64\u7ad9\u8eab\u4e4b\u5904\u8d70\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u65e9\u5df2\u5fc3\u610f\u76f8\u901a\uff0c\u4ed6\u4eec\u65e2\u4e0d\u6253\u7b97\u518d\u5728\u4e94\u5173\uff0c\u4e5f\u5c31\u7528\u4e0d\u7740\u5984\u8d39\u7cbe\u795e\u5e94\u4ed8\u51e4\u5ae3 \u7ea2\uff0c\u56e0\u4e4b\uff0c\u4e24\u4eba\u5bf9\u5979\u6839\u672c\u4e0d\u52a0\u7406\u776c\u3002 \u3000\u3000\u4f46\u51e4\u5ae3\u7ea2\u5374\u4ecd\u81ea\u4f5c\u591a\u60c5\uff0c\u5ae3\u7136\u4e00\u7b11\u62db\u547c\u7740\u9053\uff1a\u201c\u6211\u65e9\u996d\u540e\u6765\u8fc7\uff0c\u6ca1\u770b\u5230\u4e24\u4f4d\u4f7f\u8005\uff0c\u4ec0\u4e48 \u65f6\u5019\u56de\u6765\u7684\uff1f\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u7ba1\u6211\u4eec\u4ec0\u4e48\u65f6\u5019\u56de\u6765\u7684\uff0c\u6211\u4eec\u53c8\u4f55\u5fc5\u8ba9\u4f60\u770b\u5230\uff1f\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u8138\u8272\u4e00\u53d8\uff0c\u671b\u5411\u4e01\u6d9b\u548c\u7518\u9716\u9053\uff1a\u201c\u83ab\u975e\u4f60\u4eec\u521a\u624d\u6ca1\u544a\u8bc9\u8fc7\u4ed6\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u4e3a\u4e86\u732e\u6bb7\u52e4\uff0c\u7acb\u523b\u5411\u524d\u8d70\u8fd1\u4e00\u6b65\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\uff0c\u521a\u624d\u4e0d\u662f\u5df2\u5bf9\u4f60\u8bf4\u8fc7\u51e4\u7edf\u9886\u5df2\u5949\u6559 \u4e3b\u4e4b\u547d\u4ece\u73b0\u5728\u8d77\u517c\u4ee3\u4e94\u5173\u526f\u603b\u9547\u4e48\uff0c\u5979\u8001\u4eba\u5bb6\u804c\u52a1\u5728\u8eab\uff0c\u5f53\u7136\u8981\u95ee\u95ee\u4f60\u4eec\u4e24\u4f4d\u662f\u4ec0\u4e48\u65f6\u5019\u56de \u6765\u7684\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u8fd1\u524d\u4e00\u6b65\u9053\uff1a\u201c\u4e01\u6d9b\uff0c\u4f60\u7b97\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u4e01\u6d9b\u4e24\u773c\u4e00\u76f4\uff1a\u201c\u5cb3\u4f7f\u8005\uff0c\u73b0\u5728\u7684\u4e94\u5173\u3002\u4e0d\u662f\u82b1\u526f\u603b\u9547\u5728\u5f53\u5bb6\u4e86\uff0c\u4e01\u67d0\u4e5f\u4e0d\u662f\u8005\u53d7\u4f60\u6c14 \u7684\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u65e9\u5df2\u6012\u4e0d\u53ef\u904f\uff0c\u626c\u4e00\u638c\uff0c\u76f4\u5411\u4e01\u6d9b\u63b4\u53bb\u3002 \u3000\u3000\u4ed6\u542b\u6124\u51fa\u624b\uff0c\u638c\u529b\u4f55\u5176\u4e4b\u731b\uff0c\u4e00\u58f0\u66b4\u54cd\u4e4b\u540e\uff0c\u3000\u4e01\u6d9b\u5f53\u573a\u6454\u51fa\u4e24\u4e09\u4e08\u5916\u3002\u5f85\u7518\u9716\u6276\u7740\u4ed6 \u52c9\u5f3a\u7ad9\u8d77\u4ee5\u540e\uff0c\u4e01\u6d9b\u65e9\u5df2\u53e3\u9f3b\u9c9c\u8840\u76f4\u55b7\uff0c\u8fde\u7259\u9f7f\u4e5f\u88ab\u6253\u6389\u597d\u51e0\u9897\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u72b9\u81ea\u4f59\u6012\u672a\u606f\uff0c\u8dc3\u8eab\u800c\u8fc7\uff0c\u518d\u98de\u8d77\u4e00\u811a\u3002 \u3000\u3000\u8fd9\u4e00\u811a\u4e0d\u4f46\u4e01\u6d9b\u88ab\u8e22\u5f97\u50cf\u5929\u5916\u98de\u77f3\uff0c\u8fde\u7518\u9716\u4e5f\u8ddf\u7740\u4e00\u8d77\u906d\u6b83\uff0c\u88ab\u5e26\u5f97\u8dcc\u51fa\u4e00\u4e08\u591a\u8fdc\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u7684\u8fd9\u79cd\u4e3e\u52a8\uff0c\u6700\u611f\u96be\u582a\u7684\u8be5\u662f\u51e4\u5ae3\u7ea2\uff0c\u8fd9\u548c\u6253\u4e86\u5979\u6839\u672c\u6ca1\u6709\u5206\u522b\u3002 \u3000\u3000\u6b64\u65f6\u6b64\u5730\uff0c\u51e4\u5ae3\u7ea2\u5982\u4f55\u5fcd\u5f97\u4e0b\u8fd9\u53e3\u6c14\uff0c\u4f55\u51b5\u4e94\u5173\u7684\u5f1f\u5144\u4ee5\u53ca\u5979\u968f\u5e26\u7684\u4f8d\u5a62\u6709\u4e0d\u5c11\u5728\u8eab\u65c1\uff0c \u82e5\u4e0d\u6446\u51fa\u5e94\u6709\u7684\u5a01\u4e25\uff0c\u4eca\u540e\u4f55\u4ee5\u670d\u4f17\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u51e4\u5ae3\u7ea2\u7acb\u5373\u674f\u7738\u51dd\u5a01\uff0c\u79c0\u7709\u5e26\u715e\uff0c\u559d\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\uff0c\u5728\u672c\u526f\u603b\u9547\u9762\u524d\uff0c\u600e\u67de \u5f97\u4f60\u8fd9\u6837\u7316\u72c2\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u84e6\u5730\u957f\u5251\u51fa\u9798\uff0c\u4e00\u6307\u51e4\u5ae3\u7ea2\u9053\uff1a\u201c\u51e4\u5ae3\u7ea2\uff0c\u4f60\u8fd9\u8d31\u5973\u4eba\u53d1\u7684\u4ec0\u4e48\u5a01\u3002\u5728\u4e0b\u4eca\u5929\u8fde \u4f60\u4e5f\u4e00\u8d77\u5bb0\uff01\u201d \u3000\u3000\u4ed6\u672c\u6765\u65e9\u5c31\u4e0d\u9f7f\u51e4\u5ae3\u7ea2\u7684\u4e3a\u4eba\uff0c\u518d\u52a0\u542c\u6bcd\u4eb2\u8bf4\u5979\u5f53\u5e74\u53c8\u66fe\u5bb3\u6b7b\u5e08\u7236\uff0c\u53c8\u60f3\u8d77\u6bcd\u4eb2\u56e0\u5979\u9677 \u5bb3\u4ee5\u81f3\u5728\u7075\u5802\u4e3a\u5974\u53d7\u82e6\u4e09\u5e74\uff0c\u5728\u8bf8\u822c\u6028\u6068\u4e00\u9f50\u6d8c\u4e0a\u5fc3\u5934\u4e4b\u65f6\uff0c\u6839\u672c\u987e\u4e0d\u5f97\u4e00\u5207\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u8881\u5c0f\u9e64\u4e5f\u62d4\u51fa\u4e86\u5251\uff0c\u53cc\u76ee\u5c04\u5149\uff0c\u6012\u89c6\u7740\u51e4\u5ae3\u7ea2\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u4e0d\u89c9\u5fc3\u5934\u5927\u9a87\u3002 \u3000\u3000\u5979\u505a\u68a6\u4e5f\u6ca1\u6599\u5230\u4e8b\u60c5\u4f1a\u53d8\u5316\u5230\u8fd9\u79cd\u5730\u6b65\u3002\u540c\u65f6\uff0c\u5979\u4e5f\u65e9\u77e5\u5cb3\u5c0f\u98de\u6b66\u529f\u9ad8\u4e0d\u53ef\u6d4b\uff0c\u518d\u52a0\u65c1 \u8fb9\u8fd8\u6709\u864e\u89c6\u7708\u7708\u7684\u8881\u5c0f\u9e64\uff0c\u6559\u5979\u4e00\u4eba\u5982\u4f55\u5e94\u4ed8\uff1f \u3000\u3000\u5ffd\u6615\u8033\u65c1\u54cd\u8d77\u4e00\u4e2a\u5a07\u8106\u800c\u53c8\u51b0\u51b7\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5c0f\u98de\u9000\u4e0b\uff0c\u8ba9\u6211\u6765\u4eb2\u624b\u6740\u5979\uff01\u201d \u3000\u3000\u6765\u4eba\u8d6b\u7136\u662f\u4f55\u6167\u4ed9\u3002 \u3000\u3000\u4f55\u6167\u4ed9\u9690\u8eab\u5728\u5927\u95e8\u5916\u8def\u65c1\u6797\u836b\uff0c\u65e9\u5c31\u770b\u5230\u51e4\u5ae3\u7ea2\u8fdb\u6765\uff0c\u56e0\u4e4b\u4e5f\u5c31\u968f\u540e\u7531\u56f4\u5899\u5916\u8dc3\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u4e00\u89c1\u6765\u4eba\u662f\u4f55\u6167\u4ed9\uff0c\u8d8a\u53d1\u82b1\u5bb9\u60e8\u53d8\uff0c\u671b\u540e\u9000\u4e86\u51e0\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u4f55\u5e08\u59b9\uff0c\u76fc\u4f60\u5ff5\u5728 \u540c\u95e8\u4e4b\u8c0a\uff0c\u624b\u4e0b\u7559\u60c5\uff0c\u6211\u51e4\u5ae3\u7ea2\u77e5\u9519\u4e86\uff01\u201d \u3000\u3000\u4f55\u6167\u4ed9\u51b7\u7b11\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u79cd\u6740\u5e08\u706d\u7956\u3001\u80cc\u592b\u6deb\u5954\u3001\u4e71\u4f26\u65e0\u803b\u7684\u8d31\u5973\u4eba\uff0c\u7eb5\u7136\u77e5\u9519\uff0c\u4e5f\u662f \u6b7b\u6709\u4f59\u8f9c\uff0c\u73b0\u5728\u6c42\u9976\u53c8\u6709\u4f55\u7528\uff01\u201d \u3000\u3000\u51e4\u5ae3\u7ea2\u8bba\u6b66\u529f\u672c\u5c31\u4e0d\u662f\u4f55\u6167\u4ed9\u654c\u624b\uff0c\u518d\u52a0\u4e0a\u53c8\u6709\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u5728\u65c1\u3002\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\u3002 \u552f\u4e00\u7684\u529e\u6cd5\uff0c\u4fbf\u662f\u65bd\u5c55\u8f7b\u529f\u9003\u751f\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u5979\u8d81\u4f55\u6167\u4ed9\u3001\u5cb3\u5c0f\u98de\u3001\u8881\u5c0f\u9e64\u4e0d\u5907\uff0c\u3000\u4e00\u4e2a\u5012\u7eb5\uff0c\u817e\u8eab\u800c\u8d77\uff0c\u7136\u540e\u76f4\u5411\u56f4\u5899\u5916\u63a0 \u53bb\u3002 \u3000\u3000\u4f55\u6167\u4ed9\u7acb\u5373\u4e5f\u817e\u8eab\u8ffd\u8d76\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u4e5f\u7d27\u7d27\u76f8\u968f\u5728\u540e\u3002 \u3000\u3000\u51e4\u5ae3\u7ea2\u7684\u8f7b\u529f\uff0c\u4e0d\u5728\u4f55\u6167\u4ed9\u4e4b\u4e0b\uff0c\u518d\u52a0\u4e0a\u4e3a\u4e86\u9003\u751f\uff0c\u4e0d\u5f97\u4e0d\u536f\u8db3\u5168\u529b\uff0c\u56e0\u4e4b\uff0c\u76f4\u8ffd\u56db\u4e94 \u91cc\u3002\u4ecd\u7136\u65e0\u6cd5\u8ffd\u4e0a\u3002 \u3000\u3000\u4e0d\u5927\u4e00\u4f1a\u513f\uff0c\u4fbf\u8ffd\u81f3\u4e00\u7247\u5bc6\u6797\uff0c\u51e4\u5ae3\u7ea2\u7adf\u7136\u5931\u53bb\u6240\u5728\u3002 \u3000\u3000\u4f55\u6167\u4ed9\u56e0\u5730\u5f62\u4e0d\u719f\uff0c\u4e3a\u514d\u9677\u5165\u673a\u5173\u57cb\u4f0f\uff0c\u53ea\u597d\u505c\u4e0b\u811a\u6b65\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u5f88\u5feb\u4fbf\u968f\u540e\u6765\u5230\uff0c\u4e09\u4eba\u7ecf\u8fc7\u5546\u8bae\u4ee5\u540e\uff0c\u51b3\u5b9a\u5148\u56de\u80b2\u5316\u57ce\u518d\u8bf4\u3002 \u5367\u9f99\u751f\u300a\u60c5\u5251\u65e0\u5203\u300b \u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u6b66\u6797\u6b63\u9053 \u3000\u3000\u4e00\u8fde\u53c8\u662f\u51e0\u5929\u8fc7\u53bb\uff0c\u80b2\u5316\u57ce\u7ec8\u4e8e\u9677\u5165\u7cae\u5c3d\u63f4\u7edd\u7684\u5883\u5730\u3002 \u3000\u3000\u597d\u5728\u4e0a\u6b21\u66fe\u4fd8\u83b7\u4e0d\u5c11\u9a74\u3001\u9aa1\u548c\u9a6c\u5339\uff0c\u82e5\u628a\u8fd9\u4e9b\u7272\u755c\u5bb0\u6740\u5145\u9965\uff0c\u4ecd\u53ef\u52c9\u5f3a\u7ef4\u6301\u4e09\u4e94\u65e5\u65f6\u95f4\u3002 \u3000\u3000\u504f\u504f\u53c8\u4f20\u6c42\u6218\u62a5\uff1a\u7687\u5143\u6559\u4e3b\u5c06\u4e8e\u660e\u5929\u518d\u5ea6\u7387\u5927\u519b\u524d\u6765\u653b\u57ce\u3002 \u3000\u3000\u80b2\u5316\u5730\u65b9\u9762\u542c\u5230\u8fd9\u6d88\u606f\uff0c\u4e25\u5bd2\u7b49\u9996\u8111\u4eba\u7269\u867d\u8868\u9762\u4ecd\u80fd\u4fdd\u6301\u9547\u5b9a\uff0c\u4f46\u4e00\u822c\u5f1f\u5144\u4eec\uff0e\u5374\u90fd\u4eba \u5fc3\u60f6\u60f6\uff0c\u76f8\u987e\u5931\u8272\u3002 \u3000\u3000\u8fd9\u662f\u80b2\u5316\u57ce\u65b9\u9762\u751f\u6b7b\u5b58\u4ea1\u7684\u6700\u540e\u5173\u5934\u3002\u665a\u996d\u540e\uff0c\u4e25\u5bd2\u968f\u5373\u53ec\u96c6\u5173\u7433\u3001\u65b9\u5929\u94ce\u3001\u4f55\u6167\u4ed9\u3001 \u5cb3\u5c0f\u98de\u3001\u8881\u5c0f\u9e64\u3001\u5468\u6d77\u5c71\u7b49\u5728\u5927\u5385\u4f1a\u5546\u5bf9\u7b56\u3002 \u3000\u3000\u6446\u5728\u9762\u524d\u7684\uff0c\u53ea\u6709\u4e09\u6761\u8def\u3002 \u3000\u3000\u4e00\u662f\u5b64\u6ce8\u4e00\u63b7\uff0c\u62da\u4e00\u6b7b\u6218\u3002 \u3000\u3000\u4e00\u662f\u867d\u4e0d\u51fa\u6218\uff0e\u4f46\u5374\u575a\u51b3\u56fa\u5b88\uff0c\u4e0e\u80b2\u5316\u57ce\u5171\u5b58\u4ea1\u3002 \u3000\u3000\u6700\u540e\u4e00\u6761\u8def\uff0c\u4fbf\u662f\u5f03\u57ce\u4e58\u591c\u4e8e\u79d8\u9053\u9041\u51fa\u8c37\u5916\uff0c\u81f3\u4e8e\u4ee5\u540e\u7684\u4e8b\uff0c\u51fa\u8c37\u540e\u518d\u505a\u8ba1\u8f83\u3002 \u3000\u3000\u4f17\u4eba\u8bae\u8bba\u4e86\u5f88\u4e45\uff0c\u8fd8\u662f\u83ab\u8877\u4e00\u662f\uff0c\u4f7f\u5f97\u4e25\u5bd2\u59cb\u7ec8\u72b9\u8c6b\u4e0d\u51b3\u3002 \u3000\u3000\u8fd9\u662f\u56e0\u4e3a\u8c01\u90fd\u6ca1\u6709\u7edd\u5bf9\u826f\u7b56\uff0c\u800c\u51b3\u5b9a\u6743\u53c8\u5728\u57ce\u4e3b\u4e25\u5bd2\uff0c\u53ea\u8981\u4e25\u5bd2\u51b3\u5b9a\u8d70\u54ea\u2014\u6761\u8def\uff0c\u4ed6\u4eec \u90fd\u4e0d\u81f4\u53e6\u6709\u5f02\u8bae\u3002 \u3000\u3000\u4e25\u5bd2\u5f53\u7136\u660e\u767d\uff0e\u80b2\u5316\u57ce\u65b9\u9762\u7684\u5b9e\u529b\uff0e\u7edd\u4e0d\u80fd\u548c\u7687\u5143\u6559\u76f8\u6bd4\uff0c\u80dc\u8d25\u4e4b\u5206\uff0e\u65e9\u6210\u5b9a\u5c40\uff0e\u4f46\u5374 \u6709\u4e00\u4ef6\u4e8b\u4f7f\u4ed6\u5fc5\u987b\u8bbe\u6cd5\u786e\u4fdd\u80b2\u5316\u57ce\u3002\u90a3\u5c31\u662f\u7075\u5802\u5185\u6570\u5341\u4f4d\u670d\u836f\u4eba\u68fa\u7684\u9ad8\u4eba\uff0c\u8fd9\u4e9b\u4eba\u5168\u662f\u6b66\u6797 \u5404\u5927\u95e8\u6d3e\u4ee5\u53ca\u8c6a\u95e8\u4e16\u5bb6\u7684\u9996\u8111\u4eba\u7269\uff0c\u4e00\u65e6\u843d\u5165\u7687\u5143\u6559\u4e3b\u767d\u837b\u624b\u4e2d\uff0c\u52bf\u5fc5\u52a9\u957f\u5979\u8fdb\u519b\u4e2d\u539f\u7684\u6c14 \u7130\uff0c\u540e\u679c\u4e0d\u582a\u8bbe\u60f3\u3002 \u3000\u3000\u4f46\u4e25\u5bd2\u4e00\u65e6\u7531\u79d8\u9053\u64a4\u9000\uff0e\u5374\u53c8\u65e0\u6cd5\u5c06\u68fa\u4e2d\u8fd9\u4e9b\u9ad8\u4eba\u5e26\u8d70\u3002 \u3000\u3000\u5c31\u5728\u4e25\u5bd2\u5927\u611f\u8e0c\u8e87\u4e0d\u51b3\u4e4b\u9645\uff0e\u5ffd\u7136\u6709\u4eba\u6765\u62a5\uff1a\u8c37\u5916\u79d8\u9053\u53e3\u6709\u4eba\u7834\u574f\u4e86\u673a\u5173\uff0e\u8d1f\u8d23\u5b88\u62a4\u79d8 \u9053\u7684\u5f1f\u5144\u62e6\u963b\u4e0d\u4f4f\uff0e\u6765\u4eba\u5df2\u7ecf\u6740\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8fd9\u6d88\u606f\u4e0d\u4f46\u4e25\u5bd2\u5927\u4e3a\u9707\u60ca\uff0e\u5728\u573a\u6240\u6709\u7684\u4eba\u4e5f\u51e0\u4e4e\u5168\u5446\u5728\u5f53\u5730\u3002 \u3000\u3000\u56e0\u4e3a\u7531\u80b2\u5316\u57ce\u901a\u5f80\u8c37\u5916\u7684\u79d8\u9053\uff0c\u53ef\u8bf4\u662f\u673a\u5bc6\u4e2d\u7684\u673a\u5bc6\uff0c\u5c24\u5176\u5728\u8c37\u5916\uff0c\u6839\u672c\u65e0\u4ece\u53d1\u73b0\u3002\u5982 \u4eca\u5c45\u7136\u906d\u4eba\u7834\u574f\u800c\u6740\u4e86\u8fdb\u6765\uff0c\u5728\u4e25\u5bd2\u6765\u8bf4\uff0c\u7b80\u76f4\u662f\u5929\u5927\u7684\u5947\u4e8b\u3002 \u3000\u3000\u4e25\u5bd2\u5728\u5927\u611f\u60f6\u9a87\u4e4b\u4e0b\uff0e\u6025\u6025\u95ee\u9053\uff1b\u201c\u4e00\u5171\u6740\u8fdb\u6765\u591a\u5c11\u4eba\uff1f\u201d \u3000\u3000\u6765\u4eba\u9053\uff1a\u201c\u53ea\u6709\u4e24\u4eba\uff0c\u4ed6\u4eec\u628a\u5b88\u62a4\u79d8\u9053\u7684\u5f1f\u5144\u3002\u5168\u4ee5\u70b9\u7a74\u624b\u6cd5\u70b9\u5012\uff0e\u5e76\u672a\u52a0\u6740\u5bb3\uff0c\u7136\u540e \u5927\u6447\u5927\u6446\u7684\u51b2\u4e86\u8fdb\u6765\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e24\u4eba\u76ee\u524d\u5df2\u5230\u4e86\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u5df2\u7ecf\u8fdb\u4e86\u57ce\uff0c\u5c5e\u4e0b\u65b9\u624d\u4e00\u8def\u6025\u5954\u524d\u6765\u7980\u62a5\u65f6\uff0e\u8fd8\u770b\u89c1\u4ed6\u4eec\u5c31\u5728\u9644\u8fd1\u4e0d\u8fdc\u3002\u201d \u3000\u3000\u7acb\u523b\uff0c\u5927\u5385\u5185\u7fa4\u8c6a\u9f50\u9f50\u4eae\u51fa\u4e86\u5175\u5203\uff0c\u4e0d\u5f85\u4e25\u5bd2\u5429\u5490\uff0c\u4fbf\u51c6\u5907\u51fa\u53bb\u8fce\u6218\u6765\u4eba\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u540d\u795e\u91c7\u98d8\u9038\u3001\u7fe9\u7fe9\u5112\u96c5\u3001\u7709\u6e05\u76ee\u6717\u3001\u9894\u4e0b\u7559\u7740\u4e94\u7efa\u957f\u9aef\u7684\u4e2d\u5e74\u5112\u58eb\u6a21\u6837\u7684 \u4eba\uff0c\u7f13\u7f13\u8fc8\u6b65\u8fdb\u5165\u4e86\u5927\u5385\u3002 \u3000\u3000\u53ea\u89c1\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u5446\u4e86\u4e00\u5446\uff0c\u7acb\u523b\u51b2\u4e0a\u524d\u53bb\uff0c\u53cc\u819d\u8dea\u5012\uff0c\u4e24\u4eba\u4e00\u4e2a\u53e3\u79f0\u201c\u5e08\u7236\u201d\uff0c\u4e00 \u4e2a\u76f4\u53eb\u201c\u5e90\u4f2f\u4f2f\u201d\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4eba\u7adf\u662f\u5723\u624b\u4e66\u751f\u5e90\u4e91\u3002 \u3000\u3000\u5176\u4f59\u4f17\u4eba\uff0c\u867d\u4e0d\u8ba4\u8bc6\u5723\u624b\u4e66\u751f\u5e90\u4e91\uff0c\u4f46\u5bf9\u4ed6\u7684\u5927\u540d\uff0c\u5374\u65e9\u5df2\u5982\u96f7\u8d2f\u8033\uff0c\u542c\u8881\u5c0f\u9e64\u3001\u5cb3\u5c0f \u98de\u8fd9\u4e48\u4e00\u53eb\uff0c\u5f53\u7136\u77e5\u9053\u4e86\u4ed6\u662f\u8c01\uff0c\u4e8e\u662f\u7eb7\u7eb7\u4e0a\u524d\u89c1\u793c\uff0c\u4e95\u628a\u5e90\u4e91\u8bf7\u81f3\u4e0a\u5ea7\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u968f\u5373\u518d\u4ecb\u7ecd\u7fa4\u8c6a\u4e0e\u5e90\u4e91\u8ba4\u8bc6\u3002 \u3000\u3000\u5176\u4e2d\u4f55\u6167\u4ed9\u56e0\u5e90\u4e91\u6709\u6069\u4e8e\u7231\u5b50\u5c0f\u98de\uff0e\u7acb\u5373\u4e0a\u524d\u62dc\u8c22\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u5e90\u4e91\u8bf4\u51fa\u4ed6\u8fdb\u5165\u5929\u8c37\u7684\u7ecf\u8fc7\u3002 \u3000\u3000\u539f\u6765\u5e90\u4e91\u5728\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u8d70\u540e\u4e0d\u4e45\uff0e\u65e0\u610f\u4e2d\u9047\u5230\u4e86\u4e00\u4f4d\u5f53\u5e74\u597d\u53cb\uff0e\u800c\u8fd9\u4eba\u6b63\u662f\u66fe\u8fdb\u5165 \u5929\u8c37\u540e\u53c8\u79d8\u5bc6\u9003\u8d70\u7684\uff0c\u4fbf\u628a\u5929\u8c37\u7684\u5185\u5e55\u60c5\u5f62\u544a\u8bc9\u4e86\u5e90\u4e91\u3002 \u3000\u3000\u5e90\u4e91\u62c5\u5fc3\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u6709\u5931\uff0c\u4fbf\u4e5f\u51b3\u5b9a\u4eb2\u81ea\u8fdb\u5165\u5929\u8c37\u3002\u4ee5\u4fbf\u4ece\u4e2d\u534f\u52a9\u3002 \u3000\u3000\u5728\u4ed6\u4e34\u884c\u524d\uff0e\u7279\u5730\u53bb\u8c12\u89c1\u6d1e\u4ed9\u8f9e\u522b\u3002 \u3000\u3000\u8c01\u60f3\u5f53\u6d1e\u4ed9\u542c\u4ed6\u53d9\u8ff0\u8fc7\u5929\u8c37\u7684\u60c5\u5f62\u540e\uff0c\u7adf\u610f\u5916\u7684\u8868\u793a\u8981\u966a\u4ed6\u540c\u5230\u5929\u8c37\u6765\u3002 \u3000\u3000\u5c31\u8fd9\u6837\uff0e\u4e24\u4eba\u4e00\u8def\u800c\u6765\uff0c\u7531\u4e8e\u5728\u5404\u5730\u6e38\u5c71\u73a9\u6c34\u803d\u8bef\u4e86\u4e0d\u5c11\u65f6\u65e5\uff0c\u6240\u4ee5\u4eca\u5929\u624d\u5230\u3002 \u3000\u3000\u4ed6\u4eec\u56e0\u8c37\u53e3\u5df2\u7ecf\u5c01\u95ed\uff0c\u627e\u4e0d\u5230\u5165\u8c37\u8def\u5f84\uff0c\u521a\u597d\u80b2\u5316\u57ce\u901a\u5f80\u8c37\u5916\u7684\u90a3\u6761\u79d8\u9053\u5728\u5916\u9762\u7684\u51fa\u5165 \u53e3\u88ab\u4ed6\u4eec\u65e0\u610f\u4e2d\u53d1\u73b0\uff0c\u8fd9\u624d\u7834\u574f\u4e86\u673a\u5173\uff0c\u5c06\u5b88\u62a4\u4e4b\u4eba\u70b9\u5012\uff0e\u95ef\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u4e25\u5bd2\u542c\u5b8c\u5e90\u4e91\u7684\u53d9\u8ff0\u540e\uff0e\u8feb\u4e0d\u53ca\u5f85\u95ee\u9053\uff1a\u201c\u5e90\u5927\u4fa0\uff0c\u90a3\u4f4d\u6d1e\u4ed9\u8001\u524d\u8f88\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u8fdb\u6765\uff1f\u201d \u3000\u3000\u5e90\u4e91\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u5429\u5490\u5e90\u67d0\u5148\u8fdb\u6765\u770b\u770b\uff0c\u7136\u540e\u518d\u53bb\u8bf7\u4ed6\u3002\u201d \u3000\u3000\u4e25\u5bd2\u50ac\u4fc3\u7740\u9053\uff1a\u201c\u90a3\u5c31\u5feb\u5feb\u53bb\u8bf7\uff01\u201d \u3000\u3000\u7531\u4e8e\u5e90\u4e91\u548c\u6d1e\u4ed9\u7684\u524d\u6765\uff0c\u4e25\u5bd2\u7ec8\u4e8e\u4e0b\u4e86\u51b3\u5b9a\uff0c\u90a3\u5c31\u662f\u660e\u5929\u5927\u53ef\u5f00\u57ce\u8fce\u6218\u3002\u4ed6\u867d\u7136\u4e0d\u77e5\u6d1e \u4ed9\u7684\u6b66\u529f\u7a76\u7adf\u9ad8\u5230\u4ec0\u4e48\u7a0b\u5ea6\uff0c\u4f46\u6709\u5e90\u4e91\u7684\u52a0\u5165\uff0c\u4e5f\u591f\u58f0\u52bf\u5927\u632f\u7684\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5e90\u4e91\u9053\uff1a\u201c\u6d1e\u4ed9\uff0c\u4ed6\u8001\u4eba\u5bb6\u6027\u60c5\u53e4\u602a\uff0c\u5f85\u4f1a\u513f\u6765\u4e86\u4e4b\u540e\uff0c\u5982\u5bf9\u5404\u4f4d\u6253\u6240\u5192\u72af\u4e4b\u5904\uff0c \u5404\u4f4d\u5343\u4e07\u4e0d\u8981\u89c1\u602a\uff0c\u5426\u5219\u4ed6\u8001\u4eba\u5bb6\u4e00\u6012\u62c2\u8896\u800c\u53bb\uff0c\u90a3\u5c31\u7cdf\u4e86\uff01\u201d \u3000\u3000\u4e25\u5bd2\u966a\u7740\u5c0f\u5fc3\u9053\uff1a\u201c\u90a3\u662f\u5f53\u7136\uff0c\u4ed6\u8001\u4eba\u5bb6\u662f\u9041\u5165\u9ad8\u4eba\uff0c\u800c\u4e14\u5df2\u4fee\u70bc\u5230\u5929\u4eba\u5408\u4e00\u7684\u5883\u5916\uff0e \u5982\u4eca\u9a7e\u4e34\u655d\u57ce\uff0e\u4e25\u67d0\u53ef\u79f0\u4e09\u751f\u6709\u5e78\u3002\u5e90\u5927\u4fa0\u5feb\u8bf7\u4ed6\u6765\uff0e\u4e25\u67d0\u7b49\u4eba\u90fd\u8be5\u5411\u4ed6\u5927\u793c\u53c2\u62dc\u3002\u201d \u3000\u3000\u201c\u597d\uff0e\u8bf8\u4f4d\u8bf7\u7a0d\u5f85\uff0c\u5e90\u67d0\u8fd9\u5c31\u5230\u5916\u9762\u53bb\u8bf7\uff01\u201d \u3000\u3000\u5e90\u4e91\u53bb\u540e\u4e0d\u4e45\uff0e\u4fbf\u966a\u7740\u4e00\u4f4d\u9e64\u53d1\u7ae5\u989c\uff0e\u6ee1\u9762\u7ea2\u5149\uff0e\u795e\u6001\u5f02\u5947\u8131\u4fd7\u7684\u8001\u4eba\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u548c\u5cb3\u5c0f\u98de\u662f\u89c1\u8fc7\u6d1e\u4ed9\u7684\uff0c\u6b63\u60f3\u8d8b\u524d\u62dc\u89c1\uff0c\u5374\u53c8\u88ab\u773c\u524d\u7684\u666f\u50cf\u6014\u4f4f\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4eec\u4e24\u4eba\u6240\u89c1\u8fc7\u7684\u6d1e\u4ed9\uff0c\u662f\u5934\u5927\u5982\u6597\uff0c\u4e71\u53d1\u590d\u9762\uff0c\u6839\u672c\u4e0d\u662f\u73b0\u5728\u8fd9\u6837\u5b50\u3002 \u3000\u3000\u4ed6\u4eec\u54ea\u91cc\u77e5\u9053\uff0c\u6d1e\u4ed9\u73b0\u5728\u7684\u6a21\u6837\uff0c\u624d\u662f\u4ed6\u7684\u672c\u6765\u9762\u76ee\u3002 \u3000\u3000\u5728\u8fd9\u5239\u90a3\uff0c\u7a81\u89c1\u4e25\u5bd2\u4e24\u773c\u4e00\u76f4\uff0c\u63a5\u7740\u7ffb\u8eab\u8dea\u5012\u5728\u5730\uff0c\u60ca\u559c\u800c\u53c8\u6fc0\u52a8\u7684\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff01 \u539f\u6765\u662f\u4f60\u8001\u4eba\u5bb6\u5230\u4e86\uff0c\u8fd9\u4e0d\u662f\u5728\u505a\u68a6\u4e48\uff1f\u201d \u3000\u3000\u6d1e\u4ed9\u4e5f\u7565\u663e\u6fc0\u52a8\u7684\u9053\uff1a\u201c\u8d77\u6765\u8bb2\u8bdd\uff01\u201d \u3000\u3000\u8fde\u5e90\u4e91\u4e8b\u5148\u4e5f\u6beb\u4e0d\u77e5\u60c5\uff0c\u539f\u6765\u6d1e\u4ed9\u7adf\u662f\u4e25\u5bd2\u7684\u6069\u5e08\uff0c\u602a\u4e0d\u5f97\u4ed6\u7adf\u80af\u4e0d\u8fdc\u5343\u91cc\u524d\u6765\u5929\u8c37\u3002 \u3000\u3000\u4e25\u5bd2\u8d77\u8eab\u628a\u5e08\u7236\u6276\u81f3\u4e0a\u5ea7\uff0c\u7136\u540e\u53c8\u5927\u62dc\u4e09\u62dc\uff0c\u624d\u5782\u624b\u8eac\u8eab\u4f8d\u7acb\u4e00\u65c1\u3002 \u3000\u3000\u8fd9\u65f6\uff0e\u5176\u4ed6\u7684\u4eba\u4e5f\u90fd\u7eb7\u7eb7\u4e0a\u524d\u62dc\u89c1\u3002 \u3000\u3000\u4e25\u5bd2\u4ecd\u5728\u5fc3\u795e\u6fc0\u52a8\uff0c\u8c28\u58f0\u9053\uff1a\u201c\u5e08\u7236\u79bb\u5f00\u6606\u4ed1\u4e91\u971e\u6d1e\u51e0\u5341\u5e74\uff0c\u5f1f\u5b50\u4eec\u904d\u5bfb\u5929\u6daf\u6d77\u89d2\u4e0d\u9047\uff0c \u60f3\u4e2a\u5230\u4f60\u8001\u4eba\u5bb6\u5374\u9690\u5c45\u5728\u4e94\u53f0\u5c71\u3002\u5e08\u7236\u5f53\u5e74\u4e8b\u5148\u4e0d\u5411\u5f1f\u5b50\u900f\u9732\u534a\u70b9\u6d88\u606f\u800c\u79bb\u5f00\u4e86\u6606\u4ed1\u4e91\u971e\u6d1e\uff0c \u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6d1e\u4ed9\u5fae\u5fae\u53f9\u606f\u4e00\u58f0\u9053\uff1a\u201c\u5f53\u65f6\u4f60\u4eec\u90fd\u5df2\u827a\u6ee1\u81ea\u7acb\uff0c\u4e3a\u5e08\u65e0\u7275\u65e0\u6302\uff0c\u79bb\u5f00\u6606\u4ed1\u4e91\u971e\u6d1e\uff0c\u53c8 \u4f55\u5fc5\u901a\u77e5\u4f60\u4eec\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u987f\u4e86\u987f\u9053\uff1a\u201c\u5e08\u7236\u53ef\u77e5\u9053\u76ee\u524d\u5929\u8c37\u5185\u7684\u60c5\u5f62\uff1f\u201d \u3000\u3000\u6d1e\u4ed9\u5ffd\u7136\u795e\u8272\u6c89\u51dd\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4e3a\u5e08\u5c31\u662f\u542c\u5230\u767d\u837b\u8fd9\u755c\u751f\u5c45\u7136\u6210\u7acb\u4e86\u4ec0\u4e48\u7687\u5143\u6559\uff0c \u800c\u4e14\u53c8\u6253\u7b97\u8fdb\u519b\u4e2d\u539f\uff0c\u8840\u6d17\u6b66\u6797\uff0c\u6240\u4ee5\u624d\u4e0d\u5f97\u4e0d\u7834\u6212\u897f\u6765\uff0e\u4ee5\u4fbf\u6d88\u9664\u8fd9\u573a\u707e\u7978\u3002\u201d \u3000\u3000\u63a5\u7740\u53c8\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4e3a\u5e08\u5f53\u5e74\u65e9\u5c31\u770b\u51fa\u767d\u837b\u5c06\u6765\u5fc3\u6709\u6076\u884c\uff0c\u4f46\u5374\u6ca1\u60f3\u5230\u5979\u80fd\u505a\u51fa\u8fd9\u6837 \u5927\u9006\u4e0d\u9053\u7684\u4e8b\u6765\uff0c\u5b9e\u5728\u53ef\u6076\u4e4b\u6781\uff01\u201d \u3000\u3000\u4e25\u5bd2\u968f\u5373\u8d77\u673a\u628a\u8fd1\u6708\u6765\u6240\u53d1\u751f\u7684\u4e8b\u4ee5\u53ca\u767d\u837b\u5373\u5c06\u660e\u5929\u5927\u4e3e\u653b\u57ce\uff0c\u8be6\u7ec6\u7980\u62a5\u4e86\u4e00\u904d\u3002 \u3000\u3000\u8c01\u77e5\u6d1e\u4ed9\u5374\u5bf9\u4e25\u5bd2\u55d4\u76ee\u800c\u89c2\uff0e\u6012\u58f0\u9053\uff1a\u201c\u4e25\u5bd2\uff0c\u8fd9\u4e9b\u8bdd\u4f60\u8fd8\u6709\u8138\u5bf9\u4e3a\u5e08\u8bb2\uff0e\u6211\u770b\u4f60\u662f\u548e \u7531\u81ea\u53d6\uff01\u201d \u3000\u3000\u4e25\u5bd2\u4e0d\u89c9\u6253\u4e86\u4e2a\u51b7\u98a4\u9053\uff1a\u201c\u83ab\u975e\u5e08\u7236\u2026\u2026\u201d \u3000\u3000\u6d1e\u4ed9\u51b7\u7136\u9053\uff1b\u201c\u4f60\u4eec\u5e08\u5144\u59b9\u4e09\u4eba\uff0c\u4f60\u8eab\u4e3a\u5927\u5e08\u5144\uff0e\u767d\u837b\u5f53\u5e74\u6210\u7acb\u7687\u5143\u6559\uff0e\u4f60\u5c31\u8be5\u5bf9\u5979\u6653 \u4ee5\u5927\u4e49\uff0e\u8bbe\u6cd5\u963b\u6b62\uff0c\u4f46\u4f60\u4e0d\u6b64\u4e4b\u56fe\uff0c\u53cd\u800c\u52a9\u7ea3\u4e3a\u8650\uff0e\u72fc\u72c8\u4e3a\u5978\uff0c\u4ee5\u5927\u5e08\u5144\u4e4b\u5c0a\uff0c\u7adf\u7518\u613f\u505a\u5979 \u7684\u5c5e\u4e0b\uff0e\u53d7\u5979\u6446\u5e03\uff0e\u8fd9\u4e0d\u662f\u548e\u7531\u81ea\u53d6\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u88ab\u8bad\u65a5\u5f97\u9762\u7ea2\u8033\u8d64\uff0e\u513f\u4e4e\u8fde\u5934\u90fd\u62ac\u4e0d\u8d77\u6765\uff0c\u5b9e\u5728\u6094\u6068\u4e0d\u5df2\u3002 \u3000\u3000\u6d1e\u4ed9\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4ec0\u4e48\u4e00\u65f6\u611a\u6627\uff1f\u636e\u4e3a\u5e08\u9884\u6599\uff0c\u5f53\u65f6\u4f60\u52a9\u5979\u6210\u7acb\u7687\u5143\u6559\uff0e\u7167\u6837\u4e5f\u662f\u5fc3\u5b58 \u6743\u5229\u4e4b\u6b32\uff0c\u82e5\u4e0d\u662f\u6700\u8fd1\u56e0\u548c\u5979\u5229\u5bb3\u51b2\u7a81\uff0c\u4f60\u4f1a\u53db\u79bb\u7687\u5143\u6559\u4e48\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u88ab\u65a5\u5f97\u8d8a\u53d1\u65e0\u989c\uff0c\u771f\u6068\u4e0d\u5f97\u5730\u4e0a\u6709\u6761\u7f1d\u94bb\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u6d1e\u4ed9\u53c8\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\uff0e\u603b\u7b97\u4f60\u80fd\u8ff7\u9014\u77e5\u8fd4\uff0c\u4e3a\u5e08\u5c31\u51b3\u5b9a\u6682\u65f6\u7559\u5728\u4f60\u8fd9\u80b2\u5316\u57ce\uff0c\u7b49\u6d88\u706d \u4e86\u7687\u5143\u6559\uff0c\u60e9\u6cbb\u8fc7\u767d\u837b\u548c\u9ec4\u4e00\u9053\u8fd9\u4e24\u4e2a\u755c\u751f\u518d\u8bf4\u3002\u201d \u3000\u3000\u4e25\u5bd2\u88ab\u8bad\u5f97\u8fde\u5e08\u7236\u548c\u5e90\u4e91\u7684\u63a5\u98ce\u5bb4\u90fd\u5fd8\u8bb0\u51c6\u5907\uff0c\u8fd9\u65f6\u624d\u8fde\u5fd9\u5429\u5490\u5468\u6d77\u5c71\u8d76\u7d27\u5230\u53a8\u623f\u4ea4\u4ee3\uff0e \u4e95\u7279\u522b\u8981\u53a8\u623f\u51c6\u5907\u51e0\u6837\u5e08\u7236\u559c\u6b22\u7684\u7d20\u83dc\u3002 \u3000\u3000\u9152\u7b75\u6446\u4e0a\u4e4b\u540e\uff0c\u7fa4\u8c6a\u4eec\u867d\u5df2\u7528\u8fc7\u9910\uff0c\u5374\u4e0d\u5f97\u4e0d\u518d\u4e0a\u684c\u5949\u966a\u3002 \u3000\u3000\u6b64\u523b\u4f17\u4eba\u7684\u5fc3\u60c5\uff0c\u5df2\u548c\u5148\u524d\u5b8c\u5168\u4e0d\u540c\uff0c\u6d1e\u4ed9\u548c\u5e90\u4e91\u7684\u5230\u6765\uff0e\u7b49\u4e8e\u5929\u964d\u6551\u661f\uff0c\u8c01\u4e5f\u4e0d\u518d\u5728 \u4e4e\u660e\u5929\u7687\u5143\u6559\u7684\u5927\u4e3e\u653b\u57ce\u4e86\u3002 \u3000\u3000\u6d1e\u4ed9\u56e0\u81ea\u611f\u65b9\u624d\u8bad\u65a5\u4e25\u5bd2\u6709\u4e9b\u8fc7\u5206\uff0e\u6b64\u523b\u6001\u5ea6\u4e5f\u8f6c\u4e3a\u548c\u853c\u3002\u56e0\u4e3a\u5230\u73b0\u5728\u4ed6\u624d\u60f3\u8d77\u8fd9\u4f4d\u5f1f \u5b50\u5df2\u662f\u516d\u4e03\u5341\u5c81\u7684\u4eba\uff0c\u5df2\u7ecf\u4e0d\u662f\u5f53\u5e74\u53ea\u662f\u4e2a\u5b69\u5b50\u7684\u4e25\u5bd2\u4e86\u3002 \u3000\u3000\u6563\u7b75\u540e\uff0e\u4e25\u5bd2\u53c8\u4eb2\u81ea\u670d\u4f8d\u5e08\u7236\u5b89\u5bdd\uff0c\u76f4\u5fd9\u5230\u4e0b\u534a\u591c\uff0c\u624d\u6709\u65f6\u95f4\u56de\u5230\u81ea\u5df1\u623f\u4e2d\u4f11\u606f\u3002 \u3000\u3000\u6b21\u65e5\u65e9\u9910\u540e\uff0c\u4e25\u5bd2\u966a\u540c\u7fa4\u8c6a\u4e3b\u52a8\u6765\u5230\u57ce\u5916\uff0c\u5728\u57ce\u95e8\u53e3\u4e25\u9635\u4ee5\u5f85\u3002 \u3000\u3000\u5176\u4e2d\u6d1e\u4ed9\u6682\u4e0d\u51fa\u57ce\uff0c\u4ed6\u8001\u4eba\u5bb6\u85cf\u5728\u57ce\u697c\u4e0a\u89c2\u9635\uff0c\u4ee5\u4fbf\u5fc5\u8981\u65f6\u73b0\u8eab\u3002 \u3000\u3000\u5e90\u4e91\u5219\u5df2\u968f\u7fa4\u8c6a\u51fa\u57ce\uff0c\u4e0d\u8fc7\u4ed6\u6545\u610f\u7ad9\u5728\u540e\u9762\uff0c\u5c3d\u91cf\u907f\u514d\u8ba9\u5bf9\u65b9\u6ce8\u610f\u5230\u3002 \u3000\u3000\u987f\u996d\u5de5\u592b\u4e4b\u540e\uff0e\u7687\u5143\u6559\u7684\u5927\u961f\u4eba\u9a6c\u4fbf\u5df2\u5230\u8fbe\u3002 \u3000\u3000\u8fd9\u6b21\u7687\u5143\u6559\u7684\u9635\u5bb9\u3002\u6bd4\u4e0a\u6b21\u66f4\u76db\u3002 \u3000\u3000\u7687\u5143\u6559\u4e3b\u767d\u837b\u4e0b\u8f7f\u540e\uff0c\u7ad9\u5728\u524d\u65b9\u5f53\u4e2d\uff0c\u5979\u8eab\u65c1\u4ecd\u662f\u603b\u62a4\u6cd5\u79e6\u69d0\uff0c\u5176\u4e2d\u6b66\u529f\u6700\u9ad8\u7684\u591a\u80cc\u5929 \u7fc1\u5e9e\u821f\uff0c\u7adf\u628a\u6240\u5c5e\u5341\u540d\u9ad8\u624b\u5168\u90e8\u5e26\u4e86\u6765\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u5f88\u5feb\u4fbf\u53d1\u73b0\u5bf9\u65b9\u4eba\u9a6c\u5de6\u9996\u7ad9\u7740\u98ce\u5ae3\u7ea2\u3002 \u3000\u3000\u98ce\u5ae3\u7ea2\u8eab\u540e\u4e24\u540d\u5927\u6c49\uff0e\u6b63\u662f\u4e01\u6d9b\u548c\u7518\u9716\u3002 \u3000\u3000\u6b64\u523b\u7684\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\uff0c\u5df2\u4e0d\u518d\u6234\u4eba\u76ae\u9762\u5177\uff0e\u4ed6\u4e24\u4eba\u5c45\u7136\u548c\u4e01\u6d9b\u3001\u7518\u9716\u6210\u4e86\u654c\u5bf9\u7684\u53cc\u65b9\uff0c \u8fd9\u5728\u4ece\u524d\u53ef\u80fd\u8c01\u4e5f\u60f3\u4e0d\u5230\u3002 \u3000\u3000\u5f53\u7687\u5143\u6559\u7684\u5927\u961f\u4eba\u9a6c\u6392\u597d\u9635\u5f0f\u540e\uff0c\u4e0d\u5c11\u4eba\u90fd\u5bf9\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u6307\u6307\u70b9\u70b9\uff0c\u663e\u7136\u4ed6\u4eec\u5df2\u77e5 \u9053\u4e86\u524d\u51e0\u6b21\u90a3\u53eb\u5173\u5927\u9e4f\u7684\u5c31\u662f\u5cb3\u5c0f\u98de\u6240\u6539\u626e\u3002 \u3000\u3000\u767d\u837b\u548c\u79e6\u6127\u8fd9\u65f6\u90fd\u96be\u514d\u6709\u4e9b\u610f\u5916\uff0c\u4ed6\u4eec\u4e8b\u5148\u672c\u4ee5\u4e3a\u4e25\u5bd2\u5fc5\u5b9a\u5728\u57ce\u5185\u4e0d\u6562\u9732\u9762\uff0c\u504f\u504f\u5bf9\u65b9 \u4ecd\u6562\u51fa\u57ce\u5bf9\u9635\u3002 \u3000\u3000\u53ea\u542c\u767d\u837b\u9ad8\u53eb\u9053\uff1b\u201c\u53db\u8d3c\u4e25\u5bd2\uff0c\u672c\u6559\u4e3b\u7684\u5927\u519b\u5230\u6765\uff0c\u4f60\u5c45\u7136\u4ecd\u6562\u987d\u6297\uff0c\u5f53\u771f\u4e0d\u77e5\u6b7b\u6d3b\uff01 \u4ecd\u6562\u51fa\u57ce\u5bf9\u9635\u3002\u201d \u3000\u3000\u4e25\u5bd2\u51b7\u7b11\u9053\uff1b\u201c\u767d\u837b\uff0e\u662f\u8c01\u4e0d\u77e5\u6b7b\u6d3b\uff0c\u9a6c\u4e0a\u4fbf\u89c1\u5206\u6653\uff01\u201d \u3000\u3000\u79e6\u69d0\u7acb\u5373\u4f20\u4ee4\u9053\uff1a\u201c\u5e9e\u524d\u8f88\u5c31\u8bf7\u6d3e\u51fa\u9ad8\u4e89\u628a\u4ed6\u4eec\u7edf\u7edf\u62ff\u4e0b\uff01\u201d \u3000\u3000\u4ee5\u79e6\u69d0\u603b\u62a4\u6cd5\u4e4b\u5c0a\uff0c\u5bf9\u5e9e\u821f\u4e0d\u79f0\u804c\u8854\u800c\u79f0\u524d\u8f88\uff0c\u53ef\u89c1\u7687\u5143\u6559\u4e2d\uff0c\u8c01\u4e5f\u4e0d\u6562\u4e0d\u628a\u5e9e\u821f\u53e6\u773c \u76f8\u770b\u3002 \u3000\u3000\u5e9e\u821f\u8fd9\u6b21\u4e00\u8fde\u53eb\u51fa\u4e24\u540d\u9ad8\u624b\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u6839\u672c\u8bbe\u628a\u80b2\u5316\u57ce\u65b9\u9762\u6240\u6709\u7684\u4eba\u770b\u5728\u773c\u91cc\uff0c\u7adf\u7136\u633a\u7740\u5175\u5203\u76f4\u51b2\u5165\u5bf9\u65b9\u9635\u4e2d\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u5c82\u5bb9\u4ed6\u4eec\u5982\u6b64\u7316\u72c2\uff0c\u53cc\u53cc\u62a1\u5251\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5e9e\u821f\u6240\u6d3e\u51fa\u7684\u4e24\u4eba\u679c\u7136\u8eab\u624b\u9ad8\u5f97\u51fa\u5947\uff0c\u5c45\u7136\u5728\u5341\u62db\u4e4b\u5185\uff0c\u548c\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u6218\u6210\u4e0d\u5206\u80dc \u8d1f\u3002 \u3000\u3000\u5c82\u77e5\u5cb3\u5c0f\u98de\u5728\u524d\u5341\u62db\u53ea\u662f\u8bd5\u63a2\u6027\u8d28\uff0c\u6709\u610f\u62d6\u5ef6\u65f6\u95f4\u800c\u5df2\uff0c\u76f4\u5230\u7b2c\u5341\u4e00\u62db\uff0e\u624d\u65bd\u51fa\u4e86\u771f\u6b63 \u529f\u529b\u3002\u6218\u4e0d\u5230\u4e09\u5408\uff0c\u4fbf\u9707\u98de\u4e86\u5bf9\u65b9\u7684\u5175\u5203\uff0c\u63a5\u7740\u4e00\u5251\u523a\u8fdb\u5bf9\u65b9\u5de6\u80a9\uff0c\u518d\u98de\u8d77\u4e00\u811a\u3002 \u3000\u3000\u90a3\u5927\u6c49\u88ab\u8e22\u5f97\u6709\u5982\u5929\u5916\u98de\u77f3\uff0c\u5728\u53d7\u4f24\u53c8\u52a0\u91cd\u6454\u4e4b\u4e0b\uff0c\u5012\u5730\u4e4b\u540e\uff0c\u4fbf\u660f\u53a5\u8fc7\u53bb\u3002 \u3000\u3000\u8c01\u90fd\u770b\u5f97\u51fa\uff0c\u5cb3\u5c0f\u98de\u662f\u5b58\u5fc3\u624b\u4e0b\u7559\u60c5\uff0c\u5426\u5219\u90a3\u5927\u6c49\u6839\u672c\u6ca1\u6709\u6d3b\u547d\u53ef\u7559\u3002 \u3000\u3000\u53e6\u4e00\u5927\u6c49\u4e0d\u89c9\u5fc3\u614c\uff0c\u4e00\u65f6\u5927\u610f\uff0c\u4e5f\u88ab\u8881\u5c0f\u9e64\u4e00\u5251\u523a\u4e2d\u53f3\u81c2\u3002 \u3000\u3000\u8881\u5c0f\u9e64\u4e5f\u662f\u624b\u4e0b\u7559\u60c5\uff0c\u4f24\u4e86\u5bf9\u65b9\u4e4b\u540e\uff0c\u81ea\u52a8\u6536\u5251\u9000\u5230\u4e00\u65c1\u3002 \u3000\u3000\u591a\u81c2\u5929\u7fc1\u5e9e\u821f\u6c14\u5f97\u76ee\u9f87\u6b32\u88c2\u3002\u53f3\u638c\u4e00\u626c\uff0c\u731b\u5411\u5cb3\u5c0f\u98de\u62cd\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u5df2\u5403\u8fc7\u4ed6\u638c\u529b\u4e4b\u4e8f\uff0e\u8eab\u6cd5\u6bd4\u5bf9\u65b9\u7684\u638c\u98ce\u66f4\u5feb\uff0c\u65e9\u5df2\u8dc3\u81f3\u534a\u7a7a\uff0c\u7136\u540e\u4fef\u8eab\u4e0b\u51fb\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u5e9e\u821f\u53ea\u80fd\u5411\u7a7a\u53d1\u638c\u3002\u5cb3\u5c0f\u98de\u6700\u591a\u88ab\u5f39\u5411\u7a7a\u4e2d\uff0c\u7136\u540e\u518d\u987a\u52bf\u843d\u4e0b\u6765\u88ad\u51fb\uff0c\u6839\u672c \u4e0d\u9700\u8d39\u529b\u3002 \u3000\u3000\u679c\u7136\uff0c\u5e9e\u821f\u6bcf\u53d1\u4e00\u638c\uff0c\u5cb3\u5c0f\u98de\u5c31\u5f80\u4e0a\u5347\uff0c\u4f7f\u5f97\u5e9e\u821f\u5fc5\u987b\u4e0d\u505c\u7684\u53d1\u638c\uff0c\u5cb3\u5c0f\u98de\u53cd\u800c\u4e50\u5f97\u5341 \u5206\u81ea\u5728\u3002 \u3000\u3000\u5982\u6b64\u2014\u6765\uff0c\u4e24\u65c1\u89c2\u6218\u7684\u4eba\uff0c\u7adf\u4e0d\u518d\u6ce8\u610f\u5e9e\u821f\uff0c\u6ce8\u610f\u529b\u5168\u96c6\u4e2d\u5728\u5cb3\u5c0f\u98de\u4e00\u4eba\u8eab\u4e0a\uff0c\u83ab\u4e0d\u628a \u4ed6\u8fd9\u8eab\u8f7b\u529f\u53f9\u4e3a\u89c2\u6b62\u3002 \u3000\u3000\u5c31\u8fd9\u6837\u8db3\u8db3\u76cf\u8336\u5de5\u592b\u8fc7\u53bb\uff0c\u5cb3\u5c0f\u98de\u867d\u65e0\u6cd5\u523a\u4f24\u5e9e\u821f\uff0c\u4f46\u5e9e\u821f\u5bf9\u5cb3\u5c0f\u98de\u4e5f\u6ca1\u53ef\u5948\u4f55\u3002 \u3000\u3000\u5ffd\u542c\u4e00\u4eba\u6717\u58f0\u559d\u9053\uff1a\u201c\u98de\u513f\u56de\u6765\uff0c\u5f85\u8001\u592b\u63a5\u4ed6\u51e0\u62db\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u5728\u7a7a\u4e2d\u542c\u51fa\u8bf4\u8bdd\u7684\u662f\u5e90\u4e91\uff0c\u4e00\u4e2a\u5012\u7ffb\uff0c\u4fbf\u8f7b\u98d8\u98d8\u7684\u843d\u56de\u9635\u4e2d\u3002 \u3000\u3000\u8fd9\u65f6\u5723\u624b\u4e66\u751f\u5e90\u4e91\u5df2\u8e0f\u6b65\u8d70\u51fa\u573a\u6765\u3002 \u3000\u3000\u987f\u65f6\uff0c\u7687\u5143\u6559\u65b9\u9762\u8d77\u4e86\u4e00\u9635\u9a9a\u52a8\uff0c\u5176\u4e2d\u6709\u8ba4\u8bc6\u5e90\u4e91\u7684\uff0c\u56fa\u7136\u611f\u5230\u9707\u60ca\uff0c\u5723\u624b\u4e66\u751f\u5e90\u4e91\u4ec0 \u4e48\u65f6\u5019\u7adf\u6765\u5230\u5929\u8c37\uff1f\u53c8\u600e\u4f1a\u5728\u80b2\u5316\u57ce\u4e2d\uff1f \u3000\u3000\u90a3\u4e9b\u4e0d\u8ba4\u8bc6\u5e90\u4e91\u7684\uff0c\u4e5f\u96be\u514d\u4ea4\u5934\u63a5\u8033\u3001\u7a83\u7a83\u79c1\u8bed\uff0c\u56e0\u4e3a\u4ed6\u4eec\u4ece\u672a\u89c1\u8fc7\u6b64\u4eba\uff0c\u6b64\u4eba\u5c45\u7136\u6562 \u4e0b\u573a\u548c\u4ed6\u4eec\u773c\u4e2d\u5929\u4e0b\u6b66\u529f\u7b2c\u4e00\u7684\u5e9e\u821f\u65f6\u62da\uff0c\u53ef\u89c1\u5fc5\u975e\u7b49\u95f2\u4e4b\u8f88\u3002 \u3000\u3000\u5176\u4e2d\u6700\u611f\u9707\u60ca\u7684\uff0c\u83ab\u8fc7\u4e8e\u98ce\u5ae3\u7ea2\uff0c\u5979\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\uff0c\u51a4\u5bb6\u5bf9\u5934\u4f1a\u5728\u6b64\u65f6\u6b64\u5730\u51fa\u73b0\uff0c\u597d\u5728 \u5979\u7ad9\u5728\u5175\u591a\u5c06\u5e7f\u7687\u5143\u6559\u7684\u4e00\u65b9\uff0c\u867d\u9707\u60ca\u800c\u5374\u5e76\u4e0d\u60e7\u6015\u3002 \u3000\u3000\u5e90\u4e91\u8d70\u81f3\u8ddd\u5e9e\u821f\u8eab\u524d\u4e03\u5c3a\u5904\u505c\u4e0b\u6b65\u6765\uff0c\u6a2a\u5251\u6717\u58f0\u9053\uff1a\u201c\u5c0a\u9a7e\u638c\u529b\u96c4\u6d51\uff0c\u4ee4\u4eba\u4f69\u670d\uff0c\u5e90\u67d0 \u4e0d\u624d\uff0c\u5f88\u60f3\u9886\u6559\u9886\u6559\u9ad8\u62db\uff01\u201d \u3000\u3000\u5e9e\u821f\u5e76\u4e0d\u8ba4\u8bc6\u5e90\u4e91\uff0c\u4e24\u773c\u7728\u4e86\u51e0\u7728\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5e90\u4e91\u6de1\u7136\u7b11\u9053\uff1a\u201c\u4ea4\u624b\u8fc7\u62db\uff0c\u4f55\u5fc5\u67e5\u540d\u95ee\u59d3\uff0e\u9601\u4e0b\u5c31\u8bf7\u51fa\u624b\uff01\u201d \u3000\u3000\u5e9e\u821f\u89c9\u51fa\u6765\u4eba\u5fc5\u5b9a\u4e0d\u51e1\uff0e\u6697\u8fd0\u5185\u529b\uff0c\u594b\u81c2\u4e00\u638c\uff0c\u76f4\u5411\u5e90\u4e91\u5f53\u80f8\u62cd\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u76f8\u8ddd\u4e0d\u8fc7\u4e03\u5c3a\uff0e\u5e9e\u821f\u7684\u638c\u529b\u53c8\u6709\u5982\u957f\u6c5f\u5927\u6cb3\u5012\u6cfb\uff0c\u8fd9\u4e00\u638c\u4e0b\u53bb\uff0c\u8c01\u90fd\u4e0d\u96be\u60f3\u5230\u7ed3\u679c\u3002 \u3000\u3000\u4f46\u540e\u679c\u5374\u4e0d\u50cf\u6240\u6709\u4eba\u60f3\u8c61\u7684\u90a3\u4e48\u4e25\u91cd\uff0e\u5e90\u4e91\u53ea\u5411\u540e\u5012\u9000\u4e86\u4e24\u4e09\u6b65\uff0c\u4fbf\u590d\u53c8\u51b2\u4e86\u4e0a\u6765\uff0c\u957f \u5251\u4e00\u4e3e\uff0c\u95ea\u7535\u822c\u5411\u5e9e\u821f\u5934\u9876\u65a9\u6765\u3002 \u3000\u3000\u5e9e\u821f\u4e0d\u7531\u5927\u611f\u9707\u6115\uff0c\u56e0\u4e3a\u53cc\u65b9\u76f8\u8ddd\u4e0d\u8db3\u4e00\u5c3a\uff0c\u4ed6\u7684\u638c\u98ce\u7adf\u672a\u80fd\u628a\u5bf9\u65b9\u9707\u5012\uff0c\u81f3\u5c11\u5728\u4ed6\u4e00 \u751f\u662f\u4ece\u672a\u6709\u8fc7\u7684\u4e8b\u3002 \u3000\u3000\u5728\u8fd9\u540c\u65f6\uff0c\u4e24\u8fb9\u89c2\u6218\u7684\u4eba\u4e5f\u90fd\u9a87\u5f02\u4e0d\u5df2\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u5c45\u7136\u6709\u4eba\u80fd\u4e0e\u591a\u81c2\u5929\u7fc1\u5e9e\u821f\u6297\u8861\uff0c \u600e\u4e0d\u5927\u51fa\u4eba\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u5e9e\u821f\u672c\u6765\u4e5f\u7528\u8fc7\u5175\u5203\uff0c\u4f46\u4ed6\u81ea\u8ba4\u5929\u4e0b\u65e0\u654c\uff0c\u6240\u4ee5\u4ece\u4e0d\u643a\u5e26\uff0c\u8fd9\u4e00\u6765\uff0c\uff0a\u5f97\u4ed6\u53ea\u6709\u53cc\u638c\u8fde \u73af\u51fa\u51fb\uff0c\u603b\u7b97\u4f7f\u5f97\u5e90\u4e91\u96be\u4ee5\u8fd1\u8eab\u3002 \u3000\u3000\u4f46\u5e90\u4e91\u7684\u5251\u8292\u7167\u6837\u4e5f\u4f3c\u94f6\u6cb3\u5012\u6cfb\uff0c\u5e9e\u821f\u60f3\u4ee5\u638c\u529b\u628a\u4ed6\u9707\u9000\uff0c\u4e5f\u4e0d\u662f\u2014\u4ef6\u6613\u4e8b\u3002 \u3000\u3000\u5c31\u5728\u4e24\u4eba\u6740\u5f97\u96be\u5206\u96be\u89e3\u4e4b\u9645\uff0c\u84e6\u5730\u54cd\u8d77\u58f0\u7126\u96f7\u51fb\u9876\u822c\u7684\u5927\u559d\uff1a\u201c\u4f4f\u624b\uff01\u201d \u3000\u3000\u559d\u58f0\u521a\u8fc7\uff0e\u4f46\u89c1\u4e00\u4f4d\u9e64\u53d1\u7ae5\u989c\u3001\u6ee1\u9762\u7ea2\u5149\u3001\u795e\u6001\u5c91\u5947\u8131\u4fd7\u7684\u8001\u4eba\uff0c\u5df2\u6602\u7136\u8d70\u8fdb\u573a\u4e2d\u3002 \u3000\u3000\u6d1e\u4ed9\u672c\u662f\u9690\u8eab\u5728\u57ce\u697c\u4e0a\uff0c\u4ed6\u7a76\u7adf\u662f\u600e\u4e48\u4e0b\u6765\u7684\uff0e\u8fde\u80b2\u5316\u57ce\u65b9\u9762\u7684\u4eba\uff0c\u4e5f\u8c01\u90fd\u4e0d\u66fe\u770b\u6e05\u3002 \u3000\u3000\u8fd9\u4e00\u58f0\u6c89\u559d\uff0c\u51e0\u4e4e\u6240\u6709\u7684\u4eba\u90fd\u4e3a\u4e4b\u9707\u6151\u3002 \u3000\u3000\u5e9e\u821f\u548c\u5e90\u4e91\u90fd\u4e0d\u7531\u81ea\u4e3b\u7684\u505c\u4e0b\u624b\u6765\uff0c\u5404\u81ea\u5411\u540e\u8dc3\u5f00\u3002 \u3000\u3000\u7687\u5143\u6559\u65b9\u9762\u7684\u4eba\uff0c\u9664\u6559\u4e3b\u767d\u837b\u5916\uff0e\u5e76\u65e0\u4e00\u4eba\u8ba4\u8bc6\u6d1e\u4ed9\uff0c\u4ed6\u4eec\u53ea\u662f\u5446\u5446\u7684\u628a\u89c6\u7ebf\u96c6\u4e2d\u5728\u6d1e \u4ed9\u8eab\u4e0a\u3002 \u3000\u3000\u4f46\u767d\u837b\u5374\u5df2\u4e24\u773c\u53d1\u76f4\uff0c\u5927\u6709\u4e0d\u77e5\u6240\u63aa\u4e4b\u6982\u3002 \u3000\u3000\u6d1e\u4ed9\u76f4\u6765\u5230\u5e9e\u821f\u8eab\u524d\uff0c\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u662f\u516d\u5341\u5e74\u524d\u66fe\u5728\u4e2d\u539f\u6a2a\u884c\u4e09\u8f7d\u4ee5\u540e\u53c8\u9690\u8eab\u8377 \u5170\u5c71\u7684\u77ee\u4eba\u5e9e\u821f\uff1f\u201d \u3000\u3000\u5e9e\u821f\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u5c0a\u9a7e\u662f\u8c01\uff1f\u201d \u3000\u3000\u6d1e\u4ed9\u4e0d\u52a8\u58f0\u8272\u9053\uff1a\u201c\u4e0d\u5fc5\u95ee\u8001\u592b\u662f\u8c01\uff0c\u8001\u592b\u542c\u8bf4\u4f60\u81ea\u6043\u638c\u529b\u4e3e\u4e16\u65e0\u5339\uff0c\u4e0d\u628a\u4efb\u4f55\u4eba\u653e\u5728 \u773c\u91cc\u3002\u73b0\u5728\u8001\u592b\u5c31\u7ad9\u5728\u539f\u5730\u63a5\u4f60\u4e09\u638c\u8bd5\u8bd5\uff0c\u82e5\u4e09\u638c\u65e0\u6cd5\u5c06\u8001\u592b\u51fb\u5012\uff0c\u8001\u592b\u518d\u884c\u51fa\u624b\u3002\u201d \u3000\u3000\u5e9e\u821f\u7b80\u76f4\u4ee5\u4e3a\u81ea\u5df1\u7684\u8033\u6735\u51fa\u4e86\u6bdb\u75c5\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u5c45\u7136\u6709\u4eba\u8bf4\u51fa\u8fd9\u7b49\u5927\u8bdd\uff0c\u5f53\u4e0b\u563f\u563f\u4e00\u7b11 \u9053\uff1a\u201c\u8001\u5339\u592b\uff0c\u4f60\u53ef\u662f\u60f3\u627e\u6b7b\uff0c\u5e9e\u67d0\u4e00\u638c\u5c31\u662f\u53ef\u628a\u4f60\u9001\u4e0a\u897f\u5929\uff0c\u54ea\u8fd8\u7528\u5f97\u7740\u4e09\u638c\uff01\u201d \u3000\u3000\u6d1e\u4ed9\u9053\uff1a\u201c\u4e0d\u5fc5\u591a\u8a00\uff0c\u5feb\u4e9b\u51fa\u624b\uff01\u201d \u3000\u3000\u5e9e\u821f\u6599\u5b9a\u6b64\u4eba\u53ef\u80fd\u771f\u6b63\u6709\u4e9b\u529f\u529b\uff0c\u968f\u5373\u5410\u6c14\u51fa\u58f0\uff0c\u8fde\u805a\u5185\u529b\u4e8e\u53cc\u81c2\uff0e\u63a5\u7740\u53cc\u638c\u9f50\u63a8\uff0e\u90a3 \u6392\u5c71\u5012\u6d77\u7684\u72c2\u98d9\uff0c\u76f4\u5411\u6d1e\u4ed9\u6d8c\u53bb\u3002 \u3000\u3000\u5c82\u77e5\u6d1e\u4ed9\u7adf\u7136\u7a33\u7ad9\u5f53\u5730\uff0c\u53ea\u662f\u53cc\u624b\u5728\u80f8\u524d\u5de6\u53f3\u4e00\u5212\uff0c\u5c45\u7136\u50cf\u6ca1\u4e8b\u822c\u7684\u8fde\u52a8\u4e5f\u6ca1\u52a8\u4e00\u52a8\u3002 \u3000\u3000\u5e9e\u821f\u5927\u60ca\u4e4b\u4e0b\uff0c\u63a5\u8fde\u53c8\u662f\u4e24\u638c\uff0c\u4f9d\u7136\u4e0d\u66fe\u6447\u52a8\u6d1e\u4ed9\u5206\u6beb\u3002 \u3000\u3000\u5f53\u4ed6\u6b63\u5728\u76ee\u77aa\u53e3\u5446\uff0c\u4e0d\u77e5\u6240\u63aa\u65f6\uff0c\u7a81\u89c1\u6d1e\u4ed9\u53f3\u81c2\u4e00\u626c\uff0e\u5982\u51fb\u8d25\u9769\u7684\u54cd\u8d77\u4e00\u58f0\uff0c\u5e9e\u821f\u7684\u8eab \u5b50\uff0c\u65e9\u5df2\u88ab\u9707\u8d77\u4e24\u4e09\u4e08\u9ad8\uff0c\u7136\u540e\u50cf\u897f\u74dc\u98de\u5929\u4e00\u822c\u5411\u7687\u5143\u6559\u7684\u4eba\u9a6c\u6454\u843d\u4e0b\u53bb\u3002 \u3000\u3000\u5f53\u4ed6\u843d\u5730\u4e4b\u540e\uff0e\u4e00\u8fde\u649e\u7ffb\u4e86\u4e94\u516d\u4e2a\u4eba\uff0c\u8fd8\u662f\u6454\u4e86\u4e2a\u4eba\u4ef0\u9a6c\u7ffb\u3002 \u3000\u3000\u970e\u65f6\u4e4b\u95f4\uff0e\u7687\u5143\u6559\u65b9\u9762\u50cf\u5ffd\u7136\u53d8\u4e86\u5929\uff0c\u4ed6\u4eec\u5728\u5446\u4e86\u534a\u664c\u4e4b\u540e\uff0e\u624d\u5404\u81ea\u4e0d\u987e\u4e00\u5207\u7684\u4ed3\u7687\u5411 \u603b\u575b\u65b9\u5411\u5954\u9003\u3002 \u3000\u3000\u7687\u5143\u6559\u4e3b\u6b64\u65f6\u4e5f\u987e\u4e0d\u5f97\u4e58\u8f7f\uff0c\u548c\u603b\u62a4\u6cd5\u79e6\u69d0\u9003\u8d70\u5f97\u4e0d\u77e5\u53bb\u5411\u3002 \u3000\u3000\u4e25\u5bd2\u6b63\u6b32\u6325\u519b\u8ffd\u6740\uff0e\u5374\u88ab\u6d1e\u4ed9\u963b\u4f4f\u3002 \u3000\u3000\u4e25\u5bd2\u8d70\u4e0a\u524d\u9053\uff1a\u201c\u5e08\u7236\u4e3a\u4ec0\u4e48\u4e0d\u4e58\u673a\u8ffd\u6740\uff01\u201d \u3000\u3000\u6d1e\u4ed9\u9053\uff1a\u201c\u7687\u5143\u6559\u7684\u5f92\u4f17\uff0c\u591a\u534a\u90fd\u662f\u65e0\u8f9c\u7684\uff0e\u4e3a\u5e08\u4e0d\u5f97\u4e0d\u5b58\u4e0b\u4e00\u5ff5\u4e4b\u4ec1\u3002\u4f60\u4e8c\u5e08\u5f1f\u9ec4\u4e00 \u9053\u90a3\u755c\u751f\u7684\u5bcc\u56fd\u57ce\u79bb\u8fd9\u91cc\u591a\u8fdc\uff1f\u201d \u3000\u3000\u201c\u5bcc\u56fd\u57ce\u7d27\u90bb\u80b2\u5316\u57ce\uff0c\u79bb\u8fd9\u91cc\u4e0d\u8fc7\u516b\u4e5d\u91cc\u8def\u3002\u201d \u3000\u3000\u201c\u8d70\uff0c\u968f\u4e3a\u5e08\u5148\u5230\u5bcc\u56fd\u57ce\u53bb\u3002\u201d \u3000\u3000\u968f\u5373\u7531\u4e25\u5bd2\u5728\u524d\u5e26\u8def\uff0c\u7fa4\u8c6a\u7d27\u7d27\u76f8\u968f\u5728\u6d1e\u4ed9\u8eab\u540e\u3002 \u3000\u3000\u6765\u5230\u5bcc\u56fd\u57ce\uff0c\u5b88\u57ce\u7684\u5c1a\u672a\u6765\u5f97\u53ca\u95ee\u8def\uff0c\u4fbf\u88ab\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u4ee5\u9694\u7a7a\u6253\u7a74\u6cd5\u70b9\u5012\u5728\u5730\u3002 \u3000\u3000\u8fdb\u5165\u57ce\u5e9c\uff0c\u76f4\u5954\u5927\u5385\u3002 \u3000\u3000\u5bcc\u56fd\u57ce\u4e3b\u9ec4\u4e00\u9053\u95fb\u58f0\u540e\uff0c\u614c\u5fd9\u7387\u4f17\u51fa\u6765\u8fce\u6218\u3002 \u3000\u3000\u4ed6\u5c5e\u4e0b\u7684\u51e0\u540d\u9ad8\u624b\uff0c\u5f88\u5feb\u4fbf\u88ab\u7fa4\u8c6a\u6740\u5f97\u9003\u9038\u65e0\u8e2a\u3002 \u3000\u3000\u9ec4\u4e00\u9053\u6b63\u8981\u9003\u547d\uff0c\u4e25\u5bd2\u559d\u9053\uff1a\u201c\u5927\u80c6\u53db\u9006\uff0c\u4f60\u53ef\u770b\u6e05\u6765\u4eba\u91cc\u6709\u8c01\uff1f\u201d \u3000\u3000\u9ec4\u4e00\u9053\u5446\u4e86\u4e00\u5446\uff0c\u7ec8\u4e8e\u53d1\u73b0\u6d1e\u4ed9\uff0c\u5413\u5f97\u4ed6\u614c\u5fd9\u8dea\u5012\u5728\u5730\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u7236\u9976\u547d\uff0e\u5f1f\u5b50 \u62dc\u89c1\uff01\u201d \u3000\u3000\u6d1e\u4ed9\u54fc\u4e86\u4e00\u58f0\uff1a\u201c\u755c\u751f\uff0c\u4f60\u8fd8\u6709\u8138\u89c1\u6211\uff01\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u752b\u6bd5\uff0c\u4e00\u811a\u5411\u9ec4\u4e00\u9053\u8e22\u53bb\u3002 \u3000\u3000\u9ec4\u4e00\u9053\u88ab\u8e22\u5f97\u8fde\u7ffb\u4e03\u516b\u4e2a\u7b4b\u6597\uff0c\u8eab\u5b50\u7a33\u4f4f\u540e\u5df2\u95ed\u6c14\u8fc7\u53bb\u3002 \u3000\u3000\u4e25\u5bd2\u5429\u5490\u5468\u6d77\u5c71\u9053\uff1a\u201c\u5148\u628a\u4ed6\u6346\u8d77\u6765\u62bc\u56de\u80b2\u5316\u57ce\u53bb\uff01\u201d \u3000\u3000\u4ec5\u662f\u8f6c\u77ac\u7684\u5de5\u592b\uff0c\u5bcc\u56fd\u57ce\u5df2\u88ab\u89e3\u51b3\u3002 \u3000\u3000\u6d1e\u4ed9\u518d\u95ee\u9053\uff1a\u201c\u7687\u5143\u6559\u603b\u575b\u79bb\u8fd9\u91cc\u591a\u8fdc\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u9053\uff1a\u201c\u4e0d\u8fdc\uff0c\u53ea\u6709\u5341\u91cc\u8def\u5de6\u53f3\u3002\u201d \u3000\u3000\u6d1e\u4ed9\u9053\uff1a\u201c\u73b0\u5728\u5c31\u968f\u8001\u592b\u653b\u8fdb\u7687\u5143\u6559\u603b\u575b\u3002\u201d \u3000\u3000\u8def\u4e0a\uff0e\u6d1e\u4ed9\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u767d\u837b\u6709\u4e09\u4e2a\u5973\u513f\uff0e\u5979\u7684\u4e08\u592b\u662f\u8c01\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u7a98\u7136\u4e7e\u54b3\u4e86\u4e24\u58f0\u9053\uff1a\u201c\u7980\u5e08\u7236\uff0e\u8fde\u5f1f\u5b50\u90fd\u6ca1\u89c1\u8fc7\u5979\u7684\u4e08\u592b\u3002\u201d \u3000\u3000\u6d1e\u4ed9\u53f1\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u4eec\u4e00\u76f4\u548c\u5979\u5728\u4e00\u8d77\uff0c\u600e\u4f1a\u6ca1\u89c1\u8fc7\u5979\u7684\u4e08\u592b\uff1f\u201d \u3000\u3000\u4e25\u5bd2\u5c34\u5c2c\u4e2d\u966a\u7740\u5c0f\u5fc3\u9053\uff1a\u201c\u4e0d\u7792\u5e08\u7236\uff0e\u5f1f\u4e8e\u548c\u4e8c\u5e08\u5f1f\u90fd\u662f\u5341\u5e74\u524d\u624d\u548c\u5979\u91cd\u9047\uff0c\u63a5\u7740\u5c31\u62e5 \u5979\u4e3a\u6559\u4e3b\u6210\u7acb\u7687\u5143\u6559\uff0e\u90a3\u65f6\u5979\u5df2\u6709\u4e86\u4e09\u4e2a\u5973\u513f\u3002\u5927\u4e2b\u5934\u767d\u91d1\u51e4\uff0c\u4e8c\u4e2b\u5934\u767d\u94f6\u51e4\uff0c\u4e09\u4e2b\u5934\u767d\u7389 \u51e4\uff0c\u6b63\u56e0\u4e3a\u5979\u6ca1\u6709\u4e08\u592b\uff0c\u6240\u4ee5\u4e09\u4e2a\u4e2b\u5934\u90fd\u4ece\u6bcd\u59d3\u3002\u201d \u3000\u3000\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u95ee\u95ee\u5979\uff1f\u201d \u3000\u3000\u201c\u5f1f\u5b50\u4eec\u5f53\u7136\u4e5f\u627e\u673a\u4f1a\u95ee\u8fc7\uff0c\u5979\u8bf4\u5979\u90a3\u4e08\u5927\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u6d1e\u4ed9\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u6b7b\u4e86\uff1f\u5979\u6839\u672c\u5c31\u662f\u4eba\u5c3d\u53ef\u592b\uff0e\u6240\u4ee5\u624d\u751f\u4e0b\u6765\u4e09\u4e2a\u5b7d\u79cd\uff0e\u8bf4\u8d77\u6765 \u4e3a\u5e08\u771f\u8be5\u6e10\u6127\uff0c\u7adf\u8c03\u6559\u51fa\u8fd9\u4e48\u4e00\u4e2a\u755c\u751f\u6765\uff01\u201d \u3000\u3000\u5f88\u5feb\u4fbf\u5230\u8fbe\u7687\u5143\u6559\u603b\u575b\u3002 \u3000\u3000\u8fd9\u91cc\u662f\u7687\u5143\u6559\u4e2d\u7684\u7981\u533a\uff0c\u5f53\u7136\u4e0d\u80fd\u8f7b\u6613\u8ba9\u4eba\u653b\u8fdb\u3002\u3000\u4e00\u65f6\u4e4b\u95f4\uff0c\u516b\u5927\u62a4\u6cd5\u3001\u5404\u62a4\u575b\u4f7f\u8005\u3001 \u7531\u6b66\u5173\u8c03\u6765\u7684\u9ad8\u624b\u4ee5\u53ca\u5176\u4ed6\u53ef\u6218\u4e4b\u5c06\uff0e\u9f50\u9f50\u51fa\u52a8\u62b5\u6297\u3002 \u3000\u3000\u7fa4\u8c6a\u5728\u6d1e\u4ed9\u548c\u4e25\u5bd2\u7387\u9886\u4e0b\uff0c\u8fd9\u6b21\u4e0d\u518d\u5ba2\u6c14\uff0c\u4e00\u9635\u53ae\u6740\u4e4b\u540e\uff0c\u7687\u5143\u6559\u65b9\u9762\u7684\u9ad8\u624b\uff0c\u7247\u523b\u95f4 \u4fbf\u534a\u6570\u4ee5\u4e0a\u5f53\u573a\u6a2a\u5c38\u6e85\u8840\uff0c\u90e8\u5206\u8d2a\u751f\u6015\u6b7b\u7684\uff0c\u5219\u8d81\u673a\u56db\u6563\u9003\u53bb\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u5f88\u5feb\u4fbf\u628a\u82b1\u7389\u9e9f\u6551\u51fa\u3002 \u3000\u3000\u53ca\u81f3\u6740\u5230\u540e\u5bab\uff0c\u767d\u837b\u548c\u79e6\u69d0\u89c1\u65e0\u5904\u53ef\u8eb2\uff0c\u4e0d\u5f97\u5df2\u53ea\u597d\u8dea\u5730\u6c42\u9976\u3002 \u3000\u3000\u53e6\u5916\uff0c\u767d\u91d1\u51e4\u3001\u767d\u94f6\u51e4\u3001\u767d\u7389\u51e4\u4e09\u59ca\u59b9\uff0c\u4e5f\u90fd\u968f\u7740\u8dea\u5728\u4e00\u65c1\u3002 \u3000\u3000\u6d1e\u4ed9\u76ee\u5149\u7f13\u7f13\u626b\u8fc7\u5c71\u837b\uff0e\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u767d\u837b\uff0c\u4f60\u5f88\u80fd\u5e72\uff0e\u505a\u7684\u597d\u4e8b\uff01\u201d \u3000\u3000\u767d\u837b\u5ffd\u7136\u6cea\u6eda\u53cc\u988a\uff0e\u60b2\u5207\u5207\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0e\u5f1f\u5b50\u77e5\u9519\u4e86\uff0c\u4f46\u613f\u4f60\u8001\u4eba\u5bb6\u80fd\u9976\u6211\u4e00\u547d\uff0c\u5f1f \u5b50\u4ece\u6b64\u9752\u706f\u53e4\u4f5b\uff0e\u4e86\u6b64\u4e00\u751f\uff01\u201d \u3000\u3000\u8881\u5c0f\u9e64\u62c5\u5fc3\u6d1e\u4ed9\u4e00\u638c\u628a\u767d\u837b\u51fb\u6bd9\u3002\u90a3\u5c31\u5f88\u96be\u518d\u627e\u56de\u201c\u70bc\u5fc3\u5927\u6cd5\u79d8\u7b08\u201d\uff0e\u5fd9\u626f\u4e86\u5e90\u4e91\u4e00\u4e0b \u8863\u895f\u9053\uff1a\u201c\u5e08\u7236\u3002\u90a3\u672c\u79d8\u85c9\u76ee\u524d\u5df2\u843d\u5728\u7687\u5143\u6559\u4e3b\u624b\u91cc\u3002\u201d \u3000\u3000\u5e90\u4e91\u53ea\u597d\u8eac\u8eab\u5411\u6d1e\u4ed9\u65bd\u4e86\u4e00\u793c\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0e\u4f60\u8001\u4eba\u5bb6\u8d50\u8d60\u665a\u8f88\u7684\u90a3\u518c\u2026\u2026\u201d \u3000\u3000\u6d1e\u4ed9\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u53ef\u662f\u90a3\u518c\u2018\u70bc\u5fc3\u5927\u6cd5\u2019\uff0e\u4e0d\u5fc5\u8981\u4e86\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u9664\u8001\u592b\u4e4b\u5916\uff0c\u4e0d\u53ef\u80fd \u518d\u6709\u7b2c\u4e8c\u4e2a\u4eba\u80fd\u70bc\u5fc3\u6210\u529f\uff0c\u4eca\u540e\u4e0d\u8bba\u8ba9\u8c01\u5f97\u53bb\uff0e\u90fd\u5c06\u6210\u4e3a\u5e9f\u7269\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7740\uff0e\u76ee\u5149\u53c8\u79fb\u5230\u767d\u91d1\u51e4\u7b49\u4eba\u8eab\u4e0a\u9053\uff1a\u201c\u8fd9\u4e09\u4e2a\u4e2b\u5934\u90fd\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u767d\u837b\u5782\u9053\u4f4e\u58f0\u9053\uff1a\u201c\u5979\u4eec\u662f\u5f1f\u5b50\u7684\u4e09\u4e2a\u5973\u513f\u3002\u201d \u3000\u3000\u6d1e\u4ed9\u51b7\u7b11\u9053\uff1a\u201c\u5979\u4eec\u7684\u7236\u4eb2\u662f\u51c6\uff1f\u201d \u3000\u3000\u5ffd\u542c\u79e6\u69d0\u58f0\u97f3\u6296\u52a8\u7684\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u662f\u2026\u2026\u662f\u665a\u8f88\uff01\u201d \u3000\u3000\u6b64\u8bed\u4e00\u51fa\uff0c\u4e0d\u4f46\u4e25\u5bd2\u7b49\u4eba\u9707\u60ca\uff0e\u8fde\u767d\u91d1\u98ce\u4e09\u59ca\u59b9\u4e5f\u6123\u5728\u5f53\u573a\uff0e\u56e0\u4e3a\u5979\u4eec\u4e5f\u59cb\u7ec8\u5f04\u4e0d\u6e05\u8c01 \u662f\u81ea\u5df1\u7684\u751f\u8eab\u7236\u4eb2\uff0c\u767d\u837b\u66fe\u5bf9\u4ed6\u4eec\u79c1\u4eba\u900f\u9732\u8fc7\uff0c\u5979\u7684\u4e08\u592b\u65e9\u5df2\u6b7b\u53bb\u3002 \u3000\u3000\u539f\u6765\u79e6\u69d0\u81ea\u52a8\u627f\u8ba4\u6b64\u4e8b\uff0e\u76ee\u7684\u4e0d\u5916\u5e0c\u671b\u4fdd\u4f4f\u4e00\u547d\uff0c\u4ed6\u60f3\u5230\u767d\u837b\u662f\u6d1e\u4ed9\u7684\u5f1f\u5b50\uff0e\u6d1e\u4ed9\u770b\u5728 \u767d\u91d1\u98ce\u7b49\u4e09\u59ca\u59b9\u65e0\u8f9c\u7684\u4efd\u4e0a\uff0c\u5fc5\u5b9a\u4e0d\u5fcd\u5fc3\u6740\u6b7b\u767d\u837b\u3002\u767d\u837b\u4e0d\u6b7b\uff0e\u4ed6\u627f\u8ba4\u4e86\u6b64\u4e8b\uff0e\u81ea\u7136\u4e5f\u53ef\u4e0d \u6b7b\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u7684\u8bdd\u521a\u521a\u8bf4\u5b8c\uff0c\u4e25\u5bd2\u65e9\u5df2\u4e00\u5251\u523a\u8fdb\u4e86\u4ed6\u7684\u80f8\u819b\u3002 \u3000\u3000\u79e6\u69d0\u8fde\u53eb\u90fd\u6ca1\u53eb\u51fa\uff0e\u4fbf\u5012\u5367\u5728\u8840\u6cca\u4e2d\u6b7b\u53bb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u767d\u837b\u60ca\u53eb\u4e00\u58f0\uff0c\u4e5f\u5012\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u539f\u6765\u767d\u837b\u89c1\u79e6\u69d0\u88ab\u4e25\u5bd2\u4e00\u5251\u523a\u6b7b\uff0c\u81ea\u5df1\u4e5f\u54ac\u820c\u81ea\u5c3d\u3002 \u3000\u3000\u7687\u5143\u6559\u5143\u51f6\u5df2\u9664\uff0c\u6d1e\u4ed9\u968f\u5373\u5429\u5490\u9053\uff1a\u201c\u8fd9\u4e09\u4e2a\u5973\u5a03\u513f\u662f\u65e0\u8f9c\u7684\uff0e\u5343\u4e07\u4e0d\u8981\u4f24\u5bb3\u4e86\u5979\u4eec\uff0e \u4f60\u4eec\u5c31\u7559\u5728\u8fd9\u91cc\u5904\u7406\u5584\u540e\uff0e\u8001\u592b\u5148\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u4e25\u5bd2\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u5e08\u7236\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u6d1e\u4ed9\u9053\uff1a\u201c\u4e3a\u5e08\u5148\u8fd4\u56de\u80b2\u5316\u57ce\uff0c\u4f60\u4eec\u5904\u7f6e\u5b8c\u6bd5\u540e\uff0c\u5c3d\u901f\u5411\u6211\u7980\uff08\u7f3a\u4e00\u9875\uff09 \u3000\u3000\u5f97\u6211\u518d\u6765\u52a8\u624b\u3002\u201d \u3000\u3000\u53ef\u60dc\u98ce\u5ae3\u7ea2\u5e76\u65e0\u767d\u837b\u90a3\u79cd\u51b3\u5fc3\u548c\u52c7\u6c14\uff0e\u53ea\u987e\u60ca\u614c\u5931\u8272\u7684\u8fde\u58f0\u6c42\u9976\uff0e\u4e1d\u6beb\u6ca1\u6253\u81ea\u5c3d\u7684\u6253\u7b97\u3002 \u3000\u3000\u5e90\u4e91\u62fe\u8d77\u957f\u5251\uff0c\u72b9\u8c6b\u4e86\u4e00\u4e0b\u4e4b\u540e\uff0e\u3000\u4e00\u54ac\u7259\uff0c\u731b\u5730\u5411\u98ce\u5ae3\u7ea2\u54bd\u5589\u523a\u53bb\u3002 \u3000\u3000\u98ce\u5ae3\u7ea2\u53ea\u60e8\u547c\u51fa\u534a\u58f0\uff0c\u4fbf\u5012\u5730\u6c14\u7edd\u3002 \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u89c1\u6b64\u60c5\u666f\uff0c\u65e9\u5df2\u5413\u5f97\u5c41\u6eda\u5c3f\u6d41\uff0c\u4e0d\u6210\u4eba\u5f62\uff0c\u60c5\u4e0d\u81ea\u7981\u7684\u8dea\u4e0b\u53bb\u4e5f\u9f50\u558a\u9976\u547d\u3002 \u3000\u3000\u5e90\u4e91\u4fa7\u8138\u95ee\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u8881\u5c0f\u9e64\u9053\uff1a\u201c\u4ed6\u4eec\u5f53\u521d\u662f\u548c\u5f1f\u5b50\u4eec\u4e00\u9f50\u8fdb\u5165\u5929\u8c37\u7684\uff0c\u5e76\u65e0\u5927\u6076\u3002\u201d \u3000\u3000\u5e90\u4e91\u9053\uff1a\u201c\u65e2\u7136\u5e76\u65e0\u5927\u6076\uff0c\u5c31\u6ca1\u6709\u5fc5\u8981\u518d\u6740\u4ed6\u4eec\uff0c\u8ba9\u4ed6\u4eec\u81ea\u884c\u9003\u751f\u53bb\u5427\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u8fd8\u4e0d\u8c22\u8fc7\u6211\u5e90\u4f2f\u4f2f\u7684\u4e0d\u6740\u4e4b\u6069\u8d77\u6765\uff01\u201d \u3000\u3000\u4e24\u4eba\u8c22\u8fc7\u5e90\u4e91\u8d77\u8eab\u540e\uff0c\u4e01\u6d9b\u5e72\u54b3\u4e86\u51e0\u58f0\uff0c\u671b\u7740\u5cb3\u5c0f\u98de\u62b1\u62f3\u8eac\u8eab\u9053\uff1a\u201c\u5cb3\u4f7f\u8005\uff0c\u4ee5\u524d\u5168\u662f \u4e01\u67d0\u7684\u9519\uff0c\u60a8\u5343\u4e07\u539f\u8c05\uff01\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u522b\u518d\u53eb\u6211\u5cb3\u4f7f\u8005\uff0c\u544a\u8bc9\u4f60\u4eec\uff0c\u7687\u5143\u6559\u4e3b\u5df2\u7ecf\u754f\u7f6a\u81ea\u5c3d\uff0c\u4ece\u6b64\u4e4b\u540e\uff0e\u7eb5\u6709\u5929 \u8c37\uff0e\u4e5f\u4e0d\u4f1a\u518d\u6709\u7687\u5143\u6559\u4e86\u3002\u201d \u3000\u3000\u4e01\u6d9b\u5927\u9a87\u4e4b\u4e0b\uff0c\u8fde\u5fd9\u53c8\u9053\uff1a\u201c\u90a3\u4e48\u4e01\u67d0\u548c\u8001\u7518\u5c31\u51b3\u5b9a\u8ffd\u968f\u82b1\u5927\u4fa0\u548c\u5cb3\u516c\u5b50\u3002\u201d \u3000\u3000\u5cb3\u5c0f\u98de\u9053\uff1a\u201c\u591a\u8c22\u4e24\u4f4d\u7684\u597d\u610f\uff0c\u5bb6\u7236\u65e2\u4e0d\u60f3\u6210\u7acb\u4ec0\u4e48\u6559\uff0c\u4e5f\u4e0d\u4f1a\u521b\u95e8\u7acb\u6d3e\uff0e\u7528\u4e0d\u7740\u4e24\u4f4d \u8ddf\u968f\u3002\u4e24\u4f4d\u539f\u672c\u5728\u592a\u767d\u5c71\u5df2\u7ecf\u6df7\u51fa\u4e86\u540d\u58f0\uff0c\u4f55\u4e0d\u518d\u56de\u592a\u767d\u5c71\u53bb\u91cd\u521b\u65e7\u4e1a\uff0c\u5c71\u4e0d\u8f6c\u8def\u8f6c\uff0c\u5f7c\u6b64 \u5c06\u6765\u4e5f\u8bb8\u540e\u4f1a\u6709\u671f\u3002\u201d \u3000\u3000\u4e01\u6d9b\u548c\u7518\u9716\u867d\u672a\u8a00\u8bed\uff0e\u5185\u5fc3\u5374\u540c\u6837\u65e9\u6709\u8fd9\u79cd\u6253\u7b97\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u518d\u95ee\u5e90\u4e91\u9053\uff1a\u201c\u5e90\u4f2f\u4f2f\uff0e\u4f60\u8001\u4eba\u5bb6\u8981\u4e0d\u8981\u518d\u5230\u5176\u4ed6\u5173\u53e3\u770b\u770b\uff1f\u201d \u3000\u3000\u5e90\u4e91\u521d\u6765\u5929\u8c37\uff0c\u5bf9\u5929\u8c37\u5185\u7684\u4e00\u5207\uff0c\u96be\u514d\u4e5f\u6709\u4e9b\u597d\u5947\uff0e\u968f\u5373\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u5728\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u7684\u966a\u540c\u4e0b\uff0c\u6c34\u5173\u3001\u706b\u5173\u3001\u6b66\u5173\u3001\u6587\u5173\u90fd\u8d70\u4e86\u4e00\u904d\u3002 \u3000\u3000\u8fd9\u4e9b\u5173\u53e3\u7684\u7edf\u9886\uff0c\u9664\u6b66\u5173\u5916\u5c45\u7136\u4ecd\u4e0d\u77e5\u9053\u7687\u5143\u6559\u5df2\u53d1\u751f\u4e86\u5de8\u53d8\uff0c\u8fd9\u5f53\u7136\u662f\u56e0\u4e3a\u4ed6\u4eec\u5e76\u672a \u88ab\u8c03\u53bb\u653b\u6253\u80b2\u6bd4\u57ce\u4e4b\u6545\u3002 \u3000\u3000\u81f3\u4e8e\u6b66\u5173\uff0c\u5219\u53ea\u6709\u4e09\u4e24\u540d\u9ad8\u624b\u5728\uff0c\u4ed6\u4eec\u90fd\u662f\u5728\u653b\u6253\u80b2\u5316\u57ce\u8d25\u9000\u4e0b\u6765\u7684\uff0e\u800c\u4e14\u672a\u89c1\u5e9e\u821f\u7684 \u4eba\u5f71\u3002 \u3000\u3000\u6587\u5173\u7edf\u9886\u5218\u7ee7\u5fb7\u6700\u597d\u5ba2\uff0c\u8fd8\u7279\u522b\u51c6\u5907\u4e86\u5348\u5bb4\u6b3e\u5f85\u5e90\u4e91\u7b49\u4e09\u4eba\u3002 \u3000\u3000\u5cb3\u5c0f\u98de\u548c\u8881\u5c0f\u9e64\u90fd\u5b88\u53e3\u5982\u74f6\uff0e\u5e76\u6ca1\u5bf9\u4efb\u4f55\u4e00\u5173\u900f\u9732\u7687\u5143\u6559\u603b\u575b\u5df2\u7ecf\u74e6\u89e3\u7684\u6d88\u606f\u3002 \u3000\u3000\u56de\u5230\u80b2\u5316\u57ce\u3002\u5728\u7687\u5143\u6559\u603b\u575b\u5904\u7406\u5927\u4e8b\u7684\u4e25\u5bd2\u7b49\u4eba\u4e5f\u56de\u6765\u4e86\uff0c\u4ed6\u4eec\u5e76\u628a\u767d\u91d1\u51e4\u59ca\u59b9\u4e09\u4eba\u5e26 \u56de\u3002 \u3000\u3000\u81f3\u4e8e\u88ab\u63a0\u8d70\u7684\u4e25\u592b\u4eba\u548c\u4e8c\u5c0f\u59d0\u4e25\u5982\u971c\uff0e\u81ea\u7136\u65e9\u5c31\u5b89\u7136\u8131\u8138\u3002 \u3000\u3000\u5f53\u665a\uff0e\u4e25\u5bd2\u5728\u5927\u5385\u6446\u4e0b\u76db\u7b75\uff0c\u7fa4\u8c6a\u9f50\u96c6\u4e00\u5802\uff0c\u9152\u9189\u996d\u9971\u800c\u6563\u3002 \u3000\u3000\u8fd9\u65f6\u5e90\u4e91\u624d\u60f3\u8d77\u5c1a\u672a\u5904\u7f6e\u53db\u5e08\u706d\u7956\u7684\u5b7d\u5f92\u9a6c\u662d\u96c4\uff0c\u53ca\u81f3\u95ee\u8fc7\u4e25\u5bd2\uff0c\u624d\u77e5\u9a6c\u662d\u96c4\u5df2\u88ab\u6d3b\u6d3b \u540a\u6b7b\u3002 \u3000\u3000\u4e09\u5929\u540e\uff0e\u6d1e\u4ed9\u548c\u5e90\u4e91\u3001\u8881\u5c0f\u9e64\u51b3\u5b9a\u8fd4\u56de\u4e94\u53f0\u5c71\u72ec\u79c0\u5cf0\uff0c\u6d1e\u4ed9\u4ecd\u8981\u518d\u56de\u539f\u6d1e\u505a\u4ed6\u7684\u6d1e\u4ed9\u3002 \u3000\u3000\u53e6\u5916\uff0e\u65b9\u5929\u94ce\u4e5f\u544a\u8f9e\u800c\u53bb\u3002 \u3000\u3000\u81f3\u4e8e\u4e25\u5bd2\uff0c\u5fc5\u987b\u4ecd\u7559\u5728\u5929\u8c37\uff0e\u9664\u4e86\u7ee7\u7eed\u5904\u7406\u5584\u540e\uff0c\u66f4\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\u3002\u4fbf\u662f\u8ba9\u90a3\u4e9b\u5728\u7075\u5802 \u5185\u670d\u836f\u4eba\u68fa\u7684\u5404\u8def\u9ad8\u4eba\uff0e\u518d\u670d\u836f\u6551\u9192\u540e\u5eb7\u590d\uff0c\u7136\u540e\u9001\u4ed6\u4eec\u51fa\u8c37\u5404\u56de\u539f\u5904\u3002 \u3000\u3000\u5c31\u5728\u6d1e\u4ed9\uff0e\u5e90\u4e91\u3001\u8881\u5c0f\u9e64\u3001\u65b9\u5929\u94ce\u7b49\u4eba\u8d70\u540e\u7684\u6b21\u65e5\u4e00\u65e9\uff0c\u5173\u7433\u5ffd\u7136\u6765\u5411\u82b1\u7389\u9cde\u8f9e\u522b\u3002 \u3000\u3000\u82b1\u7389\u9cde\u548c\u4f55\u6167\u4ed9\u592b\u5987\u56e0\u548c\u4e25\u5bd2\u5df2\u662f\u513f\u5973\u4eb2\u5bb6\uff0c\u6240\u4ee5\u51b3\u5b9a\u6682\u65f6\u7559\u5728\u80b2\u5316\u57ce\uff0c\u534f\u52a9\u5904\u7406\u5584\u540e\u3002 \u3000\u3000\u4ed6\u89c1\u5173\u7433\u524d\u6765\u8f9e\u522b\uff0e\u4e0d\u89c9\u5927\u611f\u60ca\u8be7\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4e8b\u60c5\u8fd8\u6ca1\u529e\u5b8c\u4f60\u600e\u4e48\u80fd\u8d70\uff1f\u201d \u3000\u3000\u5173\u7433\u51c4\u51c9\u4e00\u7b11\u9053\uff1a\u201c\u4e09\u5f1f\uff0c\u611a\u5144\u7ecf\u8fc7\u8fd9\u51e0\u5929\u7684\u51b7\u9759\u8003\u8651\uff0c\u5df2\u51b3\u5b9a\u4e0d\u518d\u548c\u5c0f\u98de\u7236\u5b50\u76f8\u8ba4\u3002\u201d \u3000\u3000\u82b1\u7389\u9cde\u54e6\u4e86\u58f0\u9053\uff1a\u201c\u53ef\u662f\u5927\u54e5\u603b\u662f\u4ed6\u7684\u751f\u8eab\u4e4b\u7236\uff0c\u4e16\u4e0a\u54ea\u6709\u4e0d\u8ba4\u4eb2\u9aa8\u8089\u7684\uff1f\u96be\u9053\u4f60\u8981\u8ba9 \u5c0f\u98de\u62b1\u61be\u7ec8\u8eab\uff1f\u201d \u3000\u3000\u5173\u7433\u9762\u8272\u51dd\u91cd\uff0c\u53c8\u662f\u51c4\u51c9\u2014\u7b11\u9053\uff1b\u201c\u4e09\u5f1f\uff0e\u611a\u5144\u53ea\u662f\u751f\u4e0b\u4ed6\uff0c\u5728\u4ed6\u751f\u4e0b\u4e0d\u8db3\u5468\u5c81\u4fbf\u7531\u4f60 \u4eec\u592b\u5987\u629a\u517b\uff0c\u4f60\u4eec\u5bf9\u4ed6\u7684\u6069\u60c5\uff0c\u591a\u4e8e\u6211\u5bf9\u4ed6\u5343\u767e\u500d\uff0c\u611a\u5144\u53c8\u600e\u5fcd\u5fc3\u62c6\u6563\u4f60\u4eec\u548c\u4ed6\u8fd9\u6bb5\u5173\u7cfb\uff1f\u201d \u3000\u3000\u201c\u5927\u54e5\uff0c\u8bdd\u4e0d\u80fd\u8fd9\u4e48\u8bf4\uff0c\u4ed6\u548c\u4f60\u7236\u5b50\u76f8\u8ba4\uff0c\u5bf9\u6211\u4eec\u592b\u5987\u5e76\u65e0\u59a8\u788d\uff0c\u82e5\u60a8\u771f\u662f\u8fc7\u610f\u4e0d\u53bb\uff0c \u5c3d\u53ef\u8ba9\u4ed6\u8ba4\u6211\u4eec\u592b\u5987\u505a\u4e49\u7236\u6bcd\u3002\u201d \u3000\u3000\u201c\u4e0d\uff0c\u611a\u5144\u8fd8\u6709\u96be\u8a00\u4e4b\u9690\uff0c\u4e09\u5f1f\u8bf7\u539f\u8c05\uff0c\u8fd9\u4ef6\u4e8b\u6211\u5230\u73b0\u5728\u4e00\u76f4\u8fd8\u7792\u7740\u4f60\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u5fc3\u5934\u4e00\u9707\uff0c\u832b\u7136\u9053\uff1a\u201c\u5927\u54e5\u8bf4\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u5173\u7433\u795e\u8272\u8d67\u7136\uff0c\u4f4e\u4e0b\u5934\u9053\uff1a\u201c\u4e8b\u5230\u5982\u4eca\uff0c\u611a\u5144\u53ea\u6709\u5b9e\u8bf4\u4e86\uff0c\u4f60\u53ef\u77e5\u9053\u5c0f\u98de\u7684\u751f\u8eab\u6bcd\u4eb2\u662f \u8c01\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u6014\u4e86\u6014\u9053\uff1a\u201c\u5f53\u7136\u662f\u5927\u5ac2\uff0c\u9057\u61be\u7684\u662f\u5144\u5f1f\u7adf\u7136\u81f3\u4eca\u6ca1\u89c1\u8fc7\u5927\u5ac2\u7684\u9762\u3002\u201d \u3000\u3000\u5173\u7433\u5e26\u7740\u81ea\u5632\u7684\u795e\u8272\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u5979\u4e0d\u662f\u4f60\u5927\u5ac2\u3002\u201d \u3000\u3000\u201c\u90a3\u4e48\u5979\u662f\u8c01\uff1f\u201d \u3000\u3000\u201c\u7687\u5143\u6559\u4e3b\u767d\u837b\uff01\u201d \u3000\u3000\u82b1\u7389\u9e9f\u51e0\u4e4e\u60ca\u5f97\u5446\u4e86\uff0e\u534a\u664c\uff0c\u624d\u53c8\u95ee\u9053\uff1a\u201c\u5927\u54e5\u53ef\u5426\u518d\u5bf9\u5144\u5f1f\u8bf4\u6e05\u695a\u4e9b\uff1f\u201d \u3000\u3000\u5173\u7433\u957f\u957f\u5401\u53e3\u6c14\u9053\uff1a\u201c\u5f53\u611a\u5144\u5341\u4e09\u5e74\u524d\u521d\u9047\u767d\u837b\u65f6\uff0c\u4fbf\u5bf9\u5979\u4e00\u89c1\u503e\u5fc3\uff0c\u800c\u5979\u4e5f\u5bf9\u6211\u9887\u5177 \u597d\u611f\uff0c\u4e24\u4eba\u6697\u4e2d\u6765\u5f80\uff0c\u4e0d\u4e45\u4fbf\u751f\u4e0b\u4e86\u5c0f\u98de\u3002\u540e\u6765\u4e8b\u673a\u4e0d\u5bc6\uff0c\u88ab\u79e6\u69d0\u67e5\u77e5\uff0c\u611a\u5144\u4e0d\u5f97\u5df2\u4fbf\u5e26\u7740 \u5c0f\u98de\u5077\u5077\u9003\u56de\u4e2d\u539f\uff0c\u4e0d\u4e45\u4f60\u6211\u548c\u5929\u94ce\u4e49\u7ed3\u91d1\u5170\uff0c\u611a\u5144\u56e0\u5728\u6c5f\u6e56\u884c\u8d70\u5e26\u7740\u5c0f\u98de\u4e0d\u4fbf\uff0e\u4fbf\u628a\u4ed6\u4ea4 \u7ed9\u4f60\u4eec\u592b\u5987\u4ee3\u4e3a\u629a\u517b\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u540e\u6765\u5927\u54e5\u5374\u53c8\u5230\u5929\u8c37\u6765\uff0e\u60a8\u4e3a\u4ec0\u4e48\u7adf\u81ea\u6295\u7f57\u7f51\u5462\uff1f\u201d \u3000\u3000\u201c\u8bf4\u6765\u60ed\u6127\uff0c\u611a\u5144\u4e3a\u4e86\u60f3\u518d\u89c1\u767d\u837b\u4e00\u9762\uff0c\u624d\u8d38\u7136\u53c8\u95ef\u5165\u5929\u8c37\u3002\u201d \u3000\u3000\u201c\u540e\u6765\u5462\uff1f\u201d \u3000\u3000\u201c\u540e\u6765\u7ec8\u4e8e\u4e0d\u5e78\u843d\u5165\u79e6\u69d0\u4e4b\u624b\uff0c\u8fd8\u7b97\u767d\u837b\u4ecd\u5ff5\u65e7\u60c5\uff0c\u7ecf\u5979\u5411\u79e6\u69d0\u82e6\u6c42\uff0c\u6211\u624d\u514d\u4e8e\u4e00\u6b7b\uff0c \u800c\u88ab\u6253\u5230\u80b2\u5316\u57ce\u2014\u5904\u5c71\u6d1e\u91cc\u5e7d\u7981\u4e86\u5c06\u8fd1\u5341\u4e8c\u5e74\u4e4b\u4e45\uff0c\u82e5\u4e0d\u662f\u4f60\u4eec\u592b\u5987\u524d\u6765\u76f8\u6551\uff0c\u53ea\u6015\u6211\u5fc5\u5b9a \u4e0d\u89c1\u5929\u65e5\u4e00\u76f4\u5230\u6b7b\u3002\u201d \u3000\u3000\u82b1\u7389\u9e9f\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u7531\u4e5f\u767e\u611f\u4ea4\u96c6\uff0c\u4e0d\u77e5\u8fd8\u80fd\u8bf4\u4ec0\u4e48\u597d\u3002 \u3000\u3000\u5173\u7433\u518d\u9053\uff1b\u201c\u4e09\u5f1f\uff0c\u4f60\u660e\u767d\u4e86\u8fd9\u6bb5\u56e0\u679c\uff0c\u603b\u8be5\u6210\u5168\u611a\u5144\u5fc3\u613f\uff0c\u4e0d\u518d\u8ba9\u5c0f\u98de\u548c\u6211\u7236\u5b50\u60f3\u8ba4 \u4e86\u5427\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u7d27\u8e59\u7740\u53cc\u7709\u9053\uff1a\u201c\u4e0d\u7ba1\u5982\u4f55\uff0c\u5927\u54e5\u5b9e\u5728\u6ca1\u6709\u7406\u7531\u4e0d\u548c\u5c0f\u98de\u7236\u5b50\u76f8\u8ba4\uff01\u201d \u3000\u3000\u4f46\u5173\u7433\u7684\u8bed\u6c14\uff0c\u5374\u50cf\u65a9\u9489\u622a\u94c1\u822c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6700\u5927\u7684\u7406\u7531\uff0c\u5c0f\u98de\u8ddf\u4e86\u4f60\u4eec\u592b\u5987\uff0c\u5fc5\u5b9a\u7ec8 \u751f\u5e78\u798f\u5feb\u4e50\uff0c\u4f46\u82e5\u548c\u6211\u7236\u5b50\u76f8\u8ba4\uff0c\u5f53\u4ed6\u77e5\u9053\u751f\u8eab\u4e4b\u6bcd\u7adf\u662f\u767d\u837b\uff0c\u767d\u837b\u7684\u6b7b\u72b6\uff0c\u4ed6\u662f\u4eb2\u773c\u770b\u5230 \u7684\uff0c\u5c82\u4e0d\u5c06\u4f7f\u4ed6\u75db\u82e6\u4e00\u751f\uff1f\u201d \u3000\u3000\u82b1\u7389\u9e9f\u9ed8\u7136\u4e86\uff0c\u50cf\u9677\u5165\u6c89\u601d\uff0c\u5173\u7433\u7684\u8fd9\u756a\u8bdd\uff0c\u5b8c\u5168\u662f\u81f3\u60c5\u81f3\u7406\uff0c\u4e3a\u4e86\u6210\u5168\u4e49\u5144\u8fd9\u5fc3\u613f\uff0c \u4ed6\u5b9e\u5728\u4e0d\u80fd\u518d\u8bf4\u4ec0\u4e48\uff0c\u4f46\u82e5\u8fd9\u6837\uff0c\u5374\u53c8\u603b\u89c9\u5bf9\u4e49\u5144\u4e0d\u4f4f\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5cb3\u5c0f\u98de\u8d70\u4e86\u8fdb\u6765\uff0c\u4ed6\u662f\u6765\u5411\u82b1\u7389\u9e9f\u8bf7\u793a\u4e8b\u60c5\u7684\u3002 \u3000\u3000\u5f53\u82b1\u7389\u9e9f\u4ea4\u4ee3\u8fc7\u540e\uff0c\u9762\u524d\u65e9\u5df2\u5931\u53bb\u5173\u7433\u7684\u8e2a\u5f71\u3002 \u3000\u3000\u82b1\u7389\u9cde\u6025\u6025\u8ffd\u51fa\u95e8\u5916\uff0c\u4f46\u89c1\u65ed\u65e5\u521d\u5916\uff0e\u5927\u5730\u4e00\u7247\u707f\u70c2\uff0c\u5173\u7433\u65e9\u5df2\u53bb\u8fdc\u4e86\u3002 \u3000\u3000\u5168\u4e66\u5b8c"}),showOnHomepage:!0}]}}]);