"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[5415],{73450:(e,n,t)=>{t.r(n),t.d(n,{default:()=>o});var a=t(74848);const o=[{name:"\u4e00",content:(0,a.jsx)(a.Fragment,{children:"\u55d6\u7684\u4e00\u58f0\uff0c\u4e00\u652f\u7fbd\u7bad\u4ece\u4e1c\u8fb9\u5c71\u5773\u540e\u5c04\u4e86\u51fa\u6765\uff0c\u545c\u545c\u58f0\u54cd\uff0c\u5212\u8fc7\u957f\u7a7a\uff0c\u7a7f\u5165\u4e00\u5934\u98de\u96c1\u9888\u4e2d\u3002\u5927\u96c1\u5e26\u7740\u7fbd\u7bad\u5728\u7a7a\u4e2d\u6253\u4e86\u51e0\u4e2a\u7b4b\u6597\uff0c\u843d\u5728\u96ea\u5730\u3002 \u3000\u3000\u897f\u9996\u6570\u5341\u4e08\u5916\uff0c\u56db\u9a91\u9a6c\u8e0f\u7740\u7691\u7691\u767d\u96ea\uff0c\u5954\u9a70\u6b63\u6025\u3002\u9a6c\u4e0a\u4e58\u5ba2\u542c\u5f97\u7bad\u58f0\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u4e00\u9f50\u52d2\u9a6c\u3002\u56db\u5339\u9a6c\u90fd\u662f\u8eab\u9ad8\u80a5\u8198\u7684\u826f\u9a79\uff0c\u4e00\u53d7\u7f81\u52d2\uff0c\u7acb\u65f6\u6b62\u6b65\u3002\u4e58\u8005\u9a91\u672f\u65e2\u7cbe\uff0c\u7272\u53e3\u4e5f\u90fd\u4e45\u7ecf\u8bad\u7ec3\uff0c\u8fd9\u4e00\u52d2\u9a6c\uff0c\u663e\u5f97\u978d\u4e0a\u80ef\u4e0b\uff0c\u76f8\u5f97\u76ca\u5f70\u3002\u56db\u4eba\u773c\u89c1\u5927\u96c1\u4e2d\u7bad\u8dcc\u4e0b\uff0c\u5fc3\u4e2d\u90fd\u559d\u4e00\u58f0\u5f69\uff0c\u8981\u77a7\u90a3\u53d1\u7bad\u7684\u662f\u4f55\u7b49\u6837\u4eba\u7269\u3002 \u3000\u3000\u7b49\u4e86\u534a\u664c\uff0c\u5c71\u5773\u4e2d\u59cb\u7ec8\u65e0\u4eba\u51fa\u6765\uff0c\u5374\u542c\u5f97\u4e00\u9635\u9a6c\u8e44\u58f0\u54cd\uff0c\u5c04\u7bad\u4e4b\u4eba\u7adf\u81ea\u8d70\u4e86\u3002\u56db\u4e2a\u4e58\u5ba2\u4e2d\u4e00\u4e2a\u8eab\u6750\u7626\u957f\u3001\u795e\u8272\u527d\u608d\u7684\u8001\u8005\u5fae\u5fae\u76b1\u7709\uff0c\u7eb5\u9a6c\u5954\u5411\u5c71\u5773\uff0c\u5176\u4f59\u4e09\u4eba\u8ddf\u7740\u8fc7\u53bb\u3002\u8f6c\u8fc7\u5c71\u8fb9\uff0c\u53ea\u89c1\u524d\u9762\u91cc\u8bb8\u5916\u4e94\u9a91\u9a6c\u5954\u9a70\u6b63\u6025\uff0c\u94c1\u8e44\u6e85\u96ea\uff0c\u94f6\u9b23\u4e58\u98ce\uff0c\u773c\u89c1\u5df2\u8ffd\u8d76\u4e0d\u4e0a\u3002\u90a3\u8001\u8005\u4e00\u6446\u624b\u3002\u8bf4\u9053\uff1a\u201c\u6bb7\u5e08\u5144\uff0c\u8fd9\u53ef\u6709\u70b9\u513f\u90aa\u95e8\u3002\u201d \u3000\u3000\u90a3\u201c\u6bb7\u5e08\u5144\u201d\u4e5f\u662f\u4e2a\u8001\u8005\uff0c\u8eab\u5f62\u5fae\u80d6\uff0c\u7559\u7740\u4e24\u6487\u9aed\u987b\uff0c\u8eab\u62ab\u8c82\u76ae\u5916\u5957\uff0c\u6c14\u6d3e\u662f\u4e2a\u5bcc\u5546\u6a21\u6837\uff0c\u542c\u90a3\u7626\u957f\u8001\u8005\u5982\u6b64\u8bf4\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u52d2\u9a6c\u56de\u5230\u5927\u96c1\u4e4b\u65c1\uff0c\u9a6c\u97ad\u6325\u51fa\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u62bd\u5411\u96ea\u5730\uff0c\u5f85\u5f97\u9a6c\u97ad\u63d0\u8d77\uff0c\u97ad\u68a2\u5df2\u5c06\u5927\u96c1\u5377\u4e86\u4e0a\u6765\uff0c\u4ed6\u5de6\u624b\u62ff\u7740\u7bad\u6746\u4e00\u770b\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u554a\uff01\u201d \u3000\u3000\u4e09\u4eba\u542c\u5230\u53eb\u58f0\uff0c\u4e00\u9f50\u7eb5\u9a6c\u9a70\u8fd1\u3002\u90a3\u201c\u6bb7\u5e08\u5144\u201d\u8fde\u96c1\u5e26\u7bad\u5411\u90a3\u8001\u8005\u63b7\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u962e\u5e08\u5144\uff0c\u8bf7\u770b\uff01\u201d\u7626\u957f\u8001\u8005\u4f38\u5de6\u624b\u4e00\u6284\uff0c\u63a5\u4e86\u8fc7\u6765\uff0c\u4e00\u770b\u7fbd\u7bad\uff0c\u5927\u53eb\uff1a\u201c\u5728\u8fd9\u91cc\u4e86\uff0c\u5feb\u8ffd\uff01\u201d\u52d2\u8f6c\u9a6c\u5934\uff0c\u5f53\u5148\u8ffd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u832b\u832b\u5c71\u5761\u4e0a\u4e00\u7247\u767d\u96ea\uff0c\u56db\u4e0b\u5e76\u65e0\u884c\u4eba\uff0c\u8ffd\u8e2a\u6700\u662f\u5bb9\u6613\u4e0d\u8fc7\u3002\u5176\u4f59\u4e8c\u4eba\u90fd\u662f\u58ee\u5e74\uff0c\u4e00\u4e2a\u8eab\u9ad8\u8180\u9614\u3001\u5750\u5728\u4e00\u5339\u9ad8\u5934\u5927\u9a6c\u4e4b\u4e0a\u3002\u66f4\u662f\u663e\u5f97\u5a01\u6b66\uff1b\u53e6\u4e00\u4e2a\u4e2d\u7b49\u8eab\u6750\uff0c\u8138\u8272\u9752\u767d\uff0c\u4e00\u4e2a\u9f3b\u5b50\u5374\u51bb\u5f97\u901a\u7ea2\u3002\u56db\u4eba\u9f50\u58f0\u553f\u54e8\uff0c\u56db\u5339\u9a6c\u55b7\u6c14\u6210\u96fe\uff0c\u5ffd\u5587\u5587\u653e\u8e44\u8d76\u53bb\u3002 \u3000\u3000\u8fd9\u662f\u6e05\u671d\u4e7e\u9686\u56db\u5341\u4e94\u5e74\u4e09\u6708\u5341\u4e94\u3002\u8fd9\u65e5\u5b50\u5728\u6c5f\u5357\u65e9\u5df2\u7e41\u82b1\u5982\u9526\uff0c\u5728\u8fd9\u5173\u5916\u957f\u767d\u5c71\u4e0b\u7684\u82e6\u5bd2\u4e4b\u5730\uff0c\u5374\u662f\u79ef\u96ea\u521d\u878d\uff0c\u6d51\u6ca1\u6625\u65e5\u6c14\u8c61\u3002\u4e1c\u65b9\u7ea2\u65e5\u752b\u4ece\u5c71\u540e\u5347\u8d77\uff0c\u6de1\u9ec4\u7684\u9633\u5149\u7167\u5728\u8eab\u4e0a\uff0c\u6b8a\u65e0\u6696\u610f\u3002 \u3000\u3000\u5c71\u4e2d\u867d\u51b7\uff0c\u4f46\u56db\u540d\u4e58\u8005\u7eb5\u9a6c\u6025\u9a70\u4e4b\u4e0b\uff0c\u4e0d\u4e45\u4eba\u4eba\u5934\u4e0a\u5192\u6c57\u3002 \u3000\u3000\u90a3\u9ad8\u8eab\u6750\u7684\u7537\u5b50\u5c06\u5916\u6c05\u8131\u4e86\u4e0b\u6765\uff0c\u653e\u5728\u978d\u5934\u3002\u4ed6\u8eab\u7a7f\u9752\u7ef8\u9762\u76ae\u888d\uff0c\u8170\u60ac\u957f\u5251\uff0c\u7709\u5934\u6df1\u9501\uff0c\u6ee1\u8138\u6012\u5bb9\uff0c\u773c\u4e2d\u7adf\u4f3c\u8981\u55b7\u51fa\u706b\u6765\uff0c\u4e0d\u4f4f\u4ef7\u7684\u50ac\u9a6c\u72c2\u5954\u3002 \u3000\u3000\u8fd9\u4eba\u662f\u8fbd\u4e1c\u5929\u9f99\u95e8\u5317\u5b97\u65b0\u63a5\u4efb\u7684\u638c\u95e8\u4eba\u201c\u817e\u9f99\u5251\u201d\u66f9\u4e91\u5947\u3002\u5929\u9f99\u95e8\u638c\u5251\u53cc\u7edd\uff0c\u4ed6\u6240\u5b66\u90fd\u5df2\u9887\u6709\u6240\u6210\u3002\u767d\u8138\u6c49\u5b50\u662f\u4ed6\u5e08\u5f1f\u201c\u56de\u9f99\u5251\u201d\u5468\u4e91\u9633\u3002\u9ad8\u7626\u8001\u8005\u662f\u4ed6\u4eec\u5e08\u53d4\u201c\u4e03\u661f\u624b\u201d\u962e\u58eb\u4e2d\uff0c\u5728\u5929\u9f99\u5317\u5b97\u7b97\u5f97\u662f\u7b2c\u4e00\u9ad8\u624b\u3002\u90a3\u5bcc\u5546\u6a21\u6837\u7684\u8001\u8005\u5219\u662f\u5929\u9f99\u95e8\u5357\u5b97\u7684\u638c\u95e8\u4eba\u201c\u5a01\u9707\u5929\u5357\u201d\u6bb7\u5409\uff0c\u6b64\u6b21\u4e4b\u4e8b\u4e0e\u5929\u9f99\u95e8\u5357\u5317\u4e24\u5b97\u4ff1\u6709\u91cd\u5927\u5e72\u7cfb\uff0c\u662f\u4ee5\u4ed6\u5343\u91cc\u8fe2\u8fe2\uff0c\u8fdc\u6765\u5173\u5916\u3002 \u3000\u3000\u56db\u4eba\u80ef\u4e0b\u6240\u4e58\u90fd\u662f\u5173\u5916\u826f\u9a6c\uff0c\u811a\u7a0b\u6781\u5feb\uff0c\u4e00\u53e3\u6c14\u5954\u51fa\u4e03\u516b\u91cc\u540e\uff0c\u524d\u9762\u4e94\u4e58\u9a6c\u5df2\u76f8\u8ddd\u4e0d\u8fdc\u3002\u66f9\u4e91\u5947\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5582\uff0c\u76f8\u597d\u7684\uff0c\u505c\u6b65\uff01\u201d\u90a3\u4e94\u4eba\u5168\u4e0d\u7406\u4f1a\uff0c\u53cd\u800c\u7eb5\u9a6c\u5954\u5f97\u66f4\u5feb\u3002\u66f9\u4e91\u5947\u5389\u58f0\u559d\u9053\uff1a\u201c\u518d\u4e0d\u505c\u6b65\uff0c\u83ab\u602a\u6211\u4eec\u65e0\u793c\u4e86\uff01\u201d \u3000\u3000\u53ea\u542c\u5f97\u524d\u9762\u4e00\u4eba\u820c\u5934\u6253\u6eda\uff0c\u561f\u7684\u4e00\u58f0\uff0c\u52d2\u9a6c\u8f6c\u8eab\uff0c\u5176\u4f59\u56db\u4eba\u5374\u4ecd\u662f\u7ee7\u7eed\u5954\u9a70\u3002\u66f9\u4e91\u5947\u4e00\u9a6c\u5f53\u5148\uff0c\u4f46\u89c1\u90a3\u4eba\u5f2f\u5f13\u642d\u7bad\uff0c\u7bad\u5c16\u6307\u5411\u4ed6\u7684\u80f8\u53e3\u3002\u66f9\u4e91\u5947\u827a\u9ad8\u4eba\u80c6\u5927\uff0c\u7adf\u4e0d\u5c06\u4ed6\u5229\u7bad\u653e\u5728\u5fc3\u4e0a\uff0c\u626c\u97ad\u5927\u547c\uff1a\u201c\u5582\uff0c\u662f\u9676\u4e16\u5144\u4e48\uff1f\u201d \u3000\u3000\u90a3\u4eba\u9762\u76ee\u82f1\u4fca\uff0c\u53cc\u7709\u659c\u98de\uff0c\u4e8c\u5341\u4e09\u56db\u5c81\u5e74\u7eaa\uff0c\u4e00\u8eab\u52b2\u88c5\u7ed3\u675f\uff0c\u542c\u5f97\u66f9\u4e91\u5947\u53eb\u58f0\uff0c\u7eb5\u58f0\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u770b\u7bad\uff01\u201d\u55d6\u55d6\u55d6\u8fde\u54cd\uff0c\u4e09\u652f\u7fbd\u7bad\u5206\u4e0a\u4e2d\u4e0b\u4e09\u8def\u8fde\u73e0\u5c04\u5230\u3002 \u3000\u3000\u66f9\u4e91\u5947\u6ca1\u6599\u5230\u4ed6\u4e09\u7bad\u6765\u5f97\u5982\u6b64\u8fc5\u6377\uff0c\u5fc3\u4e2d\u5fae\u5fae\u4e00\u60ca\uff0c\u9a6c\u97ad\u75be\u7529\u51fa\u53bb\uff0c\u6253\u6389\u4e86\u4e0a\u8def\u4e0e\u4e2d\u8def\u5c04\u6765\u7684\u4e24\u7bad\uff0c\u63a5\u7740\u4e00\u63d0\u9a6c\u7f30\uff0c\u90a3\u9a6c\u5411\u4e0a\u4e00\u8dc3\uff0c\u7b2c\u4e09\u652f\u7bad\u8d34\u7740\u9a6c\u809a\u5b50\u4ece\u56db\u817f\u95f4\u7a7f\u4e86\u8fc7\u53bb\uff0c\u76f8\u5dee\u53ea\u662f\u6570\u5bf8\u3002\u90a3\u9752\u5e74\u54c8\u54c8\u4e00\u7b11\uff0c\u62e8\u8f6c\u9a6c\u5934\uff0c\u5411\u524d\u4fbf\u8dd1\u3002 \u3000\u3000\u66f9\u4e91\u5947\u94c1\u9752\u7740\u8138\uff0c\u7eb5\u9a6c\u6b32\u8d76\u3002\u962e\u58eb\u4e2d\u53eb\u9053\uff1a\u201c\u4e91\u5947\uff0c\u6c89\u4f4f\u4e86\u6c14\uff0c\u4e0d\u6015\u4ed6\u98de\u4e0a\u5929\u53bb\u3002\u201d\u7eb5\u8eab\u4e0b\u9a6c\uff0c\u62fe\u8d77\u96ea\u5730\u91cc\u7684\u4e09\u652f\u7fbd\u7bad\uff0c\u679c\u7136\u4e0e\u9002\u624d\u5c04\u96c1\u7684\u4e00\u822c\u65e0\u5f02\u3002\u6bb7\u5409\u6c89\u7740\u8138\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u679c\u771f\u662f\u8fd9\u5c0f\u5b50\uff01\u201d\u66f9\u4e91\u5947\u9053\uff1a\u201c\u7b49\u4e00\u4e0b\u5e08\u59b9\uff0c\u77a7\u5979\u66f4\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u56db\u4eba\u5019\u4e86\u4e00\u987f\u996d\u529f\u592b\uff0c\u4e0d\u542c\u5f97\u6765\u8def\u4e0a\u6709\u9a6c\u8e44\u58f0\u54cd\u3002\u66f9\u4e91\u5947\u7126\u8e81\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6211\u77a7\u77a7\u53bb\uff01\u201d\u62cd\u9a6c\u8d76\u56de\u3002\u962e\u58eb\u4e2d\u671b\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u771f\u96be\u602a\u5f97\u4ed6\u3002\u201d\u6bb7\u5409\u9053\uff1a\u201c\u962e\u5e08\u5144\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u962e\u58eb\u4e2d\u6447\u4e86\u6447\u5934\uff0c\u5374\u4e0d\u7b54\u8bdd\u3002 \u3000\u3000\u66f9\u4e91\u5947\u5954\u51fa\u6570\u91cc\uff0c\u53ea\u89c1\u4e00\u5339\u7070\u9a6c\u7a7a\u8eab\u7ad9\u5728\u96ea\u5730\u91cc\uff0c\u4e00\u4e2a\u767d\u8863\u5973\u90ce\u4e00\u8db3\u8dea\u5728\u5730\u4e0b\uff0c\u4f3c\u5728\u96ea\u4e2d\u5bfb\u627e\u4ec0\u4e48\u3002\u66f9\u4e91\u5947\u53eb\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u90a3\u5973\u90ce\u4e0d\u7b54\uff0c\u5ffd\u7136\u7ad9\u76f4\u8eab\u5b50\uff0c\u624b\u4e2d\u62ff\u7740\u4e00\u6839\u9ec4\u6f84\u6f84\u4e4b\u7269\uff0c\u5728\u65e5\u5149\u4e0b\u95ea\u95ea\u53d1\u5149\u3002\u66f9\u4e91\u5947\u8d70\u8fd1\u8eab\u53bb\uff0c\u63a5\u4e86\u8fc7\u6765\uff0c\u89c1\u662f\u4e00\u652f\u9ec4\u91d1\u94f8\u6210\u7684\u5c0f\u7b14\uff0c\u957f\u7ea6\u4e09\u5bf8\uff0c\u7b14\u5c16\u950b\u5229\uff0c\u6253\u9020\u5f97\u751a\u662f\u7cbe\u81f4\uff0c\u7b14\u6746\u4e0a\u523b\u7740\u4e00\u4e2a\u5c0f\u5c0f\u7684\u201c\u5b89\u201d\u5b57\u3002\u8fd9\u652f\u91d1\u7b14\u770b\u6765\u65e2\u662f\u73a9\u7269\uff0c\u5374\u4e5f\u53ef\u4f5c\u6697\u5668\u4e4b\u7528\uff0c\u4e0d\u7981\u5fae\u5fae\u76b1\u7709\uff0c\u8bf4\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u90a3\u5973\u90ce\u9053\uff1a\u201c\u4f60\u4eec\u8d70\u540e\uff0c\u6211\u968f\u540e\u8ddf\u6765\uff0c\u5954\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u6709\u4e00\u4e58\u9a6c\u4ece\u540e\u8ffd\u6765\uff0c\u90a3\u9a6c\u597d\u5feb\uff0c\u53ea\u4e00\u4f1a\u513f\u5c31\u4ece\u6211\u8eab\u65c1\u63a0\u8fc7\u3002\u9a6c\u4e0a\u4e58\u5ba2\u624b\u4e00\u626c\uff0c\u629b\u6765\u4e86\u8fd9\u652f\u5c0f\u7b14\uff0c\u5c06\u6211\u2026\u2026\u5c06\u6211\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u8138\u4e0a\u6655\u7ea2\uff0c\u55eb\u5685\u7740\u8bf4\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u66f9\u4e91\u5947\u51dd\u671b\u7740\u5979\uff0c\u53ea\u89c1\u5979\u51dd\u8102\u822c\u7684\u96ea\u80a4\u4e4b\u4e0b\uff0c\u9690\u9690\u900f\u51fa\u4e00\u5c42\u80ed\u8102\u4e4b\u8272\uff0c\u53cc\u776b\u5fae\u5782\uff0c\u4e00\u80a1\u5973\u513f\u7f9e\u6001\uff0c\u5a07\u8273\u65e0\u4f26\uff0c\u4e0d\u7531\u5f97\u80f8\u4e2d\u4e00\u8361\uff0c\u968f\u5373\u7591\u4e91\u5927\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u54b1\u4eec\u8ffd\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u5973\u90ce\u9053\uff1a\u201c\u8c01\u554a\uff1f\u201d\u66f9\u4e91\u5947\u51b7\u51b7\u7684\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u5f53\u771f\u4e0d\u77e5\uff1f\u201d\u90a3\u5973\u90ce\u62ac\u8d77\u5934\u6765\uff0c\u9053\uff1a\u201c\u6211\u600e\u4f1a\u77e5\u9053\uff1f\u201d\u66f9\u4e91\u5947\u9053\uff1a\u201c\u662f\u4f60\u7684\u5fc3\u4e0a\u4eba\u3002\u201d\u90a3\u5973\u90ce\u51b2\u53e3\u800c\u9053\uff1a\u201c\u9676\u5b50\u5b89\uff1f\u201d\u8fd9\u8bdd\u4e00\u51fa\u53e3\uff0c\u767b\u65f6\u6ee1\u8138\u7ea2\u6655\u3002\u66f9\u4e91\u5947\u7709\u95f4\u6709\u5982\u7f69\u4e0a\u4e86\u4e00\u5c42\u9ed1\u4e91\uff0c\u53eb\u9053\uff1a\u201c\u6211\u4e00\u8bf4\u662f\u4f60\u7684\u5fc3\u4e0a\u4eba\uff0c\u4f60\u5c31\u63a5\u53e3\u8bf4\u9676\u5b50\u5b89\uff01\u201d \u3000\u3000\u90a3\u5973\u90ce\u542c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u8138\u4e0a\u66f4\u52a0\u7ea2\u4e86\uff0c\u6cea\u6c34\u5728\u4e00\u53cc\u660e\u6f84\u6e05\u6f88\u7684\u773c\u4e2d\u6eda\u6765\u6eda\u53bb\uff0c\u987f\u8db3\u53eb\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u2026\u2026\u201d\u66f9\u4e91\u5947\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u600e\u4e48\uff1f\u201d\u90a3\u5973\u90ce\u9053\uff1a\u201c\u4ed6\u662f\u6211\u6ca1\u8fc7\u95e8\u7684\u4e08\u592b\uff0c\u81ea\u7136\u662f\u6211\u5fc3\u4e0a\u4eba\u3002\u201d\u66f9\u4e91\u5947\u5927\u6012\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u62d4\u51fa\u957f\u5251\u3002\u90a3\u5973\u90ce\u53cd\u800c\u8d70\u4e0a\u4e00\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u6709\u79cd\u5c31\u5c06\u6211\u6740\u4e86\u3002\u201d\u66f9\u4e91\u5947\u54ac\u7740\u7259\u9f7f\uff0c\u671b\u7740\u5979\u5fae\u5fae\u62ac\u8d77\u7684\u8138\uff0c\u5fc3\u4e2d\u67d4\u60c5\u987f\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u7f62\u5566\uff0c\u7f62\u5566\uff01\u201d\u56de\u624b\u4e00\u5251\uff0c\u731b\u5f80\u81ea\u5df1\u5fc3\u53e3\u624e\u53bb\u3002 \u3000\u3000\u90a3\u5973\u90ce\u51fa\u624b\u597d\u5feb\uff0c\u53cd\u624b\u62d4\u5251\uff0c\u56de\u81c2\u75be\u683c\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u53cc\u5251\u76f8\u4ea4\uff0c\u8ff8\u51fa\u4e86\u6570\u661f\u706b\u82b1\u3002\u66f9\u4e91\u5947\u6068\u6068\u7684\u9053\uff1a\u201c\u4f60\u65e2\u5df2\u4e0d\u5c06\u6211\u653e\u5728\u5fc3\u4e0a\uff0c\u4f55\u5fc5\u53c8\u8ba9\u6211\u5728\u8fd9\u4e16\u4e0a\u591a\u53d7\u82e6\u695a\uff1f\u201d\u90a3\u5973\u90ce\u7f13\u7f13\u8fd8\u5251\u5165\u9798\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u65e9\u77e5\u9053\uff0c\u662f\u7239\u7239\u5c06\u6211\u8bb8\u914d\u7ed9\u4ed6\uff0c\u96be\u9053\u662f\u6211\u81ea\u5df1\u4f5c\u7684\u4e3b\u4e48\uff1f\u201d\u66f9\u4e91\u5947\u53cc\u7709\u4e00\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u613f\u8ddf\u4f60\u6d6a\u8ff9\u5929\u6daf\uff0c\u5728\u8352\u5c9b\u6df1\u5c71\u4e4b\u4e2d\u9690\u5c45\u53ae\u5b88\uff0c\u4f60\u600e\u53c8\u4e0d\u80af\uff1f\u201d\u90a3\u5973\u90ce\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u6211\u77e5\u9053\u4f60\u5bf9\u6211\u4e00\u7247\u75f4\u5fc3\uff0c\u6211\u53c8\u4e0d\u662f\u50bb\u5b50\uff0c\u600e\u80fd\u4e0d\u5ff5\u7740\u4f60\u7684\u597d\u5904\u3002\u53ef\u662f\u4f60\u6267\u638c\u6211\u5929\u9f99\u5317\u5b97\u95e8\u6237\uff0c\u82e5\u662f\u505a\u51fa\u8fd9\u7b49\u4e8b\u6765\uff0c\u5929\u9f99\u95e8\u58f0\u540d\u626b\u5730\uff0c\u5728\u6c5f\u6e56\u4e0a\u989c\u9762\u4f55\u5b58\uff1f\u201d \u3000\u3000\u66f9\u4e91\u5947\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6211\u5c31\u662f\u4e3a\u4f60\u7c89\u8eab\u788e\u9aa8\uff0c\u4e5f\u662f\u7518\u613f\u3002\u5929\u584c\u4e0b\u6765\u6211\u4e5f\u4e0d\u7406\uff0c\u7ba1\u4ed6\u4ec0\u4e48\u638c\u95e8\u4e0d\u638c\u95e8\u3002\u201d\u90a3\u5973\u90ce\u5fae\u5fae\u4e00\u7b11\uff0c\u8f7b\u8f7b\u63e1\u4f4f\u4ed6\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u6211\u5c31\u662f\u4e0d\u7231\u4f60\u8fd9\u4e2a\u9739\u96f3\u706b\u7206\u3001\u4e0d\u987e\u4e00\u5207\u7684\u813e\u6c14\u5462\u3002\u201d \u3000\u3000\u66f9\u4e91\u5947\u7ed9\u5979\u8fd9\u4e48\u4e00\u8bf4\uff0c\u518d\u4e5f\u53d1\u4f5c\u4e0d\u5f97\uff0c\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u53c8\u628a\u4ed6\u7ed9\u7684\u73a9\u610f\u513f\u5f53\u4f5c\u5b9d\u8d1d\u4f3c\u7684\uff1f\u201d\u90a3\u5973\u90ce\u9053\uff1a\u201c\u8c01\u8bf4\u662f\u4ed6\u7ed9\u7684\uff1f\u6211\u51e0\u65f6\u89c1\u8fc7\u4ed6\u6765\uff1f\u201d \u3000\u3000\u66f9\u4e91\u5947\u9053\uff1a\u201c\u54fc\uff0c\u8fd9\u6837\u503c\u94b1\u7684\u73a9\u610f\u513f\uff0c\u8fd8\u6709\u4eba\u771f\u7684\u5f53\u4f5c\u6697\u5668\u6253\u4e48\uff1f\u8fd9\u7b14\u4e0a\u4e0d\u660e\u660e\u523b\u7740\u4ed6\u7684\u540d\u5b57\uff1f\u82e5\u4e0d\u662f\u4ed6\uff0c\u53c8\u662f\u8c01\u7ed9\u4f60\u7684\uff1f\u201d\u90a3\u5973\u90ce\u55d4\u9053\uff1a\u201c\u4f60\u65e2\u7231\u8fd9\u4e48\u778e\u7591\u5fc3\uff0c\u8d81\u65e9\u522b\u8ddf\u6211\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u7eb5\u5230\u7070\u9a6c\u8eab\u65c1\uff0c\u4e00\u8dc3\u4e0a\u978d\uff0c\u7f30\u7ef3\u4e00\u63d0\uff0c\u90a3\u9a6c\u653e\u8e44\u4fbf\u5954\u3002 \u3000\u3000\u66f9\u4e91\u5947\u5fd9\u4e0a\u9a6c\u8ffd\u53bb\uff0c\u4f38\u76ae\u9774\u731b\u8e22\u5750\u9a91\u809a\u8179\uff0c\u7247\u523b\u95f4\u4fbf\u8ffd\u4e0a\u4e86\uff0c\u8eab\u5b50\u4e00\u63a2\uff0c\u53f3\u624b\u62c9\u4f4f\u4e86\u7070\u9a6c\u7684\u8f94\u5934\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4f60\u542c\u6211\u8bf4\u3002\u201d\u90a3\u5973\u90ce\u4e3e\u8d77\u9a6c\u97ad\uff0c\u5f80\u4ed6\u624b\u4e0a\u62bd\u53bb\uff0c\u559d\u9053\uff1a\u201c\u653e\u5f00\uff01\u7ed9\u4eba\u5bb6\u77a7\u89c1\u4e86\u6210\u4ec0\u4e48\u6837\u5b50\uff1f\u201d\u66f9\u4e91\u5947\u5374\u4e0d\u653e\u624b\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u624b\u80cc\u4e0a\u767b\u65f6\u8d77\u4e86\u4e00\u6761\u8840\u75d5\u3002 \u3000\u3000\u90a3\u5973\u90ce\u5fc3\u6709\u4e0d\u5fcd\uff0c\u9053\uff1a\u201c\u4f60\u4f55\u82e6\u53c8\u6765\u60f9\u6211\uff1f\u201d\u66f9\u4e91\u5947\u9053\uff1a\u201c\u662f\u6211\u4e0d\u597d\uff0c\u4f60\u518d\u6253\u5427\uff01\u201d\u90a3\u5973\u90ce\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u624b\u9178\uff0c\u6253\u4e0d\u52a8\u5566\u3002\u201d\u66f9\u4e91\u5947\u7b11\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u6376\u6376\u3002\u201d\u4f38\u624b\u53bb\u62c9\u5979\u624b\u81c2\u3002\u90a3\u5973\u90ce\u8fce\u5934\u4e00\u97ad\uff0c\u66f9\u4e91\u5947\u5934\u4e00\u504f\uff0c\u8fd9\u4e00\u6b21\u628a\u97ad\u5b50\u8eb2\u5f00\u4e86\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u624b\u600e\u4e48\u53c8\u4e0d\u9178\u5566\uff1f\u201d\u90a3\u5973\u90ce\u677f\u8d77\u4e86\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53eb\u4f60\u522b\u78b0\u6211\u3002\u201d \u3000\u3000\u66f9\u4e91\u5947\u966a\u7b11\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u4f60\u8bf4\u8fd9\u91d1\u7b14\u5230\u5e95\u54ea\u91cc\u6765\u7684\u3002\u201d\u90a3\u5973\u90ce\u7b11\u9053\uff1a\u201c\u662f\u6211\u5fc3\u4e0a\u4eba\u7ed9\u7684\u3002\u4e0d\u662f\u4ed6\u7ed9\uff0c\u8fd8\u6709\u8c01\u7ed9\uff1f\u96be\u9053\u662f\u4f60\u7ed9\u6211\u7684\uff1f\u201d\u66f9\u4e91\u5947\u5fc3\u5934\u4e00\u9178\uff0c\u70ed\u8840\u4e0a\u6d8c\uff0c\u53c8\u8981\u53d1\u4f5c\uff0c\u4f46\u89c1\u5979\u7b11\u9765\u5982\u82b1\uff0c\u7ea2\u5507\u5fae\u5fae\u98a4\u52a8\uff0c\u9732\u51fa\u4e00\u53e3\u7389\u77f3\u822c\u7684\u7259\u9f7f\uff0c\u6012\u6c14\u767b\u65f6\u6c89\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u90a3\u5973\u90ce\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u6211\u4ece\u5c0f\u5f97\u4f60\u5c3d\u5fc3\u7167\u987e\u3002\u4f60\u5f85\u6211\u771f\u6bd4\u4eb2\u751f\u54e5\u54e5\u8fd8\u597d\u3002\u6211\u53c8\u4e0d\u662f\u5168\u65e0\u5fc3\u809d\u4e4b\u4eba\uff0c\u600e\u4e0d\u60f3\u62a5\u7b54\uff1f\u4f55\u51b5\u6211\u4eec\u2026\u2026\u53ea\u662f\uff0c\u6211\u5b9e\u5728\u597d\u751f\u4e3a\u96be\u3002\u4f60\u4e00\u5411\u5173\u5fc3\u6211\u3001\u7231\u62a4\u6211\uff0c\u73b0\u4e0b\u7239\u7239\u4e0d\u5e78\u60e8\u6b7b\uff0c\u6211\u5929\u9f99\u95e8\u9762\u4e34\u6210\u8d25\u5174\u4ea1\u7684\u91cd\u5927\u5173\u5934\uff0c\u4f60\u600e\u4e48\u53cd\u800c\u4e0d\u80af\u4f53\u8c05\u6211\u4e86\uff1f\u201d\u66f9\u4e91\u5947\u5446\u4e86\u534a\u664c\uff0c\u518d\u65e0\u8bdd\u8bf4\uff0c\u5de6\u624b\u4e00\u6325\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u603b\u662f\u5bf9\u7684\uff0c\u6211\u603b\u662f\u9519\u7684\uff0c\u8d70\u5427\uff01\u201d \u3000\u3000\u90a3\u5973\u90ce\u5ae3\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u6478\u51fa\u4e00\u5757\u624b\u5e15\uff0c\u7ed9\u4ed6\u62b9\u53bb\u6ee1\u989d\u6c57\u6c34\uff0c\u9053\uff1a\u201c\u5927\u96ea\u5730\u91cc\uff0c\u51fa\u4e86\u6c57\u4e0d\u62b9\u53bb\uff0c\u83ab\u7740\u4e86\u51c9\u3002\u201d\u66f9\u4e91\u5947\u5fc3\u4e2d\u751c\u751c\u7684\u8bf4\u4e0d\u51fa\u7684\u53d7\u7528\uff0c\u6ee1\u8154\u6012\u6c14\u767b\u65f6\u5316\u4e3a\u4e4c\u6709\uff0c\u6325\u97ad\u5728\u90a3\u5973\u90ce\u7684\u7070\u9a6c\u81c0\u4e0a\u8f7b\u8f7b\u4e00\u97ad\u3002\u4e8c\u4eba\u53cc\u9a91\uff0c\u5e76\u80a9\u9a70\u53bb\u3002 \u3000\u3000\u90a3\u5973\u90ce\u540d\u53eb\u7530\u9752\u6587\uff0c\u5e74\u7eaa\u867d\u8f7b\uff0c\u5728\u5173\u5916\u6b66\u6797\u4e2d\u5374\u5df2\u9887\u6709\u540d\u58f0\u3002\u56e0\u5979\u5bb9\u8c8c\u7f8e\u4e3d\uff0c\u6027\u53c8\u673a\u4f36\uff0c\u8fbd\u4e1c\u6b66\u6797\u4e2d\u516c\u9001\u5979\u4e00\u4e2a\u5916\u53f7\uff0c\u53eb\u4f5c\u201c\u9526\u6bdb\u8c82\u201d\u3002\u90a3\u8c82\u9f20\u5728\u96ea\u5730\u4e2d\u884c\u8d70\u5982\u98de\uff0c\u806a\u660e\u4f36\u4fd0\uff0c \u201c\u9526\u6bdb\u201d\u4e8c\u5b57\uff0c\u81ea\u662f\u5f62\u5bb9\u5979\u7684\u7f8e\u8c8c\u4e86\u3002\u5979\u7236\u4eb2\u7530\u5f52\u519c\u901d\u4e16\u672a\u4e45\uff0c\u662f\u4ee5\u5979\u4e00\u8eab\u7f1f\u7d20\uff0c\u6234\u7740\u91cd\u5b5d\u3002 \u3000\u3000\u4e24\u4eba\u6025\u5954\u4e00\u9635\uff0c\u8ffd\u4e0a\u4e86\u6bb7\u5409\u3001\u962e\u58eb\u4e2d\u3001\u5468\u4e91\u9633\u4e09\u4eba\u3002\u962e\u58eb\u4e2d\u5411\u66f9\u4e91\u5947\u6a2a\u4e86\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u53bb\u4e86\u8fd9\u4e48\u4e45\uff0c\u89c1\u5230\u4ec0\u4e48\u4e86\uff1f\u201d \u3000\u3000\u66f9\u4e91\u5947\u8138\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u6ca1\u89c1\u4ec0\u4e48\u3002\u201d\u53cc\u817f\u4e00\u5939\uff0c\u7eb5\u9a6c\u5feb\u8dd1\u3002 \u3000\u3000\u53c8\u5954\u51fa\u6570\u91cc\uff0c\u5c71\u52bf\u6e10\u9661\uff0c\u96ea\u79ef\u5f97\u539a\u539a\u7684\uff0c\u9a6c\u8e44\u4e00\u6e9c\u4e00\u6ed1\uff0c\u56db\u4eba\u4e0d\u6562\u50ac\uff0c\u677e\u9a6c\u7f30\u7f13\u884c\u3002\u8f6c\u8fc7\u4e24\u4e2a\u5c71\u5773\uff0c\u5c71\u9053\u66f4\u662f\u9669\u5cfb\u3002\u5ffd\u542c\u5de6\u9996\u4e00\u58f0\u9a6c\u5636\uff0c\u66f9\u4e91\u5947\u53f3\u8db3\u5728\u9a6c\u956b\u4e0a\u4e00\u70b9\uff0c\u659c\u8eab\u98de\u51fa\uff0c\u843d\u5728\u4e00\u682a\u5927\u677e\u6811\u540e\u9762\uff0c\u5148\u85cf\u8eab\u5f62\uff0c\u518d\u7eb5\u76ee\u5411\u524d\u671b\u53bb\u3002\u53ea\u89c1\u5c71\u5761\u8fb9\u51e0\u682a\u6811\u4e0a\u7cfb\u7740\u4e94\u5339\u9a6c\uff0c\u96ea\u5730\u91cc\u4e00\u884c\u8db3\u5370\uff0c\u7b14\u76f4\u4e0a\u5c71\u3002\u66f9\u4e91\u5947\u53eb\u9053\uff1a\u201c\u4e24\u4f4d\u5e08\u53d4\uff0c\u5c0f\u8d3c\u9003\u4e0a\u5c71\u5566\uff0c\u54b1\u4eec\u5feb\u8ffd\u3002\u201d \u3000\u3000\u6bb7\u5409\u5411\u6765\u8c28\u614e\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u65b9\u82e5\u662f\u6545\u610f\u5f15\u8bf1\u54b1\u4eec\u6765\u6b64\uff0c\u53ea\u6015\u5c71\u4e2d\u8bbe\u4e86\u57cb\u4f0f\u3002\u201d\u66f9\u4e91\u5947\u9053\uff1a\u201c\u5c31\u662f\u9f99\u6f6d\u864e\u7a74\uff0c\u4eca\u65e5\u4e5f\u8981\u95ef\u4ed6\u4e00\u95ef\uff01\u201d\u6bb7\u5409\u542c\u4ed6\u8bf4\u5f97\u9c81\u83bd\uff0c\u9887\u4e3a\u4e0d\u5feb\uff0c\u5411\u962e\u58eb\u4e2d\u9053\uff1a\u201c\u962e\u5e08\u5144\uff0c\u4f60\u8bf4\u600e\u7684\uff1f\u201d\u962e\u58eb\u4e2d\u8fd8\u672a\u7b54\u8bdd\uff0c\u7530\u9752\u6587\u62a2\u7740\u9053\uff1a\u201c\u6709\u5a01\u9707\u5929\u5357\u6bb7\u5e08\u53d4\u5728\u6b64\uff0c\u5c31\u6709\u518d\u5389\u5bb3\u7684\u57cb\u4f0f\uff0c\u4e5f\u4e0d\u7528\u6015\u3002\u201d\u6bb7\u5409\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u77a7\u4ed6\u4eec\u795e\u60c5\uff0c\u8d70\u5f97\u6781\u662f\u5306\u5fd9\uff0c\u4f3c\u4e4e\u53c8\u4e0d\u662f\u8bbe\u4f0f\u3002\u8fd9\u6837\u5427\uff0c\u201d\u624b\u6307\u53f3\u9996\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u4ece\u8fd9\u8fb9\u7ed5\u9053\u4e0a\u5c71\uff0c\u8f6c\u8fc7\u6765\u653b\u4ed6\u4eec\u4e00\u4e2a\u51fa\u5176\u4e0d\u610f\u3002\u201d\u66f9\u4e91\u5947\u53eb\u9053\uff1a\u201c\u597d\uff0c\u6b64\u8ba1\u5927\u5999\uff01\u201d \u3000\u3000\u6bb7\u5409\u7b49\u90fd\u4e0b\u4e86\u9a6c\uff0c\u5c06\u9a6c\u5339\u7cfb\u5728\u5927\u677e\u6811\u4e0b\uff0c\u7ffb\u8d77\u957f\u8863\u4e0b\u895f\u7f1a\u5728\u8170\u91cc\uff0c\u5c55\u5f00\u8f7b\u529f\u63d0\u7eb5\u672f\uff0c\u4ece\u5c71\u5761\u53f3\u9996\u4e0a\u5c71\u3002\u8fd9\u4e00\u5e26\u6811\u6728\u4e1b\u751f\uff0c\u5c71\u77f3\u5d99\u5ccb\uff0c\u884c\u8d70\u751a\u662f\u4e0d\u4fbf\uff0c\u4f46\u591a\u4e86\u4e00\u5c42\u63a9\u853d\uff0c\u5374\u4e0d\u6613\u4e3a\u654c\u4eba\u53d1\u89c9\u3002\u4e94\u4eba\u521d\u65f6\u9c7c\u8d2f\u800c\u884c\uff0c\u4e00\u4e2a\u7d27\u63a5\u4e00\u4e2a\uff0c\u65f6\u5019\u4e00\u957f\uff0c\u6e10\u6e10\u5206\u51fa\u4e86\u529f\u592b\u9ad8\u4e0b\u3002\u6bb7\u5409\u4e0e\u962e\u58eb\u4e2d\u5e76\u80a9\u5728\u524d\uff0c\u66f9\u4e91\u5947\u5815\u540e\u4e08\u4f59\uff0c\u7530\u9752\u6587\u4e0e\u5468\u4e91\u9633\u53c8\u5728\u540e\u6570\u4e08\u3002\u66f9\u4e91\u5947\u5fc3\u60f3\uff1a\u201c\u6bb7\u5e08\u53d4\u662f\u5357\u5b97\u638c\u95e8\uff0c\u53f7\u79f0\u5a01\u9707\u5929\u5357\uff0c\u4e0d\u77e5\u4ed6\u5357\u5b97\u7684\u529f\u592b\u4e0e\u6211\u5317\u5b97\u5230\u5e95\u8c01\u9ad8\u8c01\u4f4e\uff1f\u4eca\u65e5\u5012\u8981\u9886\u6559\u9886\u6559\u3002\u201d\u4e00\u63d0\u6c14\uff0c\u8db3\u4e0b\u52a0\u52b2\uff0c\u500f\u5ffd\u62a2\u5728\u6bb7\u962e\u4e8c\u4eba\u524d\u5934\u3002 \u3000\u3000\u53ea\u542c\u6bb7\u5409\u8d5e\u9053\uff1a\u201c\u66f9\u4e16\u5144\uff0c\u597d\u4fca\u8eab\u624b\u554a\uff0c\u5f53\u771f\u662f\u82f1\u96c4\u51fa\u5728\u5e74\u5c11\u3002\u201d\u66f9\u4e91\u5947\u6015\u4ed6\u8ffd\u4e0a\uff0c\u4e0d\u6562\u56de\u5934\uff0c\u53ea\u9053\uff1a\u201c\u8bf7\u6bb7\u5e08\u53d4\u591a\u52a0\u6307\u70b9\u3002\u201d\u53e3\u4e2d\u8fd9\u4e48\u8bf4\uff0c\u811a\u4e0b\u4e1d\u6beb\u4e0d\u505c\uff0c\u5954\u4e86\u4e00\u9635\uff0c\u4f3c\u4e4e\u542c\u5f97\u811a\u6b65\u58f0\u606f\uff0c\u56de\u5934\u4e00\u671b\uff0c\u4e0d\u7981\u5413\u4e86\u4e00\u8df3\uff0c\u539f\u6765\u6bb7\u5409\u3001\u962e\u58eb\u4e2d\u4e24\u4eba\u5c31\u5728\u4ed6\u8eab\u540e\u4e0d\u8fdc\uff0c\u5fd9\u52a0\u5feb\u811a\u6b65\uff0c\u6025\u51b2\u6570\u4e08\u3002 \u3000\u3000\u6bb7\u5409\u5fae\u5fae\u4e00\u7b11\uff0c\u4e0d\u75be\u4e0d\u5f90\u7684\u8ddf\u5728\u540e\u9762\u3002\u5c71\u4e0a\u79ef\u96ea\u66f4\u539a\uff0c\u9053\u8def\u5d0e\u5c96\uff0c\u884c\u8d70\u81ea\u662f\u8d39\u529b\u3002\u53ea\u8fc7\u4e86\u534a\u652f\u9999\u529f\u592b\uff0c\u66f9\u4e91\u5947\u6e10\u6e10\u6162\u4e86\u4e0b\u6765\uff0c\u5ffd\u89c9\u540e\u8111\u5fae\u5fae\u6e29\u70ed\uff0c\u4f3c\u4e4e\u6709\u4eba\u547c\u6c14\uff0c\u6b63\u8981\u56de\u5934\uff0c\u53f3\u80a9\u4e0a\u6709\u4eba\u8f7b\u8f7b\u4e00\u62cd\uff0c\u542c\u5f97\u6bb7\u5409\u7b11\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u52a0\u628a\u52b2\u513f\uff01\u201d\u66f9\u4e91\u5947\u4e00\u60ca\uff0c\u63d0\u6c14\u5411\u524d\u731b\u51b2\u3002\u8fd9\u4e00\u51b2\u867d\u628a\u6bb7\u962e\u4e24\u4eba\u629b\u4e0b\u4e86\u5341\u591a\u4e08\uff0c\u4f46\u5df2\u7136\u5fc3\u6d6e\u6c14\u7c97\uff0c\u5934\u4e0a\u5192\u6c57\u3002\u4ed6\u4f38\u8896\u4e00\u64e6\u989d\u4e0a\u6c57\u6c34\uff0c\u60f3\u8d77\u9002\u624d\u7530\u9752\u6587\u7ed9\u81ea\u5df1\u64e6\u6c57\u7684\u60c5\u666f\uff0c\u5634\u91cc\u95f4\u4e0d\u7531\u5f97\u9732\u51fa\u5fae\u7b11\uff0c\u4f46\u542c\u5f97\u80cc\u540e\u8e0f\u96ea\u4e4b\u58f0\uff0c\u6bb7\u5409\u4e24\u4eba\u53c8\u8d76\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u6bb7\u5409\u89c1\u66f9\u4e91\u5947\u8fd9\u4e48\u4e00\u51b2\u4e00\u6162\uff0c\u65e9\u77e5\u4ed6\u8f7b\u529f\u8fdc\u4e0d\u662f\u81ea\u5df1\u5bf9\u624b\uff0c\u53ea\u662f\u4e03\u661f\u624b\u962e\u58eb\u4e2d\u4e00\u58f0\u4e0d\u54cd\u7684\u5e76\u80a9\u800c\u884c\uff0c\u81ea\u5df1\u8dd1\u5f97\u5feb\uff0c\u4ed6\u4e5f\u5feb\uff0c\u81ea\u5df1\u8dd1\u5f97\u6162\u4e86\uff0c\u4ed6\u8ddf\u7740\u653e\u6162\u811a\u6b65\uff0c\u770b\u6765\u5c1a\u662f\u6e38\u5203\u6709\u4f59\uff0c\u672a\u5c3d\u5168\u529b\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u4eec\u5e08\u53d4\u4f84\u4fe9\u4eca\u513f\u8003\u8f83\u8001\u513f\u6765\u7740\u3002\u201d\u5f53\u4e0b\u731b\u5438\u4e00\u53e3\u6c14\uff0c\u65bd\u5c55\u6570\u5341\u5e74\u52e4\u4fee\u82e6\u7ec3\u7684\u8f7b\u529f\uff0c\u5728\u767d\u96ea\u5c71\u5761\u4e0a\u5b9b\u4f3c\u8db3\u4e0d\u70b9\u5730\u822c\u6ed1\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5929\u9f99\u95e8\u521b\u81ea\u6e05\u521d\uff0c\u539f\u672c\u4e00\u652f\uff0c\u5230\u5eb7\u7199\u5e74\u95f4\uff0c\u638c\u95e8\u4eba\u7684\u4e24\u4e2a\u5927\u5f1f\u5b50\u4e0d\u548c\uff0c\u5f85\u638c\u95e8\u4eba\u4e00\u6b7b\uff0c\u4fbf\u5206\u4e3a\u5357\u5317\u4e24\u5b97\u3002\u5357\u5b97\u4ee5\u8f7b\u6377\u527d\u608d\u4e3a\u5c1a\uff0c\u5317\u5b97\u5374\u6ce8\u91cd\u6c89\u7a33\u72e0\u8fa3\u3002\u4e24\u5b97\u6b66\u529f\u672c\u6e90\u67b6\u5f0f\u5b8c\u5168\u76f8\u540c\uff0c\u4f7f\u7528\u4e4b\u65f6\uff0c\u5374\u9887\u6709\u5f02\u5904\u3002\u8fd9\u4e0a\u5c71\u7684\u8f7b\u529f\u539f\u662f\u5357\u5b97\u6240\u64c5\uff0c\u6bb7\u5409\u4eba\u867d\u80a5\u80d6\uff0c\u4e00\u65bd\u5c55\u672c\u95e8\u5fc3\u6cd5\uff0c\u7adf\u7136\u77eb\u6377\u80dc\u4e8e\u733f\u7334\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u8d76\u51fa\u66f9\u4e91\u5947\u4e00\u91cc\u6709\u4f59\u3002\u962e\u58eb\u4e2d\u5374\u4ecd\u662f\u4e0d\u5373\u4e0d\u79bb\u7684\u4e0e\u4ed6\u5e76\u80a9\u800c\u884c\u3002\u6bb7\u5409\u6570\u6b21\u653e\u5feb\uff0c\u8981\u60f3\u5c06\u4ed6\u629b\u843d\uff0c\u4f46\u6bcf\u6b21\u53ea\u62a2\u524d\u6570\u4e08\uff0c\u962e\u58eb\u4e2d\u53c8\u7a33\u7a33\u7684\u8ffd\u5c06\u4e0a\u6765\u3002 \u3000\u3000\u773c\u89c1\u79bb\u5cf0\u9876\u53ea\u4e24\u4e09\u91cc\u8def\u7a0b\uff0c\u6bb7\u5409\u7b11\u9053\uff1a\u201c\u962e\u5e08\u5144\uff0c\u54b1\u4fe9\u6bd4\u6bd4\u811a\u529b\uff0c\u77a7\u8c01\u5148\u4e0a\u5cf0\u9876\u3002\u201d\u962e\u58eb\u4e2d\u9053\uff1a\u201c\u6211\u54ea\u91cc\u8d76\u5f97\u4e0a\u6bb7\u5e08\u5144\uff1f\u201d \u3000\u3000\u6bb7\u5409\u9053\uff1a\u201c\u522b\u5ba2\u6c14\u5566\uff01\u201d\u8bdd\u4e00\u51fa\u53e3\uff0c\u5982\u7bad\u79bb\u5f26\u822c\u75be\u51b2\u800c\u4e0a\uff0c\u4e0d\u5230\u7247\u523b\uff0c\u79bb\u5cf0\u9876\u5df2\u53ea\u6570\u4e08\uff0c\u56de\u5934\u89c1\u962e\u58eb\u4e2d\u5728\u81ea\u5df1\u8eab\u540e\u7ea6\u6709\u4e08\u8bb8\uff0c\u4e00\u63d0\u6c14\uff0c\u6b63\u8981\u51b2\u4e0a\uff0c\u962e\u58eb\u4e2d\u7a81\u7136\u4e00\u7eb5\u800c\u8d77\uff0c\u843d\u5728\u4ed6\u7684\u8eab\u65c1\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u90a3\u8fb9\u6709\u4eba\uff01\u201d\u4f38\u624b\u5411\u5cf0\u5de6\u6811\u4e1b\u4e2d\u4e00\u6307\u3002\u6bb7\u5409\u5fc3\u4e2d\u4e00\u5bd2\uff1a\u201c\u6b64\u4eba\u8f7b\u529f\uff0c\u679c\u7136\u5728\u6211\u4e4b\u4e0a\u3002\u201d\u89c1\u4ed6\u5f2f\u8170\u4f4e\u5934\uff0c\u8f7b\u8f7b\u5411\u6811\u4e1b\u4e2d\u8d70\u53bb\uff0c\u5f53\u4e0b\u8ddf\u968f\u5728\u540e\u3002 \u3000\u3000\u4e24\u4eba\u8d70\u5230\u6811\u540e\uff0c\u8eb2\u5728\u4e00\u5757\u51f8\u51fa\u7684\u5927\u77f3\u4e4b\u540e\uff0c\u63a2\u5934\u5411\u524d\u671b\u53bb\uff0c\u53ea\u89c1\u4e0b\u9762\u8c37\u4e2d\u5200\u5251\u95ea\u5149\uff0c\u6709\u4e94\u4e2a\u4eba\u805a\u5728\u8c37\u5e95\u3002\u4e09\u4eba\u624b\u6267\u5175\u5203\uff0c\u5206\u522b\u5b88\u4f4f\u4e09\u6761\u901a\u8def\uff0c\u81ea\u662f\u6015\u4eba\u95ef\u8fdb\uff0c\u53e6\u5916\u4e24\u4eba\u4e00\u6325\u94a2\u9504\uff0c\u4e00\u821e\u94c1\u94f2\uff0c\u6b63\u5728\u4e00\u682a\u5927\u6811\u4e0b\u7528\u529b\u6316\u6398\u3002\u663e\u662f\u4e24\u4eba\u5fc3\u77e5\u5f3a\u654c\u8ffd\u968f\u5728\u540e\uff0c\u65f6\u673a\u8feb\u4fc3\uff0c\u662f\u4ee5\u56db\u53ea\u624b\u81c2\u4e00\u523b\u4e0d\u505c\uff0c\u6b64\u8d77\u5f7c\u843d\uff0c\u5fd9\u788c\u5f02\u5e38\u3002 \u3000\u3000\u6bb7\u5409\u4f4e\u58f0\u9053\uff1a\u201c\u679c\u7136\u662f\u996e\u9a6c\u5ddd\u7684\u9676\u6c0f\u7236\u5b50\u3002\u90a3\u4e09\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u962e\u58eb\u4e2d\u8f7b\u58f0\u9053\uff1a\u201c\u996e\u9a6c\u5ddd\u7684\u4e09\u4e2a\u5be8\u4e3b\uff0c\u90fd\u662f\u786c\u624b\u3002\u201d\u6bb7\u5409\u9053\uff1a\u201c\u6b63\u5408\u9002\uff0c\u4e94\u4e2a\u5bf9\u4e94\u4e2a\u3002\u201d \u3000\u3000\u962e\u58eb\u4e2d\u9053\uff1a\u201c\u6bb7\u5e08\u5144\uff0c\u4f60\u6211\u540c\u4e91\u5947\u4e09\u4eba\u81ea\u7136\u4e0d\u6015\uff0c\u4e91\u9633\u548c\u9752\u6587\u5374\u5f31\u4e86\u3002\u5148\u51fa\u5176\u4e0d\u610f\u7684\u5bb0\u4ed6\u4e00\u4e24\u4e2a\uff0c\u4f59\u4e0b\u7684\u5c31\u597d\u529e\u3002\u201d\u6bb7\u5409\u76b1\u7709\u9053\uff1a\u201c\u82e5\u662f\u6c5f\u6e56\u4e0a\u4f20\u626c\u51fa\u53bb\uff0c\u8bf4\u6211\u5929\u9f99\u95e8\u6697\u65bd\u5077\u88ad\uff0c\u5c82\u4e0d\u6559\u5929\u4e0b\u82f1\u96c4\u803b\u7b11\uff1f\u201d\u962e\u58eb\u4e2d\u51b7\u51b7\u7684\u9053\uff1a\u201c\u4e3a\u7530\u5e08\u5144\u62a5\u4ec7\uff0c\u65a9\u8349\u9664\u6839\uff0c\u4e00\u4e2a\u4e5f\u4e0d\u7559\u4e0b\u3002\u54b1\u4eec\u81ea\u5df1\u4e0d\u8bf4\uff0c\u6ca1\u4eba\u77e5\u9053\u3002\u201d\u6bb7\u5409\u9053\uff1a\u201c\u9676\u6c0f\u7236\u5b50\u5f53\u771f\u8fd9\u4e48\u96be\u5bf9\u4ed8\u4e48\uff1f\u201d \u3000\u3000\u962e\u58eb\u4e2d\u70b9\u70b9\u5934\uff0c\u9694\u4e86\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u5e73\u624b\u76f8\u6597\uff0c\u5c0f\u5f1f\u6ca1\u5fc5\u80dc\u628a\u63e1\u3002\u201d\u6bb7\u5409\u77e5\u9053\u5317\u5b97\u81ea\u638c\u95e8\u4eba\u7530\u5f52\u519c\u53bb\u4e16\u540e\uff0c\u962e\u58eb\u4e2d\u5df2\u662f\u95e8\u4e2d\u7b2c\u4e00\u9ad8\u624b\uff0c\u542c\u8bf4\u7530\u5f52\u519c\u5728\u65e5\uff0c\u4e5f\u81ea\u5fcc\u60ee\u4ed6\u4e09\u5206\uff0c\u9002\u624d\u4e0a\u5c71\u8f83\u52b2\uff0c\u4ed6\u4f3c\u4e4e\u6709\u5fc3\u76f8\u8ba9\uff0c\u624d\u6210\u4e86\u4e2a\u4e0d\u80dc\u4e0d\u8d25\u4e4b\u5c40\uff0c\u82e5\u51fa\u5168\u529b\uff0c\u53ea\u6015\u81ea\u5df1\u8981\u8f93\uff0c\u4e8e\u662f\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u5c0f\u5f1f\u662f\u5ba2\uff0c\u81ea\u5f53\u7531\u962e\u5e08\u5144\u4e3b\u6301\u5927\u5c40\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u5fc3\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u8981\u505a\u82f1\u96c4\uff0c\u7531\u6211\u505a\u5c0f\u4eba\u5c31\u662f\u3002\u201d\u5f53\u4e0b\u4e0d\u518d\u8bf4\u8bdd\u3002\u8fd9\u65f6\u66f9\u4e91\u5947\u5df2\u7ecf\u8d76\u5230\uff0c\u518d\u8fc7\u4e00\u4f1a\uff0c\u5468\u4e91\u9633\u3001\u7530\u9752\u6587\u4e8c\u4eba\u4e5f\u5148\u540e\u6765\u4e86\u3002\u962e\u58eb\u4e2d\u4f4e\u58f0\u9053\uff1a\u201c\u6bb7\u5e08\u5144\u3001\u4e91\u5947\u548c\u6211\u5404\u53d1\u6bd2\u9525\uff0c\u5e72\u4e86\u628a\u98ce\u7684\u4e09\u4eba\uff0c\u518d\u56f4\u653b\u9676\u6c0f\u7236\u5b50\u3002\u4e91\u9633\u4e0e\u9752\u6587\u5f85\u6211\u4eec\u51fa\u624b\u4e4b\u540e\uff0c\u518d\u884c\u4e0a\u524d\u3002\u201d\u56db\u4eba\u542c\u4e86\uff0c\u5f53\u5373\u653e\u8f7b\u811a\u6b65\uff0c\u5f2f\u8170\u4ece\u5c71\u77f3\u540e\u6162\u6162\u63a9\u8fd1\u3002 \u3000\u3000\u7530\u9752\u6587\u8ddf\u5728\u962e\u58eb\u4e2d\u8eab\u540e\uff0c\u4f4e\u58f0\u53eb\u9053\uff1a\u201c\u962e\u5e08\u53d4\uff01\u201d\u962e\u58eb\u4e2d\u505c\u6b65\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u7530\u9752\u6587\u9053\uff1a\u201c\u9676\u6c0f\u7236\u5b50\u8981\u6349\u6d3b\u7684\u3002\u201d\u962e\u58eb\u4e2d\u53cc\u773c\u4e00\u7ffb\uff0c\u9732\u51fa\u4e00\u5bf9\u767d\u775b\uff0c\u4f4e\u6c89\u7740\u55d3\u5b50\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u56de\u62a4\u9676\u5b50\u5b89\u90a3\u5c0f\u8d3c\uff1f\u201d\u7530\u9752\u6587\u9053\uff1a\u201c\u6211\u603b\u89c9\u5f97\u4e0d\u662f\u4ed6\u3002\u201d\u962e\u58eb\u4e2d\u8138\u8272\u94c1\u9752\uff0c\u5c06\u63d2\u5728\u8170\u5e26\u4e0a\u7684\u90a3\u652f\u7fbd\u7bad\u62d4\u4e86\u51fa\u6765\uff0c\u9012\u5728\u5979\u624b\u91cc\uff0c\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u6bd4\u4e00\u6bd4\u53bb\uff01\u8fd9\u662f\u90a3\u5c0f\u8d3c\u9002\u624d\u5c04\u96c1\u7684\u7bad\u3002\u201d \u3000\u3000\u7530\u9752\u6587\u63a5\u8fc7\u7fbd\u7bad\uff0c\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u4e0d\u7531\u5f97\u4e24\u624b\u53d1\u98a4\u3002\u66f9\u4e91\u5947\u5728\u5979\u8eab\u65c1\uff0c\u4e00\u76f4\u77a7\u5979\u7684\u65f6\u5019\u591a\uff0c\u671b\u654c\u4eba\u7684\u65f6\u5019\u5c11\uff0c\u89c1\u4e86\u5979\u8fd9\u526f\u795e\u60c5\uff0c\u4e0d\u7981\u53c8\u559c\u53c8\u6012\uff0c\u559c\u7684\u662f\u773c\u89c1\u9676\u5b50\u5b89\u6027\u547d\u96be\u4fdd\uff0c\u6012\u7684\u662f\u5979\u5bf9\u90a3\u5c0f\u8d3c\u663e\u7136\u60c5\u610f\u751a\u6df1\u3002\u4ed6\u813e\u6c14\u66b4\u8e81\uff0c\u8d8a\u60f3\u8d8a\u607c\uff0c\u6b63\u5f85\u51fa\u8a00\u8ba5\u523a\uff0c\u962e\u58eb\u4e2d\u5728\u4ed6\u80a9\u5934\u4e00\u62cd\uff0c\u5411\u7740\u5728\u4e1c\u9996\u628a\u5b88\u7684\u90a3\u4eba\u80cc\u5fc3\u4e00\u6307\u3002 \u3000\u3000\u8fd9\u65f6\u7530\u9752\u6587\u4e0e\u5468\u4e91\u9633\u5df2\u4f0f\u4e0b\u8eab\u5b50\uff0c\u505c\u6b65\u4e0d\u8fdb\u3002\u962e\u6bb7\u66f9\u4e09\u4eba\u5404\u81ea\u8ba4\u5b9a\u4e86\u4e00\u540d\u654c\u624b\uff0c\u6bcf\u4eba\u624b\u4e2d\u90fd\u6697\u6263\u4e09\u679a\u6bd2\u9525\uff0c\u6084\u6084\u8d70\u8fd1\u3002\u90a3\u6bd2\u9525\u662f\u5929\u9f99\u95e8\u4e16\u4ee3\u76f8\u4f20\u7684\u7edd\u6280\uff0c\u53d1\u51fa\u65f6\u65e2\u51c6\u4e14\u5feb\uff0c\u800c\u4e14\u6bd2\u6027\u731b\u70c8\uff0c\u88ab\u6253\u4e2d\u4e86\u4e09\u4e2a\u65f6\u8fb0\u6bd9\u547d\uff0c\u5389\u5bb3\u65e0\u6bd4\uff0c\u6c5f\u6e56\u4e0a\u9001\u5b83\u4e00\u4e2a\u540d\u53f7\uff0c\u53eb\u4f5c\u201c\u8ffd\u547d\u6bd2\u9f99\u9525\u3002\u201d \u3000\u3000\u66f9\u4e91\u5947\u5fc3\u60f3\uff1a\u201c\u5e08\u53d4\u8981\u6211\u6253\u4e1c\u9996\u90a3\u4eba\uff0c\u6211\u5374\u8981\u7528\u6bd2\u9525\u5148\u9001\u4e86\u9676\u5b50\u5b89\u90a3\u5c0f\u8d3c\u7684\u6027\u547d\uff0c\u65e2\u62a5\u5e08\u95e8\u6df1\u4ec7\uff0c\u53c8\u62d4\u4e86\u773c\u4e2d\u4e4b\u9489\u3002\u82e5\u662f\u5f85\u4f1a\u5c06\u4ed6\u6d3b\u6349\uff0c\u591c\u957f\u68a6\u591a\uff0c\u4e0d\u77e5\u5e08\u59b9\u53c8\u4f1a\u751f\u51fa\u4ec0\u4e48\u53e4\u602a\u6765\u3002\u201d \u3000\u3000\u7b97\u8ba1\u5df2\u5b9a\uff0c\u8d8a\u8d70\u8d8a\u8fd1\uff0c\u773c\u89c1\u79bb\u654c\u4eba\u5df2\u4e0d\u8db3\u4e94\u5341\u6b65\uff0c\u5f53\u4e0b\u4f0f\u4f4e\u8eab\u5b50\uff0c\u51dd\u671b\u7740\u9676\u5b50\u5b89\u4e00\u8d77\u4e00\u4f0f\u7684\u80cc\u5f71\uff0c\u53ea\u5f85\u962e\u58eb\u4e2d\u6325\u624b\u53d1\u53f7\uff0c\u4e09\u9525\u7acb\u65f6\u6fc0\u5c04\u800c\u51fa\u3002 \u3000\u3000\u94ee\u7684\u4e00\u58f0\uff0c\u9676\u5b50\u5b89\u624b\u4e2d\u7684\u94a2\u9504\u649e\u5230\u4e86\u571f\u4e2d\u4e00\u4ef6\u94c1\u5668\u3002\u962e\u58eb\u4e2d\u9ad8\u4e3e\u5de6\u624b\uff0c\u6b63\u8981\u4e0b\u843d\uff0c\u731b\u542c\u5f97\u55e4\u55e4\u55e4\u6570\u58f0\u8fde\u54cd\uff0c\u65c1\u8fb9\u96ea\u5730\u91cc\u5ffd\u7136\u5c04\u51fa\u4e03\u516b\u4ef6\u6697\u5668\uff0c\u5206\u5411\u9676\u5b50\u5b89\u7b49\u4e94\u4eba\u6253\u53bb\u3002 \u3000\u3000\u8fd9\u4e9b\u6697\u5668\u7a81\u5982\u5176\u6765\u7684\u4ece\u5730\u5e95\u4e0b\u94bb\u51fa\uff0c\u4e8b\u5148\u6ca1\u534a\u5206\u6715\u5146\uff0c\u771f\u662f\u532a\u5937\u6240\u601d\uff0c\u53e4\u602a\u4e4b\u6781\u3002\u9676\u6c0f\u7236\u5b50\u6b66\u529f\u4e86\u5f97\uff0c\u6697\u5668\u867d\u8fd1\u8eab\u800c\u53d1\uff0c\u6765\u5f97\u5947\u7279\u65e0\u6bd4\uff0c\u4f46\u4ed7\u7740\u773c\u660e\u624b\u5feb\uff0c\u8fd8\u662f\u5404\u4e3e\u9504\u94f2\u6253\u843d\u3002\u671b\u98ce\u7684\u4e09\u4eba\u4e2d\u4e00\u4eba\u4ef0\u5929\u4e00\u6454\uff0c\u6eda\u5165\u5c71\u6c9f\u4e4b\u4e2d\uff0c\u4e24\u679a\u8896\u7bad\u5206\u4ece\u5934\u9888\u9876\u8fb9\u64e6\u8fc7\uff0c\u4fa5\u5e78\u9003\u5f97\u6027\u547d\u3002\u5176\u4f59\u4e24\u4eba\u5374\u54fc\u4e5f\u6ca1\u54fc\u4e00\u58f0\uff0c\u4e00\u679a\u94a2\u9556\u3001\u4e00\u67c4\u98de\u5200\u90fd\u6b63\u4e2d\u540e\u5fc3\uff0c\u6251\u5728\u96ea\u5730\u91cc\u518d\u4e0d\u52a8\u5f39\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u53d8\u8d77\u4ed3\u5352\uff0c\u9676\u6c0f\u7236\u5b50\u56fa\u7136\u5927\u51fa\u610f\u6599\u4e4b\u5916\uff0c\u962e\u58eb\u4e2d\u7b49\u4e5f\u662f\u60ca\u6115\u4e0d\u5df2\u3002 \u3000\u3000\u9676\u5b50\u5b89\u7684\u7236\u4eb2\u201c\u9547\u5173\u4e1c\u201d\u9676\u767e\u5c81\u9a82\u9053\uff1a\u201c\u9f20\u8f88\uff0c\u6562\u65bd\u6697\u7b97\uff01\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u5b9b\u82e5\u51ed\u7a7a\u8d77\u4e86\u4e2a\u54cd\u96f7\uff0c\u5a01\u731b\u65e0\u6bd4\u3002\u53ea\u89c1\u8eab\u4fa7\u96ea\u5730\u4e2d\u5200\u5149\u95ea\u52a8\uff0c\u4ece\u5730\u5e95\u4e0b\u8dc3\u51fa\u56db\u4eba\u3002 \u3000\u3000\u539f\u6765\u8fd9\u56db\u4eba\u65e9\u77e5\u9676\u6c0f\u7236\u5b50\u8981\u5230\u6b64\u5904\uff0c\u5728\u96ea\u4e0b\u6316\u4e86\u571f\u5751\uff0c\u5df2\u7b49\u5019\u6570\u65e5\u3002\u56db\u4eba\u5b88\u5728\u5751\u4e2d\uff0c\u5751\u4e0a\u7528\u6811\u679d\u76d6\u4e86\uff0c\u767d\u96ea\u906e\u4f4f\uff0c\u53ea\u9732\u51fa\u4e86\u51e0\u4e2a\u5c0f\u5b54\u900f\u6c14\uff0c\u65c1\u4eba\u54ea\u91cc\u77e5\u6653\uff1f \u3000\u3000\u9676\u6c0f\u7236\u5b50\u629b\u4e0b\u9504\u94f2\uff0c\u6025\u4ece\u8eab\u8fb9\u53d6\u51fa\u5175\u5203\u3002\u9676\u767e\u5c81\u4f7f\u7684\u662f\u4e00\u6839\u5341\u516d\u65a4\u91cd\u7684\u94a2\u97ad\uff0c\u9676\u5b50\u5b89\u5219\u7528\u5355\u5200\u3002\u90a3\u6eda\u5728\u5c71\u6c9f\u91cc\u7684\u9a6c\u5be8\u4e3b\u6015\u654c\u4eba\u8ddf\u7740\u88ad\u51fb\uff0c\u5728\u5c71\u6c9f\u4e2d\u8fde\u6eda\u6570\u6eda\uff0c\u8fd9\u624d\u8dc3\u8d77\uff0c\u4ed6\u624b\u4e2d\u672c\u6765\u62ff\u7740\u4e00\u5bf9\u94fe\u5b50\u9524\u3002 \u3000\u3000\u770b\u654c\u4eba\u65f6\uff0c\u89c1\u5f53\u5148\u4e00\u4eba\u8eab\u5f62\u7626\u524a\uff0c\u6f06\u9ed1\u4e00\u56e2\uff0c\u8ba4\u5f97\u662f\u5317\u4eac\u5e73\u901a\u9556\u5c40\u7684\u603b\u9556\u5934\u718a\u5143\u732e\uff0c\u6b64\u4eba\u7cbe\u719f\u5730\u5802\u5200\u529f\u592b\u3002\u996e\u9a6c\u5ddd\u5c71\u5be8\u66fe\u52ab\u8fc7\u4ed6\u9556\u5c40\u7684\u4e00\u652f\u5927\u9556\uff0c\u718a\u5143\u732e\u4f7f\u5c3d\u5fc3\u673a\uff0c\u59cb\u7ec8\u6ca1\u80fd\u8981\u56de\uff0c\u662f\u4ee5\u53cc\u65b9\u7ed3\u4e0b\u6881\u5b50\u3002\u53e6\u4e00\u4e2a\u5973\u5b50\uff0c\u7ea6\u83ab\u4e09\u5341\u4e8c\u4e09\u5c81\u5e74\u7eaa\uff0c\u9a6c\u5be8\u4e3b\u8bc6\u5f97\u5979\u662f\u53cc\u5200\u90d1\u4e09\u5a18\u3002\u5979\u4e08\u592b\u672c\u662f\u5e73\u901a\u9556\u5c40\u7684\u9556\u5934\uff0c\u5728\u996e\u9a6c\u5ddd\u4f17\u5be8\u4e3b\u52ab\u9556\u65f6\u5200\u4f24\u6b92\u547d\u3002\u6b64\u5916\u662f\u4e00\u4e2a\u80d6\u5927\u548c\u5c1a\uff0c\u624b\u4f7f\u6212\u5200\uff1b\u4e00\u4e2a\u7d2b\u819b\u8138\u6c49\u5b50\uff0c\u4f7f\u4e00\u5bf9\u94c1\u62d0\uff0c\u5747\u4e0d\u76f8\u8bc6\u3002\u60f3\u6765\u90fd\u662f\u5e73\u901a\u9556\u5c40\u9080\u6765\u7684\u597d\u624b\uff0c\u57cb\u4f0f\u5728\u8fd9\u91cc\u4ee5\u62a5\u6614\u65e5\u4e4b\u4ec7\u4e86\u3002 \u3000\u3000\u9676\u767e\u5c81\u559d\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff1f\u539f\u6765\u662f\u8001\u592b\u624b\u4e0b\u8d25\u5c06\u3002\u9664\u4e86\u59d3\u718a\u7684\u9f20\u8f88\uff0c\u6b66\u6797\u4e4b\u4e2d\uff0c\u539f\u4e5f\u6ca1\u4eba\u80fd\u505a\u8fd9\u4e0b\u8d31\u52fe\u5f53\u3002\u201d\u8fd9\u8bdd\u867d\u662f\u65a5\u9a82\u718a\u5143\u732e\uff0c\u4f46\u6bb7\u5409\u542c\u4e86\uff0c\u4e0d\u7981\u8138\u4e0a\u4e00\u70ed\uff0c\u659c\u773c\u770b\u962e\u58eb\u4e2d\u65f6\uff0c\u53ea\u89c1\u4ed6\u53cc\u76ee\u51dd\u89c6\u8c37\u4e2d\u654c\u5bf9\u53cc\u65b9\uff0c\u5bf9\u8fd9\u53e5\u8bdd\u76f4\u5982\u4e0d\u95fb\u3002 \u3000\u3000\u718a\u5143\u732e\u7ec6\u58f0\u7ec6\u6c14\u7684\u9053\uff1a\u201c\u9676\u5be8\u4e3b\uff0c\u5728\u4e0b\u8ddf\u4f60\u5f15\u89c1\u5f15\u89c1\u3002\u8fd9\u4f4d\u662f\u5c71\u4e1c\u767e\u4f1a\u5bfa\u7684\u9759\u667a\u5927\u5e08\u3002\u8fd9\u4f4d\u662f\u4eac\u4e2d\u4e00\u7b49\u4f8d\u536b\u5218\u5143\u9e64\u5218\u5927\u4eba\uff0c\u662f\u5728\u4e0b\u7684\u540c\u95e8\u5e08\u5144\u3002\u4f60\u4eec\u591a\u4eb2\u8fd1\u4eb2\u8fd1\u3002\u201d\u9676\u767e\u5c81\u8eab\u6750\u9b41\u4f1f\uff0c\u58f0\u82e5\u96f7\u9707\uff0c\u718a\u5143\u732e\u6070\u4e0e\u4ed6\u76f8\u53cd\uff0c\u4e00\u4e2a\u9633\u521a\uff0c\u4e00\u4e2a\u9634\u67d4\uff0c\u4e24\u4eba\u5012\u4f3c\u5929\u751f\u4e86\u7684\u5bf9\u5934\u3002 \u3000\u3000\u9676\u767e\u5c81\u9a82\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4e00\u9f50\u4e0a\u5427\uff0c\u54b1\u4eec\u5175\u5203\u4e0a\u4eb2\u8fd1\u4eb2\u8fd1\u3002\u201d \u3000\u3000\u94a2\u97ad\u5728\u7a7a\u4e2d\u865a\u51fb\u4e00\u97ad\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u8db3\u89c1\u8182\u529b\u60ca\u4eba\u3002\u718a\u5143\u732e\u4e0d\u52a8\u58f0\u8272\uff0c\u4f4e\u4f4e\u7684\u9053\uff1a\u201c\u5728\u4e0b\u662f\u9676\u5be8\u4e3b\u624b\u4e0b\u8d25\u5c06\uff0c\u4e0d\u6562\u8ddf\u4f60\u52a8\u624b\uff0c\u53ea\u6c42\u89c1\u8d50\u4e00\u7269\u3002\u201d\u9676\u767e\u5c81\u6012\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u718a\u5143\u732e\u5411\u4ed6\u4eec\u6316\u6398\u7684\u571f\u5751\u4e00\u6307\uff0c\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u91cc\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u9676\u767e\u5c81\u4e00\u634b\u6ee1\u816e\u7070\u767d\u80e1\u5b50\uff0c\u66f4\u4e0d\u6253\u8bdd\uff0c\u5288\u9762\u5c31\u662f\u4e00\u97ad\u3002\u718a\u5143\u732e\u95ea\u8eab\u907f\u8fc7\uff0c\u53eb\u9053\uff1a\u201c\u4e14\u6162\u52a8\u624b\u3002\u201d\u9676\u767e\u5c81\u559d\u9053\uff1a\u201c\u53c8\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d\u718a\u5143\u732e\u9053\uff1a\u201c\u5728\u4e0b\u5df2\u5728\u6b64\u5904\u76f8\u5019\u4e09\u65e5\u4e09\u591c\uff0c\u4e13\u7b49\u9676\u5be8\u4e3b\u5230\u6765\u3002\u82e5\u662f\u4e0d\u77a7\u5c0a\u9a7e\u7236\u5b50\u91d1\u9762\uff0c\u6b64\u7269\u65e9\u5c31\u53d6\u4e86\u3002\u8fd9\u91cc\u7684\u4e1c\u897f\u672c\u6765\u4e0d\u662f\u996e\u9a6c\u5ddd\u4e4b\u7269\uff0c\u4e00\u5411\u7531\u5929\u9f99\u95e8\u7ecf\u7ba1\uff0c\u73b0\u4e0b\u6362\u6362\u4e3b\u513f\uff0c\u4ea6\u65e0\u4e0d\u8be5\u3002\u201d\u9676\u5b50\u5b89\u9053\uff1a\u201c\u718a\u9556\u5934\u8bf4\u5f97\u597d\u6f02\u4eae\u7684\u8bdd\u513f\u3002\u8fd9\u96ea\u5c71\u4e0a\u5343\u91cc\u51b0\u5c01\uff0c\u4f60\u4eec\u82e5\u662f\u65e9\u77e5\u57cb\u85cf\u4e4b\u5904\uff0c\u8fd8\u4e0d\u65e9\u5c31\u53d6\u4e86\u53bb\uff1f\u201d \u3000\u3000\u90a3\u90d1\u4e09\u5a18\u4e00\u5fc3\u8981\u62a5\u6740\u592b\u4e4b\u4ec7\uff0c\u53eb\u9053\uff1a\u201c\u591a\u8bf4\u4ec0\u4e48\uff1f\u52a8\u624b\u5427\uff01\u201d \u3000\u3000\u8bdd\u58f0\u672a\u6bd5\uff0c\u4e09\u67c4\u98de\u5200\u5237\u5237\u5237\u63a5\u8fde\u5411\u9a6c\u5be8\u4e3b\u5c04\u53bb\u3002\u9a6c\u5be8\u4e3b\u94fe\u5b50\u53cc\u9524\u98de\u8d77\uff0c\u5c06\u4e24\u67c4\u98de\u5200\u6253\u843d\uff0c\u773c\u89c1\u7b2c\u4e09\u67c4\u6765\u5f97\u66f4\u662f\u52b2\u6025\uff0c\u76f4\u53d6\u80f8\u53e3\uff0c\u5f53\u4e0b\u53cc\u624b\u4e00\u5d29\uff0c\u53cc\u9524\u4e4b\u95f4\u7684\u94c1\u94fe\u6a2a\u5728\u5f53\u80f8\uff0c\u6b63\u597d\u5c06\u98de\u5200\u6321\u843d\uff0c\u5de6\u9524\u4e00\u7f29\uff0c\u53f3\u9524\u5df2\u6251\u9762\u6253\u51fa\u3002\u90d1\u4e09\u5a18\u8eab\u5f62\u7075\u52a8\uff0c\u77ee\u8eab\u4f4e\u5934\uff0c\u53cc\u5200\u4e00\u62db\u201c\u65cb\u98ce\u52bf\u201d\uff0c\u76f4\u6251\u8fdb\u6000\u3002\u9a6c\u5be8\u4e3b\u5de6\u9524\u98de\u51fa\uff0c\u6d88\u53bb\u4e86\u8fd9\u62db\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u52a8\u4e0a\u624b\uff0c\u90a3\u548c\u5c1a\u6325\u6212\u5200\u76f4\u53d6\u9676\u767e\u5c81\u3002\u9547\u5173\u4e1c\u4e0d\u907f\u53cd\u8fce\uff0c\u94c1\u97ad\u6a2a\u6253\uff0c\u5200\u97ad\u76f8\u4ea4\uff0c\u8ff8\u51fa\u661f\u661f\u706b\u82b1\u3002\u548c\u5c1a\u53ea\u89c9\u624b\u81c2\u9178\u9ebb\uff0c\u5200\u950b\u5df2\u7ed9\u6253\u51fa\u4e00\u4e2a\u7f3a\u53e3\u3002\u9676\u5b50\u5b89\u821e\u5200\u5954\u5411\u718a\u5143\u732e\u3002\u516d\u4eba\u5206\u4f5c\u4e09\u5bf9\uff0c\u5728\u96ea\u5730\u91cc\u6027\u547d\u76f8\u6251\u3002\u5218\u5143\u9e64\u624b\u6267\u53cc\u62d0\uff0c\u5728\u65c1\u63a0\u9635\uff0c\u773c\u89c1\u90a3\u548c\u5c1a\u4e0d\u662f\u9676\u767e\u5c81\u5bf9\u624b\uff0c\u53eb\u9053\uff1a\u201c\u5927\u5e08\u9000\u4e0b\uff0c\u8ba9\u6211\u6765\u4f1a\u4f1a\u9547\u5173\u4e1c\u3002\u201d\u90a3\u548c\u5c1a\u5140\u81ea\u604b\u6218\u3002\u5218\u5143\u9e64\u8de8\u4e0a\u4e00\u6b65\uff0c\u53f3\u8180\u5728\u9759\u667a\u548c\u5c1a\u80a9\u5934\u4e00\u649e\u3002\u90a3\u548c\u5c1a\u7acb\u8db3\u4e0d\u4f4f\uff0c\u8dcc\u51fa\u4e09\u6b65\uff0c\u5ffd\u89c9\u91d1\u5203\u5288\u98ce\uff0c\u4e00\u5200\u5411\u8111\u95e8\u5288\u6765\uff0c\u6025\u5fd9\u7f29\u5934\u8eb2\u95ea\uff0c\u539f\u6765\u662f\u9676\u5b50\u5b89\u62bd\u7a7a\u780d\u4e86\u4ed6\u4e00\u5200\u3002\u9759\u667a\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u60ca\u6012\u4e4b\u4e0b\uff0c\u633a\u5200\u4e0e\u718a\u5143\u732e\u53cc\u6597\u9676\u5b50\u5b89\u3002 \u3000\u3000\u5218\u5143\u9e64\u6b66\u529f\u6bd4\u5e08\u5f1f\u5f3a\u5f97\u591a\uff0c\u9676\u767e\u5c81\u94c1\u97ad\u6a2a\u626b\uff0c\u4ed6\u7adf\u786c\u63a5\u786c\u67b6\uff0c\u94c1\u62d0\u4e00\u7acb\uff0c\u94c1\u97ad\u78b0\u94c1\u62d0\uff0c\u5f53\u7684\u4e00\u58f0\u5927\u54cd\u3002\u5218\u5143\u9e64\u4e0d\u52a8\u58f0\u8272\uff0c\u53f3\u62d0\u4e00\u6c89\uff0c\u62d0\u5934\u9501\u4f4f\u654c\u4eba\u97ad\u8eab\uff0c\u5de6\u62d0\u6402\u5934\u76d6\u4e86\u4e0b\u6765\u3002\u9676\u767e\u5c81\u4e0e\u4ed6\u6570\u62db\u4e00\u8fc7\uff0c\u5df2\u77e5\u4eca\u65e5\u9047\u5230\u52b2\u654c\uff0c\u5f53\u4e0b\u6296\u64de\u7cbe\u795e\uff0c\u4f7f\u5f00\u516d\u5408\u97ad\u6cd5\uff0c\u5355\u97ad\u6597\u53cc\u62d0\uff0c\u731b\u7838\u72e0\u6253\u3002 \u3000\u3000\u65f6\u5019\u4e00\u957f\uff0c\u5218\u5143\u9e64\u6e10\u5360\u4e0a\u98ce\uff0c\u9676\u767e\u5c81\u5df2\u662f\u62db\u67b6\u591a\uff0c\u8fd8\u624b\u5c11\u3002\u9676\u5b50\u5b89\u4ee5\u4e00\u654c\u4e8c\uff0c\u66f4\u662f\u5f62\u8feb\u52bf\u8e59\uff0c\u5fc3\u60f3\u773c\u524d\u552f\u4e00\u6307\u671b\uff0c\u662f\u9a6c\u5be8\u4e3b\u901f\u4e0b\u6740\u624b\u51fb\u6bd9\u90d1\u4e09\u5a18\uff0c\u5c06\u718a\u5143\u732e\u63a5\u8fc7\uff0c\u81ea\u5df1\u5c31\u80fd\u4fdf\u673a\u6740\u4e86\u548c\u5c1a\u3002\u4f46\u90d1\u4e09\u5a18\u4e5f\u5df2\u77a7\u660e\u767d\u6218\u5c40\u5927\u52bf\uff0c\u53ea\u8981\u81ea\u5df1\u5c3d\u529b\u652f\u6491\uff0c\u9676\u6c0f\u7236\u5b50\u4e0d\u514d\u5148\u540e\u9001\u547d\uff0c\u5f53\u4e0b\u53ea\u5b88\u4e0d\u653b\uff0c\u53cc\u5200\u5b88\u5f97\u4e25\u5bc6\u5f02\u5e38\uff0c\u9a6c\u5be8\u4e3b\u53cc\u9524\u867d\u5982\u72c2\u98ce\u66b4\u96e8\u822c\u8fde\u73af\u8fdb\u653b\uff0c\u5374\u59cb\u7ec8\u4f24\u5979\u4e0d\u5f97\u3002\u518d\u62c6\u6570\u5341\u62db\uff0c\u90d1\u4e09\u5a18\u7a76\u662f\u5973\u6d41\uff0c\u6108\u6765\u6108\u662f\u529b\u6c14\u4e0d\u52a0\uff0c\u4e0d\u4f4f\u5411\u540e\u9000\u907f\u3002\u9a6c\u5be8\u4e3b\u8e0f\u6b65\u4e0a\u524d\u8ffd\u51fb\uff0c\u7a81\u89c1\u90d1\u4e09\u5a18\u5de6\u5200\u4e00\u6643\uff0c\u9732\u51fa\u8001\u5927\u4e00\u4e2a\u7a7a\u95e8\uff0c\u4e0d\u7981\u5927\u559c\uff0c\u62a2\u4e0a\u4e00\u6b65\uff0c\u6325\u9524\u51fb\u4e0b\uff0c\u84e6\u5730\u91cc\u53f3\u8db3\u8db3\u5e95\u7a81\u7136\u4e00\u865a\uff0c\u7adf\u5df2\u8e0f\u5728\u718a\u5143\u732e\u7b49\u5148\u524d\u85cf\u8eab\u7684\u571f\u5751\u4e4b\u4e2d\u3002 \u3000\u3000\u8fd9\u5751\u5927\u534a\u4ecd\u88ab\u767d\u96ea\u6df9\u6ca1\uff0c\u6fc0\u6597\u4e4b\u9645\uff0c\u672a\u52a0\u7559\u795e\uff0c\u90d1\u4e09\u5a18\u6709\u610f\u5f15\u4ed6\u8fc7\u53bb\u3002\u4ed6\u8fd9\u4e00\u8db3\u8e0f\u7a7a\uff0c\u8eab\u5b50\u5411\u524d\u4e00\u8dcc\uff0c\u6697\u53eb\u4e0d\u597d\uff0c\u5f85\u8981\u8dc3\u8d77\uff0c\u90d1\u4e09\u5a18\u4e00\u5200\u75be\u780d\uff0c\u767b\u65f6\u5c06\u4ed6\u5de6\u80a9\u5378\u843d\u3002 \u3000\u3000\u9a6c\u5be8\u4e3b\u60e8\u53eb\u4e00\u58f0\uff0c\u6655\u4e86\u8fc7\u53bb\uff0c\u90d1\u4e09\u5a18\u53f3\u624b\u8865\u4e0a\u4e00\u5200\uff0c\u5c06\u4ed6\u780d\u6b7b\u5728\u5751\u4e2d\u3002\u9676\u5b50\u5b89\u542c\u5230\u9a6c\u5be8\u4e3b\u53eb\u58f0\uff0c\u60c5\u77e5\u4e0d\u5999\uff0c\u4f46\u88ab\u718a\u5143\u732e\u4e0e\u9759\u667a\u4e24\u4eba\u7f20\u4f4f\u4e86\uff0c\u81ea\u987e\u5c1a\u4e14\u4e0d\u6687\uff0c\u54ea\u80fd\u5206\u624b\u6551\u4eba\uff1f\u90d1\u4e09\u5a18\u5598\u4e86\u51e0\u53e3\u6c14\uff0c\u7406\u4e00\u7406\u9b13\u53d1\uff0c\u53d6\u51fa\u4e00\u5757\u767d\u5e03\u624b\u5e15\u5305\u5728\u5934\u4e0a\uff0c\u821e\u52a8\u53cc\u5200\u4e0a\u524d\u5939\u51fb\u9676\u767e\u5c81\u3002 \u3000\u3000\u90a3\u9676\u767e\u5c81\u82e5\u662f\u5e74\u8f7b\u4e0a\u4e8c\u5341\u5c81\uff0c\u5218\u5143\u9e64\u539f\u4e0d\u662f\u4ed6\u7684\u654c\u624b\u3002\u4ed6\u5411\u4ee5\u529b\u5927\u62db\u731b\u89c1\u957f\uff0c\u73b0\u4e0b\u5e74\u7eaa\u4e00\u8001\uff0c\u7cbe\u529b\u7a76\u5df2\u8870\u9000\uff0c\u4e0e\u5218\u5143\u9e64\u5355\u6253\u72ec\u6597\u5df2\u76f8\u5f62\u89c1\u7ecc\uff0c\u518d\u52a0\u4e0a\u4e00\u4e2a\u90d1\u4e09\u5a18\u5728\u65c1\u5077\u88ad\u9a9a\u6270\uff0c\u66f4\u662f\u9669\u8c61\u73af\u751f\u3002 \u3000\u3000\u6597\u5230\u9163\u5904\uff0c\u5218\u5143\u9e64\u53eb\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d\u4e00\u62db\u201c\u9f99\u7fd4\u51e4\u821e\u201d\uff0c\u53cc\u62d0\u9f50\u81f3\u3002\u9676\u767e\u5c81\u6325\u97ad\u6321\u4f4f\uff0c\u5374\u89c1\u90d1\u4e09\u5a18\u53cc\u5200\u5708\u8f6c\uff0c\u4e5f\u662f\u4e24\u6837\u5175\u5203\u540c\u65f6\u653b\u5230\u3002\u9676\u767e\u5c81\u4e00\u6761\u97ad\u67b6\u4e0d\u5f00\u56db\u822c\u5175\u5203\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u98de\u5de6\u811a\u5c06\u90d1\u4e09\u5a18\u8e22\u4e86\u4e2a\u7b4b\u6597\uff0c\u4f46\u5de6\u80c1\u4e0a\u7ec8\u4e8e\u88ab\u5979\u5200\u950b\u5212\u4e86\u4e00\u4e2a\u5927\u53e3\u5b50\u3002\u7247\u523b\u4e4b\u95f4\uff0c\u4f24\u53e3\u6d41\u51fa\u7684\u9c9c\u8840\u5c06\u96ea\u5730\u67d3\u5f97\u6bb7\u7ea2\u4e00\u7247\u3002\u4f46\u8fd9\u8001\u513f\u52c7\u608d\u5f02\u5e38\uff0c\u821e\u97ad\u9163\u6218\uff0c\u6beb\u4e0d\u793a\u602f\u3002 \u3000\u3000\u9676\u5b50\u5b89\u773c\u89c1\u60c5\u52bf\u9669\u6076\uff0c\u5fc3\u77e5\u4eca\u65e5\u6709\u8d25\u65e0\u80dc\uff0c\u5f53\u4e0b\u75be\u653b\u4e09\u5200\uff0c\u4e58\u9759\u667a\u9000\u5f00\u4e24\u6b65\uff0c\u968f\u5373\u5411\u540e\u4e00\u8dc3\uff0c\u53eb\u9053\uff1a\u201c\u7f62\u5566\uff0c\u6211\u7236\u5b50\u8ba4\u8f93\u5c31\u662f\u3002\u4f60\u4eec\u8981\u5b9d\u8fd8\u662f\u8981\u547d\uff1f\u201d\u90d1\u4e09\u5a18\u6325\u5200\u5411\u9676\u767e\u5c81\u8fdb\u653b\uff0c\u53eb\u9053\uff1a\u201c\u5b9d\u4e5f\u8981\uff0c\u547d\u4e5f\u8981\u3002\u201d\u718a\u5143\u732e\u5fc3\u91cc\u5374\u53e6\u6709\u8ba1\u8f83\uff0c\u4ed6\u53bb\u5e74\u5931\u4e86\u4e00\u652f\u5927\u9556\uff0c\u8d54\u5f97\u503e\u5bb6\u8361\u4ea7\uff0c\u5fc3\u60f3\u4e0e\u5176\u6740\u4ed6\u7236\u5b50\uff0c\u4e0d\u5982\u53eb\u996e\u9a6c\u5ddd\u732e\u51fa\u91d1\u94f6\u8d4e\u547d\uff0c\u4e8e\u662f\u53eb\u9053\uff1a\u201c\u5927\u5bb6\u4e14\u4f4f\uff0c\u6211\u6709\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u5218\u5143\u9e64\u4e3a\u4eba\u7cbe\u7ec6\uff0c\u90d1\u4e09\u5a18\u4e00\u5411\u542c\u603b\u9556\u5934\u7684\u5429\u5490\uff0c\u542c\u4ed6\u5982\u6b64\u8bf4\uff0c\u5404\u81ea\u5411\u65c1\u8dc3\u5f00\u3002\u90a3\u9759\u667a\u5374\u662f\u4e2a\u83bd\u548c\u5c1a\uff0c\u6597\u5f97\u5174\u53d1\uff0c\u54ea\u91cc\u8fd8\u80af\u7f62\u624b\uff0c\u4e00\u67c4\u6212\u5200\u4f7f\u5f97\u5982\u98ce\u8f66\u76f8\u4f3c\uff0c\u76f4\u5411\u9676\u5b50\u5b89\u8feb\u5c06\u8fc7\u53bb\u3002\u718a\u5143\u732e\u8fde\u53eb\uff1a\u201c\u9759\u667a\u5927\u5e08\uff0c\u9759\u667a\u5927\u5e08\u3002\u201d\u9759\u667a\u5b9b\u5982\u672a\u95fb\u3002\u9676\u5b50\u5b89\u4e00\u58f0\u51b7\u7b11\uff0c\u5c06\u5355\u5200\u5f80\u5730\u4e0b\u4e00\u629b\uff0c\u633a\u80f8\u9053\uff1a\u201c\u4f60\u6562\u6740\u6211\uff1f\u201d \u3000\u3000\u9759\u667a\u4e3e\u8d77\u6212\u5200\uff0c\u6b63\u8981\u4e00\u5200\u780d\u4e0b\uff0c\u7a81\u7136\u89c1\u4ed6\u5982\u6b64\uff0c\u4e0d\u7981\u4e00\u5446\uff0c\u6212\u5200\u4e3e\u5728\u534a\u7a7a\uff0c\u5374\u4e0d\u843d\u4e0b\u3002\u9676\u5b50\u5b89\u9a82\u9053\uff1a\u201c\u8d3c\u79c3\uff01\u201d\u8fce\u9762\u4e00\u62f3\uff0c\u6b63\u4e2d\u9f3b\u6881\u3002\u9759\u667a\u51fa\u5176\u4e0d\u610f\uff0c\u8eab\u5b50\u4e00\u6643\uff0c\u4e00\u4ea4\u5750\u5728\u5730\u4e0b\uff0c\u4e00\u6478\u81ea\u5df1\u9f3b\u5b50\uff0c\u6ee1\u624b\u90fd\u662f\u9f3b\u8840\u3002\u8fd9\u4e00\u6765\u53eb\u4ed6\u5982\u4f55\u4e0d\u6012\uff0c\u4e00\u58f0\u543c\u53eb\uff0c\u722c\u8d77\u8eab\u6765\uff0c\u5411\u9676\u5b50\u5b89\u731b\u6251\u8fc7\u53bb\u3002\u718a\u5143\u732e\u4f38\u81c2\u62c9\u4f4f\uff0c\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u53ea\u89c1\u9676\u5b50\u5b89\u8dc3\u5165\u5751\u4e2d\uff0c\u6325\u52a8\u94a2\u9504\u6398\u4e86\u51e0\u4e0b\uff0c\u968f\u5373\u629b\u5f00\u9504\u5934\uff0c\u6367\u7740\u4e00\u53ea\u4e24\u5c3a\u6765\u957f\u7684\u957f\u65b9\u94c1\u76d2\u7eb5\u8eab\u800c\u4e0a\u3002\u5218\u5143\u9e64\u7b49\u9762\u4e0a\u5404\u73b0\u559c\u8272\uff0c\u5411\u9676\u5b50\u5b89\u8d70\u8fd1\u51e0\u6b65\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u4f4e\u58f0\u5411\u6bb7\u5409\u9053\uff1a\u201c\u6bb7\u5e08\u5144\uff0c\u4f60\u4e0e\u4e91\u5947\u53d1\u9525\u4f24\u4eba\uff0c\u6211\u53bb\u62a2\u5b9d\u3002\u201d\u6bb7\u5409\u4f4e\u58f0\u9053\uff1a\u201c\u4f24\u54ea\u4e00\u8fb9\u7684\u4eba\uff1f\u201d\u962e\u58eb\u4e2d\u5de6\u624b\u4e2d\u95f4\u4e09\u6307\u5377\u66f2\uff0c\u4f38\u51fa\u62c7\u6307\u4e0e\u5c0f\u6307\uff0c\u505a\u4e2a\u201c\u516d\u201d\u5b57\u7684\u624b\u52bf\u3002\u610f\u601d\u8bf4\u516d\u4e2a\u4eba\u5168\u4f24\u3002\u6bb7\u5409\u5fc3\u9053\uff1a\u201c\u597d\u72e0\u6bd2\uff01\u201d\u70b9\u4e86\u70b9\u5934\uff0c\u6263\u7d27\u624b\u4e2d\u7684\u6bd2\u9525\uff0c\u659c\u773c\u770b\u66f9\u4e91\u5947\u65f6\uff0c\u53ea\u89c1\u4ed6\u53cc\u773c\u76ef\u7740\u9676\u5b50\u5b89\uff0c\u770b\u6765\u8fd9\u4e9b\u65f6\u5019\u4e4b\u4e2d\uff0c\u4ed6\u773c\u5149\u59cb\u7ec8\u672a\u6709\u4e00\u77ac\u79bb\u5f00\u8fc7\u6b64\u4eba\u3002 \u3000\u3000\u9676\u5b50\u5b89\u6367\u7740\u94c1\u76d2\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u6211\u7236\u5b50\u4e2d\u4e86\u8be1\u8ba1\uff0c\u8fd9\u6b66\u6797\u81f3\u5b9d\u4e48\uff0c\u563f\u563f\uff0c\u81ea\u5f53\u53cc\u624b\u5949\u4e0a\u3002\u53ea\u662f\u5728\u4e0b\u6709\u4e00\u4e8b\u4e0d\u660e\uff0c\u5012\u8981\u9886\u6559\u3002\u201d\u718a\u5143\u732e\u772f\u7740\u4e00\u53cc\u5c0f\u773c\uff0c\u9053\uff1a\u201c\u5c11\u5be8\u4e3b\u6709\u4f55\u5429\u5490\uff1f\u201d\u9676\u5b50\u5b89\u9053\uff1a\u201c\u4f60\u4eec\u600e\u77e5\u8fd9\u94c1\u76d2\u57cb\u5728\u6b64\u5904\uff1f\u53c8\u600e\u77e5\u6211\u4eec\u8fd9\u51e0\u65e5\u8981\u6765\u6316\u53d6\uff1f\u201d\u718a\u5143\u732e\u9053\uff1a\u201c\u5c11\u5be8\u4e3b\u65e2\u60f3\u77e5\u9053\uff0c\u8ddf\u4f60\u8bf4\u4e86\uff0c\u4e5f\u662f\u4e0d\u59a8\u3002 \u3000\u3000\u5929\u9f99\u95e8\u7530\u8001\u638c\u95e8\u5c01\u5251\u4e4b\u65e5\uff0c\u5927\u5bb4\u5bbe\u670b\u3002\u5c11\u5be8\u4e3b\u662f\u7530\u95e8\u5feb\u5a7f\uff0c\u90a3\u4e00\u5b9a\u662f\u5230\u7684\u4e86\u3002\u201d\u9676\u5b50\u5b89\u70b9\u4e86\u70b9\u5934\u3002\u718a\u5143\u732e\u6307\u7740\u5218\u5143\u9e64\u9053\uff1a\u201c\u6211\u8fd9\u4f4d\u5e08\u5144\u5f53\u65e5\u4e5f\u662f\u5ea7\u4e0a\u5bbe\u5ba2\uff0c\u53ea\u662f\u5c11\u5be8\u4e3b\u82f1\u96c4\u5e74\u5c11\uff0c\u6ca1\u628a\u5218\u5e08\u5144\u653e\u5728\u773c\u91cc\u3002\u201d\u9676\u5b50\u5b89\u51b7\u7b11\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u6211\u5cb3\u4e08\u5bb4\u8bf7\u597d\u670b\u53cb\uff0c\u539f\u6765\u8bf7\u5230\u4e86\u5978\u7ec6\u3002\u201d \u3000\u3000\u718a\u5143\u732e\u5e76\u4e0d\u52a8\u6012\uff0c\u4ecd\u662f\u7ec6\u58f0\u7ec6\u6c14\u7684\u9053\uff1a\u201c\u8a00\u91cd\u4e86\u3002\u5218\u5e08\u5144\u4e45\u4ef0\u5c0a\u9a7e\u82f1\u540d\uff0c\u4e0d\u514d\u5bf9\u5c11\u5be8\u4e3b\u591a\u770b\u4e86\u51e0\u773c\uff0c\u90a3\u4e5f\u662f\u996e\u9a6c\u5ddd\u5a01\u540d\u8fdc\u64ad\u4e4b\u6545\u554a\u3002\u90a3\u65e5\u5be8\u4e3b\u4e00\u4e3e\u4e00\u52a8\uff0c\u6ca1\u66fe\u79bb\u4e86\u5218\u5e08\u5144\u7684\u773c\u775b\u3002\u201d \u3000\u3000\u9676\u5b50\u5b89\u9053\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781\uff01\u8fd9\u76d2\u513f\u8be5\u5f53\u732e\u7ed9\u5218\u5927\u4eba\u7684\u4e86\u3002\u201d\u53cc\u624b\u524d\u4f38\uff0c\u5c06\u94c1\u76d2\u9012\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5218\u5143\u9e64\u7709\u4e0d\u626c\uff0c\u8089\u4e0d\u52a8\uff0c\u4f38\u624b\u53bb\u63a5\u3002\u9676\u5b50\u5b89\u7a81\u7136\u5728\u94c1\u76d2\u8fb9\u4e0a\u4e00\u6380\uff0c\u55d6\u55d6\u55d6\u4e09\u58f0\uff0c\u4e09\u652f\u77ed\u7bad\u4ece\u94c1\u76d2\u4e2d\u75be\u98de\u800c\u51fa\uff0c\u5411\u5218\u5143\u9e64\u5f53\u80f8\u5c04\u53bb\u3002\u4e24\u4eba\u76f8\u8ddd\u4e0d\u5230\u4e09\u5c3a\uff0c\u6025\u5207\u95f4\u54ea\u80fd\u95ea\u907f\uff1f \u3000\u3000\u597d\u4e2a\u5218\u5143\u9e64\uff0c\u8eab\u624b\u679c\u771f\u4e0d\u51e1\uff0c\u5371\u6025\u4e2d\u987a\u624b\u62c9\u4f4f\u9759\u667a\u5728\u8eab\u524d\u4e00\u6321\u3002\u53ea\u542c\u4e00\u58f0\u60e8\u547c\uff0c\u4e24\u652f\u77ed\u7bad\u4e00\u9f50\u9489\u5165\u90a3\u548c\u5c1a\u7684\u54bd\u5589\uff0c\u7acb\u65f6\u6c14\u7edd\u3002\u7b2c\u4e09\u652f\u7bad\u504f\u5728\u4e00\u65c1\uff0c\u5374\u5c04\u5165\u4e86\u718a\u5143\u732e\u5de6\u80a9\uff0c\u76f4\u6ca1\u81f3\u7fbd\uff0c\u53d7\u4f24\u4e5f\u81ea\u4e0d\u8f7b\u3002 \u3000\u3000\u8fd9\u4e2a\u53d8\u6545\uff0c\u6bd4\u9002\u624d\u718a\u5143\u732e\u7b49\u5077\u88ad\u6765\u5f97\u66f4\u662f\u5947\u7279\u3002\u7530\u9752\u6587\u5fcd\u4e0d\u4f4f\u201c\u554a\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\u3002\u5218\u5143\u9e64\u4e00\u542c\u80cc\u540e\u6709\u4eba\uff0c\u987e\u4e0d\u5f97\u4e0e\u9676\u6c0f\u7236\u5b50\u52a8\u624b\uff0c\u8dc3\u5411\u5c71\u77f3\uff0c\u5148\u62a4\u4f4f\u80cc\u5fc3\uff0c\u8fd9\u624d\u8f6c\u8eab\u5bdf\u770b\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u53eb\u9053\uff1a\u201c\u52a8\u624b\uff01\u201d\u7eb5\u8eab\u6251\u4e86\u4e0b\u53bb\u3002\u66f9\u4e91\u5947\u624b\u4e00\u626c\uff0c\u4e09\u679a\u6bd2\u9525\u5bf9\u51c6\u9676\u5b50\u5b89\u5c04\u51fa\u3002\u7530\u9752\u6587\u65e9\u77e5\u4ed6\u5fc3\u610f\uff0c\u4e00\u89c1\u4ed6\u626c\u624b\u53d1\u9525\uff0c\u7acb\u5373\u633a\u80a9\u5f80\u4ed6\u5de6\u80a9\u649e\u53bb\u3002\u66f9\u4e91\u5947\u8eab\u5b50\u4e00\u4fa7\uff0c\u6012\u559d\uff1a\u201c\u5e72\u4ec0\u4e48\uff1f\u201d\u4e09\u9525\u51c6\u5934\u5168\u504f\uff0c\u90fd\u843d\u5165\u96ea\u5730\u4e4b\u4e2d\u3002 \u3000\u3000\u6bb7\u5409\u7684\u6bd2\u9525\u672c\u5f85\u5c04\u5411\u5218\u5143\u9e64\uff0c\u53ea\u662f\u7530\u9752\u6587\u4e00\u51fa\u58f0\uff0c\u88ab\u4ed6\u7acb\u65f6\u77e5\u89c9\uff0c\u6b64\u4eba\u5e94\u53d8\u6781\u5feb\uff0c\u7adf\u7136\u65e0\u673a\u53ef\u4e58\u3002\u962e\u58eb\u4e2d\u5927\u53eb\uff1a\u201c\u7269\u5f52\u539f\u4e3b\u3002\u201d\u5de6\u624b\u4e94\u6307\u5982\u94a9\uff0c\u6293\u5411\u9676\u5b50\u5b89\u53cc\u76ee\uff0c\u53f3\u624b\u4e94\u6307\u5df2\u6293\u4f4f\u94c1\u76d2\u8fb9\u7f18\u3002 \u3000\u3000\u5218\u5143\u9e64\u94c1\u62d0\u4e00\u7acb\uff0c\u4e0e\u6bb7\u5409\u7684\u957f\u5251\u642d\u4e0a\u4e86\u624b\u3002\u4e24\u4eba\u5728\u7530\u5f52\u519c\u7684\u7b75\u5e2d\u4e2d\u66fe\u4f1a\u8fc7\u9762\uff0c\u90fd\u77e5\u5bf9\u65b9\u662f\u6b66\u5b66\u540d\u5bb6\uff0c\u6b64\u523b\u6570\u62db\u4e00\u8fc7\uff0c\u5fc3\u4e2d\u5404\u81ea\u4f69\u670d\u3002 \u3000\u3000\u5468\u4e91\u9633\u633a\u5251\u5954\u5411\u718a\u5143\u732e\u3002\u7530\u9752\u6587\u7684\u5355\u5251\u4e0e\u90d1\u4e09\u5a18\u53cc\u5200\u6218\u5728\u4e00\u8d77\u3002\u66f9\u4e91\u5947\u957f\u5251\u95ea\u52a8\uff0c\u4e0d\u53bb\u6597\u95f2\u5728\u4e00\u65c1\u7684\u9676\u767e\u5c81\uff0c\u5374\u5411\u9676\u5b50\u5b89\u80f8\u53e3\u523a\u53bb\uff0c\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u8eab\u968f\u5251\u81f3\uff0c\u7adf\u662f\u62da\u547d\u7684\u6253\u6cd5\uff0c\u51f6\u72e0\u5f02\u5e38\u3002 \u3000\u3000\u9676\u5b50\u5b89\u6ca1\u6301\u5175\u5203\uff0c\u53ea\u5f97\u653e\u624b\u677e\u5f00\u94c1\u76d2\uff0c\u540e\u8dc3\u907f\u5f00\uff0c\u4fef\u8eab\u62a2\u8d77\u5355\u5200\uff0c\u53cd\u8eab\u6765\u593a\u3002\u962e\u58eb\u4e2d\u5de6\u624b\u62b1\u4f4f\u76d2\u5b50\uff0c\u9634\u6c89\u7740\u8138\u9a82\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u653e\u6697\u7bad\u5bb3\u6b7b\u5cb3\u4e08\uff0c\u539f\u6765\u662f\u770b\u4e2d\u4e86\u6211\u5929\u9f99\u95e8\u7684\u81f3\u5b9d\u3002\u201d \u3000\u3000\u9676\u5b50\u5b89\u53eb\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u5bb3\u4e86\u5cb3\u7236\uff1f\u201d\u6325\u5200\u731b\u653b\uff0c\u6025\u7740\u8981\u593a\u56de\u94c1\u76d2\u3002 \u3000\u3000\u4f46\u8fd9\u94c1\u76d2\u4e00\u5165\u4e03\u661f\u624b\u962e\u58eb\u4e2d\u4e4b\u624b\uff0c\u83ab\u8bf4\u66f9\u4e91\u5947\u5728\u65c1\u4ed7\u5251\u76f8\u52a9\uff0c\u5c31\u662f\u5355\u51ed\u962e\u58eb\u4e2d\u4e00\u53cc\u8089\u638c\uff0c\u9676\u5b50\u5b89\u4e5f\u4f11\u60f3\u593a\u5f97\u56de\u53bb\u3002\u9676\u767e\u5c81\u53eb\u9053\uff1a\u201c\u59d3\u962e\u7684\uff0c\u8fd9\u94c1\u76d2\u662f\u7530\u4eb2\u5bb6\u4eb2\u624b\u4ea4\u4e0e\u6211\u513f\uff0c\u4f60\u662f\u4e0d\u670d\uff0c\u8fd8\u662f\u600e\u5730\uff1f\u201d\u5927\u58f0\u53eb\u56b7\uff0c\u6325\u97ad\u5411\u962e\u58eb\u4e2d\u5934\u9876\u51fb\u843d\u3002\u962e\u58eb\u4e2d\u4e00\u8dc3\u4e08\u4f59\uff0c\u7eb5\u5230\u7530\u9752\u6587\u7684\u8eab\u65c1\uff0c\u4e3e\u76d2\u5411\u90d1\u4e09\u5a18\u8fce\u9762\u4e00\u626c\u3002\u90d1\u4e09\u5a18\u9002\u624d\u89c1\u76d2\u4e2d\u653e\u51fa\u6697\u5668\uff0c\u53ea\u6015\u53c8\u6709\u77ed\u7bad\u5c04\u51fa\uff0c\u5fd9\u77ee\u8eab\u95ea\u907f\u3002 \u3000\u3000\u54ea\u77e5\u962e\u58eb\u4e2d\u53ea\u662f\u865a\u5f20\u58f0\u52bf\uff0c\u5f85\u7530\u9752\u6587\u6446\u8131\u7ea0\u7f20\uff0c\u5f53\u5373\u5c06\u94c1\u76d2\u4ea4\u5728\u5979\u624b\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u62a4\u4f4f\u76d2\u513f\uff0c\u8ba9\u6211\u5bf9\u4ed8\u654c\u4eba\u3002\u201d \u3000\u3000\u4ed6\u624b\u4e2d\u4e00\u7a7a\uff0c\u7acb\u5373\u8fd4\u8eab\u6765\u6597\u9676\u767e\u5c81\u3002\u8fd9\u5929\u9f99\u5317\u5b97\u7b2c\u4e00\u9ad8\u624b\u679c\u7136\u6b66\u529f\u4e86\u5f97\uff0c\u9676\u767e\u5c81\u867d\u7136\u97ad\u6c89\u529b\u731b\uff0c\u5374\u88ab\u4ed6\u4e00\u53cc\u7a7a\u624b\u8feb\u5f97\u8fde\u8fde\u5012\u9000\u3002\u718a\u5143\u732e\u80a9\u5934\u4e2d\u7bad\uff0c\u88ab\u5468\u4e91\u9633\u4e00\u67c4\u957f\u5251\u8feb\u4f4f\u4e86\uff0c\u59cb\u7ec8\u7f13\u4e0d\u51fa\u624b\u6765\u53bb\u62d4\u7bad\uff0c\u90a3\u7bad\u7559\u5728\u8089\u91cc\uff0c\u4e00\u7528\u52b2\u534a\u8fb9\u8eab\u5b50\u5267\u75db\u96be\u5f53\u3002\u53ea\u6709\u5218\u5143\u9e64\u5374\u4e0e\u6bb7\u5409\u6597\u4e86\u4e2a\u65d7\u9f13\u76f8\u5f53\u3002 \u3000\u3000\u7530\u9752\u6587\u62b1\u4f4f\u94c1\u76d2\uff0c\u65bd\u5f00\u8f7b\u529f\uff0c\u75be\u5411\u897f\u5317\u65b9\u5954\u53bb\u3002\u9676\u5b50\u5b89\u4e3e\u5200\u5411\u66f9\u4e91\u5947\u731b\u5288\uff0c\u89c1\u4ed6\u63d0\u5251\u5c01\u95e8\uff0c\u8fd9\u4e00\u5200\u7adf\u4e0d\u5288\u4e0b\uff0c\u5ffd\u5730\u8f6c\u8eab\uff0c\u5411\u7530\u9752\u6587\u8ffd\u53bb\u3002 \u3000\u3000\u66f9\u4e91\u5947\u5927\u6012\uff0c\u968f\u540e\u6025\u8d76\uff0c\u53ea\u8ffd\u51fa\u6570\u6b65\uff0c\u659c\u523a\u91cc\u53cc\u5200\u780d\u5230\uff0c\u539f\u6765\u662f\u90d1\u4e09\u5a18\u4ece\u65c1\u622a\u4f4f\u3002\u66f9\u4e91\u5947\u5fc3\u4e2d\u7126\u8e81\uff0c\u8fde\u8fdb\u9669\u62db\u3002\u54ea\u77e5\u90d1\u4e09\u5a18\u7684\u6b66\u827a\u867d\u4e0d\u751a\u7cbe\uff0c\u5374\u7ec3\u5c31\u4e86\u4e00\u5957\u4e13\u95e8\u5b88\u5fa1\u7684\u5200\u6cd5\uff0c\u53ea\u8981\u8fd9\u5957\u201c\u94c1\u95e8\u95e9\u201d\u5200\u6cd5\u4f7f\u5f00\u4e86\uff0c\u516d\u516d\u4e09\u5341\u516d\u62db\u4e4b\u5185\uff0c\u5bf9\u65b9\u529f\u592b\u518d\u9ad8\uff0c\u4e5f\u662f\u4e0d\u6613\u53d6\u80dc\u3002\u66f9\u4e91\u5947\u8fde\u53d8\u4e09\u8def\u5251\u6cd5\uff0c\u4e00\u65f6\u7adf\u5948\u4f55\u5979\u4e0d\u5f97\u3002 \u3000\u3000\u7530\u9752\u6587\u5954\u51fa\u91cc\u8bb8\uff0c\u89c1\u9676\u5b50\u5b89\u968f\u540e\u8ddf\u6765\uff0c\u6b63\u5408\u5fc3\u610f\uff0c\u8f6c\u8fc7\u4e00\u4e2a\u5c71\u5761\uff0c\u7ad9\u5b9a\u8eab\u5b50\uff0c\u4f3c\u55d4\u4f3c\u7b11\u7684\u9053\uff1a\u201c\u4f60\u8ffd\u6211\u5e72\u4e48\uff1f\u201d\u9676\u5b50\u5b89\u9053\uff1a\u201c\u59b9\u5b50\uff0c\u54b1\u4eec\u5408\u529b\u5bf9\u4ed8\u4e86\u90a3\u51e0\u4e2a\u5978\u8d3c\uff0c\u81ea\u5df1\u7684\u4e8b\u603b\u597d\u5546\u91cf\u3002\u201d\u7530\u9752\u6587\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u59b9\u5b50\uff1f\u4f60\u5e72\u4e48\u5bb3\u6211\u7239\u7239\uff1f\u201d\u9676\u5b50\u5b89\u7a81\u7136\u5728\u96ea\u5730\u91cc\u53cc\u819d\u8dea\u5012\uff0c\u6307\u5929\u7acb\u8a93\uff0c\u5927\u58f0\u9053\uff1a\u201c\u7687\u5929\u5728\u4e0a\uff0c\u82e5\u662f\u6211\u9676\u5b50\u5b89\u5bb3\u4e86\u5929\u9f99\u95e8\u7530\u8001\u638c\u95e8\uff0c\u53eb\u6211\u65e5\u540e\u4e07\u7bad\u6512\u8eab\uff0c\u4e71\u5200\u5206\u5c38\uff01\u201d \u3000\u3000\u7530\u9752\u6587\u8138\u4e0a\u9732\u51fa\u7b11\u5bb9\uff0c\u4f38\u624b\u62c9\u7740\u4ed6\u81c2\u8180\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4e0d\u662f\u4f60\u5c31\u597d\u5566\u3002\u6211\u4e5f\u65e9\u77e5\u4e0d\u662f\u4f60\uff0c\u4ed6\u4eec\u2026\u2026\u4ed6\u4eec\u2026\u2026\u201d\u9676\u5b50\u5b89\u8dc3\u8d77\u8eab\u6765\uff0c\u63e1\u4f4f\u5979\u5de6\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u59b9\u5b50\u2026\u2026\u201d\u521a\u53eb\u5f97\u4e00\u58f0\uff0c\u5ffd\u89c1\u7530\u9752\u6587\u8138\u4e0a\u53d8\u8272\uff0c\u77e5\u9053\u80cc\u540e\u6765\u4e86\u4eba\uff0c\u6025\u5fd9\u8f6c\u8eab\uff0c\u53ea\u542c\u4e00\u4eba\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\uff0c\u5728\u8fd9\u91cc\u9b3c\u9b3c\u795f\u795f\u7684\u5e72\u4ec0\u4e48\uff1f\u201d\u7530\u9752\u6587\u6012\u9053\uff1a\u201c\u4ec0\u4e48\u9b3c\u9b3c\u795f\u795f\uff1f\u4f60\u7ed9\u6211\u53e3\u91cc\u653e\u5e72\u51c0\u4e9b\u3002\u201d \u3000\u3000\u9676\u5b50\u5b89\u4e00\u56de\u5934\uff0c\u89c1\u662f\u66f9\u4e91\u5947\u8d76\u5230\uff0c\u53eb\u9053\uff1a\u201c\u66f9\u5e08\u5144\uff0c\u4f60\u83ab\u8bef\u4f1a\u3002\u201d\u66f9\u4e91\u5947\u5706\u7741\u53cc\u76ee\uff0c\u559d\u9053\uff1a\u201c\u8bef\u4f1a\u4f60\u5988\u4e2a\u5c41\uff01\u201d\u63d0\u5251\u5206\u5fc3\u4fbf\u523a\uff0c\u9676\u5b50\u5b89\u53ea\u5f97\u4e3e\u5200\u62db\u67b6\u3002 \u3000\u3000\u4e24\u4eba\u6597\u4e86\u6570\u5408\uff0c\u96ea\u5730\u91cc\u811a\u6b65\u58f0\u54cd\uff0c\u90d1\u4e09\u5a18\u5982\u98ce\u5954\u6765\u3002\u66f9\u4e91\u5947\u9a82\u9053\uff1a\u201c\u81ed\u5a46\u5a18\uff0c\u7f20\u4e2a\u6ca1\u5b8c\u6ca1\u4e86\u3002\u201d\u53cd\u624b\u5c31\u662f\u4e00\u5251\u3002\u90d1\u4e09\u5a18\u5de6\u5200\u6321\u67b6\uff0c\u53f3\u624b\u56de\u4e86\u4e00\u5200\u3002\u9676\u5b50\u5b89\u53eb\u9053\uff1a\u201c\u90d1\u4e09\u5a18\uff0c\u54b1\u4fe9\u5e76\u80a9\u5b50\u4e0a\uff0c\u5148\u6740\u4e86\u8fd9\u86ee\u6c49\u518d\u8bf4\u3002\u201d \u3000\u3000\u4ed6\u4e00\u8bed\u752b\u6bd5\uff0c\u4e00\u62db\u201c\u62bd\u6881\u6362\u67f1\u201d\uff0c\u5de6\u624b\u865a\u6258\uff0c\u5200\u950b\u4ece\u6a2a\u91cc\u5411\u66f9\u4e91\u5947\u53cd\u5288\u8fc7\u53bb\u3002\u66f9\u4e91\u5947\u4ee5\u4e00\u654c\u4e8c\uff0c\u4e1d\u6beb\u4e0d\u60e7\u3002\u4ed6\u6709\u610f\u8981\u5728\u5fc3\u4e0a\u4eba\u4e4b\u524d\u5356\u5f04\u672c\u4e8b\uff0c\u5251\u8d70\u504f\u950b\uff0c\u53cd\u800c\u8fde\u8fde\u8fdb\u62db\u3002\u9676\u5b50\u5b89\u8d5e\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff01\u201d\u8eab\u5f62\u4e00\u77ee\uff0c\u4e00\u62db\u201c\u4e0a\u6b65\u64a9\u9634\u201d\u5411\u4ed6\u80ef\u4e0b\u6325\u53bb\u3002\u90d1\u4e09\u5a18\u5fc3\u60f3\u4ed6\u5b9a\u7136\u7ad6\u5251\u76f8\u67b6\uff0c\u4e0a\u76d8\u52bf\u5fc5\u7a7a\u865a\uff0c\u5f53\u5373\u53cc\u5200\u5411\u66f9\u4e91\u5947\u80a9\u5934\u780d\u843d\u3002\u4e0d\u6599\u9676\u5b50\u5b89\u8fd9\u4e00\u5200\u6325\u5230\u4e2d\u9014\uff0c\u7a81\u7136\u8f6c\u4e3a\u201c\u9000\u6b65\u65a9\u9a6c\u5200\u201d\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u4e00\u5200\u780d\u5728\u90d1\u4e09\u5a18\u817f\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u8eba\u4e0b\u3002\u201d \u3000\u3000\u8fd9\u4e00\u62db\u6bd2\u8fa3\u5f02\u5e38\uff0c\u6bd4\u90d1\u4e09\u5a18\u518d\u5f3a\u6570\u500d\u7684\u9ad8\u624b\uff0c\u4e5f\u662f\u96be\u4ee5\u9632\u5907\uff0c\u6559\u5979\u5982\u4f55\u95ea\u907f\u5f97\u4e86\uff1f\u5979\u817f\u4e0a\u5267\u75db\uff0c\u5411\u540e\u4fbf\u8dcc\u3002\u9676\u5b50\u5b89\u62a2\u4e0a\u4e00\u6b65\uff0c\u4e3e\u5200\u5f80\u5979\u9888\u4e2d\u780d\u4e0b\u3002\u547c\u7684\u4e00\u58f0\uff0c\u66f9\u4e91\u5947\u957f\u5251\u9012\u51fa\uff0c\u5c06\u4ed6\u5355\u5200\u67b6\u5f00\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8981\u4e0d\u8981\u8138\uff1f\u201d\u9676\u5b50\u5b89\u7b11\u9053\uff1a\u201c\u5175\u4e0d\u538c\u8bc8\uff0c\u6211\u662f\u6709\u5fc3\u52a9\u4f60\u3002\u201d \u3000\u3000\u66f9\u4e91\u5947\u6b63\u8981\u559d\u9a82\uff0c\u5218\u5143\u9e64\u3001\u6bb7\u5409\u3001\u9676\u767e\u5c81\u3001\u962e\u58eb\u4e2d\u7b49\u5df2\u5148\u540e\u8d76\u5230\u3002\u539f\u6765\u4ed6\u4eec\u90fd\u6302\u5ff5\u7740\u94c1\u76d2\uff0c\u773c\u89c1\u7530\u9752\u6587\u62b1\u7740\u76d2\u5b50\u5954\u5f00\uff0c\u4e0d\u613f\u65e0\u8c13\u604b\u6218\uff0c\u4e00\u5f85\u654c\u4eba\u653b\u52bf\u7565\u7f13\uff0c\u90fd\u62bd\u7a7a\u8ffd\u6765\u3002\u9676\u5b50\u5b89\u53eb\u9053\uff1a\u201c\u7239\uff0c\u5929\u9f99\u95e8\u662f\u597d\u670b\u53cb\u3002\u4f60\u522b\u8ddf\u962e\u5e08\u53d4\u52a8\u624b\u3002\u201d \u3000\u3000\u9676\u767e\u5c81\u5c1a\u672a\u7b54\u8bdd\uff0c\u66f9\u4e91\u5947\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u5bb3\u6b7b\u6211\u6069\u5e08\uff0c\u8c01\u8ddf\u4f60\u662f\u597d\u670b\u53cb\uff1f\u201d\u5237\u5237\u5237\uff0c\u5411\u4ed6\u75be\u523a\u4e09\u5251\u3002\u9676\u5b50\u5b89\u6321\u5f00\u4e24\u5251\uff0c\u7b2c\u4e09\u5251\u9669\u9669\u907f\u4e0d\u5f00\u53bb\uff0c\u8eab\u5b50\u5411\u5de6\u6025\u95ea\uff0c\u5251\u5203\u5728\u53f3\u988a\u8fb9\u8d34\u9762\u800c\u8fc7\uff0c\u53ea\u8981\u5dee\u5f97\u4e24\u5bf8\uff0c\u90a3\u4fbf\u662f\u7a7f\u5934\u7834\u8111\u4e4b\u7978\u3002\u4ed6\u5413\u5f97\u8138\u65e0\u8840\u8272\uff0c\u5ffd\u542c\u7530\u9752\u6587\u53eb\u58f0\uff1a\u201c\u5c0f\u5fc3\uff01\u201d\u4e00\u679a\u6697\u5668\u4ece\u8eab\u65c1\u98de\u4e86\u8fc7\u53bb\uff0c\u7d27\u63a5\u7740\u98ce\u58f0\u5fae\u54cd\uff0c\u540e\u81c0\u4e0a\u5df2\u5403\u4e86\u4e00\u5200\u3002 \u3000\u3000\u539f\u6765\u90d1\u4e09\u5a18\u53d7\u4f24\u540e\u5012\u5730\u4e0d\u8d77\uff0c\u5fc3\u4e2d\u53c8\u6068\u53c8\u6094\uff1a\u201c\u4ed6\u996e\u9a6c\u5ddd\u662f\u6211\u6740\u592b\u5927\u4ec7\uff0c\u8fd9\u5c0f\u8d3c\u53c8\u662f\u7d20\u6765\u8be1\u8ba1\u591a\u7aef\uff0c\u6211\u600e\u80fd\u4fe1\u4ed6\u7684\u8bdd\uff0c\u4e0d\u52a0\u63d0\u9632\uff1f\u201d\u5ffd\u89c1\u9676\u5b50\u5b89\u907f\u5251\u540e\u9000\uff0c\u6b63\u662f\u5077\u88ad\u826f\u673a\uff0c\u5f53\u5373\u594b\u8eab\u8dc3\u8d77\uff0c\u6325\u5200\u5f80\u4ed6\u5934\u9876\u780d\u53bb\u3002\u7530\u9752\u6587\u773c\u660e\u624b\u5feb\uff0c\u6025\u53d1\u4e00\u9525\uff0c\u62a2\u5148\u9489\u4e2d\u5979\u7684\u53f3\u80a9\u3002\u5e78\u5f97\u8fd9\u4e00\u9525\uff0c\u624d\u6551\u4e86\u9676\u5b50\u5b89\u7684\u6027\u547d\uff0c\u90d1\u4e09\u5a18\u90a3\u5200\u780d\u5f97\u4f4e\u4e86\uff0c\u53ea\u4e2d\u4e86\u4ed6\u7684\u540e\u81c0\u3002 \u3000\u3000\u90d1\u4e09\u5a18\u8eab\u4e2d\u6bd2\u9525\uff0c\u53c8\u5411\u540e\u8dcc\u3002\u9676\u5b50\u5b89\u9a82\u58f0\uff1a\u201c\u8d31\u4eba\uff01\u201d\u5355\u5200\u8131\u624b\uff0c\u5bf9\u51c6\u5979\u80f8\u53e3\u731b\u63b7\u4e0b\u53bb\uff0c\u8fd9\u4e00\u63b7\u52bf\u52b2\u529b\u75be\uff0c\u76f8\u8ddd\u53c8\u8fd1\uff0c\u773c\u89c1\u5f97\u4e00\u5200\u8981\u5c06\u5979\u9489\u5728\u5730\u4e0b\uff0c\u7a81\u7136\u7a7a\u4e2d\u55e4\u7684\u4e00\u58f0\u6025\u54cd\uff0c\u4e00\u679a\u6697\u5668\u4ece\u8fdc\u5904\u98de\u6765\u3002\u6b63\u597d\u6253\u5728\u5200\u4e0a\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u5355\u5200\u8361\u5f00\uff0c\u659c\u659c\u7684\u63d2\u5165\u90d1\u4e09\u5a18\u8eab\u65c1\u96ea\u5730\u4e4b\u4e2d\u3002 \u3000\u3000\u5218\u5143\u9e64\u3001\u962e\u58eb\u4e2d\u7b49\u5747\u6b63\u6ce8\u76ee\u94c1\u76d2\uff0c\u6216\u4e9f\u6b32\u52ab\u593a\u3001\u6216\u65e8\u5728\u5b88\u62a4\uff0c\u5ffd\u542c\u8fd9\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\u54cd\u5f97\u602a\u5f02\uff0c\u90fd\u662f\u4e00\u60ca\uff0c\u4f46\u89c1\u8fd9\u6697\u5668\u8fdc\u98de\u800c\u81f3\uff0c\u843d\u70b9\u65e2\u51c6\uff0c\u52b2\u529b\u53c8\u91cd\uff0c\u7adf\u5c06\u5355\u5200\u6253\u5728\u4e00\u65c1\u3002\u5404\u4eba\u4e00\u60ca\u4e4b\u4e0b\uff0c\u9f50\u5411\u6697\u5668\u6765\u8def\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u82b1\u767d\u80e1\u5b50\u7684\u8001\u50e7\u53f3\u624b\u62ff\u7740\u4e00\u4e32\u5ff5\u73e0\uff0c\u5ff5\u9053\uff1a\u201c\u5584\u54c9\uff0c\u5584\u54c9\uff01\u201d\u5feb\u6b65\u8d70\u6765\uff0c\u4fef\u8eab\u62fe\u8d77\u4e00\u7269\uff0c\u4e32\u5728\u5ff5\u73e0\u7ef3\u4e0a\uff0c\u539f\u6765\u4ed6\u9002\u624d\u6240\u53d1\u6697\u5668\u53ea\u662f\u4e00\u7c92\u5ff5\u73e0\u3002 \u3000\u3000\u8fd9\u4e32\u5ff5\u73e0\u770b\u6765\u4efd\u91cf\u4e0d\u8f7b\uff0c\u9ed1\u9edd\u9edd\u7684\u4f3c\u662f\u94c1\u94f8\uff0c\u4f46\u8fd9\u548c\u5c1a\u4ece\u6570\u4e08\u5916\u5f39\u6765\uff0c\u5c0f\u5c0f\u4e00\u7c92\u5ff5\u73e0\u7adf\u80fd\u649e\u5f00\u4e00\u628a\u516b\u4e5d\u65a4\u91cd\u7684\u94a2\u5200\uff0c\u6307\u529b\u5b9e\u662f\u975e\u540c\u5c0f\u53ef\u3002\u4f17\u4eba\u60ca\u6115\u4e4b\u4e0b\uff0c\u90fd\u773c\u7741\u7741\u7684\u671b\u7740\u4ed6\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u4e00\u5bf9\u4e09\u89d2\u773c\uff0c\u584c\u9f3b\u6b6a\u5634\uff0c\u4e00\u53cc\u767d\u7709\u659c\u659c\u4e0b\u5782\uff0c\u5bb9\u8c8c\u6781\u662f\u8be1\u5f02\uff0c\u53cc\u773c\u5e03\u6ee1\u7ea2\u4e1d\uff0c\u5355\u770b\u76f8\u8c8c\uff0c\u5012\u4f3c\u662f\u4e2a\u5e02\u4e95\u8001\u5149\u68cd\uff0c\u54ea\u60f3\u5f97\u5230\u6b66\u529f\u7adf\u662f\u5982\u6b64\u9ad8\u5f3a\u3002 \u3000\u3000\u90a3\u50e7\u4eba\u4f38\u624b\u6276\u8d77\u90d1\u4e09\u5a18\uff0c\u62d4\u4e0b\u5979\u80a9\u5934\u7684\u6bd2\u9525\uff0c\u53ea\u89c1\u4f24\u53e3\u4e2d\u55b7\u51fa\u9ed1\u8840\uff0c\u90d1\u4e09\u5a18\u5927\u58f0\u547b\u541f\u3002\u90a3\u50e7\u4eba\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u7c92\u7ea2\u8272\u836f\u4e38\uff0c\u585e\u5728\u5979\u7684\u53e3\u91cc\uff0c\u5411\u4f17\u4eba\u9010\u4e2a\u671b\u53bb\uff0c\u81ea\u8a00\u81ea\u8bed\u8bf4\u9053\uff1a\u201c\u8fd9\u836f\u4e38\u53ea\u53ef\u6682\u65f6\u6b62\u75db\u3002\u6bd2\u9f99\u9525\u662f\u5929\u9f99\u95e8\u72ec\u95e8\u6697\u5668\uff0c\u548c\u5c1a\u53ef\u6551\u5979\u4e0d\u5f97\u3002\u201d\u4ed6\u773c\u5149\u505c\u5728\u962e\u58eb\u4e2d\u8138\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u65bd\u4e3b\u662f\u5929\u9f99\u95e8\u9ad8\u624b\u4e86\uff1f\u4e0d\u770b\u50e7\u9762\u770b\u4f5b\u9762\uff0c\u6562\u8bf7\u6148\u60b2\u5219\u4e2a\u3002\u201d\u8bf4\u7740\u5408\u5341\u884c\u793c\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u548c\u90d1\u4e09\u5a18\u672c\u4e0d\u76f8\u8bc6\uff0c\u539f\u65e0\u4ec7\u6028\uff0c\u773c\u89c1\u90a3\u50e7\u4eba\u5982\u6b64\u672c\u9886\uff0c\u82e5\u662f\u4e0d\u5141\u62ff\u51fa\u89e3\u836f\uff0c\u4eca\u65e5\u51b3\u8ba8\u4e0d\u4e86\u597d\u53bb\uff0c\u4ed6\u662f\u4e2a\u4e45\u5386\u6c5f\u6e56\u4e4b\u4eba\uff0c\u5f53\u786c\u5219\u786c\uff0c\u5f53\u8f6f\u5219\u8f6f\uff0c\u773c\u89c1\u90a3\u50e7\u4eba\u5408\u5341\u8eac\u8eab\uff0c\u7acb\u5373\u8fd8\u793c\uff0c\u9053\uff1a\u201c\u5927\u5e08\u5429\u5490\uff0c\u81ea\u5f53\u9075\u547d\u3002\u201d\u4ece\u6000\u4e2d\u53d6\u51fa\u4e24\u4e2a\u5c0f\u74f6\uff0c\u5728\u4e00\u4e2a\u74f6\u91cc\u5012\u51fa\u5341\u7c92\u9ed1\u8272\u5c0f\u4e38\uff0c\u7ed9\u90d1\u4e09\u5a18\u670d\u4e86\uff0c\u5c06\u53e6\u4e00\u4e2a\u74f6\u5b50\u9012\u7ed9\u7530\u9752\u6587\u9053\uff1a\u201c\u7ed9\u5979\u6577\u4e0a\u3002\u201d\u7530\u9752\u6587\u63a5\u8fc7\u836f\u74f6\uff0c\u5c06\u94c1\u76d2\u4ea4\u7ed9\u5e08\u53d4\uff0c\u81ea\u53bb\u7ed9\u90d1\u4e09\u5a18\u6577\u836f\u3002 \u3000\u3000\u90a3\u50e7\u4eba\u9053\uff1a\u201c\u65bd\u4e3b\u6148\u60b2\u3002\u201d\u53c8\u6253\u4e86\u4e00\u8eac\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u95ee\u5404\u4f4d\u5728\u6b64\u4e92\u6597\uff0c\u5374\u662f\u4e3a\u4e86\u4f55\u4e8b\uff1f\u5929\u4e0b\u6ca1\u89e3\u4e0d\u5f00\u7684\u6881\u5b50\uff0c\u548c\u5c1a\u8001\u4e86\u8138\u76ae\uff0c\u5012\u60f3\u4f5c\u4e2a\u8c03\u4eba\uff0c\u563f\u563f\u3002\u201d \u3000\u3000\u4f17\u4eba\u76f8\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u6709\u7684\u6c89\u541f\u4e0d\u8bed\uff0c\u6709\u7684\u8138\u73b0\u6012\u5bb9\u3002\u66f9\u4e91\u5947\u6307\u7740\u9676\u5b50\u5b89\u9a82\u9053\uff1a\u201c\u8fd9\u5c0f\u8d3c\u5bb3\u6b7b\u6211\u5e08\u7236\uff0c\u5077\u4e86\u6211\u5929\u9f99\u95e8\u7684\u9547\u95e8\u4e4b\u5b9d\u3002\u5927\u5e08\uff0c\u4f60\u8bf4\u8be5\u4e0d\u8be5\u627e\u4ed6\u507f\u547d\uff1f\u201d\u8bf4\u7740\u624b\u4e2d\u957f\u5251\u865a\u5288\uff0c\u5251\u5203\u9707\u52a8\uff0c\u55e1\u55e1\u4f5c\u58f0\u3002 \u3000\u3000\u90a3\u8001\u50e7\u95ee\u9053\uff1a\u201c\u5c0a\u5e08\u662f\u54ea\u4e00\u4f4d\uff1f\u201d\u66f9\u4e91\u5947\u9053\uff1a\u201c\u5148\u5e08\u662f\u655d\u95e8\u5317\u5b97\u638c\u95e8\uff0c\u59d3\u7530\u3002\u201d\u90a3\u8001\u50e7\u201c\u554a\u54df\u201d\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u5f52\u519c\u53bb\u4e16\u4e86\uff0c\u53ef\u60dc\u554a\u53ef\u60dc\u3002\u201d\u8bed\u6c14\u4e4b\u4e2d\uff0c\u4f3c\u4e4e\u8bc6\u5f97\u7530\u5f52\u519c\uff0c\u800c\u53e3\u79f0\u201c\u5f52\u519c\u201d\uff0c\u7adf\u7136\u81ea\u5c45\u5c0a\u957f\u3002\u7530\u9752\u6587\u521a\u7ed9\u90d1\u4e09\u5a18\u6577\u5b8c\u836f\uff0c\u542c\u90a3\u8001\u50e7\u5982\u6b64\u8bf4\uff0c\u4e0a\u524d\u76c8\u76c8\u62dc\u5012\uff0c\u54ed\u9053\uff1a\u201c\u6c42\u5927\u5e08\u7ed9\u5148\u7236\u62a5\u4ec7\uff0c\u627e\u5230\u771f\u51f6\u3002\u201d \u3000\u3000\u90a3\u8001\u50e7\u5c1a\u672a\u56de\u7b54\uff0c\u66f9\u4e91\u5947\u5df2\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u4ec0\u4e48\u771f\u51f6\u5047\u51f6\uff1f \u3000\u3000\u8fd9\u91cc\u6709\u8d43\u6709\u8bc1\uff0c\u8fd9\u5c0f\u8d3c\u96be\u9053\u8fd8\u4e0d\u662f\u771f\u51f6\uff1f\u201d\u9676\u5b50\u5b89\u53ea\u662f\u51b7\u7b11\uff0c\u5e76\u4e0d\u7b54\u8bdd\u3002\u9676\u767e\u5c81\u5374\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u559d\u9053\uff1a\u201c\u7530\u4eb2\u5bb6\u8ddf\u6211\u6570\u5341\u5e74\u4ea4\u60c5\uff0c\u4e24\u5bb6\u53c8\u662f\u81f3\u4eb2\uff0c\u6211\u4eec\u600e\u80fd\u5bb3\u4ed6\uff1f\u201d \u3000\u3000\u66f9\u4e91\u5947\u9053\uff1a\u201c\u5c31\u662f\u4e3a\u4e86\u76d7\u5b9d\u554a\uff01\u201d\u9676\u767e\u5c81\u5927\u6012\uff0c\u7eb5\u4e0a\u524d\u53bb\u5c31\u662f\u4e00\u97ad\u3002\u66f9\u4e91\u5947\u6b63\u8981\u8fd8\u624b\uff0c\u7a81\u89c1\u90a3\u8001\u50e7\u5de6\u624b\u6325\u51fa\uff0c\u5728\u9676\u767e\u5c81\u53f3\u8155\u4e0a\u8f7b\u8f7b\u4e00\u52fe\uff0c\u94a2\u97ad\u731b\u7136\u53cd\u6fc0\u56de\u53bb\u3002\u9676\u767e\u5c81\u53ea\u89c9\u624b\u638c\u5fc3\u4e00\u9707\uff0c\u864e\u53e3\u5267\u75db\uff0c\u7adf\u7136\u62ff\u634f\u4e0d\u4f4f\uff0c\u6025\u5fd9\u6492\u624b\u5411\u65c1\u8dc3\u5f00\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u94a2\u97ad\u8dcc\u5728\u96ea\u5730\uff0c\u57cb\u5165\u4e86\u534a\u622a\u3002 \u3000\u3000\u4f17\u4eba\u672c\u6765\u56f4\u5728\u50e7\u4eba\u8eab\u5468\uff0c\u7a81\u89c1\u94a2\u97ad\u98de\u8d77\u8dcc\u843d\uff0c\u5404\u81ea\u5411\u540e\u8dc3\u5f00\uff0c\u767b\u65f6\u5728\u90a3\u50e7\u4eba\u8eab\u65c1\u7559\u51fa\u597d\u5927\u4e00\u4e2a\u5706\u5708\uff0c\u5404\u4eba\u773c\u7741\u7741\u7684\u671b\u7740\u8fd9\u548c\u5c1a\uff0c\u90fd\u662f\u597d\u751f\u8be7\u5f02\uff0c\u6697\u60f3\uff1a\u201c\u9547\u5173\u4e1c\u7d20\u4ee5\u8182\u529b\u521a\u731b\u79f0\u96c4\u6b66\u6797\uff0c\u600e\u4e48\u7ed9\u4ed6\u8fd9\u822c\u8f7b\u63cf\u6de1\u5199\u7684\u4e00\u52fe\u4e00\u5e26\uff0c\u7adf\u7136\u8fde\u5175\u5203\u4e5f\u6492\u624b\u4e86\uff1f\u201d \u3000\u3000\u9676\u767e\u5c81\u6ee1\u8138\u901a\u7ea2\uff0c\u53eb\u9053\uff1a\u201c\u597d\u548c\u5c1a\uff0c\u539f\u6765\u4f60\u662f\u5929\u9f99\u95e8\u9080\u6765\u7684\u5e2e\u624b\u3002\u201d\u90a3\u8001\u50e7\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u65bd\u4e3b\u6041\u5927\u5e74\u7eaa\uff0c\u4ecd\u662f\u8fd9\u7b49\u706b\u6c14\u3002\u4e0d\u9519\uff0c\u548c\u5c1a\u786e\u662f\u53d7\u4eba\u4e4b\u9080\uff0c\u624d\u5230\u957f\u767d\u5c71\u6765\u3002\u4e0d\u8fc7\u9080\u8bf7\u548c\u5c1a\u7684\uff0c\u5012\u4e0d\u662f\u5929\u9f99\u95e8\u3002\u201d\u5929\u9f99\u95e8\u8bf8\u4eba\u4e0e\u9676\u6c0f\u7236\u5b50\u4ff1\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u76f8\u6551\u90d1\u4e09\u5a18\u3002\u4ed6\u65e2\u662f\u5e73\u901a\u9556\u5c40\u7684\u5e2e\u624b\uff0c\u8fd9\u94c1\u76d2\u513f\u53ef\u5c31\u96be\u4fdd\u4e86\u3002\u201d\u962e\u58eb\u4e2d\u9000\u540e\u4e00\u6b65\u3002\u6bb7\u5409\u4e0e\u66f9\u4e91\u5947\u53cc\u5251\u4e0a\u524d\uff0c\u62a4\u5728\u4ed6\u5de6\u53f3\u4e24\u4fa7\u3002 \u3000\u3000\u90a3\u50e7\u4eba\u5b9b\u5982\u672a\u89c1\uff0c\u7eed\u9053\uff1a\u201c\u6b64\u95f4\u4e00\u65e0\u67f4\u706b\uff0c\u4e8c\u65e0\u9152\u996d\uff0c\u5bd2\u6c14\u597d\u751f\u96be\u71ac\u3002\u90a3\u4e3b\u4eba\u7684\u5e84\u5b50\u79bb\u6b64\u4e0d\u8fdc\uff0c\u5404\u4f4d\u90fd\u7b97\u662f\u548c\u5c1a\u7684\u670b\u53cb\uff0c\u4e0d\u5982\u540c\u53bb\u6b47\u811a\u3002\u90a3\u4e3b\u4eba\u89c1\u5230\u5927\u7fa4\u82f1\u96c4\u597d\u6c49\u964d\u4e34\uff0c\u4e00\u5b9a\u5f00\u5fc3\uff0c\u4ed6\u5988\u7684\uff0c\u5927\u5bb6\u540c\u53bb\u6270\u4ed6\u4e00\u987f\uff01\u201d\u8bf4\u7f62\u5475\u5475\u800c\u7b11\uff0c\u5bf9\u4f17\u4eba\u9002\u624d\u7684\u6d74\u8840\u6076\u6597\uff0c\u4f3c\u4e4e\u5168\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u4ed6\u9762\u76ee\u867d\u7136\u4e11\u964b\uff0c\u8bf4\u8bdd\u5012\u662f\u548c\u6c14\uff0c\u51fa\u5bb6\u4eba\u53e3\u51fa\u201c\u4ed6\u5988\u7684\u201d\u4e09\u5b57\uff0c\u672a\u514d\u6709\u4e9b\u7a81\u5140\uff0c\u4f46\u8fd9\u4e9b\u8c6a\u5ba2\u542c\u5728\u8033\u91cc\uff0c\u53cd\u611f\u4eb2\u5207\u81ea\u5728\uff0c\u63d0\u9632\u4e4b\u5fc3\u6d88\u4e86\u5927\u534a\u3002 \u3000\u3000\u6bb7\u5409\u9053\uff1a\u201c\u4e0d\u77e5\u5927\u5e08\u6240\u8bf4\u7684\u4e3b\u4eba\uff0c\u662f\u54ea\u4e00\u4f4d\u524d\u8f88\uff1f\u201d\u90a3\u8001\u50e7\u9053\uff1a\u201c\u8fd9\u4e3b\u4eba\u4e0d\u8bb8\u548c\u5c1a\u8bf4\u4ed6\u540d\u5b57\u3002\u548c\u5c1a\u751f\u6765\u597d\u5ba2\uff0c\u65e2\u7136\u51fa\u53e3\u9080\u8bf7\uff0c\u82e5\u6709\u54ea\u4e00\u4f4d\u4e0d\u7ed9\u9762\u5b50\uff0c\u548c\u5c1a\u53ef\u8981\u5927\u611f\u8138\u4e0a\u65e0\u5149\u4e86\u3002\u201d \u3000\u3000\u5218\u5143\u9e64\u89c1\u8fd9\u8001\u50e7\u5904\u5904\u900f\u7740\u53e4\u602a\uff0c\u5fc3\u4e2d\u5600\u5495\uff0c\u5fae\u4e00\u62f1\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u5e08\u83ab\u602a\uff0c\u4e0b\u5b98\u5931\u966a\u4e86\u3002\u201d\u8bf4\u7f62\u8fd4\u8eab\u4fbf\u5954\u3002\u90a3\u8001\u50e7\u7b11\u9053\uff1a\u201c\u5728\u8fd9\u8352\u5c71\u91ce\u5730\u4e4b\u4e2d\uff0c\u5c45\u7136\u8fd8\u80fd\u89c1\u5230\u4e00\u4f4d\u5b98\u8001\u7237\uff0c\u597d\u798f\u6c14\u554a\uff0c\u4ed6\u5988\u7684\u597d\u798f\u6c14\u3002\u201d\u4ed6\u5f85\u5218\u5143\u9e64\u5954\u51fa\u4e00\u9635\uff0c\u7f13\u7f13\u8bf4\u5b8c\u8fd9\u51e0\u53e5\u8bdd\uff0c\u6597\u7136\u95f4\u8eab\u5f62\u6643\u52a8\uff0c\u968f\u540e\u8ffd\u53bb\u3002\u53ea\u89c1\u4ed6\u5728\u96ea\u5730\u91cc\u7eb5\u8df3\u75be\u5954\uff0c\u8eab\u6cd5\u6781\u5176\u96be\u770b\uff0c\u53c8\u7b28\u53c8\u602a\uff0c\u4ee4\u4eba\u4e0d\u7531\u5f97\u597d\u7b11\u3002 \u3000\u3000\u4f46\u5c3d\u7ba1\u4ed6\u8eab\u5f62\u53c8\u4f3c\u80a5\u9e2d\uff0c\u53c8\u4f3c\u86e4\u87c6\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u7adf\u5df2\u6284\u5728\u5218\u5143\u9e64\u8eab\u524d\uff0c\u7b11\u9053\uff1a\u201c\u548c\u5c1a\u8981\u5bf9\u4e0d\u4f4f\u5b98\u8001\u7237\u4e86\u3002\u201d\u4e0d\u5f85\u5218\u5143\u9e64\u7b54\u8bdd\uff0c\u5de6\u624b\u515c\u4e86\u4e2a\u5708\u5b50\uff0c\u5ffd\u7136\u7ffb\u4e86\u8fc7\u6765\uff0c\u6293\u4f4f\u4ed6\u7684\u53f3\u8155\u3002 \u3000\u3000\u5218\u5143\u9e64\u9661\u611f\u534a\u8eab\u9178\u9ebb\uff0c\u77e5\u9053\u81ea\u5df1\u80e1\u91cc\u80e1\u6d82\u7684\u5df2\u88ab\u4ed6\u6263\u4f4f\u8109\u95e8\uff0c\u60c5\u6025\u4e4b\u4e0b\uff0c\u5de6\u624b\u51fa\u638c\u5f80\u8001\u50e7\u51fb\u53bb\u3002\u90a3\u8001\u50e7\u5de6\u624b\u62c7\u6307\u4e0e\u98df\u6307\u62ff\u7740\u4ed6\u7684\u53f3\u8155\uff0c\u89c1\u4ed6\u5de6\u638c\u51fb\u6765\uff0c\u5de6\u624b\u63d0\u7740\u4ed6\u53f3\u81c2\u4e00\u4e3e\uff0c\u4e2d\u6307\u3001\u65e0\u540d\u6307\u3001\u5c0f\u6307\u4e09\u6839\u624b\u6307\u94a9\u51fa\uff0c\u642d\u4e0a\u4e86\u4ed6\u5de6\u8155\u3002\u8fd9\u4e00\u6765\uff0c\u4ed6\u4e00\u53ea\u624b\u5c06\u5218\u5143\u9e64\u53cc\u624b\u4e00\u9f50\u6293\u4f4f\uff0c\u53f3\u624b\u63d0\u7740\u5ff5\u73e0\uff0c\u4e00\u7a9c\u4e00\u8df3\u7684\u56de\u6765\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u5218\u5143\u9e64\u53cc\u624b\u5c31\u5982\u88ab\u4e00\u526f\u94c1\u94d0\u7262\u7262\u94d0\u7740\uff0c\u8eab\u4e0d\u7531\u4e3b\u7684\u7ed9\u90a3\u8001\u50e7\u62d6\u56de\uff0c\u90fd\u662f\u53c8\u60ca\u53c8\u559c\uff0c\u60ca\u7684\u662f\u8fd9\u8001\u50e7\u529f\u592b\u4e4b\u9ad8\uff0c\u751a\u4e3a\u7f55\u89c1\uff0c\u559c\u7684\u662f\u4ed6\u5e76\u975e\u5e73\u901a\u9556\u5c40\u6240\u9080\u7684\u5e2e\u624b\u3002\u90a3\u8001\u50e7\u62c9\u7740\u5218\u5143\u9e64\u8d70\u5230\u4f17\u4eba\u8eab\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u5218\u5927\u4eba\u5df2\u7b54\u5e94\u8d4f\u8138\uff0c\u5404\u4f4d\u8bf7\u5427\u3002\u201d \u3000\u3000\u6709\u5218\u5143\u9e64\u7684\u699c\u6837\u5728\u524d\uff0c\u5373\u4ee4\u6709\u4eba\u5fc3\u5b58\u7591\u60e7\uff0c\u4e5f\u4e0d\u6562\u518d\u51fa\u8a00\u76f8\u62d2\uff0c\u81ea\u8ba8\u6ca1\u8da3\u3002\u53ea\u89c1\u90a3\u8001\u50e7\u63e1\u7740\u5218\u5143\u9e64\u7684\u624b\u8155\uff0c\u7f13\u7f13\u5411\u524d\uff0c\u8d70\u51fa\u6570\u6b65\uff0c\u5ffd\u7136\u8f6c\u8eab\u9053\uff1a\u201c\u4ec0\u4e48\u58f0\u97f3\uff1f\u201d\u4f17\u4eba\u505c\u6b65\u4fa7\u8033\u4e00\u542c\uff0c\u4f46\u542c\u5f97\u6765\u8def\u4e0a\u9690\u9690\u4f20\u6765\u4e00\u9635\u6c14\u5598\u5406\u559d\u4e4b\u58f0\uff0c\u4f3c\u4e4e\u6709\u4eba\u5728\u594b\u529b\u640f\u51fb\u3002\u962e\u58eb\u4e2d\u9661\u7136\u9192\u609f\uff0c\u53eb\u9053\uff1a\u201c\u4e91\u5947\uff0c\u5feb\u53bb\u76f8\u52a9\u4e91\u9633\u3002\u201d \u3000\u3000\u66f9\u4e91\u5947\u53eb\u9053\uff1a\u201c\u554a\u54df\uff0c\u6211\u7adf\u5fd8\u4e86\u3002\u201d\u633a\u5251\u5411\u6765\u8def\u5954\u56de\u3002 \u3000\u3000\u90a3\u8001\u50e7\u4ecd\u4e0d\u653e\u5f00\u5218\u5143\u9e64\uff0c\u62c9\u7740\u4ed6\u4e00\u9f50\u8d76\u53bb\uff0c\u53ea\u8d76\u51fa\u5341\u4f59\u4e08\uff0c\u5218\u5143\u9e64\u8db3\u4e0b\u529f\u592b\u5df2\u76f8\u5f62\u89c1\u7ecc\u3002\u4ed6\u867d\u63d0\u6c14\u72c2\u5954\uff0c\u4ecd\u662f\u4e0d\u53ca\u90a3\u8001\u50e7\u5feb\u6377\uff0c\u53ef\u662f\u53cc\u624b\u88ab\u63e1\uff0c\u7eb5\u7136\u7528\u529b\u6323\u624e\uff0c\u90a3\u8001\u50e7\u4e94\u6839\u53c8\u7626\u53c8\u957f\u7684\u624b\u6307\u7adf\u672a\u653e\u677e\u534a\u70b9\u3002\u518d\u5954\u6570\u6b65\uff0c\u90a3\u8001\u50e7\u53c8\u62a2\u524d\u534a\u5c3a\uff0c\u8fd9\u4e00\u6765\uff0c\u5218\u5143\u9e64\u7acb\u8db3\u4e0d\u7a33\uff0c\u8eab\u5b50\u5411\u524d\u4ef0\u8dcc\u4e0b\u53bb\uff0c\u53cc\u81c2\u5939\u5728\u8033\u65c1\u4e3e\u8fc7\u5934\u9876\uff0c\u88ab\u90a3\u8001\u50e7\u5728\u96ea\u5730\u91cc\u62d6\u66f3\u800c\u884c\u3002\u4ed6\u53c8\u6c14\u53c8\u6025\uff0c\u6b32\u5f85\u98de\u811a\u5411\u90a3\u8001\u50e7\u8e22\u53bb\uff0c\u4f46\u90a3\u8001\u50e7\u8d8a\u62d6\u8d8a\u5feb\uff0c\u81ea\u5df1\u7ad9\u7acb\u5c1a\u4e14\u4e0d\u80fd\uff0c\u54ea\u91cc\u8bf4\u5f97\u4e0a\u53d1\u8db3\u8e22\u654c\uff1f \u3000\u3000\u500f\u5ffd\u4e4b\u95f4\uff0c\u4f17\u4eba\u5df2\u56de\u5230\u5751\u8fb9\uff0c\u53ea\u89c1\u5468\u4e91\u9633\u4e0e\u718a\u5143\u732e\u6402\u62b1\u7740\u5728\u96ea\u5730\u91cc\u6eda\u6765\u6eda\u53bb\u3002\u4e24\u4eba\u5175\u5203\u5747\u5df2\u8131\u624b\uff0c\u8d34\u8eab\u8089\u640f\uff0c\u8fde\u62f3\u811a\u4e5f\u4f7f\u7528\u4e0d\u4e0a\uff0c\u8098\u649e\u819d\u8e6c\u3001\u5934\u9876\u53e3\u54ac\uff0c\u6253\u5f97\u72fc\u72c8\u4e0d\u582a\uff0c\u54ea\u91cc\u50cf\u4ec0\u4e48\u6b66\u6797\u4e2d\u7684\u597d\u624b\u76f8\u6597\uff0c\u76f4\u5982\u5e02\u4e95\u6cfc\u5987\u5f53\u8857\u53ae\u6253\u4e00\u822c\u3002\u66f9\u4e91\u5947\u4ed7\u5251\u4e0a\u524d\uff0c\u8981\u5f85\u5f80\u718a\u5143\u732e\u8eab\u4e0a\u523a\u53bb\uff0c\u4f46\u4e24\u4eba\u7ffb\u6eda\u7f20\u6253\uff0c\u53ea\u6015\u8bef\u4f24\u4e86\u5e08\u5f1f\uff0c\u6025\u5207\u95f4\u4e0b\u624b\u4e0d\u5f97\u3002 \u3000\u3000\u90a3\u8001\u50e7\u8d70\u4e0a\u513f\u6b65\uff0c\u53f3\u624b\u6293\u4f4f\u5468\u4e91\u9633\u80cc\u5fc3\uff0c\u63d0\u4e86\u8d77\u6765\u3002\u5468\u718a\u4e24\u4eba\u624b\u811a\u90fd\u76f8\u4e92\u52fe\u7f20\uff0c\u63d0\u8d77\u4e00\u4eba\uff0c\u5c06\u53e6\u4e00\u4eba\u4e5f\u5e26\u4e86\u4e0a\u6765\u3002\u4e24\u4eba\u6253\u5f97\u5174\u53d1\uff0c\u867d\u7136\u8eab\u5b50\u4e34\u7a7a\uff0c\u4ecd\u662f\u6bb4\u51fb\u4e0d\u4f11\u3002\u90a3\u8001\u50e7\u54c8\u54c8\u5927\u7b11\uff0c\u53f3\u624b\u4e00\u632f\uff0c\u4e24\u4eba\u624b\u8db3\u90fd\u662f\u4e00\u9ebb\uff0c\u7830\u7684\u4e00\u54cd\uff0c\u718a\u5143\u732e\u51b2\u51fa\u4e86\u4e94\u5c3a\u4e4b\u5916\u3002\u90a3\u8001\u50e7\u5c06\u5468\u4e91\u9633\u653e\u5728\u5730\u4e0b\uff0c\u8fd9\u624d\u677e\u4e86\u5218\u5143\u9e64\u7684\u624b\u8155\u3002\u5218\u5143\u9e64\u7ed9\u4ed6\u6293\u5f97\u4e45\u4e86\uff0c\u624b\u81c2\u4e00\u65f6\u4e4b\u95f4\u7adf\u96be\u4ee5\u5f2f\u66f2\uff0c\u4ecd\u662f\u9ad8\u4e3e\u8fc7\u5934\uff0c\u8fc7\u4e86\u4e00\u4f1a\u624d\u6162\u6162\u653e\u4e0b\uff0c\u53ea\u89c1\u53cc\u8155\u4e0a\u6307\u5370\u6df1\u5165\u8089\u91cc\uff0c\u5fc3\u4e2d\u4e0d\u7981\u9a87\u7136\u3002 \u3000\u3000\u90a3\u8001\u50e7\u9053\uff1a\u201c\u4ed6\u5976\u5976\u7684\uff0c\u5927\u4f19\u513f\u5feb\u8d70\uff0c\u8fd8\u6765\u5f97\u53ca\u53bb\u6270\u4e3b\u4eba\u4e00\u987f\u65e9\u996d\u3002\u201d\u4f17\u4eba\u76f8\u4e92\u77a7\u4e86\u4e00\u773c\uff0c\u4e00\u9f50\u8ddf\u5728\u4ed6\u7684\u8eab\u540e\uff0c\u90d1\u4e09\u5a18\u817f\u4e0a\u4f24\u91cd\uff0c\u718a\u5143\u732e\u987e\u4e0d\u5f97\u7537\u5973\u4e4b\u5acc\uff0c\u5c06\u5979\u80cc\u5728\u80cc\u4e0a\uff0c\u9676\u6c0f\u7236\u5b50\u3001\u5468\u4e91\u9633\u7b49\u5747\u5404\u8d1f\u4f24\u3002\u4f46\u89c1\u96ea\u5730\u91cc\u4e00\u9053\u6bb7\u7ea2\u8840\u8ff9\uff0c\u5f15\u5411\u5317\u53bb\u3002 \u3000\u3000\u884c\u51fa\u6570\u91cc\uff0c\u4f24\u8005\u54fc\u54fc\u5527\u5527\uff0c\u90fd\u6709\u4e9b\u96be\u4ee5\u652f\u6301\u3002\u7530\u9752\u6587\u4ece\u80cc\u56ca\u4e2d\u53d6\u51fa\u4e00\u4ef6\u66ff\u6362\u7684\u5e03\u886b\uff0c\u6495\u788e\u4e86\u5148\u7ed9\u5468\u4e91\u9633\u88f9\u4f24\uff0c\u53c8\u7ed9\u9676\u6c0f\u7236\u5b50\u5305\u624e\u3002\u66f9\u4e91\u5947\u54fc\u4e86\u4e00\u58f0\uff0c\u5f85\u8981\u53d1\u8bdd\u3002\u7530\u9752\u6587\u6a2a\u76ee\u4f7f\u4e2a\u773c\u8272\uff0c\u66f9\u4e91\u5947\u867d\u4e0d\u660e\u5979\u610f\u601d\uff0c\u7ec8\u4e8e\u5fcd\u4f4f\u4e86\u53e3\u8fb9\u8a00\u8bed\u3002 \u3000\u3000\u53c8\u884c\u91cc\u8bb8\uff0c\u8f6c\u8fc7\u4e00\u4e2a\u5c71\u5761\uff0c\u5730\u4e0b\u767d\u96ea\u66f4\u6df1\uff0c\u76f4\u6ca1\u81f3\u819d\uff0c\u884c\u8d70\u597d\u751f\u4e3a\u96be\uff0c\u4f17\u4eba\u867d\u7136\u90fd\u6709\u6b66\u529f\uff0c\u4f46\u4ea6\u611f\u4e0d\u6613\u62d4\u8db3\uff0c\u5404\u81ea\u5fc3\u60f3\uff1a\u201c\u4e0d\u77e5\u90a3\u4e3b\u4eba\u4e4b\u5bb6\u8fd8\u6709\u591a\u8fdc\uff1f\u201d\u90a3\u8001\u50e7\u4f3c\u77e5\u5404\u4eba\u5fc3\u610f\uff0c\u6307\u7740\u5de6\u4fa7\u4e00\u5ea7\u7b14\u7acb\u7684\u5c71\u5cf0\u9053\uff1a\u201c\u4e0d\u8fdc\u4e86\uff0c\u5c31\u5728\u90a3\u4e0a\u9762\u3002\u201d \u3000"}),showOnHomepage:!0},{name:"\u4e8c",content:(0,a.jsx)(a.Fragment,{children:"\u4f17\u4eba\u4e00\u671b\u5c71\u5cf0\uff0c\u4e0d\u7981\u5012\u62bd\u4e00\u53e3\u51c9\u6c14\uff0c\u5168\u8eab\u51b7\u4e86\u534a\u622a\u3002\u90a3\u5c71\u5cf0\u867d\u975e\u5947\u9ad8\uff0c\u4f46\u5b9b\u5982\u4e00\u6839\u7b14\u7ba1\u822c\u7ad6\u7acb\u5728\u7fa4\u5c71\u4e4b\u4e2d\uff0c\u9661\u5ced\u5f02\u5e38\uff0c\u83ab\u8bf4\u662f\u4eba\uff0c\u5373\u4ee4\u733f\u7334\u4e5f\u662f\u4e0d\u6613\u4e0a\u53bb\uff0c\u5fc3\u4e2d\u90fd\u5c06\u4fe1\u5c06\u7591\uff1a \u3000\u3000\u201c\u672c\u9886\u9ad8\u5f3a\u4e4b\u4eba\u5c31\u7b97\u80fd\u722c\u5f97\u4e0a\u53bb\uff0c\u53ef\u662f\u5728\u8fd9\u9661\u5cf0\u7684\u7edd\u9876\u4e4b\u4e0a\uff0c\u96be\u9053\u8fd8\u4f1a\u6709\u4eba\u5c45\u4f4f\u4e0d\u6210\uff1f\u201d \u3000\u3000\u90a3\u8001\u50e7\u5fae\u5fae\u4e00\u7b11\uff0c\u5728\u524d\u5f15\u8def\uff0c\u53c8\u8f6c\u8fc7\u4e24\u4e2a\u5c71\u5761\uff0c\u8fdb\u4e86\u4e00\u5ea7\u5927\u677e\u6797\u3002\u6797\u4e2d\u677e\u6811\u90fd\u662f\u6570\u767e\u5e74\u7684\u8001\u6811\uff0c\u679d\u67ef\u4ea4\u6a2a\uff0c\u6811\u9876\u4e0a\u538b\u4e86\u6570\u5c3a\u539a\u7684\u767d\u96ea\uff0c\u662f\u4ee5\u6797\u4e2d\u96ea\u5c11\uff0c\u53cd\u800c\u597d\u8d70\u3002\u8fd9\u5ea7\u677e\u6797\u597d\u957f\uff0c\u8d70\u4e86\u534a\u4e2a\u65f6\u8fb0\u65b9\u59cb\u8fc7\u5b8c\uff0c\u4e00\u51fa\u677e\u6797\uff0c\u5373\u5230\u5c71\u5cf0\u811a\u4e0b\u3002 \u3000\u3000\u4f17\u4eba\u4ef0\u671b\u5c71\u5cf0\uff0c\u6b64\u65f6\u8fd1\u89c2\uff0c\u66f4\u89c9\u60ca\u5fc3\u52a8\u9b44\uff0c\u5fc3\u60f3\u5373\u5728\u590f\u65e5\uff0c\u4ea6\u96be\u722c\u4e0a\uff0c\u773c\u524d\u6ee1\u5cf0\u662f\u96ea\uff0c\u82e5\u662f\u5192\u9669\u6500\u63f4\uff0c\u5341\u6210\u4e2d\u5012\u6709\u4e5d\u6210\u8981\u8dcc\u4e2a\u7c89\u8eab\u788e\u9aa8\u3002 \u3000\u3000\u53ea\u542c\u4e00\u9635\u5c71\u98ce\u8fc7\u53bb\uff0c\u5439\u5f97\u677e\u6811\u679d\u53f6\u76f8\u649e\uff0c\u6709\u4f3c\u79cb\u6f6e\u591c\u81f3\u3002 \u3000\u3000\u4f17\u4eba\u6d6a\u8ff9\u6c5f\u6e56\uff0c\u90fd\u89c1\u8fc7\u4e0d\u5c11\u5927\u9635\u5927\u4ed7\uff0c\u4f46\u6b64\u523b\u7acb\u5728\u8fd9\u5c71\u5cf0\u4e4b\u4e0b\uff0c\u7adf\u4e0d\u81ea\u7981\u7684\u5ffd\u611f\u80c6\u602f\u3002\u90a3\u8001\u50e7\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u4e2a\u82b1\u7b52\u706b\u7bad\uff0c\u6643\u706b\u6298\u70b9\u7740\u4e86\u3002\u55e4\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u706b\u7bad\u51b2\u5929\u800c\u8d77\uff0c\u653e\u51fa\u4e00\u9053\u84dd\u70df\uff0c\u4e45\u4e45\u4e0d\u6563\u3002 \u3000\u3000\u4f17\u4eba\u77e5\u9053\u8fd9\u662f\u6c5f\u6e56\u4e0a\u901a\u6d88\u606f\u7684\u8baf\u53f7\uff0c\u53ea\u662f\u8fd9\u706b\u7bad\u98de\u5f97\u5982\u6b64\u4e4b\u9ad8\uff0c\u84dd\u70df\u5728\u7a7a\u4e2d\u53c8\u505c\u7559\u8fd9\u4e48\u4e45\uff0c\u5374\u662f\u6781\u4e3a\u7f55\u89c1\u3002\u4f17\u4eba\u4ef0\u671b\u5cf0\u9876\uff0c\u5bdf\u770b\u6709\u4f55\u52a8\u9759\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u53ea\u89c1\u5cf0\u9876\u51fa\u73b0\u4e00\u4e2a\u9ed1\u70b9\uff0c\u8fc5\u901f\u5f02\u5e38\u7684\u6ed1\u4e86\u4e0b\u6765\uff0c\u8d8a\u8fd1\u8d8a\u5927\uff0c\u5f85\u5f97\u6ed1\u5230\u534a\u5c71\uff0c\u5df2\u770b\u6e05\u695a\u662f\u4e00\u53ea\u6781\u5927\u7684\u7af9\u7bee\uff0c\u7bee\u4e0a\u7cfb\u7740\u7af9\u7d22\uff0c\u539f\u6765\u662f\u5c71\u5cf0\u4e0a\u653e\u4e0b\u6765\u63a5\u5ba2\u4e4b\u7528\u3002 \u3000\u3000\u7af9\u7bee\u843d\u5230\u4f17\u4eba\u9762\u524d\uff0c\u505c\u4f4f\u4e0d\u52a8\u3002\u90a3\u8001\u50e7\u9053\uff1a\u201c\u8fd9\u7bee\u5b50\u5750\u5f97\u4e09\u4eba\uff0c\u8ba9\u4e24\u4f4d\u5973\u5ba2\u5148\u4e0a\u53bb\uff0c\u8fd8\u53ef\u518d\u5750\u4e00\u4f4d\u7537\u5ba2\u3002\u54ea\u4e00\u4e2a\u5750\uff1f\u548c\u5c1a\u4e0d\u63e9\u5973\u65bd\u4e3b\u7684\u6cb9\uff0c\u6211\u662f\u4e0d\u5750\u7684\uff0c\u54c8\u54c8\u3002\u201d\u4f17\u4eba\u5747\u60f3\uff1a\u201c\u8fd9\u548c\u5c1a\u6b66\u529f\u6781\u9ad8\uff0c\u8bf4\u8bdd\u5374\u6041\u5730\u7c97\u9c81\u65e0\u804a\u3002\u201d \u3000\u3000\u7530\u9752\u6587\u6276\u7740\u90d1\u4e09\u5a18\u5750\u5165\u7bee\u4e2d\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u65e2\u5148\u4e0a\u4e86\u53bb\uff0c\u66f9\u5e08\u54e5\u5b9a\u8981\u4e58\u673a\u76f8\u5bb3\u5b50\u5b89\u3002\u82e5\u662f\u6211\u53eb\u5b50\u5b89\u540c\u4e0a\uff0c\u5e08\u53d4\u9762\u524d\u987b\u4e0d\u597d\u770b\u3002\u201d\u4e8e\u662f\u5411\u66f9\u4e91\u5947\u62db\u624b\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u4f60\u8ddf\u6211\u4e00\u8d77\u4e0a\u3002\u201d\u66f9\u4e91\u5947\u53d7\u5ba0\u82e5\u60ca\uff0c\u5411\u9676\u5b50\u5b89\u671b\u4e86\u4e00\u773c\uff0c\u5f97\u610f\u4e4b\u60c5\uff0c\u89c1\u4e8e\u989c\u8272\uff0c\u5f53\u4e0b\u8de8\u8fdb\u7bee\u53bb\uff0c\u5728\u7530\u9752\u6587\u8eab\u65c1\u5750\u4e0b\uff0c\u62c9\u7740\u7af9\u7d22\uff0c\u7528\u529b\u6447\u4e86\u51e0\u4e0b\u3002 \u3000\u3000\u53ea\u89c9\u7bee\u5b50\u6643\u52a8\uff0c\u767b\u65f6\u5411\u5cf0\u9876\u5347\u4e86\u4e0a\u53bb\u3002\u66f9\u7530\u90d1\u4e09\u4eba\u5c31\u5982\u51ed\u865a\u5fa1\u98ce\u3001\u817e\u4e91\u9a7e\u96fe\u4e00\u822c\uff0c\u5fc3\u4e2d\u7a7a\u8361\u8361\u7684\u751a\u4e0d\u597d\u53d7\u3002\u7bee\u5230\u5cf0\u8170\uff0c\u7530\u9752\u6587\u5411\u4e0b\u4e00\u671b\uff0c\u53ea\u89c1\u5c71\u4e0b\u4f17\u4eba\u5df2\u7f29\u6210\u4e86\u5c0f\u70b9\uff0c\u539f\u6765\u8fd9\u5c71\u5cf0\u8fdc\u671b\u4f3c\u4e0d\u751a\u9ad8\uff0c\u5176\u5b9e\u58c1\u7acb\u5343\u4ede\uff0c\u5374\u662f\u975e\u540c\u5c0f\u53ef\u3002\u7530\u9752\u6587\u53ea\u611f\u5934\u6655\u76ee\u7729\uff0c\u5f53\u5373\u95ed\u773c\uff0c\u4e0d\u6562\u518d\u770b\u3002 \u3000\u3000\u7ea6\u83ab\u4e00\u76cf\u8336\u65f6\u5206\uff0c\u7bee\u5b50\u5347\u5230\u4e86\u5cf0\u9876\u3002\u66f9\u4e91\u5947\u8de8\u51fa\u7af9\u7bee\uff0c\u6276\u7530\u90d1\u4e8c\u4eba\u51fa\u6765\u3002\u53ea\u89c1\u5c71\u5cf0\u65c1\u597d\u5927\u4e09\u4e2a\u7ede\u76d8\uff0c\u4e92\u4ee5\u7af9\u7d22\u7275\u8fde\uff0c\u4e09\u76d8\u4e92\u7ede\uff0c\u5347\u964d\u7af9\u7bee\uff0c\u5341\u4f59\u540d\u58ee\u6c49\u6273\u52a8\u4e09\u4e2a\u7ede\u76d8\uff0c\u53c8\u5c06\u7bee\u5b50\u653e\u4e86\u4e0b\u53bb\u3002\u7bee\u5b50\u4e0a\u4e0b\u6570\u6b21\uff0c\u90a3\u8001\u50e7\u4e0e\u7fa4\u8c6a\u90fd\u4e0a\u4e86\u5cf0\u9876\u3002\u7ede\u76d8\u65c1\u7ad9\u7740\u4e24\u540d\u7070\u8863\u6c49\u5b50\uff0c\u5148\u89c1\u66f9\u4e91\u5947\u7b49\u5747\u4e0d\u7406\u776c\uff0c\u76f4\u5230\u8001\u50e7\u4e0a\u6765\uff0c\u8fd9\u624d\u8d8b\u524d\u8eac\u8eab\u884c\u793c\u3002 \u3000\u3000\u90a3\u8001\u50e7\u7b11\u9053\uff1a\u201c\u548c\u5c1a\u6ca1\u901a\u77e5\u4e3b\u4eba\uff0c\u5c31\u5e26\u4e86\u51e0\u4e2a\u670b\u53cb\u6765\u5403\u767d\u98df\u4e86\u3002\u54c8\u54c8\uff01\u201d\u4e00\u4e2a\u957f\u9888\u9614\u989d\u7684\u4e2d\u5e74\u6c49\u5b50\u8eac\u8eab\u9053\uff1a\u201c\u65e2\u662f\u5b9d\u6811\u5927\u5e08\u7684\u670b\u53cb\uff0c\u655d\u4e0a\u81ea\u662f\u5341\u5206\u6b22\u8fce\u3002\u201d\u4f17\u4eba\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u8001\u50e7\u53eb\u4f5c\u5b9d\u6811\u3002\u201d \u3000\u3000\u4f46\u89c1\u90a3\u6c49\u5b50\u56e2\u56e2\u5411\u4f17\u4eba\u4f5c\u4e86\u4e2a\u56db\u65b9\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u655d\u4e0a\u56e0\u4e8b\u51fa\u95e8\uff0c\u6ca1\u80fd\u606d\u8fce\u5609\u5bbe\uff0c\u8bf7\u5404\u4f4d\u82f1\u96c4\u6055\u7f6a\u3002\u201d\u4f17\u4eba\u6025\u5fd9\u8fd8\u793c\uff0c\u5fc3\u4e2d\u5404\u81ea\u7eb3\u7f55\uff1a\u201c\u8fd9\u4eba\u8eab\u5c45\u96ea\u5cf0\u7edd\u9876\uff0c\u8863\u886b\u5355\u8584\uff0c\u5374\u6ca1\u4e1d\u6beb\u6015\u51b7\u7684\u6a21\u6837\uff0c\u81ea\u7136\u662f\u5185\u529f\u4e0d\u5f31\u3002\u53ef\u662f\u542c\u4ed6\u8bed\u6c14\uff0c\u5374\u662f\u4e3a\u4eba\u4f63\u4ec6\u4e0b\u8d70\uff0c\u90a3\u4ed6\u7684\u4e3b\u4eba\u53c8\u662f\u4f55\u7b49\u82f1\u96c4\u4eba\u7269\uff1f\u201d \u3000\u3000\u53ea\u89c1\u5b9d\u6811\u8138\u4e0a\u5fae\u6709\u8bb6\u8272\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4e3b\u4eba\u4e0d\u5728\u5bb6\u4e48\uff1f\u600e\u4e48\u5728\u8fd9\u5f53\u53e3\u8fd8\u51fa\u95e8\uff1f\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u655d\u4e0a\u4e03\u65e5\u524d\u51fa\u95e8\uff0c\u5230\u5b81\u53e4\u5854\u53bb\u4e86\u3002\u201d\u5b9d\u6811\u9053\uff1a\u201c\u5b81\u53e4\u5854\uff1f\u53bb\u5e72\u4ec0\u4e48\uff1f\u201d\u90a3\u6c49\u5b50\u5411\u962e\u58eb\u4e2d\u7b49\u671b\u4e86\u4e00\u773c\uff0c\u4f3c\u4e4e\u4e0d\u4fbf\u76f8\u544a\u3002\u5b9d\u6811\u9053\uff1a\u201c\u4f46\u8bf4\u4e0d\u59a8\u3002\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u4e3b\u4eba\u8bf4\u5bf9\u5934\u5389\u5bb3\uff0c\u53ea\u6015\u5230\u65f6\u654c\u4ed6\u4e0d\u4f4f\uff0c\u6240\u4ee5\u8d76\u8d74\u5b81\u53e4\u5854\uff0c\u53bb\u8bf7\u91d1\u9762\u4f5b\u4e0a\u5c71\u52a9\u62f3\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u542c\u201c\u91d1\u9762\u4f5b\u201d\u4e09\u5b57\uff0c\u90fd\u5413\u4e86\u4e00\u8df3\u3002\u6b64\u4eba\u662f\u6b66\u6797\u524d\u8f88\uff0c\u4e8c\u5341\u5e74\u6765\u6c5f\u6e56\u4e0a\u53f7\u79f0\u201c\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u201d\u3002\u4e3a\u4e86\u8fd9\u4e03\u4e2a\u5b57\u5916\u53f7\uff0c\u4e0d\u77e5\u7ed9\u4ed6\u62db\u6765\u591a\u5c11\u5f3a\u4ec7\uff0c\u6811\u4e0a\u591a\u5c11\u52b2\u654c\uff0c\u53ef\u662f\u4ed6\u6b66\u529f\u4e5f\u771f\u9ad8\uff0c\u4e0d\u8bba\u662f\u54ea\u4e00\u95e8\u54ea\u4e00\u6d3e\u7684\u597d\u624b\uff0c\u65e0\u4e0d\u4e00\u4e00\u8f93\u5728\u4ed6\u7684\u624b\u91cc\u3002\u8fd1\u5341\u5e74\u4ed6\u9500\u58f0\u533f\u8ff9\uff0c\u6b66\u6797\u4e2d\u4e0d\u518d\u542c\u5230\u8baf\u606f\uff0c\u6709\u4eba\u4f20\u8a00\u4ed6\u5df2\u5728\u897f\u57df\u75c5\u6b7b\uff0c\u4f46\u65e0\u4eba\u4eb2\u89c1\uff0c\u4e5f\u53ea\u662f\u5c06\u4fe1\u5c06\u7591\u3002\u8fd9\u65f6\u5ffd\u542c\u5f97\u4ed6\u975e\u4f46\u5c1a\u5728\u4eba\u4e16\uff0c\u800c\u4e14\u6b64\u95f4\u4e3b\u4eba\u6b63\u53bb\u9080\u4ed6\u4e0a\u5c71\uff0c\u4eba\u4eba\u767b\u65f6\u90fd\u611f\u4e0d\u5b89\u3002 \u3000\u3000\u539f\u6765\u8fd9\u91d1\u9762\u4f5b\u6b66\u529f\u65e2\u9ad8\uff0c\u4e3a\u4eba\u53c8\u662f\u5ac9\u6076\u5982\u4ec7\uff0c\u82e5\u662f\u6709\u8c01\u5e72\u4e86\u4e0d\u7aef\u884c\u5f84\uff0c\u4ed6\u4e0d\u77e5\u9053\u4fbf\u7f62\uff0c\u53ea\u8981\u7ed9\u4ed6\u542c\u5230\u4e86\uff0c\u5b9a\u8981\u627e\u4e0a\u95e8\u6765\u7406\u4f1a\uff0c\u4f5c\u6076\u4e4b\u4eba\uff0c\u8f7b\u5219\u635f\u6298\u4e00\u624b\u4e00\u8db3\uff0c\u91cd\u5219\u6b92\u547d\uff0c\u51b3\u7136\u9003\u9041\u4e0d\u4e86\uff0c\u4e0a\u5c71\u8fd9\u4f19\u4eba\u4e2a\u4e2a\u505a\u8fc7\u6216\u5927\u6216\u5c0f\u7684\u4e8f\u5fc3\u4e8b\uff0c\u731b\u7136\u95f4\u542c\u5230\u201c\u91d1\u9762\u4f5b\u201d\u4e09\u5b57\uff0c\u5982\u4f55\u4e0d\u5fc3\u60ca\u8089\u8df3\uff1f \u3000\u3000\u5b9d\u6811\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e3b\u4eba\u4e5f\u5fd2\u715e\u5c0f\u5fc3\u4e86\uff0c\u8c05\u90a3\u6709\u591a\u5927\u672c\u9886\uff0c\u7528\u5f97\u7740\u8fd9\u7b49\u8d39\u4e8b\uff1f\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u6709\u5927\u5e08\u8fdc\u6765\u52a9\u62f3\uff0c\u54b1\u4eec\u539f\u5df2\u7a33\u64cd\u80dc\u5238\u3002\u4f46\u542c\u8bf4\u90a3\u98de\u72d0\u786e\u662f\u51f6\u72e1\u65e0\u6bd4\u3002\u655d\u4e0a\u8bf4\u6709\u5907\u65e0\u60a3\uff0c\u591a\u51e0\u4e2a\u5e2e\u624b\uff0c\u4e5f\u514d\u5f97\u8ba9\u90a3\u98de\u72d0\u6b65\u4e86\u3002\u201d\u4f17\u4eba\u53c8\u5404\u5bfb\u601d\uff1a\u201c\u53c8\u662f\u4ec0\u4e48\u5389\u5bb3\u811a\u8272\uff1f\u201d \u3000\u3000\u5b9d\u6811\u548c\u90a3\u6c49\u5b50\u8bf4\u7740\u8bdd\uff0c\u5f53\u5148\u800c\u884c\uff0c\u8f6c\u8fc7\u4e86\u51e0\u682a\u96ea\u677e\u3002\u53ea\u89c1\u524d\u9762\u4e00\u5ea7\u4e94\u5f00\u95f4\u6781\u5927\u7684\u77f3\u5c4b\uff0c\u5c4b\u524d\u5c4b\u540e\u90fd\u662f\u767d\u96ea\u3002 \u3000\u3000\u4f17\u4eba\u8fdb\u4e86\u5927\u95e8\uff0c\u8d70\u8fc7\u4e00\u9053\u957f\u5eca\uff0c\u6765\u5230\u524d\u5385\u3002\u90a3\u5385\u6781\u5927\uff0c\u56db\u89d2\u5404\u751f\u7740\u4e00\u76c6\u5927\u70ad\u706b\u3002\u5385\u4e0a\u5c45\u4e2d\u6302\u7740\u4e00\u526f\u6728\u677f\u5bf9\u8054\uff0c\u5199\u7740\u5eff\u4e8c\u4e2a\u5927\u5b57\uff1a \u3000\u3000\u4e0d\u6765\u8fbd\u4e1c\u5927\u8a00\u5929\u4e0b\u65e0\u654c\u624b \u3000\u3000\u9082\u9005\u5180\u5317\u65b9\u4fe1\u4e16\u95f4\u6709\u82f1\u96c4 \u3000\u3000\u4e0a\u6b3e\u662f\u201c\u5e0c\u5b5f\u4ec1\u5144\u6b63\u4e4b\u201d\uff0c\u4e0b\u6b3e\u662f\u201c\u5984\u4eba\u82d7\u4eba\u51e4\u6df1\u60ed\u6614\u5e74\u72c2\u8a00\u9189\u540e\u6d82\u9e26\u201d\u3002 \u3000\u3000\u4f17\u4eba\u90fd\u662f\u6c5f\u6e56\u8349\u83bd\uff0c\u4e5f\u4e0d\u660e\u767d\u5bf9\u8054\u4e0a\u7684\u5b57\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u4f3c\u4e4e\u8fd9\u82d7\u4eba\u51e4\u5bf9\u81ea\u5df1\u7684\u5916\u53f7\u611f\u5230\u60ed\u6127\u3002\u6bcf\u4e2a\u5b57\u90fd\u6df1\u5165\u6728\u91cc\uff0c\u5f53\u662f\u7528\u5229\u5668\u525c\u523b\u800c\u6210\u3002 \u3000\u3000\u5b9d\u6811\u8138\u8272\u5fae\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5bb6\u4e3b\u4eba\u8ddf\u91d1\u9762\u4f5b\u4ea4\u60c5\u53ef\u6df1\u5f97\u5f88\u54ea\u3002\u201d\u90a3\u957f\u9888\u6c49\u5b50\u9053\uff1a\u201c\u662f\uff01\u6211\u4eec\u5e84\u4e3b\u8ddf\u82d7\u5927\u4fa0\u5df2\u76f8\u4ea4\u6570\u5341\u5e74\u3002\u201d \u3000\u3000\u5b9d\u6811\u201c\u54e6\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u5218\u5143\u9e64\u4e00\u9897\u5fc3\u66f4\u662f\u6026\u6026\u8df3\u52a8\uff0c\u6697\u9053\uff1a\u201c\u6765\u5230\u82d7\u4eba\u51e4\u670b\u53cb\u7684\u5bb6\u91cc\u5566\uff0c\u6211\u8fd9\u6761\u8001\u547d\u770b\u6765\u5df2\u9001\u4e86\u4e5d\u6210\u3002\u201d\u7247\u523b\u4e4b\u95f4\uff0c\u4e24\u53ea\u624b\u638c\u4e2d\u90fd\u662f\u51b7\u6c57\u6dcb\u6f13\u3002 \u3000\u3000\u5404\u4eba\u5206\u522b\u5750\u4e0b\uff0c\u90a3\u540d\u6c49\u5b50\u547d\u4eba\u732e\u4e0a\u8336\u6765\uff0c\u7ad9\u5728\u4e0b\u9996\u76f8\u966a\u3002 \u3000\u3000\u5b9d\u6811\u8bf4\u9053\uff1a\u201c\u8fd9\u91d1\u9762\u4f5b\u5f53\u5e74\u53f7\u79f0\u2018\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u2019\uff0c\u539f\u4e5f\u592a\u8fc7\u72c2\u5984\u3002\u77a7\u8fd9\u526f\u5bf9\u8054\uff0c\u4ed6\u81ea\u5df1\u4e5f\u77e5\u9519\u4e86\u3002\u201d\u90a3\u957f\u9888\u6c49\u5b50\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u5bb6\u4e3b\u4eba\u8a00\u9053\uff0c\u8fd9\u662f\u82d7\u5927\u4fa0\u81ea\u8c26\u3002\u5176\u5b9e\u82e5\u4e0d\u662f\u592a\u7d2f\u8d58\u4e86\u4e9b\uff0c\u82d7\u5927\u4fa0\u8fd9\u5916\u53f7\u4e4b\u4e0a\uff0c\u53ea\u6015\u8fd8\u5f97\u52a0\u4e0a\u2018\u53e4\u5f80\u4eca\u6765\u2019\u56db\u5b57\u3002\u201d \u3000\u3000\u5b9d\u6811\u54fc\u4e86\u4e00\u58f0\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u563f\uff01\u4f5b\u7ecf\u4e0a\u8bf4\uff0c\u5f53\u5e74\u4f5b\u7956\u91ca\u8fe6\u725f\u5c3c\u964d\u4e16\uff0c\u4e00\u843d\u5730\u4fbf\u81ea\u79f0\u2018\u5929\u4e0a\u5929\u4e0b\u2019\u552f\u6211\u4e00\u4eba\u79f0\u72ec\u5c0a\u2019\uff0c\u8fd9\u53e5\u8bdd\u8ddf\u2018\u53e4\u5f80\u4eca\u6765\uff0c\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u2019\uff0c\u5012\u914d\u5f97\u4e0a\u5bf9\u513f\u3002\u201d \u3000\u3000\u66f9\u4e91\u5947\u542c\u4ed6\u8a00\u4e2d\u6709\u8ba5\u523a\u4e4b\u610f\uff0c\u653e\u58f0\u5927\u7b11\u3002\u90a3\u957f\u9888\u6c49\u5b50\u6012\u76ee\u76f8\u89c6\uff0c\u8bf4\u9053\uff1a\u201c\u8d35\u5ba2\u653e\u5c0a\u91cd\u4e9b\u3002\u201d\u66f9\u4e91\u5947\u6115\u7136\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u82e5\u662f\u91d1\u9762\u4f5b\u77e5\u4f60\u7b11\u4ed6\uff0c\u53ea\u6015\u8d35\u5ba2\u987b\u4e0d\u65b9\u4fbf\u3002\u201d\u66f9\u4e91\u5947\u9053\uff1a\u201c\u6b66\u5b66\u4e4b\u9053\u65e0\u7a77\uff0c\u8981\u77e5\u5929\u5916\u6709\u5929\uff0c\u4eba\u4e0a\u6709\u4eba\u3002\u4ed6\u4e5f\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u5c31\u7b97\u672c\u9886\u518d\u9ad8\uff0c\u600e\u79f0\u5f97\u2018\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u2019\u4e03\u5b57\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u5c0f\u4eba\u89c1\u8bc6\u9119\u964b\uff0c\u4e0d\u660e\u4e16\u4e8b\u3002\u53ea\u662f\u655d\u4e0a\u8bf4\u79f0\u5f97\uff0c\u60f3\u6765\u5fc5\u5b9a\u79f0\u5f97\u3002\u201d\u66f9\u4e91\u5947\u542c\u4ed6\u8a00\u8bed\u8c26\u4e0b\uff0c\u795e\u8272\u5374\u4f46\u662f\u4e0d\u606d\uff0c\u5fc3\u4e2d\u6012\u6c14\u4e0a\u51b2\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u662f\u4e00\u6d3e\u638c\u95e8\uff0c\u7109\u80fd\u53d7\u4f60\u8fd9\u4f4e\u4e09\u4e0b\u56db\u7684\u4f63\u4ec6\u4e4b\u6c14\uff1f\u201d\u5f53\u5373\u51b7\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u9664\u4e86\u91d1\u9762\u4f5b\uff0c\u60f3\u6765\u8d35\u4e3b\u4eba\u7b97\u5f97\u7b2c\u4e00\u4e86\uff1f\u563f\u563f\uff0c\u53ef\u7b11\uff01\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u8fd9\u4e2a\u5c82\u6562\uff01\u201d\u4f38\u624b\u5728\u66f9\u4e91\u5947\u6240\u5750\u7684\u6905\u80cc\u4e0a\u8f7b\u8f7b\u4e00\u62cd\u3002\u66f9\u4e91\u5947\u53ea\u611f\u6905\u5b50\u4e00\u9707\uff0c\u8eab\u5b50\u5411\u4e0a\u4e00\u5f39\u3002\u4ed6\u624b\u4e2d\u6b63\u62ff\u7740\u8336\u7897\uff0c\u8fd9\u4e00\u4e0b\u51fa\u5176\u4e0d\u610f\uff0c\u8336\u7897\u8131\u624b\u6389\u843d\uff0c\u773c\u89c1\u8981\u5728\u5730\u4e0b\u8dcc\u5f97\u7c89\u788e\uff0c\u90a3\u6c49\u5b50\u4fef\u8eab\u4e00\u6284\uff0c\u5df2\u5c06\u8336\u7897\u63a5\u4f4f\uff0c\u9053\uff1a\u201c\u8d35\u5ba2\u5c0f\u5fc3\u4e86\u3002\u201d\u66f9\u4e91\u5947\u6ee1\u8138\u901a\u7ea2\uff0c\u8f6c\u8fc7\u5934\u4e0d\u7406\u3002\u90a3\u6c49\u5b50\u81ea\u884c\u5c06\u8336\u7897\u653e\u5728\u51e0\u4e0a\u3002 \u3000\u3000\u5b9d\u6811\u5bf9\u8fd9\u4e8b\u89c6\u82e5\u4e0d\u89c1\uff0c\u5411\u90a3\u957f\u9888\u6c49\u5b50\u9053\uff1a\u201c\u9664\u4e86\u91d1\u9762\u4f5b\u8ddf\u8001\u8872\u4e4b\u5916\uff0c\u4f60\u4e3b\u4eba\u8fd8\u7ea6\u4e86\u8c01\u6765\u52a9\u62f3\uff1f\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u4e3b\u4eba\u4e34\u53bb\u65f6\u5429\u5490\u5c0f\u4eba\uff0c\u8bf4\u9752\u85cf\u6d3e\u7384\u51a5\u5b50\u9053\u957f\u3001\u6606\u4ed1\u5c71\u7075\u6e05\u5c45\u58eb\u3001\u6cb3\u5357\u592a\u6781\u95e8\u848b\u8001\u62f3\u5e08\u8fd9\u51e0\u4f4d\uff0c\u65e5\u5185\u90fd\u8981\u4e0a\u5c71\uff0c\u5631\u5490\u5c0f\u4eba\u597d\u597d\u4f8d\u5949\u3002 \u3000\u3000\u5927\u5e08\u7b2c\u4e00\u4f4d\u5230\uff0c\u8db3\u89c1\u76db\u60c5\uff0c\u655d\u4e0a\u77e5\u9053\u4e86\uff0c\u5fc5\u5b9a\u611f\u6fc0\u5f97\u7d27\u3002\u201d \u3000\u3000\u5b9d\u6811\u5927\u5e08\u53d7\u6b64\u95f4\u4e3b\u4eba\u4e4b\u9080\uff0c\u53ea\u9053\u81ea\u5df1\u4e00\u5230\uff0c\u4fbf\u6709\u5929\u5927\u7684\u68d8\u624b\u4e4b\u4e8b\u4e5f\u5fc5\u8fce\u5203\u800c\u89e3\uff0c\u5c82\u77e5\u9664\u4e86\u81ea\u5df1\u4e4b\u5916\uff0c\u4e3b\u4eba\u8fd8\u9080\u4e86\u8fd9\u8bb8\u591a\u6210\u540d\u4eba\u7269\u3002\u8fd9\u4e9b\u4eba\u81ea\u5df1\u867d\u5927\u90fd\u672a\u89c1\u8fc7\u9762\uff0c\u5374\u90fd\u7d20\u6765\u95fb\u540d\uff0c\u65e0\u4e00\u4e0d\u662f\u6b66\u6797\u4e2d\u9876\u513f\u5c16\u513f\u7684\u9ad8\u624b\uff0c\u65e9\u77e5\u4e3b\u4eba\u9080\u4e86\u8fd9\u8bb8\u591a\u4eba\uff0c\u5012\u4e0d\u5982\u4e0d\u6765\u4e86\uff0c\u90a3\u91d1\u9762\u4f5b\u82d7\u4eba\u51e4\u66f4\u662f\u8fdc\u800c\u907f\u4e4b\u7684\u4e3a\u5999\uff1b\u517c\u4e4b\u81ea\u5df1\u8fdc\u6765\u76f8\u52a9\uff0c\u4e3b\u4eba\u5374\u4e0d\u5728\u5bb6\u63a5\u5ba2\uff0c\u672a\u514d\u751a\u662f\u4e0d\u656c\uff0c\u5fc3\u4e0b\u4e0d\u5feb\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u8872\u56fa\u7136\u4e0d\u4e2d\u7528\uff0c\u4f46\u91d1\u9762\u4f5b\u4e00\u5230\uff0c\u8fd8\u6709\u529e\u4e0d\u4e86\u7684\u4e8b\u5417\uff1f \u3000\u3000\u4f55\u5fc5\u518d\u53e6\u7ea6\u65c1\u4eba\uff1f\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u655d\u4e0a\u8a00\u9053\uff0c\u4e58\u6b64\u673a\u4f1a\uff0c\u548c\u4f17\u5bb6\u82f1\u96c4\u805a\u805a\u3002\u5174\u6c49\u4e10\u5e2e\u7684\u8303\u5e2e\u4e3b\u4e5f\u8981\u6765\u3002\u201d\u5b9d\u6811\u4e00\u51db\uff0c\u9053\uff1a\u201c\u8303\u5e2e\u4e3b\u4e5f\u6765\uff1f\u90a3\u98de\u72d0\u5230\u5e95\u7ea6\u4e86\u591a\u5c11\u5e2e\u624b\uff1f\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u4e0d\u7ea6\u5e2e\u624b\uff0c\u5c31\u53ea\u5b64\u8eab\u4e00\u4eba\u3002\u201d \u3000\u3000\u962e\u58eb\u4e2d\u3001\u6bb7\u5409\u3001\u9676\u767e\u5c81\u7b49\u5747\u662f\u4e45\u5386\u6c5f\u6e56\u4e4b\u4eba\uff0c\u4e00\u542c\u5b64\u8eab\u6765\u72af\uff0c\u800c\u8fd9\u91cc\u4e3b\u4eba\u5e03\u7f6e\u4e86\u8bb8\u591a\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\u4e4b\u5916\uff0c\u8fd8\u8981\u53bb\u8bf7\u91d1\u9762\u4f5b\u4e0e\u4e10\u5e2e\u8303\u5e2e\u4e3b\u6765\u52a9\u62f3\uff0c\u90fd\u60f3\u8fd9\u5c31\u7b97\u6709\u4e09\u5934\u516d\u81c2\uff0c\u4e5f\u7528\u4e0d\u7740\u5bf9\u4ed6\u5982\u6b64\u5927\u52a8\u5e72\u6208\u3002\u773c\u89c1\u8fd9\u5b9d\u6811\u548c\u5c1a\u6b66\u529f\u5982\u6b64\u4e86\u5f97\uff0c\u5355\u662f\u4ed6\u4e00\u4eba\uff0c\u591a\u534a\u4e5f\u8db3\u4ee5\u5e94\u4ed8\uff0c\u4f55\u51b5\u6211\u4eec\u4e0a\u5f97\u5c71\u6765\uff0c\u5230\u65f6\u4e5f\u4e0d\u4f1a\u8896\u624b\u65c1\u89c2\uff0c\u53ea\u4e0d\u8fc7\u5f53\u65f6\u4e3b\u4eba\u6599\u4e0d\u5230\u4f1a\u6709\u8fd9\u8bb8\u591a\u4e0d\u901f\u4e4b\u5ba2\u800c\u5df2\u3002 \u3000\u3000\u5176\u4e2d\u5218\u5143\u9e64\u5fc3\u4e2d\uff0c\u5374\u5982\u5341\u4e94\u4e2a\u540a\u6876\u6253\u6c34\uff0c\u4e03\u4e0a\u516b\u4e0b\u3002\u539f\u6765\u4e10\u5e2e\u7d20\u6765\u4e0e\u671d\u5ef7\u4f5c\u5bf9\uff0c\u5728\u5e2e\u540d\u4e0a\u52a0\u4e0a\u201c\u5174\u6c49\u201d\u4e8c\u5b57\uff0c\u79f0\u4e3a\u201c\u5174\u6c49\u4e10\u5e2e\u201d\uff0c\u663e\u662f\u6709\u53cd\u6e05\u4e4b\u610f\u3002\u4e0a\u4e2a\u6708\u5fa1\u524d\u4f8d\u536b\u603b\u7ba1\u8d5b\u603b\u7ba1\u4eb2\u7387\u5927\u5185\u4f8d\u536b\u5341\u516b\u9ad8\u624b\uff0c\u5c06\u8303\u5e2e\u4e3b\u64d2\u4f4f\u5173\u5165\u5929\u7262\u3002\u8fd9\u4e8b\u505a\u5f97\u751a\u662f\u673a\u5bc6\uff0c\u6c5f\u6e56\u4e0a\u77e5\u8005\u6781\u5c11\u3002\u5218\u5143\u9e64\u81ea\u5df1\u5c31\u662f\u8fd9\u5927\u5185\u5341\u516b\u9ad8\u624b\u4e4b\u4e00\u3002\u4eca\u65e5\u80e1\u91cc\u80e1\u6d82\u7684\u6df1\u5165\u864e\u7a74\uff0c\u5b9a\u7136\u662f\u51f6\u591a\u5409\u5c11\u3002 \u3000\u3000\u5b9d\u6811\u89c1\u5218\u5143\u9e64\u542c\u5230\u8303\u5e2e\u4e3b\u4e4b\u540d\u65f6\uff0c\u8138\u8272\u5fae\u53d8\uff0c\u95ee\u9053\uff1a\u201c\u5218\u5927\u4eba\u8bc6\u5f97\u8303\u5e2e\u4e3b\u4e48\uff1f\u201d\u5218\u5143\u9e64\u5fd9\u9053\uff1a\u201c\u4e0d\u8bc6\u3002\u5728\u4e0b\u53ea\u77e5\u8303\u5e2e\u4e3b\u662f\u5317\u9053\u4e0a\u54cd\u5f53\u5f53\u7684\u82f1\u96c4\u597d\u6c49\uff0c\u5f53\u5e74\u8d64\u624b\u7a7a\u62f3\uff0c\u66fe\u4ee5\u2018\u9f99\u722a\u64d2\u62ff\u624b\u2019\u6293\u6b7b\u8fc7\u4e24\u5934\u731b\u864e\u3002\u201d \u3000\u3000\u5b9d\u6811\u5fae\u5fae\u4e00\u7b11\uff0c\u4e0d\u518d\u7406\u4ed6\uff0c\u8f6c\u5934\u95ee\u90a3\u957f\u9888\u6c49\u5b50\u9053\uff1a\u201c\u90a3\u5230\u5e95\u662f\u4f55\u7b49\u6837\u4eba\uff1f\u4ed6\u4e0e\u4f60\u5bb6\u4e3b\u4eba\u53c8\u7ed3\u4e0b\u4e86\u4ec0\u4e48\u6881\u5b50\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u4e3b\u4eba\u4e0d\u66fe\u8bf4\u8d77\uff0c\u5c0f\u7684\u4e0d\u6562\u591a\u95ee\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u7ae5\u4ec6\u5949\u4e0a\u996d\u9152\uff0c\u5728\u8fd9\u96ea\u5c71\u7edd\u9876\uff0c\u5c45\u7136\u80b4\u7cbe\u9152\u7f8e\uff0c\u5927\u51fa\u4f17\u4eba\u610f\u6599\u4e4b\u5916\u3002\u90a3\u957f\u9888\u6c49\u5b50\u9053\uff1a\u201c\u4e3b\u4eba\u5a18\u5b50\u591a\u8c22\u5404\u4f4d\u5149\u4e34\uff0c\u5404\u4f4d\u591a\u996e\u51e0\u676f\u3002\u201d\u4f17\u4eba\u8c22\u4e86\u3002 \u3000\u3000\u5e2d\u4e0a\u66f9\u4e91\u5947\u4e0e\u9676\u5b50\u5b89\u6012\u76ee\u76f8\u5411\uff0c\u718a\u5143\u732e\u4e0e\u5468\u4e91\u9633\u5404\u81ea\u78e8\u62f3\u64e6\u638c\uff0c\u9676\u767e\u5c81\u5bf9\u90d1\u4e09\u5a18\u6068\u4e0d\u5f97\u4e00\u97ad\u6253\u53bb\uff0c\u867d\u7136\u5171\u684c\u996e\u98df\uff0c\u5374\u662f\u5404\u6000\u5fc3\u75c5\u3002\u53ea\u6709\u5b9d\u6811\u8a00\u7b11\u81ea\u82e5\uff0c\u5927\u5757\u5403\u8089\uff0c\u5927\u7897\u559d\u9152\uff0c\u6ee1\u5634\u7c97\u8a00\u79fd\u8bed\uff0c\u54ea\u91cc\u50cf\u4e2a\u51fa\u5bb6\u4eba\u7684\u6a21\u6837\uff1f \u3000\u3000\u9152\u8fc7\u6570\u5de1\uff0c\u4e00\u540d\u4ec6\u4eba\u6367\u4e0a\u4e00\u76d8\u70ed\u6c14\u817e\u817e\u7684\u9992\u5934\uff0c\u5404\u4eba\u7d2f\u4e86\u534a\u65e5\uff0c\u65e9\u5c31\u997f\u4e86\uff0c\u89c1\u5230\u9992\u5934\uff0c\u90fd\u662f\u5927\u5408\u5fc3\u610f\uff0c\u6b63\u8981\u4f38\u624b\u53bb\u62ff\uff0c\u5ffd\u542c\u5f97\u7a7a\u4e2d\u55e4\u7684\u4e00\u58f0\u54cd\uff0c\u4f17\u4eba\u4e00\u9f50\u62ac\u5934\uff0c\u53ea\u89c1\u4e00\u679a\u706b\u7bad\u6a2a\u8fc7\u5929\u7a7a\uff0c\u5c04\u5230\u9ad8\u5904\uff0c\u5fae\u5fae\u4e00\u987f\uff0c\u5ffd\u7136\u70b8\u4e86\u5f00\u6765\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u539f\u6765\u662f\u4e2a\u5f69\u8272\u7f24\u7eb7\u7684\u70df\u82b1\uff0c\u7f13\u7f13\u6563\u5f00\uff0c\u9690\u7ea6\u662f\u4e00\u53ea\u751f\u4e86\u7fc5\u8180\u7684\u72d0\u72f8\u3002\u5b9d\u6811\u63a8\u5e2d\u800c\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u5230\u4e86\u3002\u201d \u3000\u3000\u4f17\u4eba\u5c3d\u7686\u53d8\u8272\u3002\u90a3\u957f\u9888\u6c49\u5b50\u5411\u5b9d\u6811\u8bf7\u4e86\u4e2a\u5b89\uff0c\u8bf4\u9053\uff1a\u201c\u655d\u4e0a\u672a\u56de\uff0c\u5bf9\u5934\u5ffd\u7136\u6765\u5230\uff0c\u6b64\u95f4\u4e00\u5207\uff0c\u5168\u4ed7\u5927\u5e08\u4e3b\u6301\u3002\u201d\u5b9d\u6811\u9053\uff1a\u201c\u6709\u6211\u5462\uff0c\u4f60\u4e0d\u7528\u614c\u3002\u4fbf\u8bf7\u4ed6\u4e0a\u6765\u5427\u3002\u201d\u90a3\u6c49\u5b50\u8e0c\u8e87\u9053\uff1a\u201c\u5c0f\u7684\u6709\u8bdd\u4e0d\u6562\u8bf4\u3002\u201d\u5b9d\u6811\u9053\uff1a\u201c\u4f46\u8bf4\u65e0\u59a8\u3002\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u8fd9\u96ea\u5cf0\u5929\u9669\uff0c\u8c05\u90a3\u98de\u72d0\u65e0\u6cd5\u4e0a\u6765\u3002\u5c0f\u4eba\u60f3\u8bf7\u5927\u5e08\u4e0b\u53bb\u8ddf\u4ed6\u8bf4\uff0c\u4e3b\u4eba\u5e76\u4e0d\u5728\u5bb6\u3002\u201d \u3000\u3000\u5b9d\u6811\u8bf4\uff1a\u201c\u4f60\u540a\u4ed6\u4e0a\u6765\uff0c\u6211\u4f1a\u5bf9\u4ed8\u3002\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u5c31\u6015\u4ed6\u4e0a\u5cf0\u4e4b\u540e\uff0c\u60ca\u52a8\u4e86\u4e3b\u6bcd\uff0c\u5c0f\u7684\u6ca1\u8138\u6765\u89c1\u4e3b\u4eba\u3002\u201d \u3000\u3000\u5b9d\u6811\u8138\u4e00\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6015\u6211\u5bf9\u4ed8\u4e0d\u4e86\u98de\u72d0\u4e48\uff1f\u201d\u90a3\u957f\u9888\u6c49\u5b50\u5fd9\u53c8\u8bf7\u4e86\u4e2a\u5b89\uff0c\u9053\uff1a\u201c\u5c0f\u7684\u4e0d\u6562\u3002\u201d\u5b9d\u6811\u9053\uff1a\u201c\u4f60\u8ba9\u4ed6\u4e0a\u6765\u5c31\u662f\u3002\u201d\u90a3\u6c49\u5b50\u65e0\u5948\uff0c\u53ea\u5f97\u5e94\u4e86\uff0c\u6084\u6084\u4e0e\u53e6\u4e00\u540d\u4f8d\u4ec6\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u60f3\u662f\u53eb\u4ed6\u591a\u52a0\u63d0\u9632\uff0c\u4fdd\u62a4\u4e3b\u6bcd\u3002 \u3000\u3000\u5b9d\u6811\u77a7\u5728\u773c\u91cc\uff0c\u5fae\u5fae\u51b7\u7b11\uff0c\u5374\u4e0d\u8a00\u8bed\uff0c\u547d\u4eba\u64a4\u4e86\u5e2d\u3002\u5404\u4eba\u6563\u5750\u559d\u6761\uff0c\u53ea\u559d\u4e86\u4e00\u76cf\u8336\uff0c\u90a3\u957f\u9888\u6c49\u5b50\u9ad8\u58f0\u62a5\u9053\uff1a\u201c\u5ba2\u4eba\u5230\uff01\u201d \u3000\u3000\u4e24\u6247\u5927\u95e8\u201c\u5440\u201d\u7684\u4e00\u58f0\u5f00\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u505c\u76cf\u4e0d\u996e\uff0c\u51dd\u76ee\u671b\u7740\u5927\u95e8\uff0c\u5374\u89c1\u95e8\u4e2d\u5e76\u80a9\u8fdb\u6765\u4e24\u540d\u7ae5\u513f\u3002\u8fd9\u4e24\u540d\u7ae5\u4e00\u822c\u9ad8\u77ee\uff0c\u7ea6\u83ab\u5341\u4e09\u56db\u5c81\u5e74\u7eaa\uff0c\u8eab\u7a7f\u767d\u8272\u8c82\u88d8\uff0c\u5934\u9876\u7528\u7ea2\u4e1d\u7ed3\u7740\u4e24\u6839\u7ad6\u7acb\u7684\u5c0f\u8fab\uff0c\u80cc\u4e0a\u5404\u8d1f\u4e00\u67c4\u957f\u5251\u3002\u8fd9\u4e24\u4eba\u7709\u76ee\u5982\u753b\uff0c\u5f62\u76f8\u4fca\u96c5\uff0c\u6700\u5947\u7684\u662f\u9762\u8c8c\u4e00\u6a21\u4e00\u6837\uff0c\u6beb\u65e0\u5206\u522b\uff0c\u53ea\u662f\u8d70\u5728\u53f3\u8fb9\u90a3\u7ae5\u513f\u7684\u5251\u67c4\u659c\u5728\u53f3\u80a9\uff0c\u53e6\u4e00\u4e2a\u7ae5\u513f\u7684\u5251\u67c4\u659c\u5728\u5de6\u80a9\uff0c\u624b\u4e2d\u591a\u6367\u4e86\u4e00\u53ea\u62dc\u76d2\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u4e86\u8fd9\u4e24\u4e2a\u7ae5\u513f\u7684\u6a21\u6837\uff0c\u90fd\u611f\u6115\u7136\uff0c\u5fc3\u4e2d\u5374\u5747\u662f\u4e00\u5bbd\uff0c\u672c\u4ee5\u4e3a\u6765\u7684\u662f\u90a3\u7a77\u51f6\u6781\u6076\u7684\u201c\u201d\uff0c\u54ea\u77e5\u5374\u662f\u4e24\u4e2a\u4e2a\u5c0f\u5b69\u7ae5\u3002\u5f85\u8fd9\u4e24\u4eba\u8d70\u8fd1\uff0c\u53ea\u89c1\u4e24\u4eba\u6bcf\u6839\u5c0f\u8fab\u513f\u4e0a\u5404\u7cfb\u4e00\u9897\u660e\u73e0\uff0c\u56db\u9897\u73e0\u5b50\u90fd\u662f\u5c0f\u6307\u5934\u822c\u5927\u5c0f\uff0c\u53d1\u51fa\u6de1\u6de1\u5149\u5f69\u3002\u718a\u5143\u732e\u662f\u9556\u5c40\u7684\u9556\u5934\uff0c\u9676\u767e\u5c81\u4e45\u5728\u7eff\u6797\uff0c\u8bc6\u522b\u5b9d\u7269\u7684\u773c\u5149\u5747\u9ad8\uff0c\u4e00\u89c1\u56db\u9897\u5927\u73e0\uff0c\u90fd\u662f\u6026\u7136\u5fc3\u52a8\uff1a\u201c\u8fd9\u56db\u9897\u5b9d\u73e0\u53ef\u8d35\u91cd\u5f97\u5f88\u54ea\uff0c\u4e24\u4eba\u6240\u7a7f\u7684\u8c82\u88d8\u6ca1\u4e00\u6839\u6742\u6bdb\uff0c\u4e5f\u662f\u96be\u5f97\u4e4b\u6781\u3002\u5c31\u7b97\u662f\u5927\u5bcc\u5927\u8d35\u4e4b\u5bb6\uff0c\u4e5f\u672a\u5fc5\u6709\u6b64\u73cd\u7269\u3002\u201d \u3000\u3000\u4e24\u4e2a\u7ae5\u513f\u89c1\u5b9d\u6811\u5750\u5728\u6b63\u4e2d\uff0c\u4e0a\u524d\u8eac\u8eab\u884c\u793c\uff0c\u5de6\u8fb9\u90a3\u7ae5\u513f\u9ad8\u4e3e\u62dc\u76d2\u3002\u90a3\u957f\u9888\u6c49\u5b50\u63a5\u4e86\u8fc7\u6765\uff0c\u6253\u5f00\u76d2\u5b50\uff0c\u5448\u5230\u5b9d\u6811\u9762\u524d\u3002 \u3000\u3000\u5b9d\u6811\u89c1\u76d2\u4e2d\u662f\u4e00\u5f20\u5927\u7ea2\u5e16\u5b50\uff0c\u53d6\u51fa\u4e00\u770b\uff0c\u89c1\u4e0a\u9762\u6d53\u58a8\u5199\u7740\u4e00\u884c\u5b57\u9053\uff1a\u201c\u665a\u751f\u80e1\u6590\u8c28\u62dc\u3002\u96ea\u5cf0\u4e4b\u4f1a\uff0c\u8c28\u4e8e\u4eca\u65e5\u5348\u65f6\u8df5\u7ea6\u3002\u201d\u5b57\u8ff9\u751a\u662f\u96c4\u52b2\u633a\u62d4\u3002 \u3000\u3000\u5b9d\u6811\u89c1\u4e86\u201c\u80e1\u6590\u201d\u4e24\u5b57\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u55ef\uff0c\u98de\u72d0\u7684\u5916\u53f7\uff0c\u539f\u6765\u662f\u5c06\u4ed6\u540d\u5b57\u5012\u8f6c\u800c\u6210\u3002\u201d\u5f53\u4e0b\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u4f60\u5bb6\u4e3b\u4eba\u5230\u4e86\u4e48\uff1f\u201d\u53f3\u8fb9\u90a3\u7ae5\u513f\u9053\uff1a\u201d\u4e3b\u4eba\u8bf4\u5348\u65f6\u51c6\u5230\uff0c\u56e0\u6050\u8d24\u4e3b\u4eba\u4e45\u5019\uff0c\u7279\u547d\u5c0f\u7684\u524d\u6765\u6295\u523a\u3002\u201d\u4ed6\u8bf4\u8bdd\u8bed\u58f0\u6e05\u8106\uff0c\u7ae5\u97f3\u672a\u8131\u3002\u5b9d\u6811\u89c1\u4e24\u7ae5\u751f\u5f97\u53ef\u7231\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u662f\u53cc\u751f\u5144\u5f1f\u4e48\uff1f\u201d\u90a3\u7ae5\u513f\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u6ca1\u7740\u884c\u4e86\u4e00\u793c\uff0c\u8f6c\u8eab\u4fbf\u51fa\u3002\u90a3\u957f\u9888\u6c49\u5b50\u9053\uff1a\u201c\u5144\u5f1f\u5c11\u7559\uff0c\u5403\u4e9b\u70b9\u5fc3\u518d\u53bb\u3002\u201d\u53f3\u8fb9\u90a3\u7ae5\u5b50\u9053\uff1a\u201c\u591a\u8c22\u5927\u54e5\uff0c\u672a\u5f97\u5bb6\u4e3b\u4e4b\u547d\uff0c\u4e0d\u6562\u9017\u7559\u3002\u201d\u7530\u9752\u6587\u4ece\u679c\u76d8\u91cc\u53d6\u4e86\u4e9b\u679c\u5b50\uff0c\u9012\u7ed9\u4e24\u4eba\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u5403\u4e9b\u679c\u513f\u3002\u201d\u5de6\u8fb9\u90a3\u7ae5\u513f\u63a5\u4e86\uff0c\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u3002\u201d \u3000\u3000\u66f9\u4e91\u5947\u6700\u662f\u5992\u5fcc\uff0c\u517c\u4e4b\u6027\u5982\u70c8\u706b\uff0c\u534a\u5206\u513f\u90fd\u5fcd\u8010\u4e0d\u5f97\uff0c\u89c1\u7530\u9752\u6587\u5bf9\u4e24\u4eba\u795e\u6001\u4eb2\u5bc6\uff0c\u5fc3\u4e2d\u6012\u6c14\u5df2\u751f\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u5c0f\u5c0f\u5b69\u7ae5\uff0c\u5c45\u7136\u80cc\u8d1f\u957f\u5251\uff0c\u96be\u9053\u4f60\u4eec\u4e5f\u4f1a\u5251\u672f\u4e48\uff1f\u201d\u4e24\u7ae5\u6115\u7136\u5411\u4ed6\u671b\u4e86\u4e00\u773c\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u5c0f\u7684\u4e0d\u4f1a\u3002\u201d\u66f9\u4e91\u5947\u559d\u9053\uff1a\u201c\u90a3\u4e48\u88c5\u6a21\u4f5c\u6837\u7684\u80cc\u7740\u5251\u5e72\u4e48\uff1f\u7ed9\u6211\u7559\u4e0b\u4e86\u3002\u201d\u4f38\u51fa\u53cc\u624b\uff0c\u53bb\u6293\u4e24\u4eba\u80cc\u4e0a\u957f\u5251\u7684\u5251\u67c4\u3002 \u3000\u3000\u4e24\u4e2a\u7ae5\u513f\u7edd\u672a\u60f3\u5230\u6b64\u65f6\u6709\u4eba\u8981\u593a\u4ed6\u4eec\u5175\u5668\uff0c\u66f9\u4e91\u5947\u51fa\u624b\u53c8\u662f\u6781\u5feb\uff0c\u53ea\u542c\u5237\u5237\u4e24\u58f0\uff0c\u4f17\u4eba\u773c\u524d\u9752\u5149\u95ea\u52a8\uff0c\u4e24\u67c4\u957f\u5251\u8131\u9798\u800c\u51fa\uff0c\u90fd\u5df2\u88ab\u4ed6\u62a2\u5728\u624b\u4e2d\u3002\u66f9\u4e91\u5947\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e24\u4e2a\u5c0f\u2026\u2026\u201d\u7b2c\u4e94\u5b57\u672a\u51fa\u53e3\uff0c\u4e24\u4e2a\u7ae5\u513f\u4e00\u9f50\u7eb5\u8d77\uff0c\u4e00\u51fa\u5de6\u624b\uff0c\u4e00\u51fa\u53f3\u624b\uff0c\u8fc5\u901f\u4e4b\u6781\u7684\u6309\u5728\u66f9\u4e91\u5947\u9888\u4e2d\u3002\u4e24\u4eba\u540c\u65f6\u5411\u524d\u4e00\u6273\uff0c\u66f9\u4e91\u5947\u5f85\u8981\u62db\u67b6\uff0c\u53cc\u811a\u88ab\u4e24\u4eba\u4e00\u51fa\u5de6\u811a\u3001\u4e00\u51fa\u53f3\u811a\u7684\u4e00\u52fe\uff0c\u767b\u65f6\u8eab\u4e0d\u7531\u4e3b\u7684\u5728\u7a7a\u4e2d\u7ffb\u4e86\u534a\u4e2a\u7b4b\u6597\uff0c\u5566\u7684\u4e00\u58f0\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u6454\u5728\u5730\u4e0b\u3002 \u3000\u3000\u4ed6\u593a\u5251\u56fa\u5feb\uff0c\u8fd9\u4e00\u4ea4\u6454\u5f97\u66f4\u5feb\uff0c\u4f17\u4eba\u4e00\u6115\u5f97\u4e4b\u4e0b\uff0c\u4e24\u7ae5\u5411\u524d\u6251\u4e0a\uff0c\u8981\u593a\u56de\u4ed6\u624b\u4e2d\u957f\u5251\u3002\u66f9\u4e91\u5947\u5c82\u662f\u5f31\u8005\uff0c\u9002\u624d\u53ea\u56e0\u672a\u53ca\u9632\u5907\uff0c\u65b9\u7740\u4e86\u9053\u513f\uff0c\u4ed6\u4e00\u843d\u5730\u7acb\u5373\u7eb5\u8d77\uff0c\u53cc\u5251\u7ad6\u7acb\uff0c\u8981\u5c06\u4e24\u7ae5\u5413\u9000\u3002\u4e0d\u6599\u4e24\u7ae5\u4e00\u7eb5\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u4e00\u4eba\u4e00\u624b\u53c8\u5df2\u6500\u5728\u4ed6\u7684\u9888\u4e2d\uff0c\u4e00\u6273\u4e00\u52fe\uff0c\u62db\u5f0f\u4fbf\u548c\u5148\u524d\u7684\u5168\u65e0\u5206\u522b\uff0c\u66f9\u4e91\u5947\u53c8\u662f\u556a\u7684\u6454\u4e86\u4e00\u4ea4\u3002 \u3000\u3000\u7b2c\u4e00\u4ea4\u8fd8\u53ef\u8bf4\u662f\u7ed9\u4e24\u7ae5\u653b\u5176\u65e0\u5907\uff0c\u8fd9\u7b2c\u4e8c\u4ea4\u5374\u6454\u5f97\u66f4\u91cd\u3002 \u3000\u3000\u4ed6\u662f\u5929\u9f99\u95e8\u7684\u638c\u95e8\uff0c\u6b63\u5f53\u5e74\u5bcc\u529b\u58ee\uff0c\u4e24\u7ae5\u7ad9\u7740\u53ea\u53ca\u5230\u4ed6\u7684\u80f8\u53e3\uff0c\u4e8c\u6b21\u53c8\u8dcc\uff0c\u6559\u4ed6\u8138\u4e0a\u5982\u4f55\u4e0b\u5f97\u6765\uff1f\u72c2\u6012\u4e4b\u4e0b\uff0c\u6740\u5fc3\u987f\u8d77\uff0c\u4eba\u672a\u7eb5\u8d77\uff0c\u5de6\u5251\u4e0b\u5782\uff0c\u53f3\u5251\u7a81\u7136\u6a2a\u5288\uff0c\u8981\u5c06\u4e24\u4e2a\u7ae5\u513f\u7acb\u6bd9\u5251\u4e0b\u3002 \u3000\u3000\u7530\u9752\u6587\u89c1\u4ed6\u8fd9\u4e00\u62db\u662f\u672c\u95e8\u4e2d\u7684\u6740\u624b\u201c\u4e8c\u90ce\u62c5\u5c71\u201d\uff0c\u62db\u6570\u72e0\u8fa3\uff0e\u5373\u4ee4\u6b66\u529f\u9ad8\u5f3a\u4e4b\u4eba\uff0c\u4e00\u65f6\u4e5f\u96be\u4ee5\u62db\u67b6\uff0c\u773c\u89c1\u8fd9\u4e00\u53cc\u7389\u96ea\u53ef\u7231\u7684\u5b69\u5b50\u8981\u6b7b\u4e8e\u975e\u547d\uff0c\u5fd9\u53eb\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u4f11\u4e0b\u6740\u62db\u3002\u201d \u3000\u3000\u66f9\u4e91\u5947\u6325\u5251\u524a\u51fa\uff0c\u542c\u5f97\u7530\u9752\u6587\u53eb\u558a\uff0c\u4ed6\u867d\u7d20\u6765\u542c\u4ece\u8fd9\u5e08\u59b9\u7684\u8a00\u8bba\uff0c\u4f46\u62db\u5df2\u9012\u51fa\uff0c\u6025\u5207\u95f4\u6536\u5251\u4e0d\u53ca\uff0c\u5f53\u4e0b\u8155\u529b\u4e00\u6c89\uff0c\u5fc3\u60f3\u5728\u4e24\u4e2a\u5c0f\u5b50\u80f8\u53e3\u7559\u4e2a\u8bb0\u53f7\u4e5f\u5c31\u7f62\u4e86\u3002\u54ea\u77e5\u5de6\u8fb9\u7684\u7ae5\u513f\u5ffd\u4ece\u4ed6\u814b\u4e0b\u94bb\u5230\u53f3\u8fb9\uff0c\u53f3\u8fb9\u7684\u7ae5\u513f\u5374\u94bb\u5230\u4e86\u5de6\u8fb9\u3002\u4ed6\u4e00\u5251\u767b\u65f6\u524a\u7a7a\uff0c\u6b63\u8981\u6536\u62db\u518d\u53d1\uff0c\u7a81\u89c9\u4e24\u65c1\u4eba\u5f71\u95ea\u52a8\uff0c\u4e24\u4e2a\u5c0f\u5c0f\u7684\u8eab\u8eaf\u53c8\u5df2\u6251\u5230\u3002 \u3000\u3000\u66f9\u4e91\u5947\u5403\u8fc7\u4e24\u6b21\u82e6\u5934\uff0c\u53ef\u662f\u957f\u5251\u5728\u5916\uff0c\u500f\u5ffd\u95f4\u96be\u4ee5\u56de\u523a\uff0c\u773c\u89c1\u8fd9\u602a\u62db\u53c8\u6765\uff0c\u4ecd\u662f\u65e0\u6cd5\u62c6\u67b6\u95ea\u907f\uff0c\u5f53\u5373\u53cc\u5251\u6492\u624b\uff0c\u5e73\u638c\u5411\u5916\u63a8\u51fa\uff0c\u559d\u4e00\u58f0\u201c\u53bb\uff01\u201d\u4e24\u638c\u4e0a\u5404\u7528\u4e86\u5341\u6210\u529b\uff0c\u4e24\u4e2a\u7ae5\u513f\u53ea\u8981\u7ed9\u638c\u7f18\u626b\u4e0a\u4e86\uff0c\u4e5f\u975e\u5f97\u53d7\u4f24\u4e0d\u53ef\u3002\u7a81\u89c1\u4eba\u5f71\u4e00\u95ea\uff0c\u4e24\u4e2a\u7ae5\u513f\u5ffd\u7136\u4e0d\u89c1\uff0c\u6025\u5fd9\u8f6c\u8fc7\u8eab\u6765\uff0c\u53ea\u89c1\u5de6\u7ae5\u77ee\u8eab\u7a9c\u5230\u53f3\u8fb9\uff0c\u53f3\u7ae5\u77ee\u8eab\u7a9c\u5230\u5de6\u8fb9\uff0c\u773c\u775b\u4e00\u82b1\uff0c\u9879\u9888\u53c8\u88ab\u4e24\u4eba\u6500\u4f4f\u3002 \u3000\u3000\u5371\u6025\u4e4b\u4e0b\uff0c\u4ed6\u8170\u80cc\u7528\u529b\uff0c\u4f7f\u52b2\u5411\u540e\u6025\u4ef0\uff0c\u5b58\u5fc3\u8981\u5c06\u4e24\u7ae5\u5411\u540e\u7529\u8dcc\u51fa\u53bb\u3002\u52b2\u529b\u521a\u4e00\u7529\u51fa\uff0c\u9661\u89c9\u9888\u4e0a\u4e24\u53ea\u5c0f\u624b\u5ffd\u7136\u653e\u5f00\uff0c\u4e00\u60ca\u4e4b\u4e0b\uff0c\u77e5\u9053\u4e0d\u5999\uff0c\u6025\u5fd9\u6536\u52b2\u7ad9\u76f4\uff0c\u5374\u5df2\u4e0d\u53ca\uff0c\u4e24\u7ae5\u53c8\u662f\u4e00\u51fa\u5de6\u8db3\uff0c\u4e00\u51fa\u53f3\u8db3\uff0c\u5728\u4ed6\u53cc\u811a\u540e\u8ddf\u5411\u524d\u4e00\u6311\u3002\u66f9\u4e91\u5947\u81ea\u5df1\u4f7f\u529b\u5927\u4e86\uff0c\u672c\u5df2\u7ad9\u7acb\u4e0d\u4f4f\uff0c\u518d\u88ab\u4e24\u4eba\u8fd9\u4e00\u6311\uff0c\u5927\u9a82\u201c\u76f4\u5a18\u8d3c\u201d\u58f0\u4e2d\uff0c\u817e\u7684\u4e00\u4e0b\uff0c\u4ef0\u5929\u4e00\u53d8\u3002\u8fd9\u4e00\u4e0b\u53ea\u8dcc\u5f97\u4ed6\u810a\u9aa8\u5982\u8981\u65ad\u6298\uff0c\u633a\u8eab\u8981\u5f85\u7ad9\u8d77\uff0c\u8170\u4e0a\u4f7f\u4e0d\u51fa\u52b2\uff0c\u7adf\u53c8\u4ef0\u8dcc\u3002 \u3000\u3000\u5468\u4e91\u9633\u62a2\u6b65\u4e0a\u524d\uff0c\u4f38\u624b\u6276\u8d77\u3002\u4e24\u4e2a\u7ae5\u513f\u5df2\u4e58\u673a\u62ac\u8d77\u957f\u5251\u3002 \u3000\u3000\u66f9\u4e91\u5947\u672c\u662f\u7d2b\u819b\u8138\u76ae\uff0c\u8fd9\u65f6\u6c14\u5f97\u7d2b\u4e2d\u53d1\u9ed1\uff0c\u62d4\u51fa\u8170\u4e2d\u4f69\u5251\uff0c\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u5f84\u5411\u5de6\u7ae5\u523a\u53bb\u3002\u5468\u4e91\u9633\u89c1\u5e08\u5144\u63a5\u8fde\u4e09\u756a\u7684\u6454\u8dcc\uff0c\u77e5\u9053\u4e24\u4e2a\u7ae5\u513f\u5e74\u7eaa\u867d\u5e7c\uff0c\u5374\u662f\u6781\u4e0d\u597d\u6597\uff0c\u5bf9\u65b9\u5171\u6709\u4e8c\u4eba\uff0c\u81ea\u5df1\u4e0a\u524d\u76f8\u52a9\uff0c\u4e5f\u7b97\u4e0d\u5f97\u7406\u4e8f\uff0c\u5f53\u4e0b\u8ddf\u7740\u51fa\u5251\uff0c\u5411\u53f3\u7ae5\u53d1\u62db\u3002 \u3000\u3000\u5de6\u7ae5\u5411\u53f3\u7ae5\u4f7f\u4e2a\u773c\u8272\uff0c\u4e24\u4eba\u4e3e\u5251\u67b6\u5f00\uff0c\u7a81\u7136\u540c\u65f6\u8dc3\u540e\u4e09\u6b65\u3002\u5de6\u7ae5\u53eb\u9053\uff1a\u201c\u5927\u548c\u5c1a\uff0c\u5c0f\u4eba\u5949\u4e3b\u4eba\u4e4b\u547d\u524d\u6765\u4e0b\u4e66\uff0c\u5e76\u6ca1\u5f97\u7f6a\u8fd9\u4e24\u4f4d\uff0c\u4e3a\u4ec0\u4e48\u5b9a\u8981\u6253\u67b6\uff1f\u201d\u5b9d\u6811\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u8981\u8003\u8f83\u4e00\u4e0b\u4f60\u4eec\u7684\u529f\u592b\uff0c\u5e76\u65e0\u6076\u610f\u3002\u4f60\u4eec\u5c31\u966a\u7740\u7ec3\u7ec3\u3002\u201d\u5de6\u7ae5\u9053\uff1a\u201c\u5982\u6b64\u8bf7\u7237\u4eec\u6307\u70b9\u3002\u201d\u4e24\u4eba\u53cc\u5251\u8d77\u5904\uff0c\u4e0e\u66f9\u5468\u4e8c\u4eba\u6597\u5728\u4e00\u8d77\u3002 \u3000\u3000\u8fd9\u5e84\u5b50\u4e2d\u4f63\u4ec6\u5a62\u5973\uff0c\u4e2a\u4e2a\u90fd\u4f1a\u6b66\u529f\uff0c\u542c\u8bf4\u5bf9\u65b9\u4e24\u4e2a\u4e0b\u4e66\u7684\u7ae5\u513f\u5728\u5385\u4e0a\u4e0e\u4eba\u52a8\u624b\uff0c\u7eb7\u7eb7\u8d70\u51fa\u6765\uff0c\u7ad9\u5728\u5eca\u4e0b\u89c2\u6597\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u7ae5\u513f\u5de6\u624b\u6301\u5251\uff0c\u53e6\u4e00\u4e2a\u53f3\u624b\u6301\u5251\uff0c\u4e24\u4eba\u8fdb\u9000\u8d8b\u907f\uff0c\u7b80\u76f4\u4fbf\u662f\u4e00\u4eba\uff0c\u53cc\u5251\u8fde\u73af\u8fdb\u51fb\uff0c\u7d27\u5bc6\u65e0\u6bd4\u3002\u770b\u6765\u4e24\u4eba\u81ea\u5c0f\u8d77\u59cb\u5b66\u5251\uff0c\u5c31\u662f\u7ec3\u8fd9\u95e8\u53cc\u5251\u5408\u74a7\u7684\u5251\u672f\u3002\u96be\u5f97\u7684\u662f\u90a3\u5de6\u7ae5\u5de6\u624b\u4f7f\u5251\uff0c\u7adf\u548c\u53f3\u7ae5\u7684\u53f3\u624b\u4e00\u822c\u7075\u4fbf\uff0c\u5b9a\u662f\u5929\u751f\u64c5\u7528\u5de6\u624b\u3002 \u3000\u3000\u66f9\u5468\u5e08\u5144\u5f1f\u4e8c\u4eba\u8fde\u53d8\u5251\u62db\uff0c\u59cb\u7ec8\u5948\u4f55\u4e0d\u4e86\u4e24\u4e2a\u5b69\u5b50\u3002\u8f6c\u773c\u95f4\u6597\u4e86\u6570\u5341\u5408\uff0c\u66f9\u5468\u4e8c\u4eba\u867d\u65e0\u8d25\u8c61\uff0c\u5374\u4e5f\u534a\u70b9\u5360\u4e0d\u5230\u4e0a\u98ce\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u5fc3\u4e2d\u7126\u8e81\uff0c\u7ec6\u770b\u4e8c\u7ae5\u6b66\u672f\u5bb6\u6570\uff0c\u4e5f\u4e0d\u8fc7\u662f\u4e00\u8def\u5c11\u6797\u6d3e\u7684\u8fbe\u6469\u5251\u6cd5\uff0c\u6beb\u65e0\u51fa\u5947\u4e4b\u5904\uff0c\u53ea\u662f\u6216\u523a\u6216\u67b6\uff0c\u4ea4\u53c9\u653b\u9632\uff0c\u51fa\u51fb\u7684\u65e0\u540e\u987e\u4e4b\u5fe7\uff0c\u5b88\u5fa1\u7684\u7edd\u56de\u653b\u4e4b\u5ff5\uff0c\u4e0d\u8bba\u653b\u5b88\uff0c\u4ff1\u53ef\u5168\u529b\u4ee5\u8d74\u800c\u5df2\uff0c\u81ea\u5fd6\u4ee5\u4e00\u53cc\u8089\u638c\u53ef\u4ee5\u593a\u4e0b\u4e8c\u7ae5\u5175\u5203\uff0c\u773c\u89c1\u4e24\u4e2a\u5e08\u4f84\u4e45\u6597\u4e0d\u4e0b\uff0c\u5929\u9f99\u5317\u5b97\u7684\u5a01\u540d\u6447\u6447\u6b32\u5760\u3002\u5f53\u5373\u559d\u9053\uff1a\u201c\u4e24\u4e2a\u5b69\u5b50\u679c\u7136\u4e86\u5f97\u3002\u4e91\u5947\uff0c\u4e91\u9633\u9000\u4e0b\uff0c\u8001\u592b\u8ddf\u4ed6\u4eec\u73a9\u73a9\u3002\u201d \u3000\u3000\u66f9\u5468\u4e8c\u4eba\u542c\u5f97\u5e08\u53d4\u53eb\u5524\uff0c\u7b54\u5e94\u4e00\u58f0\uff0c\u8981\u5f85\u9000\u5f00\uff0c\u54ea\u77e5\u4e8c\u7ae5\u51fa\u5251\u7a81\u5feb\uff0c\u9877\u523b\u4e4b\u95f4\uff0c\u53cc\u5251\u4ff1\u662f\u8fdb\u624b\u62db\u6570\u3002\u66f9\u5468\u53ea\u5f97\u6325\u5251\u6321\u67b6\uff0c\u4f46\u4e8c\u7ae5\u4e00\u5251\u8ddf\u7740\u4e00\u5251\uff0c\u7ef5\u7ef5\u4e0d\u5c3d\uff0c\u6321\u5f00\u4e86\u7b2c\u4e00\u5251\uff0c\u7b2c\u4e8c\u5251\u53c8\u4e0d\u5f97\u4e0d\u6321\uff0c\u5341\u4f59\u62d3\u8fc7\u53bb\uff0c\u7adf\u5c14\u4e0d\u80fd\u62bd\u8eab\u3002 \u3000\u3000\u7530\u9752\u6587\u5fc3\u9053\uff1a\u201c\u5f85\u6211\u63a5\u5e94\u4e24\u4f4d\u5e08\u5144\u4e0b\u6765\uff0c\u8ba9\u962e\u5e08\u53d4\u5236\u4f4f\u8fd9\u4e24\u4e2a\u5c0f\u5a03\u5a03\u3002\u962e\u5e08\u53d4\u6b66\u529f\u4f55\u7b49\u5389\u5bb3\uff0c\u81ea\u7136\u4e00\u51fa\u624b\u4fbf\u6293\u4f4f\u4e86\u56db\u6839\u5c0f\u8fab\u5b50\u3002\u201d\u633a\u5251\u4e0a\u524d\uff0c\u53eb\u9053\uff1a\u201c\u4e24\u4f4d\u5e08\u54e5\u4e0b\u884c\u6765\u3002\u201d\u5979\u89c1\u5de6\u7ae5\u6b63\u5411\u66f9\u4e91\u5947\u63a5\u8fde\u8fdb\u653b\uff0c\u5f53\u5373\u6325\u5251\u67b6\u5f00\u4ed6\u7684\u4e00\u5251\uff0c\u5c82\u77e5\u8fd9\u7ae5\u513f\u7b2c\u4e8c\u5251\u51fa\u62db\u65f6\u7adf\u662f\u4e00\u5251\u53cc\u51fb\uff0c\u65e2\u523a\u66f9\u4e91\u5947\u7684\u773c\u89d2\uff0c\u53c8\u523a\u7530\u9752\u6587\u5de6\u80a9\u3002\u7530\u9752\u6587\u53ea\u5f97\u62db\u67b6\uff0c\u8fd9\u4e00\u6765\uff0c\u5979\u63a5\u66ff\u4e0d\u4e0b\u5e08\u5144\uff0c\u53cd\u800c\u8fde\u81ea\u5df1\u4e5f\u7ed9\u7f20\u4e0a\u4e86\u3002\u66f9\u4e91\u5947\u6108\u6597\u6108\u6012\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u5929\u9f99\u5317\u5b97\u5251\u672f\u5411\u6765\u6709\u540d\uff0c\u4eca\u65e5\u4ee5\u6211\u4e09\u4eba\u5408\u529b\uff0c\u8fd8\u6597\u4e0d\u8fc7\u4e24\u4e2a\u5c0f\u5c0f\u5b69\u7ae5\uff0c\u6c5f\u6e56\u4e0a\u4f20\u8a00\u5f00\u53bb\uff0c\u5929\u9f99\u5317\u5b97\u989c\u9762\u4f55\u5b58\uff1f\u201d\u60f3\u5230\u6b64\u5904\uff0c\u51fa\u624b\u52a0\u91cd\u3002 \u3000\u3000\u53f3\u7ae5\u89c1\u957f\u5144\u53d7\u903c\uff0c\u56de\u5251\u5411\u66f9\u4e91\u5947\u523a\u53bb\u3002\u66f9\u4e91\u5947\u8f6c\u8eab\u6321\u5f00\uff0c\u5de6\u7ae5\u5df2\u53d1\u5251\u653b\u5411\u5468\u4e91\u9633\u3002\u4e8c\u4eba\u5728\u500f\u5ffd\u4e4b\u95f4\u8c03\u4e86\u5bf9\u624b\uff0c\u8fd9\u4e00\u4e0b\u8f6c\u6362\u8fc5\u901f\u4e4b\u6781\uff0c\u8eab\u6cd5\u53c8\u6781\u7f8e\u5999\uff0c\u65c1\u89c2\u4f17\u4eba\u4e0d\u81ea\u7981\u7684\u9f50\u58f0\u559d\u5f69\u3002 \u3000\u3000\u6bb7\u5409\u4f4e\u58f0\u9053\uff1a\u201c\u962e\u5e08\u5144\uff0c\u8fd8\u662f\u4f60\u4e0a\u53bb\u3002\u4ed6\u4eec\u4e09\u4e2a\u80dc\u4e0d\u4e86\u3002\u201d \u3000\u3000\u962e\u58eb\u4e2d\u70b9\u70b9\u5934\uff0c\u52d2\u4e86\u52d2\u8170\u5e26\u3002\u53eb\u9053\uff1a\u201c\u8ba9\u6211\u6765\u73a9\u73a9\u3002\u201d\u4e00\u7eb5\u8eab\uff0c\u5df2\u6b3a\u5230\u53f3\u7ae5\u8eab\u8fb9\uff0c\u5de6\u6307\u70b9\u4ed6\u80a9\u5934\u201c\u5de8\u9aa8\u7a74\u201d\uff0c\u53f3\u624b\u4ee5\u5927\u64d2\u62ff\u624b\u5f84\u6765\u593a\u5251\u3002\u65c1\u4eba\u89c1\u4ed6\u8eab\u6cd5\u5feb\u6377\uff0c\u51fa\u624b\u72e0\u8fa3\uff0c\u90fd\u4e0d\u7981\u4e3a\u8fd9\u7ae5\u513f\u62c5\u5fc3\uff0c\u5374\u89c1\u5251\u5149\u95ea\u52a8\uff0c\u5de6\u7ae5\u7684\u5251\u5c16\u6307\u5230\u4e86\u962e\u58eb\u4e2d\u540e\u5fc3\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u4e00\u5fc3\u593a\u5251\uff0c\u53c8\u60f3\u5de6\u7ae5\u6709\u5468\u4e91\u9633\u654c\u4f4f\uff0c\u5e76\u672a\u60f3\u5230\u4ed6\u4f1a\u5ffd\u65bd\u5077\u88ad\uff0c\u53ea\u542c\u7530\u9752\u6587\u6025\u53eb\uff1a\u201c\u5e08\u53d4\uff0c\u540e\u9762\uff01\u201d\u962e\u58eb\u4e2d\u5fd9\u5411\u5de6\u95ea\u907f\uff0c\u5374\u542c\u55e4\u7684\u4e00\u58f0\uff0c\u540e\u895f\u5df2\u5212\u7834\u4e86\u4e00\u9053\u53e3\u5b50\u3002\u90a3\u5de6\u7ae5\u53eb\u9053\uff1a\u201c\u8fd9\u4f4d\u7237\u5c0f\u5fc3\u4e86\u3002\u201d\u770b\u6765\u4ed6\u8fd8\u662f\u6709\u5fc3\u76f8\u8ba9\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u5fc3\u5934\u4e00\u8e81\uff0c\u9762\u7ea2\u8fc7\u8033\uff0c\u4f46\u4ed6\u4e45\u7ecf\u5927\u654c\uff0c\u9002\u624d\u8fd9\u4e00\u632b\u6298\uff0c\u53cd\u800c\u4f7f\u4ed6\u6c89\u4f4f\u4e86\u6c14\uff0c\u5f53\u4e0b\u4e0d\u6562\u5192\u8fdb\uff0c\u5c55\u5f00\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u9501\u3001\u9519\u3001\u95ed\u3001\u5206\uff0c\u5bfb\u7455\u62b5\u9699\uff0c\u6765\u593a\u4e8c\u7ae5\u624b\u4e2d\u5175\u5203\u3002\u4ed6\u5728\u8fd9\u53cc\u8089\u638c\u4e0a\u4e0b\u4e86\u6570\u5341\u5e74\u82e6\u529f\uff0c\u65bd\u5c55\u5f00\u6765\u679c\u7136\u4e0d\u540c\u5bfb\u5e38\u3002\u4f46\u8bf4\u4e5f\u5947\u602a\uff0c\u66f9\u5468\u4e8c\u4eba\u8fce\u654c\u4e4b\u65f6\uff0c\u4e8c\u7ae5\u5e76\u672a\u5360\u5230\u4e0a\u98ce\uff0c\u73b0\u4e0b\u52a0\u591a\u962e\u7530\u4e8c\u4eba\uff0c\u5374\u4ecd\u7136\u662f\u6597\u4e86\u4e2a\u65d7\u9f13\u76f8\u5f53\u3002 \u3000\u3000\u6bb7\u5409\u5fc3\u60f3\uff1a\u201c\u5357\u5317\u4e8c\u5b97\u540c\u6c14\u8fde\u679d\uff0c\u82e5\u662f\u5317\u5b97\u6298\u4e86\u9510\u6c14\uff0c\u6211\u5357\u5b97\u4e5f\u65e0\u5149\u5f69\u3002\u4eca\u65e5\u4e4b\u5c40\uff0c\u7eb5\u8ba9\u65c1\u4eba\u8bf4\u4e2a\u4ee5\u591a\u80dc\u5c11\uff0c\u603b\u4e5f\u6bd4\u843d\u8d25\u597d\u4e9b\u3002\u201d\u957f\u5251\u51fa\u9798\uff0c\u4e00\u62db\u201c\u6d41\u661f\u8d76\u6708\u201d\uff0c\u4eba\u672a\u62a2\u5165\u5708\u5b50\uff0c\u5251\u950b\u5374\u5df2\u6307\u5411\u5de6\u7ae5\u80f8\u53e3\u3002\u53f3\u7ae5\u53eb\u9053\uff1a\u201c\u53c8\u6765\u4e86\u4e00\u4e2a\u3002\u201d\u6a2a\u5251\u56de\u6307\uff0c\u70b9\u5411\u4ed6\u7684\u624b\u8155\u3002\u6bb7\u5409\u4e00\u51db\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5b69\u513f\u8fde\u73af\u6551\u5e94\uff0c\u679c\u5df2\u7ec3\u5f97\u51fa\u795e\u5165\u5316\u3002\u201d\u624b\u8155\u4e00\u6c89\uff0c\u907f\u5f00\u4e86\u8fd9\u4e00\u5251\u3002\u907f\u5f00\u8fd9\u4e00\u5251\u5e76\u4e0d\u4e3a\u96be\uff0c\u4f46\u4ed6\u653b\u5411\u5de6\u7ae5\u7684\u5251\u52bf\uff0c\u5374\u4e5f\u56e0\u6b64\u800c\u5378\u3002 \u3000\u3000\u5927\u5385\u4e0a\u516d\u67c4\u957f\u5251\u3001\u4e00\u5bf9\u8089\u638c\uff0c\u6253\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u4e00\u6597\u6570\u5341\u5408\uff0c\u4ecd\u662f\u4e2a\u4e0d\u80dc\u4e0d\u8d25\u4e4b\u5c40\u3002 \u3000\u3000\u9676\u5b50\u5b89\u89c1\u7530\u9752\u6587\u8138\u73b0\u7ea2\u6655\uff0c\u8fde\u4f38\u51e0\u6b21\u8896\u53e3\u62b9\u6c57\uff0c\u53eb\u9053\uff1a\u201c\u9752\u59b9\uff0c\u4f60\u6b47\u6b47\uff0c\u6211\u6765\u66ff\u4f60\u3002\u201d\u5f53\u5373\u6325\u5200\u4e0a\u524d\u3002\u66f9\u4e91\u5947\u559d\u9053\uff1a\u201c\u8c01\u8981\u4f60\u8ba8\u597d\uff01\u201d\u957f\u5251\u6321\u5f00\u53f3\u7ae5\u523a\u6765\u5251\u62db\uff0c\u5de6\u624b\u63e1\u62f3\uff0c\u5374\u5f80\u9676\u5b50\u5b89\u9f3b\u4e0a\u51fb\u53bb\u3002\u9676\u5b50\u5b89\u4e00\u7b11\uff0c\u6ed1\u5f00\u4e09\u6b65\uff0c\u7ed5\u5230\u4e86\u5de6\u7ae5\u8eab\u540e\u3002\u4ed6\u867d\u817f\u4e0a\u8d1f\u4f24\uff0c\u5200\u6cd5\u4ecd\u662f\u6781\u4e3a\u7cbe\u5999\uff0c\u4f46\u4e8c\u7ae5\u7684\u5251\u672f\u602a\u5f02\u65e0\u6bd4\uff0c\u654c\u4eba\u6108\u4f17\uff0c\u7adf\u4f3c\u5a01\u529b\u76f8\u5e94\u800c\u589e\u3002\u9676\u5b50\u5b89\u65e2\u987b\u9632\u5907\u66f9\u4e91\u5947\u88ad\u51fb\uff0c\u53c8\u5f97\u5bf9\u4ed8\u4e8c\u7ae5\u51fa\u5176\u4e0d\u610f\u9012\u6765\u7684\u5251\u62db\uff0c\u7adf\u5c14\u95f9\u4e86\u4e2a\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u9676\u767e\u5c81\u6162\u6162\u8d70\u8fd1\uff0c\u63d0\u7740\u94a2\u97ad\u4fdd\u62a4\u513f\u5b50\u3002\u5200\u5149\u5251\u5f71\u4e4b\u4e2d\uff0c\u66f9\u4e91\u5947\u731b\u5730\u4e00\u5251\u5411\u9676\u5b50\u5b89\u5288\u53bb\u3002\u9676\u767e\u5c81\u6012\u543c\u4e00\u58f0\uff0c\u6325\u97ad\u67b6\u5f00\uff0c\u8ddf\u7740\u5411\u66f9\u4e91\u5947\u8fdb\u62db\u3002\u65c1\u89c2\u4f17\u4eba\u89c1\u6218\u5c40\u53d8\u5e7b\uff0c\u4e0d\u7531\u5f97\u90fd\u662f\u6697\u6697\u79f0\u5947\u3002 \u3000\u3000\u718a\u5143\u732e\u5f53\u962e\u58eb\u4e2d\u4e0b\u573a\u65f6\u89c1\u4ed6\u5c06\u94c1\u76d2\u653e\u5728\u6000\u5185\uff0c\u5fc3\u60f3\u4e0d\u5982\u4e0a\u524d\u52a9\u6218\uff0c\u6d51\u6c34\u6478\u9c7c\uff0c\u4e58\u673a\u4e0b\u624b\uff0c\u62a2\u593a\u94c1\u76d2\u4e5f\u597d\uff0c\u6740\u4e86\u9676\u6c0f\u7236\u5b50\u62a5\u4ec7\u4e5f\u597d\uff0c\u5f53\u4e0b\u53eb\u9053\uff1a\u201c\u597d\u70ed\u95f9\u554a\uff0c\u5218\u5e08\u5144\uff0c\u54b1\u54e5\u513f\u4fe9\u4e5f\u4e0a\uff01\u201d\u5218\u5143\u9e64\u4e0e\u4ed6\u81ea\u5c0f\u540c\u5728\u5e08\u95e8\uff0c\u5f7c\u6b64\u77e5\u5fc3\uff0c\u4e00\u542c\u4ed6\u53eb\u5524\uff0c\u5df2\u660e\u5176\u610f\uff0c\u53cc\u62d0\u6446\u52a8\uff0c\u9760\u5411\u962e\u58eb\u4e2d\u8eab\u7554\u3002 \u3000\u3000\u90a3\u5de6\u7ae5\u54ea\u60f3\u5f97\u5230\u8fd9\u8bb8\u591a\u654c\u624b\u5404\u6709\u56fe\u8c0b\uff0c\u89c1\u5218\u5143\u9e64\u3001\u718a\u5143\u732e\u52a0\u5165\u6218\u56e2\uff0c\u7adf\u5c14\u5148\u53d1\u5236\u4eba\uff0c\u51fa\u5251\u5411\u4e24\u4eba\u76f4\u653b\uff0c\u53cc\u7ae5\u5251\u672f\u867d\u7cbe\uff0c\u4f46\u4ee5\u4e8c\u654c\u4e5d\uff0c\u672c\u6765\u65e0\u8bba\u5982\u4f55\u975e\u8d25\u4e0d\u53ef\uff0c\u53ea\u662f\u4e5d\u4e2a\u4eba\u5404\u6000\u5f02\u5fc3\uff0c\u6240\u4f7f\u62db\u6570\uff0c\u5012\u662f\u653b\u654c\u8005\u5c11\uff0c\u4e92\u76f8\u7275\u5236\u9632\u8303\u8005\u591a\u3002 \u3000\u3000\u7530\u9752\u6587\u89c1\u5218\u718a\u4e8c\u4eba\u624b\u4e0a\u4e0e\u53cc\u7ae5\u76f8\u6597\uff0c\u76ee\u5149\u5374\u4e0d\u4f4f\u5f80\u5e08\u53d4\u8eab\u4e0a\u779f\u53bb\uff0c\u5df2\u77e5\u5b58\u5fc3\u4e0d\u5584\uff0c\u53eb\u9053\uff1a\u201c\u962e\u5e08\u53d4\uff0c\u7559\u795e\u94c1\u76d2\u3002\u201d\u962e\u58eb\u4e2d\u4e45\u6597\u4e0d\u4e0b\uff0c\u65e9\u5df2\u5fc3\u4e2d\u7126\u8e81\uff0c\u5bfb\u601d\uff1a\u201c\u6211\u7b49\u4e5d\u4e2a\u5927\u4eba\uff0c\u8fd8\u6253\u4e0d\u5012\u4e24\u4e2a\u5c0f\u5b69\uff0c\u4eca\u65e5\u53ef\u7b97\u662f\u4e22\u8db3\u4e86\u8138\u3002\u82e5\u662f\u94c1\u76d2\u518d\u5931\uff0c\u4ee5\u540e\u66f4\u96be\u505a\u4eba\u4e86\u3002\u201d\u5fae\u4e00\u758f\u795e\uff0c\u53ea\u89c9\u4e00\u80a1\u52b2\u98ce\u63a0\u9762\u800c\u8fc7\uff0c\u539f\u6765\u662f\u53f3\u7ae5\u67b6\u5f00\u66f9\u4e91\u5947\u3001\u5468\u4e91\u9633\u7684\u53cc\u5251\u540e\uff0c\u62bd\u7a7a\u5411\u4ed6\u5288\u4e86\u4e00\u5251\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u5fc3\u4e2d\u4e00\u51db\uff0c\u6697\u9053\uff1a\u201c\u5de6\u53f3\u662f\u6ca1\u4e86\u8138\u9762\u3002\u201d\u659c\u8eab\u4fa7\u95ea\uff0c\u624b\u8155\u7ffb\u5904\uff0c\u5df2\u5c06\u957f\u5251\u62d4\u5728\u624b\u91cc\u3002\u8fd9\u4e5d\u4eba\u4e4b\u4e2d\uff0c\u8bba\u5230\u6b66\u529f\u539f\u662f\u6570\u4ed6\u4e3a\u9996\u3002\u8fd9\u65f6\u5c06\u5929\u9f99\u5251\u6cd5\u4f7f\u5c06\u5f00\u6765\uff0c\u53ea\u542c\u53ee\u5f53\u58f0\u54cd\uff0c\u9676\u6c0f\u7236\u5b50\u3001\u5218\u718a\u5e08\u5144\u5f1f\u7b49\u4eba\u7684\u5175\u5203\u90fd\u88ab\u4ed6\u78b0\u4e86\u5f00\u53bb\u3002\u6bb7\u5409\u62a4\u4f4f\u95e8\u6237\uff0c\u9000\u5728\u540e\u9762\uff0c\u4e58\u673a\u89c2\u6469\u5317\u5b97\u5251\u672f\u7684\u79d8\u5965\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u89c1\u4f17\u4eba\u6e10\u6e10\u9000\u5f00\uff0c\u81ea\u5df1\u8eab\u65c1\u7a7a\u4e86\u6570\u5c3a\uff0c\u957f\u5251\u4f7f\u52a8\u65f6\u66f4\u4e3a\u7075\u4fbf\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u8e0f\u524d\u4e24\u6b65\uff0c\u4e00\u62db\u201c\u4e91\u4e2d\u63a2\u722a\u201d\uff0c\u5f80\u53f3\u7ae5\u5f53\u5934\u75be\u5288\u4e0b\u53bb\u3002\u8fd9\u4e00\u62db\u5feb\u6377\u5f02\u5e38\uff0c\u53f3\u7ae5\u624b\u4e2d\u957f\u5251\u6b63\u4e0e\u5218\u5143\u9e64\u94c1\u62d0\u76f8\u4ea4\uff0c\u5ffd\u89c1\u5251\u5230\uff0c\u6025\u5fd9\u77ee\u8eab\u76f8\u907f\uff0c\u53ea\u542c\u5237\u7684\u4e00\u54cd\uff0c\u5c0f\u8fab\u4e0a\u7684\u4e00\u9897\u660e\u73e0\u5df2\u88ab\u5229\u5251\u524a\u4e3a\u4e24\u534a\uff0c\u8dcc\u5728\u5730\u4e0b\u3002 \u3000\u3000\u53cc\u7ae5\u540c\u65f6\u53d8\u8272\u3002\u53f3\u7ae5\u53eb\u4e86\u58f0\uff1a\u201c\u54e5\u54e5\uff01\u201d\u5c0f\u5634\u6241\u4e86\uff0c\u4f3c\u4e4e\u5c31\u8981\u54ed\u51fa\u58f0\u6765\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u54c8\u54c8\u4e00\u7b11\uff0c\u7a81\u89c1\u773c\u524d\u767d\u5f71\u6643\u52a8\uff0c\u53cc\u7ae5\u4ea4\u53c9\u79fb\u4f4d\uff0c\u53ee\u53ee\u6570\u54cd\uff0c\u5468\u4e91\u9633\u4e0e\u718a\u5143\u732e\u7684\u5175\u5203\u5df2\u88ab\u524a\u65ad\u3002\u4e24\u4eba\u5927\u60ca\u4e4b\u4e0b\uff0c\u6025\u5fd9\u8dc3\u51fa\u5708\u5b50\uff0c\u4f46\u89c1\u53cc\u7ae5\u624b\u4e2d\u5df2\u5404\u591a\u4e86\u4e00\u67c4\u7cbe\u5149\u8000\u773c\u7684\u5315\u9996\u3002 \u3000\u3000\u5de6\u7ae5\u53eb\u9053\uff1a\u201c\u4f60\u627e\u4ed6\u7b97\u5e10\u3002\u201d\u53f3\u624b\u5315\u9996\u7ffb\u5904\uff0c\u53ee\u53ee\u4e24\u54cd\uff0c\u53c8\u5df2\u5c06\u66f9\u4e91\u5947\u4e0e\u6bb7\u5409\u624b\u4e2d\u957f\u5251\u524a\u65ad\uff0c\u539f\u6765\u8fd9\u5315\u9996\u7adf\u662f\u780d\u91d1\u5207\u7389\u7684\u5b9d\u5251\u3002\u66f9\u4e91\u5947\u540e\u9000\u7a0d\u6162\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u5de6\u80c1\u88ab\u5315\u9996\u5212\u8fc7\uff0c\u8170\u4e2d\u9769\u5e26\u8fde\u7740\u5251\u9798\u65ad\u4e3a\u6570\u622a\u3002 \u3000\u3000\u53f3\u7ae5\u53f3\u624b\u957f\u5251\uff0c\u5de6\u624b\u5315\u9996\uff0c\u5411\u962e\u58eb\u4e2d\u6b3a\u8eab\u76f4\u653b\u3002\u8fd9\u65f6\u4ed6\u53cc\u5203\u5728\u624b\uff0c\u5251\u6cd5\u5927\u5f02\u3002\u962e\u58eb\u4e2d\u53c8\u60ca\u53c8\u6012\uff0c\u4e00\u65f6\u77a7\u4e0d\u6e05\u4ed6\u7684\u5251\u8def\uff0c\u4f46\u89c9\u90a3\u5315\u9996\u523a\u8fc7\u6765\u65f6\u5bd2\u6c14\u8feb\u4eba\uff0c\u4e0d\u6562\u4ee5\u5251\u76f8\u78b0\uff0c\u53ea\u5f97\u4e0d\u4f4f\u9000\u540e\u3002\u53f3\u7ae5\u4e0d\u7406\u65c1\u4eba\uff0c\u7740\u7740\u8fdb\u8feb\u3002 \u3000\u3000\u5de6\u7ae5\u4e0e\u5144\u5f1f\u80cc\u810a\u9760\u7740\u80cc\u810a\uff0c\u4e00\u4eba\u5c06\u4f59\u654c\u5c3d\u6570\u63a5\u8fc7\uff0c\u8ba9\u5144\u5f1f\u4e0e\u962e\u58eb\u4e2d\u5355\u6253\u72ec\u6597\uff0c\u62c6\u4e86\u6570\u62db\uff0c\u9676\u767e\u5c81\u7684\u94a2\u97ad\u53c8\u88ab\u524a\u65ad\u4e00\u622a\u3002\u5218\u5143\u9e64\u3001\u9676\u5b50\u5b89\u4e0d\u6562\u8feb\u8fd1\uff0c\u53ea\u662f\u7ed5\u7740\u5708\u5b50\u6e38\u6597\u3002\u6bb7\u5409\u3001\u66f9\u4e91\u5947\u3001\u5468\u4e91\u9633\u3001\u7530\u9752\u6587\u56db\u4eba\u89c1\u962e\u58eb\u4e2d\u88ab\u8feb\u5230\u4e86\u5c4b\u89d2\uff0c\u5df2\u662f\u9000\u65e0\u53ef\u9000\uff0c\u90fd\u662f\u7126\u6025\u5f02\u5e38\uff0c\u8981\u5f85\u4e0a\u524d\u6551\u63f4\uff0c\u4e00\u6765\u4e09\u4eba\u624b\u4e2d\u5175\u5203\u5df2\u65ad\uff0c\u4e8c\u6765\u4e5f\u95ef\u4e0d\u8fc7\u5de6\u7ae5\u90a3\u4e00\u5173\u3002 \u3000\u3000\u5b9d\u6811\u5728\u65c1\u77a7\u7740\u53cc\u7ae5\u5251\u6cd5\uff0c\u5fc3\u4e2d\u6697\u6697\u79f0\u5947\uff0c\u521d\u65f6\u89c1\u53cc\u7ae5\u4e0e\u66f9\u4e91\u5947\u7b49\u76f8\u6597\uff0c\u5251\u672f\u4e5f\u53ea\u5e73\u5e73\uff0c\u4f46\u5f53\u654c\u624b\u6e10\u591a\uff0c\u53cc\u7ae5\u5251\u4e0a\u5a01\u529b\u7adf\u8ddf\u7740\u589e\u5f3a\u3002\u6b64\u65f6\u4eae\u51fa\u5315\u9996\uff0c\u60c5\u52bf\u66f4\u662f\u5927\u53d8\u3002\u5de6\u7ae5\u957f\u5251\u8fde\u89c1\uff0c\u903c\u5f97\u654c\u5bf9\u4f17\u4eba\u624b\u5fd9\u811a\u4e71\uff0c\u8f6c\u773c\u95f4\u9676\u5b50\u5b89\u4e0e\u5218\u5143\u9e64\u7684\u5175\u5203\u53c8\u88ab\u524a\u65ad\u3002\u4e0e\u5de6\u7ae5\u76f8\u6597\u7684\u516b\u4eba\u4e4b\u4e2d\uff0c\u5c31\u53ea\u7530\u9752\u6587\u4e00\u4eba\u624b\u4e2d\u957f\u5251\u5b8c\u597d\u65e0\u7f3a\uff0c\u663e\u7136\u5e76\u975e\u5979\u529f\u592b\u72ec\u5230\uff0c\u800c\u662f\u5de6\u7ae5\u611f\u5979\u76f8\u8d60\u679c\u5b50\u4e4b\u60c5\uff0c\u624b\u4e0b\u5bb9\u8ba9\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u80cc\u9760\u5899\u89d2\uff0c\u8d1f\u9685\u529b\u6218\uff0c\u53ea\u89c1\u53f3\u7ae5\u957f\u5251\u5f84\u523a\u81ea\u5df1\u524d\u80f8\uff0c\u5f53\u4e0b\u5e94\u4ee5\u4e00\u62db\u201c\u817e\u86df\u8d77\u51e4\u201d\u3002\u8fd9\u662f\u4e00\u62db\u6d17\u52bf\u3002\u5251\u8bc0\u6709\u4e91\uff1a \u3000\u3000\u201c\u9ad8\u6765\u6d17\u3001\u4f4e\u6765\u51fb\uff0c\u91cc\u6765\u63a9\uff0c\u5916\u6765\u62b9\uff0c\u4e2d\u6765\u523a\u3002\u201d\u8fd9\u201c\u6d17\u3001\u51fb\u3001\u63a9\u3001\u62b9\u3001\u523a\u201d\u4e94\u5b57\uff0c\u662f\u5404\u5bb6\u5251\u672f\u5171\u901a\u7684\u8981\u8bc0\u3002\u962e\u58eb\u4e2d\u89c1\u654c\u5251\u9ad8\u523a\uff0c\u4ee5\u201c\u6d17\u201d\u5b57\u8bc0\u76f8\u5e94\uff0c\u539f\u672c\u4e0d\u9519\uff0c\u54ea\u77e5\u53cc\u5251\u76f8\u4ea4\uff0c\u7a81\u89c9\u624b\u8155\u4e00\u6c89\uff0c\u5df1\u5251\u88ab\u654c\u5251\u76f4\u538b\u4e0b\u53bb\u3002\u962e\u58eb\u4e2d\u5927\u559c\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u5251\u672f\u867d\u7cbe\uff0c\u8155\u529b\u5c82\u6709\u6211\u5f3a\uff1f\u201d\u5f53\u4e0b\u8fd0\u52b2\u53cd\u51fb\u3002\u53f3\u7ae5\u53f3\u624b\u5251\u4e00\u7f29\uff0c\u5de6\u624b\u5315\u9996\u500f\u5730\u6325\u51fa\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u5c06\u4ed6\u957f\u5251\u524a\u4e3a\u4e24\u622a\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u5927\u5403\u4e00\u60ca\uff0c\u7acb\u5c06\u534a\u622a\u65ad\u5251\u8fce\u9762\u63b7\u53bb\u3002\u53f3\u7ae5\u4f4e\u5934\u95ea\u5f00\uff0c\u957f\u5251\u5de6\u53f3\u75be\u523a\uff0c\u5c06\u4ed6\u5c01\u95ed\u4e8e\u5c4b\u89d2\uff0c\u51fa\u6765\u4e0d\u5f97\u3002\u6bb7\u5409\u3001\u66f9\u4e91\u5947\u3001\u5468\u4e91\u9633\u9f50\u58f0\u5927\u53eb\uff0c\u6697\u5668\u7eb7\u7eb7\u51fa\u624b\u3002\u5de6\u7ae5\u7a9c\u9ad8\u8dc3\u4f4e\uff0c\u53f3\u624b\u8fde\u6325\uff0c\u5c06\u5341\u591a\u679a\u6bd2\u9f99\u9525\u5c3d\u6570\u63a5\u53bb\u3002\u539f\u6765\u4ed6\u5315\u9996\u7684\u67c4\u5e95\u88c5\u6709\u4e00\u5c0f\u5c0f\u7f51\u515c\uff0c\u4e13\u63a5\u654c\u4eba\u6697\u5668\u3002 \u3000\u3000\u4e03\u661f\u624b\u962e\u58eb\u4e2d\u5175\u5203\u867d\u5931\uff0c\u62f3\u811a\u529f\u592b\u4ecd\u6781\u5389\u5bb3\uff0c\u4ed6\u662f\u6c5f\u6e56\u8001\u624b\uff0c\u867d\u8d25\u4e0d\u4e71\uff0c\u5f53\u4e0b\u4ee5\u4e00\u53cc\u8089\u638c\u6c89\u7740\u5e94\u654c\uff0c\u53ea\u662f\u53f3\u7ae5\u90a3\u5315\u9996\u5bd2\u5149\u8000\u773c\uff0c\u53ea\u8981\u88ab\u5203\u5c16\u626b\u4e0a\u4e00\u4e0b\uff0c\u53ea\u6015\u624b\u638c\u7acb\u65f6\u5c31\u7ed9\u5272\u4e86\u4e0b\u6765\u3002\u4ed6\u6700\u6015\u7684\u8fd8\u4e0d\u662f\u5bf9\u65b9\u6b66\u529f\u602a\u5f02\uff0c\u800c\u662f\u90a3\u5315\u9996\u5b9e\u5728\u592a\u8fc7\u950b\u5229\uff0c\u5f53\u4e0b\u53ea\u6709\u7aed\u529b\u95ea\u907f\uff0c\u4e0d\u6562\u51fa\u624b\u8fd8\u62db\u3002 \u3000\u3000\u53f3\u7ae5\u4e0d\u4f4f\u53eb\u9053\uff1a\u201c\u8d54\u6211\u7684\u73e0\u513f\uff0c\u8d54\u6211\u7684\u73e0\u513f\u3002\u201d\u962e\u58eb\u4e2d\u5fc3\u4e2d\u4e00\u767e\u4e8c\u5341\u4e2a\u613f\u610f\u8d54\u73e0\uff0c\u53ef\u662f\u4e00\u6765\u65e0\u73e0\u53ef\u8d54\uff0c\u4e8c\u6765\u8fd9\u8138\u4e0a\u53c8\u5982\u4f55\u4e0b\u5f97\u6765\uff1f \u3000\u3000\u5b9d\u6811\u89c1\u5c40\u52bf\u6781\u662f\u5c2c\u5c34\uff0c\u518d\u50f5\u6301\u7247\u523b\uff0c\u82e5\u662f\u90a3\u5b69\u7ae5\u5f53\u771f\u607c\u4e86\uff0c\u4e00\u5315\u9996\u5c31\u4f1a\u5728\u962e\u58eb\u4e2d\u80f8\u819b\u4e0a\u523a\u4e2a\u900f\u660e\u7a9f\u7abf\u3002\u4ed6\u662f\u81ea\u5df1\u9080\u4e0a\u5c71\u6765\u7684\u5ba2\u4eba\uff0c\u5c82\u80fd\u8ba9\u5bf9\u5934\u7684\u7ae5\u4ec6\u6b3a\u8fb1\uff1f\u53ea\u662f\u8fd9\u4e24\u4e2a\u5b69\u7ae5\u7684\u6b66\u529f\u751a\u4e3a\u602a\u5f02\uff0c\u5355\u800c\u8bba\uff0c\u56fa\u7136\u4e0d\u53ca\u962e\u58eb\u4e2d\uff0c\u53ea\u6015\u8fde\u5218\u5143\u9e64\u3001\u9676\u767e\u5c81\u4e5f\u6709\u4e0d\u53ca\uff0c\u4f46\u4e8c\u4eba\u4e00\u8054\u624b\uff0c\u7adf\u662f\u9047\u5f3a\u6108\u5f3a\uff0c\u81ea\u5df1\u82e5\u662f\u63d2\u624b\uff0c\u4e00\u4e2a\u5e94\u4ed8\u5c0f\u4e86\uff0c\u5c82\u975e\u81ea\u53d6\u5176\u8fb1\uff1f \u3000\u3000\u5f53\u4ed6\u6c89\u541f\u96be\u51b3\u4e4b\u65f6\uff0c\u962e\u58eb\u4e2d\u5904\u5883\u5df2\u66f4\u52a0\u72fc\u72c8\u3002\u4f46\u89c1\u4ed6\u8863\u886b\u788e\u88c2\uff0c\u6ee1\u8138\u8840\u6c61\uff0c\u80f8\u524d\u81c2\u4e0a\uff0c\u88ab\u53f3\u7ae5\u957f\u5251\u5272\u4e86\u4e00\u6761\u6761\u4f24\u75d5\u3002 \u3000\u3000\u4ed6\u51e0\u6b21\u9669\u4e9b\u513f\u8981\u8131\u53e3\u6c42\u9976\uff0c\u7ec8\u4e8e\u5f3a\u884c\u5fcd\u4f4f\u3002\u53f3\u7ae5\u53ea\u53eb\uff1a\u201c\u4f60\u8d54\u4e0d\u8d54\u6211\u73e0\u513f\uff1f\u201d\u90a3\u957f\u9888\u4ec6\u4eba\u8d70\u5230\u5b9d\u6811\u8eab\u8fb9\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u5e08\uff0c\u8bf7\u4f60\u51fa\u624b\u6253\u53d1\u4e86\u4e24\u4e2a\u5c0f\u5a03\u5a03\u3002\u201d\u5b9d\u6811\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3\u4e2d\u6c89\u541f\u672a\u5b9a\uff0c\u5ffd\u542c\u55e4\u7684\u4e00\u58f0\u54cd\uff0c\u96ea\u5cf0\u5916\u4e00\u9053\u84dd\u7130\u51b2\u5929\u800c\u8d77\u3002\u90a3\u957f\u9888\u4ec6\u4eba\u77e5\u662f\u4e3b\u4eba\u6240\u7ea6\u7684\u5e2e\u624b\u5230\u4e86\uff0c\u5fc3\u4e2d\u5927\u559c\uff1a\u201c\u8fd9\u548c\u5c1a\u5148\u628a\u8bdd\u513f\u8bf4\u5f97\u6ee1\u4e86\uff0c\u4e8b\u5230\u4e34\u5934\u5374\u652f\u652f\u543e\u543e\uff0c\u5e78\u597d\u53c8\u6709\u4e3b\u4eba\u7684\u670b\u53cb\u8d76\u5230\u3002\u201d \u3000\u3000\u5fd9\u5954\u51fa\u95e8\u53bb\uff0c\u653e\u7bee\u8fce\u5bbe\u3002"}),showOnHomepage:!0},{name:"\u4e09",content:(0,a.jsx)(a.Fragment,{children:"\u8fd9\u957f\u9888\u6c49\u5b50\u662f\u5c71\u5e84\u7684\u7ba1\u5bb6\uff0c\u59d3\u4e8e\uff0c\u672c\u4e5f\u662f\u6c5f\u6e56\u4e0a\u7684\u4e00\u628a\u597d\u624b\uff0c\u751a\u662f\u7cbe\u660e\u5e72\u7ec3\u3002\u4ed6\u89c1\u7af9\u7bee\u540a\u5230\u5c71\u8170\uff0c\u4fbf\u63a2\u5934\u4e0b\u671b\uff0c\u8981\u77a7\u6765\u63f4\u7684\u662f\u54ea\u4e00\u4f4d\u82f1\u96c4\u3002\u521d\u65f6\u4f46\u89c1\u7bee\u4e2d\u9ed1\u9edd\u9edd\u7684\u51e0\u5806\u4e1c\u897f\uff0c\u4f3c\u4e4e\u5e76\u975e\u4eba\u5f62\uff0c\u5f85\u540a\u5230\u4e34\u8fd1\uff0c\u89c1\u662f\u51e0\u53ea\u7bb1\u7b3c\uff0c\u53e6\u6709\u4e9b\u82b1\u76c6\u3001\u9999\u7089\u4e4b\u5c5e\uff0c\u628a\u540a\u7bee\u88c5\u5f97\u6ee1\u6ee1\u7684\u6ca1\u4e00\u70b9\u7a7a\u9699\u3002\u4e8e\u7ba1\u5bb6\u4e0d\u7981\u5927\u5947\uff1a \u3000\u3000\u201c\u96be\u9053\u662f\u7ed9\u4e3b\u4eba\u9001\u793c\u6765\u4e86\uff1f\u201d \u3000\u3000\u4e8c\u6b21\u540a\u4e0a\u6765\u7684\u662f\u4e09\u4e2a\u5973\u4eba\u3002\u4e24\u4e2a\u56db\u5341\u6765\u5c81\uff0c\u90fd\u662f\u4ec6\u5987\u6253\u626e\u3002\u53e6\u4e00\u4e2a\u5341\u4e94\u516d\u5c81\u5e74\u7eaa\uff0c\u5706\u5706\u7684\u4e00\u53cc\u5927\u773c\uff0c\u5de6\u988a\u4e0a\u6709\u4e2a\u9152\u7a9d\u513f\uff0c\u770b\u6a21\u6837\u662f\u4e2a\u4e2b\u9b1f\u3002\u5979\u4e0d\u7b49\u7af9\u7bee\u505c\u597d\uff0c\u4fbf\u5373\u8de8\u51fa\uff0c\u5411\u4e8e\u7ba1\u5bb6\u671b\u4e86\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5b9a\u662f\u4e8e\u5927\u54e5\u4e86\u3002\u4f60\u7684\u5934\u9888\u957f\uff0c\u6211\u542c\u4eba\u8bf4\u8fc7\u7684\u3002\u201d\u4e00\u53e3\u4eac\u7247\u5b50\uff0c\u58f0\u97f3\u6781\u662f\u6e05\u8106\u3002\u4e8e\u7ba1\u5bb6\u751f\u5e73\u6700\u4e0d\u559c\u6b22\u522b\u4eba\u8bf4\u4ed6\u5934\u9888\uff0c\u4f46\u89c1\u5979\u6ee1\u8138\u7b11\u5bb9\uff0c\u5012\u4e5f\u751f\u4e0d\u51fa\u6c14\uff0c\u53ea\u5f97\u7b11\u7740\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u90a3\u4e2b\u9b1f\u9053\uff1a\u201c\u6211\u53eb\u7434\u513f\u3002\u5979\u662f\u5468\u5976\u5988\uff0c\u5c0f\u59d0\u5403\u5979\u5976\u957f\u5927\u7684\u3002 \u3000\u3000\u8fd9\u4f4d\u662f\u97e9\u5a76\u5b50\uff0c\u5c0f\u59d0\u5c31\u7231\u5403\u5979\u70e7\u7684\u83dc\uff0c\u4f60\u5feb\u653e\u540a\u7bee\u53bb\u63a5\u5c0f\u59d0\u4e0a\u6765\u3002\u201d\u4e8e\u7ba1\u5bb6\u5f85\u8981\u8be2\u95ee\u662f\u8c01\u5bb6\u7684\u5c0f\u59d0\uff0c\u7434\u513f\u5374\u53fd\u53fd\u54af\u54af\u7684\u8bf4\u4e2a\u4e0d\u505c\uff0c\u4e00\u9762\u5728\u7bee\u4e2d\u642c\u51fa\u9e1f\u7b3c\u3001\u72f8\u732b\u3001\u9e66\u9e49\u67b6\u3001\u5170\u82b1\u74f6\u7b49\u8bb8\u8bb8\u591a\u591a\u53c8\u53e4\u602a\u53c8\u7410\u788e\u7684\u7269\u4e8b\uff0c\u624b\u4e2d\u5fd9\u7740\uff0c\u5634\u91cc\u4e5f\u4e0d\u95f2\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c71\u5cf0\u771f\u9ad8\uff0c\u5509\uff0c\u5c71\u9876\u4e0a\u6ca1\u4ec0\u4e48\u82b1\u513f\u8349\u513f\uff0c\u6211\u60f3\u5c0f\u59d0\u4e00\u5b9a\u4e0d\u559c\u6b22\u3002\u4e8e\u5927\u54e5\uff0c\u4f60\u6574\u4eba\u5728\u8fd9\u91cc\u4f4f\uff0c\u4e0d\u6c14\u95f7\u5417\uff1f\u201d \u3000\u3000\u4e8e\u7ba1\u5bb6\u7709\u5934\u4e00\u76b1\uff0c\u5fc3\u9053\uff1a\u201c\u4e3b\u4eba\u6b63\u8981\u5168\u529b\u5e94\u4ed8\u5f3a\u654c\uff0c\u5374\u4ece\u54ea\u91cc\u94bb\u51fa\u8fd9\u95e8\u5b50\u7f57\u5506\u4e2a\u6ca1\u5b8c\u6ca1\u4e86\u7684\u4eba\u5bb6\u6765\uff1f\u201d\u95ee\u9053\uff1a\u201c\u4f60\u5bb6\u8d35\u59d3\uff1f\u662f\u6211\u4eec\u4eb2\u621a\u4e48\uff1f\u201d \u3000\u3000\u7434\u513f\u8bf4\u9053\uff1a\u201c\u4f60\u731c\u731c\u770b\uff0c\u600e\u4e48\u6211\u4e00\u89c1\u5c31\u77e5\u4f60\u662f\u4e8e\u5927\u54e5\uff0c\u4f60\u5374\u8fde\u6211\u5bb6\u5c0f\u59d0\u59d3\u4ec0\u4e48\u4e5f\u4e0d\u77e5\u9053\u5462\uff1f\u6211\u82e5\u662f\u4e0d\u8bf4\u6211\u53eb\u7434\u513f\uff0c\u62c5\u4fdd\u4f60\u731c\u4e0a\u4e00\u5343\u5e74\uff0c\u4e5f\u731c\u4e0d\u5230\u6211\u53eb\u4ec0\u4e48\u3002\u554a\uff0c\u522b\u4e71\u8dd1\uff0c\u5c0f\u5fc3\u5c0f\u59d0\u751f\u6c14\u3002\u201d\u4e8e\u7ba1\u5bb6\u4e00\u5446\uff0c\u5374\u89c1\u5979\u4fef\u8eab\u62b1\u8d77\u4e00\u53ea\u5c0f\u732b\uff0c\u539f\u6765\u5979\u6700\u540e\u51e0\u53e5\u8bdd\u662f\u8ddf\u732b\u513f\u8bf4\u7684\u3002 \u3000\u3000\u4e8e\u7ba1\u5bb6\u5e2e\u5979\u628a\u540a\u7bee\u4e2d\u7684\u7269\u4e8b\u53d6\u4e86\u51fa\u6765\u3002\u7434\u513f\u8bf4\u9053\uff1a\u201c\u554a\u5537\uff0c\u4f60\u522b\u5f04\u4e71\u4e86\uff01\u8fd9\u7bb1\u5b50\u91cc\u5168\u662f\u5c0f\u59d0\u7684\u4e66\uff0c\u8fd9\u6837\u5012\u8fc7\u6765\uff0c\u4e66\u5c31\u4e71\u5566\u3002\u5509\uff0c\u5509\uff0c\u4e0d\u884c\u3002\u8fd9\u5170\u82b1\u95fb\u4e0d\u5f97\u7537\u4eba\u6c14\u3002\u5c0f\u59d0\u8bf4\u5170\u82b1\u662f\u6700\u6e05\u96c5\uff0c\u7537\u4eba\u5bb6\u8d70\u8fd1\u53bb\uff0c\u5b83\u5f53\u665a\u5c31\u8981\u8c22\u4e86\u3002\u201d \u3000\u3000\u4e8e\u7ba1\u5bb6\u5fd9\u5c06\u624b\u4e2d\u6367\u7740\u7684\u4e00\u5c0f\u76c6\u5170\u82b1\u653e\u4e0b\uff0c\u731b\u542c\u5f97\u80cc\u540e\u4e00\u4eba\u541f\u9053\uff1a\u201c\u6b32\u53d6\u9e23\u7434\u5f39\uff0c\u6068\u65e0\u77e5\u97f3\u8d4f\u3002\u201d\u58f0\u97f3\u751a\u662f\u602a\u5f02\u3002 \u3000\u3000\u4ed6\u5413\u4e86\u4e00\u8df3\uff0c\u6025\u5fd9\u56de\u5934\uff0c\u53cc\u638c\u6a2a\u80f8\uff0c\u6446\u4e86\u8fce\u654c\u7684\u67b6\u5f0f\uff0c\u5374\u89c1\u541f\u8bd7\u7684\u662f\u67b6\u4e0a\u90a3\u5934\u767d\u9e66\u9e49\u3002\u4ed6\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u547d\u4eba\u653e\u540a\u7bee\u63a5\u5c0f\u59d0\u4e0a\u6765\u3002\u90a3\u5976\u5988\u5374\u8bf4\u8981\u5148\u5f00\u7bb1\u5b50\uff0c\u53d6\u5757\u76ae\u88d8\u5728\u7bee\u4e2d\u57ab\u597d\uff0c\u514d\u5f97\u5c0f\u59d0\u5acc\u7bee\u5e95\u786c\u4e86\uff0c\u5750\u5f97\u4e0d\u8212\u670d\u3002\u5979\u6162\u541e\u7684\u53d6\u94a5\u5319\uff0c\u5f00\u7bb1\u5b50\uff0c\u53c8\u8ddf\u97e9\u5a76\u5b50\u5546\u91cf\u8be5\u57ab\u94f6\u72d0\u7684\u8fd8\u662f\u6c34\u8c82\u7684\uff0c\u4e8e\u7ba1\u5bb6\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u53c8\u6302\u5ff5\u5385\u4e0a\u6fc0\u6597\u60c5\u52bf\uff0c\u4e0d\u77e5\u962e\u58eb\u4e2d\u6027\u547d\u5982\u4f55\uff0c\u5f53\u4e0b\u5411\u4e00\u540d\u4ec6\u4eba\u5631\u5490\u597d\u597d\u62db\u547c\u5c0f\u59d0\uff0c\u81ea\u884c\u5954\u8fdb\u5385\u53bb\u3002 \u3000\u3000\u4ed6\u51fa\u5916\u8fce\u5bbe\uff0c\u53bb\u4e86\u597d\u4e00\u9635\u5b50\uff0c\u5385\u4e0a\u76f8\u6597\u7684\u60c5\u52bf\u5374\u6ca1\u591a\u5927\u53d8\u52a8\u3002\u962e\u58eb\u4e2d\u4ecd\u88ab\u53f3\u7ae5\u8feb\u5728\u5c4b\u89d2\u4e4b\u4e2d\uff0c\u53ea\u662f\u60c5\u5f62\u66f4\u4e3a\u72fc\u72c8\uff0c\u5de6\u811a\u978b\u5b50\u5df2\u7136\u8dcc\u843d\uff0c\u5934\u4e0a\u672c\u6765\u76d8\u7740\u7684\u8fab\u5b50\u4e5f\u7ed9\u5272\u53bb\u4e86\u534a\u622a\uff0c\u5934\u53d1\u6563\u4e86\u5f00\u6765\u3002\u66f9\u4e91\u5947\u3001\u6bb7\u5409\u3001\u5468\u4e91\u9633\u7b49\u5df2\u4ece\u5e84\u4e0a\u4f63\u4ec6\u5904\u501f\u5f97\u5175\u5203\uff0c\u6570\u6b21\u731b\u6251\u4e0a\u524d\u6551\u63f4\uff0c\u59cb\u7ec8\u88ab\u5de6\u7ae5\u62e6\u4f4f\uff0c\u53cd\u800c\u4e0e\u962e\u58eb\u4e2d\u8d8a\u79bb\u8d8a\u8fdc\u3002 \u3000\u3000\u5218\u5143\u9e64\u7b49\u672c\u60f3\u4e58\u673a\u52ab\u593a\u94c1\u76d2\uff0c\u4f46\u5728\u5de6\u7ae5\u7684\u5315\u9996\u4e0a\u5403\u4e86\u51e0\u6b21\u4e8f\uff0c\u53ea\u5f97\u9000\u5728\u540e\u9762\u3002\u5404\u4eba\u5fc3\u4e2d\u5374\u5140\u81ea\u4e0d\u670d\u6c14\uff0c\u773c\u89c1\u53cc\u7ae5\u4e0a\u62db\u6570\u5b9e\u5728\u5e76\u4e0d\u600e\u4e48\u51fa\u5947\uff0c\u5185\u529b\u4fee\u4e3a\u66f4\u662f\u5341\u5206\u6709\u9650\uff0c\u53ea\u4e0d\u8fc7\u4ed7\u7740\u4e24\u628a\u950b\u5229\u7edd\u4f26\u7684\u5315\u9996\uff0c\u4e00\u5957\u653b\u5b88\u547c\u5e94\u7684\u5251\u6cd5\uff0c\u7adf\u5c06\u4e00\u7fa4\u6c5f\u6e56\u8c6a\u58eb\u5236\u5f97\u7f1a\u624b\u7f1a\u811a\u3002 \u3000\u3000\u4e8e\u7ba1\u5bb6\u770b\u4e86\u4e00\u4f1a\uff0c\u5fc3\u60f3\uff1a\u201c\u4e3b\u4eba\u51fa\u95e8\u4e4b\u65f6\uff0c\u628a\u5e84\u4e0a\u7684\u4e8b\u90fd\u4ea4\u4e86\u7ed9\u6211\uff0c\u73b0\u4e0b\u5bbe\u5ba2\u5728\u5e84\u4e0a\u5982\u6b64\u53d7\u4eba\u6b3a\u8fb1\uff0c\u4e3b\u4eba\u989c\u9762\u4f55\u5b58\uff1f\u6211\u62da\u6b7b\u4e5f\u8981\u6551\u4e86\u8fd9\u59d3\u962e\u7684\u3002\u201d\u5f53\u4e0b\u5954\u5230\u81ea\u5df1\u623f\u4e2d\uff0c\u53d6\u4e86\u5f53\u5e74\u5728\u6c5f\u6e56\u4e0a\u6240\u7528\u7684\u7d2b\u91d1\u5200\uff0c\u8f6c\u56de\u5927\u5385\uff0c\u518d\u770b\u4e86\u770b\u53cc\u7ae5\u7684\u62db\u5f0f\uff0c\u53eb\u9053\uff1a\u201c\u4e24\u4f4d\u5c0f\u5144\u5f1f\u518d\u4e0d\u4f4f\u624b\uff0c\u6211\u4eec\u7389\u7b14\u5c71\u5e84\u53ef\u8981\u65e0\u793c\u4e86\u3002\u201d\u53f3\u7ae5\u53eb\u9053\uff1a\u201c\u4e3b\u4eba\u5dee\u6211\u4eec\u6765\u4e0b\u4e66\uff0c\u53c8\u6ca1\u53eb\u6211\u4eec\u8ddf\u4eba\u6253\u67b6\u3002\u4ed6\u53ea\u8981\u8d54\u4e86\u6211\u7684\u73e0\u513f\uff0c\u6211\u4eec\u9a6c\u4e0a\u5c31\u9976\u4ed6\u4e86\u3002\u201d\u8bf4\u7740\u8e0f\u4e0a\u4e00\u6b65\uff0c\u55e4\u7684\u4e00\u5251\uff0c\u962e\u58eb\u4e2d\u5de6\u80a9\u53c8\u7ed9\u5212\u7834\u4e86\u4e00\u9053\u53e3\u5b50\u3002 \u3000\u3000\u4e8e\u7ba1\u5bb6\u6b63\u8981\u63a5\u8bdd\uff0c\u53ea\u542c\u80cc\u540e\u4e00\u4e2a\u5973\u5b50\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u554a\u54df\uff0c\u522b\u6253\u67b6\uff01\u522b\u6253\u67b6\uff01\u6211\u5c31\u6700\u4e0d\u7231\u4eba\u5bb6\u52a8\u5200\u52a8\u67aa\u7684\u3002\u201d\u8fd9\u51e0\u53e5\u8bdd\u58f0\u97f3\u4e0d\u54cd\uff0c\u53ef\u662f\u5a07\u67d4\u65e0\u4f26\uff0c\u542c\u5728\u8033\u91cc\uff0c\u4eba\u4eba\u89c9\u5f97\u771f\u662f\u8bf4\u4e0d\u51fa\u7684\u53d7\u7528\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u90fd\u56de\u8fc7\u5934\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u9ec4\u8863\u5c11\u5973\u7b11\u541f\u541f\u7684\u7ad9\u5728\u95e8\u53e3\uff0c\u80a4\u5149\u80dc\u96ea\uff0c\u53cc\u76ee\u72b9\u4f3c\u4e00\u6cd3\u6e05\u6c34\uff0c\u5728\u5404\u4eba\u8138\u4e0a\u8f6c\u4e86\u51e0\u8f6c\u3002\u8fd9\u5c11\u5973\u5bb9\u8c8c\u79c0\u4e3d\u4e4b\u6781\uff0c\u5f53\u771f\u5982\u660e\u73e0\u751f\u6655\u3001\u7f8e\u7389\u83b9\u5149\uff0c\u7709\u76ee\u95f4\u9690\u7136\u6709\u4e00\u80a1\u4e66\u5377\u7684\u6e05\u6c14\u3002 \u3000\u3000\u5385\u4e0a\u8fd9\u4e9b\u4eba\u90fd\u662f\u6d6a\u8ff9\u6c5f\u6e56\u7684\u6b66\u6797\u8c6a\u5ba2\uff0c\u9661\u7136\u95f4\u4e0e\u8fd9\u6837\u4e00\u4e2a\u6587\u79c0\u5c11\u5973\u76f8\u9047\uff0c\u5b9b\u4f3c\u8d70\u8fdb\u4e86\u53e6\u4e00\u4e2a\u4e16\u754c\uff0c\u4e0d\u81ea\u7981\u7684\u4e3a\u5979\u4e00\u526f\u6e05\u96c5\u9ad8\u534e\u7684\u6c14\u6d3e\u6240\u6151\uff0c\u5404\u4f3c\u81ea\u60ed\u5f62\u79fd\uff0c\u4e0d\u6562\u4eb5\u6e0e\u3002 \u3000\u3000\u4e24\u4e2a\u7ae5\u513f\u5374\u5bf9\u90a3\u5c11\u5973\u6beb\u4e0d\u7406\u4f1a\uff0c\u4e58\u7740\u6bb7\u5409\u7b49\u4eba\u4e00\u6014\u4e4b\u95f4\uff0c\u53ee\u53ee\u5f53\u5f53\u4e00\u9635\u54cd\uff0c\u53c8\u5c06\u4ed6\u4eec\u624b\u4e2d\u5175\u5203\u9010\u4e00\u524a\u65ad\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4e24\u4e2a\u5c0f\u5144\u5f1f\u522b\u80e1\u95f9\u5566\uff0c\u628a\u4eba\u5bb6\u8eab\u4e0a\u4f24\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u53ef\u6709\u591a\u96be\u770b\u3002\u201d\u53f3\u7ae5\u9053\uff1a\u201c\u4ed6\u4e0d\u80af\u8d54\u6211\u7684\u73e0\u513f\u3002\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u73e0\u513f\uff1f\u201d\u53f3\u7ae5\u5251\u5c16\u6307\u4f4f\u962e\u58eb\u4e2d\u80f8\u819b\uff0c\u4fef\u8eab\u62fe\u8d77\u534a\u8fb9\u660e\u73e0\uff0c\u54ed\u4e27\u7740\u8138\u9053\uff1a\u201c\u4f60\u77a7\uff0c\u662f\u4ed6\u5f04\u574f\u7684\uff0c\u6211\u8981\u4ed6\u8d54\u3002\u201d\u90a3\u5c11\u5973\u8d70\u8fd1\u8eab\u53bb\uff0c\u63a5\u8fc7\u4e00\u770b\uff0c\u9053\uff1a\u201c\u554a\uff0c\u8fd9\u73e0\u513f\u5f53\u771f\u597d\uff0c\u6211\u4e5f\u8d54\u4e0d\u8d77\u3002\u8fd9\u6837\u5427\uff0c\u7434\u513f\uff0c\u201d\u56de\u5934\u5bf9\u8eab\u540e\u5c0f\u4e2b\u9b1f\u9053\uff1a\u201c\u53d6\u6211\u90a3\u5bf9\u7389\u9a6c\u513f\u6765\uff0c\u7ed9\u4e86\u8fd9\u4e24\u4e2a\u5c0f\u5144\u5f1f\u3002\u201d\u7434\u513f\u5fc3\u4e2d\u4e0d\u613f\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u59d0\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u504f\u4f60\u5c31\u6709\u8fd9\u4e48\u5c0f\u6c14\u3002\u4f60\u77a7\u4e24\u4e2a\u5c0f\u5144\u5f1f\u591a\u4fca\uff0c\u4f69\u4e86\u7389\u9a6c\uff0c\u90a3\u624d\u53eb\u76f8\u5f97\u76ca\u5f70\u5462\u3002\u201d \u3000\u3000\u4e24\u7ae5\u5bf9\u671b\u4e00\u773c\uff0c\u53ea\u89c1\u7434\u513f\u6253\u5f00\u4e00\u53ea\u63cf\u91d1\u7bb1\u5b50\uff0c\u53d6\u51fa\u4e00\u5bf9\u9526\u56ca\u4ea4\u7ed9\u5c11\u5973\u3002\u90a3\u5c11\u5973\u89e3\u5f00\u4e00\u53ea\u9526\u56ca\uff0c\u62ff\u51fa\u4e00\u53ea\u5c0f\u5c0f\u7389\u9a6c\uff0c\u9a6c\u53e3\u91cc\u6709\u4e1d\u7ee6\u4e3a\u7f30\u3002\u90a3\u5c11\u5973\u66ff\u53f3\u7ae5\u6302\u5728\u8170\u5e26\u4e0a\uff0c\u53c8\u628a\u53e6\u4e00\u53ea\u9526\u56ca\u4e2d\u6240\u88c5\u7684\u7389\u9a6c\u9012\u7ed9\u4e86\u5de6\u7ae5\u3002\u5de6\u7ae5\u8bf7\u5b89\u9053\u8c22\uff0c\u63a5\u5728\u624b\u91cc\uff0c\u53ea\u89c1\u90a3\u7389\u9a6c\u6676\u5149\u83b9\u6d01\uff0c\u523b\u5de5\u7cbe\u81f4\u5f02\u5e38\uff0c\u9a6c\u4f5c\u5954\u8dc3\u4e4b\u72b6\uff0c\u5f62\u4f53\u867d\u5c0f\uff0c\u5374\u662f\u8c8c\u76f8\u795e\u9a8f\uff0c\u7684\u975e\u51e1\u54c1\u3002\u4ed6\u4e00\u89c1\u4e4b\u4e0b\uff0c\u4fbf\u5341\u5206\u559c\u6b22\uff0c\u53ea\u662f\u4e0d\u660e\u90a3\u5c11\u5973\u6765\u5386\uff0c\u5fc3\u4e0b\u4e00\u65f6\u672a\u51b3\uff0c\u4e0d\u77e5\u662f\u5426\u8be5\u5f53\u53d7\u6b64\u91cd\u793c\u3002 \u3000\u3000\u53f3\u7ae5\u53c8\u5728\u5899\u7554\u6361\u8d77\u53e6\u4e00\u534a\u8fb9\u73e0\u513f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u9897\u662f\u591c\u660e\u5b9d\u73e0\uff0c\u548c\u54e5\u54e5\u7684\u662f\u4e00\u5bf9\u513f\u3002\u5c31\u7b97\u6709\u7389\u9a6c\uff0c\u603b\u662f\u4e0d\u9f50\u5168\u5566\uff01\u201d\u8bf4\u7740\u5341\u5206\u61ca\u607c\u3002 \u3000\u3000\u90a3\u5c11\u5973\u4e00\u89c1\u4e24\u4eba\u76f8\u8c8c\u6253\u626e\uff0c\u5df2\u77e5\u8fd9\u5bf9\u53cc\u751f\u5144\u5f1f\u76f8\u4eb2\u76f8\u7231\uff0c\u6bc1\u4e86\u660e\u73e0\u4e8b\u5c0f\uff0c\u4e0d\u75db\u5feb\u7684\u662f\u5728\u5c06\u4e24\u4eba\u9970\u7269\u5f04\u6210\u5f02\u6837\uff0c\u914d\u4e0d\u6210\u5bf9\uff0c\u5f53\u4e0b\u62ff\u8d77\u7389\u9a6c\uff0c\u5c06\u4e24\u4e2a\u534a\u8fb9\u660e\u73e0\u653e\u5728\u7389\u9a6c\u53cc\u773c\u4e4b\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6709\u4e00\u4e2a\u4e3b\u610f\uff0c\u5c06\u534a\u8fb9\u73e0\u513f\u5d4c\u5728\u7389\u9a6c\u773c\u4e0a\u3002\u73e0\u5b50\u65e2\u80fd\u591c\u660e\uff0c\u7389\u9a6c\u665a\u4e0a\u4e24\u773c\u653e\u5149\uff0c\u5c82\u4e0d\u597d\u770b\uff1f\u201d\u5de6\u7ae5\u5927\u559c\uff0c\u4ece\u8fab\u513f\u4e0a\u6458\u4e0b\u73e0\u5b50\uff0c\u4f38\u5315\u9996\u5256\u6210\u4e24\u534a\uff0c\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u54b1\u4fe9\u7684\u73e0\u513f\u548c\u7389\u9a6c\u90fd\u4e00\u6a21\u4e00\u6837\u5566\u3002\u201d\u53f3\u7ae5\u56de\u55d4\u4f5c\u559c\uff0c\u5411\u5c11\u5973\u8fde\u8fde\u9053\u8c22\uff0c\u53c8\u5411\u962e\u58eb\u4e2d\u8bf7\u4e86\u4e2a\u5b89\uff0c\u9053\uff1a\u201c\u884c\u5566\uff0c\u4f60\u8001\u522b\u751f\u6c14\u3002\u201d\u962e\u58eb\u4e2d\u6ee1\u8eab\u8840\u6c61\uff0c\u5fc3\u4e2d\u607c\u6012\u5f02\u5e38\uff0c\u5374\u53c8\u4e0d\u6562\u51fa\u58f0\ue40a\u9a82\u3002 \u3000\u3000\u53f3\u7ae5\u62c9\u7740\u5de6\u7ae5\u7684\u624b\uff0c\u4fbf\u8981\u8d70\u51fa\u3002\u5de6\u7ae5\u5411\u90a3\u5c11\u5973\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u539a\u8d50\uff0c\u8bf7\u95ee\u59d1\u5a18\u5c0a\u59d3\uff0c\u4e3b\u4eba\u95ee\u8d77\uff0c\u597d\u6709\u5bf9\u7b54\u3002\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u5bb6\u4e3b\u4eba\u662f\u8c01\uff1f\u201d\u5de6\u7ae5\u9053\uff1a\u201c\u5bb6\u4e3b\u59d3\u80e1\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u4e00\u542c\uff0c\u767b\u65f6\u8138\u4e0a\u53d8\u8272\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u662f\u7684\u5bb6\u7ae5\u3002\u201d\u4e24\u7ae5\u4e00\u9f50\u8eac\u8eab\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d\u90a3\u5c11\u5973\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u59d3\u82d7\u3002\u4f60\u5bb6\u4e3b\u4eba\u95ee\u8d77\uff0c\u5c31\u8bf4\u8fd9\u5bf9\u7389\u9a6c\u662f\u91d1\u9762\u4f5b\u82d7\u7237\u7684\u5973\u513f\u7ed9\u7684\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u7fa4\u8c6a\u65e0\u4e0d\u52a8\u5bb9\u3002\u91d1\u9762\u4f5b\u5a01\u540d\u8d6b\u8d6b\uff0c\u4e07\u60f3\u4e0d\u5230\u4ed6\u7684\u5973\u513f\u7adf\u662f\u8fd9\u6837\u4e00\u4e2a\u5a07\u67d4\u817c\u8146\u7684\u5c11\u5973\u3002\u77a7\u5979\u795e\u6c14\uff0c\u82e5\u975e\u4faf\u95e8\u5de8\u5ba4\u7684\u5c0f\u59d0\uff0c\u5c31\u662f\u4e16\u4ee3\u4e66\u9999\u4eba\u5bb6\u7684\u95fa\u5973\uff0c\u54ea\u91cc\u50cf\u662f\u6c5f\u6e56\u5927\u4fa0\u4e4b\u5973\u3002\u53cc\u7ae5\u5bf9\u671b\u4e00\u773c\uff0c\u9f50\u628a\u7389\u9a6c\u653e\u5728\u51e0\u4e0a\uff0c\u4e00\u8a00\u4e0d\u53d1\u7684\u8f6c\u8eab\u51fa\u5385\u3002 \u3000\u3000\u90a3\u5c11\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u4e0d\u8a00\u8bed\u3002\u7434\u513f\u6b22\u5929\u559c\u5730\u7684\u6536\u8d77\u7389\u9a6c\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u8fd9\u4e24\u4e2a\u5b69\u513f\u4e0d\u8bc6\u597d\u6b79\uff0c\u5c0f\u59d0\u8d4f\u8d50\u8fd9\u6837\u597d\u7684\u4e1c\u897f\uff0c\u4ed6\u4eec\u90fd\u4e0d\u8981\uff0c\u8981\u662f\u6211\u554a\u2026\u2026\u201d\u90a3\u5c11\u5973\u7b11\u9053\uff1a\u201c\u522b\u591a\u8bf4\u5566\uff0c\u4e5f\u4e0d\u6015\u4eba\u5bb6\u7b11\u54b1\u4eec\u5bd2\u789c\u3002\u201d \u3000\u3000\u5b9d\u6811\u5927\u5e08\u8d8a\u4f17\u800c\u524d\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u539f\u6765\u59d1\u5a18\u662f\u82d7\u5927\u4fa0\u7684\u5343\u91d1\uff0c\u4ee4\u5c0a\u53ef\u597d\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u591a\u8c22\u3002\u5bb6\u4e25\u6258\u798f\u5b89\u5eb7\u3002\u8bf7\u95ee\u5927\u5e08\u4e0a\u4e0b\uff1f\u201d\u5b9d\u6811\u5fae\u7b11\u9053\uff1a\u201c\u8001\u8872\u5b9d\u6811\u3002\u59d1\u5a18\u82b3\u540d\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u540d\u53eb\u82d7\u82e5\u5170\uff0c\u542c\u4e86\u8fd9\u8bdd\u987f\u7136\u8138\u4e0a\u4e00\u7ea2\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u7684\u540d\u5b57\uff0c\u600e\u80e1\u4e71\u8ddf\u4eba\u8bf4\u5f97\u7684\uff1f\u201d\u5f53\u4e0b\u4e0d\u7b54\u95ee\u8bdd\uff0c\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u8bf7\u5bbd\u5750\uff0c\u665a\u8f88\u8981\u8fdb\u5185\u5802\u62dc\u89c1\u4f2f\u6bcd\u3002\u201d\u8bf4\u7740\u5411\u7fa4\u8c6a\u88e3\u887d\u884c\u793c\u3002 \u3000\u3000\u4f17\u4eba\u9707\u4e8e\u5979\u7236\u4eb2\u7684\u540d\u5934\uff0c\u54ea\u6562\u6709\u4e1d\u6beb\u6020\u6162\uff0c\u90fd\u606d\u606d\u656c\u656c\u7684\u8fd8\u793c\uff0c\u5747\u60f3\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u6ca1\u534a\u70b9\u4ed7\u52bf\u6b3a\u4eba\u7684\u9a84\u6001\uff0c\u5f53\u771f\u96be\u5f97\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u5f85\u4f17\u4eba\u90fd\u5750\u4e0b\u4e86\uff0c\u53c8\u544a\u7f6a\u4e00\u904d\uff0c\u8fd9\u624d\u5165\u5185\u3002\u53ea\u89c1\u5927\u95e8\u5916\u8fdb\u6765\u4e03\u516b\u540d\u5bb6\u4e01\u4ec6\u5987\uff0c\u62ac\u7740\u94fa\u76d6\u7bb1\u7b3c\u7b49\u7269\uff0c\u770b\u6765\u90fd\u662f\u8ddf\u6765\u670d\u4f8d\u82d7\u5c0f\u59d0\u7684\u3002\u9676\u767e\u5c81\u3001\u9676\u5b50\u5b89\u7236\u5b50\u5bf9\u671b\u4e00\u773c\uff0c\u5fc3\u4e2d\u90fd\u60f3\uff1a \u3000\u3000\u201c\u82e5\u662f\u6211\u7236\u5b50\u5728\u9053\u4e0a\u9047\u89c1\u8fd9\u4e00\u6279\u4eba\uff0c\u5b9a\u7136\u5f53\u4f5c\u662f\u5b98\u5ba6\u8c6a\u5bcc\u7684\u7737\u5c5e\uff0c\u52bf\u5fc5\u52a8\u624b\u884c\u52ab\uff0c\u8fd9\u4e71\u5b50\u53ef\u5c31\u95ef\u5f97\u5927\u4e86\u3002\u201d \u3000\u3000\u962e\u58eb\u4e2d\u4f38\u8896\u62ed\u62b9\u8eab\u4e0a\u8840\u6c61\uff0c\u5e78\u597d\u53f3\u7ae5\u5e76\u975e\u771f\u6b32\u4f24\u4ed6\uff0c\u6bcf\u9053\u4f24\u53e3\u90fd\u53ea\u6d45\u6d45\u7684\u5212\u7834\u76ae\u8089\uff0c\u5e76\u65e0\u5927\u788d\u3002\u7530\u9752\u6587\u8d70\u8fd1\u76f8\u52a9\uff0c\u53d6\u51fa\u91d1\u521b\u836f\u7ed9\u4ed6\u6b62\u8840\u3002\u962e\u58eb\u4e2d\u6495\u5f00\u5de6\u80f8\u8863\u895f\uff0c\u8ba9\u5979\u88f9\u4f24\uff0c\u5ffd\u7136\u95f4\u5f53\u5577\u4e00\u54cd\uff0c\u90a3\u53ea\u94c1\u76d2\u843d\u5728\u5730\u4e0b\u3002\u7fa4\u8c6a\u4e0d\u7ea6\u800c\u540c\u7684\u4e00\u9f50\u8dc3\u8d77\uff0c\u4f38\u624b\u90fd\u6765\u62a2\u593a\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u7ad9\u5f97\u6700\u8fd1\uff0c\u5de6\u624b\u5212\u4e86\u4e2a\u5708\u5b50\uff0c\u6321\u5f00\u4f17\u4eba\uff0c\u7acb\u5373\u4fef\u8eab\u62fe\u76d2\uff0c\u624b\u6307\u521a\u89e6\u5230\u76d2\u9762\uff0c\u7a81\u89c9\u4e00\u80a1\u5927\u529b\u5728\u80a9\u5934\u4e00\u649e\uff0c\u8eab\u4e0d\u7531\u4e3b\u7684\u8dcc\u5f00\u6570\u6b65\uff0c\u5f85\u5f97\u62ff\u6869\u7ad9\u5b9a\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u53ea\u89c1\u94c1\u76d2\u5df2\u6367\u5728\u5b9d\u6811\u624b\u4e2d\u3002 \u3000\u3000\u7fa4\u8c6a\u90fd\u6015\u4ed6\u672c\u9886\u4e86\u5f97\uff0c\u53ea\u773c\u7741\u7741\u7684\u671b\u7740\u4ed6\uff0c\u6ca1\u4eba\u6562\u5f00\u53e3\u8bf4\u8bdd\u3002 \u3000\u3000\u9694\u4e86\u7247\u523b\uff0c\u66f9\u4e91\u5947\u9053\uff1a\u201c\u5927\u5e08\uff0c\u8fd9\u53ea\u76d2\u5b50\u662f\u6211\u5929\u9f99\u95e8\u7684\u9547\u95e8\u4e4b\u5b9d\uff0c\u8bf7\u4f60\u8fd8\u6765\u3002\u201d\u5b9d\u6811\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u8fd9\u662f\u8d35\u6d3e\u9547\u95e8\u4e4b\u5b9d\uff0c\u90a3\u4e48\u76d2\u4e2d\u662f\u4f55\u5b9d\u7269\uff0c\u5b9d\u7269\u662f\u4f55\u6765\u5386\uff0c\u4f60\u65e2\u662f\u5929\u9f99\u638c\u95e8\uff0c\u5c31\u8be5\u77e5\u9053\u3002\u53ea\u987b\u8bf4\u5f97\u660e\u767d\uff0c\u5c31\u62ff\u53bb\u7f62\uff01\u201d\u8bf4\u7740\u53cc\u624b\u6258\u4e86\u94c1\u76d2\uff0c\u5411\u524d\u4f38\u51fa\u3002 \u3000\u3000\u66f9\u4e91\u5947\u6ee1\u8138\u901a\u7ea2\uff0c\u53cc\u624b\u4f38\u51fa\u4e86\u4e00\u534a\uff0c\u4e0d\u6562\u53bb\u63a5\uff0c\u53c8\u4e0d\u597d\u610f\u601d\u7f29\u56de\uff0c\u505c\u5728\u7a7a\u4e2d\uff0c\u6162\u6162\u5782\u4e0b\u3002\u539f\u6765\u4ed6\u53ea\u89c1\u5e08\u7236\u5bf9\u94c1\u76d2\u5341\u5206\u73cd\u89c6\uff0c\u5b88\u85cf\u4e25\u5bc6\uff0c\u5374\u4ece\u672a\u89c1\u4ed6\u6253\u5f00\u8fc7\u76d2\u76d6\uff0c\u522b\u8bf4\u5b9d\u7269\u6765\u5386\uff0c\u8fde\u662f\u4ec0\u4e48\u5b9d\u7269\u4e5f\u4e0d\u77e5\u9053\u3002\u962e\u58eb\u4e2d\u3001\u6bb7\u5409\u867d\u662f\u5929\u9f99\u95e8\u7684\u524d\u8f88\u9ad8\u624b\uff0c\u4e5f\u662f\u9762\u9762\u76f8\u89d1\uff0c\u8bf4\u4e0d\u51fa\u4e2a\u6240\u4ee5\u3002\u5468\u4e91\u9633\u5ffd\u9053\uff1a\u201c\u6211\u4eec\u81ea\u7136\u77e5\u9053\uff0c\u90a3\u662f\u4e00\u67c4\u5b9d\u5200\u3002\u201d \u3000\u3000\u4ed6\u5728\u5929\u9f99\u95e8\u4e2d\u8bba\u6b66\u529f\u53ea\u662f\u4e00\u6d41\u811a\u8272\uff0c\u7d20\u6765\u4e0d\u5f97\u5e08\u7236\u5ba0\u7231\uff0c\u4e3a\u4eba\u53c8\u975e\u5e72\u7ec3\uff0c\u7a81\u7136\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u6765\uff0c\u962e\u58eb\u4e2d\u7b49\u90fd\u662f\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\uff1f\u8d81\u65e9\u522b\u80e1\u8bf4\u516b\u9053\u3002\u201d\u54ea\u77e5\u5b9a\u6811\u5374\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u4e00\u67c4\u5b9d\u5200\u3002\u4f60\u53ef\u77e5\u8fd9\u53e3\u5200\u539f\u6765\u662f\u8c01\u7684\uff1f\u600e\u4e48\u843d\u5165\u5929\u9f99\u95e8\u4e4b\u624b\uff1f\u201d \u3000\u3000\u962e\u58eb\u4e2d\u7b49\u4e0d\u6599\u5468\u4e91\u9633\u5c45\u7136\u4e00\u8bed\u4e2d\u7684\uff0c\u65e0\u4e0d\u5927\u4e3a\u8be7\u5f02\uff0c\u4e00\u9f50\u6ce8\u76ee\uff0c\u7b49\u4ed6\u518d\u8bf4\u3002\u5374\u89c1\u4ed6\u9752\u767d\u8272\u7684\u8138\u4e0a\u7ea2\u4e86\u4e00\u7ea2\uff0c\u968f\u5373\u53c8\u8f6c\u9752\u8272\uff0c\u60bb\u60bb\u7684\u9053\uff1a\u201c\u8fd9\u662f\u6211\u5929\u9f99\u95e8\u7956\u4f20\u4e0b\u6765\u7684\uff0c\u8c01\u5f97\u4e86\u5b9d\u5200\uff0c\u8c01\u5c31\u505a\u638c\u95e8\u3002\u201d\u6bb7\u5409\u63a5\u53e3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u662f\u672c\u95e8\u5b9d\u5200\uff0c\u5357\u5317\u4e24\u5b97\u8f6e\u6d41\u638c\u7ba1\u3002\u201d \u3000\u3000\u5b9d\u6811\u6447\u5934\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff01\u6211\u6599\u4f60\u4eec\u4e5f\u4e0d\u4f1a\u77e5\u9053\u3002\u201d\u5468\u4e91\u9633\u9053\uff1a\u201c\u96be\u9053\u4f60\u5c31\u77e5\u9053\u4e86\uff1f\u201d\u5b9d\u6811\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u524d\uff0c\u6211\u5c31\u77e5\u9053\u3002 \u3000\u3000\u4e0e\u6b64\u95f4\u5e84\u4e3b\u7684\u4e89\u7aef\u3002\u4e5f\u5c31\u7531\u6b64\u800c\u8d77\u3002\u4e2d\u95f4\u82e5\u4e0d\u662f\u6709\u8fd9\u4e9b\u74dc\u845b\uff0c\u8001\u8872\u53c8\u4f55\u5fc5\u9080\u5404\u4f4d\u4e0a\u5c71\uff1f\u201d \u3000\u3000\u5929\u9f99\u7fa4\u8c6a\u3001\u9676\u6c0f\u7236\u5b50\u3001\u5218\u718a\u5e08\u5144\u5f1f\u7b49\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u8fd9\u8001\u548c\u5c1a\u679c\u7136\u4e0d\u6000\u597d\u610f\uff0c\u539f\u6765\u4e5f\u60f3\u52ab\u593a\u8fd9\u76d2\u4e2d\u5b9d\u5200\u3002\u6211\u4eec\u4eca\u65e5\u8eab\u9677\u7edd\u5730\uff0c\u90a3\u53ef\u662f\u6709\u6b7b\u65e0\u751f\u4e86\u3002\u201d\u4f17\u4eba\u60f3\u5230\u6b64\u5904\uff0c\u53ea\u542c\u5237\u7684\u4e00\u58f0\uff0c\u4e00\u4eba\u4eae\u51fa\u4e86\u5175\u5203\uff0c\u63a5\u7740\u5237\u5237\u3001\u53ee\u53ee\u4e00\u9635\u54cd\u58f0\u8fc7\u53bb\uff0c\u7fa4\u8c6a\u5df2\u5404\u6267\u5175\u5203\u5c06\u5b9d\u6811\u56f4\u4f4f\u3002\u962e\u58eb\u4e2d\u7b49\u5175\u5203\u88ab\u53cc\u7ae5\u524a\u65ad\u4e86\u7684\uff0c\u4e5f\u4fef\u8eab\u628a\u65ad\u5200\u65ad\u5251\u62a2\u5728\u624b\u91cc\u3002 \u3000\u3000\u5b9d\u6811\u5728\u4eba\u4e1b\u4e2d\u7f13\u7f13\u8f6c\u4e86\u4e2a\u5708\u5b50\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u5404\u4f4d\u8981\u8ddf\u8001\u548c\u5c1a\u52a8\u624b\u4e48\uff1f\u201d\u7fa4\u8c6a\u6012\u76ee\u800c\u89c6\uff0c\u65e0\u4eba\u63a5\u53e3\u3002\u8fd9\u65f6\u7ad9\u5f97\u8fd1\u4e86\uff0c\u4eba\u4eba\u770b\u5f97\u6e05\u695a\uff0c\u5b9d\u6811\u867d\u7136\u80e1\u5b50\u82b1\u767d\uff0c\u8138\u6709\u76b1\u7eb9\uff0c\u4f46\u53cc\u76ee\u70af\u70af\uff0c\u5e74\u7eaa\u5176\u5b9e\u4e5f\u4e0d\u751a\u5927\u3002 \u3000\u3000\u5218\u5143\u9e64\u9000\u540e\u4e00\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u5927\u4f19\u513f\u9f50\u4e0a\uff0c\u5148\u6740\u8001\u548c\u5c1a\u3002\u54b1\u4eec\u81ea\u5df1\u7684\u4e8b\uff0c\u4e0b\u4e86\u5c71\u6162\u6162\u5546\u91cf\u3002\u201d\u4ed6\u53ea\u89c9\u5728\u5c71\u5cf0\u4e0a\u591a\u803d\u4e00\u523b\uff0c\u4fbf\u591a\u4e00\u5206\u5371\u9669\u3002\u7fa4\u8c6a\u90fd\u611f\u5728\u8fd9\u5c71\u5e84\u4e2d\u5750\u7acb\u4e0d\u5b89\uff0c\u5218\u5143\u9e64\u7684\u8bdd\u6b63\u5408\u5fc3\u610f\u3002\u6b63\u8981\u4e00\u6d8c\u800c\u4e0a\uff0c\u5ffd\u542c\u95e8\u5916\u7830\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u4f3c\u662f\u5f00\u4e86\u4e00\u70ae\u3002 \u3000\u3000\u4f17\u4eba\u6115\u7136\u76f8\u987e\u3002\u9694\u4e86\u7247\u523b\uff0c\u4e8e\u7ba1\u5bb6\u5306\u5306\u4ece\u5916\u5954\u8fdb\uff0c\u8138\u6709\u60ca\u60f6\u4e4b\u8272\uff0c\u53eb\u9053\uff1a\u201c\u5404\u4f4d\uff0c\u5927\u4e8b\u4e0d\u5999\uff01\u201d\u66f9\u4e91\u5947\u53eb\u9053\uff1a\u201c\u5230\u4e86\u4e48\uff1f\u201d\u4e8e\u7ba1\u5bb6\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u662f\u3002\u6211\u4eec\u4e0a\u4e0b\u5c71\u5cf0\u7684\u957f\u7d22\u548c\u7ede\u76d8\uff0c\u90fd\u7ed9\u4eba\u5bb6\u6bc1\u4e86\u3002\u201d\u4f17\u4eba\u5413\u4e86\u4e00\u8df3\uff0c\u4e03\u5634\u516b\u820c\u7684\u95ee\u9053\uff1a\u201c\u90a3\u600e\u4e48\u4f1a\uff1f\u201d\u201c\u6ca1\u7b2c\u4e8c\u6761\u7d22\u513f\u4e86\u4e48\uff1f\u201d\u201c\u6709\u6ca1\u522b\u7684\u6cd5\u513f\u4e0b\u53bb\uff1f\u201d\u4e8e\u7ba1\u5bb6\u9053\uff1a\u201c\u5cf0\u4e0a\u5c31\u53ea\u8fd9\u6761\u957f\u7d22\uff0c\u5c0f\u4eba\u4e00\u65f6\u4e0d\u5bdf\uff0c\u7adf\u7136\u7ed9\u98de\u72d0\u624b\u4e0b\u90a3\u4e24\u4e2a\u7ae5\u513f\u6bc1\u4e86\u3002\u201d\u5b9d\u6811\u53d8\u8272\u9053\uff1a\u201c\u600e\u4e48\u6bc1\u7684\uff1f\u201d \u3000\u3000\u4e8e\u7ba1\u5bb6\u9053\uff1a\u201c\u5f1f\u5144\u4eec\u7f12\u4e86\u90a3\u4e24\u4e2a\u5c0f\u9b3c\u5934\u4e0b\u5cf0\uff0c\u90fd\u8fdb\u5c4b\u4f11\u606f\uff0c\u5ffd\u542c\u5230\u7206\u70b8\u4e4b\u58f0\uff0c\u62a2\u51fa\u53bb\u770b\u65f6\uff0c\u89c1\u7ede\u76d8\u548c\u957f\u7d22\u5df2\u70b8\u5f97\u7c89\u788e\uff0c\u5b9a\u662f\u8fd9\u4e24\u4e2a\u5929\u6740\u7684\u5c0f\u9b3c\u5728\u7ede\u76d8\u4e2d\u653e\u4e86\u70b8\u836f\uff0c\u5c06\u836f\u5f15\u901a\u4e0b\u5c71\u5cf0\uff0c\u70b9\u4e86\u706b\u70e7\u4e0a\u6765\u7684\u3002\u201d\u4f17\u4eba\u4e00\u5446\uff0c\u7eb7\u7eb7\u62a2\u51fa\u95e8\u53bb\uff0c\u679c\u89c1\u7ede\u76d8\u70b8\u6210\u4e86\u788e\u7247\uff0c\u957f\u7d22\u4e1c\u4e00\u6bb5\u897f\u4e00\u6bb5\u6563\u5f97\u6ee1\u5730\u3002\u5e78\u597d\u7ede\u76d8\u65c1\u7684\u6c49\u5b50\u90fd\u5df2\u8d70\u5f00\uff0c\u65e0\u4eba\u6b7b\u4f24\u3002 \u3000\u3000\u6bb7\u5409\u95ee\u5b9d\u6811\u9053\uff1a\u201c\u5927\u5e08\uff0c\u98de\u72d0\u6b64\u4e3e\u6709\u4f55\u7528\u610f\uff1f\u201d\u5b9d\u6811\u9053\uff1a\u201c\u90a3\u6709\u4ec0\u4e48\u96be\u731c\uff1f\u4ed6\u8981\u54b1\u4eec\u5c3d\u6570\u997f\u6b7b\u5728\u8fd9\u5cf0\u4e0a\u3002\u201d\u6bb7\u5409\u9053\uff1a\u201c\u54b1\u4eec\u8ddf\u4ed6\u65e0\u6028\u65e0\u4ec7\u3002\u201d\u5b9d\u6811\u9053\uff1a\u201c\u4ed6\u53ef\u4e0e\u6b64\u95f4\u7684\u4e3b\u4eba\u4ec7\u6df1\u4f3c\u6d77\u3002\u518d\u8bf4\uff0c\u94c1\u76d2\u5728\u4f60\u4eec\u624b\u91cc\uff0c\u90a3\u5c31\u662f\u8ddf\u4ed6\u7ed3\u4e0a\u4e86\u6881\u5b50\u3002\u201d\u6bb7\u5409\u9053\uff1a\u201c\u98de\u72d0\u4e5f\u8981\u8fd9\u94c1\u76d2\uff1f\u201d\u5b9d\u6811\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u5417\uff1f\u201d \u3000\u3000\u4f17\u4eba\u4e00\u60f3\u5230\u4e24\u4e2a\u7ae5\u513f\u602a\u5f02\u7684\u6b66\u529f\uff0c\u5fc3\u4e2d\u90fd\u662f\u4e00\u822c\u7684\u5ff5\u5934\uff1a \u3000\u3000\u201c\u7ae5\u513f\u5df2\u662f\u8fd9\u822c\u4e86\u5f97\uff0c\u6b63\u4e3b\u513f\u66f4\u4e0d\u7528\u8bf4\u4e86\u3002\u201d\u9ed8\u9ed8\u8ddf\u7740\u5b9d\u6811\u56de\u8fdb\u5927\u5385\u3002 \u3000\u3000\u53ea\u89c1\u82d7\u82e5\u5170\u5df2\u4ece\u5185\u5802\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u5e08\uff0c\u90a3\u8981\u628a\u54b1\u4eec\u90fd\u56f0\u6b7b\u5728\u8fd9\u513f\uff1f\u201d\u5b9d\u6811\u6c89\u7740\u8138\u9053\uff1a\u201c\u6b63\u662f\u3002\u5927\u4f19\u513f\u5750\u4e0a\u4e86\u4e00\u6761\u8239\uff0c\u5f97\u60f3\u4e2a\u6cd5\u513f\u4e0b\u5cf0\u3002\u201d\u82d7\u82e5\u5170\u9053\uff1a\u201c\u90a3\u4e0d\u7528\u803d\u5fc3\uff0c\u6211\u7239\u7239\u65e5\u5185\u5c31\u4f1a\u4e0a\u6765\uff0c\u81ea\u80fd\u6551\u54b1\u4eec\u4e0b\u53bb\u3002\u201d\u4f17\u4eba\u4e00\u60f3\uff0c\u91d1\u9762\u4f5b\u82d7\u4eba\u51e4\u7684\u5973\u513f\u5728\u6b64\uff0c\u4ed6\u5c82\u80fd\u8896\u624b\u4e0d\u987e\uff1f\u4e0d\u7531\u5f97\u987f\u611f\u5bbd\u5fc3\u3002\u53ea\u6709\u5218\u5143\u9e64\u6697\u6697\u6447\u5934\uff0c\u5374\u4e5f\u4e0d\u4fbf\u660e\u8a00\u3002 \u3000\u3000\u5b9d\u6811\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u867d\u7136\u6b66\u529f\u76d6\u4e16\uff0c\u4f46\u8fd9\u96ea\u5cf0\u51e0\u767e\u4e08\u9ad8\uff0c\u4e00\u65f6\u4e4b\u95f4\u600e\u80fd\u4e0a\u6765\uff1f\u201d\u82d7\u82e5\u5170\u9053\uff1a\u201c\u65e2\u6709\u4eba\u80fd\u4e0a\u6765\u5efa\u4e86\u5e84\u5b50\uff0c\u6211\u7239\u7239\u600e\u4f1a\u4e0a\u4e0d\u6765\uff1f\u201d\u5b9d\u6811\u9053\uff1a\u201c\u590f\u5929\u5cf0\u4e0a\u51b0\u878d\u96ea\u6d88\uff0c\u4e0a\u6765\u4e0d\u96be\uff0c\u8fd9\u65f6\u5019\u6b63\u5f53\u4e25\u5bd2\uff0c\u8981\u5f85\u96ea\u6d88\uff0c\u5c11\u8bf4\u4e5f\u5f97\u4e09\u4e2a\u6708\u3002\u7ba1\u5bb6\uff0c\u8fd9\u5c71\u4e0a\u8d2e\u5907\u4e86\u51e0\u4e2a\u6708\u7cae\u98df\uff1f\u201d\u4e8e\u7ba1\u5bb6\u9053\uff1a\u201c\u4e0b\u5c71\u91c7\u8d2d\u7cae\u98df\u7684\u7ba1\u5bb6\u9884\u8ba1\u540e\u65e5\u80fd\u56de\u3002\u6b64\u95f4\u6240\u8d2e\u7cae\u98df\u672c\u6765\u8fd8\u53ef\u7528\u5f97\u4e8c\u5341\u591a\u5929\uff0c\u73b0\u4e0b\u6dfb\u4e86\u5404\u4f4d\u5bbe\u5ba2\u4e0e\u82d7\u5c0f\u59d0\u5e26\u6765\u7684\u4ec6\u5987\u4f7f\u5973\uff0c\u7b97\u6765\u53ea\u6709\u5341\u65e5\u4e4b\u7cae\u4e86\u3002\u201d \u3000\u3000\u4f17\u4eba\u8138\u4e0a\u53d8\u8272\uff0c\u9ed8\u7136\u4e0d\u8bed\uff0c\u5fc3\u4e2d\u90fd\u5728\u5492\u9a82\u6b79\u6bd2\u66f9\u4e91\u5947\u5ffd\u9053\uff1a\u201c\u54b1\u4eec\u6162\u6162\u4ece\u5c71\u5cf0\u4e0a\u6e9c\u4e0b\u53bb\u2026\u2026\u201d\u53ea\u8bf4\u4e86\u534a\u53e5\u8bdd\uff0c\u4fbf\u77e5\u4e0d\u59a5\uff0c\u5fd9\u5373\u4f4f\u53e3\u3002\u8fd9\u5c71\u5cf0\u9661\u5ced\u65e0\u6bd4\uff0c\u53ea\u6015\u6e9c\u4e0d\u5230\u4e24\u4e09\u4e08\uff0c\u7acb\u65f6\u4fbf\u6454\u4e0b\u53bb\u4e86\u3002\u65c1\u4eba\u4e00\u9f50\u77a7\u7740\u4ed6\uff0c\u5747\u60f3\uff1a\u201c\u8fd9\u4eba\u8349\u5305\u4e4b\u6781\u3002\u201d \u3000\u3000\u66f9\u4e91\u5947\u89c1\u4e86\u5404\u4eba\u773c\u8272\uff0c\u4e0d\u7531\u5f97\u80c0\u7ea2\u4e86\u8138\u3002 \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u82e5\u662f\u5927\u5bb6\u7ec8\u4e8e\u4e0d\u514d\u997f\u6b7b\uff0c\u4e5f\u5f97\u77e5\u9053\u4e2a\u7f18\u7531\u3002 \u3000\u3000\u5927\u5e08\uff0c\u5230\u5e95\u8ddf\u54b1\u4eec\u6709\u4f55\u4ec7\u51a4\uff1f\u4ed6\u6709\u4ec0\u4e48\u672c\u4e8b\uff0c\u53eb\u6b64\u95f4\u4e3b\u4eba\u8fd9\u751f\u5fcc\u60ee\uff1f\u8fd9\u94c1\u76d2\u53c8\u6709\u4ec0\u4e48\u5e72\u7cfb\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u95ee\u4ee3\u4f17\u4eba\u8bf4\u51fa\u4e86\u5fc3\u5934\u4e4b\u8bdd\u3002\u7fa4\u8c6a\u820d\u547d\u4e89\u593a\u94c1\u76d2\uff0c\u6709\u4eba\u8fd8\u56e0\u6b64\u4e27\u751f\uff0c\u53ef\u662f\u9664\u4e86\u77e5\u9053\u76d2\u4e2d\u85cf\u6709\u91cd\u5b9d\u4e4b\u5916\uff0c\u6ca1\u4e00\u4e2a\u8bf4\u5f97\u51fa\u539f\u59d4\uff0c\u5f53\u4e0b\u4e00\u9f50\u671b\u7740\u5b9d\u6811\uff0c\u76fc\u4ed6\u89e3\u91ca\u3002 \u3000\u3000\u5b9d\u6811\u9053\uff1a\u201c\u597d\uff0c\u4e8b\u5df2\u81f3\u6b64\uff0c\u6025\u4e5f\u65e0\u7528\u3002\u5927\u5bb6\u5f00\u8bda\u5e03\u516c\u8bf4\u4e2a\u660e\u767d\uff0c\u9f50\u5fc3\u5408\u529b\uff0c\u4e5f\u8bb8\u80fd\u60f3\u5f97\u51fa\u4e0b\u5c71\u7684\u6cd5\u5b50\u3002\u82e5\u662f\u81ea\u76f8\u706b\u5e76\u6b8b\u6740\uff0c\u53ea\u6709\u6b7b\u5f97\u66f4\u5feb\uff0c\u6b63\u597d\u4e2d\u4e86\u98de\u72d0\u7684\u5978\u8ba1\u3002\u201d\u7fa4\u8c6a\u8f70\u7136\u79f0\u662f\uff0c\u56e2\u56e2\u5750\u4e0b\u3002 \u3000\u3000\u6b64\u65f6\u5c71\u4e0a\u5bd2\u6c14\u6e10\u589e\uff0c\u4e8e\u7ba1\u5bb6\u547d\u4eba\u5728\u7089\u4e2d\u52a0\u67f4\u6dfb\u706b\u3002\u5404\u4eba\u9759\u542c\u5b9d\u6811\u8bf4\u8bdd\u3002 \u3000\u3000\u5b9d\u6811\u7aef\u8d77\u76d6\u7897\uff0c\u559d\u4e86\u4e00\u53e3\u8336\uff0c\u5148\u8d5e\u58f0\uff1a\u201c\u597d\u8336\uff01\u201d\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u6b64\u4e8b\u5f53\u771f\u8bf4\u6765\u8bdd\u957f\u3002\u54b1\u4eec\u5148\u770b\u770b\u76d2\u4e2d\u7684\u5b9d\u5200\u53ef\u597d\uff1f\u201d\u4f17\u4eba\u9f50\u58f0\u53eb\u597d\u3002\u5b9d\u6811\u5c06\u94c1\u76d2\u9012\u7ed9\u66f9\u4e91\u5947\uff0c\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u662f\u5929\u9f99\u5317\u5b97\u638c\u95e8\uff0c\u8bf7\u6253\u5f00\u7ed9\u5927\u5bb6\u77a7\u77a7\u3002\u201d \u3000\u3000\u66f9\u4e91\u5947\u60f3\u8d77\u9676\u5b50\u5b89\u66fe\u4ece\u76d2\u4e2d\u5c04\u51fa\u77ed\u7bad\uff0c\u4f24\u4eba\u6027\u547d\uff0c\u53ea\u6015\u76d2\u5185\u66f4\u85cf\u6709\u4ec0\u4e48\u6697\u5668\uff0c\u53cc\u624b\u5c06\u76d2\u5b50\u63a5\u8fc7\uff0c\u5374\u4e0d\u6562\u53bb\u63ed\u76d2\u76d6\u3002\u5b9d\u6811\u7b11\u563b\u563b\u7684\u77a7\u7740\u4ed6\uff0c\u4e00\u8bed\u4e0d\u53d1\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u76d2\u4e0a\u751f\u6ee1\u4e86\u94c1\u9508\uff0c\u6591\u70c2\u9a73\u6742\uff0c\u8150\u8680\u51f9\u51f9\u51f8\u51f8\uff0c\u663e\u662f\u767e\u5e74\u4ee5\u4e0a\u7684\u53e4\u7269\uff0c\u5374\u4e5f\u4e0d\u89c1\u6709\u4f55\u5f02\u5904\u3002 \u3000\u3000\u66f9\u4e91\u5947\u5fc3\u60f3\uff1a\u201c\u6211\u82e5\u4e0d\u6562\u52a8\u624b\u5f00\u76d2\uff0c\u5c82\u4e0d\u6559\u9676\u5b50\u5b89\u8fd9\u8d3c\u5c0f\u89d1\u4e86\u3002\u201d\u4e00\u54ac\u7259\uff0c\u4f38\u53f3\u624b\u53bb\u63ed\u76d2\u76d6\u3002\u54ea\u77e5\u4e00\u63ed\u4e4b\u4e0b\uff0c\u76d2\u76d6\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u51dd\u76ee\u5bdf\u770b\uff0c\u76d2\u4e0a\u5e76\u65e0\u9501\u5b54\u94ae\u7eca\uff0c\u4e0d\u77e5\u4f55\u4ee5\u7adf\u63ed\u5b83\u4e0d\u5f00\uff0c\u5f53\u4e0b\u53cc\u624b\u52a0\u52b2\uff0c\u90a3\u94c1\u76d2\u5b9b\u4f3c\u7528\u4e00\u5757\u6574\u94c1\u94f8\u6210\uff0c\u5168\u65e0\u52a8\u9759\u3002 \u3000\u3000\u7530\u9752\u6587\u89c1\u4ed6\u80c0\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u77e5\u9053\u76d2\u4e2d\u5fc5\u6709\u673a\u62ec\uff0c\u5982\u6b64\u86ee\u5f00\u786c\u63ed\u975e\u4f46\u65e0\u7528\uff0c\u53ea\u6015\u53cd\u800c\u53d7\u4f24\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5468\u5e08\u54e5\uff0c\u4f60\u6765\u5f00\u5427\u3002\u201d\u5468\u4e91\u9633\u795e\u8272\u8fdf\u7591\uff0c\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u4e0d\u77e5\u2026\u2026\u201d\u7530\u9752\u6587\u4ece\u66f9\u4e91\u5947\u624b\u4e2d\u63a5\u8fc7\u94c1\u76d2\uff0c\u653e\u5728\u5468\u4e91\u9633\u624b\u4e2d\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4f1a\u7684\u3002\u201d\u5468\u4e91\u9633\u5411\u5979\u77aa\u4e86\u4e00\u773c\uff0c\u5c06\u94c1\u76d2\u653e\u5728\u684c\u4e0a\uff0c\u4f38\u624b\u6478\u7740\u76d2\u76d6\uff0c\u4e0d\u5411\u4e0a\u63ed\uff0c\u5374\u5728\u56db\u89d2\u6328\u6b21\u6380\u4e86\u4e09\u6380\uff0c\u7136\u540e\u4f38\u59c6\u6307\u5728\u76d2\u5e95\u6b63\u4e2d\u5411\u4e0a\u4e00\u6309\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u76d2\u76d6\u5f39\u4e86\u5f00\u6765\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u4e0e\u66f9\u4e91\u5947\u540c\u65f6\u5411\u4ed6\u6a2a\u4e86\u4e00\u773c\uff0c\u5fc3\u4e2d\u5600\u5495\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u5f00\u542f\u6b64\u76d2\uff1f\u201d\u7acb\u5373\u8f6c\u5934\u671b\u76d2\uff0c\u53ea\u89c1\u76d2\u4e2d\u679c\u6709\u4e00\u67c4\u77ed\u5200\uff0c\u5957\u5728\u9798\u4e2d\u3002\u66f9\u4e91\u5947\u201c\u54e6\u201d\u7684\u4e00\u58f0\u3002\u8fd9\u53e3\u5b9d\u5200\uff0c\u4ed6\u5f53\u5e74\u66fe\u89c1\u5e08\u7236\u4f7f\u8fc7\u3002\u66fe\u524a\u65ad\u8fc7\u4e0d\u5c11\u82f1\u96c4\u8c6a\u6770\u7684\u5175\u5203\u3002 \u3000\u3000\u5b9d\u6811\u4f38\u624b\u62ff\u8d77\u77ed\u5200\uff0c\u6307\u7740\u5200\u9798\u4e0a\u523b\u7740\u7684\u4e00\u884c\u5b57\u9053\uff1a\u201c\u4f17\u4f4d\u8bf7\u770b\u3002\u201d\u53ea\u89c1\u90a3\u5200\u9798\u751f\u6ee1\u94dc\u7eff\u94c1\u9508\uff0c\u9664\u4e86\u9576\u6709\u4e00\u5757\u7ea2\u5b9d\u77f3\u5916\uff0c\u53ea\u662f\u5e73\u5e73\u65e0\u5947\u7684\u4e00\u628a\u65e7\u5200\uff0c\u9798\u8eab\u523b\u7740\u4e24\u884c\u5b57\u9053\uff1a\u6740\u4e00\u4eba\u5982\u6740\u6211\u7236\u6deb\u4e00\u4eba\u5982\u6deb\u627e\u6bcd \u3000\u3000\u8fd9\u5341\u56db\u4e2a\u5b57\u6781\u4e3a\u5e73\u6613\u6d45\u767d\uff0c\u5374\u81ea\u6709\u4e00\u80a1\u8c6a\u610f\u4fa0\u6c14\uff0c\u8dc3\u7136\u800c\u51fa\u3002 \u3000\u3000\u5b9d\u6811\u9053\uff1a\u201c\u5404\u4f4d\u53ef\u77e5\u8fd9\u5341\u56db\u4e2a\u5b57\u7684\u6765\u5386\u4e48\uff1f\u201d\u4f17\u4eba\u90fd\u9053\uff1a\u201c\u4e0d\u77e5\u201d\u5b9d\u6811\u9053\uff1a\u201c\u8fd9\u662f\u95ef\u738b\u674e\u81ea\u6210\u6240\u9057\u4e0b\u7684\u519b\u4ee4\u3002\u8fd9\u4e00\u67c4\u5200\uff0c\u5c31\u662f\u674e\u95ef\u738b\u5f53\u5e74\u6307\u6325\u767e\u4e07\u5927\u519b\u3001\u8f6c\u6218\u5343\u91cc\u7684\u519b\u5200\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u542c\uff0c\u4e00\u9f50\u79bb\u5e2d\u800c\u8d77\uff0c\u671b\u7740\u5b9d\u6811\u624b\u4e2d\u6258\u7740\u7684\u8fd9\u53e3\u77ed\u5200\uff0c\u5fc3\u4e2d\u5c06\u4fe1\u5c06\u7591\u3002\u6b64\u65f6\u8ddd\u674e\u95ef\u738b\u5df2\u6709\u4e00\u767e\u4f59\u5e74\uff0c\u53ef\u662f\u5728\u8349\u83bd\u7fa4\u8c6a\u5fc3\u4e2d\uff0c\u95ef\u738b\u7684\u58f0\u5a01\u4ecd\u662f\u663e\u8d6b\u65e0\u6bd4\u3002\u5b9d\u6811\u9053\uff1a\u201c\u5404\u4f4d\u4e0d\u4fe1\uff0c\u8bf7\u770b\u6b64\u9762\u3002\u201d\u8bf4\u7740\u5c06\u5200\u9798\u7ffb\u4e86\u8fc7\u6765\u3002\u53ea\u89c1\u8fd9\u4e00\u8fb9\u523b\u7740\u201c\u5949\u5929\u5021\u4e49\u201d\u56db\u5b57\u3002\u5b9d\u6811\u9053\uff1a\u201c\u674e\u95ef\u738b\u5f53\u5e74\u7684\u79f0\u53f7\uff0c\u4fbf\u53eb\u505a\u5949\u5929\u5021\u4e49\u5927\u5143\u5e05\u3002\u201d\u7fa4\u8c6a\u8fd9\u624d\u4fe1\u670d\u3002 \u3000\u3000\u5b9d\u6811\u53c8\u9053\uff1a\u201c\u5f53\u5e74\u4e5d\u5341\u516b\u5be8\u54cd\u9a6c\u3001\u4e8c\u5341\u56db\u5bb6\u5be8\u4e3b\u7ed3\u4e49\u8d77\u4e8b\uff0c\u7fa4\u63a8\u674e\u81ea\u6210\u4e3a\u5927\u5143\u5e05\u3002\u4ed6\u540e\u6765\u79f0\u4e3a\u95ef\u738b\uff0c\u8f6c\u6218\u5341\u4f59\u5e74\uff0c\u7ec8\u4e8e\u653b\u7834\u5317\u4eac\uff0c\u5efa\u5927\u987a\u56fd\u53f7\u3002\u5d07\u796f\u7687\u5e1d\u8feb\u5f97\u540a\u6b7b\u7164\u5c71\u3002\u82e5\u975e\u6c49\u5978\u5434\u4e09\u6842\u5356\u56fd\uff0c\u5f15\u6e05\u5175\u5165\u5173\uff0c\u8fd9\u5929\u4e0b\u5c31\u662f\u59d3\u674e\u7684\u4e86\u3002\u81ea\u53e4\u8349\u83bd\u82f1\u96c4\uff0c\u4ece\u672a\u6709\u5982\u95ef\u738b\u8fd9\u822c\u5a01\u98ce\u7684\u3002\u201d\u4ed6\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u5509\uff0c\u53ea\u53ef\u60dc\u4ed6\u521a\u6210\u5927\u4e8b\uff0c\u8f6c\u773c\u6210\u7a7a\u3002\u5d07\u796f\u5341\u4e03\u5e74\u4e09\u6708\u95ef\u738b\u7834\u5317\u4eac\uff0c\u56db\u6708\u51fa\u4eac\u8fce\u6218\u6e05\u5175\uff0c\u6708\u5e95\u5175\u8d25\u897f\u5954\u3002\u8fd9\u82b1\u82b1\u6c5f\u5c71\u4ece\u6b64\u9001\u8fdb\u4e86\u6ee1\u6e05\u9791\u5b50\u7684\u624b\u91cc\u3002\u201d \u3000\u3000\u5218\u5143\u9e64\u5411\u4ed6\u77aa\u4e86\u4e00\u773c\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u548c\u5c1a\u597d\u5927\u80c6\uff0c\u7adf\u6562\u51fa\u6b64\u5927\u9006\u4e0d\u9053\u4e4b\u8a00\u3002\u201d\u5b9d\u6811\u7f13\u7f13\u8fd8\u5200\u5165\u76d2\uff0c\u8bf4\u9053\uff1a\u201c\u95ef\u738b\u4e0e\u5434\u4e09\u6842\u5927\u6218\u65f6\u4e2d\u7bad\u91cd\u4f24\uff0c\u4ece\u5317\u4eac\u9000\u5230\u5c71\u897f\u3001\u9655\u897f\uff0c\u6e05\u5175\u548c\u5434\u4e09\u6842\u4e00\u8def\u8ffd\u6765\uff0c\u53c8\u9000\u5230\u6cb3\u5357\u3001\u6e56\u5e7f\uff0c\u5c06\u58eb\u81ea\u76f8\u6b8b\u6740\uff0c\u90e8\u5c5e\u56db\u6563\uff0c\u540e\u6765\u9000\u5230\u6b66\u660c\u5e9c\u901a\u5c71\u53bf\u4e5d\u5bab\u5c71\uff0c\u654c\u5175\u91cd\u91cd\u56f4\u56f0\uff0c\u51e0\u6b21\u51b2\u6740\u4e0d\u51fa\uff0c\u7ec8\u4e8e\u82f1\u96c4\u5230\u4e86\u672b\u8def\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u671b\u7740\u76d2\u4e2d\u519b\u5200\uff0c\u60f3\u6a61\u95ef\u738b\u5f53\u5e74\u7684\u82f1\u70c8\u96c4\u98ce\uff0c\u4e0d\u7981\u795e\u5f80\uff0c\u5f85\u60f3\u5230\u4ed6\u5175\u8d25\u8eab\u6b7b\uff0c\u53c8\u81ea\u9eef\u7136\u3002 \u3000\u3000\u5b9d\u6811\u9053\uff1a\u201c\u95ef\u738b\u8eab\u8fb9\u6709\u56db\u540d\u536b\u58eb\uff0c\u4e2a\u4e2a\u6b66\u827a\u9ad8\u5f3a\uff0c\u4e00\u76f4\u8d64\u80c6\u5fe0\u5fc3\u7684\u4fdd\u4ed6\u3002\u8fd9\u56db\u540d\u536b\u58eb\u4e00\u4e2a\u59d3\u80e1\uff0c\u4e00\u4e2a\u59d3\u82d7\uff0c\u4e00\u4e2a\u59d3\u8303\uff0c\u4e00\u4e2a\u59d3\u7530\uff0c\u519b\u4e2d\u79f0\u4e3a\u80e1\u82d7\u8303\u7530\u3002\u201d \u3000\u3000\u6bb7\u5409\u3001\u7530\u9752\u6587\u7b49\u4e00\u542c\u5230\u201c\u80e1\u82d7\u8303\u7530\u201d\u56db\u5b57\uff0c\u5df2\u77e5\u8fd9\u56db\u540d\u536b\u58eb\u5fc5\u4e0e\u4eca\u65e5\u4e4b\u4e8b\u6709\u91cd\u5927\u5173\u8fde\u3002\u7530\u9752\u6587\u659c\u773c\u671b\u4e86\u82d7\u82e5\u5170\u4e00\u773c\uff0c\u53ea\u89c1\u5979\u62ff\u7740\u4e00\u6839\u62e8\u706b\u68d2\u8f7b\u8f7b\u62e8\u7740\u7089\u4e2d\u70ad\u706b\uff0c\u5140\u81ea\u51fa\u795e\uff0c\u5979\u767d\u7389\u822c\u7684\u8138\u988a\u88ab\u706b\u5149\u4e00\u6620\uff0c\u5fae\u73b0\u7ea2\u6655\u3002 \u3000\u3000\u5b9d\u6811\u62ac\u5934\u671b\u7740\u5c4b\u9876\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u56db\u5927\u536b\u58eb\u8ddf\u7740\u95ef\u738b\u51fa\u6b7b\u5165\u751f\uff0c\u4e0d\u77e5\u7ecf\u5386\u8fc7\u591a\u5c11\u8270\u9669\uff0c\u4e5f\u4e0d\u77e5\u6551\u8fc7\u95ef\u738b\u591a\u5c11\u6b21\u6027\u547d\u3002\u95ef\u738b\u81ea\u5c06\u4ed6\u4eec\u5f85\u4f5c\u5fc3\u8179\u3002\u8fd9\u56db\u4eba\u4e4b\u4e2d\uff0c\u53c8\u4ee5\u90a3\u59d3\u80e1\u7684\u6b66\u529f\u6700\u5f3a\uff0c\u4eba\u6700\u80fd\u5e72\uff0c\u95ef\u738b\u519b\u4e2d\u79f0\u4ed6\u4e3a\u2018\u98de\u5929\u72d0\u72f8\u2019\uff01\u201d\u4f17\u4eba\u542c\u5230\u8fd9\u91cc\uff0c\u90fd\u662f\u201c\u54e6\u201d\u7684\u4e00\u58f0\u3002 \u3000\u3000\u5b9d\u6811\u7ee7\u7eed\u8bf4\u4ed6\u7684\u6545\u4e8b\uff1a\u201c\u95ef\u738b\u88ab\u56f4\u5728\u4e5d\u5bab\u5c71\u4e0a\uff0c\u5371\u6025\u4e07\u5206\uff0c\u773c\u89c1\u6d3e\u51fa\u53bb\u6c42\u63f4\u7684\u4f7f\u8005\u4e00\u5230\u5c71\u811a\uff0c\u5c31\u88ab\u654c\u519b\u622a\u4f4f\u6740\u6b7b\uff0c\u53ea\u5f97\u6d3e\u59d3\u82d7\u3001\u59d3\u8303\u3001\u59d3\u7530\u4e09\u540d\u536b\u58eb\u9ed1\u591c\u91cc\u51b2\u51fa\u53bb\u6c42\u6551\u3002\u59d3\u80e1\u7684\u7559\u4e0b\u4fdd\u62a4\u95ef\u738b\u3002\u4e0d\u6599\u7b49\u5230\u82d7\u8303\u7530\u4e09\u540d\u536b\u58eb\u9886\u5f97\u63f4\u519b\u524d\u6765\u6551\u9a7e\uff0c\u95ef\u738b\u5374\u5df2\u88ab\u5bb3\u8eab\u6b7b\u4e86\u3002 \u3000\u3000\u201c\u4e09\u540d\u536b\u58eb\u5927\u54ed\u4e00\u573a\uff0c\u90a3\u59d3\u8303\u7684\u5f53\u573a\u5c31\u8981\u81ea\u520e\u6b89\u4e3b\u3002\u4f46\u53e6\u5916\u4e24\u540d\u536b\u58eb\u8bf4\u9053\uff0c\u8be5\u5f53\u5148\u62a5\u8fd9\u8840\u6d77\u6df1\u4ec7\u3002\u4e09\u4eba\u5728\u4e5d\u5bab\u5c71\u56db\u4e0b\u91cc\u6253\u542c\u95ef\u738b\u6b89\u96be\u7684\u8be6\u60c5\u3002\u90a3\u59d3\u80e1\u7684\u536b\u58eb\u4f3c\u4e4e\u5c1a\u5728\u4eba\u95f4\uff0c\u4e09\u4eba\u5fc3\u60f3\u6b64\u4eba\u6b66\u827a\u76d6\u4e16\uff0c\u8db3\u667a\u591a\u8c0b\uff0c\u82e5\u5f97\u6709\u4ed6\u4e3b\u6301\uff0c\u95ef\u738b\u5927\u4ec7\u53ef\u62a5\u3002\u5f53\u4e0b\u5206\u5934\u63a2\u8bbf\u4ed6\u7684\u4e0b\u843d\u3002 \u3000\u3000\u201c\u6b66\u6797\u4e2d\u53e4\u8001\u76f8\u4f20\uff0c\u53ea\u56e0\u8fd9\u756a\u627e\u5bfb\uff0c\u751f\u51fa\u4e00\u573a\u8f69\u7136\u5927\u6ce2\u6765\u3002 \u3000\u3000\u82d7\u8303\u7530\u4e09\u4eba\u65e5\u540e\u5c06\u5f53\u65f6\u60c5\u666f\uff0c\u90fd\u8be6\u8be6\u7ec6\u7ec6\u8bf4\u7ed9\u4e86\u81ea\u5df1\u7684\u513f\u5b50\u77e5\u9053\uff0c\u5e76\u7acb\u4e0b\u5bb6\u89c4\uff0c\u6bcf\u4e00\u4ee3\u90fd\u987b\u5c06\u8fd9\u756a\u8bdd\u4f20\u7ed9\u540e\u55e3\uff0c\u597d\u6559\u82d7\u8303\u7530\u4e09\u5bb6\u5b50\u5b59\uff0c\u4e16\u4e16\u4ee3\u4ee3\u4e0d\u5fd8\u6b64\u4e8b\u3002\u201d \u3000\u3000\u5b9d\u6811\u8bf4\u5230\u8fd9\u91cc\uff0c\u773c\u671b\u82d7\u82e5\u5170\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u548c\u5c1a\u662f\u5916\u4eba\uff0c\u53ea\u77e5\u9053\u4e2a\u5927\u7565\u3002\u82d7\u59d1\u5a18\u82e5\u80af\u7ed9\u6211\u4eec\u8bf4\u8bf4\uff0c\u5b9a\u7136\u8be6\u7ec6\u5f97\u591a\u3002\u201d\u4f17\u4eba\u5fc3\u4e2d\u5747\u60f3\uff1a\u201c\u539f\u6765\u82d7\u4eba\u51e4\u7236\u5973\u4fbf\u662f\u8fd9\u59d3\u82d7\u536b\u58eb\u7684\u540e\u4ee3\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u773c\u671b\u706b\u76c6\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u6211\u4e03\u5c81\u90a3\u4e00\u5e74\uff0c\u6709\u4e00\u665a\u89c1\u7239\u7239\u78e8\u6d17\u957f\u5251\u3002\u6211\u8bf4\u6211\u6015\u5200\u5251\uff0c\u8981\u7239\u7239\u6536\u8d77\u4e86\u522b\u73a9\u3002\u7239\u8bf4\u8fd9\u67c4\u5251\u8fd8\u5f97\u6740\u4e00\u4e2a\u4eba\uff0c\u624d\u80fd\u6536\u8d77\u6c38\u8fdc\u4e0d\u7528\u3002\u6211\u6402\u4f4f\u4ed6\u5934\u9888\uff0c\u6c42\u4ed6\u4e0d\u8981\u6740\u4eba\uff0c\u4ed6\u5c31\u8ddf\u6211\u8bf4\u4e86\u4e00\u4e2a\u6545\u4e8b\u3002 \u3000\u3000\u201c\u4ed6\u8bf4\u8bb8\u591a\u8bb8\u591a\u5e74\u4ee5\u524d\uff0c\u8001\u767e\u59d3\u90fd\u7a77\u5f97\u6ca1\u996d\u5403\u3001\u6ca1\u8863\u7a7f\uff0c\u5927\u5bb6\u53ea\u597d\u5403\u6811\u76ae\u8349\u6839\u3002\u8fde\u6811\u76ae\u8349\u6839\u4e5f\u5403\u5b8c\u4e86\uff0c\u53ea\u597d\u5403\u6ce5\u5df4\uff0c\u5f88\u591a\u4eba\u90fd\u997f\u6b7b\u4e86\u3002\u505a\u5988\u5988\u7684\u6ca1\u996d\u5403\uff0c\u751f\u4e0d\u51fa\u5976\uff0c\u8bb8\u591a\u5c0f\u5b69\u5b50\u4e5f\u90fd\u5728\u5988\u5988\u6000\u91cc\u997f\u6b7b\u4e86\u3002\u53ef\u662f\u5b98\u5e9c\u8fd8\u662f\u8981\u5411\u8001\u767e\u59d3\u5f81\u7cae\uff0c\u8d22\u4e3b\u8fd8\u8981\u5411\u7a77\u4eba\u8feb\u79df\u50ac\u503a\u3002\u8001\u767e\u59d3\u62ff\u4e0d\u51fa\uff0c\u53c8\u6709\u8bb8\u591a\u4eba\u7ed9\u5b98\u5e9c\u6740\u4e86\uff0c\u7ed9\u8d22\u4e3b\u6349\u53bb\u5173\u8d77\u6765\u3002\u7239\u7239\u6559\u6211\u5531\u4e86\u4e00\u4e2a\u6b4c\u513f\u3002\u8bf4\u662f\u90a3\u65f6\u5019\u4e00\u4f4d\u6587\u6b66\u53cc\u5168\u7684\u516c\u5b50\u4f5c\u7684\u3002\u8981\u4e0d\u8981\u6211\u5ff5\u51fa\u6765\u554a\uff1f\u201d \u3000\u3000\u4f17\u4eba\u9f50\u58f0\u9053\uff1a\u201c\u8bf7\u59d1\u5a18\u5ff5\u3002\u201d\u5b9d\u6811\u542c\u5979\u8bf4\u201c\u6587\u6b66\u53cc\u5168\u7684\u516c\u5b50\u201d\u4e03\u5b57\uff0c\u77e5\u9053\u5fc5\u662f\u674e\u81ea\u6210\u624b\u4e0b\u7684\u5927\u5c06\u674e\u5ca9\uff0c\u53ea\u542c\u5979\u5ff5\u9053\uff1a\u201c\u5e74\u6765\u8757\u65f1\u82e6\u9891\u4ecd\uff0c\u56bc\u556e\u79be\u82d7\u5c81\u4e0d\u767b\u3002\u7c73\u4ef7\u5347\u817e\u589e\u6570\u500d\uff0c\u9ece\u6c11\u5904\u5904\u4e0d\u804a\u751f\u3002\u8349\u6839\u6728\u53f6\u6743\u5145\u8179\uff0c\u513f\u5973\u5471\u5471\u76f8\u5411\u54ed\u3002\u91dc\u7511\u5c18\u98de\u7228\u7edd\u70df\uff0c\u6570\u65e5\u96be\u6c42\u4e00\u9910\u7ca5\u3002\u5b98\u5e9c\u5f81\u7cae\u7eb5\u864e\u5dee\uff0c\u8c6a\u5bb6\u7d22\u503a\u5982\u72fc\u8c7a\u3002\u53ef\u601c\u6b8b\u5598\u5b58\u547c\u5438\uff0c\u9b42\u9b44\u5148\u5f52\u6cc9\u58e4\u57cb\u3002\u9ab7\u9ac5\u904d\u5730\u79ef\u5982\u5c71\uff0c\u4e1a\u91cd\u96be\u8fc7\u9965\u997f\u5173\u3002\u80fd\u4e0d\u6559\u4eba\u6570\u884c\u6cea\uff1f\u6cea\u6d12\u8fd8\u6210\u70b9\u8840\u6591\u3002\u201d \u3000\u3000\u6b64\u65f6\u6b63\u5f53\u4e7e\u9686\u4e2d\u53f6\uff0c\u867d\u79f0\u592a\u5e73\u76db\u4e16\uff0c\u53ef\u662f\u6bcf\u5e74\u6c34\u707e\u65f1\u707e\uff0c\u8001\u767e\u59d3\u65e5\u5b50\u4e5f\u4e0d\u597d\u8fc7\u3002\u4f17\u4eba\u542c\u5979\u4e00\u5b57\u4e00\u53e5\uff0c\u5ff5\u5f97\u5b57\u6b63\u8154\u5706\uff0c\u58f0\u97f3\u4e2d\u5145\u6ee1\u4e86\u51c4\u695a\u4e4b\u60c5\uff0c\u60f3\u8d77\u5728\u6c5f\u6e56\u4e0a\u7684\u6240\u89c1\u6240\u95fb\uff0c\u90fd\u4e0d\u7981\u8038\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u6211\u7239\u7239\u8bf4\uff0c\u5230\u540e\u6765\u8001\u767e\u59d3\u5b9e\u5728\u518d\u4e5f\u6371\u4e0d\u4e0b\u53bb\u4e86\uff0c\u7ec8\u4e8e\u6709\u4e00\u4f4d\u5927\u82f1\u96c4\u51fa\u6765\uff0c\u9886\u7740\u4ed6\u4eec\u6253\u5230\u5317\u4eac\u3002\u4f46\u53ef\u60dc\u8fd9\u4f4d\u82f1\u96c4\u505a\u4e86\u7687\u5e1d\u4e4b\u540e\uff0c\u5904\u4e8b\u4e0d\u5f53\uff0c\u4e5f\u6ca1\u6709\u5584\u5f85\u767e\u59d3\uff0c\u624b\u4e0b\u7684\u4f17\u5c06\u519b\uff0c\u53cd\u800c\u53bb\u5bb3\u767e\u59d3\uff0c\u62a2\u767e\u59d3\u7684\u4e1c\u897f\uff0c\u4e8e\u662f\u8001\u767e\u59d3\u53c8\u4e0d\u670d\u90a3\u82f1\u96c4\u4e86\u3002\u4ed6\u4ee5\u4e3a\u8001\u767e\u59d3\u7684\u5fc3\u90fd\u5411\u7740\u90a3\u4f4d\u505a\u6b4c\u513f\u7684\u516c\u5b50\uff0c\u4fbf\u5c06\u90a3\u516c\u5b50\u6740\u4e86\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u4ed6\u624b\u4e0b\u7684\u4eba\u90fd\u4e71\u4e86\u8d77\u6765\u3002\u8fd9\u4f4d\u5927\u82f1\u96c4\u6ca1\u591a\u4e45\u5c31\u7ed9\u5978\u4eba\u5bb3\u6b7b\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u624d\u9053\uff1a\u201c\u4ed6\u624b\u4e0b\u7684\u4e09\u540d\u536b\u58eb\u53bb\u627e\u5bfb\u53e6\u4e00\u4e2a\u536b\u58eb\uff0c\u8981\u4ed6\u51fa\u4e2a\u4e3b\u610f\uff0c\u7ed9\u8fd9\u4f4d\u5927\u82f1\u96c4\u62a5\u4ec7\u3002 \u3000\u3000\u201c\u8fd9\u65f6\u5019\u5f02\u65cf\u4eba\u6765\u505a\u4e86\u7687\u5e1d\uff0c\u5230\u5904\u6349\u62ff\u90a3\u4f4d\u5927\u82f1\u96c4\u7684\u670b\u53cb\u3002\u8fd9\u4e09\u4e2a\u536b\u58eb\u6ca1\u6cd5\u5b89\u8eab\uff0c\u53ea\u5f97\u4e54\u88c5\u6539\u626e\u3002\u4e00\u4e2a\u626e\u6210\u5356\u836f\u7684\u6c5f\u6e56\u90ce\u4e2d\uff0c\u4e00\u4e2a\u626e\u6210\u53eb\u5316\u5b50\uff0c\u53e6\u4e00\u4e2a\u529b\u6c14\u6700\u5927\uff0c\u5c31\u626e\u6210\u4e86\u811a\u592b\u3002\u4ed6\u4eec\u548c\u90a3\u7b2c\u56db\u4e2a\u536b\u58eb\u662f\u7ed3\u4e49\u5144\u5f1f\uff0c\u6570\u5341\u5e74\u6765\u540c\u7518\u5171\u82e6\uff0c\u771f\u6bd4\u4eb2\u5144\u5f1f\u8fd8\u8981\u597d\u3002\u4ed6\u4eec\u65f6\u65f6\u523b\u523b\u60f3\u5ff5\u4ed6\u3002\u53ef\u662f\u627e\u4e86\u4e03\u516b\u5e74\uff0c\u7adf\u6ca1\u534a\u70b9\u97f3\u8baf\uff0c\u60f3\u6765\u4ed6\u5b9a\u662f\u5728\u4fdd\u62a4\u90a3\u4f4d\u5927\u82f1\u96c4\u7684\u65f6\u5019\u6218\u6b7b\u4e86\uff0c\u4e09\u4e2a\u4eba\u90fd\u662f\u5341\u5206\u4f24\u5fc3\u3002\u201d \u3000\u3000\u4f17\u4eba\u542c\u5979\u8bf4\u8bdd\u7684\u8bed\u6c14\u58f0\u8c03\uff0c\u5c31\u4f3c\u662f\u7ed9\u5c0f\u5b69\u5b50\u8bb2\u6545\u4e8b\u4e00\u822c\uff0c\u6599\u662f\u5b66\u7740\u5f53\u5e74\u7236\u4eb2\u7684\u53e3\u543b\uff0c\u5747\u60f3\uff1a\u7d20\u95fb\u91d1\u9762\u4f5b\u5916\u53f7\u4e2d\u867d\u6709\u4e2a\u201c\u4f5b\u201d\u5b57\uff0c\u4e3a\u4eba\u5374\u662f\u5ac9\u6076\u5982\u4ec7\uff0c\u51fa\u624b\u72e0\u8fa3\uff0c\u53ef\u662f\u5bf9\u5973\u513f\u5374\u662f\u8fd9\u822c\u6e29\u67d4\u6148\u7231\u3002\u53ea\u542c\u5979\u9053\uff1a\u201c\u518d\u8fc7\u51e0\u5e74\uff0c\u4ed6\u4eec\u51b3\u5b9a\u4e0d\u518d\u5bfb\u8bbf\u8fd9\u4f4d\u4e49\u5144\u4e86\u3002\u4e09\u4eba\u4e00\u5546\u91cf\uff0c\u90fd\u8bf4\u5bb3\u6b7b\u5927\u82f1\u96c4\u7684\u90a3\u4e2a\u6c49\u5978\u73b0\u5728\u5c01\u4e86\u738b\uff0c\u5728\u4e91\u5357\u4eab\u798f\uff0c\u51b3\u610f\u53bb\u523a\u6b7b\u4ed6\uff0c\u597d\u66ff\u5927\u82f1\u96c4\u548c\u4e49\u5144\u62a5\u4ec7\u3002\u4e8e\u662f\u4e09\u4e2a\u4eba\u52a8\u8eab\u5230\u4e91\u5357\u53bb\u3002\u201d \u3000\u3000\u5218\u5143\u9e64\u3001\u718a\u5143\u732e\u5e08\u5144\u5f1f\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u5fc3\u77e5\u5979\u6240\u8bf4\u7684\u6c49\u5978\uff0c\u5c31\u662f\u7235\u5c01\u5e73\u897f\u4eb2\u738b\u7684\u5434\u4e09\u6842\u3002 \u3000\u3000\u82d7\u82e5\u5170\u53c8\u9053\uff1a\u201c\u4e09\u4eba\u5230\u4e86\u6606\u660e\uff0c\u5728\u5927\u6c49\u5978\u7684\u5c45\u6240\u524d\u540e\u63a2\u8bbf\u660e\u767d\u3002\u4e09\u6708\u521d\u4e94\u90a3\u5929\u665a\u4e0a\uff0c\u4e09\u4eba\u5e26\u4e86\u5175\u5203\u6697\u5668\uff0c\u8d8a\u5899\u8fdb\u53bb\u3002\u90a3\u5927\u6c49\u5978\u9632\u5907\u5f97\u5341\u5206\u5468\u5bc6\uff0c\u4e09\u4e2a\u4eba\u521a\u8fdb\u53bb\uff0c\u5c31\u7ed9\u536b\u58eb\u53d1\u89c9\u4e86\u3002\u90a3\u4e09\u4eba\u6b66\u827a\u9ad8\u5f3a\uff0c\u4e00\u52a8\u624b\uff0c\u4e8c\u5341\u591a\u4e2a\u536b\u58eb\u6216\u6b7b\u6216\u4f24\uff0c\u963b\u6321\u4e0d\u4f4f\uff0c\u88ab\u4ed6\u4eec\u51b2\u8fdb\u4e86\u5367\u5ba4\u3002\u773c\u89c1\u90a3\u5927\u6c49\u5978\u9003\u8d70\u4e0d\u4e86\uff0c\u54ea\u77e5\u65c1\u8fb9\u7a81\u7136\u95ea\u51fa\u4e00\u4eba\uff0c\u6321\u5728\u5927\u6c49\u5978\u9762\u524d\u3002\u4e09\u4eba\u4e00\u770b\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u539f\u6765\u8fd9\u4eba\u5c31\u662f\u4ed6\u4eec\u5bfb\u8bbf\u4e86\u591a\u5e74\u7684\u4e49\u5144\u3002\u8fd9\u4eba\u6b66\u529f\u6bd4\u4ed6\u4eec\u9ad8\uff0c\u4fdd\u62a4\u7740\u5927\u6c49\u5978\uff0c\u4e0d\u8bb8\u4e09\u4eba\u6740\u4ed6\u3002\u4e09\u4e2a\u4eba\u53c8\u60ca\u53c8\u6012\uff0c\u548c\u4ed6\u52a8\u8d77\u624b\u6765\u3002\u4e0d\u4e45\u5916\u9762\u53c8\u6d8c\u8fdb\u6570\u5341\u540d\u536b\u58eb\uff0c\u4e09\u4eba\u5be1\u4e0d\u654c\u4f17\uff0c\u53ea\u5f97\u9003\u8d70\u3002 \u3000\u3000\u811a\u592b\u516c\u516c\u5374\u5931\u624b\u88ab\u64d2\u3002 \u3000\u3000\u201c\u5927\u6c49\u5978\u4eb2\u81ea\u5ba1\u95ee\u3002\u811a\u592b\u516c\u516c\u7834\u53e3\u5927\u9a82\uff0c\u9a82\u4ed6\u5c06\u6c49\u4eba\u6c5f\u5c71\u9001\u7ed9\u4e86\u9791\u5b50\u3002\u5927\u6c49\u5978\u6253\u6298\u4e86\u4ed6\u53cc\u817f\uff0c\u5173\u5728\u7262\u91cc\u3002\u90a3\u4e2a\u4e49\u5144\u5927\u6982\u60f3\u60f3\u4e0d\u597d\u610f\u601d\uff0c\u5077\u5077\u5230\u7262\u4e2d\u653e\u4e86\u4ed6\u51fa\u53bb\u3002\u811a\u592b\u516c\u516c\u4e0e\u90ce\u4e2d\u516c\u516c\u3001\u5316\u5b50\u516c\u516c\u4f1a\u9762\u540e\uff0c\u4e09\u4e2a\u4eba\u62b1\u5934\u75db\u54ed\uff0c\u771f\u60f3\u4e0d\u5230\u8fd9\u4e2a\u7ed3\u4e49\u5144\u957f\u5c45\u7136\u4f1a\u53d8\u8282\u6295\u654c\u3002\u4e09\u4eba\u6697\u4e2d\u518d\u4e00\u6253\u542c\uff0c\u7adf\u67e5\u51fa\u4e00\u4ef6\u66f4\u52a0\u53eb\u4eba\u75db\u6068\u4e07\u5206\u7684\u4e8b\u6765\uff0c\u539f\u6765\u5f53\u65e5\u4e09\u4eba\u4ece\u4e5d\u5bab\u5c71\u51b2\u51fa\u53bb\u6c42\u6551\uff0c\u90a3\u4e49\u5144\u7b49\u4e86\u51e0\u5929\u4e0d\u89c1\u63f4\u5175\uff0c\u7adf\u4eb2\u624b\u5c06\u5927\u82f1\u96c4\u5bb3\u6b7b\uff0c\u5411\u654c\u4eba\u6295\u964d\u3002\u6ee1\u6e05\u7687\u5e1d\u5c01\u4e86\u4ed6\u4e00\u4e2a\u5927\u5b98\uff0c\u773c\u4e0b\u5df2\u5728\u90a3\u5927\u6c49\u5978\u624b\u4e0b\u505a\u5230\u63d0\u7763\u3002\u201d \u3000\u3000\u4f17\u4eba\u542c\u5230\u8fd9\u91cc\uff0c\u8138\u4e0a\u4e00\u9f50\u53d8\u8272\u3002\u4ed6\u4eec\u90fd\u66fe\u542c\u8bf4\u95ef\u738b\u662f\u5728\u4e5d\u5bab\u5c71\u4e3a\u4eba\u6240\u5bb3\uff0c\u6709\u7684\u8bf4\u662f\u8001\u767e\u59d3\u6740\u7684\uff0c\u6709\u7684\u8bf4\u662f\u5b98\u519b\u6740\u7684\uff0c\u5374\u4e0d\u77e5\u51f6\u624b\u7adf\u662f\u4ed6\u7684\u5fc3\u8179\u536b\u58eb\u3002 \u3000\u3000\u82d7\u82e5\u5170\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4e09\u4e2a\u4eba\u8bbf\u67e5\u786e\u5b9e\uff0c\u51b3\u610f\u53bb\u8ddf\u4ed6\u7b97\u5e10\u3002\u53ea\u662f\u4e09\u4eba\u672c\u5c31\u96be\u4ee5\u80dc\u4ed6\uff0c\u73b0\u4e0b\u811a\u592b\u516c\u516c\u53d7\u4e86\u4f24\uff0c\u66f4\u52a0\u4e0d\u662f\u654c\u624b\u3002\u6b63\u5728\u8e0c\u8e87\uff0c\u5ffd\u7136\u90a3\u4e49\u5144\u6d3e\u4eba\u9001\u6765\u4e00\u5c01\u4fe1\uff0c\u7ea6\u4e09\u4eba\u4e09\u6708\u5341\u4e94\u665a\u95f4\u5728\u6ec7\u6c60\u996e\u9152\u3002 \u3000\u3000\u201c\u4e09\u4eba\u77e5\u4ed6\u5fc5\u6709\u8be1\u8ba1\uff0c\u4f46\u60f3\u4ed6\u5bf9\u4e09\u4eba\u7684\u4f4f\u5904\u52a8\u9759\u77e5\u9053\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5728\u6b64\u5904\u4ed6\u5927\u6743\u5728\u63e1\uff0c\u8981\u907f\u4e5f\u907f\u4e0d\u4e86\u3002\u4e8b\u5df2\u81f3\u6b64\uff0c\u5c31\u662f\u9f99\u6f6d\u864e\u7a74\uff0c\u4e5f\u53ea\u597d\u53bb\u95ef\u3002\u5230\u4e86\u90a3\u65e5\uff0c\u4e09\u4eba\u8eab\u4e0a\u6697\u5e26\u5175\u5203\uff0c\u5230\u6ec7\u6c60\u8fb9\u8d74\u7ea6\u3002\u53ea\u89c1\u4ed6\u65e9\u5728\u90a3\u91cc\u7b49\u5019\uff0c\u5b64\u8eab\u4e00\u4eba\uff0c\u5e76\u6ca1\u5e26\u4eb2\u968f\u536b\u5175\uff0c\u7a7f\u7684\u4e5f\u662f\u4e00\u8eab\u7c97\u5e03\u9752\u8863\uff0c\u5c31\u548c\u5f53\u5e74\u56db\u4eba\u540c\u5728\u519b\u4e2d\u65f6\u6240\u7a7f\u7684\u4e00\u6837\uff0c\u56db\u4eba\u5728\u5c0f\u9152\u5e97\u91cc\u4e70\u4e86\u4e9b\u719f\u8089\u3001\u70e7\u9e21\u3001\u9992\u5934\uff0c\u6253\u4e86\u5341\u51e0\u65a4\u767d\u9152\uff0c\u4e0a\u8239\u5230\u6ec7\u6c60\u4e2d\u8d4f\u6708\u996e\u98df\u3002 \u3000\u3000\u201c\u56db\u4eba\u4e00\u9762\u559d\u9152\uff0c\u4e00\u9762\u8bf4\u4e9b\u4ece\u524d\u540c\u5728\u519b\u4e2d\u7684\u8c6a\u4e8b\u80dc\u6982\u3002\u90a3\u4e09\u4eba\u89c1\u4ed6\u7edd\u53e3\u4e0d\u63d0\u90a3\u4f4d\u5927\u82f1\u96c4\u7684\u540d\u5b57\uff0c\u4e5f\u5c31\u5fcd\u7740\u4e0d\u8bf4\u3002\u4f46\u89c1\u4ed6\u4e00\u5927\u7897\u4e00\u5927\u7897\u7684\u559d\u9152\uff0c\u773c\u89c1\u6708\u81f3\u4e2d\u5929\u3002\u4ed6\u4ef0\u5929\u53eb\u9053\uff1a\u2018\u4e09\u4f4d\u5144\u5f1f\uff0c\u54b1\u4eec\u4e45\u522b\u91cd\u9022\uff0c\u6211\u4eca\u65e5\u597d\u6b22\u559c\u554a\uff01\u2019\u201d \u3000\u3000\u8fd9\u6837\u4e00\u53e5\u8c6a\u6c14\u5954\u653e\u7684\u8bdd\uff0c\u4ece\u4e00\u4e2a\u6e29\u67d4\u6587\u96c5\u7684\u5c11\u5973\u53e3\u4e2d\u8bf4\u51fa\u6765\uff0c\u672a\u514d\u663e\u5f97\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u53ef\u662f\u4f17\u4eba\u4e3a\u6545\u4e8b\u4e2d\u5916\u5f1b\u5185\u5f20\u7684\u60c5\u52bf\u6240\u6151\uff0c\u7686\u672a\u5728\u610f\u3002 \u3000\u3000\u53ea\u542c\u5979\u53c8\u9053\uff1a\u201c\u90a3\u4f4d\u626e\u6210\u90ce\u4e2d\u7684\u516c\u516c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u51b7\u7b11\u9053\uff1a\u2018\u4f60\u505a\u4e86\u5927\u5b98\uff0c\u8eab\u4eab\u8363\u534e\u5bcc\u8d35\uff0c\u81ea\u7136\u6b22\u559c\u3002\u53ea\u4e0d\u77e5\u5143\u5e05\u7237\u73b0\u4e0b\u5fc3\u4e2d\u5982\u4f55\uff1f\u2019\u90a3\u4f4d\u5927\u82f1\u96c4\u540e\u6765\u505a\u4e86\u7687\u5e1d\uff0c\u4e0d\u8fc7\u56db\u4e2a\u536b\u58eb\u4e00\u76f4\u53eb\u4ed6\u4f5c\u5143\u5e05\u7237\u3002 \u3000\u3000\u201c\u90a3\u4e49\u5144\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u2018\u5509\uff0c\u5143\u5e05\u7237\u5b9a\u7136\u5bc2\u5bde\u5f97\u7d27\u3002\u5f85\u6b64\u95f4\u5927\u4e8b\u4e00\u4e86\uff0c\u6211\u5c31\u6307\u70b9\u4e09\u4f4d\u5144\u5f1f\u53bb\u62dc\u89c1\u5143\u5e05\u7237\u3002\u2019\u201c\u4e09\u4eba\u4e00\u542c\uff0c\u4e2a\u4e2a\u6012\u6c14\u51b2\u5929\uff0c\u5fc3\u9053\uff1a\u2018\u597d\u54c7\uff0c\u4f60\u8fd8\u60f3\u6740\u6211\u4eec\u4e09\u4eba\uff0c\u53eb\u6211\u4eec\u53bb\u9634\u66f9\u5730\u5e9c\u548c\u5143\u5e05\u7237\u76f8\u4f1a\u3002\u2019\u811a\u592b\u516c\u516c\u4f38\u624b\u5165\u6000\uff0c\u5c31\u8981\u53bb\u6478\u5200\u5b50\u3002\u90ce\u4e2d\u516c\u516c\u5411\u4ed6\u4f7f\u4e2a\u773c\u8272\uff0c\u63d0\u8d77\u9152\u58f6\u5411\u4e49\u5144\u659f\u4e86\u676f\u9152\u3002\u8bf4\u9053\uff1a\u2018\u90a3\u65e5\u4e5d\u5bab\u5c71\u5934\u522b\u540e\uff0c\u5143\u5e05\u7237\u5230\u5e95\u600e\u6837\u4e86\uff1f\u2019\u90a3\u4e49\u5144\u53cc\u7709\u4e00\u626c\uff0c\u8bf4\u9053\uff1a\u2018\u4eca\u65e5\u7ea6\u4e09\u4f4d\u5144\u5f1f\u6765\uff0c\u5c31\u662f\u8981\u8bf4\u8fd9\u56de\u4e8b\u3002\u2019\u53eb\u5316\u516c\u516c\u5ffd\u7136\u4f38\u624b\u5411\u4ed6\u80cc\u540e\u4e00\u6307\uff0c\u53eb\u9053\uff1a\u2018\u54a6\uff0c\u662f\u8c01\u6765\u4e86\uff1f\u2019\u201c\u90a3\u4e49\u5144\u8f6c\u5934\u53bb\u770b\uff0c\u53eb\u5316\u516c\u516c\u4e0e\u90ce\u4e2d\u516c\u516c\u53cc\u5200\u9f50\u51fa\uff0c\u4e00\u5200\u780d\u65ad\u4e86\u4ed6\u7684\u53f3\u81c2\uff0c\u4e00\u5200\u65a9\u5728\u4ed6\u80cc\u5fc3\uff0c\u6df1\u5165\u6570\u5bf8\u3002\u90a3\u4e49\u5144\u5927\u53eb\u4e00\u58f0\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u5de6\u81c2\u8fde\u4f38\uff0c\u5df2\u5c06\u4e24\u4eba\u5200\u5b50\u593a\u4e0b\uff0c\u629b\u5165\u4e86\u6ec7\u6c60\uff0c\u624b\u638c\u4e00\u63a2\uff0c\u5df2\u6293\u4f4f\u4e86\u90ce\u4e2d\u516c\u516c\u7684\u80f8\u53e3\u7a74\u9053\uff0c\u8138\u8272\u82cd\u767d\uff0c\u559d\u9053\uff1a\u2018\u54b1\u56db\u4eba\u4e49\u7ed3\u91d1\u5170\uff0c\u5e72\u4e48\u2026\u2026\u5e72\u4e48\u65bd\u6697\u7b97\u4f24\u6211\uff1f\u2019\u90ce\u4e2d\u516c\u516c\u88ab\u4ed6\u8fd9\u4e00\u6293\uff0c\u767b\u65f6\u52a8\u5f39\u4e0d\u5f97\u3002\u811a\u592b\u516c\u516c\u633a\u5200\u53eb\u9053\uff1a\u2018\u4f60\u5bb3\u6b7b\u5143\u5e05\u7237\uff0c\u5356\u4e3b\u6c42\u8363\uff0c\u8fd8\u6709\u8138\u63d0\u5230\u4e49\u6c14\u4e24\u5b57\uff1f\u2019\u201c\u90a3\u4e49\u5144\u98de\u8d77\u4e00\u811a\u3002\u5c06\u4ed6\u624b\u4e2d\u5200\u5b50\u8e22\u53bb\uff0c\u5927\u7b11\u9053\uff1a\u2018\u597d\uff0c\u597d\uff01 \u3000\u3000\u6709\u4e49\u6c14\uff0c\u6709\u4e49\u6c14\u3002\u2019\u4e09\u4eba\u89c1\u4ed6\u4e00\u81c2\u88ab\u65a9\uff0c\u8eab\u53d7\u91cd\u4f24\uff0c\u7adf\u7136\u8fd8\u662f\u5982\u6b64\u795e\u52c7\uff0c\u4e0d\u7981\u90fd\u60ca\u5f97\u5446\u4e86\u3002\u90a3\u4e49\u5144\u7b11\u58f0\u752b\u6bd5\uff0c\u5ffd\u7136\u6d41\u4e0b\u6cea\u6765\uff0c\u8bf4\u9053\uff1a\u2018\u53ef\u60dc\uff0c\u53ef\u60dc\u6211\u5927\u4e8b\u4e0d\u6210\uff01\u2019\u968f\u5373\u653e\u677e\u4e86\u90ce\u4e2d\u516c\u516c\u3002 \u3000\u3000\u53eb\u5316\u516c\u516c\u6015\u4ed6\u518d\u65bd\u6bd2\u624b\uff0c\u731b\u51fa\u4e00\u62f3\uff0c\u6b63\u4e2d\u4ed6\u7684\u80f8\u819b\u3002\u8fd9\u4e00\u62f3\u4f7f\u7684\u662f\u91cd\u624b\u6cd5\uff0c\u529b\u9053\u60ca\u4eba\uff0c\u90a3\u4e49\u5144\u2018\u54c7\u2019\u7684\u4e00\u58f0\uff0c\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u5ffd\u5730\u63d0\u8d77\u5de6\u638c\uff0c\u51fb\u5728\u8239\u8237\u4e4b\u4e0a\uff0c\u53ea\u51fb\u5f97\u6728\u5c51\u7eb7\u98de\uff0c\u8239\u8237\u7f3a\u4e86\u4e00\u5757\u3002\u4ed6\u82e6\u7b11\u9053\uff1a\u2018\u6211\u867d\u53d7\u91cd\u4f24\uff0c\u8981\u6740\u4f60\u4eec\uff0c\u4ecd\u662f\u6613\u5982\u53cd\u638c\u3002\u4f46\u4f60\u4eec\u662f\u6211\u597d\u5144\u5f1f\uff0c\u6211\u600e\u820d\u5f97\u554a\uff01\u2019\u201c\u90a3\u4e09\u4eba\u4e00\u9f50\u9000\u5728\u8239\u68a2\uff0c\u5e76\u80a9\u800c\u7acb\uff0c\u9632\u4ed6\u66b4\u8d77\u4f24\u4eba\u3002\u90a3\u4e49\u5144\u53f9\u9053\uff1a\u2018\u4eca\u65e5\u4e4b\u4e8b\uff0c\u5343\u4e07\u4e0d\u53ef\u6cc4\u6f0f\u3002\u82e5\u662f\u7ed9\u6211\u513f\u5b50\u77e5\u9053\uff0c\u4f60\u4eec\u4e09\u4e2a\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u3002\u6211\u5f53\u81ea\u520e\u800c\u6b7b\uff0c\u4ee5\u514d\u4f60\u4eec\u8d1f\u4e2a\u6215\u5bb3\u4e49\u5144\u7684\u6076\u540d\u3002\u2019\u8bf4\u7740\u62bd\u51fa\u5355\u5200\uff0c\u5728\u9888\u4e2d\u4e00\u5272\uff0c\u4e00\u4ea4\u4fef\u8dcc\u4e0b\u53bb\u3002\u811a\u592b\u516c\u516c\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u62a2\u4e0a\u53bb\u6276\u4f4f\uff0c\u53eb\u9053\uff1a\u2018\u5927\u54e5\uff01\u2019\u90a3\u4e49\u5144\u9053\uff1a\u2018\u597d\u5144\u5f1f\uff0c\u505a\u54e5\u54e5\u7684\u53bb\u4e86\u3002\u5143\u5e05\u7237\u7684\u519b\u5200\u5927\u6709\u5e72\u7cfb\uff0c\u4ed6\u2026\u2026\u8001\u4eba\u5bb6\u662f\u5728\u77f3\u95e8\u5ce1\u2026\u2026\u2019\u8fd9\u53e5\u8bdd\u6ca1\u8bf4\u5b8c\uff0c\u54bd\u5589\u6d41\u8840\uff0c\u6b7b\u5728\u8239\u4e2d\u3002 \u3000\u3000\u201c\u4e09\u4eba\u671b\u7740\u4ed6\u7684\u5c38\u8eab\uff0c\u53c8\u662f\u96be\u8fc7\uff0c\u53c8\u662f\u75db\u5feb\uff0c\u53ea\u89c1\u4ed6\u7528\u6765\u81ea\u520e\u7684\u90a3\u67c4\u5200\u4e0a\u523b\u7740\u5341\u56db\u4e2a\u5b57\uff0c\u8ba4\u5f97\u5c31\u662f\u90a3\u4f4d\u5927\u82f1\u96c4\u7684\u519b\u5200\u4e86\u3002\u201d \u3000\u3000\u4f17\u4eba\u542c\u5230\u6b64\u5904\uff0c\u773c\u5149\u4e00\u9f50\u8f6c\u8fc7\u53bb\u671b\u7740\u5b9d\u6811\u624b\u4e2d\u7684\u90a3\u67c4\u77ed\u5200\u3002\u5218\u5143\u9e64\u5ffd\u7136\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u3002\u201d\u9676\u767e\u5c81\u6012\u559d\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\uff1f\u201d\u5218\u5143\u9e64\u9053\uff1a\u201c\u90a3\u674e\u81ea\u6210\u6d41\u8840\u5343\u91cc\uff0c\u6740\u4eba\u5982\u9ebb\uff0c\u600e\u4f1a\u4e0b\u8fd9\u5341\u56db\u5b57\u519b\u4ee4\uff1f\u201d\u4f17\u4eba\u4e00\u6014\uff0c\u4e0d\u77e5\u6240\u5bf9\u3002 \u3000\u3000\u4e8e\u7ba1\u5bb6\u5ffd\u7136\u63a5\u53e3\u9053\uff1a\u201c\u95ef\u738b\u6740\u4eba\u5982\u9ebb\uff0c\u662f\u8c01\u89c1\u6765\uff1f\u201d\u5218\u5143\u9e64\u9053\uff1a\u201c\u4eba\u4eba\u90fd\u8fd9\u822c\u8bf4\uff0c\u96be\u9053\u662f\u5047\uff1f\u201d\u4e8e\u7ba1\u5bb6\u9053\uff1a\u201c\u4f60\u4eec\u5c45\u5b98\u4e4b\u4eba\uff0c\u81ea\u7136\u8bf4\u4ed6\u80e1\u4e71\u6740\u4eba\u3002\u5176\u5b9e\u95ef\u738b\u6740\u7684\u53ea\u662f\u8d2a\u5b98\u6c61\u540f\u3001\u571f\u8c6a\u52a3\u7ec5\u3002\u8fd9\u4e9b\u672c\u5c31\u7b97\u4e0d\u5f97\u662f\u4eba\u3002\u2018\u6740\u4e00\u4eba\u5982\u6740\u6211\u7236\u2019\u4e4b\u4ee4\uff0c\u662f\u4e0d\u8bb8\u90e8\u5c5e\u5984\u6740\u4e00\u4e2a\u597d\u4eba\uff0c\u8fd9\u8bdd\u4e00\u70b9\u513f\u4e5f\u4e0d\u9519\u3002\u201d \u3000\u3000\u5218\u5143\u9e64\u6b32\u5f85\u518d\u8fa9\uff0c\u4f46\u89c1\u4ed6\u82f1\u6c14\u903c\u4eba\uff0c\u987f\u7136\u4f4f\u53e3\u4e0d\u8bf4\u3002\u718a\u5143\u732e\u610f\u6b32\u6253\u5f00\u50f5\u5c40\uff0c\u9053\uff1a\u201c\u82d7\u59d1\u5a18\uff0c\u540e\u6765\u600e\u6837\uff1f\u8bf7\u4f60\u8bf4\u4e0b\u53bb\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u811a\u592b\u516c\u516c\u8bf4\u9053\uff1a\u2018\u4ed6\u8bf4\u5143\u5e05\u7237\u5728\u77f3\u95e8\u5ce1\uff0c\u90a3\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u2019\u90ce\u4e2d\u516c\u516c\u9053\uff1a\u2018\u96be\u9053\u4ed6\u8bf4\u5143\u5e05\u7237\u846c\u5728\u77f3\u95e8\u5ce1\uff1f\u2019\u53eb\u5316\u516c\u516c\u6447\u5934\u9053\uff1a\u2018\u8fd9\u4eba\u5978\u6076\u4e4b\u6781\uff0c\u4e34\u6b7b\u8fd8\u8981\u9a97\u4eba\u3002\u2019\u539f\u6765\u5927\u82f1\u96c4\u6b7b\u540e\uff0c\u6c49\u5978\u5c06\u4ed6\u7684\u9057\u4f53\u9001\u5230\u5317\u4eac\u53bb\u9886\u8d4f\u3002\u7687\u5e1d\u5c06\u5927\u82f1\u96c4\u7684\u9996\u7ea7\u6302\u5728\u57ce\u95e8\u4e0a\u53f7\u4ee4\u793a\u4f17\u3002\u4e09\u540d\u536b\u58eb\u5192\u4e86\u5947\u9669\uff0c\u5c06\u9996\u7ea7\u76d7\u6765\uff0c\u65e9\u5df2\u846c\u5728\u4e00\u4e2a\u9669\u5cfb\u4e07\u5206\u3001\u4eba\u8ff9\u4e0d\u5230\u7684\u6240\u5728\u3002\u90a3\u4e49\u5144\u8bf4\u4ed6\u5728\u77f3\u95e8\u5ce1\uff0c\u4e09\u4eba\u81ea\u7136\u4e0d\u4fe1\u3002 \u3000\u3000\u201c\u4e09\u4eba\u6740\u4e86\u4e49\u5144\u540e\uff0c\u53c8\u53bb\u884c\u523a\u90a3\u5927\u6c49\u5978\uff0c\u4f46\u5927\u6c49\u5978\u9632\u8303\u5468\u5bc6\uff0c\u6570\u6b21\u884c\u523a\u90fd\u4e0d\u6210\u529f\uff0c\u800c\u4ed6\u4eec\u5927\u4e49\u6740\u5144\u7684\u4e8b\uff0c\u5374\u5728\u6c5f\u6e56\u4e0a\u4f20\u5f00\u6765\u4e86\u3002\u6b66\u6797\u4e2d\u7684\u82f1\u96c4\u597d\u6c49\u542c\u5230\uff0c\u90fd\u7fd8\u8d77\u5927\u62c7\u6307\uff0c\u8d5e\u4e00\u58f0\uff1a \u3000\u3000\u2018\u6740\u5f97\u597d\uff01\u2019\u6d88\u606f\u4f20\u5230\u4e86\u90a3\u4e49\u5144\u7684\u5bb6\u4e61\uff0c\u4ed6\u513f\u5b50\u5341\u5206\u60b2\u4f24\uff0c\u5c31\u8d76\u5230\u6606\u660e\u6765\u66ff\u7236\u4eb2\u62a5\u4ec7\u3002\u201d \u3000\u3000\u9676\u767e\u5c81\u63a5\u53e3\u9053\uff1a\u201c\u90a3\u505a\u513f\u5b50\u7684\u8fd9\u5c31\u4e0d\u662f\u4e86\u3002\u867d\u7136\u8bf4\u7236\u4ec7\u4e0d\u5171\u6234\u5929\uff0c\u4f46\u4ed6\u7236\u4eb2\u505a\u4e86\u5978\u6076\u4e4b\u4e8b\uff0c\u4eba\u4eba\u5f97\u800c\u8bdb\u4e4b\uff0c\u8fd9\u4ec7\u4e0d\u62a5\u4e5f\u7f62\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u6211\u7239\u5f53\u65f6\u4e5f\u8fd9\u6837\u8bf4\uff0c\u53ef\u662f\u90a3\u513f\u5b50\u7684\u60f3\u6cd5\u5374\u5927\u5927\u4e0d\u540c\u3002\u4ed6\u5230\u4e86\u6606\u660e\uff0c\u4e0d\u4e45\u5c31\u5728\u4e00\u5ea7\u7834\u5e99\u4e4b\u4e2d\u627e\u5230\u4e09\u4eba\uff0c\u52a8\u8d77\u624b\u6765\u3002\u8fd9\u513f\u5b50\u6b66\u529f\u5f97\u5230\u7236\u4eb2\u771f\u4f20\uff0c\u90a3\u4e09\u4eba\u679c\u7136\u4e0d\u662f\u5bf9\u624b\uff0c\u6597\u4e86\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u4e09\u4eba\u88ab\u4ed6\u4e00\u4e00\u6253\u5012\u3002 \u3000\u3000\u201c\u90a3\u513f\u5b50\u9053\uff1a\u2018\u4e09\u4f4d\u53d4\u53d4\uff0c\u6211\u7239\u7239\u5fcd\u803b\u8d1f\u8fb1\uff0c\u7518\u613f\u8d1f\u4e00\u4e2a\u5356\u4e3b\u6c42\u8363\u7684\u6076\u540d\uff0c\u4f60\u4eec\u600e\u61c2\u5f97\u5176\u4e2d\u6df1\u4e49\uff1f\u77a7\u7740\u4f60\u4eec\u548c\u6211\u7239\u7239\u7ed3\u4e49\u4e00\u573a\uff0c\u4eca\u65e5\u9976\u4e86\u4f60\u4eec\u6027\u547d\u3002\u5feb\u5feb\u56de\u5bb6\u53bb\u6599\u7406\u540e\u4e8b\uff0c\u660e\u5e74\u4e09\u6708\u5341\u4e94\u662f\u6211\u7239\u7239\u6b7b\u5fcc\uff0c\u6211\u5f53\u6765\u767b\u95e8\u62dc\u8bbf\u3002\u2019\u4ed6\u8bf4\u4e86\u8fd9\u756a\u8bdd\u540e\uff0c\u593a\u4e86\u90a3\u5927\u82f1\u96c4\u7684\u519b\u5200\uff0c\u626c\u957f\u800c\u53bb\u3002 \u3000\u3000\u201c\u8fd9\u65f6\u5df2\u662f\u9686\u51ac\uff0c\u90a3\u4e09\u4eba\u5f53\u5373\u5317\u4e0a\uff0c\u5c06\u4e09\u5bb6\u5bb6\u5c5e\u805a\u5728\u4e00\u8d77\uff0c\u8be6\u8be6\u7ec6\u7ec6\u7684\u5c06\u5f53\u65e5\u821f\u4e2d\u558b\u8840\u4e4b\u4e8b\u8bf4\u4e86\u3002\u5927\u5bb6\u90fd\u9053\uff1a\u2018\u4ed6\u5bb3\u6b7b\u5927\u82f1\u96c4\uff0c\u4fdd\u62a4\u5927\u6c49\u5978\uff0c\u81ea\u5df1\u53c8\u505a\u5f02\u65cf\u4eba\u624b\u4e0b\u7684\u5927\u5b98\uff0c\u8fd8\u80fd\u6709\u4ec0\u4e48\u6df1\u610f\uff1f\u4ed6\u513f\u5b50\u5f3a\u8f9e\u72e1\u8fa9\uff0c\u8bf4\u51fa\u8bdd\u6765\u6ca1\u4eba\u80fd\u4fe1\u3002\u2019\u6c5f\u6e56\u670b\u53cb\u5f97\u5230\u8baf\u606f\uff0c\u7eb7\u7eb7\u8d76\u6765\u4ed7\u4e49\u76f8\u52a9\u3002 \u3000\u3000\u201c\u5230\u4e86\u4e09\u6708\u5341\u4e94\u90a3\u5929\u665a\u4e0a\uff0c\u90a3\u513f\u5b50\u679c\u7136\u5b64\u8eab\u8d76\u5230\u3002\u201d \u3000\u3000\u4f17\u4eba\u773c\u671b\u82d7\u82e5\u5170\uff0c\u7b49\u5979\u7ee7\u7eed\u8ff0\u8bf4\uff0c\u5374\u89c1\u5c0f\u4e2b\u5934\u7434\u513f\u8d70\u5c06\u8fc7\u6765\uff0c\u624b\u91cc\u6367\u4e86\u4e00\u4e2a\u5957\u7740\u9526\u7f0e\u5957\u5b50\u7684\u767d\u94dc\u5c0f\u706b\u7089\uff0c\u653e\u5728\u5979\u7684\u6000\u91cc\u3002 \u3000\u3000\u82d7\u82e5\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u53bb\u70b9\u4e00\u76d8\u9999\u3002\u201d\u7434\u513f\u7b54\u5e94\u4e86\uff0c\u4e0d\u4e00\u4f1a\u6367\u6765\u4e00\u4e2a\u767d\u7389\u9999\u7089\uff0c\u653e\u5728\u5979\u8eab\u65c1\u51e0\u4e0a\u3002\u53ea\u89c1\u4e00\u7f15\u9752\u70df\uff0c\u4ece\u9999\u7089\u9876\u4e0a\u96d5\u7740\u7684\u51e4\u51f0\u5634\u4e2d\u8885\u8885\u5410\u51fa\uff0c\u4f17\u4eba\u968f\u5373\u95fb\u5230\u6de1\u6de1\u5e7d\u9999\uff0c\u4f3c\u5170\u975e\u5170\uff0c\u4f3c\u9e9d\u975e\u9e9d\uff0c\u95fb\u7740\u751a\u662f\u8212\u6cf0\u3002 \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u6211\u72ec\u81ea\u4e2a\u5728\u623f\uff0c\u70b9\u8fd9\u7d20\u99a8\u3002\u8fd9\u91cc\u4eba\u591a\uff0c\u600e\u4e48\u53c8\u70b9\u8fd9\u4e2a\uff1f\u201d\u7434\u513f\u7b11\u9053\uff1a\u201c\u6211\u5f53\u771f\u80e1\u6d82\u5566\u3002\u201d\u6367\u8d77\u9999\u7089\uff0c\u53bb\u6362\u4e86\u4e00\u76d8\u9999\u51fa\u6765\u3002\u82d7\u82e5\u5170\u9053\uff1a\u201c\u8fd9\u91cc\u98ce\u4ece\u5317\u6765\uff0c\u5317\u8fb9\u867d\u7136\u6ca1\u7a97\uff0c\u4f46\u5c71\u9876\u98ce\u5927\uff0c\u603b\u6709\u4e9b\u98ce\u513f\u6f0f\u8fdb\u6765\u3002\u4f60\u77a7\u8fd9\u9999\u7089\u653e\u5bf9\u4e86\u4e48\uff1f\u201d\u7434\u513f\u4e00\u7b11\uff0c\u5c06\u5c0f\u51e0\u7aef\u5230\u897f\u5317\u89d2\u653e\u4e0b\uff0c\u53c8\u7ed9\u5c0f\u59d0\u6ce1\u4e86\u4e00\u7897\u8336\uff0c\u8fd9\u624d\u8d70\u5f00\u3002 \u3000\u3000\u4f17\u4eba\u90fd\u60f3\uff1a\u201c\u91d1\u9762\u4f5b\u82d7\u4eba\u51e4\u8eab\u4e3a\u4e00\u4ee3\u5927\u4fa0\uff0c\u5374\u628a\u4e2a\u5973\u513f\u5a07\u7eb5\u6210\u8fd9\u822c\u6a21\u6837\u3002\u201d\u53ea\u89c1\u5979\u6162\u6162\u62ff\u8d77\u76d6\u7897\uff0c\u63ed\u5f00\u76d6\u5b50\uff0c\u77a7\u4e86\u77a7\u7897\u4e2d\u7684\u8336\u53f6\u4e0e\u73ab\u7470\u82b1\uff0c\u8f7b\u8f7b\u555c\u4e86\u4e00\u53e3\uff0c\u7f13\u7f13\u653e\u4e0b\uff0c\u4f17\u4eba\u53ea\u9053\u5979\u8981\u8bf4\u6545\u4e8b\u4e86\uff0c\u54ea\u77e5\u9053\u5979\u5374\u8bf4\uff1a\u201c\u6211\u6709\u4e9b\u513f\u5934\u75db\uff0c\u8981\u8fdb\u53bb\u4f11\u606f\u4e00\u4f1a\u3002\u8bf8\u4f4d\u4f2f\u4f2f\u53d4\u53d4\u8bf7\u5bbd\u5ea7\u3002\u201d\u8bf4\u7740\u7ad9\u8d77\u8eab\u6765\uff0c\u5165\u5185\u53bb\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u76f8\u987e\u54d1\u7136\u3002\u66f9\u4e91\u5947\u7b2c\u4e00\u4e2a\u5fcd\u8010\u4e0d\u4f4f\uff0c\u6b63\u8981\u53d1\u4f5c\uff0c\u7530\u9752\u6587\u5411\u4ed6\u4f7f\u4e2a\u773c\u8272\u3002\u66f9\u4e91\u5947\u8bdd\u5230\u53e3\u8fb9\uff0c\u53c8\u54bd\u4e86\u4e0b\u53bb\u3002\u82d7\u82e5\u5170\u8fdb\u53bb\u4e0d\u4e45\uff0c\u968f\u5373\u51fa\u6765\uff0c\u53ea\u89c1\u5979\u6362\u4e86\u4e00\u4ef6\u6de1\u7eff\u76ae\u8884\uff0c\u4e00\u6761\u9e45\u9ec4\u8272\u767e\u8936\u88d9\uff0c\u8138\u4e0a\u6d17\u53bb\u4e86\u521d\u4e0a\u5c71\u65f6\u7684\u8102\u7c89\uff0c\u66f4\u663e\u5f97\u6de1\u96c5\u5b9c\u4eba\uff0c\u98ce\u81f4\u5929\u7136\u3002\u539f\u6765\u5979\u5e76\u975e\u5f53\u771f\u5934\u75db\uff0c\u5374\u662f\u53bb\u6362\u8863\u6d17\u8138\u3002\u7434\u513f\u8ddf\u968f\u5728\u540e\uff0c\u62ff\u4e86\u4e00\u4e2a\u94f6\u72d0\u57ab\u5b50\u653e\u5728\u6905\u4e0a\u3002\u82d7\u82e5\u5170\u6162\u6162\u5750\u4e0b\uff0c\u8fd9\u624d\u542f\u6731\u5507\u3001\u53d1\u7693\u9f7f\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u5929\u665a\u4e0a\uff0c\u90ce\u4e2d\u516c\u516c\u5bb6\u91cc\u5927\u5f00\u7b75\u5e2d\uff0c\u8bf7\u4e86\u4e00\u767e\u591a\u4f4d\u6c5f\u6e56\u4e0a\u6210\u540d\u7684\u82f1\u96c4\u8c6a\u6770\uff0c\u9759\u5019\u90a3\u4e49\u5144\u7684\u513f\u5b50\u5230\u6765\u3002\u7b49\u5230\u521d\u66f4\u65f6\u5206\uff0c\u53ea\u542c\u5f97\u6258\u7684\u4e00\u58f0\u54cd\uff0c\u7b75\u5e2d\u524d\u5df2\u591a\u4e86\u4e00\u4eba\uff0c\u5385\u4e0a\u597d\u624b\u751a\u591a\uff0c\u5374\u6ca1\u4e00\u4e2a\u77a7\u6e05\u695a\u4ed6\u662f\u600e\u4e48\u8fdb\u6765\u7684\u3002\u53ea\u89c1\u4ed6\u7ea6\u83ab\u4e8c\u5341\u5c81\u4e0a\u4e0b\u5e74\u7eaa\uff0c\u8eab\u7a7f\u7c97\u5e03\u9ebb\u8863\uff0c\u5934\u6234\u767d\u5e3d\uff0c\u624b\u91cc\u62ff\u7740\u4e00\u6839\u54ed\u4e27\u68d2\uff0c\u80cc\u4e0a\u659c\u63d2\u5355\u5200\u3002\u4ed6\u4e0d\u7406\u65c1\u4eba\uff0c\u5f84\u5411\u90ce\u4e2d\u3001\u53eb\u5316\u3001\u811a\u592b\u4e09\u4e2a\u516c\u516c\u8bf4\u9053\uff1a\u2018\u4e09\u4f4d\u53d4\u7236\uff0c\u8bf7\u501f\u4e2a\u50fb\u9759\u5904\u6240\u8bf4\u8bdd\u3002\u2019\u201c\u4e09\u4f4d\u516c\u516c\u5c1a\u672a\u7b54\u8bdd\uff0c\u5ce8\u5d4b\u6d3e\u7684\u4e00\u4f4d\u524d\u8f88\u82f1\u96c4\u53eb\u9053\uff1a\u2018\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u6709\u8bdd\u8981\u8bf4\u4fbf\u8bf4\uff0c\u4f55\u987b\u9b3c\u9b3c\u795f\u795f\uff1f\u4f60\u7236\u5356\u4e3b\u6c42\u8363\uff0c\u6211\u77a7\u4f60\u4e5f\u975e\u5584\u7c7b\uff0c\u5b9a\u662f\u6b32\u65bd\u5978\u8ba1\u3002\u4e09\u4f4d\u5927\u54e5\uff0c\u83ab\u4e0a\u4e86\u8fd9\u5c0f\u8d3c\u7684\u5f53\u3002\u2019\u53ea\u542c\u5f97\u556a\u556a\u556a\u3001\u556a\u556a\u556a\u516d\u58f0\u54cd\uff0c\u90a3\u4eba\u8138\u4e0a\u5403\u4e86\u516d\u8bb0\u8033\u5149\uff0c\u54c7\u7684\u4e00\u58f0\uff0c\u53e3\u5410\u9c9c\u8840\uff0c\u6570\u5341\u679a\u7259\u9f7f\u90fd\u6492\u5728\u5730\u4e0b\u3002 \u3000\u3000\u201c\u5e2d\u4e0a\u7fa4\u8c6a\u4e00\u9f50\u7ad9\u8d77\uff0c\u60ca\u6115\u4e4b\u4e0b\uff0c\u5927\u5385\u4e2d\u767e\u4f59\u4eba\u7adf\u5c14\u6084\u65e0\u58f0\u606f\uff0c\u5747\u60f3\uff1a\u6b64\u4eba\u8eab\u6cd5\u600e\u5730\u5982\u6b64\u5feb\u6cd5\uff1f\u90a3\u5ce8\u5d4b\u6d3e\u7684\u540d\u5bbf\u53d7\u6b64\u91cd\u521b\uff0c\u5413\u5f97\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u53e3\u3002\u90a3\u513f\u5b50\u7eb5\u4e0a\u524d\u53bb\u6253\u4eba\u65f6\u7fa4\u8c6a\u5e76\u672a\u770b\u6e05\uff0c\u9000\u56de\u539f\u5904\u65f6\u4ecd\u662f\u4e00\u6643\u5373\u56de\uff0c\u8fd9\u4e00\u77ac\u4e4b\u95f4\u500f\u5ffd\u6765\u53bb\uff0c\u7adf\u4f3c\u5e76\u672a\u79fb\u52a8\u8fc7\u8eab\u5b50\u3002\u90a3\u4e09\u4f4d\u516c\u516c\u4e0e\u4ed6\u7236\u4eb2\u6570\u5341\u5e74\u540c\u98df\u5171\u5bbf\uff0c\u77e5\u9053\u8fd9\u662f\u4ed6\u5bb6\u4f20\u7684\u2018\u98de\u5929\u795e\u884c\u2019\u8f7b\u529f\u7edd\u6280\uff0c\u53ea\u662f\u4ed6\u9752\u51fa\u4e8e\u84dd\uff0c\u4f3c\u4e4e\u72b9\u80dc\u4e43\u7236\u3002\u90a3\u513f\u5b50\u9053\uff1a\u2018\u4e09\u4f4d\u53d4\u53d4\uff0c\u82e5\u662f\u6211\u8981\u76f8\u5bb3\uff0c\u5728\u6606\u660e\u53e4\u5e99\u4e4b\u4e2d\u4f55\u5fc5\u653e\u624b\uff1f\u73b0\u4e0b\u6211\u6709\u51e0\u53e5\u8981\u7d27\u8bdd\u8bf4\uff0c\u65c1\u4eba\u542c\u4e86\u751a\u662f\u4e0d\u4fbf\u3002\u2019\u201c\u4e09\u4eba\u4e00\u60f3\u4e0d\u9519\u3002\u90a3\u90ce\u4e2d\u516c\u516c\u5f53\u4e0b\u9886\u4ed6\u8d70\u8fdb\u5185\u5802\u4e00\u95f4\u5c0f\u623f\u3002\u5927\u5385\u4e0a\u767e\u4f59\u4f4d\u82f1\u96c4\u597d\u6c49\u505c\u676f\u76f8\u987e\uff0c\u4fa7\u8033\u503e\u542c\u5185\u5802\u52a8\u9759\u3002 \u3000\u3000\u201c\u7ea6\u83ab\u8fc7\u4e86\u4e00\u987f\u996d\u529f\u592b\uff0c\u56db\u4eba\u76f8\u5055\u51fa\u6765\u3002\u90ce\u4e2d\u516c\u516c\u5411\u7fa4\u96c4\u4f5c\u4e86\u4e2a\u56db\u65b9\u63d6\uff0c\u8bf4\u9053\uff1a\u2018\u591a\u8c22\u5404\u4f4d\u5149\u4e34\uff0c\u8db3\u89c1\u6c5f\u6e56\u4e49\u6c14\u3002\u2019\u7fa4\u96c4\u6b63\u8981\u8fd8\u793c\uff0c\u5374\u89c1\u4ed6\u4e00\u6a2a\u5200\u5728\u9888\u4e2d\u4e00\u5212\uff0c\u767b\u65f6\u81ea\u520e\u800c\u6b7b\u3002\u7fa4\u96c4\u5927\u60ca\uff0c\u5f85\u8981\u62a2\u4e0a\u53bb\u6551\u63f4\uff0c\u5374\u89c1\u53eb\u5316\u516c\u516c\u4e0e\u811a\u592b\u516c\u516c\u62a2\u8fc7\u5200\u6765\uff0c\u5148\u540e\u81ea\u520e\uff0c\u8fd9\u4e2a\u5947\u53d8\u6765\u5f97\u7a81\u7136\u4e4b\u6781\uff0c\u7fa4\u96c4\u4e2d\u867d\u6709\u4e0d\u5c11\u9ad8\u624b\uff0c\u5374\u6ca1\u4e00\u4e2a\u6765\u5f97\u53ca\u963b\u62e6\u3002 \u3000\u3000\u201c\u90a3\u4e49\u5144\u7684\u513f\u5b50\u8dea\u4e0b\u6765\u5411\u4e09\u5177\u5c38\u4f53\u62dc\u4e86\u51e0\u62dc\uff0c\u62fe\u8d77\u4e09\u4eba\u7528\u4ee5\u81ea\u520e\u7684\u77ed\u5200\uff0c\u4e00\u8dc3\u4e0a\u5c4b\u3002\u7fa4\u96c4\u5927\u53eb\uff1a\u2018\u83ab\u8d70\u4e86\u5978\u8d3c\uff01\u2019\u7eb7\u7eb7\u4e0a\u5c4b\u8ffd\u8d76\u3002\u90a3\u4eba\u65e9\u5df2\u4e0d\u89c1\u4e86\u8e2a\u5f71\u3002 \u3000\u3000\u201c\u4e09\u4f4d\u516c\u516c\u7684\u5b50\u5973\u62b1\u7740\u7236\u4eb2\u7684\u5c38\u8eab\uff0c\u653e\u58f0\u5927\u54ed\u3002\u7fa4\u96c4\u63a2\u8be2\u4e09\u4eba\u5bb6\u5c5e\u5974\u4ec6\uff0c\u7adf\u6ca1\u4e00\u4e2a\u5f97\u77e5\u8fd9\u56db\u4eba\u5728\u5bc6\u5ba4\u4e2d\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u66f4\u4e0d\u77e5\u90a3\u513f\u5b50\u65bd\u4e86\u4ec0\u4e48\u5978\u8ba1\uff0c\u903c\u5f97\u4e09\u4eba\u5f53\u4f17\u81ea\u6740\u3002\u7fa4\u96c4\u89c1\u4e09\u4f4d\u82f1\u96c4\u5c38\u6a2a\u5f53\u5730\uff0c\u4e2a\u4e2a\u6c14\u6124\u586b\u81ba\uff0c\u7acb\u8a93\u8981\u66ff\u4e09\u4eba\u62a5\u4ec7\u3002 \u3000\u3000\u201c\u53ea\u662f\u90a3\u513f\u5b50\u4ece\u6b64\u9500\u58f0\u533f\u8ff9\uff0c\u4e0d\u77e5\u8eb2\u5230\u4e86\u4f55\u5904\u3002\u4e09\u4f4d\u516c\u516c\u7684\u5b50\u5973\u7531\u7fa4\u96c4\u629a\u517b\u6210\u4eba\u3002\u7fa4\u96c4\u601c\u4ed6\u4eec\u7684\u7236\u4eb2\u4ed7\u4e49\u62a5\u4e3b\uff0c\u5374\u843d\u5f97\u60e8\u906d\u6a2a\u7978\uff0c\u662f\u4ee5\u65e0\u4e0d\u7528\u5fc3\u629a\u80b2\u6559\u5bfc\u3002\u4e09\u5bb6\u5b50\u5973\u672c\u5df2\u4ece\u7236\u4eb2\u5b66\u8fc7\u5bb6\u4f20\u6b66\u529f\uff0c\u6709\u4e86\u6839\u57fa\uff0c\u518d\u5f97\u660e\u5e08\u6307\u70b9\uff0c\u5230\u540e\u6765\u878d\u4f1a\u8d2f\u901a\uff0c\u5404\u81ea\u5353\u7136\u6210\u5bb6\u3002\u201d\u5979\u8bf4\u5230\u8fd9\u91cc\uff0c\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u559f\u7136\u9053\uff1a\u201c\u4ed6\u4eec\u6b66\u529f\u8d8a\u5f3a\uff0c\u62a5\u4ec7\u4e4b\u5fc3\u6108\u5207\u3002\u7ec3\u4e86\u6b66\u529f\u5230\u5e95\u5bf9\u4eba\u662f\u7978\u662f\u798f\uff0c\u6211\u53ef\u5b9e\u5728\u60f3\u4e0d\u660e\u767d\u3002\u201d \u3000\u3000\u5b9d\u6811\u89c1\u5979\u671b\u7740\u7089\u706b\u53ea\u662f\u51fa\u795e\uff0c\u4f17\u4eba\u5374\u6025\u6b32\u542c\u4e0b\u6587\uff0c\u4e8e\u662f\u63a5\u53e3\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u8fd9\u6545\u4e8b\u8bf4\u5f97\u6781\u662f\u52a8\u542c\u3002\u5979\u867d\u4e0d\u63d0\u540d\u9053\u59d3\uff0c\u5404\u4f4d\u81ea\u7136\u4e5f\u90fd\u77e5\u9053\uff0c\u6545\u4e8b\u4e2d\u7684\u4e49\u5144\uff0c\u662f\u95ef\u738b\u7b2c\u4e00\u536b\u58eb\u59d3\u80e1\u7684\u98de\u5929\u72d0\u72f8\uff0c\u90a3\u811a\u592b\u516c\u516c\u59d3\u82d7\uff0c\u5316\u5b50\u516c\u516c\u59d3\u8303\uff0c\u90ce\u4e2d\u516c\u516c\u59d3\u7530\u3002\u4e09\u5bb6\u540e\u4eba\u5b66\u5f97\u7edd\u6280\u540e\u5404\u6811\u4e00\u5e1c\uff0c\u82d7\u5bb6\u6b66\u529f\u79f0\u4e3a\u82d7\u5bb6\u5251\uff0c\u59d3\u8303\u7684\u6210\u4e3a\u5174\u6c49\u4e10\u5e2e\u4e2d\u7684\u5934\u8111\uff0c\u59d3\u7530\u7684\u5230\u540e\u6765\u5efa\u7acb\u4e86\u5929\u9f99\u95e8\u3002\u201d \u3000\u3000\u962e\u58eb\u4e2d\u3001\u6bb7\u5409\u867d\u662f\u5929\u9f99\u524d\u8f88\uff0c\u4f46\u672c\u95e8\u7684\u6765\u5386\u5374\u5230\u6b64\u523b\u65b9\u77e5\uff0c\u4e0d\u7531\u5f97\u6697\u81ea\u60ed\u6127\u3002 \u3000\u3000\u5b9d\u6811\u53c8\u9053\uff1a\u201c\u8fd9\u82d7\u8303\u7530\u4e09\u5bb6\u540e\u4ee3\uff0c\u4e8c\u5341\u4f59\u5e74\u540e\u7ec8\u4e8e\u627e\u5230\u4e86\u90a3\u59d3\u80e1\u7684\u513f\u5b50\u3002\u90a3\u65f6\u4ed6\u6b63\u8eab\u60a3\u91cd\u75c5\uff0c\u5f53\u88ab\u4e09\u5bb6\u903c\u5f97\u81ea\u6740\u3002\u4ece\u6b64\u56db\u5bb6\u540e\u4eba\u8f97\u8f6c\u62a5\u590d\uff0c\u767e\u4f59\u5e74\u6765\uff0c\u6ca1\u4e00\u5bb6\u7684\u5b50\u5b59\u80fd\u5f97\u5584\u7ec8\u3002\u6211\u81ea\u5df1\u5c31\u4eb2\u773c\u89c1\u8fc7\u8fd9\u56db\u5bb6\u540e\u4eba\u4e00\u573a\u60ca\u5fc3\u52a8\u9b44\u7684\u6076\u6597\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u62ac\u8d77\u5934\u6765\uff0c\u671b\u7740\u5b9d\u6811\u9053\uff1a\u201c\u5927\u5e08\uff0c\u8fd9\u6545\u4e8b\u6211\u77e5\u9053\uff0c\u4f60\u522b\u8bf4\u4e86\u3002\u201d\u5b9d\u6811\u9053\uff1a\u201c\u8fd9\u4e9b\u670b\u53cb\u4eec\u5374\u4e0d\u77e5\u9053\uff0c\u4f60\u8bf4\u7ed9\u5927\u4f19\u513f\u542c\u5427\u3002\u201d\u82d7\u82e5\u5170\u6447\u5934\u9053\uff1a\u201c\u90a3\u4e00\u5e74\u7239\u7239\u8ddf\u6211\u8bf4\u4e86\u8fd9\u56db\u4f4d\u516c\u516c\u7684\u6545\u4e8b\u4e4b\u540e\uff0c\u63a5\u7740\u53c8\u8bf4\u4e86\u4e00\u4e2a\u6545\u4e8b\u3002\u4ed6\u8bf4\u4e3a\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u4ed6\u8feb\u5f97\u8fd8\u8981\u6740\u4e00\u4e2a\u4eba\uff0c\u987b\u5f97\u78e8\u5229\u90a3\u67c4\u5251\u3002\u53ea\u662f\u8fd9\u6545\u4e8b\u592a\u60b2\u60e8\u4e86\uff0c\u6211\u4e00\u60f3\u8d77\u5fc3\u91cc\u5c31\u96be\u53d7\uff0c\u771f\u613f\u6211\u4ece\u6765\u6ca1\u542c\u7239\u8bf4\u8fc7\u3002\u201d\u5979\u6c89\u9ed8\u4e86\u534a\u664c\uff0c\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u53d1\u751f\u7684\u65f6\u5019\uff0c\u8fd8\u5728\u6211\u51fa\u4e16\u4e4b\u524d\u7684\u5341\u5e74\u3002\u4e0d\u77e5\u90a3\u4e2a\u53ef\u601c\u7684\u5b69\u5b50\u600e\u6837\u4e86\uff0c\u6211\u771f\u76fc\u671b\u4ed6\u597d\u597d\u7684\u6d3b\u7740\u3002\u201d \u3000\u3000\u4f17\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u4e0d\u77e5\u5979\u6240\u8bf4\u7684\u201c\u53ef\u601c\u5b69\u5b50\u201d\u662f\u4ec0\u4e48\u4eba\uff0c\u53c8\u600e\u4e0e\u773c\u524d\u4e4b\u4e8b\u6709\u5173\uff1f\u4f17\u4eba\u671b\u671b\u82d7\u82e5\u5170\uff0c\u53c8\u671b\u671b\u5b9d\u6811\uff0c\u9759\u5f85\u4e24\u4eba\u4e4b\u4e2d\u6709\u8c01\u6765\u89e3\u5f00\u8fd9\u4e2a\u7591\u56e2\u3002 \u3000\u3000\u5ffd\u7136\u4e4b\u95f4\uff0c\u7ad9\u5728\u4e00\u65c1\u4f8d\u5019\u8336\u6c34\u7684\u4e00\u4e2a\u4ec6\u4eba\u8bf4\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u597d\u5fc3\u6709\u597d\u62a5\u3002\u60f3\u6765\u90a3\u4e2a\u53ef\u601c\u7684\u5b69\u5b50\u5b9a\u662f\u597d\u597d\u7684\u6d3b\u7740\u3002\u201d\u4ed6\u8bdd\u58f0\u751a\u662f\u5636\u54d1\u3002\u4f17\u4eba\u4e00\u9f50\u8f6c\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u4ed6\u767d\u53d1\u8427\u7d22\uff0c\u5e74\u7eaa\u5df2\u8001\uff0c\u7f3a\u4e86\u4e00\u6761\u53f3\u81c2\uff0c\u7528\u5de6\u624b\u6258\u7740\u8336\u76d8\uff0c\u4e00\u6761\u7c97\u5927\u7684\u5200\u75a4\u4ece\u53f3\u7709\u8d77\u659c\u8fc7\u9f3b\u5b50\uff0c\u4e00\u76f4\u5ef6\u5230\u5de6\u8fb9\u5634\u89d2\u3002\u4f17\u4eba\u5fc3\u60f3\uff1a\u201c\u6b64\u4eba\u53d7\u6b64\u91cd\u4f24\uff0c\u5c45\u7136\u8fd8\u80fd\u6328\u4e86\u4e0b\u6765\uff0c\u5b9e\u662f\u4e0d\u6613\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u53f9\u9053\uff1a\u201c\u6211\u542c\u4e86\u7239\u7239\u8bb2\u7684\u6545\u4e8b\u4e4b\u540e\uff0c\u5e38\u5e38\u6697\u4e2d\u795d\u544a\uff0c\u6c42\u8001\u5929\u7237\u4fdd\u4f51\u8fd9\u5b69\u5b50\u957f\u5927\u6210\u4eba\u3002\u53ea\u662f\u6211\u76fc\u671b\u4ed6\u4e0d\u8981\u5b66\u6b66\uff0c\u8981\u50cf\u6211\u8fd9\u6837\uff0c\u4e00\u70b9\u6b66\u827a\u4e5f\u4e0d\u4f1a\u624d\u597d\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u6014\uff0c\u90fd\u611f\u5947\u602a\uff1a\u201c\u77a7\u5979\u8fd9\u526f\u6587\u96c5\u79c0\u6c14\u7684\u6837\u513f\uff0c\u81ea\u662f\u4e0d\u4f1a\u6b66\u827a\uff0c\u4f46\u5979\u662f\u2018\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u2019\u91d1\u9762\u4f5b\u82d7\u5927\u4fa0\u7684\u7231\u5973\uff0c\u96be\u9053\u5979\u7236\u4eb2\u7adf\u4e0d\u4f20\u6388\u4e00\u4e24\u624b\u7edd\u6280\u7ed9\u5979\uff1f\u201d \u3000\u3000\u82d7\u82e5\u5170\u4e00\u89c1\u4f17\u4eba\u8138\u8272\uff0c\u5df2\u77e5\u5927\u5bb6\u5fc3\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7239\u8bf4\u9053\uff0c\u767e\u4f59\u5e74\u6765\uff0c\u80e1\u82d7\u8303\u7530\u56db\u5bb6\u5b50\u5b59\u6028\u6028\u76f8\u62a5\uff0c\u6ca1\u4e00\u4ee3\u80fd\u5f97\u5584\u7ec8\u3002\u4efb\u4ed6\u6b66\u827a\u5982\u4f55\u9ad8\u5f3a\uff0c\u4e00\u751f\u4e0d\u662f\u5fd9\u7740\u53bb\u6740\u4eba\u62a5\u4ec7\uff0c\u5c31\u662f\u9632\u4eba\u524d\u6765\u62a5\u4ec7\u3002\u4e00\u5e74\u4e4b\u4e2d\uff0c\u96be\u5f97\u6709\u51e0\u4e2a\u6708\u5b89\u4e50\u996d\u5403\uff0c\u5c31\u7b97\u6d3b\u5230\u4e86\u4e03\u516b\u5341\u5c81\u9ad8\u9f84\uff0c\u8fd8\u662f\u7ed9\u4ec7\u5bb6\u4e00\u5200\u6740\u6b7b\u3002\u7ec3\u4e86\u6b66\u529f\u975e\u4f46\u4e0d\u80fd\u9632\u8eab\uff0c\u53cd\u8db3\u4ee5\u81f4\u7978\u3002\u6240\u4ee5\u6211\u7239\u7acb\u4e0b\u4e00\u6761\u5bb6\u8bad\uff0c\u81ea\u4ed6\u4ee5\u540e\uff0c\u82d7\u95e8\u7684\u5b50\u5b59\u4e0d\u8bb8\u5b66\u6b66\u3002\u4ed6\u4e5f\u51b3\u4e0d\u6536\u4e00\u4e2a\u5f1f\u5b50\u3002\u6211\u7239\u8bf4\u9053\uff1a\u7eb5\u7136\u4ed6\u5c06\u6765\u7ed9\u4ec7\u4eba\u6740\u4e86\uff0c\u82d7\u5bb6\u5b50\u5f1f\u4e0d\u4f1a\u6b66\u827a\uff0c\u81ea\u7136\u65e0\u6cd5\u4e3a\u4ed6\u62a5\u4ec7\u3002\u90a3\u4e48\u8fd9\u767e\u4f59\u5e74\u6765\u6108\u79ef\u6108\u91cd\u7684\u8840\u503a\uff0c\u6108\u6765\u6108\u662f\u7ea0\u7f20\u4e0d\u6e05\u7684\u51a4\u5b7d\uff0c\u6216\u8bb8\u5c31\u53ef\u4e00\u7b14\u52fe\u9500\u4e86\u3002\u201d\u5b9d\u6811\u5408\u5341\u9053\uff1a\u201c\u5584\u54c9\uff0c\u5584\u54c9\uff01\u82d7\u5927\u4fa0\u80fd\u5982\u6b64\u5927\u5f7b\u5927\u609f\uff0c\u7518\u613f\u8ba9\u76d6\u4e16\u65e0\u53cc\u7684\u82d7\u5bb6\u5251\u5251\u6cd5\u81ea\u4ed6\u800c\u7edd\uff0c\u867d\u662f\u6b66\u6797\u7684\u5927\u635f\u5931\uff0c\u5374\u4e5f\u662f\u4e00\u4ef6\u5927\u5927\u5584\u4e8b\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u89c1\u90a3\u8138\u6709\u5200\u75a4\u7684\u4ec6\u4eba\u76ee\u4e2d\u53d1\u51fa\u5f02\u5149\uff0c\u5fc3\u4e2d\u5fae\u611f\u5947\u602a\uff0c\u5411\u5b9d\u6811\u9053\uff1a\u201c\u6211\u8fdb\u53bb\u6b47\u6b47\uff0c\u5927\u5e08\u8ddf\u5404\u4f4d\u4f2f\u4f2f\u53d4\u53d4\uff0c\u5931\u966a\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\u88e3\u887d\u884c\u793c\uff0c\u8fdb\u4e86\u5185\u5802\u3002 \u3000\u3000\u5b9d\u6811\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u5fc3\u5730\u4ec1\u6148\uff0c\u4e0d\u5fcd\u518d\u542c\u6b64\u4e8b\u3002\u5979\u65e2\u6709\u610f\u907f\u5f00\uff0c\u8001\u8872\u5c31\u8ddf\u5404\u4f4d\u8bf4\u8bf4\u3002\u201d \u3000\u3000\u8fd9\u4e00\u65e5\u81ea\u6e05\u6668\u8d77\u5230\u6b64\u523b\uff0c\u53ea\u4e0d\u8fc7\u51e0\u4e2a\u65f6\u8fb0\uff0c\u65e5\u672a\u8fc7\u5348\uff0c\u4f46\u5404\u4eba\u5df2\u7ecf\u5386\u4e86\u8bb8\u591a\u602a\u5f02\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u5b58\u4e86\u4e0d\u5c11\u7591\u56e2\uff0c\u90fd\u662f\u6025\u6b32\u660e\u767d\u771f\u76f8\u3002 \u3000\u3000\u53ea\u542c\u5b9d\u6811\u8bf4\u9053\uff1a\u201c\u81ea\u4ece\u95ef\u738b\u7684\u56db\u5927\u536b\u58eb\u76f8\u4e92\u4ec7\u6740\u4ee5\u540e\uff0c\u56db\u5bb6\u5b50\u5b59\u767e\u4f59\u5e74\u6765\u65ab\u6740\u4e0d\u4f11\u3002\u53ea\u662f\u90a3\u59d3\u80e1\u7684\u5356\u4e3b\u6c42\u8363\uff0c\u4e3a\u6b66\u6797\u540c\u9053\u6240\u5171\u5f03\uff0c\u6240\u4ee5\u6bcf\u6b21\u5927\u4e89\u6597\uff0c\u80e1\u5bb6\u5b50\u5b59\u52bf\u5b64\uff0c\u5341\u4e5d\u843d\u5728\u4e0b\u98ce\u3002\u53ef\u662f\u80e1\u5bb6\u7684\u5bb6\u4f20\u6b66\u529f\u5f53\u771f\u5389\u5bb3\u65e0\u6bd4\uff0c\u6bcf\u9694\u4e09\u56db\u5341\u5e74\uff0c\u80e1\u5bb6\u5b9a\u6709\u4e00\u4e24\u4e2a\u6770\u51fa\u7684\u5b50\u5f1f\u51fa\u6765\u4e3a\u4e0a\u4ee3\u62a5\u4ec7\uff0c\u4e0d\u4f26\u662f\u80dc\u662f\u8d25\uff0c\u603b\u662f\u6380\u8d77\u4e86\u6ee1\u5929\u8165\u98ce\u8840\u96e8\u3002 \u3000\u3000\u201c\u82d7\u8303\u7530\u4e09\u5bb6\u867d\u7136\u4eba\u4f17\u529b\u5f3a\u3001\u5f97\u9053\u591a\u52a9\uff0c\u4f46\u80e1\u5bb6\u5e38\u5728\u6697\u4e2d\u5ffd\u65bd\u88ad\u51fb\uff0c\u4ee4\u4eba\u9632\u4e0d\u80dc\u9632\u3002\u96cd\u6b63\u521d\u5e74\uff0c\u82d7\u8303\u7530\u4e09\u5bb6\u4e3a\u4e86\u4e89\u593a\u638c\u7ba1\u95ef\u738b\u7684\u519b\u5200\uff0c\u8d77\u4e86\u4e89\u6267\u3002\u504f\u5de7\u80e1\u5bb6\u53c8\u51fa\u4e86\u4e00\u5bf9\u6b66\u529f\u6781\u9ad8\u7684\u5144\u5f1f\uff0c\u4e00\u53e3\u6c14\u4f24\u4e86\u4e09\u5bb6\u5341\u591a\u4eba\u3002\u4e09\u5bb6\u6025\u4e86\uff0c\u7531\u7530\u5bb6\u51fa\u9762\uff0c\u9080\u8bf7\u6c5f\u6e56\u597d\u624b\uff0c\u624d\u9f50\u5fc3\u5408\u529b\u6740\u4e86\u80e1\u6c0f\u5144\u5f1f\u3002\u8fd9\u4e00\u5e74\u5927\u6c5f\u5357\u5317\u7684\u82f1\u96c4\u8c6a\u6770\u805a\u4f1a\u6d1b\u9633\uff0c\u7ed3\u76df\u7acb\u8a93\uff0c\u4ece\u6b64\u95ef\u738b\u519b\u5200\u7531\u5929\u9f99\u95e8\u7530\u6c0f\u6267\u638c\uff0c\u82e5\u662f\u80e1\u5bb6\u540e\u4eba\u518d\u6765\u5bfb\u7554\u751f\u4e8b\uff0c\u7531\u5929\u9f99\u7530\u6c0f\u62ff\u8fd9\u53e3\u519b\u5200\u53f7\u53ec\u6c5f\u6e56\u597d\u6c49\uff0c\u5171\u540c\u5bf9\u4ed8\u3002\u5929\u4e0b\u82f1\u96c4\u53ea\u8981\u89c1\u5230\u519b\u5200\uff0c\u4e0d\u8bba\u8eab\u6709\u5929\u5927\u7684\u8981\u4e8b\uff0c\u90fd\u5f97\u6401\u4e0b\u4e86\u5e94\u53ec\u8d74\u4e49\u3002 \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u8fc7\u5f97\u4e45\u4e86\uff0c\u540e\u4eba\u4e5f\u6e10\u6e10\u6de1\u5fd8\u4e86\u3002\u53ea\u662f\u5929\u9f99\u95e8\u638c\u95e8\u5bf9\u8fd9\u53e3\u5b9d\u5200\u59cb\u7ec8\u5341\u5206\u91cd\u89c6\u3002\u542c\u8bf4\u5929\u9f99\u95e8\u540e\u6765\u5206\u4e3a\u5357\u5b97\u5317\u5b97\uff0c\u4e24\u5b97\u6bcf\u9694\u5341\u5e74\uff0c\u8f6e\u6d41\u638c\u7ba1\u3002\u962e\u5e08\u5144\u3001\u6bb7\u5e08\u5144\uff0c\u6211\u8bf4\u5f97\u53ef\u5bf9\u4e48\uff1f\u201d \u3000\u3000\u962e\u58eb\u4e2d\u548c\u6bb7\u5409\u9f50\u58f0\u9053\uff1a\u201c\u5927\u5e08\u8bf4\u5f97\u4e0d\u9519\u3002\u201d \u3000\u3000\u5b9d\u6811\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u4e8b\u9694\u591a\u5e74\uff0c\u5929\u9f99\u95e8\u95e8\u4e0b\u867d\u7136\u90fd\u77e5\u8fd9\u5200\u662f\u672c\u95e8\u7684\u9547\u95e8\u4e4b\u5b9d\uff0c\u4f46\u6b64\u5200\u5230\u5e95\u6765\u5386\u5982\u4f55\uff0c\u5374\u5df2\u6781\u5c11\u6709\u4eba\u8003\u7a76\u3002 \u3000\u3000\u65f6\u65e5\u4e45\u4e86\uff0c\u539f\u4e5f\u96be\u602a\u3002\u53ea\u662f\u548c\u5c1a\u6709\u4e00\u4e8b\u4e0d\u660e\uff0c\u5374\u8981\u8bf7\u6559\u66f9\u5144\u3002\u201d \u3000\u3000\u66f9\u4e91\u5947\u5927\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d\u5b9d\u6811\u9053\uff1a\u201c\u8001\u8872\u66fe\u542c\u4eba\u8bf4\u8fc7\uff0c\u5929\u9f99\u95e8\u65b0\u65e7\u638c\u95e8\u4ea4\u66ff\u4e4b\u65f6\uff0c\u8001\u638c\u95e8\u5fc5\u5c06\u6b64\u5200\u6765\u5386\u8bf4\u4e0e\u65b0\u638c\u95e8\u77e5\u6653\u3002 \u3000\u3000\u600e\u5730\u66f9\u5144\u8363\u4e3a\u638c\u95e8\uff0c\u7adf\u7136\u4e0d\u77e5\uff0c\u96be\u9053\u7530\u5f52\u519c\u7530\u8001\u638c\u95e8\u5fd8\u4e86\u8fd9\u4e00\u6761\u95e8\u89c4\u4e48\uff1f\u201d \u3000\u3000\u66f9\u4e91\u5947\u80c0\u7ea2\u4e86\u8138\uff0c\u5f85\u8981\u8bf4\u8bdd\uff0c\u7530\u9752\u6587\u63a5\u53e3\u9053\uff1a\u201c\u5bd2\u95e8\u4e0d\u5e78\uff0c\u5148\u7236\u7a81\u7136\u53bb\u4e16\uff0c\u6765\u4e0d\u53ca\u8ddf\u66f9\u5e08\u54e5\u8be6\u8a00\u3002\u201d\u5b9d\u6811\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\u3002 \u3000\u3000\u5509\uff0c\u6b64\u5200\u6211\u5df2\u7b2c\u4e8c\u6b21\u77a7\u89c1\u3002\u9996\u6b21\u89c1\u5230\u4e4b\u65f6\uff0c\u5c48\u6307\u7b97\u6765\u5df2\u662f\u4e8c\u5341\u4e03\u5e74\u4e4b\u524d\u7684\u4e8b\u4e86\u3002\u201d\u7530\u9752\u6587\u5fc3\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u7ea6\u83ab\u5341\u4e03\u516b\u5c81\u5e74\u7eaa\uff0c\u5979\u8bf4\u90a3\u573a\u60e8\u4e8b\u53d1\u751f\u5728\u5979\u51fa\u4e16\u4e4b\u524d\u5341\u5e74\uff0c\u6b63\u662f\u4e8c\u5341\u4e03\u5e74\u4e4b\u524d\u3002\u90a3\u4e48\u8fd9\u548c\u5c1a\u89c1\u5230\u6b64\u5200\uff0c\u770b\u6765\u4f1a\u4e0e\u82d7\u59d1\u5a18\u6240\u8bf4\u7684\u4e8b\u6709\u5173\u3002\u201d \u3000"}),showOnHomepage:!0},{name:"\u56db",content:(0,a.jsx)(a.Fragment,{children:"\u53ea\u542c\u5b9d\u6811\u8bf4\u9053\uff1a\u201c\u90a3\u65f6\u8001\u8872\u5c1a\u672a\u51fa\u5bb6\uff0c\u5728\u76f4\u96b6\u6ca7\u5dde\u4e61\u4e0b\u7684\u4e00\u4e2a\u5c0f\u9547\u4e0a\u884c\u533b\u4e3a\u751f\u3002\u6ca7\u5dde\u6c11\u98ce\u597d\u6b66\uff0c\u5c11\u5e74\u5b50\u5f1f\u5927\u90fd\u5b66\u8fc7\u4e09\u62f3\u4e24\u811a\u3002\u8001\u8872\u505a\u7684\u662f\u8dcc\u6253\u533b\u751f\uff0c\u4e5f\u5b66\u8fc7\u4e00\u70b9\u6b66\u827a\u3002\u90a3\u5c0f\u9547\u5730\u5904\u504f\u50fb\uff0c\u53ea\u4e94\u516d\u767e\u5c45\u6c11\u3002\u8001\u8872\u9760\u4e00\u70b9\u513f\u533b\u9053\u52c9\u5f3a\u7cca\u53e3\uff0c\u81ea\u7136\u517b\u4e0d\u8d77\u5bb6\uff0c\u8bf4\u4e0d\u4e0a\u5a36\u59bb\u751f\u5b50\u3002 \u3000\u3000\u201c\u90a3\u4e00\u5e74\u814a\u6708\uff0c\u8001\u8872\u559d\u4e86\u4e09\u7897\u51b7\u9762\u6c64\u7761\u4e86\uff0c\u6b63\u5728\u505a\u68a6\u53d1\u4e86\u5927\u8d22\uff0c\u4ed6\u5988\u7684\u8981\u5a36\u4e2a\u7f8e\u8c8c\u8001\u5a46\uff0c\u5ffd\u542c\u5f97\u562d\u562d\u562d\u4e00\u9635\u54cd\uff0c\u6709\u4eba\u7528\u529b\u6253\u95e8\u3002 \u3000\u3000\u201c\u5c4b\u5b50\u5916\u5317\u98ce\u522e\u5f97\u6b63\u7d27\uff0c\u6211\u7095\u91cc\u65e9\u7184\u4e86\u706b\uff0c\u88ab\u5b50\u53c8\u8584\uff0c\u5b9e\u5728\u4e0d\u60f3\u8d77\u6765\uff0c\u597d\u68a6\u7ed9\u4eba\u60ca\u9192\u4e86\uff0c\u66f4\u662f\u6ca1\u597d\u6c14\u3002\u4f46\u6572\u95e8\u58f0\u8d8a\u6765\u8d8a\u54cd\uff0c\u6709\u4eba\u5927\u53eb\uff1a\u2018\u5927\u592b\uff0c\u5927\u592b\uff01\u2019\u90a3\u4eba\u662f\u5173\u897f\u53e3\u97f3\uff0c\u4e0d\u662f\u672c\u5730\u4eba\uff0c\u518d\u4e0d\u5f00\u95e8\uff0c\u77a7\u6765\u5c31\u8981\u7834\u95e8\u800c\u5165\u3002\u6211\u4e0d\u77e5\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff0c\u5fd9\u62ab\u8863\u8d77\u6765\uff0c\u521a\u62d4\u5f00\u95e8\u95e9\uff0c\u7830\u7684\u4e00\u54cd\uff0c\u5927\u95e8\u5c31\u7ed9\u4eba\u7528\u529b\u63a8\u5f00\uff0c\u4e0d\u662f\u6211\u95ea\u5f97\u5feb\uff0c\u989d\u89d2\u51c6\u6559\u7ed9\u5927\u95e8\u649e\u8d77\u4e00\u4e2a\u8001\u5927\u7624\u5b50\u3002\u53ea\u89c1\u706b\u5149\u4e00\u6643\uff0c\u4e00\u6761\u6c49\u5b50\u624b\u6267\u706b\u628a\uff0c\u649e\u4e86\u8fdb\u6765\uff0c\u53eb\u9053\uff1a\u2018\u5927\u592b\uff0c\u8bf7\u4f60\u5feb\u53bb\u3002\u2019\u201c\u6211\u9053\uff1a\u2018\u4ec0\u4e48\u4e8b\uff1f\u8001\u5144\u662f\u8c01\uff1f\u2019\u90a3\u4eba\u9053\uff1a\u2018\u6709\u4eba\u751f\u4e86\u6025\u75c5\uff01\u2019\u4ed6\u4e0d\u7b54\u6211\u7b2c\u4e8c\u53e5\u8bdd\uff0c\u5de6\u624b\u4e00\u6325\uff0c\u5f53\u7684\u4e00\u54cd\uff0c\u5728\u684c\u4e0a\u4e22\u4e86\u4e00\u952d\u5927\u94f6\u3002\u8fd9\u952d\u94f6\u5b50\u8db3\u8db3\u6709\u4e8c\u5341\u4e24\u91cd\uff0c\u6211\u5728\u4e61\u4e0b\u7ed9\u4eba\u533b\u75c5\uff0c\u603b\u662f\u51e0\u5341\u6587\u51e0\u767e\u6587\u7684\u533b\u91d1\uff0c\u54ea\u91cc\u89c1\u8fc7\u4e00\u51fa\u624b\u5c31\u662f\u4e8c\u5341\u4e24\u4e00\u53ea\u7684\u5927\u5143\u5b9d\uff1f\u5fc3\u4e2d\u53c8\u60ca\u53c8\u559c\uff0c\u5fd9\u6536\u4e86\u94f6\u5b50\uff0c\u7a7f\u8863\u7740\u978b\u3002\u90a3\u6c49\u5b50\u4e0d\u4f4f\u53e3\u7684\u50ac\u4fc3\u3002\u6211\u4e00\u9762\u7a7f\u8863\uff0c\u4e00\u9762\u77a7\u4ed6\u76f8\u8c8c\uff0c\u4f46\u89c1\u4ed6\u795e\u60c5\u7c97\u8c6a\uff0c\u4e00\u526f\u4f1a\u5bb6\u5b50\u7684\u6a21\u6837\uff0c\u53ea\u662f\u8138\u5e26\u5fe7\u8272\u3002 \u3000\u3000\u201c\u4ed6\u4e0d\u7b49\u6211\u6263\u597d\u8863\u94ae\uff0c\u4e00\u624b\u66ff\u6211\u633d\u4e86\u836f\u7bb1\uff0c\u4e00\u624b\u62c9\u4e86\u6211\u624b\u5c31\u8d70\u3002\u6211\u9053\uff1a\u2018\u5f85\u6211\u63a9\u4e0a\u4e86\u95e8\u3002\u2019\u4ed6\u9053\uff1a\u2018\u7ed9\u5077\u4e86\u4ec0\u4e48\uff0c\u90fd\u8d54\u4f60\u7684\u3002\u2019\u62c9\u7740\u6211\u6025\u6b65\u800c\u884c\uff0c\u8d70\u8fdb\u4e86\u5e73\u5b89\u5ba2\u5e97\u3002\u90a3\u662f\u9547\u4e0a\u53ea\u6b64\u4e00\u5bb6\u7684\u5ba2\u5e97\uff0c\u4e13\u4f9b\u6765\u5f80\u5317\u4eac\u7684\u9a74\u592b\u811a\u592b\u4f4f\u5bbf\uff0c\u5730\u65b9\u867d\u4e0d\u7b97\u5c0f\uff0c\u53ef\u662f\u53c8\u9ed1\u53c8\u810f\u3002\u6211\u60f3\u6b64\u4eba\u6041\u5730\u8c6a\u5bcc\uff0c\u600e\u80fd\u5728\u8fd9\u822c\u5730\u65b9\u6b47\u8db3\uff1f\u5ff5\u5934\u5c1a\u672a\u8f6c\u5b8c\uff0c\u4ed6\u5df2\u62c9\u7740\u6211\u8d70\u8fdb\u5e97\u5802\u3002\u5927\u5802\u4e0a\u70db\u706b\u70b9\u5f97\u660e\u6643\u6643\u5730\uff0c\u5750\u7740\u56db\u4e94\u4e2a\u6c49\u5b50\u3002\u62c9\u7740\u6211\u624b\u7684\u90a3\u4eba\u53eb\u9053\uff1a\u2018\u5927\u592b\u6765\u5566\uff01\u2019\u5404\u4eba\u8138\u73b0\u559c\u8272\uff0c\u62e5\u7740\u6211\u8d70\u8fdb\u4e1c\u53a2\u623f\u3002 \u3000\u3000\u201c\u6211\u4e00\u8fdb\u95e8\uff0c\u4e0d\u7531\u5f97\u5413\u4e86\u4e00\u8df3\uff0c\u53ea\u89c1\u7095\u4e0a\u5e76\u6392\u8eba\u7740\u56db\u4e2a\u4eba\uff0c\u90fd\u662f\u6ee1\u8eab\u8840\u6c61\u3002\u6211\u53eb\u90a3\u6c49\u5b50\u62ff\u70db\u706b\u79fb\u8fd1\u7ec6\u770b\uff0c\u89c1\u90a3\u56db\u4eba\u90fd\u53d7\u4e86\u91cd\u4f24\uff0c\u6709\u7684\u8138\u4e0a\u53d7\u5230\u5200\u780d\uff0c\u6709\u7684\u624b\u81c2\u88ab\u65a9\u53bb\u4e00\u622a\u3002\u6211\u95ee\u9053\uff1a\u2018\u600e\u4e48\u4f24\u6210\u8fd9\u6837\u5b50\uff1f\u7ed9\u5f3a\u4eba\u5bb3\u7684\u4e48\uff1f\u2019\u90a3\u6c49\u5b50\u5389\u58f0\u9053\uff1a\u2018\u4f60\u5feb\u7ed9\u6cbb\u4f24\uff0c\u53e6\u6709\u91cd\u8c22\u3002\u53ef\u4e0d\u8bb8\u591a\u7ba1\u95f2\u4e8b\uff0c\u4e71\u8bf4\u4e71\u95ee\u3002\u2019\u6211\u5fc3\u9053\uff1a\u2018\u597d\u5bb6\u4f19\uff0c\u8fd9\u4e48\u51f6\uff01\u2019\u4f46\u89c1\u4ed6\u4eec\u4e2a\u4e2a\u72e0\u9738\u9738\u7684\uff0c\u8eab\u4e0a\u53c8\u5404\u5e26\u5175\u5203\uff0c\u4e0d\u6562\u518d\u95ee\uff0c\u66ff\u56db\u4eba\u4e0a\u4e86\u91d1\u521b\u836f\uff0c\u6b62\u8840\u5305\u624e\u505c\u5f53\u3002 \u3000\u3000\u201c\u90a3\u6c49\u5b50\u9053\uff1a\u2018\u8fd9\u8fb9\u8fd8\u6709\u3002\u2019\u9886\u6211\u8d70\u5230\u897f\u53a2\uff0c\u7095\u4e0a\u4e5f\u6709\u4e09\u4e2a\u53d7\u4f24\u7684\u8eba\u7740\uff0c\u8eab\u4e0a\u4e5f\u90fd\u662f\u5175\u5203\u7684\u65b0\u4f24\u3002\u6211\u7ed9\u4e0a\u836f\u6b62\u4e86\u8840\uff0c\u53c8\u7ed9\u4ed6\u4eec\u670d\u4e9b\u5b81\u795e\u51cf\u75bc\u7684\u6c64\u836f\u3002\u4e03\u4e2a\u4eba\u5148\u540e\u90fd\u7761\u7740\u4e86\u3002 \u3000\u3000\u201c\u90a3\u51e0\u4e2a\u6c49\u5b50\u89c1\u6211\u7528\u836f\u6709\u6548\uff0c\u5bf9\u6211\u5c31\u5ba2\u6c14\u4e9b\u4e86\uff0c\u4e0d\u518d\u50cf\u521d\u65f6\u90a3\u822c\u51f6\u72e0\u3002\u4ed6\u4eec\u53eb\u5e97\u4f34\u5728\u4e1c\u53a2\u623f\u7528\u95e8\u677f\u7ed9\u6211\u642d\u4e00\u5f20\u5e8a\uff0c\u4ee5\u9632\u4f24\u52bf\u5982\u6709\u53d8\u5316\uff0c\u968f\u65f6\u53ef\u4ee5\u533b\u6cbb\u3002 \u3000\u3000\u201c\u7761\u5230\u9e21\u9e23\u65f6\u5206\uff0c\u95e8\u5916\u9a6c\u8e44\u58f0\u54cd\uff0c\u5954\u5230\u5e97\u524d\uff0c\u90a3\u4e00\u6279\u6c49\u5b50\u4e00\u9f50\u51fa\u53bb\u8fce\u63a5\u3002\u6211\u88c5\u7761\u5077\u770b\uff0c\u53ea\u89c1\u8fdb\u6765\u4e86\u4e24\u4eba\uff0c\u4e00\u4e2a\u53eb\u5316\u5b50\u6253\u626e\uff0c\u53cc\u76ee\u70af\u70af\u6709\u795e\uff0c\u53e6\u4e00\u4e2a\u9762\u76ee\u6e05\u79c0\uff0c\u5e74\u7eaa\u4e0d\u5927\u3002\u8fd9\u4e24\u4eba\u8d70\u5230\u7095\u8fb9\u5bdf\u770b\u4f24\u8005\u3002\u53d7\u4f24\u7684\u4eba\u5fd9\u5fcd\u75db\u5750\u8d77\uff0c\u5bf9\u4e24\u4eba\u6781\u662f\u606d\u656c\u3002 \u3000\u3000\u6211\u542c\u4ed6\u4eec\u53eb\u90a3\u5316\u5b50\u4e3a\u8303\u5e2e\u4e3b\uff0c\u53eb\u90a3\u9752\u5e74\u4e3a\u7530\u76f8\u516c\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u987f\u4e86\u4e00\u987f\uff0c\u5411\u7530\u9752\u6587\u9053\uff1a\u201c\u6211\u521d\u89c1\u4ee4\u5c0a\u7684\u65f6\u5019\uff0c\u59d1\u5a18\u8fd8\u6ca1\u51fa\u4e16\u5462\u3002\u4ee4\u5c0a\u4e3a\u4eba\u662f\u5f88\u7cbe\u660e\u7684\uff0c\u90a3\u5929\u65e9\u6668\u4ed6\u90a3\u526f\u679c\u65ad\u5e72\u7ec3\u7684\u6a21\u6837\uff0c\u4eca\u65e5\u72b9\u5728\u76ee\u524d\u3002\u201d\u7530\u9752\u6587\u773c\u5708\u513f\u4e00\u7ea2\uff0c\u5782\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u5b9d\u6811\u9053\uff1a\u201c\u6ca1\u53d7\u4f24\u7684\u51e0\u4e2a\u6c49\u5b50\u4e4b\u4e2d\uff0c\u6709\u4e00\u4eba\u4f4e\u58f0\u8bf4\u9053\uff1a\u2018\u8303\u5e2e\u4e3b\uff0c\u7530\u76f8\u516c\uff0c\u5f20\u5bb6\u5144\u5f1f\u4ece\u5173\u5916\u4e00\u8def\u8ddf\u968f\u8fd9\u70b9\u5b50\u592b\u59bb\u5357\u6765\uff0c\u67e5\u5f97\u786e\u786e\u5b9e\u5b9e\uff0c\u94c1\u76d2\u513f\u786e\u662f\u5728\u70b9\u5b50\u8eab\u4e0a\u3002\u2019\u201d\u4f17\u4eba\u542c\u5230\u201c\u94c1\u76d2\u513f\u201d\u4e09\u5b57\uff0c\u76f8\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u90fd\u60f3\uff1a\u201c\u8bf4\u5230\u6b63\u9898\u5566\u3002\u201d \u3000\u3000\u5b9d\u6811\u9053\uff1a\u201c\u8303\u5e2e\u4e3b\u70b9\u4e86\u70b9\u5934\u3002\u90a3\u6c49\u5b50\u53c8\u9053\uff1a\u2018\u54b1\u4eec\u90fd\u5019\u5728\u5510\u5b98\u5c6f\u63a5\u5e94\uff0c\u6d3e\u4eba\u7ed9\u60a8\u4e24\u4f4d\u548c\u91d1\u9762\u4f5b\u82d7\u5927\u4fa0\u9001\u4fe1\u3002\u4e0d\u6599\u7ed9\u90a3\u70b9\u5b50\u77a7\u7834\u4e86\u3002\u4ed6\u4e00\u4eba\u62e6\u5728\u9053\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u4eec\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4e00\u8def\u8ddf\u7740\u6211\u4f5c\u751a\uff1f\u4f60\u4eec\u662f\u82d7\u8303\u7530\u4e09\u5bb6\u6d3e\u6765\u7684\u662f\u4e0d\u662f\uff1f\u201d\u5f20\u5927\u54e5\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5c31\u597d\u5566\u3002\u201d\u90a3\u70b9\u5b50\u8138\u4e00\u6c89\uff0c\u5939\u624b\u5c06\u5f20\u5927\u54e5\u7684\u5200\u593a\u4e86\u53bb\uff0c\u6298\u4e3a\u4e24\u6bb5\uff0c\u629b\u5728\u5730\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u591a\u4f24\u4eba\u547d\uff0c\u5feb\u6eda\u5427\uff01\u201d\u6211\u4eec\u89c1\u70b9\u5b50\u624b\u4e0b\u5389\u5bb3\uff0c\u4e00\u62e5\u800c\u4e0a\u3002\u5f20\u5927\u54e5\u5374\u98de\u811a\u53bb\u8e22\u4ed6\u5a18\u5b50\u7684\u5927\u809a\u5b50\u3002\u90a3\u70b9\u5b50\u5927\u6012\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6b32\u76f8\u9976\uff0c\u4f60\u4eec\u7adf\u5982\u6b64\u65e0\u793c\uff01\u201d\u62a2\u4e86\u4e00\u628a\u5200\uff0c\u4e00\u53e3\u6c14\u4f24\u4e86\u6211\u4eec\u4e03\u4eba\u3002\u2019\u201c\u7530\u76f8\u516c\u9053\uff1a\u2018\u4ed6\u8fd8\u8bf4\u4e86\u4e9b\u4ec0\u4e48\u8bdd\uff1f\u2019\u90a3\u6c49\u5b50\u9053\uff1a\u2018\u90a3\u70b9\u5b50\u672c\u6765\u8fd8\u8981\u4f24\u4eba\uff0c\u4ed6\u5a18\u5b50\u5728\u8f66\u4e2d\u53eb\u9053\uff1a\u2018\u7b97\u5566\uff0c\u7ed9\u4f60\u6ca1\u51fa\u4e16\u7684\u5b69\u5b50\u79ef\u79ef\u5fb7\u5427\u2019\u90a3\u70b9\u5b50\u7b11\u4e86\u7b11\uff0c\u53cc\u624b\u4e00\u62d7\uff0c\u5c06\u90a3\u67c4\u5200\u6298\u65ad\u4e86\u3002\u7530\u76f8\u516c\u5411\u8303\u5e2e\u4e3b\u671b\u4e86\u4e00\u773c\uff0c\u95ee\u9053\uff1a\u2018\u4f60\u77a7\u6e05\u695a\u4e86\uff1f\u5f53\u771f\u662f\u7528\u624b\u6298\u65ad\u7684\uff1f\u2019\u90a3\u6c49\u5b50\u9053\uff1a\u2018\u662f\uff0c\u5c0f\u4eba\u5f53\u65f6\u6b63\u5728\u4ed6\u8eab\u65c1\uff0c\u77a7\u5f97\u6e05\u6e05\u695a\u695a\u3002\u2019\u7530\u76f8\u516c\u55ef\u4e86\u4e00\u58f0\uff0c\u62ac\u8d77\u4e86\u5934\u51fa\u795e\u3002\u8303\u5e2e\u4e3b\u9053\uff1a\u2018\u8d24\u5f1f\u4e0d\u7528\u62c5\u5fc3\uff0c\u82d7\u5927\u4fa0\u5b9a\u80fd\u5bf9\u4ed8\u5f97\u4e86\u4ed6\u3002\u2019\u201c\u90a3\u6c49\u5b50\u9053\uff1a\u2018\u4ed6\u5230\u6c5f\u5357\u53bb\uff0c\u5b9a\u8981\u6253\u4ece\u6b64\u5904\u8fc7\u3002\u4e24\u4f4d\u5b88\u5728\u8fd9\u91cc\uff0c\u7ba1\u6559\u4ed6\u9003\u4e0d\u4e86\u3002\u2019\u8303\u7530\u4e8c\u4eba\u8138\u8272\u90d1\u91cd\uff0c\u4e00\u9762\u4f4e\u58f0\u5546\u91cf\uff0c\u6162\u6162\u8d70\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u201c\u6211\u7b49\u4ed6\u4eec\u51fa\u53bb\u540e\uff0c\u8fd9\u624d\u5047\u88c5\u9192\u6765\uff0c\u8d77\u8eab\u7ed9\u4e03\u4e2a\u4f24\u8005\u6362\u836f\u3002 \u3000\u3000\u6211\u5fc3\u91cc\u60f3\uff1a\u201c\u90a3\u70b9\u5b50\u4e0d\u77e5\u662f\u8c01\uff0c\u4ed6\u53ef\u662f\u624b\u4e0b\u5bb9\u60c5\u3002\u8fd9\u4e03\u4eba\u4f24\u52bf\u867d\u91cd\uff0c\u5374\u4e2a\u4e2a\u6ca1\u4f24\u5230\u8981\u5bb3\u3002\u2019\u201c\u8fd9\u5929\u508d\u665a\uff0c\u5927\u5bb6\u6b63\u5728\u5385\u4e0a\u5403\u996d\uff0c\u4e00\u4e2a\u6c49\u5b50\u5954\u4e86\u8fdb\u6765\uff0c\u53eb\u9053\uff1a\u2018\u6765\u5566\uff01\u2019\u4f17\u4eba\u8138\u4e0a\u53d8\u8272\uff0c\u629b\u4e0b\u7b77\u5b50\u996d\u7897\uff0c\u62bd\u51fa\u5175\u5203\uff0c\u62a2\u4e86\u51fa\u53bb\u3002\u6211\u6084\u6084\u8ddf\u5728\u540e\u9762\uff0c\u5fc3\u4e2d\u5bb3\u6015\uff0c\u53ef\u4e5f\u60f3\u77a7\u4e2a\u70ed\u95f9\u3002 \u3000\u3000\u201c\u53ea\u89c1\u5927\u9053\u4e0a\u5c18\u571f\u98de\u626c\uff0c\u4e00\u8f86\u5927\u8f66\u8fdc\u8fdc\u9a76\u6765\u3002\u8303\u7530\u4e8c\u4f4d\u7387\u4f17\u8fce\u4e86\u4e0a\u53bb\u3002\u6211\u8ddf\u5728\u6700\u540e\u3002\u90a3\u5927\u8f66\u9a76\u5230\u4f17\u4eba\u9762\u524d\uff0c\u5c31\u505c\u4f4f\u4e86\u3002 \u3000\u3000\u8303\u5e2e\u4e3b\u53eb\u9053\uff1a\u2018\u59d3\u80e1\u7684\uff0c\u51fa\u6765\u5427\u3002\u2019\u53ea\u542c\u5f97\u8f66\u5e18\u5185\u4e00\u4eba\u8bf4\u9053\uff1a\u2018\u53eb\u5316\u513f\u6765\u8ba8\u8d4f\u662f\u4e0d\u662f\uff1f\u597d\uff0c\u6bcf\u4e2a\u4eba\u65bd\u820d\u4e00\u6587\uff01\u2019\u773c\u89c1\u9ec4\u5149\u8fde\u95ea\uff0c\u4f17\u4eba\u554a\u54df\u3001\u554a\u54df\u7684\u51e0\u58f0\u53eb\uff0c\u5148\u540e\u6454\u5012\u3002\u8303\u7530\u4e24\u4f4d\u6b66\u529f\u9ad8\uff0c\u6ca1\u6454\u5012\uff0c\u4f46\u624b\u8155\u4e0a\u8fd8\u662f\u5404\u4e2d\u4e86\u4e00\u679a\u91d1\u94b1\u9556\uff0c\u4e00\u6756\u4e00\u5251\uff0c\u6492\u624b\u843d\u5728\u5730\u4e0b\u3002\u7530\u76f8\u516c\u53eb\u9053\uff1a\u2018\u8303\u5927\u54e5\uff0c\u626f\u547c\uff01\u2019\u201c\u8303\u5e2e\u4e3b\u8eab\u624b\u597d\u751f\u4e86\u5f97\uff0c\u5f2f\u8170\u62fe\u8d77\u94c1\u6756\uff0c\u5982\u98ce\u822c\u62a2\u5230\u5012\u5728\u5730\u4e0b\u7684\u51e0\u540d\u6c49\u5b50\u8eab\u65c1\uff0c\u8981\u7ed9\u4ed6\u4eec\u89e3\u5f00\u7a74\u9053\u3002\u6211\u5b66\u8dcc\u6253\u4e4b\u65f6\uff0c\u5e08\u7236\u6559\u8fc7\u4eba\u8eab\u7684\u4e09\u5341\u516d\u9053\u5927\u7a74\uff0c\u6240\u4ee5\u8303\u5e2e\u4e3b\u4f38\u624b\u89e3\u7a74\uff0c\u6211\u4e5f\u61c2\u5f97\u4e00\u70b9\u513f\u3002\u54ea\u77e5\u4ed6\u63a8\u62ff\u6309\u634f\uff0c\u5fd9\u4e2a\u4e0d\u4e86\uff0c\u5012\u5728\u5730\u4e0b\u7684\u4eba\u7adf\u662f\u4e1d\u6beb\u4e0d\u52a8\u3002\u8f66\u4e2d\u90a3\u4eba\u7b11\u9053\uff1a\u2018\u5f88\u597d\uff0c\u4e00\u6587\u94b1\u4e0d\u591f\uff0c\u6bcf\u4eba\u518d\u8d4f\u4e00\u6587\u3002\u2019\u53c8\u662f\u5341\u51e0\u679a\u94dc\u94b1\u4e00\u679a\u8ddf\u7740\u4e00\u679a\u6492\u51fa\u6765\uff0c\u6bcf\u4eba\u7a74\u9053\u4e0a\u4e2d\u4e86\u4e00\u4e0b\uff0c\u767b\u65f6\u56db\u80a2\u6d3b\u52a8\uff0c\u7eb7\u7eb7\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u201c\u7530\u76f8\u516c\u6a2a\u5251\u62a4\u8eab\uff0c\u53eb\u9053\uff1a\u2018\u59d3\u80e1\u7684\uff0c\u4eca\u65e5\u6211\u4eec\u7518\u62dc\u4e0b\u98ce\uff0c\u4f60\u6709\u79cd\u5c31\u522b\u9003\u3002\u2019\u8f66\u4e2d\u90a3\u4eba\u5e76\u4e0d\u56de\u7b54\uff0c\u4f46\u542c\u5f97\u55e4\u7684\u4e00\u58f0\uff0c\u4e00\u679a\u94dc\u94b1\u4ece\u8f66\u4e2d\u6fc0\u5c04\u800c\u51fa\uff0c\u6b63\u6253\u5728\u4ed6\u5251\u5c16\u4e4b\u4e0a\uff0c\u94ee\u7684\u4e00\u54cd\uff0c\u90a3\u5251\u76f4\u98de\u51fa\u53bb\uff0c\u63d2\u5728\u571f\u4e2d\u3002\u7530\u76f8\u516c\u4e3e\u8d77\u6301\u5251\u7684\u53f3\u624b\uff0c\u864e\u53e3\u4e0a\u6d41\u51fa\u8840\u6765\u3002 \u3000\u3000\u201c\u4ed6\u89c1\u654c\u4eba\u5982\u6b64\u5389\u5bb3\uff0c\u8138\u8272\u5927\u53d8\uff0c\u624b\u4e00\u6325\uff0c\u4e0e\u8303\u5e2e\u4e3b\u7387\u9886\u4f17\u4eba\u5954\u56de\u5ba2\u5e97\uff0c\u80cc\u8d77\u4e03\u4e2a\u4f24\u8005\uff0c\u4e0a\u9a6c\u5411\u5357\u9a70\u53bb\u3002\u7530\u76f8\u516c\u4e34\u53bb\u4e4b\u65f6\uff0c\u53c8\u7ed9\u4e86\u6211\u4e8c\u5341\u4e24\u94f6\u5b50\u3002\u6211\u89c1\u4ed6\u8fd9\u7b49\u6177\u6168\uff0c\u786e\u662f\u4f4d\u8c6a\u4fa0\u541b\u5b50\uff0c\u5fc3\u60f3\uff1a\u2018\u8f66\u4e2d\u5b9a\u662f\u4e2a\u7a77\u51f6\u6781\u6076\u7684\u6b79\u5f92\uff0c\u5426\u5219\u50cf\u7530\u76f8\u516c\u8fd9\u6837\u7684\u597d\u4eba\uff0c\u600e\u4f1a\u548c\u4ed6\u7ed3\u4ec7\uff1f\u2019\u6b63\u8981\u56de\u5bb6\uff0c\u53ea\u89c1\u90a3\u8f86\u5927\u8f66\u9a76\u5230\u4e86\u5ba2\u5e97\u95e8\u53e3\u505c\u4e0b\u3002\u6211\u597d\u5947\u5fc3\u8d77\uff0c\u8981\u77a7\u77a7\u90a3\u6b79\u5f92\u600e\u751f\u6a21\u6837\uff0c\u5f53\u4e0b\u8eb2\u5728\u67dc\u53f0\u540e\u9762\uff0c\u671b\u7740\u8f66\u95e8\u3002 \u3000\u3000\u201c\u53ea\u89c1\u95e8\u5e18\u6380\u5f00\uff0c\u8f66\u4e2d\u51fa\u6765\u4e00\u6761\u5927\u6c49\uff0c\u8fd9\u4eba\u751f\u5f97\u5f53\u771f\u51f6\u6076\uff0c\u4e00\u5f20\u9ed1\u6f06\u8138\u76ae\uff0c\u6ee1\u816e\u6d53\u9aef\uff0c\u5934\u53d1\u5374\u53c8\u4e0d\u7ed3\u8fab\u5b50\uff0c\u84ec\u84ec\u677e\u677e\u7684\u5806\u5728\u5934\u4e0a\u3002\u6211\u4e00\u89c1\u4ed6\u7684\u6a21\u6837\uff0c\u5c31\u5413\u4e86\u4e00\u8df3\uff0c\u5fc3\u60f3\uff1a\u2018\u4f60\u5976\u5976\u7684\uff0c\u4ece\u54ea\u91cc\u94bb\u51fa\u6765\u7684\u6076\u9b3c\uff1f\u2019\u53ea\u60f3\u5feb\u4e9b\u79bb\u5f00\u5ba2\u5e97\u56de\u5bb6\uff0c\u4f46\u8bf4\u4e5f\u5947\u602a\uff0c\u4e24\u53ea\u773c\u775b\u671b\u4f4f\u4e86\u5b83\uff0c\u7adf\u7136\u4e0d\u80fd\u907f\u5f00\u3002\u6211\u5fc3\u4e2d\u6697\u9a82\uff1a\u2018\u5927\u767d\u65e5\u89c1\u4e86\u9b3c\uff0c\u83ab\u975e\u8fd9\u4eba\u6709\u5996\u6cd5\uff1f\u2019\u201c\u53ea\u542c\u90a3\u4eba\u8bf4\u9053\uff1a\u2018\u52b3\u9a7e\uff0c\u638c\u67dc\u7684\uff0c\u8fd9\u513f\u54ea\u91cc\u6709\u533b\u751f\uff1f\u2019\u638c\u67dc\u7684\u5411\u6211\u4e00\u6307\uff0c\u8bf4\u9053\uff1a\u2018\u8fd9\u4e2a\u5c31\u662f\u533b\u751f\u3002\u2019\u6211\u53cc\u624b\u4e71\u6447\uff0c\u5fd9\u9053\uff1a\u2018\u4e0d\uff0c\u4e0d\u2026\u2026\u2019\u90a3\u4eba\u7b11\u9053\uff1a\u2018\u522b\u6015\uff0c\u6211\u4e0d\u4f1a\u5c06\u4f60\u716e\u719f\u6765\u5403\u4e86\u3002\u2019\u6211\u9053\uff1a\u2018\u6211\u2026\u2026\u6211\u2026\u2026\u2019\u90a3\u4eba\u6c89\u7740\u8138\u9053\uff1a\u2018\u82e5\u662f\u8981\u5403\u4f60\uff0c\u4e5f\u53ea\u751f\u5403\u3002\u2019\u6211\u66f4\u52a0\u6015\u4e86\uff0c\u90a3\u4eba\u5374\u54c8\u54c8\u5927\u7b11\u8d77\u6765\u3002\u6211\u8fd9\u624d\u77e5\u9053\u4ed6\u539f\u6765\u662f\u8bf4\u7b11\uff0c\u5fc3\u60f3\uff1a\u2018\u4f60\u8bb2\u7b11\u8bdd\uff0c\u4e5f\u5f97\u62e3\u62e3\u4eba\uff0c\u8001\u5b50\u662f\u7ed9\u4f60\u6d88\u9063\u7684\u4e48\uff1f\u2019\u4f46\u60f3\u662f\u8fd9\u4e48\u60f3\uff0c\u5634\u91cc\u5374\u54ea\u6562\u8bf4\u51fa\u6765\uff1f \u3000\u3000\u201c\u90a3\u4eba\u8bf4\u9053\uff1a\u2018\u638c\u67dc\u7684\uff0c\u7ed9\u6211\u4e24\u95f4\u5e72\u51c0\u7684\u4e0a\u623f\u3002\u6211\u5a18\u5b50\u8981\u751f\u4ea7\uff0c\u5feb\u53bb\u627e\u4e2a\u7a33\u5a46\u6765\u3002\u2019\u4ed6\u7709\u5934\u4e00\u76b1\uff0c\u8bf4\u9053\uff1a\u2018\u8def\u4e0a\u60ca\u52a8\u4e86\u80ce\u6c14\uff0c\u53ea\u6015\u662f\u96be\u4ea7\u3002\u533b\u751f\uff0c\u8bf7\u4f60\u522b\u8d70\u5f00\u3002\u2019\u638c\u67dc\u7684\u6240\u8bf4\u8981\u5728\u4ed6\u5e97\u91cc\u751f\u4ea7\uff0c\u5f04\u810f\u5c4b\u5b50\uff0c\u81ea\u7136\u8001\u5927\u4e0d\u613f\u610f\uff0c\u4f46\u89c1\u4e86\u4ed6\u8fd9\u526f\u51f6\u9738\u9738\u7684\u6a21\u6837\uff0c\u534a\u53e5\u4e5f\u4e0d\u6562\u591a\u8bf4\uff0c\u53ef\u662f\u9547\u4e0a\u505a\u7a33\u5a46\u7684\u5218\u5a46\u5a46\u524d\u51e0\u5929\u6b7b\u5566\uff0c\u638c\u67dc\u7684\u53ea\u5f97\u8ddf\u4ed6\u8bf4\u5b9e\u8bdd\u3002\u90a3\u4eba\u6a21\u6837\u66f4\u53ef\u6015\u4e86\uff0c\u6478\u51fa\u4e00\u952d\u5927\u94f6\uff0c\u629b\u5728\u684c\u4e0a\uff0c\u9053\uff1a\u2018\u638c\u67dc\u7684\uff0c\u52b3\u4f60\u9a7e\u5230\u522b\u5904\u53bb\u627e\u4e00\u4e2a\uff0c\u8d8a\u5feb\u8d8a\u597d\u3002\u2019\u6211\u5fc3\u60f3\uff1a\u2018\u600e\u4e48\u8fd9\u6279\u4eba\u4e00\u51fa\u624b\u90fd\u662f\u4e8c\u5341\u4e24\u94f6\u5b50\uff1f\u2019\u201c\u90a3\u6076\u9b3c\u6a21\u6837\u7684\u4eba\u7b49\u638c\u67dc\u5b89\u6392\u597d\u4e86\u623f\u95f4\uff0c\u4ece\u8f66\u4e2d\u6276\u4e0b\u4e00\u4e2a\u5973\u4eba\u6765\u3002\u8fd9\u5973\u4eba\u5168\u8eab\u88f9\u5728\u76ae\u88d8\u4e4b\u4e2d\uff0c\u53ea\u9732\u51fa\u4e86\u4e00\u5f20\u8138\u86cb\u3002\u8fd9\u4e00\u7537\u4e00\u5973\u54ea\uff0c\u6253\u4e2a\u6bd4\u65b9\uff0c\u90a3\u5c31\u662f\u8c82\u8749\u5ac1\u7ed9\u4e86\u5f20\u98de\u3002\u6211\u4e00\u89c1\u90a3\u5973\u5b50\u5982\u6b64\u7f8e\u6cd5\uff0c\u4e0d\u7981\u53c8\u5413\u4e86\u4e00\u8df3\uff0c\u5fc3\u4e0b\u7422\u78e8\uff1a\u2018\u8fd9\u5b9a\u662f\u4e00\u4f4d\u5b98\u5bb6\u7684\u5343\u91d1\u5c0f\u59d0\uff0c\u4e0d\u77e5\u600e\u5730\u88ab\u903c\u5ac1\u7ed9\u4e86\u8fd9\u4e2a\u6076\u9b3c\uff1f\u662f\u4e86\uff0c\u5b9a\u662f\u4ed6\u62a2\u6765\u505a\u62bc\u5be8\u592b\u4eba\u7684\u3002\u2019\u4e0d\u77e5\u600e\u7684\uff0c\u6211\u8d77\u4e86\u4e2a\u602a\u5ff5\u5934\uff1a\u2018\u8fd9\u4f4d\u592b\u4eba\u548c\u7530\u76f8\u516c\u624d\u662f\u4e00\u5bf9\u513f\uff0c\u8bf4\u4e0d\u5b9a\u662f\u8fd9\u6076\u9b3c\u62a2\u4e86\u7530\u76f8\u516c\u7684\uff0c\u4ed6\u4e24\u4eba\u624d\u7ed3\u4e0b\u4ec7\u6028\u3002\u2019\u201c\u6ca1\u8fc7\u4e2d\u5348\uff0c\u90a3\u4f4d\u592b\u4eba\u5c31\u989d\u5934\u5192\u6c57\uff0c\u54fc\u54fc\u5527\u5527\u7684\u53eb\u75db\u3002\u90a3\u6076\u9b3c\u7126\u6025\u5f97\u5f88\uff0c\u8981\u4eb2\u81ea\u53bb\u627e\u7a33\u5a46\uff0c\u90a3\u592b\u4eba\u5374\u53c8\u62c9\u7740\u4ed6\u624b\uff0c\u4e0d\u8bb8\u4ed6\u8d70\u5f00\u3002\u5230\u672a\u724c\u65f6\u5206\uff0c\u5c0f\u5b69\u513f\u8981\u51fa\u6765\uff0c\u5b9e\u5728\u7b49\u4e0d\u5f97\u4e86\u3002\u90a3\u6076\u9b3c\u8981\u6211\u63a5\u751f\uff0c\u6211\u81ea\u7136\u4e0d\u80af\u3002\u4f60\u4eec\u60f3\uff0c\u6211\u4e00\u4e2a\u5802\u5802\u7537\u5b50\u6c49\uff0c\u7ed9\u5987\u9053\u4eba\u5bb6\u63a5\u751f\u600e\u4e48\u6210\uff1f\u90a3\u662f\u4e00\u5343\u4e00\u4e07\u4e2a\u6666\u6c14\uff0c\u8fd9\u79cd\u4e8b\u4e00\u505a\uff0c\u8fd9\u4e00\u751f\u4e00\u4e16\u5c31\u6ce8\u5b9a\u5012\u8db3\u4e86\u9709\u3002 \u3000\u3000\u201c\u90a3\u6076\u9b3c\u9053\uff1a\u2018\u4f60\u63a5\u561b\uff0c\u8fd9\u91cc\u6709\u4e8c\u767e\u4e24\u94f6\u5b50\u3002\u4e0d\u63a5\u561b\uff0c\u90a3\u4e5f\u7531\u4f60\u3002\u2019\u4ed6\u4f38\u624b\u4e00\u62cd\uff0c\u5c06\u65b9\u684c\u7684\u89d2\u513f\u62cd\u4e0b\u4e86\u4e00\u5757\u3002\u6211\u60f3\uff1a \u3000\u3000\u2018\u6027\u547d\u8981\u7d27\u3002\u518d\u8bf4\uff0c\u8fd9\u4e8c\u767e\u4e24\u94f6\u5b50\uff0c\u505a\u5341\u5e74\u8dcc\u6253\u533b\u751f\u4e5f\u8d5a\u4e0d\u5230\uff0c\u5012\u9709\u4e00\u6b21\u53c8\u6709\u4f55\u59a8\uff1f\u2019\u5f53\u4e0b\u7ed9\u90a3\u592b\u4eba\u63a5\u4e0b\u4e00\u4e2a\u767d\u767d\u80d6\u80d6\u7684\u5c0f\u5b50\u3002 \u3000\u3000\u201c\u8fd9\u5c0f\u5b50\u54ed\u5f97\u597d\u54cd\uff0c\u8138\u4e0a\u5168\u662f\u6bdb\uff0c\u773c\u775b\u7741\u5f97\u5927\u5927\u7684\uff0c\u751f\u4e0b\u6765\u5c31\u662f\u4e00\u526f\u51f6\u76f8\uff0c\u5012\u771f\u50cf\u4ed6\u7239\uff0c\u65e5\u540e\u957f\u5927\u4e86\u5341\u4e5d\u4e5f\u662f\u4e2a\u6b79\u4eba\u3002 \u3000\u3000\u201c\u90a3\u6076\u9b3c\u5f88\u662f\u5f00\u5fc3\uff0c\u5f53\u771f\u5c31\u6367\u7ed9\u6211\u5341\u53ea\u4e8c\u5341\u4e24\u7684\u5927\u5143\u5b9d\u3002 \u3000\u3000\u90a3\u592b\u4eba\u53c8\u7ed9\u4e86\u6211\u4e00\u952d\u9ec4\u91d1\uff0c\u603b\u503c\u5f97\u516b\u4e5d\u5341\u4e24\u94f6\u5b50\u3002\u90a3\u6076\u9b3c\u53c8\u6367\u51fa\u4e00\u76d8\u94f6\u5b50\uff0c\u5ba2\u5e97\u4e2d\u4ece\u638c\u67dc\u5230\u7076\u4e0b\u70e7\u706b\u7684\uff0c\u6bcf\u4eba\u90fd\u9001\u4e86\u5341\u4e24\u3002\u8fd9\u4e00\u4e0b\u5927\u4f19\u513f\u53ef\u5c31\u4e50\u5f00\u5566\u3002\u90a3\u6076\u9b3c\u62c9\u7740\u5927\u4f19\u513f\u559d\u9152\uff0c\u8fde\u6253\u6742\u7684\u3001\u626b\u5730\u7684\u5c0f\u53ae\uff0c\u90fd\u6559\u4e0a\u4e86\u684c\u3002\u5927\u5bb6\u7ba1\u4ed6\u53eb\u80e1\u5927\u7237\u3002\u4ed6\u8bf4\u9053\uff1a\u2018\u6211\u59d3\u80e1\uff0c\u751f\u5e73\u53ea\u8981\u9047\u5230\u505a\u574f\u4e8b\u7684\uff0c\u7acb\u65f6\u4e00\u5200\u6740\u4e86\uff0c\u6240\u4ee5\u540d\u5b57\u53eb\u4f5c\u80e1\u4e00\u5200\u3002\u4f60\u4eec\u522b\u5927\u7237\u957f\u5927\u7237\u77ed\u7684\uff0c\u6211\u4e5f\u662f\u7a77\u6c49\u51fa\u8eab\u3002\u6253\u4ece\u6076\u9738\u90a3\u91cc\u62a2\u4e86\u4e9b\u94b1\u8d22\uff0c\u7b97\u4ec0\u4e48\u5927\u7237\uff1f\u53eb\u6211\u80e1\u5927\u54e5\u5f97\u5566\uff01\u2019\u201c\u6211\u65e9\u77e5\u4ed6\u4e0d\u662f\u597d\u4eba\uff0c\u4ed6\u679c\u7136\u81ea\u5df1\u8bf4\u4e86\u51fa\u6765\u3002\u5927\u4f19\u4e0d\u6562\u53eb\u4ed6\u2018\u5927\u54e5\u2019\uff0c\u4ed6\u5374\u903c\u7740\u975e\u53eb\u4e0d\u53ef\u3002\u540e\u6765\u5927\u4f19\u513f\u9152\u559d\u591a\u4e86\uff0c\u5927\u4e86\u80c6\u5b50\uff0c\u5c31\u8ddf\u4ed6\u5927\u54e5\u957f\u3001\u5927\u54e5\u77ed\u8d77\u6765\u3002\u8fd9\u4e00\u665a\u4ed6\u4e0d\u653e\u6211\u56de\u5bb6\uff0c\u8981\u6211\u966a\u4ed6\u559d\u9152\u3002\u559d\u5230\u4e8c\u66f4\u65f6\u5206\uff0c\u522b\u4eba\u90fd\u9189\u5012\u4e86\uff0c\u53ea\u6709\u6211\u9152\u91cf\u597d\uff0c\u8fd8\u966a\u7740\u4ed6\u4e00\u7897\u4e00\u7897\u7684\u704c\u3002\u4ed6\u8d8a\u559d\u5174\u81f4\u8d8a\u9ad8\uff0c\u8fdb\u623f\u53bb\u62b1\u4e86\u513f\u5b50\u51fa\u6765\uff0c\u7528\u6307\u5934\u8638\u4e86\u9152\u7ed9\u4ed6\u542e\u3002\u8fd9\u5c0f\u5b50\u751f\u4e0b\u4e0d\u5230\u4e00\u5929\uff0c\u542e\u7740\u70c8\u9152\u975e\u4f46\u4e0d\u54ed\uff0c\u53cd\u800c\u8210\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u771f\u662f\u5929\u751f\u7684\u9152\u9b3c\u3002 \u3000\u3000\u201c\u5c31\u5728\u90a3\u65f6\uff0c\u5357\u8fb9\u5ffd\u7136\u4f20\u6765\u9a6c\u8e44\u58f0\u54cd\uff0c\u4e00\u5171\u6709\u4e8c\u4e09\u5341\u5339\u9a6c\uff0c\u5f88\u5feb\u7684\u5954\u8fd1\u6765\uff0c\u5230\u4e86\u5e97\u95e8\u53e3\u5c31\u6b62\u4f4f\u4e86\u3002\u8ddf\u7740\u5c31\u542c\u5f97\u62cd\u95e8\u58f0\u54cd\u3002 \u3000\u3000\u638c\u67dc\u7684\u65e9\u9189\u5f97\u7cca\u6d82\u5566\uff0c\u8dcc\u8dcc\u649e\u649e\u7684\u53bb\u5f00\u95e8\u3002\u95e8\u4e00\u6253\u5f00\uff0c\u8fdb\u6765\u4e86\u4e8c\u4e09\u5341\u6761\u6c49\u5b50\uff0c\u4e2a\u4e2a\u8eab\u4e0a\u5e26\u7740\u5175\u5203\u3002\u8fd9\u4e9b\u4eba\u5728\u95e8\u53e3\u6392\u6210\u4e00\u5217\uff0c\u9ed8\u4e0d\u4f5c\u58f0\u3002\u53ea\u6709\u5176\u4e2d\u4e00\u4eba\u8d70\u4e0a\u524d\u6765\uff0c\u5728\u4e00\u5f20\u684c\u65c1\u5750\u4e0b\uff0c\u4ece\u80cc\u4e0a\u89e3\u4e0b\u4e00\u4e2a\u9ec4\u5e03\u5305\u88b1\uff0c\u653e\u5728\u684c\u4e0a\u3002\u70db\u5149\u4e0b\u770b\u5f97\u5206\u660e\uff0c\u5305\u88b1\u4e0a\u7528\u9ed1\u4e1d\u7ebf\u7ee3\u7740\u4e03\u4e2a\u5b57\uff1a\u2018\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u2019\u3002\u201d \u3000\u3000\u4f17\u4eba\u542c\u5230\u8fd9\u91cc\uff0c\u90fd\u62ac\u8d77\u5934\u6765\uff0c\u671b\u4e86\u671b\u5385\u4e2d\u5bf9\u8054\u4e0a\u201c\u5927\u8a00\u5929\u4e0b\u65e0\u654c\u624b\u201d\u548c\u201c\u82d7\u4eba\u51e4\u201d\u7b49\u5b57\u3002 \u3000\u3000\u5b9d\u6811\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u8fd9\u4e03\u5b57\u5916\u53f7\uff0c\u76f4\u5230\u73b0\u4e0b\uff0c\u6211\u8fd8\u662f\u89c9\u5f97\u6709\u70b9\u513f\u8fc7\u4e8e\u76ee\u4e2d\u65e0\u4eba\u3002\u90a3\u5929\u665a\u4e0a\u89c1\u5230\uff0c\u81ea\u7136\u5341\u5206\u60ca\u8bb6\u3002\u53ea\u89c1\u4ed6\u8eab\u6750\u6781\u9ad8\u6781\u7626\uff0c\u5b9b\u4f3c\u4e00\u6761\u7af9\u7bd9\uff0c\u9762\u76ae\u8721\u9ec4\uff0c\u6ee1\u8138\u75c5\u5bb9\uff0c\u4e00\u53cc\u7834\u84b2\u6247\u822c\u7684\u5927\u624b\uff0c\u644a\u7740\u653e\u5728\u684c\u4e0a\u3002\u6211\u8bf4\u4ed6\u8fd9\u5bf9\u624b\u50cf\u7834\u84b2\u6247\uff0c\u56e0\u4e3a\u624b\u638c\u7626\u5f97\u53ea\u5269\u4e0b\u4e00\u6839\u6839\u9aa8\u5934\u3002\u6211\u5f53\u65f6\u81ea\u7136\u4e0d\u77e5\u9053\u4ed6\u662f\u8c01\uff0c\u5230\u540e\u6765\u624d\u77e5\u662f\u91d1\u9762\u4f5b\u82d7\u4eba\u51e4\u82d7\u5927\u4fa0\u3002 \u3000\u3000\u201c\u90a3\u80e1\u4e00\u5200\u81ea\u987e\u81ea\u9017\u5f04\u5b69\u5b50\uff0c\u7adf\u4f3c\u6ca1\u77a7\u89c1\u8fd9\u8bb8\u591a\u4eba\u8fdb\u6765\u3002 \u3000\u3000\u82d7\u5927\u4fa0\u4e5f\u662f\u4e00\u53e5\u8bdd\u4e0d\u8bf4\uff0c\u81ea\u6709\u4ed6\u7684\u4ece\u4eba\u659f\u4e0a\u9152\u6765\u3002\u90a3\u51e0\u5341\u4e2a\u6c49\u5b50\u77aa\u7740\u773c\u775b\u77a7\u80e1\u4e00\u5200\u3002\u4ed6\u5374\u53ea\u7ba1\u8638\u9152\u7ed9\u5b69\u5b50\u542e\u3002\u4ed6\u8638\u4e00\u6ef4\u9152\uff0c\u4ef0\u8116\u5b50\u559d\u4e00\u7897\uff0c\u7237\u513f\u4fe9\u7adf\u662f\u529d\u4e0a\u4e86\u9152\u3002 \u3000\u3000\u201c\u6211\u5fc3\u4e2d\u6026\u6026\u4e71\u8df3\uff0c\u53ea\u60f3\u5feb\u5feb\u79bb\u5f00\u8fd9\u662f\u975e\u4e4b\u5730\uff0c\u53ef\u662f\u53c8\u600e\u6562\u79fb\u52a8\u4e00\u6b65\uff1f\u90a3\u65f6\u5019\u554a\uff0c\u53ea\u8981\u8c01\u7a0d\u7a0d\u52a8\u4e00\u52a8\uff0c\u51e0\u5341\u628a\u5200\u5251\u7acb\u65f6\u5c31\u780d\u5c06\u4e0b\u6765\uff0c\u5c31\u7b97\u4e0d\u662f\u5bf9\u51c6\u4e86\u5f80\u6211\u8eab\u4e0a\u62db\u547c\uff0c\u53ea\u987b\u6328\u7740\u4e00\u70b9\u8fb9\u513f\uff0c\u90a3\u4e5f\u975e\u91cd\u4f24\u4e0d\u53ef\u3002 \u3000\u3000\u201c\u80e1\u4e00\u5200\u548c\u82d7\u5927\u4fa0\u95f7\u58f0\u4e0d\u54cd\u7684\uff0c\u5404\u81ea\u559d\u4e86\u5341\u591a\u7897\u9152\uff0c\u8c01\u4e5f\u4e0d\u5411\u8c01\u77a7\u4e00\u773c\u3002\u5ffd\u7136\u623f\u4e2d\u592b\u4eba\u9192\u4e86\uff0c\u53eb\u4e86\u58f0\uff1a\u2018\u5927\u54e5\uff01\u2019\u90a3\u5b69\u5b50\u542c\u5230\u6bcd\u4eb2\u58f0\u97f3\uff0c\u54c7\u7684\u4e00\u58f0\uff0c\u5927\u54ed\u8d77\u6765\u3002\u80e1\u4e00\u5200\u624b\u4e00\u98a4\uff0c\u545b\u5577\u4e00\u58f0\uff0c\u9152\u7897\u843d\u5728\u5730\u4e0b\uff0c\u8dcc\u5f97\u7c89\u788e\u3002\u4ed6\u8138\u8272\u7acb\u53d8\uff0c\u62b1\u7740\u5b69\u5b50\u7ad9\u8d77\u8eab\u6765\u3002\u82d7\u5927\u4fa0\u2018\u563f\u3001\u563f\u3001\u563f\u2019\u7684\u51b7\u7b11\u4e09\u58f0\uff0c\u8f6c\u8eab\u51fa\u95e8\u3002\u4f17\u4eba\u4e00\u9f50\u8ddf\u51fa\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u9a6c\u8e44\u58f0\u6e10\u6e10\u8fdc\u53bb\u3002\u6211\u53ea\u9053\u4e00\u573a\u6076\u6597\u4e00\u5b9a\u662f\u96be\u514d\u7684\u4e86\uff0c\u54ea\u77e5\u9053\u5b69\u5b50\u8fd9\u4e48\u4e00\u54ed\uff0c\u82d7\u5927\u4fa0\u5c45\u7136\u7acb\u523b\u5c31\u8d70\u3002\u6211\u548c\u638c\u67dc\u3001\u4f19\u8ba1\u4eec\u9762\u9762\u76f8\u89d1\uff0c\u6478\u4e0d\u7740\u534a\u70b9\u5934\u8111\u3002 \u3000\u3000\u201c\u80e1\u4e00\u5200\u62b1\u7740\u5b69\u5b50\u8d70\u8fdb\u623f\u53bb\uff0c\u90a3\u623f\u95f4\u7684\u677f\u58c1\u6781\u8584\uff0c\u53ea\u542c\u592b\u4eba\u95ee\u9053\uff1a\u2018\u5927\u54e5\uff0c\u662f\u8c01\u6765\u4e86\u554a\uff1f\u2019\u80e1\u4e00\u5200\u9053\uff1a\u2018\u51e0\u4e2a\u6bdb\u8d3c\uff0c\u4f60\u597d\u597d\u7761\u7f62\uff01\u522b\u62c5\u5fc3\u3002\u2019\u592b\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u4f4e\u58f0\u9053\uff1a\u2018\u4e0d\u7528\u9a97\u6211\uff0c\u662f\u91d1\u9762\u4f5b\u6765\u5566\u3002\u2019\u80e1\u4e00\u5200\u9053\uff1a\u2018\u4e0d\u662f\u7684\uff0c\u4f60\u522b\u778e\u7591\u5fc3\u3002\u2019\u592b\u4eba\u9053\uff1a\u2018\u90a3\u4f60\u5e72\u4e48\u8bf4\u8bdd\u58f0\u97f3\u53d1\u6296\uff1f\u4f60\u4ece\u6765\u4e0d\u662f\u8fd9\u6837\u7684\u3002\u2019\u201c\u80e1\u4e00\u5200\u4e0d\u8bed\uff0c\u9694\u4e86\u7247\u523b\u8bf4\u9053\uff1a\u2018\u4f60\u731c\u5230\u5c31\u7b97\u5566\u3002\u6211\u4e0d\u4f1a\u6015\u4ed6\u7684\u3002\u2019\u592b\u4eba\u9053\uff1a\u2018\u5927\u54e5\uff0c\u4f60\u5343\u4e07\u522b\u4e3a\u4e86\u6211\uff0c\u4e3a\u4e86\u5b69\u5b50\u62c5\u5fc3\u3002 \u3000\u3000\u4f60\u5fc3\u91cc\u4e00\u6015\uff0c\u5c31\u6253\u4ed6\u4e0d\u8fc7\u4e86\u3002\u2019\u80e1\u4e00\u5200\u53f9\u4e86\u53e3\u957f\u6c14\uff0c\u9053\uff1a\u2018\u4e5f\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\uff0c\u6211\u4ece\u6765\u5929\u4e0d\u6015\u5730\u4e0d\u6015\uff0c\u4eca\u665a\u62b1\u7740\u5b69\u5b50\uff0c\u89c1\u5230\u91d1\u9762\u4f5b\u8fdb\u6765\uff0c\u4ed6\u628a\u5305\u88b1\u5f80\u684c\u4e0a\u4e00\u653e\uff0c\u773c\u89d2\u5411\u5b69\u5b50\u4e00\u6643\uff0c\u6211\u5c31\u5168\u8eab\u51fa\u4e86\u4e00\u9635\u51b7\u6c57\u3002\u59b9\u5b50\uff0c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u6015\u91d1\u9762\u4f5b\u3002\u2019\u592b\u4eba\u9053\uff1a\u2018\u4f60\u4e0d\u662f\u81ea\u5df1\u6015\u4ed6\uff0c\u662f\u6015\u4ed6\u5bb3\u6211\uff0c\u6015\u4ed6\u5bb3\u54b1\u4eec\u7684\u5b69\u5b50\u3002\u2019\u80e1\u4e00\u5200\u9053\uff1a\u2018\u542c\u8bf4\u91d1\u9762\u4f5b\u884c\u4fa0\u4ed7\u4e49\uff0c\u6c5f\u6e56\u4e0a\u90fd\u53eb\u4ed6\u82d7\u5927\u4fa0\uff0c\u603b\u4e0d\u4f1a\u5bb3\u5973\u4eba\u5b69\u5b50\u5427\uff1f\u2019\u4ed6\u8bf4\u8fd9\u51e0\u53e5\u8bdd\u65f6\u58f0\u97f3\u66f4\u52a0\u53d1\u98a4\uff0c\u663e\u662f\u5fc3\u91cc\u534a\u5206\u513f\u4e5f\u62ff\u4e0d\u51c6\u3002\u6211\u542c\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5ffd\u7136\u53ef\u601c\u4ed6\u8d77\u6765\uff0c\u5fc3\u60f3\uff1a\u2018\u8fd9\u4eba\u8138\u4e0a\u4e00\u526f\u51f6\u50cf\uff0c\u539f\u6765\u5fc3\u91cc\u5374\u5bb3\u6015\u5f97\u7d27\u3002\u2019\u201c\u53ea\u542c\u592b\u4eba\u8f7b\u58f0\u9053\uff1a\u2018\u5927\u54e5\uff0c\u4f60\u62b1\u4e86\u5b69\u5b50\uff0c\u56de\u5bb6\u53bb\u5427\u3002\u7b49\u6211\u517b\u597d\u8eab\u5b50\uff0c\u5230\u5173\u5916\u5bfb\u4f60\u3002\u2019\u201c\u80e1\u4e00\u5200\u9053\uff1a\u2018\u5509\uff0c\u90a3\u600e\u4e48\u6210\uff1f\u8981\u6b7b\uff0c\u54b1\u4fe9\u4e5f\u6b7b\u5728\u4e00\u5757\u3002\u2019\u592b\u4eba\u53f9\u9053\uff1a\u2018\u65e9\u77e5\u5982\u6b64\uff0c\u5f53\u5e74\u6211\u4e0d\u963b\u4f60\u5357\u6765\u8ddf\u91d1\u9762\u4f5b\u6311\u6218\u5012\u597d\u3002 \u3000\u3000\u90a3\u65f6\u4f60\u5fc3\u65e0\u7275\u6302\uff0c\u51c6\u80fd\u80dc\u4ed6\u3002\u2019\u80e1\u4e00\u5200\u7b11\u9053\uff1a\u2018\u4eca\u65e5\u76f8\u9022\uff0c\u4e5f\u672a\u5fc5\u5c31\u8d25\u5728\u4ed6\u624b\u91cc\u3002\u4ed6\u90a3\u4e2a\u201c\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u201d\u7684\u9ec4\u5305\u88b1\uff0c\u53ea\u6015\u5f97\u6362\u6362\u4e3b\u513f\u3002\u2019\u4ed6\u867d\u7136\u5e26\u7b11\u800c\u8bf4\uff0c\u4f46\u58f0\u97f3\u603b\u662f\u53d1\u98a4\uff0c\u5373\u662f\u9694\u4e86\u4e00\u9053\u677f\u58c1\uff0c\u4ecd\u7136\u542c\u5f97\u51fa\u6765\u3002 \u3000\u3000\u201c\u592b\u4eba\u5ffd\u9053\uff1a\u2018\u5927\u54e5\uff0c\u4f60\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\u3002\u2019\u80e1\u4e00\u5200\u9053\uff1a\u2018\u4ec0\u4e48\uff1f\u2019\u592b\u4eba\u9053\uff1a\u2018\u54b1\u4eec\u628a\u4e00\u5207\u8ddf\u91d1\u9762\u4f5b\u660e\u8bf4\u4e86\uff0c\u77a7\u4ed6\u600e\u4e48\u8bf4\u3002\u4ed6\u53f7\u79f0\u5927\u4fa0\uff0c\u96be\u9053\u4e0d\u8bb2\u9053\u7406\uff1f\u2019\u201c\u80e1\u4e00\u5200\u9053\uff1a\u2018\u6211\u5728\u5916\u9762\u4e00\u8fb9\u559d\u9152\uff0c\u4e00\u8fb9\u5fc3\u4e2d\u7422\u78e8\uff0c\u5341\u51e0\u6761\u53ef\u884c\u7684\u8def\u5b50\u90fd\u7ec6\u7ec6\u60f3\u8fc7\u4e86\u3002\u4f60\u521a\u751f\u4e0b\u5b69\u5b50\uff0c\u600e\u80fd\u51fa\u5916\uff1f\u6211\u81ea\u5df1\u53bb\uff0c\u4e00\u8bf4\u5c31\u50f5\u3002\u5018\u82e5\u6709\u4e2a\u4eba\u80fd\u4f7f\uff0c\u4f60\u7684\u4e3b\u610f\u5012\u4e5f\u884c\u5f97\u3002\u2019\u592b\u4eba\u60f3\u4e86\u4e00\u4f1a\uff0c\u9053\uff1a\u2018\u90a3\u4e2a\u533b\u751f\u5012\u633a\u80fd\u5e72\u7684\uff0c\u53e3\u9f7f\u4f36\u4fd0\uff0c\u4e0d\u5982\u70e6\u4ed6\u4e00\u884c\u3002\u2019\u80e1\u4e00\u5200\u9053\uff1a\u2018\u6b64\u4eba\u8d2a\u8d22\uff0c\u672a\u5fc5\u53ef\u9760\u3002\u2019\u592b\u4eba\u9053\uff1a\u2018\u54b1\u4eec\u91cd\u91cd\u916c\u8c22\u4ed6\u5c31\u662f\u3002\u2019\u54c8\u54c8\uff0c\u8001\u548c\u5c1a\u5e74\u8f7b\u4e4b\u65f6\uff0c\u786e\u662f\u597d\u9152\u8d2a\u8d22\uff0c\u8bf4\u51fa\u6765\u4e5f\u4e0d\u6015\u5404\u4f4d\u7b11\u8bdd\uff0c\u6211\u4e00\u542c\u2018\u91cd\u91cd\u916c\u8c22\u2019\u56db\u5b57\uff0c\u65e9\u5c31\u6253\u5b9a\u4e86\u4e3b\u610f\uff1a\u2018\u5c31\u662f\u6c34\u91cc\u706b\u91cc\uff0c\u4e5f\u8981\u4e3a\u4ed6\u8d70\u4e00\u906d\u3002\u2019\u201c\u4ed6\u4eec\u592b\u59bb\u4fe9\u4f4e\u58f0\u5546\u91cf\u4e86\u51e0\u53e5\uff0c\u80e1\u4e00\u5200\u5c31\u51fa\u6765\u53eb\u6211\u8fdb\u623f\uff0c\u8bf4\u9053\uff1a\u2018\u660e\u65e5\u4e00\u65e9\uff0c\u6709\u4eba\u9001\u4fe1\u6765\u3002\u76f8\u70e6\u4f60\u8ddf\u968f\u4ed6\u524d\u53bb\uff0c\u9001\u6211\u7684\u56de\u4fe1\u7ed9\u91d1\u9762\u4f5b\u82d7\u5927\u4fa0\uff0c\u5c31\u662f\u521a\u624d\u6765\u559d\u9152\u7684\u90a3\u4f4d\u9ec4\u8138\u5927\u7237\u3002\u2019\u6211\u60f3\u6b64\u4e8b\u4f55\u96be\uff0c\u5f53\u4e0b\u6ee1\u53e3\u7b54\u5e94\u3002 \u3000\u3000\u201c\u6b21\u65e5\u5927\u6e05\u65e9\uff0c\u679c\u7136\u4e00\u4e2a\u6c49\u5b50\u9a91\u9a6c\u9001\u4e86\u4e00\u5c01\u4fe1\u6765\u7ed9\u80e1\u4e00\u5200\u3002\u6211\u542c\u592b\u4eba\u5ff5\u4fe1\uff0c\u539f\u6765\u662f\u82d7\u5927\u4fa0\u7ea6\u4ed6\u6bd4\u6b66\u7684\uff0c\u8981\u4ed6\u81ea\u62e9\u65e5\u5b50\u5730\u65b9\u3002\u80e1\u4e00\u5200\u5199\u4e86\u4e00\u5c01\u56de\u4fe1\u4ea4\u7ed9\u6211\u3002\u6211\u5411\u5ba2\u5e97\u638c\u67dc\u501f\u4e86\u5339\u9a6c\uff0c\u8ddf\u4e86\u90a3\u6c49\u5b50\u524d\u53bb\u3002\u5411\u5357\u8d70\u4e86\u4e09\u5341\u591a\u91cc\uff0c\u90a3\u6c49\u5b50\u9886\u6211\u8fdb\u4e86\u4e00\u5ea7\u5927\u5c4b\u3002\u82d7\u5927\u4fa0\u3001\u8303\u5e2e\u4e3b\u3001\u7530\u76f8\u516c\u90fd\u5728\u91cc\u9762\uff0c\u6b64\u5916\u8fd8\u6709\u56db\u4e94\u5341\u4eba\uff0c\u7537\u7684\u5973\u7684\u3001\u548c\u5c1a\u9053\u58eb\u90fd\u6709\u3002 \u3000\u3000\u201c\u7530\u76f8\u516c\u770b\u4e86\u90a3\u4fe1\uff0c\u8bf4\u9053\uff1a\u2018\u4e0d\u5fc5\u53e6\u7ea6\u65e5\u5b50\u4e86\uff0c\u6211\u4eec\u660e\u65e5\u51c6\u5230\u3002\u2019\u6211\u9053\uff1a\u2018\u76f8\u516c\u8fd8\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u2019\u7530\u76f8\u516c\u9053\uff1a\u2018\u4f60\u53bb\u8ddf\u80e1\u4e00\u5200\u8bf4\uff0c\u53eb\u4ed6\u5148\u4e70\u5b9a\u4e09\u53e3\u68fa\u6750\uff0c\u4e24\u53e3\u5927\u7684\uff0c\u4e00\u53e3\u5c0f\u7684\uff0c\u514d\u5f97\u5927\u7237\u4eec\u5230\u5934\u6765\u7834\u8d39\u3002\u2019\u6211\u56de\u5230\u5ba2\u5e97\uff0c\u628a\u8fd9\u51e0\u53e5\u8bdd\u5bf9\u80e1\u4e00\u5200\u592b\u5987\u8bf4\u4e86\uff0c\u5fc3\u60f3\u4ed6\u4eec\u5fc5\u5b9a\u7834\u53e3\u5927\u9a82\uff0c\u54ea\u77e5\u4ed6\u4eec\u53ea\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002\u4e24\u4e2a\u4eba\u8f6e\u6d41\u62b1\u7740\u5b69\u5b50\uff0c\u53ea\u7ba1\u4eb2\u4ed6\u75bc\u4ed6\uff0c\u597d\u4f3c\u81ea\u77e5\u6b7b\u671f\u5df2\u8fd1\uff0c\u591a\u62b1\u4e00\u523b\u4e5f\u662f\u597d\u7684\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u665a\u6211\u5c3d\u505a\u7586\u68a6\uff0c\u4e00\u4f1a\u513f\u68a6\u89c1\u80e1\u4e00\u5200\u5c06\u82d7\u5927\u4fa0\u6740\u4e86\uff0c\u4e00\u4f1a\u513f\u68a6\u89c1\u82d7\u5927\u4fa0\u5c06\u80e1\u4e00\u5200\u6740\u4e86\uff0c\u4e00\u4f1a\u513f\u53c8\u68a6\u89c1\u8fd9\u4e24\u4eba\u628a\u6211\u6740\u4e86\u3002\u7761\u5230\u534a\u591c\uff0c\u5ffd\u7136\u7ed9\u51e0\u4e0b\u602a\u58f0\u5435\u9192\uff0c\u4e00\u542c\u539f\u6765\u662f\u9694\u58c1\u623f\u91cc\u80e1\u4e00\u5200\u5728\u54ed\u6ce3\u3002 \u3000\u3000\u201c\u6211\u597d\u751f\u5947\u602a\uff1b\u5fc3\u60f3\uff1a\u2018\u77a7\u4ed6\u4e5f\u662f\u4e2a\u54cd\u5f53\u5f53\u7684\u6c49\u5b50\uff0c\u5927\u4e08\u592b\u6b7b\u5c31\u6b7b\u4e86\uff0c\u4e8b\u5230\u4e34\u5934\uff0c\u8fd8\u54ed\u4e9b\u4ec0\u4e48\uff1f\u600e\u5730\u5982\u6b64\u8113\u5305\uff1f\u2019\u5374\u542c\u4ed6\u545c\u54bd\u7740\u9053\uff1a\u2018\u5b69\u5b50\uff0c\u4f60\u751f\u4e0b\u4e09\u5929\uff0c\u4fbf\u6210\u4e86\u6ca1\u7239\u6ca1\u5a18\u7684\u5b64\u513f\uff0c\u5c06\u6765\u6709\u8c01\u75bc\u4f60\uff1f\u4f60\u997f\u4e86\u51b7\u4e86\uff0c\u8c01\u6765\u7ba1\u4f60\uff1f\u4f60\u53d7\u4eba\u6b3a\u4fae\uff0c\u8c01\u6765\u5e2e\u4f60\uff1f\u2019\u201c\u8d77\u521d\u6211\u8fd8\u9a82\u4ed6\u8113\u5305\uff0c\u542c\u5230\u540e\u6765\uff0c\u5374\u4e0d\u7981\u5fc3\u91cc\u9178\u4e86\uff0c\u6697\u60f3\uff1a \u3000\u3000\u8fd9\u4e48\u51f6\u6076\u7c97\u8c6a\u7684\u4e00\u6761\u731b\u6c49\u5b50\uff0c\u5bf9\u5c0f\u5b69\u513f\u7adf\u7136\u5982\u6b64\u7231\u601c\u3002\u4ed6\u54ed\u4e86\u4e00\u9635\uff0c\u4ed6\u592b\u4eba\u5ffd\u9053\uff1a\u2018\u5927\u54e5\uff0c\u4f60\u4e0d\u7528\u4f24\u5fc3\u3002\u82e5\u662f\u4f60\u5f53\u771f\u547d\u4e27\u91d1\u9762\u4f5b\u4e4b\u624b\uff0c\u6211\u51b3\u5b9a\u4e0d\u6b7b\uff0c\u597d\u597d\u5c06\u5b69\u5b50\u5e26\u5927\u5c31\u662f\u3002\u2019\u80e1\u4e00\u5200\u5927\u559c\uff0c\u9053\uff1a\u2018\u59b9\u5b50\uff0c\u6211\u6700\u653e\u5fc3\u4e0d\u4e0b\u7684\u5c31\u662f\u8fd9\u4ef6\u4e8b\u3002\u82e5\u662f\u6211\u4e0d\u5e78\u6b7b\u4e86\uff0c\u4f60\u600e\u80fd\u6d3b\u7740\uff1f\u73b0\u4e0b\u4f60\u80af\u6bc5\u7136\u6311\u8d77\u8fd9\u526f\u91cd\u62c5\uff0c\u6211\u5c31\u6ca1\u4ec0\u4e48\u62c5\u5fe7\u7684\u4e86\u3002\u54c8\u54c8\uff0c\u4eba\u751f\u81ea\u53e4\u8c01\u65e0\u6b7b\uff1f\u8ddf\u8fd9\u4f4d\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\u75db\u75db\u5feb\u5feb\u7684\u5927\u6253\u4e00\u573a\uff0c\u90a3\u4e5f\u662f\u767e\u5e74\u96be\u9022\u7684\u5947\u9047\u554a\uff01\u2019\u201c\u6211\u542c\u4e86\u8fd9\u756a\u8bdd\uff0c\u89c9\u5f97\u4ed6\u771f\u662f\u4e2a\u5947\u4eba\uff0c\u53ea\u542c\u4ed6\u5927\u7b11\u4e86\u4e00\u4f1a\uff0c\u5ffd\u53c8\u53f9\u6c14\u9053\uff1a\u2018\u59b9\u5b50\uff0c\u5200\u5251\u4e00\u5272\uff0c\u9888\u4e2d\u4e00\u75db\uff0c\u4ec0\u4e48\u90fd\u5b8c\u4e8b\u5566\u3002 \u3000\u3000\u6b7b\u662f\u5f88\u5bb9\u6613\u7684\uff0c\u4f60\u6d3b\u7740\u53ef\u5c31\u96be\u4e86\u3002\u6211\u6b7b\u4e86\u4e4b\u540e\uff0c\u65e0\u77e5\u65e0\u89c9\uff0c\u4f60\u5374\u8981\u65e5\u65e5\u591c\u591c\u7684\u4f24\u5fc3\u96be\u8fc7\u3002\u5509\uff0c\u6211\u5fc3\u4e2d\u771f\u662f\u820d\u4e0d\u5f97\u4f60\u3002\u2019\u592b\u4eba\u9053\uff1a\u2018\u6211\u77a7\u7740\u5b69\u5b50\uff0c\u5c31\u5982\u77a7\u7740\u4f60\u4e00\u822c\u3002\u7b49\u4ed6\u957f\u5927\u4e86\uff0c\u6211\u53eb\u4ed6\u5b66\u4f60\u7684\u6837\uff0c\u4ec0\u4e48\u8d2a\u5b98\u6c61\u540f\u3001\u571f\u8c6a\u6076\u9738\uff0c\u89c1\u4e86\u5c31\u662f\u4e00\u5200\u3002\u2019\u80e1\u4e00\u5200\u9053\uff1a\u2018\u6211\u751f\u5e73\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u4f60\u89c9\u5f97\u90fd\u6ca1\u6709\u9519\uff1f\u8981\u5b69\u5b50\u5168\u5b66\u6211\u7684\u6837\uff1f\u2019\u592b\u4eba\u9053\uff1a\u2018\u90fd\u6ca1\u6709\u9519\uff01\u8981\u5b69\u5b50\u5168\u5b66\u4f60\u7684\u6837\uff01\u2019\u80e1\u4e00\u5200\u9053\uff1a\u2018\u597d\uff0c\u4e0d\u8bba\u6211\u662f\u6b7b\u662f\u6d3b\uff0c\u8fd9\u4e00\u751f\u8fc7\u5f97\u65e0\u6127\u5929\u5730\u3002\u8fd9\u53ea\u94c1\u76d2\u513f\uff0c\u7b49\u5b69\u5b50\u8fc7\u4e86\u5341\u516d\u5c81\u751f\u65e5\u65f6\u4ea4\u7ed9\u4ed6\u3002\u2019\u201c\u6211\u5728\u95e8\u7f1d\u4e2d\u6084\u6084\u5f20\u671b\uff0c\u53ea\u89c1\u592b\u4eba\u62b1\u7740\u5b69\u5b50\uff0c\u80e1\u4e00\u5200\u4ece\u8863\u56ca\u4e2d\u53d6\u51fa\u4e00\u53ea\u94c1\u76d2\u6765\uff0c\u90a3\u5c31\u662f\u8fd9\u4e00\u53ea\u76d2\u5b50\u4e86\u3002\u4e0d\u8fc7\u90a3\u65f6\u95ef\u738b\u7684\u519b\u5200\u5374\u5728\u5929\u9f99\u95e8\u7530\u5bb6\u624b\u91cc\uff0c\u5e76\u975e\u653e\u5728\u76d2\u4e2d\u3002 \u3000\u3000\u201c\u90a3\u4e48\u76d2\u4e2d\u653e\u7684\u662f\u4ec0\u4e48\u5462\uff1f\u4f60\u4eec\u5b9a\u7136\u8981\u95ee\u3002\u5f53\u65f6\u6211\u5fc3\u4e2d\u4e5f\u662f\u8001\u5927\u4e2a\u7591\u7aa6\u3002\u53ef\u662f\u80e1\u4e00\u5200\u4e0d\u6253\u5f00\u76d2\u5b50\uff0c\u6211\u81ea\u7136\u4e5f\u6ca1\u6cd5\u770b\u5230\u3002 \u3000\u3000\u2018\u4ed6\u4ea4\u4ee3\u4e86\u8fd9\u4e9b\u8bdd\u540e\uff0c\u5fc3\u4e2d\u65e0\u7275\u65e0\u6302\uff0c\u5012\u5934\u4fbf\u7761\uff0c\u7247\u523b\u95f4\u9f3e\u58f0\u5927\u4f5c\u3002\u8fd9\u6253\u9f3e\u58f0\u5c31\u5982\u96f7\u9e23\u4e00\u822c\u3002\u6211\u77e5\u9053\u6ca1\u4ec0\u4e48\u542c\u7684\u4e86\uff0c\u60f3\u5408\u773c\u7761\u89c9\uff0c\u4f46\u9694\u58c1\u90a3\u9f3e\u58f0\u5b9e\u5728\u54cd\u5f97\u5389\u5bb3\uff0c\u5435\u5f97\u6211\u600e\u80fd\u7761\u5f97\u7740\uff1f \u3000\u3000\u6211\u5fc3\u91cc\u60f3\uff1a\u8fd9\u4f4d\u5c11\u5e74\u592b\u4eba\u5343\u5a07\u767e\u5a9a\uff0c\u5982\u82b1\u4f3c\u7389\uff0c\u5374\u5ac1\u4e86\u80e1\u4e00\u5200\u8fd9\u4e48\u4e2a\u53c8\u7c97\u9c81\u53c8\u4e11\u964b\u7684\u6c49\u5b50\uff0c\u8fd9\u672c\u5df2\u5947\u4e86\uff0c\u5c45\u7136\u8fd8\u6b7b\u5fc3\u584c\u5730\u7684\u656c\u4ed6\u7231\u4ed6\uff0c\u90a3\u66f4\u662f\u6559\u4eba\u8bf4\u4ec0\u4e48\u4e5f\u60f3\u4e0d\u901a\u3002 \u3000\u3000\u201c\u7b2c\u4e8c\u65e5\u5929\u6ca1\u4eae\uff0c\u592b\u4eba\u51fa\u623f\u6765\u5429\u5490\u5e97\u4f34\uff0c\u5bb0\u4e00\u53e3\u732a\u4e00\u53e3\u7f8a\uff0c\u53c8\u8981\u6740\u9e21\u6740\u9e2d\uff0c\u5979\u4eb2\u81ea\u4e0b\u53a8\u53bb\u505a\u83dc\u3002\u6211\u529d\u9053\uff1a\u2018\u4f60\u751f\u5b69\u5b50\u6ca1\u8fc7\u4e09\u671d\uff0c\u52b3\u788c\u4e0d\u5f97\uff0c\u5426\u5219\u65e5\u540e\u8170\u9178\u80cc\u75db\uff0c\u9ebb\u70e6\u53ef\u591a\u7740\u4e86\u3002\u2019\u5979\u7b11\u4e86\u7b11\u9053\uff1a\u2018\u773c\u524d\u7684\u9ebb\u70e6\u5df2\u591f\u591a\u4e86\uff0c\u8fd8\u7ba1\u65e5\u540e\u5462\uff1f\u2019\u80e1\u4e00\u5200\u89c1\u5979\u7d2f\u5f97\u8f9b\u82e6\uff0c\u4e5f\u529d\u5979\u6b47\u6b47\u3002\u592b\u4eba\u4e5f\u53ea\u662f\u671d\u4ed6\u7b11\u7b11\uff0c\u81ea\u987e\u81ea\u505a\u83dc\u3002 \u3000\u3000\u80e1\u4e00\u5200\u7b11\u9053\uff1a\u2018\u597d\uff0c\u518d\u5403\u4e00\u6b21\u4f60\u7684\u5999\u624b\u70f9\u8c03\uff0c\u6b7b\u800c\u65e0\u61be\u3002\u2019\u6211\u8fd9\u624d\u660e\u767d\uff0c\u539f\u6765\u5979\u77e5\u592b\u59bb\u6b7b\u522b\u5728\u5373\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u8981\u518d\u505a\u4e00\u6b21\u83dc\u7ed9\u4e08\u592b\u5403\u3002 \u3000\u3000\u201c\u5230\u5929\u8272\u5927\u4eae\uff0c\u592b\u4eba\u5df2\u505a\u597d\u4e86\u4e8c\u4e09\u5341\u4e2a\u83dc\uff0c\u653e\u6ee1\u4e86\u4e00\u684c\u3002 \u3000\u3000\u80e1\u4e00\u5200\u53eb\u5e97\u4f34\u6253\u6765\u51e0\u5341\u65a4\u9152\uff0c\u653e\u6000\u5927\u559d\u3002\u592b\u4eba\u62b1\u7740\u5b69\u5b50\u5750\u5728\u4ed6\u8eab\u65c1\uff0c\u7ed9\u4ed6\u659f\u9152\u5e03\u83dc\uff0c\u8138\u4e0a\u7adf\u81ea\u5e26\u7740\u7b11\u5bb9\u3002 \u3000\u3000\u201c\u80e1\u4e00\u5200\u4e00\u53e3\u6c14\u559d\u4e86\u4e03\u516b\u7897\u767d\u5e72\uff0c\u7528\u624b\u6293\u4e86\u51e0\u5757\u7f8a\u8089\u5165\u53e3\uff0c\u53ea\u542c\u5f97\u95e8\u5916\u9a6c\u8e44\u58f0\u54cd\uff0c\u6e10\u6e10\u9a70\u8fd1\u3002\u80e1\u4e00\u5200\u4e0e\u592b\u4eba\u5bf9\u671b\u4e00\u773c\uff0c\u7b11\u4e86\u4e00\u7b11\uff0c\u8138\u4e0a\u795e\u8272\u90fd\u663e\u5f97\u5b9e\u662f\u96be\u820d\u96be\u5206\u3002\u80e1\u4e00\u5200\u9053\uff1a\u2018\u4f60\u8fdb\u623f\u53bb\u5427\u3002\u7b49\u5b69\u5b50\u5927\u4e86\uff0c\u4f60\u8bb0\u5f97\u8ddf\u4ed6\u8bf4\uff1a\u201c\u7238\u7238\u53eb\u4ed6\u5fc3\u80a0\u72e0\u4e9b\u786c\u4e9b\u3002\u201d\u5c31\u8fd9\u4e48\u4e00\u53e5\u8bdd\u3002\u2019\u592b\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u2018\u8ba9\u6211\u77a7\u77a7\u91d1\u9762\u4f5b\u662f\u4ec0\u4e48\u6a21\u6837\u3002\u2019\u201c\u8fc7\u4e0d\u591a\u65f6\uff0c\u9a6c\u8e44\u58f0\u5728\u95e8\u5916\u505c\u4f4f\uff0c\u91d1\u9762\u4f5b\u3001\u8303\u5e2e\u4e3b\u3001\u7530\u76f8\u516c\u53c8\u5e26\u4e86\u90a3\u51e0\u5341\u4e2a\u4eba\u8fdb\u6765\u3002\u80e1\u4e00\u5200\u5934\u4e5f\u4e0d\u62ac\uff0c\u8bf4\u9053\uff1a\u2018\u5403\u7f62\uff01\u2019\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u597d\uff01\u2019\u5750\u5728\u4ed6\u7684\u5bf9\u9762\uff0c\u7aef\u8d77\u7897\u5c31\u8981\u559d\u9152\u3002\u7530\u76f8\u516c\u5fd9\u4f38\u624b\u62e6\u4f4f\uff0c\u8bf4\u9053\uff1a\u2018\u82d7\u5927\u4fa0\uff0c\u987b\u9632\u9152\u8089\u4e4b\u4e2d\u6709\u4ec0\u53e4\u602a\u3002\u2019\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u7d20\u95fb\u80e1\u4e00\u5200\u662f\u94c1\u94ee\u94ee\u7684\u6c49\u5b50\uff0c\u884c\u4e8b\u5149\u660e\u78ca\u843d\uff0c\u5c82\u80fd\u6697\u7b97\u5bb3\u6211\uff1f\u2019\u4e3e\u8d77\u7897\u4e00\u4ef0\u8116\u5b50\uff0c\u4e00\u53e3\u559d\u5e72\uff0c\u631f\u5757\u9e21\u8089\u5403\u4e86\uff0c\u4ed6\u5403\u83dc\u7684\u6a21\u6837\u53ef\u6bd4\u80e1\u4e00\u5200\u65af\u6587\u5f97\u591a\u4e86\u3002 \u3000\u3000\u201c\u592b\u4eba\u5411\u91d1\u9762\u4f5b\u51dd\u671b\u4e86\u51e0\u773c\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u5bf9\u80e1\u4e00\u5200\u9053\uff1a\u2018\u5927\u54e5\uff0c\u5e76\u4e16\u8c6a\u6770\u4e4b\u4e2d\uff0c\u9664\u4e86\u8fd9\u4f4d\u82d7\u5927\u4fa0\uff0c\u5f53\u771f\u518d\u65e0\u7b2c\u4e8c\u4eba\u662f\u4f60\u654c\u624b\u3002\u4ed6\u5bf9\u4f60\u63a8\u5fc3\u7f6e\u8179\uff0c\u8fd9\u526f\u6c14\u6982\uff0c\u5929\u4e0b\u5c31\u53ea\u4f60\u4eec\u4e24\u4eba\u3002\u2019\u80e1\u4e00\u5200\u54c8\u54c8\u7b11\u9053\uff1a\u2018\u59b9\u5b50\uff0c\u4f60\u662f\u5973\u4e2d\u4e08\u592b\uff0c\u4f60\u4e5f\u7b97\u5f97\u4e0a\u4e00\u4e2a\u3002\u2019\u592b\u4eba\u5411\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u82d7\u5927\u4fa0\uff0c\u4f60\u662f\u7537\u513f\u6c49\u5927\u4e08\u592b\uff0c\u679c\u771f\u540d\u4e0d\u865a\u4f20\u3002\u6211\u4e08\u592b\u82e5\u662f\u6b7b\u5728\u4f60\u624b\u91cc\uff0c\u4e0d\u7b97\u6789\u4e86\u3002\u4f60\u82e5\u662f\u7ed9\u6211\u4e08\u592b\u6740\u4e86\uff0c\u4e5f\u4e0d\u5bb3\u4f60\u4e00\u4e16\u82f1\u540d\u3002\u6765\uff0c\u6211\u656c\u4f60\u4e00\u7897\u3002\u2019\u8bf4\u7740\u659f\u4e86\u4e24\u7897\u9152\uff0c\u81ea\u5df1\u5148\u559d\u4e86\u4e00\u7897\u3002 \u3000\u3000\u201c\u91d1\u9762\u4f5b\u4f3c\u4e4e\u4e0d\u7231\u8bf4\u8bdd\uff0c\u53ea\u53cc\u7709\u4e00\u626c\uff0c\u53c8\u8bf4\u9053\uff1a\u2018\u597d\uff01\u2019\u63a5\u8fc7\u9152\u7897\u3002\u8303\u5e2e\u4e3b\u4e00\u76f4\u5728\u65c1\u6c89\u7740\u8138\uff0c\u8fd9\u65f6\u62a2\u4e0a\u4e00\u6b65\uff0c\u53eb\u9053\uff1a\u2018\u82d7\u5927\u4fa0\uff0c\u987b\u9632\u6700\u6bd2\u5987\u4eba\u5fc3\u3002\u2019\u91d1\u9762\u4f5b\u7709\u5934\u4e00\u76b1\uff0c\u4e0d\u53bb\u7406\u4ed6\uff0c\u81ea\u884c\u5c06\u9152\u559d\u4e86\u3002\u592b\u4eba\u62b1\u7740\u5b69\u5b50\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u2018\u82d7\u5927\u4fa0\uff0c\u4f60\u6709\u4ec0\u4e48\u653e\u4e0d\u4e0b\u4e4b\u4e8b\uff0c\u5148\u8ddf\u6211\u8bf4\u3002\u5426\u5219\u82e5\u4f60\u4e00\u4e2a\u5931\u624b\uff0c\u7ed9\u6211\u4e08\u592b\u6740\u4e86\uff0c\u4f60\u8fd9\u4e9b\u670b\u53cb\uff0c\u563f\u563f\uff0c\u672a\u5fc5\u80fd\u7ed9\u4f60\u529e\u4ec0\u4e48\u4e8b\u3002\u2019\u201c\u91d1\u9762\u4f5b\u5fae\u4e00\u6c89\u541f\uff0c\u8bf4\u9053\uff1a\u2018\u56db\u5e74\u4e4b\u524d\uff0c\u6211\u6709\u4e8b\u53bb\u4e86\u5cad\u5357\uff0c\u5bb6\u4e2d\u5374\u6765\u4e86\u4e00\u4eba\uff0c\u81ea\u79f0\u662f\u5c71\u4e1c\u6b66\u5b9a\u53bf\u7684\u5546\u5251\u9e23\u3002\u2019\u592b\u4eba\u9053\uff1a\u2018\u55ef\uff0c\u6b64\u4eba\u662f\u5a01\u9707\u6cb3\u6714\u738b\u7ef4\u626c\u7684\u5f1f\u5b50\uff0c\u516b\u5366\u95e8\u4e2d\u597d\u624b\uff0c\u516b\u5366\u638c\u4e0e\u516b\u5366\u5200\u90fd\u5f88\u4e86\u5f97\u3002\u2019\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u4e0d\u9519\u3002\u4ed6\u542c\u8bf4\u6211\u6709\u4e2a\u5916\u53f7\u53eb\u4f5c\u201c\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u201d\uff0c\u5fc3\u4e2d\u4e0d\u670d\uff0c\u627e\u4e0a\u95e8\u6765\u6bd4\u6b66\u3002\u504f\u5de7\u6211\u4e0d\u5728\u5bb6\uff0c\u4ed6\u548c\u6211\u5144\u5f1f\u4e09\u8a00\u4e24\u8bed\uff0c\u52a8\u8d77\u624b\u6765\uff0c\u7adf\u4e0b\u6740\u624b\uff0c\u5c06\u6211\u4e24\u4e2a\u5144\u5f1f\u3001\u4e00\u4e2a\u59b9\u5b50\uff0c\u5168\u7528\u91cd\u624b\u9707\u6b7b\u3002\u6bd4\u6b66\u6709\u8f93\u6709\u8d62\uff0c\u6211\u5f1f\u59b9\u5b66\u827a\u4e0d\u7cbe\uff0c\u6b7b\u5728\u4ed6\u7684\u624b\u91cc\uff0c\u90a3\u4e5f\u7f62\u4e86\uff0c\u54ea\u77e5\u4ed6\u8fd8\u5c06\u6211\u90a3\u4e0d\u4f1a\u6b66\u827a\u7684\u5f1f\u5987\u4e5f\u4e00\u638c\u6253\u6b7b\u3002\u2019\u592b\u4eba\u9053\uff1a\u2018\u6b64\u4eba\u597d\u6a2a\u3002\u4f60\u5c31\u8be5\u53bb\u627e\u4ed6\u554a\u3002\u2019\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u6211\u4e24\u4e2a\u5144\u5f1f\u6b66\u529f\u4e0d\u5f31\uff0c\u5546\u5251\u9e23\u65e2\u6709\u6b64\u624b\u6bb5\uff0c\u81ea\u662f\u52b2\u654c\u3002\u60f3\u6211\u82d7\u5bb6\u4e0e\u80e1\u5bb6\u7d2f\u4e16\u6df1\u4ec7\uff0c\u80e1\u4e00\u5200\u4e4b\u4e8b\u672a\u4e86\uff0c\u4e0d\u8be5\u5192\u9669\u8f7b\u751f\uff0c\u662f\u4ee5\u56db\u5e74\u6765\u4e00\u76f4\u6ca1\u4e0a\u5c71\u4e1c\u6b66\u5b9a\u53bb\u3002\u2019\u592b\u4eba\u9053\uff1a\u2018\u8fd9\u4ef6\u4e8b\u4ea4\u7ed9\u6211\u4eec\u5c31\u662f\u3002\u2019\u91d1\u9762\u4f5b\u70b9\u70b9\u5934\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u62bd\u51fa\u4f69\u5251\uff0c\u8bf4\u9053\uff1a\u2018\u80e1\u4e00\u5200\uff0c\u6765\u5427\u3002\u2019\u201c\u80e1\u4e00\u5200\u53ea\u987e\u5403\u8089\uff0c\u5374\u4e0d\u7406\u4ed6\u3002\u592b\u4eba\u9053\uff1a\u2018\u82d7\u5927\u4fa0\uff0c\u6211\u4e08\u592b\u6b66\u529f\u867d\u5f3a\uff0c\u4e5f\u672a\u5fc5\u4e00\u5b9a\u80fd\u80dc\u4f60\u3002\u2019\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u554a\uff0c\u6211\u5fd8\u4e86\u3002 \u3000\u3000\u80e1\u4e00\u5200\uff0c\u4f60\u5fc3\u4e2d\u6709\u4ec0\u4e48\u653e\u4e0d\u4e0b\u4e4b\u4e8b\uff1f\u2019\u80e1\u4e00\u5200\u62b9\u62b9\u5634\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u2018\u4f60\u82e5\u6740\u4e86\u6211\uff0c\u8fd9\u5b69\u5b50\u65e5\u540e\u5fc5\u5b9a\u627e\u4f60\u62a5\u4ec7\u3002\u4f60\u597d\u597d\u7167\u987e\u4ed6\u5427\u3002\u2019\u6211\u5fc3\u91cc\u60f3\uff1a\u2018\u5e38\u8a00\u9053\uff1a\u65a9\u8349\u9664\u6839\u3002\u91d1\u9762\u4f5b\u82e5\u5c06\u80e1\u4e00\u5200\u6740\u4e86\uff0c\u54ea\u80af\u653e\u8fc7\u4ed6\u59bb\u513f\uff1f\u4ed6\u5c45\u7136\u8fd8\u6015\u91d1\u9762\u4f5b\u5fd8\u8bb0\uff0c\u7279\u5730\u63d0\u4e0a\u4e00\u63d0\u3002\u2019\u54ea\u77e5\u91d1\u9762\u4f5b\u8bf4\u9053\uff1a\u2018\u4f60\u653e\u5fc3\uff0c\u4f60\u82e5\u4e0d\u5e78\u5931\u624b\uff0c\u8fd9\u5b69\u5b50\u6211\u5f53\u81ea\u5df1\u513f\u5b50\u4e00\u822c\u770b\u5f85\u3002\u2019\u201c\u8303\u5e2e\u4e3b\u4e0e\u7530\u76f8\u516c\u76b1\u7740\u7709\u5934\u7ad9\u5728\u4e00\u65c1\uff0c\u6a21\u6837\u513f\u663e\u5f97\u597d\u4e0d\u8010\u70e6\u3002\u6211\u5fc3\u4e2d\u4e5f\u6697\u6697\u7eb3\u7f55\uff1a\u2018\u77a7\u80e1\u4e00\u5200\u592b\u5987\u4e0e\u91d1\u9762\u4f5b\u7684\u795e\u60c5\uff0c\u4e92\u76f8\u656c\u91cd\u5631\u6258\uff0c\u5012\u4f3c\u662f\u6781\u597d\u7684\u670b\u53cb\uff0c\u54ea\u91cc\u4f1a\u6027\u547d\u76f8\u62da\uff1f\u2019\u201c\u5c31\u5728\u6b64\u65f6\uff0c\u80e1\u4e00\u5200\u4ece\u8170\u95f4\u62d4\u51fa\u5200\u6765\uff0c\u5bd2\u5149\u4e00\u95ea\uff0c\u53eb\u9053\uff1a\u2018\u597d\u670b\u53cb\uff0c\u4f60\u5148\u8bf7\uff01\u2019\u91d1\u9762\u4f5b\u957f\u5251\u4e00\u633a\uff0c\u8bf4\u58f0\uff1a\u2018\u9886\u6559\uff01\u2019\u865a\u8d70\u4e24\u62db\u3002\u7530\u76f8\u516c\u53eb\u9053\uff1a\u2018\u82d7\u5927\u4fa0\uff0c\u4e0d\u7528\u5ba2\u6c14\uff0c\u8fdb\u62db\u5427\uff01\u2019\u91d1\u9762\u4f5b\u7a81\u7136\u6536\u5251\uff0c\u56de\u5934\u8bf4\u9053\uff1a\u2018\u5404\u4f4d\u901a\u7edf\u8bf7\u51fa\u95e8\u53bb\uff01\u2019\u7530\u76f8\u516c\u8ba8\u4e86\u4e2a\u6ca1\u8da3\uff0c\u89c1\u4ed6\u8138\u8272\u4e25\u91cd\uff0c\u4e0d\u6562\u8fdd\u80cc\uff0c\u548c\u8303\u5e2e\u4e3b\u7b49\u90fd\u9000\u51fa\u5927\u5385\uff0c\u7ad9\u5728\u95e8\u53e3\u89c2\u6218\u3002 \u3000\u3000\u201c\u80e1\u4e00\u5200\u53eb\u9053\uff1a\u2018\u597d\uff0c\u6211\u8fdb\u62db\u4e86\u3002\u2019\u6b3a\u8fdb\u4e00\u6b65\uff0c\u6325\u5200\u5f53\u5934\u731b\u5288\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u91d1\u9762\u4f5b\u8eab\u5b50\u659c\u8d70\uff0c\u5251\u950b\u5708\u8f6c\uff0c\u5251\u5c16\u98a4\u52a8\uff0c\u523a\u5411\u5bf9\u65b9\u53f3\u80c1\u3002 \u3000\u3000\u80e1\u4e00\u5200\u9053\uff1a\u2018\u6211\u8fd9\u628a\u5200\u662f\u5b9d\u5200\uff0c\u5c0f\u5fc3\u4e86\u3002\u2019\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u6325\u5200\u5f80\u5251\u8eab\u780d\u53bb\u3002\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u627f\u6559\uff01\u2019\u624b\u8155\u632f\u5904\uff0c\u5251\u5203\u65e9\u5df2\u907f\u5f00\u3002 \u3000\u3000\u6211\u5728\u6ca7\u5dde\u770b\u4eba\u52a8\u5200\u5b50\u6bd4\u6b66\uff0c\u4e5f\u4e0d\u77e5\u770b\u4e86\u591a\u5c11\uff0c\u4f46\u4e24\u4eba\u90a3\u4e48\u5feb\u7684\u8eab\u624b\uff0c\u5374\u4ece\u6765\u6ca1\u89c1\u8fc7\u3002\u4e24\u4eba\u53ea\u62c6\u4e86\u4e03\u516b\u62db\uff0c\u6211\u624b\u5fc3\u4e2d\u5df2\u5168\u662f\u51b7\u6c57\u3002 \u3000\u3000\u201c\u53c8\u62c6\u6570\u62db\uff0c\u4e24\u4eba\u5175\u5203\u500f\u5730\u76f8\u4ea4\uff0c\u545b\u5577\u4e00\u58f0\uff0c\u91d1\u9762\u4f5b\u7684\u957f\u5251\u88ab\u524a\u4e3a\u4e24\u622a\u3002\u4ed6\u4e1d\u6beb\u4e0d\u60e7\uff0c\u629b\u4e0b\u65ad\u5251\uff0c\u8981\u4ee5\u7a7a\u624b\u4e0e\u654c\u4eba\u76f8\u640f\u3002\u80e1\u4e00\u5200\u5374\u8dc3\u51fa\u5708\u5b50\uff0c\u53eb\u9053\uff1a\u2018\u4f60\u6362\u67c4\u5251\u5427\uff01\u2019\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u4e0d\u788d\u4e8b\uff01\u2019\u7530\u76f8\u516c\u5374\u5df2\u5c06\u81ea\u5df1\u7684\u957f\u5251\u9012\u4e86\u8fc7\u53bb\u3002\u91d1\u9762\u4f5b\u5fae\u4e00\u6c89\u541f\uff0c\u8bf4\u9053\uff1a\u2018\u6211\u7a7a\u624b\u6253\u4e0d\u8fc7\u4f60\u7684\u5355\u5200\uff0c\u8fd8\u662f\u7528\u5251\u7684\u597d\u3002\u2019\u63a5\u8fc7\u957f\u5251\uff0c\u4e24\u4eba\u53c8\u52a8\u8d77\u624b\u6765\u3002\u6211\u5fc3\u60f3\uff1a\u2018\u6ca7\u5dde\u7684\u5c11\u5e74\u5b50\u5f1f\u6bd4\u6b66\uff0c\u660e\u660e\u683d\u4e86\uff0c\u8fd8\u662f\u4e0d\u80af\u670d\u6c14\uff0c\u5b9a\u8981\u8bf4\u51e0\u53e5\u8bdd\u6765\u5706\u8138\u3002\u8fd9\u4f4d\u91d1\u9762\u4f5b\u81ea\u79f0\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\uff0c\u624b\u4e0a\u5e76\u672a\u8f93\u62db\uff0c\u5634\u4e0a\u5374\u5df2\u6cc4\u6c14\uff0c\u4e5f\u7b97\u5f97\u53e4\u602a\u3002\u2019\u540e\u6765\u6211\u624d\u660e\u767d\uff0c\u8fd9\u4e24\u4eba\u90fd\u662f\u5929\u4e0b\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u62c6\u4e86\u8fd9\u51e0\u62db\uff0c\u5fc3\u4e2d\u90fd\u5df2\u4f69\u670d\u5bf9\u65b9\uff0c\u81ea\u7136\u4e0d\u6562\u76f8\u8f7b\u3002 \u3000\u3000\u201c\u8fd9\u65f6\u4e24\u4eba\u4e92\u8f6c\u5708\u5b50\uff0c\u79bb\u5f97\u8fdc\u8fdc\u7684\uff0c\u7a81\u7136\u95f4\u6251\u4e0a\u4ea4\u6362\u4e00\u62db\u4e24\u5f0f\uff0c\u7acb\u5373\u8dc3\u5f00\u3002\u8fd9\u822c\u6597\u4e86\u5341\u591a\u4e2a\u56de\u5408\uff0c\u91d1\u9762\u4f5b\u9661\u7136\u4e00\u5251\u523a\u5411\u80e1\u4e00\u5200\u5934\u9888\u3002\u8fd9\u4e00\u5251\u53bb\u52bf\u52b2\u6025\u4e4b\u6781\uff0c\u773c\u89c1\u96be\u4ee5\u95ea\u907f\u3002\u80e1\u4e00\u5200\u5f80\u5730\u4e0b\u4e00\u6eda\uff0c\u7529\u8d77\u5200\u6765\uff0c\u5f53\u7684\u4e00\u54cd\uff0c\u53c8\u5c06\u957f\u5251\u524a\u65ad\u4e86\u3002\u4ed6\u968f\u5373\u8dc3\u8d77\uff0c\u53eb\u9053\uff1a\u2018\u5bf9\u4e0d\u8d77\uff01\u4e0d\u662f\u6211\u81ea\u6043\u5175\u5668\u950b\u5229\uff0c\u5b9e\u662f\u4f60\u8fd9\u4e00\u62db\u592a\u8fc7\u5389\u5bb3\uff0c\u975e\u6b64\u4e0d\u80fd\u7834\u89e3\u3002\u2019\u201c\u91d1\u9762\u4f5b\u70b9\u70b9\u5934\u9053\uff1a\u2018\u4e0d\u788d\u4e8b\u3002\u2019\u7530\u76f8\u516c\u53c8\u9012\u4e86\u4e00\u67c4\u5251\u4e0a\u6765\u3002 \u3000\u3000\u4ed6\u63a5\u5728\u624b\u4e2d\u3002\u80e1\u4e00\u5200\u9053\uff1a\u2018\u5582\uff0c\u4f60\u4eec\u501f\u4e00\u67c4\u5200\u6765\u3002\u6211\u8fd9\u5200\u592a\u5229\uff0c\u4e24\u4eba\u90fd\u663e\u4e0d\u51fa\u771f\u529f\u592b\u3002\u2019\u7530\u76f8\u516c\u5927\u559c\uff0c\u5f53\u5373\u5728\u4ece\u4eba\u624b\u4e2d\u53d6\u8fc7\u4e00\u67c4\u5200\u4ea4\u7ed9\u4ed6\u3002\u80e1\u4e00\u5200\u6382\u4e86\u4e00\u6382\u3002\u91d1\u9762\u4f5b\u9053\uff0c\u2018\u592a\u8f7b\u4e86\u5427\uff1f\u2019\u6a2a\u8fc7\u957f\u5251\uff0c\u53f3\u624b\u62c7\u6307\u4e0e\u98df\u6307\u634f\u4f4f\u5251\u5c16\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u5c06\u5251\u5c16\u6298\u4e86\u4e00\u622a\u4e0b\u6765\u3002\u8fd9\u6307\u529b\u5f53\u771f\u5389\u5bb3\u4e4b\u6781\u3002\u6211\u5fc3\u4e2d\u6697\u6697\u5403\u60ca\u3002\u53ea\u542c\u5f97\u80e1\u4e00\u5200\u7b11\u9053\uff1a\u2018\u82d7\u4eba\u51e4\uff0c\u4f60\u4e0d\u80af\u5360\u4eba\u534a\u70b9\u4fbf\u5b9c\uff0c\u679c\u7136\u79f0\u5f97\u4e0a\u4e00\u4e2a\u201c\u4fa0\u201d\u5b57\u3002\u2019\u201c\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u5c82\u6562\uff0c\u6709\u4e00\u4e8b\u987b\u5f97\u8ddf\u4f60\u660e\u8a00\u3002\u2019\u80e1\u4e00\u5200\u9053\uff1a\u2018\u8bf4\u5427\u3002\u2019\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u6211\u65e9\u77e5\u4f60\u6b66\u529f\u5353\u7edd\uff0c\u82d7\u4eba\u51e4\u672a\u5fc5\u662f\u4f60\u5bf9\u624b\u3002\u53ef\u662f\u6211\u5728\u6c5f\u6e56\u4e0a\u5230\u5904\u5ba3\u626c\u201c\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u201d\u4e03\u5b57\uff0c\u975e\u662f\u82d7\u4eba\u51e4\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\uff0c\u72c2\u5984\u65e0\u803b\u2026\u2026\u2019\u80e1\u4e00\u5200\u5de6\u624b\u4e00\u6446\uff0c\u62e6\u4f4f\u4e86\u4ed6\u7684\u8bdd\u5934\uff0c\u8bf4\u9053\uff1a\u2018\u6211\u65e9\u77e5\u4f60\u7684\u771f\u610f\u3002\u4f60\u60f3\u627e\u6211\u52a8\u624b\uff0c\u53ef\u662f\u65e0\u6cd5\u627e\u5230\uff0c\u4e8e\u662f\u5ba3\u626c\u8fd9\u4e03\u5b57\u5916\u53f7\uff0c\u597d\u6fc0\u6211\u8fdb\u5173\u3002\u2019\u4ed6\u82e6\u7b11\u4e86\u4e00\u4e0b\uff0c\u9053\uff1a\u2018\u73b0\u5728\u6211\u8fdb\u5173\u4e86\u3002\u4f60\u82e5\u662f\u6253\u8d25\u4e86\u6211\uff0c\u8fd9\u4e03\u5b57\u5916\u53f7\u540d\u526f\u5176\u5b9e\uff0c\u5c3d\u53ef\u7528\u5f97\u3002\u8fdb\u62db\u5427\uff01\u2019\u201d \u3000\u3000\u4f17\u4eba\u542c\u5230\u8fd9\u91cc\uff0c\u624d\u77e5\u82d7\u4eba\u51e4\u8fd9\u4e03\u5b57\u5916\u53f7\u7684\u771f\u610f\u3002 \u3000\u3000\u53ea\u542c\u5b9d\u6811\u8bf4\u9053\uff1a\u201c\u4e24\u4eba\u8bf4\u4e86\u8fd9\u756a\u8bdd\uff0c\u5200\u5251\u95ea\u52a8\uff0c\u53c8\u5df2\u6597\u5728\u4e00\u8d77\u3002\u8fd9\u4e00\u6b21\u5175\u5203\u4e0a\u626f\u5e73\uff0c\u4e24\u4eba\u5404\u663e\u5e73\u751f\u7edd\u6280\uff0c\u8d77\u521d\u4e24\u767e\u4f59\u62db\u4e2d\uff0c\u7adf\u662f\u6ca1\u5206\u534a\u70b9\u4e0a\u4e0b\u3002\u540e\u6765\u80e1\u4e00\u5200\u4f3c\u4e4e\u6e10\u6e10\u843d\u8d25\uff0c\u4e00\u8def\u5200\u6cd5\u5168\u53d6\u5b88\u52bf\uff0c\u8303\u3001\u7530\u8bf8\u4eba\u8138\u4e0a\u5747\u73b0\u559c\u8272\u3002\u53ea\u89c1\u4ed6\u5b88\u5f97\u7d27\u5bc6\u5f02\u5e38\uff0c\u91d1\u9762\u4f5b\u56db\u9762\u516b\u65b9\u8fde\u73af\u8fdb\u653b\uff0c\u5374\u5948\u4f55\u4e0d\u5f97\u4ed6\u534a\u70b9\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u80e1\u4e00\u5200\u5200\u6cd5\u4e00\u53d8\uff0c\u51fa\u624b\u5168\u662f\u786c\u5288\u786c\u65ab\u3002\u91d1\u9762\u4f5b\u6ee1\u5385\u6e38\u8d70\uff0c\u957f\u5251\u6216\u523a\u6216\u51fb\uff0c\u4e5f\u662f\u7075\u52a8\u4e4b\u6781\u3002 \u3000\u3000\u201c\u8fd9\u5355\u5200\u529f\u592b\uff0c\u6211\u4e5f\u66fe\u8ddf\u5e08\u7236\u4e0b\u8fc7\u4e03\u516b\u5e74\u82e6\u529f\uff0c\u77e5\u9053\u5355\u5200\u5206\u2018\u5929\u5730\u541b\u4eb2\u5e08\u2019\u4e94\u4f4d\uff1a\u5200\u80cc\u4e3a\u5929\uff0c\u5200\u53e3\u4e3a\u5730\uff0c\u67c4\u4e2d\u4e3a\u541b\uff0c\u62a4\u624b\u4e3a\u4eb2\uff0c\u67c4\u540e\u4e3a\u5e08\u3002\u8fd9\u4e94\u4f4d\u4e4b\u4e2d\uff0c\u81ea\u4ee5\u5929\u5730\u4e24\u4f4d\u4e3a\u4e3b\uff0c\u770b\u90a3\u80e1\u4e00\u5200\u7684\u5200\u6cd5\uff0c\u5929\u5730\u4e24\u4f4d\u56fa\u7136\u4f7f\u5f97\u51fa\u795e\u5165\u5316\uff0c\u800c\u541b\u4eb2\u5e08\u4e09\u4f4d\uff0c\u7adf\u4e5f\u80fd\u7528\u4ee5\u653b\u654c\u9632\u8eab\u3002\u6709\u65f6\u91d1\u9762\u4f5b\u7684\u957f\u5251\u5947\u62db\u7a81\u751f\uff0c\u4ece\u51fa\u4eba\u610f\u6599\u4e4b\u5916\u7684\u90e8\u4f4d\u523a\u53bb\uff0c\u82e5\u7528\u5200\u80cc\u5200\u53e3\uff0c\u4e07\u96be\u6321\u67b6\uff0c\u80e1\u4e00\u5200\u7adf\u4f1a\u7a81\u7136\u6389\u8f6c\u5200\u950b\uff0c\u4ee5\u5200\u67c4\u6253\u51fb\u5251\u5203\uff0c\u8feb\u4f7f\u654c\u4eba\u53d8\u62db\u3002\u81f3\u4e8e\u2018\u5c55\u3001\u62b9\u3001\u94a9\u3001\u5241\u3001\u780d\u3001\u5288\u2019\u516d\u5b57\u8bc0\uff0c\u66f4\u662f\u53d8\u5e7b\u83ab\u6d4b\u3002 \u3000\u3000\u201c\u5251\u4e0a\u7684\u529f\u592b\uff0c\u90a3\u65f6\u6211\u53ef\u4e0d\u5927\u61c2\u5566\u3002\u53ea\u662f\u80e1\u4e00\u5200\u7684\u5200\u6cd5\u5982\u6b64\u7cbe\u5947\uff0c\u800c\u91d1\u9762\u4f5b\u59cb\u7ec8\u8ddf\u4ed6\u6253\u4e86\u4e2a\u65d7\u9f13\u76f8\u5f53\uff0c\u81ea\u7136\u4e5f\u662f\u5389\u5bb3\u4e4b\u6781\u3002\u5200\u5251\u67aa\u662f\u6b66\u5b66\u7684\u4e09\u5927\u4e3b\u5175\uff0c\u5e38\u8a00\u9053\uff1a\u2018\u5200\u5982\u731b\u864e\uff0c\u5251\u5982\u98de\u51e4\uff0c\u67aa\u5982\u6e38\u9f99\u3002\u2019\u8fd9\u4e24\u4eba\u4f7f\u5200\u7684\u679c\u5982\u731b\u864e\u4e0b\u5c71\uff0c\u4f7f\u5251\u7684\u4e5f\u786e\u4f3c\u51e4\u51f0\u98de\u821e\uff0c\u4e00\u521a\u4e00\u67d4\uff0c\u5404\u6709\u5404\u7684\u672c\u4e8b\uff0c\u8c01\u4e5f\u80dc\u4e0d\u4e86\u8c01\u3002\u8d77\u521d\u6211\u8fd8\u770b\u5f97\u51fa\u62db\u6570\u67b6\u5f0f\uff0c\u5230\u5f97\u540e\u6765\uff0c\u53ea\u77a7\u5f97\u5934\u6655\u76ee\u7729\uff0c\u751f\u6015\u5f53\u573a\u6454\u5012\uff0c\u53ea\u597d\u8f6c\u8fc7\u4e86\u5934\u4e0d\u770b\u3002 \u3000\u3000\u201c\u90a3\u65f6\u8033\u4e2d\u53ea\u542c\u5f97\u5200\u5251\u5288\u98ce\u7684\u547c\u547c\u4e4b\u58f0\uff0c\u5076\u5c14\u53cc\u5203\u76f8\u4ea4\uff0c\u53d1\u51fa\u94ee\u7684\u4e00\u58f0\u3002\u6211\u5411\u80e1\u4e00\u5200\u7684\u592b\u4eba\u8138\u4e0a\u4e00\u671b\uff0c\u53ea\u89c1\u5979\u795e\u8272\u5e73\u548c\uff0c\u7adf\u4e1d\u6beb\u4e0d\u4e3a\u4e08\u592b\u7684\u5b89\u5371\u62c5\u5fc3\u3002 \u3000\u3000\u201c\u6211\u56de\u5934\u518d\u770b\u80e1\u4e00\u5200\u65f6\uff0c\u53ea\u89c1\u4ed6\u6108\u6253\u6108\u662f\u9547\u5b9a\uff0c\u8138\u9732\u7b11\u5bb9\uff0c\u4f3c\u4e4e\u80dc\u7b97\u5728\u63e1\u3002\u91d1\u9762\u4f5b\u4e00\u5f20\u9ec4\u9ec4\u7684\u9762\u76ae\u4e0a\u5374\u4e0d\u6cc4\u9732\u534a\u70b9\u5fc3\u4e8b\uff0c\u65e2\u4e0d\u7d27\u5f20\uff0c\u4ea6\u4e0d\u6c14\u9981\u3002\u53ea\u89c1\u80e1\u4e00\u5200\u7740\u7740\u8fdb\u903c\uff0c\u91d1\u9762\u4f5b\u5374\u4e0d\u4f4f\u5012\u9000\u3002\u8303\u5e2e\u4e3b\u548c\u7530\u76f8\u516c\u4e24\u4eba\u795e\u8272\u6108\u6765\u6108\u662f\u7d27\u5f20\u3002\u6211\u5fc3\u60f3\uff1a\u2018\u96be\u9053\u91d1\u9762\u4f5b\u7adf\u8981\u8f93\u5728\u80e1\u4e00\u5200\u624b\u91cc\uff1f\u2019\u201c\u5ffd\u542c\u5f97\u556a\u3001\u556a\u3001\u556a\u4e00\u9635\u54cd\uff0c\u7530\u76f8\u516c\u62c9\u5f00\u5f39\u5f13\uff0c\u4e00\u9635\u8fde\u73e0\u5f39\u7a81\u7136\u5f80\u80e1\u4e00\u5200\u4e0a\u4e2d\u4e0b\u4e09\u8def\u5c04\u53bb\u3002\u80e1\u4e00\u5200\u54c8\u54c8\u5927\u7b11\uff0c\u5c06\u5355\u5200\u5f80\u5730\u4e0b\u4e00\u6454\u3002\u91d1\u9762\u4f5b\u8138\u4e00\u6c89\uff0c\u957f\u5251\u6325\u52a8\uff0c\u5c06\u5f39\u5b50\u90fd\u62e8\u4e86\u5f00\u53bb\uff0c\u7eb5\u5230\u7530\u76f8\u516c\u8eab\u65c1\uff0c\u5939\u624b\u62a2\u8fc7\u5f39\u5f13\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u6298\u6210\u4e86\u4e24\u622a\uff0c\u8fdc\u8fdc\u629b\u5728\u95e8\u5916\uff0c\u4f4e\u6c89\u7740\u55d3\u5b50\u9053\uff1a\u2018\u51fa\u53bb\uff01\u2019\u6211\u597d\u751f\u5947\u602a\uff1a\u2018\u4eba\u5bb6\u6015\u4f60\u6253\u8f93\uff0c\u624d\u597d\u610f\u76f8\u52a9\uff0c\u4f60\u5374\u5982\u6b64\u4e0d\u8bc6\u597d\u6b79\u3002\u2019\u7530\u76f8\u516c\u7d2b\u80c0\u4e86\u8138\u76ae\uff0c\u6012\u76ee\u5411\u91d1\u9762\u4f5b\u77aa\u4e86\u4e00\u773c\uff0c\u8d70\u51fa\u95e8\u53bb\u3002 \u3000\u3000\u201c\u91d1\u9762\u4f5b\u62fe\u8d77\u5355\u5200\uff0c\u5411\u80e1\u4e00\u5200\u629b\u53bb\uff0c\u8bf4\u9053\uff1a\u2018\u54b1\u4eec\u518d\u6765\u3002\u2019\u80e1\u4e00\u5200\u4f38\u624b\u63a5\u4f4f\uff0c\u987a\u52bf\u4e00\u5200\u6325\u51fa\uff0c\u5f53\u7684\u4e00\u54cd\uff0c\u5200\u5251\u76f8\u4ea4\u3002\u6597\u4e86\u4e00\u9635\uff0c\u773c\u89c1\u65e5\u5df2\u8fc7\u5348\uff0c\u80e1\u4e00\u5200\u53eb\u9053\uff1a\u2018\u809a\u5b50\u997f\u5566\uff0c\u4f60\u5403\u4e0d\u5403\u996d\uff1f\u2019\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u597d\uff0c\u5403\u4e00\u70b9\u3002\u2019\u4e24\u4eba\u5750\u5728\u684c\u8fb9\uff0c\u65c1\u82e5\u65e0\u4eba\u7684\u5403\u4e86\u8d77\u6765\u3002\u80e1\u4e00\u5200\u72fc\u541e\u864e\u54bd\uff0c\u4e00\u53e3\u6c14\u5403\u4e86\u5341\u591a\u4e2a\u9992\u5934\u3001\u4e24\u53ea\u9e21\u3001\u4e00\u53ea\u7f8a\u817f\u3002\u91d1\u9762\u4f5b\u5374\u53ea\u5403\u4e86\u4e24\u6761\u9e21\u817f\u3002\u80e1\u4e00\u5200\u7b11\u9053\uff1a\u2018\u4f60\u5403\u5f97\u592a\u5c11\uff0c\u96be\u9053\u5185\u4eba\u7684\u70f9\u8c03\u624b\u6bb5\u6b20\u4f73\u4e48\uff1f\u2019\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u5f88\u597d\u3002\u2019\u631f\u4e86\u4e00\u5927\u5757\u7f8a\u8089\u5403\u4e86\u3002 \u3000\u3000\u201c\u5403\u8fc7\u996d\uff0c\u4e24\u4eba\u62b9\u62b9\u5634\u518d\u6253\uff0c\u4e0d\u4e45\u90fd\u65bd\u5f00\u8f7b\u8eab\u529f\u592b\uff0c\u6ee1\u5385\u98de\u5954\u6765\u53bb\u3002\u522b\u77a7\u80e1\u4e00\u5200\u8eab\u5b50\u7c97\u58ee\uff0c\u8fdb\u9000\u95ea\u907f\uff0c\u7adf\u662f\u7075\u52a8\u5f02\u5e38\uff1b\u91d1\u9762\u4f5b\u624b\u957f\u817f\u957f\uff0c\u81ea\u4e5f\u4e0d\u80fd\u6162\u4e86\u3002\u8fd9\u4e00\u756a\u6251\u51fb\uff0c\u6211\u770b\u5f97\u8d8a\u52a0\u773c\u82b1\u7f2d\u4e71\uff0c\u5ffd\u542c\u5f97\u554a\u7684\u4e00\u58f0\uff0c\u80e1\u4e00\u5200\u5de6\u8db3\u4e00\u6ed1\uff0c\u8dea\u4e86\u4e0b\u53bb\u3002\u8fd9\u539f\u662f\u91d1\u9762\u4f5b\u8fdb\u62db\u7684\u826f\u673a\uff0c\u4ed6\u53ea\u8981\u4e00\u5251\u5288\u4e0b\uff0c\u654c\u624b\u4e07\u96be\u95ea\u907f\uff0c\u54ea\u77e5\u91d1\u9762\u4f5b\u53cd\u5411\u540e\u8dc3\uff0c\u53eb\u9053\uff1a\u2018\u4f60\u8e0f\u7740\u5f39\u5b50\uff0c\u5c0f\u5fc3\u4e86\uff01\u2019\u80e1\u4e00\u5200\u819d\u672a\u70b9\u5730\uff0c\u65e9\u5df2\u7ad9\u8d77\uff0c\u9053\uff1a\u2018\u4e0d\u9519\uff01\u2019\u5de6\u624b\u62fe\u8d77\u5f39\u5b50\uff0c\u4e2d\u6307\u4e00\u5f39\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u90a3\u5f39\u5b50\u4ece\u95e8\u4e2d\u76f4\u98de\u51fa\u53bb\u3002 \u3000\u3000\u201c\u91d1\u9762\u4f5b\u53eb\u9053\uff1a\u2018\u770b\u5251\uff01\u2019\u633a\u5251\u53c8\u4e0a\u3002\u4e24\u4eba\u7ffb\u7ffb\u6eda\u6eda\uff0c\u76f4\u6597\u5230\u591c\u8272\u6726\u80e7\uff0c\u4e5f\u4e0d\u77e5\u53d8\u6362\u4e86\u591a\u5c11\u62db\u5f0f\uff0c\u5140\u81ea\u96be\u5206\u80dc\u8d25\u3002\u91d1\u9762\u4f5b\u8dc3\u51fa\u5708\u5b50\uff0c\u8bf4\u9053\uff1a\u2018\u80e1\u5144\uff0c\u4f60\u6b66\u827a\u9ad8\u5f3a\uff0c\u5728\u4e0b\u4f69\u670d\u5f97\u7d27\u3002\u54b1\u4eec\u6311\u706f\u591c\u6218\u5462\uff0c\u8fd8\u662f\u660e\u65e5\u518d\u51b3\u96cc\u96c4\uff1f\u2019\u80e1\u4e00\u5200\u7b11\u9053\uff1a\u2018\u4f60\u8ba9\u6211\u591a\u6d3b\u4e00\u5929\u5427\uff01\u2019\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u4e0d\u6562\uff01\u2019\u957f\u5251\u4e00\u4f38\uff0c\u4e00\u62db\u2018\u4e39\u51e4\u671d\u9633\u2019\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002\u8fd9\u2018\u4e39\u51e4\u671d\u9633\u2019\u5f0f\u867d\u4e3a\u5251\u62db\uff0c\u4f46\u4ed6\u9000\u540e\u4e09\u6b65\u518d\u4f7f\u5c06\u51fa\u6765\uff0c\u5df2\u53d8\u4e3a\u884c\u793c\u81f4\u656c\u3002\u80e1\u4e00\u5200\u7ad6\u8d77\u5200\u6765\uff0c\u659c\u659c\u5411\u4e0a\u4e00\u6307\uff0c\u8fd9\u4e00\u62db\u2018\u53c2\u62dc\u5317\u6597\u2019\uff0c\u4e5f\u662f\u5411\u5bf9\u65b9\u81f4\u610f\u3002\u4e24\u4eba\u521d\u6597\u65f6\u6027\u547d\u76f8\u640f\uff0c\u4f46\u6253\u4e86\u4e00\u65e5\uff0c\u5fc3\u4e2d\u76f8\u4e92\u94a6\u4f69\uff0c\u5206\u624b\u4e4b\u65f6\uff0c\u5c45\u7136\u90fd\u7528\u4e0a\u4e86\u6b66\u6797\u4e2d\u6700\u606d\u656c\u7684\u793c\u8282\u3002 \u3000\u3000\u201c\u80e1\u4e00\u5200\u5f85\u654c\u4eba\u53bb\u540e\uff0c\u9971\u9910\u4e86\u4e00\u987f\uff0c\u9a91\u4e0a\u9a6c\u75be\u9a70\u800c\u53bb\u3002\u6211\u5fc3\u60f3\uff0c\u4ed6\u5fc5\u662f\u8981\u5230\u5357\u8fb9\u5927\u5c4b\u53bb\u7aa5\u63a2\u654c\u4eba\u52a8\u9759\uff0c\u8bf4\u4e0d\u5b9a\u8981\u6697\u65bd\u5077\u88ad\uff0c\u53ea\u8981\u5c06\u91d1\u9762\u4f5b\u4f24\u4e86\uff0c\u4f59\u4eba\u6ca1\u4e00\u4e2a\u662f\u4ed6\u5bf9\u624b\u3002\u6211\u6ee1\u5fc3\u8981\u60f3\u53bb\u8ddf\u7530\u76f8\u516c\u901a\u98ce\u62a5\u4fe1\uff0c\u53eb\u4ed6\u9632\u5907\uff0c\u53ea\u662f\u5bb3\u6015\u649e\u5230\u80e1\u4e00\u5200\uff0c\u5374\u53c8\u4e0d\u6562\u51fa\u5916\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u665a\u9694\u623f\u867d\u7136\u6ca1\u4eba\u6253\u9f3e\uff0c\u6211\u53ef\u4ecd\u662f\u7761\u4e0d\u5b89\u7a33\uff0c\u4e00\u76f4\u7559\u795e\u503e\u542c\u80e1\u4e00\u5200\u56de\u8f6c\u7684\u9a6c\u8e44\u58f0\u3002\u4f46\u5b88\u5230\u534a\u591c\uff0c\u8fd8\u662f\u6ca1\u6709\u58f0\u606f\u3002\u6211\u60f3\uff0c\u53bb\u5357\u8fb9\u5927\u5c4b\uff0c\u5feb\u9a6c\u5954\u9a70\uff0c\u4e0d\u7528\u4e00\u4e2a\u65f6\u8fb0\u4fbf\u53ef\u6765\u56de\uff0c\u96be\u9053\u4ed6\u7ed9\u91d1\u9762\u4f5b\u53d1\u89c9\u4e86\uff0c\u5be1\u4e0d\u654c\u4f17\uff0c\u56e0\u800c\u4e27\u547d\uff1f \u3000\u3000\u201c\u4ed6\u8d8a\u662f\u8fdf\u5f52\uff0c\u6211\u8d8a\u662f\u4e0d\u653e\u5fc3\uff0c\u4f46\u542c\u9694\u58c1\u623f\u91cc\u592b\u4eba\u8f7b\u8f7b\u5531\u7740\u6b4c\u513f\u54c4\u5b69\u5b50\uff0c\u5374\u4e00\u70b9\u4e0d\u4e3a\u4e08\u592b\u62c5\u5fc3\uff0c\u53c8\u89c9\u5f97\u5947\u602a\u3002 \u3000\u3000\u201c\u5230\u540e\u6765\u6668\u9e21\u62a5\u6653\uff0c\u4e94\u66f4\u5929\u65f6\uff0c\u80e1\u4e00\u5200\u9a91\u7740\u9a6c\u56de\u6765\u4e86\u3002\u6211\u6025\u5fd9\u8d77\u6765\uff0c\u53ea\u89c1\u4ed6\u7684\u5750\u9a91\u5df2\u6362\u4e86\u4e00\u5339\uff0c\u53bb\u65f6\u9a91\u9752\u9a6c\uff0c\u56de\u6765\u65f6\u9a91\u7684\u5374\u662f\u9ec4\u9a6c\u3002\u90a3\u9ec4\u9a6c\u5954\u5230\u5e97\u524d\uff0c\u80e1\u4e00\u5200\u4e00\u8dc3\u843d\u978d\uff0c\u90a3\u9a6c\u6643\u4e86\u51e0\u4e0b\uff0c\u6251\u5730\u5012\u4e86\uff0c\u53e3\u5410\u767d\u6cab\u800c\u6b7b\u3002\u6211\u8fc7\u53bb\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u9a6c\u5168\u8eab\u5927\u6c57\u6dcb\u6f13\uff0c\u539f\u6765\u662f\u7d2f\u6b7b\u7684\u3002\u77a7\u8fd9\u60c5\u5f62\uff0c\u8fd9\u4e00\u665a\u4ed6\u7adf\u957f\u9014\u8dcb\u6d89\uff0c\u4e0d\u77e5\u53bb\u4e86\u4f55\u5904\u3002\u6211\u5fc3\u60f3\uff1a\u4eca\u65e5\u4ed6\u8fd8\u8981\u8ddf\u91d1\u9762\u4f5b\u62da\u6597\uff0c\u6628\u665a\u4e0d\u597d\u597d\u5b89\u7761\uff0c\u517b\u597d\u6c14\u529b\u4ee5\u5907\u5927\u6218\uff0c\u5374\u53bb\u7d2f\u4e86\u4e00\u665a\uff0c\u771f\u662f\u4e2a\u602a\u4eba\u3002 \u3000\u3000\u201c\u8fd9\u65f6\u592b\u4eba\u4e5f\u5df2\u8d77\u6765\uff0c\u53c8\u505a\u4e86\u4e00\u684c\u83dc\u3002\u80e1\u4e00\u5200\u7adf\u4e0d\u518d\u7761\uff0c\u5c06\u5b69\u5b50\u4e00\u629b\u4e00\u629b\u7684\u73a9\u5f04\u3002\u5f85\u5f97\u5929\u8272\u5927\u660e\uff0c\u91d1\u9762\u4f5b\u53c8\u4e0e\u7530\u76f8\u516c\u7b49\u6765\u4e86\u3002\u82d7\u80e1\u4e24\u4eba\u5bf9\u559d\u4e86\u4e09\u7897\u9152\uff0c\u6ca1\u8bf4\u4ec0\u4e48\u8bdd\uff0c\u8e22\u5f00\u51f3\u5b50\uff0c\u62bd\u51fa\u5200\u5251\u5c31\u52a8\u624b\u3002\u6253\u5230\u5929\u9ed1\uff0c\u4e24\u4eba\u6536\u5175\u884c\u793c\u3002\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u80e1\u5144\uff0c\u4f60\u4eca\u65e5\u529b\u6c14\u5dee\u4e86\uff0c\u660e\u65e5\u53ea\u6015\u8981\u8f93\u3002\u2019\u80e1\u4e00\u5200\u9053\uff1a\u2018\u90a3\u4e5f\u672a\u5fc5\u3002\u6628\u665a\u6211\u6ca1\u7761\u89c9\uff0c\u4eca\u665a\u5b89\u7761\u4e00\u5bb5\uff0c\u6c14\u529b\u5c31\u957f\u4e86\u3002\u2019\u91d1\u9762\u4f5b\u5947\u9053\uff1a\u2018\u6628\u665a\u6ca1\u7761\u89c9\uff1f\u90a3\u4e0d\u5bf9\u3002\u2019\u201c\u80e1\u4e00\u5200\u7b11\u9053\uff1a\u2018\u82d7\u5144\uff0c\u6211\u9001\u4f60\u4e00\u4ef6\u7269\u4e8b\u3002\u2019\u4ece\u623f\u91cc\u63d0\u51fa\u4e00\u4e2a\u5305\u88f9\uff0c\u63b7\u4e86\u8fc7\u53bb\u3002\u91d1\u9762\u4f5b\u63a5\u8fc7\uff0c\u89e3\u5f00\u4e00\u770b\uff0c\u539f\u6765\u662f\u4e2a\u5272\u4e0b\u7684\u9996\u7ea7\uff0c\u9996\u7ea7\u4e4b\u65c1\u8fd8\u6709\u4e03\u679a\u91d1\u9556\u3002\u8303\u5e2e\u4e3b\u5411\u90a3\u9996\u7ea7\u671b\u4e86\u4e00\u773c\uff0c\u60ca\u53eb\u9053\uff1a\u2018\u662f\u516b\u5366\u5200\u5546\u5251\u9e23\uff01\u2019\u91d1\u9762\u4f5b\u62ff\u8d77\u4e00\u679a\u91d1\u9556\uff0c\u5728\u624b\u91cc\u6382\u4e86\u4e00\u6382\uff0c\u4efd\u91cf\u5f88\u6c89\uff0c\u89c1\u9556\u8eab\u4e0a\u523b\u7740\u56db\u5b57\uff1a\u2018\u516b\u5366\u95e8\u5546\u2019\uff0c\u8bf4\u9053\uff1a\u2018\u6628\u665a\u4f60\u8d76\u5230\u5c71\u4e1c\u6b66\u5b9a\u53bf\u4e86\uff1f\u2019\u80e1\u4e00\u5200\u7b11\u9053\uff1a\u2018\u7d2f\u6b7b\u4e86\u4e94\u5339\u9a6c\uff0c\u603b\u7b97\u6ca1\u8bef\u4e86\u4f60\u7684\u7ea6\u4f1a\u3002\u2019\u201c\u6211\u53c8\u60ca\u53c8\u6015\uff0c\u6014\u6014\u7684\u671b\u7740\u80e1\u4e00\u5200\u3002\u4ece\u76f4\u96b6\u6ca7\u5dde\u5230\u5c71\u4e1c\u6b66\u5b9a\uff0c\u76f8\u53bb\u8fd1\u4e09\u767e\u91cc\uff0c\u4ed6\u4e00\u591c\u4e4b\u95f4\u6765\u56de\uff0c\u8fd8\u5272\u4e86\u4e00\u4e2a\u6b66\u6797\u5927\u8c6a\u7684\u9996\u7ea7\uff0c\u8fd9\u4eba\u884c\u4e8b\u5f53\u771f\u662f\u795e\u51fa\u9b3c\u6ca1\u3002 \u3000\u3000\u201c\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u4f60\u7528\u4ec0\u4e48\u5200\u6cd5\u6740\u4ed6\uff1f\u2019\u80e1\u4e00\u5200\u9053\uff1a\u2018\u6b64\u4eba\u7684\u516b\u5366\u5200\u529f\u592b\uff0c\u786e\u662f\u4e86\u5f97\uff0c\u6211\u63a5\u4f4f\u4e86\u4ed6\u4e03\u679a\u8fde\u73e0\u9556\uff0c\u8ddf\u7740\u7528\u201c\u51b2\u5929\u638c\u82cf\u79e6\u80cc\u5251\u201d\u8fd9\u4e00\u62db\uff0c\u7834\u4e86\u4ed6\u516b\u5366\u5200\u6cd5\u7b2c\u4e8c\u5341\u4e5d\u62db\u201c\u53cd\u8eab\u5288\u5c71\u201d\u3002\u2019\u91d1\u9762\u4f5b\u4e00\u6014\uff0c\u5947\u9053\uff1a\u2018\u51b2\u5929\u638c\u82cf\u79e6\u80cc\u5251\uff1f\u8fd9\u662f\u6211\u82d7\u5bb6\u5251\u6cd5\u554a\uff1f\u2019\u80e1\u4e00\u5200\u7b11\u9053\uff1a\u2018\u6b63\u662f\uff0c\u90a3\u662f\u6211\u6628\u5929\u4ece\u4f60\u8fd9\u513f\u5077\u5b66\u6765\u7684\u529f\u592b\u3002\u6211\u4e0d\u7528\u5200\uff0c\u662f\u7528\u5251\u6740\u4ed6\u7684\u3002\u2019\u201c\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u597d\uff01\u4f60\u66ff\u82d7\u5bb6\u62a5\u4ec7\uff0c\u7528\u7684\u662f\u82d7\u5bb6\u5251\u6cd5\uff0c\u8db3\u89c1\u76db\u60c5\u3002\u2019\u80e1\u4e00\u5200\u7b11\u9053\uff1a\u2018\u4f60\u82d7\u5bb6\u5251\u72ec\u6b65\u5929\u4e0b\uff0c\u4ee5\u6b64\u5251\u6cd5\u6740\u4ed6\u4f55\u96be\uff0c\u5728\u4e0b\u53ea\u662f\u4ee3\u52b3\u800c\u5df2\u3002\u2019\u201c\u6211\u8fd9\u65f6\u65b9\u624d\u660e\u767d\uff0c\u80e1\u4e00\u5200\u662f\u5904\u5904\u5c0a\u91cd\u91d1\u9762\u4f5b\u3002\u5546\u5251\u9e23\u5bb3\u4e86\u82d7\u5bb6\u56db\u4eba\uff0c\u80e1\u4e00\u5200\u82e5\u662f\u7528\u5200\u5c06\u4ed6\u6740\u4e86\uff0c\u5c82\u975e\u663e\u5f97\u82d7\u5bb6\u5251\u4e0d\u5982\u516b\u5366\u5200\uff1f\u66f4\u52a0\u4e0d\u5982\u80e1\u5bb6\u5200\u6cd5\uff1f\u53ea\u662f\u4ed6\u4e00\u65e5\u4e4b\u95f4\uff0c\u80fd\u5b66\u5f97\u82d7\u5bb6\u5251\u7684\u7edd\u62db\uff0c\u7528\u4ee5\u6740\u4e86\u53e6\u4e00\u4e2a\u6b66\u5b66\u540d\u5bb6\uff0c\u8fd9\u756a\u529f\u592b\u5b9e\u4e0d\u7531\u5f97\u4ee4\u4eba\u4e0d\u4e3a\u4e4b\u5fc3\u5bd2\u3002\u4ed6\u76f4\u5230\u8fd9\u65e5\u6597\u5b8c\uff0c\u624d\u62ff\u51fa\u9996\u7ea7\u6765\uff0c\u6beb\u65e0\u5c45\u529f\u5356\u597d\u4e4b\u610f\uff0c\u66f4\u662f\u5927\u65b9\u78ca\u843d\uff0c\u800c\u5176\u81ea\u6043\u4e0d\u8d25\uff0c\u4e5f\u5df2\u660e\u663e\u5f97\u5f88\u4e86\u3002 \u3000\u3000\u201c\u6211\u60f3\u5230\u6b64\u8282\uff0c\u8303\u7530\u4e24\u4eba\u65e9\u5df2\u60f3\u5230\u3002\u4e24\u4eba\u8138\u8272\u82cd\u767d\uff0c\u4e92\u76f8\u4f7f\u4e86\u4e2a\u773c\u8272\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002\u91d1\u9762\u4f5b\u671b\u671b\u592b\u4eba\u624b\u91cc\u62b1\u7740\u7684\u5b69\u5b50\uff0c\u89e3\u4e0b\u80cc\u4e0a\u7684\u9ec4\u5305\u88b1\uff0c\u6253\u4e86\u5f00\u6765\u3002\u6211\u5fc3\u60f3\u8fd9\u91cc\u9762\u4e0d\u77e5\u88c5\u7740\u4e9b\u4ec0\u4e48\u53e4\u602a\u7269\u4e8b\uff0c\u4f38\u957f\u4e86\u8116\u5b50\u4e00\u77a7\uff0c\u5374\u89c1\u5305\u88b1\u91cc\u53ea\u662f\u51e0\u4ef6\u5bfb\u5e38\u8863\u886b\u3002 \u3000\u3000\u91d1\u9762\u4f5b\u5c06\u90a3\u5757\u9ec4\u5e03\u4e00\u6296\uff0c\u77a7\u7740\u5e03\u4e0a\u7ee3\u7740\u7684\u4e03\u4e2a\u5b57\uff0c\u4f4e\u58f0\u9053\uff1a\u2018\u563f\uff0c\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\uff01\u80e1\u5439\u5927\u6c14\uff01\u2019\u4f38\u624b\u62b1\u8fc7\u5b69\u5b50\uff0c\u5c06\u9ec4\u5e03\u5305\u5728\u4ed6\u8eab\u4e0a\uff0c\u5bf9\u80e1\u4e00\u5200\u9053\uff1a\u2018\u80e1\u5144\uff0c\u82e5\u662f\u4f60\u6709\u751a\u4e09\u957f\u4e24\u77ed\uff0c\u522b\u62c5\u5fc3\u8fd9\u5b69\u5b50\u6709\u4eba\u6562\u6b3a\u4fae\u4ed6\u3002\u2019\u80e1\u4e00\u5200\u5927\u559c\uff0c\u8fde\u8fde\u79f0\u8c22\u3002 \u3000\u3000\u201c\u91d1\u9762\u4f5b\u53bb\u540e\uff0c\u80e1\u4e00\u5200\u53c8\u9971\u9910\u4e86\u4e00\u987f\uff0c\u8fd9\u624d\u7761\u89c9\uff0c\u8fd9\u4e00\u7761\u4e0b\u6765\uff0c\u9f3e\u58f0\u66f4\u662f\u60ca\u5929\u52a8\u5730\u3002 \u3000\u3000\u201c\u5f85\u5230\u4e8c\u66f4\u65f6\u5206\uff0c\u5ffd\u542c\u5c4b\u9876\u4e0a\u811a\u6b65\u58f0\u54cd\uff0c\u6709\u4eba\u53eb\u9053\uff1a\u2018\u80e1\u4e00\u5200\uff0c\u5feb\u6eda\u51fa\u6765\u9886\u6b7b\uff01\u2019\u80e1\u4e00\u5200\u5e76\u6ca1\u60ca\u9192\uff0c\u4ecd\u662f\u9f3e\u58f0\u5927\u4f5c\u3002\u4e0d\u4e45\u559d\u9a82\u58f0\u8d8a\u6765\u8d8a\u54cd\uff0c\u4eba\u4e5f\u8d8a\u6765\u8d8a\u591a\u3002\u80e1\u4e00\u5200\u5982\u804b\u4e86\u4e00\u822c\uff0c\u53ea\u662f\u6c89\u7761\u3002\u6211\u60f3\u6b64\u4eba\u6b66\u827a\u867d\u9ad8\uff0c\u5374\u662f\u592a\u4e0d\u673a\u7075\uff0c\u5c4b\u5916\u6765\u4e86\u8bb8\u591a\u654c\u4eba\uff0c\u7adf\u7136\u6beb\u4e0d\u60ca\u89c9\u3002\u4f46\u8bf4\u4e5f\u5947\u602a\uff0c\u80e1\u4e00\u5200\u56fa\u7136\u6ca1\u6709\u542c\u89c1\uff0c\u592b\u4eba\u660e\u660e\u9192\u7740\uff0c\u5374\u53ea\u4f4e\u58f0\u54fc\u6b4c\u513f\u54c4\u5b69\u5b50\uff0c\u5bf9\u7a97\u5916\u5c4b\u9876\u7684\u53eb\u56b7\uff0c\u4e5f\u662f\u7f6e\u4e4b\u4e0d\u7406\u3002 \u3000\u3000\u201c\u5c4b\u5916\u90a3\u4e9b\u4eba\u5c3d\u662f\u5435\u56b7\uff0c\u5374\u53c8\u4e0d\u6562\u95ef\u8fdb\u5c4b\u6765\uff0c\u80e1\u4e00\u5200\u5219\u53ea\u7ba1\u6253\u9f3e\u3002\u5c4b\u5185\u5c4b\u5916\u4e00\u5531\u4e00\u548c\uff0c\u54cd\u6210\u4e00\u7247\u3002\u5435\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u592b\u4eba\u5ffd\u7136\u67d4\u58f0\u8bf4\u9053\uff1a\u2018\u5b69\u5b50\uff0c\u5916\u8fb9\u6709\u8bb8\u591a\u91ce\u72d7\uff0c\u60f3\u5420\u53eb\u4e00\u591c\uff0c\u5435\u5f97\u7239\u7239\u7761\u4e0d\u6210\u89c9\uff0c\u6559\u4ed6\u660e\u513f\u8ddf\u82d7\u4f2f\u4f2f\u6bd4\u6b66\u8f93\u4e86\u3002\u4f60\u8bf4\u8fd9\u7fa4\u91ce\u72d7\u574f\u4e0d\u574f\uff1f\u2019\u5b69\u5b50\u751f\u4e0b\u6765\u8fd8\u53ea\u51e0\u5929\uff0c\u81ea\u7136\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u53ea\u662f\u4f0a\u4f0a\u554a\u554a\u51e0\u58f0\u3002\u592b\u4eba\u9053\uff1a\u2018\u771f\u662f\u4e56\u5b69\u5b50\uff0c\u4f60\u4e5f\u8bf4\u91ce\u72d7\u574f\u3002\u8ba9\u5988\u5988\u53bb\u8d76\u8d70\u4e86\uff0c\u597d\u4e0d\u597d\uff1f\u2019\u90a3\u5b69\u5b50\u53c8\u662f\u554a\u554a\u51e0\u58f0\u3002\u592b\u4eba\u9053\uff1a\u2018\u55ef\uff0c\u4f60\u4e5f\u8bf4\u597d\uff0c\u771f\u4e0d\u6789\u4e86\u7239\u5988\u75bc\u4f60\u3002\u2019\u5979\u5de6\u624b\u62b1\u4e86\u5b69\u5b50\uff0c\u53f3\u624b\u4ece\u5e8a\u5934\u62ff\u8d77\u4e00\u6839\u7ef8\u5e26\uff0c\u63a8\u5f00\u7a97\u5b50\uff0c\u55d6\u7684\u4e00\u4e0b\uff0c\u8dc3\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u201c\u6211\u5927\u5403\u4e00\u60ca\uff0c\u77a7\u4e0d\u51fa\u8fd9\u6837\u5a07\u6ef4\u6ef4\u7684\u4e00\u4e2a\u5973\u5b50\uff0c\u8f7b\u529f\u7adf\u5982\u6b64\u4e86\u5f97\u3002\u6211\u5fd9\u8d70\u5230\u7a97\u8fb9\uff0c\u5728\u7a97\u683c\u7eb8\u4e0a\u523a\u4e86\u4e00\u4e2a\u5b54\u3002\u5411\u5916\u5f20\u671b\uff0c\u53ea\u89c1\u5c4b\u9762\u4e0a\u9ad8\u9ad8\u77ee\u77ee\uff0c\u7ad9\u4e86\u4e8c\u4e09\u5341\u6761\u5927\u6c49\uff0c\u624b\u4e2d\u90fd\u62ff\u4e86\u5175\u5203\uff0c\u6b63\u5728\u5927\u58f0\u5406\u559d\u3002\u592b\u4eba\u53f3\u624b\u4e00\u6325\uff0c\u4e00\u6761\u767d\u7ef8\u5e26\u5982\u957f\u86c7\u4e5f\u4f3c\u7684\u4f38\u4e86\u51fa\u53bb\uff0c\u5377\u4f4f\u4e00\u6761\u5927\u6c49\u624b\u4e0a\u7684\u5355\u5200\uff0c\u4e00\u593a\u4e00\u653e\uff0c\u90a3\u5927\u6c49\u53eb\u58f0\u554a\u54df\uff0c\u5355\u5200\u8131\u624b\uff0c\u8eab\u5b50\u5374\u4ece\u5c4b\u9762\u4e0a\u6454\u4e86\u4e0b\u53bb\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u8dcc\u5728\u5730\u4e0b\u3002 \u3000\u3000\u201c\u5176\u4f59\u7684\u6c49\u5b50\u54d7\u7136\u53eb\u56b7\uff0c\u7eb7\u7eb7\u6251\u4e0a\u3002\u6708\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u592b\u4eba\u624b\u4e2d\u7684\u767d\u7ef8\u5e26\u5c31\u5982\u662f\u4e00\u6761\u767d\u9f99\uff0c\u76d8\u65cb\u98de\u821e\uff0c\u7eb5\u6a2a\u4e0a\u4e0b\uff0c\u4f46\u542c\u5f97\u545b\u5577\u3001\u545b\u5577\u3001\u554a\u54df\u3001\u554a\u54df\u3001\u7830\u84ec\u3001\u7830\u84ec\u4e4b\u58f0\u8fde\u54cd\uff0c\u4e0d\u5230\u4e00\u987f\u996d\u529f\u592b\uff0c\u51e0\u5341\u6761\u6c49\u5b50\u7684\u5175\u5203\u5168\u8ba9\u592b\u4eba\u7528\u7ef8\u5e26\u593a\u4e0b\uff0c\u4eba\u90fd\u6454\u4e0b\u4e86\u5c4b\u9876\u3002\u8fd9\u4e9b\u4eba\u54ea\u6562\u518d\u6597\uff0c\u722c\u8d77\u8eab\u6765\u4fbf\u9003\uff0c\u6709\u4e9b\u8fde\u9a6c\u4e5f\u4e0d\u6562\u9a91\uff0c\u628a\u7272\u53e3\u6487\u4e0b\u4e5f\u4e0d\u8981\u4e86\u3002\u53ea\u628a\u6211\u77a7\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u5fc3\u60ca\u8089\u8df3\u3002\u592b\u4eba\u5c06\u90a3\u4e9b\u5175\u5203\u4ece\u5c4b\u9876\u8e22\u5728\u5730\u4e0b\uff0c\u4e5f\u4e0d\u6361\u62fe\uff0c\u62b1\u4e86\u5b69\u5b50\u8fdb\u5c4b\u5582\u5976\u3002\u80e1\u4e00\u5200\u59cb\u7ec8\u9f3e\u58f0\u5982\u96f7\uff0c\u4f3c\u4e4e\u6d51\u4e0d\u77e5\u6709\u8fd9\u4e00\u56de\u4e8b\u3002 \u3000\u3000\u201c\u6b21\u65e5\u65e9\u6668\uff0c\u592b\u4eba\u505a\u4e86\u83dc\uff0c\u547d\u5e97\u4f34\u62fe\u8d77\u5175\u5203\uff0c\u7528\u7ef3\u5b50\u7cfb\u4f4f\uff0c\u4e00\u4ef6\u4ef6\u90fd\u6302\u5728\u5c4b\u6a90\u4e0b\uff0c\u5317\u98ce\u4e00\u5439\uff0c\u5200\u5566\u3001\u5251\u5566\u3001\u9524\u5566\u3001\u97ad\u5566\uff0c\u76f8\u4e92\u649e\u51fb\uff0c\u53ee\u53ee\u5f53\u5f53\u7684\u5341\u5206\u597d\u542c\u3002 \u3000\u3000\u201c\u5403\u8fc7\u65e9\u996d\uff0c\u91d1\u9762\u4f5b\u53c8\u6765\u5566\u3002\u4ed6\u542c\u5f97\u58f0\u97f3\uff0c\u62ac\u5934\u4e00\u77a7\uff0c\u89c1\u4e86\u8fd9\u4e9b\u5175\u5203\uff0c\u5df2\u77e5\u539f\u59d4\uff0c\u5411\u8ddf\u968f\u4ed6\u6765\u7684\u4f17\u4eba\u72e0\u72e0\u77aa\u4e86\u4e00\u773c\u3002\u90a3\u4e9b\u4eba\u4f4e\u4e86\u5934\u4e0d\u6562\u77a7\u4ed6\u3002\u91d1\u9762\u4f5b\u9a82\u9053\uff1a\u2018\u4e0d\u8981\u8138\uff01\u7b97\u4ec0\u4e48\u7537\u5b50\u6c49\uff1f \u3000\u3000\u90fd\u7ed9\u6211\u6eda\u5f00\uff01\u2019\u90a3\u4e9b\u4eba\u4e0d\u6562\u4f5c\u58f0\uff0c\u90fd\u9000\u4e86\u51e0\u6b65\u3002\u6211\u60f3\uff0c\u592b\u4eba\u6628\u665a\u82e5\u8981\u6740\u4e86\u8fd9\u4e9b\u4eba\uff0c\u5f53\u771f\u6613\u5982\u53cd\u638c\uff0c\u5c31\u7b97\u5c06\u4ed6\u4eec\u4e00\u4e00\u70b9\u5012\uff0c\u8eba\u5728\u5730\u4e0b\uff0c\u4e5f\u662f\u6beb\u4e0d\u4e3a\u96be\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u4e00\u6765\uff0c\u672a\u514d\u524a\u4e86\u91d1\u9762\u4f5b\u7684\u8138\u9762\u3002 \u3000\u3000\u201c\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u80e1\u5144\uff0c\u8fd9\u6279\u6ca1\u51fa\u606f\u7684\u5bb6\u4f19\u5435\u5f97\u4f60\u96be\u4ee5\u5b89\u7761\u3002 \u3000\u3000\u54b1\u4eec\u4eca\u65e5\u505c\u6218\uff0c\u4f60\u597d\u597d\u7761\u4e00\u89c9\uff0c\u660e\u65e5\u518d\u6bd4\u3002\u2019\u80e1\u4e00\u5200\u7b11\u9053\uff1a\u2018\u662f\u5185\u4eba\u6253\u53d1\u7684\uff0c\u5144\u5f1f\u7761\u7740\u4e0d\u77e5\u3002\u6765\u5427\uff01\u2019\u5355\u5200\u4e00\u632f\uff0c\u7acb\u4e2a\u95e8\u6237\u3002 \u3000\u3000\u201c\u91d1\u9762\u4f5b\u5411\u80e1\u592b\u4eba\u9053\uff1a\u2018\u591a\u627f\u592b\u4eba\u624b\u4e0b\u5bb9\u60c5\uff0c\u9976\u4e86\u8fd9\u4e9b\u5bb6\u4f19\u7684\u6027\u547d\u3002\u2019\u592b\u4eba\u5fae\u5fae\u4e00\u7b11\u3002\u80e1\u4e00\u5200\u4e0e\u82d7\u4eba\u51e4\u4e24\u4eba\u5ba2\u6c14\u51e0\u53e5\uff0c\u968f\u5373\u5200\u5251\u76f8\u4ea4\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u65e5\u6253\u5230\u5929\u9ed1\uff0c\u4ecd\u662f\u4e0d\u5206\u80dc\u8d1f\u3002\u91d1\u9762\u4f5b\u6536\u5251\u9053\uff1a\u2018\u80e1\u5144\uff0c\u4eca\u65e5\u5144\u5f1f\u4e0d\u56de\u53bb\u5566\u3002\u60f3\u8ddf\u4f60\u75db\u996e\u4e00\u756a\uff0c\u7136\u540e\u62b5\u8db3\u800c\u7720\uff0c\u8c08\u8bba\u6b66\u827a\u3002\u2019\u80e1\u4e00\u5200\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u2018\u5999\u6781\uff0c\u5999\u6781\u3002\u5144\u5f1f\u53c2\u7814\u82d7\u5144\u5251\u6cd5\uff0c\u5c1a\u6709\u8bb8\u591a\u4e0d\u660e\u4e4b\u5904\uff0c\u4eca\u665a\u6b63\u597d\u9886\u6559\u3002\u2019\u91d1\u9762\u4f5b\u5411\u8303\u5e2e\u4e3b\u3001\u7530\u76f8\u516c\u9053\uff1a\u2018\u4f60\u4eec\u8d70\u5427\uff0c\u4eca\u665a\u6211\u4f4f\u5728\u8fd9\u91cc\u3002\u2019\u201c\u8303\u5e2e\u4e3b\u4e0d\u7531\u5f97\u5927\u60ca\u5931\u8272\uff0c\u8bf4\u9053\uff1a\u2018\u82d7\u5927\u4fa0\uff0c\u5c0f\u5fc3\u4ed6\u7684\u5978\u8ba1\u2026\u2026\u2019\u91d1\u9762\u4f5b\u51b7\u7136\u9053\uff1a\u2018\u6211\u7231\u600e\u4e48\u4fbf\u600e\u4e48\uff0c\u4f60\u7ba1\u5f97\u7740\uff1f\u2019\u7530\u76f8\u516c\u9053\uff1a\u2018\u4f60\u522b\u5fd8\u4e86\u6740\u7236\u4e4b\u4ec7\uff0c\u505a\u4e2a\u4e0d\u5b5d\u5b50\u5b59\u3002\u2019\u91d1\u9762\u4f5b\u8138\u4e00\u6c89\u3002\u8303\u7530\u4e8c\u4eba\u4e0d\u6562\u518d\u8bf4\uff0c\u5e26\u7740\u4f17\u4eba\u8d70\u4e86\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u665a\u4e24\u4eba\u4e00\u9762\u559d\u9152\uff0c\u4e00\u9762\u8c08\u8bba\u6b66\u529f\u3002\u91d1\u9762\u4f5b\u5c06\u82d7\u5bb6\u5251\u7684\u7cbe\u8981\uff0c\u4e00\u62db\u4e00\u5f0f\u8bb2\u7ed9\u80e1\u4e00\u5200\u542c\u3002\u80e1\u4e00\u5200\u4e5f\u628a\u80e1\u5bb6\u5200\u6cd5\u503e\u56ca\u4ee5\u6388\u3002\u4e24\u4eba\u8d8a\u8c08\u8d8a\u6295\u673a\uff0c\u771f\u8bf4\u5f97\u4e0a\u662f\u76f8\u89c1\u6068\u665a\u3002\u4e24\u4eba\u559d\u51e0\u7897\u9152\uff0c\u7ad9\u8d77\u6765\u8bd5\u6f14\u51e0\u62db\uff0c\u53c8\u5750\u4e0b\u559d\u9152\u3002\u4ed6\u4e8c\u4eba\u8c08\u8bba\u7684\u90fd\u662f\u6700\u7cbe\u6df1\u7684\u529f\u592b\uff0c\u6211\u867d\u6e05\u6e05\u695a\u695a\u7684\u542c\u5728\u8033\u91cc\uff0c\u5374\u4e00\u53e5\u4e5f\u4e0d\u61c2\u3002 \u3000\u3000\u201c\u8bf4\u5230\u534a\u591c\uff0c\u80e1\u4e00\u5200\u53eb\u638c\u67dc\u7684\u5f00\u4e86\u4e00\u95f4\u4e0a\u623f\uff0c\u4ed6\u548c\u91d1\u9762\u4f5b\u5f53\u771f\u540c\u69bb\u800c\u7720\u3002\u6211\u6697\u81ea\u5bfb\u601d\uff1a\u2018\u4e24\u4e2a\u6d3b\u4eba\u8fdb\u623f\uff0c\u660e\u65e5\u623f\u4e2d\u5b9a\u7136\u6709\u4e2a\u6b7b\u4eba\uff0c\u5374\u4e0d\u77e5\u8c01\u5148\u4e0b\u624b\uff1f\u91d1\u9762\u4f5b\u4f3c\u4e4e\u4e0d\u662f\u5978\u9669\u5c0f\u4eba\uff0c\u8fd9\u4e00\u56de\u4ed6\u53ef\u8981\u7cdf\u4e86\u3002\u2019\u201c\u540e\u6765\u8f6c\u5ff5\u53c8\u60f3\uff0c\u80e1\u4e00\u5200\u7c97\u8c6a\u5364\u83bd\uff0c\u8fdc\u4e0d\u5982\u91d1\u9762\u4f5b\u7cbe\u7ec6\u3002 \u3000\u3000\u4e24\u4eba\u6b66\u529f\u867d\u7136\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u4f46\u8bf4\u5230\u6597\u667a\u5f04\u5de7\uff0c\u5b9a\u662f\u91d1\u9762\u4f5b\u80dc\u4e86\u4e00\u7b79\u3002\u90a3\u4e48\u660e\u65e5\u6d3b\u7740\u51fa\u6765\u7684\uff0c\u60f3\u5fc5\u662f\u91d1\u9762\u4f5b\u800c\u4e0d\u662f\u80e1\u4e00\u5200\u4e86\u3002 \u3000\u3000\u201c\u6211\u597d\u5947\u5fc3\u8d77\uff0c\u6084\u6084\u8d70\u5230\u4ed6\u4eec\u623f\u5916\u7a97\u8fb9\u5077\u542c\u3002\u90a3\u65f6\u4e24\u4eba\u8c08\u8bba\u7684\u5df2\u4e0d\u662f\u6b66\u529f\uff0c\u800c\u662f\u6c5f\u6e56\u4e0a\u7684\u5947\u95fb\u79d8\u4e8b\uff0c\u548c\u4e24\u4eba\u5f80\u65e5\u7684\u6240\u4f5c\u6240\u4e3a\u3002\u6709\u65f6\u91d1\u9762\u4f5b\u8bf4\u5728\u4ec0\u4e48\u5730\u65b9\u6740\u4e86\u4e00\u4e2a\u51f6\u5f92\uff0c\u6709\u65f6\u80e1\u4e00\u5200\u8bf4\u5728\u4ec0\u4e48\u65f6\u5019\u6551\u4e86\u4e00\u4e2a\u82e6\u4eba\uff0c\u8bf4\u5230\u75db\u5feb\u5904\uff0c\u4e00\u9f50\u62cd\u638c\u5927\u7b11\u3002 \u3000\u3000\u53ea\u628a\u6211\u542c\u5f97\u5f20\u5927\u4e86\u53e3\u5408\u4e0d\u62e2\u6765\u3002\u6211\u60f3\u80e1\u4e00\u5200\u7a77\u51f6\u6781\u6076\uff0c\u505a\u8fd9\u4e9b\u4e8b\u5e76\u4e0d\u5947\u602a\uff0c\u4f46\u91d1\u9762\u4f5b\u7684\u5916\u53f7\u4e2d\u6709\u4e2a\u2018\u4f5b\u2019\u5b57\uff0c\u7adf\u7136\u4e5f\u662f\u8fd9\u822c\u7684\u6740\u4eba\u4e0d\u7728\u773c\u3002 \u3000\u3000\u201c\u8bf4\u5230\u540e\u6765\uff0c\u91d1\u9762\u4f5b\u5ffd\u7136\u53f9\u9053\uff1a\u2018\u53ef\u60dc\u554a\u53ef\u60dc\uff01\u2019\u80e1\u4e00\u5200\u9053\uff1a\u2018\u53ef\u60dc\u4ec0\u4e48\uff1f\u2019\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u5018\u82e5\u4f60\u4e0d\u59d3\u80e1\uff0c\u6216\u662f\u6211\u4e0d\u59d3\u82d7\uff0c\u54b1\u4fe9\u5b9a\u7136\u7ed3\u6210\u751f\u6b7b\u4e4b\u4ea4\u3002\u6211\u82d7\u4eba\u51e4\u4e00\u5411\u81ea\u8d1f\u5f97\u7d27\uff0c\u8fd9\u4e00\u56de\u89c1\u4e86\u4f60\uff0c\u90a3\u53ef\u771f\u662f\u53e3\u670d\u5fc3\u670d\u4e86\u3002\u5509\uff0c\u5929\u4e0b\u867d\u5927\uff0c\u9664\u4e86\u80e1\u4e00\u5200\uff0c\u82d7\u4eba\u51e4\u518d\u65e0\u53ef\u4ea4\u4e4b\u4eba\u3002\u2019\u80e1\u4e00\u5200\u9053\uff1a\u2018\u6211\u82e5\u6b7b\u5728\u4f60\u624b\u91cc\uff0c\u4f60\u53ef\u548c\u6211\u5185\u4eba\u65f6\u5e38\u8c08\u8c08\u3002\u5979\u662f\u5973\u4e2d\u8c6a\u6770\uff0c\u8fdc\u80dc\u4f60\u90a3\u4e9b\u80c6\u5c0f\u9b3c\u670b\u53cb\u3002\u2019\u91d1\u9762\u4f5b\u6012\u9053\uff1a\u2018\u54fc\uff0c\u8fd9\u4e9b\u5bb6\u4f19\u54ea\u91cc\u914d\u5f97\u4e0a\u505a\u6211\u670b\u53cb\uff1f\u2019\u201c\u4ed6\u4eec\u8bf4\u6765\u8bf4\u53bb\uff0c\u603b\u662f\u4e0d\u6d89\u53ca\u4e0a\u4ee3\u7ed3\u4ec7\u4e4b\u4e8b\u3002\u5076\u5c14\u6709\u4eba\u628a\u8bdd\u5e26\u5f97\u8fd1\u4e86\uff0c\u53e6\u4e00\u4e2a\u7acb\u5373\u5c06\u8bdd\u5934\u5c94\u5f00\u3002\u8fd9\u4e00\u665a\u4e24\u4eba\u7adf\u6ca1\u7761\u89c9\uff0c\u7d2f\u5f97\u6211\u4e5f\u5728\u7a97\u5916\u7ad9\u4e86\u534a\u591c\u3002\u9662\u5b50\u91cc\u5bd2\u98ce\u523a\u9aa8\uff0c\u628a\u6211\u4e24\u53ea\u811a\u51bb\u5f97\u6ca1\u4e86\u77e5\u89c9\u3002\u5230\u5929\u8272\u5927\u660e\uff0c\u91d1\u9762\u4f5b\u5ffd\u7136\u8d70\u5230\u7a97\u8fb9\uff0c\u51b7\u7b11\u9053\uff1a\u2018\u54fc\uff0c\u542c\u591f\u4e86\u4e48\uff1f\u2019\u4f46\u542c\u5f97\u683c\u7684\u4e00\u54cd\uff0c\u80e1\u4e00\u5200\u9053\uff1a\u2018\u82d7\u5144\uff0c\u6b64\u4eba\u8fd8\u597d\uff0c\u9976\u4e86\u4ed6\u5427\uff01\u2019\u6211\u53ea\u89c9\u5f97\u5934\u4e0a\u88ab\u4ec0\u4e48\u4e1c\u897f\u4e00\u649e\uff0c\u767b\u65f6\u660f\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u201c\u5f85\u5f97\u9192\u8f6c\uff0c\u6211\u5df2\u7761\u5728\u81ea\u5df1\u7095\u4e0a\uff0c\u8fc7\u4e86\u8001\u534a\u5929\uff0c\u8fd9\u624d\u60f3\u8d77\uff0c\u5b9a\u662f\u91d1\u9762\u4f5b\u53d1\u89c9\u6211\u5728\u5916\u5077\u542c\uff0c\u5f00\u7a97\u6253\u4e86\u6211\u4e00\u62f3\u3002\u82e5\u975e\u80e1\u4e00\u5200\u4ee3\u6211\u6c42\u60c5\uff0c\u6211\u8fd9\u6761\u5c0f\u547d\u662f\u65e9\u5df2\u4e0d\u5728\u4e86\u3002\u6211\u722c\u4e0b\u7095\u6765\uff0c\u53ea\u89c9\u5f97\u8111\u5b50\u660f\u660f\u6c89\u6c89\u7684\uff0c\u62ff\u955c\u5b50\u4e00\u7167\uff0c\u534a\u8fb9\u8138\u5168\u6210\u4e86\u7d2b\u8272\uff0c\u80bf\u8d77\u4e00\u5bf8\u6765\u9ad8\u3002\u6211\u5413\u4e86\u4e00\u5927\u8df3\uff0c\u5f53\u5577\u4e00\u58f0\uff0c\u955c\u5b50\u6389\u5728\u5730\u4e0b\u6454\u5f97\u7c89\u788e\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u65e5\u4ed6\u4e8c\u4eba\u5728\u5802\u4e0a\u6bd4\u6b66\uff0c\u6211\u4e0d\u6562\u518d\u51fa\u53bb\u77a7\uff0c\u672c\u6765\u6211\u4e00\u76f4\u76fc\u671b\u91d1\u9762\u4f5b\u5f97\u80dc\uff0c\u4f46\u8138\u4e0a\u80bf\u8d77\u5904\u9635\u9635\u53d1\u75bc\uff0c\u8fd9\u65f6\u5374\u53ea\u60f3\u80e1\u4e00\u5200\u7ed9\u6211\u62a5\u4ec7\uff0c\u5728\u82d7\u4eba\u51e4\u8eab\u4e0a\u780d\u4ed6\u5988\u7684\u4e00\u4e24\u5200\u3002\u5230\u5f97\u5929\u9ed1\uff0c\u9694\u7740\u677f\u58c1\u542c\u5f97\u91d1\u9762\u4f5b\u8bf4\u9053\uff1a\u2018\u80e1\u5144\uff0c\u6211\u539f\u60f3\u4eca\u665a\u518d\u8ddf\u4f60\u8054\u5e8a\u591c\u8bdd\uff0c\u53ea\u662f\u751f\u6015\u5ac2\u592b\u4eba\u602a\u8d23\u3002\u660e\u665a\u82e5\u662f\u4ecd\u65e7\u4e0d\u5206\u80dc\u8d25\uff0c\u54b1\u4eec\u518d\u8c08\u4e00\u591c\u5982\u4f55\uff1f\u2019\u80e1\u4e00\u5200\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u2018\u597d\uff0c\u597d\u3002\u2019\u201c\u91d1\u9762\u4f5b\u8f9e\u53bb\u540e\uff0c\u592b\u4eba\u659f\u4e86\u4e00\u7897\u9152\uff0c\u9012\u7ed9\u80e1\u4e00\u5200\uff0c\u8bf4\u9053\uff1a\u2018\u606d\u559c\u5927\u54e5\u3002\u2019\u80e1\u4e00\u5200\u63a5\u8fc7\u7897\u6765\uff0c\u4e00\u53e3\u559d\u5e72\u4e86\uff0c\u7b11\u9053\uff1a\u2018\u606d\u559c\u4ec0\u4e48\uff1f\u2019\u592b\u4eba\u9053\uff1a\u2018\u660e\u5929\u4f60\u53ef\u6253\u8d25\u91d1\u9762\u4f5b\u4e86\u3002\u2019\u80e1\u4e00\u5200\u6115\u7136\u9053\uff1a\u2018\u6211\u8ddf\u4ed6\u62c6\u4e86\u6570\u5343\u62db\uff0c\u59cb\u7ec8\u77a7\u4e0d\u51fa\u534a\u70b9\u7834\u7efd\uff0c\u660e\u5929\u600e\u80fd\u80dc\u4ed6\uff1f\u2019\u592b\u4eba\u5fae\u7b11\u9053\uff1a\u2018\u6211\u5374\u770b\u51fa\u4e86\u4e00\u70b9\u6bdb\u75c5\u3002\u5b69\u5b50\uff0c\u4f60\u7239\u624d\u662f\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u554a\u3002\u2019\u5979\u6700\u540e\u4e00\u53e5\u8bdd\u5374\u662f\u5411\u5b69\u5b50\u8bf4\u7684\u3002 \u3000\u3000\u201c\u80e1\u4e00\u5200\u5fd9\u95ee\uff1a\u2018\u4ec0\u4e48\u6bdb\u75c5\uff1f\u600e\u4e48\u6211\u6ca1\u77a7\u51fa\u6765\uff1f\u2019\u592b\u4eba\u9053\uff1a\u2018\u4ed6\u8fd9\u6bdb\u75c5\u662f\u5728\u80cc\u540e\uff0c\u4f60\u8ddf\u4ed6\u6b63\u9762\u5bf9\u6218\uff0c\u81ea\u7136\u89c1\u4e0d\u5230\u3002\u2019\u80e1\u4e00\u5200\u6c89\u541f\u4e0d\u8bed\u3002\u592b\u4eba\u9053\uff1a\u2018\u4f60\u8ddf\u4ed6\u8fde\u6218\u56db\u5929\uff0c\u6211\u7ec6\u7ec6\u77a7\u4ed6\u7684\u5251\u8def\uff0c\u679c\u7136\u95e8\u6237\u4e25\u5bc6\uff0c\u6ca1\u5206\u6beb\u7834\u7efd\u3002\u6211\u770b\u5f97\u53c8\u60ca\u53c8\u6015\uff0c\u5fc3\u60f3\u957f\u6b64\u4e0b\u53bb\uff0c\u4f60\u603b\u6709\u4e2a\u758f\u795e\u5931\u624b\u7684\u65f6\u5019\uff0c\u800c\u4ed6\u5374\u59cb\u7ec8\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\u3002\u4f46\u5230\u4eca\u65e5\u4e0b\u5348\uff0c\u6211\u624d\u77a7\u51fa\u4e86\u4ed6\u7684\u6bdb\u75c5\u3002\u4ed6\u7684\u5251\u6cd5\u4e4b\u4e2d\uff0c\u4f60\u8bf4\u54ea\u51e0\u62db\u6700\u5389\u5bb3\uff1f\u2019\u80e1\u4e00\u5200\u9053\uff1a\u2018\u5389\u5bb3\u62db\u6570\u5f88\u591a\uff0c\u597d\u6bd4\u6d17\u5251\u6000\u4e2d\u62b1\u6708\u3001\u8fce\u95e8\u817f\u53cd\u5288\u534e\u5c71\u3001\u63d0\u64a9\u5251\u542f\u9e64\u8212\u7fc5\u3001\u51b2\u5929\u638c\u82cf\u79e6\u80cc\u5251\u2026\u2026\u201d\u592b\u4eba\u9053\uff1a\u2018\u6bdb\u75c5\u5c31\u662f\u51fa\u5728\u63d0\u64a9\u5251\u767d\u9e64\u8212\u7fc5\u8fd9\u4e00\u62db\u4e0a\u3002\u2019\u80e1\u4e00\u5200\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u4ee5\u653b\u4e3a\u5b88\uff0c\u521a\u4e2d\u6709\u67d4\uff0c\u72e0\u8fa3\u5f97\u7d27\u554a\u3002\u2019\u592b\u4eba\u9053\uff1a\u2018\u5927\u54e5\uff0c\u4f60\u7528\u7a7f\u624b\u85cf\u5200\u3001\u8fdb\u6b65\u8fde\u73af\u5200\u3001\u7f20\u8eab\u6458\u5fc3\u5200\u8fd9\u4e9b\u62db\u5f0f\u65f6\uff0c\u4ed6\u6709\u65f6\u4f1a\u7528\u63d0\u64a9\u5251\u767d\u9e64\u8212\u7fc5\u53cd\u51fb\u3002\u4f46\u4ed6\u5728\u51fa\u8fd9\u4e00\u62db\u4e4b\u524d\uff0c\u80cc\u5fc3\u5fc5\u5b9a\u5fae\u5fae\u4e00\u8038\uff0c\u4f3c\u4e4e\u6709\u70b9\u513f\u6015\u75d2\u3002\u2019\u201c\u80e1\u4e00\u5200\u5947\u9053\uff1a\u2018\u5f53\u771f\u5982\u6b64\uff1f\u2019\u592b\u4eba\u9053\uff1a\u2018\u4eca\u65e5\u4ed6\u524d\u540e\u4f7f\u4e86\u4e24\u6b21\uff0c\u6bcf\u6b21\u80cc\u5fc3\u5fc5\u8038\u3002\u660e\u65e5\u6bd4\u6b66\u4e4b\u65f6\uff0c\u6211\u89c1\u5230\u4ed6\u80cc\u5fc3\u4e00\u8038\uff0c\u7acb\u5373\u54b3\u55fd\uff0c\u90a3\u65f6\u4f60\u5236\u654c\u673a\u5148\uff0c\u4e0d\u5f85\u4ed6\u8fd9\u4e00\u62db\u4f7f\u51fa\uff0c\u62a2\u5148\u7528\u516b\u65b9\u85cf\u5200\u5f0f\u5f3a\u653b\uff0c\u4ed6\u975e\u64a4\u5251\u8ba4\u8f93\u4e0d\u53ef\u3002\u2019\u80e1\u4e00\u5200\u5927\u559c\uff0c\u8fde\u53eb\uff1a\u2018\u5999\u8ba1\uff01\u2019\u6211\u542c\u4e86\u4e24\u4eba\u8bf4\u8bdd\uff0c\u672c\u8be5\u53bb\u901a\u77e5\u91d1\u9762\u4f5b\uff0c\u53eb\u4ed6\u63d0\u9632\uff0c\u4f46\u4e00\u6478\u5230\u8138\u4e0a\u75bc\u5904\uff0c\u5fc3\u60f3\u4ed6\u51fb\u4e86\u6211\u8fd9\u4e00\u62f3\uff0c\u4f7f\u4e86\u5982\u6b64\u91cd\u624b\uff0c\u8f93\u4e86\u4e5f\u662f\u6d3b\u8be5\u3002 \u3000\u3000\u201c\u6b21\u65e5\u6bd4\u6b66\u662f\u7b2c\u4e94\u5929\u4e86\uff0c\u6211\u8138\u4e0a\u7684\u80bf\u7a0d\u7a0d\u9000\u4e86\u4e9b\uff0c\u53c8\u7ad9\u5728\u65c1\u8fb9\u89c2\u6218\u3002\u8fd9\u5929\u4e0a\u5348\u592b\u4eba\u6ca1\u6709\u54b3\u55fd\uff0c\u60f3\u662f\u91d1\u9762\u4f5b\u6ca1\u4f7f\u8fd9\u62db\u3002\u4e2d\u5348\u5403\u996d\u4e4b\u65f6\uff0c\u592b\u4eba\u7ed9\u4e08\u592b\u659f\u9152\uff0c\u8fde\u4f7f\u51e0\u4e2a\u773c\u8272\uff0c\u6211\u5728\u65c1\u77a7\u5f97\u6e05\u695a\uff0c\u77e5\u662f\u53eb\u4ed6\u8bf1\u903c\u91d1\u9762\u4f5b\u4f7f\u51fa\u6b64\u62db\uff0c\u4ee5\u4fbf\u4e58\u673a\u53d6\u80dc\u3002\u80e1\u4e00\u5200\u6447\u6447\u5934\uff0c\u4f3c\u4e4e\u5fc3\u4e2d\u4e0d\u5fcd\u3002\u592b\u4eba\u6307\u6307\u5b69\u5b50\uff0c\u5c06\u5b69\u5b50\u5728\u51f3\u4e0a\u91cd\u91cd\u4e00\u6454\uff0c\u5b69\u5b50\u5927\u54ed\u8d77\u6765\u3002\u6211\u660e\u767d\u5979\u7684\u7528\u610f\uff0c\u90a3\u662f\u8bf4\u4f60\u5982\u6bd4\u6b66\u5931\u624b\uff0c\u5b69\u5b50\u6ca1\u4e86\u7236\u4eb2\uff0c\u90a3\u53ef\u7ec8\u8eab\u53d7\u82e6\u4e86\u3002\u80e1\u4e00\u5200\u542c\u5230\u5b69\u5b50\u557c\u54ed\uff0c\u7f13\u7f13\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u201c\u5348\u540e\u4e24\u4eba\u4ea4\u624b\uff0c\u62c6\u4e86\u6570\u5341\u62db\u3002\u80e1\u4e00\u5200\u731b\u780d\u51e0\u5200\uff0c\u53ea\u542c\u5f97\u592b\u4eba\u54b3\u55fd\u4e00\u58f0\uff0c\u80e1\u4e00\u5200\u7709\u5934\u5fae\u76b1\uff0c\u4e0d\u8fdb\u53cd\u9000\uff0c\u91d1\u9762\u4f5b\u679c\u7136\u4f7f\u4e86\u4e00\u62db\u63d0\u64a9\u5251\u767d\u9e64\u8212\u7fc5\u3002\u8fd9\u4e00\u62db\u6211\u672c\u6765\u4e0d\u8bc6\uff0c\u4f46\u6628\u665a\u80e1\u4e00\u5200\u4e0e\u592b\u4eba\u7814\u5546\u5b9a\u8ba1\u4e4b\u65f6\uff0c\u66fe\u89c1\u592b\u4eba\u8fde\u4f7f\u51e0\u6b21\u3002\u6211\u5fc3\u60f3\uff1a\u2018\u592b\u4eba\u7684\u773c\u5149\u597d\u5389\u5bb3\u3002\u2019\u82e5\u662f\u80e1\u4e00\u5200\u4f9d\u5979\u4e4b\u8ba1\u884c\u4e8b\uff0c\u6b64\u65f6\u5df2\u7ecf\u80dc\u4e86\uff0c\u4f46\u4ed6\u7adf\u4e34\u65f6\u7f29\u624b\uff0c\u4e0d\u662f\u4ed6\u8d77\u4e86\u60fa\u60fa\u76f8\u60dc\u4e4b\u610f\u4e0d\u5fcd\u4f24\u5bb3\u91d1\u9762\u4f5b\uff0c\u90a3\u4fbf\u662f\u89c9\u5f97\u6709\u4eba\u5728\u65c1\u76f8\u52a9\uff0c\u80dc\u4e4b\u4e0d\u6b66\u3002\u6211\u5ffd\u7136\u60f3\u8d77\u80e1\u4e00\u5200\u66fe\u5631\u5490\u592b\u4eba\uff0c\u5c06\u6765\u5b69\u5b50\u957f\u5927\uff0c\u8981\u544a\u8bc9\u4ed6\u4e00\u53e5\u8bdd\uff0c\u53eb\u4ed6\u5fc3\u80a0\u72e0\u4e9b\u786c\u4e9b\uff0c\u770b\u6765\u80e1\u4e00\u5200\u9762\u8c8c\u867d\u7136\u51f6\u6076\uff0c\u5fc3\u80a0\u5374\u8f6f\uff0c\u4e8b\u5230\u4e34\u5934\uff0c\u5c45\u7136\u4e0b\u4e0d\u4e86\u624b\u3002 \u3000\u3000\u201c\u592b\u4eba\u5728\u5b69\u5b50\u624b\u81c2\u4e0a\u7528\u529b\u4e00\u634f\uff0c\u5b69\u5b50\u5927\u54ed\u8d77\u6765\u3002\u5200\u5251\u53ee\u5f53\u76f8\u4ea4\u58f0\u4e2d\uff0c\u6742\u7740\u5b69\u5b50\u7684\u54ed\u58f0\uff0c\u5ffd\u542c\u5f97\u563f\u7684\u4e00\u54cd\uff0c\u592b\u4eba\u53c8\u662f\u4e00\u58f0\u8f7b\u54b3\u3002\u80e1\u4e00\u5200\u8e0f\u4e0a\u4e00\u6b65\uff0c\u516b\u65b9\u85cf\u5200\u5f0f\uff0c\u5200\u5149\u95ea\u95ea\uff0c\u767b\u65f6\u628a\u91d1\u9762\u4f5b\u7684\u5251\u8def\u5c3d\u6570\u5c01\u4f4f\u3002 \u3000\u3000\u201c\u773c\u89c1\u5f97\u91d1\u9762\u4f5b\u65e0\u6cd5\u62b5\u6321\uff0c\u4ed6\u90a3\u62db\u63d0\u64a9\u5251\u767d\u9e64\u8212\u7fc5\u53ea\u4f7f\u5f97\u51fa\u534a\u62db\u3002\u6309\u90a3\u5251\u6cd5\uff0c\u4ed6\u53f3\u624b\u4e00\u5251\u659c\u523a\uff0c\u5de6\u624b\u4e0a\u626c\uff0c\u5c31\u4e0e\u767d\u9e64\u5c06\u53cc\u7fc5\u6251\u5f00\u6765\u4e00\u822c\uff0c\u4f46\u80e1\u4e00\u5200\u62a2\u4e86\u5148\u7740\uff0c\u91d1\u9762\u4f5b\u53cc\u624b\u521a\u8981\u5c55\u5f00\uff0c\u88ab\u4ed6\u5de6\u53f3\u8fde\u73af\u4e24\u5200\uff0c\u91d1\u9762\u4f5b\u8fd9\u5bf9\u81c2\u8180\uff0c\u5c82\u975e\u81ea\u884c\u9001\u5230\u5200\u4e0a\u53bb\u7ed9\u4ed6\u780d\u4e86\u4e0b\u6765\uff1f \u3000\u3000\u201c\u5c82\u77e5\u91d1\u9762\u4f5b\u7684\u6b66\u529f\uff0c\u5f53\u771f\u662f\u51fa\u795e\u5165\u5316\uff0c\u5c31\u5728\u8fd9\u5371\u6025\u4e4b\u95f4\uff0c\u4ed6\u53cc\u81c2\u4e00\u66f2\uff0c\u5251\u5c16\u9661\u7136\u523a\u5411\u81ea\u5df1\u80f8\u53e3\u3002\u80e1\u4e00\u5200\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u9053\u4ed6\u6bd4\u6b66\u8f93\u4e86\uff0c\u8fd8\u5251\u81ea\u6740\uff0c\u5fd9\u53eb\u9053\uff1a\u2018\u82d7\u5144\uff0c\u4e0d\u53ef\uff01\u2019\u201c\u6b8a\u4e0d\u77e5\u91d1\u9762\u4f5b\u7684\u5251\u5c16\u5728\u7b2c\u4e00\u65e5\u6bd4\u6b66\u4e4b\u65f6\u5c31\u5df2\u7528\u624b\u6307\u62d7\u65ad\u4e86\u7684\uff0c\u5251\u5c16\u672c\u8eab\u662f\u949d\u5934\uff0c\u4ed6\u518d\u80f8\u53e3\u4e00\u8fd0\u6c14\uff0c\u90a3\u5251\u523a\u5728\u8eab\u4e0a\uff0c\u7adf\u7136\u53cd\u5f39\u51fa\u6765\u3002\u8fd9\u4e00\u62db\u4e00\u6765\u53d8\u5316\u5947\u5e7b\uff0c\u4e8c\u6765\u80e1\u4e00\u5200\u4e00\u5fc3\u529d\u4ed6\u4e0d\u53ef\u81ea\u6740\uff0c\u4e1d\u6beb\u6ca1\u9632\u4ed6\u7adf\u662f\u51fa\u5947\u5236\u80dc\uff0c\u4f46\u89c1\u957f\u5251\u4e00\u5f39\uff0c\u5251\u67c4\u8e66\u5c06\u51fa\u6765\uff0c\u6b63\u597d\u70b9\u5728\u80e1\u4e00\u5200\u80f8\u53e3\u7684\u2018\u795e\u85cf\u7a74\u2019\u4e0a\u3002 \u3000\u3000\u201c\u8fd9\u2018\u795e\u85cf\u7a74\u2019\u662f\u4eba\u8eab\u5927\u7a74\uff0c\u4e00\u88ab\u5251\u5c16\u70b9\u4e2d\uff0c\u80e1\u4e00\u5200\u767b\u65f6\u8f6f\u5012\u3002\u91d1\u9762\u4f5b\u4f38\u624b\u6276\u4f4f\uff0c\u53eb\u9053\uff1a\u2018\u5f97\u7f6a\uff01\u2019\u80e1\u4e00\u5200\u7b11\u9053\uff1a\u2018\u82d7\u5144\u5251\u6cd5\uff0c\u9b3c\u795e\u83ab\u6d4b\uff0c\u4f69\u670d\u4f69\u670d\u3002\u2019\u91d1\u9762\u4f5b\u9053\uff1a\u2018\u82e5\u975e\u80e1\u5144\u597d\u610f\u5173\u5fc3\uff0c\u6b64\u62db\u4f55\u80fd\u5f97\u624b\uff1f\u2019\u4e24\u4eba\u5750\u5728\u684c\u8fb9\u4e00\u53e3\u6c14\u5e72\u4e86\u4e09\u7897\u70e7\u9152\u3002\u80e1\u4e00\u5200\u54c8\u54c8\u4e00\u7b11\uff0c\u63d0\u8d77\u5200\u6765\u5f80\u81ea\u5df1\u9888\u4e2d\u4e00\u62b9\uff0c\u54bd\u5589\u4e2d\u55b7\u51fa\u9c9c\u8840\uff0c\u4f0f\u684c\u800c\u6b7b\u3002 \u3000\u3000\u201c\u6211\u60ca\u5f97\u5446\u4e86\uff0c\u770b\u592b\u4eba\u65f6\uff0c\u5979\u8138\u4e0a\u7adf\u65e0\u60b2\u75db\u4e4b\u8272\uff0c\u53ea\u9053\uff1a\u2018\u82d7\u5927\u4fa0\uff0c\u8bf7\u4f60\u7a0d\u5f85\uff0c\u6211\u518d\u5582\u4e00\u6b21\u5976\uff0c\u8ba9\u5b69\u5b50\u5403\u5f97\u9971\u9971\u7684\u3002\u2019\u8d70\u8fdb\u623f\u53bb\uff0c\u8fc7\u4e86\u4e00\u987f\u996d\u65f6\u5206\uff0c\u91cd\u53c8\u51fa\u6765\uff0c\u5728\u5b69\u5b50\u8138\u4e0a\u6df1\u6df1\u4e00\u543b\uff0c\u7b11\u9053\uff1a\u2018\u4ed6\u5403\u9971\u4e86\u7761\u7740\u5566\u3002\u2019\u5c06\u5b69\u5b50\u4ea4\u7ed9\u91d1\u9762\u4f5b\uff0c\u9053\uff1a\u2018\u6211\u672c\u7b54\u5e94\u54b1\u5bb6\u5927\u54e5\uff0c\u8981\u4eb2\u624b\u628a\u5b69\u5b50\u517b\u5927\uff0c\u4f46\u8fd9\u4e94\u5929\u4e4b\u4e2d\uff0c\u4eb2\u89c1\u82d7\u5927\u4fa0\u809d\u80c6\u7167\u4eba\uff0c\u4e49\u91cd\u5982\u5c71\uff0c\u4f60\u65e2\u7b54\u5141\u7167\u987e\u5b69\u5b50\uff0c\u6211\u5c31\u5077\u4e00\u4e0b\u61d2\uff0c\u4e0d\u6328\u8fd9\u4e8c\u5341\u5e74\u7684\u82e6\u695a\u4e86\u3002\u2019\u8bf4\u7740\u5411\u91d1\u9762\u4f5b\u798f\u4e86\u51e0\u798f\uff0c\u62ff\u8fc7\u80e1\u4e00\u5200\u7684\u5200\u6765\uff0c\u4e5f\u662f\u5728\u9888\u4e0a\u4e00\u5272\u3002\u592b\u59bb\u4fe9\u5e76\u6392\u5750\u5728\u4e00\u6761\u957f\u51f3\u4e0a\uff0c\u592b\u4eba\u62c9\u7740\u80e1\u4e00\u5200\u7684\u624b\uff0c\u8eab\u5b50\u6162\u6162\u8f6f\u5012\uff0c\u4f0f\u5728\u4e08\u592b\u8eab\u4e0a\uff0c\u5c31\u6b64\u4e0d\u52a8\u4e86\u3002\u6211\u4e0d\u5fcd\u518d\u770b\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u89c1\u82d7\u5927\u4fa0\u81c2\u4e2d\u62b1\u7740\u7684\u5b69\u5b50\u7761\u5f97\u6b63\u6c89\uff0c\u5c0f\u8138\u513f\u4e0a\u4f3c\u4e4e\u8fd8\u9732\u7740\u4e00\u4e1d\u5fae\u7b11\u3002\u201d \u3000"}),showOnHomepage:!0},{name:"\u4e94",content:(0,a.jsx)(a.Fragment,{children:"\u5b9d\u6811\u8bf4\u5b8c\u8fd9\u6545\u4e8b\uff0c\u5927\u5385\u4e2d\u9759\u5bc2\u65e0\u58f0\u3002\u7fa4\u8c6a\u867d\u7136\u90fd\u662f\u5fc3\u80a0\u521a\u786c\u4e4b\u4eba\uff0c\u4f46\u542c\u4e86\u80e1\u4e00\u5200\u592b\u5987\u6177\u6168\u5c31\u6b7b\u7684\u4e8b\u8ff9\uff0c\u4e0d\u7531\u5f97\u5747\u611f\u607b\u7136\u3002 \u3000\u3000\u5ffd\u542c\u4e00\u4e2a\u5973\u5b50\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5b9d\u6811\u5927\u5e08\uff0c\u600e\u4e48\u6211\u542c\u5230\u7684\u6545\u4e8b\uff0c\u5374\u8ddf\u4f60\u8bf4\u7684\u6709\u70b9\u513f\u4e0d\u540c\u5462\uff1f\u201d \u3000\u3000\u4f17\u4eba\u4e00\u9f50\u8f6c\u8fc7\u5934\u6765\uff0c\u89c1\u8bf4\u8bdd\u7684\u662f\u82d7\u82e5\u5170\u3002\u5927\u5bb6\u51dd\u795e\u503e\u542c\u5b9d\u6811\u8ff0\u8bf4\uff0c\u90fd\u6ca1\u7559\u5fc3\u5979\u4f55\u65f6\u53c8\u56de\u5230\u4e86\u5385\u4e0a\u3002 \u3000\u3000\u5b9d\u6811\u9053\uff1a\u201c\u5e74\u4ee3\u4e45\u8fdc\uff0c\u53ea\u6015\u6709\u4e9b\u5730\u65b9\u662f\u8001\u8872\u8bb0\u9519\u4e86\u3002\u5374\u4e0d\u77e5\u4ee4\u5c0a\u662f\u600e\u4e48\u8bf4\uff1f\u201d\u82d7\u82e5\u5170\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u7239\u7239\u66fe\u539f\u539f\u672c\u672c\u5bf9\u6211\u8bf4\u8fc7\u3002\u8d77\u5148\u7684\u4e8b\uff0c\u4e5f\u8ddf\u5927\u5e08\u8bf4\u7684\u4e00\u6837\uff0c\u53ea\u662f\u80e1\u4e00\u5200\u4f2f\u4f2f\u548c\u80e1\u4f2f\u6bcd\u901d\u4e16\u7684\u60c5\u666f\uff0c\u5374\u4e0e\u5927\u5e08\u6240\u8bf4\u5927\u4e0d\u76f8\u540c\u3002\u201d \u3000\u3000\u5b9d\u6811\u8138\u8272\u5fae\u53d8\uff0c\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u5374\u4e0d\u8ffd\u95ee\u3002\u7530\u9752\u6587\u9053\uff1a\u201c\u82d7\u59d1\u5a18\uff0c\u4ee4\u5c0a\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u82d7\u82e5\u5170\u4ece\u8eab\u8fb9\u4e00\u53ea\u9526\u7f0e\u76d2\u5b50\u4e2d\u53d6\u51fa\u4e00\u6839\u6de1\u7070\u8272\u7ebf\u9999\uff0c\u71c3\u7740\u4e86\u63d2\u5165\u9999\u7089\u3002\u4f17\u4eba\u968f\u5373\u95fb\u5230\u4e00\u7f15\u5e7d\u5e7d\u6e05\u9999\u3002\u82d7\u82e5\u5170\u8138\u4e0a\u795e\u8272\u5e84\u4e25\u8083\u7a46\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4ece\u5c0f\u89c1\u7239\u7239\u6bcf\u5230\u51ac\u5929\uff0c\u603b\u662f\u663e\u5f97\u90c1\u90c1\u4e0d\u4e50\uff0c\u4e0d\u8bba\u6211\u600e\u4e48\u9017\u4ed6\u6b22\u559c\uff0c\u90fd\u96be\u5f97\u5f15\u4ed6\u53d1\u7b11\u3002\u6bcf\u5e74\u5feb\u8fc7\u5e74\u7684\u65f6\u5019\uff0c\u7239\u7239\u603b\u8981\u5728\u4e00\u95f4\u5c0f\u5ba4\u91cc\u4f9b\u4e24\u4e2a\u795e\u4f4d\uff0c\u4e00\u4e2a\u5199\uff1a\u2018\u4e49\u5144\u80e1\u516c\u4e00\u5200\u5927\u4fa0\u4e4b\u7075\u4f4d\u2019\uff0c\u53e6\u4e00\u4e2a\u5199\uff1a\u2018\u4e49\u5ac2\u80e1\u592b\u4eba\u4e4b\u7075\u4f4d\u2019\uff0c\u7075\u4f4d\u65c1\u8fb9\u8fd8\u653e\u4e86\u4e00\u67c4\u5355\u5200\uff0c\u8fd9\u628a\u5200\u751f\u6ee1\u4e86\u94c1\u9508\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u7279\u5f02\u3002\u7239\u7239\u53eb\u53a8\u5b50\u505a\u4e86\u6ee1\u684c\u83dc\uff0c\u5012\u5341\u51e0\u7897\u9152\uff0c\u4ece\u5341\u4e8c\u6708\u5eff\u4e8c\u8d77\uff0c\u4e00\u8fde\u4e94\u5929\uff0c\u4ed6\u6bcf\u665a\u5728\u7075\u4f4d\u8fb9\u559d\u8fd9\u5341\u51e0\u7897\u9152\uff0c\u559d\u5230\u540e\u6765\uff0c\u5e38\u5e38\u75db\u54ed\u4e00\u573a\u3002 \u3000\u3000\u201c\u8d77\u521d\u6211\u95ee\u7239\u7239\uff0c\u7075\u4f4d\u4e0a\u90a3\u4f4d\u80e1\u4f2f\u4f2f\u662f\u8c01\uff0c\u7239\u7239\u603b\u662f\u6447\u5934\u3002 \u3000\u3000\u6709\u4e00\u5e74\u7239\u7239\u8bf4\u6211\u5e74\u7eaa\u5927\u4e86\uff0c\u80fd\u61c2\u4e8b\u5566\uff0c\u4e8e\u662f\u628a\u4ed6\u8ddf\u80e1\u4f2f\u4f2f\u6bd4\u6b66\u7684\u6545\u4e8b\u8bf4\u7ed9\u6211\u542c\u3002\u6bd4\u6b66\u7684\u7ecf\u8fc7\uff0c\u5b9d\u6811\u5927\u5e08\u8bf4\u5f97\u5f88\u8be6\u7ec6\u4e86\u3002 \u3000\u3000\u201c\u7239\u7239\u8ddf\u80e1\u4f2f\u4f2f\u4e00\u8fde\u6bd4\u4e86\u56db\u5929\uff0c\u4e24\u4eba\u8d8a\u6253\u8d8a\u662f\u6295\u5951\uff0c\u8c01\u4e5f\u4e0d\u613f\u4f24\u4e86\u5bf9\u65b9\u3002\u5230\u7b2c\u4e94\u5929\u4e0a\uff0c\u80e1\u4f2f\u6bcd\u77a7\u51fa\u7239\u7239\u80cc\u540e\u7684\u7834\u7efd\uff0c\u4e00\u58f0\u54b3\u55fd\uff0c\u80e1\u4f2f\u4f2f\u7acb\u4f7f\u516b\u65b9\u85cf\u5200\u5f0f\uff0c\u5c06\u6211\u7239\u7239\u5236\u4f4f\u3002\u5b9d\u6811\u5927\u5e08\u8bf4\u6211\u7239\u7239\u5ffd\u4f7f\u602a\u62db\uff0c\u80dc\u4e86\u80e1\u4f2f\u4f2f\u3002\u4f46\u7239\u7239\u8bf4\u7684\u5374\u4e0d\u662f\u8fd9\u6837\u3002\u5f53\u65f6\u80e1\u4f2f\u4f2f\u62a2\u4e86\u5148\u7740\uff0c\u7239\u7239\u53ea\u597d\u675f\u624b\u5f85\u6bd9\uff0c\u65e0\u6cd5\u8fd8\u624b\u3002\u80e1\u4f2f\u4f2f\u7a81\u7136\u5411\u540e\u8dc3\u5f00\uff0c\u8bf4\u9053\uff1a\u2018\u82d7\u5144\uff0c\u6211\u6709\u4e00\u4e8b\u4e0d\u89e3\u3002\u2019\u7239\u7239\u8bf4\u9053\uff1a\u2018\u662f\u6211\u8f93\u4e86\u3002\u4f60\u8981\u95ee\u4ec0\u4e48\u4e8b\uff1f\u2019\u201c\u80e1\u4f2f\u4f2f\u9053\uff1a\u2018\u4f60\u8fd9\u5251\u6cd5\u53cd\u590d\u6570\u5343\u62db\uff0c\u7edd\u65e0\u534a\u70b9\u7834\u7efd\uff0c\u4e3a\u4ec0\u4e48\u5728\u4f7f\u63d0\u64a9\u5251\u767d\u9e64\u8212\u7fc5\u8fd9\u4e00\u62db\u4e4b\u524d\uff0c\u80cc\u4e0a\u5374\u8981\u5fae\u5fae\u4e00\u8038\uff0c\u4ee5\u81f4\u88ab\u5185\u4eba\u770b\u7834\uff1f\u2019\u7239\u7239\u53f9\u9053\uff1a\u2018\u5148\u7236\u6559\u6211\u5251\u6cd5\u4e4b\u65f6\uff0c\u7763\u7387\u6781\u4e25\u3002 \u3000\u3000\u5f53\u6211\u5341\u4e00\u5c81\u90a3\u5e74\uff0c\u5148\u7236\u6b63\u6559\u5230\u8fd9\u4e00\u62db\uff0c\u80cc\u4e0a\u5ffd\u6709\u86a4\u5b50\u54ac\u6211\uff0c\u5947\u75d2\u96be\u5f53\u3002\u6211\u4e0d\u6562\u4f38\u624b\u6414\u75d2\uff0c\u53ea\u597d\u8038\u52a8\u80cc\u810a\uff0c\u60f3\u628a\u86a4\u5b50\u8d76\u5f00\uff0c\u4f46\u8d8a\u8038\u8d8a\u75d2\uff0c\u96be\u8fc7\u4e4b\u6781\u3002\u5148\u7236\u770b\u5230\u6211\u7684\u602a\u6837\uff0c\u8bf4\u6211\u5b66\u5251\u4e0d\u7528\u5fc3\uff0c\u72e0\u72e0\u6253\u4e86\u6211\u4e00\u987f\u3002\u8fd9\u4ef6\u4e8b\u6211\u6df1\u5370\u8111\u6d77\uff0c\u81ea\u6b64\u4ee5\u540e\uff0c\u6bcf\u5f53\u4f7f\u5230\u8fd9\u4e00\u62db\uff0c\u6211\u80cc\u4e0a\u867d\u7136\u4e0d\u75d2\uff0c\u5374\u4e5f\u4e60\u60ef\u6210\u81ea\u7136\uff0c\u603b\u662f\u8038\u4e0a\u4e00\u8038\u3002 \u3000\u3000\u5c0a\u592b\u4eba\u5f53\u771f\u597d\u773c\u529b\u3002\u2019\u80e1\u4f2f\u4f2f\u7b11\u9053\uff1a\u2018\u6211\u6709\u5185\u4eba\u76f8\u52a9\uff0c\u4e0d\u80fd\u7b97\u8d62\u4e86\uff01\u63a5\u4f4f\u4e86\u3002\u2019\u8bf4\u7740\u5c06\u624b\u4e2d\u5355\u5200\u629b\u7ed9\u7239\u7239\u3002 \u3000\u3000\u201c\u7239\u7239\u63a5\u4e86\u5355\u5200\uff0c\u4e0d\u660e\u4ed6\u7684\u7528\u610f\u3002\u80e1\u4f2f\u4f2f\u4ece\u7239\u7239\u624b\u91cc\u53d6\u8fc7\u957f\u5251\uff0c\u8bf4\u9053\uff1a\u2018\u7ecf\u8fc7\u8fd9\u56db\u5929\u7684\u5207\u8e49\uff0c\u4f60\u6211\u7684\u6b66\u529f\u76f8\u4e92\u90fd\u5df2\u4e86\u7136\u4e8e\u80f8\u3002\u8fd9\u6837\u5427\uff0c\u6211\u4f7f\u82d7\u5bb6\u5251\u6cd5\uff0c\u4f60\u4f7f\u80e1\u5bb6\u5200\u6cd5\uff0c\u54b1\u4fe9\u518d\u51b3\u80dc\u8d1f\u3002\u4e0d\u8bba\u8c01\u80dc\u8c01\u8d25\uff0c\u90fd\u4e0d\u635f\u4e86\u5a01\u540d\u3002\u2019\u201c\u6211\u7239\u7239\u4e00\u542c\u6b64\u8a00\uff0c\u5df2\u77e5\u4ed6\u7684\u5fc3\u610f\u3002\u6211\u82d7\u5bb6\u4e0e\u80e1\u5bb6\u7d2f\u4e16\u6df1\u4ec7\uff0c\u662f\u767e\u4f59\u5e74\u524d\u7956\u5b97\u79ef\u4e0b\u6765\u7684\u3002\u6211\u7239\u7239\u8ddf\u80e1\u4f2f\u4f2f\u4ee5\u524d\u4ece\u6ca1\u4f1a\u8fc7\u9762\uff0c\u672c\u8eab\u5e76\u65e0\u4ec7\u6028\u3002\u6c5f\u6e56\u4e0a\u56fa\u7136\u4eba\u8a00\u7c4d\u7c4d\uff0c\u6211\u7956\u7236\u548c\u7530\u5f52\u519c\u53d4\u53d4\u7684\u7236\u4eb2\u7a81\u7136\u540c\u65f6\u4e0d\u77e5\u6240\u8e2a\uff0c\u8fde\u5c38\u9aa8\u4e5f\u4e0d\u5f97\u8fd8\u4e61\uff0c\u90fd\u662f\u80e1\u4e00\u5200\u4e0b\u7684\u6bd2\u624b\uff0c\u6211\u7239\u7239\u5374\u662f\u5c06\u4fe1\u5c06\u7591\uff0c\u7d20\u95fb\u80e1\u4f2f\u4f2f\u884c\u4fa0\u4ed7\u4e49\uff0c\u6240\u4f5c\u6240\u4e3a\u5f88\u4ee4\u4eba\u4f69\u670d\uff0c\u4f3c\u4e4e\u4e0d\u81f4\u4e8e\u6697\u7b97\u5bb3\u4eba\uff0c\u53ea\u662f\u51e0\u756a\u8981\u548c\u4ed6\u76f8\u89c1\uff0c\u59cb\u7ec8\u4e0d\u80fd\u5982\u613f\u3002\u7530\u53d4\u53d4\u3001\u8303\u5e2e\u4e3b\u66fe\u9080\u7239\u7239\u540c\u53bb\u8fbd\u4e1c\u5bfb\u4ec7\uff0c\u6211\u7239\u7239\u8ddf\u8303\u5e2e\u4e3b\u662f\u4ea4\u60c5\u5f88\u6df1\u7684\uff0c\u53ef\u662f\u4e00\u5411\u4e0d\u5927\u77a7\u5f97\u8d77\u7530\u53d4\u53d4\u7684\u4e3a\u4eba\u3002\u554a\u54df\uff0c\u7530\u59d0\u59d0\uff0c\u5bf9\u4e0d\u8d77\uff0c\u60a8\u522b\u89c1\u602a\uff0c\u8fd9\u662f\u6211\u7239\u7239\u8bf4\u7684\uff0c\u4ed6\u8bf4\u4ed6\u5b81\u53ef\u81ea\u884c\u5176\u662f\uff0c\u4e0d\u613f\u8ddf\u7530\u53d4\u53d4\u8054\u624b\u3002\u8fd9\u6b21\u542c\u5f97\u80e1\u4f2f\u4f2f\u6765\u5230\u4e2d\u539f\uff0c\u8fd9\u624d\u53d7\u8303\u7530\u4e24\u5bb6\u4e4b\u9080\uff0c\u5230\u6ca7\u5dde\u62e6\u4f4f\u80e1\u4f2f\u4f2f\u6bd4\u6b66\uff0c\u4f46\u9996\u5148\u5374\u8981\u5411\u80e1\u4f2f\u4f2f\u67e5\u95ee\u771f\u76f8\u3002 \u3000\u3000\u201c\u540e\u6765\u4e00\u95ee\u4e4b\u4e0b\uff0c\u6211\u7956\u7236\u4e0e\u7530\u516c\u516c\u679c\u7136\u662f\u80e1\u4f2f\u4f2f\u5bb3\u7684\u3002\u6211\u7239\u7239\u867d\u7231\u60dc\u4ed6\u82f1\u96c4\uff0c\u4f46\u7236\u4ec7\u4e0d\u80fd\u4e0d\u62a5\u3002\u53ea\u662f\u6211\u7239\u7239\u5b9e\u5728\u4e0d\u613f\u8ba9\u8fd9\u56db\u5bb6\u7684\u6028\u4ec7\u518d\u4e00\u4ee3\u4e00\u4ee3\u7684\u4f20\u7ed9\u5b50\u5b59\uff0c\u6781\u76fc\u5728\u81ea\u5df1\u624b\u4e2d\u4e86\u7ed3\u8fd9\u767e\u4f59\u5e74\u7684\u4e16\u4ec7\uff0c\u542c\u80e1\u4f2f\u4f2f\u8bf4\u8981\u4ea4\u6362\u5200\u5251\u6bd4\u6b66\uff0c\u6b63\u6295\u5176\u610f\u3002 \u3000\u3000\u56e0\u4e3a\u82e5\u662f\u6211\u7239\u7239\u80dc\u4e86\uff0c\u90a3\u662f\u4ed6\u7528\u80e1\u5bb6\u5200\u6253\u8d25\u82d7\u5bb6\u5251\uff0c\u5018\u82e5\u80e1\u4f2f\u4f2f\u5f97\u80dc\uff0c\u5219\u662f\u4ed6\u7528\u82d7\u5bb6\u5251\u6253\u8d25\u80e1\u5bb6\u5200\u3002\u80dc\u8d1f\u53ea\u5173\u4e2a\u4eba\uff0c\u4e0d\u7275\u6d89\u4e24\u5bb6\u6b66\u529f\u7684\u5a01\u540d\u3002 \u3000\u3000\u201c\u5f53\u4e0b\u4e24\u4eba\u6362\u4e86\u5200\u5251\uff0c\u4ea4\u8d77\u624b\u6765\u3002\u8fd9\u4e00\u573a\u62da\u6597\uff0c\u4e0e\u56db\u65e5\u6765\u7684\u82e6\u6218\u53c8\u81ea\u4e0d\u540c\u3002\u56e0\u4e3a\u4e24\u4eba\u867d\u7136\u90fd\u662f\u9ad8\u624b\uff0c\u4f46\u4f7f\u7684\u5175\u5203\u62db\u6570\u90fd\u4e0d\u987a\u4fbf\uff0c\u4f55\u51b5\u81ea\u5df1\u6240\u4f7f\u7684\u4e00\u62db\u4e00\u5f0f\uff0c\u5bf9\u65b9\u65e0\u4e0d\u70c2\u719f\u4e8e\u80f8\uff0c\u8981\u51ed\u8fd9\u56db\u5929\u4e4b\u4e2d\u4ece\u5bf9\u65b9\u5b66\u6765\u7684\u6b66\u529f\u514b\u654c\u5236\u80dc\uff0c\u90a3\u771f\u662f\u8c08\u4f55\u5bb9\u6613\uff1f \u3000\u3000\u6211\u7239\u7239\u8bf4\uff0c\u8fd9\u4e00\u5929\u7684\u6fc0\u6218\uff0c\u662f\u4ed6\u751f\u5e73\u6700\u51f6\u9669\u7684\u4e00\u6b21\u3002\u80e1\u4f2f\u4f2f\u8c8c\u4f3c\u7c97\u9c81\uff0c\u5176\u5b9e\u806a\u660e\u4e4b\u6781\uff0c\u5c06\u82d7\u5bb6\u5251\u6cd5\u65bd\u5c55\u5f00\u6765\uff0c\u7adf\u4f3c\u4e0b\u8fc7\u6570\u5e74\u82e6\u529f\u4e00\u822c\uff0c\u5355\u4ee5\u4ed6\u7528\u82d7\u5bb6\u5251\u7834\u53bb\u5c71\u4e1c\u5927\u8c6a\u5546\u5251\u9e23\u7684\u516b\u5366\u5200\uff0c\u5c31\u53ef\u60f3\u89c1\u5176\u4f59\u3002\u6211\u7239\u7239\u609f\u6027\u6ca1\u80e1\u4f2f\u4f2f\u9ad8\uff0c\u5e78\u597d\u4ed6\u5341\u516b\u822c\u6b66\u827a\u4ef6\u4ef6\u7686\u901a\uff0c\u80e1\u5bb6\u5200\u6cd5\u867d\u662f\u521d\u89c1\uff0c\u4f46\u5c11\u5e74\u65f6\u66fe\u7ec3\u8fc7\u5355\u5200\uff0c\u603b\u7b97\u5728\u8fd9\u70b9\u4e0a\u5360\u4e86\u4fbf\u5b9c\uff0c\u6240\u4ee5\u8fd8\u53ef\u8ddf\u4ed6\u6253\u6210\u5e73\u624b\u3002 \u3000\u3000\u201c\u6597\u5230\u5348\u540e\uff0c\u4e24\u4eba\u5404\u8d70\u6c89\u7a33\u51dd\u91cd\u7684\u8def\u5b50\uff0c\u51fa\u624b\u8d8a\u6765\u8d8a\u6162\u3002 \u3000\u3000\u80e1\u4f2f\u4f2f\u5ffd\u9053\uff1a\u2018\u82d7\u5144\uff0c\u4f60\u8fd9\u62db\u95ed\u95e8\u94c1\u6247\u5200\uff0c\u8fd8\u662f\u4f7f\u5f97\u592a\u5feb\u4e86\u4e9b\uff0c\u52b2\u529b\u4e0d\u957f\u3002\u2019\u6211\u7239\u7239\u9053\u2018\u591a\u627f\u6307\u6559\uff0c\u6211\u53ea\u9053\u5df2\u7ecf\u591f\u6162\u4e86\u3002\u2019\u4e24\u4eba\u5168\u795e\u62da\u6597\uff0c\u4f46\u5bf9\u65b9\u62db\u6570\u82e5\u6709\u4e0d\u5230\u4e4b\u5904\uff0c\u5374\u76f8\u4e92\u5f00\u8bda\u6307\u70b9\uff0c\u6beb\u4e0d\u85cf\u79c1\u3002\u7ffb\u7ffb\u6eda\u6eda\uff0c\u53c8\u6218\u6570\u767e\u56de\u5408\uff0c\u4e24\u4eba\u62db\u6570\u6e10\u81fb\u5706\u719f\u3002 \u3000\u3000\u201c\u6211\u7239\u7239\u89c1\u4ed6\u7684\u82d7\u5bb6\u5251\u6cd5\u8d8a\u4f7f\u8d8a\u7cbe\uff0c\u6697\u6697\u60ca\u5fc3\uff0c\u5bfb\u601d\uff1a \u3000\u3000\u2018\u4ed6\u5b66\u5251\u7684\u672c\u4e8b\u6bd4\u6211\u5b66\u5200\u7684\u672c\u4e8b\u597d\uff0c\u65f6\u95f4\u4e00\u957f\uff0c\u6211\u5c11\u5e74\u65f6\u6240\u7ec3\u7684\u5200\u6cd5\u6839\u57fa\u5c31\u8981\u4e0d\u7ba1\u7528\uff0c\u987b\u5f97\u7acb\u65f6\u53d8\u62db\uff0c\u5426\u5219\u5fc5\u8d25\u65e0\u7591\u3002\u2019\u5f53\u4e0b\u4f7f\u4e00\u62db\u2018\u6c99\u9e25\u63a0\u6ce2\u2019\uff0c\u672c\u6765\u662f\u5148\u780d\u4e0b\u624b\u5200\uff0c\u518d\u780d\u4e0a\u624b\u5200\uff0c\u4f46\u6211\u7239\u7239\u6545\u610f\u53d8\u62db\uff0c\u5148\u780d\u4e0a\u624b\u5200\uff0c\u518d\u780d\u4e0b\u624b\u5200\u3002 \u3000\u3000\u201c\u80e1\u4f2f\u4f2f\u4e00\u6014\uff0c\u521a\u8bf4\u5f97\u58f0\uff1a\u2018\u4e0d\u5bf9\uff01\u2019\u6211\u7239\u7239\u53eb\u9053\u2018\u770b\u5200\uff01\u2019\u5355\u5200\u9661\u7136\u7ffb\u8d77\uff0c\u7b2c\u4e8c\u5200\u4e0b\u624b\u5200\u7adf\u53c8\u53d8\u4e3a\u4e0a\u624b\u5200\u3002\u8fd9\u662f\u4ed6\u81ea\u521b\u7684\u5200\u6cd5\uff0c\u867d\u662f\u8131\u80ce\u4e8e\u80e1\u5bb6\u5200\u6cd5\uff0c\u4f46\u65b0\u5947\u53d8\u5e7b\uff0c\u4ee4\u4eba\u96be\u6d4b\u3002\u5018\u82e5\u8ddf\u4ed6\u5bf9\u6218\u7684\u662f\u53e6\u4e00\u4e2a\u9ad8\u624b\uff0c\u591a\u534a\u80fd\u907f\u8fc7\u8fd9\u62db\uff0c\u504f\u504f\u80e1\u4f2f\u4f2f\u719f\u77e5\u80e1\u5bb6\u5200\u6cd5\uff0c\u4e07\u6599\u4e0d\u5230\u6211\u7239\u7239\u4e34\u65f6\u53d8\u62db\uff0c\u65b0\u521b\u4e00\u5f0f\uff0c\u4e00\u4e2a\u63aa\u624b\u4e0d\u53ca\uff0c\u6211\u7239\u7239\u7684\u5200\u950b\u5df2\u5728\u4ed6\u5de6\u81c2\u4e0a\u5212\u4e86\u4e00\u9053\u53e3\u5b50\u3002 \u3000\u3000\u201c\u65c1\u89c2\u4f17\u4eba\uff0c\u4e00\u9f50\u60ca\u547c\uff0c\u80e1\u4f2f\u4f2f\u84e6\u5730\u98de\u51fa\u4e00\u817f\uff0c\u6211\u7239\u7239\u4e00\u4ea4\u6454\u51fa\uff0c\u8dcc\u5728\u5730\u4e0b\uff0c\u518d\u4e5f\u722c\u4e0d\u8d77\u6765\uff0c\u539f\u6765\u5df2\u88ab\u8e22\u4e2d\u4e86\u8170\u95f4\u7684\u2018\u4eac\u95e8\u7a74\u2019\u3002 \u3000\u3000\u201c\u8303\u5e2e\u4e3b\u3001\u7530\u76f8\u516c\u548c\u5176\u4ed6\u7684\u6c49\u5b50\u4e00\u9f50\u62a2\u4e0a\u3002\u80e1\u4f2f\u4f2f\u629b\u53bb\u624b\u4e2d\u957f\u5251\uff0c\u53cc\u624b\u5ffd\u4f38\u5ffd\u7f29\uff0c\u6293\u4f4f\u4f17\u4eba\u4e00\u4e00\u63b7\u4e86\u51fa\u53bb\uff0c\u968f\u5373\u6276\u8d77\u6211\u7239\u7239\uff0c\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\uff0c\u7b11\u9053\uff1a\u2018\u82d7\u5144\uff0c\u4f60\u81ea\u521b\u65b0\u62db\uff0c\u679c\u7136\u5389\u5bb3\u3002\u53ea\u662f\u6211\u8fd9\u80e1\u5bb6\u5200\u6cd5\uff0c\u6bcf\u4e00\u62db\u90fd\u542b\u6709\u540e\u7740\uff0c\u4f60\u8fde\u780d\u4e24\u62db\u4e0a\u624b\u5200\uff0c\u8170\u95f4\u4e0d\u514d\u9732\u51fa\u7a7a\u9699\u3002\u2019\u201c\u6211\u7239\u7239\u9ed8\u7136\u4e0d\u8bed\uff0c\u8170\u95f4\u9635\u9635\u62bd\u75db\uff0c\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u53e3\u3002\u80e1\u4f2f\u4f2f\u53c8\u9053\uff1a\u2018\u82e5\u975e\u4f60\u624b\u4e0b\u5bb9\u60c5\uff0c\u6211\u8fd9\u6761\u5de6\u8180\u5df2\u8ba9\u4f60\u5378\u4e86\u4e0b\u6765\u3002\u4eca\u65e5\u54b1\u4eec\u53ea\u7b97\u6253\u6210\u5e73\u624b\uff0c\u4f60\u56de\u53bb\u597d\u597d\u5b89\u7761\uff0c\u660e\u65e5\u518d\u6bd4\u5982\u4f55\uff1f\u2019\u6211\u7239\u7239\u5fcd\u75db\u9053\uff1a\u2018\u80e1\u5144\uff0c\u6211\u51fa\u5200\u65f6\u56fa\u7136\u7565\u6709\u5bb9\u8ba9\uff0c\u4f46\u5373\u4ee4\u780d\u4e0b\u4f60\u7684\u5de6\u81c2\uff0c\u4f60\u8fd9\u4e00\u817f\u4ecd\u80fd\u81f4\u6211\u6b7b\u547d\u3002\u77a7\u4f60\u8fd9\u822c\u4e3a\u4eba\uff0c\u51b3\u4e0d\u80fd\u6697\u5bb3\u6211\u7239\u7239\u3002\u4f60\u5012\u4eb2\u53e3\u8bf4\u4e00\u53e5\uff0c\u5230\u5e95\u6211\u7239\u7239\u662f\u600e\u6837\u6b7b\u7684\uff1f\u2019\u80e1\u4f2f\u4f2f\u8138\u4e0a\u9732\u51fa\u60ca\u8be7\u4e4b\u8272\uff0c\u9053\uff1a\u2018\u6211\u4e0d\u662f\u8ddf\u4f60\u8bf4\u5f97\u660e\u660e\u767d\u767d\u4e86\u4e48\uff1f \u3000\u3000\u4f60\u4e0d\u76f8\u4fe1\uff0c\u5b9a\u8981\u52a8\u6b66\u3002\u6211\u53ea\u597d\u820d\u547d\u966a\u541b\u5b50\u3002\u2019\u201c\u6211\u7239\u7239\u5927\u662f\u8be7\u5f02\uff0c\u95ee\u9053\uff1a\u2018\u4f60\u8ddf\u6211\u8bf4\u4e86\uff1f\u51e0\u65f6\u8bf4\u7684\uff1f\u2019\u80e1\u4f2f\u4f2f\u8f6c\u8fc7\u5934\u6765\uff0c\u6307\u7740\u65c1\u8fb9\u4e00\u4eba\u9053\uff1a\u2018\u4f60\u2026\u2026\u4f60\u2026\u2026\u2019\u53ea\u8bf4\u5f97\u4e24\u4e2a\u2018\u4f60\u2019\u5b57\uff0c\u5ffd\u7136\u53cc\u819d\u4e00\u8f6f\uff0c\u8dea\u5012\u5728\u5730\u3002\u6211\u7239\u7239\u5927\u60ca\uff0c\u5fd9\u4f38\u624b\u6276\u8d77\uff0c\u53ea\u89c1\u4ed6\u8138\u8272\u5927\u53d8\uff0c\u53eb\u9053\uff1a\u2018\u597d\u3001\u597d\u3001\u4f60\u2026\u2026\u201d\u5934\u4e00\u5782\uff0c\u7adf\u81ea\u6b7b\u4e86\u3002 \u3000\u3000\u201c\u6211\u7239\u7239\u60ca\u5f02\u4e07\u5206\uff0c\u5fc3\u60f3\u4ed6\u8eab\u5b50\u58ee\u5065\uff0c\u624b\u81c2\u4e0a\u8f7b\u8f7b\u5212\u7834\u4e00\u9053\u53e3\u5b50\uff0c\u5982\u4f55\u80fd\u591f\u81f4\u547d\uff1f\u62b1\u7740\u4ed6\u8eab\u5b50\uff0c\u8fde\u53eb\uff1a\u2018\u80e1\u5144\uff0c\u80e1\u5144\u3002\u2019\u4f46\u89c1\u4ed6\u8138\u988a\u6e10\u6e10\u8f6c\u6210\u7d2b\u8272\uff0c\u7adf\u662f\u4e2d\u4e86\u5267\u6bd2\u4e4b\u8c61\uff0c\u5fd9\u6495\u5f00\u4ed6\u7684\u8863\u8896\uff0c\u4f46\u89c1\u4e00\u6761\u624b\u81c2\u5df2\u80bf\u5f97\u7c97\u4e86\u4e00\u500d\uff0c\u4f24\u53e3\u4e2d\u6d41\u51fa\u7684\u90fd\u662f\u9ed1\u8840\u3002 \u3000\u3000\u201c\u80e1\u4f2f\u6bcd\u53c8\u60ca\u53c8\u60b2\uff0c\u629b\u4e0b\u624b\u4e2d\u5b69\u5b50\uff0c\u62ff\u8d77\u90a3\u67c4\u5355\u5200\u7ec6\u770b\u3002 \u3000\u3000\u90a3\u65f6\u6211\u7239\u7239\u4e5f\u77e5\u662f\u5200\u53e3\u4e0a\u5582\u4e86\u5267\u6bd2\u7684\u836f\u7269\u3002\u80e1\u4f2f\u6bcd\u89c1\u6211\u7239\u7239\u6c89\u541f\u4e0d\u8bed\uff0c\u8bf4\u9053\uff1a\u2018\u82d7\u5927\u4fa0\uff0c\u8fd9\u67c4\u5200\u662f\u5411\u4f60\u670b\u53cb\u501f\u7684\u3002\u54b1\u5bb6\u5927\u54e5\u56fa\u7136\u4e0d\u77e5\u5200\u4e0a\u6709\u6bd2\uff0c\u8c05\u4f60\u4e5f\u4e0d\u77e5\u60c5\uff0c\u5426\u5219\u8fd9\u7b49\u4e0b\u6d41\u5175\u5203\uff0c\u4f60\u4e24\u4eba\u600e\u80fd\u7528\u5b83\uff1f\u8fd9\u662f\u547d\u8be5\u5982\u6b64\uff0c\u602a\u4e0d\u5f97\u8c01\u3002\u6211\u672c\u7b54\u5e94\u54b1\u5bb6\u5927\u54e5\uff0c\u8981\u4eb2\u624b\u628a\u5b69\u5b50\u517b\u5927\uff0c\u4f46\u8fd9\u4e94\u65e5\u4e4b\u4e2d\uff0c\u4eb2\u89c1\u82d7\u5927\u4fa0\u809d\u80c6\u7167\u4eba\uff0c\u4e49\u91cd\u5982\u5c71\uff0c\u4f60\u65e2\u7b54\u5141\u7167\u987e\u5b69\u5b50\uff0c\u6211\u5c31\u5077\u4e00\u4e0b\u61d2\uff0c\u4e0d\u6328\u8fd9\u4e8c\u5341\u5e74\u7684\u82e6\u695a\u4e86\u3002\u2019\u8bf4\u7740\u6a2a\u5200\u5728\u9888\u4e2d\u4e00\u5272\uff0c\u7acb\u65f6\u6b7b\u53bb\u3002 \u3000\u3000\u201c\u6211\u4eb2\u542c\u7239\u7239\u8ff0\u8bf4\uff0c\u80e1\u4f2f\u4f2f\u901d\u4e16\u7684\u60c5\u5f62\u662f\u8fd9\u6837\u3002\u4f46\u5b9d\u6811\u5927\u5e08\u8bf4\u7684\u7adf\u662f\u5927\u4e0d\u76f8\u540c\u3002\u867d\u7136\u4e8b\u9694\u4e8c\u5341\u4f59\u5e74\uff0c\u6216\u6709\u8bb0\u4e0d\u5468\u5168\u4e4b\u5904\uff0c\u4f46\u60f3\u6765\u4e0d\u8be5\u53c2\u5dee\u592a\u591a\uff0c\u5374\u4e0d\u77e5\u662f\u4ec0\u4e48\u7f18\u6545\uff1f\u201d \u3000\u3000\u5b9d\u6811\u6447\u5934\u53f9\u606f\uff0c\u8bf4\u9053\uff1a\u2018\u4ee4\u5c0a\u5f53\u65f6\u8eab\u5728\u5c40\u4e2d\uff0c\u5168\u795e\u9163\u6597\uff0c\u53ea\u6015\u672a\u53ca\u65c1\u89c2\u8005\u770b\u5f97\u6e05\u695a\uff0c\u4e5f\u662f\u6709\u7684\u3002\u201d\u82d7\u82e5\u5170\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u4f4e\u5934\u4e0d\u8bed\u3002 \u3000\u3000\u5ffd\u7136\u65c1\u8fb9\u4e00\u4e2a\u5636\u54d1\u58f0\u97f3\u9053\uff1a\u201c\u4e24\u4f4d\u8bf4\u7684\u7ecf\u8fc7\u4e0d\u540c\uff0c\u53ea\u56e0\u4e3a\u6709\u4e00\u4e2a\u4eba\u662f\u5728\u6545\u610f\u8bf4\u8c0e\u3002\u201d \u3000\u3000\u4f17\u4eba\u542c\u5f97\u8fd9\u58f0\u97f3\u7a81\u5982\u5176\u6765\uff0c\u4e00\u9f50\u8f6c\u8fc7\u5934\u53bb\uff0c\u89c1\u8bf4\u8fd9\u8bdd\u7684\u539f\u6765\u662f\u90a3\u8138\u6709\u5200\u75a4\u7684\u4ec6\u4eba\u3002 \u3000\u3000\u5b9d\u6811\u548c\u82d7\u82e5\u5170\u90fd\u662f\u5916\u5ba2\uff0c\u867d\u542c\u4ed6\u8bf4\u8bdd\u65e0\u793c\uff0c\u5374\u4e5f\u4e0d\u4fbf\u53d1\u4f5c\u3002\u66f9\u4e91\u5947\u6700\u662f\u9c81\u83bd\uff0c\u62a2\u5148\u95ee\u9053\uff1a\u201c\u662f\u8c01\u8bf4\u8c0e\u4e86\uff1f\u201d\u90a3\u4ec6\u4eba\u9053\uff1a\u201c\u5c0f\u4eba\u662f\u4f4e\u4e09\u4e0b\u56db\u4e4b\u4eba\uff0c\u5982\u4f55\u6562\u8bf4\uff1f\u201d\u82d7\u82e5\u5170\u9053\uff1a\u201c\u82e5\u662f\u6211\u8bf4\u5f97\u4e0d\u5bf9\uff0c\u4f60\u4e0d\u59a8\u660e\u8a00\u3002\u201d\u5979\u610f\u6001\u95f2\u9038\uff0c\u4f3c\u4e4e\u6f2b\u4e0d\u5728\u610f\u3002 \u3000\u3000\u90a3\u4ec6\u4eba\u9053\uff1a\u201c\u9002\u624d\u5927\u5e08\u4e0e\u59d1\u5a18\u6240\u8bf4\u4e4b\u4e8b\uff0c\u5c0f\u4eba\u5f53\u65f6\u4e5f\u66fe\u4eb2\u89c1\uff0c\u5404\u4f4d\u82e5\u662f\u4e0d\u5acc\u8052\u566a\uff0c\u5c0f\u4eba\u4e5f\u6765\u8bf4\u8bf4\u3002\u201d \u3000\u3000\u5b9d\u6811\u559d\u9053\uff1a\u201c\u4f60\u5f53\u65f6\u4e5f\u66fe\u4eb2\u89c1\uff1f\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u4ec6\u4eba\u9053\uff1a\u201c\u5c0f\u4eba\u8ba4\u5f97\u5927\u5e08\uff0c\u5927\u5e08\u5374\u8ba4\u4e0d\u5f97\u5c0f\u4eba\u3002\u201d\u5b9d\u6811\u94c1\u9752\u4e86\u8138\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u4ec6\u4eba\u4e0d\u7b54\uff0c\u5374\u5411\u82d7\u82e5\u5170\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u53ea\u6015\u5c0f\u4eba\u8981\u8bf4\u7684\u8bdd\uff0c\u96be\u4ee5\u8bb2\u5f97\u5468\u5168\u3002\u201d\u82d7\u82e5\u5170\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u90a3\u4ec6\u4eba\u9053\uff1a\u201c\u53ea\u6d88\u8bf4\u5f97\u4e00\u534a\uff0c\u5c0f\u4eba\u7684\u6027\u547d\u5c31\u4e0d\u5728\u4e86\u3002\u201d\u82d7\u82e5\u5170\u5411\u5b9d\u6811\u9053\uff1a\u201c\u5927\u5e08\uff0c\u6b64\u523b\u5728\u8fd9\u5cf0\u4e0a\uff0c\u4e00\u5207\u7531\u4f60\u4f5c\u4e3b\u3002\u4f60\u662f\u6b66\u6797\u524d\u8f88\uff0c\u5fb7\u9ad8\u671b\u91cd\uff0c\u53ea\u8981\u4f60\u8001\u4eba\u5bb6\u4e00\u53e5\u8bdd\uff0c\u65e0\u4eba\u6562\u4f24\u4ed6\u6027\u547d\u3002\u201d \u3000\u3000\u5b9d\u6811\u51b7\u7b11\u9053\uff1a\u201c\u82d7\u59d1\u5a18\uff0c\u4f60\u662f\u6fc0\u6211\u6765\u7740\uff1f\u201d\u90a3\u4ec6\u4eba\u62a2\u7740\u9053\uff1a\u201c\u5c0f\u4eba\u81ea\u5df1\u7684\u6b7b\u6d3b\uff0c\u5012\u4e5f\u6ca1\u653e\u5728\u5fc3\u4e0a\uff0c\u5c31\u53ea\u6015\u6211\u6240\u77e5\u9053\u7684\u4e8b\u6ca1\u6cd5\u8bf4\u5b8c\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u5fae\u4e00\u6c89\u541f\uff0c\u6307\u7740\u90a3\u526f\u6728\u677f\u5bf9\u8054\u7684\u4e0b\u8054\uff0c\u9053\uff1a\u201c\u52b3\u9a7e\u4f60\u9664\u4e0b\u6765\u3002\u201d\u90a3\u4ec6\u4eba\u4e0d\u660e\u5979\u7528\u610f\uff0c\u4f46\u4f9d\u8a00\u5c06\u6728\u8054\u9664\u4e0b\uff0c\u653e\u5728\u5979\u9762\u524d\u3002\u82d7\u82e5\u5170\u9053\uff1a\u201c\u4f60\u77a7\u6e05\u695a\u4e86\uff0c\u8fd9\u4e0a\u9762\u5199\u7740\u6211\u7239\u7239\u7684\u540d\u5b57\u3002 \u3000\u3000\u4f60\u5c06\u8fd9\u6728\u8054\u62b1\u5728\u624b\u91cc\uff0c\u5c3d\u7ba1\u653e\u80c6\u800c\u8a00\u3002\u82e5\u662f\u6709\u4eba\u4f24\u4f60\u4e00\u6839\u6bdb\u53d1\uff0c\u90a3\u5c31\u662f\u6709\u610f\u8ddf\u6211\u7239\u7239\u8fc7\u4e0d\u53bb\u3002\u201d\u4f17\u4eba\u76f8\u4e92\u671b\u4e86\u4e00\u773c\uff0c\u5fc3\u60f3\u4ee5\u91d1\u9762\u4f5b\u4f5c\u62a4\u7b26\uff0c\u8fd8\u6709\u8c01\u6562\u4f24\u4ed6\uff1f \u3000\u3000\u90a3\u4ec6\u4eba\u8138\u9732\u559c\u8272\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u53ea\u662f\u8fd9\u4e00\u7b11\u7275\u52a8\u8138\u4e0a\u4f24\u75a4\uff0c\u66f4\u662f\u663e\u5f97\u8be1\u5f02\uff0c\u5f53\u4e0b\u679c\u771f\u5c06\u6728\u8054\u7262\u7262\u62b1\u4f4f\u3002 \u3000\u3000\u5b9d\u6811\u5750\u56de\u6905\u4e2d\uff0c\u51dd\u76ee\u77aa\u89c6\uff0c\u56de\u601d\u4e8c\u5341\u4e03\u5e74\u524d\u4e4b\u4e8b\uff0c\u59cb\u7ec8\u60f3\u4e0d\u8d77\u6b64\u4eba\u662f\u8c01\u3002 \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u4f60\u5750\u4e0b\u4e86\u597d\u8bf4\u8bdd\u3002\u201d\u90a3\u4ec6\u4eba\u9053\uff1a\u201c\u5c0f\u4eba\u7ad9\u7740\u8bf4\u7684\u597d\u3002\u8bf7\u95ee\u59d1\u5a18\uff0c\u80e1\u4e00\u5200\u5927\u7237\u9057\u4e0b\u7684\u90a3\u4e2a\u5b69\u5b50\uff0c\u540e\u6765\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u82d7\u82e5\u5170\u8f7b\u8f7b\u53f9\u606f\uff0c\u9053\uff1a\u201c\u6211\u7239\u7239\u89c1\u80e1\u4f2f\u4f2f\u3001\u80e1\u4f2f\u6bcd\u90fd\u6b7b\u4e86\uff0c\u5fc3\u4e2d\u5341\u5206\u96be\u8fc7\uff0c\u671b\u7740\u4e24\u4eba\u5c38\u8eab\uff0c\u5446\u4e86\u534a\u5929\uff0c\u8dea\u4e0b\u62dc\u4e86\u516b\u62dc\uff0c\u8bf4\u9053\uff1a\u2018\u80e1\u5144\u3001\u5927\u5ac2\uff0c\u4f60\u592b\u5987\u5c3d\u7ba1\u653e\u5fc3\uff0c\u6211\u5fc5\u597d\u597d\u629a\u517b\u4ee4\u90ce\u3002\u2019\u62dc\u7f62\u8d77\u8eab\uff0c\u56de\u5934\u53bb\u62b1\u5b69\u5b50\uff0c\u4e0d\u6599\u7adf\u62b1\u4e86\u4e2a\u7a7a\u3002\u6211\u7239\u7239\u5927\u60ca\uff0c\u6025\u5fd9\u8be2\u95ee\uff0c\u53ef\u662f\u5927\u5bb6\u90fd\u77a7\u7740\u80e1\u4f2f\u4f2f\u592b\u5987\u4e4b\u6b7b\uff0c\u8c01\u4e5f\u6ca1\u7559\u5fc3\u5b69\u5b50\u3002 \u3000\u3000\u6211\u7239\u7239\u5fd9\u53eb\u5927\u5bb6\u8d76\u5feb\u8ffd\u5bfb\u3002\u4ed6\u5fcd\u4f4f\u8170\u95f4\u75bc\u75db\uff0c\u4eb2\u81ea\u5728\u5ba2\u5e97\u524d\u540e\u67e5\u95ee\uff0c\u5ffd\u542c\u5f97\u5c4b\u540e\u6709\u5b69\u5b50\u557c\u54ed\uff0c\u58f0\u97f3\u6d2a\u4eae\u3002\u6211\u7239\u7239\u5927\u559c\uff0c\u6025\u5954\u8fc7\u53bb\uff0c\u54ea\u77e5\u4ed6\u8170\u95f4\u4e2d\u4e86\u80e1\u4f2f\u4f2f\u8fd9\u4e00\u817f\uff0c\u4f24\u52bf\u4e0d\u8f7b\uff0c\u731b\u4e00\u7528\u529b\uff0c\u7adf\u6454\u5728\u5730\u4e0b\u722c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u201c\u5f85\u5f97\u65c1\u4eba\u6276\u4ed6\u8d77\u8eab\uff0c\u8d76\u5230\u5c4b\u540e\uff0c\u53ea\u89c1\u5730\u4e0b\u4e00\u6ee9\u9c9c\u8840\uff0c\u8fd8\u6709\u5b69\u5b50\u7684\u4e00\u9876\u5c0f\u5e3d\uff0c\u5b69\u5b50\u5374\u5df2\u4e0d\u77e5\u53bb\u5411\u3002 \u3000\u3000\u201c\u5ba2\u5e97\u540e\u9762\u662f\u4e00\u6761\u6cb3\uff0c\u6c34\u6d41\u5f88\u6025\u3002\u773c\u89c1\u8840\u6e0d\u4e00\u76f4\u6d41\u5230\u6cb3\u8fb9\uff0c\u663e\u662f\u5b69\u5b50\u88ab\u4eba\u4e00\u5200\u6740\u6b7b\uff0c\u5c38\u8eab\u6295\u5165\u6cb3\u91cc\uff0c\u767b\u65f6\u88ab\u6c34\u51b2\u8d70\u4e86\u3002\u6211\u7239\u7239\u53c8\u60ca\u53c8\u6012\uff0c\u53ec\u96c6\u4e86\u4e00\u5e72\u4eba\u7ec6\u7ec6\u76d8\u95ee\uff0c\u59cb\u7ec8\u67e5\u4e0d\u5230\u51f6\u624b\u662f\u8c01\u3002 \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u4ed6\u65e0\u65e5\u4e0d\u803f\u803f\u4e8e\u6000\uff0c\u7acb\u8a93\u8981\u627e\u5230\u90a3\u6740\u5bb3\u5b69\u5b50\u4e4b\u4eba\u3002\u90a3\u4e00\u5e74\u6211\u89c1\u4ed6\u78e8\u5251\uff0c\u4ed6\u8bf4\u987b\u5f97\u518d\u6740\u4e00\u4eba\uff0c\u5c31\u662f\u8981\u6740\u90a3\u4e2a\u51f6\u624b\u4e86\u3002\u6211\u5bf9\u7239\u7239\u8bf4\uff0c\u6216\u8bb8\u5b69\u5b50\u7ed9\u4eba\u6551\u53bb\uff0c\u6d3b\u4e86\u4e0b\u6765\uff0c\u4e5f\u672a\u53ef\u77e5\u3002\u6211\u7239\u7239\u867d\u8bf4\u4f46\u613f\u5982\u6b64\uff0c\u7136\u800c\u5fc3\u4e2d\u5374\u7edd\u96be\u76f8\u4fe1\u3002\u5509\uff0c\u8fd9\u53ef\u601c\u7684\u5b69\u5b50\uff0c\u6211\u771f\u76fc\u4ed6\u662f\u597d\u597d\u7684\u6d3b\u7740\u3002\u6709\u4e00\u6b21\u7239\u7239\u5bf9\u6211\u8bf4\uff1a \u3000\u3000\u2018\u5b69\u513f\uff0c\u6211\u7231\u4f60\u80dc\u4e8e\u81ea\u5df1\u7684\u6027\u547d\u3002\u4f46\u82e5\u8001\u5929\u8bb8\u6211\u7528\u4f60\u53bb\u6389\u6362\u80e1\u4f2f\u4f2f\u7684\u5b69\u5b50\uff0c\u6211\u5b81\u53ef\u4f60\u6b7b\u4e86\uff0c\u80e1\u4f2f\u4f2f\u7684\u5b69\u5b50\u5374\u6d3b\u7740\u3002\u2019\u201d \u3000\u3000\u90a3\u4ec6\u4eba\u773c\u5708\u4e00\u7ea2\uff0c\u58f0\u97f3\u54fd\u54bd\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u80e1\u4e00\u5200\u5927\u7237\u3001\u80e1\u592b\u4eba\u5730\u4e0b\u6709\u7075\uff0c\u4e00\u5b9a\u611f\u6fc0\u4f60\u7236\u5973\u9ad8\u4e49\u3002\u201d \u3000\u3000\u4e8e\u7ba1\u5bb6\u672c\u6765\u4ee5\u4e3a\u4ed6\u662f\u82d7\u82e5\u5170\u5e26\u6765\u7684\u7537\u4ec6\uff0c\u4f46\u77a7\u4ed6\u795e\u60c5\uff0c\u542c\u4ed6\u8a00\u8bed\uff0c\u5374\u8d8a\u6765\u8d8a\u89c9\u4e0d\u4f3c\uff0c\u6b63\u60f3\u51fa\u8a00\u76f8\u8be2\uff0c\u5374\u542c\u4ed6\u8bf4\u8d77\u6545\u4e8b\u6765\uff0c\u89c1\u4f17\u4eba\u9759\u5750\u503e\u542c\uff0c\u4e5f\u4e0d\u4fbf\u6253\u65ad\u4ed6\u7684\u8bdd\u5934\u3002 \u3000\u3000\u53ea\u542c\u4ed6\u8bf4\u9053\uff1a\u201c\u4e8c\u5341\u4e03\u5e74\u4e4b\u524d\uff0c\u6211\u662f\u6ca7\u5dde\u90a3\u5c0f\u9547\u4e0a\u5ba2\u5e97\u4e2d\u7076\u4e0b\u70e7\u706b\u7684\u5c0f\u53ae\u3002\u90a3\u5e74\u51ac\u5929\uff0c\u6211\u5bb6\u4e2d\u906d\u9022\u5927\u7978\u3002\u6211\u7239\u7239\u4e09\u5e74\u524d\u6b20\u4e86\u5f53\u5730\u8d75\u8d22\u4e3b\u4e94\u4e24\u94f6\u5b50\uff0c\u5229\u4e0a\u52a0\u5229\uff0c\u4e00\u5e74\u7ffb\u4e00\u756a\uff0c\u8fc7\u5f97\u4e09\u5e74\uff0c\u5df2\u7b97\u6210\u56db\u5341\u4e24\u3002\u8d75\u8d22\u4e3b\u628a\u6211\u7239\u7239\u6293\u53bb\uff0c\u903c\u8feb\u7acb\u4e0b\u6587\u4e66\uff0c\u8981\u628a\u6211\u5988\u5356\u7ed9\u4ed6\u505a\u5c0f\u8001\u5a46\u3002 \u3000\u3000\u201c\u6211\u7239\u81ea\u7136\u8bf4\u4ec0\u4e48\u4e5f\u4e0d\u80af\uff0c\u5f53\u4e0b\u7ed9\u8d22\u4e3b\u7684\u72d7\u817f\u5b50\u62f7\u6253\u5f97\u6b7b\u53bb\u6d3b\u6765\u3002\u6211\u7239\u56de\u5f97\u5bb6\u6765\uff0c\u8ddf\u5988\u5546\u91cf\uff0c\u8fd9\u56db\u5341\u4e24\u94f6\u5b50\u518d\u8fc7\u4e00\u5e74\uff0c\u5c31\u53d8\u6210\u4e86\u516b\u5341\u4e24\uff0c\u8fd9\u7b14\u503a\u54b1\u4eec\u662f\u4e00\u8f88\u5b50\u8fd8\u4e0d\u8d77\u7684\u4e86\u3002\u6211\u7239\u5988\u5c31\u60f3\u56fe\u4e2a\u81ea\u5c3d\uff0c\u6b7b\u4e86\u7b97\u5566\uff0c\u5374\u53c8\u820d\u4e0d\u5f97\u6211\u3002\u4e09\u4e2a\u4eba\u53ea\u662f\u62b1\u7740\u75db\u54ed\u3002\u6211\u767d\u5929\u5728\u5ba2\u5e97\u91cc\u70e7\u706b\uff0c\u665a\u4e0a\u56de\u5bb6\u5b88\u7740\u7239\u5988\uff0c\u5fc3\u4e2d\u62c5\u60ca\u53d7\u6015\uff0c\u751f\u6015\u4ed6\u4fe9\u5bfb\u4e86\u77ed\u89c1\uff0c\u4e22\u4e0b\u6211\u4e00\u4eba\u5b64\u96f6\u96f6\u7684\u5728\u8fd9\u4e16\u4e0a\u3002 \u3000\u3000\u201c\u4e00\u665a\u5e97\u4e2d\u6765\u4e86\u597d\u591a\u53d7\u4f24\u7684\u5ba2\u4eba\uff0c\u7076\u4e0b\u4e8b\u5fd9\uff0c\u5e97\u4e3b\u4e0d\u8ba9\u6211\u56de\u5bb6\u3002\u7b2c\u4e8c\u65e5\u80e1\u4e00\u5200\u5927\u7237\u6765\u4e86\uff0c\u4ed6\u592b\u4eba\u751f\u4e86\u4f4d\u5c11\u7237\uff0c\u8981\u70e7\u6c34\u70e7\u6c64\uff0c\u5e97\u4e3b\u66f4\u662f\u4e0d\u8bb8\u6211\u56de\u5bb6\u53bb\u3002\u6211\u7275\u8bb0\u7239\u5988\uff0c\u6bdb\u624b\u6bdb\u811a\u7684\u649e\u70c2\u4e86\u51e0\u53ea\u7897\uff0c\u53c8\u7ed9\u5e97\u4e3b\u6253\u4e86\u51e0\u5df4\u638c\u3002\u6211\u4e00\u4e2a\u4eba\u8eb2\u5728\u7076\u8fb9\u5077\u5077\u7684\u54ed\u3002\u80e1\u5927\u7237\u8d70\u8fc7\u53a8\u623f\uff0c\u542c\u89c1\u6211\u54ed\u58f0\uff0c\u5c31\u8fdb\u6765\u95ee\u6211\u4ec0\u4e48\u4e8b\u3002\u6211\u89c1\u4ed6\u751f\u5f97\u51f6\u6076\uff0c\u4e0d\u6562\u8bf4\u8bdd\u3002\u4ed6\u8d8a\u662f\u95ee\uff0c\u6211\u8d8a\u662f\u54ed\u5f97\u5389\u5bb3\u3002\u540e\u6765\u4ed6\u548c\u548c\u6c14\u6c14\u7684\u597d\u8a00\u597d\u8bed\uff0c\u6211\u624d\u628a\u5bb6\u91cc\u7684\u4e8b\u8ddf\u4ed6\u8bf4\u4e86\u3002 \u3000\u3000\u201c\u80e1\u5927\u7237\u5f88\u751f\u6c14\uff0c\u8bf4\u9053\uff1a\u2018\u8fd9\u59d3\u8d75\u7684\u5982\u6b64\u6a2a\u884c\u9738\u9053\uff0c\u672c\u8be5\u53bb\u4e00\u5200\u6740\u4e86\uff0c\u53ea\u662f\u6211\u6709\u4e8b\u5728\u8eab\uff0c\u6ca1\u529f\u592b\u8ddf\u4ed6\u7b97\u5e10\u3002\u6211\u7ed9\u4f60\u4e00\u767e\u4e24\u94f6\u5b50\uff0c\u4f60\u53bb\u62ff\u7ed9\u4f60\u7239\uff0c\u8ba9\u4ed6\u8fd8\u503a\uff0c\u4f59\u4e0b\u7684\u94b1\u597d\u597d\u8fc7\u65e5\u5b50\uff0c\u53ef\u5343\u4e07\u522b\u518d\u501f\u8d22\u4e3b\u7684\u503a\u4e86\u3002\u2019\u6211\u53ea\u9053\u4ed6\u8bf4\u7b11\u8bdd\u54c4\u6211\uff0c\u54ea\u77e5\u4ed6\u5f53\u771f\u62ff\u4e86\u4e94\u53ea\u5927\u5143\u5b9d\u7ed9\u6211\u3002\u6211\u54ea\u91cc\u6562\u62ff\uff1f\u80e1\u5927\u7237\u9053\uff1a\u2018\u6211\u4eca\u65e5\u751f\u4e86\u513f\u5b50\uff0c\u6211\u751a\u662f\u75bc\u4ed6\u601c\u4ed6\uff0c\u5c06\u5fc3\u6bd4\u5fc3\uff0c\u4f60\u7239\u5988\u75bc\u4f60\u4e5f\u662f\u8fd9\u822c\u3002 \u3000\u3000\u4f60\u5feb\u56de\u5bb6\u53bb\u3002\u6211\u8ddf\u5e97\u4e3b\u8bf4\uff0c\u662f\u6211\u53eb\u4f60\u56de\u5bb6\u7684\uff0c\u4ed6\u4e0d\u6562\u96be\u4e3a\u4f60\u3002\u2019\u201c\u6211\u4ecd\u662f\u5446\u5446\u671b\u7740\u4ed6\uff0c\u5fc3\u91cc\u6251\u901a\u6251\u901a\u76f4\u8df3\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u3002 \u3000\u3000\u80e1\u5927\u7237\u62ff\u4e86\u4e00\u5757\u5305\u88b1\uff0c\u628a\u4e94\u53ea\u5927\u5143\u5b9d\u5305\u4e86\uff0c\u66ff\u6211\u7f1a\u5728\u80cc\u4e0a\uff0c\u518d\u5728\u6211\u5c41\u80a1\u4e0a\u8f7b\u8f7b\u8e22\u4e86\u4e00\u811a\uff0c\u7b11\u9053\uff1a\u2018\u50bb\u5c0f\u5b50\uff0c\u8fd8\u4e0d\u7ed9\u6211\u5feb\u6eda\uff01\u2019\u201c\u6211\u80e1\u91cc\u80e1\u6d82\u7684\u5954\u56de\u5bb6\u53bb\uff0c\u8ddf\u7239\u5988\u4e00\u8bf4\u3002\u4e09\u4e2a\u4eba\u4e50\u5f97\u75af\u4e86\uff0c\u771f\u96be\u4ee5\u76f8\u4fe1\u5929\u4e0b\u6709\u8fd9\u822c\u597d\u4eba\uff0c\u8bf4\u662f\u505a\u68a6\u7f62\uff0c\u767d\u82b1\u82b1\u7684\u4e94\u53ea\u5927\u5143\u5b9d\u660e\u660e\u653e\u5728\u684c\u4e0a\u3002\u6211\u5988\u548c\u6211\u6276\u7740\u7239\u5230\u5ba2\u5e97\u53bb\uff0c\u8981\u5411\u80e1\u5927\u7237\u78d5\u5934\u9053\u8c22\u3002\u4ed6\u8fde\u8fde\u6447\u624b\uff0c\u8bf4\u751f\u5e73\u6700\u4e0d\u7231\u522b\u4eba\u8c22\u4ed6\uff0c\u5c06\u6211\u4eec\u4e09\u4e2a\u63a8\u4e86\u51fa\u6765\u3002 \u3000\u3000\u201c\u6211\u548c\u7239\u5988\u6b63\u8981\u56de\u53bb\uff0c\u5ffd\u542c\u9a6c\u8e44\u58f0\u54cd\uff0c\u51e0\u5341\u4e2a\u4eba\u8d76\u6765\u5ba2\u5e97\uff0c\u539f\u6765\u662f\u80e1\u5927\u7237\u7684\u4ec7\u5bb6\u3002\u6211\u4e0d\u653e\u5fc3\uff0c\u8ba9\u7239\u5988\u5148\u56de\u5bb6\u53bb\uff0c\u81ea\u5df1\u7559\u7740\u8981\u77a7\u4e2a\u7a76\u7adf\u3002\u6211\u60f3\u80e1\u5927\u7237\u6551\u4e86\u6211\u4e00\u5bb6\u4e09\u53e3\u7684\u6027\u547d\uff0c\u53ea\u8981\u6709\u7528\u5f97\u7740\u6211\u7684\uff0c\u6c34\u91cc\u5c31\u6c34\u91cc\u53bb\uff0c\u706b\u91cc\u5c31\u706b\u91cc\u53bb\uff0c\u51b3\u4e0d\u80fd\u76b1\u4e00\u76b1\u7709\u5934\u3002 \u3000\u3000\u201c\u91d1\u9762\u4f5b\u82d7\u5927\u4fa0\u8ddf\u80e1\u5927\u7237\u5750\u7740\u5bf9\u996e\uff0c\u80e1\u5927\u7237\u820d\u4e0d\u5f97\u513f\u5b50\u8fd9\u4e9b\u60c5\u5f62\uff0c\u5b9d\u6811\u5927\u5e08\u8bf4\u5f97\u4e00\u70b9\u4e0d\u9519\u3002\u53ea\u662f\u4ed6\u5374\u4e0d\u77e5\u9053\uff0c\u90a3\u8dcc\u6253\u533b\u751f\u5728\u9694\u623f\u542c\u80e1\u5927\u7237\u592b\u5987\u8bf4\u8bdd\uff0c\u5374\u6559\u4e00\u4e2a\u7076\u4e0b\u70e7\u706b\u7684\u5c0f\u53ae\u5168\u77a7\u5728\u773c\u91cc\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u5b9d\u6811\u731b\u5730\u7ad9\u8d77\u8eab\u6765\uff0c\u6307\u7740\u4ed6\u559d\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f\u8c01\uff1f\u53d7\u8c01\u6307\u4f7f\u5728\u8fd9\u91cc\u80e1\u8bf4\u516b\u9053\uff1f\u201d \u3000\u3000\u90a3\u4ec6\u4eba\u4e0d\u52a8\u58f0\u8272\uff0c\u6de1\u6de1\u7684\u9053\uff1a\u201c\u6211\u53eb\u5e73\u963f\u56db\u3002\u6211\u8bc6\u5f97\u8dcc\u6253\u533b\u751f\u960e\u57fa\uff0c\u90a3\u8dcc\u6253\u533b\u751f\u960e\u57fa\uff0c\u81ea\u7136\u4e0d\u8bc6\u5f97\u6211\u8fd9\u70e7\u706b\u7684\u5c0f\u53ae\u765e\u75e2\u5934\u963f\u56db\u3002\u201d \u3000\u3000\u5b9d\u6811\u542c\u5230\u4ed6\u8bf4\u8d77\u201c\u960e\u57fa\u201d\u4e8c\u5b57\uff0c\u8138\u4e0a\u7acb\u65f6\u53d8\u8272\uff0c\u4f9d\u7a00\u8bb0\u5f97\u5f53\u5e74\u90a3\u5c0f\u5ba2\u5e97\u4e4b\u4e2d\uff0c\u679c\u4f3c\u6709\u4e2a\u765e\u75e2\u5934\u5c0f\u53ae\uff0c\u53ea\u662f\u4ed6\u7684\u9762\u8c8c\u795e\u60c5\u5f53\u65e5\u5c31\u672a\u7559\u610f\uff0c\u6b64\u65f6\u66f4\u662f\u534a\u70b9\u4e5f\u8bb0\u4e0d\u8d77\u4e86\u3002\u4ed6\u5411\u5e73\u963f\u56db\u6000\u4e2d\u62b1\u7740\u7684\u6728\u8054\u72e0\u72e0\u77aa\u4e86\u4e00\u773c\uff0c\u201c\u5478\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u5e73\u963f\u56db\u9053\uff1a\u201c\u6211\u534a\u591c\u91cc\u542c\u5230\u80e1\u5927\u7237\u7684\u54ed\u58f0\uff0c\u5b9e\u5728\u653e\u5fc3\u4e0d\u4e0b\uff0c\u8d70\u5230\u4ed6\u7684\u623f\u5916\uff0c\u5374\u89c1\u5230\u9694\u623f\u7a97\u5b50\u4e0a\u6620\u51fa\u4e00\u4e2a\u9ed1\u5f71\uff0c\u4e00\u52a8\u4e0d\u52a8\u7684\u4f0f\u7740\u3002\u6211\u8d70\u8fc7\u53bb\u5230\u7a97\u7f1d\u91cc\u4e00\u5f20\uff0c\u539f\u6765\u662f\u90a3\u8dcc\u6253\u533b\u751f\u960e\u57fa\u5c06\u8033\u6735\u51d1\u5728\u677f\u58c1\u4e0a\uff0c\u5728\u5077\u542c\u80e1\u5927\u7237\u592b\u5987\u8bf4\u8bdd\u3002\u6211\u6b63\u60f3\u53bb\u8ddf\u80e1\u5927\u7237\u8bf4\uff0c\u80e1\u5927\u7237\u5374\u8d70\u5230\u960e\u57fa\u623f\u91cc\u6765\u4e86\uff0c\u8ddf\u4ed6\u8bf4\u4e86\u5f88\u591a\u5f88\u591a\u8bdd\u3002\u8fd9\u4e9b\u8bdd\u5b9d\u6811\u5927\u5e08\u59cb\u7ec8\u6ca1\u8ddf\u5404\u4f4d\u63d0\u8d77\u4e00\u5b57\u534a\u53e5\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u7f18\u6545\u3002 \u3000\u3000\u201c\u80e1\u5927\u7237\u7684\u8bdd\u5f88\u957f\uff0c\u81ea\u7136\u6709\u4e9b\u6211\u542c\u4e86\u4e0d\u61c2\uff0c\u4f46\u6211\u660e\u767d\uff0c\u80e1\u5927\u7237\u662f\u6d3e\u90a3\u960e\u57fa\u7b2c\u4e8c\u5929\u53bb\u8ddf\u91d1\u9762\u4f5b\u82d7\u5927\u4fa0\u89e3\u91ca\u51e0\u4ef6\u4e8b\u3002\u8fd9\u4e9b\u4e8b\u60c5\u7275\u8fde\u91cd\u5927\uff0c\u672c\u6765\u4e0d\u8be5\u8ba9\u4e00\u4e2a\u4e0d\u76f8\u5e72\u7684\u5916\u4eba\u53bb\u8bf4\u3002\u53ea\u662f\u80e1\u592b\u4eba\u521a\u751f\u4e86\u5b69\u5b50\uff0c\u4e0d\u80fd\u8d70\u52a8\u3002\u80e1\u5927\u7237\u53c8\u813e\u6c14\u66b4\u8e81\uff0c\u5018\u82e5\u4eb2\u81ea\u53bb\u5411\u5bf9\u5934\u8a00\u8bb2\uff0c\u52bf\u5fc5\u8ddf\u8303\u5e2e\u4e3b\u3001\u7530\u76f8\u516c\u4ed6\u4eec\u5f15\u8d77\u4e89\u6267\uff0c\u4e00\u4e2a\u8bf4\u4e0d\u660e\u767d\uff0c\u5230\u5934\u6765\u8fd8\u662f\u52a8\u5200\u52a8\u67aa\uff0c\u8bf4\u4e0e\u4e0d\u8bf4\uff0c\u90fd\u662f\u4e00\u822c\uff0c\u6ca1\u5948\u4f55\u53ea\u5f97\u8ba9\u960e\u57fa\u53bb\u4f20\u8bdd\u3002\u9002\u624d\u5b9d\u6811\u5927\u5e08\u8bf4\u9053\uff0c\u80e1\u5927\u7237\u6d3e\u4ed6\u9001\u4fe1\u53bb\u7ed9\u91d1\u9762\u4f5b\uff0c\u4e8b\u6210\u4e4b\u540e\u5fc5\u6709\u91cd\u8c22\uff0c\u8fd9\u8bdd\u5c31\u4e0d\u5bf9\u4e86\u3002\u60f3\u9001\u4e00\u5c01\u4fe1\u8f7b\u800c\u6613\u4e3e\uff0c\u4f55\u5fc5\u91cd\u8c22\uff1f\u4f55\u5fc5\u592b\u5987\u4fe9\u5546\u91cf\u534a\u65e5\uff1f\u5b9d\u6811\u5927\u5e08\u6216\u8bb8\u5fd8\u4e86\u80e1\u5927\u7237\u5f53\u65f6\u7684\u8bf4\u8bdd\uff0c\u6211\u5374\u4e00\u53e5\u4e5f\u6ca1\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u4f17\u4eba\u542c\u4e86\u8fd9\u756a\u8bdd\uff0c\u624d\u77e5\u5b9d\u6811\u51fa\u5bb6\u4e4b\u524d\u7684\u4fd7\u5bb6\u59d3\u540d\u53eb\u4f5c\u960e\u57fa\u3002\u77a7\u4ed6\u4e24\u4eba\u795e\u60c5\uff0c\u7a7a\u6811\u4e0e\u80e1\u4e00\u5200\u4e4b\u6b7b\u5fc5\u6709\u91cd\u5927\u5173\u8fde\uff0c\u800c\u4ed6\u5148\u524d\u7684\u8bdd\u4e2d\u4e5f\u5fc5\u6709\u751a\u591a\u4e0d\u5c3d\u4e0d\u5b9e\u4e4b\u5904\u3002\u5404\u4eba\u597d\u5947\u5fc3\u8d77\uff0c\u90fd\u76fc\u5e73\u963f\u56db\u63ed\u7834\u8fd9\u4e2a\u7591\u56e2\uff0c\u4f46\u53c8\u6015\u4ed6\u5f53\u771f\u8bf4\u51fa\u4ec0\u4e48\u91cd\u5927\u79d8\u5bc6\uff0c\u5b9d\u6811\u8001\u7f9e\u6210\u6012\uff0c\u7a81\u65bd\u6bd2\u624b\uff0c\u8fd9\u96ea\u5cf0\u4e0a\u53ef\u6ca1\u4e00\u4eba\u662f\u4ed6\u5bf9\u624b\uff0c\u96be\u4ee5\u963b\u62e6\u3002\u7eb5\u7136\u65e5\u540e\u91d1\u9762\u4f5b\u627e\u5230\u5b9d\u6811\u7b97\u5e10\uff0c\u4f46\u5e73\u963f\u56db\u4e00\u6b7b\uff0c\u8fd9\u79d8\u5bc6\u53ea\u6015\u6c38\u8fdc\u968f\u4ed6\u800c\u901d\u4e86\u3002 \u3000\u3000\u5404\u4eba\u90fd\u4ee3\u5e73\u963f\u56db\u62c5\u5fc3\uff0c\u4f46\u4ed6\u81ea\u5df1\u5374\u662f\u795e\u8272\u6728\u7136\uff0c\u6beb\u65e0\u60e7\u610f\uff0c\u7adf\u4f3c\u6709\u6043\u65e0\u6050\uff0c\u53ea\u542c\u4ed6\u8bf4\u9053\uff1a\u201c\u80e1\u5927\u7237\u8ddf\u960e\u57fa\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u6211\u5c31\u7ad9\u5728\u960e\u57fa\u7684\u7a97\u5916\u3002\u6211\u5012\u4e0d\u662f\u6709\u5fc3\u60f3\u5077\u542c\u80e1\u5927\u7237\u8bf4\u8bdd\uff0c\u53ea\u662f\u6211\u77e5\u9053\u8fd9\u8dcc\u6253\u533b\u751f\u4e00\u5411\u5949\u627f\u90a3\u6b3a\u4fae\u6211\u7239\u5988\u7684\u8d75\u8d22\u4e3b\uff0c\u5b9e\u5728\u4e0d\u662f\u597d\u4eba\uff0c\u53ea\u6015\u80e1\u5927\u7237\u4e0a\u4e86\u4ed6\u7684\u5f53\u3002\u90a3\u65f6\u6211\u5e74\u8f7b\u8bc6\u6d45\uff0c\u80e1\u5927\u7237\u7684\u8bdd\u662f\u4e0d\u5927\u660e\u767d\uff0c\u4f46\u4e00\u5b57\u4e00\u53e5\uff0c\u5374\u90fd\u8bb0\u5728\u5fc3\u91cc\uff0c\u7b49\u6211\u540e\u6765\u5e74\u7eaa\u5927\u4e86\uff0c\u6162\u6162\u4e5f\u90fd\u61c2\u4e86\u3002 \u3000\u3000\u201c\u90a3\u4e00\u665a\u80e1\u5927\u7237\u53eb\u960e\u57fa\u53bb\u8bf4\u4e09\u4ef6\u4e8b\u3002\u7b2c\u4e00\u4ef6\u8bf4\u7684\u662f\u80e1\u82d7\u8303\u7530\u56db\u5bb6\u4e0a\u4ee3\u7ed3\u4ec7\u7684\u7f18\u7531\u3002\u7b2c\u4e8c\u4ef6\u8bf4\u7684\u662f\u91d1\u9762\u4f5b\u4e4b\u7236\u4e0e\u7530\u76f8\u516c\u4e4b\u7236\u7684\u6b7b\u56e0\u3002\u7b2c\u4e09\u4ef6\u5219\u662f\u5173\u4e8e\u95ef\u738b\u519b\u5200\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u9f50\u8f6c\u5934\uff0c\u5411\u684c\u4e0a\u7684\u519b\u5200\u671b\u4e86\u4e00\u773c\uff0c\u6b32\u77e5\u4e4b\u5fc3\u66f4\u662f\u8feb\u5207\u3002 \u3000\u3000\u5e73\u963f\u56db\u9053\uff1a\u201c\u80e1\u82d7\u8303\u7530\u56db\u5bb6\u4e0a\u4ee3\u4e3a\u4ec0\u4e48\u7ed3\u4ec7\uff0c\u82d7\u59d1\u5a18\u5df2\u7ecf\u8bf4\u4e86\uff0c\u53ea\u662f\u4e2d\u95f4\u53e6\u6709\u4e00\u4e2a\u91cd\u5927\u79d8\u5bc6\uff0c\u5374\u975e\u5916\u4eba\u6240\u77e5\uff0c\u8fde\u82d7\u5927\u4fa0\u4e5f\u81f3\u4eca\u4e0d\u77e5\u3002\u8fd9\u79d8\u5bc6\u8d77\u56e0\u4e8e\u674e\u95ef\u738b\u5927\u987a\u6c38\u660c\u4e8c\u5e74\uff0c\u90a3\u5e74\u662f\u4e59\u9149\u5e74\uff0c\u4e5f\u5c31\u662f\u987a\u6cbb\u4e8c\u5e74\uff0c\u5f53\u65f6\u80e1\u82d7\u8303\u7530\u56db\u5bb6\u7956\u5b97\u8a00\u660e\uff0c\u82e5\u662f\u6e05\u671d\u4e0d\u4ea1\uff0c\u987b\u5230\u4e00\u767e\u5e74\u540e\u7684\u4e59\u4e11\u5e74\uff0c\u65b9\u80fd\u6cc4\u6f0f\u8fd9\u4e2a\u5927\u79d8\u5bc6\u3002 \u3000\u3000\u4e59\u4e11\u5e74\u662f\u4e7e\u9686\u5341\u5e74\uff0c\u8ddd\u4eca\u5df2\u6709\u4e09\u5341\u4f59\u5e74\uff0c\u6240\u4ee5\u5f53\u4e8c\u5341\u4e03\u5e74\u524d\u80e1\u5927\u7237\u8ddf\u960e\u57fa\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u767e\u5e74\u671f\u9650\u5df2\u8fc7\uff0c\u8fd9\u4e2a\u5927\u79d8\u5bc6\u5df2\u4e0d\u987b\u9690\u7792\u4e86\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u4e2a\u79d8\u5bc6\uff0c\u679c\u7136\u662f\u7275\u8fde\u91cd\u5927\u3002\u539f\u6765\u5f53\u65e5\u95ef\u738b\u5175\u8d25\u4e5d\u5bab\u5c71\uff0c\u4ed6\u53ef\u6ca1\u6709\u6b7b\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u90fd\u662f\u4e00\u9707\uff0c\u4e00\u9f50\u7ad9\u8d77\u8eab\u6765\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u53ea\u6709\u5b9d\u6811\u7aef\u5750\u65e0\u5f02\uff0c\u663e\u662f\u65e9\u5df2\u77e5\u6653\uff0c\u4e0d\u4e3a\u6240\u52a8\u3002 \u3000\u3000\u5e73\u963f\u56db\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u95ef\u738b\u6ca1\u6709\u6b7b\u3002\u53ea\u4e0d\u8fc7\u5f53\u65f6\u6e05\u5175\u91cd\u91cd\u56f4\u56f0\uff0c\u5b9e\u662f\u96be\u4ee5\u8131\u8eab\u3002\u82d7\u8303\u7530\u4e09\u540d\u536b\u58eb\u51b2\u4e0b\u5c71\u53bb\u6c42\u6551\uff0c\u63f4\u5175\u8fdf\u8fdf\u4e0d\u81f3\uff0c\u654c\u519b\u5374\u6108\u8feb\u6108\u8fd1\u3002\u773c\u89c1\u624b\u4e0b\u5c06\u58eb\u6b7b\u7684\u6b7b\uff0c\u4f24\u7684\u4f24\uff0c\u518d\u4e5f\u62b5\u6321\u4e0d\u4f4f\uff0c\u95ef\u738b\u5fc3\u7070\u610f\u61d2\uff0c\u4e3e\u8d77\u519b\u5200\u8981\u5f85\u6a2a\u5200\u81ea\u520e\uff0c\u5374\u88ab\u90a3\u53f7\u79f0\u98de\u5929\u72d0\u72f8\u7684\u59d3\u80e1\u536b\u58eb\u62e6\u4f4f\u3002 \u3000\u3000\u201c\u59d3\u80e1\u7684\u536b\u58eb\u60c5\u6025\u4e4b\u4e0b\uff0c\u751f\u4e86\u4e00\u8ba1\uff0c\u4ece\u9635\u4ea1\u5c06\u58eb\u4e4b\u4e2d\u6361\u4e86\u4e00\u4e2a\u548c\u95ef\u738b\u8eab\u6750\u5927\u5c0f\u76f8\u4eff\u7684\u5c38\u9996\uff0c\u6362\u4e0a\u95ef\u738b\u7684\u9ec4\u888d\u7bad\u8863\uff0c\u5c06\u95ef\u738b\u7684\u91d1\u5370\u6302\u5728\u5c38\u9996\u9888\u4e2d\u3002\u4ed6\u518d\u4e3e\u5200\u5c06\u5c38\u9996\u9762\u8c8c\u780d\u5f97\u7a00\u70c2\uff0c\u53eb\u4eba\u96be\u4ee5\u8fa8\u8ba4\uff0c\u4eb2\u81ea\u9a6e\u4e86\uff0c\u5230\u6e05\u5175\u8425\u4e2d\u6295\u964d\uff0c\u8bf4\u5df2\u5c06\u95ef\u738b\u6740\u6b7b\uff0c\u7279\u6765\u8bf7\u529f\u9886\u8d4f\u3002\u8fd9\u662f\u4e00\u4ef6\u4f55\u7b49\u5927\u529f\uff0c\u654c\u5c06\u5448\u62a5\u4e0a\u53bb\uff0c\u81ea\u4f1a\u5347\u5b98\u5c01\u7235\uff0c\u83ab\u8bf4\u4e1d\u6beb\u6ca1\u7591\u5fc3\u662f\u5047\uff0c\u5373\u4ee4\u6709\u4ec0\u4e48\u6000\u7591\uff0c\u4e5f\u8981\u6781\u529b\u8499\u853d\u63a9\u9970\uff0c\u4ee5\u4fbf\u9886\u529f\u5347\u5b98\u3002\u5047\u95ef\u738b\u4e00\u6b7b\uff0c\u654c\u519b\u5373\u65e5\u89e3\u4e86\u4e5d\u5bab\u5c71\u4e4b\u56f4\u3002\u771f\u95ef\u738b\u65e9\u5df2\u6613\u5bb9\u6539\u88c5\uff0c\u626e\u6210\u5e73\u6c11\uff0c\u8f7b\u8f7b\u6613\u6613\u7684\u8131\u9669\u4e0b\u5c71\u3002\u5509\uff0c\u95ef\u738b\u662f\u8131\u5374\u4e86\u5371\u96be\uff0c\u8fd9\u4f4d\u98de\u5929\u72d0\u72f8\u53ef\u5c31\u5927\u96be\u4e34\u5934\u4e86\u3002 \u3000\u3000\u201c\u90a3\u98de\u5929\u72d0\u72f8\u884c\u8fd9\u8ba1\u7b56\uff0c\u7528\u5fc3\u5b9e\u5728\u662f\u82e6\u5230\u4e86\u6781\u5904\u3002\u6c5f\u6e56\u4e0a\u82f1\u96c4\u597d\u6c49\uff0c\u4e3a\u4e86\u2018\u4fa0\u4e49\u2019\u4e8c\u5b57\uff0c\u66ff\u597d\u670b\u53cb\u4e24\u808b\u63d2\u5200\u539f\u975e\u96be\u4e8b\uff0c\u53ef\u662f\u4ed6\u4e3a\u4e86\u76f8\u6551\u95ef\u738b\uff0c\u4e0d\u4f46\u8981\u59d4\u5c48\u4e07\u5206\u7684\u6295\u964d\u654c\u4eba\uff0c\u8fd8\u5f97\u7518\u5192\u4e00\u4e2a\u5356\u4e3b\u6c42\u8363\u7684\u6076\u540d\u3002\u60f3\u90a3\u98de\u5929\u72d0\u72f8\u672c\u6765\u540d\u9707\u5929\u4e0b\uff0c\u6b66\u6797\u4eba\u7269\u4e00\u63d0\u5230\u4ed6\u7684\u540d\u5934\uff0c\u65e0\u4e0d\u7fd8\u8d77\u5927\u62c7\u6307\u8d5e\u4e00\u58f0\uff1a\u2018\u597d\u6c49\u5b50\uff01\u2019\u73b0\u4e0b\u8981\u4ed6\u81ea\u6c61\u4e00\u4e16\u82f1\u540d\uff0c\u90a3\u53ef\u6bd4\u6177\u6168\u5c31\u4e49\u96be\u4e0a\u4e07\u500d\u3002 \u3000\u3000\u201c\u4ed6\u6295\u964d\u5434\u4e09\u6842\u540e\uff0c\u5728\u8fd9\u6c49\u5978\u624b\u4e0b\u505a\u5b98\u3002\u4ed6\u667a\u52c7\u53cc\u5168\u3001\u7cbe\u660e\u80fd\u5e72\uff0c\u6781\u5f97\u5434\u4e09\u6842\u4fe1\u4efb\u3002\u4ed6\u60f3\u95ef\u738b\u5927\u987a\u56fd\u7684\u5929\u4e0b\uff0c\u786c\u751f\u751f\u65ad\u9001\u5728\u5434\u4e09\u6842\u624b\u91cc\uff0c\u6b64\u4ec7\u4e0d\u62a5\uff0c\u975e\u4e08\u592b\u4e5f\u3002\u4ed6\u82e5\u8981\u523a\u6b7b\u5434\u4e09\u6842\uff0c\u539f\u53ea\u4e00\u4e3e\u624b\u4e4b\u52b3\uff0c\u53ef\u662f\u98de\u5929\u72d0\u72f8\u667a\u8c0b\u6df1\u6c89\uff0c\u5c82\u80af\u5982\u6b64\u8f7b\u6613\u4e86\u4e8b\uff1f\u6570\u5e74\u4e4b\u95f4\uff0c\u4ed6\u4e0d\u9732\u75d5\u8ff9\u7684\u8fde\u4f7f\u5de7\u8ba1\uff0c\u5b89\u6392\u4e0b\u8bb8\u591a\u4e8b\u7aef\uff0c\u4e00\u9762\u4f7f\u6ee1\u6e05\u7687\u5e1d\u5bf9\u5434\u4e09\u6842\u5927\u8d77\u7591\u5fc3\uff0c\u53e6\u4e00\u9762\u4f7f\u5434\u4e09\u6842\u5fc3\u4e0d\u81ea\u5b89\uff0c\u5230\u5934\u6765\u4e0d\u5f97\u4e0d\u4e3e\u5175\u8c0b\u53cd\u3002\u4ed6\u5c06\u5434\u4e09\u6842\u5728\u4e91\u5357\u62db\u5175\u4e70\u9a6c\u3001\u8dcb\u6248\u81ea\u5927\u7684\u79cd\u79cd\u4e8b\u8ff9\uff0c\u6697\u4e2d\u7980\u62a5\u6e05\u5ef7\uff0c\u800c\u6e05\u5ef7\u5404\u79cd\u731c\u5fcc\u9632\u8303\u7684\u624b\u6bb5\uff0c\u4ed6\u53c8\u523a\u63a2\u4e86\u53bb\u544a\u77e5\u5434\u4e09\u6842\u3002 \u3000\u3000\u201c\u5982\u6b64\u4e0d\u51fa\u6570\u5e74\uff0c\u5434\u4e09\u6842\u52bf\u5728\u5fc5\u53cd\u3002\u90a3\u65f6\u5929\u4e0b\u5927\u4e71\uff0c\u6ee1\u6e05\u5927\u4f24\u5143\u6c14\uff0c\u81ea\u662f\u95ef\u738b\u590d\u56fd\u7684\u826f\u673a\u3002\u5373\u4ee4\u5434\u4e09\u6842\u7684\u53cd\u53db\u8fc5\u5373\u6549\u5e73\uff0c\u95ef\u738b\u590d\u56fd\u4e0d\u6210\uff0c\u4f46\u5434\u4e09\u6842\u4e5f\u975e\u706d\u65cf\u4e0d\u53ef\uff0c\u8fd9\u6bd4\u523a\u6b7b\u4ed6\u4e00\u4e2a\u4eba\u81ea\u662f\u597d\u5f97\u591a\u4e86\u3002 \u3000\u3000\u201c\u5f53\u90a3\u59d3\u82d7\u3001\u59d3\u8303\u3001\u59d3\u7530\u4e09\u4e2a\u7ed3\u4e49\u5144\u5f1f\u5230\u6606\u660e\u53bb\u884c\u523a\u5434\u4e09\u6842\u4e4b\u65f6\uff0c\u98de\u5929\u72d0\u72f8\u7684\u8ba1\u8c0b\u6b63\u5df2\u6e10\u6e10\u6709\u4e86\u6210\u6548\uff0c\u56e0\u6b64\u4ed6\u5728\u5371\u6025\u4e4b\u4e2d\u51fa\u6765\u62e6\u963b\uff0c\u514d\u5f97\u90a3\u4e09\u4eba\u574f\u4e86\u5927\u4e8b\u3002 \u3000\u3000\u201c\u90a3\u5e74\u4e09\u6708\u5341\u4e94\uff0c\u4ed6\u4e0e\u4e09\u4e2a\u4e49\u5f1f\u4f1a\u996e\u6ec7\u6c60\uff0c\u6b63\u8981\u5c06\u95ef\u738b\u672a\u6b7b\u3001\u5434\u4e09\u6842\u5c06\u53cd\u7684\u79cd\u79cd\u4e8b\u8ff9\u76f4\u8bf4\u51fa\u6765\uff0c\u54ea\u77e5\u4e09\u4e2a\u4e49\u5f1f\u5fcc\u60ee\u4ed6\u529f\u592b\u4e86\u5f97\uff0c\u4e0d\u6562\u4e0e\u4ed6\u591a\u8c08\uff0c\u4e58\u4ed6\u4e00\u4e2a\u63aa\u624b\u4e0d\u53ca\u4fbf\u5c06\u4ed6\u6740\u6b7b\u3002\u98de\u5929\u72d0\u72f8\u4e34\u6b7b\u4e4b\u9645\uff0c\u6d41\u6cea\u8bf4\u9053\uff1a\u2018\u53ef\u60dc\u6211\u5927\u4e8b\u4e0d\u6210\u3002\u2019\u5c31\u662f\u6307\u7684\u6b64\u4e8b\u3002\u4ed6\u53c8\u9053\uff1a\u2018\u5143\u5e05\u7237\u662f\u5728\u77f3\u95e8\u5939\u2026\u2026\u2019\u539f\u6765\u95ef\u738b\u662f\u5728\u77f3\u95e8\u53bf\u5939\u5c71\u666e\u6148\u5bfa\u51fa\u5bb6\uff0c\u6cd5\u540d\u53eb\u4f5c\u5949\u5929\u7389\u548c\u5c1a\u3002\u95ef\u738b\u4e00\u76f4\u6d3b\u5230\u5eb7\u7199\u7532\u8fb0\u5e74\u4e8c\u6708\uff0c\u5230\u4e03\u5341\u5c81\u7684\u9ad8\u9f84\u65b9\u624d\u901d\u4e16\u3002\u95ef\u738b\u8d77\u4e8b\u4e4b\u65f6\uff0c\u79f0\u4e3a\u2018\u5949\u5929\u5021\u4e49\u5927\u5143\u5e05\u2019\uff0c\u4ed6\u7684\u6cd5\u540d\u5b9e\u662f\u2018\u5949\u5929\u738b\u2019\uff0c\u4e3a\u4e86\u9690\u8bb3\uff0c\u624d\u5728\u2018\u738b\u2019\u5b57\u4e2d\u52a0\u4e86\u4e00\u70b9\uff0c\u6210\u4e3a\u2018\u7389\u2019\u5b57\u3002\u201d \u3000\u3000\u4f17\u4eba\u542c\u82d7\u82e5\u5170\u5148\u524d\u6240\u8ff0\u6545\u4e8b\uff0c\u53ea\u9053\u98de\u5929\u72d0\u72f8\u5978\u6076\u65e0\u6bd4\uff0c\u54ea\u77e5\u4e2d\u95f4\u53e6\u6709\u5982\u6b64\u91cd\u5927\u7684\u79d8\u5bc6\uff0c\u53ea\u662f\u8fc7\u4e8e\u602a\u5f02\uff0c\u4e00\u65f6\u5b9e\u5728\u96be\u4ee5\u7f6e\u4fe1\u3002 \u3000\u3000\u5e73\u963f\u56db\u89c1\u4f17\u4eba\u5c06\u4fe1\u5c06\u7591\uff0c\u82d7\u82e5\u5170\u8138\u4e0a\u4e5f\u6709\u8be7\u5f02\u4e4b\u8272\uff0c\u63a5\u7740\u9053\uff1a\u201c\u82d7\u59d1\u5a18\uff0c\u4f60\u5148\u524d\u8bf4\u9053\uff0c\u98de\u5929\u72d0\u72f8\u7684\u513f\u5b50\u4e09\u6708\u5341\u4e94\u90a3\u5929\u627e\u5230\u4e09\u4f4d\u7ed3\u4e49\u53d4\u53d4\u5bb6\u91cc\uff0c\u8ddf\u4ed6\u4eec\u5728\u5bc6\u5ba4\u4e2d\u8bf4\u4e86\u4e00\u9635\u5b50\u8bdd\uff0c\u90a3\u4e09\u4eba\u5c31\u51fa\u6765\u5f53\u4f17\u81ea\u520e\u3002\u4f60\u9053\u5728\u90a3\u5bc6\u5ba4\u4e4b\u4e2d\uff0c\u56db\u4eba\u8bf4\u4e86\u4e9b\u4ec0\u4e48\u8bdd\uff1f\u201d\u82d7\u82e5\u5170\u9053\uff1a\u201c\u83ab\u975e\u90a3\u513f\u5b50\u5c06\u98de\u5929\u72d0\u72f8\u7684\u82e6\u5fc3\u8ddf\u4e09\u4f4d\u53d4\u53d4\u8bf4\u4e86\uff1f\u201d \u3000\u3000\u5e73\u963f\u56db\u9053\uff1a\u201c\u662f\u554a\uff0c\u8fd9\u4e09\u4eba\u82e5\u4e0d\u662f\u81ea\u6068\u6740\u9519\u4e86\u4e49\u5144\uff0c\u600e\u80fd\u5f53\u4f17\u81ea\u520e\uff1f\u53ef\u662f\u90a3\u65f6\u95ef\u738b\u5c1a\u5728\u4eba\u4e16\uff0c\u8fd9\u673a\u5bc6\u4e07\u4e07\u6cc4\u6f0f\u4e0d\u5f97\u3002\u53ea\u53ef\u60dc\u8fd9\u4e09\u4eba\u867d\u7136\u5fc3\u5b58\u5fe0\u4e49\uff0c\u6027\u5b50\u5374\u8fc7\u4e8e\u5364\u83bd\uff0c\u6740\u4e49\u5144\u5df2\u662f\u9519\u4e86\uff0c\u5f53\u4f17\u81ea\u6740\u5374\u53c8\u5feb\u4e86\u4e00\u6b65\uff0c\u4e8b\u5148\u53c8\u6ca1\u5631\u5490\u4f17\u5b50\u5f1f\u4e0d\u5f97\u627e\u90a3\u59d3\u80e1\u7684\u513f\u5b50\u62a5\u4ec7\uff0c\u5f53\u65f6\u5b9a\u662f\u60b2\u75db\u6094\u6068\u5df2\u6781\uff0c\u518d\u4e5f\u60f3\u4e0d\u5230\u5176\u4f59\uff0c\u4ee5\u81f4\u4e00\u9519\u518d\u9519\u3002\u80e1\u82d7\u8303\u7530\u56db\u5bb6\uff0c\u4ece\u6b64\u4e16\u4e16\u4ee3\u4ee3\uff0c\u7ed3\u4e0b\u6df1\u4ec7\u5927\u6028\u3002 \u3000\u3000\u201c\u90a3\u513f\u5b50\u4e0e\u4e09\u4f4d\u53d4\u53d4\u5728\u5bc6\u5ba4\u4e2d\u8a00\u660e\uff0c\u8fd9\u79d8\u5bc6\u5fc5\u987b\u7b49\u5230\u4e00\u767e\u5e74\u4e4b\u540e\u7684\u4e59\u4e11\u5e74\u65b9\u80fd\u516c\u4e4b\u4e8e\u4e16\u3002\u90a3\u65f6\u95ef\u738b\u5bff\u547d\u518d\u957f\uff0c\u4e5f\u5fc5\u5df2\u7ecf\u901d\u4e16\u3002\u82e5\u662f\u6cc4\u6f0f\u65e9\u4e86\uff0c\u6e05\u5ef7\u5b9a\u7136\u5927\u4e3e\u641c\u6355\uff0c\u81ea\u4f1a\u5371\u53ca\u95ef\u738b\u6027\u547d\u3002\u80e1\u5bb6\u4e16\u4ee3\u77e5\u9053\u8fd9\u79d8\u5bc6\uff0c\u82d7\u8303\u7530\u4e09\u5bb6\u5374\u4e0d\u77e5\u6653\u3002\u5f85\u4f20\u5230\u80e1\u4e00\u5200\u5927\u7237\u624b\u91cc\uff0c\u767e\u5e74\u4e4b\u671f\u5df2\u8fc7\uff0c\u4e8e\u662f\u4ed6\u547d\u90a3\u8dcc\u6253\u533b\u751f\u960e\u57fa\u53bb\u5bf9\u91d1\u9762\u4f5b\u8bf4\u77e5\u6b64\u4e8b\u3002 \u3000\u3000\u201c\u90a3\u7b2c\u4e8c\u4ef6\u4e8b\uff0c\u8bf4\u7684\u662f\u91d1\u9762\u4f5b\u4e4b\u7236\u4e0e\u7530\u76f8\u516c\u4e4b\u7236\u7684\u6b7b\u56e0\u3002 \u3000\u3000\u5728\u82d7\u80e1\u4e8c\u4f4d\u62da\u6597\u7684\u5341\u4f59\u5e74\u524d\uff0c\u8fd9\u59d3\u82d7\u59d3\u7530\u7684\u4e24\u4f4d\u4e0a\u8f88\u540c\u8d74\u5173\u5916\uff0c\u4ece\u6b64\u5f71\u8e2a\u5168\u65e0\u3002 \u3000\u3000\u201c\u8fd9\u4e24\u4eba\u6b66\u827a\u9ad8\u5f3a\uff0c\u540d\u9707\u6c5f\u6e56\uff0c\u5982\u6b64\u4e0d\u660e\u4e0d\u767d\u7684\u6b7b\u4e86\uff0c\u5bb3\u6b7b\u4ed6\u4eec\u7684\u5b9a\u662f\u5927\u6709\u6765\u5934\u4e4b\u4eba\u3002\u80e1\u5927\u7237\u5411\u5728\u5173\u5916\uff0c\u80e1\u5bb6\u4e0e\u82d7\u7530\u4e24\u5bb6\u53c8\u662f\u4e16\u4ec7\uff0c\u4efb\u8c01\u60f3\u6765\uff0c\u90fd\u5fc5\u662f\u4ed6\u4e0b\u7684\u6bd2\u624b\u3002\u91d1\u9762\u4f5b\u4e0e\u7530\u76f8\u516c\u5206\u522b\u67e5\u8bbf\u4e86\u5341\u4f59\u5e74\uff0c\u67e5\u4e0d\u51fa\u534a\u70b9\u7aef\u502a\uff0c\u8fde\u80e1\u5927\u7237\u4e5f\u59cb\u7ec8\u89c1\u4e0d\u5230\u4e00\u9762\u3002\u91d1\u9762\u4f5b\u65e0\u6cd5\u53ef\u65bd\uff0c\u8fd9\u624d\u5927\u8086\u5ba3\u626c\u4ed6\u2018\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u2019\u7684\u4e03\u5b57\u5916\u53f7\uff0c\u597d\u6fc0\u80e1\u5927\u7237\u8fdb\u5173\u3002\u80e1\u5927\u7237\u77e5\u9053\u4ed6\u7684\u7528\u610f\uff0c\u5374\u4e0d\u7406\u4f1a\uff0c\u4e00\u9762\u4e5f\u5728\u5230\u5904\u5bfb\u8bbf\u82d7\u7530\u4e24\u4f4d\u4e0a\u8f88\uff0c\u5fc3\u60f3\u53ea\u6709\u8bbf\u5230\u8fd9\u4e24\u4eba\u7684\u4e0b\u843d\uff0c\u65b9\u80fd\u4e0e\u91d1\u9762\u4f5b\u76f8\u89c1\uff0c\u6d17\u5237\u81ea\u5df1\u7684\u51a4\u6789\u3002 \u3000\u3000\u201c\u7687\u5929\u4e0d\u8d1f\u82e6\u5fc3\u4eba\uff0c\u4ed6\u8bbf\u67e5\u6570\u5e74\uff0c\u7ec8\u4e8e\u5f97\u77e5\u4e8c\u4eba\u786e\u606f\u3002\u80e1\u592b\u4eba\u8fd9\u65f6\u5df2\u6000\u4e86\u5b55\uff0c\u5979\u662f\u6c5f\u5357\u4eba\uff0c\u4e34\u5230\u751f\u80b2\u4e4b\u65f6\uff0c\u5ffd\u7136\u601d\u4e61\u4e4b\u60c5\u6df1\u5207\u3002\u80e1\u5927\u7237\u4f53\u8d34\u592b\u4eba\uff0c\u4fbf\u966a\u4e86\u5979\u5357\u4e0b\u3002\u884c\u5230\u5510\u5b98\u5c6f\uff0c\u4ed6\u5148\u4e0e\u8303\u7530\u4e8c\u4eba\u52a8\u4e0a\u4e86\u624b\uff0c\u540e\u6765\u53c8\u9047\u5230\u91d1\u9762\u4f5b\u3002\u80e1\u5927\u7237\u547d\u960e\u57fa\u53bb\u8ddf\u4ed6\u8bf4\uff0c\u5f85\u80e1\u5927\u7237\u9001\u592b\u4eba\u56de\u5f52\u6545\u4e61\u4e4b\u540e\uff0c\u53ef\u4eb2\u81ea\u5e26\u4ed6\u53bb\u8fce\u56de\u7236\u4eb2\u5c38\u9996\uff0c\u4ed6\u7236\u4eb2\u5982\u4f55\u6b7b\u6cd5\uff0c\u4e00\u770b\u4fbf\u77e5\u3002\u53ea\u662f\u82d7\u7530\u8fd9\u4e24\u4f4d\u4e0a\u8f88\u6b7b\u5f97\u592a\u4e5f\u4e0d\u591f\u4f53\u9762\uff0c\u80e1\u5927\u7237\u4e0d\u4fbf\u5f53\u9762\u8ff0\u8bf4\uff0c\u53ea\u597d\u9886\u4ed6\u4eec\u4eb2\u81ea\u53bb\u770b\u3002 \u3000\u3000\u201c\u7b2c\u4e09\u4ef6\u4e8b\uff0c\u5219\u662f\u5173\u6d89\u5230\u95ef\u738b\u7684\u90a3\u67c4\u519b\u5200\u4e86\u3002\u8fd9\u67c4\u519b\u5200\u4e4b\u4e2d\u85cf\u7740\u4e00\u4e2a\u6781\u5927\u7684\u5b9d\u85cf\uff0c\u9ec4\u91d1\u767d\u94f6\u4e0d\u5fc5\u8bf4\uff0c\u5947\u73cd\u5f02\u5b9d\u4e5f\u662f\u4e0d\u8ba1\u5176\u6570\u3002\u201d \u3000\u3000\u4f17\u4eba\u5927\u5947\uff0c\u5fc3\u60f3\u8fd9\u67c4\u519b\u5200\u4e4b\u4e2d\u8fde\u4e00\u53ea\u5c0f\u5143\u5b9d\u4e5f\u85cf\u4e0d\u4e0b\uff0c\u8bf4\u4ec0\u4e48\u5947\u73cd\u5f02\u5b9d\u4e0d\u8ba1\u5176\u6570\uff1f \u3000\u3000\u53ea\u542c\u5e73\u963f\u56db\u9053\uff1a\u201c\u90a3\u5929\u665a\u4e0a\uff0c\u80e1\u5927\u7237\u8ddf\u960e\u57fa\u8bf4\u4e86\u8fd9\u56de\u4e8b\u7684\u7f18\u7531\u3002\u4f17\u4f4d\u4e00\u542c\uff0c\u90a3\u5c31\u6beb\u4e0d\u5947\u602a\u3002 \u3000\u3000\u201c\u95ef\u738b\u7834\u4e86\u5317\u4eac\u4e4b\u540e\uff0c\u660e\u671d\u7684\u7687\u4eb2\u56fd\u621a\u3001\u5927\u81e3\u5927\u5c06\u5c3d\u6570\u6295\u964d\u3002\u8fd9\u4e9b\u4eba\u65e0\u4e0d\u5bb6\u8d44\u8c6a\u5bcc\uff0c\u56fd\u738b\u90e8\u4e0b\u7684\u5c06\u9886\u903c\u4ed6\u4eec\u732e\u51fa\u91d1\u94f6\u73e0\u5b9d\u8d4e\u547d\u3002\u6570\u65e5\u4e4b\u95f4\uff0c\u8d22\u5b9d\u5c71\u79ef\uff0c\u54ea\u91cc\u6570\u5f97\u6e05\u4e86\u3002\u540e\u6765\u95ef\u738b\u9000\u51fa\u5317\u4eac\uff0c\u6d3e\u4e86\u4eb2\u4fe1\u5c06\u9886\uff0c\u62bc\u7740\u8d22\u5b9d\u53bb\u85cf\u5728\u4e00\u4e2a\u6781\u7a33\u59a5\u7684\u6240\u5728\uff0c\u4ee5\u4fbf\u5c06\u6765\u5377\u571f\u91cd\u6765\u4e4b\u65f6\u4f5c\u4e3a\u519b\u9977\u3002\u4ed6\u5c06\u85cf\u5b9d\u7684\u6240\u5728\u7ed8\u6210\u4e00\u56fe\uff0c\u800c\u770b\u56fe\u5bfb\u5b9d\u7684\u5173\u952e\uff0c\u5374\u7f6e\u5728\u519b\u5200\u4e4b\u4e2d\u3002\u4e5d\u5bab\u5c71\u5175\u8d25\u9003\u4ea1\uff0c\u95ef\u738b\u5c06\u5b9d\u85cf\u4e4b\u56fe\u4e0e\u519b\u5200\u90fd\u4ea4\u7ed9\u4e86\u98de\u5929\u72d0\u72f8\u3002\u540e\u6765\u98de\u5929\u72d0\u72f8\u88ab\u6740\uff0c\u4e00\u56fe\u4e00\u5200\u843d\u5165\u4e09\u4f4d\u4e49\u5f1f\u624b\u4e2d\uff0c\u4f46\u4e0d\u4e45\u53c8\u88ab\u98de\u5929\u72d0\u72f8\u7684\u513f\u5b50\u593a\u53bb\u3002 \u3000\u3000\u201c\u767e\u5e74\u6765\u8f97\u8f6c\u4e89\u593a\uff0c\u7ec8\u4e8e\u519b\u5200\u7531\u5929\u9f99\u95e8\u7530\u6c0f\u638c\u7ba1\uff0c\u85cf\u5b9d\u4e4b\u56fe\u5374\u7531\u82d7\u5bb6\u5bb6\u4f20\u3002\u53ea\u662f\u82d7\u7530\u4e24\u5bb6\u4e0d\u77e5\u5176\u4e2d\u6709\u8fd9\u6837\u4e00\u4e2a\u5927\u79d8\u5bc6\uff0c\u662f\u4ee5\u6ca1\u53bb\u53d1\u6398\u5b9d\u85cf\u3002\u8fd9\u79d8\u5bc6\u7531\u80e1\u5bb6\u4e16\u4ee3\u76f8\u4f20\uff0c\u53ef\u662f\u59d3\u80e1\u7684\u6ca1\u519b\u5200\u5730\u56fe\uff0c\u81ea\u4e5f\u65e0\u6cd5\u627e\u5230\u5b9d\u85cf\u3002 \u3000\u3000\u201c\u80e1\u5927\u7237\u5c06\u8fd9\u4e8b\u544a\u77e5\u91d1\u9762\u4f5b\uff0c\u8bf7\u4ed6\u53bb\u6398\u51fa\u5b9d\u85cf\uff0c\u6551\u6d4e\u5929\u4e0b\u7a77\u4eba\uff0c\u751a\u800c\u7528\u8fd9\u7b14\u5927\u8d22\u5b9d\u6765\u5927\u4e3e\u8d77\u4e8b\uff0c\u9a71\u9010\u6ee1\u4eba\u51fa\u5173\uff0c\u8fd8\u6211\u6c49\u5bb6\u6cb3\u5c71\u3002 \u3000\u3000\u201c\u80e1\u5927\u7237\u6240\u8bf4\u8fd9\u4e09\u4ef6\u4e8b\uff0c\u6ca1\u4e00\u4ef6\u4e0d\u662f\u5173\u7cfb\u6781\u5927\u3002\u91d1\u9762\u4f5b\u5f97\u77e5\u4e4b\u540e\uff0c\u4f55\u4ee5\u4ecd\u6765\u627e\u4ed6\u6bd4\u6b66\uff0c\u975e\u62da\u4e2a\u4f60\u6b7b\u6211\u6d3b\u4e0d\u53ef\uff0c\u80e1\u5927\u7237\u76f4\u5230\u4e34\u6b7b\uff0c\u4ecd\u662f\u4e0d\u89e3\u3002\u53ea\u6015\u91d1\u9762\u4f5b\u6789\u79f0\u5927\u4fa0\uff0c\u662f\u975e\u66f2\u76f4\uff0c\u5374\u4e5f\u8fa8\u4e0d\u660e\u767d\uff1b\u53c8\u6216\u56e0\u8fd9\u4e09\u4ef6\u4e8b\u8bf4\u6765\u90fd\u662f\u8038\u4eba\u542c\u95fb\uff0c\u592a\u8fc7\u4e0d\u5408\u60c5\u7406\uff0c\u91d1\u9762\u4f5b\u4e00\u4ef6\u90fd\u4e0d\u76f8\u4fe1\uff0c\u4ea6\u672a\u53ef\u77e5\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u957f\u957f\u53f9\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u9676\u767e\u5c81\u4e00\u76f4\u5728\u65c1\u503e\u542c\uff0c\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u6b64\u65f6\u5ffd\u7136\u63d2\u53e3\u9053\uff1a\u201c\u91d1\u9762\u4f5b\u4f55\u4ee5\u4ecd\u8981\u627e\u80e1\u4e00\u5200\u6bd4\u6b66\uff0c\u5176\u4e2d\u539f\u56e0\u6211\u5374\u660e\u767d\u3002\u6b64\u4e8b\u6682\u4e14\u4e0d\u8bf4\u3002\u6211\u95ee\u4f60\uff0c\u4f60\u5230\u8fd9\u5c71\u5cf0\u4e0a\u6765\u5e72\u4ec0\u4e48\uff1f\u201d\u8fd9\u6b63\u662f\u4f17\u4eba\u5fc3\u4e2d\u6b32\u95ee\u4e4b\u4e8b\u3002 \u3000\u3000\u53ea\u542c\u5e73\u963f\u56db\u51db\u7136\u9053\uff1a\u201c\u6211\u662f\u4e3a\u80e1\u5927\u7237\u62a5\u4ec7\u6765\u7684\u3002\u201d\u9676\u767e\u5c81\u9053\uff1a\u201c\u62a5\u4ec7\uff1f\u627e\u8c01\u62a5\u4ec7\uff1f\u201d\u5e73\u963f\u56db\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u627e\u5bb3\u6b7b\u80e1\u5927\u7237\u7684\u4eba\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u8138\u8272\u82cd\u767d\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u8981\u627e\u6211\u7239\u7239\u5417\uff1f\u201d\u5e73\u963f\u56db\u9053\uff1a\u201c\u5bb3\u6b7b\u80e1\u5927\u7237\u7684\u4e0d\u662f\u91d1\u9762\u4f5b\uff0c\u662f\u4ece\u524d\u53eb\u505a\u8dcc\u6253\u533b\u751f\u960e\u57fa\u3001\u73b0\u4e0b\u51fa\u4e86\u5bb6\u505a\u548c\u5c1a\u3001\u53eb\u4f5c\u5b9d\u6811\u7684\u90a3\u4eba\u3002\u201d\u4f17\u4eba\u5927\u4e3a\u5947\u602a\uff0c\u5747\u60f3\uff1a \u3000\u3000\u201c\u80e1\u4e00\u5200\u600e\u4f1a\u662f\u5b9d\u6811\u5bb3\u6b7b\u7684\uff1f\u201d \u3000\u3000\u5b9d\u6811\u957f\u8eab\u7ad9\u8d77\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u597d\u554a\uff0c\u4f60\u6709\u672c\u4e8b\u5c31\u6765\u6740\u6211\u3002\u5feb\u52a8\u624b\u5427\uff01\u201d\u5e73\u963f\u56db\u9053\uff1a\u201c\u6211\u65e9\u5df2\u52a8\u4e86\u624b\uff0c\u4ece\u4eca\u5929\u7b97\u8d77\uff0c\u7ba1\u6559\u4f60\u6d3b\u4e0d\u8fc7\u4e03\u65e5\u4e03\u591c\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u60ca\uff0c\u5747\u60f3\u4e0d\u77e5\u4ed6\u600e\u6837\u6697\u4e2d\u4e0b\u4e86\u6bd2\u624b\uff1f\u5b9d\u6811\u4e0d\u7981\u6697\u6697\u5fc3\u60ca\uff0c\u5634\u4e0a\u5374\u786c\uff0c\u9a82\u9053\uff1a\u201c\u51ed\u4f60\u8fd9\u70b9\u81ed\u672c\u4e8b\uff0c\u4e5f\u80fd\u7b97\u8ba1\u4e8e\u6211\uff1f\u201d \u3000\u3000\u5e73\u963f\u56db\u5389\u58f0\u9053\uff1a\u201c\u4e0d\u4f46\u662f\u4f60\uff0c\u8fd9\u5c71\u5cf0\u4e0a\u7537\u5973\u8001\u5e7c\uff0c\u4e2a\u4e2a\u6d3b\u4e0d\u8fc7\u4e03\u65e5\u4e03\u665a\uff01\u201d \u3000\u3000\u4f17\u4eba\u90fd\u662f\u4e00\u60ca\uff0c\u6216\u6115\u7136\u79bb\u5ea7\uff0c\u6216\u77aa\u76ee\u6b20\u8eab\u3002\u5404\u4eba\u81ea\u4e0a\u96ea\u5cf0\u4e4b\u540e\uff0c\u4e00\u76f4\u5fc3\u795e\u4e0d\u5b89\uff0c\u5e73\u963f\u56db\u6b64\u8a00\u867d\u4f3c\u8352\u8bde\u4e0d\u7ecf\uff0c\u4f46\u6b64\u65f6\u542c\u6765\uff0c\u65e0\u4e0d\u4e3a\u4e4b\u8038\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u5b9d\u6811\u5389\u58f0\u9053\uff1a\u201c\u4f60\u5728\u8336\u6c34\u70b9\u5fc3\u4e2d\u4e0b\u4e86\u6bd2\u836f\u4e48\uff1f\u201d\u5e73\u963f\u56db\u51b7\u7136\u9053\uff1a\u201c\u82e5\u662f\u53eb\u4f60\u4e2d\u6bd2\uff0c\u6b7b\u5f97\u592a\u5feb\uff0c\u5c82\u80fd\u5982\u6b64\u4fbf\u5b9c\uff1f\u6211\u8981\u53eb\u4f60\u6162\u6162\u997f\u6b7b\u3002\u201d\u66f9\u4e91\u5947\u3001\u9676\u767e\u5c81\u3001\u90d1\u4e09\u5a18\u7b49\u4e00\u9f50\u53eb\u9053\uff1a\u201c\u997f\u6b7b\uff1f\u201d \u3000\u3000\u5e73\u963f\u56db\u4e0d\u52a8\u58f0\u8272\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff01\u8fd9\u5cf0\u4e0a\u672c\u6709\u5341\u65e5\u4e4b\u7cae\uff0c\u73b0\u4e0b\u5374\u4e00\u65e5\u4e5f\u6ca1\u6709\u4e86\uff0c\u90fd\u7ed9\u6211\u5012\u4e0b\u5c71\u5cf0\u53bb\u4e86\u3002\u201d \u3000\u3000\u4f17\u4eba\u60ca\u53eb\u58f0\u4e2d\uff0c\u5b9d\u6811\u7a81\u65bd\u64d2\u62ff\u624b\u6293\u4f4f\u4e86\u4ed6\u5de6\u81c2\u3002\u5e73\u963f\u56db\u53f3\u81c2\u65e9\u65ad\uff0c\u6beb\u4e0d\u6297\u62d2\uff0c\u53ea\u662f\u5fae\u5fae\u51b7\u7b11\u3002\u66f9\u4e91\u5947\u4e0e\u5468\u4e91\u9633\u4f38\u81c2\u63e1\u62f3\uff0c\u7ad9\u5728\u4ed6\u7684\u8eab\u524d\uff0c\u53ea\u8981\u4ed6\u5fae\u6709\u52a8\u6b66\u4e4b\u610f\uff0c\u7acb\u5373\u53d1\u62f3\u6bb4\u51fb\u3002 \u3000\u3000\u4e8e\u7ba1\u5bb6\u6025\u5954\u5165\u5185\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u56de\u5230\u5927\u5385\uff0c\u8138\u8272\u82cd\u767d\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u5e84\u5b50\u91cc\u7684\u7cae\u98df\u3001\u725b\u8089\u7f8a\u8089\u3001\u9e21\u9e2d\u3001\u852c\u83dc\uff0c\u679c\u771f\u2026\u2026\u679c\u771f\u662f\u4e00\u53e4\u8111\u513f\uff0c\u90fd\u2026\u2026\u90fd\u7ed9\u8fd9\u53ae\u5012\u4e0b\u4e86\u5c71\u5cf0\u3002\u201d \u3000\u3000\u53ea\u542c\u7830\u7684\u4e00\u54cd\uff0c\u66f9\u4e91\u5947\u4e00\u62f3\u6253\u5728\u5e73\u963f\u56db\u7684\u80f8\u53e3\u3002\u8fd9\u4e00\u62f3\u52b2\u529b\u597d\u5927\uff0c\u5e73\u963f\u56db\u54c7\u7684\u4e00\u58f0\uff0c\u5410\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u4f46\u8138\u4e0a\u4ecd\u662f\u5fae\u5fae\u51b7\u7b11\uff0c\u7adf\u65e0\u534a\u70b9\u60e7\u8272\u3002 \u3000\u3000\u5b9d\u6811\u9053\uff1a\u201c\u7cae\u4ed3\u548c\u53a8\u623f\u91cc\u90fd\u6ca1\u4eba\u4e48\uff1f\u201d\u4e8e\u7ba1\u5bb6\u9053\uff1a\u201c\u6709\u4e09\u4e2a\u5e72\u7c97\u6d3b\u7684\uff0c\u90fd\u6559\u8fd9\u53ae\u7ed9\u7ed1\u4e86\u3002\u5509\uff0c\u5148\u524d\u90a3\u4e24\u4e2a\u5c0f\u9b3c\u5728\u5385\u4e0a\u95f9\u4e8b\uff0c\u5927\u4f19\u513f\u90fd\u51fa\u6765\u89c2\u770b\uff0c\u8c01\u77e5\u662f\u90a3\u7684\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\u3002 \u3000\u3000\u82d7\u59d1\u5a18\uff0c\u6211\u4eec\u53ea\u9053\u8fd9\u53ae\u662f\u60a8\u5e26\u6765\u7684\u4e0b\u4eba\u3002\u201d\u82d7\u82e5\u5170\u6447\u5934\u9053\uff1a\u201c\u4e0d\u662f\u3002\u6211\u5374\u5f53\u4ed6\u662f\u5e84\u4e0a\u7684\u7ba1\u5bb6\u3002\u201d\u5b9d\u6811\u9053\uff1a\u201c\u5403\u7684\u4e1c\u897f\u4e00\u70b9\u90fd\u6ca1\u7559\u4e0b\u4e48\uff1f\u201d\u4e8e\u7ba1\u5bb6\u60e8\u7136\u6447\u5934\u3002 \u3000\u3000\u66f9\u4e91\u5947\u4e3e\u8d77\u62f3\u5934\uff0c\u53c8\u8981\u4e00\u62f3\u6253\u53bb\u3002\u82d7\u82e5\u5170\u9053\uff1a\u201c\u4e14\u6162\uff0c\u66f9\u5927\u7237\uff0c\u4f60\u5fd8\u4e86\u6211\u8bf4\u8fc7\u7684\u8bdd\u3002\u201d\u66f9\u4e91\u5947\u6115\u7136\u4e0d\u89e3\uff0c\u62f3\u5934\u4e3e\u5728\u534a\u7a7a\uff0c\u5374\u4e0d\u843d\u4e0b\u3002\u82d7\u82e5\u5170\u9053\uff1a\u201c\u4ed6\u62b1\u7740\u6211\u7239\u7239\u7684\u540d\u53f7\uff0c\u6211\u8bf4\u8fc7\u8c01\u4e5f\u4e0d\u8bb8\u4f24\u4ed6\u3002\u201d\u66f9\u4e91\u5947\u9053\uff1a\u201c\u54b1\u4eec\u5927\u4f19\u513f\u6027\u547d\u90fd\u8981\u9001\u5728\u4ed6\u624b\u91cc\uff0c\u4f60\u2026\u2026\u4f60\u600e\u4e48\u2026\u2026\u201d \u3000\u3000\u82d7\u82e5\u5170\u6447\u5934\u9053\uff1a\u201c\u6b7b\u6d3b\u662f\u4e00\u56de\u4e8b\uff0c\u8bf4\u8fc7\u7684\u8bdd\uff0c\u53ef\u603b\u5f97\u7b97\u6570\u3002 \u3000\u3000\u8fd9\u4eba\u628a\u5cf0\u4e0a\u7684\u7cae\u98df\u90fd\u629b\u4e86\u4e0b\u53bb\uff0c\u5927\u5bb6\u56fa\u7136\u8981\u997f\u6b7b\uff0c\u4ed6\u81ea\u5df1\u53ef\u4e5f\u6d3b\u4e0d\u6210\u3002\u4e00\u4e2a\u4eba\u62da\u7740\u6027\u547d\u4e0d\u8981\u6765\u505a\u4e00\u4ef6\u4e8b\uff0c\u603b\u6709\u91cd\u5927\u4e4b\u6781\u7684\u539f\u56e0\u3002\u5b9d\u6811\u5927\u5e08\uff0c\u66f9\u5927\u7237\uff0c\u751f\u6b7b\u6709\u547d\uff0c\u7740\u6025\u4e5f\u662f\u6ca1\u7528\u3002\u4e14\u542c\u4ed6\u8bf4\u8bf4\uff0c\u5230\u5e95\u54b1\u4eec\u662f\u5426\u5f53\u771f\u8be5\u6b7b\u3002\u201d\u5979\u8fd9\u756a\u8bdd\u8bf4\u5f97\u5fc3\u5e73\u6c14\u548c\uff0c\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u5374\u6709\u4e00\u80a1\u6781\u5927\u529b\u91cf\uff0c\u7adf\u8bf4\u5f97\u5b9d\u6811\u653e\u5f00\u4e86\u5e73\u963f\u56db\u7684\u624b\u81c2\uff0c\u66f9\u4e91\u5947\u4e5f\u81ea\u6c14\u9f13\u9f13\u7684\u5f52\u5ea7\u3002 \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u5e73\u7237\uff0c\u4f60\u8981\u8ba9\u5927\u4f19\u513f\u4e00\u9f50\u997f\u6b7b\uff0c\u8fd9\u4e2d\u95f4\u7684\u539f\u56e0\uff0c\u80fd\u4e0d\u80fd\u7ed9\u6211\u4eec\u8bf4\u8bf4\uff1f\u4f60\u662f\u4e3a\u80e1\u4e00\u5200\u4f2f\u4f2f\u62a5\u4ec7\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5e73\u963f\u56db\u9053\uff1a\u201c\u4f60\u79f0\u6211\u5e73\u7237\u53ef\u4e0d\u6562\u5f53\u3002\u6211\u8fd9\u4e00\u751f\u4e4b\u4e2d\uff0c\u53ea\u6709\u79f0\u522b\u4eba\u505a\u7237\u7684\u4efd\u513f\uff0c\u53ef\u6ca1\u798f\u6c14\u53d7\u4eba\u5bb6\u8fd9\u4e48\u79f0\u547c\u3002\u82d7\u59d1\u5a18\uff0c\u5f53\u5e74\u80e1\u5927\u7237\u7ed9\u6211\u94f6\u5b50\uff0c\u6551\u4e86\u6211\u4e00\u5bb6\u4e09\u53e3\u6027\u547d\uff0c\u6211\u81ea\u662f\u611f\u6fc0\u4e07\u5206\u3002 \u3000\u3000\u53ef\u662f\u6709\u4e00\u4ef6\u4e8b\u6211\u662f\u540c\u6837\u7684\u611f\u6fc0\u3002\u4f60\u9053\u662f\u4ec0\u4e48\u4e8b\uff1f\u4eba\u4eba\u53eb\u6211\u765e\u75e2\u5934\u963f\u56db\uff0c\u8f7b\u6211\u8d31\u6211\uff0c\u80e1\u5927\u7237\u5374\u53eb\u6211\u2018\u5c0f\u5144\u5f1f\u2019\uff0c\u4e00\u5b9a\u8981\u6211\u53eb\u4ed6\u5927\u54e5\u3002\u6211\u5e73\u963f\u56db\u4e00\u751f\u53d7\u4eba\u547c\u6765\u559d\u53bb\uff0c\u80e1\u5927\u7237\u5374\u8ddf\u6211\u8bf4\uff0c\u4e16\u4eba\u5e76\u65e0\u9ad8\u4f4e\uff0c\u5728\u8001\u5929\u7237\u773c\u4e2d\u770b\u6765\uff0c\u4eba\u4eba\u90fd\u662f\u4e00\u822c\u3002\u6211\u542c\u4e86\u8fd9\u756a\u8bdd\uff0c\u5c31\u4f3c\u4e00\u4e2a\u76f2\u4e86\u5341\u51e0\u5e74\u773c\u7684\u778e\u5b50\uff0c\u5ffd\u7136\u95f4\u89c1\u5230\u4e86\u5149\u660e\u3002\u6211\u9047\u5230\u80e1\u5927\u7237\u53ea\u4e0d\u8fc7\u4e00\u5929\uff0c\u5fc3\u4e2d\u5c31\u5c06\u4ed6\u5f53\u4f5c\u4e86\u4eb2\u4eba\uff0c\u656c\u4ed6\u7231\u4ed6\uff0c\u4fbf\u5982\u662f\u6211\u4eb2\u751f\u7239\u5a18\u4e00\u822c\u3002 \u3000\u3000\u201c\u80e1\u5927\u7237\u548c\u91d1\u9762\u4f5b\u63a5\u8fde\u6597\u4e86\u51e0\u5929\uff0c\u59cb\u7ec8\u4e0d\u5206\u80dc\u8d25\uff0c\u6211\u81ea\u7136\u5f88\u4e3a\u80e1\u5927\u7237\u62c5\u5fc3\u3002\u5230\u6700\u540e\u4e00\u5929\u76f8\u6597\uff0c\u80e1\u5927\u7237\u53d7\u4e86\u6bd2\u5200\u4e4b\u4f24\u800c\u6b7b\u3002\u80e1\u592b\u4eba\u4e5f\u81ea\u6740\u6b89\u592b\uff0c\u90a3\u60c5\u5f62\u6b63\u5982\u82d7\u59d1\u5a18\u6240\u8bf4\u3002\u6211\u4eb2\u773c\u76ee\u7779\uff0c\u5f53\u65f6\u60c5\u666f\uff0c\u51b3\u4e0d\u4f1a\u5fd8\u4e86\u534a\u70b9\u3002\u960e\u5927\u592b\uff0c\u90a3\u5929\u4f60\u5de6\u624b\u633d\u4e86\u836f\u7bb1\uff0c\u80cc\u4e0a\u5305\u88f9\u4e2d\u88c5\u7740\u5341\u591a\u952d\u5927\u94f6\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u90a3\u5929\u4f60\u7a7f\u7740\u9752\u5e03\u9762\u7684\u8001\u7f8a\u76ae\u888d\uff0c\u5934\u4e0a\u6234\u4e00\u9876\u7a7f\u7a9f\u7abf\u7684\u70df\u9ec4\u6be1\u5e3d\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5b9d\u6811\u94c1\u9752\u7740\u8138\uff0c\u62ff\u7740\u5ff5\u73e0\u7684\u53f3\u624b\u5fae\u5fae\u98a4\u52a8\uff0c\u53cc\u76ee\u77aa\u89c6\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u5e73\u963f\u56db\u53c8\u9053\uff1a\u201c\u65e9\u4e00\u65e5\u665a\u4e0a\uff0c\u80e1\u5927\u7237\u548c\u91d1\u9762\u4f5b\u540c\u69bb\u957f\u8c08\uff0c\u960e\u5927\u592b\u5728\u7a97\u5916\u5077\u542c\uff0c\u540e\u6765\u7ed9\u91d1\u9762\u4f5b\u9694\u7a97\u6253\u4e86\u4e00\u62f3\uff0c\u53ea\u6253\u5f97\u773c\u9752\u9f3b\u80bf\uff0c\u6ee1\u8138\u6d41\u8840\u3002\u4ed6\u8bf4\u4ed6\u6328\u6253\u4e4b\u540e\uff0c\u5c31\u53bb\u7761\u4e86\u3002\u53ef\u662f\uff0c\u6211\u77a7\u89c1\u4ed6\u5728\u7761\u89c9\u4e4b\u524d\uff0c\u8fd8\u505a\u4e86\u4e00\u4ef6\u4e8b\u3002\u80e1\u5927\u7237\u4e0e\u91d1\u9762\u4f5b\u540c\u623f\u800c\u7761\uff0c\u4e24\u4eba\u5149\u660e\u78ca\u843d\uff0c\u628a\u5175\u5203\u90fd\u653e\u5728\u5927\u5385\u4e4b\u4e2d\u3002\u960e\u5927\u592b\u4ece\u836f\u7bb1\u91cc\u53d6\u51fa\u4e00\u76d2\u836f\u818f\uff0c\u6084\u6084\u53bb\u6d82\u5728\u4e24\u4eba\u7684\u5200\u5251\u4e4b\u4e0a\u3002\u90a3\u65f6\u5019\u6211\u8fd8\u662f\u4e2a\u5341\u591a\u5c81\u7684\u5b69\u5b50\uff0c\u6beb\u4e0d\u61c2\u4e8b\uff0c\u4e00\u70b9\u4e5f\u6ca1\u77e5\u4ed6\u662f\u5728\u6697\u4f7f\u8be1\u8ba1\uff0c\u76f4\u81f3\u80e1\u5927\u7237\u53d7\u4f24\u4e2d\u6bd2\uff0c\u6211\u624d\u60f3\u5230\u960e\u5927\u592b\u5728\u4e24\u4eba\u5175\u5203\u4e0a\u90fd\u6d82\u4e86\u6bd2\u836f\uff0c\u4ed6\u662f\u76fc\u671b\u82d7\u80e1\u4e8c\u4eba\u540c\u5f52\u4e8e\u5c3d\u3002\u5509\uff0c\u960e\u5927\u592b\u554a\u960e\u5927\u592b\uff0c\u4f60\u5f53\u771f\u662f\u597d\u6bd2\u7684\u5fc3\u80a0\u554a\uff01 \u3000\u3000\u201c\u4ed6\u8981\u91d1\u9762\u4f5b\u6b7b\uff0c\u81ea\u7136\u662f\u4e3a\u4e86\u62a5\u90a3\u4e00\u51fb\u4e4b\u6068\u3002\u53ef\u662f\u80e1\u5927\u7237\u8ddf\u4ed6\u5f80\u65e5\u65e0\u51a4\uff0c\u8fd1\u65e5\u65e0\u4ec7\uff0c\u4ed6\u5e72\u4e48\u5728\u91d1\u9762\u4f5b\u7684\u5251\u4e0a\u4e5f\u8981\u6d82\u4e0a\u6bd2\u836f\uff1f\u6211\u5f53\u65f6\u4e0d\u660e\u767d\uff0c\u540e\u6765\u5e74\u7eaa\u5927\u4e86\uff0c\u624d\u731c\u5230\u4e86\u4ed6\u7684\u5fc3\u610f\u3002\u54fc\uff0c\u6b64\u4eba\u539f\u6765\u662f\u4e3a\u4e86\u56fe\u8c0b\u80e1\u5927\u7237\u90a3\u53ea\u94c1\u76d2\u3002 \u3000\u3000\u201c\u960e\u5927\u592b\u8bf4\u4ed6\u4e0d\u77e5\u90a3\u94c1\u76d2\u4e2d\u88c5\u7740\u4f55\u7269\uff0c\u90a3\u662f\u8bf4\u8c0e\u3002\u4ed6\u662f\u77e5\u9053\u7684\u3002\u80e1\u5927\u7237\u5c06\u94c1\u76d2\u4ea4\u7ed9\u592b\u4eba\u4e4b\u65f6\uff0c\u628a\u76d2\u4e2d\u5404\u7269\u4e00\u8d77\u5012\u5728\u684c\u4e0a\uff0c\u6ee1\u684c\u8000\u773c\u751f\u5149\uff0c\u90fd\u662f\u73cd\u73e0\u5b9d\u7269\u3002\u80e1\u5927\u7237\u8bf4\u9053\uff1a\u2018\u59b9\u5b50\uff0c\u4f60\u4e00\u8eab\u672c\u4e8b\uff0c\u4f46\u6709\u6240\u9700\uff0c\u8d2a\u5b98\u571f\u8c6a\u5bb6\u4e2d\u7684\u91d1\u94f6\uff0c\u81ea\u662f\u624b\u5230\u62ff\u6765\u3002 \u3000\u3000\u53ea\u662f\u51fa\u624b\u591a\u4e86\uff0c\u96be\u514d\u6709\u5dee\u5931\u4e4b\u65e5\uff0c\u6211\u2026\u2026\u6211\u2026\u2026\u2019\u592b\u4eba\u9053\uff1a\u2018\u5927\u54e5\u653e\u5fc3\u3002\u4f60\u82e5\u6709\u4e0d\u6d4b\uff0c\u6211\u4e00\u5fc3\u4e00\u610f\u629a\u517b\u5b69\u5b50\uff0c\u8fd9\u4e9b\u73e0\u5b9d\u6162\u6162\u53d8\u5356\uff0c\u4e5f\u5c3d\u591f\u6bcd\u5b50\u4fe9\u4f7f\u4e00\u8f88\u5b50\u7684\u4e86\u3002\u6211\u4e0d\u518d\u8ddf\u4eba\u52a8\u5200\u52a8\u67aa\u3002 \u3000\u3000\u4e5f\u4e0d\u518d\u65bd\u5c55\u7a7a\u7a7a\u5999\u624b\u5982\u4f55\uff1f\u2019 \u3000\u3000\u201c\u80e1\u5927\u7237\u5927\u7b11\u53eb\u597d\uff0c\u62ff\u8d77\u4e00\u672c\u4e66\u6765\uff0c\u8bf4\u9053\uff1a\u2018\u8fd9\u4e00\u672c\u62f3\u7ecf\u5200\u8c31\uff0c\u662f\u6211\u9ad8\u7956\u4eb2\u624b\u6240\u4e66\u3002\u2019\u592b\u4eba\u63a5\u8fc7\u4e86\uff0c\u7b11\u9053\uff1a\u2018\u597d\u554a\uff0c\u98de\u5929\u72d0\u72f8\u4e00\u8eab\u7684\u672c\u4e8b\u90fd\u5199\u5728\u8fd9\u91cc\u3002\u4f60\u7792\u5f97\u597d\u7a33\u554a\uff0c\u8fde\u6211\u4e5f\u4e0d\u8ba9\u77e5\u9053\u3002\u2019\u80e1\u5927\u7237\u7b11\u9053\uff1a\u2018\u6211\u7956\u5b97\u9057\u8bad\u662f\u4f20\u5b50\u4e0d\u4f20\u5973\uff0c\u4f20\u4f84\u4e0d\u4f20\u59bb\uff0c\u8fd9\u624d\u53eb\u4f5c\u80e1\u5bb6\u5200\u6cd5\u554a\u3002\u2019\u592b\u4eba\u7b11\u9053\uff1a\u2018\u5f85\u5b69\u5b50\u8bc6\u4e86\u5b57\uff0c\u8ba9\u4ed6\u81ea\u770b\uff0c\u6211\u51b3\u4e0d\u5077\u5b66\u5c31\u662f\u3002\u2019\u80e1\u5927\u7237\u53f9\u4e86\u53e3\u6c14\uff0c\u5c06\u5404\u7269\u90fd\u6536\u5165\u94c1\u76d2\uff0c\u518d\u5c06\u76d2\u5b50\u653e\u5728\u592b\u4eba\u6795\u5934\u5e95\u4e0b\u3002 \u3000\u3000\u201c\u540e\u6765\u6211\u89c1\u592b\u4eba\u4e00\u6b7b\uff0c\u6025\u5fd9\u5954\u5230\u5979\u623f\u4e2d\uff0c\u54ea\u77e5\u960e\u5927\u592b\u5df2\u5148\u8fdb\u4e86\u623f\u3002\u6211\u5fc3\u4e2d\u6026\u6026\u4e71\u8df3\uff0c\u5fd9\u8eb2\u5728\u95e8\u540e\uff0c\u53ea\u89c1\u960e\u5927\u592b\u5de6\u624b\u62b1\u7740\u5b69\u5b50\uff0c\u53f3\u624b\u4ece\u6795\u5934\u5e95\u4e0b\u53d6\u51fa\u94c1\u76d2\uff0c\u4f9d\u7167\u80e1\u5927\u7237\u5148\u524d\u5f00\u76d2\u7684\u6cd5\u5b50\uff0c\u5728\u76d2\u5b50\u56db\u89d2\u6380\u4e86\u4e09\u6380\uff0c\u53c8\u5728\u76d2\u5e95\u4e00\u6309\uff0c\u76d2\u76d6\u4fbf\u5f39\u4e86\u5f00\u6765\u3002\u4ed6\u53d6\u51fa\u73cd\u73e0\u5b9d\u7269\u628a\u73a9\uff0c\u998b\u6d8e\u90fd\u6389\u4e86\u4e0b\u6765\uff0c\u5c06\u5b69\u5b50\u5f80\u5730\u4e0b\u4e00\u653e\uff0c\u53c8\u4ece\u76d2\u91cc\u53d6\u51fa\u62f3\u7ecf\u5200\u8c31\u6765\u7ffb\u770b\u3002\u5b69\u5b50\u6ca1\u4eba\u62b1\u4e86\uff0c\u653e\u58f0\u5927\u54ed\u3002\u960e\u5927\u592b\u6015\u4eba\u542c\u89c1\uff0c\u968f\u624b\u5728\u7095\u4e0a\u62c9\u8fc7\u68c9\u88ab\uff0c\u5c06\u5b69\u5b50\u6ca1\u5934\u6ca1\u8111\u7684\u7f69\u4f4f\u3002 \u3000\u3000\u201c\u6211\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u60f3\u65f6\u5019\u4e00\u957f\uff0c\u5b69\u5b50\u4e0d\u95f7\u6b7b\u624d\u602a\uff0c\u5ff5\u53ca\u80e1\u5927\u7237\u5f85\u6211\u7684\u597d\u5904\uff0c\u975e\u8981\u62a2\u6551\u5b69\u5b50\u51fa\u6765\u4e0d\u53ef\u3002\u53ea\u662f\u6211\u5e74\u7eaa\u5c0f\uff0c\u53c8\u4e0d\u4f1a\u6b66\u827a\uff0c\u51b3\u4e0d\u662f\u960e\u5927\u592b\u7684\u5bf9\u624b\uff0c\u53ea\u89c1\u95e8\u8fb9\u501a\u7740\u4e00\u6839\u5927\u95e8\u95e9\uff0c\u5f53\u4e0b\u6084\u6084\u63d0\u5728\u624b\u91cc\uff0c\u8e51\u624b\u8e51\u811a\u8d70\u5230\u4ed6\u7684\u8eab\u540e\uff0c\u5728\u4ed6\u540e\u8111\u4e0a\u731b\u529b\u6253\u4e86\u4e00\u68cd\u3002 \u3000\u3000\u201c\u8fd9\u4e00\u4e0b\u6211\u662f\u51fa\u5c3d\u4e86\u5e73\u751f\u4e4b\u529b\uff0c\u960e\u5927\u592b\u6ca1\u63d0\u9632\uff0c\u54fc\u4e5f\u6ca1\u54fc\u4e00\u58f0\uff0c\u4fbf\u4fef\u8eab\u8dcc\u5012\uff0c\u73e0\u5b9d\u6454\u5f97\u6ee1\u5730\u3002\u6211\u5fd9\u63ed\u5f00\u68c9\u88ab\uff0c\u62b1\u8d77\u5b69\u5b50\uff0c\u5fc3\u60f3\u8fd9\u91cc\u4e2a\u4e2a\u90fd\u662f\u80e1\u5927\u7237\u7684\u4ec7\u4eba\uff0c\u5f97\u5c06\u5b69\u5b50\u62b1\u56de\u5bb6\u53bb\uff0c\u7ed9\u6211\u5988\u629a\u517b\u3002\u6211\u77e5\u9053\u90a3\u672c\u62f3\u7ecf\u5200\u8c31\u5e72\u7cfb\u91cd\u5927\uff0c\u4e0d\u80fd\u843d\u5728\u65c1\u4eba\u624b\u4e2d\uff0c\u5f53\u4e0b\u5230\u960e\u5927\u592b\u624b\u4e2d\u53bb\u62ff\u3002\u54ea\u77e5\u4ed6\u6655\u53bb\u65f6\u7262\u7262\u63e1\u7740\uff0c\u6211\u5fc3\u614c\u610f\u4e71\uff0c\u7528\u529b\u4e00\u593a\uff0c\u7adf\u5c06\u62f3\u7ecf\u5200\u8c31\u7684\u524d\u9762\u4e24\u9875\u6495\u4e86\u4e0b\u6765\uff0c\u7559\u5728\u4ed6\u7684\u624b\u4e2d\u3002\u53ea\u542c\u5f97\u95e8\u5916\u4eba\u58f0\u55a7\u54d7\uff0c\u82d7\u5927\u4fa0\u5728\u627e\u5b69\u5b50\uff0c\u6211\u987e\u4e0d\u5230\u65c1\u7684\uff0c\u62b1\u4e86\u5b69\u5b50\u6e9c\u51fa\u540e\u95e8\uff0c\u8981\u9003\u56de\u5bb6\u53bb\u3002\u201c\u4ece\u90a3\u65f6\u8d77\u76f4\u5230\u4eca\u65e5\uff0c\u6211\u6ca1\u518d\u89c1\u960e\u5927\u592b\u7684\u9762\uff0c\u5c82\u77e5\u4ed6\u7adf\u4f1a\u505a\u4e86\u548c\u5c1a\u3002\u662f\u4e0d\u662f\u4ed6\u81ea\u89c9\u7f6a\u5b7d\u6df1\u91cd\uff0c\u56e0\u800c\u51fa\u5bb6\u5fcf\u6094\u5462\uff1f\u4ed6\u5077\u5f97\u4e86\u62f3\u7ecf\u7684\u524d\u9762\u4e24\u9875\uff0c\u5c45\u7136\u7ec3\u6210\u4e00\u8eab\u6b66\u827a\uff0c\u626c\u540d\u6c5f\u6e56\u3002\u4ed6\u53ea\u9053\u8fd9\u4e16\u4e0a\u518d\u6ca1\u4eba\u77e5\u9053\u4ed6\u7684\u6765\u5386\uff0c\u60f3\u4e0d\u5230\u5f53\u65e5\u8111\u540e\u6253\u4ed6\u4e00\u95e8\u95e9\u90a3\u4eba\uff0c\u73b0\u5728\u8fd8\u597d\u597d\u6d3b\u7740\u3002\u960e\u5927\u592b\uff0c\u4f60\u8f6c\u8fc7\u8eab\u6765\uff0c\u8ba9\u5927\u4f19\u513f\u77a7\u77a7\u4f60\u8111\u540e\u7684\u90a3\u5757\u4f24\u75a4\uff0c\u8fd9\u662f\u5f53\u5e74\u4e00\u4e2a\u7076\u4e0b\u70e7\u706b\u5c0f\u53ae\u4e00\u95e8\u95e9\u6253\u7684\u554a\u3002\u201d \u3000\u3000\u5b9d\u6811\u7f13\u7f13\u7ad9\u8d77\u8eab\u6765\u3002\u4f17\u4eba\u5c4f\u606f\u4ee5\u89c2\uff0c\u5fc3\u60f3\u4ed6\u52bf\u5fc5\u51fa\u624b\uff0c\u7acb\u65f6\u8981\u4e86\u5e73\u963f\u56db\u7684\u6027\u547d\u3002\u54ea\u77e5\u4ed6\u53ea\u5ff5\u4e86\u4e24\u58f0\u201c\u963f\u5f25\u9640\u4f5b\u201d\uff0c\u4f38\u624b\u6478\u4e86\u6478\u540e\u8111\uff0c\u53c8\u5750\u56de\u6905\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u5341\u4e03\u5e74\u6765\uff0c\u6211\u4e00\u76f4\u4e0d\u77e5\u662f\u8c01\u5728\u6211\u540e\u8111\u6253\u4e86\u8fd9\u4e00\u8bb0\u51b7\u68cd\uff0c\u8001\u662f\u7eb3\u95f7\u3002\u8fd9\u4e2a\u7591\u56e2\uff0c\u4eca\u65e5\u603b\u7b97\u63ed\u7834\u4e86\u3002\u201d\u4f17\u4eba\u4e07\u6599\u4e0d\u5230\u4ed6\u7adf\u4f1a\u76f4\u627f\u6b64\u4e8b\uff0c\u90fd\u662f\u5927\u611f\u8be7\u5f02\u3002 \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u90a3\u4e2a\u53ef\u601c\u7684\u5b69\u5b50\u5462\uff1f\u540e\u6765\u4ed6\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u5e73\u963f\u56db\u9053\uff1a\u201c\u6211\u62b1\u7740\u5b69\u5b50\u6e9c\u51fa\u540e\u95e8\uff0c\u53ea\u5954\u4e86\u6570\u6b65\uff0c\u8eab\u540e\u6709\u4eba\u53eb\u9053\uff1a\u2018\u5582\uff0c\u5c0f\u765e\u75e2\uff0c\u628a\u5b69\u5b50\u62b1\u56de\u6765\uff01\u2019\u6211\u4e0d\u7406\u4f1a\uff0c\u5954\u5f97\u66f4\u5feb\u3002\u90a3\u4eba\u5492\u9a82\u51e0\u53e5\uff0c\u8d76\u4e0a\u6765\u4e00\u628a\u6293\u4f4f\u6211\u7684\u624b\u81c2\uff0c\u5c31\u8981\u62a2\u593a\u5b69\u5b50\u3002\u6211\u6025\u4e86\uff0c\u5728\u4ed6\u624b\u4e0a\u7528\u529b\u54ac\u4e86\u4e00\u53e3\uff0c\u53ea\u54ac\u5f97\u4ed6\u6ee1\u624b\u80cc\u90fd\u662f\u8840\u2026\u2026\u201d \u3000\u3000\u66f9\u4e91\u5947\u7a81\u7136\u51b2\u53e3\u800c\u51fa\uff1a\u201c\u662f\u6211\u5e08\u7236\uff01\u201d\u7530\u9752\u6587\u6a2a\u4e86\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u66f9\u4e91\u5947\u597d\u751f\u540e\u6094\uff0c\u4f46\u8bdd\u5df2\u51fa\u53e3\uff0c\u96be\u4ee5\u6536\u56de\uff0c\u89c1\u4f17\u4eba\u90fd\u671b\u7740\u81ea\u5df1\uff0c\u5fc3\u4e2d\u751a\u662f\u4e0d\u5b89\u3002 \u3000\u3000\u5e73\u963f\u56db\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u7530\u5f52\u519c\u7530\u76f8\u516c\u3002\u4ed6\u624b\u80cc\u4e0a\u4e00\u76f4\u7559\u4e0b\u7259\u9f7f\u54ac\u7684\u4f24\u75d5\u3002\u6211\u731c\u4ed6\u4e5f\u4e0d\u4f1a\u8ddf\u4f60\u4eec\u8bf4\u662f\u8c01\u54ac\u7684\uff0c\u66f4\u4e0d\u4f1a\u8bf4\u4e3a\u4e86\u4ec0\u4e48\u624d\u7ed9\u54ac\u7684\u3002\u201d \u3000\u3000\u7530\u9752\u6587\u3001\u962e\u58eb\u4e2d\u3001\u66f9\u4e91\u5947\u3001\u5468\u4e91\u9633\u56db\u4eba\u76f8\u4e92\u5bf9\u89c6\u4e86\u4e00\u773c\uff0c\u90fd\u60f3\u7530\u5f52\u519c\u624b\u80cc\u4e0a\u9f7f\u75d5\u751a\u6df1\uff0c\u679c\u7136\u4ece\u6765\u4e0d\u66fe\u8bf4\u8d77\u8fc7\u539f\u56e0\u3002 \u3000\u3000\u5e73\u963f\u56db\u53c8\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u54ac\u662f\u62da\u4e86\u6027\u547d\uff0c\u7530\u76f8\u516c\u6b66\u529f\u867d\u9ad8\uff0c\u53ea\u6015\u4e5f\u75db\u5f97\u96be\u5f53\u3002\u4ed6\u62d4\u51fa\u5251\u6765\uff0c\u5728\u6211\u8138\u4e0a\u780d\u4e86\u4e00\u5251\uff0c\u53c8\u4e00\u5251\u5c06\u6211\u7684\u624b\u81c2\u5378\u4e86\u4e0b\u6765\u3002\u4ed6\u76db\u6012\u4e4b\u4e0b\uff0c\u98de\u8d77\u4e00\u811a\uff0c\u5c06\u6211\u8e22\u5165\u6cb3\u4e2d\u3002\u6211\u4e00\u81c2\u867d\u65ad\uff0c\u53e6\u4e00\u81c2\u5374\u4ecd\u7262\u7262\u62b1\u7740\u90a3\u4e2a\u5b69\u5b50\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u4f4e\u4f4e\u7684\u201c\u554a\u201d\u4e86\u4e00\u58f0\u3002\u5e73\u963f\u56db\u9053\uff1a\u201c\u6211\u6389\u5165\u6cb3\u4e2d\u65f6\u65e9\u5df2\u75db\u5f97\u4eba\u4e8b\u4e0d\u77e5\uff0c\u5f85\u5f97\u9192\u8f6c\uff0c\u5374\u662f\u8eba\u5728\u4e00\u8258\u8239\u4e0a\uff0c\u539f\u6765\u7ed9\u4eba\u6551\u4e86\u4e0a\u6765\u3002\u6211\u5927\u53eb\uff1a\u2018\u5b69\u5b50\uff01\u5b69\u5b50\uff01\u2019\u8239\u4e0a\u4e00\u4f4d\u5927\u5a18\u8bf4\u9053\uff1a\u2018\u963f\u5f25\u9640\u4f5b\uff01\u603b\u7b97\u9192\u8fc7\u6765\u5566\u3002\u5b69\u5b50\u5728\u8fd9\u91cc\u3002\u2019\u6211\u62ac\u5934\u4e00\u770b\uff0c\u5374\u89c1\u5979\u62b1\u7740\u5b69\u5b50\u5728\u5582\u5976\u3002\u540e\u6765\u624d\u77e5\u9053\uff0c\u6211\u7ed9\u6551\u4e0a\u8239\u5230\u9192\u8f6c\uff0c\u5df2\u9694\u4e86\u516d\u65e5\u516d\u591c\u3002\u90a3\u65f6\u6211\u79bb\u5bb6\u4e61\u5df2\u8fdc\uff0c\u53c8\u6015\u80e1\u5927\u7237\u7684\u4ec7\u4eba\u5bb3\u8fd9\u5b69\u5b50\uff0c\u4ece\u6b64\u4e0d\u6562\u56de\u53bb\u3002\u542c\u82d7\u59d1\u5a18\u8bf4\u6765\uff0c\u82d7\u5927\u4fa0\u53ea\u5f53\u8fd9\u5b69\u5b50\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u559c\u9053\uff1a\u201c\u662f\u554a\uff0c\u539f\u6765\u8fd9\u53ef\u601c\u7684\u5b69\u5b50\u8fd8\u6d3b\u7740\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u7239\u7239\u77e5\u9053\u4e86\u4e00\u5b9a\u559c\u6b22\u5f97\u7d27\u3002\u8fd9\u5b69\u5b50\u5728\u54ea\u91cc\uff0c\u4f60\u5e26\u6211\u4eec\u53bb\u77a7\u77a7\u597d\u4e0d\u597d\uff1f\u201d\u5979\u968f\u5373\u60f3\u5230\uff0c\u81ea\u5df1\u4e00\u76f4\u53eb\u4ed6\u201c\u53ef\u601c\u7684\u5b69\u5b50\u201d\uff0c\u5176\u5b9e\u4ed6\u5df2\u662f\u4e2a\u4e8c\u5341\u4e03\u5c81\u7684\u7537\u5b50\uff0c\u6bd4\u81ea\u5df1\u8fd8\u5927\u7740\u5341\u5c81\uff0c\u8138\u4e0a\u4e0d\u7981\u4e00\u7ea2\u3002 \u3000\u3000\u5e73\u963f\u56db\u9053\uff1a\u201c\u4f60\u77a7\u4ed6\u4e0d\u7740\u4e86\u3002\u8fd9\u91cc\u7684\u4eba\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u6d3b\u7740\u4e0b\u5c71\u3002\u201d\u82d7\u82e5\u5170\u9053\uff1a\u201c\u6211\u7239\u7239\u5fc5\u4f1a\u4e0a\u5cf0\u6765\u6551\uff0c\u6211\u4e00\u70b9\u4e5f\u4e0d\u62c5\u5fc3\u3002\u201d \u3000\u3000\u5e73\u963f\u56db\u9053\uff1a\u201c\u4f60\u7239\u7239\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\uff0c\u6253\u7684\u662f\u51e1\u4eba\u3002\u4ed6\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u5948\u4f55\u4e0d\u4e86\u8fd9\u4e07\u4e08\u9ad8\u5cf0\u3002\u201d\u82d7\u82e5\u5170\u9053\uff1a\u201c\u662f\u90a3\u5b69\u5b50\u53eb\u4f60\u6765\u5bb3\u6b7b\u6211\u4eec\u4e48\uff1f\u201d\u5e73\u963f\u56db\u6447\u5934\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u4e0d\u662f\u3002\u8fd9\u5b69\u5b50\u82f1\u96c4\u8c6a\u4fa0\uff0c\u8ddf\u4ed6\u7236\u4eb2\u4e00\u6a21\u4e00\u6837\uff0c\u82e5\u662f\u77e5\u9053\u6211\u6765\u5e72\u8fd9\u79cd\u9634\u6bd2\u52fe\u5f53\uff0c\u5b9a\u8981\u62e6\u963b\u3002\u201d\u66f9\u4e91\u5947\u6012\u9053\uff1a\u201c\u597d\u554a\uff0c\u539f\u6765\u4f60\u4e5f\u77e5\u9053\u8fd9\u662f\u9634\u6bd2\u52fe\u5f53\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u95ee\u9053\uff1a\u201c\u90a3\u5b69\u5b50\u600e\u6837\u4e86\uff1f\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u6b66\u529f\u597d\u5417\uff1f \u3000\u3000\u5728\u5e72\u4ec0\u4e48\u4e8b\uff1f\u4ed6\u4e5f\u662f\u4e2a\u597d\u4eba\u5417\uff1f\u201d\u5979\u81ea\u5c0f\u89c1\u7236\u4eb2\u6bcf\u5e74\u796d\u5960\u80e1\u4e00\u5200\u592b\u5987\uff0c\u4e00\u76f4\u4ee5\u672a\u80fd\u629a\u517b\u90a3\u5b69\u5b50\u4e3a\u6bd5\u751f\u6068\u4e8b\uff0c\u662f\u4ee5\u6781\u4e3a\u5173\u5fc3\u3002 \u3000\u3000\u5e73\u963f\u56db\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u6211\u70b8\u6bc1\u4e86\u957f\u7d22\uff0c\u82d7\u59d1\u5a18\uff0c\u4f60\u4eca\u65e5\u5c31\u80fd\u89c1\u5230\u4ed6\u5566\u3002\u201d\u66f9\u4e91\u5947\u7b49\u516d\u4e03\u4eba\u9f50\u58f0\u6012\u9053\uff1a\u201c\u957f\u7d22\u662f\u4f60\u70b8\u6bc1\u7684\uff1f\u201d \u3000\u3000\u5e73\u963f\u56db\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d\u82d7\u82e5\u5170\u5374\u95ee\uff1a\u201c\u600e\u4e48\u6211\u4eca\u65e5\u80fd\u89c1\u5230\u4ed6\uff1f\u201d\u5e73\u963f\u56db\u9053\uff1a\u201c\u4ed6\u4e0e\u6b64\u95f4\u4e3b\u4eba\u6709\u7ea6\uff0c\u4eca\u65e5\u5348\u65f6\u8981\u6765\u62dc\u5c71\u3002\u773c\u89c1\u5348\u65f6\u5df2\u5230\uff0c\u8fd9\u4f1a\u513f\u60f3\u5fc5\u5df2\u6765\u5230\u5c71\u5cf0\u4e4b\u4e0b\u4e86\u3002\u201d\u4f17\u4eba\u9f50\u58f0\u53eb\u9053\uff1a\u201c\u662f\uff1f\u201d \u3000\u3000\u5e73\u963f\u56db\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u80e1\u4e00\u5200\u80e1\u5927\u7237\u7684\u513f\u5b50\uff0c\u53eb\u4f5c\u80e1\u6590\uff0c\u5916\u53f7\uff01\u201d \u3000"}),showOnHomepage:!0},{name:"\u516d",content:(0,a.jsx)(a.Fragment,{children:"\u4f17\u4eba\u542c\u4e86\u534a\u5929\u6545\u4e8b\uff0c\u5bf9\u80e1\u4e00\u5200\u7684\u4e3a\u4eba\u751a\u662f\u795e\u5f80\uff0c\u542c\u8bf4\u662f\u4ed6\u513f\u5b50\uff0c\u5fc3\u4e2d\u90fd\u8d77\u5f02\u6837\u4e4b\u611f\uff0c\u867d\u60f3\u89c1\u4e86\u4ed6\u672a\u5fc5\u6709\u751a\u597d\u5904\uff0c\u5374\u90fd\u4e0d\u81ea\u7981\u7684\u6e34\u6b32\u4e00\u89c1\uff0c\u53c8\u60f3\u6b64\u95f4\u4e3b\u4eba\u904d\u9080\u9ad8\u624b\uff0c\u4ee5\u5907\u8fce\u6218\uff0c\u53ea\u6015\u6b64\u4eba\u672c\u9886\u4ea6\u4e0d\u5728\u4e43\u7236\u4e4b\u4e0b\u3002 \u3000\u3000\u82d7\u82e5\u5170\u5ffd\u7136\u60ca\u9053\uff1a\u201c\u554a\u54df\uff0c\u6b64\u95f4\u4e3b\u4eba\u6240\u9080\u7684\u5e2e\u624b\u548c\u6211\u7239\u7239\u90fd\u672a\u4e0a\u5c71\uff0c\u5982\u5728\u5c71\u4e0b\u649e\u5230\u4e86\u90a3\uff0c\u5b9a\u8981\u52a8\u624b\u3002\u6211\u7239\u7239\u4e0d\u77e5\u4ed6\u662f\u80e1\u4f2f\u4f2f\u7684\u513f\u5b50\uff0c\u82e5\u662f\u4e00\u5251\u5c06\u4ed6\u6740\u4e86\uff0c\u90a3\u4fbf\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u5e73\u963f\u56db\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u867d\u8bf4\u662f\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\uff0c\u53ef\u662f\u8981\u8bf4\u80fd\u4e00\u5251\u6740\u4e86\u80e1\u76f8\u516c\uff0c\u5374\u4e5f\u672a\u5fc5\u3002\u201d\u4ed6\u8138\u4e0a\u4e00\u4e2a\u957f\u957f\u7684\u4f24\u75a4\uff0c\u8fd9\u4e48\u4e00\u7b11\uff0c\u7275\u52a8\u808c\u8089\uff0c\u663e\u5f97\u52a0\u500d\u7684\u4e11\u964b\u53ef\u6016\u3002 \u3000\u3000\u4ed6\u53c8\u9053\uff1a\u201c\u80e1\u76f8\u516c\u4eca\u65e5\u4e0a\u5c71\uff0c\u4e00\u6765\u662f\u5f7c\u6b64\u95f4\u4e3b\u4eba\u7684\u6666\u6c14\uff0c\u4e8c\u6765\u662f\u8981\u627e\u82d7\u5927\u4fa0\u6bd4\u6b66\u590d\u4ec7\u3002\u53ea\u662f\u6211\u4eb2\u773c\u89c1\u5230\u5f53\u5e74\u80e1\u82d7\u4e8c\u4f4d\u5927\u4fa0\u809d\u80c6\u76f8\u7167\u7684\u4ea4\u60c5\uff0c\u5bb3\u6b7b\u80e1\u5927\u7237\u7684\u5176\u5b9e\u662f\u53e6\u6709\u5176\u4eba\uff0c\u6211\u529d\u80e1\u76f8\u516c\u522b\u5411\u82d7\u5927\u4fa0\u4e3a\u96be\u4e86\uff0c\u53ef\u662f\u4ed6\u8bf4\u8981\u5f53\u9762\u5411\u82d7\u5927\u4fa0\u95ee\u4e2a\u6e05\u695a\u3002\u540e\u6765\u6211\u5728\u5c71\u4e0b\u89c1\u5230\u4e86\u8fd9\u4f4d\u960e\u5927\u592b\uff0c\u867d\u7136\u9694\u4e86\u8fd9\u4e48\u4e8c\u5341\u51e0\u5e74\uff0c\u6211\u53ef\u8fd8\u662f\u8ba4\u5f97\u4ed6\uff0c\u5f53\u4e0b\u8ddf\u4e0a\u5cf0\u6765\uff0c\u70b8\u7d22\u6bc1\u7cae\uff0c\u5927\u4f19\u513f\u5728\u8fd9\u513f\u4e00\u9f50\u997f\u6b7b\uff0c\u603b\u7b97\u662f\u62a5\u4e86\u80e1\u5927\u7237\u5f85\u6211\u7684\u6069\u4e49\u5566\u3002\u201d \u3000\u3000\u8fd9\u4e00\u5e2d\u8bdd\uff0c\u53ea\u628a\u4f17\u4eba\u542c\u5f97\u9762\u9762\u76f8\u89d1\uff0c\u5fc3\u60f3\u5b9d\u6811\u5f53\u5e74\u8c0b\u8d22\u5bb3\u547d\uff0c\u4eca\u65e5\u81ea\u662f\u6b7b\u6709\u5e94\u5f97\uff0c\u53ea\u662f\u5404\u4eba\u4e0e\u6b64\u4e8b\u5e76\u4e0d\u76f8\u5e72\uff0c\u5374\u5728\u8fd9\u513f\u8d54\u4e0a\u4e00\u6761\u6027\u547d\uff0c\u4e5f\u53ef\u7b97\u5f97\u6781\u51a4\u3002 \u3000\u3000\u5b9d\u6811\u89c1\u4e86\u4f17\u4eba\u8138\u8272\uff0c\u77e5\u9053\u5927\u5bb6\u5bf9\u81ea\u5df1\u9887\u6709\u602a\u8d23\u4e4b\u610f\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u53d6\u8fc7\u4e86\u5b9d\u5200\u94c1\u76d2\uff0c\u559d\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u54b1\u4eec\u53ea\u6709\u540c\u821f\u5171\u6d4e\uff0c\u4e00\u9f50\u60f3\u4e2a\u4e0b\u5c71\u7684\u6cd5\u513f\u3002\u8fd9\u4e2a\u6076\u5f92\u561b\u2026\u2026\u201d \u3000\u3000\u4e00\u8bed\u672a\u6bd5\uff0c\u5ffd\u542c\u6251\u7fc5\u58f0\u54cd\uff0c\u4e00\u53ea\u767d\u9e3d\u98de\u8fdb\u5927\u5385\uff0c\u505c\u5728\u684c\u4e0a\u3002 \u3000\u3000\u82d7\u82e5\u5170\u559c\u9053\uff1a\u201c\u554a\uff0c\u8fd9\u53ea\u5c0f\u9e3d\u513f\u591a\u53ef\u7231\uff01\u201d\u4e0a\u524d\u53cc\u624b\u8f7b\u8f7b\u6367\u8d77\u767d\u9e3d\uff0c\u629a\u6478\u9e3d\u80cc\u7fbd\u6bdb\uff0c\u53ea\u89c1\u9e3d\u811a\u4e0a\u7f1a\u7740\u4e00\u6761\u4e1d\u7ebf\u3002\u8fd9\u4e1d\u7ebf\u4ece\u9e3d\u811a\u4e0a\u4e00\u76f4\u901a\u5230\u95e8\u5916\uff0c\u82d7\u82e5\u5170\u5411\u91cc\u62c9\u626f\uff0c\u90a3\u7ebf\u7adf\u662f\u6781\u957f\uff0c\u62c9\u4e86\u597d\u4e00\u5927\u622a\uff0c\u59cb\u7ec8\u672a\u89c1\u7ebf\u5934\u3002\u5979\u597d\u5947\u5fc3\u8d77\uff0c\u53cc\u624b\u4ea4\u4e92\u6536\u7ebf\uff0c\u90a3\u7ebf\u7adf\u4f3c\u65e0\u7a77\u65e0\u5c3d\u4e00\u822c\u3002\u7530\u9752\u6587\u4e0a\u524d\u76f8\u52a9\uff0c\u4e24\u4eba\u6536\u4e86\u6570\u5341\u4e08\uff0c\u5ffd\u89c9\u4e1d\u7ebf\u6e10\u6e10\u6c89\u91cd\uff0c\u770b\u6765\u7ebf\u5934\u5f7c\u7aef\u7f1a\u5f97\u6709\u7269\u3002 \u3000\u3000\u4e8e\u7ba1\u5bb6\u5927\u559c\uff0c\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u6709\u6551\u5566\uff01\u201d\u4f17\u4eba\u9f50\u95ee\uff1a\u201c\u600e\u4e48\uff1f\u201d \u3000\u3000\u4e8e\u7ba1\u5bb6\u9053\uff1a\u201c\u8fd9\u767d\u9e3d\u662f\u672c\u5e84\u6240\u517b\uff0c\u5c71\u4e0a\u5c71\u4e0b\u7528\u4ee5\u4f20\u9012\u6d88\u606f\u3002\u5b9a\u662f\u5c71\u4e0b\u7684\u672c\u5e84\u4f19\u4f34\u53d1\u89c9\u957f\u7d22\u70b8\u65ad\uff0c\u653e\u8fd9\u9e3d\u5b50\u4e0a\u5cf0\uff0c\u5728\u4e1d\u7ebf\u4e0a\u7f1a\u7740\u6551\u54b1\u4eec\u4e0b\u5cf0\u7684\u7269\u4e8b\u3002\u201d \u3000\u3000\u5e73\u963f\u56db\u542c\u4e86\u6b64\u8bdd\uff0c\u8138\u8272\u5927\u53d8\uff0c\u72c2\u543c\u4e00\u58f0\uff0c\u6251\u4e0a\u53bb\u8981\u62c9\u65ad\u4e1d\u7ebf\u3002\u6bb7\u5409\u7ad9\u5728\u90bb\u8fd1\uff0c\u8eab\u5b50\u4e00\u6643\uff0c\u5df2\u62e6\u5728\u4ed6\u9762\u524d\uff0c\u53cc\u638c\u8d77\u5904\uff0c\u5c06\u4ed6\u63a8\u5012\u5728\u5730\u3002 \u3000\u3000\u7530\u9752\u6587\u9053\uff1a\u201c\u59ca\u59ca\uff0c\u5c0f\u5fc3\u62c9\u65ad\u4e86\u4e1d\u7ebf\u3002\u201d\u82d7\u82e5\u5170\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u90a3\u4e1d\u7ebf\u867d\u7ec6\uff0c\u5374\u6781\u575a\u97e7\uff0c\u4e24\u4eba\u624b\u4e0a\u6108\u6765\u6108\u6c89\uff0c\u4e1d\u7ebf\u59cb\u7ec8\u4e0d\u65ad\u3002 \u3000\u3000\u518d\u62c9\u4e00\u4f1a\uff0c\u82d7\u82e5\u5170\u4f3c\u4e4e\u6709\u70b9\u5403\u529b\u3002\u9676\u5b50\u5b89\u9053\uff1a\u201c\u82d7\u59d1\u5a18\u4f60\u6b47\u6b47\uff0c\u6211\u6765\u62c9\u3002\u201d\u8d70\u4e0a\u524d\u53bb\u63a5\u8fc7\u4e86\u4e1d\u7ebf\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u3001\u66f9\u4e91\u5947\u3001\u5218\u5143\u9e64\u7b49\u65e9\u5df2\u62a2\u51fa\u95e8\u53bb\uff0c\u8981\u770b\u90a3\u4e1d\u7ebf\u4e0a\u540a\u7684\u662f\u4ec0\u4e48\u6551\u661f\u3002 \u3000\u3000\u9676\u7530\u4e8c\u4eba\u6536\u4e86\u4e00\u4f1a\uff0c\u5ffd\u542c\u95e8\u5916\u6b22\u547c\u58f0\u8d77\uff0c\u624b\u4e0a\u987f\u677e\uff0c\u60f3\u6765\u6240\u540a\u4e4b\u7269\u5df2\u4e0a\u4e86\u5cf0\u3002\u5385\u4e0a\u5404\u4eba\u4e00\u9f50\u8d70\u51fa\uff0c\u53ea\u89c1\u962e\u58eb\u4e2d\u4e0e\u66f9\u4e91\u5947\u7ad9\u5728\u5d16\u8fb9\uff0c\u53cc\u624b\u6b64\u8d77\u5f7c\u843d\uff0c\u5fd9\u788c\u5f02\u5e38\uff0c\u4ecd\u662f\u5728\u6536\u7ebf\uff0c\u539f\u6765\u4e1d\u7ebf\u4e0a\u7f1a\u7684\u662f\u4e00\u6839\u8f83\u7c97\u7684\u4e1d\u7d22\u3002\u5f85\u90a3\u4e1d\u7d22\u6536\u5c3d\uff0c\u53c8\u5f15\u4e0a\u4e00\u6839\u6781\u7c97\u7684\u7ef3\u7d22\u3002 \u3000\u3000\u4f17\u4eba\u4e00\u9f50\u9ad8\u547c\uff0c\u4e03\u624b\u516b\u811a\uff0c\u5c06\u90a3\u6839\u7c97\u7d22\u7f1a\u5728\u5d16\u8fb9\u4e24\u682a\u5927\u677e\u6811\u4e0a\u3002 \u3000\u3000\u5218\u5143\u9e64\u9053\uff1a\u201c\u54b1\u4eec\u8d70\u5427\uff0c\u5f85\u6211\u5148\u4e0b\u3002\u201d\u53cc\u624b\u6293\u4f4f\u4e86\u7ef3\u7d22\uff0c\u5c31\u8981\u5f80\u4e0b\u6e9c\u53bb\u3002\u9676\u767e\u5c81\u559d\u9053\uff1a\u201c\u4e14\u6162\uff0c\u5e72\u4e48\u8981\u8ba9\u4f60\u5148\u4e0b\uff1f\u8c01\u77e5\u4f60\u5728\u4e0b\u9762\u4f1a\u6363\u4ec0\u4e48\u9b3c\uff1f\u201d\u5218\u5143\u9e64\u6012\u9053\uff1a\u201c\u4f9d\u4f60\u8bf4\u4fbf\u600e\u5730\uff1f\u201d\u9676\u767e\u5c81\u4e00\u6014\uff0c\u5fc3\u60f3\u5cf0\u4e0a\u4eba\u4eba\u5404\u6000\u79c1\u5fc3\uff0c\u4e92\u4e0d\u4fe1\u4efb\uff0c\u4e0d\u8bba\u8c01\u5148\u4e0b\u53bb\uff0c\u65c1\u4eba\u90fd\u96be\u653e\u5fc3\uff0c\u7ed9\u4ed6\u8fd9\u4e48\u4e00\u95ee\uff0c\u5012\u4e5f\u96be\u4ee5\u5bf9\u7b54\u3002 \u3000\u3000\u66f9\u4e91\u5947\u9053\uff1a\u201c\u8ba9\u51e0\u4f4d\u5973\u5ba2\u5148\u4e0b\u53bb\uff0c\u54b1\u4eec\u7537\u5b50\u6c49\u62c8\u7b79\u4ee5\u5b9a\u5148\u540e\u3002\u201d\u718a\u5143\u732e\u7ec6\u58f0\u7ec6\u6c14\u7684\u9053\uff1a\u201c\u8fd9\u6837\u5427\uff0c\u5929\u9f99\u95e8\u3001\u996e\u9a6c\u5ddd\u5c71\u5be8\u3001\u8ddf\u6211\u4eec\u5e73\u901a\u9556\u5c40\u7684\uff0c\u6bcf\u4e00\u5bb6\u8f6e\u6d41\u4e0b\u53bb\u4e00\u4e2a\u3002\u5927\u4f19\u513f\u4e92\u76f8\u76d1\u5b88\uff0c\u4e0d\u7528\u6015\u6709\u8c01\u4f7f\u5978\u884c\u8bc8\u3002\u201d \u3000\u3000\u962e\u58eb\u4e2d\u9053\uff1a\u201c\u90a3\u4e5f\u597d\u3002\u5b9d\u6811\u5927\u5e08\uff0c\u8bf7\u60a8\u5c06\u94c1\u76d2\u513f\u89c1\u8fd8\u5427\u3002\u201d \u3000\u3000\u8bf4\u7740\u8d70\u4e0a\u4e00\u6b65\uff0c\u5411\u5b9d\u6811\u4f38\u51fa\u624b\u53bb\u3002 \u3000\u3000\u4f17\u4eba\u521d\u65f6\u53ea\u987e\u5ff5\u751f\u6b7b\u5b89\u5371\uff0c\u6b64\u65f6\u5927\u96be\u5df2\u8fc7\uff0c\u53c8\u90fd\u60f3\u5230\u4e86\u90a3\u4ef6\u5b9d\u7269\u3002\u672c\u6765\u5927\u5bb6\u53ea\u77e5\u8fd9\u94c1\u76d2\u662f\u4ef6\u6b66\u6797\u5f02\u5b9d\uff0c\u4f46\u5230\u5e95\u5f02\u5728\u54ea\u91cc\uff0c\u5b9d\u4e8e\u4f55\u5904\uff0c\u5374\u5747\u4e0d\u751a\u4e86\u7136\uff0c\u5f85\u5f97\u77e5\u9053\u662f\u95ef\u738b\u9057\u4e0b\u7684\u519b\u5200\uff0c\u5df2\u89c9\u6b64\u7269\u975e\u540c\u5c0f\u53ef\uff0c\u53ca\u81f3\u542c\u5e73\u963f\u56db\u8bf4\u8fd9\u67c4\u5200\u4e0e\u674e\u95ef\u738b\u7684\u5927\u5b9d\u85cf\u6709\u5173\uff0c\u66f4\u662f\u4e2a\u4e2a\u773c\u7ea2\u5fc3\u70ed\u3002\u6545\u8001\u76f8\u4f20\uff0c\u95ef\u738b\u8fdb\u4eac\u4e4b\u540e\uff0c\u90e8\u5c5e\u5927\u5c06\u5218\u5b97\u654f\u7b49\u62f7\u63a0\u660e\u671d\u7684\u5b97\u5ba4\u5927\u81e3\uff0c\u6240\u5f97\u73cd\u5b9d\u5806\u79ef\u5982\u5c71\uff0c\u4e0d\u4e45\u5175\u8d25\uff0c\u8fd9\u6279\u73cd\u5b9d\u8fde\u540c\u660e\u5bab\u4e2d\u7687\u5ba4\u5386\u5e74\u7684\u5e93\u85cf\uff0c\u90fd\u662f\u4ece\u6b64\u4e0d\u77e5\u4e0b\u843d\uff0c\u82e5\u662f\u7531\u8fd9\u94c1\u76d2\u5b9d\u5200\u800c\u6398\u5f97\u5b9d\u85cf\uff0c\u4e16\u4e0a\u5c1a\u6709\u4f55\u79cd\u8d22\u7269\u80fd\u4e0e\u4e4b\u76f8\u6bd4\uff1f \u3000\u3000\u5b9d\u6811\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5929\u9f99\u95e8\u4f55\u5fb7\u4f55\u80fd\uff0c\u8981\u72ec\u5360\u5b9d\u5200\uff1f\u8fd9\u628a\u5200\u5929\u9f99\u95e8\u638c\u7ba1\u4e86\u4e00\u767e\u591a\u5e74\uff0c\u4e5f\u8be5\u6362\u6362\u4e3b\u513f\u4e86\u3002\u201d \u3000\u3000\u962e\u58eb\u4e2d\u6115\u7136\uff0c\u773c\u9732\u51f6\u5149\u3002\u6bb7\u5409\u3001\u66f9\u4e91\u5947\u3001\u5468\u4e91\u9633\u4e0d\u7ea6\u800c\u540c\u7684\u62a2\u4e0a\u4e00\u6b65\uff0c\u7ad9\u5728\u962e\u58eb\u4e2d\u8eab\u65c1\u3002 \u3000\u3000\u5b9d\u6811\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u54e5\u513f\u4eec\u60f3\u52a8\u6b66\uff0c\u662f\u4e0d\u662f\uff1f\u60f3\u5f53\u5e74\u5929\u9f99\u95e8\u5728\u5200\u5934\u4e0a\u5f97\u5b9d\uff0c\u4eca\u65e5\u5728\u5200\u5934\u4e0a\u5931\u5b9d\uff0c\u90a3\u4e5f\u662f\u516c\u5e73\u5f97\u7d27\u554a\u3002\u201d \u3000\u3000\u962e\u58eb\u4e2d\u7b49\u5927\u6012\uff0c\u6068\u4e0d\u5f97\u6251\u5c06\u4e0a\u53bb\uff0c\u628a\u8fd9\u8001\u548c\u5c1a\u780d\u6210\u51e0\u6bb5\uff0c\u593a\u8fc7\u5b9d\u5200\uff0c\u53ea\u662f\u5fcc\u60ee\u4ed6\u6b66\u529f\u4e86\u5f97\uff0c\u5374\u53c8\u4e0d\u6562\u52a8\u624b\uff0c\u5728\u4ed6\u70af\u70af\u6709\u795e\u7684\u53cc\u76ee\u51dd\u89c6\u4e4b\u4e0b\uff0c\u53cd\u800c\u5012\u9000\u4e86\u6570\u6b65\u3002 \u3000\u3000\u4e00\u65f6\u96ea\u5cf0\u8fb9\u5bc2\u9759\u65e0\u58f0\uff0c\u5ffd\u7136\u82d7\u82e5\u5170\u7684\u5a62\u5973\u7434\u513f\u6307\u7740\u5c71\u4e0b\u53eb\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u77a7\uff0c\u597d\u50cf\u6709\u4eba\u4e0a\u6765\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u600e\u4e48\u6211\u4eec\u6ca1\u4e0b\u5c71\uff0c\u53cd\u5012\u6709\u4eba\u4e0a\u6765\u4e86\uff1f\u201d \u3000\u3000\u7eb7\u7eb7\u5954\u5230\u5d16\u8fb9\uff0c\u5411\u4e0b\u5f20\u671b\uff0c\u53ea\u89c1\u957f\u7d22\u4e0a\u6709\u4e00\u56e2\u767d\u5f71\u8fc5\u901f\u5f02\u5e38\u7684\u6500\u63f4\u4e0a\u6765\uff0c\u51dd\u795e\u4e00\u770b\uff0c\u5374\u662f\u4e00\u4e2a\u767d\u8863\u7537\u5b50\u3002 \u3000\u3000\u7530\u9752\u6587\u9053\uff1a\u201c\u82d7\u59d0\u59d0\uff0c\u8fd9\u4f4d\u662f\u4ee4\u5c0a\u4e48\uff1f\u201d\u82d7\u82e5\u5170\u6447\u5934\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u6211\u7239\u7239\u4ece\u6765\u4e0d\u7a7f\u767d\u8863\u7684\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u90a3\u7537\u5b50\u722c\u5f97\u66f4\u52a0\u8fd1\u4e86\u3002\u4e8e\u7ba1\u5bb6\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5c0a\u9a7e\u662f\u54ea\u4e00\u4f4d\uff1f\u201d\u5ffd\u542c\u5f97\u534a\u5c71\u8170\u91cc\u4f20\u4e0a\u6765\u4e00\u58f0\u957f\u7b11\uff0c\u58f0\u97f3\u6d2a\u4eae\uff0c\u53ea\u9707\u5f97\u5c71\u8c37\u9e23\u54cd\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u4f3c\u4e4e\u6ee1\u5c71\u90fd\u662f\u5927\u7b11\u4e4b\u58f0\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u89c1\u5b9d\u6811\u624b\u6367\u94c1\u76d2\uff0c\u7ad9\u5728\u5d16\u8fb9\uff0c\u8f7b\u8f7b\u4e00\u62c9\u66f9\u4e91\u5947\u7684\u624b\uff0c\u6307\u6307\u5b9d\u6811\u80cc\u5fc3\uff0c\u7528\u53f3\u80a9\u4f5c\u4e86\u4e2a\u76f8\u649e\u7684\u59ff\u6001\u3002\u66f9\u4e91\u5947\u4f1a\u610f\uff0c\u77e5\u9053\u5e08\u53d4\u547d\u81ea\u5df1\u5c06\u4ed6\u649e\u4e0b\u5c71\u5cf0\uff0c\u5fc3\u60f3\u8fd9\u8d3c\u79c3\u672c\u9886\u518d\u5f3a\uff0c\u4ece\u8fd9\u4e07\u4e08\u9ad8\u5cf0\u4e0a\u6389\u5c06\u4e0b\u53bb\uff0c\u54ea\u91cc\u4fdd\u5f97\u4f4f\u6027\u547d\uff1f\u94c1\u76d2\u5b9d\u5200\u662f\u8dcc\u4e0d\u574f\u7684\uff0c\u5f85\u4f1a\u4e0b\u53bb\u5bfb\u627e\u4fbf\u662f\u3002\u962e\u66f9\u4e8c\u4eba\u4e00\u70b9\u5934\uff0c\u540c\u65f6\u53d1\u8db3\uff0c\u731b\u7136\u51b2\u5411\u5b9d\u6811\u540e\u5fc3\u3002\u6b64\u65f6\u5b9d\u6811\u79bb\u5d16\u8fb9\u4e0d\u8fc7\u5c3a\u8bb8\uff0c\u5168\u795e\u6ce8\u89c6\u5c71\u4e0b\uff0c\u4e1d\u6beb\u4e0d\u77e5\u6709\u4eba\u5728\u80cc\u540e\u7a81\u65bd\u6697\u7b97\u3002 \u3000\u3000\u5f85\u5f97\u542c\u5230\u811a\u6b65\u58f0\u54cd\uff0c\u962e\u66f9\u4e8c\u4eba\u5df2\u51b2\u5230\u8eab\u540e\uff0c\u5b9d\u6811\u89c1\u5230\u90a3\u767d\u8863\u7537\u5b50\u4e0a\u6765\u65f6\u7684\u8eab\u6cd5\u795e\u6001\uff0c\u6b63\u81ea\u60ca\u7591\u4e0d\u5b9a\uff0c\u7a81\u89c9\u80cc\u540e\u6709\u4eba\u6765\u88ad\uff0c\u66f4\u662f\u5927\u5403\u4e00\u60ca\uff0c\u5371\u6025\u4e2d\u500f\u65bd\u201c\u94c1\u677f\u6865\u201d\u529f\u592b\uff0c\u8eab\u5b50\u5411\u5de6\u659c\u51fa\u3002\u8fd9\u201c\u94c1\u6781\u6865\u201d\u529f\u592b\uff0c\u539f\u662f\u95ea\u907f\u654c\u4eba\u6697\u5668\u7684\u6551\u547d\u7edd\u62db\uff0c\u901a\u5e38\u662f\u6697\u5668\u6765\u5f97\u592a\u5feb\uff0c\u4e0d\u53ca\u8dc3\u8d77\u6216\u5411\u65c1\u907f\u8ba9\uff0c\u53ea\u5f97\u8eab\u5b50\u50f5\u76f4\uff0c\u7a81\u7136\u5411\u540e\u4ef0\u5929\u659c\u501a\uff0c\u8ba9\u90a3\u6697\u5668\u63a0\u9762\u800c\u8fc7\uff0c\u53cc\u811a\u5374\u4ecd\u662f\u7262\u7262\u9489\u4f4f\u5730\u4e0b\u3002\u529f\u592b\u8d8a\u9ad8\uff0c\u80cc\u5fc3\u8d8a\u80fd\u8d34\u8fd1\u5730\u9762\uff0c\u8bb2\u7a76\u7684\u662f\u8d77\u843d\u5feb\uff0c\u8eab\u5f62\u76f4\uff0c\u6240\u8c13\u201c\u8db3\u5982\u94f8\u94c1\uff0c\u8eab\u633a\u4f3c\u677f\uff0c\u659c\u8d77\u82e5\u6865\u201d\u3002\u5b9d\u6811\u8fd9\u4e00\u62db\u201c\u94c1\u677f\u6865\u201d\uff0c\u53c8\u4e0e\u901a\u5e38\u6240\u4f7f\u7684\u4e0d\u540c\uff0c\u5e76\u975e\u5411\u540e\u4ef0\u501a\uff0c\u5374\u662f\u5411\u5de6\u503e\u659c\uff0c\u53cc\u8db3\u9489\u5728\u5d16\u8fb9\uff0c\u8eab\u5b50\u51cc\u7a7a\uff0c\u5df2\u6709\u4e00\u5c0f\u534a\u51ed\u865a\u503e\u5728\u96ea\u5cf0\u4e4b\u5916\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u4e0e\u66f9\u4e91\u5947\u649e\u5230\u5b9d\u6811\u80cc\u540e\uff0c\u53ea\u9053\u88ad\u51fb\u5f97\u901e\uff0c\u6b63\u81ea\u5927\u559c\uff0c\u7a81\u89c9\u80a9\u5934\u649e\u51fa\uff0c\u524d\u9762\u7adf\u7136\u6ca1\u4e86\u53d7\u529b\u4e4b\u5904\u3002\u962e\u58eb\u4e2d\u6b66\u529f\u7cbe\u6e5b\uff0c\u6025\u5fd9\u4e00\u4e2a\u7b4b\u6597\uff0c\u6eda\u5728\u4e00\u65c1\u3002\u66f9\u4e91\u5947\u5374\u6536\u811a\u4e0d\u4f4f\uff0c\u75be\u51b2\u800c\u51fa\uff0c\u76f4\u5f80\u96ea\u5cf0\u4e0b\u6389\u843d\u3002 \u3000\u3000\u4f17\u4eba\u9f50\u58f0\u60ca\u547c\u3002\u5b9d\u6811\u633a\u8170\u7ad9\u76f4\uff0c\u8bf4\u9053\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff0c\u7f6a\u8fc7\uff01 \u3000\u3000\u7f6a\u8fc7\uff01\u201d\u80cc\u4e0a\u5374\u4e5f\u5df2\u51fa\u4e86\u4e00\u9635\u51b7\u6c57\u3002 \u3000\u3000\u7530\u9752\u6587\u4e00\u5413\uff0c\u5df2\u6655\u5012\u5728\u5730\u3002\u9676\u5b50\u5b89\u7ad9\u5728\u5979\u8eab\u65c1\uff0c\u5fd9\u4f38\u624b\u6276\u4f4f\u3002 \u3000\u3000\u4f59\u4eba\u671b\u7740\u66f9\u4e91\u5947\u9b41\u68a7\u7684\u8eab\u8eaf\u5411\u4e0b\u76f4\u843d\uff0c\u65e0\u4e0d\u5931\u58f0\u60ca\u547c\u3002\u773c\u89c1\u4ed6\u52bf\u5fc5\u6454\u5f97\u7c89\u8eab\u788e\u9aa8\uff0c\u5ffd\u89c1\u90a3\u767d\u8863\u7537\u5b50\u53cc\u8db3\u94a9\u4f4f\u7ef3\u7d22\uff0c\u5de6\u624b\u5728\u5cf0\u58c1\u4e0a\u4e00\u63a8\uff0c\u957f\u7d22\u5e26\u7740\u4ed6\u7684\u8eab\u5b50\uff0c\u5982\u8361\u79cb\u5343\u822c\u5411\u66f9\u4e91\u5947\u6025\u98de\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u65f6\u673a\u7528\u529b\u90fd\u662f\u6070\u5230\u597d\u5904\uff0c\u90a3\u767d\u8863\u4eba\u53f3\u624b\u63a2\u51fa\uff0c\u5df2\u6293\u4f4f\u66f9\u4e91\u5947\u7684\u540e\u5fc3\u3002\u4e0d\u6599\u66f9\u4e91\u5947\u8eab\u8eaf\u751a\u91cd\uff0c\u8fd9\u4e00\u5815\u4e4b\u52bf\u66f4\u662f\u731b\u70c8\u5f02\u5e38\uff0c\u4f46\u542c\u5f97\u5580\u5587\u4e00\u54cd\uff0c\u8863\u886b\u7834\u88c2\uff0c\u7adf\u53c8\u6389\u4e86\u4e0b\u53bb\uff0c\u90a3\u767d\u8863\u4eba\u957f\u8eab\u4f38\u624b\uff0c\u5c31\u5728\u8fd9\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u53c8\u6293\u4f4f\u4e86\u66f9\u4e91\u5947\u53f3\u8db3\u8db3\u8e1d\u3002\u53ef\u662f\u4e24\u4eba\u4ecd\u662f\u5411\u4e0b\u6025\u843d\uff0c\u4f46\u89c1\u4e24\u4eba\u8eab\u5f62\u6108\u6765\u6108\u5c0f\uff0c\u4e00\u5815\u6570\u5341\u4e08\u3002\u4e0b\u5815\u4e4b\u52bf\u5947\u6025\uff0c\u767d\u8863\u4eba\u6b66\u529f\u518d\u9ad8\uff0c\u53cc\u8db3\u7684\u529b\u9053\u5374\u4e5f\u94a9\u4e0d\u4f4f\u7ef3\u7d22\uff0c\u770b\u6765\u53ea\u6709\u677e\u624b\u653e\u8131\u66f9\u4e91\u5947\uff0c\u624d\u4fdd\u5f97\u4e86\u81ea\u5df1\u6027\u547d\u3002\u4f17\u4eba\u76ee\u7729\u795e\u9a70\u4e4b\u9645\uff0c\u5ffd\u89c1\u4ed6\u53f3\u624b\u4e00\u7529\uff0c\u5c06\u66f9\u4e91\u5947\u7684\u8eab\u5b50\u5411\u7ef3\u7d22\u7529\u5c06\u8fc7\u53bb\u3002 \u3000\u3000\u66f9\u4e91\u5947\u65e9\u5df2\u795e\u667a\u8ff7\u7cca\uff0c\u53cc\u624b\u78b0\u5230\u7ef3\u7d22\uff0c\u7acb\u5373\u7262\u7262\u6293\u4f4f\u3002\u51e1\u662f\u6eba\u6c34\u4e4b\u4eba\uff0c\u5373\u4ee4\u5728\u6c34\u4e2d\u78b0\u5230\u4e00\u6839\u6c34\u8349\uff0c\u4e5f\u5fc5\u5168\u529b\u6293\u4f4f\uff0c\u81f3\u6b7b\u4e0d\u653e\uff0c\u539f\u662f\u6c42\u751f\u672c\u6027\uff0c\u8fd9\u65f6\u66f9\u4e91\u5947\u4e5f\u662f\u5982\u6b64\u3002\u6309\u4ed6\u6b66\u529f\uff0c\u672c\u4e0d\u8db3\u4ee5\u6293\u4f4f\u7ef3\u7d22\u4ee5\u6297\u4e24\u4eba\u6025\u5815\u4e4b\u52bf\uff0c\u4f46\u5371\u96be\u4e4b\u9645\uff0c\u4e0d\u77e5\u600e\u7684\u529b\u6c14\u767b\u65f6\u5927\u4e86\u6570\u500d\u3002\u90a3\u7ef3\u7d22\u76f4\u6643\u51fa\u53bb\uff0c\u5e26\u7740\u4e8c\u4eba\u5411\u5de6\u98de\u8361\u3002 \u3000\u3000\u90a3\u767d\u8863\u4eba\u8170\u95f4\u4f7f\u52b2\uff0c\u8eab\u5b50\u5012\u7ffb\uff0c\u5de6\u624b\u4e5f\u5df2\u6293\u4f4f\u7ef3\u7d22\u3002\u4ed6\u5728\u66f9\u4e91\u5947\u8033\u8fb9\u8bf4\u4e86\u4e24\u53e5\u8bdd\uff0c\u62cd\u62cd\u4ed6\u7684\u80cc\u5fc3\u3002 \u3000\u3000\u66f9\u4e91\u5947\u60ca\u9b42\u672a\u5b9a\uff0c\u4f46\u542c\u4e86\u4ed6\u7684\u8bdd\uff0c\u6709\u5982\u63a5\u5230\u7eb6\u97f3\u5723\u65e8\u4e00\u822c\uff0c\u5fd9\u53cc\u624b\u4ea4\u4e92\u62c9\u7ef3\uff0c\u6500\u63f4\u800c\u4e0a\u3002 \u3000\u3000\u4f17\u4eba\u5728\u5d16\u8fb9\u89c1\u4e86\u8fd9\u573a\u60ca\u5fc3\u52a8\u9b44\u7684\u5947\u9669\uff0c\u5c3d\u7686\u6322\u820c\u96be\u4e0b\u3002\u66f9\u4e91\u5947\u6500\u5230\u5cf0\u8fb9\uff0c\u6bb7\u5409\u4e0e\u5468\u4e91\u9633\u62a2\u8fc7\u53bb\u62c9\u4f4f\u4ed6\u53cc\u624b\uff0c\u63d0\u4e86\u4e0a\u6765\uff0c\u9f50\u95ee\uff1a\u201c\u8fd9\u767d\u8863\u4eba\u662f\u8c01\uff1f\u201c\u66f9\u4e91\u5947\u5598\u4e86\u51e0\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4f4d\u82f1\u96c4\u547d\u6211\u4e0a\u6765\u7980\u62a5\uff0c\u8bf4\u9053\u662f\u2026\u2026\u662f\u80e1\u6590\u5230\u4e86\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e3a\u90a3\u767d\u8863\u4eba\u7684\u6c14\u52bf\u6240\u5f97\uff0c\u4e00\u65f6\u90fd\u6014\u4f4f\u4e86\uff0c\u4e5f\u4e0d\u77e5\u662f\u8c01\u9996\u5148\u53eb\u4e86\u58f0\uff1a\u201c\u554a\u54df\uff01\u201d\u5f80\u5e84\u5185\u4fbf\u5954\u3002 \u3000\u3000\u4f17\u4eba\u4e0d\u53ca\u7ec6\u60f3\uff0c\u4e00\u7a9d\u8702\u7684\u5f80\u5927\u95e8\u62a2\u53bb\u3002\u9676\u767e\u5c81\u3001\u5218\u5143\u9e64\u3001\u962e\u58eb\u4e2d\u4e09\u4eba\u4e00\u9f50\u6324\u5728\u95e8\u53e3\uff0c\u4f60\u63a8\u6211\u6324\uff0c\u4e89\u5148\u800c\u5165\u3002\u66f9\u4e91\u5947\u62a2\u7740\u53bb\u6276\u7530\u9752\u6587\uff0c\u4e0e\u9676\u5b50\u5b89\u767e\u5fd9\u4e2d\u53c8\u4e92\u6325\u6570\u62f3\u3002\u53ea\u4e00\u9635\u4e71\uff0c\u95e8\u5916\u4f17\u4eba\u8d70\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u4e8e\u7ba1\u5bb6\u4e0e\u7434\u513f\u6276\u7740\u82d7\u82e5\u5170\u8d70\u5728\u6700\u540e\uff0c\u9669\u4e9b\u513f\u7ed9\u5173\u5728\u95e8\u5916\u3002 \u3000\u3000\u6bb7\u5409\u89c1\u718a\u5143\u732e\u95ed\u4e0a\u5927\u95e8\uff0c\u7acb\u5373\u53d6\u8fc7\u95e8\u95e9\uff0c\u6a2a\u7740\u95e9\u4e0a\u3002\u9676\u767e\u5c81\u53ea\u6015\u4e0d\u56fa\uff0c\u53c8\u53d6\u8fc7\u6491\u67f1\uff0c\u7262\u7262\u6491\u4f4f\u3002 \u3000\u3000\u6b64\u65f6\u7530\u9752\u6587\u5df2\u9192\u4e86\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u90a3\u8ddf\u54b1\u4eec\u7d20\u4e0d\u76f8\u8bc6\uff0c\u6015\u4ed6\u600e\u7684\uff1f\u201d\u962e\u58eb\u4e2d\u6a2a\u4e86\u5979\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u7d20\u4e0d\u76f8\u8bc6\uff1f\u54fc\uff0c\u4f60\u7239\u7239\u662f\u4ed6\u8001\u5b50\u7684\u5927\u4ec7\u4eba\uff0c\u4ed6\u80af\u653e\u8fc7\u4f60\u4e48\uff1f\u201d\u5218\u5143\u9e64\u4e5f\u9053\uff1a\u201c\u54b1\u4eec\u4f24\u4e86\u5e73\u963f\u56db\uff0c\u90a3\u5c82\u80af\u5e72\u4f11\uff1f\u201d \u3000\u3000\u9676\u5b50\u5b89\u5ffd\u5411\u5899\u5934\u4e00\u6307\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u6491\u4f4f\u5927\u95e8\uff0c\u4ed6\u4ece\u4e0a\u9762\u4e0d\u80fd\u8fdb\u6765\u4e48\uff1f\u201d\u962e\u58eb\u4e2d\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9676\u4e16\u5144\u5feb\u4e0a\u9ad8\u5b88\u7740\u3002\u201d\u9676\u5b50\u5b89\u51b7\u7b11\u9053\uff1a\u201c\u962e\u5e08\u53d4\u6b66\u529f\u9ad8\uff0c\u8fd8\u662f\u4f60\u8001\u4eba\u5bb6\u4e0a\u53bb\u3002\u201d\u4e00\u8a00\u752b\u6bd5\uff0c\u731b\u542c\u5580\u5587\u5587\u51e0\u58f0\u5de8\u54cd\uff0c\u90a3\u6491\u67f1\u4e0e\u95e8\u95e9\u7a81\u7136\u8ff8\u65ad\uff0c\u7830\u562d\u4e00\u54cd\uff0c\u4e24\u6247\u5927\u95e8\u5df2\u88ab\u4eba\u63a8\u5f00\u3002 \u3000\u3000\u4f17\u4eba\u9f50\u58f0\u60ca\u547c\uff0c\u76f4\u5f80\u5185\u9662\u5954\u53bb\uff0c\u970e\u65f6\u4e4b\u95f4\uff0c\u5927\u5385\u4e0a\u53c8\u662f\u6773\u65e0\u4e00\u4eba\u3002 \u3000\u3000\u7fa4\u8c6a\u521d\u542c\u5e73\u963f\u56db\u8bf4\u90a3\u80e1\u4e00\u5200\u7684\u5f80\u4e8b\uff0c\u9887\u60f3\u89c1\u89c1\u4ed6\u9057\u4e0b\u7684\u5b64\u513f\uff0c\u53ef\u662f\u5f85\u5f97\u5f53\u771f\u4e0a\u5c71\uff0c\u773c\u89c1\u4ed6\u8eab\u624b\u7adf\u5982\u6b64\u4e86\u5f97\uff0c\u4e0d\u7981\u5fc3\u5bd2\u80c6\u602f\uff0c\u53c8\u89c1\u65c1\u4eba\u9003\u907f\uff0c\u76f8\u4e92\u60ca\u5413\uff0c\u4f60\u6015\u6211\u66f4\u6015\uff0c\u5e73\u7d20\u7684\u8c6a\u6c14\u96c4\u98ce\uff0c\u5c3d\u6570\u4e22\u5230\u4e5d\u9704\u4e91\u5916\u53bb\u4e86\u3002 \u3000\u3000\u4e8e\u7ba1\u5bb6\u6b32\u89c5\u5b9d\u6811\u51fa\u53bb\u62b5\u6321\u4e00\u9635\uff0c\u53ef\u662f\u56db\u4e0b\u5f20\u671b\uff0c\u5b9d\u6811\u65e9\u5df2\u4e0d\u89c1\uff0c\u4e0d\u77e5\u8eb2\u5230\u4e86\u54ea\u91cc\uff0c\u5fc3\u60f3\uff1a\u201c\u4e3b\u4eba\u5c06\u5e84\u4e0a\u4e4b\u4e8b\u6258\u4ed8\u4e86\u7ed9\u6211\uff0c\u62da\u7740\u4e00\u6b7b\uff0c\u4e5f\u5f97\u5168\u4e86\u4e3b\u4eba\u7684\u8138\u9762\u3002\u201d\u5f53\u4e0b\u5411\u82d7\u82e5\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u82d7\u59d1\u5a18\uff0c\u4f60\u5feb\u5230\u592b\u4eba\u623f\u53bb\uff0c\u8ddf\u592b\u4eba\u4e00\u540c\u8eb2\u5165\u5730\u7a96\u5bc6\u5ba4\uff0c\u53ef\u522b\u8ba9\u4eba\u77a7\u89c1\u3002\u8fd9\u91cc\u7684\u4eba\u6ca1\u4e00\u4e2a\u5b89\u7740\u597d\u5fc3\u3002\u5f85\u6211\u51fa\u53bb\u89c1\u4ed6\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u5411\u90d1\u4e09\u5a18\u4e0e\u7530\u9752\u6587\u671b\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u5e26\u8fd9\u4e24\u4f4d\u59ca\u59ca\u4e00\u8d77\u53bb\u5730\u7a96\u5427\u3002\u201d\u4e8e\u7ba1\u5bb6\u6025\u5fd9\u6447\u5934\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e0d\uff0c\u8fd9\u4e24\u4e2a\u5973\u4eba\u6050\u6015\u4e0d\u662f\u597d\u4eba\u3002\u59d1\u5a18\u8ddf\u592b\u4eba\u662f\u5343\u91d1\u8d35\u4f53\uff0c\u83ab\u7406\u4f1a\u65c1\u4eba\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u90a3\u59d3\u80e1\u7684\u82e5\u662f\u8981\u6740\u4eba\u653e\u706b\uff0c\u4f60\u6321\u5f97\u4e86\u4e48\uff1f\u201d\u4e8e\u7ba1\u5bb6\u4e00\u6309\u8170\u95f4\u5355\u5200\u7684\u5200\u67c4\uff0c\u60e8\u7136\u9053\uff1a\u201c\u4eca\u65e5\u662f\u4e8e\u67d0\u4ee5\u6b7b\u62a5\u4e3b\u4e4b\u65f6\uff0c\u4f46\u6c42\u592b\u4eba\u4e0e\u59d1\u5a18\u5e73\u5b89\u65e0\u4e8b\uff0c\u5c0f\u4eba\u5c31\u5bf9\u5f97\u8d77\u4e3b\u4eba\u4e86\u3002\u201d\u82d7\u82e5\u5170\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u4e00\u9f50\u51fa\u53bb\u4f1a\u4ed6\u3002\u201d\u4e8e\u7ba1\u5bb6\u5927\u6025\uff0c\u5fd9\u9053\uff1a\u201c\u82d7\u59d1\u5a18\uff0c\u4f60\u6ca1\u542c\u90a3\u548c\u5c1a\u8bf4\uff0c\u4ee4\u5c0a\u82d7\u5927\u4fa0\u4e0e\u4ed6\u6709\u6740\u7236\u5927\u4ec7\uff1f\u4f60\u82e5\u4e0d\u8eb2\u5f00\uff0c\u843d\u5728\u6b64\u4eba\u624b\u4e2d\uff0c\u90a3\u2026\u2026\u90a3\u2026\u2026\u201d \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u81ea\u4ece\u6211\u542c\u7239\u7239\u8bf4\u4e86\u80e1\u4f2f\u4f2f\u7684\u5f80\u4e8b\uff0c\u4e00\u76f4\u5c31\u76fc\u90a3\u4e2a\u5b69\u5b50\u8fd8\u6d3b\u5728\u4e16\u4e0a\uff0c\u4e5f\u76fc\u7ec8\u987b\u6709\u65e5\u80fd\u89c1\u4ed6\u4e00\u89c1\u3002\u4eca\u65e5\u4e4b\u4e8b\u867d\u9669\uff0c\u4f46\u82e5\u4ece\u6b64\u4e0d\u80fd\u518d\u4e0e\u4ed6\u76f8\u89c1\uff0c\u6211\u53ef\u8981\u62b1\u61be\u4e00\u751f\u4e86\u3002\u201d \u3000\u3000\u5979\u8fd9\u51e0\u53e5\u867d\u8bf4\u5f97\u8f7b\u67d4\u6e29\u6587\uff0c\u7136\u8bed\u610f\u6781\u4e3a\u575a\u5b9a\uff0c\u4e8e\u7ba1\u5bb6\u7adf\u5c14\u4e0d\u80fd\u8fdd\u6297\u3002\u4ed6\u5fc3\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\uff0c\u5374\u52c7\u51b3\u5982\u6b64\uff0c\u771f\u4e0d\u6127\u662f\u91d1\u9762\u4f5b\u82d7\u5927\u4fa0\u4e4b\u5973\u3002\u4ec0\u4e48\u9547\u5173\u4e1c\u3001\u5a01\u9707\u5929\u5357\uff0c\u540d\u53f7\u513f\u53eb\u5f97\u633a\u54cd\uff0c\u4e0e\u82d7\u59d1\u5a18\u4e00\u6bd4\uff0c\u5018\u4e0d\u6127\u6b7b\uff0c\u4e5f\u53ef\u7b97\u5f97\u8138\u76ae\u539a\u6781\u3002\u201d \u3000\u3000\u4ed6\u672c\u6765\u5fc3\u4e2d\u5bb3\u6015\uff0c\u4f46\u89c1\u82d7\u82e5\u5170\u795e\u8272\u5b81\u5b9a\uff0c\u60ca\u60e7\u4e4b\u5fc3\u767b\u51cf\uff0c\u5f53\u4e0b\u7d27\u4e00\u7d27\u8170\u5e26\uff0c\u5728\u8336\u76d8\u4e2d\u653e\u4e86\u4e24\u53ea\u9752\u82b1\u7ec6\u74f7\u7684\u76d6\u7897\uff0c\u51b2\u4e0a\u4e86\u8336\uff0c\u8d70\u51fa\u5385\u53bb\u3002\u82d7\u82e5\u5170\u8ddf\u968f\u5728\u540e\u3002 \u3000\u3000\u4e8e\u7ba1\u5bb6\u8f6c\u51fa\u5385\u58c1\uff0c\u53ea\u89c1\u90a3\u767d\u8863\u4eba\u8138\u5b54\u671d\u5916\uff0c\u53cc\u624b\u53c9\u8170\uff0c\u62ac\u5934\u671b\u5929\uff0c\u4fbf\u9ad8\u58f0\u9053\uff1a\u201c\u80e1\u5927\u7237\u8fdc\u6765\uff0c\u4e0d\u66fe\u8fdc\u8fce\uff0c\u8fd8\u8bf7\u6055\u7f6a\u3002\u201d\u8bf4\u7740\u732e\u4e0a\u8336\u53bb\u3002\u90a3\u767d\u8863\u4eba\u542c\u5f97\u4e8e\u7ba1\u5bb6\u8bf4\u8bdd\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u89c1\u5230\u82d7\u82e5\u5170\u8fd9\u6837\u4e00\u4e2a\u6587\u79c0\u6e05\u96c5\u7684\u5c11\u5973\uff0c\u5f31\u6001\u751f\u5a07\uff0c\u660e\u6ce2\u6d41\u6167\uff0c\u602f\u751f\u751f\u7684\u7ad9\u5728\u5f53\u5730\uff0c\u4e0d\u7981\u4e00\u6014\u3002 \u3000\u3000\u82d7\u82e5\u5170\u89c1\u8fd9\u4eba\u6ee1\u816e\u866c\u9aef\uff0c\u6839\u6839\u5982\u94c1\uff0c\u4e00\u5934\u6d53\u53d1\uff0c\u5374\u4e0d\u7ed3\u8fab\uff0c\u6a2a\u751f\u5012\u7ad6\u822c\u6709\u5982\u4e71\u8349\uff0c\u4e5f\u662f\u4e00\u60ca\u3002\u5979\u81ea\u5e7c\u5bf9\u80e1\u4e00\u5200\u4e4b\u5b50\u5fc3\u6000\u601c\u60dc\u60b2\u60af\u4e4b\u60c5\uff0c\u60f3\u5230\u4ed6\u65f6\uff0c\u603b\u89c9\u4ed6\u662f\u4e2a\u53d7\u4eba\u6b3a\u4fae\u8650\u5f85\u7684\u7a1a\u5b50\uff0c\u4eca\u65e5\u76f8\u89c1\uff0c\u5374\u4e0d\u6599\u7adf\u662f\u5982\u6b64\u7c97\u8c6a\u731b\u6076\u7684\u4e00\u6761\u6c49\u5b50\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5f97\u4e09\u5206\u60ca\u5f02\uff0c\u4e09\u5206\u60f6\u60d1\uff0c\u53c8\u6709\u4e09\u5206\u5931\u671b\uff0c\u4f46\u968f\u5373\u60f3\u5230\uff1a \u3000\u3000\u201c\u80e1\u4e00\u5200\u80e1\u4f2f\u4f2f\u5bb9\u8c8c\u5a01\u4e25\uff0c\u4ed6\u751f\u7684\u5b69\u5b50\u81ea\u4e5f\u662f\u8fd9\u822c\uff0c\u53c8\u4f55\u8db3\u4e3a\u5947\uff1f\u5374\u662f\u6211\u4e00\u5411\u5c06\u4ed6\u60f3\u9519\u4e86\u3002\u201d\u5f53\u4e0b\u4e0a\u524d\u76c8\u76c8\u4e00\u798f\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u76f8\u516c\u4e07\u798f\u3002\u201d \u3000\u3000\u80e1\u6590\u6b64\u756a\u4e0a\u5cf0\uff0c\u51c6\u62df\u4e0e\u6ee1\u5c71\u9ad8\u624b\u4f5c\u4e00\u573a\u9f99\u4e89\u864e\u6597\uff0c\u54ea\u77e5\u5e84\u4e2d\u51fa\u6765\u76f8\u89c1\u7684\u7adf\u662f\u4e00\u4e2a\u59e3\u597d\u5c11\u5973\uff0c\u4e0d\u7981\u5927\u662f\u8be7\u5f02\uff0c\u6697\u9053\uff1a\u201c\u4e14\u77a7\u4ed6\u4eec\u4f7f\u4ec0\u4e48\u8be1\u8ba1\u3002\u201d\u5f53\u4e0b\u8fd8\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u80e1\u6590\u5949\u63d6\u3002\u4e0d\u6562\u8bf7\u95ee\u59d1\u5a18\u9ad8\u59d3\u3002\u201d \u3000\u3000\u4e8e\u7ba1\u5bb6\u5411\u82d7\u82e5\u5170\u4f7f\u4e2a\u773c\u8272\uff0c\u53eb\u5979\u634f\u9020\u4e2a\u5047\u59d3\uff0c\u5343\u4e07\u4e0d\u53ef\u5410\u9732\u662f\u82d7\u4eba\u51e4\u4e4b\u5973\uff0c\u54ea\u77e5\u82d7\u82e5\u5170\u7adf\u4f3c\u4e0d\u89e3\uff0c\u8bf4\u9053\uff1a\u201c\u80e1\u4e16\u5144\uff0c\u54b1\u4eec\u662f\u7d2f\u4ee3\u4e16\u4ea4\uff0c\u53ef\u60dc\u4ece\u6765\u672a\u66fe\u4f1a\u9762\u3002\u6211\u59d3\u82d7\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u66f4\u662f\u4e00\u51db\uff0c\u8138\u4e0a\u5374\u4e0d\u52a8\u58f0\u8272\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u4e0e\u91d1\u9762\u4f5b\u82d7\u5927\u4fa0\u600e\u751f\u79f0\u547c\uff1f\u201d\u4e8e\u7ba1\u5bb6\u5927\u6025\uff0c\u5728\u82d7\u82e5\u5170\u8eab\u65c1\u6697\u626f\u5979\u7684\u8863\u8896\u3002\u5979\u4ecd\u662f\u4e0d\u7406\uff0c\u9053\uff1a\u201c\u91d1\u9762\u4f5b\u5c31\u662f\u5bb6\u7236\u3002\u201d\u80e1\u6590\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u3002\u201d\u8bf4\u9053\uff1a\u201c\u4ee4\u5c0a\u600e\u4e0d\u51fa\u6765\u76f8\u89c1\uff1f\u201d \u3000\u3000\u4e8e\u7ba1\u5bb6\u624b\u6309\u5200\u67c4\uff0c\u53ea\u6015\u80e1\u6590\u51fa\u624b\u76f8\u5bb3\uff0c\u659c\u773c\u770b\u82d7\u82e5\u5170\u65f6\uff0c\u5374\u89c1\u5979\u795e\u8272\u5982\u5e38\uff0c\u4e0d\u7981\u53f9\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u5e74\u5e7c\u65e0\u77e5\uff0c\u773c\u524d\u4fbf\u662f\u6740\u7236\u7684\u5927\u4ec7\u4eba\uff0c\u5979\u7adf\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\uff0c\u5c3d\u5410\u771f\u76f8\u3002\u201d\u53ea\u542c\u5979\u8bf4\u9053\uff1a\u201c\u5bb6\u7236\u5c1a\u672a\u4e0a\u5c71\u3002\u5979\u82e5\u77e5\u80e1\u4e16\u5144\u662f\u6545\u4eba\u4e4b\u5b50\uff0c\u7eb5\u6709\u5929\u5927\u7684\u8981\u4e8b\uff0c\u4e5f\u65e9\u6401\u4e0b\uff0c\u5fc5\u5df2\u8d76\u6765\u4e0e\u4e16\u5144\u76f8\u89c1\u3002\u201d \u3000\u3000\u80e1\u6590\u66f4\u662f\u5947\u602a\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u77e5\u9053\u5728\u4e0b\u8eab\u4e16\uff0c\u4ee4\u5c0a\u5374\u4e0d\u77e5\u6653\uff0c\u6562\u95ee\u4f55\u6545\uff1f\u201d\u82d7\u82e5\u5170\u9053\uff1a\u201c\u8fd8\u662f\u9002\u624d\u542c\u4ee4\u53cb\u5e73\u541b\u8bf4\u7684\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u5e73\u56db\u53d4\u5230\u4e86\u8fd9\u513f\uff0c\u4ed6\u4eba\u5462\uff1f\u201d \u3000\u3000\u4e8e\u7ba1\u5bb6\u4e00\u6014\uff0c\u5728\u5385\u4e2d\u56db\u4e0b\u4e00\u671b\uff0c\u65e9\u4e0d\u89c1\u4e86\u5e73\u963f\u56db\u7684\u4eba\u5f71\uff0c\u5730\u4e0a\u7684\u4e00\u6ee9\u9c9c\u8840\u5374\u5140\u81ea\u672a\u5e72\uff0c\u5fc3\u9053\uff1a\u201c\u81ea\u90a3\u9e3d\u513f\u5e26\u7ebf\u5165\u6765\uff0c\u4e2a\u4e2a\u60f3\u7740\u4e0b\u5cf0\u9003\u751f\uff0c\u7adf\u90fd\u5c06\u6b64\u4eba\u5fd8\u4e86\u3002\u4ed6\u662f\u80e1\u6590\u7684\u6551\u547d\u6069\u4eba\uff0c\u82e5\u662f\u6709\u4ec0\u4e48\u4e0d\u6d4b\uff0c\u7978\u60a3\u53c8\u662f\u52a0\u6df1\u4e86\u4e00\u5c42\u3002\u201d \u3000\u3000\u80e1\u6590\u89c1\u4ed6\u671b\u7740\u5730\u4e0b\u7684\u4e00\u6ee9\u9c9c\u8840\uff0c\u8138\u8272\u6709\u5f02\uff0c\u5927\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u5e73\u56db\u53d4\u7684\u8840\u4e48\uff1f\u201d\u4e8e\u7ba1\u5bb6\u4e0d\u6562\u6253\u8bf3\uff0c\u53ea\u5f97\u5e94\u58f0\u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u80e1\u6590\u7236\u6bcd\u65e9\u4e27\uff0c\u81ea\u5e7c\u7531\u5e73\u963f\u56db\u629a\u517b\u957f\u5927\uff0c\u4e0e\u4ed6\u60c5\u82e5\u7236\u5b50\uff0c\u4e00\u95fb\u6b64\u8a00\u5982\u4f55\u4e0d\u60ca\uff1f\u5f53\u4e0b\u4e00\u8dc3\u800c\u524d\uff0c\u4e00\u4f38\u624b\uff0c\u63e1\u4f4f\u4e8e\u7ba1\u5bb6\u7684\u53f3\u81c2\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u4ed6\u5728\u54ea\u91cc\uff1f\u4ed6\u2026\u2026\u4ed6\u600e\u6837\u4e86\uff1f\u201d\u4e8e\u7ba1\u5bb6\u53ea\u89c9\u624b\u81c2\u5267\u75db\uff0c\u5b9b\u4f3c\u4e00\u9053\u94a2\u7b8d\u8d8a\u6536\u8d8a\u7d27\uff0c\u53ea\u5f97\u54ac\u7d27\u4e86\u7259\u9f7f\u7aed\u529b\u5fcd\u75db\uff0c\u989d\u5934\u4e0a\u9ec4\u8c46\u5927\u7684\u6c57\u73e0\u4e00\u7c92\u7c92\u6e17\u5c06\u51fa\u6765\uff0c\u7adf\u8bf4\u4e0d\u51fa\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u82d7\u82e5\u5170\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u80e1\u4e16\u5144\u4e0d\u5fc5\u7126\u6025\uff0c\u5e73\u56db\u7237\u597d\u597d\u7684\u5728\u90a3\u8fb9\u3002\u201d\u8bf4\u7740\u4f38\u624b\u5411\u897f\u8fb9\u53a2\u623f\u4e00\u6307\u3002\u80e1\u6590\u653e\u8131\u4e86\u4e8e\u7ba1\u5bb6\u7684\u624b\u81c2\uff0c\u968f\u5373\u817e\u8eab\u800c\u8d77\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u8e22\u5f00\u897f\u53a2\u623f\u623f\u95e8\uff0c\u53ea\u89c1\u5e73\u963f\u56db\u8eba\u5728\u69bb\u4e0a\uff0c\u6b63\u4e0d\u4f4f\u5598\u606f\u3002\u80e1\u6590\u5927\u559c\uff0c\u53eb\u9053\uff1a\u201c\u56db\u53d4\uff0c\u4f60\u6ca1\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u5e73\u963f\u56db\u5728\u53a2\u623f\u91cc\u65e9\u5c31\u542c\u5230\u4ed6\u7684\u58f0\u97f3\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u4f60\u653e\u5fc3\u3002\u201d\u80e1\u6590\u62a2\u4e0a\u524d\u53bb\uff0c\u89c1\u4ed6\u8138\u5982\u91d1\u7eb8\uff0c\u547c\u5438\u4f4e\u5fae\uff0c\u9002\u624d\u4e00\u65f6\u4e4b\u95f4\u7684\u559c\u60a6\u53c8\u8f6c\u4e3a\u62c5\u5fe7\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\u53d7\u7684\u4f24\uff1f\u4f24\u5f97\u5389\u5bb3\u4e48\uff1f\u201d \u3000\u3000\u5e73\u963f\u56db\u9053\uff1a\u201c\u8fd9\u4e8b\u8bf4\u6765\u8bdd\u957f\u3002\u82e5\u4e0d\u662f\u82d7\u59d1\u5a18\u642d\u6551\uff0c\u4eca\u751f\u4e0d\u80fd\u518d\u8ddf\u4f60\u76f8\u89c1\u4e86\u3002\u201d\u539f\u6765\u4f17\u4eba\u4e00\u89c1\u767d\u9e3d\u4f20\u4e1d\uff0c\u4e00\u7a9d\u8702\u7684\u6d8c\u51fa\u5927\u5385\u3002 \u3000\u3000\u82d7\u82e5\u5170\u4e58\u673a\u4e0e\u7434\u513f\u5c06\u5e73\u963f\u56db\u6276\u5165\u4e86\u53a2\u623f\u3002\u540e\u6765\u5b9d\u6811\u6b32\u5f85\u4f24\u4ed6\u6027\u547d\uff0c\u5374\u5df2\u627e\u4ed6\u4e0d\u5230\uff0c\u60c5\u52bf\u7d27\u6025\uff0c\u4e0d\u53ca\u4ed4\u7ec6\u5bfb\u627e\uff0c\u5e73\u963f\u56db\u56e0\u6b64\u800c\u5f97\u4fdd\u5168\u3002 \u3000\u3000\u80e1\u6590\u70b9\u70b9\u5934\uff0c\u4ece\u8863\u56ca\u4e2d\u53d6\u51fa\u4e00\u9897\u6731\u7ea2\u4e38\u836f\uff0c\u585e\u5728\u4ed6\u7684\u53e3\u91cc\uff0c\u9053\uff1a\u201c\u56db\u53d4\uff0c\u4f60\u5148\u670d\u4e86\u8fd9\u9897\u4f24\u836f\u3002\u201d \u3000\u3000\u4ed6\u89c1\u5e73\u963f\u56db\u5c06\u4f24\u836f\u56bc\u70c2\u541e\u4e0b\uff0c\u7a0d\u7a0d\u653e\u5fc3\uff0c\u56de\u5230\u5385\u4e0a\uff0c\u5411\u82d7\u82e5\u5170\u4e00\u63d6\u5230\u5730\uff0c\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u6551\u6211\u5e73\u56db\u53d4\u3002\u201d\u82d7\u82e5\u5170\u5fd9\u5373\u8fd8\u793c\uff0c\u9053\uff1a\u201c\u5e73\u56db\u7237\u53e4\u9053\u70ed\u80a0\uff0c\u5c0f\u59b9\u94a6\u4ef0\u5f97\u7d27\u3002\u4e9b\u4e9b\u5fae\u52b3\uff0c\u4f55\u8db3\u6302\u9f7f\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u751f\u6b7b\u5927\u4e8b\uff0c\u5c82\u662f\u5fae\u52b3\uff1f\u5728\u4e0b\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u89c1\u4ed6\u795e\u60c5\u7c97\u8c6a\uff0c\u5410\u5c5e\u5374\u9887\u4e3a\u65af\u6587\uff0c\u8bf4\u9053\uff1a\u201c\u80e1\u4e16\u5144\u8fdc\u6765\uff0c\u5e84\u4e0a\u65e0\u4ee5\u4e3a\u656c\u3002\u7434\u513f\uff0c\u5feb\u53d6\u9152\u80b4\u51fa\u6765\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u6b64\u95f4\u4e3b\u4eba\u7ea6\u5b9a\u5728\u4e0b\u4eca\u65e5\u5348\u65f6\u76f8\u4f1a\uff0c\u600e\u4e48\u5230\u6b64\u523b\u8fd8\u4e0d\u51fa\u6765\u76f8\u89c1\uff1f\u201d \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u4e3b\u4eba\u56e0\u6709\u8981\u4e8b\u4e0b\u5c71\uff0c\u60f3\u6765\u9014\u4e2d\u803d\u6401\uff0c\u672a\u53ca\u8d76\u56de\uff0c\u81f4\u8bef\u4e16\u5144\u4e4b\u7ea6\uff0c\u5c0f\u59b9\u5148\u6b64\u8c22\u8fc7\u3002\u201d \u3000\u3000\u80e1\u6590\u542c\u5979\u5e94\u5bf9\u5f97\u4f53\uff0c\u5fc3\u4e2d\u66f4\u5947\uff1a\u201c\u82d7\u8303\u7530\u4e09\u5bb6\u5411\u79f0\u4eba\u6750\u9f0e\u76db\uff0c\u600e\u4e48\u7537\u5b50\u6c49\u90fd\u7f29\u5728\u540e\u9762\uff0c\u5374\u53eb\u8fd9\u6837\u4e00\u4e2a\u5f31\u4e0d\u7981\u98ce\u7684\u5c11\u5973\u51fa\u6765\u63a8\u642a\uff1f\u8fd9\u59d1\u5a18\u5bf9\u6211\u4e1d\u6beb\u4e0d\u793a\u602f\u610f\uff0c\u96be\u9053\u5979\u7adf\u662f\u4e00\u8eab\u6b66\u827a\uff0c\u5374\u6709\u610f\u7684\u6df1\u85cf\u4e0d\u9732\u4e48\uff1f\u201d\u53ea\u89c1\u7434\u513f\u6258\u4e86\u4e00\u53ea\u6728\u76d8\u8fc7\u6765\uff0c\u76d8\u4e2d\u653e\u7740\u4e00\u5927\u58f6\u9152\uff0c\u4e00\u53ea\u9152\u676f\uff0c\u5979\u5de6\u624b\u62ff\u7740\u6728\u76d8\uff0c\u53f3\u624b\u5728\u676f\u4e2d\u659f\u4e86\u9152\uff0c\u7b11\u9053\uff1a\u201c\u80e1\u76f8\u516c\uff0c\u5c71\u4e0a\u7684\u9e21\u9e2d\u9c7c\u8089\u3001\u852c\u83dc\u74dc\u679c\uff0c\u901a\u7edf\u7ed9\u4f60\u7684\u5e73\u56db\u7237\u6bc1\u5566\u3002\u5bf9\u4e0d\u8d77\uff0c\u53ea\u597d\u8bf7\u4f60\u559d\u676f\u767d\u9152\u3002\u201d \u3000\u3000\u80e1\u6590\u89c1\u90a3\u6728\u76d8\u6b63\u5728\u4ed6\u4e0e\u82d7\u82e5\u5170\u4e4b\u95f4\uff0c\u5f53\u5373\u4f38\u51fa\u5de6\u624b\uff0c\u5728\u76d8\u8fb9\u8f7b\u8f7b\u4e00\u63a8\uff0c\u6728\u76d8\u5f84\u5411\u82d7\u82e5\u5170\u80a9\u4e0a\u649e\u53bb\u3002\u8fd9\u4e00\u63a8\u867d\u4f3c\u51fa\u624b\u751a\u8f7b\uff0c\u5176\u5b9e\u501f\u52b2\u6253\u4eba\uff0c\u53d7\u7740\u7684\u4eba\u82e5\u662f\u4e0d\u52a0\u62b5\u5fa1\uff0c\u5c31\u5982\u4e2d\u4e86\u5175\u5203\u4e4b\u4f24\u65e0\u5f02\u3002\u82d7\u82e5\u5170\u4e0d\u4f1a\u6b66\u827a\uff0c\u53ea\u662f\u987a\u4e4e\u81ea\u7136\u7684\u5fae\u5fae\u4e00\u8ba9\uff0c\u5e76\u672a\u51fa\u62db\u5316\u52b2\uff0c\u773c\u89c1\u8fd9\u4e00\u4e0b\u4fbf\u8981\u8eab\u53d7\u91cd\u4f24\u3002 \u3000\u3000\u4e8e\u7ba1\u5bb6\u5927\u60ca\uff0c\u4ed6\u81ea\u77e5\u6b66\u529f\u4e0e\u80e1\u6590\u5dee\u5f97\u592a\u8fdc\uff0c\u7eb5\u7136\u4e0d\u987e\u6027\u547d\u7684\u4e0a\u524d\u6551\u63f4\uff0c\u4e5f\u5fc5\u65e0\u6d4e\u4e8e\u4e8b\uff0c\u53ea\u53eb\u5f97\u4e00\u58f0\uff1a\u201c\u554a\u54df\uff01\u201d\u5374\u89c1\u80e1\u6590\u5de6\u624b\u4e24\u6839\u624b\u6307\u5df2\u8fc5\u6377\u65e0\u6bd4\u7684\u62c9\u4f4f\u4e86\u6728\u76d8\uff0c\u8fd9\u4e00\u4e0b\u65f6\u673a\u51d1\u5408\u5f97\u51c6\u6781\uff0c\u76d8\u8fb9\u4e0e\u82d7\u82e5\u5170\u7684\u5916\u8863\u53ea\u5fae\u5fae\u4e00\u78b0\uff0c\u7acb\u5373\u7f29\u56de\u3002\u5979\u4e1d\u6beb\u4e0d\u77e5\u5c31\u5728\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u81ea\u5df1\u5df2\u4ece\u751f\u5230\u6b7b\u3001\u4ece\u6b7b\u5230\u751f\u7684\u8d70\u4e86\u4e00\u4e2a\u5faa\u73af\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4ee4\u5c0a\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\uff0c\u5374\u4f55\u4ee5\u4e0d\u4f20\u59d1\u5a18\u6b66\u529f\uff1f \u3000\u3000\u7d20\u95fb\u82d7\u5bb6\u5251\u95e8\u4e2d\uff0c\u4f20\u5b50\u4f20\u5973\uff0c\u4e00\u89c6\u540c\u4ec1\u3002\u201d\u82d7\u82e5\u5170\u9053\uff1a\u201c\u6211\u7239\u7239\u7acb\u5fd7\u8981\u5316\u89e3\u8fd9\u573a\u767e\u4f59\u5e74\u6765\u7ea0\u7f20\u4e0d\u6e05\u7684\u4ec7\u6028\uff0c\u662f\u4ee5\u82d7\u5bb6\u5251\u6cd5\uff0c\u81f3\u4ed6\u800c\u7edd\uff0c\u4e0d\u518d\u4f20\u6388\u5b50\u5f1f\u3002\u201d \u3000\u3000\u80e1\u6590\u6115\u7136\uff0c\u62ff\u7740\u9152\u676f\u7684\u624b\u505c\u5728\u534a\u7a7a\uff0c\u9694\u4e86\u7247\u523b\uff0c\u65b9\u59cb\u4e3e\u5230\u53e3\u8fb9\uff0c\u4e00\u996e\u800c\u5c3d\uff0c\u53eb\u9053\uff1a\u201c\u82d7\u4eba\u51e4\uff0c\u82d7\u5927\u4fa0\uff0c\u597d\uff01\u679c\u7136\u79f0\u5f97\u4e0a\u2018\u5927\u4fa0\u2019\u4e8c\u5b57\uff01\u201d \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u6211\u66fe\u542c\u7239\u7239\u8bf4\u8d77\u4ee4\u5c0a\u5f53\u65e5\u4e4b\u4e8b\u3002\u90a3\u65f6\u4ee4\u5802\u8bf7\u6211\u7239\u7239\u996e\u9152\uff0c\u65c1\u4eba\u8bf4\u9053\u987b\u9632\u9152\u4e2d\u6709\u6bd2\u3002\u6211\u7239\u7239\u8a00\u9053\uff1a\u2018\u80e1\u4e00\u5200\u4e43\u5929\u4e0b\u82f1\u96c4\uff0c\u5149\u660e\u78ca\u843d\uff0c\u5c82\u80fd\u884c\u6b64\u5351\u52a3\u4e4b\u4e8b\uff1f\u2019\u4eca\u65e5\u6211\u8bf7\u4f60\u996e\u9152\uff0c\u80e1\u4e16\u5144\u5c45\u7136\u4e5f\u662f\u5766\u7387\u996e\u5c3d\uff0c\u96be\u9053\u4f60\u4e5f\u4e0d\u6015\u522b\u4eba\u6697\u7b97\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u4e00\u7b11\uff0c\u4ece\u53e3\u4e2d\u5410\u51fa\u4e00\u9897\u9ec4\u8272\u836f\u4e38\uff0c\u8bf4\u9053\uff1a\u201c\u5148\u7236\u4e2d\u4eba\u5978\u8ba1\u800c\u6b7b\uff0c\u6211\u82e5\u518d\u4e0d\u9632\uff0c\u5c82\u975e\u75f4\u5446\uff1f\u8fd9\u836f\u4e38\u5584\u80fd\u89e3\u6bd2\uff0c\u8bf8\u5bb3\u4e0d\u4fb5\uff0c\u53ea\u662f\u9002\u624d\u542c\u4e86\u59d1\u5a18\u4e4b\u8a00\uff0c\u5012\u663e\u662f\u6211\u80f8\u895f\u72ed\u9698\u4e86\u3002\u201d\u8bf4\u7740\u81ea\u5df1\u659f\u4e86\u4e00\u676f\u9152\uff0c\u53c8\u662f\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u5c71\u4e0a\u65e0\u4e0b\u9152\u4e4b\u7269\uff0c\u6b8a\u4e3a\u6162\u5ba2\u3002\u5c0f\u59b9\u91cf\u7a84\uff0c\u53c8\u4e0d\u80fd\u656c\u966a\u541b\u5b50\u3002\u53e4\u4eba\u4ee5\u6c49\u4e66\u4e0b\u9152\uff0c\u5c0f\u59b9\u6709\u6c49\u7434\u4e00\u5f20\uff0c\u6b32\u629a\u4e00\u66f2\uff0c\u4ee5\u52a9\u9152\u5174\uff0c\u4f46\u6050\u6709\u6c61\u6e05\u542c\u3002\u201d\u80e1\u6590\u559c\u9053\uff1a\u201c\u613f\u95fb\u96c5\u594f\u3002\u201d\u7434\u513f\u4e0d\u7b49\u5c0f\u59d0\u518d\u8bf4\uff0c\u65e9\u8fdb\u5185\u5ba4\u53bb\u62b1\u4e86\u4e00\u5f20\u53e4\u7434\u51fa\u6765\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u53c8\u6362\u4e86\u4e00\u7089\u9999\u70b9\u8d77\u3002 \u3000\u3000\u82d7\u82e5\u5170\u8f7b\u6292\u7d20\u8155\uff0c\u201c\u4ed9\u7fc1\u3001\u4ed9\u7fc1\u201d\u7684\u8c03\u4e86\u51e0\u58f0\uff0c\u5f39\u5c06\u8d77\u6765\uff0c\u968f\u5373\u629a\u7434\u4f4e\u5531\uff1a\u201c\u6765\u65e5\u5927\u96be\uff0c\u53e3\u71e5\u820c\u5e72\u3002\u4eca\u65e5\u76f8\u4e50\uff0c\u7686\u5f53\u559c\u6b22\u3002 \u3000\u3000\u7ecf\u5386\u540d\u5c71\uff0c\u829d\u8349\u7ffb\u7ffb\u3002\u4ed9\u4eba\u738b\u4e54\uff0c\u5949\u836f\u4e00\u4e38\u3002\u201d\u5531\u5230\u8fd9\u91cc\uff0c\u7434\u58f0\u672a\u6b47\uff0c\u6b4c\u8f9e\u5df2\u7ec8\u3002 \u3000\u3000\u80e1\u6590\u5c11\u5e74\u65f6\u591a\u5386\u82e6\u96be\uff0c\u4e13\u5fc3\u7ec3\u6b66\uff0c\u4e8c\u5341\u4f59\u5c81\u540e\u9887\u66fe\u8bfb\u4e66\uff0c\u542c\u5f97\u61c2\u5979\u5531\u7684\u662f\u4e00\u66f2\u300a\u5584\u54c9\u884c\u300b\uff0c\u90a3\u662f\u53e4\u65f6\u5bb4\u4f1a\u4e2d\u4e3b\u5ba2\u8d60\u7b54\u7684\u6b4c\u8f9e\uff0c\u81ea\u6c49\u9b4f\u4ee5\u6765\uff0c\u5c11\u6709\u4eba\u594f\uff0c\u4e0d\u610f\u4eca\u65e5\u4e0a\u5c71\u62a5\u4ec7\uff0c\u5374\u9047\u4e0a\u8fd9\u4ef6\u9976\u6709\u53e4\u98ce\u4e4b\u4e8b\u3002\u5979\u5531\u7684\u516b\u53e5\u6b4c\u4e2d\uff0c\u524d\u56db\u53e5\u529d\u5ba2\u5c3d\u6b22\u996e\u9152\uff0c\u540e\u56db\u53e5\u9882\u5ba2\u957f\u5bff\u3002\u9002\u624d\u80e1\u6590\u542b\u836f\u89e3\u6bd2\uff0c\u6b4c\u4e2d\u6b63\u597d\u8bf4\u5230\u7075\u829d\u4ed9\u836f\uff0c\u90a3\u53c8\u6709\u53cc\u5173\u4e4b\u610f\u4e86\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u62cd\u51fb\u684c\u5b50\uff0c\u541f\u9053\uff1a\u201c\u81ea\u60dc\u8896\u77ed\uff0c\u5185\u624b\u77e5\u5bd2\u3002\u60ed\u65e0\u7075\u8f84\uff0c\u4ee5\u62a5\u8d75\u5ba3\u3002\u201d\u610f\u601d\u8bf4\u4e3b\u4eba\u6bb7\u52e4\u76f8\u5f85\uff0c\u81ea\u60ed\u6ca1\u4ec0\u4e48\u597d\u4e1c\u897f\u76f8\u62a5\u3002 \u3000\u3000\u82d7\u82e5\u5170\u542c\u4ed6\u4e5f\u4ee5\u300a\u5584\u54c9\u884c\u300b\u4e2d\u7684\u6b4c\u8f9e\u76f8\u7b54\uff0c\u5fc3\u4e0b\u751a\u559c\uff0c\u6697\u9053\uff1a\u201c\u6b64\u4eba\u6587\u6b66\u53cc\u5168\uff0c\u6211\u7239\u7239\u77e5\u9053\u80e1\u4f2f\u4f2f\u6709\u6b64\u540e\u4eba\uff0c\u5fc5\u5b9a\u6b22\u559c\u3002\u201d \u3000\u3000\u5f53\u4e0b\u5531\u9053\uff1a\u201c\u6708\u6ca1\u53c2\u6a2a\uff0c\u5317\u6597\u9611\u5e72\u3002\u4eb2\u4ea4\u5728\u95e8\uff0c\u9965\u4e0d\u53ca\u9910\u3002\u201d\u610f\u601d\u8bf4\u65f6\u5019\u867d\u665a\uff0c\u4f46\u5ba2\u4eba\u5149\u4e34\uff0c\u9ad8\u5174\u5f97\u996d\u4e5f\u6765\u4e0d\u53ca\u5403\u3002 \u3000\u3000\u80e1\u6590\u63a5\u7740\u541f\u9053\uff1a\u201c\u6b22\u65e5\u5c1a\u5c11\uff0c\u621a\u65e5\u82e6\u591a\uff0c\u4ee5\u4f55\u5fd8\u5fe7\uff1f\u5f39\u7b5d\u9152\u6b4c\u3002\u6dee\u5357\u516b\u516c\uff0c\u8981\u9053\u4e0d\u70e6\uff0c\u53c2\u9a7e\u516d\u9f99\uff0c\u6e38\u620f\u4e91\u7aef\u3002\u201d\u6700\u540e\u56db\u53e5\u662f\u795d\u9882\u4e3b\u4eba\u6210\u4ed9\u957f\u5bff\uff0c\u4e0e\u4e3b\u4eba\u9996\u5148\u6240\u5531\u4e4b\u8f9e\u76f8\u5e94\u7b54\u3002 \u3000\u3000\u80e1\u6590\u5531\u7f62\uff0c\u4e3e\u676f\u996e\u5c3d\uff0c\u62f1\u624b\u800c\u7acb\u3002\u82d7\u82e5\u5170\u5212\u5f26\u800c\u6b62\uff0c\u7ad9\u4e86\u8d77\u6765\u3002\u4e24\u4eba\u76f8\u5bf9\u884c\u793c\u3002 \u3000\u3000\u80e1\u6590\u5c06\u9152\u676f\u653e\u5728\u684c\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u4e3b\u4eba\u65e2\u7136\u672a\u5f52\uff0c\u660e\u65e5\u5f53\u518d\u9020\u8bbf\u3002\u201d\u5927\u8e0f\u6b65\u8d70\u5411\u897f\u53a2\u623f\uff0c\u5c06\u5e73\u963f\u56db\u8d1f\u5728\u80cc\u4e0a\uff0c\u5411\u82d7\u82e5\u5170\u5fae\u5fae\u8eac\u8eab\uff0c\u8d70\u51fa\u5927\u5385\u3002\u82d7\u82e5\u5170\u51fa\u95e8\u76f8\u9001\uff0c\u53ea\u89c1\u4ed6\u80cc\u5f71\u5728\u5d16\u8fb9\u4e00\u95ea\uff0c\u62c9\u7740\u7ef3\u7d22\u6e9c\u4e0b\u5c71\u5cf0\u53bb\u4e86\u3002 \u3000\u3000\u5979\u671b\u7740\u6ee1\u5c71\u767d\u96ea\uff0c\u9759\u9759\u51fa\u795e\u3002\u7434\u513f\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u60f3\u4ec0\u4e48\uff1f \u3000\u3000\u5feb\u8fdb\u53bb\u5427\uff0c\u83ab\u7740\u4e86\u51c9\u3002\u201d\u82d7\u82e5\u5170\u9053\uff1a\u201c\u6211\u4e0d\u51b7\u3002\u201d\u5979\u81ea\u5df1\u5fc3\u4e2d\u5176\u5b9e\u4e5f\u4e0d\u77e5\u5230\u5e95\u5728\u60f3\u4ec0\u4e48\u3002\u7434\u513f\u50ac\u4e86\u4e24\u6b21\uff0c\u82d7\u82e5\u5170\u624d\u6162\u6162\u56de\u8fdb\u5e84\u5b50\u3002 \u3000\u3000\u4e00\u8fdb\u5927\u5385\uff0c\u53ea\u89c1\u6ee1\u5385\u90fd\u5750\u6ee1\u4e86\u4eba\uff0c\u4f17\u4eba\u9002\u624d\u8eb2\u5f97\u5f71\u8e2a\u4e0d\u89c1\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u53c8\u4e0d\u77e5\u90fd\u4ece\u4ec0\u4e48\u5730\u65b9\u51fa\u6765\u4e86\u3002\u5404\u4eba\u4e00\u9f50\u7ad9\u8d77\u76f8\u8be2\uff1a\u201c\u4ed6\u8d70\u4e86\u4e48\uff1f\u201d\u201c\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d\u201c\u4ed6\u8bf4\u4ec0\u4e48\u65f6\u5019\u518d\u6765\uff1f\u201d \u3000\u3000\u201c\u4ed6\u4e0a\u5c71\u662f\u6765\u62a5\u4ec7\u4e48\uff1f\u201d\u201c\u4ed6\u8981\u627e\u8c01\uff1f\u201d \u3000\u3000\u82d7\u82e5\u5170\u5fc3\u4e2d\u9119\u89c6\u8fd9\u4e9b\u4eba\u80c6\u602f\uff0c\u5371\u96be\u4e4b\u65f6\u4e2a\u4e2a\u9003\u8d70\uff0c\u7559\u4e0b\u5979\u4e00\u4e2a\u5f31\u5973\u5b50\u62b5\u6321\u5927\u654c\uff0c\u5f53\u4e0b\u6de1\u6de1\u7684\u9053\uff1a\u201c\u4ed6\u4ec0\u4e48\u4e5f\u6ca1\u8bf4\u3002\u201d\u5b9d\u6811\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u3002\u4f60\u5728\u5385\u4e0a\u966a\u4e86\u4ed6\u8fd9\u8bb8\u4e45\uff0c\u603b\u6709\u4e9b\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u672c\u975e\u559c\u7231\u6076\u4f5c\u5267\u4e4b\u4eba\uff0c\u4f46\u8fd9\u65f6\u80f8\u6000\u6b22\u7545\uff0c\u4e00\u9897\u5fc3\u98d8\u98d8\u8361\u8361\u7684\uff0c\u53ea\u60f3\u8ddf\u4eba\u95f9\u7740\u73a9\uff0c\u89c1\u5404\u4eba\u795e\u8272\u53e4\u602a\uff0c\u4fbf\u9053\uff1a\u201c\u90a3\u4f4d\u80e1\u4e16\u5144\u8bf4\u9053\uff0c\u4ed6\u8fd9\u6b21\u4e0a\u5c71\uff0c\u4e3a\u7684\u662f\u62a5\u6740\u7236\u4e4b\u4ec7\uff0c\u53ef\u60dc\u4ec7\u4eba\u8eb2\u4e86\u8d77\u6765\u3002\u73b0\u4e0b\u4ed6\u5b88\u5728\u5c71\u4e0b\uff0c\u5f85\u90a3\u4ec7\u4eba\u4e0b\u53bb\uff0c\u4e0b\u4e00\u4e2a\uff0c\u6740\u4e00\u4e2a\uff1b\u4e0b\u4e24\u4e2a\uff0c\u6740\u4e00\u53cc\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e00\u51db\uff0c\u90fd\u60f3\uff1a\u201c\u5c71\u4e0a\u6ca1\u6709\u7cae\u98df\uff0c\u5c71\u4e0b\u53c8\u5b88\u7740\u8fd9\u4e00\u4e2a\u51f6\u715e\u592a\u5c81\uff0c\u8fd9\u4fbf\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u80e1\u4e16\u5144\u8a00\u9053\uff1a\u5c71\u4e0a\u4f17\u4eba\uff0c\u4e2a\u4e2a\u4e0e\u4ed6\u6709\u4ec7\uff0c\u53ea\u662f\u6709\u7684\u4ec7\u6df1\uff0c\u6709\u7684\u4ec7\u6d45\u3002\u4ed6\u6069\u6028\u5206\u660e\uff0c\u6df1\u8005\u91cd\u62a5\uff0c\u6d45\u8005\u8f7b\u62a5\uff0c\u4e0d\u613f\u9519\u5bb3\u4e86\u597d\u4eba\u3002\u4ed6\u8981\u6211\u4ee3\u8be2\u5404\u4f4d\uff0c\u4e3a\u4f55\u9f50\u6765\u8fd9\u5173\u5916\u82e6\u5bd2\u4e4b\u5730\uff0c\u662f\u5426\u8981\u5408\u529b\u5bb3\u4ed6\uff1f\u201d \u3000\u3000\u9664\u4e86\u5b9d\u6811\u4e4b\u5916\uff0c\u4f59\u4eba\u5f02\u53e3\u540c\u58f0\u7684\u8bf4\u9053\uff1a\u201c\u4e4b\u540d\uff0c\u6211\u4eec\u4ee5\u524d\u4ece\u6765\u6ca1\u542c\u5230\u8fc7\uff0c\u4e0e\u4ed6\u6709\u4ec0\u4e48\u4ec7\u6028\uff1f\u66f4\u52a0\u8bf4\u4e0d\u4e0a\u5408\u529b\u5bb3\u4ed6\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u5411\u9676\u767e\u5c81\u9053\uff1a\u201c\u9676\u4f2f\u4f2f\uff0c\u4f84\u5973\u6709\u4e00\u4e8b\u4e0d\u660e\uff0c\u8981\u60f3\u8bf7\u6559\u3002\u201d\u9676\u767e\u5c81\u9053\uff1a\u201c\u59d1\u5a18\u8bf7\u8bf4\u3002\u201d\u82d7\u82e5\u5170\u9053\uff1a\u201c\u9002\u624d\u90a3\u4f4d\u5e73\u56db\u7237\u8bf4\u9053\uff1a\u80e1\u4e00\u5200\u80e1\u4f2f\u4f2f\u8bf7\u5b9d\u6811\u5927\u5e08\u53bb\u8f6c\u544a\u6211\u7239\u7239\u4e09\u4ef6\u5927\u4e8b\uff0c\u53ef\u662f\u6211\u7239\u7239\u8bf4\u5230\u6b64\u4e8b\u7ecf\u8fc7\u4e4b\u65f6\uff0c\u5374\u4ece\u672a\u63d0\u8d77\u3002\u9676\u4f2f\u4f2f\u66fe\u8bf4\u77e5\u9053\u6b64\u4e2d\u539f\u59d4\uff0c\u4e0d\u77e5\u80fd\u89c1\u544a\u4e48\uff1f\u201d \u3000\u3000\u9676\u767e\u5c81\u9053\uff1a\u201c\u59d1\u5a18\u5373\u4f7f\u4e0d\u95ee\uff0c\u6211\u4e5f\u6b63\u8981\u8bf4\u3002\u201d\u4ed6\u6307\u7740\u962e\u58eb\u4e2d\u3001\u6bb7\u5409\u3001\u66f9\u4e91\u5947\u7b49\u4eba\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8fd9\u51e0\u4f4d\u5929\u9f99\u95e8\u7684\u82f1\u96c4\uff0c\u8bec\u6307\u6211\u513f\u5bb3\u6b7b\u7530\u5f52\u519c\u7530\u4eb2\u5bb6\u3002\u54fc\u54fc\uff01\u201d\u4ed6\u55d3\u95e8\u672c\u5c31\u7c97\u5927\uff0c\u8fd9\u65f6\u5fc3\u4e2d\u6124\u6fc0\uff0c\u66f4\u52a0\u8bf4\u5f97\u54cd\u4e86\uff1a\u201c\u6211\u5c06\u8fd9\u4e8b\u4ece\u5934\u8bf4\u6765\uff0c\u4e14\u8bf7\u5404\u4f4d\u79c9\u516c\u8bc4\u4e2a\u662f\u975e\u66f2\u76f4\u3002\u201d\u6bb7\u5409\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\uff0c\u6211\u4eec\u6b63\u8981\u5411\u9676\u5be8\u4e3b\u8bf7\u6559\u3002\u201d \u3000"}),showOnHomepage:!0},{name:"\u4e03",content:(0,a.jsx)(a.Fragment,{children:"\u9676\u767e\u5c81\u54b3\u55fd\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5728\u5c11\u5e74\u4e4b\u65f6\uff0c\u5c31\u548c\u5f52\u519c\u4e00\u8d77\u505a\u6ca1\u672c\u94b1\u7684\u4e70\u5356\u2026\u2026\u201d \u3000\u3000\u4f17\u4eba\u90fd\u77e5\u4ed6\u8eab\u5728\u7eff\u6797\uff0c\u662f\u996e\u9a6c\u5ddd\u5c71\u5be8\u7684\u5927\u5be8\u4e3b\uff0c\u5374\u4e0d\u77e5\u7530\u5f52\u519c\u4e5f\u66fe\u4e3a\u76d7\uff0c\u5927\u5bb6\u4e92\u671b\u4e86\u4e00\u773c\u3002\u66f9\u4e91\u5947\u53eb\u9053\uff1a\u201c\u653e\u5c41\uff01\u6211\u5e08\u7236\u662f\u6b66\u6797\u8c6a\u6770\uff0c\u4f60\u83ab\u80e1\u8bf4\u516b\u9053\uff0c\u6c61\u4e86\u6211\u5e08\u7236\u7684\u540d\u5934\u3002\u201d \u3000\u3000\u9676\u767e\u5c81\u5389\u58f0\u9053\uff1a\u201c\u4f60\u77a7\u4e0d\u8d77\u9ed1\u9053\u4e0a\u7684\u82f1\u96c4\uff0c\u53ef\u662f\u9ed1\u9053\u4e0a\u7684\u82f1\u96c4\u8fd8\u77a7\u4e0d\u8d77\u4f60\u8fd9\u79cd\u72d7\u718a\u5462\uff01\u6211\u4eec\u5f00\u5c71\u7acb\u67dc\uff0c\u51ed\u4e00\u5200\u4e00\u67aa\u6323\u996d\u5403\uff0c\u6bd4\u4f60\u4eec\u770b\u5bb6\u62a4\u9662\u3001\u4fdd\u9556\u505a\u5b98\uff0c\u53c8\u5dee\u5728\u54ea\u91cc\u4e86\uff1f\u201d \u3000\u3000\u66f9\u4e91\u5947\u7ad9\u8d77\u8eab\u6765\uff0c\u6b32\u5f85\u518d\u8fa9\u3002\u7530\u9752\u6587\u62c9\u62c9\u4ed6\u7684\u8863\u895f\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u522b\u4e89\u5566\uff0c\u4e14\u8ba9\u4ed6\u8bf4\u4e0b\u53bb\u3002\u201d\u66f9\u4e91\u5947\u4e00\u5f20\u8138\u80c0\u5f97\u901a\u7ea2\uff0c\u72e0\u72e0\u77aa\u7740\u9676\u767e\u5c81\uff0c\u7ec8\u4e8e\u5750\u4e0b\u3002 \u3000\u3000\u9676\u767e\u5c81\u5927\u58f0\u9053\uff1a\u201c\u6211\u9676\u767e\u5c81\u81ea\u5e7c\u8eab\u5728\u7eff\u6797\uff0c\u6253\u5bb6\u52ab\u820d\uff0c\u4ece\u6765\u4e0d\u66fe\u9690\u7792\u8fc7\u4e00\u5b57\uff0c\u5927\u4e08\u592b\u6562\u4f5c\u6562\u5f53\uff0c\u53c8\u6015\u4ec0\u4e48\u4e86\uff1f\u201d\u82d7\u82e5\u5170\u542c\u4ed6\u8bf4\u8bdd\u5c94\u4e86\u5f00\u53bb\uff0c\u4e8e\u662f\u9053\uff1a\u201c\u9676\u4f2f\u4f2f\uff0c\u6211\u7239\u7239\u4e5f\u8bf4\uff0c\u7eff\u6797\u4e2d\u5c3d\u6709\u82f1\u96c4\u8c6a\u6770\uff0c\u8c01\u4e5f\u4e0d\u6562\u5c0f\u89d1\u4e86\u3002\u4f60\u8bf7\u8bf4\u7530\u5bb6\u53d4\u7236\u7684\u4e8b\u5427\u3002\u201d \u3000\u3000\u9676\u767e\u5c81\u6307\u7740\u66f9\u4e91\u5947\u7684\u9f3b\u5b50\u9053\uff1a\u201c\u4f60\u542c\uff0c\u82d7\u5927\u4fa0\u4e5f\u8fd9\u4e48\u8bf4\uff0c\u4f60\u72e0\u5f97\u8fc7\u82d7\u5927\u4fa0\u4e48\uff1f\u201d\u66f9\u4e91\u5947\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u5374\u4e0d\u7b54\u8bdd\u3002 \u3000\u3000\u9676\u767e\u5c81\u80f8\u4e2d\u5fff\u6c14\u7565\u6292\uff0c\u9053\uff1a\u201c\u5f52\u519c\u5e74\u8f7b\u65f6\u548c\u6211\u4e00\u8d77\u505a\u8fc7\u8bb8\u591a\u5927\u6848\uff0c\u6211\u4e00\u76f4\u662f\u4ed6\u526f\u624b\u3002\u4ed6\u5230\u6210\u5bb6\u4e4b\u540e\uff0c\u8fd9\u624d\u6d17\u624b\u4e0d\u5e72\u3002\u4ed6\u82e5\u662f\u77a7\u4e0d\u8d77\u9ed1\u9053\u4eba\u7269\uff0c\u5e72\u4e48\u53c8\u80af\u5c06\u72ec\u751f\u5973\u513f\u8bb8\u914d\u7ed9\u6211\u5b69\u513f\uff1f\u4e0d\u8fc7\u8bdd\u53c8\u5f97\u8bf4\u56de\u6765\uff0c\u4ed6\u548c\u6211\u7ed3\u6210\u4eb2\u5bb6\uff0c\u5374\u4e5f\u672a\u5fc5\u5f53\u771f\u5b89\u7740\u4ec0\u4e48\u597d\u5fc3\u3002\u4ed6\u662f\u8981\u5835\u4f4f\u6211\u7684\u53e3\uff0c\u8981\u6211\u9690\u7792\u4e00\u4ef6\u5927\u4e8b\u3002 \u3000\u3000\u201c\u90a3\u65e5\u5f52\u519c\u4e0e\u8303\u5e2e\u4e3b\u5728\u6ca7\u5dde\u622a\u963b\u80e1\u4e00\u5200\u592b\u5987\uff0c\u6211\u8fd8\u662f\u5728\u505a\u5f52\u519c\u7684\u526f\u624b\u3002\u80e1\u4e00\u5200\u5728\u5927\u8f66\u4e2d\u98de\u63b7\u91d1\u94b1\u9556\uff0c\u90a3\u4e9b\u7ed9\u6253\u4e2d\u7a74\u9053\u7684\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u5c31\u662f\u6211\u9676\u767e\u5c81\uff1b\u540e\u6765\u80e1\u592b\u4eba\u5728\u5c4b\u9876\u7528\u767d\u7ee2\u593a\u5200\u63b7\u4eba\uff0c\u90a3\u4e9b\u7ed9\u629b\u4e0b\u5c4b\u9876\u7684\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u5c31\u662f\u6211\u9676\u767e\u5c81\uff1b\u82d7\u4eba\u51e4\u9a82\u4e00\u7fa4\u4eba\u662f\u80c6\u5c0f\u9b3c\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u5c31\u662f\u6211\u9676\u767e\u5c81\u3002\u53ea\u4e0d\u8fc7\u5f53\u5e74\u6211\u6ca1\u7559\u80e1\u5b50\uff0c\u5934\u53d1\u6ca1\u767d\uff0c\u6a21\u6837\u8ddf\u773c\u4e0b\u5168\u7136\u4e0d\u540c\u800c\u5df2\u3002 \u3000\u3000\u201c\u80e1\u4e00\u5200\u592b\u5987\u4e34\u6b7b\u7684\u60c5\u666f\uff0c\u6211\u4e5f\u662f\u5728\u573a\u4eb2\u773c\u76ee\u7779\uff0c\u6b63\u5982\u82d7\u59d1\u5a18\u4e0e\u90a3\u5e73\u963f\u56db\u6240\u8bf4\uff0c\u5b9d\u6811\u8fd9\u548c\u5c1a\u8bf4\u7684\u5374\u662f\u8c0e\u8bdd\u3002\u82d7\u59d1\u5a18\u95ee\u9053\uff1a\u82d7\u5927\u4fa0\u82e5\u77e5\u80e1\u4e00\u5200\u5e76\u975e\u4ed6\u6740\u7236\u4ec7\u4eba\uff0c\u4f55\u4ee5\u4ecd\u53bb\u627e\u4ed6\u6bd4\u6b66\uff1f \u3000\u3000\u5404\u4f4d\u5fc3\u4e2d\u5fc5\u60f3\uff0c\u5b9a\u662f\u5b9d\u6811\u5fc3\u6000\u6076\u610f\uff0c\u6ca1\u5c06\u8fd9\u756a\u8bdd\u544a\u77e5\u82d7\u5927\u4fa0\u4e86\u3002\u201d\u4f17\u4eba\u5fc3\u4e2d\u6b63\u90fd\u5982\u6b64\u60f3\uff0c\u53ea\u662f\u788d\u5f97\u5b9d\u6811\u5728\u5ea7\uff0c\u4e0d\u4fbf\u6709\u6240\u663e\u793a\u3002 \u3000\u3000\u9676\u767e\u5c81\u5374\u6447\u5934\u9053\uff1a\u201c\u9519\u4e86\uff0c\u9519\u4e86\u3002\u60f3\u90a3\u8dcc\u6253\u533b\u751f\u960e\u57fa\u5f53\u65f6\u672c\u9886\u4f4e\u5fae\uff0c\u600e\u6562\u5728\u82d7\u80e1\u4e24\u4f4d\u9762\u524d\u5f04\u9b3c\uff1f\u4ed6\u786e\u662f\u4f9d\u7740\u80e1\u4e00\u5200\u7684\u5631\u5490\uff0c\u53bb\u8bf4\u4e86\u90a3\u4e09\u6869\u5927\u4e8b\uff0c\u53ea\u662f\u82d7\u5927\u4fa0\u5374\u6ca1\u542c\u89c1\u3002\u960e\u57fa\u53bb\u5927\u5c4b\u4e4b\u65f6\uff0c\u82d7\u5927\u4fa0\u6709\u4e8b\u51fa\u5916\uff0c\u4e43\u662f\u7530\u5f52\u519c\u63a5\u89c1\u3002\u4ed6\u4e00\u4e94\u4e00\u5341\u7684\u8bf4\u7ed9\u5f52\u519c\u542c\uff0c\u5f53\u65f6\u6211\u5728\u4e00\u65c1\uff0c\u4e5f\u90fd\u542c\u5230\u4e86\u3002 \u3000\u3000\u201c\u5f52\u519c\u5bf9\u4ed6\u8bf4\u9053\uff1a\u2018\u90fd\u77e5\u9053\u4e86\u3002\u4f60\u56de\u53bb\u5427\uff0c\u6211\u81ea\u4f1a\u8f6c\u544a\u82d7\u5927\u4fa0\uff0c\u4f60\u89c1\u5230\u4ed6\u65f6\u4e0d\u5fc5\u518d\u63d0\u3002\u80e1\u4e00\u5200\u95ee\u8d77\uff0c\u4f60\u53ea\u8bf4\u5df2\u5f53\u9762\u544a\u77e5\u82d7\u5927\u4fa0\u5c31\u662f\u3002\u518d\u53eb\u4ed6\u4e70\u5b9a\u4e09\u53e3\u68fa\u6750\uff0c\u4e24\u53e3\u5927\u7684\uff0c\u4e00\u53e3\u5c0f\u7684\uff0c\u514d\u5f97\u5927\u7237\u4eec\u5230\u5934\u6765\u53c8\u8981\u7834\u8d39\u3002\u2019\u8bf4\u7740\u8d4f\u4e86\u4ed6\u4e09\u5341\u4e24\u94f6\u5b50\u3002\u90a3\u960e\u57fa\u77a7\u5728\u94f6\u5b50\u9762\u4e0a\uff0c\u81ea\u7136\u9075\u4f9d\u3002 \u3000\u3000\u201c\u82d7\u5927\u4fa0\u6240\u4ee5\u518d\u53bb\u627e\u80e1\u4e00\u5200\u6bd4\u6b66\uff0c\u5c31\u56e0\u4e3a\u5f52\u519c\u59cb\u7ec8\u6ca1\u8ddf\u4ed6\u63d0\u8fd9\u4e09\u4ef6\u5927\u4e8b\u3002\u4e3a\u4ec0\u4e48\u4e0d\u63d0\u5462\uff1f\u5404\u4f4d\u5b9a\u7136\u731c\u60f3\uff1a\u7530\u5f52\u519c\u5bf9\u80e1\u4e00\u5200\u5fc3\u6000\u4ec7\u6028\uff0c\u60f3\u501f\u624b\u82d7\u5927\u4fa0\u5c06\u4ed6\u6740\u4e86\u3002\u8fd9\u4e48\u60f3\u561b\uff0c\u53ea\u5bf9\u4e86\u4e00\u534a\u3002\u5f52\u519c\u786e\u662f\u76fc\u80e1\u4e00\u5200\u4e27\u547d\uff0c\u53ef\u662f\u4ed6\u4e5f\u76fc\u501f\u80e1\u4e00\u5200\u4e4b\u624b\uff0c\u5c06\u82d7\u5927\u4fa0\u6740\u4e86\u3002 \u3000\u3000\u201c\u82d7\u5927\u4fa0\u6298\u65ad\u4ed6\u7684\u5f39\u5f13\uff0c\u5bf9\u4ed6\u5f53\u4f17\u8fb1\u9a82\uff0c\u4e1d\u6beb\u4e0d\u7ed9\u4ed6\u8138\u9762\u3002 \u3000\u3000\u6211\u7d20\u77e5\u5f52\u519c\u7684\u6027\u5b50\uff0c\u4ed6\u8981\u5f3a\u597d\u80dc\uff0c\u6700\u4f1a\u8bb0\u6068\u3002\u82d7\u5927\u4fa0\u5982\u6b64\u626b\u4ed6\u9762\u76ae\uff0c\u4ed6\u5fc3\u4e2d\u75db\u6068\u82d7\u5927\u4fa0\uff0c\u53ea\u6709\u6bd4\u6068\u80e1\u4e00\u5200\u66f4\u751a\u3002\u90a3\u65e5\u5f52\u519c\u4ea4\u7ed9\u6211\u4e00\u76d2\u836f\u818f\uff0c\u53eb\u6211\u53bb\u8bbe\u6cd5\u6d82\u5728\u80e1\u4e00\u5200\u4e0e\u82d7\u5927\u4fa0\u6bd4\u6b66\u6240\u7528\u7684\u5200\u5251\u4e4b\u4e0a\u3002\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u8001\u5b9e\u8bf4\u6211\u65e2\u4e0d\u60f3\u505a\uff0c\u4e5f\u4e0d\u6562\u505a\uff0c\u53ef\u53c8\u4e0d\u4fbf\u8fdd\u62d7\uff0c\u4e8e\u662f\u5c31\u4ea4\u7ed9\u4e86\u90a3\u8dcc\u6253\u533b\u751f\u960e\u57fa\uff0c\u8981\u4ed6\u53bb\u5e72\u3002 \u3000\u3000\u201c\u5404\u4f4d\u8bf7\u60f3\uff0c\u80e1\u4e00\u5200\u662f\u4f55\u7b49\u7684\u529f\u592b\uff0c\u82e5\u662f\u4e2d\u4e86\u5bfb\u5e38\u6bd2\u836f\uff0c\u7109\u80fd\u7acb\u65f6\u6bd9\u547d\uff1f\u4ed6\u960e\u57fa\u5f53\u65f6\u53ea\u662f\u4e2a\u4e61\u4e0b\u90ce\u4e2d\uff0c\u54ea\u6709\u4ec0\u4e48\u6c5f\u6e56\u597d\u624b\u96be\u4ee5\u89e3\u6551\u7684\u6bd2\u836f\uff1f\u80e1\u4e00\u5200\u4e2d\u7684\u662f\u4ec0\u4e48\u6bd2\uff1f\u90a3\u5c31\u662f\u5929\u9f99\u95e8\u72ec\u4e00\u65e0\u4e8c\u7684\u79d8\u5236\u6bd2\u836f\u4e86\u3002\u6b66\u6797\u4eba\u7269\u95fb\u540d\u4e27\u80c6\u7684\u8ffd\u547d\u6bd2\u9f99\u9525\uff0c\u5c31\u5168\u4ed7\u8fd9\u6bd2\u836f\u800c\u5f97\u540d\u3002\u540e\u6765\u6211\u53c8\u542c\u8bf4\uff0c\u7530\u5f52\u519c\u8fd9\u76d2\u836f\u818f\u4e4b\u4e2d\uff0c\u8fd8\u6df7\u4e0a\u4e86\u2018\u6bd2\u624b\u836f\u738b\u2019\u7684\u836f\u7269\uff0c\u662f\u4ee5\u89c1\u8840\u5c01\u5589\uff0c\u7aef\u7684\u5389\u5bb3\u65e0\u6bd4\u3002\u201d \u3000\u3000\u4f59\u4eba\u672c\u6765\u5c06\u4fe1\u5c06\u7591\uff0c\u542c\u5230\u8fd9\u91cc\uff0c\u5374\u5df2\u4fe1\u4e86\u516b\u4e5d\u6210\uff0c\u5411\u962e\u58eb\u4e2d\u3001\u66f9\u4e91\u5947\u7b49\u5929\u9f99\u5f1f\u5b50\u671b\u4e86\u51e0\u773c\u3002\u962e\u66f9\u7b49\u5fc3\u4e2d\u607c\u6012\uff0c\u5374\u662f\u4e0d\u4fbf\u53d1\u4f5c\u3002 \u3000\u3000\u9676\u767e\u5c81\u9053\uff1a\u201c\u90a3\u4e00\u65e5\u5929\u9f99\u95e8\u5317\u5b97\u8f6e\u503c\u638c\u7406\u95e8\u6237\u4e4b\u671f\u5c4a\u6ee1\uff0c\u7530\u5f52\u519c\u4e5f\u62e3\u4e86\u8fd9\u65e5\u95ed\u95e8\u5c01\u5251\u3002\u4ed6\u5927\u5f20\u7b75\u5e2d\uff0c\u8bf7\u4e86\u6570\u767e\u4f4d\u6c5f\u6e56\u4e0a\u7684\u6210\u540d\u82f1\u96c4\u3002\u6211\u548c\u4ed6\u662f\u8001\u5144\u5f1f\uff0c\u53c8\u662f\u513f\u5973\u4eb2\u5bb6\uff0c\u81ea\u7136\u65e9\u51e0\u65e5\u5c31\u5df2\u8d76\u5230\uff0c\u52a9\u4ed6\u6599\u7406\u4e00\u5207\u3002\u6309\u7740\u5929\u9f99\u95e8\u7684\u89c4\u77e9\uff0c\u5317\u5b97\u503c\u6ee1\uff0c\u5929\u9f99\u95e8\u7684\u5251\u8c31\uff0c\u5386\u7956\u5b97\u7252\uff0c\u4ee5\u53ca\u8fd9\u53e3\u9547\u95e8\u4e4b\u5b9d\u7684\u5b9d\u5200\uff0c\u90fd\u5f97\u4ea4\u7531\u5357\u5b97\u63a5\u638c\u3002\u6bb7\u5144\uff0c\u6211\u8bf4\u5f97\u4e0d\u9519\u5427\uff1f\u201d\u6bb7\u5409\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u9676\u767e\u5c81\u53c8\u9053\uff1a\u201c\u8fd9\u4f4d\u5a01\u9707\u5929\u5357\u6bb7\u5409\u6bb7\u5927\u8d22\u4e3b\uff0c\u662f\u5929\u9f99\u95e8\u5357\u5b97\u638c\u95e8\uff0c\u4ed6\u4e5f\u662f\u65e9\u51e0\u65e5\u5c31\u5df2\u5230\u4e86\u3002\u7530\u5f52\u519c\u662f\u5426\u5c06\u5251\u8c31\u3001\u5386\u7956\u5b97\u7252\u4e0e\u5b9d\u5200\u6309\u7167\u7956\u8bad\u4ea4\u7ed9\u4f60\uff0c\u8bf7\u6bb7\u5144\u7167\u5b9e\u8bf4\u5427\u3002\u201d \u3000\u3000\u6bb7\u5409\u7ad9\u8d77\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u9676\u5be8\u4e3b\u4e0d\u63d0\uff0c\u5728\u4e0b\u539f\u4e0d\u4fbf\u4e0e\u5916\u4eba\u660e\u8a00\uff0c\u53ef\u662f\u4e2d\u95f4\u5b9e\u6709\u8bb8\u591a\u8df7\u8e4a\u4e4b\u5904\uff0c\u5728\u4e0b\u82e5\u662f\u9690\u7792\u4e0d\u8bf4\uff0c\u8fd9\u7591\u56e2\u603b\u662f\u96be\u4ee5\u6253\u7834\u3002 \u3000\u3000\u201c\u90a3\u65e5\u7530\u5e08\u5144\u5bb4\u5ba2\u4e4b\u540e\uff0c\u9000\u5230\u5185\u5802\uff0c\u6309\u7740\u5386\u6765\u89c4\u77e9\uff0c\u4ed6\u5c31\u5f97\u4f1a\u96c6\u5357\u5317\u4e24\u5b97\u95e8\u4eba\uff0c\u62dc\u8fc7\u95ef\u738b\u3001\u521b\u6d3e\u7956\u5b97\u548c\u5386\u4ee3\u638c\u95e8\u4eba\u7684\u795e\u4f4d\uff0c\u4fbf\u5c06\u5b9d\u5200\u4f20\u4ea4\u5728\u4e0b\u3002\u54ea\u77e5\u4ed6\u8fdb\u4e86\u5185\u5ba4\uff0c\u59cb\u7ec8\u6ca1\u518d\u51fa\u6765\u3002 \u3000\u3000\u201c\u6211\u5fc3\u4e2d\u7126\u6025\uff0c\u76f4\u7b49\u5230\u534a\u591c\uff0c\u5916\u5ba2\u65e9\u5df2\u6563\u5c3d\uff0c\u9752\u6587\u4f84\u5973\u5ffd\u4ece\u5ba4\u5185\u51fa\u6765\u5bf9\u6211\u8bf4\u9053\uff0c\u5979\u7239\u7239\u8eab\u5b50\u4e0d\u9002\uff0c\u6388\u8c31\u4e4b\u4e8b\u5f85\u660e\u65e5\u518d\u884c\u3002 \u3000\u3000\u201c\u6211\u597d\u751f\u5947\u602a\uff0c\u9002\u624d\u7530\u5e08\u5144\u8c22\u5ba2\u656c\u9152\uff0c\u8138\u4e0a\u6ca1\u4e00\u70b9\u75b2\u6001\uff0c\u600e\u4e48\u7a81\u7136\u611f\u5230\u4e0d\u9002\uff1f\u518d\u8bf4\u4f20\u8c31\u6388\u5200\uff0c\u53ea\u662f\u62dc\u4e00\u62dc\u5217\u7956\u5217\u5b97\uff0c\u7247\u523b\u53ef\u4e86\uff0c\u4e00\u5207\u90fd\u5df2\u5c31\u7eea\uff0c\u4f55\u5fc5\u518d\u7b49\u660e\u65e5\uff1f\u83ab\u975e\u7530\u5e08\u5144\u4e0d\u80af\u4ea4\u51fa\u5b9d\u5200\uff0c\u6545\u610f\u62d6\u5ef6\u63a8\u59d4\u4e48\uff1f\u201d \u3000\u3000\u962e\u58eb\u4e2d\u63d2\u53e3\u9053\uff1a\u201c\u6bb7\u5e08\u5144\uff0c\u4f60\u8fd9\u822c\u5984\u81ea\u5fd6\u5ea6\uff0c\u90a3\u5c31\u4e0d\u662f\u4e86\u3002 \u3000\u3000\u90a3\u65e5\u4f60\u82e5\u5355\u662f\u4e3a\u4e86\u53d7\u8c31\u53d7\u5200\u800c\u53bb\uff0c\u7530\u5e08\u54e5\u65e9\u5c31\u4ea4\u4e86\u7ed9\u4f60\u3002\u53ef\u662f\u4f60\u9080\u4e86\u522b\u95e8\u522b\u6d3e\u7684\u8bb8\u591a\u9ad8\u624b\u540c\u6765\uff0c\u663e\u7136\u4e0d\u5b89\u7740\u597d\u5fc3\u3002\u201d\u6bb7\u5409\u51b7\u7b11\u9053\uff1a\u201c\u563f\uff0c\u6211\u80fd\u6709\u4ec0\u4e48\u574f\u5fc3\u773c\u513f\uff1f\u201d\u962e\u58eb\u4e2d\u9053\uff1a\u201c\u4f60\u662f\u60f3\u4e00\u7b49\u62ff\u5230\u8c31\u7252\u5b9d\u5200\uff0c\u5c31\u52d2\u903c\u6211\u4eec\u5357\u5317\u5f52\u5b97\uff0c\u8ba9\u4f60\u505a\u72ec\u4e00\u65e0\u4e8c\u7684\u638c\u95e8\u4eba\u3002\u90a3\u65f6\u7530\u5e08\u54e5\u5df2\u7ecf\u5c01\u5251\uff0c\u4e0d\u80fd\u518d\u51fa\u624b\u8ddf\u4eba\u52a8\u6b66\uff0c\u4f60\u4eba\u591a\u52bf\u4f17\uff0c\u5c82\u4e0d\u662f\u4e3a\u6240\u6b32\u4e3a\u4e48\uff1f\u201d \u3000\u3000\u6bb7\u5409\u8138\u4e0a\u5fae\u5fae\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u5929\u9f99\u95e8\u5206\u4e3a\u5357\u5317\u4e8c\u5b97\uff0c\u539f\u662f\u6743\u5b9c\u4e4b\u8ba1\u3002\u5f53\u5e74\u7530\u5e08\u5144\u521d\u4efb\u5317\u5b97\u638c\u95e8\u4e4b\u65f6\uff0c\u4ed6\u4f55\u5c1d\u4e0d\u60f3\u5f52\u5e76\u5357\u5b97\uff1f\u5c31\u7b97\u5144\u5f1f\u610f\u6b32\u4e24\u5b97\u5408\u4e00\uff0c\u5149\u5927\u6211\u95e8\uff0c\u90a3\u4e5f\u662f\u4e00\u6869\u7f8e\u4e8b\u3002\u8fd9\u603b\u80dc\u4e8e\u962e\u5e08\u5144\u4f60\u9601\u4e0b\u7aed\u529b\u6392\u6324\u4e91\u5947\u3001\u610f\u56fe\u81ea\u4e3a\u638c\u95e8\u5427\uff1f\u201d \u3000\u3000\u4f17\u4eba\u542c\u4ed6\u4eec\u81ea\u63ed\u4e11\u4e8b\uff0c\u539f\u6765\u5404\u6000\u79c1\u6b32\uff0c\u9664\u4e86\u5929\u9f99\u95e8\u4e2d\u4eba\u4e4b\u5916\uff0c\u5927\u5bb6\u7b11\u563b\u563b\u7684\u542c\u7740\uff0c\u5747\u6709\u5e78\u707e\u4e50\u7978\u4e4b\u611f\u3002 \u3000\u3000\u82d7\u82e5\u5170\u5bf9\u8fd9\u4e9b\u6b66\u6797\u4e2d\u95e8\u6237\u5b97\u6d3e\u4e4b\u4e89\u4e0d\u6b32\u591a\u542c\uff0c\u8f7b\u58f0\u95ee\u9053\uff1a\u201c\u540e\u6765\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u6bb7\u5409\u9053\uff1a\u201c\u6211\u56de\u5230\u5bb6\u91cc\uff0c\u4e0e\u6211\u5357\u5b97\u7684\u8bf8\u4f4d\u5e08\u5f1f\u4e00\u5546\u8bae\uff0c\u5927\u5bb6\u90fd\u8bf4\u7530\u5e08\u5144\u5fc5\u6709\u4ed6\u610f\uff0c\u6211\u4eec\u53ef\u4e0d\u80fd\u542c\u51ed\u6b3a\u5f04\uff0c\u4e8e\u662f\u63a8\u6211\u53bb\u63a2\u660e\u771f\u60c5\u3002 \u3000\u3000\u201c\u5f53\u4e0b\u6211\u5230\u7530\u5e08\u5144\u5367\u5ba4\u53bb\u95ee\u5019\u63a2\u75c5\u3002\u9752\u6587\u4f84\u5973\u4e00\u53cc\u773c\u775b\u54ed\u5f97\u7ea2\u7ea2\u7684\uff0c\u62e6\u5728\u95e8\u53e3\uff0c\u8bf4\u9053\uff1a\u2018\u7239\u5df2\u7761\u7740\u5566\u3002\u6bb7\u53d4\u7236\u8bf7\u56de\uff0c\u591a\u8c22\u60a8\u5173\u6000\u3002\u2019\u6211\u89c1\u5979\u795e\u60c5\u6709\u5f02\uff0c\u5fc3\u60f3\u7530\u5e08\u5144\u82e5\u662f\u5f53\u771f\u8eab\u5b50\u6709\u751a\u4e0d\u9002\uff0c\u53c8\u4e0d\u662f\u4ec0\u4e48\u96be\u6cbb\u7684\u91cd\u75c5\uff0c\u5979\u4e5f\u4e0d\u7528\u54ed\u5f97\u8fd9\u4e48\u5389\u5bb3\uff0c\u8fd9\u4e2d\u95f4\u5b9a\u6709\u53e4\u602a\u3002\u5f53\u4e0b\u56de\u623f\u5f85\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u6362\u4e86\u8863\u670d\uff0c\u518d\u5230\u7530\u5e08\u5144\u623f\u5916\u53bb\u63a2\u75c5\u2026\u2026\u201d \u3000\u3000\u962e\u58eb\u4e2d\u4f38\u638c\u5728\u684c\u4e0a\u7528\u529b\u4e00\u62cd\uff0c\u559d\u9053\uff1a\u201c\u563f\uff0c\u63a2\u75c5\uff01\u63a2\u75c5\u662f\u5728\u623f\u5916\u63a2\u7684\u4e48\uff1f\u201d \u3000\u3000\u6bb7\u5409\u51b7\u7b11\u9053\uff1a\u201c\u5c31\u7b97\u662f\u6211\u5077\u542c\uff0c\u5374\u53c8\u600e\u5730\uff1f\u6211\u8eb2\u5728\u7a97\u5916\uff0c\u53ea\u542c\u7530\u5e08\u5144\u9053\uff1a\u2018\u4f60\u4e0d\u7528\u903c\u6211\u3002\u4eca\u65e5\u6211\u95ed\u95e8\u5c01\u5251\uff0c\u5f53\u7740\u6c5f\u6e56\u8c6a\u6770\u4e4b\u9762\uff0c\u5df2\u5c06\u5929\u9f99\u5317\u5b97\u7684\u638c\u95e8\u4eba\u4f20\u7ed9\u4e86\u4e91\u5947\uff0c\u600e\u4e48\u8fd8\u80fd\u66f4\u6539\uff1f \u3000\u3000\u4f60\u903c\u6211\u5c06\u638c\u95e8\u4e4b\u4f4d\u4f20\u7ed9\u4f60\uff0c\u8fd9\u65f6\u5019\u53ef\u5df2\u7ecf\u8fdf\u4e86\u3002\u2019\u53c8\u542c\u8fd9\u4f4d\u962e\u58eb\u4e2d\u962e\u5e08\u5144\u8bf4\u9053\uff1a\u2018\u6211\u600e\u6562\u903c\u8feb\u5e08\u54e5\uff1f\u4f46\u60f3\u4e91\u5947\u4e0e\u9752\u6587\u505a\u51fa\u8fd9\u7b49\u4e8b\u6765\uff0c\u8fde\u5b69\u5b50\u4e5f\u751f\u4e0b\u4e86\u3002\u5982\u6b64\u4f24\u98ce\u8d25\u4fd7\uff0c\u5927\u72af\u6deb\u6212\uff0c\u6211\u95e8\u4e2d\u4e0a\u4e0a\u4e0b\u4e0b\uff0c\u54ea\u4e00\u4e2a\u8fd8\u80fd\u670d\u4ed6\uff1f\u2019\u201d \u3000\u3000\u6bb7\u5409\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u542c\u5f97\u5495\u51ac\u4e00\u58f0\uff0c\u7530\u9752\u6587\u8fde\u4eba\u5e26\u6905\uff0c\u5f80\u540e\u4fbf\u5012\uff0c\u5df2\u6655\u4e86\u8fc7\u53bb\u3002\u9676\u5b50\u5b89\u62d4\u51fa\u5355\u5200\uff0c\u8fce\u9762\u5f80\u66f9\u4e91\u5947\u5934\u9876\u5288\u843d\u3002\u66f9\u4e91\u5947\u624b\u4e2d\u6ca1\u6709\u5175\u5203\uff0c\u53ea\u5f97\u4e3e\u8d77\u6905\u5b50\u62db\u67b6\u3002\u9676\u767e\u5c81\u542c\u5f97\u672a\u8fc7\u95e8\u7684\u5ab3\u5987\u7adf\u505a\u4e0b\u8fd9\u7b49\u4e11\u4e8b\uff0c\u53ea\u607c\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u4e5f\u4e3e\u8d77\u4e00\u5f20\u6905\u5b50\uff0c\u5939\u5934\u5939\u8111\u5f80\u66f9\u4e91\u5947\u5934\u4e0a\u7838\u53bb\u3002 \u3000\u3000\u5929\u9f99\u8bf8\u4eba\u672c\u6765\u9f50\u5fc3\u5bf9\u5916\uff0c\u4f46\u8fd9\u65f6\u4e94\u4eba\u63ed\u7834\u4e86\u8138\uff0c\u7adf\u65e0\u4eba\u8fc7\u53bb\u76f8\u52a9\u66f9\u4e91\u5947\u3002\u556a\u7684\u4e00\u54cd\uff0c\u66f9\u4e91\u5947\u80cc\u5fc3\u4e0a\u5df2\u5403\u9676\u767e\u5c81\u6905\u5b50\u91cd\u91cd\u4e00\u51fb\u3002\u773c\u89c1\u5385\u4e0a\u53c8\u662f\u4e71\u6210\u4e00\u56e2\u3002 \u3000\u3000\u82d7\u82e5\u5170\u53eb\u9053\uff1a\u201c\u5927\u5bb6\u522b\u52a8\u624b\uff0c\u6211\u8bf4\uff0c\u5927\u5bb6\u8bf7\u5750\u4e0b\uff01\u201d\u5979\u8bdd\u58f0\u4e2d\u81ea\u6709\u4e00\u80a1\u5a01\u4e25\u4e4b\u610f\uff0c\u7adf\u662f\u6559\u4eba\u96be\u4ee5\u6297\u62d2\u3002\u9676\u5b50\u5b89\u4e00\u6014\uff0c\u6536\u56de\u5355\u5200\u3002\u9676\u767e\u5c81\u5140\u81ea\u72c2\u6012\uff0c\u6325\u6905\u731b\u51fb\u3002\u9676\u5b50\u5b89\u6293\u4f4f\u7236\u4eb2\u6253\u8fc7\u53bb\u7684\u6905\u5b50\uff0c\u9053\uff1a\u201c\u7239\uff0c\u54b1\u4eec\u522b\u5148\u52a8\u624b\uff0c\u597d\u6559\u8fd9\u91cc\u5404\u4f4d\u8bc4\u4e2a\u662f\u975e\u66f2\u76f4\u3002\u201d\u9676\u767e\u5c81\u542c\u513f\u5b50\u8bf4\u5f97\u6709\u7406\uff0c\u8fd9\u624d\u4f4f\u624b\u3002 \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u7434\u513f\uff0c\u4f60\u6276\u7530\u59d1\u5a18\u5230\u5185\u623f\u53bb\u6b47\u6b47\u3002\u201d\u8fd9\u65f6\u7530\u9752\u6587\u5df2\u6162\u6162\u9192\u8f6c\uff0c\u8138\u8272\u60e8\u767d\uff0c\u4f4e\u4e0b\u5934\u81ea\u884c\u8d70\u5165\u5185\u5802\u3002\u4f17\u4eba\u773c\u671b\u6bb7\u5409\uff0c\u76fc\u4ed6\u7ee7\u7eed\u8bb2\u8ff0\u3002 \u3000\u3000\u6bb7\u5409\u9053\uff1a\u201c\u53ea\u542c\u5f97\u7530\u5e08\u5144\u957f\u53f9\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u2018\u4f5c\u5b7d\uff0c\u4f5c\u5b7d\uff01 \u3000\u3000\u62a5\u5e94\uff0c\u62a5\u5e94\uff01\u2019\u4ed6\u53cd\u6765\u590d\u53bb\uff0c\u4e0d\u4f4f\u53e3\u7684\u8bf4\u2018\u4f5c\u5b7d\uff0c\u62a5\u5e94\u2019\uff0c\u9694\u4e86\u597d\u4e00\u9635\uff0c\u624d\u9053\uff1a\u2018\u6b64\u4e8b\u660e\u5929\u518d\u8bae\uff0c\u4f60\u53bb\u5427\u3002\u53eb\u5b50\u5b89\u6765\uff0c\u6211\u6709\u8bdd\u8ddf\u4ed6\u8bf4\u3002\u2019\u201d \u3000\u3000\u6bb7\u5409\u5411\u9676\u6c0f\u7236\u5b50\u671b\u4e86\u4e00\u773c\uff0c\u7eed\u9053\uff1a\u201c\u962e\u5e08\u5144\u8fd8\u5f85\u4e89\u8fa9\uff0c\u7530\u5e08\u5144\u62cd\u5e8a\u6012\u9053\uff1a\u2018\u4f60\u662f\u4e0d\u662f\u60f3\u903c\u6b7b\u6211\uff1f\u2019\u962e\u5e08\u5144\u8fd9\u624d\u6ca1\u6709\u8bdd\u8bf4\uff0c\u63a8\u95e8\u8d70\u51fa\u3002\u6211\u542c\u4ed6\u4eec\u8bf4\u7684\u662f\u81ea\u5df1\u5bb6\u4e2d\u4e11\u4e8b\uff0c\u5012\u8ddf\u6211\u5357\u5b97\u65e0\u5173\uff0c\u53c8\u6015\u962e\u5e08\u5144\u51fa\u6765\u649e\u89c1\uff0c\u5927\u5bb6\u8138\u4e0a\u987b\u4e0d\u597d\u770b\uff0c\u5f53\u4e0b\u62a2\u5148\u56de\u5230\u81ea\u5df1\u623f\u4e2d\u3002\u201d \u3000\u3000\u962e\u58eb\u4e2d\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u665a\u6211\u548c\u7530\u5e08\u54e5\u8bf4\u4e86\u8bdd\u51fa\u6765\uff0c\u773c\u89c1\u9ed1\u5f71\u4e00\u95ea\uff0c\u559d\u95ee\uff1a\u2018\u54ea\u4e2a\u72d7\u6742\u79cd\u5728\u6b64\u5077\u542c\uff1f\u2019\u5f53\u65f6\u6ca1\u4eba\u7b54\u8bdd\uff0c\u6211\u53ea\u9053\u5f53\u771f\u662f\u72d7\u6742\u79cd\uff0c\u539f\u6765\u5374\u662f\u6bb7\u5e08\u5144\uff0c\u8fd9\u53ef\u5f97\u7f6a\u4e86\u3002\u201d\u8bf4\u7740\u5411\u6bb7\u5409\u4e00\u63d6\u3002\u4ed6\u660e\u662f\u966a\u7f6a\uff0c\u5b9e\u662f\u9a82\u4eba\u3002\u6bb7\u5409\u8138\u8272\u5fae\u53d8\uff0c\u4f46\u4ed6\u6db5\u517b\u529f\u592b\u751a\u597d\uff0c\u56de\u4e86\u4e00\u793c\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u8005\u4e0d\u7f6a\uff0c\u597d\u8bf4\u597d\u8bf4\u3002\u201d \u3000\u3000\u9676\u5b50\u5b89\u9053\uff1a\u201c\u597d\uff0c\u73b0\u4e0b\u8f6e\u5230\u6211\u6765\u8bf4\u5566\u3002\u65e2\u7136\u5927\u5bb6\u6495\u7834\u4e86\u8138\uff0c\u6211\u2026\u2026\u6211\u4e5f\u4e0d\u5fc5\u518d\u9690\u7792\u4ec0\u4e48\u3002\u6211\u2026\u2026\u6211\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5589\u5934\u54fd\u54bd\uff0c\u5fc3\u60c5\u6fc0\u52a8\uff0c\u7adf\u7136\u8bf4\u4e0d\u4e0b\u53bb\uff0c\u4e24\u9053\u6cea\u6c34\u5374\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u4ed6\u8fd9\u6837\u4e00\u4e2a\u6c14\u5b87\u8f69\u6602\u7684\u5c11\u5e74\u82f1\u96c4\u7adf\u5728\u4eba\u524d\u793a\u5f31\uff0c\u4e0d\u514d\u90fd\u6709\u4e9b\u4e0d\u5fcd\u4e4b\u610f\uff0c\u4e8e\u662f\u5c04\u5411\u66f9\u4e91\u5947\u7684\u76ee\u5149\u4e4b\u4e2d\uff0c\u81ea\u4ea6\u542b\u7740\u51e0\u5206\u6c14\u6124\uff0c\u51e0\u5206\u602a\u8d23\u3002\u9676\u767e\u5c81\u559d\u9053\uff1a\u201c\u8fd9\u822c\u4e0d\u4e89\u6c14\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u5927\u4e08\u592b\u96be\u4fdd\u59bb\u8d24\u5b50\u5b5d\u3002\u597d\u5728\u8fd9\u5ab3\u5987\u8fd8\u6ca1\u8fc7\u95e8\uff0c\u73b7\u8fb1\u4e0d\u5230\u6211\u9676\u5bb6\u7684\u95e8\u6963\u3002\u201d \u3000\u3000\u9676\u5b50\u5b89\u4f38\u8896\u64e6\u4e86\u773c\u6cea\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u4ee5\u524d\u6bcf\u6b21\u6211\u5230\u7530\u5bb6\u2026\u2026\u7530\u4f2f\u7236\u5bb6\u4e2d\u2026\u2026\u201d \u3000\u3000\u66f9\u4e91\u5947\u542c\u4ed6\u7a0d\u4e00\u8fdf\u7591\uff0c\u5bf9\u7530\u5f52\u519c\u7adf\u6539\u53e3\u79f0\u4e3a\u201c\u4f2f\u7236\u201d\uff0c\u4e0d\u518d\u79f0\u4ed6\u201c\u5cb3\u7236\u201d\uff0c\u5fc3\u4e2d\u6697\u559c\uff1a\u201c\u54fc\uff0c\u8fd9\u5c0f\u5b50\u607c\u4e86\uff0c\u4e0d\u8ba4\u9752\u59b9\u4e3a\u59bb\uff0c\u6211\u6b63\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u201d \u3000\u3000\u53ea\u542c\u4ed6\u7eed\u9053\uff1a\u201c\u9752\u59b9\u5728\u6709\u4eba\u5904\u603b\u662f\u7ea2\u7740\u8138\u907f\u5f00\uff0c\u4e0d\u8ddf\u6211\u8bf4\u8bdd\uff0c\u53ef\u662f\u80cc\u7740\u5728\u6ca1\u4eba\u7684\u5730\u65b9\uff0c\u54b1\u4fe9\u603b\u8981\u4eb2\u4eb2\u70ed\u70ed\u7684\u8bf4\u4e00\u9635\u5b50\u8bdd\u3002\u6211\u6bcf\u6b21\u5e26\u4e9b\u73a9\u610f\u513f\u7ed9\u5979\uff0c\u5979\u4e5f\u603b\u6709\u7269\u4e8b\u7ed9\u6211\uff0c\u7ee3\u4e2a\u8377\u5305\u5566\u3001\u505a\u4ef6\u9a6c\u7532\u5566\uff0c\u4ece\u6765\u5c31\u77ed\u4e0d\u4e86\u2026\u2026\u201d \u3000\u3000\u66f9\u4e91\u5947\u8138\u8272\u6e10\u6e10\u96be\u770b\uff0c\u5fc3\u9053\uff1a\u201c\u54fc\uff0c\u8fd8\u6709\u8fd9\u95e8\u5b50\u4e8b\uff0c\u5012\u7792\u5f97\u6211\u597d\u82e6\u3002\u201d \u3000\u3000\u9676\u5b50\u5b89\u7eed\u9053\uff1a\u201c\u8fd9\u6b21\u7530\u4f2f\u7236\u95ed\u95e8\u5c01\u5251\uff0c\u6211\u968f\u5bb6\u7236\u5174\u5174\u5934\u5934\u7684\u8d76\u53bb\uff0c\u4e00\u89c1\u9752\u59b9\uff0c\u5c31\u89c9\u5f97\u5979\u5bb9\u989c\u6194\u60b4\uff0c\u597d\u4f3c\u751f\u8fc7\u4e86\u4e00\u573a\u5927\u75c5\u3002\u6211\u5fc3\u4e2d\u601c\u60dc\uff0c\u80cc\u7740\u4eba\u5b89\u6170\uff0c\u95ee\u5979\u662f\u4e0d\u662f\u751f\u4e86\u4ec0\u4e48\u75c5\u3002\u5979\u521d\u65f6\u652f\u652f\u543e\u543e\uff0c\u6211\u5bfb\u6839\u7a76\u5e95\u7ec6\u95ee\uff0c\u5979\u5374\u53d1\u8d77\u6012\u6765\uff0c\u62a2\u767d\u4e86\u6211\u51e0\u53e5\uff0c\u4ece\u6b64\u4e0d\u518d\u7406\u6211\u3002 \u3000\u3000\u201c\u6211\u7ed9\u5979\u9a82\u5f97\u80e1\u6d82\u5566\uff0c\u53ea\u6709\u81ea\u4e2a\u513f\u7eb3\u95f7\u3002\u90a3\u65e5\u9152\u5bb4\u5b8c\u4e86\uff0c\u6211\u5728\u540e\u82b1\u56ed\u51c9\u4ead\u4e2d\u649e\u89c1\u4e86\u5979\uff0c\u53ea\u89c1\u5979\u4e00\u53cc\u773c\u54ed\u5f97\u7ea2\u7ea2\u7684\uff0c\u6211\u4e0d\u7ba1\u4ec0\u4e48\uff0c\u5c31\u5411\u5979\u966a\u4e0d\u662f\uff0c\u8bf4\u9053\uff1a\u2018\u9752\u59b9\uff0c\u90fd\u662f\u6211\u4e0d\u597d\uff0c\u4f60\u5c31\u522b\u751f\u6c14\u5566\u3002\u2019\u54ea\u77e5\u5979\u8138\u4e00\u6c89\uff0c\u53d1\u4f5c\u9053\uff1a\u2018\u54fc\uff0c\u5f53\u771f\u662f\u4f60\u4e0d\u597d\uff0c\u90a3\u4e5f\u7f62\u4e86\uff01\u504f\u751f\u662f\u522b\u4eba\u4e0d\u597d\uff0c\u6211\u8fd8\u662f\u6b7b\u4e86\u7684\u5e72\u51c0\u3002\u2019\u6211\u66f4\u52a0\u6478\u4e0d\u7740\u5934\u8111\uff0c\u518d\u8ffd\u95ee\u51e0\u53e5\uff0c\u5979\u5934\u4e00\u6487\u5c31\u8d70\u4e86\u3002 \u3000\u3000\u201c\u6211\u56de\u623f\u7761\u4e86\u4e00\u4f1a\uff0c\u8d8a\u60f3\u8d8a\u662f\u4e0d\u5b89\uff0c\u5b9e\u5728\u4e0d\u660e\u767d\u4ec0\u4e48\u5730\u65b9\u5f97\u7f6a\u4e86\u5979\uff0c\u4e8e\u662f\u6084\u6084\u8d77\u6765\uff0c\u8d70\u5230\u5979\u7684\u623f\u5916\uff0c\u5728\u7a97\u4e0a\u8f7b\u8f7b\u5f39\u4e86\u4e09\u5f39\u3002\u5f80\u65e5\u6211\u4eec\u76f8\u7ea6\u51fa\u6765\u4f1a\u9762\uff0c\u603b\u7528\u8fd9\u4e09\u5f39\u6307\u7684\u8bb0\u53f7\u3002\u54ea\u77e5\u8fd9\u665a\u6211\u8fde\u5f39\u4e86\u51e0\u6b21\uff0c\u623f\u4e2d\u7adf\u662f\u6ca1\u534a\u70b9\u52a8\u9759\u3002 \u3000\u3000\u201c\u9694\u4e86\u534a\u664c\uff0c\u6211\u53c8\u8f7b\u5f39\u4e09\u4e0b\uff0c\u4ecd\u662f\u6ca1\u542c\u5230\u58f0\u606f\u3002\u6211\u5947\u602a\u8d77\u6765\uff0c\u5728\u7a97\u683c\u5b50\u4e0a\u4e00\u63a8\uff0c\u90a3\u7a97\u5b50\u5e76\u6ca1\u95e9\u4f4f\uff0c\u5e94\u624b\u800c\u5f00\uff0c\u623f\u4e2d\u9ed1\u6f06\u6f06\u7684\uff0c\u6ca1\u77a7\u89c1\u4ec0\u4e48\u3002\u6211\u6025\u4e8e\u8981\u8ddf\u5979\u8bf4\u8bdd\uff0c\u5c31\u4ece\u7a97\u5b50\u8df3\u4e86\u8fdb\u53bb\u2026\u2026\u201d \u3000\u3000\u66f9\u4e91\u5947\u542c\u5230\u6b64\u5904\uff0c\u6ee1\u8154\u918b\u610f\u4ece\u80f8\u53e3\u76f4\u51b2\u4e0a\u6765\uff0c\u518d\u4e5f\u4e0d\u53ef\u6291\u5236\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u534a\u591c\u4e09\u66f4\u7684\uff0c\u5077\u5165\u4eba\u5bb6\u95fa\u623f\uff0c\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9676\u5b50\u5b89\u6b63\u6b32\u53cd\u5507\u76f8\u7a3d\uff0c\u82d7\u82e5\u5170\u7684\u4f8d\u5a62\u5feb\u5634\u7434\u513f\u5374\u62a2\u7740\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u672a\u5a5a\u592b\u5987\uff0c\u4f60\u53c8\u7ba1\u5f97\u7740\u4e48\uff1f\u201d \u3000\u3000\u9676\u5b50\u5b89\u5411\u7434\u513f\u5fae\u4e00\u70b9\u5934\uff0c\u8c22\u5979\u76f8\u5e2e\uff0c\u63a5\u7740\u9053\uff1a\u201c\u6211\u8d70\u5230\u5979\u5e8a\u8fb9\uff0c\u9690\u7ea6\u89c1\u5e8a\u524d\u653e\u7740\u4e00\u5bf9\u978b\u5b50\uff0c\u5f53\u4e0b\u5927\u7740\u80c6\u5b50\uff0c\u63ed\u5f00\u7f57\u5e10\uff0c\u4f38\u624b\u5230\u88ab\u4e0b\u4e00\u6478\u2026\u2026\u201d \u3000\u3000\u66f9\u4e91\u5947\u7d2b\u80c0\u4e86\u8138\uff0c\u5f85\u6b32\u559d\u9a82\uff0c\u5374\u89c1\u7434\u513f\u6012\u89c6\u7740\u81ea\u5df1\uff0c\u8bdd\u5230\u53e3\u5934\uff0c\u53c8\u7f29\u4e86\u56de\u53bb\u3002\u53ea\u542c\u9676\u5b50\u5b89\u7eed\u9053\uff1a\u201c\u2026\u2026\u89e6\u624b\u5904\u4f3c\u4e4e\u662f\u4e00\u4e2a\u5305\u88b1\uff0c\u9752\u59b9\u5374\u4e0d\u5728\u5e8a\u4e0a\u3002\u6211\u66f4\u662f\u5947\u602a\uff0c\u6478\u4e00\u6478\u90a3\u662f\u4ec0\u4e48\u5305\u88b1\uff0c\u624b\u4e0a\u4e00\u51c9\uff0c\u4f3c\u4e4e\u662f\u4e2a\u5a74\u513f\uff0c\u53ef\u628a\u6211\u5413\u4e86\u4e00\u5927\u8df3\u3002\u518d\u4ed4\u7ec6\u4e00\u6478\uff0c\u5374\u4e0d\u662f\u5a74\u513f\u662f\u4ec0\u4e48\uff1f\u53ea\u662f\u5168\u8eab\u51b0\u51c9\uff0c\u65e9\u5df2\u6b7b\u53bb\u591a\u65f6\uff0c\u770b\u6765\u662f\u628a\u68c9\u88ab\u538b\u5728\u5b69\u5b50\u8eab\u4e0a\u5c06\u4ed6\u95f7\u6b7b\u7684\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u545b\u5577\u4e00\u54cd\uff0c\u82d7\u82e5\u5170\u5931\u624b\u5c06\u8336\u7897\u6454\u5728\u5730\u4e0b\uff0c\u8138\u8272\u82cd\u767d\uff0c\u5634\u5507\u5fae\u5fae\u53d1\u98a4\u3002 \u3000\u3000\u9676\u5b50\u5b89\u9053\uff1a\u201c\u5404\u4f4d\u4eca\u65e5\u542c\u7740\u89c9\u5f97\u53ef\u6015\uff0c\u5f53\u65e5\u6211\u9ed1\u6697\u4e4b\u4e2d\u4eb2\u624b\u6478\u5230\uff0c\u66f4\u662f\u60ca\u9a87\u65e0\u6bd4\uff0c\u9669\u4e9b\u513f\u53eb\u51fa\u58f0\u6765\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u623f\u5916\u811a\u6b65\u58f0\u54cd\uff0c\u6709\u4eba\u8fdb\u6765\uff0c\u6211\u5fd9\u5f80\u5e8a\u5e95\u4e0b\u4e00\u94bb\u3002\u53ea\u542c\u90a3\u4eba\u8d70\u5230\u5e8a\u8fb9\uff0c\u5750\u5728\u5e8a\u6cbf\uff0c\u5624\u5624\u555c\u6ce3\uff0c\u539f\u6765\u5c31\u662f\u9752\u59b9\u3002\u5979\u628a\u6b7b\u5b69\u5b50\u62b1\u5728\u624b\u91cc\uff0c\u4e0d\u4f4f\u4eb2\u4ed6\uff0c\u4f4e\u58f0\u9053\uff1a\u2018\u513f\u554a\uff0c\u4f60\u83ab\u602a\u5a18\u4eb2\u624b\u5bb3\u4e86\u4f60\u7684\u5c0f\u547d\uff0c\u5a18\u5fc3\u91cc\u53ef\u6bd4\u5200\u5272\u8fd8\u8981\u75db\u54ea\u3002\u53ea\u662f\u4f60\u82e5\u6d3b\u7740\uff0c\u5a18\u53ef\u6d3b\u4e0d\u6210\u5566\u3002\u5a18\u771f\u72e0\u5fc3\uff0c\u5bf9\u4e0d\u8d77\u4f60\u3002\u2019\u201c\u6211\u5728\u5e8a\u4e0b\u53ea\u542c\u5f97\u6bdb\u9aa8\u609a\u7136\uff0c\u8fd9\u624d\u660e\u767d\uff0c\u539f\u6765\u5979\u4e0d\u77e5\u8ddf\u54ea\u4e2a\u72d7\u8d3c\u79c1\u901a\uff0c\u751f\u4e0b\u4e86\u5b69\u513f\uff0c\u7adf\u4e0b\u6bd2\u624b\u5c06\u5b69\u513f\u5bb3\u6b7b\u3002\u5979\u62b1\u7740\u6b7b\u5a74\u54ed\u4e00\u9635\uff0c\u4eb2\u4e00\u9635\uff0c\u7ec8\u4e8e\u7ad9\u8d77\u8eab\u6765\uff0c\u62ab\u4e0a\u4e00\u4ef6\u62ab\u98ce\uff0c\u5c06\u5a74\u513f\u7f69\u4f4f\uff0c\u8d70\u51fa\u623f\u53bb\u3002\u6211\u5f85\u5979\u8d70\u51fa\u623f\u95e8\uff0c\u624d\u4ece\u5e8a\u4e0b\u51fa\u6765\uff0c\u6084\u6084\u8ddf\u5728\u5979\u540e\u9762\u3002\u90a3\u65f6\u6211\u5fc3\u91cc\u53c8\u60b2\u53c8\u6124\uff0c\u8981\u67e5\u51fa\u8ddf\u5979\u79c1\u901a\u7684\u90a3\u72d7\u8d3c\u662f\u8c01\u3002 \u3000\u3000\u201c\u53ea\u89c1\u5979\u8d70\u5230\u540e\u56ed\uff0c\u5728\u5899\u8fb9\u62ff\u4e86\u4e00\u628a\u77ed\u94f2\uff0c\u8d8a\u5899\u800c\u51fa\uff0c\u6211\u4e00\u8def\u8fdc\u8fdc\u6387\u7740\uff0c\u89c1\u5979\u8d70\u4e86\u534a\u91cc\u591a\u8def\uff0c\u5230\u4e86\u4e00\u5904\u575f\u573a\u3002\u5979\u62ff\u8d77\u77ed\u94f2\uff0c\u6b63\u8981\u6398\u5730\u63a9\u57cb\uff0c\u5ffd\u7136\u6570\u4e08\u5916\u4f20\u6765\u94c1\u5668\u4e0e\u571f\u77f3\u76f8\u51fb\u4e4b\u58f0\uff0c\u6df1\u591c\u4e4b\u4e2d\uff0c\u7adf\u7136\u53e6\u5916\u4e5f\u6709\u4eba\u5728\u6398\u5730\u3002\u5979\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u5fd9\u8e72\u4e0b\u8eab\u5b50\uff0c\u8fc7\u4e86\u597d\u4e00\u9635\uff0c\u5f2f\u7740\u8170\u6162\u6162\u722c\u8fc7\u53bb\u5bdf\u770b\u3002\u6211\u60f3\u5fc5\u662f\u76d7\u5893\u8d3c\u5728\u6398\u575f\uff0c\u5f53\u4e0b\u4e5f\u8ddf\u7740\u8fc7\u53bb\u3002\u53ea\u89c1\u575f\u65c1\u4e00\u76cf\u706f\u7b3c\u53d1\u7740\u6de1\u6de1\u9ec4\u5149\uff0c\u7167\u7740\u4e00\u4e2a\u9ed1\u5f71\u6b63\u5728\u6398\u5730\u3002 \u3000\u3000\u201c\u6211\u51dd\u76ee\u4e00\u77a7\uff0c\u8fd9\u4eba\u5374\u4e0d\u662f\u6398\u575f\uff0c\u662f\u5728\u575f\u65c1\u6316\u4e2a\u571f\u5751\uff0c\u4e5f\u5728\u63a9\u57cb\u4ec0\u4e48\u3002\u6211\u5fc3\u9053\uff1a\u2018\u8fd9\u53ef\u5947\u4e86\uff0c\u96be\u9053\u53c8\u6709\u8c01\u5728\u57cb\u79c1\u751f\u513f\uff1f\u2019\u4f46\u89c1\u90a3\u4eba\u6398\u4e86\u4e00\u9635\uff0c\u4ece\u5730\u4e0b\u6367\u8d77\u4e00\u4e2a\u957f\u957f\u7684\u5305\u88f9\uff0c\u679c\u771f\u4e0e\u4e00\u4e2a\u5a74\u513f\u5c38\u8eab\u76f8\u4f3c\u3002\u90a3\u4eba\u5c06\u5305\u88f9\u653e\u5165\u5751\u4e2d\uff0c\u94f2\u571f\u76d6\u571f\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u706b\u5149\u4e0b\u770b\u5f97\u660e\u767d\uff0c\u539f\u6765\u6b64\u4eba\u975e\u522b\uff0c\u5374\u662f\u8fd9\u4f4d\u5468\u4e91\u9633\u5468\u5e08\u5144\u3002\u201d \u3000\u3000\u5468\u4e91\u9633\u8138\u4e0a\u672c\u6765\u5c31\u65e0\u8840\u8272\uff0c\u542c\u9676\u5b50\u5b89\u8bf4\u5230\u8fd9\u91cc\uff0c\u66f4\u662f\u82cd\u767d\u3002 \u3000\u3000\u9676\u5b50\u5b89\u63a5\u7740\u9053\uff1a\u201c\u5f53\u65f6\u6211\u5fc3\u4e0b\u7591\u4e91\u5927\u8d77\uff1a\u2018\u96be\u9053\u4e0e\u9752\u59b9\u79c1\u901a\u7684\u7adf\u662f\u8fd9\u755c\u751f\uff1f\u600e\u4e48\u4ed6\u4e5f\u6765\u63a9\u57cb\u4e00\u4e2a\u6b7b\u5a74\uff1f\u2019\u9752\u59b9\u4e00\u89c1\u662f\u4ed6\uff0c\u8eab\u5b50\u4f0f\u5f97\u66f4\u4f4e\uff0c\u7adf\u4e0d\u51fa\u6765\u4e0e\u4ed6\u76f8\u4f1a\u3002\u5468\u5e08\u5144\u5c06\u571f\u8e0f\u5b9e\uff0c\u53c8\u94f2\u4e9b\u9752\u8349\u94fa\u5728\u4e0a\u9762\uff0c\u518d\u5728\u8349\u4e0a\u5806\u4e86\u597d\u591a\u4e71\u77f3\uff0c\u6559\u4eba\u5206\u8fa8\u4e0d\u51fa\uff0c\u8fd9\u624d\u8d70\u5f00\u3002 \u3000\u3000\u201c\u5468\u5e08\u5144\u4e00\u8d70\u8fdc\uff0c\u9752\u59b9\u5fd9\u6398\u4e86\u4e00\u5751\uff0c\u5c06\u6b7b\u5a74\u57cb\u4e0b\uff0c\u968f\u5373\u642c\u5f00\u5468\u5e08\u5144\u6240\u653e\u7684\u4e71\u77f3\uff0c\u8981\u6316\u6398\u51fa\u6765\uff0c\u77a7\u4ed6\u57cb\u7684\u662f\u4ec0\u4e48\u7269\u4e8b\u3002\u6211\u5fc3\u60f3\uff1a\u2018\u5c31\u7b97\u4f60\u4e0d\u52a8\u624b\uff0c\u6211\u4e5f\u8981\u6398\uff0c\u73b0\u4e0b\u5012\u7701\u4e86\u6211\u4e00\u756a\u624b\u811a\u3002\u2019\u9752\u59b9\u4e3e\u8d77\u94c1\u94f2\u521a\u6398\u5f97\u51e0\u4e0b\uff0c\u5468\u5e08\u5144\u7a81\u7136\u4ece\u575f\u540e\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u2018\u9752\u6587\u59b9\u5b50\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u2019\u539f\u6765\u4ed6\u5fc3\u601d\u4e5f\u771f\u5468\u5bc6\uff0c\u57cb\u4e0b\u4e4b\u540e\u5047\u88c5\u8d70\u5f00\uff0c\u8fc7\u4e00\u4f1a\u5374\u53c8\u56de\u6765\u5bdf\u770b\u3002\u9752\u59b9\u5413\u4e86\u4e00\u8df3\uff0c\u4e00\u677e\u624b\uff0c\u94c1\u94f2\u843d\u5728\u5730\u4e0b\uff0c\u65e0\u8bdd\u53ef\u8bf4\u3002 \u3000\u3000\u201c\u5468\u5e08\u5144\u51b7\u51b7\u7684\u9053\uff1a\u2018\u9752\u6587\u59b9\u5b50\uff0c\u4f60\u77e5\u9053\u6211\u57cb\u4ec0\u4e48\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u57cb\u4ec0\u4e48\u3002\u8981\u7792\u5462\uff0c\u5927\u5bb6\u90fd\u7792\uff1b\u8981\u63ed\u5f00\u5462\uff0c\u5927\u5bb6\u90fd\u63ed\u5f00\u3002\u2019\u9752\u59b9\u9053\uff1a\u2018\u597d\uff0c\u90a3\u4e48\u4f60\u8d77\u4e2a\u8a93\u3002\u2019\u5468\u5e08\u5144\u5f53\u5373\u8d77\u4e2a\u6bd2\u8a93\uff0c\u9752\u59b9\u8ddf\u7740\u4ed6\u4e5f\u8d77\u4e86\u8a93\u3002\u4e24\u4eba\u7ea6\u5b9a\u4e86\u4e92\u76f8\u9690\u7792\uff0c\u4e00\u9f50\u56de\u8fdb\u5e84\u53bb\u3002 \u3000\u3000\u201c\u6211\u77a7\u4e24\u4eba\u795e\u60c5\uff0c\u4f3c\u4e4e\u6709\u4ec0\u4e48\u79c1\u60c5\uff0c\u4f46\u53c8\u6709\u70b9\u4e0d\u50cf\uff0c\u770b\u6765\u9752\u59b9\u90a3\u5b69\u5b50\u4e0d\u4f1a\u662f\u8ddf\u5468\u5e08\u5144\u751f\u7684\uff0c\u5f53\u4e0b\u6084\u6084\u8ddf\u5728\u540e\u9762\uff0c\u624b\u91cc\u6263\u4e86\u5582\u6bd2\u7684\u6697\u5668\uff0c\u53ea\u8981\u4e24\u4eba\u6709\u4e1d\u6beb\u4eb2\u6635\u7684\u795e\u6001\uff0c\u6709\u534a\u53e5\u6559\u4eba\u542c\u4e0d\u5165\u8033\u7684\u8bf4\u8bdd\uff0c\u6211\u7acb\u65f6\u5c06\u4ed6\u6bd9\u4e86\u3002 \u3000\u3000\u201c\u603b\u7b97\u4ed6\u8fd0\u6c14\u597d\uff0c\u4e24\u4eba\u4ece\u575f\u573a\u56de\u8fdb\u5e84\u5b50\uff0c\u59cb\u7ec8\u79bb\u5f97\u8fdc\u8fdc\u7684\uff0c\u4e00\u53e5\u8bdd\u4e5f\u6ca1\u8bf4\u3002 \u3000\u3000\u201c\u9752\u59b9\u56de\u5230\u81ea\u5df1\u623f\u91cc\uff0c\u4e0d\u65ad\u62bd\u62bd\u564e\u564e\u7684\u4f4e\u58f0\u54ed\u6ce3\u3002\u6211\u7ad9\u5728\u5979\u7684\u7a97\u4e0b\uff0c\u601d\u524d\u60f3\u540e\uff0c\u4ec0\u4e48\u90fd\u60f3\u5230\u4e86\u3002\u6211\u60f3\u95ef\u8fdb\u53bb\u4e00\u5200\u5c06\u5979\u5288\u6b7b\uff0c\u60f3\u653e\u628a\u706b\u5c06\u7530\u5bb6\u5e84\u70e7\u6210\u767d\u5730\uff0c\u60f3\u628a\u5979\u7684\u4e11\u4e8b\u6296\u5c06\u51fa\u6765\u8ba9\u4eba\u4eba\u77e5\u9053\uff0c\u53ef\u53c8\u60f3\u62b1\u7740\u5979\u5927\u54ed\u4e00\u573a\u3002\u7ec8\u4e8e\u6253\u5b9a\u4e86\u4e3b\u610f\uff1a\u2018\u773c\u4e0b\u987b\u5f97\u4e0d\u52a8\u58f0\u8272\uff0c\u4e14\u5f85\u67e5\u660e\u5978\u592b\u662f\u8c01\u518d\u8bf4\u3002\u2019\u201c\u6211\u5168\u8eab\u51b0\u51b7\uff0c\u56de\u5230\u623f\u4e2d\uff0c\u7239\u7239\u5140\u81ea\u597d\u7761\uff0c\u6211\u5374\u72ec\u4e2a\u513f\u7ad9\u7740\u53d1\u5446\u3002\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u5ffd\u7136\u962e\u5e08\u53d4\u6765\u53eb\u6211\uff0c\u8bf4\u7530\u4f2f\u7236\u6709\u8bdd\u8ddf\u6211\u8bf4\u3002\u6211\u5fc3\u9053\uff1a\u2018\u8fd9\u8bdd\u513f\u6765\u4e86\uff0c\u4e14\u77a7\u4ed6\u600e\u751f\u8bf4\uff1f\u662f\u8981\u6211\u7b54\u5e94\u9000\u5a5a\u5462\uff0c\u8fd8\u662f\u6b3a\u6211\u4e0d\u77e5\uff0c\u9001\u4e00\u9876\u73b0\u6210\u7684\u7eff\u5934\u5dfe\u7ed9\u6211\u6234\u6234\uff1f\u2019\u962e\u5e08\u53d4\u8bf4\u591c\u6df1\u4e0d\u966a\u6211\u4e86\uff0c\u53eb\u6211\u81ea\u53bb\u3002\u6211\u751f\u6015\u6709\u751a\u4e0d\u6d4b\uff0c\u53eb\u9192\u4e86\u7239\u7239\uff0c\u8bf7\u4ed6\u9632\u5907\uff0c\u81ea\u5df1\u8eab\u4e0a\u5e26\u4e86\u5175\u5203\u6697\u5668\uff0c\u8fde\u5f13\u7bad\u4e5f\u6697\u85cf\u5728\u957f\u888d\u5e95\u4e0b\u3002 \u3000\u3000\u201c\u5230\u4e86\u7530\u4f2f\u7236\u623f\u91cc\uff0c\u89c1\u4ed6\u8eba\u5728\u5e8a\u4e0a\uff0c\u773c\u671b\u5e8a\u9876\uff0c\u5446\u5446\u7684\u51fa\u795e\uff0c\u624b\u91cc\u62ff\u7740\u4e00\u5f20\u767d\u7eb8\uff0c\u7adf\u6ca1\u89c9\u5bdf\u5230\u6211\u8fdb\u623f\u3002\u6211\u54b3\u55fd\u4e00\u58f0\uff0c\u53eb\u9053\uff1a\u2018\u963f\u7239\uff01\u2019\u4ed6\u5403\u4e86\u4e00\u60ca\uff0c\u5c06\u767d\u7eb8\u85cf\u5165\u4e86\u8925\u5b50\u5e95\u4e0b\uff0c\u9053\uff1a\u2018\u554a\uff0c\u5b50\u5b89\uff0c\u662f\u4f60\u3002\u2019\u6211\u5fc3\u60f3\uff1a\u2018\u660e\u660e\u662f\u4f60\u53eb\u6211\u6765\u7684\uff0c\u5374\u8fd9\u4e48\u88c5\u8154\u4f5c\u52bf\u3002\u2019\u4f46\u77a7\u4ed6\u795e\u8272\uff0c\u5374\u5f53\u771f\u662f\u5f02\u5e38\u60ca\u6050\u3002\u4ed6\u53eb\u6211\u95e9\u4e0a\u623f\u95e8\uff0c\u5374\u53c8\u6253\u5f00\u7a97\u5b50\uff0c\u4ee5\u9632\u6709\u4eba\u5728\u7a97\u5916\u5077\u542c\uff0c\u8fd9\u624d\u98a4\u58f0\u8bf4\u9053\uff1a\u2018\u5b50\u5b89\uff0c\u6211\u773c\u4e0b\u5371\u5728\u65e6\u5915\uff0c\u5168\u51ed\u4f60\u6551\u6211\u4e00\u547d\uff0c\u4f60\u5f97\u53bb\u7ed9\u6211\u529e\u4e00\u4ef6\u4e8b\u3002\u2019\u201d \u3000\u3000\u66f9\u4e91\u5947\u5fc3\u4e2d\u618b\u4e86\u534a\u5929\uff0c\u542c\u5230\u8fd9\u91cc\uff0c\u731b\u5730\u7ad9\u8d77\u8eab\u6765\uff0c\u621f\u6307\u53eb\u904d\uff1a\u201c\u653e\u5c41\uff0c\u653e\u5c41\uff01\u6211\u5e08\u7236\u662f\u4f55\u7b49\u529f\u592b\uff0c\u4f60\u8fd9\u5c0f\u5b50\u6709\u4ec0\u4e48\u672c\u4e8b\u6551\u4ed6\uff1f\u201d \u3000\u3000\u9676\u5b50\u5b89\u773c\u89d2\u513f\u4e5f\u4e0d\u5411\u4ed6\u77a5\u4e0a\u4e00\u77a5\uff0c\u4fbf\u4f3c\u8ddf\u524d\u6ca1\u8fd9\u4e2a\u4eba\u4e00\u822c\uff0c\u5411\u7740\u5b9d\u6811\u7b49\u4eba\u8bf4\u9053\uff1a\u201c\u6211\u542c\u4e86\u4ed6\u8fd9\u4e24\u53e5\u8bdd\uff0c\u5927\u662f\u60ca\u7591\uff0c\u5fd9\u9053\uff1a\u2018\u963f\u7239\u4f46\u6709\u6240\u547d\uff0c\u5c0f\u5a7f\u8d74\u6c64\u8e48\u706b\uff0c\u5728\u6240\u4e0d\u8f9e\u3002\u2019\u7530\u4f2f\u7236\u70b9\u70b9\u5934\uff0c\u4ece\u68c9\u88ab\u4e2d\u53d6\u51fa\u4e00\u4e2a\u957f\u957f\u7684\u3001\u7528\u9526\u7f0e\u5305\u7740\u7684\u5305\u88f9\uff0c\u4ea4\u5728\u6211\u7684\u624b\u91cc\u3002\u9053\uff1a\u2018\u4f60\u62ff\u4e86\u8fd9\u4e1c\u897f\uff0c\u8fde\u591c\u8d76\u8d74\u5173\u5916\uff0c\u57cb\u5728\u9690\u853d\u65e0\u4eba\u4e4b\u5904\u3002\u82e5\u80fd\u4e0d\u8ba9\u65c1\u4eba\u5bdf\u89c9\uff0c\u6216\u53ef\u6551\u5f97\u6211\u4e00\u547d\u3002\u2019\u201c\u6211\u63a5\u8fc7\u624b\u6765\uff0c\u53ea\u89c9\u90a3\u5305\u88f9\u53c8\u6c89\u53c8\u786c\uff0c\u4f3c\u662f\u4e00\u4ef6\u94c1\u5668\uff0c\u95ee\u9053\uff1a\u2018\u90a3\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u6709\u8c01\u8981\u6765\u5bb3\u4f60\uff1f\u2019\u7530\u4f2f\u7236\u5c06\u624b\u6325\u4e86\u51e0\u6325\uff0c\u795e\u8272\u6781\u4e3a\u75b2\u5026\uff0c\u9053\uff1a\u2018\u4f60\u5feb\u53bb\uff0c\u8fde\u4f60\u7239\u7239\u4e5f\u5343\u4e07\u4e0d\u53ef\u544a\u77e5\uff0c\u518d\u8fdf\u7247\u523b\u5c31\u6765\u4e0d\u53ca\u5566\u3002\u8fd9\u5305\u88f9\u5343\u4e07\u4e0d\u5f97\u6253\u5f00\u3002\u2019\u6211\u4e0d\u6562\u518d\u95ee\uff0c\u8f6c\u8eab\u51fa\u623f\u3002\u521a\u8d70\u5230\u95e8\u53e3\uff0c\u7530\u4f2f\u7236\u5ffd\u9053\uff1a\u2018\u5b50\u5b89\uff0c\u4f60\u888d\u5b50\u5e95\u4e0b\u85cf\u7740\u4ec0\u4e48\uff1f\u2019\u6211\u5413\u4e86\u4e00\u8df3\uff0c\u5fc3\u9053\uff1a\u2018\u4ed6\u773c\u5149\u597d\u5389\u5bb3\uff01\u2019\u53ea\u5f97\u7167\u5b9e\u8bf4\u9053\uff1a\u2018\u90a3\u662f\u5175\u5203\u5f13\u7bad\u3002\u4eca\u65e5\u5ba2\u4eba\u591a\uff0c\u5c0f\u5a7f\u6015\u6df7\u8fdb\u4e86\u6b79\u4eba\u6765\uff0c\u6240\u4ee5\u7279\u5730\u9632\u7740\u70b9\u513f\u3002\u2019\u7530\u4f2f\u7236\u9053\uff1a\u2018\u597d\uff0c\u4f60\u7cbe\u660e\u80fd\u5e72\uff0c\u4e91\u5947\u80fd\u5b66\u7740\u4f60\u4e00\u70b9\u513f\uff0c\u90a3\u5c31\u597d\u4e86\u3002\u5509\uff0c\u4f60\u628a\u5f13\u7bad\u7ed9\u6211\u3002\u2019\u201c\u6211\u4ece\u888d\u5e95\u4e0b\u53d6\u51fa\u5f13\u7bad\uff0c\u9012\u7ed9\u4e86\u4ed6\u3002\u4ed6\u62bd\u51fa\u4e00\u652f\u957f\u7bad\uff0c\u770b\u4e86\u51e0\u773c\uff0c\u642d\u5728\u5f13\u4e0a\uff0c\u9053\uff1a\u2018\u4f60\u5feb\u53bb\u5427\uff01\u2019\u6211\u89c1\u4e86\u8fd9\u526f\u6a21\u6837\uff0c\u5fc3\u4e0b\u5230\u6709\u4e9b\u60ca\u614c\uff1a\u2018\u4ed6\u522b\u8981\u5728\u6211\u80cc\u5fc3\u5c04\u4e0a\u4e00\u7bad\uff01\u2019\u88c5\u7740\u8eac\u8eab\u884c\u793c\uff0c\u6162\u6162\u53cd\u9000\u51fa\u53bb\uff0c\u9000\u5230\u623f\u95e8\uff0c\u8fd9\u624d\u7a81\u7136\u8f6c\u8eab\u3002\u51fa\u623f\u95e8\u540e\u6211\u56de\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u4ed6\u5c06\u7bad\u5934\u5bf9\u51c6\u7a97\u53e3\uff0c\u663e\u662f\u9632\u5907\u4ec7\u5bb6\u4ece\u7a97\u4e2d\u8fdb\u6765\u3002 \u3000\u3000\u201c\u6211\u56de\u5230\u81ea\u5df1\u623f\u91cc\uff0c\u5bf9\u8fd9\u4e8b\u597d\u751f\u72af\u7591\uff0c\u5fc3\u60f3\u7530\u4f2f\u7236\u7684\u795e\u8272\u4e4b\u4e2d\uff0c\u59cb\u7ec8\u900f\u7740\u4e03\u5206\u60ca\u60f6\u3001\u4e09\u5206\u8be1\u79d8\uff0c\u53ef\u4ee5\u6599\u5b9a\u4ed6\u5bf9\u6211\u51b3\u65e0\u597d\u610f\u3002\u6211\u5c06\u8fd9\u4e8b\u5bf9\u7239\u7239\u8bf4\u4e86\uff0c\u4f46\u4e3a\u4e86\u6015\u60f9\u4ed6\u751f\u6c14\uff0c\u9752\u6587\u59b9\u5b50\u7684\u4e8b\u5374\u7792\u7740\u4e0d\u8bf4\u3002\u7239\u7239\u9053\uff1a\u2018\u5148\u77a7\u77a7\u5305\u4e2d\u662f\u4ec0\u4e48\u4e1c\u897f\u3002\u2019\u6211\u4e5f\u6b63\u6709\u6b64\u610f\uff0c\u4e24\u4eba\u6253\u5f00\u5305\u88f9\uff0c\u539f\u6765\u6b63\u662f\u8fd9\u53ea\u94c1\u76d2\u3002 \u3000\u3000\u201c\u7239\u7239\u5f53\u5e74\u4eb2\u773c\u89c1\u5230\u7530\u4f2f\u7236\u5c06\u8fd9\u53ea\u94c1\u76d2\u4ece\u80e1\u4e00\u5200\u7684\u9057\u5b64\u624b\u4e2d\u62a2\u6765\uff0c\u540e\u6765\u5c31\u5c06\u5929\u9f99\u95e8\u9547\u95e8\u4e4b\u5b9d\u7684\u5b9d\u5200\u653e\u5728\u76d2\u91cc\u3002\u7239\u7239\u5f53\u65f6\u8bf4\u9053\uff1a\u2018\u8fd9\u5c31\u5947\u4e86\u3002\u2019\u4ed6\u77e5\u9053\u94c1\u76d2\u65c1\u85cf\u6709\u77ed\u7bad\uff0c\u4e5f\u77e5\u9053\u94c1\u76d2\u7684\u5f00\u542f\u4e4b\u6cd5\uff0c\u5f53\u5373\u4f9d\u6cd5\u6253\u5f00\u3002\u6211\u7237\u513f\u4fe9\u4e00\u770b\u4e4b\u4e0b\uff0c\u9762\u9762\u76f8\u89d1\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u539f\u6765\u76d2\u4e2d\u7adf\u662f\u7a7a\u65e0\u4e00\u7269\u3002\u7239\u7239\u9053\uff1a\u2018\u90a3\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u2019\u201c\u6211\u65e9\u5c31\u77a7\u51fa\u4e0d\u5999\uff0c\u8fd9\u65f6\u66f4\u5df2\u5fc3\u4e2d\u96ea\u4eae\uff0c\u77e5\u9053\u5fc5\u662f\u7530\u4f2f\u7236\u9677\u5bb3\u6211\u7684\u4e00\u6761\u6bd2\u8ba1\uff0c\u4ed6\u5c06\u5b9d\u5200\u85cf\u5728\u522b\u5904\uff0c\u5374\u5c06\u94c1\u76d2\u7ed9\u6211\u3002\u4ed6\u5fc5\u6d3e\u4eba\u5728\u8def\u4e0a\u622a\u963b\uff0c\u62ff\u5230\u6211\u540e\uff0c\u4fbf\u8bec\u9677\u6211\u76d7\u4ed6\u5b9d\u5200\uff0c\u903c\u6211\u4ea4\u51fa\u3002\u6211\u4ea4\u4e0d\u51fa\u5200\uff0c\u4ed6\u7eb5\u4e0d\u6740\u6211\uff0c\u4e5f\u5fc5\u5c06\u9752\u59b9\u7684\u5a5a\u4e8b\u9000\u4e86\uff0c\u597d\u8ba9\u5979\u53e6\u5ac1\u66f9\u5e08\u5144\u3002\u7239\u7239\u4e0d\u77e5\u5176\u4e2d\u539f\u59d4\uff0c\u81ea\u7136\u77a7\u4e0d\u900f\u8fd9\u6bd2\u8ba1\u3002\u6211\u4e0d\u4fbf\u5bf9\u7239\u7239\u660e\u8a00\uff0c\u53d1\u4e86\u534a\u5929\u5446\uff0c\u7237\u4fe9\u513f\u53c8\u5546\u91cf\u4e86\u534a\u5929\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u3002\u201d \u3000\u3000\u66f9\u4e91\u5947\u5927\u53eb\uff1a\u201c\u4f60\u5bb3\u6b7b\u6211\u5e08\u7236\uff0c\u5077\u7a83\u6211\u5929\u9f99\u95e8\u81f3\u5b9d\uff0c\u5374\u53c8\u6765\u80e1\u8bf4\u516b\u9053\u3002\u8fd9\u5957\u9b3c\u8bdd\uff0c\u8fde\u4e09\u5c81\u5b69\u513f\u4e5f\u7792\u9a97\u4e0d\u8fc7\u3002\u201d\u9676\u5b50\u5b89\u51b7\u7b11\u9053\uff1a\u201c\u7530\u4f2f\u7236\u867d\u5df2\u6b7b\u65e0\u5bf9\u8bc1\uff0c\u6211\u624b\u4e2d\u5374\u6709\u8bc1\u636e\u3002\u201d\u66f9\u4e91\u5947\u66f4\u662f\u66b4\u8df3\u5982\u96f7\uff0c\u559d\u9053\uff1a\u201c\u8bc1\u636e\uff1f\u4ec0\u4e48\u8bc1\u636e\uff1f\u62ff\u51fa\u6765\u5927\u5bb6\u77a7\u77a7\u3002\u201d\u9676\u5b50\u5b89\u9053\uff1a\u201c\u5230\u65f6\u5019\u6211\u81ea\u4f1a\u62ff\u51fa\u6765\uff0c\u4e0d\u7528\u4f60\u7740\u5fd9\u3002\u5404\u4f4d\uff0c\u8fd9\u4f4d\u66f9\u5e08\u5144\u8001\u662f\u6253\u65ad\u6211\u7684\u8bdd\u5934\uff0c\u8fd8\u4e0d\u5982\u8bf7\u4ed6\u6765\u8bf4\u3002\u201d \u3000\u3000\u5b9d\u6811\u51b7\u51b7\u7684\u9053\uff1a\u201c\u66f9\u4e91\u5947\uff0c\u4f60\u5988\u5df4\u7f94\u5b50\u7684\uff0c\u4f60\u8981\u628a\u8001\u548c\u5c1a\u649e\u4e0b\u5c71\u53bb\uff0c\u548c\u5c1a\u8fd8\u6ca1\u8ddf\u4f60\u7b97\u5e10\u5462\uff01\u76f4\u5a18\u8d3c\uff0c\u4f60\u77aa\u773c\u73e0\u7c97\u8116\u5b50\u5e72\u4e48\uff1f\u201d\u66f9\u4e91\u5947\u5fc3\u4e2d\u4e00\u5bd2\uff0c\u4e0d\u6562\u518d\u8bf4\u3002 \u3000\u3000\u9676\u5b50\u5b89\u9053\uff1a\u201c\u6211\u77e5\u9053\u53ea\u8981\u62ff\u7740\u94c1\u76d2\u4e00\u51fa\u7530\u95e8\uff0c\u5c31\u7b97\u6ca1\u6740\u8eab\u4e4b\u7978\uff0c\u4e5f\u5fc5\u95f9\u4e2a\u8eab\u8d25\u540d\u88c2\u3002\u6211\u9053\uff1a\u2018\u7239\uff0c\u8fd9\u4e2d\u95f4\u5927\u6709\u53e4\u602a\uff0c\u6211\u628a\u5305\u88f9\u53bb\u8fd8\u7ed9\u5cb3\u7236\uff0c\u4e0d\u80fd\u62db\u63fd\u8fd9\u95e8\u5b50\u4e8b\u3002\u2019\u5f53\u4e0b\u5c06\u94c1\u76d2\u5305\u56de\u5728\u9526\u7f0e\u4e4b\u4e2d\uff0c\u5fc3\u4e0b\u7422\u78e8\u4e86\u51e0\u53e5\u8bdd\uff0c\u8981\u70b9\u7834\u4ed6\u7684\u8be1\u8ba1\uff0c\u5927\u5bb6\u6765\u4e2a\u5fc3\u7167\u4e0d\u5ba3\u3002 \u3000\u3000\u201c\u5f85\u6211\u6367\u7740\u5305\u88f9\u8d76\u5230\u7530\u4f2f\u7236\u623f\u5916\uff0c\u4ed6\u623f\u4e2d\u706f\u5149\u5df2\u7184\uff0c\u7a97\u5b50\u623f\u95e8\u90fd\u5df2\u7d27\u95ed\u3002\u6211\u60f3\u8fd9\u4ef6\u4e8b\u968f\u65f6\u90fd\u80fd\u95f9\u7a7f\uff0c\u7247\u523b\u5ef6\u6328\u4e0d\u5f97\uff0c\u5f53\u4e0b\u5728\u7a97\u5916\u53eb\u4e86\u51e0\u58f0\uff1a\u2018\u963f\u7239\uff0c\u963f\u7239\uff01\u2019\u623f\u91cc\u5374\u6ca1\u6709\u5e94\u58f0\u3002\u6211\u5fc3\u4e0b\u8d77\u7591\uff1a\u2018\u4ed6\u8fd9\u7b49\u6b66\u529f\uff0c\u7eb5\u5728\u6c89\u7761\u4e4b\u4e2d\u4e5f\u5fc5\u7acb\u65f6\u60ca\u89c9\uff0c\u770b\u6765\u662f\u6545\u610f\u4e0d\u7b54\u3002\u2019\u201c\u6211\u8d8a\u60f3\u8d8a\u6015\uff0c\u4f3c\u89c9\u5929\u9f99\u95e8\u7684\u5f1f\u5b50\u5df2\u57cb\u4f0f\u5728\u4fa7\uff0c\u9a6c\u4e0a\u5c31\u8981\u4e00\u62e5\u800c\u4e0a\uff0c\u903c\u6211\u4ea4\u51fa\u5b9d\u5200\u3002\u6211\u4e00\u9762\u62cd\u95e8\uff0c\u4e00\u9762\u628a\u8bdd\u8bf4\u660e\u5728\u5148\uff1a \u3000\u3000\u2018\u963f\u7239\uff01\u6211\u7239\u7239\u8981\u6211\u628a\u5305\u88f9\u8fd8\u60a8\u3002\u6211\u4eec\u6709\u8981\u4e8b\u5728\u8eab\uff0c\u6ca1\u80fd\u8ddf\u60a8\u8001\u529e\u4e8b\u3002\u8fd9\u5305\u88f9\u5c0f\u5a7f\u53ef\u6ca1\u6253\u5f00\u8fc7\u3002\u2019\u62cd\u4e0b\u51e0\u4e0b\uff0c\u623f\u4e2d\u4ecd\u662f\u65e0\u58f0\u65e0\u606f\u3002\u6211\u6025\u4e86\uff0c\u53d6\u51fa\u5200\u5b50\u64ac\u5f00\u4e86\u95e8\u95e9\uff0c\u63a8\u95e8\u8fdb\u53bb\uff0c\u6253\u706b\u70b9\u4eae\u8721\u70db\uff0c\u4e0d\u7531\u5f97\u60ca\u5f97\u5446\u4e86\uff0c\u53ea\u89c1\u7530\u4f2f\u7236\u5df2\u6b7b\u5728\u5e8a\u4e0a\uff0c\u80f8\u53e3\u63d2\u4e86\u4e00\u652f\u957f\u7bad\uff0c\u90a3\u6b63\u662f\u6211\u5e38\u7528\u7684\u7fbd\u7bad\u3002\u6211\u90a3\u526f\u5f13\u7bad\u653e\u5728\u4ed6\u68c9\u88ab\u4e4b\u4e0a\u3002\u4ed6\u8138\u8272\u60ca\u6016\u5f02\u5e38\uff0c\u4f3c\u4e4e\u4e34\u6b7b\u4e4b\u524d\u66fe\u89c1\u5230\u4ec0\u4e48\u6781\u53ef\u6015\u7684\u5996\u9b54\u9b3c\u602a\u4e00\u822c\u3002 \u3000\u3000\u201c\u6211\u5446\u4e86\u534a\u664c\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff0c\u773c\u89c1\u95e8\u7a97\u7d27\u95ed\uff0c\u4e0d\u77e5\u5bb3\u6b7b\u7530\u4f2f\u7236\u7684\u51f6\u624b\u600e\u751f\u8fdb\u6765\uff0c\u4e0b\u624b\u540e\u53c8\u4ece\u4f55\u5904\u51fa\u53bb\uff1f\u62ac\u5934\u5411\u5c4b\u9876\u4e00\u5f20\uff0c\u4f46\u89c1\u5c4b\u74e6\u597d\u597d\u7684\u6ca1\u534a\u70b9\u7834\u788e\uff0c\u90a3\u4e48\u51f6\u624b\u5c31\u4e0d\u662f\u4ece\u5c4b\u9876\u51fa\u5165\u7684\u4e86\u3002 \u3000\u3000\u201c\u6211\u518d\u60f3\u67e5\u770b\uff0c\u5ffd\u542c\u5f97\u8d70\u5eca\u4e2d\u4f20\u6765\u51e0\u4e2a\u4eba\u7684\u811a\u6b65\u4e4b\u58f0\u3002\u6211\u60f3\u7530\u4f2f\u7236\u6b7b\u5728\u6211\u7684\u7bad\u4e0b\uff0c\u6b64\u65f6\u82e5\u6709\u4eba\u8fdb\u6765\uff0c\u6211\u5982\u4f55\u8131\u5f97\u4e86\u5e72\u7cfb\uff1f\u5fd9\u5728\u88ab\u4e0a\u53d6\u8fc7\u6211\u7684\u5f13\u7bad\uff0c\u6b63\u8981\u53bb\u62d4\u4ed6\u80f8\u53e3\u7684\u7fbd\u7bad\uff0c\u70db\u5149\u4e0b\u7a81\u7136\u89c1\u5230\u5e8a\u4e0a\u6709\u4e24\u4ef6\u7269\u4e8b\uff0c\u8fd9\u4e00\u60ca\u66f4\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u624b\u4e00\u98a4\uff0c\u70db\u53f0\u8131\u624b\uff0c\u70db\u706b\u7acb\u65f6\u706d\u4e86\u3002 \u3000\u3000\u201c\u5404\u4f4d\u5b9a\u7136\u731c\u4e0d\u5230\u6211\u89c1\u4e86\u4ec0\u4e48\u4e1c\u897f\u3002\u539f\u6765\u4e00\u6837\u662f\u8fd9\u67c4\u5b9d\u5200\uff0c\u53e6\u4e00\u6837\u5374\u662f\u9752\u59b9\u57cb\u5728\u575f\u4e2d\u7684\u90a3\u4e2a\u6b7b\u5a74\u3002\u5f53\u65f6\u6211\u53ea\u9053\u662f\u8fd9\u5a74\u513f\u4e0d\u7518\u65e0\u8f9c\u6789\u6b7b\uff0c\u7adf\u4ece\u575f\u4e2d\u94bb\u51fa\u6765\u7d22\u547d\uff0c\u614c\u4e71\u4e4b\u4e0b\uff0c\u987a\u624b\u62a2\u4e86\u5b9d\u5200\u5c31\u9003\u3002\u521a\u5954\u5230\u95e8\u53e3\uff0c\u5ffd\u7136\u60f3\u8d77\u4e00\u4e8b\uff0c\u56de\u6765\u5728\u7530\u4f2f\u7236\u7684\u8925\u4e0b\u4e00\u6478\uff0c\u679c\u7136\u6478\u5230\u4e86\u90a3\u5f20\u767d\u7eb8\u3002\u6211\u6599\u5230\u4ed6\u7684\u6b7b\u56e0\u8ddf\u8fd9\u5f20\u7eb8\u4e00\u5b9a\u5927\u6709\u5e72\u7cfb\uff0c\u4e8e\u662f\u585e\u5165\u6000\u4e2d\uff0c\u6b63\u8981\u4f38\u624b\u518d\u53bb\u62d4\u7bad\uff0c\u811a\u6b65\u58f0\u8fd1\uff0c\u5df2\u6709\u4e09\u4eba\u8d70\u5230\u4e86\u95e8\u53e3\u3002\u6211\u6697\u53eb\uff1a\u2018\u7cdf\u7cd5\uff01\u8fd9\u4e00\u4e0b\u95e8\u53e3\u88ab\u5835\uff0c\u6211\u9676\u5b50\u5b89\u6027\u547d\u4f11\u77e3\uff01\u2019\u201c\u5371\u6025\u4e4b\u4e0b\uff0c\u773c\u89c1\u65e0\u5904\u8eb2\u85cf\uff0c\u53ea\u5f97\u5f80\u5e8a\u5e95\u4e0b\u4e00\u94bb\uff0c\u4f46\u542c\u5f97\u90a3\u4e09\u4eba\u63a8\u95e8\u8fdb\u6765\uff0c\u539f\u6765\u662f\u962e\u5e08\u53d4\u548c\u66f9\u5468\u4e24\u4f4d\u5e08\u5144\u3002\u962e\u5e08\u53d4\u53eb\u4e86\u4e24\u58f0\uff1a\u2018\u5e08\u54e5\u2019\u4e0d\u542c\u89c1\u5e94\u58f0\uff0c\u5c31\u547d\u5468\u5e08\u5144\u53bb\u70b9\u8721\u70db\u6765\u3002\u6211\u60f3\u5f85\u4f1a\u53d6\u6765\u70db\u706b\uff0c\u4ed6\u4eec\u89c1\u5230\u7530\u4f2f\u7236\u6789\u6b7b\uff0c\u4e00\u641c\u4e4b\u4e0b\uff0c\u6211\u6027\u547d\u96be\u4fdd\uff0c\u6b64\u523b\u4e58\u9ed1\uff0c\u6b63\u597d\u51b2\u5c06\u51fa\u53bb\u3002 \u3000\u3000\u201c\u962e\u5e08\u53d4\u4e0e\u66f9\u5e08\u54e5\u90fd\u662f\u9ad8\u624b\uff0c\u6211\u4e00\u4eba\u81ea\u4e0d\u662f\u4ed6\u4e8c\u4eba\u4e4b\u654c\uff0c\u4f46\u51fa\u5176\u4e0d\u610f\uff0c\u6216\u80fd\u8131\u8eab\uff0c\u6b64\u65f6\u987b\u5f97\u5f53\u673a\u7acb\u65ad\uff0c\u4e07\u4e07\u8fc1\u5ef6\u4e0d\u5f97\uff0c\u5f53\u4e0b\u6162\u6162\u722c\u5230\u5e8a\u8fb9\uff0c\u6b63\u8981\u8dc3\u51fa\uff0c\u7a81\u7136\u624b\u81c2\u4f38\u5c06\u51fa\u53bb\uff0c\u78b0\u5230\u4e00\u4eba\u7684\u8138\u5b54\uff0c\u539f\u6765\u5e8a\u5e95\u4e0b\u5df2\u6709\u4eba\u6bd4\u6211\u5148\u5230\u3002 \u3000\u3000\u201c\u6211\u9669\u4e9b\u5931\u58f0\u60ca\u547c\uff0c\u90a3\u4eba\u5df2\u4f38\u624b\u6263\u4f4f\u6211\u7684\u8109\u95e8\u3002\u6211\u6697\u6697\u53eb\u82e6\uff0c\u90a3\u4eba\u5728\u6211\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u2018\u522b\u4f5c\u58f0\uff0c\u4e00\u8d77\u51fa\u53bb\u3002\u2019\u6211\u5fc3\u4e2d\u5927\u559c\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u773c\u524d\u4e00\u4eae\uff0c\u5468\u5e08\u54e5\u5df2\u63d0\u4e86\u706f\u7b3c\u6765\u5230\u3002 \u3000\u3000\u201c\u53ea\u542c\u5f97\u5657\u7684\u4e00\u54cd\uff0c\u90a3\u4eba\u53d1\u4e86\u4e00\u679a\u6697\u5668\uff0c\u5c06\u706f\u7b3c\u6253\u706d\uff0c\u8ddf\u7740\u7ffb\u624b\u7adf\u6765\u593a\u6211\u624b\u4e2d\u7684\u5b9d\u5200\u3002\u6211\u4e00\u4e2a\u6253\u6eda\uff0c\u6eda\u51fa\u5e8a\u5e95\uff0c\u6025\u51b2\u800c\u51fa\u3002\u5e8a\u5e95\u90a3\u4eba\u8ffd\u5c06\u51fa\u6765\u3002\u53ea\u542c\u962e\u5e08\u53d4\u53eb\u9053\uff1a\u2018\u597d\u8d3c\u5b50\uff01\u2019\u6325\u638c\u6253\u51fa\u3002\u962e\u5e08\u53d4\u6b66\u529f\u6781\u9ad8\uff0c\u6599\u60f3\u90a3\u4eba\u4e5f\u8131\u4e0d\u4e86\u8eab\u3002\u6211\u6025\u5fd9\u5954\u56de\u623f\u4e2d\uff0c\u53eb\u4e86\u7239\u7239\uff0c\u8fde\u591c\u9003\u51fa\u7530\u5bb6\u3002 \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u7684\u7ecf\u8fc7\u5c31\u662f\u8fd9\u6837\u3002\u8fd9\u53ea\u94c1\u76d2\u662f\u7530\u4f2f\u7236\u4eb2\u624b\u4ea4\u7ed9\u6211\u7684\uff0c\u4ed6\u53eb\u6211\u57cb\u5728\u5173\u5916\uff0c\u6211\u662f\u4f9d\u4ed6\u7684\u9057\u547d\u800c\u4e3a\u3002\u5929\u9f99\u95e8\u7684\u5e08\u53d4\u5e08\u5144\u4eec\u89c1\u5230\u7530\u4f2f\u7236\u80f8\u4e0a\u7fbd\u7bad\uff0c\u81ea\u7136\u7591\u5fc3\u662f\u6211\u4e0b\u624b\u5bb3\u4ed6\uff0c\u8fd9\u539f\u662f\u96be\u602a\u3002\u53ea\u53ef\u60dc\u6211\u4e0d\u77e5\u5e8a\u5e95\u90a3\u4eba\u7684\u5e95\u7ec6\uff0c\u5426\u5219\u5927\u53ef\u627e\u6765\u4f5c\u4e2a\u89c1\u8bc1\u3002\u4f46\u5c31\u7b97\u627e\u4e0d\u5230\u5e8a\u4e0b\u90a3\u4eba\uff0c\u6211\u4e5f\u77e5\u5bb3\u6b7b\u7530\u4f2f\u7236\u7684\u51f6\u624b\u662f\u8c01\u3002\u5404\u4f4d\u8bf7\u770b\uff0c\u8fd9\u5f20\u7eb8\u662f\u7530\u4f2f\u7236\u89c1\u5230\u6211\u65f6\u585e\u5728\u8925\u5b50\u5e95\u4e0b\u7684\uff0c\u4ed6\u5bb3\u6015\u4ec7\u5bb6\u524d\u6765\u76f8\u5bb3\uff0c\u5f2f\u5f13\u642d\u7bad\u5bf9\u51c6\u7a97\u53e3\uff0c\u7b49\u7684\u5c31\u662f\u6b64\u4eba\u3002\u53ef\u662f\u6b64\u4eba\u7ec8\u4e8e\u5230\u6765\uff0c\u800c\u7530\u4f2f\u7236\u4e5f\u7ec8\u4e8e\u9003\u4e0d\u51fa\u4ed6\u7684\u6bd2\u624b\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ece\u6000\u91cc\u53d6\u51fa\u4e00\u53ea\u7ee3\u82b1\u7684\u9526\u56ca\u3002\u4f17\u4eba\u89c1\u8fd9\u9526\u56ca\u624b\u5de5\u7cbe\u81f4\uff0c\u6599\u77e5\u662f\u7530\u9752\u6587\u6240\u4f5c\uff0c\u4e0d\u7531\u5f97\u8f6c\u5934\u53bb\u671b\u66f9\u4e91\u5947\u3002\u53ea\u89c1\u4ed6\u607c\u5f97\u773c\u4e2d\u5982\u8981\u55b7\u706b\uff0c\u5fc3\u4e2d\u90fd\u662f\u6697\u6697\u597d\u7b11\u3002\u9676\u5b50\u5b89\u6253\u5f00\u9526\u56ca\uff0c\u6478\u51fa\u4e00\u5f20\u767d\u7eb8\uff0c\u8981\u5f85\u4ea4\u7ed9\u5b9d\u6811\uff0c\u5fae\u4e00\u8fdf\u7591\uff0c\u5374\u9012\u7ed9\u4e86\u82d7\u82e5\u5170\u3002 \u3000\u3000\u90a3\u767d\u7eb8\u6298\u6210\u4e00\u4e2a\u65b9\u80dc\uff0c\u82d7\u82e5\u5170\u63a5\u8fc7\u6765\u6253\u5f00\u4e00\u770b\uff0c\u8f7b\u8f7b\u54a6\u4e86\u4e00\u58f0\uff0c\u53ea\u89c1\u7eb8\u4e0a\u6d53\u58a8\u5199\u7740\u4e24\u884c\u5b57\u9053\uff1a\u201c\u606d\u8d3a\u7530\u8001\u524d\u8f88\u95ed\u95e8\u5c01\u5251\uff0c\u798f\u5bff\u5168\u5f52\u3002\u95e8\u4e0b\u4f8d\u6559\u665a\u751f\u80e1\u6590\u8c28\u62dc\u3002\u201d\u8fd9\u4e24\u884c\u5b57\u7b14\u529b\u9052\u52b2\uff0c\u4e0e\u5de6\u53f3\u53cc\u7ae5\u9001\u4e0a\u5c71\u6765\u7684\u62dc\u5e16\u4e66\u6cd5\u4e00\u6a21\u4e00\u6837\uff0c\u786e\u662f\u80e1\u6590\u7684\u4eb2\u7b14\u3002\u82d7\u82e5\u5170\u62ff\u7740\u767d\u7eb8\u7684\u624b\u5fae\u5fae\u98a4\u52a8\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u96be\u9053\u662f\u4ed6\uff1f\u201d \u3000\u3000\u962e\u58eb\u4e2d\u4ece\u82d7\u82e5\u5170\u624b\u4e2d\u63a5\u8fc7\u767d\u7eb8\u4e00\u770b\uff0c\u9053\uff1a\u201c\u8fd9\u786e\u662f\u80e1\u6590\u7684\u7b14\u8ff9\u3002\u8fd9\u6837\u8bf4\u6765\uff0c\u54b1\u4eec\u5012\u662f\u9519\u602a\u5b50\u5b89\u4e86\u3002\u201d\u4ed6\u7a81\u7136\u56de\u8fc7\u5934\u6765\uff0c\u671b\u7740\u5218\u5143\u9e64\u9053\uff1a\u201c\u5218\u5927\u4eba\uff0c\u90a3\u4e48\u4f60\u8eb2\u5728\u6211\u7530\u5e08\u54e5\u5e8a\u5e95\u4e0b\u5e72\u4ec0\u4e48\uff1f \u3000\u3000\u4f60\u662f\u7ed9\u5367\u5e95\u6765\u5566\uff0c\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4f17\u4eba\u95fb\u8a00\uff0c\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u66f9\u4e91\u5947\u4e0e\u5468\u4e91\u9633\u4e5f\u90fd\u6478\u4e0d\u7740\u5934\u8111\u3002\u5f53\u665a\u9ed1\u6697\u4e4b\u4e2d\uff0c\u90a3\u5e8a\u5e95\u4eba\u4e0e\u962e\u58eb\u4e2d\u4ea4\u624b\u6570\u5408\uff0c\u968f\u5373\u9038\u53bb\uff0c\u4e09\u4eba\u4e8b\u540e\u731c\u6d4b\uff0c\u59cb\u7ec8\u4e0d\u77e5\u662f\u8c01\uff0c\u600e\u4e48\u4ed6\u6b64\u65f6\u7a81\u7136\u6307\u7740\u5218\u5143\u9e64\u53eb\u9635\uff1f \u3000\u3000\u5218\u5143\u9e64\u53ea\u662f\u51b7\u7b11\u4e00\u58f0\uff0c\u5374\u4e0d\u7b54\u8bdd\u3002\u962e\u58eb\u4e2d\u53c8\u9053\uff1a\u201c\u90a3\u665a\u9ed1\u6697\u4e4b\u4e2d\uff0c\u5728\u4e0b\u672a\u80fd\u5f97\u89c1\u5e8a\u4e0b\u541b\u5b50\u7684\u9762\u8c8c\uff0c\u5fc3\u4e2d\u5374\u5f88\u4f69\u670d\u6b64\u516c\u6b66\u827a\u4e86\u5f97\u3002\u6211\u4eec\u5e08\u53d4\u4f84\u4e09\u4eba\u4e0d\u4f46\u672a\u80fd\u5c06\u4ed6\u622a\u4f4f\uff0c\u8fde\u4ed6\u7684\u5e95\u7ec6\u6765\u5386\u4e5f\u662f\u6478\u4e0d\u5230\u534a\u70b9\u8fb9\u513f\uff0c\u5f53\u771f\u7b97\u5f97\u65e0\u80fd\u3002\u4eca\u65e5\u96ea\u5730\u4e00\u6218\uff0c\u5f97\u4e0e\u5218\u5927\u4eba\u8fc7\u62db\uff0c\u5374\u6b63\u662f\u5f53\u65e5\u5e8a\u4e0b\u541b\u5b50\u7684\u8eab\u624b\u3002\u563f\u563f\uff0c\u5e78\u4f1a\u554a\u5e78\u4f1a\uff01\u563f\u563f\uff0c\u53ef\u60dc\u554a\u53ef\u60dc\u3002\u201d \u3000\u3000\u5468\u4e91\u9633\u77e5\u9053\u5e08\u53d4\u6b64\u65f6\u5fc5\u5f97\u8981\u4e2a\u642d\u5f53\uff0c\u5c31\u5982\u8bf4\u76f8\u58f0\u7684\u4e0b\u624b\uff0c\u5426\u5219\u63a5\u4e0d\u4e0b\u53e3\u53bb\uff0c\u4e8e\u662f\u95ee\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d\u962e\u58eb\u4e2d\u53cc\u7709\u4e00\u626c\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u53ef\u60dc\u5802\u5802\u4e00\u4f4d\u5fa1\u524d\u4f8d\u536b\u5218\u5927\u4eba\uff0c\u5c45\u7136\u4e0d\u987e\u8eab\u5206\uff0c\u6765\u5e72\u8fd9\u7b49\u7a7f\u5802\u5165\u6237\u3001\u5077\u9e21\u6478\u72d7\u7684\u52fe\u5f53\u3002\u201d \u3000\u3000\u5218\u5143\u9e64\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u962e\u5927\u54e5\u9a82\u5f97\u597d\uff0c\u9a82\u5f97\u75db\u5feb\uff0c\u90a3\u665a\u8eb2\u5728\u7530\u5f52\u519c\u5e8a\u4e0b\u7684\uff0c\u4e0d\u9519\u6b63\u662f\u533a\u533a\u5728\u4e0b\u3002\u4f60\u9a82\u6211\u5077\u9e21\u6478\u72d7\uff0c\u539f\u4e5f\u4e0d\u5047\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u8138\u4e0a\u663e\u51fa\u4e00\u526f\u5f97\u610f\u7684\u795e\u60c5\uff0c\u53c8\u9053\uff1a\u201c\u53ea\u662f\u5728\u4e0b\u7684\u5077\u9e21\u6478\u72d7\uff0c\u5374\u662f\u5949\u4e86\u7687\u4e0a\u7684\u5723\u65e8\u800c\u884c\uff01\u201d \u3000\u3000\u4f17\u4eba\u5fc3\u4e2d\u4e00\u5947\uff0c\u90fd\u89c9\u4ed6\u80e1\u8bf4\u516b\u9053\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u4ed6\u662f\u6e05\u5bab\u4f8d\u536b\uff0c\u53ea\u6015\u5f53\u771f\u662f\u5949\u65e8\u5bf9\u4ed8\u5929\u9f99\u95e8\uff0c\u4ea6\u672a\u53ef\u77e5\u3002\u5929\u9f99\u8bf8\u4eba\u90fd\u662f\u6709\u5bb6\u6709\u4e1a\u4e4b\u4eba\uff0c\u95fb\u8a00\u4e0d\u7981\u6c14\u6cae\u3002\u6bb7\u5409\u662f\u4e24\u5e7f\u8457\u540d\u7684\u5927\u8d22\u4e3b\uff0c\u5fc3\u4e2d\u5c24\u5176\u60ca\u60e7\u3002 \u3000\u3000\u5218\u5143\u9e64\u89c1\u4e00\u53e5\u8bdd\u4fbf\u628a\u4f17\u4eba\u6151\u4f0f\u4e86\uff0c\u66f4\u662f\u6d0b\u6d0b\u81ea\u5f97\uff0c\u8bf4\u9053\uff1a\u201c\u4e8b\u5230\u5982\u4eca\uff0c\u6211\u5c31\u628a\u8fd9\u4e8b\u8ddf\u5404\u4f4d\u8bf4\u8bf4\uff0c\u5f85\u4f1a\u6216\u8005\u5c1a\u6709\u501f\u91cd\u5404\u4f4d\u4e4b\u5904\uff0c\u8fd9\u4e00\u4ef6\u4e1c\u897f\uff0c\u6216\u8005\u5404\u4f4d\u4ece\u672a\u89c1\u8fc7\u3002\u201d\u8bf4\u7740\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u4e2a\u9ec4\u8272\u7684\u5927\u5c01\u5957\u6765\u3002\u5c01\u5957\u5916\u5199\u7740\u201c\u5bc6\u4ee4\u201d\u4e8c\u5b57\uff0c\u4ed6\u5f00\u4e86\u888b\u53e3\uff0c\u53d6\u51fa\u4e00\u5f20\u9ec4\u7eb8\uff0c\u6717\u58f0\u8bfb\u9053\uff1a\u201c\u5949\u5bc6\u8c15\uff0c\u4ee4\u5fa1\u524d\u4e00\u7b49\u4f8d\u536b\u5218\u5143\u9e64\u4f9d\u8ba1\u884c\u4e8b\uff0c\u4e0d\u5f97\u6709\u8bef\u3002\u603b\u7ba1\u8d5b\u3002\u201d\u8bfb\u6bd5\uff0c\u5c06\u90a3\u9ec4\u7eb8\u644a\u5728\u684c\u4e0a\uff0c\u8ba9\u4f17\u4eba\u5171\u89c2\u3002 \u3000\u3000\u6bb7\u5409\u3001\u9676\u767e\u5c81\u7b49\u591a\u89c1\u535a\u95fb\uff0c\u773c\u89c1\u9ec4\u7eb8\u4e0a\u76d6\u7740\u6731\u7ea2\u7684\u56fe\u7ae0\uff0c\u77e5\u9053\u786e\u662f\u4f8d\u536b\u603b\u7ba1\u8d5b\u5c1a\u9102\u6240\u4e0b\u7684\u5bc6\u4ee4\u3002\u90a3\u8d5b\u603b\u7ba1\u5411\u79f0\u6ee1\u6d32\u6b66\u58eb\u7684\u7b2c\u4e00\u9ad8\u624b\uff0c\u7d20\u4e3a\u4e7e\u9686\u7687\u5e1d\u6240\u501a\u91cd\u3002 \u3000\u3000\u5218\u5143\u9e64\u9053\uff1a\u201c\u962e\u5927\u54e5\uff0c\u4f60\u4e0d\u7528\u8ddf\u6211\u77aa\u773c\u73e0\u5439\u80e1\u5b50\uff0c\u8fd9\u4ef6\u4e8b\u4ece\u5934\u8bf4\u6765\uff0c\u8fd8\u662f\u4ee4\u5e08\u5144\u7530\u5f52\u519c\u8d77\u7684\u56e0\u5934\u3002\u6709\u4e00\u65e5\uff0c\u8d5b\u603b\u7ba1\u9080\u4e86\u6211\u4eec\u5341\u516b\u4e2a\u4f8d\u536b\u5230\u603b\u7ba1\u5e9c\u53bb\u5403\u665a\u996d\u3002\u8fd9\u5341\u516b\u4e2a\u4eba\u54ea\uff0c\u5916\u8fb9\u670b\u53cb\u9001\u6211\u4eec\u4e00\u4e2a\u5916\u53f7\uff0c\u53eb\u4f5c\u2018\u5927\u5185\u5341\u516b\u9ad8\u624b\u2019\u3002\u5176\u5b9e\u51ed\u6211\u4eec\u8fd9\u4e00\u70b9\u513f\u4e09\u811a\u732b\u672c\u4e8b\uff0c\u54ea\u91cc\u8bf4\u5f97\u4e0a\u2018\u9ad8\u624b\u2019\u4e8c\u5b57\uff1f\u4e0d\u8fc7\u670b\u53cb\u4eec\u8981\u8fd9\u4e48\u53eb\uff0c\u8981\u7ed9\u6211\u4eec\u8138\u4e0a\u8d34\u91d1\uff0c\u90a3\u4e5f\u6ca1\u6709\u6cd5\u513f\uff0c\u662f\u4e0d\u662f\uff1f \u3000\u3000\u201c\u6211\u4eec\u4e00\u5230\uff0c\u8d5b\u603b\u7ba1\u5c31\u8bf4\uff0c\u4eca\u65e5\u8981\u7ed9\u5927\u4f19\u513f\u5f15\u89c1\u4e00\u4f4d\u6b66\u6797\u4e2d\u54cd\u5f53\u5f53\u7684\u811a\u8272\u3002\u6211\u4eec\u5fd9\u95ee\u662f\u8c01\uff0c\u8d5b\u603b\u7ba1\u5fae\u7b11\u4e0d\u8bf4\u3002\u5f85\u4f1a\u5f00\u4e86\u9152\u5e2d\uff0c\u8d5b\u603b\u7ba1\u5230\u5185\u5802\u5f15\u51fa\u4e00\u4e2a\u4eba\u6765\u3002\u53ea\u89c1\u4ed6\u8170\u677f\u7b14\u633a\uff0c\u6b65\u5c65\u77eb\u5065\uff0c\u53cc\u76ee\u6709\u795e\uff0c\u679c\u7136\u662f\u4e00\u6d3e\u6b66\u6797\u9ad8\u624b\u7684\u98ce\u8303\u3002\u4ed6\u4e24\u9b13\u867d\u5df2\u7070\u767d\uff0c\u4f46\u9762\u76ee\u4ecd\u662f\u6781\u4e3a\u82f1\u4fca\u6e05\u79c0\uff0c\u60f3\u5f53\u5e74\u5b9a\u662f\u4e00\u4f4d\u7f8e\u7537\u5b50\u3002 \u3000\u3000\u8d5b\u603b\u7ba1\u6717\u58f0\u9053\uff1a\u2018\u5404\u4f4d\u5144\u5f1f\uff0c\u8fd9\u4f4d\u662f\u5929\u9f99\u95e8\u5317\u5b97\u638c\u95e8\uff0c\u6b66\u6797\u4e2d\u5927\u5927\u6709\u540d\u7684\u4eba\u7269\uff0c\u7530\u5f52\u519c\u7530\u5927\u54e5\uff01\u2019\u201c\u6211\u4eec\u4e00\u542c\uff0c\u90fd\u662f\u5fae\u5fae\u4e00\u60ca\u3002\u7530\u5f52\u519c\u7684\u540d\u5934\u5927\u5bb6\u90fd\u662f\u77e5\u9053\u7684\uff0c\u53ea\u662f\u5929\u9f99\u95e8\u7d20\u6765\u5c11\u8ddf\u5b98\u5e9c\u5f80\u6765\uff0c\u4e0d\u77e5\u8d5b\u603b\u7ba1\u51ed\u4e86\u4ec0\u4e48\u9762\u5b50\u80fd\u628a\u4ed6\u8bf7\u5230\u3002\u996e\u9152\u4e2d\u95f4\uff0c\u5927\u4f19\u513f\u9010\u4e00\u5411\u4ed6\u628a\u76cf\u656c\u9152\u3002\u7530\u5927\u54e5\u4e5f\u662f\u5ba2\u6c14\u4e4b\u6781\uff0c\u8bf4\u4e86\u8bb8\u591a\u5957\u4ea4\u60c5\u7684\u8a00\u8bed\uff0c\u53ef\u4e00\u53e5\u4e0d\u63d0\u4ed6\u4e0a\u4eac\u7684\u539f\u56e0\u3002\u76f4\u5230\u5403\u559d\u5b8c\u4e86\uff0c\u8d5b\u603b\u7ba1\u9080\u5927\u4f19\u513f\u5230\u53a2\u623f\u559d\u8336\uff0c\u4ed6\u4e24\u4eba\u624d\u628a\u5176\u4e2d\u539f\u59d4\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u201c\u539f\u6765\u7530\u5927\u54e5\u867d\u7136\u8eab\u5728\u8349\u83bd\uff0c\u53ef\u662f\u5fe0\u541b\u62a5\u56fd\u4e4b\u5fc3\uff0c\u5374\u4e00\u70b9\u6ca1\u6bd4\u6211\u4eec\u5f53\u5dee\u7684\u5c11\u4e86\u3002 \u3000\u3000\u201c\u4ed6\u8fd9\u6b21\u4e0a\u4eac\uff0c\u4e3a\u7684\u662f\u8981\u5411\u7687\u4e0a\u8fdb\u8d21\u4e00\u4e2a\u5927\u5b9d\u85cf\u3002\u8fd9\u5927\u5b9d\u85cf\u561b\uff0c\u90a3\u5c31\u662f\u53cd\u8d3c\u674e\u81ea\u6210\u5728\u5317\u4eac\u6240\u641c\u522e\u7684\u91d1\u94f6\u8d22\u5b9d\u4e86\u3002\u7530\u5927\u54e5\u8bf4\u9053\uff0c\u8981\u627e\u5bfb\u8fd9\u4e2a\u5b9d\u85cf\uff0c\u5171\u6709\u4e24\u4e2a\u7ebf\u7d22\uff0c\u987b\u5f97\u4e24\u4e2a\u7ebf\u7d22\u62fc\u51d1\u8d77\u6765\uff0c\u65b9\u80fd\u5bfb\u5230\u3002\u4e00\u4e2a\u7ebf\u7d22\u662f\u674e\u81ea\u6210\u7684\u4e00\u628a\u519b\u5200\uff0c\u90a3\u662f\u4ed6\u5929\u9f99\u95e8\u638c\u7ba1\uff0c\u4ed6\u5c31\u643a\u5e26\u5728\u8eab\u3002\u53e6\u4e00\u4e2a\u7ebf\u7d22\u53ef\u5c31\u96be\u4e86\uff0c\u90a3\u662f\u4e00\u5e45\u5b9d\u85cf\u6240\u5728\u7684\u5730\u56fe\uff0c\u81ea\u6765\u7531\u82d7\u5bb6\u5251\u82d7\u5bb6\u4e16\u4ee3\u76f8\u4f20\u3002\u5355\u6709\u5730\u56fe\u800c\u65e0\u519b\u5200\uff0c\u4e0d\u77e5\u5bfb\u5b9d\u5173\u952e\uff1b\u5355\u6709\u519b\u5200\u800c\u65e0\u5730\u56fe\uff0c\u4e0d\u77e5\u5b9d\u85cf\u7684\u6240\u5728\u3002\u82e5\u662f\u4e8c\u5b9d\u5408\u74a7\uff0c\u53d6\u90a3\u5b9d\u85cf\u5c31\u5982\u63a2\u56ca\u53d6\u7269\u4e00\u822c\u3002 \u3000\u3000\u201c\u6211\u4eec\u867d\u5728\u5b98\u5bb6\u5f53\u5dee\uff0c\u53ef\u4e2a\u4e2a\u51fa\u8eab\u6b66\u6797\uff0c\u4e00\u542c\u5230\u2018\u82d7\u5bb6\u5251\u2019\u4e09\u5b57\uff0c\u90fd\u60f3\uff1a\u2018\u90a3\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u91d1\u9762\u4f5b\u82d7\u4eba\u51e4\u4f55\u7b49\u5389\u5bb3\uff0c\u8c01\u6562\u60f9\u4ed6\uff1f\u2019\u7530\u5927\u54e5\u89c1\u6211\u4eec\u8138\u73b0\u96be\u8272\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u2018\u5728\u4e0b\u82e5\u4e0d\u662f\u5df2\u7ecf\u60f3\u5230\u4e86\u5bf9\u4ed8\u82d7\u4eba\u51e4\u7684\u8ba1\u7b56\uff0c\u53c8\u600e\u6562\u8f7b\u6613\u524d\u6765\u60ca\u52a8\u5404\u4f4d\uff1f\u2019\u8d5b\u603b\u7ba1\u5fd9\u95ee\u4f55\u8ba1\u3002\u7530\u5927\u54e5\u4e8e\u662f\u8bf4\u51fa\u4e00\u756a\u8bdd\u6765\uff0c\u53ea\u628a\u4f17\u4eba\u542c\u5f97\u8fde\u8fde\u70b9\u5934\uff0c\u9f50\u53eb\u5999\u8ba1\u3002\u4ed6\u5230\u5e95\u8bf4\u7684\u662f\u4ec0\u4e48\u5999\u8ba1\uff0c\u65f6\u5019\u4e00\u5230\uff0c\u5404\u4f4d\u81ea\u7136\u77e5\u6653\uff0c\u6b64\u523b\u4e5f\u4e0d\u5fc5\u591a\u8bf4\u3002 \u3000\u3000\u201c\u6b21\u65e5\u7530\u5927\u54e5\u544a\u522b\u79bb\u4eac\uff0c\u8d5b\u603b\u7ba1\u5c31\u6d3e\u6211\u4eec\u4f9d\u8ba1\u800c\u884c\u3002\u4ed6\u4e00\u9762\u7422\u78e8\u6b64\u4e8b\uff0c\u603b\u89c9\u7530\u5927\u54e5\u4e00\u4e0d\u60f3\u5347\u5b98\u3001\u4e8c\u4e0d\u60f3\u53d1\u8d22\uff0c\u5e73\u767d\u65e0\u7aef\u9001\u6211\u4eec\u8fd9\u6837\u4e00\u4efd\u5927\u793c\uff0c\u5929\u4e0b\u54ea\u6709\u8fd9\u7b49\u597d\u4eba\uff1f\u6599\u5f97\u5176\u4e2d\u5fc5\u6709\u522b\u56e0\uff0c\u4e8e\u662f\u6d3e\u4e86\u51e0\u4e2a\u4eba\u6697\u4e2d\u51fa\u4eac\u6253\u63a2\u3002\u6211\u79bb\u4eac\u4e0d\u4e45\uff0c\u5c31\u542c\u5230\u7530\u5927\u54e5\u95ed\u95e8\u5c01\u5251\u7684\u8baf\u606f\uff0c\u5f53\u4e0b\u5907\u4e86\u4e00\u4efd\u793c\u7269\uff0c\u4e0a\u95e8\u9053\u8d3a\u3002 \u3000\u3000\u201c\u548c\u7530\u5927\u54e5\u4e00\u89c1\u9762\uff0c\u4ed6\u663e\u5f97\u5341\u5206\u6b22\u559c\uff0c\u8bf4\u9053\u8d35\u5ba2\u4e0a\u95e8\uff0c\u771f\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u8ddf\u7740\u6084\u6084\u7684\u8981\u6211\u529e\u4e00\u4ef6\u4e8b\u3002\u6bb7\u5927\u54e5\uff0c\u8bf4\u51fa\u6765\u4f60\u53ef\u522b\u751f\u6c14\uff0c\u4ed6\u662f\u8981\u6211\u77e5\u4f1a\u5b98\u5e9c\uff0c\u968f\u4fbf\u8bec\u9677\u4f60\u4e00\u4e2a\u7f6a\u540d\uff0c\u5c06\u4f60\u62ff\u5728\u72f1\u91cc\uff0c\u5148\u5173\u4e0a\u51e0\u5e74\u518d\u8bf4\u3002\u201d \u3000\u3000\u6bb7\u5409\u5413\u4e86\u4e00\u8df3\uff0c\u6d51\u8eab\u6c57\u6bdb\u76f4\u7ad6\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u7530\u5e08\u5144\u4e3a\u4eba\u539f\u662f\u5982\u6b64\uff0c\u5e78\u8499\u5218\u5927\u4eba\u660e\u9274\uff0c\u9ad8\u62ac\u8d35\u624b\uff0c\u5c0f\u7684\u5fc5\u6709\u539a\u62a5\u3002\u201d \u3000\u3000\u5218\u5143\u9e64\u7b11\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\u3002\u5f53\u65f6\u6211\u5c31\u95ee\u4ed6\u8ddf\u6bb7\u5927\u54e5\u6709\u751a\u4ec7\u6028\u3002\u4ed6\u9053\uff0c\u4ec7\u6028\u662f\u6ca1\u6709\uff0c\u53ea\u662f\u4f9d\u4ed6\u4eec\u5929\u9f99\u95e8\u89c4\u77e9\uff0c\u5317\u5b97\u638c\u95e8\u4eba\u8f6e\u503c\u638c\u5200\u7684\u671f\u9650\u5df2\u6ee1\uff0c\u90a3\u628a\u9547\u95e8\u4e4b\u5b9d\u7684\u5b9d\u5200\u5c31\u987b\u4f20\u7ed9\u5357\u5b97\uff0c\u7247\u523b\u5ef6\u6328\u4e0d\u5f97\u3002\u82e5\u662f\u843d\u5230\u6bb7\u5927\u54e5\u624b\u91cc\uff0c\u518d\u8981\u7d22\u56de\uff0c\u4e0d\u514d\u5c31\u591a\u4e00\u756a\u5468\u6298\u3002 \u3000\u3000\u201c\u8fd9\u8bdd\u867d\u662f\u4e0d\u9519\uff0c\u53ef\u662f\u6211\u4e0d\u7531\u5f97\u7591\u5fc3\u66f4\u751a\uff0c\u5f53\u65f6\u8ddf\u4ed6\u552f\u552f\u5426\u5426\uff0c\u65e2\u4e0d\u7b54\u5e94\uff0c\u4e5f\u4e0d\u62d2\u5374\uff0c\u53ea\u662f\u5728\u4e00\u8fb9\u53a2\u51b7\u773c\u65c1\u89c2\u3002 \u3000\u3000\u201c\u9152\u7b75\u4e4b\u540e\uff0c\u6211\u60f3\u7530\u5927\u54e5\u8fd9\u628a\u5b9d\u5200\u975e\u4ea4\u4e0d\u53ef\uff0c\u96be\u4ee5\u63a8\u6258\uff0c\u6211\u5012\u6709\u6cd5\u513f\u7ed9\u4ed6\u5e2e\u4e2a\u5fd9\u3002\u82e5\u662f\u6211\u6697\u4e2d\u5c06\u5b9d\u5200\u6536\u8d77\uff0c\u4ed6\u81ea\u7136\u65e0\u6cd5\u4ea4\u51fa\uff0c\u6bb7\u5927\u54e5\u7eb5\u7136\u4e0d\u6ee1\uff0c\u5374\u4e5f\u65e0\u8ba1\u53ef\u65bd\u3002\u8fd9\u6b63\u662f\u6211\u7acb\u5927\u529f\u62a5\u5723\u6069\u7684\u826f\u673a\uff0c\u5c82\u80fd\u8f7b\u6613\u653e\u8fc7\uff1f\u4e8e\u662f\u6211\u6084\u6084\u8d70\u8fdb\u7530\u5927\u54e5\u623f\u4e2d\uff0c\u5f85\u8981\u627e\u5bfb\u5b9d\u5200\uff0c\u5374\u542c\u5f97\u95e8\u5916\u811a\u6b65\u58f0\u54cd\uff0c\u539f\u6765\u662f\u7530\u5927\u54e5\u56de\u6765\u4e86\u3002 \u3000\u3000\u4e8b\u6025\u4e4b\u9645\uff0c\u53ea\u5f97\u8eb2\u5165\u4e86\u5e8a\u4e0b\u3002 \u3000\u3000\u201c\u53ea\u542c\u5f97\u7530\u5927\u54e5\u8d70\u8fdb\u623f\u6765\uff0c\u6253\u5f00\u7bb1\u5b50\uff0c\u53d6\u51fa\u94c1\u76d2\uff0c\u7a81\u7136\u60ca\u547c\uff1a\u2018\u54a6\uff0c\u5200\u5462\uff1f\u2019\u542c\u4ed6\u8fd9\u547c\u58f0\u60ca\u60f6\u5f02\u5e38\uff0c\u5b9e\u975e\u4f5c\u5047\uff0c\u770b\u6765\u8fd9\u5b9d\u5200\u662f\u7ed9\u4eba\u76d7\u53bb\u4e86\u3002\u4ed6\u7acb\u65f6\u53eb\u4e86\u5973\u513f\u6765\u67e5\u95ee\uff0c\u7530\u59d1\u5a18\u6beb\u4e0d\u77e5\u60c5\uff0c\u4e5f\u5f88\u7740\u6025\u3002\u4e0d\u4e45\u962e\u5927\u54e5\u8fdb\u6765\u4e86\u3002\u5e08\u5144\u5f1f\u4fe9\u4e3a\u4e86\u7acb\u638c\u95e8\u7684\u4e8b\u5927\u8d77\u4e89\u6267\uff0c\u63d0\u5230\u4e86\u66f9\u4e91\u5947\u66f9\u5e08\u5144\u4e0e\u7530\u59d1\u5a18\u7684\u66a7\u6627\u4e4b\u4e8b\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u7530\u5927\u54e5\u8981\u962e\u5927\u54e5\u53bb\u53eb\u9676\u5b50\u5b89\u9676\u4e16\u5144\u6765\u3002 \u3000\u3000\u201c\u7530\u5927\u54e5\u5c06\u94c1\u76d2\u4ea4\u7ed9\u9676\u4e16\u5144\uff0c\u547d\u4ed6\u53bb\u57cb\u5728\u5173\u5916\u3002\u6211\u5728\u5e8a\u4e0b\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u6697\u60f3\u9676\u5b50\u5b89\u8fd9\u50bb\u74dc\u8fd9\u756a\u53ef\u4e0a\u4e86\u5927\u5f53\u3002 \u3000\u3000\u201c\u9676\u4e16\u5144\u8d70\u540e\uff0c\u6211\u5728\u5e8a\u4e0b\u542c\u5f97\u7530\u5927\u54e5\u53ea\u662f\u6376\u5e8a\u53f9\u606f\uff0c\u5583\u5583\u81ea\u8bed\uff1a\u2018\u597d\u80e1\u4e00\u5200\uff0c\u597d\u82d7\u4eba\u51e4\uff01\u2019\u5f53\u65f6\u6211\u4e0d\u77e5\u80e1\u4e00\u5200\u662f\u8c01\uff0c\u6599\u60f3\u662f\u82d7\u4eba\u51e4\u76d7\u4e86\u4ed6\u7684\u5200\u53bb\u3002\u5374\u539f\u6765\u4ed6\u63a5\u5230\u4e86\u80e1\u4e00\u5200\u4e4b\u5b50\u80e1\u6590\u7684\u62dc\u5e16\uff0c\u81ea\u77e5\u96be\u9003\u4e00\u6b7b\uff0c\u662f\u4ee5\u5341\u5206\u60f6\u6050\u3002\u4f46\u8fd9\u65f6\u5019\u504f\u5de7\u5931\u4e86\u5b9d\u5200\uff0c\u53c8\u4e0d\u80fd\u5c31\u6b64\u9ad8\u98de\u8fdc\u8d70\uff0c\u4e00\u6e9c\u4e86\u4e4b\u3002 \u3000\u3000\u201c\u8ddf\u7740\u7530\u59d1\u5a18\u8d70\u8fdb\u623f\u6765\uff0c\u8bf4\u9053\uff1a\u2018\u7239\uff0c\u6211\u67e5\u5230\u4e86\u4f60\u5b9d\u5200\u7684\u4e0b\u843d\u3002\u2019\u7530\u5927\u54e5\u4e00\u8dc3\u800c\u8d77\uff0c\u53eb\u9053\uff1a\u2018\u5728\u54ea\u91cc\uff1f\u2019\u7530\u59d1\u5a18\u8d70\u8fd1\u51e0\u6b65\uff0c\u8f7b\u58f0\u9053\uff1a\u2018\u7ed9\u5468\u5e08\u5144\u5077\u53bb\u4e86\u3002\u2019\u7530\u5927\u54e5\u9053\uff1a\u2018\u5f53\u771f\uff1f\u4ed6\u4eba\u5462\uff1f\u5200\u5462\uff1f\u2019\u7530\u59d1\u5a18\u9053\uff1a\u2018\u6211\u4eb2\u773c\u89c1\u5230\u4ed6\u5c06\u5200\u57cb\u5728\u4e00\u4e2a\u5904\u6240\u3002\u2019\u7530\u5927\u54e5\u9053\uff1a\u2018\u597d\uff0c\u4f60\u5feb\u53bb\u6398\u6765\u3002\u2019\u7530\u59d1\u5a18\u9053\uff0c\u2018\u7239\uff0c\u6211\u8981\u505a\u4e00\u4ef6\u4e8b\uff0c\u4f60\u53ef\u83ab\u602a\u6211\u3002\u2019\u7530\u5927\u54e5\u9053\uff1a\u2018\u4ec0\u4e48\u4e8b\uff1f\u2019\u7530\u59d1\u5a18\u9053\uff1a\u2018\u4f60\u53bb\u628a\u5468\u5e08\u5144\u53eb\u6765\uff0c\u6211\u8eb2\u5728\u95e8\u540e\u3002\u4f60\u95ee\u4ed6\u662f\u4e0d\u662f\u76d7\u4e86\u5b9d\u5200\u3002\u4ed6\u82e5\u8ba4\u4e86\uff0c\u6211\u5c31\u5728\u4ed6\u80cc\u4e0a\u9489\u4e00\u679a\u6bd2\u9f99\u9525\u3002\u2019\u6211\u5fc3\u91cc\u60f3\uff0c\u8fd9\u4f4d\u59d1\u5a18\u7684\u624b\u6bb5\u597d\u72e0\u554a\u3002\u53ea\u542c\u7530\u5927\u54e5\u9053\uff1a\u2018\u6211\u6253\u6298\u4ed6\u53cc\u817f\u5c31\u662f\uff0c\u4e0d\u5fc5\u53d6\u4ed6\u6027\u547d\u3002\u2019\u7530\u59d1\u5a18\u9053\uff1a\u2018\u4f60\u4e0d\u4f9d\u6211\uff0c\u6211\u5c31\u4e0d\u7ed9\u4f60\u53d6\u5200\u3002\u2019\u7530\u5927\u54e5\u5fae\u4e00\u8fdf\u7591\uff0c\u9053\uff1a\u2018\u597d\uff0c\u4f60\u5feb\u53bb\u53d6\u4e86\u5200\u6765\uff0c\u51ed\u4f60\u600e\u4e48\u5904\u7f6e\u4ed6\u3002\u2019\u4e8e\u662f\u7530\u59d1\u5a18\u8f6c\u8eab\u51fa\u53bb\u3002\u5f53\u65f6\u6211\u4e0d\u77e5\u7530\u59d1\u5a18\u8ddf\u5979\u5e08\u5144\u6709\u4ec0\u4e48\u4ec7\u6028\uff0c\u4eca\u65e5\u542c\u4e86\u9676\u5e08\u5144\u4e4b\u8a00\uff0c\u65b9\u77e5\u7530\u59d1\u5a18\u662f\u8981\u6740\u4eba\u706d\u53e3\u3002\u563f\uff0c\u597d\u5bb6\u4f19\uff01\u4eba\u5bb6\u5927\u59d1\u5a18\u63a9\u57cb\u79c1\u751f\u513f\u5b50\uff0c\u8fd9\u79cd\u4e8b\u4e5f\u89c1\u5f97\u7684\uff1f\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u4f17\u4eba\u90fd\u8f6c\u773c\u53bb\u77a7\u5468\u4e91\u9633\uff0c\u53ea\u89c1\u4ed6\u8138\u8272\u94c1\u9752\uff0c\u53cc\u76ee\u4e0d\u4f4f\u7728\u52a8\u3002 \u3000\u3000\u53c8\u542c\u5218\u5143\u9e64\u7eed\u9053\uff1a\u201c\u6211\u7d22\u6027\u5728\u5e8a\u4e0b\u5367\u5012\uff0c\u9759\u7b49\u77a7\u8fd9\u5e55\u6740\u4eba\u7684\u6d3b\u5267\uff0c\u518d\u5219\uff0c\u6211\u8fd8\u5f97\u7b49\u90a3\u67c4\u5200\u5462\uff0c\u4f55\u51b5\u7530\u5927\u54e5\u9192\u7740\u8eba\u5728\u5e8a\u4e0a\uff0c\u6211\u53c8\u600e\u80fd\u51fa\u53bb\uff1f\u7b49\u4e86\u6ca1\u591a\u4e45\uff0c\u7530\u59d1\u5a18\u5306\u5306\u56de\u6765\uff0c\u98a4\u58f0\u9053\uff1a\u2018\u7239\uff0c\u90a3\u5200\u7ed9\u4ed6\u6398\u53bb\u5566\u3002\u6211\u597d\u80e1\u6d82\uff0c\u7adf\u8fdf\u4e86\u4e00\u6b65\uff0c\u4ed6\u2026\u2026\u4ed6\u8fd8\u2026\u2026\u2019\u7530\u5927\u54e5\u60ca\u6050\u4ea4\u96c6\uff0c\u95ee\u9053\uff1a\u2018\u4ed6\u8fd8\u600e\u4e48\uff1f\u2019\u7530\u59d1\u5a18\u5176\u5b9e\u60f3\u8bf4\uff1a\u2018\u4ed6\u8fde\u6211\u5b69\u513f\u7684\u5c38\u4f53\u4e5f\u6398\u53bb\u5566\uff01\u2019\u4f46\u8fd9\u53e5\u8bdd\u600e\u8bf4\u5f97\u51fa\u53e3\uff0c\u5446\u4e86\u4e00\u5446\uff0c\u53eb\u9053\uff1a\u2018\u6211\u627e\u4ed6\u53bb\uff01\u2019\u62d4\u8db3\u6025\u5954\u800c\u51fa\uff0c\u60f3\u662f\u60ca\u6050\u8fc7\u751a\uff0c\u5954\u5230\u95e8\u8fb9\u65f6\u7adf\u4e00\u4ea4\u6454\u5012\u3002 \u3000\u3000\u201c\u6211\u5728\u5e8a\u4e0b\u618b\u5f97\u6c14\u95f7\uff0c\u5b9d\u5200\u53c8\u4e0d\u660e\u4e0b\u843d\uff0c\u672c\u60f3\u4e58\u673a\u6253\u706d\u70db\u706b\u9003\u53bb\uff0c\u54ea\u77e5\u7530\u5927\u54e5\u89c1\u5979\u5973\u513f\u6454\u5012\uff0c\u53ea\u53f9\u4e86\u53e3\u957f\u6c14\uff0c\u5374\u4e0d\u4e0b\u5e8a\u53bb\u6276\u3002\u7530\u59d1\u5a18\u7ad9\u8d77\u8eab\u6765\uff0c\u6276\u7740\u95e8\u6846\u5598\u606f\u4e00\u4f1a\u65b9\u8d70\u3002 \u3000\u3000\u201c\u7530\u5927\u54e5\u4e0b\u5e8a\u53bb\u5173\u4e0a\u95e8\u7a97\uff0c\u5750\u5728\u6905\u4e0a\u3002\u4f46\u89c1\u4ed6\u5c06\u957f\u5251\u653e\u5728\u684c\u4e0a\uff0c\u624b\u91cc\u62ff\u4e86\u5f13\u7bad\uff0c\u94c1\u9752\u7740\u8138\uff0c\u795e\u8272\u6781\u662f\u6015\u4eba\u3002\u6211\u5fc3\u4e2d\u4e5f\u662f\u60f4\u60f4\u4e0d\u5b89\uff0c\u8981\u662f\u7ed9\u4ed6\u53d1\u89c9\u4e86\uff0c\u4ed6\u4e00\u4e2a\u7ffb\u8138\u65e0\u60c5\uff0c\u6211\u6b66\u529f\u4e0d\u53ca\uff0c\u53ea\u6015\u6027\u547d\u96be\u4fdd\u3002 \u3000\u3000\u201c\u7530\u5927\u54e5\u5750\u5728\u6905\u4e0a\uff0c\u7adf\u4e00\u52a8\u4e5f\u4e0d\u52a8\uff0c\u5b9b\u5982\u50f5\u76f4\u4e86\u4e00\u822c\uff0c\u4f46\u53cc\u76ee\u5374\u662f\u7cbe\u5149\u95ea\u70c1\uff0c\u663e\u5f97\u5fc3\u4e0b\u6781\u4e3a\u70e6\u8e81\u4e0d\u5b89\u3002\u56db\u4e0b\u4e00\u7247\u6b7b\u5bc2\uff0c\u53ea\u542c\u5f97\u8fdc\u5904\u9690\u9690\u6709\u72ac\u5420\u4e4b\u58f0\uff0c\u63a5\u7740\u8fd1\u5904\u4e00\u53ea\u72d7\u4e5f\u5420\u4e86\u8d77\u6765\uff0c\u7a81\u7136\u4e4b\u95f4\uff0c\u8fd9\u72d7\u513f\u60b2\u5420\u4e00\u58f0\uff0c\u7acb\u65f6\u4f4f\u53e3\uff0c\u4f3c\u662f\u88ab\u4eba\u7528\u6781\u5feb\u624b\u6cd5\u5f04\u6b7b\u4e86\u3002\u7530\u5927\u54e5\u731b\u5730\u7ad9\u8d77\uff0c\u623f\u95e8\u4e0a\u5374\u8d77\u4e86\u51e0\u4e0b\u6572\u51fb\u4e4b\u58f0\u3002\u8fd9\u58f0\u97f3\u6765\u5f97\u597d\u5feb\uff0c\u542c\u90a3\u72d7\u513f\u5420\u53eb\u58f0\u97f3\u603b\u5728\u6570\u5341\u4e08\u5916\uff0c\u5c82\u77e5\u8fd9\u4eba\u4e00\u5f04\u6b7b\u72d7\u513f\uff0c\u8f6c\u77ac\u95f4\u5c31\u5230\u4e86\u95e8\u5916\u3002 \u3000\u3000\u201c\u7530\u5927\u54e5\u4f4e\u6c89\u7740\u58f0\u97f3\u9053\uff1a\u2018\u80e1\u6590\uff0c\u4f60\u7ec8\u4e8e\u6765\u4e86\uff1f\u2019\u95e8\u5916\u90a3\u4eba\u5374\u9053\uff1a\u2018\u7530\u5f52\u519c\uff0c\u4f60\u8ba4\u5f97\u6211\u58f0\u97f3\u4e48\uff1f\u2019\u7530\u5927\u54e5\u8138\u8272\u66f4\u662f\u82cd\u767d\uff0c\u98a4\u58f0\u9053\uff1a\u2018\u82d7\u2026\u2026\u82d7\u5927\u4fa0\uff01\u2019\u95e8\u5916\u90a3\u4eba\u9053\uff1a\u2018\u4e0d\u9519\uff0c\u662f\u6211\uff01\u2019\u7530\u5927\u54e5\u9053\uff1a\u2018\u82d7\u5927\u4fa0\uff0c\u4f60\u6765\u5e72\u4ec0\u4e48\uff1f\u2019\u95e8\u5916\u90a3\u4eba\u9053\uff1a\u2018\u54fc\uff0c\u6211\u7ed9\u4f60\u9001\u4e1c\u897f\u6765\u5566\uff01\u2019\u7530\u5927\u54e5\u8fdf\u7591\u7247\u523b\uff0c\u653e\u4e0b\u5f13\u7bad\uff0c\u53bb\u5f00\u4e86\u95e8\u3002\u53ea\u89c1\u4e00\u4e2a\u53c8\u9ad8\u53c8\u7626\u3001\u8138\u8272\u8721\u9ec4\u7684\u6c49\u5b50\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u201c\u6211\u5728\u5e8a\u5e95\u7559\u795e\u77a7\u4ed6\u6a21\u6837\uff0c\u5fc3\u9053\uff1a\u2018\u6b64\u4eba\u53f7\u79f0\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\uff0c\u662f\u5f53\u4eca\u6b66\u6797\u4e2d\u9876\u513f\u5c16\u513f\u7684\u811a\u8272\uff0c\u679c\u7136\u662f\u4e0d\u6012\u81ea\u5a01\uff0c\u6c14\u52bf\u6151\u4eba\u3002\u2019\u53ea\u89c1\u4ed6\u624b\u91cc\u6367\u7740\u4e24\u4ef6\u7269\u4e8b\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u8bf4\u9053\uff1a\u2018\u8fd9\u662f\u4f60\u7684\u5b9d\u5200\uff0c\u8fd9\u662f\u4f60\u7684\u5916\u5b59\u513f\u5b50\u3002\u2019\u539f\u6765\u4e00\u5305\u957f\u957f\u7684\u4e1c\u897f\u7adf\u662f\u4e00\u4e2a\u6b7b\u5a74\u3002 \u3000\u3000\u201c\u7530\u5927\u54e5\u8eab\u5b50\u4e00\u98a4\uff0c\u5012\u5728\u6905\u4e2d\u3002\u82d7\u5927\u4fa0\u9053\uff1a\u2018\u4f60\u5f92\u5f1f\u7792\u7740\u4f60\u53bb\u57cb\u5200\uff0c\u4f60\u5973\u513f\u7792\u7740\u4f60\u53bb\u57cb\u79c1\u751f\u513f\uff0c\u90fd\u7ed9\u6211\u77a7\u89c1\u5566\uff0c\u73b0\u4e0b\u6398\u4e86\u51fa\u6765\u8fd8\u4f60\u3002\u2019\u7530\u5927\u54e5\u9053\uff1a\u2018\u8c22\u8c22\u3002\u6211\u2026\u2026\u6211\u5bb6\u95e8\u4e0d\u5e78\uff0c\u8a00\u4e4b\u6709\u6127\u3002\u2019\u82d7\u5927\u4fa0\u7a81\u7136\u773c\u7736\u4e00\u7ea2\uff0c\u4f3c\u8981\u6d41\u6cea\uff0c\u4f46\u968f\u5373\u6ee1\u8138\u6740\u6c14\uff0c\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u8bf4\u9053\uff1a\u2018\u5979\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u2019\u201d \u3000\u3000\u53ea\u542c\u5f97\u5f53\u5577\u4e00\u54cd\uff0c\u82d7\u82e5\u5170\u624b\u91cc\u7684\u8336\u7897\u6454\u5728\u5730\u4e0b\uff0c\u8dcc\u5f97\u7c89\u788e\u3002\u5979\u4e3e\u6b62\u672c\u6765\u5341\u5206\u65af\u6587\u9547\u5b9a\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u542c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u7adf\u81ea\u628a\u6301\u4e0d\u5b9a\u3002\u7434\u513f\u5fd9\u53d6\u51fa\u624b\u5e15\uff0c\u62b9\u53bb\u5979\u8eab\u4e0a\u8336\u6c34\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u8fdb\u53bb\u6b47\u6b47\u5427\uff0c\u522b\u542c\u5566\uff01\u201d\u82d7\u82e5\u5170\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u8981\u542c\u4ed6\u8bf4\u5b8c\u3002\u201d \u3000\u3000\u5218\u5143\u9e64\u5411\u5979\u671b\u4e86\u4e00\u773c\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u7530\u5927\u54e5\u9053\uff1a\u2018\u90a3\u5929\u5979\u53d7\u4e86\u51c9\uff0c\u4f24\u98ce\u54b3\u55fd\u3002\u6211\u8bf7\u533b\u751f\u7ed9\u5979\u8bca\u6cbb\uff0c\u533b\u751f\u8bf4\u4e0d\u788d\u4e8b\uff0c\u53ea\u662f\u53d7\u4e86\u4e9b\u5c0f\u5c0f\u98ce\u5bd2\uff0c\u5403\u4e00\u5e16\u836f\uff0c\u53d1\u6c57\u9000\u70e7\u5c31\u884c\u4e86\u3002\u53ef\u662f\u5979\u8bf4\u836f\u592a\u82e6\uff0c\u5c06\u714e\u597d\u7684\u836f\u6cfc\u4e86\u53bb\uff0c\u53c8\u4e0d\u80af\u5403\u996d\uff0c\u8fd9\u4e00\u6765\u75c5\u52bf\u8d8a\u6765\u8d8a\u6c89\u3002\u6211\u4e00\u8fde\u8bf7\u4e86\u597d\u51e0\u4e2a\u533b\u751f\uff0c\u4f46\u5979\u4e0d\u80af\u670d\u836f\uff0c\u4e0d\u5403\u4e1c\u897f\uff0c\u8bf4\u4ec0\u4e48\u4e5f\u529d\u4e0d\u542c\u3002\u2019\u201d \u3000\u3000\u82d7\u82e5\u5170\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u7531\u5f97\u8f7b\u8f7b\u555c\u6ce3\u3002\u718a\u5143\u732e\u7b49\u90fd\u611f\u5341\u5206\u5947\u602a\uff0c\u4e0d\u77e5\u8fd9\u4e0d\u80af\u670d\u836f\u5403\u996d\u4e4b\u4eba\u662f\u8c01\uff0c\u4e0e\u7530\u5f52\u519c\u53ca\u82d7\u6c0f\u7236\u5973\u4e09\u4eba\u53c8\u6709\u4ec0\u4e48\u5173\u8fde\u3002\u9676\u6c0f\u7236\u5b50\u4e0e\u5929\u9f99\u8bf8\u4eba\u5374\u77e5\u8bf4\u7684\u662f\u7530\u5f52\u519c\u7684\u7eed\u5f26\u592b\u4eba\uff0c\u4f46\u82d7\u5927\u4fa0\u4f55\u4ee5\u5173\u5fc3\u6b64\u4e8b\uff0c\u82d7\u82e5\u5170\u4f55\u4ee5\u4f24\u5fc3\uff0c\u5374\u53c8\u4e0d\u660e\u6240\u4ee5\u4e86\uff0c\u90fd\u60f3\uff1a\u201c\u96be\u9053\u7530\u592b\u4eba\u662f\u82d7\u5bb6\u4eb2\u621a\uff1f\u600e\u4e48\u6211\u4eec\u4ece\u6765\u6ca1\u542c\u8bf4\u8fc7\uff1f\u201d \u3000\u3000\u5218\u5143\u9e64\u9053\uff1a\u201c\u5f53\u65f6\u6211\u5728\u5e8a\u4e0b\u542c\u5f97\u6478\u4e0d\u7740\u534a\u70b9\u5934\u8111\uff0c\u4e0d\u77e5\u4ed6\u4eec\u8bf4\u7684\u662f\u8c01\uff0c\u5fc3\u60f3\u82d7\u4eba\u51e4\u8fd9\u4e48\u98ce\u5934\u706b\u52bf\u7684\u8d76\u6765\uff0c\u53ea\u4e0d\u8fc7\u662f\u95ee\u4e00\u4e2a\u4eba\u7684\u75c5\u3002\u90a3\u4eba\u4e0d\u670d\u836f\u3001\u4e0d\u5403\u996d\uff0c\u8fd9\u4e0d\u662f\u6492\u5a07\u4e48\uff1f\u4f46\u542c\u82d7\u5927\u4fa0\u53c8\u95ee\uff1a\u2018\u8fd9\u4e48\u8bf4\u6765\uff0c\u662f\u5979\u81ea\u5df1\u4e0d\u60f3\u6d3b\u4e86\uff1f\u2019\u7530\u5927\u54e5\u9053\uff1a\u2018\u6211\u540e\u6765\u8dea\u5728\u5730\u4e0b\u54c0\u6c42\uff0c\u8bf4\u5f97\u58f0\u5636\u529b\u7aed\uff0c\u5979\u59cb\u7ec8\u4e0d\u7406\u3002\u2019\u201c\u82d7\u5927\u4fa0\u9053\uff1a\u2018\u5979\u7559\u4e0b\u4e86\u4ec0\u4e48\u8bdd\uff1f\u2019\u7530\u5927\u54e5\u9053\uff1a\u2018\u5979\u53eb\u6211\u5728\u5979\u6b7b\u540e\u5c06\u5c38\u4f53\u706b\u5316\u4e86\uff0c\u628a\u9aa8\u7070\u6492\u5728\u5927\u8def\u4e4b\u4e0a\uff0c\u53eb\u5343\u4eba\u8e29\uff0c\u4e07\u4eba\u8e0f\uff01\u2019\u82d7\u5927\u4fa0\u8df3\u4e86\u8d77\u6765\uff0c\u5389\u58f0\u9053\uff1a\u2018\u4f60\u7167\u5979\u7684\u8bdd\u505a\u4e86\u6ca1\u6709\uff1f\u2019\u7530\u5927\u54e5\u9053\uff1a\u2018\u5c38\u4f53\u662f\u706b\u5316\u4e86\uff0c\u9aa8\u7070\u5374\u5728\u8fd9\u91cc\u3002\u2019\u8bf4\u7740\u7ad9\u8d77\u8eab\u6765\uff0c\u4ece\u91cc\u5e8a\u53d6\u51fa\u4e00\u4e2a\u5c0f\u5c0f\u74f7\u575b\uff0c\u653e\u5728\u684c\u4e0a\u3002 \u3000\u3000\u201c\u82d7\u5927\u4fa0\u671b\u7740\u74f7\u575b\uff0c\u8138\u4e0a\u795e\u8272\u53c8\u662f\u4f24\u5fc3\u53c8\u662f\u6124\u6012\u3002\u6211\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u5c31\u4e0d\u6562\u518d\u671b\u4ed6\u7684\u8138\u3002 \u3000\u3000\u201c\u7530\u5927\u54e5\u53c8\u4ece\u6000\u91cc\u53d6\u51fa\u4e00\u679a\u51e4\u5934\u73e0\u9497\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u8bf4\u9053\uff1a\u2018\u5979\u8981\u6211\u628a\u8fd9\u73e0\u9497\u8fd8\u7ed9\u4f60\uff0c\u6216\u8005\u4ea4\u7ed9\u82d7\u59d1\u5a18\uff0c\u8bf4\u8fd9\u662f\u82d7\u5bb6\u7684\u7269\u4e8b\u3002\u2019\u201d \u3000\u3000\u4f17\u4eba\u542c\u5230\u6b64\u5904\uff0c\u9f50\u5411\u82d7\u82e5\u5170\u671b\u53bb\uff0c\u53ea\u89c1\u5979\u9b13\u8fb9\u63d2\u4e86\u4e00\u679a\u51e4\u5934\u73e0\u9497\uff0c\u5fae\u5fae\u6643\u52a8\u3002\u90a3\u51e4\u5934\u6253\u5f97\u7cbe\u81f4\u65e0\u6bd4\uff0c\u51e0\u9897\u73e0\u5b50\u4e5f\u662f\u6eda\u5706\u51c0\u6ed1\uff0c\u53ea\u662f\u73e0\u8eab\u5df2\u73b0\u5fae\u9ec4\uff0c\u4f3c\u662f\u5386\u65f6\u5df2\u4e45\u7684\u53e4\u7269\u3002 \u3000\u3000\u5218\u5143\u9e64\u7eed\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u62ff\u8d77\u73e0\u9497\uff0c\u4ece\u81ea\u5df1\u5934\u4e0a\u62d4\u4e0b\u4e00\u6839\u5934\u53d1\uff0c\u7f13\u7f13\u7a7f\u5230\u51e4\u5934\u7684\u53e3\u91cc\uff0c\u90a3\u5934\u53d1\u7adf\u4ece\u9497\u5c16\u4e0a\u900f\u4e86\u51fa\u6765\uff0c\u539f\u6765\u9497\u8eab\u4e2d\u95f4\u662f\u7a7a\u7684\u3002\u4f46\u89c1\u4ed6\u5c06\u5934\u53d1\u4e24\u7aef\u8f7b\u8f7b\u4e00\u62c9\uff0c\u51e4\u5934\u7684\u4e00\u8fb9\u8df3\u4e86\u5f00\u6765\u3002\u82d7\u5927\u4fa0\u4fa7\u8fc7\u73e0\u9497\uff0c\u4ece\u51e4\u5934\u91cc\u843d\u51fa\u4e00\u4e2a\u7eb8\u56e2\u3002\u4ed6\u5c06\u7eb8\u56e2\u644a\u4e86\u5f00\u6765\uff0c\u51b7\u51b7\u7684\u9053\uff1a\u2018\u77a7\u89c1\u4e86\u4e48\uff1f\u2019\u7530\u5927\u54e5\u8138\u5982\u571f\u8272\uff0c\u9694\u4e86\u534a\u664c\uff0c\u53f9\u4e86\u53e3\u957f\u6c14\u3002 \u3000\u3000\u201c\u82d7\u5927\u4fa0\u9053\uff1a\u2018\u4f60\u5343\u65b9\u767e\u8ba1\u8981\u5f04\u8fd9\u5f20\u5730\u56fe\u5230\u624b\uff0c\u53ef\u662f\u5979\u7ec8\u4e8e\u77a7\u7a7f\u4e86\u4f60\u7684\u771f\u9762\u76ee\uff0c\u4e0d\u80af\u5c06\u673a\u5bc6\u544a\u77e5\u4f60\uff0c\u4ecd\u5c06\u73e0\u9497\u5f52\u8fd8\u82d7\u5bb6\u3002\u5b9d\u85cf\u7684\u5730\u56fe\u662f\u5728\u8fd9\u73e0\u9497\u4e4b\u4e2d\uff0c\u54fc\uff0c\u53ea\u6015\u4f60\u4f5c\u68a6\u4e5f\u96be\u4ee5\u60f3\u5230\u7f62\uff01\u2019\u4ed6\u8bf4\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53c8\u5c06\u7eb8\u56e2\u8fd8\u5165\u51e4\u5934\uff0c\u7528\u5934\u53d1\u62c9\u4e0a\u673a\u62ec\uff0c\u5c06\u73e0\u9497\u653e\u5728\u684c\u4e0a\uff0c\u8bf4\u9053\uff1a\u2018\u5f00\u51e4\u5934\u7684\u6cd5\u513f\u6211\u6559\u4e86\u4f60\u5566\uff0c\u4f60\u62ff\u53bb\u6309\u56fe\u5bfb\u5b9d\u7f62\uff01\u2019\u7530\u5927\u54e5\u54ea\u91cc\u6562\u52a8\uff0c\u7d27\u95ed\u7740\u53e3\u4e00\u58f0\u4e0d\u54cd\u3002\u6211\u5728\u5e8a\u4e0b\u5374\u77a7\u5f97\u7126\u6025\u5f02\u5e38\uff0c\u5730\u56fe\u4e0e\u5b9d\u5200\u79bb\u5f00\u6211\u8eab\u5b50\u4e0d\u8fc7\u6570\u5c3a\uff0c\u53ef\u662f\u5c31\u6ca1\u6cd5\u53d6\u5f97\u5230\u624b\u3002\u53ea\u89c1\u82d7\u5927\u4fa0\u5446\u5446\u7684\u77a7\u7740\u74f7\u575b\uff0c\u6162\u6162\u4f38\u51fa\u53cc\u624b\u6367\u8d77\u4e86\u74f7\u575b\uff0c\u653e\u5165\u4e86\u6000\u4e2d\uff0c\u8138\u4e0a\u7684\u795e\u8272\u5341\u5206\u53ef\u6015\u3002\u201d \u3000\u3000\u53ea\u542c\u5f97\u8f7b\u8f7b\u4e00\u58f0\u547b\u541f\uff0c\u82d7\u82e5\u5170\u4f0f\u5728\u684c\u4e0a\u54ed\u4e86\u51fa\u6765\uff0c\u9b13\u8fb9\u90a3\u51e4\u5934\u73e0\u9497\u8d77\u4f0f\u98a4\u52a8\u4e0d\u5df2\u3002\u4f17\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u4e0d\u660e\u5176\u6545\u3002 \u3000\u3000\u5218\u5143\u9e64\u63a5\u7740\u9053\uff1a\u201c\u7530\u5927\u54e5\u4f38\u624b\u5728\u684c\u4e0a\u4e00\u62cd\uff0c\u9053\uff1a\u2018\u82d7\u5927\u4fa0\uff0c\u4f60\u52a8\u624b\u5427\uff0c\u6211\u6b7b\u800c\u65e0\u6028\u3002\u2019\u82d7\u5927\u4fa0\u563f\u563f\u4e00\u7b11\uff0c\u9053\uff1a\u2018\u6211\u4f55\u5fc5\u6740\u4f60\uff1f\u4e00\u4e2a\u4eba\u6d3b\u7740\uff0c\u5c31\u672a\u5fc5\u6bd4\u6b7b\u4e86\u7684\u4eba\u5feb\u6d3b\u3002\u60f3\u5f53\u5e74\u6211\u548c\u80e1\u4e00\u5200\u6bd4\u6b66\uff0c\u5927\u6218\u6570\u65e5\uff0c\u7ec8\u4e8e\u662f\u4ed6\u592b\u5987\u6b7b\u4e86\uff0c\u6211\u5374\u6d3b\u7740\u3002\u6211\u5fc3\u4e2d\u4e00\u76f4\u96be\u8fc7\uff0c\u4f46\u540e\u6765\u60f3\u60f3\uff0c\u4ed6\u592b\u5987\u6069\u7231\u4e0d\u6e1d\uff0c\u540c\u751f\u540c\u6b7b\uff0c\u53ef\u6bd4\u6211\u72ec\u4e2a\u513f\u6d3b\u5728\u4e16\u4e0a\u597d\u5f97\u591a\u5566\u3002\u563f\u563f\uff0c\u8fd9\u5f20\u5730\u56fe\u5728\u4f60\u8eab\u8fb9\u8fd9\u8bb8\u591a\u5e74\uff0c\u4f60\u59cb\u7ec8\u4e0d\u77e5\uff0c\u5374\u53c8\u4eb2\u624b\u4ea4\u8fd8\u7ed9\u6211\u3002\u6211\u4f55\u5fc5\u6740\u4f60\uff1f\u8ba9\u4f60\u61ca\u607c\u4e00\u8f88\u5b50\uff0c\u90a3\u4e0d\u662f\u5f3a\u5f97\u591a\u4e48\uff1f\u2019\u8bf4\u7740\u62ff\u8d77\u73e0\u9497\uff0c\u5927\u8e0f\u6b65\u51fa\u623f\u3002 \u3000\u3000\u7530\u5927\u54e5\u624b\u8fb9\u867d\u6709\u5f13\u7bad\u5200\u5251\uff0c\u5374\u54ea\u6562\u52a8\u624b\uff1f \u3000\u3000\u201c\u7530\u5927\u54e5\u5509\u58f0\u53f9\u6c14\uff0c\u5c06\u6b7b\u5a74\u548c\u5b9d\u5200\u90fd\u653e\u5728\u5e8a\u4e0a\uff0c\u56de\u8eab\u95e9\u4e0a\u4e86\u95e8\uff0c\u5583\u5583\u7684\u9053\uff1a\u2018\u4e00\u4e2a\u4eba\u6d3b\u7740\uff0c\u5c31\u672a\u5fc5\u6bd4\u6b7b\u4e86\u7684\u4eba\u5feb\u6d3b\u3002\u2019\u5750\u5728\u5e8a\u4e0a\uff0c\u53eb\u9053\uff1a\u2018\u5170\u554a\u5170\uff0c\u4f60\u4e3a\u6211\u5931\u8db3\uff0c\u6211\u4e3a\u4f60\u5931\u8db3\uff0c\u5f53\u771f\u662f\u4f55\u82e6\u6765\uff1f\u2019\u63a5\u7740\u563f\u7684\u4e00\u58f0\uff0c\u542c\u5f97\u4ec0\u4e48\u4e1c\u897f\u6233\u5165\u4e86\u8089\u91cc\uff0c\u4ed6\u5728\u5e8a\u4e0a\u6323\u4e86\u51e0\u6323\uff0c\u5c31\u6b64\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u201c\u6211\u5403\u4e86\u4e00\u60ca\uff0c\u5fd9\u4ece\u5e8a\u5e95\u94bb\u5c06\u51fa\u6765\uff0c\u53ea\u89c1\u4ed6\u5c06\u7fbd\u7bad\u63d2\u5728\u81ea\u5df1\u5fc3\u53e3\uff0c\u7adf\u5df2\u6c14\u7edd\u3002\u5404\u4f4d\uff0c\u7530\u5927\u54e5\u662f\u81ea\u5c3d\u6b7b\u7684\uff0c\u5e76\u975e\u65c1\u4eba\u7528\u7bad\u5c04\u6b7b\u3002\u5bb3\u6b7b\u4ed6\u7684\u65e2\u4e0d\u662f\u9676\u5b50\u5b89\uff0c\u66f4\u4e0d\u662f\u80e1\u6590\uff0c\u90a3\u662f\u4ed6\u81ea\u5df1\u3002 \u3000\u3000\u6211\u8ddf\u9676\u80e1\u4e8c\u4eba\u7edd\u65e0\u4ea4\u60c5\uff0c\u72af\u4e0d\u7740\u7ed9\u4ed6\u4eec\u5f00\u8131\u3002 \u3000\u3000\u201c\u6211\u89c1\u4ed6\u6b7b\u4e86\uff0c\u5f53\u4e0b\u5439\u706d\u70db\u706b\uff0c\u6b63\u60f3\u53bb\u62ff\u5b9d\u5200\uff0c\u7136\u540e\u6e9c\u4e4b\u5927\u5409\uff0c\u9676\u4e16\u5144\u5374\u5df2\u6765\u5230\u623f\u5916\u62cd\u95e8\uff0c\u6211\u53ea\u5f97\u8eb2\u56de\u5e8a\u5e95\u3002\u4ee5\u540e\u7684\u4e8b\uff0c\u9676\u4e16\u5144\u90fd\u5df2\u8bf4\u4e86\u3002\u4ed6\u62ff\u4e86\u5b9d\u5200\uff0c\u9003\u5230\u5173\u5916\u6765\u3002\u6211\u5728\u5e8a\u5e95\u4e0b\u618b\u4e86\u8fd9\u8001\u534a\u5929\uff0c\u96be\u9053\u662f\u767d\u6328\u7684\u4e48\uff1f\u52a0\u4e0a\u6211\u8fd9\u4f4d\u718a\u5e08\u5f1f\u8ddf\u996e\u9a6c\u5ddd\u5411\u6765\u6709\u6881\u5b50\uff0c\u54b1\u54e5\u513f\u5c31\u8ddf\u7740\u6765\u5566\u3002\u201d \u3000\u3000\u4ed6\u4e00\u756a\u8bdd\u8bf4\u5b8c\uff0c\u53cc\u624b\u62cd\u62cd\u8eab\u4e0a\u7070\u5c18\uff0c\u62c2\u4e86\u62c2\u5934\u9876\uff0c\u6070\u4f3c\u521a\u4ece\u5e8a\u5e95\u4e0b\u94bb\u51fa\u6765\u4e00\u822c\uff0c\u559d\u4e86\u4e24\u53e3\u8336\uff0c\u795e\u60c5\u751a\u662f\u8f7b\u677e\u81ea\u5f97\u3002 \u3000"}),showOnHomepage:!0},{name:"\u516b",content:(0,a.jsx)(a.Fragment,{children:"\u8fd9\u4e9b\u4eba\u4f60\u8bf4\u4e00\u6bb5\uff0c\u6211\u8bf4\u4e00\u6bb5\uff0c\u51d1\u5728\u4e00\u8d77\uff0c\u4f17\u4eba\u5fc3\u5934\u7591\u56e2\u5df2\u89e3\u4e86\u5927\u534a\uff0c\u53ea\u662f\u9965\u706b\u4e0a\u51b2\uff0c\u8336\u8d8a\u559d\u5f97\u591a\u8d8a\u662f\u809a\u997f\u3002 \u3000\u3000\u9676\u767e\u5c81\u5927\u58f0\u9053\uff1a\u201c\u73b0\u4e0b\u8bdd\u5df2\u8bf4\u660e\u767d\u4e86\uff0c\u8fd9\u67c4\u5200\u786e\u662f\u7530\u5f52\u519c\u4eb2\u624b\u4ea4\u7ed9\u6211\u513f\u7684\uff0c\u5404\u4f4d\u4e0d\u5f97\u4e89\u593a\u4e86\u5427\uff1f\u201d\u5218\u5143\u9e64\u7b11\u9053\uff1a\u201c\u7530\u5927\u54e5\u4ea4\u7ed9\u9676\u4e16\u5144\u7684\uff0c\u53ea\u662f\u4e00\u53ea\u7a7a\u94c1\u76d2\u3002\u82e5\u662f\u4f60\u8981\u7a7a\u76d2\uff0c\u5728\u4e0b\u5e76\u65e0\u8bdd\u8bf4\u3002\u5b9d\u5200\u5374\u54ea\u6709\u4f60\u7684\u4efd\uff1f\u201d\u6bb7\u5409\u9053\uff1a\u201c\u6b64\u5200\u8be5\u5f52\u6211\u5929\u9f99\u5357\u5b97\uff0c\u518d\u65e0\u7591\u95ee\u3002\u201d\u962e\u58eb\u4e2d\u9053\uff1a\u201c\u5f53\u65e5\u7530\u5e08\u5144\u672a\u884c\u6388\u5200\u4e4b\u793c\uff0c\u6b64\u5200\u4ecd\u5c5e\u5317\u5b97\u3002\u201d\u4f17\u4eba\u8d8a\u4e89\u58f0\u97f3\u8d8a\u5927\u3002 \u3000\u3000\u5b9d\u6811\u5ffd\u7136\u6717\u58f0\u9053\uff1a\u201c\u5404\u4f4d\u4e89\u593a\u6b64\u5200\uff0c\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d\u4f17\u4eba\u4e00\u65f6\u54d1\u53e3\u65e0\u8a00\uff0c\u7adf\u7136\u96be\u4ee5\u56de\u7b54\u3002 \u3000\u3000\u5b9d\u6811\u51b7\u7b11\u9053\uff1a\u201c\u5148\u524d\u5404\u4f4d\u53ea\u77e5\u6b64\u5200\u524a\u94c1\u5982\u6ce5\uff0c\u950b\u5229\u65e0\u6bd4\uff0c\u8fd8\u4e0d\u77e5\u5b83\u5173\u8fde\u7740\u4e00\u4e2a\u6781\u5927\u5b9d\u85cf\u3002\u73b0\u4eca\u6709\u4eba\u8bf4\u4e86\u51fa\u6765\uff0c\u90a3\u66f4\u662f\u4eba\u4eba\u773c\u7ea2\uff0c\u4e2a\u4e2a\u8d77\u5fc3\u3002\u53ef\u662f\u8001\u548c\u5c1a\u5012\u8981\u8bf7\u6559\uff1a\u82e5\u65e0\u5b9d\u85cf\u5730\u56fe\uff0c\u5355\u8981\u6b64\u5200\u4f55\u7528\uff1f\u201d\u4f17\u4eba\u5fc3\u5934\u4e00\u51db\uff0c\u4e00\u9f50\u671b\u7740\u82d7\u82e5\u5170\u9b13\u8fb9\u90a3\u53ea\u73e0\u9497\u3002 \u3000\u3000\u82d7\u82e5\u5170\u6587\u79c0\u67d4\u5f31\uff0c\u8981\u53d6\u5979\u5934\u4e0a\u73e0\u9497\uff0c\u76f4\u662f\u4e00\u4e3e\u624b\u4e4b\u52b3\uff0c\u53ea\u662f\u4eba\u4eba\u60f3\u5230\u5979\u7236\u4eb2\u5a01\u9738\u5929\u4e0b\uff0c\u82e5\u662f\u5bf9\u5979\u6709\u4e1d\u6beb\u5192\u72af\u4eb5\u6e0e\uff0c\u5979\u7236\u4eb2\u8ffd\u7a76\u8d77\u6765\uff0c\u8c01\u4eba\u6562\u5f53\uff1f\u662f\u4ee5\u773c\u89c1\u90a3\u73e0\u9497\u5fae\u5fae\u98a4\u52a8\uff0c\u5374\u65e0\u4eba\u6562\u5148\u8bf4\u8bdd\u3002 \u3000\u3000\u5218\u5143\u9e64\u5411\u4f17\u4eba\u6a2a\u773c\u4e00\u626b\uff0c\u8138\u9732\u50b2\u8272\uff0c\u8d70\u5230\u82d7\u82e5\u5170\u9762\u524d\uff0c\u53f3\u624b\u4e00\u63a2\uff0c\u7a81\u7136\u5c06\u5979\u9b13\u8fb9\u7684\u73e0\u9497\u62d4\u4e86\u4e0b\u6765\u3002\u82d7\u82e5\u5170\u53c8\u7f9e\u53c8\u6012\uff0c\u8138\u8272\u82cd\u767d\uff0c\u9000\u540e\u4e86\u4e24\u6b65\u3002\u4f17\u4eba\u89c1\u5218\u5143\u9e64\u5c45\u7136\u5982\u6b64\u5927\u80c6\uff0c\u65e0\u4e0d\u5931\u8272\u3002 \u3000\u3000\u5218\u5143\u9e64\u9053\uff1a\u201c\u672c\u4eba\u5949\u65e8\u800c\u884c\uff0c\u6015\u4ed6\u4ec0\u4e48\u82d7\u5927\u4fa0\uff0c\u79e7\u5927\u4fa0\uff1f \u3000\u3000\u518d\u8bf4\uff0c\u90a3\u91d1\u9762\u4f5b\u6b64\u523b\u662f\u6b7b\u662f\u6d3b\uff0c\u54fc\uff0c\u54fc\uff0c\u5374\u4e5f\u5728\u672a\u77e5\u4e4b\u6570\u5462\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u9f50\u95ee\uff1a\u201c\u600e\u4e48\uff1f\u201d\u5218\u5143\u9e64\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u773c\u4e0b\u8ba1\u6765\uff0c\u90a3\u91d1\u9762\u4f5b\u7eb5\u7136\u5c1a\u5728\u4eba\u4e16\uff0c\u5341\u4e4b\u516b\u4e5d\uff0c\u4e5f\u5df2\u5168\u8eab\u9563\u94d0\u3001\u843d\u5165\u5929\u7262\u4e4b\u4e2d\u4e86\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u5927\u5403\u4e00\u60ca\uff0c\u767b\u5fd8\u73e0\u9497\u88ab\u593a\u4e4b\u8fb1\uff0c\u53ea\u6302\u5ff5\u7740\u7236\u4eb2\u7684\u5b89\u5371\uff0c\u5fd9\u95ee\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8bf4\u6211\u7239\u7239\u600e\u4e48\u4e86\uff1f\u201d\u5b9d\u6811\u4e5f\u9053\uff1a\u201c\u8bf7\u9053\u5176\u8be6\u3002\u201d \u3000\u3000\u5218\u5143\u9e64\u60f3\u8d77\u4e0a\u5cf0\u4e4b\u65f6\uff0c\u88ab\u4ed6\u5728\u96ea\u4e2d\u6a2a\u62d6\u5012\u66f3\uff0c\u72fc\u72c8\u4e0d\u582a\uff0c\u4f46\u81ea\u5df1\u8bf4\u8d77\u5949\u65e8\u800c\u884c\u79cd\u79cd\u60c5\u7531\uff0c\u5b9d\u6811\u795e\u8272\u767b\u53d8\uff0c\u6b64\u65f6\u542c\u4ed6\u76f8\u8be2\uff0c\u66f4\u662f\u5f97\u610f\uff0c\u5fcd\u4e0d\u4f4f\u8981\u5c06\u673a\u5bc6\u5927\u4e8b\u5410\u9732\u51fa\u6765\uff0c\u597d\u5728\u4eba\u524d\u81ea\u5360\u8eab\u5206\uff0c\u4e8e\u662f\u95ee\u9053\uff1a\u201c\u5b9d\u6811\u5927\u5e08\uff0c\u5728\u4e0b\u5148\u8981\u95ee\u4f60\u4e00\u53e5\uff0c\u6b64\u95f4\u4e3b\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u7fa4\u8c6a\u5728\u5c71\u4e0a\u534a\u65e5\uff0c\u59cb\u7ec8\u4e0d\u77e5\u4e3b\u4eba\u662f\u8c01\uff0c\u542c\u5218\u5143\u9e64\u6b64\u95ee\uff0c\u6b63\u5408\u5fc3\u610f\uff0c\u4e00\u9f50\u671b\u7740\u5b9d\u6811\uff0c\u53ea\u542c\u4ed6\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5927\u4f19\u513f\u90fd\u4e0d\u9690\u7792\uff0c\u8001\u8872\u4e5f\u4e0d\u7528\u5356\u90a3\u81ed\u5173\u5b50\u4e86\u3002\u6b64\u95f4\u4e3b\u4eba\u59d3\u675c\u540d\u5e0c\u5b5f\uff0c\u662f\u6b66\u6797\u4e2d\u4e00\u4f4d\u54cd\u5f53\u5f53\u7684\u811a\u8272\u3002\u201d\u4f17\u4eba\u4e92\u76f8\u671b\u4e86\u4e00\u773c\uff0c\u5fc3\u4e2d\u6697\u5ff5\uff1a\u201c\u675c\u5e0c\u5b5f\uff1f\u675c\u5e0c\u5b5f\uff1f\u201d\u5374\u90fd\u60f3\u4e0d\u8d77\u6b64\u4eba\u662f\u8c01\u3002\u5b9d\u6811\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u675c\u8001\u82f1\u96c4\u81ea\u89c6\u751a\u9ad8\uff0c\u7b49\u95f2\u4e0d\u4e0e\u4eba\u4ea4\u5f80\uff0c\u662f\u4ee5\u6b66\u529f\u867d\u5f3a\uff0c\u5e38\u4eba\u53ef\u4e0d\u77e5\u4ed6\u540d\u5934\u3002\u7136\u800c\u6c5f\u6e56\u4e0a\u4e00\u7b49\u4e00\u7684\u4eba\u7269\uff0c\u5374\u4e2a\u4e2a\u5bf9\u4ed6\u6781\u662f\u94a6\u6155\u3002\u201d\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u8f7b\u63cf\u6de1\u5199\uff0c\u53ef\u628a\u4f17\u4eba\u90fd\u635f\u4e86\u4e00\u4e0b\uff0c\u8a00\u4e0b\u4e4b\u610f\uff0c\u660e\u662f\u8bf4\u4f17\u4eba\u5b9e\u4e0d\u8db3\u9053\u3002 \u3000\u3000\u6bb7\u5409\u3001\u962e\u58eb\u4e2d\u7b49\u90fd\u611f\u607c\u6012\uff0c\u4f46\u60f3\u82d7\u4eba\u51e4\u5728\u90a3\u5bf9\u8054\u4e0a\u79f0\u4ed6\u4e3a\u201c\u5e0c\u5b5f\u4ec1\u5144\u201d\uff0c\u800c\u81ea\u5df1\u786e\u591f\u4e0d\u4e0a\u4e0e\u91d1\u9762\u4f5b\u79f0\u5144\u9053\u5f1f\uff0c\u5b9d\u6811\u4e4b\u8a00\u867d\u4ee4\u4eba\u4e0d\u5feb\uff0c\u5374\u4e5f\u65e0\u53ef\u8fa9\u9a73\u3002 \u3000\u3000\u5218\u5143\u9e64\u9053\uff1a\u201c\u54b1\u4eec\u4e0a\u5c71\u4e4b\u65f6\uff0c\u6b64\u95f4\u7684\u7ba1\u5bb6\u8bf4\u9053\uff1a\u2018\u4e3b\u4eba\u8d74\u5b81\u53e4\u5854\u76f8\u8bf7\u91d1\u9762\u4f5b\uff0c\u53c8\u6d3e\u4eba\u524d\u53bb\u9080\u8bf7\u5174\u6c49\u4e10\u5e2e\u7684\u8303\u5e2e\u4e3b\u3002\u2019\u8fd9\u8bdd\u53ef\u6709\u70b9\u513f\u4e0d\u5c3d\u4e0d\u5b9e\u3002\u60f3\u90a3\u8303\u5e2e\u4e3b\u5728\u6cb3\u5357\u5f00\u5c01\u5e9c\u88ab\u64d2\uff0c\u5c0f\u5f1f\u4e5f\u66fe\u51fa\u4e86\u4e00\u70b9\u513f\u529b\u6c14\u3002\u201d\u4f17\u4eba\u60ca\u9053\uff1a\u201c\u8303\u5e2e\u4e3b\u88ab\u64d2\uff1f\u201d\u5218\u5143\u9e64\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u5fa1\u524d\u4f8d\u536b\u603b\u7ba1\u8d5b\u5927\u4eba\u4eb2\u81ea\u4e0b\u7684\u624b\u3002\u60f3\u90a3\u8303\u5e2e\u4e3b\u867d\u7136\u4e5f\u7b97\u5f97\u4e0a\u662f\u4e2a\u4eba\u7269\uff0c\u5374\u4e5f\u4e0d\u5fc5\u52b3\u52a8\u8d5b\u603b\u7ba1\u7684\u5927\u9a7e\u554a\u3002\u6211\u4eec\u62ff\u4f4f\u8303\u5e2e\u4e3b\uff0c\u53ea\u662f\u628a\u4ed6\u5f53\u4f5c\u4e00\u7247\u9999\u9975\uff0c\u7528\u6765\u9493\u4e00\u6761\u5927\u5927\u7684\u91d1\u9ccc\u3002 \u3000\u3000\u90a3\u91d1\u9ccc\u561b\uff0c\u81ea\u7136\u662f\u82d7\u4eba\u51e4\u5566\u3002\u675c\u5e84\u4e3b\u8981\u53bb\u9080\u82d7\u4eba\u51e4\u6765\u5bf9\u4ed8\u4ec0\u4e48\uff0c\u5176\u5b9e\u54ea\u91cc\u9080\u5f97\u5230\uff1f\u82d7\u4eba\u51e4\u8fd9\u5f53\u513f\u5b9a\u662f\u53bb\u4e86\u5317\u4eac\uff0c\u60f3\u8981\u642d\u6551\u8303\u5e2e\u4e3b\u3002\u563f\u563f\uff0c\u8d5b\u603b\u7ba1\u5728\u5317\u4eac\u5b89\u6392\u4e0b\u5929\u7f57\u5730\u7f51\uff0c\u4e13\u5019\u82d7\u4eba\u51e4\u5927\u9a7e\u5149\u4e34\u3002\u4ed6\u82e5\u662f\u4e0d\u4e0a\u8fd9\u5f53\uff0c\u6211\u4eec\u539f\u662f\u62ff\u4ed6\u6ca1\u6709\u6cd5\u513f\u3002\u4ed6\u7adf\u4e0a\u4eac\u6551\u4eba\uff0c\u8fd9\u53eb\u505a\u5544\u6728\u9e1f\u5543\u9ec4\u8fde\u6811\uff0c\u81ea\u8ba8\u82e6\u5403\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u4e0e\u7236\u4eb2\u76f8\u522b\u4e4b\u65f6\uff0c\u786e\u662f\u542c\u7236\u4eb2\u8bf4\u6709\u4e8b\u8d74\u4eac\uff0c\u5631\u5979\u5148\u4e0a\u96ea\u5cf0\uff0c\u5230\u675c\u5bb6\u6682\u5c45\u3002\u8fd9\u65f6\u542c\u5218\u5143\u9e64\u5982\u6b64\u8bf4\u6765\uff0c\u53ea\u6015\u7236\u4eb2\u771f\u662f\u51f6\u591a\u5409\u5c11\uff0c\u4e0d\u7531\u5f97\u7389\u5bb9\u5931\u8272\u3002 \u3000\u3000\u5218\u5143\u9e64\u6d0b\u6d0b\u5f97\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5730\u56fe\u6709\u4e86\uff0c\u5b9d\u5200\u4e5f\u6709\u4e86\uff0c\u53bb\u628a\u674e\u81ea\u6210\u7684\u5b9d\u85cf\u53d1\u6398\u51fa\u6765\uff0c\u732e\u7ed9\u5723\u4e0a\uff0c\u8fd9\u91cc\u4eba\u4eba\u5c11\u4e0d\u4e86\u4e00\u4e2a\u5c01\u59bb\u836b\u5b50\u7684\u529f\u540d\u3002\u201d\u4ed6\u89c1\u6709\u7684\u4eba\u8138\u73b0\u559c\u8272\uff0c\u6709\u7684\u5374\u6709\u72b9\u8c6b\u4e4b\u610f\uff0c\u5fc3\u77e5\u5982\u9676\u767e\u5c81\u7b49\u4eba\uff0c\u628a\u53d1\u8d22\u77a7\u5f97\u6bd4\u5347\u5b98\u66f4\u91cd\uff0c\u53c8\u9053\uff1a\u201c\u60f3\u90a3\u5b9d\u85cf\u5806\u79ef\u5982\u5c71\uff0c\u5927\u4f19\u513f\u987a\u624b\u7275\u7f8a\uff0c\u53d6\u4e0a\u4e00\u4e9b\uff0c\u90a3\u5c31\u4e00\u4e16\u5403\u7740\u4e0d\u5c3d\uff0c\u6709\u4f55\u4e0d\u7f8e\uff1f\u201d\u4f17\u4eba\u8f70\u7136\u559d\u5f69\uff0c\u518d\u65e0\u5f02\u8bae\u3002 \u3000\u3000\u7530\u9752\u6587\u672c\u6765\u7f9e\u6127\u96be\u5f53\uff0c\u72ec\u81ea\u8eb2\u5728\u5185\u5ba4\uff0c\u542c\u5f97\u5385\u4e0a\u53eb\u597d\u4e4b\u58f0\u4e0d\u7edd\uff0c\u77e5\u9053\u5df2\u4e0d\u5728\u8c08\u8bba\u5979\u7684\u4e11\u4e8b\uff0c\u5f53\u4e0b\u6084\u6084\u51fa\u6765\uff0c\u7ad9\u5728\u95e8\u8fb9\u3002 \u3000\u3000\u5218\u5143\u9e64\u5728\u5934\u4e0a\u62d4\u4e0b\u4e00\u6839\u5934\u53d1\uff0c\u6162\u6162\u4ece\u73e0\u9497\u7684\u51e4\u5634\u91cc\u7a7f\u4e86\u8fc7\u53bb\uff0c\u4f9d\u7740\u5f53\u65e5\u6240\u89c1\u82d7\u4eba\u51e4\u7684\u624b\u6cd5\uff0c\u8f7b\u8f7b\u4e00\u62c9\u4e00\u7529\uff0c\u51e4\u5934\u673a\u62ec\u5f39\u5f00\uff0c\u679c\u7136\u6709\u4e2a\u7eb8\u56e2\u6389\u4e86\u51fa\u6765\u3002\u4f17\u4eba\u90fd\u662f\u201c\u54e6\u201d\u7684\u4e00\u58f0\u3002\u5218\u5143\u9e64\u6253\u5f00\u7eb8\u56e2\uff0c\u644a\u5728\u684c\u4e0a\u3002\u4f17\u4eba\u56f4\u62e2\u53bb\u770b\u3002 \u3000\u3000\u4f46\u89c1\u90a3\u7eb8\u8584\u5982\u8749\u7ffc\uff0c\u867d\u7136\u5e74\u6df1\u65e5\u4e45\uff0c\u4f46\u56e0\u5bc6\u85cf\u73e0\u9497\u4e4b\u4e2d\uff0c\u5374\u662f\u4e1d\u6beb\u672a\u635f\uff0c\u7eb8\u4e0a\u7ed8\u7740\u4e00\u5ea7\u7b14\u7acb\u9ad8\u8038\u7684\u5c71\u5cf0\uff0c\u5cf0\u65c1\u5199\u7740\u4e5d\u4e2a\u5b57\u9053\uff1a\u201c\u8fbd\u4e1c\u4e4c\u5170\u5c71\u7389\u7b14\u5cf0\u540e\u201d\u3002 \u3000\u3000\u5b9d\u6811\u5927\u53eb\uff1a\u201c\u554a\u54c8\uff0c\u5929\u4e0b\u7adf\u6709\u8fd9\u7b49\u5de7\u4e8b\uff1f\u54b1\u4eec\u6240\u5728\u4e4b\u5904\uff0c\u5c31\u662f\u4e4c\u5170\u5c71\u7389\u7b14\u5cf0\u554a\u3002\u201d \u3000\u3000\u4f17\u4eba\u77a7\u90a3\u56fe\u4e0a\u5c71\u5cf0\u4e4b\u5f62\uff0c\u679c\u771f\u4e0e\u8fd9\u96ea\u5cf0\u4e00\u822c\u65e0\u5f02\uff0c\u4e0a\u5cf0\u65f6\u6240\u89c1\u5cb8\u8fb9\u7684\u4e09\u682a\u53e4\u677e\uff0c\u56fe\u4e0a\u4e5f\u753b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5f53\u4e0b\u65e0\u4e0d\u5567\u5567\u79f0\u5f02\u3002 \u3000\u3000\u5b9d\u6811\u9053\uff1a\u201c\u6b64\u5904\u5e84\u4e0a\u675c\u8001\u82f1\u96c4\u89c1\u95fb\u5e7f\u535a\uff0c\u5fc5\u662f\u5f97\u77e5\u4e86\u5b9d\u85cf\u7684\u6d88\u606f\uff0c\u662f\u4ee5\u7279\u610f\u5728\u6b64\u5efa\u5e84\u3002\u5426\u5219\u6b64\u5904\u6c14\u5019\u9177\u5bd2\uff0c\u4e0a\u4e0b\u8270\u96be\uff0c\u53c8\u4f55\u5fc5\u8d39\u8fd9\u4e48\u5927\u7684\u4e8b\uff1f\u201d\u5218\u5143\u9e64\u5fc3\u4e2d\u4e00\u6025\uff0c\u5fd9\u9053\uff1a\u201c\u554a\u54df\uff01\u90a3\u53ef\u4e0d\u5999\u3002\u4ed6\u8fd9\u5e84\u5b50\u5efa\u9020\u5df2\u4e45\uff0c\u8fd8\u4e0d\u65e9\u5c06\u5b9d\u85cf\u642c\u5f97\u4e00\u5e72\u4e8c\u51c0\uff1f\u201d \u3000\u3000\u5b9d\u6811\u5fae\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u672a\u5fc5\u3002\u5218\u5927\u4eba\u4f60\u60f3\uff0c\u8981\u662f\u4ed6\u5df2\u627e\u5230\u4e86\u5b9d\u85cf\u6240\u5728\uff0c\u5b9a\u7136\u65e9\u5c31\u53bb\u4e86\u522b\u5730\uff0c\u51b3\u4e0d\u4f1a\u4ecd\u5728\u6b64\u5904\u5c45\u4f4f\u3002\u201d\u5218\u5143\u9e64\u4e00\u62cd\u5927\u817f\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff01\u5feb\u5230\u540e\u5c71\u53bb\u3002\u201d \u3000\u3000\u5b9d\u6811\u6307\u7740\u82d7\u82e5\u5170\u9053\uff1a\u201c\u8fd9\u4f4d\u82d7\u59d1\u5a18\u4e0e\u5e84\u4e0a\u4f17\u4eba\u600e\u4e48\u529e\uff1f\u201d\u5218\u5143\u9e64\u8f6c\u8fc7\u8eab\u6765\uff0c\u53ea\u89c1\u4e8e\u7ba1\u5bb6\u7b49\u5e84\u4e0a\u4f63\u4ec6\uff0c\u4e2a\u4e2a\u5df2\u8d70\u5f97\u4e0d\u77e5\u53bb\u5411\u3002\u7530\u9752\u6587\u4ece\u95e8\u540e\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u600e\u7684\uff0c\u5e84\u4e0a\u7537\u7537\u5973\u5973\u90fd\u8eb2\u4e86\u4e2a\u5e72\u5e72\u51c0\u51c0\u3002\u201d\u5218\u5143\u9e64\u62a2\u8fc7\u4e00\u67c4\u5355\u5200\uff0c\u8d70\u5230\u82d7\u82e5\u5170\u8eab\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u6240\u8bf4\u4e4b\u4e8b\uff0c\u5979\u53e5\u53e5\u542c\u5728\u8033\u91cc\uff0c\u8fd9\u7978\u6839\u53ef\u7559\u4e0d\u5f97\u3002\u201d \u3000\u3000\u4e3e\u8d77\u5355\u5200\uff0c\u5c31\u8981\u5f80\u5979\u5934\u9876\u780d\u843d\u3002 \u3000\u3000\u7a81\u7136\u95f4\u4eba\u5f71\u4e00\u95ea\uff0c\u7434\u513f\u4ece\u6905\u80cc\u540e\u8dc3\u51fa\uff0c\u62b1\u4f4f\u5218\u5143\u9e64\u7684\u624b\uff0c\u72e0\u547d\u5728\u4ed6\u624b\u8155\u4e0a\u54ac\u4e86\u4e00\u53e3\u3002\u5218\u5143\u9e64\u51fa\u5176\u4e0d\u610f\uff0c\u624b\u8155\u4e00\u75bc\uff0c\u5f53\u5577\u4e00\u54cd\uff0c\u5355\u5200\u843d\u5730\u3002\u7434\u513f\u5927\u9a82\uff1a\u201c\u77ed\u547d\u7684\u6076\u8d3c\uff0c\u4f60\u6562\u4f24\u4e86\u5c0f\u59d0\u4e00\u6839\u6beb\u6bdb\uff0c\u6211\u5bb6\u8001\u7237\u4e0a\u5f97\u5c71\u6765\uff0c\u62bd\u4f60\u7684\u7b4b\uff0c\u5265\u4f60\u7684\u76ae\uff0c\u8fd9\u91cc\u4eba\u4eba\u8131\u4e0d\u4e86\u5e72\u7cfb\u3002\u201d \u3000\u3000\u5218\u5143\u9e64\u5927\u6012\uff0c\u53cd\u624b\u4e00\u62f3\uff0c\u731b\u5f80\u7434\u513f\u8138\u4e0a\u51fb\u53bb\u3002\u718a\u5143\u732e\u4f38\u51fa\u53f3\u81c2\uff0c\u683c\u5f00\u4e86\u4ed6\u4e00\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u54b1\u4eec\u5bfb\u5b9d\u8981\u7d27\uff0c\u4e0d\u5fc5\u591a\u4f24\u4eba\u547d\uff01\u201d\u8981\u77e5\u718a\u5143\u732e\u4e00\u751f\u8d70\u9556\uff0c\u5411\u6765\u80c6\u5c0f\u6015\u4e8b\uff0c\u8c28\u614e\u7a33\u91cd\uff0c\u4e0d\u50cf\u4ed6\u5e08\u5144\u505a\u4e86\u7687\u5e1d\u4f8d\u536b\uff0c\u6740\u51e0\u4e2a\u8001\u767e\u59d3\u4e0d\u5f53\u4e00\u56de\u4e8b\uff0c\u4ed6\u542c\u4e86\u7434\u513f\u4e4b\u8a00\uff0c\u5fc3\u60f3\u82e5\u662f\u4f24\u4e86\u82d7\u82e5\u5170\uff0c\u4e07\u4e00\u5979\u7236\u4eb2\u9003\u8131\u7f57\u7f51\uff0c\u90a3\u53ef\u5927\u7978\u4e34\u5934\u4e86\u3002\u6bb7\u5409\u548c\u4ed6\u5fc3\u610f\u76f8\u540c\uff0c\u4e5f\u9053\uff1a\u201c\u5218\u5e08\u5144\uff0c\u54b1\u4eec\u5feb\u53bb\u5bfb\u5b9d\u3002\u201d \u3000\u3000\u5218\u5143\u9e64\u53cc\u76ee\u4e00\u77aa\uff0c\u6307\u7740\u82d7\u82e5\u5170\u9053\uff1a\u201c\u8fd9\u599e\u513f\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u5b9d\u6811\u7b11\u541f\u541f\u7684\u8d70\u4e0a\u4e24\u6b65\uff0c\u5927\u8896\u5fae\u626c\uff0c\u5df2\u5728\u82d7\u82e5\u5170\u9888\u53e3\u201c\u5929\u7a81\u201d\u4e0e\u80cc\u5fc3\u201c\u795e\u901a\u201d\u4e24\u7a74\u4e0a\u5404\u70b9\u4e86\u4e00\u6307\u3002\u82d7\u82e5\u5170\u5168\u8eab\u9178\u8f6f\uff0c\u762b\u5728\u6905\u4e0a\uff0c\u5fc3\u91cc\u53c8\u7f9e\u53c8\u6025\uff0c\u5374\u8bf4\u4e0d\u51fa\u8bdd\u3002\u7434\u513f\u53ea\u9053\u4ed6\u4f24\u4e86\u5c0f\u59d0\uff0c\u6a2a\u4e86\u5fc3\u53c8\u6293\u4f4f\u4e86\u548c\u5c1a\u7684\u624b\uff0c\u8981\u72e0\u72e0\u54ac\u4ed6\u4e00\u53e3\u3002\u5b9d\u6811\u8ba9\u5979\u6293\u4f4f\u81ea\u5df1\u53f3\u624b\u62c9\u5230\u53e3\u8fb9\uff0c\u624b\u6307\u6296\u52a8\uff0c\u70b9\u4e86\u5979\u9f3b\u8fb9\u201c\u8fce\u9999\u201d\u3001\u53e3\u65c1\u201c\u5730\u4ed3\u201d\u4e24\u7a74\u3002\u7434\u513f\u8eab\u5b50\u4e00\u9707\uff0c\u6454\u5012\u5728\u5730\u3002 \u3000\u3000\u7530\u9752\u6587\u9053\uff1a\u201c\u82d7\u5bb6\u59b9\u5b50\u5750\u5728\u6b64\u5904\u987b\u4e0d\u597d\u770b\u3002\u201d\u4fef\u8eab\u6258\u8d77\u5979\u7684\u8eab\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u771f\u8f7b\uff0c\u5012\u4f3c\u6ca1\u751f\u9aa8\u5934\u3002\u201d\u8d70\u5411\u4e1c\u8fb9\u53a2\u623f\u3002 \u3000\u3000\u90a3\u4e1c\u53a2\u623f\u539f\u662f\u675c\u5e84\u4e3b\u6b3e\u5f85\u5bbe\u5ba2\u7684\u6240\u5728\uff0c\u5e8a\u5e10\u51e0\u684c\u3001\u4e00\u5e94\u8d77\u5c45\u4e4b\u5177\u9f50\u5907\uff0c\u9648\u8bbe\u5f97\u751a\u662f\u8003\u7a76\u3002\u7530\u9752\u6587\u63a9\u4e0a\u4e86\u95e8\uff0c\u66ff\u82d7\u82e5\u5170\u9664\u53bb\u978b\u889c\u5916\u88f3\uff0c\u53ea\u7559\u4e0b\u8d34\u8eab\u5c0f\u8863\uff0c\u5c06\u5979\u88f9\u5728\u88ab\u4e2d\uff0c\u5782\u4e0b\u4e86\u7f57\u5e10\u3002\u82d7\u82e5\u5170\u81ea\u4e03\u516b\u5c81\u540e\uff0c\u672a\u5728\u4eba\u524d\u9664\u8fc7\u8863\u886b\uff0c\u773c\u524d\u4e4b\u4eba\u867d\u662f\u5973\u5b50\uff0c\u4e5f\u5df2\u7f9e\u5f97\u6ee1\u8138\u7ea2\u6655\u3002\u7530\u9752\u6587\u671b\u7740\u5979\u8eab\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u6015\u6211\u77a7\u4e48\uff1f\u59b9\u5b50\uff0c\u4f60\u751f\u5f97\u771f\u7f8e\uff0c\u8fde\u6211\u4e5f\u4e0d\u7981\u52a8\u5fc3\u5462\u3002\u201d\u62b1\u4e86\u5979\u8863\u886b\u8d70\u5230\u5385\u4e0a\uff0c\u9053\uff1a\u201c\u5979\u8863\u886b\u90fd\u7ed9\u6211\u9664\u4e0b\u4e86\uff0c\u7eb5\u7136\u65f6\u8fb0\u4e00\u8fc7\uff0c\u7a74\u9053\u89e3\u4e86\uff0c\u4e5f\u53eb\u5979\u8d70\u52a8\u4e0d\u5f97\u3002\u201d\u7fa4\u8c6a\u4e00\u9f50\u5927\u7b11\u3002 \u3000\u3000\u5b9d\u6811\u9053\uff1a\u201c\u54b1\u4eec\u5927\u5bb6\u6765\u77a7\u77a7\uff0c\u4ece\u8fd9\u5200\u5b50\u4e4b\u4e2d\uff0c\u5230\u5e95\u5982\u4f55\u80fd\u5bfb\u5230\u5b9d\u85cf\u3002\u201d\u8bf4\u7740\u4ece\u6000\u4e2d\u53d6\u51fa\u94c1\u76d2\uff0c\u6253\u5f00\u76d2\u76d6\uff0c\u63d0\u5200\u5728\u624b\uff0c\u89c1\u5200\u9798\u4e0a\u9664\u4e86\u523b\u5f97\u6709\u5b57\u5916\uff0c\u66f4\u65e0\u522b\u6837\u5947\u5f02\u4e4b\u5904\u3002\u4ed6\u4e00\u624b\u6301\u9798\uff0c\u4e00\u624b\u6301\u67c4\uff0c\u5237\u7684\u4e00\u54cd\uff0c\u5c06\u5200\u62d4\u4e86\u51fa\u6765\uff0c\u53ea\u89c9\u9752\u5149\u56db\u5c04\uff0c\u5bd2\u6c14\u900f\u9aa8\uff0c\u4e0d\u7981\u673a\u4f36\u4f36\u7684\u6253\u4e2a\u51b7\u6218\u3002\u4f17\u4eba\u540c\u65f6\u201c\u554a\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u5c06\u5b9d\u5200\u653e\u5728\u684c\u4e0a\uff0c\u4f17\u4eba\u56f4\u62e2\u89c2\u770b\uff0c\u89c1\u5200\u8eab\u4e00\u9762\u5149\u6ed1\u5e73\u6574\uff0c\u53e6\u4e00\u9762\u5374\u96d5\u9542\u7740\u53cc\u9f99\u62a2\u73e0\u7684\u82b1\u7eb9\u3002\u4e24\u6761\u9f99\u4e00\u5927\u4e00\u5c0f\uff0c\u5f62\u72b6\u65e2\u6781\u4e11\u964b\uff0c\u800c\u4e14\u9f99\u4e0d\u50cf\u9f99\uff0c\u86c7\u4e0d\u50cf\u86c7\uff0c\u5012\u5982\u4e24\u6761\u6bdb\u866b\uff0c\u4f46\u6240\u62a2\u4e4b\u73e0\u5374\u662f\u4e00\u5757\u7ea2\u7389\uff0c\u5b9d\u5149\u7167\u4eba\uff0c\u7684\u662f\u73cd\u7269\u3002 \u3000\u3000\u66f9\u4e91\u5947\u62ff\u8d77\u5200\u6765\u7ec6\u770b\uff0c\u9053\uff1a\u201c\u90a3\u6709\u4ec0\u4e48\u53e4\u602a\uff1f\u201d\u5b9d\u6811\u9053\uff1a\u201c\u8fd9\u4e24\u6761\u866b\u513f\u5fc5\u4e0e\u5b9d\u85cf\u6709\u5173\uff0c\u54b1\u4eec\u5230\u540e\u5c71\u77a7\u77a7\u518d\u8bf4\u3002\u7ed9\u6211\uff01\u201d\u8bf4\u7740\u4f38\u624b\u53bb\u63a5\u5b9d\u5200\u3002\u66f9\u4e91\u5947\u66f4\u4e0d\u6253\u8bdd\uff0c\u56de\u5200\u62a4\u8eab\uff0c\u6025\u5954\u800c\u51fa\u3002\u5b9d\u6811\u6012\u9053\uff1a\u201c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u8ffd\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u51fa\u5f97\u5927\u95e8\uff0c\u53ea\u89c1\u66f9\u4e91\u5947\u63e1\u5200\u5411\u524d\u6025\u5954\uff0c\u5b9d\u6811\u53f3\u624b\u4e00\u626c\uff0c\u4e00\u9897\u94c1\u5ff5\u73e0\u6fc0\u98de\u800c\u51fa\uff0c\u6b63\u4e2d\u4ed6\u53f3\u80a9\u80a9\u80db\u9aa8\u3002\u66f9\u4e91\u5947\u624b\u81c2\u9178\u9ebb\uff0c\u62ff\u634f\u4e0d\u4f4f\uff0c\u64e6\u7684\u4e00\u58f0\uff0c\u5b9d\u5200\u843d\u5728\u96ea\u5730\u4e4b\u4e2d\u3002\u5b9d\u6811\u5927\u8e0f\u6b65\u4e0a\u524d\uff0c\u62fe\u8d77\u5b9d\u5200\u3002\u66f9\u4e91\u5947\u4e0d\u6562\u518d\u4e89\uff0c\u9000\u5728\u4e00\u65c1\uff0c\u773c\u89c1\u5b9d\u6811\u4e0e\u5218\u5143\u9e64\u4e00\u4e2a\u6301\u5200\u3001\u4e00\u4e2a\u6301\u56fe\uff0c\u5e76\u80a9\u5411\u5c71\u540e\u8d70\u53bb\u3002\u8fd9\u65f6\u4f59\u4eba\u4e5f\u90fd\u6d8c\u51fa\u5927\u95e8\uff0c\u8ddf\u968f\u5728\u540e\u3002 \u3000\u3000\u5b9d\u6811\u7b11\u9053\uff1a\u201c\u5218\u5927\u4eba\uff0c\u9002\u624d\u8001\u8872\u591a\u6709\u5192\u72af\uff0c\u8bf7\u52ff\u89c1\u602a\u3002\u201d\u5218\u5143\u9e64\u89c1\u4ed6\u966a\u7b11\u8c22\u7f6a\uff0c\u5fc3\u4e2d\u4e50\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u5e08\u6b66\u827a\u9ad8\u5f3a\uff0c\u5728\u4e0b\u4f69\u670d\u5f97\u7d27\uff0c\u65e5\u540e\u8fd8\u6709\u501f\u91cd\u4e4b\u5904\u3002\u201d\u5b9d\u6811\u9053\uff1a\u201c\u4e0d\u6562\u3002\u201d \u3000\u3000\u4e24\u4eba\u8d70\u4e86\u4e00\u9635\uff0c\u773c\u89c1\u5c71\u5cf0\u5df2\u65e0\u8def\u53ef\u884c\uff0c\u56db\u987e\u5c3d\u662f\u7691\u7691\u767d\u96ea\uff0c\u867d\u7136\u660e\u77e5\u5b9d\u85cf\u662f\u5728\u8fd9\u7389\u7b14\u5cf0\u4e0b\uff0c\u4f46\u504c\u5927\u4e00\u5ea7\u5c71\u5cf0\uff0c\u5230\u5904\u51b0\u5c01\u96ea\u51bb\uff0c\u6ca1\u7559\u4e0b\u4e1d\u6beb\u75d5\u8ff9\uff0c\u5374\u5230\u54ea\u91cc\u627e\u53bb\uff1f\u82e5\u8981\u628a\u5cf0\u4e0a\u51b0\u96ea\u94f2\u9664\uff0c\u5373\u7a77\u5343\u767e\u4eba\u4e4b\u529b\uff0c\u4e5f\u975e\u4e00\u5e74\u534a\u8f7d\u4e4b\u529f\uff0c\u4f55\u51b5\u4eca\u65e5\u94f2\u4e86\uff0c\u660e\u65e5\u53c8\u6709\u5927\u96ea\u843d\u4e0b\uff1b\u60f3\u5230\u675c\u5e0c\u5b5f\u5df2\u5728\u5cf0\u4e0a\u4f4f\u4e86\u51e0\u5341\u5e74\uff0c\u5fc5\u5b9a\u65e5\u65e5\u591c\u591c\u82e6\u5fc3\u7126\u8651\u3001\u5343\u65b9\u767e\u8ba1\u7684\u5bfb\u5b9d\uff0c\u81f3\u4eca\u672a\u80fd\u6210\u529f\uff0c\u5bfb\u5b9d\u4e4b\u4e8b\uff0c\u81ea\u7136\u5927\u975e\u6613\u6613\u3002 \u3000\u3000\u4f17\u4eba\u7ad9\u5728\u5d16\u8fb9\u4e1c\u5f20\u897f\u671b\uff0c\u675f\u624b\u65e0\u7b56\u3002\u7530\u9752\u6587\u5ffd\u7136\u6307\u7740\u5cf0\u4e0b\u4e00\u6761\u4e18\u5ce6\u8d77\u4f0f\u7684\u5c0f\u5c0f\u5c71\u8109\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u77a7\uff01\u201d\u4f17\u4eba\u987a\u7740\u5979\u624b\u6307\u671b\u53bb\uff0c\u672a\u89c1\u6709\u4f55\u5f02\u72b6\u3002\u7530\u9752\u6587\u9053\uff1a\u201c\u5404\u4f4d\uff0c\u770b\u8fd9\u5c71\u4e18\u7684\u6a21\u6837\uff0c\u662f\u5426\u4e0e\u519b\u5200\u4e0a\u7684\u82b1\u7eb9\u76f8\u4f3c\uff1f\u201d \u3000\u3000\u4f17\u4eba\u7ed9\u5979\u4e00\u8bed\u63d0\u9192\uff0c\u7ec6\u770b\u90a3\u6761\u5c71\u8109\uff0c\u4f46\u89c1\u4e00\u8def\u4ece\u4e1c\u5317\u8d70\u5411\u897f\u5357\uff0c\u53e6\u4e00\u8def\u81ea\u6b63\u5357\u5411\u5317\uff0c\u4e24\u8def\u5c71\u8109\u76f8\u4f1a\u4e4b\u5904\uff0c\u6709\u4e00\u5ea7\u5f62\u4f3c\u5706\u58a9\u7684\u77ee\u5cf0\u3002\u5b9d\u6811\u4e3e\u8d77\u5b9d\u5200\u4e00\u770b\uff0c\u518d\u671b\u5c71\u8109\uff0c\u89c1\u90a3\u5c71\u8109\u7684\u53bb\u52bf\u4f4d\u7f6e\uff0c\u6b63\u4e0e\u5200\u4e0a\u6240\u96d5\u7684\u53cc\u9f99\u62a2\u73e0\u56fe\u4e00\u822c\u65e0\u5f02\uff0c\u90a3\u5706\u5cf0\u6b63\u5f53\u5200\u4e0a\u5b9d\u77f3\u7684\u6240\u5728\uff0c\u4e0d\u7981\u53eb\u4e86\u51fa\u6765\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u5b9d\u85cf\u5b9a\u662f\u5728\u90a3\u5706\u5cf0\u4e4b\u4e2d\u3002\u201d\u5218\u5143\u9e64\u9053\uff1a\u201c\u54b1\u4eec\u5feb\u4e0b\u53bb\u3002\u201d \u3000\u3000\u6b64\u65f6\u4f17\u4eba\u4e00\u610f\u5bfb\u5b9d\uff0c\u5012\u4e5f\u7b97\u5f97\u4e0a\u9f50\u5fc3\u5408\u529b\u3002\u4e0d\u518d\u4e92\u76f8\u731c\u7591\u52a0\u5bb3\u3002\u5404\u4eba\u6495\u4e0b\u8863\u895f\u88f9\u5728\u624b\u4e0a\uff0c\u62c9\u7740\u7c97\u7d22\u6162\u6162\u6e9c\u4e0b\u5cf0\u53bb\u3002\u7b2c\u4e00\u4e2a\u6e9c\u4e0b\u7684\u662f\u5218\u5143\u9e64\uff0c\u6700\u540e\u4e00\u4e2a\u662f\u6bb7\u5409\u3002\u4ed6\u6e9c\u4e0b\u540e\u672c\u60f3\u5c06\u7ef3\u7d22\u6bc1\u53bb\uff0c\u4ee5\u514d\u540e\u60a3\uff0c\u4f46\u89c1\u4f17\u4eba\u90fd\u5df2\u53bb\u8fdc\uff0c\u751f\u6015\u5bfb\u5230\u5b9d\u85cf\u65f6\u6ca1\u4e86\u81ea\u5df1\u7684\u4efd\uff0c\u5f53\u4e0b\u4e0d\u6562\u505c\u7559\uff0c\u5c55\u5f00\u8f7b\u529f\u5411\u524d\u75be\u8ffd\u3002 \u3000\u3000\u81ea\u7389\u7b14\u5cf0\u671b\u5c06\u4e0b\u6765\uff0c\u90a3\u5706\u5cf0\u5c31\u5728\u773c\u524d\uff0c\u53ef\u662f\u5e73\u5730\u8d70\u53bb\uff0c\u8def\u7a0b\u5374\u4e5f\u4e0d\u8fd1\uff0c\u7ea6\u83ab\u6709\u4e8c\u5341\u6765\u91cc\u3002\u4f17\u4eba\u8f7b\u529f\u90fd\u597d\uff0c\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u5df2\u5954\u5230\u5706\u5cf0\u4e4b\u524d\u3002\u5404\u4eba\u7ed5\u7740\u90a3\u5706\u5cf0\u8f6c\u6765\u8f6c\u53bb\uff0c\u627e\u5bfb\u5b9d\u85cf\u7684\u6240\u5728\u3002\u9676\u5b50\u5b89\u5ffd\u5411\u5de6\u4e00\u6307\uff0c\u53eb\u9053\uff1a\u201c\u90a3\u662f\u8c01\uff1f\u201d \u3000\u3000\u4f17\u4eba\u542c\u4ed6\u8bed\u58f0\u5ffd\u4fc3\uff0c\u4e00\u9f50\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u6761\u7070\u767d\u8272\u7684\u4eba\u5f71\u5728\u96ea\u5730\u4e2d\u6025\u9a70\u800c\u8fc7\uff0c\u8eab\u6cd5\u4e4b\u5feb\uff0c\u5b9e\u662f\u96be\u4ee5\u5f62\u5bb9\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u90a3\u767d\u5f71\u5df2\u5954\u5411\u7389\u7b14\u5cf0\u800c\u53bb\u3002\u5b9d\u6811\u5931\u58f0\u9053\uff1a\u201c\uff01\u80e1\u4e00\u5200\u4e4b\u5b50\uff0c\u5982\u6b64\u4e86\u5f97\uff01\u201d\u8bf4\u8bdd\u4e4b\u65f6\u8138\u8272\u7070\u6697\uff0c\u663e\u662f\u5fc3\u6709\u91cd\u5fe7\u3002 \u3000\u3000\u4ed6\u6b63\u81ea\u6c89\u601d\uff0c\u5ffd\u542c\u7530\u9752\u6587\u5c16\u58f0\u5927\u53eb\uff0c\u6025\u5fd9\u8f6c\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u5706\u5cf0\u7684\u5761\u4e0a\u7a7a\u4e86\u4e00\u4e2a\u7a9f\u7abf\uff0c\u7530\u9752\u6587\u4eba\u5f62\u5374\u5df2\u4e0d\u89c1\u3002 \u3000\u3000\u9676\u5b50\u5b89\u4e0e\u66f9\u4e91\u5947\u4e00\u76f4\u90fd\u5f85\u5728\u7530\u9752\u6587\u8eab\u7554\uff0c\u89c1\u5979\u7a81\u7136\u5931\u8db3\u9677\u843d\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u53eb\u9053\uff1a\u201c\u9752\u59b9\uff01\u201d\u90fd\u6b32\u8dc3\u5165\u6551\u63f4\u3002\u9676\u767e\u5c81\u4e00\u628a\u62c9\u4f4f\u513f\u5b50\uff0c\u559d\u9053\uff1a\u201c\u5e72\u4ec0\u4e48\uff1f\u201d\u9676\u5b50\u5b89\u4e0d\u7406\uff0c\u7528\u529b\u6323\u8131\uff0c\u4e0e\u66f9\u4e91\u5947\u4e00\u9f50\u8df3\u843d\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u7a9f\u7abf\u5176\u5b9e\u751a\u6d45\uff0c\u4e24\u4eba\u8df3\u4e86\u4e0b\u53bb\uff0c\u90fd\u538b\u5728\u7530\u9752\u6587\u8eab\u4e0a\uff0c\u4e09\u4eba\u9f50\u60ca\u547c\u3002\u4e0a\u9762\u4f17\u4eba\u4e0d\u7981\u597d\u7b11\uff0c\u4f38\u624b\u5c06\u4e09\u4eba\u62c9\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u5b9d\u6811\u9053\uff1a\u201c\u53ea\u6015\u5b9d\u85cf\u5c31\u5728\u7a9f\u7abf\u4e4b\u4e2d\u4e5f\u672a\u53ef\u77e5\u3002\u7530\u59d1\u5a18\uff0c\u5728\u4e0b\u9762\u89c1\u5230\u4ec0\u4e48\uff1f\u201d\u7530\u9752\u6587\u629a\u6478\u8eab\u4e0a\u649e\u7740\u5c71\u77f3\u7684\u75db\u5904\uff0c\u6028\u9053\uff1a\u201c\u9ed1\u6f06\u6f06\u7684\u4ec0\u4e48\u4e5f\u6ca1\u77a7\u89c1\u3002\u201d\u5b9d\u6811\u8dc3\u4e86\u4e0b\u53bb\uff0c\u6643\u4eae\u706b\u6298\uff0c\u89c1\u90a3\u7a9f\u7abf\u5f84\u4e0d\u903e\u4e08\uff0c\u91cc\u9762\u90fd\u662f\u6781\u575a\u786c\u7684\u5ca9\u77f3\u4e0e\u51b0\u96ea\uff0c\u518d\u65e0\u5f02\u72b6\uff0c\u53ea\u5f97\u7eb5\u8eab\u800c\u4e0a\u3002 \u3000\u3000\u731b\u542c\u5f97\u5468\u4e91\u9633\u4e0e\u90d1\u4e09\u5a18\u4e24\u4eba\u7eb5\u58f0\u60ca\u547c\uff0c\u5148\u540e\u9677\u5165\u4e86\u4e1c\u8fb9\u548c\u5357\u8fb9\u7684\u96ea\u4e2d\u7a9f\u7abf\u3002\u962e\u58eb\u4e2d\u4e0e\u718a\u5143\u732e\u5206\u522b\u5c06\u4e24\u4eba\u62c9\u8d77\u3002\u770b\u6765\u8fd9\u5706\u5cf0\u5468\u56f4\u90fd\u662f\u7a9f\u7abf\uff0c\u4f17\u4eba\u53ea\u6015\u5931\u8db3\u6389\u5165\u6781\u6df1\u6781\u9669\u7684\u6d1e\u4e2d\uff0c\u5f53\u4e0b\u4e0d\u6562\u4e71\u8d70\uff0c\u90fd\u7ad9\u5728\u539f\u5730\u4e0d\u52a8\u3002 \u3000\u3000\u5b9d\u6811\u53f9\u9053\uff1a\u201c\u675c\u5e84\u4e3b\u5728\u7389\u7b14\u5cf0\u4e00\u4f4f\u6570\u5341\u5e74\uff0c\u4e0d\u77e5\u5b9d\u85cf\u6240\u5728\u3002 \u3000\u3000\u4ed6\u65e0\u5b9d\u5200\u5730\u56fe\uff0c\u832b\u65e0\u5934\u7eea\uff0c\u90a3\u4e5f\u7f62\u4e86\u3002\u4f46\u54b1\u4eec\u660e\u77e5\u662f\u5728\u8fd9\u5706\u4e18\u4e4b\u4e2d\uff0c\u4ecd\u662f\u65e0\u5904\u7740\u624b\uff0c\u90a3\u66f4\u52a0\u7b97\u5f97\u65e0\u80fd\u4e86\u3002\u201d \u3000\u3000\u4f17\u4eba\u7ad9\u5f97\u7d2f\u4e86\uff0c\u5404\u81ea\u6563\u5750\u539f\u5730\u3002\u809a\u4e2d\u8d8a\u6765\u8d8a\u997f\uff0c\u90fd\u662f\u795e\u56f0\u6c14\u6cae\u3002 \u3000\u3000\u90d1\u4e09\u5a18\u4f24\u5904\u53c8\u75db\u4e86\u8d77\u6765\uff0c\u54ac\u7740\u7259\u9f7f\uff0c\u4f38\u624b\u6309\u4f4f\u521b\u53e3\uff0c\u4e00\u8f6c\u5934\u95f4\uff0c\u53ea\u89c1\u5b9d\u6811\u624b\u4e2d\u5200\u4e0a\u7684\u5b9d\u77f3\u7ed9\u96ea\u5149\u4e00\u6620\uff0c\u66f4\u662f\u6676\u83b9\u7f8e\u8273\u3002\u5979\u8ddf\u7740\u4e08\u592b\u8d70\u9556\u591a\u5e74\uff0c\u89c1\u8fc7\u4e0d\u5c11\u73cd\u5f02\u5b9d\u7269\uff0c\u8fd9\u65f6\u89c1\u90a3\u5b9d\u77f3\u5149\u5f69\u6709\u4e9b\u5f02\u6837\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u5e08\uff0c\u8bf7\u4f60\u501f\u5b9d\u5200\u7ed9\u6211\u77a7\u77a7\u3002\u201d\u5b9d\u6811\u5fc3\u60f3\uff1a\u201c\u5979\u662f\u5973\u6d41\u4e4b\u8f88\uff0c\u817f\u4e0a\u53c8\u53d7\u4e86\u4f24\uff0c\u6015\u5979\u4f55\u6765\uff1f\u201d\u5f53\u4e0b\u5c06\u5200\u9012\u4e86\u8fc7\u53bb\u3002\u90d1\u4e09\u5a18\u63a5\u5200\u7ec6\u770b\uff0c\u679c\u89c1\u90a3\u5b9d\u77f3\u662f\u53cd\u9762\u9576\u5d4c\u7684\u3002\u539f\u6765\u5b9d\u77f3\u4e24\u9762\u6709\u9634\u9633\u6b63\u53cd\u4e4b\u5206\uff0c\u6709\u4e9b\u9ad8\u624b\u5320\u4eba\u80fd\u5c06\u5b9d\u77f3\u96d5\u7422\u5f97\u6b63\u53cd\u9762\u4e00\u822c\u65e0\u5f02\uff0c\u4f46\u5728\u884c\u5bb6\u773c\u4e2d\uff0c\u4ecd\u80fd\u5206\u8fa8\u6e05\u695a\u3002\u90d1\u4e09\u5a18\u9053\uff1a\u201c\u5927\u5e08\uff0c\u8fd9\u5b9d\u77f3\u53cd\u9762\u671d\u4e0a\uff0c\u53ea\u6015\u4e2d\u95f4\u53e6\u6709\u53e4\u602a\u3002\u201d \u3000\u3000\u5b9d\u6811\u6b63\u81ea\ue40b\u5fa8\u65e0\u8ba1\uff0c\u4e00\u542c\u6b64\u8a00\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u7ba1\u5979\u8bf4\u7684\u662f\u5bf9\u662f\u9519\uff0c\u5f04\u5f00\u6765\u77a7\u77a7\u518d\u8bf4\u3002\u201d\u5f53\u4e0b\u63a5\u8fc7\u5200\u6765\uff0c\u4ece\u8eab\u8fb9\u53d6\u51fa\u4e00\u67c4\u5315\u9996\uff0c\u529b\u900f\u6307\u5c16\uff0c\u7528\u5315\u9996\u5c16\u5934\u5728\u5b9d\u77f3\u4e0b\u8f7b\u8f7b\u4e00\u6311\uff0c\u5b9d\u77f3\u79bb\u5200\u8df3\u843d\u3002\u5b9d\u6811\u62c8\u8d77\u5b9d\u77f3\uff0c\u7ec6\u770b\u4e24\u9762\uff0c\u5e76\u65e0\u7279\u5f02\u4e4b\u5904\uff0c\u518d\u5411\u5200\u8eab\u4e0a\u9576\u5d4c\u5b9d\u77f3\u7684\u51f9\u7a9d\u513f\u4e00\u77a7\uff0c\u4e0d\u7981\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5728\u8fd9\u91cc\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u90a3\u7a9d\u513f\u4e4b\u4e2d\uff0c\u523b\u7740\u4e00\u4e2a\u7bad\u5934\uff0c\u6307\u5411\u4e1c\u5317\u504f\u5317\uff0c\u7bad\u5934\u5c3d\u5904\u6709\u4e2a\u5c0f\u5c0f\u7684\u5706\u5708\u3002\u5b9d\u6811\u559c\u4e0d\u81ea\u80dc\uff0c\u5fc3\u60f3\u8fd9\u7a9d\u513f\u6b63\u4e2d\uff0c\u5f53\u662f\u5706\u5cf0\u4e4b\u9876\uff0c\u4e00\u7b97\u8ddd\u79bb\u8fdc\u8fd1\uff0c\u770b\u51c6\u4e86\u65b9\u4f4d\uff0c\u4e00\u6b65\u6b65\u8d70\u5c06\u8fc7\u53bb\uff0c\u5f85\u8d70\u5230\u6240\u8ba1\u4e4b\u5904\uff0c\u679c\u7136\u811a\u4e0b\u677e\u52a8\uff0c\u8eab\u5b50\u4e0b\u843d\u3002\u4ed6\u65e9\u6709\u9632\u5907\uff0c\u53cc\u8db3\u7740\u5730\uff0c\u7acb\u5373\u6643\u4eae\u706b\u6298\uff0c\u62e8\u5f00\u51b0\u96ea\uff0c\u89c1\u524d\u9762\u662f\u6761\u957f\u957f\u7684\u901a\u9053\uff0c\u5f53\u5373\u5411\u524d\u8d70\u53bb\u3002\u5218\u5143\u9e64\u7b49\u4e5f\u8ddf\u7740\u8dc3\u4e0b\u3002 \u3000\u3000\u706b\u6298\u70b9\u4e0d\u591a\u4e45\u5c31\u7184\u4e86\uff0c\u53ef\u662f\u90a3\u5c71\u6d1e\u76d8\u65cb\u66f2\u6298\uff0c\u63a5\u8fde\u8f6c\u4e86\u51e0\u4e2a\u5f2f\uff0c\u4ecd\u662f\u672a\u5230\u5c3d\u5934\u3002 \u3000\u3000\u66f9\u4e91\u5947\u9053\uff1a\u201c\u6211\u53bb\u6298\u4e9b\u67af\u679d\u3002\u201d\u4ed6\u5954\u51fa\u5c71\u6d1e\uff0c\u62b1\u4e86\u4e00\u5927\u6346\u67af\u67f4\u56de\u6765\uff0c\u6253\u706b\u70b9\u71c3\u4e86\u4e00\u6839\u706b\u628a\u3002\u4ed6\u4e3a\u4eba\u5364\u83bd\uff0c\u5374\u4e5f\u6709\u4e00\u6837\u597d\u5904\uff0c\u505a\u4e8b\u52c7\u5f80\u76f4\u524d\uff0c\u624b\u6267\u706b\u628a\uff0c\u5f53\u5148\u800c\u884c\u3002 \u3000\u3000\u6d1e\u4e2d\u5230\u5904\u662f\u5343\u5e74\u4e0d\u5316\u7684\u575a\u51b0\uff0c\u6709\u4e9b\u5904\u6240\u7684\u51b0\u6761\u5982\u5200\u5251\u822c\u950b\u9510\u7a81\u51fa\u3002\u9676\u767e\u5c81\u6367\u4e86\u4e00\u5757\u5927\u77f3\uff0c\u6cbf\u9014\u51fb\u53bb\u963b\u8def\u7684\u51b0\u5c16\u3002\u4f17\u4eba\u4e0a\u5c71\u65f6\u5404\u6000\u654c\u610f\uff0c\u6b64\u65f6\u91cd\u5b9d\u5728\u671b\uff0c\u7adf\u7136\u540c\u821f\u5171\u6d4e\u3001\u76f8\u4e92\u6276\u6301\u8d77\u6765\u3002 \u3000\u3000\u53c8\u8f6c\u4e86\u4e2a\u5f2f\uff0c\u7530\u9752\u6587\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u54a6\uff01\u201d\u6307\u7740\u66f9\u4e91\u5947\u8eab\u524d\u5730\u4e0b\u9ec4\u6f84\u6f84\u7684\u4e00\u7269\u3002\u66f9\u4e91\u5947\u4fef\u8eab\u62fe\u8d77\uff0c\u539f\u6765\u662f\u4e00\u652f\u91d1\u94f8\u7684\u5c0f\u7b14\uff0c\u7b14\u8eab\u4e0a\u523b\u7740\u4e00\u4e2a\u201c\u5b89\u201d\u5b57\uff0c\u5c31\u548c\u7530\u9752\u6587\u4e0a\u5cf0\u4e4b\u524d\u624b\u4e2d\u6240\u62ff\u7684\u4e00\u6a21\u4e00\u6837\u3002\u66f9\u4e91\u5947\u7591\u4e91\u5927\u8d77\uff0c\u56de\u5934\u5bf9\u9676\u5b50\u5b89\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u563f\uff0c\u539f\u6765\u4f60\u5230\u8fd9\u513f\u6765\u8fc7\u5566\uff01\u201d\u9676\u5b50\u5b89\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u6765\u8fc7\uff1f\u4f60\u77a7\u4e00\u8def\u4e0a\u6709\u6ca1\u4eba\u884c\u7684\u75d5\u8ff9\uff1f\u201d\u66f9\u4e91\u5947\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c71\u6d1e\u4e4b\u4e2d\uff0c\u786e\u65e0\u4eba\u884c\u8db3\u8ff9\uff0c\u90a3\u4e48\u4ed6\u8fd9\u679a\u91d1\u7b14\u53c8\u600e\u4f1a\u6389\u5728\u6b64\u5904\uff1f\u201d\u4ed6\u5fc3\u4e2d\u60f3\u5230\u4f55\u4e8b\uff0c\u518d\u4e5f\u85cf\u4e0d\u4f4f\u7247\u523b\uff0c\u5f53\u5373\u644a\u5f00\u624b\u638c\uff0c\u9732\u51fa\u9ec4\u91d1\u5c0f\u7b14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u4f60\u7684\u4e48\uff1f\u4e0a\u9762\u660e\u660e\u523b\u7740\u4f60\u7684\u540d\u5b57\uff01\u201d \u3000\u3000\u9676\u5b50\u5b89\u4e00\u770b\uff0c\u6447\u5934\u9053\uff1a\u201c\u6211\u4ece\u6ca1\u89c1\u8fc7\u3002\u201d\u66f9\u4e91\u5947\u5927\u6012\uff0c\u624b\u638c\u4e00\u7ffb\uff0c\u629b\u7b14\u5728\u5730\uff0c\u63a2\u624b\u6293\u4f4f\u9676\u5b50\u5b89\u8863\u895f\uff0c\u4e00\u53e3\u553e\u6cab\u5410\u4e86\u8fc7\u53bb\uff0c\u559d\u9053\uff1a\u201c\u8fd8\u60f3\u8d56\uff01\u6211\u660e\u660e\u89c1\u5979\u62ff\u7740\u4f60\u9001\u7684\u7b14\u513f\u3002\u201d \u3000\u3000\u8fd9\u5c71\u6d1e\u4e2d\u8f6c\u8eab\u90fd\u4e0d\u65b9\u4fbf\uff0c\u9676\u5b50\u5b89\u54ea\u80fd\u95ea\u907f\uff1f\u8fd9\u4e00\u53e3\u553e\u6cab\uff0c\u6b63\u5410\u5728\u4ed6\u9f3b\u5b50\u5de6\u4fa7\u3002\u4ed6\u5927\u6012\u4e4b\u4e0b\uff0c\u53f3\u811a\u98de\u51fa\uff0c\u8e22\u4e2d\u66f9\u4e91\u5947\u5c0f\u8179\uff0c\u540c\u65f6\u53cc\u624b\u4e00\u62db\u201c\u71d5\u5f52\u5de2\u201d\uff0c\u51fb\u4e2d\u4e86\u5bf9\u65b9\u80f8\u53e3\u3002\u66f9\u4e91\u5947\u8eab\u5b50\u4e00\u9707\uff0c\u629b\u4e0b\u706b\u628a\uff0c\u53f3\u624b\u8fd8\u4e86\u4e00\u62f3\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u6253\u5728\u9676\u5b50\u5b89\u8138\u4e0a\u3002\u706b\u628a\u7184\u706d\uff0c\u6d1e\u4e2d\u4e00\u7247\u6f06\u9ed1\uff0c\u53ea\u542c\u5f97\u4e24\u4eba\u5406\u559d\u6012\u9a82\uff0c\u5939\u7740\u7830\u7830\u84ec\u84ec\u4e4b\u58f0\u3002\u4e24\u4eba\u62f3\u6253\u8db3\u8e22\uff0c\u62db\u62db\u90fd\u51fb\u4e2d\u5bf9\u65b9\uff0c\u5230\u540e\u6765\u626d\u6210\u4e00\u56e2\uff0c\u6eda\u5728\u5730\u4e0b\u3002 \u3000\u3000\u4f17\u4eba\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u9f50\u58f0\u529d\u89e3\u3002\u66f9\u9676\u4e8c\u4eba\u54ea\u91cc\u80af\u542c\uff1f\u5ffd\u542c\u7530\u9752\u6587\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u54ea\u4e00\u4e2a\u518d\u4e0d\u4f4f\u624b\uff0c\u6211\u6c38\u4e0d\u518d\u8ddf\u4ed6\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u66f9\u9676\u4e8c\u4eba\u4e00\u6014\uff0c\u4e0d\u7531\u5f97\u677e\u5f00\u4e86\u624b\uff0c\u7ad9\u8d77\u8eab\u6765\u3002 \u3000\u3000\u53ea\u542c\u718a\u5143\u732e\u5728\u9ed1\u6697\u4e2d\u7ec6\u58f0\u7ec6\u6c14\u7684\u8bf4\u9053\uff1a\u201c\u662f\u6211\u718a\u5143\u732e\uff0c\u627e\u706b\u628a\u70b9\u706b\uff0c\u4e24\u4f4d\u53ef\u522b\u559d\u9519\u4e86\u918b\uff0c\u62f3\u811a\u5f80\u5728\u4e0b\u8eab\u4e0a\u62db\u547c\u3002\u201d\u4ed6\u4f38\u624b\u5728\u5730\u4e0b\u6478\u7d22\uff0c\u6478\u5230\u4e86\u706b\u628a\uff0c\u91cd\u53c8\u70b9\u71c3\u3002\u53ea\u89c1\u66f9\u9676\u4e8c\u4eba\u773c\u9752\u9f3b\u80bf\uff0c\u547c\u547c\u5598\u6c14\uff0c\u56db\u624b\u63e1\u62f3\uff0c\u6012\u76ee\u76f8\u89c6\u3002 \u3000\u3000\u7530\u9752\u6587\u4ece\u6000\u91cc\u53d6\u51fa\u4e00\u652f\u9ec4\u91d1\u5c0f\u7b14\uff0c\u518d\u62fe\u8d77\u5730\u4e0b\u7684\u5c0f\u7b14\uff0c\u5411\u66f9\u4e91\u5947\u9053\uff1a\u201c\u8fd9\u4e24\u652f\u7b14\u679c\u771f\u662f\u4e00\u5bf9\u513f\uff0c\u53ef\u8c01\u8ddf\u4f60\u8bf4\u662f\u4ed6\u7ed9\u6211\u7684\uff1f\u201d\u66f9\u4e91\u5947\u65e0\u8bdd\u53ef\u7b54\uff0c\u7ed3\u7ed3\u5df4\u5df4\u7684\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\u7ed9\u7684\uff0c\u90a3\u4f60\u4ece\u54ea\u513f\u6765\u7684\uff1f\u4e3a\u4ec0\u4e48\u7b14\u4e0a\u53c8\u6709\u4ed6\u540d\u5b57\uff1f\u201d \u3000\u3000\u9676\u767e\u5c81\u63a5\u8fc7\u5c0f\u7b14\uff0c\u770b\u4e86\u4e00\u773c\uff0c\u95ee\u66f9\u4e91\u5947\u9053\uff1a\u201c\u4f60\u5e08\u7236\u662f\u7530\u5f52\u519c\uff0c\u4f60\u5e08\u7956\u662f\u8c01\uff1f\u201d\u66f9\u4e91\u5947\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5e08\u7956\uff1f\u90a3\u662f\u6211\u5e08\u7236\u7684\u7236\u4eb2\uff0c\u4ed6\u8001\u4eba\u5bb6\u8bb3\u4e0a\u5b89\u4e0b\u8c79\u3002\u201d\u9676\u767e\u5c81\u51b7\u7b11\u9053\uff1a\u201c\u662f\u554a\uff01\u7530\u5b89\u8c79\uff0c\u4ed6\u7528\u4ec0\u4e48\u6697\u5668\uff1f\u201d\u66f9\u4e91\u5947\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u6ca1\u89c1\u8fc7\u5e08\u7956\u3002\u201d \u3000\u3000\u9676\u767e\u5c81\u9053\uff1a\u201c\u4f60\u6ca1\u89c1\u8fc7\uff0c\u4f60\u962e\u5e08\u53d4\u7684\u6b66\u827a\u662f\u7530\u5b89\u8c79\u4eb2\u624b\u6240\u6388\uff0c\u4f60\u95ee\u95ee\u4ed6\u3002\u201d \u3000\u3000\u66f9\u4e91\u5947\u8fd8\u6ca1\u5f00\u53e3\uff0c\u962e\u58eb\u4e2d\u5df2\u63a5\u53e3\u9053\uff1a\u201c\u4e91\u5947\u4e0d\u7528\u80e1\u95f9\u5566\u3002 \u3000\u3000\u8fd9\u5bf9\u9ec4\u91d1\u5c0f\u7b14\uff0c\u662f\u4f60\u5e08\u7956\u7237\u6240\u7528\u7684\u6697\u5668\u3002\u201d\u66f9\u4e91\u5947\u54d1\u53e3\u65e0\u8a00\uff0c\u4f46\u5fc3\u4e2d\u7591\u60d1\u4e1d\u6beb\u4e0d\u51cf\u3002 \u3000\u3000\u5b9d\u6811\u9053\uff1a\u201c\u4f60\u4eec\u8981\u4e89\u98ce\u6253\u67b6\uff0c\u4e0d\u59a8\u8bf7\u5230\u5916\u9762\u53bb\u62da\u4e2a\u6b7b\u6d3b\u3002 \u3000\u3000\u6211\u4eec\u53ef\u662f\u8981\u5bfb\u5b9d\u3002\u201d \u3000\u3000\u718a\u5143\u732e\u9ad8\u4e3e\u706b\u628a\u5f53\u5148\u9886\u8def\uff0c\u8f6c\u8fc7\u4e86\u5f2f\u53bb\u3002\u8fd9\u65f6\u6d1e\u7a74\u6108\u6765\u6108\u7a84\uff0c\u4f17\u4eba\u987b\u5f97\u5f13\u8eab\u800c\u884c\uff0c\u6709\u65f6\u5934\u9876\u649e\u4e0a\u4e86\u575a\u51b0\u5c16\u89d2\uff0c\u9690\u9690\u751f\u75bc\uff0c\u4f46\u60f3\u5230\u91cd\u5b9d\u5728\u671b\uff0c\u4e5f\u90fd\u4e0d\u4ee5\u4e3a\u82e6\u3002 \u3000\u3000\u884c\u4e86\u4e00\u76cf\u8336\u65f6\u5206\uff0c\u524d\u9762\u5df2\u65e0\u53bb\u8def\uff0c\u53ea\u89c1\u4e00\u5757\u5706\u5f62\u5de8\u5ca9\u53e0\u5728\u53e6\u4e00\u5757\u5706\u5ca9\u4e0a\uff0c\u4e24\u5757\u5de8\u5ca9\u5c01\u4f4f\u4e86\u53bb\u8def\u3002\u4e24\u5ca9\u4e4b\u95f4\u90fd\u662f\u575a\u51b0\u7262\u7262\u51dd\u7ed3\u3002\u718a\u5143\u732e\u4f38\u624b\u4e00\u63a8\uff0c\u5de8\u5ca9\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u8f6c\u8fc7\u5934\u6765\uff0c\u95ee\u5b9d\u6811\u9053\uff1a\u201c\u600e\u4e48\u529e\uff1f\u201d\u5b9d\u6811\u6414\u5934\u4e0d\u8bed\u3002 \u3000\u3000\u7fa4\u8c6a\u4e4b\u4e2d\u4ee5\u6bb7\u5409\u6700\u6709\u667a\u8ba1\uff0c\u4ed6\u5fae\u4e00\u6c89\u541f\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u5757\u5706\u77f3\u76f8\u53e0\uff0c\u5fc5\u53ef\u63a8\u52a8\uff0c\u53ea\u662f\u7ed9\u51b0\u51bb\u4f4f\u4e86\u3002\u201d\u5b9d\u6811\u559c\u9053\uff1a\u201c\u5bf9\uff0c\u628a\u51b0\u878d\u5f00\u5c31\u662f\u3002\u201d\u718a\u5143\u732e\u4fbf\u5c06\u706b\u628a\u51d1\u8fd1\u5706\u5ca9\uff0c\u53bb\u70e7\u4e8c\u5ca9\u4e4b\u95f4\u7684\u575a\u51b0\u3002\u66f9\u4e91\u5947\u3001\u5468\u4e91\u9633\u7b49\u56de\u5230\u5916\u9762\uff0c\u53c8\u62fe\u4e86\u4e9b\u67f4\u679d\u6765\u52a0\u706b\u3002\u706b\u7130\u8d8a\u70e7\u8d8a\u5927\uff0c\u51b0\u5316\u4e3a\u6c34\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u4e4b\u58f0\u4e0d\u7edd\uff0c\u4e00\u5757\u5757\u788e\u51b0\u843d\u5728\u5730\u4e0b\u3002 \u3000\u3000\u773c\u89c1\u4e8c\u5ca9\u4e4b\u95f4\u7684\u575a\u51b0\u5df2\u878d\u53bb\u5927\u534a\uff0c\u5b9d\u6811\u6027\u6025\uff0c\u53cc\u624b\u5728\u5de8\u5ca9\u4e0a\u8fd0\u529b\u4e00\u63a8\uff0c\u90a3\u5ca9\u77f3\u6beb\u4e0d\u52a8\u5f39\uff0c\u518d\u70e7\u4e00\u9635\uff0c\u575a\u51b0\u878d\u53bb\u66f4\u591a\uff0c\u5b9d\u6811\u7b2c\u4e8c\u6b21\u518d\u63a8\u65f6\uff0c\u90a3\u5de8\u5ca9\u6643\u4e86\u51e0\u6643\uff0c\u7adf\u6162\u6162\u8f6c\u5c06\u8fc7\u53bb\uff0c\u9732\u51fa\u4e00\u9053\u7a7a\u9699\uff0c\u5b9b\u4f3c\u4e2a\u5929\u9020\u5730\u8bbe\u7684\u77f3\u95e8\u4e00\u822c\u3002 \u3000\u3000\u4f17\u4eba\u5927\u559c\uff0c\u9f50\u58f0\u6b22\u547c\u8d77\u6765\u3002\u962e\u58eb\u4e2d\u4f38\u624b\u76f8\u52a9\uff0c\u548c\u5b9d\u6811\u4e8c\u4eba\u5408\u529b\uff0c\u5c06\u7a7a\u9699\u63a8\u5927\u3002\u5b9d\u6811\u4ece\u706b\u5806\u91cc\u62fe\u8d77\u4e00\u6839\u67f4\u679d\uff0c\u5f53\u5148\u800c\u5165\u3002\u4f17\u4eba\u5404\u6267\u706b\u628a\uff0c\u7eb7\u7eb7\u8ddf\u8fdb\u3002\u4e00\u8e0f\u8fdb\u77f3\u95e8\uff0c\u4e00\u9635\u91d1\u5149\u7167\u5c04\uff0c\u4eba\u4eba\u773c\u82b1\u7f2d\u4e71\uff0c\u51dd\u795e\u5c4f\u6c14\uff0c\u4e2a\u4e2a\u5f20\u5927\u4e86\u53e3\u5408\u4e0d\u62e2\u6765\u3002 \u3000\u3000\u539f\u6765\u91cc\u9762\u7adf\u662f\u4e2a\u6781\u5927\u7684\u6d1e\u7a74\uff0c\u56db\u9762\u5806\u6ee1\u4e86\u91d1\u7816\u94f6\u5757\uff0c\u73cd\u73e0\u5b9d\u77f3\uff0c\u4e0d\u8ba1\u5176\u6570\u3002\u53ea\u662f\u91d1\u94f6\u73e0\u5b9d\u90fd\u9690\u5728\u900f\u660e\u7684\u575a\u51b0\u4e4b\u540e\u3002\u6599\u60f3\u5f53\u5e74\u95ef\u738b\u7684\u90e8\u5c5e\u628a\u91d1\u94f6\u73e0\u5b9d\u85cf\u5165\u4e4b\u540e\uff0c\u6d47\u4e0a\u51b7\u6c34\u3002\u8be5\u5730\u7ec8\u5e74\u9177\u5bd2\uff0c\u575a\u51b0\u4e0d\u6eb6\uff0c\u91d1\u73e0\u5c31\u4f3c\u85cf\u5728\u6c34\u6676\u4e4b\u4e2d\u4e00\u822c\u3002\u5404\u4eba\u773c\u671b\u91d1\u94f6\u73e0\u5b9d\uff0c\u597d\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u4e00\u65f6\u6d1e\u4e2d\u5bc2\u9759\u65e0\u58f0\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u6b22\u547c\u4e4b\u58f0\u5927\u4f5c\u3002\u5b9d\u6811\u3001\u9676\u767e\u5c81\u7b49\u90fd\u6251\u5230\u51b0\u4e0a\uff0c\u4e0d\u77e5\u8bf4\u4ec0\u4e48\u597d\u3002 \u3000\u3000\u5ffd\u7136\u7530\u9752\u6587\u60ca\u547c\uff1a\u201c\u6709\u4eba\uff01\u201d\u6307\u7740\u58c1\u5185\u3002\u706b\u5149\u7167\u8000\u4e0b\u679c\u89c1\u6709\u4e24\u4e2a\u9ed1\u5f71\uff0c\u7ad9\u5728\u9760\u58c1\u4e4b\u5904\u3002 \u3000\u3000\u4f17\u4eba\u8fd9\u4e00\u60ca\u76f4\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u4e07\u60f3\u4e0d\u5230\u6d1e\u5185\u7adf\u4f1a\u6709\u4eba\uff0c\u96be\u9053\u6d1e\u7a74\u53e6\u6709\u5165\u53e3\u4e4b\u5904\uff1f\u5404\u4eba\u624b\u6267\u5175\u5203\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u76f8\u4e92\u9760\u5728\u4e00\u8d77\u3002\u9694\u4e86\u597d\u4e00\u4f1a\uff0c\u53ea\u89c1\u4e24\u4e2a\u9ed1\u5f71\u7adf\u7136\u4e00\u52a8\u4e5f\u4e0d\u52a8\u3002\u5b9d\u6811\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u91cc\u9762\u4e24\u4eba\u5e76\u4e0d\u56de\u7b54\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u4e8c\u4eba\u59cb\u7ec8\u4e0d\u52a8\uff0c\u5fc3\u4e0b\u60ca\u7591\u66f4\u751a\u3002\u5b9d\u6811\u9053\uff1a\u201c\u662f\u54ea\u4e00\u4f4d\u524d\u8f88\u9ad8\u4eba\uff0c\u8bf7\u51fa\u6765\u76f8\u89c1\u3002\u201d\u4ed6\u559d\u58f0\u88ab\u6d1e\u7a74\u56db\u58c1\u4e00\u6fc0\uff0c\u53cd\u5c04\u56de\u6765\uff0c\u53ea\u9707\u5f97\u5404\u4eba\u8033\u4e2d\u55e1\u55e1\u7684\u751a\u4e0d\u597d\u53d7\uff0c\u4f46\u90a3\u4e24\u4eba\u65e2\u4e0d\u56de\u7b54\uff0c\u4ea6\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5b9d\u6811\u4e3e\u8d77\u706b\u628a\uff0c\u8d70\u8fd1\u51e0\u6b65\uff0c\u770b\u6e05\u695a\u4e24\u4e2a\u9ed1\u5f71\u662f\u5728\u4e00\u5c42\u575a\u51b0\u4e4b\u5916\uff0c\u8fd9\u4e00\u5c42\u51b0\u5c31\u5982\u4e00\u5835\u6c34\u6676\u5899\u822c\uff0c\u5c06\u6d1e\u7a74\u9694\u4e3a\u524d\u540e\u4e24\u95f4\u3002 \u3000\u3000\u5b9d\u6811\u5927\u7740\u80c6\u5b50\uff0c\u903c\u8fd1\u51b0\u5899\uff0c\u89c1\u90a3\u4e24\u4eba\u60c5\u72b6\u602a\u5f02\uff0c\u59cb\u7ec8\u4e0d\u52a8\uff0c\u663e\u662f\u88ab\u70b9\u4e2d\u4e86\u7a74\u9053\u3002\u8fd9\u65f6\u4ed6\u54ea\u91cc\u8fd8\u6709\u5fcc\u60ee\uff0c\u53eb\u9053\uff1a\u201c\u5927\u5bb6\u968f\u6211\u6765\u3002\u201d \u3000\u3000\u5927\u8e0f\u6b65\u7ed5\u8fc7\u51b0\u5899\uff0c\u4ed6\u53f3\u624b\u63d0\u8d77\u5355\u5200\uff0c\u5de6\u624b\u4e3e\u706b\u628a\u5f80\u4e24\u4eba\u8138\u4e0a\u4e00\u7167\uff0c\u4e0d\u7981\u5012\u62bd\u4e00\u53e3\u6c14\u3002\u539f\u6765\u90a3\u4e8c\u4eba\u65e9\u5df2\u6b7b\u53bb\u591a\u65f6\uff0c\u9762\u76ee\u72f0\u72de\uff0c\u8138\u4e0a\u7b4b\u8089\u62bd\u6410\uff0c\u5f02\u5e38\u53ef\u6016\u3002 \u3000\u3000\u90d1\u4e09\u5a18\u4e0e\u7530\u9752\u6587\u89c1\u662f\u6b7b\u4eba\uff0c\u90fd\u5c16\u58f0\u60ca\u547c\u51fa\u6765\u3002\u5404\u4eba\u8d70\u8fd1\u5c38\u8eab\uff0c\u89c1\u90a3\u4e8c\u4eba\u53f3\u624b\u5404\u6267\u5315\u9996\uff0c\u63d2\u5728\u5bf9\u65b9\u8eab\u4e0a\uff0c\u4e00\u4e2d\u524d\u80f8\uff0c\u4e00\u4e2d\u5c0f\u8179\uff0c\u81ea\u662f\u76f8\u4e92\u6740\u6b7b\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u770b\u6e05\u695a\u4e00\u5c38\u7684\u9762\u8c8c\uff0c\u7a81\u7136\u62dc\u4f0f\u5728\u5730\uff0c\u54ed\u9053\uff1a\u201c\u6069\u5e08\uff0c\u539f\u6765\u4f60\u8001\u4eba\u5bb6\u5728\u8fd9\u91cc\u3002\u201d\u4f17\u4eba\u542c\u4ed6\u8fd9\u822c\u8bf4\uff0c\u90fd\u662f\u4e00\u60ca\uff0c\u9f50\u95ee\uff1a \u3000\u3000\u201c\u600e\u4e48\uff1f\u201c\u8fd9\u4e8c\u4eba\u662f\u8c01\uff1f\u201d\u201c\u662f\u4f60\u5e08\u7236\uff1f\u201d\u201c\u600e\u4e48\u4f1a\u6b7b\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u962e\u58eb\u4e2d\u62b9\u4e86\u62b9\u773c\u6cea\uff0c\u6307\u7740\u90a3\u8eab\u6750\u8f83\u77ee\u7684\u5c38\u8eab\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6211\u7530\u6069\u5e08\u3002\u4e91\u5947\u521a\u624d\u62fe\u5230\u7684\u9ec4\u91d1\u5c0f\u7b14\uff0c\u5c31\u662f\u6211\u6069\u5e08\u7684\u3002\u201d \u3000\u3000\u4f17\u4eba\u89c1\u7530\u5b89\u8c79\u7684\u5bb9\u8c8c\u77a7\u6765\u5e74\u7eaa\u4e0d\u8fc7\u56db\u5341\uff0c\u6bd4\u962e\u58eb\u4e2d\u8fd8\u8981\u5e74\u8f7b\uff0c\u521d\u65f6\u89c9\u5f97\u5947\u602a\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u968f\u5373\u604d\u7136\u3002\u8fd9\u4e24\u5177\u5c38\u4f53\u5176\u5b9e\u6b7b\u53bb\u5df2\u6570\u5341\u5e74\uff0c\u53ea\u662f\u6d1e\u4e2d\u4e25\u5bd2\uff0c\u5c38\u8eab\u4e0d\u8150\uff0c\u7adf\u4f3c\u6b7b\u53bb\u4e0d\u8fc7\u6570\u5929\u4e00\u822c\u3002 \u3000\u3000\u66f9\u4e91\u5947\u6307\u7740\u53e6\u4e00\u5177\u5c38\u4f53\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u6b64\u4eba\u662f\u8c01\uff1f\u4ed6\u600e\u6562\u5bb3\u6b7b\u54b1\u4eec\u5e08\u7956\u7237\uff1f\u201d\u8bf4\u7740\u5411\u90a3\u5c38\u4f53\u8e22\u4e86\u4e00\u811a\u3002\u4f17\u4eba\u89c1\u8fd9\u5c38\u4f53\u8eab\u5f62\u9ad8\u7626\uff0c\u56db\u80a2\u957f\u5927\uff0c\u90fd\u5df2\u731c\u5230\u4e86\u516b\u4e5d\u5206\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u91d1\u9762\u4f5b\u7684\u7236\u4eb2\uff0c\u6211\u4ece\u5c0f\u53eb\u4ed6\u82d7\u7237\u3002\u4ed6\u4e0e\u6211\u6069\u5e08\u7d20\u6765\u4ea4\u597d\uff0c\u6709\u4e00\u5e74\u7ed3\u4f34\u540c\u53bb\u5173\u5916\uff0c\u5f53\u65f6\u6211\u4eec\u4e0d\u77e5\u4e3a\u4e86\u4f55\u4e8b\uff0c\u4f46\u89c1\u4ed6\u4e8c\u4eba\u5174\u9ad8\u91c7\u70c8\uff0c\u6b22\u6b22\u559c\u559c\u800c\u53bb\uff0c\u53ef\u662f\u4ece\u6b64\u4e0d\u89c1\u5f52\u6765\u3002\u6b66\u6797\u4e2d\u670b\u53cb\u540e\u6765\u4f20\u8a00\uff0c\u8bf4\u9053\u4ed6\u4eec\u4e24\u4f4d\u4e3a\u8fbd\u4e1c\u5927\u8c6a\u80e1\u4e00\u5200\u6240\u5bb3\uff0c\u6240\u4ee5\u91d1\u9762\u4f5b\u4e0e\u7530\u5e08\u5144\u4ed6\u4eec\u624d\u5927\u4e3e\u5411\u80e1\u4e00\u5200\u5bfb\u4ec7\uff0c\u54ea\u77e5\u9053\u82d7\u2026\u2026\u82d7\uff0c\u8fd9\u59d3\u82d7\u7684\u8d22\u8ff7\u5fc3\u7a8d\uff0c\u89c1\u5230\u6d1e\u4e2d\u73cd\u5b9d\uff0c\u7adf\u5411\u6211\u6069\u5e08\u4e0b\u4e86\u6bd2\u624b\u3002\u201d\u8bf4\u7740\u4e5f\u5411\u90a3\u5c38\u8eab\u817f\u4e0a\u8e22\u4e86\u4e00\u811a\u3002\u90a3\u82d7\u7530\u4e8c\u4eba\u6b7b\u540e\uff0c\u5168\u8eab\u51bb\u5f97\u50f5\u786c\uff0c\u962e\u58eb\u4e2d\u4e00\u811a\u8e22\u53bb\uff0c\u5c38\u8eab\u4ecd\u662f\u633a\u7acb\u4e0d\u5012\uff0c\u4ed6\u81ea\u5df1\u8db3\u5c16\u5374\u78b0\u5f97\u9690\u9690\u751f\u75bc\u3002 \u3000\u3000\u4f17\u4eba\u5fc3\u60f3\uff1a\u201c\u8c01\u77e5\u4e0d\u662f\u4f60\u5e08\u7236\u8d22\u8ff7\u5fc3\u7a8d\uff0c\u5148\u4e0b\u6bd2\u624b\u5462\uff1f\u201d \u3000\u3000\u962e\u58eb\u4e2d\u4f38\u624b\u53bb\u63a8\u90a3\u59d3\u82d7\u7684\u5c38\u8eab\uff0c\u60f3\u5c06\u4ed6\u63a8\u79bb\u5e08\u7236\u3002\u4f46\u82d7\u7530\u4e8c\u4eba\u8fd9\u6837\u7ea0\u7f20\u7740\u5df2\u8fbe\u6570\u5341\u5e74\uff0c\u624b\u8fde\u5200\uff0c\u5200\u8fde\u8eab\uff0c\u575a\u51b0\u51dd\u7ed3\uff0c\u5374\u54ea\u91cc\u63a8\u5f97\u5f00\uff1f \u3000\u3000\u9676\u767e\u5c81\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5f53\u5e74\u80e1\u4e00\u5200\u6258\u4eba\u5411\u82d7\u5927\u4fa0\u548c\u7530\u5f52\u519c\u8bf4\u9053\uff0c\u4ed6\u77e5\u9053\u82d7\u7530\u4e24\u5bb6\u4e0a\u4ee3\u7684\u6b7b\u56e0\uff0c\u4e0d\u8fc7\u8fd9\u4e24\u4eba\u6b7b\u5f97\u592a\u4e5f\u4e0d\u591f\u4f53\u9762\uff0c\u4ed6\u4e0d\u4fbf\u5f53\u9762\u8ff0\u8bf4\uff0c\u53ea\u597d\u9886\u4ed6\u4eec\u4eb2\u81ea\u53bb\u770b\u3002\u73b0\u4e0b\u54b1\u4eec\u4eb2\u773c\u76ee\u7779\uff0c\u4ed6\u8fd9\u8bdd\u679c\u7136\u4e0d\u9519\u3002\u5982\u6b64\u8bf4\u6765\uff0c\u80e1\u4e00\u5200\u5fc5\u662f\u66fe\u7ecf\u6765\u8fc7\u6b64\u95f4\uff0c\u4f46\u4ed6\u89c1\u4e86\u5b9d\u85cf\uff0c\u5374\u4e0d\u6398\u53d6\uff0c\u5b9e\u4e0d\u77e5\u4f55\u6545\u3002\u201d \u3000\u3000\u7530\u9752\u6587\u5ffd\u9053\uff1a\u201c\u6211\u4eca\u65e5\u9047\u4e0a\u4e00\u4e8b\uff0c\u5f88\u662f\u5947\u602a\u3002\u201d\u962e\u58eb\u4e2d\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d\u7530\u9752\u6587\u9053\uff1a\u201c\u54b1\u4eec\u4eca\u65e5\u65e9\u6668\u8ffd\u8d76\u4ed6\u2026\u2026\u4ed6\u2026\u2026\u201d\u8bf4\u7740\u5634\u5507\u5411\u9676\u5b50\u5b89\u4e00\u52aa\uff0c\u8138\u4e0a\u5fae\u73b0\u7ea2\u6655\uff0c\u7eed\u9053\uff1a\u201c\u5e08\u53d4\u4f60\u4eec\u8d76\u5728\u524d\u5934\uff0c\u6211\u843d\u5728\u540e\u9762\u2026\u2026\u201d\u66f9\u4e91\u5947\u5fcd\u8010\u4e0d\u4f4f\uff0c\u559d\u9053\uff1a\u201c\u4f60\u9a91\u7684\u9a6c\u6700\u597d\uff0c\u600e\u4e48\u53cd\u800c\u843d\u5728\u540e\u9762\uff1f\u4f60\u2026\u2026\u4f60\u2026\u2026\u5c31\u662f\u4e0d\u80af\u8ddf\u8fd9\u59d3\u9676\u7684\u52a8\u624b\u3002\u201d\u7530\u9752\u6587\u5411\u4ed6\u77a7\u4e5f\u4e0d\u77a7\uff0c\u5e7d\u5e7d\u7684\u9053\uff1a\u201c\u4f60\u5bb3\u4e86\u6211\u4e00\u4e16\uff0c\u8981\u518d\u600e\u6837\u6298\u78e8\u6211\uff0c\u4e5f\u53ea\u597d\u7531\u5f97\u4f60\u3002\u9676\u5b50\u5b89\u662f\u6211\u4e08\u592b\uff0c\u6211\u5bf9\u4ed6\u4e0d\u8d77\u3002\u4ed6\u867d\u7136\u4e0d\u80fd\u518d\u8981\u6211\uff0c\u53ef\u662f\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u6211\u5fc3\u91cc\u51b3\u4e0d\u80fd\u518d\u6709\u65c1\u4eba\u3002\u201d \u3000\u3000\u9676\u5b50\u5b89\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6211\u5f53\u7136\u8981\u4f60\uff0c\u9752\u59b9\uff0c\u6211\u5f53\u7136\u8981\u4f60\u3002\u201d\u9676\u767e\u5c81\u4e0e\u66f9\u4e91\u5947\u9f50\u58f0\u6012\u559d\uff0c\u4e00\u4e2a\u9053\uff1a\u201c\u4f60\u8981\u8fd9\u8d31\u4eba\uff1f\u6211\u53ef\u4e0d\u8981\u5979\u4f5c\u513f\u5ab3\u5987\u3002\u201d\u4e00\u4e2a\u9053\uff1a\u201c\u4f60\u6709\u672c\u4e8b\u5c31\u5148\u6740\u4e86\u6211\u3002\u201d\u4e24\u4eba\u540c\u65f6\u9ad8\u58f0\u5927\u53eb\uff0c\u6d1e\u4e2d\u56de\u97f3\u53c8\u5927\uff0c\u6df7\u5728\u4e00\u8d77\uff0c\u7adf\u542c\u4e0d\u51fa\u4ed6\u4e8c\u4eba\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u7530\u9752\u6587\u773c\u671b\u5730\u4e0b\uff0c\u5f85\u4ed6\u4eec\u53eb\u58f0\u505c\u6b47\uff0c\u8f7b\u8f7b\u9053\uff1a\u201c\u4f60\u867d\u7136\u8981\u6211\uff0c\u53ef\u662f\uff0c\u6211\u600e\u4e48\u8fd8\u6709\u8138\u518d\u6765\u8ddf\u4f60\uff1f\u51fa\u6d1e\u4e4b\u540e\uff0c\u4f60\u6c38\u8fdc\u522b\u518d\u89c1\u6211\u4e86\u3002\u201d\u9676\u5b50\u5b89\u6025\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u9752\u59b9\uff0c\u90fd\u662f\u4ed6\u4e0d\u597d\u3002\u4ed6\u6b3a\u4fae\u4f60\uff0c\u6298\u78e8\u4f60\uff0c\u6211\u8ddf\u4ed6\u62da\u4e86\u3002\u201d\u63d0\u8d77\u5355\u5200\uff0c\u76f4\u5954\u66f9\u4e91\u5947\u3002 \u3000\u3000\u5218\u5143\u9e64\u6321\u5728\u4ed6\u8eab\u524d\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u4e89\u98ce\u5403\u918b\uff0c\u5230\u5916\u9762\u53bb\u6253\u3002\u201d \u3000\u3000\u5de6\u638c\u865a\u626c\uff0c\u53f3\u624b\u4e00\u4f38\uff0c\u6263\u4f4f\u4ed6\u7684\u624b\u8155\uff0c\u8f7b\u8f7b\u4e00\u626d\uff0c\u593a\u4e0b\u4e86\u4ed6\u624b\u4e2d\u5355\u5200\uff0c\u629b\u5728\u5730\u4e0b\u3002\u90a3\u4e00\u8fb9\u66f9\u4e91\u5947\u66b4\u8df3\u4e0d\u5df2\uff0c\u4e5f\u7ed9\u6bb7\u5409\u62e6\u7740\u3002\u4f59\u4eba\u89c1\u7530\u9752\u6587\u4ee5\u9000\u4e3a\u8fdb\uff0c\u5c06\u9676\u66f9\u4e8c\u4eba\u800d\u5f97\u670d\u670d\u8d34\u8d34\uff0c\u5fc3\u4e2d\u90fd\u662f\u6697\u6697\u597d\u7b11\u3002 \u3000\u3000\u5b9d\u6811\u9053\uff1a\u201c\u7530\u59d1\u5a18\uff0c\u4f60\u7231\u5ac1\u8c01\u5c31\u5ac1\u8c01\uff0c\u603b\u4e0d\u80fd\u5ac1\u6211\u8fd9\u548c\u5c1a\u3002 \u3000\u3000\u6240\u4ee5\u8001\u548c\u5c1a\u53ea\u95ee\u4f60\uff0c\u4f60\u4eca\u65e5\u65e9\u6668\u9047\u89c1\u4e86\u4ec0\u4e48\u602a\u4e8b\u3002\u201d \u3000\u3000\u4f17\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u7530\u9752\u6587\u4e5f\u662f\u5657\u54e7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u7684\u9a6c\u513f\u8d70\u5f97\u6162\uff0c\u8d76\u4e0d\u4e0a\u5e08\u53d4\u4ed6\u4eec\uff0c\u6b63\u884c\u4e4b\u95f4\uff0c\u5ffd\u542c\u5f97\u9a6c\u8e44\u58f0\u54cd\uff0c\u4e00\u4e58\u9a6c\u4ece\u540e\u9762\u9a70\u6765\u3002\u9a6c\u4e0a\u7684\u4e58\u5ba2\u624b\u91cc\u62ff\u7740\u4e00\u4e2a\u5927\u846b\u82a6\uff0c\u4ef0\u8116\u5b50\u5c31\u7740\u846b\u82a6\u5634\u559d\u9152\u3002\u6211\u89c1\u4ed6\u6ee1\u8138\u7edc\u816e\u80e1\u5b50\uff0c\u5728\u9a6c\u4e0a\u9189\u5f97\u6447\u6447\u6643\u6643\uff0c\u8fd8\u662f\u5495\u565c\u5495\u565c\u7684\u5927\u559d\uff0c\u4e0d\u7981\u7b11\u4e86\u4e00\u58f0\u3002\u4ed6\u8f6c\u8fc7\u5934\u6765\uff0c\u95ee\u9053\u2018\u4f60\u662f\u7530\u5f52\u519c\u7684\u5973\u513f\uff0c\u662f\u4e0d\u662f\uff1f\u2019\u6211\u9053\uff1a\u2018\u662f\u554a\uff0c\u5c0a\u9a7e\u662f\u8c01\uff1f\u2019\u4ed6\u8bf4\u9053\uff1a\u2018\u8fd9\u4e2a\u7ed9\u4f60\uff01\u2019\u624b\u6307\u4e00\u5f39\uff0c\u5c06\u8fd9\u9ec4\u91d1\u5c0f\u7b14\u5f39\u4e86\u8fc7\u6765\uff0c\u4ece\u6211\u8138\u65c1\u64e6\u8fc7\uff0c\u6253\u843d\u4e86\u6211\u7684\u8033\u73af\u3002\u6211\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u5374\u7eb5\u9a6c\u8d70\u4e86\u3002 \u3000\u3000\u6211\u5fc3\u4e0b\u4e00\u76f4\u5728\u5600\u5495\uff0c\u4e0d\u77e5\u4ed6\u4e3a\u4ec0\u4e48\u7ed9\u6211\u8fd9\u652f\u5c0f\u7b14\u3002\u201d \u3000\u3000\u5b9d\u6811\u95ee\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u6b64\u4eba\u4e48\uff1f\u201d\u7530\u9752\u6587\u70b9\u70b9\u5934\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u5c31\u662f\u90a3\u4e2a\u80e1\u6590\u3002\u4ed6\u7ed9\u6211\u5c0f\u7b14\u4e4b\u65f6\uff0c\u6211\u81ea\u7136\u4e0d\u8ba4\u5f97\u4ed6\uff0c\u4ed6\u540e\u6765\u4e0a\u5f97\u5c71\u6765\uff0c\u4e0e\u82d7\u5bb6\u59b9\u5b50\u8bf4\u8bdd\uff0c\u6211\u8ba4\u51fa\u4e86\u4ed6\u7684\u58f0\u97f3\uff0c\u518d\u5728\u677f\u58c1\u7f1d\u4e2d\u4e00\u5f20\uff0c\u679c\u7136\u662f\u4ed6\u3002\u201d\u66f9\u4e91\u5947\u918b\u5fc3\u53c8\u8d77\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u5c0f\u7b14\u65e2\u662f\u5e08\u7956\u7237\u7684\uff0c\u90a3\u80e1\u6590\u4ece\u4f55\u5904\u5f97\u6765\uff1f\u4ed6\u7ed9\u4f60\u5e72\u4e48\uff1f\u201d \u3000\u3000\u7530\u9752\u6587\u5bf9\u522b\u4eba\u8bf4\u8bdd\u6e29\u8a00\u8f6f\u8bed\uff0c\u4f46\u4e00\u542c\u66f9\u4e91\u5947\u8bf4\u8bdd\uff0c\u7acb\u65f6\u6709\u4e0d\u6109\u4e4b\u8272\uff0c\u5168\u4e0d\u7406\u776c\u3002 \u3000\u3000\u5218\u5143\u9e64\u9053\uff1a\u201c\u90a3\u80e1\u4e00\u5200\u65e2\u66fe\u6765\u8fc7\u6b64\u95f4\uff0c\u5b9a\u662f\u5728\u5730\u4e0b\u62fe\u5230\uff0c\u6216\u5728\u7530\u5b89\u8c79\u8eab\u4e0a\u5f97\u5230\u6b64\u7b14\u3002\u53ea\u662f\u4ed6\u6b7b\u65f6\u80e1\u6590\u751f\u4e0b\u4e0d\u8fc7\u51e0\u5929\uff0c\u600e\u80fd\u5c06\u5c0f\u7b14\u7559\u4f20\u7ed9\u4ed6\uff1f\u201d\u718a\u5143\u732e\u9053\uff1a\u201c\u8bf4\u4e0d\u5b9a\u4ed6\u5c06\u5c0f\u7b14\u7559\u5728\u5bb6\u4e2d\uff0c\u540e\u6765\u80e1\u6590\u5e74\u957f\uff0c\u56de\u5230\u6545\u5c45\uff0c\u81ea\u7136\u5728\u7236\u4eb2\u7684\u9057\u7269\u4e2d\u5bfb\u7740\u4e86\u3002\u201d\u962e\u58eb\u4e2d\u70b9\u5934\u9053\uff1a\u201c\u90a3\u4e5f\u672a\u59cb\u4e0d\u53ef\u3002\u8fd9\u5c0f\u7b14\u4e2d\u7a7a\uff0c\u7b14\u5934\u53ef\u4ee5\u65cb\u4e0b\uff0c\u9752\u6587\u3002\u4f60\u77a7\u77a7\u7b14\u91cc\u6709\u4f55\u7269\u4e8b\u3002\u201d \u3000\u3000\u7530\u9752\u6587\u5148\u5c06\u6d1e\u7a74\u4e2d\u62fe\u5230\u7684\u5c0f\u7b14\u65cb\u4e0b\u7b14\u5934\uff0c\u7b14\u5185\u7a7a\u65e0\u4e00\u7269\uff0c\u518d\u5c06\u80e1\u6590\u63b7\u6765\u7684\u5c0f\u7b14\u7b14\u5934\u65cb\u4e0b\uff0c\u53ea\u89c1\u7b14\u7ba1\u5185\u85cf\u7740\u4e00\u4e2a\u5c0f\u5c0f\u7eb8\u5377\u3002\u4f17\u4eba\u4e00\u9f50\u56f4\u62e2\uff0c\u5747\u60f3\u82e5\u65e0\u962e\u58eb\u4e2d\u5728\u6b64\uff0c\u5b9e\u4e0d\u6613\u60f3\u5230\u8fd9\u6697\u5668\u6253\u9020\u5f97\u5982\u6b64\u7cbe\u5de7\uff0c\u7b14\u7ba1\u5185\u5c45\u7136\u8fd8\u53ef\u85cf\u7269\u3002 \u3000\u3000\u53ea\u89c1\u7530\u9752\u6587\u644a\u5f00\u7eb8\u5377\uff0c\u7eb8\u4e0a\u5199\u7740\u5341\u516d\u4e2a\u5b57\uff0c\u9053\uff1a\u201c\u5929\u9f99\u8bf8\u516c\uff0c\u9a7e\u4e34\u8fbd\u4e1c\uff0c\u6765\u65f6\u4e58\u9a6c\uff0c\u5f52\u65f6\u5fa1\u98ce\u3002\u201d\u7eb8\u89d2\u4e0b\u753b\u7740\u4e00\u53ea\u80cc\u4e0a\u751f\u7fc5\u8180\u7684\u72d0\u72f8\uff0c\u8fd9\u5341\u516d\u5b57\u6b63\u662f\u7684\u624b\u7b14\u3002 \u3000\u3000\u962e\u58eb\u4e2d\u8138\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u563f\uff0c\u4e5f\u672a\u5fc5\u5982\u6b64\uff01\u201d\u4ed6\u8bdd\u662f\u8fd9\u4e48\u8bf4\uff0c\u4f46\u60f3\u5230\u80e1\u6590\u7684\u672c\u9886\uff0c\u53c8\u60f3\u5230\u4ed6\u5bf9\u5929\u9f99\u95e8\u4eba\u7684\u884c\u8e2a\u77e5\u9053\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5374\u4e5f\u4e0d\u7981\u6817\u6817\u81ea\u5371\u3002\u66f9\u4e91\u5947\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u4ec0\u4e48\u53eb\u2018\u5f52\u65f6\u5fa1\u98ce\u2019\uff1f\u201d\u962e\u58eb\u4e2d\u9053\uff1a\u201c\u54fc\uff0c\u4ed6\u8bf4\u54b1\u4eec\u90fd\u8981\u6b7b\u5728\u8fbd\u4e1c\uff0c\u53d8\u6210\u4ed6\u4e61\u4e4b\u9b3c\uff0c\u9b42\u9b44\u98d8\u98d8\u8361\u8361\u7684\u4e58\u98ce\u56de\u53bb\u3002\u201d\u66f9\u4e91\u5947\u9a82\u9053\uff1a\u201c\u64cd\u4ed6\u5976\u5976\u7684\u718a\uff01\u201d \u3000\u3000\u5929\u9f99\u95e8\u8bf8\u4eba\u77a7\u7740\u90a3\u5c0f\u67ec\uff0c\u5404\u81ea\u6c89\u601d\u3002\u5b9d\u6811\u3001\u9676\u767e\u5c81\u3001\u5218\u5143\u9e64\u7b49\u8bf8\u4eba\uff0c\u76ee\u5149\u5374\u65e9\u8f6c\u5230\u56db\u4e0b\u91cc\u7684\u91d1\u94f6\u73e0\u5b9d\u4e4b\u4e0a\u3002\u5b9d\u6811\u53d6\u8fc7\u4e00\u67c4\u5355\u5200\uff0c\u5c31\u5f80\u51b0\u4e0a\u780d\u53bb\uff0c\u4ed6\u780d\u4e86\u51e0\u5200\uff0c\u65a9\u5f00\u575a\u51b0\uff0c\u6367\u4e86\u4e00\u628a\u91d1\u73e0\u5728\u624b\uff0c\u54c8\u54c8\u5927\u7b11\u3002\u706b\u5149\u7167\u8000\u4e4b\u4e0b\uff0c\u4ed6\u624b\u4e2d\u91d1\u73e0\u53d1\u51fa\u5947\u5e7b\u593a\u76ee\u7684\u5149\u5f69\u3002\u4f17\u4eba\u4e00\u89c1\uff0c\u80f8\u4e2d\u70ed\u8840\u4e0a\u6d8c\uff0c\u5404\u53d6\u5175\u5203\uff0c\u780d\u51b0\u53d6\u5b9d\u3002\u4f46\u780d\u4e86\u4e00\u9635\uff0c\u5200\u5251\u5377\u53e3\uff0c\u6e10\u6e10\u4e0d\u5229\u4fbf\u4e86\u3002\u539f\u6765\u4f17\u4eba\u81ea\u7528\u7684\u5175\u5203\u90fd\u5df2\u5728\u5cf0\u9876\u88ab\u5de6\u53f3\u53cc\u7ae5\u524a\u65ad\uff0c\u8fd9\u65f6\u643a\u5e26\u7684\u662f\u4ece\u675c\u5bb6\u5e84\u4e0a\u987a\u624b\u53d6\u6765\uff0c\u5e76\u975e\u7cbe\u9009\u7684\u5229\u5668\u3002\u5404\u4eba\u53d6\u5230\u73cd\u5b9d\uff0c\u4e0d\u4f4f\u624b\u7684\u585e\u5165\u8863\u56ca\uff0c\u6108\u53d6\u5f97\u591a\uff0c\u6108\u662f\u5fc3\u70ed\uff0c\u4f46\u5200\u5251\u6e10\u949d\uff0c\u5374\u662f\u8d8a\u780d\u8d8a\u6162\u3002 \u3000\u3000\u7530\u9752\u6587\u9053\uff1a\u201c\u54b1\u4eec\u53bb\u62fe\u4e9b\u67f4\u6765\uff0c\u7194\u51b0\u53d6\u5b9d\uff01\u201d\u4f17\u4eba\u8f70\u7136\u53eb\u597d\u3002\u6b64\u4e8b\u539f\u8be5\u65e9\u5c31\u60f3\u5230\uff0c\u4f46\u4e00\u89c1\u5b9d\u6811\u73cd\u5b9d\u5728\u624b\uff0c\u4eba\u4eba\u8feb\u4e0d\u53ca\u5f85\u7684\u6325\u5200\u633a\u5251\u780d\u51b0\u3002\u53ef\u662f\u4f17\u4eba\u867d\u7136\u9f50\u58f0\u9644\u548c\u7530\u9752\u6587\u7684\u8bf4\u8bdd\uff0c\u5374\u6ca1\u4e00\u4eba\u79fb\u6b65\u53bb\u53d6\u67f4\u3002\u539f\u6765\u4eba\u4eba\u90fd\u6015\u81ea\u5df1\u4e00\u51fa\u53bb\uff0c\u522b\u4eba\u591a\u53d6\u4e86\u73cd\u5b9d\u3002 \u3000\u3000\u5b9d\u6811\u5411\u4f17\u4eba\u6a2a\u76ee\u800c\u987e\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u9f99\u95e8\u5468\u4e16\u5144\u3001\u996e\u9a6c\u5ddd\u9676\u4e16\u5144\u3001\u9556\u5c40\u5b50\u7684\u718a\u9556\u5934\uff0c\u4f60\u4eec\u4e09\u4f4d\u51fa\u53bb\u6361\u67f4\u3002\u6211\u4eec\u5728\u8fd9\u91cc\u7559\u4e0b\u7684\uff0c\u4e00\u9f50\u7f62\u624b\u4f11\u606f\uff0c\u8c01\u4e5f\u4e0d\u8bb8\u79c1\u81ea\u53d6\u5b9d\u3002\u201d\u5468\u9676\u718a\u4e09\u4eba\u867d\u5c06\u4fe1\u5c06\u7591\uff0c\u4f46\u6015\u5b9d\u6811\u7528\u5f3a\uff0c\u53ea\u5f97\u51fa\u6d1e\u53bb\u6361\u62fe\u67af\u679d\u3002 \u3000"}),showOnHomepage:!0},{name:"\u4e5d",content:(0,a.jsx)(a.Fragment,{children:"\u80e1\u6590\u4e0e\u4e4c\u5170\u5c71\u7389\u7b14\u5cf0\u675c\u5e0c\u5b5f\u5e84\u4e3b\u76f8\u7ea6\uff0c\u5b9a\u4e09\u6708\u5341\u4e94\u4e0a\u5cf0\u7b97\u4e00\u7b14\u6614\u65e5\u65e7\u5e10\uff0c\u4f46\u9996\u6b21\u4e0a\u5cf0\uff0c\u675c\u5e84\u4e3b\u5916\u51fa\u672a\u5f52\uff0c\u5374\u4e0e\u82d7\u82e5\u5170\u916c\u7b54\u4e86\u4e00\u756a\u3002\u4ed6\u4e0b\u5f97\u5cf0\u6765\uff0c\u5fc3\u4e2d\u6014\u5fe1\u4e0d\u5b9a\uff0c\u773c\u4e2d\u6240\u89c1\uff0c\u4f3c\u4e4e\u53ea\u662f\u82d7\u82e5\u5170\u7684\u5029\u5f71\uff0c\u8033\u4e2d\u6240\u95fb\uff0c\u5c3d\u662f\u5979\u5f39\u7434\u548c\u6b4c\u4e4b\u58f0\u3002\u4ed6\u4e0e\u5e73\u963f\u56db\u3001\u5de6\u53f3\u53cc\u7ae5\u5728\u5c71\u6d1e\u4e2d\u9971\u9910\u4e00\u987f\u5e72\u7cae\uff0c\u773c\u89c1\u5e73\u963f\u56db\u4f24\u52bf\u867d\u91cd\uff0c\u6027\u547d\u5374\u662f\u65e0\u788d\uff0c\u5fc3\u4e2d\u751a\u6170\u3002\u5f53\u4e0b\u8eba\u5728\u5730\u4e0b\u95ed\u76ee\u517b\u795e\uff0c\u4f46\u53cc\u76ee\u4e00\u95ed\uff0c\u82d7\u82e5\u5170\u79c0\u4e3d\u6e29\u96c5\u7684\u9762\u8c8c\u66f4\u662f\u6e05\u6e05\u695a\u695a\u7684\u5728\u8111\u6d77\u4e2d\u51fa\u73b0\u3002 \u3000\u3000\u80e1\u6590\u7741\u5927\u773c\u775b\uff0c\u671b\u7740\u5c71\u6d1e\u4e2d\u9ed1\u9edd\u9edd\u7684\u77f3\u58c1\uff0c\u82d7\u82e5\u5170\u7684\u6b4c\u58f0\u5374\u53c8\u4f3c\u9690\u9690\u4ece\u77f3\u58c1\u4e2d\u900f\u4e86\u51fa\u6765\u3002\u4ed6\u53f9\u4e86\u4e00\u53e3\u957f\u6c14\uff0c\u5fc3\u60f3\uff1a \u3000\u3000\u201c\u6211\u5c3d\u60f3\u7740\u5979\u5e72\u4e48\uff1f\u5979\u7236\u4eb2\u662f\u6740\u5bb3\u6211\u7236\u7684\u5927\u4ec7\u4eba\uff0c\u867d\u8bf4\u5f53\u65f6\u5979\u7236\u4eb2\u5e76\u975e\u6709\u610f\uff0c\u4f46\u6211\u7236\u603b\u662f\u56e0\u6b64\u800c\u6b7b\u3002\u6211\u4e00\u751f\u5b64\u82e6\u4f36\u4ec3\uff0c\u6ca1\u7239\u6ca1\u5a18\uff0c\u5c3d\u662f\u62dc\u5979\u7236\u4eb2\u4e4b\u8d50\u3002\u6211\u53c8\u60f3\u5979\u5e72\u4e48\uff1f\u201d\u8a00\u5ff5\u53ca\u6b64\uff0c\u6068\u6068\u4e0d\u5df2\uff0c\u4f46\u4e0d\u77e5\u4e0d\u89c9\u53c8\u60f3\uff1a\u201c\u90a3\u65f6\u5979\u5c1a\u672a\u51fa\u4e16\uff0c\u8fd9\u4e0a\u4ee3\u6028\u4ec7\uff0c\u4e0e\u5979\u53c8\u6709\u4ec0\u4e48\u76f8\u5e72\uff1f\u5509\uff01\u5979\u662f\u5343\u91d1\u5c0f\u59d0\uff0c\u6211\u662f\u4e2a\u6d41\u8361\u6c5f\u6e56\u7684\u82e6\u547d\u6c49\u5b50\uff0c\u4f55\u82e6\u6ca1\u6765\u7531\u81ea\u5bfb\u70e6\u607c\uff1f\u201d \u3000\u3000\u8bdd\u867d\u662f\u8fd9\u822c\u8bf4\uff0c\u53ef\u662f\u70e6\u607c\u4e4b\u6765\uff0c\u5c82\u662f\u8f7b\u6613\u6446\u8131\u5f97\u4e86\u7684\uff1f\u5018\u82e5\u60c5\u4e1d\u4e00\u65a9\u4fbf\u65ad\uff0c\u90a3\u4e5f\u7b97\u4e0d\u5f97\u662f\u60c5\u4e1d\u4e86\u3002 \u3000\u3000\u80e1\u6590\u5728\u5c71\u6d1e\u4e2d\u8eba\u4e86\u5c06\u8fd1\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5fc3\u4e2d\u6240\u601d\u6240\u5ff5\uff0c\u4fbf\u662f\u82d7\u82e5\u5170\u4e00\u4eba\u3002\u4ed6\u5076\u5c14\u60f3\u5230\uff1a\u201c\u83ab\u975e\u5bf9\u5934\u751f\u6015\u654c\u6211\u4e0d\u8fc7\uff0c\u5b89\u6392\u4e0b\u4e86\u8fd9\u7f8e\u4eba\u4e4b\u8ba1\uff1f\u201d\u4f46\u7acb\u5373\u89c9\u5f97\u8fd9\u5ff5\u5934\u592a\u4e5f\u4eb5\u6e0e\u4e86\u5979\uff0c\u5fc3\u4e2d\u4fbf\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u5979\u8fd9\u6837\u5929\u4ed9\u4e00\u822c\u7684\u4eba\u7269\uff0c\u5c82\u80fd\u505a\u8fd9\u7b49\u5351\u9119\u4e4b\u4e8b\u3002\u6211\u600e\u80fd\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\uff0c\u5192\u72af\u4e8e\u5979\uff1f\u201d\u773c\u89c1\u5929\u8272\u6e10\u9ed1\uff0c\u518d\u4e5f\u6309\u637a\u4e0d\u4f4f\uff0c\u5bf9\u5e73\u963f\u56db\u9053\uff1a\u201c\u56db\u53d4\uff0c\u6211\u518d\u4e0a\u5cf0\u53bb\u3002\u4f60\u5728\u8fd9\u91cc\u6b47\u6b47\u3002\u201d \u3000\u3000\u4ed6\u5c55\u5f00\u8f7b\u8eab\u529f\u592b\uff0c\u8f6c\u773c\u53c8\u5954\u5230\u5cf0\u4e0b\uff0c\u63f4\u7d22\u800c\u4e0a\u3002\u4e00\u89c1\u675c\u5bb6\u5e84\u5e84\u95e8\uff0c\u5df2\u662f\u6026\u7136\u5fc3\u52a8\u3002\u8fdb\u4e86\u5927\u5385\uff0c\u5374\u89c1\u5e84\u4e2d\u65e0\u4eba\u76f8\u8fce\uff0c\u4e0d\u7981\u5fae\u611f\u8be7\u5f02\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u80e1\u6590\u6c42\u89c1\uff0c\u675c\u5e84\u4e3b\u53ef\u56de\u6765\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8fde\u95ee\u51e0\u904d\uff0c\u59cb\u7ec8\u65e0\u4eba\u56de\u7b54\u3002\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u60f3\uff1a\u201c\u675c\u5e0c\u5b5f\u6789\u79f0\u8fbd\u4e1c\u5927\u8c6a\uff0c\u5374\u8fd9\u822c\u8eb2\u8eb2\u95ea\u95ea\uff0c\u88c5\u795e\u5f04\u9b3c\u3002\u4f60\u7eb5\u5b89\u6392\u4e0b\u5978\u8ba1\uff0c\u80e1\u67d0\u53c8\u6709\u4f55\u60e7\uff1f\u201d \u3000\u3000\u4ed6\u5728\u5927\u5385\u4e0a\u5750\u4e86\u7247\u523b\uff0c\u672c\u60f3\u7559\u4e0b\u51e0\u53e5\u5b57\u53e5\uff0c\u7f9e\u8fb1\u675c\u5e0c\u5b5f\u4e00\u756a\uff0c\u5c31\u6b64\u4e0b\u5cf0\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5bf9\u6b64\u5730\u7adf\u662f\u604b\u604b\u4e0d\u820d\uff0c\u5f53\u4e0b\u8d70\u5411\u4e1c\u53a2\u623f\uff0c\u63a8\u5f00\u623f\u95e8\uff0c\u89c1\u91cc\u9762\u56db\u58c1\u56fe\u4e66\uff0c\u9648\u8bbe\u5f97\u751a\u662f\u7cbe\u96c5\u3002\u4e8e\u662f\u8d70\u5c06\u8fdb\u53bb\uff0c\u987a\u624b\u53d6\u8fc7\u4e00\u672c\u4e66\u6765\uff0c\u5750\u4e0b\u7ffb\u9605\u3002\u53ef\u662f\u7ffb\u6765\u7ffb\u53bb\uff0c\u54ea\u91cc\u770b\u5f97\u8fdb\u4e00\u5b57\u5165\u8111\uff0c\u5fc3\u4e2d\u53ea\u5ff5\u7740\u4e00\u53e5\u8bdd\uff1a\u201c\u5979\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u5979\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u4e0d\u4e45\u5929\u8272\u66f4\u52a0\u9ed1\u4e86\uff0c\u4ed6\u53d6\u51fa\u706b\u6298\uff0c\u6b63\u5f85\u70b9\u71c3\u8721\u70db\uff0c\u5ffd\u542c\u5f97\u5e84\u5916\u4e1c\u8fb9\u96ea\u5730\u91cc\u8f7b\u8f7b\u7684\u51e0\u4e0b\u64e6\u64e6\u4e4b\u58f0\u3002\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u77e5\u6709\u9ad8\u624b\u8e0f\u96ea\u800c\u6765\u3002\u987b\u77e5\u82e5\u5728\u5b9e\u5730\u4e4b\u4e0a\uff0c\u4eba\u4eba\u5f97\u4ee5\u8e51\u8db3\u6084\u884c\uff0c\u4f46\u5728\u79ef\u96ea\u4e2d\u5374\u662f\u534a\u70b9\u5047\u501f\u4e0d\u5f97\uff0c\u529f\u592b\u9ad8\u7684\u843d\u8db3\u8f7b\u7075\uff0c\u529f\u592b\u6d45\u7684\u811a\u6b65\u6ede\u91cd\uff0c\u4e00\u542c\u4fbf\u77e5\u3002\u80e1\u6590\u542c\u4e86\u8fd9\u51e0\u4e0b\u8db3\u6b65\u58f0\uff0c\u5fc3\u60f3\uff1a\u201c\u5012\u8981\u77a7\u77a7\u6765\u7684\u662f\u4f55\u65b9\u9ad8\u4eba\u3002\u201d\u5f53\u4e0b\u5c06\u706b\u6298\u63e3\u56de\u6000\u4e2d\uff0c\u503e\u8033\u7ec6\u542c\u3002 \u3000\u3000\u4f46\u542c\u5f97\u96ea\u5730\u91cc\u53c8\u6709\u51e0\u4eba\u7684\u8db3\u6b65\u58f0\uff0c\u7adf\u7136\u4e2a\u4e2a\u6b66\u529f\u751a\u9ad8\u3002\u80e1\u6590\u4e00\u6570\uff0c\u6765\u7684\u5171\u6709\u4e94\u4eba\uff0c\u53ea\u542c\u5f97\u8fdc\u5904\u9690\u9690\u4f20\u6765\u4e09\u4e0b\u51fb\u638c\uff0c\u5e84\u5916\u6709\u4eba\u56de\u51fb\u4e09\u4e0b\uff0c\u8fc7\u4e0d\u591a\u65f6\uff0c\u5e84\u5916\u53c8\u591a\u4e86\u516d\u4eba\u3002\u80e1\u6590\u867d\u7136\u827a\u9ad8\u4eba\u80c6\u5927\uff0c\u4f46\u542c\u9ad8\u624b\u6bd5\u96c6\uff0c\u8f6c\u773c\u95f4\u7adf\u5230\u4e86\u5341\u4e00\u4eba\u4e4b\u591a\uff0c\u5fc3\u4e0b\u4e5f\u4e0d\u514d\u60ca\u7591\u4e0d\u5b9a\uff0c\u5bfb\u601d\uff1a\u201c\u5148\u79bb\u6b64\u5e84\u8981\u7d27\uff0c\u5bf9\u65b9\u5927\u9080\u5e2e\u624b\uff0c\u6211\u8fd9\u53ef\u662f\u5be1\u4e0d\u654c\u4f17\u3002\u201d\u5f53\u4e0b\u8d70\u51fa\u53a2\u623f\uff0c\u6b63\u5f85\u4e0a\u9ad8\uff0c\u5ffd\u542c\u5c4b\u9876\u5580\u5580\u513f\u54cd\uff0c\u53c8\u6709\u4eba\u5230\u6765\u3002 \u3000\u3000\u80e1\u6590\u6025\u5fd9\u7f29\u56de\uff0c\u5206\u8fa8\u5c4b\u9876\u6765\u4eba\uff0c\u5c45\u7136\u53c8\u662f\u4e03\u540d\u597d\u624b\u3002\u53ea\u542c\u5c4b\u9876\u4e0a\u6709\u4eba\u62cd\u4e86\u4e09\u4e0b\u624b\u638c\uff0c\u5e84\u5916\u8fd8\u4e86\u4e09\u4e0b\uff0c\u5c4b\u9876\u4e03\u4eba\u8f7b\u8f7b\u843d\u5728\u5ead\u4e2d\uff0c\u5f84\u81ea\u8d70\u5411\u53a2\u623f\u3002\u4ed6\u60f3\u654c\u4eba\u4f17\u591a\uff0c\u8fd9\u756a\u53ef\u987b\u5f97\u51fa\u5947\u5236\u80dc\uff0c\u4e8b\u5148\u539f\u6599\u675c\u5e0c\u5b5f\u4f1a\u9080\u8bf7\u5e2e\u624b\u52a9\u62f3\uff0c\u4f46\u60f3\u4e0d\u5230\u7adf\u8bf7\u4e86\u8fd9\u4e48\u591a\u9ad8\u624b\u5230\u6765\u3002\u8033\u542c\u90a3\u4e03\u4eba\u8d70\u5411\u623f\u95e8\uff0c\u5f53\u4e0b\u7f29\u8eab\u5728\u5c4f\u98ce\u4e4b\u540e\uff0c\u8981\u63a2\u660e\u654c\u4eba\u5b89\u6392\u4e0b\u4ec0\u4e48\u673a\u5173\uff0c\u5982\u4f55\u5bf9\u4ed8\u81ea\u5df1\u3002 \u3000\u3000\u4f46\u542c\u5657\u7684\u4e00\u58f0\uff0c\u5df2\u6709\u4eba\u6643\u4eae\u706b\u6298\u3002\u80e1\u6590\u5fc3\u60f3\u5c4f\u98ce\u540e\u85cf\u4e0d\u4f4f\u8eab\uff0c\u6e38\u76ee\u4e00\u77a5\uff0c\u89c1\u5e8a\u4e0a\u7f57\u5e10\u4f4e\u5782\uff0c\u5e8a\u524d\u5374\u65e0\u978b\u5b50\uff0c\u663e\u662f\u65e0\u4eba\u7761\u5367\uff0c\u5f53\u4e0b\u63d0\u4e00\u53e3\u6c14\uff0c\u8f7b\u8f7b\u8d70\u5230\u5e8a\u524d\uff0c\u63ed\u5f00\u7f57\u5e10\uff0c\u5750\u4e0a\u5e8a\u6cbf\uff0c\u94bb\u8fdb\u4e86\u88ab\u91cc\u3002\u8fd9\u51e0\u4e0b\u884c\u52a8\u8f7b\u5de7\u4e4b\u6781\uff0c\u623f\u5916\u4e03\u4eba\u867d\u7136\u90fd\u662f\u9ad8\u624b\uff0c\u7adf\u65e0\u4e00\u4eba\u77e5\u89c9\u3002 \u3000\u3000\u53ef\u662f\u80e1\u6590\u4e00\u8fdb\u68c9\u88ab\uff0c\u5374\u662f\u5927\u5403\u4e00\u60ca\uff0c\u89e6\u624b\u78b0\u5230\u4e00\u4eba\u808c\u80a4\uff0c\u8f7b\u67d4\u8f6f\u6ed1\uff0c\u539f\u6765\u88ab\u4e2d\u7adf\u7761\u7740\u4e00\u4e2a\u5973\u5b50\u3002\u4ed6\u6b63\u8981\u4e00\u6eda\u4e0b\u5e8a\uff0c\u773c\u524d\u706b\u5149\u95ea\u52a8\uff0c\u5df2\u6709\u4eba\u8d70\u8fdb\u623f\u6765\u3002\u4e00\u4eba\u62ff\u7740\u8721\u70db\u5728\u5c4f\u98ce\u540e\u4e00\u63a2\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u5904\u6ca1\u4eba\uff0c\u54b1\u4eec\u5728\u8fd9\u91cc\u8bf4\u8bdd\u3002\u201d\u8bf4\u7740\u4fbf\u5728\u6905\u4e0a\u5750\u4e0b\u3002 \u3000\u3000\u6b64\u65f6\u80e1\u6590\u9f3b\u4e2d\u5145\u6ee1\u5e7d\u9999\uff0c\u6b63\u662f\u9002\u624d\u4e0e\u82d7\u82e5\u5170\u916c\u5531\u65f6\u95fb\u5230\u7684\uff0c\u4e00\u9897\u5fc3\u76f4\u6b32\u8df3\u51fa\u8154\u5b50\u6765\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u5979\u7adf\u662f\u82d7\u59d1\u5a18\uff1f\u6211\u8fd9\u756a\u5510\u7a81\u4f73\u4eba\uff0c\u90a3\u5f53\u771f\u662f\u7f6a\u8be5\u4e07\u6b7b\u3002\u4f46\u6211\u82e5\u5728\u6b64\u523b\u8df3\u5c06\u51fa\u53bb\uff0c\u90a3\u51e0\u4eba\u89c1\u5979\u4e0e\u6211\u540c\u5e8a\u5171\u887e\uff0c\u5fc5\u9053\u6709\u751a\u66a7\u6627\u4e4b\u4e8b\u3002\u82d7\u59d1\u5a18\u4e00\u751f\u6e05\u540d\uff0c\u53ef\u7ed9\u6211\u6bc1\u4e86\u3002\u53ea\u5f97\u5f85\u8fd9\u51e0\u4eba\u8d70\u5f00\uff0c\u518d\u884c\u79bb\u5e8a\u81f4\u6b49\u3002\u201d \u3000\u3000\u4ed6\u8eab\u5b50\u5fae\u4fa7\uff0c\u624b\u80cc\u53c8\u78b0\u5230\u4e86\u90a3\u5973\u5b50\u4e0a\u81c2\u808c\u80a4\uff0c\u53ea\u89c9\u67d4\u817b\u65e0\u6bd4\uff0c\u7adf\u4f3c\u6ca1\u7a7f\u8863\u670d\uff0c\u60ca\u5f97\u6025\u5fd9\u7f29\u624b\u3002\u5176\u5b9e\u7530\u9752\u6587\u9664\u53bb\u82d7\u82e5\u5170\u7684\u5916\u88f3\uff0c\u5c1a\u7559\u4e0b\u8d34\u8eab\u5c0f\u8863\uff0c\u4f46\u80e1\u6590\u53ea\u9053\u5979\u8eab\u5b50\u88f8\u9732\uff0c\u95ed\u4f4f\u4e86\u773c\u65e2\u4e0d\u6562\u770b\uff0c\u624b\u811a\u66f4\u4e0d\u6562\u7a0d\u6709\u52a8\u5f39\uff0c\u5fd9\u5438\u80f8\u6536\u8179\uff0c\u6084\u6084\u5411\u5916\u5e8a\u632a\u79fb\uff0c\u4e0e\u5979\u8eab\u5b50\u76f8\u8ddd\u7565\u8fdc\u3002 \u3000\u3000\u4ed6\u867d\u95ed\u4f4f\u4e86\u773c\uff0c\u4f46\u9f3b\u4e2d\u95fb\u5230\u53c8\u751c\u53c8\u817b\u3001\u8361\u4eba\u5fc3\u9b44\u7684\u9999\u6c14\uff0c\u8033\u4e2d\u542c\u5230\u5bf9\u65b9\u7684\u4e00\u9897\u5fc3\u5728\u6025\u901f\u8df3\u52a8\uff0c\u5fcd\u4e0d\u4f4f\u7741\u5f00\u773c\u6765\uff0c\u53ea\u89c1\u4e00\u4e2a\u5c11\u5973\u5411\u5916\u800c\u5367\uff0c\u8138\u86cb\u513f\u7f9e\u5f97\u4e0e\u6d77\u68e0\u82b1\u4e00\u822c\uff0c\u5374\u4e0d\u662f\u82d7\u82e5\u5170\u662f\u8c01\uff0c\u70db\u5149\u6620\u8fc7\u73e0\u7f57\u7eb1\u5e10\u7167\u5c04\u8fdb\u6765\uff0c\u66f4\u663e\u5f97\u773c\u524d\u6795\u4e0a\uff0c\u8fd9\u5f20\u8138\u86cb\u5a07\u7f8e\u8273\u4e3d\uff0c\u96be\u63cf\u96be\u753b\u3002 \u3000\u3000\u80e1\u6590\u672c\u60f3\u53ea\u77a7\u4e00\u773c\uff0c\u7acb\u5373\u95ed\u773c\uff0c\u4ece\u6b64\u4e0d\u770b\uff0c\u4f46\u53cc\u76ee\u4e00\u5408\uff0c\u767b\u65f6\u610f\u9a6c\u5fc3\u733f\uff0c\u628a\u6301\u4e0d\u4f4f\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u773c\u7741\u4e00\u7ebf\uff0c\u518d\u77a7\u5979\u4e00\u773c\u3002 \u3000\u3000\u82d7\u82e5\u5170\u88ab\u70b9\u4e2d\u4e86\u7a74\u9053\uff0c\u52a8\u5f39\u4e0d\u5f97\uff0c\u5fc3\u4e2d\u5374\u6709\u77e5\u89c9\uff0c\u89c1\u80e1\u6590\u5ffd\u7136\u8fdb\u5e8a\u4e0e\u81ea\u5df1\u5e76\u5934\u800c\u5367\uff0c\u521d\u65f6\u60ca\u60f6\u4e07\u5206\uff0c\u53ea\u6015\u4ed6\u6b32\u56fe\u975e\u793c\uff0c\u5f53\u4e0b\u95ed\u7740\u773c\u775b\uff0c\u53ea\u597d\u542c\u5929\u7531\u547d\u3002\u54ea\u77e5\u4ed6\u8eba\u4e86\u7247\u523b\uff0c\u975e\u4f46\u4e0d\u6328\u8fd1\u8eab\u5b50\uff0c\u53cd\u800c\u5411\u5916\u79fb\u5f00\u3002\u4e0d\u7981\u60e7\u610f\u5c11\u51cf\uff0c\u597d\u5947\u5fc3\u8d77\uff0c\u5fcd\u4e0d\u4f4f\u5fae\u5fae\u7741\u773c\uff0c\u6b63\u597d\u80e1\u6590\u4e5f\u6b63\u7741\u773c\u671b\u5979\u3002\u56db\u76ee\u76f8\u4ea4\uff0c\u76f8\u8ddd\u4e0d\u5230\u534a\u5c3a\uff0c\u4e24\u4eba\u90fd\u662f\u5927\u7f9e\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5c4f\u98ce\u5916\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u8d5b\u603b\u7ba1\uff0c\u4f60\u5f53\u771f\u662f\u795e\u673a\u5999\u7b97\uff0c\u4eba\u6240\u96be\u6d4b\u3002\u90a3\u4eba\u5c31\u7b97\u4e0d\u6298\u4e0d\u6263\uff0c\u5f53\u771f\u662f\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u7684\u82f1\u96c4\u8c6a\u6770\uff0c\u843d\u5165\u4e86\u4f60\u8fd9\u7f57\u7f51\uff0c\u4e5f\u8981\u6559\u4ed6\u63d2\u7fc5\u96be\u98de\u3002\u201d \u3000\u3000\u62ff\u7740\u8721\u70db\u7684\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u653e\u4e0b\u70db\u53f0\uff0c\u8d70\u5230\u5c4f\u98ce\u4e4b\u5916\uff0c\u9053\uff1a\u201c\u5f20\u8d24\u5f1f\uff0c\u4f60\u4e5f\u522b\u5c3d\u5f80\u6211\u8138\u4e0a\u8d34\u91d1\u3002\u4e8b\u6210\u4e4b\u540e\uff0c\u6211\u603b\u5fd8\u4e0d\u4e86\u5927\u5bb6\u7684\u597d\u5904\u3002\u201d \u3000\u3000\u80e1\u6590\u4e0e\u82d7\u82e5\u5170\u542c\u4e86\u4e24\u4eba\u4e4b\u8a00\uff0c\u90fd\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u4e9b\u4eba\u660e\u662f\u5b89\u6392\u673a\u5173\uff0c\u8981\u52a0\u5bb3\u91d1\u9762\u4f5b\u82d7\u4eba\u51e4\u3002\u82d7\u82e5\u5170\u4e0d\u77e5\u6c5f\u6e56\u4e4b\u4e8b\uff0c\u8fd8\u4e0d\u600e\u6837\uff0c\u5fc3\u60f3\u7239\u7239\u6b66\u529f\u65e0\u654c\uff0c\u4e5f\u4e0d\u6015\u65c1\u4eba\u52a0\u5bb3\u3002\u80e1\u6590\u5374\u77e5\u8d5b\u603b\u7ba1\u662f\u6ee1\u6d32\u7b2c\u4e00\u9ad8\u624b\uff0c\u5185\u529f\u5916\u529f\u4ff1\u81fb\u5316\u5883\uff0c\u4e3a\u4eba\u51f6\u5978\u72e1\u8bc8\uff0c\u4e0d\u77e5\u5bb3\u6b7b\u8fc7\u591a\u5c11\u5fe0\u81e3\u4e49\u58eb\u3002\u4ed6\u662f\u5f53\u4eca\u4e7e\u9686\u7687\u5e1d\u624b\u4e0b\u7b2c\u4e00\u4eb2\u4fe1\u536b\u58eb\uff0c\u4eca\u65e5\u5c45\u7136\u4eb2\u81ea\u7387\u4eba\u4ece\u5317\u4eac\u8d76\u5230\u8fd9\u7389\u7b14\u5cf0\u4e0a\u3002\u542c\u90a3\u59d3\u5f20\u7684\u8a00\u8bed\uff0c\u4ed6\u4eec\u6697\u4e2d\u5b89\u6392\u4e0b\u5de7\u8ba1\uff0c\u82d7\u4eba\u51e4\u7eb5\u7136\u5389\u5bb3\uff0c\u53ea\u6015\u4e5f\u96be\u9003\u6bd2\u624b\u3002\u8033\u542c\u5f97\u8d5b\u603b\u7ba1\u8d70\u5230\u5c4f\u98ce\u4e4b\u5916\uff0c\u5fc3\u60f3\u673a\u4e0d\u53ef\u5931\uff0c\u8f7b\u8f7b\u63ed\u8d77\u7f57\u5e10\uff0c\u53f3\u638c\u5bf9\u51c6\u70db\u706b\u4e00\u6325\uff0c\u4e00\u9635\u52b2\u98ce\u6251\u5c06\u8fc7\u53bb\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u70db\u706b\u767b\u65f6\u7184\u4e86\u3002 \u3000\u3000\u53ea\u542c\u4e00\u4eba\u8bf4\u9053\uff1a\u201c\u554a\uff0c\u70db\u706b\u706d\u5566\uff01\u201d\u5c31\u5728\u6b64\u65f6\uff0c\u53c8\u6709\u4eba\u9646\u7eed\u8d70\u8fdb\u53a2\u623f\uff0c\u56b7\u9053\uff1a\u201c\u5feb\u70b9\u706b\uff0c\u638c\u706f\u5427\uff01\u201d\u8d5b\u603b\u7ba1\u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u662f\u5728\u6697\u4e2d\u8bf4\u8bdd\u7684\u597d\u3002\u90a3\u82d7\u4eba\u51e4\u673a\u7075\u5f97\u7d27\uff0c\u82e5\u5728\u5c4b\u5916\u89c1\u5230\u706b\u5149\uff0c\u8bf4\u4e0d\u5b9a\u541e\u4e86\u9975\u7684\u9c7c\u513f\uff0c\u53c8\u7ed9\u4ed6\u8131\u94a9\u9003\u8d70\u3002\u201d\u597d\u51e0\u4eba\u7eb7\u7eb7\u9644\u548c\uff0c\u8bf4\u9053\uff1a\u201c\u8d5b\u603b\u7ba1\u6df1\u8c0b\u8fdc\u8651\uff0c\u89c1\u4e8b\u5468\u8be6\uff0c\u679c\u7136\u4e0d\u540c\u3002\u201d \u3000\u3000\u4f46\u542c\u6709\u4eba\u8f7b\u8f7b\u63a8\u5f00\u5c4f\u98ce\uff0c\u6b64\u65f6\u53a2\u623f\u4e2d\u56db\u4e0b\u91cc\u90fd\u5750\u6ee1\u4e86\u4eba\uff0c\u6709\u7684\u5750\u5728\u5730\u4e0b\uff0c\u6709\u7684\u5750\u5728\u684c\u4e0a\uff0c\u66f4\u6709\u4e09\u4eba\u5728\u5e8a\u6cbf\u5750\u4e0b\u3002 \u3000\u3000\u80e1\u6590\u751f\u6015\u90a3\u4e09\u4eba\u5750\u5f97\u5026\u4e86\uff0c\u5411\u540e\u4e00\u4ef0\uff0c\u8eba\u5c06\u4e0b\u6765\uff0c\u4e8b\u60c5\u53ef\u5c31\u95f9\u7a7f\uff0c\u53ea\u5f97\u8f7b\u8f7b\u5411\u91cc\u5e8a\u7565\u79fb\u3002\u8fd9\u4e00\u6765\uff0c\u4e0e\u82d7\u82e5\u5170\u5374\u66f4\u52a0\u8fd1\u4e86\uff0c\u53ea\u89c9\u5979\u5439\u6c14\u5982\u5170\uff0c\u8361\u4eba\u5fc3\u9b44\u3002\u4ed6\u65e2\u6015\u4e0e\u5e8a\u6cbf\u4e0a\u7684\u4e09\u4eba\u76f8\u78b0\uff0c\u6bc1\u4e86\u82d7\u82e5\u5170\u7684\u540d\u8282\uff0c\u53c8\u6015\u81ea\u5df1\u80e1\u5b50\u5982\u621f\uff0c\u523a\u5230\u5979\u5439\u5f39\u5f97\u7834\u7684\u8138\u988a\uff0c\u5f53\u4e0b\u5fc3\u4e2d\u6253\u5b9a\u4e86\u4e3b\u610f\uff0c\u82e5\u662f\u7ed9\u4eba\u53d1\u89c9\uff0c\u5fc5\u5f53\u5c06\u623f\u4e2d\u8fd9\u4e00\u5341\u516b\u4eba\u6740\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u5b81\u6559\u81ea\u5df1\u6027\u547d\u4e0d\u5728\uff0c\u4e5f\u4e0d\u80fd\u7559\u4e0b\u4e00\u5f20\u6d3b\u53e3\uff0c\u7d2f\u4e86\u8fd9\u4f4d\u51b0\u6e05\u7389\u6d01\u7684\u59d1\u5a18\u3002 \u3000\u3000\u5e78\u559c\u90a3\u4e09\u4eba\u90fd\u597d\u7aef\u7aef\u7684\u5750\u7740\uff0c\u4e0d\u518d\u52a8\u5f39\u3002\u80e1\u6590\u4e0d\u77e5\u82d7\u82e5\u5170\u88ab\u70b9\u4e2d\u4e86\u7a74\u9053\uff0c\u4f46\u89c9\u5979\u7adf\u4e0d\u5411\u91cc\u5e8a\u95ea\u907f\uff0c\u4e0d\u7531\u5f97\u53c8\u662f\u60f6\u6050\uff0c\u53c8\u662f\u6b22\u559c\uff0c\u4e00\u4e2a\u4eba\u5c31\u4f3c\u5728\u534a\u7a7a\u4e2d\u817e\u4e91\u9a7e\u96fe\u4e00\u822c\u3002 \u3000\u3000\u53ea\u542c\u8d5b\u603b\u7ba1\u9053\uff1a\u201c\u5404\u4f4d\uff0c\u54b1\u4eec\u8bf7\u675c\u5e84\u4e3b\u7ed9\u5927\u4f19\u513f\u5f15\u89c1\u5f15\u89c1\u3002\u201d\u53ea\u542c\u5f97\u4e00\u4e2a\u55d3\u97f3\u4f4e\u6c89\u7684\u4eba\u8bf4\u9053\uff1a\u201c\u627f\u8499\u5404\u4f4d\u5149\u964d\uff0c\u5144\u5f1f\u81f3\u611f\u8363\u5e78\u3002\u8fd9\u4f4d\u662f\u5fa1\u524d\u4f8d\u536b\u603b\u7ba1\u8d5b\u603b\u7ba1\u8d5b\u5927\u4eba\u3002\u8d5b\u5927\u4eba\u5a01\u9707\u6c5f\u6e56\uff0c\u5404\u4f4d\u5f53\u7136\u90fd\u4e45\u4ef0\u7684\u4e86\u3002\u201d\u8bf4\u8bdd\u4e4b\u4eba\u81ea\u662f\u7389\u7b14\u5e84\u5e84\u4e3b\u675c\u5e0c\u5b5f\u3002\u4f17\u4eba\u8f70\u8a00\u8bf4\u4e86\u4e9b\u4ef0\u6155\u4e4b\u8a00\u3002 \u3000\u3000\u80e1\u6590\u503e\u542c\u675c\u5e0c\u5b5f\u7ed9\u5404\u4eba\u62a5\u540d\u5f15\u89c1\uff0c\u8d8a\u542c\u8d8a\u662f\u60ca\u8bb6\u3002\u539f\u6765\u9664\u4e86\u8d5b\u603b\u7ba1\u7b49\u4e03\u4eba\u662f\u5fa1\u524d\u4f8d\u536b\u4e4b\u5916\uff0c\u5176\u4f59\u4e2a\u4e2a\u662f\u6c5f\u6e56\u4e0a\u6210\u540d\u7684\u4e00\u6d41\u9ad8\u624b\u3002\u9752\u85cf\u6d3e\u7684\u7384\u51a5\u5b50\u5230\u4e86\uff0c\u6606\u4ed1\u5c71\u7075\u6e05\u5c45\u58eb\u5230\u4e86\uff0c\u6cb3\u5357\u65e0\u6781\u95e8\u7684\u848b\u8001\u62f3\u5e08\u4e5f\u5230\u4e86\u3002\u6b64\u5916\u4e0d\u662f\u90a3\u4e00\u6d3e\u7684\u638c\u95e8\u3001\u540d\u5bbf\uff0c\u5c31\u662f\u4ec0\u4e48\u5e2e\u4f1a\u7684\u603b\u8235\u4e3b\u3001\u4ec0\u4e48\u9556\u5c40\u7684\u603b\u9556\u5934\uff0c\u6ca1\u4e00\u4e2a\u4e0d\u662f\u5927\u6709\u6765\u5934\u4e4b\u4eba\uff1b\u800c\u90a3\u4e03\u540d\u4f8d\u536b\uff0c\u4e5f\u5168\u662f\u6b66\u6797\u4e2d\u65e9\u4eab\u76db\u540d\u7684\u786c\u624b\u3002 \u3000\u3000\u82d7\u82e5\u5170\u5fc3\u4e2d\u601d\u6f6e\u8d77\u4f0f\uff0c\u6697\u60f3\uff1a\u201c\u6211\u53ea\u7a7f\u4e86\u8fd9\u4e00\u70b9\u70b9\u8863\u670d\uff0c\u5374\u7761\u5728\u4ed6\u7684\u6000\u4e2d\u3002\u6b64\u4eba\u4e0e\u6211\u5bb6\u6069\u6028\u7ea0\u845b\uff0c\u4e0d\u77e5\u4ed6\u8981\u62ff\u6211\u600e\u6837\uff1f \u3000\u3000\u4eca\u65e5\u521d\u6b21\u4e0e\u4ed6\u76f8\u4f1a\uff0c\u53ea\u89c9\u4ed6\u76f8\u8c8c\u867d\u7136\u7c97\u9c81\uff0c\u5374\u662f\u4e2a\u6587\u6b66\u53cc\u5168\u7684\u5947\u7537\u5b50\uff0c\u54ea\u77e5\u4ed6\u7adf\u6562\u5bf9\u6211\u8fd9\u822c\u65e0\u793c\u3002\u201d\u867d\u89c9\u80e1\u6590\u8fd9\u6837\u5bf9\u5f85\u81ea\u5df1\uff0c\u5b9e\u662f\u5927\u5927\u4e0d\u8be5\uff0c\u4f46\u4e0d\u77e5\u600e\u7684\uff0c\u5fc3\u4e2d\u6b8a\u65e0\u607c\u6012\u6028\u602a\u4e4b\u610f\uff0c\u53cd\u800c\u4e0d\u7531\u81ea\u4e3b\u7684\u5fae\u5fae\u6709\u4e9b\u6b22\u559c\uff0c\u5916\u9762\u5341\u4f59\u4eba\u5927\u58f0\u8c08\u8bba\uff0c\u5979\u7adf\u4e00\u53e5\u4e5f\u6ca1\u542c\u5728\u8033\u91cc\u3002 \u3000\u3000\u80e1\u6590\u6bd4\u5979\u5927\u4e86\u5341\u5c81\uff0c\u9605\u5386\u53c8\u591a\uff0c\u77e5\u9053\u773c\u524d\u4e4b\u4e8b\u5e72\u7cfb\u4e0d\u5c0f\uff0c\u662f\u4ee5\u867d\u7136\u53c8\u60ca\u53c8\u559c\uff0c\u516d\u795e\u65e0\u4e3b\uff0c\u4f46\u4e8e\u5e10\u5916\u5404\u4eba\u7684\u8bf4\u8bdd\uff0c\u5374\u53e5\u53e5\u542c\u5f97\u5341\u5206\u4ed4\u7ec6\u3002\u4ed6\u542c\u675c\u5e0c\u5b5f\u4e00\u4e2a\u4e2a\u7684\u5f15\u89c1\uff0c\u5c48\u6307\u6570\u7740\uff0c\u6570\u5230\u7b2c\u5341\u516d\u4e2a\u65f6\uff0c\u675c\u5e0c\u5b5f\u4fbf\u4f4f\u53e3\u4e0d\u518d\u8bf4\u4e86\u3002\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u5e10\u5916\u5171\u6709\u4e00\u5341\u516b\u4eba\uff0c\u9664\u675c\u5e0c\u5b5f\u5916\uff0c\u8be5\u6709\u5341\u4e03\u4eba\uff0c\u8fd9\u4f59\u4e0b\u4e00\u4e2a\u4e0d\u77e5\u662f\u8c01\uff1f\u201d\u4ed6\u5fc3\u4e2d\u8d77\u4e86\u8fd9\u7591\u7aa6\uff0c\u5e10\u5916\u4e5f\u6709\u51e0\u4e2a\u7ec6\u5fc3\u4e4b\u4eba\u7559\u610f\u5230\u4e86\u3002 \u3000\u3000\u6709\u4eba\u95ee\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4f4d\u662f\u8c01\uff1f\u201d\u675c\u5e0c\u5b5f\u5374\u4e0d\u7b54\u8bdd\u3002 \u3000\u3000\u9694\u4e86\u534a\u664c\uff0c\u8d5b\u603b\u7ba1\u9053\uff1a\u201c\u597d\uff01\u6211\u8ddf\u5404\u4f4d\u8bf4\uff0c\u8fd9\u4f4d\u662f\u5174\u6c49\u4e10\u5e2e\u7684\u8303\u5e2e\u4e3b\u3002\u201d \u3000\u3000\u4f17\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u5185\u4e2d\u6709\u4e00\u4e8c\u4eba\u8baf\u606f\u7075\u901a\u7684\uff0c\u5f97\u77e5\u8303\u5e2e\u4e3b\u5df2\u7ed9\u5b98\u5bb6\u6349\u4e86\u53bb\u3002\u4f59\u4eba\u5374\u77e5\u4e10\u5e2e\u7d20\u6765\u4e0e\u5b98\u5e9c\u4f5c\u5bf9\uff0c\u51b3\u4e0d\u80fd\u8ddf\u5fa1\u524d\u4f8d\u536b\u8054\u624b\uff0c\u4ed6\u7a81\u5728\u5cf0\u4e0a\u51fa\u73b0\uff0c\u4eba\u4eba\u90fd\u89c9\u5947\u602a\u3002 \u3000\u3000\u8d5b\u603b\u7ba1\u9053\uff1a\u201c\u4e8b\u60c5\u662f\u8fd9\u6837\u3002\u5404\u4f4d\u5e94\u675c\u5e84\u4e3b\u4e4b\u9080\uff0c\u4e0a\u5cf0\u6765\u52a9\u62f3\uff0c\u4e3a\u7684\u662f\u5bf9\u4ed8\u3002\u53ef\u662f\u5728\u62ff\u72d0\u72f8\u4e4b\u524d\uff0c\u54b1\u4eec\u5148\u5f97\u62ac\u4e00\u5c0a\u83e9\u8428\u4e0b\u5c71\u3002\u201d\u6709\u4eba\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u9762\u4f5b\uff1f\u201d\u8d5b\u603b\u7ba1\u9053\uff1a\u201c\u4e0d\u9519\u3002\u6211\u4eec\u60ca\u52a8\u8303\u5e2e\u4e3b\uff0c\u672c\u6765\u4e3a\u7684\u662f\u8981\u5f15\u82d7\u4eba\u51e4\u4e0a\u5317\u4eac\u76f8\u6551\u3002 \u3000\u3000\u5929\u7262\u4e2d\u5b89\u6392\u4e0b\u4e86\u6a0a\u7b3c\uff0c\u7b49\u5019\u4ed6\u7684\u5927\u9a7e\u3002\u54ea\u77e5\u4ed6\u5012\u4e5f\u4e56\u89c9\uff0c\u7adf\u6ca1\u4e0a\u94a9\u3002\u201d\u4f8d\u536b\u4e2d\u6709\u4eba\u5589\u5934\u5495\u565c\u4e86\u4e00\u58f0\uff0c\u5374\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u539f\u6765\u8d5b\u603b\u7ba1\u8fd9\u756a\u8bdd\u4e2d\u9690\u7792\u4e86\u4e00\u4ef6\u4e8b\u3002\u82d7\u4eba\u51e4\u4f55\u5c1d\u6ca1\u53bb\u5317\u4eac\uff1f\u4ed6\u5355\u8eab\u95ef\u5929\u7262\uff0c\u642d\u6551\u8303\u5e2e\u4e3b\uff0c\u4eba\u867d\u6ca1\u6551\u51fa\uff0c\u4f46\u4e00\u67c4\u957f\u5251\u6740\u4e86\u5341\u4e00\u540d\u5927\u5185\u4f8d\u536b\uff0c\u8fde\u8d5b\u603b\u7ba1\u81c2\u4e0a\u4e5f\u4e2d\u4e86\u5251\u4f24\u3002\u8d5b\u603b\u7ba1\u5e03\u7f6e\u867d\u6781\u5468\u5bc6\uff0c\u7ec8\u56e0\u5bf9\u65b9\u6b66\u529f\u592a\u9ad8\uff0c\u7adf\u7136\u64d2\u62ff\u4e0d\u7740\u3002\u8fd9\u4ef6\u4e8b\u662f\u4ed6\u751f\u5e73\u7684\u5947\u803b\u5927\u8fb1\uff0c\u5728\u65c1\u4eba\u4e4b\u524d\u81ea\u7136\u7edd\u53e3\u4e0d\u63d0\u3002 \u3000\u3000\u8d5b\u603b\u7ba1\u9053\uff1a\u201c\u675c\u5e84\u4e3b\u4e0e\u8303\u5e2e\u4e3b\u4e24\u4f4d\uff0c\u5bf9\u5f85\u670b\u53cb\u4e49\u6c14\u6df1\u91cd\uff0c\u7b54\u5141\u52a9\u6211\u4eec\u4e00\u81c2\u4e4b\u529b\uff0c\u5728\u4e0b\u5b9e\u662f\u611f\u6fc0\u4e0d\u5c3d\uff0c\u4e8b\u6210\u4e4b\u540e\uff0c\u5728\u4e0b\u594f\u660e\u7687\u4e0a\uff0c\u81ea\u6709\u5927\u5927\u7684\u5c01\u8d4f\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u542c\u5e84\u5916\u8fdc\u5904\u9690\u9690\u4f20\u6765\u51e0\u4e0b\u811a\u6b65\u4e4b\u58f0\u3002\u4ed6\u8033\u97f3\u6781\u597d\uff0c\u811a\u6b65\u867d\u7136\u53c8\u8f7b\u53c8\u8fdc\uff0c\u53ef\u4e5f\u542c\u5f97\u6e05\u695a\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u91d1\u9762\u4f5b\u6765\u5566\uff0c\u6211\u4eec\u5bab\u91cc\u5f53\u5dee\u7684\u57cb\u4f0f\u5728\u8fd9\u91cc\uff0c\u5404\u4f4d\u51fa\u53bb\u8fce\u63a5\u3002\u201d\u675c\u5e0c\u5b5f\u3002\u8303\u5e2e\u4e3b\u3001\u7384\u51a5\u5b50\u3001\u7075\u6e05\u5c45\u58eb\u3001\u848b\u8001\u62f3\u5e08\u7b49\u90fd\u7ad9\u8d77\u6765\uff0c\u8d70\u51fa\u53a2\u623f\uff0c\u53ea\u5269\u4e0b\u4e03\u540d\u5927\u5185\u4f8d\u536b\u3002 \u3000\u3000\u8fd9\u65f6\u811a\u6b65\u58f0\u500f\u745a\u95f4\u5df2\u5230\u5e84\u5916\uff0c\u8c01\u90fd\u60f3\u4e0d\u5230\u4ed6\u7adf\u4f1a\u6765\u5f97\u8fd9\u6837\u5feb\uff0c\u72b9\u5982\u8239\u53ea\u5728\u5927\u6d77\u4e2d\u9047\u4e0a\u66b4\u98ce\uff0c\u752b\u89c1\u5f81\u5146\uff0c\u72c2\u98ce\u5927\u4e0e\u5df2\u6253\u4e0a\u5e06\u6765\uff1b\u53c8\u5982\u8fc5\u96f7\u4e0d\u6025\u63a9\u8033\uff0c\u95ea\u7535\u521a\u8fc7\uff0c\u9739\u96f3\u5df2\u81f3\u3002 \u3000\u3000\u8d5b\u603b\u7ba1\u4e0e\u516d\u540d\u536b\u58eb\u90fd\u662f\u4e00\u60ca\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u4e00\u9f50\u62bd\u51fa\u5175\u5203\u3002 \u3000\u3000\u8d5b\u603b\u7ba1\u9053\uff1a\u201c\u4f0f\u4e0b\u3002\u201d\u5c31\u6709\u4eba\u624b\u6380\u7f57\u5e10\uff0c\u60f3\u8eb2\u5165\u95ef\u4e2d\u3002\u8d5b\u603b\u7ba1\u65a5\u9053\uff1a\u201c\u8822\u624d\uff0c\u5728\u5e8a\u4e0a\u8fd8\u4e0d\u7ed9\u4eba\u80a2\u9053\uff1f\u201d\u90a3\u4eba\u7f29\u56de\u4e86\u624b\u3002\u4e03\u4e2a\u4eba\u6216\u8eb2\u5165\u5e8a\u5e95\uff0c\u6216\u85cf\u5728\u67dc\u4e2d\uff0c\u6216\u9690\u8eab\u4e66\u67b6\u4e4b\u540e\u3002 \u3000\u3000\u80e1\u6590\u5fc3\u4e2d\u6697\u7b11\uff1a\u201c\u4f60\u9a82\u4eba\u662f\u8822\u624d\uff0c\u81ea\u5df1\u624d\u662f\u8822\u624d\u3002\u201d\u4f46\u89c9\u82d7\u82e5\u5170\u9f3b\u4e2d\u547c\u5438\uff0c\u8f7b\u8f7b\u7684\u55b7\u5728\u81ea\u5df1\u8138\u4e0a\uff0c\u518d\u4e5f\u628a\u6301\u4e0d\u5b9a\uff0c\u8f7b\u8f7b\u4f38\u5634\u8fc7\u53bb\uff0c\u5728\u5979\u8138\u988a\u4e0a\u543b\u4e86\u4e00\u4e0b\u3002\u82d7\u5f31\u5170\u53c8\u559c\u53c8\u7f9e\uff0c\u5f85\u8981\u95ea\u63e9\uff0c\u82e6\u4e8e\u52a8\u5f39\u4e0d\u5f97\u3002\u8774\u6590\u4e00\u543b\u4e4b\u540e\uff0c\u5ffd\u7136\u4e0d\u7531\u81ea\u4e3b\u7684\u81ea\u60ed\u5f62\u79fd\uff0c\u5fc3\u60f3\uff1a\u201c\u5979\u6d59\u4e48\u6e29\u67d4\u6587\u96c5\uff0d\u6211\u600e\u4e48\u80fd\u8fb1\u4e8e\u5979\uff1f\u201d\u5f85\u8981\u632a\u8eab\u5411\u5916\uff0c\u4e0d\u4e0e\u5979\u5982\u6b64\u9760\u8fd1\uff0c\u5ffd\u542c\u5e8a\u5e95\u4e0b\u4e24\u540d\u536b\u58eb\u52a8\u4e86\u810a\u4e0b\uff0c\u4f4e\u58f0\u5492\u9a82\u3002\u539f\u6765\u51e0\u4e2a\u4eba\u6324\u5728\u5e8a\u5e95\uff0c\u4e00\u4eba\u624b\u8098\u78b0\u75db\u4e86\u53e6\u4e00\u4eba\u7684\u9f3b\u5b50\u3002 \u3000\u3000\u80e1\u6590\u5bf9\u654c\u4eba\u8c61\u6765\u6ed1\u7a3d\uff0c\u4ee5\u4ed6\u5f80\u65e5\u813e\u6c14\uff0c\u6b64\u65f6\u60d1\u8000\u63ed\u5f00\u8925\u5b50\uff0c\u5f80\u5e8a\u5e95\u4e0b\u6492\u4e00\u5927\u6ce1\u5c3f\uff0c\u5c06\u4f17\u536b\u58eb\u6dcb\u4e00\u4e2a\u918d\u9190\u704c\u9876\uff0c\u4f46\u5fc3\u4e2d\u521a\u6709\u6b64\u5ff5\uff0c\u7acb\u5373\u60f3\u5230\u82d7\u82e5\u5170\u7761\u5728\u8eab\u65c1\uff0c\u5c82\u80fd\u80e1\u6765\uff1f \u3000\u3000\u8fc7\u4e0d\u591a\u65f6\uff0c\u675c\u5e0c\u5b5f\u4e0e\u848b\u8001\u62f3\u5e08\u7b49\u9ad8\u58f0\u8bf4\u7b11\uff0c\u966a\u7740\u4e00\u4eba\u8d70\u8fdb\u53a2\u623f\uff0c\u90a3\u4eba\u6b63\u662f\u82d7\u4eba\u51e4\u3002\u6709\u4eba\u62ff\u4e86\u70db\u53f0\uff0c\u8d70\u5728\u524d\u9762\u3002 \u3000\u3000\u675c\u5e0c\u5b5f\u5fc3\u4e2d\u7eb3\u95f7\uff0c\u4e0d\u77e5\u81ea\u5df1\u5bb6\u4eba\u4e0e\u5a62\u4ec6\u5230\u4e86\u4f55\u5904\uff0c\u600e\u4e48\u4e00\u5404\u4eba\u5f71\u4e5f\u4e0d\u89c1\u3002\u4f46\u8d5b\u603b\u7ba1\u4e00\u5230\uff0c\u82d7\u4eba\u51e4\u8015\u7740\u4e0a\u5cf0\uff0c\u5b9e\u65e0\u4f59\u88d5\u518d\u53bb\u67e5\u5bdf\u5bb6\u4e8b\uff0c\u659c\u773c\u671b\u82d7\u4eba\u51e4\u65f6\uff0c\u89c1\u4ed6\u8138\u8272\u6728\u7136\uff0d\u4e0d\u77e5\u4ed6\u5fc3\u4e2d\u6240\u60f3\u4f55\u4e8b\u3002 \u3000\u3000\u4f17\u4eba\u5728\u53a2\u623f\u4e2d\u5750\u5b9a\u3002\u675c\u5e0c\u5b5f\u9053\uff1a\u201c\u82d7\u5144\uff0c\u5144\u5f1f\u4e0e\u90a3\u96ea\u5c71\u98de\u72d0\u76f8\u7ea6\uff0c\u4eca\u65e5\u5728\u6b64\u95f4\u7b97\u4e00\u7b14\u65e7\u5e10\u3002\u82d7\u5144\u4e0e\u8fd9\u91cc\u51e0\u4f4d\u597d\u670b\u53cb\u9ad8\u4e49\uff0c\u8fdc\u9053\u524d\u6765\u52a9\u62f3\uff0c\u5144\u5f1f\u5b9e\u5728\u611f\u6fc0\u4e0d\u5c3d\u3002\u53ea\u662f\u73b0\u4e0b\u5929\u8272\u5df2\u9ed1\uff0c\u90a3\u4ecd\u672a\u5230\u6765\uff0c\u5b9a\u662f\u5f97\u6089\u5404\u4f4d\u82f1\u540d\uff0c\u5413\u5f97\u5939\u4f4f\u7cca\u72f8\u5c3e\u5df4\uff0c\u8fdc\u8fdc\u9003\u53bb\u4e86\u3002\u201d\u80e1\u6590\u5927\u6012\uff0c\u771f\u60f3\u4e00\u8dc3\u800c\u51fa\uff0c\u5288\u8138\u6839\u4ed6\u4e00\u638c\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u54fc\u4e86\u4e00\u58f0\uff0c\u5411\u8303\u5e2e\u4e3b\u9053\uff1a\u201c\u540e\u6765\u8303\u5144\u7ec8\u4e8e\u8131\u9669\u4e86\uff1f\u201d \u3000\u3000\u8303\u5e2e\u4e3b\u7ad9\u8d77\u6765\u6df1\u6df1\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u82d7\u7237\u4e0d\u987e\u5371\u96be\uff0c\u4eb2\u5165\u9669\u5730\u76f8\u6551\uff0c\u6b64\u6069\u6b64\u5fb7\uff0c\u5144\u5f1f\u7ec8\u8eab\u4e0d\u6562\u76f8\u5fd8\u3002\u82d7\u7237\u5927\u95f9\u5317\u4eac\uff0c\u4e0d\u4e45\u655d\u5e2e\u5144\u5f1f\u53c8\u5927\u4e3e\u6765\u6551\uff0c\u5e78\u597d\u4eba\u591a\u52bf\u4f17\uff0c\u5144\u5f1f\u4ed7\u7740\u82d7\u7237\u7684\u5a01\u98ce\uff0c\u624d\u5f97\u4fa5\u5e78\u8131\u96be\u3002\u201d \u3000\u3000\u8303\u5e2e\u4e3b\u8fd9\u756a\u8bdd\u81ea\u662f\u5168\u5c5e\u865a\u8a00\u3002\u82d7\u4eba\u51e4\u4eb2\u5165\u5929\u7262\uff0c\u867d\u6ca1\u6f4d\u8d5b\u603b\u7ba1\u6240\u64d2\uff0c\u4f46\u5927\u95f9\u4e00\u573a\u4e4b\u540e\uff0c\u4e5f\u672a\u80fd\u5c06\u8303\u5e2e\u4e3b\u6551\u51fa\u3002\u5eff\u5e2e\u95ef\u5929\u7262\u4e91\u4e91\uff0c\u5168\u65e0\u5176\u4e8b\u3002\u8d5b\u603b\u7ba1\u4e00\u8ba1\u4e0d\u6210\uff0c\u4e8c\u8ba1\u53c8\u751f\uff0c\u4eb2\u5165\u5929\u7262\u4e0e\u8303\u5e2e\u4e3b\u4e00\u573a\u8c08\u8bba\uff0d\u4ee5\u6b7b\u76f8\u80c1\u3002\u8303\u5e2e\u4e3b\u6f4d\u4eba\u9aa8\u5934\u5012\u786c\uff0c\u4efb\u51ed\u8d5b\u603b\u7ba1\u5982\u4f55\u5a01\u5413\u5229\u8bf1\uff0c\u7adf\u662f\u534a\u70b9\u4e0d\u5c48\u3002\u8d5b\u603b\u7ba1\u8001\u5978\u5de8\u733e\uff0c\u5584\u77e5\u522b\u4eba\u5fc3\u610f\uff0c\u8ddf\u8303\u5e2e\u4e3b\u8fde\u8c08\u6570\u65e5\u4e4b\u540e\uff0c\u77e5\u9053\u5bf9\u4ed8\u8fd9\u7c7b\u786c\u6c49\uff0c\u65e2\u4e0d\u80fd\u52a8\u4e4b\u4ee5\u5229\u7984\uff0d\u4ea6\u4e0d\u80fd\u5a01\u4e4b\u4ee5\u65a7\u94d6\uff0c\u4f46\u82e5\u7ed9\u4ed6\u4e00\u9876\u9ad8\u5e3d\u5b50\u6234\u6234\uff0c\u5012\u662f\u9887\u53ef\u6536\u6548\u3002\u5f53\u4e0b\u4eb2\u81ea\u8fce\u63a5\u4ed6\u8fdb\u603b\u7ba1\u5e9c\u5c45\u4f4f\uff0c\u547d\u624b\u4e0b\u6700\u4f1a\u8c04\u8c00\u62cd\u9a6c\u7ec7\u4eba\uff0c\u6bcf\u65e5\u91cc\u201d\u5e2e\u4e3b\u6a31\u96c4\u65e0\u654c\u201d\uff0c\u201c\u5e2e\u4e3b\u5a01\u9707\u6c5f\u6e56\u201d\u7b49\u7b49\u8a00\u8bed\uff0c\u6d41\u6c34\u4ef7\u704c\u8fdb\u4ed6\u5c14\u4e2d\u3002\u8303\u5e2e\u4e3b\u521d\u65f6\u8fd8\u5140\u81ea\u751f\u6c14\uff0c\u4f46\u8fc7\u5f97\u6570\u65e5\uff0c\u751c\u8a00\u871c\u8bed\u542c\u5f97\u591a\u4e86\uff0c\u7adf\u7136\u6709\u8bf4\u6709\u7b11\u8d77\u6765\u3002\u4e8e\u662f\u8d5b\u603b\u7ba1\u4eb2\u81ea\u51fa\u9a6c\uff0c\u7ed9\u4ed6\u6234\u7684\u5e3d\u5b50\u8d8a\u6765\u8d8a\u9ad8\u3002\u540e\u8d56\u8bba\u5230\u5f53\u4e16\u82f1\u96c4\uff0c\u8303\u5e2e\u4e3b\u56fa\u7136\u81ea\u8d1f\uff0c\u5374\u4ecd\u63a8\u82d7\u4eba\u51e4\u5929\u4e0b\u7b2c\u4e00\u3002\u8d5b\u603b\u7ba1\u8bf4\u9053\uff1a\u201c\u8303\u5e2e\u4e3b\u8fd9\u8bdd\u592a\u8c26\uff0c\u60f3\u90a3\u91d1\u9762\u4f5b\u867d\u7136\u53f7\u79f0\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\uff0c\u4f9d\u5144\u5f1f\u4e4b\u89c1\uff0c\u4e0d\u89c1\u5f97\u5c31\u80fd\u80dc\u8fc7\u5e2e\u4e3b\u3002\u201d\u8303\u5e2e\u4e3b\u7ed9\u4ed6\u4e00\u6367\uff0c\u8212\u670d\u65e0\u6bd4\uff0c\u5fc3\u60f3\u82d7\u4eba\u51e4\u540d\u6c14\u81ea\u7136\u6781\u5927\uff0c\u6b66\u529f\u4e5f\u662f\u771f\u9ad8\uff0d\u4f46\u81ea\u5df1\u4e5f\u672a\u5fc5\u5c31\u5dee\u4e86\u591a\u5c11\u3002 \u3000\u3000\u4e24\u4e2a\u4eba\u957f\u8c08\u4e86\u534a\u591c\u3002\u5230\u7b2c\u4e8c\u65e5\u4e0a\uff0c\u8d5b\u603b\u7ba1\u5ffd\u7136\u5766\u5c82\u81ea\u5df1\u6b66\u529f\u6765\u3002\u4e0d\u4e45\u5728\u603b\u7ba1\u5e9c\u4e2d\u7684\u4f8d\u536b\u4e5f\u6765\u4e00\u9f50\u8bb2\u8bba\uff0c\u90fd\u8bf4\u65e5\u524d\u8d5b\u603b\u7ba1\u4e0e\u82d7\u4eba\u51e4\u63a5\u6218\uff0c\u8d77\u521d\u4e8c\u767e\u62db\u6253\u6210\u4e86\u5e73\u624b\u3002\u5230\u540e\u6765\u8d5b\u603b\u7ba1\u5df2\u7136\u80dc\u5238\u5728\u63e1\uff0c\u82e5\u975e\u82d7\u4eba\u51e4\u89c1\u673a\u9003\u53bb\uff0c\u518d\u62c6\u4e00\u767e\u62db\u4ed6\u975e\u8d25\u4e0d\u53ef\u3002\u8303\u5e2e\u4e3b\u542c\u4e86\uff0c\u8138\u4e0a\u4fbf\u6709\u4e0d\u4fe1\u4e4b\u8272\u3002 \u3000\u3000\u8d5b\u603b\u7ba1\u7b11\u9053\uff1a\u201c\u4e45\u6155\u8303\u5e2e\u4e3b\u4e5d\u4e5d\u516b\u5341\u4e00\u8def\u4e94\u864e\u5200\u5e76\u4e16\u65e0\u53cc\uff0c\u8fd9\u6b21\u6211\u4eec\u5192\u72af\u864e\u5a01\uff0c\u867d\u8bf4\u662f\u7687\u4e0a\u6709\u65e8\uff0c\u4f46\u4e00\u534a\u4e5f\u662f\u5f1f\u5144\u4eec\u60f3\u89c1\u8bc6\u89c1\u8bc6\u5e2e\u4e3b\u7684\u6b66\u529f\u3002\u53ea\u53ef\u60dc\u5927\u4f19\u513f\u8d2a\u529f\u5fc3\u5207\uff0c\u51fa\u9f50\u4e86\u5927\u5185\u5341\u516b\u9ad8\u624b\uff0c\u624d\u8bf7\u5f97\u52a8\u5e2e\u4e3b\u3002\u5144\u5f1f\u672a\u5f97\u80fd\u4e0e\u5e2e\u4e3b\u4e00\u5bf9\u4e00\u7684\u8fc7\u62db\uff0c\u5b9e\u4e3a\u61be\u4e8b\u3002\u73b0\u4e0b\u54b1\u4eec\u8bf4\u5f97\u9ad8\u5174\uff0c\u5c31\u5728\u8fd9\u513f\u9886\u6559\u51e0\u62db\u5982\u4f55\uff1f\u201d\u8303\u5e2e\u4e3b\u4e00\u542c\uff0c\u50b2\u7136\u9053\uff1a\u201c\u8fde\u82d7\u4eba\u51e4\u4e5f\u8d25\u5728\u603b\u7ba1\u624b\u91cc\uff0c\u53ea\u6015\u5728\u4e0b\u4e0d\u662f\u654c\u624b\u3002\u201d\u8d5b\u603b\u7ba1\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\u592a\u5ba2\u6c14\u4e86\u3002\u201d\u4e24\u4eba\u8bf4\u4e86\u810a\u53e5\uff0c\u5f53\u5373\u5728\u603b\u7ba1\u5e9c\u7684\u7ec3\u6b66\u5385\u4e2d\u6bd4\u6b66\u8f83\u91cf\u3002 \u3000\u3000\u8303\u5e2e\u4e3b\u4f7f\u5200\uff0c\u8d5b\u603b\u7ba1\u7684\u5175\u5203\u5374\u6781\u4e3a\u5947\u7279\uff0c\u662f\u4e00\u5bf9\u77ed\u67c4\u7684\u72fc\u7259\u68d2\u3002\u4ed6\u529b\u5927\u62db\u731b\uff0c\u6b66\u529f\u679c\u7136\u5341\u5206\u4e86\u5f97\u3002\u4e24\u4eba\u7ffb\u7ffb\u6eda\u6eda\u6597\u4e86\u4e09\u767e\u4f59\u62db\uff0c\u5168\u7136\u4e0d\u5206\u4e0a\u4e0b\uff0c\u53c8\u6597\u4e86\u4e00\u987f\u996d\u529f\u592b\uff0c\u8d5b\u603b\u7ba1\u6e10\u73b0\u75b2\u6001\uff0c\u7ed9\u8303\u5e2e\u4e3b\u4e00\u67c4\u5200\u8feb\u5728\u5c4b\u89d2\uff0c\u8fde\u51b2\u6570\u6b21\u90fd\u62a2\u4e0d\u51fa\u4ed6\u5200\u5708\u3002\u8d5b\u603b\u7ba1\u65e0\u5948\uff0c\u53ea\u5f97\u8bf4\u9053\uff1a\u201c\u8303\u5e2e\u4e3b\u679c\u7136\u597d\u672c\u4e8b\uff0c\u5728\u4e0b\u670d\u8f93\u4e86\u3002\u201d\u8303\u5e2e\u4e3b\u4e00\u7b11\uff0c\u63d0\u5200\u8dc3\u5f00\u3002\u8d5b\u603b\u7ba1\u6068\u6068\u7684\u5c06\u53cc\u68d2\u629b\u5728\u5730\u4e0b\uff0c\u53f9\u9053\uff1a\u201c\u6211\u81ea\u8d1f\u82f1\u96c4\u65e0\u654c\uff0c\u5c82\u77e5\u5929\u5916\u6709\u5929\uff0c\u4eba\u4e0a\u6709\u4eba\u3002\u201d\u8bf4\u7740\u4f38\u8896\u62b9\u6c57\uff0c\u6c14\u5598\u4e0d\u5df2\u3002 \u3000\u3000\u7ecf\u6b64\u4e00\u5f79\uff0c\u8303\u5e2e\u4e3b\u66f4\u8ba9\u4f17\u4eba\u6367\u4e0a\u4e86\u5929\u53bb\u3002\u4ed6\u628a\u4f17\u4f8d\u536b\u4e5f\u90fd\u5f53\u6210\u4e86\u81f3\u4ea4\u597d\u53cb\uff0c\u5bf9\u8d5b\u603b\u7ba1\u66f4\u662f\u8a00\u542c\u8ba1\u4ece\u3002\u8fd9\u4e2a\u7c97\u9c81\u6c49\u5b50\u54ea\u77e5\u9053\u8d5b\u603b\u7ba1\u6709\u610f\u76f8\u8ba9\uff0c\u82e5\u662f\u5404\u51ed\u771f\u5b9e\u529f\u592b\u76f8\u62da\uff0c\u4ed6\u5728\u4e00\u767e\u62db\u5185\u5c31\u5f97\u8f93\u5728\u72fc\u7259\u53cc\u68d2\u4e4b\u4e0b\u3002 \u3000\u3000\u7136\u5219\u8d5b\u603b\u7ba1\u4f55\u4ee5\u8981\u8d39\u504c\u5927\u6c14\u529b\uff0c\u5343\u65b9\u767e\u8ba1\u7684\u4e0e\u4ed6\u7ed3\u7eb3\uff1f\u539f\u6765\u8303\u5e2e\u4e3b\u7684\u6b66\u529f\u867d\u672a\u80fd\u7b97\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u4f46\u4ed6\u6709\u4e00\u9879\u5bb6\u4f20\u7edd\u6280\uff0c\u5374\u662f\u4eba\u6240\u83ab\u53ca\uff0c\u90a3\u5c31\u662f\u4e8c\u5341\u4e09\u8def\u201c\u9f99\u722a\u64d2\u62ff\u624b\u201d\uff0c\u6cbe\u4e0a\u8eab\u65f6\u76f4\u5982\u94bb\u7b4b\u5165\u9aa8\uff0c\u6572\u9489\u8f6c\u811a\u3002\u4e0d\u8bba\u654c\u4eba\u6b66\u529f\u5982\u4f55\u9ad8\u5f3a\uff0c\u53ea\u8981\u8eab\u4f53\u7684\u4efb\u4f55\u90e8\u4f4d\u7ed9\u4ed6\u624b\u6307\u4e00\u642d\u4e0a\uff0c\u7acb\u65f6\u5c31\u7ed9\u62ff\u4f4f\uff0c\u4e07\u4e07\u8131\u8eab\u4e0d\u5f97\u3002\u8d5b\u603b\u7ba1\u542c\u4e86\u7530\u5f52\u519c\u4e4b\u8a00\uff0c\u8981\u64d2\u4f4f\u82d7\u4eba\u51e4\u53d6\u90a3\u5b9d\u85cf\u7684\u5173\u952e\uff0c\u201c\u5929\u7262\u8bbe\u7b3c\u201d\u4e4b\u8ba1\u65e2\u7136\u4e0d\u6210\uff0c\u4e8e\u662f\u60f3\u5230\u501f\u91cd\u8303\u5e2e\u4e3b\u8fd9\u9879\u7edd\u6280\u3002\u60f3\u90a3\u91d1\u9762\u4f5b\u4f55\u7b49\u672c\u9886\uff0c\u8303\u5e2e\u4e3b\u82e5\u662f\u6b63\u9762\u548c\u4ed6\u4e3a\u654c\uff0c\u4ed6\u7109\u80fd\u8ba9\u9f99\u722a\u64d2\u62ff\u624b\u4e0a\u8eab\uff1f\u4f46\u8303\u5e2e\u4e3b\u548c\u4ed6\u662f\u591a\u5e74\u4e16\u4ea4\uff0c\u8981\u662f\u51fa\u5176\u4e0d\u610f\u7684\u7a81\u65bd\u6697\u88ad\uff0c\u4fbf\u6709\u6210\u529f\u4e4b\u673a\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u89c1\u8303\u5e2e\u4e3b\u76f8\u8c22\uff0c\u5f53\u5373\u62f1\u624b\u8fd8\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u533a\u533a\u5c0f\u4e8b\uff0c\u4f55\u5fc5\u6302\u9f7f\uff1f\u201d\u8f6c\u5934\u95ee\u675c\u5e0c\u5b5f\u9053\uff1a\u201c\u4f46\u4e0d\u77e5\u90a3\u5230\u5e95\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u675c\u5144\u56e0\u4f55\u4e0e\u4ed6\u7ed3\u6028\uff1f\u201d \u3000\u3000\u675c\u5e0c\u5b5f\u8138\u4e0a\u4e00\u5589\uff0c\u542b\u542b\u7cca\u7cca\u7684\u9053\uff1a\u201c\u6211\u548c\u8fd9\u4eba\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4e0d\u77e5\u4ed6\u542c\u4e86\u4ec0\u4e48\u8c23\u8a00\uff0c\u7adf\u8bf4\u6211\u62ff\u4e86\u4ed6\u5bb6\u4f20\u5b9d\u7269\uff0c\u6570\u6b21\u5411\u6211\u7d22\u53d6\u3002\u6211\u77e5\u4ed6\u6b66\u827a\u9ad8\u5f3a\uff0c\u81ea\u5df1\u5e74\u7eaa\u5927\u4e86\uff0c\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u662f\u4ee5\u8bf7\u5404\u4f4d\u4e0a\u5cf0\uff0c\u5927\u5bb6\u8bf4\u4e2a\u660e\u767d\u3002\u82e5\u662f\u4ed6\u6043\u5f3a\u4e0d\u670d\u8bad\u6559\u8bad\u8fd9\u540e\u751f\u5c0f\u5b50\u3002\u201d\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u4ed6\u8bf4\u675c\u5144\u53d6\u4e86\u4ed6\u7684\u5bb6\u4f20\u5b9d\u7269\uff0c\u5374\u662f\u4f55\u7269\uff1f\u201d \u3000\u3000\u675c\u5e0c\u5b5f\u9053\uff1a\u201c\u54ea\u6709\u4ec0\u4e48\u5b9d\u7269\uff1f\u5b8c\u5168\u80e1\u8bf4\u516b\u9053\u3002\u201d \u3000\u3000\u5f53\u5e74\u82d7\u4eba\u51e4\u81ea\u80e1\u4e00\u5200\u6b7b\u540e\uff0c\u5fc3\u4e2d\u90c1\u90c1\uff0c\u4fbf\u5373\u524d\u8d74\u8fbd\u4e1c\uff0c\u60f3\u67e5\u8bbf\u80e1\u4e00\u5200\u7684\u4eb2\u4ea4\u6545\u65e7\uff0c\u6253\u542c\u8fd9\u4f4d\u751f\u5e73\u552f\u4e00\u77e5\u5df1\u7684\u8f76\u4e8b\u4e49\u4e3e\u3002 \u3000\u3000\u4e00\u67e5\u4e4b\u4e0b\uff0c\u5f97\u6089\u675c\u5e0c\u5b5f\u4e0e\u80e1\u4e00\u5200\u76f8\u8bc6\uff0c\u4e8e\u662f\u4e0a\u7389\u7b14\u5cf0\u675c\u5bb6\u5e84\u6765\u62dc\u8bbf\u3002\u675c\u5e0c\u5b5f\u4e8e\u80e1\u4e00\u5200\u7684\u4e8b\u8ff9\u8bf4\u4e0d\u4e0a\u591a\u5c11\uff0c\u4f46\u5bf9\u82d7\u4eba\u51e4\u62db\u5f85\u5f97\u5341\u5206\u6bb7\u52e4\uff0c\u53c8\u4eb2\u81ea\u966a\u4ed6\u53bb\u770b\u80e1\u4e00\u5200\u7684\u6545\u5b85\uff0c\u5374\u89c1\u80e1\u5bb6\u95e8\u57a3\u7834\u8d25\uff0c\u65e9\u65e0\u4eba\u5c45\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u63a8\u7231\u5bf9\u80e1\u4e00\u5200\u7684\u60c5\u8c0a\uff0c\u7531\u6b64\u800c\u4e0e\u675c\u5e0c\u5b5f\u8ba2\u4ea4\uff0c\u90a3\u5df2\u662f\u4e8c\u5341\u591a\u5e74\u524d\u7684\u4e8b\u4e86\u3002\u8fd9\u65f6\u542c\u4ed6\u8bf4\u5f97\u652f\u652f\u543e\u543e\uff0c\u4fbf\u9053\uff1a\u201c\u5018\u82e5\u6b64\u7269\u5f53\u771f\u662f\u90a3\u6240\u6709\uff0c\u5f85\u4f1a\u4ed6\u4e0a\u5f97\u5cf0\u6765\uff0c\u675c\u5144\u8fd8\u4e86\u7ed9\u4ed6\uff0c\u4e5f\u5c31\u662f\u4e86\u3002\u201d\u675c\u5e0c\u5b5f\u6025\u9053\uff1a\u201c\u672c\u5c31\u6ca1\u4ec0\u4e48\u5b9d\u7269\uff0c\u5374\u6559\u6211\u54ea\u91cc\u53bb\u53d8\u51fa\u6765\u7ed9\u4ed6\uff1f\u201d \u3000\u3000\u8303\u5e2e\u4e3b\u5fc3\u60f3\u82d7\u4eba\u51e4\u7cbe\u660e\u673a\u8b66\uff0c\u65f6\u5019\u4e00\u957f\uff0c\u5fc5\u80fd\u53d1\u89c9\u5c4b\u4e2d\u6709\u4eba\u57cb\u4f0f\uff0c\u5f53\u5373\u529d\u9053\uff1a\u201c\u675c\u5e84\u4e3b\uff0c\u82d7\u7237\u7684\u69d0\u4e00\u70b9\u4e0d\u9519\uff0c\u7269\u5404\u6709\u4e3b\uff0c\u4f55\u51b5\u662f\u5bb6\u4f20\u73cd\u5b9d\uff1f\u4f60\u8fd8\u7ed9\u4e86\u4ed6\uff0c\u4e5f\u5c31\u662f\u4e86\uff0c\u4f55\u5fc5\u5927\u52a8\u5e72\u6208\uff0c\u4f24\u4e86\u548c\u6c14\uff1f\u201d\u675c\u5e0c\u5b5f\u6025\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u8fd9\u822c\u8bf4\uff0c\u96be\u9053\u4e0d\u4fe1\u6211\u7684\u8bf4\u8bdd\uff1f\u201d\u8303\u5e2e\u4e3b\u9053\uff1a\u201c\u5728\u4e0b\u5bf9\u6b64\u4e8b\u4e0d\u77e5\u539f\u59d4\uff0c\u4f46\u91d1\u9762\u4f5b\u82d7\u7237\u65e2\u8fd9\u822c\u8bf4\uff0c\u5b9a\u662f\u4e0d\u9519\u3002\u8303\u67d0\u7eb5\u6a2a\u6c5f\u6e56\uff0c\u5bf9\u8c01\u7684\u8bdd\u90fd\u4e0d\u80af\u4fe1\uff0c\u53ef\u5c31\u53ea\u670d\u4e86\u91d1\u9762\u4f5b\u82d7\u7237\u4e00\u4eba\u3002\u201d \u3000\u3000\u4ed6\u4e00\u9762\u8bf4\uff0c\u4e00\u9762\u8d70\u5230\u82d7\u4eba\u51e4\u8eab\u540e\uff0c\u53cc\u624b\u821e\u52a8\uff0c\u4ee5\u52a9\u8a00\u8bed\u7684\u58f0\u52bf\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u542c\u4ed6\u8bdd\u4e2d\u504f\u7740\u81ea\u5df1\uff0c\u5fc3\u60f3\uff1a\u201c\u4ed6\u662f\u4e00\u5e2e\u4e4b\u4e3b\uff0c\u7a76\u7adf\u89c1\u4e8b\u660e\u767d\u3002\u201d\u7a81\u89c9\u8033\u540e\u201c\u98ce\u6c60\u7a74\u201d\u4e0e\u80cc\u5fc3\u201c\u795e\u9053\u7a74\u201d\u4e0a\u4e00\u9ebb\uff0c\u60c5\u77e5\u4e0d\u5999\uff0c\u5de6\u81c2\u6025\u5fd9\u6325\u51fa\u51fb\u53bb\u3002\u54ea\u77e5\u8fd9\u4e24\u5927\u8981\u7a74\u88ab\u8303\u5e2e\u4e3b\u7528\u9f99\u722a\u64d2\u62ff\u624b\u62ff\u4f4f\uff0c\u767b\u65f6\u5168\u8eab\u9178\u9ebb\uff0c\u4efb\u4ed6\u6709\u5929\u5927\u6b66\u529f\u3001\u767e\u822c\u795e\u901a\uff0c\u5374\u5df2\u662f\u534a\u70b9\u65bd\u5c55\u4e0d\u51fa\u3002 \u3000\u3000\u4f46\u91d1\u9762\u4f5b\u53f7\u79f0\u201c\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u201d\uff0c\u5947\u53d8\u5f02\u9669\uff0c\u4e00\u751f\u4e2d\u4e0d\u77e5\u5df2\u7ecf\u5386\u51e1\u51e0\uff0c\u5c82\u80fd\u5982\u6b64\u675f\u624b\u5f85\u6bd9\uff1f\u5f53\u4e0b\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u4f4e\u5934\uff0c\u8170\u95f4\u7528\u529b\uff0c\u7adf\u5c06\u8303\u5e2e\u4e3b\u4e00\u4e2a\u5e9e\u5927\u7684\u8eab\u8eaf\u4ece\u5934\u9876\u7529\u4e86\u8fc7\u53bb\u3002\u8d5b\u603b\u7ba1\u7b49\u9f50\u58f0\u547c\u53f1\uff0c\u5404\u4ece\u9690\u8eab\u5904\u7a9c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8303\u5e2e\u4e3b\u88ab\u82d7\u4eba\u51e4\u7529\u8fc7\u4e86\u5934\u9876\uff0c\u4f46\u4ed6\u8fd9\u9f99\u722a\u64d2\u62ff\u624b\u5982\u5f71\u968f\u5f62\uff0c\u4f3c\u86c6\u9644\u9aa8\uff0c\u8eab\u5b50\u5df2\u5728\u82d7\u4eba\u51e4\u524d\u9762\uff0c\u4e24\u53ea\u624b\u722a\u5374\u4ecd\u662f\u7262\u7262\u62ff\u4f4f\u4e86\u4ed6\u80cc\u5fc3\u7a74\u9053\u3002\u82d7\u4eba\u51e4\u773c\u89c1\u56db\u4e0b\u91cc\u6709\u4eba\u7a9c\u51fa\uff0c\u6697\u60f3\uff1a\u201c\u6211\u4e00\u751f\u7eb5\u6a2a\u6c5f\u6e56\uff0c\u4eca\u65e5\u9634\u6c9f\u7ffb\u8239\uff0c\u7adf\u906d\u5c0f\u4eba\u6bd2\u624b\u3002\u201d\u53ea\u89c1\u4e00\u540d\u4f8d\u536b\u6251\u4e0a\u524d\u6765\uff0c\u5f20\u81c2\u62b1\u5411\u4ed6\u5934\u9888\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u76db\u6012\u4e4b\u4e0b\uff0c\u65e0\u53ef\u95ea\u907f\uff0c\u8116\u5b50\u5411\u540e\u4e00\u4ef0\uff0c\u968f\u5373\u8111\u888b\u5411\u524d\u4e00\u633a\uff0c\u731b\u5730\u4e00\u4e2a\u5934\u9524\u649e\u4e86\u8fc7\u53bb\u3002\u8fd9\u65f6\u4ed6\u5168\u8eab\u5185\u52b2\uff0c\u90fd\u805a\u5728\u989d\u5934\uff0c\u4e00\u9524\u649e\u5728\u90a3\u4f8d\u536b\u53cc\u773c\u4e4b\u4e0b\uff0c\u5580\u7684\u4e00\u58f0\uff0c\u90a3\u4f8d\u536b\u767b\u65f6\u6bd9\u547d\u3002\u4f59\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u672c\u6765\u4e00\u9f50\u6251\u4e0b\uff0c\u5ffd\u5730\u90fd\u5728\u79bb\u82d7\u4eba\u51e4\u6570\u5c3a\u4e4b\u5916\u6b62\u4f4f\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u56db\u80a2\u65e0\u529b\uff0c\u5934\u9888\u5374\u80fd\u8f6c\u52a8\uff0c\u4ed6\u4e00\u649e\u6210\u529f\uff0c\u968f\u5373\u6a2a\u9888\u53c8\u5411\u8303\u5e2e\u4e3b\u6025\u649e\u3002\u8303\u5e2e\u4e3b\u5413\u5f97\u5fc3\u80c6\u4ff1\u88c2\uff0c\u6025\u4e2d\u751f\u667a\uff0c\u4e00\u4f4e\u5934\uff0c\u7262\u7262\u62b1\u4f4f\u4ed6\u7684\u8170\u8eab\uff0c\u5c06\u8111\u888b\u9876\u4f4f\u4ed6\u7684\u5c0f\u8179\u3002\u82d7\u4eba\u51e4\u56db\u80a2\u6d3b\u52a8\uff0c\u4e00\u8db3\u8e22\u98de\u4e00\u540d\u8feb\u8fd1\u8eab\u65c1\u7684\u4f8d\u536b\uff0c\u7acb\u5373\u4f38\u624b\u5f80\u8303\u5e2e\u4e3b\u80cc\u5fc3\u62cd\u53bb\uff0c\u54ea\u77e5\u624b\u638c\u521a\u4e3e\u5230\u7a7a\u4e2d\uff0c\u56db\u80a2\u7acb\u65f6\u9178\u9ebb\uff0c\u8fd9\u4e00\u638c\u7adf\u7136\u51fb\u4e0d\u4e0b\u6765\uff0c\u539f\u6765\u8303\u5e2e\u4e3b\u53c8\u5df2\u62ff\u4f4f\u4ed6\u8170\u95f4\u7a74\u9053\u3002 \u3000\u3000\u8fd9\u51e0\u4e0b\u5154\u8d77\u9e58\u843d\uff0c\u77ac\u606f\u6570\u53d8\u3002\u8d5b\u603b\u7ba1\u77e5\u9053\u8303\u5e2e\u4e3b\u7684\u5077\u88ad\u53ea\u80fd\u89c1\u529f\u4e8e\u9877\u523b\uff0c\u65f6\u5019\u7a0d\u957f\uff0c\u82d7\u4eba\u51e4\u5fc5\u80fd\u5316\u89e3\uff0c\u5f53\u5373\u62a2\u4e0a\u524d\u53bb\uff0c\u4f38\u6307\u5728\u4ed6\u7b11\u8170\u7a74\u4e2d\u70b9\u4e86\u4e24\u70b9\uff0c\u4ed6\u7684\u70b9\u7a74\u529f\u592b\u51fa\u624b\u8fdf\u7f13\uff0c\u4f46\u843d\u624b\u6781\u91cd\u3002\u82d7\u4eba\u51e4\u563f\u7684\u4e00\u58f0\uff0c\u9669\u9669\u6655\u53bb\uff0c\u5c31\u6b64\u5168\u8eab\u8f6f\u762b\u3002 \u3000\u3000\u8303\u5e2e\u4e3b\u94bb\u5728\u82d7\u4eba\u51e4\u6000\u4e2d\uff0c\u4e0d\u77e5\u8eab\u5916\u4e4b\u4e8b\uff0c\u5341\u6307\u7d27\u7d27\u62ff\u5728\u4ed6\u7a74\u9053\u4e4b\u4e2d\u3002\u8d5b\u603b\u7ba1\u7b11\u9053\uff1a\u201c\u8303\u5e2e\u4e3b\uff0c\u4f60\u7acb\u4e86\u5947\u529f\u4e00\u4ef6\uff0c\u653e\u624b\u4e86\u5427\uff01\u201d\u4ed6\u8bf4\u5230\u7b2c\u4e09\u904d\uff0c\u8303\u5e2e\u4e3b\u65b9\u59cb\u542c\u89c1\u3002\u4ed6\u62ac\u8d77\u5934\u6765\uff0c\u53ef\u662f\u5140\u81ea\u4e0d\u6562\u653e\u624b\u3002 \u3000\u3000\u4e00\u540d\u4f8d\u536b\u4ece\u56ca\u4e2d\u53d6\u51fa\u7cbe\u94a2\u9563\u94d0\uff0c\u5c06\u82d7\u4eba\u51e4\u624b\u811a\u90fd\u94d0\u4f4f\u4e86\uff0c\u8303\u5e2e\u4e3b\u8fd9\u624d\u677e\u624b\u3002 \u3000\u3000\u8d5b\u603b\u7ba1\u5bf9\u82d7\u4eba\u51e4\u6781\u662f\u5fcc\u60ee\uff0c\u53ea\u6015\u4ed6\u7adf\u53c8\u8bbe\u6cd5\u5154\u8131\uff0c\u90a3\u53ef\u662f\u540e\u60a3\u65e0\u7a77\uff0c\u4ece\u4f8d\u536b\u624b\u4e2d\u63a5\u8fc7\u5355\u5200\uff0c\u8bf4\u9053\uff1a\u201c\u82d7\u4eba\u51e4\uff0c\u975e\u662f\u6211\u59d3\u8d5b\u7684\u4e0d\u591f\u670b\u53cb\uff0c\u53ea\u6028\u4f60\u672c\u9886\u592a\u5f3a\uff0c\u4e0d\u6311\u65ad\u4f60\u7684\u624b\u7b4b\u811a\u7b4b\uff0c\u6211\u4eec\u5927\u4f19\u513f\u767d\u5929\u5403\u4e0d\u4e0b\u996d\uff0c\u665a\u4e0a\u7761\u4e0d\u7740\u89c9\u3002\u201d\u5de6\u624b\u62ff\u4f4f\u82d7\u4eba\u51e4\u53f3\u81c2\uff0c\u53f3\u624b\u4e3e\u5200\uff0c\u5c31\u8981\u65a9\u4ed6\u81c2\u4e0a\u7b4b\u8109\uff0c\u53ea\u6d88\u56db\u5200\u4e0b\u53bb\uff0c\u82d7\u4eba\u51e4\u7acb\u65f6\u5c31\u6210\u4e86\u5e9f\u4eba\u3002 \u3000\u3000\u8303\u5e2e\u4e3b\u4f38\u624b\u67b6\u4f4f\u8d5b\u603b\u7ba1\u624b\u8155\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u80fd\u4f24\u4ed6\uff01\u4f60\u7b54\u5e94\u6211\u7684\uff0c\u53c8\u53d1\u8fc7\u6bd2\u8a93\u3002\u201d\u8d5b\u603b\u7ba1\u4e00\u58f0\u51b7\u7b11\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u8fd8\u9053\u6211\u5f53\u771f\u654c\u4f60\u4e0d\u8fc7\u3002\u4e0d\u7ed9\u4f60\u4e9b\u989c\u8272\u770b\u770b\uff0c\u53ea\u6015\u4f60\u8fd9\u5c0f\u5b50\u72c2\u5984\u4e00\u4e16\uff01\u201d \u3000\u3000\u5f53\u4e0b\u624b\u8155\u4e00\u6c89\uff0c\u8170\u95f4\u8fd0\u52b2\uff0c\u53f3\u80a9\u7a81\u7136\u649e\u5c06\u8fc7\u53bb\u3002\u4e00\u6765\u4ed6\u8fd9\u4e00\u649e\u529b\u9053\u5947\u5927\uff0c\u4e8c\u6765\u8303\u5e2e\u4e3b\u5e76\u672a\u63d0\u9632\uff0c\u84ec\u7684\u4e00\u58f0\uff0c\u8eab\u5b50\u76f4\u98de\u51fa\u53bb\uff0c\u7adf\u5c06\u53a2\u623f\u677f\u58c1\u649e\u7a7f\u4e00\u4e2a\u7a9f\u7abf\uff0c\u7834\u58c1\u800c\u51fa\u3002\u8d5b\u603b\u7ba1\u54c8\u54c8\u5927\u7b11\uff0c\u4e3e\u5200\u53c8\u5411\u82d7\u4eba\u51e4\u53f3\u81c2\u65a9\u4e0b\u3002 \u3000\u3000\u80e1\u6590\u5728\u5e10\u5185\u542c\u5f97\u660e\u767d\uff0c\u5fc3\u60f3\uff1a\u201c\u82d7\u4eba\u51e4\u867d\u662f\u6211\u6740\u7236\u4ec7\u4eba\uff0c\u4f46\u4ed6\u4e43\u5f53\u4e16\u5927\u4fa0\uff0c\u5c82\u80fd\u547d\u4e27\u9f20\u8f88\u4e4b\u624b\uff1f\u201d\u4e00\u58f0\u5927\u559d\uff0c\u4ece\u7f57\u5e10\u5185\u8dc3\u51fa\uff0c\u98de\u51fa\u4e00\u638c\uff0c\u5df2\u5c06\u4e00\u540d\u4f8d\u536b\u62cd\u5f97\u649e\u5411\u8d5b\u603b\u7ba1\u3002\u8fd9\u4e00\u6765\u5947\u53d8\u9661\u8d77\uff0c\u8d5b\u603b\u7ba1\u731d\u4e0d\u53ca\u9632\uff0c\u629b\u4e0b\u624b\u4e2d\u5355\u5200\uff0c\u5c06\u90a3\u4f8d\u536b\u63a5\u4f4f\u3002 \u3000\u3000\u80e1\u6590\u4e58\u8d5b\u603b\u7ba1\u8fd9\u4e48\u4e00\u7f13\uff0c\u53cc\u624b\u5df2\u6293\u4f4f\u4e24\u540d\u4f8d\u536b\uff0c\u5934\u5bf9\u5934\u7684\u4e00\u78b0\uff0c\u4e24\u4eba\u5934\u9aa8\u7834\u88c2\uff0c\u7acb\u65f6\u6bd9\u547d\u3002\u80e1\u6590\u5de6\u638c\u53f3\u62f3\uff0c\u53c8\u5411\u4e8c\u4eba\u6253\u53bb\u3002\u6df7\u4e71\u4e4b\u4e2d\uff0c\u4f17\u4eba\u4e5f\u4e0d\u77e5\u6765\u4e86\u591a\u5c11\u654c\u4eba\uff0c\u4f46\u89c1\u80e1\u6590\u4e00\u51fa\u624b\u5c31\u662f\u795e\u5a01\u8feb\u4eba\uff0c\u4e0d\u7981\u5148\u81ea\u80c6\u602f\u3002 \u3000\u3000\u80e1\u6590\u4e00\u62f3\u6253\u5728\u4e00\u540d\u4f8d\u536b\u5934\u4e0a\uff0c\u5c06\u4ed6\u51fb\u5f97\u6655\u4e86\u8fc7\u53bb\uff0c\u5de6\u624b\u4e00\u638c\u6325\u51fa\uff0c\u500f\u89c9\u654c\u4eba\u4e00\u7c98\u4e00\u63a8\uff0c\u81ea\u5df1\u624b\u638c\u767b\u65f6\u6ed1\u4e86\u4e0b\u6765\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u5b9a\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u5bf9\u624b\u94f6\u9aef\u8fc7\u8179\uff0c\u6ee1\u8138\u7ea2\u5149\uff0c\u867d\u4e0d\u8bc6\u6b64\u4eba\uff0c\u4f46\u4ed6\u8fd9\u4e00\u62db\u201c\u6df7\u6c8c\u521d\u5f00\u201d\u5b88\u4e2d\u6709\u653b\uff0c\u7684\u662f\u5185\u5bb6\u540d\u624b\uff0c\u975e\u65e0\u6781\u95e8\u848b\u8001\u62f3\u5e08\u83ab\u5c5e\u3002 \u3000\u3000\u80e1\u6590\u773c\u89c1\u654c\u624b\u4f17\u591a\uff0c\u5185\u4e2d\u4e0d\u4e4f\u9ad8\u624b\uff0c\u5f53\u4e0b\u5fc3\u751f\u4e00\u8ba1\uff0c\u98de\u8d77\u4e00\u817f\uff0c\u731b\u5730\u5f80\u7075\u6e05\u5c45\u58eb\u7684\u80f8\u53e3\u8e22\u53bb\u3002\u7075\u6e05\u5c45\u58eb\u7ec3\u7684\u662f\u5916\u5bb6\u529f\u592b\uff0c\u89c1\u4ed6\u98de\u8db3\u8e22\u5230\uff0c\u624b\u638c\u5f80\u4ed6\u8db3\u80cc\u786c\u65a9\u4e0b\u53bb\u3002\u80e1\u6590\u5c31\u52bf\u4e00\u7f29\uff0c\u53cc\u624b\u63a2\u51fa\uff0c\u5f80\u4eba\u4e1b\u4e2d\u6293\u53bb\u3002\u53a2\u623f\u4e4b\u4e2d\uff0c\u5730\u52bf\u72ed\u7a84\uff0c\u5341\u591a\u4eba\u6324\u5728\u4e00\u8d77\uff0c\u4f17\u4eba\u65e0\u5904\u53ef\u907f\u3002\u547c\u559d\u58f0\u4e2d\uff0c\u80e1\u6590\u4e00\u624b\u5df2\u6293\u4f4f\u675c\u5e0c\u5b5f\u80f8\u819b\uff0c\u53e6\u4e00\u624b\u6293\u4f4f\u4e86\u7384\u51a5\u5b50\u7684\u5c0f\u8179\uff0c\u5c06\u4e24\u4eba\u5f53\u4f5c\u5175\u5668\u4e00\u822c\uff0c\u76f4\u5f80\u4f17\u4eba\u8eab\u4e0a\u731b\u63a8\u8fc7\u53bb\u3002\u4f17\u4eba\u6324\u5728\u4e00\u8d77\uff0c\u88ab\u4ed6\u6293\u7740\u4e24\u4eba\u5f3a\u529b\u63a8\u6765\uff0c\u53ea\u6015\u4f24\u4e86\u81ea\u5df1\u4eba\uff0c\u4e0d\u6562\u53cd\u624b\u76f8\u6297\uff0c\u53ea\u5f97\u5411\u540e\u9000\u7f29\u3002 \u3000\u3000\u5341\u4f59\u4eba\u7ed9\u903c\u5728\u5c4b\u89d2\u4e4b\u4e2d\uff0c\u4e00\u65f6\u6781\u4e3a\u72fc\u72c8\u3002 \u3000\u3000\u8d5b\u603b\u7ba1\u89c1\u60c5\u52bf\u4e0d\u5999\uff0c\u4ece\u4eba\u4e1b\u4e2d\u4e00\u8dc3\u800c\u8d77\uff0c\u5341\u6307\u5982\u94a9\uff0c\u731b\u5f80\u80e1\u6590\u5934\u9876\u6293\u5230\u3002\u80e1\u6590\u6b63\u662f\u8981\u5f15\u4ed6\u51fa\u624b\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u5411\u540e\u8dc3\u5f00\u6570\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u8001\u8d5b\u554a\u8001\u8d5b\uff0c\u4f60\u592a\u4e0d\u8981\u8138\u54ea\uff01\u201d\u8d5b\u603b\u7ba1\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u8981\u8138\uff1f\u201d \u3000\u3000\u80e1\u6590\u624b\u4e2d\u4ecd\u662f\u6293\u4f4f\u675c\u5e0c\u5b5f\u4e0e\u7384\u51a5\u5b50\u4e8c\u4eba\uff0c\u4ed6\u6240\u6293\u4ff1\u5728\u8981\u7a74\uff0c\u4e24\u4eba\u7a7a\u6709\u4e00\u8eab\u672c\u4e8b\uff0c\u5374\u534a\u70b9\u65bd\u5c55\u4e0d\u51fa\uff0c\u53ea\u6709\u8f6f\u7ef5\u7ef5\u7684\u4efb\u4ed6\u6446\u5e03\u3002\u80e1\u6590\u9053\uff1a\u201c\u4f60\u5408\u5341\u4f59\u4eba\u4e4b\u529b\uff0c\u53c8\u65bd\u5978\u8c0b\u8be1\u8ba1\uff0c\u624d\u5c06\u91d1\u9762\u4f5b\u62ff\u4f4f\uff0c\u79f0\u4ec0\u4e48\u6ee1\u6d32\u7b2c\u4e00\u9ad8\u624b\uff1f\u201d \u3000\u3000\u8d5b\u603b\u7ba1\u7ed9\u4ed6\u8bf4\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u5de6\u624b\u4e00\u6446\uff0c\u547d\u4f17\u4eba\u5e03\u5728\u56db\u89d2\uff0c\u5c06\u80e1\u6590\u56e2\u56e2\u56f4\u4f4f\uff0c\u559d\u9053\uff1a\u201c\u4f60\u5c31\u662f\u4ec0\u4e48\u4e86\uff1f\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u6b63\u662f\u533a\u533a\u5728\u4e0b\u3002\u6211\u5148\u524d\u4e5f\u66fe\u542c\u8bf4\u5317\u4eac\u6709\u4e2a\u4ec0\u4e48\u8d5b\u603b\u7ba1\uff0c\u8fd8\u7b97\u5f97\u662f\u4e2a\u4eba\u7269\uff0c\u54ea\u77e5\u7adf\u662f\u5982\u6b64\u65e0\u803b\u5c0f\u4eba\u3002\u8fd9\u6837\u7684\u8113\u5305\u6df7\u86cb\uff0c\u5230\u5916\u9762\u6765\u5145\u4ec0\u4e48\u5b57\u53f7\uff1f\u7ed9\u6211\u65e9\u70b9\u513f\u56de\u53bb\u62b1\u5a03\u5a03\u5427\uff01\u201d \u3000\u3000\u8d5b\u603b\u7ba1\u4e00\u751f\u81ea\u8d1f\uff0c\u54ea\u91cc\u54bd\u5f97\u4e0b\u8fd9\u53e3\u6c14\u53bb\uff1f\u773c\u89c1\u80e1\u6590\u867d\u662f\u6d53\u9aef\u6ee1\u816e\uff0c\u5e74\u7eaa\u5374\u8f7b\uff0c\u5fc3\u60f3\u4f60\u672c\u9886\u518d\u5f3a\uff0c\u529f\u529b\u54ea\u6709\u6211\u6df1\uff0c\u7136\u89c1\u4ed6\u6293\u4f4f\u4e86\u675c\u5e0c\u5b5f\u4e0e\u7384\u51a5\u5b50\uff0c\u4e3e\u91cd\u82e5\u8f7b\uff0c\u6beb\u4e0d\u8d39\u529b\uff0c\u5fc3\u4e0b\u53c8\u81ea\u5fcc\u60ee\uff0c\u4e0d\u6562\u51fa\u53e3\u6311\u6218\uff0c\u6b63\u81ea\u8e0c\u8e87\uff0c\u80e1\u6590\u53eb\u9053\uff1a\u201c\u6765\u6765\u6765\uff0c\u54b1\u4eec\u6bd4\u5212\u6bd4\u5212\u3002\u4e09\u62db\u4e4b\u5185\u8d62\u4e0d\u4e86\u4f60\uff0c\u59d3\u80e1\u7684\u8ddf\u4f60\u78d5\u5934\uff01\u201d \u3000\u3000\u8d5b\u603b\u7ba1\u6b63\u611f\u4e3a\u96be\uff0c\u4e00\u542c\u6b64\u8a00\uff0c\u5fc3\u60f3\uff1a\u201c\u82e5\u8981\u80dc\u4f60\uff0c\u539f\u65e0\u628a\u63e1\uff0c\u4f46\u51ed\u4f60\u6709\u5929\u5927\u672c\u9886\uff0c\u60f3\u5728\u4e09\u62db\u4e4b\u4e2d\u80dc\u6211\uff0c\u9664\u975e\u6211\u662f\u6b7b\u4eba\u3002\u201d \u3000\u3000\u4ed6\u6124\u6781\u53cd\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5f88\u597d\uff0c\u59d3\u8d5b\u7684\u5c31\u966a\u4f60\u8d70\u8d70\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u5018\u82e5\u4e09\u62db\u4e4b\u5185\u4f60\u8d25\u4e8e\u6211\u624b\uff0c\u90a3\u4fbf\u600e\u5730\uff1f\u201d\u8d5b\u603b\u7ba1\u9053\uff1a\u201c\u4efb\u51ed\u4f60\u5904\u7f6e\u4fbf\u662f\u3002\u8d5b\u67d0\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u90a3\u65f6\u5c82\u80fd\u518d\u6709\u8138\u9762\u6d3b\u5728\u4e16\u4e0a\uff1f\u4e0d\u5fc5\u591a\u8a00\uff0c\u770b\u62db\uff01\u201d\u8bf4\u7740\u53cc\u62f3\u76f4\u51fa\uff0c\u731b\u5f80\u80e1\u6590\u80f8\u53e3\u51fb\u53bb\u3002\u4ed6\u89c1\u80e1\u6590\u6293\u4f4f\u675c\u7384\u4e8c\u4eba\uff0c\u53ea\u6015\u4ed6\u4ee5\u4e8c\u4eba\u8eab\u5b50\u6321\u67b6\uff0c\u5f53\u4e0b\u6b3a\u8eab\u76f4\u8fdb\uff0c\u53eb\u4ed6\u975e\u6492\u624b\u653e\u4eba\u3001\u56de\u638c\u76f8\u683c\u4e0d\u53ef\u3002 \u3000\u3000\u80e1\u6590\u5f85\u4ed6\u62f3\u5934\u6253\u5230\u80f8\u53e3\uff0c\u7adf\u662f\u4e0d\u95ea\u4e0d\u6321\uff0c\u7a81\u7136\u95f4\u80f8\u90e8\u5411\u5185\u4e00\u7f29\uff0c\u5c06\u8fd9\u4e00\u62f3\u5316\u89e3\u4e8e\u65e0\u5f62\u3002\u8d5b\u603b\u7ba1\u4e07\u6599\u4e0d\u5230\u4ed6\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5185\u529f\u7adf\u5982\u6b64\u7cbe\u6e5b\uff0c\u5fc3\u5934\u4e00\u60ca\uff0c\u9632\u4ed6\u8fd0\u52b2\u53cd\u51fb\uff0c\u6025\u5fd9\u5411\u540e\u8dc3\u5f00\u3002 \u3000\u3000\u4f17\u4eba\u9f50\u58f0\u53eb\u9053\uff1a\u201c\u7b2c\u4e00\u62db\uff01\u201d\u5176\u5b9e\u8fd9\u4e00\u62db\u662f\u8d5b\u603b\u7ba1\u51fa\u624b\uff0c\u80e1\u6590\u5e76\u672a\u8fd8\u51fb\uff0c\u4f46\u4f17\u4eba\u6709\u610f\u504f\u8892\uff0c\u7adf\u7136\u4e5f\u7b97\u662f\u4e00\u62db\u3002 \u3000\u3000\u80e1\u6590\u5fae\u5fae\u4e00\u7b11\uff0c\u5ffd\u5730\u54b3\u55fd\u4e00\u58f0\uff0c\u4e00\u53e3\u553e\u6db2\u6fc0\u98de\u800c\u51fa\uff0c\u731b\u5f80\u8d5b\u603b\u7ba1\u8138\u4e0a\u5410\u53bb\uff0c\u540c\u65f6\u53cc\u8db3\u201c\u9e33\u9e2f\u8fde\u73af\u201d\uff0c\u5411\u524d\u8e22\u51fa\u3002 \u3000\u3000\u8d5b\u603b\u7ba1\u5403\u4e86\u4e00\u60ca\uff0c\u8981\u8eb2\u5f00\u8fd9\u4e00\u53e3\u553e\u6db2\uff0c\u4e0d\u662f\u4e0a\u8dc3\u4fbf\u662f\u4f4e\u5934\u7f29\u8eab\uff0c\u5018\u82e5\u4e0a\u8dc3\uff0c\u5c0f\u8179\u52bf\u975e\u7ed9\u654c\u4eba\u5de6\u8db3\u8e22\u4e2d\u4e0d\u53ef\uff0c\u4f46\u5982\u7f29\u8eab\uff0c\u5374\u662f\u5c06\u4e0b\u989a\u51d1\u5411\u654c\u4eba\u53f3\u8db3\u53bb\u5403\u4ed6\u4e00\u811a\uff0c\u8fd9\u5f53\u53e3\u4e0a\u4e0b\u4e24\u96be\uff0c\u53ea\u5f97\u6a2a\u638c\u5f53\u80f8\uff0c\u62a4\u4f4f\u95e8\u6237\uff0c\u90a3\u53e3\u553e\u6db2\u5657\u7684\u4e00\u58f0\uff0c\u6b63\u4e2d\u53cc\u7709\u4e4b\u95f4\u3002\u672c\u6765\u8fd9\u6837\u4e00\u53e3\u553e\u6db2\uff0c\u8fde\u4e03\u516b\u5c81\u5c0f\u513f\u4e5f\u80fd\u907f\u5f00\uff0c\u82e6\u4e8e\u654c\u4eba\u4f0f\u4e0b\u51f6\u72e0\u540e\u7740\uff0c\u4ee4\u4ed6\u4e0d\u5f97\u4e0d\u773c\u7741\u7741\u7684\u633a\u8eab\u9886\u53d7\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u4ed6\u8138\u4e0a\u88ab\u553e\uff0c\u4e3a\u4e86\u9632\u5907\u654c\u4eba\u7a81\u51fb\uff0c\u7adf\u662f\u4e0d\u6562\u4f38\u624b\u53bb\u64e6\uff0c\u5982\u6b64\u72fc\u72c8\uff0c\u90a3\u201c\u7b2c\u4e8c\u62db\u201d\u8fd9\u4e00\u58f0\u53eb\uff0c\u5c31\u8fdc\u6ca1\u9996\u6b21\u54cd\u4eae\u3002 \u3000\u3000\u8d5b\u603b\u7ba1\u5fc3\u9053\uff1a\u201c\u6211\u7eb5\u7136\u53d7\u8fb1\uff0c\u53ea\u8981\u5b88\u7d27\u95e8\u6237\uff0c\u518d\u63a5\u4ed6\u4e00\u62db\u53c8\u6709\u4f55\u96be\uff0c\u5230\u90a3\u65f6\u4e14\u77a7\u4ed6\u6709\u4f55\u8bdd\u8bf4\uff1f\u201d\u5927\u58f0\u559d\u9053\uff1a\u201c\u8fd8\u5269\u4e0b\u4e00\u62db\u3002\u4e0a\u5427\uff01\u201d \u3000\u3000\u80e1\u6590\u5fae\u5fae\u4e00\u7b11\uff0c\u8de8\u4e0a\u4e00\u6b65\uff0c\u7a81\u7136\u63d0\u8d77\u675c\u5e0c\u5b5f\u4e0e\u7384\u51a5\u5b50\uff0c\u8fce\u9762\u5411\u4ed6\u6253\u53bb\u3002\u8d5b\u603b\u7ba1\u65e9\u6599\u4ed6\u8981\u51fa\u6b64\u62db\uff0c\u5fc3\u4e0b\u8ba1\u7b97\u65e9\u5b9a\uff1a\u201c\u5e38\u8a00\u9053\u65e0\u6bd2\u4e0d\u4e08\u592b\uff0c\u5f53\u6b64\u5371\u6025\u4e4b\u9645\uff0c\u975e\u8981\u4f24\u4e86\u670b\u53cb\u4e0d\u53ef\uff0c\u90a3\u4e5f\u53eb\u505a\u65e0\u6cd5\u3002\u201d\u773c\u89c1\u4e24\u4eba\u8eab\u5b50\u6a2a\u626b\u800c\u6765\uff0c\u7acb\u5373\u53cc\u81c2\u4e00\u632f\uff0c\u731b\u6325\u51fa\u53bb\u3002 \u3000\u3000\u80e1\u6590\u53cc\u624b\u6293\u7740\u4e24\u4eba\u8981\u7a74\uff0c\u5f85\u4e24\u4eba\u8eab\u5b50\u548c\u8d5b\u603b\u7ba1\u5c06\u89e6\u672a\u89e6\u4e4b\u9645\uff0c\u5ffd\u5730\u677e\u624b\uff0c\u968f\u5373\u6293\u4f4f\u4e24\u4eba\u975e\u5f53\u7a74\u9053\u5904\u7684\u808c\u8089\u3002 \u3000\u3000\u675c\u5e0c\u5b5f\u4e0e\u7384\u51a5\u5b50\u88ab\u4ed6\u6293\u4f4f\u4e86\u5728\u7a7a\u4e2d\u4e71\u6325\uff0c\u6d51\u6d51\u5669\u5669\uff0c\u65e9\u4e0d\u77e5\u8eab\u5728\u4f55\u5904\uff0c\u7a81\u89c9\u7a74\u9053\u677e\u5f1b\uff0c\u624b\u8db3\u80fd\u52a8\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u56db\u624b\u9f50\u65bd\uff0c\u6253\u4e86\u51fa\u53bb\u3002\u4ed6\u4e8c\u4eba\u539f\u610f\u662f\u8981\u6323\u8131\u654c\u4eba\u7684\u638c\u63e1\uff0c\u662f\u4ee5\u51fa\u624b\u90fd\u662f\u5404\u81ea\u7684\u751f\u5e73\u7edd\u62db\uff0c\u51b3\u6b7b\u4e00\u62da\uff0c\u72e0\u8fa3\u65e0\u6bd4\u3002\u4f46\u542c\u8d5b\u603b\u7ba1\u4e00\u58f0\u5927\u543c\uff0c\u592a\u9633\u7a74\u3001\u80f8\u53e3\u3001\u5c0f\u8179\u3001\u80c1\u4e0b\u56db\u5904\u540c\u65f6\u4e2d\u62db\uff0c\u518d\u4e5f\u7ad9\u7acb\u4e0d\u4f4f\uff0c\u53cc\u819d\u4e00\u8f6f\uff0c\u5750\u5012\u5730\u4e0b\u3002\u80e1\u6590\u53cc\u624b\u4e00\u653e\u4e00\u6293\uff0c\u53c8\u5df2\u62ff\u4f4f\u4e86\u675c\u7384\u4e8c\u4eba\u7684\u8981\u7a74\uff0c\u53eb\u9053\uff1a\u201c\u7b2c\u4e09\u62db\uff01\u201d \u3000\u3000\u4ed6\u4e00\u8a00\u51fa\u53e3\uff0c\u53cc\u624b\u52a0\u52b2\uff0c\u675c\u7384\u4e8c\u4eba\u54fc\u4e5f\u6ca1\u54fc\u4e00\u58f0\uff0c\u90fd\u5df2\u6655\u4e86\u8fc7\u53bb\u3002\u8fd9\u4e00\u4e0b\u91cd\u624b\u62ff\u7a74\uff0c\u529b\u900f\u7ecf\u8109\uff0c\u7eb5\u6709\u9ad8\u624b\u89e3\u6551\uff0c\u4e5f\u975e\u5341\u5929\u534a\u6708\u4e4b\u5185\u6240\u80fd\u6cbb\u6108\u3002\u4ed6\u8ddf\u7740\u63d0\u8d77\u4e8c\u4eba\uff0c\u987a\u624b\u5f80\u8eab\u524d\u53e6\u5916\u4e8c\u4eba\u63b7\u53bb\u3002\u90a3\u4e8c\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u6015\u675c\u7384\u4e8c\u4eba\u53c8\u5982\u5bf9\u4ed8\u8d5b\u603b\u7ba1\u90a3\u4e48\u5bf9\u4ed8\u81ea\u5df1\uff0c\u6025\u5fd9\u4e0a\u8dc3\u95ea\u907f\u3002\u80e1\u6590\u4e00\u7eb5\u800c\u524d\uff0c\u4e58\u4e8c\u4eba\u8eab\u5728\u534a\u7a7a\u3001\u5c1a\u672a\u843d\u4e0b\u4e4b\u9645\uff0c\u4e00\u624b\u4e00\u4e2a\uff0c\u53c8\u5df2\u6293\u4f4f\uff0c\u8fd9\u624d\u8f6c\u8fc7\u8eab\u6765\uff0c\u5411\u8d5b\u603b\u7ba1\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u8d5b\u603b\u7ba1\u59d4\u987f\u5728\u5730\uff0c\u767b\u89c9\u96c4\u5fc3\u5c3d\u4e27\uff0c\u4e07\u5ff5\u4ff1\u7070\uff0c\u5583\u5583\u7684\u9053\uff1a\u201c\u4f60\u8bf4\u600e\u4e48\u5c31\u600e\u4e48\u7740\uff0c\u53c8\u95ee\u6211\u600e\u5730\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u5feb\u653e\u4e86\u82d7\u5927\u4fa0\u3002\u201d \u3000\u3000\u8d5b\u603b\u7ba1\u5411\u4e24\u540d\u4f8d\u536b\u6446\u4e86\u6446\u624b\u3002\u90a3\u4e24\u4eba\u8fc7\u53bb\u89e3\u5f00\u4e86\u82d7\u4eba\u51e4\u7684\u9563\u94d0\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u8eab\u4e0a\u7684\u7a74\u9053\u662f\u8d5b\u603b\u7ba1\u6240\u70b9\uff0c\u90a3\u4e24\u540d\u4f8d\u536b\u4e0d\u4f1a\u89e3\u7a74\u3002 \u3000\u3000\u80e1\u6590\u6b63\u5f85\u4f38\u624b\u89e3\u6551\uff0c\u54ea\u77e5\u82d7\u4eba\u51e4\u6697\u4e2d\u8fd0\u6c14\uff0c\u6b63\u5728\u81ea\u884c\u901a\u89e3\uff0c\u624b\u811a\u4e0a\u9563\u94d0\u4e00\u677e\uff0c\u4ed6\u6df1\u6df1\u5438\u4e00\u53e3\u6c14\uff0c\u5c0f\u8179\u4e00\u6536\uff0c\u7adf\u81ea\u5c06\u7a74\u9053\u89e3\u4e86\uff0c\u5de6\u8db3\u8d77\u5904\uff0c\u5df2\u5c06\u7075\u6e05\u5c45\u58eb\u8e22\u4e86\u51fa\u53bb\uff0c\u540c\u65f6\u4e00\u62f3\u9012\u51fa\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u5c06\u53e6\u4e00\u4eba\u6253\u5f97\u76f4\u63bc\u800c\u51fa\u3002 \u3000\u3000\u8303\u5e2e\u4e3b\u88ab\u8d5b\u603b\u7ba1\u649e\u51fa\u677f\u58c1\uff0c\u9694\u4e86\u534a\u664c\uff0c\u65b9\u80fd\u7ad9\u8d77\uff0c\u6b63\u4ece\u677f\u58c1\u7834\u6d1e\u4e2d\u8de8\u8fdb\u623f\u6765\uff0c\u4e0d\u6599\u82d7\u4eba\u51e4\u6253\u51fa\u7684\u90a3\u4eba\u6b63\u597d\u649e\u5728\u4ed6\u7684\u8eab\u4e0a\u3002\u8fd9\u4e00\u649e\u529b\u9053\u5947\u5927\uff0c\u4e24\u4eba\u4f53\u5185\u6c14\u8840\u7ffb\u6d8c\uff0c\u660f\u660f\u6c89\u6c89\uff0c\u96be\u5206\u53cb\u654c\uff0c\u7acb\u5373\u5404\u51fa\u7edd\u62db\uff0c\u4e92\u76f8\u7f20\u6253\u4e0d\u4f11\u3002 \u3000\u3000\u7075\u6e05\u5c45\u58eb\u867d\u88ab\u82d7\u4eba\u51e4\u4e00\u811a\u8e22\u51fa\uff0c\u4f46\u4ed6\u7a76\u662f\u6606\u4ed1\u6d3e\u7684\u540d\u5bbf\uff0c\u6b66\u529f\u6709\u72ec\u5230\u9020\u8be3\uff0c\u8eab\u5b50\u98de\u5728\u534a\u7a7a\uff0c\u8170\u95f4\u4e00\u626d\uff0c\u5df2\u5934\u4e0a\u811a\u4e0b\uff0c\u6362\u8fc7\u4f4d\u6765\uff0c\u817e\u7684\u4e00\u58f0\uff0c\u8dcc\u5750\u5728\u5e8a\u6cbf\u4e4b\u4e0a\u3002 \u3000\u3000\u80e1\u6590\u5927\u5403\u4e00\u60ca\uff0c\u5f85\u8981\u62a2\u4e0a\u524d\u53bb\u5c06\u4ed6\u63a8\u5f00\uff0c\u5ffd\u89c9\u4e00\u80a1\u52b2\u98ce\u6251\u80f8\u800c\u81f3\uff0c\u540c\u65f6\u53f3\u4fa7\u53c8\u6709\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u539f\u6765\u848b\u8001\u62f3\u5e08\u4e0e\u53e6\u4e00\u540d\u4f8d\u536b\u540c\u65f6\u653b\u5230\u3002\u4f8d\u536b\u7684\u4e00\u5200\u8fd8\u6613\u95ea\u907f\uff0c\u848b\u8001\u62f3\u5e08\u8fd9\u4e00\u7ecd\u201c\u6597\u67c4\u4e1c\u6307\u201d\u5374\u662f\u4e0d\u6613\u5316\u89e3\uff0c\u53ea\u5f97\u53cc\u8db3\u7ad9\u7a33\uff0c\u8fd0\u52b2\u63a5\u4e86\u4ed6\u4e00\u62db\u3002 \u3000\u3000\u4f46\u90a3\u65e0\u6781\u62f3\u7ef5\u82e5\u6c5f\u6cb3\uff0c\u4e00\u62db\u752b\u8fc7\uff0c\u6b21\u62db\u7ee7\u81f3\uff0c\u4e00\u65f6\u7adf\u6559\u4ed6\u7f13\u4e0d\u51fa\u624b\u8db3\u3002 \u3000\u3000\u7075\u6e05\u5c45\u58eb\u8dcc\u5728\u5e8a\u8fb9\uff0c\u55e4\u7684\u4e00\u54cd\uff0c\u5c06\u534a\u8fb9\u7f57\u5e10\u62c9\u4e86\u4e0b\u6765\uff0c\u8dc3\u8d77\u8eab\u65f6\uff0c\u7adf\u5c06\u82d7\u82e5\u5170\u8eab\u4e0a\u76d6\u7740\u7684\u68c9\u88ab\u63a0\u5728\u4e00\u65c1\uff0c\u9732\u51fa\u4e86\u4e0a\u8eab\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u6b63\u6597\u5f97\u5174\u8d77\uff0c\u5ffd\u89c1\u5e8a\u4e0a\u8eba\u7740\u4e00\u4e2a\u5c11\u5973\uff0c\u4eb5\u8863\u4e0d\u8db3\u4ee5\u853d\u4f53\uff0c\u53cc\u988a\u6655\u7ea2\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\uff0c\u6b63\u662f\u81ea\u5df1\u7684\u72ec\u751f\u7231\u5973\uff0c\u8fd9\u4e00\u4e0b\u4ed6\u5982\u4f55\u4e0d\u614c\uff0c\u53eb\u9053\uff1a\u201c\u5170\u513f\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u82d7\u82e5\u5170\u5f00\u4e0d\u5f97\u53e3\uff0c\u53ea\u662f\u4e3e\u76ee\u671b\u7740\u7236\u4eb2\uff0c\u53c8\u7f9e\u53c8\u6025\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u53cc\u81c2\u4e00\u632f\uff0c\u4ece\u56db\u540d\u654c\u4eba\u4e4b\u95f4\u786c\u6324\u4e86\u8fc7\u53bb\uff0c\u4e00\u62c9\u5973\u513f\uff0c\u4f46\u89c9\u5979\u8eab\u5b50\u8f6f\u7ef5\u7ef5\u7684\u52a8\u5f39\u4e0d\u5f97\uff0c\u7adf\u662f\u88ab\u9ad8\u624b\u70b9\u4e2d\u4e86\u7a74\u9053\u3002 \u3000\u3000\u4ed6\u4eb2\u773c\u89c1\u80e1\u6590\u4ece\u5e8a\u4e0a\u88ab\u4e2d\u8dc3\u51fa\uff0c\u539f\u6765\u7adf\u5728\u6b3a\u4fae\u81ea\u5df1\u7231\u5973\u3002\u4ed6\u6c14\u5f97\u51e0\u6b32\u6655\u53bb\uff0c\u4e5f\u4e0d\u53ca\u89e3\u5f00\u5973\u513f\u7a74\u9053\uff0c\u53ea\u9a82\u4e86\u4e00\u58f0\uff1a\u201c\u5978\u8d3c\uff01\u201d \u3000\u3000\u53cc\u81c2\u6325\u51fa\uff0c\u75be\u5411\u80e1\u6590\u6253\u53bb\u3002 \u3000\u3000\u6b64\u65f6\u4ed6\u773c\u4e2d\u5982\u8981\u55b7\u51fa\u706b\u6765\uff0c\u8fd9\u53cc\u62f3\u51fb\u51fa\uff0c\u5b9e\u662f\u6bd5\u751f\u529f\u529b\u4e4b\u6240\u805a\uff0c\u52bf\u9053\u72b9\u5982\u6392\u5c71\u5012\u6d77\u4e00\u822c\u3002\u80e1\u6590\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u9002\u624d\u6b63\u4e0e\u848b\u8001\u62f3\u5e08\u51dd\u795e\u62c6\u62db\uff0c\u5fc3\u65e0\u65c1\u9e5c\uff0c\u6ca1\u89c1\u5230\u82d7\u4eba\u51e4\u5982\u4f55\u53bb\u62c9\u82d7\u82e5\u5170\uff0c\u5fc3\u4e2d\u53ea\u89c9\u5947\u602a\uff0c\u660e\u660e\u81ea\u5df1\u6551\u4e86\u4ed6\uff0c\u4f55\u4ee5\u4ed6\u53cd\u5411\u81ea\u5df1\u52a8\u6b66\uff0c\u4f46\u89c1\u6765\u52bf\u5389\u5bb3\uff0c\u4e0d\u53ca\u559d\u95ee\uff0c\u6025\u5fd9\u5411\u5de6\u95ea\u8ba9\uff0c\u4f46\u542c\u7830\u7684\u4e00\u58f0\u5927\u54cd\uff0c\u82d7\u4eba\u51e4\u53cc\u62f3\u5df2\u51fb\u4e2d\u4e00\u540d\u62f3\u5e08\u80cc\u5fc3\u3002 \u3000\u3000\u8fd9\u4eba\u6240\u7ec3\u4e0b\u76d8\u529f\u592b\u76f4\u5982\u78d0\u77f3\u4e4b\u7a33\uff0c\u4e00\u4e2a\u9a6c\u6b65\u4e00\u624e\uff0c\u7eb5\u662f\u51e0\u6761\u58ee\u6c49\u4e00\u9f50\u51fa\u529b\uff0c\u4e5f\u62d6\u4ed6\u4e0d\u52a8\u3002\u82d7\u4eba\u51e4\u53cc\u62f3\u51fb\u5230\u4e4b\u65f6\uff0c\u4ed6\u6b63\u80cc\u5411\u80e1\u6590\uff0c\u4e0d\u610f\u4e00\u4e2a\u6253\u5f97\u6025\uff0c\u4e00\u4e2a\u907f\u5f97\u5feb\uff0c\u8fd9\u53cc\u62f3\u5934\u6b63\u597d\u51fb\u4e2d\u4ed6\u7684\u80cc\u5fc3\u3002\u82e5\u662f\u6362\u4f5c\u65c1\u4eba\uff0c\u4e2d\u4e86\u8fd9\u4e24\u62f3\u52bf\u5fc5\u6251\u5730\u6454\u5012\uff0c\u4f46\u8fd9\u62f3\u5e08\u4e0b\u76d8\u529f\u592b\u5b9e\u5728\u592a\u597d\uff0c\u4ee5\u786c\u78b0\u786c\uff0c\u5580\u7684\u4e00\u54cd\uff0c\u810a\u9aa8\u4ece\u4e2d\u65ad\u7edd\uff0c\u4e00\u4e2a\u8eab\u5b50\u8f6f\u8f6f\u7684\u6298\u4e3a\u4e24\u622a\uff0c\u53cc\u817f\u4ecd\u662f\u7262\u9489\u5728\u5730\uff0c\u4e0a\u8eab\u5374\u5f2f\u4e86\u4e0b\u53bb\uff0c\u989d\u89d2\u78b0\u5730\uff0c\u518d\u4e5f\u633a\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u82d7\u4eba\u51e4\u5982\u6b64\u5a01\u731b\uff0c\u53d1\u4e00\u58f0\u558a\uff0c\u56db\u4e0b\u6563\u5f00\u3002\u82d7\u4eba\u51e4\u5de6\u817f\u6a2a\u626b\uff0c\u53c8\u5411\u80e1\u6590\u8e22\u5230\u3002 \u3000\u3000\u80e1\u6590\u89c1\u82d7\u82e5\u5170\u5728\u70db\u5149\u4e0b\u8d64\u8eab\u9732\u4f53\uff0c\u51e0\u4e2a\u5b58\u5fc3\u4e0d\u6b63\u4e4b\u5f92\u5df2\u5728\u5411\u5979\u659c\u7768\u76f4\u671b\uff0c\u5fc3\u60f3\u5148\u4fdd\u5979\u6d01\u767d\u4e4b\u8eaf\u8981\u7d27\uff0c\u987a\u624b\u62c9\u8fc7\u4e00\u540d\u4f8d\u536b\uff0c\u5728\u81ea\u5df1\u4e0e\u82d7\u4eba\u51e4\u4e4b\u95f4\u4e00\u6321\uff0c\u8eab\u5f62\u4e00\u659c\uff0c\u7a9c\u5230\u5e8a\u8fb9\uff0c\u626f\u8fc7\u88ab\u5b50\u88f9\u5728\u82d7\u82e5\u5170\u8eab\u4e0a\u3002\u8fd9\u51e0\u4e0b\u8d77\u843d\u5feb\u6377\u65e0\u4f26\uff0c\u4f17\u4eba\u5c1a\u672a\u770b\u6e05\uff0c\u4ed6\u5df2\u62b1\u8d77\u82d7\u82e5\u5170\u4ece\u677f\u58c1\u7f3a\u53e3\u94bb\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u4e00\u811a\u5c06\u90a3\u540d\u4f8d\u536b\u8e22\u5f97\u98de\u5411\u5c4b\u9876\uff0c\u89c1\u80e1\u6590\u63b3\u4e86\u5973\u513f\u800c\u8d70\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u5927\u53eb\uff1a\u201c\u5978\u8d3c\uff0c\u5feb\u653e\u4e0b\u6211\u513f\uff01\u201d\u7eb5\u8eab\u6b32\u8ffd\uff0c\u4f46\u5ba4\u5c0f\u4eba\u6324\uff0c\u88ab\u51e0\u540d\u654c\u4eba\u7f20\u4f4f\u4e86\u624b\u8db3\uff0c\u4efb\u4ed6\u62f3\u5288\u8db3\u8e22\uff0c\u4e00\u65f6\u7adf\u662f\u96be\u4ee5\u8131\u8eab\u3002 \u3000"}),showOnHomepage:!0},{name:"\u4e00\u25cb",content:(0,a.jsx)(a.Fragment,{children:"\u80e1\u6590\u89c1\u5230\u82d7\u4eba\u51e4\u53d1\u6012\u65f6\u795e\u5a01\u51db\u51db\uff0c\u5fc3\u4e2d\u4e5f\u81ea\u9a87\u7136\uff0c\u62b1\u7740\u82d7\u82e5\u5170\u4e0d\u6562\u505c\u7559\uff0c\u62a2\u5230\u5d16\u8fb9\uff0c\u4e00\u624b\u62c9\u7d22\uff0c\u6e9c\u4e0b\u5cf0\u53bb\u3002\u4ed6\u77e5\u9644\u8fd1\u6709\u4e2a\u5c71\u6d1e\u4eba\u8ff9\u7f55\u81f3\uff0c\u5f53\u4e0b\u5c55\u5f00\u8f7b\u8eab\u529f\u592b\uff0c\u76f4\u5954\u800c\u53bb\uff0c\u624b\u4e2d\u867d\u62b1\u4e86\u4eba\uff0c\u4f46\u82d7\u82e5\u5170\u8eab\u5b50\u751a\u8f7b\uff0c\u5168\u6ca1\u51cf\u4e86\u4ed6\u5954\u8dd1\u4e4b\u901f\u3002 \u3000\u3000\u4e0d\u5230\u4e00\u76cf\u8336\u529f\u592b\uff0c\u5df2\u62b1\u7740\u82d7\u82e5\u5170\u8fdb\u4e86\u5c71\u6d1e\uff0c\u5c06\u68c9\u88ab\u7d27\u7d27\u88f9\u4f4f\u5979\u8eab\u5b50\uff0c\u8ba9\u5979\u9760\u5728\u6d1e\u58c1\uff0c\u5fc3\u4e2d\u8e0c\u8e87\uff1a\u201c\u82e5\u8981\u89e3\u5979\u7a74\u9053\uff0c\u975e\u78b0\u5230\u8eab\u5b50\u4e0d\u53ef\uff0c\u5982\u4e0d\u89e3\u6551\uff0c\u65f6\u95f4\u4e00\u957f\uff0c\u5979\u4e0d\u4f1a\u5185\u529f\uff0c\u53ea\u6015\u8eab\u5b50\u6709\u635f\u3002\u201d\u5b9e\u5728\u597d\u751f\u96be\u4ee5\u59d4\u51b3\uff0c\u5f53\u4e0b\u53d6\u706b\u6298\u70b9\u71c3\u4e86\u4e00\u6839\u67af\u679d\u3002 \u3000\u3000\u706b\u5149\u4e0b\u4f46\u89c1\u82d7\u82e5\u5170\u7f8e\u76ee\u6d41\u6ce2\uff0c\u4fcf\u8138\u751f\u6655\uff0c\u4fbf\u9053\uff1a\u201c\u82d7\u59d1\u5a18\uff0c\u5728\u4e0b\u7edd\u65e0\u8f7b\u8584\u5192\u6e0e\u4e4b\u610f\uff0c\u4f46\u8981\u89e3\u5f00\u59d1\u5a18\u7a74\u9053\uff0c\u96be\u4ee5\u4e0d\u78b0\u59d1\u5a18\u8d35\u4f53\uff0c\u6b64\u4e8b\u8be5\u5f53\u5982\u4f55\uff1f\u201d\u82d7\u82e5\u5170\u867d\u4e0d\u80fd\u70b9\u5934\u793a\u610f\uff0c\u4f46\u76ee\u5149\u67d4\u548c\uff0c\u4f3c\u7f9e\u4f3c\u8c22\uff0c\u6b8a\u65e0\u534a\u70b9\u6012\u8272\uff0c\u80e1\u6590\u5927\u559c\uff0c\u5148\u5439\u7184\u67f4\u706b\uff0c\u4f38\u624b\u5230\u887e\u4e2d\u5728\u5979\u51e0\u5904\u7a74\u9053\u4e0a\u8f7b\u8f7b\u6309\u6469\uff0c\u66ff\u5979\u901a\u4e86\u7ecf\u8109\u3002 \u3000\u3000\u82d7\u82e5\u5170\u624b\u8db3\u6e10\u80fd\u6d3b\u52a8\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u884c\u5566\uff0c\u591a\u8c22\u60a8\uff01\u201d\u80e1\u6590\u6025\u5fd9\u7f29\u624b\uff0c\u5f85\u8981\u8bf4\u8bdd\uff0c\u5374\u4e0d\u77e5\u8bf4\u4ec0\u4e48\u597d\uff0c\u8fc7\u4e86\u826f\u4e45\uff0c\u624d\u9053\uff1a\u201c\u9002\u624d\u5192\u72af\uff0c\u5b9e\u662f\u65e0\u610f\u4e4b\u8fc7\uff0c\u6b64\u5fc3\u5149\u660e\u78ca\u843d\uff0c\u5929\u65e5\u53ef\u9274\uff0c\u52a1\u8bf7\u59d1\u5a18\u6055\u7f6a\u3002\u201d\u82d7\u82e5\u5170\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u4e24\u4eba\u5728\u9ed1\u6697\u4e4b\u4e2d\uff0c\u76f8\u5bf9\u4e0d\u8bed\u3002\u5c71\u6d1e\u5916\u867d\u662f\u51b0\u5929\u96ea\u5730\uff0c\u4f46\u4e24\u4eba\u5fc3\u5934\u6e29\u6696\uff0c\u5c71\u6d1e\u4e2d\u5374\u5982\u6625\u98ce\u548c\u7166\uff0c\u6625\u65e5\u878d\u878d\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u82d7\u82e5\u5170\u9053\uff1a\u201c\u4e0d\u77e5\u6211\u7239\u7239\u73b0\u4e0b\u600e\u6837\u4e86\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4ee4\u5c0a\u82f1\u96c4\u65e0\u654c\uff0c\u8fd9\u4e9b\u4eba\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u3002\u4f60\u653e\u5fc3\u597d\u5566\u3002\u201d\u82d7\u82e5\u5170\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u601c\u7684\u7239\u7239\uff0c\u4ed6\u4ee5\u4e3a\u4f60\u2026\u2026\u4f60\u5bf9\u6211\u4e0d\u597d\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u4e5f\u96be\u602a\uff0c\u9002\u624d\u60c5\u52bf\u786e\u751a\u5c34\u5c2c\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u8138\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u6211\u7239\u7239\u56e0\u6709\u4f24\u5fc3\u4e4b\u4e8b\uff0c\u662f\u4ee5\u611f\u89e6\u7279\u6df1\uff0c\u8bf7\u80e1\u7237\u4e0d\u8981\u89c1\u602a\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d\u4e00\u95ee\u51fa\u53e3\uff0c\u7acb\u89c9\u5931\u8a00\uff0c\u60f3\u8981\u7528\u8a00\u8bed\u5c94\u5f00\uff0c\u5374\u4e00\u65f6\u4e0d\u77e5\u8bf4\u4ec0\u4e48\u597d\u3002\u4ed6\u53f7\u79f0\uff0c\u5e73\u65f6\u806a\u660e\u4f36\u4fd0\uff0c\u673a\u53d8\u767e\u51fa\uff0c\u4f46\u4eca\u65e5\u5728\u8fd9\u4e2a\u6e29\u96c5\u7684\u5c11\u5973\u4e4b\u524d\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u7adf\u4f3c\u53d8\u6210\u4e86\u53e6\u4e00\u4e2a\u4eba\uff0c\u663e\u5f97\u5341\u5206\u62d9\u8bb7\u3002 \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u6b64\u4e8b\u8bf4\u6765\u6709\u6127\uff0c\u4f46\u6211\u4e5f\u4e0d\u5fc5\u7792\u4f60\uff0c\u90a3\u662f\u6211\u5988\u7684\u4e8b\u3002\u201d\u80e1\u6590\u201c\u554a\u201d\u4e86\u4e00\u58f0\u3002\u82d7\u82e5\u5170\u9053\uff1a\u201c\u6211\u5988\u505a\u8fc7\u4e00\u4ef6\u9519\u4e8b\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4eba\u5b70\u65e0\u8fc7\uff1f\u90a3\u4e5f\u4e0d\u5fc5\u653e\u5728\u5fc3\u4e0a\u3002\u201d\u82d7\u82e5\u5170\u7f13\u7f13\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u662f\u4e00\u4ef6\u5927\u9519\u4e8b\u3002\u4e00\u4e2a\u5973\u5b50\u4e00\u751f\u4e0d\u80fd\u9519\u8fd9\u4e48\u4e00\u6b21\u3002\u6211\u5988\u5988\u6559\u8fd9\u4ef6\u4e8b\u6bc1\u4e86\uff0c\u8fde\u6211\u7239\u7239\u4e5f\u9669\u9669\u7ed9\u8fd9\u4e8b\u6bc1\u4e86\u3002\u201d \u3000\u3000\u80e1\u6590\u9ed8\u7136\uff0c\u5fc3\u4e0b\u5df2\u6599\u5230\u4e86\u51e0\u5206\u3002\u82d7\u82e5\u5170\u9053\uff1a\u201c\u6211\u7239\u662f\u6c5f\u6e56\u8c6a\u6770\u3002\u6211\u5988\u5374\u662f\u51fa\u8eab\u5b98\u5bb6\u7684\u4e00\u4e2a\u5343\u91d1\u5c0f\u59d0\u3002\u6709\u4e00\u6b21\u6211\u7239\u65e0\u610f\u4e4b\u4e2d\u6551\u4e86\u6211\u5988\u7684\u6027\u547d\uff0c\u4ed6\u4eec\u624d\u7ed3\u4e86\u4eb2\u3002\u4e24\u4eba\u672c\u6765\u4e0d\u5927\u76f8\u914d\uff0c\u90a3\u4e5f\u7f62\u4e86\u3002\u53ef\u662f\u6211\u7239\u6709\u4e00\u4ef6\u4e8b\u5927\u5927\u4e0d\u5bf9\uff0c\u4ed6\u5e38\u5728\u6211\u5988\u9762\u524d\uff0c\u5938\u5956\u4f60\u5988\u7684\u597d\u5904\u3002\u201d \u3000\u3000\u80e1\u6590\u5947\u9053\uff1a\u201c\u6211\u7684\u6bcd\u4eb2\uff1f\u201d\u82d7\u82e5\u5170\u9053\uff1a\u201c\u662f\u554a\u3002\u6211\u7239\u8ddf\u4ee4\u5c0a\u6bd4\u6b66\u4e4b\u65f6\uff0c\u4f60\u5988\u5988\u82f1\u98ce\u98d2\u723d\uff0c\u6bd4\u7537\u5b50\u6c49\u8fd8\u6709\u6c14\u6982\u3002\u6211\u7239\u5e73\u65f6\u95f2\u8c08\uff0c\u5e38\u81ea\u7fa1\u6155\u4ee4\u5c0a\uff0c\u8bf4\u9053\uff1a\u2018\u80e1\u5927\u4fa0\u5f97\u6b64\u4f73\u5076\uff0c\u6d3b\u4e00\u65e5\u80dc\u8fc7\u65c1\u4eba\u767e\u5e74\u3002\u2019\u6211\u5988\u542c\u4e86\u867d\u4e0d\u8a00\u8bed\uff0c\u5fc3\u4e2d\u5374\u751a\u4e0d\u5feb\u3002\u540e\u6765\u5929\u9f99\u95e8\u7684\u7530\u5f52\u519c\u5230\u6211\u5bb6\u6765\u4f5c\u5ba2\u3002\u4ed6\u76f8\u8c8c\u82f1\u4fca\uff0c\u8c08\u5410\u98ce\u96c5\uff0c\u53c8\u80fd\u4f4e\u58f0\u4e0b\u6c14\u7684\u8ba8\u4eba\u559c\u6b22\u3002\u6211\u5988\u4e00\u65f6\u7cca\u6d82\uff0c\u7adf\u6487\u4e0b\u4e86\u6211\uff0c\u5077\u5077\u8ddf\u7740\u90a3\u4eba\u8d70\u4e86\u3002\u201d \u3000\u3000\u80e1\u6590\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u96be\u4ee5\u63a5\u53e3\u3002\u82d7\u82e5\u5170\u8bdd\u58f0\u54fd\u54bd\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u65f6\u6211\u8fd8\u53ea\u4e09\u5c81\uff0c\u7239\u62b1\u4e86\u6211\u8fde\u591c\u8ffd\u8d76\uff0c\u4ed6\u4e0d\u5403\u996d\u4e0d\u7761\u89c9\uff0c\u8fde\u8ffd\u4e09\u65e5\u4e09\u591c\uff0c\u7ec8\u4e8e\u8d76\u4e0a\u4e86\u4ed6\u4eec\u3002\u90a3\u7530\u5f52\u519c\u89c1\u5230\u6211\u7239\uff0c\u54ea\u6562\u52a8\u624b\uff1f\u6211\u5988\u5374\u5168\u529b\u62a4\u7740\u4ed6\u3002\u6211\u7239\u89c1\u6211\u5988\u5988\u5bf9\u8fd9\u4eba\u5982\u6b64\u771f\u5fc3\u76f8\u7231\uff0c\u65e0\u53ef\u5948\u4f55\uff0c\u62b1\u4e86\u6211\u8d70\u4e86\uff0c\u56de\u5230\u5bb6\u6765\u751f\u4e86\u4e00\u573a\u5927\u75c5\u3001\u9669\u4e9b\u6b7b\u53bb\u3002 \u3000\u3000\u4ed6\u5bf9\u6211\u8bf4\uff0c\u82e5\u4e0d\u662f\u89c1\u6211\u5b64\u82e6\u4f36\u4ec3\uff0c\u5728\u8fd9\u4e16\u4e0a\u6ca1\u4eba\u7167\u987e\uff0c\u4ed6\u771f\u4e0d\u60f3\u6d3b\u5566\u3002\u4e00\u8fde\u4e09\u5e74\uff0c\u4ed6\u4e0d\u51fa\u5927\u95e8\u4e00\u6b65\uff0c\u6709\u65f6\u53eb\u7740\uff1a\u2018\u5170\u554a\u5170\uff0c\u4f60\u600e\u5730\u5982\u6b64\u7cca\u6d82\uff1f\u2019\u6211\u5988\u5988\u7684\u540d\u5b57\u4e4b\u4e2d\uff0c\u4e5f\u662f\u6709\u4e2a\u2018\u5170\u2019\u5b57\u7684\u3002\u201d \u3000\u3000\u5979\u8bf4\u5230\u6b64\u5904\uff0c\u8138\u4e0a\u4e00\u7ea2\u3002\u8981\u77e5\u5f53\u65f6\u5973\u5b50\u7684\u540d\u5b57\u4e5f\u662f\u79d8\u5bc6\uff0c\u65c1\u4eba\u53ea\u77e5\u5973\u5b50\u59d3\u6c0f\uff0c\u53ea\u6709\u5bf9\u81f3\u4eb2\u81f3\u8fd1\u4e4b\u4eba\u65b9\u80fd\u544a\u77e5\u540d\u5b57\uff0c\u5979\u8fd9\u4e48\u8bf4\uff0c\u7b49\u4e8e\u662f\u5bf9\u80e1\u6590\u8bf4\u81ea\u5df1\u540d\u5b57\u4e2d\u6709\u4e2a\u201c\u5170\u201d\u5b57\u3002 \u3000\u3000\u80e1\u6590\u867d\u89c1\u4e0d\u5230\u5979\u8138\u4e0a\u795e\u8272\uff0c\u4f46\u542c\u5979\u7adf\u628a\u5bb6\u4e2d\u6700\u9690\u79d8\u7684\u53ef\u803b\u79c1\u4e8b\uff0c\u4e5f\u6beb\u4e0d\u8bb3\u8a00\u7684\u544a\u77e5\u4e86\u81ea\u5df1\uff0c\u4e0d\u7981\u5927\u662f\u611f\u6fc0\uff0c\u6700\u540e\u542c\u5979\u63d0\u5230\u5979\u81ea\u5df1\u5c0f\u540d\uff0c\u66f4\u662f\u5982\u996e\u9187\u91aa\uff0c\u9887\u6709\u5fae\u91ba\u8584\u9189\u4e4b\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u82d7\u59d1\u5a18\uff0c\u90a3\u7530\u5f52\u519c\u5b58\u5fc3\u6781\u574f\uff0c\u5bf9\u4f60\u5988\u672a\u5fc5\u6709\u4ec0\u4e48\u771f\u6b63\u7684\u60c5\u610f\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6211\u7239\u4e5f\u662f\u8fd9\u4e48\u8bf4\u3002\u53ea\u662f\u4ed6\u65f6\u5e38\u57cb\u6028\u81ea\u5df1\uff0c\u8bf4\u9053\u82e5\u975e\u4ed6\u5bf9\u6211\u5988\u4e0d\u591f\u6e29\u5b58\u4f53\u8d34\uff0c\u6211\u5988\u4e5f\u4e0d\u81f4\u53d7\u4e86\u65c1\u4eba\u4e4b\u9a97\u3002\u6211\u7239\u53f7\u79f0\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\uff0c\u4f46\u8bf4\u5230\u5f85\u4eba\u5904\u4e16\uff0c\u5374\u4e0d\u53ca\u7530\u5f52\u519c\u4e86\u3002\u90a3\u59d3\u7530\u7684\u6b3a\u9a97\u6211\u5988\uff0c\u5176\u5b9e\u662f\u60f3\u5f97\u6211\u82d7\u5bb6\u5bb6\u4f20\u7684\u4e00\u5f20\u85cf\u5b9d\u4e4b\u56fe\u3002\u53ef\u662f\u4ed6\u867d\u4ee4\u6211\u4e00\u5bb6\u53d7\u82e6\uff0c\u4ee4\u6211\u81ea\u5e7c\u5c31\u6210\u4e86\u4e2a\u65e0\u6bcd\u4e4b\u4eba\uff0c\u5230\u5934\u6765\u5374\u4ecd\u662f\u767d\u8d39\u4e86\u5fc3\u673a\u3002\u6211\u5988\u770b\u7a7f\u4e86\u4ed6\u7684\u7528\u5fc3\uff0c\u4e34\u7ec8\u4e4b\u65f6\uff0c\u4ecd\u5c06\u85cf\u7740\u5730\u56fe\u7684\u51e4\u5934\u73e0\u9497\u8fd8\u7ed9\u4e86\u6211\u7239\u3002\u201d\u4e8e\u662f\u5c06\u5218\u5143\u9e64\u5728\u7530\u5f52\u519c\u5e8a\u5e95\u7684\u6240\u89c1\u6240\u95fb\uff0c\u8bf4\u4e86\u4e00\u904d\uff0c\u6700\u540e\u8bf4\u5230\u90a3\u56fe\u5982\u4f55\u7ed9\u5b9d\u6811\u4ed6\u4eec\u62a2\u53bb\uff0c\u90a3\u4e9b\u4eba\u5982\u4f55\u51ed\u4e86\u95ef\u738b\u519b\u5200\u4e0e\u5730\u56fe\u53bb\u627e\u85cf\u5b9d\u3002 \u3000\u3000\u80e1\u6590\u6068\u6068\u7684\u9053\uff1a\u201c\u8fd9\u59d3\u7530\u7684\u5fc3\u601d\u4e5f\u5fd2\u715e\u6b79\u6bd2\u3002\u4ed6\u754f\u60e7\u4f60\u7239\u7239\uff0c\u53c8\u5f04\u4e0d\u5230\u5730\u56fe\uff0c\u5c31\u60f3\u5047\u624b\u5b98\u5bb6\uff0c\u5c06\u4f60\u7239\u7239\u64d2\u4f4f\uff0c\u597d\u8feb\u4ed6\u4ea4\u51fa\u56fe\u6765\u3002\u54ea\u77e5\u5929\u7f51\u6062\u6062\uff0c\u7ec8\u4e8e\u96be\u9003\u5b7d\u62a5\u3002\u5509\uff0c\u8fd9\u5b9d\u85cf\u4e0d\u77e5\u5bb3\u4e86\u591a\u5c11\u4eba\u3002\u201d \u3000\u3000\u4ed6\u505c\u4e86\u7247\u523b\uff0c\u53c8\u9053\uff1a\u201c\u82d7\u59d1\u5a18\uff0c\u6211\u7239\u548c\u6211\u5988\u5c31\u662f\u56e0\u8fd9\u5b9d\u85cf\u800c\u6210\u4eb2\u7684\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u554a\uff0c\u662f\u4e48\uff1f\u5feb\u8bf4\u7ed9\u6211\u542c\u3002\u201d\u5979\u867d\u77dc\u6301\uff0c\u7a76\u7adf\u5e74\u7eaa\u5e7c\u5c0f\uff0c\u5fc3\u559c\u4e4b\u4e0b\uff0c\u4f38\u624b\u53bb\u63e1\u4f4f\u4e86\u80e1\u6590\u7684\u624b\uff0c\u4f46\u968f\u5373\u89c9\u5f97\u4e0d\u5999\uff0c\u8981\u5f85\u7f29\u56de\uff0c\u80e1\u6590\u5374\u7ffb\u8fc7\u624b\u638c\uff0c\u8f7b\u8f7b\u63e1\u4f4f\u4e86\u5979\u624b\u4e0d\u653e\u3002\u82d7\u82e5\u5170\u8138\u4e0a\u4e00\u7ea2\uff0c\u4e5f\u5c31\u4e0d\u518d\u7f29\u56de\uff0c\u53ea\u89c9\u80e1\u6590\u624b\u4e0a\u70ed\u6c14\uff0c\u76f4\u900f\u8fdb\u81ea\u5df1\u7684\u5fc3\u91cc\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u4f60\u9053\u6211\u5988\u662f\u8c01\uff1f\u5979\u662f\u675c\u5e0c\u5b5f\u675c\u5e84\u4e3b\u7684\u8868\u59b9\u3002\u201d\u82d7\u82e5\u5170\u66f4\u52a0\u60ca\u5947\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u81ea\u5e7c\u8bc6\u5f97\u675c\u4f2f\u4f2f\uff0c\u7239\u7239\u5374\u4ece\u6765\u6ca1\u63d0\u8d77\u8fc7\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u6211\u5728\u7239\u7239\u5988\u5988\u7684\u9057\u4e66\u4e2d\u5f97\u6089\u6b64\u4e8b\uff0c\u60f3\u6765\u4ee4\u5c0a\u672a\u5fc5\u77e5\u9053\u5176\u4e2d\u8be6\u60c5\u3002\u675c\u5e84\u4e3b\u5f97\u5230\u4e00\u4e9b\u7ebf\u7d22\uff0c\u731c\u5f97\u5b9d\u85cf\u5fc5\u5728\u96ea\u5cf0\u9644\u8fd1\uff0c\u662f\u4ee5\u957f\u4f4f\u5cf0\u4e0a\u627e\u5bfb\u3002\u53ea\u662f\u4ed6\u4e00\u6765\u5fc3\u601d\u8fdf\u949d\uff0c\u4e8c\u6765\u673a\u7f18\u4e0d\u5de7\uff0c\u59cb\u7ec8\u53c2\u4e0d\u900f\u85cf\u5b9d\u7684\u6240\u5728\u3002\u6211\u7239\u7239\u6697\u4e2d\u67e5\u8bbf\uff0c\u5374\u53cd\u800c\u5148\u4ed6\u5f97\u77e5\u3002\u4ed6\u8fdb\u4e86\u85cf\u5b9d\u4e4b\u6d1e\uff0c\u89c1\u5230\u7530\u5f52\u519c\u7684\u7236\u4eb2\u4e0e\u4f60\u7956\u7236\u6b7b\u5728\u6d1e\u4e2d\uff0c\u6b63\u60f3\u53d1\u6398\u85cf\u5b9d\uff0c\u54ea\u77e5\u6211\u5988\u8ddf\u7740\u6765\u4e86\u3002 \u3000\u3000\u201c\u6211\u5988\u7684\u672c\u4e8b\u8981\u6bd4\u675c\u5e84\u4e3b\u9ad8\u5f97\u591a\u3002\u6211\u7239\u8fde\u65e5\u5728\u5de6\u8fd1\u51fa\u6ca1\uff0c\u5979\u65e9\u5df2\u770b\u51fa\u4e86\u7aef\u502a\u3002\u5979\u8ddf\u8fdb\u5b9d\u6d1e\uff0c\u548c\u6211\u7239\u52a8\u8d77\u624b\u6765\u3002\u4e24\u4eba\u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\uff0c\u4e92\u76f8\u94a6\u6155\uff0c\u6211\u7239\u5c31\u63d0\u6c42\u4eb2\u4e4b\u8bae\u3002\u6211\u5988\u8bf4\u9053\uff1a\u5979\u81ea\u5e7c\u53d7\u8868\u54e5\u675c\u5e0c\u5b5f\u629a\u517b\uff0c\u82e5\u662f\u8ba9\u6211\u7239\u53d6\u53bb\u85cf\u5b9d\uff0c\u90a3\u662f\u5bf9\u8868\u54e5\u4e0d\u8d77\uff0c\u95ee\u6211\u7239\u8981\u5979\u8fd8\u662f\u8981\u5b9d\u85cf\uff0c\u4e24\u8005\u53ea\u80fd\u5f97\u4e00\u3002 \u3000\u3000\u201c\u6211\u7239\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\u5c31\u662f\u5341\u4e07\u4e2a\u5b9d\u85cf\uff0c\u4e5f\u53ca\u4e0d\u4e0a\u6211\u5988\u3002 \u3000\u3000\u4ed6\u63d0\u7b14\u5199\u4e86\u4e00\u7bc7\u6587\u5b57\uff0c\u8bb0\u8ff0\u6b64\u4e8b\uff0c\u5c01\u5728\u6d1e\u5185\uff0c\u597d\u4ee4\u540e\u4eba\u53d1\u73b0\u5b9d\u85cf\u4e4b\u65f6\uff0c\u77e5\u9053\u4e16\u4e0a\u6700\u5b9d\u8d35\u4e4b\u7269\uff0c\u4e43\u662f\u4e24\u5fc3\u76f8\u60a6\u7684\u771f\u6b63\u60c5\u7231\uff0c\u51b3\u975e\u4ef7\u503c\u8fde\u57ce\u7684\u5b9d\u85cf\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u542c\u5230\u6b64\u5904\uff0c\u4e0d\u7981\u60a0\u7136\u795e\u5f80\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u7239\u5a18\u867d\u7136\u65e9\u6b7b\uff0c\u53ef\u6bd4\u6211\u7239\u5988\u5feb\u6d3b\u5f97\u591a\u3002\u201d \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u53ea\u662f\u6211\u81ea\u5e7c\u6ca1\u7239\u6ca1\u5a18\uff0c\u5374\u6bd4\u4f60\u53ef\u601c\u5f97\u591a\u4e86\u3002\u201d\u82d7\u82e5\u5170\u9053\uff1a\u201c\u6211\u7239\u7239\u82e5\u77e5\u4f60\u6d3b\u5728\u4e16\u4e0a\uff0c\u5c31\u662f\u629b\u5c3d\u4e00\u5207\uff0c\u4e5f\u8981\u9886\u4f60\u53bb\u629a\u517b\u3002\u90a3\u4e48\u54b1\u4eec\u65e9\u5c31\u53ef\u4ee5\u76f8\u89c1\u5566\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u6211\u82e5\u4f4f\u5728\u4f60\u5bb6\u91cc\uff0c\u53ea\u6015\u4f60\u4f1a\u538c\u618e\u6211\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u6025\u9053\uff1a\u201c\u4e0d\uff01\u4e0d\uff01\u90a3\u600e\u4e48\u4f1a\uff1f\u6211\u4e00\u5b9a\u4f1a\u5f85\u4f60\u5f88\u597d\u5f88\u597d\uff0c\u5c31\u5f53\u4f60\u662f\u6211\u4eb2\u54e5\u54e5\u4e00\u822c\u3002\u201d\u80e1\u6590\u6026\u6026\u5fc3\u8df3\uff0c\u95ee\u9053\uff1a\u201c\u73b0\u5728\u76f8\u9022\u8fd8\u4e0d\u8fdf\u4e48\uff1f\u201d\u82d7\u82e5\u5170\u4e0d\u7b54\uff0c\u8fc7\u4e86\u826f\u4e45\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u53c8\u8fc7\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5f88\u6b22\u559c\u3002\u201d \u3000\u3000\u53e4\u4eba\u7537\u5973\u98ce\u6000\u604b\u6155\uff0c\u53ea\u51ed\u4e00\u8a00\u7247\u8bed\uff0c\u4fbf\u4f20\u503e\u5fc3\u4e4b\u610f\u3002 \u3000\u3000\u80e1\u6590\u542c\u4e86\u6b64\u8a00\uff0c\u5fc3\u4e2d\u72c2\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u80e1\u6590\u7ec8\u751f\u4e0d\u6562\u6709\u8d1f\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u5b66\u4f60\u5988\u5988\uff0c\u4e0d\u5b66\u6211\u5988\u3002\u201d\u5979\u8fd9\u4e24\u53e5\u8bdd\u8bf4\u5f97\u5929\u771f\uff0c\u53ef\u662f\u8bed\u610f\u4e4b\u4e2d\uff0c\u5145\u6ee1\u4e86\u51b3\u5fc3\uff0c\u90a3\u662f\u628a\u81ea\u5df1\u4e00\u751f\u7684\u547d\u8fd0\uff0c\u5168\u76d8\u4ea4\u6258\u7ed9\u4e86\u4ed6\uff0c\u4e0d\u7ba1\u662f\u597d\u662f\u574f\uff0c\u4e0d\u7ba1\u5c06\u6765\u662f\u7978\u662f\u798f\uff0c\u603b\u4e4b\u662f\u4e0e\u4ed6\u5171\u540c\u62c5\u5f53\u3002 \u3000\u3000\u4e24\u4eba\u53cc\u624b\u76f8\u63e1\uff0c\u4e0d\u518d\u8bf4\u8bdd\uff0c\u4f3c\u4e4e\u8fd9\u5c0f\u5c0f\u5c71\u6d1e\u5c31\u662f\u6574\u4e2a\u4e16\u754c\uff0c\u767b\u5fd8\u8eab\u5916\u5929\u5730\u3002 \u3000\u3000\u8fc7\u4e86\u826f\u4e45\uff0c\u82d7\u82e5\u5170\u624d\u9053\uff1a\u201c\u54b1\u4eec\u53bb\u627e\u5230\u6211\u7239\uff0c\u4e00\u8d77\u8d70\u5427\uff0c\u522b\u7406\u675c\u5e84\u4e3b\u4ed6\u4eec\u5566\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u597d\u7684\u3002\u201d\u53ef\u662f\u4ed6\u4e00\u751f\u4e4b\u4e2d\uff0c\u4ece\u672a\u6709\u5982\u6b64\u523b\u4e4b\u4e50\uff0c\u5b9e\u662f\u4e0d\u613f\u79bb\u5f00\u5c71\u6d1e\u3002\u82d7\u82e5\u5170\u4e5f\u6709\u6b64\u5fc3\uff0c\u89c9\u5f97\u4e0d\u5982\u8bf4\u4e9b\u95f2\u8bdd\uff0c\u591a\u7559\u4e00\u523b\u597d\u4e00\u523b\uff0c\u4e8e\u662f\u95ee\u9053\uff1a\u201c\u675c\u5e84\u4e3b\u65e2\u662f\u4f60\u957f\u4eb2\uff0c\u4f55\u4ee5\u4f60\u8981\u8ddf\u4ed6\u4e3a\u96be\uff1f\u201d \u3000\u3000\u80e1\u6590\u6068\u6068\u7684\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u8bf4\u6765\u5f53\u771f\u6c14\u4eba\u3002\u6211\u5988\u4e34\u7ec8\u4e4b\u65f6\uff0c\u62dc\u6073\u4f60\u7239\u7167\u770b\uff0c\u517b\u6211\u6210\u4eba\u3002\u6211\u5988\u5728\u6211\u8941\u8913\u4e2d\u653e\u4e86\u4e00\u5305\u9057\u7269\uff0c\u4e00\u901a\u9057\u4e66\uff0c\u5176\u4e2d\u8bb0\u660e\u6211\u7684\u751f\u65e5\u65f6\u8fb0\uff0c\u6211\u80e1\u5bb6\u7684\u7c4d\u8d2f\u3001\u7956\u5b97\u59d3\u540d\uff0c\u4ee5\u53ca\u4e16\u4e0a\u7684\u4eb2\u621a\u3002\u540e\u6765\u53d8\u751f\u4e0d\u6d4b\uff0c\u5e73\u56db\u53d4\u62b1\u4e86\u6211\u9003\u8d70\u3002\u4ed6\u4ee5\u4e3a\u4f60\u7236\u6709\u5bb3\u6211\u4e4b\u610f\uff0c\u89c1\u5230\u9057\u4e66\u4e2d\u6709\u675c\u5e84\u4e3b\u7684\u59d3\u540d\uff0c\u4fbf\u62b1\u4e86\u6211\u524d\u53bb\u6295\u5954\u3002\u54ea\u77e5\u675c\u5e84\u4e3b\u8d77\u5fc3\u4e0d\u826f\uff0c\u60f3\u5f97\u6211\u7239\u7684\u6b66\u5b66\u79d8\u672c\u3002\u4ed6\u53c8\u9690\u7ea6\u731c\u5230\u6211\u7239\u5988\u77e5\u9053\u85cf\u5b9d\u79d8\u5bc6\uff0c\u7adf\u6765\u641c\u67e5\u6211\u5988\u7ed9\u6211\u7684\u9057\u7269\u3002 \u3000\u3000\u5e73\u56db\u53d4\u60c5\u77e5\u4e0d\u5999\uff0c\u62b1\u7740\u6211\u8fde\u591c\u9003\u4e0b\u96ea\u5cf0\u3002\u6211\u7239\u7684\u6b66\u5b66\u79d8\u672c\u662f\u5e26\u8d70\u4e86\uff0c\u4f46\u6211\u5988\u7ed9\u6211\u7684\u4e00\u5305\u9057\u7269\uff0c\u5374\u5931\u843d\u5728\u5e84\u4e0a\u3002\u8fd9\u6b21\u6211\u8ddf\u4ed6\u7ea6\u4f1a\uff0c\u662f\u8981\u95ee\u4ed6\u4e3a\u4ec0\u4e48\u6b3a\u4fae\u6211\u4e00\u4e2a\u5e7c\u5e74\u5b64\u513f\uff0c\u53c8\u8981\u5411\u4ed6\u8981\u56de\u6211\u5988\u6240\u9057\u7684\u7269\u4e8b\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u9053\uff1a\u201c\u675c\u5e84\u4e3b\u5bf9\u4eba\u6e29\u548c\u8c26\u5584\uff0c\u751a\u662f\u597d\u5ba2\uff0c\u60f3\u4e0d\u5230\u5f85\u4f60\u7adf\u8fd9\u4e48\u574f\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u4eba\u5047\u4ec1\u5047\u4e49\uff0c\u5355\u662f\u4ed6\u9634\u8c0b\u5bb3\u4f60\u7239\u7239\uff0c\u5c31\u53ef\u60f3\u89c1\u5176\u4f59\u2026\u2026\u201d\u968f\u5373\u8bed\u6c14\u8f6c\u67d4\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8fc7\u73b0\u5728\u6211\u4e5f\u4e0d\u607c\u4ed6\u4e86\u3002\u82e5\u4e0d\u662f\u4ed6\uff0c\u6211\u53c8\u600e\u80fd\u8ddf\u4f60\u76f8\u9022\uff1f\u201d \u3000\u3000\u6b63\u8bf4\u5230\u6b64\u5904\uff0c\u5ffd\u542c\u6d1e\u5916\u4f20\u6765\u4e00\u9635\u5175\u5203\u76f8\u4ea4\u4e4b\u58f0\uff0c\u9690\u9690\u5939\u6742\u7740\u547c\u559d\u53f1\u9a82\u3002\u53ea\u662f\u58f0\u97f3\u6781\u6c89\u6781\u95f7\uff0c\u80e1\u6590\u4f9d\u7a00\u5206\u8fa8\u5f97\u51fa\uff0c\u82d7\u82e5\u5170\u5374\u8fd8\u9053\u662f\u98ce\u52a8\u677e\u67cf\uff0c\u96ea\u843d\u5c71\u5dc5\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u8fd9\u58f0\u97f3\u6765\u81ea\u5730\u5e95\uff0c\u90a3\u53ef\u5947\u4e86\u3002\u4f60\u7559\u5728\u8fd9\u91cc\uff0c\u6211\u77a7\u77a7\u53bb\u3002\u201d\u8bf4\u7740\u7ad9\u8d77\u8eab\u6765\u3002\u82d7\u82e5\u5170\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u8ddf\u4f60\u53bb\u3002\u201d\u80e1\u6590\u4e5f\u4e0d\u613f\u7559\u5979\u4e00\u4eba\u5b64\u8eab\u5728\u6b64\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u3002\u201d\u643a\u7740\u5979\u624b\uff0c\u51fa\u6d1e\u5bfb\u58f0\u800c\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u5728\u96ea\u5730\u4e0a\u7f13\u7f13\u8d70\u51fa\u6570\u5341\u4e08\u3002\u8fd9\u5929\u662f\u4e09\u6708\u5341\u4e94\uff0c\u6708\u4eae\u6b63\u5706\uff0c\u94f6\u8272\u7684\u6708\u5149\u6620\u7740\u94f6\u8272\u7684\u96ea\u5149\uff0c\u518d\u4e0e\u82d7\u82e5\u5170\u768e\u6d01\u65e0\u7455\u7684\u808c\u80a4\u4e00\u6620\uff0c\u5f53\u771f\u662f\u4eba\u95f4\u4ed9\u5883\uff0c\u6b64\u5915\u4f55\u5915\uff1f\u8fd9\u65f6\u80e1\u6590\u65e9\u5df2\u9664\u4e0b\u81ea\u5df1\u957f\u888d\uff0c\u62ab\u5728\u82d7\u82e5\u5170\u8eab\u4e0a\u3002\u6708\u5149\u4e0b\u56db\u76ee\u4ea4\u6295\uff0c\u4e8e\u8eab\u5916\u4e4b\u4e8b\uff0c\u7adf\u662f\u5168\u4e0d\u8426\u6000\u3002 \u3000\u3000\u4e24\u4eba\u5fc3\u4e2d\u67d4\u548c\uff0c\u53e4\u4eba\u548f\u53f9\u6df1\u60c5\u871c\u610f\u7684\u8bd7\u53e5\uff0c\u5ffd\u5730\u4e00\u53e5\u53e5\u4f3c\u8131\u53e3\u800c\u51fa\u3002\u80e1\u6590\u4e0d\u81ea\u7981\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5b9c\u8a00\u996e\u9152\uff0c\u4e0e\u5b50\u5055\u8001\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u4ef0\u8d77\u5934\u6765\uff0c\u671b\u7740\u4ed6\u7684\u773c\u775b\uff0c\u8f7b\u8f7b\u7684\u9053\uff1a\u201c\u7434\u745f\u5728\u5fa1\uff0c\u83ab\u4e0d\u9759\u597d\u3002\u201d\u8fd9\u662f\u300a\u8bd7\u7ecf\u300b\u4e2d\u4e00\u5bf9\u592b\u5987\u7684\u5bf9\u7b54\u4e4b\u8bcd\uff0c\u60c5\u610f\u7ef5\u7ef5\uff0c\u6e29\u99a8\u65e0\u9650\u3002\u7a81\u7136\u4e4b\u95f4\uff0c\u5730\u5e95\u547c\u58f0\u8f6c\u5267\uff0c\u4e24\u4eba\u5f53\u5373\u6b62\u6b65\uff0c\u4fa7\u8033\u503e\u542c\u3002 \u3000\u3000\u80e1\u6590\u4e00\u8fa8\u58f0\u97f3\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u627e\u5230\u4e86\u5b9d\u85cf\u6240\u5728\uff0c\u6b63\u5728\u5730\u4e0b\u53ae\u6740\u4e89\u593a\u3002\u201d\u4ed6\u4ece\u7236\u4eb2\u9057\u4e66\u4e4b\u4e2d\u5f97\u77e5\u5b9d\u85cf\u5730\u70b9\uff0c\u66fe\u8fdb\u5165\u6570\u6b21\uff0c\u53d6\u51fa\u7236\u6bcd\u5f53\u5e74\u5c01\u5b58\u7684\u6587\u5b57\uff0c\u53c8\u53d6\u4e86\u7530\u5f52\u519c\u4e4b\u7236\u7684\u9ec4\u91d1\u5c0f\u7b14\u3002\u8fd9\u65e5\u65e9\u6668\u4ed6\u7528\u5c0f\u7b14\u6295\u5c04\u7530\u9752\u6587\uff0c\u5c31\u662f\u793a\u8b66\u4e4b\u610f\u3002\u4ed6\u867d\u77e5\u5b9d\u85cf\u6240\u5728\uff0c\u4f46\u4f53\u5ff5\u7236\u6bcd\u9057\u5fd7\uff0c\u4e0d\u80af\u53d1\u6398\u3002\u8fd9\u65f6\u8fa8\u58f0\u77e5\u5411\uff0c\u6599\u5b9a\u5b9d\u6811\u7b49\u5fc5\u662f\u89c1\u8d22\u773c\u7ea2\uff0c\u6b63\u5728\u4e92\u76f8\u4e89\u593a\u3002 \u3000\u3000\u80e1\u6590\u6240\u6599\u4e1d\u6beb\u4e0d\u9519\uff0c\u90a3\u5730\u5e95\u5c71\u6d1e\u4e4b\u4e2d\uff0c\u5929\u9f99\u95e8\u3001\u996e\u9a6c\u5ddd\u5c71\u5be8\u3001\u5e73\u901a\u9556\u5c40\u8bf8\u8def\u4eba\u9a6c\uff0c\u4e3a\u4e86\u4e89\u593a\u5b9d\u7269\uff0c\u6b63\u81ea\u6740\u6210\u4e00\u56e2\u3002\u5b9d\u6811\u8896\u624b\u65c1\u89c2\uff0c\u53ea\u662f\u51b7\u7b11\uff0c\u5fc3\u60f3\u4e14\u8ba9\u4f60\u4eec\u6253\u4e2a\u4e09\u8d25\u4ff1\u4f24\uff0c\u8001\u50e7\u518d\u6162\u6162\u4e00\u4e2a\u4e2a\u7684\u6536\u62fe\u3002 \u3000\u3000\u5468\u4e91\u9633\u4e0e\u718a\u5143\u732e\u53c8\u662f\u626d\u5728\u4e00\u8d77\uff0c\u5728\u5730\u4e0b\u6eda\u6765\u6eda\u53bb\u3002\u4e24\u4eba\u7a81\u7136\u95f4\u6eda\u5230\u4e86\u706b\u5806\u4e4b\u65c1\u3002\u521d\u65f6\u4e92\u6b32\u5c06\u5bf9\u65b9\u538b\u5728\u706b\u4e0a\uff0c\u54ea\u77e5\u51e0\u4e2a\u6253\u6eda\uff0c\u9669\u9669\u5c06\u706b\u5934\u538b\u7184\uff0c\u5b9d\u6811\u9a82\u9053\uff1a\u201c\u538b\u706d\u4e86\u706b\uff0c\u5927\u4f19\u513f\u90fd\u51bb\u6b7b\u4e48\uff1f\u201d\u4f38\u51fa\u53f3\u811a\uff0c\u6284\u5230\u5468\u4e91\u9633\u8eab\u5e95\u4e00\u6311\uff0c\u4e24\u4e2a\u4eba\u4e00\u9f50\u98de\u4e86\u8d77\u6765\uff0c\u817e\u7684\u4e00\u58f0\uff0c\u843d\u5728\u5730\u4e0b\u3002 \u3000\u3000\u5b9d\u6811\u563f\u563f\u4e00\u7b11\uff0c\u5f2f\u8170\u62ff\u8d77\u51e0\u6839\u7c97\u67f4\uff0c\u6dfb\u5165\u706b\u5806\u3002\u6b63\u8981\u633a\u76f4\u8eab\u5b50\uff0c\u5ffd\u89c1\u706b\u5149\u7a81\u7a81\u8df3\u8df3\uff0c\u5728\u5bf9\u9762\u51b0\u58c1\u4e0a\u6620\u51fa\u4e24\u4e2a\u4eba\u5f71\uff0c\u4eba\u5f71\u4e5f\u5728\u5fae\u5fae\u8df3\u52a8\u3002\u5b9d\u6811\u5403\u4e86\u4e00\u60ca\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u89c1\u5c71\u6d1e\u53e3\u5e76\u80a9\u7ad9\u7740\u4e8c\u4eba\u3002\u4e00\u4e2a\u8138\u5e26\u5a07\u7f9e\uff0c\u4e43\u662f\u82d7\u82e5\u5170\uff0c\u53e6\u4e00\u4e2a\u866c\u9aef\u621f\u5f20\u3001\u773c\u9732\u6740\u6c14\uff0c\u5374\u662f\u80e1\u6590\u3002 \u3000\u3000\u5b9d\u6811\u201c\u554a\u201d\u7684\u4e00\u58f0\uff0c\u53f3\u624b\u4e00\u626c\uff0c\u4e00\u4e32\u94c1\u5ff5\u73e0\u6fc0\u98de\u800c\u51fa\u3002\u5ff5\u73e0\u521d\u63b7\u51fa\u4f3c\u662f\u4e00\u4e32\uff0c\u5176\u5b9e\u4e32\u7740\u94c1\u73e0\u7684\u4e1d\u7ebf\u65e9\u88ab\u4ed6\u634f\u65ad\uff0c\u6570\u5341\u9897\u94c1\u73e0\u5ffd\u7136\u4e0a\u4e0b\u5de6\u53f3\uff0c\u5206\u6253\u80e1\u82d7\u4e8c\u4eba\u7684\u8981\u5bb3\u3002\u8fd9\u662f\u4ed6\u82e6\u7ec3\u5341\u4f59\u5e74\u7684\u7edd\u6280\uff0c\u6043\u4ee5\u4fdd\u8eab\u6551\u547d\uff0c\u4e34\u654c\u4e4b\u65f6\u4ece\u672a\u7528\u8fc7\uff0c\u6b64\u65f6\u9661\u9022\u5927\u654c\uff0c\u4e8b\u52bf\u7d27\u8feb\uff0c\u7acb\u65bd\u6740\u624b\u3002 \u3000\u3000\u80e1\u6590\u4e00\u58f0\u51b7\u7b11\uff0c\u8e0f\u4e0a\u4e00\u6b65\uff0c\u6321\u5728\u82d7\u82e5\u5170\u8eab\u524d\u3002\u5b9d\u6811\u89c1\u4ed6\u5e76\u65e0\u7279\u5f02\u529f\u592b\u6321\u907f\uff0c\u5fc3\u4e0b\u5927\u559c\uff0c\u6697\u9053\uff1a\u201c\u539f\u6765\u4f60\u88c5\u6a21\u4f5c\u6837\uff0c\u529f\u592b\u4e5f\u4e0d\u8fc7\u5c14\u5c14\uff0c\u8fd9\u756a\u53ef\u8981\u53eb\u4f60\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u4e86\u3002\u201d\u6b63\u81ea\u5f97\u610f\uff0c\u4f46\u89c1\u80e1\u6590\u53cc\u624b\u8863\u8896\u500f\u5730\u6325\u51fa\uff0c\u5df2\u5c06\u6570\u5341\u9897\u6765\u52bf\u5947\u6025\u7684\u94c1\u5ff5\u73e0\u5c3d\u884c\u5377\u4f4f\uff0c\u8863\u8896\u632f\u5904\uff0c\u55d2\u55d2\u6025\u54cd\uff0c\u5982\u843d\u51b0\u96f9\uff0c\u94c1\u5ff5\u73e0\u90fd\u98de\u5411\u51b0\u58c1\uff0c\u53ea\u6253\u5f97\u788e\u51b0\u56db\u6e85\u3002 \u3000\u3000\u5b9d\u6811\u4e00\u89c1\u4e4b\u4e0b\uff0c\u4e0d\u7531\u5f97\u5fc3\u80c6\u4ff1\u88c2\uff0c\u6025\u5fd9\u5012\u8dc3\uff0c\u9000\u5728\u66f9\u4e91\u5947\u8eab\u540e\uff0c\u751f\u6015\u80e1\u6590\u8ddf\u7740\u4e0a\u524d\uff0c\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u4e0d\u597d\u4e86\uff01\u201d\u53cc\u624b\u6293\u4f4f\u66f9\u4e91\u5947\u80cc\u5fc3\uff0c\u63d0\u8d77\u4ed6\u4e00\u4e2a\u9b41\u4f1f\u957f\u5927\u7684\u8eab\u5b50\uff0c\u5c31\u5f80\u706b\u5806\u4e2d\u63b7\u5c06\u8fc7\u53bb\u3002\u4ed6\u672c\u610f\u5c06\u706b\u5806\u538b\u706d\uff0c\u597d\u6559\u80e1\u6590\u77a7\u4e0d\u89c1\u81ea\u5df1\uff0c\u54ea\u77e5\u9053\u706b\u5806\u521a\u5f97\u4ed6\u6dfb\u4e86\u5e72\u67f4\uff0c\u70e7\u5f97\u6b63\u65fa\u3002\u66f9\u4e91\u5947\u8dcc\u5728\u706b\u4e2d\uff0c\u8863\u670d\u7740\u706b\uff0c\u6d1e\u4e2d\u66f4\u662f\u660e\u4eae\u3002 \u3000\u3000\u80e1\u6590\u89c1\u5b9d\u6811\u4e00\u4e0a\u6765\u5c31\u5411\u81ea\u5df1\u548c\u82d7\u82e5\u5170\u731b\u65bd\u6bd2\u624b\uff0c\u60f3\u8d77\u5e73\u963f\u56db\u9002\u624d\u6240\u8a00\uff0c\u8fd9\u548c\u5c1a\u5351\u9119\u8d2a\u8d22\uff0c\u5bb3\u4e86\u81ea\u5df1\u7236\u6bcd\u6027\u547d\uff0c\u5fc3\u4e2d\u6012\u706b\u5927\u70bd\uff0c\u7acb\u65f6\u4e5f\u5982\u90a3\u706b\u5806\u4e00\u822c\u70e7\u4e86\u8d77\u6765\uff0c\u4e00\u5f2f\u8170\u6284\u8d77\u4e86\u4e00\u628a\u73e0\u5b9d\uff0c\u6258\u5728\u5de6\u624b\u638c\u5fc3\uff0c\u53f3\u624b\u98df\u6307\u4e0d\u4f4f\u5f39\u52a8\u3002 \u3000\u3000\u4f46\u89c1\u73cd\u73e0\u3001\u73ca\u745a\u3001\u78a7\u7389\u3001\u739b\u7459\u3001\u7fe1\u7fe0\u3001\u5b9d\u77f3\u3001\u732b\u513f\u773c\u3001\u7956\u6bcd\u7eff\u3001\u5404\u79cd\u5404\u6837\u7684\u73cd\u7269\uff0c\u5982\u96e8\u70b9\u822c\u5f80\u5b9d\u6811\u8eab\u4e0a\u98de\u53bb\u3002\u6bcf\u4e00\u5757\u5b9d\u7269\u5c04\u5230\uff0c\u90fd\u6253\u5f97\u4ed6\u5267\u75db\u96be\u5f53\u3002\u5b9d\u6811\u7eb5\u9ad8\u7a9c\u4f4e\uff0c\u7aed\u529b\u95ea\u907f\uff0c\u4f46\u80e1\u6590\u624b\u6307\u5f39\u51fa\uff0c\u73cd\u5b9d\u98de\u5230\uff0c\u51c6\u5934\u7adf\u662f\u4e0d\u504f\u534a\u70b9\uff0c\u6d1e\u4e2d\u4eba\u6570\u4e0d\u5c11\uff0c\u8fd9\u4e9b\u73e0\u5b9d\u5374\u59cb\u7ec8\u4e0d\u78b0\u5230\u522b\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u5218\u5143\u9e64\u3001\u9676\u767e\u5c81\u7b49\u89c1\u6b64\u60c5\u666f\uff0c\u4e2a\u4e2a\u8d34\u8eab\u51b0\u58c1\uff0c\u4e00\u52a8\u4e5f\u4e0d\u6562\u52a8\u3002\u5b9d\u6811\u521d\u65f6\u8fd8\u4e1c\u897f\u5954\u8dc3\uff0c\u540e\u6765\u8db3\u8e1d\u4e0a\u8fde\u4e2d\u4e86\u4e24\u5757\u78a7\u7389\uff0c\u7adf\u81ea\u5012\u5730\uff0c\u518d\u4e5f\u7ad9\u4e0d\u8d77\u6765\uff0c\u9ad8\u58f0\u53f7\u53eb\uff0c\u5728\u5730\u4e0b\u6eda\u6765\u6eda\u53bb\u3002\u4ed6\u5148\u524d\u53ea\u6101\u73cd\u5b9d\u4e0d\u591a\uff0c\u6b64\u65f6\u5374\u4f46\u613f\u73cd\u5b9d\u8d8a\u5c11\u8d8a\u597d\u3002 \u3000\u3000\u80e1\u6590\u8d8a\u5f39\u624b\u52b2\u8d8a\u91cd\uff0c\u6709\u610f\u907f\u5f00\u5b9d\u6811\u7684\u8981\u5bb3\uff0c\u8981\u8ba9\u4ed6\u591a\u5403\u4e9b\u82e6\u5934\u3002\u4f17\u4eba\u7f29\u5728\u6d1e\u89d2\uff0c\u51dd\u795e\u89c2\u770b\uff0c\u4e2a\u4e2a\u5413\u5f97\u5fc3\u60ca\u8089\u8df3\uff0c\u8fde\u5927\u6c14\u4e5f\u4e0d\u6562\u5598\u4e00\u53e3\u3002 \u3000\u3000\u82d7\u82e5\u5170\u542c\u5b9d\u6811\u53eb\u5f97\u51c4\u60e8\uff0c\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u4eba\u786e\u662f\u5f88\u574f\uff0c\u4f46\u4e5f\u591f\u4ed6\u53d7\u7684\u4e86\u3002\u9976\u4e86\u4ed6\u5427\uff01\u201d\u80e1\u6590\u751f\u5e73\u9664\u6076\u52a1\u5c3d\uff0c\u4f55\u51b5\u8fd9\u4eba\u6b63\u662f\u6740\u7236\u5bb3\u6bcd\u7684\u5927\u4ec7\u4eba\uff0c\u4f46\u4e00\u542c\u82d7\u82e5\u5170\u4e4b\u8a00\uff0c\u7a81\u7136\u89c9\u5f97\u81ea\u5df1\u6b63\u5904\u4e8e\u6781\u5927\u5e78\u798f\u4e4b\u4e2d\uff0c\u5bf9\u8fd9\u4e16\u4e0a\u6700\u5927\u7684\u6076\u4eba\uff0c\u618e\u6068\u4e4b\u5fc3\u4e5f\u767b\u65f6\u6de1\u4e86\u8bb8\u591a\uff0c\u5f53\u5373\u5de6\u624b\u4e00\u63b7\uff0c\u638c\u4e2d\u4f59\u4e0b\u7684\u5341\u4f59\u4ef6\u73cd\u5b9d\u6fc0\u98de\u800c\u51fa\uff0c\u53ee\u53ee\u5f53\u5f53\u4e00\u9635\u54cd\uff0c\u5c3d\u6570\u5d4c\u5728\u51b0\u58c1\u4e4b\u4e2d\u3002 \u3000\u3000\u4f17\u4eba\u5c3d\u7686\u9a87\u7136\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u4e9b\u73e0\u5b9d\u82e5\u8981\u5b9d\u6811\u53d7\u7528\uff0c\u5355\u53ea\u4e00\u4ef6\u5c31\u8981\u4e86\u4ed6\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u80e1\u6590\u6a2a\u7709\u6012\u76ee\uff0c\u81ea\u5de6\u81f3\u53f3\u9010\u4e00\u671b\u8fc7\u53bb\uff0c\u773c\u5149\u5c04\u5230\u8c01\u7684\u8138\u4e0a\uff0c\u8c01\u5c31\u4e0d\u81ea\u7981\u7684\u4f4e\u4e0b\u5934\u53bb\uff0c\u4e0d\u6562\u4e0e\u4ed6\u76ee\u5149\u76f8\u63a5\u3002\u6d1e\u4e2d\u5bc2\u9759\u65e0\u58f0\u3002\u5b9d\u6811\u8eab\u4e0a\u867d\u75db\uff0c\u5374\u4e5f\u4e0d\u6562\u53d1\u51fa\u534a\u58f0\u547b\u541f\u3002 \u3000\u3000\u9694\u4e86\u826f\u4e45\uff0c\u80e1\u6590\u559d\u9053\uff1a\u201c\u5404\u4f4d\u5982\u6b64\u8d2a\u7231\u73cd\u5b9d\uff0c\u5c31\u7559\u5728\u8fd9\u91cc\u966a\u4f34\u5b9d\u85cf\u5427\uff01\u201d\u8bf4\u7740\u643a\u4e86\u82d7\u82e5\u5170\u7684\u624b\uff0c\u8f6c\u8eab\u4fbf\u51fa\u3002 \u3000\u3000\u4f17\u4eba\u4e07\u6599\u4e0d\u5230\u4ed6\u5c45\u7136\u80af\u8fd9\u4e48\u8f7b\u6613\u7f62\u624b\uff0c\u4e2a\u4e2a\u559c\u51fa\u671b\u5916\uff0c\u4f46\u542c\u4ed6\u4e8c\u4eba\u811a\u6b65\u58f0\u5728\u96a7\u9053\u4e2d\u9010\u6e10\u8fdc\u53bb\uff0c\u5404\u4eba\u9f50\u58f0\u4f4e\u547c\uff0c\u4fef\u8eab\u53c8\u53bb\u6361\u62fe\u73e0\u5b9d\u3002 \u3000\u3000\u80e1\u6590\u548c\u82d7\u82e5\u5170\u6765\u5230\u4e24\u5757\u5706\u5ca9\u4e4b\u5916\u3002\u80e1\u6590\u9053\uff1a\u201c\u6211\u4eec\u5728\u8fd9\u91cc\u7b49\u4e0a\u4e00\u4f1a\uff0c\u77a7\u4ed6\u4eec\u51fa\u4e0d\u51fa\u6765\u3002\u54ea\u4e00\u4e2a\u8d2a\u5ff5\u7a0d\u8f7b\uff0c\u81ea\u884c\u51fa\u6765\uff0c\u5c31\u9976\u4e86\u4ed6\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u6d1e\u5185\u5404\u4eba\u53cc\u624b\u4e71\u6252\uff0c\u62da\u547d\u7684\u6267\u62fe\u73e0\u5b9d\uff0c\u53ea\u6068\u7239\u5a18\u5f53\u65f6\u5c11\u751f\u4e86\u81ea\u5df1\u4e24\u4e09\u53ea\u624b\u3002\u8fc7\u4e86\u826f\u4e45\uff0c\u7a81\u7136\u96a7\u9053\u4e2d\u4f20\u6765\u4e00\u9635\u90c1\u95f7\u7684\u8f67\u8f67\u4e4b\u58f0\uff0c\u4f17\u4eba\u521d\u5c1a\u4e0d\u89e3\uff0c\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u4e2a\u4e2a\u60ca\u5f97\u8138\u5982\u571f\u8272\uff0c\u9f50\u53eb\uff1a\u201c\u554a\u54df\uff0c\u4e0d\u597d\u5566\uff01\u201d\u201c\u4ed6\u5835\u6b7b\u4e86\u54b1\u4eec\u51fa\u8def\u3002\u201d\u201c\u5feb\u8ddf\u4ed6\u62da\u4e86\u3002\u201d \u3000\u3000\u4f17\u4eba\u60c5\u6025\u4e4b\u4e0b\uff0c\u4e89\u5148\u6050\u540e\u7684\u62e5\u51fa\uff0c\u5954\u5230\u5706\u5ca9\u4e4b\u540e\uff0c\u679c\u89c1\u90a3\u5757\u5de8\u5ca9\u5df2\u88ab\u80e1\u6590\u63a8\u56de\u539f\u5904\uff0c\u7262\u7262\u7684\u5835\u4f4f\u4e86\u6d1e\u95e8\u3002 \u3000\u3000\u6d1e\u95e8\u751a\u7a84\uff0c\u5728\u5916\u5c1a\u6709\u7740\u529b\u4e4b\u5904\uff0c\u5185\u9762\u5374\u53ea\u5bb9\u5f97\u4e00\u4eba\u7ad9\u7acb\uff0c\u5ca9\u9762\u5149\u6ed1\uff0c\u65e0\u6240\u62c9\u626f\uff0c\u8fd9\u4e48\u4e00\u5835\u4e0a\uff0c\u8fc7\u4e0d\u591a\u65f6\uff0c\u878d\u5316\u4e86\u7684\u51b0\u6c34\u91cd\u884c\u51bb\u7ed3\uff0c\u82e5\u975e\u5916\u9762\u6709\u4eba\u6765\u6551\uff0c\u6d1e\u5185\u8bf8\u4eba\u4e07\u4e07\u4e0d\u80fd\u51fa\u6765\u3002 \u3000\u3000\u82d7\u82e5\u5170\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u9053\uff1a\u201c\u4f60\u8981\u4ed6\u4eec\u90fd\u6b7b\u5728\u91cc\u9762\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u91cc\u9762\u54ea\u4e00\u4e2a\u662f\u597d\u4eba\uff0c\u9976\u5f97\u4ed6\u6d3b\u547d\uff1f\u201d \u3000\u3000\u82d7\u82e5\u5170\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u9664\u4e86\u7239\u7239\u548c\u4f60\uff0c\u6211\u4e0d\u77e5\u9053\u8fd8\u6709\u8c01\u662f\u771f\u6b63\u7684\u597d\u4eba\u3002\u53ef\u662f\uff0c\u4f60\u603b\u4e0d\u80fd\u628a\u5929\u4e0b\u7684\u574f\u4eba\u90fd\u6740\u4e86\u554a\u3002\u201d\u80e1\u6590\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6211\u54ea\u7b97\u5f97\u662f\u597d\u4eba\uff1f\u201d \u3000\u3000\u82d7\u82e5\u5170\u62ac\u5934\u671b\u7740\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u597d\u4eba\u3002\u6211\u6ca1\u89c1\u4f60\u9762\u7684\u65f6\u5019\u5c31\u77e5\u9053\u5566\uff01\u5927\u54e5\uff0c\u4f60\u53ef\u77e5\u5728\u4ec0\u4e48\u65f6\u5019\uff0c\u6211\u8fd9\u9897\u5fc3\u5c31\u5df2\u4ea4\u4e86\u7ed9\u4f60\uff1f\u201d \u3000\u3000\u8fd9\u662f\u5979\u7b2c\u4e00\u6b21\u51fa\u53e3\u53eb\u4ed6\u201c\u5927\u54e5\u201d\uff0c\u53ef\u662f\u8fd9\u4e00\u58f0\u53eb\u5f97\u90a3\u4e48\u81ea\u7136\u6d41\u7545\uff0c\u968f\u968f\u4fbf\u4fbf\u7684\u8131\u53e3\u800c\u51fa\uff0c\u5374\u4f3c\u5df2\u7ecf\u53eb\u4e86\u4e00\u8f88\u5b50\u4e00\u822c\u3002\u80e1\u6590\u518d\u4e5f\u6291\u5236\u4e0d\u4f4f\uff0c\u5f20\u81c2\u62b1\u4f4f\u4e86\u5979\u3002\u82d7\u82e5\u5170\u4f38\u624b\u8fd8\u62b1\uff0c\u501a\u5728\u4ed6\u7684\u6000\u4e2d\u3002\u4e24\u4eba\u6402\u62b1\u5728\u4e00\u8d77\uff0c\u4f46\u613f\u8fd9\u4e00\u523b\u65e0\u7a77\u65e0\u5c3d\u3002 \u3000\u3000\u4e24\u4eba\u8fd9\u6837\u62b1\u7740\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u5ffd\u7136\u6d1e\u53e3\u4f20\u8fdb\u6765\u51e0\u4e0b\u811a\u6b65\u4e4b\u58f0\u3002\u80e1\u6590\u5fc3\u9053\uff1a\u201c\u4e0d\u597d\uff01\u6211\u5835\u6b7b\u522b\u4eba\uff0c\u522b\u8981\u87b3\u8782\u6355\u8749\uff0c\u9ec4\u96c0\u5728\u540e\uff0c\u53e6\u6709\u522b\u4eba\u6765\u5835\u6b7b\u4e86\u6211\u4eec\u3002\u201d\u624b\u81c2\u6402\u7740\u82d7\u82e5\u5170\u4e0d\u653e\uff0c\u6025\u6b65\u62a2\u51fa\u6d1e\u53bb\u3002 \u3000\u3000\u6708\u5149\u4e4b\u4e0b\uff0c\u4f46\u89c1\u96ea\u5730\u91cc\u6709\u4e24\u4eba\u5728\u53d1\u529b\u5954\u9003\uff0c\u663e\u7136\u4fbf\u662f\u96ea\u5cf0\u4e0a\u4e0e\u81ea\u5df1\u52a8\u8fc7\u624b\u7684\u6b66\u6797\u8c6a\u5ba2\u3002\u80e1\u6590\u7b11\u9053\uff1a\u201c\u4f60\u7239\u7239\u628a\u90a3\u4e9b\u5bb6\u4f19\u90fd\u8d76\u8dd1\u5566\u3002\u201d\u5f2f\u8170\u5728\u5730\u4e0b\u6293\u8d77\u4e00\u628a\u96ea\uff0c\u624b\u6307\u7528\u52b2\uff0c\u8fd9\u628a\u96ea\u7acb\u65f6\u56e2\u5f97\u575a\u5982\u94c1\u77f3\u3002\u4ed6\u624b\u81c2\u4e00\u6325\uff0c\u96ea\u56e2\u76f4\u98de\u8fc7\u53bb\uff0c\u51fb\u4e2d\u524d\u9762\u4e00\u4eba\u540e\u8170\u3002\u90a3\u4eba\u4e00\u4ea4\u4fef\u8dcc\uff0c\u518d\u4e5f\u7ad9\u4e0d\u8d77\u6765\u3002\u540e\u9762\u4e00\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u4e00\u4e2a\u96ea\u56e2\u98de\u5230\uff0c\u6b63\u4e2d\u80f8\u53e3\uff0c\u7acb\u65f6\u4ef0\u5929\u6454\u5012\u3002\u4e24\u4eba\u8dcc\u6cd5\u4e0d\u540c\uff0c\u5374\u662f\u540c\u6837\u7684\u518d\u4e0d\u7ad9\u8d77\u3002 \u3000\u3000\u80e1\u6590\u54c8\u54c8\u4e00\u7b11\uff0c\u5ffd\u7136\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u4ec0\u4e48\u65f6\u5019\u628a\u5fc3\u4ea4\u7ed9\u4e86\u6211\uff1f \u3000\u3000\u6211\u60f3\u4e00\u5b9a\u6ca1\u6211\u65e9\u3002\u6211\u7b2c\u4e00\u773c\u77a7\u4f60\uff0c\u6211\u2026\u2026\u6211\u5c31\u7ba1\u4e0d\u4f4f\u81ea\u5df1\u4e86\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u8f7b\u58f0\u9053\uff1a\u201c\u5341\u5e74\u4e4b\u524d\uff0c\u90a3\u65f6\u5019\u6211\u8fd8\u53ea\u4e03\u5c81\uff0c\u6211\u542c\u7239\u7239\u8bf4\u4f60\u7239\u5988\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u5c31\u5c3d\u60f3\u7740\u4f60\u3002\u6211\u5bf9\u81ea\u5df1\u8bf4\uff0c\u82e5\u662f\u90a3\u4e2a\u53ef\u601c\u7684\u5b69\u5b50\u6d3b\u5728\u4e16\u4e0a\uff0c\u6211\u8981\u7167\u987e\u4ed6\u4e00\u751f\u4e00\u4e16\uff0c\u8981\u6559\u4ed6\u5feb\u5feb\u6d3b\u6d3b\uff0c\u5fd8\u4e86\u5c0f\u65f6\u5019\u522b\u4eba\u600e\u6837\u6b3a\u4fae\u4ed6\u3001\u4e8f\u5f85\u4ed6\u3002\u201d \u3000\u3000\u80e1\u6590\u5fc3\u4e0b\u611f\u6fc0\uff0c\u4e0d\u77e5\u8bf4\u4ec0\u4e48\u624d\u597d\uff0c\u53ea\u662f\u7d27\u7d27\u7684\u5c06\u5979\u6402\u5728\u6000\u91cc\uff0c\u773c\u5149\u4ece\u5979\u80a9\u4e0a\u671b\u53bb\uff0c\u5ffd\u89c1\u96ea\u5cf0\u4e0a\u51e0\u4e2a\u9ed1\u5f71\uff0c\u6b63\u7f18\u7740\u7ef3\u7d22\u5f80\u4e0b\u6025\u6e9c\u3002 \u3000\u3000\u80e1\u6590\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u5e2e\u4f60\u7239\u7239\u622a\u4f4f\u8fd9\u4e9b\u6b79\u4eba\u3002\u201d\u8bf4\u7740\u8db3\u5e95\u52a0\u52b2\uff0c\u62b1\u7740\u82d7\u82e5\u5170\u6025\u5954\uff0c\u7247\u523b\u95f4\u5df2\u5230\u4e86\u96ea\u5cf0\u4e4b\u4e0b\u3002 \u3000\u3000\u8fd9\u65f6\u4e24\u540d\u8c6a\u5ba2\u5df2\u8e0f\u5230\u5cf0\u4e0b\u5b9e\u5730\uff0c\u5c1a\u6709\u51e0\u540d\u6b63\u6025\u901f\u4e0b\u6e9c\u3002\u80e1\u6590\u653e\u4e0b\u82d7\u82e5\u5170\uff0c\u53cc\u624b\u5404\u63e1\u4e00\u4e2a\u96ea\u56e2\uff0c\u53cc\u81c2\u9f50\u626c\uff0c\u5cf0\u4e0b\u4e24\u540d\u8c6a\u5ba2\u5e94\u58f0\u5012\u5730\u3002 \u3000\u3000\u80e1\u6590\u6b63\u8981\u518d\u63b7\u96ea\u56e2\uff0c\u6295\u51fb\u5c1a\u672a\u7740\u5730\u4e4b\u4eba\uff0c\u5ffd\u542c\u534a\u5c71\u95f4\u6709\u4eba\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u6211\u653e\u4eba\u8d70\u8def\uff0c\u65c1\u4eba\u4e0d\u5fc5\u62e6\u963b\u3002\u201d\u8fd9\u4e24\u53e5\u8bdd\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u7684\u4ece\u534a\u5c71\u91cc\u98d8\u5c06\u4e0b\u6765\uff0c\u6d2a\u4eae\u6e05\u6717\uff0c\u6b63\u662f\u82d7\u4eba\u51e4\u7684\u8bf4\u8bdd\u3002 \u3000\u3000\u82d7\u82e5\u5170\u559c\u53eb\uff1a\u201c\u7239\u7239\uff01\u201d\u80e1\u6590\u542c\u8fd9\u58f0\u97f3\u5c1a\u5728\u767e\u4e08\u4ee5\u5916\uff0c\u4f46\u8bed\u97f3\u9065\u4f20\uff0c\u82e5\u5bf9\u5176\u9762\uff0c\u91d1\u9762\u4f5b\u5185\u529b\u4e4b\u6df1\uff0c\u786e\u662f\u5df2\u6240\u83ab\u53ca\uff0c\u4e0d\u7981\u5927\u4e3a\u94a6\u4f69\uff0c\u53cc\u624b\u4e00\u632f\uff0c\u6263\u5728\u638c\u4e2d\u7684\u96ea\u56e2\u53cc\u53cc\u98de\u51fa\uff0c\u53c8\u4e2d\u8eba\u4f0f\u5728\u5730\u7684\u4e24\u540d\u8c6a\u5ba2\u8eab\u4e0a\uff0c\u4e0d\u8fc7\u4e0a\u6b21\u662f\u6253\u7a74\uff0c\u8fd9\u6b21\u5374\u662f\u89e3\u7a74\u3002\u90a3\u4e8c\u4eba\u8815\u52a8\u4e86\u51e0\u4e0b\uff0c\u6491\u6301\u8d77\u6765\uff0c\u53d1\u8db3\u72c2\u5954\u800c\u53bb\u3002 \u3000\u3000\u4f46\u542c\u534a\u7a7a\u4e2d\u82d7\u4eba\u51e4\u53eb\u9053\uff1a\u201c\u679c\u7136\u597d\u4fca\u529f\u592b\uff0c\u5c31\u53ef\u60dc\u4e0d\u5b66\u597d\u3002\u201d\u8fd9\u5341\u4e8c\u5b57\u8bc4\u8bed\uff0c\u4e00\u5b57\u8fd1\u4f3c\u4e00\u5b57\uff0c\u53ea\u89c1\u4ed6\u53c8\u7626\u53c8\u957f\u7684\u4eba\u5f62\u7f18\u7d22\u76f4\u4e0b\uff0c\u201c\u597d\u201d\u5b57\u4e00\u8131\u53e3\uff0c\u4eba\u5df2\u7ad9\u5728\u80e1\u6590\u8eab\u524d\u3002 \u3000\u3000\u4e24\u4eba\u4e92\u76f8\u5bf9\u89c6\uff0c\u5747\u4e0d\u8bf4\u8bdd\u3002\u4f46\u542c\u56db\u4e0b\u91cc\u4e5e\u4e5e\u64e6\u64e6\uff0c\u5c3d\u662f\u8e0f\u96ea\u4e4b\u58f0\uff0c\u8fd9\u6b21\u4e0a\u5cf0\u7684\u597d\u624b\u4e2d\u7559\u5f97\u6027\u547d\u7684\uff0c\u90fd\u56db\u6563\u8d70\u4e86\u3002 \u3000\u3000\u6708\u5149\u4e0b\u53ea\u89c1\u4e00\u4eba\u4e00\u8ddb\u4e00\u62d0\u7684\u8d70\u8fd1\uff0c\u6b63\u662f\u675c\u5e0c\u5b5f\u675c\u5e84\u4e3b\u3002\u4ed6\u5c06\u4e00\u4e2a\u5c3a\u6765\u957f\u7684\u5305\u88f9\u9012\u7ed9\u80e1\u6590\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u5988\u7684\u9057\u7269\uff0c\u91cc\u9762\u4e00\u4ef6\u4e0d\u5c11\uff0c\u4f60\u6536\u7740\u5427\u3002\u201d\u80e1\u6590\u63a5\u5728\u624b\u4e2d\uff0c\u4f3c\u6709\u4e00\u80a1\u70ed\u6c14\u4ece\u5305\u88f9\u4f20\u5230\u5fc3\u4e2d\uff0c\u5168\u8eab\u4e0d\u7981\u53d1\u6296\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u89c1\u675c\u5e0c\u5b5f\u7684\u80cc\u5f71\u5728\u96ea\u5730\u91cc\u8e52\u8dda\u8fdc\u53bb\uff0c\u5fc3\u60f3\u6b64\u4eba\u6587\u6b66\u5168\u624d\uff0c\u7ed3\u4ea4\u904d\u4e8e\u5929\u4e0b\uff0c\u4e5f\u7b97\u5f97\u662f\u4e2a\u4eba\u6770\uff0c\u4e0e\u81ea\u5df1\u4e8c\u5341\u4f59\u5e74\u7684\u4ea4\u60c5\uff0c\u53ea\u56e0\u4e00\u5ff5\u4e4b\u5dee\uff0c\u843d\u5f97\u8eab\u8d25\u540d\u88c2\uff0c\u5b9e\u662f\u53ef\u60dc\u3002\u4ed6\u4e0d\u77e5\u675c\u5e0c\u5b5f\u4e0e\u80e1\u6590\u4e4b\u6bcd\u6709\u4e2d\u8868\u4e4b\u4eb2\uff0c\u66f4\u4e0d\u77e5\u80e1\u6590\u5c31\u662f\u4e8c\u5341\u591a\u5e74\u6765\u81ea\u5df1\u5ff5\u5ff5\u4e0d\u5fd8\u7684\u5b64\u513f\uff0c\u5f53\u4e0b\u7f13\u7f13\u8f6c\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u5973\u513f\u8eab\u62ab\u7537\u4eba\u888d\u670d\uff0c\u602f\u751f\u751f\u7684\u7ad9\u5728\u96ea\u4e2d\uff0c\u5fc3\u60f3\u773c\u524d\u6b64\u4eba\u867d\u7136\u6551\u4e86\u81ea\u5df1\u6027\u547d\uff0c\u5374\u73b7\u6c61\u4e86\u5973\u513f\u6e05\u767d\uff0c\u5ff5\u53ca\u4ea1\u59bb\u5931\u8282\u4e4b\u4e8b\uff0c\u6068\u4e0d\u5f97\u6740\u5c3d\u5929\u4e0b\u8f7b\u8584\u65e0\u884c\u4e4b\u5f92\uff0c\u4e00\u65f6\u80f8\u53e3\u5982\u8981\u8ff8\u88c2\uff0c\u4f4e\u6c89\u7740\u58f0\u97f3\u9053\uff1a\u201c\u8ddf\u6211\u6765\uff01\u201d\u8bf4\u7740\u8f6c\u8eab\u5927\u8e0f\u6b65\u4fbf\u8d70\u3002 \u3000\u3000\u82d7\u82e5\u5170\u53eb\u9053\uff1a\u201c\u7239\uff0c\u662f\u4ed6\u2026\u2026\u201d\u82d7\u4eba\u51e4\u6c89\u9ed8\u5be1\u8a00\uff0c\u7d20\u6765\u4e0d\u559c\u591a\u8bf4\u4e00\u4e2a\u5b57\uff0c\u4e5f\u4e0d\u559c\u591a\u542c\u4e00\u4e2a\u5b57\uff0c\u6b64\u65f6\u76db\u6012\u4e4b\u4e0b\uff0c\u66f4\u4e0d\u8ba9\u5973\u513f\u591a\u8bf4\u3002\u4ed6\u89c1\u80e1\u6590\u4f38\u624b\u53bb\u62c9\u5973\u513f\uff0c\u559d\u9053\uff1a\u201c\u597d\u5927\u80c6\uff01\u201d\u95ea\u8eab\u6b3a\u8fd1\uff0c\u5de6\u624b\u500f\u5730\u4f38\u51fa\uff0c\u7834\u84b2\u6247\u4e00\u822c\u7684\u624b\u638c\u5df2\u5c06\u80e1\u6590\u5de6\u81c2\u63e1\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u5170\u513f\u4f60\u7559\u5728\u8fd9\u513f\uff0c\u6211\u548c\u8fd9\u4eba\u6709\u51e0\u53e5\u8bdd\u8bf4\u3002\u201d\u8bf4\u7740\u5411\u53f3\u4fa7\u4e00\u5ea7\u5c71\u5cf0\u4e00\u6307\u3002\u90a3\u5c71\u5cf0\u867d\u8fdc\u4e0d\u5982\u7389\u7b14\u5cf0\u90a3\u4e48\u9ad8\u8038\u5165\u4e91\uff0c\u4f46\u9669\u5cfb\u5dcd\u5ce8\uff0c\u6b8a\u4e0d\u5c11\u900a\u3002\u4ed6\u653e\u5f00\u80e1\u6590\u624b\u81c2\uff0c\u5411\u90a3\u5c71\u5cf0\u6025\u5954\u8fc7\u53bb\u3002 \u3000\u3000\u80e1\u6590\u9053\uff1a\u201c\u5170\u59b9\uff0c\u4f60\u7239\u65e2\u8fd9\u822c\u8bf4\uff0c\u6211\u5c31\u8fc7\u53bb\u4e00\u4f1a\u513f\uff0c\u4f60\u5728\u8fd9\u91cc\u7b49\u7740\u3002\u201d\u82d7\u82e5\u5170\u9053\uff1a\u201c\u4f60\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\u3002\u201d\u80e1\u6590\u9053\uff1a\u201c\u522b\u8bf4\u4e00\u4ef6\uff0c\u5c31\u662f\u5343\u4ef6\u4e07\u4ef6\uff0c\u4e5f\u5168\u51ed\u4f60\u5429\u5490\u3002\u201d\u82d7\u82e5\u5170\u9053\uff1a\u201c\u6211\u7239\u82e5\u8981\u4f60\u5a36\u6211\u2026\u2026\u201d\u6700\u540e\u4e24\u5b57\u58f0\u82e5\u868a\u9e23\uff0c\u51e0\u4e0d\u5f97\u95fb\uff0c\u4f4e\u4e0b\u4e86\u5934\uff0c\u7f9e\u4e0d\u53ef\u6291\u3002 \u3000\u3000\u80e1\u6590\u5c06\u9002\u624d\u4ece\u675c\u5e0c\u5b5f\u624b\u91cc\u63a5\u6765\u7684\u5305\u88f9\u4ea4\u5728\u5979\u624b\u91cc\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u5c06\u6211\u5988\u7684\u9057\u7269\u4ea4\u4e8e\u4f60\u624b\u3002\u5929\u4e0b\u518d\u6ca1\u4e00\u4ef6\u6587\u5b9a\u4e4b\u7269\uff0c\u80fd\u6709\u5982\u6b64\u9686\u91cd\u7684\u3002\u201d \u3000\u3000\u82d7\u82e5\u5170\u63a5\u8fc7\u5305\u88f9\uff0c\u8eab\u5b50\u4e0d\u81ea\u7981\u7684\u5fae\u5fae\u98a4\u52a8\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u81ea\u7136\u4fe1\u5f97\u8fc7\u4f60\u3002\u53ea\u662f\u6211\u77e5\u9053\u7239\u7239\u813e\u6c14\uff0c\u82e5\u662f\u4ed6\u607c\u4e86\u4f60\uff0c\u751a\u81f3\u9a82\u4f60\u6253\u4f60\uff0c\u4f60\u90fd\u77a7\u5728\u6211\u8138\u4e0a\uff0c\u4fbf\u8ba9\u4e86\u4ed6\u8fd9\u4e00\u56de\u3002\u201d\u80e1\u6590\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u7b54\u5e94\u4f60\u4e86\u3002\u201d\u8fdc\u8fdc\u671b\u53bb\uff0c\u53ea\u89c1\u82d7\u4eba\u51e4\u7684\u4eba\u5f71\u5728\u767d\u96ea\u5c71\u77f3\u95f4\u500f\u5ffd\u51fa\u6ca1\uff0c\u6b63\u81ea\u6781\u8fc5\u6377\u7684\u5411\u5c71\u5cf0\u5954\u4e0a\uff0c\u5f53\u4e0b\u8f7b\u8f7b\u7684\u5728\u82d7\u82e5\u5170\u7684\u8138\u988a\u4e0a\u4eb2\u4e86\u4e00\u4eb2\uff0c\u63d0\u6c14\u5411\u82d7\u4eba\u51e4\u8eab\u540e\u8ddf\u53bb\u3002 \u3000\u3000\u4ed6\u987a\u7740\u96ea\u5730\u91cc\u7684\u8db3\u8ff9\uff0c\u4e00\u8def\u4e0a\u5c71\uff0c\u8f6c\u4e86\u51e0\u4e2a\u5f2f\uff0c\u4f46\u89c9\u5c71\u9053\u6108\u6765\u6108\u9669\uff0c\u5f53\u4e0b\u4e1d\u6beb\u4e0d\u6562\u5927\u610f\uff0c\u53ea\u6015\u4e00\u4e2a\u5931\u8db3\uff0c\u6454\u5f97\u7c89\u8eab\u788e\u9aa8\u3002\u5954\u5230\u540e\u6765\uff0c\u5c71\u58c1\u95f4\u5168\u662f\u51dd\u51b0\u79ef\u96ea\uff0c\u6ed1\u6e9c\u5f02\u5e38\uff0c\u7adf\u96be\u6709\u4e0b\u8db3\u4e4b\u5904\uff0c\u5fc3\u9053\uff1a\u201c\u82d7\u5927\u4fa0\u6545\u610f\u9009\u6b64\u9669\u9053\uff0c\u5fc5\u662f\u8003\u8f83\u6211\u7684\u6b66\u529f\u6765\u7740\u3002\u201d\u4e8e\u662f\u5c55\u5f00\u8f7b\u529f\uff0c\u5168\u529b\u65bd\u4e3a\uff0c\u5c71\u9053\u8d8a\u9669\uff0c\u4ed6\u7adf\u5954\u5f97\u8d8a\u5feb\u3002 \u3000\u3000\u53c8\u8f6c\u8fc7\u4e00\u4e2a\u5f2f\uff0c\u5ffd\u89c1\u4e00\u6761\u7626\u957f\u7684\u4eba\u5f71\u7ad9\u5728\u5c71\u58c1\u65c1\u4e00\u5757\u51f8\u51fa\u7684\u77f3\u4e0a\uff0c\u8eab\u5f62\u886c\u7740\u6df1\u84dd\u8272\u7684\u5929\u7a7a\uff0c\u72b9\u4f3c\u4e00\u682a\u67af\u69c1\u7684\u8001\u6811\uff0c\u6b63\u662f\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u91d1\u9762\u4f5b\u82d7\u4eba\u51e4\u3002 \u3000\u3000\u80e1\u6590\u4e00\u6014\uff0c\u6025\u5fd9\u505c\u6b65\uff0c\u53cc\u8db3\u4f7f\u51fa\u201c\u5343\u65a4\u5760\u201d\u529f\u592b\uff0c\u5c06\u8eab\u5b50\u7262\u7262\u5b9a\u4f4f\u5ced\u58c1\u4e4b\u65c1\u3002\u82d7\u4eba\u51e4\u4f4e\u6c89\u7740\u55d3\u5b50\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6709\u79cd\u8ddf\u6765\u3002\u4e0a\u5427\uff01\u201d\u4ed6\u80cc\u5411\u6708\u5149\uff0c\u8138\u4e0a\u9634\u6c89\u6c89\u7684\u77a7\u4e0d\u6e05\u695a\u795e\u8272\u3002 \u3000\u3000\u80e1\u6590\u5598\u4e86\u53e3\u6c14\uff0c\u9762\u5bf9\u7740\u8fd9\u4e2a\u81ea\u5df1\u751f\u5e73\u60f3\u8fc7\u51e0\u5343\u51e0\u4e07\u904d\u4e4b\u4eba\uff0c\u4e00\u65f6\u4e4b\u95f4\u7adf\u5c14\u6ca1\u4e86\u4e3b\u610f\uff1a \u3000\u3000\u201c\u4ed6\u662f\u6211\u6740\u7236\u4ec7\u4eba\uff0c\u53ef\u662f\u4ed6\u53c8\u662f\u82e5\u5170\u7684\u7236\u4eb2\u3002 \u3000\u3000\u201c\u4ed6\u5bb3\u5f97\u6211\u4e00\u751f\u5b64\u82e6\uff0c\u4f46\u542c\u5e73\u56db\u53d4\u8bf4\uff0c\u4ed6\u8c6a\u4fa0\u4ed7\u4e49\uff0c\u59cb\u7ec8\u6ca1\u5bf9\u4e0d\u8d77\u6211\u7684\u7239\u5988\u3002 \u3000\u3000\u201c\u4ed6\u53f7\u79f0\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\uff0c\u6b66\u529f\u827a\u4e1a\uff0c\u4e3e\u4e16\u65e0\u53cc\uff0c\u4f46\u6211\u504f\u4e0d\u4fe1\u670d\uff0c\u5012\u8981\u8bd5\u8bd5\u662f\u4ed6\u5f3a\u5462\u8fd8\u662f\u6211\u5f3a\uff1f \u3000\u3000\u201c\u4ed6\u82d7\u5bb6\u4e0e\u6211\u80e1\u5bb6\u7d2f\u4e16\u4e3a\u4ec7\uff0c\u767e\u4f59\u5e74\u6765\u76f8\u65ab\u4e0d\u4f11\uff0c\u7136\u800c\u4ed6\u4e0d\u4f20\u5973\u513f\u6b66\u529f\uff0c\u662f\u4e0d\u662f\u771f\u7684\u8981\u5c06\u8fd9\u573a\u4e16\u4ec7\u81f3\u4ed6\u800c\u89e3\uff1f \u3000\u3000\u201c\u9002\u624d\u6211\u6551\u4e86\u4ed6\u7684\u6027\u547d\uff0c\u53ef\u662f\u4ed6\u773c\u89c1\u6211\u4e0e\u82e5\u5170\u540c\u5e8a\u5171\u88ab\uff0c\u8ba4\u5b9a\u6211\u5bf9\u4ed6\u5973\u513f\u8f7b\u8584\u65e0\u793c\uff0c\u4e0d\u77e5\u80fd\u5426\u76f8\u8c05\uff1f\u201d \u3000\u3000\u82d7\u4eba\u51e4\u89c1\u80e1\u6590\u795e\u60c5\u7c97\u8c6a\uff0c\u866c\u9aef\u621f\u5f20\uff0c\u4f9d\u7a00\u662f\u5f53\u5e74\u80e1\u4e00\u5200\u7684\u6a21\u6837\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u52a8\uff0c\u4f46\u968f\u5373\u60f3\u8d77\uff0c\u80e1\u4e00\u5200\u4e4b\u5b50\u65e9\u5df2\u4e3a\u4eba\u6240\u5bb3\uff0c\u6295\u5728\u6ca7\u5dde\u6cb3\u4e2d\uff0c\u6b64\u4eba\u5bb9\u8c8c\u76f8\u4f3c\uff0c\u53ea\u662f\u5076\u7136\u5de7\u5408\uff0c\u60f3\u8d77\u4ed6\u6b3a\u8fb1\u81ea\u5df1\u7684\u72ec\u751f\u7231\u5973\uff0c\u6012\u706b\u4e0a\u51b2\uff0c\u5de6\u638c\u4e00\u626c\uff0c\u53f3\u62f3\u547c\u7684\u4e00\u58f0\uff0c\u51b2\u62f3\u76f4\u51fa\uff0c\u731b\u5f80\u80e1\u6590\u80f8\u53e3\u51fb\u53bb\u3002 \u3000\u3000\u80e1\u6590\u4e0e\u4ed6\u76f8\u8ddd\u4e0d\u8fc7\u6570\u5c3a\uff0c\u89c1\u4ed6\u6325\u62f3\u6253\u6765\uff0c\u52bf\u9053\u5a01\u731b\u65e0\u6bd4\uff0c\u53ea\u5f97\u51fa\u638c\u6321\u67b6\u3002\u4e24\u4eba\u62f3\u638c\u76f8\u4ea4\uff0c\u8eab\u5b50\u90fd\u662f\u4e00\u9707\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u81ea\u90a3\u5e74\u4e0e\u80e1\u4e00\u5200\u6bd4\u6b66\u4ee5\u6765\uff0c\u4e8c\u5341\u4f59\u5e74\u6765\u4ece\u672a\u9047\u5230\u654c\u624b\uff0c\u6b64\u65f6\u81ea\u5df1\u4e00\u62f3\u88ab\u80e1\u6590\u5316\u89e3\uff0c\u4f46\u89c9\u5bf9\u65b9\u638c\u6cd5\u7cbe\u5999\uff0c\u5185\u529b\u6df1\u539a\uff0c\u4e0d\u7981\u654c\u5ffe\u4e4b\u5fc3\u5927\u589e\uff0c\u8fd0\u638c\u6210\u98ce\uff0c\u8fde\u8fdb\u4e09\u62db\u3002 \u3000\u3000\u80e1\u6590\u4e00\u4e00\u62c6\u5f00\uff0c\u5230\u7b2c\u4e09\u62db\u4e0a\uff0c\u82d7\u4eba\u51e4\u638c\u529b\u731b\u6781\uff0c\u4ed6\u867d\u6025\u95ea\u907f\u5f00\uff0c\u4f46\u8eab\u5b50\u8fde\u6643\u51e0\u6643\uff0c\u9669\u9669\u5815\u4e0b\u5cf0\u53bb\uff0c\u5fc3\u9053\uff1a\u201c\u82e5\u518d\u76f8\u8ba9\uff0c\u975e\u7ed9\u4ed6\u903c\u5f97\u6454\u6b7b\u4e0d\u53ef\u3002\u201d\u773c\u89c1\u82d7\u4eba\u51e4\u5de6\u8db3\u98de\u8d77\uff0c\u6025\u5411\u81ea\u5df1\u5c0f\u8179\u8e22\u5230\uff0c\u5f53\u5373\u53f3\u62f3\u5de6\u638c\uff0c\u9f50\u5411\u5bf9\u65b9\u9762\u95e8\u62cd\u51fb\uff0c\u8fd9\u4e00\u62db\u653b\u654c\u4e4b\u4e0d\u5f97\u4e0d\u6551\uff0c\u662f\u62c6\u89e3\u4ed6\u5de6\u8db3\u4e00\u8e22\u7684\u9ad8\u62db\u3002 \u3000\u3000\u80e1\u6590\u8fd9\u4e00\u62db\u7528\u7684\u867d\u662f\u91cd\u624b\uff0c\u7a76\u7adf\u672a\u51fa\u5168\u529b\u3002\u4f46\u9ad8\u624b\u6bd4\u6b66\uff0c\u534a\u70b9\u5bb9\u8ba9\u4e0d\u5f97\uff0c\u82d7\u4eba\u51e4\u4f38\u81c2\u76f8\u683c\uff0c\u4f7f\u7684\u5374\u662f\u5341\u6210\u529b\u3002\u56db\u81c2\u76f8\u4ea4\uff0c\u54af\u54af\u4e24\u54cd\uff0c\u80e1\u6590\u53ea\u89c9\u80f8\u53e3\u9690\u9690\u53d1\u75db\uff0c\u6025\u5fd9\u8fd0\u6c14\u76f8\u62b5\u3002\u5c82\u77e5\u82d7\u4eba\u51e4\u7684\u62f3\u6cd5\u521a\u731b\u65e0\u6bd4\uff0c\u4e00\u5360\u4e0a\u98ce\uff0c\u62f3\u52bf\u6108\u6765\u6108\u5f3a\uff0c\u518d\u4e0d\u5bb9\u654c\u4eba\u6709\u5598\u606f\u4e4b\u673a\u3002\u82e5\u5728\u5e73\u5730\uff0c\u80e1\u6590\u539f\u53ef\u8df3\u51fa\u5708\u5b50\uff0c\u9003\u5f00\u6570\u6b65\uff0c\u907f\u4e86\u4ed6\u638c\u98ce\u7684\u7b3c\u7f69\uff0c\u7136\u540e\u53cd\u8eab\u518d\u6597\uff0c\u4f46\u5728\u8fd9\ue40c\u5d16\u5ced\u58c1\u4e4b\u5904\uff0c\u5b9e\u662f\u65e0\u5730\u53ef\u9000\uff0c\u53ea\u5f97\u54ac\u7d27\u7259\u5173\uff0c\u4f7f\u51fa\u201c\u6625\u8695\u638c\u6cd5\u201d\uff0c\u5bc6\u5bc6\u62a4\u4f4f\u5168\u8eab\u5404\u5904\u8981\u5bb3\u3002 \u3000\u3000\u8fd9\u201c\u6625\u8695\u638c\u6cd5\u201d\u62db\u62db\u5168\u662f\u5b88\u52bf\uff0c\u51fa\u624b\u5947\u77ed\uff0c\u62ac\u624b\u8e22\u8db3\uff0c\u5168\u4e0d\u51fa\u534a\u5c3a\u4e4b\u5916\uff0c\u4f46\u62db\u672f\u7ef5\u5bc6\u65e0\u6bd4\uff0c\u5468\u8eab\u59cb\u7ec8\u4e0d\u9732\u534a\u70b9\u7834\u7efd\u3002\u8fd9\u8def\u638c\u6cd5\u539f\u672c\u7528\u4e8e\u906d\u4eba\u56f4\u653b\u800c\u5927\u5904\u52a3\u52bf\u4e4b\u65f6\uff0c\u4e0d\u6c42\u6709\u529f\uff0c\u4f46\u6c42\u65e0\u8fc7\uff0c\u867d\u5b88\u5f97\u7d27\u5bc6\uff0c\u5374\u6709\u4e00\u4e2a\u6781\u5927\u4e0d\u597d\u5904\uff0c\u4e00\u5f00\u5934\u5373\u662f\u201c\u7acb\u4e8e\u4e0d\u80dc\u4e4b\u5730\u201d\uff0c\u540d\u76ee\u53eb\u505a\u201c\u6625\u8695\u638c\u6cd5\u201d\uff0c\u786e\u662f\u4f5c\u8327\u81ea\u7f1a\uff0c\u4e0d\u80fd\u53cd\u51fb\uff0c\u4e0d\u8bba\u654c\u4eba\u62db\u6570\u4e2d\u9732\u51fa\u5982\u4f55\u91cd\u5927\u7834\u7efd\uff0c\u82e5\u975e\u6539\u53d8\u638c\u6cd5\uff0c\u6c38\u96be\u514b\u654c\u5236\u80dc\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u4e00\u62db\u7d27\u4f3c\u4e00\u62db\uff0c\u773c\u89c1\u5bf9\u65b9\u60c5\u52bf\u6076\u52a3\uff0c\u4f46\u4e0d\u8bba\u81ea\u5df1\u5982\u4f55\u5f3a\u653b\u731b\u51fb\uff0c\u80e1\u6590\u5fc5\u6709\u65b9\u6cd5\u89e3\u6551\uff0c\u53ea\u662f\u4ed6\u4f46\u5b88\u4e0d\u653b\uff0c\u81ea\u5df1\u5374\u65e0\u5371\u9669\uff0c\u5f53\u4e0b\u4e0d\u987e\u9632\u5fa1\uff0c\u5341\u5206\u529b\u6c14\u5168\u7528\u5728\u653b\u575a\u7834\u654c\u4e4b\u4e0a\u3002 \u3000\u3000\u6597\u5230\u9163\u5904\uff0c\u82d7\u4eba\u51e4\u4e00\u62f3\u6253\u51fa\uff0c\u80e1\u6590\u4e00\u907f\uff0c\u90a3\u62f3\u6253\u5728\u5c71\u58c1\u4e4b\u4e0a\uff0c\u51b0\u51cc\u98de\u6e85\uff0c\u4e00\u5c0f\u5757\u5c04\u4e0a\u4e86\u4ed6\u5de6\u773c\u3002\u773c\u76ae\u6781\u662f\u67d4\u8f6f\uff0c\u8fd9\u4e00\u4e0b\u53c8\u662f\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\uff0c\u96be\u4ee5\u9632\u5907\uff0c\u80e1\u6590\u4f46\u89c9\u773c\u4e0a\u5267\u75db\uff0c\u867d\u4e0d\u6562\u4f38\u624b\u53bb\u63c9\uff0c\u62f3\u811a\u4e0a\u603b\u662f\u4e00\u7f13\u3002\u82d7\u4eba\u51e4\u4e58\u52bf\u62a2\u8fdb\uff0c\u9760\u8eab\u5c71\u58c1\uff0c\u5df2\u5c06\u80e1\u6590\u903c\u5728\u5916\u6863\u3002 \u3000\u3000\u6b64\u65f6\u5f3a\u5f31\u4f18\u52a3\u4e4b\u52bf\u5df2\u5224\uff0c\u80e1\u6590\u534a\u8eab\u51cc\u7a7a\uff0c\u53ea\u8981\u8db3\u5e95\u5fae\u51fa\uff0c\u8eab\u5b50\u7a0d\u6709\u4e0d\u7a33\uff0c\u7acb\u65f6\u6389\u4e0b\u5c71\u8c37\uff0c\u82d7\u4eba\u51e4\u5374\u662f\u80cc\u5fc3\u5411\u7740\u5c71\u58c1\uff0c\u62db\u62db\u903c\u8feb\u5bf9\u624b\u786c\u63a5\u786c\u67b6\u3002\u80e1\u6590\u6781\u662f\u673a\u4f36\uff0c\u5374\u4e5f\u504f\u4e0d\u4e0a\u8fd9\u4e2a\u5f53\uff0c\u51fa\u624b\u67d4\u97e7\u6ed1\u6e9c\uff0c\u5c3d\u529b\u5316\u89e3\u6765\u52bf\uff0c\u51b3\u4e0d\u6b63\u9762\u76f8\u63a5\u3002 \u3000\u3000\u4e24\u4eba\u6b66\u529f\u672c\u5728\u4ef2\u4f2f\u4e4b\u95f4\uff0c\u5e73\u624b\u76f8\u6597\uff0c\u80e1\u6590\u5df2\u672a\u5fc5\u80fd\u80dc\uff0c\u73b0\u4e0b\u52a0\u4e0a\u8bb8\u591a\u4e0d\u5229\u4e4b\u5904\uff0c\u5982\u4f55\u80fd\u591f\u6301\u4e45\uff1f\u53c8\u6597\u6570\u62db\uff0c\u82d7\u4eba\u51e4\u5ffd\u5730\u8dc3\u8d77\uff0c\u8fde\u8e22\u4e09\u811a\u3002\u80e1\u6590\u6025\u95ea\u76f8\u907f\uff0c\u4f46\u89c1\u5bf9\u624b\u7b2c\u4e09\u811a\u8e22\u8fc7\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u76f4\u51fb\u81ea\u5df1\u80f8\u53e3\u3002\u8fd9\u4e24\u638c\u96be\u4ee5\u5316\u89e3\uff0c\u81ea\u5df1\u7ad9\u7acb\u4e4b\u5904\u53c8\u662f\u65e0\u53ef\u907f\u8ba9\uff0c\u53ea\u5f97\u4e5f\u662f\u53cc\u638c\u62cd\u51fa\uff0c\u786c\u63a5\u6765\u62db\u3002 \u3000\u3000\u56db\u62f3\u76f8\u4ea4\uff0c\u82d7\u4eba\u51e4\u5927\u559d\u4e00\u58f0\uff0c\u52b2\u529b\u76f4\u900f\u638c\u5fc3\u3002\u80e1\u6590\u8eab\u5b50\u4e00\u6643\uff0c\u6025\u5fd9\u8fd0\u52b2\u53cd\u51fb\u3002\u4e24\u4eba\u90fd\u5c06\u6bd5\u751f\u529f\u529b\u8fd0\u5230\u4e86\u638c\u4e0a\uff0c\u8fd9\u662f\u786c\u78b0\u786c\u7684\u6bd4\u62da\uff0c\u534a\u70b9\u53d6\u5de7\u4e0d\u5f97\u3002\u4e24\u4eba\u6c14\u51dd\u4e39\u7530\uff0c\u56db\u76ee\u4e92\u89c6\uff0c\u7adf\u662f\u50f5\u4f4f\u4e86\u518d\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u89c1\u4ed6\u6b66\u529f\u4e86\u5f97\uff0c\u4e0d\u7531\u5f97\u6697\u6697\u60ca\u5fc3\uff1a\u201c\u8fd1\u5e74\u6765\u5c11\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\uff0c\u7adf\u4e0d\u77e5\u6b66\u6797\u4e2d\u51fa\u4e86\u8fd9\u7b49\u5389\u5bb3\u4eba\u7269\uff01\u201d\u53cc\u817f\u7a0d\u5f2f\uff0c\u80cc\u810a\u5df2\u9760\u4e0a\u5c71\u58c1\uff0c\u4e00\u6536\u4e00\u5410\uff0c\u5148\u5c06\u80e1\u6590\u7684\u638c\u529b\u5f15\u5c06\u8fc7\u6765\uff0c\u7136\u540e\u501f\u7740\u5c71\u58c1\u4e4b\u529b\uff0c\u731b\u63a8\u51fa\u53bb\uff0c\u559d\u9053\uff1a\u201c\u4e0b\u53bb\uff01\u201d \u3000\u3000\u8fd9\u4e00\u63a8\u672c\u5c31\u529b\u9053\u5f3a\u52b2\u65e0\u6bd4\uff0c\u518d\u52a0\u4e0a\u501f\u4e86\u5c71\u58c1\u7684\u53cd\u6fc0\uff0c\u66f4\u662f\u96be\u4ee5\u62b5\u6321\uff0c\u80e1\u6590\u8eab\u5b50\u8fde\u6643\uff0c\u5de6\u8db3\u5df2\u7136\u51cc\u7a7a\u3002\u4f46\u4ed6\u4e0b\u76d8\u4e4b\u7a33\uff0c\u5b9e\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u53f3\u8db3\u5728\u5c71\u5d16\u8fb9\u7262\u7262\u5b9a\u4f4f\uff0c\u5b9b\u4f3c\u94c1\u94f8\u4e00\u822c\u3002\u82d7\u4eba\u51e4\u8fde\u50ac\u4e09\u6b21\u52b2\uff0c\u4e5f\u53ea\u80fd\u63a8\u5f97\u4ed6\u4e0a\u8eab\u6643\u52a8\uff0c\u5374\u4e0d\u80fd\u4f7f\u4ed6\u53f3\u8db3\u79fb\u52a8\u534a\u5206\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u6697\u6697\u60ca\u4f69\uff1a\u201c\u5982\u6b64\u529f\u592b\uff0c\u4e5f\u53ef\u7b97\u5f97\u662f\u65f7\u4e16\u5c11\u6709\uff0c\u53ea\u53ef\u60dc\u8d70\u4e0a\u4e86\u90aa\u8def\u3002\u4ed6\u5e74\u5c81\u5c1a\u8f7b\uff0c\u4eca\u65e5\u82e5\u4e0d\u6740\u4ed6\uff0c\u65e5\u540e\u9047\u4e0a\uff0c\u672a\u5fc5\u518d\u662f\u4ed6\u654c\u624b\u3002\u4ed6\u6043\u5f3a\u4e3a\u6076\uff0c\u4e16\u4e0a\u6709\u8c01\u80fd\u5236\uff1f\u201d\u60f3\u5230\u6b64\u5904\uff0c\u7a81\u7136\u95f4\u5de6\u8db3\u4e00\u767b\uff0c\u4e00\u62db\u201c\u7834\u7891\u811a\u201d\uff0c\u731b\u5f80\u80e1\u6590\u53f3\u819d\u4e0a\u8e39\u53bb\u3002 \u3000\u3000\u80e1\u6590\u5168\u9760\u5355\u8db3\u652f\u6301\uff0c\u773c\u89c1\u4ed6\u4e00\u811a\u8e39\u5230\uff0c\u65e0\u53ef\u95ea\u907f\uff0c\u53f9\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff0c\u6211\u4eca\u65e5\u7ec8\u7a76\u547d\u4e27\u4ed6\u624b\u3002\u201d\u5371\u96be\u4e0b\u6b7b\u4e2d\u6c42\u751f\uff0c\u53f3\u8db3\u4e00\u767b\uff0c\u8eab\u5b50\u6597\u7136\u62d4\u8d77\u4e08\u4f59\uff0c\u4e00\u4e2a\u9e5e\u5b50\u7ffb\u8eab\uff0c\u51cc\u7a7a\u4e0b\u51fb\u3002\u82d7\u4eba\u51e4\u9053\uff1a\u201c\u597d\uff01\u201d\u80a9\u5934\u4e00\u6446\uff0c\u649e\u4e86\u51fa\u53bb\u3002\u80e1\u6590\u53cc\u62f3\u6253\u4e2d\u4e86\u4ed6\u80a9\u5934\uff0c\u5374\u88ab\u4ed6\u5de8\u529b\u4e00\u649e\uff0c\u8dcc\u51fa\u60ac\u5d16\uff0c\u5411\u4e0b\u76f4\u5815\u3002 \u3000\u3000\u80e1\u6590\u60e8\u7136\u4e00\u7b11\uff0c\u4e00\u4e2a\u5ff5\u5934\u5982\u7535\u5149\u822c\u5728\u5fc3\u4e2d\u4e00\u95ea\uff1a\u201c\u6211\u81ea\u5e7c\u5b64\u82e6\uff0c\u53ef\u662f\u4e34\u6b7b\u4e4b\u524d\u5f97\u8499\u5170\u59b9\u503e\u5fc3\uff0c\u4e5f\u81ea\u4e0d\u6789\u4e86\u8fd9\u4e00\u751f\u3002\u201d\u7a81\u7136\u81c2\u4e0a\u4e00\u7d27\uff0c\u4e0b\u5815\u4e4b\u52bf\u767b\u65f6\u6b62\u4f4f\uff0c\u539f\u6765\u82d7\u4eba\u51e4\u5df2\u6293\u4f4f\u4ed6\u624b\u81c2\uff0c\u5c06\u4ed6\u62c9\u4e86\u4e0a\u6765\uff0c\u559d\u9053\uff1a\u201c\u4f60\u66fe\u6551\u6211\u6027\u547d\uff0c\u73b0\u4e0b\u9976\u4f60\u76f8\u62a5\u3002\u4e00\u547d\u6362\u4e00\u547d\uff0c\u8c01\u4e5f\u4e0d\u4e8f\u8d1f\u4e86\u8c01\u3002\u6765\uff0c\u54b1\u4eec\u91cd\u65b0\u6253\u8fc7\u3002\u201d\u8bf4\u7740\u7ad9\u5728\u4e00\u65c1\uff0c\u4e0e\u80e1\u6590\u5e76\u6392\u800c\u7acb\uff0c\u4e0d\u518d\u5360\u501a\u58c1\u4e4b\u5229\u3002 \u3000\u3000\u80e1\u6590\u6b7b\u91cc\u9003\u751f\uff0c\u5df2\u65e0\u6597\u5fd7\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u662f\u82d7\u5927\u4fa0\u654c\u624b\uff0c\u4f55\u5fc5\u518d\u6bd4\uff1f\u82d7\u5927\u4fa0\u8981\u5982\u4f55\u5904\u7f6e\uff0c\u665a\u8f88\u542c\u51ed\u5429\u5490\u5c31\u662f\u3002\u201d \u3000\u3000\u82d7\u4eba\u51e4\u76b1\u7709\u9053\uff1a\u201c\u4f60\u4e0a\u624b\u65f6\u6709\u610f\u76f8\u8ba9\uff0c\u96be\u9053\u6211\u5c31\u4e0d\u77e5\uff1f\u4f60\u6b3a\u82d7\u4eba\u51e4\u5e74\u8001\u529b\u8870\u3002\u4e0d\u662f\u4f60\u5bf9\u624b\u4e48\uff1f\u201d\u80e1\u6590\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u6562\u3002\u201d\u82d7\u4eba\u51e4\u559d\u9053\uff1a\u201c\u51fa\u624b\uff01\u201d\u80e1\u6590\u8981\u89e3\u91ca\u4e0e\u82d7\u82e5\u5170\u540c\u5e8a\u5171\u887e\uff0c\u5b9e\u662f\u51fa\u4e8e\u610f\u5916\uff0c\u51b3\u975e\u5b58\u5fc3\u8f7b\u8584\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u90a3\u53a2\u623f\u4e4b\u4e2d\u2026\u2026\u201d \u3000\u3000\u82d7\u4eba\u51e4\u542c\u4ed6\u63d0\u53ca\u201c\u53a2\u623f\u201d\u4e8c\u5b57\uff0c\u6012\u706b\u5927\u70bd\uff0c\u81c2\u9762\u5c31\u662f\u4e00\u638c\u3002\u80e1\u6590\u53ea\u5f97\u63a5\u4f4f\uff0c\u7ecf\u8fc7\u4e86\u9002\u624d\u4e4b\u4e8b\uff0c\u77e5\u9053\u53ea\u8981\u5fae\u4e00\u9000\u8ba9\uff0c\u7acb\u65f6\u53c8\u7ed9\u4ed6\u638c\u529b\u7f69\u4f4f\uff0c\u53ea\u5f97\u5168\u529b\u65bd\u4e3a\u3002\u4e24\u4eba\u5404\u5c55\u5e73\u751f\u7edd\u827a\uff0c\u5728\u5c71\u5d16\u8fb9\u62f3\u6765\u811a\u5f80\uff0c\u6597\u667a\u6597\u529b\uff0c\u6597\u62f3\u6cd5\uff0c\u6597\u5185\u529f\uff0c\u62c6\u4e86\u4e09\u767e\u4f59\u62db\uff0c\u7adf\u662f\u96be\u5206\u80dc\u8d25\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u6108\u6597\u5fc3\u4e0b\u6108\u7591\uff0c\u4e0d\u4f4f\u60f3\u5230\u5f53\u5e74\u5728\u6ca7\u5dde\u4e0e\u80e1\u4e00\u5200\u6bd4\u6b66\u4e4b\u4e8b\uff0c\u5ffd\u5730\u5411\u540e\u8dc3\u5f00\u4e24\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u4e14\u4f4f\uff01\u4f60\u53ef\u8bc6\u5f97\u80e1\u4e00\u5200\u4e48\uff1f\u201d \u3000\u3000\u80e1\u6590\u542c\u4ed6\u63d0\u5230\u4ea1\u7236\u4e4b\u540d\uff0c\u60b2\u6124\u4ea4\u96c6\uff0c\u54ac\u7259\u9053\uff1a\u201c\u80e1\u5927\u4fa0\u4e43\u524d\u8f88\u82f1\u96c4\uff0c\u4e0d\u5e78\u4e3a\u5978\u4eba\u6240\u5bb3\u3002\u6211\u82e5\u6709\u798f\u6c14\u80fd\u5f97\u4ed6\u6559\u8bf2\u51e0\u53e5\uff0c\u7acb\u65f6\u6b7b\u4e86\uff0c\u4e5f\u6240\u7518\u5fc3\u3002\u201d \u3000\u3000\u82d7\u4eba\u51e4\u5fc3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u80e1\u4e00\u5200\u53bb\u4e16\u5df2\u4e8c\u5341\u4e03\u5e74\u3002\u773c\u524d\u6b64\u4eba\u4e5f\u4e0d\u8fc7\u4e8c\u5341\u591a\u5c81\uff0c\u7109\u80fd\u76f8\u8bc6\uff1f\u4ed6\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u751a\u597d\uff0c\u82e5\u4e0d\u662f\u4ed6\u6b3a\u8fb1\u5170\u513f\uff0c\u5355\u51ed\u8fd9\u51e0\u53e5\u8bdd\uff0c\u6211\u5c31\u4ea4\u4e86\u4ed6\u8fd9\u4e2a\u670b\u53cb\u3002\u201d\u987a\u624b\u5728\u5c71\u8fb9\u6298\u4e0b\u4e24\u6839\u575a\u786c\u7684\u6811\u679d\uff0c\u6382\u4e86\u4e00\u6382\uff0c\u91cd\u91cf\u76f8\u82e5\uff0c\u5c06\u4e00\u6839\u629b\u7ed9\u80e1\u6590\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u62f3\u811a\u96be\u5206\u9ad8\u4e0b\uff0c\u5175\u5203\u4e0a\u518d\u51b3\u751f\u6b7b\u3002\u201d\u8bf4\u7740\u6811\u679d\u4e00\u63a2\uff0c\u5de6\u624b\u634f\u4e86\u5251\u8bc0\uff0c\u6811\u679d\u8d70\u504f\u950b\u523a\u51fa\uff0c\u4f7f\u7684\u6b63\u662f\u5929\u4e0b\u65e0\u53cc\u3001\u6b66\u6797\u7edd\u827a\u7684\u201c\u82d7\u5bb6\u5251\u6cd5\u201d\u3002\u867d\u662f\u4e00\u6839\u5c0f\u5c0f\u6811\u679d\uff0c\u4f46\u523a\u51fa\u65f6\u52bf\u5939\u52b2\u98ce\uff0c\u53c8\u72e0\u53c8\u51c6\uff0c\u8981\u662f\u7ed9\u5c16\u68a2\u523a\u4e0a\u4e86\uff0c\u5b9e\u4e5f\u4e0e\u4e2d\u5251\u65e0\u5f02\u3002 \u3000\u3000\u80e1\u6590\u89c1\u6765\u52bf\u5389\u5bb3\uff0c\u54ea\u6562\u6709\u4e1d\u6beb\u6020\u5ffd\uff0c\u6811\u679d\u4e00\u6446\uff0c\u5411\u4e0a\u6a2a\u683c\uff0c\u8fd9\u4e00\u683c\u521a\u4e2d\u6709\u67d4\uff0c\u786e\u662f\u540d\u5bb6\u624b\u6cd5\u3002\u82d7\u4eba\u51e4\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u4ed6\u6b66\u529f\u4e0e\u80e1\u4e00\u5200\u8fd9\u822c\u76f8\u4f3c\uff1f\u201d\u4f46\u9ad8\u624b\u76f8\u6597\uff0c\u5200\u5251\u4e00\u4ea4\uff0c\u540e\u7740\u7ef5\u7ef5\u800c\u81f3\uff0c\u51b3\u4e0d\u5bb9\u4ed6\u6709\u4e1d\u6beb\u601d\u7d22\u8fdf\u7591\u7684\u4f59\u88d5\uff0c\u4f46\u89c1\u80e1\u6590\u6811\u5200\u683c\u8fc7\uff0c\u8ddf\u7740\u63d0\u624b\u4e0a\u64a9\uff0c\u82d7\u4eba\u51e4\u6325\u6811\u5251\u53cd\u524a\uff0c\u6559\u4ed6\u4e0d\u5f97\u4e0d\u56de\u5200\u76f8\u6551\u3002 \u3000\u3000\u8fd9\u4e00\u756a\u6076\u6597\uff0c\u80e1\u6590\u4e00\u751f\u4ece\u672a\u9047\u8fc7\u3002\u4ed6\u6b66\u529f\u5168\u662f\u51ed\u7740\u7236\u4eb2\u4f20\u4e0b\u9057\u4e66\u4fee\u4e60\u800c\u6210\uff0c\u62db\u6570\u867d\u7136\u7cbe\u5999\uff0c\u5b9e\u6218\u7ecf\u9a8c\u6bd5\u7adf\u6b20\u7f3a\uff0c\u529f\u529b\u706b\u5019\u56e0\u5e74\u5c81\u6240\u9650\uff0c\u4ea6\u672a\u81fb\u4e0a\u4e58\uff0c\u597d\u5728\u5e74\u8f7b\u529b\u58ee\uff0c\u7cbe\u529b\u8fdc\u8fc7\u5bf9\u65b9\uff0c\u662f\u4ee5\u6570\u5341\u62db\u4e2d\u6253\u5f97\u96be\u89e3\u96be\u5206\u3002\u4e24\u4eba\u8fed\u9047\u9669\u62db\uff0c\u4f46\u5747\u5728\u6781\u5371\u6025\u4e0b\u4ee5\u5de7\u5999\u62db\u6570\u62c6\u5f00\u3002\u80e1\u6590\u594b\u529b\u62c6\u6597\uff0c\u5fc3\u4e2d\u4f69\u670d\uff1a\u201c\u91d1\u9762\u4f5b\u82d7\u5927\u4fa0\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u82e5\u4ed6\u5e74\u8f7b\u4e8c\u5341\u5c81\uff0c\u6211\u65e9\u5df2\u8d25\u4e86\u3002\u96be\u602a\u5f53\u5e74\u4ed6\u548c\u6211\u7239\u7239\u80fd\u6253\u6210\u5e73\u624b\uff0c\u5f53\u771f\u82f1\u96c4\u4e86\u5f97\u3002\u201d \u3000\u3000\u4e24\u4eba\u5747\u77e5\u8981\u51ed\u62db\u6570\u4e0a\u80dc\u5f97\u5bf9\u65b9\uff0c\u6781\u662f\u4e0d\u6613\uff0c\u4f46\u53ea\u987b\u81ea\u5df1\u80cc\u810a\u4e00\u9760\u4e0a\u5c71\u58c1\uff0c\u5360\u4e86\u5730\u5229\uff0c\u8fd9\u4e00\u573a\u6bd4\u62da\u5c31\u662f\u80dc\u4e86\u3002\u56e0\u6b64\u90fd\u662f\u7aed\u529b\u8981\u5c06\u5bf9\u65b9\u903c\u5411\u5916\u56f4\uff0c\u4e89\u593a\u9760\u8fd1\u5c71\u58c1\u7684\u5730\u52bf\u3002\u4f46\u4e24\u4eba\u62db\u62db\u6263\u5f97\u7d27\u5bc6\uff0c\u53ea\u8981\u5411\u5185\u7f18\u8e0f\u8fdb\u534a\u6b65\uff0c\u7acb\u65f6\u4fbf\u53d7\u5bf9\u65b9\u5200\u5251\u4e4b\u4f24\u3002 \u3000\u3000\u6597\u5230\u9163\u5904\uff0c\u82d7\u4eba\u51e4\u4f7f\u4e00\u62db\u201c\u9ec4\u9f99\u8f6c\u8eab\u5410\u987b\u52bf\u201d\u75be\u523a\u5bf9\u65b9\u80f8\u53e3\uff0c\u773c\u89c1\u4ed6\u65e0\u5904\u95ea\u907f\uff0c\u800c\u6811\u5200\u780d\u5728\u5916\u6863\uff0c\u66f4\u662f\u4e0d\u53ca\u56de\u6551\u3002 \u3000\u3000\u80e1\u6590\u5403\u4e86\u4e00\u60ca\uff0c\u5fd9\u4f38\u5de6\u624b\u5728\u4ed6\u6811\u679d\u4e0a\u6a2a\u62e8\uff0c\u53f3\u624b\u4e00\u62db\u201c\u4f0f\u864e\u5f0f\u201d\u5288\u51fa\u3002\u82d7\u4eba\u51e4\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u597d\uff01\u201d\u6811\u5251\u4e00\u6296\u3002\u80e1\u6590\u5de6\u624b\u624b\u6307\u5267\u75db\uff0c\u6025\u5fd9\u6492\u624b\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u8e0f\u4e0a\u534a\u6b65\uff0c\u6b63\u8981\u523a\u51fa\u4e00\u62db\u201c\u4e0a\u6b65\u6458\u661f\u5f0f\u201d\uff0c\u54ea\u77e5\u5d16\u8fb9\u575a\u58c1\u7ed9\u4e8c\u4eba\u8e0f\u5f97\u4e45\u4e86\uff0c\u7adf\u6e10\u6e10\u677e\u88c2\u7194\u5316\uff0c\u4ed6\u5251\u52bf\u5411\u524d\uff0c\u5168\u8eab\u91cd\u91cf\u5c3d\u5728\u540e\u8fb9\u7684\u5de6\u8db3\u4e4b\u4e0a\uff0c\u53ea\u542c\u5580\u5587\u4e00\u54cd\uff0c\u4e00\u5757\u5ca9\u77f3\u5e26\u7740\u51b0\u96ea\uff0c\u5815\u5165\u4e0b\u9762\u6df1\u8c37\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u811a\u5e95\u4e00\u7a7a\uff0c\u8eab\u4e0d\u7531\u4e3b\u7684\u5411\u4e0b\u8dcc\u843d\uff0c\u80e1\u6590\u5927\u60ca\uff0c\u5fd9\u4f38\u624b\u53bb\u62c9\u3002\u53ea\u662f\u82d7\u4eba\u51e4\u4e00\u5815\u4e4b\u52bf\u7740\u5b9e\u4e0d\u8f7b\uff0c\u867d\u7136\u62c9\u4f4f\u4e86\u4ed6\u8896\u5b50\uff0c\u53ef\u662f\u4e00\u5e26\u4e4b\u4e0b\uff0c\u8fde\u81ea\u5df1\u4e5f\u8dcc\u51fa\u5d16\u8fb9\u3002 \u3000\u3000\u4e8c\u4eba\u4e0d\u7ea6\u800c\u540c\u7684\u9f50\u5728\u7a7a\u4e2d\u8f6c\u8eab\uff0c\u8d34\u5411\u5c71\u58c1\uff0c\u65bd\u5c55\u201c\u58c1\u864e\u6e38\u5899\u529f\u201d\uff0c\u8981\u722c\u56de\u5c71\u5d16\u3002\u4f46\u90a3\u5c71\u58c1\u4e0a\u5168\u662f\u51b0\u96ea\uff0c\u6ed1\u6e9c\u65e0\u6bd4\uff0c\u90a3\u201c\u58c1\u864e\u6e38\u5899\u529f\u201d\u7adf\u7136\u65bd\u5c55\u4e0d\u51fa\uff0c\u83ab\u8bf4\u662f\u4eba\uff0c\u5c31\u5f53\u771f\u58c1\u864e\u5230\u6b64\uff0c\u53ea\u6015\u4e5f\u6e38\u4e0d\u4e0a\u53bb\u3002\u53ef\u662f\u4e0a\u53bb\u867d\u7136\u4e0d\u80fd\uff0c\u4e0b\u5815\u4e4b\u52bf\u5374\u4e5f\u7f13\u4e86\u3002 \u3000\u3000\u4e8c\u4eba\u6162\u6162\u6e9c\u4e0b\uff0c\u773c\u89c1\u518d\u6e9c\u5341\u4f59\u4e08\uff0c\u662f\u4e00\u5757\u5411\u5916\u51f8\u51fa\u7684\u60ac\u5ca9\uff0c\u5982\u4e0d\u80fd\u5728\u8fd9\u5ca9\u4e0a\u505c\u4f4f\uff0c\u90a3\u975e\u8dcc\u4e2a\u7c89\u8eab\u788e\u9aa8\u4e0d\u53ef\u3002\u5ff5\u5934\u521a\u8f6c\u5f97\u4e00\u8f6c\uff0c\u8eab\u5b50\u5df2\u843d\u5728\u5ca9\u4e0a\u3002\u4e8c\u4eba\u6b66\u529f\u76f8\u82e5\uff0c\u5fc3\u4e2d\u6240\u60f3\u4e5f\u662f\u4e00\u6a21\u4e00\u6837\uff0c\u5f53\u4e0b\u9f50\u4f7f\u201c\u5343\u65a4\u5760\u201d\u529f\u592b\uff0c\u7262\u7262\u5b9a\u4f4f\u811a\u6b65\u3002 \u3000\u3000\u5ca9\u9762\u5149\u5706\uff0c\u79ef\u4e86\u51b0\u96ea\u66f4\u662f\u6ed1\u6e9c\u65e0\u6bd4\uff0c\u4e8c\u4eba\u6b66\u529f\u9ad8\u5f3a\uff0c\u4e00\u843d\u4e0a\u5ca9\u9762\u7acb\u65f6\u5b9a\u8eab\uff0c\u7adf\u6ca1\u6ed1\u52a8\u534a\u6b65\u3002\u53ea\u542c\u683c\u683c\u8f7b\u54cd\uff0c\u90a3\u6570\u4e07\u65a4\u91cd\u7684\u5de8\u5ca9\u5374\u6447\u6643\u4e86\u51e0\u4e0b\u3002\u539f\u6765\u8fd9\u5757\u5de8\u5ca9\u6a2a\u67b6\u5c71\u8170\uff0c\u5e74\u6df1\u6708\u4e45\uff0c\u5ca9\u4e0b\u6c99\u77f3\u6e10\u6e10\u8131\u843d\uff0c\u672c\u5c31\u968f\u65f6\u90fd\u80fd\u6389\u4e0b\u8c37\u4e2d\uff0c\u73b0\u4e0b\u52a0\u4e0a\u4e86\u4e8c\u4eba\u91cd\u91cf\uff0c\u6c99\u77f3\u5939\u51b0\u7eb7\u7eb7\u4e0b\u5815\uff0c\u5de8\u5ca9\u8d8a\u6643\u8d8a\u662f\u5389\u5bb3\u3002 \u3000\u3000\u90a3\u4e24\u6839\u6811\u679d\u968f\u4eba\u4e00\u9f50\u8dcc\u5728\u5ca9\u4e0a\u3002\u82d7\u4eba\u51e4\u89c1\u60c5\u52bf\u5371\u6025\u5f02\u5e38\uff0c\u5de6\u638c\u62cd\u51fa\uff0c\u53f3\u624b\u5df2\u62fe\u8d77\u4e00\u6839\u6811\u679d\uff0c\u968f\u5373\u201c\u4e0a\u6b65\u4e91\u8fb9\u6458\u6708\u201d\uff0c\u633a\u5251\u659c\u523a\u3002\u80e1\u6590\u5934\u4e00\u4f4e\uff0c\u5f2f\u8170\u907f\u5251\uff0c\u4e5f\u5df2\u62fe\u8d77\u6811\u679d\uff0c\u8fd8\u4e86\u4e00\u62db\u201c\u62dc\u4f5b\u542c\u7ecf\u201d\u3002 \u3000\u3000\u4e24\u4eba\u8fd9\u65f6\u4f7f\u7684\u5168\u662f\u8fdb\u624b\u62db\u6570\uff0c\u62db\u62db\u72e0\u6781\u9669\u6781\uff0c\u4f46\u542c\u5f97\u683c\u683c\u4e4b\u58f0\u8d8a\u6765\u8d8a\u54cd\uff0c\u811a\u6b65\u96be\u4ee5\u7ad9\u7a33\u3002\u4e24\u4eba\u5747\u60f3\uff1a\u201c\u53ea\u6709\u5c06\u5bf9\u65b9\u903c\u5c06\u4e0b\u53bb\uff0c\u51cf\u8f7b\u5ca9\u4e0a\u91cd\u91cf\uff0c\u8fd9\u5de8\u5ca9\u4e0d\u81f4\u7acb\u65f6\u4e0b\u5815\uff0c\u81ea\u5df1\u624d\u6709\u6d3b\u547d\u4e4b\u671b\u3002\u201d\u5176\u65f6\u751f\u6b7b\u51b3\u4e8e\u77ac\u606f\uff0c\u624b\u4e0b\u66f4\u4e0d\u5bb9\u60c5\u3002 \u3000\u3000\u7247\u523b\u95f4\u4ea4\u624b\u5341\u4f59\u62db\uff0c\u82d7\u4eba\u51e4\u89c1\u5bf9\u65b9\u6240\u4f7f\u7684\u5200\u6cd5\u4e0e\u80e1\u4e00\u5200\u5f53\u5e74\u4e00\u6a21\u4e00\u6837\uff0c\u7591\u5fc3\u5927\u76db\uff0c\u53ea\u662f\u5f62\u683c\u52bf\u7981\uff0c\u5b9e\u65e0\u4f59\u6687\u76f8\u8be2\uff0c\u4e00\u62db\u201c\u8fd4\u8155\u7ffc\u5fb7\u95ef\u5e10\u201d\u524a\u51fa\uff0c\u63a5\u7740\u5c31\u8981\u4f7f\u51fa\u4e00\u62db\u201c\u63d0\u64a9\u5251\u767d\u9e64\u8212\u7fc5\u201d\u3002\u8fd9\u4e00\u62db\u5251\u638c\u9f50\u65bd\uff0c\u8981\u903c\u5f97\u5bf9\u65b9\u975e\u8dcc\u4e0b\u5ca9\u53bb\u4e0d\u53ef\uff0c\u53ea\u662f\u4ed6\u81ea\u5e7c\u4e60\u60ef\u4f7f\u7136\uff0c\u51fa\u62db\u4e4b\u524d\u4e0d\u7981\u80cc\u810a\u5fae\u5fae\u4e00\u8038\u3002 \u3000\u3000\u5176\u65f6\u6708\u660e\u5982\u6d17\uff0c\u957f\u7a7a\u4e00\u78a7\uff0c\u6708\u5149\u5c06\u5c71\u58c1\u6620\u5f97\u4e00\u7247\u660e\u4eae\u3002\u90a3\u5c71\u58c1\u4e0a\u5168\u662f\u6676\u5149\u7684\u51dd\u51b0\uff0c\u72b9\u4f3c\u955c\u5b50\u4e00\u822c\uff0c\u5c06\u82d7\u4eba\u51e4\u80cc\u5fc3\u53cd\u7167\u51fa\u6765\u3002 \u3000\u3000\u80e1\u6590\u770b\u5f97\u660e\u767d\uff0c\u767b\u65f6\u60f3\u8d77\u5e73\u963f\u56db\u6240\u8bf4\u81ea\u5df1\u7236\u4eb2\u5f53\u5e74\u4e0e\u4ed6\u6bd4\u6b66\u7684\u60c5\u72b6\uff0c\u90a3\u65f6\u6bcd\u4eb2\u5728\u4ed6\u80cc\u540e\u54b3\u55fd\u793a\u610f\uff0c\u6b64\u523b\u4ed6\u8eab\u540e\u653e\u4e86\u4e00\u9762\u660e\u955c\uff0c\u4e0d\u987b\u65c1\u4eba\u76f8\u52a9\uff0c\u5df2\u77e5\u4ed6\u4e0b\u4e00\u6b65\u975e\u51fa\u6b64\u62db\u4e0d\u53ef\uff0c\u5f53\u4e0b\u4e00\u62db\u201c\u516b\u65b9\u85cf\u5200\u5f0f\u201d\uff0c\u62a2\u4e86\u5148\u7740\u3002 \u3000\u3000\u82d7\u4eba\u51e4\u8fd9\u4e00\u62db\u201c\u63d0\u64a9\u5251\u767d\u9e64\u8212\u7fc5\u201d\u53ea\u51fa\u5f97\u534a\u62db\uff0c\u5168\u8eab\u5df2\u88ab\u80e1\u6590\u6811\u5200\u7f69\u4f4f\u3002\u4ed6\u6b64\u65f6\u518d\u65e0\u7591\u5fc3\uff0c\u77e5\u9053\u773c\u524d\u6b64\u4eba\u5fc5\u4e0e\u80e1\u4e00\u5200\u6709\u6781\u6df1\u7684\u6e0a\u6e90\uff0c\u53f9\u9053\uff1a\u201c\u62a5\u5e94\uff0c\u62a5\u5e94\uff01\u201d\u95ed\u76ee\u5f85\u6b7b\u3002 \u3000\u3000\u80e1\u6590\u4e3e\u8d77\u6811\u5200\uff0c\u4e00\u62db\u5c31\u80fd\u5c06\u4ed6\u5288\u4e0b\u5ca9\u53bb\uff0c\u4f46\u60f3\u8d77\u66fe\u7b54\u5e94\u8fc7\u82d7\u82e5\u5170\uff0c\u51b3\u4e0d\u80fd\u4f24\u5979\u7236\u4eb2\u3002\u7136\u800c\u82e5\u4e0d\u5288\u4ed6\uff0c\u5bb9\u4ed6\u5c06\u4e00\u62db\u201c\u63d0\u64a9\u5251\u767d\u9e64\u8212\u7fc5\u201d\u4f7f\u5168\u4e86\uff0c\u81ea\u5df1\u975e\u6b7b\u4e0d\u53ef\uff0c\u96be\u9053\u4e3a\u4e86\u76f8\u9976\u5bf9\u65b9\uff0c\u7adf\u767d\u767d\u9001\u4e86\u81ea\u5df1\u6027\u547d\u4e48\uff1f \u3000\u3000\u970e\u65f6\u4e4b\u95f4\uff0c\u4ed6\u5fc3\u4e2d\u8f6c\u8fc7\u4e86\u5343\u767e\u4e2a\u5ff5\u5934\uff1a \u3000\u3000\u8fd9\u4eba\u66fe\u5bb3\u6b7b\u81ea\u5df1\u7236\u6bcd\uff0c\u6559\u81ea\u5df1\u4e00\u751f\u5b64\u82e6\uff0c\u53ef\u662f\u4ed6\u8c6a\u6c14\u5343\u4e91\uff0c\u662f\u4e2a\u5927\u5927\u7684\u82f1\u96c4\u8c6a\u6770\uff0c\u53c8\u662f\u81ea\u5df1\u610f\u4e2d\u4eba\u7684\u751f\u7236\uff0c\u6309\u7406\u8fd9\u4e00\u5200\u4e0d\u8be5\u5288\u5c06\u4e0b\u53bb\uff1b\u4f46\u82e5\u4e0d\u5288\uff0c\u81ea\u5df1\u51b3\u65e0\u6d3b\u547d\u4e4b\u671b\uff0c\u81ea\u5df1\u752b\u5f53\u58ee\u5e74\uff0c\u5c82\u80af\u4fbf\u6b7b\uff1f\u5018\u4f7f\u6740\u4e86\u4ed6\u5427\uff0c\u56de\u5934\u600e\u80fd\u6709\u8138\u53bb\u89c1\u82d7\u82e5\u5170\uff1f\u8981\u662f\u7ec8\u751f\u907f\u5f00\u5979\u4e0d\u518d\u76f8\u89c1\uff0c\u8fd9\u4e00\u751f\u6d3b\u5728\u4e16\u4e0a\uff0c\u5fc3\u4e2d\u75db\u82e6\uff0c\u751f\u4e0d\u5982\u6b7b\u3002 \u3000\u3000\u90a3\u65f6\u80e1\u6590\u4e07\u5206\u4e3a\u96be\uff0c\u5b9e\u4e0d\u77e5\u8fd9\u4e00\u5200\u8be5\u5f53\u5288\u662f\u4e0d\u5288\u3002\u4ed6\u4e0d\u613f\u4f24\u4e86\u5bf9\u65b9\uff0c\u5374\u53c8\u4e0d\u613f\u8d54\u4e0a\u81ea\u5df1\u6027\u547d\u3002 \u3000\u3000\u4ed6\u82e5\u4e0d\u662f\u4fa0\u70c8\u91cd\u4e49\u4e4b\u58eb\uff0c\u8fd9\u4e00\u5200\u81ea\u7136\u5288\u4e86\u4e0b\u53bb\uff0c\u66f4\u65e0\u8e0c\u8e87\u3002\u4f46\u4e00\u4e2a\u4eba\u518d\u6177\u6168\u8c6a\u8fc8\uff0c\u5374\u4e5f\u4e0d\u80fd\u8f7b\u6613\u628a\u81ea\u5df1\u6027\u547d\u9001\u4e86\u3002\u5f53\u6b64\u4e4b\u9645\uff0c\u8981\u4e0b\u8fd9\u51b3\u65ad\u5b9e\u662f\u5343\u96be\u4e07\u96be\u2026\u2026 \u3000\u3000\u82d7\u82e5\u5170\u7ad9\u5728\u96ea\u5730\u4e4b\u4e2d\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u4e0d\u89c1\u4e8c\u4eba\u5f52\u6765\uff0c\u5f53\u4e0b\u7f13\u7f13\u6253\u5f00\u80e1\u6590\u4ea4\u7ed9\u5979\u7684\u5305\u88f9\u3002\u53ea\u89c1\u5305\u88f9\u662f\u51e0\u4ef6\u5a74\u513f\u8863\u886b\uff0c\u4e00\u53cc\u5a74\u513f\u978b\u5b50\uff0c\u8fd8\u6709\u4e00\u5757\u9ec4\u5e03\u5305\u88b1\uff0c\u6708\u5149\u4e0b\u770b\u5f97\u660e\u767d\uff0c\u5305\u4e0a\u7ee3\u7740\u201c\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u201d\u4e03\u4e2a\u9ed1\u5b57\uff0c\u6b63\u662f\u5979\u7236\u4eb2\u5f53\u5e74\u7ed9\u80e1\u6590\u88f9\u5728\u8eab\u4e0a\u7684\u3002 \u3000\u3000\u5979\u7ad9\u5728\u96ea\u5730\u4e4b\u4e2d\uff0c\u6708\u5149\u4e4b\u4e0b\uff0c\u671b\u7740\u90a3\u5a74\u513f\u7684\u5c0f\u8863\u5c0f\u978b\uff0c\u5fc3\u4e2d\u67d4\u60c5\u4e07\u79cd\uff0c\u4e0d\u7981\u75f4\u4e86\u3002 \u3000\u3000\u80e1\u6590\u5230\u5e95\u80fd\u4e0d\u80fd\u5e73\u5b89\u5f52\u6765\u548c\u5979\u76f8\u4f1a\uff0c\u4ed6\u8fd9\u4e00\u5200\u5230\u5e95\u5288\u4e0b\u53bb\u8fd8\u662f\u4e0d\u5288\uff1f"}),showOnHomepage:!0},{name:"\u540e\u8bb0",content:(0,a.jsx)(a.Fragment,{children:"\u300a\u96ea\u5c71\u98de\u72d0\u300b\u7684\u7ed3\u675f\u662f\u4e00\u4e2a\u60ac\u7591\uff0c\u6ca1\u6709\u80af\u5b9a\u7684\u7ed3\u5c40\u3002\u5230\u5e95\u80e1\u6590\u8fd9\u4e00\u5200\u5288\u4e0b\u53bb\u5462\u8fd8\u662f\u4e0d\u5288\uff0c\u8ba9\u8bfb\u8005\u81ea\u884c\u6784\u60f3\u3002 \u3000\u3000\u8fd9\u90e8\u5c0f\u8bf4\u4e8e\u4e00\u4e5d\u4e94\u4e5d\u5e74\u53d1\u8868\uff0c\u5341\u591a\u5e74\u6765\uff0c\u66fe\u6709\u597d\u51e0\u4f4d\u670b\u53cb\u548c\u8bb8\u591a\u4e0d\u76f8\u8bc6\u7684\u8bfb\u8005\u5e0c\u671b\u6211\u5199\u4e2a\u80af\u5b9a\u7684\u7ed3\u5c3e\u3002\u4ed4\u7ec6\u60f3\u8fc7\u4e4b\u540e\uff0c\u89c9\u5f97\u8fd8\u662f\u4fdd\u7559\u539f\u72b6\u7684\u597d\uff0c\u8ba9\u8bfb\u8005\u4eec\u591a\u4e00\u4e9b\u60f3\u50cf\u7684\u4f59\u5730\u3002\u6709\u4f59\u4e0d\u5c3d\u548c\u9002\u5f53\u7684\u542b\u84c4\uff0c\u4e5f\u662f\u4e00\u79cd\u8da3\u5473\u3002\u5728\u6211\u81ea\u5df1\u5fc3\u4e2d\uff0c\u66fe\u60f3\u8fc7\u4e03\u516b\u79cd\u4e0d\u540c\u7684\u7ed3\u5c40\uff0c\u6709\u65f6\u60f3\u60f3\u5404\u79cd\u4e0d\u540c\u7ed3\u5c40\uff0c\u90a3\u4e5f\u662f\u4e00\u9879\u4eab\u53d7\u3002\u80e1\u6590\u8fd9\u4e00\u5200\u5288\u6216\u662f\u4e0d\u5288\uff0c\u5728\u80e1\u6590\u662f\u4e00\u79cd\u6289\u62e9\uff0c\u800c\u6bcf\u4e00\u4f4d\u8bfb\u8005\uff0c\u90fd\u53ef\u4ee5\u51ed\u7740\u81ea\u5df1\u7684\u4e2a\u6027\uff0c\u51ed\u7740\u5404\u4eba\u5bf9\u4eba\u6027\u548c\u8fd9\u4e2a\u4e16\u754c\u7684\u770b\u6cd5\uff0c\u4f5c\u51fa\u4e0d\u540c\u7684\u6289\u62e9\u3002 \u3000\u3000\u5173\u4e8e\u674e\u81ea\u6210\u4e4b\u6b7b\uff0c\u6709\u597d\u51e0\u79cd\u8bf4\u6cd5\u3002\u7b2c\u4e00\u79cd\u662f\u300a\u660e\u53f2\u300b\u8bf4\u7684\uff0c\u4ed6\u5728\u4e5d\u5bab\u5c71\u4e3a\u6751\u6c11\u51fb\u6bd9\uff0c\u5f53\u65f6\u8c23\u8a00\u53c8\u8bf4\u662f\u4e3a\u795e\u9053\u6240\u6b9b\u3002\u7b2c\u4e8c\u79cd\u662f\u300a\u660e\u7eaa\u300b\u8bf4\u4ed6\u4e3a\u6751\u6c11\u6240\u56f0\uff0c\u4e0d\u80fd\u8131\uff0c\u81ea\u7f22\u800c\u6b7b\u3002\u7b2c\u4e09\u79cd\u662f\u300a\u660e\u5b63\u5317\u7565\u300b\u8bf4\u4ed6\u5728\u7f57\u516c\u5c71\u519b\u4e2d\u75c5\u6b7b\u3002\u7b2c\u56db\u79cd\u662f\u300a\u6ca3\u5dde\u5fd7\u300b\u6240\u8f7d\uff0c\u4ed6\u9003\u5230\u5939\u5c71\u51fa\u5bb6\u4e3a\u50e7\uff0c\u5230\u4e03\u5341\u5c81\u624d\u5750\u5316\u3002\u7b2c\u4e94\u79cd\u662f\u300a\u5434\u4e09\u6842\u6f14\u4e49\u300b\u5c0f\u8bf4\u7684\u60f3\u50cf\uff0c\u8bf4\u662f\u4e3a\u725b\u91d1\u661f\u6240\u6bd2\u6740\u3002 \u3000\u3000\u5386\u53f2\u5c0f\u8bf4\u6709\u60f3\u50cf\u7684\u81ea\u7531\uff0c\u53ef\u4ee5\u4e0d\u5fc5\u8ba8\u8bba\u3002\u5176\u4ed6\u5404\u79cd\u8bf4\u6cd5\u7ecf\u540e\u4eba\u8003\u8bc1\uff0c\u4f3c\u4e4e\u90fd\u6709\u7591\u70b9\u3002\u4f55\u817e\u86df\u7684\u594f\u7ae0\u4e2d\u8bf4\uff1a\u201c\u4e3a\u95ef\u6b7b\u786e\u6709\u8bc1\u636e\u3001\u95ef\u7ea7\u672a\u6562\u6276\u540c\u3001\u8c28\u5177\u5b9e\u56de\u594f\u4e8b\u2026\u2026\u9053\u963b\u97f3\u7edd\uff0c\u65e0\u590d\u5f97\u5176\u9996\u7ea7\u62a5\u9a8c\u3002\u4eca\u65e5\u9006\u9996\u5df2\u8bef\u6b7b\u4e8e\u4e61\u5175\uff0c\u800c\u4e61\u5175\u521d\u4e0d\u77e5\u4e5f\u2026\u2026\u201d\u5f97\u4e0d\u5230\u674e\u81ea\u6210\u7684\u9996\u7ea7\uff0c\u603b\u4e4b\u662f\u542b\u542b\u7cca\u7cca\u3002\u6e05\u5c06\u963f\u6d4e\u683c\u7684\u594f\u758f\u5219\u8bf4\uff1a\u201c\u6709\u964d\u5352\u8a00\uff0c\u81ea\u6210\u7a9c\u5165\u4e5d\u5bab\u5c71\uff0c\u4e3a\u6751\u6c11\u6240\u56f0\uff0c\u81ea\u7f22\u6b7b\uff0c\u5c38\u673d\u83ab\u8fa8\u3002\u201d\u5c38\u9996\u8150\u70c2\uff0c\u4e5f\u65e0\u6cd5\u9a8c\u660e\u6b63\u8eab\u3002 \u3000\u3000\u6c5f\u5bbe\u8c37\uff08\u540d\u6631\u5fd7\uff09\u6240\u64b0\u300a\u674e\u81ea\u6210\u5893\u5fd7\u300b\u5168\u6587\u5982\u4e0b\uff1a \u3000\u3000\u201c\u4f55\u7498\u300a\u6ca3\u5dde\u5fd7\u300b\u4e91\uff1a\u2018\u674e\u95ef\u4e4b\u6b7b\uff0c\u91ce\u53f2\u8f7d\u901a\u57ce\u7f57\u516c\u5c71\uff0c\u300a\u660e\u53f2\u300b\u8f7d\u901a\u57ce\u4e5d\u5bab\u5c71\uff0c\u5176\u4ee5\u4e3a\u6b7b\u4e8e\u6751\u6c11\uff0c\u4e00\u4e5f\u3002\u4eca\u6309\u7f57\u516c\u5c71\uff0c\u5b9e\u5728\u9ed4\u9633\uff0c\u800c\u4e5d\u5bab\u5c71\u5b9e\u5728\u901a\u5c71\u53bf\uff0c\u5176\u8a00\u901a\u57ce\uff0c\u7686\u8bef\u4e5f\u3002\u6709\u5b59\u6559\u6388\u4e3a\u4f59\u8a00\uff1a\u674e\u81ea\u6210\u5b9e\u7a9c\u6ca3\u5dde\uff0c\u81f3\u6e05\u5316\u9a7f\uff0c\u968f\u5341\u4f59\u9a91\u8d70\u726f\u725b\u575d\uff0c\u5728\u4eca\u5b89\u798f\u53bf\u5883\u3002\u590d\u4e58\u9a91\u53bb\uff0c\u72ec\u7a9c\u77f3\u95e8\u4e4b\u5939\u5c71\u4e3a\u50e7\uff0c\u4eca\u5176\u575f\u5c1a\u5728\u3002\u2019\u4e91\u4e91\u3002\u4f59\u8bb6\u4e4b\uff0c\u7279\u81f3\u5939\u5c71\u3002\u89c1\u5bfa\u65c1\u6709\u77f3\u5854\uff0c\u590d\u4ee5\u5c4b\uff0c\u5854\u9762\u5927\u4e66\u2018\u5949\u5929\u7389\u548c\u5c1a\u2019\u3002\u524d\u6709\u7891\uff0c\u4e43\u5176\u5f92\u91ce\u62c2\u6587\uff0c\u8f7d\u548c\u5c1a\u4e0d\u77e5\u8c01\u6c0f\u5b50\u3002\u4e00\u8001\u50e7\u5e74\u4e03\u5341\u4f59\uff0c\u5c1a\u80fd\u8a00\u5939\u5c71\u65e7\u4e8b\uff0c\u4e91\u548c\u5c1a\u987a\u6cbb\u521d\u5165\u5bfa\uff0c\u4e8b\u5f8b\u95e8\uff0c\u4e0d\u8a00\u6765\u81ea\u4f55\u5904\uff0c\u5176\u58f0\u4f3c\u897f\u4eba\u3002\u540e\u6570\u5e74\u590d\u6709\u4e00\u50e7\u6765\uff0c\u4e91\u662f\u5176\u5f92\uff0c\u4e43\u5b97\u95e8\uff0c\u53f7\u91ce\u62c2\uff0c\u6c5f\u5357\u4eba\uff0c\u4e8b\u548c\u5c1a\u751a\u8c28\u3002 \u3000\u3000\u548c\u5c1a\u5352\u4e8e\u5eb7\u7199\u7532\u8fb0\u5c81\u4e8c\u6708\uff0c\u7ea6\u5e74\u4e03\u5341\u3002\u4e34\u7ec8\uff0c\u6709\u9057\u8a00\u4e8e\u91ce\u62c2\uff0c\u5f7c\u65f6\u5e7c\uff0c\u4e0d\u4e0e\u95fb\u3002\u5bfa\u5c1a\u85cf\u6709\u9057\u50cf\uff0c\u547d\u53d6\u89c6\u4e4b\uff0c\u5219\u9ad8\u98a7\u6df1\u9890\uff0c\u9e31\u76ee\u874e\u9f3b\uff0c\u72b6\u8c8c\u72f0\u72de\uff0c\u4e0e\u300a\u660e\u53f2\u300b\u6240\u8f7d\u6b63\u540c\u3002\u81ea\u6210\u50ed\u53f7\u5949\u5929\u5021\u4e49\u5927\u5143\u5e05\uff0c\u540e\u590d\u81ea\u79f0\u65b0\u987a\u738b\u3002\u5176\u81ea\u79f0\u5949\u5929\u7389\u548c\u5c1a\uff0c\u76d6\u81ea\u5bd3\u52a0\u70b9\u4ee5\u8bb3\u4e4b\u3002\u800c\u91ce\u62c2\u4ee5\u5b97\u95e8\u4e3a\u5f8b\u95e8\u5f1f\u5b50\uff0c\u4e8b\u4e4b\u751a\u8c28\uff0c\u5c82\u5176\u65e7\u65e5\u81e3\u76f8\u4e0e\u5de6\u53f3\u8005\u4e0e\uff1f\u300a\u660e\u53f2\u300b\u4e8e\u4e5d\u5bab\u5c71\ue40e\u6b7b\u4e4b\u81ea\u6210\uff0c\u4ea6\u4e91\uff1a\u2018\u6211\u5175\u9063\u8bc6\u8005\u9a8c\u5176\u5c38\uff0c\u673d\u83ab\u8fa8\u3002\u2019\u800c\u8001\u50e7\u4eb2\u95fb\u8b26\ue40f\uff0c\u5176\u897f\u97f3\u53c8\u8db3\u5f02\u4e5f\u3002\u201d \u3000\u3000\u6240\u8bf7\u201c\u897f\u4eba\u201d\u201c\u897f\u97f3\u201d\uff0c\u6307\u9655\u897f\u4eba\u548c\u9655\u897f\u53e3\u97f3\u3002\u674e\u81ea\u6210\u662f\u9655\u897f\u7c73\u8102\u53bf\u4eba\u3002\u674e\u81ea\u6210\u778e\u4e86\u4e00\u53ea\u773c\u775b\uff0c\u662f\u5728\u56f4\u653b\u5f00\u5c01\u65f6\u7ed9\u9648\u6c38\u798f\u5c04\u778e\u7684\uff0c\u672c\u662f\u4e00\u4e2a\u6781\u660e\u663e\u7684\u7279\u5f81\uff0c\u4f46\u90a3\u8001\u50e7\u63cf\u8ff0\u5949\u5929\u7389\u548c\u5c1a\u65f6\u6ca1\u6709\u63d0\u53ca\uff0c\u4f3c\u662f\u4e00\u4e2a\u91cd\u5927\u7591\u70b9\u3002 \u3000\u3000\u674e\u81ea\u6210\u5728\u6b64\u4ee5\u524d\uff0c\u5f53\u88ab\u660e\u5175\u903c\u5f97\u52bf\u7a77\u529b\u7aed\u65f6\uff0c\u66fe\u5047\u6b7b\u8fc7\u4e00\u6b21\uff0c\u90a3\u662f\u5728\u5d07\u796f\u5341\u4e8c\u5e74\u3002\u4ed6\u5e7c\u65f6\u505a\u8fc7\u548c\u5c1a\u3002\u963f\u82f1\u5728\u5267\u672c\u300a\u674e\u95ef\u738b\u300b\u7684\u8003\u636e\u4e2d\u8bf4\uff1a\u201c\u2026\u2026\u81ea\u6210\u518d\u8fc7\u548c\u5c1a\u751f\u6daf\uff0c\u4e5f\u662f\u2018\u9a7e\u8f7b\u5c31\u719f\u2019\u7684\uff0c\u4f55\u51b5\u2018\u6210\u5219\u4e3a\u738b\uff0c\u8d25\u5219\u4e3a\u50e7\u2019\uff0c\u662f\u4e2d\u56fd\u7684\u8001\u4e00\u5957\u5462\uff01\u201d \u3000\u3000\u5728\u5c0f\u8bf4\u4e2d\u52a0\u63d2\u4e00\u4e9b\u5386\u53f2\u80cc\u5883\uff0c\u5f53\u7136\u4e0d\u5fc5\u4e00\u5207\u7ec6\u8282\u90fd\u5b8c\u5168\u7b26\u5408\u53f2\u5b9e\uff0c\u53ea\u8981\u91cd\u5927\u4e8b\u4ef6\u4e0d\u8fdd\u80cc\u5c31\u662f\u4e86\u3002\u81f3\u4e8e\u6ca1\u6709\u5b9a\u8bba\u7684\u5386\u53f2\u4e8b\u4ef6\uff0c\u5c0f\u8bf4\u4f5c\u8005\u81ea\u7136\u66f4\u53ef\u9009\u62e9\u5176\u4e2d\u7684\u4e00\u79cd\u8bf4\u6cd5\u6765\u52a0\u4ee5\u53d1\u6325\u3002 \u3000\u3000\u4f46\u65e7\u5c0f\u8bf4\u300a\u5434\u4e09\u6842\u6f14\u4e49\u300b\u548c\u300a\u94c1\u51a0\u56fe\u300b\u53d9\u8ff0\u674e\u81ea\u6210\u6545\u4e8b\uff0c\u548c\u4f17\u6240\u516c\u8ba4\u7684\u4e8b\u5b9e\u8ddd\u79bb\u592a\u8fdc\uff0c\u4ee5\u300a\u94c1\u51a0\u56fe\u300b\u4e2d\u63cf\u5199\u8d39\u5bab\u5a25\u6240\u523a\u6740\u7684\u95ef\u519b\u5927\u5c06\u7adf\u662f\u674e\u5ca9\uff0c\u672a\u514d\u81ea\u7531\u5f97\u8fc7\u4e86\u5206\u3002 \u3000\u3000\u300a\u96ea\u5c71\u98de\u72d0\u300b\u4e8e\u4e00\u4e5d\u4e94\u4e5d\u5e74\u5728\u62a5\u4e0a\u53d1\u8868\u540e\uff0c\u6ca1\u6709\u51fa\u7248\u8fc7\u4f5c\u8005\u6240\u8ba4\u53ef\u7684\u5355\u884c\u672c\u3002\u574a\u95f4\u7684\u5355\u884c\u672c\uff0c\u636e\u6211\u6240\u89c1\uff0c\u5171\u6709\u516b\u79cd\uff0c\u6709\u4e00\u518c\u672c\u3001\u4e24\u518c\u672c\u3001\u4e09\u518c\u672c\u3001\u4e03\u518c\u672c\u4e4b\u5206\uff0c\u90fd\u662f\u4e66\u5546\u64c5\u81ea\u7ffb\u5370\u7684\u3002\u603b\u7b97\u627f\u4ed6\u4eec\u77a7\u5f97\u8d77\uff0c\u6240\u4ee5\u4e00\u76f4\u4e5f\u672a\u52a0\u7406\u4f1a\u3002\u53ea\u662f\u4e66\u4e2d\u9519\u5b57\u5f88\u591a\uff0c\u800c\u7ffb\u5370\u8005\u5f3a\u5206\u7ae0\u8282\uff0c\u81ea\u64b0\u56de\u76ee\uff0c\u672a\u5fc5\u7b26\u5408\u4f5c\u8005\u539f\u610f\uff0c\u6709\u4e9b\u7248\u672c\u6240\u9644\u7684\u63d2\u56fe\uff0c\u4e5f\u975e\u4f5c\u8005\u6240\u559c\u3002 \u3000\u3000\u73b0\u5728\u91cd\u884c\u589e\u5220\u6539\u5199\uff0c\u5148\u5728\u300a\u660e\u62a5\u665a\u62a5\u300b\u53d1\u8868\uff0c\u51fa\u4e66\u65f6\u53c8\u4f5c\u4e86\u51e0\u6b21\u4fee\u6539\uff0c\u7ea6\u7565\u4f30\u8ba1\uff0c\u539f\u4e66\u5341\u5206\u4e4b\u516d\u4e03\u7684\u53e5\u5b50\u90fd\u5df2\u6539\u5199\u8fc7\u4e86\u3002\u539f\u4e66\u7684\u8131\u6f0f\u7c97\u758f\u4e4b\u5904\uff0c\u5927\u81f4\u5df2\u4f5c\u4e86\u4e00\u4e9b\u6539\u6b63\u3002\u53ea\u662f\u4e66\u4e2d\u4eba\u7269\u5b9d\u6811\u3001\u5e73\u963f\u56db\u3001\u9676\u767e\u5c81\u3001\u5218\u5143\u9e64\u7b49\u90fd\u662f\u7c97\u4eba\uff0c\u8bb2\u8ff0\u6545\u4e8b\u65f6\u8bed\u6c14\u4ecd\u5acc\u592a\u6587\uff0c\u5982\u6539\u5f97\u7b26\u5408\u5404\u4eba\u8eab\u5206\uff0c\u6ee1\u7eb8\u201c\u4ed6\u5988\u7684\u201d\u53c8\u672a\u514d\u592a\u8fc7\u4e0d\u96c5\u3002\u9650\u4e8e\u624d\u529b\uff0c\u90a3\u662f\u65e0\u53ef\u5982\u4f55\u4e86\u3002 \u3000\u3000\u300a\u96ea\u5c71\u98de\u72d0\u300b\u6709\u82f1\u6587\u8bd1\u672c\uff0c\u66fe\u5728\u7ebd\u7ea6\u51fa\u7248\u4e4b\u300a\uff22\uff52\uff49\uff44\uff47\uff45\u300b\u53cc\u6708\u520a\u4e0a\u8fde\u8f7d\u3002 \u3000\u3000\u300a\u96ea\u5c71\u98de\u72d0\u300b\u4e0e\u300a\u98de\u72d0\u5916\u4f20\u300b\u867d\u6709\u5173\u8fde\uff0c\u7136\u800c\u662f\u4e24\u90e8\u5404\u81ea\u72ec\u7acb\u7684\u5c0f\u8bf4\uff0c\u6240\u4ee5\u5185\u5bb9\u5e76\u4e0d\u5f3a\u6c42\u4e00\u81f4\u3002\u6309\u7406\u8bf4\uff0c\u80e1\u6590\u5728\u9047\u5230\u82d7\u82e5\u5170\u65f6\uff0c\u5fc5\u5b9a\u4f1a\u60f3\u5230\u8881\u7d2b\u8863\u548c\u7a0b\u7075\u7d20\u3002\u4f46\u5355\u5c31\u201c\u96ea\u5c71\u98de\u72d0\u201d\u8fd9\u90e8\u5c0f\u8bf4\u672c\u8eab\u800c\u8a00\uff0c\u4f3c\u4e4e\u4e0d\u5fc5\u8ba9\u53e6\u4e00\u90e8\u5c0f\u8bf4\u7684\u89d2\u8272\u51fa\u73b0\uff0c\u5373\u4f7f\u53ea\u662f\u5728\u80e1\u6590\u5fc3\u4e2d\u51fa\u73b0\u3002\u4e8b\u5b9e\u4e0a\uff0c\u300a\u96ea\u5c71\u98de\u72d0\u300b\u64b0\u4f5c\u5728\u5148\uff0c\u5f53\u65f6\u4f5c\u8005\u5fc3\u4e2d\uff0c\u4e5f\u4ece\u6765\u6ca1\u6709\u8881\u7d2b\u8863\u548c\u7a0b\u7075\u7d20\u90a3\u4e24\u4e2a\u4eba\u7269\u3002 \u3000"}),showOnHomepage:!0}]}}]);