"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[47921],{31871:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u4fa0\u9aa8\u4e39\u5fc3",content:(0,t.jsx)(t.Fragment,{children:"\u7b2c\u4e00\u56de\u3000\u8352\u5c71\u9690\u58eb\u8fce\u4f73\u5ba2\u3000\u7f8e\u9152\u751c\u8a00\u60d1\u5c11\u5e74 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000  \u9676\u6f5c\u8bd7\u559c\u8bf4\u8346\u8f72\uff0c\u60f3\u89c1\u505c\u4e91\u53d1\u6d69\u6b4c\u3002 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000  \u541f\u5230\u6069\u4ec7\u5fc3\u4e8b\u6d8c\uff0c\u6c5f\u6e56\u4fa0\u9aa8\u6050\u65e0\u591a\u3002 \u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000\u3000 \u2014\u2014\u9f9a\u5b9a\u76d2\u5df1\u672a\u6742\u8bd7 \u3000\u3000\u7a7a\u5c71\u5bc2\u5bc2\uff0c\u9e1f\u9e23\u5624\u5624\uff0c\u733f\u54cd\u5bd2\u4e25\u6811\uff0c\u9e1f\u9e23\u5c71\u66f4\u5e7d\u3002\u5728\u733f\u557c\u9e1f\u8bed\u4e4b\u4e2d\uff0c\u5374\u5ffd\u6709\u7a7a\u8c37\u8db3\u97f3\uff0c \u8e0f\u7834\u4e86\u8352\u5c71\u7684\u5bc2\u9759\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u4e2a\u62ab\u7740\u6ee1\u8eab\u98ce\u6c99\u7684\u5c11\u5e74\uff0c\u4ed6\u662f\u6b66\u5f53\u6d3e\u638c\u95e8\u4eba\u96f7\u9707\u5b50\u7684\u5173\u95e8\u5f92\u5f1f\u79e6\u5143\u6d69\u3002\u6b64\u65f6\u6b63\u4ece \u9669\u7a84\u5d0e\u5c96\u7684\u5f82\u5f95\u5c71\u9053\u4e0a\u7ecf\u8fc7\u3002 \u3000\u3000\u5f82\u5f95\u5c71\u662f\u5728\u5c71\u4e1c\u897f\u5317\u90e8\u7684\u4e00\u5ea7\u540d\u961f\u5728\u6cf0\u5b89\u53bf\u4e4b\u5357\uff0c\u4e0e\u6cf0\u5b89\u4e4b\u5317\u7684\u6cf0\u5c71\u9065\u9065\u76f8\u5bf9\u3002\u5c71\u867d\u7136 \u4e0d\u7b97\u5f88\u9ad8\uff0c\u4f46\u56e0\u65e0\u751a\u51fa\u4ea7\uff0c\u91ce\u517d\u4e5f\u4e0d\u591a\uff0c\u5c71\u4e0a\u5374\u662f\u5c11\u6709\u4eba\u5bb6\u3002\u79e6\u5143\u6d69\u8e0f\u8fdb\u5f82\u5f95\u5c71\u4e4b\u540e\uff0c\u4e00\u76f4 \u5c31\u662f\u8e3d\u8e3d\u72ec\u884c\uff0c\u6ca1\u6709\u78b0\u89c1\u8fc7\u4e00\u4e2a\u8def\u4eba\u3002 \u3000\u3000\u867d\u7136\u662f\u8e3d\u8e3d\u72ec\u884c\uff0c\u5bc2\u5bde\u65e0\u4f34\uff0c\u4f46\u79e6\u5143\u6d69\u7684\u5fc3\u4e2d\u5374\u662f\u70ed\u70d8\u70d8\u7684\u3002\u4ed6\u542c\u7740\u5c71\u4e2d\u7684\u9e1f\u8bed\uff0c\u4f3c\u4e4e \u662f\u5728\u4e00\u5531\u4e00\u548c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53e4\u8bd7\u8bf4\u2018\u5624\u5176\u9e23\u77e3\uff0c\u6c42\u5176\u53cb\u58f0\u3002\u2019\u9e1f\u9e23\u5624\u5624\uff0c\u81ea\u53e4\u4ee5\u6765\uff0c\u5c31\u5f53 \u4f5c\u662f\u6c42\u53cb\u4e4b\u58f0\uff0c\u6211\u8fd9\u6b21\u5230\u4e1c\u5e73\u53bf\u53bb\uff0c\u6b63\u662f\u5e7f\u4ea4\u5929\u4e0b\u82f1\u8c6a\u7684\u597d\u673a\u4f1a\u3002\u201d \u3000\u3000\u5728\u7a7a\u5c71\u5bc2\u5bc2\u4e4b\u4e2d\uff0c\u79e6\u5143\u6d69\u5df2\u7ecf\u5728\u61a7\u61ac\u4e94\u5929\u4e4b\u540e\u7684\u70ed\u95f9\u5e38\u70d8\u4e86\u3002 \u3000\u3000\u201c\u4eca\u5929\u662f\u516b\u6708\u521d\u5341\uff0c\u51fa\u4e86\u5f82\u5f95\u5c71\uff0c\u4e24\u5929\u4e4b\u5185\uff0c\u6211\u53ef\u4ee5\u8d76\u5230\u6c5f\u5bb6\u3002\u516b\u6708\u5341\u4e94\u624d\u662f\u6b63\u65e5\uff0c\u6211 \u65e9\u6765\u4e09\u5929\uff0c\u4e0d\u77e5\u6c5f\u5bb6\u53ef\u6709\u5bbe\u5ba2\u5230\u4e86\uff1f\u82e5\u662f\u6211\u7b2c\u4e00\u4e2a\u5148\u5230\uff0c\u53ef\u6709\u70b9\u4e0d\u597d\u610f\u601d\uff0c\u4e0d\u8fc7\u6c5f\u5927\u4fa0\u6781\u4e3a \u597d\u5ba2\uff0c\u4ed6\u4e00\u5b9a\u4e0d\u4f1a\u602a\u6211\u65e9\u5230\u7684\u3002\u201d \u3000\u3000\u539f\u672a\u516b\u6708\u5341\u4e94\u8fd9\u5929\u662f\u5929\u4e0b\u95fb\u540d\u7684\u6c5f\u5927\u4fa0\u6c5f\u6d77\u5929\u7684\u5973\u513f\u51fa\u9601\u7684\u65e5\u5b50\uff0c\u5979\u7684\u5973\u513f\u6c5f\u6653\u8299\u8bb8\u914d \u7ed9\u4ed6\u7684\u638c\u95e8\u5f1f\u5b50\u5b87\u6587\u96c4\uff0c\u5b9a\u4e0b\u4e86\u5728\u4eca\u5e74\u7684\u4e2d\u79cb\u4f73\u8282\u5b8c\u5a5a\u3002\u6c5f\u6d77\u5929\u7ed3\u4ea4\u6ee1\u5929\u4e0b\uff0c\u5404\u5927\u95e8\u6d3e\u77e5\u9053 \u4e86\u8fd9\u4e2a\u6d88\u606f\uff0c\u5c11\u4e0d\u514d\u90e8\u8981\u6d3e\u4eba\u6765\u6c5f\u5bb6\u8d3a\u559c\u3002\u79e6\u5143\u6d69\u5c31\u662f\u4ee3\u8868\u6b66\u5f53\u6d3e\u524d\u5f80\u6c5f\u5bb6\u9053\u8d3a\u7684\u3002 \u3000\u3000\u672c\u6765\u6b66\u5f53\u6d3e\u4eba\u624d\u6d4e\u6d4e\uff0c\u800c\u4ee5\u6c5f\u6d77\u5929\u7684\u8eab\u4efd\uff0c\u4ed6\u7684\u5973\u513f\u51fa\u9601\uff0c\u6b66\u5f53\u6d3e\u5e94\u5f53\u6d3e\u4e00\u4e2a\u8f88\u4efd\u66f4\u9ad8 \u7684\u53bb\u53c2\u52a0\u5a5a\u793c\u624d\u80fd\u8868\u793a\u9686\u91cd\u3002\u4f46\u56e0\u79e6\u5143\u6d69\u867d\u7136\u53ea\u662f\u96f7\u9707\u5b50\u7684\u5173\u95e8\u5f1f\u5b50\uff0c\u4f46\u4ed6\u5929\u751f\u5f02\u7980\uff0c\u6b66\u529f \u4e4b\u9ad8\uff0c\u5374\u5728\u4e00\u4f17\u540c\u95e8\u4e4b\u4e0a\uff0c\u96f7\u9707\u5b50\u6700\u559c\u7231\u4ed6\uff0c\u6709\u610f\u683d\u57f9\u4e8e\u4ed6\uff0c\u6545\u800c\u5728\u4ed6\u5b66\u6210\u51fa\u5e08\u4e4b\u540e\uff0c\u7b2c\u4e00 \u6b21\u201c\u51fa\u9053\u201d\uff0c\u5c31\u53eb\u4ed6\u4f5c\u4e3a\u81ea\u5df1\u4ee3\u8868\uff0c\u5230\u6c5f\u5bb6\u53bb\u4f5c\u8d3a\u5ba2\u3002 \u3000\u3000\u96f7\u9707\u5b50\u77e5\u9053\u6c5f\u6d77\u5929\u6700\u559c\u6b22\u5e74\u5c11\u7684\u82f1\u96c4\uff0c\u4ed6\u628a\u672c\u95e8\u6700\u5f97\u610f\u7684\u5f1f\u5b50\u9063\u6c5f\u5bb6\uff0c\u6c5f\u6d77\u5929\u4e00\u5b9a\u4f1a\u9752 \u773c\u6709\u52a0\uff0c\u611f\u5230\u9ad8\u5174\uff0c\u800c\u51b3\u4e0d\u4f1a\u5acc\u4ed6\u5931\u793c\u7684\u3002\u4f46\u56e0\u79e6\u5143\u6d69\u662f\u7b2c\u4e00\u6b21\u51fa\u9053\uff0c\u6c5f\u5bb6\u7684\u4eba\u4e0d\u8ba4\u8bc6\u4ed6\uff0c \u6240\u4ee5\u96f7\u9707\u5b50\u7279\u522b\u5199\u4e86\u4e00\u5c01\u4eb2\u7b14\u7684\u4ecb\u7ecd\u4fe1\uff0c\u8fde\u540c\u6c5f\u5bb6\u9001\u6765\u7684\u8bf7\u5e16\uff0c\u53eb\u4ed6\u4e00\u8d77\u5e26\u53bb\u3002 \u3000\u3000\u6b64\u9645\u79e6\u5143\u6d69\u5c31\u6b63\u5728\u505a\u7740\u5e7f\u4ea4\u5929\u4e0b\u82f1\u8c6a\u7684\u7f8e\u68a6\u3002 \u3000\u3000\u79e6\u5143\u6d69\u6b63\u81ea\u60f3\u5f97\u5f97\u610f\uff0c\u4e00\u9635\u98ce\u5439\u6765\uff0c\u5ffd\u95fb\u5f97\u6c81\u4eba\u813e\u8151\u7684\u6842\u82b1\u9999\u5473\u3002\u79e6\u5143\u6d69\u62ac\u5934\u4e00\u671b\uff0c\u53ea \u89c1\u5c71\u5761\u4e0a\u6709\u5bb6\u4eba\u5bb6\uff0c\u623f\u5c4b\u501a\u5c71\u4fee\u5efa\uff0c\u7eff\u74e6\u7ea2\u5899\uff0c\u9887\u6709\u6c14\u6d3e\u3002\u4e00\u770b\u5c31\u77e5\u51b3\u975e\u730e\u6237\uff0c\u800c\u662f\u6709\u70b9\u94b1 \u7684\u4eba\u5bb6\u3002\u8fd9\u5bb6\u4eba\u5bb6\u7684\u82b1\u56ed\u91cc\u79cd\u6709\u8bb8\u591a\u6842\u6811\uff0c\u4e39\u6842\u98d8\u9999\uff0c\u968f\u98ce\u9001\u5165\u79e6\u5143\u6d69\u7684\u9f3b\u5b50\u3002 \u3000\u3000\u6b64\u65f6\u5df2\u662f\u65e5\u5f71\u897f\u659c\u7684\u508d\u665a\u65f6\u5206\uff0c\u665a\u971e\u5982\u8840\uff0c\u5728\u665a\u971e\u6620\u886c\u4e4b\u4e0b\uff0c\u5c71\u5761\u4e0a\u7684\u91ce\u82b1\u66f4\u663e\u5f97\u7ea2\u9163 \u7d2b\u9189\uff0c\u5c3d\u6001\u6781\u598d\uff0c\u52a0\u4e0a\u4e86\u4e39\u6842\u98d8\u9999\uff0c\u758f\u6797\u91cc\u7ea2\u5899\u9690\u73b0\u3002\u8fd9\u6837\u4f18\u7f8e\u7684\u73af\u5883\uff0c\u5b9e\u662f\u4ee4\u4eba\u4e0d\u5fcd\u901f \u53bb\u3002\u79e6\u5143\u6d69\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5929\u8272\u8fd1\u665a\uff0c\u51fa\u4e86\u5f82\u5f95\u5c71\u672a\u5fc5\u627e\u5f97\u5bbf\u5934\uff0c\u4e0d\u5982\u5c31\u5728\u8fd9\u5bb6\u4eba\u5bb6\u6c42\u5bbf\u3002\u201d \u4f46\u968f\u5373\u60f3\u9053\uff1a\u201c\u5374\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u4eba\u5bb6\uff0c\u5e08\u7236\u5429\u5490\uff0c\u6c5f\u6e56\u4e0a\u987b\u5f97\u6b65\u6b65\u5c0f\u5fc3\uff0c\u5904\u5904\u8c28\u614e\uff0c\u8352\u5c71\u5e7d \u8c37\u4e4b\u4e2d\uff0c\u6709\u8fd9\u6837\u4e00\u5bb6\u4eba\u5bb6\uff0c\u663e\u89c1\u662f\u4e0d\u5927\u5bfb\u5e38\uff0c\u5c82\u80fd\u968f\u4fbf\u6295\u5bbf\uff1f\u6211\u5728\u6df1\u5c71\u91ce\u5cad\u91cc\u9732\u5bbf\u4e5f\u662f\u60ef\u4e86 \u7684\uff0c\u627e\u4e0d\u5230\u5bbf\u5934\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u53ef\u662f\u79e6\u5143\u6d69\u56e0\u4e3a\u8fde\u65e5\u5954\u6ce2\uff0c\u6b64\u9645\u6b63\u81ea\u611f\u5230\u75b2\u5026\u3002\u4ed6\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u82b1\u9999\u5982\u9152\uff0c\u4ee4\u4ed6\u89c9\u5f97 \u597d\u4e0d\u8212\u670d\u3002\u79e6\u5143\u6d69\u4f38\u4e86\u4e00\u4e2a\u61d2\u8170\uff0c\u5750\u4e86\u4e0b\u6765\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u4e14\u6b47\u6b47\u4e00\u4f1a\u518d\u8d70\u3002\u53cd\u6b63\u4e5f\u4e0d\u5fd9\u7740\u8d76 \u8def\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u90a3\u5bb6\u4eba\u5bb6\u7684\u82b1\u56ed\u91cc\u6709\u4e2a\u5c11\u5e74\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5927\u6f20\u5b64\u70df\u76f4\u3002\u201d\u968f\u5373\u6709\u4e2a\u5c11\u5973\u7684\u58f0\u97f3 \u8bf4\u9053\uff1a\u201c\u957f\u6cb3\u843d\u65e5\u5706\u3002\u201d\u79e6\u5143\u6d69\u5728\u96f7\u9707\u5b50\u95e8\u4e0b\uff0c\u4e43\u662f\u65e5\u95f4\u5b66\u6b66\uff0c\u665a\u4e0a\u5b66\u6587\uff0c\u5510\u8bd7\u5b8b\u8bcd\u90fd\u66fe\u8bfb \u8fc7\u4e00\u4e9b\u3002\u542c\u5f97\u56ed\u4e2d\u7684\u7537\u5973\u6bcf\u4eba\u5ff5\u4e00\u53e5\u8bd7\uff0c\u4e0d\u89c9\u6709\u70b9\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u2018\u4ed6\u4eec\u4e0d\u5728\u4e66\u623f\u91cc\u8bfb\uff0c\u5374\u5728 \u82b1\u56ed\u91cc\u5ff5\u8bd7\uff0c\u53c8\u4e0d\u662f\u6574\u9996\u7684\u5ff5\uff0c\u8fd9\u5374\u4e3a\u4f55\uff1f\u201d \u3000\u3000\u90a3\u5bb6\u4eba\u5bb6\u5728\u5c71\u5761\u4e0b\u9762\uff0c\u79e6\u5143\u6d69\u5219\u662f\u5728\u5c71\u5761\u4e0a\u9762\u5750\u7740\u7684\uff0c\u82b1\u56ed\u867d\u6709\u56f4\u5899\uff0c\u5374\u6321\u4e0d\u4f4f\u4ed6\u7684\u89c6 \u7ebf\u3002\u4ed6\u65e0\u610f\u5077\u770b\u4eba\u5bb6\uff0c\u4f46\u56e0\u597d\u5947\u5fc3\u8d77\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7684\u5c31\u628a\u89c6\u7ebf\u6295\u4e86\u53bb\u53bb\u3002\u521a\u624d\u90a3\u5bf9\u5c11\u5e74\u7537\u5973\u662f \u5728\u82b1\u6811\u574e\u4e2d\uff0c\u5982\u4eca\u5219\u51fa\u5230\u56ed\u4e2d\u7684\u4e00\u7247\u5e73\u5766\u7684\u8349\u5730\u4e0a\u3002\u53ea\u89c1\u4ed6\u4eec\u6bcf\u4eba\u624b\u91cc\u63d0\u7740\u4e00\u628a\u957f\u5251\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u9053\u3002\u201d\u4f60\u7684\u2018\u5927\u6f20\u5b64\u70df\u76f4\u2019\u4f7f\u5f97\u5bf9\u4e86\uff0c\u4e0d\u8fc7\u52b2\u9053\u5c1a\u8d5a\u4e0d\u8db3\uff1b\u2018\u957f\u6cb3\u843d\u65e5\u5706\u2019\u5374\u4f7f \u5f97\u4e0d\u5bf9\uff0c\u8fd8\u8981\u518d\u7ec3\u3002\u4f60\u770b\u6211\u7684\u3002\u201d\u8bf4\u7f62\uff0c\u5c06\u957f\u5251\u4e00\u6296\uff0c\u5212\u4e86\u4e00\u9053\u5706\u5708\u3002\u4f46\u89c1\u5251\u5f71\u5982\u73af\uff0c\u5c11\u5e74 \u7684\u6574\u4e2a\u8eab\u5b50\u90fd\u4f3c\u5728\u5149\u73af\u4e4b\u4e2d\u3002 \u3000\u3000\u90a3\u5c11\u5973\u8ddf\u7740\u5c06\u5251\u53cd\u590d\u7684\u5212\u7740\u5706\u5708\uff0c\u53ef\u662f\u5708\u513f\u603b\u5212\u4e0d\u5706\u3002\u5c11\u5973\u8d4c\u6c14\u9053\uff1a\u201c\u8fd9\u4e48\u96be\u7ec3\uff0c\u6211\u4e0d \u5b66\u4e86\u3002\u201d\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u6211\u66fe\u6574\u6574\u5b66\u4e86\u4e00\u4e2a\u6708\uff0c\u624d\u80fd\u8fd0\u7528\u7eaf\u70ed\u4e86\uff0c\u4f60\u624d\u5b66\u4e86\u4e09\u5927\uff0c\u5c31\u7070 \u5fc3\u4e86\uff1f\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u62db\u660e\u5929\u518d\u7ec3\uff0c\u4f60\u518d\u628a\u90a3\u62db\u2018\u5927\u6f20\u5b64\u70df\u76f4\u2019\u6bd4\u5212\u7ed9\u6211\u770b\u770b\uff0c\u6211\u60f3\u77e5\u9053\u4e3a \u4ec0\u4e48\u6211\u7684\u52b2\u9053\u603b\u662f\u4f7f\u5f97\u4e0d\u8db3\u3002\u201d \u3000\u3000\u5c11\u5e74\u4e00\u5251\u523a\u51fa\uff0c\u5176\u76f4\u5982\u77e2\uff0c\u53ea\u89c1\u6811\u4e0a\u7684\u6842\u82b1\uff0c\u7eb7\u7eb7\u98d8\u4e0b\u3002 \u3000\u3000\u79e6\u5143\u6d69\u867d\u7136\u4e0d\u5728\u56ed\u4e2d\uff0c\u4f46\u770b\u89c1\u6842\u82b1\u7eb7\u843d\uff0c\u4e5f\u597d\u4f3c\u611f\u89c9\u5f97\u5230\u4ed6\u90a3\u864e\u864e\u7684\u5251\u98ce\u3002\u79e6\u5143\u6d69\u4e0d\u89c9 \u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u4f7f\u7684\u786e\u662f\u4e0a\u4e58\u5251\u6cd5\uff0c\u529f\u529b\u4e5f\u5f88\u4e0d\u51e1\uff0c\u5982\u4e0d\u77e5\u662f\u54ea\u4e00\u6d3e\u7684\uff1f\u201d \u8fd9\u65f6\uff0c\u79e6\u5143\u6d69\u624d\u77e5\u9053\u4ed6\u4eec\u662f\u5728\u7ec3\u4e60\u5251\u672f\uff0c\u6240\u5ff5\u7684\u5510\u8bd7\u4e43\u662f\u62db\u6570\u7684\u540d\u79f0\u3002 \u3000\u3000\u5c11\u5e74\u8bf4\u9053\uff1a\u201c\u51fa\u5251\u4e4b\u65f6\uff0c\u5c0f\u81c2\u5fae\u5f2f\uff0c\u6c14\u6c89\u4e39\u7530\uff0c\u84c4\u52b2\u5f85\u53d1\uff0c\u51fa\u62db\u4e4b\u9645\uff0c\u529b\u6c42\u5176\u76f4\u3002\u8fd9\u6837 \u52b2\u9053\u5c31\u81ea\u7136\u8db3\u4e86\u3002\u201d\u5c11\u5973\u7ec3\u4e86\u51e0\u6b21\uff0c\u51fa\u5251\u4e4b\u65f6\uff0c\u679c\u7136\u4e5f\u6709\u6842\u82b1\u843d\u4e0b\u3002\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u7684 \u5929\u8d44\u6bd4\u6211\u9ad8\uff0c\u8fd9\u4e00\u62db\u884c\u4e86\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u6211\u548c\u4f60\u5bf9\u62c6\u7ec3\u8fc7\u7684\u5341\u4e8c\u62db\u3002\u558f\uff0c\u661f\u5782\u5e73\u91ce\u9614\u3002\u201d\u4e00\u5251\u523a\u51fa\uff0c\u5251\u950b\u98a4\u6296\uff0c\u5251\u5149 \u951a\u5f00\u3002\u79e6\u5143\u6d69\u867d\u7136\u4e0d\u61c2\u5f97\u4ed6\u4eec\u8fd9\u4e00\u5957\u5251\u6cd5\u7684\u5965\u5999\uff0c\u4e5f\u77e5\u9053\u5c11\u5973\u4f7f\u7684\u8fd9\u62db\uff0c\u5df2\u7ecf\u7b26\u5408\u4e86\u8bd7\u7684\u610f \u5883\u3002\u679c\u7136\u542c\u5f97\u5c11\u5e74\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5fc3\u63a5\u62db\uff0c\u6211\u8fd8\u4f60\u4e00\u62db\u2018\u6708\u6d8c\u5927\u6c5f\u6d41\u2019\u3002\u201d \u58f0\u51fa\u62db\u53d1\uff0c\u767b\u65f6\u53ea\u89c1\u4e00\u7247\u5bd2\u5149\uff0c\u7a81\u73b0\u6d8c\u73b0\uff0c\u5251\u52bf\u7ef5\u7ef5\u4e0d\u65ad\uff0c\u5f53\u771f\u6709\u5982\u4e00\u8f6e\u7693\u6708\uff0c\u6d8c\u51fa\u6c5f\u5fc3\uff0c \u800c\u6d6a\u82b1\u56db\u8d77\uff0c\u5c06\u6c5f\u5fc3\u7684\u6708\u5f71\uff0c\u8361\u5f97\u7834\u4e86\u53c8\u5706\uff0c\u5706\u4e86\u53c8\u7834\u7684\u6a21\u6837\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u5bf9\u62c6\u4e86\u4e00\u5341\u4e8c\u62db\uff0c\u6bcf\u4e00\u62db\u90fd\u7b26\u5408\u4e00\u53e5\u5510\u8bd7\u7684\u610f\u5883\uff0c\u770b\u5f97\u79e6\u5143\u6d69\u76ee\u7729\u795e\u8ff7\uff0c\u5fc3\u4e2d\u60f3 \u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5e08\u7236\u8bf4\u6c5f\u6e56\u4e0a\u85cf\u9f99\u5367\u864e\uff0c\u5904\u5904\u90fd\u6709\u80fd\u4eba\u3002\u8fd9\u4e2a\u5c11\u5e74\u7684\u5251\u6cd5\u4e0d\u77e5\u662f\u90a3\u4e00\u6d3e\u7684\uff0c\u4f46 \u5df2\u4e0d\u5728\u6211\u6d3e\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u593a\u547d\u5251\u4e4b\u4e0b\u3002\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u4e0d\u89c9\u6cb9\u7136\u8d77\u4e86\u7ed3\u4ea4\u4e4b\u5fc3\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u90a3\u5c11\u5e74\u5ff5\u9053\uff1a\u201c\u98ce\u6025\u7ffb\u971c\u51b7\u201d\uff0c\u5bd2\u5149\u4e00\u62b9\uff0c\u5251\u5f71\u7ffb\u817e\uff0c\u51fa\u624b\u5feb\u6781\u3002\u90a3\u5c11\u5973\u56de \u4e86\u4e00\u62db\u201c\u4e91\u5f00\u89c1\u6708\u60ca\u201d\uff0c\u8fd9\u4e00\u62db\u6a2a\u5251\u524d\u63a8\uff0c\u672c\u6765\u662f\u89e3\u62c6\u5c11\u5e74\u90a3\u4e00\u62db\u7684\uff0c\u4f46\u56e0\u8fd9\u5c11\u5973\u65f6\u5019\u62ff\u634f \u5f97\u4e0d\u591f\u51c6\u786e\uff0c\u6162\u4e86\u4e9b\u513f\uff0c\u52b2\u529b\u4e0d\u8db3\uff0c\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u53cc\u5251\u76f8\u4ea4\uff0c\u5c11\u5973\u7684\u9752\u94a2\u5251\u8131\u624b\u5760 \u5730\u3002 \u3000\u3000\u79e6\u5143\u6d69\u89c1\u8fd9\u5c11\u5e74\u7684\u5251\u672f\u4f7f\u5f97\u5982\u6b64\u7cbe\u5999\uff0c\u51e0\u4e4e\u7981\u4e0d\u4f4f\u559d\u8d77\u5f69\u6765\uff0c\u5e78\u4e8f\u60ca\u89c9\u5f97\u65e9\uff0c\u8bdd\u5230\u53e3 \u8fb9\uff0c\u7ec8\u4e8e\u5fcd\u4f4f\u3002 \u3000\u3000\u5c11\u5e74\u62fe\u8d77\u5251\u6765\uff0c\u8d54\u7b11\u8bf4\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u6536\u52bf\u4e0d\u53ca\uff0c\u628a\u4f60\u7684\u5251\u6253\u843d\u4e86\u3002\u518d\u6765\uff0c\u518d\u6765\u3002\u201d \u3000\u3000\u5c11\u5973\u8d4c\u6c14\u9053\uff1a\u201c\u6211\u7684\u5251\u6bd4\u4e0d\u8fc7\u4f60\uff0c\u4e0d\u6765\u4e86\u3002\u201d\u5c11\u5e74\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u662f\u62c6\u62db\u73a9\u513f\uff0c\u4f60\u600e\u4e48\u8ba4 \u771f\u8d77\u6765\u4e86\uff1f\u201d\u5c11\u5973\u8bf4\u9053\uff1a\u201c\u8bf4\u662f\u62c6\u62db\u73a9\u7684\u4e2d\u4e3a\u4ec0\u4e48\u5b58\u5fc3\u8981\u6211\u597d\u770b\uff1f\u5c31\u7b97\u4f60\u662f\u5e08\u7236\uff0c\u6211\u662f\u5f92\u5f1f \u5427\uff0c\u4f60\u4e5f\u4e0d\u8be5\u628a\u6211\u7684\u5251\u6253\u843d\u3002\u597d\uff0c\u4f60\u7684\u5251\u672f\u591a\u597d\uff0c\u6211\u4e5f\u4e0d\u8ddf\u5b66\u4e86\u3002\u201d \u3000\u3000\u5c11\u5e74\u8fde\u5fd9\u8d54\u793c\u9053\uff1a\u201c\u6211\u82e5\u662f\u5b58\u5fc3\u7684\uff0c\u53eb\u6211\u4e0d\u5f97\u597d\u6b7b\u3002\u597d\u5728\u4e5f\u6ca1\u65c1\u4eba\uff0c\u4f60\u4e5f\u4e0d\u6015\u7ed9\u4eba\u7b11 \u8bdd\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6ca1\u6709\u65c1\u4eba\uff1f\u201d\u5c11\u5e74\u8bf4\u9053\uff1a\u201c\u6211\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\u3002\u6211\u8bf4\u6ca1\u6709\u65c1 \u4eba\u5c31\u6ca1\u6709\u65c1\u4eba\uff0c\u8981\u662f\u771f\u7684\u6709\u7684\u8bdd\uff0c\u6211\u8fd8\u4e0d\u628a\u4ed6\u63ea\u51fa\u6765\u5417\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u542c\u5f97\u4ed6\u4eec\u5982\u6b64\u8bf4\u8bdd\uff0c\u4f3c\u4e4e\u662f\u9488\u5bf9\u81ea\u5df1\u800c\u53d1\uff0c\u4e0d\u81ea\u89c9\u7684\u8fde\u5fd9\u628a\u8eab\u5b50\u8eb2\u85cf\u5f97\u9690\u5bc6\u4e00 \u4e9b\u3002 \u3000\u3000\u79e6\u5143\u6d69\u672c\u6765\u662f\u52a8\u4e86\u4e0e\u4ed6\u4eec\u7ed3\u4ea4\u4e4b\u5ff5\u7684\uff0c\u5982\u4eca\u542c\u4e86\u4ed6\u4eec\u7684\u8bf4\u8bdd\uff0c\u65b9\u624d\u8b66\u89c9\u5018\u82e5\u81ea\u5df1\u6b64\u65f6\u51fa \u53bb\uff0c\u5b9e\u662f\u5927\u5927\u7684\u4e0d\u59a5\u3002\u8981\u77e5\u6b66\u6797\u4e2d\u4eba\uff0c\u5728\u4ed6\u4eec\u7ec3\u4e60\u672c\u95e8\u7684\u79d8\u4f20\u6b66\u6280\u4e4b\u65f6\uff0c\u662f\u51b3\u4e0d\u6b22\u8fce\u5916\u4eba\u65c1 \u89c2\u7684\u3002\u6545\u800c\u5077\u770b\u522b\u4eba\u7ec3\u6b66\uff0c\u5217\u4e3a\u6b66\u6797\u7981\u5fcc\u4e4b\u53e3\u3002\u79e6\u5143\u6d69\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5e78\u4e8f\u4ed6\u4eec\u6ca1\u53d1\u73b0\u6211\uff0c\u8981 \u4e0d\u7136\u53ea\u6015\u8981\u60f9\u51fa\u9ebb\u70e6\u3002\u6211\u53c8\u4e0d\u77e5\u9053\u8fd9\u5bb6\u4eba\u5bb6\u7684\u6765\u5386\uff0c\u8fd8\u662f\u7b49\u5f85\u5230\u4e86\u6c5f\u5bb6\u4e4b\u540e\uff0c\u5411\u540c\u9053\u7684\u957f\u8f88 \u6253\u542c\uff0c\u77e5\u5f97\u6e05\u695a\u4e86\uff0c\u518d\u6765\u7ed3\u4ea4\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u60f3\u8981\u8d70\u5f00\uff0c\u4f46\u8fd9\u5bf9\u5c11\u5e74\u7537\u5973\u8fd8\u5728\u56ed\u4e2d\uff0c\u79e6\u5143\u6d69\u4e00\u8d70\uff0c\u53ea\u6015\u4f1a\u7ed9\u4ed6\u4eec\u53d1\u73b0\u3002\u56e0\u4e3a\u79e6 \u5143\u6d69\u867d\u7136\u65e0\u610f\u5077\u770b\u522b\u4eba\u7ec3\u6b66\uff0c\u4e5f\u53ea\u597d\u518d\u770b\u4e0b\u53bb\u4e86\u3002\u53ea\u542c\u5f97\u90a3\u5c11\u5e74\u8bf4\u9053\uff1a\u201c\u5ae6\u59b9\uff0c\u54b1\u4eec\u53ea\u662f\u5f7c \u6b64\u5207\u78cb\uff0c\u600e\u8c08\u5f97\u4e0a\u4ec0\u4e48\u4f20\u6388\uff1f\u8fd9\u5957\u5251\u6cd5\u662f\u6211\u7ec3\u4e86\u591a\u5e74\u7684\uff0c\u5f53\u7136\u53ef\u4ee5\u7531\u6211\u6559\u4f60\uff0c\u4f46\u8bf4\u5230\u6697\u5668\u529f \u592b\uff0c\u6211\u53ef\u5c31\u8981\u5411\u4f60\u8bf7\u6559\u4e86\u3002\u5bf9\u5566\uff0c\u54b1\u4eec\u4eca\u5929\u4e0d\u7ec3\u5251\u672f\u4e86\uff0c\u7ee7\u7eed\u518d\u7ec3\u6697\u5668\u5982\u4f55\uff1f\u542c\u8bf4\u4f60\u7684\u6885\u82b1 \u9488\u6253\u5f97\u51fa\u795e\u5165\u5316\uff0c\u9732\u4e00\u624b\u7ed9\u6211\u5f00\u5f00\u773c\u754c\u5427\uff0c\u4e5f\u597d\u8ba9\u6211\u5b66\u5b66\u9ad8\u62db\u3002\u201d \u3000\u3000\u5c11\u5e74\u8fd9\u4e48\u4e00\u6367\uff0c\u8fd9\u5c11\u5973\u624d\u5316\u55d4\u4e3a\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u7ed9\u6211\u4e71\u6234\u9ad8\u5e3d\uff0c\u6211\u7239\u7239\u8bf4\uff0c\u4f60\u7684\u53d4\u7956 \u6700\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\uff0c\u4f60\u7684\u6697\u5668\u529f\u592b\u600e\u4f1a\u6bd4\u4e0d\u4e0a\u6211\uff1f\u662f\u5b58\u5fc3\u8981\u770b\u6211\u7684\u7b11\u8bdd\u5427\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u542c\u4e86\uff0c\u4e0d\u89c9\u60ca\u7591\u4e0d\u5b9a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5f53\u4eca\u7684\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\uff0c\u8c01\u4e0d\u77e5\u9053\u662f\u6c5f\u5927\u4fa0\uff1f \u54ea\u6765\u7684\u53c8\u4e00\u4e2a\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\uff1f\u82e5\u8bf4\u8fd9\u5c11\u5e74\u7684\u53d4\u7956\u5c31\u662f\u6c5f\u5927\u4fa0\u5427\uff0c\u4f46\u6c5f\u5927\u4fa0\u4eca\u5e74\u4e0d\u8fc7\u56db\u5341\u51fa \u5934\uff0c\u600e\u80fd\u5c31\u6709\u4f84\u5b59\uff1f\u4f55\u51b5\u4e5f\u6ca1\u542c\u8bf4\u6c5f\u5927\u4fa0\u53e6\u6709\u5144\u5f1f\uff1f\u201d \u3000\u3000\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u6b66\u529f\u4e4b\u9053\uff0c\u5404\u6709\u6240\u957f\uff0c\u4f60\u5bb6\u7684\u70b9\u7a74\u6cd5\u4e0e\u6697\u5668\u529f\u592d\uff0c\u6211\u7684\u53d4\u7956\u4e5f\u662f\u5f88\u4f69\u670d \u7684\u3002\u4f60\u522b\u5ba2\u6c14\u4e86\uff0c\u793c\u5c1a\u5f80\u6765\uff0c\u4f60\u4e5f\u8be5\u6559\u6559\u6211\u4e86\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u8d77\u521d\u4ee5\u4e3a\u4ed6\u4eec\u662f\u540c\u95e8\u7684\u5e08\u5144\u59b9\u62c6\u62db\uff0c\u5982\u4eca\u624d\u77e5\u9053\u4e0d\u662f\u3002 \u3000\u3000\u5c11\u5973\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u65e2\u7136\u8d70\u8981\u770b\u6211\u7b11\u8bdd\uff0c\u90a3\u6211\u5c31\u53ea\u597d\u732e\u4e11\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u638f\u51fa\u4e86\u4e00\u628a\u6885 \u82b1\u9488\uff0c\u81ea\u8a00\u81ea\u6697\u9053\uff1a\u201c\u600e\u4e48\u7ec3\u5462\uff1f\u55ef\uff0c\u6709\u4e86\uff0c\u8fd9\u4e9b\u55e1\u55e1\u53eb\u7684\u871c\u8702\u4ee4\u4eba\u8ba8\u538c\uff0c\u5f85\u6211\u628a\u5b83\u6253 \u4e0b\u3002\u201d \u3000\u3000\u5c11\u5973\u9644\u8fd1\u7684\u6842\u6811\u4e0a\uff0c\u6b63\u6709\u4e00\u7fa4\u871c\u8702\u98de\u6765\u91c7\u82b1\u917f\u871c\u3002\u5c11\u5973\u8bf4\u7f62\uff0c\u628a\u624b\u4e00\u626c\uff0c\u53ea\u89c1\u91d1\u5149\u95ea \u70c1\uff0c\u4e00\u5927\u7fa4\u871c\u8702\u7eb7\u7eb7\u5760\u4e0b\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u559d\u5f69\u9053\uff1a\u201c\u597d\uff0c\u597d\u529f\u592b\uff0c\u96be\u5f97\u7684\u662f\u6bcf\u4e00\u53ea\u871c\u8702\u90fd\u7740\u4e86\u4e00\u53e3\u6885\u82b1\u9488\uff0c\u4e0d\u591a\u4e5f\u4e0d\u5c11\uff0c \u8fd9\u624b\u529f\u592b\u6bd4\u2018\u5929\u5973\u6563\u82b1\u2019\u8981\u9ad8\u660e\u591a\u4e86\u3002\u201d \u3000\u3000\u5c11\u5973\u7b11\u9053\uff1a\u201c\u4f60\u5012\u662f\u4e2a\u8bc6\u8d27\u7684\u884c\u5bb6\uff0c\u5982\u4eca\u8be5\u770b\u4f60\u7684\u5566\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u89c1\u4e86\u8fd9\u5c11\u5973\u7684\u6697\u5668\u529f\u592b\uff0c\u4e5f\u4e0d\u7981\u6697\u6697\u5403\u60ca\uff0c\u4f46\u5fc3\u91cc\u5374\u6700\u60f3\u9053\uff1a\u201c\u8fd9\u5c11\u5973\u7684\u6697\u5668\u624b \u6cd5\u786e\u662f\u9ad8\u660e\uff0c\u5374\u672a\u514d\u592a\u6b8b\u5fcd\u4e86\u3002\u4e14\u770b\u8fd9\u5c11\u5e74\u7684\u6697\u5668\u529f\u592b\u53c8\u662f\u5982\u4f55\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u5e76\u4e0d\u5ba2\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u8981\u6211\u732e\u4e11\uff0c\u6211\u4e5f\u53ea\u597d\u4ece\u547d\u3002\u201d\u8bf4\u7f62\uff0c\u8138\u513f\u671d\u5916\uff0c\u500f \u5730\u628a\u624b\u4e00\u626c\u3002 \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4f60\u6253\u4ec0\u4e48\uff1f\u201d\u5c31\u5728\u5c11\u5973\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u79e6\u5143\u6d69\u53ea\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u5bf9\u65b9\u7684\u6697\u5668\u5df2\u7136\u6253 \u5230\u3002\u539f\u6765\u8fd9\u5c11\u5e74\u662f\u628a\u4ed6\u5f53\u4f5c\u6697\u5668\u7684\u76ee\u6807\u7684\u3002 \u3000\u3000\u79e6\u5143\u6d69\u51b7\u4e0d\u53ca\u9632\uff0c\u9669\u4e9b\u7ed9\u4ed6\u6253\u7740\uff0c\u8fde\u5fd9\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u65bd\u5c55\u201c\u5f39\u6307\u795e\u901a\u201d\u7684\u4e0a\u4e58\u6b66 \u5b66\uff0c\u94ee\u3001\u94ee\u3001\u94ee\u4e09\u58f0\u54cd\u8fc7\uff0c\u4e09\u679a\u900f\u9aa8\u9489\u7ed9\u4ed6\u5f39\u5f97\u98de\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u65b9\u624d\u843d\u5730\u3002\u4f46\u79e6\u5143\u6d69\u7684\u6307\u5934 \u4e5f\u5fae\u89c9\u75bc\u75db\uff0c\u4ed6\u4e0e\u8fd9\u5c11\u5e74\u4e4b\u95f4\u8ddd\u79bb\u6709\u4e8c\u5341\u4e08\u5f00\u5916\uff0c\u800c\u4e14\u8fd9\u5c11\u5e74\u662f\u5728\u5c71\u5761\u4e0b\u9762\u6253\u4e0a\u6765\u7684\uff0c\u6253\u5230 \u4e8c\u5341\u4e08\u5f00\u5916\uff0c\u5c45\u7136\u8fd8\u6709\u5982\u6b64\u52b2\u9053\uff0c\u79e6\u5143\u6d69\u4e5f\u4e0d\u7981\u5927\u529b\u60ca\u9a87\u4e86\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u628a\u900f\u9aa8\u9489\u4e00\u53d1\uff0c\u731b\u7684\u5c31\u5927\u559d\u9053\uff1a\u201c\u4f55\u65b9\u5c0f\u5b50\uff0c\u80c6\u6562\u5077\u770b\u6211\u4eec\u7ec3\u6b66\uff0c\u4f60\u5f53\u6211\u4eec\u4e0d\u77e5 \u9053\u5417\uff1f\u5feb\u5feb\u7ed9\u6211\u4eec\u6eda\u51fa\u6765\uff01\u201d\u5c11\u5973\u5219\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u7684\u529f\u592b\u4e5f\u8fd8\u5f53\u771f\u4e0d\u574f\u5462\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u672c\u6765\u5c31\u6709\u4e0e\u4ed6\u4eec\u7ed3\u4ea4\u7684\u5fc3\u610f\uff0c\u53ea\u56e0\u6015\u72af\u4e86\u6c5f\u6e56\u7981\u5fcc\uff0c\u624d\u4e0d\u6562\u51fa\u6765\u3002\u4f46\u5982\u4eca\u65e2\u7136\u662f \u7ed9\u4ed6\u4eec\u53d1\u73b0\uff0c\u4e5f\u5c31\u53ea\u597d\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u79e6\u5143\u6d69\u8dd1\u4e0b\u5c71\u5761\uff0c\u8dc3\u8fc7\u56f4\u5899\uff0c\u5230\u4e86\u56ed\u4e2d\uff0c\u5411\u90a3\u5c11\u5e74\u62f1\u4e00\u62f1\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u662f\u6b66 \u5f53\u6d3e\u7684\u5f1f\u5b50\u79e6\u5143\u6d69\uff0c\u8def\u8fc7\u6b64\u5730\uff0c\u5e76\u975e\u6709\u610f\u5077\u7aa5\u3002\u8bf7\u5144\u53f0\u539f\u8c05\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u81ea\u62a5\u5e08\u95e8\u6765\u5386\uff0c\u4e00\u6765\u662f\u4f9d\u7167\u6c5f\u6e56\u89c4\u77e9\uff0c\u5411\u5bf9\u65b9\u8868\u793a\u5c0a\u91cd\u7684\u610f\u601d\u3002\u4e8c\u6765\u4e5f\u662f\u5e0c\u671b\u53d6 \u5f97\u5bf9\u65b9\u7684\u597d\u611f\uff0c\u4e0d\u81f3\u4e8e\u5bf9\u4ed6\u6709\u6240\u8bef\u89e3\u3002\u8981\u77e5\u5c11\u6797\u3001\u6b66\u5f53\uff0c\u5e76\u9a7e\u9f50\u9a71\uff0c\u4e43\u662f\u6b66\u6797\u4e2d\u6700\u5927\u7684\u4e24\u4e2a \u95e8\u6d3e\u3002\u522b\u4eba\u542c\u5f97\u6b66\u5f53\u7684\u540d\u5934\uff0c\u591a\u5c11\u4f1a\u5bf9\u4ed6\u6709\u51e0\u5206\u5c0a\u91cd\u3002 \u3000\u3000\u4e0d\u6599\u8fd9\u5c11\u5e74\u53d7\u4e86\u79e6\u5143\u6d69\u7684\u4e00\u63d6\uff0c\u5927\u523a\u523a\u7684\u7adf\u4e0d\u8fd8\u793c\uff0c\u5374\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7ba1\u4f60\u662f\u4ec0\u4e48\u6b66\u5f53\u5f1f \u5b50\uff0c\u4f60\u5077\u5b66\u6211\u4eec\u7684\u5251\u672f\uff0c\u5c31\u662f\u4e0d\u8be5\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u662f\u4e2a\u5916\u5706\u5185\u65b9\u7684\u4eba\uff0c\u4e5f\u5f88\u6709\u51e0\u5206\u50b2\u6c14\u7684\u3002\u5c3d\u7ba1\u4ed6\u60f3\u4e0e\u5bf9\u65b9\u7ed3\u4ea4\uff0c\u4f46\u542c\u4e86\u5c11\u5e74\u8fd9\u6837 \u4e0d\u5ba2\u6c14\u7684\u8bf4\u8bdd\uff0c\u4e5f\u4e0d\u89c9\u52a8\u4e86\u6012\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5144\u53f0\u7684\u5251\u672f\u786e\u5c5e\u9ad8\u660e\uff0c\u4f46\u6211\u6b66\u5f53\u5f1f\u5b50\uff0c\u8fd8\u4e0d\u81f3\u4e8e \u662f\u5077\u5b66\u522b\u4eba\u6b66\u827a\u4e4b\u8f88\uff01\u201d \u3000\u3000\u5c11\u5e74\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6b66\u5f53\u5f1f\u5b50\u53c8\u600e\u4e48\u6837\uff1f\u597d\uff0c\u6211\u5c31\u9886\u6559\u4f60\u7684\u6b66\u5f53\u5251\u672f\uff01\u201d\u957f\u5251 \u4e8c\u6307\uff0c\u75be\u5982\u95ea\u7535\uff0c\u9661\u7136\u98de\u8d77\u51e0\u6735\u5251\u82b1\uff0c\u5c31\u5411\u79e6\u5143\u6d69\u653b\u4e86\u8fc7\u53bb\u3002\u4e00\u62db\u4e4b\u95f4\uff0c\u8fde\u523a\u79e6\u5143\u6d69\u7684\u4e09\u5904 \u5927\u7a74\u3002 \u3000\u3000\u79e6\u5143\u6d69\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u80fd\u8fb1\u4e86\u672c\u6d3e\u7684\u5a01\u540d\u3002\u201d\u5728\u5bf9\u65b9\u82e6\u82e6\u76f8\u8feb\u4e4b\u4e0b\uff0c\u79e6\u5143\u6d69\u4e5f\u53ea\u597d \u62d4\u5251\u62db\u67b6\u4e86\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u9053\uff1a\u201c\u5ae6\u59b9\uff0c\u4f60\u5c0f\u5fc3\u7ec6\u770b\uff01\u201d\u5530\u7684\u4e00\u62db\u201c\u5927\u6f20\u5b64\u70df\u76f4\u201d\uff0c\u5251\u76f4\u5982\u5931\uff0c\u4f7f\u5f97\u8fc5\u6377\u65e0 \u6bd4\uff0c\u5251\u5c16\u6307\u5411\u79e6\u5143\u6d69\u7684\u9762\u95e8\uff0c\u7adf\u662f\u60f3\u523a\u778e\u4ed6\u7684\u773c\u775b\uff01 \u3000\u3000\u79e6\u5143\u6d69\u53c8\u60ca\u53c8\u6012\uff0c\u5fc3\u9053\uff1a\u201c\u5373\u4f7f\u6211\u662f\u5077\u7aa5\u4e86\u4f60\u7684\u6b66\u6280\uff0c\u4f60\u4e5f\u4e0d\u8be5\u51fa\u624b\u5982\u6b64\u72e0\u6bd2\uff01\u201d\u5f53\u4e0b \u53ea\u597d\u4e5f\u65bd\u5c55\u672c\u95e8\u7edd\u5b66\uff0c\u4e00\u62db\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff0c\u5251\u52bf\u4e00\u5c01\uff0c\u500f\u5730\u4e00\u7ffb\u4e00\u7ede\uff0c\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00 \u58f0\uff0c\u5c11\u5e74\u7684\u957f\u5251\u7ed9\u4ed6\u683c\u5f00\uff0c\u8eab\u5f62\u659c\u7a9c\u4e09\u6b65\u3002\u5c11\u5973\u683c\u683c\u7b11\u9053\uff1a\u201c\u6211\u4ed4\u7ec6\u770b\u4e86\uff0c\u539f\u6765\u4f60\u8fd9\u4e00\u62db \u2018\u5927\u6f20\u5b64\u70df\u76f4\u2019\u662f\u53ef\u4ee5\u8fd9\u6837\u7834\u89e3\u7684\u3002\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u672c\u6765\u662f\u60f3\u5728\u610f\u4e2d\u4eba\u7684\u9762\u524d\u70ab\u8000\u4ed6\u7684\u5251\u6cd5\u7684\uff0c\u4e0d\u6599\u4f24\u4e0d\u7740\u4eba\u5bb6\uff0c\u53cd\u800c\u7ed9\u4eba\u5bb6\u8feb\u9000\u4e09 \u6b65\uff0c\u4e0d\u7981\u8001\u7f9e\u6210\u6012\uff0c\u559d\u9053\u3002\u201d\u597d\uff0c\u53eb\u4f60\u8fd9\u5c0f\u5b50\u77e5\u9053\u5389\u5bb3\u3002\u201d\u957f\u5251\u4e00\u5708\uff0c\u63a5\u7740\u4e00\u62db\u201c\u957f\u6cb3\u843d\u65e5 \u5706\u201d\uff0c\u5251\u5149\u98de\u821e\uff0c\u500f\u7136\u95f4\u5408\u6210\u4e86\u4e00\u9053\u5149\u73af\uff0c\u5c06\u79e6\u5143\u6d69\u7684\u8eab\u5f62\u7b3c\u7f69\u5728\u4ed6\u7684\u5251\u5149\u4e4b\u4e0b\uff0c\u5018\u82e5\u7ed9\u4ed6 \u8fd9\u62db\u5f97\u624b\uff0c\u79e6\u5143\u6d69\u5c31\u8981\u7ed9\u4ed6\u62e6\u8170\u65a9\u4e3a\u4e24\u622a\u3002 \u3000\u3000\u79e6\u5143\u6d69\u89c1\u4ed6\u8d8a\u6765\u8d8a\u72e0\uff0c\u5fc3\u4e2d\u706b\u8d77\uff0c\u60f3\u9053\uff1a\u201c\u4e0d\u8fd8\u4ed6\u4e00\u70b9\u989c\u8272\uff0c\u4ed6\u53ea\u5f53\u6211\u662f\u597d\u6b3a\u8d1f\u7684 \u4e86\u3002\u201d\u4e8e\u662f\u5251\u5c16\u4e00\u6311\uff0c\u4ece\u5149\u73af\u4e2d\u7a7f\u5165\uff0c\u4e00\u62db\u201c\u6a2a\u626b\u516d\u5408\u201d\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\uff0c\u4e0d\u7edd\u4e8e \u8033\uff0c\u970e\u7136\u95f4\u5251\u5149\u6d41\u6563\u3002\u5c11\u5e74\u7684\u8fd9\u62db\u201c\u957f\u6cb3\u843d\u65e5\u5706\u201d\u53c8\u7ed9\u4ed6\u7834\u4e86\u3002 \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u53ef\u4ee5\u7f62\u624b\u4e86\u5427\uff1f\u201d\u5c11\u5e74\u559d\u9053\uff1a\u201c\u80dc\u8d1f\u672a\u51b3\uff0c\u7109\u80fd\u7f62\u624b\uff1f\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u75be\u653b \u4e09\u62db\uff0c\u4e00\u62db\u72e0\u8fc7\u4e00\u62db\u3002\u4ed6\u8fd9\u5957\u5251\u6cd5\u786e\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u6bcf\u4e00\u62db\u90fd\u6709\u7740\u597d\u51e0\u4e2a\u53d8\u5316\uff0c\u8fde\u73af\u4e09\u62db\uff0c\u4e00 \u6c14\u5475\u6210\u3002\u5e78\u4e8f\u79e6\u5143\u6d69\u770b\u8fc7\u4ed6\u4e0e\u8fd9\u5c11\u5973\u62c6\u62db\uff0c\u7a0d\u5fae\u6478\u5230\u4e00\u70b9\u5e95\u5b50\uff0c\u8fd9\u624d\u4e0d\u81f3\u4e8e\u7ed9\u4ed6\u6740\u5f97\u624b\u5fd9\u811a \u4e71\u3002 \u3000\u3000\u79e6\u5143\u6d69\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e0d\u80af\u7f62\u4f11\uff0c\u6211\u82e5\u53ea\u5b88\u4e0d\u653b\uff0c\u7ec8\u987b\u5403\u4ed6\u7684\u4e8f\u3002\u201d\u8981\u77e5\u6b66\u5f53\u6d3e\u7684 \u4e03\u5341\u4e8c\u624b\u8fde\u73af\u593a\u547d\u5251\u6cd5\u672c\u6765\u662f\u4ee5\u653b\u4e3a\u4e3b\u7684\uff0c\u7528\u6765\u9632\u5b88\uff0c\u5b9e\u662f\u4e0d\u80fd\u53d1\u6325\u5251\u6cd5\u4e4b\u957f\u3002 \u3000\u3000\u79e6\u5143\u6d69\u4e00\u58f0\u957f\u5578\uff0c\u8bf4\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff01\u5bf9\u4e0d\u4f4f\uff0c\u6211\u53ef\u8981\u8fd8\u62db\u4e86\uff01\u201d\u624b\u8d77\u5251\u843d\uff0c\u5de6 \u523a\u4e24\u5251\uff0c\u53f3\u523a\u4e24\u5251\uff0c\u4e2d\u95f4\u53c8\u523a\u4e09\u5251\u3002\u201d\u51fa\u624b\u4e03\u62db\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u5f0f\u5f0f\u4e0d\u540c\u3002\u5c11\u5973\u5728\u65c1\u8fb9\u770b\u5f97\u76ee \u7729\u795e\u6447\uff0c\u5931\u58f0\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u6587\u5927\u54e5\uff0c\u4ed6\u7684\u5251\u6cd5\u4f3c\u4e4e\u6bd4\u4f60\u8fd8\u5feb\u51e0\u5206\u5462\uff01\u201d \u3000\u3000\u5c11\u5e74\u9762\u82e5\u5bd2\u971c\uff0c\u4ed6\u5728\u79e6\u5143\u6d69\u7684\u8fde\u73af\u593a\u547d\u5251\u6cd5\u6025\u653b\u4e4b\u4e0b\uff0c\u5df2\u662f\u5206\u4e0d\u51fa\u5fc3\u795e\u4e0e\u8fd9\u5c11\u5973\u8bf4\u8bdd\u3002 \u53ea\u89c1\u4ed6\u84e6\u5730\u5e73\u5730\u62d4\u8d77\u6570\u5c3a\uff0c\u957f\u5251\u6a2a\u7a7a\u4e00\u63a0\u3002\u5251\u950b\u81ea\u5de6\u800c\u53f3\uff0c\u5ffd\u5730\u5728\u4e2d\u9014\u4e00\u8f6c\u3002\u5251\u52bf\u9661\u7136\u8fed \u8f6c\uff0c\u51fa\u624b\u5982\u6b64\u4e4b\u5feb\uff0c\u62db\u6570\u968f\u5fc3\u8f6c\u6362\uff0c\u8fd9\u5728\u5251\u672f\u4e2d\u4e5f\u662f\u6781\u96be\u7ec3\u7684\u4e86\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u4e00\u8d77\u4e00\u843d\uff0c\u5251\u5149\u6a2a\u7a7a\u4e00\u63a0\uff0c\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u4e5f\u4f7f\u51fa\u4e86\u4e94\u79cd\u4e0d\u540c\u7684\u62db\u6570\u3002\u53ea\u542c\u5f97\u53c8 \u662f\u4e00\u7247\u65ad\u91d1\u788e\u7389\u4e4b\u58f0\uff0c\u79e6\u5143\u6d69\u7684\u8fde\u73af\u4e03\u5251\uff0c\u7adf\u4e5f\u7ed9\u4ed6\u5316\u89e3\u5f00\u4e86\u3002\u79e6\u5143\u6d69\u89c1\u4ed6\u89e3\u5f97\u5982\u6b64\u7cbe\u5999\uff0c \u5fc3\u4e2d\u4e5f\u4e0d\u7531\u5f97\u6697\u6697\u4f69\u670d\u3002 \u3000\u3000\u79e6\u5143\u6d69\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u610f\u6b32\u5c31\u6b64\u6536\u624b\u3002\u4e0d\u6599\u90a3\u5c11\u5e74\u53c8\u653b\u8fc7\u6765\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u7684\u5251 \u6cd5\u662f\u597d\u662f\u574f\uff0c\u7528\u4e0d\u7740\u4f60\u6765\u8bc4\u8bba\u3002\u54fc\uff0c\u4f60\u4eec\u6b66\u5f53\u6d3e\u7684\u6240\u8c13\u2018\u8fde\u73af\u593a\u547d\u5251\u6cd5\u201d\u4e5f\u4e0d\u89c1\u5f97\u5c31\u593a\u5f97\u4e86 \u6211\u7684\u6027\u547d\u3002\u201d\u75be\u653b\u8fc7\u6765\uff0c\u8eab\u968f\u5251\u8fdb\uff0c\u5de6\u4e00\u62db\u201c\u661f\u5782\u5e73\u91ce\u9614\u201d\uff0c\u53f3\u4e00\u62db\u201c\u6708\u6d8c\u5927\u6c5f\u6d41\u201d\uff0c\u5251\u5149 \u970d\u970d\u5c55\u5f00\uff0c\u5f53\u771f\u662f\u6709\u82e5\u957f\u6c5f\u5927\u5777\uff0c\u6eda\u6eda\u800c\u4e0a\u3002 \u3000\u3000\u79e6\u5143\u6d69\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6b64\u4eba\u7b80\u76f4\u662f\u4e0d\u53ef\u7406\u55bb\uff0c\u8bf4\u4e0d\u5f97\u6211\u53ea\u597d\u4e0e\u4ed6\u8ba4\u771f\u53ae\u6740\u4e00\u573a\u4e86\u3002\u201d\u79e6\u5143 \u6d69\u6709\u6240\u4e0d\u77e5\uff0c\u8fd9\u5c11\u5e74\u5012\u4e0d\u662f\u86ee\u4e0d\u8bb2\u7406\uff0c\u800c\u662f\u6c14\u91cf\u72ed\u7a84\uff0c\u4ed6\u6c14\u5fff\u4ed6\u7684\u610f\u4e2d\u4eba\u79f0\u8d5e\u4e86\u79e6\u5143\u6d69\u7684\u5251 \u6cd5\uff0c\u6545\u800c\u975e\u628a\u79e6\u5143\u6d69\u632b\u6298\u4e0d\u53ef\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u8eab\u968f\u5251\u8fdb\uff0c\u5251\u6cd5\u5c55\u5f00\uff0c\u51f6\u731b\u5904\u6709\u5982\u5954\u96f7\u9a87\u7535\uff0c\u8f7b\u7075\u5999\u53c8\u5b9b\u82e5\u6d41\u6c34\u884c\u4e91\uff0c\u786e\u662f\u4e0d\u5bb9 \u5c0f\u89d1\uff0c\u79e6\u5143\u6d69\u4e4d\u9022\u52b2\u654c\uff0c\u6296\u64de\u7cbe\u795e\uff0c\u628a\u201c\u8fde\u73af\u593a\u547d\u5251\u6cd5\u201d\u4f7f\u5f97\u51cc\u5389\u65e0\u80fd\uff0c\u970e\u7136\u95f4\u53ea\u89c1\u6ee1\u573a\u90fd \u662f\u5251\u5149\uff0c\u5ffd\u4e1c\u5ffd\u897f\uff0c\u5ffd\u805a\u5ffd\u6563\uff0c\u5b9b\u5982\u6c34\u94f6\u6cfb\u5730\uff0c\u82b1\u96e8\u7f24\u7eb7\uff01\u573a\u4e2d\u53ea\u6709\u4e24\u4eba\u6bd4\u5251\uff0c\u5374\u4f3c\u6709\u5e72\u519b \u4e07\u9a6c\u5728\u5954\u817e\u8ffd\u9010\uff0c\u4e0d\u591a\u4e00\u4f1a\uff0c\u53cc\u65b9\u8d8a\u6597\u8d8a\u7d27\uff0c\u4f46\u89c1\u5251\u5149\uff0c\u4e0d\u89c1\u4eba\u5f71\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u521d\u65f6\u8fd8\u662f\u795e\u8272\u4ece\u5bb9\u7684\u6ce8\u76ee\u800c\u89c2\uff0c\u968f\u7740\u4ed6\u4eec\u8d8a\u6597\u8d8a\u7d27\uff0c\u8fd9\u5c11\u5973\u7684\u5fc3\u60c5\u4e5f\u4e0d\u89c9\u8d8a\u6765\u8d8a \u662f\u7d27\u5f20\u3002\u5f85\u5230\u53ea\u89c1\u5251\u5149\u4e0d\u89c1\u4eba\u5f71\u4e4b\u65f6\uff0c\u5979\u5df2\u662f\u4e0d\u7531\u81ea\u5df1\u7684\u60ca\u614c\u8d77\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u6015\u7684\u662f\u201c\u4e24\u864e\u76f8\u6597\uff0c\u5fc5\u6709\u4e00\u4f24\u3002\u201d\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4f24\u4e86\u6587\u5927\u54e5\u56fa\u7136\u4e0d\u597d\uff0c\u4f46\u82e5\u4f24\u4e86 \u8fd9\u59d3\u79e6\u7684\u5c11\u5e74\uff0c\u8fd9\uff0c\u8fd9\u4e5f\u662f\u4e0d\u597d\u3002\u4ed6\u5077\u7aa5\u6211\u4eec\u7ec3\u6b66\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e00\u70b9\u70b9\u5c0f\u4e8b\uff0c\u91cd\u4f24\u4e86\u4ed6\u4e8e\u5fc3\u4f55 \u5fcd\uff1f\u800c\u4e14\u4ed6\u662f\u6b66\u5f53\u6d3e\u7684\u8363\u5b50\uff0c\u4f24\u4e86\u4ed6\u53ea\u6015\u4e5f\u4f1a\u7559\u4e0b\u65e0\u7a77\u540e\u7978\u3002\u53ef\u662f\uff0c\u6211\u53c8\u6ca1\u6709\u80fd\u8010\u5c06\u4ed6\u4eec\u5206 \u5f00\uff0c\u8fd9\u53ef\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u5c11\u5973\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u6ee1\u7a7a\u5251\u5149\u6536\u655b\u3002\u539f\u6765\u4ed6\u4eec\u53cc\u65b9\u90fd\u7528\u82e5\u662f\u5feb\u5251\u75be \u653b\uff0c\u6709\u4e00\u62db\u6070\u597d\u78b0\u4e0a\u3002\u53cc\u5251\u76f8\u4ea4\uff0c\u5404\u4ee5\u5185\u529b\u76f8\u6597\u3002 \u3000\u3000\u59d3\u6587\u7684\u8fd9\u4e2a\u5c11\u5e74\u4e0e\u79e6\u5143\u6d69\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u5df2\u77e5\u6b66\u5f53\u6d3e\u7684\u201c\u8fde\u73af\u593a\u547d\u5251\u6cd5\u201d\u679c\u662f\u975e\u540c\u5c0f \u53ef\uff0c\u4e45\u6218\u4e0b\u53bb\uff0c\u53ea\u4f2f\u81ea\u5df1\u7a0d\u6709\u758f\u865e\uff0c\u4fbf\u8981\u5403\u4e8f\u3002\u6545\u800c\u53cc\u5251\u4e00\u4ea4\uff0c\u4ed6\u4fbf\u7acb\u5373\u7528\u4e2a\u201c\u538b\u201d\u5b57\u8bc0\u5c06 \u79e6\u5143\u6d69\u538b\u4f4f\uff0c\u4e0d\u8bb8\u4ed6\u62bd\u51fa\u5251\u6765\u3002\u610f\u6b32\u51ed\u85c9\u672c\u8eab\u7684\u5185\u529f\uff0c\u5c06\u4ed6\u538b\u670d\u3002 \u3000\u3000\u79e6\u5143\u6d69\u6b63\u60f3\u62bd\u51fa\u5251\u6765\uff0c\u5ffd\u89c9\u4e00\u80a1\u5927\u529b\u4f3c\u6697\u6d41\u822c\u7684\u7a81\u7136\u6c79\u6d8c\u800c\u81f3\uff0c\u51b2\u51fb\u4ed6\u7684\u864e\u53e3\u3002\u79e6\u5143\u6d69 \u5fc3\u9053\u3002\u201d\u539f\u6765\u8fd9\u5c0f\u5b50\u5c45\u7136\u4e5f\u4f1a\u9694\u7269\u4f20\u529f\u3002\u201d\u672c\u6765\u4ee5\u79e6\u5143\u6d69\u7684\u529f\u529b\uff0c\u4ed6\u8981\u62bd\u51fa\u5251\u6765\uff0c\u8fd8\u662f\u53ef\u4ee5 \u7684\uff0c\u4f46\u79e6\u5143\u6d69\u662f\u4e2a\u5916\u5706\u5185\u65b9\u7684\u4eba\uff0c\u5e74\u8f7b\u4eba\u4e5f\u96be\u514d\u5e26\u6709\u51e0\u5206\u50b2\u6c14\uff0c\u5728\u5bf9\u65b9\u7d27\u7d27\u76f8\u8feb\u4e4b\u4e0b\uff0c\u4e0d\u89c9 \u4e5f\u8d77\u4e86\u4e89\u80dc\u4e4b\u5fc3\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u82e5\u62bd\u5251\uff0c\u4ed6\u53ea\u5f53\u6211\u662f\u6015\u4e86\u4ed6\u3002\u597d\uff0c\u6211\u5c31\u4e0e\u4ed6\u8f83\u91cf\u8f83\u91cf\u5185 \u529f\u3002\u201d\u5f53\u4e0b\uff0c\u4e5f\u8fd0\u5185\u529f\u53cd\u51fb\u3002\u5230\u4e86\u53cc\u65b9\u7684\u5185\u529b\u4e92\u76f8\u51b2\u51fb\u7684\u65f6\u5019\uff0c\u90a3\u5c31\u8c01\u4e5f\u4e0d\u80fd\u6536\u62db\u7f62\u624b\u4e86\u3002 \u3000\u3000\u8f6c\u773c\u95f4\u4e24\u4eba\u90fd\u662f\u5927\u6c57\u6dcb\u6f13\uff0c\u4f46\u79e6\u5143\u6d69\u7684\u795e\u8272\u8fd8\u6bd4\u8f83\u4ece\u5bb9\uff0c\u90a3\u59d3\u6587\u7684\u5c11\u5e74\u5219\u5df2\u662f\u9752\u7b4b\u66b4 \u9732\uff0c\u6bd4\u4ed6\u72fc\u72c8\u5f97\u591a\u3002\u539f\u6765\u79e6\u5143\u6d69\u6240\u5b66\u7684\u4e43\u662f\u6b63\u5b97\u5185\u529f\uff0c\u8f83\u4e3a\u7eaf\u539a\uff0c\u90a3\u59d3\u6587\u7684\u5c11\u5e74\u6240\u5b66\u7684\u5219\u662f \u90aa\u6d3e\u5185\u529f\uff0c\u521d\u4ea4\u624b\u662f\u6781\u4e3a\u9738\u9053\u3002\u65f6\u95f4\u7a0d\u4e71\u514b\u5236\u5bf9\u65b9\u4e0d\u4e0b\uff0c\u5c31\u6e10\u6e10\u53d8\u6210\u4e86\u5f3a\u5f29\u4e4b\u672b\u4e86\u3002 \u3000\u3000\u5185\u529f\u7684\u8f83\u91cf\u975e\u6bd4\u5bfb\u5e38\uff0c\u4e00\u4e2a\u4e0d\u654c\uff0c\u5c31\u6709\u6027\u547d\u4e4b\u5fe7\u3002\u6b64\u65f6\u8fd9\u59d3\u6587\u7684\u5c11\u5e74\u53c8\u662f\u540e\u6094\u53c8\u662f\u7740 \u6025\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u65e9\u77e5\u8fd9\u5c0f\u5b50\u6709\u5982\u6b64\u529f\u529b\uff0c\u6211\u4e0d\u5982\u548c\u4ed6\u6bd4\u5251\u8fd8\u597d\uff0c\u6bd4\u5251\u4e0d\u654c\uff0c\u6700\u591a\u4e0d\u8fc7\u53d7 \u4f24\u3002\u5982\u4eca\u8981\u60f3\u8f6c\u8d25\u4e3a\u80dc\uff0c\u9664\u975e\u662f\u59b9\u59b9\u52a9\u6211\u4e00\u81c2\u4e4b\u529b\u4e86\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u6b64\u65f6\u4e1a\u5df2\u7a33\u7a33\u5360\u4e86\u4e0a\u98ce\uff0c\u4f46\u80dc\u8d1f\u4f9d\u7136\u672a\u51b3\uff0c\u4ed6\u5fc5\u987b\u5168\u795e\u8d2f\u6ce8\u7684\u6765\u5bf9\u4ed8\u8fd9\u59d3\u6587\u7684\u5c11 \u5e74\uff0c\u6545\u6b64\u82e5\u5728\u6b64\u65f6\uff0c\u5373\u4f7f\u4e00\u4e2a\u6b66\u529f\u5f88\u5e73\u5eb8\u7684\u4eba\u5728\u4ed6\u80cc\u540e\u5077\u88ad\uff0c\u4ed6\u4e5f\u662f\u96be\u4ee5\u5206\u795e\u5e94\u4ed8\u7684\u3002 \u3000\u3000\u8fd9\u59d3\u6587\u7684\u5c11\u5e74\u5e73\u7d20\u5728\u8fd9\u5c11\u5973\u9762\u524d\u5938\u53e3\u60ef\u4e86\uff0c\u8fd9\u5c11\u5973\u4e5f\u662f\u4e00\u5411\u4f69\u670d\u4ed6\u7684\u6b66\u529f\u7684\u3002\u6b64\u65f6\u5730\u60f3 \u5411\u5979\u6c42\u52a9\uff0c\u5374\u662f\u82e6\u4e8e\u8bf4\u4e0d\u51fa\u53e3\u6765\uff0c\u5fc3\u91cc\u53c8\u662f\u7740\u6025\u53c8\u662f\u7740\u607c\uff1a\u201c\u5ae6\u59b9\u771f\u662f\u5c82\u6709\u6b64\u7406\uff0c\u96be\u9053\u5979\u8fd8 \u770b\u4e0d\u51fa\u6765\uff0c\u5374\u8fd8\u8896\u624b\u65c1\u89c2\uff1f\u201d\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u5411\u5979\u6253\u4e86\u4e00\u4e2a\u670d\u8272\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u867d\u7136\u4e0d\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u4f46\u80dc\u8d1f\u7684\u5173\u952e\u5979\u662f\u770b\u5f97\u51fa\u6765\u7684\u3002\u4e0d\u8fc7\uff0c\u5979\u82e5\u4e0a\u524d\u5077\u88ad\u79e6 \u5143\u6d69\u7684\u8bdd\uff0c\u79e6\u5143\u6d69\u4e00\u5b9a\u7ed9\u5979\u7684\u201c\u6587\u5927\u54e5\u201d\u6240\u6740\uff0c\u4e3a\u4e86\u4e00\u70b9\u5c0f\u4e8b\uff0c\u5c31\u6740\u4e86\u4e00\u4e2a\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\uff0c \u5373\u4f7f\u5979\u4e0d\u8ba1\u540e\u679c\uff0c\u4e5f\u662f\u89c9\u5f97\u4e8e\u5fc3\u4e0d\u5fcd\u7684\u3002\u53ef\u662f\u5979\u82e5\u4e0d\u4e0a\u53bb\u6697\u52a9\u201c\u6587\u5927\u54e5\u201d\u7684\u8bdd\uff0c\u5979\u53c8\u6015\u5979\u7684 \u201c\u6587\u5927\u54e5\u201d\u4e0d\u6b7b\u4e5f\u53d7\u91cd\u4f24\u3002\u662f\u4ee5\u5979\u5728\u8fd9\u5c11\u5e74\u5411\u5979\u6253\u4e86\u4e00\u4e2a\u773c\u8272\u4e4b\u540e\uff0c\u867d\u7136\u62d4\u5251\u51fa\u9798\uff0c\u4e00\u65f6\u95f4 \u5374\u4ecd\u662f\u8e0c\u8e87\u83ab\u51b3\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u5728\u79e6\u5143\u6d69\u7684\u80cc\u540e\uff0c\u5979\u62d4\u5251\u51fa\u9798\uff0c\u79e6\u5143\u6d69\u5e76\u4e0d\u77e5\u9053\u3002\u4f46\u90a3\u5c11\u5e74\u6240\u6253\u7684\u773c\u8272\u4ed6\u5374\u662f\u770b \u89c1\u4e86\u3002\u79e6\u5143\u6d69\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u4e0e\u4ed6\u672c\u6765\u5e76\u65e0\u4ec7\u6028\uff0c\u4f55\u82e6\u8981\u4f24\u4e86\u4ed6\uff1f\u770b\u4ed6\u5982\u6b64\u7126\u6025\u7684\u795e\u60c5\uff0c\u5927 \u7ea6\u5c31\u5feb\u652f\u6301\u4e0d\u4f4f\uff0c\u6025\u4e8e\u5411\u4eba\u6c42\u52a9\u4e86\u3002\u4e0d\u5982\u6211\u62fc\u7740\u5192\u70b9\u5371\u9669\uff0c\u5c31\u6b64\u7f62\u624b\uff0c\u5927\u5bb6\u90fd\u6709\u597d\u5904\u3002\u201d \u3000\u3000\u5176\u5b9e\u4ed6\u6b64\u65f6\u7f62\u624b\uff0c\u5e76\u975e\u5982\u4ed6\u6240\u60f3\u7684\u53ea\u662f\u201c\u5192\u70b9\u5371\u9669\u201d\uff0c\u800c\u662f\u8981\u5192\u7740\u6781\u5927\u7684\u5371\u9669\u7684\u3002\u56e0\u4e3a \u53cc\u65b9\u90fd\u6b63\u5728\u4ee5\u5168\u529b\u6bd4\u62fc\u5185\u529f\uff0c\u4ed6\u82e5\u662f\u7a81\u7136\u6536\u624b\uff0c\u5bf9\u65b9\u731b\u653b\u8fc7\u6765\u7684\u8bdd\uff0c\u4ed6\u5c31\u53ef\u80fd\u6709\u6740\u8eab\u4e4b\u7978\u3002 \u4f46\u79e6\u5143\u6d69\u662f\u4ee5\u541b\u5b50\u4e4b\u5fc3\u5ea6\u5c0f\u4eba\u4e4b\u8179\uff0c\u4ed6\u8ba4\u4e3a\u4ee5\u8fd9\u5c11\u5e74\u7684\u6b66\u5b66\u9020\u8be3\uff0c\u4ed6\u82e5\u7136\u4e34\u80dc\u6536\u624b\uff0c\u8fd9\u5c11\u5e74 \u51b3\u4e0d\u81f3\u4e8e\u4e0d\u77e5\u9053\u4ed6\u662f\u624b\u4e0b\u7559\u60c5\u3002\u65e2\u7136\u77e5\u9053\u4ed6\u662f\u624b\u4e0b\u7559\u60c5\uff0c\u96be\u9053\u8fd8\u4f1a\u4e58\u673a\u53d6\u4ed6\u6027\u547d\uff1f\u6545\u6b64\u4ed6\u8ba4 \u4e3a\u6240\u53d7\u7684\u5371\u9669\uff0c\u53ea\u4e0d\u8fc7\u662f\u5728\u6536\u624b\u7684\u90a3\u5239\u90a3\u95f4\uff0c\u6240\u53d7\u7684\u5bf9\u65b9\u7684\u5185\u529b\u9707\u64bc\u800c\u5df2\uff0c\u4ed6\u76f8\u4fe1\u4ee5\u4ed6\u7684\u5185 \u529f\u9020\u8be3\uff0c\u662f\u4e0d\u81f3\u4e8e\u53d7\u4f24\u7684\u3002 \u3000\u3000\u79e6\u5143\u6d69\u60f3\u5f97\u5982\u610f\uff0c\u4e0d\u6599\u90a3\u5c11\u5e74\u7684\u52a8\u4f5c\u5374\u5b8c\u5168\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u8fd9\u59d3\u6587\u7684\u5c11\u5e74\u4e00\u6765\u6068\u79e6\u5143\u6d69\u5728\u8fd9\u5c11\u5973\u7684\u9762\u524d\u5c06\u4ed6\u8f83\u91cf\u4e0b\u53bb\uff0c\u5927\u5927\u635f\u4f24\u4e86\u4ed6\u7684\u989c\u9762\uff1b\u4e8c\u6765 \u89c1\u8fd9\u5c11\u5973\u62d4\u5251\u51fa\u9798\uff0c\u5374\u8fdf\u8fdf\u4e0d\u80af\u4e0a\u7684\uff0c\u5fc3\u4e2d\u66f4\u4e3a\u6124\u6012\u3002\u79e6\u5143\u6d69\u7a81\u7136\u6536\u624b\uff0c\u4ed6\u4e0d\u5047\u601d\u7d22\uff0c\u4e00\u5251 \u5c31\u731b\u523a\u8fc7\u53bb\u3002 \u3000\u3000\u79e6\u5143\u6d69\u5927\u5403\u4e00\u60ca\uff0c\u4f46\u4ed6\u6bd5\u7adf\u662f\u6b66\u5f53\u9ad8\u5f92\uff0c\u5728\u8fd9\u6027\u547d\u4fc4\u9877\u4e4b\u95f4\uff0c\u663e\u51fa\u4ed6\u7684\u8d85\u51e1\u672c\u9886\uff0c\u4e00\u4e2a \u201c\u79fb\u5f62\u6362\u4f4d\u201d\uff0c\u7acb\u5373\u4fbf\u8fd8\u4e86\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\u4e43\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u4f9d\u7167\u5e38\u7406\uff0c\u8fd9\u5c11\u5e74\u5fc5\u987b\u95ea\u907f\uff0c\u540c\u65f6\u53d8\u62db\u62db\u67b6\u624d \u884c\u3002\u4f46\u4e0d\u6599\u8fd9\u5c11\u5e74\u51fa\u624b\u4e4b\u65f6\uff0c\u4ee5\u4e3a\u6709\u673a\u53ef\u4e58\uff0c\u5fd7\u5728\u5fc5\u5f97\uff0c\u4f7f\u7684\u7ade\u662f\u4e00\u62db\u6781\u4e3a\u9738\u9053\u7684\u5f3a\u653b\u62db \u6570\uff0c\u540d\u4e3a\u201c\u63d2\u7fbd\u7834\u5929\u9a84\u201d\uff0c\u4e00\u62db\u4e4b\u4e2d\uff0c\u5305\u542b\u7740\u4e09\u4e2a\u5f0f\u5b50\uff0c\u5fc5\u987b\u4e00\u6c14\u5475\u6210\uff0c\u624d\u80fd\u5236\u654c\u6b7b\u547d\u7684\u3002 \u8fd9\u5c11\u5e74\u552f\u6050\u5251\u52bf\u4e0d\u591f\u51cc\u5389\uff0c\u5168\u529b\u4f7f\u51fa\uff0c\u4e00\u65f6\u95f4\u54ea\u80fd\u6536\u5f97\u4f4f\u52bf\u5b50\uff1f\u201d \u3000\u3000\u773c\u770b\u53cc\u65b9\u5c31\u8981\u4e24\u8d25\u4ff1\u4f24\uff0c\u8fd9\u5c11\u5973\u5931\u58f0\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u5feb\u6765\uff01\u201d\u5ffd\u542c\u5f97\u201c\u94ee\u201d\u201c\u94ee\u201d\u4e24 \u58f0\uff0c\u5c31\u5728\u53cc\u65b9\u7684\u5251\u5c16\u582a\u582a\u5c31\u8981\u523a\u7740\u5bf9\u65b9\u7684\u65f6\u5019\uff0c\u7a81\u7136\u4e00\u6761\u4eba\u5f71\uff0c\u95ea\u7535\u822c\u7684\u6765\u5230\uff0c\u4f38\u6307\u75be\u5f39\uff0c \u79e6\u5143\u6d69\u548c\u90a3\u5c11\u5e74\u7684\u957f\u5251\u7adf\u7136\u5728\u4ed6\u4e00\u6307\u4e4b\u4e0b\uff0c\u540c\u65f6\u8131\u624b\u3002 \u3000\u3000\u79e6\u5143\u6d69\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8981\u77e5\u4ed6\u548c\u8fd9\u59d3\u6587\u7684\u5c11\u5e74\u5251\u52bf\u90fd\u662f\u84c4\u6ee1\u5f85\u53d1\u7684\uff0c\u52b2\u9053\u4f55\u7b49\u51cc\u5389\u9053 \u52b2\uff1f\u8fd9\u4eba\u80fd\u591f\u5728\u8fd9\u5371\u673a\u77ac\u606f\u4e4b\u95f4\uff0c\u540c\u65f6\u5c06\u4ed6\u4eec\u7684\u4e24\u628a\u957f\u5251\u5f39\u5f97\u98de\u51fa\u624b\u53bb\uff0c\u8fd9\u662f\u4f55\u7b49\u672c\u9886\uff0c\u4f55 \u7b49\u529f\u529b\uff01\u79e6\u5143\u6d69\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f3c\u6b64\u80fd\u4e3a\uff0c\u672c\u6d3e\u4e4b\u4e2d\uff0c\u9664\u4e86\u5e08\u7236\u6216\u8005\u53ef\u4ee5\u505a\u5230\u4e4b\u5916\uff0c\u677e\u77f3\u5e08 \u53d4\uff0c\u53ea\u6015\u4e5f\u672a\u5fc5\u80fd\u591f\u3002\u4ed6\u82e5\u662f\u542b\u6709\u654c\u610f\u7684\u8bdd\uff0c\u8fd9\uff0c\u8fd9\u53ef\u662f\u4e0d\u582a\u8bbe\u60f3\u3002\u201d\u4f46\u770b\u4ed6\u540c\u65f6\u4e5f\u5c06\u90a3\u5c11 \u5e74\u7684\u957f\u5251\u5f39\u98de\uff0c\u770b\u6765\u53c8\u4f3c\u4e4e\u6709\u5fc3\u6bd4\u89e3\uff0c\u5e76\u975e\u5bf9\u81ea\u5df1\u542b\u6709\u654c\u610f\u3002 \u3000\u3000\u8fd9\u4eba\u662f\u4e2a\u4e66\u751f\u88c5\u675f\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u4e3e\u6b62\u751a\u4e3a\u6587\u96c5\uff0c\u79e6\u5143\u6d69\u6b63\u5728\u60ca\u6115\u4e4b\u9645\uff0c\u4ed6\u5df2\u7ecf\u5411\u79e6\u5143\u6d69 \u4f5c\u4e86\u4e2a\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u54e5\u53d7\u60ca\u4e86\uff0c\u8bf7\u6055\u72ac\u5b50\u65e0\u77e5\uff0c\u6587\u67d0\u4ee3\u72ac\u5b50\u8d54\u7f6a\u3002\u201d \u3000\u3000\u8fd9\u59d3\u6587\u7684\u5c11\u5e74\u9762\u7ea2\u8fc7\u8033\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u2026\u2026\u201d\u90a3\u4e2d\u5e74\u4e66\u751f\u6012\u9053\uff1a\u201c\u6211\u5e73\u65e5\u600e\u6837\u6559\u8bad \u4f60\u7684\uff0c\u5c82\u53ef\u5bf9\u5ba2\u4eba\u5982\u6b64\u65e0\u793c\uff1f\u8fd8\u4e0d\u5feb\u5feb\u7ed9\u6211\u5411\u8d35\u5ba2\u8d54\u7f6a\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u8fde\u5fd9\u5411\u8fd9\u4e2d\u5e74\u4e66\u751f\u8fd8\u793c\uff0c\u60f6\u6050\u8bf4\u9053\uff1a\u201c\u8bf7\u4e0d\u8981\u602a\u8d23\u4eca\u90ce\uff0c\u8fd9\u539f\u662f\u6211\u7684\u4e0d\u5bf9\u3002\u201d\u59d3 \u6587\u7684\u5c11\u5e74\u9053\uff1a\u201c\u662f\u5440\uff0c\u4ed6\u5077\u770b\u6211\u4eec\u7ec3\u6b66\uff0c\u6211\u8fd9\u624d\u548c\u4ed6\u52a8\u624b\u7684\u3002\u201d \u3000\u3000\u8fd9\u4e2d\u5e74\u4e66\u751f\u6447\u4e86\u6447\u5934\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u7b11\u8bdd\uff0c\u7b11\u8bdd\uff0c\u4eba\u5bb6\u6b66\u5f53\u6d3e\u7684\u540d\u95e8\u5f1f\u5b50\uff0c\u4f60\u8fd9\u51e0\u624b\u4e09\u811a \u732b\u7684\u529f\u592b\uff0c\u522b\u4eba\u4f1a\u653e\u5728\u773c\u91cc\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u89c1\u8fd9\u4eba\u75db\u8d23\u4ed6\u7684\u513f\u5b50\uff0c\u5fc3\u4e2d\u6012\u6c14\u65e9\u5df2\u6d88\u5f97\u4e00\u5e72\u4e8c\u51c0\uff0c\u53cd\u800c\u89c9\u5f97\u4e8e\u5fc3\u4e0d\u5b89\u4e86\u3002\u8fde\u5fd9 \u8bf4\u9053\uff1a\u201c\u4ee4\u90ce\u5251\u6cd5\u9ad8\u660e\uff0c\u6211\u662f\u6781\u4e3a\u94a6\u4f69\u3002\u6b64\u6b21\u6211\u867d\u662f\u65e0\u5fc3\u5077\u770b\uff0c\u4f46\u95ef\u8fdb\u8d35\u5e9c\uff0c\u4e5f\u662f\u4e0d\u8be5\u3002\u8bf7 \u5bb9\u6211\u5411\u4e3b\u4eba\u8d54\u7f6a\u3002\u201d\u90a3\u4e2d\u5e74\u4e66\u751f\u542c\u4e86\uff0c\u5ffd\u5730\u54c8\u54c8\u4e00\u7b11\u3002 \u3000\u3000\u79e6\u5143\u6d69\u4e0d\u77e5\u4ed6\u56e0\u4f55\u53d1\u7b11\uff0c\u6b63\u81ea\u7eb3\u7f55\uff0c\u5ffd\u89c1\u8fd9\u4e2d\u5e74\u4e66\u751f\u5411\u540e\u4e00\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5c01\u5927\u54e5\u624d \u662f\u6b64\u5730\u7684\u4e3b\u4eba\uff0c\u6211\u662f\u5728\u4ed6\u5bb6\u4f5c\u5ba2\u7684\u3002\u201d\u79e6\u5143\u6d69\u968f\u5730\u6240\u6307\u7684\u65b9\u5411\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u5e74\u7ea6\u4e94\u65ec\uff0c\u988f \u4e0b\u7559\u7740\u4e09\u7efa\u957f\u987b\u7684\u6c49\u5b50\u4ece\u4e00\u4e2a\u6708\u7259\u5f62\u7684\u89d2\u95e8\u8d70\u4e86\u51fa\u6765\u3002\u90a3\u5c11\u5973\u53eb\u4e86\u4e00\u58f0\u201c\u7239\u7239\u201d\uff0c\u7acb\u5373\u5411\u4ed6 \u8dd1\u53bb\uff0c\u8fb9\u8d70\u8fb9\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8fd9\u8bb8\u4e45\u4e0d\u51fa\u6765\uff0c\u4f60\u6ca1\u542c\u89c1\u6211\u53eb\u4f60\u4e48\uff1f\u54ce\uff0c\u521a\u624d\uff0c\u521a\u624d \u771f\u9669\u2026\u2026\u201d \u3000\u3000\u90a3\u59d3\u5c01\u7684\u4e3b\u4eba\u7b11\u9053\uff1a\u201c\u5ae6\u513f\uff0c\u6211\u90fd\u77e5\u9053\u4e86\u3002\u96be\u5f97\u6709\u6b66\u5f53\u6d3e\u7684\u9ad8\u5f92\u5230\u6765\uff0c\u5f53\u771f\u662f\u7a00\u5ba2\uff0c\u7a00 \u5ba2\u3002\u8bf7\u6055\u6211\u6709\u5931\u8fce\u63a5\u4e86\u3002\u201d\u79e6\u5143\u6d69\u5fd9\u5411\u4e3b\u4eba\u65bd\u793c\uff0c\u5e76\u5411\u4ed6\u4eec\u8bf7\u6559\u59d3\u540d\uff0c\u8fd9\u624d\u77e5\u9053\u4e3b\u4eba\u662f\u59d3\u5c01 \u540d\u5b50\u8d85\u3002\u4ed6\u7684\u5973\u513f\u540d\u53eb\u5c01\u5999\u5ae6\u3002\u4e2d\u5e74\u4e66\u751f\u540d\u53eb\u6587\u9053\u5e84\uff0c\u4ed6\u7684\u513f\u5b50\u540d\u53eb\u6587\u80dc\u4e2d\u3002 \u3000\u3000\u79e6\u5143\u6d69\u5411\u4e3b\u4eba\u8c22\u8fc7\u4e0d\u7a76\u8bef\u95ef\u4e4b\u7f6a\uff0c\u5c01\u5b50\u8d85\u8bf4\u9053\uff1a\u201c\u79e6\u5c11\u4fa0\u5230\u6765\uff0c\u90a3\u662f\u6211\u4eec\u8bf7\u4e5f\u8bf7\u4e0d\u5230 \u7684\u3002\u770b\u79e6\u5c11\u4fa0\u7684\u5251\u6cd5\uff0c\u60f3\u5fc5\u662f\u51fa\u4e8e\u8d35\u6d3e\u638c\u95e8\u96f7\u8001\u524d\u8f88\u7684\u4eb2\u81ea\u4f20\u6388\u5427\uff1f\u201d\u79e6\u5143\u6d69\u8fd9\u624d\u77e5\u9053\u521a\u624d \u81ea\u5df1\u4e0e\u6587\u80dc\u4e2d\u6bd4\u5251\u4e4b\u65f6\uff0c\u4ed6\u4eec\u5df2\u5728\u5077\u770b\u7684\u4e86\u3002 \u3000\u3000\u957f\u8f88\u5077\u770b\u5c0f\u8f88\u7684\u529f\u592b\uff0c\u53ef\u80fd\u662f\u8981\u5224\u660e\u4ed6\u7684\u95e8\u6d3e\u6765\u5386\uff0c\u4e5f\u53ef\u80fd\u662f\u51fa\u4e8e\u7231\u62a4\u4e4b\u610f\uff0c\u4e8b\u540e\u53ef\u4ee5 \u52a0\u4ee5\u6307\u70b9\u7684\u3002\u603b\u4e4b\u4e0d\u8bba\u4ed6\u7684\u7528\u610f\u5982\u4f55\uff0c\u957f\u8f88\u770b\u5c0f\u8f88\u8fc7\u62db\uff0c\u5374\u7b97\u4e0d\u5f97\u662f\u5931\u793c\u4e4b\u4e8b\u3002\u79e6\u5143\u6d69\u5929\u6027 \u7eaf\u539a\uff0c\u53c8\u662f\u521d\u6b21\u51fa\u9053\uff0c\u65e0\u751a\u673a\u5fc3\uff0c\u4ed6\u6839\u672c\u6ca1\u6709\u731c\u6d4b\u5bf9\u65b9\u7684\u7528\u610f\uff0c\u5f53\u4e0b\u5c31\u606d\u606d\u656c\u656c\u5730\u7b11\u9053\uff1a \u201c\u6b63\u662f\u5bb6\u5e08\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u54c8\u54c8\u7b11\u9053\uff0c\u201c\u90a3\u66f4\u8d8a\u53d1\u96be\u5f97\u4e86\u3002\u5c0a\u5e08\u662f\u6b66\u6797\u4e2d\u7684\u6cf0\u5c71\u5317\u6597\uff0c\u6211\u662f\u4ef0\u5e55\u5df2\u4e45\u7684\u4e86\uff0c \u96be\u5f97\u79e6\u5c11\u4fa0\u5230\u6765\uff0c\u8bf7\u5bb9\u6211\u4ee5\u4e00\u676f\u6c34\u9152\u76f8\u656c\uff0c\u7565\u5c3d\u5730\u4e3b\u4e4b\u8c0a\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u8fd9\u4e2a\u665a\u8f88\u53ef\u4e0d\u6562\u5f53\u3002\u201d\u5c01\u5b50\u8d85\u529d\u9053\uff1a\u201c\u5929\u8272\u5df2\u7ecf\u665a\u4e86\uff0c\u8fd9\u5f82\u5f95\u5c71\u524d\u4e0d\u5df4 \u6751\uff0c\u540e\u4e0d\u5df4\u5e97\u3002\u79e6\u5c11\u4fa0\u4e5f\u5f97\u6709\u4e2a\u6295\u5bbf\u4e4b\u5904\uff0c\u4f55\u4e0d\u5bb9\u6211\u7a0d\u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff1f\u201d \u3000\u3000\u6587\u9053\u5e84\u7b11\u9053\uff1a\u201c\u79e6\u5c11\u4fa0\u83ab\u975e\u662f\u56e0\u5c0f\u513f\u65e0\u793c\uff0c\u5fc3\u4e2d\u5c1a\u6709\u82a5\u8482\u4e48\uff1f\u4e2d\u513f\uff0c\u5feb\u8fc7\u6765\u4e0e\u79e6\u5c11\u4fa0\u8d54 \u793c\uff01\u201d\u8bf4\u7f62\uff0c\u5077\u5077\u7684\u5bf9\u4ed6\u7684\u513f\u5b50\u4f7f\u4e86\u4e2a\u773c\u8272\u3002\u6587\u80dc\u4e2d\u672c\u6765\u662f\u5014\u5f3a\u4e0d\u80af\u8d54\u672d\u7684\uff0c\u6b64\u65f6\u5ffd\u5730\u5982\u6709 \u6240\u609f\uff0c\u5fd9\u8d70\u8fc7\u6765\u5411\u79e6\u5143\u6d69\u65bd\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u79e6\u5144\u8bf7\u6055\u5c0f\u5f1f\u9002\u624d\u5192\u72af\u4e4b\u7f6a\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u8bf7\u4f60\u5728\u8fd9 \u91cc\u7559\u4e2a\u4e24\u4e09\u5929\uff0c\u5c0f\u5f1f\u4e5f\u597d\u5411\u79e6\u5144\u8bf7\u6559\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u672c\u6765\u6709\u60f3\u4e0e\u4ed6\u4eec\u7ed3\u4ea4\u4e4b\u610f\uff0c\u800c\u4e14\u4ed6\u4eca\u665a\u786e\u5b9e\u4e5f\u9700\u8981\u6709\u4e2a\u5bb9\u8eab\u4e4b\u5730\uff0c\u82e5\u7136\u518d\u4e09\u575a \u62d2\uff0c\u672a\u514d\u4e0d\u8fd1\u4eba\u60c5\u3002\u4e09\u6765\u6587\u80dc\u4e2d\u5df2\u8bf4\u4e86\u8fd9\u6837\u7684\u8bdd\uff0c\u4ed6\u82e5\u8fd8\u575a\u7edd\u7684\u8bdd\uff0c\u90a3\u4e0d\u662f\u7b49\u4e8e\u627f\u8ba4\u4ed6\u5fc3\u6709 \u201c\u82a5\u8482\u201d\u4e86\uff1f \u3000\u3000\u6587\u5bb6\u7236\u5b50\u8fd9\u4e48\u4e00\u505a\u4f5c\uff0c\u79e6\u5143\u6d69\u751a\u89c9\u5c34\u5c2c\uff0c\u8fde\u5fd9\u8fd8\u793c\u8bf4\u9053\uff1a\u201c\u6587\u5144\u4e0d\u7a76\u5c0f\u5f1f\u8bef\u95ef\u4e4b\u7f6a\uff0c\u5c0f \u5f1f\u5df2\u89c9\u6c57\u989c\u3002\u53c8\u8499\u4e3b\u4eba\u76db\u610f\u9080\u7559\uff0c\u5c0f\u5f1f\u53ea\u597d\u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\uff0c\u6587\u5144\u5251\u6cd5\u9ad8\u660e\u4e4b\u6781\uff0c\u8bf4\u5230\u6307\u6559 \u4e8c\u5b57\uff0c\u5c0f\u5f1f\u662f\u51b3\u4e0d\u6562\u5f53\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u54c8\u54c8\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4f60\u4eec\u4e24\u4eba\u771f\u53ef\u4ee5\u7b97\u5f97\u662f\u4e0d\u6253\u4e0d\u76f8\u8bc6\u4e86\u3002\u79e6\u5c11\u4fa0\u52a1\u5fc5\u591a\u7559\u51e0 \u5929\uff0c\u8ba9\u5c0f\u5973\u4e5f\u53ef\u4ee5\u6709\u673a\u4f1a\u5411\u79e6\u5c11\u4fa0\u591a\u4e9b\u8bf7\u6559\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9762\u4e0a\u53d1\u70e7\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u8001\u524d\u8f88\u7684\u672c\u9886\u80dc\u6211\u767e\u500d\uff0c\u8fd9\u4e48\u5ba2\u6c14\uff0c\u53eb\u665a\u8f88\u600e\u53d7\u5f97\u8d77\uff1f \u6b64\u6b21\u665a\u8f88\u6709\u70b9\u4e8b\u60c5\u8981\u8d76\u62c4\u4e1c\u5e73\uff0c\u4eca\u665a\u6253\u6270\u4e00\u5bb5\uff0c\u660e\u5929\u4fbf\u8981\u8d70\u4e86\uff0c\u4e14\u5f85\u56de\u6765\u4e4b\u65f6\uff0c\u518d\u5230\u8d35\u5e9c\u5411 \u4e24\u4f4d\u8001\u524d\u8f88\u8bf7\u6559\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u597d\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u81ea\u662f\u4e0d\u4fbf\u591a\u7559\uff0c\u4eca\u665a\u5c31\u59d4\u5c48\u79e6\u5c11\u4fa0\u5728\u5bd2\u820d\u6682\u4f4f\u4e00\u665a\u3002\u65f6 \u5019\u4e0d\u65e9\uff0c\u8bf7\u8fdb\u53bb\u7528\u996d\u5427\u3002\u9152\u83dc\u90fd\u5df2\u51c6\u5907\u597d\u4e86\u3002\u53ea\u662f\u5c71\u4e0a\u65e0\u751a\u7f8e\u9152\u4f73\u6dc6\uff0c\u5374\u672a\u514d\u6020\u6162\u8d35\u5ba2 \u4e86\u3002\u201d \u3000\u3000\u4ed6\u4eec\u8fb9\u8d70\u8fb9\u8bf4\uff0c\u8fdb\u4e86\u996d\u5385\uff0c\u79e6\u5143\u6d69\u4e00\u770b\uff0c\u53ea\u89c1\u5385\u4e2d\u65e9\u5df2\u6446\u597d\u4e00\u684c\u9152\u5e2d\u3002\u60f3\u662f\u81ea\u5df1\u4e0e\u6587\u80dc \u4e2d\u6bd4\u5251\u4e4b\u65f6\uff0c\u5c01\u5b50\u8d85\u5df2\u7ecf\u8ba1\u5212\u597d\u7559\u5ba2\u7684\u4e86\u3002 \u3000\u3000\u4e3b\u4eba\u5bb6\u548c\u6587\u9053\u5e84\u5982\u6b64\u5ba2\u6c14\uff0c\u79e6\u5143\u6d69\u6709\u70b9\u4e0d\u5b89\uff0c\u53c8\u6709\u70b9\u201c\u53d7\u5ba0\u82e5\u60ca\u201d\u7684\u7591\u60d1\uff0c\u60f3\u9053\uff1a\u201c\u6211 \u4e0d\u8fc7\u662f\u6b66\u5f53\u6d3e\u7684\u4e00\u4e2a\u521d\u51fa\u9053\u7684\u5f1f\u5b50\uff0c\u4ed6\u4eec\u4e3a\u4f55\u5bf9\u6211\u5982\u6b64\u606d\u656c\uff0c\u771f\u4e2a\u662f\u628a\u6211\u5f53\u4f5c\u8d35\u5ba2\u4e00\u822c\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u5fc3\u91cc\u8d77\u4e86\u6000\u7591\uff0c\u5374\u53c8\u5728\u5fc3\u91cc\u81ea\u95ee\u81ea\u7b54\u9053\uff1a\u201c\u50bb\u74dc\uff0c\u4ed6\u4eec\u4e0d\u662f\u628a\u4f60\u5f53\u4f5c\u8d35\u5ba2\uff0c\u662f\u5bf9 \u4f60\u5e08\u7236\u7684\u5c0a\u656c\u3002\u6b66\u5f53\u5c11\u6797\u5e76\u9a7e\u9f50\u9a71\uff0c\u9886\u8896\u6b66\u6797\u3002\u672c\u6d3e\u4e2d\u4efb\u4f55\u4e00\u4e2a\u672a\u5165\u6d41\u7684\u5f1f\u5b50\u5728\u6c5f\u6e56\u884c\u8d70\uff0c \u522b\u4eba\u90fd\u4f1a\u7ed9\u51e0\u5206\u9762\u5b50\u7684\u3002\u4f55\u51b5\u4f60\u7684\u5e08\u7236\u4e43\u662f\u638c\u95e8\u3002\u201d\u4ed6\u8fd9\u4e48\u81ea\u95ee\u81ea\u7b54\uff0c\u5fc3\u4e2d\u7684\u6000\u7591\u4e5f\u5c31\u51b0\u91ca \u4e86\u3002 \u3000\u3000\u5165\u5e2d\u4e4b\u540e\uff0c\u5c01\u5b50\u8d85\u4e0e\u6587\u9053\u5e84\u90fd\u5411\u79e6\u5143\u6d69\u6bb7\u6bb7\u529d\u9152\uff0c\u79e6\u5143\u6d69\u672c\u6765\u4f1a\u559d\u51e0\u676f\uff0c\u4f46\u5374\u5ffd\u5730\u60f3\u8d77 \u5e08\u7236\u7684\u544a\u8beb\uff1a\u201c\u5728\u5916\u9762\u5fc5\u987b\u5904\u5904\u8c28\u614e\uff0c\u5c24\u5176\u4e0d\u53ef\u8d2a\u676f\u8bef\u4e8b\u3002\u78b0\u4e0a\u4e0d\u77e5\u6765\u5386\u7684\u964c\u751f\u4eba\u66f4\u987b\u5c0f \u5fc3\u3002\u201d\u4ed6\u60f3\u8d77\u4e86\u5e08\u8bad\uff0c\u5f53\u4e0b\u4fbf\u9053\uff1a\u201c\u5c0f\u4f84\u9152\u91cf\u592a\u6d45\uff0c\u660e\u513f\u8fd8\u8981\u52a8\u8eab\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u5c01\u5b50\u8d85\u4e0d\u5f85\u4ed6\u628a\u8bdd\u8bf4\u5b8c\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u9152\u4e0d\u662f\u70c8\u9152\uff0c\u591a\u559d\u51e0\u676f\uff0c\u4e5f\u4e0d\u4f1a\u559d\u9189\u7684\u3002\u597d\uff0c\u6211\u5148 \u5e72\u4e3a\u656c\uff0c\u8bf7\u79e6\u5c11\u4fa0\u4e5f\u8d4f\u4e2a\u8138\u3002\u201d\u8bf4\u7f62\uff0c\u628a\u676f\u4e2d\u7684\u9152\u4e00\u53e3\u559d\u5149\u3002 \u3000\u3000\u79e6\u5143\u6d69\u867d\u7136\u4e0d\u5927\u61c2\u5f97\u6c5f\u6e56\u89c4\u77e9\uff0c\u4f46\u4e5f\u77e5\u9053\u4e3b\u4eba\u5148\u5e72\u4e4b\u610f\uff0c\u4e0d\u4ec5\u4ec5\u662f\u8868\u793a\u201c\u5148\u5e72\u4e3a\u656c\u201d\uff0c \u8fd8\u542b\u6709\u514d\u4f7f\u81ea\u5df1\u7591\u5fc3\u7684\u610f\u601d\u3002\u5176\u5b9e\u79e6\u5143\u6d69\u5012\u662f\u4e1d\u6beb\u4e5f\u6ca1\u7591\u5fc3\u4e3b\u4eba\u4f1a\u5728\u9152\u4e2d\u5f04\u9b3c\u7684\u3002 \u3000\u3000\u79e6\u5143\u6d69\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u82e5\u8981\u6697\u7b97\u6211\uff0c\u4f55\u987b\u5728\u9152\u4e2d\u4e0b\u6bd2\uff1f\u201d\u4e3b\u4eba\u672c\u9886\u5982\u4f55\u4ed6\u672a\u77e5\u9053\uff0c\u6587 \u9053\u5e84\u7684\u672c\u9886\u4ed6\u5374\u662f\u89c1\u8fc7\u7684\uff0c\u82e5\u8981\u53d6\u4ed6\u6027\u547d\uff0c\u4e00\u51fa\u624b\u4ed6\u662f\u51b3\u65e0\u62b5\u6321\u7684\u4f59\u5730\u3002 \u3000\u3000\u79e6\u5143\u6d69\u4e00\u6765\u662f\u8ba4\u8d70\u4ed6\u4eec\u4e0d\u4f1a\u5728\u9152\u4e2d\u4e0b\u6cf0\uff1b\u4e8c\u6765\u4e3b\u4eba\u76db\u610f\u62f3\u62f3\uff0c\u53c8\u5148\u5e72\u4e86\u4e00\u676f\uff0c\u4ed6\u82e5\u8fd8\u4e0d \u559d\uff0c\u90a3\u5c31\u662f\u8868\u660e\u81ea\u5df1\u6709\u6240\u6000\u7591\uff0c\u5bf9\u4e3b\u4eba\u662f\u5927\u5927\u7684\u4e0d\u656c\u4e86\u3002\u4e8e\u662f\u79e6\u5143\u6d69\u53ea\u597d\u9053\u4e86\u4e2a\u8c22\uff0c\u628a\u4e00\u676f \u9152\u4e5f\u559d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u9152\u679c\u7136\u6ca1\u6709\u4e1d\u6beb\u8f9b\u8fa3\u7684\u5473\u9053\uff0c\u79e6\u5143\u6d69\u559d\u4e86\u4e0b\u53bb\uff0c\u53ea\u89c9\u4e00\u80a1\u6e05\u9999\uff0c\u6c81\u4eba\u813e\u8151\u3002\u79e6\u5143\u6d69\u7981 \u4e0d\u7740\u5567\u5567\u8d5e\u9053\uff1a\u201c\u597d\u9999\uff0c\u597d\u9999\uff01\u201d\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u8fd9\u6c34\u9152\u8fd8\u52c9\u5f3a\u53ef\u4ee5\u4e00\u559d\u5427\uff1f\u201d\u79e6\u5143\u6d69\u7b11\u9053\uff1a \u201c\u5018\u82e5\u8bf4\u8fd9\u662f\u6c34\u9152\uff0c\u5929\u4e0b\u5c31\u6ca1\u6709\u53ef\u4ee5\u79f0\u5f97\u662f\u7f8e\u9152\u7684\u4e86\u3002\u8fd9\u7b80\u76f4\u662f\u7389\u6db2\u743c\u6d46\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u7b11\u9053\uff1a\u201c\u79e6\u5c11\u4fa0\u8fd8\u8bf4\u4e0d\u4f1a\u559d\u9152\uff0c\u5374\u539f\u6765\u662f\u4e2a\u54c1\u9152\u7684\u5927\u884c\u5bb6\u3002\u597d\uff0c\u6211\u4e5f\u656c\u4f60\u4e00 \u676f\u3002\u201d\u79e6\u5143\u6d69\u65e2\u7136\u548c\u5c01\u5b50\u8d85\u559d\u4e86\uff0c\u5f53\u7136\u4e5f\u5f97\u548c\u6587\u9053\u5e84\u559d\u4e00\u676f\u3002\u63a5\u7740\u6587\u80dc\u4e2d\u4e5f\u6765\u656c\u9152\uff0c\u7b11\u9053\uff1a \u201c\u5c01\u8001\u4f2f\u8bf4\u5f97\u597d\uff0c\u54b1\u4eec\u662f\u4e0d\u6253\u4e0d\u76f8\u8bc6\uff0c\u8fd9\u4e00\u676f\u5c31\u7b97\u662f\u5e86\u8d3a\u54b1\u4eec\u7684\u7f14\u4ea4\u5427\u3002\u201d\u79e6\u5143\u6d69\u5fc3\u91cc\u60f3 \u9053\uff1a\u201c\u8fd9\u9152\u6211\u518d\u559d\u4e09\u676f\u60f3\u6765\u4e5f\u4e0d\u4f1a\u9189\u7684\u3002\u201d\u4e8e\u662f\u548c\u6587\u80dc\u4e2d\u4e5f\u5e72\u4e86\u676f\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u559d\u4e86\u4e09\u5927\u676f \u4e86\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u7239\u7239\uff0c\u8fd9\u662f\u4ec0\u4e48\u9152\uff0c\u6211\u597d\u50cf\u6ca1\u6709\u89c1\u4f60\u559d\u8fc7\u7684\uff1f\u5f53\u771f\u662f\u9999\u5f97\u8bf1\u4eba\uff0c\u8ba9\u6211\u4e5f\u559d \u4e00\u676f\u3002\u201d\u5c01\u5b50\u8d85\u677f\u8d77\u8138\u5b54\u9053\uff1a\u201c\u5973\u5b69\u513f\u5bb6\u4e0d\u8bb8\u559d\u9152\uff01\u201d\u5c01\u5999\u5ae6\u4ece\u6765\u4e0d\u66fe\u7ed9\u7236\u4eb2\u65a5\u8d23\u8fc7\u7684\uff0c\u60f3 \u4e0d\u5230\u7236\u4eb2\u7adf\u4f1a\u5f53\u770b\u5ba2\u4eba\u7684\u9762\u7ed9\u5979\u96be\u582a\uff0c\u767b\u65f6\u6ee1\u9762\u901a\u7ea2\uff0c\u4e0d\u89c9\u5446\u4e86\uff0c\u6587\u9053\u5e84\u7b11\u9053\uff1a\u201c\u5c01\u5927\u54e5\uff0c \u4f60\u5bf9\u4f84\u5973\u4e5f\u672a\u514d\u7ba1\u5f97\u592a\u4e25\u4e86\u3002\u597d\u5566\uff0c\u7239\u7239\u4e0d\u8bb8\u4f60\u559d\uff0c\u4f60\u5c31\u656c\u79e6\u5c11\u4fa0\u4e00\u676f\u5427\u3002\u201d\u5c01\u5999\u5ae6\u8d4c\u6c14 \u9053\uff1a\u201c\u4e0d\u559d\u5c31\u4e0d\u559d\uff0c\u6709\u4ec0\u4e48\u7a00\u7f55\uff1f\u201d\u5979\u81ea\u5df1\u4e0d\u559d\uff0c\u4e5f\u6ca1\u53bb\u7ed9\u79e6\u5143\u6d69\u656c\u9152\u3002 \u3000\u3000\u79e6\u5143\u6d69\u4e5f\u89c9\u6709\u70b9\u5c34\u5c2c\uff0c\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u91cf\u6d45\uff0c\u559d\u4e86\u4e09\u676f\uff0c\u5df2\u662f\u4e0d\u80fd\u518d\u559d\u4e86\u3002\u5c01\u59d1\u5a18\u7684\u9152\u6211 \u5fc3\u9886\u5566\u3002\u201d\u51e0\u53e5\u8bdd\u8f7b\u8f7b\u7684\u66ff\u5c01\u5999\u5ae6\u6697\u6253\u4e86\u5706\u573a\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u6211\u8fd9\u4e2a\u4e2b\u5934\u81ea\u5e7c\u5931\u6bcd\uff0c\u6211\u4e0d\u514d\u5bf9\u5979\u653e\u7eb5\u4e86\u4e9b\u3002\u79e6\u5c11\u4fa0\u4e0d\u8981\u89c1\u7b11\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u597d\u4e86\uff0c\u54b1\u4eec\u8c08\u522b\u7684\u4e8b\u5427\uff0c\u79e6\u5c11\u4fa0\uff0c\u4f60\u662f\u8bf4\u5230\u4e1c\u5e73\u53bf\u7684\uff0c\u662f\u5417\uff1f\u201d\u79e6\u5143\u6d69 \u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u6587\u9053\u5e84\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u6c5f\u6d77\u5929\u5c31\u662f\u4f4f\u5728\u4e1c\u5e73\u6768\u5bb6\u5e84\u7684\uff0c\u542c\u8bf4\u4ed6\u5728\u516b\u6708\u5341\u4e94\u5ac1\u5973 \u513f\uff0c\u79e6\u5c11\u4fa0\u53ef\u5982\u6b64\u4e8b\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u665a\u8f88\u6b63\u5949\u4e86\u5bb6\u5e08\u4e4b\u547d\u524d\u5f80\u6c5f\u5bb6\u9053\u8d3a\u7684\u3002\u201d\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u6211\u679c\u7136\u6599\u5f97\u4e0d\u9519\u3002 \u4ee5\u4e86\u4e09\u6d3e\u548c\u6c5f\u5927\u4fa0\u7684\u4ea4\u60c5\uff0c\u96f7\u5927\u638c\u95e8\u4e0d\u53bb\uff0c\u81ea\u8be5\u6d3e\u9053\u4ed6\u95e8\u4e0b\u6700\u5f97\u610f\u7684\u5f1f\u5b50\u524d\u5f80\u7684\u4e86\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u5bb6\u5e08\u662f\u53eb\u6211\u53bb\u89c1\u89c1\u4e16\u9762\u7684\uff0c\u5728\u672c\u6d3e\u4e2d\uff0c\u6211\u5176\u5b9e\u53ea\u662f\u4e2a\u672a\u5165\u6d41 \u7684\u5f1f\u5b50\u3002\u201d\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u79e6\u5c11\u4fa0\u592a\u8c26\u865a\u4e86\u3002\u4e0d\u8fc7\uff0c\u6b66\u529f\u597d\u7684\u9752\u5e74\u4eba\u6700\u96be\u5f97\u7684\u5c31\u662f\u8c26\u865a\uff0c\u6211\u656c \u4f60\u4e00\u676f\u3002\u201d\u79e6\u5143\u6d69\u9053\uff1a\u201c\u665a\u8f88\u5b9e\u5728\u4e0d\u80fd\u518d\u559d\u4e86\u3002\u201d\u79e6\u5143\u6d69\u56e0\u4e3a\u521a\u624d\u66ff\u5c01\u5999\u5ae6\u6253\u5706\u573a\u7684\u65f6\u5019\uff0c \u8bf4\u8fc7\u8fd9\u6837\u7684\u8bdd\uff0c\u56e0\u6b64\u5bf9\u5c01\u5b50\u8d85\u7684\u656c\u9152\uff0c\u53ea\u597d\u5a49\u8f6c\u63a8\u8f9e\uff0c\u5176\u5b9e\u4ed6\u5fc3\u91cc\u662f\u60f3\u559d\u7684\u3002 \u3000\u3000\u4f46\u8bf4\u4e5f\u5947\u602a\uff0c\u79e6\u5143\u6d69\u81ea\u5df1\u4ee5\u4e3a\u662f\u4e0d\u4f1a\u9189\u7684\uff0c\u6b64\u65f6\u5374\u5ffd\u5730\u6709\u4e86\u98d8\u98d8\u7136\u7684\u611f\u89c9\uff0c\u9152\u610f\u7adf\u662f\u6709 \u4e86\u4e2a\u516b\u4e5d\u5206\u4e86\u3002 \u3000\u3000\u79e6\u5143\u6d69\u6709\u4e86\u516b\u4e5d\u5206\u9189\u610f\uff0c\u5ffd\u5730\u60f3\u8d77\u4e00\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u8001\u4f2f\u4e5f\u6709\u63a5\u5230\u6c5f\u5bb6\u7684\u8bf7\u5e16\u5427\uff1f\u201d \u5f82\u5f95\u5c71\u4e0e\u4e1c\u5e73\u53bf\u7684\u8ddd\u79bb\u4e0d\u8fc7\u51e0\u767e\u91cc\uff0c\u79e6\u5143\u6d69\u56e0\u4e3a\u4ed6\u4eec\u662f\u6b66\u6797\u9ad8\u624b\uff0c\u4f4f\u5f97\u53c8\u8fd9\u6837\u8fd1\uff0c\u60f3\u6765\u5e94\u8be5 \u662f\u548c\u6c5f\u5927\u4fa0\u65e9\u5c31\u76f8\u8bc6\u7684\u4e86\uff0c\u662f\u4ee5\u6709\u6b64\u4e00\u95ee\u3002\u5176\u5b9e\u8fd9\u6837\u7684\u95ee\u6cd5\u662f\u6709\u5931\u793c\u8c8c\u7684\uff0c\u4f46\u79e6\u5143\u6d69\u56e0\u4e3a\u9152 \u610f\u5df2\u6d53\uff0c\u4e5f\u5c31\u4e0d\u89c9\u5f97\u4e86\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u6253\u4e86\u4e2a\u54c8\u54c8\u8bf4\u9053\uff1a\u201c\u6211\u5728\u6b64\u9690\u5c45\uff0c\u6781\u5c11\u4e0e\u5916\u4eba\u6765\u5f80\u3002\u6c5f\u5927\u4fa0\u867d\u662f\u95fb\u540d\u4e8e\u4e0b\uff0c\u6211\u53ef \u6ca1\u6709\u53bb\u62dc\u8bbf\u8fc7\u4ed6\u3002\u6599\u60f3\u6c5f\u5927\u4fa0\u4e5f\u4e0d\u4f1a\u77e5\u9053\u6211\u8fd9\u4e2a\u5c71\u91ce\u9119\u4eba\uff0c\u4ed6\u600e\u4f1a\u53d1\u8bf7\u5e16\u7ed9\u6211\u3002\u201d\u6587\u9053\u5e84\u7b11 \u9053\uff1a\u201c\u6211\u662f\u4e2a\u65e0\u540d\u5c0f\u5352\uff0c\u66f4\u4e0d\u4f1a\u6709\u6c5f\u5bb6\u7684\u8bf7\u5e16\u4e86\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u4e24\u4f4d\u662f\u4e16\u5916\u9ad8\u4eba\uff0c\u53ef\u656c\uff0c\u53ef\u656c\uff01\u597d\uff0c\u6211\u656c\u4e24\u4f4d\u4e00\u676f\u3002\u201d\u4ed6\u81ea\u5df1\u8bf4\u8fc7\u4e0d\u80fd\u518d \u559d\u7684\uff0c\u5982\u4eca\u5374\u53c8\u8981\u548c\u4eba\u5bb6\u5e72\u676f\u4e86\u3002\u5c01\u5999\u5ae6\u770b\u770b\u4ed6\u7684\u9762\u8272\u4e0d\u5bf9\uff0c\u8bf4\u9053\uff1a\u201c\u79e6\u5c11\u4fa0\u770b\u6765\u4f60\u662f\u5f53\u771f \u9189\u4e86\uff0c\u4e0d\u80fd\u518d\u559d\u5566\uff01\u201d\u5c01\u5b50\u8d85\u6a2a\u5979\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u5ae6\u513f\uff0c\u4f60\u600e\u7684\u5982\u6b64\u4e0d\u61c2\u793c\u8c8c\uff0c\u53ea\u6709\u529d\u5ba2\u4eba \u559d\u9152\uff0c\u54ea\u6709\u963b\u5ba2\u4eba\u559d\u9152\u7684\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u9189\uff1f\u6211\u6ca1\u6709\u9189\uff0c\u6211\u8fd8\u80fd\u518d\u559d\u3002\u5c01\u59d1\u5a18\uff0c\u6211\u548c\u4f60\u5e72\u676f\uff01\u201d\u7ad9\u8d77 \u8eab\u6765\uff0c\u62ff\u7740\u9152\u676f\uff0c\u6447\u6447\u6643\u7075\uff0c\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u5730\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\uff0c\u90a3\u201c\u5e72\u676f\u201d\u4e8c\u5b57\u662f \u5012\u5728\u5730\u4e0a\u5636\u54d1\u7740\u5589\u5499\u8bf4\u51fa\u6765\u7684\u3002\u8bf4\u51fa\u4e86\u8fd9\u4e24\u4e2a\u5b57\uff0c\u90a3\u676f\u9152\u5df2\u662f\u6cfc\u5e72\uff0c\u4eba\u4e5f\u5c31\u660f\u8ff7\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u8fd8\u8981\u529d\u4ed6\u559d\u9152\u3002\u4f60\u4eec\u7b80\u76f4\u662f\u6709\u610f\u6349\u5f04\u4ed6\u7684\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5ae6\u513f\uff0c\u4f60\u73b0\u5728\u5e94\u8be5\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u4e0d\u8bb8\u4f60\u559d\u4e86\u5427\uff1f\u8fd9\u662f\u5343\u65e5\u9189\uff01\u4ee5\u4f60 \u7684\u529f\u529b\uff0c\u5373\u4f7f\u53e3\u4e2d\u542b\u4e86\u89e3\u836f\uff0c\u559d\u4e86\u4e00\u676f\uff0c\u4e5f\u4f1a\u9189\u5012\u7684\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u63a5\u7740\u5bf9\u6587\u9053\u5e84\u9053\uff1a\u201c\u8bf4\u662f\u5343\u65e5\u9189\uff0c\u5f53\u7136\u5938\u5927\u4e86\u4e9b\u3002\u4f46\u8fd9\u5c0f\u5b50\u559d\u4e86\u4e09\u676f\uff0c\u81f3\u5c11\u4e5f\u8981 \u9189\u4e2a\u4e03\u5929\u4e03\u591c\u4e0d\u7701\u4eba\u4e8b\u3002\u5982\u4f55\u5904\u7f6e\u4ed6\u5462\uff1f\u6211\u542c\u4f60\u7684\u4e3b\u610f\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u4e08\u53d4\u53d4\uff0c\u7239\u7239\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8981\u5f04\u9189\u4e86\u4ed6\u4e86\uff0c\u5c01\u5b50\u8d85\u607c\u9053\uff1a\u201c\u5927\u4eba\u8bf4\u8bdd\uff0c\u4f60 \u4e0d\u8981\u591a\u4e8b\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u7b11\u9053\uff1a\u201c\u8fd9\u4e8b\u7ec8\u987b\u7792\u4e0d\u4e86\u5979\uff0c\u4e5f\u8bb8\u8fd8\u8981\u5979\u4e00\u540c\u53bb\u51d1\u70ed\u95f9\uff0c\u544a\u8bc9\u5979\u4e5f\u662f\u65e0\u59a8\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u597d\u5427\uff0c\u5c31\u544a\u8bc9\u4f60\u5427\u3002\u4f60\u7684\u6587\u53d4\u53d4\u4e0e\u6c5f\u6d77\u5929\u6709\u4e24\u4ee3\u4e4b\u4ec7\uff0c\u6b63\u60f3\u8d81\u6c5f\u5bb6\u5ac1\u5973\u7684 \u673a\u4f1a\uff0c\u95f9\u5b83\u4e00\u573a\u3002\u8fd9\u5c0f\u5b50\u9002\u9022\u5176\u4f1a\uff0c\u6765\u5230\u54b1\u5bb6\uff0c\u4ed6\u8eab\u4e0a\u6709\u6c5f\u5bb6\u7684\u8bf7\u5e16\uff0c\u6b63\u53ef\u4ee5\u6d3e\u6d3e\u7528\u573a\u3002\u8bf4 \u4e0d\u5f97\u53ea\u597d\u59d4\u5c48\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6c5f\u6d77\u5929\u65e2\u6709\u5927\u4fa0\u4e4b\u79f0\uff0c\u60f3\u6765\u8be5\u662f\u4e2a\u5978\u4eba\u5427\uff1f\u6587\u53d4\u53d4\uff0c\u4f60\u600e\u7684\u548c\u4ed6\u7ed3\u4e86\u51a4 \u4ec7\uff1f\u201d\u8fd9\u4e00\u95ee\u628a\u6587\u9053\u5e84\u95ee\u5f97\u751a\u662f\u5c34\u5c2c\u3002\u6b63\u662f\uff1a \u3000\u3000\u53ef\u601c\u5c0f\u513f\u5973\uff0c\u5c1a\u672a\u89e3\u673a\u5fc3\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e8c\u56de\u3000\u5d0e\u5c96\u4e16\u8def\u6e5b\u55df\u53f9\u3000\u6005\u60af\u60c5\u6000\u53ef\u5948\u4f55 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e8c\u56de\u3000\u5d0e\u5c96\u4e16\u8def\u6e5b\u55df\u53f9\u3000\u6005\u60af\u60c5\u6000\u53ef\u5948\u4f55 \u3000\u3000\u539f\u6765\u8fd9\u6587\u9053\u5e84\u4e43\u662f\u4e1c\u6d77\u65e0\u540d\u5c9b\u5c9b\u4e3b\u6587\u5ef7\u74a7\u7684\u4f84\u513f\uff0c\u6587\u5ef7\u74a7\u662f\u4e00\u6d3e\u7684\u6b66\u5b66\u5927\u5b97\u5e08\uff0c\u56e0\u4e3a\u81ea \u5df1\u6ca1\u6709\u513f\u5b50\uff0c\u628a\u4f84\u513f\u5f53\u4f5c\u513f\u5b50\uff0c\u4e00\u8eab\u7684\u6b66\u529f\u90fd\u4f20\u6388\u7ed9\u4ed6\u3002\u4e8c\u5341\u4f59\u5e74\u4e4b\u524d\uff0c\u4ed6\u4eec\u4e24\u53d4\u4f84\u6765\u5230\u4e86 \u4e2d\u539f\u3002 \u3000\u3000\u6587\u5ef7\u74a7\u7684\u6b66\u5b66\u81ea\u8f9f\u9014\u5f84\uff0c\u7ec3\u6210\u4e86\u201c\u4e09\u8c61\u795e\u529f\u201d\uff0c\u81ea\u4ee5\u4e3a\u53ef\u4ee5\u79f0\u96c4\u5f53\u4e16\uff0c\u6545\u800c\u4e0d\u7518\u57cb\u6ca1\u5b64 \u5c9b\uff0c\u8981\u5230\u4e2d\u539f\u6765\u79f0\u9738\u6b66\u6797\uff0c\u7ee7\u800c\u5f00\u5b97\u7acb\u6d3e\u7684\u3002 \u3000\u3000\u6587\u5ef7\u74a7\u81ea\u4ee5\u4e3a\u53ef\u4ee5\u79f0\u96c4\u5f53\u4e16\uff0c\u4e0d\u6599\u540e\u6765\u78b0\u4e0a\u4e86\u91d1\u4e16\u9057\uff0c\u51e0\u6b21\u4e09\u756a\uff0c\u90fd\u4e3a\u91d1\u4e16\u9057\u6240\u632b\u6298\uff0c \u6587\u5ef7\u74a7\u672c\u6765\u4e0d\u662f\u6b63\u4eba\u541b\u5b50\uff0c\u540d\u5229\u4e4b\u5fc3\u6781\u91cd\uff0c\u53d7\u4e86\u632b\u6298\u4e4b\u540e\uff0c\u6025\u4e8e\u62a5\u4ec7\uff0c\u7ec8\u4e8e\u5f53\u4e0a\u4e86\u6e05\u5ef7\u7684\u9e70 \u722a\u3002\u6700\u540e\u5728\u6c13\u5c71\u4e00\u6218\uff0c\u88ab\u91d1\u4e16\u9057\u5e9f\u4e86\u4ed6\u7684\u6b66\u529f\uff0c\u8fd9\u8fd8\u662f\u91d1\u4e16\u9057\u5ff5\u5728\u4ed6\u7684\u4fee\u4e3a\u4e0d\u6613\uff0c\u7279\u5730\u624b\u4e0b \u7559\u60c5\uff0c\u4e0d\u53d6\u4ed6\u7684\u6027\u547d\uff0c\u597d\u8ba9\u4ed6\u53ef\u4ee5\u5c06\u4ed6\u7684\u6b66\u5b66\u4f20\u6d41\u4e0b\u53bb\u7684\u3002 \u3000\u3000\u6587\u9053\u5e84\u672c\u4eba\u5728\u4e2d\u539f\u90a3\u51e0\u5e74\uff0c\u8ddf\u4ed6\u53d4\u7236\u4e00\u540c\u505a\u4e86\u6e05\u5ef7\u7684\u9e70\u722a\uff0c\u4ed6\u53d4\u7236\u548c\u91d1\u4e16\u9057\u7ed3\u4e0b\u6df1\u4ec7\uff0c \u4ed6\u81ea\u5df1\u5219\u548c\u91d1\u4e16\u9057\u7684\u5f92\u5f1f\u6c5f\u6d77\u5929\u7ed3\u4e86\u6df1\u4ec7\uff0c\u4e8b\u60c5\u7531\u4e8e\u4ed6\u8981\u5a36\u5927\u9b54\u5934\u6b27\u9633\u4ef2\u548c\u7684\u5973\u513f\u6b27\u9633\u5a49\u800c \u8d77\uff0c\u5f53\u65f6\u6b27\u9633\u5a49\u6b63\u5728\u79c1\u604b\u6c5f\u6d77\u5929\uff0c\u4e0d\u613f\u5ac1\u7ed9\u6587\u9053\u5e84\u3002\u62dc\u5802\u4e4b\u65e5\uff0c\u79c1\u9003\u51fa\u53bb\u3002\u800c\u6c5f\u6d77\u5929\u5728\u90a3\u65e5 \u4e5f\u6070\u5de7\u6765\u5230\u5979\u5bb6\uff0c\u548c\u6b27\u9633\u5a49\u7684\u4e00\u4e2a\u5e08\u5144\u628a\u6587\u9053\u5e84\u6253\u5f97\u91cd\u4f24\uff0c\u539f\u6765\u6b27\u9633\u5a49\u79c1\u604b\u6c5f\u6d77\u5929\uff0c\u800c\u5979\u90a3 \u4e2a\u5e08\u5144\u53c8\u662f\u79c1\u604b\u5979\u7684\u3002\u91cd\u4f24\u6587\u9053\u5e84\u7684\u5176\u5b9e\u662f\u4ed6\uff0c\u6c5f\u6d77\u5929\u53ea\u662f\u5e2e\u624b\u3002\u4e0d\u8fc7\uff0c\u6b27\u9633\u5a49\u8fd9\u4e2a\u5e08\u5144\u5f53 \u573a\u81ea\u6740\uff0c\u6587\u9053\u5e84\u9042\u628a\u6240\u6709\u7684\u5e10\u90fd\u7b97\u5728\u6c5f\u6d77\u5929\u8eab\u4e0a\u3002\u4e8c\u5341\u4f59\u5e74\u8fc7\u53bb\uff0c\u6c5f\u6d77\u5929\u3001\u6b27\u9633\u5a49\u5747\u5df2\u53e6\u5ac1 \u53e6\u5a36\uff0c\u6587\u9053\u5e84\u56de\u8f6c\u4e86\u65e0\u540d\u5c9b\u4ea6\u5df2\u5a36\u59bb\u751f\u5b50\u3002\u4f46\u8fd9\u4e8c\u5341\u591a\u5e74\u524d\u7684\u65e7\u6068\uff0c\u4ed6\u51e0\u662f\u5ff5\u5ff5\u4e0d\u540e\u3002\uff08\u6587 \u5ef7\u74a7\u53d4\u4f84\u4e0e\u4f1a\u4e16\u9057\u5e08\u5f92\u7ed3\u6028\u4e4b\u4e8b\uff0c\u4e8b\u8be6\u62d9\u8457\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\uff09 \u3000\u3000\u6587\u9053\u5e84\u7ecf\u8fc7\u4e86\u4e8c\u5341\u5e74\u5728\u65e0\u540d\u5c9b\u4e0a\u7684\u82e6\u7ec3\uff0c\u65e9\u5df2\u7ec3\u6210\u4e86\u4e09\u8c61\u795e\u529f\uff0c\u4ed6\u4eec\u53d4\u4f84\u867d\u7136\u8eab\u5728\u6d77 \u5916\uff0c\u5bf9\u4e2d\u539f\u7684\u6b66\u6797\u6d88\u606f\u4ecd\u662f\u65f6\u6709\u6240\u95fb\u3002\u91d1\u4e16\u9057\u592b\u59bb\u65e9\u5df2\u9041\u8ff9\u6d77\u5916\uff0c\u4e0d\u77e5\u6240\u7ec8\uff1b\u5929\u5c71\u6d3e\u7684\u8001\u638c \u95e8\u5510\u6653\u6f9c\uff0c\u5c11\u6797\u6d3e\u7684\u957f\u8001\u75db\u7985\u4e0a\u4eba\u3001\u5ce8\u5d4b\u6d3e\u7684\u540d\u5bbf\u91d1\u5149\u4e0a\u4eba\uff0c\u8fd9\u4e00\u4e9b\u4e8c\u4e09\u5341\u5e74\u7684\u7684\u7b2c\u4e00\u6d41\u9ad8 \u624b\u90e8\u5df2\u5148\u540e\u53bb\u4e16\u3002\u6587\u9053\u5e84\u5f97\u77e5\u4e86\u8fd9\u4e9b\u6d88\u606f\uff0c\u4e0d\u89c9\u91ce\u5fc3\u52c3\u52c3\uff0c\u8ba4\u4e3a\u5f53\u4e16\u7684\u5927\u654c\uff0c\u5c31\u53ea\u662f\u6c5f\u6d77\u5929 \u4e00\u4eba\uff0c\u4e8e\u662f\u4ed6\u9042\u6000\u7740\u5bbf\u6028\uff0c\u4e0e\u513f\u5b50\u91cd\u5c65\u4e2d\u539f\u3002 \u3000\u3000\u81f3\u4e8e\u5c01\u5b50\u8d85\u5219\u662f\u4e8c\u5341\u5e74\u524d\u6c13\u5c71\u4e4b\u6218\u4e2d\uff0c\u4fa5\u5e78\u9003\u5f97\u6027\u547d\u7684\u6e05\u5ef7\u5927\u5185\u536b\u58eb\uff0c\u4ed6\u66fe\u5f97\u8fc7\u6587\u5ef7\u74a7 \u7684\u6307\u70b9\uff0c\u548c\u6587\u9053\u5e84\u7ed3\u4e3a\u516b\u62dc\u4e4b\u4ea4\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u4fa5\u5e78\u9003\u5f97\u6027\u547d\u4e4b\u540e\uff0c\u6df1\u6050\u4fa0\u6587\u9053\u4e2d\u4eba\u627e\u4ed6\u6666\u6c14\uff0c\u4e0d\u6562\u518d\u7ed9\u6e05\u5ef7\u5356\u547d\uff0c\u9690\u59d3\u57cb\u540d\uff0c \u533f\u5c45\u5f82\u5f95\u5c71\u4e2d\u3002\u6587\u9053\u5e84\u91cd\u5230\u4e2d\u539f\uff0c\u5c31\u4f4f\u5728\u4ed6\u7684\u5bb6\u91cc\u3002\u5c01\u5b50\u8d85\u672c\u4eba\u81ea\u662f\u4e0d\u6562\u4e0e\u6c5f\u6d77\u5929\u4e3a\u654c\uff0c\u4f46 \u6709\u4e86\u6587\u9053\u5e84\u6491\u8170\uff0c\u4ed6\u7684\u80c6\u5b50\u5c31\u5927\u8d77\u6765\u4e86\u3002\u4e24\u4eba\u65e5\u5915\u7b79\u601d\uff0c\u90fd\u662f\u5982\u4f55\u62a5\u4ec7\u4e4b\u4e8b\u3002 \u3000\u3000\u6587\u9053\u5e84\u7ec3\u6210\u4e86\u201c\u4e09\u8c61\u795e\u529f\u201d\uff0c\u8fd9\u6b21\u91cd\u5c65\u4e2d\u539f\uff0c\u5c31\u50cf\u4ed6\u7684\u53d4\u7236\u5f53\u5e74\u4e00\u6837\uff0c\u91ce\u5fc3\u52c3\u52c3\uff0c\u767d\u89c6 \u6781\u9ad8\u3002\u53ef\u662f\u4ed6\u5bf9\u4e8e\u91d1\u4e16\u9057\u7684\u8863\u94b5\u4f20\u4eba\u6c5f\u6d77\u5927\uff0c\u52a0\u8fd8\u662f\u4e0d\u80fd\u4e0d\u6709\u51e0\u5206\u987e\u5fcc\uff0c\u81ea\u5fd6\u672a\u5fc5\u5c31\u6709\u6218\u80dc \u6c5f\u6d77\u5929\u7684\u628a\u63e1\u3002\u6545\u6b64\u4ed6\u4eec\u867d\u7136\u662f\u65e5\u5915\u7b79\u601d\uff0c\u5fd7\u5207\u590d\u4ec7\uff0c\u5374\u4ecd\u662f\u8fdf\u8fdf\u4e0d\u6562\u53d1\u96be\u3002\u4ed6\u4eec\u5728\u7b49\u5f85\u6709 \u5229\u7684\u65f6\u673a\u3002 \u3000\u3000\u5982\u4eca\u8fd9\u6709\u5229\u7684\u65f6\u673a\u6765\u4e86\uff0c\u4e09\u5929\u4e4b\u540e\u662f\u6c5f\u6d77\u5929\u5973\u513f\u51fa\u9601\u7684\u65e5\u5b50\uff0c\u4ee3\u8868\u6b66\u5f53\u6d3e\u524d\u5f80\u8d3a\u559c\u7684\u79e6 \u5143\u6d69\u5374\u5de7\u5728\u4eca\u5929\u95ef\u8fdb\u4e86\u5c01\u5bb6\uff0c\u6587\u9053\u5e84\u9042\u4e0e\u5c01\u5b50\u8d85\u5e03\u4e0b\u9677\u9631\uff0c\u9a97\u79e6\u5143\u6d69\u5410\u51fa\u771f\u60c5\uff0c\u4e8e\u662f\u5c31\u7528 \u201c\u5343\u65e5\u9189\u201d\u7684\u836f\u9152\u704c\u9189\u4e86\u4ed6\uff0c\u5728\u559d\u9152\u4e4b\u65f6\u6587\u9053\u5e84\u3001\u5c01\u5b50\u8d85\u548c\u6587\u80dc\u4e2d\u90fd\u662f\u53e3\u4e2d\u5148\u542b\u4e86\u89e3\u836f\u7684\u3002 \u3000\u3000\u4f46\u5bf9\u4e8e\u4ed6\u4eec\u53d1\u52a8\u7684\u8fd9\u4e2a\u9634\u8c0b\uff0c\u5c01\u5999\u5ae6\u5374\u662f\u6beb\u4e0d\u77e5\u60c5\uff0c\u662f\u4ee5\u624d\u6709\u4ee4\u5f97\u6587\u9053\u5e84\u751a\u611f\u5c34\u5c2c\u7684\u4e00 \u95ee\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u76b1\u4e86\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5ae6\u513f\uff0c\u5927\u4eba\u7684\u4e8b\u4f60\u4e0d\u5fc5\u591a\u95ee\u3002\u6211\u548c\u53d4\u53d4\u505a\u7684\u4e8b\u603b\u4e0d\u4f1a\u9519 \u7684\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u603b\u662f\u6253\u70c2\u6c99\u9505\u8981\u95ee\u5230\u5e95\u7684\u813e\u6c14\uff0c\u5658\u7740\u5634\u513f\u4f9d\u7136\u95ee\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u5c31\u8981\u95ee\u561b\u3002\u6c5f\u6d77\u5929 \u5728\u6c5f\u6e56\u4e0a\u4e0d\u662f\u6709\u5927\u4fa0\u4e4b\u79f0\u7684\u5417\uff1f\u90a3\u4e48\u6587\u53d4\u53d4\u4f55\u4ee5\u4f1a\u4e0e\u4ed6\u4f5c\u5bf9\uff1f\u201d \u3000\u3000\u6587\u9053\u5e84\u5fae\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6c5f\u6d77\u5929\u662f\u6709\u5927\u4fa0\u4e4b\u79f0\u3002\u4f46\u8fd9\u4e43\u662f\u6d6a\u5f97\u865a\u540d\uff0c\u9a97\u9a97\u65e0\u77e5\u7684\u51e1\u592b\u4fd7 \u5b50\u800c\u5df2\u3002\u5176\u5b9e\u2026\u2026\u201d\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u5176\u5b9e\u4ec0\u4e48\u3002\u201d\u6587\u9053\u5e84\u671b\u4e86\u5c01\u5b50\u8d85\u4e00\u773c\uff0c\u8bf4\uff1a\u201c\u8bf4\u7ed9\u4f60\u542c\u4e5f \u4e0d\u6253\u7d27\u3002\u5176\u5b9e\u6c5f\u6d77\u5929\u4e43\u662f\u53cd\u53db\u671d\u5ef7\u7684\u9006\u8d3c\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u53cd\u53db\u671d\u5ef7\u53c8\u6709\u4ec0\u4e48\u4e0d\u597d\u4e86\uff1f\u201d\u6211\u4ee5\u524d\u8fd8\u542c\u5f97\u6709\u51e0\u4e2a\u730e\u4eba\u8bf4\u8bdd\uff0c\u8bf4\u662f\u671d\u5ef7\u7684 \u5b98\u90fd\u662f\u4e9b\u6df7\u5e10\u4e1c\u897f\uff0c\u82db\u6350\u6742\u7a0e\uff0c\u62c9\u592b\u5f81\u5de5\uff0c\u8feb\u5f97\u4ed6\u4eec\u4e0d\u80fd\u4e0d\u8eb2\u8fdb\u8352\u5c71\u91ce\u5cad\u6765\u505a\u730e\u6237\u5462\uff01\u5728\u8fd9 \u5c71\u4e0a\u8981\u730e\u867d\u7136\u5f88\u8270\u96be\uff0c\u4e5f\u8fd8\u80dc\u4e8e\u5728\u5e73\u5730\u4e0a\u53d7\u5b98\u5e9c\u7684\u6b3a\u538b\u3002 \u3000\u3000\u201c\u539f\u6765\u5c01\u5999\u5ae6\u662f\u5c01\u5b50\u8d85\u5c45\u5c4a\u5728\u5f82\u5f95\u5c71\u4e4b\u540e\u624d\u51fa\u4e16\u7684\uff0c\u5c01\u5b50\u8d85\u6050\u9632\u4fa0\u6587\u9053\u653e\u4e0d\u8fc7\u4ed6\uff0c\u4ed6\u81ea \u8eab\u7684\u6765\u5386\u662f\u8fde\u5973\u513f\u90e8\u6ca1\u6709\u544a\u8bc9\u7684\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u4eca\u5e74\u5341\u4e5d\u5c81\u4e86\uff0c\u4ece\u6ca1\u6709\u51fa\u8fc7\u8fdc\u95e8\uff0c\u4e0d\u8fc7\u5979\u751f\u6027\u597d\u73a9\uff0c\u5728\u8fd9\u5c71\u4e0a\u5c71\u4e0b\uff0c\u5979\u5219\u662f\u5230\u5904 \u4e71\u8dd1\u7684\u3002\u5f82\u5f95\u5c71\u4e0a\u4eba\u70df\u7a00\u5c11\uff0c\u4f46\u4e5f\u6709\u51e0\u5bb6\u730e\u6237\uff0c\u5c71\u4e0b\u7684\u519c\u5bb6\u90a3\u5c31\u66f4\u591a\u4e86\u3002\u5f82\u5f95\u5c71\u4e0e\u4e1c\u5e73\u53bf\u6768 \u5bb6\u5e84\u7684\u8ddd\u79bb\u4e0d\u8fc7\u51e0\u65e5\u8def\u7a0b\uff0c\u5c01\u5999\u5ae6\u662d\u5c31\u662f\u8ba4\u5979\u6240\u63a5\u89e6\u7684\u90a3\u4e9b\u519c\u5bb6\u4e0e\u730e\u6237\u7684\u53e3\u4e2d\uff0c\u5f97\u77e5\u6c5f\u6d77\u5929 \u5927\u4fa0\u4e4b\u540d\uff0c\u4ee5\u53ca\u5b98\u5e9c\u6b3a\u538b\u767e\u59d3\u7684\u4e00\u4e9b\u4e8b\u5b9e\u7684\uff0c\u53ef\u662f\u5979\u5374\u4e0d\u77e5\u9053\u5979\u7684\u7239\u7239\u662f\u6e05\u5bab\u5927\u5185\u7684\u536b\u58eb\u3002 \u3000\u3000\u6587\u9053\u5e84\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\u3002\u201d\u4f84\u5973\uff0c\u4f60\u600e\u80fd\u542c\u4fe1\u65e0\u77e5\u7684\u611a\u6c11\u4e4b\u8bf4\uff0c\u4e0d\u9519\uff0c\u6709\u4e9b\u5f53\u5b98\u7684\u5f88 \u574f\uff01\u4f46\u5e76\u4e0d\u662f\u6240\u6709\u7684\u5b98\u90fd\u662f\u574f\u7684\u3002\u4f60\u4e0d\u662f\u66fe\u8bfb\u8fc7\u4e66\u7684\u5417\uff1f\u4e66\u4e2d\u6709\u8bdd\uff1a\u6c11\u4e0d\u53ef\u4e00\u65e5\u65e0\u541b\u3002\u53ef\u89c1 \u7687\u5e1d\u662f\u8981\u6709\u7684\uff0c\u671d\u5ef7\u4e5f\u603b\u662f\u8981\u6709\u7684\u3002\u600e\u80fd\u53cd\u53db\u671d\u5ef7\u5462\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u5e74\u7eaa\u592a\u8f7b\uff0c\u601d\u60f3\u66f4\u672a\u6210\u719f\u3002\u6587\u9053\u5e84\u4e00\u756a\u4f3c\u662f\u800c\u975e\u7684\u6b6a\u7406\uff0c\u628a\u5979\u8bf4\u5f97\u53c8\u7cca\u6d82\u8d77\u6765\u3002 \u5979\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5728\u4e66\u4e0a\u662f\u66fe\u8bfb\u8fc7\u8fd9\u53e5\u8bdd\u3002\u53ef\u662f\u4e66\u4e2d\u8bf4\u7684\u548c\u8001\u767e\u59d3\u8bf4\u7684\u53ef\u662f\u5b8c \u5168\u4e0d\u4e00\u6837\u7684\u554a\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u7b11\u9053\uff1a\u201c\u4f60\u8bfb\u7684\u662f\u5723\u8d24\u4e4b\u4e66\uff0c\u4f60\u542c\u5230\u7684\u5219\u662f\u5eb8\u592b\u4fd7\u5b50\u4e4b\u8bf4\u3002\u4f60\u60f3\u60f3\uff0c\u90a3\u4e9b\u4e00\u811a\u725b \u7caa\u3001\u8eab\u62ab\u517d\u76ae\u7684\u519c\u5bb6\u730e\u4ea7\uff0c\u600e\u6bd4\u5f97\u4e0a\u53e4\u65f6\u7684\u5723\u8d24\uff1f\u4f60\u542c\u6765\u7684\u90a3\u4e9b\u8bdd\u7528\u5723\u8d24\u4e66\u4e2d\u7684\u9053\u7406\u6765\u8bb2\uff0c \u5c31\u90fd\u662f\u2018\u5f02\u7aef\u90aa\u8bf4\u2019\u3002\u5f02\u7aef\u90aa\u8bf4\u662f\u4e0d\u80fd\u542c\u4fe1\u7684\u554a\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u5f88\u5c11\u7528\u5fc3\u601d\u8003\u8fc7\u4e00\u4e2a\u95ee\u9898\uff0c\u5982\u4eca\u542c\u4e86\u6587\u9053\u5e84\u201c\u5f15\u7ecf\u636e\u5178\u201d\u6240\u8bf4\u7684\u8bdd\uff0c\u89c9\u5f97\u4e5f\u4f3c\u4e4e \u5f88\u6709\u9053\u7406\uff0c\u4f46\u8001\u767e\u59d3\u8bf4\u7684\u90a3\u4e9b\u4e8b\u5b9e\uff0c\u5979\u4e5f\u662f\u76f8\u4fe1\u51b3\u975e\u634f\u9020\u7684\u3002\u90a3\u4e48\u7a76\u7adf\u662f\u8c01\u5bf9\u8c01\u4e0d\u5bf9\u5462\uff1f\u5c01 \u5999\u5ae6\u60f3\u5f97\u5934\u660f\u8111\u80c0\uff0c\u5fc3\u4e2d\u4e00\u7247\u6df7\u4e71\u3002\u5979\u4e0d\u6562\u6000\u7591\u4e66\u4e0a\u7684\u8bf4\u8bdd\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6216\u8bb8\u5f53\u771f\u662f\u50cf\u4e08 \u53d4\u53d4\u8bf4\u7684\u90a3\u6837\uff0c\u574f\u5b98\u53ea\u662f\u4e2a\u522b\u7684\u5427\uff1f\u5982\u679c\u8fd9\u6837\uff0c\u6c5f\u5927\u4fa0\u53cd\u53db\u671d\u5ef7\u90a3\u5c31\u662f\u4e0d\u5bf9\u4e86\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u53c8\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c01\u5927\u54e5\uff0c\u539f\u6765\u4f60\u8fd8\u6ca1\u6709\u628a\u81ea\u5df1\u7684\u8eab\u4efd\u544a\u8bc9\u4f84\u5973\u3002\u8fd9\u6b21\u54b1\u4eec \u82e5\u662f\u5927\u529f\u544a\u6210\uff0c\u5c31\u4e0d\u5fc5\u7792\u7740\u4f84\u5973\u4e86\u3002\u201d\u5c01\u5b50\u8d85\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u4eec\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u7239\u7239\u4f60\u4e00\u5411\u8bf4\u81ea\u5df1\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0 \u4e48\u8eab\u4efd\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u7b11\u9053\uff1a\u201c\u50bb\u4e2b\u5934\uff0c\u4e0d\u5fc5\u7740\u6025\uff0c\u4e09\u5929\u4e4b\u540e\uff0c\u7239\u7239\u90fd\u4f1a\u544a\u8bc9\u4f60\u7684\u3002\u4ece\u73b0\u5728\u8d77\uff0c\u4e0d\u51c6\u4f60 \u8981\u6270\u5927\u4eba\u7684\u8bf4\u8bdd\u4e86\u3002\u6587\u8d24\u5f1f\uff0c\u54b1\u4eec\u5e94\u8be5\u8c08\u6b63\u7ecf\u7684\u4e86\u3002\u8fd9\u5c0f\u5b50\u5982\u4f55\u5904\u7f6e\uff1f\u201d\u8bf4\u7740\u8bdd\u6307\u4e00\u6307\u9189\u5012 \u5730\u4e0a\u7684\u79e6\u5143\u6d69\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u7559\u7740\u603b\u662f\u7978\u80ce\uff0c\u5e72\u8106\u628a\u4ed6\u4e00\u5200\u5bb0\u4e86\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u5514\uff0c\u6740\u4e86\u4e5f\u597d\uff0c\u5e72\u51c0\u5229\u843d\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u5fcd\u4e0d\u7740\u53c8\u8981\u201c\u6253\u6270\u201d\u4ed6\u4eec\u7684\u8c08\u8bdd\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u521a\u624d\u4f60\u4eec\u8fd8\u628a\u4ed6\u5949\u4e3a\u4e0a\u5bbe\uff0c \u4ed6\u5e76\u6ca1\u6709\u4ec0\u4e48\u7f6a\u554a\uff0c\u600e\u4e48\u53ef\u4ee5\u5c31\u5c06\u4ed6\u4e00\u5200\u5bb0\u4e86\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u4f60\u61c2\u5f97\u4ec0\u4e48\uff1f\u6211\u8bf4\u4e0d\u8bb8\u4f60\u6253\u5c94\u4f60\u5c31\u4e0d\u8981\u6253\u5c94\u3002\u4e0d\u8fc7\uff0c\u8bdd\u8bf4\u56de\u6765\uff0c\u6587\u8d24\u5f1f\uff0c \u8fd9\u5c0f\u5b50\u662f\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\uff0c\u6740\u4e86\u4ed6\u53ea\u6015\u4e0d\u5927\u597d\u3002\u4e8b\u60c5\u603b\u4f1a\u66b4\u9732\u7684\uff0c\u54b1\u4eec\u4f55\u82e6\u4e0e\u6b66\u5f53\u6d3e\u7ed3\u4e0b\u5927 \u4ec7\uff1f\u201d\u5c01\u5b50\u8d85\u6709\u5bb6\u4e1a\u5728\u6b64\uff0c\u53ea\u6015\u95ef\u4e0b\u5927\u7978\u4e4b\u540e\uff0c\u540e\u679c\u8981\u4ed6\u627f\u62c5\uff0c\u800c\u6587\u9053\u5e84\u5374\u53ef\u4ee5\u4e00\u8d70\u4e86\u4e4b\u3002 \u3000\u3000\u6587\u9053\u5e84\u6709\u70b9\u4e0d\u5927\u9ad8\u5174\uff0c\u4f46\u4ed6\u8fd8\u6709\u8981\u4f9d\u9760\u5c01\u5b50\u8d85\u4e4b\u5904\uff0c\u9762\u8272\u4e0a\u5374\u6ca1\u8868\u9732\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u597d \u5427\uff0c\u90a3\u5c31\u6682\u4e14\u7559\u4ed6\u4e00\u547d\uff0c\u5f85\u54b1\u4eec\u4e8b\u6210\u4e4b\u540e\u518d\u8bf4\uff0c\u53cd\u6b63\u4ed6\u662f\u8dd1\u4e0d\u4e86\u7684\u3002\u201d\u5c01\u5b50\u8d85\u653e\u4e0b\u4e86\u4e00\u9897 \u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u554a\uff0c\u4ed6\u559d\u4e86\u6211\u7684\u2018\u5343\u65e5\u9189\u2019\uff0c\u81f3\u5c11\u4e5f\u8981\u660f\u8ff7\u4e2a\u516d\u5929\u4e03\u591c\u3002\u5f85\u54b1\u4eec\u4e8b\u6210\u4e4b\u540e\uff0c \u518d\u6740\u4ed6\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u4e2d\u513f\uff0c\u4f60\u628a\u8fd9\u5c0f\u5b50\u62d6\u8fdb\u623f\u53bb\uff0c\u7167\u6211\u7684\u8bdd\u505a\u3002\u201d \u3000\u3000\u6587\u80dc\u4e2d\u5e94\u4e86\u58f0\u201c\u662f\u3002\u201d\u628a\u79e6\u5143\u6d69\u62d6\u4e86\u8d77\u6765\uff0c\u62d6\u7740\u4ed6\u8d70\u56de\u81ea\u5df1\u7684\u623f\u95f4\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u6211\u89c9\u5f97\u5934\u6655\u3002\u6211\u4e5f\u8981\u56de\u623f\u4e2d\u6b47\u606f\u4e86\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u4f60\u4e00\u6ef4\u9152\u90fd\u6ca1\u6cbe\u5507\uff0c\u4e5f\u5934\u6655\u4e86\uff1f\u201d\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4e5f\u4e0d\u77e5\u662f\u4f55\u7f18 \u6545\uff0c\u5f53\u771f\u662f\u5934\u6655\u8d77\u6765\u3002\u201d\u5979\u662f\u60f3\u95ee\u9898\u60f3\u4e0d\u901a\u800c\u8111\u80c0\u5934\u660f\u7684\u3002\u4f46\u5979\u53ef\u4e0d\u613f\u610f\u544a\u8bc9\u6587\u9053\u5e84\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u90a3\u4f60\u8d76\u5feb\u56de\u623f\u53bb\u5427\u3002\u4f60\u4e0d\u5728\u8fd9\u513f\uff0c\u6211\u7684\u8033\u6839\u8fd8\u6e05\u51c0\u4e00\u4e9b\u3002\u201d \u3000\u3000\u6587\u80dc\u4e2d\u4e0e\u5c01\u5999\u5ae6\u8d70\u540e\uff0c\u6587\u9053\u5e84\u4e0e\u5c01\u5b50\u8d85\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u60f3\u4e0d\u5230\u6b63\u5728\u54b1\u4eec\u82e6\u601d\u65e0\u7b56 \u4e4b\u65f6\uff0c\u8fd9\u5c0f\u5b50\u5374\u795e\u5dee\u9b3c\u4f7f\u7684\u95ef\u5230\u8fd9\u513f\u6765\u4e86\u3002\u771f\u662f\u4e0a\u5929\u8d50\u7ed9\u54b1\u4eec\u7684\u597d\u673a\u4f1a\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u5982\u4f55\u505a\u6cd5\uff0c\u613f\u95fb\u5176\u8be6\u3002\u201d\u5c01\u5b50\u8d85\u662f\u8001\u6c5f\u6e56\uff0c\u4ed6\u5f53\u7136\u77e5\u9053\u6587\u9053\u5e84\u662f\u8981\u85c9\u6b64\u673a \u4f1a\uff0c\u5192\u5145\u8d3a\u5ba2\uff0c\u6df7\u8fdb\u6c5f\u5bb6\u3002\u4f46\u5177\u4f53\u7684\u505a\u6cd5\uff0c\u6587\u9053\u5e84\u8fd8\u6ca1\u6709\u544a\u8bc9\u4ed6\uff0c\u4ed6\u662f\u5fc5\u987b\u95ee\u4e2a\u6e05\u695a\u7684\u3002\u4ed6 \u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u82e5\u662f\u592a\u8fc7\u5371\u9669\uff0c\u6211\u5c31\u4e0d\u5e72\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\u662f\u8ba9\u4e2d\u513f\u5192\u5145\u8fd9\u5c0f\u5b50\u7684\u8eab\u4efd\uff0c\u54b1\u4eec\u8ddf\u7740\u4ed6\u6df7\u8fdb\u6c5f\u5bb6\u3002\u7136\u540e\u2026\u2026\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u4e14\u6162\uff0c\u65e6\u6162\u3002\u8fd9\u91cc\u6709\u4e2a\u7834\u7efd\uff0c\u8bf7\u5e16\u53ea\u6709\u4e00\u5f20\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u7b11\u9053\uff1a\u201c\u8fd9\u8bf7\u5e16\u662f\u53d1\u7ed9\u6b66\u5f53\u6d3e\u638c\u95e8\u4eba\u7684\uff0c\u53ef\u5e76\u6ca1\u6709\u89c4\u5b9a\u4e00\u5f20\u8bf7\u5e16\u53ea\u8bb8\u4ed6\u6d3e\u9053\u4e00\u4e2a \u5f1f\u5b50\u505a\u8d3a\u5ba2\u554a\u3002\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\u6709\u6570\u5343\u4e4b\u4f17\uff0c\u54b1\u4eec\u53ef\u4ee5\u5192\u5145\u6b66\u5f53\u6d3e\u7684\u4eba\uff0c\u4e5f\u53ef\u4ee5\u5f53\u4f5c\u662f\u79e6\u5143\u6d69 \u8fd9\u5c0f\u5b50\u4ee3\u9080\u7684\u670b\u53cb\uff0c\u5c31\u8bf4\u662f\u6155\u540d\u524d\u53bb\u9053\u8d3a\u7684\uff0c\u90a3\u4e5f\u884c\u554a\u3002\u6c5f\u6d77\u5929\u597d\u5ba2\u4e4b\u540d\uff0c\u5929\u4e0b\u7686\u77e5\u3002\u7ed9\u4ed6 \u4f5c\u77e5\u5ba2\u7684\uff0c\u96be\u9053\u8fd8\u4f1a\u963b\u62e6\u54b1\u4eec\uff1f\u5f53\u7136\uff0c\u82e5\u662f\u5b8c\u5168\u4e0d\u77e5\u6765\u5386\u7684\u4eba\uff0c\u90a3\u662f\u4e0d\u80fd\u8f7b\u6613\u8fdb\u53bb\u7684\u3002\u4f46\u73b0 \u5728\u6709\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\u5e26\u5f15\uff0c\u8fd9\u53ef\u5c31\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4e14\u6162\u3002\u8fd8\u6709\u7834\u7efd\u3002\u5047\u5982\u5bbe\u5ba2\u4e4b\u4e2d\u6709\u8ba4\u5f97\u79e6\u5143\u6d69\u8fd9\u5c0f\u5b50\u7684\u5462\uff1f\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u6211\u65e9\u5c31\u60f3\u8fc7\u4e86\u3002\u7b2c\u4e00\uff0c\u79e6\u5143\u6d69\u8fd9\u5c0f\u5b50\u662f\u521d\u6b21\u51fa\u9053\uff0c\u6c5f\u6e56\u4e0a\u7684\u6210\u540d\u4eba\u7269\u8ba4\u5f97 \u4ed6\u7684\u4e00\u5b9a\u662f\u5c11\u4e4b\u53c8\u5c11\u3002\u6c5f\u5bb6\u8d3a\u5ba2\u4f17\u591a\uff0c\u54ea\u6709\u8fd9\u6837\u5de7\u6070\u6070\u5c31\u8ba9\u8ba4\u8bc6\u4ed6\u7684\u4eba\u78b0\u4e0a\u4e86\uff1b\u7b2c\u4e8c\uff0c\u6211\u6709 \u5bb6\u53d4\u79d8\u5236\u7684\u6613\u5bb9\u4e39\uff0c\u4e2d\u513f\u548c\u8fd9\u5c0f\u5b50\u7684\u8eab\u6750\u5dee\u4e0d\u591a\uff0c\u5316\u88c5\u4e4b\u540e\uff0c\u9664\u975e\u662f\u4ed6\u7684\u5e08\u7236\uff0c\u4eb2\u4eba\uff0c\u6216\u8005 \u65e5\u5e38\u548c\u4ed6\u671d\u5915\u76f8\u5904\u7684\u540c\u95e8\u624d\u80fd\u5206\u522b\u771f\u5047\uff0c\u666e\u901a\u89c1\u8fc7\u51e0\u9762\u7684\u4eba\u662f\u4e00\u5b9a\u5206\u4e0d\u51fa\u6765\u7684\u3002\u800c\u4e14\u54b1\u4eec\u53ea \u8981\u6df7\u5f97\u8fdb\u6c5f\u5bb6\u4fbf\u884c\uff0c\u53c8\u4e0d\u9700\u8981\u9017\u7559\u591a\u4e45\u7684\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u6211\u8fd8\u6709\u70b9\u62c5\u5fe7\uff0c\u4ece\u524d\u66fe\u7ecf\u53d1\u751f\u8fc7\u4e00\u6869\u76f8\u4f3c\u7684\u4e8b\u3002\u53ea\u6015\u6c5f\u6d77\u5929\u5b9a\u6709\u6212\u5fc3\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u53ef\u662f\u4ece\u524d\u53f6\u5c60\u6237\u7684\u513f\u5b50\u5192\u5145\u6c5f\u6d77\u5929\u5185\u4f84\u4e4b\u4e8b\uff1f\u201d\u6587\u9053\u5e84\u8fd9\u4e8c\u5341\u5e74\u6765 \u867d\u662f\u8fdc\u5c45\u6d77\u5916\uff0c\u4f46\u4e00\u5230\u4e2d\u539f\uff0c\u5c31\u628a\u6c5f\u5bb6\u7684\u4e8b\u60c5\u90fd\u6253\u542c\u5f97\u6e05\u695a\u4e86\u3002\u662f\u4ee5\u4ed6\u77e5\u9053\u6709\u201c\u771f\u5047\u53f6\u51cc \u98ce\u201d\u8fd9\u4e2a\u6545\u4e8b\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u6b63\u662f\u3002\u8bd5\u60f3\u6c5f\u6d77\u5929\u66fe\u53d7\u8fc7\u8fd9\u4e48\u5927\u7684\u6559\u8bad\uff0c\u4ed6\u80fd\u4e0d\u5177\u6212\u5fc3\uff1f\u6c5f\u592b\u4eba\u53c8\u662f\u4e2a\u975e \u5e38\u7cbe\u660e\u80fd\u5e72\u7684\u5973\u4eba\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u4f60\u662f\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\u3002\u8fd9\u4e24\u4ef6\u4e8b\u60c5\u770b\u6765\u76f8\u4f3c\uff0c\u5176\u5b9e\u5927\u4e0d\u76f8\u540c\uff0c\u53f6\u51cc\u98ce \u5f53\u5e74\u5728\u6c5f\u6d77\u5929\u7684\u95e8\u4e0b\u6570\u5e74\u4e4b\u4e45\uff0c\u54b1\u4eec\u5219\u53ea\u987b\u5728\u6c5f\u5bb6\u6df7\u51e0\u4e2a\u65f6\u8fb0\u3002\u7b2c\u4e8c\uff0c\u79e6\u5143\u6d69\u662f\u540e\u751f\u665a\u8f88\uff0c \u54b1\u4eec\u7b97\u51c6\u4e86\u65f6\u5019\uff0c\u5f85\u65b0\u4eba\u62dc\u5802\u4e4b\u524d\u4e00\u4e2a\u65f6\u8fb0\u624d\u8fdb\u6c5f\u5bb6\u3002\u63a5\u5f85\u4e00\u4e2a\u665a\u8f88\uff0c\u4e3b\u4eba\u5bb6\u5b9a\u7136\u4e0d\u4f1a\u51fa\u8fce \u7684\uff0c\u591a\u534a\u53ea\u662f\u77e5\u5ba2\u5f15\u8fdb\u800c\u5df2\u3002\u8fdb\u4e86\u6c5f\u5bb6\u4e4b\u540e\uff0c\u54b1\u4eec\u548c\u666e\u901a\u7684\u5ba2\u4eba\u540c\u5750\u4e00\u75e4\uff0c\u60f3\u6765\u6c5f\u6d77\u5929\u4e5f\u4e0d \u4f1a\u9080\u8bf7\u4e00\u4e2a\u6b66\u5f53\u6d3e\u7684\u5c0f\u5b50\u5750\u4e0a\u9996\u5e2d\u7684\u5427\uff1f\u8fd9\u4e5f\u5c31\u662f\u8bf4\uff0c\u6c5f\u6d77\u5929\u592b\u59bb\u5f88\u53ef\u80fd\u6839\u672c\u5c31\u6ca1\u6709\u89c1\u5230 \u2018\u79e6\u5143\u6d69\u2019\u7684\u673a\u4f1a\uff0c\u8fd9\u548c\u53f6\u51cc\u98ce\u7684\u60c5\u5f62\u5f53\u7136\u662f\u5927\u5927\u4e0d\u540c\uff01\u4efb\u5979\u6c5f\u592b\u4eba\u5982\u4f55\u7cbe\u660e\uff0c\u5979\u6ca1\u6709\u673a\u4f1a \u89c1\u5230\u2018\u79e6\u5143\u6d69\u2019\uff0c\u53c8\u4ece\u4f55\u76d8\u95ee\u8d77\u6765\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u542c\u6587\u9053\u5e84\u8bf4\u5f97\u6709\u7406\uff0c\u5fc3\u60f3\u3002\u201d\u8fd9\u4e48\u8bf4\u6765\uff0c\u5192\u7684\u9669\u5e76\u4e0d\u7b97\u5927\uff0c\u5012\u662f\u53ef\u4ee5\u8bd5\u8bd5\u3002\u201d\u4e8e \u662f\u95ee\u9053\uff1a\u201c\u54b1\u4eec\u6df7\u8fdb\u6c5f\u5bb6\u4e4b\u540e\uff0c\u53c8\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u90a3\u5c31\u662f\u6211\u7684\u4e8b\u4e86\u3002\u6211\u6216\u8005\u672a\u5fc5\u80dc\u5f97\u8fc7\u6c5f\u6d77\u5929\uff0c\u4f46\u5bf9\u4ed8\u4ed6\u7684\u95e8\u4eba\u5f1f\u5b50\uff0c\u81ea\u4fe1 \u662f\u7ef0\u7ef0\u6709\u4f59\u3002\u6211\u53ef\u4ee5\u7528\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6bb5\uff0c\u628a\u4ed6\u7684\u5973\u513f\u5973\u5a7f\u64d2\u4e86\u4e0b\u6765\uff0c\u4f5c\u4e3a\u4eba\u8d28\u3002\u4f60\u53ea\u987b \u7167\u5e94\u4e2d\u51e0\uff0c\u8d81\u6df7\u4e71\u4e4b\u65f6\uff0c\u9003\u8d70\u4fbf\u884c\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u4e00\u542c\u4e0d\u7528\u4ed6\u52a8\u624b\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e8b\u4e0d\u6210\uff0c\u6c5f\u6d77\u5929\u5f53\u573a\u628a\u6587\u9053\u5e84\u51fb\u6740\u7684\u8bdd\uff0c\u6211\u4e5f\u53ef \u4ee5\u8d81\u4e71\u9003\u8d70\u3002\u4e8b\u82e5\u6210\u529f\uff0c\u6709\u4eba\u8d28\u5728\u624b\uff0c\u90a3\u5c31\u66f4\u4e0d\u6015\u4e86\u3002\u8fd9\u4e2a\u9669\u4e5f\u503c\u5f97\u4e00\u5192\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5f53\u7136\uff0c\u4e8b\u6210\u4e4b\u540e\uff0c\u8fd8\u6709\u4ef0\u4ed7\u4f60\u4eec\u7236\u5973\u4e4b\u5904\u3002\u636e\u6211\u6240\u77e5\uff0c\u671d\u5ef7\u662f\u628a\u6c5f \u6d77\u5929\u6068\u4e4b\u5165\u9aa8\u7684\uff0c\u53ea\u662f\u4ed6\u6ca1\u6709\u516c\u5f00\u53db\u4e71\uff0c\u800c\u6b66\u529f\u53c8\u592a\u9ad8\u5f3a\uff0c\u4e00\u65f6\u65e0\u53ef\u5948\u4f55\u800c\u5df2\u3002\u201d\u5c01\u5b50\u8d85\u63d2 \u53e3\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\uff0c\u671d\u5ef7\u81ea\u662f\u6068\u4e0d\u5f97\u628a\u6c5f\u6d77\u5929\u6740\u6389\u7684\u3002\u4f46\u82e5\u4e3a\u4ed6\u4e00\u4eba\u5174\u5e08\u52a8\u4f17\uff0c\u672a\u514d\u7b11 \u8bdd\uff0c\u82e5\u6d3e\u51e0\u4e2a\u9ad8\u624b\u53bb\u884c\u523a\u4ed6\u5427\uff0c\u6c5f\u6d77\u5929\u7684\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\uff0c\u53c8\u6709\u8c01\u6562\u53bb\u5192\u8fd9\u4e2a\u9669\uff1f\u8fd9\u4e5f\u5c31\u662f\u6c5f \u6d77\u5929\u6562\u4e8e\u5728\u5bb6\u4e2d\u5927\u8bf7\u5bbe\u5ba2\uff0c\u5927\u529e\u559c\u4e8b\u7684\u7f18\u6545\u3002\u201d\u8bf4\u5230\u6b64\u65f6\uff0c\u53d1\u515a\u6587\u9053\u5e84\u6709\u70b9\u4e0d\u8c6b\u4e4b\u8272\uff0c\u63a5\u7740 \u7b11\u9053\uff1a\u201c\u6c5f\u6d77\u5929\u7684\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\uff0c\u8fd9\u662f\u4ece\u524d\u7684\u4e8b\uff0c\u6709\u4f60\u6765\u5230\u4e2d\u539f\uff0c\u90a3\u5f53\u7136\u5c31\u4e0d\u662f\u4ed6\u4e86\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u662f\u8001\u5144\u5f1f\u4e86\uff0c\u4f60\u4e0d\u5fc5\u7ed9\u6211\u6234\u8fd9\u9876\u9ad8\u5e3d\u3002\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6211\u5f53 \u7136\u4e0d\u6015\u6c5f\u6d77\u5929\uff0c\u4f46\u5355\u6253\u72ec\u6597\uff0c\u8c01\u80dc\u8c01\u8d1f\uff0c\u53ea\u6015\u4e5f\u662f\u4e2a\u672a\u77e5\u4e4b\u6570\u5462\u3002\u4e0d\u8fc7\uff0c\u6211\u5374\u662f\u6562\u53bb\u5192\u8fd9\u4e2a \u9669\u7684\u3002\u201d\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u5f53\u7136\uff0c\u5f53\u7136\uff0c\u8001\u5f1f\u667a\u52c7\u53cc\u5168\uff0c\u8fd9\u6b21\u524d\u5f80\u6c5f\u5bb6\uff0c\u4e00\u5b9a\u9a6c\u5230\u6210\u529f\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u867d\u7136\u53ea\u662f\u6253\u7b97\u6d3b\u64d2\u4ed6\u7684\u5973\u513f\u5973\u5a7f\uff0c\u5e76\u975e\u6740\u6389\u6c5f\u6d77\u5929\uff0c\u4f46\u6709\u4e86\u8fd9\u4e24\u4e2a \u4eba\u8d28\u5728\u624b\uff0c\u89e3\u4e0a\u4eac\u57ce\uff0c\u671d\u5ef7\u5c31\u53ef\u7528\u6765\u62db\u964d\u6c5f\u6d77\u5929\u4e86\u3002\u5373\u4f7f\u6c5f\u6d77\u5929\u4e0d\u53d7\u62db\u964d\uff0c\u5973\u513f\u548c\u5973\u5a7f\u843d\u5728 \u5b98\u5e9c\u624b\u4e2d\uff0c\u4ed6\u4e5f\u603b\u5f97\u6709\u51e0\u5206\u987e\u5fcc\uff0c\u4e0d\u6562\u4e0e\u671d\u5ef7\u4f5c\u5bf9\u4e86\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u542c\u5f97\u7709\u98de\u8272\u821e\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u64d2\u5f97\u6c5f\u6d77\u5929\u7684\u5973\u513f\u5973\u5a7f\uff0c\u8fd9\u4ef6\u529f\u52b3\u4e5f\u662f\u6781\u4e4b\u4e0d\u5c0f \u4e86\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e8b\u6210\u4e4b\u540e\uff0c\u6211\u8981\u4ef0\u4ed7\u4f60\u7684\u5730\u65b9\u4e86\u3002\u4f60\u66fe\u5728\u5927\u5185\u5145\u5f53\u536b\u58eb\u5341\u5e74\u4e4b\u4e45\uff0c \u60f3\u6765\u8fd8\u6709\u65e7\u65e5\u7684\u540c\u50da\u5982\u4eca\u5c1a\u5728\u671d\u5ef7\u7684\uff0c\u4e8b\u6210\u4e4b\u540e\uff0c\u5c31\u8981\u4ef0\u4ed7\u4f60\u53bb\u62a5\u529f\u4e86\uff0c\u6211\u6709\u4e9b\u4e0d\u65b9\u4fbf\u81ea\u5df1 \u8bf4\u7684\u8bdd\uff0c\u4e5f\u5f97\u8bf7\u4f60\u4ee3\u4e3a\u7980\u594f\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u5f53\u7136\u61c2\u5f97\u4ed6\u7684\u610f\u601d\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u4f55\u987b\u8001\u5f1f\u63d0\u51fa\uff0c\u54b1\u4eec\u5f53\u7136\u4e0d\u4f1a\u5e73\u767d\u628a\u6c5f\u6d77\u5929\u7684 \u5973\u513f\u5973\u5a7f\u4ea4\u51fa\u6765\u7684\uff0c\u6211\u81ea\u7136\u4f1a\u7ed9\u4f60\u8c08\u59a5\u6761\u4ef6\u3002\u5fa1\u6797\u519b\u7edf\u9886\u548c\u5927\u5185\u603b\u7ba1\u8fd9\u4e24\u4e2a\u804c\u4f4d\u6050\u6015\u4e00\u65f6\u4e0d \u80fd\u66f4\u6362\uff0c\u4f46\u4f60\u8981\u5f53\u4e0a\u4e00\u4e2a\u5fa1\u6797\u519b\u7684\u526f\u7edf\u9886\u7684\u8bdd\uff0c\u6211\u770b\u90a3\u662f\u4e00\u5b9a\u53ef\u4ee5\u529e\u5230\u7684\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6682\u65f6\u5f53\u4e00\u4e2a\u5fa1\u6797\u519b\u7684\u526f\u7edf\u9886\uff0c\u90a3\u4e5f\u4e0d\u9519\u4e86\u3002\u4f60\u653e\u5fc3\uff0c\u6211\u82e5\u5f97 \u6709\u9ad8\u5b98\u539a\u7984\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u8f9c\u8d1f\u4f60\u7684\u3002\u54b1\u4eec\u662f\u6709\u798f\u540c\u4eab\uff0c\u6709\u7978\u540c\u5f53\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u7b11\u9053\uff1a\u201c\u6211\u53ea\u6c42\u5b98\u590d\u539f\u804c\uff0c\u6211\u4e5f\u5c31\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u4f60\u4e0d\u77e5\u9053\uff0c\u5f53\u5e74\u6211\u5728\u6c13\u5c71\u4e00\u8d25\u4e4b \u540e\uff0c\u65e0\u989c\u56de\u53bb\u670d\u4f8d\u7687\u4e0a\uff0c\u4e5f\u6015\u7687\u4e0a\u964d\u7f6a\uff0c\u624d\u4e0d\u5f97\u5df2\u5728\u8fd9\u8352\u5c71\u9690\u5c45\u3002\u8fd9\u5341\u5e74\u6765\uff0c\u6211\u8db3\u8ff9\u4e0d\u51fa\u6b64 \u5c71\uff0c\u6574\u5929\u4e0e\u9e1f\u517d\u4e3a\u7fa4\uff0c\u5fc3\u4e2d\u4e0d\u77e5\u6709\u591a\u6291\u90c1\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u7b11\u9053\uff1a\u201c\u662f\u5440\uff0c\u8fd9\u662f\u54b1\u4eec\u4e1c\u5c71\u590d\u51fa\u7684\u5927\u597d\u673a\u4f1a\uff0c\u54b1\u4eec\u5fc5\u987b\u597d\u597d\u7684\u5e72\u4e86\u3002\u55ef\uff0c\u8fd8\u6709 \u4e00\u4ef6\u4e8b\u60c5\u8bf7\u4f84\u5973\u5e2e\u5fd9\u7684\uff0c\u4f46\u6211\u521a\u624d\u542c\u5979\u7684\u8bf4\u8bdd\uff0c\u5374\u662f\u6709\u70b9\u4e0d\u5927\u653e\u5fc3\uff0c\u4e0d\u77e5\u8fd9\u4ef6\u4e8b\u5979\u53ef\u80fd\u529e\u5f97 \u59a5\u5f53\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u4f55\u4e8b\uff1f\u201d\u6587\u9053\u5e84\u9053\uff1a\u201c\u4f60\u3001\u6211\u548c\u4e2d\u513f\u516b\u6708\u5341\u4e94\u90a3\u5929\u524d\u5f80\u6c5f\u5bb6\uff0c\u79e6\u5143\u6d69\u8fd9\u5c0f \u5b50\u5c31\u8981\u8bf7\u5999\u5ae6\u4f84\u5973\u770b\u5b88\u4e86\u3002\u4e8b\u60c5\u662f\u5bb9\u6613\u7684\uff0c\u6211\u5c31\u6015\u5979\u3001\u6015\u5979\u6709\u5979\u81ea\u5df1\u7684\u60f3\u6cd5\uff0c\u4e07\u4e00\u628a\u8fd9\u5c0f\u5b50 \u653e\u4e86\uff0c\u5c31\u5f88\u53ef\u80fd\u574f\u4e86\u54b1\u4eec\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u6211\u4f1a\u90d1\u91cd\u544a\u8beb\u5979\u7684\u3002\u4f60\u653e\u5fc3\uff0c\u5979\u542c\u4e86\u4e00\u4e9b\u6751\u592b\u91ce\u8001\u4e4b\u8a00\uff0c\u6709\u65f6\u867d\u7136\u4f1a\u548c\u6211 \u9a73\u9a73\u5634\uff0c\u4f46\u6211\u7684\u8bdd\uff0c\u5979\u8fd8\u662f\u542c\u7684\u3002\u201d \u3000\u3000\u5c31\u5728\u4ed6\u4eec\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u5c01\u5999\u5ae6\u5374\u5e76\u6ca1\u6709\u542c\u7236\u4eb2\u7684\u8bdd\u56de\u623f\u6b47\u606f\uff0c\u800c\u662f\u5077\u5077\u7684\u53bb\u770b\u6587\u80dc\u4e2d\u5e72 \u4e9b\u4ec0\u4e48\u3002\u4e0d\u77e5\u600e\u7684\uff0c\u5979\u4e0e\u6587\u80dc\u4e2d\u76f8\u5904\u6570\u6708\u3002\u5979\u5bf9\u6587\u80dc\u4e2d\u7684\u6b66\u529f\u5341\u5206\u4f69\u670d\uff0c\u4f46\u4e24\u4eba\u95f4\u5374\u603b\u4f3c\u6709 \u4e9b\u4ec0\u4e48\u4e1c\u897f\u76f8\u9694\uff0c\u8fd9\u4e2a\u201c\u4e1c\u897f\u201d\u662f\u4ec0\u4e48\u5462\uff1f\u5979\u8bf4\u4e0d\u4e0a\u6765\u3002\u76f4\u5230\u4eca\u5929\uff0c\u5c06\u4ed6\u4e0e\u79e6\u5143\u6d69\u4f5c\u4e86\u4e00\u4e2a \u5bf9\u6bd4\u4e4b\u540e\uff0c\u5979\u624d\u9690\u9690\u611f\u5230\u6587\u80dc\u4e2d\u4f3c\u4e4e\u7f3a\u5c11\u4e00\u4e2a\u201c\u4fa0\u201d\u5b57\uff0c\u4e0e\u5979\u7406\u60f3\u4e2d\u7684\u201c\u4fa0\u58eb\u201d\u76f8\u5dee\u751a\u8fdc\uff01 \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u79e6\u5143\u6d69\u867d\u7136\u53ea\u662f\u4e0e\u5979\u7b2c\u4e00\u6b21\u89c1\u9762\uff0c\u5979\u5bf9\u79e6\u5143\u6d69\u5374\u9887\u6709\u597d\u611f\u3002\u79e6\u5143\u6d69\u662f\u5426\u80fd\u5f53 \u4e00\u4e2a\u201c\u4fa0\u58eb\u201d\u7684\u79f0\u53f7\uff0c\u5979\u4e0d\u77e5\u9053\uff0c\u4f46\u770b\u4ed6\u4eca\u5929\u7684\u8a00\u8c08\u4e3e\u6b62\uff0c\u5374\u662f\u4e2a\u5149\u660e\u78ca\u843d\uff0c\u6709\u80f8\u895f\u6709\u6c14\u5ea6 \u7684\u7537\u5b50\u3002\u800c\u6587\u80dc\u4e2d\u7f3a\u5c11\u7684\u5c31\u6b63\u662f\u8fd9\u4e9b\u201c\u4e1c\u897f\u201d\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u6084\u6084\u53bb\u770b\u6587\u80dc\u4e2d\uff0c\u5728\u5979\u5185\u5fc3\u6df1\u5904\u5176\u5b9e\u4e0d\u662f\u4e3a\u4e86\u53bb\u770b\u6587\u80dc\u4e2d\uff0c\u800c\u662f\u6050\u9632\u6587\u80dc\u4e2d\u4f1a\u628a \u79e6\u5143\u6d69\u6740\u5bb3\u7684\u3002\u5979\u5bf9\u79e6\u5143\u6d69\u7684\u65e0\u8f9c\u53d7\u7d2f\uff0c\u751a\u611f\u540c\u60c5\uff0c\u4e5f\u5927\u611f\u4e0d\u5b89\uff0c\u867d\u7136\u5979\u5e76\u6ca1\u6709\u53c2\u9884\u7236\u4eb2\u4e0e \u6587\u5bb6\u7236\u5b50\u4ed6\u4eec\u7684\u9634\u8c0b\u8be1\u8ba1\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u5728\u623f\u91cc\u628a\u79e6\u5143\u6d69\u7684\u8863\u88f3\u6362\u4e0a\u4e4b\u540e\uff0c\u60f3\u8d77\u56ed\u4e2d\u6bd4\u5251\u4e4b\u4e8b\uff0c\u60f3\u8d77\u4e86\u5c01\u5999\u5ae6\u79f0\u8d5e\u79e6\u5143\u6d69 \u5251\u6cd5\u7684\u90a3\u4e9b\u8bf4\u8bdd\uff0c\u8d8a\u60f3\u8d8a\u662f\u751f\u6c14\u3002\u62d4\u51fa\u5251\u6765\uff0c\u6307\u7740\u79e6\u5143\u6d69\u7684\u54bd\u5589\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53ef\u60dc\u5c01\u4f2f\u4f2f \u4e0d\u80af\u542c\u6211\u7684\u8bf4\u8bdd\uff0c\u5426\u5219\u4e00\u5251\u628a\u4ed6\u6740\u4e86\uff0c\u591a\u597d\uff01\u54fc\uff0c\u4f46\u5982\u4eca\u4ed6\u843d\u5728\u6211\u7684\u624b\u91cc\uff0c\u6211\u4e0d\u6740\u4ed6\uff0c\u4e5f\u8fd8 \u6709\u529e\u6cd5\u6574\u6cbb\u4ed6\u7684\uff0c\u6211\u8fd9\u4e00\u5251\u7a7f\u8fc7\u4ed6\u7684\u7435\u7436\u9aa8\uff0c\u5c31\u628a\u4ed6\u7684\u6b66\u529f\u5e9f\u4e86\u3002\u53cd\u6b63\u548c\u6b66\u5f53\u6d3e\u7684\u4ec7\u662f\u7ed3\u5b9a \u4e86\uff0c\u5c01\u4f2f\u4f2f\u987e\u8651\u7684\u53ea\u662f\u7ed3\u5f97\u592a\u6df1\u800c\u5df2\uff0c\u73b0\u5728\u6211\u4e0d\u6740\u5979\uff0c\u53ea\u5e9f\u4ed6\u7684\u6b66\u529f\uff0c\u6b66\u5f53\u6d3e\u5174\u5e08\u95ee\u7f6a\uff0c\u6709 \u6211\u7239\u7239\u62b5\u6321\u3002\u60f3\u6765\u5c01\u4f2f\u4f2f\u4e5f\u4e0d\u6562\u600e\u6837\u602a\u8d23\u6211\u7684\u3002\u6211\u4e0d\u662f\u4f9d\u4ed6\u4e4b\u8a00\u4fdd\u5168\u4e86\u8fd9\u5c0f\u5b50\u7684\u6027\u547d\u5417\uff1f\u201d \u3000\u3000\u6587\u80dc\u4e2d\u62ff\u5251\u6307\u7740\u79e6\u5143\u6d69\uff0c\u60f3\u662f\u8fd9\u6837\u60f3\uff0c\u4f46\u4e00\u65f6\u95f4\u8fd8\u4e0d\u6562\u4e0b\u624b\u3002\u5f85\u5230\u4ed6\u628a\u5fc3\u4e00\u6a2a\uff0c\u6b63\u8981\u4e0d \u987e\u540e\u679c\u5c31\u523a\u7a7f\u79e6\u5143\u6d69\u7684\u7435\u7436\u9aa8\u7684\u65f6\u5019\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u80dc\u4e2d\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u6765\u5f97\u6b63\u662f\u5408\u65f6\uff0c\u4e00\u58f0\u559d\u6b62\u4e86\u4ed6\u3002\u6587\u80dc\u4e2d\u56de\u8fc7\u5934\u6765\uff0c\u5c34\u5c2c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u3002\u6211\u51e0 \u4e4e\u7ed9\u4f60\u5413\u4e86\u4e00\u8df3\u3002\u201d \u3000\u3000\u6587\u80dc\u4e2d\u8fd9\u4e00\u56de\u5934\uff0c\u5c01\u5999\u5ae6\u4e0d\u7981\u53c8\u662f\u5927\u5403\u4e00\u60ca\u3002\u539f\u6765\u6587\u80dc\u4e2d\u6362\u4e86\u79e6\u5143\u6d69\u7684\u8863\u88f3\uff0c\u6b64\u65f6\u4ed6\u5df2 \u662f\u6253\u626e\u5f97\u548c\u79e6\u5143\u6d69\u4e00\u6a21\u4e00\u6837\uff0c\u8fde\u9762\u8c8c\u4e5f\u6709\u4e03\u516b\u5206\u76f8\u4f3c\u3002\u5c01\u5999\u5ae6\u9aa4\u7136\u4e00\u770b\uff0c\u51e0\u4e4e\u4ee5\u4e3a\u662f\u79e6\u5143\u6d69 \u5728\u620f\u5f04\u4ed6\u3002\u4f46\u770b\u4e00\u770b\uff0c\u7095\u4e0a\u5206\u660e\u53c8\u8eba\u7740\u4e00\u4e2a\u79e6\u5143\u6d69\u3002\u800c\u4e14\u6587\u80dc\u4e2d\u7684\u58f0\u97f3\u4e5f\u8bf4\u660e\u4e86\u4ed6\u5e76\u4e0d\u662f\u79e6 \u5143\u6d69\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6211\u624d\u662f\u7ed9\u4f60\u5413\u4e86\u4e00\u8df3\u5462\uff01\u4f60\u4e3a\u4ec0\u4e48\u8981\u6740\u4ed6\uff0c\u53c8\u4e3a\u4ec0\u4e48\u8981\u626e\u6210\u4ed6\u7684\u6a21\u6837\uff1f\u201d \u3000\u3000\u6587\u80dc\u4e2d\u7b11\u9053\uff1a\u201c\u6211\u54ea\u91cc\u662f\u771f\u8981\u6740\u4ed6\uff1f\u4e0d\u8fc7\u56e0\u4e3a\u4f60\u521a\u624d\u8d5e\u4ed6\uff0c\u6211\u5fc3\u91cc\u4e0d\u8212\u670d\uff0c\u77e5\u9053\u4f60\u6765 \u4e86\uff0c\u6709\u610f\u5413\u5413\u4f60\u7684\u3002\u563f\uff0c\u563f\uff0c\u4f60\u8bf4\uff0c\u4f60\u662f\u4e0d\u662f\u770b\u4e0a\u4e86\u8fd9\u4e2a\u5c0f\u5b50\uff1f\u82e5\u7136\u771f\u662f\uff0c\u6211\u53ef\u5c31\u8981\u5f53\u771f\u7684 \u6740\u6389\u4ed6\u4e86\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u770b\u4e0a\u4ec0\u4e48\u4eba\u4e86\uff1f\u6211\u4ec0\u4e48\u4eba\u4e5f\u770b\u4e0d\u4e0a\uff01\u201d\u63a5\u7740\u534a \u4fe1\u534a\u7591\u5730\u95ee\u9053\uff1a\u201c\u4f60\u771f\u7684\u662f\u4e0d\u60f3\u6740\u4ed6\uff1f\u4f60\u600e\u4e48\u77e5\u9053\u662f\u6211\u6765\u4e86\u3002\u4f60\u80cc\u540e\u53c8\u6ca1\u957f\u773c\u775b\u3002\u201d\u5c01\u5999\u5ae6 \u662f\u6084\u6084\u5730\u8d70\u6765\u7684\uff0c\u5f53\u65f6\u6587\u8138\u4e2d\u53c8\u5728\u5168\u795e\u8d2f\u6ce8\u5730\u62ff\u5251\u76ef\u7740\u79e6\u5143\u6d69\u3002\u5c01\u5999\u5ae6\u4e0d\u76f8\u4fe1\u6587\u80dc\u4e2d\u65e9\u5df2\u53d1 \u89c9\u4e86\u5979\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u6709\u542c\u98ce\u8fa8\u5668\u4e4b\u80fd\uff0c\u4f55\u987b\u56de\u5934\u5f20\u671b\uff1f\u201d\u201c\u542c\u98ce\u8fa8\u5668\u201d\u662f\u63a5\u6697\u5668\u7684\u4e00 \u79cd\u4e0a\u4e58\u529f\u592b\uff0c\u53ea\u8981\u4e00\u542c\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u5c31\u53ef\u4ee5\u5224\u65ad\u654c\u4eba\u53d1\u7684\u662f\u54ea\u79cd\u6697\u5668\uff0c\u6253\u7684\u662f\u54ea\u4e2a\u65b9\u5411\u3001 \u90e8\u4f4d\u3002\u6709\u4e9b\u6697\u5668\u662f\u5f88\u5c0f\u7684\uff0c\u4f8b\u5982\u6885\u82b1\u9488\u4e4b\u7c7b\uff0c\u53d1\u5c04\u51fa\u6765\uff0c\u51e0\u4e4e\u4e0d\u5e26\u98ce\u58f0\uff0c\u4f46\u6b66\u5b66\u7684\u5927\u884c\u5bb6\u4e00 \u6837\u53ef\u4ee5\u5206\u8fa8\u3002 \u3000\u3000\u6709\u201c\u542c\u98ce\u8fa8\u5668\u201d\u7684\u672c\u9886\u7684\u4eba\uff0c\u80fd\u591f\u5bdf\u89c9\u80cc\u540e\u6709\u4eba\u8d70\u6765\uff0c\u81ea\u7136\u4e0d\u662f\u5947\u4e8b\uff0c\u867d\u7136\u5c01\u5999\u5ae6\u5df2\u662f \u4f7f\u7528\u8f7b\u8eab\u672c\u9886\uff0c\u6084\u6084\u8d70\u6765\u7684\u3002\u5c01\u5999\u5ae6\u5fc3\u60f3\uff1a\u201c\u6216\u8bb8\u4ed6\u662f\u771f\u7684\u6709\u8fd9\u4e2a\u672c\u9886\uff0c\u542c\u89c1\u6211\u7684\u811a\u6b65\u58f0 \u4e86\u3002\u201d\u5f53\u4e0b\u4e0d\u518d\u60f3\u8fd9\u4e2a\u7591\u70b9\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u5c31\u7b97\u4f60\u662f\u4e3a\u4e86\u5413\u6211\uff0c\u4e0d\u662f\u771f\u7684\u8981\u6740\u4ed6\u7684\u3002\u7136 \u5219\uff0c\u4f60\u53c8\u4e3a\u4ec0\u4e48\u8981\u626e\u6210\u4ed6\u7684\u6a21\u6837\uff1f\u201d \u3000\u3000\u6587\u80dc\u4e2d\u7b11\u9053\uff1a\u201c\u626e\u5f97\u50cf\u4e0d\u50cf\uff0c\u4f60\u5148\u8bf4\u8bf4\u3002\u201d\u5c01\u5999\u5ae6\u7b11\u9053\uff1a\u201c\u9664\u4e86\u58f0\u97f3\uff0c\u7b80\u76f4\u5c31\u50cf\u4ed6\u7684\u540c \u80de\u5144\u5f1f\u3002\u4f60\u662f\u6253\u7b97\u5192\u5145\u4ed6\u5427\uff1f\u4e3a\u4ec0\u4e48\uff1f\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6587\u80dc\u4e2d\u5472\u7259\u54a7\u5634\u7684\u683c\u683c\u4e00\u7b11\uff0c\u5ffd\u5730\u5bf9\u5c01\u5999\u5ae6\u4f5c\u4e86\u4e2a\u63d6\uff0c\u634f\u7740\u55d3\u5b50\u8bf4\u9053\uff1a\u201c\u5c01\u59d1\u5a18\uff0c\u4f60\u8fd9 \u676f\u9152\u6211\u5fc3\u9886\u4e86\u3002\u201d\u8fd9\u4e00\u6b21\u8fde\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u8bf4\u8bdd\u7684\u795e\u6001\u90fd\u4f3c\u8db3\u4e86\u79e6\u5143\u6d69\u4e86\u3002\u8fd9\u4e00\u53e5\u8bdd\u5c31\u662f\u521a\u624d \u79e6\u5143\u6d69\u5728\u5e2d\u4e0a\u5411\u5c01\u5999\u5ae6\u8bf4\u8fc7\u7684\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u8bf4\u9053\uff1a\u201c\u6211\u7684\u53e3\u6280\u4e5f\u4e0d\u9519\u5427\uff1f\u201d\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6cb9\u5634\u6ed1\u820c\uff0c\u4f60\u8fd8\u6ca1\u6709\u56de\u7b54\u6211\u7684\u95ee\u9898 \u5462\uff1f\u201d \u3000\u3000\u6587\u80dc\u4e2d\u9053\uff1a\u201c\u4f60\u731c\u5f97\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u8981\u5192\u5145\u4ed6\u3002\u4f60\u8981\u77e5\u9053\u8fd9\u4e2a\u539f\u56e0\uff0c\u95ee\u4f60\u7239\u7239\u53bb\u3002\u201d\u5c01\u5999 \u5ae6\u8be7\u9053\uff1a\u201c\u662f\u6211\u7239\u7239\u53eb\u4f60\u5982\u6b64\u505a\u7684\uff0c\u6211\u4e0d\u76f8\u4fe1\uff01\u201d \u3000\u3000\u6587\u80dc\u4e2d\u9053\uff1a\u201c\u522b\u7684\u53ef\u4ee5\u9a97\u4f60\uff0c\u8fd9\u4ef6\u4e8b\u5982\u4f55\u53ef\u4ee5\u9a97\u4f60\uff1f\u4f60\u4e0d\u76f8\u4fe1\uff0c\u9a6c\u4e0a\u5c31\u53ef\u4ee5\u95ee\u4f60\u7239\u7239\u3002 \u597d\u5427\uff0c\u54b1\u4eec\u73b0\u5728\u51fa\u53bb\u5427\u3002\u6211\u548c\u4f60\u4e00\u540c\u51fa\u53bb\uff0c\u4e5f\u7701\u5f97\u4f60\u8001\u662f\u63d0\u5fc3\u540a\u80c6\uff0c\u5bb3\u6015\u6211\u4f1a\u5bb3\u4e86\u4f60\u7684 \u4ed6\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u60f6\u60d1\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u505a\u4eba\u5e94\u5f53\u5149\u660e\u78ca\u843d\uff0c\u5192\u5145\u522b\u4eba\uff0c\u8fd9\u7b97\u4ec0\u4e48\uff1f \u6211\u7684\u7239\u7239\u4e3a\u4ec0\u4e48\u8981\u6559\u4ed6\u505a\u51fa\u8fd9\u79cd\u9b3c\u9b3c\u795f\u795f\u7684\u4e8b\u60c5\uff1f\u201d\u6b63\u56e0\u4e3a\u5979\u5fc3\u4e2d\u60f6\u60d1\uff0c\u6240\u4ee5\u5bf9\u6587\u80dc\u4e2d\u7684\u8ba5 \u8bee\uff0c\u5979\u5df2\u7ecf\u662f\u6beb\u4e0d\u5728\u610f\u4e86\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5934\u75db\u5f97\u5f88\u96be\u53d7\uff0c\u6211\u8fd8\u662f\u5148\u56de\u623f\u6b47\u6b47\u3002\u660e\u65e9\u518d\u95ee\u7239\u7239\u5427\u3002 \u597d\uff0c\u6211\u76f8\u4fe1\u4f60\u7684\u8bdd\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u6587\u80dc\u4e2d\u5fc3\u60f3\uff1a\u201c\u8c05\u5979\u4e0d\u6562\u6363\u9b3c\u3002\u201d\u4e8e\u662f\u5c31\u548c\u5979\u8d70\u51fa\u623f\u95f4\uff0c\u9501\u4e0a\u4e86\u623f\u95e8\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u597d\uff0c \u4f60\u5148\u6b47\u6b47\u3002\u56de\u5934\u6211\u8fd8\u6709\u8bdd\u8981\u548c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u89c1\u4e86\u5316\u88c5\u540e\u7684\u6587\u80dc\u4e2d\uff0c\u8fde\u58f0\u8d5e\u5999\u3002\u6587\u9053\u5e84\u5219\u6307\u51fa\u4ed6\u7684\u51e0\u5904\u5c0f\u7834\u7efd\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7ec3 \u4e60\u597d\u4e86\uff0c\u54b1\u4eec\u660e\u5929\u5c31\u52a8\u8eab\u3002\u201d\u5f53\u4e0b\u628a\u8be6\u7ec6\u7684\u8ba1\u5212\u544a\u8bc9\u4e86\u513f\u5b50\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u542c\u8bf4\u662f\u8981\u628a\u5c01\u5999\u5ae6\u7559\u4e0b\uff0c\u8ba9\u5979\u8d1f\u8d77\u770b\u5b88\u79e6\u5143\u6d69\u4e4b\u8d23\uff0c\u4e0d\u89c9\u5fc3\u6709\u6240\u89e6\uff0c\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u7709\u5934\u4e00\u76b1\uff0c\u8bf4\u900d\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u4e5f\u653e\u5fc3\u4e0d\u4e0b\u4ed6\u4eec\uff1f\u201d\u539f\u6765\u5c01\u5b50\u8d85\u65e9\u5df2\u6709\u5fc3\u201c\u9ad8 \u6500\u201d\uff0c\u60f3\u4e0e\u6587\u9053\u5e84\u7ed3\u6210\u513f\u5973\u4eb2\u5bb6\u7684\u3002\u4ed6\u89c1\u8fd9\u201c\u4e24\u5c0f\u53e3\u5b50\u201d\u5f62\u5f71\u4e0d\u79bb\uff0c\u65e5\u76ca\u4eb2\u8fd1\uff0c\u5fc3\u91cc\u597d\u751f\u6b22 \u559c\uff0c\u53ea\u4ee5\u4e3a\u4ed6\u4eec\u65e9\u5df2\u662f\u60c5\u6295\u610f\u5408\uff0c\u8bf4\u4e0d\u5b9a\u65e0\u987b\u5bb6\u957f\u5f00\u53e3\uff0c\u4ed6\u4eec\u5df2\u662f\u79c1\u8ba2\u7ec8\u8eab\u7684\u4e86\u3002\u6b63\u56e0\u5982 \u6b64\uff0c\u4ed6\u8ba4\u4e3a\u6587\u80dc\u4e2d\u662f\u5e94\u8be5\u653e\u5fc3\u5f97\u4e0b\u4ed6\u7684\u5973\u513f\u7684\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u6709\u82e6\u8bf4\u4e0d\u51fa\u6765\uff0c\u534a\u664c\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u5ae6\u59b9\u6211\u5f53\u7136\u662f\u653e\u5fc3\u5f97\u4e0b\u7684\u3002\u4e0d\u8fc7\u5979\u81f3\u4eca\u672a\u660e \u771f\u76f8\uff0c\u5bf9\u8fd9\u59d3\u79e6\u7684\u5c0f\u5b50\uff0c\u4f3c\u4e4e\u6709\u70b9\u601c\u60af\u4e4b\u60c5\uff0c\u89c9\u5f97\u4ed6\u662f\u65e0\u8f9c\u53d7\u7d2f\uff0c\u6211\u4ee5\u4e3a\u8fd8\u662f\u4e0d\u5fc5\u7792\u5979\u7684 \u597d\u3002\u5979\u77e5\u9053\u8fd9\u662f\u5173\u7cfb\u5c01\u8001\u4f2f\u62a5\u4ec7\u7684\u5927\u4e8b\uff0c\u5979\u5c31\u4f1a\u5c3d\u5fc3\u5c3d\u529b\u548c\u54b1\u4eec\u4e00\u540c\u5e72\u4e86\u3002\u201d\u5f53\u7136\u8fd9\u53ea\u662f\u6587 \u80dc\u4e2d\u7684\u60f3\u6cd5\uff0c\u4ed6\u662f\u8ba4\u4e3a\u5c01\u5999\u5ae6\u662f\u4e2a\u5b5d\u5973\u7684\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u597d\u7684\uff0c\u6211\u73b0\u5728\u5c31\u548c\u5979\u8bf4\u53bb\u3002\u201d\u5c01\u5999\u5ae6\u7684\u623f\u95f4\u662f\u5728\u6700\u540e\u4e00\u8fdb\uff0c\u5230\u5979\u7684\u623f\u95f4\u5148 \u8981\u7ecf\u8fc7\u6587\u80dc\u4e2d\u6240\u4f4f\u7684\u90a3\u4e00\u95f4\u3002\u4ed6\u4eec\u4e09\u4eba\u4e00\u540c\u8d70\u53bb\uff0c\u7ecf\u8fc7\u6587\u80dc\u4e2d\u90a3\u95f4\u5367\u623f\u7684\u65f6\u5019\uff0c\u6587\u9053\u5e84\u5ffd\u5730 \u5982\u6709\u6240\u7591\uff0c\u539f\u6765\u9189\u5012\u7684\u4eba\u547c\u5438\u91cd\u6d4a\uff0c\u4ee5\u6587\u9053\u5e84\u7684\u6b66\u5b66\u9020\u8be3\uff0c\u8033\u806a\u76ee\u660e\u8fdc\u80dc\u5e38\u4eba\uff0c\u7ecf\u8fc7\u8fd9\u95f4\u623f \u95f4\uff0c\u662f\u5e94\u8be5\u542c\u5f97\u5230\u91cc\u9762\u7684\u547c\u5438\u6c14\u606f\u7684\uff0c\u4f46\u73b0\u5728\u5374\u662f\u9759\u6084\u6084\u7684\uff0c\u8fde\u4e00\u70b9\u8f7b\u5fae\u7684\u58f0\u606f\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e0d\u77e5\u600e\u4e48\u6837\u4e86\uff0c\u54b1\u4eec\u770b\u4e00\u770b\u4ed6\u3002\u201d\u5c01\u5b50\u8d85\u7b11\u9053\uff1a\u201c\u60f3\u6765\u8fd8\u4e0d\u662f\u70c2\u9189 \u5982\u6ce5\uff1f\u6587\u4e16\u5144\u82e5\u662f\u5acc\u4ed6\u7684\u9152\u6c14\uff0c\u53ef\u4ee5\u79fb\u5230\u6211\u7684\u623f\u95f4\u53bb\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u4ee5\u4e3a\u79e6\u5143\u6d69\u5b9a\u662f\u70c2\u9189\u5982\u6ce5\uff0c\u4e0d\u6599\u5f00\u4e86\u623f\u95e8\u4e00\u770b\uff0c\u53ea\u89c1\u7a97\u95e8\u6253\u5f00\uff0c\u7a7a\u6c14\u4e2d\u8fd8\u8361\u6f3e\u7740 \u9152\u9999\uff0c\u79e6\u5143\u6d69\u5374\u5df2\u662f\u4e0d\u89c1\u4e86\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u4e09\u4eba\u90fd\u60ca\u5f97\u5446\u4e86\u3002 \u3000\u3000\u6587\u9053\u5e84\u9a6c\u4e0a\u8dc3\u51fa\u623f\u95f4\uff0c\u8dd1\u5230\u82b1\u56ed\u4e2d\u7684\u5047\u5c71\u9ad8\u5904\u5f20\u76ee\u56db\u671b\uff0c\u4f46\u89c1\u661f\u6cb3\u803f\u803f\u660e\u6708\u5728\u5929\uff0c\u5374\u54ea \u91cc\u6709\u4eba\u7684\u5f71\u5b50\uff1f\u6587\u9053\u5e84\u56de\u5230\u623f\u95f4\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u770b\u8fd8\u662f\u95ee\u95ee\u4ee4\u5a9b\u53bb\u5427\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u53c8\u60ca\u53c8\u6012\uff0c\u8bf4\u9053\uff1a\u201c\u82e5\u662f\u8fd9\u4e2b\u5934\u653e\u7684\uff0c\u6211\u5c31\u4e00\u638c\u51fb\u6bd9\u4e86\u5979\u3002\u201d\u6587\u9053\u5e84\u9053\uff1a\u201c\u5927\u54e5 \u4e5f\u4e0d\u7528\u5982\u6b64\u706b\u6c14\uff0c\u5148\u95ee\u4e2a\u6e05\u695a\u518d\u8bf4\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u6572\u95e8\u9053\uff1a\u201c\u5ae6\u513f\uff0c\u4f60\u5728\u91cc\u9762\u505a\u4ec0\u4e48\uff0c\u5feb\u5feb\u51fa\u6765\uff01\u201d\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6211\u5934\u75db\u5f97\u5389\u5bb3\uff0c \u5df2\u7ecf\u7761\u4e86\uff01\u201d\u5c01\u5b50\u8d85\u559d\u9053\uff1a\u201c\u51fa\u6765\uff01\u201d \u3000\u3000\u53ea\u542c\u5f97\u5c01\u5999\u5ae6\u4e0b\u5e8a\u7684\u811a\u6b65\u58f0\uff0c\u6089\u6089\u7d22\u7d22\u7684\u7a7f\u8863\u88f3\u7684\u58f0\u97f3\uff0c\u597d\u4e00\u4f1a\u5b50\uff0c\u5c01\u5999\u5ae6\u624d\u7761\u773c\u60fa\u677e \u5730\u6253\u5f00\u4e86\u623f\u95e8\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4e09\u66f4\u534a\u591c\uff0c\u4f60\u6709\u4ec0\u4e48\u7d27\u8981\u7684\u4e8b\u60c5\uff0c\u8981\u548c\u6211\u8bf4\uff1f\u201d \u3000\u3000\u4e09\u4eba\u6e38\u76ee\u56db\u987e\uff0c\u623f\u95e8\u91cc\u9664\u4e86\u5c01\u5999\u5ae6\u4e4b\u5916\uff0c\u54ea\u91cc\u8fd8\u6709\u4ed6\u4eba\uff0c\u5c01\u5b50\u8d85\u5389\u58f0\u95ee\u9053\uff1a\u201c\u59d3\u79e6\u7684\u8fd9 \u5c0f\u5b50\u5462\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u5446\u4e86\u4e00\u5446\uff0c\u84e6\u5730\u53d8\u4e86\u9762\u8272\uff0c\u54fd\u54bd\u7740\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u79e6\u5143\u6d69\u4e0d \u662f\u7ed9\u4f60\u5f04\u9189\u7684\u4e48\uff1f\u4f60\u8981\u627e\u4ed6\uff0c\u5e94\u8be5\u5230\u6587\u5927\u54e5\u7684\u623f\u95f4\u53bb\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u6012\u9053\uff1a\u201c\u5728\u7239\u7239\u9762\u524d\uff0c\u4f60\u522b\u88c5\u849c\uff0c\u7ed9\u6211\u8bf4\u8001\u5b9e\u8bdd\uff01\u59d3\u79e6\u90a3\u5c0f\u5b50\u662f\u4e0d\u662f\u4f60\u5077\u5077\u628a\u4ed6 \u653e\u4e86\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u53c8\u6c14\u53c8\u6025\uff0c\u53ef\u662f\u542c\u5f97\u79e6\u5143\u6d69\u8dd1\u6389\uff0c\u5fc3\u4e2d\u53c8\u6709\u83ab\u540d\u5947\u5999\u7684\u5feb\u610f\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u7239 \u7239\uff0c\u4f60\u4e5f\u4e0d\u60f3\u4e00\u60f3\uff0c\u90a3\u4f4d\u79e6\u5c11\u4fa0\u662f\u559d\u4e86\u4f60\u7684\u5343\u65e5\u9189\u7684\uff0c\u5973\u513f\u5c31\u662f\u653e\u4ed6\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u81ea\u5df1\u8dd1\u6389\u3002 \u96be\u9053\u5973\u513f\u8fd8\u80fd\u80cc\u4ed6\u51fa\u53bb\uff0c\u5c06\u4ed6\u85cf\u8d77\u6765\u5417\uff1f\u5373\u4f7f\u5973\u513f\u8981\u8fd9\u6837\u505a\uff0c\u4e5f\u7edd\u4e0d\u80fd\u8fd9\u6837\u5feb\u5c31\u56de\u6765\u5440\u3002\u545c \u54c7\uff01\u7239\u7239\uff0c\u539f\u6765\u4f60\u5e73\u65e5\u75bc\u6211\u90fd\u662f\u5047\u7684\uff0c\u4f60\u8fd9\u6837\u51a4\u8d56\u6211\uff0c\u53eb\u5973\u513f\u600e\u4e48\u505a\u4eba\uff1f\u201d\u8bf4\u7740\u8bf4\u7740\uff0c\u5c31\u54ed \u4e86\u8d77\u6765\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u4e00\u542c\uff0c\u5973\u513f\u8bf4\u7684\u786e\u662f\u5f88\u6709\u9053\u7406\uff0c\u5fc3\u91cc\u60f3\u9053\u3002\u201d\u4e0d\u9519\uff0c\u82e5\u662f\u5ae6\u513f\u5c06\u4ed6\u80cc\u51fa\u53bb\uff0c\u83ab\u8bf4 \u4e0d\u80fd\u8fd9\u6837\u5feb\u56de\u6765\uff0c\u51ed\u5979\u90a3\u70b9\u8f7b\u529f\uff0c\u6211\u4e5f\u4f1a\u542c\u5f97\u51fa\u5979\u7684\u811a\u6b65\u58f0\u7684\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u597d\u4e86\uff0c\u597d \u4e86\u3002\u7b97\u4e3a\u7236\u7684\u4e0d\u662f\uff0c\u4f60\u522b\u54ed\u4e86\u3002\u597d\u5728\u6587\u53d4\u53d4\u548c\u4f60\u7684\u6587\u5927\u54e5\u90fd\u4e0d\u662f\u5916\u4eba\uff0c\u4f60\u4e5f\u4e0d\u7528\u62c5\u5fc3\u7ed9\u4eba\u7b11 \u8bdd\u3002\u201d\u6587\u80dc\u4e2d\u6709\u5fc3\u5411\u5979\u8ba8\u597d\uff0c\u4e5f\u8fc7\u6765\u8d54\u4e86\u4e2a\u4e0d\u662f\uff0c\u8bf4\u9053\uff1a\u201c\u90fd\u602a\u6211\u770b\u7ba1\u4e0d\u4e25\uff0c\u8fde\u7d2f\u5ae6\u59b9\u53d7\u4e86 \u59d4\u5c48\u4e86\u3002\u201d\u5c01\u5999\u5ae6\u62bd\u62bd\u54bd\u54bd\uff0c\u7ed9\u4ed6\u4e00\u4e2a\u4e0d\u7406\u4e0d\u776c\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u597d\uff0c\u5f85\u6211\u4ed4\u7ec6\u7684\u518d\u67e5\u4e00\u67e5\u3002\u201d\u6587\u80dc\u4e2d\u6b63\u89c9\u8baa\u8baa\u7684\u4e0d\u597d\u610f\u601d\uff0c\u4e8e\u662f\u4e24\u7236\u5b50\u8ddf \u5728\u5c01\u5b50\u8d85\u7684\u540e\u9762\uff0c\u90fd\u8d70\u51fa\u4e86\u5c01\u5999\u5ae6\u7684\u623f\u95f4\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u5148\u5230\u5bc6\u5ba4\u91cc\u67e5\u770b\u201c\u5343\u65e5\u9189\u201d\u7684\u89e3\u836f\uff0c\u53ea\u89c1\u89e3\u836f\u539f\u5c01\u4e0d\u52a8\uff0c\u4e00\u9897\u90fd\u6ca1\u6709\u5c11\u3002\u5c01\u5b50\u8d85 \u653e\u4e0b\u4e00\u91cd\u5fc3\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u6599\u60f3\u963f\u5ae6\u6ca1\u6709\u8fd9\u6837\u5927\u80c6\uff0c\u6562\u5077\u89e3\u836f\u3002\u597d\uff0c\u53ea\u8981\u89e3\u836f\u6ca1\u5931\uff0c\u8fd9\u5c0f \u5b50\u5c31\u81f3\u5c11\u8981\u9189\u4e2a\u516d\u65e5\u4e03\u591c\u3002\u54b1\u4eec\u867d\u4e0d\u6740\u4ed6\u706d\u53e3\uff0c\u4e5f\u7b49\u4e8e\u662f\u6740\u4e00\u822c\u3002\u5230\u4e86\u90a3\u65f6\uff0c\u54b1\u4eec\u65e9\u5df2\u53bb\u4e86 \u4e1c\u5e73\u53bf\u53c8\u56de\u6765\u4e86\u3002\u201d\u8bc1\u5b9e\u4e86\u4e0d\u662f\u5c01\u5999\u5ae6\u5c06\u79e6\u5143\u6d69\u653e\u8d70\u4e4b\u540e\uff0c\u6587\u9053\u5e84\u5374\u66f4\u662f\u5fe7\u5fc3\u5fe1\u4ef2\uff0c\u8bf4 \u9053\u3002\u201d\u5947\u602a\uff0c\u65e2\u7136\u8fd9\u5c0f\u5b50\u672a\u5f97\u89e3\u836f\uff0c\u4ed6\u5c31\u51b3\u4e0d\u662f\u81ea\u5df1\u5077\u8d70\u7684\u4e86\u3002\u201d\u6587\u80dc\u4e2d\u9053\uff1a\u201c\u8fd9\u8fd8\u7528\u8bf4\uff0c \u5f53\u7136\u662f\u5916\u4eba\u5c06\u4ed6\u6551\u8d70\u7684\u4e86\u3002\u201d\u6587\u9053\u5e84\u4e0e\u5c01\u5b50\u8d85\u9762\u9762\u76f8\u89d1\uff0c\u534a\u664c\u6587\u9053\u5e84\u8bf4\u9053\uff1a\u201c\u6211\u62c5\u5fc3\u7684\u5c31\u6b63 \u662f\u8fd9\u4e2a\u3002\u201d\u8981\u77e5\u5018\u82e5\u662f\u5916\u4eba\u5c06\u79e6\u5143\u6d69\u6551\u4e86\u51fa\u53bb\uff0c\u5219\u8fd9\u4eba\u7684\u672c\u9886\u5b9a\u975e\u4e00\u6d41\u9ad8\u624b\u83ab\u4e3a\uff0c\u5426\u5219\u7109\u80fd \u4efb\u4ed6\u7a7f\u5802\u5165\u5ba4\uff0c\u8fde\u6587\u9053\u5e84\u90fd\u6ca1\u5bdf\u89c9\u4e00\u70b9\u58f0\u606f\uff1f \u3000\u3000\u6587\u80dc\u4e2d\u9053\uff1a\u201c\u4ed6\u80cc\u4e86\u4e2a\u4eba\uff0c\u4e5f\u8bb8\u8dd1\u5f97\u672a\u8fdc\u3002\u201d\u4e8e\u662f\u6587\u5bb6\u7236\u5b50\u548c\u5c01\u5b50\u8d85\u90fd\u51fa\u53bb\u641c\u7d22\uff0c\u641c\u5230 \u4e86\u5341\u91cc\u4e4b\u5916\uff0c\u5140\u662f\u4e0d\u89c1\u4e00\u4e2a\u4eba\u5f71\u3002\u8fd9\u5f82\u5f95\u5c71\u7ef5\u4e98\u767e\u58c1\uff0c\u5c71\u9ad8\u6797\u5bc6\uff0c\u5f53\u7136\u4e0d\u80fd\u628a\u6574\u4e2a\u5c71\u90fd\u7ffb\u8fc7 \u6765\u3002\u6587\u9053\u4e2d\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5c01\u5927\u54e5\uff0c\u8fd9\u4eba\u7684\u529f\u592b\u53ea\u6015\u4e0d\u5728\u4f60\u6211\u4e4b\u4e0b\uff0c\u6050\u6015\u6b64\u65f6\u4ed6\u5df2\u51fa\u4e86 \u5f82\u5f95\u5c71\u4e86\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u90a3\u4e48\u4e1c\u5e73\u53bf\u54b1\u4eec\u662f\u53bb\u8fd8\u4e0d\u53bb\uff1f\u201d\u6587\u9053\u5e84\u54ac\u4e86\u54ac\u7259\u8bf4\u9053\u3002\u201d\u673a\u4f1a\u96be\u9022\uff0c\u54b1\u4eec \u8fd8\u662f\u6309\u7167\u8ba1\u5212\u884c\u4e8b\u3002\u201d\u5c01\u5b50\u8d85\u56e0\u4e3a\u51fa\u4e86\u8fd9\u4ef6\u610f\u5916\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u8138\u4e0a\u4e5f\u5c31\u4e0d\u514d\u6709\u4e86\u72b9 \u7591\u4e0d\u51b3\u7684\u795e\u8272\u3002 \u3000\u3000\u6587\u9053\u5e84\u5b89\u6170\u4ed6\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u8fc7\u8fd9\u5c0f\u5b50\u81f3\u5c11\u4e5f\u8981\u9189\u4e2a\u4e03\u65e5\u4e03\u591c\u5417\uff1f\u5373\u4f7f\u6709\u4eba\u5c06\u4ed6\u6551\u4e86\u51fa \u53bb\uff0c\u4e5f\u51b3\u4e0d\u80fd\u4ece\u4ed6\u7684\u53e3\u4e2d\u95ee\u51fa\u4ec0\u4e48\u8bdd\u6765\u3002\u53c8\u600e\u77e5\u9053\u54b1\u4eec\u7684\u5b89\u6392\uff1f\u5f88\u53ef\u80fd\u4ed6\u8fd8\u5f53\u5730\u662f\u771f\u7684\u9189 \u4e86\uff0c\u6b64\u65f6\u6b63\u5728\u7ed9\u4ed6\u89e3\u9152\u836f\u5462\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff0c\u201c\u8981\u662f\u6c5f\u6d77\u5929\u7684\u4eba\u5c06\u5730\u6551\u8d70\u7684\uff0c\u8fd9\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u6587\u9053\u5e84\u7b11\u9053\uff1a\u201c\u6c5f\u6d77\u5929\u53c8\u7109\u80fd\u672a\u535c\u5148\u77e5\uff0c\u6070\u6070\u77e5\u9053\u8fd9\u5c0f\u5b50\u4eca\u65e5\u4f1a\u95ef\u5230\u4f60\u7684\u5bb6\u91cc\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u7136\u5219\u4f60\u4ee5\u4e3a\u8fd9\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u6211\u600e\u4e48\u77e5\u9053\uff1f\u4e0d\u8fc7\u5373\u4f7f\u8fd9\u4eba\u4e5f\u662f\u8981\u5f80\u6c5f\u5bb6\uff0c\u90a3\u4e5f\u4e0d\u6253\u7d27\u3002\u4e00\u6765\u4ed6\u4e0d\u77e5\u9053\u54b1 \u4eec\u7684\u8ba1\u5212\uff0c\u7b49\u5f85\u4ed6\u660e\u767d\u4e86\u59d3\u79e6\u8fd9\u5c0f\u5b50\u5e76\u975e\u666e\u901a\u7684\u9152\u9189\uff0c\u4ed6\u4e00\u5b9a\u7591\u662f\u4e2d\u6bd2\uff0c\u975e\u5f97\u6025\u5fd9\u5c31\u8fd1\u7ed9\u4ed6 \u5ef7\u533b\u8bca\u6cbb\u4e0d\u53ef\uff0c\u4ed6\u8fd8\u6709\u5de5\u592b\u8d76\u5f80\u6c5f\u5bb6\u53bb\u5417\uff1f\u5c01\u5927\u54e5\uff0c\u6b32\u56fe\u5927\u4e8b\uff0c\u603b\u5f97\u6709\u51e0\u5206\u5192\u9669\u7684\u3002\u5c31\u7b97\u6709 \u51e0\u5206\u98ce\u9669\uff0c\u4f46\u8fd9\u662f\u54b1\u4eec\u4e1c\u5c71\u590d\u51fa\u7684\u6700\u597d\u65f6\u673a\uff0c\u4f60\u540e\u534a\u4e16\u7684\u8363\u534e\u5bcc\u8d35\u4e5f\u662f\u5168\u770b\u8fd9\u4e00\u56de\u4e86\uff0c\u4f60\u613f \u610f\u9519\u8fc7\u8fd9\u4e2a\u673a\u4f1a\u5417\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u672c\u6765\u6709\u70b9\u5bb3\u6015\uff0c\u4f46\u4ed6\u7684\u529f\u540d\u5229\u7984\u4e4b\u5fc3\u6781\u91cd\uff0c\u7ecf\u8fc7\u4e86\u6587\u9053\u5e84\u8fd9\u4e48\u4e00\u8bf4\uff0c\u80c6\u6c14\u590d\u58ee\uff0c \u8bf4\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u5c31\u8d4c\u5b83\u4e00\u8d4c\uff0c\u660e\u5929\u52a8\u8eab\u5f80\u4e1c\u5e73\u53bf\u53bb\u3002\u53ea\u662f\u5982\u4eca\u5df2\u7ecf\u65e0\u987b\u8d54\u4eba\u770b\u7ba1\u4eba\u8d28\u4e86\uff0c \u8981\u4e0d\u8981\u5e26\u5979\u540c\u53bb\uff1f\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u4f60\u4e5f\u5f97\u7559\u4e00\u4e2a\u4eba\u770b\u5bb6\uff0c\u4f84\u5973\u5c31\u7559\u4e0b\u6765\u5427\uff01\u6587\u9053\u5e84\u662f\u6015\u5c01\u5999\u5ae6\u4e0d\u77e5\u8f7b\u91cd\uff0c\u65b9 \u4e00\u5728\u6c5f\u5bb6\u8bf4\u9519\u4e86\u8bdd\uff0c\u5c82\u4e0d\u8bef\u4e86\u4ed6\u4eec\u7684\u201c\u5927\u4e8b\u201d\uff1f \u3000\u3000\u5c01\u5b50\u8d85\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u8ba9\u5979\u4e00\u4e2a\u4eba\u7559\u5728\u5bb6\u4e2d\uff0c\u6211\u4e5f\u6709\u70b9\u653e\u5fc3\u4e0d\u4e0b\u3002\u201d\u6587\u9053\u5e84\u61c2\u5f97\u4ed6 \u7684\u610f\u601d\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u662f\u6015\u90a3\u4e2a\u4eba\u518d\u6765\u6363\u4e71\u5417\uff1f\u8fd9\u4e2a\u5012\u53ef\u4ee5\u653e\u5fc3\uff0c\u82e5\u7136\u4ed6\u8981\u751f\u4e8b\uff0c\u6628\u665a\u5c31\u53ef \u4ee5\u751f\u4e8b\u4e86\u3002\u800c\u4e14\u4f3c\u8fd9\u6837\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u5c82\u80fd\u4e0d\u987e\u8eab\u4efd\uff1f\u5373\u4f7f\u4ed6\u518d\u5230\u4f60\u7684\u5e9c\u4e0a\uff0c\u60f3\u6765\u4e5f\u4e0d\u81f3\u4e8e\u548c \u4f84\u5973\u4e3a\u96be\u7684\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u5fc3\u60f3\u53cd\u6b63\u5230\u6c5f\u5bb6\u4e5f\u8981\u5192\u9669\uff0c\u5012\u4e0d\u5982\u8ba9\u5979\u7559\u5728\u5bb6\u4e2d\uff0c\u98ce\u9669\u53ef\u80fd\u8fd8\u4f1a\u5c11\u4e9b\uff0c\u4e8e\u662f\u5c31\u540c \u610f\u4e86\u6587\u9053\u5e84\u7684\u4e3b\u5f20\u3002\u4f46\u5c01\u5b50\u8d85\u51b3\u5b9a\u4e4b\u540e\uff0c\u5374\u53c8\u6015\u5973\u513f\u4e0d\u80af\u540c\u610f\u3002\u5973\u513f\u662f\u5e74\u8f7b\u4eba\u7684\u6027\u60c5\uff0c\u559c\u6b22 \u70ed\u95f9\u7684\uff0c\u5e73\u65f6\u90fd\u5e38\u5e38\u5435\u56b7\u8981\u4e0b\u5c71\u53bb\u73a9\uff0c\u8fd9\u6b21\u6709\u8fd9\u4e48\u597d\u7684\u673a\u4f1a\u5374\u53c8\u4e0d\u5e26\u5979\u540c\u53bb\u3002\u5979\u5fc3\u91cc\u4e00\u5b9a\u5f88 \u4e0d\u8212\u670d\uff0c\u5c24\u5176\u5728\u521a\u521a\u95f9\u8fc7\u4e86\u4e00\u573a\u4e4b\u540e\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u56de\u5230\u5bb6\u671b\uff0c\u672c\u6765\u51c6\u5907\u5c01\u5999\u5ae6\u8981\u548c\u4ed6\u5435\u95f9\u7684\uff0c\u54ea\u77e5\u4e00\u8bf4\u4e4b\u540e\uff0c\u5c01\u5999\u5ae6\u5374\u6de1\u6de1\u5730\u8bf4 \u9053\uff1a\u201c\u6211\u624d\u4e0d\u7a00\u7f55\u548c\u4f60\u4eec\u53bb\u5192\u5145\u6c5f\u5bb6\u7684\u8d3a\u5ba2\u5462\uff0c\u8ba9\u6211\u7559\u5728\u5bb6\u4e2d\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u201d\u4e0d\u4f46\u6ca1\u6709 \u5435\u95f9\uff0c\u542c\u5979\u7684\u8bed\u6c14\uff0c\u53cd\u800c\u662f\u6709\u51e0\u5206\u9ad8\u5174\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u8fd9\u4e00\u53cd\u5e38\u7684\u6001\u5ea6\uff0c\u5f15\u8d77\u4e86\u5c01\u5b50\u8d85\u7684\u7591\u5fc3\u3002\u6697\u81ea\u60f3\u9053\uff1a\u201c\u79e6\u5143\u6d69\u8fd9\u5c0f\u5b50\u83ab\u540d\u5176\u5999\u7684 \u5931\u4e86\u8e2a\uff0c\u83ab\u975e\u5979\u662f\u77e5\u60c5\u4e0d\u62a5\uff1f\u867d\u7136\u6551\u8fd9\u5c0f\u5b50\u7684\u4e0d\u662f\u5979\u3002\u201d\u4ed6\u6000\u7591\u5973\u513f\u7559\u5728\u5bb6\u4e2d\uff0c\u8bf4\u4e0d\u5b9a\u53e6\u6709 \u7528\u610f\uff0c\u4e0e\u79e6\u65e0\u6d69\u6709\u5173\u3002\u53ef\u662f\u4ed6\u4e00\u6765\u6beb\u65e0\u51ed\u636e\uff0c\u4e8c\u6765\u8981\u5973\u513f\u7559\u5728\u5bb6\u4e2d\u53c8\u662f\u4ed6\u7684\u4e3b\u610f\uff0c\u4ed6\u7eb5\u6709\u7591 \u5fc3\uff0c\u4e5f\u662f\u4e0d\u597d\u66f4\u6539\u4e86\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u4e0d\u597d\u66f4\u6539\u4e3b\u610f\u3002\u53ea\u5f97\u7559\u4e0b\u5973\u513f\u770b\u5bb6\uff0c\u81ea\u5df1\u5219\u8ddf\u7740\u6587\u9053\u5e84\u7236\u5b50\u524d\u5f80\u4e1c\u5e73\u53bf\u6c5f\u5bb6\u5192\u5145 \u8d3a\u5ba2\uff0c\u8ba1\u5212\u7ed1\u67b6\u6c5f\u6d77\u5927\u7684\u5973\u513f\u5973\u5a7f\u3002 \u3000\u3000\u5176\u5b9e\u5c01\u5b50\u8d85\u53ea\u731c\u4e2d\u4e86\u4e00\u534a\u3002\u79e6\u5143\u6d69\u7684\u786e\u4e0d\u662f\u5c01\u5999\u5ae6\u653e\u8d70\u7684\uff0c\u4f46\u4f55\u4eba\u6551\u4ed6\uff0c\u5c01\u5999\u5ae6\u5374\u5e76\u4e0d \u77e5\u60c5\u3002\u4e0d\u8fc7\u5979\u613f\u610f\u7559\u5728\u5bb6\u4e2d\uff0c\u5012\u662f\u6709\u4e00\u534a\u662f\u4e3a\u7740\u79e6\u5143\u6d69\uff0c\u5979\u5e0c\u671b\u53ef\u4ee5\u6709\u673a\u4f1a\u5355\u72ec\u89c1\u7740\u79e6\u5143 \u6d69\u3002\u53e6\u4e00\u534a\u539f\u56e0\u5219\u662f\u56e0\u4e3a\u5979\u8ba8\u538c\u6587\u80dc\u4e2d\uff0c\u4e0d\u613f\u548c\u4ed6\u540c\u5728\u4e00\u8d77\uff0c\u800c\u5b81\u613f\u5355\u72ec\u7559\u5728\u5bb6\u4e2d\u3002 \u3000\u3000\u201c\u662f\u4ec0\u4e48\u4eba\u5c06\u79e6\u5143\u6d69\u6551\u51fa\u53bb\u7684\u5462\uff1f\u4ed6\u6ca1\u6709\u89e3\u836f\uff0c\u8fd9\u4e03\u5929\u4e03\u591c\u79e6\u5143\u6d69\u6c89\u9189\u4e0d\u9192\uff0c\u4ed6\u600e\u4e48 \u529e\uff1f\u201d\u5c01\u5999\u5ae6\u5f88\u5e0c\u671b\u89c1\u7740\u79e6\u5143\u6d69\uff0c\u5012\u4e0d\u662f\u56e0\u4e3a\u5979\u5df2\u7ecf\u53d1\u751f\u4e86\u7231\u610f\uff0c\u867d\u7136\u5979\u5bf9\u79e6\u5143\u6d69\u751a\u6709\u597d \u611f\uff0c\u6bd5\u7adf\u53ea\u662f\u4e00\u9762\u4e4b\u4ea4\uff0c\u7231\u60c5\u662f\u8fd8\u8c08\u4e0d\u4e0a\u7684\u3002\u4e0d\u8fc7\u5979\u7531\u4e8e\u5bf9\u79e6\u5143\u6d69\u7684\u94a6\u656c\uff0c\u5374\u5e0c\u671b\u6709\u4e2a\u673a\u4f1a \u4e3a\u4ed6\u6548\u52b3\u3002 \u3000\u3000\u5979\u5fc3\u91cc\u60f3\u7684\u662f\uff1a\u201c\u90a3\u4e2a\u4eba\u6551\u4e0d\u9192\u79e6\u5143\u6d69\uff0c\u53ef\u80fd\u4f1a\u518d\u5230\u6211\u5bb6\u76d7\u53d6\u89e3\u836f\u3002\u89e3\u836f\u6240\u5728\u4e4b\u5904\uff0c\u53ea \u6709\u6211\u548c\u7239\u7239\u77e5\u9053\u3002\u4ed6\u627e\u4e0d\u7740\uff0c\u6211\u53ef\u4ee5\u53d6\u6765\u9001\u7ed9\u4ed6\u3002\u201d\u5979\u8fd8\u672a\u77e5\u9053\uff0c\u5979\u7684\u7239\u7239\u4e0d\u4f1a\u50cf\u5979\u60f3\u8c61\u7684 \u90a3\u6837\u7b28\uff0c\u4ed6\u4e0d\u4f46\u67e5\u8fc7\u89e3\u836f\uff0c\u800c\u4e14\u628a\u89e3\u836f\u5168\u90e8\u5e26\u8d70\u4e86\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u5f88\u5e0c\u671b\u89c1\u7740\u79e6\u5143\u6d69\uff0c\u53e6\u4e00\u4e2a\u539f\u56e0\u662f\u8981\u6ee1\u8db3\u5979\u7684\u597d\u5947\u4e4b\u5fc3\u3002\u5979\u60f3\u77e5\u9053\u79e6\u5143\u6d69\u7684\u4e0b \u843d\uff0c\u60f3\u77e5\u9053\u90a3\u4e2a\u6551\u8d70\u4ed6\u7684\u4eba\u53c8\u662f\u4e00\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\u7269\u3002 \u3000\u3000\u90a3\u4e48\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\u628a\u79e6\u5143\u6d69\u4ece\u5c01\u5bb6\u6551\u51fa\u53bb\u7684\u5462\uff1f\u82b1\u5f00\u4e24\u6735\uff0c\u5404\u8868\u4e00\u679d\u3002\u5c01\u5bb6\u7236\u5973\u4e4b\u4e8b\uff0c \u90fd\u6682\u4e14\u6309\u4e0b\u4e0d\u63d0\uff0c\u73b0\u5728\u5c31\u8bf4\u79e6\u5143\u6d69\u7684\u79bb\u5947\u906d\u9047\u3002 \u3000\u3000\u4e14\u8bf4\u79e6\u5143\u6d69\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9189\u4e86\u591a\u4e45\uff0c\u4e00\u89c9\u9192\u6765\uff0c\u53ea\u89c9\u80cc\u810a\u6795\u7740\u786c\u5730\uff0c\u5730\u65b9\u53c8\u6e7f\u53c8\u51b7\uff0c\u4ed6\u60ca \u89c9\u5730\u8df3\u4e86\u8d77\u6765\uff0c\u63c9\u63c9\u773c\u775b\uff0c\u5f20\u76ee\u56db\u987e\uff0c\u53ea\u89c1\u5468\u56f4\u90fd\u662f\u6811\u6728\uff0c\u81ea\u5df1\u7adf\u662f\u7761\u5728\u6811\u6797\u91cc\u9762\u3002\u5730\u4e0a\u6ee1 \u662f\u82d4\u84df\uff0c\u770b\u6765\u4e0d\u4f46\u4eba\u8ff9\u7f55\u81f3\uff0c\u8fde\u91ce\u517d\u4e5f\u5c11\u7ecf\u8fc7\u3002\u671d\u9633\u521d\u51fa\uff0c\u9732\u73e0\u672a\u5e72\uff0c\u602a\u4e0d\u5f97\u80cc\u810a\u89c9\u5f97\u53c8\u6e7f \u53c8\u51b7\uff0c\u6781\u4e0d\u8212\u670d\u3002 \u3000\u3000\u79e6\u5143\u6d69\u770b\u6e05\u695a\u4e86\u6240\u5904\u7684\u73af\u5883\uff0c\u4e0d\u7981\u5927\u4e3a\u5947\u602a\uff0c\u51e0\u4e4e\u7591\u5fc3\u81ea\u5df1\u662f\u5728\u505a\u7740\u4e00\u4e2a\u6076\u68a6\u3002\u201c\u6211\u600e \u4e48\u4f1a\u7761\u5728\u8fd9\u4e2a\u5730\u65b9\u7684\uff1f\u201d\u4ed6\u6458\u4e0b\u4e00\u628a\u5e26\u7740\u9732\u6c34\u7684\u91ce\u8349\uff0c\u643d\u4e86\u643d\u8138\uff0c\u8111\u888b\u6e05\u9192\u4e86\u4e9b\uff0c\u6e10\u6e10\u5c31\u60f3 \u8d77\u6628\u65e5\u5728\u5c01\u5bb6\u4f5c\u5ba2\u4e4b\u4e8b\uff0c\u60f3\u8d77\u4e86\u5c01\u5b50\u8d85\u548c\u6587\u9053\u5e84\u7236\u5b50\u5bf9\u4ed6\u90e8\u662f\u5341\u5206\u6bb7\u52e4\uff0c\u9891\u9891\u529d\u4ed6\u559d\u9152\u4e4b \u4e8b\u3002\u4f46\u4ed6\u4e5f\u4e0d\u8fc7\u559d\u4e86\u4e09\u676f\u3002 \u3000\u3000\u201c\u6211\u53ea\u559d\u4e09\u676f\uff0c\u600e\u4f1a\u4fbf\u9189\uff1f\u5373\u4f7f\u9189\u4e86\u4e5f\u5e94\u8be5\u662f\u7761\u5728\u5c01\u5bb6\uff0c\u600e\u7684\u4f1a\u6765\u5230\u6b64\u5730\uff1f\u5440\uff0c\u96be\u9053\u6211 \u5f53\u771f\u662f\u5728\u68a6\u91cc\u4e0d\u6210\uff1f\u201d\u4ed6\u8bd5\u4e00\u8bd5\u54ac\u54ac\u6307\u5934\uff0c\u5f88\u75db\u3002\u6709\u75db\u7684\u611f\u89c9\uff0c\u90a3\u5f53\u7136\u4e0d\u4f1a\u662f\u68a6\u4e86\u3002 \u3000\u3000\u79e6\u5143\u6d69\u6b63\u5728\u83ab\u540d\u5176\u5999\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u54c8\u54c8\u5927\u7b11\u3002\u4e00\u4e2a\u53eb\u5316\u5b50\u5411\u4ed6\u8d70\u6765\uff0c\u4e24\u53ea\u6307\u5934\u6253\u5f97\u567c\u556a \u4f5c\u54cd\uff0c\u8fb9\u8d70\u8fb9\u5531\uff0c\u6b63\u662f\uff1a \u3000\u3000\u4e00\u8eab\u7591\u8eab\u68a6\uff0c\u5f02\u4e10\u5ffd\u76f8\u9022\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\u4e86\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e09\u56de\u3000\u7591\u68a6\u7591\u771f\u8c28\u5f02\u4e10\u3000\u534a\u5fe7\u534a\u559c\u6551\u4f73\u4eba \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e09\u56de\u3000\u7591\u68a6\u7591\u771f\u8c28\u5f02\u4e10\u3000\u534a\u5fe7\u534a\u559c\u6551\u4f73\u4eba \u3000\u3000\u8fd9\u5c0f\u53eb\u5316\u5531\u7684\u662f\u4e00\u652f\u81ea\u7f16\u7684\u201c\u83b2\u82b1\u843d\u201d\uff1a\u201c\u4e00\u6735\u4e00\u679d\u83b2\u82b1\uff0c\u6709\u4e2a\u5c0f\u5b50\u662f\u5927\u50bb\u74dc\uff0c\u4ed6\u628a\u8001 \u864e\u5f53\u5916\u5a46\uff0c\u4ed6\u628a\u6bd2\u9152\u5f53\u9999\u8336\u3002\u89c1\u4e86\u4eba\u5bb6\u7684\u597d\u95fa\u5973\uff0c\u5c31\u7cca\u91cc\u7cca\u6d82\u7684\u95ef\u4e86\u8fdb\u53bb\u5566\uff01\u5477\u5440\u5440\uff01\u9152\u4e0d \u9189\u4eba\u4eba\u81ea\u9189\uff0c\u8fd9\u5c0f\u5b50\u8981\u62dc\u5012\u5728\u77f3\u69b4\u88d9\u4e0b\uff0c\u81ea\u5df1\u5148\u9189\u6210\u4e86\u4e00\u56e2\u70c2\u6ce5\u5df4\u3002\u5477\u5440\u5440\uff0c\u4e00\u6735\u4e00\u679d\u83b2 \u82b1\uff0c\u8fd9\u4e2a\u5c0f\u5b50\u771f\u771f\u662f\u4e2a\u5927\u50bb\u74dc\uff0c\u54bf\u5440\u5440\uff0c\u54c8\uff0c\u54c8\uff0c\u54c8\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u4e00\u770b\uff0c\u8fd9\u5c0f\u53eb\u5316\u7bf7\u9996\u57a2\u9762\uff0c\u4f46\u7a7f\u7684\u4e00\u8eab\u8863\u56ca\uff0c\u867d\u662f\u904d\u6253\u8865\u4e01\uff0c\u5374\u9887\u5e72\u51c0\uff0c\u770b\u6765\u4e5f \u662f\u4e8c\u5341\u5c81\u5de6\u53f3\u7684\u5e74\u7eaa\uff0c\u548c\u4ed6\u4e0d\u76f8\u4e0a\u4e0b\u3002\u79e6\u5143\u6d69\u542c\u4ed6\u7684\u6b4c\u8bcd\uff0c\u4f3c\u4e4e\u5c31\u662f\u5632\u8bbd\u81ea\u5df1\u7684\uff0c\u4e0d\u7981\u8df3\u4e86 \u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u5531\u8fd9\u4e2a\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u90a3\u5c0f\u53eb\u5316\u54a7\u5634\u9732\u9f7f\u5730\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u4f60\u81ea\u5df1\u5e94\u8be5\u660e\u767d\u3002\u4f60\u600e\u4e48\u5230\u8fd9\u513f\u6765\u7684\uff0c\u4f60\u522b\u505a \u68a6\u5566\uff01\u4f60\u54ac\u7740\u6307\u5934\u513f\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u5fc3\u91cc\u6b63\u5728\u4ee5\u4e3a\u81ea\u5df1\u505a\u68a6\uff0c\u542c\u5c0f\u53eb\u5316\u8fd9\u4e48\u4e00\u8bf4\uff0c\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u662f\u4e0d\u660e \u767d\uff0c\u6211\u600e\u4e48\u4f1a\u5230\u8fd9\u513f\u6765\u7684\uff1f\u4f60\u82e5\u77e5\u9053\uff0c\u201c\u8bf7\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u5c0f\u53eb\u5316\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\uff0c\u662f\u6211\u628a\u4f60\u5f04\u5230\u8fd9\u91cc\u6765\u7684\u3002\u600e\u4e48\u6837\uff0c\u7761\u4e00\u89c9\u8212\u670d\u5417\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u5927\u6012\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u6363\u7684\u9b3c\uff01\u201d\u5c0f\u53eb\u5316\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6363\u9b3c\uff1f\u8981\u4e0d \u662f\u6211\u628a\u4f60\u5f04\u51fa\u6765\uff0c\u53ea\u6015\u4f60\u5c31\u8981\u7cca\u91cc\u7cca\u6d82\u7684\u9189\u6b7b\u4e86\u5462\uff01\u4e0d\u9519\uff0c\u8fd9\u91cc\u5f53\u7136\u7761\u5f97\u6ca1\u6709\u5c01\u5bb6\u7684\u8212\u670d\uff0c \u4f60\u82e5\u6b22\u559c\uff0c\u4f60\u5c3d\u53ef\u4ee5\u56de\u53bb\uff0c\u518d\u559d\u5c01\u5bb6\u7684\u5343\u65e5\u9189\uff0c\u518d\u7761\u4f60\u5988\u7684\u6625\u79cb\u5927\u89c9\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u5fcd\u7740\u4e86\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\uff0c\u6211\u4e0d\u4e0e\u4f60\u4e00\u822c\u89c1\u8bc6\uff0c\u4f60\u9a82\u6211\u6211\u4e0d\u7ba1\uff0c\u4f46\u4f60\u53ef\u5f97\u7ed9\u6211\u8bf4\u4e2a \u660e\u767d\uff0c\u4ec0\u4e48\u5343\u65e5\u9189\uff1f\u96be\u9053\u4f60\u662f\u8bf4\u5c01\u5bb6\u7ed9\u6211\u559d\u7684\u662f\u6bd2\u9152\u4e0d\u6210\uff1f\u201d\u4ed6\u8bd5\u8bd5\u8fd0\u6c14\uff0c\u53ea\u89c9\u771f\u6c14\u8fd0\u8f6c\u81ea \u5982\uff0c\u6beb\u65e0\u4e2d\u6bd2\u7684\u8ff9\u8c61\u3002 \u3000\u3000\u5c0f\u53eb\u5316\u9053\uff1a\u201c\u6211\u8bf4\u90a3\u662f\u6bd2\u9152\uff0c\u5927\u7ea6\u4f60\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u3002\u4f60\u81ea\u5df1\u5230\u6c34\u6f6d\u7167\u7167\u770b\u3002\u201d\u5728\u79e6\u5143\u6d69\u5367 \u5904\u7684\u9644\u8fd1\uff0c\u6b63\u6709\u4e00\u4e2a\u5c71\u6cc9\u6c47\u6210\u7684\u6c34\u6f6d\u3002 \u3000\u3000\u79e6\u5143\u6d69\u6c34\u8fb9\u7167\u5f71\uff0c\u53ea\u89c1\u81ea\u5df1\u6ee1\u8eab\u6c61\u6ce5\uff0c\u8fd9\u4e0d\u5947\u602a\uff0c\u4ed6\u7761\u5728\u6e7f\u5730\u4e0a\uff0c\u5f53\u7136\u4f1a\u6cbe\u4e0a\u6c61\u6ce5\u3002\u5947 \u602a\u7684\u662f\uff0c\u4ed6\u7a7f\u7684\u53ea\u662f\u4e00\u8eab\u5355\u8584\u7684\u5185\u8863\uff0c\u5916\u8863\u5374\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u79e6\u5143\u6d69\u6012\u9053\uff1a\u201c\u8fd9\u53c8\u662f\u4f60\u6363\u7684\u9b3c\u5427\uff1f\u4f60\u628a\u6211\u7684\u8863\u88f3\u62ff\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u53eb\u5316\u51b7\u7b11\u9053\uff1a\u201c\u5356\u4e86\uff0c\u5f53\u4e86\uff0c\u600e\u4e48\u6837\uff1f\u4f60\u8fd9\u4e48\u8bf4\u5c31\u5f53\u662f\u6211\u5077\u53bb\u7684\u5427\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u6c14\u5f97\u51e0\u4e4e\u8981\u8dd1\u8fc7\u53bb\u6253\u4ed6\uff0c\u4f46\u4ed6\u6bd5\u7adf\u662f\u540d\u95e8\u5f1f\u5b50\uff0c\u9887\u6709\u6db5\u517b\u529f\u592b\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c0f\u53eb \u5316\u7626\u9aa8\u4f36\u4ec3\uff0c\u7109\u80fd\u6328\u5f97\u6211\u7684\u4e00\u62f3\uff1f\u5b66\u6b66\u7684\u4eba\uff0c\u7edd\u4e0d\u53ef\u8f7b\u6613\u51fa\u624b\u3002\u8fd9\u662f\u5e08\u8bad\uff0c\u6211\u600e\u7684\u5fd8\u4e86\uff1f\u201d \u3000\u3000\u90a3\u5c0f\u53eb\u5316\u653e\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u591a\u8c22\u6211\u4e5f\u5c31\u7b97\u4e86\uff0c\u4f60\u8fd8\u60f3\u548c\u6211\u6253\u67b6\u5440\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u5fcd\u7740\u6c14\u628a\u4f38\u51fa\u7684\u62f3\u5934\u6536\u56de\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u7b97\u662f\u4f60\u5077\u7684\uff0c\u90a3\u4e48\u5176\u5b9e\u662f\u8c01\u5077\u7684\uff1f\u201d \u3000\u3000\u5c0f\u53eb\u5316\u7ef7\u7740\u8138\u8bf4\u9053\uff1a\u201c\u4f60\u5bf9\u6211\u5b9e\u662f\u65e0\u793c\uff0c\u4e0d\u8fc7\uff0c\u6211\u770b\u4f60\u662f\u6211\u7684\u5c0f\u8f88\u4efd\u4e0a\uff0c\u6211\u4e5f\u4e0d\u8d23\u7f5a\u4f60 \u4e86\uff0c\u53ef\u662f\uff0c\u4f60\u8981\u6211\u544a\u8bc9\u4f60\uff0c\u4f60\u5e94\u8be5\u5411\u6211\u5148\u8d54\u4e00\u4e2a\u793c\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u5411\u4ed6\u8d54\u4e86\u4e2a\u793c\uff0c\u9053\uff1a\u201c\u73b0\u5728\u4f60\u53ef\u4ee5\u8bf4\u5b9e\u8bdd\u4e86\u5427\uff1f\u201d \u3000\u3000\u5c0f\u53eb\u5316\u9053\u3002\u201d\u597d\uff0c\u6211\u8bf4\u5b9e\u8bdd\uff0c\u4f46\u53ea\u6015\u6211\u8bf4\u4e86\u5b9e\u8bdd\uff0c\u4f60\u8fd8\u662f\u4e0d\u80af\u76f8\u4fe1\u6211\u3002\u544a\u8bc9\u4f60\uff0c\u4f60\u7684\u5916 \u8863\u662f\u6587\u80dc\u4e2d\u90a3\u5c0f\u5b50\u5265\u6389\u7684\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u679c\u7136\u4e0d\u6562\u76f8\u4fe1\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u5265\u6211\u7684\u8863\u88f3\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u53eb\u5316\u9053\u3002\u201d\u6211\u600e\u4e48\u77e5\u9053\u4ed6\u7684\u7528\u610f\uff0c\u6211\u53ea\u77e5\u9053\u662f\u4ed6\u5265\u4e86\u4f60\u7684\u8863\u88f3\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u59d1\u4e14\u5f53\u4f60\u8bf4\u7684\u662f\u5b9e\u8bdd\u3002\u90a3\u4e48\uff0c\u5c01\u5bb6\u7ed9\u6211\u559d\u7684\u662f\u6bd2\u9152\uff0c\u6587\u516c\u5b50\u5265\u6389 \u6211\u7684\u8863\u88f3\uff0c\u4ed6\u4eec\u8fd9\u4e24\u5bb6\u5c82\u4e0d\u90fd\u53d8\u6210\u5f3a\u76d7\u4e86\uff1f\u8fd9\u4e2a\u8bf4\u6cd5\u672a\u514d\u592a\u8352\u5510\u4e86\u5427\uff1f\u4ed6\u4eec\u82e5\u60f3\u8c0b\u8d22\u5bb3\u547d\uff0c \u4f55\u987b\u4f7f\u7528\u6bd2\u9152\uff1f\u201d \u3000\u3000\u5c0f\u53eb\u5316\u6de1\u6de1\u8bf4\u9053\u3002\u201d\u6211\u65e9\u8bf4\u8fc7\uff0c\u4fe1\u4e0d\u4fe1\u7531\u4f60\uff01\u201d\u79e6\u5143\u6d69\u9053\uff1a\u201c\u4f60\u8981\u6211\u76f8\u4fe1\uff0c\u4e5f\u5f97\u6709\u4e2a\u7406 \u7531\u5440\uff01\u201d \u3000\u3000\u5c0f\u53eb\u5316\u9053\uff1a\u201c\u6211\u8bf4\u7684\u90fd\u662f\u6211\u77e5\u9053\u7684\u4e8b\u5b9e\u3002\u6211\u8fd9\u4e2a\u4eba\u7684\u813e\u6c14\uff0c\u4e0d\u77e5\u9053\u7684\u6211\u5c31\u51b3\u4e0d\u4e71\u8bf4\u3002\u4f60 \u8981\u6211\u66ff\u5979\u4eec\u8bf4\u51fa\u4e00\u4e2a\u2018\u7406\u7531\u2019\uff0c\u54fc\uff0c\u54fc\uff0c\u6211\u600e\u4e48\u77e5\u9053\u4ed6\u4eec\u4e3a\u4f55\u8981\u5bf9\u4f60\u8fd9\u6837\uff1f\u6211\u5bf9\u4e0d\u80fd\u66ff\u4ed6\u4eec \u7f16\u51fa\u4e00\u4e2a\u2018\u7406\u7531\u2019\u3002\u6211\u770b\uff0c\u8fd8\u662f\u4f60\u6765\u8bf4\u5427\uff0c\u4ed4\u7ec6\u5730\u544a\u8bc9\u6211\uff0c\u4f60\u662f\u5230\u54ea\u513f\u53bb\u7684\uff0c\u8981\u505a\u7684\u662f\u4ec0\u4e48 \u4e8b\u60c5\uff0c\u5728\u5c01\u5bb6\u8bf4\u4e86\u4e9b\u4ec0\u4e48\u8bdd\uff0c\u8bf4\u4e0d\u5b9a\u6211\u53ef\u4ee5\u5e2e\u4f60\u63a8\u6572\u63a8\u6572\uff0c\u63a8\u6d4b\u51fa\u4ed6\u4eec\u4e4b\u6240\u4ee5\u5982\u6b64\u7684\u7406 \u7531\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u5fc3\u60f3\uff1a\u53bb\u6c5f\u5927\u4fa0\u5bb6\u53bb\u559d\u559c\u9152\uff0c\u8fd9\u867d\u7136\u4e0d\u662f\u4ec0\u4e48\u79d8\u5bc6\uff0c\u4f46\u8fd9\u80ae\u810f\u7684\u5c0f\u53eb\u5316\u61c2\u5f97\u4ec0\u4e48 \u6b66\u6797\u5927\u4e8b\uff0c\u6211\u4f55\u5fc5\u4e0e\u4ed6\u591a\u8bf4\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u8bf4\u9053\uff1a\u201c\u6211\u7684\u4e8b\u60c5\u6682\u4e14\u4e0d\u8bf4\uff0c\u627e\u60f3\u5148\u95ee\u4e00\u95ee\u4f60\u3002\u201d\u5c0f\u53eb\u5316\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u95ee\u5427\uff01\u4e0d \u8fc7\u5f97\u52a0\u4e0a\u4e00\u4e2a\u8bf7\u5b57\u3002\u201d\u5927\u6a21\u5927\u6837\u5730\u5750\u5728\u77f3\u4e0a\uff0c\u8ba9\u79e6\u5143\u6d69\u5411\u4ed6\u95ee\u8bdd\u3002 \u3000\u3000\u79e6\u5143\u6d69\u9053\u3002\u201d\u597d\uff0c\u8bf7\u95ee\u4f60\uff0c\u7167\u4f60\u7684\u8bf4\u6cd5\uff0c\u662f\u4f60\u628a\u6211\u6551\u51fa\u6765\u7684\u4e86\u3002\u4f60\u662f\u7528\u4ec0\u4e48\u65b9\u6cd5\u628a\u6211\u6551 \u51fa\u6765\u7684\uff1f\u201d \u3000\u3000\u201c\u5c0f\u53eb\u5316\u9053\uff1a\u201c\u90a3\u8fd8\u4e0d\u5bb9\u6613\uff0c\u6211\u8dd1\u8fdb\u5c01\u5bb6\uff0c\u5c31\u628a\u4f60\u80cc\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\u3002\u201d\u6587\u9053\u5e84\u548c\u5c01\u5b50\u8d85\u80af\u8ba9\u4f60\u8981\u6765\u5c31\u6765\uff0c\u8981\u53bb\u5c31\u53bb\u3002\u6211\u2026\u2026\u201d \u3000\u3000\u5c0f\u53eb\u5316\u63d2\u9053\uff1a\u201c\u6211\u8981\u6765\u5c31\u6765\uff0c\u8981\u53bb\u5c31\u53bb\uff0c\u600e\u7531\u5f97\u4ed6\u4eec\u4e0d\u8bb8\u3002\u544a\u8bc9\u4f60\uff0c\u4ed6\u4eec\u73b0\u5728\u4e5f\u6b63\u662f\u548c \u4f60\u4e00\u6837\uff0c\u5982\u5728\u68a6\u4e2d\u5462\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u4e0d\u7531\u5f97\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5927\u7684\u53e3\u6c14\uff0c\u8bf7\u95ee\u5c0a\u9a7e\u4eca\u5e74\u591a\u5927\u5e74\u7eaa\uff1f\u201d\u5fc3\u60f3\uff1a\u201c\u5c01\u5b50\u8d85\u7684\u6b66 \u529f\u5982\u4f55\u6211\u4e0d\u77e5\u9053\uff0c\u6587\u9053\u5e84\u7684\u672c\u9886\u51b3\u4e0d\u5728\u6211\u5e08\u7236\u4e4b\u4e0b\uff0c\u51ed\u4f60\u8fd9\u4e2a\u5c0f\u53eb\u5316\u5c31\u80fd\u8981\u6765\u5c31\u6765\uff0c\u8981\u53bb\u5c31 \u53bb\uff1f\u201d \u3000\u3000\u5c0f\u53eb\u5316\u7768\u76ee\u659c\u77a7\uff0c\u201c\u54e6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\uff0c\u662f\u4e0d\u662f\u8ba4\u4e3a\u53ea\u6709\u6b66\u6797\u4e2d\u7684\u8001\u524d\u8f88\uff0c\u624d \u6709\u672c\u9886\u628a\u4f60\u6551\u51fa\u53bb\u4e48\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u4e0d\u5ba2\u6c14\u7684\u9876\u4ed6\u4e00\u53e5\uff0c\u8bf4\u9053\uff1a\u201c\u6b63\u662f\u5982\u6b64\uff01\u201d \u3000\u3000\u5c0f\u53eb\u5316\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u6b66\u5f53\u6d3e\u96f7\u9707\u5b50\u7684\u5f1f\u5b50\u5427\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u542c\u5f97\u8fd9\u5c0f\u53eb\u5316\u76f4\u547c\u4ed6\u7684\u5e08\u7236\u4e4b\u540d\uff0c\u5fc3\u91cc\u5f88\u4e0d\u9ad8\u5174\uff0c\u4f46\u542c\u4ed6\u4e00\u53e3\u5c31\u8bf4\u7834\u4e86\u81ea\u5df1\u7684\u6765 \u5386\uff0c\u5fc3\u91cc\u4e5f\u597d\u751f\u8be7\u5f02\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b66\u5f53\u638c\u95e8\u96f7\u9707\u5b50\u6b63\u662f\u5bb6\u5e08\u3002\u201d \u3000\u3000\u5c0f\u53eb\u5316\u5ffd\u5730\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u8bb2\u8f88\u4efd\u3001\u8bba\u5c0a\u5351\uff0c\u8fd9\u4e00\u5957\u6211\u672c\u6765\u4e0d\u60ef\uff0c\u4e5f\u4ece\u4e0d\u62d8 \u6ce5\u3002\u4f46\u4f60\u65e2\u7136\u8981\u8bb2\uff0c\u90a3\u4f60\u5c31\u5e94\u8be5\u7ed9\u6211\u5148\u53e9\u4e09\u4e2a\u54cd\u5934\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u5fc3\u5934\u706b\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u4f60\u662f\u8001\u524d\u8f88\uff1f\u201d \u3000\u3000\u5c0f\u53eb\u5316\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5e74\u7eaa\u4e0d\u5927\uff0c\u8f88\u4efd\u5374\u8001\u3002\u4f60\u662f\u6211\u7684\u5b59\u5b50\u8f88\uff0c\u4f60\u7684\u5e08\u7236\u662f\u6211\u7684\u5c0f\u4e00\u8f88\u3002 \u4f60\u7ed9\u6211\u53e9\u4e09\u4e2a\u54cd\u5934\uff0c\u7b97\u662f\u4fbf\u5b9c\u4f60\u4e86\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u8fd9\u4e00\u4e0b\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4fae\u8fb1\u6211\u4e0d\u6253\u7d27\uff0c\u4f60\u8fd8\u6562\u4fae\u8fb1\u6211\u7684\u987a\u7236\uff01\u201d\u5c0f\u53eb \u5316\u9053\uff1a\u201c\u6211\u600e\u4e48\u4fae\u8fb1\u4ed6\u4e86\uff1f\u201d\u79e6\u5143\u6d69\u6012\u9053\uff1a\u201c\u4f60\u3001\u4f60\u3001\u4f60\u3001\u4f60\u8fd9\u4e2a\u5c0f\u53eb\u5316\u5c45\u7136\u6562\u8bf4\u6211\u7684\u5e08\u7236 \u662f\u4f60\u7684\u665a\u8f88\uff01\u201d\u5c0f\u53eb\u5316\u4f5c\u51fa\u6ee1\u8138\u4e0d\u89e3\u7684\u795e\u6c14\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u4fae\u8fb1\uff1f\u6211\u8bf4\u7684\u53ea\u662f\u4e8b\u5b9e\u3002\u4f60\u7684\u5e08 \u7236\u5b9e\u5728\u662f\u6211\u7684\u5c0f\u8f88\u3002\u6211\u5c06\u6765\u82e5\u6709\u513f\u5b50\uff0c\u4f60\u7684\u5e08\u7236\u53ef\u4ee5\u548c\u6211\u7684\u513f\u5b50\u5e73\u8f88\u8bba\u4ea4\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u53e3\u51fa\u6c61\u8a00\uff0c\u5403\u6211\u4e00\u638c\uff01\u201d\u4e00\u638c\u6253\u51fa\uff0c\u89c1\u90a3\u5c0f\u53eb\u5316\u4e0d\u95ea\u4e0d\u8eb2\u4e5f\u4e0d \u62db\u67b6\uff0c\u79e6\u5143\u6d69\u5012\u6015\u6253\u4f24\u4e86\u4ed6\uff0c\u8fd9\u4e00\u638c\u505c\u5728\u4ed6\u7684\u5934\u9876\u4e09\u5bf8\u4e4b\u5904\uff0c\u4e0d\u6562\u6253\u4e0b\u3002 \u3000\u3000\u5c0f\u53eb\u5316\u7b11\u9053\uff1a\u201c\u8bf4\u5230\u6253\u67b6\uff0c\u8fd9\u662f\u6211\u6700\u559c\u6b22\u7684\u4e8b\u60c5\u3002\u4f46\u4f60\u662f\u6211\u7684\u5b59\u5b50\u8f88\uff0c\u6211\u53ef\u4e0d\u80fd\u548c\u4f60\u52a8 \u624b\u8fc7\u62db\u3002\u6211\u4efb\u51ed\u4f60\u6253\u597d\u4e86\u3002\u4f46\u6211\u53ef\u5f97\u6709\u8a00\u5728\u5148\uff0c\u4f60\u4e0d\u6015\u5403\u4e8f\u4f60\u5148\u6253\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u8fd9\u4e00\u638c\u529b\u8db3\u5f00\u7891\u7834\u77f3\uff0c\u4f60\u4e0d\u6015\u6211\u6253\u6b7b\u4e86\u4f60\uff1f\u201d \u3000\u3000\u5c0f\u53eb\u5316\u9053\uff1a\u201c\u6211\u4e5f\u8981\u544a\u8bc9\u4f60\uff0c\u4f60\u6253\u6211\u53ea\u662f\u4f60\u81ea\u5df1\u5403\u4e8f\u3002\u6211\u624d\u4e0d\u4f1a\u6015\u6253\u5462\uff0c\u4e0d\u4fe1\uff0c\u4f60\u5c31\u8bd5 \u8bd5\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u6012\u4e0d\u53ef\u904f\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u7ed9\u4ed6\u4e00\u70b9\u5389\u5bb3\uff0c\u4ed6\u53ea\u5f53\u6211\u6b66\u5f53\u6d3e\u662f\u597d\u6b3a\u4fae\u7684\u4e86\uff0c\u4e3a\u4e86\u5e08\u95e8 \u58f0\u8a89\uff0c\u6211\u4e5f\u975e\u5f97\u51fa\u8fd9\u53e3\u6c14\u4e0d\u53ef\uff01\u201d\u4e8e\u662f\u547c\u7684\u4e00\u638c\uff0c\u5c31\u5411\u90a3\u5c0f\u53eb\u5316\u6253\u53bb\u3002\u4f46\u4ed6\u6015\u8fd9\u5c0f\u53eb\u5316\u53d7 \u4f24\uff0c\u7528\u7684\u53ea\u662f\u4e24\u4e09\u5206\u6c14\u529b\u3002 \u3000\u3000\u90a3\u5c0f\u53eb\u5316\u5750\u5728\u77f3\u4e0a\uff0c\u79e6\u5143\u6d69\u7ad9\u5728\u4ed6\u7684\u9762\u524d\uff0c\u8ddd\u79bb\u6781\u8fd1\uff0c\u6309\u8bf4\u8fd9\u4e00\u638c\u662f\u975e\u6253\u4e2d\u4e0d\u53ef\u7684\uff0c\u4e0d \u6599\u79e6\u5143\u6d69\u4e00\u638c\u6253\u53bb\uff0c\u5374\u6251\u4e86\u4e2a\u7a7a\u3002\u4f46\u89c9\u773c\u775b\u4e00\u82b1\uff0c\u77f3\u5934\u4e0a\u7684\u5c0f\u53eb\u6bd4\u4e0d\u89c1\u4e86\uff01 \u3000\u3000\u79e6\u5143\u6d69\u5927\u5403\u4e00\u60ca\uff0c\u6b64\u65f6\u624d\u77e5\u9053\u8fd9\u4e2a\u5c0f\u53eb\u5316\u4e43\u662f\u98ce\u5c18\u5f02\u4eba\u3002\u53ea\u542c\u5f97\u5c0f\u53eb\u5316\u7684\u58f0\u97f3\u5728\u4ed6\u80cc\u540e \u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u6211\u8bf4\u8fc7\u4f60\u662f\u6211\u7684\u5b59\u5b50\u8f88\uff0c\u4efb\u51ed\u4f60\u6253\uff0c\u6211\u662f\u51b3\u4e0d\u8fd8\u624b\u7684\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u867d\u7136\u77e5\u9053\u8fd9\u5c0f\u53eb\u662f\u98ce\u5c18\u5f02\u4eba\uff0c\u5374\u4e0d\u7518\u53d7\u4ed6\u4fae\u8fb1\uff0c\u5f53\u4e0b\u53cd\u624b\u4fbf\u662f\u4e00\u638c\u3002\u8fd9\u4e00\u638c\u52a0\u5f3a \u4e86\u51e0\u5206\u529b\u9053\uff0c\u51fa\u624b\u4e5f\u6bd4\u521a\u624d\u8fc5\u6377\uff0c\u4e0d\u6599\u4ecd\u7136\u6253\u4e86\u4e2a\u7a7a\u3002\u90a3\u5c0f\u53eb\u5316\u7b11\u9053\uff1a\u201c\u5582\uff0c\u6211\u5728\u8fd9\u8fb9\u3002\u201d \u79e6\u5143\u6d69\u659c\u8eab\u6ed1\u6b65\uff0c\u5de6\u53f3\u5f00\u5f13\uff0c\u5fc3\u60f3\u8fd9\u56de\u4f60\u603b\u9003\u907f\u4e0d\u4e86\u3002\u4ed6\u542c\u5f97\u8fd9\u5c0f\u53eb\u5316\u7684\u58f0\u97f3\u5728\u4ed6\u5de6\u4fa7\uff0c\u5373 \u4f7f\u8eab\u6cd5\u5947\u5feb\uff0c\u7acb\u5373\u8f6c\u8fc7\u4ed6\u7684\u53f3\u4fa7\uff0c\u4ed6\u8fd9\u4e00\u62db\u5de6\u53f3\u5f00\u5f13\u8fd8\u662f\u53ef\u4ee5\u6253\u4e2d\u7684\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u5c0f\u53eb\u5316\u53eb\u9053\uff1a\u201c\u54ce\u5440\uff0c\u4e0d\u597d\u4e86\uff01\u201d\u547c\u7684\u4e00\u58f0\uff0c\u7a81\u7136\u4ece\u4ed6\u5934\u9876\u8dc3\u8fc7\uff0c\u626e\u4e86\u4e2a\u9b3c \u8138\uff0c\u7b11\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff0c\u8fd8\u662f\u6253\u4e0d\u7740\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u6c89\u4f4f\u4e86\u6c14\uff0c\u4f7f\u5f00\u6b66\u5f53\u6d3e\u7684\u4e09\u5927\u7edd\u6280\u4e4b\u4e00\u7684\u4e5d\u5bab\u8fde\u73af\u638c\u6cd5\u5411\u8fd9\u5c0f\u53eb\u5316\u8fdb\u903c\uff0c\u8fd9\u4e5d\u5bab \u8fde\u73af\u638c\u6cd5\u811a\u8e0f\u4e94\u95e8\u516b\u5366\u65b9\u4f4d\uff0c\u638c\u6cd5\u4f7f\u5f00\uff0c\u4e0d\u8bba\u654c\u4eba\u5728\u54ea\u4e2a\u65b9\u4f4d\uff0c\u90fd\u9003\u4e0d\u5f00\u4ed6\u7684\u638c\u52bf\u5ba0\u7f69\u3002 \u3000\u3000\u90a3\u5c0f\u53eb\u5316\u8d5e\u9053\uff1a\u201c\u597d\uff0c\u4f60\u662f\u6211\u7684\u5b59\u5b50\u8f88\uff1b\u4e5d\u5bab\u8fde\u73af\u638c\u6cd5\u6709\u5982\u6b64\u706b\u5019\uff0c\u4e5f\u7b97\u5f88\u4e0d\u9519\u4e86\uff01\u201d \u4e00\u526f\u8001\u6c14\u6a2a\u79cb\u7684\u6837\u5b50\uff0c\u800c\u4e14\u8fd8\u7d22\u6027\u80cc\u8d77\u53cc\u624b\uff0c\u5c31\u5728\u79e6\u5143\u6d69\u7684\u638c\u52bf\u7b3c\u7f69\u4e4b\u4e0b\u8e0f\u8d77\u65b9\u6b65\u6765\u3002\u4f46\u8bf4 \u4e5f\u5947\u602a\uff0c\u5c3d\u7ba1\u8fd9\u5c0f\u53eb\u5316\u604d\u5982\u95f2\u5ead\u4fe1\u6b65\uff0c\u79e6\u5143\u6d69\u7684\u6307\u5934\u5374\u8fde\u4ed6\u7684\u8863\u89d2\u90fd\u6ca1\u6cbe\u4e0a\u3002\u79e6\u5143\u6d69\u6012\u9053\uff1a \u201c\u6709\u672c\u9886\u7684\u4f60\u6562\u548c\u6211\u786c\u5bf9\u4e00\u638c\u4e48\uff1f\u201d\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c0f\u53eb\u5316\u4e0d\u77e5\u4ece\u54ea\u91cc\u5b66\u6765\u7684\u4e00\u5957\u53e4\u602a\u8eab\u6cd5\uff0c\u771f \u5b9e\u7684\u672c\u9886\u5374\u672a\u5fc5\u5b9a\u80fd\u80dc\u6211\u3002\u201d\u4ed6\u542b\u6012\u8bf4\u8bdd\uff0c\u5b9e\u662f\u610f\u6b32\u6fc0\u8fd9\u5c0f\u53eb\u5316\u8fd8\u624b\u7684\u3002 \u3000\u3000\u5c0f\u53eb\u5316\u7b11\u9053\uff1a\u201c\u6211\u53ea\u4f1a\u6328\u6253\uff0c\u8fd8\u624b\u6211\u662f\u4e0d\u4f1a\u7684\u3002\u4e56\u5b59\u5b50\uff0c\u6211\u5c31\u786c\u63a5\u4f60\u7684\u4e00\u638c\uff0c\u4f60\u6253 \u5427\uff01\u201d\u79e6\u5143\u6d69\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u5de6\u62f3\u53f3\u638c\uff0c\u671d\u7740\u5c0f\u53eb\u5316\u53d1\u58f0\u4e4b\u5904\u6253\u53bb\u3002\u4ed6\u672c\u6765\u4ee5\u4e3a\u8fd9\u5c0f\u53eb \u5316\u4ecd\u4f1a\u95ea\u907f\u7684\uff0c\u4e0d\u6599\u8fd9\u5c0f\u53eb\u5316\u679c\u7136\u8a00\u800c\u6709\u4fe1\uff0c\u7a81\u7136\u505c\u5728\u4ed6\u7684\u9762\u524d\u4e0d\u52a8\u3002\u8fd9\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d \u79e6\u5143\u6d69\u7528\u5230\u5341\u6210\u6c14\u529b\uff0c\u539f\u610f\u662f\u8981\u8fd9\u5c0f\u53eb\u5316\u77e5\u9053\u5389\u5bb3\uff0c\u82e5\u4e0d\u95ea\u907f\uff0c\u5c31\u975e\u62db\u67b6\u4e0d\u884c\uff0c\u56e0\u4e3a\u79e6\u5143\u6d69 \u4e5f\u5e76\u4e0d\u60f3\u6253\u4e00\u4e2a\u4e0d\u80af\u8fd8\u624b\u7684\u4eba\u3002 \u3000\u3000\u8fd9\u5c0f\u53eb\u5316\u7a81\u7136\u6b62\u6b65\uff0c\u4ecd\u7136\u80cc\u8d1f\u53cc\u624b\uff0c\u505c\u5728\u4ed6\u7684\u9762\u524d\uff0c\u8fd9\u4e00\u4e0b\u5927\u51fa\u79e6\u5143\u6d69\u610f\u6599\u4e4b\u5916\u3002\u53ef\u662f \u4ed6\u7684\u62db\u6570\u5df2\u7ecf\u53d1\u51fa\uff0c\u800c\u4ed6\u7684\u6b66\u5b66\u9020\u8be3\u8fd8\u672a\u66fe\u8fbe\u5230\u6536\u53d1\u968f\u5fc3\u7684\u5883\u754c\uff0c\u767e\u5fd9\u4e2d\u4ed6\u53ea\u80fd\u51cf\u5f31\u51e0\u5206\u6c14 \u529b\uff0c\u5de6\u624b\u7684\u4e00\u62f3\u4ecd\u7136\u5411\u524d\u76f4\u6363\u3002 \u3000\u3000\u4e00\u62f3\u6363\u51fa\uff0c\u6b63\u4e2d\u5c0f\u53eb\u5316\u7684\u8179\u90e8\u3002\u5728\u8fd9\u4e00\u5239\u90a3\uff0c\u79e6\u5143\u6d69\u8fd8\u5728\u62c5\u5fc3\u8fd9\u5c0f\u53eb\u5316\u4f1a\u53d7\u91cd\u4f24\uff0c\u5fc3\u4e2d \u6697\u53eb\uff1a\u201c\u7cdf\u4e86\uff0c\u7cdf\u4e86\uff01\u201d\u54ea\u77e5\u62f3\u5934\u89e6\u7740\u5bf9\u65b9\u7684\u8eab\u4f53\uff0c\u7adf\u4f3c\u6253\u5728\u4e00\u56e2\u68c9\u7d6e\u4e4b\u4e2d\uff0c\u8f6f\u7ef5\u7ef5\u7684\u6839\u672c \u5c31\u65e0\u4ece\u7740\u529b\u3002\u79e6\u5143\u6d69\u60f3\u6536\u56de\u62f3\u5934\uff0c\u5bf9\u65b9\u7684\u8179\u90e8\u9661\u7136\u751f\u4e86\u4e00\u80a1\u5438\u529b\uff0c\u79e6\u5143\u6d69\u7adf\u7136\u8fde\u62f3\u5934\u4e5f\u62d4\u4e0d \u51fa\u6765\uff0c\u4e0d\u7531\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u5c34\u5c2c\u4e4b\u6781\u3002 \u3000\u3000\u5c0f\u53eb\u5316\u54c8\u54c8\u4e00\u7b11\uff0c\u80f8\u8179\u4e00\u633a\uff0c\u79e6\u5143\u6d69\u53ea\u89c9\u4e00\u80a1\u5de8\u529b\u5411\u4ed6\u63a8\u538b\uff0c\u767b\u65f6\u8eab\u4e0d\u7531\u5df1\u7684\u7ed9\u8fd9\u5c0f\u53eb \u5316\u629b\u4e86\u8d77\u6765\uff0c\u817e\u4e91\u9a7e\u96fe\u822c\u7684\u76f4\u8dcc\u51fa\u4e86\u4e03\u516b\u4e08\u5f00\u5916\u3002 \u3000\u3000\u5c0f\u53eb\u5316\u9053\uff1a\u201c\u5982\u4f55\uff1f\u6211\u8bf4\u8fc7\u4f60\u6253\u6211\u53ea\u6709\u4f60\u81ea\u5df1\u5403\u4e8f\u7684\u3002\u5e78\u4e8f\u4f60\u4e34\u65f6\u6536\u56de\u4e86\u51e0\u5206\u6c14\u529b\uff0c\u8981 \u4e0d\u7136\u4f60\u8fd9\u4e2a\u7b4b\u6597\u683d\u5f97\u66f4\u5927\u3002\u201d\u79e6\u5143\u6d69\u722c\u4e86\u8d77\u6765\uff0c\u53ea\u89c1\u81ea\u5df1\u521a\u8dcc\u5728\u4e00\u5757\u5ca9\u77f3\u65c1\u8fb9\uff0c\u8981\u662f\u81ea\u5df1\u521a \u624d\u7528\u8db3\u6c14\u529b\u6253\u90a3\u5c0f\u53eb\u5316\u7684\u8bdd\uff0c\u53cd\u5f39\u4e4b\u529b\u5fc5\u7136\u66f4\u5927\uff0c\u90a3\u5c31\u5fc5\u7136\u8981\u649e\u7740\u5ca9\u77f3\uff0c\u78b0\u5f97\u5934\u7834\u8840\u6d41\u4e86\u3002 \u3000\u3000\u79e6\u5143\u6d69\u6ee1\u9762\u901a\u7ea2\uff0c\u505a\u58f0\u4e0d\u5f97\u3002\u5c0f\u53eb\u5316\u7b11\u9053\uff1a\u201c\u5982\u4eca\u4f60\u8be5\u76f8\u4fe1\u6211\u7684\u8bf4\u8bdd\u4e86\u5427\uff1f\u6211\u6709\u6ca1\u6709\u672c \u9886\u628a\u4f60\u4ece\u5c01\u5bb6\u6551\u51fa\u6765\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u7cca\u91cc\u7cca\u6d82\u7684\u8d25\u5728\u8fd9\u5c0f\u53eb\u5316\u624b\u4e0b\uff0c\u5bf9\u65b9\u7684\u6b66\u529f\u6df1\u6d45\uff0c\u4ed6\u4ecd\u7136\u662f\u83ab\u6d4b\u9ad8\u6df1\uff0c\u4e0d\u7531\u5f97\u5341 \u5206\u4f69\u670d\u3002\u4f46\u79e6\u5143\u6d69\u4e5f\u662f\u6709\u51e0\u5206\u50b2\u9aa8\u7684\u4eba\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u672c\u9886\u6211\u662f\u5341\u5206\u4f69\u670d\uff0c\u4f46\u4f60\u8981\u4fae\u8fb1 \u6211\u6b66\u5f53\u6d3e\uff0c\u8fd9\u2026\u2026\u201d \u3000\u3000\u5c0f\u53eb\u5316\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u662f\u4e0d\u7518\u4e8e\u505a\u6211\u7684\u5c0f\u8f88\uff0c\u662f\u4e48\uff1f\u597d\uff0c\u4f60\u65e2\u7136\u8ba4\u4e3a\u8fd9\u662f\u4fae\u8fb1\uff0c\u54b1\u4eec \u5e73\u8f88\u8bba\u4ea4\u5c31\u662f\u3002\u6211\u672c\u6765\u4e0d\u559c\u6b22\u548c\u4eba\u5bb6\u8bba\u8f88\u4efd\u3001\u8bba\u6392\u884c\u7684\u3002\u8981\u662f\u5f53\u771f\u6392\u8bba\u8d77\u6765\uff0c\u5c82\u53ea\u662f\u4f60\u7684\u5e08 \u7236\uff0c\u6c5f\u6e56\u4e0a\u8bb8\u591a\u6210\u540d\u4eba\u7269\u90fd\u662f\u6211\u7684\u665a\u8f88\u5462\uff01\u597d\uff0c\u79e6\u5144\uff0c\u4f60\u73b0\u5728\u53ef\u4ee5\u544a\u8bc9\u6211\u771f\u8bdd\u4e86\u5427\uff1f\u4f60\u4e5f\u5e94 \u8be5\u76f8\u4fe1\u6211\u6240\u8bf4\u7684\u90fd\u662f\u771f\u8bdd\u4e86\u5427\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u4ecd\u7136\u4e0d\u76f8\u4fe1\u8fd9\u5c0f\u53eb\u5316\u7684\u8f88\u4efd\u4f1a\u6bd4\u4ed6\u7684\u5e08\u7236\u9ad8\u4e00\u8f88\uff0c\u4f46\u65e2\u7136\u5bf9\u65b9\u5411\u4ed6\u8d54\u4e86\u672d\uff0c\u5e76\u613f \u4e0e\u4ed6\u5e73\u8f88\u8bba\u4ea4\uff0c\u79e6\u5143\u6d69\u7684\u5fc3\u4e2d\u4e4b\u6c14\u4e5f\u5c31\u6d88\u4e86\u4e00\u5927\u534a\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u7684\u5f02\u4eba\u6bcf\u591a\u6e38\u620f\u98ce \u5c18\u4e4b\u4e3e\uff0c\u5018\u82e5\u8fd9\u5c0f\u53eb\u5316\u5f53\u771f\u662f\u51fa\u4e8e\u597d\u5fc3\u6551\u6211\uff0c\u4ed6\u548c\u6211\u5f00\u5f00\u73a9\u7b11\uff0c\u6211\u53c8\u4f55\u987b\u5b9a\u8981\u8ba1\u8f83\u4e0d\u4f11\uff1f\u201d \u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u76f8\u4fe1\u4f60\u6709\u672c\u9886\u5c06\u6211\u4ece\u5c01\u5bb6\u6551\u51fa\u6765\u4e86\u3002\u53ef\u662f\u4f60\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\uff0c\u96be\u9053\u4ed6\u4eec \u5f53\u771f\u662f\u8981\u5bb3\u6211\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5c0f\u53eb\u5316\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u53ea\u662f\u76f8\u4fe1\u6211\u7684\u4e00\u534a\u8bf4\u8bdd\u3002\u4ed6\u4eec\u56e0\u4f55\u5bb3\u4f60\uff0c\u6211\u4e0d\u77e5\u9053\u3002\u4f46\u4ed6\u4eec \u7ed9\u4f60\u559d\u7684\u9152\u540d\u53eb\u2018\u5343\u65e5\u9189\u2019\u8fd9\u6211\u53ef\u662f\u77e5\u9053\u7684\u3002\u6587\u80dc\u4e2d\u90a3\u5c0f\u5b50\u5265\u4f60\u7684\u8863\u88f3\uff0c\u6211\u4e5f\u662f\u4eb2\u773c\u89c1\u5230 \u7684\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u5583\u5583\u8bf4\u9053\uff1a\u201c\u5343\u65e5\u9189\uff1f\u90a3\u4e48\u6211\u5df2\u7ecf\u9189\u4e86\u51e0\u65e5\u4e86\uff1f\u201d\u4ed6\u8bb0\u8d77\u81ea\u5df1\u6628\u665a\u53ea\u662f\u559d\u4e86\u4e09 \u676f\uff0c\u5c45\u7136\u5c31\u9189\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u4e0d\u7531\u5f97\u4e0d\u76f8\u4fe1\u4e86\u51e0\u5206\u3002\u6b64\u65f6\u4ed6\u53ea\u6015\u81ea\u5df1\u9189\u91cc\u4e0d\u77e5\u65f6\u65e5\u8fc7\uff0c\u5df2\u7ecf\u8bef \u4e86\u6c5f\u5bb6\u7684\u559c\u9152\u3002 \u3000\u3000\u5c0f\u53eb\u5316\u9053\uff1a\u201c\u672c\u6765\u4f60\u81f3\u5c11\u8981\u9189\u4e2a\u4e03\u5929\u4e03\u591c\u7684\uff0c\u5982\u4eca\u53ea\u9189\u4e86\u4e00\u5929\u4e00\u591c\uff0c\u90a3\u662f\u56e0\u4e3a\u6211\u7ed9\u4f60\u670d \u4e86\u4e00\u9897\u78a7\u7075\u4e39\u4e4b\u6545\u3002\u78a7\u7075\u4e39\u662f\u7528\u5929\u5c71\u96ea\u83b2\u4f5c\u4e3b\u836f\uff0c\u529f\u80fd\u6d88\u89e3\u8bf8\u822c\u90aa\u6bd2\uff0c\u4e0d\u8fc7\uff0c\u5b83\u7a76\u7adf\u4e0d\u662f\u4e13 \u4e3a\u5343\u65e5\u9189\u800c\u914d\u5236\u7684\u89e3\u836f\uff0c\u6240\u4ee5\u4f60\u8fd8\u662f\u8981\u9189\u4e00\u5929\u4e00\u591c\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u54e6\uff0c\u8fd9\u4e48\u8bf4\u4eca\u5929\u662f\u516b\u6708\u5341\u4e8c\u7684\u6e05\u6668\u4e86\uff1f\u201d \u3000\u3000\u5c0f\u53eb\u5316\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f60\u53ef\u662f\u6709\u4ec0\u4e48\u7d27\u8981\u4e4b\u4e8b\uff0c\u5fc5\u987b\u5728\u67d0\u4e00\u5929\u53bb\u505a\u7684\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u51fa\u4e86\u5f82\u5f95\u5c71\uff0c\u6211\u6709\u4e24\u5929\u529f\u592b\u5c31\u53ef\u4ee5\u5230\u8fbe\u4e1c\u5e73\uff0c\u6070\u597d\u53ef\u4ee5\u8d76\u4e0a \u6b63\u65e5\u3002\u201d\u4ed6\u56e0\u4e3a\u5c1a\u672a\u77e5\u9053\u8fd9\u5c0f\u53eb\u5316\u7684\u6765\u5386\uff0c\u5f53\u4e0b\u542b\u7cca\u9053\uff1a\u201c\u662f\u6709\u70b9\u5c0f\u4e8b\uff0c\u4f46\u8fd8\u4e0d\u81f3\u4e8e\u803d\u8bef\u5c31 \u662f\u4e86\uff0c\u55ef\uff0c\u6211\u8fd8\u6ca1\u6709\u8bf7\u6559\u8001\u54e5\u9ad8\u59d3\u5927\u540d\u5462\uff1f\u8001\u54e5\u7684\u672c\u9886\u5982\u6b64\u4e86\u5f97\uff0c\u4e0d\u77e5\u5c0a\u5e08\u662f\u54ea\u4e00\u6d3e\u7684\u9ad8 \u4eba\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u8d77\u521d\u672c\u6765\u6709\u70b9\u7591\u5fc3\u4ed6\u662f\u4e10\u5e2e\u7684\u5f1f\u5b50\uff0c\u4f46\u4e10\u5e2e\u7684\u5e2e\u4e3b\u4ef2\u957f\u7edf\u662f\u4ed6\u5e08\u7236\u7684\u597d\u53cb\uff0c\u4ed6\u66fe \u89c1\u8fc7\u4ef2\u957f\u7edf\u548c\u5e08\u7236\u5207\u78cb\u6b66\u529f\uff0c\u4ef2\u957f\u7edf\u7684\u8eab\u6cd5\u3001\u624b\u6cd5\u548c\u8fd9\u5c0f\u53eb\u5316\u7edd\u65e0\u76f8\u540c\u4e4b\u5904\u3002\u800c\u4e14\u4e10\u5e2e\u7684\u7edd \u6280\u662f\u201c\u6df7\u5143\u4e00\u6c14\u529f\u201d\uff0c\u90a3\u662f\u4e00\u79cd\u6781\u4e3a\u521a\u731b\u7684\u5185\u529f\u3002 \u3000\u3000\u8fd9\u5c0f\u53eb\u5316\u7528\u809a\u76ae\u5438\u4ed6\u7684\u62f3\u5934\u7136\u540e\u5c06\u4ed6\u53cd\u5f39\u51fa\u53bb\u7684\u529f\u592b\uff0c\u594f\u5143\u6d69\u867d\u7136\u4e0d\u61c2\u8fd9\u662f\u54ea\u95e8\u529f\u592b\uff0c \u4f46\u5374\u77e5\u9053\u8fd9\u662f\u4e00\u79cd\u4ee5\u67d4\u514b\u521a\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u548c\u4e10\u5e2e\u7684\u201c\u6df7\u5143\u4e00\u6770\u201d\u521a\u521a\u76f8\u53cd\u3002\u770b\u6765\u8fd9\u5c0f\u53eb\u5316\u53c8 \u4e0d\u4f3c\u662f\u4e10\u5e2e\u7684\u4e86\uff0c\u662f\u4ee5\u79e6\u5143\u6d69\u6709\u6b64\u4e00\u95ee\u3002 \u3000\u3000\u5c0f\u53eb\u5316\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u9ad8\u4eba\u4e8c\u5b57\uff0c\u4e0e\u6211\u65e0\u7f18\u3002\u6211\u8bf4\u6211\u7684\u59d3\u540d\uff0c\u6211\u7684\u59d3\u540d\u5c31\u662f\u6700\u4fd7\u6700 \u4fd7\u7684\u3002\u4f60\u6709\u94b1\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5c0f\u53eb\u5316\u8fd9\u7a81\u5982\u5176\u6765\u7684\u4e00\u95ee\uff0c\u4ee4\u5f97\u79e6\u5143\u6d69\u83ab\u540d\u5176\u5999\u3002\u4ed6\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\uff1a\u201c\u8001\u54e5\u7f3a\u94b1\u7528\u4e48\uff1f \u6211\u5e26\u7684\u4e0d\u591a\uff0c\u6709\u51e0\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u5c0f\u53eb\u5316\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5411\u4f60\u8ba8\u5316\u3002\u4f60\u8fd8\u6709\u6bd4\u94f6\u5b50\u66f4\u8d35\u91cd\u7684\u4e1c\u897f\u5417\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u6bd4\u94f6\u5b50\u66f4\u8d35\u91cd\u90a3\u5e94\u8be5\u662f\u91d1\u5b50\u4e86\u3002\u91d1\u5b50\u4e48\u6211\u53ef\u6ca1\u6709\u3002\u201d \u3000\u3000\u5c0f\u53eb\u5316\u7b11\u9053\uff1a\u201c\u4f60\u6ca1\u6709\uff0c\u6211\u6709\u3002\u6211\u5c31\u662f\u59d3\u91d1\u7684\u3002\u91d1\u94f6\u94dc\u94c1\u9521\u7684\u91d1\uff0c\u4f60\u770b\u8fd9\u4e2a\u59d3\u53ef\u4e0d\u662f\u5f88 \u4fd7\u5f88\u4fd7\u4e48\uff1f\u81f3\u4e8e\u6211\u7684\u540d\u5b57\u4e48\uff0c\u90a3\u5c31\u66f4\u4fd7\u4e86\u3002\u6211\u540d\u53eb\u2018\u9010\u6d41\u2019\uff0c\u968f\u6ce2\u9010\u6d41\u7684\u2018\u9010\u6d41\u2019\u4e8c\u5b57\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u5fc3\u91cc\u6697\u81ea\u5ff5\u9053\uff1a\u201c\u91d1\u9010\u6d41\uff1f\u8fd9\u4e2a\u540d\u5b57\u6211\u53ef\u4ece\u6765\u6ca1\u6709\u542c\u4eba\u8bf4\u8fc7\uff0c\u5947\u602a\uff0c\u4ed6\u6709\u8fd9\u6837\u8d85 \u5353\u7684\u672c\u9886\uff0c\u5e74\u7eaa\u53c8\u8fd9\u6837\u8f7b\uff0c\u6b66\u6797\u4e2d\u7684\u8001\u524d\u8f88\u5bf9\u540e\u8d77\u4e4b\u79c0\u662f\u6781\u4e3a\u6ce8\u610f\u7684\uff0c\u4f55\u4ee5\u6211\u5374\u4ece\u6765\u6ca1\u6709\u542c \u5f97\u957f\u8f88\u8c08\u8d77\u8fd1\u6765\u7684\u6c5f\u6e56\u4e0a\u6709\u8fd9\u6837\u7684\u4e00\u4e2a\u4eba\u3002\u96be\u9053\u4ed6\u4e5f\u662f\u50cf\u6211\u4e00\u6837\uff0c\u4e43\u662f\u521d\u6b21\u51fa\u9053\u4e0d\u6210\uff1f\u201d \u3000\u3000\u5c0f\u53eb\u5316\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u5c5e\u4e8e\u4efb\u4f55\u4e00\u6d3e\uff0c\u6211\u4e5f\u6ca1\u6709\u62dc\u8fc7\u5e08\u7236\u3002\u5f53\u4eca\u4e4b\u4e16\u7684\u5404\u6d3e\u638c\u95e8\uff0c\u53d9 \u8d77\u8f88\u4efd\u81f3\u591a\u662f\u6211\u7684\u5e73\u8f88\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u914d\u505a\u6211\u7684\u5e08\u7236\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u53ea\u5f53\u4ed6\u662f\u80e1\u4e71\u5439\u725b\uff0c\u5fc3\u60f3\uff1a\u201c\u6c5f\u6e56\u4e0a\u7684\u907f\u5fcc\u751a\u591a\uff0c\u4ed6\u65e2\u7136\u4e0d\u613f\u8bf4\u51fa\u4ed6\u7684\u5e08\u95e8\u6765 \u5386\uff0c\u6211\u53c8\u4f55\u5fc5\u8ffd\u67e5\u3002\u4e0d\u8fc7\uff0c\u4ee5\u4ed6\u7684\u672c\u9886\u800c\u8bba\uff0c\u5374\u662f\u7684\u786e\u8db3\u4e0e\u5404\u6d3e\u7684\u4e00\u6d41\u9ad8\u624b\u6297\u8861\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u95ee\u5f97\u5192\u6627\u4e86\uff0c\u4e0d\u8fc7\u6211\u8fd8\u60f3\u8bf7\u91d1\u5144\u4e00\u6869\u4e8b\u60c5\uff1f\u201d\u91d1\u9010\u6d41 \u9053\uff1a\u201c\u4f60\u6211\u65e2\u662f\u5e73\u8f88\u8bba\u4ea4\uff0c\u90a3\u5c31\u4e0d\u5fc5\u5ba2\u6c14\u3002\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u91d1\u5144\uff0c\u4f60\u8bf4\u662f\u4f60\u5c06\u6211\u4ece\u5c01\u5bb6\u6551\u51fa\u6765\u7684\uff0c\u8fd9\u4e2a\u6211\u76f8\u4fe1\u4e86\u3002\u4f46\u4f60\u4f55\u4ee5\u4f1a\u5230\u5c01\u5bb6 \u6551\u6211\uff0c\u60f3\u6765\u662f\u5bf9\u5c01\u5bb6\u7684\u4e3b\u4eba\u6709\u6240\u6000\u7591\uff0c\u8fd9\u624d\u4f1a\u6f5c\u5165\u5c01\u5bb6\u7aa5\u63a2\u7684\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u5728\u5c01\u5bb6\u7684\u82b1\u56ed\u548c\u90a3\u59d3\u6587\u7684\u5c0f\u5b50\u6bd4\u6b66\uff0c\u540e\u6765\u6587\u9053\u5e84\u548c\u5c01\u5b50\u8d85\u51fa\u6765\uff0c \u5c06\u4f60\u8bf7\u8fdb\u53bb\uff0c\u8fd9\u4e9b\u7ecf\u8fc7\u6211\u90fd\u770b\u89c1\u4e86\u3002\u6211\u5c31\u662f\u56e0\u4e3a\u4e0d\u653e\u5fc3\uff0c\u8fd9\u624d\u5077\u5077\u8fdb\u53bb\u63a2\u671b\u4f60\u7684\u3002\u679c\u4e0d\u5176 \u7136\uff0c\u4f60\u771f\u7684\u662f\u4e2d\u4e86\u4ed6\u4eec\u7684\u8be1\u8ba1\u4e86\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u90a3\u4e48\u4ed6\u4eec\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u5f15\u8d77\u4f60\u7684\u7591\u5fc3\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u76f4\u5230\u73b0\u5728\u8fd8\u4e0d\u77e5\u9053\u4ed6\u4eec\u7684\u8eab\u4efd\u3002\u4e5f\u96be\u602a\u4f60\uff0c\u4f60\u82e5\u662f\u77e5\u9053\u7684\u8bdd\uff0c \u4e5f\u5c31\u4e0d\u4f1a\u559d\u4ed6\u4eec\u7684\u6bd2\u9152\u4e86\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u7591\u5fc3\u6ee1\u8179\uff0c\u5fc3\u60f3\uff1a\u201c\u5c01\u5b50\u8d85\u4e0e\u6587\u9053\u5e84\u5bf9\u6211\u6bb7\u52e4\u62db\u5f85\uff0c\u96be\u9053\u4ed6\u4eec\u7adf\u662f\u574f\u4eba\uff0c\u5e03\u4e0b\u5708 \u5957\u8981\u9677\u5bb3\u6211\u4e48\uff1f\u4ee5\u4ed6\u4eec\u7684\u672c\u9886\uff0c\u53c8\u4f55\u987b\u8d39\u5982\u6b64\u673a\u5fc3\uff1f\u201d\u4e8e\u662f\u95ee\u9053\uff1a\u201c\u5c0f\u5f1f\u59d4\u5b9e\u4e0d\u77e5\uff0c\u4ed6\u4eec\u7a76 \u7adf\u662f\u4ec0\u4e48\u8eab\u4efd\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5ffd\u5730\u4f3c\u4e4e\u5bdf\u89c9\u4ec0\u4e48\uff0c\u4f5c\u51fa\u4fa7\u8033\u7ec6\u542c\u7684\u795e\u6c14\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u4e86\u4f60\u4e5f \u4e0d\u4f1a\u76f8\u4fe1\u7684\u3002\u6211\u8ba9\u65c1\u4eba\u8bf4\u7ed9\u4f60\u542c\u3002\u4f60\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u4e0d\u77e5\u91d1\u9010\u6d41\u8981\u5c06\u4ed6\u5e26\u5230\u54ea\u513f\uff0c\u4f46\u4e5f\u53ea\u597d\u8ddf\u7740\u4ed6\u8dd1\uff0c\u8dd1\u4e86\u6570\u5341\u6b65\uff0c\u79e6\u5143\u6d69\u8fd9\u624d\u9690\u7ea6 \u542c\u5f97\u6811\u6797\u91cc\u6709\u811a\u6b65\u58f0\u3002\u91d1\u9010\u6d41\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5077\u542c\u4ed6\u4eec\u8bf4\u8bdd\uff0c\u4e0d\u53ef\u8ba9\u4ed6\u4eec\u542c\u51fa\u58f0\u606f\u3002\u201d\u62c9 \u7740\u79e6\u5143\u6d69\u8dd1\u4e86\u4e00\u7a0b\uff0c\u7a81\u7136\u5e26\u7740\u4ed6\u98de\u8eab\u4e00\u7eb5\uff0c\u4e0a\u4e86\u4e00\u68f5\u5927\u6811\uff0c\u53ea\u662f\u843d\u4e86\u51e0\u7247\u6811\u53f6\uff0c\u8fde\u6811\u679d\u90fd\u6ca1 \u6709\u6447\u52a8\u3002\u4e0a\u4e86\u5927\u6811\uff0c\u79e6\u5143\u6d69\u5df2\u7ecf\u53ef\u4ee5\u770b\u89c1\u6709\u4e24\u4e2a\u6c49\u5b50\u6b63\u5728\u671d\u8fd9\u8fb9\u8d70\u6765\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u4e00\u9ad8\u4e00\u77ee\uff0c\u90fd\u662f\u5927\u7ea6\u4e94\u5341\u5de6\u53f3\u5e74\u7ec4\u3002\u53ea\u542c\u5f97\u524d\u5934\u90a3\u4e2a\u9ad8\u4e2a\u5b50\u8bf4\u9053\uff1a\u201c\u6731\u5927\u54e5\uff0c\u4f60 \u770b\u90a3\u5c0f\u599e\u513f\u4f1a\u4e0d\u4f1a\u8bf4\u8c0e\uff1f\u201d\u540e\u5934\u90a3\u4e2a\u77ee\u5b50\u8bf4\u9053\uff1a\u201c\u6211\u60f3\u8be5\u4e0d\u4f1a\u5427\uff1f\u4ed6\u4eec\u82e5\u662f\u5728\u5bb6\u7684\u8bdd\uff0c\u5c82\u80fd \u4e0d\u89c1\u54b1\u4eec\uff1f\u201d\u90a3\u9ad8\u4e2a\u5b50\u9053\uff1a\u201c\u662f\u5440\uff0c\u8bba\u7406\u4ed6\u4eec\u662f\u5e94\u8be5\u89c1\u54b1\u4eec\u7684\u3002\u59d3\u6587\u7684\u6211\u867d\u7136\u4e0d\u5f88\u719f\uff0c\u4f46\u5c01 \u5b50\u8d85\u5374\u662f\u548c\u54b1\u4eec\u6709\u5341\u5e74\u4ee5\u4e0a\u540c\u4e8b\u4e4b\u8c0a\u7684\uff0c\u6211\u5df2\u7ecf\u901a\u540d\u6c42\u89c1\uff0c\u4ed6\u82e5\u5728\u5bb6\u7684\u8bdd\uff0c\u7406\u5e94\u4eb2\u81ea\u51fa\u8fce\u624d \u662f\u3002\u4e0d\u8fc7\uff0c\u4e5f\u8bb8\u662f\u4e3a\u4e86\u53e6\u4e00\u4e2a\u539f\u56e0\uff0c\u4ed6\u6545\u610f\u907f\u800c\u4e0d\u89c1\u3002\u201d \u3000\u3000\u90a3\u77ee\u5b50\u9053\uff1a\u201c\u4ec0\u4e48\u539f\u56e0\uff1f\u90a3\u9ad8\u4e2a\u5b50\u9053\uff1a\u201c\u4ed6\u6015\u54b1\u4eec\u662f\u6765\u5411\u4ed6\u95ee\u7f6a\u7684\u3002\u5f53\u5e74\u6c13\u5c71\u4e4b\u6218\uff0c\u6b7b \u4e86\u5341\u4e03\u540d\u5927\u5185\u9ad8\u624b\uff0c\u53ea\u6709\u4ed6\u548c\u82cf\u8499\u8131\u9003\uff0c\u82cf\u8499\u56de\u6765\u88ab\u6253\u5165\u5929\u7262\u5173\u4e86\u4e09\u5e74\u3002\u4ed6\u5219\u6839\u672c\u5f03\u804c\u6f5c \u9003\uff0c\u7f6a\u540d\u6bd4\u82cf\u8499\u66f4\u5927\uff0c\u4e5f\u8bb8\u662f\u4ed6\u6015\u8428\u603b\u7ba1\u8fd8\u8bb0\u7740\u5f53\u5e74\u4e4b\u4e8b\uff0c\u6d3e\u54b1\u4eec\u524d\u6765\u7f09\u62ff\u4ed6\u56de\u53bb\u5904\u7f5a\u7684 \u5427\uff1f\u201d \u3000\u3000\u90a3\u77ee\u5b50\u9053\uff1a\u201c\u53ef\u6068\u90a3\u5c0f\u599e\u513f\u6839\u672c\u4e0d\u5bb9\u54b1\u4eec\u591a\u8bf4\uff0c\u542c\u8bf4\u54b1\u4eec\u662f\u6765\u627e\u5979\u7239\u7239\u7684\uff0c\u5979\u53ea\u662f\u4e00 \u53e5\uff1a\u2018\u4e0d\u5728\u5bb6\uff01\u2019\u4e53\u7684\u5c31\u5173\u4e0a\u5927\u95e8\u4e86\u3002\u8981\u4e0d\u662f\u4e3a\u4e86\u6015\u5f97\u7f6a\u5c01\u5927\u54e5\uff0c\u6211\u771f\u60f3\u7834\u95e8\u800c\u5165\uff0c\u628a\u90a3\u5c0f \u599e\u513f\u63b4\u4e24\u5df4\u638c\u3002\u201d \u3000\u3000\u90a3\u9ad8\u4e2a\u5b50\u9053\uff1a\u201c\u4e0d\u5982\u54b1\u4eec\u518d\u56de\u53bb\u4e00\u8d9f\uff0c\u628a\u8bdd\u548c\u4ed6\u4eec\u8bf4\u4e2a\u6e05\u695a\u3002\u201d\u90a3\u77ee\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u5c01\u5b50 \u8d85\u4e0d\u80af\u9732\u9762\uff0c\u518d\u53bb\u4e5f\u662f\u78b0\u9489\u3002\u6211\u770b\u4ed6\u5012\u4e0d\u6700\u4e3a\u4e86\u6015\u54b1\u4eec\u62ff\u4ed6\u95ee\u7f6a\uff0c\u800c\u662f\u4e3a\u6015\u6c5f\u6d77\u5929\u7684\u7f18\u6545\u3002 \u54b1\u4eec\u867d\u7136\u662f\u4ed6\u7684\u8001\u670b\u53cb\uff0c\u4ed6\u4e5f\u62c5\u5fc3\u54b1\u4eec\u4f1a\u5728\u65e0\u610f\u4e4b\u4e2d\u6cc4\u6f0f\u4ed6\u7684\u8e2a\u8ff9\u5440\u3002\u201d \u3000\u3000\u90a3\u9ad8\u4e2a\u5b50\u7b11\u9053\uff1a\u201c\u6587\u9053\u5e84\u5728\u4ed6\u90a3\u513f\uff0c\u4ed6\u8fd8\u6015\u6c5f\u6d77\u5929\uff1f\u8428\u603b\u7ba1\u5c31\u662f\u8981\u54b1\u4eec\u6765\u793c\u8058\u6587\u9053\u5e84 \u7684\uff0c\u4e3a\u7684\u4e5f\u5c31\u662f\u8981\u7528\u6587\u9053\u5e84\u6765\u5bf9\u4ed8\u6c5f\u6d77\u5929\u3002\u82e5\u662f\u5982\u4f60\u6240\u8bf4\uff0c\u5c01\u5b50\u8d85\u4e0e\u6587\u9053\u5e84\u4e24\u4eba\u540c\u5728\u4e00\u8d77\u90fd \u8fd8\u6015\u6c5f\u6d77\u5929\u7684\u8bdd\uff0c\u8fd9\u4efd\u8058\u672d\u5c82\u4e0d\u662f\u9001\u51a4\u6789\u4e86\u3002\u201d \u3000\u3000\u90a3\u77ee\u5b50\u9053\uff1a\u201c\u60f3\u5f53\u5e74\u6587\u9053\u5e84\u7684\u53d4\u7236\u6587\u5ef7\u74a7\u4f55\u7b49\u81ea\u8d1f\uff0c\u5938\u8a00\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\uff0c\u4e0d\u6599\u6c13\u5c71\u4e00 \u6218\uff0c\u4ed6\u7684\u6b66\u529f\u53cd\u800c\u7ed9\u91d1\u4e16\u9057\u5e9f\u6389\u5566\u3002\u6587\u9053\u5e84\u603b\u4e0d\u80fd\u80dc\u8fc7\u4ed6\u5f53\u5e74\u7684\u53d4\u53d4\uff0c\u800c\u6c5f\u6d77\u5929\u5219\u5df2\u5c3d\u5f97\u91d1 \u4e16\u9057\u6240\u4f20\uff0c\u4f9d\u6211\u770b\u6765\uff0c\u53ea\u6015\u6587\u9053\u5e84\u4ecd\u7136\u4e0d\u662f\u6c5f\u6d77\u5929\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u90a3\u9ad8\u4e2a\u5b50\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u80dc\u5f97\u4e86\u4e5f\u597d\uff0c\u80dc\u4e0d\u4e86\u4e5f\u597d\uff0c\u54b1\u4eec\u5949\u4e86\u8428\u603b\u7ba1\u4e4b\u547d\uff0c\u603b\u8981\u4ea4\u5dee\u3002\u201d \u54b1\u4eec\u56de\u53bb\u548c\u4ed6\u8bf4\uff0c\u53ea\u8981\u6587\u9053\u5e84\u6536\u4e0b\u8058\u793c\uff0c\u8fde\u4ed6\u5c01\u5b50\u8d85\u4e5f\u53ef\u5b98\u590d\u539f\u804c\u3002\u4ed6\u8fd8\u6709\u4e0d\u6b23\u7136\u53d7\u547d \u4e48\uff1f\u201d\u90a3\u77ee\u5b50\u9053\uff1a\u201c\u5c01\u5b50\u8d85\u5018\u82e5\u907f\u800c\u4e0d\u89c1\uff0c\u90a3\u53c8\u5982\u4f55\uff1f\u201d\u90a3\u9ad8\u4e2a\u5b50\u9053\uff1a\u201c\u54b1\u4eec\u548c\u4ed6\u7684\u5973\u513f \u8bf4\uff0c\u53eb\u4ed6\u5973\u513f\u8f6c\u544a\u3002\u201d\u90a3\u77ee\u5b50\u9053\u3002\u201d\u5018\u5256\u90a3\u5c0f\u599e\u513f\u8fd8\u662f\u4e0d\u7406\u4f1a\u54b1\u4eec\uff0c\u6839\u672c\u5c31\u4e0d\u5f00\u95e8\u5462\uff1f\u201d\u90a3 \u9ad8\u4e2a\u5b50\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u53ea\u6709\u51fa\u6700\u540e\u4e00\u624b\u7edd\u62db\u4e86\u3002\u628a\u4ed6\u7684\u5973\u513f\u62ff\u4e0b\uff0c\u5c01\u5b50\u8d85\u8fd8\u80fd\u4e0d\u9732\u9762\u4e48\u3002\u201d \u90a3\u77ee\u5b50\u9053\uff1a\u201c\u5018\u82e5\u5c01\u5b50\u8d85\u548c\u6587\u9053\u5e84\u662f\u771f\u7684\u4e0d\u5728\u5bb6\u5462\uff1f\u201d\u90a3\u9ad8\u4e2a\u5b50\u9053\uff1a\u201c\u90a3\u4e5f\u53ef\u4ee5\u8feb\u90a3\u5c0f\u599e\u513f \u8bf4\u51fa\u4ed6\u4eec\u7684\u53bb\u5411\u5440\uff01\u201d \u3000\u3000\u90a3\u77ee\u5b50\u54ac\u4e86\u54ac\u7259\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u65e2\u7136\u4e0d\u6015\u5f97\u7f6a\u5c01\u5927\u54e5\uff0c\u54b1\u4eec\u5c31\u56de\u53bb\u5427\u3002\u201d\u90a3\u9ad8\u4e2a\u5b50\u7b11 \u9053\uff1a\u201c\u5c01\u5b50\u8d85\u5f97\u77e5\u4ed6\u53ef\u4ee5\u5b98\u590d\u539f\u804c\u7684\u6d88\u606f\uff0c\u6b22\u559c\u8fd8\u6765\u4e0d\u53ca\u5462\uff0c\u600e\u4f1a\u602a\u8d23\u54b1\u4eec\uff1f\u201d\u8ba1\u8bae\u5df2\u5b9a\uff0c \u8fd9\u4e24\u4e2a\u6c49\u5b50\u5c31\u56de\u8f6c\u5934\u6765\uff0c\u518d\u5411\u5c01\u5bb6\u8d70\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u8d70\u540e\uff0c\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u542c\u6e05\u695a\u6ca1\u6709\uff0c\u73b0\u5728\u8be5\u660e\u767d\u4e86\u5427\uff1f\u201d\u79e6\u5143\u6d69\u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635 \u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u6587\u9053\u5e84\u548c\u5c01\u5b50\u8d85\u90fd\u662f\u671d\u5ef7\u7684\u9e70\u72ac\uff0c\u55ef\u3002\u6211\u660e\u767d\u4e86\uff0c\u6211\u90fd\u660e\u767d\u4e86\uff01\u201d\u4ed6\u4e0d\u53ea \u660e\u767d\u6587\u3001\u5c01\u4e8c\u4eba\u7684\u8eab\u4efd\uff0c\u8fd8\u660e\u767d\u4e86\u4ed6\u4eec\u4f55\u4ee5\u8981\u7528\u6bd2\u9152\u704c\u9189\u4ed6\uff0c\u5e76\u5265\u6389\u4ed6\u7684\u8863\u88f3\u7684\u539f\u56e0\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u6587\u9053\u5e84\u7236\u5b50\u548c\u5c01\u5b50\u8d85\u4e09\u4eba\uff0c\u6628\u5929\u4e00\u65e9\u5c31\u51fa\u95e8\u4e86\u3002\u5bb6\u4e2d\u7559\u4e0b\u7684 \u786e\u5b9e\u53ea\u662f\u90a3\u5c0f\u599e\u513f\u3002\u55ef\uff0c\u79e6\u5144\u4f60\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u5446\u4e86\u4e00\u5446\uff0c\u9762\u5b54\u901a\u7ea2\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u6211\uff0c\u6211\u60f3\u2026\u2026\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u60f3\u90a3\u5c0f\u599e\u513f \u662f\u4e0d\u662f\uff1f\u201d\u79e6\u65e0\u540e\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u662f\u3002\u662f\u2026\u2026\u201d\u91d1\u9010\u6d41\u9053\u3002\u201d\u600e\u4e48\u53c8\u662f\u53c8\u4e0d\u662f\uff1f\u201d\u79e6\u5143\u6d69\u8bb7\u8bb7 \u8bf4\u9053\u3002\u201d\u6211\uff0c\u6211\u60f3\u54b1\u4eec\u53bb\u770b\u770b\u70ed\u95f9\uff0c\u5982\u4f55\uff1f\u201d\u91d1\u9010\u6d41\u7b11\u9053\u3002\u201d\u6211\u77e5\u9053\u4f60\u662f\u6015\u90a3\u5c0f\u599e\u513f\u5403\u4e8f\u3002 \u597d\u5427\uff0c\u4f60\u65e2\u6709\u62a4\u82b1\u6551\u7f8e\u4e4b\u5fc3\uff0c\u6211\u5f53\u7136\u53ea\u6709\u966a\u4f60\u53bb\u4e86\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u7ed9\u4ed6\u8bf4\u7834\uff0c\u8138\u5b54\u66f4\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5144\u4e0d\u53ef\u8bef\u4f1a\uff0c\u5c0f\u5f1f\u53ea\u662f\u53ef\u601c\u90a3\u4f4d\u5c01\u59d1\u5a18\u3002\u201d\u5979 \u7684\u8a00\u8c08\u4f3c\u4e4e\u8fd8\u7b97\u6b63\u6d3e\uff0c\u800c\u4e14\u5979\u662f\u672a\u66fe\u4e0b\u8fc7\u5c71\u7684\uff0c\u5979\u7239\u7239\u662f\u574f\u4eba\uff0c\u5979\u53ef\u4e0d\u662f\u3002\u201d\u91d1\u9010\u6d41\u7b11 \u9053\u3002\u201d\u4e0d\u7528\u7f57\u54e9\u7f57\u55e6\u7684\u89e3\u91ca\u4e86\uff0c\u8fd9\u5c31\u53bb\u5427\u3002\u201d \u3000\u3000\u4e24\u4eba\u8df3\u4e0b\u5927\u6811\uff0c\u79e6\u5143\u6d69\u7acb\u5373\u65bd\u5c55\u8f7b\u529f\uff0c\u91d1\u9010\u6d41\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u77a7\u597d\u620f\uff0c\u4e0d\u53ef\u8ba9\u4ed6\u4eec\u542c \u51fa\u811a\u6b65\u58f0\u54cd\u3002\u201d\u5f53\u4e0b\uff0c\u4e00\u624b\u633d\u7740\u79e6\u5143\u6d69\uff0c\u98ce\u9a70\u7535\u63a3\u822c\u7684\u5f80\u524d\u98de\u8dd1\uff0c\u79e6\u5143\u6d69\u53ea\u89c9\u8eab\u8f7b\u5982\u71d5\uff0c\u811a \u5e95\u4f3c\u4e4e\u62b9\u4e86\u6cb9\u4f3c\u7684\uff0c\u6ed1\u4e0d\u7559\u8db3\u3002 \u3000\u3000\u79e6\u5143\u6d69\u7684\u8f7b\u529f\u5728\u540c\u95e8\u4e2d\u88ab\u63a8\u4e3a\u7b2c\u4e00\uff0c\u4ed6\u7684\u5e08\u7236\u4e5f\u7ecf\u5e38\u5938\u8d5e\u4ed6\u7684\uff0c\u5982\u4eca\u65b9\u77e5\u5929\u5916\u6709\u5927\uff0c\u4eba \u5916\u6709\u4eba\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u53eb\u5316\u5e26\u7740\u6211\u8dd1\uff0c\u8fd8\u8dd1\u5f97\u5982\u662f\u4e4b\u5feb\uff0c\u4ed6\u82e5\u662f\u4e00\u4e2a\u4eba\u8dd1\uff0c\u53ea\u6015\u6211\u7684\u5e08 \u7236\u4e5f\u8ffd\u4e0d\u4e0a\u4ed6\u3002\u602a\u4e0d\u5f97\u4ed6\u524d\u665a\u6f5c\u5165\u5c01\u5bb6\uff0c\u5c06\u6211\u6551\u4e86\u51fa\u6765\uff0c\u8fde\u6587\u9053\u5e84\u548c\u5c01\u5b50\u8d85\u90a3\u6837\u5927\u6709\u672c\u9886\u7684 \u4eba\uff0c\u90fd\u6ca1\u53d1\u89c9\u3002\u201d \u3000\u3000\u4e0d\u8fc7\u4e00\u4f1a\uff0c\u91d1\u3001\u79e6\u4e8c\u4eba\u5df2\u662f\u53ef\u4ee5\u770b\u89c1\u524d\u5934\u90a3\u4e24\u4e2a\u6c49\u5b50\u7684\u80cc\u5f71\uff0c\u91d1\u9010\u6d41\u653e\u6162\u811a\u6b65\uff0c\u5728\u6811\u6797 \u91cc\u501f\u7269\u969c\u5f62\uff0c\u548c\u524d\u9762\u7684\u4eba\u4fdd\u6301\u5341\u6765\u4e08\u7684\u8ddd\u79bb\u3002\u90a3\u4e24\u4e2a\u6c49\u5b50\u5728\u5c01\u5bb6\u95e8\u524d\u505c\u4e0b\u811a\u6b65\uff0c\u91d1\u3001\u79e6\u4e8c\u4eba \u5219\u8eb2\u5728\u5c71\u5761\u4e0a\u7684\u91ce\u8349\u4e1b\u4e2d\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u6c49\u5b50\u62cd\u4e86\u62cd\u95e8\uff0c\u4ed6\u4eec\u672c\u6765\u62c5\u5fc3\u5c01\u5999\u5ae6\u4e0d\u5f00\u95e8\u7684\uff0c\u53ef\u662f\u5c01\u5999\u5ae6\u5ae6\u5374\u5f00\u95e8\u4e86\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u4e00\u5fc3\u7b49\u5f85\u79e6\u5143\u6d69\u5230\u6765\u6216\u8005\u522b\u4eba\u66ff\u4ed6\u4ee3\u6c42\u89e3\u836f\uff0c\u6240\u4ee5\u53ea\u8981\u6709\u4eba\u6765\u62cd\u95e8\u5979\u90fd\u7ed9\u5f00\u95e8\u3002 \u4e0d\u6599\u5f00\u95e8\u4e00\u770b\uff0c\u53c8\u662f\u521a\u624d\u6765\u8fc7\u7684\u90a3\u4e24\u4e2a\u4eba\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u6012\u9053\uff1a\u201c\u6211\u7239\u7239\u4e0d\u5728\u5bb6\uff0c\u6587\u53d4\u53d4\u4e5f\u8d70\u4e86\uff0c\u6211\u4e0d\u662f\u544a\u8bc9\u4e86\u4f60\u4eec\u7684\u4e48\uff0c\u4f60\u4eec\u600e\u4e48\u53c8\u6765 \u4e86\uff1f\u201d \u3000\u3000\u7acb\u5373\u5c31\u8981\u628a\u5927\u95e8\u5173\u4e0a\u3002\u90a3\u9ad8\u4e2a\u5b50\u9053\uff1a\u201c\u5c01\u59d1\u5a18\uff0c\u4f60\u542c\u6211\u8bf4\uff01\u201d\u53cc\u811a\u4e00\u6491\uff0c\u628a\u5373\u5c06\u5408\u62e2\u7684 \u4e24\u6247\u5927\u95e8\u6491\u5f00\u3002\u5c01\u5999\u5ae6\u6b63\u5728\u5173\u95e8\uff0c\u7ed9\u4ed6\u731b\u529b\u4e00\u6491\u95e8\uff0c\u51e0\u4e4e\u8dcc\u5012\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u5927\u6012\u9053\uff1a\u201c\u4f60\u4eec\u5f53\u6211\u662f\u597d\u6b3a\u8d1f\u7684\u4e48\uff1f\u201d\u5530\u7684\u5c31\u62d4\u51fa\u5251\u6765\u6307\u7740\u90a3\u9ad8\u4e2a\u5b50\u3002\u90a3\u9ad8\u4e2a\u5b50 \u7b11\u9053\uff1a\u201c\u8d24\u4f84\u5973\uff0c\u522b\u62ff\u5200\u5f04\u6756\u7684\u6765\u5413\u552c\u6211\u4eec\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u4f84\u5973\uff1f\u201d\u90a3\u77ee\u5b50\u7b11\u9053\uff1a\u201c\u554a\uff0c\u4f60\u5927\u7ea6\u8fd8\u672a\u77e5\u9053\u6211\u4eec\u662f\u8c01\u5427\uff1f\u6211\u544a \u8bc9\u4f60\uff0c\u6211\u59d3\u6731\uff0c\u540d\u53eb\u864e\u81e3\u3002\u4ed6\u59d3\u8f66\uff0c\u5355\u540d\u4e00\u4e2a\u9510\u5b57\u3002\u4f60\u7239\u7239\u603b\u63d0\u8fc7\u6211\u4eec\u7684\u540d\u5b57\u5427\uff1f\u201d\u5c01\u5999\u5ae6 \u677f\u7740\u8138\u5b54\u9053\uff1a\u201c\u6ca1\u542c\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u8f66\u9510\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4e5f\u8bb8\u4f60\u7239\u7239\u7684\u6765\u5386\u8fde\u4f60\u90fd\u8fd8\u7792\u7740\u5427\uff1f\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u548c \u4f60\u7684\u7239\u7239\u5728\u4e8c\u5341\u5e74\u524d\u90fd\u662f\u5927\u5185\u7684\u536b\u58eb\uff0c\u4f60\u7239\u7239\u540e\u6765\u56e0\u4e8b\u79bb\u804c\uff0c\u8fd9\u624d\u5728\u5f82\u5f95\u5c71\u4e2d\u9690\u5c45\u7684\u3002\u6211\u4eec \u4e09\u4eba\u540c\u4e8b\u4e4b\u65f6\uff0c\u4ea4\u60c5\u80dc\u5982\u5144\u5f1f\uff0c\u6240\u4ee5\u6211\u624d\u6562\u53eb\u4f60\u4e00\u58f0\u4f84\u5973\uff0c\u4f60\u522b\u4ee5\u4e3a\u6211\u662f\u8ba8\u4f60\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u7239\u7239\u548c\u4f60\u4eec\u6709\u4ea4\u8bf7\uff0c\u6211\u53ef\u4e0d\u8ba4\u5f97\u4f60\u4eec\u3002\u6211\u7239\u7239\u4e0d\u5728\u5bb6\uff0c\u7ba1\u4f60\u8bf4\u7684 \u662f\u771f\u662f\u5047\uff0c\u6055\u6211\u53ef\u4e0d\u62db\u5f85\u5566\u3002\u4f60\u8981\u5957\u4ea4\u60c5\uff0c\u627e\u6211\u7239\u7239\u8bf4\u53bb\u3002\u201d \u3000\u3000\u8f66\u9510\u60f3\u4e0d\u5230\u5728\u81ea\u5df1\u8bf4\u660e\u4e86\u8eab\u4efd\u4e4b\u540e\uff0c\u5c01\u5999\u5ae6\u5bf9\u4ed6\u8fd8\u662f\u8fd9\u6837\u7684\u4e0d\u5ba2\u6c14\uff0c\u8f66\u9510\u6014\u4e86\u4e00\u6014\uff0c\u8bf4 \u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u4f60\u544a\u8bc9\u6211\u4f60\u7239\u7239\u548c\u6587\u53d4\u53d4\u662f\u5728\u54ea\u513f\uff1f\u201d\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff0c\u77e5\u9053\u4e5f\u4e0d\u544a\u8bc9 \u4f60\u3002\u201d \u3000\u3000\u6731\u864e\u81e3\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u5c01\u59d1\u5a18\uff0c\u4f60\u53ef\u8981\u77e5\u9053\uff0c\u6211\u4eec\u662f\u8981\u8bf7\u4f60\u7239\u7239\u51fa\u53bb\u505a\u5b98\u7684\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u7a00\u7f55\u3002\u201d\u6731\u864e\u81e3\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u7a00\u7f55\uff0c\u4f60\u7239\u7239\u7a00 \u7f55\u3002\u201d \u3000\u3000\u5c82\u77e5\u5c01\u5999\u5ae6\u6b63\u662f\u56e0\u4e3a\u77e5\u9053\u4e86\u5979\u7239\u7239\u7684\u8eab\u4efd\u4e4b\u540e\u800c\u611f\u5230\u70e6\u607c\uff0c\u5979\u4e00\u53e3\u95f7\u6c14\u65e0\u5904\u53d1\u6cc4\u5c31\u53d1\u6cc4 \u5728\u8fd9\u4e24\u4eba\u8eab\u4e0a\uff0c\u5f53\u4e0b\u5c06\u9752\u94a2\u5251\u4e00\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7239\u7239\u7a00\u7f55\uff0c\u4f60\u5c31\u548c\u6211\u7239\u7239\u8bf4\u53bb\u3002\u4f60\u518d\u7f57\u55e6\uff0c \u6211\u53ef\u8981\u4e0d\u5ba2\u6c14\u4e86\uff01\u4f60\u4eec\u6eda\u4e0d\u6eda\uff1f\u201d \u3000\u3000\u8f66\u9510\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8d24\u4f84\u5973\u8981\u8f83\u8003\u4e3a\u53d4\u7684\u6b66\u529f\u4e48\uff1f\u201d\u7a81\u7136\u5389\u58f0\u4e00\u559d\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211 \u4e5f\u8981\u4e0d\u5bb9\u6c14\u4e86\uff01\u201d\u58f0\u51fa\u638c\u53d1\uff0c\u4e00\u62db\u201c\u6a2a\u9501\u82cd\u6c5f\u201d\uff0c\u7528\u7a7a\u624b\u5165\u767d\u5203\u7684\u529f\u592b\u6765\u6263\u5979\u7684\u624b\u8155\uff0c\u5c31\u8981 \u62a2\u5979\u7684\u5b9d\u5251\u3002 \u3000\u3000\u4e0d\u6599\u5c01\u5999\u5ae6\u7684\u672c\u9886\u867d\u7136\u4e0d\u53ca\u4ed6\u4eec\uff0c\u5374\u4e5f\u4e0d\u662f\u6cdb\u6cdb\u4e4b\u8f88\uff0c\u8f66\u9510\u592a\u8fc7\u8f7b\u654c\uff0c\u7a7a\u624b\u62a2\u5979\u7684\u5251\uff0c \u5c01\u5999\u5ae6\u5251\u950b\u9661\u8f6c\uff0c\u5530\u7684\u4e00\u62db\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff0c\u53cd\u524a\u4ed6\u7684\u624b\u8155\u3002\u8981\u4e0d\u662f\u8f66\u9510\u7f29\u624b\u5f97\u5feb\uff0c\u51e0\u4e4e\u7ed9\u4ed6 \u524a\u7740\u3002 \u3000\u3000\u6731\u864e\u81e3\u9053\uff1a\u201c\u6211\u4eec\u53ef\u6ca1\u5de5\u592b\u548c\u4f60\u7ea0\u7f20\uff0c\u64a4\u5251\uff01\u201d\u4e00\u62db\u201c\u624b\u6325\u6325\u7436\u201d\uff0c\u4e94\u6307\u9760\u62e2\uff0c\u53cd\u624b\u4e00 \u6325\u3002\u5c01\u5999\u5ae6\u7109\u80fd\u62b5\u654c\u5f97\u4e86\u4e24\u4e2a\u597d\u624b\u7684\u5939\u653b\uff0c\u53ea\u89c9\u864e\u53e3\u706b\u8fa3\u8fa3\u7684\u4e00\u9635\u9178\u9ebb\uff0c\u7ed9\u4ed6\u4e94\u6307\u62c2\u4e86\u4e00 \u4e0b\uff0c\u9752\u521a\u5251\u767b\u65f6\u8131\u624b\u98de\u53bb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u628a\u79e6\u5143\u6d69\u4e00\u63a8\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u4e0d\u5feb\u51fa\u53bb\u62a4\u82b1\u6551\u7f8e\uff01\u201d\u79e6\u5143\u6d69\u8eab\u4e0d\u7531\u5df1\u7684\u5411\u524d\u5954\u51fa\uff0c \u8eab\u5f62\u5df1\u9732\uff0c\u4e5f\u5c31\u7d22\u6027\u62d4\u5251\u4e0a\u524d\u4e86\u3002\u53ef\u662f\u91d1\u9010\u6d41\u5374\u5e76\u6ca1\u6709\u968f\u7740\u6765\u3002 \u3000\u3000\u79e6\u5143\u6d69\u559d\u9053\uff1a\u201c\u4e24\u4e2a\u9e70\u722a\u5b59\u6b3a\u8d1f\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u8981\u4e0d\u8981\u8138\uff1f\u201d\u5530\u5530\u4e24\u5251\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357 \u6253\u5317\uff0c\u540c\u65f6\u5206\u88ad\u6731\u3001\u8f66\u4e8c\u4eba\u3002\u628a\u4ed6\u4eec\u4e8c\u4eba\u8feb\u9000\u4e86\u51e0\u6b65\uff0c\u5e78\u4e8f\u4ed6\u6765\u5f97\u53ca\u65f6\uff0c\u5c01\u5999\u5ae6\u5f97\u4ee5\u8131\u51fa\u9b54 \u722a\uff0c\u6731\u864e\u81e3\u51b7\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u8fd9\u91ce\u4e2b\u5934\u79c1\u604b\u4e0a\u6b66\u5f53\u6d3e\u7684\u5251\u5ba2\uff0c\u602a\u4e0d\u5f97\u8fde\u7236\u4eb2\u4e5f\u80cc\u53db \u4e86\u3002\u201d\u5c01\u5999\u5ae6\u6c14\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u65a5\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\u3002\u79e6\u5927\u54e5\u72e0\u72e0\u63cd\u4ed6\uff01\u201d\u5c01\u5999\u5ae6\u89c1\u79e6\u5143\u6d69\u7a81\u7136 \u51fa\u73b0\uff0c\u53c8\u60ca\u53c8\u559c\u3002\u867d\u7136\u751f\u6c14\uff0c\u5fc3\u91cc\u53ef\u662f\u751c\u4e1d\u4e1d\u7684\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5c31\u628a\u201c\u79e6\u5927\u54e5\u201d\u4e09\u4e2a\u5b57\u53eb\u51fa\u6765 \u4e86\u3002 \u3000\u3000\u6731\u3001\u8f66\u4e8c\u4eba\u65e2\u770b\u51fa\u4e86\u79e6\u5143\u6d69\u662f\u6b66\u5f53\u95e8\u4e0b\uff0c\u5f53\u4e0b\u4e5f\u5c31\u4e0d\u6562\u8f7b\u654c\uff0c\u90fd\u4eae\u51fa\u4e86\u5175\u5203\u548c\u79e6\u5143\u6d69\u52a8 \u624b\u3002\u6731\u864e\u81e3\u4f7f\u7684\u662f\u4e00\u5bf9\u864e\u5934\u94a9\uff0c\u529f\u80fd\u4e13\u514b\u5200\u5251\u3002\u8f66\u9510\u4f7f\u7684\u662f\u4e00\u53e3\u539a\u80cc\u5200\uff0c\u5200\u91cd\u529b\u6c89\uff0c\u4f7f\u51fa\u7684 \u201c\u4e94\u864e\u65ad\u95e8\u5200\u6cd5\u201d\u4e5f\u662f\u975e\u540c\u5c0f\u53ef\u3002 \u3000\u3000\u79e6\u5143\u6d69\u7684\u5251\u6cd5\u5341\u5206\u7cbe\u5999\uff0c\u53ef\u662f\u7a76\u7adf\u662f\u7b2c\u4e00\u6b21\u51fa\u9053\uff0c\u800c\u4e14\u529f\u529b\u4e5f\u8fd8\u672a\u591f\u529b\u654c\u4e8c\u4eba\uff0c\u8fc7\u4e86\u4e09 \u5341\u62db\u4e4b\u540e\uff0c\u5c31\u6e10\u6e10\u611f\u5230\u529b\u4e0d\u4ece\u5fc3\u4e86\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u5598\u606f\u5df2\u8fc7\uff0c\u89c1\u79e6\u5143\u6d69\u5f62\u52bf\u4e0d\u5999\uff0c\u4fbf\u62fe\u8d77\u4e86\u9752\u94a2\u5251\uff0c\u4e0a\u524d\u4e0e\u4ed6\u5e76\u80a9\u62b5\u654c\u3002\u8f66\u9510\u6b63\u81ea \u62a1\u5200\u5411\u79e6\u5143\u6d69\u65ab\u4e0b\uff0c\u5de6\u80c1\u9732\u51fa\u7a7a\u95e8\uff0c\u5c01\u5999\u5ae6\u5530\u7684\u4e00\u5251\u523a\u4ed6\u80c1\u4e0b\u7684\u201c\u6108\u6c14\u7a74\u201d\u3002\u6731\u864e\u81e3\u53cc\u94a9\u4e00 \u9519\uff0c\u4ea4\u53c9\u7a7f\u63d2\uff0c\u66ff\u8f66\u9510\u683c\u5f00\u4e86\u5c01\u5999\u5ae6\u7684\u4e00\u5251\u3002 \u3000\u3000\u53ef\u662f\u8f66\u9510\u4e00\u4eba\u5374\u906e\u62e6\u4e0d\u4f4f\u79e6\u5143\u6d69\u7cbe\u5999\u7684\u5251\u62db\uff0c\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u79e6\u5143\u6d69\u8fde\u73af\u4e09\u62db\uff0c\u75be\u5982 \u95ea\u7535\uff0c\u8f66\u9510\u907f\u5f00\u4e86\u524d\u4e24\u62db\uff0c\u540e\u4e00\u62db\u5374\u95ea\u4e0d\u5f00\uff0c\u7ed9\u79e6\u5143\u6d69\u7684\u5251\u5c16\u5728\u4ed6\u7684\u5de6\u81c2\u4e0a\u5212\u5f00\u4e86\u4e00\u9053\u4e94\u5bf8 \u591a\u957f\u7684\u53e3\u5b50\uff0c\u5e78\u800c\u53ea\u662f\u4f24\u7740\u76ae\u8089\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u518d\u4e5f\u6321\u4e0d\u4f4f\u6731\u864e\u81e3\u7684\u62a4\u624b\u94a9\uff0c\u7ed9\u4ed6\u7684\u53cc\u94a9\u4e00\u7ffb\u4e00\u7ede\uff0c\u9752\u94a2\u5251\u51e0\u4e4e\u8131\u624b\u3002\u79e6\u5143\u6d69\u8fde \u5fd9\u79fb\u8f6c\u5251\u950b\uff0c\u66ff\u5c01\u5999\u5ae6\u89e3\u5371\u3002\u6731\u864e\u81e3\u53cd\u624b\u4e00\u52fe\uff0c\u5316\u89e3\u4e86\u79e6\u5143\u6d69\u7684\u62db\u6570\u3002\u5c01\u5999\u5ae6\u5530\u7684\u62bd\u51fa\u5251 \u6765\uff0c\u654c\u4f4f\u4e86\u8f66\u9510\u3002 \u3000\u3000\u8f66\u9510\u53d7\u4f24\u4e4b\u540e\uff0c\u6012\u6c14\u500d\u589e\uff0c\u52bf\u5982\u75af\u864e\uff0c\u51b2\u5411\u5c01\u5999\u5ae6\u547c\u547c\u7684\u5c31\u662f\u8fde\u5288\u4e09\u5200\uff0c\u5c01\u5999\u5ae6\u4ed7\u770b\u8f7b \u7075\u7684\u8eab\u6cd5\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u907f\u5f00\u4e86\u4ed6\u7684\u4e09\u5200\u3002\u6731\u864e\u81e3\u9053\uff1a\u201c\u8f66\u8001\u4e8c\uff0c\u770b\u5728\u5c01\u5927\u54e5\u7684\u4efd\u4e0a\uff0c\u7559\u4ed6\u5973 \u513f\u4e00\u547d\u3002\u201d \u3000\u3000\u8f66\u9510\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u91ce\u4e2b\u5934\u5403\u91cc\u722c\u5916\uff0c\u4e3a\u4e86\u59d3\u79e6\u7684\u8fd9\u5c0f\u5b50\uff0c\u5c45\u7136\u8fde\u5979\u7239\u7239\u7684 \u8001\u670b\u53cb\u4e5f\u6253\u8d77\u6765\u4e86\u3002\u5c01\u5927\u54e5\u82e5\u7136\u77e5\u9053\u6b64\u4e8b\uff0c\u5b9a\u8981\u7ed9\u5979\u6c14\u4e2a\u534a\u6b7b\uff0c\u6211\u5c31\u662f\u4e3a\u4e86\u5c01\u5927\u54e5\u7684\u7f18\u6545\uff0c \u4e5f\u5f97\u6559\u8bad\u6559\u8bad\u5979\u3002\u6b7b\u7f6a\u514d\u4e86\uff0c\u751f\u7f6a\u96be\u9976\u3002\u8fd9\u5c0f\u5b50\u4f24\u4e86\u6211\u7684\u818a\u81c2\uff0c\u6211\u5c31\u65a9\u5979\u7684\u4e00\u6761\u818a\u81c2\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u6c14\u529b\u4e0d\u6d4e\uff0c\u53ea\u4ed7\u7740\u8f7b\u7075\u7684\u8eab\u6cd5\uff0c\u5728\u8f66\u9510\u7684\u5200\u950b\u4e4b\u4e0b\uff0c\u5de6\u7a9c\u53f3\u95ea\uff0c\u83ab\u8bf4\u6beb\u65e0\u8fd8\u624b\u4e4b \u529b\uff0c\u8fde\u62db\u67b6\u4e5f\u662f\u4e0d\u80fd\u3002\u79e6\u5143\u6d69\u53eb\u9053\uff1a\u201c\u5c01\u59d1\u5a18\uff0c\u4f60\u5feb\u8dd1\uff01\u201d\u53ef\u662f\u5c01\u5999\u5ae6\u5374\u4e0d\u80af\u8def\uff0c\u5979\u5fc3\u91cc \u60f3\uff1a\u201c\u6211\u867d\u7136\u6253\u4e0d\u8fc7\u8fd9\u9ad8\u4e2a\u5b50\uff0c\u4f46\u7ed9\u79e6\u5c11\u4fa0\u7275\u5236\u4f4f\u4e00\u4e2a\u654c\u4eba\u4e5f\u662f\u597d\u7684\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u6296\u64de\u7cbe\u795e\uff0c\u628a\u6731\u864e\u81e3\u8feb\u9000\u51e0\u6b65\uff0c\u518d\u6b21\u62a2\u4e0a\u524d\u53bb\uff0c\u4e0e\u5c01\u5999\u5ae6\u4f1a\u5408\u3002\u53cc\u65b9\u4e24\u4e2a\u5bf9\u4e24\u4e2a \u7684\u6df7\u6218\uff0c\u79e6\u5143\u6d69\u5904\u5904\u7167\u987e\u7740\u5c01\u5999\u5ae6\uff0c\u66ff\u5979\u6321\u4f4f\u4e86\u654c\u4eba\u7684\u653b\u51fb\uff0c\u5f62\u52bf\u624d\u597d\u8f6c\u4e00\u4e9b\u3002 \u3000\u3000\u4f46\u4e5f\u7531\u4e8e\u79e6\u5143\u6d69\u5904\u5904\u8981\u7167\u987e\u5c01\u5999\u5ae6\uff0c\u5c01\u5999\u5ae6\u7684\u5371\u9669\u51cf\u5c11\u4e86\uff0c\u4ed6\u7684\u5371\u9669\u5374\u589e\u591a\u4e86\u3002\u597d\u51e0\u6b21 \u9047\u7740\u9669\u62db\u3002\u5c01\u5999\u5ae6\u594b\u4e0d\u987e\u8eab\u7684\u7ed9\u4ed6\u8fdb\u62db\u89e3\u5371\uff0c\u4f46\u7531\u4e8e\u53cc\u65b9\u672a\u80fd\u914d\u5408\u5f97\u597d\uff0c\u5f7c\u6b64\u53c8\u662f\u4e89\u7740\u5e94 \u654c\uff0c\u7ae0\u6cd5\u66f4\u4e71\uff0c\u4e5f\u5c31\u66f4\u7ed9\u4e86\u654c\u4eba\u4ee5\u53ef\u4e58\u4e4b\u673a\u3002 \u3000\u3000\u79e6\u5143\u6d69\u6fc0\u6218\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u4ea6\u5df2\u5927\u6c57\u6dcb\u6f13\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u91d1\u9010\u6d41\u600e\u7684\u8fd8\u5728\u8896\u624b\u65c1\u89c2\uff1f\u201d\u53ef \u662f\u4ed6\u4e0e\u91d1\u9010\u6d41\u53ea\u662f\u521d\u4ea4\uff0c\u5982\u662f\u4e0d\u4fbf\u81ea\u52a8\u5f00\u53e3\uff0c\u5411\u91d1\u9010\u6d41\u6c42\u63f4\u3002 \u3000\u3000\u6731\u864e\u81e3\u7684\u62a4\u624b\u94a7\u6709\u514b\u5236\u5200\u5251\u4e4b\u80fd\uff0c\u53ea\u56e0\u79e6\u5143\u6d69\u7684\u8fde\u73af\u593a\u547d\u5251\u6cd5\u4e43\u662f\u4e0a\u4e58\u7684\u5251\u6cd5\uff0c\u5251\u52bf\u8fde \u7ef5\u4e0d\u65ad\uff0c\u529f\u529b\u53c8\u4e0e\u4ed6\u76f8\u5dee\u4e0d\u8fdc\uff0c\u6545\u6b64\u4ed6\u7684\u62a4\u624b\u94a9\u624d\u4e0d\u80fd\u514b\u5236\u79e6\u5143\u6d69\u3002\u4f46\u5bf9\u5c01\u5999\u5ae6\u53ef\u4e0d\u540c\u4e86\uff0c \u5c01\u5999\u5ae6\u53ef\u4ee5\u907f\u5f00\u8f66\u9510\u7684\u539a\u80cc\u65ab\u5c71\u5200\uff0c\u5374\u96be\u907f\u5f00\u4ed6\u7684\u53cc\u94a9\u52fe\u9501\u3002\u6fc0\u6218\u4e2d\u6709\u4e00\u62db\u7531\u4e8e\u79e6\u3001\u5c01\u4e8c\u4eba \u4e89\u7740\u5e94\u654c\uff0c\u7ed9\u4e86\u6731\u864e\u81e3\u4e00\u4e2a\u53ef\u4e58\u4e4b\u673a\uff0c\u53cc\u94a9\u4e00\u9501\uff0c\u767b\u65f6\u628a\u5c01\u5999\u5ae6\u7684\u5251\u7ede\u4f4f\u3002 \u3000\u3000\u79e6\u5143\u6d69\u8fde\u5fd9\u62a2\u4e0a\u524d\u53bb\uff0c\u62e6\u5728\u5c01\u5999\u5ae6\u7684\u9762\u524d\uff0c\u4e00\u62db\u201c\u94c1\u9501\u6a2a\u6c5f\u201d\uff0c\u5251\u5288\u6731\u864e\u81e3\u7684\u624b\u8155\uff0c\u6731 \u864e\u81e3\u559d\u58f0\u201c\u53bb\uff01\u201d\u53cc\u81c2\u4e00\u632f\uff0c\u5c01\u5999\u5ae6\u7684\u9752\u94a2\u5251\u518d\u4e00\u6b21\u8131\u624b\u98de\u4e0a\u4e86\u534a\u7a7a\u3002 \u3000\u3000\u79e6\u5143\u6d69\u529b\u900f\u5251\u5c16\uff0c\u4f7f\u8db3\u4e86\u52b2\uff0c\u731b\u523a\u8fc7\u53bb\uff0c\u53cc\u65b9\u529f\u529b\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u6731\u864e\u81e3\u7ede\u4e0d\u8131\u79e6\u5143\u6d69\u7684\u957f \u5251\u3002\u53ef\u662f\u8f66\u9510\u4e5f\u5e76\u4e0d\u95f2\u7740\uff0c\u4ed6\u5f97\u7740\u4e86\u8fd9\u4e2a\u7edd\u597d\u7684\u673a\u4f1a\uff0c\u5927\u5200\u62a1\u5706\uff0c\u731b\u5730\u559d\u9053\u3002\u201d\u597d\u5c0f\u5b50\uff0c\u5982 \u4eca\u6211\u8fd8\u4e0d\u62a5\u4f60\u8fd9\u4e00\u5251\u4e4b\u4ec7\uff01\u201d\u547c\u7684\u4e00\u58f0\uff0c\u5927\u5200\u5c31\u5411\u79e6\u5143\u6d69\u5288\u4e0b\u3002 \u3000\u3000\u79e6\u5143\u6d69\u521a\u521a\u62bd\u51fa\u957f\u5251\uff0c\u8fd8\u6765\u4e0d\u53ca\u62db\u67b6\uff0c\u53ea\u89c1\u5200\u5149\u95ea\u95ea\uff0c\u5200\u950b\u5df2\u5288\u5230\u4e86\u4ed6\u7684\u9876\u95e8\u3002\u79e6\u5143\u6d69 \u4e0d\u89c9\u5fc3\u5934\u4e00\u51c9\uff0c\u6697\u81ea\u53eb\u9053\uff1a\u201c\u6211\u547d\u4f11\u77e3\uff01\u201d\u5c01\u5999\u5ae6\u6b64\u65f6\u521a\u521a\u7ad9\u7a33\u811a\u6b65\uff0c\u62fe\u8d77\u5251\u6765\uff0c\u8ddd\u79bb\u8fd8\u5728 \u4e03\u6b65\u4e4b\u5916\uff0c\u83ab\u8bf4\u4ee5\u5979\u7684\u672c\u9886\u65e0\u6cd5\u89e3\u6551\uff0c\u5c31\u662f\u60f3\u8981\u89e3\u6551\uff0c\u4e5f\u662f\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u8bf4\u4e5f\u5947\u602a\uff0c\u8f66\u9510\u7684\u539a\u80cc\u65ab\u5c71\u5200\u53ea\u8981\u843d\u4e0b\u4e09\u5bf8\uff0c\u773c\u770b\u5c31\u53ef\u4ee5\u5288\u788e\u79e6\u5143\u6d69\u7684\u5934\u9885\uff0c\u5374\u7a81\u7136\u5982 \u7740\u4e86\u201c\u5b9a\u8eab\u6cd5\u201d\u4e00\u822c\uff0c\u5200\u950b\u5c31\u5728\u79e6\u5143\u6d69\u7684\u5934\u9876\u505c\u4e86\u4e0b\u6765\u3002\u79e6\u5143\u6d69\u7684\u5251\u6cd5\u4f55\u7b49\u8fc5\u6377\uff0c\u5c31\u5728\u8fd9\u4e00 \u77ac\u4e4b\u95f4\uff0c\u4ed6\u4e0d\u5047\u601d\u7d22\u7684\u4e00\u5251\u523a\u53bb\uff0c\u8f66\u9510\u6ca1\u6709\u65ab\u7740\u79e6\u5143\u6d69\uff0c\u53cd\u800c\u7ed9\u79e6\u5143\u6d69\u201c\u5530\u201d\u7684\u4e00\u5251\uff0c\u4ece\u4ed6 \u7684\u524d\u5fc3\u7a7f\u5165\uff0c\u540e\u5fc3\u7a7f\u51fa\uff0c\u4e00\u547d\u545c\u547c\u3002 \u3000\u3000\u79e6\u5143\u6d69\u62bd\u51fa\u5251\u6765\uff0c\u8fd9\u624d\u89c9\u5f97\u5947\u602a\uff0c\u5c01\u5999\u5ae6\u559c\u51fa\u671b\u5916\uff0c\u8fde\u5fd9\u5954\u6765\u3002\u6731\u864e\u81e3\u89c1\u540c\u4f34\u7a81\u7136\u6bd9 \u547d\uff0c\u5927\u5927\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\uff0c\u9aa4\u5403\u4e00\u60ca\uff0c\u5728\u79e6\u3001\u5c01\u4e8c\u4eba\u5148\u540e\u5939\u653b\u4e4b\u4e0b\uff0c\u6321\u5f97\u4e86\u79e6\u5143\u6d69\u7684\u5251\u62db\uff0c \u5374\u907f\u4e0d\u5f00\u5c01\u5999\u5ae6\u7684\u653b\u523a\uff0c\u5de6\u80c1\u8fde\u7740\u4e24\u5251\uff0c\u8840\u6d41\u5982\u6ce8\u3002 \u3000\u3000\u5e78\u800c\u5c01\u5999\u5ae6\u6c14\u529b\u5f31\uff0c\u8fd9\u4e24\u5251\u4ee4\u4ed6\u53d7\u4e86\u4f24\uff0c\u5374\u8fd8\u672a\u80fd\u81f4\u4ed6\u6b7b\u547d\u3002\u6731\u864e\u81e3\u5927\u53eb\u4e00\u58f0\uff0c\u63b7\u51fa\u62a4 \u624b\u94a9\uff0c\u5c01\u5999\u5ae6\u4e00\u95ea\u95ea\u5f00\uff0c\u6731\u864e\u81e3\u7acb\u5373\u4ece\u7f3a\u53e3\u51b2\u51fa\uff0c\u6ca1\u547d\u98de\u9003\u3002\u79e6\u5143\u6d69\u6b7b\u91cc\u9003\u751f\uff0c\u60ca\u9b42\u65b9\u5b9a\uff0c \u4ed6\u8981\u4e0e\u5c01\u5999\u5ae6\u53d9\u8bdd\uff0c\u4e5f\u5c31\u987e\u4e0d\u5f97\u53bb\u8ffd\u6740\u6731\u864e\u81e3\u4e86\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u7b11\u76c8\u76c8\u5730\u8d70\u4e0a\u7684\u6765\uff0c\u8d5e\u9053\uff1a\u201c\u79e6\u5927\u54e5\uff0c\u597d\u5251\u6cd5\uff01\u201d\u79e6\u5143\u6d69\u9762\u4e0a\u4e00\u7ea2\uff0c\u53eb\u9053\uff1a\u201c\u91d1 \u5927\u54e5\uff0c\u91d1\u5927\u54e5\uff01\u8bf7\u51fa\u6765\u5427\uff01\u5bb9\u5c0f\u5f1f\u5411\u4f60\u9053\u8c22\u3002\u201d\u4ed6\u5fc3\u4e2d\u5df2\u662f\u660e\u767d\uff0c\u521a\u624d\u8d70\u662f\u91d1\u9010\u6d41\u6697\u4e2d\u52a9 \u4ed6\uff0c\u53ef\u662f\u4ed6\u8fde\u53eb\u4e24\u58f0\uff0c\u5374\u542c\u4e0d\u89c1\u91d1\u9010\u6d41\u7b54\u8bdd\u3002\u79e6\u5143\u6d69\u8dd1\u5230\u521a\u624d\u85cf\u533f\u4e4b\u5904\u4e00\u770b\uff0c\u54ea\u91cc\u8fd8\u6709\u91d1\u9010 \u6d41\u7684\u5f71\u5b50\uff1f\u91d1\u9010\u6d41\u65e9\u5df2\u8d70\u4e86\u3002 \u3000\u3000\u79e6\u5143\u6d69\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u98ce\u5c18\u5f02\u4e10\uff0c\u5f53\u771f\u662f\u795e\u51fa\u9b3c\u6ca1\uff01\u201d\u5c01\u5999\u5ae6\u8d70\u6765\uff0c\u6014\u4e86\u4e00 \u6014\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u6709\u670b\u53cb\u540c\u6765\u7684\u5417\uff1f\u201d\u79e6\u5143\u6d69\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u5c31\u662f\u524d\u665a\u4ece\u4f60\u7684\u5bb6\u4e2d\u5c06\u6211\u6551\u4e86\u51fa \u53bb\u7684\u4eba\uff0c\u53ef\u60dc\u4ed6\u73b0\u5728\u5df2\u7ecf\u8d70\u4e86\u3002\u201d\u5c01\u5999\u5ae6\u542c\u4ed6\u63d0\u8d77\u524d\u665a\u4e4b\u4e8b\uff0c\u4e0d\u7531\u5f97\u7c89\u8138\u901a\u7ea2\u3002\u6b63\u662f\uff1a \u3000\u3000\u5f53\u65f6\u5802\u4e0a\u5ba2\uff0c\u4eca\u65e5\u53c8\u91cd\u6765\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u56db\u56de\u3000\u795e\u529f\u96be\u6d4b\u60ca\u9ad8\u5f1f\u3000\u7978\u60a3\u6f5c\u57cb\u95ef\u559c\u7b75 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u56db\u56de\u3000\u795e\u529f\u96be\u6d4b\u60ca\u9ad8\u5f1f\u3000\u7978\u60a3\u6f5c\u57cb\u95ef\u559c\u7b75 \u3000\u3000\u5c01\u5999\u5ae6\u7c89\u8138\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7239\u7239\u9a97\u4f60\u559d\u4e86\u5343\u65e5\u9189\uff0c\u5f53\u65f6\u6211\u53ea\u9053\u662f\u666e\u901a\u7684\u6842\u82b1\u9152\uff0c\u540e\u6765 \u624d\u77e5\u9053\u662f\u5343\u65e5\u9189\u7684\u3002\u6211\uff0c\u6211\u59d4\u5b9e\u6ca1\u6709\u4e0e\u7239\u7239\u540c\u8c0b\u3002\u201d\u79e6\u5143\u6d69\u9053\uff1a\u201c\u6211\u77e5\u9053\u8fd9\u4e0d\u5173\u4f60\u7684\u4e8b\u3002\u8981 \u4e0d\u7136\u6211\u4e5f\u4e0d\u4f1a\u518d\u5230\u4f60\u8fd9\u513f\u6765\u4e86\u3002\u201d\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6211\u6700\u521d\u8fd8\u4ee5\u4e3a\u4f60\u662f\u6765\u5174\u5e08\u95ee\u7f6a\u7684\u5462\u3002\u201d\u79e6\u5143 \u6d69\u9053\uff1a\u201c\u6211\u548c\u91d1\u5927\u54e5\u542c\u5f97\u90a3\u4e24\u4e2a\u9e70\u722a\u5b59\u7684\u79c1\u8bed\uff0c\u6015\u4f60\u5403\u4e8f\uff0c\u8fd9\u624d\u8ddf\u8e2a\u6765\u7684\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u53c8\u662f\u60ed\u6127\uff0c\u53c8\u662f\u6b22\u559c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u539f\u6765\u4f60\u975e\u4f46\u6ca1\u6709\u602a\u6211\uff0c\u8fd8\u5728\u5173\u5fc3\u7740\u6211\u3002\u201d\u4e00 \u5f20\u7ea2\u8138\uff0c\u70e7\u5f97\u66f4\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u867d\u7136\u6ca1\u6709\u540c\u8c0b\uff0c\u4f46\u6211\u7239\u7239\u9a97\u4f60\u559d\u4e86\u836f\u9152\uff0c\u6211\uff0c\u6211\u4e5f\u60ed\u6127\u5f97 \u5f88\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u4e8b\u60c5\u90fd\u5df2\u7ecf\u8fc7\u53bb\u4e86\uff0c\u6211\u4e5f\u6ca1\u6709\u53d7\u5230\u4e1d\u6beb\u7684\u4f24\u635f\uff0c\u4e0d\u5fc5\u518d\u63d0\u4e86\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6211\u7239\u7239\u8bf4\uff0c\u559d\u4e86\u8fd9\u5343\u65e5\u9189\uff0c\u6700\u5c11\u4e5f\u8981\u9189\u4e2a\u4e03\u5929\u4e03\u591c\u7684\u3002\u4f60\u662f\u600e\u4e48\u5f97\u7684\u89e3 \u836f\uff1f\u201d\u5c01\u5999\u5ae6\u6628\u65e5\u53bb\u627e\u89e3\u836f\uff0c\u53d1\u89c9\u89e3\u836f\u5df2\u5931\uff0c\u4e00\u591c\u60f6\u6050\u4e0d\u5b89\uff0c\u4e0d\u77e5\u662f\u5979\u7239\u7239\u53d6\u53bb\u8fd8\u662f\u7ed9\u4eba\u5077 \u53bb\u3002 \u3000\u3000\u79e6\u5143\u6d69\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u539f\u6765\u91d1\u9010\u6d41\u8bf4\u7684\u679c\u7136\u4e0d\u662f\u8c0e\u8bdd\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u90a3\u4f4d\u670b\u53cb\u7ed9\u6211\u670d\u4e86 \u4e00\u9897\u636e\u8bf4\u662f\u7528\u5929\u5c71\u96ea\u83b2\u9971\u5236\u7684\u78a7\u7075\u4e39\u3002\u201d\u5c01\u5999\u5ae6\u89c1\u79e6\u5143\u6d69\u9762\u8272\u5982\u5e38\uff0c\u4e1d\u6beb\u4e0d\u5e26\u75c5\u5bb9\uff0c\u8fd9\u624d\u653e \u4e0b\u4e86\u5fc3\u4e0a\u7684\u4e00\u5757\u5927\u77f3\uff0c\u4f46\u5982\u53c8\u6362\u4e0a\u4e86\u53e6\u4e00\u6837\u60f6\u6050\u4e0d\u5b89\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u4f4e\u5782\u7c89\u9888\uff0c\u8f7b\u63a0\u4e91\u9b13\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u6c42\u4f60\u4e00\u4ef6\u4e8b\u60c5\uff0c\u4e0d\uff0c\u4e0d\u77e5\u53ef\u80af \u5e94\u5141\uff1f\u201d\u79e6\u5143\u6d69\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u4f60\u867d\u7136\u4e0d\u602a\u6211\uff0c\u4f46\u4e00\u5b9a\u662f\u6068\u6211\u7239\u7239\u7684\u4e86\u3002\u662f\u4e48\uff0c\u6211\u7239\u7239\u5b9e\u5728\u4e0d\u7684\u8be5\u8fd9\u6837\u5bf9 \u4f60\u7684\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u6211\u6b63\u60f3\u8bf7\u6559\u59d1\u5a18\uff0c\u4ee4\u5c0a\u4f55\u4ee5\u9a97\u6211\u559d\u4e0b\u90a3\u5343\u65e5\u9189\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6211\u5374\u60f3\u5148\u95ee\u4f60\uff0c\u6c5f\u6d77\u5929\u662f\u5978\u4eba\u8fd8\u662f\u574f\u4eba\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u4ee5\u4fa0\u4e49\u8457\u79f0\u6c5f\u6e56\uff0c\u5929\u4e0b\u540c\u94a6\uff0c\u7109\u80fd\u4e0d\u662f\u5978\u4eba\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6709\u4eba\u8bf4\u4ed6\u662f\u671d\u5ef7\u7684\u53db\u9006\uff0c\u7687\u5e1d\u662f\u4e0d\u80fd\u53cd\u53db\u7684\uff0c\u8fd9\u8bdd\u53ef\u5bf9\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u76b1\u4e86\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u8bdd\u60f3\u5fc5\u662f\u4ee4\u5c0a\u8bf4\u7684\u5427\uff1f\u4ed6\u66fe\u662f\u671d\u5ef7\u7684\u6b66\u5b98\u3002\u96be\u602a\u4ed6\u4f1a\u8bf4\u8fd9 \u6837\u7684\u8bdd\u3002\u4f46\u666e\u5929\u4e0b\u7684\u767e\u59d3\uff0c\u53ea\u8981\u4e0d\u7518\u5fc3\u4e8e\u4f5c\u9791\u5b50\u7684\u5974\u624d\u7684\uff0c\u5219\u8ba4\u4e3a\u8fd9\u8bdd\u662f\u5927\u5927\u7684\u4e0d\u5bf9\u3002\u6ee1\u6d32 \u9791\u5b50\u5360\u593a\u6211\u4eec\u7684\u56fd\u571f\uff0c\u6b3a\u4fae\u6211\u4eec\u7684\u6c49\u4eba\u3002\u8fd9\u6837\u7684\u9791\u5b50\u7687\u5e1d\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u53cd\u53db\u4ed6\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u60f3\u8d77\u4e86\u5e73\u65e5\u5c71\u4e2d\u7684\u730e\u6237\u548c\u5979\u8bf4\u7684\u8bdd\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e66\u4e0a\u867d\u7136\u6709\u2018\u56fd\u4e0d\u53ef\u4e00 \u65e5\u65e0\u541b\u2019\u7684\u8bf4\u8bdd\uff0c\u4f46\u4e5f\u8981\u770b\u662f\u4ec0\u4e48\u6837\u7684\u7687\u5e1d\uff0c\u574f\u7687\u5e1d\u662f\u5e94\u8be5\u53ef\u4ee5\u53cd\u5bf9\u7684\u3002\u201d\u5979\u7684\u7406\u89e3\u529b\u53ea\u80fd \u8fbe\u5230\u8fd9\u6837\u5883\u5730\uff0c\u4e0d\u8fc7\u603b\u7b97\u662f\u5f00\u59cb\u8bc6\u5f97\u5206\u8fa8\u5927\u662f\u5927\u975e\u4e86\u3002 \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u59d1\u5a18\u8ba4\u4e3a\u5982\u4f55\uff0c\u6211\u8bf4\u5f97\u5bf9\u662f\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u5bf9\uff0c\u5f88\u5bf9\u3002\u4e0d\u8fc7\uff0c\u6700\u5148\u8bf4\u6c5f\u5927\u4fa0\u662f\u574f\u4eba\u7684\uff0c\u5374\u662f\u6211\u7684\u6587\u53d4\u53d4\uff0c\u6211\u7239\u7239\u662f\u968f \u58f0\u9644\u548c\u4ed6\u7684\u3002\u201d\u5c01\u5999\u5ae6\u867d\u7136\u4e0d\u9f7f\u7236\u4eb2\u4e4b\u6240\u4e3a\uff0c\u4f46\u6bd5\u7adf\u8fd8\u662f\u60f3\u7ef4\u62a4\u5979\u7684\u7239\u7239\uff0c\u6545\u800c\u5728\u8a00\u8bed\u4e4b \u4e2d\uff0c\u5c3d\u529b\u4e3a\u5979\u7236\u4eb2\u5f00\u8131\u3002 \u3000\u3000\u79e6\u5143\u6d69\u4e0d\u77e5\u6587\u9053\u5e84\u7684\u6765\u5386\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u7684\u7239\u7239\u65e2\u7136\u548c\u90a3\u59d3\u6587\u7684\u662f\u7ed3\u62dc\u5144\u5f1f\uff0c\u4ed6\u4eec\u8bf4\u7684\u5f53 \u7136\u662f\u540c\u6837\u7684\u8bdd\u4e86\u3002\u4f46\u5374\u4e0d\u77e5\u59d1\u5a18\u63d0\u8d77\u6b64\u4e8b\uff0c\u662f\u4f55\u7528\u610f\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6587\u53d4\u53d4\u8bf4\u6c5f\u5927\u4fa0\u662f\u574f\u4eba\uff0c\u53c8\u8bf4\u4ed6\u548c\u6c5f\u5927\u4fa0\u6709\u4ec7\uff0c\u6545\u800c\u8981\u60f3\u53bb\u4e0e\u4ed6\u6bd4\u6b66\uff0c\u4e00 \u96ea\u65e7\u4ec7\u3002\u4ed6\u6015\u8fdb\u4e0d\u4e86\u6c5f\u5bb6\uff0c\u8fd9\u624d\u8bf4\u52a8\u4e86\u6211\u7684\u7239\u7239\uff0c\u8bf7\u6211\u7239\u7239\u5e2e\u5fd9\uff0c\u9a97\u4f60\u559d\u4e0b\u4e86\u4e09\u676f\u5343\u65e5\u9189 \u7684\u3002\u4ed6\u8981\u6587\u80dc\u4e2d\u5192\u5145\u4f60\u7684\u8eab\u4efd\uff0c\u7a7f\u4e0a\u4f60\u7684\u8863\u88f3\uff0c\u53d6\u4e86\u4f60\u7684\u8bf7\u67ec\uff0c\u8fd9\u624d\u65b9\u4fbf\u649e\u8fdb\u6c5f\u5bb6\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u5e76\u4e0d\u77e5\u9053\u4ed6\u4eec\u7684\u5177\u4f53\u8ba1\u5212\uff0c\u53ea\u5c31\u6240\u89c1\u6240\u95fb\u7684\u6765\u8bf4\uff0c\u79e6\u5143\u6d69\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u539f \u6765\u5982\u6b64\uff01\u4f46\u6587\u9053\u5e84\u8981\u60f3\u627e\u6c5f\u5927\u4fa0\u62a5\u4ec7\uff0c\u6211\u6562\u65ad\u5b9a\uff0c\u4efb\u4ed6\u6253\u7684\u4ec0\u4e48\u4e3b\u610f\uff0c\u90fd\u662f\u51b3\u4e0d\u80fd\u5982\u613f\uff01\u201d \u3000\u3000\u5c01\u5999\u5a1f\u9053\uff0c\u201c\u4f46\u6211\u7239\u7239\u8bf4\u4ed6\u7684\u6b66\u529f\u662f\u5929\u4e0b\u7b2c\u4e00\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u7b11\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u7684\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\uff0c\u8fd9\u5374\u662f\u6b66\u6797\u516c\u8ba4\u7684\u3002\u6211\u867d\u7136\u672a\u89c1\u8fc7\u6c5f\u5927\u4fa0\u7684\u6b66 \u529f\uff0c\u4f46\u4f60\u7684\u6587\u53d4\u53d4\u7684\u6b66\u529f\u6211\u662f\u89c1\u8fc7\u7684\uff0c\u4e0d\u9519\uff0c\u5f53\u7136\u8fdc\u8fdc\u975e\u6211\u53ef\u53ca\uff0c\u4f46\u6bd4\u8d77\u6211\u90a3\u4f4d\u59d3\u91d1\u7684\u670b \u53cb\uff0c\u6050\u6015\u4e5f\u5f3a\u4e0d\u4e86\u591a\u5c11\u3002\u4f9d\u6211\u770b\u6765\uff0c\u4ed6\u627e\u6c5f\u5927\u4fa0\u6bd4\u6b66\uff0c\u53ea\u662f\u81ea\u53d6\u5176\u8fb1\u3002\u201d\u79e6\u5143\u6d69\u6bd5\u7adf\u4e5f\u662f\u9605 \u4e16\u672a\u6df1\uff0c\u4e0d\u77e5\u4eba\u5fc3\u9669\u6076\uff0c\u53ea\u5f53\u6587\u9053\u5e84\u662f\u4f9d\u7167\u6c5f\u6e56\u89c4\u77e9\u53bb\u627e\u6c5f\u6d77\u65e0\u6bd4\u6b66\u3001\u62a5\u4ec7\u3002\u56e0\u6b64\uff0c\u542c\u4e86\u5c01 \u5999\u5ae6\u7684\u8bdd\u540e\uff0c\u53cd\u800c\u4e00\u70b9\u4e5f\u4e0d\u62c5\u5fc3\u4e86\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u7d27\u8e59\u5ce8\u7709\uff0c\u8bf4\u9053\uff1a\u201c\u6587\u5bb6\u7236\u5b50\u81ea\u53d6\u5176\u8fb1\uff0c\u6211\u4e0d\u7ba1\u4ed6\uff0c\u6587\u9053\u5e84\u4e5f\u4e0d\u662f\u6211\u7684\u4eb2\u53d4\u53d4\u3002 \u4f46\u662f\uff0c\u6211\u3001\u6211\u7684\u7239\u7239\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u6b64\u5904\uff0c\u79e6\u5143\u6d69\u5df2\u7ecf\u660e\u767d\u4e86\u51e0\u5206\uff0c\u5fc3\u91cc\u60f3\u9053\u3002\u201d\u4f60\u7239\u7239\u66fe\u7ecf\u5f53\u8fc7\u5927\u5185\u536b\u58eb\uff0c\u5018\u82e5\u56e0\u4e86 \u6b64\u4e8b\uff0c\u53d7\u4e86\u90a3\u59d3\u6587\u7684\u8fde\u7d2f\uff0c\u90a3\u4e5f\u662f\u6d3b\u8be5\u3002\u83ab\u8bf4\u6211\u4e0d\u60f3\u5e2e\u4ed6\uff0c\u8981\u60f3\u5e2e\u5fd9\u4e5f\u5e2e\u5fd9\u4e0d\u4e86\u3002\u201d\u4f46\u8fd9\u756a \u8bf4\u8bdd\uff0c\u4ed6\u5374\u4e0d\u65b9\u4fbf\u5f53\u7740\u5c01\u5999\u5ae6\u7684\u9762\u8bf4\u51fa\u6765\u3002\u5f53\u4e0b\u53ea\u80fd\u52c9\u5f3a\u7684\u5b89\u6170\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u65e2\u7136\u53ea\u662f\u6587\u9053 \u5e84\u627e\u6c5f\u5927\u4fa0\u6bd4\u6b66\uff0c\u4f60\u7684\u7239\u7239\u53ea\u8981\u4e0d\u5f3a\u51fa\u5934\uff0c\u60f3\u6765\u6c5f\u5927\u4fa0\u4e5f\u4e0d\u4f1a\u600e\u6837\u4e3a\u96be\u4ed6\u7684\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\u3002\u4e0d\u8fc7\uff0c\u6211\u8fd8\u662f\u5e0c\u671b\u4f60\u5e2e\u4e00\u4e2a\u5fd9\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u4f60\u60f3\u6211\u600e\u4e48\u5e2e\u5fd9\uff1f\u5982\u679c\u662f\u4e0d\u8fdd\u80cc\u6211\u7684\u5e08\u95e8\u7684\u6559\u8bad\u7684\uff0c\u6211\u53ef\u4ee5\u7b54\u5e94\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u5fc3\u91cc\u751a\u4e3a\u96be\u8fc7\uff0c\u9762\u8272\u4e5f\u5927\u4e3a\u5c34\u5c2c\uff0c\u8981\u662f\u4f9d\u5979\u5e73\u65e5\u7684\u813e\u6c14\uff0c\u79e6\u5143\u6d69\u8bf4\u4e86\u8fd9\u6837\u7684\u8bdd\uff0c \u5979\u662f\u51b3\u4e0d\u80af\u518d\u5f00\u53e3\u6709\u6240\u592e\u6c42\u7684\uff0c\u4f46\u6b64\u9645\uff0c\u5979\u4e3a\u4e86\u633d\u6551\u5979\u7684\u7236\u4eb2\uff0c\u53ea\u80fd\u8bf4\u4e86\u5979\u6240\u60f3\u8981\u8bf4\u7684\u8bdd\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u8bf4\u9053\uff1a\u201c\u4f60\u5230\u6c5f\u5bb6\uff0c\u6211\u3001\u6211\u60f3\u8bf7\u4f60\u4e0d\u8981\u7528\u4f60\u672c\u6765\u7684\u540d\u5b57\u3002\u201d\u79e6\u5143\u6d69\u9053\uff1a\u201c\u4e3a\u4ec0 \u4e48\uff1f\u201d\u4ed6\u662f\u4e2a\u5fe0\u539a\u8001\u5b9e\u7684\u4eba\uff0c\u4e00\u65f6\u95f4\u672a\u80fd\u7701\u609f\u5c01\u5999\u5ae6\u7684\u7528\u610f\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6211\u7239\u7239\u8ddf\u4e86\u4ed6\u4eec\u524d\u5f80\u6c5f\u5bb6\uff0c\u4e00\u5b9a\u6bd4\u4f60\u5148\u5230\u3002\u201d\u79e6\u5143\u6d69\u9053\uff1a\u201c\u54e6\uff0c\u6211\u660e\u767d \u4e86\u3002\u6211\u82e5\u8bf4\u51fa\u6211\u7684\u771f\u540d\u5b9e\u59d3\uff0c\u6587\u80dc\u4e2d\u7684\u5047\u5192\u7acb\u5373\u4fbf\u8981\u88ab\u6211\u63ed\u7a7f\u3002\u4ed6\u4eec\u7236\u5b50\u53ea\u6015\u5f53\u573a\u5c31\u8981\u7ed9\u6c5f \u5bb6\u7684\u4eb2\u53cb\u62ff\u4e0b\uff0c\u4ee4\u5c0a\u53ea\u6015\uff0c\u53ea\u6015\u2026\u2026\u201d\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u7684\u7239\u7239\u5f53\u7136\u4e5f\u53d7\u8fde\u7d2f\u3002\u5373\u4f7f\u4e0d \u88ab\u64d2\u62ff\uff0c\u81f3\u5c11\u4e5f\u8981\u8d76\u51fa\u6c5f\u5bb6\uff0c\u4f17\u76ee\u77aa\u77aa\u4e4b\u4e0b\uff0c\u53eb\u4ed6\u3001\u53eb\u4ed6\u4f55\u4ee5\u81ea\u5bb9\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u6587\u80dc\u4e2d\u5192\u5145\u6211\uff0c\u7136\u5219\u6211\u53c8\u5192\u5145\u8c01\u4eba\uff1f\u6211\u6ca1\u6709\u8bf7\u67ec\uff0c\u53c8\u600e\u80fd\u8fdb\u5f97\u53bb\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u5192\u5145\u4ec0\u4e48\u4eba\uff0c\u53ea\u662f\u53e6\u7528\u4e2a\u540d\u5b57\u4e0d\u5c31\u884c\u4e48\uff1f\u4f60\u53ea\u8981\u9732\u51fa\u4e00\u4e24\u624b\u672c\u95e8\u7684 \u6b66\u529f\uff0c\u8fd8\u4f2f\u6c5f\u5bb6\u7684\u4eba\u4e0d\u77e5\u9053\u4f60\u662f\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\uff1f\u4ee5\u6c5f\u5927\u4fa0\u4e0e\u6b66\u5f53\u6d3e\u7684\u4ea4\u60c5\uff0c\u53c8\u600e\u80fd\u4e0d\u8ba9\u4f60\u8fdb \u53bb\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5012\u7b97\u4e0d\u5f97\u662f\u6559\u6211\u8c0e\u8a00\u6b3a\u9a97\u6c5f\u5bb6\u3002\u4f46\u4e3a\u4e86\u4e00\u4e2a\u66fe\u4efb\u671d\u5ef7\u9e70\u72ac\u7684\u4eba\uff0c \u6211\u503c\u4e0d\u503c\u5f97\u5982\u6b64\u505a\u5462\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4f60\u5230\u4e86\u6c5f\u5bb6\uff0c\u8bf7\u4f60\u6084\u6084\u5730\u627e\u7740\u6211\u7684\u7236\u4eb2\uff0c\u4e0d\u7528\u4f60\u8bf4\u4e00\u53e5\u8bdd\uff0c\u4ed6\u4e00\u89c1 \u4e86\u4f60\uff0c\u5b9a\u77e5\u4e8b\u60c5\u4e1a\u5df2\u8d25\u9732\uff0c\u4ed6\u5c31\u4f1a\u5077\u5077\u5730\u8d70\u4e86\u3002\u5728\u4f60\u65e0\u987b\u8d39\u529b\uff0c\u5c31\u53ef\u4ee5\u6551\u4e86\u6211\u7684\u7239\u7239\u3002\u7ecf\u8fc7 \u4e86\u8fd9\u6b21\u6559\u8bad\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u5c31\u4f1a\u6539\u8fc7\u81ea\u65b0\u3002\u6211\uff0c\u6211\u4e5f\u4f1a\u529d\u8c0f\u4ed6\u7684\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u671b\u4e86\u79e6\u5143\u6d69\u4e00\u773c\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u8fd9\u6837\u505a\u662f\u5426\u8fdd\u80cc\u5c0a\u5e08\u7684\u6559\u8bad\uff0c\u8981\u662f\u4f60\u8ba4 \u4e3a\u6709\u8fdd\u5e08\u8bad\u7684\u8bdd\uff0c\u6211\u4e5f\u4e0d\u6562\u52c9\u5f3a\u4e8e\u4f60\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u5bfb\u601d\uff1a\u201c\u5c01\u5b50\u8d85\u5df2\u7ecf\u6d17\u624b\u4e8c\u5341\u5e74\uff0c\u5e08\u95e8\u6559\u8bad\u4e5f\u6709\u4e0e\u4eba\u4e3a\u5584\u4e00\u6761\u3002\u5018\u80fd\u4ee4\u4ed6\u4ece\u6b64\u5411 \u5584\uff0c\u5012\u4e5f\u662f\u4ef6\u597d\u4e8b\u3002\u201d\u5f53\u4e0b\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u79e6\u67d0\u613f\u4e3a\u59d1\u5a18\u6548\u529b\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u559c\u4e0d\u81ea\u80dc\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u79e6\u5c11\u4fa0\u4ee5\u5fb7\u62a5\u6028\uff0c\u55ef\uff0c\u79e6\u5c11\u4fa0\uff0c\u4f60\u8981\u6362\u8fc7\u4e00\u8eab\u8863\u88f3\u5427\u3002 \u6211\u628a\u6587\u80dc\u4e2d\u7684\u8863\u88f3\u7ed9\u4f60\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u7684\u5916\u8863\u5df2\u7ed9\u6587\u80dc\u4e2d\u5265\u53bb\uff0c\u6b64\u65f6\u53ea\u662f\u7a7f\u7740\u4e00\u4ef6\u886c\u886b\uff0c\u521a\u624d\u7ecf\u8fc7\u4e00\u573a\u6fc0\u70c8\u7684\u6253\u6597\uff0c\u65e9 \u5df2\u6495\u7834\u4e86\u597d\u51e0\u5904\uff0c\u88f8\u9732\u4e86\u76ae\u8089\u3002\u79e6\u5143\u6d69\u7ecf\u5979\u63d0\u9192\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u6b63\u8981\u627e\u8863\u88f3\u66ff\u6362\u3002\u4f60 \u544a\u8bc9\u6211\u6587\u80dc\u4e2d\u7684\u623f\u95f4\uff0c\u6211\u81ea\u5df1\u4f1a\u53bb\u627e\u3002\u53cd\u6b63\u4ed6\u5148\u5077\u4e86\u6211\u7684\u8863\u88f3\uff0c\u6211\u4e5f\u4e0d\u59a8\u62ff\u4ed6\u7684\u4e86\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u7684\u8eab\u6750\u4e0e\u6587\u80dc\u4e2d\u5dee\u4e0d\u591a\uff0c\u968f\u4fbf\u6362\u4e86\u4e00\u5957\uff0c\u6b63\u597d\u5408\u9002\uff0c\u79e6\u5143\u6d69\u51fa\u6765\u53ea\u89c1\u5c01\u5999\u5ae6\u6b63\u5728 \u4f4e\u9996\u6c89\u601d\uff0c\u4f3c\u4e4e\u662f\u5fc3\u4e2d\u6709\u751a\u4e3a\u96be\u4e4b\u4e8b\uff0c\u89c1\u79e6\u5143\u6d69\u51fa\u6765\uff0c\u8fd9\u624d\u62ac\u8d77\u5934\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u3001\u4f60\u8981\u8d70 \u4e86\u4e48\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u540e\u5929\u4fbf\u662f\u6c5f\u5927\u4fa0\u5ac1\u5973\u7684\u65e5\u5b50\uff0c\u8bf7\u6055\u6211\u4e0d\u80fd\u4e45\u7559\u4e86\u3002\u201d\u84e6\u5730\u4e00\u7701\uff0c\u8bf4\u9053\uff1a \u201c\u5c01\u59d1\u5a18\uff0c\u4f60\u4e5f\u6050\u6015\u4e0d\u4fbf\u7559\u5728\u5bb6\u4e2d\u4e86\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u70b9\u4e86\u70b9\u5934\uff0c\u6307\u7740\u8f66\u9510\u7684\u90a3\u4e2a\u5c38\u9996\u8bf4\u9053\u3002\u201d\u662f\u5440\uff0c\u6211\u6b63\u5728\u60f3\u7740\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u8fd9\u4e24\u4e2a\u81ea \u79f0\u662f\u6211\u7239\u7239\u8001\u670b\u53cb\u7684\u4eba\uff0c\u8eab\u4efd\u662f\u5927\u5185\u536b\u58eb\uff0c\u5982\u4eca\u4e00\u4e2a\u88ab\u6740\uff0c\u4e00\u4e2a\u9003\u4e86\uff0c\u53ea\u6015\u6b64\u4e8b\u8fd8\u6709\u540e \u60a3\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u6240\u4ee5\u6211\u529d\u4f60\u8fd8\u662f\u8eb2\u4e00\u8eb2\u7684\u597d\u3002\u4f60\u6709\u5730\u65b9\u597d\u53bb\u5417\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u671d\u7740\u4ed6\u79cb\u6ce2\u4e00\u8f6c\uff0c\u9ed8\u9ed8\u65e0\u8bed\uff0c\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u82e5\u662f\u65e0\u4eb2\u53ef\u6295\uff0c\u5230\u90bb\u53bf\u53bb\u4f4f\u51e0\u5929\u4e5f\u597d\u3002\u4e94\u5929\u4e4b\u540e\uff0c\u4f60\u7239\u7239\u603b\u53ef\u4ee5\u56de\u5230\u5bb6 \u4e86\u3002\u90a3\u65f6\u4f60\u518d\u56de\u6765\uff0c\u4f60\u7239\u7239\u53ef\u4ee5\u7ed9\u4f60\u4f5c\u4e3b\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u5927\u5931\u6240\u671b\uff0c\u5979\u539f\u662f\u60f3\u79e6\u5143\u6d69\u5e26\u5979\u8d70\u7684\uff0c\u5373\u4f7f\u5979\u4e0d\u65b9\u4fbf\u540c\u5f80\u6c5f\u5bb6\uff0c\u4e5f\u53ef\u4ee5\u5728\u4e1c\u5e73\u9547 \u4e0a\u7b49\u4ed6\u3002\u4f46\u5979\u5374\u4e0d\u597d\u610f\u601d\u53bb\u6c42\u79e6\u5143\u6d69\u5e26\u5979\uff0c\u5c24\u5176\u521a\u624d\u5979\u6c42\u79e6\u5143\u6d69\u5e2e\u5fd9\u5979\u7684\u7236\u4eb2\uff0c\u79e6\u5143\u6d69\u7b54\u5e94 \u5f97\u5df2\u662f\u76f8\u5f53\u52c9\u5f3a\uff0c\u5c11\u5973\u603b\u6709\u5c11\u5973\u7684\u4e00\u5206\u77dc\u6301\uff0c\u5979\u8fd8\u600e\u80af\u51fa\u53e3\u6c42\u60c5\u3002\u5f53\u4e0b\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e0d\u52b3\u79e6 \u76f8\u516c\u64cd\u5fc3\uff0c\u6211\u81ea\u5df1\u8bbe\u6cd5\u5e94\u4ed8\u4fbf\u662f\u3002\u201d\u79f0\u53f7\u201c\u79e6\u5927\u54e5\u201d\u4e00\u53d8\u800c\u4e3a\u201c\u79e6\u5c11\u4fa0\u201d\uff0c\u518d\u53d8\u800c\u4e3a\u201c\u79e6\u76f8 \u516c\u201d\uff0c\u4e00\u6b21\u6bd4\u4e00\u6b21\u758f\u8fdc\u3002 \u3000\u3000\u79e6\u5143\u6d69\u7684\u60f3\u6cd5\u662f\uff1a\u5c01\u5999\u5ae6\u662f\u4e2a\u4f1a\u6b66\u529f\u7684\u5973\u5b50\uff0c\u72ec\u81ea\u884c\u8d70\u6c5f\u6e56\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u4f55\u51b5\u53ea\u662f\u5230 \u90bb\u53bf\u6682\u907f\u51e0\u5929\uff1f\u4e8c\u6765\u4ed6\u4e5f\u4e0d\u4fbf\u4e0e\u4e00\u4e2a\u5355\u8eab\u5973\u5b50\u540c\u884c\uff0c\u6240\u4ee5\u538b\u6839\u513f\u5c31\u6ca1\u6709\u60f3\u5230\u8981\u9080\u5979\u4f5c\u4f34\u3002\u4ed6 \u5374\u600e\u77e5\u5c01\u5999\u5ae6\u6b64\u65f6\u590d\u6742\u7684\u5fc3\u60c5\uff1f\u5c01\u5999\u5ae6\u53ea\u5f53\u79e6\u5143\u6d69\u662f\u8f7b\u89c6\u5979\uff0c\u5fc3\u91cc\u81ea\u601d\uff1a\u201c\u4eba\u5bb6\u662f\u540d\u95e8\u6b63\u6d3e \u7684\u5f1f\u5b50\uff0c\u600e\u770b\u5f97\u8d77\u4f60\u5982\u6b64\u51fa\u8eab\u7684\u5973\u5b50\uff1f\u4f60\u4e0d\u77e5\u81ea\u91cf\uff0c\u5984\u60f3\u4eba\u5bb6\u628a\u4f60\u5f53\u4f5c\u670b\u53cb\uff0c\u8fd9\u4e0d\u662f\u592a\u53ef\u7b11 \u4e86\u4e48\u3002\u201d\u5979\u6df1\u6df1\u611f\u5230\u5fc3\u5e95\u7684\u96be\u582a\uff0c\u795e\u8272\u4e5f\u5c31\u4e0d\u7981\u8981\u51b7\u51b7\u6de1\u6de1\u4e86\u3002 \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u59d1\u5a18\u8bf7\u81ea\u73cd\u91cd\u4e86\u3002\u201d\u4ed6\u8d70\u51fa\u5c01\u5bb6\uff0c\u5fc3\u60c5\u4e5f\u4e0d\u81ea\u89c9\u6709\u4e9b\u513f\u6005\u60af\uff0c\u60f3 \u9053\uff1a\u201c\u4f46\u613f\u5979\u80fd\u5f97\u5e73\u5b89\u3002\u201d\u54ce\uff0c\u6211\u8fd9\u6b21\u6551\u4eba\u6ca1\u6709\u6551\u5f7b\u5e95\uff0c\u8fd9\u4e5f\u662f\u65e0\u53ef\u5948\u4f55\u3002\u201d\u4ed6\u4e00\u770b\u65e5\u5934\u5df2 \u5c06\u8fd1\u5348\uff0c\u5fd9\u7740\u8981\u8d76\u5f80\u4e1c\u5e73\uff0c\u53ea\u5f97\u628a\u5c01\u5999\u5ae6\u7684\u5f71\u5b50\u538b\u4e0b\u53bb\uff0c\u4e13\u5fc3\u8d76\u8def\u3002 \u3000\u3000\u8d70\u5230\u5c71\u4e0b\uff0c\u5ffd\u89c1\u91d1\u9010\u6d41\u61d2\u6d0b\u6d0b\u5730\u8eba\u5728\u8349\u5730\u4e0a\u3002\u79e6\u5143\u6d69\u5927\u559c\u9053\uff1a\u201c\u91d1\u5927\u54e5\uff0c\u4f60\u8fd8\u6ca1\u8d70\uff1f\u201d \u91d1\u9010\u6d41\u9053\uff1a\u201c\u5514\uff0c\u6211\u5df2\u7ecf\u7761\u4e86\u4e00\u4e2a\u5927\u89c9\u3002\u4f60\u5012\u6765\u5f97\u65e9\u5440\uff0c\u5c45\u7136\u820d\u5f97\u79bb\u5f00\u90a3\u4e48\u6807\u81f4\u7684\u5927\u59d1\u5a18\u4e86 \u4e48\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u91d1\u5927\u54e5\u8bf4\u7b11\u4e86\uff0c\u6211\u627e\u8863\u88f3\u66ff\u6362\uff0c\u803d\u6401\u4e86\u4f1a\u513f\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u600e\u4e48\u6837\uff0c\u5982\u4eca\u4f60\u53ef\u660e\u767d\u4e86\u4e48\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u660e\u767d\u4e86\uff0c\u591a\u8c22\u91d1\u5927\u54e5\u76f8\u6551\u4e4b\u5fb7\u3002\u201d\u8bf4\u7f62\u606d\u606d\u656c\u656c\u7684\u5411\u91d1\u9010\u6d41\u78d5\u4e86\u4e09\u4e2a\u54cd \u5934\u3002\u4ed6\u60f3\u8d77\u81ea\u5df1\u8d77\u521d\u8fd8\u53ea\u5f53\u91d1\u9010\u6d41\u662f\u80e1\u4e71\u5439\u725b\uff0c\u5982\u4eca\u624d\u77e5\u9053\u91d1\u9010\u6d41\u771f\u7684\u662f\u4ed6\u7684\u6551\u547d\u6069\u4eba\uff0c\u5fc3 \u91cc\u5341\u5206\u60ed\u6127\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4f38\u51fa\u4e86\u624b\uff0c\u4f3c\u4e4e\u60f3\u8981\u62c9\u4ed6\u8d77\u6765\uff0c\u5374\u53c8\u505c\u4f4f\u7b11\u9053\uff1a\u201c\u4e5f\u7f62\uff0c\u4f60\u8fd9\u4e09\u4e2a\u54cd\u5934\uff0c\u6211\u4e5f\u8fd8 \u53ef\u4ee5\u53d7\u5f97\u8d77\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u91d1\u5927\u54e5\u4e0a\u54ea\u513f\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u4e0a\u54ea\u513f\uff1f\u201d\u79e6\u5143\u6d69\u9053\uff1a\u201c\u6211\u5f80\u6c5f\u5927\u4fa0\u90a3 \u513f\u559d\u4ed6\u5973\u513f\u51fa\u9601\u7684\u559c\u9152\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u54ea\u4e2a\u6c5f\u5927\u4fa0\uff1f\u662f\u6c5f\u6d77\u5929\u4e48\uff1f\u201d\u79e6\u5143\u6d69\u9053\u3002\u201d\u4e0d\u9519\u3002\u201d\u5fc3\u91cc\u5947\u602a\uff0c\u6c5f\u6e56\u4e0a\u8bf4 \u8d77\u201c\u6c5f\u5927\u4fa0\u201d\u4e09\u5b57\u65e0\u4eba\u4e0d\u77e5\u9053\u662f\u6c5f\u6d77\u5929\uff0c\u8fd9\u5c0f\u53eb\u5316\u5374\u8fd8\u8981\u591a\u6b64\u4e0a\u4e00\u5411\uff0c\u800c\u767e\u5c45\u7136\u6562\u76f4\u547c\u6c5f\u6d77 \u5929\u4e4b\u540d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\u3002\u201d\u54e6\uff0c\u6c5f\u6d77\u5929\u5c45\u7136\u6709\u8fd9\u6837\u5927\u7684\u5973\u513f\u53ef\u4ee5\u51fa\u5ac1\u4e86\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u5fc3\u91cc\u6697\u7b11\uff1a\u201c\u6c5f\u5927\u4fa0\u7684\u5973\u513f\u5e74\u7eaa\u53ea\u6015\u6bd4\u4f60\u8fd8\u957f\u4e00\u4e24\u5c81\u5462\uff0c\u4f60\u7adf\u7136\u4e00\u526f\u501a\u8001\u5356\u8001\u7684 \u795e\u6c14\u3002\u201d\u6b64\u65f6\u4ed6\u867d\u7136\u77e5\u9053\u91d1\u9010\u6d41\u8bf4\u7684\u6551\u4ed6\u4e4b\u4e8b\u4e0d\u662f\u5439\u725b\uff0c\u4f46\u5374\u8ba4\u4e3a\u4ed6\u52a8\u8f84\u628a\u4eba\u5bb6\u5f53\u4f5c\u665a\u8f88\uff0c \u8fd9\u8fd8\u662f\u5439\u725b\u65e0\u7591\u3002 \u3000\u3000\u79e6\u5143\u6d69\u5fcd\u4e0d\u4f4f\u5632\u4ed6\u4e24\u53e5\uff1a\u201c\u91d1\u5927\u54e5\uff0c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u83ab\u975e\u6c5f\u5927\u4fa0\u4e5f\u662f\u4f60\u7684\u665a\u8f88\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u6211\u7684\u665a\u8f88\uff0c\u6211\u4e5f\u4e0d\u662f\u4ed6\u7684\u665a\u8f88\uff0c\u9a6c\u9a6c\u864e\u864e\uff0c\u7b97\u4f5c\u5e73\u8f88\u597d\u5566\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u6697\u6697\u6447\u5934\uff0c\u60f3\u9053\u3002\u201d\u5404\u6d3e\u638c\u95e8\uff0c\u81f3\u591a\u4e5f\u53ea\u80fd\u4e0e\u6c5f\u5927\u4fa0\u5e73\u8f88\u8bba\u6587\uff0c\u4f60\u5c45\u7136\u4e5f\u4e0e\u4ed6\u6273 \u4f5c\u5e73\u8f88\u3002\u4e0d\u8fc7\uff0c\u4e5f\u8fd8\u7b97\u597d\uff0c\u4f60\u4e0d\u6562\u4ee5\u4ed6\u7684\u957f\u8f88\u81ea\u5c45\uff0c\u8fd9\u725b\u76ae\u8fd8\u4e0d\u7b97\u5439\u5f97\u592a\u5927\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4ecd\u662f\u4e00\u526f\u61d2\u6d0b\u6d0b\u7684\u795e\u6c14\uff0c\u6839\u672c\u4e0d\u7406\u4f1a\u4ed6\u60f3\u4e9b\u4ec0\u4e48\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u53bb\u559d\u6c5f\u6d77\u5929\u5ac1 \u5973\u7684\u559c\u9152\uff0c\u5999\u6781\uff0c\u5999\u6781\uff01\u201d\u79e6\u5143\u6d69\u9053\uff1a\u201c\u600e\u4e48\u5999\u6781\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u6709\u597d\u51e0\u5929\u6ca1\u6709\u5403\u9971\uff0c \u6b63\u597d\u8ddf\u4f60\u5230\u6c5f\u6d77\u5929\u90a3\u513f\uff0c\u5927\u5927\u5403\u4ed6\u4e00\u987f\uff0c\u6211\u548c\u4f60\u540c\u53bb\uff0c\u4f60\u505a\u4e00\u4efd\u8d3a\u793c\uff0c\u4e24\u4e2a\u4eba\u5403\u4ed6\u4e5f\u53ef\u4ee5 \u5427\uff1f\u6211\u60f3\u4ed6\u603b\u4e0d\u597d\u610f\u601d\u62d2\u6211\u5165\u5e2d\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u91d1\u5144\u4e0e\u6c5f\u5927\u4fa0\u53ef\u662f\u76f8\u8bc6\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u77e5\u9053\u6b64\u4eba\uff0c\u6ca1\u6709\u89c1\u8fc7\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\u3002\u201d\u91d1\u5144\u540c\u53bb\uff0c\u5c0f\u5f1f\u6c42\u4e4b\u4e0d\u5f97\u3002\u53ea\u662f\u91d1\u5144\u8fd9\u4e2a\u6a21\u6837\u524d\u5f80\uff0c\u6050\u6015\u2026\u2026\u201d\u4ed6\u8fd8\u6ca1\u8bf4 \u5b8c\uff0c\u91d1\u9010\u6d41\u5c31\u6253\u65ad\u4ed6\u7684\u8bdd\u9053\uff1a\u201c\u600e\u6837\uff1f\u8d5a\u6211\u8863\u88f3\u7834\u70c2\uff1f\u5acc\u6211\u80ae\u810f\uff1f\u5acc\u6211\u662f\u4e2a\u8eab\u4e0a\u5e26\u6709\u81ed\u6c14\u7684 \u5c0f\u53eb\u5316\uff1f\u6c5f\u6d77\u5929\u96be\u9053\u7adf\u662f\u4e2a\u5acc\u8d2b\u7231\u5bcc\u7684\u4eba\u4e48\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u6c5f\u5927\u4fa0\u597d\u5ba2\uff0c\u5929\u4e0b\u7686\u77e5\uff0c\u91d1\u5144\u5982\u6b64\u672c\u9886\uff0c\u5c82\u6709\u4e0d\u914d\u4f5c \u6c5f\u5927\u4fa0\u5ba2\u4eba\u4e4b\u7406\uff1f\u4e0d\u8fc7\uff0c\u6253\u626e\u5f97\u6574\u9f50\u4e00\u4e9b\uff0c\u8fd9\u4e5f\u662f\u5bf9\u4e3b\u4eba\u7684\u606d\u656c\u3002\u91d1\u5144\uff0c\u5230\u524d\u9762\u5c0f\u9547\uff0c\u5c0f\u5f1f \u66ff\u4f60\u4e70\u4e00\u5957\u65b0\u8863\u5982\u4f55\uff1f\u4f60\u7406\u4e00\u7406\u53d1\uff0c\u7528\u4e0d\u4e86\u591a\u5c11\u65f6\u95f4\uff0c\u54b1\u4eec\u665a\u4e0a\u591a\u8dd1\u4e9b\u8def\uff0c\u660e\u5929\u8fd8\u662f\u53ef\u4ee5\u8d76 \u4e0a\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u662f\u559c\u6b22\u4ee5\u672c\u6765\u9762\u76ee\u793a\u4eba\u4f55\u5fc5\u4f5c\u4f2a\uff1f\u201d\u8bf4\u7f62\uff0c\u6293\u8d77\u4e00 \u628a\u6c61\u6ce5\uff0c\u7d22\u6027\u53cd\u628a\u9762\u76ae\u6d82\u5f97\u66f4\u810f\u4e00\u4e9b\uff0c\u6c61\u624b\u4e00\u6293\u5934\u53d1\uff0c\u53c8\u628a\u5934\u53d1\u5f04\u5f97\u66f4\u4e71\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6211 \u5c31\u662f\u8fd9\u526f\u6837\u5b50\u53bb\uff0c\u4f60\u82e5\u662f\u6015\u6211\u4e22\u4f60\u7684\u8138\uff0c\u4f60\u6211\u5c31\u5404\u8d70\u5404\u7684\uff0c\u4e0d\u7528\u4f60\u966a\u6211\u4e86\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u5fc3\u91cc\u6697\u7b11\uff1a\u201c\u4f60\u6d82\u6c61\u4e86\u9762\u5b54\uff0c\u8fd9\u4e0d\u6b63\u662f\u63a9\u9970\u4e86\u672c\u6765\u9762\u76ee\uff1f\u201d\u4f46\u4ed6\u6015\u91d1\u9010\u6d41\u751f\u6c14\uff0c \u5374\u53ea\u5f97\u8bf4\u9053\uff1a\u201c\u662f\uff0c\u662f\u3002\u91d1\u5144\u4e43\u98ce\u5c18\u5f02\u58eb\uff0c\u4f55\u5728\u4e4e\u5916\u8868\u8863\u51a0\uff1f\u5c0f\u5f1f\u4fd7\u4eba\u4e4b\u89c1\uff1a\u8bf4\u9519\u4e86\u8bdd\uff0c\u8fd8 \u671b\u91d1\u5144\u6d77\u6db5\u3002\u201d\u91d1\u9010\u6d41\u53cc\u773c\u4e00\u7ffb\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u4f60\u662f\u96c5\u4e5f\u597d\uff0c\u4fd7\u4e5f\u597d\uff0c\u6211\u53ea\u6c42\u6709\u5f97\u5927\u5403\u4e00 \u987f\u3002\u563f\uff0c\u563f\uff0c\u6211\u7684\u9f3b\u5b50\u5df2\u7136\u95fb\u5230\u6c5f\u5bb6\u7684\u9152\u8089\u9999\u4e86\u3002\u8d70\uff0c\u5feb\u8d70\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u4e00\u8def\u62c5\u5fc3\u7740\u4e24\u4ef6\u4e8b\u60c5\uff0c\u4e00\u662f\u6050\u6015\u8bef\u4e86\u65f6\u5019\uff0c\u5018\u82e5\u6587\u9053\u5e84\u5728\u4ed6\u4eec\u5230\u8fbe\u6c5f\u5bb6\u4e4b\u524d\uff0c\u5df2 \u7ecf\u5411\u6c5f\u6d77\u5929\u6311\u6218\uff0c\u90a3\u4e48\u5c01\u5b50\u8d85\u53ea\u6015\u4e5f\u96be\u514d\u53d7\u7275\u7d2f\u3002\u4ed6\u662f\u53d7\u4e86\u5c01\u5999\u5ae6\u4e4b\u8bf7\u8981\u633d\u6551\u5979\u7684\u7236\u4eb2\u7684\u3002 \u82e5\u662f\u4e0d\u80fd\u53ca\u65f6\u963b\u6b62\uff0c\u5fc3\u4e2d\u96be\u514d\u4e0d\u5b89\u3002\u7b2c\u4e8c\u4ef6\u662f\u6050\u6015\u6c5f\u5bb6\u7684\u4eba\u4e0d\u80af\u653e\u91d1\u9010\u6d41\u8fdb\u53bb\uff0c\u4ee5\u91d1\u9010\u6d41\u7684 \u813e\u6c14\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u5927\u95f9\u4e00\u573a\u3002\u90a3\u4e48\u4e5f\u5c31\u66f4\u96be\u4ee5\u4e3a\u60c5\u4e86\u3002 \u3000\u3000\u672c\u6765\u4ed6\u4eec\u662f\u53ef\u4ee5\u5728\u6b63\u65e5\u65e9\u4e0a\u5230\u7684\uff0c\u56e0\u4e3a\u79e6\u5143\u6d69\u5728\u5c01\u5bb6\u803d\u6401\u4e86\u534a\u5929\uff0c\u8def\u4e0a\u91d1\u9010\u6d41\u53c8\u5230\u5927\u6237 \u4eba\u5bb6\u5077\u4e86\u4e24\u6b21\u9152\u8089\u6765\u5403\uff0c\u79e6\u5143\u6d69\u8981\u7528\u94f6\u94b1\u66ff\u4ed6\u4e70\u9152\u4ed6\u4e5f\u4e0d\u80af\u7b54\u5e94\uff0c\u8bf4\u662f\u53eb\u5316\u5b50\u8981\u82b1\u94b1\u4e70\u9152\u98df \u5c82\u975e\u7b11\u8bdd\uff0c\u8ba8\u4e0d\u5230\u5c31\u53ea\u6709\u5077\u3002 \u3000\u3000\u79e6\u65e0\u6d69\u78b0\u4e0a\u8fd9\u6837\u602a\u813e\u6c14\u7684\u4e00\u4e2a\u4eba\uff0c\u771f\u662f\u7ed9\u4ed6\u5f04\u5f97\u557c\u7b11\u7686\u975e\uff0c\u5374\u53c8\u4e0d\u53d6\u8fdd\u62d7\u4ed6\uff0c\u53ea\u597d\u6b47\u4e86 \u4e24\u56de\uff0c\u7b49\u4ed6\u5077\u4e86\u9152\u8089\u51fa\u6765\u5206\u98df\u3002\u5c31\u8fd9\u6837\u7684\u4e00\u518d\u803d\u6401\uff0c\u5c3d\u7ba1\u79e6\u5143\u6d69\u5df2\u662f\u52a0\u5feb\u811a\u6b65\uff0c\u5230\u8fbe\u6c5f\u5bb6\u4e4b \u65f6\uff0c\u5df2\u662f\u65e5\u5934\u8fc7\u5348\u3002 \u3000\u3000\u6c5f\u5bb6\u7684\u77e5\u5ba2\u770b\u89c1\u79e6\u5143\u6d69\u4e0e\u4e00\u4e2a\u80ae\u810f\u7684\u5c0f\u53eb\u5316\u540c\u6765\uff0c\u9887\u4e3a\u60ca\u5f02\u3002\u91d1\u9010\u6d41\u504f\u4e0d\u201c\u8bc6\u76f8\u201d\uff0c\u4e00 \u4e2a\u4eba\u5c31\u5148\u62a2\u4e0a\u524d\u53bb\u3002\u770b\u95e8\u7684\u628a\u624b\u4e00\u62e6\uff0c\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u8bf7\u6012\u5c0f\u7684\u773c\u62d9\uff0c\u8ba4\u4e0d\u5f97\u8d35\u5ba2\u3002\u4e0d\u77e5\u53ef\u5426 \u8d50\u793a\u6211\u4eec\u4e3b\u4eba\u6240\u53d1\u7684\u8bf7\u67ec\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7ffb\u8d77\u4e00\u53cc\u767d\u773c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u8bf7\u67ec\uff1f\u6211\u4e0d\u662f\u8d35\u5ba2\uff0c\u6211\u53ea\u77e5\u9053\u53eb\u5316\u5b50\u8ba8\u996d\u662f\u4ece\u6765\u4e0d\u7528 \u8bf7\u67ec\u7684\uff01\u201d\u770b\u95e8\u7684\u5fcd\u7740\u6c14\u9053\uff1a\u201c\u5c0f\u54e5\u8bf4\u7b11\u4e86\u3002\u4f46\u65e2\u6ca1\u6709\u8bf7\u67ec\uff0c\u90a3\u5c31\u2026\u2026\u201d\u8fd9\u8fd8\u662f\u56e0\u4e3a\u770b\u95e8\u7684 \u77e5\u9053\u6c5f\u6d77\u5929\u6027\u5e73\u559c\u5ba2\uff0c\u8981\u4e0d\u7136\u65e9\u5c31\u628a\u8fd9\u201c\u5c0f\u53eb\u5316\u201d\u8f70\u4e86\u51fa\u53bb\uff0c\u4e0d\u4f1a\u5bf9\u4ed6\u5982\u6b64\u5ba2\u6c14\u4e86\u3002 \u3000\u3000\u90a3\u5f53\u77e5\u5ba2\u7684\u662f\u9093\u5c71\u6d3e\u7684\u4e00\u4e2a\u5f1f\u5b50\uff0c\u8f83\u6709\u89c1\u8bc6\uff0c\u4f46\u4e5f\u6349\u6478\u4e0d\u900f\u91d1\u9010\u6d41\u662f\u771f\u7684\u53eb\u5316\u8fd8\u662f\u5047\u7684 \u53eb\u5316\uff0c\u91d1\u9010\u6d41\u9053\uff1a\u201c\u90a3\u5c31\u600e\u6837\uff1f\u201d\u77e5\u5ba2\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u6837\uff0c\u4e0d\u8fc7\u2026\u2026\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u90a3\u5f53\u77e5\u5ba2\u7684\u53c8\u662f\u5c34\u5c2c\uff0c\u53c8\u662f\u7740\u607c\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u7684\u6765\u4e86\u8fd9\u6837\u4e00\u4e2a\u4e0d\u901a\u4eba\u60c5\u7684\u53eb\u5316\u3002\u201d\u4ed6\u7684 \u610f\u601d\u662f\u5e0c\u671b\u91d1\u9010\u6d41\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u5018\u82e5\u771f\u662f\u53eb\u5316\u5b50\u7684\u8bdd\uff0c\u90a3\u5c31\u5e94\u8be5\u7559\u5728\u95e8\u5916\uff0c\u7b49\u5019\u4e3b\u4eba\u5bb6\u5206\u6d3e \u9152\u8089\uff1b\u82e5\u7136\u662f\u6709\u6765\u5386\u7684\u8bdd\uff0c\u90a3\u4e5f\u5e94\u8be5\u628a\u6765\u5386\u8bf4\u660e\u3002\u8fd9\u756a\u8bdd\u5f53\u77e5\u5ba2\u7684\u4e0d\u4fbf\u76f4\u8bf4\u51fa\u6765\uff0c\u53ea\u80fd\u541e\u541e \u5410\u5410\u7684\u6697\u793a\u3002\u504f\u504f\u91d1\u9010\u6d41\u201c\u4e0d\u8bc6\u76f8\u201d\uff0c\u975e\u201c\u6253\u70c2\u6c99\u94a2\u95ee\u5230\u5e95\u201d\u4e0d\u53ef\u3002 \u3000\u3000\u79e6\u5143\u6d69\u8fde\u5fd9\u4e0a\u524d\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u662f\u6b66\u5f53\u6d3e\u5f1f\u5b50\u3002\u8fd9\u4f4d\u91d1\u5144\u662f\u6211\u7684\u597d\u53cb\u3002\u201d\u77e5\u5ba2\u7684\u542c\u8bf4\u662f\u6b66 \u5f53\u5f1f\u5b50\uff0c\u8138\u4e0a\u4e0d\u89c9\u53c8\u9732\u51fa\u8be7\u5f02\u4e4b\u8272\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u8fd9\u4e2a\u4eba\u662f\u600e\u4e48\u7684\uff1f\u8981\u561b\u8bf7\u6211\u4eec\u8fdb\u53bb\uff0c\u8981\u561b\u628a\u6211\u4eec\u8d76\u8dd1\u3002\u7f57\u54e9\u7f57\u5506\u5730 \u95ee\u4e2a\u4e0d\u4f11\u7b97\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5f53\u77e5\u5ba2\u7684\u5fcd\u4f4f\u6c14\uff0c\u4e0d\u7406\u91d1\u9010\u6d41\uff0c\u5374\u8f6c\u8fc7\u5934\u95ee\u79e6\u5143\u6d69\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u8fd8\u8981\u8bf7\u6559\u8bf7\u6559\u3002 \u8bf7\u95ee\u8fd9\u4f4d\u5e08\u5144\u9ad8\u59d3\u5927\u540d\uff0c\u4ee4\u5e08\u662f\u54ea\u4e00\u4f4d\uff1f\u201d\u79e6\u5143\u6d69\u62a5\u9053\u59d3\u201c\u79e6\u201d\uff0c\u5374\u634f\u4e86\u4e00\u4e2a\u5047\u540d\u3002 \u3000\u3000\u90a3\u5f53\u77e5\u5ba2\u7684\u8bf4\u9053\uff1a\u201c\u79e6\u5e08\u5144\u8bf7\u7b49\u4e00\u7b49\u3002\u201d\u8fdb\u53bb\u901a\u62a5\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u89c1\u4e00\u4e2a\u8eab\u6750\u7855\u6210\u7684\u5c11 \u5e74\u8d70\u4e86\u51fa\u6765\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u6b66\u5f53\u6d3e\u7684\u79e6\u5c11\u4fa0\u6765\u5230\uff0c\u8bf7\u6055\u5931\u8fce\u4e86\u3002\u521a\u624d\u8d35\u6d3e\u4e5f\u6709\u4e00\u4f4d\u59d3 \u79e6\u7684\u5e08\u5144\u6765\u5230\uff0c\u5927\u540d\u5143\u6d69\uff0c\u5374\u4e0d\u77e5\u4e0e\u79e6\u5c11\u4fa0\u662f\u600e\u4e48\u4e2a\u79f0\u547c\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u9762\u4e0a\u4e00\u7ea2\uff0c\u53ea\u5f97\u80e1\u4e71\u8bf4\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u54e5\u54e5\uff0c\u6211\u4eec\u5144\u5f1f\u4e8c\u4eba\u90fd\u662f\u5728\u655d\u6d3e\u96f7\u638c\u95e8\u7684 \u95e8\u4e0b\u3002\u201d\u96f7\u9707\u5b50\u95e8\u4e0b\u5f1f\u5b50\u751a\u591a\uff0c\u8fd9\u5c11\u5e74\u4e5f\u5f04\u4e0d\u6e05\u695a\uff0c\u4e0d\u8fc7\u5fc3\u4e2d\u5374\u662f\u9887\u6709\u6240\u7591\uff0c\u4e8e\u662f\u95ee\u9053\uff1a \u201c\u4f60\u4eec\u5144\u5f1f\u600e\u7684\u4e0d\u662f\u4e00\u8d77\u540c\u6765\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u5e73\u751f\u4e0d\u60ef\u6253\u8c0e\uff0c\u4e00\u65f6\u672a\u80fd\u4e34\u673a\u5e94\u53d8\u3002\u91d1\u9010\u6d41\u5df2\u62a2\u7740\u66ff\u4ed6\u7b54\u9053\uff1a\u201c\u4f60\u4eec\u6709\u6240\u4e0d\u77e5\uff0c \u8fd9\u4f4d\u79e6\u5c11\u4fa0\u6700\u6015\u4ed6\u7684\u54e5\u54e5\uff0c\u4ed6\u7684\u54e5\u54e5\u662f\u5949\u4e86\u5e08\u7236\u4e4b\u547d\u62ff\u7740\u8bf7\u5e16\u6765\u7684\uff0c\u4ed6\u5374\u662f\u7792\u7740\u4ed6\u7684\u54e5\u54e5\u6765 \u7684\uff0c\u4ed6\u600e\u6562\u4e0e\u4ed6\u54e5\u54e5\u540c\u5728\u4e00\u8d77\uff1f\u5230\u4e86\u8fd9\u513f\u518d\u7ed9\u54e5\u54e5\u53d1\u89c9\uff0c\u90a3\u5012\u65e0\u59a8\u3002\u53ea\u8981\u4f60\u4eec\u80af\u62db\u5f85\u4ed6\uff0c\u4ed6 \u54e5\u54e5\u603b\u4e0d\u597d\u610f\u601d\u628a\u4ed6\u8d76\u8dd1\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u5bb6\u5e08\u4e0e\u8d35\u6d3e\u4e43\u662f\u4e24\u4ee3\u4ea4\u60c5\uff0c\u53ea\u8981\u662f\u8d35\u6d3e\u7684\u5f1f\u5b50\u5230\u6765\uff0c\u6211\u4eec\u54ea \u6709\u4e0d\u606d\u8fce\u4e4b\u7406\uff1f\u79e6\u5c11\u4fa0\uff0c\u8bf7\u3002\u5c0f\u5f1f\u8fce\u9022\u6765\u8fdf\uff0c\u8fd8\u8bf7\u6055\u7f6a\u3002\u201d\u8bf4\u7f62\u4f38\u51fa\u624b\u6765\u4e0e\u79e6\u5143\u6d69\u4e00\u63e1\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5c11\u5e74\u4e43\u662f\u6c5f\u6d77\u5929\u7684\u5927\u5f1f\u5b50\u53f6\u6155\u534e\u3002\u53f6\u6155\u534e\u5165\u95e8\u5728\u5b87\u6587\u96c4\u4e4b\u540e\uff0c\u4f46\u5e74\u7eaa\u5374\u6bd4\u5b87\u6587\u96c4 \u7a0d\u957f\uff0c\u58f0\u540d\u4e5f\u6bd4\u5b87\u6587\u96c4\u5927\uff0c\u5b87\u6587\u96c4\u4e3a\u4e86\u5c0a\u656c\u4ed6\uff0c\u4fbf\u8981\u8ba9\u4ed6\u505a\u638c\u95e8\u7684\u5927\u5e08\u5144\u3002\u53f6\u6155\u534e\u4e0d\u80af\u7b54 \u5141\uff0c\u540e\u6765\u7531\u6c5f\u6d77\u5929\u63d0\u51fa\u6298\u8877\u7684\u529e\u6cd5\uff0c\u4e0d\u4f9d\u5165\u95e8\u5148\u540e\u4e3a\u5e8f\uff0c\u8ba9\u53f6\u6155\u534e\u4f5c\u5927\u5e08\u5144\uff0c\u4f46\u638c\u95e8\u5f1f\u5b50\u5219 \u4ecd\u7531\u5b87\u6587\u96c4\u62c5\u4efb\u3002 \u3000\u3000\u8fd9\u662f\u7531\u4e8e\u53f6\u6155\u534e\u53e6\u6709\u5bb6\u4f20\u7684\u6b66\u529f\uff0c\u672c\u9886\u867d\u7136\u6700\u5f3a\uff0c\u4f46\u672c\u95e8\u7684\u6b66\u529f\u5374\u4e0d\u53ca\u5b87\u6587\u96c4\u4e4b\u7eaf\u7cb9\uff0c \u638c\u95e8\u5f1f\u5b50\u5e94\u8be5\u662f\u7acb\u672c\u95e8\u6b66\u529f\u6700\u6709\u5fc3\u5f97\u7684\u4eba\uff0c\u800c\u4e14\u53f6\u6155\u534e\u662f\u5728\u5b87\u6587\u96c4\u5df1\u88ab\u7acb\u4e3a\u638c\u95e8\u5f1f\u5b50\u4e4b\u540e\u624d \u62dc\u59d1\u7236\u4e3a\u5e08\u7684\uff0c\u6c5f\u6d77\u5929\u4e5f\u4e0d\u613f\u610f\u518d\u591a\u4e8b\u66f4\u6362\u4e86\u3002 \u3000\u3000\u5176\u65f6\u5df2\u662f\u5728\u5c0f\u91d1\u5ddd\u4e4b\u6218\u7684\u4e09\u5e74\u4e4b\u540e\u3002\u53f6\u6155\u534e\u66fe\u62c5\u4efb\u8fc7\u5f53\u5e74\u63f4\u5ddd\u7684\u4e49\u519b\u7edf\u5e05\uff0c\u5929\u4e0b\u77e5\u540d\u3002 \u6b66\u6797\u4eba\u7269\uff0c\u5927\u90fd\u4e0e\u4ed6\u76f8\u8bc6\uff0c\u6545\u6b64\u8fd9\u6b21\u5e08\u5f1f\u5e08\u59b9\u6210\u5a5a\uff0c\u5c31\u7531\u4ed6\u62c5\u4efb\u6c5f\u5e9c\u7684\u603b\u77e5\u5ba2\u3002\u8981\u6709\u8eab\u4efd\u7684 \u4eba\u7269\u6765\u5230\uff0c\u624d\u7531\u4ed6\u51fa\u8fce\u7684\uff0c\u4ee5\u79e6\u5143\u6d69\u7684\u8eab\u4efd\u672c\u6765\u8fd8\u65e0\u987b\u60ca\u52a8\u5230\u4ed6\uff0c\u53ea\u56e0\u90a3\u4f4d\u5728\u5927\u95e8\u8fce\u5ba2\u7684\u9093 \u5c71\u5f1f\u5b50\uff0c\u5bf9\u79e6\u3001\u91d1\u4e8c\u4eba\u7684\u8eab\u4efd\u8d77\u7591\uff0c\u8fd9\u624d\u8bf7\u4e86\u53f6\u6155\u534e\u51fa\u6765\uff0c\u597d\u8ba9\u53f6\u6155\u534e\u4f5c\u4e3b\u3002 \u3000\u3000\u53f6\u6155\u534e\u5bf9\u91d1\u9010\u6d41\u66ff\u79e6\u5143\u6d69\u6240\u7f16\u7684\u90a3\u6bb5\u8c0e\u8a00\u5fc3\u4e2d\u4e5f\u5e76\u4e0d\u76f8\u4fe1\uff0c\u6240\u4ee5\u4ed6\u8bf4\u53ea\u8981\u662f\u8d35\u6d3e\u7684\u5f1f\u5b50 \u5230\u6765\uff0c\u6211\u4eec\u54ea\u6709\u4e0d\u606d\u8fce\u4e4b\u7406\uff1f\u201d\u8fd9\u4e24\u53e5\u8bdd\uff0c\u8bdd\u4e2d\u4e4b\u610f\uff0c\u5df2\u542b\u6709\u6000\u7591\u79e6\u5143\u6d69\u4e0d\u662f\u6b66\u5f53\u95e8\u4e0b\u4e4b \u610f\u3002\u79e6\u5143\u6d69\u662f\u4e2a\u8bda\u6734\u7684\u5c11\u5e74\uff0c\u4f46\u5374\u5e76\u975e\u611a\u8822\u4e4b\u8f88\uff0c\u4e00\u542c\u4e5f\u5c31\u542c\u61c2\u4e86\u53f6\u5e55\u534e\u7684\u610f\u601d\u3002\u4e8e\u662f\u5728\u53f6 \u5e55\u534e\u4f38\u624b\u4e0e\u4ed6\u76f8\u63e1\u4e4b\u65f6\uff0c\u4ed6\u5c31\u4f7f\u51fa\u4e86\u672c\u95e8\u7684\u5185\u529f\u3002 \u3000\u3000\u53f6\u6155\u534e\u6b63\u662f\u8981\u85c9\u63e1\u624b\u4e3a\u793c\uff0c\u6765\u8bd5\u63a2\u79e6\u5143\u6d69\u7684\u865a\u5b9e\u3002\u4e00\u8bd5\u4e4b\u4e0b\uff0c\u53ea\u89c9\u79e6\u5143\u6d69\u7684\u638c\u529b\u521a\u4e2d\u6709 \u67d4\uff0c\u6b63\u662f\u6b66\u5f53\u6d3e\u7684\u6b63\u5b97\u5185\u529f\u3002\u53f6\u6155\u534e\u9010\u6e10\u52a0\u5f3a\u638c\u529b\uff0c\u52a0\u5230\u4e86\u4e94\u516d\u5206\uff0c\u8fd9\u624d\u89c1\u79e6\u5143\u6d69\u7684\u7709\u5934\u7565 \u76b1\u3002 \u3000\u3000\u53f6\u6155\u534e\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u4e0d\u8fc7\u4e8c\u5341\u5c81\u5de6\u53f3\uff0c\u5c45\u7136\u80fd\u63a5\u5f97\u4f4f\u6211\u7684\u4e94\u6210\u529f\u529b\uff0c\u8fd9\u5b9a\u7136\u662f\u96f7\u9707\u5b50 \u7684\u5f97\u610f\u9ad8\u5f92\u65e0\u7591\u4e86\u3002\u201d\u8981\u77e5\u53f6\u6155\u534e\u8eab\u517c\u4e24\u95e8\u7684\u4e03\u4e58\u6b66\u5b66\uff0c\u5e74\u7eaa\u867d\u7136\u4e0d\u5230\u4e09\u5341\u5c81\uff0c\u672c\u9886\u5df2\u5dee\u4e0d \u591a\u53ef\u4ee5\u6324\u8fdb\u4e00\u6d41\u9ad8\u624b\u4e4b\u5217\uff0c\u80fd\u654c\u5f97\u4f4f\u4ed6\u7684\u4e94\u6210\u529f\u529b\u7684\uff0c\u5728\u6c5f\u6e56\u4e0a\u5df2\u662f\u7f55\u89c1\u7684\u4e86\u3002 \u3000\u3000\u53f6\u6155\u534e\u677e\u5f00\u4e86\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u79e6\u5c11\u4fa0\u8bf7\u8fdb\u3002\u8bf7\u95ee\u8fd9\u4f4d\u91d1\u5144\u53c8\u662f\u54ea\u4e00\u6d3e\u7684\uff0c\u4ee4\u5e08\u662f\u8c01\uff0c\u53ef\u80af \u8d50\u544a\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u53eb\u6211\u91d1\u5144\uff0c\u8fd9\u79f0\u547c\u53ef\u6709\u70b9\u4e0d\u5bf9\uff1f\u201d\u53f6\u6155\u534e\u9053\uff1a\u201c\u4e0d\u77e5\u6709\u4f55 \u4e0d\u5bf9\uff1f\u201d\u79e6\u5143\u6d69\u751f\u6015\u91d1\u9010\u6d41\u8bf4\u51fa\u4e0d\u4e2d\u542c\u7684\u8bdd\u6765\uff0c\u5fd9\u5411\u4ed6\u6253\u4e86\u4e00\u4e2a\u773c\u8272\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9762\u8272\u4e00\u7aef\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4e2a\u5c0f\u53eb\u5316\uff0c\u4f60\u600e\u80fd\u4e0e\u6211\u79f0\u5144\u9053\u5f1f\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u662f\u7279\u5730 \u4e3a\u4e86\u5403\u4e00\u987f\u4e0d\u82b1\u94b1\u7684\u9152\u8089\u6765\u7684\uff0c\u6211\u662f\u4e5e\u4e10\u4e16\u5bb6\uff0c\u54ea\u6765\u7684\u4ec0\u4e48\u95e8\u6d3e\uff1f\u8fd9\u4f4d\u59d3\u79e6\u7684\u670b\u53cb\u662f\u56e0\u4e3a\u6211 \u5728\u8def\u4e0a\u66ff\u4ed6\u8d76\u4e86\u4e24\u6761\u6076\u72d7\uff0c\u56e0\u6b64\u4ed6\u4e5f\u5c31\u6709\u5fc3\u5e26\u6211\u6765\u5403\u4f60\u4eec\u4e00\u987f\uff0c\u4e8e\u4ed6\u4e0d\u8d39\uff0c\u4e8e\u6211\u6709\u60e0\uff0c\u8fd9\u7b97 \u76d8\u4e0d\u662f\u6253\u5f97\u5f88\u54cd\u4e48\uff1f\u563f\uff0c\u563f\uff0c\u4ed6\u8bf4\u6211\u662f\u4ed6\u7684\u597d\u53cb\u8fd9\u662f\u5047\u7684\uff0c\u4ed6\u53ea\u662f\u8981\u62a5\u7b54\u6211\u7684\u4eba\u60c5\u800c\u5df2\uff0c\u666e \u666e\u901a\u901a\u7684\u670b\u53cb\u90a3\u8fd8\u9a6c\u9a6c\u864e\u864e\u53ef\u4ee5\u7b97\u5f97\u3002\u597d\uff0c\u6211\u90fd\u5df2\u5b9e\u8bdd\u5b9e\u8bf4\u4e86\uff0c\u62db\u4e0d\u62db\u5f85\u6211\u8fd9\u4e2a\u5c0f\u53eb\u5316\uff0c\u90a3 \u5c31\u662f\u4f60\u4eec\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e00\u7247\u75af\u8a00\u75af\u8bed\uff0c\u628a\u79e6\u5143\u6d69\u5f04\u5f97\u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u7ea2\uff0c\u53ea\u597d\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u59d3\u91d1\u7684 \u670b\u53cb\u6700\u559c\u8bf4\u7b11\uff0c\u4ed6\u2026\u2026\u4ed6\u2026\u2026\u201d\u79e6\u5143\u6d69\u60f3\u4e3a\u91d1\u9010\u6d41\u4f5c\u4e00\u4e2a\u4ecb\u7ecd\uff0c\u53ef\u662f\u4ed6\u4e5f\u4e0d\u77e5\u9053\u91d1\u9010\u6d41\u7684\u6765 \u5386\uff0c\u53c8\u4e0d\u4fbf\u63d0\u8d77\u91d1\u9010\u6d41\u5728\u5c01\u5bb6\u6551\u4ed6\u4e4b\u4e8b\uff0c\u56e0\u6b64\u8fde\u8bf4\u4e86\u4e24\u4e2a\u201c\u4ed6\u201d\u5b57\uff0c\u4fbf\u671f\u671f\u827e\u827e\u5730\u8bf4\u4e0d\u4e0b\u53bb \u4e86\u3002 \u3000\u3000\u53f6\u6155\u534e\u9053\uff1a\u201c\u91d1\u5144\uff0c\u8bf4\u7b11\u4e86\u3002\u201d\u4f38\u51fa\u624b\u6765\u3002\u91d1\u9010\u6d41\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u4e5f\u80af\u4e0e\u6211\u4eb2\u8fd1\u4eb2\u8fd1 \u4e48\u3002\u201d\u5f53\u4e0b\u53cc\u624b\u4e00\u6413\uff0c\u6c61\u79fd\u7684\u6ce5\u5c51\u5728\u638c\u5fc3\u6cdb\u8d77\u4e00\u7247\uff0c\u770b\u5f97\u90a3\u4e2a\u9099\u5c71\u6d3e\u7684\u5f1f\u5b50\u4e5f\u4e0d\u7981\u76b1\u4e86\u53cc \u7709\u3002 \u3000\u3000\u53f6\u6155\u534e\u7d20\u6765\u597d\u6d01\uff0c\u4f46\u4e3a\u4e86\u8bd5\u63a2\u5bf9\u65b9\u7684\u865a\u5b9e\uff0c\u5374\u662f\u4e0d\u6562\u76b1\u7709\uff0c\u5927\u5927\u65b9\u65b9\u7684\u4fbf\u4e0e\u91d1\u9010\u6d41\u63e1 \u624b\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4f60\u620f\u5f04\u6211\uff0c\u6211\u4e14\u6559\u4f60\u5403\u591a\u5c11\u82e6\u5934\u3002\u201d \u3000\u3000\u53f6\u6155\u534e\u9010\u6e10\u52a0\u638c\u529b\uff0c\u53ea\u89c9\u5bf9\u65b9\u6beb\u4e0d\u8fd0\u52b2\u76f8\u6297\uff0c\u4ed6\u52a0\u5230\u4e86\u516b\u4e5d\u5206\uff0c\u5bf9\u65b9\u4ecd\u662f\u795e\u8272\u81ea\u5982\uff0c\u8138 \u4e0a\u7b11\u563b\u563b\u7684\uff0c\u4f3c\u4e4e\u6839\u672c\u4e0d\u77e5\u9053\u53f6\u6155\u534e\u662f\u5728\u8bd5\u63a2\u4ed6\u7684\u672c\u9886\u3002\u53f6\u6155\u534e\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ee5 \u6211\u73b0\u5728\u6240\u4f7f\u7684\u638c\u529b\u5df2\u662f\u8db3\u4ee5\u5f00\u7891\u88c2\u77f3\uff0c\u600e\u7684\u662f\u5c0f\u53eb\u5316\u8fd8\u662f\u6beb\u4e0d\u611f\u89c9\u7684\u6837\u5b50\uff1f\u201d\u5f53\u4e0b\u4e00\u53d1\u72e0\u628a \u5168\u8eab\u6c14\u529b\u90fd\u4f7f\u4e86\u51fa\u6765\uff0c\u800c\u4e14\u662f\u4e13\u4f24\u5947\u7ecf\u516b\u8109\u7684\u5927\u4e58\u822c\u82e5\u638c\u529b\u3002 \u3000\u3000\u5927\u4e58\u822c\u82e5\u638c\u529b\u521a\u731b\u65e0\u6bd4\uff0c\u4f46\u8bf4\u4e5f\u5947\u602a\uff0c\u8fd9\u80a1\u638c\u529b\u53d1\u4e86\u51fa\u6765\uff01\u7adf\u4f3c\u628a\u4e00\u5757\u77f3\u5934\u6295\u5165\u6d77\u4e2d\uff0c \u5bf9\u5927\u6d77\u56fa\u7136\u65e0\u635f\uff0c\u800c\u4e14\u4e5f\u4ecd\u7136\u662f\u96be\u6d4b\u5927\u6d77\u7684\u6df1\u6d45\u3002\u53f6\u6155\u534e\u8fd9\u4e00\u60ca\u66f4\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u5fc3\u9053\uff1a\u201c\u6050 \u6015\u8981\u6211\u7684\u5e08\u7236\u624d\u80fd\u8bd5\u51fa\u4ed6\u7684\u865a\u5b9e\u4e86\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u89c9\u4e00\u9635\u5934\u6655\u3002\u8fd9\u5e76\u4e0d\u662f\u91d1\u9010\u6d41\u8fd0\u52b2\u53cd\u51fb \u6240\u81f4\uff0c\u800c\u662f\u56e0\u4e3a\u5927\u4e58\u822c\u82e5\u638c\u529b\u751a\u4f24\u771f\u6c14\uff0c\u53f6\u6155\u534e\u628a\u5168\u8eab\u6c14\u529b\u90fd\u4f7f\u4e86\u51fa\u6765\uff0c\u8eab\u4f53\u81ea\u662f\u4e0d\u514d\u6709\u75b2 \u8f6f\u865a\u5f31\u7684\u53cd\u5e94\u3002 \u3000\u3000\u53f6\u6155\u534e\u8fde\u5fd9\u653e\u5f00\u4e86\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u670b\u53cb\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u4f69\u670d\uff0c\u4f69\u670d\uff01\u201d\u4ed6\u521a\u624d\u79f0\u91d1\u9010\u6d41 \u4e3a\u201c\u91d1\u5144\u201d\u78b0\u4e86\u4e00\u4e2a\u9489\u5b50\uff0c\u4e00\u65f6\u60f3\u4e0d\u5230\u9002\u5f53\u7684\u79f0\u547c\uff0c\u9042\u4f9d\u6c5f\u6e56\u4e0a\u5bf9\u964c\u751f\u4eba\u7684\u666e\u901a\u79f0\u8c13\uff0c\u53eb\u4ed6 \u4e00\u58f0\u201c\u91d1\u670b\u53cb\u201d\u3002 \u3000\u3000\u4e0d\u6599\uff0c\u91d1\u9010\u6d41\u53c8\u662f\u53cc\u773c\u4e00\u7ffb\uff0c\u4f3c\u4e4e\u53c8\u60f3\u53d1\u813e\u6c14\u7684\u6837\u5b50\uff0c\u4f46\u53ea\u662f\u77ac\u606f\u4e4b\u95f4\uff0c\u4ed6\u53c8\u6062\u590d\u4e86\u7b11 \u563b\u563b\u7684\u73a9\u4e16\u4e0d\u606d\u7684\u6001\u5ea6\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u5c45\u7136\u80af\u548c\u6211\u8fd9\u6837\u80ae\u810f\u7684\u5c0f\u53eb\u5316\u505a\u670b\u53cb\uff0c\u96be\u5f97\uff0c\u96be\u5f97\uff01 \u597d\uff0c\u4f60\u65e2\u7136\u80af\u4ee5\u670b\u53cb\u5f85\u6211\uff0c\u6211\u4e5f\u613f\u610f\u628a\u4f60\u5f53\u4f5c\u6211\u7684\u670b\u53cb\u4e86\u3002\u53f6\u670b\u53cb\uff0c\u4f60\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u4f60\u7684\u6b66\u529f \u4e5f\u5f88\u4e0d\u9519\u5440\u3002\u201d\u53f6\u6155\u534e\u9053\uff1a\u201c\u8fd8\u5f97\u8bf7\u91d1\u670b\u53cb\u6307\u70b9\u6307\u70b9\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u4e5f\u53ef\u4ee5\u6307\u70b9\u4f60\u7684\uff0c\u4f46\u4f60\u65e2\u7136\u6709\u6c5f\u6d77\u5929\u6307\u70b9\uff0c\u90a3\u4e5f\u5c31\u7528 \u4e0d\u7740\u6211\u4e86\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u79e6\u5143\u6d69\u5927\u60ca\u5931\u8272\u3002\u6c5f\u5bb6\u7684\u95e8\u5ba2\u542c\u4ed6\u8bf4\u5f97\u5982\u6b64\u4e4b\u4e0d\u5ba2\u6c14\uff0c\u800c\u98ce\u76f4\u547c\u6c5f \u6d77\u5929\u4e4b\u540d\uff0c\u4e5f\u90fd\u4e0d\u7981\u9762\u6709\u6012\u5bb9\u3002\u4f46\u53f6\u6155\u534e\u5374\u662f\u5e76\u4e0d\u52a8\u6012\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u53eb\u5316\u4e00\u5b9a\u5927\u6709\u6765 \u5386\uff0c\u4e14\u5f85\u4ed6\u8fdb\u53bb\u4e4b\u540e\uff0c\u6211\u53bb\u7980\u660e\u5e08\u7236\uff0c\u4e00\u5b9a\u53ef\u4ee5\u67e5\u77e5\u4ed6\u7684\u5e95\u7ec6\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e00\u7b11\u4e4b\u540e\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u7684\u6b66\u529f\u4e0d\u9519\uff0c\u8fd9\u53ef\u4e0d\u662f\u5ba2\u6c14\u3002\u4f60\u662f\u5e26\u827a\u6295\u5e08\u7684 \u5427\uff1f\u201d\u53f6\u6155\u534e\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u7684\u5927\u4e58\u822c\u82e5\u638c\u529b\u5f00\u5934\u7ec3\u5f97\u4e0d\u5bf9\uff0c\u8fd9\u95e8\u529f\u592b\u5e76\u975e \u5355\u7eaf\u4ee5\u9738\u9053\u4e3a\u4e3b\u7684\u3002\u5927\u7ea6\u4f60\u8ddf\u4e86\u6c5f\u6d77\u5929\u51e0\u5e74\uff0c\u8fd9\u624d\u9010\u6e10\u6539\u6b63\u8fc7\u6765\u3002\u4f46\u706b\u5019\u672a\u5230\uff0c\u56e0\u6b64\u5c31\u663e\u5f97 \u9a73\u6742\u4e0d\u7eaf\u3002\u4f60\u4ee5\u540e\u5bf9\u672c\u95e8\u6b66\u529f\uff0c\u8fd8\u987b\u82e6\u5b66\u52e4\u7ec3\uff01\u4e0d\u8fc7\u8bdd\u8bf4\u56de\u6765\uff0c\u4f60\u53ea\u8ddf\u6c5f\u6d77\u5929\u51e0\u5e74\uff0c\u5c31\u7ec3\u5230 \u5982\u6b64\u5883\u754c\uff0c\u4e5f\u786e\u5b9e\u662f\u5f88\u96be\u5f97\u4e86\uff01\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u8bf4\u5f97\u66f4\u4e0d\u5ba2\u6c14\uff0c\u7b80\u76f4\u662f\u957f\u8f88\u6559\u8bad\u540e\u8f88\u7684\u53e3\u543b\uff0c\u53ef\u662f\u53f6\u6155\u534e\u542c\u8fdb\u8033\u4e2d\uff0c\u5374\u662f\u4e0d\u7531\u5f97 \u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u4f69\u670d\uff0c\u91d1\u9010\u6d41\u6307\u51fa\u4ed6\u7684\u7f3a\u70b9\u4e0d\u4f46\u8bf4\u5f97\u5341\u5206\u4e2d\u80af\uff0c\u800c\u4e14\u4e5f\u6b63\u662f\u6c5f\u6d77\u5929\u4e0d\u4e45\u4e4b\u524d \u5bf9\u53f6\u6155\u534e\u8bf4\u8fc7\u7684\u3002 \u3000\u3000\u53f6\u6155\u534e\u597d\u751f\u9a87\u5f02\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u662f\u5728\u9664\u6389\u53f6\u51cc\u98ce\u90a3\u8d3c\u5b50\u4e4b\u540e\uff0c\u624d\u62dc\u59d1\u7236\u4e3a\u5e08\u7684\uff0c\u6b64\u4e8b \u77e5\u8005\u751a\u591a\u3002\u8fd9\u5c0f\u53eb\u5316\u8bf4\u5f97\u51fa\u6211\u662f\u5e26\u827a\u6280\u5e08\uff0c\u4e0d\u8db3\u4e3a\u5947\u3002\u4f46\u5947\u5c31\u5947\u5728\u4ed6\u600e\u4e48\u77e5\u9053\u6211\u7684\u7ec3\u529f\u79d8 \u5bc6\uff1f\u8fde\u6211\u81ea\u5df1\u4e5f\u662f\u8fd8\u662f\u6700\u8fd1\u624d\u5bdf\u89c9\u7684\u6bdb\u75c5\u4ed6\u4e5f\u770b\u5f97\u51fa\u6765\uff1f\u82e6\u975e\u5bf9\u672c\u95e8\u7684\u5185\u529f\u5fc3\u6cd5\u6df1\u6709\u9020\u8be3\uff0c \u7109\u80fd\u5982\u6b64\u6d1e\u82e5\u89c2\u706b\uff1f\u6b64\u4e8b\u53ef\u771f\u662f\u90aa\u95e8\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u80af\u548c\u6211\u505a\u670b\u53cb\uff0c\u60f3\u5fc5\u53ef\u4ee5\u8ba9\u6211\u767d\u5403\u4e00\u987f\u4e86\u5427\uff1f\u5403\u9971\u4e86\u6211\u518d\u4e0e\u4f60\u8c08\u8bba\u6b66 \u529f\u3002\u201d\u53f6\u6155\u534e\u9053\uff1a\u201c\u91d1\u670b\u53cb\u5149\u4e34\uff0c\u6211\u4eec\u662f\u6c42\u4e5f\u6c42\u4e0d\u5230\u7684\u3002\u8bf7\u8fdb\uff01\u8bf7\u8fdb\uff01\u201d \u3000\u3000\u6c5f\u5bb6\u8d3a\u5ba2\u5982\u4e91\uff0c\u6210\u540d\u7684\u6b66\u6797\u4eba\u7269\u4e0d\u77e5\u591a\u5c11\u3002\u79e6\u5143\u6d69\u662f\u4e2a\u521d\u51fa\u8305\u5e90\u7684\u6b66\u5f53\u5f1f\u5b50\uff0c\u6df7\u5728\u8d3a\u5ba2 \u4e4b\u4e2d\uff0c\u8c01\u4e5f\u4e0d\u6ce8\u610f\u4ed6\u3002\u4f46\u91d1\u9010\u6d41\u53ef\u4e0d\u540c\u4e86\uff0c\u6ee1\u5802\u8d3a\u5ba2\u867d\u7136\u6ca1\u6709\u4e00\u4e2a\u4eba\u8ba4\u5f97\u4ed6\uff0c\u4f46\u5bf9\u8fd9\u4e48\u4e00\u4e2a \u602a\u6a21\u6837\u7684\u5c0f\u53eb\u5316\uff0c\u5374\u662f\u65e0\u4eba\u4e0d\u52a0\u6ce8\u89c6\uff0c\u611f\u5230\u60ca\u5947\u3002 \u3000\u3000\u53f6\u6155\u534e\u9053\uff1a\u201c\u79e6\u5c11\u4fa0\uff0c\u4f60\u53ef\u8981\u548c\u4ee4\u5144\u76f8\u89c1\u4e48\uff1f\u201d\u79e6\u5143\u6d69\u8bf4\u9053\uff1a\u201c\u53f6\u5144\u4e0d\u7528\u8d39\u795e\uff0c\u5c0f\u5f1f\u81ea \u4f1a\u627e\u6c60\u3002\u53f6\u5144\u62db\u5f85\u522b\u7684\u8d35\u5ba2\u5427\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4ed6\u6015\u4ed6\u7684\u54e5\u54e5\uff0c\u8981\u5e2d\u6563\u4e4b\u540e\u624d\u6562\u89c1\u4ed6\u54e5\u54e5 \u7684\uff0c\u4f46\u548c\u4ed6\u54e5\u54e5\u540c\u6765\u7684\u90a3\u4e24\u4e2a\u4eba\uff0c\u5012\u4e0d\u59a8\u8ba9\u6211\u5148\u89c1\u4e00\u89c1\u3002\u201d \u3000\u3000\u53f6\u6155\u534e\u9053\uff1a\u201c\u597d\uff0c\u6211\u66ff\u4f60\u53bb\u627e\u4ed6\u4eec\u3002\u201d\u5fc3\u91cc\u6697\u6697\u5947\u602a\uff0c\u201c\u8fd9\u5c0f\u53eb\u5316\u53c8\u600e\u4e48\u77e5\u9053\u79e6\u5143\u6d69\u662f \u548c\u4e24\u4e2a\u4eba\u540c\u6765\u7684\uff1f\u7167\u79e6\u5143\u6d69\u7684\u8bf4\u6cd5\uff0c\u8fde\u4ed6\u7684\u5f1f\u5f1f\u4e5f\u4e0d\u5e94\u8be5\u77e5\u9053\u7684\u561b\u3002\u55ef\uff0c\u8fd9\u6b21\u6765\u7684\u6b66\u5f53\u5f1f\u5b50 \u4e5f\u771f\u7279\u522b\uff0c\u5144\u5f1f\u4e8c\u4eba\u5404\u8d70\u5404\u7684\uff0c\u800c\u53c8\u5404\u81ea\u5e26\u6765\u4e86\u4e00\u4e2a\u6b66\u529f\u6781\u9ad8\u3001\u6765\u5386\u4e0d\u660e\u7684\u4eba\u7269\uff01\u8fd9\u4e2a\u5c0f\u53eb \u5316\u53c8\u8981\u6bd4\u521a\u624d\u79e6\u5143\u6d69\u5e26\u6765\u7684\u90a3\u4e2a\u6c49\u5b50\u66f4\u4e3a\u5947\u602a\uff01\u201d \u3000\u3000\u539f\u6765\u6587\u80dc\u4e2d\u3001\u6587\u9053\u5e84\u548c\u5c01\u5b50\u8d85\u6765\u7684\u65f6\u5019\uff0c\u4e5f\u662f\u53f6\u6155\u534e\u63a5\u5f85\u7684\u3002\u6587\u80dc\u4e2d\u6301\u6709\u8bf7\u5e16\uff0c\u5192\u5145\u79e6 \u5143\u6d69\u7684\u8eab\u4efd\uff1b\u4ed6\u7684\u7236\u4eb2\u6587\u9053\u5e84\uff0c\u4e16\u53d4\u5c01\u5b50\u8d85\u5219\u5192\u5145\u662f\u4ed6\u7684\u670b\u53cb\u3002\u6587\u80dc\u4e2d\u6709\u8bf7\u5e16\uff0c\u53f6\u6155\u534e\u5bf9\u4ed6 \u7684\u8eab\u4efd\u81ea\u662f\u6beb\u4e0d\u6000\u7591\uff0c\u56e0\u6b64\u4e5f\u5c31\u7528\u4e0d\u7740\u8bd5\u63a2\u4ed6\u7684\u6b66\u529f\u8def\u6570\u3002\u5bf9\u6587\u9053\u5e84\uff0c\u5c01\u5b50\u8d85\u4e8c\u4eba\uff0c\u53f6\u6155\u534e \u5219\u66fe\u7ecf\u8bd5\u63a2\uff0c\u5982\u540c\u8bd5\u63a2\u91d1\u9010\u6d41\u4e00\u6837\uff0c\u4e5f\u662f\u85c9\u63e1\u624b\u4e3a\u793c\u6765\u8bd5\u63a2\u7684\u3002\u5c01\u5b50\u8d85\u4e5f\u8fd8\u7f62\u4e86\uff0c\u6587\u9053\u5e84\u7684 \u529f\u529b\u53ef\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u4e00\u8bd5\u4e4b\u4e0b\uff0c\u864e\u53e3\u7ed9\u6587\u9053\u5e84\u6697\u8fd0\u4e09\u8c61\u795e\u529f\u9707\u5f97\u9690\u9690\u4f5c\u75db\uff0c\u4e5f\u662f\u50cf\u8bd5\u63a2\u91d1 \u9010\u6d41\u4e00\u6837\uff0c\u8bd5\u63a2\u4e0d\u51fa\u6587\u9053\u5e84\u7684\u6765\u5386\u3002 \u3000\u3000\u53f6\u6155\u534e\u6b63\u8981\u53bb\u627e\u5c01\u5b50\u8d85\u4e0e\u6587\u9053\u5e84\uff0c\u674e\u5149\u590f\u8d70\u6765\u8bf4\u9053\uff1a\u201c\u5927\u5e08\u54e5\uff0c\u5e08\u7236\u627e\u4f60\u53bb\u966a\u5ba2\u3002\u4e8c\u5e08 \u54e5\u548c\u5e08\u59d0\u5c31\u8981\u51fa\u6765\u656c\u9152\u4e86\u3002\u201d\u53f6\u6155\u534e\u9053\uff1a\u201c\u597d\uff0c\u6211\u7b49\u4e00\u4f1a\u5c31\u53bb\u3002\u590f\u5f1f\uff0c\u4f60\u66ff\u6211\u966a\u8fd9\u4e24\u4f4d\u8d35 \u5ba2\u3002\u201d\u79e6\u5143\u6d69\u9053\uff1a\u201c\u53f6\u5144\u8bf7\u4fbf\u3002\u6211\u4e5f\u5e76\u4e0d\u6025\u4e8e\u5c31\u89c1\u4ed6\u4eec\u3002\u201d\u79e6\u5143\u6d69\u53ea\u662f\u60f3\u8ba9\u5c01\u5b50\u8d85\u770b\u89c1\u4ed6\uff0c \u53ef\u4e0d\u613f\u610f\u60ca\u5e08\u52a8\u4f17\u3002 \u3000\u3000\u6c5f\u5bb6\u56e0\u4e3a\u8d3a\u5ba2\u4f17\u591a\uff0c\u4e43\u662f\u5206\u6279\u5165\u5e2d\u7684\u3002\u79e6\u5143\u6d69\u6765\u5f97\u8fdf\uff0c\u6b64\u65f6\u5b87\u6587\u96c4\u4e0e\u6c5f\u6653\u8299\u5df2\u7ecf\u62dc\u8fc7\u4e86 \u5802\uff0c\u7b2c\u4e00\u8f6e\u7684\u9152\u5e2d\u4e5f\u65e9\u5df2\u5f00\u4e86\u3002 \u3000\u3000\u53f6\u6155\u534e\u9053\uff1a\u201c\u4e24\u4f4d\u8bf7\u5148\u7528\u8336\u70b9\uff0c\u5f85\u4f1a\u513f\u5165\u5e2d\u3002\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u597d\uff0c\u5148\u5403\u70b9\u5fc3\uff0c\u518d\u5c1d\u76db \u5bb4\uff0c\u5403\u4e00\u987f\u7b49\u4e8e\u5403\u4e24\u987f\uff0c\u5bf9\u6211\u8fd9\u5c0f\u53eb\u5316\u6b63\u662f\u5f97\u5176\u6240\u54c9\u3002\u53f6\u670b\u53cb\uff0c\u4f60\u53ea\u7ba1\u8bf7\u4fbf\uff0c\u6211\u5403\u4e1c\u897f\u662f\u4e0d \u7528\u4eba\u62db\u5f85\u7684\u3002\u201d \u3000\u3000\u9152\u5e2d\u8bbe\u5728\u82b1\u56ed\uff0c\u82b1\u56ed\u4e24\u8fb9\u662f\u957f\u5eca\uff0c\u672a\u5165\u5e2d\u7684\u8d3a\u5ba2\u5c31\u5728\u957f\u5eca\u559d\u8336\uff0c\u65e2\u53ef\u8d4f\u82b1\uff0c\u53c8\u53ef\u770b\u70ed \u95f9\uff0c\u79e6\u5143\u6d69\u628a\u773c\u56db\u671b\uff0c\u4f46\u89c1\u6ee1\u56ed\u5b50\u91cc\u95f9\u54c4\u54c4\u7684\uff0c\u4e00\u65f6\u95f4\u54ea\u91cc\u80fd\u591f\u53d1\u73b0\u5c01\u3001\u6587\u7b49\u4eba\u3002 \u3000\u3000\u6b64\u65f6\u5404\u5e2d\u8d3a\u5ba2\u90fd\u5df2\u5750\u8d70\uff0c\u552f\u6709\u4e3b\u5bb6\u90a3\u4e00\u5e2d\u5c1a\u672a\u6392\u597d\u5ea7\u4f4d\u3002\u8fd9\u4e00\u5e2d\u6309\u7167\u4e60\u60ef\u4e43\u662f\u4e24\u5bb6\u7684\u957f \u8f88\uff0c\u4ee5\u53ca\u81f3\u4eb2\u597d\u53cb\u4e0e\u4e3b\u5bb6\u6240\u8ba4\u4e3a\u7684\u8d35\u5bbe\u5750\u7684\u3002\u7537\u5bb6\u7684\u5b87\u6587\u96c4\u7236\u6bcd\u65e9\u4e27\uff0c\u5e76\u65e0\u4eb2\u5c5e\u3002\u5973\u5bb6\u7684\u957f \u8f88\u4e5f\u53ea\u662f\u6c5f\u6d77\u5929\u592b\u5987\u4e8c\u4eba\uff0c\u6c5f\u6d77\u5929\u7684\u7236\u4eb2\u6c5f\u5357\u524d\u5e74\u5df2\u53bb\u4e16\u4e86\u3002\u53f6\u6155\u534e\u4e0e\u803f\u79c0\u51e4\u65e9\u5df2\u6210\u5a5a\uff0c\u4ed6 \u4eec\u4e8c\u4eba\u4ee5\u5e08\u5144\u5e08\u5ac2\u7684\u8eab\u4efd\u4e5f\u5750\u5728\u4e3b\u5bb6\u4e00\u5e2d\uff0c\u517c\u4f5c\u966a\u5ba2\u3002\u53e6\u5916\u8fd8\u6709\u516d\u4e2a\u5ea7\u4f4d\u5374\u662f\u715e\u8d39\u5b89\u6392\u3002\u6c5f \u6d77\u5929\u4ea4\u6e38\u904d\u5929\u4e0b\uff0c\u8d3a\u5ba2\u4e2d\u6210\u540d\u7684\u4eba\u7269\u4e0d\u53ef\u80dc\u6570\uff0c\u9080\u8bf7\u8fd9\u4e2a\u4e0d\u9080\u8bf7\u90a3\u4e2a\u5c31\u96be\u514d\u6709\u539a\u6b64\u8584\u5f7c\u4e4b \u5acc\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5148\u8bf7\u4e86\u4e10\u5e2e\u7684\u5e2e\u4e3b\u4ef2\u957f\u7edf\u548c\u5ce8\u5d4b\u6d3e\u7684\u4e00\u9633\u5b50\u5165\u5ea7\u3002\u4ef2\u957f\u7edf\u5728\u8d3a\u5ba2\u4e2d\u8f88\u4efd\u6700\u9ad8\uff0c\u4e10 \u5e2e\u4e0e\u6c5f\u5bb6\u7684\u6e0a\u6e90\u53c8\u6781\u6df1\u539a\uff0c\u6c5f\u6d77\u5929\u8bf7\u4ed6\u4e0e\u81ea\u5df1\u540c\u5e2d\uff0c\u81ea\u662f\u65e0\u4eba\u95f2\u8bdd\uff0c\u4e00\u9633\u5b50\u662f\u5ce8\u5d4b\u6d3e\u4e0a\u8f88\u957f \u8001\u91d1\u5149\u5927\u5e05\u7684\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u73b0\u4efb\u5ce8\u5d4b\u638c\u95e8\u95f2\u4e91\u957f\u8001\u7684\u5e08\u5f1f\uff0c\u9664\u4e86\u4ef2\u957f\u7edf\u4e4b\u5916\uff0c\u5c31\u6570\u4ed6\u7684\u8f88\u4efd\u6700 \u5c0a\u4e86\uff0c\u662f\u4ee5\u6c5f\u6d77\u5929\u5c06\u4ed6\u5f53\u4f5c\u8d35\u5bbe\u3002\u4f46\u8bf7\u4e86\u8fd9\u4e24\u4e2a\u4eba\u4e4b\u540e\uff0c\u8fd8\u6709\u56db\u4e2a\u7a7a\u4f4d\u5374\u4e0d\u77e5\u8bf7\u8c01\u6765\u5750\u624d \u597d\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u4f60\u7d2f\u6765\u559c\u6b22\u540e\u8d77\u4e4b\u79c0\uff0c\u4f55\u4e0d\u6e05\u51e0\u4f4d\u5c11\u5e74\u82f1\u6770\u4e00\u540c\u5165\u5e2d\uff0c\u4e5f\u597d\u8ba9\u4ed6\u4eec\u665a\u4e00 \u8f88\u7684\u4ea4\u4ea4\u670b\u53cb\u3002\u201d\u6c5f\u6d77\u5929\u5f97\u59bb\u5b50\u4e00\u8a00\u63d0\u9192\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5c31\u8bf7\u5510\u5927\u4fa0\u8fc7\u6765\u3002\u201d\u6c5f\u6d77\u5929\u6240\u8bf7 \u7684\u8fd9\u4e2a\u5c11\u5e74\u4e43\u662f\u5929\u5c71\u6d3e\u638c\u95e8\u5510\u7ecf\u5929\u7684\u513f\u5b50\u5510\u52a0\u6e90\u3002\u5510\u52a0\u6e90\u7684\u8f88\u4efd\u5012\u662f\u5f88\u9ad8\uff0c\u4e0e\u6c5f\u6d77\u5929\u5c5e\u4e8e\u540c \u8f88\uff0c\u4e0d\u8fc7\u5e74\u7eaa\u53ea\u6709\u5341\u591a\u5c81\u3002\u5728\u8bb8\u8bb8\u591a\u591a\u7684\u6210\u540d\u4eba\u7269\u4e4b\u4e2d\uff0c\u4ed6\u4e5f\u8fd8\u662f\u88ab\u8ba4\u4e3a\u201c\u540e\u8d77\u4e4b\u79c0\u201d\u7684\u3002 \u3000\u3000\u5510\u52a0\u6e90\u5750\u4e0a\u4e3b\u5bb6\u5e2d\u4e0a\uff0c\u8fd8\u6709\u4e09\u4e2a\u7a7a\u4f4d\uff0c\u6c5f\u6d77\u5929\u60f3\u4e86\u4e00\u60f3\uff0c\u7b11\u9053\uff1a\u201c\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u96f7\u9707\u5b50 \u6709\u5c01\u4fe1\u7ed9\u6211\uff0c\u63a8\u8350\u4ed6\u7684\u5f97\u610f\u5f1f\u5b50\u79e6\u5143\u6d69\u7ed9\u6211\u76f8\u8bc6\uff0c\u542c\u8bf4\u4ed6\u5df2\u7ecf\u6765\u4e86\uff0c\u8fd8\u6709\u4e24\u4f4d\u548c\u4ed6\u540c\u6765\u7684\u670b \u53cb\uff0c\u5c31\u8bf7\u4ed6\u4eec\u4eba\u90fd\u5750\u4e0a\u6765\u5427\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7a81\u7136\u60f3\u5230\u8fd9\u6837\u5b89\u6392\uff0c\u9664\u4e86\u4ed6\u7740\u91cd\u96f7\u9707\u5b50\u7684\u4ea4\u60c5\u4e0e\u53ca\u559c\u7231\u540e\u8f88\u7684\u5fc3\u7406\u4e4b\u5916\uff0c\u53e6\u5916\u8fd8 \u6709\u4e00\u4e2a\u539f\u56e0\uff0c\u56e0\u4e3a\u4ed6\u5df1\u6240\u5f97\u53f6\u6155\u534e\u7684\u7980\u62a5\uff0c\u77e5\u9053\u4e0e\u201c\u79e6\u5143\u6d69\u201d\u540c\u6765\u7684\u4e24\u4e2a\u670b\u53cb\u4e4b\u4e2d\uff0c\u6709\u4e00\u4e2a \u4eba\u6b66\u529f\u6781\u9ad8\uff0c\u4f46\u5374\u65e0\u4eba\u8ba4\u5f97\u4ed6\u662f\u8c01\u3002\u6587\u9053\u5e84\u662f\u7ecf\u8fc7\u6539\u5bb9\u6613\u8c8c\u7684\uff0c\u800c\u4e14\u4ed6\u4e0d\u5230\u4e2d\u539f\u4e5f\u5df2\u6709\u4e8c\u5341 \u5e74\u4e86\uff0c\u662f\u4ee5\u5728\u8d3a\u5ba2\u4e2d\u867d\u6709\u51e0\u4eba\u4e8c\u5341\u5e74\u524d\u66fe\u89c1\u8fc7\u4ed6\uff0c\u4ea6\u5df2\u4e0d\u8ba4\u5f97\u4ed6\u4e86\u3002\u6545\u6b64\u6c5f\u6d77\u5929\u60f3\u8bf7\u8fd9\u4eba\u8fc7 \u6765\u4e00\u89c1\u3002\u81f3\u4e8e\u5c01\u5b50\u8d85\u5219\u662f\u966a\u886c\u7684\uff0c\u65e2\u7136\u662f\u4e0e\u79e6\u5143\u6d69\u540c\u6765\uff0c\u4e5f\u5c31\u4e0d\u80fd\u4e0d\u8bf7\u4ed6\u4e86\u3002 \u3000\u3000\u6587\u9053\u5e84\u542c\u5f97\u6c5f\u6d77\u5929\u8981\u8bf7\u4ed6\u540c\u5e2d\uff0c\u8fd9\u662f\u4ed6\u4e8b\u5148\u6ca1\u6709\u6599\u5230\u7684\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\u3002\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c \u4e0d\u6765\u4e5f\u5df2\u6765\u4e86\uff0c\u6b64\u65f6\u82e5\u7136\u9003\u8dd1\uff0c\u66f4\u60f9\u5acc\u7591\uff0c\u800c\u4e14\u5750\u4e0a\u4e3b\u5bb6\u7684\u5e2d\u4f4d\uff0c\u53ef\u4ee5\u6709\u66f4\u591a\u7684\u673a\u4f1a\u5077\u88ad\uff0c \u4e8e\u662f\u628a\u5fc3\u4e00\u6a2a\uff0c\u4fbf\u7ad9\u4e86\u8d77\u6765\uff0c\u795e\u8272\u81ea\u5982\u5730\u7b11\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u6562\u5f53\uff0c\u6c5f\u5927\u4fa0\u592a\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u53f6\u6155\u534e\u8d70\u5230\u4ed6\u4eec\u539f\u6765\u6240\u5750\u7684\u90a3\u5e2d\u8bf4\u9053\u3002\u201d\u4e91\u5148\u751f\u3001\u79e6\u5c11\u4fa0\uff0c\u4e24\u4f4d\u662f\u7b2c\u4e00\u6b21\u5149\u4e34\u7684\u8d35\u5ba2\uff0c \u5949\u5bb6\u5e08\u4e4b\u547d\uff0c\u8bf7\u4e24\u4f4d\u4e0a\u5750\u3002\u54a6\uff0c\u8fd8\u6709\u4e00\u4f4d\u98ce\u5148\u751f\u5462\uff1f\u201d\u6587\u9053\u5e84\u3001\u5c01\u5b50\u8d85\u90fd\u662f\u7528\u7684\u5047\u540d\uff0c\u53d6\u4e86 \u4e00\u4e2a\u540c\u97f3\u7684\u59d3\u3002\u6587\u9053\u5e84\u81ea\u8ba4\u59d3\u201c\u4e91\u201d\uff0c\u5c01\u5b50\u8d85\u5219\u8ba4\u59d3\u201c\u98ce\u201d\u3002 \u3000\u3000\u6587\u9053\u5e84\u662f\u8001\u5978\u5de8\u6ed1\uff0c\u5fc3\u5185\u5403\u60ca\uff0c\u795e\u8272\u4e1d\u6beb\u4e0d\u9732\u3002\u6587\u80dc\u4e2d\u5374\u4e0d\u7981\u9732\u51fa\u5f20\u7687\u5931\u63aa\u7684\u795e\u8272\uff0c\u5c24 \u5176\u5f53\u4ed6\u4e00\u770b\u4e0d\u89c1\u4e86\u5c01\u5b50\u8d85\u7684\u65f6\u5019\uff0c\u66f4\u662f\u5403\u60ca\u3002 \u3000\u3000\u6587\u9053\u5e84\u4e5f\u4e0d\u77e5\u5c01\u5b50\u8d85\u4f55\u5f80\uff0c\u6025\u4e2d\u751f\u667a\uff0c\u8bf4\u9053\uff1a\u201c\u98ce\u5148\u751f\u809a\u5b50\u6709\u70b9\u4e0d\u5927\u8212\u670d\uff0c\u53eb\u6211\u4eec\u4e0d\u5fc5 \u7b49\u4ed6\uff0c\u563f\uff0c\u563f\uff0c\u4ed6\u6050\u6015\u662f\u3001\u6050\u6015\u662f\u2026\u2026\u201d\u76b1\u76b1\u7709\u5934\uff0c\u7b11\u4e86\u4e00\u7b11\uff0c\u6697\u793a\u4ed6\u662f\u6015\u8bf4\u51fa\u201c\u5982\u5395\u201d\u4e8c \u5b57\uff0c\u592a\u8fc7\u4e0d\u96c5\u3002 \u3000\u3000\u53f6\u6155\u534e\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u4e24\u4f4d\u8bf7\u5148\u4e0a\u5ea7\u3002\u201d\u6587\u80dc\u4e2d\u9053\uff1a\u201c\u6211\u662f\u672a\u5165\u6d41\u7684\u5c0f\u8f88\uff0c\u6c5f\u5927\u4fa0\u5982\u6b64 \u5ba2\u6c14\uff0c\u6211\u662f\u66f4\u4e0d\u6562\u5f53\u3002\u6211\uff0c\u627e\u4e0d\u5982\u53bb\u7167\u6599\u98ce\u5148\u751f\u5427\u3002\u201d\u53f6\u6155\u534e\u9053\uff1a\u201c\u5bb6\u5e08\u6b63\u662f\u60f3\u8bf7\u5c0f\u4e00\u8f88\u7684 \u5c11\u5e74\u8c6a\u6770\u4e0e\u5929\u4e0b\u82f1\u96c4\u76f8\u8bc6\uff0c\u8fd9\u662f\u5bb6\u5e08\u7684\u4e00\u756a\u7f8e\u610f\uff0c\u8bf7\u79e6\u5c11\u4fa0\u4e0d\u5fc5\u63a8\u8f9e\u4e86\uff0c\u81f3\u4e8e\u98ce\u5148\u751f\u561b\uff0c\u6211 \u53eb\u4eba\u53bb\u627e\u4ed6\u5c31\u662f\uff0c\u98ce\u5148\u751f\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u75c5\uff0c\u79e6\u5c11\u4fa0\u65e0\u987b\u62c5\u5fe7\u3002\u6211\u4eec\u8fd9\u91cc\u4e5f\u6709\u4eba\u7167\u6599\u4ed6\u7684\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u6697\u6697\u8e22\u4e86\u513f\u5b50\u4e00\u4e0b\uff0c\u793a\u610f\u53eb\u4ed6\u9547\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u662f\u4e3b\u4eba\u5bb6\u4e00\u756a\u7f8e\u610f\uff0c\u54b1\u4eec\u53ea\u6709 \u606d\u656c\u4e0d\u5982\u4ece\u547d\u4e86\u3002\u201d\u4e8e\u662f\u4e24\u7236\u5b50\u5c31\u5728\u53f6\u6155\u534e\u5e26\u5f15\u4e4b\u4e0b\uff0c\u8fc7\u53bb\u4e0e\u6c5f\u6d77\u5929\u76f8\u89c1\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u79e6\u4e16\u5144\uff0c\u4ee4\u5e08\u4e0e\u6211\u6709\u4e8c\u4e09\u5341\u5e74\u7684\u4ea4\u60c5\uff0c\u6211\u77e5\u9053\u4f60\u662f\u4ed6\u6700\u5f97\u610f\u7684\u5f1f\u5b50\uff0c\u4f60\u6765 \u5230\u8fd9\u513f\uff0c\u6211\u628a\u4f60\u5f53\u4f5c\u5b50\u4f84\u4e00\u822c\uff0c\u4f60\u7528\u4e0d\u7740\u62d8\u675f\u4e0d\u5b89\u3002\u597d\uff0c\u4f60\u4eec\u4e24\u4f4d\u4e00\u4e2a\u662f\u5929\u5c71\u6d3e\u7684\u540e\u8d77\u4e4b \u79c0\uff0c\u4e00\u4e2a\u662f\u6b66\u5f53\u95e8\u4e0b\u7684\u5c11\u5e74\u82f1\u6770\uff0c\u4f60\u4eec\u4e24\u4eba\u5750\u5728\u4e00\u8d77\uff0c\u4eb2\u8fd1\u4eb2\u8fd1\u3002\u201d\u628a\u6587\u80dc\u4e2d\u7684\u5ea7\u4f4d\u5b89\u6392\u5728 \u5929\u5c71\u6d3e\u5c11\u638c\u95e8\u5510\u52a0\u6e90\u7684\u65c1\u8fb9\u3002 \u3000\u3000\u6587\u9053\u5e84\u7684\u6539\u5bb9\u6613\u8c8c\u4e4b\u672f\u5341\u5206\u5de7\u5999\uff0c\u6c5f\u6d77\u5929\u548c\u4ed6\u9694\u522b\u4e86\u4e8c\u5341\u5e74\uff0c\u679c\u7136\u8ba4\u4e0d\u51fa\u4ed6\uff0c\u8bf4\u9053\uff1a \u201c\u4e91\u5148\u751f\u8fdc\u6765\uff0c\u6c5f\u67d0\u6709\u5931\u8fdc\u8fce\u3002\u4e0d\u77e5\u4e91\u5148\u751f\u4e0e\u6c34\u4e91\u5e84\u7684\u4e91\u5e84\u4e3b\u53ef\u662f\u672c\u5bb6\uff1f\u201d\u6587\u9053\u5e84\u542b\u7cca\u7b54 \u9053\uff1a\u201c\u4e0d\u662f\u3002\u6211\u53ea\u56e0\u4ef0\u6155\u6c5f\u5927\u4fa0\u7684\u5927\u540d\uff0c\u96be\u5f97\u6709\u6b64\u673a\u4f1a\uff0c\u79e6\u4e16\u5144\u9080\u6211\u6765\uff0c\u6211\u4e5f\u5c31\u4e0d\u8bf7\u81ea\u6765 \u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u65e9\u5df2\u542c\u5f97\u53f6\u6155\u534e\u7684\u7980\u62a5\uff0c\u8bf4\u662f\u6b64\u4eba\u6b66\u529f\u6781\u9ad8\uff0c\u4f46\u4ed6\u4ee5\u4e3b\u4eba\u5e76\u517c\u6b66\u5b66\u5927\u5b97\u5e08\u7684\u8eab \u4efd\uff0c\u5374\u662f\u4e0d\u4fbf\u4eb2\u81ea\u51fa\u624b\u8bd5\u4ed6\u3002\u4e0d\u8fc7\uff0c\u6c5f\u6d77\u5929\u542c\u4e86\u4ed6\u7684\u8bdd\uff0c\u4e5f\u4e0d\u89c9\u8d77\u4e86\u4e00\u70b9\u6000\u7591\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u53ea\u89c1\u8d35\u5bbe\u865a\u4f4d\u5f85\uff0c\u8c01\u77e5\u5374\u662f\u5bf9\u5934\u6765\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e94\u56de\u3000\u91cd\u6765\u8e2a\u8ff9\u4ece\u4f55\u89c5\u3000\u51fa\u5904\u6069\u4ec7\u5fcd\u7ec6\u8bba \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e94\u56de\u3000\u91cd\u6765\u8e2a\u8ff9\u4ece\u4f55\u89c5\u3000\u51fa\u5904\u6069\u4ec7\u5fcd\u7ec6\u8bba \u3000\u3000\u6c5f\u6d77\u5929\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u4ed6\u548c\u79e6\u5143\u6d69\u4e3a\u4e16\u5144\uff0c\u79e6\u5143\u6d69\u4ee5\u6b66\u5f53\u540e\u8f88\u5f1f\u5b50\u7684\u8eab\u4efd\uff0c\u6562\u4f5c\u4e3b\u9080\u4ed6\u540c \u6765\uff0c\u60f3\u5fc5\u4ed6\u4e0e\u6b66\u5f53\u6d3e\u6709\u6df1\u539a\u7684\u4ea4\u60c5\uff0c\u8981\u4e0d\u7136\u79e6\u5143\u6d69\u4e0d\u4f1a\u5982\u6b64\u3002\u4f46\u6211\u4e0e\u96f7\u9707\u5b50\u76f8\u4ea4\u4e8c\u5341\u5e74\uff0c\u5374 \u4ece\u6765\u4e0d\u66fe\u542c\u5f97\u96f7\u9707\u5b50\u63d0\u8d77\u8fc7\u6b64\u4eba\u7684\u540d\u5b57\uff0c\u8fd9\u5374\u662f\u4f55\u6545\u5462\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u867d\u6709\u6240\u7591\uff0c\u4f46\u4ed6\u662f\u4e00\u4e2a\u6b63\u76f4\u7684\u4eba\uff0c\u7d20\u6765\u4e0d\u82df\u8a00\u7b11\uff0c\u5bf9\u7b2c\u4e00\u6b21\u89c1\u9762\u7684\u201c\u751f\u5ba2\u201d\uff0c\u5f53 \u7136\u4e0d\u597d\u610f\u601d\u53bb\u76d8\u95ee\u4ed6\u7684\u6765\u5386\uff0c\u6b8a\u4e0d\u77e5\u8fd9\u4e2a\u4ed6\u8ba4\u4e3a\u662f\u201c\u7b2c\u4e00\u6b21\u89c1\u9762\u201d\u7684\u751f\u5ba2\uff0c\u5374\u662f\u4e8c\u5341\u5e74\u524d\u548c \u4ed6\u4ea4\u8fc7\u624b\u7684\u3002 \u3000\u3000\u4e10\u5e2e\u5e2e\u4e3b\u4ef2\u957f\u7edf\u548c\u5ce8\u5d4b\u6d3e\u540d\u5bbf\u4e00\u9633\u5b50\u6b63\u5728\u4e92\u76f8\u63a8\u8ba9\uff0c\u4e0d\u80af\u5750\u4e0a\u9996\u5e2d\u7684\u4f4d\u5b50\u3002\u6b64\u65f6\u6c5f\u6d77\u5929 \u6b63\u8981\u8bf7\u6587\u9053\u5e84\u5165\u5e2d\uff0c\u4ef2\u957f\u7edf\u5fc3\u5934\u4e00\u52a8\uff0c\u84e6\u5730\u60f3\u9053\uff1a\u201c\u6c5f\u6d77\u5929\u4e0d\u4fbf\u8bd5\u4ed6\uff0c\u6211\u4f55\u4e0d\u4ee3\u4e3b\u4eba\u8bd5\u4ed6\u4e00 \u8bd5\u3002\u201d\u4e8e\u662f\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u4f38\u51fa\u624b\u53bb\uff0c\u4fbf\u628a\u6587\u9053\u5e84\u4e00\u62c9\uff0c\u8bf4\u9053\uff1a\u201c\u4e91\u5148\u751f\u662f\u8fdc\u6765\u7684\u8d35\u5ba2\uff0c\u8fd8 \u662f\u8bf7\u4e91\u5148\u751f\u5750\u8fd9\u9996\u5e2d\u5427\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u8fde\u5fd9\u51fa\u638c\u4e00\u63a8\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u600e\u6562\u50ed\u8d8a\u4e24\u4f4d\u8001\u524d\u8f88\uff1f\u201d\u4ef2\u957f\u7edf\u7684\u6df7\u5143\u4e00\u6c14\u529f\u4f55\u7b49\u5389 \u5bb3\uff0c\u4e00\u63a8\u4e00\u62c9\u4e4b\u4e0b\uff0c\u6587\u9053\u5e84\u7684\u90a3\u4ef6\u957f\u886b\u8d77\u4e86\u4e00\u5708\u5708\u7684\u7ec9\u7eb9\uff0c\u98ce\u5e06\u4f3c\u7684\u9f13\u6da8\u8d77\u6765\u3002\u53ef\u662f\u4ef2\u957f\u7edf \u7684\u957f\u987b\u4e5f\u662f\u6296\u52a8\u5f97\u7c41\u7c41\u4f5c\u54cd\uff0c\u663e\u7136\u4e24\u4eba\u90fd\u5728\u8fd0\u7528\u5185\u529b\u76f8\u6297\uff0c\u4e0d\u5206\u4e0a\u4e0b\u3002 \u3000\u3000\u6587\u9053\u5e84\u7684\u201c\u4e09\u8c61\u795e\u529f\u201d\u4e0e\u4e2d\u539f\u5404\u5927\u95e8\u6d3e\u7684\u5185\u529f\u90fd\u4e0d\u76f8\u540c\uff0c\u4ef2\u957f\u7edf\u8bd5\u4e0d\u51fa\u4ed6\u7684\u6765\u5386\uff0c\u751a\u4e3a \u60ca\u5f02\uff0c\u6587\u9053\u5e84\u5df2\u5750\u4e86\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u662f\u4ef2\u5e2e\u4e3b\u4e0a\u5750\u5427\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7684\u4e09\u5f1f\u5b50\u674e\u5149\u590f\u662f\u5728\u5916\u9762\u5e2e\u5fd9\u62db\u5f85\u5ba2\u4eba\u7684\uff0c\u521a\u624d\u53f6\u6155\u534e\u6258\u4ed6\u53bb\u627e\u5bfb\u5c01\u5b50\u8d85\uff0c\u6b64\u65f6 \u56de\u6765\u7980\u62a5\u5e08\u5144\u3002\u4ed6\u628a\u53f6\u6155\u534e\u62c9\u8fc7\u4e00\u8fb9\uff0c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u51e0\u4e2a\u5395\u6240\u90fd\u627e\u8fc7\u4e86\uff0c\u4e0d\u89c1\u6b64\u4eba\u3002\u4e5f\u4e0d\u77e5 \u4ed6\u8eb2\u5728\u54ea\u513f\uff1f\u201d \u3000\u3000\u539f\u6765\u5c01\u5b50\u8d85\u5df2\u7ecf\u53d1\u73b0\u79e6\u5143\u6d69\u5728\u957f\u5eca\u4e4b\u4e2d\u559d\u8336\uff0c\u5413\u5f97\u4ed6\u8fde\u5fd9\u6e9c\u8d70\u3002\u4ed6\u672c\u6765\u662f\u548c\u6587\u9053\u5e84\u540c\u4e00 \u5e2d\u7684\uff0c\u4ed6\u6e9c\u8d70\u4e4b\u65f6\uff0c\u6b63\u503c\u6c5f\u6d77\u5929\u8981\u8bf7\u4ed6\u4eec\u8fc7\u53bb\uff0c\u6587\u9053\u5e84\u5168\u795e\u601d\u7d22\u5982\u4f55\u5bf9\u4ed8\uff0c\u7adf\u4e0d\u77e5\u4ed6\u5df2\u662f\u4e00 \u58f0\u4e0d\u54cd\u5730\u5077\u8dd1\uff0c\u800c\u5c01\u5b50\u8d85\u4e3a\u4e86\u907f\u7978\uff0c\u4e16\u4e0d\u6562\u548c\u6587\u9053\u5e84\u6253\u4e2a\u7167\u4f1a\u3002\u56e0\u4e3a\u4e00\u4e2a\u4eba\u6e9c\u8d70\u5bb9\u6613\uff0c\u4e09\u4e2a \u4eba\u4e00\u540c\u8d70\u5c31\u96be\u514d\u60f9\u4eba\u6ce8\u76ee\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u662f\u4f46\u6c42\u65e9\u65e9\u8131\u8eab\uff0c\u672a\u6765\u7684\u8363\u534e\u5bcc\u8d35\u90a3\u662f\u5b81\u53ef\u4e0d\u8981\u4e86\u3002\u5f53\u65f6\u6b63\u6709\u4e00\u6279\u5bbe\u5ba2\u544a\u8f9e\uff0c\u5728 \u5927\u95e8\u9001\u5ba2\u7684\u6c13\u5c71\u6d3e\u5f1f\u5b50\u767d\u96c4\u53c8\u4e0d\u8ba4\u5f97\u4ed6\uff0c\u4e8e\u662f\u4fbf\u7ed9\u4ed6\u8f7b\u8f7b\u6613\u6613\u5730\u8dd1\u6389\u3002 \u3000\u3000\u53f6\u6155\u534e\u9053\uff1a\u201c\u4f60\u518d\u53bb\u627e\u627e\u3002\u201d\u9063\u8d70\u4e86\u674e\u5149\u590f\uff0c\u4fbf\u7980\u544a\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u90a3\u4f4d\u98ce\u5148\u751f\u8fd8\u672a\u627e \u7740\u3002\u201d\u6587\u9053\u5e84\u9053\uff1a\u201c\u6211\u8fd9\u4f4d\u670b\u53cb\u6709\u70b9\u4e0d\u5927\u8212\u670d\uff0c\u6211\u770b\u4e0d\u5fc5\u518d\u7b49\u4ed6\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u597d\uff0c\u90a3 \u5c31\u8ba9\u98ce\u5148\u751f\u5148\u6b47\u4e00\u6b47\u5427\u3002\u6155\u534e\uff0c\u4f60\u8bf7\u53f6\u5927\u592b\u6216\u97e9\u5927\u592b\u7167\u6599\u7167\u6599\u8d35\u5ba2\u3002\u201d\u53f6\u6155\u534e\u9053\uff1a\u201c\u6211\u5df2\u7ecf \u4ea4\u4ee3\u8fc7\u4e86\u3002\u201d \u3000\u3000\u6b64\u65f6\u4e00\u9633\u5b50\u4e0e\u4ef2\u957f\u7edf\u8fd8\u5728\u4f60\u63a8\u6211\u8ba9\u3002\u5c01\u5b50\u8d85\u4e0d\u6765\uff0c\u8fd9\u4e00\u5e2d\u4e5f\u8fd8\u6709\u4e2a\u7a7a\u4f4d\u672a\u6709\u4eba\u5750\u3002\u53f6\u6155 \u534e\u4f8d\u7acb\u4e00\u65c1\u7b49\u5019\u5e08\u7236\u7684\u5429\u5490\uff0c\u770b\u662f\u8981\u8bf7\u4f55\u4eba\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u8001\u524d\u8f88\u4e0d\u5fc5\u63a8\u8ba9\u4e86\u3002\u6211\u770b\u8fd9\u6837\u5427\uff0c\u4e61\u515a\u8bba\u9f7f\uff0c\u4e00\u9633\u9053\u957f\u4f3c\u4e4e\u6bd4\u4ef2\u5e2e \u4e3b\u5e74\u7eaa\u5927\u4e9b\uff0c\u4e0d\u77e5\u6211\u731c\u5f97\u5bf9\u4e0d\u5bf9\u3002\u201d\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u4ed6\u6bd4\u6211\u5927\u4e86\u4e94\u5c81\u5462\u3002\u597d\uff0c\u4f60\u8fd9\u8001\u9053\u53ef\u6ca1 \u8bdd\u8bf4\u4e86\u5427\u3002\u5feb\u8bf7\u5c31\u5ea7\u3002\u9152\u90fd\u51c9\u4e86\u3002\u201d\u4e00\u9633\u5b50\u9053\uff1a\u201c\u8fd9\u5982\u4f55\u4f7f\u5f97\uff1f\u4e10\u5e2e\u662f\u5929\u4e0b\u7b2c\u4e00\u5927\u5e2e\uff0c\u6b66\u6797 \u4e2d\u7684\u89c4\u77e9\u5e94\u8be5\u5148\u8bba\u5fb7\u3001\u671b\u3002\u5c11\u6797\u5bfa\u7684\u4e3b\u6301\u5927\u60b2\u7985\u5e08\u6ca1\u6765\uff0c\u8fd9\u9996\u5e2d\u7684\u4f4d\u5b50\u4f60\u4e0d\u5750\u8c01\u8fd8\u6562\u5750\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u8bb8\u591a\u81ed\u89c4\u77e9\uff1f\u82e5\u8bba\u89c4\u77e9\uff0c\u4e10\u5e2e\u7684\u8001\u53eb\u5316\u5c0f\u53eb\u5316\u90fd\u662f\u53ea\u80fd\u5411\u4eba \u8ba8\u996d\u7684\uff0c\u51e0\u66fe\u89c1\u8fc7\u6709\u53eb\u5316\u5b50\u5750\u9996\u5e2d\u7684\uff1f\u201d\u5f53\u7136\u8fd9\u53ea\u662f\u4ef2\u957f\u7edf\u4fe1\u53e3\u5f00\u6cb3\uff0c\u5e76\u975e\u4e10\u5e2e\u771f\u7684\u6709\u8fd9\u4e2a \u4e0d\u80fd\u5750\u9996\u5e2d\u7684\u89c4\u77e9\u3002 \u3000\u3000\u4e0d\u6599\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5374\u5f15\u51fa\u4e00\u4e2a\u5c0f\u53eb\u5316\u4e86\u3002\u5c31\u5728\u54c4\u5802\u7684\u5927\u7b11\u58f0\u4e2d\uff0c\u91d1\u9010\u6d41\u5ffd\u5730\u4e00\u8dc3\u800c\u51fa\uff0c \u5927\u6447\u5927\u6446\u7684\u5411\u4ed6\u4eec\u8fd9\u5e2d\u8d70\u6765\uff0c\u4e5f\u5728\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u6b63\u627e\u4e0d\u5230\u4f4d\u5b50\uff0c\u5374\u539f\u6765\u8fd9\u91cc\u8fd8\u6709\u4e00\u4e2a\u7a7a \u4f4d\uff0c\u5c0f\u53eb\u5316\u96be\u5f97\u6709\u5927\u7897\u9152\u5927\u5757\u8089\u5403\u559d\uff0c\u4f60\u4eec\u63a8\u8ba9\u5427\uff0c\u6211\u53ef\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d\u4e00\u5c41\u80a1\u7adf\u7136\u5c31\u5728\u9996\u5e2d \u7684\u4f4d\u5b50\u5927\u9a6c\u91d1\u5200\u5730\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5f53\u91d1\u9010\u6d41\u8d70\u6765\u7684\u65f6\u5019\uff0c\u53f6\u6155\u534e\u6084\u6084\u7684\u5411\u5e08\u7236\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u53eb\u5316\u4f3c\u4e4e\u5927\u6709\u6765\u5934\uff0c\u6211\u8bd5\u8fc7\u4ed6 \u7684\u6b66\u529f\uff0c\u5f53\u771f\u662f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u8bd5\u4e0d\u51fa\u4ed6\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8fd9\u4e00\u5750\u4e0b\u6765\uff0c\u6ee1\u5802\u5931\u8272\uff0c\u8fde\u4e00\u9633\u5b50\u7b49\u4eba\u4e5f\u6115\u7136\u4e0d\u77e5\u6240\u63aa\u3002\u4ef2\u957f\u7edf\u6c14\u5f97\u9762\u8272\u53d1\u7d2b\uff0c \u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u53eb\u5316\u662f\u54ea\u91cc\u6765\u7684\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u662f\u8c01\u5417\uff1f\u201d\u91d1\u9010\u6d41\u7b11\u563b\u563b\u9053\uff1a\u201c\u4f60\u662f\u8c01\u5440\uff1f\u4ed6 \u4eec\u53eb\u4f60\u505a\u5e2e\u4e3b\uff0c\u6211\u5374\u4e0d\u77e5\u4f60\u662f\u54ea\u4e00\u5e2e\u7684\u5e2e\u4e3b\uff1f\u201d\u4ef2\u957f\u7edf\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4e10\u5e2e\u7684\u5e2e\u4e3b\uff0c\u5929\u4e0b \u7684\u5c0f\u53eb\u5316\u90fd\u5f52\u6211\u7ba1\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\u3002\u201d\u54e6\uff0c\u90a3\u4f60\u53ef\u7ba1\u6211\u4e0d\u7740\u4e86\u3002\u6211\u662f\u5ba2\u4e32\u7684\u5c0f\u53eb\u5316\u3001\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u8fd8\u517c\u4f5c\u5c0f \u5077\uff0c\u5077\u4e0d\u5230\u4e1c\u897f\u65f6\u624d\u5076\u5c14\u8ba8\u996d\u7684\u3002\u5f85\u6211\u5c06\u6765\u51b3\u5fc3\u53ea\u4f5c\u5c0f\u53eb\u5316\u7684\u65f6\u5019\uff0c\u518d\u8bf7\u4f60\u8fd9\u8001\u53eb\u5316\u6536\u6211\u8fdb \u5e2e\u5427\u3002\u6c5f\u5927\u4fa0\uff0c\u4f60\u4eec\u4e3b\u4eba\u5bb6\u4e0d\u5acc\u6211\u8fd9\u5c0f\u53eb\u5316\u517c\u4f5c\u5c0f\u5077\u7684\u4e0e\u4f60\u540c\u5e2d\u5427\uff1f\u563f\u3001\u563f\uff0c\u4e3b\u4eba\u5bb6\u4e0d\u8d76\u6211 \u8dd1\uff0c\u90a3\u5c31\u8c01\u4e5f\u7ba1\u6211\u4e0d\u4e86\u3002\u6211\u53ef\u4e0d\u5bb9\u6c14\u8981\u5148\u559d\u9152\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e5f\u89c9\u5f97\u8fd9\u5c0f\u53eb\u5316\u6709\u70b9\u80e1\u95f9\uff0c\u4e0d\u8fc7\u4ed6\u662f\u4e2a\u7231\u624d\u7684\u4eba\uff0c\u542c\u5f97\u5927\u5f1f\u5b50\u53f6\u6155\u534e\u8d5e\u8fd9\u5c0f\u53eb\u5316 \u7684\u6b66\u529f\u201c\u6df1\u4e0d\u53ef\u6d4b\u201d\uff0c\u4ed6\u4e5f\u611f\u5230\u8be7\u5f02\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u53eb\u5316\u6709\u8fd9\u6837\u5927\u7684\u80c6\u5b50\uff0c\u53ea\u6015\u666e\u5929\u4e4b \u4e0b\u4e5f\u627e\u4e0d\u5230\u7b2c\u4e8c\u4e2a\u4eba\u3002\u4e14\u4e0d\u8bba\u6b66\u529f\uff0c\u53ea\u8bba\u4ed6\u8fd9\u526f\u80c6\u91cf\uff0c\u6211\u4e5f\u4e0d\u53ef\u5c0f\u89d1\u4ed6\u4e86\u3002\u201d\u4ed6\u662f\u5927\u4fa0\u895f \u6000\uff0c\u522b\u4eba\u90fd\u5728\u53d1\u6c14\uff0c\u6c14\u8fd9\u4e2a\u5c0f\u53eb\u5316\u4e0d\u77e5\u81ea\u91cf\u3002\u6c5f\u6d77\u5929\u5374\u662f\u795e\u8272\u5982\u5e38\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a \u201c\u597d\uff0c\u90a3\u5c31\u8bf7\u91d1\u5144\u5f1f\u559d\u9152\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e3e\u8d77\u9152\u676f\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u5440\uff0c\u9152\u662f\u8981\u8d81\u70ed\u559d\u624d\u597d\u3002\u8bf7\uff0c\u8bf7\uff01\u201d\u4ef2\u957f\u7edf\u5ffd\u5730\u54c8\u54c8\u4e00 \u7b11\uff0c\u4e3e\u638c\u5411\u91d1\u9010\u6d41\u7684\u80a9\u818a\u4e00\u62cd\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u53eb\u5316\u5012\u662f\u723d\u5feb\u5f97\u5f88\uff0c\u8fd9\u4f4d\u5b50\u6211\u8001\u53eb\u5316\u90fd\u4e0d\u6562 \u5750\u4f60\u5374\u6562\u5750\u3002\u597d\uff0c\u4f60\u5750\u7a33\u4e86\uff01\u201d\u4ed6\u662f\u6709\u5fc3\u4ee4\u91d1\u9010\u6d41\u5f53\u573a\u51fa\u4e11\uff0c\u8fd9\u4e00\u638c\u7528\u4e86\u4e94\u6210\u7684\u6df7\u5143\u4e00\u6c14 \u529f\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u7a81\u7136\u5411\u91d1\u9010\u6d41\u7684\u80a9\u818a\u62cd\u4e0b\uff0c\u6c5f\u6d77\u5929\u4e0d\u7981\u5927\u5403\u4e00\u60ca\u3002\u8bd5\u60f3\u4ef2\u957f\u7edf\u7684\u6df7\u5143\u4e00\u6c14\u529f\u4f55\u7b49 \u5389\u5bb3\uff0c\u5c31\u662f\u4e00\u6d41\u9ad8\u624b\u53ea\u6015\u4e5f\u6321\u4e0d\u4f4f\u4ed6\u8fd9\u4e00\u62db\uff0c\u6c5f\u6d77\u5929\u8fde\u5fd9\u4e5f\u5411\u91d1\u9010\u6d41\u5de6\u80a9\u4e00\u62cd\uff0c\u7b11\u9053\uff1a\u201c\u6211 \u6700\u559c\u6b22\u723d\u5feb\u7684\u5c11\u5e74\u8c6a\u6770\u3002\u8001\u5f1f\uff0c\u4f60\u53ef\u5bf9\u4e86\u6211\u7684\u813e\u6c14\u4e86\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u8fd9\u4e00\u62cd\u662f\u8981\u628a\u91d1\u9010\u6d41\u7684\u6905\u5b50\u9707\u5f97\u88c2\u6210\u516b\u5757\uff0c\u597d\u53eb\u4ed6\u6454\u4e00\u5927\u8de4\uff0c\u5f53\u573a\u51fa\u4e11\u7684\u3002\u4ef2\u957f \u7edf\u7684\u6df7\u5143\u4e00\u6c14\u529f\u8fd1\u5e74\u7cbe\u76ca\u6c42\u7cbe\uff0c\u65e9\u5df2\u5230\u4e86\u7089\u706b\u7eaf\u9752\u7684\u5883\u754c\u3002\u5728\u77f3\u5934\u4e0a\u6401\u4e00\u5757\u8c46\u8150\uff0c\u4ed6\u53ef\u4ee5\u638c \u51fb\u8c46\u8150\uff0c\u8c46\u8150\u5b8c\u6574\u800c\u77f3\u5934\u788e\u88c2\u3002\u6240\u4ee5\u4ed6\u662f\u6709\u628a\u63e1\u4e0d\u4f7f\u91d1\u9010\u6d41\u7684\u8eab\u4f53\u53d7\u4f24\u800c\u53ea\u4ee4\u4ed6\u51fa\u4e11\u7684\u3002 \u3000\u3000\u4f46\u6c5f\u6d77\u5929\u5e76\u4e0d\u77e5\u9053\u4ef2\u957f\u7edf\u662f\u6253\u8fd9\u4e2a\u4e3b\u610f\uff0c\u4ed6\u751f\u6015\u4ef2\u957f\u7edf\u4e00\u65f6\u607c\u6012\uff0c\u4e0d\u77e5\u8f7b\u91cd\uff0c\u8981\u628a\u91d1\u9010 \u6d41\u6253\u6210\u91cd\u4f24\uff0c\u6240\u4ee5\u4ed6\u90a3\u4e00\u62cd\u5374\u662f\u7528\u4e86\u201c\u9694\u7269\u4f20\u529f\u201d\u7684\u672c\u9886\uff0c\u5e2e\u5fd9\u91d1\u9010\u6d41\u5316\u89e3\u4ef2\u957f\u7edf\u7684\u638c\u529b \u7684\u3002\u4f46\u4ed6\u51fa\u624b\u5728\u4ef2\u957f\u7edf\u4e4b\u540e\uff0c\u8fd9\u4e00\u62cd\u4e5f\u53ea\u662f\u65e0\u53ef\u5948\u4f55\u4e2d\u7684\u89e3\u6551\u529e\u6cd5\u800c\u5df2\u3002 \u3000\u3000\u5728\u6c5f\u6d77\u5929\u7684\u5fc3\u76ee\u4e2d\u8fd9\u5c0f\u53eb\u5316\u7684\u672c\u9886\u518d\u9ad8\u4e5f\u51b3\u8ba1\u9ad8\u4e0d\u8fc7\u4ef2\u957f\u7edf\uff0c\u56e0\u6b64\u4ed6\u53ea\u80fd\u5e0c\u671b\u8fd9\u5c0f\u53eb\u5316 \u5c11\u53d7\u4e9b\u4f24\uff0c\u5728\u4ed6\u8fc5\u901f\u7684\u5316\u89e3\u4e86\u4ef2\u957f\u7edf\u7684\u638c\u529b\u4e4b\u540e\uff0c\u53ef\u4ee5\u514d\u4e8e\u6b8b\u5e9f\u3002 \u3000\u3000\u4e14\u8bf4\u4ef2\u957f\u7edf\u4e00\u638c\u62cd\u4e0b\uff0c\u53ea\u89c9\u5bf9\u65b9\u7684\u80a9\u818a\u7adf\u662f\u8f6f\u7ef5\u7ef5\u7684\uff0c\u5c31\u4f3c\u6253\u7740\u4e00\u5806\u68c9\u82b1\u4f3c\u7684\uff0c\u8fd9\u5c0f\u53eb \u5316\u4ecd\u7136\u5927\u9a6c\u91d1\u5200\u5730\u5750\u7740\u4e0d\u52a8\uff0c\u6905\u5b50\u4e5f\u6ca1\u6709\u788e\u88c2\u3002\u4ef2\u957f\u7edf\u5927\u5403\u4e00\u60ca\uff0c\u6b63\u8981\u52a0\u91cd\u638c\u529b\u4e4b\u65f6\uff0c\u6c5f\u6d77 \u5929\u90a3\u4e00\u638c\u4ea6\u5df2\u62cd\u4e0b\u3002\u4ef2\u957f\u7edf\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u8eab\u5f62\u4e00\u6643\uff0c\u5750\u4e86\u4e0b\u6765\uff0c\u5c31\u4f3c\u7ed9\u4eba\u63a8\u7740 \u4ed6\u5750\u5230\u6905\u5b50\u4e00\u6837\uff0c\u90a3\u5f20\u6905\u5b50\u6447\u4e86\u4e24\u6447\uff0c\u5e78\u800c\u6ca1\u6709\u7ffb\u5012\u3002\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u8001\u53eb\u5316\u4f60\u4e5f\u5750\u7a33 \u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5f53\u7136\u77e5\u9053\u8fd9\u5c0f\u53eb\u5316\u662f\u5229\u7528\u4e86\u4ed6\u7684\u638c\u529b\u5c06\u4ef2\u957f\u7edf\u9707\u9000\u7684\u3002\u4ed6\u672c\u610f\u53ea\u662f\u60f3\u5316\u89e3\u4ef2\u957f\u7edf \u7684\u638c\u529b\uff0c\u5374\u60f3\u4e0d\u5230\u8fd9\u5c0f\u53eb\u5316\u7684\u672c\u8eab\u529f\u529b\u7adf\u7136\u8db3\u4ee5\u4e0e\u4ef2\u957f\u7edf\u6297\u8861\uff0c\u52a0\u4e0a\u4e86\u4ed6\u7684\u90a3\u80a1\u529b\u9053\uff0c\u5c31\u8981 \u5927\u5927\u8d85\u8fc7\u4e86\u4ef2\u957f\u7edf\u4e86\u3002 \u3000\u3000\u4f46\u8fd9\u8fd8\u4e0d\u8db3\u4ee5\u4ee4\u6c5f\u6d77\u5929\u5927\u611f\u60ca\u5947\uff0c\u4eca\u4ed6\u6700\u611f\u60ca\u5947\u7684\u662f\uff0c\u5f53\u4ed6\u90a3\u4e00\u638c\u62cd\u4e0b\u53bb\u7684\u65f6\u5019\uff0c\u4ed6\u662f \u51c6\u5907\u8fd9\u5c0f\u53eb\u5316\u8fd0\u529f\u76f8\u6297\u7684\uff0c\u56e0\u4e3a\u5177\u6709\u4e0a\u4e58\u5185\u529f\u4e4b\u4eba\uff0c\u7a81\u9047\u88ad\u51fb\uff0c\u5fc5\u7136\u4f1a\u751f\u51fa\u53cd\u5e94\u3002\u4e0d\u8fc7\u6c5f\u6d77 \u5929\u81ea\u5fd6\u5728\u5c0f\u53eb\u5316\u8fd0\u52b2\u53cd\u51fb\u4e4b\u4e0b\uff0c\u4ed6\u4ecd\u7136\u53ef\u4ee5\u65bd\u5c55\u9694\u7269\u4f20\u529f\u7684\u672c\u9886\uff0c\u540c\u65f6\u5316\u89e3\u5c0f\u53eb\u5316\u7684\u53cd\u51fb\u4e4b \u529b\u4e0e\u4ef2\u957f\u7edf\u6253\u5728\u5c0f\u53eb\u5316\u8eab\u4e0a\u7684\u638c\u529b\u3002\u4e0d\u6599\u4ed6\u4e00\u638c\u6253\u4e86\u4e0b\u53bb\uff0c\u5374\u53d1\u89c9\u8fd9\u5c0f\u53eb\u5316\u7684\u5185\u529b\u4e0e\u4ed6\u6c34\u4e73 \u4ea4\u878d\uff0c\u4e0d\u662f\u76f8\u6297\u800c\u662f\u76f8\u5408\u3002 \u3000\u3000\u5c0f\u53eb\u5316\u80fd\u4ee4\u6c5f\u6d77\u5929\u7684\u5185\u529b\u548c\u4ed6\u6c34\u4e73\u4ea4\u878d\uff0c\u5fc5\u987b\u5177\u5907\u4e24\u4e2a\u6761\u4ef6\uff1a\u4e00\u662f\u4ed6\u65e9\u5df2\u77e5\u9053\u6c5f\u6d77\u5929\u4e43 \u662f\u52a9\u4ed6\u800c\u975e\u4f24\u4ed6\uff1b\u4e8c\u662f\u4ed6\u7684\u5185\u529f\u8def\u5b50\u4e0e\u6c5f\u6d77\u5929\u662f\u540c\u4e00\u5bb6\u6570\u3002\u6c5f\u6d77\u5929\u7684\u5185\u529f\u4f20\u81ea\u91d1\u4e16\u9057\uff0c\u662f\u53ea \u6b64\u4e00\u5bb6\u7684\u3002\u56e0\u6b64\u6c5f\u6d77\u5929\u5c31\u4e0d\u7531\u5f97\u5927\u611f\u60ca\u5947\u4e86\u3002\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u96be\u9053\u5929\u4e0b\u8fd8\u6709\u54ea\u4f4d\u4e0d\u77e5\u540d\u7684\u6b66 \u5b66\u540d\u5bb6\uff0c\u7adf\u4e5f\u50cf\u6211\u5e08\u7236\u4e00\u6837\uff0c\u7ec3\u6210\u4e86\u6b63\u90aa\u5408\u4e00\u7684\u5185\u529f\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u4fa5\u5e78\u201d\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u8001\u53eb\u5316\u7684\u6df7\u5143\u4e00\u6c14\u529f\u679c\u7136\u5389\u5bb3\uff0c\u8981\u662f\u4ed6 \u7528\u4e86\u5168\u529b\u7684\u8bdd\uff0c\u6211\u867d\u7136\u4e0d\u81f4\u53d7\u4ed6\u6240\u4f24\uff0c\u53ea\u6015\u4e5f\u8981\u5341\u5206\u72fc\u72c8\u4e86\u3002\u5e78\u4e8f\u6709\u6c5f\u5e08\u5144\u52a9\u6211\u4e00\u81c2\u4e4b \u529b\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u5bf9\u8fd9\u5c0f\u53eb\u5316\u66f4\u662f\u4f69\u670d\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u867d\u7136\u4ed6\u662f\u501f\u4e86\u6c5f\u6d77\u5929\u4e4b\u529b\u628a\u6211\u9732\u5f00\uff0c\u4f46\u4ed6\u672c \u8eab\u53d7\u5f97\u8d77\u6211\u8fd9\u4e00\u62cd\uff0c\u529f\u529b\u4e5f\u786e\u662f\u975e\u540c\u5c0f\u53ef\u4e86\u3002\u201d\u4e8e\u662f\u7b11\u9053\uff1a\u201c\u8bba\u8f88\u4efd\u4f60\u4e0d\u8be5\u5750\u8fd9\u4e2a\u4f4d\u5b50\uff0c\u4f46 \u4ee5\u4f60\u8fd9\u6837\u7684\u5e74\u7eaa\u800c\u6709\u8fd9\u6837\u7684\u6b66\u529f\uff0c\u5f53\u4eca\u4e4b\u4e16\u6050\u6015\u662f\u518d\u4e5f\u627e\u4e0d\u5230\u7b2c\u4e8c\u4e2a\u4e86\u3002\u5750\u8fd9\u9996\u5e2d\u3002\u5012\u4e5f\u65e0 \u59a8\u3002\u597d\uff0c\u6211\u8001\u53eb\u5316\u8ba9\u4f60\u4e86\u3002\u5c0f\u54e5\uff0c\u4f60\u8d35\u59d3\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u6697\u7b11\uff1a\u201c\u600e\u89c1\u5f97\u6211\u7684\u8f88\u4efd\u5c31\u4f4e\u4e8e\u4f60\uff1f\u201d\u4f46\u4ed6\u5bf9\u4ef2\u957f\u7edf\u5df2\u6709\u51e0\u5206\u4f69\u670d\uff0c\u5f53\u4e0b\u4e5f \u5c31\u4e00\u6539\u8f7b\u4f7b\u7684\u6001\u5ea6\uff0c\u6b63\u6b63\u7ecf\u7ecf\u5730\u7b54\u9053\uff1a\u201c\u6211\u59d3\u91d1\uff0c\u540d\u9010\u6d41\u3002\u968f\u6ce2\u9010\u6d41\u7684\u9010\u6d41\u4e8c\u5b57\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u540d\u5b57\u5012\u771f\u6709\u610f\u601d\uff0c\u6c5f\u5927\u4fa0\u7684\u5e08\u7236\u91d1\u5927\u4fa0\u5168\u4e16\u9057\u521d\u51fa\u9053\u65f6\uff0c\u662f\u4ee5\u53eb\u5316 \u5b50\u7684\u9762\u76ee\u51fa\u73b0\u6c5f\u6e56\u7684\u3002\u5982\u4eca\u4f60\u4e5f\u59d3\u91d1\uff0c\u540c\u6837\u4e5f\u662f\u7528\u5c0f\u53eb\u5316\u7684\u9762\u76ee\u51fa\u73b0\u3002\u6211\u542c\u91d1\u5927\u4fa0\u8bf4\u8fc7\uff0c\u4ed6 \u4ee5\u2018\u4e16\u9057\u2019\u4e3a\u540d\uff0c\u662f\u8868\u793a\u4e3a\u4e16\u6240\u9057\uff0c\u4e0e\u4e16\u4fd7\u4e0d\u80fd\u76f8\u5408\u4e4b\u610f\uff0c\u800c\u4f60\u5219\u540d\u53eb\u9010\u6d41\uff0c\u968f\u6ce2\u9010\u6d41\uff0c\u8fd9 \u547d\u540d\u7684\u542b\u610f\u6070\u6070\u548c\u91d1\u5927\u4fa0\u7684\u2018\u4e16\u9057\u2019\u4e8c\u5b57\u76f8\u53cd\uff0c\u8fd9\u53ef\u4e0d\u662f\u5f88\u6709\u610f\u601d\u5417\uff1f\u53ef\u60dc\u91d1\u5927\u4fa0\u73b0\u5728\u4e0d\u77e5 \u662f\u5728\u54ea\u513f\uff0c\u8981\u662f\u4ed6\u77e5\u9053\u6709\u4f60\u8fd9\u4e2a\u672c\u5bb6\u5b50\u5f1f\u7684\u8bdd\uff0c\u4ed6\u4e00\u5b9a\u6b22\u559c\u5f97\u4e0d\u5f97\u4e86\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u8981\u6536\u4f60\u4f5c\u4e49 \u5b50\u5462\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u624d\u4e0d\u7a00\u7f55\u505a\u91d1\u5927\u4fa0\u7684\u4e49\u5b50\u5462\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u6ee1\u5802\u5bbe\u5ba2\u4e0d\u7981 \u53c8\u662f\u76f8\u987e\u5931\u8272\uff0c\u89c9\u5f97\u8fd9\u5c0f\u53eb\u5316\u592a\u4e0d\u8bc6\u62ac\u4e3e\u3002\u53ea\u6709\u6c5f\u6d77\u5929\u5374\u662f\u84e6\u5730\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6069 \u5e08\u9041\u8ff9\u6d77\u5916\uff0c\u81f3\u4eca\u5df2\u662f\u6709\u4e8c\u5341\u4e00\u5e74\u6ca1\u6709\u6d88\u606f\u4e86\u3002\u5018\u82e5\u4ed6\u6709\u513f\u5b50\u7684\u8bdd\uff0c\u5012\u662f\u548c\u8fd9\u5c0f\u53eb\u5316\u7684\u5e74\u7eaa \u5dee\u4e0d\u591a\u3002\u201d\u91d1\u4e16\u9057\u3001\u6c5f\u6d77\u5929\u4e24\u5e08\u5f92\u662f\u540c\u4e00\u65e5\u4f5c\u65b0\u90ce\u7684\uff0c\u6240\u4ee5\u91d1\u4e16\u9057\u82e5\u6709\u513f\u5b50\uff0c\u5e94\u8be5\u662f\u4e0e\u6c5f\u6d77 \u5929\u7684\u5973\u513f\u5e74\u7eaa\u76f8\u5f53\u3002 \u3000\u3000\u53f6\u6155\u534e\u7b11\u9053\uff1a\u201c\u6211\u7ed9\u4f60\u4eec\u4ecb\u7ecd\u4ecb\u7ecd\u3002\u8fd9\u4f4d\u662f\u4e91\u5148\u751f\uff0c\u8fd9\u4f4d\u662f\u6b66\u5f53\u6d3e\u7684\u79e6\u5c11\u4fa0\u3002\u79e6\u5c11\u4fa0\uff0c \u6050\u6015\u4f60\u8fd8\u672a\u77e5\u9053\u5427\uff0c\u8fd9\u4f4d\u91d1\u5144\u662f\u4e0e\u4ee4\u5f1f\u540c\u6765\u7684\u3002\u201d\u6587\u80dc\u4e2d\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u54ea\u6765\u7684\u5f1f \u5f1f\uff1f\u201d\u6587\u9053\u5e84\u5df2\u77e5\u4e0d\u5999\u3002\u5fc3\u60f3\uff1a\u201c\u4e0d\u77e5\u8fd9\u5c0f\u53eb\u5316\u641e\u7684\u662f\u4ec0\u4e48\u9b3c\u628a\u620f\uff1f\u6211\u53ef\u5f97\u5f53\u5fc3\u4e9b\u513f\u3002\u4e0d\u8fc7 \u60f3\u6765\u8fd9\u5c0f\u53eb\u5316\u4e5f\u4e0d\u5e94\u8be5\u77e5\u9053\u6211\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u4e00\u9633\u5b50\u4e0e\u96f7\u9707\u5b50\u662f\u5e38\u6709\u5f80\u6765\u7684\u670b\u53cb\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u5fc3\u4e2d\u8be7\u5f02\uff1a\u201c\u6211\u53ea\u77e5\u9053\u96f7\u9707\u5b50\u53ea\u6709\u4e00\u4e2a \u59d3\u79e6\u7684\u5f1f\u5b50\uff0c\u79e6\u5143\u6d69\u8fd8\u6709\u4e00\u4e2a\u5f1f\u5f1f\u8fd9\u6211\u53ef\u6ca1\u542c\u8bf4\u8fc7\u3002\u96be\u9053\u662f\u6700\u8fd1\u624d\u62dc\u7684\u5e08\uff1f\u201d\u6587\u80dc\u4e2d\u6539\u626e\u5f97 \u5341\u5206\u5de7\u5999\uff0c\u4e00\u9633\u5b50\u5728\u6b66\u5f53\u5c71\u867d\u7136\u89c1\u8fc7\u79e6\u5143\u6d69\u51e0\u6b21\uff0c\u7531\u4e8e\u5fc3\u4e2d\u6ca1\u6709\u8d77\u7591\uff0c\u521a\u624d\u4e5f\u5c31\u89c9\u5bdf\u4e0d\u51fa\u4ed6 \u662f\u5047\u5192\uff0c\u4f46\u73b0\u5728\u542c\u4e86\u8fd9\u8bdd\uff0c\u4e0d\u7981\u5bf9\u6587\u80dc\u4e2d\u591a\u77a7\u4e86\u4e24\u773c\uff0c\u5374\u9690\u9690\u7684\u611f\u89c9\u5230\u6709\u70b9\u4ec0\u4e48\u4e0d\u5bf9\u4e86\u3002 \u3000\u3000\u4e00\u9633\u5b50\u7684\u6027\u60c5\u4e0e\u4ef2\u957f\u7edf\u76f8\u53cd\u3002\u4ef2\u957f\u7edf\u6700\u7231\u591a\u7ba1\u95f2\u4e8b\uff0c\u800c\u4ed6\u5219\u662f\u6700\u4e0d\u597d\u4e8b\u7684\u3002\u4e0d\u8fc7\u65e2\u7136\u6709 \u4e86\u6000\u7591\uff0c\u4ed6\u4e5f\u60f3\u8981\u628a\u79e6\u5143\u6d69\u7684\u5f1f\u5f1f\u627e\u6765\u4e00\u89c1\u3002 \u3000\u3000\u4e00\u9633\u5b50\u6b63\u8981\u5f00\u53e3\uff0c\u5ffd\u542c\u5f97\u73af\u4f69\u53ee\u549a\uff0c\u539f\u6765\u662f\u65b0\u5a18\u5df2\u7ecf\u51fa\u6765\u656c\u9152\u4e86\u3002\u4e00\u9633\u5b50\u628a\u8bdd\u8bed\u54bd\u56de\uff0c \u5fc3\u9053\uff1a\u201c\u4e14\u5f85\u656c\u9152\u8fc7\u4e86\u518d\u95ee\u5143\u6d69\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u6c5f\u6653\u8299\u548c\u5b87\u6587\u96c4\u662f\u7ecf\u8fc7\u8bb8\u591a\u6ce2\u6298\u624d\u6210\u5a5a\u7684\u3002\u4eca\u65e5\u5979\u505a\u4e86\u65b0\u5a18\uff0c\u7531\u65b0\u90ce\u966a\u5979\u51fa\u6765\u656c\u9152\uff0c\u4e0d \u7981\u559c\u4e0a\u7709\u68a2\uff0c\u77eb\u7f9e\u4e4b\u4e2d\u66f4\u6dfb\u4e86\u51e0\u5206\u59a9\u5a9a\u3002\u53ef\u662f\u5f53\u5979\u7684\u59d7\u59d7\u83b2\u6b65\uff0c\u6765\u5230\u8d35\u5bbe\u7684\u4e00\u5e2d\u4e4b\u65f6\uff0c\u5374\u662f \u4e0d\u7531\u5f97\u6123\u4f4f\u4e86\u3002 \u3000\u3000\u6309\u7406\u5979\u662f\u5e94\u8be5\u5411\u9996\u5e2d\u8d35\u5bbe\u5148\u884c\u656c\u9152\u7684\uff0c\u4f46\u5979\u5374\u600e\u60f3\u5f97\u5230\u8fd9\u4e2a\u9996\u5e2d\u7684\u8d35\u5bbe\u7adf\u662f\u4e2a\u5c0f\u53eb\u5316\uff01 \u800c\u540c\u4e00\u5e2d\u7684\u53c8\u6709\u4e24\u4f4d\u8f88\u4efd\u6781\u9ad8\u4e0e\u5e08\u95e8\u6e0a\u6e90\u6781\u539a\u7684\u4e24\u4f4d\u8001\u524d\u8f88\u2014\u2014\u4ef2\u957f\u7edf\u4e0e\u4e00\u9633\u5b50\u3002\u5979\u5e94\u8be5\u5411 \u8c01\u5148\u656c\u9152\u5462\uff1f \u3000\u3000\u6025\u4e2d\u751f\u667a\uff0c\u6c5f\u6653\u8299\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8bf7\u5404\u4f4d\u8d35\u5ba2\u559d\u676f\u6de1\u9152\u3002\u201d\u90a3\u662f\u8868\u793a\u5bf9\u5e2d\u4e0a\u7684\u5ba2\u4eba\u90fd\u4e00\u6837 \u5c0a\u91cd\uff0c\u8ba9\u5ba2\u4eba\u81ea\u5df1\u53d6\u9152\u3002\u5ba2\u4eba\u90fd\u7ad9\u4e86\u8d77\u6765\uff0c\u53ea\u6709\u6c5f\u6d77\u65e0\u592b\u59bb\u4ee5\u5bb6\u957f\u7684\u8eab\u4efd\u7aef\u5750\u4e0d\u52a8\u3002 \u3000\u3000\u6587\u9053\u5e84\u6479\u4ed6\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u5f53\u3002\u201d\u628a\u6258\u76d8\u8f7b\u8f7b\u4e00\u63a8\u3002\u770b\u662f\u8f7b\u8f7b\u4e00\u63a8\uff0c\u5176\u5b9e\u5df2\u662f\u8fd0\u7528\u4e86\u4e09\u8c61 \u795e\u529f\uff0c\u8981\u628a\u6c5f\u6653\u8299\u9707\u4f24\uff0c\u8ddf\u7740\u4fbf\u8981\u51fa\u624b\u64d2\u5979\u4f5c\u4e3a\u4eba\u8d28\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u6709\u8fd9\u79cd\u4e8b\u60c5\u53d1\u751f\uff0c\u4f46\u91d1\u9010\u6d41\u5219\u662f\u65e9\u5df2\u51c6\u5907\u597d\u4e86\u7684\u3002\u5c31\u5728\u8fd9\u540c\u4e00\u77ac \u95f4\uff0c\u91d1\u9010\u6d41\u4e5f\u84e6\u5730\u628a\u90a3\u6258\u76d8\u4e00\u63a8\uff0c\u76d8\u4e2d\u7684\u9152\u676f\u767b\u65f6\u90fd\u8df3\u4e86\u8d77\u6765\uff0c\u9152\u82b1\u56db\u6e85\uff0c\u8fde\u6c5f\u6d77\u5929\u4e5f\u7ed9\u6e85 \u5f97\u6ee1\u5934\u6ee1\u9762\uff0c\u4f46\u6587\u9053\u5e84\u7684\u4e09\u8c61\u795e\u529f\uff0c\u5374\u662f\u7ed9\u91d1\u9010\u6d41\u6d88\u89e3\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u53c8\u4e0d\u662f\u7279\u5730\u7ed9\u4f60\u656c\u9152\uff0c\u8981\u4f60\u5148\u8bf4\u4ec0\u4e48\u4e0d\u6562\u5f53\uff1f\u201d\u6211\u5750\u9996\u5e2d\uff0c\u6211\u90fd\u8fd8 \u672a\u66fe\u559d\u9152\u5462\uff01\u201d \u3000\u3000\u6c5f\u6653\u8299\u5413\u5f97\u5446\u82e5\u6728\u9e21\uff0c\u4f46\u5979\u8fd8\u672a\u77e5\u9053\u5979\u7684\u6027\u547d\u5df2\u662f\u5728\u90a3\u6258\u76d8\u4e00\u8f6c\u4e4b\u95f4\uff0c\u4ece\u6b7b\u4ea1\u7684\u8fb9\u7f18\u4e0a \u8f6c\u4e86\u56de\u6765\uff0c\u7531\u751f\u5230\u6b7b\uff0c\u7531\u6b7b\u5230\u751f\uff0c\u4f5c\u4e86\u4e00\u4e2a\u5faa\u73af\u3002\u4e0d\u4f46\u6c5f\u6653\u8299\u832b\u7136\u4e0d\u89e3\uff0c\u4e00\u4f17\u5bbe\u5ba2\u4e5f\u90fd\u4e0d\u660e \u767d\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u60c5\uff0c\u8c01\u4e5f\u4e0d\u6562\u60f3\u8c61\u6587\u9053\u5e84\u7adf\u662f\u5305\u85cf\u7978\u5fc3\uff0c\u6709\u90a3\u4e48\u5927\u7684\u80c6\u5b50\u8981\u6697\u5bb3\u5929\u4e0b\u7b2c\u4e00 \u9ad8\u624b\u6c5f\u6d77\u5929\u7684\u5973\u513f\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u662f\u660e\u767d\u7684\uff0c\u4f46\u6587\u9053\u5e84\u7684\u201c\u4e09\u8c61\u795e\u529f\u201d\u5e76\u4e0d\u662f\u4ed6\u51fa\u624b\u6d88\u89e3\uff1b\u56e0\u6b64\u4ed6\u4e5f\u8fd8\u672a\u80fd\u7701\u609f\u6587 \u9053\u5e84\u662f\u8c01\u3002\u5f53\u4e0b\uff0c\u4ed6\u5c31\u6682\u4e0d\u4f5c\u58f0\uff0c\u770b\u6587\u9053\u5e84\u5982\u4f55\u8a00\u8bed\u3002\u4ef2\u957f\u7edf\u4e0e\u4e00\u9633\u5b50\u89c1\u4e3b\u4eba\u6ca1\u6709\u53d1\u4f5c\uff0c\u4e5f \u5c31\u6682\u4e14\u8896\u624b\u65c1\u89c2\u3002 \u3000\u3000\u6587\u9053\u5e84\u53c8\u60ca\u53c8\u6012\uff0c\u5f3a\u81ea\u4f5c\u6001\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u53eb\u5316\uff0c\u4f60\u597d\u65e0\u793c\uff01\u4e3b\u4eba\u5bb9\u5fcd\u4f60\u7684\u72c2\u5984\uff0c\u8ba9\u4f60\u5750 \u9996\u5e2d\uff0c\u4f60\u5c31\u5984\u81ea\u5c0a\u5927\u4e86\u4e48\uff1f\u201d\u4ed6\u5df2\u77e5\u662f\u518d\u96be\u5f97\u901e\uff0c\u8981\u9003\u4e5f\u9003\u4e0d\u4e86\uff0c\u6545\u6b64\u4e0d\u80fd\u4e0d\u5185\u832c\u800c\u8272\u5386\uff0c \u4f46\u76fc\u53ef\u4ee5\u63a9\u9970\u5f97\u8fc7\u53bb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u80af\u670d\u6211\uff0c\u90a3\u4e48\u54b1\u4eec\u5c31\u8f83\u91cf\u8f83\u91cf\u3002\u6211\u82e6\u8f93\u4e86\u7ed9\u4f60\uff0c\u8ba9\u4f60\u5750\u8fd9\u9996\u5e2d \u4fbf\u662f\u3002\u201d \u3000\u3000\u559c\u7b75\u4e0a\u6709\u5ba2\u4eba\u8981\u6253\u8d77\u6765\uff0c\u8fd9\u5f53\u7136\u662f\u5927\u6740\u98ce\u666f\u4e4b\u4e8b\u3002\u8c37\u4e2d\u83b2\u8fd8\u53ea\u9053\u4ed6\u4eec\u662f\u610f\u6c14\u4e4b\u4e89\uff0c\u4f4e\u58f0 \u8bf4\u9053\uff1a\u201c\u6d77\u5929\uff0c\u4f60\u529d\u4e00\u529d\u5427\u3002\u90fd\u662f\u5ba2\u4eba\uff0c\u4f55\u5fc5\u4f24\u4e86\u548c\u6c14\u3002\u201d\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u4ee5\u6b66\u4f1a\u53cb\uff0c\u4e8b\u5c5e \u5bfb\u5e38\u3002\u96be\u5f97\u8fd9\u4e24\u4f4d\u8d35\u5ba2\u9ad8\u5174\uff01\u54b1\u4eec\u4e5f\u4e50\u5f97\u5f00\u5f00\u773c\u754c\u3002\u8299\u513f\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u5fd9\u7740\u656c\u9152\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6653\u8299\u60ca\u9b42\u7a0d\u5b9a\uff0c\u653e\u4e0b\u6258\u76d8\uff0c\u5750\u5728\u6bcd\u4eb2\u8eab\u65c1\u3002\u8c37\u4e2d\u83b2\u8fd8\u672a\u77e5\u9053\u5973\u513f\u521a\u624d\u662f\u9669\u4e9b\u53d7\u4e86\u6697 \u7b97\uff0c\u4f46\u89c1\u5973\u513f\u9762\u8272\u53d1\u9752\u800c\u4e08\u592b\u53c8\u662f\u8fd9\u822c\u8a00\u8bed\uff0c\u4ea6\u5df2\u77e5\u9053\u4e8b\u60c5\u5b9a\u6709\u8e4a\u8df7\u3002\u5979\u7ed9\u5973\u513f\u628a\u4e86\u628a\u8109\uff0c \u77e5\u5979\u5e76\u6ca1\u6709\u53d7\u4f24\uff0c\u8fd9\u624d\u653e\u5fc3\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5219\u77e5\u9053\u662f\u91d1\u9010\u6d41\u6551\u4e86\u4ed6\u7684\u5973\u513f\u7684\uff0c\u5f53\u7136\u4ed6\u4e5f\u662f\u77e5\u9053\u6587\u9053\u5e84\u4e0d\u6000\u597d\u610f\u7684\u4e86\u3002\u4e0d\u8fc7\uff0c \u4ed6\u5374\u4e0d\u5148\u9053\u7834\uff0c\u6709\u5fc3\u770b\u770b\u8fd9\u4e24\u4eba\u7684\u6b66\u529f\u3002\u5fc3\u91cc\u601d\u91cf\uff1a\u201c\u53ea\u8981\u4ed6\u4eec\u4e00\u52a8\u4e86\u624b\uff0c\u6211\u5c31\u4e0d\u96be\u77e5\u9053\u4ed6 \u4eec\u7684\u6765\u5386\u3002\u8fd9\u5c0f\u53eb\u5316\u8981\u662f\u6253\u4e0d\u8fc7\u7684\u8bdd\uff0c\u6211\u4e5f\u603b\u6709\u529e\u6cd5\u5e2e\u4ed6\u7684\u5fd9\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u65e2\u4e0d\u963b\u62e6\uff0c\u5ba2\u4eba\u4eec\u66f4\u662f\u4e50\u5f97\u770b\u770b\u70ed\u95f9\u3002\u4e8e\u662f\u5728\u56ed\u4e2d\u817e\u51fa\u4e00\u5757\u7a7a\u5730\uff0c\u5927\u5bb6\u90fd\u505c\u4e0b\u7b77 \u5b50\uff0c\u770b\u4ed6\u4eec\u4e8c\u4eba\u6bd4\u6b66\u3001 \u3000\u3000\u6587\u9053\u5e84\u6068\u4e0d\u5f97\u628a\u8fd9\u5c0f\u53eb\u5316\u4e00\u638c\u51fb\u6bd9\uff0c\u4f46\u5374\u4e0d\u5f97\u4e0d\u88c5\u6a21\u4f5c\u6837\u5730\u8bf4\u9053\uff1a\u201c\u8c05\u4f60\u8fd9\u5c0f\u53eb\u5316\u80fd\u6709 \u591a\u5927\u672c\u9886\uff0c\u4f60\u662f\u8981\u70b9\u5230\u5373\u6b62\u8fd8\u662f\u6b7b\u4f24\u4e0d\u8bba\uff1f\u201d\u91d1\u9010\u6d41\u7b11\u563b\u563b\u9053\uff1a\u201c\u968f\u4fbf\u3002\u4f60\u8981\u600e\u4e48\u6253\u6211\u5c31\u966a \u4f60\u600e\u4e48\u6253\u3002\u201d\u6587\u9053\u5e84\u53cc\u7709\u4e00\u7ad6\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u51fa\u62db\u5427\uff01\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u65e2\u7136\u5750\u5728\u9996\u5e2d\uff0c \u7406\u8be5\u8ba9\u4f60\u4e09\u62db\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u5927\u6012\uff0c\u66f4\u4e0d\u7b54\u8bdd\uff0c\u4e00\u638c\u5c31\u5288\u51fa\u53bb\uff0c\u91d1\u9010\u6d41\u7528\u4e86\u4e2a\u201c\u98ce\u522e\u843d\u82b1\u201d\u7684\u8eab\u6cd5\u4e00\u95ea\u95ea\u5f00\uff0c \u7b11\u9053\uff1a\u201c\u6ca1\u6253\u7740\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u6587\u9053\u5e84\u7b2c\u4e8c\u62db\u76f8\u7ee7\u53d1\u51fa\uff0c\u662f\u6781\u4e3a\u51cc\u5389\u7684\u4e00\u62db\u64d2\u62ff\u5b57\u6cd5\uff0c\u53ea\u542c \u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u91d1\u9010\u6d41\u7684\u4e00\u53ea\u8896\u5b50\u7ed9\u4ed6\u6495\u4e86\u4e0b\u6765\uff0c\u4f46\u8fd8\u662f\u8eb2\u8fc7\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u4e2a\u653b\u5f97\u72e0\uff0c\u4e00\u4e2a\u907f\u5f97\u5999\u3002\u5bbe\u5ba2\u90fd\u7981\u4e0d\u4f4f\u559d\u8d77\u5f69\u6765\uff0c\u4f46\u4e5f\u7981\u4e0d\u4f4f\u4e3a\u8fd9\u5c0f\u53eb\u5316\u6697\u6697\u62c5 \u5fc3\uff0c\u8fd9\u5c0f\u53eb\u5316\u7b2c\u4e8c\u62db\u4fbf\u7ed9\u6495\u4e86\u4e00\u53ea\u8896\u5b50\uff0c\u53ea\u6015\u7b2c\u4e09\u62db\u66f4\u96be\u5e94\u4ed8\u3002\u4ef2\u957f\u7edf\u5bf9\u6c5f\u6d77\u5929\u8bf4\u9053\uff1a\u201c\u6211 \u770b\u8fd9\u4e24\u4eba\u7684\u672c\u9886\u53ea\u6015\u662f\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u5c0f\u53eb\u5316\u8981\u8ba9\u5bf9\u65b9\u4e09\u62db\uff0c\u672a\u514d\u592a\u5192\u9669\u4e86\u3002\u201d\u4ed6\u4e0e\u91d1\u9010\u6d41\u662f \u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\uff1f\u56e0\u6b64\u5bf9\u91d1\u9010\u6d41\u9887\u6709\u7231\u60dc\u4e4b\u5fc3\u3002\u6c5f\u6d77\u5929\u542c\u4e86\u4ed6\u7684\u8bdd\uff0c\u5fae\u7b11\u4e0d\u7b54\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u53eb\u5316\u5b50\u7684\u8863\u88f3\u662f\u5e94\u8be5\u7834\u7834\u70c2\u70c2\u7684\uff0c\u591a\u8c22\u4f60\u5e2e\u4e86\u6211\u7684\u5fd9\u4e86\u3002\u201d\u4ed6\u9669\u4e9b\u5403\u4e86 \u5927\u4e8f\uff0c\u5634\u91cc\u5374\u8fd8\u5728\u8bf4\u98ce\u51c9\u8bdd\u3002 \u3000\u3000\u6587\u9053\u5e84\u7ed9\u4ed6\u8fde\u907f\u8fc7\u4e86\u4e24\u62db\uff0c\u5fc3\u4e2d\u4e5f\u662f\u5403\u60ca\u4e0d\u5df2\u3002\u5728\u53c8\u60ca\u53c8\u6012\u4e4b\u4e0b\uff0c\u7b2c\u4e09\u62db\u4f7f\u51fa\u4e86\u72ec\u95e8\u6740 \u624b\u3002 \u3000\u3000\u6b64\u62db\u4e00\u51fa\uff0c\u987f\u7136\u95f4\u53ea\u89c1\u638c\u5f71\u91cd\u91cd\uff0c\u91d1\u9010\u6d41\u7684\u540e\u5de6\u53f3\u7684\u9000\u8def\u5168\u90fd\u7ed9\u4ed6\u5c01\u95ed\u3002\u5468\u56f4\u6570\u4e08\u65b9\u5706 \u4e4b\u5185\uff0c\u5377\u8d77\u4e86\u4e00\u80a1\u65cb\u98ce\uff0c\u6c99\u5c18\u6eda\u6eda\uff0c\u88ab\u65cb\u98ce\u5377\u4e0a\u534a\u7a7a\uff0c\u597d\u50cf\u4e00\u6839\u9ed1\u8272\u7684\u5706\u67f1\uff0c\u6587\u9053\u5e84\u5df2\u662f\u7528 \u4e0a\u4e86\u201c\u4e09\u8c61\u795e\u529f\u3002\u201d \u3000\u3000\u8fd1\u5904\u7684\u5bbe\u5ba2\u8fde\u5fd9\u540e\u9000\uff0c\u6015\u7ed9\u5c18\u6c99\u98de\u5165\u773c\u775b\uff0c\u4f46\u5374\u53c8\u820d\u4e0d\u5f97\u4e0d\u7741\u5927\u4e86\u773c\u775b\u6765\u770b\uff0c\u8981\u770b\u91d1\u9010 \u6d41\u5982\u4f55\u80fd\u591f\u907f\u5f97\u8fc7\u8fd9\u4e00\u62db\u3002 \u3000\u3000\u5c18\u96fe\u8ff7\u6f2b\u4e2d\u4f17\u4eba\u8fd8\u672a\u66fe\u770b\u5f97\u6e05\u695a\uff0c\u53ea\u89c1\u91d1\u9010\u6d41\u5df2\u662f\u8eb2\u8fc7\u4e86\u8fd9\u4e00\u62db\uff0c\u5728\u4e00\u68f5\u6843\u6811\u4e0b\u7b11\u563b\u563b \u5730\u8bf4\u9053\uff1a\u201c\u597d\u5389\u5bb3\uff0c\u53ef\u60dc\u8fd8\u662f\u4f24\u6211\u4e0d\u7740\u3002\u201d \u3000\u3000\u6b66\u529f\u7a0d\u5f31\u7684\u90fd\u770b\u4e0d\u51fa\u91d1\u9010\u6d41\u7528\u7684\u662f\u4ec0\u4e48\u8eab\u6cd5\uff0c\u5c45\u7136\u80fd\u591f\u5728\u5bf9\u65b9\u7684\u638c\u52bf\u7b3c\u7f69\u4e4b\u4e0b\u8131\u8eab\u800c \u51fa\u3002\u4f46\u5bbe\u5ba2\u4e2d\u4e0d\u4e4f\u9ad8\u624b\uff0c\u6709\u4eba\u56b7\u9053\uff1a\u201c\u8fd9\u662f\u5929\u7f57\u6b65\u6cd5\uff01\u201d\u201c\u54a6\uff0c\u8fd9\u5c0f\u53eb\u5316\u4e5f\u4f1a\u5929\u7f57\u6b65\u6cd5\uff0c\u96be \u9053\u662f\u9752\u57ce\u6d3e\u7684\u5f1f\u5b50\uff1f\u201d\u201c\u4e0d\u9519\uff0c\u4ed6\u8e0f\u7684\u597d\u50cf\u662f\u5929\u7f57\u6b65\u6cd5\uff0c\u4f46\u53c8\u4f3c\u6709\u70b9\u4e0d\u5bf9\u3002\u4ed6\u4e0d\u662f\u9752\u57ce\u6d3e \u7684\u3002\u201d\u6700\u540e\u8bf4\u8bdd\u7684\u8fd9\u4e2a\u4eba\u662f\u9752\u57ce\u6d3e\u7684\u9ad8\u624b\u8427\u5fd7\u8fdc\uff0c\u4ed6\u7684\u7956\u7236\u8427\u9752\u5cf0\u5c31\u662f\u7cbe\u901a\u5929\u7f57\u6b65\u6cd5\u7684\u5927\u5b97 \u5e08\u3002 \u3000\u3000\u539f\u6765\u5929\u7f57\u6b65\u6cd5\u59cb\u521b\u4e8e\u9752\u57ce\uff0c\u4f46\u5e76\u975e\u53ea\u6b64\u4e00\u5bb6\u3002\u6c5f\u6d77\u5929\u7684\u5e08\u7236\u91d1\u4e16\u9057\u66fe\u91c7\u7528\u4e86\u9752\u57ce\u6d3e\u7684\u4e94 \u7f57\u6b65\u6cd5\uff0c\u63c9\u6742\u4e86\u4e54\u5317\u6e9f\u79d8\u7b08\u4e0a\u7684\u6b66\u529f\uff0c\u7cbe\u76ca\u6c42\u7cbe\uff0c\u5728\u9752\u57ce\u6d3e\u7684\u57fa\u7840\u4e0a\u52a0\u4ee5\u6539\u8fdb\uff0c\u6bd4\u539f\u6765\u7684\u5929 \u7f57\u6b65\u6cd5\u662f\u66f4\u4e3a\u7cbe\u5999\u4e86\uff0c\u6c5f\u6d77\u5929\u4e00\u89c1\u91d1\u9010\u6d41\u8e0f\u51fa\u4e86\u672c\u95e8\u7684\u5929\u7f57\u6b65\u6cd5\uff0c\u4e0d\u7981\u60ca\u559c\u4ea4\u96c6\uff0c\u5bf9\u91d1\u9010\u6d41 \u7684\u8eab\u4efd\u5fc3\u4e2d\u5df2\u662f\u660e\u767d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5401\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u8fd8\u62db\uff01\u201d\u53f3\u638c\u5212\u51fa\u4e00\u9053\u5f27\u5f62\uff0c\u5de6\u624b\u9a88\u6307\u5982 \u621f\uff0c\u4ece\u8098\u5e95\u7a7f\u51fa\uff0c\u70b9\u6587\u9053\u5e84\u7684\u80f8\u819b\u7a74\u9053\u3002\u6587\u9053\u5e84\u6a2a\u638c\u4e00\u5c01\uff0c\u53cc\u65b9\u5bf9\u4e86\u4e00\u638c\uff0c\u5404\u81ea\u9000\u540e\u4e09\u6b65\u3002 \u3000\u3000\u91d1\u9010\u6d41\u53cc\u6307\u631f\u7740\u4e00\u5e45\u7834\u5e03\uff0c\u7b11\u9053\u3002\u201d\u6295\u6843\u62a5\u674e\u3001\u4f60\u6495\u6211\u7684\u8863\u88f3\uff0c\u6211\u5c82\u80fd\u4e0d\u6495\u4f60\u7684\u3002\u597d\uff0c \u5982\u4eca\u662f\u4e24\u4e0d\u8f93\u4e8f\u4e86\uff1b\u518d\u6765\uff0c\u518d\u6765\uff01\u201d\u539f\u6765\u91d1\u9010\u6d41\u4e43\u662f\u638c\u6307\u517c\u65bd\uff0c\u4ed6\u7684\u53cc\u6307\u70b9\u4e0d\u7740\u6587\u9053\u5e84\u7684\u7a74 \u9053\uff0c\u987a\u624b\u4e00\u52fe\uff0c\u52fe\u7834\u4e86\u4ed6\u7684\u8863\u88f3\u3002 \u3000\u3000\u53f6\u6155\u534e\u770b\u51fa\u91d1\u9010\u6d41\u4f7f\u7684\u662f\u5927\u4e58\u822c\u82e5\u638c\u6cd5\uff0c\u5fc3\u60f3\u3002\u201d\u602a\u4e0d\u5f97\u4ed6\u9002\u624d\u5938\u53e3\uff0c\u679c\u7136\u662f\u6bd4\u6211\u9ad8\u660e \u5f97\u591a\u3002\u201d\u5fc3\u4e2d\u7591\u4e91\u9661\u8d77\uff0c\u6084\u6084\u5730\u95ee\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4ed6\u7528\u7684\u662f\u5927\u4e58\u822c\u82e5\u638c\u5427\uff1f\u8def\u6570\u5012\u597d\u50cf\u548c\u672c\u95e8 \u7684\u76f8\u540c\u3002\u4ed6\u90a3\u6307\u6cd5\u6211\u4e5f\u4f3c\u66fe\u89c1\u5e08\u7236\u4f7f\u8fc7\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u662f\u60ca\u795e\u6307\u6cd5\uff0c\u4f60\u8fd8\u672a\u66fe\u5b66\u5230\u3002\u55ef\uff0c\u8fd9\u5c0f\u53eb\u5316\u7684\u60ca\u795e\u6307\u6cd5\u6bd4\u6211\u8fd8\u8981\u9ad8\u660e\uff0c \u53ef\u60dc\u6b20\u4e86\u4e00\u70b9\u706b\u5019\uff0c\u8981\u4e0d\u7136\u9694\u8863\u70b9\u7a74\uff0c\u8fd9\u59d3\u4e91\u7684\u5df2\u662f\u7981\u53d7\u4e0d\u8d77\u4e86\u3002\u201d\u53f6\u6155\u534e\u542c\u5e08\u7236\u8bf4\u8fd9\u5c0f\u53eb \u5316\u7684\u6307\u6cd5\u66f4\u80dc\u4e8e\u4ed6\uff0c\u8fd9\u4e00\u60ca\u5f53\u771f\u662f\u975e\u540c\u5c0f\u53ef\uff01\u4ed6\u6b63\u60f3\u518d\u5411\u5e08\u7236\u8bf7\u95ee\uff0c\u91d1\u9010\u6d41\u548c\u6587\u9053\u5e84\u5df2\u662f\u518d \u5ea6\u4ea4\u624b\uff0c\u53f6\u5e55\u534e\u987e\u5f97\u4e86\u770b\uff0c\u4e5f\u5c31\u65e0\u6687\u591a\u95ee\u4e86\u3002 \u3000\u3000\u53cc\u65b9\u4ea4\u4e86\u4e00\u638c\uff0c\u90fd\u662f\u4e0d\u6562\u8f7b\u654c\uff0c\u5927\u4e58\u822c\u82e5\u638c\u80fd\u4f24\u5947\u7ecf\u516b\u8109\uff0c\u6587\u9053\u5e84\u867d\u7136\u7981\u53d7\u5f97\u8d77\uff0c\u5728\u5bf9 \u638c\u4e4b\u540e\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u8109\u606f\u4ea6\u611f\u4e0d\u8c03\u3002\u8fd9\u8fd8\u7f62\u4e86\uff0c\u91d1\u9010\u6d41\u7684\u60ca\u795e\u6307\u6cd5\u5982\u6b64\u5947\u5999\uff0c\u66f4\u662f\u4ee4\u4ed6\u60ca \u5f02\u3002\u4ed6\u662f\u4e2a\u8bc6\u8d27\u7684\u4eba\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c0f\u53eb\u5316\u6b66\u5b66\u751a\u6742\uff0c\u8fd8\u4e0d\u77e5\u4ed6\u6709\u4ec0\u4e48\u53e4\u602a\u7684\u62db\u6570\uff0c\u6211\u5012\u8981\u5c0f \u5fc3\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5728\u5bf9\u638c\u4e4b\u540e\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u4e5f\u611f\u5230\u6c14\u8840\u4e0d\u8212\uff0c\u5fc3\u60f3\uff1a\u201c\u602a\u4e0d\u5f97\u7239\u7239\u8bf4\u6587\u5bb6\u53d4\u4f84\u7684\u4e09 \u50cf\u795e\u529f\u4e5f\u7b97\u5f97\u662f\u4e00\u95e8\u6b66\u6797\u7edd\u5b66\uff0c\u770b\u6765\u6b64\u4eba\u7684\u529f\u529b\u4f3c\u4e4e\u6bd4\u6211\u8fd8\u8981\u7a0d\u80dc\u4e00\u7b79\u3002\u201d \u3000\u3000\u4e24\u4eba\u518d\u6b21\u4ea4\u624b\uff0c\u91d1\u9010\u6d41\u91c7\u53d6\u4ee5\u5de7\u964d\u529b\u7684\u6253\u6cd5\uff0c\u5947\u62db\u5999\u7740\u5c42\u51fa\u4e0d\u7a77\u3002\u6587\u9053\u5e84\u6349\u6478\u4e0d\u900f\u4ed6\u7684 \u8def\u6570\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u5e94\u4ed8\u3002\u4e0d\u6c42\u6709\u529f\uff0c\u4f46\u6c42\u65e0\u8fc7\uff0c\u6253\u4e86\u534a\u67f1\u9999\u7684\u65f6\u523b\uff0c\u5140\u662f\u4e0d\u5206\u80dc\u8d1f\u3002\u65c1\u8fb9\u7684 \u4e00\u68f5\u6843\u6811\uff0c\u6811\u53f6\u7eb7\u843d\uff0c\u5df2\u662f\u53ea\u5269\u4e0b\u5149\u79c3\u79c3\u7684\u6811\u679d\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u624b\u6301\u9152\u676f\uff0c\u634b\u987b\u8d5e\u53f9\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u5e08\u7236\u5728\u8fd9\u4e8c\u5341\u5e74\u4e2d\uff0c\u539f\u6765\u53c8\u5df2\u521b\u4e86\u4e0d\u5c11\u65b0 \u5947\u7684\u62db\u6570\u3002\u6b66\u5b66\u672c\u6765\u662f\u4e00\u4ee3\u80dc\u8fc7\u4e00\u4ee3\uff0c\u5728\u524d\u4eba\u7684\u4f20\u6388\u4e4b\u4e0a\uff0c\u6709\u6240\u521b\u9020\uff0c\u6709\u6240\u53d1\u660e\u7684\u3002\u800c\u6211\u5728 \u8fd9\u4e8c\u5341\u5e74\u6765\u81ea\u521b\u7684\u6b66\u529f\u6bd4\u8d77\u5e05\u7236\u6765\u5374\u662f\u592a\u5c11\u4e86\uff0c\u771f\u662f\u60ed\u6127\u5440\u60ed\u6127\uff01\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u89c1\u4e08\u592b\u505c\u676f\u4e0d\u8bed\uff0c\u5982\u6709\u6240\u601d\uff0c\u95ee\u4ed6\u9053\uff1a\u201c\u8fd9\u5c0f\u53eb\u5316\u7684\u672c\u9886\u597d\u5f97\u51fa\u5947\uff0c\u5c31\u53ea\u53ef\u60dc\u6b20 \u7f3a\u4e00\u70b9\u706b\u5019\u3002\u6d77\u54e5\uff0c\u6211\u542c\u4f60\u4e00\u76f4\u8d5e\u58f0\u4e0d\u7edd\uff0c\u600e\u7684\u5ffd\u5730\u4e00\u58f0\u4e0d\u54cd\u4e86\u3002\u4f60\u662f\u5728\u60f3\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u662f\u5728\u60f3\uff0c\u8fd9\u8bb8\u591a\u5e74\u6765\u6b66\u6797\u7684\u670b\u53cb\u7ed9\u6211\u9762\u4e0a\u8d34\u91d1\u3002\u628a\u6211\u6367\u6210\u4e86\u5929\u4e0b\u7b2c\u4e00\u9ad8 \u624b\uff0c\u6211\u662f\u4e0d\u662f\u7ed9\u8fd9\u2018\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\u2019\u7684\u79f0\u53f7\u5f04\u5f97\u81ea\u6211\u9676\u9189\uff0c\u4ee5\u81f4\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\u56fa\u6b65\u81ea\u5c01 \u4e86\u3002\u201d \u3000\u3000\u6597\u5230\u767e\u62db\u5f00\u5916\uff0c\u6587\u9053\u5e84\u5140\u662f\u672a\u80fd\u53d6\u80dc\uff0c\u5fc3\u4e2d\u4e0d\u514d\u7126\u8e81\uff0c\u4ed6\u5df2\u770b\u51fa\u91d1\u9010\u6d41\u7684\u529f\u529b\u7a0d\u4e0d\u5982 \u4ed6\uff0c\u4e8e\u662f\u5356\u4e2a\u7834\u7efd\uff0c\u8bf1\u91d1\u9010\u6d41\u8fdb\u62db\uff0c\u7acb\u610f\u548c\u4ed6\u4e00\u62fc\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5c06\u8ba1\u5c31\u8ba1\uff0c\u500f\u5730\u5c31\u662f\u4e00\u4e2a\u7a7f\u6b65\u8fdb\u638c\u3002\u6587\u9053\u5e84\u559d\u58f0\u201c\u7740\uff01\u201d\u4e00\u62db\u201c\u659c\u5207\u8026\u201d\u786c\u5288\u4e0b \u6765\uff0c\u4e0d\u6599\u91d1\u9010\u6d41\u6d41\u4e00\u4f3c\u6e38\u9c7c\uff0c\u62db\u6570\u672a\u8001\uff0c\u500f\u5730\u6536\u56de\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u5fd9\u4ec0\u4e48\uff1f\u4f60\u7740\u4e86\u6211\u7684\u9053\u513f \u4e86\uff01\u201d\u968f\u7740\u4ed6\u7684\u7b11\u58f0\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u7684\u4e00\u7247\u54cd\uff0c\u91d1\u9010\u6d41\u624b\u638c\u4e00\u5f20\uff0c\u788e\u94f6\u5b50\u548c\u94dc\u94b1\u6492\u4e86\u6ee1 \u5730\uff0c\u624b\u4e2d\u8fd8\u7559\u4e86\u4e00\u4e2a\u5c0f\u5c0f\u7684\u7f8a\u8102\u767d\u7389\u74f6\uff0c\u7b11\u9053\uff1a\u201c\u65e0\u540d\u5c9b\u7684\u7eed\u65ad\u818f\u662f\u96be\u5f97\u4e4b\u7269\uff0c\u8fd9\u6211\u53ef\u8981\u7559 \u4e0b\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u90a3\u4e00\u62db\u4e43\u662f\u865a\u62db\uff0c\u5728\u8fd1\u8eab\u4e4b\u65f6\u5374\u65bd\u5c55\u5999\u624b\u7a7a\u7a7a\u7684\u624b\u6bb5\uff0c\u628a\u6587\u9053\u5e84\u8863\u888b\u4e2d\u7684\u4e1c \u897f\u90fd\u53d6\u4e86\u8fc7\u6765\u3002\u4ed6\u867d\u7136\u4f24\u4e0d\u4e86\u6587\u9053\u5e84\uff0c\u4f46\u5077\u4e86\u4ed6\u7684\u4e1c\u897f\uff0c\u5df2\u662f\u4ee4\u5f97\u6587\u9053\u5e84\u4e0b\u4e86\u4e00\u4e2a\u5927\u53f0\u3002\u6587 \u9053\u5e84\u53c8\u6025\u53c8\u6c14\uff0c\u9762\u8272\u7ea2\u5f97\u53d1\u7d2b\uff0c\u53d8\u6210\u4e86\u50cf\u4e00\u5757\u732a\u809d\uff0c\u89c2\u6218\u7684\u5bbe\u5ba2\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u4e0e\u6c5f\u6d77\u5929\u5e76\u80a9\u89c2\u6218\uff0c\u770b\u5230\u6b64\u5904\uff0c\u5fc3\u4e2d\u9887\u4e3a\u8be7\u5f02\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u6084\u6084\u548c\u6c5f\u6d77\u5929\u8bf4 \u9053\uff1a\u201c\u6c5f\u5927\u4fa0\uff0c\u4f60\u770b\u8fd9\u5c0f\u53eb\u5316\u5077\u4e1c\u897f\u7684\u624b\u6cd5\uff0c\u83ab\u975e\u4ed6\u662f\u795e\u5077\u59ec\u6653\u98ce\u7684\u5f1f\u5b50\uff1f\u201d\u59ec\u6653\u98ce\u5728\u4e8c\u4e09 \u5341\u5e74\u4e4b\u524d\u662f\u540d\u95fb\u5929\u4e0b\u7684\u5999\u624b\u795e\u5077\uff0c\u4ed6\u6700\u559c\u6b22\u548c\u6b66\u6797\u4eba\u7269\u5f00\u73a9\u7b11\uff0c\u6216\u8005\u662f\u5077\u4ed6\u4eec\u7684\u62f3\u7ecf\u5251\u8c31\uff0c \u6216\u8005\u662f\u5077\u4ed6\u4eec\u7684\u72ec\u95e8\u6697\u5668\u6216\u79d8\u5236\u7075\u4e39\u3002\u4ef2\u957f\u7edf\u4e5f\u66fe\u7740\u8fc7\u4ed6\u7684\u9053\u513f\uff0c\u662f\u4ee5\u8bc6\u5f97\u4ed6\u5077\u4e1c\u897f\u7684\u624b \u6cd5\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u5999\u624b\u7a7a\u7a7a\u7684\u624b\u6cd5\u662f\u59ec\u6653\u98ce\u4f20\u6388\uff0c\u4f46\u4e5f\u51b3\u4e0d\u4f1a\u662f\u59ec\u6653\u98ce\u7684\u5f1f\u5b50\u3002\u59ec\u6653\u98ce\u8fd8 \u6ca1\u6709\u4ed6\u8fd9\u4e48\u597d\u7684\u6b66\u529f\u3002\u201d\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u542c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u83ab\u975e\u4f60\u5df2\u7ecf\u77e5\u9053\u4e86\u4ed6\u7684\u5e08\u95e8\u6765\u5386\uff1f\u201d\u6c5f \u6d77\u5929\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u77e5\u9053\u4e86\u3002\u4e0d\u8fc7\uff0c\u73b0\u5728\u8fd8\u4e0d\u597d\u8bf4\u3002\u5f85\u4f1a\u513f\u5f85\u6211\u95ee\u8fc7\u4e86\u4ed6\uff0c\u8981\u662f\u6211\u731c\u5f97\u5bf9\u7684 \u8bdd\uff0c\u6211\u518d\u8bf7\u4f60\u4eec\u559d\u676f\u559c\u9152\u3002\u201d\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u4eca\u5929\u4f60\u8bf7\u6211\u4eec\u559d\u7684\u672c\u6765\u5c31\u662f\u559c\u9152\u5440\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\uff0c\u8fd9\u4ef6\u559c\u4e8b\u975e\u540c\u5c0f\u53ef\uff0c\u6bd4\u6211\u7684\u5973\u513f\u51fa\u5ac1\u8fd8\u66f4\u4ee4\u4eba\u9ad8\u5174\u3002\u201d\u4ef2\u957f\u7edf\u9053\uff1a \u201c\u662f\u4e48\uff1f\u90a3\u6211\u5012\u5e0c\u671b\u8fd9\u573a\u6bd4\u6b66\u5feb\u5feb\u7ed3\u675f\u3002\u597d\u8ba9\u4f60\u6253\u7834\u8fd9\u4e2a\u95f7\u846b\u82a6\u4e86\u3002\u201d\u4ef2\u957f\u7edf\u89c1\u6c5f\u6d77\u5929\u82e5\u6709 \u6240\u601d\uff0c\u53e3\u4e2d\u8bf4\u662f\u201c\u559c\u4e8b\u201d\uff0c\u8138\u4e0a\u5374\u662f\u4e00\u7247\u6005\u60d8\u7684\u795e\u8272\uff0c\u5fc3\u4e2d\u66f4\u4e3a\u8be7\u5f02\uff0c\u4f46\u6c5f\u6d77\u5929\u65e2\u7136\u4e0d\u613f\u6b64 \u65f6\u63ed\u6653\uff0c\u4ef2\u957f\u7edf\u4e5f\u5c31\u4e0d\u4fbf\u518d\u95ee\u4e0b\u53bb\u3002 \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u6b63\u662f\u56e0\u4e3a\u770b\u4e86\u91d1\u9010\u6d41\u4f7f\u51fa\u7684\u7a7a\u7a7a\u5999\u624b\uff0c\u800c\u5f15\u8d77\u4e86\u4ed6\u5bf9\u59ec\u6653\u98ce\u7684\u6000\u5ff5\uff0c\u4ece\u800c\u53c8 \u66f4\u5f15\u8d77\u4e86\u4ed6\u5bf9\u5e08\u7236\u7684\u6000\u5ff5\u3002\u59ec\u6653\u98ce\u548c\u4ed6\u7684\u7236\u4eb2\u6c5f\u5357\u4e43\u662f\u516b\u62dc\u4e4b\u4ea4\uff0c\u548c\u4ed6\u7684\u5e08\u7236\u91d1\u4e16\u9057\u4e5f\u662f\u4e0d \u62d8\u5f62\u8ff9\u7684\u597d\u53cb\u3002 \u3000\u3000\u4e8c\u5341\u5e74\u524d\uff0c\u5728\u91d1\u4e16\u9057\u592b\u59bb\u9041\u8ff9\u6d77\u5916\u4e4b\u540e\uff0c\u4e0d\u4e45\u59ec\u6653\u98ce\u4e5f\u5931\u8e2a\u4e86\uff0c\u6709\u4eba\u8bf4\u4ed6\u662f\u5230\u5929\u7afa\u53bb \u4e86\uff0c\u4e5f\u6709\u4eba\u8bf4\u4ed6\u662f\u53bb\u8bbf\u5bfb\u91d1\u4e16\u9057\u53bb\u4e86\u3002\u7a76\u7adf\u5982\u4f55\uff0c\u65e0\u4eba\u77e5\u9053\uff0c\u603b\u4e4b\u4ed6\u5c31\u662f\u50cf\u91d1\u4e16\u9057\u4e00\u6837\uff0c\u4e00 \u53bb\u65e0\u8e2a\uff0c\u6b66\u6797\u4e2d\u5c11\u4e86\u4e2a\u7231\u95f9\u4e8b\u7684\u59ec\u6653\u51e4\uff0c\u4e5f\u5bc2\u5bde\u591a\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u770b\u6765\u8fd9\u5c0f\u53eb\u5316\u8d70\u662f\u6211\u7684\u5c0f\u5e08\u5f1f\u65e0\u7591\u4e86\u3002\u4ed6\u4f7f\u5f97\u51fa\u59ec\u4f2f\u4f2f\u7684\u795e\u5077\u624b \u6cd5\uff0c\u770b\u6765\u59ec\u4f2f\u4f2f\u4e5f\u5b9a\u662f\u548c\u6211\u7684\u5e08\u7236\u540c\u5728\u4e00\u8d77\u3002\u53ea\u4e0d\u77e5\u4ed6\u662f\u5426\u8fd8\u6d3b\u5728\u4eba\u95f4\u3002\u8981\u662f\u59ec\u4f2f\u4f2f\u8fd8\u6d3b\u7740 \u7684\u8bdd\uff0c\u90a3\u5df2\u662f\u4e03\u5341\u5f00\u5916\u5c06\u8fd1\u516b\u5341\u7684\u8001\u4eba\u4e86\u3002\u55ef\uff0c\u6211\u7684\u7239\u7239\u6bd4\u4ed6\u5e74\u7eaa\u5c0f\u4e86\u5dee\u4e0d\u591a\u5341\u5c81\uff0c\u5374\u4e0d\u5e78 \u5148\u53bb\u4e16\u4e86\u3002\u8981\u662f\u4ed6\u80fd\u591f\u6d3b\u5230\u4eca\u65e5\uff0c\u5f97\u77e5\u4ed6\u8001\u670b\u53cb\u7684\u6d88\u606f\uff0c\u8be5\u662f\u591a\u4e48\u9ad8\u5174\u5462\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u91cc\u53c8\u60f3\uff1a\u201c\u4eca\u5e74\u6b63\u662f\u5e08\u7236\u7684\u516d\u5341\u5927\u5bff\uff0c\u5e08\u7236\u6b66\u529f\u76d6\u4e16\uff0c\u60f3\u5fc5\u8eab\u4f53\u5065\u6717\u3002\u65f6\u95f4\u771f \u662f\u8fc7\u5f97\u5feb\uff0c\u5df2\u6709\u4e8c\u5341\u5e74\u6765\u6ca1\u6709\u542c\u5230\u4ed6\u8001\u4eba\u5bb6\u7684\u6559\u8bad\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u4ee5\u4e00\u4e2a\u4e66\u50ee\u4e4b\u5b50\u7684\u8eab\u4efd\uff0c\u5f97 \u4ee5\u6210\u4e3a\u5f53\u4ee3\u7684\u5927\u4fa0\uff0c\u8fb9\u90fd\u662f\u9760\u4e86\u5e08\u7236\u5168\u4e16\u9057\u4e00\u624b\u6559\u517b\u4e4b\u529f\uff0c\u5e08\u95e8\u6069\u4e49\uff0c\u771f\u53ef\u4ee5\u8bf4\u5f97\u662f\u6069\u91cd\u5982 \u5c71\u3002\u6240\u4ee5\u6c5f\u6d77\u5929\u5bf9\u4e8e\u5e08\u7236\uff0c\u90a3\u662f\u65e0\u65e5\u4e0d\u5728\u6000\u5ff5\u7684\uff0c\u5982\u4eca\u89c1\u4e86\u91d1\u9010\u6d41\uff0c\u5f53\u7136\u662f\u66f4\u60f3\u8d77\u5e08\u7236\u6765 \u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u81ea\u9050\u601d\uff0c\u5ffd\u542c\u5f97\u4ef2\u957f\u7edf\u53eb\u9053\uff1a\u201c\u5999\u5440\uff01\u6c5f\u5927\u4fa0\u4f60\u770b\u5230\u4e86\u4ed6\u8fd9\u62db\u5251\u6cd5\u6ca1\u6709\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u628a\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u6587\u9053\u5e84\u6b63\u88ab\u91d1\u9010\u6d41\u8feb\u5f97\u8fde\u9000\u4e09\u6b65\uff0c\u4f46\u91d1\u9010\u6d41\u4ecd\u662f\u53cc\u624b\u7a7a\u7a7a\uff0c\u624b\u4e2d \u5e76\u6ca1\u62ff\u5251\u3002\u53f6\u6155\u534e\u4f8d\u7acb\u5e08\u7236\u8eab\u65c1\uff0c\u542c\u4e86\u4ef2\u957f\u7edf\u7684\u8bdd\uff0c\u8be7\u9053\uff1a\u201c\u4ef2\u5e2e\u4e3b\u4f60\u8bf4\u4ed6\u4f7f\u7684\u662f\u5251\u6cd5 \u4e48\uff1f\u201d\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u6211\u6b63\u60f3\u8bf7\u6559\u4f60\u7684\u5e08\u7236\uff0c\u8fd9\u5c0f\u53eb\u5316\u4f7f\u7684\u4f3c\u4e4e\u662f\u5929\u5c71\u6d3e\u7684\u5251\u6cd5\u5427\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u73b0\u5728\u4f7f\u7684\u4e00\u62db\u6b63\u662f\u5929\u5c71\u6d3e\u7684\u987b\u5f25\u5251\u5f0f\uff0c\u521a\u624d\u7684\u90a3\u4e00 \u62db\uff0c\u6211\u5374\u6ca1\u6709\u7559\u610f\u3002\u201d\u539f\u6765\u91d1\u9010\u6d41\u662f\u4ee5\u6307\u4ee3\u5251\uff0c\u4f7f\u51fa\u672c\u95e8\u7684\u5251\u5f0f\u7684\u3002\u91d1\u4e16\u9057\u6240\u4f20\u7684\u5251\u6cd5\uff0c\u662f \u4ee5\u5929\u5c71\u5251\u6cd5\u4f5c\u57fa\u7840\u800c\u52a0\u4ee5\u53d8\u5316\u7684\uff0c\u4e0d\u8fc7\u4ee5\u6307\u4ee3\u5251\uff0c\u628a\u5251\u6cd5\u5316\u5230\u6307\u6cd5\u4e0a\u6765\u5219\u5b8c\u5168\u662f\u5168\u4e16\u9057\u7684\u521b \u9020\u3002\u6c5f\u6d77\u5929\u7684\u95e8\u4e0b\u5f1f\u5b50\u90fd\u8fd8\u672a\u66fe\u5b66\u5230\u8fd9\u6837\u9ad8\u6df1\u7684\u529f\u592b\u3002 \u3000\u3000\u4e00\u9633\u5b50\u4e5f\u770b\u51fa\u4e86\u91d1\u9010\u6d41\u4f7f\u7684\u662f\u5929\u5c71\u5251\u6cd5\uff0c\u5929\u5c71\u6d3e\u7684\u5c11\u638c\u95e8\u2014\u2014\u5510\u52a0\u6e90\u6b63\u5728\u4ed6\u7684\u8eab\u540e\uff0c\u4e00 \u9633\u5b50\u56de\u5934\u95ee\u9053\uff1a\u201c\u5510\u5c11\u4fa0\uff0c\u6b64\u4eba\u53ef\u662f\u4f60\u4eec\u8d35\u6d3e\u5f1f\u5b50\uff1f\u201d\u5510\u52a0\u6e90\u4e00\u7247\u8ff7\u832b\u7684\u795e\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d \u662f\u3002\u4f46\u5374\u4e0d\u77e5\u4ed6\u600e\u5730\u5b66\u6765\u4e86\u8fd9\u51e0\u62db\u987b\u5f25\u5251\u5f0f\uff0c\u548c\u655d\u6d3e\u6240\u4f20\u7684\u5251\u5f0f\u795e\u5f62\u4ff1\u4f3c\uff0c\u4f46\u5374\u53c8\u4e0d\u5c3d\u76f8 \u540c\u3002\u201d \u3000\u3000\u4e00\u9633\u5b50\u5ffd\u5730\u4f3c\u4e4e\u53d1\u89c9\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u95ee\u9053\uff0c\u201c\u90a3\u6b66\u5f53\u6d3e\u7684\u79e6\u5c11\u4fa0\u5462\uff1f\u201d\u539f\u6765\u6587 \u80dc\u4e2d\u7684\u5ea7\u4f4d\u662f\u88ab\u5b89\u6392\u5728\u5510\u52a0\u6e90\u8eab\u8fb9\u7684\uff0c\u540e\u6765\u6587\u9053\u5e84\u4e0e\u91d1\u9010\u6d41\u4ea4\u624b\uff0c\u5510\u52a0\u6e90\u548c\u4ed6\u4e5f\u662f\u540c\u7ad9\u5728\u4e00 \u8d77\u89c2\u6218\uff0c\u4e00\u9633\u5b50\u5219\u7ad9\u5728\u4ed6\u4eec\u7684\u524d\u9762\u3002\u4e00\u9633\u5b50\u5168\u795e\u89c2\u6218\uff0c\u6b64\u65f6\u56de\u5934\u4e00\u671b\uff0c\u65b9\u59cb\u53d1\u89c9\u201c\u79e6\u5143\u6d69\u201d \u5df2\u7ecf\u4e0d\u89c1\u3002 \u3000\u3000\u5510\u52a0\u6e90\u4e5f\u662f\u6b64\u65f6\u65b9\u59cb\u53d1\u89c9\uff0c\u8bf4\u9053\uff1a\u201c\u5947\u602a\uff0c\u6211\u521a\u624d\u8fd8\u5728\u548c\u4ed6\u8bf4\u8bdd\u7684\uff0c\u4ed6\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u5bf9\u9762\u7684\u957f\u5eca\u4f20\u51fa\u4e86\u55a7\u95f9\u4e4b\u58f0\u3002 \u3000\u3000\u539f\u6765\u6587\u80dc\u4e2d\u8d8a\u770b\u8d8a\u5bb3\u6015\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\u53ea\u6015\u51f6\u591a\u5409\u5c11\uff0c\u7239\u7239\u6697\u7b97\u4e0d\u6210\uff0c\u8fdf\u65e9\u4f1a \u7ed9\u4e00\u4eba\u8bc6\u7834\u3002\u5c01\u4f2f\u4f2f\u5df2\u7136\u8d70\u4e86\uff0c\u6211\u8fd8\u7559\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u4ed6\u53ea\u6c42\u81ea\u8eab\u8131\u9669\uff0c\u8fde\u7236\u4eb2\u4e5f\u4e0d\u987e \u4e86\u3002\u4e8e\u662f\u8d81\u7740\u4f17\u4eba\u90fd\u5728\u51dd\u795e\u89c2\u6218\u7684\u5f53\u513f\uff0c\u6084\u6084\u6e9c\u8d70\u3002 \u3000\u3000\u8981\u8d70\u51fa\u56ed\u5b50\u5fc5\u987b\u7ecf\u8fc7\u957f\u5eca\uff0c\u957f\u5eca\u4e0a\u79e6\u5143\u6d69\u4e5f\u5728\u51dd\u795e\u89c2\u6218\uff0c\u4e0d\u8fc7\u5f53\u6587\u80dc\u4e2d\u8d70\u8fc7\u957f\u5eca\u7684\u65f6 \u5019\uff0c\u5374\u5076\u7136\u7ed9\u4ed6\u53d1\u73b0\u4e86\u3002 \u3000\u3000\u79e6\u5143\u6d69\u4e00\u8dc3\u800c\u8d77\uff0c\u62e6\u4f4f\u4e86\u6587\u80dc\u4e2d\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6587\u5144\uff0c\u4f60\u6211\u8be5\u6362\u56de\u8863\u88f3\u4e86\u5427\uff1f\u201d\u6587\u80dc\u4e2d \u5927\u5403\u4e00\u60ca\uff0c\u4f46\u4ed6\u4e5f\u6781\u4e3a\u673a\u8b66\uff0c\u5f53\u4e0b\u7acb\u5373\u8fd0\u638c\u4e00\u63a8\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u4f60\u80e1\u95f9\u4ec0\u4e48\uff1f\u4f60\u7792\u4e86\u6211\u5077 \u8dd1\u4e0b\u5c71\uff0c\u6211\u8fd8\u672a\u8d23\u7f5a\u4f60\u5462\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u638c\u6587\u80dc\u4e2d\u8fd0\u7684\u662f\u201c\u4e09\u8c61\u795e\u529f\u201d\uff0c\u4ed6\u7684\u201c\u4e09\u8c61\u795e\u529f\u201d\u53ea\u662f\u5165\u95e8\u7684\u529f\u592b\uff0c\u548c\u4ed6\u7684\u7236\u4eb2\u5f53 \u7136\u662f\u5dee\u5f97\u592a\u8fdc\u3002\u4e0d\u8fc7\uff0c\u867d\u7136\u5982\u6b64\uff0c\u638c\u529b\u4e5f\u8fd8\u662f\u8db3\u53ef\u88c2\u77f3\u5f00\u7891\u3002\u79e6\u5143\u6d69\u559d\u9053\uff1a\u201c\u4f60\u80e1\u8bf4\u4ec0 \u4e48\uff1f\u201d\u7528\u638c\u4e00\u6258\u6587\u80dc\u4e2d\u7684\u8098\u5c16\uff0c\u4fe1\u624b\u4e00\u62db\u201c\u8986\u96e8\u7ffb\u4e91\u201d\uff0c\u5316\u89e3\u4e86\u4ed6\u7684\u8fd9\u4e00\u62db\u5077\u88ad\uff0c\u53cc\u6307\u4fbf\u70b9 \u5411\u4ed6\u8098\u5c16\u7684\u201c\u66f2\u6c60\u7a74\u201d\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u8fde\u5fd9\u4f7f\u4e2a\u201c\u8131\u888d\u89e3\u7532\u201d\uff0c\u6c89\u80a9\u584c\u80cc\uff0c\u907f\u62db\u8fd8\u62db\uff0c\u8fd0\u529b\u53c8\u662f\u4e00\u63a8\u3002\u53ef\u662f\u79e6\u5143\u6d69\u7684\u672c \u9886\u4e43\u662f\u5728\u4ed6\u4e4b\u4e0a\uff0c\u4ed6\u7684\u521d\u5165\u95e8\u7684\u201c\u4e09\u8c61\u795e\u529f\u201d\u4f24\u4e0d\u4e86\u79e6\u5143\u6d69\uff0c\u7ed9\u79e6\u5143\u6d69\u6a2a\u638c\u4e00\u5c01\uff0c\u4fbf\u628a\u4ed6\u7684 \u638c\u529b\u5c3d\u90fd\u5316\u89e3\uff0c\u6587\u80dc\u4e2d\u4e5f\u4f9d\u7136\u662f\u51b2\u4e0d\u8fc7\u53bb\u3002\u4e0d\u8fc7\u79e6\u5143\u6d69\u4e5f\u80dc\u4e0d\u4e86\u4ed6\u8bb8\u591a\uff0c\u662f\u4ee5\u5728\u6570\u62db\u4e4b\u5185\uff0c \u79e6\u5143\u6d69\u4e5f\u8fd8\u672a\u80fd\u5c06\u4ed6\u5236\u4f0f\u3002 \u3000\u3000\u4ed6\u4eec\u8fd9\u4e00\u4ea4\u624b\uff0c\u60ca\u52a8\u4e86\u65c1\u8fb9\u7684\u5ba2\u4eba\u3002\u5f53\u53f6\u6155\u534e\u5e26\u79e6\u5143\u6d69\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u6709\u51e0\u4f4d\u5ba2\u4eba\u66fe\u542c\u5f97 \u4ed6\u4eec\u8c08\u8bdd\uff0c\u5f53\u65f6\u79e6\u5143\u6d69\u4e0d\u613f\u4fbf\u5373\u63ed\u5f00\u771f\u76f8\uff0c\u5047\u8ba4\u6587\u80dc\u4e2d\u662f\u4ed6\u54e5\u54e5\uff0c\u8fd9\u51e0\u4f4d\u5ba2\u4eba\u53ea\u9053\u662f\u771f\u3002\u6587 \u80dc\u4e2d\u7684\u6539\u5bb9\u6613\u8c8c\u4e4b\u672f\u6781\u5999\uff0c\u4ed6\u4eec\u4e24\u4eba\u7ad9\u5728\u4e00\u8d77\uff0c\u5341\u5206\u76f8\u4f3c\uff0c\u65c1\u4eba\u4e5f\u90fd\u628a\u4ed6\u4eec\u5f53\u4f5c\u4e86\u4e24\u5144\u5f1f\u3002 \u3000\u3000\u6709\u4e2a\u5ba2\u4eba\u4fbf\u7b11\u9053\u3002\u201d\u4e24\u5144\u5f1f\u95f9\u4e9b\u4ec0\u4e48\uff1f\u8fd9\u662f\u6c5f\u5927\u4fa0\u7684\u559c\u4e8b\uff0c\u4f60\u5f1f\u5f1f\u6765\u8d81\u70ed\u95f9\uff0c\u4f60\u505a\u54e5\u54e5 \u7684\u53c8\u4f55\u5fc5\u8d23\u5907\uff1f\u201d\u4ed6\u54ea\u91cc\u77e5\u9053\u662f\u79e6\u5143\u6d69\u8981\u626d\u7740\u8fd9\u4e2a\u5047\u5192\u4ed6\u7684\u4eba\uff0c\u8fd8\u53ea\u5f53\u662f\u505a\u201c\u54e5\u54e5\u201d\u7684\u6587\u80dc \u4e2d\u8981\u8d23\u6253\u5f1f\u5f1f\u3002 \u3000\u3000\u79e6\u5143\u6d69\u6b63\u8981\u8bf4\u51fa\u771f\u76f8\uff0c\u5ffd\u542c\u5f97\u573a\u4e2d\u54d7\u7136\u5927\u547c\uff0c\u539f\u6765\u662f\u91d1\u9010\u6d41\u6495\u4e0b\u4e86\u6587\u9053\u5e84\u7684\u957f\u886b\uff0c\u4f46\u5374 \u7ed9\u6587\u9053\u5e84\u6253\u4e86\u4e00\u638c\u3002\u4ee5\u4e00\u4e2a\u6b66\u5b66\u9ad8\u624b\u7684\u8eab\u624b\uff0c\u7ed9\u4eba\u5265\u4e0b\u4e86\u8863\u88f3\uff0c\u5f53\u7136\u662f\u5927\u5927\u4e22\u8138\u4e4b\u4e8b\uff0c\u4f46\u91d1 \u9010\u6d41\u7ed9\u6253\u4e86\u4e00\u638c\uff0c\u5374\u662f\u5403\u7684\u5b9e\u4e8f\u3002\u6c5f\u5bb6\u7684\u5bbe\u5ba2\u8d77\u521d\u4e0d\u6ee1\u610f\u8fd9\u5c0f\u53eb\u5316\u7684\u72c2\u5984\u65e0\u793c\uff0c\u4f46\u89c1\u4ed6\u5e74\u7eaa \u8f7b\u8f7b\uff0c\u6b66\u529f\u597d\u5f97\u51fa\u5947\uff0c\u6e10\u6e10\u7684\u4e0d\u89c9\u4f69\u773c\u8d77\u4ed6\u6765\uff0c\u6b64\u65f6\u89c1\u4ed6\u5403\u4e86\u4e8f\uff0c\u6709\u8bb8\u591a\u5ba2\u4eba\u5c31\u7981\u4e0d\u4f4f\u60ca\u547c \u4e86\u3002 \u3000\u3000\u79e6\u5143\u6d69\u4e5f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u6587\u80dc\u4e2d\u4f55\u7b49\u6e9c\u6ed1\uff0c\u8d81\u7740\u79e6\u5143\u6d69\u84e6\u5730\u4e00\u5446\u4e4b\u9645\uff0c\u5df2\u662f\u628a\u4ed6\u63a8\u5f00\uff0c \u8dd1\u8fc7\u957f\u5eca\u3002\u957f\u5eca\u4e0a\u7684\u4eba\u6b64\u65f6\u6b63\u5728\u5168\u795e\u6ce8\u610f\u573a\u4e2d\u6bd4\u6b66\uff0c\u5c31\u662f\u521a\u624d\u8981\u60f3\u529d\u67b6\u7684\u90a3\u51e0\u4f4d\u5ba2\u4eba\uff0c\u6b64\u65f6 \u5df2\u4e5f\u90fd\u628a\u6ce8\u610f\u529b\u91cd\u65b0\u96c6\u4e2d\uff0c\u770b\u573a\u4e2d\u7cbe\u5f69\u7d27\u5f20\u7684\u6bd4\u6b66\uff0c\u61d2\u5f97\u7406\u4ec0\u4e48\u201c\u4e24\u5144\u5f1f\u201d\u7684\u201c\u5435\u95f9\u201d\u4e86\u3002 \u3000\u3000\u594f\u5143\u6d69\u6b64\u65f6\u8fd8\u6765\u5f97\u53ca\u63ed\u53d1\u4ed6\u7684\u771f\u76f8\u7684\uff0c\u53ea\u8981\u4ed6\u8bf4\u4e00\u58f0\uff0c\u6c5f\u5bb6\u7684\u4eb2\u53cb\u5373\u4f7f\u4e0d\u662f\u7acb\u5373\u76f8\u4fe1\u4ed6 \u7684\u8bdd\uff0c\u4e5f\u5b9a\u7136\u4e0d\u80af\u8ba9\u6587\u80dc\u4e2d\u8dd1\u6389\u3002\u4f46\u79e6\u5143\u6d69\u8fd8\u672a\u77e5\u9053\u6587\u9053\u5e84\u7684\u8fc7\u53bb\u6765\u5386\uff0c\u4e5f\u8fd8\u672a\u5b8c\u5168\u77e5\u9053\u6587 \u5bb6\u7236\u5b50\u7684\u9634\u8c0b\u3002\u4ed6\u53ea\u9053\u6587\u9053\u5e84\u548c\u6c5f\u6d77\u5929\u53ea\u662f\u5bfb\u5e38\u7684\u201c\u8fc7\u8282\u201d\uff0c\u4eca\u65e5\u4e4b\u6765\uff0c\u662f\u8981\u627e\u6c5f\u6d77\u65e0\u6bd4 \u6b66\uff0c\u201c\u51fa\u4e00\u53e3\u6c14\u201d\u7684\uff0c\u800c\u91d1\u9010\u6d41\u4ee3\u6c5f\u6d77\u5929\u51fa\u5934\uff0c\u5219\u5927\u51fa\u4ed6\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u79e6\u5143\u6d69\u6bd5\u7adf\u662f\u4e00\u4e2a\u5fe0\u539a\u7684\u4eba\uff0c\u5c31\u5728\u4ed6\u60f3\u8981\u51fa\u58f0\u7684\u65f6\u5019\uff0c\u5fc3\u91cc\u5374\u5ffd\u5730\u60f3\u9053\uff1a\u201c\u8fd9\u59d3\u6587\u7684\u5e74 \u7eaa\u8f7b\u8f7b\uff0c\u867d\u7136\u5fc3\u672f\u4e0d\u6b63\uff0c\u5c1a\u975e\u5978\u6076\u4e4b\u8f88\u3002\u6211\u82e5\u5f00\u53e3\u63ed\u7a7f\u4ed6\u7684\u771f\u76f8\uff0c\u53ef\u5c31\u8981\u6bc1\u4e86\u4ed6\u7684\u4e00\u751f\u4e86\u3002 \u6211\u65e2\u7136\u53ef\u4ee5\u653e\u5c01\u5b50\u8d85\u8d70\u6389\uff0c\u53c8\u4f55\u5fc5\u5b9a\u8981\u4e0e\u4ed6\u4e3a\u96be\uff1f\u7f62\u4e86\uff0c\u4e0d\u5982\u8ba9\u4ed6\u8d70\u4e86\u4e4b\u540e\uff0c\u6211\u518d\u5411\u6c5f\u5927\u4fa0 \u8bf4\u660e\u771f\u76f8\u5427\u3002\u201d\u4e8e\u662f\u8bdd\u5230\u53e3\u8fb9\uff0c\u53c8\u541e\u4e86\u56de\u53bb\u3002 \u3000\u3000\u79e6\u5143\u6d69\u5173\u5fc3\u91d1\u9010\u6d41\u7684\u5b89\u5371\uff0c\u4ed6\u542c\u5f97\u4f17\u4eba\u60ca\u547c\u4e4b\u58f0\uff0c\u4e0d\u77e5\u91d1\u9010\u6d41\u6253\u5f97\u5982\u4f55\uff0c\u4e8e\u662f\u5c31\u56de\u8fc7\u8eab \u6765\uff0c\u518d\u5411\u6597\u573a\u89c2\u671b\u3002\u53ea\u89c1\u91d1\u9010\u6d41\u6325\u821e\u90a3\u4ef6\u7834\u70c2\u7684\u957f\u886b\uff0c\u5411\u6587\u9053\u5e84\u4e00\u7f69\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u5c0f\u53eb\u5316 \u5077\u4e1c\u897f\uff0c\u6709\u65f6\u4e5f\u96be\u514d\u5931\u624b\u7684\u3002\u6328\u4e00\u638c\u6362\u6765\u4e86\u4e00\u4ef6\u7834\u957f\u886b\uff0c\u5012\u4e5f\u8fd8\u7b97\u662f\u503c\u5f97\u3002\u201d\u539f\u6765\u91d1\u9010\u6d41\u521a \u624d\u662f\u6709\u610f\u6fc0\u6012\u6587\u9053\u5e84\uff0c\u4e8e\u662f\u5192\u9669\u6b3a\u8eab\uff0c\u7528\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\u6495\u4e0b\u4ed6\u7684\u957f\u886b\u7684\u3002 \u3000\u3000\u867d\u8bf4\u201c\u5192\u9669\u201d\uff0c\u4f46\u91d1\u9010\u6d41\u662f\u81ea\u6709\u51e0\u5206\u628a\u63e1\u7684\u3002\u4ed6\u6253\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u65e9\u5df2\u662f\u77e5\u5df1\u77e5\u5f7c\u4e86\u3002\u4ed6 \u4f30\u8ba1\u5bf9\u65b9\u7684\u672c\u9886\uff0c\u81ea\u5df1\u7528\u5929\u7f57\u638c\u6cd5\u53bb\u6495\u4ed6\u7684\u957f\u886b\uff0c\u53ea\u8981\u52a8\u4f5c\u5f97\u5feb\uff0c\u5373\u4f7f\u5403\u4ed6\u4e00\u638c\uff0c\u7acb\u5373\u4fbf \u9000\uff0c\u638c\u529b\u672a\u900f\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u53d7\u4f24\uff0c\u7ed3\u679c\u50cf\u4ed6\u4f30\u8ba1\u4e00\u6837\u3002\u79e6\u5143\u6d69\u89c1\u91d1\u9010\u6d41\u5e76\u672a\u53d7\u4f24\uff0c\u653e\u4e0b\u4e86\u5fc3\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e4b\u6240\u4ee5\u8981\u6fc0\u6012\u6587\u9053\u5e84\uff0c\u5012\u4e5f\u5e76\u975e\u53ea\u4e3a\u7231\u597d\u620f\u800d\uff0c\u800c\u662f\u56e0\u4e3a\u9ad8\u624b\u6bd4\u6597\uff0c\u5018\u7b26\u4e00\u65b9\u5fc3 \u6d6e\u6c14\u8e81\uff0c\u5bf9\u65b9\u4fbf\u6709\u53ef\u4e58\u4e4b\u673a\u3002\u91d1\u9010\u6d41\u7684\u6b66\u5b66\u6bd4\u6587\u9053\u5e84\u9ad8\u660e\uff0c\u529f\u529b\u5219\u7a0d\u6709\u4e0d\u8db3\uff0c\u56e0\u6b64\u5c3d\u7ba1\u4ed6\u5947 \u62db\u5999\u7740\u5c42\u51fa\u4e0d\u7a77\uff0c\u6597\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5140\u662f\u4e0d\u80fd\u53d6\u80dc\u3002\u91d1\u9010\u6d41\u60f3\u8981\u53d6\u80dc\uff0c\u8fd9\u624d\u6709\u610f\u5c06\u4ed6\u6fc0\u6012\u3002 \u3000\u3000\u6587\u9053\u5e84\u679c\u7136\u4e2d\u8ba1\uff0c\u4ed6\u81ea\u547d\u662f\u4ec5\u6b21\u4e8e\u6c5f\u6d77\u5929\u7684\u6b66\u5b66\u9ad8\u624b\uff0c\u5982\u4eca\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u7ed9\u4e00\u4e2a\u5c0f \u53eb\u5316\u5265\u4e0b\u8863\u88f3\uff0c\u5f53\u771f\u662f\u53c8\u7f9e\u53c8\u6012\u3002 \u3000\u3000\u7f9e\u6012\u66f4\u52a0\u4e4b\u4e0b\uff0c\u6587\u9053\u5e84\u66b4\u8df3\u5982\u96f7\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u76d8\u65cb\u98de\u821e\uff0c\u628a\u91d1\u9010\u6d41\u5411\u4ed6\u6402\u5934\u7f69\u4e0b\u7684 \u90a3\u4ef6\u8863\u886b\u6253\u5f97\u5316\u6210\u7247\u7247\u8774\u8776\uff0c\u8863\u88f3\u662f\u67d4\u8f6f\u4e4b\u7269\uff0c\u672c\u6765\u4e0d\u6613\u53d7\u529b\u7684\uff0c\u800c\u6587\u9053\u5e84\u7684\u638c\u529b\u5c45\u7136\u80fd\u628a \u957f\u886b\u788e\u6210\u7247\u7247\uff0c\u638c\u529b\u4e4b\u795e\u5999\uff0c\u4e5f\u5f53\u771f\u662f\u8db3\u4ee5\u60ca\u4e16\u9a87\u4fd7\u4e86\u3002 \u3000\u3000\u4f46\u91d1\u9010\u6d41\u6b63\u662f\u8981\u4ed6\u5982\u6b64\uff0c\u6587\u9053\u5e84\u4e00\u5f00\u59cb\u66b4\u8e81\u6025\u653b\uff0c\u7acb\u5373\u4fbf\u7ed9\u4e86\u4ed6\u53ef\u4ee5\u4e58\u4e4b\u673a\u3002\u91d1\u9010\u6d41\u4f7f \u51fa\u4e86\u7cbe\u5999\u65e0\u65b9\u7684\u5929\u7f57\u6b65\u6cd5\uff0c\u8eab\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u5ffd\u638c\u5ffd\u6307\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u5bfb\u6444\u62b5\u9699\uff0c\u7740 \u7740\u627e\u5bfb\u6587\u9053\u5e84\u7684\u201c\u7a7a\u95e8\u201d\u653b\u51fb\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u6587\u9053\u5e84\u5df2\u662f\u8fed\u9047\u9669\u62db\u3002 \u3000\u3000\u6587\u9053\u5e84\u5fc3\u5934\u4e00\u51db\uff0c\u8fde\u5fd9\u51dd\u795e\u9759\u6c14\uff0c\u6c89\u7740\u5e94\u4ed8\uff0c\u53ef\u662f\uff0c\u5df2\u7ecf\u8fdf\u4e86\u3002\u91d1\u9010\u6d41\u4e00\u65e6\u62a2\u4e86\u5148\u624b\uff0c \u7d27\u7d27\u8fdb\u903c\uff0c\u54ea\u91cc\u8fd8\u80fd\u5bb9\u4ed6\u6273\u56de\u5e73\u624b\uff1f\u6587\u9053\u5e84\u5c3d\u7ba1\u662f\u4f7f\u51fa\u6d51\u8eab\u89e3\u6570\uff0c\u4e5f\u662f\u53ea\u6709\u62db\u67b6\u7684\u4efd\u513f\u4e86\u3002 \u3000\u3000\u573a\u4e2d\u7684\u55a7\u95f9\u5e73\u9759\u4e0b\u6765\uff0c\u4e00\u9633\u5b50\u627e\u7740\u4e86\u53f6\u5e55\u534e\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u521a\u624d\u770b\u89c1\u79e6\u5143\u6d69\u5df2\u7ecf\u8d70\u4e86\uff0c\u4ed6 \u7684\u5f1f\u5f1f\u5728\u90a3\u8fb9\u957f\u5eca\uff0c\u521a\u624d\u4e24\u5144\u5f1f\u4f3c\u4e4e\u53d1\u751f\u4e86\u4e89\u5435\u3002\u4f60\u628a\u4ed6\u7684\u5f1f\u5f1f\u8bf7\u8fc7\u6765\uff0c\u6211\u60f3\u95ee\u4e00\u95ee\u4ed6\u3002\u201d \u957f\u5eca\u6240\u95f9\u4e4b\u4e8b\uff0c\u53f6\u6155\u534e\u4e5f\u66fe\u6ce8\u610f\u5230\u4e86\uff0c\u4e0d\u8fc7\u521a\u624d\u56e0\u4e3a\u573a\u4e2d\u6597\u5f97\u6b63\u5728\u5403\u7d27\uff0c\u4ed6\u65e0\u6687\u62bd\u8eab\u53bb\u770b\uff0c \u6b64\u65f6\u67b6\u52bf\u5df2\u7ecf\u7a0d\u7f13\uff0c\u4ed6\u542c\u4e86\u4e00\u9633\u5b50\u7684\u8bdd\uff0c\u4fbf\u53bb\u628a\u79e6\u5143\u6d69\u627e\u6765\u4e86\u3002 \u3000\u3000\u6587\u9053\u5e84\u5168\u795e\u5e94\u4ed8\u91d1\u9010\u6d41\u7684\u602a\u62db\uff0c\u5bf9\u5468\u56f4\u7684\u4e00\u5207\uff0c\u89c6\u800c\u4e0d\u89c1\uff0c\u542c\u800c\u4e0d\u95fb\uff0c\u4ed6\u6b63\u5728\u957f\u5eca\u4e0a\u548c \u79e6\u5143\u6d69\u6253\u67b6\uff0c\u4ed6\u4e5f\u8fd8\u672a\u77e5\u9053\uff0c\u4f46\u73b0\u5728\uff0c\u53f6\u6155\u534e\u5e26\u9886\u79e6\u5143\u6d69\u8fc7\u6765\uff0c\u5c31\u5728\u4ed6\u7684\u773c\u524d\u7ecf\u8fc7\uff0c\u4ed6\u867d\u7136 \u5fc3\u65e0\u65c1\u9a9b\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u770b\u5230\u4e86\u79e6\u5143\u6d69\u4e86\u3002 \u3000\u3000\u6587\u9053\u5e84\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5fc3\u91cc\u6697\u53eb\u201c\u7cdf\u4e86\uff0c\u7cdf\u4e86\uff0c\u8fd9\u5c0f\u5b50\u4e00\u6765\uff0c\u4ec0\u4e48\u4e8b\u90fd\u88ab\u4ed6\u62c6\u7a7f\u4e86\uff01\u201d \u9ad8\u624b\u6bd4\u6597\uff0c\u54ea\u5bb9\u5f97\u7a0d\u6709\u5206\u5fc3\uff1f\u6587\u9053\u5e84\u672c\u6765\u5c31\u88ab\u91d1\u9010\u6d41\u62a2\u4e86\u653b\u52bf\uff0c\u53ea\u6709\u62db\u67b6\u7684\u4efd\u4e86\uff0c\u5982\u4eca\u7531\u4e8e \u79e6\u5143\u6d69\u7684\u51fa\u73b0\uff0c\u4ed6\u9aa4\u5403\u4e00\u60ca\uff0c\u5fc3\u5934\u5927\u9707\uff0c\u7ae0\u6cd5\u5927\u4e71\uff0c\u8fde\u62db\u67b6\u4e5f\u62db\u67b6\u4e0d\u6765\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u7ed9\u91d1\u9010\u6d41\u91cd\u91cd\u7684\u51fb\u4e86\u4e00\u638c\uff0c\u5654\u3001\u5654\u3001\u5654\u7684\u63a5\u8fde\u9000\u51fa\u4e86\u516d\u4e03\u6b65\uff0c\u5140 \u662f\u672a\u80fd\u7a33\u4f4f\u8eab\u5f62\uff0c\u91d1\u9010\u6d41\u8fd9\u6b21\u7528\u7684\u662f\u91d1\u521a\u638c\u529b\uff0c\u9976\u662f\u4ed6\u6709\u62a4\u4f53\u795e\u529f\uff0c\u4e5f\u75db\u5f97\u53cc\u773c\u53d1\u660f\uff0c\u91d1\u661f \u4e71\u5192\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u6b63\u5728\u5411\u6c5f\u6d77\u5929\u53d1\u95ee\uff1a\u201c\u5c0f\u53eb\u5316\u7684\u6765\u5386\u4f60\u770b\u51fa\u6765\u4e86\uff0c\u8fd9\u59d3\u4e91\u7684\u6765\u5386\u4f60\u53ef\u770b\u51fa\u4e86\u6ca1 \u6709\uff1f\u201d\u5c31\u5728\u6b64\u65f6\uff0c\u6587\u9053\u5e84\u5df2\u7ed9\u91d1\u9010\u6d41\u4e00\u638c\u51fb\u9000\uff0c\u4ef2\u957f\u7edf\u5927\u559c\u53eb\u9053\u3002\u201d\u5c0f\u53eb\u5316\u8d62\u4e86\uff0c\u8d62\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u563b\u563b\u7b11\u9053\uff1a\u201c\u4e00\u638c\u8fd8\u4e00\u638c\uff0c\u6211\u4e5f\u8fd8\u672a\u80fd\u7b97\u8d62\u3002\u518d\u6765\uff0c\u518d\u6765\u3002\u201d\u6251\u4e0a\u53bb\uff0c\u6b63\u8981\u518d\u52a0 \u4e00\u638c\uff0c\u628a\u6587\u9053\u5e84\u51fb\u5012\uff0c\u5ffd\u542c\u5f97\u6c5f\u6d77\u5929\u53eb\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u8ba9\u4ed6\u53bb\u5427\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u6ee1\u5802\u5bbe\u5ba2\uff0c\u65e0 \u4e0d\u60ca\u5947\u3002\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4ed6\u662f\u91d1\u5927\u4fa0\u7684\u513f\u5b50\uff0c\u8001\u53eb\u5316\u771f\u662f\u80e1\u6d82\uff0c\u4ed6\u540d\u53eb\u91d1\u9010\u6d41\uff0c\u6211 \u542c\u4e86\u4ed6\u7684\u540d\u5b57\uff0c\u65e9\u5c31\u8be5\u60f3\u5230\u4e86\u7684\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u5fcd\u7740\u75bc\u75db\uff0c\u8fd8\u5728\u505a\u7740\u9632\u5fa1\u7684\u59ff\u6001\uff0c\u6c5f\u6d77\u5929\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6587\u5148\u751f\u4e5f\u53ef\u4ee5\u7f62\u624b \u4e86\u3002\u4e8c\u5341\u5e74\u4e0d\u89c1\uff0c\u606d\u559c\u4f60\u7684\u4e09\u8c61\u795e\u529f\u5df2\u7ecf\u7ec3\u6210\uff0c\u4ee4\u53d4\u597d\u5417\uff1f\u201d \u3000\u3000\u6587\u9053\u5e84\u60d8\u7136\u82e5\u4e27\uff0c\u9762\u5982\u6b7b\u7070\uff0c\u8bf4\u9053\uff1a\u201c\u59d3\u6c5f\u7684\uff0c\u4f60\u4e0d\u5fc5\u8bf4\u98ce\u51c9\u8bdd\u4e86\uff0c\u6211\u6253\u4e0d\u8fc7\u4f60\u7684\u5e08 \u5f1f\uff0c\u5f53\u7136\u66f4\u6253\u4e0d\u8fc7\u4f60\uff0c\u8981\u6740\u8981\u5250\uff0c\u968f\u4f60\u7684\u4fbf\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u96be\u5f97\u4f60\u8fd8\u5ff5\u7740\u6545\u4eba\uff0c\u4eca\u65e5\u4f60\u6765\u5230\u6211\u5bb6\uff0c\u6c5f\u67d0\u5c82\u6709\u5c06\u5ba2\u4eba\u96be\u4e3a\u4e4b\u7406\uff1f\u4f60\u82e5\u662f \u9ad8\u5174\uff0c\u53ef\u4ee5\u548c\u6211\u518d\u559d\u4e09\u676f\u3002\u82e5\u662f\u8981\u8d70\uff0c\u6211\u4e5f\u662f\u4e3b\u968f\u5ba2\u610f\uff0c\u51b3\u4e0d\u963b\u62e6\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7d20\u6765\u662f\u4e00\u8bfa\u5343\u91d1\uff0c\u6b66\u6797\u4e2d\u4eba\uff0c\u4eba\u4eba\u77e5\u9053\uff0c\u4f46\u6587\u9053\u5e84\u5374\u662f\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\u5ea6\u541b\u5b50\u4e4b\u8179\uff0c \u5728\u8fd9\u6837\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c\u4ed6\u8fd8\u4e0d\u6562\u76f8\u4fe1\u6c5f\u6d77\u5929\u8bf4\u7684\u662f\u771f\uff0c\u5fc3\u60f3\uff1a\u201c\u54ea\u6709\u8fd9\u6837\u4fbf\u5b9c\u7684\u4e8b\uff1f\u201d\u6b63\u7531\u4e8e \u4ed6\u6349\u6478\u4e0d\u5b9a\u662f\u5047\u662f\u771f\uff0c\u4e00\u65f6\u95f4\u8fd8\u4e0d\u6562\u8d70\u3002 \u3000\u3000\u79e6\u5143\u6d69\u53ea\u6015\u6c5f\u6d77\u5929\u8fd8\u672a\u77e5\u9053\u5185\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\uff0c\u8fd9\u4eba\u662f\u7279\u5730\u6765\u95f9\u4e8b\u7684\u3002\u521a\u624d\u8d70\u6389\u7684 \u90a3\u4e2a\u5c0f\u5b50\u5c31\u662f\u4ed6\u7684\u513f\u5b50\uff0c\u4ed6\u5077\u4e86\u6211\u7684\u8bf7\u5e16\uff0c\u5192\u5145\u6211\u7684\u8eab\u4efd\u800c\u6765\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u5192\u540d\u95ef\u9690\u56e0\u4f55\u6545\uff1f\u53ea\u4e3a\u5f53\u5e74\u5bbf\u6028\u6765\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u300288 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u516d\u56de\u3000\u8c6a\u58eb\u60ca\u5fc3\u8c08\u6076\u6597\u3000\u8352\u5c71\u52a8\u9b44\u9047\u7a77\u5112 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u516d\u56de\u3000\u8c6a\u58eb\u60ca\u5fc3\u8c08\u6076\u6597\u3000\u8352\u5c71\u52a8\u9b44\u9047\u7a77\u5112 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u53cd\u6b63\u6211\u5bb6\u4e5f\u6ca1\u6709\u4ec0\u4e48\u635f\u5931\uff0c\u4e8f\u5f97\u4ed6\u8fd9\u4e00\u6765\uff0c\u5f15\u6765\u4e86\u6211\u7684\u5e08\u5f1f\uff0c\u6211\u8fd8\u8be5\u591a\u8c22 \u4ed6\u5462\u3002\u201d\u79e6\u5143\u6d69\u542c\u4e86\u6c5f\u6d77\u5929\u8fd9\u6837\u7684\u8a00\u8bed\uff0c\u4e0d\u6562\u518d\u8bf4\u3002\u4ef2\u957f\u7edf\u9053\u3002\u201d\u5c31\u8fd9\u6837\u4fbf\u5b9c\u4ed6\u4e86\uff1f\u201d\u8fd9\u53e5 \u8bdd\u6b63\u8bf4\u4e2d\u4e86\u6587\u9053\u5e84\u5fc3\u4e2d\u7684\u7591\u8651\uff0c\u4ed6\u521a\u521a\u677e\u4e86\u53e3\u6c14\uff0c\u4e0d\u89c9\u53c8\u7d27\u5f20\u8d77\u6765\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u524d\uff0c\u5bb6\u5e08\u5728\u90bb\u5c71\u653e\u8d70\u4e86\u6587\u5ef7\u74a7\uff0c\u8fd9\u4ef6\u4e8b\u670b\u53cb\u4eec\u90fd\u662f\u77e5 \u9053\u7684\uff0c\u6c5f\u67d0\u5eb8\u611a\uff0c\u6709\u6127\u6069\u5e08\u4f20\u6388\u3002\u522b\u7684\u6211\u5b66\u4e0d\u6765\uff0c\u4f46\u7acb\u8eab\u5904\u4e16\u4e4b\u9053\uff0c\u6211\u5219\u662f\u5904\u5904\u4ee5\u4ed6\u8001\u4eba\u5bb6 \u4e3a\u699c\u6837\u7684\uff0c\u5f53\u5e74\u6211\u7684\u5e08\u7236\u53ef\u4ee5\u9976\u4e86\u6587\u5ef7\u74a7\uff0c\u5982\u4eca\u6211\u53c8\u4f55\u5c1d\u4e0d\u53ef\u653e\u4e86\u4ed6\u7684\u4f84\u513f\uff1f\u4f55\u51b5\u8fd9\u4f4d\u6587\u5148 \u751f\u4eca\u65e5\u662f\u6765\u559d\u559c\u9152\u7684\uff0c\u96be\u4e3a\u5ba2\u4eba\uff0c\u8fd9\u4e0d\u662f\u5927\u6740\u98ce\u666f\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6b47\u4e86\u4e00\u6b47\u3002\u63a5\u7740\u9762\u5411\u6587\u9053\u5e84\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u6587\u5148\u751f\u7684\u6b66\u529f\u5f97\u6765\u4e0d\u6613\uff0c\u597d\u81ea\u4e3a\u4e4b\uff0c\u53ef \u4ee5\u6210\u4e3a\u4e00\u6d3e\u5b97\u5e08\u3002\u5bb6\u5e08\u5f53\u5e74\u653e\u8d70\u4f60\u7684\u53d4\u7236\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u4fdd\u5168\u4f60\u4eec\u8fd9\u6b66\u6797\u4e00\u8109\u3002\u6211\u8fd8\u8bb0\u5f97\u5bb6\u5e08 \u5f53\u5e74\u66fe\u529d\u544a\u4ee4\u53d4\uff1a\u2018\u6539\u90aa\u5f52\u6b63\uff0c\u4e0d\u53ef\u8bef\u5165\u6b67\u9014\u3002\u2019\u5982\u4eca\u6211\u4e5f\u7528\u6211\u5e08\u7236\u5f53\u5e74\u7684\u8bf4\u8bdd\u529d\u544a\u4f60\uff0c\u5e0c \u671b\u4f60\u4e0d\u8981\u8f9c\u8d1f\u4e86\u6211\u4eec\u5e08\u5f92\u7684\u671f\u671b\u3002\u597d\uff0c\u4f60\u8d70\u5427\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8bf4\u5f97\u5341\u5206\u8bda\u6073\uff0c\u573a\u4e2d\u5bbe\u5ba2\u65e0\u4e0d\u611f\u52a8\uff0c\u4eba\u4eba\u90fd\u662f\u5982\u6b64\u60f3\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u7684\u786e\u662f\u4e0d\u6127\u5927 \u4fa0\u7684\u895f\u6000\uff0c\u6587\u9053\u5e84\u8fd9\u53ae\u82e5\u8fd8\u4e0d\u77e5\u6094\u609f\uff0c\u90a3\u5c31\u5f53\u771f\u662f\u79bd\u517d\u4e0d\u5982\u4e86\u3002\u201d \u3000\u3000\u5c82\u77e5\u6587\u9053\u5e84\u5374\u662f\u6267\u8ff7\u4e0d\u609f\uff0c\u60f3\u6cd5\u5b8c\u5168\u4e24\u6837\u3002\u4ed6\u5728\u5929\u4e0b\u82f1\u96c4\u4e4b\u524d\uff0c\u574d\u4e86\u8fd9\u6837\u5927\u7684\u53f0\uff0c\u6df1\u89c9 \u989c\u9762\u65e0\u5149\uff0c\u5fc3\u91cc\u662f\u53c8\u7f9e\u53c8\u607c\uff0c\u60f3\u9053\uff1a\u201c\u6c5f\u6d77\u5929\u6545\u4f5c\u4ec1\u6148\uff0c\u65e0\u975e\u662f\u8981\u6210\u5168\u4ed6\u5927\u4fa0\u4e4b\u540d\uff0c\u8ba9\u5929\u4e0b \u82f1\u96c4\u5bf9\u4ed6\u66f4\u589e\u656c\u4ef0\u800c\u5df2\u3002\u4ed6\u4e0d\u4eb2\u81ea\u51fa\u624b\uff0c\u5374\u8ba9\u4ed6\u7684\u5c0f\u5e08\u5f1f\u6765\u6298\u8fb1\u6211\uff0c\u8fd9\u53e3\u6c14\u6211\u662f\u975e\u51fa\u4e0d\u53ef\u3002 \u6211\u6253\u4e0d\u8fc7\u4ed6\uff0c\u8fd8\u53ef\u4ee5\u9080\u51e0\u4e2a\u4ed6\u7684\u5927\u5bf9\u5934\u6765\uff0c\u603b\u8981\u628a\u4ed6\u4eec\u5e08\u5144\u5f1f\u6253\u8d25\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u5fc3\u6000\u6028\u6068\uff0c\u8138\u8272\u5374\u662f\u4e1d\u6beb\u4e0d\u9732\uff0c\u5f53\u4e0b\u5411\u6c5f\u6d77\u5929\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u6587\u67d0\u4ed6\u5e74\u82e5\u5f97\u5bf8 \u8fdb\uff0c\u5f53\u518d\u6765\u5411\u6c5f\u5927\u4fa0\u9053\u8c22\u3002\u201d\u8bf4\u7f62\uff0c\u56de\u5934\u4fbf\u8d70\u3002\u4ed6\u4ece\u79e6\u5143\u6d69\u7684\u8bdd\u4e2d\uff0c\u77e5\u9053\u513f\u5b50\u5df2\u7ecf\u8d70\u6389\uff0c\u5fc3 \u91cc\u662f\u66f4\u65e0\u7275\u6302\u4e86\u3002 \u3000\u3000\u79e6\u5143\u6d69\u5fc3\u4e2d\u5374\u662f\u6709\u6240\u7275\u6302\u7684\uff0c\u201c\u6587\u5bb6\u7236\u5b50\u8fd9\u6b21\u94ba\u7fbd\u800c\u5f52\uff0c\u60f3\u5fc5\u662f\u4e0e\u5c01\u5b50\u8d85\u4e00\u540c\u56de\u53bb\u7684 \u4e86\u3002\u4e0d\u77e5\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u5c06\u5c01\u5999\u5ae6\u96be\u4e3a\uff1f\u201d\u53ef\u662f\u4ed6\u7684\u5fe7\u8651\u4e5f\u53ea\u80fd\u9690\u853d\u5fc3\u5e95\uff0c\u4e0d\u6562\u5411\u4efb\u4f55\u4eba\u8bf4\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6b63\u8981\u62dc\u89c1\u5e08\u5144\uff0c\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e14\u6162\u3002\u8299\u513f\uff0c\u4f60\u4eec\u592b\u5987\u8fc7\u6765\uff0c\u4f60\u4eec\u5e94\u8be5\u5148\u5411\u5e08\u53d4\u53e9 \u8c22\u6551\u547d\u4e4b\u6069\u3002\u201d\u6c5f\u6653\u8299\u6014\u4e86\u4e00\u6014\uff0c\u4e00\u65f6\u5c1a\u672a\u660e\u767d\u3002\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u521a\u624d\u4f60\u656c\u9152\u4e4b\u65f6\uff0c\u8981\u4e0d\u662f \u5e08\u53d4\u6697\u4e2d\u52a9\u4f60\uff0c\u4f60\u65e9\u5df2\u7ed9\u6587\u9053\u5e84\u7684\u4e09\u8c61\u795e\u529f\u9707\u4f24\u5185\u810f\u4e86\u3002\u201d\u6c5f\u6653\u8299\u4e0e\u5b87\u6587\u96c4\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9 \u5411\u91d1\u9010\u6d41\u53e9\u8c22\u3002 \u3000\u3000\u91d1\u9010\u6d41\u563b\u563b\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u7684\u5e74\u7eaa\u90fd\u5dee\u4e0d\u591a\uff0c\u4f60\u4eec\u884c\u8fd9\u5927\u793c\uff0c\u6211\u53ef\u4e0d\u6562\u5f53\u3002\u201d\u6c5f\u6d77\u5929\u7b11 \u9053\uff1a\u201c\u672c\u95e8\u53ea\u8bba\u8f88\u4efd\uff0c\u4e0d\u8bba\u5e74\u7eaa\u3002\u4f60\u548c\u5c0f\u8f88\u4eec\u5ba2\u6c14\u4f5c\u4ec0\u4e48\uff1f\u201d\u91d1\u9010\u6d41\u672c\u6765\u8981\u6b20\u8eab\u907f\u793c\u7684\uff0c\u7ed9 \u6c5f\u6d77\u5929\u8f7b\u8f7b\u4e00\u6309\uff0c\u7adf\u662f\u4e1d\u6beb\u4e0d\u80fd\u52a8\u5f39\u3002\u53ea\u5f97\u5927\u9a6c\u91d1\u5200\u5730\u5750\u7740\uff0c\u53d7\u4e86\u8fd9\u5bf9\u65b0\u4eba\u7684\u4e09\u4e2a\u54cd\u5934\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e0d\u7531\u5f97\u5fc3\u4e2d\u6697\u6674\u4f69\u670d\uff0c\u60f3\u9053\u3002\u201d\u6c5f\u6d77\u5929\u679c\u7136\u4e0d\u6127\u505a\u6211\u7684\u5e08\u5144\uff0c\u6211\u82e5\u8981\u6709\u4ed6\u8fd9\u6837\u7684 \u9020\u8be3\uff0c\u53ea\u6015\u81f3\u5c11\u8fd8\u5f97\u4e0b\u5341\u5e74\u7684\u529f\u592b\u3002\u201d \u3000\u3000\u6c5f\u6653\u8299\u505a\u4e86\u65b0\u5a18\uff0c\u4ecd\u4e0d\u5931\u5979\u539f\u6709\u7684\u5929\u771f\uff0c\u53e9\u8fc7\u4e86\u5934\uff0c\u7ad9\u8d77\u6765\u7b11\u9053\uff1a\u201c\u5c0f\u5e08\u53d4\uff0c\u6211\u7239\u7239\u8bf4 \u4f60\u7684\u672c\u95e8\u6b66\u5b66\uff0c\u6bd4\u4ed6\u8fd8\u8981\u9ad8\u660e\u3002\u4f60\u53ef\u5f97\u6307\u70b9\u6307\u70b9\u6211\u4eec\u8fd9\u73ed\u5e08\u4f84\u5440\u3002\u201d\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u5e08\u5f1f\uff0c \u4f60\u542c\u89c1\u4e86\u6ca1\u6709\uff1f\u8fd9\u5927\u793c\u53ef\u662f\u4e0d\u597d\u53d7\u7684\u554a\uff01\u55ef\uff0c\u8299\u513f\uff0c\u4f60\u4e5f\u592a\u4e0d\u61c2\u793c\u8c8c\u4e86\uff0c\u5e08\u53d4\u5c31\u662f\u5e08\u53d4\uff0c\u4e3a \u4ec0\u4e48\u52a0\u4e0a\u4e00\u4e2a\u5c0f\u5b57\uff1f\u201d\u6c5f\u6d77\u5929\u5e73\u7d20\u662f\u8a00\u7b11\u4e0d\u82df\u7684\uff0c\u96be\u5f97\u4ed6\u4eca\u5929\u5982\u6b64\u9ad8\u5174\uff0c\u81ea\u52a8\u8bf4\u8d77\u7b11\u6765\u3002\u5ba2 \u4eba\u4eec\u90fd\u8ddf\u7740\u4ed6\u54c4\u5802\u5927\u7b11\u3002 \u3000\u3000\u79e6\u5143\u6d69\u8fd9\u624d\u77e5\u9053\u4e86\u91d1\u9010\u6d41\u7684\u8eab\u4efd\u3001\u6765\u5386\uff0c\u5fc3\u60f3\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u8bf4\u6211\u5e08\u7236\u6bd4\u4ed6\u8fd8\u5c0f\u4e00\u8f88\uff0c\u539f \u6765\u7adf\u662f\u771f\u7684\u3002\u201d\u539f\u6765\u91d1\u4e16\u9057\u7684\u8f88\u4efd\u6781\u9ad8\uff0c\u4ed6\u7684\u5e08\u7236\u6bd2\u9f99\u5c0a\u8005\u662f\u6bd4\u9099\u5c71\u6d3e\u524d\u4e24\u8f88\u7684\u638c\u95e8\u4eba\u5415\u56db \u5a18\u8fd8\u9ad8\u4e00\u8f88\u7684\u3002\u4f46\u56e0\u91d1\u4e16\u9057\u7684\u5e08\u95e8\u548c\u4e2d\u539f\u5404\u6d3e\u5e76\u65e0\u6e0a\u6e90\uff0c\u6240\u4ee5\u5168\u4e16\u9057\u548c\u6b66\u6797\u5404\u6d3e\u540d\u5bbf\u8ba4\u4e0d\u62d8 \u8bba\u8f88\u4efd\u3002\u53c8\u7531\u4e8e\u91d1\u4e16\u9057\u7684\u59bb\u5b50\u8c37\u4e4b\u534e\u662f\u5415\u56db\u5a18\u7684\u5f92\u5f1f\uff0c\u6240\u4ee5\u4ed6\u5bf9\u672c\u6765\u5e94\u8be5\u662f\u5e73\u8f88\u7684\u5415\u56db\u5a18\u548c \u5510\u6653\u6f9c\u7b49\u4eba\uff0c\u4e5f\u90fd\u662f\u4ee5\u5c0f\u8f88\u81ea\u5c45\u7684\u3002\u5176\u5b9e\u82e5\u7136\u8ba4\u771f\u8bba\u8d77\u6765\u7684\u8bdd\uff0c\u5929\u5c71\u6d3e\u73b0\u4efb\u7684\u638c\u95e8\u4eba\u5510\u7ecf\u5929 \u548c\u91d1\u9010\u6d41\u4e5f\u4e0d\u8fc7\u662f\u5c5e\u4e8e\u5e73\u8f88\uff0c\u5510\u7ecf\u5929\u7684\u59bb\u5b50\u51b0\u5ddd\u5929\u5973\u662f\u6b66\u5f53\u6d3e\u7684\u957f\u8001\uff0c\u6bd4\u96f7\u9707\u5b50\u9ad8\u4e00\u8f88\uff0c\u91d1 \u9010\u6d41\u4e5f\u5c31\u5f53\u7136\u8981\u6bd4\u96f7\u9707\u5b50\u7684\u5f92\u5f1f\u79e6\u5143\u6d69\u9ad8\u4e24\u8f88\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u563b\u54c8\u5927\u7b11\u58f0\u4e2d\uff0c\u4ef2\u957f\u7edf\u5374\u6709\u6124\u6124\u4e0d\u5e73\u4e4b\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\uff0c\u4f60\u4e5f\u672a\u514d\u592a\u8fc7\u5bbd\u539a \u4e86\uff0c\u6587\u9053\u5e84\u8fd9\u53ae\u6697\u7b97\u4f60\u7684\u5973\u513f\uff0c\u4f60\u5c45\u7136\u653e\u8fc7\u4e86\u4ed6\uff01\u53ef\u60dc\u6211\u73b0\u5728\u624d\u77e5\u9053\uff0c\u8981\u662f\u6211\u65e9\u77e5\u9053\u7684\u8bdd\uff0c \u4f60\u653e\u8fc7\u4ed6\uff0c\u6211\u8001\u53eb\u6bd4\u4e5f\u4e0d\u80af\u653e\u8fc7\u4ed6\uff01\u6700\u5c11\u4e5f\u5f97\u50cf\u4f60\u7684\u5e08\u7236\u5f53\u5e74\u5bf9\u4ed8\u6587\u5ef7\u74a7\u90a3\u6837\uff0c\u5e9f\u6389\u4ed6\u7684\u6b66 \u529f\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff0c\u201c\u7b97\u4e86\u3002\u8fd9\u53ea\u662f\u79c1\u4eba\u6069\u6028\uff0c\u53cd\u6b63\u4ed6\u4e5f\u6ca1\u4f24\u4e86\u6211\u7684\u5973\u513f\u3002\u201d\u6c5f\u6d77\u5929\u54ea\u91cc\u77e5 \u9053\uff0c\u6587\u9053\u5e84\u5df2\u662f\u51c6\u5907\u518d\u6b21\u51fa\u5c71\uff0c\u63a5\u53d7\u671d\u5ef7\u8058\u793c\uff0c\u4ed6\u8fd9\u6b21\u6765\uff0c\u5e76\u975e\u4ec5\u4ec5\u662f\u4e3a\u4e86\u79c1\u4eba\u6069\u6028\u800c\u5df2\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4eca\u65e5\u6211\u662f\u53cc\u559c\u4e34\uff0c\u4e00\u70b9\u70b9\u7684\u98ce\u6ce2\u4e0d\u5fc5\u518d\u63d0\u4e86\u3002\u5e08\u5f1f\uff0c\u6211\u53ef\u5f97\u5148\u95ee\u4f60\uff0c\u5e08\u7236 \u4ed6\u8001\u4eba\u5bb6\u597d\u5417\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\u3002\u7239\u7239\u6709\u4e00\u5c01\u4fe1\u548c\u4e00\u4ef6\u4fe1\u7269\u53eb\u6211\u4ea4\u7ed9\u4f60\u3002\u201d\u6c5f\u6d77\u5929\u5df1\u6709\u4e8c \u5341\u5e74\u4e0d\u89c1\u5e08\u7236\u7684\u4eb2\u7b14\u624b\u8ff9\uff0c\u5f53\u4e0b\u5148\u8dea\u5012\u5730\u4e0a\uff0c\u884c\u8fc7\u4e86\u201c\u89c1\u7269\u5982\u89c1\u4eba\u201d\u7684\u672c\u95e8\u5927\u793c\uff0c\u8fd9\u624d\u63a5\u8fc7 \u4e86\u5e08\u7236\u7684\u4eb2\u7b14\u4fe1\u548c\u90a3\u4ef6\u4fe1\u7269\uff0c\u90a3\u4ef6\u4fe1\u7269\u662f\u4e00\u53ea\u6676\u83b9\u7684\u767d\u7389\u73af\u3002 \u3000\u3000\u7389\u73af\u5165\u624b\uff0c\u89e6\u4f53\u751f\u5bd2\uff0c\u6c5f\u6d77\u5929\u4e00\u770b\u5c31\u77e5\u8fd9\u662f\u6d77\u4e2d\u7684\u5bd2\u7389\u3002\u5f53\u5e74\u91d1\u4e16\u9057\u6240\u5f97\u7684\u4e54\u5317\u6e9f\u7684\u9057 \u7269\u4e4b\u4e2d\uff0c\u6709\u4e00\u526f\u767d\u7389\u7532\u548c\u4e00\u526f\u7389\u5f13\u4e09\u679d\u7389\u7bad\uff0c\u90a3\u526f\u7389\u7532\u91d1\u4e16\u9057\u7ed9\u4e86\u6c5f\u6d77\u5929\u5f53\u4f5c\u4f20\u5bb6\u4e4b\u5b9d\uff0c\u7389 \u5f13\u7389\u7bad\u5219\u4ecd\u5728\u91d1\u4e16\u9057\u624b\u4e0a\u3002\u8fd9\u679a\u7389\u73af\u7684\u7389\u8d28\uff0c\u6b63\u662f\u548c\u6c5f\u6d77\u65e0\u6240\u5f97\u7684\u90a3\u526f\u7389\u7532\u76f8\u540c\u3002\u91d1\u9010\u6d41\u8bf4 \u9053\uff1a\u201c\u7239\u7239\u5c06\u90a3\u4e09\u679d\u7389\u7bad\u6253\u6210\u4e86\u4e09\u679a\u6307\u73af\u3002\u53eb\u6211\u7ed9\u4f60\u4e00\u4e2a\uff0c\u4f5c\u4e3a\u4fe1\u7269\u3002\u8bf7\u6055\u6211\u73b0\u5728\u624d\u62ff\u51fa \u6765\u3002\u201d\u8bf4\u7f62\uff0c\u59cb\u884c\u540c\u95e8\u76f8\u89c1\u4e4b\u793c\uff0c\u91d1\u9010\u6d41\u7ed9\u5e08\u5144\u53e9\u4e86\u4e00\u4e2a\u5934\uff0c\u6c5f\u6d77\u5929\u957f\u63d6\u4e0d\u8dea\uff0c\u8fd8\u4e86\u534a\u793c\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u975e\u5e38\u611f\u6fc0\u5e08\u7236\u5bf9\u4ed6\u7684\u5173\u5fc3\uff0c\u4f46\u5374\u4e5f\u6709\u70b9\u4e0d\u89e3\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5e08\u7236\u53eb\u5e08\u5f1f\u6765\u89c1\u6211\uff0c \u4f55\u5fc5\u7528\u4ec0\u4e48\u4fe1\u7269\uff1f\u6709\u4ed6\u7684\u4eb2\u7b14\u4e66\u4fe1\u8db3\u5df2\u591f\u4e86\u3002\u96be\u9053\u6211\u8fd8\u770b\u4e0d\u51fa\u4ed6\u7684\u672c\u95e8\u6b66\u529f\u5417\uff1f\u201d\u4f46\u5f53\u4ed6\u770b \u4e86\u5e08\u7236\u7684\u8fd9\u5c01\u4fe1\u540e\uff0c\u8fd9\u624d\u660e\u767d\u8fd9\u679a\u7389\u73af\u5e76\u975e\u4ec5\u4ec5\u662f\u7ed9\u91d1\u9010\u6d41\u62ff\u6765\u5f53\u4f5c\u4f1a\u89c1\u540c\u95e8\u7684\u4fe1\u7269\u7684\u3002 \u3000\u3000\u8fd9\u5c01\u4fe1\u4ea4\u4ee3\u6c5f\u6d77\u5929\u4e09\u4ef6\u4e8b\u60c5\uff0c\u7b2c\u4e00\u4ef6\u662f\u6258\u4ed6\u7167\u987e\u5e08\u5f1f\uff1b\u7b2c\u4e8c\u4ef6\u544a\u8bc9\u4ed6\uff0c\u4ed6\u7684\u5927\u8205\u53f6\u51b2\u9704 \u5c06\u8981\u4ece\u6d77\u5916\u5f52\u6765\uff0c\u5e76\u95ee\u6c5f\u6d77\u5929\u5df2\u7ecf\u6536\u4e86\u53f6\u51b2\u9704\u7684\u513f\u5b50\u505a\u5f92\u5f1f\u6ca1\u6709\uff0c\u5982\u679c\u8fd8\u672a\u89c1\u7740\u7684\u8bdd\uff0c\u90a3\u5c31 \u5f97\u8d76\u5feb\u5bfb\u627e\u3002\u7b2c\u4e09\u4ef6\u662f\u8981\u6c5f\u6d77\u5929\u5728\u660e\u5e74\u5143\u9704\u8282\u65e5\u7684\u665a\u4e0a\uff0c\u6234\u7740\u8fd9\u679a\u767d\u7389\u73af\uff0c\u5230\u5317\u4eac\u897f\u5c71\u7684\u79d8 \u9b54\u5d16\u53bb\u4f1a\u4e00\u4e2a\u4eba\uff0c\u90a3\u4e2a\u4eba\u7684\u624b\u4e0a\u5c06\u4f1a\u6234\u7740\u4e00\u679a\u540c\u6837\u7684\u767d\u7389\u73af\u3002\u8fd9\u4e09\u4ef6\u4e8b\u60c5\u91cd\u8981\u7684\u662f\u6700\u540e\u4e00 \u4ef6\uff0c\u53ef\u662f\u4fe1\u4e0a\u5374\u6ca1\u6709\u8bf4\u660e\u8fd9\u4e2a\u4eba\u662f\u8c01\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u60f3\uff1a\u201c\u6216\u8005\u5c0f\u5e08\u5f1f\u4f1a\u77e5\u9053\uff0c\u5f85\u4eca\u665a\u5ba2\u4eba\u6563\u4e86\uff0c\u6211\u518d\u95ee\u4ed6\u3002\u201d\u6c5f\u6d77\u5929\u6df1\u77e5\u5e08\u7236\u7684 \u813e\u6c14\uff0c\u6240\u505a\u7684\u4e8b\u60c5\uff0c\u5f80\u5f80\u662f\u4ee4\u4eba\u83ab\u6d4b\u9ad8\u6df1\u7684\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5e08\u7236\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5927\u54e5\u5feb\u8981\u56de\u6765\u4e86\u3002\u5e08\u7236\u4ed6\u8001\u4eba\u5bb6 \u8fd8\u4e0d\u77e5\u9053\u6155\u534e\u65e9\u5df2\u4e0e\u54b1\u4eec\u8ba4\u4e86\u4eb2\u5462\u3002\u201d\u8c37\u4e2d\u83b2\u5927\u559c\u9053\uff1a\u201c\u5927\u54e5\u82e5\u662f\u56de\u6765\uff0c\u77e5\u9053\u534e\u4f84\u8fd9\u51e0\u5e74\u5e72 \u7684\u8f70\u8f70\u70c8\u70c8\u7684\u4e8b\u8ff9\uff0c\u4e0d\u77e5\u9053\u8be5\u591a\u9ad8\u5174\u5462\uff01\u91d1\u5e08\u5f1f\uff0c\u4f60\u548c\u5e08\u7236\u4f4f\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u6211\u7684\u5927\u54e5\u662f\u5e38\u5e38 \u53bb\u62dc\u671b\u4f60\u4eec\u7684\u5417\uff1f\u4ed6\u7684\u8fd1\u51b5\u5982\u4f55\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u5148\u5165\u5e2d\u5427\uff0c\u9152\u90fd\u51c9\u4e86\u3002\u201d\u91d1\u9010\u6d41\u8fd9\u6b21\u5374\u4e0d\u518d\u5750\u9996\u5e2d\u4e86\uff0c\u7b11\u9053\uff1a\u201c\u521a\u624d\u6211 \u662f\u4ee3\u8868\u7239\u7239\u6765\u5411\u5e08\u5144\u9053\u8d3a\u7684\uff0c\u5982\u4eca\u4fe1\u5df2\u4ea4\u4e86\uff0c\u6211\u53ea\u80fd\u4ee5\u4e3b\u4eba\u7684\u5e08\u5f1f\u8eab\u4efd\u5165\u5ea7\u5566\u3002\u5e08\u5144\u529e\u559c \u4e8b\uff0c\u6211\u505a\u5e08\u5f1f\u7684\u5e94\u8be5\u662f\u534a\u4e2a\u4e3b\u4eba\uff0c\u8fd9\u4e2a\u9996\u4f4d\u5e94\u8be5\u7531\u4ef2\u5e2e\u4e3b\u5750\u4e86\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u63a8\u8f9e\u4e0d\u5f97\uff0c\u53ea\u597d\u5750\u4e0b\u3002\u7b11\u9053\uff1a\u201c\u91d1\u8001\u5f1f\uff0c\u4f60\u7684\u6027\u60c5\u4e0e\u4eca\u5c0a\u53c8\u4f3c\u53c8\u4e0d\u4f3c\uff0c\u5408\u5c0a\u521d\u5728 \u6c5f\u6e56\u884c\u8d70\u7684\u65f6\u5019\uff0c\u75af\u75af\u766b\u766b\u7684\uff0c\u522b\u4eba\u90fd\u6015\u4ed6\u51e0\u5206\u3002\u4f60\u521d\u6765\u7684\u65f6\u5019\uff0c\u6709\u4f60\u7239\u7239\u7684\u90a3\u4e00\u4efd\u4e0d\u7f81\u6c14 \u6982\uff0c\u4f46\u5374\u4e0d\u4f3c\u4f60\u7239\u7239\u7684\u75af\u766b\uff0c\u8f6c\u773c\u95f4\u4f60\u53c8\u5f6c\u5f6c\u6709\u793c\u8d77\u6765\u4e86\uff0c\u8fd9\u5012\u4ee4\u6211\u9887\u51fa\u610f\u5916\u5462\u3002\u563f\u563f\uff0c\u54c8 \u54c8\uff0c\u6211\u548c\u4f60\u7684\u7239\u7239\u662f\u8001\u670b\u53cb\uff0c\u4f60\u53ef\u522b\u602a\u6211\u80e1\u8bf4\u516b\u9053\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u662f\u4e48\uff1f\u7239\u7239\u7684\u65e7\u4e8b\u6211\u77e5\u9053\u5f97\u5f88\u5c11\uff0c\u4e0d\u8fc7\u5988\u5988\u5012\u662f\u5e38\u5e38\u8bf4\u6211\u7684\u813e\u6c14\u50cf\u7239\u7239 \u7684\u3002\u201d\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u4f9d\u6211\u770b\u6765\uff0c\u4f60\u662f\u4e00\u534a\u50cf\u4f60\u7239\u7239\uff0c\u4e00\u534a\u50cf\u4f60\u5988\u5988\u3002\u4f60\u4e0d\u77e5\u9053\uff0c\u4f60\u7239\u7239\u5c11 \u5e74\u65f6\u5019\u6bd4\u4f60\u80e1\u95f9\u767e\u500d\uff0c\u540e\u6765\u8ba4\u8bc6\u4e86\u4f60\u7684\u5988\u5988\uff0c\u6027\u60c5\u8fd9\u624d\u6e10\u6e10\u6709\u4e86\u6539\u53d8\u7684\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u8bf4\u5f97\u4e0d\u9519\uff0c\u91d1\u9010\u6d41\u7684\u7236\u6bcd\u4e00\u4e2a\u662f\u653e\u8361\u4e0d\u7f81\uff0c\u4e00\u4e2a\u662f\u7aef\u5e84\u4e25\u8c28\uff0c\u91d1\u9010\u6d41\u81ea\u5c0f\u53d7\u7236\u6bcd \u7684\u718f\u9676\uff0c\u4ed6\u7684\u6027\u60c5\u5f53\u7136\u4e5f\u662f\u4e24\u65b9\u9762\u90fd\u53d7\u4e86\u5f71\u54cd\u3002 \u3000\u3000\u5f53\u4e0b\u91cd\u65b0\u5165\u5e2d\uff0c\u4ef2\u957f\u7edf\u5750\u4e0b\u91d1\u9010\u6d41\u521a\u624d\u7684\u4f4d\u5b50\uff0c\u91d1\u9010\u6d41\u5219\u5750\u5728\u6c5f\u6d77\u5929\u7684\u4e0b\u9996\u3002\u91d1\u9010\u6d41\u53c8 \u628a\u79e6\u5143\u6d69\u62c9\u4e86\u6765\uff0c\u8981\u4ed6\u5750\u5728\u81ea\u5df1\u7684\u65c1\u8fb9\uff0c\u53e6\u4e00\u8fb9\u4e0e\u5510\u52a0\u6e90\u76f8\u90bb\uff0c\u8fd9\u662f\u521a\u624d\u6587\u80dc\u4e2d\u5750\u7684\u4f4d\u5b50\u3002 \u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u5047\u7684\u8dd1\u4e86\uff0c\u4f60\u8fd9\u4e2a\u771f\u7684\u7406\u8be5\u5c31\u5750\uff0c\u8fd8\u5ba2\u6c14\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u9887\u662f\u5c34\u5c2c\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u91d1\u3001\u91d1\u5c11\u4fa0\uff0c\u6211\u4e0d\u77e5\u9053\u4f60\u7684\u8eab\u4efd\uff0c\u8bf8\u591a\u5931\u793c\uff0c\u4f60\u3001\u4f60\u83ab \u89c1\u602a\u3002\u8fd9\u4e48\u591a\u8001\u524d\u8f88\u5728\u5ea7\uff0c\u6211\u600e\u6562\u5106\u8d8a\uff1f\u201d\u79e6\u5143\u6d69\u5df2\u77e5\u9053\u91d1\u9010\u6d41\u6bd4\u4ed6\u957f\u4e24\u8f88\uff0c\u4e0d\u4fbf\u518d\u548c\u4ed6\u79f0 \u5144\u9053\u5f1f\uff0c\u4f46\u91d1\u9010\u6d41\u7684\u5e74\u7eaa\u4e0e\u4ed6\u76f8\u82e5\uff0c\u4ed6\u53c8\u4e0d\u597d\u610f\u601d\u4ee5\u201c\u8001\u524d\u8f88\u201d\u76f8\u79f0\uff0c\u662f\u4ee5\u53ea\u597d\u79f0\u4ed6\u4e00\u58f0 \u201c\u5c11\u4fa0\u201d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u7684\u5e08\u5144\u4eba\u79f0\u5927\u4fa0\uff0c\u8fd9\u662f\u540d\u526f\u5176\u5b9e\uff0c\u6211\u521a\u521a\u51fa\u9053\uff0c\u54ea\u91cc\u5c31\u80fd\u79f0\u4e00\u4e2a \u2018\u4fa0\u2019\u5b57\uff1f\u6211\u65e9\u5c31\u4e0e\u4f60\u8bf4\u8fc7\u4e86\uff0c\u54b1\u4eec\u662f\u5404\u4ea4\u5404\u7684\uff0c\u4e0d\u5fc5\u62d8\u6ce5\u4ec0\u4e48\u8f88\u4efd\u3002\u4f60\u6211\u8fd8\u662f\u5144\u5f1f\u76f8\u79f0\uff0c \u79e6\u5927\u54e5\uff0c\u6211\u6700\u8ba8\u538c\u522b\u4eba\u5ba2\u6c14\uff0c\u4f60\u5c31\u7ed9\u6211\u5750\u4e0b\u6765\u5427\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e5f\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6c5f\u6e56\u4e0a\u662f\u8bb2\u7a76\u5404\u4ea4\u5404\u7684\uff0c\u82e5\u5f53\u771f\u8981\u7b97\u8d77\u8f88\u4efd\uff0c\u8bba\u8d77\u6392\u884c\uff0c\u90a3\u9ebb \u70e6\u53ef\u5c31\u592a\u591a\u4e86\u3002\u8fd9\u4e2a\u4f4d\u5b50\u672c\u6765\u662f\u7ed9\u4f60\u7684\uff0c\u4f60\u4e0d\u5fc5\u5ba2\u6c14\u4e86\u3002\u201d\u79e6\u5143\u6d69\u542c\u5f97\u6c5f\u6d77\u5929\u4e5f\u5982\u6b64\u8bf4\uff0c\u53ea \u597d\u5750\u4e0b\u3002\u4f46\u4ed6\u5fc3\u4e2d\u6709\u4e8b\uff0c\u5e2d\u4e0a\u8bf8\u4eba\u53d9\u65e7\u8c08\u65b0\uff0c\u5341\u5206\u70ed\u95f9\uff0c\u4ed6\u5374\u662f\u6c89\u9ed8\u4e0d\u8a00\uff0c\u663e\u51fa\u5fc3\u795e\u4e0d\u5c5e\u7684 \u6a21\u6837\u3002 \u3000\u3000\u5ea7\u4e2d\u4ee5\u4ed6\u8f88\u4efd\u6700\u4f4e\uff0c\u6c5f\u6d77\u5929\u53ea\u9053\u4ed6\u662f\u8fc7\u4e8e\u62d8\u8c28\uff0c\u4e0d\u6562\u8bf4\u8bdd\u3002\u53ea\u6709\u91d1\u9010\u6d41\u660e\u767d\u4ed6\u7684\u5fc3\u4e8b\uff0c \u6084\u6084\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u8fc7\u4e24\u5929\u6211\u548c\u4f60\u5230\u5f82\u5f95\u5c71\u63a2\u542c\u6d88\u606f\uff0c\u51b3\u4e0d\u8ba9\u4f60\u90a3\u4f4d\u5c01\u59d1\u5a18 \u53d7\u5230\u5106\u78e8\u5c31\u662f\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9762\u4e0a\u4e00\u7ea2\uff0c\u4f4e\u5934\u559d\u9152\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u54ac\u8033\u6735\uff0c\u6084\u6084\u5730\u8bf4\u4e9b\u4ec0\u4e48\u5440\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u79e6\u5927\u54e5\u662f\u60f3 \u5ff5\u4e00\u4f4d\u670b\u53cb\u3002\u6211\u7b54\u5e94\u966a\u4ed6\u540c\u53bb\u63a2\u8bbf\u3002\u201d\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u662f\u5973\u670b\u53cb\u4e48\uff1f\u6211\u8001\u53eb\u5316\u6700\u559c\u6b22\u505a\u5a92 \u4eba\uff0c\u4f60\u82e5\u6709\u4e3a\u96be\u4e4b\u4e8b\uff0c\u8bf4\u7ed9\u6211\u542c\uff0c\u8001\u53eb\u5316\u603b\u6709\u529e\u6cd5\u6210\u5168\u4f60\u7684\u5fc3\u613f\u3002\u201d\u539f\u6765\u91d1\u9010\u6d41\u6240\u8bf4\u7684\u8bdd\uff0c \u4ef2\u957f\u7edf\u867d\u6ca1\u542c\u5168\uff0c\u4e5f\u5df2\u9690\u7ea6\u542c\u5230\u4e86\u4e00\u534a\u3002\u201c\u5f82\u5f95\u5c71\u201d\u548c\u201c\u5c01\u59d1\u5a18\u201d\u7b49\u7b49\uff0c\u4ed6\u90fd\u542c\u89c1\u4e86\u3002\u79e6\u5143 \u6d69\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u8bf4\u7b11\u4e86\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u6700\u7231\u591a\u7ba1\u95f2\u4e8b\uff0c\u5fc3\u60f3\u3002\u201d\u5f82\u5f95\u5c71\u6709\u4ec0\u4e48\u59d3\u5c01\u6216\u59d3\u98ce\u7684\u6b66\u6797\u4eba\u5bb6\uff0c\u8fd9\u6211\u5012\u4e0d\u77e5\u9053\u3002 \u8fd9\u5c0f\u5a03\u513f\u4e0d\u597d\u610f\u601d\u8bf4\uff0c\u6211\u5012\u8981\u53bb\u6253\u542c\u6253\u542c\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u628a\u8fd9\u4ef6\u4e8b\u60c5\u653e\u5728\u5fc3\u4e0a\uff0c\u4e0d\u518d\u8ffd\u95ee\u4e0b\u53bb\uff0c\u5f53\u4e0b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u91d1\u8001\u5f1f\uff0c\u4f60\u4e0d\u77e5\u9053\uff0c\u6211 \u8001\u53eb\u5316\u559c\u6b22\u505a\u5a92\u4eba\u8fd9\u662f\u51fa\u4e86\u540d\u7684\uff0c\u5f53\u5e74\u4ee4\u5c0a\u4ee4\u5802\u7684\u5a5a\u4e8b\u4e5f\u662f\u7531\u6211\u64ae\u5408\u7684\u5462\u3002\u201d\u5176\u5b9e\u91d1\u9010\u6d41\u7684 \u7236\u6bcd\u91d1\u4e16\u9057\u548c\u8c37\u4e4b\u534e\u65e9\u5df2\u76f8\u8bc6\u591a\u5e74\uff0c\u53ea\u56e0\u7ecf\u8fc7\u8bb8\u591a\u6ce2\u6298\uff0c\u6240\u4ee5\u5230\u4e86\u91d1\u4e16\u9057\u56db\u5341\u5c81\u7684\u5e74\u7eaa\u65b9\u624d \u6210\u5a5a\uff0c\u4ef2\u957f\u7edf\u662f\u66fe\u529d\u8fc7\u91d1\u4e16\u9057\u65e9\u5b9a\u4e3b\u610f\uff0c\u4f46\u8fd9\u6bb5\u5a5a\u4e8b\u5219\u5e76\u975e\u7531\u4ed6\u201c\u64ae\u201d\u5408\u7684\u3002\u4ef2\u957f\u7edf\u8bf4\u5230\u4ed6 \u81ea\u8ba4\u4e3a\u5f97\u610f\u4e4b\u4e8b\uff0c\u4e0d\u514d\u5938\u5927\u5176\u8f9e\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u63a5\u7740\u7b11\u9053\uff1a\u201c\u4ee4\u5c0a\u548c\u4f60\u7684\u5e08\u5144\u662f\u540c\u4e00\u65e5\u6210\u4eb2\u7684\uff0c\u6b64\u4e8b\u4f20\u4e3a\u6b66\u6797\u4f73\u8bdd\uff0c\u5374\u4e00\u6643\u773c\u5c31 \u8fc7\u4e86\u4e8c\u5341\u5e74\u4e86\uff0c\u4eca\u5c0a\u4ee4\u5802\u597d\u5417\uff1f\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u56de\u4e2d\u539f\u8d70\u8d70\uff1f\u96be\u9053\u628a\u4ee5\u524d\u7684\u8001\u670b\u53cb\u90fd\u5fd8\u8bb0 \u4e86\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u8fd9\u624d\u6709\u7a7a\u7b54\u590d\u5404\u4eba\u7684\u95ee\u9898\uff0c\u8bf4\u9053\uff1a\u201c\u5bb6\u7236\u662f\u4f4f\u5728\u4e54\u5317\u6e9f\u7956\u5e08\u4ece\u524d\u4f4f\u8fc7\u7684\u706b\u5c71\u5c9b \u4e0a\uff0c\u59ec\u4f2f\u4f2f\u5341\u4e94\u5e74\u524d\u6765\u548c\u6211\u4eec\u540c\u4f4f\uff0c\u90a3\u65f6\u6211\u8fd8\u672a\u6ee1\u4e94\u5c81\uff0c\u542c\u8bf4\u4ed6\u662f\u4ece\u5929\u7afa\u56de\u6765\u7684\uff0c\u5077\u6765\u4e86\u8bb8 \u591a\u597d\u4e1c\u897f\uff0c\u9001\u7ed9\u6211\u8bb8\u591a\u597d\u73a9\u7684\u73a9\u610f\u513f\u3002\u201d\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u59ec\u4f2f\u4f2f\u4e00\u5411\u662f\u6280\u75d2\u96be\u71ac\uff0c\u5230\u4ec0\u4e48\u5730 \u65b9\u90fd\u8981\u4e00\u65bd\u7a7a\u7a7a\u5999\u624b\u7684\u3002\u4f46\u4ed6\u5728\u706b\u5c71\u5c9b\u5341\u4e94\u5e74\uff0c\u8fd9\u4e5f\u662f\u91d1\u76c6\u6d17\u624b\u4e86\u3002\u8fd9\u65e5\u5b50\u4e0d\u77e5\u4ed6\u662f\u600e\u4e48\u8fc7 \u7684\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u706b\u5c71\u5c9b\u6ca1\u6709\u7b2c\u4e8c\u5bb6\u4eba\u5bb6\uff0c\u4ed6\u5f53\u7136\u662f\u4e0d\u80fd\u518d\u65bd\u7a7a\u7a7a\u5999\u624b\u4e86\u3002\u4e0d\u8fc7\uff0c\u4ed6\u81ea\u5df1 \u6ca1\u5077\u4e1c\u897f\u5374\u6559\u6211\u5077\u4e1c\u897f\uff0c\u8fd9\u662f\u7792\u7740\u6211\u7684\u6bcd\u4eb2\u6559\u7684\u3002\u201d\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u6ca1\u6709\u7792\u7740\u5e08\u7236\uff1f\u201d\u91d1\u9010 \u6d41\u9053\uff1a\u201c\u7239\u7239\u8fd8\u9f13\u52b1\u6211\u5411\u59ec\u4f2f\u4f2f\u8ba8\u6559\u5462\u3002\u7239\u7239\u8bf4\u8ba9\u59ec\u4f2f\u4f2f\u6559\u6211\uff0c\u4e5f\u597d\u8ba9\u4ed6\u8fc7\u8fc7\u8d3c\u763e\u3002\u5176\u5b9e\u6211 \u5988\u4e5f\u662f\u77e5\u9053\u7684\uff0c\u4e0d\u8fc7\u5979\u88c5\u4f5c\u4e0d\u77e5\u7f62\u4e86\u3002\u201d\u4f17\u4eba\u542c\u4e86\uff0c\u65e0\u4e0d\u5927\u7b11\u3002\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u4e86\u4e0d\u8d77\uff0c\u4e86\u4e0d \u8d77\u4f60\u7684\u6b66\u529f\u4e0d\u900a\u4e8e\u4f60\u7236\u5f53\u5e74\uff0c\u4f46\u6bd4\u4f60\u7236\u4eb2\u8fd8\u591a\u4e86\u4e00\u9879\u795e\u5077\u7edd\u6280\uff0c\u6c5f\u6e56\u4e0a\u8fd8\u6709\u8c01\u4eba\u6562\u6765\u60f9 \u4f60\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u53f6\u5927\u54e5\uff08\u51b2\u9704\uff09\u5230\u8fc7\u706b\u5c71\u5c9b\u51e0\u6b21\uff0c\u542c\u8bf4\u4ed6\u4eec\u592b\u59bb\u662f\u5728\u4e1c\u6d77\u4e00\u4e2a\u65e0\u540d \u5c0f\u5c9b\u5c45\u4f4f\u3002\u7239\u7239\u6709\u65f6\u5019\u4e5f\u5230\u4ed6\u4eec\u90a3\u513f\u53bb\u73a9\uff0c\u4e0d\u8fc7\u6211\u5374\u6ca1\u53bb\u8fc7\u3002\u6700\u8fd1\u4e00\u6b21\u662f\u53bb\u5e74\u814a\u6708\u53bb\u7684\uff0c\u8fc7 \u4e86\u5e74\u624d\u56de\u6765\u3002\u636e\u7239\u7239\u8bf4\uff0c\u53f6\u5927\u54e5\u5c31\u8981\u56de\u4e2d\u539f\u4e86\u3002\u201d\u8c37\u4e2d\u83b2\u548c\u53f6\u6155\u534e\u7b49\u4eba\u542c\u4e86\u90fd\u662f\u5927\u4e3a\u6b22\u559c\u3002 \u4ef2\u957f\u7edf\u518d\u95ee\u4e00\u53e5\uff1a\u201c\u90a3\u4e48\u4f60\u7239\u7239\u56de\u4e0d\u56de\u4e00\u6765\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u7239\u7239\u8bf4\u4ed6\u4e5f\u5f88\u60f3\u56de\u6765\u770b\u770b\uff0c\u4e0d\u8fc7\u8981\u8fc7\u4e86\u660e\u5e74\u5143\u9704\u624d\u80fd\u4f5c\u4e2a\u51b3\u5b9a\u3002\u201d\u8fd9\u4e00\u5929 \u662f\u4e2d\u79cb\u8282\uff0c\u5230\u660e\u5e74\u5143\u9704\u4e0d\u8fc7\u4e94\u4e2a\u6708\uff0c\u5047\u82e5\u91d1\u4e16\u9057\u662f\u8fc7\u4e86\u660e\u5e74\u5143\u9704\u56de\u6765\uff0c\u5219\u5728\u534a\u5e74\u4e4b\u540e\u5c31\u53ef\u4ee5 \u548c\u4ed6\u4eec\u89c1\u9762\u4e86\u3002\u4ef2\u957f\u7edf\u7b49\u4eba\u542c\u5f97\u91d1\u4e16\u9057\u884c\u671f\u6709\u5b9a\uff0c\u89c1\u9762\u53ef\u671f\uff0c\u7686\u5927\u6b22\u559c\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5219\u662f\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u5e08\u7236\u53eb\u6211\u5728\u660e\u5e74\u7684\u5143\u9704\u8282\u5230\u897f\u5c71\u79d8\u9b54\u5d16\u53bb\u4f1a\u4e00\u4e2a\u4eba\uff0c\u4ed6 \u81ea\u5df1\u4e5f\u8981\u5230\u4e86\u90a3\u5929\u624d\u80fd\u51b3\u5b9a\u56de\u4e0d\u56de\u6765\uff0c\u8fd9\u4e24\u4ef6\u4e8b\u4e0d\u77e5\u53ef\u6709\u4ec0\u4e48\u8fde\u5e26\u7684\u5173\u7cfb\uff1f\u201d \u3000\u3000\u5e2d\u6563\u4e4b\u540e\uff0c\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u534e\u4f84\u4f60\u5e26\u5e08\u53d4\u8fdb\u540e\u5802\u6b47\u606f\uff0c\u7ed9\u5e08\u53d4\u6362\u8fc7\u8863\u88f3\u3002\u201d\u91d1\u9010\u6d41\u5411\u6c5f\u6d77 \u5929\u7b11\u9053\uff1a\u201c\u5e08\u5144\u6709\u547d\uff0c\u6211\u53ef\u4e0d\u80fd\u518d\u505a\u5c0f\u53eb\u5316\u5566\u3002\u201d\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u4f60\u5728\u6c5f\u6e56\u6e38\u620f\u98ce\u5c18\u6211\u4e0d\u53cd \u5bf9\uff0c\u4f46\u5728\u5bb6\u4e2d\u4e0e\u5bbe\u5ba2\u76f8\u5bf9\uff0c\u8fd8\u662f\u6574\u6d01\u4e9b\u513f\u7684\u597d\u3002\u201d\u91d1\u9010\u6d41\u7b11\u7740\u5e94\u4e86\u58f0\u201c\u662f\u201d\u3002\u4e8e\u662f\u968f\u53f6\u6155\u534e \u8fdb\u540e\u5802\u66f4\u8863\u3002 \u3000\u3000\u674e\u5149\u590f\u4e0e\u6797\u9053\u8f69\u4e5f\u968f\u7740\u8fdb\u53bb\u966a\u8fd9\u4f4d\u521a\u8ba4\u8bc6\u7684\u5c0f\u5e08\u53d4\u3002\u6c5f\u6d77\u5929\u95e8\u4e0b\u56db\u4e2a\u5f1f\u5b50\uff0c\u53f6\u6155\u534e\u3001\u5b87 \u6587\u96c4\u4e8c\u4eba\u5e74\u7eaa\u90fd\u6bd4\u91d1\u9010\u6d41\u5927\u4e9b\uff0c\u674e\u3001\u6797\u4e8c\u4eba\u5219\u6bd4\u91d1\u9010\u6d41\u5c0f\u4e00\u4e8c\u5c81\u3002\u4ed6\u4eec\u89c1\u5c0f\u5e08\u53d4\u6b66\u529f\u53c8\u597d\uff0c \u4eba\u53c8\u98ce\u8da3\uff0c\u66f4\u96be\u5f97\u7684\u662f\u5e74\u7eaa\u53c8\u548c\u4ed6\u4eec\u5dee\u4e0d\u591a\uff0c\u56e0\u6b64\u90fd\u5f88\u60f3\u548c\u8fd9\u5c0f\u5e08\u53d4\u4eb2\u8fd1\u4eb2\u8fd1\u3002 \u3000\u3000\u6c5f\u5bb6\u5f00\u7684\u662f\u201c\u6d41\u6c34\u5e2d\u201d\uff0c\u6c5f\u6d77\u5929\u8fd8\u8981\u5728\u5916\u9762\u62db\u5f85\u5ba2\u4eba\uff0c\u53f6\u6155\u4e2d\u5e26\u9886\u91d1\u9010\u6d41\u8fdb\u5165\u540e\u5b66\u66f4\u8863 \u4e4b\u540e\uff0c\u4e5f\u8981\u51fa\u6765\u5e2e\u5fd9\u5e08\u7236\u9001\u5ba2\uff0c\u4e8e\u662f\u5c31\u8ba9\u4ed6\u7684\u4e24\u4e2a\u5e08\u5f1f\u966a\u91d1\u9010\u6d41\u5728\u540e\u5e38\u95f2\u8bdd\u3002\u674e\u3001\u6797\u4e8c\u4eba\u7f20 \u7740\u5e08\u53d4\u8c08\u8bba\u6b66\u529f\uff0c\u8c08\u8bf4\u6d77\u5916\u98ce\u5149\uff0c\u5341\u5206\u9ad8\u5174\u3002\u91d1\u9010\u6d41\u77e5\u9053\u4ef2\u957f\u7edf\u3001\u4e00\u9633\u5b50\u7b49\u4eba\u4eca\u5929\u662f\u4e0d\u4f1a\u8d70 \u7684\uff0c\u4ed6\u4e5f\u4e0d\u559c\u6b22\u5e94\u916c\uff0c\u4e50\u5f97\u5728\u540e\u5802\u6b47\u606f\uff0c\u5f85\u5ba2\u4eba\u6563\u4e86\uff0c\u518d\u548c\u8fd9\u51e0\u4f4d\u6b66\u6797\u7684\u8001\u524d\u8f88\u53d9\u8bdd\u3002 \u3000\u3000\u65b0\u90ce\u65b0\u5a18\u656c\u5b8c\u4e86\u9152\uff0c\u7531\u803f\u79c0\u98ce\u966a\u4ed6\u4eec\u56de\u5230\u540e\u5802\uff0c\u51c6\u5907\u6b47\u606f\u534a\u4e2a\u65f6\u8fb0\uff0c\u7b49\u4e0b\u4e00\u8f6e\u9152\u5e2d\u5f00\u65f6 \u518d\u51fa\u53bb\u656c\u9152\uff0c\u6c5f\u6653\u8299\u56de\u5230\u540e\u5802\uff0c\u653e\u4e0b\u4e86\u65b0\u5a18\u7684\u77dc\u6301\uff0c\u548c\u4e24\u4f4d\u5e08\u5f1f\u53d6\u7b11\u9053\uff1a\u201c\u5c0f\u5e08\u53d4\uff0c\u5e78\u4e8f\u6709 \u4f60\u6765\u4e86\uff0c\u624d\u9017\u5f97\u4ed6\u4eec\u8fd9\u6837\u9ad8\u5174\u3002\u4f60\u4e0d\u77e5\u9053\uff0c\u4ed6\u4eec\u4eca\u5929\u4e00\u6574\u5929\u90fd\u662f\u90c1\u90c1\u4e0d\u4e50\u5462\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4f5c\u4e86\u4e00\u4e2a\u8be7\u5f02\u7684\u795e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4e48\uff1f\u55ef\uff0c\u8fd9\u5c31\u662f\u4f60\u4eec\u4e0d\u5bf9\u4e86\uff0c\u5e08\u5144\u5e08\u59d0\u5927\u559c\uff0c \u4f60\u4eec\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u5f00\u5fc3\uff1f\u201d\u803f\u79c0\u51e4\u7b11\u9053\uff1a\u201c\u5c0f\u5e08\u53d4\uff0c\u4f60\u4e0d\u77e5\u9053\u4ed6\u4eec\u6b63\u56e0\u4e3a\u5e08\u5144\u5e08\u59d0\u4eca\u65e5\u6210\u4eb2\uff0c \u4ed6\u4eec\u662f\u773c\u7ea2\u8d77\u6765\u4e86\uff0c\u4e0d\u77e5\u51e0\u65f6\u624d\u8f6e\u5230\u4ed6\u4eec\u3002\u201d\u674e\u5149\u590f\u548c\u6797\u9053\u8f69\uff0c\u6da8\u7ea2\u4e86\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5e08 \u53d4\uff0c\u4f60\u522b\u542c\u5e08\u5ac2\u548c\u5e08\u59d0\u7684\u80e1\u8bf4\u3002\u201d \u3000\u3000\u6c5f\u6653\u8299\u9053\uff1a\u201c\u4ec0\u4e48\u80e1\u8bf4\uff1f\u4f60\u4eec\u6562\u8bf4\u4e0d\u662f\u5404\u81ea\u5728\u60f3\u5ff5\u7740\u5fc3\u4e0a\u4eba\u4e48\u3002\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u54e6\uff0c \u4ed6\u4eec\u5e74\u7eaa\u8fd9\u6837\u5c0f\u5c31\u90fd\u6709\u4e86\u5fc3\u4e0a\u4eba\u4e48\u3002\u201d\u6c5f\u6653\u8299\u9053\uff1a\u201c\u4e5f\u4e0d\u5c0f\u4e86\uff0c\u4ed6\u4eec\u4e00\u4e2a\u5341\u4e5d\u5c81\uff0c\u4e00\u4e2a\u5341\u516b \u5c81\u4e86\u3002\u674e\u5e08\u5f1f\u7684\u5fc3\u4e0a\u4eba\u662f\u6b66\u5b66\u5927\u5b97\u5e08\u7afa\u5c1a\u7236\u7684\u5973\u513f\uff0c\u6797\u5e08\u5f1f\u7684\u5fc3\u4e0a\u4eba\u5219\u662f\u5929\u67f1\u5cf0\u5c71\u4e3b\u4e0a\u5b98\u6cf0 \u7684\u5973\u513f\u3002\u5e08\u7236\u7684\u610f\u601d\u672c\u6765\u60f3\u5728\u4eca\u5929\u66ff\u4ed6\u4eec\u8bf4\u5b9a\u4eb2\u4e8b\u7684\uff0c\u53ef\u60dc\u8fd9\u4e24\u5bb6\u4eba\u5230\u73b0\u5728\u90fd\u8fd8\u4e0d\u89c1\u5230 \u6765\u3002\u201d \u3000\u3000\u803f\u79c0\u51e4\u63a5\u7740\u89e3\u91ca\u9053\u3002\u201d\u7afa\u5c1a\u7236\u3001\u4e0a\u5b98\u6cf0\u8fd9\u4e24\u4f4d\u8001\u524d\u8f88\u4e0e\u5e08\u7236\u4ea4\u60c5\u6781\u539a\uff0c\u6211\u4eec\u7684\u5e16\u5b50\u662f\u65e9 \u4e24\u4e2a\u6708\u53d1\u51fa\u7684\uff0c\u6309\u8bf4\u4ed6\u4eec\u4e24\u5bb6\u7236\u5973\u4eca\u5929\u662f\u5e94\u8be5\u6765\u7684\u3002\u5e08\u7236\u5c31\u51c6\u5907\u5f85\u4ed6\u4eec\u4e00\u6765\uff0c\u4fbf\u66ff\u674e\u3001\u6797\u4e24 \u4f4d\u5e08\u5f1f\u5b9a\u5b9e\u5a5a\u4e8b\uff0c\u9526\u4e0a\u6dfb\u82b1\uff0c\u8ba9\u5bbe\u5ba2\u4eec\u66f4\u591a\u9ad8\u5174\u3002\u4ed6\u4eec\u53e3\u91cc\u4e0d\u8bf4\uff0c\u5fc3\u91cc\u5374\u5728\u7740\u6025\u5462\uff01\u5de6\u987e\u53f3 \u76fc\uff0c\u76fc\u5230\u73b0\u5728\u4ed6\u4eec\u4e24\u5bb6\u90fd\u8fd8\u6ca1\u6765\u3002\u6240\u4ee5\u4e5f\u5c31\u96be\u602a\u4ed6\u4eec\u90c1\u90c1\u4e0d\u4e50\u4e86\u3002\u201d\u674e\u5149\u590f\u548c\u6797\u9053\u8f69\u7ed9\u5979\u8bf4 \u4e2d\u5fc3\u4e8b\uff0c\u6ee1\u8138\u901a\u7ea2\uff0c\u505a\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u6c5f\u6653\u7b11\u770b\u4e86\u770b\u5929\u8272\uff0c\u7b11\u9053\uff1a\u201c\u5929\u8272\u5c06\u665a\uff0c\u4eca\u5929\u5927\u7ea6\u662f\u4e0d\u4f1a\u6765\u4e86\u3002\u4e0d\u8fc7\u4f60\u4eec\u4e5f\u7528\u4e0d\u7740\u5fc3 \u6025\uff0c\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\uff0c\u8fdf\u5230\u4e00\u5929\uff0c\u90a3\u4e5f\u662f\u5e38\u6709\u7684\u4e8b\u3002\u660e\u5929\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u6765\u7684\u3002\u201d\u674e\u3001\u6797\u4e8c\u4eba\u5fc3\u91cc \u90fd\u662f\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u65e9\u5c31\u63a5\u5230\u4e86\u5e16\u5b50\uff0c\u4ee5\u4ed6\u4eec\u548c\u5e08\u7236\u7684\u4ea4\u60c5\uff0c\u53ea\u6709\u65e9\u6765\uff0c\u54ea\u6709\u8fdf\u6765\u4e4b\u7406\uff1f\u201d\u4ed6 \u4eec\u5fc3\u6709\u6240\u7591\uff0c\u4e0d\u89c9\u5f62\u4e4b\u8f9e\u8272\u3002 \u3000\u3000\u803f\u79c0\u51e4\u7b11\u9053\uff1b\u201c\u4f60\u4eec\u5bb3\u6015\u4ec0\u4e48\uff1f\u6015\u716e\u719f\u4e86\u7684\u9e2d\u513f\u98de\u4e86\u5417\uff1f\u4f60\u4eec\u4eb2\u4e8b\u867d\u6ca1\u8bf4\u5b9a\uff0c\u59fb\u7f18\u65e9\u5df2 \u5b9a\u4e86\u3002\u7afa\u6e05\u534e\u914d\u674e\u5e08\u5f1f\uff0c\u4e0a\u5b98\u7ea8\u914d\u6797\u5e08\u5f1f\uff0c\u4e24\u5bb6\u957f\u8f88\u65e9\u5df2\u662f\u540c\u610f\u4e86\u7684\u3002\u5b9a\u4eb2\u4e0d\u8fc7\u662f\u529e\u4e00\u529e\u4eea \u5f0f\uff0c\u77e5\u4f1a\u4eb2\u53cb\u800c\u5df2\u3002\u55ef\uff0c\u4f60\u4eec\u82e5\u662f\u5fc3\u6025\uff0c\u5f85\u6211\u51fa\u53bb\u770b\u770b\u3002\u4ea4\u4ee3\u4f60\u4eec\u7684\u53f6\u5e08\u54e5\uff0c\u4ed6\u4eec\u4e00\u6765\u4fbf\u7acb \u5373\u7ed9\u4f60\u4eec\u62a5\u559c\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u53f6\u6155\u534e\u5df2\u7ecf\u8fdb\u6765\u3002\u803f\u79c0\u51e4\u7b11\u9053\uff1a\u201c\u521a\u8bf4\u8425\u64cd\uff0c\u66f9\u64cd\u4fbf\u5230\u3002\u600e\u4e48\u6837\uff0c\u4f60\u53ef \u8fce\u7740\u4e86\u8d35\u5ba2\uff1f\u201d\u53f6\u6155\u534e\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4e0a\u5b98\u524d\u8f88\u6765\u4e86\u3002\u201d\u201d\u803f\u79c0\u51e4\u5927\u559c\u9053\uff1a\u201c\u6797\u5e08\u5f1f\uff0c\u4f60\u542c\u89c1 \u4e86\u6ca1\u6709\uff1f\u8fd8\u4e0d\u5feb\u5feb\u53bb\u8fce\u63a5\u4f60\u7684\u6cf0\u5c71\uff1f\u201d\u4f46\u53f6\u6155\u534e\u5374\u6b8a\u65e0\u6b22\u559c\u7684\u795e\u8272\uff0c\u803f\u79c0\u98ce\u84e6\u7136\u4e00\u7701\uff0c\u8bf4 \u9053\uff1a\u201c\u600e\u4e48\uff0c\u53ea\u662f\u4e0a\u5b98\u524d\u8f88\u4e00\u4eba\u6765\u4e48\uff1f\u201d\u6c5f\u6653\u8299\u9053\uff1a\u201c\u7afa\u5bb6\u7236\u5973\u600e\u4e48\u4e0d\u6765\uff1f\u4e0a\u5b98\u7ea8\u5462\uff0c\u5979\u603b \u8be5\u8ddf\u5979\u7236\u4eb2\u6765\u5427\uff1f\u201d \u3000\u3000\u53f6\u6155\u534e\u9053\uff1a\u201c\u4e0d\uff0c\u4e0a\u5b98\u7ea8\u4e5f\u6ca1\u6709\u6765\uff0c\u5c31\u53ea\u662f\u5979\u7684\u7236\u4eb2\u4e0a\u5b98\u6cf0\u6765\u4e86\u3002\u4e0a\u5b98\u524d\u8f88\uff0c\u4ed6\u3001 \u4ed6\u2026\u2026\u201d\u6797\u9053\u8f69\u5fd9\u95ee\uff1a\u201c\u4ed6\u600e\u4e48\u6837\u4e86\uff1f\u201d\u53f6\u6155\u534e\u5ffd\u5730\u201c\u5618\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u966a\u4e0a\u5b98\u524d \u8f88\u6765\u4e86\u3002\u4f60\u4e0d\u7528\u51fa\u53bb\u5566\uff0c\u89c1\u4e86\u4ed6\u5c31\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u4e3b\u4eba\u966a\u4e00\u4e2a\u5bbe\u5ba2\u8fdb\u5165\u540e\u5802\uff0c\u8fd9\u662f\u5c11\u6709\u4e4b\u4e8b\u3002\u867d\u8bf4\u4e0a\u5b98\u6cf0\u7684\u5973\u513f\u4e0e\u6c5f\u6d77\u5929\u7684\u5f92\u5f1f\u5c06\u8981\u8ba2 \u4eb2\uff0c\u4ee5\u4eb2\u5bb6\u7684\u5173\u7cfb\uff0c\u53ef\u4ee5\u8ba9\u4e0a\u5b98\u6cf0\u8fdb\u5165\u540e\u5802\u3002\u4f46\u5916\u9762\u591a\u5c11\u6b66\u6797\u670b\u53cb\uff0c\u4e0a\u5b98\u6cf0\u521a\u521a\u6765\u5230\uff0c\u4e0d\u4e0e \u76f8\u8bc6\u7684\u6b66\u6797\u670b\u53cb\u5bd2\u55a7\uff0c\u7acb\u5373\u5c31\u7531\u6c5f\u6d77\u5929\u5e26\u4ed6\u8fdb\u6765\uff0c\u8fd9\u4e8b\u5374\u662f\u9887\u51fa\u60c5\u7406\u4e4b\u5916\u7684\u3002 \u3000\u3000\u6797\u9053\u8f69\u9690\u9690\u89c9\u5f97\u4e0d\u5999\uff0c\u53ea\u89c1\u4e0a\u5b98\u6cf0\u5df2\u7ecf\u968f\u7740\u5e08\u7236\u8fdb\u6765\u3002\u4e0a\u5b98\u6cf0\u9762\u5982\u9ec4\u8721\uff0c\u4f3c\u4e4e\u662f\u5927\u75c5\u4e86 \u4e00\u573a\uff0c\u8fd8\u672a\u66fe\u75ca\u6108\u7684\u6a21\u7965\u3002\u6797\u9053\u8f69\u5403\u4e86\u4e00\u60ca\uff0c\u4e0a\u524d\u95ee\u597d\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6211\u7684\u5e08\u5f1f\uff0c\u540d\u53eb\u91d1\u9010\u6d41\uff0c\u4f60\u4eec\u8fd8\u6ca1\u6709\u89c1\u8fc7\u3002\u597d\u4e86\uff0c\u73b0\u5728\u5728\u6b64\u7684\u90fd\u662f \u4e00\u5bb6\u4eba\uff0c\u4e0a\u5b98\u524d\u8f88\uff0c\u4f60\u53ef\u4ee5\u8bf4\u4e86\u3002\u4e0d\u8fc7\uff0c\u4f60\u8981\u5148\u6b47\u4e00\u6b47\u5417\uff1f\u201d\u6797\u9053\u8f69\u8fd9\u624d\u77e5\u9053\u4e0a\u5b98\u6cf0\u662f\u6709\u4ec0 \u4e48\u8981\u7d27\u7684\u4e8b\u60c5\uff0c\u8981\u544a\u8bc9\u4ed6\u7684\u5e08\u7236\uff0c\u4e0d\u60f3\u5f53\u7740\u4f17\u4eba\u6765\u8bf4\uff0c\u8fd9\u624d\u7531\u5e08\u7236\u966a\u4ed6\u5165\u540e\u5802\u7684\u3002 \u3000\u3000\u4e0a\u5b98\u6cf0\u82e6\u7b11\u9053\u3002\u201d\u6211\u6ca1\u5173\u7cfb\u3002\u6211\u4f24\u5f97\u4e0d\u91cd\uff0c\u53ea\u4e0d\u8fc7\u8fde\u65e5\u8d76\u8def\uff0c\u6240\u4ee5\u624d\u663e\u5f97\u6194\u60b4\u4e86\u4e9b\u3002\u201d \u6797\u9053\u8f69\u60ca\u9053\uff1a\u201c\u4e0a\u5b98\u8001\u4f2f\uff0c\u662f\u8c01\u6562\u4f24\u4e86\u4f60\uff1f\u201d\u4e0a\u5b98\u6cf0\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u6162\u6162\u518d\u8bf4\uff0c\u6211\u8981\u5148\u8bf4\u4e00\u8bf4 \u7afa\u5927\u54e5\u7684\u4e8b\u60c5\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u662f\u554a\uff1a\u7afa\u8001\u524d\u8f88\u662f\u4e3a\u4e86\u4f55\u4e8b\uff0c\u4e0d\u80fd\u524d\u6765\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u6709\u4e0d\u6d4b\u4e4b\u98ce\u4e91\uff0c\u4eba\u6709\u65e6\u5915\u4e4b\u7978\u798f\uff0c\u7afa\u5927\u54e5\uff0c\u4ed6\u3001\u4ed6\u662f\u906d\u4e86 \u4e0d\u6d4b\u4e4b\u7978\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u8fde\u6c5f\u6d77\u5929\u4e5f\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u7afa\u8001\u7684\u8f88\u906d\u4e86\u4ec0\u4e48\u4e0d\u6d4b\u4e4b\u7978\uff1f\u201d \u3000\u3000\u4e0a\u5bab\u6cf0\u9053\uff1a\u201c\u906d\u4eba\u6697\u7b97\uff0c\u53d7\u4e86\u91cd\u4f24\u3002\u201d\u6c5f\u6d77\u5929\u542c\u8bf4\u7afa\u5c1a\u7236\u8fd8\u662f\u6d3b\u7740\uff0c\u65b9\u59cb\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684 \u4e00\u5757\u77f3\u5934\uff0c\u4f46\u4ecd\u662f\u60ca\u9a87\u4e0d\u5df2\u3002 \u3000\u3000\u8981\u77e5\u7afa\u5c1a\u7236\u4e43\u662f\u5f53\u4e16\u7684\u6b66\u5b66\u5b97\u5e08\uff0c\u6b66\u6797\u516c\u8ba4\u4e3a\u4ec5\u6b21\u4e8e\u6c5f\u6d77\u5929\u7684\u5929\u4e0b\u7b2c\u4e8c\u9ad8\u624b\uff0c\u90a3\u4eba\u5373\u4f7f \u662f\u5077\u65bd\u6697\u7b97\uff0c\u4f46\u80fd\u591f\u4f24\u5f97\u4e86\u4ed6\uff0c\u8fd9\u4e5f\u662f\u6781\u4e0d\u5bb9\u6613\u7684\u4e86\uff0c\u6c5f\u6d77\u5929\u514d\u4e0d\u4e86\u8981\u95ee\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff0c\u7528 \u4ec0\u4e48\u65b9\u6cd5\u4f24\u4e86\u4ed6\u7684\uff1f\u201d\u5728\u6c5f\u6d77\u5929\u7684\u5fc3\u91cc\u4ee5\u4e3a\u8fd9\u4e2a\u4eba\u5fc5\u5b9a\u662f\u6b66\u6797\u4e2d\u8457\u540d\u7684\u9b54\u5934\uff0c\u5c82\u77e5\u4e0a\u5b98\u6cf0\u8bf4 \u4e86\u51fa\u6765\uff0c\u7adf\u5927\u51fa\u4ed6\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u4e0a\u5b98\u6cf0\u9053\uff1a\u201c\u7afa\u5927\u54e5\u5360\u4e86\u897f\u661f\u4e4b\u540e\uff0c\u56db\u65b9\u8c6a\u6770\uff0c\u6295\u5954\u4ed6\u7684\u5f88\u591a\uff0c\u6709\u4e00\u5929\u6765\u4e86\u4e00\u4e2a\u6c49\u5b50\uff0c \u81ea\u79f0\u662f\u51c9\u5dde\u4eba\u6c0f\uff0c\u59d3\u540d\u53eb\u505a\u5e05\u5b5f\u96c4\uff0c\u7ea6\u6709\u56db\u5341\u591a\u5c81\u5e74\u7eaa\u3002\u55ef\uff0c\u6c5f\u5927\u4fa0\uff0c\u8fd9\u4e2a\u4eba\u7684\u540d\u5b57\u4f60\u53ef\u542c \u8fc7\u4e48\u3002\u201d\u6c5f\u6d77\u5929\u76b1\u76b1\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4ece\u672a\u542c\u8fc7\u3002\u7afa\u8001\u524d\u8f88\u5c31\u662f\u53d7\u4ed6\u6240\u4f24\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u6765\u4e86\u4e4b\u540e\uff0c\u548c\u7afa\u5927\u54e5\u8c08\u8bba\u6b66\u529f\u751a\u662f\u76f8\u5f97\u3002\u4f46\u4ed6\u5374\u6ca1\u6709\u53c2 \u52a0\u4e49\u519b\uff0c\u53ea\u662f\u4ee5\u670b\u53cb\u7684\u8eab\u4efd\u548c\u7afa\u5927\u54e5\u5f80\u6765\uff0c\u5927\u7ea6\u6bcf\u4e2a\u6708\u603b\u8981\u6765\u4e00\u6b21\u3002\u201d\u6c5f\u6d77\u5929\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u7afa\u5c1a\u7236\u4e5f\u672a\u514d\u592a\u8fc7\u7c97\u5fc3\u5927\u610f\uff0c\u600e\u80fd\u8ba9\u4e00\u4e2a\u4e0d\u77e5\u6765\u5386\u7684\u4eba\u51fa\u5165\u519b\u4e2d\u3002\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u7afa\u5927\u54e5\u548c\u4ed6\u5f80\u6765\u4e86\u76f8\u8fd1\u4e24\u5e74\uff0c\u4ece\u672a\u5bf9\u4ed6\u8d77\u8fc7\u7591\u5fc3\u3002\u8fd9\u4eba\u88c5\u4f5c\u4e2a\u6027 \u559c\u6b66\u5b66\u7684\u9690\u58eb\uff0c\u4e00\u6765\u5c31\u4e0e\u7afa\u5927\u54e5\u8c08\u8bba\u6b66\u5b66\u4e0a\u7684\u79cd\u79cd\u7591\u96be\u95ee\u9898\uff0c\u4ece\u4e0d\u6d89\u53ca\u5916\u4e8b\u3002\u7afa\u5927\u54e5\u4e5f\u5f88\u63a8 \u91cd\u6c60\uff0c\u8ba4\u4e3a\u4ed6\u7684\u6b66\u5b66\u9020\u8be3\uff0c\u53ef\u4ee5\u5217\u5728\u5929\u4e0b\u4e94\u540d\u4e4b\u5185\u3002\u6b63\u56e0\u4e3a\u4ed6\u4eec\u5f80\u6765\u4e24\u5e74\uff0c\u4ece\u6ca1\u51fa\u8fc7\u4e8b\uff0c\u6240 \u4ee5\u7afa\u5927\u54e5\u624d\u4f1a\u53d7\u4e86\u4ed6\u7684\u6697\u7b97\uff0c\u4e1d\u6beb\u4e5f\u6ca1\u6709\u9632\u5907\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4ed6\u662f\u600e\u6837\u6697\u7b97\u4e86\u7afa\u8001\u524d\u8f88 \u7684\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u9053\uff1a\u201c\u51fa\u4e8b\u90a3\u5929\uff0c\u6211\u4eec\u7236\u5973\u6b63\u5728\u897f\u661f\u3002\u90a3\u4e00\u5929\u4e5f\u5c31\u662f\u6070\u5de7\u63a5\u5230\u4e86\u4f60\u7684\u8bf7\u5e16\u7684\u90a3\u4e00 \u5929\uff0c\u7afa\u5927\u54e5\u548c\u8fd9\u4e2a\u5e05\u76c2\u96c4\u53c8\u5728\u8c08\u8bba\u6b66\u529f\uff0c\u5e05\u5b5f\u96c4\u9053\uff1a\u2018\u4eba\u4eba\u90fd\u8bf4\u6c5f\u5927\u4fa0\u7684\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\uff0c\u4f60 \u53ef\u66fe\u548c\u4ed6\u4ea4\u8fc7\u624b\u4e48\uff1f\u2019\u7afa\u5927\u54e5\u9053\u2018\u6ca1\u6709\u6b63\u5f0f\u4ea4\u8fc7\u624b\uff0c\u4f46\u4e5f\u66fe\u8bd5\u8fc7\u4e00\u62db\u3002\u90a3\u6b21\u6211\u5728\u9093\u5c71\u548c\u5c11\u6797 \u5bfa\u7684\u65b9\u4e08\u5927\u60b2\u7985\u5e08\u8f83\u91cf\u5185\u529f\uff0c\u4ed6\u628a\u6211\u4eec\u5206\u5f00\uff0c\u6211\u4eec\u4e24\u4eba\u7684\u638c\u529b\u90fd\u6253\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u4ed6\u4e5f\u7981\u53d7\u5f97 \u8d77\u3002\u4f9d\u6b64\u770b\u6765\uff0c\u4ed6\u7684\u6b66\u529f\u81f3\u5c11\u6bd4\u6211\u9ad8\u660e\u4e00\u500d\u3002\u2019\u5e05\u5b5f\u96c4\u53f9\u9053\uff1a\u2018\u5509\uff0c\u5929\u4e0b\u7adf\u6709\u5982\u6b64\u9ad8\u4eba\uff0c\u53ef \u60dc\u6211\u5c1a\u65e0\u7f18\u4e0e\u4ed6\u76f8\u4f1a\uff01\u2019\u7afa\u5927\u54e5\u7b11\u9053\uff1a\u2018\u6c5f\u5927\u9655\u6df1\u85cf\u82e5\u865a\uff0c\u4f60\u5c31\u662f\u548c\u4ed6\u89c1\u4e86\u9762\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u4f1a \u5c31\u4e0e\u4f60\u6bd4\u8bd5\u6b66\u529f\u7684\u3002\u2019\u5e05\u5b5f\u96c4\u5ffd\u9053\uff1a\u2018\u54b1\u4eec\u8c08\u8bba\u6b66\u529f\u4e5f\u8c08\u5f97\u591a\u4e86\uff0c\u5374\u4ece\u672a\u4e92\u76f8\u5370\u8bc1\uff0c\u4eca\u65e5\u8bd5 \u8bd5\u5982\u4f55\uff1f\u6211\u65e0\u7f18\u4e0e\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\u5370\u8bc1\uff0c\u5411\u5929\u4e0b\u7b2c\u4e8c\u9ad8\u624b\u8bf7\u6559\uff0c\u4e5f\u53ef\u4e86\u4e00\u6869\u5fc3\u613f\u3002\u201d \u3000\u3000\u6b66\u6797\u4e2d\u7684\u597d\u670b\u53cb\u5207\u78cb\u6b66\u529f\u79f0\u4e3a\u201c\u5370\u8bc1\u201d\uff0c\u8fd9\u662f\u4e0e\u666e\u901a\u7684\u201c\u8f83\u91cf\u201d\u4e0d\u540c\u7684\u3002\u201c\u8f83\u91cf\u201d\u8981\u5206 \u51fa\u80dc\u8d1f\uff0c\u201c\u5370\u8bc1\u201d\u5219\u4e0d\u4e00\u5b9a\u8981\u5206\u51fa\u80dc\u8d1f\u3002\u201c\u8f83\u91cf\u201d\u53ef\u4ee5\u542b\u6709\u654c\u610f\uff1b\u201c\u5370\u8bc1\u201d\u5219\u7eaf\u5c5e\u53cb\u8c0a\u7684\u5207 \u78cb\u3002 \u3000\u3000\u6b66\u6797\u4e2d\u7684\u9ad8\u624b\u7edd\u4e0d\u8f7b\u6613\u4e0e\u4eba\u8f83\u91cf\uff0c\u5c31\u662f\u201c\u5370\u8bc1\u2019\u6b66\u529f\u4e5f\u662f\u5c11\u6709\u7684\u4e8b\u3002\u6c5f\u6d77\u5929\u5fc3\u9053\uff1a\u201c\u7afa \u5c1a\u7236\u82e5\u4e0d\u662f\u628a\u4ed6\u5f53\u4f5c\u77e5\u5df1\uff0c\u51b3\u4e0d\u80af\u4e0e\u4ed6\u5370\u8bc1\u6b66\u529f\u3002\u8fd9\u4eba\u82b1\u4e86\u4e24\u5e74\u5de5\u592b\uff0c\u9a97\u5f97\u7afa\u5c1a\u7236\u628a\u4ed6\u5f53\u4f5c \u77e5\u5df1\uff0c\u4e5f\u771f\u53ef\u4ee5\u7b97\u5f97\u662f\u5904\u5fc3\u79ef\u8651\u4e86\u3002\u201d \u3000\u3000\u679c\u7136\u4e0a\u5b98\u6cf0\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u7afa\u5927\u54e5\u5bf9\u4ed6\u4ece\u6ca1\u731c\u7591\uff0c\u90a3\u4e00\u5929\u7afa\u5927\u54e5\u7684\u5174\u8da3\u53c8\u5f88\u597d\uff0c\u4e8e\u662f\u5f88 \u723d\u5feb\u7684\u5c31\u7b54\u5e94\u4e86\u4ed6\u3002\u4e24\u4eba\u5728\u9662\u5b50\u7b14\u4ea4\u624b\uff0c\u90a3\u4eba\u65f6\u62db\u6570\u5f88\u662f\u53e4\u602a\uff0c\u6211\u770b\u4e0d\u51fa\u4ed6\u662f\u4ec0\u4e48\u95e8\u6d3e\u3002\u4ea4 \u624b\u7ea6\u6709\u4e09\u5341\u6765\u62db\uff0c\u7afa\u5927\u54e5\u4f7f\u4e86\u4e00\u62db\u2018\u9634\u9633\u53cc\u649e\u62f3\u2019\u5c06\u4ed6\u7684\u2018\u622a\u624b\u6cd5\u2019\u5c01\u4f4f\uff0c\u7b11\u9053\uff1a\u2018\u5929\u4e0b\u7b2c \u4e8c\u9ad8\u624b\u6211\u4e0d\u6562\u5f53\uff0c\u53ea\u662f\u6bd4\u4f60\u5927\u4e86\u51e0\u5c81\u5e74\u7eaa\uff0c\u529f\u529b\u7a0d\u9ad8\u800c\u5df2\u3002\u2019\u5e05\u5b5f\u96c4\u9053\uff1a\u2018\u4e0d\u9519\uff0c\u6211\u662f\u7518\u62dc \u4e0b\u98ce\u4e86\uff01\u7afa\u5927\u54e5\u54c8\u54c8\u4e00\u7b11\uff0c\u5c06\u638c\u529b\u5f90\u5f90\u6536\u56de\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u2018\u4f60\u7684\u62db\u6570\u5f88\u662f\u7cbe\u5999\uff0c\u53ef\u60dc\u672a\u80fd\u66f2 \u5c3d\u5176\u53d8\uff0c\u5426\u5219\u6211\u5c31\u7834\u89e3\u4e0d\u4e86\u3002\u2019\u5c31\u5728\u6b64\u65f6\uff0c\u5e05\u5b5f\u96c4\u5ffd\u9053\uff1a\u2018\u662f\u4e48\uff1f\u6211\u8fd9\u62db\u8fd8\u6709\u53d8\u5316\u7684\uff01\u7a81\u7136 \u2018\u4e52\u2019\u7684\u4e00\u638c\uff0c\u628a\u7afa\u5927\u54e5\u6253\u7ffb\uff01\u201d \u3000\u3000\u674e\u5149\u590d\u8be7\u9053\uff1a\u201c\u7afa\u4f2f\u4f2f\u7684\u529f\u529b\u9ad8\u8fc7\u4ed6\uff0c\u5374\u600e\u7684\u4f1a\u7ed9\u4ed6\u6253\u7ffb\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u529f\u529b\u5927\u81f4\u76f8\u5f53\u7684\u9ad8\u624b\u7f62\u6597\uff0c\u5fc5\u987b\u628a\u5185\u529b\u5f90\u5f90\u6536\u56de\uff0c\u624d\u4e0d\u81f3\u4e8e\u4f24\u4e86\u81ea\u8eab\u3002\u7afa \u8001\u524d\u8f88\u5927\u7ea6\u662f\u56e0\u4e3a\u542c\u4e86\u90a3\u53e5\u5df2\u8bf4\u51fa\u4e86\u2018\u7518\u62dc\u4e0b\u98ce\u2019\u7684\u8bf4\u8bdd\uff0c\u6240\u4ee5\u6beb\u65e0\u9632\u5907\u3002\u800c\u90a3\u53ae\u5374\u7a81\u7136\u628a \u5185\u529b\u5c3d\u53d1\uff0c\u5728\u4e00\u6536\u4e00\u53d1\u4e4b\u95f4\uff0c\u5c31\u50cf\u540e\u6d6a\u63a8\u8feb\u524d\u6d6a\u4e00\u6837\uff0c\u90a3\u53ae\u7684\u5185\u529f\u52a0\u4e0a\u7afa\u8001\u524d\u8f88\u7684\u5185\u529b\uff0c\u90fd \u6253\u5230\u4e86\u7afa\u8001\u524d\u8f88\u8eab\u4e0a\uff0c\u7109\u80fd\u4e0d\u53d7\u91cd\u4f24\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u8bf4\u9053\uff1a\u201c\u6b63\u662f\u8fd9\u6837\u3002\u5f53\u65f6\u7afa\u5927\u54e5\u5927\u543c\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u2018\u4f60\uff0c\u4f60\u597d\u3002\u2019\u7ad9\u8d77\u6765\u53d1\u51fa\u4e00 \u8bb0\u5288\u7a7a\u638c\uff0c\u5e05\u5b5f\u96c4\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5f53\u7136\u597d\uff0c\u4f60\uff0c\u53ef\u662f\u597d\u4e0d\u4e86\u4e86\uff01\u4f60\u82e5\u662f\u60f3\u6b7b\u5f97\u5feb\u4e9b\uff0c\u5c3d\u53ef\u548c\u6211 \u518d\u6597\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u53f9\u9053\uff1a\u2018\u7afa\u8001\u524d\u8f88\u4e5f\u592a\u8fc7\u5fc3\u6025\u62a5\u4ec7\uff0c\u6b64\u65f6\uff0c\u65e0\u5b9c\u518d\u8fd0\u5185\u529b\uff1f\u53ea\u6015\u8981\u4f24\u4e0a\u52a0\u4f24 \u4e86\uff01\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u731c\u5f97\u4e00\u70b9\u4e0d\u9519\uff0c\u7afa\u5927\u54e5\u53d1\u4e86\u4e00\u638c\uff0c\u8eab\u5f62\u6643\u4e86\u4e24\u6643\uff0c\u53c8\u5012\u4e0b\u53bb\u4e86\u3002\u53ef \u662f\u90a3\u5e05\u5b5f\u96c4\u53d7\u4e86\u4ed6\u8fd9\u8bb0\u5288\u7a7a\u638c\uff0c\u4e5f\u662f\u7981\u4e0d\u4f4f\u4e00\u4e2a\u8e09\u8dc4\uff0c\u9669\u4e9b\u8dcc\u5012\u3002\u6211\u8fde\u5fd9\u8ffd\u4e0a\u53bb\uff0c\u5f85\u8981\u64d2\u4ed6 \u4f4f\u4e86\uff0c\u60ed\u6127\uff0c\u60ed\u6127\u2026\u2026\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u7684\u529f\u529b\u6bd4\u7afa\u5c1a\u7236\u76f8\u5dee\u4e0d\u6b62\u4e00\u7b79\uff0c\u6c5f\u6d77\u5929\u4e0d\u5fc5\u95ee\u4ed6\u5df2\u77e5\u4ed6\u4eec\u4ea4\u624b\u7684\u7ed3\u679c\uff0c\u8bf4\u9053\uff1a \u201c\u80dc\u8d1f\u5175\u5bb6\u5e38\u4e8b\uff0c\u6b66\u6797\u4e2d\u7684\u9ad8\u624b\u4e5f\u4ece\u65e0\u4e00\u4eba\u80fd\u591f\u4fdd\u6301\u4e0d\u8d25\u7684\uff0c\u4f55\u8db3\u4ecb\u6000\u3002\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u60ed\u6127\u7684\u662f\u8fd9\u53ae\u5df2\u53d7\u4e86\u7afa\u5927\u54e5\u7684\u4e00\u8bb0\u5288\u7a7a\u638c\uff0c\u6211\u5374\u8fd8\u662f\u654c\u4ed6\u4e0d\u8fc7\u3002 \u4f46\u4e5f\u5e78\u4e8f\u6709\u7afa\u5927\u54e5\u53d1\u4e86\u8fd9\u4e00\u8bb0\u5288\u7a7a\u638c\uff0c\u8017\u635f\u4e86\u4ed6\u7684\u51e0\u5206\u5185\u529b\uff0c\u6211\u624d\u4e0d\u81f3\u4e8e\u53d7\u4e86\u91cd\u4f24\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u548c\u4ed6\u4ea4\u4e86\u4e00\u638c\uff0c\u53ef\u6478\u5230\u4e86\u4ed6\u7684\u6b66\u529f\u662f\u4ec0\u4e48\u95e8\u8def\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u9053\uff1a\u201c\u53cc\u638c\u76f8\u4ea4\u4e4b\u9645\uff0c\u6211\u53ea\u89c9\u5f97\u4ed6\u7684\u638c\u5fc3\u5982\u540c\u70e7\u7ea2\u4e86\u7684\u94c1\u5757\u4e00\u822c\uff0c\u6bd4\u6b27\u9633\u4f2f\u548c\u7684 \u96f7\u795e\u638c\u4f3c\u4e4e\u8fd8\u8981\u5389\u5bb3\uff0c\u5374\u4e0d\u77e5\u4ed6\u662f\u4ec0\u4e48\u8def\u9053\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\u3002\u201d\u80fd\u4ee5\u70ed\u6bd2\u7684\u638c\u529b\u4f24\u4eba\uff0c\u6bd4\u96f7\u795e\u638c\u66f4\u5389\u5bb3\u7684\u53ea\u6709\u524d\u8f88\u9b54\u5934\u8d64\u795e\u5b50\u7684\u8fd9\u6d3e\u6b66 \u529f\u3002\u770b\u6765\u8fd9\u53ae\u6050\u6015\u662f\u8d64\u795e\u5b50\u7684\u8863\u94b5\u4f20\u4eba\u3002\u201d \u3000\u3000\u8d64\u795e\u5b50\u662f\u548c\u91d1\u4e16\u9057\u540c\u65f6\u7684\u90aa\u6d3e\u6559\u4eba\uff0c\u5e74\u7eaa\u5219\u6bd4\u91d1\u4e16\u9057\u957f\u8bb8\u591a\uff0c\u4e09\u5341\u5e74\u524d\u4ed6\u5230\u73e0\u7a46\u6717\u739b\u5cf0 \u627e\u4e00\u79cd\u836f\u8349\uff0c\u53d7\u4e0d\u4e86\u9ad8\u5c71\u4e25\u5bd2\uff0c\u6b7b\u5728\u559c\u739b\u62c9\u96c5\u5c71\u4e0a\u7684\u51b0\u6cb3\u4e4b\u4e2d\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6682\u4e14\u4e0d\u5fc5\u7ba1\u4ed6\u662f\u8c01\uff0c\u7afa\u8001\u524d\u8f88\u7684\u4f24\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u9053\uff1a\u201c\u7afa\u5927\u54e5\u4f24\u5f97\u5f88\u91cd\uff0c\u4f46\u597d\u5728\u4ed6\u7684\u5185\u529f\u6df1\u539a\uff0c\u8fd8\u4e0d\u81f3\u4e8e\u6709\u6027\u547d\u4e4b\u5fe7\uff0c\u4e0d\u8fc7\uff0c\u4e5f \u53ea\u80fd\u5367\u5e8a\uff0c\u4e0d\u80fd\u884c\u52a8\u4e86\u3002\u201d \u3000\u3000\u674e\u5149\u590f\u9053\uff1a\u201c\u59d3\u5e05\u8fd9\u53ae\u4e3a\u4ec0\u4e48\u65e0\u7f18\u65e0\u6545\u7684\u4f24\u4e86\u7afa\u4f2f\u4f2f\uff1f\u67e5\u51fa\u4e86\u4ed6\u7684\u8eab\u4efd\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u9053\uff1a\u201c\u8d77\u521d\u6211\u4eec\u90fd\u4e0d\u660e\u767d\uff0c\u4f46\u8fc7\u4e86\u51e0\u5929\u5c31\u660e\u767d\u4e86\u3002\u8fd9\u53ae\u5904\u5fc3\u79ef\u8651\u6765\u4f24\u5bb3\u7afa\u5927\u54e5\uff0c \u5185\u4e2d\u5b9e\u6709\u4e00\u5927\u9634\u8c0b\uff0c\u5e76\u975e\u53ea\u662f\u4e3a\u4e86\u79c1\u4eba\u4ec7\u6028\u7684\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u540e\u6765\u53c8\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u4e0a\u5bab\u6cf0\u9053\uff1a\u201c\u6b64\u4e8b\u4e09\u5929\u4e4b\u540e\uff0c\u6e05\u5175\u4fbf\u5927\u4e3e\u6765\u653b\uff0c\u7afa\u5927\u54e5\u5367\u75c5\u5728\u5e8a\uff0c\u519b\u5fc3\u5927\u53d7\u5f71\u54cd\u3002\u7ed3\u679c \u662f\u897f\u661f\u53c8\u7ed9\u6e05\u5175\u635e\u56de\u53bb\u4e86\uff0c\u7afa\u5927\u54e5\u8fd9\u652f\u4e49\u519b\u4f24\u4ea1\u4e0d\u5c11\uff0c\u5982\u4eca\u5df2\u9063\u5165\u4e86\u5927\u51c9\u5c71\u4e2d\uff0c\u6050\u6015\u4e0d\u662f\u77ed \u671f\u95f4\u5185\u53ef\u80fd\u590d\u8d77\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0a\u5b98\u524d\u8f88\uff0c\u4f60\u867d\u7136\u6ca1\u6709\u53d7\u4f24\uff0c\u4f46\u8fde\u65e5\u5954\u6ce2\uff0c\u8eab\u4f53\u662f\u5426\u4e5f\u6709\u70b9\u611f\u5230\u4e0d\u5927\u8212 \u9002\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u662f\u5c06\u4ed6\u5f53\u4f5c\u81ea\u5df1\u4eba\u770b\u5f85\uff0c\u6240\u4ee5\u5f88\u5766\u7387\u5730\u95ee\u4ed6\u3002\u4e0a\u5b98\u6cf0\u82e6\u7b11\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u6cd5\u773c\u65e0 \u5dee\uff0c\u8fd9\u53ae\u7684\u638c\u529b\u59d4\u5b9e\u5389\u5bb3\uff0c\u662f\u5728\u6211\u7684\u8eab\u4e0a\u7559\u4e0b\u540e\u60a3\u4e86\u3002\u6211\u56e0\u4e3a\u8981\u8d76\u6765\u62a5\u8baf\uff0c\u66fe\u7528\u5185\u529f\u5c06\u70ed\u6bd2 \u903c\u51fa\u4f53\u5916\uff0c\u4e0d\u6599\u4f59\u6bd2\u672a\u80fd\u6e05\u9664\uff0c\u51dd\u7ed3\u5728\u819d\u5934\u76d6\u7684\u5730\u65b9\uff0c\u7531\u4e8e\u8fde\u65e5\u8d76\u8def\uff0c\u819d\u76d6\u7684\u9aa8\u5934\u6709\u51e0\u5904\u7834 \u88c2\u4e86\u3002\u4e0d\u8fc7\u6211\u7684\u4f24\u4e0d\u7b97\u8981\u7d27\uff0c\u81f3\u591a\u4e00\u8db3\u6b8b\u5e9f\u800c\u5df2\u3002\u7afa\u5927\u54e5\u7684\u5185\u4f24\u5374\u975e\u4ece\u901f\u7597\u6cbb\u4e0d\u53ef\uff0c\u5426\u5219\u6050 \u6015\u4ed6\u7684\u8fd9\u4e00\u8eab\u6b66\u529f\u4f1a\u56e0\u6b64\u5e9f\u4e86\u3002\u6c5f\u5927\u4fa0\uff0c\u4f60\u548c\u5c11\u6797\u5bfa\u6709\u6df1\u539a\u7684\u4ea4\u60c5\uff0c\u7afa\u5927\u54e5\u548c\u5c11\u6797\u5bfa\u7684\u65b9\u4e08 \u4e5f\u66fe\u6709\u6743\u5c71\u6bd4\u6b66\u4e4b\u96c5\uff0c\u4f60\u53ef\u5426\u66ff\u7afa\u5927\u54e5\u5411\u5927\u60b2\u7985\u5e08\u6c42\u4e24\u9897\u5c0f\u8fd8\u4e39\uff0c\u5c0f\u8fd8\u4e39\u4e43\u662f\u7597\u6cbb\u5185\u4f24\u7684\u65e0 \u53cc\u5723\u836f\uff0c\u5018\u6709\u5c0f\u8fd8\u4e39\u914d\u5408\u4e0a\u7afa\u5c1a\u7236\u7684\u5185\u529f\uff0c\u4e00\u5b9a\u53ef\u4ee5\u836f\u5230\u56de\u53bb\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u4e2a\u5bb9\u6613\uff0c\u4e0d\u8fc7\u6050\u6015\u8981\u82b1\u4e9b\u65f6\u65e5\u7f62\u4e86\u3002\u5012\u662f\u4f60\u7684\u4f24\u867d\u4e0d\u4e25\u91cd\uff0c\u5bf9\u75c7\u7684\u836f\u4e00 \u65f6\u95f4\u5374\u96be\u4ee5\u627e\u5bfb\u3002\u6211\u6709\u5929\u5c71\u96ea\u83b2\u70ae\u5236\u7684\u78a7\u7075\u4e39\u53ef\u4ee5\u7ed9\u4f60\u6e05\u9664\u4f59\u6bd2\uff0c\u4f46\u8fd8\u8981\u6709\u7eed\u7b4b\u9a73\u9aa8\u7684\u826f \u836f\uff0c\u624d\u80fd\u591f\u5b8c\u5168\u533b\u597d\u3002\u4f60\u5728\u6211\u5bb6\u91cc\u4f4f\u4e2a\u5341\u5929\u534a\u6708\u5427\uff0c\u6211\u60f3\u529e\u6cd5\u66ff\u4f60\u5bfb\u627e\u3002\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u76b1\u4e86\u7709\u5934\u9053\uff1a\u201c\u6050\u6015\u4e0d\u80fd\u803d\u6401\u8fd9\u8bb8\u591a\u65f6\u5019\u4e86\u3002\u6211\u4e0d\u7d27\u8981\uff0c\u6015\u662f\u7afa\u5927\u54e5\u7684\u75c5\u60c5\u4f1a\u6709 \u53d8\u5316\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u529e\uff1f\u6211\u5230\u5c11\u6797\u5bfa\u6253\u4e2a\u6765\u56de\uff0c\u81f3\u5c11\u4e5f\u5f97\u5341\u5929\u534a\u6708\u3002\u201d \u3000\u3000\u5168\u9010\u6d41\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u5e08\u5144\u4e0d\u5fc5\u7740\u6025\uff0c\u4e0a\u5b98\u524d\u8f88\u6240\u9700\u7684\u4e24\u79cd\u836f\u7269\u3002\u6211\u7684\u8eab\u4e0a\u5c31\u6709\u3002\u201d\u4e0a\u5b98 \u6cf0\u559c\u51fa\u671b\u5916\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u6709\u5417\uff1f\u201d\u8bed\u6c14\u4e4b\u4e2d\uff0c\u8fd8\u662f\u5e26\u70b9\u534a\u4fe1\u534a\u7591\u7684\u6837\u5b50\u3002\u539f\u6765\u4ed6\u89c1\u91d1\u9010\u6d41 \u5e74\u7eaa\u592a\u8f7b\uff0c\u672a\u6562\u5341\u5206\u76f8\u4fe1\u4ed6\u7684\u8bdd\u3002 \u3000\u3000\u5168\u9010\u6d41\u62ff\u51fa\u4e00\u4e2a\u76d2\u5b50\uff0c\u6253\u4e86\u5f00\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e09\u9897\u5c0f\u8fd8\u4e39\u662f\u59ec\u4f2f\u4f2f\u7ed9\u6211\u7684\u3002\u201d\u4e0a\u5b98\u6cf0\u5403 \u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u8fd9\u4f4d\u59ec\u4f2f\u4f2f\u662f\u4e09\u5341\u5e74\u524d\u540d\u95fb\u5929\u4e0b\u7684\u90a3\u4f4d\u795e\u5077\u59ec\u6653\u98ce\u5417\uff1f\u201d\u6c5f\u6d77\u5929\u4ee3\u7b54 \u9053\uff1a\u201c\u6b63\u662f\u6b64\u4eba\u3002\u5bb6\u5e08\u548c\u59ec\u8001\u524d\u8f88\u540c\u4f4f\u5728\u4ee5\u524d\u4e54\u5317\u6e9f\u4f4f\u8fc7\u7684\u706b\u5c71\u5c9b\u4e0a\uff0c\u5e08\u5f1f\u662f\u6700\u8fd1\u624d\u5949\u4e86\u5bb6 \u5e08\u4e4b\u547d\u56de\u6765\u7684\u3002\u201d\u4e0a\u5b98\u6cf0\u8fd9\u624d\u77e5\u9053\u91d1\u9010\u6d41\u662f\u91d1\u4e16\u9057\u7684\u513f\u5b50\uff0c\u81ea\u7136\u662f\u5bf9\u4ed6\u53e6\u773c\u76f8\u770b\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\u3002\u201d\u8fd9\u662f\u59ec\u4f2f\u4f2f\u4e09\u5341\u5e74\u7684\u4ece\u5c11\u6797\u5bfa\u5077\u6765\u7684\uff0c\u4e0d\u77e5\u8fd8\u80fd\u4e0d\u80fd\u7528\uff1f\u201d\u4e0a\u5b98\u6cf0\u9053\uff1a \u201c\u5c0f\u8fd8\u4e39\u662f\u4e0d\u4f1a\u53d8\u574f\u7684\uff0c\u4e00\u767e\u5e74\u4e5f\u8fd8\u80fd\u7528\u3002\u7afa\u5927\u54e5\u4f24\u52bf\u867d\u91cd\uff0c\u6709\u4e24\u9897\u4e5f\u8db3\u591f\u4e86\u3002\u4f60\u81ea\u5df1\u7559\u4e0b \u4e00\u9897\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u63a5\u7740\u53d6\u51fa\u4e00\u53ea\u5c0f\u5c0f\u7f8a\u8102\u767d\u7389\u74f6\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u521a\u521a\u4ece\u6587\u9053\u5e84\u8eab\u4e0a\u5077\u6765\u7684\uff0c\u91cc\u9762 \u88c5\u7684\u662f\u7eed\u65ad\u818f\u3002\u6211\u591a\u7239\u8bf4\u6587\u5bb6\u7684\u7eed\u65ad\u818f\u7528\u6765\u7eed\u7b4b\u9a73\u9aa8\uff0c\u529f\u6548\u53ef\u4ee5\u53ca\u5f97\u4e0a\u5343\u5e74\u7eed\u65ad\u3002\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u9053\uff1a\u201c\u91d1\u5c11\u4fa0\uff0c\u591a\u8c22\u4f60\u8d60\u836f\u4e4b\u5fb7\uff0c\u6211\u4e0d\u77e5\u8be5\u5982\u4f55\u62a5\u7b54\u4f60\u624d\u597d\u3002\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1b \u201c\u591a\u8c22\u4ec0\u4e48\uff0c\u53cd\u6b63\u90fd\u662f\u5077\u6765\u7684\u3002\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u9053\uff1a\u201c\u597d\uff0c\u96be\u5f97\u91d1\u5c11\u4fa0\u5982\u6b64\u8c6a\u723d\uff0c\u90a3\u6211\u4e5f\u4e0d\u5ba2\u6c14\u4e86\u3002\u51e0\u65f6\u4f60\u5230\u5927\u51c9\u5c71\u6765\uff0c\u5bb9\u6211\u4e0e \u7afa\u5927\u54e5\u548c\u5c3d\u5730\u4e3b\u4e4b\u8c0a\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u662f\u8981\u53bb\u62dc\u89c1\u7afa\u8001\u524d\u8f88\u7684\uff0c\u4e0d\u8fc7\u6050\u6015\u5f97\u5728\u534a\u5e74\u4e4b \u540e\u3002\u201d\u7afa\u5c1a\u7236\u548c\u4e0a\u5b98\u6cf0\u90fd\u662f\u8bb2\u7a76\u6069\u6028\u5206\u660e\u7684\u4eba\u7269\uff0c\u5c24\u5176\u662f\u7afa\u5c1a\u7236\uff0c\u4ee5\u6b66\u5b66\u5b97\u5e08\u7684\u8eab\u4efd\uff0c\u66f4\u4e0d \u80af\u8f7b\u6613\u63a5\u53d7\u522b\u4eba\u7684\u6069\u60e0\uff0c\u6240\u4ee5\u540e\u6765\u91d1\u9010\u6d41\u5f88\u5f97\u4e86\u4ed6\u7684\u597d\u5904\uff0c\u8fd9\u662f\u540e\u8bdd\u3002 \u3000\u3000\u4e0a\u5b98\u6cf0\u8c22\u8fc7\u4e86\u91d1\u9010\u6d41\uff0c\u63a5\u7740\u5bf9\u6c5f\u6d77\u5929\u8bf4\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\uff0c\u8fd8\u6709\u4e24\u4ef6\u4e8b\u60c5\u8981\u548c\u4f60\u5546\u91cf\u5546 \u91cf\u3002\u201d\u6c5f\u6d77\u5929\u731c\u5230\u4e86\u51e0\u5206\uff0c\u7b11\u9053\uff1a\u201c\u590f\u513f\u3001\u8f69\u513f\uff0c\u4f60\u4eec\u90fd\u5750\u62e2\u6765\u542c\u3002\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u9053\uff1a\u201c\u8fd9\u6b21\u6211\u672c\u60f3\u5e26\u7ea8\u513f\u6765\u7684\uff0c\u4f46\u7afa\u5927\u54e5\u906d\u4e86\u610f\u5916\uff0c\u6e05\u534e\u8981\u5979\u4f5c\u4f34\uff0c\u670d\u4f8d\u5979\u7684\u7236 \u4eb2\u3002\u6211\u53ea\u597d\u5355\u8eab\u6765\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u53cd\u6b63\u4ed6\u4eec\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u8fdf\u4e9b\u8ba2\u5a5a\uff0c\u4e5f\u4e0d\u6253\u7d27\u3002 \u3000\u3000\u201c\u4e0a\u5b98\u6cf0\u9053\uff1a\u201c\u7afa\u5927\u54e5\u4e5f\u5f88\u60f3\u5ff5\u5149\u590f\uff0c\u4ed6\u7684\u610f\u601d\u662f\u8981\u6211\u628a\u5149\u590f\u548c\u9053\u8f69\u5e26\u53bb\uff0c\u5c06\u6765\u5c31\u8ba9\u4ed6 \u4eec\u5728\u897f\u661f\u6210\u4eb2\uff0c\u4f60\u770b\u53ef\u597d\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u6b63\u8981\u4ed6\u4eec\u5728\u4e49\u519b\u4e4b\u4e2d\u591a\u4e9b\u5386\u7ec3\uff0c\u8fd9\u6837\u6700\u597d\u4e0d\u8fc7\u3002\u201d\u674e\u5149\u590f\u4e0e\u6797\u9053\u8f69\u4e8c \u4eba\uff0c\u4e00\u4e2a\u60f3\u89c1\u7afa\u6e05\u534e\uff0c\u4e00\u4e2a\u60f3\u89c1\u4e0a\u5b98\u7ea8\uff0c\u542c\u5f97\u5e08\u7236\u7b54\u5e94\uff0c\u5fc3\u91cc\u90fd\u662f\u6697\u6697\u6b22\u559c\u3002 \u3000\u3000\u8c37\u4e2d\u83b2\u7b11\u9053\uff1a\u201c\u4f60\u662f\u8f69\u513f\u7684\u5cb3\u7236\uff0c\u7afa\u8001\u524d\u8f88\u662f\u590f\u513f\u7684\u5cb3\u7236\uff0c\u4ed6\u4eec\u4ee5\u534a\u5b50\u7684\u8eab\u4efd\uff0c\u7406\u8be5\u8ddf \u4f60\u4eec\u7684\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e24\u4e2a\u5f92\u513f\u6211\u4eec\u592b\u5987\u6559\u517b\u4e86\u591a\u5e74\uff0c\u4e00\u65e6\u79bb\u5f00\uff0c\u53ef\u662f\u6709\u70b9\u820d\u4e0d\u5f97\u5462\u3002\u201d \u3000\u3000\u4e0a\u5bab\u6cf0\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u592b\u4eba\uff0c\u6211\u4eec\u6050\u6015\u8fd8\u8981\u501f\u91cd\u4f60\u7684\u4e00\u4e2a\u5f92\u5f1f\u5462\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u201c\u5bf9\u5566\uff0c\u4f60\u8981\u548c\u6211\u5546\u91cf\u7684\u7b2c\u4e8c\u4ef6\u4e8b\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u7b11\u5bb9\u4e00\u655b\uff0c\u9762\u8272\u663e\u5f97\u6709\u51e0\u5206\u6c89\u91cd\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u53ef\u662f\u516c\u4e8b\u4e86\u3002\u897f\u661f\u4e0e\u5c0f\u91d1\u5ddd\u4e4b \u95f4\u867d\u7136\u6709\u6e05\u519b\u9694\u65ad\uff0c\u4f46\u5374\u662f\u4e92\u76f8\u547c\u5e94\u7684\u3002\u7afa\u5927\u54e5\u53d7\u4e86\u4f24\uff0c\u897f\u661f\u91cd\u9677\u654c\u624b\uff0c\u7afa\u5927\u54e5\u56fa\u7136\u8981\u9041\u5165 \u6df1\u5c71\uff0c\u529b\u6c42\u81ea\u4fdd\uff0c\u5c0f\u91d1\u5ddd\u7684\u5f62\u52bf\u4e5f\u56e0\u800c\u5403\u7d27\u4e86\u3002\u76ee\u524d\u6211\u4eec\u6700\u9700\u8981\u4e00\u4e2a\u61c2\u5f97\u884c\u519b\u7528\u5175\u4e4b\u9053\u7684\u4eba \u624d\uff0c\u66ff\u4ee3\u7afa\u5927\u54e5\u6307\u6325\u4f5c\u6218\u3002\u5c0f\u91d1\u5ddd\u65b9\u9762\u7684\u51b7\u94c1\u6a35\u66fe\u6d3e\u6709\u4eba\u6765\uff0c\u5e0c\u671b\u6211\u4eec\u80fd\u51fa\u4e00\u652f\u5947\u5175\u6253\u5f00\u8fd9 \u52a3\u52bf\u5c40\u9762\u3002\u4ed6\u4eec\u8fd8\u63d0\u51fa\u4e86\u6700\u9002\u5b9c\u7684\u7edf\u5e05\u4eba\u9009\uff0c\u6c5f\u5927\u4fa0\uff0c\u4f60\u4e00\u5b9a\u4f1a\u77e5\u9053\u4ed6\u4eec\u8981\u63a8\u9009\u7684\u662f\u8c01 \u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u4eec\u662f\u6253\u5e55\u534e\u7684\u4e3b\u610f\u3002\u201d\u6c5f\u6d77\u5929\u7684\u5927\u5f1f\u5b50\u53f6\u5e55\u534e\u4e09\u5e74\u80fd\u66fe\u5f53\u8fc7 \u63f4\u5ddd\u4e49\u519b\u7684\u9996\u9886\uff0c\u548c\u5c0f\u91d1\u5ddd\u65b9\u9762\u7684\u4e49\u519b\u9886\u8896\u51b7\u94c1\u6a35\u66fe\u5171\u540c\u4f5c\u8fc7\u6218\u7684\uff0c\u7531\u4ed6\u672a\u534f\u52a9\u7afa\u5c1a\u7236\u548c\u5c0f \u91d1\u5ddd\u5408\u4f5c\u6297\u6218\uff0c\u5f53\u7136\u662f\u6700\u9002\u5f53\u7684\u4eba\u9009\u4e86\u3002 \u3000\u3000\u4e0a\u5b98\u6cf0\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u53ef\u4ee5\u653e\u53f6\u5c11\u4fa0\u8d70\u5417\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u8d70\uff1f\u201d\u4e0a\u5bab\u6cf0\u9053\uff1a \u201c\u6211\u60f3\u660e\u5929\u5c31\u8d70\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u8fdc\u9053\u800c\u6765\uff0c\u672c\u6765\u5e94\u591a\u4f4f\u4e24\u5927\u7684\u3002\u4f46\u7afa\u8001\u524d\u8f88\u90a3\u8fb9\u7b49\u7740\u4f60 \u56de\u53bb\uff0c\u6211\u4e5f\u4e0d\u633d\u7559\u4f60\u4e86\u3002\u6155\u534e\uff0c\u79c0\u51e4\uff0c\u4f60\u4eec\u592b\u5987\u4eca\u665a\u6536\u62fe\u6536\u62fe\uff0c\u660e\u5929\u4e00\u65e9\u5c31\u548c\u4e0a\u5b98\u524d\u8f88\u52a8\u8eab \u5427\u3002\u4f60\u7684\u4e24\u4e2a\u5e08\u5f1f\uff0c\u4eca\u540e\u5c31\u7531\u4f60\u66ff\u6211\u7ba1\u6559\u4e86\u3002\u201d\u53f6\u6155\u534e\u592b\u5987\u5e94\u4e86\u4e2a\u201c\u662f\u201d\u5b57\uff0c\u4fbf\u5373\u544a\u9000\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5c0f\u5973\u8fd9\u6b21\u51fa\u9601\uff0c\u5404\u65b9\u5ba2\u4eba\u6765\u5f97\u751a\u591a\uff0c\u6211\u6050\u6015\u8fd8\u8981\u5fd9\u51e0\u5929\uff0c\u660e\u5929\u4e0d\u80fd\u548c\u4f60\u4eec \u4e00\u540c\u8d70\u4e86\u3002\u4e0a\u5b98\u6cf0\u7b11\u9053\uff1a\u201c\u4f60\u80af\u8981\u6211\u5e26\u8d70\u4f60\u7684\u4e09\u4e2a\u5f92\u5f1f\uff0c\u6211\u5df2\u662f\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a \u201c\u7afa\u8001\u524d\u8f88\u53d7\u4e86\u4f24\uff0c\u6211\u5e94\u8be5\u53bb\u63a2\u671b\u4ed6\u7684\u3002\u5728\u4eca\u5e74\u5e74\u5e95\u4e4b\u524d\uff0c\u6211\u4f1a\u5230\u5927\u51c9\u5c71\u7684\u3002\u201d\u6c5f\u6d77\u5929\u7684\u8ba1 \u5212\u662f\u5728\u7afa\u5c1a\u7236\u90a3\u513f\u8fc7\u5e74\uff0c\u7136\u540e\u8fdb\u4eac\u8d74\u7ea6\uff0c\u5728\u5143\u5bb5\u8282\u4f1a\u89c1\u5e08\u7236\u6240\u6307\u5b9a\u8981\u89c1\u7684\u90a3\u4e2a\u795e\u79d8\u4eba\u7269\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e0e\u4e0a\u5b98\u6cf0\u8ba2\u4e86\u540e\u4f1a\u4e4b\u671f\uff0c\u4fbf\u53eb\u6797\u9053\u8f69\u5e26\u4e0a\u5b98\u6cf0\u5165\u623f\u6b47\u606f\uff0c\u674e\u5149\u590f\u4e5f\u968f\u540c\u544a\u9000\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u628a\u91d1\u9010\u6d41\u7559\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u6709\u4ec0\u4e48\u6253\u7b97\u3002\u201d\u91d1\u9010\u6d41\u9053\u3002\u201d\u6211\u60f3\u5728\u6c5f\u6e56\u8d70 \u8d70\uff0c\u8bbf\u95ee\u7239\u7239\u7684\u51e0\u4f4d\u597d\u670b\u53cb\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u597d\u7684\uff0c\u4ee5\u4f60\u7684\u672c\u9886\uff0c\u5728\u6c5f\u6e56\u4e0a\u5df2\u7ecf\u7f55\u4eba\u80fd\u52ab\uff0c \u65e0\u987b\u6211\u7167\u987e\u4f60\u4e86\u3002\u4e0d\u8fc7\uff0c\u4f60\u8981\u8bb0\u7740\u4e0d\u53ef\u631f\u6280\u51cc\u4eba\u3002\u201d\u91d1\u9010\u6d41\u5bf9\u8fd9\u4f4d\u5927\u5e08\u5144\u9887\u6709\u51e0\u5206\u656c\u754f\uff0c\u8bf4 \u9053\uff1a\u201c\u5c0f\u5f1f\u8bb0\u5f97\u5e08\u5144\u7684\u6559\u8bad\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u51c6\u5907\u4ec0\u4e48\u65f6\u5019\u8d70\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u60f3\u548c\u79e6 \u5143\u6d69\u540c\u8d70\uff0c\u6050\u6015\u660e\u5929\u4e5f\u8981\u52a8\u8eab\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8be7\u9053\uff1a\u201c\u4f60\u4f55\u5fc5\u8d70\u5f97\u8fd9\u6837\u5306\u5fd9\uff1f\u8fd9\u91cc\u6709\u8bb8\u591a\u6b66\u6797\u670b\u53cb\uff0c\u6211\u60f3\u4f60\u548c\u4ed6\u4eec\u8ba4\u8bc6\u8ba4\u8bc6\u3002 \u5c31\u662f\u79e6\u5143\u6d69\u6211\u4e5f\u60f3\u4ed6\u7559\u591a\u4e00\u5929\uff0c\u8ba9\u4ed6\u548c\u5c0f\u4e00\u8f88\u7684\u7ed3\u4ea4\u7ed3\u4ea4\u3002\u201d\u91d1\u9010\u6d41\u4e0d\u6562\u628a\u79e6\u5143\u6d69\u7684\u79c1\u4e8b\u544a \u8bc9\u5e08\u5144\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5c01\u5b50\u8d85\u603b\u4e0d\u4f1a\u6253\u6b7b\u4ed6\u7684\u5973\u513f\uff0c\u8fdf\u4e00\u5929\u518d\u548c\u5143\u6d69\u53bb\u6253\u542c\u5979\u7684\u6d88\u606f\u4e5f\u8fd8\u4e0d \u8fdf\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u597d\u54b1\uff0c\u6211\u628a\u5e08\u5144\u7684\u610f\u601d\u548c\u5143\u6d69\u8bf4\u53bb\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e14\u6162\uff0c\u6211\u8fd8\u6709\u4e00\u4ef6\u4e8b\u60c5\u60f3\u8981\u95ee\u4f60\uff0c\u5e08\u7236\u53eb\u6211\u5728\u660e\u5e74\u7684\u5143\u9704\u665a\u4e0a\uff0c\u5230\u5317\u4eac\u897f \u5c71\u7684\u79d8\u9b54\u5d16\u53bb\u4f1a\u4e00\u4e2a\u4eba\uff0c\u4f60\u53ef\u77e5\u9053\u8fd9\u4ef6\u4e8b\u60c5\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u7239\u7239\u6ca1\u6709\u548c\u6211\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u660e\u5e74\u5143\u9704\uff0c\u4f60\u8981\u662f\u8d76\u5f97\u6765\u7684\u8bdd\uff0c\u54b1\u4eec\u4e5f\u53ef\u4ee5\u5728\u5317\u4eac\u7684\u897f\u5c71\u76f8\u4f1a\u3002\u201d\u4ed6\u4ee5\u4e3a \u5e08\u7236\u8981\u4ed6\u6240\u89c1\u4e4b\u4eba\uff0c\u548c\u5e08\u5f1f\u76f8\u4f1a\uff0c\u60f3\u5fc5\u4e5f\u65e0\u5173\u7cfb\uff0c\u662f\u4ee5\u548c\u5e08\u5f1f\u8ba2\u4e0b\u4e86\u540e\u4f1a\u4e4b\u671f\u3002\u91d1\u9010\u6d41\u6027\u559c \u70ed\u95f9\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u7239\u7239\u4e0d\u77e5\u8981\u5e08\u5144\u4f1a\u7684\u4ec0\u4e48\u4eba\uff0c\u8fde\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u201d\u5e26\u7740\u51e0\u5206\u597d\u5947\uff0c\u6b23\u7136\u7b54 \u5e94\u3002 \u3000\u3000\u5f53\u665a\u91d1\u9010\u6d41\u548c\u79e6\u5143\u6d69\u540c\u4f4f\u4e00\u95f4\u5ba2\u623f\uff0c\u628a\u6c5f\u6d77\u5929\u60f3\u8981\u4ed6\u591a\u7559\u4e00\u5929\u7684\u610f\u601d\u548c\u79e6\u5143\u6d69\u8bf4\u4e86\u3002\u79e6 \u5143\u6d69\u8fd9\u6b21\u4ee3\u8868\u5e08\u7236\u6765\u6c5f\u5bb6\u9053\u8d3a\uff0c\u4ed6\u5e08\u7236\u7684\u610f\u601d\u672c\u6765\u5c31\u662f\u8981\u4ed6\u85c9\u6b64\u673a\u4f1a\u591a\u4ea4\u670b\u53cb\u7684\uff0c\u4f55\u51b5\u53c8\u6709 \u4e3b\u4eba\u7684\u76db\u610f\u633d\u7559\uff0c\u56e0\u6b64\u79e6\u5143\u6d69\u867d\u7136\u60e6\u8bb0\u7740\u5c01\u5999\u5ae6\uff0c\u4e5f\u53ea\u6709\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u7eed\u65ad\u818f\u679c\u7136\u5341\u5206\u7075\u6548\uff0c\u7b2c\u4e8c\u5929\u4e0a\u5b98\u6cf0\u7684\u819d\u76d6\u5df2\u7ecf\u533b\u597d\uff0c\u4f59\u6bd2\u4ea6\u5df2\u62d4\u6e05\u4e86\u3002\u4ed6\u5e26\u6765\u53f6\u5e55\u534e \u592b\u5987\u4e0e\u674e\u5149\u590f\u3001\u6797\u9020\u8f69\u56db\u4eba\u540c\u8d70\u3002\u4e00\u9633\u5b50\u3001\u4ef2\u957f\u7edf\u7b49\u4eba\u4e5f\u5728\u8fd9\u4e00\u5929\u4e4b\u5185\u5148\u540e\u5411\u6c5f\u6d77\u5929\u8f9e\u884c\u4e86 \u3000\u3000\u7b2c\u4e09\u5929\u91d1\u9010\u6d41\u548c\u79e6\u5143\u6d69\u540c\u8d70\uff0c\u4ed6\u4eec\u517c\u7a0b\u8d76\u8def\uff0c\u5f53\u665a\u5c31\u5230\u4e86\u5f82\u5f95\u5c71\u3002\u79e6\u5143\u6d69\u8bf4\u9053\uff1a\u201c\u4f60\u6084 \u6084\u53bb\u5c01\u5bb6\u770b\u4e00\u770b\uff0c\u6211\u53ea\u8981\u77e5\u9053\u5c01\u59d1\u5a18\u7684\u60c5\u5f62\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4e0d\u884c\uff0c\u4eba\u5bb6\u597d\u5fc3\u5f85\u4f60\uff0c\u4f60\u600e\u53ef\u4e0d\u53bb\u89c1\u5979\u4e00\u89c1\uff1f\u201d\u79e6\u5143\u6d69\u9762\u4e0a\u4e00 \u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u6837\u8dd1\u53bb\u7b97\u662f\u4ec0\u4e48\uff1f\u592a\u4e0d\u597d\u610f\u601d\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u8d8a\u53d1\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u8fd9\u5c0f\u5b50\u81ea\u5df1\u4e0d\u6562\u53bb\uff0c\u5982\u8981\u6211\u4ee3\u8868\u4f60\u53bb\u548c\u5c01\u59d1\u5a18\u79c1 \u4f1a\uff0c\u6211\u8fd9\u53c8\u7b97\u662f\u4ec0\u4e48\uff1f\u5927\u4e08\u592b\u5149\u660e\u78ca\u843d\uff0c\u8981\u53bb\u5c31\u5149\u660e\u6b63\u5927\u7684\u53bb\uff0c\u6015\u4ec0\u4e48\uff1f\u201d\u79e6\u5143\u6d69\u9053\uff1a\u201c\u4f60 \u53eb\u6211\u600e\u6837\u548c\u5c01\u5b50\u8d85\u8bf4\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5c01\u5b50\u8d85\u8fd9\u6b21\u5168\u9760\u4e86\u4f60\uff0c\u4ed6\u624d\u4e0d\u81f3\u4e8e\u4e0e\u6587\u9053\u5e84\u540c\u4e00\u547d\u8fd0\u2014\u2014\u4e22\u8138\u574d\u53f0\u3002\u4ed6\u82e5 \u662f\u7a0d\u6709\u826f\u5fc3\u7684\u8bdd\uff0c\u5bf9\u4f60\u5e94\u8be5\u5f53\u4f5c\u6069\u4eba\u9053\u8c22\u624d\u662f\uff0c\u4f60\u5374\u6015\u89c1\u4ed6\uff1f\u597d\u5427\uff0c\u4f60\u627e\u4e0d\u5230\u7c4d\u53e3\u662f\u4e0d\u662f\u4e86 \u4f60\u8ddf\u6211\u6765\uff0c\u6211\u66ff\u4f60\u8bf4\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d\u91d1\u9010\u6d41\u751f\u6027\u4e0d\u7f81\uff0c\u4ed6\u5374\u662f\u6709\u70b9\u62d8\u8c28\u7684\u3002\u6b63\u60f3\u8bf4\u9053\uff1a\u201c\u8fd9 \u4e2a\u6050\u6015\u4e0d\u5927\u597d\u5427\uff1f\u201d\u91d1\u9010\u6d41\u5df1\u4e0d\u7531\u5206\u8bf4\uff0c\u5c06\u4ed6\u62c9\u5230\u4e86\u5c01\u5bb6\u7684\u5927\u95e8\u7684\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u7684\u670b\u53cb \u559d\u4e86\u4f60\u7684\u6842\u82b1\u9648\u9152\uff0c\u5341\u5206\u6b23\u8d4f\uff0c\u5982\u4eca\u6211\u4e5f\u60f3\u6765\u5411\u4f60\u8ba8\u4e00\u676f\u559d\u5566\u3002\u4f60\u662f\u62db\u5f85\u8fc7\u79e6\u5143\u6d69\u7684\u4e86\uff0c\u8fd9 \u6b21\u60f3\u4e0d\u81f3\u4e8e\u62d2\u7edd\u6211\u4eec\u5427\uff1f\u201d \u3000\u3000\u5168\u9010\u6d41\u7528\u7684\u662f\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u529f\u592b\uff0c\u5c01\u5bb6\u82e5\u662f\u6709\u4eba\uff0c\u51b3\u6ca1\u6709\u542c\u4e0d\u89c1\u7684\u9053\u7406\u3002\u53ef\u662f\u91cc\u9762\u5374 \u662f\u6beb\u65e0\u53cd\u5e94\u3002 \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u62db\u5f85\u4e5f\u597d\uff0c\u4e0d\u62db\u5f85\u4e5f\u597d\uff0c\u6211\u65e2\u7136\u6765\u4e86\uff0c\u5c31\u5403\u5b9a\u4f60\u4e86\uff01\u4f60\u4e0d \u5f00\u95e8\uff0c\u6211\u81ea\u5df1\u4e0d\u4f1a\u8fdb\u6765\u5417\uff1f\u201d\u79e6\u5143\u6d69\u6b63\u8981\u8bf4\u9053\uff1a\u201c\u4e0d\u597d\u3002\u201d\u8bdd\u672a\u51fa\u53e3\uff0c\u53ea\u89c9\u8eab\u5b50\u4e00\u8f7b\uff0c\u5df2\u88ab \u91d1\u9010\u6d41\u62d6\u7740\u4ed6\u8d8a\u8fc7\u4e86\u5899\u5934\u3002 \u3000\u3000\u91d1\u9010\u6d41\u672c\u6765\u51c6\u5907\u6709\u4eba\u5077\u88ad\u7684\uff0c\u4f46\u51fa\u4e4e\u4ed6\u7684\u610f\u6599\u4e4b\u5916\uff0c\u8fdb\u4e86\u5c01\u5bb6\uff0c\u5374\u8fde\u9b3c\u5f71\u4e5f\u6ca1\u770b\u89c1\u4e00 \u4e2a\u3002\u91d1\u9010\u6d41\u4fa7\u8033\u7ec6\u542c\uff0c\u4e5f\u542c\u4e0d\u51fa\u6709\u4e1d\u6beb\u58f0\u606f\u3002\u4ed4\u7ec6\u770b\u65f6\uff0c\u53ea\u89c1\u5ead\u9662\u91cc\u6709\u51cc\u4e71\u7684\u8db3\u5370\u3002 \u3000\u3000\u91d1\u9010\u6d41\u76b1\u4e00\u76b1\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u770b\u8fd9\u60c5\u5f62\uff0c\u53ea\u6015\u4ed6\u4eec\u65e9\u5df2\u8d70\u4e86\u3002\u4e0d\u8fc7\uff0c\u54b1\u4eec\u65e2\u7136\u6765\u4e86\uff0c\u4e5f \u5c31\u8fdb\u53bb\u770b\u770b\u5427\u3002\u201d \u3000\u3000\u4ed6\u4eec\u7a7f\u5802\u5165\u5ba4\uff0c\u641c\u67e5\u6587\u9053\u5e84\u7236\u5b50\u6240\u4f4f\u7684\u5ba2\u623f\u548c\u5c01\u5b50\u8d85\u7684\u623f\u95f4\uff0c\u90fd\u4e0d\u89c1\u6709\u4eba\u3002\u91d1\u9010\u6d41\u5728\u4e00 \u95f4\u623f\u91cc\u627e\u5230\u4e00\u575b\u6842\u82b1\u9152\uff0c\u95fb\u4e86\u4e00\u95fb\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u771f\u7684\u6842\u82b1\u9152\u3002\u201d\u559d\u4e86\u4e00\u53e3\uff0c\u53c8\u627e\u6765\u4e86\u4e00\u4e2a \u846b\u82a6\uff0c\u76db\u6ee1\u4e86\u9152\u5e26\u8d70\uff0c\u7b11\u9053\uff1a\u201c\u59ec\u4f2f\u4f2f\u4f20\u4e0b\u6765\u7684\u5077\u513f\u89c4\u77e9\uff0c\u8fdb\u4e86\u522b\u4eba\u5bb6\uff0c\u51b3\u4e0d\u80fd\u7a7a\u624b\u800c \u56de\u3002\u201d \u3000\u3000\u5230\u4e86\u540e\u9762\u7684\u5ead\u9662\uff0c\u79e6\u5143\u6d69\u6709\u6240\u53d1\u73b0\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u51e0\u6839\u7af9\u6367\u63d2\u5728\u8fd9\u91cc\u662f\u4ec0 \u4e48\u610f\u601d\uff1f\u201d\u539f\u6765\u5728\u5ead\u9662\u4e2d\u95f4\u63d2\u7740\u4e5d\u6839\u7af9\u68d2\uff0c\u4e2d\u95f4\u7684\u4e00\u6839\u7af9\u68d2\u88ab\u65ab\u4e86\u4e00\u5200\uff0c\u5f53\u4e2d\u5256\u4e0b\uff0c\u5206\u6210\u4e24 \u8fb9\u3002\u5ead\u9662\u662f\u788e\u77f3\u548c\u6ce5\u571f\u6df7\u5408\u7684\u5730\u9762\uff0c\u7af9\u68d2\u63d2\u5f97\u8fdb\u53bb\uff0c\u53ef\u89c1\u63d2\u68d2\u7684\u4eba\u5b9a\u662f\u4e2a\u5185\u5bb6\u9ad8\u624b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u4ef2\u5e2e\u4e3b\u6765\u8fc7\u4e86\u3002\u4e10\u5e2e\u4e2d\u4eba\u63d2\u7af9\u68d2\u7b49\u4e8e\u662f\u7559\u5200\u793a\u8b66\u7684\u610f\u601d\u3002\u4f46\u53ea\u6709 \u5e2e\u4e3b\u624d\u6709\u8d44\u683c\u63d2\u51e0\u6839\u7af9\u68d2\u3002\u201d\u91d1\u9010\u6d41\u867d\u7136\u662f\u56de\u56fd\u672a\u4e45\uff0c\u4f46\u56e0\u59ec\u6653\u98ce\u65f6\u5e38\u7ed9\u4ed6\u8bb2\u8ff0\u6c5f\u6e56\u4e0a\u7684\u5404 \u79cd\u89c4\u77e9\uff0c\u5374\u662f\u6bd4\u521d\u51fa\u9053\u7684\u79e6\u5143\u6d69\u61c2\u5f97\u591a\u3002 \u3000\u3000\u79e6\u5143\u6d69\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u8fd9\u7b49\u4e8e\u662f\u7559\u5200\u793a\u8b66\uff1f\u90a3\u4e48\u4ef2\u5e2e\u4e3b\u60f3\u5fc5\u662f\u5df2\u77e5\u9053\u5c01\u5b50\u8d85\u662f \u4ec0\u4e48\u4eba\u4e86\uff1f\u4f46\u4e2d\u95f4\u8fd9\u6839\u7af9\u68d2\u88ab\u5256\u5f00\uff0c\u8fd9\u53c8\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u662f\u6709\u4eba\u5411\u4ed6\u6311\u6218\u3002\u201d\u79e6\u5143\u6d69\u9053\uff1a\u201c\u6587\u9053\u5e84\u4e0d\u662f\u53d7\u4f24\u4e86\u5417\uff1f\u5c01\u5b50\u8d85\u600e\u80fd\u6709\u6b64 \u80c6\u91cf\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53ea\u6015\u662f\u53e6\u5916\u6709\u4eba\uff0c\u4e0d\u4e00\u5b9a\u662f\u5c01\u3001\u6587\u4e24\u4e2a\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u5c01\u5b50\u8d8a\u4e0d\u77e5\u662f\u7ed9\u4ef2\u957f\u7edf\u5413\u8dd1\u7684\uff0c\u8fd8\u662f\u4ed6\u6839\u672c\u5c31\u4e0d\u6562\u56de\u5bb6\u3002\u4ece\u5ead\u9662\u91cc\u7684\u8db3 \u5370\u770b\u6765\uff0c\u6765\u8fc7\u8fd9\u91cc\u7684\u663e\u7136\u4e0d\u6b62\u4e00\u4eba\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u4ed6\u4eec\u65e2\u7136\u90fd\u8d70\u4e86\uff0c\u54b1\u4eec\u51fa\u53bb\u5427\u3002\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u5fd9\u4ec0\u4e48\uff0c\u5230\u4f60\u90a3\u4f4d\u5c01\u59d1 \u5a18\u7684\u9999\u95fa\u91cc\u770b\u770b\u5427\u3002\u201d\u5c01\u5999\u5ae6\u7684\u5367\u623f\u662f\u5728\u6700\u540e\u4e00\u8fdb\u623f\u5b50\u9760\u8fd1\u82b1\u56ed\u7684\u4e00\u95f4\u623f\u95f4\u3002\u79e6\u5143\u6d69\u9762\u4e0a\u4e00 \u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5144\uff0c\u8bf4\u7b11\u4e86\u3002\u201d\u91d1\u9010\u6d41\u9762\u8272\u4e00\u7aef\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u548c\u4f60\u5f00\u73a9\u7b11\uff0c\u8bf4\u4e0d\u5b9a\u5979\u4f1a\u7559 \u6709\u4ec0\u4e48\u4e1c\u897f\u7ed9\u4f60\u5462\u3002\u201d\u79e6\u5143\u6d69\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u8ddf\u7740\u4ed6\u8d70\u3002\u6b63\u662f\uff1a \u3000\u3000\u6843\u82b1\u6d41\u6c34\u6773\u7136\u53bb\uff0c\u5d14\u62a4\u91cd\u6765\u4e0d\u89c1\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e03\u56de\u3000\u9526\u5e10\u4f4e\u5782\u4eba\u5df2\u6773\u3000\u743c\u6d46\u96be\u5f97\u5ba2\u5f52\u6765 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e03\u56de\u3000\u9526\u5e10\u4f4e\u5782\u4eba\u5df2\u6773\u3000\u743c\u6d46\u96be\u5f97\u5ba2\u5f52\u6765 \u3000\u3000\u91d1\u9010\u6d41\u63a8\u5f00\u623f\u95e8\uff0c\u4f46\u89c1\u9526\u5e10\u4f4e\u5782\uff0c\u5986\u53f0\u5c18\u6697\uff0c\u91d1\u9010\u6d41\u7b11\u9053\u3002\u201c\u5143\u6d69\u5144\uff0c\u4f60\u8fd9\u4f4d\u59d1\u5a18\u5df2\u8d70 \u4e86\u597d\u51e0\u5929\u4e86\uff0c\u4e5f\u6ca1\u4e1c\u897f\u7559\u7ed9\u4f60\uff0c\u4f60\u5931\u671b\u4e86\u5427\uff1f\u4f46\u8fd9\u6837\u4e5f\u597d\uff0c\u5979\u4e0d\u662f\u8ddf\u5979\u7239\u7239\u8d70\u7684\uff0c\u4f60\u53ef\u4ee5\u653e \u5fc3\u4e86\u3002\u201d\u91d1\u9010\u6d41\u4ece\u68b3\u5986\u53f0\u4e0a\u5c18\u57c3\u672a\u62ed\u7684\u8fd9\u4e2a\u73b0\u8c61\uff0c\u63a8\u65ad\u51fa\u5c01\u5999\u5ae6\u5df2\u8d70\u4e86\u597d\u51e0\u5929\u3002\u6240\u4ee5\u5c01\u5b50\u8d85 \u5373\u4f7f\u66fe\u7ecf\u56de\u5bb6\uff0c\u4ed6\u7684\u5973\u513f\u4e5f\u51b3\u4e0d\u662f\u8ddf\u7740\u4ed6\u8d70\u7684\u3002 \u3000\u3000\u79e6\u5143\u6d69\u9053\u3002\u201d\u90a3\u4e48\u54b1\u4eec\u8fd8\u8fdb\u53bb\u505a\u4ec0\u4e48\uff1f\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u91d1\u9010\u6d41\u5df2\u62d6\u7740\u4ed6\u8fdb\u4e86\u623f\u95f4\uff0c\u5ffd\u5730\u5728 \u4ed6\u8033\u8fb9\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u63ed\u5f00\u5e10\u5b50\u77a7\u77a7\uff01\u201d\u79e6\u5143\u6d69\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u53ef\u4ee5\uff1f\u201d\u91d1\u9010\u6d41 \u628a\u4ed6\u4e00\u628a\u63a8\u4e0a\u524d\u53bb\u3001\u8bf4\u9053\uff1a\u201c\u6211\u53eb\u4f60\u63ed\u4f60\u5c31\u63ed\uff0c\u4e0d\u5fc5\u5bb3\u6015\uff01\u201d \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u9690\u7ea6\u542c\u5f97\u5e10\u5185\u4f3c\u6709\u5fae\u5f31\u7684\u547c\u5438\u6c14\u606f\uff0c\u8fd9\u6709\u4e24\u4e2a\u4e00\u53ef\u80fd\uff0c\u4e00\u4e2a\u53ef\u80fd\u662f\u5c01\u5999\u5ae6\u53d7 \u4e86\u4f24\uff0c\u8eba\u5728\u5e8a\u4e0a\u4e0d\u80fd\u52a8\u5f39\u4e5f\u4e0d\u80fd\u8bf4\u8bdd\uff1b\u4e00\u4e2a\u53ef\u80fd\u662f\u53e6\u6709\u9ad8\u624b\u8eb2\u5728\u5979\u7684\u5e8a\u4e2d\u3002\u5185\u529f\u6709\u4e00\u5b9a\u9020\u8be3 \u7684\u4eba\u53ef\u4ee5\u63a7\u5236\u547c\u5524\uff0c\u6240\u4ee5\u79e6\u5143\u6d69\u4e0d\u80fd\u53d1\u89c9\uff0c\u4f46\u91d1\u9010\u6d41\u5374\u53ef\u4ee5\u542c\u5f97\u51fa\u6765\u3002\u91d1\u9010\u6d41\u4e0d\u77e5\u5728\u5e10\u4e2d\u7684 \u662f\u4e0d\u662f\u5c01\u5999\u5ae6\uff0c\u4ed6\u81ea\u5df1\u4e0d\u4fbf\u53bb\u63ed\u5f00\u5e10\u5b50\uff0c\u53ea\u597d\u53eb\u79e6\u5143\u6d69\u52a8\u624b\u3002 \u3000\u3000\u8fd9\u8bdd\u4e0d\u597d\u660e\u8bf4\uff0c\u79e6\u5143\u6d69\u4e0d\u61c2\u5f97\u91d1\u9010\u6d41\u7684\u7528\u610f\uff0c\u5927\u58f0\u7684\u56b7\u4e86\u51fa\u6765\uff0c\u8bdd\u72b9\u672a\u4e86\uff0c\u5e8a\u4e0a\u7a81\u7136\u8df3 \u8d77\u4e86\u4e00\u4e2a\u4eba\uff0c\u6495\u5f00\u5e10\u5b50\uff0c\u4e00\u6293\u5411\u79e6\u5143\u6d69\u6293\u4e0b\uff0c\u559d\u9053\uff1a\u201c\u539f\u6765\u4f60\u5c31\u662f\u59d3\u79e6\u7684\u5c0f\u5b50\uff0c\u4f60\u628a\u5c01\u59d1\u5a18 \u62d0\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u662f\u6b66\u5f53\u7b2c\u4e8c\u4ee3\u6700\u6770\u51fa\u7684\u5f1f\u5b50\uff0c\u731d\u9047\u654c\u88ad\uff0c\u867d\u60ca\u4e0d\u4e71\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u58f0 \u51fa\u638c\u53d1\uff0c\u4e00\u62db\u201c\u6392\u4e91\u624b\u201d\u628a\u90a3\u4eba\u7684\u4e00\u6293\u8361\u5f00\u3002 \u3000\u3000\u4e0d\u6599\u53cc\u638c\u4e00\u89e6\uff0c\u90a3\u4eba\u7684\u624b\u638c\u5176\u51b7\u5982\u51b0\uff0c\u79e6\u5143\u6d69\u4e0d\u7531\u5f97\u673a\u4f36\u4f36\u5730\u6253\u4e86\u4e00\u4e2a\u51b7\u98a4\u3002\u8bf4\u65f6\u8fdf\uff0c \u90a3\u65f6\u5feb\uff0c\u91d1\u9010\u6d41\u5df2\u662f\u8f7b\u98d8\u98d8\u7684\u4e00\u638c\u62cd\u5c06\u8fc7\u53bb\uff0c\u770b\u4f3c\u6beb\u4e0d\u7528\u529b\uff0c\u5185\u4e2d\u5374\u85cf\u7740\u597d\u51e0\u4e2a\u7cbe\u5999\u7684\u53d8 \u5316\u3002 \u3000\u3000\u90a3\u4eba\u5927\u7ea6\u6709\u4e09\u5341\u5c81\u5de6\u53f3\u7684\u5e74\u7eaa\uff0c\u50cf\u4e2a\u75c5\u592b\uff0c\u9762\u8272\u82cd\u767d\uff0c\u6beb\u65e0\u8868\u60c5\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f \u53eb\u5316\u5b50\u4e5f\u6562\u591a\u4e8b\uff0c\u53eb\u4f60\u77e5\u9053\u6211\u7684\u5389\u5bb3\uff01\u201d\u4ed6\u770b\u4f3c\u75c5\u592b\uff0c\u5185\u529b\u5374\u5f88\u4e0d\u5f31\uff0c\u5c45\u7136\u628a\u91d1\u9010\u6d41\u8fd9\u4e00\u638c \u8574\u85cf\u7740\u5185\u5bb6\u529f\u529b\u7684\u52b2\u9053\u89e3\u4e86\u51e0\u5206\u3002 \u3000\u3000\u91d1\u9010\u6d41\u638c\u5fc3\u4e00\u7ffb\uff0c\u638c\u529b\u5c3d\u5410\uff0c\u7cbe\u5999\u7684\u540e\u7740\u4e5f\u8ddf\u7740\u4f7f\u51fa\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u90a3\u4eba\u7740\u4e86\u4e00 \u638c\uff0c\u201c\u54c7\u201d\u7684\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u597d\u5389\u5bb3\uff01\u201d\u4e0d\u6562\u518d\u63a5\u91d1\u9010\u6d41\u7684\u7b2c\u4e8c\u638c\uff0c\u4ece\u7a97\u5b50\u91cc \u5c31\u8df3\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u53ef\u662f\u8bf4\u4e5f\u5947\u602a\uff0c\u5728\u90a3\u4eba\u53e3\u5410\u9c9c\u8840\u7684\u90a3\u5239\u90a3\u95f4\uff0c\u91d1\u9010\u6d41\u5374\u4e0d\u7531\u81ea\u4e3b\u5730\u9000\u4e86\u4e24\u6b65\uff0c\u53ea\u80fd\u773c\u7741 \u7741\u5730\u770b\u7740\u90a3\u4eba\u9003\u8d70\uff0c\u8981\u60f3\u963b\u62e6\u5df2\u6765\u4e0d\u53ca\u3002 \u3000\u3000\u79e6\u5143\u6d69\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5144\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u65e2\u7136\u77e5\u9053\u6211 \u7684\u5389\u5bb3\uff0c\u6211\u5c31\u8ba9\u4f60\u8d70\u5427\uff0c\u4f60\u5e94\u8be5\u660e\u767d\uff0c\u5982\u679c\u6211\u8ffd\u4f60\u7684\u8bdd\uff0c\u8fd8\u662f\u53ef\u4ee5\u628a\u4f60\u63ea\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7684\u7b11\u58f0\u4e2d\u6c14\u5145\u6c9b\uff0c\u90a3\u662f\u5411\u654c\u4eba\u793a\u5a01\u7684\u4e00\u79cd\u8868\u73b0\u3002\u79e6\u5143\u6d69\u4e00\u542c\u5230\u4ed6\u7684\u7b11\u58f0\uff0c\u4e5f\u5c31\u77e5 \u9053\u4ed6\u5e76\u6ca1\u53d7\u4f24\u4e86\u3002\u91d1\u9010\u6d41\u5411\u90a3\u4eba\u53d1\u8bdd\u4e4b\u540e\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u5411\u79e6\u5143\u6d69\u7b11\u9053\uff1a\u201c\u6211\u6ca1\u4ec0\u4e48\uff0c\u6211\u5012\u662f \u62c5\u5fc3\u4f60\u5462\u3002\u600e\u4e48\u6837\uff0c\u4f60\u51b7\u5f97\u5f88\u96be\u53d7\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u5947\u602a\uff0c\u90a3\u4eba\u7adf\u4e0d\u4f3c\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u6211\u5f53\u771f\u662f\u51b7\u5f97\u96be\u53d7\u3002\u4e0d\u8fc7\uff0c\u73b0\u5728\u5df2\u597d\u4e86\u4e00 \u4e9b\u4e86\u3002\u201d\u91d1\u9010\u6d41\u63e1\u7740\u4ed6\u7684\u624b\uff0c\u4e00\u80a1\u70ed\u529b\u4ece\u79e6\u5143\u6d69\u7684\u638c\u5fc3\u4f20\u4e86\u8fdb\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u7384\u9634\u6307\u5df2 \u6709\u516d\u4e03\u5206\u706b\u5019\uff0c\u7384\u9634\u6307\u662f\u4e00\u95e8\u90aa\u6d3e\u529f\u592b\uff0c\u662f\u4ece\u4fee\u7f57\u9634\u715e\u529f\u53d8\u6bd4\u51fa\u6765\u7684\uff0c\u4e0d\u8fc7\u8fd8\u8fdc\u4e0d\u5982\u9634\u715e\u529f \u7684\u5389\u5bb3\uff0c\u60f3\u5fc5\u90a3\u4eba\u662f\u8d2a\u56fe\u4e91\u9634\u6307\u6613\u7ec3\uff0c\u6240\u4ee5\u820d\u96be\u56fe\u6613\u4e86\u3002\u4ed6\u82e5\u662f\u7ec3\u6210\u4e86\u8179\u7f57\u9634\u715e\u529f\u6211\u53ef\u80fd\u5fcc \u4ed6\u51e0\u5206\uff0c\u53ea\u662f\u7384\u9634\u6307\u5c82\u80fd\u4f24\u6211\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u5f97\u91d1\u9010\u6d41\u4ee5\u4e0a\u4e58\u5185\u529f\u76f8\u52a9\uff0c\u4e0d\u8fc7\u7247\u52be\uff0c\u5df2\u662f\u5168\u8eab\u6696\u548c\uff0c\u5bd2\u610f\u5c3d\u5931\u3002\u91d1\u9010\u6d41\u7b11\u9053\uff1a \u201c\u4f60\u80fd\u591f\u8361\u5f00\u4ed6\u7684\u4e00\u6293\uff0c\u5e76\u6ca1\u53d7\u4f24\uff0c\u529f\u592b\u4e5f\u5f88\u4e0d\u9519\u4e86\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u6211\u66fe\u542c\u5f97\u5e08\u7236\u8bf4\u8fc7\uff0c\u4e09\u56db\u5341\u5e74\u4e4b\u524d\uff0c\u90aa\u6d3e\u7684\u5927\u9b54\u5934\u5b5f\u795e\u901a\u66fe\u4ee5\u4fee\u7f57\u9634\u715e\u529f \u79f0\u9738\u6b66\u6797\uff0c\u8fd9\u53ae\u4f1a\u7384\u9634\u6307\uff0c\u83ab\u975e\u662f\u76c2\u795e\u901a\u7684\u5f92\u5b50\u5f92\u5b59\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e5f\u4e0d\u4e00\u5b9a\uff0c\u6211\u542c\u7239\u7239\u8bf4\uff0c\u9664\u4e86\u6211\u7684\u5916\u7956\u7236\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u59d3\u9633\u7684\u5e08\u5f1f\uff0c\u4e5f \u66fe\u628a\u4fee\u7f57\u9634\u715e\u529f\u7ec3\u5230\u4e86\u7b2c\u516b\u91cd\u3002\u4fee\u7f57\u9634\u715e\u529f\u4ece\u5929\u7afa\u4f20\u6765\uff0c\u8bf4\u4e0d\u5b9a\u5728\u5929\u7afa\u4e5f\u8fd8\u6709\u4e2a\u4e2d\u9ad8\u624b\uff0c\u4f20 \u4e0b\u4e86\u6c49\u4eba\u5f1f\u5b50\u3002\u201d\u79e6\u5143\u6d69\u8fd9\u624d\u60f3\u8d77\u91d1\u9010\u6d41\u7684\u6bcd\u4eb2\u8c37\u4e4b\u534e\u6b63\u662f\u5b5f\u795e\u901a\u7684\u5973\u513f\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97 \u4ed6\u77e5\u9053\u7384\u9634\u6307\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u8fd9\u4eba\u5373\u4f7f\u4e0d\u662f\u6211\u5916\u7956\u7236\u7684\u8fd9\u6d3e\u4f20\u4eba\uff0c\u4ed6\u7684\u5e08\u7236\u4e5f\u4e00\u5b9a\u662f\u4e2a\u90aa\u6d3e\u9ad8 \u624b\uff0c\u4ed6\u9664\u4e86\u7384\u9634\u6307\u8fd8\u4f1a\u90aa\u6d3e\u4e2d\u6700\u53e4\u602a\u7684\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u3002\u201d\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u5728\u5410\u8840\u4e4b\u540e\uff0c\u529f\u529b\u53ef \u4ee5\u9661\u589e\u4e00\u500d\u3002\u201d\u79e6\u5143\u6d69\u8fd9\u624d\u660e\u767d\u4e86\u91d1\u9010\u6d41\u521a\u624d\u4f55\u4ee5\u5728\u4f24\u4e86\u654c\u4eba\u4e4b\u540e\uff0c\u53cd\u800c\u81ea\u5df1\u4e5f\u9000\u4e86\u51e0\u6b65\u7684 \u539f\u56e0\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5ffd\u5730\u4fa7\u8033\u7ec6\u542c\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u5c71\u4e0a\u6709\u4eba\u6253\u6597\uff0c\u54b1\u4eec\u51fa\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u4e24\u4eba\u8d70\u5165\u6811\u6797\uff0c\u5faa\u58f0\u89c5\u8ff9\uff0c\u8d70\u4e86\u4e00\u4f1a\uff0c\u53ea\u542c\u5f97\u9ad8\u547c\u9163\u6597\u4e4b\u58f0\u5982\u96f7\u9707\u8033\uff0c\u91d1\u9010\u6d41\u7b11\u9053\uff1a \u201c\u539f\u6765\u662f\u4ef2\u5e2e\u4e3b\u5728\u8fd9\u91cc\u548c\u4eba\u6253\u67b6\u3002\u8fd9\u4eba\u7684\u529f\u592b\u53c8\u6bd4\u521a\u624d\u7684\u90a3\u4eba\u9ad8\u5f97\u591a\u4e86\uff0c\u628a\u4ef2\u5e2e\u4e3b\u5f53\u4e2d\u90a3\u6839 \u7af9\u68d2\u5256\u5f00\u7684\u60f3\u5fc5\u4e5f\u5c31\u662f\u4ed6\u4e86\u3002\u201d \u3000\u3000\u8d70\u5230\u8fd1\u5904\u4e00\u770b\uff0c\u53ea\u89c1\u548c\u4ef2\u957f\u7edf\u6076\u6597\u7684\u4eba\u662f\u4e2a\u4e2d\u5e74\u6c49\u5b50\uff0c\u4e24\u4eba\u7684\u638c\u529b\u90fd\u6781\u521a\u731b\uff0c\u5468\u56f4\u6570\u4e08 \u4e4b\u5185\u6c99\u98de\u77f3\u8d70\u3002\u90a3\u4eba\u7684\u638c\u98ce\u8fd8\u6709\u5947\u5f02\u4e4b\u5904\uff0c\u50cf\u662f\u4ece\u9f13\u98ce\u7089\u55b7\u51fa\u6765\u7684\u70ed\u98ce\u4f3c\u7684\uff0c\u89e6\u4eba\u5982\u70eb\u3002\u79e6 \u5143\u6d69\u529f\u529b\u8f83\u5f31\uff0c\u5728\u70ed\u98ce\u9f13\u8361\u4e4b\u4e2d\u6c57\u4e0b\u5982\u96e8\uff0c\u8fde\u5fd9\u540e\u9000\u3002\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u96f7\u795e\u638c\u5f88 \u662f\u4e0d\u5f31\uff0c\u4e0d\u8fc7\u8fd8\u662f\u4ef2\u5e2e\u4e3b\u80dc\u4ed6\u4e00\u7b79\uff0c\u7528\u4e0d\u7740\u6211\u53bb\u5e2e\u5fd9\u4ed6\u4e86\u3002\u201d \u3000\u3000\u9ad8\u624b\u6bd4\u62fc\uff0c\u773c\u89c2\u56db\u9762\u3001\u8033\u542c\u516b\u65b9\u3002\u91d1\u3001\u79e6\u4e8c\u4eba\u6765\u5230\uff0c\u4ef2\u957f\u7edf\u548c\u90a3\u4e2d\u5e74\u6c49\u5b50\u90fd\u5df2\u53d1\u89c9\u3002\u4f46 \u4ef2\u957f\u7edf\u597d\u50cf\u89c6\u800c\u4e0d\u89c1\uff0c\u4ecd\u5728\u9ad8\u547c\u9163\u6597\u3002\u90a3\u4e2d\u5e74\u6c49\u5b50\u5374\u7981\u4e0d\u4f4f\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u53eb\u5316\u4e0d \u754f\u6211\u7684\u638c\u98ce\uff0c\u5185\u529f\u9020\u8be3\u4e5f\u662f\u5f88\u4e0d\u5f31\u4e86\u3002\u542c\u8bf4\u6587\u9053\u5e84\u5728\u6c5f\u5bb6\u66fe\u5403\u4e86\u4e00\u4e2a\u5c0f\u53eb\u5316\u7684\u4e8f\uff0c\u4e0d\u77e5\u662f\u5426 \u5c31\u662f\u4ed6\uff1f\u201d\u8fd9\u6c49\u5b50\u751f\u6015\u91d1\u9010\u6d41\u662f\u6765\u52a9\u4ef2\u957f\u7edf\u7684\uff0c\u4e00\u6025\u4e4b\u4e0b\uff0c\u62fc\u547d\u62a2\u653b\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u5ffd\u542c\u5f97\u8fdc\u8fdc\u7684\u4e00\u58f0\u957f\u5578\uff0c\u5578\u58f0\u91cd\u6d4a\uff0c\u800c\u4e14\u97f3\u5c3e\u6781\u5f31\uff0c\u6b66\u5b66\u9ad8\u660e\u4e4b\u58eb\uff0c\u4e00\u542c\u4e4b\u4e0b\uff0c \u5c31\u77e5\u9053\u6b64\u4eba\u662f\u53d7\u4e86\u5185\u4f24\uff0c\u6545\u800c\u4e2d\u6c14\u4e0d\u8db3\u3002\u91d1\u9010\u6d41\u6697\u81ea\u597d\u7b11\uff1a\u201c\u8c01\u53eb\u4f60\u7528\u4e86\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\uff0c\u4f24 \u6211\u4e0d\u6210\uff0c\u53cd\u800c\u4f24\u4e86\u81ea\u5df1\u4e86\u3002\u201d\u91d1\u9010\u6d41\u542c\u5f97\u51fa\u53d1\u5578\u8fd9\u4eba\u5c31\u662f\u4ed6\u4eec\u521a\u624d\u5728\u5c01\u5bb6\u6240\u9047\u7684\u90a3\u4e2a\u4eba\uff0c\u60f3 \u5fc5\u548c\u8fd9\u4e2a\u6c49\u5b50\u4e43\u662f\u540c\u4f34\uff0c\u6545\u800c\u5728\u53d7\u4f24\u4e4b\u540e\uff0c\u5411\u540c\u4f34\u6253\u4e2a\u62db\u547c\uff0c\u597d\u53eb\u540c\u4f34\u9003\u8dd1\u7684\u3002 \u3000\u3000\u8fd9\u6c49\u5b50\u542c\u4e86\u5578\u58f0\uff0c\u5fc3\u4e2d\u66f4\u662f\u5403\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u725f\u8001\u4e09\u4e0d\u77e5\u662f\u5426\u5c31\u662f\u53d7\u4e86\u8fd9\u5c0f\u53eb\u5316\u7684\u4f24\uff1f \u5509\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u6211\u4e0d\u8be5\u8ba9\u4ed6\u7559\u5728\u5c01\u5bb6\u7684\u3002\u6211\u53ea\u9053\u53ef\u4ee5\u80dc\u5f97\u8001\u53eb\u5316\uff0c\u54ea\u77e5\u5374\u662f\u5982\u6b64\u624e\u624b\uff0c\u8fde\u8131 \u8eab\u90fd\u96be\u3002\u201d\u5f53\u4e0b\u8c41\u51fa\u4e86\u6027\u547d\uff0c\u8fde\u8fde\u5192\u9669\u8fdb\u62db\uff0c\u5180\u56fe\u4fa5\u5e78\uff0c\u8d25\u4e2d\u6c42\u80dc\u3002\u6fc0\u6597\u4e4b\u4e0b\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c \u8fd9\u6c49\u5b50\u5df2\u662f\u5927\u6c57\u6dcb\u6f13\u3002\u800c\u4ef2\u957f\u7edf\u5219\u4ecd\u662f\u6c14\u5b9a\u795e\u95f2\uff0c\u4ece\u5bb9\u5e94\u4ed8\u3002\u91d1\u9010\u6d41\u60f3\u9053\uff1a\u201c\u8fd9\u6c49\u5b50\u529f\u529b\u4e0d \u5982\u4ef2\u5e2e\u4e3b\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u53ea\u662f\u81ea\u4fc3\u5176\u8d25\u800c\u5df2\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u4ef2\u957f\u7edf\u9739\u96f3\u4f3c\u7684\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u628a\u90a3\u6c49\u5b50\u9707\u5f97\u6709\u5982\u98ce\u4e2d\u4e4b\u70db\u4f3c \u7684\uff0c\u6447\u6447\u6643\u6643\u5730\u9000\u51fa\u4e86\u516d\u4e03\u6b65\u3002\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u4ef2\u5e2e\u4e3b\u53ea\u8981\u518d\u52a0\u4e00\u638c\uff0c\u5c31\u53ef\u4ee5\u628a\u8fd9\u4eba\u6253\u5f97\u91cd \u4f24\uff0c\u54a6\uff0c\u8fd9\u5927\u597d\u7684\u673a\u4f1a\uff0c\u4e3a\u4ec0\u4e48\u4ed6\u5374\u5e73\u767d\u7684\u9519\u8fc7\u4e86\u3002\u201d\u539f\u6765\u4ef2\u957f\u7edf\u5e76\u6ca1\u6709\u4e58\u80dc\u8ffd\u51fb\uff0c\u53cd\u800c\u6536 \u624b\u4e86\u3002 \u3000\u3000\u8fd9\u6c49\u5b50\u4f3c\u4e4e\u4e5f\u5f88\u611f\u610f\u5916\uff0c\u4f38\u5927\u4e86\u53cc\u773c\u671b\u7740\u4ef2\u957f\u7edf\u3002\u4ef2\u957f\u7edf\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6b27\u9633\u575a\uff0c\u4f60\u7684\u96f7 \u795e\u638c\u662f\u5dee\u4e0d\u591a\u53ef\u4ee5\u53ca\u5f97\u4e0a\u4f60\u7236\u5f53\u5e74\u4e86\uff0c\u4f60\u8981\u60f3\u4f24\u6211\u8001\u53eb\u5316\u561b\uff0c\u6050\u6015\u8fd8\u4e0d\u80fd\u591f\u3002\u201d\u539f\u6765\u8fd9\u4e2a\u6c49 \u5b50\u4e43\u662f\u6b27\u9633\u4f2f\u548c\u7684\u513f\u5b50\uff0c\u4e09\u5e74\u4e4b\u524d\u6b27\u9633\u4f2f\u548c\u8d25\u5728\u4ef2\u957f\u7edf\u624b\u4e0b\uff0c\u662f\u7ed9\u4ef2\u957f\u7edf\u5e9f\u4e86\u6b66\u529f\u7684\u3002 \u3000\u3000\u6b27\u9633\u575a\u5598\u8fc7\u53e3\u6c14\uff0c\u8bf4\u9053\u3002\u201d\u4e0d\u9519\uff0c\u6211\u672c\u6765\u4ee5\u4e3a\u53ef\u4ee5\u80dc\u8fc7\u4f60\u7684\uff0c\u73b0\u5728\u77e5\u9053\u662f\u8fd8\u4e0d\u5982\u4f60\u4e86\uff0c \u4f46\u6211\u5728\u58ee\u5e74\uff0c\u4f60\u5df2\u8001\u8fc8\uff0c\u603b\u6709\u4e00\u5929\uff0c\u6211\u4f1a\u628a\u4f60\u6253\u8d25\u3002\u90a3\u65f6\u6211\u4e0d\u6740\u4f60\uff0c\u4e5f\u4f1a\u7167\u6837\u5e9f\u4f60\u6b66\u529f\u7684\u3002 \u4e3a\u4f60\u7740\u60f3\uff0c\u4f60\u82e5\u662f\u6015\u6211\u4e3a\u7236\u62a5\u4ec7\u7684\u6d3b\uff0c\u8fd8\u662f\u4eca\u5929\u628a\u6211\u6740\u4e86\u7684\u597d\uff01\u201d \u3000\u3000\u4ef2\u957f\u7edf\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u628a\u6211\u6253\u8d25\uff0c\u5927\u7ea6\u8fd8\u5f97\u518d\u8fc7\u5341\u5e74\u3002\u90a3\u65f6\u8bf4\u4e0d\u5b9a\u6211\u8001\u53eb\u5316\u5df2 \u7ecf\u2018\u5f52\u4f4d\u2019\u4e86\u3002\u4f60\u6709\u8fd9\u4efd\u8c6a\u6c14\uff0c\u5012\u662f\u6bd4\u4f60\u7684\u7239\u7239\u66f4\u4f3c\u4e00\u4e2a\u6c49\u5b50\u3002\u6211\u5df2\u7136\u5e9f\u4e86\u4f60\u7239\u7239\u7684\u6b66\u529f\uff0c \u5c31\u4e0d\u60f3\u518d\u5e9f\u4f60\u7684\u6b66\u529f\u4e86\u3002\u6211\u8981\u6559\u4f60\u77e5\u9053\uff0c\u8001\u53eb\u5316\u7684\u8fa3\u624b\u662f\u56e0\u4eba\u800c\u65bd\uff0c\u5e76\u975e\u8d76\u5c3d\u6740\u7edd\u3002\u597d\u5427\uff0c \u4f60\u8d70\u5427\uff01\u201d \u3000\u3000\u6b27\u9633\u575a\u9053\uff1a\u201c\u597d\u3001\u4f60\u4eca\u65e5\u653e\u4e86\u6211\uff0c\u6211\u4ed6\u65e5\u62a5\u4ec7\uff0c\u4f60\u53ef\u4e0d\u8981\u540e\u6094\uff01\u201d\u4ef2\u957f\u7edf\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u8001\u53eb\u5316\u505a\u7684\u4e8b\uff0c\u4ece\u6765\u4e0d\u4f1a\u540e\u6094\uff01\u201d\u6b27\u9633\u575a\u5fc3\u4e2d\u6697\u6697\u6b22\u559c\uff0c\u9762\u4e0a\u5374\u662f\u4e1d\u6beb\u4e0d\u9732\u795e\u8272\uff0c\u6de1\u6de1\u8bf4 \u9053\uff1a\u201c\u4f46\u613f\u5341\u5e74\u4e4b\u540e\uff0c\u4f60\u8fd8\u6d3b\u5728\u4eba\u95f4\uff0c\u5426\u5219\u4f60\u4e0d\u540e\u6094\uff0c\u6211\u5374\u8981\u5931\u671b\u4e86\u3002\u201d\u8bf4\u7f62\u626d\u5934\u4fbf\u8d70\u3002 \u3000\u3000\u539f\u6765\u6b27\u9633\u575a\u65e9\u5df2\u77e5\u9053\u4ef2\u957f\u7edf\u7684\u813e\u6c14\uff0c\u8d25\u5728\u4ef2\u957f\u7edf\u7684\u624b\u91cc\uff0c\u4e0e\u5176\u5411\u4ed6\u6c42\u9976\uff0c\u4e0d\u5982\u786c\u5145\u597d \u6c49\u3002\u4ed6\u4e0d\u9886\u4ef2\u957f\u7edf\u7684\u60c5\uff0c\u53e3\u53e3\u58f0\u58f0\u8bf4\u662f\u8fd8\u8981\u62a5\u4ec7\uff0c\u4ef2\u957f\u7edf\u679c\u7136\u53cd\u81ea\u5c06\u4ed6\u653e\u4e86\u3002 \u3000\u3000\u6b27\u9633\u575a\u8d70\u540e\uff0c\u91d1\u9010\u6d41\u4e0e\u79e6\u5143\u6d69\u4e0a\u7684\u4e0e\u4ef2\u957f\u7edf\u76f8\u89c1\u3002\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u53eb\u5316\u60f3\u5fc5\u662f\u966a\u4e86 \u5143\u6d69\u5230\u5c01\u5bb6\u7684\u5427\uff1f\u201d\u91d1\u9010\u6d41\u9053\u3002\u201d\u4e0d\u9519\u3002\u6211\u8fd8\u89c1\u5230\u4f60\u8001\u4eba\u5bb6\u6240\u63d2\u7684\u4e5d\u6839\u7af9\u68d2\u5462\u3002\u201d\u4ef2\u957f\u7edf \u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u662f\u53bb\u627e\u4eba\u5bb6\u7684\u5927\u59d1\u5a18\u7684\u5427\uff1f\u89c1\u7740\u4e86\u6ca1\u6709\uff1f\u201d\u79e6\u5143\u6d69\u6ee1\u9762\u901a\u7ea2\u3002 \u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6ca1\u6709\u89c1\u7740\uff0c\u5374\u5728\u90a3\u4e2a\u5927\u59d1\u5a18\u7684\u623f\u4e2d\u89c1\u7740\u4e86\u4e00\u4e2a\u4f1a\u4f7f\u7384\u9634\u6307\u7684\u6c49\u5b50\uff0c\u4e0d\u77e5\u662f \u8c01\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u4ed6\u662f\u9633\u6d69\u7684\u5f1f\u5b50\uff0c\u540d\u53eb\u9f9a\u5e73\u91ce\uff0c\u662f\u90aa\u6d3e\u4e2d\u4e00\u4e2a\u4e8c\u6d41\u89d2\u8272\uff0c\u4ed6\u7684\u5e08\u7236\u9633\u6d69\u5374 \u662f\u9633\u8d64\u7b26\u7684\u513f\u5b50\u3002\u9633\u8d64\u7b26\u662f\u4f60\u5916\u7956\u7236\u76c2\u795e\u901a\u7684\u5e08\u5f1f\uff0c\u751f\u524d\u4e5f\u66fe\u5c06\u4fee\u7f57\u9634\u715e\u529f\u7ec3\u5230\u7b2c\u516b\u91cd\u7684\u3002 \u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4f55\u4ee5\u4f1a\u540c\u6b27\u9633\u575a\u6765\u5230\u5c01\u5bb6\uff0c\u4f46\u6b27\u9633\u575a\u5374\u7b97\u6709\u70b9\u786c\u4efd\uff0c\u4ed6\u9080\u6211\u51fa\u6765\u51b3\u6218\uff0c\u4e0d\u8981\u9f9a \u5e73\u91ce\u5e2e\u4ed6\u7684\u624b\uff0c\u5426\u5219\u4ed6\u4eec\u82e5\u662f\u4e24\u4eba\u8054\u624b\u6597\u6211\uff0c\u6211\u867d\u7136\u4e0d\u81f3\u8d25\u7ed9\u4ed6\u4eec\uff0c\u4e5f\u662f\u5f88\u4e0d\u5bb9\u6613\u53d6\u80dc\u7684 \u4e86\u3002\u8001\u5b9e\u8bf4\u6211\u4e00\u6765\u662f\u4e3a\u4e86\u4ed6\u7684\u6076\u884c\u4e0d\u591a\uff0c\u4e8c\u6765\u4e5f\u662f\u770b\u5728\u4ed6\u8fd9\u70b9\u786c\u4efd\uff0c\u8fd9\u624d\u653e\u8fc7\u4ed6\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u8fd9\u6837\u8bf4\u6765\uff0c\u8fd9\u4e2a\u59d3\u9f9a\u7684\u5012\u662f\u548c\u6211\u6709\u70b9\u6e0a\u6e90\u5462\u3002\u6211\u5bf9\u4ed6\u5c0f\u65bd\u60e9 \u6212\uff0c\u672a\u4e0b\u6740\u624b\uff0c\u4e5f\u7b97\u662f\u505a\u5f97\u5bf9\u4e86\u3002\u201d\u5f53\u4e0b\u5c06\u521a\u624d\u7684\u4e8b\u8bf4\u7ed9\u4ef2\u957f\u7edf\u542c\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u8bf4\u9053\u3002\u201d\u4f60\u8fd9\u5c0f\u53eb\u5316\u548c\u6211\u8fd9\u8001\u53eb\u5316\u4e00\u6837\u813e\u6c14\uff0c\u603b\u7231\u591a\u7ba1\u95f2\u4e8b\u3002\u54b1\u4eec\u5f7c\u6b64\u5f7c\u6b64\uff0c\u4f60 \u505a\u5f97\u5bf9\u4e5f\u597d\uff0c\u4e0d\u5bf9\u4e5f\u597d\uff0c\u6211\u8001\u53eb\u5316\u90e8\u4e0d\u7ba1\u4f60\u3002\u4f46\u79e6\u5143\u6d69\u505a\u5f97\u4e0d\u5bf9\uff0c\u6211\u8001\u53eb\u5316\u5374\u662f\u8981\u7ba1\u4e86\u3002\u79e6 \u5143\u6d69\uff0c\u4f60\u505a\u9519\u4e86\u4e8b\uff0c\u4f60\u53ef\u77e5\u9519\u4e48\uff1f\u201d\u79e6\u5143\u6d69\u662f\u5185\u521a\u5916\u67d4\u7684\u813e\u6c14\uff0c\u4ed6\u660e\u77e5\u4ef2\u957f\u7edf\u8981\u8d23\u5907\u4ed6\u7684\u662f \u4ec0\u4e48\uff0c\u4f46\u4ed6\u4e0d\u80af\u670d\u6c14\uff0c\u786c\u7740\u5934\u76ae\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u4e0d\u77e5\u9519\u5728\u54ea\u91cc\uff0c\u8bf7\u8001\u524d\u8f88\u6559\u6094\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9762\u8272\u4e00\u7aef\uff0c\u8bf4\u9053\uff1a\u201c\u5c01\u5b50\u8d85\u662f\u66fe\u7ecf\u5f53\u8fc7\u6e05\u5ef7\u536b\u58eb\u7684\u6b66\u6797\u8d25\u7c7b\uff0c\u8001\u53eb\u5316\u5df2\u7ecf\u67e5\u6e05\u4ed6 \u7684\u5e95\u7ec6\u4e86\u3002\u54fc\uff0c\u5929\u4e0b\u591a\u5c11\u5978\u4eba\u5bb6\u7684\u59d1\u5a18\u3002\u4f60\u4e3a\u4ec0\u4e48\u504f\u504f\u8981\u627e\u5c01\u5b50\u8d85\u7684\u5973\u513f\uff1f\u201d\u79e6\u5143\u6d69\u7ea2\u7740\u8138 \u5206\u8fa9\u9053\uff1a\u201c\u5f1f\u5b50\u548c\u8fd9\u4f4d\u5c01\u59d1\u5a18\u5e76\u6ca1\u4ec0\u4e48\uff0c\u8fde\u670b\u53cb\u4e5f\u8c08\u4e0d\u4e0a\u3002\u53ea\u4e0d\u8fc7\u53ef\u601c\u5979\u7684\u5883\u9047\uff0c\u8def\u8fc7\u6b64 \u5730\uff0c\u987a\u4fbf\u53bb\u63a2\u671b\u4e00\u4e0b\u800c\u5df2\u3002\u201d\u4ef2\u957f\u7edf\u534a\u4fe1\u534a\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u7684\u548c\u5979\u5e76\u6ca1\u79c1\u60c5\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5ffd\u9053\uff1a\u201c\u8001\u53eb\u5316\uff0c\u4f60\u8fd9\u6869\u95f2\u4e8b\u53ef\u7ba1\u5f97\u4e0d\u5bf9\u4e86\uff01\u201d \u3000\u3000\u4ef2\u957f\u7edf\u53cc\u773c\u4e00\u7ffb\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8bf4\u4f60\u4f60\u5012\u8bf4\u6211\u3002\u597d\u5440\uff0c\u90a3\u4f60\u5c31\u8bf4\u8bf4\u770b\u6211\u600e\u4e48\u7ba1\u5f97\u4e0d \u5bf9\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u67e5\u6e05\u695a\u4e86\u5c01\u5b50\u8d85\u7684\u5e95\u7ec6\uff0c\u4f46\u4f60\u53ef\u67e5\u6e05\u695a\u4ed6\u5973\u513f\u7684\u5e95\u7ec6\u6ca1\u6709\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u4f60\u8fd9\u4e00\u95ee\u597d\u6ca1\u9053\u7406\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e3a\u4f55\u6ca1\u6709\u9053\u7406\uff1f\u201d\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u4e00\u4e2a \u9ec4\u6bdb\u4e2b\u5934\u6709\u4ec0\u4e48\u5e95\u7ec6\u597d\u67e5\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u624d\u6ca1\u6709\u9053\u7406\uff0c\u5e74\u7eaa\u5c0f\u5c31\u6ca1\u6709\u5e95\u7ec6\u53ef\u67e5\u4e48\uff1f\u4eba\u5bb6 \u4e5f\u662f\u5341\u516b\u3001\u4e8c\u5341\u7684\u5927\u59d1\u5a18\u4e86\uff0c\u4e0d\u89c1\u5f97\u6837\u6837\u4e8b\u60c5\u90fd\u662f\u8ddf\u7740\u7236\u4eb2\u7684\u5475\u3002\u4f60\u77e5\u9053\u5979\u662f\u5978\u4eba\u8fd8\u662f\u574f \u4eba\uff1f\u4e3a\u4ec0\u4e48\u4e00\u70b9\u4e5f\u4e0d\u67e5\u7a76\uff0c\u5c31\u6d3e\u79e6\u5143\u6d69\u7684\u4e0d\u662f\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u4f60\u7259\u5c16\u5634\u5229\uff0c\u6bd4\u4f60\u7239\u7239\u8fd8\u8981\u5389\u5bb3\u3002\u597d\uff0c\u7b97\u6211\u8bf4\u4f60\u4e0d\u8fc7\uff0c\u6211\u662f\u758f\u5ffd\u4e86\u4e9b\u3002\u4f46 \u4f60\u4e5f\u4e0d\u8fc7\u662f\u521d\u5230\u4e2d\u539f\uff0c\u96be\u9053\u4f60\u5c31\u80fd\u6e05\u695a\u7684\u77e5\u9053\u4e86\u5979\u7684\u5e95\u7ec6\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u867d\u672a\u6e05\u695a\uff0c\u4ea6\u5df2\u7a0d\u77e5\u4e00\u4e8c\u3002\u7b2c\u4e00\uff0c\u6211\u77e5\u9053\u5979\u5fc3\u5730\u5584\u826f\uff0c\u548c\u5979\u7239\u7239\u5e76\u4e0d\u4e00 \u6837\uff0c\u5979\u7684\u7239\u7239\u7528\u5343\u65e5\u9189\u4f5c\u5f04\u79e6\u5143\u6d69\uff0c\u5979\u77e5\u9053\u4e86\u5341\u5206\u96be\u8fc7\uff0c\u8fd8\u60f3\u5077\u5979\u7239\u7239\u7684\u89e3\u836f\u7ed9\u79e6\u5143\u6d69\u5462\u3002 \u7b2c\u4e8c\uff0c\u5979\u66fe\u7ecf\u548c\u79e6\u5143\u6d69\u8054\u624b\u6740\u4f24\u4e86\u4e24\u4e2a\u5927\u5185\u536b\u58eb\uff0c\u8fd9\u4e24\u4e2a\u536b\u58eb\u662f\u5c01\u5b50\u8d85\u7684\u65e7\u540c\u4e8b\uff0c\u6765\u627e\u5c01\u5b50 \u8d85\u51fa\u5c71\u7684\u3002\u6211\u548c\u5143\u6d69\u5c31\u662f\u56e0\u6b64\u800c\u6015\u5979\u906d\u53d7\u5c01\u5b50\u8d85\u7684\u78e8\u6298\uff0c\u8fd9\u624d\u6765\u63a2\u542c\u5979\u7684\u6d88\u606f\u7684\uff0c\u5143\u6d69\u662f\u7ed9 \u6211\u62d6\u8ff8\u5c01\u5bb6\u7684\uff0c\u4f60\u8981\u602a\u4ed6\u4e0d\u5982\u602a\u6211\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u542c\u4e86\u91d1\u9010\u6d41\u6240\u8bf4\u7684\u4e8b\u60c5\uff0c\u5c34\u5c2c\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u5012\u662f\u8001\u53eb\u5316\u7684\u4e0d\u662f\u4e86\u3002\u201d\u91d1\u9010 \u6d41\u9053\uff1a\u201c\u672c\u6765\u662f\u4f60\u7684\u9519\u561b\uff0c\u7236\u4eb2\u662f\u7236\u4eb2\uff0c\u5973\u513f\u662f\u5973\u513f\uff0c\u4e24\u4ee3\u600e\u80fd\u6df7\u4e3a\u4e00\u8c08\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u6027\u60c5\u8c6a\u8fc8\uff0c\u6b64\u523b\u4ed6\u77e5\u9053\u81ea\u5df1\u7406\u4e8f\uff0c\u5012\u662f\u5f88\u723d\u5feb\u7684\u627f\u8ba4\u4e86\u3002\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c \u7236\u4eb2\u662f\u7236\u4eb2\uff0c\u5973\u513f\u662f\u5973\u513f\u3002\u4f60\u7684\u5916\u7956\u7236\u76c2\u795e\u901a\u5f53\u5e74\u662f\u5929\u4e0b\u7b2c\u4e00\u9b54\u5934\uff0c\u4f60\u7684\u6bcd\u4eb2\u5374\u662f\u4eba\u6240\u656c\u4f69 \u7684\u5973\u4fa0\u3002\u6211\u8001\u53eb\u5316\u597d\u7cca\u6d82\uff0c\u600e\u4e48\u628a\u8fd9\u4e2a\u4f8b\u5b50\u4e5f\u5fd8\u4e86\uff0c\u4f60\u7684\u7236\u6bcd\u7684\u5a5a\u4e8b\u8fd8\u662f\u6211\u8001\u53eb\u5316\u64ae\u5408\u7684 \u5462\uff01\u201d\u8bf4\u7f62\uff0c\u53c8\u56de\u8fc7\u5934\u5b89\u6170\u79e6\u5143\u6d69\u9053\uff1a\u201c\u597d\uff0c\u7b97\u662f\u6211\u8d23\u5907\u9519\u4e86\uff0c\u4f60\u4ee5\u540e\u548c\u5c01\u59d1\u5a18\u600e\u6837\uff0c\u6211\u90fd \u4e0d\u7ba1\u4f60\u5566\u3002\u201d\u8bf4\u7f62\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u7b11\u8fc7\u4e4b\u540e\uff0c\u4ef2\u957f\u7edf\u95ee\u9053\uff1a\u201c\u5143\u6d69\uff0c\u4f60\u662f\u8981\u56de\u5c71\u5462\uff0c\u8fd8\u662f\u8981\u7ee7\u7eed\u53bb\u627e\u4f60\u90a3\u4f4d\u5c01\u59d1\u5a18\uff1f\u201d\u79e6 \u5143\u6d69\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u5c01\u59d1\u5a18\u6beb\u65e0\u74dc\u845b\uff0c\u8001\u524d\u8f88\u4e0d\u8981\u8bef\u4f1a\u3002\u5982\u4eca\u6211\u77e5\u9053\u4e86\u5979\u5df2\u72ec\u81ea\u51fa \u8d70\uff0c\u6211\u4e5f\u5c31\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u8bf4\u662f\u6beb\u65e0\u722a\u845b\uff0c\u53ef\u662f\u6beb\u65e0\u74dc\u845b\u7684\u4eba\u4f60\u5374\u60e6\u8bb0\u5f97\u5f88\u554a\uff01\u4e0d\u8fc7\uff0c\u4f60\u53ef \u4ee5\u653e\u5fc3\uff0c\u6211\u4e0d\u4f1a\u8bf4\u7ed9\u4f60\u5e08\u7236\u542c\u7684\u3002\u542c\u4f60\u7684\u610f\u601d\uff0c\u4f60\u662f\u8981\u56de\u5c71\u7684\u4e86\uff1f\u201d\u79e6\u5143\u6d69\u9053\uff1a\u201c\u662f\uff0c\u5f1f\u5b50 \u60f3\u56de\u5c71\u5411\u5e08\u7236\u7980\u660e\u4e86\u6b64\u884c\u7ecf\u8fc7\uff0c\u518d\u53bb\u884c\u8d70\u6c5f\u6e56\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u597d\uff0c\u6211\u6b63\u662f\u8981\u5230\u4f60\u5e08\u7236\u90a3\u513f\uff0c\u4f60\u53ef\u4f3c\u548c\u6211\u540c\u884c\u3002\u5c0f\u53eb\u5316\uff0c\u4f60\u5462\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\u3002\u201d\u6211\u8fd9\u5c0f\u53eb\u5316\u6015\u7ed9\u4f60\u8fd9\u8001\u53eb\u5316\u62d8\u675f\uff0c\u8bf7\u6055\u6211\u4e0d\u4e0e\u4f60\u4eec\u540c\u8d70\u4e86\u3002\u201d\u4ef2\u957f\u7edf\u54c8 \u54c8\u7b11\u9053\uff0c\u201c\u4e0d\u9519\uff0c\u4f60\u521a\u521a\u56de\u6765\uff0c\u662f\u8be5\u72ec\u81ea\u95ef\u8361\u6c5f\u6e56\uff0c\u626c\u540d\u7acb\u4e07\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u626c\u540d\u7acb\u4e07\u6211 \u662f\u4e0d\u60f3\u7684\u3002\u4e0d\u8fc7\uff0c\u6211\u4e00\u4e2a\u4eba\u884c\u8d70\uff0c\u8981\u5077\u4e1c\u897f\u7684\u65f6\u5019\uff0c\u5374\u65b9\u4fbf\u8bb8\u591a\uff0c\u81f3\u5c11\u4e0d\u4f1a\u7ed9\u540c\u4f34\u62e6\u963b\u3002\u201d \u4ef2\u957f\u7edf\u5927\u7b11\uff1a\u201c\u4f60\u8fd9\u813e\u6c14\u548c\u4f60\u7239\u7239\u5b8c\u5168\u4e00\u6837\uff0c\u5c31\u662f\u559c\u6b22\u72ec\u5f80\u72ec\u6765\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u3002\u6211\u7239\u7239\u53ef\u5e76\u4e0d\u5e0c\u671b\u6211\u5b8c\u5168\u50cf\u4ed6\uff0c\u4ed6\u662f\u8981\u6211\u968f\u6ce2\u9010\u6d41\u7684\uff0c\u4e0d\u77e5\u9053\u505a \u5f97\u5230\u505a\u4e0d\u5230\uff0c\u4f46\u6211\u5012\u8fdb\u60f3\u8bd5\u8bd5\u548c\u6c5f\u6e56\u4e0a\u4e09\u6559\u4e5d\u6d41\u7684\u670b\u53cb\u90fd\u4ea4\u4ea4\u670b\u53cb\u3002\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u5ffd\u5730\u5411\u79e6 \u5143\u6d69\u626e\u4e2a\u9b3c\u8138\uff0c\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u5047\u9053\u5b66\uff0c\u5fc3\u91cc\u60e6\u8bb0\u4eba\u5bb6\u7684\u59d1\u5a18\uff0c\u5634\u91cc\u53ef\u4e0d\u80af\u627f\u8ba4\u3002 \u563f\uff0c\u563f\uff0c\u8fd9\u5c31\u662f\u7531\u5f97\u7740\u6211\u5e2e\u5fd9\u4f60\u4e86\u3002\u6211\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\uff0c\u4f1a\u66ff\u4f60\u7559\u5fc3\uff0c\u7559\u5fc3\u6253\u542c\u4f60\u90a3\u4f4d\u5c01\u59d1\u5a18 \u7684\u6d88\u606f\u7684\u3002\u201d\u79e6\u5143\u6d69\u7ed9\u4ed6\u8bf4\u5f97\u557c\u7b11\u7686\u975e\uff0c\u7ea2\u7740\u8138\u9053\uff1a\u201c\u91d1\u5144\uff0c\u8bf4\u7b11\u4e86\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4ec0\u4e48 \u8bf4\u7b11\uff0c\u6211\u662f\u9876\u8ba4\u771f\u7684\u3002\u201d \u3000\u3000\u8001\u53eb\u5316\u5c0f\u53eb\u5316\u563b\u563b\u54c8\u54c8\u5730\u7b11\u4e86\u4e00\u4f1a\uff0c\u4ef2\u957f\u7edf\u9053\u3002\u201d\u8bf4\u8001\u5b9e\u8bdd\uff0c\u8001\u53eb\u5316\u8bb8\u591a\u5e74\u6765\u90fd\u6ca1\u6709\u7ed3 \u4ea4\u8fc7\u50cf\u4f60\u8fd9\u6837\u6027\u60c5\u6295\u5408\u7684\u670b\u53cb\u4e86\u3002\u8001\u53eb\u5316\u5b9e\u5728\u820d\u4e0d\u5f97\u548c\u4f60\u8fd9\u5c0f\u53eb\u5316\u5206\u5f00\u3002\u4e0d\u8fc7\uff0c\u5929\u4e0b\u65e0\u4e0d\u6563 \u4e4b\u7b75\u5e2d\uff0c\u54b1\u4eec\u4e5f\u53ea\u597d\u5206\u624b\u4e86\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4e14\u6162\u3002\u5c0f\u53eb\u5316\u8fd8\u6709\u4e00\u6869\u4e8b\u60c5\u60f3\u8bf7\u95ee\u8001\u53eb \u5316\u3002\u201d\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff0c\u53ea\u8981\u4f60\u95ee\uff0c\u8001\u53eb\u5316\u662f\u77e5\u65e0\u4e0d\u8a00\uff0c\u8a00\u65e0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u5f82\u5f95\u5c71\u4e0a\uff0c\u4ee5\u524d\u662f\u6709\u8fc7\u4e00\u4e2a\u4ec0\u4e48\u5929\u9b54\u6559\u7684\uff0c\u4e0d\u77e5\u662f\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u8fd9\u662f\u5c06\u8fd1\u4e09\u5341\u5e74\u524d\u7684\u4e8b\u60c5\u4e86\uff0c\u6559\u4e3b\u662f\u4e2a\u7f8e\u8c8c\u7684\u5973\u5b50\uff0c\u540e\u6765\u5ac1\u7ed9\u4e86\u5979\u4e00\u4e2a\u6027 \u5389\u7684\u526f\u6559\u4e3b\u3002\u4ed6\u4eec\u50cf\u4f60\u7236\u6bcd\u4e00\u6837\uff0c\u4e5f\u65e9\u5df2\u9041\u8ff9\u6d77\u5916\u4e86\u3002\u4f60\u95ee\u8d77\u5929\u9b54\u6559\uff0c\u53ef\u662f\u56e0\u4e3a\u542c\u4f60\u7239\u7239\u8bf4 \u8fc7\u7684\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u7239\u7239\u4ece\u6ca1\u8bf4\u8fc7\uff0c\u6211\u662f\u542c\u5f97\u59ec\u4f2f\u4f2f\u8bf4\u7684\u3002\u5982\u4eca\u6211\u5728\u5f82\u5f95\u5c71\u4e2d\uff0c\u4e00\u65f6\u60f3\u8d77\uff0c \u6240\u4ee5\u95ee\u95ee\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u54e6\uff0c\u8fd9\u5c31\u4e0d\u9519\u4e86\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u9519\uff1f\u201d\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u8bf4\u8d77\u5929\u9b54 \u6559\uff0c\u5012\u662f\u548c\u4f60\u4e00\u5bb6\u6709\u70b9\u5173\u7cfb\u3002\u4f60\u7684\u5927\u5e08\u5144\u7ae5\u5e74\u7684\u65f6\u5019\u66fe\u88ab\u5929\u9b54\u6559\u4e3b\u6349\u53bb\uff0c\u540e\u6765\u59ec\u6653\u98ce\u8dd1\u53bb\u6551 \u4ed6\u51fa\u6765\u7684\u3002\u4f46\u4f60\u7684\u59ec\u4f2f\u4f2f\u6551\u4f60\u5927\u5e08\u5144\u7684\u65f6\u5019\uff0c\u4e5f\u66fe\u5403\u8fc7\u5929\u9b54\u6559\u4e3b\u7684\u4e8f\u3002\u60f3\u6765\u4f60\u7684\u59ec\u4f2f\u4f2f\u8fd8\u672a \u5fd8\u6000\u6b64\u4e8b\u3002\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u59ec\u4f2f\u4f2f\u8bf4\u8fc7\u6b64\u4e8b\uff0c\u4f46\u4ed6\u9690\u7792\u4e86\u4ed6\u66fe\u5403\u8fc7\u4e8f\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u5929\u9b54\u6559\u7684\u8235\u5740\u5728\u5f82\u5f95\u5c71\u7684\u5317\u5cf0\uff0c\u558f\uff0c\u5c31\u662f\u4ece\u8fd9\u91cc\u770b\u8fc7\u53bb\u90a3\u5ea7\u70df\u96fe\u7f2d\u7ed5\u7684\u5c71 \u5cf0\u4e0a\u3002\u4e0d\u8fc7\uff0c\u90a3\u51e0\u5ea7\u5929\u9b54\u6559\u7684\u5efa\u7b51\u65e9\u5df2\u6bc1\u4e8e\u5175\u706b\uff0c\u53ea\u5269\u4e0b\u4e00\u7247\u74e6\u783e\u573a\u4e86\u3002\u5929\u9b54\u6559\u70df\u6d88\u4e91\u6563\u4e4b \u540e\uff0c\u4e8c\u5341\u591a\u5e74\u6765\uff0c\u90a3\u5df2\u662f\u4eba\u8ff9\u7f55\u5230\u4e4b\u5730\uff0c\u6211\u4e5f\u4ece\u6ca1\u6709\u4e0a\u8fc7\u90a3\u513f\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff0c\u201c\u6211\u5012\u60f3\u4e0a\u53bb\u770b\u770b\u3002\u201d\u4ef2\u957f\u7ec3\u9053\uff1a\u201c\u4e00\u7247\u74e6\u783e\u573a\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff1f\u201d\u91d1\u9010\u6d41 \u9053\uff1a\u201c\u8001\u4eba\u5bb6\u559c\u6b22\u6000\u65e7\uff0c\u8fd9\u662f\u59ec\u4f2f\u4f2f\u65e7\u6e38\u4e4b\u5730\uff0c\u6211\u53bb\u51ed\u540a\u4e00\u756a\uff0c\u56de\u53bb\u4e5f\u597d\u548c\u59ec\u4f2f\u4f2f\u804a\u90a3 \u513f\u3002\u201d\u4ef2\u957f\u7edf\u7b11\u9053\u3002\u201d\u5c0f\u8001\u5f1f\uff0c\u4f60\u771f\u662f\u6027\u60c5\u4e2d\u4eba\uff0c\u602a\u4e0d\u5f97\u59ec\u6653\u98ce\u90a3\u4e48\u75bc\u4f60\uff0c\u628a\u4ed6\u7684\u90a3\u770b\u5bb6\u672c \u9886\u90fd\u4f20\u7ed9\u4f60\u4e86\u3002\u4f46\u722c\u8fd9\u6837\u9ad8\u7684\u5c71\u53bb\u770b\u51e0\u7247\u74e6\u783e\uff0c\u6211\u53ef\u6ca1\u6709\u5174\u8da3\uff0c\u6055\u6211\u4e0d\u5949\u966a\u4f60\u4e86\u3002\u201d\u5f53\u4e0b\u53cc \u65b9\u6325\u624b\u9053\u522b\uff0c\u91d1\u9010\u6d41\u72ec\u4e0a\u5317\u5cf0\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8d70\u4e86\u4e00\u7a0b\uff0c\u84e6\u5730\u60f3\u9053\uff1a\u201c\u4ef2\u957f\u7edf\u8bf4\u5929\u9b54\u6559\u548c\u6211\u5bb6\u9887\u6709\u5173\u7cfb\uff0c\u4f46\u4ed6\u53ea\u8bf4\u4e86\u59ec\u4f2f\u4f2f\u548c \u6211\u6c5f\u5e08\u5144\u7684\u4e8b\u60c5\uff0c\u53ea\u80fd\u7b97\u662f\u95f4\u63a5\u7684\u5173\u7cfb\u3002\u4ed6\u597d\u50cf\u6709\u4e9b\u8bdd\u4e0d\u613f\u610f\u8bf4\u51fa\u6765\u7684\u6837\u5b50\uff0c\u4e0d\u77e5\u662f\u4f55\u7f18 \u6545\uff1f\u201d\u4ed6\u53c8\u60f3\u8d77\u5728\u706b\u5c71\u5c9b\u7684\u65f6\u5019\uff0c\u59ec\u4f2f\u4f2f\u4ece\u6765\u6ca1\u6709\u5f53\u7740\u4ed6\u7684\u7236\u6bcd\u9762\u524d\u8bf4\u8fc7\u201c\u5929\u9b54\u6559\u201d\u4e09\u5b57\uff0c \u90a3\u6b21\u59ec\u4f2f\u4f2f\u548c\u4ed6\u8c08\u8d77\u6765\u7684\u65f6\u5019\uff0c\u542c\u5f97\u4ed6\u7239\u7239\u6765\u4e86\uff0c\u5c31\u4e0d\u8bf4\u4e86\u3002\u540e\u6765\u53c8\u544a\u8bc9\u4ed6\u4e0d\u8981\u628a\u5929\u9b54\u6559\u7684 \u4e8b\u60c5\u95ee\u4ed6\u7239\u5a18\u3002 \u3000\u3000\u201c\u59ec\u4f2f\u4f2f\u548c\u6211\u7239\u7239\u662f\u8001\u670b\u53cb\uff0c\u4ed6\u4eec\u4e00\u5411\u662f\u8131\u7565\u5f62\u9ab8\u65e0\u6240\u4e0d\u8c08\u7684\u3002\u4f55\u4ee5\u59ec\u4f2f\u4f2f\u5355\u72ec\u5bf9\u8fd9\u4ef6 \u4e8b\u4f3c\u4e4e\u6709\u6240\u907f\u5fcc\uff1f\u201d\u91d1\u9010\u6d41\u5fc3\u60f3\u3002\u56e0\u800c\u5bf9\u5929\u9b54\u6559\u7684\u597d\u5947\u4e4b\u5fc3\uff0c\u4e5f\u5c31\u66f4\u52a0\u6cb9\u7136\u800c\u751f\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6709\u6240\u4e0d\u77e5\uff0c\u539f\u6765\u5929\u9b54\u6559\u7684\u7956\u5e08\u5389\u80dc\u7537\u662f\u4ed6\u7239\u7239\u91d1\u4e16\u9057\u7684\u65e7\u60c5\u4eba\u3002\u540e\u6765\u548c\u5929\u9b54\u6559\u6559 \u4e3b\u7ed3\u5a5a\u7684\u5389\u590d\u751f\u53c8\u662f\u5389\u80dc\u7537\u7684\u4f84\u513f\u3002\u91d1\u4e16\u9057\u662f\u4e2a\u6700\u91cd\u611f\u60c5\u7684\u4eba\uff0c\u4ed6\u66fe\u4e3a\u4e86\u5389\u80dc\u7537\u7684\u7f18\u6545\uff0c\u628a \u4ed6\u548c\u8c37\u4e4b\u534e\u7684\u5a5a\u671f\u62d6\u8fdf\u4e86\u5c06\u8fd1\u4e8c\u5341\u5e74\u3002\u59ec\u6653\u98ce\u4e3a\u4e86\u4e0d\u613f\u89e6\u8d77\u4ed6\u7684\u4f24\u611f\uff0c\u6545\u6b64\u5728\u4ed6\u4eec\u592b\u5987\u9762\u524d \u662f\u4ece\u6765\u4e0d\u63d0\u201c\u5929\u9b54\u6559\u201d\u4e09\u5b57\u7684\u3002\u91d1\u9010\u6d41\u4e0d\u77e5\u5176\u4e2d\u7f18\u6545\uff0c\u5c31\u96be\u514d\u8d77\u4e86\u4e00\u5c42\u795e\u79d8\u4e4b\u611f\uff0c\u56e0\u800c\u4e5f\u5c31 \u60f3\u53bb\u770b\u770b\u5929\u9b54\u6559\u7684\u65e7\u5740\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e0a\u5230\u5c71\u9876\uff0c\u5df2\u7ecf\u662f\u5165\u9ed1\u7684\u65f6\u5206\u4e86\uff0c\u53ea\u89c1\u679c\u7136\u662f\u4e00\u7247\u74e6\u783e\u3002\u4f46\u6709\u4e00\u95f4\u5c4b\u5b50\u867d\u7136\u7834 \u70c2\uff0c\u5899\u5934\u4e5f\u957f\u6ee1\u82cd\u82d4\u5374\u8fd8\u7b97\u5f97\u662f\u6bd4\u8f83\u5b8c\u6574\u7684\u5efa\u7b51\u7269\u3002\u5c4b\u4e2d\u900f\u51fa\u706b\u5149\uff0c\u663e\u7136\u91cc\u9762\u6709\u4eba\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u9053\uff1a\u201c\u5947\u602a\uff0c\u6211\u5012\u8981\u770b\u770b\u662f\u4ec0\u4e48\u4eba\u8eb2\u5728\u8fd9\u513f\uff1f\u201d\u5f53\u4e0b\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u6084\u65e0\u58f0\u5230 \u4e86\u5c4b\u5b50\u540e\u7a97\uff0c\u5077\u5077\u5f20\u671b\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u4e09\u5341\u5c81\u5de6\u53f3\u7684\u53cc\u5b50\uff0c\u5750\u5728\u5730\u4e0a\u70e4\u706b\u3002\u8fd9\u6c49\u5b50\u8eab\u7a7f\u4e00\u4ef6\u655d\u65e7\u7684\u957f\u886b\uff0c\u50cf\u4e00\u4e2a\u4e09\u5bb6 \u6751\u6559\u8499\u9986\u7684\u7a77\u4e66\u751f\uff0c\u6a21\u6837\u513f\u751a\u662f\u5bd2\u9178\u3002\u5c4b\u4e2d\u56db\u58c1\u8427\u6761\uff0c\u53ea\u6709\u4e00\u4e2a\u5927\u949f\u8986\u5728\u5730\u4e0a\u3002\u539f\u6765\u8fd9\u662f\u5929 \u9b54\u6559\u7684\u795e\u5802\uff0c\u7ecf\u8fc7\u4e86\u5175\u706b\u4e4b\u52ab\uff0c\u795e\u50cf\u65e9\u5df2\u6bc1\u706d\uff0c\u4f9b\u684c\u4e5f\u65e9\u5df2\u88ab\u4eba\u5f53\u4f5c\u67f4\u706b\u70e7\u4e86\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u4e0d\u77e5\u5916\u9762\u6709\u4eba\uff0c\u6b64\u65f6\u6b63\u4ece\u8eab\u4e0a\u638f\u51fa\u4e00\u628a\u4e1c\u897f\u653e\u5728\u5730\u4e0a\u3002\u91d1\u9010\u6d41\u4e00\u770b\uff0c\u4e0d\u89c9\u5403\u4e86\u4e00 \u60ca\u3002\u539f\u6765\u90a3\u5806\u4e1c\u897f\u4e4b\u4e2d\uff0c\u6709\u4e00\u4e32\u73cd\u73e0\uff0c\u5b9d\u5149\u5916\u9732\uff0c\u663e\u7136\u662f\u4ef7\u503c\u8fde\u57ce\u7684\u5b9d\u7269\u3002\u53e6\u5916\u7684\u4e1c\u897f\u5219\u662f \u5341\u51e0\u6587\u94dc\u94b1\u548c\u4e00\u628a\u5315\u9996\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u662f\u4e2a\u5077\u513f\uff0c\u6562\u60c5\u662f\u5728\u8fd9\u91cc\u68c0\u89c6\u8d43\u7269\u3002\u201d\u6c49\u5b50\u628a\u73cd\u73e0\u4e32\u62ff\u8d77\u6765\u770b\u4e86\u53c8 \u770b\uff0c\u6447\u4e86\u6447\u5934\uff0c\u53d1\u51fa\u4e00\u58f0\u82e6\u7b11\u3002\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u4ed6\u597d\u50cf\u5acc\u5077\u5f97\u4e0d\u591f\u5462\uff0c\u4e5f\u672a\u514d\u592a\u8d2a\u5fc3\u4e86\u3002\u201d \u8fd9\u6c49\u5b50\u7a7f\u5f97\u5bd2\u9178\uff0c\u888b\u91cc\u53ea\u6709\u5341\u51e0\u6587\u94dc\u94b1\uff0c\u5374\u6709\u8fd9\u6837\u4e00\u4e32\u4ef7\u503c\u8fde\u57ce\u7684\u73e0\u94fe\uff0c\u96be\u602a\u91d1\u9010\u6d41\u4ee5\u4e3a\u4ed6 \u662f\u4e2a\u5077\u513f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u9053\uff1a\u201c\u8fd9\u4e2a\u5077\u513f\u5012\u53ef\u4ee5\u4ea4\u4ea4\u3002\u201d\u4e8e\u662f\u201c\u563f\u201d\u7684\u4e00\u7b11\uff0c\u63a8\u5f00\u95e8\u5c31\u8d70\u8fdb\u53bb\u3002\u90a3\u4eba\u5927 \u5403\u4e00\u60ca\uff0c\u614c\u5fd9\u628a\u4e1c\u897f\u6536\u597d\uff0c\u53cc\u773c\u77aa\u7740\u91d1\u9010\u6d41\u3002\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u522b\u60ca\u614c\uff0c\u54b1\u4eec\u662f\u540c \u9053\u3002\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u662f\u5077\u513f\uff0c\u6211\u4e5f\u662f\u5077\u513f\u3002\u5e72\u54b1\u4eec\u8fd9\u4e00\u884c\u7684\uff0c \u867d\u7136\u6709\u4e9b\u4eba\u4e0d\u8bb2\u6c5f\u6e56\u9053\u4e49\uff0c\u4f46\u6211\u53ef\u662f\u4e0d\u4f1a\u9ed1\u5403\u9ed1\u7684\uff0c\u6240\u4ee5\u4f60\u4e0d\u7528\u63d0\u9632\u6211\u3002\u201d \u3000\u3000\u8fd9\u6c49\u5b50\u6697\u6697\u597d\u7b11\uff0c\u4f46\u5fc3\u91cc\u5219\u5728\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u53eb\u5316\u8d70\u4e86\u8fdb\u6765\uff0c\u6211\u624d\u77e5\u9053\u3002\u522b\u7684\u672c\u9886\u4e0d\u77e5\u5982 \u4f55\uff0c\u53ea\u662f\u8fd9\u95e8\u8f7b\u529f\u5c31\u5df2\u7ecf\u5728\u6211\u4e4b\u4e0a\u4e86\u3002\u5e78\u597d\u4e0d\u662f\u6211\u7684\u5bf9\u5934\u3002\u597d\u5427\uff0c\u4ed6\u628a\u6211\u5f53\u4f5c\u5077\u513f\uff0c\u6211\u5c31\u7b97 \u662f\u4e2a\u5077\u513f\u5427\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u62db\u4e86\u62db\u624b\uff0c\u8bf4\u9053\u3002\u201d\u96be\u5f97\u540c\u9053\u5230\u6765\uff0c\u6055\u6211\u65e0\u7269\u62db\u5f85\uff0c\u4f60\u5750\u4e0b\u6765\u70e4\u70e4\u706b\uff0c\u6211\u8bf7\u4f60\u5403 \u70e4\u5c71\u828b\u3002\u201d\u91d1\u9010\u6d41\u4e5f\u4e0d\u5ba2\u6c14\uff0c\u5927\u9a6c\u91d1\u5200\u7684\u5c31\u5750\u4e86\u4e0b\u6765\uff0c\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5514\uff0c\u597d\u9999\uff0c \u597d\u9999\uff01\u4f46\u53ea\u6015\u6709\u4e2a\u5c71\u828b\u70e4\u7126\u4e86\u3002\u6211\u809a\u5b50\u6b63\u997f\uff0c\u4f60\u62ff\u6765\u5427\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u62e8\u5f00\u70ad\u7070\uff0c\u53d6\u51fa\u4e00\u4e2a\u5c71\u828b\uff0c\u8bf4\u9053\uff1a\u201c\u70eb\u624b\u5f97\u5f88\uff0c\u4f60\u5c0f\u5fc3\u63a5\u4e86\u3002\u201d\u4ed6\u5750\u5728\u91d1\u9010\u6d41\u5bf9 \u9762\uff0c\u4e2d\u95f4\u53ea\u9694\u7740\u4e00\u5806\u706b\uff0c\u4f38\u624b\u53ef\u53ca\uff0c\u4f46\u4ed6\u5374\u628a\u5c71\u828b\u629b\u4e86\u8fc7\u53bb\uff0c\u800c\u4e0d\u662f\u76f4\u63a5\u9012\u7ed9\u91d1\u9010\u6d41\u3002 \u3000\u3000\u91d1\u9010\u6d41\u77e5\u9053\u4ed6\u662f\u6709\u5fc3\u76f8\u8bd5\uff0c\u5f53\u4e0b\u628a\u624b\u4e00\u62db\uff0c\u5c71\u828b\u5c31\u843d\u4e0b\u4ed6\u7684\u638c\u5fc3\uff0c\u91d1\u9010\u6d41\u54ac\u4e86\u4e00\u53e3\uff0c\u8bf4 \u9053\uff1a\u201c\u867d\u7136\u7126\u4e86\u4e00\u4e9b\uff0c\u5473\u9053\u5f88\u662f\u4e0d\u9519\uff0c\u591a\u8c22\u4f60\u4e86\u3002\u201d\u8fd9\u6c49\u5b50\u672c\u6765\u8fd8\u6709\u70b9\u5bb3\u6015\uff0c\u5bb3\u6015\u91d1\u9010\u6d41\u63a5 \u4e0d\u8d77\u4ed6\u8fd9\u5c71\u828b\uff0c\u53ef\u80fd\u53d7\u4e86\u4f24\u7684\uff0c\u6b64\u65f6\u4e0d\u89c9\u5fc3\u5185\u6697\u60ca\uff1a\u201c\u8fd9\u5c0f\u53eb\u5316\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u600e\u7684\u5374\u6709\u5982\u6b64\u672c \u9886\uff1f\u770b\u6765\u4ed6\u7684\u5185\u529f\u9020\u8be3\u4e5f\u662f\u5728\u6211\u4e4b\u4e0a\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u4eca\u5929\u624b\u6c14\u5f88\u4e0d\u9519\u554a\uff0c\u5077\u4e86\u4ec0\u4e48\u4eba\u5bb6\uff1f\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u662f\u4e2a\u4e3a\u5bcc\u4e0d\u4ec1\u7684\u4eba \u5bb6\uff0c\u6211\u672c\u4ee5\u4e3a\u8fd8\u53ef\u4ee5\u591a\u4e9b\u6536\u83b7\u7684\uff0c\u54ea\u77e5\u53ea\u5230\u624b\u4e86\u4e00\u4e32\u73e0\u94fe\uff0c\u5c31\u7ed9\u90a3\u5bb6\u4eba\u5bb6\u53d1\u89c9\uff0c\u6211\u53ea\u597d\u614c\u5fd9 \u9003\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4e3a\u4eba\u4e0d\u53ef\u592a\u8d2a\uff0c\u8fd9\u4e32\u73e0\u94fe\u4e5f\u591f\u4f60\u5403\u559d\u4e0d\u5c3d\u7684\u4e86\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u8001\u5f1f\u6b64\u8a00\u5dee\u77e3\uff0c\u82e5\u7136\u53ea\u662f\u4e3a\u4e86\u81ea\u5df1\u7684\u5403\u559d\uff0c\u6211\u4f55\u82e6\u8d39\u5982\u8bb8\u6c14\u529b\u53bb\u5077\u4e00\u6761\u73e0 \u94fe\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u662f\u4e00\u4f4d\u52ab\u5bcc\u6d4e\u8d2b\u7684\u4fa0\u76d7\uff0c\u5931\u656c\uff0c\u5931\u656c\u3002\u201d\u90a3\u6c49\u5b50\u7b11\u9053\uff1a\u201c\u4fa0\u5b57 \u662f\u8bf4\u4e0d\u7684\uff0c\u4f46\u6211\u53ef\u4e0d\u613f\u610f\u53ea\u56fe\u5403\u559d\u800c\u5077\u4e1c\u897f\uff0c\u8fd9\u662f\u53e6\u6709\u539f\u56e0\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u54e6\uff0c\u4ec0\u4e48\u539f\u56e0\uff0c\u5012\u8981\u8bf7\u6559\u3002\u201d\u90a3\u7a77\u4e66\u751f\u6a21\u6837\u7684\u6c49\u5b50\u7b11\u9053\uff1a\u201c\u4f60\u662f\u65b0\u5165\u884c\u7684 \u5427\uff1f\u4f60\u4e0d\u77e5\u9053\u5e72\u54b1\u4eec\u5077\u513f\u8fd9\u4e00\u884c\u7684\uff0c\u5e72\u4e45\u4e86\u5c31\u4f1a\u4e0a\u763e\u7684\uff0c\u82e5\u7136\u53ea\u56fe\u6e29\u9971\uff0c\u635e\u4e86\u4e00\u7968\u5c31\u91d1\u76c6\u6d17 \u624b\u7684\u8bdd\uff0c\u90a3\u5c82\u4e0d\u662f\u8f9c\u8d1f\u4e86\u54b1\u4eec\u597d\u4e0d\u5bb9\u6613\u624d\u7ec3\u6210\u7684\u8fd9\u526f\u8eab\u624b\u4e86\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u5927\u7b11\uff1a\u201c\u8bf4\u5f97\u6709\u7406\uff01\u6211\u7684\u59ec\u4f2f\u4f2f\u4e5f\u662f\u8fd9\u6837\u8bf4\u7684\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u59ec\u4f2f\u4f2f\u4e5f\u662f\u5e72\u54b1\u4eec\u8fd9\u884c\u7684\u5417\uff1f\u4e0d\u77e5\u662f\u54ea\u4f4d\u8001\u524d\u8f88\uff1f\u201d\u91d1 \u9010\u6d41\u9053\uff1a\u201c\u4ed6\u662f\u54b1\u4eec\u8fd9\u884c\u7684\u8001\u79df\u5b97\u3002\u795e\u5077\u59ec\u6653\u98ce\u7684\u540d\u5b57\u4f60\u542c\u8fc7\u5417\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u4f59\u751f\u4e5f\u665a\uff0c\u59ec\u8001\u524d\u8f88\u6211\u6ca1\u89c1\u8fc7\uff0c\u4f46\u5df2\u662f\u5fc3\u4eea\u5df2\u4e45\u7684\u4e86\u3002\u8001\u5f1f\u662f\u59ec\u8001\u524d\u8f88\u7684 \u95e8\u4eba\u4e48\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4ed6\u7684\u5f92\u5f1f\uff0c\u4e0d\u8fc7\uff0c\u4e5f\u66fe\u8ddf\u4ed6\u5b66\u8fc7\u5077\u4e1c\u897f\u7684\u672c\u9886\u3002\u201d\u90a3\u6c49\u5b50\u89c1 \u91d1\u9010\u6d41\u5982\u6b64\u5e74\u8f7b\uff0c\u5bf9\u4ed6\u7684\u8bdd\u534a\u4fe1\u534a\u7591\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u6b63\u8981\u8bf7\u6559\u91d1\u9010\u6d41\u7684\u59d3\u540d\uff0c\u91d1\u9010\u6d41\u5ffd\u9053\uff1a\u201c\u4f60\u542c\uff0c\u597d\u50cf\u662f\u53c8\u6709\u4ec0\u4e48\u4eba\u6765\u4e86\uff1f\u53ef\u662f\u4f60 \u7684\u62cd\u624b\u4f19\u4f34\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u7ad6\u8d77\u8033\u6735\u4e00\u542c\uff0c\u9762\u8272\u767b\u65f6\u5927\u53d8\uff0c\u8bf4\u9053\u3002\u201d\u6765\u7684\u6050\u6015\u662f\u8981\u6765\u6349\u62ff\u6211\u7684\u3002\u8001\u5f1f\uff0c\u4f60\u5e2e \u6211\u4e2a\u5fd9\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u600e\u4e48\u5e2e\u6cd5\uff1f\u201d\u5fc3\u60f3\uff1a\u201c\u6253\u67b6\u5bb9\u6613\uff0c\u53ef\u662f\u6211\u8fd8\u672a\u77e5\u9053\u4f60\u7684\u5e95\u7ec6\uff0c\u600e\u80fd \u5c31\u542c\u4fe1\u4f60\u4e00\u9762\u4e4b\u8f9e\u3002\u201d \u3000\u3000\u90a3\u53cc\u5b50\u7ad9\u4e86\u8d77\u6765\uff0c\u63d0\u8d77\u4e86\u5730\u4e0a\u90a3\u53e3\u5927\u949f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6253\u4e0d\u8fc7\u4ed6\u4eec\uff0c\u53ea\u5f97\u6682\u8eb2\u4e00\u8eb2\u4e86\u3002\u4ed6 \u4eec\u8d70\u4e86\uff0c\u4f60\u653e\u6211\u51fa\u6765\u3002\u201d\u8bf4\u7f62\uff0c\u94bb\u4e86\u8fdb\u53bb\uff0c\u628a\u949f\u653e\u4e0b\u3002\u4ed6\u89c1\u8fc7\u91d1\u9010\u6d41\u7684\u672c\u9886\uff0c\u77e5\u9053\u91d1\u9010\u6d41\u662f \u53ef\u4ee5\u63d0\u5f97\u8d77\u8fd9\u53e3\u5927\u949f\u7684\u3002 \u3000\u3000\u8fd9\u53e3\u5927\u949f\u4f30\u8ba1\u6709\u4e94\u516d\u767e\u65a4\u4e4b\u91cd\uff0c\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u6c49\u5b50\u7684\u6c14\u529b\u5012\u4e5f\u4e0d\u5c0f\uff0c\u4f46\u4ed6\u5185\u529f \u5916\u529f\u90fd\u9887\u6709\u9020\u8be3\uff0c\u5374\u8fd8\u8fd9\u6837\u5bb3\u6015\uff0c\u4e0d\u77e5\u8fd9\u4e24\u4e2a\u6765\u6349\u4ed6\u7684\u4eba\uff0c\u53c8\u662f\u4ec0\u4e48\u6837\u7684\u5389\u5bb3\u89d2\u8272\uff1f\u201d\u53c8 \u60f3\uff1a\u8fd9\u6c49\u5b50\u548c\u6211\u521d\u6b21\u89c1\u9762\u5c45\u7136\u5c31\u8fd9\u6837\u76f8\u4fe1\u6211\uff0c\u6211\u5012\u4e0d\u80fd\u4e0d\u628a\u4ed6\u5f53\u4f5c\u670b\u53cb\u770b\u5f85\u4e86\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u90a3\u4e24\u4e2a\u4eba\u5df1\u8d70\u4e86\u8fdb\u6765\uff0c\u4e00\u4e2a\u662f\u9053\u58eb\uff0c\u624b\u63d0\u4e00\u652f\u4f5b\u5c18\uff0c\u53e6\u4e00\u4eba\u5219\u662f\u624b\u91cc\u62ff\u7740\u9b3c \u5934\u5200\u7684\u6c49\u5b50\u3002\u9053\u58eb\u53cc\u76ee\u70af\u70af\u6709\u795e\uff0c\u91d1\u9010\u6d41\u4e00\u770b\u5c31\u77e5\u4ed6\u662f\u5185\u5bb6\u9ad8\u624b\u3002\u90a3\u62ff\u7740\u9b3c\u5934\u5200\u7684\u6c49\u5b50\u9762\u8272 \u8721\u9ec4\uff0c\u4e24\u9762\u592a\u9633\u7a74\u575f\u8d77\uff0c\u770b\u6765\u4e5f\u662f\u4e2a\u90aa\u6d3e\u9ad8\u624b\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fc7\u8def\u7684\u5c0f\u53eb\u5316\u3002\u201d\u90a3\u6c49\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u8fc7\u8def\u7684 \u5c0f\u53eb\u5316\u5374\u600e\u7684\u5230\u8fd9\u8352\u5c71\u91ce\u5e99\u6765\u4e86\uff1f\u201d\u91d1\u9010\u6d41\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u662f\u4ec0\u4e48\u4eba\uff0c\u4f60\u51ed\u4ec0\u4e48\u6765\u7ba1\u6211\uff1f\u6211 \u559c\u6b22\u5728\u8fd9\u91cc\u8fc7\u591c\u4f60\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u90a3\u9752\u8863\u9053\u58eb\u770b\u51fa\u91d1\u9010\u6d41\u662f\u4e2a\u4e0d\u5bfb\u5e38\u7684\u4eba\u7269\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u54e5\uff0c\u4f60\u522b\u52a8\u6c14\u3002\u6211\u4eec\u53ea\u662f\u60f3\u5411\u4f60 \u6253\u542c\u4e00\u4e2a\u4eba\uff0c\u6709\u4e2a\u7a77\u9178\u6a21\u6837\u7684\u6c49\u5b50\uff0c\u521a\u624d\u662f\u5728\u8fd9\u91cc\u7684\u5427\uff1f\u4f60\u77e5\u9053\u4ed6\u8eb2\u5230\u54ea\u513f\u53bb\u4e86\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u7a77\u9178\uff1f\u6ca1\u6709\u89c1\u8fc7\uff01\u201d\u90a3\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\u7528\u9b3c\u5934\u5200\u62e8\u62d4\u706b\u5806\uff0c\u51b7 \u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u53eb\u5316\u5012\u4f1a\u8bf4\u8c0e\uff0c\u53ef\u60dc\u9a97\u4e0d\u4e86\u6211\u3002\u521a\u624d\u8fd8\u5728\u8fd9\u91cc\u548c\u4f60\u70d8\u828b\u5934\u5403\u7684\u4eba\u662f\u8c01\uff1f\u201d \u91d1\u9010\u6d41\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\uff0c\u4f60\u7ba1\u4e0d\u7740\uff01\u6211\u77e5\u9053\u4e5f\u4e0d\u544a\u8bc9\u4f60\uff01\u201d\u90a3\u53cc\u5b50\u5927\u6012\uff0c\u5c31\u8981\u53d1\u4f5c\uff0c\u9752\u8863\u9053 \u58eb\u529d\u9053\uff1a\u201c\u770b\u8fd9\u5149\u666f\uff0c\u90a3\u7a77\u9178\u60f3\u5fc5\u5c31\u5728\u9644\u8fd1\uff0c\u54b1\u4eec\u51fa\u53bb\u641c\u641c\u3002\u4f55\u5fc5\u5f85\u5728\u8fd9\u91cc\u548c\u4e00\u4e2a\u5c0f\u53eb\u5316\u751f \u6c14\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u5148\u641c\u8fd9\u91cc\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u8fd8\u672a\u8d70\u51fa\u8fd9\u95f4\u5c4b\u5b50\u5462\uff01\u201d \u3000\u3000\u8fd9\u5ea7\u7834\u5e99\u5e76\u6ca1\u591a\u4f59\u7684\u4e1c\u897f\uff0c\u4e00\u773c\u5c31\u53ef\u4ee5\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002\u8fd9\u6c49\u5b50\u662f\u56e0\u4e3a\u6c14\u4e0d\u8fc7\u91d1\u9010\u6d41\uff0c\u4e0d \u613f\u5c31\u653e\u8fc7\u4e86\u4ed6\uff0c\u6545\u800c\u8981\u7559\u5728\u5c4b\u5185\uff0c\u60f3\u501f\u4e2a\u9898\u76ee\u5411\u91d1\u9010\u6d41\u53d1\u4f5c\u7684\u3002\u90a3\u9752\u8863\u9053\u58eb\u5374\u4e0d\u613f\u60f9\u4e8b\uff0c\u5728 \u7834\u70c2\u7684\u4f9b\u6848\u540e\u9762\u5f20\u671b\u4e00\u4e0b\uff0c\u4fbf\u9053\u3002\u201d\u9b3c\u5f71\u4e5f\u6ca1\u4e00\u4e2a\uff0c\u54b1\u4eec\u8fd8\u662f\u8d70\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e00\u624b\u652f\u5934\uff0c\u61d2\u6d0b\u6d0b\u5730\u8eba\u5728\u5730\u4e0a\uff0c\u4e00\u624b\u5265\u828b\u5934\u6765\u5403\uff0c\u7b11\u9053\u3002\u201d\u5bf9\u5566\uff0c\u4f60\u4eec\u8fd8\u662f\u5feb\u5feb \u7684\u7ed9\u6211\u6eda\u5f00\u7684\u597d\u3002\u6211\u5403\u9971\u4e86\u5c31\u8981\u7761\u7684\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u6012\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u8fd9\u5c0f\u53eb\u5316\u80c6\u6562\u5bf9\u6211\u65e0\u793c\uff0c\u6211\u4e0d\u8981\u4f60\u6eda\u4f60\u8981\u6211\u6eda\uff0c\u54fc\uff0c\u54fc\uff0c\u60f9\u5f97 \u8001\u5b50\u751f\u6c14\u2026\u2026\u201d\u91d1\u9010\u6d41\u4fa7\u76ee\u659c\u89c6\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u600e\u6837\u2026\u2026\u201d \u3000\u3000\u90a3\u9752\u8863\u9053\u58eb\u62c9\u4e86\u540c\u4f34\u4e00\u628a\uff0c\u8bf4\u9053\uff1a\u201c\u7126\u8001\u4e09\uff0c\u548c\u5c0f\u53eb\u5316\u5435\u5634\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u8d70\u5427\uff01\u201d\u8fd9\u9752 \u8863\u9053\u58eb\u662f\u4e2a\u8001\u4e8e\u6c5f\u6e56\u7684\u5927\u884c\u5bb6\uff0c\u4ed6\u89c1\u91d1\u9010\u6d41\u8fd9\u4e00\u526f\u6ee1\u4e0d\u5728\u4e4e\u7684\u6001\u5ea6\uff0c\u5fc3\u4e2d\u5012\u662f\u6709\u70b9\u60ca\u7591\u4e0d \u5b9a\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u53eb\u5316\u80c6\u6562\u5982\u6b64\uff0c\u5b9a\u6709\u6240\u6043\u3002\u4ed6\u672c\u8eab\u7684\u6b66\u529f\uff0c\u6216\u8005\u4e0d\u600e\u4e48\u9ad8\uff0c\u4f46\u4ed6\u7684\u5e08\u7236\u5b9a \u662f\u5927\u6709\u6765\u5934\u7684\u4eba\u7269\uff0c\u201d\u9752\u8863\u9053\u571f\u529d\u540c\u4f34\u7684\u53e3\u6c14\u4f3c\u4e4e\u662f\u770b\u4e0d\u8d77\u91d1\u9010\u6d41\uff0c\u5b9e\u5728\u5219\u662f\u9887\u6709\u987e\u8651\uff0c\u4e0d \u60f3\u6811\u654c\u3002 \u3000\u3000\u9752\u8863\u9053\u571f\u662f\u4ed6\u4eec\u90a3\u4e00\u5e2e\u7684\u5927\u54e5\uff0c\u4f7f\u9b3c\u5934\u5200\u7684\u6c49\u5b50\u4e0d\u6562\u4e0d\u542c\u4ed6\u7684\u8bf4\u8bdd\u3002\u5728\u4ed6\u8fde\u62c9\u5e26\u529d\u4e4b \u4e0b\uff0c\u53ea\u597d\u60bb\u60bb\u5730\u79bb\u5f00\u3002\u53ef\u662f\u5728\u4ed6\u7ecf\u8fc7\u90a3\u4e2a\u5927\u949f\u7684\u65f6\u5019\uff0c\u5374\u53c8\u505c\u4e0b\u4e86\u811a\u6b65\uff0c\u6572\u4e86\u51e0\u4e0b\u94dc\u949f\u3002 \u3000\u3000\u9752\u8863\u9053\u58eb\u7b11\u9053\uff1a\u201c\u60f3\u6765\u8fd9\u7a77\u9178\u4e0d\u4f1a\u662f\u8eb2\u5728\u91cc\u9762\u7684\u3002\u201d\u539f\u6765\u9752\u8863\u9053\u58eb\u867d\u7136\u5bf9\u91d1\u9010\u6d41\u6709\u6240\u987e \u5fcc\uff0c\u4f46\u5bf9\u91d1\u9010\u6d41\u7684\u4f30\u8ba1\u8fd8\u662f\u4e0d\u8db3\uff0c\u5fc3\u91cc\u5728\u60f3\uff1a\u201c\u8fd9\u7a77\u9178\u82e5\u662f\u85cf\u5728\u94dc\u949f\u4e4b\u5185\uff0c\u5c0f\u53eb\u5316\u7684\u6c14\u529b\u600e \u80fd\u63d0\u5f97\u8d77\u8fd9\u53e3\u94dc\u949f\uff0c\u6ca1\u4eba\u628a\u90a3\u7a77\u9178\u653e\u51fa\u53bb\uff0c\u4ed6\u4e0d\u662f\u8981\u6d3b\u751f\u751f\u7684\u997f\u6b7b\u4e86\uff1f\u8fd9\u7a77\u9178\u662f\u4e2a\u673a\u7075\u9b3c\uff0c \u51b3\u4e0d\u4f1a\u8fd9\u6837\u7b28\u7684\uff01\u201d \u3000\u3000\u90a3\u6c49\u5b50\u4f59\u6012\u672a\u6d88\uff0c\u7528\u9b3c\u5934\u5200\u53c8\u91cd\u91cd\u5730\u6572\u4e86\u51e0\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u82e5\u662f\u85cf\u5728\u91cc\u9762\uff0c\u6211\u5c31\u9707\u804b\u4ed6 \u7684\u8033\u6735\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7ffb\u4e86\u4e2a\u8eab\uff0c\u534a\u5750\u534a\u8eba\u7684\u659c\u501a\u8eab\u5b50\u8bf4\u9053\uff1a\u201c\u5582\uff0c\u6211\u8bf4\u8fc7\u6211\u8981\u7761\u89c9\u7684\uff0c\u6211\u4e0d\u559c\u6b22\u6709\u4eba \u9a9a\u6270\uff0c\u4f60\u518d\u6572\u949f\uff0c\u6211\u5c31\u8981\u5bf9\u4f60\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u90a3\u6c49\u5b50\u7ed9\u91d1\u9010\u6d41\u50b2\u6162\u7684\u6001\u5ea6\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u731b\u5730\u8df3\u8fc7\u6765\uff0c\u9a88\u6307\u5982\u621f\uff0c\u4fbf \u5411\u91d1\u9010\u6d41\u7684\u80cc\u5fc3\u4e00\u6233\u3002 \u3000\u3000\u8fd9\u6c49\u5b50\u5012\u4e5f\u4e0d\u662f\u60f3\u8981\u91d1\u9010\u6d41\u7684\u6027\u547d\uff0c\u4ed6\u6233\u7684\u662f\u91d1\u9010\u6d41\u80cc\u5fc3\u7684\u9ebb\u75d2\u7a74\uff0c\u7528\u4ed6\u72ec\u95e8\u7684\u624b\u6cd5\u6233 \u4e86\u522b\u4eba\u7684\u9ebb\u75d2\u7a74\uff0c\u53ef\u4ee5\u4ee4\u5bf9\u65b9\u5982\u53d7\u9177\u5211\u3002\u8fd9\u6c49\u5b50\u662f\u60f3\u7528\u8fd9\u4e2a\u72e0\u6bd2\u7684\u624b\u6cd5\u6765\u8ffd\u95ee\u91d1\u9010\u6d41\u7684\u53e3 \u4f9b\uff0c\u540c\u65f6\u4e5f\u8ba9\u4ed6\u5403\u70b9\u82e6\u5934\u3002 \u3000\u3000\u9752\u8863\u9053\u58eb\u76b1\u4e86\u76b1\u7709\uff0c\u53eb\u9053\u3002\u201d\u8001\u4e09\uff01\u201d\u53ef\u662f\u8fd9\u6c49\u5b50\u5df2\u7ecf\u51fa\u624b\u3002\u9752\u8863\u9053\u58eb\u60f3\u8981\u5236\u6b62\u4e5f\u6765\u4e0d \u53ca\u4e86\u3002\u8fd9\u6c49\u5b50\u4e00\u58f0\u5927\u559d\uff1a\u201c\u53eb\u4f60\u8fd9\u5c0f\u53eb\u5316\u77e5\u9053\u6211\u7684\u5389\u5bb3\uff01\u201d\u6307\u5934\u5df2\u7ecf\u6233\u5230\u4e86\u91d1\u9010\u6d41\u7684\u80cc\u4e0a\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u4e0d\u89c1\u5f97\u600e\u4e48\u5389\u5bb3\u3002\u201d\u4ecd\u662f\u90a3\u4e48\u6837\u61d2\u6d0b\u6d0b\u5730\u4fdd\u6301\u7740\u534a\u8eba\u534a\u5750 \u7684\u59ff\u52bf\uff0c\u8fde\u52a8\u4e5f\u6ca1\u6709\u52a8\u4e00\u4e0b\uff0c\u53e3\u91cc\u8fd8\u5728\u5403\u7740\u828b\u5934\u5462\uff0c\u53ef\u662f\u4ed6\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u90a3\u77ed\u5c0f\u7cbe\u608d\u7684 \u6c49\u5b50\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u5982\u662f\u8eab\u4e0d\u7531\u5df1\u5730\u5411\u524d\u4e00\u4e2a\u8d94\u8d84\uff0c\u6025\u51b2\u4e09\u6b65\uff0c\u8e0f\u8fdb\u4e86\u706b\u5806\u4e4b\u4e2d\u3002\u539f\u6765\u91d1\u9010 \u6d41\u867d\u7136\u6ca1\u6709\u53cd\u51fb\uff0c\u4f46\u4ed6\u8eab\u6709\u62a4\u4f53\u795e\u529f\uff0c\u8fd9\u6c49\u5b50\u7684\u624b\u6307\u6233\u5230\u4e86\u4ed6\u7684\u8eab\u4e0a\uff0c\u5982\u53d7\u7535\u9707\uff01 \u3000\u3000\u8fd9\u6c49\u5b50\u7684\u53cc\u811a\u8e0f\u5165\u706b\u5806\uff0c\u54c7\u54c7\u5927\u53eb\uff0c\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u60f3\u5403\u7168\u828b\u5934\u662f\u4e0d\u662f\uff1f\u4e0d\u7528\u4f60\u62a2\uff0c\u6211 \u8bf7\u4f60\u5403\uff01\u201d\u5728\u706b\u5806\u91cc\u6361\u8d77\u4e00\u4e2a\u6cbe\u4e0a\u7070\u7684\u828b\u5934\uff0c\u5c31\u5411\u4ed6\u7684\u5634\u5df4\u4e00\u585e\u3002 \u3000\u3000\u8fd9\u53cc\u5b50\u7ed9\u70ed\u5c71\u828b\u4e00\u70eb\uff0c\u597d\u4e0d\u96be\u53d7\uff0c\u5634\u5507\u70eb\u80bf\uff0c\u773c\u6cea\u4e5f\u6389\u4e86\u4e0b\u6765\u3002\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff0c \u4e0d\u597d\u5403\u5417\uff1f\u201d\u6c49\u5b50\u5927\u6012\uff0c\u4ed6\u7684\u624b\u4e0a\u672c\u6765\u662f\u63d0\u7740\u9b3c\u5934\u5200\u7684\uff0c\u4e00\u6012\u4e4b\u4e0b\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u4fbf\u5411\u91d1\u9010\u6d41 \u731b\u65ab\uff0c\u5927\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u6bd9\u4e86\u4f60\uff01\u201d\u8fd9\u6c49\u5b50\u7684\u5feb\u5200\u4e5f\u5f53\u771f\u4e86\u5f97\uff0c\u53e3\u4e2d\u53ea\u8bf4\u4e86\u516d\u4e2a\u5b57\uff0c\u624b\u5e95 \u5df2\u662f\u95ea\u7535\u822c\u5730\u65ab\u4e86\u516d\u516d\u4e09\u5341\u516d\u5200\uff01 \u3000\u3000\u91d1\u9010\u6d41\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5582\uff0c\u8fd9\u53ef\u4e0d\u662f\u5f00\u73a9\u7b11\u7684\uff0c\u4f60\u53ef\u522b\u8981\u5f53\u771f\u65ab\u7740\u6211\u624d\u597d\uff01\u201d\u90a3\u6c49\u5b50\u8fde\u65ab \u4e09\u5341\u516d\u5200\uff0c\u8fde\u91d1\u9010\u6d41\u7684\u8863\u89d2\u4e5f\u6ca1\u6cbe\u4e0a\uff0c\u4e0d\u7531\u5f97\u5446\u4e86\u4e00\u5446\u3002\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u4f60\u679c\u7136\u662f \u548c\u6211\u5f00\u73a9\u7b11\u7684\u3002\u597d\uff0c\u793c\u5c1a\u5f80\u6765\uff0c\u54b1\u4eec\u73a9\u800d\u73a9\u800d\uff01\u201d\u6a2a\u638c\u4e00\u62b9\uff0c\u8fd9\u6c49\u5b50\u672a\u80fd\u907f\u5f00\uff0c\u7ed9\u4ed6\u62b9\u4e86\u6ee1 \u5934\u6ee1\u9762\u3002\u91d1\u9010\u6d41\u7684\u638c\u5fc3\u6709\u70c2\u6ce5\u4f3c\u7684\u201c\u828b\u6d46\u201d\u8fd8\u6709\u7164\u7070\uff0c\u4e00\u62b9\u4e4b\u4e0b\uff0c\u628a\u8fd9\u6c49\u5b50\u53d8\u4e86\u4e2a\u5927\u82b1\u9762\u3002 \u3000\u3000\u9752\u8863\u9053\u58eb\u770b\u89c1\u91d1\u9010\u6d41\u8fde\u7eed\u4f7f\u51fa\u7684\u4e0a\u4e58\u6b66\u529f\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff01\u53ea\u6015\u91d1\u9010\u6d41\u8981\u65bd\u5c55\u6bd2\u624b\uff0c \u8fde\u5fd9\u62a2\u4e0a\u524d\u53bb\uff0c\u6296\u5f00\u62c2\u5c18\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u53eb\u5316\u4f11\u5f97\u653e\u8086\u3002\u201d \u3000\u3000\u9752\u8863\u9053\u58eb\u7684\u62c2\u5c18\u62c2\u5c06\u8fc7\u6765\uff0c\u5c18\u5c3e\u6563\u5f00\uff0c\u628a\u91d1\u9010\u6d41\u7684\u8eab\u5f62\u90fd\u7b3c\u7f69\u4e86\uff0c\u6bcf\u4e00\u6839\u5c18\u4e1d\u90fd\u4f3c\u5229\u9488 \u4f3c\u7684\u633a\u8d77\uff0c\u5a01\u80c1\u7740\u91d1\u9010\u6d41\u7684\u5168\u8eab\u7a74\u9053\u3002\u91d1\u9010\u6d41\u4e5f\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u2018\u8fd9\u5348\u9f3b\u5b50\u81ed\u9053\u571f \u5012\u662f\u4e2a\u4e00\u6d41\u9ad8\u624b\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e5f\u63d0\u9632\u4ed6\u8981\u4e0b\u7740\u624b\uff0c\u4e0d\u6562\u8f7b\u6545\uff0c\u4e00\u58f0\u957f\u5578\uff0c\u628a\u9053\u58eb\u7684\u62c2\u5c18\u5439\u5f97\u604d\u5982\u4e71\u8349\u968f\u98ce\uff0c\u5c18 \u4e1d\u98d8\u656c\uff0c\u9752\u8863\u9053\u58eb\u559d\u9053\uff1a\u201c\u597d\u529f\u592b\u3002\u201d\u968f\u624b\u4e00\u6296\uff0c\u62c2\u5c18\u91cd\u53c8\u96c6\u6210\u4e00\u675f\uff0c\u7adf\u7136\u5f53\u4f5c\u5224\u5b98\u7b14\u4f7f\uff0c \u51fa\u624b\u751f\u98ce\uff0c\u70b9\u5411\u91d1\u9010\u6d41\u80f8\u819b\u7684\u201c\u6108\u6c14\u7a74\u201d\u3002 \u3000\u3000\u62c2\u5c18\u662f\u8f7b\u67d4\u4e4b\u7269\uff0c\u8fd9\u9053\u58eb\u5c45\u7136\u80fd\u628a\u5b83\u5f53\u4f5c\u5224\u5b98\u7b14\u4f7f\uff0c\u5185\u529f\u7684\u9020\u8be3\u4e5f\u786e\u662f\u4e0d\u51e1\u7684\u4e86\uff01\u793c\u5c1a \u5f80\u6765\uff0c\u91d1\u9010\u6d41\u4e5f\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u5f53\u4e0b\u6325\u8896\u4e00\u4f5b\uff0c\u89e3\u4e86\u9752\u8863\u9053\u58eb\u62c2\u5c18\u523a\u7a74\u7684\u62db\u6570\u3002 \u3000\u3000\u90a3\u77ed\u5c0f\u3001\u7cbe\u608d\u7684\u6c49\u5b50\u63d0\u5200\u590d\u4f4d\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u53eb\u5316\u4e00\u5b9a\u662f\u7a77\u9178\u4e00\u515a\u7684\uff0c\u54b1\u4eec\u53ef\u4e0d\u80fd\u653e\u8fc7 \u4e86\u4ed6\uff01\u201d\u9752\u8863\u9053\u58eb\u9053\uff1a\u201c\u5f53\u7136\uff0c\u6211\u600e\u80fd\u8ba9\u4f60\u5e73\u767d\u5403\u4ed6\u7684\u4e8f\u3002\u201d\u4ed6\u867d\u7136\u5403\u60ca\u4e8e\u91d1\u9010\u6d41\u7684\u6b66\u529f\uff0c \u4f46\u4e3a\u4e86\u540c\u5e2e\u5144\u5f1f\u7684\u4e49\u6c14\uff0c\u53ea\u597d\u628a\u5168\u90e8\u7684\u672c\u9886\u90fd\u62ff\u51fa\u6765\uff0c\u4e0e\u90a3\u6c49\u5b50\u8054\u624b\u731b\u653b\u91d1\u9010\u6d41\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u7684\u672c\u9886\u867d\u662f\u4e0e\u91d1\u9010\u6d41\u76f8\u5dee\u9887\u8fdc\uff0c\u4f46\u9752\u8863\u9053\u58eb\u7684\u6b66\u529f\u5219\u662f\u751a\u5f3a\uff0c\u5728\u9752\u8863\u9053\u58eb\u63a5\u4e86\u91d1 \u9010\u6d41\u516b\u6210\u653b\u52bf\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c\u8fd9\u6c49\u5b50\u7684\u5feb\u5200\u5bf9\u91d1\u9010\u6d41\u4e5f\u5c31\u6709\u70b9\u5a01\u80c1\u4e86\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u8fd9\u6c49\u5b50\u770b\u51fa\u4fbf\u5b9c\uff0c\u4e00\u5200\u4ece\u91d1\u9010\u6d41\u80cc\u540e\u780d\u6765\uff0c\u91d1\u9010\u6d41\u542c\u58f0\u8fa8\u5668\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u53cd\u624b\u4e00 \u5f39\u3002\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u628a\u6c49\u5b50\u7684\u9b3c\u5934\u5200\u5f39\u5f00\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u9053\u58eb\u7684\u62c2\u5c18\u53c8\u5df2\u5f53\u80f8\u62c2\u5230\uff0c\u662f \u6781\u5389\u5bb3\u7684\u4e00\u62db\u62c2\u7a74\u62db\u6570\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4f7f\u4e86\u4e2a\u201c\u79fb\u5f62\u6362\u4f4d\u201d\u7684\u5929\u7f57\u6b65\u6cd5\uff0c\u5de6\u624b\u9634\u638c\uff0c\u53f3\u624b\u9633\u638c\uff0c\u53cc\u638c\u4e00\u5206\uff0c\u5f62\u5982\u96c1\u7fc5\u63a0 \u63a8\u51fa\uff0c\u529b\u9053\u4e00\u521a\u4e00\u67d4\uff0c\u76f8\u4e92\u7275\u5f15\uff0c\u4f7f\u5200\u7684\u6c49\u5b50\u4e00\u4e2a\u8e09\u8dc4\uff0c\u4e00\u5200\u5288\u5c06\u8fc7\u53bb\uff0c\u9669\u4e9b\u5288\u7740\u4e86\u4ed6\u7684\u540c \u4f34\u3002 \u3000\u3000\u9752\u8863\u9053\u58eb\u5ffd\u5730\u54a6\u4e86\u4e00\u4e00\u58f0\uff0c\u9000\u540e\u4e09\u6b65\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u53eb\u5316\uff0c\u4f60\u662f\u5929\u9b54\u6559\u7684\u5f1f\u5b50\u4e48\uff1f\u201d\u91d1\u9010 \u6d41\u9053\uff1a\u201c\u4ec0\u4e48\u5929\u9b54\u6559\uff0c\u6211\u624d\u4e0d\u5c51\u4e8e\u505a\u90aa\u6559\u7684\u5f1f\u5b50\u5462\uff01\u4f60\u80e1\u8bf4\u516b\u9053\uff0c\u5403\u6211\u4e00\u638c\uff01\u201d\u9752\u8863\u9053\u58eb\u5927 \u4e3a\u60ca\u8be7\uff0c\u89e3\u4e86\u91d1\u9010\u6d41\u7684\u4e00\u62db\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5929\u9b54\u6559\u7684\u5f1f\u5b50\uff0c\u4e3a\u4ec0\u4e48\u5374\u4f1a\u5929\u9b54\u6559\u7684\u6b66\u529f\uff1f\u201d \u9010\u6d41\u5927\u7b11\u9053\uff1a\u201c\u7b11\u8bdd\uff0c\u7b11\u8bdd\uff0c\u4f60\u4e0d\u8bc6\u6211\u7684\u6b66\u529f\u5c31\u4e0d\u8981\u4e71\u8bf4\uff01\u201d\u8fde\u73af\u638c\u53d1\uff0c\u628a\u90a3\u9752\u8863\u9053\u58eb\u6253\u5f97 \u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6709\u6240\u4e0d\u77e5\uff0c\u9752\u8863\u903c\u571f\u8bef\u8ba4\u4ed6\u662f\u5929\u9b54\u6559\u7684\u5f1f\u5b50\u5176\u5b9e\u4e5f\u662f\u6709\u6839\u636e\u7684\u3002\u539f\u6765\u5929\u9b54\u6559\u7684\u7956 \u5e08\u5389\u80dc\u7537\u4e5f\u66fe\u7ec3\u8fc7\u4e54\u5317\u6e9f\u7684\u6b66\u529f\u79d8\u7b08\uff0c\u91d1\u4e16\u9057\u7684\u6b66\u529f\u5219\u878d\u4f1a\u4e86\u5404\u6d3e\u6240\u957f\uff0c\u7279\u522b\u4ee5\u4e54\u5317\u6e9f\u7684\u6b66 \u529f\u79d8\u7b08\u4e3a\u6881\u67f1\uff0c\u4ee5\u5929\u5c71\u6d3e\u7684\u5185\u529f\u5fc3\u6cd5\u4e3a\u6839\u57fa\u800c\u6f14\u5316\u7684\u3002\u91d1\u9010\u6d41\u521a\u624d\u6240\u4f7f\u7684\u4e00\u62db\uff0c\u6b63\u662f\u4e54\u5317\u6e9f \u6b66\u529f\u79d8\u7b08\u4e2d\u7684\u201c\u9634\u9633\u53cc\u649e\u638c\u201d\u7684\u529f\u707e\uff0c\u8fd9\u9752\u8863\u9053\u58eb\u5728\u4e8c\u5341\u5e74\u524d\u66fe\u89c1\u8fc7\u5929\u9b54\u6559\u4e3b\u4f7f\u8fc7\u3002 \u3000\u3000\u9752\u8863\u9053\u58eb\u60ca\u7591\u4e0d\u8db3\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u53eb\u5316\u82e5\u662f\u5929\u9b54\u6559\u7684\u5f1f\u5b50\u3002\u51b3\u4e0d\u6562\u5bf9\u672c\u6559\u5982\u6b64\u8fb1 \u9a82\uff0c\u53ea\u4e0d\u77e5\u4ed6\u7684\u6b66\u529f\u5374\u53c8\u4f55\u4ee5\u662f\u5929\u9b54\u6559\u4e00\u8def\uff1f\u201d \u3000\u3000\u9752\u8863\u9053\u58eb\u5fc3\u6709\u6240\u7591\uff0c\u8d8a\u53d1\u60f3\u8981\u628a\u91d1\u9010\u6d41\u6d3b\u64d2\u8ffd\u95ee\u4ed6\u7684\u6765\u5386\uff0c\u4ed6\u77e5\u9053\u91d1\u9010\u6d41\u7684\u672c\u9886\u5728\u4ed6\u4e4b \u4e0a\uff0c\u4f46\u4ed6\u4e5f\u770b\u51fa\u91d1\u9010\u6d41\u7ecf\u9a8c\u4e0d\u8db3\u7684\u5f31\u70b9\uff0c\u4e8e\u662f\u91c7\u7528\u7f20\u6597\u7684\u6218\u7565\uff0c\u6d88\u8017\u91d1\u9010\u6d41\u7684\u6c14\u529b\uff0c\u5e0c\u671b\u91d1 \u9010\u6d41\u4e00\u6709\u7834\u7efd\uff0c\u4fbf\u53ef\u4e58\u6687\u62b5\u9699\u3002\u90a3\u77ed\u5c0f\u7cbe\u608d\u7684\u6c49\u5b50\u7528\u5feb\u5200\u914d\u5408\u81ea\u5df1\u653b\u51fb\uff0c\u4e5f\u662f\u6bcf\u4e00\u5200\u90fd\u65ab\u5411 \u91d1\u9010\u6d41\u7684\u8981\u5bb3\u3002 \u3000\u3000\u9752\u8863\u9053\u58eb\u6253\u5f97\u5982\u610f\u7b97\u76d8\uff0c\u91d1\u9010\u6d41\u4e5f\u5e76\u4e0d\u7b28\uff0c\u4ed6\u770b\u51fa\u5bf9\u65b9\u662f\u8981\u6d88\u8017\u4ed6\u7684\u6c14\u529b\uff0c\u4fbf\u4e5f\u7acb\u5373\u6539 \u53d8\u6218\u672f\uff0c\u4f7f\u51fa\u201c\u5929\u7f57\u6b65\u6cd5\u201d\u4e0e\u5bf9\u65b9\u6e38\u6597\uff0c\u6597\u4e86\u4e00\u4f1a\uff0c\u91d1\u9010\u6d41\u6697\u81ea\u601d\u91cf\uff1a\u201c\u8fd9\u81ed\u9053\u58eb\u7684\u6b66\u529f\u5f88 \u662f\u4e0d\u5f31\uff0c\u6211\u53c8\u4e0d\u77e5\u9053\u4ed6\u7684\u5e95\u7ec6\uff0c\u82e5\u7136\u6740\u4f24\u4e86\u4ed6\uff0c\u53ea\u6015\u4f1a\u505a\u9519\u4e86\u4e8b\u3002\u201d\u539f\u6765\u4ee5\u91d1\u9010\u6d41\u7684\u672c\u9886\uff0c \u672c\u662f\u53ef\u4ee5\u901f\u80dc\u7684\uff0c\u4f46\u56e0\u9752\u8863\u9053\u58eb\u7684\u6b66\u529f\u4e5f\u5f88\u4e0d\u5f31\uff0c\u82e5\u6c42\u901f\u80dc\uff0c\u5219\u975e\u65bd\u5c55\u6700\u5389\u5bb3\u7684\u6740\u624b\u4e0d\u53ef\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8e1f\u8e70\u672a\u51b3\uff0c\u90a3\u6c49\u5b50\u53ea\u9053\u91d1\u9010\u6d41\u5df2\u6709\u602f\u610f\uff0c\u8d8a\u53d1\u903c\u5f97\u7d27\u4e86\u3002\u91d1\u9010\u6d41\u84e6\u5730\u5f97\u4e86\u4e00\u4e2a\u4e3b \u610f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u53ef\u6076\u5f97\u5f88\uff0c\u6211\u4e14\u548c\u4ed6\u5f00\u4e2a\u73a9\u7b11\u3002\u201d\u6fc0\u6218\u4e2d\u6545\u610f\u9732\u51fa\u4e2a\u7834\u7efd\uff0c\u8eab\u5f62\u4e00 \u6643\uff0c\u4f3c\u6b32\u8dcc\u5012\uff0c\u90a3\u6c49\u5b50\u559c\u51fa\u671b\u5916\uff0c\u51b2\u4e0a\u53bb\u4fbf\u662f\u4e00\u5200\u3002\u4ed6\u4e0e\u9752\u8863\u9053\u571f\u8054\u624b\u4f5c\u6218\uff0c\u672c\u6765\u662f\u914d\u5408\u5f97 \u5341\u5206\u7d27\u5bc6\u7684\uff0c\u6b64\u65f6\u72ec\u81ea\u51b2\u4e0a\u524d\u53bb\uff0c\u767b\u65f6\u4fbf\u5931\u4e86\u7167\u5e94\u3002 \u3000\u3000\u9752\u8863\u9053\u58eb\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u91d1\u9010\u6d41\u8eab\u5f62\u4e00\u95ea\uff0c\u5df2\u662f\u95ea\u7535\u822c\u7684\u7ed5\u5230\u4e86\u90a3\u6c49 \u5b50\u7684\u80cc\u540e\u3002\u53cc\u624b\u4e00\u5200\u5288\u7a7a\uff0c\u53ea\u89c9\u9888\u9879\u9ebb\u75d2\u75d2\u7684\u597d\u4e0d\u96be\u53d7\uff0c\u539f\u6765\u662f\u7ed9\u91d1\u9010\u6d41\u8f7b\u8f7b\u5730\u634f\u4e86\u4ed6\u4e00 \u628a\u3002 \u3000\u3000\u9752\u8863\u9053\u58eb\u62c2\u5c18\u62e9\u51fa\uff0c\u5df2\u7ecf\u8fdf\u4e86\u4e00\u6b65\uff0c\u91d1\u9010\u6d41\u6325\u8896\u8361\u5f00\u4ed6\u7684\u62c2\u5c18\uff0c\u8bf4\u9053\uff1a\u201c\u6253\u5f97\u4e45\u4e86\uff0c\u4e5f \u8be5\u6362\u6362\u53e3\u5473\u5566\uff0c\u7b49\u4e0b\u8bf7\u4f60\u770b\u573a\u597d\u620f\u3002\u201d\u53ea\u89c1\u90a3\u6c49\u5b50\u597d\u50cf\u6ee1\u8eab\u90fd\u662f\u8df3\u86a4\u4f3c\u7684\uff0c\u8038\u80a9\uff0c\u626d\u9888\u3001\u624b \u821e\u3001\u8db3\u8e48\uff0c\u53e3\u4e2d\u8fd8\u53d1\u51fa\u201c\u55ec\u55ec\u201d\u7684\u58f0\u97f3\uff0c\u5f62\u72b6\u6781\u662f\u6ed1\u7a3d\u3002 \u3000\u3000\u9752\u8863\u9053\u58eb\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u7126\u8001\u4e09\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u53ef\u601c\u90a3\u6c49\u5b50\u75af\u72c2\u822c\u5730\u8df3\u8dc3\u4e0d\u4f11\uff0c\u54ea \u91cc\u7b54\u5e94\u5f97\u51fa\u8bdd\u3002\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u8981\u4e0d\u4e86\u4ed6\u7684\u547d\u7684\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\u3002\u6211\u53ea\u4e0d\u8fc7\u793c \u5c1a\u5f80\u6765\uff0c\u987a\u4fbf\u4e5f\u8bf7\u4f60\u770b\u4e00\u573a\u800d\u7334\u513f\u7684\u628a\u620f\u800c\u5df2\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u7126\u8001\u4e09\u662f\u7ed9\u91d1\u9010\u6d41\u7528\u72ec\u95e8\u624b\u6cd5\u70b9\u4e86\u4ed6\u7684\u201c\u9ebb\u75d2\u7a74\u201d\u3002\u5728\u4ed6\u521a\u624d\u5077\u88ad\u91d1\u9010\u6d41\u7684\u4e2d \u2018\u9ebb\u75d2\u7a74\u2019\u7684\uff0c\u5982\u4eca\u662f\u5730\u70b9\u4e0d\u7740\u91d1\u9010\u6d41\uff0c\u5374\u7ed9\u91d1\u9010\u6d41\u4ee5\u5176\u4eba\u4e4b\u9053\u53cd\u6cbb\u5176\u4eba\u4e4b\u8eab\u4e86\u3002\u6545\u6b64\u91d1\u9010 \u6d41\u8bf4\u662f\u201c\u793c\u5c1a\u5f80\u6765\u3002\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7684\u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\u66f4\u4e3a\u5389\u5bb3\uff0c\u8fd9\u6c49\u5b50\u75d2\u5f97\u96be\u53d7\uff0c\u201c\u94db\u5577\u201d\u4e00\u58f0\uff0c\u629b\u4e0b\u4e86\u9b3c\u5934\u5200\uff0c\u53cc \u624b\u5728\u8eab\u4e0a\u4e71\u6293\uff0c\u81ea\u5df1\u628a\u8863\u88f3\u6495\u88c2\uff0c\u5728\u8eab\u4e0a\u6293\u8d77\u4e86\u4e00\u6761\u6761\u7684\u8840\u75d5\u3002 \u3000\u3000\u9752\u8863\u9053\u58eb\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u7126\u8001\u4e09\uff0c\u54b1\u4eec\u6253\u4e0d\u8fc7\u4eba\u5bb6\uff0c\u522b\u5728\u8fd9\u91cc\u4e22\u4eba\u73b0\u4e16\u5566\u3002\u201d\u62d6\u4e86 \u90a3\u4e2a\u6c49\u5b50\uff0c\u8dd1\u51fa\u5e99\u95e8\uff0c\u91d1\u9010\u6d41\u54c8\u54c8\u4e00\u7b11\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u597d\u8d70\uff0c\u597d\u8d70\uff0c\u6055\u6211\u4e0d\u9001\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u56de\u8fc7\u5934\u6765\uff0c\u7b11\u9053\uff1a\u201c\u5077\u513f\u670b\u53cb\uff0c\u73b0\u5728\u4f60\u53ef\u4ee5\u51fa\u6765\u5566\uff01\u201d\u8bf4\u7f62\uff0c\u63d0\u8d77\u90a3\u53e3\u94dc\u949f\u3002\u5ffd \u89c1\u706b\u5149\u4e00\u95ea\u5373\u706d\uff0c\u539f\u6765\u662f\u90a3\u4eba\u624b\u4e0a\u62ff\u7740\u4e00\u4e2a\u706b\u77f3\uff0c\u8138\u4e0a\u5374\u9732\u51fa\u4e00\u7247\u832b\u7136\u7684\u795e\u8272\uff0c\u5982\u75f4\u4f3c\u5446\u5730 \u4ecd\u7136\u76d8\u5750\u5728\u5730\u4e0a\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u8ffd\u5175\u9000\u540e\u98ce\u6ce2\u9759\uff0c\u4f55\u6545\u75f4\u5446\u4e8b\u592a\u5947\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u516b\u56de\u3000\u660e\u73e0\u5c3d\u6563\u6ecb\u7591\u8651\u3000\u7ea2\u7c89\u4f55\u5c1d\u662f\u7978\u80ce \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u516b\u56de\u3000\u660e\u73e0\u5c3d\u6563\u6ecb\u7591\u8651\u3000\u7ea2\u7c89\u4f55\u5c1d\u662f\u7978\u80ce \u3000\u3000\u91d1\u9010\u6d41\u8be7\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u90a3\u4e66\u751f\u5982\u68a6\u521d\u9192\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u4f60\u628a\u949f\u7f69\u4e0a\uff0c\u8ba9\u6211\u518d \u8eb2\u4e00\u4f1a\u513f\u3002\u201d\u91d1\u9010\u6d41\u66f4\u662f\u5947\u602a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u654c\u4eba\u90fd\u5df2\u8dd1\u4e86\uff0c\u4f60\u8fd8\u8981\u8eb2\u8d77\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u90a3\u4e66 \u751f\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\u3002\u201d\u6211\u7684\u8d3c\u763e\u53c8\u53d1\u4f5c\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u5ff5\u4e00\u52a8\uff0c\u65bd\u5c55\u5999\u624b\u7a7a\u7a7a\u7684\u624b\u6bb5\uff0c\u4ece\u90a3\u4e66\u751f\u7684\u888b\u5b50\u91cc\u628a\u706b\u77f3\u6478\u4e86\u51fa\u6765\uff0c\u4e00\u4e2a\u8f6c \u8eab\uff0c\u64e6\u71c3\u706b\u77f3\uff0c\u90a3\u4e66\u751f\u65b9\u624d\u77e5\u89c9\uff0c\u4e0d\u89c9\u4e5f\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\u3002\u201d\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u4e0d\u662f\u9ed1\u5403\u9ed1\uff0c\u73cd\u73e0\u8fd8\u5728\u4f60\u7684\u888b\u5b50\u91cc\uff0c\u6211\u53ea\u662f\u501f\u4f60\u7684\u706b\u77f3\u4e00 \u7528\u3002\u201d\u90a3\u4e66\u751f\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\u3002\u201d\u54e6\uff0c\u6211\u660e\u767d\u4e86\u3002\u4f60\u731c\u5230\u79d8\u5bc6\u4e86\u5427\uff1f\u591a\u8c22\u4f60\u5e2e\u4e86\u6211\u7684\u5927\u5fd9\uff0c \u8fd9\u79d8\u5bc6\u6211\u672c\u6765\u4e5f\u4e0d\u60f3\u7792\u4f4f\u4f60\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u660e\u767d\u6211\u4e0d\u660e\u767d\u3002\u4f46\u4f60\u4e5f\u4e0d\u5fc5\u8bf4\u51fa\u6765\u3002\u6211\u5e2e\u5fd9\u4f60\u53ea\u4e0d\u8fc7\u662f\u4e3a\u4e86\u540c\u9053\u7684\u4e49 \u6c14\uff0c\u5e76\u4e0d\u60f3\u5957\u53d6\u4f60\u7684\u79d8\u5bc6\u4f5c\u4e3a\u62a5\u916c\u3002\u201d\u90a3\u4e66\u751f\u82e6\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8bef\u4f1a\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u8bef\u4f1a\u4e86\u3002\u6211\u4e0d\u8981\u4f60\u544a\u8bc9\u6211\uff0c\u90a3\u662f\u4e3a\u4e86\u4e0d\u60f3\u9886\u4f60\u7684\u4eba\u60c5\u3002 \u563f\uff0c\u563f\uff0c\u6211\u81ea\u5df1\u4e0d\u4f1a\u770b\u4e48\uff1f\u201d\u8bf4\u7f62\u628a\u90a3\u53e3\u94dc\u949f\u7ffb\u8f6c\u8fc7\u6765\uff0c\u7528\u706b\u77f3\u4e00\u7167\uff0c\u4e0d\u89c9\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u539f \u6765\u5982\u6b64\uff01\u201d \u3000\u3000\u539f\u6765\u90a3\u53e3\u94dc\u949f\u5185\u523b\u6709\u8bb8\u591a\u6587\u5b57\uff0c\u91d1\u9010\u6d41\u770b\u4e86\u51e0\u884c\uff0c\u5df2\u77e5\u662f\u5929\u9b54\u6559\u7684\u6bd2\u529f\u79d8\u5178\uff0c\u60f3\u6765\u90a3\u4e66 \u751f\u5c31\u662f\u56e0\u4e3a\u53d1\u73b0\u8fd9\u4e2a\u6bd2\u529f\u79d8\u5178\u6240\u4ee5\u770b\u5f97\u5982\u9189\u5982\u75f4\u3002 \u3000\u3000\u90a3\u4e66\u751f\u9053\uff1a\u201c\u5929\u9b54\u6559\u6709\u4e09\u7bc7\u767e\u6bd2\u771f\u7ecf\uff0c\u90fd\u5df2\u523b\u5728\u4e0a\u9762\u4e86\u3002\u5929\u9b54\u6559\u7684\u6b66\u529f\u5305\u7f57\u751a\u5e7f\uff0c\u4e0d\u53ea \u6bd2\u529f\u4e00\u6837\uff0c\u4f46\u5176\u4ed6\u7684\u6b66\u529f\u672a\u5fc5\u80dc\u5f97\u8fc7\u5404\u5927\u95e8\u6d3e\uff0c\u53ea\u6709\u8fd9\u767e\u6bd2\u771f\u7ecf\u5374\u662f\u6b66\u6797\u4e2d\u72ec\u4e00\u65e0\u4e8c\u7684\u3002\u8001 \u5f1f\uff0c\u4f60\u6284\u4e00\u4efd\u5427\u3002\u8fd9\u662f\u4f60\u81ea\u5df1\u53d1\u73b0\u7684\uff0c\u4e0d\u80fd\u7b97\u662f\u6211\u7ed9\u4f60\u7684\u62a5\u916c\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u7684\u201c\u767e\u6bd2\u771f\u7ecf\u201d\u672c\u6765\u662f\u4e54\u5317\u6e9f\u201c\u6b66\u529f\u79d8\u7b08\u201d\u4e2d\u7684\u4e00\u90e8\u4efd\uff0c\u4f46\u91d1\u4e16\u9057\u5f53\u5e74\u4ece\u5389\u80dc\u7537 \u624b\u4e2d\u53d6\u56de\u8fd9\u672c\u79d8\u7b08\u4e4b\u65f6\uff0c\u7531\u4e8e\u4ed6\u60f3\u8981\u81ea\u7acb\u7684\u4e43\u662f\u6b63\u5927\u5149\u660e\u7684\u6b66\u529f\uff0c\u6545\u6b64\u4e00\u5230\u624b\u5c31\u628a\u767e\u6bd2\u771f\u7ecf \u6bc1\u6389\uff0c\u53ea\u662f\u5438\u53d6\u5176\u4ed6\u90e8\u4efd\u7684\u7cbe\u534e\u3002\u540e\u6765\u5f85\u4ed6\u81ea\u6210\u4e86\u4e00\u5bb6\u4e4b\u540e\uff0c\u66f4\u8fde\u4e09\u4e54\u5317\u6e9f\u90a3\u672c\u201c\u6b66\u6797\u79d8 \u7b08\u201d\u4e5f\u90fd\u5728\u5389\u80dc\u7537\u7684\u5893\u524d\u70e7\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u81ea\u5df1\u6ca1\u6709\u5b66\u8fc7\u201c\u767e\u6bd2\u771f\u7ecf\u201d\uff0c\u91d1\u9010\u6d41\u5f53\u7136\u662f\u8fde\u8fd9\u4e2a\u540d\u79f0\u4e5f\u6ca1\u542c\u8fc7\uff0c\u5728\u91d1\u9010\u6d41\u7684\u5fc3 \u76ee\u4e2d\u5929\u9b54\u6559\u4e43\u662f\u4e00\u4e2a\u90aa\u6559\uff0c\u4ed6\u600e\u5c51\u4e8e\u5077\u5b66\u90aa\u6d3e\u7684\u529f\u592b\uff1f \u3000\u3000\u5f53\u4e0b\u91d1\u9010\u6d41\u51b7\u51b7\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u867d\u7136\u662f\u5c0f\u8d3c\uff0c\u5077\u4e1c\u897f\u4e5f\u8981\u7ecf\u8fc7\u9009\u62e9\u7684\uff0c\u8fd9\u4e1c\u897f\u4e48\uff0c\u8fd8 \u4e0d\u503c\u5f97\u6211\u5077\u3002\u201d \u3000\u3000\u90a3\u4e66\u751f\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u8fd8\u6ca1\u6709\u770b\u8fc7\u3002\u8fd9\u91cc\u9762\u4e5f\u5e76\u975e\u5c3d\u662f\u65c1\u95e8\u5de6\u9053\u7684\u4e0b\u4e58\u529f\u592b\u7684\u3002\u54b1\u4eec\u5f53\u7136 \u4e0d\u5c51\u4e8e\u4f7f\u6bd2\u5bb3\u4eba\u3002\u4f46\u4e5f\u4e0d\u80fd\u4e0d\u77e5\u9053\u4e00\u4e9b\u3002\u78b0\u4e0a\u4e86\u6c5f\u6e56\u4e0a\u4ee5\u6bd2\u4f24\u4eba\u4e4b\u8f88\uff0c\u54b1\u4eec\u5c31\u7528\u6765\u4ee5\u6bd2\u653b \u6bd2\uff0c\u53c8\u6709\u4f55\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u6de1\u6de1\u8bf4\u9053\u3002\u201d\u4f60\u559c\u6b22\u5b66\u4f60\u81ea\u5df1\u5b66\uff0c\u6211\u53ef\u6ca1\u6709\u8fd9\u4e2a\u5174\u8da3\u5949\u966a\u3002\u201d\u90a3\u4e66\u751f\u9053\uff1a\u201c\u8fd9\u4e48 \u8bf4\uff0c\u4f60\u4e5f\u4e0d\u613f\u610f\u5e2e\u5fd9\u6211\u4e86\uff1f\u201d \u3000\u3000\u5168\u9010\u6d41\u9053\uff1a\u201c\u6211\u7ed9\u4f60\u628a\u949f\u7f69\u4e0a\u5bb9\u6613\uff0c\u4f46\u6211\u6ca1\u5de5\u592b\u7b49\u4f60\u3002\u53cd\u6b63\u8fd9\u53e3\u949f\u5df2\u7ecf\u7ffb\u8f6c\u4e86\uff0c\u4f60\u81ea\u5df1 \u722c\u8fdb\u53bb\u770b\u4e2a\u9971\u53bb\u3002\u201d\u949f\u5df2\u7ffb\u8f6c\uff0c\u8981\u60f3\u770b\u949f\u5185\u6240\u523b\u7684\u6587\u5b57\uff0c\u5fc5\u987b\u722c\u8fdb\u53bb\u5934\u4e0b\u811a\u4e0a\u7684\u201c\u5012\u770b\u201d\u624d \u884c\u3002\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u4f60\u8fd9\u4e0d\u6210\u5668\u7684\u5077\u513f\u8fd8\u60f3\u6211\u5e2e\u5fd9\u4f60\u770b\u5f97\u8212\u8212\u670d\u670d\u5417\uff1f\u201d\u7531\u4e8e\u91d1\u9010\u6d41\u770b\u4e0d\u8d77 \u90aa\u6d3e\u7684\u529f\u592b\uff0c\u8fde\u5e26\u5bf9\u8fd9\u4e66\u751f\u4e5f\u6709\u4e86\u9119\u89c6\u4e4b\u610f\u3002 \u3000\u3000\u90a3\u4e66\u751f\u95ed\u8d77\u53cc\u76ee\uff0c\u53e3\u4e2d\u5583\u5583\u6709\u8f9e\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5f00\u773c\u7b11\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u6211\u90fd\u8bb0\u7262\u4e86\u3002\u4e0d\u7528\u518d \u770b\u5566\uff01\u201d\u770b\u6765\uff0c\u4ed6\u521a\u624d\u53ea\u662f\u6050\u6015\u81ea\u5df1\u6709\u6240\u9057\u5fd8\uff0c\u6545\u800c\u60f3\u518d\u770b\u4e00\u904d\u3002\u73b0\u5728\u4ed6\u5df2\u7ecf\u80cc\u5f97\u51fa\u6765\uff0c\u90a3 \u81ea\u662f\u4e0d\u5fc5\u91d1\u9010\u6d41\u518d\u5e2e\u4ed6\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u53ae\u5012\u6709\u8fc7\u76ee\u4e0d\u5fd8\u7684\u672c\u9886\uff0c\u4f46\u4ed6\u8981\u5077\u5b66\u90aa\u6d3e\u6bd2 \u529f\uff0c\u6211\u53ef\u4e0d\u6562\u65ad\u5b9a\u4ed6\u7684\u5fc3\u672f\u662f\u597d\u662f\u574f\uff0c\u55ef\uff0c\u8fd9\u6837\u7684\u670b\u53cb\uff0c\u6211\u65e2\u7136\u662f\u731c\u4ed6\u4e0d\u900f\uff0c\u4ea4\u4e0d\u4ea4\u4e5f\u7f62 \u4e86\u3002\u201d \u3000\u3000\u90a3\u4e66\u751f\u770b\u4e86\u91d1\u9010\u6d41\u4e00\u773c\uff0c\u8bf4\u9053\u3002\u201d\u4f60\u5e2e\u6211\u53e6\u4e00\u4e2a\u5fd9\u884c\u4e0d\u884c\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5e2e\u4ec0\u4e48 \u5fd9\uff1f\u201d\u90a3\u4e66\u751f\u9053\uff1a\u201c\u628a\u8fd9\u4e2a\u949f\u6bc1\u6389\u3002\u6211\u4e00\u4eba\u6050\u6015\u505a\u4e0d\u5230\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u628a\u5b83\u6bc1 \u6389\uff1f\u201d\u90a3\u4e66\u751f\u9053\uff1a\u201c\u514d\u5f97\u7ed9\u574f\u4eba\u53d1\u73b0\u3002\u201d\u91d1\u9010\u6d41\u5fc3\u91cc\u51b7\u7b11\uff1a\u201c\u4f60\u662f\u597d\u73af\u6211\u4e5f\u4e0d\u77e5\u5462\u3002\u6050\u6015\u8fd9 \u53ea\u662f\u4f60\u627e\u7684\u85c9\u53e3\uff0c\u4ee5\u4fbf\u72ec\u5360\u5929\u9b54\u6559\u7684\u6bd2\u529f\u5427\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5bf9\u4ed6\u6709\u4e86\u6000\u7591\uff0c\u5bf9\u4ed6\u66f4\u589e\u9119\u89c6\uff0c\u4e8e\u662f\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8981\u548c\u4f60\u5206\u8d43\uff0c\u4f46\u6bc1 \u5c38\u706d\u8ff9\u7684\u4e8b\u6211\u4e5f\u662f\u4e0d\u5e72\u7684\uff01\u201d \u3000\u3000\u90a3\u4e66\u751f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6bc1\u5c38\u706d\u8ff9\uff0c\u8fd9\u8bf4\u6cd5\u5012\u5f88\u65b0\u9c9c\uff0c\u4f46\u8fd9\u662f\u6709\u6bd2\u7684\u5c38\u4f53\uff0c\u6bc1\u4e86\u4ed6\u6709\u4f55\u4e0d \u53ef\uff1f\u4e0d\u8fc7\uff0c\u4f60\u4e0d\u613f\u610f\u5e72\u6211\u5f53\u7136\u4e5f\u4e0d\u4fbf\u52c9\u5f3a\u4f60\uff0c\u53ea\u6709\u6211\u81ea\u5df1\u5e72\u5566\u3002\u201d\u8bf4\u7f62\uff0c\u63d0\u8d77\u4e86\u90a3\u53e3\u94dc\u949f\uff0c \u8d70\u51fa\u7834\u5e99\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u4e14\u770b\u4ed6\u6709\u4ec0\u4e48\u672c\u9886\u53ef\u4ee5\u6bc1\u6389\u8fd9\u53e3\u5927\u949f\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u90a3\u4e66\u751f\u5df2\u662f\u56de\u5934\u5411 \u4ed6\u6253\u4e86\u4e2a\u62db\u547c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u534a\u4e2a\u65f6\u8fb0\u624d\u4f1a\u5929\u4eae\uff0c\u4f60\u4e0d\u6025\u4e8e\u8d70\u5427\uff1f\u4e0d\u8981\u4f60\u6bc1\u5c38\u706d\u8ff9\uff0c\u53ea\u8bf7\u4f60 \u9001\u949f\uff08\u7ec8\uff09\u3002\u201d\u91d1\u9010\u6d41\u542c\u4ed6\u8bf4\u5f97\u98ce\u8da3\uff0c\u4e0d\u89c9\u7b11\u9053\u3002\u201d\u597d\uff0c\u53ea\u662f\u7ed9\u949f\u9001\u7ec8\uff0c\u4e0d\u662f\u7ed9\u4f60\u9001\u949f\uff0c\u90a3 \u6211\u5012\u662f\u4e50\u610f\u7684\u3002\u201d \u3000\u3000\u90a3\u4e66\u751f\u4e00\u9762\u8d70\u4e00\u9762\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u5b9e\u8bf4\uff0c\u6211\u662f\u968f\u65f6\u51c6\u5907\u6b7b\u6389\u7684\uff0c\u6709\u6ca1\u6709\u4eba\u9001\u7ec8\u90fd \u65e0\u6240\u8c13\u3002\u8001\u5f1f\uff0c\u521a\u624d\u8981\u4e0d\u662f\u4f60\u5e2e\u6211\u7684\u5fd9\uff0c\u6211\u65e9\u5df2\u4e27\u5728\u90a3\u4e24\u4eba\u4e4b\u624b\u4e86\u3002\u6240\u4ee5\uff0c\u4f60\u867d\u7136\u53ea\u662f\u5e2e\u5fd9 \u4e00\u6b21\uff0c\u4ee5\u540e\u4e0d\u80af\u518d\u5e2e\uff0c\u6211\u8fd8\u662f\u975e\u5e38\u611f\u6fc0\u4f60\u7684\u3002\u55ef\uff0c\u5bf9\u5566\uff0c\u4f60\u77a7\u6211\u591a\u7cca\u6d82\uff0c\u6211\u8fd8\u6ca1\u6709\u8bf7\u6559\u4f60\u7684 \u9ad8\u59d3\u5927\u540d\u5462\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u8981\u4f60\u9053\u8c22\uff0c\u4f55\u987b\u9053\u59d3\u901a\u540d\uff1f\u840d\u6c34\u76f8\u7406\uff0c\u6563\u4e86\u5c31\u662f\u6563\u4e86\u3002\u201d\u90a3\u4e66\u751f \u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u3002\u6211\uff0c\u5e76\u4e0d\u662f\u60f3\u8981\u62a5\u7b54\u4f60\uff0c\u563f\uff0c\u563f\uff0c\u6211\u662f\u5728\u60f3\uff0c\u6211\u662f\u5728\u60f3\u2026\u2026\u201d\u91d1\u9010\u6d41\u53cc\u76ee\u4e00 \u77aa\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u60f3\u4ec0\u4e48\uff1f\u75db\u75db\u5feb\u5feb\u5730\u8bf4\uff01\u201d \u3000\u3000\u90a3\u4e66\u751f\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5999\u624b\u7a7a\u7a7a\u624b\u6bb5\u6bd4\u6211\u9ad8\u660e\u5f97\u591a\uff0c\u522b\u4eba\u8bf4\u540c\u884c\u5982\u654c\u56fd\uff0c\u6211\u5219\u662f\u7518\u62dc\u4e0b \u98ce\uff0c\u6211\u662f\u5728\u60f3\uff0c\u4f60\u6709\u8fd9\u526f\u8eab\u624b\uff0c\u5f03\u800c\u4e0d\u7528\uff0c\u5c82\u4e0d\u53ef\u60dc\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u662f\u60f3\u4e0e\u6211 \u5408\u4f19\uff0c\u662f\u4e48\uff1f\u201d\u90a3\u4e66\u751f\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f46\u6211\u82e5\u4e0d\u77e5\u4f60\u7684\u59d3\u540d\u4f4f\u5740\uff0c\u5374\u5230\u90a3\u91cc\u627e\u4f60\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u591a\u53ea\u9999\u7089\u591a\u53ea\u9b3c\uff0c\u6211\u8981\u5077\u4e1c\u897f\u81ea\u5df1\u4e0d\u4f1a\u52a8\u624b\u4e48\uff0c\u4f55\u5fc5\u548c\u4f60\u5408\u4f19\uff1f\u201d\u90a3\u4e66\u751f \u9053\uff1a\u201c\u563f\uff0c\u4f60\u4e5f\u522b\u770b\u5c0f\u6211\u4e86\u3002\u5077\u4e1c\u897f\u7684\u672c\u9886\u6211\u867d\u7136\u6bd4\u4e0d\u4e0a\u4f60\uff0c\u95e8\u69db\u6211\u5374\u5f88\u7cbe\u3002\u4ec0\u4e48\u5730\u65b9\u6709\u597d \u4e1c\u897f\u503c\u5f97\u5077\u7684\u6211\u90fd\u77e5\u9053\uff0c\u4f60\u548c\u6211\u5408\u4f19\uff0c\u6709\u4f60\u7684\u4fbf\u5b9c\u5462\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e0d\u77e5\u4ed6\u662f\u8bf4\u7b11\u8fd8\u662f\u5f53\u771f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u5012\u662f\u6709\u70b9\u53e4\u602a\uff0c\u4e0d\u77e5\u662f\u4f55\u8def\u9053\uff1f\u55ef\uff0c \u6709\u4e86\uff0c\u6c5f\u5e08\u5144\u4ea4\u6e38\u5e7f\u9614\uff0c\u5018\u82e5\u89c1\u7740\u4ed6\uff0c\u5b9a\u4f1a\u77e5\u9053\u4ed6\u7684\u6765\u5386\u3002\u4e14\u770b\u4ed6\u6562\u4e0d\u6562\u53bb\uff1f\u201d\u4e8e\u662f\u8bf4\u9053\uff1a \u201c\u6211\u884c\u8e2a\u65e0\u5b9a\uff0c\u4e0d\u8fc7\uff0c\u4f60\u8981\u627e\u6211\uff0c\u90a3\u4e5f\u5bb9\u6613\uff0c\u4f60\u53ef\u4ee5\u5230\u4e1c\u5e73\u53bf\u6c5f\u6d77\u5929\u7684\u5bb6\u4e2d\uff0c\u968f\u65f6\u53ef\u4ee5\u6253\u542c \u6211\u7684\u6d88\u606f\u3002\u6211\u540d\u53eb\u91d1\u9010\u6d41\uff0c\u6c5f\u6d77\u5929\u662f\u6211\u6700\u8981\u597d\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u8fd9\u4e66\u751f\u89c1\u91d1\u9010\u6d41\u5e74\u7eaa\u592a\u8f7b\uff0c\u5fc3\u4e2d\u534a\u4fe1\u534a\u7591\uff0c\u5f53\u4e0b\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u9601\u4e0b\u8fd8\u662f\u6c5f\u5927 \u4fa0\u7684\u597d\u670b\u53cb\uff0c\u6211\u5012\u662f\u5931\u656c\u4e86\u3002\u201d\u91d1\u9010\u6d41\u602b\u7136\u4e0d\u60a6\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u662f\u5439\u725b\u7684\u4e48\uff1f\u201d\u90a3\u4e66\u751f \u7b11\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u55ef\uff0c\u6211\u6709\u4e9b\u8bdd\u8981\u548c\u4f60\u8bf4\uff0c\u4e14\u5f85\u9001\u949f\u4e4b\u540e\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u89c1\u4ed6\u63d0\u7740\u5927\u949f\uff0c\u4f9d\u7136\u5065\u6b65\u5982\u98de\uff0c\u5012\u4e5f\u6709\u70b9\u60fa\u60fa\u76f8\u60dc\u4e4b\u610f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u672c \u9886\u4e5f\u7b97\u662f\u5f88\u4e0d\u9519\u7684\u4e86\u3002\u4ed6\u8fd9\u843d\u62d3\u4e0d\u7f81\u7684\u6027\u60c5\u4e5f\u5f88\u5bf9\u6211\u7684\u80c3\u53e3\uff0c\u53ea\u53ef\u60dc\u4e0d\u77e5\u4ed6\u7684\u8def\u9053\uff0c\u5374\u662f\u4e0d \u4fbf\u6df1\u4ea4\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u4e24\u4eba\u5df2\u5230\u4e00\u5904\u5c71\u5934\uff0c\u4e0b\u9762\u662f\u4e2a\u6df1\u6f6d\uff0c\u90a3\u4e66\u751f\u7acb\u8db3\u60ac\u5d16\uff0c\u8bf4\u9053\u3002\u201d\u6211\u6bc1\u4e0d\u4e86\u8fd9 \u4e2a\u5927\u949f\uff0c\u5374\u53ef\u4ee5\u53eb\u4eba\u5f97\u4e0d\u7740\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u628a\u5927\u949f\u629b\u51fa\uff0c\u201c\u549a\u201d\u7684\u4e00\u58f0\uff0c\u6c34\u82b1\u56db\u6e85\uff0c\u641e\u788e\u4e86\u4e00 \u6f6d\u5e73\u9759\u3002\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u6b7b\u6c34\u626c\u6ce2\uff0c\u5feb\u54c9\uff01\u5feb\u54c9\uff01\u201d\u91d1\u9010\u6d41\u51b7\u7b11\u9053\uff1a\u201c\u4ece\u4eca\u4e4b\u540e\uff0c\u53ea\u6709\u4f60\u4e00\u4eba \u77e5\u9053\u5929\u9b54\u6559\u7684\u6bd2\u529f\uff0c\u90a3\u5f53\u7136\u662f\u201c\u5feb\u54c9\u2019\u7684\u4e86\uff01\u201d \u3000\u3000\u90a3\u4e66\u751f\u7b11\u5bb9\u4e00\u655b\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\u4f60\u4ee5\u4e3a\u6211\u662f\u8d2a\u8d22\u7684\u4eba\u5417\uff1f\u8fd9\u4e32\u73cd\u73e0\u6211\u5077\u4e86\u6765\u6ca1\u6709\u7528\u5904\uff0c\u8bf7 \u4f60\u6536\u4e0b\uff01\u201d\u91d1\u9010\u6d41\u201c\u54c8\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5012\u597d\u7b11\u4e86\uff0c\u4f60\u8981\u8bc1\u660e\u4f60\u4e0d\u662f\u8d2a\u5fc3\uff0c\u96be\u9053\u6211\u5c31\u8d2a \u5fc3\u5417\uff1f\u8fd9\u4e32\u73cd\u73e0\uff0c\u6211\u82e5\u60f3\u8981\uff0c\u65e9\u5df2\u8981\u4e86\u3002\u6211\u8bf4\u8fc7\u6211\u7edd\u4e0d\u4f1a\u9ed1\u5403\u9ed1\u7684\uff0c\u4f60\u7ed9\u6211\u90a3\u5012\u662f\u770b\u5c0f\u6211 \u4e86\u3002\u201d \u3000\u3000\u90a3\u4e66\u751f\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8bef\u89e3\u4e86\u6211\u7684\u610f\u601d\u4e86\u3002\u4f60\u4e0d\u77e5\u9053\uff0c\u8fd9\u4e32\u73cd\u73e0\u7559\u5728\u6211\u7684\u8eab\u4e0a\u4e43\u662f\u7978 \u6839\uff0c\u4e0d\u5982\u7ed9\u4e86\u4f60\u7684\u597d\u3002\u201d\u91d1\u9010\u6d41\u51b7\u7b11\u9053\uff1a\u201c\u65e2\u662f\u7978\u6839\uff0c\u90a3\u4f60\u5c31\u66f4\u4e0d\u5e94\u8be5\u5bb3\u6211\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e66\u751f\u76b1\u4e86\u76b1\u7709\uff0c\u4f3c\u4e4e\u6709\u4ec0\u4e48\u96be\u8a00\u4e4b\u9690\uff0c\u4e0d\u4fbf\u548c\u91d1\u9010\u6d41\u89e3\u91ca\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u8fd9\u624d\u53f9\u53e3\u6c14 \u9053\uff1a\u201c\u4f60\u4e0d\u80af\u8981\uff0c\u6211\u662f\u65e0\u6cd5\u52c9\u5f3a\u3002\u8fd9\u6837\u5427\uff0c\u4e0d\u5982\u4f60\u62ff\u53bb\u9001\u7ed9\u6c5f\u5927\u4fa0\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u6709\u7528\u5904\u3002\u201d\u91d1 \u9010\u6d41\u66f4\u4e0d\u9ad8\u5174\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u5c82\u80af\u8981\u8fd9\u4e0d\u4e49\u4e4b\u8d22\uff01\u201d \u3000\u3000\u90a3\u4e66\u751f\u9762\u8272\u4e00\u53d8\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u563f\uff0c\u563f\uff0c\u4e0d\u4e49\u4e4b\u8d22\uff01\u8fd9\u5012\u4e5f\u8bf4\u5f97\u662f\u3002\u65e2\u7136\u4f60\u4eec\u90fd\u4e0d\u80af \u8981\uff0c\u8fd9\u4e32\u73cd\u73e0\u7559\u4e0b\u6765\u53ea\u662f\u7978\u60a3\uff0c\u6211\u4fdd\u4e0d\u4f4f\u5b83\u4e5f\u4e0d\u80fd\u8ba9\u574f\u4eba\u5f97\u5230\uff01\u201d\u8bf4\u7f62\u628a\u73e0\u4e32\u4e00\u629b\uff0c\u6697\u8fd0\u5185 \u52b2\u5c06\u94fe\u5b50\u634f\u65ad\uff0c\u4e00\u9897\u9897\u53c8\u5706\u53c8\u5927\u7684\u73cd\u73e0\u5168\u843d\u6f6d\u4e2d\u3002\u6c34\u9762\u5192\u51fa\u8bb8\u591a\u6ce1\u6cab\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u53c8\u5f52\u5e73 \u9759\u3002 \u3000\u3000\u65e0\u4ef7\u660e\u73e0\uff0c\u6c89\u57cb\u6f6d\u5e95\uff0c\u91d1\u9010\u6d41\u867d\u7136\u4e0d\u60f3\u8981\u8fd9\u4e32\u73cd\u73e0\uff0c\u4f46\u5bf9\u4e66\u751f\u7684\u8fd9\u4e00\u7a81\u5982\u5176\u6765\u7684\u4e3e\u52a8\uff0c \u4e5f\u662f\u9887\u611f\u610f\u5916\uff0c\u4e0d\u7981\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d\u90a3\u4e66\u751f\u7b11\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4e0d\u80af\u8981\uff0c\u90a3\u8fd8\u53ef\u60dc\u4ec0 \u4e48\uff1f\u6211\u629b\u4e86\u5b83\uff0c\u80dc\u4e8e\u8ba9\u574f\u4eba\u593a\u4e86\u5b83\u53bb\uff0c\u62ff\u5b83\u6765\u505a\u574f\u4e8b\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u6b64\u4eba\u884c\u4e8b\uff0c\u96be\u4ee5\u6349\u6478\u3002\u4ed6\u5077\u5b66\u5929\u9b54\u6559\u7684\u6bd2\u529f\uff0c\u4f3c\u4e4e\u5fc3\u672f\u4e0d\u6b63\u3002\u53ef\u662f\u4ed6\u53c8 \u820d\u5f97\u5c06\u8fd9\u65e0\u4ef7\u660e\u73e0\u629b\u5f03\uff0c\u8fd9\u5374\u51b3\u4e0d\u662f\u666e\u901a\u4eba\u505a\u5f97\u5230\u7684\u3002\u201d\u4e8e\u662f\u95ee\u9053\uff1a\u201c\u5144\u53f0\u4e4b\u8a00\uff0c\u4f3c\u4e4e\u8bdd\u4e2d \u6709\u8bdd\uff0c\u4e0d\u77e5\u8fd9\u4e32\u73cd\u73e0\u7684\u6765\u5386\u5982\u4f55\uff0c\u6211\u5144\u4f55\u4ee5\u8bf4\u5b83\u662f\u4e2a\u7978\u5bb3\uff1f\u201d \u3000\u3000\u90a3\u4e66\u751f\u9053\uff1a\u201c\u8bf4\u8d77\u8fd9\u4e32\u73cd\u73e0\uff0c\u5012\u662f\u6d89\u53ca\u4e00\u4ef6\u79d8\u5bc6\u3002\u201d\u91d1\u9010\u6d41\u76b1\u4e86\u76b1\u7709\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u662f\u4e8b \u5173\u79d8\u5bc6\uff0c\u90a3\u5c31\u4e0d\u5fc5\u8bf4\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e66\u751f\u7b11\u9053\uff1a\u201c\u5bf9\u5916\u4eba\u6211\u662f\u4e0d\u80af\u8bf4\u7684\uff0c\u8001\u5f1f\u662f\u6c5f\u5927\u4fa0\u7684\u670b\u53cb\uff0c\u8bf4\u4e5f\u65e0\u59a8\u3002\u8fd9\u662f\u6211\u81ea\u613f\u544a \u8bc9\u4f60\u7684\uff0c\u542c\u4e0d\u542c\u968f\u4f60\u7684\u4fbf\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\uff0c\u4f60\u65e2\u7136\u8981\u8bf4\uff0c\u90a3\u6211\u53ea\u597d\u542c\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e66\u751f\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6c5f\u6e56\u4e0a\u6709\u4e2a\u516d\u5408\u5e2e\u5417\uff1f\u201d\u91d1\u9010\u6d41\u901a\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d\u90a3\u4e66\u751f\u9053\uff1a\u201c\u516d \u5408\u5e2e\u7684\u5e2e\u4e3b\u53f2\u767d\u90b5\u5728\u6c5f\u6e56\u4e0a\u5927\u5927\u6709\u540d\uff0c\u96be\u9053\u4f60\u6ca1\u6709\u542c\u8fc7\u4ed6\u7684\u540d\u5b57\uff1f\u201d\u91d1\u9010\u6d41\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211 \u7d20\u6765\u4e0d\u559c\u6b22\u548c\u4ec0\u4e48\u540d\u4eba\u6500\u4ea4\u60c5\uff0c\u4e5f\u4e0d\u60f3\u6253\u542c\u540d\u4eba\u7684\u4e8b\u8ff9\u3002\u6ca1\u6709\u542c\u8fc7\uff01\u201d \u3000\u3000\u90a3\u4e66\u751f\u5927\u4e3a\u8be7\u5f02\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c0f\u53eb\u5316\u65e2\u7136\u662f\u6c5f\u5927\u4fa0\u3001\u6c5f\u6d77\u5929\u7684\u670b\u53cb\uff0c\u5e94\u8be5\u662f\u4e2a\u6709\u6765\u5386\u7684 \u4eba\u7269\uff0c\u600e\u7684\u5374\u6ca1\u6709\u542c\u8fc7\u53f2\u767d\u90b5\u7684\u540d\u5b57\uff1f\u563f\uff0c\u563f\uff0c\u8bf4\u5230\u6b66\u6797\u4e2d\u7684\u6709\u540d\u4eba\u7269\uff0c\u7b2c\u4e00\u4e2a\u5c31\u662f\u6c5f\u5927 \u4fa0\uff0c\u4ed6\u8fd8\u8bf4\u4e0d\u559c\u6b22\u540d\u4eba\u5462\uff1f\u8fd9\u672a\u514d\u592a\u77eb\u60c5\u4e86\uff01\u201d\u4ed6\u54ea\u91cc\u77e5\u9053\u91d1\u9010\u6d41\u662f\u521a\u4ece\u6d77\u5916\u56de\u6765\uff0c\u5bf9\u4e2d\u539f \u7684\u6b66\u6797\u4eba\u4e8b\u5e76\u4e0d\u719f\u6089\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6240\u77e5\u9053\u7684\u53ea\u662f\u4ed6\u7236\u4eb2\u5f53\u5e74\u7684\u4e00\u73ed\u597d\u53cb\uff0c\u516d\u5408\u5e2e\u662f\u65b0\u8fd1\u624d\u5d1b\u8d77\u7684\uff0c\u4ed6\u5f53\u7136\u662f\u4e0d\u77e5\u9053 \u4e86\u3002\u516d\u5408\u5e2e\u548c\u53f2\u767d\u90b5\u7684\u540d\u5934\u6c5f\u6d77\u5929\u662f\u77e5\u9053\u7684\uff0c\u4f46\u91d1\u9010\u6d41\u4e0e\u4ed6\u5e05\u5144\u76f8\u805a\u53ea\u6709\u4e00\u5929\uff0c\u591a\u5c11\u4e8b\u60c5\u8981 \u8c08\uff0c\u6c5f\u6d77\u5929\u5f53\u7136\u4e5f\u662f\u65e0\u6687\u63d0\u53ca\u4e86\u3002 \u3000\u3000\u5c3d\u7ba1\u8fd9\u4e66\u751f\u5bf9\u91d1\u9010\u6d41\u6709\u6240\u8bef\u4f1a\uff0c\u542c\u4e86\u4ed6\u7684\u8bdd\uff0c\u5fc3\u4e2d\u5f88\u4e0d\u8212\u670d\uff0c\u4f46\u4ecd\u7136\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u8fd9\u4e32 \u73cd\u73e0\u662f\u53f2\u767d\u90b5\u8d39\u5c3d\u5fc3\u529b\u5f97\u6765\u7684\u4e1c\u897f\uff0c\u4ed6\u60f3\u62ff\u53bb\u4f5c\u7ed3\u4ea4\u6743\u8d35\u4e4b\u7528\u7684\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5 \u9053\uff1f\u55ef\uff0c\u6211\u8fd8\u6ca1\u6709\u95ee\u4f60\u7684\u540d\u5b57\u5462\u3002\u201d \u3000\u3000\u90a3\u4e66\u751f\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u674e\uff0c\u5355\u540d\u4e00\u4e2a\u6566\u5b57\u3002\u6211\u662f\u53f2\u767d\u90b5\u7684\u2018\u8bb0\u5ba4\u2019\uff0c\u66ff\u4ed6\u638c\u7ba1\u6587\u4e66\u7684\u3002\u201d \u91d1\u9010\u6d41\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5982\u6b64\uff0c\u6240\u4ee5\u4f60\u8981\u5077\u4e86\u8fd9\u4e32\u73e0\u73e0\uff0c\u4ee5\u963b\u4ed6\u7ed3\u4ea4\u6743\u8d35\u3002\u201d\u90a3\u4e66\u751f\u9053\uff1a\u201c\u6211 \u8fd9\u6837\u505a\u4e00\u6765\u662f\u4e3a\u4e86\u7231\u60dc\u53f2\u767d\u90b5\uff0c\u4e8c\u6765\u4e5f\u514d\u5f97\u6b66\u6797\u7684\u4fa0\u4e49\u9053\u53c8\u6dfb\u65b0\u654c\u3002\u54ce\uff0c\u53ef\u60dc\u53f2\u767d\u90b5\u4e0d\u80fd\u4f53 \u8c05\u6211\u7684\u597d\u5fc3\uff0c\u53cd\u800c\u975e\u8981\u6740\u6211\u4e0d\u53ef\uff0c\u6211\u53ea\u597d\u4e1c\u5954\u897f\u8eb2\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5982\u6b64\u3002\u201d\u5fc3\u4e2d\u5374\u662f\u534a\u4fe1\u534a\u7591\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u53f2\u767d\u90b5\u4e3a\u4eba\u5982 \u4f55\u6211\u6beb\u65e0\u6240\u77e5\uff0c\u4e0d\u80fd\u592a\u8fc7\u4fe1\u4ed6\u7247\u9762\u4e4b\u8f9e\u3002\u8bf4\u4e0d\u5b9a\u4ed6\u662f\u5077\u4e86\u5e2e\u4e3b\u7684\u4e1c\u897f\u88ab\u53d1\u89c9\uff0c\u53d7\u7f09\u62ff\uff0c\u6050\u60f9 \u6740\u8eab\u4e4b\u7978\uff0c\u8fd9\u624d\u7f16\u4e86\u4e00\u5957\u8bf4\u8f9e\uff0c\u610f\u6b32\u5411\u6211\u6c42\u52a9\u7684\u3002\u6211\u4e0d\u5982\u6307\u70b9\u4ed6\u5230\u6c5f\u5e08\u5144\u90a3\u513f\uff0c\u662f\u5047\u662f\u771f\uff0c \u6c5f\u5e08\u5144\u81ea\u4f1a\u7a76\u660e\u771f\u76f8\u3002\u201d \u3000\u3000\u90a3\u4e66\u751f\u5374\u4f3c\u731c\u7740\u4e86\u91d1\u9010\u6d41\u7684\u5fc3\u610f\uff0c\u4e0d\u5f85\u91d1\u9010\u6d41\u53d1\u8bdd\uff0c\u4fbf\u5373\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u867d\u7136\u4ea1\u547d\u6c5f\u6e56\uff0c \u5374\u4e5f\u65e0\u987b\u6c42\u4eba\u601c\u60af\u3002\u91d1\u5c11\u4fa0\u4e0e\u6211\u840d\u6c34\u76f8\u9022\uff0c\u51fa\u624b\u52a9\u4e86\u6211\u4e00\u6b21\uff0c\u5728\u91d1\u5c11\u4fa0\u662f\u9022\u573a\u4f5c\u620f\uff0c\u5728\u6211\u5df2 \u662f\u611f\u6fc0\u4e0d\u5c3d\u4e86\u3002\u6211\u5c82\u80fd\u518d\u539a\u7740\u9762\u76ae\uff0c\u9ebb\u70e6\u4e86\u670b\u53cb\uff1f\u597d\uff0c\u597d\uff0c\u4f46\u613f\u540e\u4f1a\u6709\u671f\uff0c\u5c31\u6b64\u522b\u8fc7\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u4e86\u4ed6\u8fd9\u756a\u8bf4\u8bdd\uff0c\u5012\u662f\u6709\u70b9\u8fc7\u610f\u4e0d\u53bb\u3002\u53ef\u662f\u5728\u4eba\u5bb6\u5df2\u7ecf\u8bf4\u4e86\u4e0d\u8981\u4ed6\u7684\u5e2e\u5fd9\u4e4b\u540e\uff0c \u4ed6\u624d\u8bf4\u8981\u5e2e\u5fd9\u4eba\u5bb6\uff0c\u5bf9\u4e8e\u4e00\u4e2a\u6709\u50b2\u9aa8\u7684\u4eba\uff0c\u8fd9\u5c31\u53cd\u800c\u662f\u4fae\u8fb1\u4e86\u3002\u91d1\u9010\u6d41\u60f3\u5230\u4e86\u8fd9\u4e00\u5c42\uff0c\u662f\u4ee5 \u4ed6\u60f3\u8981\u628a\u90a3\u4e66\u751f\u62c9\u4f4f\u5374\u7ec8\u4e8e\u6ca1\u6709\u8ffd\u53bb\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u4e66\u751f\u6717\u58f0\u541f\u9053\uff1a\u201c\u77e5\u6211\u8005\u8c13\u6211\u5fc3\u4f18\uff0c\u4e0d\u77e5\u6211\u8005\u8c13\u6211\u4f55\u6c42\uff1f\u60a0\u60a0\u82cd\u5929\uff0c\u5f7c\u4f55\u4eba \u54c9\uff01\u201d\u56de\u58f0\u5728\u5c71\u8c37\u4e4b\u95f4\u9707\u8361\uff0c\u90a3\u4e66\u751f\u7684\u5f71\u5b50\u5df2\u6ca1\u5165\u6797\u4e2d\uff0c\u770b\u4e0d\u89c1\u4e86\u3002\u91d1\u9010\u6d41\u832b\u7136\u82e5\u5931\uff0c\u5fc3 \u60f3\uff1a\u201c\u4ed6\u5012\u662f\u6709\u610f\u548c\u6211\u7ed3\u4e3a\u77e5\u5df1\u7684\uff0c\u53ef\u60dc\u6211\u5374\u8f9c\u8d1f\u4e86\u4ed6\u7684\u597d\u610f\u4e86\u3002\u201d\u53c8\u60f3\uff1a\u201c\u4ed6\u8bf4\u7684\u516d\u5408\u5e2e \u4e0e\u53f2\u767d\u90b5\u65e2\u7136\u662f\u5927\u5927\u6709\u540d\uff0c\u6211\u7684\u4e16\u4ea4\u53d4\u4f2f\u5b9a\u6709\u6240\u77e5\uff0c\u60f3\u6765\u53ef\u4ee5\u6253\u542c\u5230\u6b64\u4e2d\u771f\u76f8\u3002\u201d\u4e8e\u662f\u4fbf\u5373 \u4e0b\u5c71\uff0c\u524d\u5f80\u6c5f\u82cf\uff0c\u5bfb\u8bbf\u4ed6\u7236\u4eb2\u7684\u65e7\u53cb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6240\u8981\u62dc\u8bbf\u7684\u7b2c\u4e00\u4e2a\u4e16\u53d4\u662f\u9648\u5929\u5b87\u3002\u9648\u5929\u5b87\u662f\u4e16\u5bb6\u4e4b\u5b50\uff0c\u6c5f\u6d77\u5929\u7684\u7236\u4eb2\u6c5f\u5357\u66fe\u7ecf\u505a \u8fc7\u4ed6\u7684\u4e66\u50ee\uff0c\u540e\u6765\u53c8\u7ed3\u62dc\u4e3a\u5144\u5f1f\u7684\u3002\u91d1\u4e16\u9057\u548c\u4ed6\u7684\u4ea4\u60c5\u9664\u4e86\u6c5f\u5357\u548c\u4ef2\u957f\u7edf\u4e4b\u5916\uff0c\u5c31\u6570\u5230\u4ed6 \u4e86\u3002 \u3000\u3000\u9648\u5929\u5b87\u5bb6\u4f4f\u5728\u6c5f\u82cf\u6728\u6e0e\uff0c\u90a3\u662f\u4e00\u4e2a\u79bb\u82cf\u5dde\u7ea6\u767e\u91cc\u4e4b\u9065\u7684\u4e00\u4e2a\u5c0f\u9547\uff0c\u9760\u8fd1\u592a\u6e56\uff0c\u98ce\u666f\u5f88 \u7f8e\u3002\u8fd9\u4e00\u65e5\u91d1\u9010\u6d41\u6765\u5230\u6728\u6e0e\uff0c\u5df2\u7ecf\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u7239\u7239\u8bf4\u9648\u53d4\u53d4\u662f\u5b98\u5ba6\u4eba\u5bb6\uff0c \u867d\u7136\u5230\u4e86\u9648\u53d4\u53d4\u8fd9\u4e00\u4ee3\u5df2\u7ecf\u4e0d\u518d\u4e3a\u5b98\uff0c\u4f5c\u4e86\u6b66\u6797\u4eba\u7269\uff0c\u4f46\u65e7\u5bb6\u98ce\u8fd8\u662f\u5f88\u8bb2\u7a76\u7684\u3002\u5728\u6c5f\u6e56\u4e0a\u6211 \u53ef\u4ee5\u6e38\u620f\u98ce\u5c18\uff0c\u5230\u4e86\u957f\u8f88\u5bb6\u4e2d\uff0c\u90a3\u5374\u662f\u8981\u8bb2\u7a76\u4e00\u70b9\u793c\u6570\u4e86\u3002\u201d\u4e8e\u662f\u5728\u9547\u4e0a\u5077\u4e86\u4e00\u5957\u65b0\u8863\u88f3\uff0c \u5230\u65e0\u4eba\u4e4b\u5904\u6362\u4e0a\uff0c\u53c8\u53bb\u7406\u4e86\u4e00\u6b21\u53d1\uff0c\u8fd9\u624d\u5230\u9648\u5bb6\u53bb\uff0c\u8fd9\u65f6\u5df2\u7ecf\u662f\u4e8c\u66f4\u65f6\u5206\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5728\u9648\u5bb6\u95e8\u524d\u5f98\u5f8a\u4e86\u4e00\u9635\uff0c\u5bfb\u601d\uff1a\u201c\u6211\u82e5\u6572\u95e8\u6c42\u89c1\uff0c\u987b\u5f97\u8d39\u8bb8\u591a\u5de5\u592b\u624d\u80fd\u8bf4\u5f97\u6e05 \u695a\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u96be\u514d\u60ca\u52a8\u90bb\u91cc\uff1b\u6211\u65e0\u6240\u8c13\uff0c\u53ea\u6015\u9648\u53d4\u53d4\u4f1a\u6709\u987e\u5fcc\u3002\u4e0d\u5982\u5148\u8fdb\u53bb\u518d\u8bf4\u3002\u201d\u8981\u77e5 \u91d1\u9010\u6d41\u7684\u7236\u4eb2\u91d1\u4e16\u9057\u66fe\u662f\u671d\u5ef7\u7684\u94a6\u72af\uff0c\u867d\u7136\u91d1\u4e16\u9057\u9041\u8ff9\u6d77\u5916\u5df2\u4e8c\u5341\u5e74\uff0c\u4f46\u5374\u8fd8\u662f\u672a\u66fe\u201c\u8096 \u6848\u201d\u7684\u3002\u800c\u91d1\u9010\u6d41\u8981\u8bf4\u660e\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u5fc5\u987b\u5148\u8bf4\u51fa\u4ed6\u7236\u4eb2\u7684\u540d\u5b57\uff0c\u662f\u4ee5\u91d1\u6d41\u9010\u6050\u9632\u9648\u5929\u5b87\u6709 \u6240\u987e\u5fcc\u3002 \u3000\u3000\u5168\u9010\u6d41\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u8df3\u8fdb\u9648\u5bb6\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u5413\u4ed6\u4e00\u60ca\uff0c\u518d\u5411\u4ed6\u8d54\u7f6a\uff0c\u60f3\u6765\u9648\u53d4\u53d4\u4e5f\u4e0d \u4f1a\u89c1\u602a\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7528\u7684\u662f\u7edd\u9876\u8f7b\u529f\uff0c\u8eab\u5982\u4e00\u53f6\u98d8\u843d\uff0c\u6beb\u65e0\u58f0\u606f\uff0c\u4ee5\u4e3a\u9648\u5929\u5b87\u592b\u5987\u5b9a\u7136\u6ca1\u6709\u53d1\u89c9\u4ed6\uff0c \u8fd8\u6050\u6015\u4ed6\u4eec\u53d7\u60ca\uff0c\u54ea\u77e5\u9648\u5929\u5b87\u592b\u5987\u65e9\u5df2\u5728\u6697\u4e2d\u57cb\u4f0f\uff0c\u7b49\u4ed6\u8fdb\u6765\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u811a\u672a\u6cbe\u5730\uff0c\u53ea\u542c\u5f97\u55e4\u55e4\u7684\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\u5df2\u7136\u88ad\u5230\uff0c\u90a3\u6697\u5668\u4e5f\u4e0d\u77e5\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u4e00 \u9897\u9897\u597d\u4f3c\u73cd\u73e0\u5927\u5c0f\uff0c\u4eae\u6676\u6676\u7684\uff0c\u4ece\u7a7a\u4e2d\u6d12\u4e0b\uff0c\u91d1\u9010\u6d41\u4ee5\u5288\u7a7a\u638c\u6253\u51fa\uff0c\u90a3\u4e00\u9897\u9897\u597d\u4f3c\u73cd\u73e0\u7684\u6697 \u5668\u500f\u5ffd\u788e\u88c2\uff0c\u6563\u51fa\u5bd2\u5149\u51b7\u6c14\uff0c\u91d1\u9010\u6d41\u673a\u4f36\u4f36\u4ed6\u6253\u4e86\u4e00\u4e2a\u51b7\u98a4\uff0c\u4e0d\u7531\u5f97\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u53d1\u6697\u5668\u7684\u662f\u9648\u5929\u5b87\u7684\u59bb\u5b50\u5e7d\u840d\uff0c\u6240\u53d1\u7684\u6697\u5668\u540d\u4e3a\u201c\u51b0\u9b44\u795e\u5f39\u201d\uff0c\u90a3\u662f\u4ece\u5929\u5c71\u51b0\u7a9f\u4e4b\u4e2d\uff0c \u64b7\u53d6\u51b0\u9b44\u7cbe\u82f1\uff0c\u7ec3\u6210\u7684\u4e00\u79cd\u4e16\u4e0a\u72ec\u4e00\u65e0\u4e8c\u7684\u5947\u95e8\u6697\u5668\u3002\u4e16\u4e0a\u6240\u6709\u7684\u6697\u5668\u6216\u7528\u4ee5\u4f24\u4eba\uff0c\u6216\u7528\u4ee5 \u6253\u7a74\uff0c\u6240\u8bb2\u7a76\u7684\u4e0d\u5916\u4e4e\u51c6\u5934\u3001\u52b2\u529b\u7684\u529f\u592b\uff0c\u6216\u8005\u518d\u52a0\u4e0a\u6697\u5668\u672c\u8eab\u7684\u950b\u5229\uff0c\u552f\u6709\u201c\u51b0\u9b44\u795e\u5f39\u201d \u4e0d\u540c\uff0c\u5b83\u6240\u501a\u4ed7\u7684\u662f\u4e07\u8f7d\u7384\u51b0\u7684\u90a3\u79cd\u9634\u51b7\u4e4b\u6c14\uff0c\u5bd2\u6c14\u53d1\u51fa\uff0c\u7aef\u7684\u662f\u4fb5\u80a4\u523a\u9aa8\uff0c\u5389\u5bb3\u975e\u5e38\uff0c\u5e7d \u840d\u4ee5\u524d\u505a\u51b0\u5bab\u4f8d\u5973\u7684\u65f6\u5019\uff1b\u51b0\u5ddd\u5929\u5973\u7ed9\u4e86\u5979\u4e00\u74f6\u51b0\u9b44\u795e\u5f39\uff0c\u5171\u6709\u767e\u9897\u3002\u5982\u4eca\u8fd8\u5269\u6709\u4e09\u5341\u591a \u9897\uff0c\u5df2\u662f\u591a\u5e74\u6765\u6ca1\u6709\u4f7f\u7528\u8fc7\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u56fa\u7136\u5403\u60ca\uff0c\u5e7d\u840d\u66f4\u662f\u5403\u60ca\u4e0d\u5c0f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u8d3c\u5c45\u7136\u7981\u53d7\u5f97\u8d77\u6211\u7684\u51b0\u9b44\u795e \u5f39\uff0c\u5012\u662f\u4e0d\u53ef\u5c0f\u89d1\u4e86\u3002\u201d\u91d1\u9010\u6d41\u6697\u8fd0\u7384\u529f\uff0c\u6d88\u9664\u9634\u5bd2\u4e4b\u6c14\uff0c\u4e00\u65f6\u672a\u80fd\u5f00\u53e3\u8bf4\u8bdd\u201d\u3002\u8bf4\u65f6\u8fdf\uff0c \u90a3\u65f6\u5feb\uff0c\u5e7d\u840d\u5df2\u662f\u6325\u5251\u523a\u6765\uff0c\u65a5\u9053\uff1a\u201c\u516d\u5408\u5e2e\u7684\u5c0f\u8d3c\uff0c\u6211\u6b63\u8981\u627e\u4f60\u4eec\u7b97\u5e10\uff0c\u4f60\u5012\u5148\u6765\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u89c1\u5979\u8fd9\u4e00\u5251\u6765\u52bf\u51cc\u5389\uff0c\u53ea\u597d\u65bd\u5c55\u4e0a\u4e58\u7684\u5185\u529f\u3001\u4e2d\u6307\u4e00\u63b8\uff0c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u5979\u7684 \u5bd2\u7389\u5251\u5f39\u5f00\uff0c\u5e7d\u840d\u7684\u5185\u529b\u4e0d\u53ca\u91d1\u9010\u6d41\uff0c\u8e6c\u8e6c\u8e6c\u7684\u8fde\u9000\u4e09\u6b65\u3002 \u3000\u3000\u9648\u5929\u5b87\u559d\u9053\uff1a\u201c\u5c0f\u8d3c\u4f11\u8981\u901e\u80fd\uff01\u201d\u5530\u5530\u5530\u8fde\u73af\u4e09\u5251\uff0c\u5251\u5251\u6307\u5411\u91d1\u9010\u6d41\u7684\u8981\u5bb3\uff0c\u4ed6\u662f\u6015\u91d1 \u9010\u6d41\u4f24\u5bb3\u4ed6\u7684\u59bb\u5b50\uff0c\u6545\u800c\u4e0d\u80fd\u4e0d\u72e0\u4e0b\u6740\u624b\u3002\u9648\u5929\u5b87\u7684\u529f\u529b\u8fdc\u5728\u59bb\u5b50\u4e4b\u4e0a\uff0c\u91d1\u9010\u6d41\u4e0d\u6562\u7528\u201c\u5f39 \u6307\u795e\u901a\u201d\u7684\u529f\u592b\u5f39\u5f00\u4ed6\u7684\u5b9d\u5251\uff0c\u767e\u5fd9\u4e2d\u53ea\u80fd\u4f7f\u51fa\u201c\u5929\u7f57\u6b65\u6cd5\u201d\uff0c\u5de7\u5999\u5730\u907f\u4e86\u4e24\u62db\uff0c\u9648\u5929\u5b57\u7b2c \u4e09\u5251\u523a\u5230\uff0c\u91d1\u9010\u6d41\u6325\u8896\u4e00\u5377\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u8896\u5b50\u7ed9\u9648\u5929\u5b87\u524a\u53bb\u4e86\u4e00\u622a\uff0c\u53ef\u662f\u9648\u5929\u5b87\u7684\u5b9d\u5251 \u4ea6\u5df2\u7ed9\u4ed6\u5f39\u5f00\u3002 \u3000\u3000\u9648\u5927\u5b57\u597d\u751f\u60ca\u5f02\uff0c\u6309\u5251\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u5e7d\u840d\u8bf4\u9053\uff1a\u201c\u8fd8\u7528\u95ee\u4ed6\uff1f\u9664\u4e86\u516d\u5408\u5e2e\u8fd8\u6709 \u8c01\u6562\u6697\u7b97\u54b1\u4eec\u3002\u201d\u9648\u5927\u5b57\u9053\u3002\u201d\u597d\u50cf\u4e0d\u5bf9\u3002\u5582\uff0c\u4f60\u5feb\u8bf4\u5b9e\u8bdd\uff0c\u4f60\u662f\u4e0d\u662f\u53f2\u767d\u90b5\u6d3e\u6765\u7684\u4eba\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5934\u9876\u4e0a\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u6b64\u65f6\u4ed6\u5df2\u628a\u4fb5\u5165\u4f53\u4e2d\u7684\u9634\u5bd2\u4e4b\u6c14\u5c3d\u90fd\u9a71\u51fa\uff0c\u4e8e\u662f\u54c8\u54c8 \u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u9648\u53d4\u53d4\uff0c\u6211\u8fd9\u4e2a\u6212\u6307\u60f3\u6765\u9648\u53d4\u53d4\u4f1a\u8ba4\u5f97\u5427\uff1f\u5c0f\u4f84\u91d1\u9010\u6d41\uff0c\u5f85\u6765\u62dc\u8bbf\u53d4\u53d4\uff0c\u8fd9 \u53a2\u6709\u793c\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7684\u6212\u6307\u662f\u4ed6\u7236\u4eb2\u9633\u4e54\u5317\u6e9f\u6240\u7559\u7684\u7389\u7bad\u6253\u9020\u7684\uff0c\u5171\u6709\u4e09\u679a\uff0c\u4e00\u679a\u7ed9\u4e86\u6c5f\u6d77\u5929\uff0c\u4e00\u679a \u7ed9\u4e86\u4ed6\u6307\u5b9a\u6c5f\u6d77\u5929\u8981\u53bb\u4f1a\u89c1\u7684\u90a3\u4e2a\u4eba\uff0c\u6700\u540e\u4e00\u679a\u5219\u7ed9\u4e86\u513f\u5b50\u3002 \u3000\u3000\u9648\u5929\u5b87\u521a\u624d\u770b\u89c1\u91d1\u9010\u6d41\u4f7f\u51fa\u201c\u5929\u7f57\u6b65\u6cd5\u201d\uff0c\u5df2\u662f\u8d77\u7591\uff0c\u6b64\u65f6\u518d\u7559\u5fc3\u770b\u4e86\u770b\u4ed6\u7684\u7389\u6212\uff0c\u767b \u65f6\u660e\u767d\u8fc7\u6765\uff0c\u4e0d\u7981\u60ca\u559c\u4ea4\u96c6\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u7239\u7239\u662f\u91d1\u4e16\u9057\u3001\u91d1\u5927\u4fa0\u4e48\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5c0f\u4f84 \u6b63\u662f\u5949\u4e86\u5bb6\u7236\u4e4b\u547d\uff0c\u7279\u6765\u62dc\u8bbf\u53d4\u53d4\u3002\u201d \u3000\u3000\u5e7d\u840d\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u8d70\u8fc7\u6765\u9053\uff1a\u201c\u4f60\u4f55\u4e0d\u65e9\u8bf4\uff1f\u5012\u5f04\u5f97\u6211\u4eec\u8bef\u4f1a\u4e86\u3002\u201d\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a \u201c\u6211\u4e00\u5230\u6765\uff0c\u4f60\u5c31\u7528\u51b0\u9b44\u795e\u5f39\u6253\u6211\uff0c\u6211\u54ea\u6709\u5de5\u592b\u548c\u4f60\u8bf4\u8bdd\uff1f\u201d\u5f53\u4e0b\u884c\u4e86\u89c1\u9762\u4e4b\u672d\uff0c\u8bf4\uff1a\u201c\u5c0f \u4f84\u6050\u6709\u4e0d\u4fbf\uff0c\u672a\u66fe\u901a\u540d\u5148\u81ea\u8fdb\u6765\uff0c\u602a\u4e0d\u5f97\u53d4\u53d4\u5a76\u5a76\u8bef\u4f1a\u3002\u53ea\u4e0d\u77e5\u5a76\u5a76\u4f55\u4ee5\u628a\u5c0f\u4f84\u5f53\u4f5c\u662f\u516d\u5408 \u5e2e\u7684\uff1f\u201d \u3000\u3000\u5e7d\u840d\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u8bf4\u6765\u8bdd\u957f\uff0c\u54b1\u4eec\u8fdb\u53bb\u518d\u8bf4\u3002\u4ee4\u5c0a\u4ee4\u5802\u90fd\u597d\u5417\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6258\u5e87\u8fd8 \u597d\u3002\u201d\u9648\u5929\u5b87\u54fd\u54bd\u53f9\u9053\uff1a\u201c\u6643\u773c\u4e8c\u5341\u5e74\uff0c\u6211\u4eec\u90fd\u5df2\u8001\u4e86\uff0c\u60f3\u4e0d\u5230\u4eca\u65e5\u8fd8\u5f97\u89c1\u6545\u4eba\u4e4b\u5b50\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u5c06\u91d1\u9010\u6d41\u5e26\u5230\u4ed6\u7684\u4e66\u623f\uff0c\u5750\u5b9a\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u516d\u5408\u5e2e\u662f\u6700\u8fd1\u51e0\u5e74\u624d\u5728\u6c5f\u6e56\u4e0a\u5d1b\u8d77 \u7684\u4e00\u4e2a\u5e2e\u4f1a\uff0c\u5e2e\u4e3b\u53f2\u767d\u90b5\uff0c\u636e\u8bf4\u6b66\u529f\u5f88\u9ad8\uff0c\u6211\u5374\u6ca1\u6709\u4f1a\u8fc7\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u90a3\u4e48\u516d\u5408\u5e2e\u4f55\u4ee5 \u8981\u6765\u627e\u53d4\u53d4\u7684\u9ebb\u70e6\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u548c\u4ed6\u4eec\u672c\u6765\u662f\u98ce\u9a6c\u725b\u4e0d\u76f8\u53ca\u7684\uff0c\u8fd9\u4e8b\u662f\u5c0f\u513f\u548c\u4ed6\u4eec\u7ed3\u4e0b\u7684\u6881\u5b50\u3002\u201d\u5e7d\u840d \u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u5c0f\u513f\u9648\u5149\u7167\uff0c\u524d\u51e0\u5e74\u5df2\u7ecf\u51fa\u9053\uff0c\u6700\u8fd1\u4e3a\u4e86\u516d\u5408\u5e2e\u4e4b\u4e8b\uff0c\u56de\u8fc7\u5bb6\u4e00\u8d9f\u3002\u53ef\u60dc\u4f60\u6765 \u8fdf\u4e86\u4e00\u6b65\uff0c\u4ed6\u662f\u6628\u5929\u521a\u8d70\u7684\u3002\u201d\u63a5\u7740\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u521a\u624d\u5728\u6211\u4eec\u7684\u95e8\u524d\u5f98\u5f8a\uff0c\u6211\u8fd8\u4ee5\u4e3a\u662f\u516d \u5408\u5e2e\u5f97\u77e5\u4ed6\u56de\u5bb6\u7684\u6d88\u606f\uff0c\u6d3e\u4eba\u6765\u4fa6\u67e5\u7684\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4e0a\u6708\u5c0f\u513f\u5728\u5180\u5317\u8def\u4e0a\uff0c\u78b0\u89c1\u516d\u5408\u5e2e\u7684\u4eba\u6253\u52ab\u4e00\u4f19\u836f\u6750\u5546\u4eba\uff0c\u5c0f\u513f\u62d4 \u5251\u76f8\u52a9\uff0c\u6740\u4e86\u516d\u5408\u5e2e\u7684\u4e24\u4e2a\u4eba\uff0c\u4f46\u4ed6\u4eec\u5e2e\u4e2d\u6709\u4e2a\u548c\u5c1a\uff0c\u5f88\u4e3a\u4e86\u5f97\uff0c\u4ed6\u7740\u4e86\u5c0f\u513f\u4e00\u5251\uff0c\u5c0f\u513f\u4e5f \u7ed9\u4ed6\u6253\u4e86\u4e00\u679a\u900f\u9aa8\u9489\uff0c\u56de\u5bb6\u4f11\u517b\u4e86\u51e0\u5929\u624d\u597d\u3002\u6881\u5b50\u5c31\u662f\u8fd9\u6837\u7ed3\u4e0b\u6765\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u516d\u5408\u5e2e\u4e43\u662f\u532a\u5e2e\uff0c\u53f2\u767d\u90b5\u4e5f\u662f\u4e2a\u9b54\u5934\u4e86\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u53f2\u767d\u90b5\u5584\u4e8e\u4f5c\u4f2a\uff0c\u4ee5\u524d\u5012\u9887\u6709\u201c\u8c6a\u4fa0\u4e4b\u540d\uff0c\u8c01\u77e5\u9053\u4ed6\u5374\u662f\u6697\u4e2d\u65e0\u6076\u4e0d\u4f5c\u3002 \u4e0d\u8fc7\uff0c\u4e5f\u8fd8\u662f\u6700\u8fd1\u4e00\u5e74\uff0c\u4ed6\u7684\u6076\u884c\u624d\u6e10\u6e10\u7ed9\u6c5f\u6e56\u4e0a\u7684\u4fa0\u4e49\u9053\u77e5\u6653\uff0c\u6240\u4ee5\u8fd8\u672a\u66fe\u526a\u9664\u4ed6\u4eec\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u542c\u8bf4\u53f2\u767d\u90b5\u60f3\u8981\u7ed3\u4ea4\u6743\u8d35\uff0c\u6295\u9760\u671d\u5ef7\uff0c\u6709\u8fd9\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u4e5f\u542c\u5230\u98ce\u58f0\u4e86\u3002\u4e0b\u4e2a\u6708\u521d\u5341\u662f\u5927\u5185\u603b\u7ba1\u8428\u798f\u9f0e\u7684\u516d\u5341\u5bff\u8fb0\uff0c\u542c \u8bf4\u53f2\u767d\u90b5\u8981\u4eb2\u81ea\u7ed9\u4ed6\u795d\u5bff\uff0c\u8fd8\u5230\u5904\u641c\u7f57\u5947\u73cd\u5f02\u5b9d\uff0c\u9001\u53bb\u505a\u8d3a\u793c\u5462\u3002\u8fd9\u662f\u4ed6\u4eec\u5e2e\u4e2d\u7684\u4eba\u6cc4\u6f0f\u51fa \u6765\u7684\uff0c\u60f3\u6765\u4e0d\u5047\u3002\u90a3\u6b21\u4ed6\u4eec\u62a2\u52ab\u90a3\u4f19\u836f\u6750\u5546\u4eba\uff0c\u5c31\u662f\u56e0\u4e3a\u5728\u90a3\u6279\u836f\u6750\u4e2d\u6709\u4e00\u652f\u5343\u5e74\u4f55\u9996\u4e4c\uff0c \u5df2\u7531\u4e00\u4e2a\u4eb2\u738b\u5b9a\u4e0b\uff0c\u516d\u5408\u5e2e\u5374\u60f3\u62a2\u8fc7\u6765\u732e\u7ed9\u8428\u798f\u9f0e\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\u3002\u201d\u4e0d\u77e5\u4ed6\u8981\u9001\u7684\u8d3a\u793c\u4e4b\u4e2d\uff0c\u6709\u6ca1\u6709\u4e00\u4e32\u4ef7\u503c\u8fde\u57ce\u7684\u73cd\u73e0\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u8fd9\u6211\u53ef\u4e0d\u77e5\u9053\u4e86\u3002\u8d24\u4f84\u56e0\u4f55\u6709\u6b64\u4e00\u95ee\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5c06\u4ed6\u5728\u5f82\u5f95\u5c71\u5929\u9b54\u6559\u795e\u5e99\u4e2d\u7684\u906d\u9047\u544a\u8bc9\u4e86\u9648\u5929\u5b87\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e2a\u81ea\u79f0\u662f\u53f2\u767d\u90b5\u8bb0 \u5ba4\u7684\u674e\u6566\uff0c\u4e0d\u77e5\u9648\u53d4\u53d4\u53ef\u77e5\u6b64\u4eba\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u6211\u6ca1\u6709\u542c\u4eba\u8bf4\u8fc7\u3002\u4e0d\u8fc7\u6765\u6349\u62ff\u4ed6\u7684\u90a3\u4e24\u4e2a\u4eba\u5728\u6c5f\u6e56\u4e0a\u5374\u662f\u9887\u6709\u540d\u6c14\u7684\uff0c\u4ed6 \u4eec\u4e5f\u662f\u540d\u5217\u516d\u5408\u5e2e\u4e2d\u56db\u5927\u9ad8\u624b\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u56db\u5927\u9ad8\u624b\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u516d\u5408\u5e2e\u4e2d\u56db\u5927\u9ad8\u624b\uff0c\u4e00\u548c\u5c1a\u3001\u4e00\u9053\u58eb\u3001\u4e00\u5f3a\u76d7\u3001\u4e00\u5be1\u5987\u3002\u548c\u5c1a\u6cd5\u540d\u5706\u6d77\u3002 \u9053\u58eb\u9053\u53f7\u9752\u7b26\u3002\u5f3a\u76d7\u540d\u53eb\u7126\u78ca\uff0c\u672c\u6765\u662f\u8fdc\u4e1c\u7684\u72ec\u811a\u5927\u76d7\uff0c\u6bd4\u6b66\u8f93\u7ed9\u4e86\u53f2\u767d\u90b5\uff0c\u81ea\u613f\u505a\u4ed6\u7684\u624b \u4e0b\u7684\u3002\u8fd8\u6709\u4e2a\u5be1\u5987\u8463\u5341\u4e09\u5a18\uff0c\u636e\u8bf4\u5728\u56db\u4eba\u4e4b\u4e2d\uff0c\u4ee5\u5979\u7684\u6b66\u529f\u6700\u9ad8\uff0c\u4e08\u592b\u6b7b\u540e\uff0c\u7ed9\u53f2\u767d\u90b5\u52fe\u642d \u4e0a\u624b\uff0c\u4e5f\u5c31\u505a\u4e86\u516d\u5408\u5e2e\u7684\u5973\u9999\u4e3b\u4e86\u3002\u4f60\u5728\u5f82\u5f95\u5c71\u78b0\u4e0a\u7684\u662f\u9053\u58eb\u548c\u5f3a\u76d7\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\u3002\u201d\u90a3\u72ec\u811a\u5927\u76d7\u7126\u78ca\u7684\u6b66\u529f\u5012\u4e0d\u600e\u4e48\u6837\uff0c\u9752\u7b26\u9053\u4eba\u5374\u662f\u9887\u4e3a\u4e86\u5f97\uff0c\u90a3\u65e5\u6211\u867d\u7136 \u80dc\u4e86\u4ed6\uff0c\u80dc\u6765\u4e5f\u5f88\u4e0d\u5bb9\u6613\u3002\u90a3\u4e2a\u5be1\u5987\u7684\u6b66\u529f\u8fd8\u5728\u4ed6\u4e4b\u4e0a\uff0c\u8fd9\u4e48\u8bf4\u6765\uff0c\u516d\u5408\u5e2e\u4e2d\u5012\u4e5f\u4e0d\u4e4f\u80fd\u4eba \u5462\uff01\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u636e\u8bf4\u53f2\u767d\u90b5\u7684\u6b66\u529f\u66f4\u9ad8\uff0c\u6240\u4ee5\u8981\u6d88\u706d\u516d\u5408\u5e2e\u53ef\u4e5f\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u60c5 \u5462\uff01\u201d \u3000\u3000\u5e7d\u840d\u9053\u3002\u201d\u4e0d\u8fc7\uff0c\u6211\u4eec\u592b\u5987\u603b\u662f\u8981\u548c\u516d\u5408\u5e2e\u7b97\u4e00\u7b97\u5e10\u7684\u3002\u91d1\u8d24\u4f84\uff0c\u4f60\u7684\u6c5f\u5e08\u5144\u53ef\u66fe\u548c\u4f60 \u8c08\u8fc7\u516d\u5408\u5e2e\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u90a3\u4e24\u5929\u6c5f\u5e08\u5144\u5fd9\u4e8e\u62db\u5f85\u5bbe\u5ba2\uff0c\u4e00\u4e9b\u4e0d\u592a\u91cd\u8981\u7684\u4e8b\u60c5\u4ed6\u5c31\u6ca1\u6709\u548c\u6211\u8c08\u4e86\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u4f60\u7684\u5e08\u5144\u5ac1\u5973\uff0c\u6211\u4eec\u672c\u6765\u8be5\u53bb\u9053\u8d3a\u7684\uff0c\u53ea\u56e0\u5c0f\u513f\u6070\u5de7\u56de\u5bb6\u517b\u75c5\uff0c\u4ee5\u81f4\u4e0d\u80fd \u62bd\u8eab\uff0c\u5b9e\u662f\u62b1\u6b49\u3002\u4e0d\u8fc7\uff0c\u6211\u8fd8\u662f\u60f3\u53bb\u62dc\u8bbf\u4ee4\u5e08\u5144\u7684\u3002\u201d\u539f\u6765\u9648\u5929\u5b87\u867d\u7136\u4e0d\u6015\u516d\u5408\u5e2e\uff0c\u4f46\u4e5f\u6050 \u9632\u53f2\u767d\u90b5\u4e0e\u4ed6\u5e2e\u4e2d\u7684\u56db\u5927\u9ad8\u624b\u9f50\u6765\uff0c\u4ed6\u4eec\u592b\u59bb\u4e8c\u4eba\u53ef\u5c31\u5e94\u4ed8\u4e0d\u4e86\u3002\u662f\u4ee5\u60f3\u53bb\u63a2\u8bbf\u6c5f\u6d77\u5929\uff0c\u4e00 \u6765\u907f\u907f\u98ce\u5934\uff0c\u4e8c\u6765\u4e5f\u597d\u4e0e\u6c5f\u6d77\u5929\u5546\u91cf\u5982\u4f55\u5bf9\u4ed8\u516d\u5408\u5e2e\u4e4b\u6cd5\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6c5f\u5e08\u5144\u6700\u8fd1\u6050\u6015\u8981\u5230\u5317\u4eac\u8d70\u4e00\u8d9f\uff0c\u51c6\u5907\u53d6\u9053\u897f\u5317\uff0c\u515c\u4e00\u4e2a\u5927\u5708\u3002\u201d\u9648\u5929\u5b87 \u9053\uff1a\u201c\u8fd9\u5374\u4e3a\u4f55\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4ed6\u60f3\u5728\u8fdb\u5317\u4eac\u4e4b\u524d\uff0c\u5148\u5230\u897f\u661f\u63a2\u8bbf\u7afa\u5c1a\u7236\u3002\u201d\u9648\u5929\u5b87\u9053\uff1a \u201c\u4ed6\u4ec0\u4e48\u65f6\u5019\u53ef\u5230\u5317\u4eac\uff0c\u4f60\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u660e\u5e74\u5143\u5bb5\u8282\uff0c\u6c5f\u5e08\u5144\u4e00\u5b9a\u4f1a\u5728\u5317\u4eac\u3002\u201d\u91d1\u9010\u6d41\u56e0\u4e3a\u6c5f\u6d77\u5929\u7684\u79d8\u9b54\u5d16\u4e4b\u4f1a\u662f \u4ed6\u7239\u7239\u6307\u5b9a\u7684\uff0c\u53ea\u8981\u4ed6\u5e08\u5144\u4e00\u4e2a\u4eba\u53bb\uff0c\u91d1\u9010\u6d41\u4e0d\u77e5\u662f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6240\u4ee5\u6ca1\u6709\u544a\u8bc9\u9648\u5929\u5b87\u3002\u4ed6\u60f3 \u5e08\u5144\u4ea4\u6e38\u5e7f\u9614\uff0c\u9648\u5929\u5b87\u82e5\u5230\u5317\u4eac\uff0c\u4e00\u5b9a\u53ef\u4ee5\u627e\u5230\u4ed6\u7684\u5e08\u5144\u3002 \u3000\u3000\u9648\u5929\u5b87\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211\u5230\u5317\u4eac\u4e0e\u4ee4\u5e08\u5144\u76f8\u4f1a\u4fbf\u662f\u3002\u516d\u5408\u5e2e\u5fd9\u4e8e\u7ed9\u8428\u798f\u9f0e\u8d3a\u5bff\uff0c\u5927\u7ea6\u8fd8 \u4e0d\u81f3\u4e8e\u5c31\u6765\u627e\u6211\u751f\u4e8b\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5728\u9648\u5bb6\u4f4f\u4e86\u4e24\u5929\uff0c\u7b2c\u4e09\u5929\u624d\u4e0e\u9648\u5929\u5b87\u592b\u59bb\u9053\u522b\uff0c\u7ea6\u5b9a\u4e86\u660e\u5e74\u7684\u5143\u5bb5\u8282\u8fc7\u540e\u5728\u5317\u4eac \u76f8\u4f1a\u3002 \u3000\u3000\u8fd9\u4e00\u5929\u6070\u597d\u662f\u4e5d\u6708\u5341\u4e94\uff0c\u8ddd\u79bb\u660e\u5e74\u5143\u5bb5\u8fd8\u6709\u6574\u6574\u7684\u56db\u4e2a\u6708\u3002\u91d1\u9010\u6d41\u5e76\u4e0d\u5fd9\u4e8e\u8d76\u8def\uff0c\u5fc3\u91cc \u60f3\u9053\uff1a\u201c\u6211\u65e9\u5c31\u542c\u5f97\u4eba\u8bf4\uff1a\u4e0a\u6709\u5929\u5802\uff0c\u4e0b\u6709\u82cf\u676d\uff0c\u8fd9\u4e00\u6b21\u5230\u4e86\u82cf\u5dde\uff0c\u53ef\u5f97\u75db\u75db\u5feb\u5feb\u5730\u73a9\u5b83\u51e0 \u5929\u3002\u201d\u4e00\u8def\u884c\u6765\uff0c\u4f46\u89c1\u7530\u4ea9\u7eb5\u6a2a\uff0c\u6e2f\u6c49\u4ea4\u9519\uff0c\u6ce2\u5149\u4e91\u5f71\uff0c\u6c34\u79c0\u5c71\u6e05\uff0c\u7aef\u7684\u662f\u5982\u5728\u753b\u4e2d\uff0c\u5904\u5904 \u663e\u51fa\u6c5f\u5357\u6c34\u4e61\u7684\u60c5\u8c03\u3002 \u3000\u3000\u91d1\u9010\u6d41\u653e\u76ee\u6d4f\u89c8\uff0c\u5fc3\u65f7\u795e\u6021\uff0c\u4e0d\u7981\u7eb5\u60c5\u8d5e\u53f9\uff0c\u51e0\u4e4e\u5c31\u8981\u5728\u8def\u4e0a\u624b\u821e\u8db3\u8e48\u8d77\u6765\u3002\u5fc3\u91cc\u60f3 \u9053\uff1a\u201c\u9ec4\u5ead\u575a\u8bcd\u9053\uff1a\u82e5\u5230\u6c5f\u5357\u8d74\u4e0a\u6625\uff0c\u5343\u4e07\u548c\u6625\u4f4f\u3002\u6c5f\u5357\u4e4b\u6625\uff0c\u6211\u6ca1\u8d76\u4e0a\uff0c\u4f46\u53ea\u770b\u6c5f\u5357\u7684\u79cb \u5929\uff0c\u4ea6\u5df2\u662f\u7f8e\u5f97\u4ee4\u4eba\u6d41\u8fde\u5fd8\u8fd4\u4e86\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u8e44\u58f0\u5f97\u5f97\uff0c\u91d1\u9010\u6d41\u4e00\u5fc3\u8d4f\u73a9\u98ce\u666f\uff0c\u6709\u4e24\u9a91\u5feb\u9a6c\u8981\u6765\u5230\u4ed6\u7684\u8eab\u8fb9\uff0c\u4ed6\u624d\u53d1\u73b0\u3002\u62ac\u5934 \u4e00\u770b\uff0c\u53ea\u89c1\u662f\u4e00\u4e2a\u80a5\u5934\u5927\u8033\u7684\u548c\u5c1a\u548c\u4e00\u4e2a\u8273\u5986\u6d53\u62b9\u7684\u5987\u4eba\u3002\u90a3\u80a5\u5934\u5927\u8033\u7684\u548c\u5c1a\u6b63\u5728\u4e3e\u8d77\u9a6c \u97ad\uff0c\u559d\u9053\uff1a\u201c\u50bb\u5c0f\u5b50\uff0c\u8ba9\u8def\uff01\u201d\u8fd9\u548c\u5c1a\u60f3\u662f\u56e0\u4e3a\u770b\u89c1\u91d1\u9010\u6d41\u5728\u8def\u4e0a\u6447\u5934\u6446\u8111\u7684\u72ec\u81ea\u541f\u54e6\uff0c\u4ee5 \u4e3a\u4ed6\u662f\u4e00\u4e2a\u75f4\u5446\u7684\u4e66\u751f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8eab\u5f62\u4e00\u4fa7\uff0c\u4f5c\u597d\u51c6\u5907\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f60\u8fd9\u4e00\u97ad\u6253\u4e0b\u6765\uff0c\u6211\u5c31\u8981\u4f60\u597d\u770b\uff01\u201d\u5fc3\u5ff5\u672a \u5df2\uff0c\u53ea\u542c\u5f97\u90a3\u5987\u4eba\u5df2\u5728\u53eb\u9053\uff1a\u201c\u4e0d\u53ef\u83bd\u649e\uff0c\u8fd9\u4eba\u4f3c\u662f\u8d35\u5bb6\u5b50\u5f1f\uff01\u201d\u90a3\u548c\u5c1a\u6536\u56de\u9a6c\u97ad\uff0c\u4e00\u63d0\u7ef3 \u7586\uff0c\u5feb\u9a6c\u4ece\u91d1\u9010\u6d41\u8eab\u8fb9\u9a70\u8fc7\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u9053\uff1a\u201c\u597d\u5440\uff0c\u539f\u6765\u4f60\u662f\u5148\u656c\u7f57\u8863\u540e\u656c\u4eba\uff0c\u8981\u4e0d\u662f\u6211\u521a\u597d\u6362\u4e86\u4e00\u5957\u65b0\u8863\uff0c\u5c82\u4e0d\u8981 \u6328\u4e86\u4f60\u8fd9\u4e00\u97ad\u4e86\u3002\u54fc\uff0c\u54fc\uff01\u4f60\u4e0d\u60f9\u6211\uff0c\u6211\u5012\u60f3\u60f9\u4f60\uff01\u201d\u6b63\u8981\u65bd\u5c55\u516b\u6b65\u8d76\u8749\u7684\u8f7b\u529f\u8ffd\u4e0b\u53bb\uff0c\u5374 \u542c\u5f97\u90a3\u5987\u4eba\u53c8\u5728\u7b11\u9053\uff1a\u201c\u4e8c\u54e5\uff0c\u4f60\u603b\u662f\u8fd9\u4e48\u5927\u7684\u706b\u6c14\uff0c\u521a\u624d\u4ece\u6728\u6e0e\u7ecf\u8fc7\uff0c\u6211\u771f\u62c5\u5fc3\u4f60\u8981\u53bb\u95f9 \u4e8b\u5462\uff01\u201d\u90a3\u548c\u5c1a\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u4e3a\u7740\u8fd9\u635e\u4ec0\u5b50\uff0c\u6211\u5c82\u80fd\u4e0d\u627e\u4ed6\u4eec\u7236\u5b50\u62a5\u4ec7\uff1f\u73b0\u5728\u53ea\u597d\u7b49\u5f85\u56de\u6765 \u7684\u65f6\u5019\uff0c\u518d\u627e\u4ed6\u7b97\u5e10\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6014\u4e86\u4e00\u6014\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u4e24\u4e2a\u4eba\u5c31\u662f\u516d\u5408\u5e2e\u4e2d\u7684\u50e7\u4eba\u548c\u5be1\u5987\uff01\u201d\u6728\u6e0e\u7684\u6b66 \u6797\u4eba\u7269\u53ea\u6709\u4e00\u4e2a\u9648\u5929\u5b87\uff0c\u9648\u5929\u5b87\u7684\u513f\u5b50\u9648\u5149\u7167\u5c31\u662f\u56e0\u4e3a\u66fe\u7ecf\u523a\u4f24\u4e86\u516d\u5408\u5e2e\u7684\u548c\u5c1a\u5706\u6d77\uff0c\u8fd9\u624d \u4e0e\u516d\u5408\u5e2e\u7ed3\u4e0b\u51a4\u4ec7\u7684\u3002\u8fd9\u4e24\u4e2a\u4eba\u7684\u8c08\u8bdd\u548c\u8fd9\u4e9b\u4e8b\u5b9e\u6070\u597d\u76f8\u7b26\u3002\u53ea\u4e0d\u77e5\u90a3\u548c\u5c1a\u8bf4\u7684\u201c\u635e\u4ec0\u5b50\u201d \u662f\u4ec0\u4e48\u4e1c\u897f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u53ea\u5446\u4e86\u4e00\u5446\uff0c\u90a3\u4e24\u9a91\u9a6c\u5df2\u7ecf\u53bb\u5f97\u8fdc\u8fdc\u4e86\u3002\u91d1\u9010\u6d41\u5e73\u606f\u4e86\u706b\u6c14\uff0c\u54d1\u7136\u5931\u7b11\uff1a\u201c\u5927\u597d \u7684\u5c71\u8272\u6e56\u5149\uff0c\u4f55\u82e6\u4e3a\u4ed6\u4eec\u8d25\u4e86\u6211\u7684\u6e05\u5174\uff1f\u7ba1\u4ed6\u4eec\u662f\u4ec0\u4e48\u4eba\uff0c\u6211\u5230\u82cf\u5dde\u73a9\u4e2a\u75db\u5feb\u518d\u8bf4\u3002\u201d \u3000\u3000\u6728\u6e0e\u79bb\u82cf\u5dde\u4e0d\u8fc7\u4e00\u767e\u91cc\u5de6\u53f3\uff0c\u91d1\u9010\u6d41\u867d\u7136\u5e76\u4e0d\u52a0\u5feb\u811a\u7a0b\uff0c\u9ec4\u660f\u65e5\u843d\u4e4b\u524d\u4ea6\u5df1\u5230\u4e86\u3002\u82cf\u5dde \u662f\u4e00\u4e2a\u8457\u540d\u7684\u56ed\u6797\u57ce\u5e02\uff0c\u4f46\u89c1\u5904\u5904\u7eff\u836b\u63a9\u8174\uff0c\u68a7\u6850\u6768\u67f3\u9ad8\u51fa\u56f4\u5899\u3002\u91d1\u9010\u6d41\u5927\u4e3a\u6b22\u559c\uff0c\u5fc3\u9053\uff1a \u201c\u597d\uff0c\u6211\u4e14\u5230\u5feb\u6d3b\u6797\u53bb\u4eab\u51e0\u5929\u6e05\u798f\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u6c5f\u5357\u6625\u5df2\u901d\uff0c\u6765\u8d4f\u592a\u6e56\u79cb\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e5d\u56de\u3000\u8c01\u65bd\u5999\u624b\u7a7a\u7a7a\u6280\u3000\u54ea\u8bc6\u82b3\u5fc3\u60d8\u60d8\u60c5 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e5d\u56de\u3000\u8c01\u65bd\u5999\u624b\u7a7a\u7a7a\u6280\u3000\u54ea\u8bc6\u82b3\u5fc3\u60d8\u60d8\u60c5 \u3000\u3000\u5feb\u6d3b\u6797\u5728\u82cf\u5dde\u5317\u90ca\uff0c\u672c\u6765\u662f\u5143\u672b\u5272\u636e\u6c5f\u6dee\u7684\u5434\u738b\u5f20\u58eb\u8bda\u7684\u79bb\u5b98\uff0c\u5f20\u58eb\u8bda\u4e0e\u6731\u5143\u748b\u9010\u9e7f\u4e2d \u539f\uff0c\u957f\u6c5f\u4e00\u6218\uff0c\u5175\u8d25\u6295\u6c5f\u3002\u5f20\u571f\u8bda\u6b7b\u540e\uff0c\u8fd9\u5ea7\u79bb\u5bab\u88ab\u5f53\u4f5c\u9006\u4ea7\u53d8\u5356\uff0c\u5230\u4e86\u6e05\u521d\uff0c\u6570\u5ea6\u6613\u4e3b\uff0c \u5230\u4e86\u4e00\u4e2a\u5bcc\u5546\u624b\u4e2d\uff0c\u5c06\u5b83\u5efa\u6210\u4e00\u4e2a\u56ed\u6797\u5f0f\u7684\u65c5\u5e97\uff0c\u4e13\u95e8\u62db\u5f85\u5bcc\u5546\u5927\u8d3e\u3001\u516c\u5b50\u738b\u5b59\u3002\u79df\u91d1\u6bd4\u666e \u901a\u7684\u5ba2\u5e97\u6602\u8d35\u767e\u500d\uff0c\u4f46\u82e5\u4e0d\u662f\u9884\u5148\u79df\u4e0b\uff0c\u4e34\u65f6\u6295\u5bbf\u8fd8\u5f80\u5f80\u4f1a\u989d\u6ee1\u89c1\u9057\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5728\u9648\u5bb6\u7684\u65f6\u5019\uff0c\u66fe\u7ecf\u548c\u9648\u5929\u5b87\u8c08\u8fc7\u60f3\u5230\u82cf\u5dde\u6e38\u73a9\u3002\u8fd9\u5feb\u6d3b\u6797\u5c31\u662f\u9648\u5929\u5b87\u4ecb\u7ecd\u7ed9\u4ed6 \u7684\u3002\u91d1\u9010\u6d41\u8eab\u4e0a\u8fd8\u6709\u5728\u8def\u4e0a\u5077\u6765\u7684\u91d1\u94f6\uff0c\u6709\u5fc3\u5230\u5feb\u6d3b\u6797\u53bb\u628a\u94b1\u82b1\u5149\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8fdb\u5165\u5feb\u6d3b\u6797\uff0c\u8fce\u9762\u4fbf\u662f\u4e00\u6761\u66f2\u6298\u7684\u957f\u5eca\uff0c\u58c1\u4e0a\u5d4c\u6709\u4e00\u5757\u5757\u7684\u5386\u4ee3\u4e66\u6cd5\u6cd5\u5e16\uff0c\u53ea\u662f \u56ed\u6797\u4e3b\u4eba\u4e0d\u77e5\u4fdd\u62a4\uff0c\u5df2\u73b0\u51fa\u5265\u843d\u6a21\u7cca\u7684\u75d5\u8ff9\u3002\u51fa\u4e86\u957f\u5eca\uff0c\u4e24\u65c1\u6797\u6728\u63a9\u6620\uff0c\u82b1\u6728\u7af9\u77f3\uff0c\u6784\u6210\u5047 \u5c71\u3001\u8377\u6c60\u3001\u5e7d\u8c37\u3001\u655e\u8f69\uff0c\u54ea\u91cc\u50cf\u4e2a\u65c5\u5e97\uff0c\u7b80\u76f4\u5c31\u662f\u738b\u5019\u4f4f\u5b85\u3002\u91d1\u9010\u6d41\u5f88\u662f\u559c\u6b22\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u5feb\u6d3b\u6797\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u53ea\u4e0d\u77e5\u6211\u6709\u6ca1\u6709\u798f\u6c14\u5728\u8fd9\u91cc\u5feb\u6d3b\u5feb\u6d3b\uff1f\u201d \u3000\u3000\u77e5\u79df\u5904\u8bbe\u5728\u957f\u5eca\u7684\u5c3d\u5934\uff0c\u91d1\u9010\u6d41\u4ece\u957f\u5eca\u8d70\u8fc7\u6765\u7684\u65f6\u5019\uff0c\u638c\u7ba1\u79df\u52a1\u7684\u6267\u4e8b\u5df2\u7ecf\u770b\u89c1\u4e86\u4ed6\u3002 \u91d1\u9010\u6d41\u8863\u670d\u534e\u4e3d\uff0c\u6267\u4e8b\u7684\u4e0d\u6562\u6020\u6162\uff0c\u5c06\u4ed6\u8fce\u4e86\u8fdb\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u5ba2\u5b98\u6765\u5f97\u5de7\uff0c\u4e0b\u5348\u521a\u597d\u6709\u4e2a\u5ba2 \u4eba\u9000\u4e86\u623f\u5b50\u3002\u8fd9\u662f\u6211\u4eec\u6700\u597d\u7684\u4e00\u95f4\u623f\u5b50\uff0c\u79df\u91d1\u53ef\u80fd\u8d35\u4e00\u4e9b\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u6b63\u662f\u8981\u6700\u597d\u7684\u623f\u5b50\uff0c\u591a\u5c11\u79df\u91d1\uff1f\u201d\u6267\u4e8b\u9053\uff1a\u201c\u4e94\u4e24\u94f6\u5b50\u4e00\u5929\u3002\u201d \u91d1\u9010\u6d41\u9053\uff1a\u201c\u592a\u4fbf\u5b9c\u4e86\uff0c\u7ed9\u591f\u4f60\u5341\u4e24\u94f6\u5b50\u3002\u201d\u968f\u624b\u638f\u51fa\u4e00\u952d\u91d1\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u952d\u91d1\u5b50\u5927\u7ea6\u603b \u503c\u4e2a\u4e94\u5341\u4e24\u7eb9\u94f6\u5427\u3002\u6211\u6682\u5b9a\u4f4f\u4e09\u5929\uff0c\u591a\u4e0b\u7684\u505a\u996d\u94b1\u3002\u5feb\u6d3b\u6797\u4e2d\u5f80\u6765\u90fd\u662f\u8d35\u5ba2\uff0c\u4f46\u50cf\u91d1\u9010\u6d41\u8fd9 \u6837\u51fa\u624b\u9614\u7ef0\u7684\u5374\u4e5f\u8fd8\u4e0d\u591a\u89c1\u3002\u6267\u4e8b\u7684\u8c22\u4e86\u53c8\u8c22\uff0c\u5c06\u91d1\u9010\u6d41\u5e26\u5f80\u4f4f\u6240\u3002 \u3000\u3000\u5feb\u6d3b\u6797\u4e0e\u666e\u901a\u7684\u65c5\u5e97\u4e0d\u540c\uff0c\u5ba2\u623f\u4e0d\u662f\u805a\u5728\u4e00\u5904\uff0c\u800c\u662f\u5206\u5e03\u56ed\u4e2d\u7684\u4e00\u5ea7\u5ea7\u623f\u5b50\uff0c\u6709\u4f9b\u7ed9\u4e00 \u5bb6\u4eba\u4f4f\u7684\uff0c\u4e5f\u6709\u4f9b\u7ed9\u5355\u8eab\u6c49\u5b50\u4f4f\u7684\u3002\u5355\u8eab\u6c49\u5b50\u4f4f\u7684\u4e5f\u6709\u5ba2\u5385\u548c\u6d74\u5ba4\u3002\u6240\u4ee5\u79df\u91d1\u624d\u4f1a\u90a3\u6837\u6602 \u8d35\u3002 \u3000\u3000\u6b64\u65f6\u5929\u8272\u5df2\u665a\uff0c\u91d1\u9010\u6d41\u6d17\u4e86\u4e2a\u6fa1\uff0c\u5403\u4e86\u665a\u996d\uff0c\u4fbf\u8fdb\u623f\u6b47\u606f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4eca\u665a\u65e9\u4e9b\u7761 \u89c9\uff0c\u660e\u5929\u597d\u53bb\u6e38\u73a9\u3002\u201d\u6b63\u8981\u66f4\u8863\u5c31\u5bdd\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u6709\u5435\u95f9\u7684\u58f0\u97f3\uff0c\u58f0\u97f3\u6b63\u662f\u6765\u81ea\u77e5\u79df\u5904\u90a3 \u513f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e8c\u542c\uff0c\u89c9\u5f97\u58f0\u97f3\u597d\u719f\uff0c\u4fbf\u8d70\u51fa\u53bb\u770b\u3002\u5374\u539f\u6765\u5c31\u662f\u8def\u4e0a\u9047\u89c1\u7684\u90a3\u4e2a\u548c\u5c1a\u548c\u90a3\u4e2a\u5987 \u4eba\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u4e2a\u6267\u4e8b\u7684\u4e0d\u4f4f\u6253\u606d\u4f5c\u63d6\u9053\uff1a\u201c\u5b9e\u5728\u5bf9\u4e0d\u4f4f\u5927\u5e08\uff0c\u59d4\u5b9e\u662f\u6ca1\u6709\u623f\u95f4\u4e86\u3002\u201d\u548c\u5c1a\u4f4f\u65c5 \u9986\u5df2\u7ecf\u5c11\u89c1\uff0c\u4f55\u51b5\u8fd8\u5e26\u7740\u4e00\u4e2a\u5987\u4eba\uff0c\u8fd9\u4e2a\u6267\u4e8b\u751f\u6015\u62db\u60f9\u5b98\u53f8\uff0c\u6709\u623f\u5b50\u4e5f\u4e0d\u6562\u51fa\u79df\u3002 \u3000\u3000\u90a3\u548c\u5c1a\u52c3\u7136\u5927\u6012\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6b3a\u8d1f\u6211\u51fa\u5bb6\u4eba\u4e48\u3002\u4e0d\u7ba1\u4f60\u6709\u6ca1\u6709\u623f\u5b50\uff0c\u6211\u662f\u4f4f\u5b9a\u7684\u4e86\uff01\u201d \u8bf4\u7f62\u628a\u624b\u4e0a\u7684\u4e00\u4e2a\u7ea2\u6f06\u5323\u5b50\u5728\u67dc\u5408\u4e0a\u4e00\u653e\u3002\u5feb\u6d3b\u6797\u4e2d\u662f\u517b\u6709\u6253\u624b\u7684\uff0c\u6709\u4e2a\u6253\u624b\u4e0a\u6765\uff0c\u63d0\u8d77\u4ed6 \u7684\u5323\u5b50\u3002 \u3000\u3000\u6253\u624b\u662f\u60f3\u628a\u8fd9\u4e2a\u5323\u5b50\u6254\u51fa\u95e8\u53bb\uff0c\u7136\u540e\u559d\u8fd9\u548c\u5c1a\u6eda\u5f00\u3002\u8fd9\u4e2a\u7ea2\u6f06\u5323\u5b50\u4e0d\u5230\u4e09\u5c3a\u957f\uff0c\u4e03\u5bf8 \u9614\uff0c\u6253\u624b\u5f53\u7136\u4ee5\u4e3a\u662f\u201c\u8f7b\u800c\u6613\u4e3e\u201d\uff0c\u54ea\u77e5\u4ed6\u521d\u65f6\u6f2b\u4e0d\u7ecf\u610f\u7684\u4e00\u63d0\uff0c\u5323\u5b50\u7adf\u7136\u52a8\u4e5f\u6ca1\u52a8\u3002\u540e\u6765 \u4ed6\u4f7f\u51fa\u4e86\u5403\u5976\u7684\u6c14\u529b\uff0c\u8fd9\u624d\u63d0\u4e86\u8d77\u6765\uff0c\u8fd9\u6253\u624b\u5927\u5403\u4e00\u60ca\uff0c\u201c\u5c0f\u5c0f\u7684\u5323\u5b50\uff0c\u600e\u7684\u7adf\u662f\u5982\u6b64\u6c89 \u91cd\uff1f\u6015\u4e0d\u6709\u4e00\u767e\u6765\u65a4\uff01\u201d \u3000\u3000\u4ee4\u8fd9\u6253\u624b\u5403\u60ca\u7684\u8fd8\u4e0d\u53ea\u6b64\uff0c\u5323\u5b50\u4e00\u63d0\u8d77\uff0c\u53ea\u89c1\u67dc\u53f0\u4e0a\u5df2\u7559\u6709\u4e00\u4e2a\u5323\u5b50\u7684\u51f9\u75d5\uff0c\u67dc\u53f0\u662f\u7528 \u575a\u5b9e\u7684\u7ea2\u6728\u505a\u7684\u3002\u4e00\u767e\u6765\u65a4\u7684\u91cd\u91cf\u538b\u5728\u5b83\u7684\u4e0a\u9762\u51b3\u4e0d\u81f3\u4e8e\u51f9\u9677\u7684\uff0c\u5f53\u7136\u662f\u8fd9\u548c\u5c1a\u7528\u4e0a\u4e86\u5185\u529b \u6240\u81f4\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u51b7\u773c\u65c1\u89c2\uff0c\u4e5f\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5323\u5b50\u60f3\u5fc5\u5c31\u662f\u8fd9\u79c3\u9a74\u8bf4\u7684\u4ec0\u4e48\u2018\u635e\u4ec0 \u5b50\u2019\u4e86\u3002\u5374\u4e0d\u77e5\u91cc\u9762\u88c5\u7684\u4ec0\u4e48\u4e1c\u897f\uff1f\u5373\u4f7f\u90fd\u662f\u91d1\u94f6\u5b9d\u77f3\uff0c\u4e5f\u4e0d\u8be5\u5982\u6b64\u6c89\u91cd\uff01\u201d \u3000\u3000\u6253\u624b\u672c\u6765\u662f\u8981\u559d\u8fd9\u548c\u5c1a\u6eda\u5f00\u7684\uff0c\u6b64\u65f6\u6ee1\u9762\u901a\u7ea2\uff0c\u505a\u58f0\u4e0d\u5f97\u3002\u90a3\u548c\u5c1a\u51b7\u7b11\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60 \u60f3\u62a2\u6211\u7684\u4e1c\u897f\u5417\uff1f\u597d\uff0c\u4f60\u559c\u6b22\u4f60\u5c31\u62ff\u53bb\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5323\u5b50\u201c\u4e52\u201d\u7684\u6389\u4e0b\u5730\u6765\uff0c\u521a\u597d\u538b\u7740\u6253 \u624b\u7684\u811a\uff0c\u75db\u5f97\u4ed6\u54c7\u54c7\u5927\u53eb\u3002\u8fd9\u5012\u4e0d\u662f\u548c\u5c1a\u6363\u9b3c\uff0c\u800c\u662f\u8fd9\u6253\u624b\u6c14\u529b\u4e0d\u7ee7\uff0c\u81ea\u5df1\u5931\u624b\u7684\u3002 \u3000\u3000\u90a3\u5987\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u811a\u5c16\u4e00\u6311\uff0c\u5323\u5b50\u98de\u8d77\uff0c\u843d\u5230\u5979\u7684\u624b\u4e2d\uff0c\u770b\u5979\u6beb\u4e0d\u8d39\u529b\uff0c\u5c31\u50cf\u5c0f\u5b69\u5b50\u8e22 \u6bea\u5b50\u4e00\u822c\u3002 \u3000\u3000\u90a3\u5987\u4eba\u7b11\u9053\uff1a\u201c\u6211\u4eec\u662f\u60f3\u7559\u4ef6\u4e1c\u897f\u5728\u67dc\u4e0a\u505a\u62bc\u5934\uff0c\u8f69\u8ba9\u4f60\u4eec\u5b89\u5fc3\u3002\u8c01\u77e5\u4f60\u4eec\u5374\u4e0d\u80af\u8981\uff0c \u90a3\u6211\u53ea\u597d\u6536\u56de\u4e86\u3002\u201d \u3000\u3000\u90a3\u6253\u624b\u811a\u8dbe\u7206\u88c2\uff0c\u8840\u8089\u6a21\u7cca\uff0c\u540c\u4f34\u6276\u4ed6\u9053\u4e0b\uff0c\u4e0d\u65ad\u547b\u541f\uff0c\u5374\u662f\u4e0d\u6562\u53d1\u4f5c\u3002 \u3000\u3000\u6267\u4e8b\u7684\u8fde\u5fd9\u8d54\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u600e\u6562\u5c0f\u89d1\u7559\u5ba2\uff1f\u59d4\u5b9e\u662f\u6ca1\u6709\u7a7a\u623f\u5b50\uff0c\u5e76\u975e\u6015\u4f60\u4eec\u4ed8\u4e0d\u8d77\u623f \u94b1\u3002\u201d \u3000\u3000\u80a5\u5934\u5927\u8033\u7684\u548c\u5c1a\u53cc\u76ee\u4e00\u77aa\uff0c\u770b\u6837\u5b50\u5c31\u8981\u53d1\u6076\uff0c\u90a3\u5987\u4eba\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\u9053\u3002\u201d\u4e8c\u54e5\u8ba9\u6211\u6765 \u8bf4\u3002\u201d \u3000\u3000\u53ea\u89c1\u8fd9\u5987\u4eba\u6253\u5f00\u4e86\u4e00\u628a\u6298\u6247\u3001\u76ae\u7b11\u8089\u4e0d\u7b11\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u518d\u60f3\u60f3\u770b\uff0c\u8bf4\u4e0d\u5b9a\u6709\u4ec0\u4e48\u7a7a\u623f \u5b50\u4f60\u4eec\u5fd8\u8bb0\u4e86\uff1f\u201d \u3000\u3000\u6298\u6247\u4e0a\u7ed8\u6709\u516d\u4e2a\u9ab7\u9ac5\u5934\uff0c\u65c1\u8fb9\u6709\u4e2a\u6253\u624b\u4e43\u662f\u5e2e\u4f1a\u4e2d\u4eba\uff0c\u89c1\u4e86\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u5411\u90a3\u4e2a\u6267\u4e8b \u6253\u4e86\u4e00\u4e2a\u773c\u8272\u3002 \u3000\u3000\u90a3\u6267\u4e8b\u7684\u82e6\u7b11\u9053\uff1a\u201c\u82cf\u5dde\u57ce\u91cc\u8bb8\u591a\u5ba2\u5e97\uff0c\u5ba2\u5b98\u5c3d\u53ef\u53bb\u627e\u5440\uff01\u201d \u3000\u3000\u90a3\u548c\u5c1a\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5c31\u662f\u8981\u627e\u6700\u597d\u7684\u5ba2\u5e97\uff0c\u95ee\u4e86\u8bb8\u591a\u4eba\uff0c\u624d\u6211\u5230\u4f60\u8fd9 \u513f\u3002\u8bf4\u4ec0\u4e48\u6211\u4e5f\u5f97\u4f4f\u4e0b\uff01\u201d\u91d1\u9010\u6d41\u8fd9\u624d\u660e\u767d\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u4eec\u5148\u8fdb\u4e86\u57ce\u5374\u6bd4\u6211\u540e\u5230\u3002 \u54fc\uff0c\u522b\u4eba\u5f53\u548c\u5c1a\u662f\u56db\u5927\u7686\u7a7a\uff0c\u8fd9\u79c3\u9a74\u5374\u662f\u61c2\u5f97\u4eab\u53d7\uff01\u201d \u3000\u3000\u65c1\u8fb9\u7684\u90a3\u4e2a\u6253\u624b\u60ca\u5f97\u9762\u4e0a\u6dcc\u6c57\uff0c\u5fd9\u4e0d\u8fed\u7684\u53c8\u5411\u8fd9\u6267\u4e8b\u8fde\u6253\u773c\u8272\u3002\u8fd9\u6267\u4e8b\u7684\u5176\u5b9e\u5df2\u662f\u5728\u60f3 \u6cd5\u8f6c\u5708\uff0c\u53ea\u56e0\u9762\u5b50\u95ee\u9898\uff0c\u4e0d\u80fd\u4e0d\u518d\u63a8\u642a\u4e00\u4e0b\u800c\u5df2\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u6267\u4e8b\u7684\u624d\u4f5c\u51fa\u52c9\u4e3a\u5176\u96be\u7684\u795e\u6c14\u8bf4\u9053\uff1a\u201c\u5927\u5e08\u8fd9\u6837\u8d4f\u9762\uff0c\u6ca1\u6709\u623f\u95f4\u6211\u4e5f\u5f97\u60f3\u529e \u6cd5\u7684\uff0c\u55ef\uff0c\u5bf9\u5566\uff0c\u6211\u60f3\u8d77\u6765\u4e86\u3002\u6709\u95f4\u623f\u5b50\u672c\u6765\u662f\u6709\u5ba2\u4eba\u5b9a\u4e0b\u4e86\u7684\uff0c\u4ed6\u8981\u660e\u5929\u624d\u5230\u3002\u7531\u6211\u62c5\u5f85 \u4e00\u70b9\u5173\u7cfb\uff0c\u5148\u7ed9\u4f60\u4eec\u597d\u4e86\u3002\u53ea\u4e0d\u8fc7\u8fd9\u662f\u5355\u4eba\u5f80\u7684\u623f\u5b50\uff0c\u53ea\u6015\u4e24\u4f4d\u4e0d\u4fbf\u3002\u201d \u3000\u3000\u90a3\u5987\u4eba\u8138\u6cdb\u6843\u82b1\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u518d\u60f3\u60f3\uff0c\u8fd8\u6709\u6ca1\u6709\u591a\u4f59\u7684\u623f\u95f4\uff1f\u201d \u3000\u3000\u6267\u4e8b\u7684\u82e6\u7740\u8138\u9053\u3002\u201d\u59d4\u5b9e\u662f\u6ca1\u6709\u4e86\u3002\u4e0d\u8fc7\u6211\u4eec\u8fd9\u91cc\u5355\u4eba\u4f4f\u7684\u623f\u5b50\u4e5f\u662f\u6709\u5367\u623f\u53c8\u6709\u5ba2\u5385 \u7684\u3002\u201d \u3000\u3000\u548c\u5c1a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u662f\u4f5b\u95e8\u5f1f\u5b50\uff0c\u65e9\u5df2\u52d8\u7834\u8272\u7a7a\uff0c\u7528\u4e0d\u7740\u907f\u7537\u5973\u4e4b\u5acc\u7684\u3002\u65e2\u6709\u5367\u623f\u53c8\u6709 \u5ba2\u5385\uff0c\u90a3\u66f4\u597d\u4e86\u3002\u56db\u59b9\uff0c\u5c31\u8fd9\u6837\u5427\uff0c\u4e5f\u4e0d\u5fc5\u4ee4\u4ed6\u4e3a\u96be\u4e86\u3002\u591a\u5c11\u623f\u94b1\uff1f\u201d \u3000\u3000\u6267\u4e8b\u7684\u5fc3\u4e2d\u5492\u9a82\uff1a\u201c\u4f60\u8fd9\u6837\u7684\u4f5b\u95e8\u5f1f\u5b50\u5e94\u8be5\u5760\u4e0b\u5341\u516b\u5c42\u5730\u72f1\uff01\u201d\u9762\u4e0a\u5374\u662f\u5806\u6ee1\u7b11\u5bb9\uff0c\u8bf4 \u9053\uff1a\u201c\u5c0f\u7684\u62db\u5f85\u4e0d\u5468\uff0c\u54ea\u80fd\u8981\u5927\u5e08\u7684\u94b1\uff1f\u201d\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u8fd9\u4e48\u8bf4\u6211\u4e5f\u5c31\u4e0d\u5ba2\u6c14 \u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7ad9\u5728\u5916\u9762\u7740\u70ed\u95f9\uff0c\u770b\u89c1\u597d\u620f\u5df2\u6563\uff0c\u5c31\u56de\u81ea\u5df1\u7684\u623f\u95f4\uff0c\u4f46\u90a3\u548c\u5c1a\u51fa\u6765\uff0c\u5374\u5df2\u7ecf\u77a7\u89c1 \u4e86\u4ed6\uff0c\u72e0\u72e0\u5730\u77aa\u4e86\u4ed6\u4e00\u773c\u3002\u90a3\u5987\u4eba\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u54e5\uff0c\u4e0d\u53ef\u591a\u4e8b\uff01\u201d\u58f0\u97f3\u5f88\u5c0f\uff0c\u91d1\u9010\u6d41\u5374\u5df2\u542c \u89c1\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u5c31\u6015\u4f60\u4eec\u4e0d\u591a\u4e8b\u3002\u54fc\uff0c\u4f60\u4e0d\u591a\u4e8b\u6211\u4e5f\u8981\u591a\u4e8b\u5462\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u56de\u5230\u623f\u95f4\uff0c\u6b47\u4e86\u4e00\u4f1a\uff0c\u7ea6\u83ab\u5230\u4e86\u4e09\u66f4\u65f6\u5206\uff0c\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5996\u5987\u62ff\u7684\u90a3\u628a\u6298 \u6247\u7ed8\u6709\u516d\u4e2a\u9ab7\u9ac5\uff0c\u60f3\u5fc5\u662f\u516d\u5408\u5e2e\u7684\u6807\u8bb0\u3002\u8fd9\u4e24\u4e2a\u4eba\u4e5f\u60f3\u5fc5\u662f\u516d\u5408\u5e2e\u4e2d\u7684\u90a3\u4e2a\u8d3c\u79c3\u5706\u6d77\uff0c\u548c\u90a3 \u4e2a\u5be1\u5987\u8463\u5341\u4e09\u5a18\u4e86\u3002\u597d\uff0c\u6211\u4e14\u53bb\u548c\u4ed6\u4eec\u5f00\u5f00\u73a9\u7b11\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u65e9\u5df2\u8bb0\u4e0b\u4e86\u4ed6\u4eec\u7684\u4f4f\u5904\uff0c\u90a3\u662f\u4e00\u95f4\u5b64\u96f6\u96f6\u72ec\u81ea\u5728\u56ed\u4e2d\u4e00\u89d2\u7684\u623f\u5b50\uff0c\u540e\u9762\u6709\u5ea7\u5047 \u5c71\uff0c\u91d1\u9010\u6d41\u6084\u6084\u5730\u6478\u5230\u90a3\u513f\uff0c\u5c31\u5728\u5047\u5c71\u77f3\u540e\u5077\u542c\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u5987\u4eba\u201c\u54ce\u54df\u201d\u4e00\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4f5c\u6b7b\u5566\uff01\u6211\u53c8\u4e0d\u662f\u9648\u5929\u5b87\uff0c\u4f60\u8981\u52a8\u624b\u52a8\u811a\uff0c\u627e\u9648 \u5929\u5b87\u53bb\uff01\u201d \u3000\u3000\u90a3\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u548c\u9648\u5929\u5b87\u52a8\u624b\u90a3\u53ef\u5c31\u662f\u5356\u547d\u7684\u73a9\u610f\u4e86\uff0c\u600e\u53ca\u5f97\u4e0a\u548c\u4f60\u52a8\u624b\u52a8\u811a\u7684\u5feb \u6d3b\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5f20\u671b\u8fdb\u53bb\uff0c\u53ea\u89c1\u548c\u5c1a\u4e0e\u90a3\u5987\u4eba\u5e76\u80a9\u5750\u5728\u5e8a\u4e0a\uff0c\u90a3\u5987\u4eba\u5df2\u7ecf\u8131\u4e86\u5916\u8863\uff0c\u53ea\u7a7f\u7740\u4e00\u4ef6 \u7c89\u7ea2\u8272\u7684\u6c57\u886b\uff0c\u610f\u6001\u5341\u5206\u9a9a\u5a9a\u3002\u91d1\u9010\u6d41\u6697\u81ea\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4e14\u8ba9\u4f60\u4eec\u6682\u4e14\u5feb\u6d3b\u5feb\u6d3b\uff0c\u7b49\u4e0b\u53eb\u4f60 \u4eec\u5403\u6211\u7684\u82e6\u5934\uff01\u201d \u3000\u3000\u90a3\u5987\u4eba\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u6015\u9648\u5929\u5b87\uff1f\u201d\u90a3\u548c\u5c1a\u9053\uff1a\u201c\u6211\u624d\u4e0d\u6015\u9648\u5929\u5b87\u5462\u3002\u6211\u53ea\u6015\u4e00\u4e2a\u4eba\uff0c\u5c31 \u662f\u4f60\u7684\u8001\u76f8\u597d\u3002\u563b\u563b\uff0c\u8981\u662f\u7ed9\u53f2\u5e2e\u4e3b\u77e5\u9053\u54b1\u4eec\u540c\u4f4f\u4e00\u4e2a\u623f\u95f4\uff0c\u4e0d\u77e5\u8981\u600e\u6837\u5bf9\u4ed8\u6211\u5462\uff1f\u201d \u3000\u3000\u90a3\u5987\u4eba\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5c31\u597d\u3002\u7ed9\u6211\u653e\u5e84\u91cd\u70b9\uff0c\u8981\u4e0d\u7136\u6211\u544a\u8bc9\u767d\u90b5\uff0c\u4ed6\u4e0d\u6495\u4e86\u4f60\u7684\u76ae\u624d \u602a\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u5230\u8fd9\u91cc\uff0c\u5df2\u7ecf\u660e\u767d\u4e86\u8fd9\u4e24\u4eba\u7684\u8eab\u4efd\uff0c\u679c\u7136\u662f\u516d\u5408\u5e2e\u7684\u5706\u6d77\u4e0e\u8463\u5341\u4e09\u5a18\u3002 \u3000\u3000\u5706\u6d77\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u6562\u544a\u8bc9\u5e2e\u4e3b\uff0c\u6211\u5c31\u8bf4\u662f\u4f60\u5f15\u8bf1\u6211\u7684\u3002\u201d \u3000\u3000\u8463\u5341\u4e8c\u5a18\u4f6f\u5567\u65a5\u9053\uff1a\u201c\u4e71\u56bc\u820c\u5934\uff0c\u6211\u662f\u53eb\u4f60\u6765\u8bf4\u6b63\u7ecf\u4e8b\u7684\uff0c\u8c01\u53eb\u4f60\u52a8\u624b\u52a8\u811a\u4e86\u201d\u8bf4\u5b8c\u4e86 \u8bdd\uff0c\u4f60\u7ed9\u6211\u6eda\u51fa\u5ba2\u5385\u53bb\u7761\u3002\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u597d\uff0c\u597d\u3002\u4f60\u8981\u8bf4\u4ec0\u4e48\u6b63\u7ecf\u4e8b\uff0c\u5a18\u5b50\uff0c\u6211\u5728\u8fd9\u91cc\u6d17\u8033\u606d\u542c\u4e86\u3002\u201d \u3000\u3000\u8463\u5341\u4e8c\u5a18\u9053\uff1a\u201c\u8fd9\u4e2a\u7ea2\u6f06\u5323\u5b50\u91cc\u88c5\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u600e\u53ef\u4ee5\u62ff\u5b83\u51fa\u6765\u968f\u4fbf\u5413 \u4eba\uff0c\u8981\u662f\u51fa\u4e86\u4ec0\u4e48\u5c94\u5b50\uff0c\u4f60\u548c\u6211\u90fd\u662f\u62c5\u5f53\u4e0d\u8d77\u7684\u3002\u201d \u3000\u3000\u5706\u6d77\u7b11\u9053\uff1a\u201c\u5413\u4e00\u5413\u90a3\u4e09\u6ee5\u7684\u5c0f\u811a\u8272\u6709\u4ec0\u4e48\u6253\u7d27\uff1f\u4f60\u662f\u770b\u89c1\u7684\u4e86\uff0c\u9001\u7ed9\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u62ff \u4e0d\u52a8\uff0c\u8fd8\u6015\u4ed6\u4eec\u62a2\u53bb\u4e0d\u6210\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\u3002\u90a3\u4e9b\u4e0b\u4e09\u6ee5\u7684\u5c0f\u811a\u8272\u867d\u7136\u4e0d\u884c\uff0c\u4f46\u4f60\u8fd9\u4e48\u4e00\u6296\u9732 \u51fa\u6765\uff0c\u65c1\u8fb9\u82e5\u6709\u9ad8\u4eba\uff0c\u9a6c\u4e0a\u5c31\u77e5\u9053\u8fd9\u5323\u5b50\u975e\u540c\u5bfb\u5e38\u4e86\uff0c\u8fd8\u662f\u5c0f\u5fc3\u70b9\u597d\u3002\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u5a18\u5b50\u7684\u5429\u5490\uff0c\u6211\u6562\u4e0d\u7262\u8bb0\u5728\u5fc3\uff1f\u4e0d\u8fc7\uff0c\u8bf4\u5230\u8fd9\u91cc\uff0c\u6211\u5012\u8981\u95ee\u95ee\u5a18\u5b50\u4e86\u3002\u8bf4\u8001 \u5b9e\u8bdd\uff0c\u6211\u53ea\u77e5\u9053\u8fd9\u662f\u8981\u9001\u7ed9\u8428\u603b\u7ba1\u7684\u5bff\u793c\uff0c\u91cc\u9762\u88c5\u7684\u4ec0\u4e48\u4e1c\u897f\uff0c\u6211\u5374\u8fd8\u4e0d\u77e5\u9053\u5462\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u4f60\u5f53\u771f\u4e0d\u77e5\uff1f\u201d\u5706\u6d77\u9053\uff1a\u201c\u5e2e\u4e3b\u53ea\u53eb\u6211\u5c0f\u5fc3\u770b\u7ba1\uff0c\u5bf9\u6ca1\u6709\u544a\u8bc9\u6211\u88c5\u7684\u4ec0 \u4e48\uff0c\u5f53\u7136\u6211\u4e5f\u5c31\u4e0d\u6562\u95ee\u4ed6\u4e86\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\u4e0d\u8ba9\u4f60\u77e5\u9053\uff0c\u90a3\u4f60\u4e5f\u5c31\u7528\u4e0d\u7740\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u5706\u6d77\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5f53\u771f\u662f\u4eb2\u758f\u6709\u522b\uff0c\u7aef\u7684\u662f\u53eb\u4eba\u7070\u5fc3\uff0c\u4f60\u6211\u90fd\u662f\u5e2e\u4e2d\u7684\u9999\u4e3b\uff0c\u505a\u82e6\u529b\u5c31 \u6709\u6211\u7684\u4efd\uff0c\u79d8\u5bc6\u5374\u53ea\u8ba9\u4f60\u77e5\u9053\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u5524\u9053\u3002\u201d\u4f60\u53c8\u80e1\u8bf4\u516b\u9053\u4e86\uff0c\u7b97\u6211\u6015\u4e86\u4f60\uff0c\u544a\u8bc9\u4f60\u5427\u3002\u8fd9\u5323\u5b50\u91cc\u6709\u4e00\u5757\u2018\u7384 \u94c1\u2019\uff0c\u6240\u4ee5\u624d\u8fd9\u6837\u6c89\u91cd\u7684\u3002\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u4ec0\u4e48\u7384\u94c1\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u7b11\u9053\uff1a\u201c\u7384\u94c1\u662f\u4ec0\u4e48\u4f60\u90fd\u4e0d\u77e5\u9053\uff0c\u4e8f\u4f60\u8fd8\u662f\u6b66\u6797\u9ad8\u624b\u5462\u3002\u8fd9\u662f\u6606\u4ed1\u9876\u661f\u5bbf\u6d77\u51fa \u4ea7\u7684\u4e00\u79cd\u7279\u6b8a\u91d1\u5c5e\uff0c\u5f88\u96be\u5bfb\u627e\u7684\u3002\u8981\u6bd4\u540c\u6837\u4f53\u79ef\u7684\u4e00\u5757\u751f\u94c1\u91cd\u5341\u500d\uff01\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u4f5c\u4ec0\u4e48\u7528\u7684\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\u3002\u201d\u82e5\u662f\u6709\u9ad8\u660e\u7684\u94f8\u5251\u5e08\uff0c\u7528\u8fd9\u5757\u7384\u94c1\u6765\u6253\u6210\u4e00\u628a\u5b9d\u5251\uff0c\u90a3\u5c31\u662f\u5251\u4e2d\u7684\u9738\u738b \u4e86\uff01\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u5509\uff0c\u5509\uff01\u8fd9\u6837\u597d\u7684\u5b9d\u8d1d\u9001\u7ed9\u4e86\u8428\u603b\u7ba1\uff0c\u53ef\u771f\u662f\u592a\u53ef\u60dc\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u672c\u6765\u767d\u90b5\u662f\u51c6\u5907\u4e86\u53e6\u4e00\u4ef6\u5b9d\u7269\u8fed\u7ed9\u8428\u603b\u7ba1\u7684\uff0c\u53ef\u60dc\u4e22\u4e86\u3002\u4e0d\u5f97\u5df2\u624d\u53ea\u597d \u628a\u4ed6\u7559\u7ed9\u81ea\u5df1\u7528\u7684\u8fd9\u5757\u7384\u94c1\u9001\u53bb\u3002\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u90a3\u4ef6\u5b9d\u7269\u53c8\u662f\u4ec0\u4e48\uff1f\u53c8\u600e\u7684\u4e22\u4e86\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u674e\u6566\u76d7\u5b9d\u79c1\u9003\u4e4b\u4e8b\u4f60\u603b\u77e5\u9053\u5427\uff1f\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u6211\u77e5\u9053\u5e2e\u4e3b\u5df2\u7ecf\u6d3e\u9752\u7b26\u548c\u7126\u78ca\u8ffd\u8fd9\u5c0f\u5b50\u53bb\u4e86\u3002\u5374\u4e0d\u77e5\u4ed6\u5077\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u5c31\u662f\u6211\u6240\u8bf4\u7684\u90a3\u4ef6\u5b9d\u8d1d\u4e86\uff0c\u8fd9\u662f\u4e00\u4e32\u4e00\u767e\u9897\u7684\u53c8\u5706\u53c8\u5927\u7684\u73cd\u73e0\u3002\u6bcf\u4e00\u9897 \u8fd9\u6837\u7684\u73cd\u73e0\u8981\u503c\u4e09\u5343\u4e24\u94f6\u5b50\uff01\u201d\u5706\u6d77\u5410\u4e86\u5410\u820c\u5934\u53eb\u9053\uff1a\u201c\u6211\u7684\u5988\u5440\uff0c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8fd9\u4e00\u4e32\u73cd\u73e0 \u5c82\u4e0d\u662f\u8981\u503c\u4e09\u5341\u4e07\u4e24\u94f6\u5b50\u4e86\uff1f\u201d\u8463\u5341\u4e09\u5a18\u9053\uff1a\u2018\u4e0d\uff0c\u8981\u503c\u4e94\u5341\u4e07\u4e24\u94f6\u5b50\uff01\u56e0\u4e3a\u96be\u5f97\u51d1\u591f\u4e00\u767e \u9897\u8fd9\u6837\u7684\u73cd\u73e0\uff0c\u6240\u4ee5\u5355\u72ec\u4e00\u9897\u53ea\u503c\u4e09\u5343\uff0c\u96c6\u6210\u4e00\u4e32\uff0c\u6bcf\u9897\u5c31\u8981\u503c\u5230\u4e94\u5343\u4e86\u3002\u8fd9\u8fd8\u662f\u53f2\u5e2e\u4e3b\u5343 \u65b9\u767e\u8ba1\uff0c\u624d\u6c42\u5f97\u4e00\u4e2a\u6ce2\u65af\u80e1\u8ba9\u4e0e\u4ed6\u7684\u3002\u82e5\u662f\u666e\u901a\u4eba\u5440\uff0c\u6709\u4e94\u5341\u4e07\u4e24\u94f6\u5b50\u4e5f\u4e0d\u77e5\u4ece\u4f55\u5904\u8d2d\u4e70 \u5462\uff01\u201d \u3000\u3000\u5706\u6d77\u54bd\u4e0b\u53e3\u6c34\uff0c\u4e0d\u80dc\u8273\u7fa1\u5730\u8bf4\u9053\u3002\u201d\u53ef\u771f\u662f\u4fbf\u5b9c\u4e86\u674e\u6566\u8fd9\u5c0f\u5b50\u4e86\uff0c\u53d1\u4e86\u8fd9\u4e48\u4e00\u7b14\u5927\u6a2a \u8d22\uff01\u4e5f\u602a\u4e0d\u5f97\u5e2e\u4e3b\u5982\u6b64\u607c\u6012\uff0c\u52a1\u5fc5\u8981\u628a\u4ed6\u7f09\u62ff\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u7b11\u9053\uff1a\u201c\u73cd\u73e0\u662f\u6709\u4ef7\u4e4b\u5b9d\uff0c\u8fd9\u4e2a\u5323\u5b50\u91cc\u7684\u90a3\u5757\u7384\u94c1\u5219\u662f\u65e0\u4ef7\u4e4b\u5b9d\u3002\u8ba4\u771f\u8bf4\u6765\uff0c \u7384\u94c1\u6bd4\u73e0\u4e32\u8fd8\u66f4\u5b9d\u8d35\u5462\uff01\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u53ef\u662f\u62ff\u6765\u5f53\u4f5c\u5bff\u793c\uff0c\u53ea\u6015\u8428\u603b\u7ba1\u4f1a\u66f4\u559c\u6b22\u90a3\u4e32\u73cd\u73e0\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\u3002\u201d\u4f46\u5bf9\u5e2e\u4e3b\u6765\u8bf4\uff0c\u4ed6\u5219\u662f\u5b81\u613f\u81ea\u5df1\u7559\u4e0b\u7384\u94c1\u7684\u3002\u504f\u504f\u5728\u5c31\u8981\u9001\u51fa\u5bff\u793c\u7684\u65f6 \u5019\uff0c\u73e0\u4e32\u5374\u7ed9\u674e\u6566\u8fd9\u5c0f\u5b50\u5077\u4e86\u3002\u8fd9\u624d\u903c\u5f97\u4ed6\u4e0d\u80fd\u4e0d\u628a\u7384\u94c1\u9001\u53bb\u3002\u4e0d\u8fc7\uff0c\u4ee4\u5f97\u767d\u90b5\u5982\u6b64\u6c14\u607c\uff0c \u73e0\u4e32\u88ab\u5077\uff0c\u8fd8\u4e0d\u662f\u552f\u4e00\u539f\u56e0\u3002\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\u53e6\u5916\u7684\u539f\u56e0\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u5929\u9b54\u6559\u6709\u4e09\u7bc7\u767e\u6bd2\u771f\u7ecf\uff1f\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u542c\u8bf4\u5929\u9b54\u6559\u6559\u4e3b\u628a\u5b83\u85cf\u5728\u5f82\u5f95\u5c71\u4e0a\u7684\u795e\u5e99\u4e4b\u4e2d\uff0c\u636e\u8bf4\u662f\u5979\u5f53\u5e74\u4e00\u4e2a\u4f8d\u5973\u6cc4\u6f0f \u51fa\u6765\u7684\u79d8\u5bc6\uff0c\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff1f\u4f46\u6b64\u4e8b\u5374\u53c8\u4e0e\u674e\u6566\u4f55\u5173\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u767d\u90b5\u4e5f\u66fe\u542c\u5230\u8fd9\u6837\u7684\u4f20\u8bf4\uff0c\u4f46\u5374\u4e0d\u77e5\u85cf\u5728\u795e\u5e99\u7684\u4ec0\u4e48\u5730\u65b9\u3002\u4ed6\u60f3\u8981\u8fd9\u4e09 \u7bc7\u767e\u6bd2\u771f\u7ecf\uff0c\u4e0d\u6599\u53c8\u7ed9\u674e\u6566\u8fd9\u5c0f\u5b50\u68cb\u5148\u4e00\u7740\uff0c\u5148\u5077\u53bb\u4e86\u3002\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u600e\u4e48\u77e5\u9053\u662f\u674e\u6566\u8fd9\u5c0f\u5b50\u5077\u53bb\u4e86\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\u3002\u201d\u5e2e\u4e3b\u4e0d\u662f\u6d3e\u4e86\u9752\u7b26\u548c\u7126\u78ca\u53bb\u8ffd\u4ed6\u5417\uff1f\u4f60\u731c\u4ed6\u9003\u5230\u54ea\u513f\uff0c\u5c31\u662f\u9003\u5230\u5f82\u5f95\u5c71 \u4e0a\u5929\u9b54\u6559\u7684\u795e\u5e99\u4e4b\u4e2d\u3002 \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u674e\u6566\u8fd9\u5c0f\u5b50\u600e\u4e48\u80fd\u9003\u51fa\u4ed6\u4eec\u624b\u4e0b\uff0c\u8fd8\u5077\u53bb\u4e86\u767e\u6bd2\u771f\u7ecf\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u674e\u6566\u5728\u5e2e\u4e2d\u7684\u65f6\u5019\uff0c\u88c5\u4f5c\u662f\u4e00\u4ecb\u4e66\u751f\uff0c\u4e0d\u61c2\u6b66\u529f\uff0c\u54b1\u4eec\u90fd\u7ed9\u4ed6\u9a97\u8fc7\u4e86\u3002 \u5176\u5b9e\u4ed6\u7684\u672c\u9886\u5f88\u5f3a\uff0c\u6050\u6015\u8fd8\u4e0d\u5728\u4f60\u6211\u4e4b\u4e0b\u5462\uff01\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u5373\u4f7f\u5982\u6b64\uff0c\u4ed6\u4e5f\u80dc\u4e0d\u4e86\u9752\u7b26\u548c\u7126\u78ca\u4e24\u4eba\u5427\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\u3002\u201d\u6211\u8fd8\u672a\u8bf4\u5b8c\u5462\u3002\u4f60\u542c\u6211\u8bf4\u4e0b\u53bb\uff0c\u4e14\u8bf4\u9752\u7b26\u4e0e\u7126\u78ca\u8ffd\u5230\u4e86\u90a3\u5ea7\u795e\u5e99\uff0c\u5374\u4e0d \u89c1\u674e\u6566\u8fd9\u5c0f\u5b50\u3002\u4e0d\u77e5\u4ece\u54ea\u91cc\u6765\u7684\u4e00\u4e2a\u5c0f\u53eb\u5316\uff0c\u628a\u4ed6\u4eec\u4e24\u4eba\u90fd\u6253\u8d25\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u6697\u7b11\uff1a\u201c\u6211\u5c31\u5728\u4f60\u4eec\u7684\u773c\u76ae\u5e95\u4e0b\uff0c\u53ef\u7b11\u4f60\u4eec\u90fd\u662f\u7741\u773c\u778e\u5b50\uff01\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u9752\u7b26\u548c\u7126\u78ca\u9003\u51fa\u90a3\u5ea7\u795e\u5e99\uff0c\u4f46\u5e76\u6ca1\u6709\u8d70\u8fdc\uff0c\u800c\u662f\u8eb2\u5728\u5c71\u4e0a\u7684\u8305\u8349 \u4e1b\u4e2d\u3002\u4e0d\u4e45\uff0c\u5c31\u89c1\u674e\u6566\u548c\u90a3\u5c0f\u53eb\u5316\u4e00\u540c\u51fa\u6765\uff0c\u674e\u6566\u624b\u4e0a\u63d0\u7740\u4e00\u53e3\u5927\u949f\uff0c\u4ed6\u628a\u90a3\u53e3\u5927\u949f\u4e00\u629b\uff0c \u5c31\u629b\u4e0b\u4e86\u767e\u4e08\u6df1\u6e0a\uff01\u4e0d\u4f46\u5982\u6b64\uff0c\u90a3\u4e32\u73cd\u73e0\u4e5f\u629b\u4e0b\u53bb\u4e86\uff01\u5371\u5d16\u5ced\u58c1\u5e7d\u8c37\u6df1\u6e0a\uff0c\u5373\u4f7f\u6709\u5929\u5927\u7684\u672c \u9886\uff0c\u4e5f\u662f\u635e\u4e0d\u8d77\u6765\u7684\u4e86\uff01\u4ed6\u4eec\u4e8c\u4eba\u89c1\u6b64\u60c5\u5f62\uff0c\u604d\u7136\u5927\u609f\uff0c\u4e0d\u7528\u8bf4\u90a3\u4e09\u7bc7\u767e\u6bd2\u771f\u7ecf\u662f\u523b\u5728\u949f\u4e0a \u7684\u4e86\uff01\u674e\u6566\u8fd9\u5c0f\u5b50\u60f3\u5fc5\u4e5f\u5c31\u662f\u8eb2\u5728\u90a3\u5927\u949f\u4e4b\u5185\uff0c\u8fd9\u624d\u9003\u8fc7\u4e86\u4ed6\u4eec\u7684\u773c\u775b\u7684\u3002\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u4e09\u7bc7\u767e\u6bd2\u771f\u7ecf\u679c\u7136\u662f\u674e\u6566\u8fd9\u5c0f\u5b50\u68cb\u5148\u4e00\u7740\uff0c\u5148\u5077\u770b\u4e86\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u53ef\u6068\u4ed6\u5077\u770b\u8fd8\u4e0d\u6253\u7d27\uff0c\u8fd8\u628a\u8fd9\u5927\u949f\u629b\u4e0b\u6df1\u6f6d\uff0c\u53eb\u767d\u90b5\u6c38\u8fdc\u4e5f\u5f97\u4e0d\u7740\uff0c\u6240 \u4ee5\u767d\u90b5\u624d\u8fd9\u6837\u751f\u6c14\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u5230\u8fd9\u91cc\uff0c\u4e5f\u662f\u604d\u7136\u5927\u609f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u6211\u5012\u662f\u9519\u602a\u674e\u6566\u4e86\u3002\u4e0d\u662f\u4ed6 \u7684\u5fc3\u672f\u4e0d\u6b63\uff0c\u800c\u662f\u4e3a\u4e86\u907f\u514d\u8ba9\u53f2\u767d\u90b5\u8fd9\u6837\u7684\u9b54\u5934\u83b7\u5f97\uff0c\u4ed6\u539f\u662f\u5e94\u8be5\u5982\u6b64\u505a\u7684\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u674e\u6566\u8fd9\u5c0f\u5b50\u662f\u7ed9\u5e2e\u4e3b\u638c\u7ba1\u6587\u4e66\u7684\uff0c\u5e2e\u4e2d\u7684\u79d8\u5bc6\uff0c\u4ed6\u4e5f\u77e5\u5f97\u4e0d\u5c11\u3002 \u6709\u6b64\u4e09\u6837\u539f\u56e0\uff0c\u5e2e\u4e3b\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u80fd\u8ba9\u4ed6\u9003\u8dd1\uff01\u4ed6\u5df2\u8bb8\u4e0b\u8bfa\u8a00\uff0c\u6709\u8c01\u80fd\u591f\u628a\u8fd9\u5c0f\u5b50\u6349\u56de\u6765\uff0c \u5c31\u5347\u4ed6\u505a\u526f\u5e2e\u4e3b\u3002\u201d \u3000\u3000\u5706\u6d77\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u6211\u5012\u8981\u7559\u5fc3\u4e86\u3002\u56db\u59b9\uff0c\u54b1\u4eec\u8d81\u8fd9\u6b21\u9001\u5bff\u793c\u4e4b\u4fbf\uff0c\u5728\u4eac\u57ce\u53ef\u4ee5\u4f1a\u89c1 \u8bb8\u591a\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\uff0c\u4e0d\u59a8\u6258\u4ed6\u4eec\u4ee3\u4e3a\u6697\u8bbf\u660e\u67e5\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u7b11\u9053\uff1a\u201c\u6211\u529d\u4f60\u4e0d\u8981\u64cd\u8fd9\u4efd\u5fc3\u601d\uff01\u201d\u5706\u6d77\u9053\uff1a\u201c\u6211\u5012\u4e0d\u662f\u5e0c\u7f55\u526f\u5e2e\u4e3b\u4e4b\u4f4d\uff0c\u4f46 \u54b1\u4eec\u56db\u5927\u9ad8\u624b\u9f50\u540d\uff0c\u4f60\u6211\u82e5\u80fd\u5efa\u6b64\u5927\u529f\uff0c\u4e5f\u597d\u538b\u5012\u9752\u7b26\u548c\u7126\u78ca\u554a\uff01\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u8fd9\u6837\u7684\u80dc\uff0c\u4e0d\u4e89\u4e5f\u7f62\uff01\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u4f60\u662f\u6015\u6211\u6253\u4e0d\u8fc7\u674e\u6566\u8fd9\u5c0f\u5b50\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u66f4\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u90a3\u5374\u4e3a\u4f55\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u8001\u5b9e\u544a\u8bc9\u4f60\u5427\uff0c\u5e2e\u4e3b\u662f\u6068\u4e0d\u5f97\u6740\u6389\u8fd9\u5c0f\u5b50\u7684\uff0c\u53ef\u662f\u8fd9\u5c0f\u5b50\u7684\u80cc\u540e\u4e5f\u662f\u6709 \u4eba\u6491\u8170\u7684\u5462\uff01\u8fd9\u4e2a\u4eba\u5440\uff0c\u4f60\u6211\u90fd\u5f97\u7f6a\u4e0d\u8d77\uff01\u201d \u3000\u3000\u5706\u6d77\u8be7\u9053\u3002\u201d\u662f\u8c01\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u660e\u767d\uff1f\u5c31\u662f\u54b1\u4eec\u5e2e\u4e3b\u7684\u90a3\u4e2a\u5b9d\u8d1d\u59b9\u59b9\uff0c\u53f2\u7ea2\u82f1\u3001\u53f2\u5927\u5c0f\u59d0\uff01\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u5979\uff01\u5e2e\u4e3b\u5bf9\u8fd9\u4e2b\u5934\u7684\u786e\u662f\u8981\u987e\u5fcc\u4e09\u5206\u7684\u3002\u201d \u3000\u3000\u7ae5\u5341\u4e09\u5a18\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u548c\u674e\u6566\u6697\u5730\u91cc\u6709\u79c1\u60c5\uff0c\u7792\u5f97\u8fc7\u522b\u4eba\u53ef\u7792\u4e0d\u8fc7\u6211\u3002\u53ea\u662f\u6211\u4e0d\u6562\u548c\u5e2e \u4e3b\u8bf4\u7f62\u4e86\u3002\u201d \u3000\u3000\u5706\u6d77\u9057\uff1a\u201c\u8fd9\u4e2b\u5934\u7684\u6b66\u529f\u4e0d\u5728\u5979\u54e5\u54e5\u4e4b\u4e0b\uff0c\u4f46\u5979\u4e5f\u4e0d\u6562\u516c\u7136\u5bf9\u6297\u5979\u7684\u54e5\u54e5\u5427\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u4eba\u5440\uff0c\u5c31\u662f\u7cca\u91cc\u7cca\u6d82\uff0c\u4e0d\u4f1a\u7528\u7528\u8111\u7b4b\u3002\u201d \u3000\u3000\u5706\u6d77\u7b11\u9053\u3002\u201d\u548c\u4f60\u8fd9\u6837\u7684\u4e00\u4e2a\u806a\u660e\u4eba\u5728\u4e00\u8d77\uff0c\u8fd8\u7528\u5f97\u7740\u6211\u52a8\u8111\u7b4b\u5417\uff1f\u597d\u5427\uff0c\u8fd9\u5c31\u8bf7\u4f60\u6307 \u6559\u6211\u5427\uff01\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u7ea2\u82f1\u8fd9\u4e2b\u5934\u867d\u7136\u4e0d\u6562\u516c\u7136\u8fdd\u6297\u5979\u7684\u54e5\u54e5\uff0c\u4f46\u54b1\u4eec\u82e5\u662f\u6349\u4e86\u5979\u7684\u5fc3\u4e0a\u4eba\uff0c \u5979\u5c82\u6709\u4e0d\u548c\u54b1\u4eec\u8bb0\u4ec7\u4e4b\u7406\uff1f\u5979\u548c\u5e2e\u4e3b\u7a76\u7adf\u662f\u5144\u59b9\uff0c\u5e2e\u4e3b\u4e5f\u8981\u8ba9\u5979\u51e0\u5206\u3002\u53ea\u6015\u6349\u4e86\u90a3\u5c0f\u5b50\u4ecd\u8981 \u7ed9\u5979\u653e\u4e86\uff0c\u54b1\u4eec\u5374\u4f55\u82e6\u4e0e\u5979\u7ed3\u4ec7\u3002\u201d \u3000\u3000\u5706\u6d77\u7b11\u9053\uff1a\u201c\u7a76\u7adf\u662f\u4f60\u4eec\u5973\u4eba\u5bb6\u5fc3\u773c\u7ec6\uff0c\u597d\uff0c\u6211\u542c\u4f60\u7684\u8bdd\uff0c\u4ee5\u540e\u89c1\u4e86\u674e\u6566\u8fd9\u5c0f\u5b50\uff0c\u6211\u4e5f \u53ea\u773c\u5f00\u53ea\u773c\u95ed\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\u3002\u201d\u6211\u77e5\u9053\u7684\u90fd\u5df2\u544a\u8bc9\u9645\u4e86\uff0c\u4f60\u53ef\u4ee5\u51fa\u53bb\u5566\u3002\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u3002\u6211\u4e5f\u8fd8\u6709\u6b63\u7ecf\u8bdd\u8981\u548c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u4f60\u53ef\u4f11\u60f3\u8d56\u5728\u8fd9\u513f\u3002\u6211\u4e0d\u4fe1\u4f60\u4e5f\u4f1a\u6709\u4ec0\u4e48\u6b63\u7ecf\u8bdd\u513f\u3002\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u771f\u7684\u662f\u6b63\u7ecf\u4e8b\u60c5\uff0c\u4f60\u542c\u6211\u8bf4\uff0c\u90a3\u5c0f\u5b50\u53ea\u6015\u6709\u4e00\u70b9\u90aa\u95e8\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u6ca1\u5934\u6ca1\u8111\u7684\u54ea\u4e2a\u5c0f\u5b50\uff1f\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u54b1\u4eec\u8def\u4e0a\u9047\u89c1\u7684\u90a3\u4e2a\u5c0f\u5b50\u5440\uff0c\u521a\u624d\u5728\u8fd9\u513f\u4e0d\u662f\u53c8\u89c1\u7740\u4e86\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u4eba\u5bb6\u662f\u8d35\u5bb6\u516c\u5b50\uff0c\u5230\u4e86\u82cf\u5dde\uff0c\u4e0d\u4f4f\u5feb\u6d3b\u6797\u8fd8\u4f4f\u54ea\u513f\uff1f\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u6211\u603b\u89c9\u5f97\u4ed6\u5f62\u8ff9\u53ef\u7591\uff0c\u4f60\u4e0d\u89c9\u5f97\u4ed6\u662f\u5728\u6ce8\u610f\u54b1\u4eec\u4e48\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u81ea\u5df1\u7591\u5fc3\u751f\u6697\u9b3c\u7f62\u4e86\u3002\u4e0d\u8fc7\uff0c\u6211\u5012\u60f3\u542c\u4f60\u8bf4\u8bf4\uff0c\u4f60\u6000\u7591\u4ed6\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u4f60\u521a\u624d\u4e0d\u662f\u8bf4\u9752\u7b26\u4e0e\u7126\u78ca\u5728\u5f82\u5f95\u5c71\u4e0a\u78b0\u89c1\u4e00\u4e2a\u5c0f\u53eb\u5316\uff0c\u7ed9\u8fd9\u5c0f\u53eb\u5316\u628a\u4ed6\u4eec\u4e24 \u4eba\u6253\u8d25\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u54e6\uff0c\u6562\u60c5\u4f60\u6000\u7591\u8fd9\u5c0f\u5b50\u5c31\u662f\u90a3\u5c0f\u53eb\u5316\u3002\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u4f60\u53ef\u522b\u7b11\u3002\u7109\u77e5\u4ed6\u4eec\u4e0d\u662f\u540c\u4e00\u4e2a\u4eba\uff0c\u4f60\u6ce8\u610f\u4e86\u90a3\u5c0f\u5b50\u7684\u773c\u795e\u6ca1\u6709\uff0c\u6211\u770b\u4ed6\u5149 \u534e\u5185\u655b\uff0c\u4e00\u5b9a\u662f\u7ec3\u8fc7\u6b66\u529f\u7684\u4eba\u3002\u800c\u4e14\u5bfb\u5e38\u7684\u8d35\u5bb6\u516c\u5b50\uff0c\u5f53\u6211\u7684\u9a6c\u51b2\u5230\u4ed6\u7684\u8eab\u524d\uff0c\u8981\u7528\u9a6c\u97ad\u6253 \u4ed6\u7684\u65f6\u5019\uff0c\u4ed6\u5c82\u6709\u4e0d\u60ca\u614c\u4e4b\u7406\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5728\u5916\u9762\u5077\u542c\uff0c\u6697\u5403\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u53ea\u9053\u8fd9\u53ae\u662f\u4e2a\u83bd\u548c\u5c1a\uff0c\u5374\u539f\u6765\u4e5f\u662f\u4e2a\u9887\u4e3a\u8001 \u7ec3\u7684\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\u5462\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8d35\u5bb6\u516c\u5b50\u7ec3\u8fc7\u6b66\u529f\u7684\u6709\u4ec0\u4e48\u51fa\u5947\uff0c\u4f60\u522b\u80e1\u4e71\u731c\u7591\u5566\uff0c\u5bcc\u8d35\u5bb6\u516c\u5b50 \u53ef\u4ee5\u5230\u5feb\u6d3b\u6797\u6765\u5bfb\u5feb\u6d3b\u7684\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u653e\u5f03\u9526\u8863\u7389\u98df\uff0c\u5374\u53bb\u626e\u4e00\u4e2a\u7834\u7834\u70c2\u70c2\u7684\u5c0f\u53eb\u5316\uff1f\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u6211\u5012\u60f3\u53bb\u63a2\u4ed6\u4e00\u63a2\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u4f60\u522b\u591a\u4e8b\u5566\uff0c\u7ed9\u6211\u7761\u5427\uff0c\u660e\u5929\u8fd8\u8981\u8d76\u8def\u5462\u3002\u201d \u3000\u3000\u5706\u6d77\u542c\u5979\u8fd9\u4e48\u4e00\u8bf4\uff0c\u767b\u65f6\u563b\u76ae\u7b11\u8138\u5730\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4f60\u8bf4\u4e0d\u662f\u5c31\u4e0d\u662f\u3002\u5a18\u5b50\u5429\u5490\uff0c\u5c0f \u50e7\u6562\u4e0d\u4ece\u547d\u3002\u90a3\u54b1\u4eec\u5c31\u7761\u5427\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u674f\u8138\u751f\u55d4\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u60f3\u5230\u54ea\u513f\u53bb\u4e86\uff1f\u51fa\u53bb\uff0c\u51fa\u53bb\uff01\u4f60\u5230\u5ba2\u5385\u53bb\u7761\u3002\u201d\u5706\u6d77\u7b11 \u563b\u563b\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u7559\u6211\u5728\u8fd9\u513f\u5462\u3002\u54ce\u5440\uff0c\u4f60\u522b\u63a8\u6211\u5440\uff01\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u55d4\u9053\uff1a\u201c\u4f60\u518d\u80e1\u8bf4\uff0c\u6211\u5c31\u8981\u6253\u4f60\u4e86\u3002\u201d\u5706\u6d77\u7b11\u9053\uff1a\u201c\u6253\u662f\u4eb2\u7231\u9a82\u662f\u75bc\uff0c\u5c31\u7ed9\u4f60 \u6253\u6253\u4f55\u59a8\uff1f\u54ce\u5440\uff0c\u4f60\u5f53\u771f\u6253\u5440\uff1f\u201d\u5706\u6d77\u672c\u6765\u662f\u5750\u5728\u5e8a\u4e0a\u7684\uff0c\u8fd9\u65f6\u7ed9\u8463\u5341\u4e09\u5a18\u8d76\u51fa\u5367\u623f\uff0c\u800c\u8463 \u5341\u4e09\u5a18\u6240\u7ad9\u7684\u4f4d\u7f6e\u4e5f\u6070\u597d\u662f\u5f53\u7a97\u7684\u4f4d\u7f6e\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u542c\u5f97\u4ed6\u4eec\u6253\u60c5\u9a82\u4fcf\uff0c\u5fc3\u91cc\u5f88\u4e0d\u8010\u70e6\uff0c\u672c\u6765\u5c31\u60f3\u51fa\u624b\u53eb\u4ed6\u4eec\u5403\u5403\u82e6\u5934\u7684\uff0c\u4f46\u8f6c\u5ff5\u4e00 \u60f3\uff1a\u201c\u4e0d\u5982\u7b49\u4ed6\u4eec\u7761\u4e86\uff0c\u518d\u5077\u4ed6\u90a3\u7ea2\u6728\u5323\u5b50\u3002\u201d\u91d1\u9010\u6d41\u4e00\u6765\u662f\u60f3\u8981\u90a3\u5757\u7384\u94c1\uff0c\u4e8c\u6765\u4e5f\u597d\u53eb\u4ed6 \u4eec\u9001\u4e0d\u6210\u5bff\u793c\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5f53\u7136\u4ed6\u4eec\u7761\u7740\u4e86\u6211\u662f\u4e0d\u80fd\u6697\u7b97\u4ed6\u4eec\u7684\uff0c\u4f46\u5077\u4e86\u4ed6\u4eec\u5e2e\u4e3b\u8981\u9001\u7ed9 \u8428\u798f\u9f0e\u7684\u5b9d\u8d1d\uff0c\u6211\u4e0d\u7ed9\u4ed6\u4eec\u82e6\u5934\u5403\uff0c\u53f2\u767d\u90b5\u4e5f\u4f1a\u7ed9\u4ed6\u4eec\u82e6\u5934\u5403\u7684\u3002\u201d \u3000\u3000\u4e0d\u6599\u91d1\u9010\u6d41\u672a\u66fe\u51fa\u624b\uff0c\u8463\u5341\u4e09\u5a18\u5374\u5148\u51fa\u624b\uff0c\u91d1\u9010\u6d41\u53cd\u800c\u9669\u4e9b\u5403\u4e86\u5979\u7684\u82e6\u5934\u3002 \u3000\u3000\u5c31\u5728\u91d1\u9010\u6d41\u6697\u81ea\u76d8\u7b97\u7684\u65f6\u5019\uff0c\u9661\u5730\u773c\u524d\u91d1\u8292\u7535\u95ea\uff0c\u8463\u5341\u4e09\u5a18\u4e00\u628a\u6885\u82b1\u9488\u6492\u4e86\u51fa\u6765\uff0c\u539f\u6765 \u8463\u5341\u4e09\u5a18\u65e9\u5df2\u53d1\u89c9\u5916\u9762\u6709\u4eba\u5077\u542c\uff0c\u800c\u4e14\u5979\u4e5f\u662f\u65e9\u5df2\u5bf9\u91d1\u9010\u6d41\u8d77\u7591\u7684\u4e86\u3002\u5979\u6545\u610f\u88c5\u4f5c\u4e0d\u76f8\u4fe1\u5706 \u6d77\u7684\u8bf4\u8bdd\uff0c\u6b63\u662f\u8981\u91d1\u9010\u6d41\u4e0d\u52a0\u6212\u5907\u7684\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u7684\u6697\u5668\u624b\u6cd5\u5de7\u5999\u4e4b\u6781\uff0c\u5168\u9010\u6d41\u662f\u8eb2\u5728\u4e00\u5757\u5047\u5c71\u77f3\u540e\uff0c\u6885\u82b1\u9488\u82e5\u662f\u5728\u4ed6\u9762\u524d\u5c04 \u6765\uff0c\u91d1\u9010\u6d41\u6709\u5c71\u77f3\u5c4f\u969c\uff0c\u81ea\u662f\u65e0\u59a8\uff0c\u4f46\u5979\u8fd9\u628a\u6885\u82b1\u9488\u5374\u662f\u4ece\u5c71\u77f3\u4e0a\u65b9\u98de\u8fc7\uff0c\u7a81\u7136\u95f4\u5012\u5c04\u56de \u6765\uff0c\u624b\u6cd5\u4e4b\u5999\uff0c\u8fd0\u52b2\u4e4b\u5de7\uff0c\u5f53\u771f\u662f\u532a\u5937\u6240\u601d\uff01 \u3000\u3000\u6885\u82b1\u9488\u65e0\u58f0\u65e0\u606f\uff0c\u800c\u4e14\u91d1\u9010\u6d41\u53c8\u7edd\u5bf9\u610f\u60f3\u4e0d\u5230\u8fd9\u628a\u6885\u82b1\u9488\u4f1a\u4ece\u4ed6\u7684\u80cc\u540e\u5c04\u6765\uff0c\u5f85\u4ed6\u84e6\u7136 \u60ca\u89c9\uff0c\u56de\u5934\u4e4b\u65f6\uff0c\u8ddf\u524d\u5df2\u662f\u91d1\u8292\u7535\u95ea\uff0c\u8981\u7528\u5288\u7a7a\u638c\u4e5f\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u597d\u4e2a\u91d1\u9010\u6d41\uff0c\u5c31\u5728\u8fd9\u5371\u673a\u77ac\u606f\u4e4b\u95f4\uff0c\u663e\u51fa\u4e86\u5353\u7edd\u7684\u8f7b\u529f\u672c\u9886\uff0c\u8eab\u5f62\u5e73\u5730\u62d4\u8d77\uff0c\u4e00\u8dc3\u4e09\u4e08 \u591a\u9ad8\uff0c\u6885\u82b1\u9488\u4ece\u4ed6\u811a\u5e95\u98de\u8fc7\uff0c\u4e00\u679a\u4e5f\u6253\u4e0d\u5230\u4ed6\u7684\u8eab\u4e0a\u3002\u91d1\u9010\u6d41\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u9690\u9690\u89c9\u5f97\u6709\u70b9\u8165 \u81ed\uff0c\u8fd9\u624d\u77e5\u9053\u8463\u5341\u4e09\u5a18\u8fd9\u628a\u6885\u5316\u9488\u4e0d\u4f46\u624b\u6cd5\u6253\u5f97\u5947\u5999\uff0c\u800c\u4e14\u8fd8\u662f\u6bd2\u9488\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9a82\u9053\uff1a\u201c\u597d\u4e2a\u72e0\u6bd2\u7684\u5a46\u5a18\uff0c\u6211\u5012\u8981\u770b\u770b\u4f60\u8fd8\u6709\u4ec0\u4e48\u9b3c\u57df\u4f0e\u4fe9\uff1f\u201d\u8eab\u5f62\u521a\u521a\u843d\u5730\uff0c \u8bdd\u72b9\u672a\u4e86\uff0c\u5706\u6d77\u5df2\u662f\u6251\u4e86\u51fa\u6765\u3002\u559d\u9053\uff1a\u201c\u5c0f\u53eb\u5316\uff0c\u4f60\u5148\u5c1d\u5c1d\u6211\u7684\u62f3\u5934\u6ecb\u5473\uff01\u201d\u4ed6\u662f\u7ed9\u8463\u5341\u4e09 \u5a18\u63a8\u4e86\u51fa\u95e8\u7684\uff0c\u6545\u6b64\u6bd4\u8463\u5341\u4e09\u5a18\u5148\u5230\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6a2a\u638c\u4e00\u6321\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u5c31\u5728\u4ed6\u62f3\u5934\u4e00\u6293\uff0c\u5706\u6d77\u5929\u543c\u4e00\u58f0\uff0c\u62f3\u5934\u5df2\u662f\u7ed9\u91d1\u9010\u6d41\u6293\u4e86 \u4e94\u9053\u8840\u75d5\u3002\u4f46\u91d1\u9010\u6d41\u63a5\u4e86\u4ed6\u8fd9\u4e00\u62f3\uff0c\u4e5f\u662f\u4e0d\u7981\u5012\u9000\u4e09\u6b65\u3002\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u8fd9\u8d3c\u79c3\u7684\u5185\u5bb6\u771f\u529b \u5012\u4e5f\u4e0d\u5f31\uff0c\u9648\u53d4\u53d4\u8bf4\u90a3\u5996\u5987\u7684\u672c\u9886\u8fd8\u5728\u4ed6\u4e4b\u4e0a\uff0c\u4eca\u665a\u6211\u662f\u5fc5\u987b\u8ba4\u771f\u5bf9\u4ed8\uff0c\u4e0d\u80fd\u8f7b\u654c\u4e86\u3002\u201d \u3000\u3000\u5706\u6d77\u5929\u543c\u4e00\u58f0\uff0c\u53cc\u62f3\u5f00\u53d1\uff0c\u53c8\u6251\u8fc7\u6765\u3002\u91d1\u9010\u6d41\u89c1\u4ed6\u6765\u52bf\u51f6\u9669\uff0c\u5148\u7528\u5927\u7f57\u6b65\u6cd5\u95ea\u5f00\uff0c\u6b63\u8981 \u7ed5\u5230\u4ed6\u7684\u80cc\u540e\u70b9\u4ed6\u7684\u201c\u98ce\u5e9c\u7a74\u201d\uff0c\u53ea\u542c\u5f97\u5530\u7684\u4e00\u58f0\uff0c\u8463\u5341\u4e09\u5a18\u624b\u91cc\u62ff\u7740\u4e00\u6839\u8f6f\u97ad\uff0c\u4eba\u672a\u5230\uff0c \u957f\u97ad\u5df2\u662f\u6253\u4e86\u5230\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u811a\u6b65\u672a\u7a33\uff0c\u6781\u96be\u95ea\u907f\uff0c\u4f46\u4ed6\u827a\u9ad8\u80c6\u5927\uff0c\u5374\u4e5f\u5e76\u4e0d\u614c\u5f20\uff0c\u773c\u770b\u90a3\u6761\u8f6f\u97ad\u5c31\u8981\u6253\u5230\u4ed6 \u7684\u8eab\u4e0a\uff0c\u91d1\u9010\u6d41\u628a\u624b\u7f29\u8fdb\u8896\u7ba1\uff0c\u957f\u8896\u4e00\u6325\uff0c\u5df2\u662f\u88f9\u4f4f\u4e86\u90a3\u6761\u8f6f\u97ad\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4f7f\u52b2\u4e00\u593a\uff0c\u559d\u9053\uff1a\u201c\u6492\u624b\uff01\u201d\u4ed6\u8fd9\u4e00\u5377\u4e00\u593a\uff0c\u7528\u4e0a\u4e86\u5185\u5bb6\u771f\u529b\uff0c\u5b9e\u662f\u975e\u540c\u5c0f\u53ef\u3002 \u8463\u5341\u4e09\u5a18\u7b11\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\uff01\u201d\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u91d1\u9010\u6d41\u7684\u534a\u6761\u8863\u8896\u5df2\u7ed9\u5979\u7684\u8f6f\u97ad\u6495\u53bb\uff0c \u529f\u529b\u5373\u4f7f\u4e0d\u662f\u5728\u91d1\u9010\u6d41\u4e4b\u4e0a\uff0c\u81f3\u5c11\u4e5f\u8db3\u4ee5\u4e0e\u4ed6\u65d7\u9f13\u76f8\u5f53\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u4e2a\u7bad\u6b65\uff0c\u8dc3\u51fa\u4e86\u4e09\u4e08\u5f00\u5916\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8463\u5341\u4e09\u5a18\u8ddf\u8e2a\u8ffd\u4e0a\uff0c\u8f6f\u97ad\u53c8\u662f\u5982 \u5f71\u968f\u5f62\u5730\u6253\u4e86\u5230\u6765\u3002\u91d1\u9010\u6d41\u4f7f\u51fa\u6d51\u8eab\u672c\u9886\uff0c\u817e\u632a\u95ea\u5c55\uff0c\u8fd8\u517c\u7528\u5288\u7a7a\u638c\u7684\u529f\u592b\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u8eb2 \u8fc7\u4e86\u5979\u7684\u201c\u56de\u98ce\u626b\u67f3\u201d\u7684\u8fde\u73af\u4e09\u97ad\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u5728\u6362\u62db\u4e4b\u9645\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u6b3a\u8d1f\u540e\u751f\u5c0f\u5b50\uff0c\u4f60\u62d4\u5251\u5427\uff01\u201d\u91d1\u9010\u6d41\u6700\u6068\u4eba\u5bb6\u770b\u5c0f \u4ed6\uff0c\u62d4\u51fa\u4e86\u4f69\u5251\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6211\u66f4\u4e0d\u80fd\u6b3a\u8d1f\u4e00\u4e2a\u5987\u9053\u4eba\u5bb6\uff0c\u4f60\u53eb\u4f60\u7684\u5978\u592b\u5e76\u80a9\u5b50\u4e0a\u5427\uff01\u201d\u8bdd \u867d\u5982\u6b64\uff0c\u91d1\u9010\u6d41\u6bd5\u7adf\u4e5f\u662f\u4e0d\u6562\u7a7a\u624b\u593a\u97ad\u4e86\u3002 \u3000\u3000\u5706\u6d77\u9762\u8272\u5927\u53d8\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5fc5\u987b\u6740\u4e4b\u706d\u53e3\uff01\u201d\u8463\u5341\u4e09\u5a18\u7b11\u9053\uff1a\u201c\u4f55\u5fc5\u548c\u4e00\u4e2a\u5c0f\u53eb\u5316 \u751f\u6c14\uff1f\u4f60\u2026\u2026\u201d\u8463\u5341\u4e09\u5a18\u60f3\u8bf4\u7684\u662f\uff1a\u201c\u4f60\uff0c\u9000\u4e0b\u53bb\u5427\uff0c\u6211\u81ea\u6709\u5206\u6570\u3002\u201d\u521a\u8bf4\u5f97\u4e00\u4e2a\u201c\u4f60\u201d \u5b57\uff0c\u53ea\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u91d1\u9010\u6d41\u5df2\u662f\u4e00\u5251\u5411\u5979\u523a\u6765\uff0c\u8463\u5341\u4e09\u5a18\u89c1\u5230\u4ed6\u5251\u6cd5\u5982\u6b64\u51cc\u5389\uff0c\u4e5f\u4e0d\u7981\u5403\u4e86 \u4e00\u60ca\u3002\u5f53\u4e0b\u987e\u4e0d\u5f97\u8bf4\u8bdd\uff0c\u8fde\u5fd9\u4f7f\u4e2a\u201c\u98ce\u522e\u843d\u82b1\u201d\u7684\u8eab\u6cd5\uff0c\u907f\u62db\u8fdb\u62db\u3002 \u3000\u3000\u54ea\u77e5\u91d1\u9010\u6d41\u4e43\u662f\u58f0\u4e1c\u51fb\u897f\u4e4b\u8ba1\uff0c\u8463\u5341\u4e09\u5a18\u521a\u4e00\u95ea\u8eab\uff0c\u91d1\u9010\u6d41\u5251\u950b\u4e00\u89e3\uff0c\u5df2\u662f\u7a81\u7136\u5411\u5706\u6d77 \u6740\u6765\u3002\u5706\u6d77\u7528\u6212\u5200\u4e00\u6321\uff0c\u4e0d\u6599\u6321\u4e86\u4e2a\u7a7a\uff0c\u91d1\u9010\u6d41\u6b3a\u8eab\u8fdb\u5251\uff0c\u90a3\u4e00\u5251\u7adf\u662f\u4ece\u4ed6\u7edd\u5bf9\u610f\u60f3\u4e0d\u5230\u7684 \u65b9\u4f4d\u523a\u6765\uff01 \u3000\u3000\u5e78\u4e8f\u8463\u5341\u4e09\u5a18\u7684\u8eab\u624b\u4ea6\u662f\u5341\u5206\u77eb\u6377\uff0c\u970d\u5730\u4e00\u97ad\uff0c\u5377\u5730\u626b\u53bb\u3002\u8fd9\u4e00\u62db\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u91d1 \u9010\u6d41\u4e0d\u6562\u88ab\u5979\u7684\u8f6f\u97ad\u7f20\u4e0a\uff0c\u4e00\u4e2a\u201c\u9ec4\u9e4a\u51b2\u9704\u201d\u7684\u8eab\u6cd5\u907f\u5f00\uff0c\u5251\u950b\u4ece\u5706\u6d77\u7684\u5149\u5934\u524a\u8fc7\uff0c\u51e0\u4e4e\u524a \u53bb\u4e86\u4ed6\u4e00\u5c42\u5934\u76ae\u3002 \u3000\u3000\u5706\u6d77\u4fa5\u5e78\u9003\u8fc7\u4e86\u5229\u5251\u7a7f\u5589\u4e4b\u707e\uff0c\u4f46\u5934\u76ae\u4e00\u7247\u6c81\u51c9\uff0c\u4ea6\u5df2\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\u3002\u8463\u5341\u4e09\u5a18\u89c1\u91d1\u9010 \u6d41\u5982\u6b64\u5389\u5bb3\uff0c\u81ea\u5fd6\u5355\u6253\u72ec\u6597\uff0c\u5373\u4f7f\u4e0d\u81f3\u4e8e\u8f93\u7ed9\u91d1\u9010\u6d41\uff0c\u4e5f\u7edd\u8d62\u4e0d\u4e86\u4ed6\u3002\u5979\u672c\u8981\u53eb\u5706\u6d77\u9000\u4e0b \u7684\uff0c\u6b64\u65f6\u4e5f\u53ea\u597d\u4f4f\u53e3\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6253\u4e0b\u4e86\u4ed6\u4eec\u7684\u5a01\u98ce\uff0c\u5927\u4e3a\u5f97\u610f\u3002\u8463\u5341\u4e09\u5a18\u4f4f\u4e86\u53e3\uff0c\u4ed6\u53ef\u8981\u8bf4\u8bdd\u4e86\u3002\u4ed6\u89c1\u5706\u6d77\u6b63\u5728 \u6478\u81ea\u5df1\u7684\u5149\u5934\uff0c\u4e0d\u7981\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8d3c\u79c3\uff0c\u4f60\u4e0d\u7528\u62c5\u5fc3\u6211\u8981\u4f60\u7684\u6027\u547d\u3002\u4fd7\u8bed\u8bf4\uff1a\u6349\u5978\u6349\u53cc\uff0c\u6211 \u6740\u4e86\u4f60\uff0c\u4f60\u4eec\u7684\u5978\u60c5\u5c82\u4e0d\u662f\u8981\u6b86\u65e0\u5bf9\u8bc1\u4e86\uff1f\u563f\uff0c\u563f\uff0c\u6211\u5012\u4e0d\u5982\u8ba9\u53f2\u767d\u90b5\u6765\u5904\u7f6e\u4f60\u4eec\uff0c\u6211\u4e50\u5f97 \u5728\u65c1\u8fb9\u770b\u770b\u628a\u620f\u3002\u201d \u3000\u3000\u4e0d\u8fc7\u91d1\u9010\u6d41\u4e5f\u6ca1\u5f97\u610f\u591a\u4e45\uff0c\u5fc3\u91cc\u53c8\u8981\u6697\u6697\u53eb\u82e6\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8d2a\u901e\u53e3\u65f6\u4e4b\u5feb\uff0c\u8bf4\u7684\u8bdd\u5927\u72af\u8463\u5341\u4e09\u5a18\u4e4b\u5fcc\u3002\u8463\u5341\u4e09\u5a18\u867d\u7136\u4e43\u662f\u6c34\u6027\u6768\u82b1\uff0c\u4f46\u9762\u5b50 \u603b\u662f\u8981\u987e\u7684\uff0c\u600e\u53d7\u5f97\u4e86\u91d1\u9010\u6d41\u7684\u53e3\u9f7f\u8f7b\u8584\uff1f\u542c\u4e86\u4ed6\u8fd9\u51e0\u53e5\u8bdd\u3001\u4e0d\u7531\u5f97\u4e5f\u662f\u52c3\u7136\u53d8\u8272\uff0c\u767b\u65f6\u52a8 \u4e86\u6740\u673a\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u52a8\u4e86\u6740\u673a\uff0c\u91d1\u9010\u6d41\u53ef\u5c31\u8981\u5927\u5403\u82e6\u5934\u4e86\u3002\u8bba\u771f\u5b9e\u7684\u672c\u9886\uff0c\u8463\u5341\u4e09\u5a18\u672a\u5fc5\u80dc\u5f97\u8fc7\u91d1 \u9010\u6d41\uff0c\u4f46\u5979\u8fd9\u6839\u866c\u9f99\u97ad\u5374\u662f\u4e00\u4ef6\u5b9d\u7269\uff0c\u53ef\u67d4\u53ef\u521a\uff0c\u575a\u97e7\u975e\u5e38\uff0c\u5200\u5251\u524a\u4e4b\u4e0d\u65ad\uff0c\u65bd\u5c55\u8d77\u6765\uff0c\u53ef \u4ee5\u6253\u5230\u4e09\u4e08\u5f00\u5916\uff0c\u91d1\u9010\u6d41\u7684\u9752\u94a2\u5251\u53ea\u6709\u4e09\u5c3a\u4e8c\u5bf8\uff0c\u97ad\u957f\u5251\u77ed\uff0c\u5728\u5175\u5668\u4e0a\u5148\u81ea\u5403\u4e86\u4e8f\u3002 \u3000\u3000\u800c\u4e14\u8463\u5341\u4e09\u5a18\u7684\u97ad\u6cd5\u53d8\u5e7b\u83ab\u6d4b\uff0c\u5947\u8be1\u65e0\u6bd4\uff0c\u91d1\u9010\u6d41\u5e7c\u627f\u5bb6\u5b66\uff0c\u59ec\u6653\u98ce\u53c8\u65f6\u5e38\u548c\u4ed6\u8c08\u8bba\u5404 \u6d3e\u7684\u6b66\u529f\uff0c\u4ed6\u7684\u6240\u5b66\u672c\u6765\u6781\u4e3a\u5e7f\u535a\uff0c\u5404\u5bb6\u5404\u6d3e\u5404\u79cd\u5175\u5668\u7684\u62db\u6570\uff0c\u4ed6\u7eb5\u7136\u4e0d\u80fd\u8bf4\u662f\u5168\u90e8\u901a\u6653\uff0c \u4f46\u4e5f\u80fd\u5165\u773c\u4fbf\u77e5\u6765\u5386\uff0c\u552f\u6709\u8463\u5341\u4e09\u5a18\u7684\u8fd9\u8def\u97ad\u6cd5\uff0c\u4ed6\u89e3\u62c6\u4e86\u4e8c\u5341\u4e09\u62db\uff0c\u4ecd\u662f\u6478\u4e0d\u6e05\u5979\u7684\u8def \u9053\u3002 \u3000\u3000\u6b8a\u4e0d\u77e5\u91d1\u9010\u6d41\u56fa\u7136\u662f\u6697\u662d\u53eb\u82e6\uff0c\u8463\u5341\u4e09\u5a18\u5374\u4e5f\u662f\u5927\u5927\u5403\u60ca\uff01\u5979\u8fd9\u6761\u866c\u9f99\u97ad\u5728\u6c5f\u6e56\u4e0a\u4e0d\u77e5 \u6253\u8d25\u8fc7\u591a\u5c11\u6210\u540d\u4eba\u7269\uff0c\u5982\u4eca\u5728\u4e00\u4e2a\u4e0d\u89c1\u7ecf\u4f20\u7684\u540e\u751f\u5c0f\u5b50\u624b\u4e0b\uff0c\u7adf\u7136\u8ba8\u4e0d\u4e86\u534a\u70b9\u4fbf\u5b9c\uff0c\u82e5\u975e\u6709 \u5706\u6d77\u76f8\u52a9\uff0c\u5979\u4e00\u4eba\u5355\u6253\u72ec\u6597\u7684\u8bdd\uff0c\u53ea\u6015\u8fd8\u6709\u70b9\u5e94\u4ed8\u4e0d\u4e86\uff01\u91d1\u9010\u6d41\u7684\u5bb6\u4f20\u5251\u6cd5\u96c6\u5404\u6d3e\u4e4b\u957f\uff0c\u8463 \u5341\u4e09\u5a18\u4e5f\u662f\u6478\u4e0d\u6e05\u4ed6\u7684\u8def\u9053\u3002 \u3000\u3000\u5706\u6d77\u6b66\u529f\u867d\u7136\u8f83\u5f31\uff0c\u4e5f\u662f\u4e00\u540d\u9ad8\u624b\u3002\u4e00\u70b3\u6212\u5200\uff0c\u5c55\u5f00\u4e86\u201c\u4e94\u864e\u65ad\u95e8\u201d\u5200\u6cd5\uff0c\u5728\u8463\u5341\u4e09\u5a18 \u7684\u957f\u97ad\u63a9\u62a4\u4e4b\u4e0b\uff0c\u4e00\u4e2a\u8fdc\u653b\uff0c\u4e00\u4e2a\u8fd1\u6597\uff0c\u5bf9\u91d1\u9010\u6d41\u9887\u6709\u5a01\u80c1\u3002\u6fc0\u6218\u4e2d\u91d1\u9010\u6d41\u627e\u5230\u4e86\u5706\u6d77\u7684\u4e00 \u4e2a\u7834\u7efd\uff0c\u521a\u8981\u7834\u4ed6\u62db\u6570\uff0c\u54ea\u77e5\u8fd9\u662f\u5706\u6d77\u8bf1\u654c\u4e4b\u8ba1\uff0c\u6709\u610f\u201c\u5356\u201d\u7684\u7834\u7efd\uff0c\u91d1\u9010\u6d41\u8981\u7834\u4ed6\u7684\u62db \u6570\uff0c\u4e0d\u514d\u591a\u5206\u4e86\u4e00\u70b9\u7cbe\u795e\u53bb\u5bf9\u4ed8\u4ed6\uff0c\u8fd9\u5c31\u7ed9\u4e86\u8463\u5341\u4e09\u5a18\u4ee5\u53ef\u4e58\u4e4b\u673a\u3002 \u3000\u3000\u5251\u5149\u97ad\u5f71\u4e4b\u4e2d\uff0c\u53cc\u65b9\u90fd\u662f\u5feb\u5982\u95ea\u7535\uff0c\u91d1\u9010\u6d41\u4e00\u5251\u5411\u5706\u6d77\u523a\u51fa\uff0c\u8463\u5341\u4e8c\u5a18\u7684\u957f\u97ad\u4ea6\u5df2\u6253\u4e86 \u4e00\u4e2a\u5708\u5708\uff0c\u5411\u91d1\u9010\u6d41\u7684\u9888\u9879\u5957\u4e0b\u3002\u539f\u6765\u5979\u4e0e\u5706\u6d77\u914d\u5408\u6709\u7d20\uff0c\u5706\u6d77\u6562\u4e8e\u5356\u4e2a\u7834\u7efd\uff0c\u5e76\u975e\u4ed6\u672c\u8eab \u6709\u7834\u654c\u4e4b\u80fd\uff0c\u800c\u662f\u7ed9\u8463\u5341\u4e09\u5a18\u5236\u9020\u6709\u5229\u7684\u6761\u4ef6\u3002 \u3000\u3000\u8fd9\u4e00\u97ad\u540d\u4e3a\u201c\u9501\u5589\u97ad\u201d\uff0c\u5589\u5499\u88ab\u9501\uff0c\u591a\u597d\u6b66\u529d\u4e5f\u5f97\u6c14\u7edd\u800c\u4ea1\u3002\u91d1\u9010\u6d41\u5f53\u771f\u662f\u827a\u9ad8\u80c6\u5927\uff0c \u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u4e4b\u95f4\uff0c\u5df2\u5c06\u5e73\u523a\u51fa\u53bb\u7684\u5251\u52bf\u53d8\u4e3a\u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u4e3e\u5251\u4e0a\u64a9\uff0c\u4ecd\u7136\u811a\u6b65\u4e0d\u505c\u5411 \u5706\u6d77\u649e\u53bb\u3002\u8463\u5341\u4e09\u5a18\u559d\u9053\uff1a\u201c\u89e3\u5f97\u597d\uff01\u201d\u957f\u97ad\u4e00\u6296\uff0c\u5ffd\u5730\u4f38\u76f4\uff0c\u53d8\u4f5c\u4e86\u4e00\u6746\u957f\u67aa\uff0c\u81ea\u4e0a\u800c \u4e0b\uff0c\u5f84\u622e\u4e0b\u6765\u3002\u6b66\u5b66\u6709\u4e91\uff1a\u201c\u67aa\u6084\u5706\uff0c\u97ad\u6015\u76f4\u3002\u201d\u80fd\u7528\u8f6f\u97ad\u4f7f\u51fa\u957f\u67aa\u7684\u62db\u6570\uff0c\u5185\u529b\u5fc5\u987b\u8d2f\u6ce8 \u97ad\u68a2\uff0c\u90a3\u662f\u6781\u96be\u8fd0\u7528\u5f97\u597d\u7684\u3002\u8463\u5341\u4e09\u5a18\u8fd9\u4e00\u4e0b\u7a81\u7136\u53d8\u62db\uff0c\u8fde\u91d1\u9010\u6d41\u4e5f\u662f\u610f\u6599\u4e0d\u5230\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u91d1\u9010\u6d41\u80cc\u810a\u7740\u4e86\u4e00\u97ad\uff0c\u767b\u65f6\u8d77\u4e86\u4e00\u9053\u8840\u75d5\u3002\u5e78\u800c\u4ed6\u662f\u826f\u5e7c\u7ec3\u201c\u7ae5 \u5b50\u529f\u201d\u7684\uff0c\u808c\u8089\u7ed3\u5b9e\uff0c\u76ae\u80a4\u53d7\u4f24\uff0c\u5e76\u65e0\u5927\u788d\u3002\u800c\u5706\u6d77\u7ed9\u4ed6\u624b\u8098\u4e00\u649e\uff0c\u5374\u4e0d\u7531\u5f97\u5012\u9000\u4e09\u6b65\uff0c\u75db \u5f97\u54c7\u54c7\u5927\u53eb\uff01 \u3000\u3000\u53ef\u662f\u5706\u6d77\u867d\u7136\u5403\u4e8f\u8f83\u5927\uff0c\u4e5f\u8fd8\u4e0d\u662f\u4e25\u91cd\u7684\u5185\u4f24\uff0c\u8463\u5341\u4e09\u5a18\u4e00\u97ad\u6253\u7740\u4e86\u91d1\u9010\u6d41\uff0c\u6c14\u7130\u66f4 \u9ad8\uff0c\u653b\u52bf\u4e5f\u8d8a\u53d1\u51cc\u5389\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6697\u53eb\u4e0d\u5999\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4eca\u665a\u6211\u6050\u6015\u662f\u96be\u8ba8\u4fbf\u5b9c\u4e86\uff0c\u4f46\u90a3\u5757\u7384\u94c1\u672a\u66fe\u5230\u624b\uff0c\u5c31\u6b64 \u4e00\u8d70\u4e86\u4e8b\uff0c\u8fd9\u53e3\u6c14\u5374\u662f\u600e\u751f\u53d1\u6cc4\uff1f\u201d\u7a0d\u4e00\u8fdf\u7591\uff0c\u5706\u6d77\u9000\u800c\u590d\u4e0a\uff0c\u91d1\u9010\u6d41\u4e24\u9762\u53d7\u654c\uff0c\u60f3\u8981\u6446\u8131 \u8463\u5341\u5a18\u8f6f\u97ad\u7684\u7ea0\u7f20\u90fd\u96be\uff0c\u9003\u8d70\u90a3\u662f\u66f4\u4e0d\u5bb9\u6613\u4e86\u3002 \u3000\u3000\u5e78\u800c\u5706\u6d77\u5403\u4e86\u4ed6\u7684\u4e8f\uff0c\u5fc3\u4e2d\u9887\u6709\u51e0\u5206\u602f\u610f\uff0c\u4e0d\u6562\u8fc7\u4efd\u8fdb\u903c\uff0c\u91d1\u9010\u6d41\u5c1a\u53ef\u52c9\u5f3a\u652f\u6301\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u7684\u8f6f\u97ad\u4f7f\u5f97\u77eb\u82e5\u6e38\u9f99\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u91d1\u9010\u6d41\u5f00\u59cb\u989d\u5934\u89c1\u6c57\uff0c\u5fc3\u91cc\u6697\u53eb\uff1a\u201c\u7cdf\u7cd5\uff0c \u7cdf\u7cd5\uff01\u518d\u8fc7\u4e94\u5341\u62db\u53ea\u6015\u6211\u5c31\u8981\u652f\u6301\u4e0d\u4f4f\u4e86\uff0c\u4eca\u756a\u53ef\u662f\u5077\u9e21\u4e0d\u7740\u8680\u628a\u7c73\u4e5f\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u6b63\u5728\u6697\u81ea\u53eb\u82e6\uff0c\u5ffd\u542c\u5f97\u8463\u5341\u4e09\u5a18\u559d\u9053\u3002\u201d\u662f\u54ea\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff0c\u723d\u723d\u5feb\u5feb\u5730\u51fa\u6765 \u5427\uff01\u201d\u91d1\u9010\u6d41\u7531\u4e8e\u5168\u795e\u5e94\u6218\uff0c\u5374\u6ca1\u542c\u5230\u4ec0\u4e48\u58f0\u606f\u3002 \u3000\u3000\u5706\u6d77\u8bf4\u9053\uff1a\u201c\u60f3\u5fc5\u662f\u54ea\u4e2a\u4e0d\u77e5\u6b7b\u6d3b\u7684\u5ba2\u4eba\u8d77\u8eab\u5077\u770b\u3002\u201d\u4e8e\u662f\u5927\u58f0\u559d\u9053\uff1a\u201c\u516d\u5408\u5e2e\u7684\u4eba\u5728 \u6b64\u505a\u6848\uff0c\u8bc6\u8da3\u7684\u5feb\u5feb\u8eb2\u8ff8\u88ab\u7a9d\u91cc\u53bb\uff01\u201d\u4ed6\u8fd9\u4e48\u4e00\u559d\u4e4b\u540e\uff0c\u679c\u7136\u542c\u5f97\u6709\u5173\u7a97\u7684\u58f0\u97f3\uff0c\u8463\u5341\u4e09\u5a18 \u6e38\u76ee\u56db\u987e\uff0c\u56ed\u5b50\u91cc\u4e95\u6ca1\u53d1\u73b0\u4e00\u6761\u4eba\u5f71\uff0c\u4e8e\u662f\u8fde\u5979\u4e5f\u4ee5\u4e3a\u521a\u624d\u542c\u5230\u7684\u58f0\u606f\u662f\u4ec0\u4e48\u5ba2\u4eba\u8d77\u6765\u5077\u770b \u7684\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u5728\u5feb\u6d3b\u6797\u6295\u5bbf\u7684\u4f4f\u5ba2\uff0c\u4e0d\u662f\u5bcc\u5546\u5927\u8d3e\u5c31\u662f\u516c\u5b50\u738b\u5b59\uff0c\u8c01\u6562\u591a\u4e8b\u5077\u770b\uff1f\u4ed6\u4eec\u540e\u6765\u8d77\u6765 \u5173\u7a97\uff0c\u90a3\u53ea\u662f\u56e0\u4e3a\u542c\u5230\u5706\u6d77\u7684\u559d\u9a82\uff0c\u751f\u6015\u5f3a\u76d7\u4f1a\u95ef\u8fdb\u4ed6\u4eec\u7684\u623f\u95f4\uff0c\u8fd9\u624d\u4e0d\u80fd\u4e0d\u5927\u7740\u80c6\u5b50\u8d77\u6765 \u5173\u7a97\u7684\u3002\u81f3\u4e8e\u5feb\u6d3b\u6797\u7684\u90a3\u73ed\u6253\u624b\uff0c\u65e9\u5df2\u77e5\u9053\u4ed6\u4eec\u4e24\u4e2a\u662f\u516d\u5408\u5e2e\u7684\u4eba\uff0c\u5f53\u7136\u66f4\u662f\u4e0d\u6562\u51fa\u6765\u5e72 \u6d89\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u521a\u624d\u542c\u5f97\u6709\u6089\u6089\u7d22\u7d22\u7684\u58f0\u606f\u4e4b\u65f6\uff0c\u6000\u7591\u662f\u91d1\u9010\u6d41\u5728\u5feb\u6d3b\u6797\u4e2d\u5c1a\u6709\u540c\u515a\uff0c\u4e0d\u514d\u5fc3 \u795e\u7565\u5206\uff0c\u653b\u52bf\u7a0d\u7f13\uff1b\u91d1\u9010\u6d41\u4f55\u7b49\u77eb\u6377\uff0c\u770b\u51fa\u6709\u53ef\u4e58\u4e4b\u673a\uff0c\u84e6\u5730\u53c8\u5411\u5706\u6d77\u649e\u53bb\uff0c\u5706\u6d77\u5403\u8fc7\u4ed6\u7684 \u4e8f\uff0c\u614c\u5fd9\u8eb2\u95ea\u3002\u8463\u5341\u4e09\u9053\u559d\u9053\uff1a\u201c\u54ea\u91cc\u8d70\uff1f\u201d\u957f\u97ad\u970d\u5730\u5377\u6765\uff0c\u91d1\u9010\u6d41\u4ece\u5706\u6d77\u8eab\u65c1\u64e6\u8fc7\uff0c\u8d81\u52bf \u5c06\u4ed6\u4e00\u63a8\uff0c\u5706\u6d77\u201c\u54df\u201d\u4e00\u58f0\uff0c\u6070\u6070\u7ed9\u8463\u5341\u4e09\u5a18\u7684\u957f\u97ad\u5377\u4e86\u8d77\u6765\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u91d1\u9010\u6d41\u5df2 \u662f\u4e00\u4e2a\u98de\u8eab\uff0c\u63a0\u51fa\u6570\u4e08\u5f00\u5916\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u653e\u4e0b\u4e86\u5706\u6d77\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4eca\u665a\u82e5\u662f\u8ba9\u4f60\u8fd9\u5c0f\u5b50\u9003\u51fa\u6211\u7684\u638c\u5fc3\uff0c\u8001\u5a18\u8a93\u4e0d\u4e3a \u4eba\uff01\u201d\u98de\u6b65\u8ffd\u6765\uff0c\u6697\u5668\u8fde\u53d1\u3002\u8463\u5341\u4e09\u5a18\u4e0d\u4ec5\u662f\u97ad\u6cd5\u65e0\u53cc\uff0c\u6697\u5668\u4e5f\u662f\u6b66\u6797\u4e00\u7edd\u3002\u5979\u7684\u6697\u5668\u90fd\u662f \u6dec\u8fc7\u6bd2\u7684\uff0c\u4e2d\u4e86\u5c31\u662f\u89c1\u8840\u5c01\u5589\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5f2f\u5f2f\u66f2\u66f2\u5730\u8d70\u7740\u201c\u4e4b\u201d\u5b57\u8def\uff0c\u7ed5\u8fc7\u4e86\u4e24\u5ea7\u5047\u5c71\uff0c\u8463\u5341\u4e09\u5a18\u7d27\u8ffd\u4e0d\u820d\u3002\u91d1\u9010\u6d41\u7709\u5934 \u4e00\u76b1\uff0c\u8ba1\u4e0a\u5fc3\u6765\uff0c\u88c5\u4f5c\u662f\u7ed9\u5979\u7684\u6697\u5668\u6253\u7740\uff0c\u53eb\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u201d\u8dcc\u5012\u5730\u4e0a\u3002\u8463\u5341\u4e09\u5a18\u60f3\u8981\u6349\u7740 \u4ed6\u76d8\u95ee\u4ed6\u7684\u6765\u5386\uff0c\u89c1\u4ed6\u8dcc\u5012\uff0c\u6697\u5668\u4fbf\u4e0d\u518d\u53d1\u3002\u6325\u97ad\u62a4\u8eab\uff0c\u4e0a\u524d\u5bdf\u770b\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u5979\u8fc7\u53bb\u5bdf\u770b\u7684\u65f6\u5019\uff0c\u6325\u97ad\u62a4\u8eab\uff0c\u5df2\u662f\u6050\u9632\u6709\u8bc8\u7684\u4e86\uff0c\u4e0d\u6599 \u4ecd\u7136\u7740\u4e86\u91d1\u9010\u6d41\u7684\u9053\u513f\u3002\u5c31\u5728\u5979\u5c06\u5230\u7684\u65f6\u5019\uff0c\u91d1\u9010\u6d41\u84e6\u5730\u8dc3\u8d77\uff0c\u7b11\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c \u4f60\u4e5f\u5c1d\u5c1d\u6211\u7684\u593a\u547d\u795e\u7802\uff01\u201d\u8463\u5341\u4e09\u5a18\u662f\u4f7f\u60ef\u6bd2\u836f\u6697\u5668\u7684\u9ad8\u624b\uff0c\u542c\u5f97\u201c\u593a\u547d\u795e\u7802\u201d\u56db\u5b57\uff0c\u5927\u5403 \u4e00\u60ca\uff0c\u8fde\u5fd9\u715e\u7740\u8eab\u5f62\u3002 \u3000\u3000\u4e00\u7247\u7070\u8499\u8499\u5e26\u7740\u6697\u9ec4\u8272\u7684\u5c18\u6c99\u8fce\u9762\u6492\u6765\uff0c\u9976\u662f\u8463\u5341\u4e09\u5a18\u7684\u8f6f\u97ad\u4f7f\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u4e5f\u662f\u96be\u4ee5 \u906e\u62e6\uff0c\u8463\u5341\u4e09\u5a18\u8fde\u5fd9\u95ed\u4e86\u53cc\u76ee\uff0c\u540c\u65f6\u6325\u8896\u62a4\u7740\u9762\u95e8\uff0c\u9976\u662f\u5982\u6b64\uff0c\u4e5f\u7740\u4e86\u51e0\u9897\u7802\u5b50\u3002\u5706\u6d77\u7740\u7684 \u66f4\u591a\uff0c\u8fde\u5149\u5934\u4e0a\u4e5f\u88ab\u6d12\u4e86\u4e00\u628a\uff0c\u706b\u8fa3\u8fa3\u7684\u4f5c\u75db\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u91d1\u9010\u6d41\u5df2\u7ecf\u98de\u63a0\u8fc7\u4e24\u5ea7\u5047\u5c71\uff0c\u8eb2\u8fdb\u4e86\u4e00\u5904\u82b1\u6811\u4e1b\u4e2d\uff0c\u5f85\u5230\u8463\u5341\u4e09\u5a18\u5f20 \u5f00\u773c\u775b\uff0c\u5df2\u662f\u770b\u4e0d\u89c1\u91d1\u9010\u6d41\u7684\u5f71\u5b50\u3002 \u3000\u3000\u5706\u6d77\u987e\u4e0d\u5f97\u5bfb\u89c5\u654c\u8e2a\uff0c\u6478\u7740\u5149\u5934\uff0c\u60f4\u60f4\u4e0d\u5b89\u5730\u8fde\u5fd9\u95ee\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u56db\u59b9\uff0c\u8fd9\u593a\u547d\u795e\u7802 \u662f\u54ea\u4e00\u95e8\u7684\u6697\u5668\uff0c\u6bd2\u6027\u5982\u4f55\uff1f\u4f60\u53ef\u77e5\u9053\u89e3\u6cd5\u4e48\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u521d\u65f6\u542c\u5f97\u201c\u593a\u547d\u795e\u7802\u201d\u56db\u5b57\uff0c\u4e5f\u4ee5\u4e3a\u8fd9\u5fc5\u662f\u4e00\u79cd\u6bd2\u7802\uff0c\u4f46\u5979\u6bd5\u7adf\u662f\u4f7f\u6bd2\u7684\u5927\u884c \u5bb6\uff0c\u201c\u6bd2\u7802\u201d\u7740\u4f53\u4e4b\u540e\uff0c\u53d1\u89c9\u5e76\u65e0\u5f02\u72b6\uff0c\u8fd9\u624d\u77e5\u9053\u662f\u53d7\u4e86\u91d1\u9010\u6d41\u4e4b\u9a97\u3002\u539f\u6765\u91d1\u9010\u6d41\u662f\u968f\u624b\u5728 \u5730\u4e0a\u6293\u8d77\u4e00\u628a\u6ce5\u6c99\uff0c\u5411\u4ed6\u4eec\u6492\u53bb\u7684\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u9a82\u9053\uff1a\u201c\u597d\u4e2a\u5c0f\u8d3c\uff0c\u7adf\u6562\u9a97\u4f60\u8001\u5a18\uff01\u54fc\uff0c\u4ec0\u4e48\u593a\u547d\u795e\u7802\uff0c\u5f85\u8001\u5a18\u6293\u7740\u4e86\u4f60\uff0c\u8fd9 \u624d\u771f\u662f\u8981\u593a\u4f60\u7684\u547d\uff01\u201d\u5706\u6d77\u542c\u5979\u8fd9\u4e48\u4e00\u9a82\uff0c\u77e5\u9053\u4e0d\u662f\u6bd2\u7802\uff0c\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684\u77f3\u5934\uff0c\u6068\u6068\u8bf4\u9053\uff1a \u201c\u8fd9\u5c0f\u5b50\u592a\u53ef\u6076\u4e86\uff0c\u6349\u7740\u4e86\u4ed6\uff0c\u8ba9\u6211\u6765\u62bd\u4ed6\u7684\u7b4b\uff0c\u5265\u4ed6\u7684\u76ae\u3002\u53ea\u662f\u8981\u4ed6\u7684\u6027\u547d\u8fd8\u662f\u592a\u4fbf\u5b9c\u4ed6 \u4e86\u3002\u201d\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u4ed6\u8fd8\u672a\u8d70\u51fa\u8fd9\u4e2a\u56ed\u5b50\uff0c\u4f60\u8ddf\u6211\u6765\uff0c\u5411\u8fd9\u8fb9\u5bfb\u627e\uff0c\u201d\u8463\u5341\u4e09\u5a18\u957f\u4e8e\u542c\u58f0 \u8fa8\u8ff9\u4e4b\u672f\uff0c\u521a\u624d\u867d\u7136\u95ed\u4e86\u53cc\u76ee\uff0c\u4e5f\u542c\u5f97\u51fa\u91d1\u9010\u6d41\u9003\u8d70\u7684\u65b9\u5411\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u6697\u7b11\uff1a\u201c\u6211\u82e5\u662f\u8981\u9003\u8d70\uff0c\u65e9\u5c31\u8d70\u51fa\u4e86\u8fd9\u4e2a\u56ed\u5b50\u4e86\u3002\u201d\u539f\u6765\u4ed6\u8fd8\u5ff5\u5ff5\u4e0d\u5fd8\u4e8e\u8981 \u7a83\u53d6\u90a3\u5757\u7384\u94c1\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8eb2\u5728\u82b1\u6811\u4e1b\u4e2d\uff0c\u6298\u4e0b\u4e00\u652f\u5c0f\u5c0f\u7684\u6811\u679d\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u6811\u679d\u98de\u51fa\uff0c\u6781\u4f3c\u591c\u884c\u4eba\u7684\u8863\u895f \u5e26\u98ce\u4e4b\u58f0\uff0c\u8463\u5341\u4e09\u5a18\u559d\u9053\uff1a\u201c\u5f80\u54ea\u91cc\u8dd1\uff1f\u201d\u7acb\u5373\u4fbf\u5411\u90a3\u6811\u679d\u6240\u843d\u4e4b\u5904\u5954\u53bb\uff0c\u5374\u4e0d\u6599\u53c8\u4e2d\u4e86\u91d1 \u9010\u6d41\u7684\u201c\u8c03\u864e\u79bb\u5c71\u201d\u4e4b\u8ba1\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5f85\u4ed6\u4eec\u5230\u4e86\u524d\u9762\uff0c\u4fbf\u6084\u6084\u5730\u4ece\u82b1\u6811\u4e1b\u4e2d\u51fa\u6765\uff0c\u4ed6\u7684\u8f7b\u529f\u51fa\u795e\u5165\u5316\uff0c\u6bd4\u8463\u5341\u4e09\u5a18\u8fd8 \u9ad8\u660e\u5f97\u591a\uff0c\u9ed1\u591c\u4e2d\u501f\u7269\u969c\u5f62\uff0c\u51e0\u4e2a\u8d77\u4f0f\uff0c\u4fbf\u5230\u4e86\u4ed6\u4eec\u6240\u4f4f\u7684\u90a3\u5ea7\u623f\u5b50\uff0c\u8463\u5341\u4e09\u5a18\u53ea\u6ce8\u610f\u5728\u524d \u9762\u641c\u7d22\uff0c\u7adf\u6ca1\u5bdf\u89c9\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8fdb\u4e86\u8463\u5341\u4e09\u5a18\u7684\u623f\u95f4\uff0c\u5374\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u90a3\u4e2a\u85cf\u7740\u7384\u94c1\u7684\u7ea2\u6f06\u5323\u5b50\u5df2\u4e0d\u89c1\u4e86\uff01 \u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\u3002\u201d\u6211\u660e\u660e\u770b\u89c1\u662f\u653e\u5728\u684c\u4e0a\u7684\uff0c\u600e\u7684\u8f6c\u773c\u5c31\u4e0d\u89c1\u4e86\uff1f\u8fd9\u4e48\u770b\u6765\uff0c\u5f53\u771f\u662f\u53e6\u6709 \u9ad8\u4eba\u4e86\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u87b3\u8782\u6355\u8749\uff0c\u9ec4\u96c0\u5728\u540e\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u5341\u56de\u3000\u5f02\u5b9d\u8f7b\u629b\u771f\u4fa0\u58eb\u3000\u8352\u6797\u8c08\u7b11\u6551\u4f73\u4eba \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u5341\u56de\u3000\u5f02\u5b9d\u8f7b\u629b\u771f\u4fa0\u58eb\u3000\u8352\u6797\u8c08\u7b11\u6551\u4f73\u4eba \u3000\u3000\u91d1\u9010\u6d41\u53d1\u73b0\u90a3\u6446\u5728\u684c\u5b50\u7684\u7ea2\u6f06\u5323\u5b50\u5df2\u7ecf\u4e0d\u89c1\uff0c\u5927\u4e3a\u5931\u671b\u3002\u4f46\u4ed6\u8fd8\u4e0d\u80af\u6b7b\u5fc3\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u90a3\u5757\u7384\u94c1\u6709\u767e\u591a\u65a4\u91cd\uff0c\u6765\u4eba\u7eb5\u662f\u9ad8\u624b\uff0c\u4e5f\u7edd\u4e0d\u80fd\u8f7b\u6613\u5e26\u8d70\uff0c\u7adf\u4e0d\u9732\u4e1d\u6beb\u58f0\u606f\u3002\u201d\u5e0c\u671b\u8fd9\u4e2a \u7ea2\u6f06\u5323\u5b50\u4ecd\u662f\u85cf\u5728\u623f\u4e2d\uff0c\u4e8e\u662f\u9042\u64e6\u71c3\u706b\u77f3\uff0c\u4ed4\u7ec6\u4e00\u770b\uff0c\u623f\u4e2d\u6446\u8bbe\u4e00\u76ee\u4e86\u7136\uff0c\u90a3\u4e2a\u7ea2\u6f06\u5323\u5b50\u679c \u7136\u662f\u7ed9\u4eba\u5077\u8d70\u4e86\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u641c\u4e0d\u7740\u91d1\u9010\u6d41\uff0c\u84e6\u7136\u60ca\u89c9\uff0c\u56de\u5934\u4e00\u671b\uff0c\u6070\u5de7\u91d1\u9010\u6d41\u6b63\u5728\u64e6\u71c3\u706b\u77f3\uff0c\u8463\u5341\u4e09\u5a18\u89c1 \u706b\u5149\u4e00\u95ea\u5373\u706d\uff0c\u51ed\u7740\u5979\u7684\u7ecf\u9a8c\uff0c\u5f53\u7136\u77e5\u9053\u91d1\u9010\u6d41\u662f\u5728\u4f5c\u4ec0\u4e48\u4e86\u3002 \u3000\u3000\u8463\u5341\u4e8c\u5a18\u5931\u58f0\u53eb\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u54b1\u4eec\u4e2d\u4e86\u8fd9\u5c0f\u5b50\u8c03\u864e\u79bb\u5c71\u4e4b\u8ba1\uff01\u201d\u60f3\u4e0d\u5230\u7684\u662f\u51e0\u4e4e\u5728\u540c\u4e00 \u65f6\u5019\uff0c\u5706\u6d77\u4e5f\u5728\u5927\u58f0\u53eb\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u5f80\u54ea\u91cc\u8dd1\uff01\u56db\u59b9\u5feb\u8ffd\u5440\uff0c\u8fd9\u5c0f\u5b50\u51fa\u4e86\u56ed\u5b50\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e00\u8dc3\u800c\u51fa\uff0c\u6708\u8272\u6726\u80e7\u4e4b\u4e0b\uff0c\u53ea\u89c1\u4e00\u6761\u9ed1\u5f71\u521a\u521a\u98de\u8fc7\u56f4\u5899\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u548c\u5706\u6d77\u540c\u65f6\u53d1\u73b0\u201c\u654c\u8e2a\u201d\uff0c\u65b9\u5411\u6070\u597d\u76f8\u53cd\uff0c\u8463\u5341\u4e09\u5a18\u8d76\u56de\u6765\uff0c\u5706\u6d77\u5219\u8981\u8dd1\u51fa \u53bb\uff0c\u8463\u5341\u4e09\u5a18\u53eb\u9053\uff1a\u201c\u5feb\u56de\u6765\uff0c\u8fd9\u5c0f\u5b50\u5728\u54b1\u4eec\u623f\u91cc\uff01\u201d\u5706\u6d77\u53eb\u9053\uff1a\u201c\u4e0d\uff0c\u8fd9\u5c0f\u5b50\u5df2\u7ecf\u51fa\u53bb \u4e86\uff01\u201d\u4e24\u4eba\u4e0d\u7ea6\u800c\u540c\u7684\u56de\u8fc7\u4e86\u5934\uff0c\u90a3\u6761\u9ed1\u5f71\u5df2\u7ecf\u98de\u8fc7\u56f4\u5899\uff0c\u8463\u5341\u4e09\u5a18\u6ca1\u6709\u89c1\u7740\u3002\u4f46\u5706\u6d77\u5219\u89c1 \u7740\u4e86\u521a\u521a\u4ece\u623f\u4e2d\u8df3\u51fa\u6765\u7684\u91d1\u9010\u6d41\u3002 \u3000\u3000\u5706\u6d77\u6014\u4e86\u4e00\u6014\u4e86\uff0c\u6b62\u4e86\u811a\u6b65\u3002\u8463\u5341\u5a18\u5219\u662f\u811a\u6b65\u4e0d\u505c\uff0c\u8fb9\u8dd1\u8fb9\u53eb\uff1a\u201c\u8fd8\u4e0d\u5feb\u6765\uff0c\u8fd9\u5c0f\u5b50\u8981 \u5077\u54b1\u4eec\u7684\u5b9d\u8d1d\uff01\u201d\u5706\u6d77\u84e6\u5730\u7701\u89c9\uff0c\u4e5f\u5728\u53eb\u9053\uff1a\u201c\u4e0d\uff0c\u8fd9\u5c0f\u5b50\u53cc\u624b\u7a7a\u7a7a\uff0c\u7384\u94c1\u82e5\u88ab\u5077\u8d70\uff0c\u4e00\u5b9a \u662f\u5728\u5916\u9762\u7684\u90a3\u4e2a\u5c0f\u5b50\u8eab\u4e0a\uff01\u201d \u3000\u3000\u7384\u94c1\u662f\u5426\u88ab\u5077\uff0c\u8463\u5341\u4e09\u5a18\u5e76\u4e0d\u77e5\u9053\uff0c\u4f46\u60f3\u4e00\u60f3\u5706\u6d77\u8bf4\u7684\u8bdd\u5374\u6709\u9053\u7406\uff0c\u91d1\u9010\u6d41\u65e2\u7136\u662f\u624b\u4e2d \u65e0\u7269\uff0c\u7384\u94c1\u5018\u82e5\u88ab\u5077\uff0c\u5f53\u7136\u662f\u5728\u53e6\u4e00\u4e2a\u4eba\u7684\u8eab\u4e0a\u3002\u4f46\u5047\u5982\u7384\u94c1\u5c1a\u672a\u5931\u53bb\uff0c\u8463\u5341\u4e09\u5a18\u53bb\u8ffd\u53e6\u4e00 \u4e2a\u4eba\uff0c\u90a3\u5c31\u662f\u6b63\u597d\u7ed9\u4e86\u91d1\u9010\u6d41\u4ee5\u4e0b\u624b\u7684\u673a\u4f1a\u4e86\u3002\u8463\u5341\u4e09\u5a18\u8e0c\u8e87\u672a\u51b3\uff0c\u4e0d\u77e5\u662f\u8ffd\u54ea\u4e00\u4e2a\u597d\u3002 \u3000\u3000\u5706\u6d77\u4e0e\u8463\u5341\u4e09\u5a18\u672a\u66fe\u4f1a\u5408\uff0c\u7ed9\u4e86\u91d1\u9010\u6d41\u4e00\u4e2a\u5404\u4e2a\u51fb\u7834\u7684\u673a\u4f1a\u3002\u91d1\u9010\u6d41\u5728\u5730\u4e0a\u4e00\u6293\uff0c\u628a\u624b \u4e00\u626c\uff0c\u7b11\u9053\uff1a\u201c\u6211\u7684\u593a\u547d\u795e\u7802\u53c8\u6765\u5566\uff01\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u4e0a\u8fc7\u4e00\u6b21\u5f53\u7109\u80fd\u518d\u4e0a\uff0c\u7834\u53e3\u9a82\u9053\uff1a\u201c\u6df7\u5e10\u5c0f\u5b50\uff0c\u8fd8\u60f3\u6050\u9a97\u8001\u5a18\uff1f\u201d\u4e0d\u6599\u5ffd\u542c\u5f97 \u55e4\u55e4\u58f0\u54cd\uff0c\u91d1\u9010\u6d41\u6253\u6765\u7684\u4e0d\u662f\u6ce5\u6c99\uff0c\u800c\u662f\u77f3\u5b50\u3002\u539f\u6765\u8fd9\u6b21\u91d1\u9010\u6d41\u6293\u8d77\u7684\u662f\u4e00\u5757\u77f3\u5934\uff0c\u634d\u788e\u4e86 \u5f53\u4f5c\u94c1\u83b2\u5b50\u6253\u51fa\u53bb\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7528\u7684\u662f\u4e0a\u4e58\u5185\u529f\u201c\u5f39\u6307\u795e\u901a\u201d\u7684\u529f\u592b\uff0c\u788e\u77f3\u53d1\u51fa\uff0c\u80dc\u4f3c\u94c5\u5f39\uff0c\u800c\u4e14\u4ed6\u5728\u9ed1\u591c\u4e4b \u4e2d\uff0c\u8ba4\u7a74\u4e0d\u5dee\u6beb\u5398\uff0c\u6240\u6253\u7684\u90fd\u662f\u4eba\u8eab\u5927\u7a74\u3002 \u3000\u3000\u77f3\u5b50\u4e0d\u6bd4\u6ce5\u7802\uff0c\u7ed9\u6253\u7740\u4e86\u7a74\u9053\u53ef\u4e0d\u662f\u5f53\u800d\u7684\u3002\u8463\u5341\u4e09\u5a18\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u542c\u98ce\u8fa8\u5668\uff0c\u77e5 \u9053\u4e0d\u80fd\u786c\u63a5\uff0c\u53ea\u597d\u53c8\u4f7f\u5f00\u4e86\u62e8\u98ce\u97ad\u6cd5\u9632\u8eab\u3002\u6cfc\u98ce\u97ad\u6cd5\u906e\u62e6\u4e0d\u4e86\u4e00\u5927\u7247\u6492\u6765\u7684\u6ce5\u6c99\uff0c\u4f46\u5341\u9897\u516b \u9897\u788e\u77f3\u6253\u6765\uff0c\u5219\u662f\u53ef\u4ee5\u9632\u62a4\u7684\u3002\u4e0d\u8fc7\u4e5f\u5df2\u6253\u5f97\u5979\u624b\u5fd9\u811a\u4e71\u4e86\uff01\u8463\u5341\u4e09\u5a18\u8fd8\u53ef\u4ee5\u5e94\u4ed8\uff0c\u5706\u6d77\u5374 \u662f\u5e94\u4ed8\u4e0d\u4e86\u3002\u91d1\u9010\u6d41\u53cc\u624b\u9f50\u626c\uff0c\u5de6\u638c\u638c\u5fc3\u6240\u6263\u7684\u788e\u77f3\uff0c\u540c\u6837\u7684\u4e5f\u662f\u4ee5\u5929\u5973\u6563\u82b1\u7684\u624b\u6cd5\u5411\u5706\u6d77 \u6253\u53bb\u3002 \u3000\u3000\u5706\u6d77\u821e\u8d77\u6212\u5200\uff0c\u53ea\u542c\u5f97\u201c\u94db\u94db\u201d\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u4ed6\u7684\u8fd9\u8def\u5200\u6cd5\u672c\u6765\u4e5f\u9632\u5b88\u5f97\u76f8\u5f53\u4e25\u5bc6\uff0c \u4f46\u5374\u7981\u53d7\u4e0d\u8d77\u91d1\u9010\u6d41\u6253\u6765\u7684\u52b2\u9053\uff0c\u53ea\u6253\u843d\u4e86\u51e0\u9897\u77f3\u5b50\uff0c\u5df2\u662f\u611f\u5230\u864e\u53e3\u75bc\u9ebb\uff0c\u4e00\u4e2a\u758f\u795e\uff0c\u201c\u66f2 \u6c60\u7a74\u201d\u7740\u4e86\u4e00\u9897\u77f3\u5b50\uff0c\u6212\u5200\u5760\u5730\uff0c\u968f\u7740\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u4eba\u4e5f\u5012\u5730\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u8d3c\u79c3\u4e0d\u662f\u8bf4\u8981\u62bd\u6211\u7684\u7b4b\u5265\u6211\u7684\u76ae\u5417\uff1f\u563f\uff0c\u563f\uff0c\u8fd9\u53e5\u8bdd\u6211\u8bb0\u4e0b \u4e86\uff0c\u4e0b\u6b21\u5982\u6cd5\u70ae\u5236\uff0c\u8fd9\u6b21\u59d1\u4e14\u9976\u4f60\u4e00\u906d\u3002\u201d\u8463\u5341\u4e09\u5a18\u9a82\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6709\u80c6\u7684\u4f60\u522b\u8d70\uff01\u201d\u91d1 \u9010\u6d41\u7b11\u9053\uff1a\u201c\u6709\u80c6\u7684\u4f60\u8ffd\u6765\uff0c\u6211\u53ef\u6ca1\u6709\u5de5\u592b\u8d54\u4f60\uff0c\u90a3\u5757\u7384\u94c1\u6bd4\u4f60\u4ef7\u503c\u5f97\u591a\uff0c\u6211\u53ea\u60f3\u8981\u90a3\u5757\u7384 \u94c1\uff0c\u53ef\u4e0d\u60f3\u8981\u4f60\uff0c\u7559\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u54c8\uff0c\u4f60\u4e0d\u6562\u8ffd\u6765\uff0c\u90a3\u5c31\u8bf7\u6055\u6211\u4e0d\u5949\u966a\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5c06\u4ed6\u4eec\u8c03\u4f83\u4e00\u756a\uff0c\u5927\u7b11\u58f0\u4e2d\u98de\u51fa\u4e86\u56f4\u5899\uff0c\u8463\u5341\u4e09\u5a18\u6c14\u5f97\u67f3\u7709\u5012\u7ad6\uff0c\u7d27\u54ac\u94f6\u7259\uff0c\u4f46 \u53ea\u662f\u5979\u4e00\u4e2a\u4eba\uff0c\u5979\u53ef\u8fd8\u5f53\u771f\u4e0d\u6562\u53bb\u8ffd\u91d1\u9010\u6d41\uff0c\u800c\u4e14\u5706\u6d77\u7ed9\u6253\u7740\u4e86\u201c\u66f2\u6c60\u7a74\u201d\uff0c\u4e5f\u5fc5\u987b\u7acb\u5373\u89e3 \u6551\uff0c\u53ea\u597d\u773c\u7741\u7741\u7684\u770b\u7740\u91d1\u9010\u6d41\u8d70\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u51fa\u4e86\u5feb\u6d3b\u6797\uff0c\u90a3\u6761\u9ed1\u5f71\u5df2\u7ecf\u4e0d\u89c1\u3002\u5e78\u597d\u524d\u9762\u53ea\u6709\u4e00\u6761\u8def\uff0c\u91d1\u9010\u6d41\u4fbf\u8ffd\u4e0b\u53bb\uff0c\u8ffd\u4e86 \u4e00\u4f1a\uff0c\u679c\u7136\u89c1\u7740\u4e00\u4e2a\u4eba\u5728\u524d\u5934\u5954\u8dd1\uff0c\u624b\u4e0a\u63d0\u7684\u4e00\u4e2a\u5323\u5b50\u4e5f\u679c\u7136\u5c31\u662f\u5706\u6d77\u6240\u643a\u7684\u90a3\u4e2a\u7ea2\u6f06\u5323 \u5b50\u3002 \u3000\u3000\u8fd9\u4eba\u8eab\u6750\u7626\u5c0f\uff0c\u7a7f\u4e00\u4ef6\u9752\u5e03\u957f\u886b\uff0c\u4f3c\u4e4e\u4e0d\u5927\u79f0\u8eab\uff0c\u886b\u811a\u7740\u5730\uff0c\u65bd\u5c55\u8f7b\u529f\u5954\u8dd1\u4e4b\u65f6\u8fde\u811a\u8ddf \u90fd\u770b\u4e0d\u89c1\u3002\u5934\u4e0a\u6234\u7740\u4e00\u9876\u98ce\u5e3d\uff0c\u906e\u8fc7\u4e86\u8033\u6735\uff0c\u4f3c\u662f\u79c0\u624d\u7684\u88c5\u675f\uff0c\u5374\u6253\u626e\u5f97\u4e0d\u4f26\u4e0d\u7c7b\u3002\u4e0d\u8fc7\u8f7b \u529f\u5374\u771f\u662f\u4e86\u5f97\uff0c\u7531\u4e8e\u4ed6\u7684\u957f\u886b\u592a\u957f\uff0c\u770b\u4e0d\u89c1\u4ed6\u5728\u4e3e\u6b65\uff0c\u6d51\u8eab\u9752\u8272\uff0c\u5c31\u50cf\u4e00\u68f5\u67f3\u6811\uff0c\u7ed9\u72c2\u98ce\u631f \u7740\u98de\u53bb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u9053\uff1a\u201c\u5e78\u4e8f\u8fd9\u4eba\u662f\u62ff\u7740\u4e00\u767e\u591a\u65a4\u7684\u7384\u94c1\u8d70\u8def\uff0c\u8981\u4e0d\u7136\u53ea\u6015\u6211\u7eb5\u80fd\u8ffd\u5f97\u4e0a\u4ed6\uff0c\u4e5f \u8981\u5927\u8d39\u4e00\u756a\u6c14\u529b\u4e86\u3002\u201d \u3000\u3000\u90a3\u4eba\u5df2\u7ecf\u53d1\u89c9\u80cc\u540e\u6709\u4eba\uff0c\u811a\u6b65\u52a0\u5feb\uff0c\u79bb\u5f00\u5927\u8def\uff0c\u60f3\u8981\u9003\u8fdb\u8def\u8fb9\u7684\u6797\u5b50\u3002\u91d1\u9010\u6d41\u7b11\u9053\uff1a \u201c\u670b\u53cb\uff0c\u522b\u614c\uff0c\u54b1\u4eec\u662f\u540c\u9053\uff01\u201d\u4ed6\u4e00\u51fa\u58f0\uff0c\u90a3\u4eba\u8dd1\u5f97\u66f4\u5feb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4f7f\u51fa\u201c\u516b\u6b65\u8d76\u8749\u201d\u7684\u8eab\u6cd5\uff0c\u51e0\u4e2a\u8d77\u4f0f\uff0c\u8ffd\u5230\u4e86\u90a3\u4eba\u80cc\u540e\uff0c\u7b11\u9053\uff1a\u201c\u9053\u4e0a\u7684\u670b\u53cb\uff0c \u6211\u7ed9\u4f60\u6253\u9000\u4e86\u8ffd\u5175\uff0c\u4f60\u4e0d\u8be5\u8c22\u8c22\u6211\u5417\uff1f\u4e0d\u8fc7\uff0c\u4f60\u53ef\u4e0d\u7528\u5403\u60ca\uff0c\u6211\u5e76\u975e\u6765\u6c42\u4f60\u5206\u810f\u7684\u3002\u201d \u3000\u3000\u90a3\u4eba\u77e5\u9053\u8dd1\u4e0d\u8fc7\u91d1\u9010\u6d41\uff0c\u53ea\u597d\u505c\u4e0b\u811a\u6b65\uff0c\u8f6c\u8fc7\u8eab\u6765\u3002\u53ea\u89c1\u4ed6\u8499\u7740\u4e00\u5757\u9ed1\u8272\u7684\u9762\u7eb1\uff0c\u6708\u8272 \u6726\u80e7\u4e4b\u4e0b\uff0c\u65e0\u6cd5\u900f\u89c6\u4ed6\u7684\u5e90\u5c71\u771f\u8c8c\u3002\u90a3\u4eba\u505c\u4e0b\u4e86\u811a\u6b65\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u9053\u4e0a\u7684\u670b\u53cb\uff1f\u4f60\u5230 \u5e95\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8fd9\u4eba\u634f\u7740\u55d3\u5b50\u8bf4\u8bdd\u3001\u800c\u4e14\u6545\u610f\u8bf4\u5f97\u7c97\u91cc\u7c97\u6c14\u3002\u91d1\u9010\u6d41\u867d\u7136\u4e0d\u662f\u8001\u6c5f\u6e56\uff0c\u4e5f\u61c2\u5f97\u4ed6\u8fd9\u6837\u7684 \u505a\u4f5c\u662f\u4e0d\u60f3\u9732\u51fa\u539f\u6765\u7684\u53e3\u97f3\uff0c\u4f46\u4ed6\u867d\u7136\u7528\u7684\u662f\u5047\u55d3\u5b50\uff0c\u800c\u53c8\u8bf4\u5f97\u7c97\u91cc\u7c97\u6c14\uff0c\u5374\u4ecd\u7136\u63a9\u9970\u4e0d\u4e86 \u4ed6\u90a3\u672c\u6765\u6e05\u8106\u7684\u97f3\u8272\uff0c\u5e76\u4e0d\u4ee4\u4eba\u611f\u89c9\u96be\u542c\u3002 \u3000\u3000\u91d1\u9010\u6d41\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4eba\u4e3a\u4ec0\u4e48\u628a\u81ea\u5df1\u6545\u610f\u5f04\u5f97\u8fd9\u6837\u795e\u79d8\uff0c\u771f\u9762\u5374\u4e0d\u80af\u793a\u4eba\uff0c \u8fde\u53e3\u97f3\u4e5f\u8981\u5f04\u5047\uff1f\u201d\u4e8e\u662f\u54c8\u54c8\u4e00\u7b11\uff1b\u201c\u6307\u7740\u4ed6\u90a3\u7ea2\u6f06\u5323\u5b50\u9053\uff1a\u201c\u771f\u4eba\u9762\u524d\uff0c\u8fd8\u8bf4\u4ec0\u4e48\u5047\u8bdd\uff1f \u4f60\u662f\u5e72\u4ec0\u4e48\u7684\uff0c\u6211\u4e5f\u5c31\u662f\u5e72\u4ec0\u4e48\u7684\u3002\u54c8\u54c8\uff0c\u54b1\u4eec\u8fd8\u4e0d\u662f\u540c\u9053\u7684\u670b\u53cb\u5417\uff1f\u201d \u3000\u3000\u90a3\u4eba\u4f3c\u4e4e\u4e5f\u662f\u5446\u4e86\u4e00\u5446\uff0c\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u7f69\u7740\u9762\u7eb1\uff0c\u91d1\u9010\u6d41\u770b\u4e0d\u89c1\u4ed6\u9762\u90e8\u7684\u8868\u60c5\u3002\u534a\u664c\uff0c\u90a3 \u4eba\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u5df2\u7ecf\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u4e1c\u897f\u4e86\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5f53\u7136\uff01\u5426\u5219\u82e5\u662f\u5bfb\u5e38\u7684\u73e0\u5b9d\uff0c\u4e5f\u4e0d\u503c\u5f97\u6211\u8f6c\u5b83\u7684\u5ff5\u5934\u4e86\u3002\u563f\uff0c\u563f\uff0c\u60f3\u4e0d\u5230 \u6211\u62fc\u547d\u7f20\u7740\u8463\u5341\u4e09\u5a18\uff0c\u5374\u7ed9\u4f60\u68c0\u4e86\u4e2a\u5929\u5927\u7684\u4fbf\u5b9c\uff01\u201d \u3000\u3000\u90a3\u4eba\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u662f\u4e00\u5b9a\u60f3\u8981\u8fd9\u5757\u7384\u94c1\u7684\u4e86\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u672c\u6765\u662f\u60f3\u8981\u5b83\u3002\u4e0d\u8fc7\uff0c\u73b0\u5728\u65e2\u7136\u662f\u7ed9\u4f60\u62a2\u5148\u4e00\u6b65\uff0c\u6211\u4e5f\u53ea\u597d\u81ea \u8ba4\u6666\u6c14\u4e86\u3002\u54b1\u4eec\u65e2\u7136\u662f\u9053\u4e0a\u7684\u670b\u53cb\uff0c\u9ed1\u5403\u9ed1\u7684\u4e8b\u60c5\u6211\u662f\u4e0d\u597d\u610f\u601d\u5e72\u7684\u3002\u201d \u3000\u3000\u90a3\u4eba\u542c\u4e86\uff0c\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u8f6c\u5934\u5c31\u8d70\u3002\u91d1\u9010\u6d41\u8ffd\u4e0a\u524d\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5582\uff01\u4f60\u8fd9\u4eba\u600e\u7684\u5982\u6b64 \u4e0d\u901a\u60c5\u7406\uff1f\u6211\u867d\u7136\u4e0d\u8981\u4f60\u5206\u8d43\uff0c\u4f60\u4e5f\u603b\u5f97\u591a\u8c22\u6211\u4e00\u58f0\u5427\uff01\u201d \u3000\u3000\u90a3\u4eba\u8bf4\u9053\u3002\u201d\u8001\u5b9e\u544a\u8bc9\u4f60\u5427\uff0c\u8fd9\u5757\u7384\u94c1\u672c\u6765\u2026\u2026\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u672c\u6765\u600e\u4e48\u6837\uff1f\u4e3a\u4ec0\u4e48\u4e0d \u8bf4\u4e0b\u53bb\uff1f\u201d\u90a3\u4eba\u987f\u4e86\u4e00\u987f\uff0c\u8fd9\u624d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u672c\u6765\u5c31\uff0c\u5c31\u662f\u6211\u2026\u2026\u201d\u91d1\u9010\u6d41\u51b7\u7b11\u9053\uff1a\u201c\u672c\u6765 \u5c31\u662f\u4f60\u7684\u4e1c\u897f\uff1f\u54c8\u54c8\uff0c\u597d\u5728\u6211\u5df2\u7ecf\u77e5\u9053\u8fd9\u5757\u7384\u94c1\u7684\u6765\u5386\uff0c\u4f60\u60f3\u9a97\u6211\uff0c\u90a3\u53ef\u4e0d\u6210\uff01\u201d\u90a3\u4eba\u8bf4 \u9053\uff1a\u201c\u4f60\u6025\u4ec0\u4e48\uff0c\u6211\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u5462\uff0c\u4f60\u5c31\u65ad\u7ae0\u53d6\u4e49\u4e86\uff0c\u6211\u662f\u8bf4\uff0c\u8fd9\u5757\u7384\u94c1\u672c\u6765\u5c31\u662f\u6211\u770b\u4e2d\u7684 \u4e1c\u897f\uff0c\u6211\u662f\u975e\u8981\u4e0d\u53ef\u7684\uff0c\u4e0d\u8fc7\uff0c\u4eca\u665a\u4f60\u65e2\u7136\u662f\u65e0\u610f\u4e2d\u5e2e\u4e86\u4e00\u4e2a\u5fd9\uff0c\u4f60\u4e00\u5b9a\u8981\u6211\u591a\u8c22\uff0c\u6211\u5c31\u591a \u8c22\u4f60\u4e00\u58f0\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u4e3a\u4e0d\u6ee1\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u591a\u8c22\u4e5f\u591a\u8c22\u5f97\u8fd9\u6837\u52c9\u5f3a\uff0c\u771f\u662f\u5c82\u6709\u6b64\u7406\uff01\u201d\u4e00\u6012\u4e4b\u4e0b\uff0c \u53c8\u8ffd\u4e0a\u524d\u53bb\uff0c\u90a3\u4eba\u9053\uff1a\u201c\u600e\u4e48\uff0c\u6211\u5df2\u7ecf\u591a\u8c22\u8fc7\u4e86\uff0c\u4f60\u53c8\u8bf4\u8fc7\u4e0d\u60f3\u5206\u4e00\u8d43\uff0c\u90a3\u4f60\u8fd8\u8ffd\u6211\u5e72 \u561b\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u672c\u6765\u662f\u60f3\u8d23\u5907\u4ed6\u7684\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u4f55\u5fc5\u8fd9\u6837\u5c0f\u6c14\uff0c\u4e8e\u662f\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u6709\u544a\u8bc9\u6211 \u4f60\u662f\u4ec0\u4e48\u4eba\u5462\uff0c\u54b1\u4eec\u65e2\u662f\u540c\u9053\u4e2d\u4eba\uff0c\u603b\u53ef\u4ee5\u4ea4\u4e2a\u670b\u53cb\u5427\uff1f\u201d \u3000\u3000\u90a3\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u771f\u662f\u7f57\u54e9\u7f57\u54c6\u7684\uff0c\u6211\u4e0d\u559c\u6b22\u4ea4\u4f60\u8fd9\u6837\u4e00\u4e2a\u559c\u6b22\u67e5\u6839\u95ee\u5e95\u7684\u670b \u53cb\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u78b0\u4e86\u4e00\u4e2a\u5927\u9489\u5b50\uff0c\u66f4\u662f\u4e0d\u80af\u7f62\u4f11\uff0c\u5982\u5f71\u968f\u5f62\u7684\u53c8\u8ffd\u4e0b\u53bb\u3002 \u3000\u3000\u90a3\u4eba\u84e6\u5730\u6b62\u6b65\uff0c\u6012\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u60f3\u600e\u6837\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u4e0d\u95ee\u4f60\u7684\u6765\u5386\u4e86\u3002\u4f46\u4f60\u53ef\u77e5\u9053\u8fd9\u5757\u7384\u94c1\u7684\u6765\u5386\uff1f\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u77e5\u9053\u53c8\u600e\u6837\uff0c\u4e0d\u77e5\u9053\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u82e5\u7136\u77e5\u9053\uff01\u5c31\u5e94\u8be5\u8981\u6211\u4f5c\u4f34\u3002\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u5413\uff0c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u5757\u7384\u94c1\u662f\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\u53f2\u767d\u90b5\u5dee\u4eba\u9001\u8fdb\u4eac\u90fd\uff0c\u8981\u9001\u7ed9\u5927\u5185\u603b\u7ba1\u8428\u798f\u9f0e\u505a \u5bff\u793c\u7684\u3002\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u8fd9\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5982\u4eca\u843d\u5728\u4f60\u7684\u624b\u4e2d\uff0c\u53f2\u767d\u90b5\u5c82\u80af\u5e72\u4f11\uff0c\u5f53\u7136\u662f\u60f3\u62a2\u56de\u7684\u4e86\u3002\u516d\u5408\u5e2e\u9ad8\u624b\u5982 \u4e91\uff0c\u4e2a\u4e2a\u90fd\u662f\u65e0\u6076\u4e0d\u4f5c\u7684\u9b54\u5934\uff0c\u4f60\u62ff\u4e86\u8fd9\u5757\u7384\u94c1\uff0c\u53ea\u6015\u62ff\u7684\u4e0d\u662f\u5b9d\u8d1d\u800c\u662f\u7978\u6b83\u3002\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u54e6\uff0c\u6211\u660e\u767d\u4e86\u3002\u4f60\u662f\u60f3\u8981\u5446\u62a4\u6211\uff0c\u662f\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4f46\u6709\u6211\u548c\u4f60\u4f5c\u4f34\uff0c\u4e24\u4e2a\u4eba\u5e94\u4ed8\u654c\u4eba\uff0c\u603b\u6bd4\u4e00\u4e2a\u4eba\u597d\u4e9b\u3002\u201d \u3000\u3000\u90a3\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u597d\u5fc3\u3002\u4f46\u4f60\u6211\u65e0\u4eb2\u65e0\u6545\uff0c\u6211\u53c8\u6ca1\u6709\u597d\u5904\u7ed9\u4f60\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u4fdd\u62a4 \u6211\uff1f\u563f\uff0c\u563f\u3002\u53ea\u6015\u4f60\u8fd8\u662f\u5728\u89ca\u89ce\u8fd9\u5757\u7384\u94c1\u5427\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e00\u518d\u53d7\u4ed6\u595a\u843d\uff0c\u4e0d\u7531\u5f97\u52a8\u4e86\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u662f\u72d7\u54ac\u5415\u6d1e\u5bbe\uff0c\u4e0d\u8bc6\u5978\u4eba\u5fc3\u3002\u597d \u5440\uff0c\u6211\u672c\u6765\u4e0d\u60f3\u8981\u7684\uff0c\u4f60\u65e2\u7136\u8fd9\u6837\u8bf4\u6211\uff0c\u6211\u5012\u662f\u975e\u8981\u4e0d\u53ef\u4e86\u3002\u201d \u3000\u3000\u90a3\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u5982\u4f55\uff1f\u6bd5\u7adf\u9732\u51fa\u2018\u9985\u513f\u2019\u6765\u4e86\uff01\u597d\uff0c\u6709\u672c\u9886\u5c31\u62ff\u53bb\u5427\uff01\u201d\u84e6\u5730\u53cc\u81c2\u4e00 \u632f\uff0c\u628a\u90a3\u7ea2\u6f06\u5323\u5b50\u5411\u91d1\u9010\u6d41\u63b7\u6765\u3002 \u3000\u3000\u8fd9\u8499\u9762\u4eba\u5176\u5b9e\u662f\u5fc3\u91cc\u6000\u7591\u4e0d\u5b9a\uff0c\u6478\u4e0d\u6e05\u91d1\u9010\u6d41\u662f\u4ec0\u4e48\u8def\u9053\uff0c\u4e5f\u4e0d\u76f8\u4fe1\u91d1\u9010\u6d41\u80fd\u591f\u6253\u8d25\u8463 \u5341\u4e09\u5a18\u548c\u5706\u6d77\u4e8c\u4eba\uff0c\u5fc3\u60f3\uff1a\u201c\u53ea\u6015\u8fd9\u4eba\u8fd8\u662f\u8463\u5341\u4e09\u5a18\u4e32\u901a\u51fa\u6765\u7684\u3002\u90a3\u5996\u5987\u4e0d\u6562\u8ffd\u6765\uff0c\u5374\u53eb\u8fd9 \u5c0f\u5b50\u548c\u6211\u7ea0\u7f20\u3002\u54fc\uff0c\u4ed6\u53e3\u6c14\u8fd9\u4e48\u5927\uff0c\u5c45\u7136\u8bf4\u8981\u4fdd\u62a4\u6211\uff0c\u6211\u4e14\u8ba9\u4ed6\u5403\u5403\u82e6\u5934\u3002\u201d\u662f\u4ee5\u4ed6\u8fd9\u4e00 \u63b7\uff0c\u5b9e\u662f\u6709\u5fc3\u8bd5\u8bd5\u91d1\u9010\u6d41\u7684\u672c\u9886\uff0c\u770b\u4ed6\u63a5\u4e0d\u63a5\u5f97\u8d77\u3002 \u3000\u3000\u8fd9\u8499\u9762\u4eba\u8eab\u6750\u7626\u524a\uff0c\u4e00\u63b7\u4e4b\u529b\u5374\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u90a3\u5757\u7384\u94c1\u6709\u767e\u591a\u65a4\u91cd\uff0c\u4ed6\u8fd9\u4e48\u4f7f\u52b2\u4e00\u63b7\uff0c \u9690\u9690\u5e26\u7740\u98ce\u96f7\u4e4b\u58f0\uff0c\u5c31\u50cf\u6cf0\u5c71\u538b\u9876\u4f3c\u7684\u5411\u91d1\u9010\u6d41\u5f53\u5934\u538b\u4e0b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e0d\u52a8\u58f0\u8272\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u4f38\u624b\u4e00\u6293\uff0c\u5c31\u628a\u90a3\u7ea2\u6f06\u5323\u5b50\u6293\u5230\u624b\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u5f97\u9601\u4e0b \u8fd9\u6837\u6177\u6168\uff0c\u8fd9\u56de\u662f\u8f6e\u5230\u6211\u8981\u591a\u8c22\u4f60\u4e86\uff01\u201d \u3000\u3000\u8499\u9762\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u624d\u77e5\u9053\u91d1\u9010\u6d41\u529f\u529b\u5728\u4ed6\u4e4b\u4e0a\uff0c\u4f46\u4e5f\u4e0d\u80af\u7518\u4f11\uff0c\u91d1\u9010\u6d41\u4e00\u8d70\uff0c\u4ed6\u8f6c\u8eab \u5c31\u8ffd\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u548c\u6211\u4ea4\u670b\u53cb\uff0c\u6211\u4e5f\u4e0d\u60f3\u548c\u4f60\u4ea4\u670b\u53cb\u4e86\uff0c\u4f60\u8ffd\u6211\u505a\u4ec0\u4e48\uff1f\u201d\u90a3\u4eba\u559d \u9053\uff1a\u201c\u5323\u5b50\u653e\u4e0b\u6765\uff0c\u5c31\u8ba9\u4f60\u8d70\u3002\u201d\u91d1\u9010\u6d41\u5927\u7b11\u9053\uff1a\u201c\u4f60\u7ed9\u4e86\u6211\u53c8\u8981\u62ff\u56de\u53bb\uff0c\u54c8\u54c8\uff0c\u771f\u662f\u597d\u7b11 \u5440\u597d\u7b11\uff01\u201d \u3000\u3000\u90a3\u8499\u9762\u4eba\u559d\u9053\uff1a\u201c\u6211\u4e0d\u662f\u548c\u4f60\u5f00\u73a9\u7b11\u7684\uff0c\u5feb\u5feb\u653e\u4e0b\u3002\u201d\u4e00\u526f\u6c14\u6025\u8d25\u574f\u7684\u6a21\u6837\uff0c\u5306\u5306\u5fd9\u5fd9 \u5730\u8d76\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6709\u610f\u6c14\u4ed6\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u677f\u8d77\u9762\u5b54\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u662f\u548c\u4f60\u5f00\u73a9\u7b11\u7684\uff0c\u4f60\u4ee5\u4e3a\u6211 \u5403\u9971\u4e86\u996d\u6ca1\u4e8b\u4f5c\u5417\uff1f\u201d \u3000\u3000\u90a3\u8499\u9762\u4eba\u5389\u58f0\u559d\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u5c31\u522b\u602a\u6211\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6709\u672c\u9886\u4f60\u62a2\u56de\u53bb\u597d \u4e86\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u94f6\u5149\u707f\u70c2\uff0c\u4e00\u6761\u7f20\u7740\u94f6\u4e1d\u7684\u8f6f\u97ad\u5df2\u662f\u5411\u7740\u91d1\u9010\u6d41\u6253 \u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u9053\uff1a\u201c\u53c8\u662f\u4e00\u4e2a\u4fbf\u8f6f\u97ad\u7684\uff0c\u4e14\u770b\u4ed6\u7684\u97ad\u6cd5\u6bd4\u90a3\u8463\u5341\u4e09\u5a18\u5982\u4f55\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u90a3 \u6761\u8f6f\u97ad\u5df2\u7ecf\u6253\u5230\uff0c\u97ad\u52bf\u5929\u77eb\u5982\u9f99\uff0c\u4f3c\u5de6\u4f3c\u53f3\u4f3c\u4e2d\uff0c\u91d1\u9010\u6d41\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u4e00\u770b\u5c31\u77e5\u4ed6\u8fd9 \u4e00\u97ad\u662f\u540c\u65f6\u6253\u81ea\u5df1\u7684\u4e09\u5904\u7a74\u9053\u3002\u8f6f\u97ad\u6253\u7a74\u662f\u6781\u96be\u7ec3\u7684\u529f\u592b\uff0c\u91d1\u9010\u6d41\u4e5f\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\u3002\u4f46\u4ed6\u827a \u9ad8\u80c6\u5927\uff0c\u5374\u4e5f\u5e76\u4e0d\u754f\u60e7\u3002 \u3000\u3000\u773c\u770b\u8f6f\u97ad\u5c31\u8981\u6253\u7740\u4e86\u91d1\u9010\u6d41\u7684\u8eab\u5b50\uff0c\u91d1\u9010\u6d41\u4f7f\u51fa\u201c\u5929\u7f57\u6b65\u6cd5\u201d\uff0c\u6ef4\u6e9c\u6e9c\u7684\u8eab\u5f62\u4e00\u8f6c\uff0c\u8f6f \u97ad\u51e0\u4e4e\u662f\u8d34\u7740\u4ed6\u7684\u7435\u7436\u9aa8\u626b\u4e86\u8fc7\u53bb\u3002\u91d1\u9010\u6d41\u6b63\u4ee5\u4e3a\u53ef\u4ee5\u907f\u5f00\uff0c\u4e0d\u6599\u90a3\u4eba\u7684\u97ad\u6cd5\u5f53\u771f\u662f\u4f7f\u5f97\u7075 \u6d3b\u4e4b\u6781\uff0c\u97ad\u68a2\u4e00\u5f2f\uff0c\u957f\u86c7\u822c\u7684\u7a81\u7136\u6389\u8fc7\u5934\u6765\uff0c\u800c\u4e14\u97ad\u68a2\u4e0a\u8fd8\u7a81\u7136\u4f38\u51fa\u4e00\u652f\u4e09\u5bf8\u6765\u957f\u4e24\u9762\u5f00\u950b \u7684\u5315\u9996\uff0c\u7b49\u4e8e\u662f\u4e00\u652f\u77ed\u5251\uff0c\u6307\u5411\u91d1\u9010\u6d41\u80f8\u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\u3002 \u3000\u3000\u5728\u5bf9\u65b9\u8fd9\u6837\u7684\u602a\u62db\u7a81\u51fa\u4e4b\u4e0b\uff0c\u8fde\u201c\u5929\u7f57\u6b65\u6cd5\u201d\u90fd\u662f\u95ea\u907f\u4e0d\u5f00\u7684\u4e86\u3002\u597d\u4e2a\u91d1\u9010\u6d41\uff0c\u5728\u8fd9\u6027 \u547d\u4fc4\u9877\u4e4b\u9645\uff0c\u4f7f\u51fa\u4e86\u5353\u7edd\u7684\u6307\u6cd5\uff0c\u4e2d\u6307\u4e00\u5f39\uff0c\u6070\u597d\u5f39\u7740\u97ad\u68a2\uff0c\u90a3\u652f\u77ed\u5251\u6b6a\u8fc7\u4e00\u8fb9\uff0c\u5272\u88c2\u4e86\u4ed6 \u7684\u8863\u88f3\uff0c\u5374\u6ca1\u6709\u523a\u4f24\u4ed6\u7684\u76ae\u8089\uff0c\u4f46\u867d\u7136\u5982\u6b64\uff0c\u91d1\u9010\u6d41\u4ea6\u4e0d\u7531\u5f97\u5413\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8499\u9762\u4eba\u4e00\u4e2a\u201c\u56de\u98ce\u626b\u67f3\u201d\uff0c\u6025\u4e09\u97ad\u53c8\u626b\u8fc7\u6765\uff0c\u4ed6\u7684\u97ad\u68a2\u662f\u5d4c\u6709\u5229\u5668 \u7684\uff0c\u97ad\u6cd5\u4e4b\u4e2d\u8fd8\u85cf\u6709\u5200\u5251\u7684\u62db\u6570\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u9053\uff1a\u201c\u597d\u72e0\u7684\u5bb6\u4f19\uff0c\u6211\u4e5f\u5f97\u53eb\u4ed6\u77e5\u9053\u4e00\u70b9\u5389\u5bb3\u3002\u201d\u62d4\u5251\u51fa\u9798\uff0c\u524a\u4ed6\u97ad\u68a2\u4e0a\u4f38\u51fa \u7684\u90a3\u652f\u77ed\u5251\u3002\u91d1\u9010\u6d41\u7528\u7684\u662f\u8ffd\u98ce\u5251\u5f0f\u3002\u5251\u6cd5\u5947\u5feb\uff0c\u4e0d\u6599\u8fd9\u4eba\u7684\u97ad\u6cd5\u77eb\u82e5\u6e38\u9f99\uff0c\u91d1\u9010\u6d41\u8fde\u51fa\u516b \u62db\uff0c\u7adf\u7136\u6ca1\u6709\u524a\u7740\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5927\u5927\u5403\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u529f\u529b\u672a\u5fc5\u80dc\u5f97\u8fc7\u8463\u5341\u4e09\u5a18\uff0c\u4f46\u8fd9\u97ad\u6cd5\u5374\u662f\u5728\u8463\u5341\u4e09\u5a18 \u4e4b\u4e0a\uff0c\u6211\u4e00\u665a\u4e4b\u95f4\u8fde\u78b0\u4e24\u4e2a\u4f7f\u97ad\u7684\u597d\u624b\uff0c\u4e5f\u771f\u7b97\u5f97\u662f\u5947\u9047\u4e86\u3002\u201d \u3000\u3000\u8fd9\u8499\u9762\u4eba\u89c1\u4e86\u91d1\u9010\u6d41\u7684\u8fd9\u624b\u5251\u6cd5\u4e5f\u662f\u5403\u60ca\u4e0d\u5c0f\uff0c\u5fc3\u9053\u3002\u201d\u6b64\u4eba\u5251\u6cd5\u65e0\u61c8\u53ef\u51fb\uff0c\u529f\u529b\u53c8\u5728 \u6211\u4e4b\u4e0a\u3002\u82e5\u7136\u4ed6\u653e\u4e0b\u7384\u94c1\uff0c\u6211\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u662f\u526f\u5014\u5f3a\u7684\u813e\u6c14\uff0c\u7531\u4e8e\u8fd9\u4eba\u66fe\u559d\u4ed6\u653e\u4e0b\u7384\u94c1\uff0c\u4ed6\u4e0d\u80af\u4f9d\u4ece\uff0c\u6b64\u65f6\u82e5\u7136\u653e\u4e0b\uff0c\u62db\u6570 \u4e0d\u8f93\uff0c\u9762\u5b50\u5374\u8981\u8f93\u4e86\u3002\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u5b81\u53ef\u6211\u6253\u8d62\u4e86\u4f60\uff0c\u518d\u5c06\u7384\u94c1\u9001\u8fd8\u3002\u5374\u4e0d\u80fd\u88ab\u8feb\u653e \u4e0b\u3002\u201d\u91d1\u9010\u6d41\u4e3a\u4e86\u4e89\u8fd9\u53e3\u6c14\uff0c\u53ef\u5c31\u4e0d\u514d\u8981\u5403\u4e8f\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u63d0\u7740\u767e\u591a\u65a4\u91cd\u7684\u7384\u94c1\uff0c\u8f7b\u529f\u591a\u5c11\u53d7\u4e86\u5f71\u54cd\uff0c\u867d\u4e0d\u81f3\u4e8e\u7eb5\u8dc3\u4e0d\u7075\uff0c\u4e5f\u662f\u7a0d\u611f\u5403\u529b \u4e86\uff0c\u97ad\u957f\u5251\u77ed\uff0c\u91d1\u9010\u6d41\u7684\u8f7b\u529f\u4e00\u53d7\u5f71\u54cd\uff0c\u5c31\u53ea\u6709\u53d7\u653b\u7684\u4efd\u513f\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u8fde\u53d7\u653b\u4e86\u5341\u4e03\u516b\u62db\uff0c\u6c14\u95f7\u4e0d\u8fc7\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u4e14\u548c\u4f60\u89c1\u4e2a\u771f\u7ae0\uff01\u201d\u7384\u529f\u9ed8\u8fd0\uff0c \u529b\u8d2f\u5251\u5c16\uff0c\u957f\u5251\u4e00\u6296\uff0c\u55e1\u55e1\u4f5c\u54cd\uff0c\u8499\u9762\u4eba\u7684\u8f6f\u97ad\u6253\u5230\u4ed6\u7684\u8eab\u524d\u4e09\u5c3a\uff0c\u5df2\u662f\u7ed9\u4ed6\u7684\u5251\u98ce\u8361\u5f00\u3002 \u91d1\u9010\u6d41\u867d\u56e0\u63d0\u91cd\u7269\uff0c\u4e0d\u80fd\u6b3a\u8eab\u8ff8\u5251\uff0c\u4f46\u5bf9\u65b9\u7684\u8f6f\u97ad\u540c\u6837\u4e5f\u662f\u6253\u4e0d\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u91d1\u9010\u6d41\u5df2\u662f\u7acb \u4e8e\u4e0d\u8d25\u4e4b\u5730\u3002 \u3000\u3000\u8499\u9762\u4eba\u6697\u6697\u5403\u60ca\uff1a\u201c\u6211\u7684\u5185\u529b\u4e0d\u53ca\u4ed6\u7684\u60a0\u957f\uff0c\u4e45\u6218\u4e0b\u53bb\uff0c\u53ea\u6015\u8981\u683d\u5728\u4ed6\u7684\u624b\u91cc\u3002\u201d\u5fc3\u5ff5 \u672a\u5df2\uff0c\u53ea\u542c\u5f97\u91d1\u9010\u6d41\u53c8\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8fc7\u662f\u60f3\u8981\u8fd9\u5757\u7384\u94c1\uff0c\u597d\uff0c\u73b0\u5728\u6211\u5c31\u9001\u8fd8\u7ed9 \u4f60\uff0c\u53ea\u8981\u4f60\u63a5\u5f97\u8d77\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u628a\u7ea2\u6f06\u5323\u5b50\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u4f5c\u52bf\u8981\u629b\u51fa\u53bb\uff0c\u8499\u9762\u4eba\u4e0d\u7531\u5f97\u53c8\u662f\u5927\u5403\u4e00\u60ca\uff0c\u8981\u77e5\u4ed6\u5df2\u7ecf \u77e5\u9053\u91d1\u9010\u6d41\u7684\u529f\u529b\u5728\u4ed6\u4e4b\u4e0a\uff0c\u8fd9\u4e00\u5757\u767e\u591a\u65a4\u91cd\u7684\u7384\u94c1\u4ece\u91d1\u9010\u6d41\u624b\u4e2d\u629b\u8fc7\u6765\uff0c\u6015\u4e0d\u628a\u4ed6\u538b\u6210\u4e86 \u8089\u997c\uff1f \u3000\u3000\u8fd9\u8499\u9762\u4eba\u7684\u8eab\u624b\u4e5f\u786e\u662f\u4e86\u5f97\uff0c\u4e00\u89c1\u91d1\u9010\u6d41\u4f5c\u52bf\u8981\u629b\uff0c\u767b\u65f6\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\uff0c\u95ea\u8fc7\u4e00\u8fb9\uff0c\u8f6f \u97ad\u4fa7\u88ad\uff0c\u8981\u60f3\u62a2\u524d\u4e00\u6b65\uff0c\u8f6f\u97ad\u7f20\u4e0a\u91d1\u9010\u6d41\u7684\u624b\u8155\uff0c\u53eb\u4ed6\u624b\u4e2d\u7684\u8fd9\u4e2a\u7ea2\u6f06\u5323\u5b50\u81ea\u5df1\u843d\u4e0b\u6765\uff0c\u538b \u4ed6\u81ea\u5df1\u7684\u811a\u3002 \u3000\u3000\u600e\u77e5\u4ed6\u8fd9\u4e00\u62db\u65e9\u5df2\u5728\u91d1\u9010\u6d41\u610f\u6599\u4e4b\u4e2d\uff0c\u91d1\u9010\u6d41\u9ad8\u4e3e\u5323\u5b50\uff0c\u4e0d\u8fc7\u662f\u865a\u5f20\u58f0\u52bf\uff0c\u4e3a\u7684\u5c31\u6b63\u662f \u8981\u8bf1\u4ed6\u53d1\u51fa\u8fd9\u62db\u3002\u53cc\u65b9\u52a8\u4f5c\u90fd\u5feb\uff0c\u91d1\u9010\u6d41\u4e00\u7f29\u624b\uff0c\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u8499\u9762\u4eba\u7684\u8f6f\u97ad\u7f20\u4e0a\u4e86\u7ea2\u6f06 \u5323\u5b50\u3002 \u3000\u3000\u8499\u9762\u4eba\u4e00\u62c9\uff0c\u91d1\u9010\u6d41\u624b\u4e2d\u7684\u5323\u5b50\u52a8\u4e5f\u4e0d\u52a8\uff0c\u53ea\u542c\u5f97\u201c\u529b\u52d2\u201d\u4f5c\u54cd\uff0c\u91d1\u9010\u6d41\u628a\u5323\u5b50\u5f90\u5f90\u5411 \u540e\u6536\u56de\u3002\u8f6f\u97ad\u7ed9\u62c9\u5f97\u50cf\u7ef7\u7d27\u4e86\u7684\u5f26\u3002\u8499\u9762\u4eba\u4e5f\u7ed9\u4ed6\u62c9\u5f97\u811a\u6b65\u8e09\u8dc4\uff0c\u8eab\u4e0d\u7531\u5df1\u7684\u51b2\u524d\u4e24\u6b65\uff0c\u8f6f \u97ad\u5018\u82e5\u4e0d\u80fd\u7acb\u5373\u89e3\u5f00\uff0c\u4e0d\u4f46\u8f6f\u97ad\u8981\u7ed9\u62c9\u65ad\uff0c\u8fd9\u8499\u9762\u4eba\u4e5f\u5c06\u8dcc\u5165\u91d1\u9010\u6d41\u7684\u6000\u4e2d\u3002 \u3000\u3000\u8499\u9762\u4eba\u8fde\u5fd9\u4e00\u4e2a\u201c\u5927\u5f2f\u8170\uff0c\u659c\u63d2\u67f3\u201d\uff0c\u8eab\u5f62\u4fef\u4f0f\uff0c\u7f13\u548c\u4e86\u8f6f\u97ad\u7684\u7ef7\u7d27\u4e4b\u52bf\uff0c\u4e00\u6296\u624b\uff0c\u8f6f \u97ad\u5df2\u662f\u677e\u5f00\uff0c\u91d1\u9010\u6d41\u89c1\u4ed6\u89e3\u97ad\u7684\u624b\u6cd5\u5982\u6b64\u5de7\u5999\uff0c\u5fc3\u91cc\u4e5f\u81ea\u6697\u6697\u4f69\u670d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u5757\u7384\u94c1\u6211\u60f3\u7ed9\u4f60\uff0c\u4f60\u4e5f\u62ff\u4e0d\u53bb\uff0c\u53ef\u602a\u4e0d\u5f97\u6211\u4e0d\u7ed9\u4f60\u4e86\u3002\u201d\u8499\u9762\u4eba \u4e00\u58f0\u4e0d\u53d1\uff0c\u8f6c\u5934\u5c31\u8d70\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5012\u662f\u8fc7\u610f\u4e0d\u53bb\uff0c\u8fde\u5fd9\u8ffd\u4e0a\u524d\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u6211\u662f\u548c\u4f60\u5f00\u73a9\u7b11\u7684\uff0c\u4f60\u8fd9\u4e48\u60f3\u8981\u8fd9\u5757\u7384 \u94c1\uff0c\u6211\u613f\u610f\u5272\u7231\u9001\u4f60\u3002\u201d \u3000\u3000\u8fd9\u8499\u9762\u4eba\u600e\u80fd\u76f8\u4fe1\u91d1\u9010\u6d41\u7684\u8bf4\u8bdd\uff0c\u4f9d\u7136\u4e00\u8a00\u4e0d\u53d1\uff0c\u800c\u4e14\u5934\u4e5f\u4e0d\u56de\u3002\u91d1\u9010\u6d41\u53eb\u9053\uff1a\u201c\u5582\uff0c \u5582\uff0c\u8fd9\u6b21\u6211\u5f53\u771f\u662f\u5f00\u73a9\u7b11\u7684\uff0c\u4e0d\uff0c\u4e0d\uff0c\u6211\u8981\u9001\u8fd8\u8fd9\u5757\u7384\u94c1\uff0c\u8fd9\u53ef\u771f\u7684\u4e0d\u662f\u5f00\u73a9\u7b11\u7684\uff01\u201d\u4ed6\u4e00 \u4f1a\u513f\u8bf4\u662f\u201c\u5f00\u73a9\u7b11\u201d\uff0c\u4e00\u4f1a\u513f\u8bf4\u201c\u4e0d\u662f\u5f00\u73a9\u7b11\u201d\uff0c\u5c3d\u7ba1\u610f\u601d\u90fd\u662f\u4e00\u6837\uff0c\u4f46\u7f20\u5939\u4e0d\u6e05\uff0c\u542c\u5728\u8fd9 \u4eba\u7684\u8033\u6735\u91cc\uff0c\u5374\u53ea\u5f53\u5730\u662f\u201c\u5f00\u73a9\u7b11\u201d\u4e86\u3002 \u3000\u3000\u8fd9\u8499\u9762\u4eba\u81ea\u5fd6\u6253\u4e0d\u8fc7\u91d1\u9010\u6d41\uff0c\u53c8\u4e0d\u7518\u5fc3\u53d7\u4ed6\u620f\u5f04\uff0c\u53ea\u597d\u4e0d\u7406\u4e0d\u776c\u52a0\u5feb\u811a\u6b65\uff0c\u5f84\u5411\u524d\u884c\u3002 \u3000\u3000\u91d1\u9010\u6d41\u624b\u4e0a\u63d0\u7740\u4e00\u5757\u767e\u591a\u65a4\u91cd\u7684\u7384\u94c1\uff0c\u8f7b\u529f\u81ea\u4e0d\u514d\u6253\u4e86\u6298\u6263\uff0c\u672c\u6765\u4ed6\u662f\u53ef\u4ee5\u80dc\u8fc7\u8fd9\u8499\u9762 \u4eba\u7684\uff0c\u5982\u4eca\u5374\u4e0d\u80fd\u4e0d\u843d\u5728\u8fd9\u8499\u9762\u4eba\u4e4b\u540e\u4e86\u3002\u8ffd\u4e86\u4e00\u4f1a\uff0c\u53cc\u65b9\u7684\u8ddd\u79bb\u53cd\u800c\u8d8a\u6765\u8d8a\u8fdc\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5c11\u5e74\u597d\u72c2\uff0c\u201c\u54fc\u2019\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u4e0e\u4f60\u6bd4\u6bd4\u8f7b\u529f\uff0c\u8ba9\u4f60\u5360\u70b9\u4fbf\u5b9c\uff0c\u6211 \u5e76\u4e0d\u5728\u4e4e\uff01\u4eca\u65e5\u8ffd\u4e0d\u4e0a\u4f60\uff0c\u660e\u65e5\u4e5f\u8981\u8ffd\u4e0a\u4e86\u4f60\uff01\u201d\u91d1\u9010\u6d41\u5df2\u77e5\u8fd9\u8499\u9762\u4eba\u7684\u529f\u529b\u900a\u4ed6\u4e00\u7b79\uff0c\u65f6 \u95f4\u4e00\u957f\uff0c\u8fd9\u8499\u9762\u4eba\u51b3\u4e0d\u80fd\u5982\u4ed6\u6301\u4e45\u3002 \u3000\u3000\u8499\u9762\u4eba\u6697\u6697\u53eb\u58f0\uff1a\u201c\u82e6\u4e5f\uff01\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u53ef\u662f\u90aa\u95e8\uff0c\u7384\u94c1\u5df2\u7ecf\u5230\u4e86\u4ed6\u7684\u624b\u91cc\uff0c \u4ed6\u7adf\u7136\u8fd8\u662f\u9634\u9b42\u4e0d\u6563\u7684\u8981\u6765\u7f20\u6211\uff0c\u4e0d\u77e5\u5b58\u7684\u4ec0\u4e48\u5fc3\u601d\uff1f\u201d\u8fd9\u4e48\u4e00\u60f3\uff0c\u5fc3\u91cc\u8d8a\u53d1\u5bb3\u6015\uff0c\u4e00\u54ac\u7259 \u6839\uff0c\u628a\u8f7b\u529f\u4f7f\u5230\u4e86\u5341\u6210\uff0c\u53ea\u76fc\u5728\u8ddd\u79bb\u62c9\u5f97\u66f4\u8fdc\u4e4b\u540e\uff0c\u5c31\u53ef\u4f3c\u628a\u91d1\u9010\u6d41\u6446\u8131\u3002 \u3000\u3000\u8499\u9762\u4eba\u9003\u5165\u6797\u4e2d\uff0c\u91d1\u9010\u6d41\u9532\u800c\u4e0d\u820d\u7684\u7d27\u8ffd\uff0c\u8ffd\u4e86\u4e00\u4f1a\uff0c\u8fd9\u8499\u9762\u4eba\u7684\u80cc\u5f71\uff0c\u6e10\u6e10\u6a21\u7cca\uff0c\u51e0 \u4e4e\u5c31\u8981\u770b\u4e0d\u89c1\u4e86\u3002\u4e8e\u662f\u91d1\u9010\u6d41\u4e5f\u52a0\u5feb\u4e86\u811a\u6b65\u3002 \u3000\u3000\u6797\u6df1\u6811\u5bc6\uff0c\u8499\u9762\u4eba\u62d0\u4e86\u4e2a\u5f2f\uff0c\u5f71\u5b50\u5728\u91d1\u9010\u6d41\u773c\u524d\u6d88\u5931\u3002\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u7cdf\u7cd5\uff0c\u4ed6\u82e5\u662f\u5728 \u6811\u6797\u91cc\u548c\u6211\u6349\u8ff7\u85cf\uff0c\u53ef\u662f\u4e0d\u6613\u5bfb\u627e\u4ed6\u4e86\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u7ed9\u6211\u7ad9\u7740\uff01\u201d\u201c\u4f60\u662f\u5e72\u4ec0\u4e48\u7684\uff1f\u62a5\u4e0a\u540d\u6765\uff01\u201d \u91d1\u9010\u6d41\u8d77\u521d\u4ee5\u4e3a\u662f\u5728\u559d\u4ed6\uff0c\u4e00\u770b\u5374\u4e0d\u89c1\u4eba\uff0c\u58f0\u97f3\u4ece\u524d\u9762\u4f20\u6765\uff0c\u9694\u7740\u4e00\u4e2a\u5c71\u5773\u3002\u91d1\u9010\u6d41\u767b\u65f6\u660e \u767d\uff0c\u662f\u90a3\u8499\u9762\u4eba\u88ab\u4eba\u5835\u4f4f\u4e86\u53bb\u8def\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6697\u6697\u597d\u7b11\uff0c\u201c\u60f3\u5fc5\u662f\u526a\u5f84\u7684\u5f3a\u76d7\uff0c\u8fd9\u8499\u9762\u4eba\u7684\u6b66\u529f\u975e\u540c\u5c0f\u53ef\uff0c\u8fd9\u51e0\u4e2a\u5c0f\u8d3c\u78b0\u4e0a\u4e86 \u4ed6\u53ef\u8981\u5927\u5927\u5012\u9709\u4e86\uff01\u4e0d\u8fc7\uff0c\u4ed6\u591a\u5c11\u4e5f\u8981\u53d7\u70b9\u963b\u5ef6\uff0c\u5374\u662f\u4fbf\u5b9c\u6211\u4e5f\u3002\u597d\uff0c\u4e14\u770b\u770b\u4ed6\u600e\u6837\u6253\u53d1\u8fd9 \u51e0\u4e2a\u526a\u5f84\u7684\u5f3a\u76d7\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5230\u4e86\u5c71\u5773\u7684\u5165\u53e3\u4e4b\u5904\uff0c\u4e00\u7eb5\u8eab\u8df3\u4e0a\u4e00\u68f5\u5927\u6811\uff0c\u5c45\u9ad8\u4e34\u4e0b\u7684\u5411\u524d\u9762\u671b\u53bb\u3002\u6b64\u65f6\u5df2\u662f \u671d\u9633\u521d\u51fa\u7684\u7b2c\u4e8c\u65e5\u65e9\u6668\uff0c\u770b\u5f97\u76f8\u5f53\u6e05\u695a\u3002\u53ea\u89c1\u6709\u56db\u4e2a\u6c49\u5b50\u5df2\u628a\u90a3\u8499\u9762\u4eba\u56f4\u4f4f\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4e2a\u866c\u9aef\u6c49\u5b50\u4f3c\u662f\u9996\u9886\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u662f\u8033\u804b\u7684\u5417\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u7b54\u6211\u4eec\u7684\u8bdd\uff01\u201d \u53e6\u4e00\u4e2a\u9762\u5b54\u8721\u9ec4\u7684\u6c49\u5b50\u559d\u9053\uff1a\u201c\u8bf4\uff01\u516d\u5408\u5e2e\u9001\u6740\u7684\u5bff\u793c\u662f\u4e0d\u662f\u5728\u4f60\u8eab\u4e0a\uff1f\u201d \u3000\u3000\u8499\u9762\u4eba\u4e00\u8a00\u4e0d\u53d1\uff0c\u9732\u5728\u5916\u9762\u7684\u4e24\u53ea\u773c\u775b\u6ef4\u6e9c\u6e9c\u5730\u6253\u4e86\u4e00\u8f6c\uff0c\u8eab\u5f62\u4e0d\u52a8\uff0c\u7f13\u7f13\u5730\u6447\u4e86\u6447 \u5934\u3002 \u3000\u3000\u4e00\u4e2a\u7a7f\u7740\u9752\u5e03\u957f\u886b\u9762\u767d\u65e0\u987b\u7684\u4e2d\u5e74\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u6211\u542c\u8bf4\u4e00\u5757\u5c0f\u5c0f\u7684\u7384\u94c1\u6709\u767e\u591a\u65a4\u91cd\uff0c\u8fd9 \u4eba\u7684\u8eab\u4e0a\u4e0d\u4f3c\u85cf\u6709\u7384\u94c1\u3002\u201d \u3000\u3000\u7b2c\u56db\u4e2a\u4eba\u662f\u4e2a\u8eab\u6750\u9b41\u609f\u7684\u6c49\u5b50\uff0c\u7c97\u91cc\u7c97\u6c14\u5730\u8bf4\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u662f\u5426\u5e26\u7740\u7384\u94c1\uff0c\u65e2\u7136\u78b0\u4e0a\u4e86 \u5c31\u5f97\u641c\u4ed6\u4e00\u641c\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u4e86\u8fd9\u56db\u4e2a\u4eba\u7684\u8bf4\u8bdd\uff0c\u8fd9\u624d\u77e5\u9053\u4e0d\u662f\u5bfb\u5e38\u7684\u526a\u5f84\u5c0f\u8d3c\u3002\u5fc3\u60f3\uff1a\u201c\u8fd9\u51e0\u4e2a\u8d3c\u4eba\u7684\u80c6 \u5b50\u5012\u4e5f\u4e0d\u5c0f\uff0c\u7adf\u6562\u6765\u62a2\u516d\u5408\u5e2e\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u5947\u602a\u7684\u662f\u8fd9\u56db\u4e2a\u4eba\u9ad8\u58f0\u559d\u9a82\uff0c\u5e76\u4e14\u8bae\u8bba\u7eb7\u7eb7\uff0c\u4f46\u8fd9\u8499\u9762\u4eba\u4f9d\u7136\u65e0\u52a8\u4e8e\u8877\uff0c\u5e76\u4e0d\u5f00\u53e3\u3002 \u3000\u3000\u866c\u9aef\u6c49\u5b50\u662f\u4e2a\u8001\u6c5f\u6e56\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c0f\u5b50\u5b9a\u6709\u6240\u6043\uff0c\u5426\u5219\u4e0d\u4f1a\u5982\u6b64\u65e0\u793c\u3002\u201d\u5f53\u4e0b\u4f7f\u4e86\u4e2a\u773c \u8272\uff0c\u53eb\u4ed6\u7684\u4e09\u4e2a\u540c\u4f34\u4e14\u6162\u52a8\u624b\uff0c\u7a0d\u7a0d\u5c06\u8bed\u6c14\u653e\u5f97\u5bbd\u548c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u54ea\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff0c\u4e0e\u516d \u5408\u5e2e\u6709\u65e0\u5173\u7cfb\uff0c\u597d\u597d\u7684\u8ddf\u6211\u4eec\u8bf4\u3002\u53ea\u8981\u4f60\u4e0d\u6b3a\u7792\uff0c\u6211\u4eec\u4e5f\u4e0d\u4f1a\u65e0\u6545\u5c06\u4f60\u4e3a\u96be\u7684\u3002\u201d\u4ed6\u81ea\u52a8\u8f6c \u5708\uff0c\u4f46\u8fd9\u8499\u9762\u4eba\u4ecd\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u866c\u9aef\u6c49\u5b50\u7709\u5934\u4e00\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u53ef\u4e0d\u8981\u656c\u9152\u4e0d\u5403\u5403\u7f5a\u9152\uff01\u522b\u4eba\u60f3\u548c\u6211\u4eec\u62c9\u4ea4\u60c5\u8fd8 \u4e0d\u80fd\u591f\u5462\u3002\u4f60\u5927\u7ea6\u4e0d\u8ba4\u8bc6\u6211\u4eec\u5427\uff0c\u4e0d\u8fc7\uff0c\u4f60\u7eb5\u7136\u4e0d\u8ba4\u8bc6\u6211\u4eec\uff0c\u4e5f\u8be5\u542c\u8fc7\u6211\u4eec\u7684\u540d\u5b57\u3002\u201d\u987f\u4e86 \u4e00\u987f\uff0c\u968f\u5373\u6307\u7740\u90a3\u8eab\u6750\u9b41\u68a7\u7684\u6c49\u5b50\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u767d\u864e\u5e2e\u7684\u5e2e\u4e3b\u675c\u5927\u4e1a\uff01\u8ddf\u7740\u6307\u7740\u90a3\u9762\u767d\u65e0\u987b \u7684\u65af\u6587\u6c49\u5b50\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u7ea2\u7f28\u4f1a\u7684\u9999\u4e3b\u5bab\u79c9\u85e9\u3002\u201d \u3000\u3000\u90a3\u9762\u8272\u8721\u9ec4\u4f3c\u5e26\u75c5\u5bb9\u7684\u6c49\u5b50\u4e0d\u5f85\u4ecb\u7ecd\uff0c\u4fbf\u662f\u8bf4\u9053\uff1a\u201c\u8001\u5b50\u5750\u4e0d\u66f4\u540d\uff0c\u884c\u4e0d\u6539\u59d3\uff0c\u4e00\u975e\u5e2e \u4e3b\uff0c\u4e8c\u975e\u9999\u4e3b\uff0c\u563f\uff0c\u563f\uff0c\u6211\u53ef\u662f\u4e2a\u6740\u4eba\u4e0d\u7728\u773c\u7684\u5f3a\u76d7\uff0c\u8001\u5b50\u662f\u5180\u5317\u9053\u4e0a\u7684\u90d1\u96c4\u56fe\uff01\u201d\u8fd9\u4eba\u9762 \u5e26\u75c5\u5bb9\uff0c\u8bf4\u8bdd\u5982\u662f\u58f0\u82e5\u6d2a\u949f\u3002\u4ed6\u4ee5\u4e3a\u8bf4\u51fa\u4e86\u81ea\u5df1\u7684\u201c\u5927\u540d\u201d\uff0c\u8fd9\u8499\u9762\u4eba\u5b9a\u7136\u98a4\u6296\uff0c\u4e0d\u6599\u8499\u9762 \u4eba\u5374\u662f\u7f6e\u82e5\u7f54\u95fb\uff0c\u8499\u9762\u7684\u9ed1\u7eb1\u4e0b\u89d2\u98d8\u8d77\uff0c\u5634\u8fb9\u9732\u51fa\u4e2a\u8f7b\u8511\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u866c\u9aef\u6c49\u5b50\u6700\u540e\u8bf4\u9053\uff1a\u201c\u533a\u533a\u5728\u4e0b\u662f\u9752\u9f99\u5e2e\u7684\u5e2e\u4e3b\u9ad8\u5927\u6210\u3002\u9601\u4e0b\u6709\u8fd9\u8eab\u8f7b\u529f\uff0c\u60f3\u5fc5\u4e0d\u662f\u6c5f \u6e56\u4e0a\u7684\u65e0\u540d\u4e4b\u8f88\uff0c\u6211\u4eec\u56db\u4e2a\u4eba\u7684\u540d\u5b57\u9601\u4e0b\u603b\u5e94\u8be5\u542c\u8fc7\u5427\uff01\u771f\u4eba\u9762\u524d\u4e0d\u8bf4\u5047\u8bdd\uff0c\u597d\uff0c\u73b0\u5728\u662f\u8be5 \u9601\u4e0b\u8bf4\u8001\u5b9e\u8bdd\u4e86\uff01\u201d \u3000\u3000\u8fd9\u56db\u4e2a\u4eba\u7684\u59d3\u540d\u3001\u8eab\u4efd\u4e00\u8bf4\u51fa\u6765\uff0c\u8fde\u91d1\u9010\u6d41\u4e5f\u4e0d\u89c9\u6697\u6697\u5403\u60ca\u3002\u539f\u6765\u9752\u9f99\u5e2e\u3001\u767d\u864e\u4e24\u5e2e\u90fd \u662f\u6c5f\u6e56\u4e0a\u8457\u540d\u7684\u5927\u5e2e\uff1b\u7ea2\u7f28\u4f1a\u662f\u5927\u540d\u9f0e\u9f0e\u8db3\u4e0e\u516d\u5408\u5e2e\u5206\u5ead\u6297\u793c\u7684\u5e2e\u4f1a\uff0c\u8235\u4e3b\u516c\u5b59\u5b8f\uff0c\u624b\u4e0b\u6709 \u516b\u5927\u9999\u4e3b\uff0c\u4e2a\u4e2a\u8eab\u8d1f\u7edd\u6280\u3002\u8fd9\u4e2a\u9762\u767d\u65e0\u987b\u7684\u5bab\u79c9\u85e9\u4fbf\u662f\u516b\u5927\u9999\u4e3b\u4e4b\u9996\u3002\u81f3\u4e8e\u90a3\u4e2a\u75c5\u592b\u4f3c\u7684\u90d1 \u96c4\u56fe\u4e43\u662f\u5317\u4e94\u7701\u7684\u72ec\u811a\u5927\u76d7\uff0c\u6b63\u5982\u4ed6\u81ea\u6211\u4ecb\u7ecd\u90a3\u6837\uff0c\u662f\u4e2a\u6740\u4eba\u4e0d\u7728\u773c\u7684\u9b54\u738b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u521a\u597d\u662f\u5728\u524d\u4e24\u5929\u542c\u5f97\u9648\u5929\u5b87\u8bf4\u8fc7\u8fd9\u51e0\u4e2a\u4eba\uff0c\u9648\u5929\u5b87\u56e0\u6050\u91d1\u9010\u6d41\u4e0d\u660e\u6c5f\u6e56\u60c5\u51b5\uff0c\u6240 \u4ee5\u5c06\u5404\u5927\u5e2e\u4f1a\u4ee5\u53ca\u9ed1\u9053\u4e0a\u8457\u540d\u7684\u4eba\u7269\u4e0d\u538c\u5176\u8be6\u7684\u4e00\u4e00\u544a\u8bc9\u4e86\u91d1\u9010\u6d41\u3002\u4f46\u867d\u8bf4\u662f\u201c\u4e0d\u538c\u5176 \u8be6\u201d\uff0c\u503c\u5f97\u9648\u5929\u5b87\u8fd9\u6837\u7684\u6b66\u5b66\u540d\u5bb6\u4e00\u63d0\u7684\uff0c\u5f53\u7136\u51b3\u4e0d\u662f\u6cdb\u6cdb\u4e4b\u8f88\u7684\u4e86\u3002 \u3000\u3000\u4f46\u8fd9\u56db\u4e2a\u4eba\u62a5\u4e86\u59d3\u540d\u4e4b\u540e\uff0c\u8499\u9762\u4eba\u4ecd\u662f\u4e0d\u58f0\u4e0d\u54cd\uff0c\u4ec5\u4ec5\u70b9\u4e86\u70b9\u5934\u3002\u610f\u601d\u597d\u50cf\u662f\u8bf4\uff1a\u201c\u6211 \u542c\u89c1\u4e86\uff0c\u6211\u77e5\u9053\u4e86\u3002\u8499\u9762\u4eba\u9762\u90e8\u7684\u8868\u60c5\u867d\u770b\u4e0d\u89c1\uff0c\u90a3\u5014\u50b2\u7684\u6001\u5ea6\u5012\u662f\u4eba\u4eba\u90fd\u611f\u89c9\u5f97\u5230\u3002 \u3000\u3000\u90d1\u96c4\u56fe\u5927\u6012\u9053\uff1a\u201c\u597d\u65e0\u793c\u7684\u5c0f\u5b50\uff01\u9ad8\u5927\u54e5\u8fd8\u548c\u4ed6\u591a\u8bf4\u4f5c\u751a\uff1f\u628a\u4ed6\u62ff\u4e0b\uff0c\u786c\u641c\u5c31\u662f\uff01\u201d \u3000\u3000\u5176\u5b9e\u8fd9\u8499\u9762\u4eba\u5374\u4e5f\u4e0d\u662f\u6545\u610f\u65e0\u793c\uff0c\u4ed6\u662f\u6015\u4e00\u5f00\u53e3\u8bf4\u8bdd\uff0c\u5c31\u7ed9\u8fd9\u56db\u4e2a\u4eba\u8bc6\u7834\u3002\u539f\u6765\u8fd9\u8499\u9762 \u4eba\u975e\u4f46\u77e5\u9053\u4ed6\u4eec\u7684\u540d\u5b57\uff0c\u800c\u4e14\u662f\u89c1\u8fc7\u4ed6\u4eec\u7684\u3002 \u3000\u3000\u8499\u9762\u4eba\u4f5c\u51fa\u5014\u50b2\u7684\u795e\u6001\uff0c\u5fc3\u91cc\u5b9e\u662f\u5341\u5206\u7126\u6025\uff0c\u6697\u81ea\u76d8\u7b97\u8131\u9669\u4e4b\u7b56\uff0c\u4ed6\u660e\u77e5\u8fd9\u56db\u4e2a\u4eba\u90fd\u662f \u5341\u5206\u624e\u624b\u7684\u5f3a\u654c\uff0c\u4f46\u524d\u65e0\u53bb\u8def\uff0c\u540e\u6709\u8ffd\u5175\uff0c\u4e5f\u53ea\u597d\u5192\u9669\u6c42\u901e\uff0c\u5148\u4e0b\u624b\u529b\u5f3a\u4e86\u3002 \u3000\u3000\u90d1\u96c4\u56fe\u4e00\u4e2a\u201c\u641c\u201d\u5b57\u521a\u521a\u51fa\u53e3\uff0c\u9661\u7136\u95f4\u94f6\u5149\u4e00\u95ea\uff0c\u8499\u9762\u4eba\u7684\u94f6\u4e1d\u8f6f\u97ad\u5df2\u662f\u5411\u4ed6\u6253\u6765\uff01\u90d1 \u96c4\u56fe\u4e00\u58f0\u5927\u543c\uff0c\u80cc\u810a\u7740\u4e86\u4e00\u97ad\u3002\u4f46\u4ed6\u7ec3\u6709\u91d1\u949f\u7f69\u7684\u529f\u592b\uff0c\u8fd9\u4e00\u97ad\u6253\u5f97\u5b9e\u662f\u4e0d\u8f7b\uff0c\u90d1\u96c4\u56fe\u8863\u88f3 \u7834\u788e\uff0c\u80cc\u810a\u5374\u53ea\u662f\u73b0\u51fa\u4e00\u9053\u6de1\u6de1\u7684\u97ad\u75d5\uff0c\u8fde\u76ae\u80a4\u90fd\u6ca1\u6709\u64e6\u7834\u3002\u201d \u3000\u3000\u90d1\u96c4\u56fe\u4e00\u58f0\u5927\u543c\uff0c\u53cc\u638c\u7acb\u5373\u4fbf\u5288\u8fc7\u53bb\u3002\u8499\u9762\u4eba\u8eab\u624b\u4f55\u7b49\u77eb\u6377\uff0c\u8f6f\u97ad\u4e00\u6296\uff0c\u53c8\u5df2\u6253\u5230\u9ad8\u5927 \u6210\u8eab\u524d\uff0c\u7528\u7684\u662f\u201c\u9501\u5589\u97ad\u201d\u7684\u62db\u6570\u3002\u9ad8\u5927\u6210\u7684\u8116\u5b50\u82e5\u662f\u7ed9\u4ed6\u5957\u4e0a\uff0c\u5b9a\u5c06\u6c14\u7edd\u800c\u4ea1\u3002 \u3000\u3000\u9ad8\u5927\u6210\u4f7f\u7684\u5175\u5668\u662f\u4e00\u6839\u4e03\u5c3a\u957f\u7684\u72fc\u7259\u68d2\uff0c\u731d\u7136\u9047\u88ad\uff0c\u5e76\u4e0d\u614c\u4e71\uff0c\u53ea\u89c1\u4ed6\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9 \u5934\u201d\uff0c\u72fc\u7259\u68d2\u5c31\u5728\u8eab\u524d\u575a\u4e86\u8d77\u6765\u3002\u8499\u9762\u4eba\u7684\u8f6f\u97ad\u82e5\u662f\u671d\u4ed6\u5708\u7ed5\uff0c\u8f6f\u97ad\u5c31\u8981\u5957\u4e0a\u4e86\u72fc\u7259\u68d2\u3002\u8499 \u9762\u4eba\u5728\u56db\u5927\u9ad8\u624b\u73af\u653b\u4e4b\u4e0b\uff0c\u7109\u80fd\u4e0e\u4ed6\u786c\u62fc\uff1f\u500f\u5730\u4e00\u4e2a\u8f6c\u8eab\uff0c\u8f6f\u97ad\u53c8\u5df2\u6539\u4e86\u65b9\u5411\uff0c\u5411\u7ea2\u7f28\u4f1a\u7684 \u9999\u4e3b\u5bab\u79c9\u85e9\u6025\u88ad\u3002\u8fd9\u65f6\u65b9\u59cb\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u539f\u6765\u662f\u90d1\u96c4\u56fe\u4e00\u638c\u5288\u65ad\u4e86\u4e00\u68f5\u677e\u6811\u3002\u4ed6\u7ec3\u7684 \u662f\u94c1\u7802\u638c\u529f\u592b\u3002 \u3000\u3000\u8499\u9762\u4eba\u6025\u88ad\u5bab\u79c9\u85e9\u8fd9\u4e00\u97ad\u66f4\u4e3a\u72e0\u8fa3\uff0c\u97ad\u68a2\u4e0a\u7684\u77ed\u5251\u5df2\u4f38\u4e86\u51fa\u6765\uff0c\u6307\u5411\u5bab\u79c9\u85e9\u7684\u54bd\u5589\uff0c\u97ad \u68a2\u5219\u4ee5\u201c\u7389\u5e26\u56f4\u8170\u201d\u4e4b\u52bf\uff0c\u5708\u626b\u5bab\u79c9\u85e9\u7684\u4e24\u80c1\uff0c\u90a3\u652f\u4f38\u51fa\u7684\u77ed\u5251\u4fbf\u4f3c\u6602\u8d77\u7684\u86c7\u5934\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\u3002\u201d\u6765\u5f97\u597d\uff01\u201d\u5251\u5149\u4e00\u95ea\uff0c\u62ff\u634f\u65f6\u5019\u4e0d\u5dee\u6beb\u5398\uff0c\u53ea\u542c\u5f97\u201c\u556a\u201d\u7684 \u4e00\u58f0\uff0c\u8499\u9762\u4eba\u97ad\u68a2\u4e0a\u7684\u77ed\u5251\u7f29\u4e86\u56de\u53bb\u3002\u539f\u6765\u5bab\u79c9\u85e9\u8fd9\u4e00\u5251\u662f\u5bf9\u51c6\u4e86\u4ed6\u7684\u77ed\u5251\u524a\u7684\uff0c\u77ed\u5251\u53ea\u6709 \u4e09\u5bf8\u957f\uff0c\u80fd\u88ab\u4ed6\u7684\u957f\u5251\u78b0\u4e0a\uff0c\u5b9a\u5c06\u524a\u65ad\u3002\u662f\u4ee5\u8499\u9762\u4eba\u53ea\u5f97\u6309\u52a8\u673a\u62ec\uff0c\u628a\u77ed\u5251\u7f29\u56de\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5bab\u79c9\u85e9\u4e00\u62db\u201c\u5999\u89e3\u8fde\u73af\u201d\uff0c\u7acb\u5373\u628a\u8499\u9762\u4eba\u7684\u90a3\u4e00\u97ad\u201c\u7389\u5e26\u56f4\u8170\u201d\u7684\u97ad \u6cd5\u7834\u4e86\u3002\u800c\u4e14\u7acb\u5373\u6b3a\u8eab\u8fdb\u5251\uff0c\u5251\u950b\u6cbf\u7740\u97ad\u8eab\u5f84\u524a\u8fc7\u6765\uff0c\u4ed6\u662f\u60f3\u903c\u8fd9\u8499\u9762\u4eba\u4e0d\u80fd\u8f6c\u6362\u97ad\u6cd5\uff0c\u5f84 \u524a\u4ed6\u7684\u624b\u6307\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6697\u6697\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u5fc3\u60f3\u3002\u201d\u8fd9\u5bab\u79c9\u6f58\u867d\u7136\u53ea\u662f\u4e00\u540d\u9999\u4e3b\uff0c\u5374\u6bd4\u9752\u9f99\u5e2e\u7684\u5e2e \u4e3b\u8fd8\u8981\u80dc\u8fc7\u4e00\u7b79\u3002\u4ed6\u7684\u8fd9\u8def\u5251\u6cd5\u867d\u7136\u4e0d\u53ca\u5929\u5c71\u5251\u6cd5\uff0c\u4f46\u5176\u72e0\u8fa3\u4e4b\u5904\uff0c\u5374\u662f\u8db3\u4ee5\u4e0e\u6b66\u5f53\u6d3e\u7684\u8fde \u73af\u593a\u547d\u5251\u6cd5\u6297\u8861\u4e86\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\u3002\u53ea\u89c1\u8499\u9762\u4eba\u4e00\u4e2a\u76d8\u9f99\u7ed5\u6b65\u907f\u5f00\u4e86\u5bab\u79c9\u85e9\u7684\u957f\u5251\uff0c\u8eab\u6cd5\u97ad\u6cd5 \u90fd\u662f\u7f8e\u5999\u4e4b\u6781\u3002\u91d1\u9010\u6d41\u4e0d\u7531\u5f97\u53c8\u662f\u6697\u6697\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u5fc3\u9053\uff1a\u201c\u6b64\u4eba\u89e3\u62db\u7684\u672c\u9886\u771f\u53ef\u8bf4 \u5f97\u662f\u673a\u53d8\u767e\u51fa\uff0c\u82e5\u7136\u662f\u6362\u4e86\u6211\uff0c\u53ea\u6015\u6211\u4e5f\u672a\u5fc5\u89e3\u5f97\u4f3c\u4ed6\u8fd9\u6837\u7684\u6070\u5230\u597d\u5904\u3002\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8499\u9762\u4eba\u53c8\u5df2\u8f6c\u4e86\u65b9\u5411\uff0c\u653b\u5411\u767d\u864e\u5e2e\u7684\u5e2e\u4e3b\u675c\u5927\u4e1a\uff0c\u675c\u5927\u4e1a\u4f7f\u7684\u662f\u4e00\u5bf9 \u62a4\u624b\u94a9\uff0c\u64c5\u4e8e\u9501\u62ff\u5200\u5251\uff0c\u4f46\u8fd9\u94f6\u4e1d\u8f6f\u97ad\u4e0d\u8fc7\u62c7\u6307\u822c\u7c97\u7ec6\uff0c\u8499\u9762\u4eba\u53c8\u4f7f\u5f97\u7075\u6d3b\u4e4b\u6781\uff0c\u675c\u5927\u4e1a\u53cc \u94a9\u4e00\u9501\uff0c\u6ca1\u6709\u9501\u7740\u3002 \u3000\u3000\u58ee\u5927\u4e1a\u7684\u62a4\u624b\u94a9\u6ca1\u6709\u9501\u7740\u8499\u9762\u4eba\u7684\u8f6f\u97ad\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u8499\u9762\u4eba\u7684\u8f6f\u97ad\u5374\u6253\u7740\u4e86\u675c\u5927 \u4e1a\u3002\u675c\u5927\u4e1a\u7a7f\u6709\u62a4\u8eab\u9a6c\u7532\uff0c\u5916\u8863\u6253\u88c2\uff0c\u5374\u672a\u53d7\u4f24\u3002\u675c\u5927\u4e1a\u4ee5\u5e2e\u4e3b\u7684\u8eab\u4efd\uff0c\u51e0\u66fe\u5403\u8fc7\u8fd9\u6837\u7684\u5927 \u4e8f\uff1f\u767b\u65f6\u66b4\u8df3\u5982\u96f7\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u9ad8\u5927\u54e5\uff0c\u54b1\u4eec\u628a\u8fd9\u5c0f\u8d3c\u6bd9\u4e86\uff01\u7ba1\u4ed6\u6709\u7384\u94c1\u4e5f\u597d\uff0c\u6ca1\u6709\u7384\u94c1 \u4e5f\u597d\uff0c\u6740\u4e86\u518d\u8bf4\uff01\u201d \u3000\u3000\u9ad8\u5927\u6210\u662f\u4ed6\u5e08\u5144\uff0c\u51fa\u5e08\u4e4b\u540e\uff0c\u5e08\u5144\u5f1f\u5206\u638c\u4e24\u5e2e\u3002\u9ad8\u5927\u6210\u6bd4\u8f83\u614e\u91cd\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a \u201c\u8d24\u5f1f\u4e0d\u5fc5\u5fc3\u6025\uff0c\u8fd9\u5c0f\u5b50\u8dd1\u4e0d\u4e86\u7684\u3002\u8fd8\u662f\u628a\u4ed6\u6349\u7740\u4e86\u5148\u95ee\u95ee\u4ed6\u7684\u53e3\u4f9b\u5427\uff01\u201d \u3000\u3000\u8fd9\u8499\u9762\u4eba\u5728\u7535\u5149\u706b\u77f3\u4e4b\u95f4\u904d\u88ad\u56db\u5927\u9ad8\u624b\uff0c\u800c\u4e14\u90d1\u96c4\u56fe\u4e0e\u675c\u5927\u4e1a\u8fd8\u5404\u81ea\u7740\u4e86\u4ed6\u7684\u4e00\u97ad\uff0c\u9ad8 \u5927\u6210\u7b49\u4eba\u867d\u8bf4\u662f\u80dc\u5238\u5728\u63e1\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u80fd\u4e0d\u51db\u7136\u751f\u60e7\u3002\u5f53\u4e0b\u6765\u53d6\u4e86\u5206\u8fdb\u5408\u51fb\u7684\u5305\u56f4\u6218\u672f\uff0c\u6b65\u6b65 \u8fdb\u903c\uff0c\u5374\u4e0d\u6562\u8fc7\u4efd\u8e81\u8fdb\u3002 \u3000\u3000\u8499\u9762\u4eba\u7684\u5077\u88ad\u4e4b\u6240\u4ee5\u80fd\u591f\u7a0d\u5360\u4fbf\u5b9c\uff0c\u4e00\u6765\u662f\u4ed7\u7740\u8eab\u6cd5\u8f7b\u7075\uff0c\u97ad\u6cd5\u5de7\u5999\uff1b\u4e8c\u6765\u4e5f\u662f\u7531\u4e8e\u51fa \u5176\u4e0d\u610f\uff0c\u653b\u5176\u65e0\u5907\uff0c\u6240\u4ee5\u624d\u80fd\u904d\u88ad\u56db\u5927\u9ad8\u624b\uff0c\u7a0d\u5360\u4fbf\u5b9c\u3002\u4e0d\u8fc7\u4e5f\u53ea\u662f\u201c\u7a0d\u5360\u4fbf\u5b9c\u201d\u800c\u5df2\uff0c\u5e76 \u672a\u80fd\u5404\u4e2a\u51fb\u7834\u3002\u82e5\u8bba\u771f\u5b9e\u7684\u672c\u9886\uff0c\u4ed6\u672a\u5fc5\u5c31\u80fd\u80dc\u5f97\u8fc7\u8fd9\u56db\u5927\u9ad8\u624b\uff0c\u5176\u4e2d\u5bab\u79c9\u85e9\u7684\u672c\u9886\uff0c\u751a\u81f3 \u8fd8\u5728\u4ed6\u4e4b\u4e0a\u3002\u4ed6\u521a\u624d\u5077\u88ad\u5bab\u79c9\u85e9\u4e4b\u65f6\uff0c\u5c31\u9669\u4e9b\u8981\u5403\u4e86\u4e8f\u7684\u3002 \u3000\u3000\u56db\u5927\u9ad8\u624b\u91c7\u53d6\u4e86\u5305\u56f4\u7684\u6218\u672f\uff0c\u4e0d\u8f7b\u654c\uff0c\u4e0d\u9a84\u8e81\uff0c\u9010\u6e10\u628a\u5305\u56f4\u5708\u7f29\u5c0f\uff0c\u8499\u9762\u4eba\u7684\u8f7b\u529f\u96be\u4ee5 \u53d1\u6325\uff0c\u6e10\u6e10\u5c31\u611f\u5230\u5403\u529b\u4e86\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u8fd0\u5251\u5982\u98ce\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u8499\u9762\u4eba\u4f7f\u51fa\u6d51\u8eab\u672c\u9886\uff0c\u89e3\u5f00\u4e86\u4ed6\u7684\u8fde\u73af\u4e03\u5251\uff0c \u5728\u8fd9\u540c\u65f6\uff0c\u8fd8\u8361\u5f00\u4e86\u675c\u5927\u4e1a\u7684\u62a4\u624b\u94a9\uff0c\u903c\u9000\u4e86\u90d1\u96c4\u56fe\u7684\u94c1\u7802\u638c\uff0c\u626b\u6b6a\u4e86\u9ad8\u5927\u6210\u7684\u72fc\u7259\u68d2\u3002\u5bab \u79c9\u85e9\u8d5e\u9053\uff1a\u201c\u597d\u97ad\u6cd5\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u7a81\u7136\u4e00\u4e2a\u6b3a\u8eab\u8fdb\u6b65\uff0c\u5251\u5c16\u5df2\u523a\u5230\u4e86\u8499\u9762\u4eba\u7684\u9762\u95e8\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u7684\u5251\u672f\u5f53\u771f\u662f\u7cbe\u5999\u4e4b\u6781\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5251\u5c16\u5df2\u5212\u7834\u4e86\u8499\u9762\u4eba\u7684\u8499\u9762\u9ed1 \u7eb1\uff0c\u8fd9\u9ed1\u7eb1\u8584\u5982\u8749\u7ffc\uff0c\u5bab\u79c9\u85e9\u5212\u7834\u4ed6\u7684\u9ed1\u7eb1\uff0c\u7adf\u6ca1\u4f24\u7740\u4ed6\u7684\u4e00\u6839\u6beb\u6bdb\uff01 \u3000\u3000\u5bab\u79c9\u85e9\u5f97\u610f\u4e4b\u6781\uff0c\u5728\u5212\u7834\u9762\u7eb1\u4e4b\u65f6\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5927\u4e08\u592b\u5c82\u5b9c\u853d\u5934\u8499\u9762\uff1f\u8bf7\u8ba9\u6211\u4eec\u89c1\u89c1 \u5c0a\u5bb9\u53c8\u6709\u4f55\u59a8\uff1f\u201d\u539f\u6765\u5bab\u79c9\u85e9\u9887\u4e3a\u81ea\u8d1f\uff0c\u4ed6\u81ea\u89c9\u4ee5\u4f17\u51cc\u5be1\uff0c\u80dc\u4e4b\u4e0d\u6b66\uff0c\u6545\u6b64\u53ea\u662f\u6311\u5f00\u8499\u9762\u4eba \u7684\u9762\u7eb1\uff0c\u5374\u4e0d\u80af\u4f24\u4ed6\uff0c\u4f46\u6311\u5f00\u9762\u7eb1\u4e4b\u540e\uff0c\u5bab\u79c9\u85e9\u53ef\u5c31\u767b\u65f6\u7b11\u4e0d\u51fa\u58f0\u4e86\u3002 \u3000\u3000\u675c\u5927\u4e1a\u521a\u624d\u5403\u4e86\u8499\u9762\u4eba\u7684\u4e8f\uff0c\u5728\u5bab\u5bdd\u85e9\u6b3a\u8eab\u8fdb\u5251\u4e4b\u65f6\uff0c\u4ed6\u4e5f\u4e58\u673a\u653b\u51fb\uff0c\u51e0\u4e4e\u662f\u5728\u5bab\u79c9\u85e9 \u6311\u5f00\u9762\u7eb1\u7684\u540c\u4e00\u65f6\u5019\uff0c\u4ed6\u7684\u62a4\u624b\u94a9\u4e5f\u6495\u7834\u4e86\u8499\u9762\u4eba\u7684\u957f\u886b\u4e0b\u6446\u3002\u4ed6\u4e5f\u50cf\u5bab\u79c9\u85e9\u4e00\u6837\uff0c\u62db\u6570\u5f97 \u624b\uff0c\u5374\u53cd\u800c\u5403\u4e86\u4e00\u60ca\u3002\u53cc\u94a9\u672c\u6765\u662f\u8981\u8fde\u7eed\u8fdb\u62db\u7684\uff0c\u4e5f\u5413\u5f97\u7a81\u7136\u6b62\u6b65\u4e86\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u8499\u9762\u4eba\u9732\u51fa\u4e86\u5e90\u5c71\u771f\u76f8\uff0c\u53ef\u4e0d\u662f\u5bab\u79c9\u85e9\u6240\u60f3\u8c61\u7684\u201c\u5927\u4e08\u592b\u201d\uff0c\u800c\u662f\u4e00\u4e2a\u6708\u8c8c\u82b1 \u5bb9\u7684\u5c11\u5973\uff01 \u3000\u3000\u91d1\u9010\u6d41\u5728\u6811\u4e0a\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u8fd9\u4e5f\u624d\u604d\u7136\u5927\u609f\uff0c\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5979\u8981\u8499\u7740\u9762\u7eb1\uff0c\u53c8\u8981\u7a7f \u7740\u8fd9\u4e48\u4e00\u4ef6\u53c8\u5bbd\u53c8\u957f\u7684\u62d6\u5730\u957f\u886b\uff0c\u539f\u6765\u662f\u8981\u906e\u63a9\u5979\u7684\u4e09\u5bf8\u91d1\u83b2\u3002\u201d \u3000\u3000\u8499\u9762\u4eba\u9732\u51fa\u771f\u76f8\uff0c\u5927\u51fa\u5bab\u79c9\u85e9\u7b49\u4eba\u610f\u6599\u4e4b\u5916\uff0c\u5bab\u79c9\u85e9\u6014\u4e86\u4e00\u6014\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u662f\u53f2\u59d1 \u5a18\uff0c\u5bf9\u4e0d\u4f4f\uff0c\u5bab\u67d0\u5192\u72af\u4e86\uff01\u201d\u90a3\u5973\u5b50\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u56db\u4e2a\u5927\u7537\u4eba\u6b3a\u4fae\u6211\u4e00\u4e2a\u5973 \u5b50\uff0c\u4f60\u4eec\u662f\u7f9e\u4e5f\u4e0d\u7f9e\uff1f\u201d \u3000\u3000\u675c\u5927\u4e1a\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u54b1\u4eec\u5982\u4eca\u662f\u9a91\u5728\u864e\u80cc\uff0c\u4e00\u4e0d\u505a\uff0c\u4e8c\u4e0d\u4f11\u2026\u2026\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3 \u5973\u5b50\u5df2\u662f\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u600e\u6837\uff1f\u597d\uff0c\u6709\u80c6\u7684\u4f60\u5c31\u628a\u6211\u6740\u4e86\u3002\u54fc\uff0c\u4f60\u77e5\u9053\u662f\u6211\u8fd8\u8981\u6b3a\u8d1f\u6211\uff0c \u6211\u54e5\u54e5\u5c82\u80af\u4e0e\u4f60\u5e72\u4f11\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u5973\u5b50\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u516d\u5408\u5e2e\u5e2e\u4e3b\u53f2\u767d\u90b5\u7684\u59b9\u59b9\u53f2\u7ea2\u82f1\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u4eba\u867d\u7136\u662f\u8981\u62a2\u516d\u5408\u5e2e\u9001\u7ed9\u5927\u5185\u603b\u7ba1\u7684\u8d3a\u793c\uff0c\u4f46\u5374\u60f3\u4e0d\u5230\u649e\u7740\u4e86\u53f2\u767d\u90b5\u7684\u4eb2\u59b9\u5b50\u3002 \u62a2\u4e86\u7384\u94c1\uff0c\u56fa\u7136\u8981\u548c\u516d\u5408\u5e2e\u7ed3\u4ec7\uff0c\u4f46\u8fd8\u4e0d\u7b97\u5341\u5206\u4e25\u91cd\uff0c\u9ed1\u9053\u4e0a\u9ed1\u5403\u9ed1\u7684\u4e8b\u60c5\u662f\u5e38\u6709\u7684\u3002\u4f46\u82e5 \u4f24\u4e86\u53f2\u7ea2\u82f1\uff0c\u8fd9\u53ef\u5c31\u8981\u548c\u53f2\u767d\u90b5\u7ed3\u4e0b\u6c38\u8fdc\u89e3\u4e0d\u5f00\u7684\u7cb1\u5b50\u4e86\u3002 \u3000\u3000\u90d1\u96c4\u56fe\u8bf4\u9053\uff1a\u201c\u8fd9\u51a4\u4ec7\u4e0d\u7ed3\u4e5f\u5df2\u7ed3\u4e86\u3002\u533a\u533a\u4e0d\u8db3\u9053\uff0c\u4f46\u4f60\u4eec\u4e09\u4f4d\u53ef\u662f\u5e2e\u4e3b\u548c\u9999\u4e3b\u7684\u8eab \u4efd\uff0c\u82e5\u662f\u653e\u8fc7\u4e86\u8fd9\u4e2a\u5c0f\u5b50\uff0c\u6c5f\u6e56\u4e0a\u7684\u597d\u6c49\u53ea\u6015\u4e0d\u662f\u8bf4\u4f60\u4eec\u591f\u4ea4\u60c5\uff0c\u800c\u662f\u8bf4\u4f60\u4eec\u7684\u9752\u9f99\u5e2e\u3001\u767d \u864e\u5e2e\u4e0e\u7ea2\u7f28\u4f1a\u90fd\u6015\u4e86\u516d\u5408\u5e2e\u7684\u53f2\u767d\u90b5\uff01\u201d\u90d1\u96c4\u56fe\u662f\u5173\u5916\u9a6c\u8d3c\uff0c\u8fd9\u6b21\u6765\u5230\u4e2d\u539f\uff0c\u672c\u6765\u5c31\u662f\u60f3\u5728 \u9ed1\u9053\u4e0a\u5174\u98ce\u4f5c\u6d6a\u4ee5\u4fbf\u6d51\u6c34\u6478\u9c7c\u7684\u3002\u800c\u4e14\u4ed6\u7ed9\u53f2\u7ea2\u82f1\u6253\u4e86\u4e00\u97ad\uff0c\u8fd9\u53e3\u6028\u6c14\u4e5f\u662f\u975e\u8981\u53d1\u6cc4\u4e0d\u53ef\u3002 \u3000\u3000\u9ad8\u5927\u6210\u4e00\u54ac\u7259\uff0c\u8bf4\u9053\u3002\u201d\u5bf9\uff0c\u4e00\u4e0d\u505a\uff0c\u4e8c\u4e0d\u4f11\u3002\u54b1\u4eec\u65e2\u7136\u662f\u51b2\u7740\u516d\u5408\u5e2e\u800c\u6765\uff0c\u90a3\u5c31\u4e0d\u7528 \u7ba1\u4ed6\u662f\u4ec0\u4e48\u4eba\u4e86\uff01\u628a\u8fd9\u96cc\u513f\u62ff\u4e0b\uff0c\u518d\u53bb\u62a2\u90a3\u7384\u94c1\u3002\u6709\u8fd9\u4e24\u4ef6\u5b9d\u8d1d\u5728\u54b1\u4eec\u624b\u4e2d\uff0c\u53f2\u767d\u90b5\u975e\u5f97\u5411 \u54b1\u4eec\u6c42\u548c\u4e0d\u53ef\uff01\u201d \u3000\u3000\u675c\u5927\u4e1a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u624d\u662f\u4e0a\u7b56\uff01\u53f2\u767d\u90b5\u5411\u54b1\u4eec\u6c42\u548c\uff01\u54b1\u4eec\u8fd8\u4e0d\u5fc5\u5c31\u8f7b\u6613\u5e94\u627f\u4ed6 \u5462\uff0c\u8d81\u52bf\u628a\u4ed6\u7684\u516d\u5408\u5e2e\u541e\u5e76\u4e86\u3002\u597d\uff0c\u5bab\u9999\u4e3b\uff0c\u73b0\u5728\u5c31\u770b\u4f60\u4e86\uff01\u201d \u3000\u3000\u5bab\u79c9\u85e9\u5012\u662f\u4e0d\u5927\u613f\u610f\u4ee5\u4f17\u51cc\u5be1\u7684\uff0c\u4f46\u4ed6\u7684\u7ea2\u7f28\u4f1a\u4e0e\u53f2\u767d\u90b5\u7684\u516d\u5408\u5e2e\u4e43\u662f\u5728\u6c5f\u6e56\u4e0a\u5206\u5ead\u6297 \u793c\u7684\u4e24\u5927\u5e2e\u4f1a\uff0c\u90d1\u96c4\u56fe\u7684\u90a3\u53e5\u8bdd\u523a\u4e2d\u4e86\u4ed6\u7684\u8981\u5bb3\uff0c\u5bab\u79c9\u85e9\u5fc3\u60f3\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u82e5\u653e\u8fc7\u4e86\u5979\uff0c\u6c5f \u6e56\u597d\u6c49\u8bf4\u7ea2\u7f28\u4f1a\u6015\u4e86\u516d\u5408\u5e2e\uff0c\u90a3\u53ef\u662f\u5927\u5931\u9762\u5b50\u4e4b\u4e8b\u3002\u800c\u4e14\u4e09\u4e2a\u4eba\u90fd\u8868\u793a\u4e86\u4e0d\u60dc\u4e0e\u516d\u5408\u5e2e\u7ed3 \u4ec7\uff0c\u4ed6\u4e00\u4e2a\u4eba\u4e5f\u4e0d\u597d\u72ec\u6392\u4f17\u8bae\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u8003\u8651\u4e86\u5f97\u5931\u5229\u5bb3\u4e4b\u540e\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u4f60\u628a\u4ee4\u5144\u642c\u51fa\u6765\u5413\u6211\uff0c\u5bab\u67d0\u5012\u662f\u7ed9 \u4f60\u903c\u5f97\u975e\u52a8\u624b\u4e0d\u53ef\u4e86\uff01\u201d \u3000\u3000\u9ad8\u5927\u6210\u5927\u559c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u56db\u4eba\u662f\u6709\u798f\u540c\u4eab\uff0c\u6709\u7978\u540c\u5f53\uff01\u5e76\u80a9\u5b50\u4e0a\u5427\uff01\u516d\u5408\u5e2e\u5b9a\u7136\u8fd8 \u6709\u540e\u63f4\uff0c\u4e0d\u5fc5\u548c\u5979\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u89c4\u77e9\u4e86\u3002\u201d \u3000\u3000\u8fd9\u56db\u4e2a\u4eba\u5df2\u6a2a\u4e86\u5fc3\uff0c\u4e0d\u60dc\u4e0e\u53f2\u767d\u90b5\u7ed3\u4ec7\uff0c\u4e8e\u662f\u4e00\u62e5\u800c\u4e0a\uff0c\u653b\u5f97\u66f4\u4e3a\u72e0\u8fa3\u3002\u4f46\u89c1\u68d2\u4f3c\u5954 \u96f7\uff0c\u5251\u5982\u9a87\u7535\uff0c\u94a9\u5149\u970d\u970d\uff0c\u97ad\u5f71\u7ffb\u98de\u3002\u53f2\u7ea2\u82f1\u4f7f\u51fa\u4e86\u6d51\u8eab\u89e3\u6570\uff0c\u5140\u662f\u906e\u62e6\u4e0d\u4f4f\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u4e00\u62db\u201c\u674e\u5e7f\u5c04\u77f3\u201d\uff0c\u5251\u5149\u5982\u77e2\uff0c\u5f84\u523a\u524d\u5fc3\uff0c\u53f2\u7ea2\u82f1\u970d\u5730\u4e00\u97ad\u626b\u51fa\uff0c\u867d\u7136\u89e3\u5f00\u4e86\u5bab \u79c9\u85e9\u8fd9\u62db\u6740\u624b\uff0c\u80c1\u4e0b\u5374\u5df2\u9732\u51fa\u7a7a\u95e8\u3002\u5bab\u79c9\u85e9\u62db\u6570\u4e00\u51fa\uff0c\u65e9\u5df2\u6599\u5230\u6709\u6b64\u7ed3\u679c\uff0c\u6210\u7af9\u5728\u80f8\uff0c\u770b\u4e5f \u4e0d\u770b\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u7acb\u5373\u4fbf\u523a\u53f2\u7ea2\u82f1\u80c1\u4e0b\u7684\u6108\u6c14\u7a74\u3002 \u3000\u3000\u773c\u770b\u5c31\u8981\u523a\u7740\u4e86\u7a74\u9053\uff0c\u5bab\u79c9\u85e9\u5ffd\u89c9\u52b2\u98ce\u98d2\u7136\uff0c\u542c\u98ce\u8fa8\u5668\uff0c\u8fd9\u679a\u6697\u5668\u4e5f\u662f\u6253\u4ed6\u80c1\u4e0b\u7684\u6108\u6c14 \u7a74\u3002\u5bab\u79c9\u85e9\u7684\u5251\u672f\u5df2\u5230\u6536\u53d1\u968f\u5fc3\u7684\u5883\u754c\uff0c\u5f53\u4e0b\u4e00\u4e2a\u659c\u8eab\u7ed5\u6b65\uff0c\u56de\u5251\u4e00\u5288\uff0c\u628a\u90a3\u679a\u201c\u6697\u5668\u201d\u5288 \u5f00\u4e24\u534a\uff0c\u5374\u539f\u6765\u662f\u4e00\u9897\u5c0f\u5c0f\u7684\u677e\u5b50\u3002\u5bab\u79c9\u85e9\u5927\u5403\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u662f\u54ea\u4f4d\u9ad8\u4eba\uff0c\u8bf7\u6765\u76f8\u89c1\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u91d1\u9010\u6d41\u5df2\u662f\u4e00\u58f0\u957f\u5578\uff0c\u4ece\u6811\u4e0a\u8df3\u4e0b\uff0c\u964d\u843d\u573a\u5fc3\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u778e\u4e86\u773c\u7684\u5f3a \u76d7\uff0c\u6211\u65e9\u5c31\u5728\u8fd9\u91cc\u4e86\u3002\u4f60\u4eec\u56db\u4e2a\u5927\u7537\u4eba\u6b3a\u4fae\u4e00\u4e2a\u5973\u5b50\uff0c\u6211\u770b\u4e0d\u8fc7\u773c\u3002\u201d \u3000\u3000\u9ad8\u5927\u6210\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u662f\u8981\u8d81\u8fd9\u8d9f\u6d51\u6c34\u7684\u4e86\uff1f\u62a5\u4e0a\u540d\u6765\uff01\u201d\u675c\u5927\u4e1a\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60 \u77a7\u77a7\u4ed6\u624b\u91cc\u62ff\u7740\u7684\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u628a\u7ea2\u6f06\u5323\u5b50\u4e00\u626c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u7ba1\u6211\u662f\u4ec0\u4e48\u4eba\uff0c\u4f60\u4eec\u60f3\u8981\u7684\u662f\u7384\u94c1\u4e0d\u662f\uff1f\u7384\u94c1 \u5728\u6211\u8fd9\u513f\uff01\u53ea\u770b\u4f60\u4eec\u6709\u6ca1\u6709\u672c\u4e8b\u62ff\u53bb\uff1f\u201d \u3000\u3000\u8fd9\u5323\u5b50\u4e00\u626c\uff0c\u767b\u65f6\u628a\u56db\u4eba\u7684\u773c\u5149\u5168\u90e8\u5438\u4f4f\uff0c\u653b\u51fb\u7684\u76ee\u6807\u4e5f\u767b\u65f6\u8f6c\u4e86\u3002\u9ad8\u5927\u6210\u4e00\u58f0\u5927\u543c\uff0c \u9996\u5148\u6251\u4e0a\u3002\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u597d\uff0c\u7384\u94c1\u7ed9\u4f60\uff01\u201d \u3000\u3000\u7384\u94c1\u6709\u767e\u591a\u65a4\u91cd\uff0c\u9ad8\u5927\u6210\u7684\u72fc\u7259\u68d2\u672c\u6765\u662f\u91cd\u5175\u5668\uff0c\u6bd4\u8d77\u7384\u94c1\uff0c\u5982\u53c8\u8f7b\u5f97\u591a\u4e86\uff0c\u72fc\u7259\u68d2\u4e0e \u90a3\u7ea2\u6f06\u5323\u5b50\u4e00\u78b0\uff0c\u53ea\u542c\u5f97\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u72fc\u7259\u68d2\u8131\u624b\u98de\u51fa\uff0c\u5728\u7a7a\u4e2d\u65ad\u4e3a\u4e24\u622a\uff0c\u9ad8\u5927\u6210\u864e\u53e3\u6d41 \u8840\uff0c\u5931\u4e86\u5175\u5668\uff0c\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u7109\u6562\u518d\u6218\uff0c\u8fde\u5fd9\u540e\u9000\u3002 \u3000\u3000\u675c\u5927\u4e1a\u53cc\u94a9\u5e76\u4e3e\uff0c\u614c\u5fd9\u6251\u6765\uff0c\u63a5\u5e94\u5e08\u5144\uff0c\u53f2\u7ea2\u82f1\u4e5f\u6ca1\u95f2\u7740\uff0c\u559d\u9053\uff1a\u201c\u7ed9\u6211\u8eba\u4e0b\uff01\u201d\u675c\u5927 \u4e1a\u5df2\u7ecf\u4ece\u5979\u7684\u8eab\u8fb9\u8dd1\u5f00\uff0c\u4e2d\u95f4\u6709\u4e86\u4e00\u6bb5\u8ddd\u79bb\uff0c\u53f2\u7ea2\u82f1\u97ad\u957f\uff0c\u5229\u4e8e\u8fdc\u653b\u3002\u5530\u7684\u4e00\u97ad\uff0c\u679c\u7136\u6253\u7740 \u4e86\u675c\u5927\u4e1a\u3002\u675c\u5927\u4e1a\u731b\u7684\u4e00\u8df3\uff0c\u8df3\u51fa\u4e86\u97ad\u52bf\u7684\u7b3c\u7f69\uff0c\u53ea\u89c9\u819d\u76d6\u706b\u8fa3\u8fa3\u7684\u4f5c\u75db\uff0c\u539f\u6765\u9aa8\u5934\u5df2\u7ed9\u6253 \u788e\u4e86\u4e00\u5757\u4e86\u3002 \u3000\u3000\u675c\u5927\u4e1a\u6709\u4e00\u8eab\u6a2a\u7ec3\u7684\u5916\u529f\uff0c\u6643\u4e86\u4e24\u6643\uff0c\u5c45\u7136\u6ca1\u6709\u5012\u4e0b\u3002\u4f46\u867d\u6ca1\u5012\u4e0b\uff0c\u4e00\u8db3\u53d7\u4f24\uff0c\u5df2\u662f\u4e0d \u582a\u518d\u6218\u3002\u9ad8\u5927\u6210\u5931\u4e86\u5175\u5668\uff0c\u81ea\u89c9\u65e0\u989c\uff0c\u4e24\u5e08\u5144\u5f1f\u76f8\u4e92\u6400\u6276\uff0c\u4e00\u8df7\u4e00\u62d0\u800c\u53bb\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u6325\u5251\u654c\u4f4f\u53f2\u7ea2\u82f1\u7684\u94f6\u97ad\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u8bc0\uff0c\u91d1\u9010\u6d41\u53c8\u5df2\u627e\u4e0a\u4e86\u90d1\u96c4\u56fe\u4f5c\u5bf9\u624b\uff0c\u90d1 \u96c4\u56fe\u7ec3\u6709\u94c1\u7802\u638c\u7684\u529f\u592b\uff0c\u4ed6\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u907f\u5f00\u4e86\u91d1\u9010\u6d41\u7838\u6765\u7684\u7384\u94c1\uff0c\u6a2a\u638c\u4fbf\u8d70\u3002 \u3000\u3000\u91d1\u9010\u6d41\u89c1\u4ed6\u638c\u5fc3\u5982\u58a8\uff0c\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u7684\u94c1\u7802\u638c\u8fd8\u662f\u6d78\u8fc7\u6bd2\u7684\uff0c\u6211\u82e5\u4e0d\u8ba9\u4f60\u6253\u7740\uff0c \u4f60\u4f1a\u5f53\u4f5c\u6211\u662f\u6015\u4f60\u4e86\uff01\u201d\u6536\u56de\u7384\u94c1\uff0c\u5de6\u638c\u62cd\u51fa\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u90d1\u96c4\u56fe\u638c\u5fc3\u5982\u5272\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u5012 \u7eb5\u51fa\u53bb\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u638c\u5fc3\u5df2\u7a7f\u4e86\u4e00\u5b54\uff0c\u9ed1\u8840\u6c69\u6c69\u800c\u51fa\uff0c\u539f\u6765\u91d1\u9010\u6d41\u5728\u548c\u4ed6\u5bf9\u638c\u7684\u65f6\u5019\uff0c \u53cc\u638c\u4e00\u4ea4\uff0c\u6d88\u53bb\u4e86\u4ed6\u7684\u5185\u529b\uff0c\u7acb\u5373\u5316\u638c\u4e3a\u6307\uff0c\u4f7f\u51fa\u4e86\u201c\u4e00\u6307\u7985\u529f\u201d\uff0c\u5f39\u7834\u4ed6\u7684\u638c\u5fc3\uff0c\u7834\u6389\u4ed6 \u7684\u6bd2\u638c\u529f\u592b\u3002\u90d1\u96c4\u56fe\u867d\u7136\u4e0d\u81f3\u4e8e\u6b8b\u5e9f\uff0c\u4f46\u8981\u518d\u7ec3\u6210\u6d78\u6bd2\u7684\u94c1\u7802\u638c\uff0c\u53ef\u5f97\u53cc\u500d\u7684\u529f\u592b\uff0c\u81f3\u5c11\u4e5f \u8981\u5341\u5e74\u4ee5\u4e0a\u4e86\u3002 \u3000\u3000\u56db\u5927\u9ad8\u624b\uff0c\u4f24\u4e86\u4e09\u4e2a\uff0c\u5269\u4e0b\u7684\u53ea\u6709\u4e00\u4e2a\u5bab\u79c9\u85e9\u4e86\u3002\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4e00\u5ba2\u4e0d\u70e6\u4e8c\u4e3b\uff0c\u53f2\u59d1 \u5a18\uff0c\u4f60\u5c31\u8ba9\u6211\u7ed9\u4f60\u6253\u53d1\u4e86\u5427\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u6536\u4e86\u94f6\u97ad\uff0c\u5b98\u79c9\u85e9\u559d\u58f0\uff1a\u201c\u770b\u5251\uff01\u201d\u5251\u950b\u8f6c\u4e86\u65b9\u5411\uff0c\u5411\u91d1\u9010\u6d41\u523a\u6765\uff0c\u8fd9\u4e00\u5251\u62db\u91cc \u85cf\u62db\uff0c\u5f0f\u4e2d\u5957\u5f0f\uff0c\u786e\u662f\u975e\u540c\u5c0f\u53ef\u3002\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u8ba9\u4f60\u89c1\u8bc6\u89c1\u8bc6\u6211\u7684\u521b\u672f\uff0c\u8f93\u4e86\u4f60\u4e5f\u4e0d \u4f1a\u5fc3\u670d\u3002\u201d \u3000\u3000\u5251\u5149\u4e00\u95ea\uff0c\u94ee\u7684\u4e00\u58f0\uff0c\u91d1\u9010\u6d41\u62d4\u5251\u51fa\u9798\uff0c\u8fdb\u62db\u89e3\u62db\uff0c\u5feb\u5982\u95ea\u7535\uff01\u5bab\u79c9\u85e9\u7684\u957f\u5251\u7ed9\u4ed6\u7c98\u51fa \u4e86\u5916\u95e8\u3002\u91d1\u9010\u6d41\u4f7f\u7684\u662f\u201c\u7c98\u201d\u5b57\u8bc0\uff0c\u8fd9\u662f\u4ee5\u67d4\u514b\u521a\u7684\u5251\u672f\uff0c\u5728\u5251\u672f\u4e2d\u662f\u6700\u96be\u7ec3\u7684\u529f\u5927\u3002\u5bab\u79c9 \u85e9\u5403\u4e86\u4e00\u60ca\uff0c\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u5251\u62db\u7acb\u53d8\uff0c\u53ea\u89c1\u4ed6\u7684\u957f\u5251\u5212\u4e86\u4e00\u4e2a\u5708\u5708\uff0c\u5251\u5149\u597d\u4f3c\u6ce2\u6d6a\u4e00 \u822c\uff0c\u4e00\u5708\u5708\u7684\u63a8\u8fdb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e00\u62db\u9f99\u95e8\u4e09\u53e0\u6d6a\u4f7f\u5f97\u4e5f\u7b97\u4e0d\u574f\u3002\u201d\u58f0\u51fa\u5251\u53d1\uff0c\u5176\u76f4\u5982\u77e2\uff0c\u4f46\u89c1\u4e00\u9053 \u767d\u5149\uff0c\u4ece\u90a3\u4e00\u5708\u5708\u7684\u5251\u5149\u4e4b\u4e2d\u7a7f\u8fdb\uff0c\u5bab\u79c9\u85e9\u8fd9\u4e00\u62db\u201c\u9f99\u95e8\u4e09\u53e0\u6d6a\u201d\u767b\u65f6\u53c8\u7ed9\u91d1\u9010\u6d41\u7834\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u559d\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u4f60\u4e5f\u770b\u770b\u6211\u7684\uff01\u201d\u5530\u5530\u51e0\u5251\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c \u5bab\u79c9\u85e9\u4f7f\u51fa\u6765\u751f\u6240\u5b66\uff0c\u7aed\u529b\u62b5\u6321\uff0c\u4f46\u6bcf\u4e00\u62db\u90fd\u7ed9\u91d1\u9010\u6d41\u62a2\u5360\u5148\u673a\uff0c\u5bab\u79c9\u85e9\u53ea\u6709\u62db\u67b6\u7684\u4efd\u513f\u3002 \u91d1\u9010\u6d41\u5de6\u624b\u63d0\u7740\u91cd\u7269\uff0c\u4ed6\u53c8\u4e0d\u662f\u7528\u7384\u94c1\u52a9\u653b\uff0c\u4ec5\u51ed\u5251\u672f\u53d6\u80dc\uff0c\u4e0d\u7531\u5f97\u5bab\u79c9\u85e9\u4e0d\u6697\u6697\u4f69\u670d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u559d\u9053\uff1a\u201c\u5c0f\u5fc3\u4e86\uff01\u201d\u9752\u94a2\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u5251\u5149\u8fc7\u5904\uff0c\u628a\u5bab\u79c9\u85e9\u5934\u4e0a\u6240\u6234\u7684\u98ce\u5e3d\u524a\u5f00 \u4e24\u534a\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4f1a\u6311\u4eba\u5bb6\u7684\u9762\u7eb1\uff0c\u6211\u4e5f\u4f1a\u524a\u4f60\u7684\u5e3d\u5b50\uff01\u201d \u3000\u3000\u5bab\u79c9\u85e9\u5e73\u751f\u4ee5\u5251\u672f\u81ea\u8d1f\uff0c\u5982\u4eca\u7ed9\u91d1\u9010\u6d41\u5288\u5f00\u4e86\u5934\u4e0a\u7684\u98ce\u5e3d\uff0c\u5934\u76ae\u4e00\u7247\u6c81\u51c9\uff0c\u4e0d\u7531\u5f97\u5012\u62bd \u4e86\u4e00\u53e3\u51b7\u6c14\uff0c\u5012\u7eb5\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff01\u9601\u4e0b\u5251\u672f\u7cbe\u5999\uff0c\u5bab\u67d0\u7518\u62dc\u4e0b\u98ce\u3002 \u8bf7\u9601\u4e0b\u7559\u4e2a\u4e07\u513f\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u662f\u4e2a\u65e0\u540d\u5c0f\u5352\uff0c\u8fde\u5343\u513f\u90fd\u6ca1\u6709\uff0c\u4f55\u51b5\u4e07\u513f\uff1f\u4f60\u60f3\u8981\u62a5\u4ec7\uff0c\u65e5\u540e\u82e5\u7136\u6709 \u7f18\u76f8\u9047\uff0c\u4f60\u968f\u65f6\u78b0\u4e0a\u4e86\u6211\uff0c\u6211\u90fd\u613f\u610f\u9886\u6559\uff0c\u51b3\u4e0d\u63a8\u8f9e\uff01\u201d\u5bab\u79c9\u85e9\u9053\uff1a\u201c\u597d\uff0c\u9601\u4e0b\u65e2\u7136\u4e0d\u5c51\u4e0b \u4ea4\uff0c\u4ece\u4eca\u65e5\u8d77\uff0c\u5bab\u67d0\u7684\u540d\u5b57\u4e5f\u7b97\u662f\u5728\u6c5f\u6e56\u4e0a\u62b9\u53bb\u597d\u5566\u3002\u4e09\u5e74\u540e\uff0c\u5bab\u67d0\u82e5\u6709\u5bf8\u8fdb\uff0c\u5f53\u518d\u627e\u5bfb\u9601 \u4e0b\u3002\u201d\u5bab\u79c9\u85e9\u8fd9\u756a\u8bdd\u7684\u610f\u601d\u5373\u662f\u8981\u5728\u4e09\u5e74\u4e4b\u5185\u9690\u59d3\u57cb\u540d\uff0c\u52e4\u7ec3\u5251\u672f\uff0c\u51c6\u5907\u5728\u4e09\u5e74\u4e4b\u540e\uff0c\u518d\u51fa \u6c5f\u6e56\uff0c\u4e0e\u91d1\u9010\u6d41\u4e00\u51b3\u96cc\u96c4\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u4e00\u8d70\uff0c\u6811\u6797\u91cc\u5c31\u53ea\u6709\u91d1\u9010\u6d41\u548c\u53f2\u7ea2\u82f1\u4e8c\u4eba\u4e86\u3002\u53f2\u7ea2\u82f1\u7ecf\u8fc7\u4e86\u4e00\u573a\u5267\u6218\uff0c\u5a07\u5598\u5401 \u5401\uff0c\u60f3\u8981\u65bd\u5c55\u8f7b\u529f\u907f\u5f00\uff0c\u5df2\u662f\u529b\u6240\u4e0d\u80fd\uff0c\u6b64\u65f6\u5979\u5e90\u5c71\u771f\u76f8\u5df2\u9732\uff0c\u5728\u91d1\u9010\u6d41\u7684\u76ee\u5149\u6ce8\u89c6\u4e4b\u4e0b\uff0c \u8eb2\u53c8\u8eb2\u4e0d\u5f00\uff0c\u4e0d\u7531\u5f97\u5927\u662f\u5c34\u5c2c\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u600e\u4e48\u6837\uff0c\u5982\u4eca\u4f60\u76f8\u4fe1\u6211\u662f\u6709\u8bda\u610f\u9001\u8fd8\u7384\u94c1\u4e86\u5427\u2026\u2026\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u591a\u8c22\u4f60\u62d4\u5251\u76f8\u52a9\uff0c\u4f60\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u6211\u867d\u7136\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f46\u5374\u6709\u4e00\u4f4d\u5171\u540c\u7684\u670b\u53cb\u3002\u6211\u8bf4\u51fa\u4ed6\u7684\u540d\u5b57\uff0c\u6216\u8bb8\u4f60\u5c31 \u4e0d\u4f1a\u89c1\u7591\u6211\u4e86\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u6211\u5012\u8981\u542c\u542c\uff0c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u516d\u5408\u5e2e\u7684\u8bb0\u5ba4\u674e\u6566\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5077\u770b\u53f2\u7ea2\u82f1\u7684\u8868\u60c5\uff0c\u53ea\u89c1\u53f2\u7ea2\u82f1\u4f3c\u662f\u60ca \u559c\u4ea4\u96c6\u7684\u6a21\u6837\uff0c\u4f46\u5374\u5e76\u65e0\u5c11\u5973\u542c\u5f97\u60c5\u90ce\u540d\u5b57\u7684\u90a3\u79cd\u7f9e\u6da9\u795e\u6001\uff0c\u800c\u662f\u843d\u843d\u5927\u65b9\u5730\u8bf4\u9053\uff1a\u201c\u54e6\uff0c \u6211\u660e\u767d\u4e86\uff0c\u83ab\u975e\u4f60\u5c31\u662f\u5728\u5f82\u5f95\u5c71\u4e0a\u6253\u9000\u9752\u7b26\u9053\u4eba\u6551\u4e86\u674e\u6566\u7684\u90a3\u4e2a\u5c0f\u53eb\u5316\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u54e6\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u4f60\u4e5f\u77e5\u9053\u4e86\uff1f\u4e0d\u9519\uff0c\u6211\u6b63\u662f\u90a3\u4e2a\u5c0f\u53eb\u5316\u3002\u4f46\u90a3\u4e00\u6b21\u6211\u53ef\u5e76\u4e0d \u662f\u5b58\u5fc3\u6551\u674e\u6566\u7684\uff0c\u4e0d\u6562\u5c45\u529f\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u548c\u674e\u6566\u4e4b\u95f4\u7684\u4e8b\u60c5\uff0c\u4f60\u5c45\u529f\u4e5f\u597d\uff0c\u4e0d\u5c45\u529f\u4e5f\u597d\uff0c\u4e0e\u6211\u65e0\u5173\u3002\u4f46\u4f60\u4eca \u65e5\u52a9\u6211\u8131\u9669\uff0c\u6211\u603b\u662f\u8981\u591a\u8c22\u4f60\u7684\u3002\u201d\u5fc3\u91cc\u89c9\u5f97\u91d1\u9010\u6d41\u7684\u8bf4\u8bdd\u9887\u662f\u5947\u602a\uff0c\u4e0d\u89e3\u4ed6\u4f55\u4ee5\u4e00\u518d\u8981\u548c \u81ea\u5df1\u63d0\u53ca\u674e\u6566\u3002\u63d0\u53ca\u4e5f\u7f62\u4e86\uff0c\u8fd8\u8981\u548c\u81ea\u5df1\u89e3\u91ca\u4ed6\u7684\u52a8\u673a\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u591a\u8c22\u4e86\u4e00\u6b21\u4e86\uff0c\u4e0d\u5fc5\u518d\u591a\u8c22\u5566\u3002\u4f46\u6709\u5173\u674e\u6566\u7684\u6d88\u606f\uff0c\u4f60\u5374\u8fd8\u6ca1\u6709 \u95ee\u6211\u5462\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u4e0d\u89c9\u53c8\u662f\u4e00\u6014\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u6211\u5c31\u95ee\u4f60\uff0c\u4ed6\u73b0\u5728\u600e\u4e48\u6837\u4e86\uff0c\u8eb2\u5728\u4ec0\u4e48\u5730 \u65b9\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u644a\u5f00\u53cc\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u53ea\u77e5\u9053\u6211\u548c\u4ed6\u5206\u624b\u7684\u65f6\u5019\u4ed6\u662f\u6ca1\u73bb\u592f\u75db\u3002\u81f3\u4e8e\u5176 \u4ed6\u7684\u6d88\u606f\uff0c\u6211\u662f\u4e00\u6982\u4e0d\u77e5\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u5982\u6b64\u86fe\u7709\u7f55\u66fe\u89c1\uff0c\u76f8\u9022\u4e00\u9762\u5df2\u7275\u60c5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u5341\u4e00\u56de\u3000\u5206\u79bb\u6700\u662f\u601c\u5b64\u5f71\u3000\u4e2d\u4f0f\u4f55\u582a\u4f5c\u695a\u56da \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u5341\u4e00\u56de\u3000\u5206\u79bb\u6700\u662f\u601c\u5b64\u5f71\u3000\u4e2d\u4f0f\u4f55\u582a\u4f5c\u695a\u56da \u3000\u3000\u53f2\u7ea2\u82f1\u67f3\u7709\u5fae\u8e59\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u8bf4\u8bdd\u4e03\u98a0\u516b\u5012\uff0c\u83ab\u975e\u662f\u6709\u795e\u7ecf\u75c5\u7684\uff1f\u201d\u4e8e\u662f\u8bf4\u9053\uff1a \u201c\u4f60\u65e2\u7136\u4e0d\u77e5\u9053\u4ed6\u7684\u6d88\u606f\uff0c\u4e3a\u4f55\u8981\u6211\u95ee\u4f60\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u96be\u9053\u4f60\u4e0d\u5173\u5fc3\u674e\u6566\u5417\uff1f\u7167\u60c5\u7406\u800c\u8bba\uff0c\u6211\u4ee5\u4e3a\u4f60\u662f\u5e94\u8be5\u95ee\u6211\u7684\u3002\u81f3\u4e8e\u6211\u77e5 \u4e0d\u77e5\u9053\uff0c\u90a3\u662f\u53e6\u5916\u4e00\u56de\u4e8b\u3002\u4f60\u4e0d\u95ee\u6211\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u7b97\u4f60\u8bf4\u5f97\u6709\u7406\u3002\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u5176\u5b9e\u53f2\u7ea2\u82f1\u5df2\u662f\u6015\u4e86\u91d1\u9010\u6d41 \u7684\u7f20\u5939\u4e0d\u6e05\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u5982\u4f55\uff1f\u4f60\u4e5f\u8ba4\u4e3a\u6211\u8bf4\u5f97\u6709\u7406\u4e86\u5427\uff1f\u5173\u4e8e\u674e\u6566\u4e4b\u4e8b\uff0c\u2026\u2026\u5582\u5582\uff0c\u6211\u8fd8\u6709\u8bdd \u8bf4\u5462\uff0c\u4f60\u600e\u4e48\u5c31\u8981\u8d70\u4e86\uff1f\u4f60\u4e0d\u5173\u5fc3\u674e\u6566\u4e48\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u5173\u5fc3\u674e\u6566\u7684\uff0c\u4f60\u4ee5\u4e3a\u6211\u5e94\u8be5\u95ee\u4f60\uff0c\u6211\u4e5f\u95ee\u8fc7\u4e86\uff0c\u4f60\u65e2\u7136\u4e0d\u77e5 \u9053\uff0c\u90a3\u5c31\u8bf7\u5427\u3002\u6055\u6211\u5c11\u966a\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff0c\u6211\u8fd8\u6709\u8bdd\u8bf4\u5462\uff0c\u6211\u867d\u7136\u4e0d\u77e5\u9053\u674e\u6566\u7684\u4e0b\u843d\uff0c\u4f46\u6211\u5728\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb \u5f88\u591a\uff0c\u6211\u53ef\u4ee5\u5e2e\u5fd9\u4f60\u6253\u542c\u4ed6\u7684\u6d88\u606f\u7684\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4e0d\u7528\u4f60\u8d39\u795e\u4e86\uff0c\u8981\u6253\u542c\u6211\u53ef\u4ee5\u81ea\u5df1\u6253\u542c\u3002\u6211\u548c\u4f60\u53c8\u4e0d\u662f\u4e00\u8def\uff0c\u4f60\u6709\u4e86\u4ec0\u4e48 \u6d88\u606f\uff0c\u8fd8\u8981\u8f97\u8f6c\u6258\u4eba\u6765\u544a\u8bc9\u6211\uff0c\u8fd9\u592a\u9ebb\u70e6\u4e86\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u6b47\u606f\u5df1\u8fc7\uff0c\u4fbf\u8981\u4e0a\u8def\u3002\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4e14\u6162\uff01\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u8fd8\u6709 \u4ec0\u4e48\u8bdd\u8bf4\uff1f\u53ef\u4e0d\u8981\u518d\u8c08\u674e\u6566\u4e86\uff0c\u6211\u6015\u4e86\u4f60\u7684\u54c6\u55e6\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u6014\u4e86\u4e00\u6014\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u6b21\u4e0d\u63d0\u674e\u6566\u4e86\uff0c\u4f46\u4f60\u53ef\u5fd8\u4e86\u4e00\u4ef6\u4e8b\u60c5\u4e86\u3002\u201d\u53f2\u7ea2\u82f1\u76b1\u7709 \u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u5757\u7384\u94c1\u5440\uff0c\u4f60\u5fd8\u8bb0\u5e26\u8d70\u4e86\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u7384\u94c1\u6211\u4e0d\u8981\u4e86\uff0c\u9001\u7ed9\u4f60\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u5c31\u602a\u4e86\uff0c\u4f60\u5343\u65b9\u767e\u8ba1\u8981\u53d6\u8fd9\u5757\u7384\u94c1\uff0c\u8fd9\u53c8\u672c\u6765\u662f\u4f60\u5bb6\u7684\u4e1c\u897f\uff0c\u600e\u4e48\u5ffd\u7136 \u53c8\u4e0d\u8981\u4e86\uff1f\u4f60\u4e0d\u76f8\u4fe1\u6211\u662f\u8bda\u5fc3\u9001\u8fd8\u4f60\u7684\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u76f8\u4fe1\uff0c\u4f46\u6211\u4e5f\u662f\u8bda\u5fc3\u8981\u9001\u7ed9\u4f60\u7684\u3002\u201d \u3000\u3000\u5168\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u53c8\u4e3a\u4f55\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u5509\uff0c\u4f60\u8fd9\u4eba\u771f\u662f\u7ea0\u7f20\u4e0d\u6e05\uff0c\u4f60\u4e00\u5b9a\u8981\u6211\u548c\u4f60\u8bf4\u4e2a\u660e\u767d\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e3a\u4e86\u8fd9\u5757\u7384\u94c1\uff0c\u6211\u81ea\u5df1\u6253\u4e86\u4e00\u67b6\uff0c\u5e2e\u4f60\u53c8\u6253\u4e86\u4e00\u67b6\u3002\u5c31\u770b\u5728\u8fd9\u4e24\u573a\u5927\u6253\u7684 \u4efd\u4e0a\uff0c\u6211\u8bf7\u4f60\u7ed9\u6211\u8bf4\u4e2a\u660e\u767d\u4e5f\u4e0d\u4e3a\u8fc7\u5427\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u90a3\u6211\u662f\u975e\u544a\u8bc9\u4f60\u4e0d\u53ef\u7684\u4e86\u3002\u8fd9\u5757\u7384\u94c1\u662f\u6211\u54e5\u54e5\u8981\u9001\u4e0a\u4eac \u53bb\uff0c\u7ed9\u8428\u603b\u7ba1\u4f5c\u5bff\u793c\u7684\uff0c\u8fd9\u4f60\u5df2\u7ecf\u77e5\u9053\u4e86\u3002\u6211\u4e0d\u613f\u610f\u6211\u7684\u54e5\u54e5\u5df4\u7ed3\u8428\u603b\u7ba1\uff0c\u4f46\u4ed6\u4e0d\u542c\uff0c\u53ea\u597d \u6697\u4e2d\u622a\u7559\u4ed6\u7684\u793c\u7269\uff0c\u4f60\u660e\u767d\u4e86\u5427\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u7684\u7528\u5fc3\u6b63\u662f\u4e0e\u674e\u6566\u4e00\u6837\uff0c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u53c8\u8981\u63d0\u8d77\u4ed6\u4e86\u3002\u201d \u3000\u3000\u8fd9\u6b21\u53f2\u7ea2\u82f1\u5374\u53ea\u662f\u70b9\u4e86\u70b9\u5934\uff0c\u4e00\u526f\u6ee1\u4e0d\u5728\u4e4e\u7684\u795e\u6c14\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u4e32\u73cd\u73e0\u4e5f\u662f \u6211\u5077\u4e86\u6765\u7ed9\u674e\u6566\u7684\uff0c\u4e3a\u7684\u5c31\u662f\u4e0d\u60f3\u6211\u7684\u54e5\u54e5\u5df4\u7ed3\u90a3\u4e2a\u4ec0\u4e48\u8428\u603b\u7ba1\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u54e6\uff0c\u8fd9\u4e48\u8bf4\u6765\u5012\u662f\u4f60\u4e3b\u8c0b\u7684\u4e86\uff0c\u4f60\u4e0d\u6015\u4f60\u7684\u54e5\u54e5\u77e5\u9053\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u77e5\u9053\u54e5\u54e5\u662f\u4f1a\u5927\u53d1\u96f7\u9706\u7684\uff0c\u4f46\u6211\u8fd9\u6837\u505a\u4e5f\u662f\u4e3a\u4e86\u4ed6\u7684\u597d\uff0c\u6211\u60f3\u4ed6\u4f1a\u6162\u6162 \u660e\u767d\u7684\u3002\u6211\u662f\u51c6\u5907\u5728\u5077\u4e86\u8fd9\u5757\u7384\u94c1\u4e4b\u540e\uff0c\u8eb2\u8fc7\u4e00\u4e9b\u65f6\u5019\uff0c\u5f85\u4ed6\u6c14\u5e73\u4e86\u518d\u53bb\u89c1\u4ed6\u3002\u201d\u8bf4\u81f3\u6b64 \u5904\uff0c\u84e6\u5730\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u4e0e\u8fd9\u4eba\u7d20\u6627\u751f\u5e73\uff0c\u4e3a\u4ec0\u4e48\u8981\u628a\u6211\u5fc3\u91cc\u60f3\u505a\u7684\u4e8b\u60c5\u90fd\u544a\u8bc9\u4ed6\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u59d1\u5a18\u5982\u6b64\u82e6\u5fc3\uff0c\u5b9e\u662f\u53ef\u656c\u3002\u53ea\u662f\u8fd9\u5757\u7384\u94c1\u4e43\u662f\u7a00\u4e16\u4e4b\u5b9d\uff0c\u4f60\u7ed9\u4e86\u6211\uff0c\u4e0d\u592a \u53ef\u60dc\u4e86\u4e48\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u867d\u662f\u7a00\u4e16\u4e4b\u5b9d\uff0c\u5bf9\u6211\u5374\u65e0\u751a\u7528\u5904\u3002\u6211\u4e0d\u662f\u4f7f\u5251\u7684\uff0c\u4f55\u5fc5\u5360\u6709\u5b83\uff1f\u4f60\u62ff\u4e86 \u53bb\uff0c\u4ee5\u540e\u53ef\u4ee5\u627e\u4e00\u4e2a\u9ad8\u660e\u7684\u94f8\u5251\u5e08\u7ed9\u4f60\u9020\u4e00\u628a\u5929\u4e0b\u65e0\u53cc\u7684\u5b9d\u5251\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u591a\u8c22\u4e86\uff0c\u4f46\u4f60\u6ca1\u6709\u8fd9\u5757\u7384\u94c1\uff0c\u5374\u600e\u751f\u5411\u4f60\u54e5\u54e5\u4ea4\u4ee3\uff1f\u4f60\u603b\u662f\u8981\u56de\u53bb\u89c1\u4ed6\u7684 \u5440\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u6de1\u6de1\u8bf4\u9053\u3002\u201d\u8fd9\u5c31\u662f\u6211\u7684\u4e8b\u60c5\u4e86\uff0c\u4e0d\u5fc5\u4f60\u66ff\u6211\u64cd\u5fc3\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e5f\u89c9\u5f97\u81ea\u5df1\u95ee\u5f97\u592a\u591a\uff0c\u4e0d\u597d\u610f\u601d\u518d\u95ee\u4e0b\u53bb\u3002\u5f53\u4e0b\u8baa\u8baa\u5730\u62ff\u8d77\u90a3\u7ea2\u6f06\u5323\u5b50\uff0c\u8bf4\u9053\uff1a \u201c\u597d\uff0c\u4f60\u65e2\u7136\u8bda\u5fc3\u9001\u7ed9\u6211\uff0c\u6211\u4e5f\u53ea\u597d\u5374\u4e4b\u4e0d\u606d\uff0c\u53d7\u4e4b\u6709\u6127\u4e86\u3002\u55ef\uff0c\u53f2\u59d1\u5a18\uff0c\u4f60\u4e0a\u54ea\u513f\uff1f\u201d\u91d1 \u9010\u6d41\u672c\u6765\u662f\u4e0d\u60f3\u518d\u95ee\u5979\u7684\u4e86\u3002\u4f46\u5728\u5979\u4e34\u8d70\u4e4b\u65f6\uff0c\u5374\u8fd8\u662f\u7981\u4e0d\u7740\u8981\u518d\u95ee\u4e00\u53e5\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u4e00\u9762\u8d70\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u840d\u6c34\u76f8\u9022\uff0c\u591a\u8c22\u4f60\u62d4\u5251\u76f8\u52a9\u4e4b\u5fb7\uff0c\u6211\u4e5f\u5df2\u7ecf\u62a5\u7b54\u8fc7\u4e86\u3002 \u5404\u8d70\u5404\u7684\uff0c\u6211\u7528\u4e0d\u7740\u77e5\u9053\u4f60\u7684\u884c\u6b62\uff0c\u4f60\u4e5f\u4f55\u987b\u95ee\u6211\u7684\u53bb\u5904\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u78b0\u4e86\u5979\u7684\u9489\u5b50\uff0c\u5927\u662f\u5c34\u5c2c\uff0c\u4e00\u65f6\u95f4\u53e3\u4e0d\u62e9\u8a00\uff0c\u6253\u4e86\u4e2a\u54c8\u54c8\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u9001 \u6211\u8fd9\u5757\u7384\u94c1\u5176\u5b9e\u4e43\u662f\u60f3\u8fd8\u6211\u7684\u4eba\u60c5\u3002\u201d\u53f2\u7ea2\u82f1\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u751f\u5e73\u4e0d\u613f\u53d7\u4eba\u6069\u60e0\u3002\u201d \u91d1\u9010\u6d41\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u5fd8\u8bb0\u4e86\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u5fd8\u8bb0\u4e86 \u8fd9\u5757\u7384\u94c1\u672c\u6765\u662f\u5728\u6211\u7684\u624b\u4e2d\u7684\uff0c\u6211\u82e5\u60f3\u8981\u5b83\uff0c\u4f3c\u4e4e\u7528\u4e0d\u7740\u4f60\u9001\u7ed9\u6211\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u52c3\u7136\u53d8\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u9601\u4e0b\u7684\u5927\u6069\uff0c\u4ee5\u540e\u6211\u5f90\u56fe\u62a5\u7b54\u5c31\u662f\uff01\u4f60\u53ef\u4ee5\u8ba9\u6211\u8d70 \u4e86\u5427\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff01\u2026\u2026\u201d\u53f2\u7ea2\u82f1\u4e0d\u7406\u4ed6\u7684\u8bf4\u8bdd\uff0c\u4e00 \u4e2a\u52b2\u513f\u5730\u8d70\uff0c\u8d70\u5f97\u5df2\u7ecf\u8fdc\u4e86\u3002\u5728\u8fd9\u6837\u60c5\u5f62\u4e4b\u5904\uff0c\u91d1\u9010\u6d41\u82e5\u7136\u518d\u53bb\u8ffd\u5979\uff0c\u5df2\u662f\u8ff9\u8fd1\u65e0\u8d56\u3002\u800c\u4e14 \u53f2\u7ea2\u82f1\u7684\u8f7b\u529f\u4e0e\u4ed6\u76f8\u5dee\u4e0d\u8fdc\uff0c\u91d1\u9010\u6d41\u624b\u4e0a\u63d0\u7740\u767e\u591a\u65a4\u91cd\u7684\u7384\u94c1\uff0c\u5373\u4f7f\u539a\u7740\u9762\u76ae\u53bb\u8ffd\uff0c\u53ea\u6015\u4e5f \u662f\u8ffd\u4e0d\u4e0a\u5979\uff0c\u53ea\u597d\u7f62\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u76ee\u9001\u5979\u7684\u80cc\u5f71\u8d70\u51fa\u4e86\u81ea\u5df1\u7684\u89c6\u7ebf\u4e4b\u4e0b\uff0c\u4e0d\u77e5\u600e\u7684\u5ffd\u6709\u60d8\u7136\u82e5\u5931\u4e4b\u611f\uff01 \u3000\u3000\u91d1\u9010\u6d41\u76ee\u9001\u7740\u53f2\u7ea2\u82f1\u7684\u80cc\u5f71\uff0c\u6e10\u8fdc\u6e10\u9690\uff0c\u7ec8\u4e8e\u770b\u4e0d\u89c1\u4e86\uff0c\u91d1\u9010\u6d41\u5fc3\u91cc\u81ea\u601d\uff1a\u201c\u4e0d\u77e5\u5979\u662f \u53bb\u4ec0\u4e48\u5730\u65b9\uff1f\u6050\u6015\u5c31\u662f\u53bb\u627e\u674e\u6566\u4e86\u5427\u3002\u201d\u5ffd\u5730\u60f3\u8d77\u4e86\u53f2\u7ea2\u82f1\u6240\u8bf4\u7684\u90a3\u51e0\u53e5\u8bdd\uff1a\u201c\u54b1\u4eec\u840d\u6c34\u76f8 \u9022\uff0c\u6211\u7528\u4e0d\u7740\u77e5\u9053\u4f60\u7684\u884c\u6b62\uff0c\u4f60\u4e5f\u4f55\u987b\u77e5\u9053\u6211\u53bb\u4ec0\u4e48\u5730\u65b9\uff01\u201d\u91d1\u9010\u6d41\u4e0d\u89c9\u84e6\u7136\u4e00\u60ca\uff0c\u54d1\u7136\u5931 \u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\u3002\u8fd9\u6b63\u662f\uff1a\u5439\u76b1\u4e00\u6c60\u6625\u6c34\uff0c\u5e72\u537f\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u53ef\u662f\u53f2\u7ea2\u82f1\u7684\u5f71\u5b50\u4ecd\u7136\u76d8\u65cb\u5728\u4ed6\u7684\u8111\u6d77\uff0c\u6325\u4e0d\u53bb\uff0c\u62b9\u4e0d\u6389\u3002\u5979\u90a3\u660e\u8273\u7edd\u4fd7\u7684\u59ff\u5bb9\uff0c\u8d85\u5353 \u4e0d\u51e1\u7684\u672c\u9886\uff0c\u843d\u843d\u5927\u65b9\u7684\u6001\u5ea6\uff0c\u90fd\u7ed9\u91d1\u9010\u6d41\u7684\u5370\u8c61\u662f\u592a\u6df1\u523b\u4e86\uff01\u91d1\u9010\u6d41\u5728\u81ea\u5632\u81ea\u7b11\u4e4b\u540e\uff0c\u81ea \u5df1\u90fd\u4e0d\u7981\u89c9\u5f97\u5947\u602a\u8d77\u6765\uff0c\u5ffd\u7136\u95f4\u4ed6\u53d1\u89c9\u4e86\u81ea\u5df1\u5fc3\u5e95\u7684\u79d8\u5bc6\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u98a4\u6144\uff0c\u6697\u6697\u5403\u60ca\uff1a \u201c\u6211\u521a\u624d\u4e3a\u4ec0\u4e48\u51e0\u6b21\u4e09\u756a\u548c\u5979\u63d0\u8d77\u674e\u6566\uff1f\u54ce\uff0c\u8fd9\u96be\u9053\u4e0d\u662f\u5728\u63a2\u6d4b\u5979\u7684\u60c5\u610f\u4e48\uff1f\u54fc\uff0c\u54fc\uff01\u91d1\u9010 \u6d41\u5440\u91d1\u9010\u6d41\uff0c\u4f60\u662f\u5728\u5992\u5fcc\u674e\u6566\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u53d1\u89c9\u4e86\u81ea\u5df1\u5fc3\u5e95\u7684\u79d8\u5bc6\uff0c\u60d8\u60d8\u524d\u884c\uff01\u81ea\u601d\u81ea\u8d23\uff1a\u201c\u670b\u53cb\u59bb\uff0c\u4e0d\u53ef\u6b3a\u3002\u674e\u6566\u548c\u4f60\u597d \u6b79\u4e5f\u7b97\u5f97\u662f\u4e2a\u670b\u53cb\u4e86\uff0c\u4e3a\u4ec0\u4e48\u4f60\u8001\u662f\u5728\u60f3\u7740\u4ed6\u7684\u610f\u4e2d\u4eba\uff1f\u91d1\u9010\u6d41\u5440\u91d1\u9010\u6d41\uff0c\u4f60\u5e94\u8be5\u505a\u4e2a\u5149\u660e \u78ca\u843d\u7684\u7537\u5b50\u6c49\uff0c\u5fc3\u91cc\u600e\u80fd\u6709\u5bf9\u4e0d\u4f4f\u670b\u53cb\u7684\u5ff5\u5934\uff01\u54ce\u5440\uff0c\u4f60\u7f9e\u4e5f\u4e0d\u7f9e\uff1f\u201d \u3000\u3000\u60f3\u81f3\u6b64\u5904\uff0c\u91d1\u9010\u6d41\u6ee1\u9762\u901a\u7ea2\u3002\u4f46\u8d70\u4e86\u4e00\u4f1a\uff0c\u7ed9\u51b7\u98ce\u4e00\u5439\uff0c\u8111\u888b\u6e05\u9192\u4e86\u4e9b\uff0c\u60f3\u9053\uff1a\u201c\u5018\u82e5 \u8fd9\u4f4d\u53f2\u59d1\u5a18\u5f53\u771f\u662f\u674e\u6566\u7684\u59bb\u5b50\uff0c\u6211\u5f53\u7136\u4e0d\u8be5\u6709\u975e\u4efd\u4e4b\u60f3\u3002\u4e0d\u662f\u59bb\u5b50\uff0c\u5df2\u662f\u60c5\u4eba\uff0c\u6211\u4e5f\u4e0d\u8be5\u63d2 \u8db3\u5176\u95f4\u3002\u53ef\u662f\u770b\u5979\u521a\u624d\u7684\u795e\u6001\uff0c\u5979\u5bf9\u674e\u6566\u53c8\u4f3c\u4e4e\u53ea\u662f\u670b\u53cb\u7684\u5173\u5fc3\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u81ea\u601d\u81ea\u60f3\uff0c\u5bf9\u81ea\u5df1\u6240\u4e0b\u7684\u8fd9\u4e2a\u201c\u5224\u65ad\u201d\uff0c\u81ea\u5df1\u4e5f\u4e0d\u6562\u65ad\u5b9a\u662f\u5bf9\u4e86\u8fd8\u662f\u9519\u4e86\u3002\u5fc3\u91cc \u4e0d\u89c9\u53c8\u5728\u60f3\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\u3002\u8fd9\u662f\u8463\u5341\u4e09\u5a18\u5bf9\u5706\u6d77\u8bf4\u7684\uff0c\u8463\u5341\u4e09\u5a18\u662f\u5979\u54e5\u54e5\u7684\u60c5\u5987\uff0c\u5979\u5f53 \u7136\u4f1a\u77e5\u9053\u4ed6\u4eec\u7684\u79d8\u5bc6\uff0c\u7167\u5979\u7684\u8bf4\u6cd5\uff0c\u4ed6\u4eec\u5df2\u7136\u662f\u60c5\u4fa3\u65e0\u7591\u4e86\u3002\u8fd9\u96be\u9053\u6709\u5047\u5417\uff1f\u800c\u4e14\uff0c\u5979\u80fd\u591f \u628a\u54e5\u54e5\u8981\u9001\u7ed9\u8428\u798f\u9f0e\u7684\u660e\u73e0\u5077\u7ed9\u674e\u6566\uff0c\u5373\u4f7f\u53ea\u662f\u670b\u53cb\uff0c\u8fd9\u4efd\u4ea4\u60c5\u4e5f\u662f\u5f88\u4e0d\u6d45\u4e86\u3002\u91d1\u9010\u6d41\u5440\u91d1 \u9010\u6d41\uff0c\u4f60\u5207\u4e0d\u53ef\u4ee5\u5fc3\u5b58\u6742\u5ff5\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5f3a\u81ea\u538b\u6291\u4e0b\u81ea\u5df1\u5fc3\u4e2d\u7684\u80e1\u601d\u4e71\u60f3\uff0c\u7ee7\u7eed\u884c\u7a0b\u3002\u53ef\u662f\u4ed6\u867d\u7136\u52c9\u5f3a\u6291\u5236\u4e86\u81ea\u5df1\uff0c\u4e0d\u53bb \u518d\u60f3\u53f2\u7ea2\u82f1\u4e86\uff0c\u516d\u5408\u5e2e\u7684\u4e8b\u60c5\uff0c\u4ed6\u5374\u8fd8\u662f\u5728\u60f3\u7740\u7684\u3002 \u3000\u3000\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\u53f2\u767d\u90b5\u8981\u7ed9\u5927\u5185\u603b\u7ba1\u8428\u798f\u9f0e\u8d3a\u5bff\uff0c\u800c\u8428\u798f\u9f0e\u7684\u5bff\u671f\u5c31\u5728\u4e0b\u6708\uff0c\u8ddd\u4eca\u4e0d\u8fc7\u4e00 \u4e2a\u591a\u6708\u4e86\u3002\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u6211\u593a\u4e86\u516d\u5408\u5e2e\u7684\u8d3a\u793c\uff0c\u4e0d\u77e5\u53f2\u767d\u90b5\u8fd8\u4f1a\u4e0d\u4f1a\u53bb\u7ed9\u8428\u798f\u9f0e\u62dc\u5bff\uff0c\u6211 \u5012\u60f3\u53bb\u770b\u4e00\u770b\u3002\u5230\u4e86\u90a3\u5929\uff0c\u6c5f\u6e56\u4e0a\u7684\u8d25\u7c7b\u4e5f\u5fc5\u5b9a\u6709\u8bb8\u591a\u4eba\u53bb\u7ed9\u8428\u798f\u9f0e\u795d\u5bff\u7684\uff0c\u8d81\u8fd9\u4e2a\u673a\u4f1a\uff0c \u6211\u4fbf\u8ba4\u8bc6\u8ba4\u8bc6\u8fd9\u4e9b\u8d25\u7c7b\u5c82\u4e0d\u6b63\u597d\uff1f\u5bf9\uff0c\u5c31\u8fd9\u6837\u529e\uff0c\u8d81\u8fd9\u4e2a\u70ed\u95f9\u5f88\u662f\u503c\u5f97\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u672c\u6765\u662f\u51c6\u5907\u904d\u6e38\u6c5f\u5357\u540d\u80dc\uff0c\u7136\u540e\u624d\u5317\u4e0a\u4eac\u534e\u7684\u3002\u5982\u4eca\u53ea\u6709\u4e00\u4e2a\u591a\u6708\u7684\u65f6\u95f4\uff0c\u5f53\u7136 \u662f\u4e0d\u80fd\u6309\u7167\u539f\u6765\u7684\u8ba1\u5212\u4e86\u3002\u4ed6\u4ece\u82cf\u5dde\u5317\u4e0a\uff0c\u6e38\u4e86\u9547\u6c5f\u7684\u91d1\u5c71\u5bfa\uff0c\u518d\u6298\u800c\u897f\u4e0b\uff0c\u4ece\u5f53\u6d82\u9644\u8fd1\u7684 \u91c7\u77f3\u77f6\u6e21\u6c5f\u3002 \u3000\u3000\u91c7\u77f3\u77f6\u662f\u5357\u6765\u540d\u5c06\u865e\u5141\u4e49\u5927\u7834\u91d1\u5175\u4e4b\u5904\uff0c\u91d1\u9010\u6d41\u9009\u62e9\u6b64\u5730\u6e21\u6c5f\uff0c\u6b63\u662f\u62b1\u7740\u6000\u4e3b\u5e7d\u60c5\uff0c\u60f3 \u51ed\u540a\u5f53\u5e74\u7684\u82f1\u96c4\u4e8b\u8ff9\uff0c\u7528\u4ee5\u6d88\u9664\u81ea\u5df1\u7684\u5fc3\u4e2d\u6742\u5ff5\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b2c\u4e00\u6b21\u6765\u5230\u957f\u6c5f\u4e4b\u6ee8\uff0c\u653e\u773c\u4e00\u671b\uff0c\u53ea\u89c1\u5927\u6c5f\u4e1c\u53bb\uff0c\u6eda\u6eda\u5954\u6d41\uff0c\u9ed8\u5ff5\u82cf\u4e1c\u5761\u7684\u540d \u53e5\uff1a\u201c\u5927\u6c5f\u4e1c\u53bb\uff0c\u6d6a\u6dd8\u5c3d\uff0c\u5343\u53e4\u98ce\u6d41\u4eba\u7269\u3002\u6545\u5792\u897f\u8fb9\uff0c\u4eba\u9053\u662f\uff0c\u4e09\u56fd\u5468\u90ce\u8d64\u58c1\uff0c\u4e71\u77f3\u7a7f\u7a7a\uff0c \u60ca\u6d9b\u62cd\u5cb8\uff0c\u5377\u8d77\u5343\u5806\u96ea\u3002\u6c5f\u5c71\u5982\u753b\uff0c\u4e00\u65f6\u591a\u5c11\u8c6a\u6770\u3002\u201d\u987f\u89c9\u80f8\u895f\u5f00\u9614\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6cbf\u7740\u6c5f\u8fb9\u8d70\u53bb\uff0c\u8d70\u4e86\u8bb8\u4e45\uff0c\u627e\u4e0d\u7740\u6e21\u8239\u3002\u4e0d\u7981\u6709\u70b9\u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u73b0\u5728\u53c8\u4e0d\u662f\u5175 \u8352\u9a6c\u4e71\u7684\u65f6\u5019\uff0c\u600e\u7684\u5374\u6ca1\u6709\u6e21\u8239\uff1f\u201d\u5ffd\u542c\u5f97\u6a79\u58f0\u54bf\u54d1\uff0c\u4e00\u6761\u5c0f\u8239\u4ece\u82a6\u82c7\u4e1b\u4e2d\u6447\u51fa\u6765\uff0c\u8239\u4e0a\u7684 \u68a2\u516c\u95ee\u9053\uff1a\u201c\u5ba2\u4eba\u53ef\u662f\u8981\u6e21\u6c5f\u4e48\uff1f\u201d\u91d1\u9010\u6d41\u559c\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u8feb\u4e0d\u53ca\u5f85\uff0c\u4e0d\u7b49\u90a3\u5c0f\u821f\u9760\u62e2\u5cb8 \u8fb9\uff0c\u4fbf\u8df3\u4e0a\u8239\u53bb\u3002\u201c \u3000\u3000\u91d1\u9010\u6d41\u90a3\u4e2a\u7ea2\u6f06\u5323\u5b50\u5185\u85cf\u7384\u94c1\uff0c\u6709\u767e\u591a\u65a4\u91cd\uff0c\u8df3\u4e0a\u8239\u53bb\uff0c\u5c0f\u8239\u81ea\u4e0d\u514d\u6447\u6643\u8d77\u6765\uff0c\u8239\u5934\u5f80 \u4e0b\u4e00\u6c89\u3002\u8fd9\u68a2\u516c\u662f\u4e2a\u6709\u7ecf\u9a8c\u7684\u8001\u624b\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u9732\u51fa\u8be7\u5f02\u7684\u795e\u60c5\uff1b\u770b\u4e86\u91d1\u9010\u6d41\u4e00\u773c\uff0c\u8bf4 \u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u4f60\u62ff\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u8fd9\u6837\u6c89\u91cd\uff1f\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u603b\u4e0d\u4f1a\u662f\u91d1\u94f6\u73e0\u5b9d\u5c31\u662f\u4e86\u3002 \u4f60\u5acc\u6211\u5e26\u7684\u4e1c\u897f\u91cd\uff0c\u7ed9\u4f60\u52a0\u500d\u7684\u8239\u94b1\u5c31\u662f\u3002\u201d \u3000\u3000\u68a2\u516c\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5012\u65e0\u9700\uff0c\u6211\u5f53\u4f5c\u662f\u591a\u642d\u4e00\u4e2a\u4eba\u7f62\u5566\u3002\u6e21\u6c5f\u4e00\u6b21\uff0c\u4e00\u94b1\u94f6\u5b50\uff0c \u4e00\u4e2a\u4eba\u4e24\u4e2a\u4eba\u90fd\u662f\u4e00\u6837\u3002\u6211\u6015\u7684\u53ea\u662f\u4f60\u5e26\u4e86\u8d35\u91cd\u7684\u4e1c\u897f\uff0c\u82e5\u7136\u5931\u4e8b\uff0c\u6211\u62c5\u5f53\u4e0d\u8d77\u3002\u201d\u91d1\u9010\u6d41 \u9053\uff1a\u201c\u4eca\u65e5\u98ce\u5e73\u6d6a\u9759\uff0c\u6211\u770b\u662f\u4e0d\u4f1a\u5931\u4e8b\u7684\u5427\uff1f\u201d\u90a3\u68a2\u516c\u9053\uff1a\u201c\u5ba2\u5b98\u6709\u6240\u4e0d\u77e5\uff0c\u957f\u6c5f\u4e0a\u65b0\u8fd1\u6765 \u4e86\u4e00\u80a1\u6c34\u5bc7\uff0c\u65f6\u4e0d\u65f6\u6709\u62a2\u63a0\u6c11\u8239\u4e4b\u65f6\u3002\u4f60\u5e26\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u53ef\u4ee5\u8bf4\u7ed9\u6211\u542c\u5417\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u53ea\u6015\u4ed6\u4e0d\u80af\u6e21\u4ed6\u8fc7\u6c5f\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u62c5\u5fc3\uff0c\u51fa\u4e86\u4e8b\u6211\u4e0d\u6028\u4f60\u3002\u6211\u5e26\u7684\u4e1c\u897f\u5f3a\u76d7 \u62a2\u4e86\u4e5f\u6ca1\u6709\u5904\u7684\uff0c\u8bf4\u7ed9\u4f60\u542c\u4f60\u4e5f\u4e0d\u77e5\u9053\u3002\u201d\u91d1\u9010\u6d41\u867d\u7136\u89c9\u5f97\u8fd9\u4e2a\u68a2\u516c\u672a\u514d\u6709\u70b9\u591a\u4e8b\uff0c\u4f46\u4e5f\u53ea \u9053\u4ed6\u662f\u5c0f\u5fc3\u8c28\u614e\uff0c\u5e76\u672a\u7591\u53ca\u5176\u4ed6\uff0c\u6b8a\u4e0d\u5982\u8fd9\u68a2\u516c\u6b63\u662f\u6b32\u64d2\u5148\u7eb5\uff0c\u597d\u8ba9\u91d1\u9010\u6d41\u653e\u5fc3\u642d\u4ed6\u8fd9\u6761\u8239 \u7684\u3002 \u3000\u3000\u8239\u5230\u4e2d\u6d41\uff0c\u91d1\u9010\u6d41\u8c6a\u6c14\u5c3d\u53d1\uff0c\u653e\u58f0\u541f\u9053\uff1a\u201c\u96ea\u6d17\u864f\u5c18\u51c0\uff0c\u98ce\u7ea6\u695a\u4e91\u7559\u3002\u4f55\u4eba\u4e3a\u5199\u60b2\u58ee\uff1f \u5439\u89d2\u65b9\u57ce\u697c\u3002\u6e56\u6d77\u5e73\u751f\u8c6a\u6c14\uff0c\u5173\u585e\u5982\u4eca\u98ce\u666f\uff0c\u526a\u70db\u770b\u5434\u94a9\uff0c\u5269\u559c\u71c3\u7280\u5904\uff0c\u9a87\u6d6a\u4e0e\u5929\u6d6e\u3002\u5fc6\u5f53 \u5e74\uff0c\u5468\u5174\u8c22\uff0c\u5bcc\u6625\u79cb\uff0c\u5c0f\u4e54\u521d\u5ac1\u3002\u9999\u56ca\u672a\u89e3\uff0c\u52cb\u4e1a\u6545\u4f18\u6e38\uff0c\u4ea6\u58c1\u77f6\u5934\u843d\u7167\uff0c\u6ddd\u6c34\u6865\u8fb9\u8870\u8349\uff0c \u6e3a\u6e3a\u5524\u4eba\u6101\u3002\u6211\u6b32\u4e58\u98ce\u53bb\uff0c\u51fb\u696b\u8a93\u4e2d\u6d41\u3002\u201d \u3000\u3000\u8fd9\u662f\u5357\u5b8b\u8bcd\u4eba\u5f20\u4e8e\u6e56\u5199\u7684\u201c\u6c34\u8c03\u6b4c\u5934\u201d\uff0c\u6b63\u662f\u5f53\u5e74\u4ed6\u5728\u91c7\u77f3\u77f6\u6218\u5f79\u4e4b\u540e\uff0c\u5199\u6765\u6b4c\u9882\u865e\u5141 \u6587\u7684\u3002 \u3000\u3000\u6b64\u8bcd\u5199\u5b8b\u519b\u5927\u6377\uff0c\u201c\u96ea\u6d17\u864f\u5c18\u9759\u201d\u4e4b\u540e\uff0c\u51ef\u6b4c\u9ad8\u594f\uff0c\u7b11\u770b\u5434\u94a9\u7684\u666f\u8c61\u4e0e\u8c6a\u60c5\uff0c\u8bcd\u4e2d\u628a\u865e \u5141\u6587\u6bd4\u4f5c\u8d64\u58c1\u7834\u66f9\u7684\u5468\u745c\uff0c\u6ddd\u6c34\u6b7c\u79e6\u7684\u8c22\u7384\uff0c\u540c\u6837\u5efa\u6811\u4e86\u5343\u79cb\u7684\u52cb\u4e1a\u3002\u5c3d\u7ba1\u7269\u6362\u661f\u79fb\uff0c\u6ca7\u6851 \u53d8\u5e7b\uff0c\u201c\u77f6\u5934\u843d\u7167\u201d\uff0c\u201c\u6865\u8fb9\u8870\u8349\u201d\uff0c\u53e4\u4eba\u7684\u82f1\u96c4\u4e8b\u4e1a\u5df2\u6210\u9648\u8ff9\uff0c\u4f46\u4ed6\u4eec\u4ee5\u5f31\u80dc\u5f3a\u7684\u6297\u654c\u7cbe \u795e\u8fd8\u5728\u9f13\u821e\u7740\u4eca\u4eba\u3002\u8bcd\u96c4\u610f\u6df1\uff0c\u4e0d\u6127\u662f\u4e00\u9996\u4f20\u8bf5\u5343\u53e4\u7684\u4f73\u4f5c\u3002 \u3000\u3000\u91d1\u9010\u6d41\u653e\u6b4c\u4e4b\u540e\uff0c\u8fd9\u624d\u53d1\u89c9\u5c0f\u821f\u4f3c\u4e4e\u7f13\u6162\u4e0b\u6765\uff0c\u628a\u773c\u4e00\u770b\uff0c\u53ea\u662f\u90a3\u68a2\u516c\u6b63\u5728\u56de\u8f6c\u5934\u6765\uff0c \u671b\u7740\u81ea\u5df1\uff0c\u4fa7\u770b\u8033\u6735\uff0c\u8fd8\u4f3c\u4e4e\u662f\u5728\u503e\u542c\u7684\u6a21\u6837\u3002\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u8001\u68a2\u516c\uff0c\u4f60\u4e5f\u61c2\u5f97\u8fd9\u9996\u8bcd \u4e48\uff1f\u201d \u3000\u3000\u68a2\u516c\u7b11\u9053\uff1a\u201c\u6211\u53ea\u61c2\u5f97\u6491\u8239\u5212\u8247\uff0c\u54ea\u91cc\u61c2\u5f97\u4ec0\u4e48\u98df\u5440\u2018\u5403\u2019\u5440\u3002\u76f8\u516c\u771f\u597d\u96c5\u5174\uff0c\u6211\u53ea\u6015 \u5f15\u6765\u4e86\u8d3c\u4eba\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6015\u4ec0\u4e48\uff1f\u201d\u8fb9\u8bf4\u8fb9\u62ff\u8d77\u4e86\u5c0f\u51e0\u4e0a\u7684\u8336\u58f6\uff0c\u5374\u627e\u4e0d\u5230\u8336\u676f\u3002 \u3000\u3000\u68a2\u516c\u8bf4\u9053\uff1a\u201c\u8fd9\u58f6\u8336\u662f\u521a\u624d\u51b2\u7684\uff0c\u60f3\u5fc5\u8fd8\u70ed\u3002\u76f8\u516c\u4f60\u53e3\u6e34\u81ea\u5df1\u659f\u5427\u3002\u4f60\u82e5\u4e0d\u5acc\u6bd2\u8336\uff0c\u8bf7 \u7528\u6211\u7684\u8336\u7897\u3002\u201d\u539f\u6765\u8239\u5bb6\u559d\u8336\uff0c\u4e43\u662f\u7528\u996d\u7897\u4ee3\u66ff\u8336\u676f\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6b63\u662f\u611f\u5230\u6709\u70b9\u53e3\u6e34\uff0c\u9042\u62ff\u8d77\u4e86\u90a3\u68a2\u516c\u7684\u996d\u7897\u5012\u8336\uff0c\u7897\u5e95\u4f3c\u4e4e\u6709\u70b9\u8336\u6e0d\uff0c\u91d1\u9010\u6d41\u662f \u4e2a\u968f\u968f\u4fbf\u4fbf\u7684\u4eba\uff0c\u4e0d\u8010\u70e6\u522e\u5374\u90a3\u70b9\u8336\u6e0d\uff0c\u659f\u4e86\u8336\u5c31\u7aef\u8d77\u6765\u559d\u4e86\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u559d\u8336\u7684\u65f6\u5019\uff0c\u5929\u4e0a\u6b63\u522e\u8d77\u4e86\u98ce\uff0c\u90a3\u68a2\u516c\u62d6\u957f\u4e86\u58f0\u97f3\u9053\uff1a\u201c\u54ce\uff0c\u53d8\u5929\u5566\uff01\u201d\u91d1\u9010\u6d41 \u89c1\u4ed6\u4e00\u76f4\u5728\u6ce8\u89c6\u7740\u81ea\u5df1\uff0c\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u76ee\u5149\u66f4\u663e\u5f97\u5f02\u6837\uff0c\u58f0\u97f3\u4e5f\u6709\u4e9b\u6296\u98a4\uff0c\u4e00\u79cd\u65e2\u60ca\u4e14\u559c\u7684 \u5fc3\u60c5\u4ee4\u4eba\u4e00\u542c\u5c31\u611f\u89c9\u5f97\u5230\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u5ff5\u65b9\u52a8\uff0c\u53ea\u542c\u5f97\u90a3\u68a2\u516c\u5df2\u5728\u62cd\u638c\u53eb\u9053\uff1a\u201c\u5012\u4e5f\uff01\u5012\u4e5f\uff01\u201d\u53ef\u662f\u91d1\u9010\u6d41\u5e76\u6ca1\u5012 \u4e0b\uff0c\u800c\u662f\u5728\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u5c31\u662f\u8d3c\u4eba\uff01\u54fc\uff0c\u4f60\u8fd9\u7897\u6bd2\u8336\uff0c\u7109\u80fd\u5bb3\u5f97\u4e86\u6211\uff1f\u201d\u4f38\u51fa\u4e2d\u6307\uff0c \u671d\u6307\u4e00\u7b03\u3002\u4e00\u80a1\u70ed\u817e\u817e\u7684\u6c34\u7ebf\u4ece\u6307\u7aef\u55b7\u5c04\u51fa\u6765\u3002\u539f\u6765\u91d1\u9010\u6d41\u53d1\u89c9\u5f97\u65e9\uff0c\u4e0d\u5f85\u6bd2\u836f\u53d1\u4f5c\uff0c\u4fbf\u4ee5 \u4e0a\u4e58\u7684\u5185\u529f\u628a\u90a3\u7897\u6bd2\u8336\u538b\u6324\u5230\u4e86\u6307\u7aef\u55b7\u5c04\u51fa\u6765\u3002 \u3000\u3000\u68a2\u516c\u4e00\u4e2a\u4fa7\u8eab\uff0c\u624b\u811a\u4ea6\u5df2\u7ed9\u70ed\u8336\u6e85\u7740\uff0c\u706b\u8fa3\u8fa3\u4f5c\u75db\uff0c\u5e78\u800c\u76ae\u80a4\u672a\u7834\uff0c\u4e0d\u81f3\u4e8e\u4e2d\u6bd2\u3002\u68a2\u516c \u970d\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u63d0\u8d77\u94c1\u6868\uff0c\u5411\u91d1\u9010\u6d41\u5f53\u5934\u4fbf\u51fb\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u8d3c\u4eba\uff0c\u4f60\u73b0\u5728\u77e5 \u9053\uff0c\u5df2\u7ecf\u8fdf\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u51ed\u4f60\u8fd9\u70b9\u672c\u9886\uff0c\u8981\u60f3\u5bb3\u6211\uff0c\u90a3\u8fd8\u5dee\u5f97\u5927\u8fdc\uff01\u201d\u4e3e\u8d77\u7ea2\u6f06\u5323\u5b50\u4e00\u683c\uff0c\u5323\u5b50 \u91cc\u662f\u85cf\u7740\u767e\u591a\u65a4\u91cd\u7684\u7384\u94c1\u7684\uff0c\u8fd9\u68a2\u516c\u7109\u80fd\u62b5\u6321\u5f97\u4f4f\uff1f\u5580\u5587\u58f0\u54cd\uff0c\u90a3\u67c4\u5305\u7740\u539a\u539a\u94c1\u76ae\u7684\u6868\u4e5f\u6298 \u65ad\u4e86\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u4e2a\u68a2\u516c\u7684\u672c\u9886\u5374\u4e5f\u4e0d\u5982\u91d1\u9010\u6d41\u60f3\u8c61\u7684\u90a3\u6837\u4e0d\u6d4e\uff0c\u94c1\u6868\u6298\u65ad\uff0c\u4ed6\u5c45\u7136\u6ca1\u7ed9\u9707\u5012\u3002\u4e0d \u8fc7\uff0c\u4ed6\u4e5f\u5f53\u7136\u77e5\u9053\u4e0d\u662f\u91d1\u9010\u6d41\u7684\u5bf9\u624b\u7684\u4e86\u3002 \u3000\u3000\u90a3\u68a2\u516c\u811a\u70b9\u8239\u5934\uff0c\u8eab\u5f62\u98de\u8d77\uff0c\u5728\u98de\u8eab\u8dc3\u8d77\u4e4b\u9645\uff0c\u8fd8\u53cd\u624b\u53d1\u51fa\u4e09\u67c4\u98de\u5200\u3002\u91d1\u9010\u6d41\u821e\u52a8\u90a3\u4e2a \u5323\u5b50\uff0c\u51c6\u5907\u683c\u6253\u98de\u5200\uff0c\u5374\u4e0d\u6599\u90a3\u4e09\u67c4\u98de\u5200\u90fd\u4e0d\u662f\u7528\u6765\u6253\u4ed6\u7684\uff0c\u53ea\u542c\u5f97\u201c\u548b\u5693\u201d\u4e00\u58f0\uff0c\u8239\u4e0a\u7684 \u90a3\u679d\u6845\u6746\u5df2\u662f\u65ad\u4e3a\u4e09\u622a\u3002\u539f\u6765\u8fd9\u68a2\u516c\u660e\u77e5\u98de\u5200\u4f24\u4e0d\u4e86\u91d1\u9010\u6d41\uff0c\u6545\u800c\u53e6\u65bd\u8be1\u8ba1\uff0c\u65a9\u65ad\u8239\u4e0a\u7684\u6845 \u6746\uff0c\u53eb\u8fd9\u53ea\u8239\u65e0\u6cd5\u524d\u8fdb\u3002\u4ed6\u4e09\u67c4\u98de\u5200\uff0c\u540c\u65f6\u65ab\u7740\u6845\u6746\uff0c\u6845\u6746\u65ad\u4e3a\u4e09\u622a\uff0c\u5374\u53ea\u662f\u53d1\u51fa\u4e00\u58f0\u201c\u548b \u5693\u201d\u7684\u58f0\u54cd\uff0c\u4f7f\u5f97\u98de\u5200\u7684\u672c\u9886\u4e5f\u7b97\u5f97\u662f\u5341\u5206\u9ad8\u660e\u7684\u4e86\u3002 \u3000\u3000\u5f85\u5230\u91d1\u9010\u6d41\u53d1\u89c9\uff0c\u8981\u53bb\u6293\u90a3\u68a2\u516c\u4e4b\u65f6\uff0c\u6845\u6746\u5df2\u65ad\uff0c\u68a2\u516c\u4ea6\u5df2\u8df3\u4e0b\u4e86\u6c5f\u4e2d\u3002 \u3000\u3000\u6c5f\u9762\u522e\u8d77\u4e86\u98ce\uff0c\u6c34\u5e73\u5982\u955c\u7684\u6c5f\u5fc3\u767b\u65f6\u6ce2\u7ffb\u6d6a\u6d8c\uff0c\u5c0f\u8239\u4e0a\u7684\u6845\u6746\u5df2\u7ed9\u65a9\u65ad\uff0c\u98ce\u5e06\u5378\u4e86\u4e0b \u6765\uff0c\u8fd9\u53ea\u5c0f\u8239\u5728\u6025\u6d41\u4e2d\u53d8\u5f97\u65e0\u5934\u82cd\u8747\u4f3c\u7684\uff0c\u56e2\u56e2\u6253\u8f6c\u3002 \u3000\u3000\u91d1\u9010\u6d41\u662f\u5728\u6d77\u5c9b\u4e0a\u957f\u5927\u7684\uff0c\u7ecf\u5e38\u51fa\u6d77\u6e38\u73a9\uff0c\u5f53\u7136\u61c2\u5f97\u9a7e\u8239\uff0c\u53ef\u662f\u94c1\u6868\u4ea6\u5df2\u65ad\u4e86\uff0c\u800c\u4e14\u8fde \u65ad\u6868\u4e5f\u7ed9\u90a3\u68a2\u516c\u626b\u4e0b\u6c5f\u4e2d\u53bb\u4e86\uff0c\u5374\u7528\u4ec0\u4e48\u9a7e\u8239\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4eba\u6025\u667a\u751f\uff0c\u4e00\u9762\u7528\u201c\u5343\u65a4\u5760\u201d\u7684\u91cd\u8eab\u6cd5\u5b9a\u7740\u5c0f\u8239\uff0c\u4e00\u9762\u7528\u624b\u4ee3\u6868\uff0c\u5212\u6c34\u524d\u8fdb\u3002\u6b64 \u65f6\u4ed6\u662f\u9006\u6c34\u884c\u821f\u9760\u624b\u638c\u6765\u62e8\u6d6a\u524d\u8fdb\uff0c\u5f53\u7136\u751a\u4e3a\u5403\u529b\uff0c\u4f46\u6bd5\u7adf\u4e5f\u662f\u5728\u7f13\u7f13\u524d\u8fdb\u4e86\u3002 \u3000\u3000\u98ce\u6d9b\u4ea4\u4f5c\u4e4b\u4e2d\u5939\u7740\u201c\u683c\u683c\u201d\u7684\u58f0\u54cd\uff0c\u8fd9\u662f\u6728\u5934\u788e\u88c2\u7684\u58f0\u97f3\u3002\u91d1\u9010\u6d41\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a \u201c\u83ab\u975e\u8fd9\u68a2\u516c\u5728\u6363\u9b3c\uff1f\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u679c\u7136\u4fbf\u770b\u89c1\u8239\u5e95\u88c2\u5f00\u4e00\u6d1e\uff0c\u6c5f\u6c34\u6c69\u6c69\u5192\u4e0a\u8239\u8231\u3002\u539f\u6765\u8fd9 \u68a2\u516c\u7cbe\u901a\u6c34\u6027\uff0c\u662f\u957f\u6c5f\u6709\u540d\u7684\u6c34\u9b3c\uff0c\u4ed6\u679c\u7136\u662f\u4f0f\u5728\u8239\u5e95\u51ff\u8239\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e0d\u52a8\u58f0\u8272\uff0c\u7a81\u7136\u628a\u53cc\u811a\u4e00\u6491\uff0c\u8239\u5934\u6253\u6a2a\u6389\u8f6c\uff0c\u91d1\u9010\u6d41\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u638c\u5c31\u5411\u6c5f\u9762\u51fb \u4e0b\u3002 \u3000\u3000\u8239\u5934\u7a81\u7136\u6389\u8f6c\uff0c\u6f5c\u4f0f\u5728\u8239\u5e95\u7684\u68a2\u516c\u4e00\u65f6\u672a\u6765\u5f97\u53ca\u8ddf\u7740\u8f6c\u8eab\uff0c\u5931\u4e86\u63a9\u62a4\uff0c\u7ed9\u91d1\u9010\u6d41\u7684\u5288\u7a7a \u638c\u529b\u4e00\u9707\uff0c\u767b\u65f6\u4e0d\u7531\u5f97\u4ed6\u4e0d\u5728\u6c34\u5e95\u94bb\u4e86\u51fa\u6765\uff0c\u8eba\u5728\u6c34\u9762\u50cf\u4e00\u6761\u6b7b\u9c7c\u4f3c\u7684\uff0c\u53cc\u773c\u7ffb\u767d\uff0c\u547c\u547c\u5598 \u6c14\uff0c\u8fd8\u5e78\u662f\u9694\u7740\u4e00\u91cd\u6c34\u9762\uff0c\u5426\u5219\u8fd9\u68a2\u516c\u5df2\u662f\u8981\u7ed9\u9707\u5f97\u4e94\u810f\u4ff1\u4f24\u3002 \u3000\u3000\u68a2\u516c\u8eba\u5728\u6c5f\u9762\u4e0a\u4ef0\u6cf3\uff0c\u6b64\u65f6\u4ed6\u5df2\u662f\u5934\u6655\u76ee\u7729\uff0c\u56db\u80a2\u65e0\u529b\uff0c\u4f46\u6c42\u80fd\u591f\u9003\u751f\u5df2\u662f\u4e07\u5e78\uff0c\u5f53\u7136 \u662f\u4e0d\u80fd\u518d\u53bb\u5f04\u6c89\u91d1\u9010\u6d41\u8fd9\u6761\u5c0f\u8239\u4e86\u3002\u4ef0\u6cf3\u53ef\u4ee5\u8f83\u5c11\u7528\u529b\uff0c\u4f46\u5728\u72c2\u6d9b\u9a8f\u6d6a\u4e4b\u4e2d\uff0c\u8fd9\u68a2\u516c\u4e5f\u53ea\u6709 \u5728\u98ce\u6d6a\u4e2d\u6323\u624e\u7684\u4efd\u513f\uff0c\u773c\u770b\u662f\u652f\u6301\u4e0d\u4e86\u591a\u4e45\u5c31\u8981\u60e8\u906d\u706d\u9876\u4e4b\u7978\u7684\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u53eb\u505a\u5bb3\u4eba\u4e0d\u6210\u53cd\u5bb3\u5df1\uff0c\u563f\uff0c\u563f\u3001\u4f60\u51c6\u5907\u53bb\u8d74\u9f99\u738b\u7237\u7237\u7684\u7ea6\u4f1a \u5427\uff01\u201d\u4f46\u5728\u72c2\u6d9b\u9a87\u6d6a\u4e4b\u4e2d\uff0c\u91d1\u9010\u6d41\u8fd9\u53ea\u5c0f\u8239\u5df2\u7ecf\u6e17\u6c34\uff0c\u88c2\u53e3\u8fd8\u5728\u6269\u5927\uff0c\u773c\u770b\u4e5f\u662f\u652f\u6301\u4e0d\u4e86\u591a \u4e45\u7684\u4e86\u3002\u91d1\u9010\u6d41\u672c\u6765\u53ef\u4ee5\u8df3\u6c34\u9003\u751f\uff0c\u4f46\u5374\u6ca1\u6709\u628a\u63e1\u6e38\u5f97\u8fc7\u957f\u6c5f\uff0c\u800c\u4e14\u4ed6\u4e5f\u820d\u4e0d\u5f97\u90a3\u5757\u7384\u94c1\u3002 \u3000\u3000\u98ce\u72c2\u6d6a\u5927\uff0c\u91d1\u9010\u6d41\u7a7a\u7740\u53cc\u624b\u90fd\u6ca1\u628a\u63e1\u6e38\u8fc7\u957f\u6c5f\uff0c\u5e26\u7740\u6c89\u91cd\u7684\u7384\u94c1\uff0c\u5f53\u7136\u66f4\u662f\u6e38\u4e0d\u8fc7\u53bb\u7684 \u4e86\u3002\u8fd9\u7384\u94c1\u4e43\u662f\u7a00\u4e16\u4e4b\u73cd\uff0c\u82e5\u7136\u4efb\u5b83\u6c89\u57cb\u6c5f\u5e95\uff0c\u91d1\u9010\u6d41\u53c8\u4e0d\u820d\u5f97\u3002 \u3000\u3000\u6b63\u81ea\u8e0c\u8e87\u4e0d\u51b3\uff0c\u5ffd\u89c1\u4e00\u8258\u5927\u8239\u987a\u6d41\u800c\u4e0b\uff0c\u75be\u5982\u5954\u9a6c\uff0c\u91d1\u9010\u6d41\u751f\u6015\u9519\u8fc7\uff0c\u8fde\u5fd9\u8df3\u51fa\u8239\u5934\uff0c \u6325\u821e\u53cc\u624b\u53eb\u9053\uff1a\u201c\u6551\u4eba\u5440\uff01\u201d\u5c31\u5728\u8fd9\u65f6\uff0c\u90a3\u8eba\u5728\u6c34\u9762\u4ef0\u6cf3\u7684\u68a2\u516c\u4e5f\u53d1\u51fa\u4e86\u4e00\u58f0\u6697\u54d1\u7684\u53eb\u558a\u3002 \u3000\u3000\u90a3\u8258\u5927\u8239\u7f13\u6162\u4e0b\u6765\uff0c\u8239\u5934\u4e0a\u7ad9\u51fa\u4e00\u4e2a\u7c97\u8c6a\u7684\u6c49\u5b50\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u8981\u6551 \u4eba\uff01\u201d\u628a\u4e00\u6761\u7ef3\u5b50\u629b\u51fa\uff0c\u8db3\u6709\u56db\u4e94\u4e08\u957f\uff0c\u521a\u597d\u626b\u5230\u90a3\u68a2\u516c\u7684\u8eab\u65c1\uff0c\u68a2\u516c\u4e00\u6293\u6293\u7740\u7ef3\u7d22\uff0c\u90a3\u7c97 \u8c6a\u7684\u6c49\u5b50\u559d\u9053\uff1a\u201c\u8d77\uff01\u201d\u957f\u7ef3\u4e00\u6536\uff0c\u628a\u90a3\u4e2a\u68a2\u516c\u626f\u4e0a\u4e86\u5927\u8239\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6014\u4e86\u4e00\u6014\uff0c\u53eb\u9053\uff1a\u201c\u8fd9\u4e2a\u662f\u8d3c\u4eba\uff0c\u6211\u662f\u7ed9\u4ed6\u5bb3\u7684\uff0c\u5feb\u6765\u6551\u6211\uff01\u201d\u90a3\u7c97\u8c6a\u6c49\u5b50\u653e\u4e0b \u4e86\u68a2\u516c\uff0c\u53c8\u662f\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c11\u5b89\u6bcb\u8e81\uff0c\u6211\u5c31\u6765\u6551\u4f60\u4e86\u3002\u54c8\u54c8\uff0c\u51b2\u770b\u4f60\u8fd9\u5757\u7384\u94c1\uff0c\u6211\u8fd8 \u80fd\u4e0d\u6551\u4f60\u5417\uff1f\u201d \u3000\u3000\u7c97\u8c6a\u6c49\u5b50\u628a\u624b\u4e00\u6325\uff0c\u84e6\u7136\u95f4\u53ea\u542c\u5f97\u567c\u556a\u8fde\u58f0\uff0c\u706b\u86c7\u98de\u821e\uff0c\u5728\u8fd9\u5927\u8239\u4e0a\u4e00\u6280\u63a5\u7740\u4e00\u679d\u7684\u706b \u7bad\u5c04\u4e86\u51fa\u6765\uff0c\u6bcf\u4e00\u679d\u706b\u7bad\uff0c\u90fd\u662f\u5c04\u4e0a\u4e86\u91d1\u9010\u6d41\u7684\u8fd9\u8239\u5c0f\u8239\u3002\u91d1\u9010\u6d41\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u624d\u77e5\u9053\u5927\u8239 \u4e0a\u7684\u4eba\u548c\u8fd9\u68a2\u516c\u662f\u4e00\u4f19\u7684\u3002\u4ed6\u5411\u4ed6\u4eec\u6c42\u6551\uff0c\u6b63\u597d\u7ed9\u4e86\u4ed6\u4eec\u4ee5\u843d\u4e95\u4e0b\u77f3\u7684\u673a\u4f1a\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6253\u843d\u4e86\u51e0\u679d\u8fce\u9762\u5c04\u6765\u7684\u706b\u7bad\uff0c\u4f46\u603b\u4e0d\u80fd\u6253\u843d\u6240\u6709\u5c04\u6765\u7684\u706b\u7bad\u3002\u5e26\u7740\u718a\u718a\u706b\u7130\u7684\u86c7 \u7130\u7bad\u843d\u5728\u8239\u677f\u4e0a\uff0c\u843d\u5728\u90a3\u5f20\u5378\u4e0b\u7684\u98ce\u5e06\u4e0a\uff0c\u751a\u81f3\u8fd8\u6709\u4e24\u679d\u3002\u706b\u7bad\u5c04\u8fdb\u4e86\u8239\u8231\u3002\u91d1\u9010\u6d41\u6251\u706d\u5f97 \u4e86\u4e1c\u9762\u7684\u706b\u5934\uff0c\u6251\u706d\u4e0d\u4e86\u897f\u9762\u7684\u706b\u5934\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u8fd9\u8258\u5c0f\u8239\u5df2\u662f\u71c3\u70e7\u8d77\u6765\u3002 \u3000\u3000\u8239\u5e95\u7684\u90a3\u4e2a\u6f0f\u6d1e\u4e5f\u6b63\u5728\u6269\u5927\uff0c\u91d1\u9010\u6d41\u5fd9\u4e8e\u6551\u706b\uff0c\u987e\u4e0d\u5f97\u5835\u585e\u6f0f\u6d1e\uff0c\u8f6c\u773c\u95f4\u8239\u8231\u91cc\u7684\u6c34\u5df2 \u7ecf\u6d78\u8fc7\u4e86\u4ed6\u7684\u819d\u76d6\uff0c\u6c34\u706b\u5939\u653b\u4e4b\u4e0b\uff0c\u91d1\u9010\u6d41\u9664\u4e86\u5f03\u821f\u800c\u9003\u4e4b\u5916\uff0c\u662f\u518d\u4e5f\u6ca1\u6709\u522b\u7684\u529e\u6cd5\u4e86\u3002 \u3000\u3000\u90a3\u8258\u5927\u8239\u548c\u91d1\u9010\u6d41\u8fd9\u8258\u5c0f\u8239\u7684\u8ddd\u79bb\u5728\u5341\u4e08\u5f00\u5916\uff0c\u91d1\u9010\u6d41\u82e5\u662f\u6e38\u6cf3\u8fc7\u53bb\uff0c\u53ea\u6015\u672a\u5230\u4e2d\u9014\uff0c \u5c31\u8981\u7ed9\u8239\u4e0a\u7684\u4e71\u7bad\u5c04\u6b7b\u3002 \u3000\u3000\u597d\u4e2a\u91d1\u9010\u6d41\uff0c\u5f53\u771f\u662f\u827a\u9ad8\u80c6\u5927\uff0c\u5728\u8fd9\u751f\u6b7b\u5b58\u4ea1\u4e4b\u9645\uff0c\u7a81\u7136\u7ed9\u4ed6\u60f3\u5230\u4e86\u4e00\u4e2a\u6781\u4e3a\u5192\u9669\u7684\u4e3b \u610f\uff0c\u4ed6\u8981\u4ed7\u7740\u81ea\u5df1\u5353\u7edd\u7684\u8f7b\u529f\uff0c\u593a\u90a3\u8258\u5927\u8239\uff01 \u3000\u3000\u91d1\u9010\u6d41\u63d0\u8d77\u4e86\u90a3\u4e2a\u7ea2\u6f06\u5323\u5b50\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8fc7\u662f\u60f3\u8981\u8fd9\u5757\u7384\u94c1\u800c\u5df2\uff0c\u8fd9\u4e2a\u5bb9\u6613\uff0c\u7ed9 \u4f60\u5c31\u662f\uff01\u201d\u632f\u81c2\u4e00\u629b\uff0c\u90a3\u4e2a\u5323\u5b50\u5e26\u7740\u547c\u547c\u98ce\u54cd\uff0c\u5411\u5927\u8239\u4e0a\u7ad9\u5728\u8239\u5934\u7684\u90a3\u4e2a\u7c97\u8c6a\u6c49\u5b50\u8fce\u9762\u98de \u53bb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5de6\u624b\u629b\u51fa\u5323\u5b50\uff0c\u53f3\u624b\u5df2\u662f\u62c6\u4e0b\u4e86\u4e00\u5757\u8239\u677f\uff0c\u90a3\u5757\u8239\u677f\u5219\u629b\u4e0b\u6c5f\u4e2d\u3002 \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u662f\u8981\u5229\u7528\u8fd9\u5757\u8239\u677f\u4f5c\u4e3a\u8e0f\u811a\u677f\uff0c\u4ee5\u4fbf\u8df3\u4e0a\u90a3\u8258\u5927\u8239\u7684\u3002\u4e24\u8239\u4e4b\u95f4\u7684\u8ddd\u79bb\u6709\u5341 \u591a\u4e08\uff0c\u91d1\u9010\u6d41\u7684\u8f7b\u529f\u518d\u597d\uff0c\u4e5f\u5fc5\u987b\u5206\u4f5c\u4e24\u6b21\uff0c\u624d\u80fd\u8df3\u4e0a\u90a3\u8258\u5927\u8239\u3002 \u3000\u3000\u5927\u8239\u4e0a\u90a3\u4e2a\u7c97\u8c6a\u6c49\u5b50\u53cc\u81c2\u4e00\u4f38\uff0c\u63a5\u4e0b\u4e86\u91d1\u9010\u6d41\u629b\u8fc7\u6765\u7684\u7384\u94c1\uff0c\u7b11\u9053\uff1a\u201c\u597d\uff0c\u591a\u8c22\u4f60 \u4e86\uff01\u201d\u7b11\u58f0\u672a\u5df2\uff0c\u8e6c\u3001\u8e6c\u3001\u8e6c\u7684\u5c31\u63a5\u8fde\u9000\u4e86\u51e0\u6b65\u3002\u8239\u8231\u91cc\u6709\u4e2a\u4eba\u51fa\u6765\uff0c\u53cc\u638c\u62b5\u7740\u4ed6\u7684\u80cc\u5fc3\uff0c \u4ed6\u624d\u5e78\u514d\u8dcc\u5012\u3002\u4e0d\u8fc7\uff0c\u4ed6\u6bd5\u7adf\u8fd8\u662f\u628a\u8fd9\u5757\u6c89\u91cd\u7684\u7384\u94c1\u63a5\u4e0b\u4e86\uff0c\u800c\u4e14\u5e76\u6ca1\u6709\u53d7\u4f24\uff0c\u8db3\u89c1\u529f\u529b\u4e4b \u9ad8\uff0c\u6bd4\u91d1\u9010\u6d41\u4e5f\u5e76\u6ca1\u76f8\u5dee\u591a\u5c11\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u91d1\u9010\u6d41\u4ece\u5c0f\u8239\u4e0a\u817e\u8eab\u98de\u8001\uff0c\u4f0a\u5982\u63a0\u6ce2\u6d77\u71d5\uff0c\u5feb\u5f97\u96be\u4ee5\u5f62\u5bb9\u3002\u90a3\u5757\u822c\u677f\u521a\u521a\u843d \u4e0b\u6c5f\u5fc3\uff0c\u4ed6\u4ea6\u5df2\u662f\u8ddf\u7740\u843d\u4e0b\u3002\u8239\u677f\u8fd8\u6ca1\u6709\u7ed9\u6ce2\u6d6a\u5377\u53bb\uff0c\u53ea\u89c1\u4ed6\u7684\u811a\u5c16\u8f7b\u8f7b\u4e00\u70b9\uff0c\u767b\u65f6\u53c8\u4f3c\u76ae \u7403\u822c\u5f39\u8d77\u6765\uff0c\u8eab\u5f62\u5982\u7bad\uff0c\u6251\u4e0a\u5927\u8239\uff01 \u3000\u3000\u8239\u8231\u91cc\u6709\u4e2a\u4eba\u62a2\u51fa\u8239\u5934\uff0c\u5c31\u662f\u521a\u624d\u7528\u53cc\u638c\u62b5\u4f4f\u90a3\u7c97\u8c6a\u6c49\u5b50\u80cc\u5fc3\u7684\u90a3\u4e2a\u4eba\uff0c\u62a2\u51fa\u8239\u5934\uff0c\u731b \u5730\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u539f\u6765\u662f\u4f60\uff01\u4e0b\u53bb\u5427\uff01\u201d\u8fd9\u4e2a\u4eba\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u5728\u6c5f\u6d77\u5929\u5ac1\u5973\u90a3\u5929\uff0c\u66fe\u8d25 \u5728\u91d1\u9010\u6d41\u624b\u4e0b\u7684\u90a3\u4e2a\u6587\u9053\u5e84\u3002 \u3000\u3000\u6587\u9053\u5e84\u90a3\u6b21\u8d25\u7ed9\u91d1\u9010\u6d41\uff0c\u662f\u8d25\u5728\u62db\u6570\u4e0d\u5982\uff0c\u82e5\u8bba\u529f\u529b\uff0c\u4ed6\u8fd8\u7a0d\u7a0d\u5728\u91d1\u9010\u6d41\u4e4b\u4e0a\u3002\u6b64\u65f6\u91d1 \u9010\u6d41\u8eab\u5b50\u60ac\u7a7a\uff0c\u811a\u5c16\u8fd8\u672a\u66fe\u70b9\u7740\u8239\u5934\uff0c\u6587\u9053\u5e84\u5df2\u662f\u4f7f\u51fa\u201c\u4e09\u8c61\u795e\u529f\u201d\uff0c\u53cc\u638c\u5e76\u63a8\uff0c\u8981\u628a\u91d1\u9010 \u6d41\u4ece\u534a\u7a7a\u51fb\u843d\uff0c\u63a8\u4e0b\u957f\u6c5f\uff01 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u534a\u7a7a\u4e2d\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u53cc\u81c2\u659c\u5206\uff0c\u604d\u5982\u96c1\u7fc5\uff0c\u53f3\u638c\u9a88\u6307\u5982 \u621f\uff0c\u6233\u5411\u6587\u9053\u5e84\u989d\u89d2\u7684\u592a\u9633\u7a74\uff0c\u5de6\u638c\u5982\u5200\uff0c\u7528\u7684\u5219\u662f\u4e2a\u201c\u5288\u201d\u5b57\u8bc0\u5f84\u5411\u6587\u9053\u5e84\u7684\u7435\u7436\u9aa8\u5288\u4e0b \u6765\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u4e24\u5f0f\u4e43\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u4e5f\u6b63\u662f\u4e24\u8d25\u4ff1\u4f24\u7684\u6253\u6cd5\u3002\u5018\u82e5\u662f\u5728\u5e73\u5730\u4e0a\u5355\u6253\u72ec\u6597\uff0c\u6587 \u9053\u5e84\u8fd8\u5f53\u771f\u4e0d\u6562\u548c\u4ed6\u786c\u62fc\uff0c\u975e\u5f97\u95ea\u907f\u4e0d\u53ef\uff0c\u53ef\u662f\u6b64\u65f6\u91d1\u9010\u6d41\u8eab\u5b50\u60ac\u7a7a\uff0c\u6587\u9053\u5e84\u5360\u4e86\u4ee5\u9759\u5236\u52a8 \u7684\u4fbf\u5b9c\u3002\u53ea\u8981\u6321\u5f97\u4e00\u62db\uff0c\u4e0d\u8ba9\u91d1\u9010\u843d\u4e0b\u8239\u5934\uff0c\u5c31\u53ef\u4ee5\u5c06\u4ed6\u6324\u4e0b\u6c5f\u53bb\u3002\u4e8c\u6765\u6587\u9053\u5e84\u5e76\u975e\u5355\u6253\u72ec \u6597\uff0c\u4ed6\u8fd8\u6709\u90a3\u4e2a\u76d7\u9b41\u5e2e\u5fd9\u3002\u6587\u9053\u5e84\u80dc\u7b97\u5728\u63e1\uff0c\u4e8e\u662f\u4e5f\u5c31\u4e00\u6b65\u4e0d\u8ba9\u4e86\u3002 \u3000\u3000\u90a3\u76d7\u9b41\u63a5\u4e0b\u4e86\u7ea2\u6f06\u5323\u5b50\uff0c\u9000\u540e\u4e09\u6b65\uff0c\u7a33\u6b65\u8eab\u5f62\uff0c\u7acb\u5373\u9000\u800c\u590d\u4e0a\uff0c\u6b63\u597d\u8fce\u7740\u6251\u4e0a\u8239\u6765\u7684\u91d1 \u9010\u6d41\u3002\u76d7\u9b41\u5c31\u7528\u8fd9\u4e2a\u88c5\u7740\u7384\u94c1\u7684\u5323\u5b50\u4f5c\u4e3a\u6b66\u5668\uff0c\u5411\u91d1\u9010\u6d41\u731b\u51fb\u3002 \u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u91d1\u9010\u6d41\u5c31\u4e0d\u80fd\u7528\u53cc\u624b\u90fd\u5bf9\u4ed8\u6587\u9053\u5e84\u4e86\uff0c\u767e\u5fd9\u4e2d\u4ed6\u53ea\u597d\u8fc5\u901f\u53d8\u62db\uff0c\u53cc\u638c\u659c\u5206\uff0c \u5206\u654c\u4e8c\u4eba\u3002 \u3000\u3000\u6587\u9053\u5e84\u7684\u529f\u529b\u672c\u6765\u5c31\u662f\u7a0d\u7a0d\u5728\u91d1\u9010\u6d41\u4e4b\u4e0a\uff0c\u91d1\u9010\u6d41\u4ee5\u4e00\u638c\u4e4b\u529b\u654c\u4ed6\u53cc\u638c\uff0c\u81ea\u662f\u62b5\u654c\u4e0d \u4f4f\uff0c\u4f55\u51b5\u8fd8\u6709\u90a3\u4e2a\u76d7\u9b41\uff0c\u529f\u529b\u4e0e\u91d1\u9010\u6d41\u76f8\u5dee\u4e0d\u4e86\u591a\u5c11\uff0c\u800c\u4e14\u4ed6\u7528\u4f5c\u6b66\u5668\u6253\u6765\u7684\u90a3\u5757\u7384\u94c1\uff0c\u53c8 \u662f\u6c89\u91cd\u975e\u5e38\u3002\u53ea\u542c\u5f97\u4e24\u58f0\u95f7\u96f7\u4e5f\u4f3c\u7684\u58f0\u54cd\uff0c\u91d1\u9010\u6d41\u5728\u4e24\u5927\u9ad8\u624b\u5939\u51fb\u4e4b\u4e0b\uff0c\u4fbf\u4f3c\u65ad\u4e86\u7ebf\u7684\u98ce\u7b5d \u4f3c\u7684\uff0c\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7ffb\u8eab\uff0c\u8dcc\u4e0b\u4e86\u957f\u6c5f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u867d\u7136\u7cbe\u901a\u6c34\u6027\uff0c\u4f46\u56e0\u540c\u65f6\u906d\u53d7\u4e24\u5927\u9ad8\u624b\u7684\u638c\u529b\u6240\u51fb\uff0c\u8dcc\u4e0b\u6c5f\u4e2d\uff0c\u53c8\u53d7\u6ce2\u6d6a\u51b2\u51fb\uff0c \u9976\u662f\u4ed6\u5185\u529f\u6df1\u539a\uff0c\u672a\u53d7\u5185\u4f24\uff0c\u4ed6\u5df2\u662f\u5dee\u4e0d\u591a\u5c31\u8981\u660f\u8ff7\u4e86\u3002 \u3000\u3000\u8ff7\u8ff7\u7cca\u7cca\u4e2d\u91d1\u9010\u6d41\u9690\u9690\u542c\u5f97\u6709\u201c\u6251\u901a\u201d\u201c\u6251\u901a\u201d\u5730\u8df3\u6c34\u58f0\uff0c\u60f3\u662f\u90a3\u8239\u4e0a\u7684\u4eba\u8df3\u4e0b\u6765\u6349 \u4ed6\u3002\u91d1\u9010\u6d41\u5f3a\u81ea\u95ed\u4e86\u6c14\uff0c\u62d4\u51fa\u5251\u6765\u5728\u6c34\u4e2d\u4e71\u821e\u3002\u90a3\u76d7\u9b41\u5728\u8239\u4e0a\u559d\u9053\u3002\u201d\u8ba9\u8fd9\u5c0f\u5b50\u591a\u704c\u51e0\u53e3 \u6c34\uff0c\u6162\u6162\u7684\u6d88\u9063\u4ed6\uff01\u201d\u91d1\u9010\u6d41\u7b4b\u75b2\u529b\u7aed\uff0c\u867d\u7136\u5df2\u662f\u6781\u529b\u5fcd\u8010\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u5f20\u5f00\u53e3\u900f\u6c14\uff0c\u679c\u7136\u5728 \u559d\u4e86\u51e0\u53e3\u6c34\u4e4b\u540e\uff0c\u4fbf\u5373\u660f\u8ff7\u8fc7\u53bb\uff0c\u4e0d\u7701\u4eba\u4e8b\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u91d1\u9010\u6d41\u60a0\u60a0\u9192\u8f6c\uff0c\u773c\u524d\u4e00\u7247\u6f06\u9ed1\uff0c\u4f38\u624b\u6478\u7d22\uff0c\u6478\u7740\u4e86\u51b0\u51b7\u7684\u77f3\u58c1\uff0c\u8fd9 \u624d\u53d1\u89c9\u81ea\u5df1\u5df2\u662f\u88ab\u56da\u5728\u4e00\u95f4\u77f3\u5ba4\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5b9a\u4e86\u5b9a\u795e\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5947\u602a\uff0c\u4ed6\u4eec\u7adf\u6ca1\u6709\u7ed9\u6211\u52a0\u4e0a\u9563\u94d0\u3002\u201d\u8033\u6735\u8d34\u5728\u5899\u4e0a\u4e00 \u542c\uff0c\u9690\u9690\u542c\u5f97\u5916\u9762\u6709\u811a\u6b65\u58f0\u8d70\u6765\u8d70\u53bb\uff01\u201c\u60f3\u5fc5\u5c31\u662f\u770b\u5b88\u4ed6\u7684\u532a\u5f92\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u4e14\u5f85\u6211\u517b\u8db3\u4e86\u6c14\u529b\u518d\u8bf4\u3002\u201d\u4ed6\u611f\u89c9\u56db\u80a2\u65e0\u529b\uff0c\u53ea\u9053\u662f\u75b2\u52b3\u672a\u66fe\u6d88\u9664\u7684\u7f18 \u6545\uff0c\u4e0d\u6599\u5750\u4e0b\u6765\u8bd5\u4e00\u8fd0\u6c14\uff0c\u53ea\u89c9\u8179\u4e2d\u4f3c\u662f\u7a7a\u7a7a\u8361\u8361\uff0c\u771f\u6c14\u7adf\u662f\u65e0\u6cd5\u8fd0\u884c\u3002\u91d1\u9010\u6d41\u8fd9\u4e00\u60ca\u975e\u540c \u5c0f\u52a8\uff0c\u4ed6\u672c\u6765\u662f\u51c6\u5907\u517b\u8db3\u6c14\u529b\u4e4b\u540e\uff0c\u4ed7\u7740\u81ea\u5df1\u6df1\u539a\u7684\u5185\u529f\uff0c\u7834\u95e8\u800c\u51fa\u7684\u3002\u5982\u4eca\u529f\u529b\u5168\u6d88\uff0c\u5df2 \u662f\u5e9f\u4eba\u4e00\u4e2a\uff0c\u7eb5\u6709\u5341\u516b\u822c\u6b66\u827a\uff0c\u4e5f\u662f\u96be\u4ee5\u8fd0\u7528\u7684\u4e86\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4e24\u4e2a\u4eba\u7684\u811a\u6b65\u58f0\u505c\u5728\u95e8\u524d\uff0c\u5176\u4e2d\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u91cc\u9762\u4f3c\u6709\u58f0\u606f\uff0c\u6562\u60c5\u662f\u8fd9\u5c0f\u5b50\u9192 \u4e86\u3002\u54b1\u4eec\u8fdb\u53bb\u770b\u770b\u3002\u201d\u53e6\u4e00\u4e2a\u9053\uff1a\u201c\u6015\u4e0d\u4f1a\u8fd9\u6837\u5feb\u9192\u7684\u5427\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff0c\u8fd9\u5c0f\u5b50 \u662f\u4e2a\u975e\u51e1\u4eba\u7269\uff0c\u8bba\u7406\u662f\u4e0d\u4f1a\u8fd9\u6837\u5feb\u9192\u7684\uff0c\u4e0d\u8fc7\u65f6\u4ed6\u53ef\u8bf4\u4e0d\u5b9a\u3002\u8235\u4e3b\u5429\u5490\uff0c\u5f85\u4ed6\u4e00\u9192\uff0c\u5c31\u8981\u63d0 \u4ed6\u53bb\u95ee\u8bdd\u7684\uff0c\u4ed6\u7684\u4f19\u4f34\u9053\u3002\u201d\u597d\uff0c\u90a3\u5c31\u8fdb\u53bb\u77a7\u77a7\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u95ed\u4e86\u773c\u775b\u88c5\u4f5c\u719f\u7761\uff0c\u90a3\u4e24\u4e2a\u770b\u5b88\u4e00\u4e2a\u4e3e\u706f\u5728\u4ed6\u9762\u524d\u4e00\u7167\uff0c\u53e6\u4e00\u4e2a\u8fd8\u4e0d\u653e\u5fc3\uff0c\u53c8\u671d \u7740\u4ed6\u7684\u5c41\u80a1\u8e22\u4e86\u4e00\u811a\u8bd5\u8bd5\u3002\u91d1\u9010\u6d41\u5fcd\u4f4f\u4e86\u6c14\uff0c\u95f7\u4e0d\u4f5c\u58f0\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5f85\u6211\u6062\u590d\u4e86\u6b66\u529f\uff0c\u53eb \u4f60\u8fd9\u4e24\u4e2a\u5c0f\u8d3c\u77e5\u9053\u6211\u7684\u5389\u5bb3\uff01\u201d\u53ef\u662f\u4ed6\u7684\u529f\u529b\u4f55\u4ee5\u7a81\u7136\u6d88\u5931\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u662f\u83ab\u540d\u5176\u5999\uff0c\u662f\u5426 \u80fd\u591f\u6062\u590d\u6b66\u529f\uff0c\u5fc3\u4e2d\u5176\u5b9e\u6beb\u65e0\u628a\u63e1\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u770b\u5b88\u9000\u4e86\u51fa\u53bb\uff0c\u5173\u4e0a\u4e86\u95e8\uff0c\u5c31\u5728\u95e8\u5916\u8bae\u8bba\u3002\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u662f\u4ec0\u4e48\u4eba\uff0c\u54b1\u4eec \u7684\u8235\u4e3b\u8fd9\u6837\u770b\u91cd\u4ed6\uff0c\u53eb\u54b1\u4eec\u4e24\u4e2a\u4e0d\u5f97\u597d\u7761\uff0c\u6574\u591c\u8981\u670d\u4f8d\u4ed6\u3002\u201d \u3000\u3000\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u8fd8\u4e0d\u77e5\u9053\u8fd9\u5c0f\u5b50\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u542c\u8bf4\u7aa6\u8001\u5927\u5f88\u5403\u4e86\u4ed6\u7684\u4e8f\uff0c\u8981\u4e0d\u662f\u54b1\u4eec\u8235\u4e3b\u53ca\u65f6\u8d76\u5230\uff0c\u4ed6\u51e0\u4e4e\u547d\u4e27\u957f\u6c5f\u3002\u201d \u7aa6\u8001\u5927\u5373\u662f\u6697\u7b97\u91d1\u9010\u6d41\u7684\u90a3\u4e2a\u68a2\u516c\u3002 \u3000\u3000\u4ed6\u7684\u540c\u4f34\u7b11\u9053\uff1a\u201c\u7aa6\u8001\u5927\u7b97\u5f97\u4ec0\u4e48\uff0c\u516d\u5408\u5e2e\u7684\u8463\u5341\u4e09\u5a18\u548c\u5706\u6d77\uff0c\u9752\u9f99\u3001\u767d\u864e\u4e24\u5e2e\u7684\u5e2e \u4e3b\uff0c\u52a0\u4e0a\u4e86\u7ea2\u7f28\u4f1a\u7684\u5bab\u79c9\u85e9\uff0c\u90fd\u66fe\u5403\u4e86\u4ed6\u7684\u4e8f\u5462\uff01\u201d \u3000\u3000\u90a3\u4e2a\u770b\u5b88\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u5389\u5bb3\uff01\u4ed6\u5230\u5e95\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u91d1\u4e16\u9057\u7684\u540d\u5b57\u4f60\u542c\u8bf4\u8fc7\u6ca1\u6709\uff1f\u201d \u3000\u3000\u90a3\u770b\u5b88\u7b11\u9053\uff1a\u201c\u4f60\u5f53\u6211\u662f\u521d\u51fa\u9053\u7684\u96cf\u513f\u4e48\uff0c\u91d1\u4e16\u9057\u5927\u540d\u9f0e\u9f0e\uff0c\u6211\u5c82\u6709\u4e0d\u77e5\u4e4b\u7406\uff1f\u4e8c\u5341\u5e74 \u524d\uff0c\u4ed6\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\u3002\u5982\u4eca\u8fde\u4ed6\u7684\u5f92\u5f1f\u6c5f\u6d77\u5929\u4e5f\u662f\u6b66\u6797\u516c\u8ba4\u7684\u7b2c\u4e00\u9ad8\u624b\u4e86\u3002\u4e0d\u8fc7\uff0c\u542c\u8bf4\u91d1 \u4e16\u9057\u5df2\u7ecf\u5931\u8e2a\u591a\u5e74\uff0c\u4e0e\u8fd9\u5c0f\u5b50\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u201c\u91d1\u4e16\u9057\u5c31\u662f\u8fd9\u5c0f\u5b50\u7684\u7236\u4eb2\u3002\u201d \u3000\u3000\u90a3\u770b\u5b88\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u602a\u4e0d\u5f97\u5982\u6b64\u4e86\u5f97\uff01\u4f46\u6211\u5374\u4e0d\u660e\u767d\u54b1\u4eec\u7684\u8235\u4e3b\u4e3a\u4f55 \u4e0d\u6740\u4e86\u4ed6\uff0c\u4e0d\u6015\u4ed6\u9003\u51fa\u53bb\u62a5\u4ec7\u5417\uff1f\u201d \u3000\u3000\u4ed6\u7684\u4f19\u4f34\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u4f60\u5012\u4e0d\u7528\u62c5\u5fc3\uff0c\u8fd9\u5c0f\u5b50\u7eb5\u662f\u5929\u5927\u795e\u901a\uff0c\u5982\u4eca\u4e5f\u662f\u63d2\u7fc5\u96be\u9003\u7684\u4e86\u3002 \u54b1\u4eec\u7684\u8235\u4e3b\u662f\u4e3a\u4e86\u516d\u5408\u5e2e\u7684\u5173\u7cfb\u624d\u4e0d\u6740\u4ed6\u7684\u3002\u201d \u3000\u3000\u90a3\u770b\u5b88\u8be7\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u516d\u5408\u5e2e\u7684\u4ec7\u4eba\u5417\uff1f\u201d \u3000\u3000\u4ed6\u7684\u4f19\u4f34\u9053\u3002\u201d\u4f60\u662f\u53ea\u77e5\u5176\u4e00\u4e0d\u77e5\u5176\u4e8c\uff0c\u516d\u5408\u5e2e\u5e2e\u4e3b\u7684\u59b9\u59b9\u79c1\u9003\u51fa\u5e2e\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u201c\u8fd9\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u201c\u542c\u8bf4\u5c31\u662f\u8ddf\u8fd9\u5c0f\u5b50\u79c1\u9003\u7684\uff0c\u53f2\u767d\u90b5\u8981\u7740\u843d\u5728\u8fd9\u5c0f\u5b50\u7684\u8eab\u4e0a\u627e\u56de\u4ed6\u7684\u59b9\u59b9\u7684\uff0c\u5df2\u7ecf\u77e5\u7167 \u5404\u4e2a\u5e2e\u4f1a\uff0c\u4ee3\u4e3a\u67e5\u7f09\uff0c\u54b1\u4eec\u7684\u8235\u4e3b\u4e0e\u53f2\u767d\u90b5\u4ea4\u60c5\u975e\u6d45\uff0c\u5982\u4eca\u6349\u83b7\u8fd9\u4e2a\u5c0f\u5b50\uff0c\u5f53\u7136\u8981\u9001\u7ed9\u516d\u5408 \u5e2e\u53d1\u843d\uff0c\u600e\u80fd\u5c31\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u5230\u8fd9\u91cc\uff0c\u6697\u6697\u9a82\u4e86\u4e00\u53e5\u201c\u80e1\u8bf4\u516b\u9053\u201d\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u53d7\u8bec\u8d56\u4e0d\u6253\u7d27\uff0c\u8fd9\u8c23\u8a00 \u4f20\u5230\u4e86\u674e\u6566\u7684\u8033\u6735\u91cc\u53ef\u4e0d\u597d\u542c\u3002\u8fd9\u53f2\u767d\u90b5\u4e5f\u771f\u662f\u7cca\u6d82\uff0c\u59b9\u59b9\u7231\u4e0a\u4e86\u4ec0\u4e48\u4eba\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u90a3\u770b\u5b88\u8bf4\u9053\uff1a\u201c\u5514\uff0c\u8fd9\u4e2a\u9053\u7406\u6211\u660e\u767d\u4e86\uff0c\u53ef\u662f\u4e3a\u4ec0\u4e48\u59d3\u91d1\u8fd9\u5c0f\u5b50\u5982\u4eca\u662f\u63d2\u7fc5\u96be\u9003\uff0c\u6211\u5374 \u8fd8\u672a\u660e\u767d\u3002\u201d \u3000\u3000\u4ed6\u7684\u4f19\u4f34\u9053\uff1a\u201c\u6587\u5c9b\u4e3b\u6709\u4e00\u79cd\u7956\u4f20\u79d8\u836f\u540d\u53eb\u9165\u9aa8\u6563\uff0c\u53ef\u4ee5\u6309\u7167\u6240\u670d\u7684\u4efd\u91cf\uff0c\u51cf\u524a\u5bf9\u65b9\u7684 \u529f\u529b\uff0c\u82e5\u662f\u670d\u4e86\u4e00\u8336\u5319\uff0c\u591a\u597d\u7684\u5185\u529f\u4e5f\u4f1a\u5316\u4e3a\u4e4c\u6709\u3002\u8fd9\u5c0f\u5b50\u5728\u88ab\u6349\u4e0a\u8239\u7684\u65f6\u5019\uff0c\u6587\u5c9b\u4e3b\u5c31\u628a \u4e00\u8336\u5319\u7684\u9165\u9aa8\u6563\u6eb6\u5316\u5728\u8336\u6c34\u4e4b\u4e2d\uff0c\u704c\u4ed6\u559d\u4e86\u3002\u4f60\u5f53\u65f6\u4e0d\u5728\u573a\uff0c\u96be\u602a\u4f60\u4e0d\u77e5\u9053\u3002\u201d\u6b47\u4e86\u4e00\u6b47\uff0c \u63a5\u7740\u53c8\u7b11\u9053\uff1a\u201c\u8981\u4e0d\u662f\u4ed6\u670d\u4e86\u9165\u9aa8\u6563\uff0c\u4f60\u60f3\u54b1\u4eec\u7684\u8235\u4e3b\u600e\u6562\u8ba9\u4ed6\u4e0d\u5e26\u624b\u94d0\u811a\u9563\uff0c\u53c8\u600e\u6562\u653e\u5fc3 \u54b1\u4eec\u4e24\u4e2a\u770b\u5b88\u4ed6\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u662f\u7740\u4e86\u6587\u9053\u5e84\u7684\u6697\u7b97\u3002\u4ed6\u6697\u6697\u5492\u9a82\u6587\u9053\u5e84\u7684\u5351\u9119\uff0c\u4f46\u5fc3\u91cc\u5374 \u53c8\u56e0\u6b64\u71c3\u8d77\u4e86\u4e00\u7ebf\u5e0c\u671b\uff0c\u5728\u4ed6\u77e5\u9053\u6240\u670d\u7684\u662f\u9165\u9aa8\u6563\u4e4b\u540e\u3002 \u3000\u3000\u539f\u6765\u4ed6\u7684\u7236\u4eb2\u5168\u4e16\u9057\u5f53\u5e74\u521d\u8bc6\u6587\u9053\u5e84\u7684\u53d4\u7236\u6587\u5ef7\u74a7\u4e4b\u65f6\uff0c\u4e5f\u66fe\u7740\u8fc7\u6587\u5ef7\u74a7\u7684\u6697\u7b97\u3002\u6587\u5ef7 \u74a7\u9a97\u4ed6\u670d\u4e86\u9165\u9aa8\u6563\uff0c\u624d\u903c\u4ed6\u8f83\u91cf\u6b66\u529f\uff0c\u628a\u91d1\u4e16\u9057\u6298\u78e8\u5f97\u6b7b\u53bb\u6d3b\u6765\u3002\uff08\u4e8b\u8be6\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\uff09 \u3000\u3000\u91d1\u4e16\u9057\u53d7\u4e86\u8fd9\u6b21\u6298\u78e8\uff0c\u75db\u5b9a\u601d\u75db\uff0c\u7ec8\u4e8e\u7ed9\u4ed6\u94bb\u7814\u51fa\u4e00\u79cd\u53ef\u4ee5\u5bf9\u4ed8\u9165\u9aa8\u6563\u5316\u529f\u7684\u5410\u7eb3\u65b9 \u6cd5\u3002 \u3000\u3000\u5f53\u4e0b\u91d1\u9010\u6d41\u5c31\u6309\u7167\u4ed6\u7236\u4eb2\u6240\u4f20\u7684\u65b9\u6cd5\uff0c\u76d8\u819d\u6253\u5750\uff0c\u610f\u5b58\u4e39\u7530\uff0c\u653e\u6162\u547c\u5438\uff0c\u5c06\u771f\u6c14\u4e00\u70b9\u4e00 \u6ef4\u7684\u51dd\u805a\u8d77\u6765\u3002 \u3000\u3000\u8fc7\u4e86\u5927\u7ea6\u534a\u67f1\u9999\u7684\u65f6\u523b\uff0c\u91d1\u9010\u6d41\u7684\u771f\u6c14\u5df2\u662f\u53ef\u4ee5\u8fd0\u884c\u65e0\u963b\u3002\u4e0d\u8fc7\u7531\u4e8e\u4ed6\u670d\u98df\u7684\u9165\u9aa8\u6563\u7684 \u4efd\u91cf\u8fc7\u591a\uff0c\u771f\u6c14\u53ea\u80fd\u4e00\u70b9\u4e00\u6ef4\u7684\u51dd\u805a\uff0c\u8981\u6025\u4e5f\u6025\u4e0d\u6765\uff0c\u6b64\u65f6\u4ed6\u867d\u7136\u53ef\u4ee5\u8fd0\u6c14\u65e0\u963b\uff0c\u529f\u592b\u5374\u53ea \u4e0d\u8fc7\u624d\u6062\u590d\u4e86\u4e00\u4e24\u5206\u7684\u5149\u666f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u62c5\u5fe7\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f46\u76fc\u5728\u8fd9\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u53ef\u5343\u4e07\u522b\u7ed9\u4ed6\u4eec\u53d1 \u89c9\u624d\u597d\u3002\u201d\u4ed6\u4f30\u8ba1\u8981\u5b8c\u5168\u6062\u590d\u539f\u6765\u7684\u529f\u529b\uff0c\u81f3\u5c11\u4e5f\u5f97\u4e24\u4e2a\u65f6\u8fb0\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u53c8\u6709\u4e00\u4e2a\u4eba\u8d70\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u94b1\u5927\uff0c\u8235\u4e3b\u53eb\u4f60\uff0c\u6211\u66ff\u4f60\u4e00\u4f1a\u513f\u3002\u201d\u58f0\u97f3 \u975e\u5e38\u719f\u6089\uff0c\u539f\u6765\u662f\u6587\u9053\u5e84\u7684\u513f\u5b50\u6587\u80dc\u4e2d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u7684\u8eab\u4efd\u662f\u5ba2\u4eba\uff0c\u4f55\u4ee5\u5374\u8981\u4ed6\u6765\u8fd9\u91cc\u770b\u5b88\uff1f\u201d \u3000\u3000\u94b1\u5927\u5c31\u662f\u90a3\u4e2a\u8bb2\u8ff0\u91d1\u9010\u6d41\u6765\u5386\u7684\u770b\u5b88\uff0c\u4ed6\u662f\u8235\u4e3b\u7684\u5fc3\u8179\uff0c\u4eba\u5f88\u673a\u7075\u3002\u91d1\u9010\u6d41\u6240\u7591\u5fc3\u7684\u4ed6 \u4e5f\u66fe\u60f3\u5230\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u6587\u516c\u5b50\uff0c\u8fd9\u6211\u600e\u4e48\u6562\u5f53\uff1f\u4f60\u662f\u8d35\u5ba2\uff0c\u5c82\u80fd\u8981\u4f60\u66ff\u6211\u770b\u5b88\uff1f\u201d \u3000\u3000\u6587\u80dc\u4e2d\u7b11\u9053\u3002\u201d\u8fd9\u662f\u6211\u81ea\u544a\u594b\u52c7\u6765\u7684\uff0c\u4f60\u65e0\u987b\u8fc7\u610f\u4e0d\u53bb\u3002\u542c\u8bf4\u8fd9\u5c0f\u5b50\u8fde\u8d25\u6b66\u6797\u8bb8\u591a\u9ad8 \u624b\uff0c\u6211\u6765\u77a7\u77a7\u4ed6\u957f\u5f97\u4ec0\u4e48\u6a21\u6837\uff0c\u662f\u4e09\u5934\u8fd8\u662f\u516d\u81c2\uff1f\u201d \u3000\u3000\u94b1\u5927\u4ee5\u4e3a\u4ed6\u53ea\u662f\u597d\u5947\uff0c\u7b11\u9053\uff1a\u201c\u4e5f\u4e0d\u8fc7\u662f\u4e2a\u666e\u901a\u7684\u5c0f\u5b50\u7f62\u4e86\u3002\u5e76\u6ca1\u6709\u7ea2\u7709\u6bdb\u3001\u7eff\u773c\u775b\uff0c \u76f8\u8c8c\u4e00\u70b9\u4e5f\u4e0d\u51f6\u6076\uff0c\u5012\u50cf\u662f\u4e2a\u8bfb\u4e66\u7684\u65af\u6587\u4eba\u5462\u3002\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6587\u5c9b\u4e3b\u662f\u8235\u4e3b\u7684\u4e0a\u5bbe\uff0c\u8fd9\u6b21 \u6349\u83b7\u8fd9\u4e2a\u5c0f\u5b50\uff0c\u8fd8\u90fd\u662f\u9760\u4e86\u4ed6\u7684\u5e2e\u5fd9\u3002\u4ed6\u7684\u513f\u5b50\u6765\u8fd9\u91cc\u770b\u5b88\uff0c\u51b3\u4e0d\u81f3\u4e8e\u6709\u4ec0\u4e48\u758f\u5931\u3002\u201d\u4e8e\u662f \u8c22\u8fc7\u4e86\u6587\u80dc\u4e2d\uff0c\u4e5f\u5c31\u653e\u5fc3\u8d70\u4e86\u3002 \u3000\u3000\u94b1\u5927\u6240\u987e\u8651\u7684\u201c\u758f\u5931\u201d\uff0c\u53ea\u662f\u6015\u91d1\u9010\u6d41\u9003\u8d70\u800c\u5df2\uff0c\u91d1\u9010\u6d41\u5df2\u662f\u670d\u4e86\u9165\u9aa8\u6563\u7684\uff0c\u8981\u60f3\u9003 \u8d70\uff0c\u9664\u975e\u662f\u770b\u5b88\u7684\u4eba\u79c1\u81ea\u653e\u4ed6\uff0c\u6587\u80dc\u4e2d\u5f53\u7136\u662f\u51b3\u8ba1\u4e0d\u4f1a\u79c1\u81ea\u653e\u4ed6\u7684\uff0c\u6240\u4ee5\u94b1\u5927\u5f88\u662f\u653e\u5fc3\u3002\u6b8a \u4e0d\u77e5\u6587\u80dc\u4e2d\u867d\u7136\u4e0d\u4f1a\u79c1\u653e\u91d1\u9010\u6d41\uff0c\u4f46\u5374\u662f\u8981\u6765\u6697\u5bb3\u4ed6\u7684\u3002 \u3000\u3000\u539f\u6765\u6587\u80dc\u4e2d\u65e9\u5df2\u662f\u542b\u6068\u5728\u5fc3\uff0c\u8981\u62a5\u79c1\u4ec7\u7684\u4e86\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u6709\u8fd9\u4e2a\u673a\u4f1a\uff0c\u8fd8\u7109\u80af\u653e\u8fc7\u91d1\u9010 \u6d41\uff1f\u53ea\u662f\u788d\u4e8e\u516d\u5408\u5e2e\u7684\u5173\u7cfb\uff0c\u6b64\u95f4\u7684\u6d77\u6c99\u5e2e\u5e2e\u4e3b\u8981\u628a\u91d1\u9010\u6d41\u9001\u53bb\u7ed9\u53f2\u767d\u90b5\u53d1\u843d\uff0c\u6587\u80dc\u4e2d\u4e0d\u6562 \u516c\u7136\u52a0\u5bb3\uff0c\u5269\u4e0b\u7684\u5c31\u53ea\u662f\u6697\u7bad\u4f24\u4eba\u4e00\u9014\u3002\u4ed6\u662f\u60f3\u5728\u9020\u6210\u65e2\u6210\u4e8b\u5b9e\u4e4b\u540e\uff0c\u53eb\u6d77\u6c99\u5e2e\u7684\u5e2e\u4e3b\u65e0\u53ef \u5948\u4f55\u3002\u8fd9\u4e2a\u94b1\u5927\u662f\u5e2e\u4e3b\u7684\u5fc3\u8179\uff0c\u4eba\u53c8\u6bd4\u8f83\u673a\u7075\uff0c\u56e0\u6b64\u4ed6\u8981\u501f\u6545\u5c06\u4ed6\u8c03\u5f00\u3002 \u3000\u3000\u94b1\u5927\u8d70\u5f00\u4e4b\u540e\uff0c\u6587\u80dc\u4e2d\u5bf9\u7559\u4e0b\u7684\u90a3\u4e2a\u770b\u5b88\u9053\uff1a\u201c\u6253\u5f00\u95e8\u8ba9\u6211\u8fdb\u53bb\u77a7\u77a7\u3002\u201d\u8fd9\u4eba\u53ea\u662f\u5e2e\u4e2d \u7684\u4e00\u4e2a\u5c0f\u89d2\u8272\uff0c\u4e0d\u6562\u4e0d\u4f9d\uff0c\u5e94\u4e86\u4e00\u4e2a\u201c\u662f\u201d\u5b57\uff0c\u4fbf\u6253\u5f00\u7262\u95e8\uff0c\u966a\u6587\u80dc\u4e2d\u8fdb\u53bb\u3002\u6587\u80dc\u4e2d\u505a\u4e8b\u5012 \u662f\u6781\u4e3a\u8c28\u614e\uff0c\u660e\u77e5\u91d1\u9010\u6d41\u662f\u670d\u4e86\u9165\u9aa8\u6563\u7684\uff0c\u4e5f\u4e0d\u6562\u4e1d\u6beb\u5927\u610f\uff0c\u4e00\u8fdb\u4e86\u77f3\u6d1e\u7acb\u5373\u4fbf\u70b9\u71c3\u4e86\u706b\u6298 \u5b50\uff0c\u540c\u65f6\u62d4\u5251\u9632\u8eab \u3000\u3000\u706b\u5149\u4e00\u4eae\uff0c\u53ea\u89c1\u91d1\u9010\u6d41\u9760\u7740\u5899\u89d2\uff0c\u4f4e\u5934\u95ed\u76ee\uff0c\u201c\u547c\u565c\u547c\u565c\u201d\u5730\u6253\u7740\u9f3e\u3002\u90a3\u770b\u5b88\u7b11\u9053\uff1a \u201c\u4ee4\u5c0a\u7684\u9165\u9aa8\u6563\u771f\u662f\u5389\u5bb3\uff0c\u4ed6\u5df2\u719f\u7761\u4e86\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e86\uff0c\u4f9d\u7136\u672a\u9192\uff01\u201d \u3000\u3000\u6587\u80dc\u4e2d\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u91d1\u9010\u6d41\uff0c\u8fd9\u6b21\u770b\u4f60\u8fd8\u9003\u5f97\u51fa\u6211\u7684\u638c\u5fc3\uff1f\u201d\u5530\u7684\u4e00\u5251 \u5c31\u523a\u8fc7\u53bb\uff0c\u90a3\u770b\u5b88\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\u3002\u201d\u6587\u76f8\u516c\uff0c\u4e0d\uff0c\u4e0d\u53ef\uff01\u201d\u6587\u80dc\u4e2d\u7b11\u9053\uff1a\u201c\u4f60\u522b\u614c\uff0c\u6211\u4e0d \u662f\u8981\u4ed6\u6027\u547d\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8fd9\u4e00\u5251\u5df2\u662f\u523a\u5230\u4e86\u91d1\u9010\u6d41\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u53ea\u542c\u5230\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u5206\u660e\u662f\u523a\u5728\u91d1\u9010\u6d41\u7684\u8eab\u4e0a\uff0c\u5374\u4f3c\u523a\u4e2d\u4e86\u4e00\u5757\u77f3\u5934\u3002\u539f \u6765\u91d1\u9010\u6d41\u65e9\u5df2\u6599\u5230\u6587\u80dc\u4e2d\u4e0d\u6562\u4f24\u4ed6\u7684\u6027\u547d\uff0c\u8981\u6697\u7b97\u7684\u8bdd\uff0c\u4e00\u5b9a\u662f\u6311\u7a7f\u4ed6\u7684\u7435\u7436\u9aa8\uff0c\u6587\u80dc\u4e2d\u4e00 \u5251\u523a\u6765\uff0c\u4ed6\u4e00\u4e2a\u6c89\u80a9\u7f29\u8098\uff0c\u80a9\u4e0a\u7684\u8863\u670d\u9f13\u8d77\uff0c\u8fd9\u4e00\u5251\u523a\u7a7f\u4e86\u4ed6\u90a3\u8863\u88f3\uff0c\u523a\u7740\u4e86\u77f3\u58c1\u3002\u91d1\u9010\u6d41 \u529f\u529b\u672a\u590d\uff0c\u53ea\u80fd\u4f7f\u7528\u5de7\u8ba1\uff0c\u4ed6\u62ff\u634f\u65f6\u5019\u7684\u51c6\u786e\uff0c\u4e5f\u5f53\u771f\u662f\u5999\u5230\u6beb\u5dc5\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u5251\u5c16\u5d4c\u77f3\uff0c\u8eab\u8eaf\u4e0d\u7981\u524d\u503e\u3002\u91d1\u9010\u6d41\u4e00\u8dc3\u800c\u8d77\uff0c\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6bb5\uff0c\u9a6c\u4e0a\u5c31\u70b9 \u4e86\u4e24\u4e2a\u4eba\u7684\u7a74\u9053\u3002 \u3000\u3000\u90a3\u4e2a\u770b\u5b88\u7ed9\u4ed6\u70b9\u7740\u4e86\u7a74\u9053\uff0c\u767b\u65f6\u5c31\u8dcc\u5012\u4e86\u3002\u6587\u80dc\u4e2d\u5219\u53ea\u662f\u95f7\u54fc\u4e00\u58f0\uff0c\u6643\u4e86\u4e24\u6643\uff0c\u5374\u6ca1\u6709 \u8dcc\u5012\u3002\u539f\u6765\u91d1\u9010\u6d41\u7684\u529f\u529b\u53ea\u662f\u6062\u590d\u4e00\u4e24\u5206\uff0c\u7528\u6765\u5bf9\u4ed8\u90a3\u4e2a\u770b\u5b88\u662f\u7ef0\u7ef0\u6709\u4f59\uff0c\u5bf9\u4ed8\u6587\u80dc\u4e2d\u5219\u8fd8 \u5acc\u4e0d\u8db3\uff0c\u5c01\u95ed\u4e0d\u4e86\u4ed6\u7684\u7a74\u9053\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u95f7\u54fc\u4e00\u58f0\uff0c\u957f\u5251\u8131\u624b\uff0c\u731b\u7684\u5c31\u662f\u4e00\u638c\uff0c\u4ed6\u5e94\u53d8\u4e5f\u7b97\u673a\u8b66\uff0c\u53ef\u662f\u53cc\u638c\u4e00\u4ea4\uff0c\u4ed6\u4ecd\u7136 \u662f\u654c\u4e0d\u8fc7\u91d1\u9010\u6d41\u7684\u638c\u529b\u3002\u91d1\u9010\u6d41\u628a\u4ed6\u51fb\u6655\uff0c\u518d\u8865\u4e0a\u4e00\u6307\uff0c\u7528\u72ec\u95e8\u624b\u6cd5\u70b9\u4e86\u4ed6\u7684\u201c\u5de8\u9615\u201d\u201c\u7389 \u6795\u201d\u201c\u7487\u7391\u201d\u4e09\u5904\u5927\u7a74\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\u3002\u201d\u6ca1\u51fa\u606f\u7684\u5c0f\u5b50\uff0c\u8fde\u6697\u7b97\u522b\u4eba\u7684\u80fd\u8010\u90fd\u6ca1\u6709\u3002\u563f\uff0c\u563f\uff0c\u73b0\u5728\u662f\u4f60\u9003\u4e0d\u51fa\u6211 \u7684\u638c\u5fc3\u4e86\uff01\u201d\u6b63\u5728\u5f97\u610f\uff0c\u5ffd\u5730\u5fc3\u5934\u4e00\u9707\uff0c\u53ea\u89c9\u56db\u80a2\u65e0\u529b\uff0c\u518d\u4e5f\u7b11\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u4e0e\u6587\u80dc\u4e2d\u62fc\u4e86\u4e00\u638c\uff0c\u6240\u7528\u7684\u6c14\u529b\u5df2\u662f\u8d85\u8fc7\u4e86\u672c\u8eab\u7684\u9650\u5ea6\u3002\u8ddf\u7740\u53c8\u4f7f\u7528\u72ec\u95e8\u7684 \u91cd\u624b\u6cd5\u5c01\u95ed\u6587\u80dc\u4e2d\u7684\u4e09\u5904\u5927\u7a74\uff0c\u771f\u529b\u6d88\u8017\u66f4\u591a\u3002\u5f53\u65f6\u4e0d\u89c9\u5f97\uff0c\u5f85\u5230\u654c\u4eba\u4e00\u5012\uff0c\u4ed6\u7684\u7cbe\u795e\u677e\u61c8 \u4e0b\u6765\uff0c\u6076\u52a3\u7684\u6548\u679c\u7acb\u5373\u5c31\u73b0\u51fa\u6765\u4e86\uff01\u4ed6\u53d1\u89c9\u81ea\u5df1\u8f9b\u8f9b\u82e6\u82e6\u6240\u51dd\u805a\u7684\u90a3\u70b9\u771f\u6c14\uff0c\u5df2\u662f\u6d88\u8017\u5f97\u5e72 \u5e72\u51c0\u51c0\uff01 \u3000\u3000\u91d1\u9010\u6d41\u6697\u6697\u53eb\u58f0\u201c\u82e6\u4e5f\uff01\u201d\u8981\u77e5\u6587\u80dc\u4e2d\u5728\u8fd9\u91cc\u51fa\u4e86\u4e8b\uff0c\u4e0d\u4e45\u4e00\u5b9a\u4f1a\u7ed9\u53d1\u89c9\uff0c\u90a3\u4e2a\u94b1\u5927\u4e0d \u4e45\u4e5f\u8981\u56de\u6765\u3002\u800c\u91d1\u9010\u6d41\u8981\u5b8c\u5168\u6062\u590d\u529f\u529b\uff0c\u90fd\u81f3\u5c11\u8fd8\u5f97\u4e24\u4e2a\u65f6\u8fb0\u3002\u65f6\u95f4\u6025\u4fc3\uff0c\u600e\u5bb9\u5f97\u4ed6\u518d\u6253\u5750 \u8fd0\u529f\uff1f \u3000\u3000\u6b63\u81ea\u7740\u6025\uff0c\u5ffd\u89c1\u6709\u4e2a\u5f71\u5b50\u5728\u77f3\u7a9f\u5916\u95e8\u4e00\u6643\u5373\u8fc7\uff0c\u91d1\u9010\u6d41\u90fd\u672a\u66fe\u770b\u5f97\u6e05\u695a\uff0c\u90a3\u4eba\u5df2\u662f\u629b\u4e86 \u4e00\u4ef6\u4e1c\u897f\u8fdb\u6765\u3002\u6b63\u662f\uff1a \u3000\u3000\u9ed1\u7262\u56da\u4fa0\u58eb\uff0c\u6697\u5ba4\u73b0\u7ea2\u5986\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u5341\u4e8c\u56de\u3000\u8be7\u89c1\u6b8b\u8102\u9003\u9ed1\u72f1\u3000\u53d8\u6765\u89e3\u836f\u620f\u9b54\u5934 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u5341\u4e8c\u56de\u3000\u8be7\u89c1\u6b8b\u8102\u9003\u9ed1\u72f1\u3000\u53d8\u6765\u89e3\u836f\u620f\u9b54\u5934 \u3000\u3000\u201c\u535c\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u90a3\u4ef6\u4e1c\u897f\u6b63\u597d\u843d\u5728\u91d1\u9010\u6d41\u7684\u811a\u8fb9\u3002\u91d1\u9010\u6d41\u62fe\u8d77\u6765\u4e00\u770b\uff0c\u53ea\u89c1\u662f\u4e00\u4e2a\u4e94 \u5bf8\u6765\u957f\u7684\u5c0f\u76d2\u5b50\uff0c\u662f\u9ec4\u6768\u6728\u9020\u7684\uff0c\u53cd\u8fc7\u6765\u4e00\u770b\uff0c\u5149\u6ed1\u7684\u5e95\u9762\u5199\u6709\u4e24\u4e2a\u7ea2\u5b57\uff1a\u89e3\u836f\uff01\u76d2\u5b50\u672a\u66fe \u6253\u5f00\uff0c\u4e00\u7f15\u6de1\u6de1\u7684\u4f3c\u662f\u8102\u7c89\u7684\u9999\u6c14\u5df2\u662f\u900f\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5168\u9010\u6d41\u60ca\u7591\u4e0d\u5b9a\uff0c\u628a\u76d2\u5b50\u62ff\u5230\u5149\u4eae\u7684\u5730\u65b9\uff0c\u6253\u5f00\u6765\u4e00\u770b\uff0c\u91cc\u9762\u662f\u4e00\u9897\u78a7\u7eff\u8272\u7684\u836f\u4e38\uff0c\u76d2 \u5e95\u4e00\u62b9\u6bb7\u7ea2\u3002\u91d1\u9010\u6d41\u7528\u6307\u7532\u522e\u4e86\u4e00\u70b9\u6b8b\u6e0d\uff0c\u4ed4\u7ec6\u8fa8\u8ba4\uff0c\u539f\u6765\u4e43\u662f\u80ed\u8102\u3002\u91d1\u9010\u6d41\u65b9\u59cb\u660e\u767d\uff0c\u8fd9 \u662f\u5973\u4eba\u7528\u7684\u80ed\u8102\u76d2\u5b50\uff0c\u76d2\u5b50\u4e0a\u90a3\u4e24\u4e2a\u7ea2\u5b57\u5c31\u662f\u7528\u76d2\u5b50\u91cc\u7684\u80ed\u8102\u5199\u7684\uff0c\u60f3\u5fc5\u521a\u624d\u90a3\u4e2a\u4eba\u5728\u4ed3\u731d \u95f4\u627e\u4e0d\u5230\u7b14\u58a8\uff0c\u56e0\u6b64\u5306\u5306\u5fd9\u5fd9\u7684\u5c31\u8638\u4e86\u80ed\u8102\u5199\u5b57\u4e86\u3002 \u3000\u3000\u201c\u96be\u9053\u8fd9\u5c31\u662f\u9165\u9aa8\u6563\u7684\u89e3\u836f\uff1f\u89e3\u836f\u5374\u4f55\u6545\u88c5\u5728\u80ed\u8102\u76d2\u91cc\uff1f\u201d\u8fd9\u4ef6\u7a81\u5982\u5176\u6765\u7684\u4e8b\u60c5\u592a\u8fc7\u79bb \u5947\uff0c\u91d1\u9010\u6d41\u600e\u4e5f\u731c\u60f3\u4e0d\u900f\u3002\u629b\u8fd9\u76d2\u5b50\u8fdb\u6765\u7684\u90a3\u4e2a\u4eba\uff0c\u662f\u7537\u4eba\u8fd8\u662f\u5973\u4eba\uff0c\u4ed6\u521a\u624d\u4e5f\u6ca1\u6709\u770b\u5f97\u6e05 \u695a\u3002 \u3000\u3000\u6309\u8bf4\u5018\u82e5\u662f\u9165\u9aa8\u6563\u7684\u89e3\u836f\uff0c\u90a3\u53ea\u80fd\u5c31\u662f\u6587\u9053\u5e84\u624d\u4f1a\u6709\u7684\u4e86\u3002\u6587\u9053\u5e84\u5f53\u7136\u662f\u4e0d\u4f1a\u628a\u89e3\u836f\u629b \u7ed9\u4ed6\u7684\u3002\u90a3\u4e48\u662f\u8fd9\u4e2a\u4eba\u5077\u6765\u7684\u4e86\uff1f\u53ef\u662f\u4ed6\u65e2\u7136\u6709\u5fc3\u6765\u8d60\u89e3\u836f\uff0c\u5374\u53c8\u4f55\u4ee5\u4e0d\u80af\u9732\u9762\u76f8\u89c1\uff1f \u3000\u3000\u7591\u56e2\u585e\u9600\uff0c\u767e\u601d\u83ab\u89e3\u3002\u4f46\u522b\u7684\u7591\u95ee\u4e0d\u89e3\u4e5f\u8fd8\u7f62\u4e86\uff0c\u6700\u7d27\u8981\u7684\u4e00\u70b9\u5374\u662f\u5fc5\u987b\u5224\u65ad\u51c6\u786e\u7684\uff1a \u8fd9\u89e3\u836f\u662f\u771f\u662f\u5047\uff1f \u3000\u3000\u65f6\u95f4\u4e0d\u5bb9\u8bb8\u91d1\u9010\u6d41\u4ed4\u7ec6\u63a8\u6572\uff0c\u4ed6\u60f3\u4e86\u4e00\u60f3\uff0c\u5fc3\u9053\uff1a\u201c\u5373\u4f7f\u662f\u6bd2\u836f\uff0c\u6700\u591a\u4e0d\u8fc7\u4e00\u6b7b\u800c\u5df2\u3002 \u53cd\u6b63\u6211\u73b0\u5728\u529f\u529b\u5df2\u5931\uff0c\u4e5f\u662f\u96be\u4ee5\u9003\u51fa\u9b54\u7a9f\u7684\u4e86\uff0c\u4f55\u51b5\u90a3\u4eba\u82e5\u8981\u5bb3\u6211\uff0c\u4e5f\u65e0\u987b\u4f7f\u7528\u6bd2\u836f\u3002\u201d\u4e8e \u662f\u51b3\u610f\u5192\u9669\u4e00\u8bd5\uff0c\u4fbf\u8019\u90a3\u9897\u836f\u4e38\u541e\u4e86\u4e0b\u53bb\u3002\u4e0d\u8fc7\u7247\u523b\uff0c\u53ea\u89c9\u4e00\u7f15\u70ed\u6c14\u4ece\u4e39\u7530\u5347\u8d77\uff0c\u91d1\u9010\u6d41\u559c \u51fa\u671b\u5916\uff0c\u77e5\u9053\u4e86\u679c\u7136\u662f\u89e3\u836f\uff0c\u5f53\u4e0b\u91d1\u9010\u6d41\u518d\u63a5\u4ed6\u7236\u4eb2\u6240\u6559\u7684\u5410\u7eb3\u65b9\u6cd5\uff0c\u8fd0\u6c14\u4e09\u8f6c\uff0c\u8bd5\u4e86\u4e00 \u8bd5\uff0c\u529f\u529b\u867d\u7136\u672a\u66fe\u5b8c\u5168\u6062\u590d\uff0c\u4ea6\u5df2\u6062\u590d\u4e86\u4e03\u516b\u6210\u4e86\u3002 \u3000\u3000\u77f3\u7a9f\u7684\u94c1\u95e8\u65e9\u5df2\u7ed9\u90a3\u770b\u5b88\u6253\u5f00\uff0c\u91d1\u9010\u6d41\u6b64\u65f6\u7684\u529f\u529b\u53c8\u5df2\u6062\u590d\uff0c\u672c\u6765\u4ed6\u53ef\u4ee5\u9003\u8d70\u7684\uff0c\u4f46\u4ed6 \u5374\u4e0d\u613f\u5c31\u8fd9\u4e48\u6837\u7684\u9003\u8d70\u3002\u4ed6\u8981\u62a5\u6587\u9053\u5e84\u7684\u4e00\u638c\u4e4b\u4ec7\uff0c\u4ed6\u4e5f\u8981\u53d6\u56de\u90a3\u5757\u7384\u94c1\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5e76\u4e0d\u662f\u4e00\u4e2a\u9c81\u83bd\u7684\u4eba\uff0c\u5f53\u7136\u4ed6\u4e5f\u660e\u767d\u81ea\u5df1\u73b0\u5728\u7684\u5904\u5883\u2014\u2014\u4ed6\u73b0\u5728\u662f\u9677\u8eab\u9b54\u7a9f\uff0c\u5b64 \u638c\u96be\u9e23\u3002\u4f46\u662f\u4ed6\u5374\u60f3\u5230\u4e86\u4e00\u4e2a\u5de7\u5999\u7684\u4e3b\u610f\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u5df2\u7ecf\u88ab\u4ed6\u70b9\u4e86\u7a74\u9053\uff0c\u662f\u53ef\u4ee5\u4efb\u51ed\u4ed6\u6446\u5e03\u7684\u4e86\u3002\u4e0d\u8fc7\u4ed6\u5374\u5e76\u4e0d\u60f3\u628a\u6587\u80dc\u4e2d\u4f5c\u4e3a\u4eba \u8d28\uff0c\u8fd9\u4e2a\u529e\u6cd5\u4ed6\u8ba4\u4e3a\u8fd8\u662f\u7b28\u62d9\u4e86\u4e9b\u3002\u91d1\u9010\u6d41\u60f3\u5230\u4e86\u4e00\u4e2a\u53ef\u4ee5\u8bf4\u662f\u5341\u5206\u6076\u4f5c\u5267\u7684\u4e3b\u610f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u628a\u6587\u80dc\u4e2d\u7ffb\u4e86\u4e2a\u8eab\uff0c\u8ba9\u4ed6\u8138\u671d\u5929\u7684\u8eba\u7740\uff0c\u7b11\u9053\uff1a\u201c\u591a\u8c22\u4f60\u6765\u63a2\u6211\uff0c\u6211\u5e94\u5f53\u597d\u597d\u7684 \u62db\u5f85\u62db\u5f85\u4f60\u624d\u884c\u3002\u201d\u8bf4\u7f62\uff0c\u8131\u4e0b\u978b\u5b50\uff0c\u5728\u811a\u677f\u5e95\u6413\u4e86\u51e0\u6413\uff0c\u6413\u51fa\u51e0\u56e2\u5f39\u4e38\u822c\u5927\u5c0f\u7684\u6ce5\u57a2\uff0c\u628a \u6587\u80dc\u4e2d\u4e0b\u5df4\u4e00\u634f\uff0c\u6587\u80dc\u4e2d\u7684\u5634\u5df4\u4e0d\u7531\u81ea\u4e3b\u5730\u5f20\u4e86\u5f00\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5c31\u628a\u90a3\u51e0\u4e38\u6ce5\u57a2\u90fd\u585e\u8fdb\u4e86\u4ed6\u7684\u5634\u5df4\u3002\u91d1\u9010\u6d41\u662f\u4ece\u6c34\u5e95\u635e\u8d77\u6765\u7684\u3002\u6c61\u6ce5\u6d4a\u6c34\u90fd\u672a\u6d17 \u6da4\uff0c\u4ece\u90a3\u811a\u677f\u5e95\u6413\u51fa\u6765\u7684\u6ce5\u4e38\uff0c\u5176\u8165\u81ed\u53ef\u60f3\u800c\u77e5\uff01\u6587\u80dc\u4e2d\u7ed9\u70b9\u4e86\u7a74\u9053\uff0c\u52a8\u5f39\u4e0d\u5f97\uff0c\u4f46\u5473\u89c9\u5374 \u662f\u5e76\u672a\u6d88\u5931\u7684\u3002\u8165\u81ed\u7684\u6c14\u5473\u51b2\u5f97\u4ed6\u4e94\u810f\u516d\u8151\u5168\u9020\u4e86\u53cd\uff0c\u5589\u5934\u54af\u54af\u4f5c\u54cd\uff0c\u60f3\u5410\u53c8\u5410\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u5473\u9053\u600e\u6837\uff0c\u6bd4\u5f97\u4e0a\u5c01\u5bb6\u7684\u4f73\u80b4\u7f8e\u9152\u5427\uff1f\u201d\u8fb9\u8bf4\u8fb9\u5265\u4e0b\u4e86\u6587\u80dc\u4e2d\u7684\u8863\u88f3\uff0c \u548c\u6587\u80dc\u4e2d\u6362\u4e86\u7a7f\u7740\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u7684\u62ff\u624b\u597d\u620f\uff0c\u6211\u8bb0\u5f97\u4f60\u662f\u66fe\u7ecf\u8fd9\u6837\u6349\u5f04\u8fc7\u79e6\u5143\u6d69\u7684\u3002 \u6211\u5982\u4eca\u53ea\u662f\u4ee5\u5176\u4eba\u4e4b\u9053\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\uff0c\u4f60\u53ef\u4e0d\u8981\u751f\u6c14\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u70ae\u5236\u4e86\u6587\u80dc\u4e2d\uff0c\u56de\u8fc7\u6765\u518d\u70ae\u5236\u90a3\u4e2a\u770b\u5b88\u3002\u8fd9\u770b\u5b88\u672c\u6765\u662f\u8138\u671d\u5929\u7684\u8eba\u7740\u7684\uff0c\u91d1\u9010\u6d41 \u628a\u4ed6\u7ffb\u8f6c\u8fc7\u6765\uff0c\u8ba9\u4ed6\u80cc\u810a\u671d\u5929\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u5bb3\u6015\uff0c\u6211\u8e22\u4f60\u4e00\u811a\uff0c\u5305\u4f60\u8212\u670d\u5f97\u591a\uff01\u201d\u8bf4\u7f62 \u4e00\u811a\u671d\u4ed6\u5c41\u80a1\u8e22\u53bb\uff0c\u8fd9\u770b\u5b88\u672c\u6765\u662f\u7ed9\u70b9\u4e86\u9ebb\u7a74\u548c\u54d1\u7a74\u7684\uff0c\u7ed9\u4ed6\u4e00\u8e22\uff0c\u5374\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u53eb\u5f97 \u51fa\u6765\u4e86\u3002\u539f\u6765\u8fd9\u4e2a\u770b\u5b88\u6b63\u662f\u8e22\u8fc7\u91d1\u9010\u6d41\u5c41\u80a1\u7684\u90a3\u4e2a\u770b\u5b88\uff0c\u4e0d\u8fc7\uff0c\u5982\u4eca\u91d1\u9010\u6d41\u8e22\u4ed6\u5c41\u80a1\uff0c\u5374\u4e0d \u662f\u5b8c\u5168\u4e3a\u4e86\u62a5\u4ec7\uff0c\u800c\u662f\u7ed9\u4ed6\u89e3\u7a74\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8fd9\u4e00\u811a\u6c14\u529b\u4e0d\u5c0f\uff0c\u8fd9\u770b\u5b88\u7684\u7a74\u9053\u867d\u7136\u89e3\u4e86\uff0c\u5374\u662f\u9ebb\u8fa3\u8fa3\u7684\u597d\u4e0d\u96be\u53d7\u3002\u91d1\u9010\u6d41\u4e00\u628a \u5c06\u4ed6\u63d0\u4e86\u8fc7\u6765\uff0c\u5728\u4ed6\u8033\u8fb9\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4e0d\u8bb8\u53eb\u56b7\uff0c\u8001\u8001\u5b9e\u5b9e\u542c\u6211\u7684\u8bdd\uff0c\u5426\u5219\u6211\u5c31\u8981\u52a0\u4e0a\u5229\u606f \u4e86\uff01\u201d \u3000\u3000\u8fd9\u770b\u5b88\u5f3a\u5fcd\u8fa3\u75db\uff0c\u5fc3\u91cc\u5341\u5206\u5bb3\u6015\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u9b54\u5934\u4e0d\u77e5\u8fd8\u6709\u4ec0\u4e48\u72e0\u6bd2\u7684\u624b\u6bb5\uff1f\u6211\u843d\u5728 \u4ed6\u7684\u624b\u91cc\uff0c\u6ca1\u5948\u4f55\uff0c\u53ea\u597d\u542c\u4ed6\u7684\u8bdd\u4e86\u3002\u201d\u4e8e\u662f\u4e0d\u6562\u4f5c\u58f0\uff0c\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u91d1\u9010\u6d41\u548c\u4ed6\u624b\u633d\u7740\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u5f80\u524d\u5e26\u8def\u5427\uff01\u201d\u8fd9\u770b\u5b88\u5413\u4e86\u4e00\u8df3\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u6211 \u5e26\u4f60\u9003\u8d70\uff1f\u8fd9\u662f\u5343\u4e07\u4e0d\u884c\u7684\uff0c\u91cc\u91cc\u5916\u5916\u6709\u4e03\u516b\u91cd\u770b\u5b88\u5462\uff01\u51fa\u53e3\u4e4b\u5904\uff0c\u8fd8\u6709\u673a\u5173\u9677\u9631\uff0c\u8fde\u6211\u4e5f \u4e0d\u77e5\u9053\uff1a\u201c \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u8981\u4f60\u9003\u8d70\uff1f\u6211\u8981\u627e\u4f60\u7684\u8235\u4e3b\u7b97\u5e10\uff01\u201d\u770b\u5b88\u53c8\u662f\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u7956 \u5b97\uff0c\u4f60\u522b\u5bb3\u6211\uff0c\u6211\u7ed9\u4f60\u78d5\u5934\uff01\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u522b\u614c\uff0c\u6211\u53ea\u662f\u8981\u4f60\u5e26\u6211\u5230\u4ed6\u7684\u4f4f\u6240\u5916\u9762\uff0c\u4e0d \u5fc5\u4f60\u8fdb\u53bb\u3002\u201d \u3000\u3000\u770b\u5b88\u8fd8\u662f\u72b9\u7591\uff0c\u91d1\u9010\u6d41\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u53ea\u6015\u4f60\u7684\u8235\u4e3b\u7684\u5211\u7f5a\uff0c\u5c31\u4e0d\u6015\u6211\u4e0d\u6210\uff1f\u544a\u8bc9\u4f60\uff0c\u4f60 \u7684\u8235\u4e3b\u6700\u591a\u628a\u4f60\u4e00\u5200\u6740\u6389\uff0c\u6211\u9971\u5236\u4f60\uff0c\u53ef\u8981\u4f60\u6c42\u751f\u4e0d\u5f97\uff0c\u6c42\u6b7b\u4e0d\u80fd\uff0c\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d\u8fd9\u770b\u5b88\u6697 \u81ea\u601d\u91cf\uff1a\u201c\u6211\u53ea\u662f\u7ed9\u4ed6\u6307\u70b9\u8235\u4e3b\u7684\u4f4f\u6240\uff0c\u672a\u5fc5\u5c31\u4f1a\u7ed9\u4eba\u53d1\u89c9\uff0c\u4e8b\u540e\u4e5f\u8fd8\u53ef\u4ee5\u62b5\u8d56\u3002\u55ef\u2018\u597d \u6c49\u2019\u53ef\u4e0d\u80fd\u5403\u773c\u524d\u4e4b\u4e8f\u3002\u201d \u3000\u3000\u90a3\u770b\u5b88\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u54ed\u4e27\u7740\u8138\u9053\uff1a\u201c\u597d\u5566\uff0c\u5c0f\u79df\u5b97\uff0c\u4f60\u8981\u600e\u4e48\u6837\uff0c\u6211\u4f9d\u4f60\u5c31\u662f\u3002\u201d\u91d1 \u9010\u6d41\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u900d\uff1a\u201c\u770b\u4f60\u8fd9\u58f0\u5c0f\u7956\u5b97\u7684\u4efd\u4e0a\uff0c\u4f60\u8fd9\u6761\u5c0f\u547d\uff0c\u6211\u4e00\u5b9a\u7ed9\u4f60\u4fdd\u5168\u3002\u201d\u4e8e\u662f\u62bc \u7740\u90a3\u4e2a\u770b\u5b88\uff0c\u8d70\u51fa\u5f00\u95e8\u3002 \u3000\u3000\u6b64\u65f6\u5df2\u662f\u4e09\u66f4\u65f6\u5206\uff0c\u5929\u4e0a\u4e00\u5f2f\u65b0\u6708\uff0c\u6708\u8272\u5e76\u4e0d\u600e\u6837\u660e\u6717\uff0c\u4ed6\u4eec\u8fd9\u4e2a\u6d77\u7802\u5e2e\u662f\u505a\u8d29\u5356\u79c1\u76d1 \u7684\u751f\u610f\u7684\uff0c\u5728\u5927\u6c5f\u5357\u5317\u5404\u4e2a\u5e2e\u4f1a\u4e4b\u4e2d\u6700\u4e3a\u5bcc\u6709\u3002\u603b\u8235\u6240\u5728\uff0c\u5efa\u6709\u4e00\u4e2a\u5927\u82b1\u56ed\uff0c\u5360\u5730\u6570\u4ea9\uff0c\u4ea8 \u53f0\u697c\u9601\uff0c\u5047\u5c71\u8377\u6c60\uff0c\u661f\u7f57\u68cb\u5e03\uff0c\u5e94\u6709\u5c3d\u6709\u3002\u56da\u7981\u91d1\u9010\u6d41\u8fd9\u4e2a\u77f3\u7a9f\uff0c\u5c31\u5728\u82b1\u56ed\u7684\u4e00\u89d2\u3002\u8fd9\u4e2a\u770b \u5b88\u719f\u8bc6\u9053\u8def\uff0c\u5e26\u9886\u7740\u91d1\u9010\u6d41\uff0c\u7a7f\u82b1\u4e1b\uff0c\u7ed5\u5047\u5c71\uff0c\u62e3\u50fb\u9759\u7684\u5904\u6240\u884c\u8d70\uff0c\u907f\u8fc7\u5de1\u903b\u3002\u6708\u8272\u6726\u80e7\uff0c \u91d1\u9010\u6d41\u7a7f\u7684\u53c8\u662f\u6587\u80dc\u4e2d\u7684\u8863\u88f3\uff0c\u518d\u52a0\u4e0a\u6709\u90a3\u4e2a\u770b\u5b88\u966a\u4f34\u7740\u4ed6\uff0c\u56e0\u6b64\u5373\u4f7f\u6709\u4e00\u4e24\u4e2a\u5de1\u903b\u7684\u5e2e\u4e01 \u77a7\u89c1\u4ed6\u4eec\u7684\u5f71\u5b50\uff0c\u4e5f\u7edd\u5bf9\u8ba4\u4e0d\u51fa\u662f\u91d1\u9010\u6d41\u3002 \u3000\u3000\u5728\u8def\u4e0a\u91d1\u9010\u6d41\u7b80\u5355\u5730\u95ee\u4e86\u90a3\u4e2a\u770b\u5b88\u51e0\u4e2a\u95ee\u9898\uff0c\u8fd9\u624d\u77e5\u9053\u8fd9\u4e2a\u6d77\u7802\u5e2e\u7684\u5e2e\u4e3b\u540d\u53eb\u6c99\u5343\u5cf0\uff0c \u4e0e\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\u53f2\u767d\u90fd\u662f\u516b\u62dc\u4e4b\u4ea4\u3002\u6587\u9053\u5e84\u7236\u5b50\u6765\u6b64\u5df2\u6709\u6570\u5929\uff0c\u636e\u8bf4\u662f\u8981\u9080\u7ea6\u6c99\u5343\u5cf0\u4e00\u540c\u3002 \u4e0a\u4eac\u7ed9\u5927\u5185\u603b\u7ba1\u8428\u798f\u9f0e\u795d\u5bff\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u539f\u6765\u90fd\u662f\u4e00\u4e18\u4e4b\u8c89\uff0c\u60f3\u8981\u5df4\u7ed3\u671d\u4e2d\u8d35\u4eba\u3002\u597d\u5440\uff0c\u6211\u504f\u8981\u53eb\u4ed6\u4eec\u4e0d\u80fd \u5982\u613f\uff0c\u975e\u5f97\u95f9\u5b83\u4e00\u4e2a\u75db\u5feb\u4e0d\u53ef\uff01\u7384\u94c1\u56fa\u7136\u8981\u62ff\u56de\u6765\uff0c\u6587\u9053\u5e84\u8fd9\u53ae\uff0c\u66f4\u662f\u8981\u620f\u5f04\u4e2a\u591f\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u65e9\u5df2\u6210\u7af9\u5728\u80f8\uff0c\u60f3\u597d\u4e86\u8981\u600e\u6837\u620f\u5f04\u6587\u9053\u5e84\u7684\u4e86\u3002\u60f3\u5230\u5f97\u610f\u4e4b\u5904\uff0c\u4e0d\u77e5\u4e0d\u89c9\u7684 \u201c\u54c8\u201d\u4e00\u58f0\u7b11\u51fa\u6765\u3002\u90a3\u770b\u5b88\u5403\u4e86\u4e00\u60ca\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u7956\u5b97\uff0c\u4f60\u522b\u58f0\u5f20\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u89c1\u706b\u5149\u4e00\u4eae\uff0c\u6709\u4e2a\u4eba\u6253\u7740\u706f\u7b3c\u8d70\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5f20\u5c0f\u4e09\uff0c\u539f\u6765\u662f\u4f60\uff0c\u5012\u6559 \u6211\u5413\u4e86\u4e00\u8df3\u3002\u4f60\u4eec\u8c08\u4ec0\u4e48\u8c08\u5f97\u8fd9\u6837\u9ad8\u5174\u554a\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u662f\u5e2e\u4e2d\u7684\u53a8\u5b50\uff0c\u624b\u91cc\u63d0\u7740\u4e00\u4e2a\u6709\u76ca\u7684\u7af9\u7bee\u5b50\uff0c\u9e21\u8089\u7684\u9999\u5473\u5c01\u95ed\u4e0d\u4f4f\uff0c\u4ece\u7f1d\u7f45\u4e2d\u6d0b \u6ea2\u51fa\u6765\u3002\u539f\u6765\u662f\u6c99\u5343\u5cf0\u548c\u6587\u9053\u5e84\u8981\u5403\u6d88\u591c\uff0c\u53a8\u623f\u91cc\u7ed9\u4ed6\u505a\u4e86\u4e00\u53ea\u201c\u53eb\u5316\u9e21\u201d\uff0c\u8fde\u540c\u51e0\u6837\u7cbe\u7f8e \u7684\u5c0f\u83dc\uff0c\u53eb\u8fd9\u4e2a\u53a8\u5b50\u7ed9\u4ed6\u4eec\u9001\u53bb\u3002 \u3000\u3000\u8fd9\u53a8\u5b50\u662f\u4e00\u624b\u63d0\u7740\u7bee\u5b50\u4e00\u624b\u63d0\u7740\u706f\u7b3c\u7684\uff0c\u4ed6\u9996\u5148\u8ba4\u51fa\u4e86\u770b\u5b88\uff0c\u8ddf\u7740\u70db\u5149\u7167\u5230\u91d1\u9010\u6d41\u7684\u9762 \u4e0a\uff0c\u8fd9\u53a8\u5b50\u8ba4\u4e0d\u5f97\u91d1\u9010\u6d41\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5144\u5f1f\u662f\u65b0\u6765\u7684\u5417\uff1f\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a \u201c\u4e0d\u9519\uff0c\u6211\u8fd8\u6ca1\u6709\u8bd5\u8fc7\u4f60\u7684\u624b\u827a\uff0c\u8ba9\u6211\u5c1d\u5c1d\u5427\uff01\u201d\u500f\u5730\u5c31\u70b9\u4e86\u8fd9\u53a8\u5b50\u7684\u7a74\u9053\uff0c\u706d\u4e86\u706f\u7b3c\uff0c\u62a2 \u8fc7\u7bee\u5b50\uff0c\u628a\u84dd\u5b50\u585e\u8fdb\u5047\u5c71\u6d1e\u91cc\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5403\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u7b11\u9053\uff1a\u201c\u6211\u6789\u81ea\u505a\u4e86\u51e0\u4e2a\u6708\u7684\u5c0f\u53eb\u5316\uff0c\u5982\u4eca\u624d\u5403\u5230\u4e86\u6b63\u5b97\u7684\u53eb\u5316 \u9e21\u3002\u201d\u4ed6\u5df2\u6709\u5341\u4e8c\u4e2a\u65f6\u8fb0\u672a\u8fdb\u996d\u98df\uff0c\u6b63\u81ea\u89c9\u5f97\u809a\u997f\uff0c\u628a\u8fd9\u4efd\u4e30\u5bcc\u7684\u6d88\u591c\u5403\u4e86\u3002\u809a\u9971\u8eab\u6696\uff0c\u767b \u65f6\u7cbe\u795e\u5955\u5955\uff0c\u6c14\u529b\u4e5f\u5b8c\u5168\u6062\u590d\u4e86\u3002 \u3000\u3000\u90a3\u53ea\u53eb\u5316\u9e21\u5df2\u7ecf\u5403\u5b8c\uff0c\u91d1\u9010\u6d41\u8fd8\u820d\u4e0d\u5f97\u629b\u5f03\uff0c\u62ff\u7740\u5df2\u7ecf\u5403\u5149\u4e86\u8089\u7684\u9e21\u817f\uff0c\u5543\u90a3\u9aa8\u5934\uff0c\u4e00 \u9762\u5543\u4e00\u9762\u7b11\u9053\uff1a\u201c\u597d\u9999\uff0c\u597d\u9999\uff01\u8fde\u9aa8\u5934\u90fd\u662f\u9999\u7684\uff01\u201d\u90a3\u4e2a\u770b\u5b88\u62c5\u5fc3\u7ed9\u4eba\u53d1\u73b0\uff0c\u770b\u770b\u91d1\u9010\u6d41\u8fd9 \u526f\u998b\u76f8\uff01\u5374\u662f\u60f3\u7b11\u4e5f\u7b11\u4e0d\u51fa\u6765\u3002\u6b64\u65f6\u4ed6\u4eec\u5df2\u662f\u7ed5\u8fc7\u4e00\u5ea7\u5047\u5c71\uff0c\u524d\u9762\u7af9\u6797\u4e4b\u4e2d\u9690\u9690\u73b0\u51fa\u7ea2\u697c\u4e00 \u89d2\uff0c\u78a7\u7eb1\u7a97\u5916\uff0c\u900f\u51fa\u706f\u5149\uff0c\u90a3\u770b\u5b88\u5982\u91ca\u91cd\u8d1f\uff0c\u505c\u4e0b\u811a\u6b65\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u5230\u4e86\uff0c\u5230\u4e86\uff01\u6c99\u8235\u4e3b \u548c\u6587\u5c9b\u4e3b\u5c31\u662f\u5728\u8fd9\u5ea7\u697c\u4e2d\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\uff0c\u591a\u8c22\u4f60\u4e86\u3002\u8fd9\u9e21\u817f\u5f88\u597d\u5403\uff0c\u4f60\u4e5f\u5c1d\u4e00\u70b9\u5427\uff01\u53cd\u624b\u4e00\u6307\uff0c\u5c31\u7528\u90a3\u6839\u9e21\u9aa8 \u70b9\u4e86\u770b\u5b88\u7684\u7a74\u9053\uff0c\u4e0d\u8fc7\u7528\u7684\u4e0d\u662f\u91cd\u624b\u6cd5\uff0c\u53ea\u987b\u8fc7\u4e09\u4e24\u4e2a\u65f6\u8fb0\uff0c\u7a74\u9053\u5c31\u4f1a\u81ea\u89e3\u7684\u3002 \u3000\u3000\u7fe0\u7af9\u7ea2\u697c\uff0c\u82b1\u660e\u6708\u6697\uff0c\u6784\u6210\u4e86\u4e00\u5e45\u4f18\u7f8e\u7684\u56fe\u753b\u3002\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6c99\u5343\u5cf0\u8fd9\u5bb6\u4f19\u5012\u662f \u5f88\u4f1a\u4eab\u798f\uff0c\u53ef\u60dc\u8fd9\u6837\u4f18\u96c5\u7684\u5904\u6240\u7ed9\u4ed6\u7cdf\u8e4b\u4e86\u3002\u4e0d\u8fc7\u4e5f\u5e78\u4e8f\u6709\u8fd9\u7247\u7af9\u6797\uff0c\u7701\u5374\u6211\u8bb8\u591a\u6c14\u529b\u3002\u201d \u8981\u77e5\u6587\u9053\u5e84\u7684\u6b66\u5b66\u9020\u8be3\u751a\u9ad8\uff0c\u5e76\u4e0d\u5728\u91d1\u9010\u6d41\u4e4b\u4e0b\uff0c\u5982\u679c\u90a3\u5ea7\u7ea2\u697c\u524d\u9762\u662f\u7a7a\u8361\u8361\u7684\u4e00\u6797\uff0c\u91d1\u9010 \u6d41\u4e00\u5b9a\u4f1a\u7ed9\u4ed6\u4eec\u53d1\u89c9\uff0c\u867d\u8bf4\u91d1\u9010\u6d41\u672c\u6765\u5c31\u51c6\u5907\u8981\u548c\u4ed6\u4eec\u4ea4\u624b\uff0c\u4f46\u82e5\u8fc7\u65e9\u7ed9\u4eba\u53d1\u89c9\uff0c\u5374\u662f\u4e0e\u4ed6 \u8ba1\u5212\u4e0d\u7b26\u3002 \u3000\u3000\u4ed7\u7740\u90a3\u7247\u7af9\u6797\u63a9\u62a4\uff0c\u91d1\u9010\u6d41\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u5730\u8d70\u5230\u697c\u524d\u3002\u6b64\u65f6\u521a\u597d\u542c\u5f97\u6c99\u5343\u5cf0\u5728\u65a5\u9a82\u90a3\u4e2a \u201c\u64c5\u79bb\u804c\u5b88\u201d\u7684\u94b1\u5927\uff1a\u201c\u662f\u8c01\u53eb\u4f60\u56de\u6765\u7684\uff1f\u4f60\u5728\u5e2e\u4e2d\u8fd9\u8bb8\u591a\u5e74\uff0c\u600e\u7684\u8fde\u89c4\u77e9\u90fd\u5fd8\u8bb0\u4e86\uff1f\u6211\u6ca1 \u6d3e\u4eba\u7ed9\u4f60\u6362\u73ed\uff0c\u4f60\u600e\u4e48\u53ef\u4ee5\u79c1\u81ea\u56de\u6765\uff1f\u201d\u94b1\u5927\u5403\u4e86\u4e00\u60ca\uff0c\u55eb\u55eb\u5685\u5685\u5730\u8bf4\u9053\uff1a\u201c\u662f\u6587\u516c\u5b50\u4f20\u4e0b \u4f60\u7684\u547d\u4ee4\uff0c\u53eb\u6211\u6765\u56de\u8bdd\u7684\u3002\u6587\u516c\u5b50\u73b0\u5728\u6b63\u5728\u66ff\u6211\u770b\u5b88\u3002\u201d \u3000\u3000\u6c99\u5343\u5cf0\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u5413\uff0c\u662f\u6587\u516c\u5b50\u53eb\u4f60\u56de\u6765\u7684\u5417\uff1f\u201d\u6587\u9053\u5e84\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u6211\u660e\u767d \u4e86\uff0c\u60f3\u5fc5\u662f\u4e2d\u513f\u5bf9\u4f60\u521a\u624d\u7684\u8bf4\u8bdd\u542c\u5f97\u4e0d\u5927\u6e05\u695a\uff0c\u4f60\u53eb\u4ed6\u53bb\u95ee\u94b1\u5927\uff0c\u4ed6\u5374\u4ee5\u4e3a\u662f\u4f60\u8981\u94b1\u5927\u6765\u5411 \u4f60\u56de\u8bdd\u4e86\u3002\u597d\u5427\uff0c\u4f60\u4e0d\u653e\u5fc3\u8ba9\u4ed6\u770b\u5b88\uff0c\u6211\u53bb\u53eb\u4ed6\u56de\u6765\u5c31\u662f\u3002\u201d\u77e5\u5b50\u83ab\u82e5\u7236\uff0c\u6587\u9053\u5e84\u5f53\u7136\u60f3\u5f97 \u5230\u4ed6\u7684\u513f\u5b50\u662f\u8981\u53bb\u6697\u5bb3\u91d1\u9010\u6d41\uff0c\u8fde\u5fd9\u7ed9\u513f\u5b50\u63a9\u9970\uff0c\u91d1\u9010\u6d41\u5728\u957f\u6c5f\u88ab\u64d2\u4e4b\u9645\uff0c\u6587\u80dc\u4e2d\u5e76\u4e0d\u5728 \u573a\uff0c\u4ed6\u521a\u624d\u5411\u6c99\u5343\u5cf0\u95ee\u8d77\u91d1\u9010\u6d41\u7684\u60c5\u51b5\uff0c\u6c99\u5343\u5cf0\u662f\u66fe\u53eb\u8fc7\u4ed6\u81ea\u5df1\u53bb\u95ee\u94b1\u5927\u3002 \u3000\u3000\u6c99\u5343\u5cf0\u8d77\u4e86\u4e00\u70b9\u6000\u7591\uff0c\u4e0d\u8fc7\u788d\u7740\u6587\u9053\u5e84\u7684\u9762\u5b50\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u4ee4\u90ce\u770b\u5b88\uff0c\u6211\u5c82\u6709\u4e0d\u653e\u5fc3 \u7684\u4e86\uff0c\u4e0d\u8fc7\u6211\u4eec\u4e5f\u4e0d\u80fd\u8ba9\u4ee4\u90ce\u5c48\u5c45\u770b\u5b88\u4e4b\u804c\u5440\uff01\u94b1\u5927\uff0c\u4f60\u9a6c\u4e0a\u56de\u53bb\uff0c\u8bf7\u6587\u516c\u5b50\u56de\u6765\u3002\u201d\u94b1\u5927 \u7b54\u4e86\u4e00\u4e2a\u201c\u662f\u201d\u5b57\uff0c\u4fbf\u5373\u4e0b\u697c\u3002 \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u6211\u548c\u4f60\u4e00\u9053\u53bb\u5427\u3002\u4e2d\u513f\u592a\u7cca\u6d82\u4e86\uff0c\u6211\u4e5f\u5e94\u8be5\u6559\u8bad\u6559\u8bad\u4ed6\u3002\u201d\u6c99\u5343\u5cf0\u9053\uff1a \u201c\u7b11\u8bdd\uff0c\u7b11\u8bdd\uff01\u4e00\u70b9\u70b9\u5c0f\u4e8b\uff0c\u5c82\u80fd\u52b3\u70e6\u5c9b\u4e3b\uff1f\u4ee4\u90ce\u4e5f\u5e76\u6ca1\u6709\u4ec0\u4e48\u8fc7\u9519\uff0c\u4f60\u8fd9\u6837\u90d1\u91cd\u5176\u4e8b\u7684\u53bb \u5524\u4ed6\u56de\u6765\uff0c\u53cd\u8981\u628a\u4ed6\u5413\u614c\u4e86\u3002\u201d\u6c99\u5343\u5cf0\u54ea\u91cc\u77e5\u9053\u6587\u9053\u5e84\u4e43\u662f\u53e6\u6709\u7528\u610f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u6697\u6697\u597d\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u7b49\u4e0b\u5c31\u6709\u597d\u620f\u770b\u4e86\uff01\u201d\u8d81\u7740\u94b1\u5927\u4e0b\u697c\u7684\u65f6\u5019\uff0c\u4ed6\u5374\u4e00\u4e2a \u98de\u8eab\u4e0a\u4e86\u697c\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4ee5\u7edd\u9876\u8f7b\u529f\u4ece\u697c\u623f\u4fa7\u9762\u7684\u6697\u89d2\u98de\u8eab\u800c\u4e0a\uff0c\u94b1\u5927\u6b63\u5728\u4e0b\u697c\uff0c\u4e1d\u6beb\u4e5f\u6ca1\u77e5\u89c9\uff0c\u800c\u94b1\u5927 \u7684\u811a\u6b65\u58f0\u53c8\u6b63\u597d\u66ff\u4ed6\u4f5c\u4e86\u63a9\u62a4\uff0c\u5426\u5219\u4ed6\u867d\u7136\u8f7b\u529f\u8d85\u5999\uff0c\u4f46\u603b\u4e0d\u514d\u6709\u70b9\u8863\u895f\u5e26\u98ce\u4e4b\u58f0\u53ef\u80fd\u4f1a\u7ed9 \u6587\u9053\u5e84\u5bdf\u89c9\u3002 \u3000\u3000\u5176\u5b9e\uff0c\u6587\u9053\u5e84\u6b64\u65f6\u6b63\u5728\u62c5\u7740\u5fc3\u4e8b\uff0c\u5373\u4f7f\u6ca1\u6709\u94b1\u5927\u7684\u811a\u6b65\u58f0\u66ff\u91d1\u9010\u6d41\u63a9\u62a4\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u5bdf\u89c9 \u7684\u3002\u4ed6\u62c5\u5fc3\u7684\u662f\uff1a\u201c\u5018\u82e5\u4e2d\u513f\u4e0d\u8bc6\u5206\u5bf8\uff0c\u4f24\u4e86\u90a3\u5c0f\u5b50\u7684\u6027\u547d\uff0c\u5728\u53f2\u767d\u90fd\u7684\u9762\u524d\u53ef\u662f\u4e0d\u597d\u4ea4 \u4ee3\uff1b\u800c\u4e14\u4e5f\u8981\u4ee4\u6c99\u5e2e\u4e3b\u4e3a\u96be\u4e86\uff01\u4e0d\u8fc7\u4e2d\u513f\u5927\u7ea6\u4e5f\u8fd8\u4e0d\u81f3\u4e8e\u8fd9\u6837\u4e0d\u61c2\u4e8b\u5427\uff1f\u201d \u3000\u3000\u6c99\u5343\u5cf0\u9053\uff1a\u201c\u6587\u5c9b\u4e3b\u4e0d\u5fc5\u6302\u5fc3\uff0c\u51b3\u4e0d\u81f3\u4e8e\u51fa\u4e8b\u7684\u3002\u90a3\u5c0f\u5b50\u4e0d\u662f\u670d\u4e86\u4f60\u7684\u9165\u9aa8\u6563\u5417\uff1f\u201d\u4ed6 \u53ea\u5f53\u6587\u9053\u5e84\u662f\u653e\u5fc3\u4e0d\u4e0b\u4ed6\u7684\u513f\u5b50\u5728\u90a3\u91cc\u770b\u5b88\u3002 \u3000\u3000\u6587\u9053\u5e84\u4e0d\u613f\u8ba9\u4ed6\u770b\u7834\u5fc3\u4e8b\uff0c\u4e0d\u9732\u75d5\u8ff9\u5730\u7b11\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u5f53\u7136\u662f\u63d2\u7ffc\u96be\u98de\u7684\u4e86\u3002\u4e0d\u8fc7\uff0c\u6211 \u8fd8\u662f\u6709\u70b9\u653e\u5fc3\u4e0d\u4e0b\uff01\u201d\u91d1\u9010\u6d41\u6b64\u65f6\u6b63\u8eb2\u5728\u540e\u7a97\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u4e0d\u89c9\u5fc3\u91cc\u53c8\u662f\u6697\u6697\u597d\u7b11\uff1a\u201c\u4f60\u8bf4 \u6211\u63d2\u7ffc\u96be\u98de\uff0c\u6211\u5374\u504f\u504f\u2018\u98de\u2019\u5230\u4e86\u4f60\u7684\u8eab\u8fb9\u4e86\u3002\u201d \u3000\u3000\u6c99\u5343\u5cf0\u8be7\u9053\uff1a\u201c\u65e2\u7136\u90a3\u5c0f\u5b50\u5df2\u662f\u63d2\u7ffc\u96be\u98de\uff01\u6587\u5c9b\u4e3b\u8fd8\u6709\u4ec0\u4e48\u653e\u5fc3\u4e0d\u4e0b\uff1f\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u6211\u4e0d\u662f\u62c5\u5fc3\u4ed6\u5728\u8fd9\u91cc\u9003\u5f97\u8131\uff0c\u6211\u662f\u62c5\u5fc3\u5c06\u4ed6\u9001\u5230\u4e86\u516d\u5408\u5e2e\u4e4b\u540e\uff0c\u90a3\u4e48\u54b1\u4eec \u53ef\u5c31\u7ba1\u4e0d\u7740\u4ed6\u4e86\uff01\u201d \u3000\u3000\u6c99\u5343\u5cf0\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u662f\u6015\u53f2\u767d\u90fd\u7684\u59b9\u5b50\u66ff\u4ed6\u8bf4\u60c5\uff0c\u53f2\u767d\u90fd\u4f1a\u5c06\u4ed6\u653e\u4e86\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6b63\u662f\u6709\u6b64\u987e\u8651\u3002\u201d \u3000\u3000\u6c99\u5343\u5cf0\u7b11\u9053\uff1a\u201c\u90a3\u4f60\u53ef\u4e0d\u7528\u62c5\u5fc3\u4e86\u3002\u53e4\u4eba\u8bf4\uff1a\u2018\u7ea2\u989c\u7978\u6c34\u2019\u53f2\u767d\u90fd\u7684\u59b9\u5b50\u5c31\u662f\u2018\u7978 \u6c34\u2019\uff0c\u54ea\u4e2a\u7537\u5b50\u60f9\u4e0a\u4e86\u5979\u90fd\u8981\u906d\u6b83\u3002\u53ea\u9664\u4e86\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u8fd9\u5374\u4e3a\u4f55\uff1f\u90a3\u4e2a\u4eba\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u6c99\u5b50\u5cf0\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff0c\u53f2\u767d\u90fd\u6709\u5fc3\u5c06\u59b9\u5b50\u8bb8\u914d\u7ed9\u5e05\u5b5f\u96c4\uff0c\u53cc\u65b9\u5df2\u6709\u4fe1\u4f7f\u5f80\u8fd8\uff0c\u53ea\u662f\u5a5a \u4e8b\u5c1a\u672a\u8bba\u6210\u3002\u6240\u4ee5\u9664\u4e86\u5e05\u5b5f\u96c4\u4e4b\u5916\uff0c\u8c01\u8981\u662f\u60f3\u5403\u8fd9\u5757\u5929\u9e45\u8089\u7684\uff0c\u5fc5\u5b9a\u8981\u906d\u6740\u8eab\u4e4b\u7978\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5e05\u5b5f\u96c4\u8fd9\u540d\u5b57\u597d\u719f\uff01\u201d\u6025\u5207\u95f4\u672a\u66fe\u60f3\u8d77\uff0c\u53ea\u542c\u5f97\u6587\u9053\u5e84\u5df2\u5728\u95ee\u9053\uff1a \u201c\u5e05\u5b5f\u96c4\uff1f\u55ef\uff0c\u53ef\u5c31\u662f\u4f24\u4e86\u7afa\u5c1a\u7236\u7684\u90a3\u4e2a\u4eba\u5417\uff1f\u201d \u3000\u3000\u6c99\u5343\u5cf0\u9053\uff1a\u201c\u6b63\u662f\u3002\u5e05\u5b5f\u96c4\u4f24\u4e86\u7afa\u5c1a\u7236\uff0c\u66ff\u671d\u5ef7\u593a\u56de\u4e86\u897f\u661f\uff0c\u2018\u5723\u7737\u2019\u6b63\u9686\u5462\uff01\u6240\u4ee5\u53f2 \u767d\u90fd\u90fd\u8981\u5df4\u7ed3\u4ed6\u3002\u201d\u63a5\u7740\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u7afa\u5c1a\u7236\u53f7\u79f0\u5929\u4e0b\u7b2c\u4e8c\u9ad8\u624b\uff0c\u5c1a\u4e14\u7ed9\u5e05\u5b5f\u96c4\u4f24\u4e86\uff0c \u518d\u52a0\u4e0a\u4e00\u4e2a\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\u53f2\u767d\u90fd\uff0c\u8c01\u80fd\u60f9\u5f97\u8d77\u4ed6\u4eec\uff1f\u53f2\u767d\u90fd\u53ea\u662f\u60f3\u4ece\u90a3\u59d3\u91d1\u7684\u5c0f\u5b50\u53e3\u4e2d\uff0c\u95ee \u51fa\u4ed6\u59b9\u5b50\u7684\u4e0b\u843d\uff0c\u56e0\u6b64\u624d\u4e0d\u8bb8\u54b1\u4eec\u6740\u4ed6\uff0c\u5f85\u4ed6\u95ee\u51fa\u53e3\u4f9b\u4e4b\u540e\u4ed6\u81ea\u5df1\u5c31\u4e00\u5b9a\u4f1a\u4eb2\u624b\u6740\u90a3\u5c0f\u5b50 \u7684\u3002\u4ed6\u8981\u59b9\u5b50\u5ac1\u7ed9\u5e05\u76c2\u96c4\uff0c\u5c82\u80fd\u8ba9\u5979\u7684\u60c5\u90ce\u6d3b\u5728\u4e16\u4e0a\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u53f9\u5012\u6963\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff01\u8fd9\u771f\u662f\u672a\u5403\u7f8a\u8089\u5148\u60f9\u4e86\u4e00\u8eab\u9a9a\u3002\u6211\u4e0e\u53f2\u7ea2\u82f1 \u53ea\u4e0d\u8fc7\u89c1\u4e0a\u4e00\u9762\uff0c\u4ed6\u4eec\u7adf\u7136\u5c31\u628a\u6211\u5f53\u4f5c\u662f\u5979\u7684\u60c5\u4eba\u4e86\u3002\u4f46\u53f2\u767d\u90fd\u8fd9\u53ae\u4e5f\u592a\u662f\u5351\u9119\uff0c\u4ed6\u81ea\u5df1\u8981 \u5df4\u7ed3\u8428\u798f\u9f0e\u4e5f\u8fd8\u7f62\u4e86\uff0c\u5374\u8fde\u59b9\u59b9\u4e5f\u60f3\u5f53\u4f5c\u793c\u7269\u9001\u7ed9\u522b\u4eba\u3002\u4e0d\u77e5\u53f2\u7ea2\u82f1\u77e5\u9053\u4e86\u6b64\u4e8b\u6ca1\u6709\uff1f\u54fc\uff0c \u53cd\u6b63\u4ed6\u4eec\u5df2\u7ecf\u662f\u628a\u6211\u8bef\u4f1a\u7684\u4e86\uff0c\u6211\u4e5f\u4e0d\u6015\u4eba\u5bb6\u8f88\u77ed\u6d41\u957f\uff0c\u975e\u60f9\u4e00\u60f9\u90a3\u53f2\u767d\u90fd\u548c\u5e05\u5b5f\u96c4\u4e0d\u53ef\uff01 \u770b\u770b\u4ed6\u4eec\u80fd\u7ed9\u6211\u964d\u4e9b\u4ec0\u4e48\u707e\u6b83\uff1f\u201d \u3000\u3000\u6587\u9053\u5e84\u542c\u4e86\u6c99\u5343\u5cf0\u7684\u8a00\u8bed\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u8fd9\u59d3\u91d1\u7684\u5c0f\u5b50\u662f\u6b7b\u5b9a\u7684\u4e86\uff01\u201d\u6c99 \u5343\u5cf0\u9053\uff1a\u201c\u5f53\u7136\u3002\u6240\u4ee5\u6211\u51c6\u5907\u660e\u5929\u5c31\u628a\u4ed6\u9001\u5230\u516d\u5408\u5e2e\u53bb\uff0c\u7701\u5374\u54b1\u4eec\u8981\u6d3e\u4eba\u770b\u5b88\u4ed6\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u88c5\u4f5c\u6f2b\u4e0d\u7ecf\u610f\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u53ea\u6253\u7b97\u628a\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\u9001\u53bb\u5417\uff1f\u201d\u6c99\u5343\u5cf0\u6014\u4e86\u4e00\u6014\uff0c \u8bf4\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u53ef\u662f\u5728\u95ee\u90a3\u5757\u7384\u94c1\uff1f\u201d\u6587\u9053\u5e84\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u5757\u7384\u94c1\u4f60\u5f52\u4e0d\u5f52\u8fd8\u516d\u5408\u5e2e \u5462\uff1f\u201d \u3000\u3000\u6c99\u5343\u5cf0\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u6309\u9053\u7406\u6211\u662f\u5e94\u8be5\u5f52\u8fd8\u516d\u5408\u5e2e\u7684\uff0c\u4f46\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6211\u5374\u5b9e\u5728\u662f\u6709 \u70b9\u820d\u4e0d\u5f97\u8fd9\u4ef6\u5b9d\u8d1d\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u90a3\u4f60\u6253\u7b97\u600e\u6837\u529e\uff1f\u201d \u3000\u3000\u6c99\u5343\u5cf0\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u63a8\u8bf4\u8fd9\u5757\u7384\u94c1\u5df2\u7ecf\u7ed9\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\u629b\u4e0b\u957f\u6c5f\u4e86\uff0c\u6211\u7684\u624b\u4e0b\u4eba\u4e0d\u4f1a\u6cc4 \u6f0f\u51fa\u53bb\u7684\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u76ae\u7b11\u8089\u4e0d\u7b11\u5730\u8bf4\u9053\uff1a\u201c\u4eba\u591a\u5634\u6742\uff0c\u53ea\u6015\u4e5f\u4e0d\u5bb9\u6613\u906e\u7792\u5427\uff1f\u201d \u3000\u3000\u6c99\u5343\u5cf0\u731b\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u8981\u9760\u8001\u54e5\u5e2e\u5fd9\u4e86\uff0c\u90a3\u5757\u7384\u94c1\u54b1\u4eec\u4e24\u4eba\u5206\u4e86\u5427\u3002\u6211\u770b\u90a3\u5757 \u7384\u94c1\u6709\u4e00\u767e\u591a\u65a4\u91cd\uff0c\u94f8\u6210\u4e24\u67c4\u5b9d\u5251\u4e5f\u53ef\u4ee5\u7684\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u6447\u4e86\u6447\u624b\uff0c\u7b11\u9053\uff1a\u201c\u6c99\u5144\u4f11\u8981\u8bef\u4f1a\uff0c\u6211\u5e76\u4e0d\u662f\u60f3\u8981\u5206\u4f60\u7684\u5b9d\u8d1d\u3002\u6211\u53ea\u662f\u5728\u60f3\uff0c\u5982 \u6b64\u4e00\u6765\uff0c\u53ea\u6015\u3001\u53ea\u6015\u662f\u56e0\u5c0f\u5931\u5927\u3002\u201d \u3000\u3000\u6c99\u5343\u5cf0\u609a\u7136\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u4f55\u56e0\u5c0f\u5931\u5927\uff0c\u8bf7\u6587\u5c9b\u4e3b\u6307\u6559\uff1f\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u8fd9\u5757\u7384\u94c1\u662f\u8981\u9001\u7ed9\u8428\u603b\u7ba1\u4f5c\u793c\u7269\u7684\uff0c\u4f60\u628a\u5b83\u94f8\u6210\u4e86\u5b9d\u5251\uff0c\u9664\u975e\u662f\u6c38\u8fdc\u4e0d\u62ff \u6765\u4f7f\u7528\uff0c\u5426\u5219\u8fd9\u79d8\u5bc6\u5c82\u80fd\u4e0d\u9732\uff1f\u79d8\u5bc6\u4e00\u9732\uff0c\u975e\u4f46\u662f\u5f97\u7f6a\u4e86\u53f2\u767d\u90fd\uff0c\u53ea\u6015\u8428\u603b\u7ba1\u4e5f\u8981\u5bf9\u4ed8\u4f60 \u4e86\u3002\u201d \u3000\u3000\u6c99\u5343\u5cf0\u9053\uff1a\u201c\u90a3\u4e48\u4f9d\u4f60\u4e4b\u89c1\uff1f\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u6211\u5012\u6709\u4e00\u4e2a\u4e24\u5168\u5176\u7f8e\u7684\u529e\u6cd5\uff0c\u8fd9\u5757\u7384\u94c1\u4f9d\u7136\u9001\u8fdb\u4eac\u53bb\uff0c\u4e0d\u8fc7\u4e0d\u662f\u4ea4\u7ed9\u516d\u5408 \u5e2e\u7684\u4eba\u9001\u53bb\uff0c\u54b1\u4eec\u7ed9\u4ed6\u9001\u53bb\uff01\u201d \u3000\u3000\u6c99\u5343\u5cf0\u9053\uff1a\u201c\u5982\u6b64\u5c82\u975e\u8d8a\u4fce\u4ee3\u75b1\uff0c\u53f2\u767d\u90fd\u9762\u524d\u600e\u6837\u4ea4\u4ee3\uff1f\u800c\u4e14\u8fd9\u6837\u505a\u5bf9\u54b1\u4eec\u53c8\u6709\u4ec0\u4e48\u597d \u5904\uff1f\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u597d\u5904\u591a\u7740\u5462\uff01\u53f2\u767d\u90fd\u4e5f\u4e0d\u4f1a\u602a\u4f60\u7684\u3002\u4f60\u542c\u6211\u8bf4\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u6545\u610f\u540a\u4e00\u540a\u6c99\u5343\u5cf0\u7684\u80c3\u53e3\uff0c\u6162\u6761\u65af\u7406\u5730\u559d\u4e86\u4e00\u53e3\u8336\uff0c\u8fd9\u624d\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u8428\u603b\u7ba1 \u7684\u5bff\u8fb0\u662f\u4e0b\u4e2a\u6708\u5341\u516b\uff0c\u8ddd\u79bb\u73b0\u5728\u5df2\u662f\u4e0d\u5230\u4e00\u4e2a\u6708\u4e86\u3002\u5018\u82e5\u54b1\u4eec\u628a\u8fd9\u5757\u7384\u94c1\u5148\u4ea4\u8fd8\u516d\u5408\u5e2e\uff0c\u518d \u7531\u516d\u5408\u5e2e\u6d3e\u4eba\u5c06\u5b83\u9001\u53bb\uff0c\u4e00\u5f80\u4e00\u8fd4\uff0c\u8d77\u7801\u4e5f\u8981\u803d\u6401\u4e2a\u5341\u5929\u516b\u5929\uff0c\u90a3\u5c31\u8d76\u4e0d\u4e0a\u5bff\u671f\u4e86\u3002\u5982\u4eca\u54b1 \u4eec\u66ff\u4ed6\u9001\u53bb\uff0c\u4eba\u60c5\u4ecd\u662f\u4ed6\u516d\u5408\u5e2e\u7684\uff0c\u53f2\u767d\u90fd\u611f\u6fc0\u4f60\u90fd\u6765\u4e0d\u53ca\u5462\uff0c\u8fd8\u80fd\u602a\u4f60\u5417\uff1f\u201d \u3000\u3000\u6c99\u5343\u5cf0\u867d\u7136\u4e0d\u662f\u4e00\u4e2a\u5341\u8db3\u7684\u83bd\u592b\uff0c\u4f46\u5fc3\u8ba1\u4e4b\u5de5\uff0c\u5374\u662f\u8fdc\u8fdc\u4e0d\u5982\u6587\u9053\u5e84\u3002\u542c\u4e86\u8fd9\u8bdd\uff0c\u4e0d\u7531 \u5f97\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f46\u54b1\u4eec\u7684\u597d\u5904\u53c8\u4f55\u5728\u5462\uff1f\u201d \u3000\u3000\u6587\u9053\u5e84\u7b11\u9053\uff1a\u201c\u5f53\u7136\u662f\u5927\u6709\u597d\u5904\u3002\u6d77\u7802\u5e2e\u7684\u4eba\u66ff\u516d\u5408\u5e2e\u9001\u793c\uff0c\u8428\u603b\u7ba1\u8fd8\u80fd\u4e0d\u95ee\u539f\u7531\u5417\uff1f \u563f\uff0c\u563f\uff01\u53ea\u8981\u4ed6\u4e00\u5411\uff0c\u54b1\u4eec\u5c31\u53ef\u4ee5\u548c\u76d8\u6258\u51fa\u3002\u8428\u603b\u7ba1\u77e5\u9053\u8fd9\u5757\u7384\u94c1\u5728\u516d\u5408\u5e2e\u7684\u9ad8\u624b\u4e4b\u4e2d\u5931 \u6389\uff0c\u662f\u4f60\u66ff\u4ed6\u593a\u4e86\u56de\u6765\uff0c\u54c8\u54c8\uff0c\u5bf9\u4f60\u8fd8\u80fd\u4e0d\u522b\u773c\u76f8\u770b\uff1f\u4f60\u5728\u8428\u603b\u7ba1\u5fc3\u4e2d\u7684\u5730\u4f4d\uff0c\u81f3\u5c11\u9ad8\u8fc7\u53f2 \u767d\u90fd\u4e86\uff01\u201d \u3000\u3000\u6c99\u5343\u5cf0\u7709\u98de\u8272\u821e\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u6709\u9053\u7406\uff0c\u5927\u6709\u9053\u7406\uff01\u53ea\u662f\u6211\u548c\u8428\u603b\u7ba1\u7d20\u4e0d\u76f8\u8bc6\uff0c\u603b\u5f97\u6709\u4e2a \u4eba\u7ed9\u6211\u5f15\u89c1\u624d\u884c\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u8fd9\u4e2a\u6c99\u5144\u4e0d\u7528\u62c5\u5fe7\uff0c\u5305\u5728\u5c0f\u5f1f\u8eab\u4e0a\u3002\u201d\u6587\u9053\u5e84\u53d7\u4e86\u8428\u798f\u9f0e\u7684\u8058\u793c\uff0c\u8fd9\u4ef6\u4e8b \u60c5\u6c99\u5343\u5cf0\u662f\u65e9\u5df2\u77e5\u9053\u4e86\u7684\uff0c\u4ed6\u6b63\u662f\u8981\u6587\u9053\u5e84\u8bf4\u51fa\u8fd9\u53e5\u8bdd\u6765\u3002\u5f53\u4e0b\u8fde\u5fd9\u9053\u8c22\u3002 \u3000\u3000\u6587\u9053\u5e84\u7b11\u9053\uff1a\u201c\u4f60\u6211\u60c5\u5982\u624b\u8db3\uff0c\u4e9b\u987b\u5c0f\u4e8b\uff0c\u4f55\u8db3\u6302\u9f7f\uff1f\u660e\u5929\u54b1\u4eec\u5c31\u4e00\u540c\u4e0a\u4eac\uff0c\u5230\u4eac\u4e4b \u540e\uff0c\u6211\u628a\u7384\u94c1\u7ed9\u4eec\u5148\u884c\u9001\u53bb\u3002\u6211\u548c\u8428\u603b\u7ba1\u662f\u4e8c\u5341\u5e74\u7684\u8001\u670b\u53cb\u4e86\uff0c\u4f60\u4e0d\u65b9\u4fbf\u8bf4\u7684\u8bdd\uff0c\u6211\u90fd\u53ef\u4ee5 \u66ff\u4f60\u8bf4\u3002\u5f85\u6211\u5b89\u6392\u59a5\u5f53\uff0c\u7ea6\u597d\u65e5\u671f\uff0c\u6211\u518d\u966a\u4f60\u8fdb\u8c12\u3002\u4f60\u770b\u53ef\u597d\uff1f\u201d \u3000\u3000\u6587\u9053\u5e84\u53e3\u53e3\u58f0\u58f0\u8bf4\u662f\u4e3a\u6c99\u5343\u5cf0\u6253\u7b97\uff0c\u5176\u5b9e\u662f\u4e3a\u81ea\u5df1\u6253\u7b97\u3002\u8981\u77e5\u90a3\u5757\u7384\u94c1\u82e5\u662f\u94f8\u6210\u4e00\u628a\u5b9d \u5251\uff0c\u53ef\u4ee5\u5929\u4e0b\u65e0\u654c\uff0c\u82e5\u662f\u94f8\u6210\u4e24\u628a\u5b9d\u5251\uff0c\u91cd\u91cf\u51cf\u4e86\u4e00\u534a\uff0c\u5a01\u529b\u4e5f\u5c31\u8981\u6253\u4e2a\u5bf9\u6298\u4e86\u3002\u6240\u4ee5\u6587\u9053 \u5e84\u4e0d\u613f\u4e0e\u6c99\u5343\u5cf0\u5206\u4eab\uff0c\u800c\u662f\u60f3\u8981\u72ec\u541e\u3002\u53ea\u8981\u8fd9\u5757\u7384\u94c1\u5230\u4e86\u4ed6\u7684\u624b\u4e2d\uff0c\u4ed6\u81ea\u6709\u529e\u6cd5\u53ef\u4ee5\u5360\u4e3a\u5df1 \u6709\u3002\u201d \u3000\u3000\u6c99\u5343\u5cf0\u4e0d\u77e5\u662f\u8ba1\uff0c\u9053\u8c22\u8fc7\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u6b64\uff0c\u8fd8\u8981\u8bf7\u6587\u5144\u5728\u8428\u603b\u7ba1\u9762\u7684\u7f8e\u8a00\u51e0\u53e5\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\uff0c\u4e0d\u7528\u8001\u5144\u5429\u5490\uff0c\u5c0f\u5f1f\u81ea\u4f1a\u7701\u5f97\u3002\u53ea\u662f\u90a3\u5757\u7384\u94c1\u5c0f\u5f1f\u5c1a\u672a \u89c1\u8fc7\uff0c\u8bf7\u8001\u5144\u62ff\u51fa\u6765\uff0c\u8ba9\u5c0f\u5f1f\u89c1\u8bc6\u89c1\u8bc6\u5982\u4f55\uff1f\u5426\u5219\u4e34\u65f6\u624d\u53eb\u5c0f\u5f1f\u9001\u53bb\uff0c\u53ea\u6015\u6211\u5c31\u96be\u4ee5\u8bf4\u5f97\u6e05 \u695a\u5b83\u7684\u597d\u5904\u4e86\u3002\u201d \u3000\u3000\u6c99\u5343\u5cf0\u9053\uff1a\u201c\u6211\u672c\u6765\u51c6\u5907\u5728\u5403\u4e86\u5bb5\u591c\u4e4b\u540e\uff0c\u62ff\u51fa\u6765\u5927\u5bb6\u9274\u8d4f\u7684\u3002\u53a8\u5b50\u4e0d\u77e5\u5728\u5f04\u4ec0\u4e48\uff0c\u8fd9 \u4e2a\u65f6\u5019\u8fd8\u6ca1\u9001\u6765\u3002\u597d\u5427\uff0c\u54b1\u4eec\u4e0d\u7b49\u4ed6\u4e86\u3002\u8bf7\u8001\u5144\u7a0d\u5f85\u7247\u523b\uff0c\u6211\u9a6c\u4e0a\u5c31\u628a\u7384\u94c1\u62ff\u6765\u3002\u201d \u3000\u3000\u90a3\u4e2a\u88c5\u7740\u7384\u94c1\u7684\u7ea2\u6f06\u5323\u5b50\u85cf\u5728\u6c99\u5343\u5cf0\u7684\u5367\u5ba4\uff0c\u5367\u623f\u4e0e\u5ba2\u5385\u76f8\u8fde\uff0c\u91d1\u9010\u6d41\u8f6c\u4e86\u4e2a\u8eab\uff0c\u7528\u4e2a \u201c\u5012\u6302\u91d1\u94a7\u201d\u7684\u8eab\u6cd5\uff0c\u53cc\u8db3\u52fe\u7740\u5c4b\u6a90\uff0c\u773c\u775b\u6b63\u5bf9\u7740\u5367\u623f\u7684\u540e\u7a97\u3002\u6b64\u65f6\u6c99\u5343\u5cf0\u5df2\u5728\u623f\u4e2d\u70b9\u8d77\u6cb9 \u706f\uff0c\u4ed6\u7684\u52a8\u4f5c\u91d1\u9010\u6d41\u53ef\u4ee5\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u91d1\u9010\u6d41\u6b63\u662f\u60f3\u77e5\u9053\u7384\u94c1\u7684\u6536\u85cf\u4e4b\u5904\uff0c\u96be\u5f97\u6c99\u5343\u5cf0\u81ea \u5df1\u53bb\u53d6\u51fa\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6697\u6697\u6b22\u559c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u59ec\u4f2f\u4f2f\u6559\u7ed9\u6211\u7684\u5999\u624b\u7a7a\u7a7a\u7684\u624b\u6bb5\uff0c\u4eca\u665a\u662f\u6b63\u597d\u65bd\u5c55 \u4e86\uff01\u201d \u3000\u3000\u53ea\u89c1\u6c99\u5343\u5cf0\u5728\u5899\u4e0a\u6309\u4e86\u4e24\u6309\uff0c\u6253\u5f00\u4e00\u9053\u6697\u95e8\uff0c\u5728\u590d\u58c1\u91cc\u62d6\u51fa\u4e00\u4e2a\u94c1\u7bb1\uff0c\u518d\u6253\u5f00\u94c1\u7bb1\uff0c\u624d \u53d6\u51fa\u90a3\u4e2a\u7ea2\u6f06\u5323\u5b50\u3002\u91d1\u9010\u6d41\u5fc3\u91cc\u6697\u7b11\uff1a\u201c\u6536\u85cf\u5f97\u5982\u6b64\u4e25\u5bc6\uff0c\u82e5\u4e0d\u662f\u4ed6\u81ea\u5df1\u62ff\u51fa\u6765\uff0c\u6211\u8fd8\u5f53\u771f \u96be\u627e\u5462\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u6b63\u8981\u593a\u90a3\u5323\u5b50\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u6c99\u5343\u5cf0\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u628a\u90a3\u5323\u5b50\u629b\u4e86\u8d77\u6765\uff0c\u8138 \u4e0a\u73b0\u51fa\u4e00\u526f\u4f3c\u662f\u5bf9\u67d0\u4ef6\u4e8b\u60c5\u610f\u60f3\u4e0d\u5230\u7684\u832b\u7136\u795e\u8272\uff01 \u3000\u3000\u91d1\u9010\u6d41\u53ea\u9053\u6c99\u5343\u5cf0\u5df2\u7ecf\u53d1\u89c9\u4e86\u4ed6\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u7acb\u5373\u628a\u624b\u4e00\u626c\uff0c\u628a\u90a3\u6839\u9e21\u817f\u9aa8\u5f53\u4f5c\u6697\u5668\uff0c \u5c04\u4e86\u8fdb\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u53eb\u5316\u9e21\uff0c\u8089\u6211\u5403\u4e86\uff0c\u8ba9\u4f60\u4e5f\u5c1d\u5c1d\u9aa8\u5934\u5427\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u8fd9\u6839\u9aa8\u5934\u672c\u6765\u662f\u8981\u6253\u6c99\u5343\u5cf0\u9f3b\u5b50\u4e0b\u9762\u7684\u201c\u8fce\u9999\u7a74\u201d\u7684\uff0c\u6c99\u5343\u5cf0\u5ffd\u5730\u62ac\u8d77\u5934\u6765\uff0c \u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u5634\u5df4\u672a\u66fe\u5408\u62e2\uff0c\u90a3\u6839\u9aa8\u5934\u6070\u597d\u5c31\u585e\u8fdb\u4ed6\u7684\u5634\u5df4\u4e86\u3002\u201c\u8fce\u9999\u7a74\u201d\u82e5\u7ed9\u70b9\u7740\u4eba\u4f1a \u660f\u8ff7\uff0c\u5982\u4eca\u6c99\u5343\u5cf0\u662f\u4fa5\u5e78\u8eb2\u8fc7\uff0c\u4f46\u7ed9\u9e21\u9aa8\u585e\u8fdb\u5634\u5df4\uff0c\u4e5f\u662f\u96be\u53d7\u7684\u4e86\u3002 \u3000\u3000\u6284\u5343\u5cf0\u53d7\u4e86\u8fd9\u7a81\u5982\u5176\u6765\u7684\u88ad\u51fb\u96f7\u4f3c\u5730\u543c\u4e86\u4e00\u58f0\uff0c\u9e21\u9aa8\u5410\u4e86\u51fa\u6765\uff0c\u95e8\u7259\u5df2\u88ab\u6253\u843d\u4e24\u9f7f\u3002\u8bf4 \u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u91d1\u9010\u6d41\u5df2\u662f\u7834\u7a97\u800c\u5165\uff0c\u90a3\u7ea2\u6f06\u5323\u8fd8\u672a\u843d\u4e0b\uff0c\u5df2\u88ab\u91d1\u9010\u6d41\u63a5\u5230\u4e86\u624b\u4e2d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u67aa\u5230\u4e86\u5323\u5b50\uff0c\u5ffd\u5730\u4e5f\u662f\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u60ca\u53eb\u8d77\u6765\uff01\u539f\u6765\u8fd9\u5323\u5b50\u62ff\u5728\u624b\u91cc\u8f7b\u98d8\u98d8\u7684\uff0c \u81f3\u591a\u53ea\u6709\u4e09\u4e8c\u65a4\u91cd\uff0c\u663e\u7136\u8fd9\u53ea\u662f\u5323\u5b50\u672c\u8eab\u7684\u91cd\u91cf\uff0c\u91cc\u9762\u88c5\u7684\u5df2\u7ecf\u4e0d\u662f\u7384\u94c1\u4e86\u3002 \u3000\u3000\u6c99\u5343\u5cf0\u5927\u543c\u4e00\u58f0\u201c\u547c\u201d\u7684\u4e00\u62f3\u5c31\u6253\u8fc7\u6765\u3002\u91d1\u9010\u6d41\u5077\u4e0d\u7740\u7384\u94c1\uff0c\u5931\u671b\u4e4b\u6781\uff0c\u5fc3\u60f3\uff1a\u201c\u5c14\u77e5 \u662f\u54ea\u4e2a\u9ad8\u624b\u6377\u8db3\u5148\u767b\uff0c\u7384\u94c1\u5df2\u7ecf\u7ed9\u4ed6\u62ff\u53bb\uff0c\u6211\u8fd8\u5728\u8fd9\u91cc\u4f5c\u751a\uff1f\u201d\u65e0\u5fc3\u5e94\u6218\uff0c\u968f\u624b\u4e00\u62db\u201c\u62c2\u4e91 \u624b\u201d\uff0c\u62e8\u5f00\u6c99\u5343\u5cf0\u7684\u62f3\u5934\uff0c\u53c8\u518d\u7a7f\u7a97\u800c\u51fa\u3002\u7384\u94c1\u867d\u5df2\u5931\u6389\uff0c\u4f46\u90a3\u7cbe\u81f4\u7684\u7ea2\u6f06\u5323\u5b50\uff0c\u4ed6\u4ecd\u4e0d\u80af \u653e\u5f03\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7a7f\u7a97\u800c\u51fa\uff0c\u811a\u672a\u6cbe\u5730\uff0c\u6587\u9053\u5e84\u5df1\u662f\u95fb\u58f0\u8d76\u5230\u3002\u89c1\u662f\u91d1\u9010\u6d41\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff01\u4f46 \u4ed6\u5fc3\u91cc\u5403\u60ca\uff0c\u51fa\u624b\u5374\u662f\u72e0\u8fa3\u4e4b\u6781\uff0c\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u4e94\u6307\u5408\u62e2\uff0c\u5f53\u4f5c\u5251\u4f7f\uff0c\u6233\u5411\u91d1\u9010\u6d41\u80f8 \u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\uff0c\u800c\u4e14\u662f\u7528\u4e0a\u4e86\u4e09\u8c61\u795e\u529f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u82e5\u662f\u5f85\u811a\u5c16\u843d\u5730\uff0c\u80f8\u53e3\u5c31\u6b63\u8981\u7ed9\u4ed6\u6233\u7740\u3002\u597d\u4e2a\u91d1\u9010\u6d41\uff0c\u5728\u8fd9\u5371\u673a\u77ac\u606f\u4e4b\u95f4\uff0c\u53cd\u811a \u4e00\u6491\u5899\u58c1\uff0c\u8eab\u4f3c\u79bb\u5f26\u4e4b\u7bad\uff0c\u7acb\u5373\u8f6c\u4e86\u4e2a\u65b9\u5411\uff0c\u8d8a\u8fc7\u680f\u6746\uff0c\u98de\u5760\u56ed\u4e2d\u3002\u6587\u9053\u5e84\u559d\u9053\uff1a\u201c\u5f80\u54ea\u91cc \u8d70\uff1f\u201d\u5982\u5f71\u968f\u5f62\u5730\u8ddf\u5728\u4ed6\u540e\u9762\u8df3\u4e0b\u53bb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u628a\u90a3\u7ea2\u6f06\u5323\u5b50\u53cd\u624b\u4e00\u63a8\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8fc7\u662f\u60f3\u8981\u7384\u94c1\u4e48\uff1f\u597d\uff0c\u8fd9\u5c31\u7ed9\u4f60\uff01\u201d\u6587\u9053 \u5e84\u77e5\u9053\u7384\u94c1\u6c89\u91cd\u65e0\u6bd4\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u53d1\u638c\u5411\u91d1\u9010\u6d41\u4fa7\u9762\u88ad\u51fb\u3002\u91d1\u9010\u6d41\u4e00\u62db\u201c\u5f2f\u5f13\u5c04 \u96d5\u201d\uff0c\u9a88\u6307\u5982\u621f\uff0c\u70b9\u4ed6\u8109\u95e8\u3002 \u3000\u3000\u6587\u9053\u5e84\u53d8\u4f5c\u4e86\u5927\u64d2\u62ff\u624b\u6cd5\u6293\u91d1\u9010\u6d41\u7684\u624b\u8155\uff0c\u91d1\u9010\u6d41\u53d8\u62db\u66f4\u662f\u8fc5\u901f\uff0c\u4e94\u6307\u5408\u62e2\uff0c\u4e00\u8bb0\u201c\u624b \u6325\u7435\u7436\u201d\u53cd\u624b\u6325\u51fa\u3002\u53ea\u542c\u5f97\u201c\u556a\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u6587\u9053\u5e84\u7684\u638c\u5fc3\u706b\u8fa3\u8fa3\u4f5c\u75db\uff0c\u91d1\u9010\u6d41\u4e5f\u662f\u4e0d\u7531\u81ea \u5df1\u7684\u5012\u9000\u4e24\u6b65\u3002\u8bba\u529f\u529b\u8fd8\u662f\u6587\u9053\u5e84\u7a0d\u80dc\u5c11\u8bb8\uff0c\u4f46\u5728\u62db\u6570\u4e0a\u5374\u662f\u4ed6\u5403\u4e86\u70b9\u4e8f\uff0c\u638c\u5fc3\u7684\u201c\u52b3\u5bab \u7a74\u201d\uff0c\u7ed9\u91d1\u9010\u6d41\u7684\u62ac\u8282\u9aa8\u6572\u51fb\u4e86\u4e00\u4e0b\uff0c\u767b\u65f6\u611f\u5230\u6c14\u8840\u4e0d\u8212\u3002 \u3000\u3000\u6587\u9053\u5e84\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u9ed8\u8fd0\u7384\u529f\u4f7f\u5f97\u6c14\u8840\u6d41\u7545\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u662f\u8c01\u5077\u4e86\u89e3\u836f\u7ed9\u4f60\uff1f\u201d\u8981 \u77e5\u9165\u9aa8\u6563\u7684\u89e3\u836f\u53ea\u6709\u6587\u9053\u5e84\u624d\u6709\uff0c\u5982\u4eca\u4ed6\u8bd5\u51fa\u4e86\u91d1\u9010\u6d41\u7684\u529f\u529b\u5df2\u7ecf\u6062\u590d\uff0c\u5f53\u7136\u77e5\u9053\u4e86\u662f\u6709\u4eba \u5077\u4e86\u89e3\u836f\u7ed9\u4ed6\u3002\u6d77\u7802\u5e2e\u9632\u536b\u68ee\u4e25\uff0c\u800c\u6587\u9053\u5e84\u7684\u89e3\u836f\u53c8\u662f\u6536\u85cf\u5f97\u5f88\u79d8\u5bc6\u7684\uff0c\u7adf\u7136\u7ed9\u4eba\u5077\u53bb\uff0c\u7109 \u5f97\u4e0d\u60ca\uff1f\u660e\u77e5\u91d1\u9010\u6d41\u662f\u4e0d\u4f1a\u544a\u8bc9\u4ed6\u7684\uff0c\u4ed6\u5728\u84e6\u5730\u4e00\u60ca\u4e4b\u4e0b\uff0c\u4e5f\u7981\u4e0d\u4f4f\u8fd9\u6837\u53d1\u95ee\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u9165\u9aa8\u6563\u4e0d\u6d4e\u4e8b\uff0c\u7109\u80fd\u5948\u4f55\u5f97\u6211\uff0c\u56de\u53bb\u518d\u70bc\u5427\u3002\u201d\u5176\u5b9e\u91d1 \u9010\u6d41\u4e5f\u4e0d\u77e5\u9053\u662f\u8c01\u5077\u7684\u89e3\u836f\uff0c\u4e50\u5f97\u62ff\u6587\u9053\u5e84\u5f00\u5f00\u73a9\u7b11\u3002 \u3000\u3000\u6587\u9053\u5e84\u5927\u6012\uff0c\u518d\u8fd0\u201c\u4e09\u8c61\u795e\u529f\u201d\uff0c\u53cc\u638c\u9f50\u53d1\u3002\u6b64\u65f6\uff0c\u6c99\u5343\u5cf0\u4ea6\u5df2\u662f\u6c14\u6025\u8d25\u574f\u5730\u8d76\u4e0b\u697c \u6765\u3002\u91d1\u9010\u6d41\u63d0\u9632\u5939\u51fb\uff0c\u5de6\u638c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u7528\u201c\u62e8\u4e91\u624b\u201d\u6765\u5316\u89e3\u4ed6\u7684\u4e09\u8c61\u795e\u529f\uff0c\u53f3\u624b\u63d0\u8d77\u7ea2 \u6f06\u5323\u5b50\uff0c\u5411\u4ed6\u5f53\u5934\u7f69\u4e0b\u3002\u6587\u9053\u5e84\u4e0d\u6562\u786c\u62fc\uff0c\u5012\u8dc3\u51fa\u4e00\u4e08\u5f00\u5916\u3002\u91d1\u9010\u6d41\u5fc3\u91cc\u6697\u6697\u7b11\u9053\uff1a\u201c\u5b54\u660e \u4ee5\u7a7a\u57ce\u8ba1\u5413\u9000\u53f8\u9a6c\u61ff\uff0c\u6211\u5982\u4eca\u5374\u662f\u4ee5\u7a7a\u5323\u5b50\u5413\u9000\u4e86\u4f60\u6587\u9053\u5e84\u4e86\u3002\u201d\u539f\u6765\u91d1\u9010\u6d41\u53ea\u51ed\u5355\u638c\u4e4b \u529b\uff0c\u662f\u5316\u89e3\u4e0d\u4e86\u6587\u9053\u5e84\u7684\u4e09\u8c61\u795e\u529f\u7684\uff0c\u4f46\u53c8\u820d\u4e0d\u5f97\u629b\u5f03\u8fd9\u4e2a\u7cbe\u81f4\u7684\u5323\u5b50\uff0c\u662f\u6545\u7528\u7a7a\u5323\u5b50\u5413\u4e00 \u5413\u4ed6\u3002 \u3000\u3000\u6c99\u5343\u5cf0\u6c14\u6025\u8d25\u574f\u5730\u56b7\u9053\uff1a\u201c\u7384\u94c1\uff0c\u7384\u94c1\u5df2\u7ecf\u7ed9\u5077\u53bb\u5566\uff01\u201d\u4ed6\u7ed9\u91d1\u9010\u6d41\u6253\u843d\u4e86\u4e24\u9f7f\u95e8\u7259\uff0c \u5589\u5499\u6697\u54d1\uff0c\u8bf4\u8bdd\u6f0f\u98ce\uff0c\u5636\u5636\u4f5c\u54cd\uff0c\u5341\u5206\u96be\u542c\u3002 \u3000\u3000\u6587\u9053\u5e84\u5fc3\u9053\uff1a\u201c\u4f60\u597d\u7cca\u6d82\uff0c\u7384\u94c1\u5982\u4eca\u6b63\u5728\u8fd9\u5c0f\u5b50\u7684\u624b\u4e0a\uff0c\u8fd8\u7528\u5f97\u7740\u4f60\u544a\u8bc9\u6211\uff1f\u201d\u4e8e\u662f\u968f \u53e3\u5e94\u9053\uff1a\u201c\u662f\u5440\uff0c\u8fd9\u5c0f\u5b50\u4e0d\u4f46\u5077\u4e86\u7384\u94c1\uff0c\u8fd8\u5077\u4e86\u6211\u7684\u89e3\u836f\u5462\u3002\u4e0d\u8fc7\uff0c\u4ed6\u603b\u662f\u8dd1\u4e0d\u4e86\u7684\uff0c\u5e9f\u8bdd \u5c11\u8bf4\uff0c\u6015\u4eec\u5feb\u5feb\u628a\u4ed6\u64d2\u4e0b\u5c31\u662f\uff01\u201d \u3000\u3000\u6c99\u5343\u5cf0\u56b7\u9053\uff1a\u201c\u4e0d\uff0c\u7384\u94c1\u4e0d\u662f\u8fd9\u5c0f\u5b50\u5077\u7684\uff0c\u5077\u7384\u94c1\u7684\u53e6\u6709\u5176\u4eba\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u4e0d\u7981\u53c8\u662f\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u6c99\u5343\u5cf0\u61d2\u5f97\u7b54\u8bdd\uff0c\u201c\u547c\u201d\u7684\u4e00\u638c\u5c31\u5411\u91d1 \u9010\u6d41\u5f53\u5934\u51fb\u4e0b\uff0c\u4ed6\u6068\u6781\u4e86\u91d1\u9010\u6d41\uff0c\u8fd9\u4e00\u638c\u5df2\u662f\u4f7f\u51fa\u4e86\u4ed6\u7684\u672c\u95e8\u6740\u624b\uff0c\u53ef\u4ee5\u5206\u7b4b\u9519\u9aa8\u7684\u5927\u529b\u9e70 \u722a\u529f\uff01 \u3000\u3000\u91d1\u9010\u6d41\u5728\u4e24\u5927\u9ad8\u624b\u5939\u51fb\u4e4b\u4e0b\uff0c\u4e0d\u80fd\u4e0d\u7528\u90a3\u4e2a\u7ea2\u6f06\u5323\u5b50\u6321\u4ed6\u4e00\u6321\u3002\u6c99\u5343\u5cf0\u7684\u529f\u529b\u867d\u7136\u8f83 \u5f31\uff0c\u4f46\u4ed6\u7684\u9e70\u722a\u529f\u4e5f\u6709\u5f00\u7891\u88c2\u77f3\u4e4b\u80fd\uff0c\u91d1\u9010\u6d41\u7528\u4e86\u4e03\u5206\u529b\u9053\u5bf9\u4ed8\u6587\u9053\u5e84\uff0c\u53ea\u7528\u4e09\u5206\u529b\u9053\u6765\u5bf9 \u4ed8\u4ed6\uff0c\u53ea\u542c\u5f97\u201c\u556a\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u90a3\u4e2a\u7ea2\u6f06\u5323\u5b50\u5df2\u662f\u7ed9\u4ed6\u6293\u88c2\uff0c\u788e\u6210\u7247\u7247\uff0c\u91d1\u9010\u6d41\u53eb\u9053\uff1a\u201c\u53ef \u60dc\uff0c\u53ef\u60dc\uff01\u201d\u6c99\u5343\u5cf0\u6012\u9053\uff1a\u201c\u6211\u7684\u7384\u94c1\u90fd\u7ed9\u4eba\u5077\u53bb\u4e86\uff0c\u4f60\u53ea\u4e0d\u8fc7\u7834\u4e86\u4e00\u4e2a\u5323\u5b50\uff0c\u5c31\u8bf4\u53ef \u60dc\uff01\u201d\u7d27\u63a5\u7740\u53c8\u662f\u4e00\u638c\u4e00\u6293\u3002 \u3000\u3000\u91d1\u9010\u6d41\u65bd\u5c55\u201c\u5929\u7f57\u6b65\u6cd5\u201d\u907f\u8fc7\u4e86\u6587\u9053\u5e84\u7684\u653b\u51fb\uff0c\u968f\u5373\u4e00\u62db\u201c\u5999\u89e3\u8fde\u73af\u201d\uff0c\u628a\u6c99\u5343\u5cf0\u7684\u4e00 \u638c\u4e00\u6293\u8361\u5f00\uff0c\u7b11\u9053\uff1a\u201c\u7384\u94c1\u672c\u6765\u5c31\u4e0d\u662f\u4f60\u7684\uff0c\u4f60\u5fc3\u75db\u4ec0\u4e48\uff1f\u6211\u8d39\u4e86\u597d\u5927\u6c14\u529b\uff0c\u53ea\u5077\u5230\u4e00\u4e2a\u7a7a \u5323\u5b50\uff0c\u5982\u4eca\u5323\u5b50\u4e5f\u7ed9\u4f60\u6253\u788e\u4e86\uff0c\u6211\u5f53\u7136\u662f\u8981\u53ef\u60dc\u4e86\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u6b64\u65f6\u624d\u77e5\u9053\u7384\u94c1\u679c\u7136\u662f\u5df2\u7ecf\u7ed9\u53e6\u5916\u7684\u4eba\u5077\u53bb\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u8bf4\u9053\uff1a\u201c\u6c99\u5927\u54e5\uff0c\u8fd9\u4e00 \u5b9a\u662f\u4ed6\u7684\u515a\u7fbd\u6240\u4e3a\uff0c\u54b1\u4eec\u53ea\u8981\u6349\u4f4f\u4e86\u8fd9\u5c0f\u5b50\uff0c\u603b\u53ef\u4ee5\u7740\u843d\u5728\u4ed6\u7684\u8eab\u4e0a\u8ffd\u56de\u7384\u94c1\uff01\u201d\u6c99\u5343\u5cf0 \u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6d3b\u7684\u6349\u4e0d\u4e86\uff0c\u6b7b\u7684\u4e5f\u8981\uff01\u201d\u4ed6\u4eec\u4e24\u4eba\u90fd\u662f\u6068\u6781\u4e86\u91d1\u9010\u6d41\uff0c\u5404\u5c55\u5e73\u751f\u6240\u5b66\uff0c\u62db\u62db \u90fd\u662f\u6740\u624b\uff01 \u3000\u3000\u6fc0\u6218\u4e2d\u91d1\u9010\u6d41\u96be\u4ee5\u4e24\u8fb9\u517c\u987e\uff0c\u6587\u9053\u5e84\u672c\u9886\u8f83\u9ad8\uff0c\u91d1\u9010\u6d41\u81ea\u662f\u5bf9\u4ed6\u591a\u7528\u7cbe\u795e\u3002\u4f46\u6c99\u5343\u5cf0\u4e5f \u5e76\u4e0d\u5f31\uff0c\u91d1\u9010\u6d41\u4e00\u4e2a\u758f\u795e\u3002\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u8863\u895f\u5df2\u662f\u7ed9\u4ed6\u6495\u53bb\u4e86\u4e00\u5e45\u3002\u91d1\u9010\u6d41\u7b11\u9053\uff1a \u201c\u4f60\u6495\u6bc1\u4e86\u6587\u80dc\u4e2d\u7684\u8863\u88f3\uff0c\u6211\u4e0d\u4f1a\u53ef\u60dc\u7684\u3002\u563f\uff0c\u563f\uff0c\u6587\u5c9b\u4e3b\uff0c\u4f60\u513f\u5b50\u7684\u65b0\u8863\u7ed9\u4f60\u7684\u597d\u670b\u53cb\u635f \u574f\u4e86\uff0c\u4f60\u5fc3\u75db\u5427\u3002\u201d \u3000\u3000\u6708\u8272\u6726\u80e7\u4e4b\u4e0b\uff0c\u6587\u9053\u5e84\u5168\u795e\u5e94\u6218\uff0c\u4e00\u76f4\u6ca1\u6709\u7559\u5fc3\u91d1\u9010\u6d41\u7a7f\u7684\u4ec0\u4e48\u8863\u88f3\uff0c\u6b64\u65f6\u7ecf\u4ed6\u4e00\u8bf4\uff0c \u4ed4\u7ec6\u770b\u65f6\uff0c\u65b9\u624d\u53d1\u89c9\u91d1\u9010\u6d41\u7a7f\u7684\u679c\u7136\u662f\u4ed6\u513f\u5b50\u7684\u8863\u88f3\u3002\u800c\u4e14\u8fd9\u4ef6\u8863\u88f3\u8fd8\u662f\u4ed6\u7684\u513f\u5b50\u4eca\u665a\u624d\u6362 \u4e0a\u7684\u65b0\u8863\u3002 \u3000\u3000\u6587\u9053\u5e84\u8fd9\u4e00\u60ca\u66f4\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u559d\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u8fd9\u4ef6\u8863\u88f3\u600e\u4e48\u5230\u4e86\u4f60\u7684\u8eab\u4e0a\uff1f\u201d\u91d1\u9010\u6d41 \u7b11\u9053\uff1a\u201c\u522b\u62c5\u5fc3\uff0c\u4f60\u7684\u5b9d\u8d1d\u513f\u5b50\u6b7b\u4e0d\u4e86\u3002\u4ed6\u60ef\u4f1a\u5077\u6362\u522b\u4eba\u7684\u8863\u88f3\uff0c\u6211\u4e0d\u8fc7\u4ee5\u5176\u4eba\u4e4b\u9053\u8fd8\u6cbb\u5176 \u4eba\u4e4b\u8eab\u800c\u5df2\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u60ca\u7591\u4e0d\u5b9a\uff0c\u5168\u529b\u640f\u51fb\uff0c\u6068\u4e0d\u5f97\u4e00\u4e0b\u5b50\u5c31\u628a\u91d1\u9010\u6d41\u6349\u7740\uff0c\u624d\u597d\u95ee\u4ed6\u7684\u53e3\u4f9b\u3002\u91d1\u9010\u6d41 \u72ec\u529b\u96be\u652f\uff0c\u9669\u62db\u8fed\u89c1\uff0c\u5fc3\u91cc\u6697\u6697\u53eb\u82e6\uff0c\u9762\u4e0a\u5374\u662f\u563b\u76ae\u7b11\u8138\u7684\u795e\u6c14\u8bf4\u9053\uff1a\u201c\u6587\u9053\u5e84\uff0c\u4f60\u4e5f\u7b97\u662f \u4e00\u6d3e\u5b97\u5e08\uff0c\u4f60\u4eec\u5982\u4eca\u4ee5\u4f17\u51cc\u5be1\uff0c\u7f9e\u4e5f\u4e0d\u7f9e\uff1f\u563f\uff0c\u563f\uff01\u4f60\u6211\u82e5\u662f\u5355\u6253\u72ec\u6597\uff0c\u4f60\u662f\u6253\u4e0d\u8fc7\u6211 \u7684\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u72e1\u8bc8\u6076\u6bd2\u7684\u5c0f\u8d3c\uff0c\u6211\u6068\u4e0d\u5f97\u628a\u4f60\u788e\u5c38\u4e07\u6bb5\uff0c\u8c01\u548c\u4f60\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u89c4 \u77e9\uff1f\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u591a\u627f\u8c2c\u8d5e\uff0c\u6211\u662f\u656c\u8c22\u4e0d\u654f\uff0c\u5168\u6570\u7167\u8fd8\u3002\u4f60\u65e2\u7136\u4e0d\u8bb2\u6c5f\u6e56\u89c4\u77e9\uff0c\u90a3\u4e5f\u4ea4 \u65e0\u6240\u8c13\u3002\u5582\uff0c\u4f60\u7684\u513f\u5b50\u6765\u4e86\uff0c\u4f60\u5c31\u53eb\u4ed6\u4e5f\u5e76\u80a9\u5b50\u4e0a\u5427\uff01\u6211\u4e0d\u5728\u4e4e\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u94b1\u5927\u548c\u53e6\u5916\u4e00\u4e2a\u5c0f\u5934\u76ee\u6276\u7740\u4ed6\u7684\u513f\u5b50\uff0c\u6b63\u5728\u8d70\u6765\u3002\u4ed6\u7684\u513f\u5b50\u597d\u4f3c \u53d1\u4e86\u5446\u7684\u795e\u6c14\uff0c\u53cc\u773c\u76f4\u89c6\uff0c\u624b\u8db3\u50f5\u786c\uff0c\u5176\u5b9e\u5df2\u662f\u672c\u8eab\u4e0d\u80fd\u8d70\u52a8\uff0c\u662f\u90a3\u4e24\u4e2a\u4eba\u62d6\u7740\u4ed6\u8d70\u7684\u3002 \u3000\u3000\u6587\u9053\u5e84\u5927\u5403\u4e00\u60ca\uff0c\u614c\u5fd9\u8dd1\u8fc7\u53bb\u53eb\u9053\uff1a\u201c\u4e2d\u513f\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u4ed6\u53ea\u6b64\u4e00\u5b50\uff0c\u4e0d\u557b\u662f\u4ed6\u7684\u547d \u6839\uff0c\u5982\u4eca\u770b\u89c1\u513f\u5b50\u8fd9\u4e2a\u6a21\u6837\uff0c\u5f53\u7136\u662f\u987e\u4e0d\u5f97\u518d\u6597\u91d1\u9010\u6d41\u4e86\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u5589\u5934\u54af\u54af\u4f5c\u54cd\uff0c\u597d\u50cf\u662f\u6709\u4e00\u53e3\u6d53\u75f0\u6897\u5728\u5589\u5934\uff0c\u8981\u5410\u5410\u4e0d\u51fa\u6765\uff0c\u8138\u4e0a\u7684 \u795e\u8272\u66f4\u662f\u5341\u5206\u96be\u770b\u4e86\u3002\u6587\u9053\u5e84\u89c1\u6b64\u60c5\u5f62\uff0c\u6000\u7591\u4e0d\u5b9a\uff0c\u5148\u8bd5\u4e00\u8bd5\u8fd0\u7528\u201c\u4e09\u8c61\u795e\u529f\u201d\u7684\u89e3\u7a74\u65b9 \u6cd5\uff0c\u4ed6\u7684\u4e09\u8c61\u795e\u529f\u53ef\u7ed9\u53d7\u8005\u63a8\u8840\u8fc7\u5bab\uff0c\u672c\u6765\u80fd\u89e3\u4efb\u4f55\u7a74\u9053\uff0c\u4f46\u8fd9\u4e00\u6b21\u5374\u662f\u5931\u7075\u3002\u91d1\u9010\u6d41\u7684\u72ec \u95e8\u70b9\u7a74\u624b\u6cd5\uff0c\u53ea\u6709\u5728\u4e24\u79cd\u60c5\u5f62\u4e4b\u4e0b\uff0c\u5916\u6d3e\u7684\u4eba\u624d\u80fd\u89e3\u7a74\uff0c\u4e00\u662f\u89e3\u7a74\u8005\u7684\u529f\u529b\u6781\u9ad8\uff0c\u6bd4\u4ed6\u4e0d\u6b62 \u9ad8\u51fa\u4e00\u7b79\uff1b\u4e00\u662f\u5f85\u8fc7\u4e86\u4e00\u5b9a\u7684\u65f6\u95f4\u4e4b\u540e\uff0c\u529f\u529b\u548c\u4ed6\u5dee\u4e0d\u591a\u7684\u4eba\uff0c\u77e5\u9053\u4e86\u6240\u70b9\u7684\u662f\u4ec0\u4e48\u7a74\u9053\uff0c \u624d\u6709\u53ef\u80fd\u89e3\u5f00\u3002 \u3000\u3000\u6587\u9053\u5e84\u89e3\u4e0d\u5f00\u7a74\u9053\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u83ab\u975e\u662f\u6211\u5224\u65ad\u9519\u4e86\uff0c\u4e2d\u513f\u5e76\u975e\u7ed9\u70b9\u4e86\u7a74\u9053\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u597d\u4f3c\u77e5\u9053\u4ed6\u7684\u5fc3\u601d\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u767d\u8d39\u6c14\u529b\u4e86\u3002\u7559\u4e0b\u70b9\u6c14\u529b\uff0c\u8fd8\u662f \u966a\u6211\u6253\u67b6\u5427\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u628a\u6211\u7684\u4e2d\u513f\u600e\u4e48\u6837\u4e86\uff0c\u4f60\u5bb3\u4e86\u4ed6\uff0c\u6211\u8981\u4f60\u7684\u547d\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u6447\u4e86\u6447\u624b\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u4f60\u8981\u6211\u7684\u547d\u662f\u505a\u4e0d\u5230\u7684\u3002\u4f46\u4f60\u4e0d\u7528\u60ca\u614c\uff0c\u6211\u4e5f\u5e76\u4e0d\u60f3\u8981 \u4f60\u513f\u5b50\u7684\u547d\u3002\u4f60\u8fd9\u5b9d\u8d1d\u513f\u5b50\uff0c\u8fd8\u503c\u4e0d\u5f97\u6211\u6740\u4ed6\u5462\u3002\u563f\uff0c\u563f\uff0c\u6211\u53ea\u662f\u793c\u5c1a\u5f80\u6765\u800c\u5df2\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u505a\u793c\u5c1a\u5f80\u6765\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u6709\u9165\u9aa8\u6563\uff0c\u6211\u4e5f\u6709\u6211\u7684\u672c\u95e8\u6bd2\u836f\uff1b\u4f60\u7684\u9165\u9aa8\u6563\u5bb3\u4e0d\u4e86\u6211\u3002\u6211\u7684\u6bd2\u836f\u5374\u662f \u53ef\u4ee5\u5bb3\u4f60\u513f\u5b50\u7684\u3002\u4e0d\u8fc7\uff0c\u6211\u5e76\u4e0d\u60f3\u8981\u4ed6\u7684\u547d\uff0c\u6240\u4ee5\u54b1\u4eec\u8fd8\u53ef\u4ee5\u5546\u91cf\u5546\u91cf\uff01\u201d \u3000\u3000\u6c99\u5343\u5cf0\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u662f\u51b3\u8ba1\u8dd1\u4e0d\u4e86\u7684\uff0c\u54b1\u4eec\u6349\u4f4f\u4e86\u4ed6\uff0c\u903c\u4ed6\u4ea4\u51fa\u89e3\u836f\u4fbf\u53ef\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u4f60\u4eec\u4e0d\u4e00\u5b9a\u6349\u5f97\u4f4f\u6211\uff1b\u7b2c\u4e8c\uff0c\u4f60\u4eec\u82e5\u8981\u6349\u6211\uff0c\u6211\u4e00\u5b9a\u548c\u4f60\u4eec\u62fc\u6b7b \u4e00\u6218\uff0c\u6211\u5373\u4f7f\u5be1\u4e0d\u654c\u4f17\uff0c\u4f60\u4eec\u4e5f\u96be\u514d\u6b7b\u4f24\uff1b\u7b2c\u4e09\uff0c\u6211\u8eab\u4e0a\u6709\u51e0\u5341\u79cd\u836f\u4e38\uff0c\u6709\u7684\u662f\u6bd2\u836f\uff0c\u6709\u7684 \u662f\u89e3\u836f\u3002\u4f60\u6349\u4f4f\u4e86\u6211\uff0c\u6211\u7acb\u5373\u81ea\u65ad\u7ecf\u8109\u800c\u4ea1\uff0c\u563f\uff0c\u563f\uff0c\u90a3\u65f6\u4f60\u53ea\u597d\u6bcf\u4e00\u79cd\u836f\u4e38\u90fd\u62ff\u53bb\u7ed9\u4f60\u7684 \u513f\u5b50\u8bd5\u541e\u4e86\u3002\u4f60\u7684\u513f\u5b50\u5927\u7ea6\u6ca1\u6709\u795e\u519c\u7684\u672c\u4e8b\u5427\uff1f\u795e\u519c\u53ef\u4ee5\u8bd5\u670d\u767e\u8349\uff0c\u4f60\u8fd9\u5b9d\u8d1d\u513f\u5b50\uff0c\u6211\u770b\u662f \u505a\u4e0d\u5230\u7684\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u563b\u76ae\u7b11\u8138\u7684\u4e71\u8bf4\u4e00\u901a\uff0c\u4f46\u4ee5\u4ed6\u7684\u672c\u9886\u800c\u8bba\uff0c\u5374\u4e5f\u5e76\u975e\u8651\u58f0\u606b\u5413\uff0c\u6587\u9053\u5e84\u542c\u4e86\uff0c\u4e0d \u7531\u5f97\u5fc3\u4e2d\u8d77\u4e86\u6050\u614c\u3002 \u3000\u3000\u91d1\u9010\u6d41\u585e\u8fdb\u6587\u80dc\u4e2d\u53e3\u4e2d\u7684\u90a3\u56e2\u6ce5\u57a2\u65e9\u5df2\u6eb6\u5316\uff0c\u4f46\u90a3\u80a1\u81ed\u5473\u5374\u662f\u672a\u66fe\u6d88\u9664\uff0c\u55c5\u5f97\u51fa\u6765\uff0c\u6587 \u9053\u5e84\u63fd\u7740\u513f\u5b50\uff0c\u9690\u9690\u95fb\u5f97\u90a3\u80a1\u523a\u9f3b\u7684\u81ed\u5473\uff0c\u66f4\u76f8\u4fe1\u8fd9\u662f\u6bd2\u836f\uff0c\u5fc3\u60f3\uff1a\u201c\u5982\u6b64\u8165\u81ed\u7684\u836f\u7269\uff0c\u6bd2 \u6027\u4e00\u5b9a\u5f88\u5927\u3002\u201d\u5fc3\u91cc\u4e00\u614c\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u7b97\u6211\u683d\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u4f60\u8981\u600e\u4e48\u6837\uff0c\u8bf4 \u5427\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u6162\u6761\u65af\u7406\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u9001\u6211\u51fa\u53bb\uff0c\u5230\u4e86\u4e09\u5341\u91cc\u4e4b\u5916\uff0c\u6211\u7ed9\u4f60\u89e3\u836f\u3002\u8fd9\u6837\uff0c\u4ee4\u90ce\u548c \u6211\u7684\u6027\u547d\u90fd\u53ef\u4ee5\u4fdd\u5168\uff0c\u4e0d\u662f\u516c\u5e73\u5f97\u5f88\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u6211\u600e\u80fd\u76f8\u4fe1\u4f60\u7684\u89e3\u836f\u662f\u771f\u662f\u5047\uff0c\u4f60\u5148\u7ed9\u89e3\u836f\uff0c\u5f85\u4ed6\u597d\u4e86\uff0c\u6211\u518d\u9001\u4f60\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u4fe1\u4e0d\u8fc7\u6211\uff0c\u6211\u53c8\u600e\u80fd\u4fe1\u5f97\u8fc7\u4f60\uff1f\u4f60\u4eec\u4eba\u591a\uff0c\u8bba\u5f62\u52bf\u662f\u4f60\u5360\u5728\u4e0a\u98ce\uff0c\u4f60\u5e94 \u8be5\u8fc1\u5c31\u6211\u624d\u5bf9\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u5c1a\u5728\u8fdf\u7591\uff0c\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u613f\u610f\u4ea4\u6613\uff0c\u90a3\u5c31\u7b97\u4e86\u3002\u54fc\uff0c\u59d3\u6587\u7684\u4f60\u4e5f\u592a\u5c0f\u770b \u4eba\u4e86\uff0c\u51ed\u6211\u7239\u7239\u548c\u6211\u5e08\u5144\u7684\u540d\u5934\uff0c\u6211\u8fd8\u80fd\u9a97\u4f60\u4e0d\u6210\uff0c\u6211\u53ef\u8fd8\u662f\u8981\u5728\u6c5f\u6e56\u4e0a\u884c\u8d70\u7684\u5440\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u4e00\u6765\u662f\u4e3a\u4e86\u513f\u5b50\u7684\u6027\u547d\u7740\u6025\uff0c\u4e8c\u6765\u4ed6\u4e5f\u89c9\u5f97\u91d1\u9010\u6d41\u8bf4\u5f97\u6709\u7406\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d \u9519\uff0c\u4ed6\u7684\u7236\u5144\u90fd\u662f\u6b66\u6797\u4e2d\u9876\u5c16\u513f\u7684\u4eba\u7269\uff0c\u8fd9\u5c0f\u5b50\u51b3\u4e0d\u80fd\u574d\u4e86\u7236\u5144\u7684\u53f0\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u597d\uff0c \u6211\u5c31\u59d1\u4e14\u4fe1\u4f60\u4e00\u7a0b\u3002\u201d\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u59d1\u4e14\u4e0d\u59d1\u4e14\u7684\uff0c\u751f\u610f\u6210\u4ea4\uff0c\u8d70\u5427\uff01\u201d \u3000\u3000\u6c99\u4e2d\u5cf0\u6781\u662f\u4e3a\u96be\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5931\u4e86\u7384\u94c1\uff0c\u53c8\u8d70\u4e86\u8fd9\u5c0f\u5b50\uff0c\u53f2\u767d\u90fd\u90a3\u513f\uff0c\u53ef\u662f\u4e0d\u597d\u4ea4 \u4ee3\u3002\u201d\u4e0d\u8fc7\uff0c\u4ed6\u867d\u7136\u5fc3\u91cc\u5f88\u4e0d\u613f\u610f\uff0c\u4f46\u4ed6\u8fd8\u662f\u8981\u4f9d\u9760\u6587\u9053\u5e84\uff0c\u6743\u8861\u8f7b\u91cd\uff0c\u4e5f\u53ea\u597d\u4e0d\u4f5c\u4e00\u58f0\uff0c \u5f53\u4f5c\u662f\u9ed8\u8bb8\u4e86\u3002 \u3000\u3000\u6587\u9053\u5e84\u4e0e\u91d1\u9010\u6d41\u90fd\u662f\u4e00\u8eab\u4e0a\u4e58\u7684\u8f7b\u529f\uff0c\u4e09\u5341\u91cc\u8def\uff0c\u4e0d\u987b\u534a\u4e2a\u65f6\u8fb0\uff0c\u5df2\u7ecf\u8d70\u8fc7\u3002\u5230\u4e86\u4e09\u5341 \u91cc\u5916\uff0c\u4e5f\u65e9\u5df2\u51fa\u4e86\u6d77\u7802\u5e2e\u7684\u9632\u5730\u4e86\u3002 \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u4e0d\u7528\u6211\u518d\u9001\u4e86\u5427\uff0c\u89e3\u836f\u62ff\u6765\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u54fc\u9053\uff1a\u201c\u6843\u82b1\u6f6d\u6c34\u6df1\u5343\u5c3a\uff0c\u4e0d\u53ca\u6c6a\u4f26\u9001\u6211\u60c5\u3002\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff0c\u8fd9\u4e24\u53e5\u8bd7\u8bf4\u7684\u662f\u597d\u53cb \u76f8\u9001\uff0c\u53ef\u4e0d\u5bf9\u54b1\u4eec\u7684\u60c5\u666f\u3002\u4f46\u4f60\u9001\u6211\u9001\u5230\u4e09\u5341\u91cc\u5916\uff0c\u6211\u4e5f\u8fd8\u662f\u8981\u611f\u6fc0\u4f60\u7684\u76db\u60c5\u7684\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u559d\u9053\uff1a\u201c\u5e9f\u8bdd\u5c11\u8bf4\uff0c\u89e3\u836f\u62ff\u6765\uff01\u201d\u4ed6\u54ea\u91cc\u77e5\u9053\uff0c\u91d1\u9010\u6d41\u4e4b\u6240\u4ee5\u6545\u610f\u80e1\u8bf4\u516b\u9053\uff0c\u5b9e \u662f\u56e0\u4e3a\u4ed6\u4e00\u65f6\u95f4\u5c1a\u672a\u60f3\u51fa\u5982\u4f55\u53d8\u51fa\u201c\u89e3\u836f\u201d\u6765\u7ed9\u6587\u9053\u5e84\u7684\u7f18\u6545\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6697\u81ea\u7b11\u9053\uff1a\u201c\u6709\u4e86\uff0c\u6709\u4e86\u6bd2\u836f\u662f\u5b83\uff0c\u89e3\u836f\u4e5f\u662f\u5b83\u3002\u201d\u4e8e\u662f\u5047\u88c5\u5728\u6000\u4e2d \u6478\u7d22\uff0c\u4f38\u624b\u5230\u814b\u4e0b\u634f\u4e86\u4e00\u4e38\u6ce5\u57a2\uff0c\u62ff\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u89e3\u836f\u4e00\u670d\uff0c\u4ee4\u90ce\u9a6c\u4e0a\u5c31\u597d\u3002\u201d \u3000\u3000\u814b\u4e0b\u7684\u6ce5\u57a2\u5e26\u7740\u9a9a\u81ed\u7684\u6c14\u5473\uff0c\u6bd4\u811a\u677f\u5e95\u7684\u6ce5\u57a2\u8fd8\u8981\u96be\u95fb\u3002\u6587\u9053\u5e84\u63a5\u4e86\u8fc7\u6765\uff0c\u7ed9\u90a3\u80a1\u6c14\u5473 \u718f\u5f97\u51e0\u4e4e\u4f5c\u5455\uff1b\u8bf4\u9053\uff1a\u201c\u8fd9\u89e3\u836f\u600e\u4e48\u81ed\u5f97\u8fd9\u6837\u5389\u5bb3\uff1f\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u826f\u836f\u82e6\u53e3\u5229\u4e8e\u75c5\uff0c\u8fd9 \u53e5\u8bdd\u96be\u9053\u4f60\u8fd8\u6ca1\u6709\u542c\u8fc7\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u5de7\u8ba1\u9000\u5f3a\u654c\uff0c\u563b\u7b11\u800d\u9b54\u5934\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u5341\u4e09\u56de\u3000\u6168\u8d60\u5947\u73cd\u6000\u7389\u5973\u3000\u5de7\u6413\u89e3\u836f\u620f\u9b54\u5934 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u5341\u4e09\u56de\u3000\u6168\u8d60\u5947\u73cd\u6000\u7389\u5973\u3000\u5de7\u6413\u89e3\u836f\u620f\u9b54\u5934 \u3000\u3000\u6587\u9053\u5e84\u534a\u4fe1\u534a\u7591\uff0c\u628a\u201c\u89e3\u836f\u201d\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u7528\u624b\u5e15\u5305\u597d\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u82e5\u662f\u89e3\u836f\u65e0\u7075\uff0c\u6211 \u518d\u627e\u4f60\u7b97\u5e10\u3002\u201d\u6b63\u8981\u56de\u53bb\uff0c\u91d1\u9010\u6d41\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4f60\u8fd9\u89e3\u836f\u53ef\u662f\u5f04\u9519\u4e86\uff1f\u201d\u4ed6\u4e00\u65b9\u9762\u662f\u7591\u5fc3\u91d1\u9010\u6d41\u5728\u8fd9\u89e3\u836f\u4e0a\u4f5c\u5f04 \u4ed6\uff0c\u53e6\u4e00\u65b9\u9762\u66f4\u5bb3\u6015\u7684\u662f\u91d1\u9010\u6d41\u4e0d\u653e\u4ed6\u8d70\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8981\u662f\u8fd9\u5c0f\u5b50\u4e0d\u987e\u6c5f\u6e56\u4fe1\u4e49\uff0c\u6211\u7eb5\u7136 \u4e0d\u81f4\u6b7b\u5728\u4ed6\u7684\u624b\u91cc\uff0c\u53ea\u6015\u4e5f\u8981\u4e24\u8d25\u4ff1\u4f24\u4e86\u3002\u54fc\uff0c\u6211\u771f\u662f\u6094\u4e0d\u8be5\u592a\u8fc7\u76f8\u4fe1\u8fd9\u5c0f\u5b50\uff0c\u6ca1\u6709\u53eb\u6c99\u5343 \u5cf0\u540c\u6765\uff0c\u5982\u4eca\u5012\u662f\u5f04\u5f97\u5b64\u638c\u96be\u9e23\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u522b\u614c\uff0c\u8fd9\u89e3\u836f\u5305\u4f60\u4e00\u670d\u5c31\u7075\uff0c\u4e0d\u8fc7\u6211\u7684\u8bdd\u8fd8\u672a\u8bf4\u5b8c\u5462\uff0c\u4f60\u90a3\u5b9d\u8d1d\u513f\u5b50 \u6211\u9664\u4e86\u5582\u4ed6\u6bd2\u836f\u4e4b\u5916\uff0c\u8fd8\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\u7684\u3002\u201d\u6587\u9053\u5e84\u66fe\u8bd5\u8fc7\u89e3\u7a74\u65e0\u6548\uff0c\u542c\u4e86\u8fd9\u8bdd\uff0c\u5403\u4e86\u4e00 \u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u600e\u80fd\u4e0b\u5f97\u5982\u6b64\u8fa3\u624b\uff1f\u201d\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c0f\u5b50\u5df2\u7136\u9003\u51fa\u4e86\u864e\u7a74\u9f99\u6f6d\uff0c\u5f53\u7136\u662f \u7edd\u4e0d\u80af\u518d\u56de\u53bb\u7684\u4e86\u3002\u4f46\u6211\u4e0d\u4f1a\u89e3\u7a74\uff0c\u8fd9\u5374\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff01\u8bf4\u5230\u5fc3\u72e0\u624b\u8fa3\u8fd9\u5c42\uff0c\u6211\u662f\u8fdc\u4e0d\u5982\u4f60\u3002\u4f60\u4e0d\u662f\u8bf4\u8fc7\u65e0\u6bd2\u4e0d\u4e08 \u592b\u7684\u5417\uff1f\u201d\u6587\u9053\u5e84\u66f4\u662f\u60ca\u6050\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u9010\u6d41\uff0c\u4f60\u60f3\u8981\u600e\u6837\uff1f\u4f60\u662f\u6545\u610f\u9a97\u6211\u6765\u6b64\u620f\u800d\u7684\u4e0d \u662f\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e00\u672c\u6b63\u7ecf\u5730\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff01\u6211\u5c82\u80fd\u4e0e\u4f60\u4e00\u822c\u89c1\u8bc6\uff1f\u6216\u8bb8\u4f60\u5fc3\u72e0\u624b\u8fa3\uff0c\u6211\u5374\u4e0d\u80fd \u4e0d\u4e00\u8bfa\u5343\u91d1\u3002\u6211\u8bf4\u8fc7\u4e0d\u8981\u4f60\u513f\u5b50\u7684\u6027\u547d\uff0c\u5f53\u7136\u4e5f\u5c31\u8981\u6559\u4f60\u89e3\u7a74\u7684\u65b9\u6cd5\u4e86\u3002\u4f60\u6d17\u8033\u606d\u542c\u5427\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u4e0d\u80fd\u4e0d\u5fcd\u6c14\u541e\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u7b97\u6211\u6015\u4e86\u4f60\u4e86\uff0c\u8bf4\u5427\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u70b9\u7684 \u662f\u7487\u7391\u7a74\uff0c\u4f60\u53ea\u8981\u5728\u76f8\u5e94\u7684\u7a74\u9053\u4e0a\u7ed9\u4ed6\u63a8\u8840\u8fc7\u5bab\uff0c\u5c31\u53ef\u89e3\u5f00\u3002\u201d\u6587\u9053\u5e84\u9053\uff1a\u201c\u4f60\u83ab\u8981\u53c8\u9a97\u6211 \u5427\uff1f\u6211\uff0c\u6211\u2026\u2026\u201d\u4e00\u8fde\u8bf4\u4e86\u51e0\u4e2a\u201c\u6211\u201d\u5b57\uff0c\u5374\u662f\u4e0d\u597d\u610f\u601d\u8bf4\u51fa\u4ed6\u5df2\u7ecf\u8bd5\u8fc7\u63a8\u8840\u8fc7\u5bab\u800c\u65e0\u6548\u679c \u7684\u4e8b\u5b9e\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u66fe\u7ecf\u8bd5\u8fc7\u4e86\u4e0d\u662f\uff1f\u4e0d\u8fc7\u8fd9\u6b21\u4e0d\u540c\uff0c\u4f60\u8ba9\u4ed6\u5148\u670d\u4e86\u89e3\u836f\uff0c\u518d\u8bd5\u5c31\u6709\u6548 \u4e86\u3002\u201d\u91d1\u9010\u6d41\u7b97\u8fc7\u65f6\u95f4\uff0c\u6587\u9053\u5e84\u4e00\u6765\u4e00\u56de\uff0c\u56de\u5230\u6d77\u7802\u5e2e\u81f3\u5c11\u4e5f\u8fc7\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u8fc7\u4e86\u8fd9\u4e00\u4e2a\u65f6 \u8fb0\uff0c\u4ed6\u5373\u4f7f\u6ca1\u6709\u4e09\u8c61\u795e\u529f\uff0c\u7a74\u9053\u4e5f\u662f\u53ef\u4ee5\u8f7b\u6613\u89e3\u5f00\u7684\u4e86\u3002 \u3000\u3000\u6587\u9053\u5e84\u5fc3\u60f3\uff1a\u201c\u4ed6\u82e5\u662f\u9a97\u6211\uff0c\u4e5f\u65e0\u9700\u544a\u8bc9\u6211\u89e3\u7a74\u7684\u65b9\u6cd5\u3002\u55ef\uff0c\u60f3\u4e0d\u5230\u8fd9\u5c0f\u5b50\u5012\u4e5f\u8fd8\u6709\u70b9 \u539a\u9053\u3002\u201d\u4e8e\u662f\u771f\u5fc3\u771f\u610f\u5730\u8c22\u8fc7\u4e86\u91d1\u9010\u6d41\uff0c\u4fbf\u5373\u56de\u53bb\u3002 \u3000\u3000\u6587\u9053\u5e84\u8d70\u540e\uff0c\u91d1\u9010\u6d41\u5fcd\u4e0d\u7740\u6367\u8179\u5927\u7b11\uff0c\u60f3\u5230\u6587\u80dc\u4e2d\u518d\u670d\u4e86\u4ed6\u7684\u201c\u89e3\u836f\u201d\u4e4b\u540e\u7684\u60c5\u666f\uff0c\u8d8a \u60f3\u8d8a\u662f\u5f97\u610f\uff01\u201d \u3000\u3000\u53ef\u662f\u7b11\u8fc7\u4e4b\u540e\uff0c\u91d1\u9010\u6d41\u60f3\u8d77\u7384\u94c1\u5c1a\u672a\u5f97\u624b\uff0c\u5374\u53c8\u4e0d\u7981\u6709\u51e0\u5206\u5931\u610f\u4e86\uff0c\u4ed6\u5583\u5583\u81ea\u8bed\uff1a\u201c\u5077 \u8fd9\u7384\u94c1\u7684\u4e0d\u77e5\u662f\u4ec0\u4e48\u4eba\uff1f\u60f3\u4e0d\u5230\u4e00\u5c71\u8fd8\u6709\u4e00\u5c71\u9ad8\uff0c\u8fd9\u6b21\u662f\u8fde\u6211\u4e5f\u683d\u4e86\u4e00\u4e2a\u7b4b\u6597\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5728\u5c71\u8def\u4e0a\u884c\u8d70\uff0c\u6b63\u5728\u5583\u5583\u81ea\u8bed\uff0c\u5ffd\u542c\u5f97\u6811\u6797\u91cc\u6709\u4eba\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u60f3 \u77e5\u9053\u662f\u8c01\u5077\u4e86\u7384\u94c1\u7684\u5417\uff1f\u201d\u91d1\u9010\u6d41\u4e00\u542c\u5f97\u8fd9\u4e2a\u719f\u6089\u7684\u58f0\u97f3\uff0c\u767b\u65f6\u5446\u4e86\uff01 \u3000\u3000\u53ea\u89c1\u53f2\u7ea2\u82f1\u4ece\u6797\u5b50\u91cc\u8885\u8885\u5a77\u5a77\u5730\u8d70\u51fa\u6765\uff0c\u4e00\u624b\u63d0\u7740\u4e00\u4e2a\u5323\u5b50\uff0c\u4e00\u624b\u63d0\u7740\u4e00\u628a\u957f\u5251\uff0c\u7b11\u9765 \u5982\u82b1\u5341\u5206\u5f97\u610f\u7684\u795e\u6001\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5446\u4e86\u4e00\u5446\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u63a5\u8fc7\u53bb\u5427\u3002\u632f\u81c2\u4e00\u629b\uff0c\u5c06\u90a3\u957f\u65b9\u5f62\u7684\u5323\u5b50\u629b\u8fc7\u6765\uff0c\u91d1\u9010\u6d41\u63a5\u5230\u624b \u4e2d\uff0c\u611f\u89c9\u5341\u5206\u6c89\u91cd\uff0c\u4e0d\u7528\u6253\u5f00\uff0c\u5df2\u77e5\u9053\u662f\u7384\u94c1\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u4e0d\u662f\u60f3\u53eb\u4f60\u683d\u7b4b\u6597\u7684\uff0c\u6211\u662f\u8bda\u5fc3\u5077\u4e86\u6765\u9001\u7ed9\u4f60\u7684\u3002\u55ef\uff0c\u600e\u4e48\u6837\uff0c\u4f60\u4e0d\u5e94 \u8be5\u9053\u8c22\u6211\u5417\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u77e5\u9053\u81ea\u5df1\u7684\u81ea\u8a00\u81ea\u8bed\u5df2\u7ecf\u5168\u7ed9\u53f2\u7ea2\u82f1\u5077\u542c\u4e86\u53bb\uff0c\u4e0d\u89c9\u6ee1\u9762\u901a\u7ea2\uff0c\u5c31\u50cf\u6597\u8d25\u4e86\u7684\u516c \u9e21\u4f3c\u7684\uff0c\u4e0d\u7531\u5f97\u4e0d\u5148\u8bf4\u4e86\u4e00\u58f0\u201c\u591a\u8c22\uff01\u201d\u7136\u540e\u8bf4\u9053\uff1a\u201c\u4f60\u5077\u6765\u7ed9\u6211\uff0c\u6211\u53ef\u662f\u4e0d\u80fd\u8981\u4f60\u7684 \u4e86\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4e0a\u6b21\u662f\u4f60\u5077\u7684\uff0c\u6211\u9001\u7ed9\u4f60\uff0c\u4e0d\u80fd\u7b97\u4f5c\u793c\u7269\u3002\u73b0\u5728\u662f\u6211\u5077\u7684\uff0c\u6211\u9001\u7ed9\u4f60\uff0c\u6211 \u6b20\u4f60\u7684\u4eba\u60c5\u8be5\u7b97\u5f97\u662f\u8fd8\u6e05\u695a\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u662f\u5c34\u5c2c\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u8bb0\u5f97\u6211\u7684\u8bf4\u8bdd\u3002\u90a3\u5929\u6211\u53e3\u4e0d\u62e9\u8a00\uff0c\u8bf4\u9519\u4e86\u8bdd\uff0c\u4f60\u4e0d \u8981\u653e\u5728\u5fc3\u4e0a\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u7b11\u9053\uff1a\u201c\u4f60\u522b\u5f53\u6211\u662f\u5c0f\u5fc3\u773c\u513f\uff0c\u6211\u662f\u751f\u6027\u4e0d\u613f\u53d7\u4eba\u6069\u60e0\uff0c\u6b20\u4e86\u7684\u4eba\u60c5\u5c31\u5b9a\u8981\u8fd8\uff0c \u800c\u4e14\u8fd9\u7384\u94c1\u6211\u8981\u4e86\u4e5f\u6ca1\u7528\uff0c\u539f\u56e0\u6211\u4e5f\u65e9\u5bf9\u4f60\u8bf4\u8fc7\u4e86\u3002\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u4ece\u5979\u7684\u8a00\u8bed\u4e4b\u4e2d\uff0c\u91d1\u9010 \u6d41\u8fd8\u662f\u611f\u89c9\u5f97\u5230\uff0c\u5979\u662f\u6709\u6240\u201c\u8ba1\u8f83\u201d\uff0c\u662f\u4e3a\u4e86\u8981\u5411\u81ea\u5df1\u51fa\u4e00\u53e3\u6c14\u7684\u3002\u4e00\u65f6\u95f4\u91d1\u9010\u6d41\u5012\u662f\u4e0d\u77e5 \u8bf4\u4e9b\u4ec0\u4e48\u8bdd\u597d\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u53c8\u9053\uff1a\u201c\u53ef\u60dc\u5323\u5b50\u5df2\u7ecf\u4e0d\u662f\u539f\u6765\u7684\u5323\u5b50\u3002\u6211\u8fd8\u4e86\u7384\u94c1\uff0c\u53ea\u80fd\u7b97\u662f\u4ed8\u672c\uff0c\u8fd8\u5e94\u8be5\u4ed8 \u606f\u624d\u5bf9\u3002\u8fd9\u67c4\u957f\u5251\uff0c\u73b0\u5728\u7269\u5f52\u539f\u4e3b\uff0c\u5c31\u8bf7\u4f60\u4e5f\u4e00\u5e76\u6536\u56de\u5427\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u5f52\u8fd8\u7684\u8fd9\u67c4\u5251\uff0c\u6b63\u662f\u91d1\u9010\u6d41\u7684\u4f69\u5251\uff0c\u4ed6\u505a\u4e86\u6d77\u6c99\u5e2e\u7684\u4fd8\u864f\u4e4b\u540e\uff0c\u7ed9\u7f34\u4e86\u53bb\u7684\u3002\u73b0 \u5728\u5374\u7ed9\u53f2\u7ea2\u82f1\u5f53\u4f5c\u201c\u5229\u606f\u201d\uff0c\u5f52\u8fd8\u4ed6\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u60f3\uff0c\u82e5\u662f\u4e0d\u8981\uff0c\u5012\u663e\u5f97\u81ea\u5df1\u5c0f\u6c14\uff0c\u7d22\u6027\u5927\u5927\u65b9\u65b9\u5730\u63a5\u4e86\u8fc7\u6765\uff0c\u518d\u8bf4\u4e86\u4e00\u58f0\u591a \u8c22\u3002\u95ee\u9053\uff1a\u201c\u4f60\u662f\u600e\u4e48\u77e5\u9053\u7384\u94c1\u843d\u5728\u6d77\u7802\u5e2e\u4e4b\u5904\u7684\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4f60\u90a3\u5929\u5728\u957f\u6c5f\u4e0a\u7ffb\u8239\uff0c\u843d\u6c34\uff0c\u7ed9\u6c99\u5343\u5cf0\u6349\u53bb\uff0c\u8fd9\u6837\u7684\u5927\u4e8b\uff0c\u6211\u8fd8\u80fd\u4e0d\u77e5\u9053 \u5417\uff1f\u6211\u77e5\u9053\u4f60\u7ed9\u4eba\u6349\u53bb\uff0c\u8fd9\u6b63\u662f\u6211\u62a5\u7b54\u4f60\u7684\u597d\u673a\u4f1a\uff0c\u6211\u53c8\u8fd8\u80fd\u4e0d\u6765\u5417\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u5979\u63d0\u8d77\u81ea\u5df1\u7684\u5931\u610f\u4e4b\u4e8b\uff0c\u4e0d\u89c9\u53c8\u662f\u9762\u4e0a\u4e00\u7ea2\u3002\u53ef\u662f\u5fc3\u91cc\u5374\u4e5f\u5728\u6697\u6697\u9ad8\u5174\uff0c\u60f3 \u9053\uff1a\u201c\u867d\u8bf4\u5979\u662f\u4e3a\u4e86\u4e89\u4e00\u53e3\u6c14\uff0c\u4f46\u5979\u4e0d\u987e\u5371\u9669\uff0c\u6df1\u5165\u864e\u7a74\u76f8\u6551\uff0c\u5bf9\u6211\u4e5f\u4e0d\u80fd\u8bf4\u662f\u4e0d\u5173\u5fc3\u7684 \u4e86\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u628a\u89e3\u836f\u629b\u7ed9\u6211\u7684\uff0c\u60f3\u5fc5\u4e5f\u4e00\u5b9a\u662f\u4f60\u4e86\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6b64\u662f\u5c0f\u4e8b\uff0c\u4f55\u8db3\u6302\u9f7f\uff0c\u4f60\u4e0d\u662f\u4e5f\u66fe\u6551\u8fc7\u6211\u5417\uff1f\u201d\u5979\u89c1\u91d1\u9010\u6d41\u5df2\u7ecf\u5bf9\u5979\u4f4e \u5934\uff0c\u95f7\u6c14\u51fa\u4e86\uff0c\u5bf9\u91d1\u9010\u6d41\u4e5f\u5c31\u5ba2\u6c14\u591a\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u521d\u65f6\u5c34\u5c2c\u4e0d\u5b89\uff0c\u6b64\u9645\u5fc3\u4e2d\u5374\u662f\u751c\u4e1d\u4e1d\u7684\u4e86\u3002\u60f3\u9053\uff1a\u201c\u5973\u5b69\u513f\u5bb6\u7684\u813e\u6c14\u771f\u662f\u96be\u4ee5\u6349 \u6478\uff0c\u5c31\u50cf\u4e94\u6708\u9ec4\u6885\u5929\u4e00\u6837\uff0c\u4e00\u4f1a\u513f\u662f\u98ce\uff0c\u4e00\u4f1a\u513f\u662f\u96e8\uff0c\u96e8\u4e1d\u98ce\u7247\u4e00\u756a\u756a\u4e4b\u540e\uff0c\u5ffd\u7136\u95f4\u53c8\u662f\u65e5 \u4e3d\u98ce\u548c\u4e86\u3002\u524d\u51e0\u5929\u5979\u5bf9\u6211\u8fd8\u662f\u7231\u7406\u4e0d\u7406\u7684\uff0c\u4eca\u5929\u5374\u662f\u5bf9\u6211\u6709\u8bf4\u6709\u7b11\u4e86\u3002\u55ef\uff0c\u6211\u53d7\u4e86\u5979\u51e0\u53e5\u595a \u843d\uff0c\u4e5f\u662f\u5927\u5927\u7684\u503c\u5f97\u4e86\u554a\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9ad8\u5174\u8d77\u6765\uff0c\u7d22\u6027\u5bf9\u53f2\u7ea2\u82f1\u591a\u606d\u7ef4\u51e0\u53e5\uff0c\u8bf4\u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u4f60\u7684\u672c\u4e8b\u771f\u662f\u4e86\u4e0d\u8d77\uff0c \u4e00\u4e2a\u4eba\u5c31\u80fd\u5728\u6d77\u7802\u5e2e\u7684\u603b\u8235\u95f9\u5b83\u4e2a\u5929\u7ffb\u5730\u8986\uff0c\u5077\u4e86\u7384\u94c1\uff0c\u53c8\u5077\u4e86\u89e3\u836f\uff0c\u795e\u4e0d\u77e5\uff0c\u9b3c\u4e0d\u89c9\uff0c\u8bf4 \u6765\u5c31\u6765\uff0c\u8bf4\u53bb\u5c31\u53bb\uff01\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6211\u91d1\u9010\u6d41\u662f\u4ece\u6765\u4e0d\u4f69\u670d\u522b\u4eba\u7684\uff0c\u4eca\u5929\u5bf9\u4f60\uff0c\u6211\u53ef\u662f\u4e0d\u80fd\u4e0d\u4e94 \u4f53\u6295\u5730\u4e86\uff01\u201d\u8fd9\u756a\u8bf4\u8bdd\u867d\u7136\u662f\u606d\u7ef4\u8fc7\u4efd\uff0c\u5374\u4e5f\u662f\u91d1\u9010\u6d41\u7684\u7531\u8877\u4e4b\u8a00\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u7b11\u9053\uff1a\u201c\u8fd9\u54ea\u91cc\u662f\u6211\u7684\u672c\u4e8b\uff0c\u8bf4\u51fa\u6765\u4e0d\u503c\u4e00\u7b11\uff0c\u6211\u5176\u5b9e\u53ea\u4e0d\u8fc7\u6361\u4e2a\u73b0\u6210\u800c\u5df2\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53ef\u4ee5\u8bf4\u7ed9\u6211\u542c\u542c\u4e48\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u5728\u6d77\u7802\u5e2e\u5e2e\u4e3b\u6c99\u5343\u5cf0\u7684\u624b\u4e0b\uff0c\u6709\u4e24\u4e2a\u4eba\u662f\u6211\u7684\u54e5\u54e5\u6d3e\u53bb\u5367\u5e95\u7684\u3002\u201d\u8fd9\u662f\u4e00 \u4e2a\u6c5f\u6e56\u672f\u8bed\uff0c\u610f\u601d\u5927\u7ea6\u76f8\u5f53\u4e8e\u201c\u5750\u63a2\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u7684\u54e5\u54e5\u548c\u6c99\u5343\u5cf0\u4e0d\u662f\u516b\u62dc\u4e4b\u4ea4\u4e48\uff0c \u4ed6\u8350\u53bb\u7684\u4eba\u6c99\u5343\u5cf0\u5f53\u7136\u662f\u4f1a\u91cd\u7528\u7684\u4e86\u3002\u4f46\u5374\u600e\u7528\u5f97\u4e0a\u2018\u5367\u5e95\u2019\u4e8c\u5b57\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4e0d\uff0c\u8fd9\u4e24\u4e2a\u4eba\u4e0d\u662f\u7531\u6211\u54e5\u54e5\u51fa\u9762\u4fdd\u8350\u7684\uff0c\u4ed6\u6258\u4e86\u53e6\u4e00\u4f4d\u6c5f\u6e56\u524d\u8f88\u8350\u53bb\uff0c\u6c99 \u5343\u5cf0\u5e76\u4e0d\u77e5\u9053\u4ed6\u4eec\u662f\u6211\u54e5\u54e5\u7684\u4eba\u3002\u54e5\u54e5\u60f3\u63a7\u5236\u6d77\u7802\u5e2e\uff0c\u6240\u4ee5\u624d\u7528\u8fd9\u4e2a\u624b\u6bb5\uff0c\u4e0d\u7740\u75d5\u8ff9\u7684\u628a\u4e24 \u4e2a\u4eba\u5b89\u63d2\u5230\u6c99\u5343\u5cf0\u7684\u8eab\u8fb9\u3002\u8fd9\u4e48\u4e00\u6765\uff0c\u6d77\u7802\u5e2e\u4e2d\u7684\u4e8b\u60c5\uff0c\u4e0d\u8bba\u5927\u5c0f\uff0c\u6211\u7684\u54e5\u54e5\u90fd\u4e86\u5982\u6307\u638c \u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u53f9\u9053\uff1a\u201c\u4f60\u7684\u54e5\u54e5\u771f\u662f\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u5bf9\u7ed3\u62dc\u5144\u5f1f\u4e5f\u662f\u8fd9\u6837\u52fe\u5fc3\u6597\u89d2\u3002\u4f46\u6211\u8fd8\u662f\u6709\u6240 \u4e0d\u660e\uff0c\u542c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u4f60\u8fd9\u6b21\u80fd\u591f\u987a\u5229\u6210\u529f\uff0c\u5927\u7ea6\u662f\u5f97\u4e86\u8fd9\u4e24\u4e2a\u4eba\u4e4b\u52a9\u7684\u4e86\uff0c\u662f\u4e48\uff1f\u201d\u53f2\u7ea2\u82f1 \u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6240\u4ee5\uff0c\u8fd9\u6211\u5c31\u4e0d\u660e\u767d\u4e86\u3002\u4f60\u8fd9\u6b21\u51fa\u6765\uff0c\u4f60\u7684\u54e5\u54e5\u4e0d\u662f\u5f88\u751f\u6c14\u7684 \u4e48\uff1f\u4f60\u4e5f\u8bf4\u8fc7\uff0c\u4f60\u7684\u54e5\u54e5\u662f\u8981\u628a\u4f60\u6349\u56de\u53bb\u7684\u3002\u90a3\u4e48\u4f60\u54e5\u54e5\u7684\u4eba\uff0c\u600e\u4e48\u8fd8\u4f1a\u52a9\u4f60\u76d7\u90a3\u7384\u94c1\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u79d8\u5bc6\u6211\u54e5\u54e5\u4e5f\u4e0d\u77e5\u9053\u7684\u3002\u4ed6\u6d3e\u53bb\u7684\u8fd9\u4e24\u4e2a\u4eba\uff0c\u4ed6\u4ee5\u4e3a\u662f\u5bf9\u4ed6\u975e\u5e38\u5fe0 \u5fc3\u7684\u4eba\uff0c\u5176\u5b9e\u5374\u662f\u674e\u6566\u7684\u670b\u53cb\u3002\u4ed6\u4eec\u548c\u674e\u6566\u4e00\u6837\uff0c\u90fd\u662f\u4e0d\u613f\u610f\u6211\u7684\u54e5\u54e5\u52fe\u7ed3\u5b98\u5e9c\u7684\uff0c\u4ed6\u4eec\u5bf9 \u516d\u5408\u5e2e\u4e0e\u6d77\u7802\u5e2e\u90fd\u6709\u6240\u4e0d\u6ee1\uff0c\u4f46\u5bf9\u6211\u5374\u662f\u5f88\u597d\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u674e\u6566\u7684\u670b\u53cb\uff0c\u5f53\u7136\u662f\u5e94\u8be5\u5bf9\u4f60\u5f88\u597d\u7684\u4e86\u3002\u4f46\u60f3\u4e0d\u5230\u674e\u6566\u4e5f\u662f\u8fd9\u6837 \u7684\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u8fde\u4f60\u7684\u54e5\u54e5\u4e5f\u4e0a\u4e86\u4ed6\u7684\u5f53\u4e86\u3002\u201d\u91d1\u9010\u6d41\u5fc3\u91cc\u6cdb\u8d77\u4e00\u80a1\u9178\u6e9c\u6e9c\u7684\u5473\u9053\uff0c\u7b11\u5f97\u5f88\u662f \u52c9\u5f3a\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u201c\u767d\u201d\u4e86\u4ed6\u4e00\u773c\uff0c\u8bf4\uff1a\u201c\u8fd9\u600e\u80fd\u6df7\u4e3a\u4e00\u8c08\uff0c\u4f7f\u7528\u5fc3\u8ba1\u4e5f\u6709\u597d\u574f\u4e4b\u5206\uff0c\u674e\u6566\u7684\u2018\u5fc3 \u8ba1\u2019\u662f\u7528\u6765\u505a\u597d\u4e8b\u7684\u3002\u6700\u5c11\u6211\u8ba4\u4e3a\u5982\u6b64\u3002\u201d\u53f2\u7ea2\u82f1\u907f\u514d\u8bae\u8bba\u54e5\u54e5\uff0c\u6240\u4ee5\u53ea\u63d0\u4e86\u674e\u6566\u3002\u91d1\u9010\u6d41 \u542c\u5728\u5fc3\u91cc\uff0c\u66f4\u611f\u5230\u4e0d\u662f\u5473\u513f\uff0c\u60f3\u9053\uff1a\u201c\u5979\u5fc3\u76ee\u4e2d\u53ea\u6709\u4e00\u4e2a\u674e\u6566\uff0c\u6211\u63d2\u5728\u4ed6\u4eec\u5f53\u4e2d\u7b97\u4ec0\u4e48\uff1f\u201d \u60f3\u8981\u8d70\u5f00\uff0c\u5374\u53c8\u820d\u4e0d\u5f97\u5c31\u4e0e\u53f2\u7ea2\u82f1\u5206\u624b\u3002 \u3000\u3000\u4e8e\u662f\u91d1\u9010\u6d41\u53ea\u597d\u8d54\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u7136\uff0c\u5f53\u7136\uff0c\u6211\u4e5f\u8ba4\u4e3a\u5982\u6b64\u3002\u6211\u8bf4\u8bdd\u4e0d\u5f53\uff0c\u4f60\u522b\u8ba1 \u8f83\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u8bdd\u7d20\u6765\u8fd9\u6837\u9634\u9633\u602a\u6c14\uff0c\u6211\u662f\u65e9\u5df2\u9886\u6559\u8fc7\u4e86\u3002\u6211\u82e5\u662f \u548c\u4f60\u8ba1\u8f83\uff0c\u8fd8\u4e0d\u4f1a\u5728\u8fd9\u91cc\u7b49\u4f60\u5462\uff01\u201d\u5176\u5b9e\u53f2\u7ea2\u82f1\u4e5f\u53ea\u4e0d\u8fc7\u201c\u9886\u6559\u201d\u8fc7\u91d1\u9010\u6d41\u4e00\u6b21\uff0c\u4f46\u5979\u7528\u4e86 \u8fd9\u4e48\u6837\u7684\u53e3\u6c14\u8bf4\u51fa\u6765\u3002\u5374\u597d\u50cf\u53d8\u6210\u4e86\u91d1\u9010\u6d41\u7684\u591a\u5e74\u8001\u53cb\u4e86\uff0c\u91d1\u9010\u6d41\u542c\u5f97\u5927\u662f\u5f00\u5fc3\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u4eba\u65e9\u5df2\u77e5\u9053\u7384\u94c1\u6536\u85cf\u7684\u6240\u5728\uff0c\u6211\u627e\u7740\u4e86\u4ed6\u4eec\uff0c\u8981\u8fd9\u7384\u94c1\uff0c\u5f53 \u7136\u662f\u6613\u5982\u53cd\u638c\u4e86\u3002\u4e0d\u8fc7\u7384\u94c1\u8fd8\u662f\u6211\u4eb2\u81ea\u5077\u7684\uff0c\u56e0\u4e3a\u4ed6\u4eec\u62ff\u4e0d\u52a8\u3002\u81f3\u4e8e\u90a3\u74f6\u89e3\u836f\uff0c\u538b\u6839\u513f\u6211\u5c31 \u6ca1\u6709\u51fa\u8fc7\u6c14\u529b\uff0c\u662f\u4ed6\u4eec\u66ff\u6211\u4ece\u6587\u9053\u5e84\u7684\u623f\u4e2d\u641c\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u7ba1\u662f\u8c01\u5077\u7684\uff0c\u6211\u603b\u662f\u8981\u9886\u4f60\u7684\u4eba\u60c5\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u8bf4\u8d77\u6765\u6211\u5012\u662f\u8981\u4f69\u670d\u4f60\u5462\uff01\u4f60\u53ea\u662f\u4e00\u4e2a\u4eba\uff0c\u6beb\u65e0\u501a\u9760\uff0c\u8d64\u624b\u7a7a\u62f3\uff0c\u5c31\u95f9\u5f97 \u6d77\u6c99\u5e2e\u5929\u7ffb\u5730\u8986\uff0c\u4f60\u624d\u662f\u771f\u6b63\u7684\u4e86\u4e0d\u8d77\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5012\u4e0d\u662f\u6b22\u559c\u522b\u4eba\u5949\u627f\uff0c\u4f46\u8fd9\u4e9b\u79f0\u8d5e\u4ed6\u7684\u8bf4\u8bdd\uff0c\u4ece\u53f2\u7ea2\u82f1\u7684\u53e3\u91cc\u8bf4\u51fa\u6765\uff0c\u5374\u662f\u4f7f\u5f97 \u4ed6\u597d\u50cf\u5403\u4e86\u4eba\u53c2\u679c\u4f3c\u7684\uff0c\u516b\u4e07\u56db\u5343\u4e2a\u6bdb\u5b54\u6ca1\u4e00\u4e2a\u4e0d\u8212\u670d\uff01\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u597d\u4e86\uff0c\u597d\u4e86\uff01\u54b1\u4eec \u90fd\u4e0d\u7528\u4e92\u76f8\u6807\u699c\u4e86\u3002\u8bf4\u6b63\u7ecf\u7684\u5427\uff0c\u4f60\u51c6\u5907\u4e0a\u54ea\u513f\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6ca1\u4e00\u5b9a\u3002\u6211\u6216\u8005\u4f1a\u968f\u4fbf\u627e\u4e2a\u5730\u65b9\u8eb2\u8d77\u6765\uff0c\u5f85\u8428\u798f\u9f0e\u7684\u5bff\u671f\u8fc7\u540e\u3002\u6211\u624d\u56de \u5bb6\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4e0d\u884c\u3002\u4f60\u5343\u4e07\u4e0d\u80fd\u56de\u5bb6\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u6211\u4e24\u6b21\u5077\u76d7\u7384\u94c1\uff0c\u90fd\u6ca1\u6709\u9732\u51fa\u884c\u85cf\u3002\u8463\u5341\u4e09\u5a18\u548c\u6c99\u5343\u5cf0\u90fd\u4e0d\u4f1a\u77e5 \u9053\u662f\u6211\u5e72\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5373\u4f7f\u4f60\u7684\u54e5\u54e5\u4e0d\u5411\u4f60\u8ffd\u7a76\u7384\u94c1\u4e4b\u4e8b\uff0c\u4f60\u4e5f\u4e0d\u80fd\u56de\u53bb\uff01\u4f60\u4e00\u56de\u53bb\uff0c\u4f60\u7684\u54e5\u54e5 \u5c31\u4e0d\u4f1a\u653e\u8fc7\u4f60\u4e86\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u542c\u5230\u4e00\u4e2a\u5341\u5206\u53ef\u9760\u7684\u6d88\u606f\u3002\u6211\u5148\u95ee\u4f60\uff0c\u4f60\u77e5\u9053\u5e05\u5b5f\u96c4\u8fd9\u4e2a\u4eba\u5417\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u5e05\u5b5f\u96c4\uff1f\u54e6\uff0c\u6211\u60f3\u8d77\u6765\u4e86\u3002\u4e09\u5e74\u524d\u4ed6\u5230\u8fc7\u6211\u7684\u5bb6\u91cc\uff0c\u548c\u6211\u7684\u54e5\u54e5\u8c08\u5f97\u5f88\u662f \u6295\u673a\uff0c\u54e5\u54e5\u8bf4\u4ed6\u662f\u5173\u5916\u7b2c\u4e00\u9ad8\u624b\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4ed6\u662f\u6ee1\u6d32\u4eba\u3002\u8fd9\u5c31\u602a\u4e0d\u5f97\u4e86\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4ed6\u5192\u5145\u6c49\u4eba\uff0c\u4f7f\u7528\u8be1\u8ba1\uff0c\u6697\u7b97\u4e86\u897f\u661f\u7684\u4e49\u519b\u9886\u8896\u7afa\u5c1a\u7236\uff0c\u66ff\u6e05\u5ef7\u593a\u56de\u4e86\u897f \u661f\uff0c\u4f60\u4e0d\u77e5\u9053\u8fd9\u4ef6\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u7684\u54e5\u54e5\u4ece\u6765\u4e0d\u548c\u6211\u8c08\u53ca\u4e49\u519b\u6297\u6e05\u4e4b\u4e8b\u7684\u3002\u6211\u77e5\u9053\u7684\u53ea\u662f\u5927\u6c5f\u5357\u5317\u7684\u4e00\u4e9b \u6c5f\u6e56\u4e0a\u7684\u4e8b\u60c5\u3002\u8fde\u897f\u661f\u5728\u4ec0\u4e48\u5730\u65b9\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u4f46\u8fd9\u4ef6\u4e8b\u60c5\u5374\u53c8\u4e0e\u6211\u4f55\u5173\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u60c5\u548c\u4f60\u6ca1\u6709\u5173\u7cfb\uff0c\u4f46\u5e05\u5b5f\u96c4\u8fd9\u4e2a\u4eba\u53ef\u5c31\u548c\u4f60\u6709\u5173\u7cfb\u4e86\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u67f3\u7709\u4e00\u7ad6\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u6211\u53ea\u4e0d\u8fc7\u89c1\u8fc7\u4ed6\u4e00\u9762\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\uff0c\u4f60\u7684\u54e5\u54e5\u8981\u4f60\u5ac1\u7ed9\u4ed6\u5462\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u55d4\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff01\u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u662f\u6211\u80e1\u8bf4\u516b\u9053\u3002\u662f\u6c99\u5343\u5cf0\u4ece\u4f60\u54e5\u54e5\u90a3\u513f\u542c\u5230\u7684\u6d88\u606f\uff0c\u60f3\u6765\u4e0d\u4f1a\u662f\u5047\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u6068\u6068\u8bf4\u9053\uff1a\u201c\u6211\u54e5\u54e5\u4e5f\u771f\u662f\u7cca\u6d82\uff0c\u4ed6\u4e5f\u4e0d\u60f3\u60f3\uff0c\u6211\u600e\u80af\u5ac1\u7ed9\u8fd9\u6837\u7684\u4eba\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u54e5\u54e5\u8d2a\u56fe\u529f\u540d\u5bcc\u8d35\uff0c\u4ec0\u4e48\u4e8b\u60c5\u5e72\u4e0d\u51fa\u6765\uff1f\u6240\u4ee5\u4f60\u662f\u7edd\u4e0d\u80fd\u56de\u5bb6\u7684\u4e86\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u6c89\u541f\u4e0d\u8bed\uff0c\u4f3c\u4e4e\u662f\u5728\u76d8\u7b97\u600e\u4f6f\u5e94\u4ed8\u8fd9\u4ef6\u4e8b\u60c5\u3002 \u3000\u3000\u5168\u9010\u6d41\u9053\uff1a\u201c\u4f60\u8eb2\u8d77\u6765\u4e5f\u4e0d\u662f\u529e\u6cd5\uff0c\u4f60\u54e5\u54e5\u8033\u76ee\u7075\u901a\uff0c\u7ed9\u4ed6\u627e\u5230\uff0c\u4f60\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u8bf4\uff0c\u6211\u5e94\u8be5\u600e\u6837\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d\u4ed6\u672c\u6765\u60f3\u8bf4\uff1a\u201c\u6700\u597d\u4f60\u540c\u6211\u4e00\u8d77\uff0c\u54b1\u4eec\u4e8c\u4eba\u8054\u624b\uff0c\u5c31\u4e0d \u7528\u5bb3\u6015\u4f60\u7684\u54e5\u54e5\u6d3e\u4eba\u6349\u4f60\u3002\u201d\u53ef\u662f\u8fd9\u53e5\u8bdd\u4ed6\u5374\u4e0d\u597d\u610f\u601d\u8bf4\u51fa\u6765\u3002\u5047\u5982\u53f2\u7ea2\u82f1\u8fd9\u6837\u95ee\u4ed6\uff1a\u201c\u6211 \u548c\u4f60\u4e00\u8d77\uff0c\u4e5f\u4e0d\u8fc7\u6682\u907f\u4e00\u65f6\u3002\u96be\u9053\u6211\u8fd8\u80fd\u6c38\u8fdc\u8ddf\u7740\u4f60\u4e48\uff1f\u201d\u4ed6\u5c06\u600e\u6837\u56de\u7b54\uff1f\u53ea\u4e0d\u8fc7\u89c1\u4e86\u4e24 \u6b21\uff0c\u603b\u4e0d\u6210\u5c31\u539a\u7740\u8138\u76ae\u5411\u4eba\u5bb6\u6c42\u5a5a\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u5fc3\u6025\u7740\u5462\uff0c\u522b\u8fd9\u4e2a\u90a3\u4e2a\u7684\u4e86\u3002\u6709\u8bdd\u723d\u5feb\u8bf4\u5427\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u7136\u4e0d\u60f3\u5ac1\u7ed9\u5e05\u76c2\u96c4\uff0c\u90a3\u4e48\uff0c\u4f60\uff0c\u4f60\u8fd8\u662f\u53bb\u627e\u674e\u6566\u5427\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u627e\u4ed6\u6709\u4ec0\u4e48\u7528\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u548c\u674e\u6566\u2026\u2026\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u4eba\u600e\u4e48\u5566\uff1f\u8bf4\u8bdd\u541e\u541e\u5410\u5410\u7684\u53eb\u6211 \u90fd\u70e6\u8d77\u6765\u4e86\uff01\u4f60\u53eb\u6211\u548c\u674e\u6566\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u4f60\u4eec\u751f\u7c73\u716e\u6210\u4e86\u719f\u996d\uff0c\u4f60\u54e5\u54e5\u5f53\u7136\u4e5f\u5c31\u4e0d\u597d\u903c\u4f60\u518d\u5ac1\u4ed6\u4eba \u4e86\u3002\u201d\u91d1\u9010\u6d41\u54ac\u4e00\u54ac\u7259\uff0c\u7ec8\u4e8e\u628a\u8981\u8bf4\u7684\u8bdd\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u767b\u65f6\u53d8\u4e86\u9762\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6b3a\u8d1f\u6211\uff0c\u6211\u518d\u4e5f\u4e0d\u7406\u4f60\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5446\u4e86\u4e00\u5446\uff0c\u53eb\u8d77\u4e86\u649e\u5929\u5c48\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u3001\u6211\u53ea\u662f\u4e3a\u4f60\u8bbe\u60f3\uff0c\u600e\u4e48\u53cd\u800c\u662f\u6b3a\u8d1f\u4f60 \u4e86\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u4e00\u54ac\u94f6\u7259\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u628a\u6211\u5f53\u4f5c\u4ec0\u4e48\u4eba\u4e86\uff1f\u7adf\u6562\u5728\u6211\u7684\u9762\u524d\u8bf4\u8fd9\u79cd\u4e0d\u582a\u5165\u8033 \u7684\u8bdd\uff0c\u4f60\u7ed9\u6211\u6eda\u5f00\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7ed9\u5979\u4e00\u9a82\uff0c\u4e00\u5446\u4e4b\u540e\uff0c\u5fc3\u4e2d\u5374\u662f\u6b22\u559c\u5f97\u96be\u4ee5\u5f62\u5bb9\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u8fd9\u90fd\u662f \u6211\u7684\u7cca\u6d82\u3002\u6211\u3001\u6211\u53ea\u4ee5\u4e3a\u4f60\u548c\u674e\u6566\u2026\u2026\u8c01\u77e5\u9053\u4e0d\u662f\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u4f59\u6012\u672a\u606f\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u600e\u4e48\u6837\u90a3\u662f\u4f60\u7684\u4e8b\u3002\u6211\u662f\u4e5f\u597d\uff0c\u4e0d\u662f\u4e5f\u597d\u3002\u4f60\u90fd\u7ba1 \u4e0d\u7740\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u8d54\u7b11\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u4f60\u522b\u751f\u6c14\u4e86\u597d\u4e0d\u597d\uff1f\u6211\u5f53\u7136\u4e0d\u6562\u7ba1\u4f60\u7684\u4e8b\uff0c\u4e0d\u8fc7\u54b1\u4eec\u603b\u7b97\u662f \u670b\u53cb\u4e86\uff0c\u662f\u4e48\uff1f\u4f60\u6709\u4e86\u4e3a\u96be\u4e4b\u4e8b\uff0c\u505a\u670b\u53cb\u7684\u4e5f\u603b\u8be5\u66ff\u4f60\u5206\u70b9\u5fe7\uff0c\u662f\u4e0d\u662f\uff1f\u5373\u4f7f\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u5546 \u91cf\u5546\u91cf\u4e5f\u597d\u3002\u53f2\u59d1\u5a18\uff0c\u65e2\u7136\u4f60\u4e0d\u627e\u674e\u6566\uff0c\u90a3\u4e48\uff0c\u54b1\u4eec\u4e00\u540c\u4e0a\u4eac\u5982\u4f55\uff1f\u6709\u4e86\u4e8b\u60c5\uff0c\u4e24\u4e2a\u4eba\u5bf9\u4ed8 \u603b\u6bd4\u4e00\u4e2a\u4eba\u597d\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7a76\u7adf\u662f\u4e00\u4e2a\u6beb\u65e0\u60c5\u573a\u7ecf\u9a8c\u7684\u6bdb\u5934\u5c0f\u4f19\u5b50\uff0c\u9976\u662f\u4ed6\u5e73\u65f6\u667a\u8ba1\u767e\u51fa\uff0c\u5374\u6beb\u4e0d\u61c2\u5f97\u5973\u5b69 \u513f\u5bb6\u7684\u5fc3\u4e8b\u3002\u5018\u82e5\u4ed6\u5728\u6ca1\u6709\u8c08\u53ca\u674e\u6566\u4e4b\u524d\uff0c\u548c\u53f2\u7ea2\u82f1\u59d4\u5a49\u7684\u8bf4\uff0c\u8bf7\u5979\u540c\u884c\uff0c\u6216\u8005\u53f2\u7ea2\u82f1\u8fd8\u4f1a \u7b54\u5141\u3002\u5982\u4eca\u5728\u95f9\u50f5\u4e4b\u540e\uff0c\u4ed6\u518d\u8fd9\u4e48\u4e00\u8bf4\uff0c\u8fd9\u5c31\u975e\u4f46\u592a\u9732\u75d5\u8ff9\uff0c\u800c\u4e14\u7ed9\u53f2\u7ea2\u82f1\u8bef\u4f1a\u4ed6\u662f\u4e00\u4e2a\u8f7b \u8584\u7684\u5c11\u5e74\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u6c14\u7ea2\u4e86\u8138\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u4e00\u5b9a\u975e\u5f97\u7537\u5b50\u4fdd\u62a4\u4e0d\u6210\u4e48\uff1f\u54fc\uff0c\u4f60\u4e5f\u592a\u8f7b\u89c6\u6211 \u4e86\uff01\uff0e\u4e0d\u9519\uff0c\u6211\u7684\u672c\u9886\u662f\u4e0d\u5982\u4f60\uff0c\u4f46\u5374\u65e0\u987b\u6c42\u4f60\u4fdd\u62a4\uff01\u4f60\u8d70\u4f60\u7684\uff0c\u6211\u8d70\u6211\u7684\uff0c\u522b\u518d\u54c6\u55e6\uff01\u201d \u91d1\u9010\u6d41\u8ffd\u4e0a\u51e0\u6b65\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5509\uff0c\u6211\u8fd9\u4e2a\u4eba\u771f\u662f\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u53c8\u5f97\u7f6a\u4e86\u4f60\uff01\u6211\u600e\u6562\u8f7b\u89c6\u4f60 \u5462\uff0c\u4f60\u662f\u5973\u4e2d\u82f1\u6770\uff0c\u6211\u4f69\u670d\u90fd\u8fd8\u6765\u4e0d\u53ca\u5462\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u8d70\u5feb\u51e0\u6b65\uff0c\u55d4\u9053\uff1a\u201c\u4f60\u771f\u662f\u6211\u547d\u4e2d\u7684\u9b54\u661f\uff0c\u8c01\u8981\u4f60\u4f69\u670d\uff1f\u6211\u53ea\u662f\u6c42\u4f60\u8d70\u5f00\uff0c\u6211\u53ef \u5f97\u8033\u6839\u6e05\u51c0\u3002\u201d\u4f59\u6012\u867d\u72b9\u672a\u606f\uff0c\u4f46\u53e3\u6c14\u5374\u662f\u6e10\u6e10\u8f6f\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u6b63\u5728\u7ea0\u7f20\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u8e44\u58f0\u5f97\u5f97\uff0c\u6587\u9053\u5e84\u98de\u9a91\u8ffd\u6765\uff0c\u8fdc\u8fdc\u7684\u5c31\u5927\u58f0\u559d\u9053\uff1a\u201c\u91d1\u9010\u6d41\u4f60\u8fd9 \u5c0f\u5b50\u7ed9\u6211\u7ad9\u7740\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u4f60\u770b\uff0c\u54b1\u4eec\u4e0d\u8d70\uff0c\u9ebb\u70e6\u53ef\u5c31\u6765\u4e86\uff01\u201d\u53f2\u7ea2\u82f1\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u6587\u9053 \u5e84\u540e\u9762\u8fd8\u6709\u4e09\u9a91\uff0c\u4e00\u9a91\u662f\u6d77\u7802\u5e2e\u7684\u5e2e\u4e3b\u6c99\u5343\u5cf0\uff0c\u8fd8\u6709\u4e24\u9a91\u7adf\u662f\u4ed6\u54e5\u54e5\u7684\u624b\u4e0b\u2014\u2014\u8463\u5341\u4e09\u5a18\u4e0e \u5706\u6d77\u6cd5\u5e08\u3002 \u3000\u3000\u6587\u9053\u5e84\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6709\u80c6\u7684\u4f60\u5c31\u522b\u9003\uff01\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5982\u4eca\u54b1\u4eec\u5012\u662f\u4e0d\u80fd\u8dd1 \u4e86\uff0c\u4e00\u8dd1\uff0c\u4ed6\u5c31\u5f53\u4f5c\u54b1\u4eec\u662f\u6015\u4e86\u4ed6\u4e86\u3002\u201d\u91d1\u9010\u6d41\u662f\u60f3\u8d81\u6b64\u597d\u4e0e\u53f2\u7ea2\u82f1\u8054\u624b\u5bf9\u654c\uff0c\u89e3\u5f00\u4ed6\u4eec\u4e4b \u95f4\u7684\u50f5\u5c40\u3002\u53e6\u4e00\u65b9\u9762\uff0c\u5728\u8fd9\u6837\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c\u91d1\u9010\u6d41\u4e5f\u662f\u96be\u4ee5\u8dd1\u6389\uff0c\u4ed6\u7684\u8f7b\u529f\u518d\u597d\uff0c\u4e5f\u603b\u8dd1\u4e0d \u8fc7\u9a8f\u9a6c\u3002 \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u597d\u5c0f\u5b50\u3001\u4eca\u65e5\u6211\u975e\u548c\u4f60\u62fc\u547d\u4e0d\u53ef\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4ed6\u90a3\u4e00\u9a91\u5df2\u7ecf\u6765 \u5230\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u751f\u6c14\uff0c\u662f\u4e0d\u662f\u4f60\u7684\u5b9d\u8d1d\u513f\u5b50\u6b7b\u4e86\uff1f\u201d\u6587\u9053\u5e84\u5927\u6012\u9053\uff1a\u201c\u4f60\u8fd8\u6562\u8bc5\u5492 \u4ed6\uff01\u54fc\uff0c\u6211\u7684\u4e2d\u513f\u8981\u628a\u4f60\u5316\u9aa8\u626c\u7070\uff0c\u624d\u80fd\u6d88\u89e3\u5fc3\u5934\u4e4b\u6068\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4ee4\u90ce\u53ef\u8fd8\u662f\u6d3b\u7740\u7684\u554a\u3002\u6211\u533b\u597d\u4e86\u4ee4\u90ce\uff0c\u4f60\u4eec\u7236\u5b50\u4e0d\u591a\u8c22\u6211\uff0c\u53cd\u800c \u8981\u53d6\u6211\u7684\u6027\u547d\uff0c\u8fd9\u672a\u514d\u6709\u70b9\u513f\u8bf4\u4e0d\u8fc7\u53bb\u5427\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u6c14\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u8df3\u4e0b\u9a6c\u6765\uff0c\u6307\u7740\u91d1\u9010\u6d41\u9a82\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u7528\u4e86\u90a3\u6837\u5351\u52a3\u7684\u624b\u6bb5 \u4f5c\u5f04\u6211\u4eec\uff0c\u5982\u4eca\u5c45\u7136\u8fd8\u5728\u8bf4\u98ce\u51c9\u8bdd\uff01\u201d\u4f7f\u51fa\u201c\u9f99\u8c61\u795e\u529f\u201d\uff0c\u4e00\u638c\u5c31\u5288\u8fc7\u53bb\u3002 \u3000\u3000\u539f\u6765\u6587\u9053\u5e84\u628a\u91d1\u9010\u6d41\u90a3\u9897\u201c\u89e3\u836f\u201d\u7ed9\u513f\u5b50\u670d\u4e0b\uff0c\u4f9d\u7167\u91d1\u9010\u6d41\u6240\u4f20\u7684\u89e3\u7a74\u4e4b\u6cd5\uff0c\u679c\u7136\u8f7b\u8f7b \u6613\u6613\u7684\u5c31\u628a\u6587\u80dc\u4e2d\u7684\u7a74\u9053\u89e3\u5f00\u3002\u7a74\u9053\u4e00\u89e3\uff0c\u6587\u80dc\u4e2d\u7684\u77e5\u89c9\u6062\u590d\uff0c\u767b\u65f6\u5927\u5455\u7279\u5455\uff0c\u51e0\u4e4e\u628a\u80c3\u56ca \u91cc\u7684\u9ec4\u80c6\u6c34\u90fd\u5455\u4e86\u51fa\u6765\uff0c\u6587\u80dc\u4e2d\u8bf4\u51fa\u6628\u665a\u88ab\u91d1\u9010\u6d41\u4f5c\u5f04\u4e4b\u4e8b\u3002\u6587\u9053\u5e84\u8fd9\u624d\u77e5\u9053\uff0c\u6240\u8c13\u201c\u6bd2 \u836f\u201d\u4e5f\u662f\u4e0d\u8fc7\u662f\u91d1\u9010\u6d41\u8eab\u4e0a\u6413\u51fa\u6765\u7684\u6ce5\u57a2\uff0c\u6587\u9053\u5e84\u518d\u56de\u60f3\u91d1\u9010\u6d41\u7ed9\u4ed6\u201c\u89e3\u836f\u201d\u7684\u60c5\u5f62\uff0c\u5f53\u7136 \u4e5f\u5c31\u660e\u767d\u4e86\uff1a\u4e0d\u4f46\u201c\u6bd2\u836f\u201d\u662f\u6ce5\u57a2\uff0c\u201c\u89e3\u836f\u201d\u4e5f\u662f\u6ce5\u57a2\uff01 \u3000\u3000\u91d1\u9010\u6d41\u7528\u4e86\u4e00\u62db\u201c\u5206\u82b1\u62c2\u67f3\u201d\uff0c\u5316\u89e3\u4e86\u6587\u9053\u5e84\u7684\u653b\u52bf\uff0c\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u53ef\u4ee5\u4fdd\u5168\u4ee4\u90ce\u7684 \u6027\u547d\uff0c\u5982\u4eca\u6211\u8fd9\u89e3\u836f\u662f\u4e00\u670d\u4fbf\u7075\uff0c\u4f60\u8fd8\u600e\u80fd\u8bf4\u6211\u9a97\u4f60\uff1f\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u6c99\u5343\u5cf0\u4e0e\u8463\u5341\u4e09\u5a18\u3001\u5706\u6d77\u7b49\u4eba\u4ea6\u5df2\u6765\u5230\u3002\u6c99\u5343\u5cf0\u9053\uff1a\u201c\u82f1\u59b9\u5b50\uff0c\u4f60\u5230\u4e86\u6211\u8fd9 \u513f\uff0c\u5374\u600e\u4e48\u4e0d\u4e0e\u611a\u5144\u89c1\u9762\u3002\u59d3\u91d1\u8fd9\u5c0f\u5b50\u4e0d\u662f\u597d\u4e1c\u897f\uff0c\u4f60\u83ab\u8981\u7740\u4e86\u4ed6\u9a97\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u597d\u4e1c\u897f\uff0c\u81f3\u5c11\u4e5f\u4e0d\u4f1a\u6bd4\u4f60\u66f4\u574f\u5427\uff1f\u6211\u53ef\u6ca1\u6709\u5728\u4eba\u5bb6\u7684\u9762\u524d\u79f0\u5144\u9053 \u5f1f\uff0c\u5728\u80cc\u540e\u5374\u8c0b\u593a\u4eba\u5bb6\u7684\u5b9d\u8d1d\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8bf4\u7684\u662f\u6c99\u5343\u5cf0\u548c\u6587\u9053\u5e84\u5bc6\u5ba4\u79c1\u8bae\uff0c\u8c0b\u53d6\u53f2\u767d\u90fd\u7684\u7384\u94c1\u4e4b\u4e8b\u3002\u6c99\u5343\u5cf0\u8fd9\u624d\u77e5\u9053\u4ed6\u4eec \u6697\u5730\u91cc\u7684\u90a3\u4e9b\u8bf4\u8bdd\uff0c\u90fd\u7ed9\u91d1\u9010\u6d41\u5077\u542c\u53bb\u4e86\u3002 \u3000\u3000\u6c99\u5343\u5cf0\u751f\u6015\u91d1\u9010\u6d41\u8bf4\u51fa\u66f4\u4e0d\u4e2d\u542c\u7684\u8bdd\u6765\uff0c\u8fde\u5fd9\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u6562\u8bf1\u9a97\u6211\u7684\u53f2\u5bb6\u59b9 \u5b50\uff0c\u6211\u4fbf\u53d6\u4f60\u7684\u6027\u547d\u4e0d\u53ef\uff01\u5341\u4e09\u5a18\uff0c\u53f2\u5bb6\u5927\u59b9\u5b50\u4f60\u53bb\u529d\u529d\u5979\u5427\uff0c\u8fd9\u662f\u8d35\u5e2e\u7684\u4e8b\u60c5\uff0c\u6211\u53ef\u4e0d\u4fbf \u8d8a\u4fce\u4ee3\u75b1\u3002\u201d\u6c99\u5343\u5cf0\u6df1\u77e5\u53f2\u7ea2\u82f1\u7684\u672c\u9886\u4e86\u5f97\uff0c\u800c\u4e14\u662f\u53f2\u767d\u90fd\u7684\u59b9\u59b9\uff0c\u53ef\u662f\u4f24\u5979\u4e0d\u5f97\u7684\u3002\u6c99\u5343 \u5cf0\u6050\u9632\u5979\u4e0a\u6765\u5e2e\u5fd9\u91d1\u9010\u6d41\uff0c\u90a3\u53ef\u5c31\u53eb\u4ed6\u4e3a\u96be\u4e86\u3002\u662f\u4ee5\u8f7b\u8f7b\u4e00\u63a8\uff0c\u4fbf\u628a\u8d23\u4efb\u63a8\u7ed9\u8463\u5341\u4e09\u5a18\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u521a\u521a\u53d1\u813e\u6c14\uff0c\u5bf9\u91d1\u9010\u6d41\u662f\u4f59\u6012\u672a\u6d88\uff0c\u53c8\u89c1\u91d1\u9010\u6d41\u4e0e\u6587\u9053\u5e84\u4ea4\u624b\uff0c\u9887\u5360\u4e0a\u98ce\uff0c\u662f\u4ee5 \u5979\u6b64\u9645\u4ecd\u5728\u8896\u624b\u65c1\u89c2\u3002\u672c\u6765\u5979\u662f\u6709\u529e\u6cd5\u53ef\u4ee5\u9003\u8dd1\u7684\uff0c\u4e0d\u8fc7\uff0c\u5979\u4e5f\u4e0d\u80af\u9003\u8dd1\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u770b\u89c1\u53f2\u7ea2\u82f1\u5e76\u6ca1\u4e0a\u524d\u52a9\u6218\uff0c\u4ee5\u4e3a\u5979\u5df2\u56de\u5fc3\u8f6c\u610f\uff0c\u4fbf\u8d70\u8fc7\u53bb\u7b11\u9053\uff1a\u201c\u82f1\u59b9\u5b50\uff0c\u4f60 \u662f\u660e\u767d\u4eba\uff0c\u5144\u59b9\u4e0d\u548c\uff0c\u4e5f\u603b\u8fd8\u662f\u81ea\u5df1\u4eba\u3002\u4f60\u54e5\u54e5\u77e5\u9053\uff0c\u4f60\u8fd9\u6b21\u51fa\u8d70\uff0c\u90fd\u662f\u53d7\u4e86\u8fd9\u5c0f\u5b50\u7684\u8bf1 \u60d1\u3002\u4f60\u56de\u53bb\u4ed6\u4e0d\u4f1a\u602a\u8d23\u4f60\u7684\u3002\u8fd9\u5c0f\u5b50\u4e0d\u4f46\u662f\u54b1\u4eec\u516d\u5408\u5e2e\u7684\u654c\u4eba\uff0c\u4ed6\u8fd8\u628a\u6c5f\u6e56\u4e0a\u53e6\u5916\u7684\u56db\u5927\u5e2e \u4f1a\u5168\u90fd\u5f97\u7ef8\u4e86\uff0c\u4f60\u8e09\u4ed6\u4e00\u8d77\uff0c\u662f\u51b3\u6ca1\u6709\u597d\u5904\u7684\u3002\u6211\u627e\u4f60\u5df2\u7ecf\u591a\u65f6\u4e86\uff0c\u597d\u4e0d\u5bb9\u6613\u5728\u8fd9\u91cc\u78b0\u89c1 \u4e86\uff0c\u54b1\u4eec\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u539f\u6765\u8463\u5341\u4e09\u5a18\u90a3\u65e5\u5728\u82cf\u5dde\u9047\u89c1\u7ea2\u7f28\u4f1a\u7684\u5bab\u79c9\u85e9\u548c\u9752\u9f99\u5e2e\u7684\u9ad8\u5927\u6210\u7b49\u4eba\uff0c\u5df2\u77e5\u53f2\u7ea2\u82f1\u548c\u91d1 \u9010\u6d41\u5728\u4e00\u8d77\uff0c\u7384\u94c1\u4e5f\u662f\u5728\u4ed6\u4eec\u624b\u4e0a\u3002\u4e8e\u662f\u8463\u5341\u4e09\u5a18\u4e00\u9762\u5411\u5e2e\u4e2d\u62a5\u8baf\uff0c\u4e00\u9762\u8ddf\u8e2a\u5bfb\u89c5\u3002\u5979\u4e00\u76f4 \u4ee5\u4e3a\u91d1\u4e8c\u4eba\u540c\u5728\u4e00\u8d77\uff0c\u5e76\u4e0d\u77e5\u9053\u4ed6\u4eec\u5df2\u7ecf\u8fc7\u4e86\u4e00\u756a\u79bb\u5408\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u4e0e\u5706\u6d77\u627e\u5230\u4e86\u6d77\u7802\u5e2e\uff0c\u521a\u597d\u662f\u6587\u9053\u5e84\u628a\u91d1\u9010\u6d41\u7684\u201c\u89e3\u836f\u201d\u62ff\u56de\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u4eec \u77e5\u9053\u91d1\u9010\u6d41\u5c1a\u672a\u8d70\u8fdc\uff0c\u4fbf\u7acb\u5373\u5feb\u9a6c\u6765\u9053\u3002\u679c\u7136\u53d1\u73b0\u4e86\u53f2\u7ea2\u82f1\u4e5f\u5728\uff0c\u4ed6\u4eec\u66f4\u4ee5\u4e3a\u53f2\u7ea2\u82f1\u4e00\u76f4\u662f \u548c\u91d1\u9010\u6d41\u540c\u8c0b\u7684\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u542c\u4e86\u8463\u5341\u4e09\u5a18\u7684\u53e3\u6c14\uff0c\u7adf\u7136\u628a\u5979\u5f53\u4f5c\u662f\u548c\u91d1\u9010\u6d41\u79c1\u5954\u7684\uff0c\u4e0d\u7981\u53c8\u7f9e\u53c8\u6c14\uff0c\u67f3\u7709\u4e00 \u575a\uff0c\u8bf4\u9053\uff1a\u201c\u968f\u4fbf\u4f60\u8bf4\u6211\u4ec0\u4e48\uff0c\u6211\u4e0d\u56de\u53bb\uff01\u201d\u53f2\u7ea2\u82f1\u6709\u51e0\u5206\u7537\u513f\u7684\u6c14\u8d28\uff0c\u53c8\u6709\u51e0\u5206\u5c11\u5973\u7684\u77dc \u6301\uff0c\u867d\u7136\u53d7\u4e86\u59d4\u5c48\uff0c\u5374\u4e5f\u4e0d\u613f\u5206\u8fa9\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u82f1\u59b9\u5b50\uff0c\u4f60\u503c\u5f97\u4e3a\u8fd9\u5c0f\u5b50\u6c38\u4e0d\u56de\u5bb6\u5417\uff1f\u6bd4\u8fd9\u5c0f\u5b50\u5f3a\u7684\u7537\u4eba \u591a\u7740\u5462\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u53d8\u4e86\u9762\u8272\uff0c\u559d\u9053\uff1a\u201c\u4f4f\u5634\uff01\u4f60\u752d\u518d\u8bf4\uff0c\u6211\u53ef\u4e0d\u548c\u4f60\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u4e0b\u4e0d\u4e86\u53f0\uff0c\u8bf4\u9053\uff1a\u201c\u82f1\u59b9\u5b50\uff0c\u4f60\u54e5\u54e5\u7684\u547d\u4ee4\u662f\u8981\u6211\u4eec\u52a1\u5fc5\u628a\u4f60\u6211\u56de\u53bb\u7684\u3002\u6211\u4e0d \u60f3\u7528\u5f3a\uff0c\u53ef\u662f\u2026\u2026\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u522b\u591a\u8bf4\u4e86\uff0c\u6211\u51b3\u4e0d\u56de\u53bb\uff01\u4f60\u6709\u672c\u9886\u4f60\u6765\u62ff\u6211\u5c31\u662f\uff01\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u6ca1\u6709\u529e\u6cd5\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u7136\u6267\u610f\u4e0d\u4ece\uff0c\u5bf9\u4e0d\u4f4f\uff0c\u90a3\u6211\u4e5f\u53ea\u597d\u2018\u8bf7\u2019\u4f60\u56de\u53bb \u4e86\u3002\u201d\u8bf4\u5230\u4e00\u4e2a\u2018\u8bf7\u2019\u5b57\uff0c\u624b\u4e0a\u7684\u8f6f\u97ad\u84e6\u5730\u5377\u5c06\u8fc7\u53bb\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u559d\u58f0\uff1a\u201c\u6765\u5f97\u597d\uff01\u54b1\u4eec\u5c31\u8f83\u91cf\u8f83\u91cf\u97ad\u6cd5\u5427\uff01\u201d\u94f6\u97ad\u4e00\u8d77\uff0c\u7acb\u5373\u8fd8\u4e86\u4e00\u62db\u201c\u73cd\u73e0\u5377 \u5e18\u201d\uff0c\u5979\u4eec\u4e24\u4eba\u90fd\u662f\u4f7f\u97ad\u7684\uff0c\u4e0d\u8fc7\u56e0\u4e3a\u53f2\u7ea2\u82f1\u7d20\u6765\u770b\u4e0d\u8d77\u8463\u5341\u4e09\u5a18\uff0c\u4e24\u4eba\u4e00\u5411\u662f\u9762\u548c\u5fc3\u4e0d \u548c\uff0c\u662f\u4ee5\u867d\u7136\u540c\u5c5e\u4e00\u5e2e\uff0c\u5374\u4ece\u6ca1\u6709\u62c6\u8fc7\u62db\u3002 \u3000\u3000\u53cc\u97ad\u4e00\u4ea4\uff0c\u53f2\u7ea2\u82f1\u7684\u94f6\u97ad\u7ed9\u8463\u5341\u4e09\u5a18\u8361\u5f00\uff0c\u4f46\u5979\u97ad\u68a2\u4e00\u8f6c\uff0c\u7acb\u5373\u53c8\u4ece\u8463\u5341\u4e09\u5a18\u610f\u60f3\u4e0d\u5230 \u7684\u65b9\u4f4d\u626b\u6765\uff0c\u8463\u5341\u4e09\u5a18\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u53f2\u7ea2\u82f1\u7684\u97ad\u68a2\u51e0\u4e4e\u662f\u8d34\u5979\u7684\u9b13\u811a\u626b\u8fc7\u3002\u539f\u6765\u8bba \u6c14\u529b\u662f\u8463\u5341\u4e09\u5a18\u5145\u6c9b\uff0c\u8bba\u97ad\u6cd5\u4e24\u4eba\u5404\u6709\u5343\u79cb\uff0c\u53f2\u7ea2\u82f1\u7684\u53d8\u5316\u5219\u66f4\u4e3a\u7cbe\u5999\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u4e0e\u8463\u5341\u4e09\u5a18\u6253\u5f97\u96be\u89e3\u96be\u5206\uff0c\u90a3\u4e00\u8fb9\uff0c\u91d1\u9010\u6d41\u548c\u6c99\u5343\u5cf0\u4e5f\u4ea4\u4e0a\u4e86\u624b\u3002 \u3000\u3000\u5176\u65f6\u91d1\u9010\u6d41\u6b63\u5728\u7528\u5230\u4e00\u62db\u201c\u4e00\u67f1\u64ce\u5929\u201d\u5355\u638c\u5316\u89e3\u6587\u9053\u5e84\u7684\u201c\u4e09\u8c61\u795e\u529f\u201d\uff0c\u6c99\u5343\u5cf0\u770b\u51fa\u6709 \u4fbf\u5b9c\u53ef\u62fe\uff0c\u547c\u7684\u4e00\u638c\u4fbf\u6253\u8fc7\u53bb\uff0c\u638c\u631f\u8165\u98ce\uff0c\u89e6\u9f3b\u96be\u95fb\u3002\u4ed6\u7ec3\u7684\u662f\u6bd2\u7802\u638c\u529f\u592b\uff0c\u82e5\u7136\u7ed9\u4ed6\u6253\u7740 \u4e00\u638c\uff0c\u808c\u8089\u4f1a\u6e83\u70c2\u800c\u4ea1\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4fa7\u8eab\u95ea\u5f00\uff0c\u63a9\u9f3b\u8bf4\u9053\uff1a\u201c\u597d\u81ed\uff0c\u597d\u81ed\uff01\u4f60\u6d51\u8eab\u662f\u6bd2\uff0c\u770b\u6765\u6211\u4e5f\u8be5\u7ed9\u4f60\u4e00\u4e38\u89e3\u836f\u624d \u662f\uff01\u201d\u91d1\u9010\u6d41\u5de6\u624b\u63d0\u7740\u7384\u94c1\uff0c\u4fa7\u8eab\u4e4b\u65f6\uff0c\u91cd\u7269\u4e0b\u5760\u7684\u8ff9\u8c61\uff0c\u5728\u6c99\u5343\u5cf0\u8fd9\u6837\u7684\u6709\u7ecf\u9a8c\u7684\u6c5f\u6e56\u5927 \u76d7\u773c\u91cc\uff0c\u770b\u5f97\u51fa\u6765\u3002 \u3000\u3000\u6c99\u5343\u5cf0\u5fc3\u5934\u4e00\u52a8\uff0c\u559d\u9053\uff1a\u201c\u59d3\u91d1\u7684\u5c0f\u5b50\uff0c\u4f60\u624b\u4e0a\u62ff\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u91d1\u9010\u6d41\u563b\u563b\u7b11\u9053\uff1a \u201c\u6b63\u662f\u4f60\u60f3\u8981\u7684\u4e1c\u897f\uff0c\u8fd8\u7ed9\u4f60\u5427\uff0c\u53ea\u8981\u4f60\u6388\u5f97\u8d77\u3002\u201d \u3000\u3000\u6c99\u5343\u5cf0\u66fe\u7ecf\u4e0a\u8fc7\u4e00\u6b21\u5f53\uff0c\u6b64\u65f6\u534a\u4fe1\u534a\u7591\uff0c\u770b\u89c1\u91d1\u9010\u6d41\u63d0\u7740\u5323\u5b50\u5411\u4ed6\u7838\u6765\uff0c\u6043\u7740\u638c\u529b\u96c4 \u539a\uff0c\u60f3\u6253\u5b83\u4e00\u638c\u8bd5\u8bd5\uff0c\u6587\u9053\u5e84\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u4e0d\u53ef\u786c\u78b0\uff01\u201d \u3000\u3000\u5706\u6d77\u4e0d\u6562\u5f97\u7f6a\u53f2\u7ea2\u82f1\uff0c\u4ed6\u5728\u82cf\u5dde\u4e4b\u65f6\uff0c\u66fe\u88ab\u91d1\u9010\u6d41\u4e24\u6b21\u4e09\u756a\u7684\u620f\u800d\uff0c\u6b64\u6068\u672a\u6d88\uff0c\u4fbf\u62d4\u51fa \u6212\u5200\uff0c\u4e0a\u524d\u52a9\u6218\u3002\u6b63\u597d\u66ff\u6c99\u5343\u5cf0\u6321\u4e86\u91d1\u9010\u6d41\u7684\u4e00\u51fb\u3002 \u3000\u3000\u5706\u6d77\u7684\u5916\u5bb6\u529f\u592b\u5dee\u4e0d\u591a\u767b\u5cf0\u9020\u6781\uff0c\u4e24\u81c2\u6709\u5343\u65a4\u4e4b\u529b\uff0c\u4f46\u5374\u8fd8\u6bd4\u4e0d\u4e0a\u91d1\u9010\u6d41\u5185\u5bb6\u771f\u529b\u7684\u7eaf \u539a\uff0c\u91d1\u8fbe\u6d41\u624b\u4e0a\u62ff\u7684\u662f\u7384\u94c1\uff0c\u4ed6\u8fd9\u4e00\u4e0b\u786c\u78b0\uff0c\u5f53\u7136\u5c31\u8981\u5927\u5927\u7684\u5403\u4e8f\u4e86\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u84ec\u98de\uff0c\u91d1\u9010\u6d41\u7684\u5323\u5b50\u662f\u6728\u5934\u505a\u7684\uff0c\u7ed9\u5706\u6d77\u7684\u6212\u5200\uff0c\u65ab\u7834\u4e86\u4e00 \u9053\u4e94\u5bf8\u957f\u4e24\u5bf8\u9614\u7684\u88c2\u7f1d\uff0c\u4f46\u5706\u6d77\u7684\u6212\u5200\u89e6\u7740\u4e86\u5323\u4e2d\u7684\u7384\u94c1\uff0c\u5374\u8fde\u5200\u53e3\u90fd\u5377\u8d77\u6765\uff01\u5706\u6d77\u864e\u53e3\u9178 \u9ebb\uff0c\u7ed9\u9707\u5f97\u8e6c\u3001\u8e6c\u3001\u8e6c\u7684\u5012\u9000\u4e09\u6b65\uff0c\u5140\u662f\u7a33\u4e0d\u4f4f\u8eab\u5f62\uff0c\u8981\u63a5\u8fde\u6253\u4e86\u516d\u4e03\u4e2a\u76d8\u65cb\uff0c\u624d\u7ad9\u5f97\u4f4f \u811a\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6253\u5f97\u6027\u8d77\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u7384\u94c1\u53c8\u5411\u6587\u9053\u5e84\u51fb\u53bb\uff0c\u6587\u9053\u5e84\u907f\u5f00\u6b63\u9762\uff0c\u53cc\u638c\u4e00\u6452\uff0c\u7528\u4e86 \u4e2a\u201c\u5378\u201d\u5b57\u8bc0\uff0c\u638c\u7f18\u8f7b\u8f7b\u5728\u5323\u5b50\u4fa7\u8fb9\u4e00\u5e26\uff0c\u628a\u91d1\u9010\u6d41\u7684\u90a3\u80a1\u731b\u65b9\u5378\u5f00\uff0c\u4f46\u4e5f\u4e0d\u7981\u5012\u9000\u4e24\u6b65\u3002 \u3000\u3000\u6c99\u5343\u5cf0\u53c8\u60ca\u53c8\u559c\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u679c\u7136\u771f\u662f\u7384\u94c1\uff01\u201d\u5634\u5df4\u672a\u66fe\u5408\u62e2\uff0c\u5ffd\u89c9\u6709\u5f02\u7269\u5165\u5589\uff0c\u5947 \u81ed\u65e0\u6bd4\u3002\u539f\u6765\u91d1\u9010\u6d41\u771f\u7684\u662f\u8bf4\u5f97\u5230\u505a\u5f97\u5230\uff0c\u634f\u4e86\u4e00\u4e38\u6ce5\u57a2\uff0c\u5f39\u5165\u4ed6\u7684\u53e3\u4e2d\uff0c\u6c99\u5343\u5cf0\u8981\u5410\u5df2\u6765 \u4e0d\u53ca\uff0c\u6ed1\u4e0b\u54bd\u5589\u53bb\u4e86\u3002 \u3000\u3000\u6c99\u5343\u5cf0\u5927\u6012\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4eca\u65e5\u6211\u4e0d\u6740\u4f60\uff0c\u8a93\u4e0d\u4e3a\u4eba\uff01\u201d\u53d6\u51fa\u4e86\u4e00\u5bf9\u5224\u5b98\u7b14\uff0c\u518d\u6b21\u4e0a \u524d\u4e0e\u6587\u9053\u5e84\u8054\u624b\u5939\u653b\u3002\u4ed6\u77e5\u9053\u91d1\u9010\u6d41\u624b\u4e2d\u62ff\u7684\u662f\u7384\u94c1\uff0c\u5df2\u4e0d\u6562\u518d\u51ed\u4e00\u53cc\u8089\u638c\u5e94\u654c\u4e86\uff01 \u3000\u3000\u5706\u6d77\u7ad9\u7a33\u4e86\u811a\u6b65\u4e4b\u540e\uff0c\u4e5f\u662f\u706b\u6c14\u51b2\u5929\uff0c\u7acb\u5373\u53c8\u6251\u4e0a\u6765\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4eca\u65e5\u4e0d\u662f\u4f60 \u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4e2a\u4eba\u90fd\u8981\u6211\u7684\u6027\u547d\uff0c\u6211\u5374\u53ea\u6709\u4e00\u6761\uff0c\u8fd9\u600e\u4e48\u529e\uff1f\u6211\u4e0d\u60f3\u539a\u6b64\u8584 \u5f7c\uff0c\u5bf9\u4e0d\u4f4f\uff0c\u53ea\u597d\u90fd\u4e0d\u7ed9\u4e86\uff01\u201d\u4ed6\u53e3\u91cc\u8bf4\u7b11\uff0c\u624b\u91cc\u5df2\u662f\u4eae\u5251\u51fa\u9798\uff0c\u95ea\u7535\u822c\u7684\u4fbf\u671d\u7740\u6c99\u5343\u5cf0\u4fbf \u662f\u4e00\u5251\uff01 \u3000\u3000\u6c99\u5343\u5cf0\u5fd9\u628a\u53cc\u7b3a\u4e00\u67b6\uff0c\u91d1\u9010\u6d41\u5251\u5cf0\u4e00\u504f\uff0c\u51e0\u4e4e\u662f\u5e73\u524a\u800c\u8fc7\uff0c\u6c99\u5343\u5cf0\u5012\u7eb5\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u5413 \u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u4e2a\u6ed1\u6b65\u56de\u8eab\uff0c\u957f\u5251\u53c8\u5411\u6587\u9053\u5e84\u80f8\u53e3\u523a\u53bb\uff0c\u6587\u9053\u5e84\u5de6\u638c\u62cd\u51fa\uff0c\u5de6\u638c\u4e00\u5212\uff0c\u53cd\u5207\u4ed6 \u7684\u864e\u53e3\uff0c\u8981\u7528\u7a7a\u624b\u5165\u767d\u5203\u7684\u529f\u592b\uff0c\u62a2\u4ed6\u7684\u5251\u3002\u54ea\u77e5\u91d1\u9010\u6d41\u7528\u7684\u9053\u98ce\u5251\u5f0f\u5947\u5feb\u65e0\u6bd4\uff0c\u9661\u7136\u95f4\u7531 \u5b9e\u5316\u865a\uff0c\u53c8\u7531\u865a\u5316\u5b9e\uff0c\u6587\u9053\u5e84\u4e00\u6293\u6293\u7a7a\uff0c\u5251\u5c16\u53c8\u6307\u5230\u4ed6\u80c1\u4e0b\u7684\u201c\u6108\u6c14\u7a74\u201d\u3002\u6587\u9053\u5e84\u8eab\u8eaf\u4e00 \u77ee\uff0c\u4e2d\u6307\u75be\u5f39\uff0c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u628a\u91d1\u9010\u6d41\u7684\u957f\u5251\u5f39\u5f00\u3002\u8fd9\u4e00\u62db\u7528\u5f97\u60ca\u9669\u7edd\u4f26\uff0c\u6587\u9053\u5e84\u867d\u7136\u5f39 \u5f00\u4e86\u91d1\u9010\u6d41\u7684\u5251\uff0c\u4ea6\u662f\u5413\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u91d1\u9010\u6d41\u4e00\u903c\u9000\u4e86\u6587\u9053\u5e84\uff0c\u957f\u5251\u53c8\u6307\u5230\u4e86\u5706\u6d77\u7684\u9762\u95e8\uff0c\u5706\u6d77\u628a\u6212\u5200\u4e00 \u6321\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u6212\u5200\u53c8\u635f\u4e86\u4e00\u4e2a\u7f3a\u53e3\u3002\u91d1\u9010\u6d41\u7528\u7684\u53ea\u662f\u4e00\u628a\u666e\u901a\u7684\u9752\u94a2\u5251\uff0c\u5706\u6d77\u7684\u6212\u5200 \u8981\u6bd4\u4ed6\u7684\u5251\u91cd\u5f97\u591a\uff0c\u5374\u51e0\u4e4e\u7ed9\u4ed6\u7684\u9752\u94a2\u5251\u524a\u65ad\uff0c\u5706\u6d77\u8fd9\u4e00\u60ca\u6bd4\u521a\u624d\u4ed6\u7684\u6212\u5200\u7ed9\u7384\u94c1\u78b0\u7740\u66f4 \u751a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u7684\u5185\u529f\u770b\u6765\u4e0d\u5728\u53f2\u5e2e\u4e3b\u4e4b\u4e0b\u3002\u4eca\u65e5\u8981\u60f3\u62a5\u4ec7\uff0c\u53ea\u6015\u8fd8\u5f53\u771f\u662f\u4e0d\u5bb9\u6613 \u5462\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4ee5\u95ea\u7535\u822c\u7684\u5947\u5e7b\u5251\u6cd5\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u8fde\u88ad\u4e09\u5927\u9ad8\u624b\uff0c\u628a\u4e09\u5927\u9ad8\u624b\uff0c\u90fd\u5413\u51fa\u4e86\u4e00\u8eab\u51b7 \u6c57\u3002\u4f46\u8fd9\u4e0d\u8fc7\u662f\u5947\u88ad\u4e4b\u52b2\uff0c\u82e5\u8bba\u771f\u5b9e\u7684\u672c\u9886\uff0c\u91d1\u9010\u6d41\u53ea\u80fd\u80dc\u8fc7\u5176\u4e2d\u7684\u4efb\u4f55\u4e00\u4eba\uff0c\u5bf9\u65b9\u82e5\u662f\u4e24 \u4eba\u8054\u624b\uff0c\u91d1\u9010\u6d41\u5df2\u975e\u5176\u654c\uff0c\u4ee5\u4e00\u654c\u4e09\uff0c\u90a3\u662f\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u80fd\u5e94\u4ed8\u7684\u4e86\u3002 \u3000\u3000\u6240\u5e78\u4ed6\u5de6\u624b\u62ff\u7684\u662f\u7384\u94c1\uff0c\u654c\u4eba\u5747\u6709\u987e\u5fcc\u3002\u4ed6\u7684\u5251\u6cd5\u53c8\u6781\u7cbe\u5999\uff0c\u654c\u4eba\u4e2d\u6587\u9053\u5e84\u7684\u672c\u9886\u6700 \u9ad8\u3002\u6587\u9053\u5e84\u6709\u4e09\u8c61\u795e\u529f\uff0c\u4e0d\u6015\u548c\u4ed6\u6bd4\u638c\uff0c\u4f46\u5bf9\u4ed8\u4ed6\u795e\u51fa\u9b3c\u6ca1\u7684\u5251\u6cd5\uff0c\u5374\u4e0d\u80fd\u4e0d\u6709\u51e0\u5206\u602f\u610f\u3002 \u6b66\u529f\u6b21\u5f3a\u7684\u662f\u6c99\u5343\u5cf0\uff0c\u6c99\u5343\u5cf0\u7684\u7edd\u6280\u662f\u6bd2\u7802\u638c\u529f\u592b\uff0c\u5982\u4eca\u4e3a\u4e86\u987e\u5fcc\u91d1\u9010\u6d41\u624b\u4e2d\u7684\u7384\u94c1\uff0c\u53ea\u6562 \u7528\u5224\u5b98\u7b14\u5e94\u654c\u3002\u6c99\u5343\u5cf0\u7684\u5224\u5b98\u7b14\u70b9\u7a74\u529f\u592b\u867d\u7136\u4e5f\u5f88\u4e0d\u9519\uff0c\u7a76\u7adf\u4e0d\u5982\u6bd2\u7802\u638c\u662f\u4ed6\u7684\u62ff\u624b\u529f\u592b\u3002 \u8fd9\u4e48\u4e00\u6765\uff0c\u6c99\u5343\u5cf0\u7684\u4f5c\u6218\u529b\u91cf\u4e5f\u6253\u4e86\u4e00\u4e2a\u6298\u6263\u3002 \u3000\u3000\u6709\u8fd9\u51e0\u91cd\u5173\u7cfb\uff0c\u91d1\u9010\u6d41\u5148\u58f0\u593a\u4eba\uff0c\u5c45\u7136\u4e0e\u4e09\u5927\u9ad8\u624b\u6253\u6210\u4e86\u5e73\u624b\u3002\u4f46\u8fd9\u5e73\u624b\u7684\u5c40\u9762\u7ef4\u6301\u4e94 \u516d\u5341\u62db\u4e4b\u540e\uff0c\u5bf9\u65b9\u4e09\u4e2a\u4eba\u60ca\u9b42\u5df2\u5b9a\uff0c\u602f\u610f\u6e10\u6d88\uff0c\u6e10\u6e10\u914d\u5408\u5f97\u5b9c\uff0c\u91d1\u9010\u6d41\u5c31\u8d8a\u6765\u8d8a\u611f\u5230\u5403\u529b \u4e86\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u53f2\u7ea2\u82f1\u548c\u8463\u5341\u4e09\u5a18\u6597\u97ad\uff0c\u5374\u662f\u9887\u5360\u4e0a\u98ce\u3002\u672c\u6765\u5979\u4eec\u5404\u6709\u6240\u957f\uff0c\u8bba\u6c14\u529b\u8fd8\u662f\u8463\u5341\u4e09 \u5a18\u5f3a\u4e9b\u3002\u4f46\u56e0\u4e3a\u8463\u5341\u4e09\u5a18\u4e0d\u6562\u4f24\u5979\uff0c\u53f2\u7ea2\u82f1\u9042\u5f97\u5927\u62a2\u653b\u52bf\uff01 \u3000\u3000\u6fc0\u6218\u4e2d\u53f2\u7ea2\u82f1\u4e00\u62db\u201c\u6d77\u96e8\u751f\u98ce\u201d\uff0c\u628a\u8463\u5341\u4e09\u5a18\u903c\u5f97\u8fde\u8fde\u540e\u9000\u3002\u53f2\u7ea2\u82f1\u500f\u5730\u4e00\u4e2a\u8f6c\u8eab\uff0c\u4fbf \u5230\u4e86\u91d1\u9010\u6d41\u8fd9\u8fb9\uff0c\u5530\u7684\u4e00\u97ad\uff0c\u5411\u5706\u6d77\u6253\u4e0b\u3002\u5706\u6d77\u6b63\u60f3\u4e58\u865a\u653b\u51fb\u91d1\u9010\u6d41\u7684\u7a7a\u95e8\uff0c\u60f3\u4e0d\u5230\u53f2\u7ea2\u82f1 \u7a81\u7136\u4f1a\u6765\u6253\u4ed6\uff0c\u4f46\u89c1\u94f6\u5149\u4e00\u95ea\uff0c\u62db\u67b6\u5df2\u6765\u4e0d\u53ca\uff0c\u201c\u5530\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u5706\u6d77\u7684\u5149\u5934\u5df2\u662f\u7740\u4e86\u4e00 \u97ad\u3002\u5e78\u800c\u53f2\u7ea2\u82f1\u8fd9\u4e00\u97ad\u53ea\u662f\u8584\u60e9\uff0c\u5e76\u975e\u6740\u624b\uff0c\u4f46\u867d\u7136\u5982\u6b64\uff0c\u4ed6\u7684\u5149\u5934\u4e0a\u4e5f\u591a\u4e86\u4e00\u9053\u6de1\u6de1\u7684\u8840 \u75d5\u4e86\u3002 \u3000\u3000\u5706\u6d77\u53c8\u60ca\u53c8\u6c14\uff0c\u5927\u53eb\u9053\uff1a\u201c\u5988\u4e00\u5988\u5440\uff01\u201d\u5706\u6d77\u6027\u60c5\u66b4\u8e81\uff0c\u4ed6\u672c\u6765\u662f\u8981\u9a82\u5988\u7684\uff01\u201d\u9a82\u4e86\u4e00 \u4e2a\u201c\u5988\u201d\u5b57\uff0c\u84e6\u5730\u60f3\u8d77\u8fd9\u662f\u5e2e\u4e3b\u7684\u59b9\u59b9\uff0c\u5c82\u53ef\u53e3\u51fa\u7c97\u8a00\uff1f\u4e8e\u662f\u4e00\u53d8\u800c\u4e3a\u53eb\u5a18\u558a\u5988\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u5012\u6709\u70b9\u8fc7\u610f\u4e0d\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u60f3\u6253\u4f60\uff0c\u4f46\u4f60\u4eec\u501a\u591a\u4e3a\u80dc\uff0c\u6b3a\u8d1f\u6211\u4eec\u3002\u8fd9\u53ef\u5c31 \u602a\u4e0d\u5f97\u6211\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u5f97\u4ece\u53f2\u7ea2\u82f1\u7684\u53e3\u4e2d\u8bf4\u51fa\u201c\u6211\u4eec\u201d\u4e8c\u5b57\uff1b\u767b\u65f6\u5fc3\u82b1\u5927\u653e\uff0c\u7cbe\u795e\u9661\u632f\uff0c\u5de6\u4e00\u62db\u201c\u767d \u8679\u8d2f\u65e5\u201d\uff0c\u53f3\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u628a\u6587\u9053\u5e84\u4e0e\u6c99\u5343\u5cf0\u90fd\u903c\u4e86\u4e00\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u5706\u6d77\uff0c\u6211\u770b\u4f60\u7684 \u5e74\u7eaa\u603b\u6709\u56db\u5341\u5c81\u4ee5\u4e0a\u4e86\u5427\uff1f\u4f60\u7684\u5988\u8fd8\u6d3b\u7740\u5417\uff1f\u6d3b\u7740\u6050\u6015\u4e5f\u6709\u516d\u4e03\u5341\u5c81\u4e86\u5427\uff1f\u4e0a\u4e86\u8fd9\u6837\u5e74\u7eaa\u7684 \u4eba\uff0c\u8033\u6735\u4e0d\u4f1a\u5f88\u597d\u7684\u4e86\u3002\u4f60\u65e2\u7136\u6302\u5ff5\u4f60\u5988\uff0c\u5c31\u5e94\u8be5\u56de\u5230\u5979\u7684\u819d\u4e0b\uff0c\u4eb2\u4eb2\u70ed\u70ed\u5730\u53eb\u5979\u4e00\u58f0\u2018\u5988 \u5440\u2019\uff0c\u4f60\u5728\u8fd9\u65f6\u53eb\uff0c\u5979\u600e\u4f1a\u542c\u5f97\u89c1\u5462\uff1f\u201d \u3000\u3000\u5706\u6d77\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u53eb\u9053\uff0c\u201c\u53f2\u59d1\u5a18\uff0c\u6211\u4e0d\u60f3\u5f97\u7f6a\u4f60\uff0c\u4f46\u8fd9\u5c0f\u5b50\uff0c\u6211\u975e\u548c\u4ed6\u62fc\u547d\u4e0d \u53ef\uff01\u201d\u91d1\u9010\u6d41\u6447\u4e86\u6447\u5934\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5509\uff0c\u6211\u597d\u5fc3\u529d\u4f60\uff0c\u60f3\u4e0d\u5230\u4f60\u7adf\u662f\u72d7\u54ac\u5415\u6d1e\u5bbe\uff0c\u4e0d\u8bc6\u597d \u4eba\u5fc3\uff01\u201d\u968f\u624b\u4e00\u4e2a\u201c\u62d4\u4e91\u89c1\u65e5\u201d\uff0c\u8f7b\u63cf\u6de1\u5199\u7684\u5c31\u5316\u89e3\u4e86\u5706\u6d77\u7684\u6cfc\u98ce\u4e09\u5200\u3002 \u3000\u3000\u6c99\u5343\u5cf0\u53cc\u7b14\u4e00\u6233\uff0c\u6311\u5f00\u4e86\u53f2\u7ea2\u82f1\u7684\u94f6\u97ad\uff0c\u8fc5\u5373\u4fbf\u70b9\u5411\u5979\u7684\u201c\u98ce\u5e9c\u7a74\u201d\uff0c\u53f2\u7ea2\u82f1\u8eab\u6cd5\u8f83 \u7075\uff0c\u6c99\u5343\u5cf0\u4e00\u7b14\u70b9\u7a7a\uff0c\u53f2\u7ea2\u82f1\u5df2\u662f\u8f6c\u5230\u4e86\u91d1\u9010\u6d41\u7684\u8eab\u8fb9\uff0c\u4e0e\u4ed6\u5e76\u80a9\u5bf9\u654c\u4e86\u3002\u6c99\u5343\u5cf0\u607c\u9053\uff1a \u201c\u82f1\u59b9\u5b50\uff0c\u6211\u770b\u5728\u4f60\u54e5\u54e5\u7684\u4efd\u4e0a\uff0c\u4e0d\u60f3\u4e0e\u4f60\u4e3a\u96be\u3002\u4f46\u4f60\u4e5f\u505a\u5f97\u592a\u8fc7\u4efd\u4e86\uff0c\u4f60\u5077\u4e86\u7384\u94c1\uff0c\u5927\u95f9 \u4e86\u6211\u4eec\u7684\u6d77\u7802\u5e2e\uff0c\u4f60\u773c\u775b\u91cc\u8fd8\u6709\u6211\u5417\uff1f\u73b0\u5728\u53ea\u80fd\u6709\u4e24\u6761\u8def\u4efb\u4f60\u9009\u62e9\uff0c\u4e00\u6761\u662f\u4f60\u81ea\u52a8\u8ddf\u5341\u4e09\u5a18 \u56de\u53bb\uff0c\u8fd9\u5c0f\u5b50\u5c31\u4e0d\u5fc5\u7ba1\u4e86\u3002\u53e6\u4e00\u6761\uff0c\u5982\u679c\u4f60\u6267\u610f\u4e0d\u4ece\uff0c\u5b9a\u8981\u548c\u6211\u4eec\u4f5c\u5bf9\u7684\u8bdd\uff0c\u90a3\u4e48\uff0c\u5bf9\u4e0d \u4f4f\uff0c\u6211\u4e5f\u53ea\u597d\u66ff\u4f60\u7684\u54e5\u54e5\u7ba1\u6559\u4f60\u4e86\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6c99\u5e2e\u4e3b\uff0c\u7384\u94c1\u662f\u6211\u53f2\u5bb6\u7684\uff0c\u6211\u62ff\u8d70\u81ea\u5bb6\u7684\u4e1c\u897f\u7109\u80fd\u8bf4\u662f\u5077\u76d7\uff1f\u9664\u975e\u4f60\u60f3\u5360 \u4e3a\u5df1\u6709\uff0c\u5426\u5219\u4f60\u8fd8\u5e94\u8be5\u591a\u8c22\u6211\u5462\u3002\u6211\u81ea\u5df1\u62ff\u56de\uff0c\u7701\u4f60\u6d3e\u4eba\u9001\u53bb\uff0c\u8fd9\u8fd8\u4e0d\u597d\u5417\uff1f\u201d \u3000\u3000\u6c99\u5343\u5cf0\u4f5c\u8d3c\u5fc3\u865a\uff0c\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u4e2a\u91ce\u4e2b\u5934\uff0c\u4f60\u54e5\u54e5\u66ff\u4f60\u5b89\u6392\u4e86\u4eb2\u4e8b\uff0c\u4f60\u5374\u8ff7\u604b \u8fd9\u4e2a\u5c0f\u5b50\uff01\u6211\u548c\u4f60\u7684\u54e5\u54e5\u662f\u516b\u62dc\u4e4b\u4ea4\uff0c\u6211\u5c31\u53ef\u4ee5\u66ff\u4f60\u7684\u54e5\u54e5\u6559\u8bad\u4f60\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5e94\u9053\uff1a\u201c\u51ed\u4f60\u8fd9\u6837\u7684\u8349\u5305\uff0c\u4e5f\u914d\u6559\u8bad\u522b\u4eba\uff1f\u54fc\uff0c\u627e\u5012\u662f\u5e94\u8be5\u6559\u8bad\u6559\u8bad\u4f60\u5462\uff01\u201d\u624b \u4e2d\u7384\u94c1\u4e00\u6643\uff0c\u4f5c\u52bf\u5c31\u5411\u6c99\u5343\u5cf0\u7838\u6765\uff0c\u6c99\u5343\u5cf0\u614c\u5fd9\u540e\u9000\uff0c\u51b7\u4e0d\u53ca\u9632\uff0c\u7ed9\u53f2\u7ea2\u82f1\u201c\u5530\u201d\u7684\u4e00\u97ad\uff0c \u9976\u662f\u4ed6\u8eb2\u95ea\u5f97\u5feb\uff0c\u97ad\u68a2\u5df2\u662f\u4ece\u4ed6\u7684\u80a9\u5934\u626b\u8fc7\uff0c\u6253\u88c2\u4e86\u4ed6\u7684\u8863\u88f3\u3002\u91d1\u9010\u6d41\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a \u201c\u6709\u7406\u6253\u5f97\u592a\u516c\uff0c\u4f55\u51b5\u4f60\u53ea\u662f\u4ed6\u54e5\u54e5\u7684\u628a\u5144\u3002\u201d \u3000\u3000\u5927\u7b11\u58f0\u4e2d\uff0c\u77f3\u624b\u957f\u5251\u53c8\u5df2\u5411\u6587\u9053\u5e84\u523a\u5230\uff0c\u6587\u9053\u5e84\u53cc\u638c\u5982\u73af\uff0c\u4ee5\u4e09\u8c61\u795e\u529f\u4f7f\u51fa\u201c\u4e09\u73af\u5957 \u6708\u201d\u7684\u62db\u6570\uff0c\u5316\u89e3\u4e86\u91d1\u9010\u6d41\u7684\u4e00\u62db\uff0c\u53f2\u7ea2\u82f1\u97ad\u6cd5\u5feb\u6781\uff0c\u4e00\u4e2a\u201c\u56de\u98ce\u626b\u67f3\u201d\uff0c\u53cd\u624b\u4fbf\u662f\u4e00\u97ad\u3002 \u6587\u9053\u5e84\u7684\u201c\u4e09\u8c61\u795e\u529f\u201d\u4f59\u529b\u672a\u8870\uff0c\u53f2\u7ea2\u82f1\u7684\u94f6\u97ad\u6253\u5230\u4e86\u4ed6\u53cc\u638c\u73af\u8f6c\u6240\u6fc0\u8d77\u7684\u6c14\u6d41\u4e4b\u4e2d\uff0c\u94f6\u97ad \u7adf\u7136\u7ed9\u8361\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6b64\u4eba\u5012\u662f\u4e00\u4e2a\u52b2\u654c\uff0c\u6bd4\u6c99\u5343\u5cf0\u5389\u5bb3\u591a\u4e86\u3002\u602a\u4e0d\u5f97\u91d1\u9010\u6d41 \u6218\u4ed6\u4eec\u4e0d\u4e0b\u3002\u770b\u6765\u4eca\u65e5\u4e4b\u4e8b\uff0c\u53ea\u6709\u4e09\u5341\u516d\u7740\u8d70\u4e3a\u4e0a\u7740\u4e86\uff01\u201d\u6587\u9053\u5e84\u4e5f\u662f\u6697\u6697\u5403\u60ca\uff1a\u201c\u602a\u4e0d\u5f97 \u53f2\u767d\u90fd\u7a9c\u5f97\u8fd9\u4e48\u5feb\uff0c\u4e0d\u8fc7\u51e0\u5e74\uff0c\u5df2\u662f\u540d\u6ee1\u6c5f\u6e56\u3002\u59b9\u59b9\u4e5f\u8fd9\u4e48\u4e86\u5f97\uff0c\u54e5\u54e5\u7684\u672c\u9886\u53ef\u60f3\u800c\u77e5\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u5fc3\u5ff5\u672a\u5df2\uff0c\u8463\u5341\u4e09\u5a18\u53c8\u5df2\u4e0a\u6765\uff0c\u8f6f\u97ad\u4e00\u626c\uff0c\u76ae\u7b11\u8089\u4e0d\u7b11\u5730\u8bf4\u9053\uff1a\u201c\u597d\u59b9\u5b50\uff0c\u4f60\u5f53 \u771f\u7518\u5fc3\u4e3a\u4e86\u8fd9\u4e2a\u5c0f\u5b50\uff0c\u4e0d\u60dc\u4e0e\u4f60\u7684\u54e5\u54e5\u7ffb\u8138\u4e86\uff1f\u5509\uff0c\u90a3\u4e5f\u6ca1\u6709\u529e\u6cd5\uff0c\u6211\u53ea\u597d\u5c06\u4f60\u4eec\u9001\u56de\u5e2e \u4e2d\uff0c\u8ba9\u4f60\u7684\u54e5\u54e5\u4f5c\u4e3b\u4e86\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5373\u662f\u8981\u628a\u4ed6\u4eec\u6d3b\u64d2\uff0c\u4ea4\u7ed9\u53f2\u767d\u90fd\u5904\u7f6e\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u6c14\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u53c8\u7f9e\u53c8\u607c\uff0c\u53ef\u662f\u5982\u4eca\u4ed6\u4eec\u5728\u56db\u5927\u9ad8\u624b\u56f4\u653b\u4e4b\u4e0b\uff0c\u53f2\u7ea2\u82f1\u4ea6\u5df2\u65e0\u5fc3 \u548c\u8463\u5341\u4e09\u5a18\u5435\u67b6\u3002\u53f2\u7ea2\u82f1\u4e0d\u7406\u5979\u51b7\u8bbd\u70ed\u5632\uff0c\u5168\u526f\u7cbe\u795e\uff0c\u53ea\u987e\u4e0e\u91d1\u9010\u6d41\u5e76\u80a9\u62b5\u654c\u3002 \u3000\u3000\u5e78\u4e8f\u4ed6\u4eec\u90fd\u662f\u6709\u6240\u987e\u5fcc\uff0c\u4e0d\u6562\u4f24\u53ca\u53f2\u7ea2\u82f1\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u8fde\u5e26\u91d1\u9010\u6d41\u4e5f\u6cbe\u4e86\u70b9\u5149\uff0c\u53f2\u7ea2\u82f1 \u4e0e\u4ed6\u9760\u5f97\u5f88\u7d27\uff0c\u4ed6\u4eec\u4e0d\u6562\u4f24\u53f2\u7ea2\u82f1\uff0c\u8bb8\u591a\u6740\u624b\u4e0d\u6562\u4f7f\u7528\u3002\u4e0d\u8fc7\uff0c\u91d1\u3001\u53f2\u4e8c\u4eba\u6bd5\u7adf\u662f\u4ee5\u4e8c\u654c \u56db\uff0c\u5b9e\u529b\u76f8\u5dee\u9887\u8fdc\uff0c\u4e45\u6218\u4e0b\u53bb\uff0c\u4e0d\u53d7\u4f24\u4e5f\u4f1a\u529b\u7aed\u906d\u64d2\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u65e9\u5df2\u6253\u5b9a\u4e86\u201c\u4e09\u5341\u516d\u7740\u8d70\u4e3a\u4e0a\u7740\u201d\u7684\u4e3b\u610f\uff0c\u4e00\u770b\u65f6\u673a\u5df2\u5230\uff0c\u53eb\u58f0\u201c\u626f\u547c\uff01\u201d\u500f\u5730 \u4e00\u97ad\u903c\u9000\u4e86\u5706\u6d77\uff0c\u4ece\u7f3a\u53e3\u51b2\u51fa\u3002\u6587\u9053\u5e84\u4e0e\u6c99\u5343\u5cf0\u6b63\u5728\u5bf9\u4ed8\u91d1\u9010\u6d41\u7684\u4e00\u62db\uff0c\u65e0\u6687\u987e\u5979\uff0c\u8463\u5341\u4e09 \u5a18\u4e00\u4eba\u62e6\u5979\u4e0d\u4f4f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8f7b\u529f\u5353\u7edd\uff0c\u53f2\u7ea2\u82f1\u5df2\u7ecf\u51b2\u5f00\u4e86\u7f3a\u53e3\uff0c\u4ed6\u8981\u9003\u8dd1\uff0c\u66f4\u662f\u5bb9\u6613\u3002\u4e0d\u8fc7\uff0c\u91d1\u9010\u6d41\u867d\u7136\u8ddf \u7740\u5979\u8dd1\uff0c\u5fc3\u91cc\u5374\u662f\u4e0d\u5927\u613f\u610f\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u6709\u5feb\u9a6c\uff0c\u65f6\u95f4\u4e00\u957f\uff0c\u603b\u4f1a\u7ed9\u4ed6\u4eec\u8ffd\u4e0a\uff0c\u90a3\u4e0d\u662f \u767d\u8017\u6c14\u529b\uff1f\u4e0e\u5176\u7ed9\u4ed6\u4eec\u5632\u7b11\uff0c\u4e0d\u5982\u5728\u8fd9\u91cc\u62fc\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u8fd8\u663e\u5f97\u662f\u4e2a\u82f1\u96c4\u597d\u6c49\uff01\u201d\u4f46\u6b64\u9645\u53f2 \u7ea2\u82f1\u5df2\u8dd1\u5728\u524d\u5934\uff0c\u91d1\u9010\u6d41\u4e0d\u53ef\u80fd\u4e0e\u5979\u4ed4\u7ec6\u5546\u91cf\uff0c\u4e5f\u53ea\u597d\u8ddf\u7740\u5979\u8dd1\u4e86\u3002 \u3000\u3000\u679c\u7136\u4ed6\u4eec\u4e00\u8dd1\uff0c\u6587\u9053\u5e84\u4e00\u4f17\u5c31\u9a91\u4e86\u9a6c\u6765\u8ffd\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u7684\u9a6c\u6700\u5feb\uff0c\u770b\u770b\u8ffd\u5f97\u8fd1\u4e86\uff0c\u5728\u9a6c\u80cc\u4e0a\u4e00\u626c\u624b\uff0c\u4fbf\u662f\u4e09\u67c4\u94f6\u68ad\uff0c\u5411\u91d1\u9010\u6d41\u98de\u53bb\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u7684\u6697\u5668\u53e6\u6709\u4e00\u529f\uff0c\u5979\u53d1\u7684\u4e43\u662f\u201c\u94f6\u68ad\u201d\uff0c\u94f6\u68ad\u4e2d\u7a7a\uff0c\u98de\u4e86\u51fa\u53bb\u5e26\u7740\u5f3a\u529b\u7684\u5578 \u58f0\uff0c\u548c\u7eff\u6797\u4e2d\u5e38\u7528\u7684\u201c\u54cd\u7bad\u201d\u5c5e\u4e8e\u540c\u4e00\u7c7b\u7684\u6697\u5668\u3002\u4e0d\u8fc7\u94f6\u68ad\u4efd\u91cf\u8f83\u91cd\uff0c\u68ad\u89d2\u950b\u5229\uff0c\u8179\u5185\u8fd8\u85cf \u6709\u4e5d\u679a\u6bd2\u9488\uff0c\u5018\u82e5\u5bf9\u65b9\u7528\u5200\u5251\u524a\u65ad\u94f6\u68ad\uff0c\u7740\u9488\u4fbf\u4f1a\u98de\u51fa\u4f24\u4eba\uff0c\u6bd4\u4e4b\u54cd\u7bad\uff0c\u90a3\u662f\u5389\u5bb3\u5f97\u591a\u4e86\uff0c \u51e1\u662f\u80fd\u591f\u4f7f\u7528\u53d1\u51fa\u58f0\u54cd\u7684\u6697\u5668\u7684\u4eba\uff0c\u4e00\u5b9a\u662f\u6253\u5f97\u53c8\u51c6\u53c8\u5feb\u7684\u9ad8\u624b\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u4e00\u626c\u624b\uff0c\u4e09\u67c4\u53d1\u51fa\u5f3a\u70c8\u5578\u58f0\u7684\u94f6\u68ad\u3001\u4ece\u4e09\u4e2a\u4e0d\u540c\u7684\u65b9\u4f4d\uff0c\u5206\u6253\u91d1\u9010\u6d41\u4e0a\u76d8\u989d\u89d2 \u7684\u201c\u592a\u9633\u7a74\u201d\uff0c\u4e2d\u76d8\u80f8\u53e3\u7684\u201c\u7487\u7391\u7a74\u201d\uff0c\u4e8c\u76d8\u53f3\u819d\u7684\u201c\u73af\u8df3\u7a74\u201d\u3002\u8fd9\u4e09\u4e2a\u90e8\u4f4d\u5e76\u975e\u8fde\u6210\u4e00\u6761 \u76f4\u7ebf\u800c\u662f\u5e03\u6210\u4e00\u4e2a\u4e0d\u89c4\u5219\u7684\u4e09\u89d2\u5f62\u7684\uff0c\u8463\u5341\u4e09\u5a18\u7684\u4e00\u624b\u4e09\u6697\u5668\uff0c\u5c45\u7136\u80fd\u591f\u6253\u8fd9\u4e09\u4e2a\u7a74\u9053\uff0c\u624b \u6cd5\u4e4b\u5de7\uff0c\u81ea\u662f\u7b2c\u4e00\u6d41\u7684\u6697\u5668\u529f\u592b\u3002 \u3000\u3000\u53ef\u662f\u8463\u5341\u4e09\u5a18\u5374\u60f3\u4e0d\u5230\uff0c\u91d1\u9010\u6d41\u62b5\u6321\u6697\u5668\u529f\u592b\u66f4\u662f\u7cbe\u5999\u7edd\u4f26\u3002\u5979\u4ee5\u4e3a\u91d1\u9010\u6d41\u4e00\u624b\u63d0\u7740\u7384 \u94c1\uff0c\u4e00\u624b\u63d0\u7740\u957f\u5251\uff0c\u4ee5\u91d1\u9010\u6d41\u7684\u5185\u529b\u4e4b\u5f3a\uff0c\u4e0d\u8bc6\u5979\u8fd9\u6697\u5668\u7684\u529f\u80fd\uff0c\u5b9a\u4f1a\u901e\u80fd\u6253\u843d\u5979\u7684\u6697\u5668\u3002 \u90a3\u65f6\u4e0d\u8bba\u7528\u7384\u94c1\u6765\u7838\u6216\u7528\u957f\u5251\u6765\u524a\uff0c\u94f6\u68ad\u4e00\u65ad\uff0c\u6bd2\u9488\u5c31\u4f1a\u5c04\u51fa\u4f24\u4ed6\u3002 \u3000\u3000\u91d1\u9010\u6d41\u563b\u563b\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u7f3a\u94f6\u5b50\u7528\uff0c\u539a\u793c\u4e0d\u6562\u63a5\u53d7\uff0c\u539f\u7269\u5949\u8fd8\uff01\u201d\u957f\u5251\u4e00\u62db\u201c\u4e09\u8f6c\u6cd5 \u8f6e\u201d\uff0c\u6296\u8d77\u4e09\u6735\u5251\u82b1\uff0c\u4e09\u67c4\u94f6\u68ad\u90fd\u98de\u4e86\u56de\u53bb\u3002\u4ed6\u7528\u7684\u52b2\u628a\u63e1\u6070\u5230\u597d\uff0c\u78d5\u56de\u4e09\u67c4\u94f6\u68ad\uff0c\u6bd2\u9488\u672a \u66fe\u5c04\u51fa\u3002 \u3000\u3000\u4ed6\u4e00\u5251\u78d5\u56de\u4e09\u67c4\u94f6\u68ad\u5df2\u662f\u96be\u80fd\uff0c\u66f4\u5999\u7684\u662f\uff1a\u8fd9\u4e09\u67c4\u98de\u56de\u6765\u7684\u94f6\u68ad\uff0c\u5feb\u6162\u53c8\u662f\u5404\u5404\u4e0d\u540c\u3002 \u8463\u5341\u4e09\u5a18\u628a\u8f6f\u97ad\u4e00\u6325\uff0c\u51c6\u5907\u5377\u56de\u94f6\u68ad\uff0c\u54ea\u77e5\u7b2c\u4e8c\u67c4\u94f6\u68ad\u5374\u662f\u540e\u53d1\u5148\u81f3\uff0c\u8463\u5341\u4e09\u5a18\u53ea\u6ce8\u610f\u7b2c\u4e00 \u67c4\u94f6\u68ad\uff0c\u9669\u4e9b\u7ed9\u7b2c\u4e8c\u67c4\u94f6\u68ad\u4f24\u7740\uff0c\u5e78\u800c\u8463\u5341\u4e09\u5a18\u9a6c\u4e0a\u7684\u529f\u592b\u4e86\u5f97\uff0c\u4e00\u4e2a\u201c\u9563\u91cc\u85cf\u8eab\u201d\uff0c\u53cd\u624b \u56de\u97ad\uff0c\u4f9d\u7136\u628a\u7b2c\u4e8c\u67c4\u94f6\u68ad\u5377\u4e86\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u7b2c\u4e00\u67c4\u548c\u7b2c\u4e09\u67c4\u94f6\u68ad\uff0c\u8463\u5341\u4e09\u5a18\u5c31\u65e0\u6cd5\u517c\u987e\u4e86\uff0c\u8fd9\u4e24\u67c4\u94f6\u68ad\u5206\u522b\u5411\u5706 \u6d77\u548c\u6c99\u5343\u5cf0\u98de\u53bb\u3002\u91d1\u9010\u6d41\u77e5\u9053\u6587\u9053\u5e84\u7684\u6b66\u529f\u6700\u5f3a\uff0c\u6253\u56de\u53bb\u7684\u94f6\u68ad\u820d\u4e86\u6700\u5f3a\u7684\u800c\u53d6\u4e24\u4e2a\u8f83\u5f31 \u7684\u3002 \u3000\u3000\u5706\u6d77\u77e5\u9053\u8463\u5341\u4e09\u5a18\u7684\u94f6\u68ad\u662f\u85cf\u6709\u6bd2\u9488\u7684\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u614c\u5fd9\u6eda\u4e0b\u9a6c\u6765\u3002\u9976\u662f\u4ed6\u6eda\u5f97\u5feb\uff0c\u90a3 \u67c4\u94f6\u68ad\u4e5f\u51e0\u4e4e\u662f\u8d34\u7740\u4ed6\u7684\u5934\u76ae\u98de\u8fc7\uff01\u5706\u6d77\u907f\u8fd9\u6697\u5668\uff0c\u903c\u5f97\u5728\u5730\u4e0b\u6253\u6eda\u3001\u5f53\u771f\u662f\u72fc\u72c8\u4e4b\u6781\uff01 \u3000\u3000\u4f46\u6bd4\u8d77\u6c99\u5343\u5cf0\u6765\uff0c\u5706\u6d77\u5403\u7684\u8fd9\u70b9\u5c0f\u4e8f\u53c8\u7b97\u4e0d\u5f97\u4ec0\u4e48\u4e86\u3002\u6c99\u5343\u5cf0\u4e0d\u77e5\u5389\u5bb3\uff0c\u4ed7\u7740\u94c1\u7802\u638c\u517c \u6bd2\u7802\u638c\u7684\u529f\u592b\uff0c\u4e00\u638c\u62cd\u53bb\u3002\u8463\u5341\u4e09\u5a18\u614c\u5fd9\u53eb\u9053\uff1a\u201c\u4e0d\u53ef\uff01\u201d\u53ef\u662f\u5df2\u7ecf\u8fdf\u4e86\uff0c\u6c99\u5343\u5cf0\u7684\u638c\u529b\u6709 \u5f00\u780c\u88c2\u77f3\u4e4b\u80fd\uff0c\u94f6\u68ad\u88c2\u5f00\uff0c\u6bd2\u9488\u4e1a\u5df2\u5c04\u51fa\u3002 \u3000\u3000\u6587\u9053\u5e84\u8fde\u5fd9\u628a\u624b\u4e00\u6325\uff0c\u53d1\u51fa\u5288\u7a7a\u638c\u529b\uff0c\u66ff\u6c99\u5343\u5cf0\u626b\u8361\u6bd2\u9488\uff01\u4f46\u4ed6\u4eec\u4e24\u9a91\u9a6c\u4e00\u5148\u4e00\u540e\uff0c\u8ddd \u79bb\u5728\u4e94\u4e08\u5f00\u5916\uff0c\u6587\u9053\u5e84\u7684\u638c\u529b\u672a\u80fd\u6070\u5230\u597d\u5904\u7684\u628a\u6bd2\u9488\u5c3d\u90fd\u6253\u843d\uff0c\u7ed3\u679c\u6c99\u5343\u5cf0\u8fd8\u662f\u4e2d\u4e86\u4e00\u652f\uff0c \u4ed6\u7684\u90a3\u5339\u5750\u9a91\uff0c\u4e5f\u7ed9\u6587\u9053\u5e84\u7684\u638c\u529b\u9707\u5f97\u9a6c\u5931\u524d\u8e44\uff0c\u628a\u4ed6\u8e22\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u56db\u4eba\u4e4b\u4e2d\uff0c\u4e24\u4eba\u843d\u9a6c\uff0c\u6c99\u5343\u5cf0\u517c\u4e14\u53d7\u4f24\uff1b\u5176\u4ed6\u4e8c\u4eba\uff0c\u8463\u5341\u4e09\u5a18\u867d\u7136\u672a\u66fe\u843d\u9a6c\uff0c\u4ea6\u662f\u60ca\u9b42 \u4e0d\u5b9a\u3002\u53ea\u6709\u6587\u9053\u5e84\u8fd8\u80fd\u591f\u7aef\u7aef\u6b63\u6b63\u7684\u5750\u5728\u9a6c\u4e0a\u3002\u8463\u5341\u4e09\u5a18\u60f3\u4e0d\u5230\u56e0\u5979\u7684\u6697\u5668\u4e00\u6253\uff0c\u53cd\u800c\u7ed9\u540c \u4f34\u60f9\u6765\u4e86\u707e\u6b83\uff0c\u4e0d\u7981\u53c8\u7f9e\u53c8\u607c\uff0c\u540c\u65f6\u53c8\u662f\u4e0d\u7981\u6697\u6697\u80c6\u5bd2\u3002\u5728\u8fd9\u6837\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c\u662f\u7ee7\u7eed\u8ffd\u654c \u5462\uff0c\u8fd8\u662f\u5148\u89e3\u52a9\u5343\u5cf0\u4e4b\u6bd2\uff1f\u4ee5\u6c99\u5343\u5cf0\u7684\u529f\u529b\uff0c\u4e00\u652f\u6bd2\u9488\u662f\u8981\u4e0d\u4e86\u4ed6\u7684\u6027\u547d\u7684\uff0c\u4f46\u82e5\u8fc7\u4e86\u4e00\u4e2a \u65f6\u8fb0\uff0c\u4e0d\u4e88\u89e3\u6551\u7684\u8bdd\uff0c\u4e5f\u6709\u6b8b\u5e9f\u7684\u5371\u9669\u3002 \u3000\u3000\u6b63\u6240\u8c13\u201c\u798f\u65e0\u53cc\u81f3\uff0c\u7978\u4e0d\u5355\u884c\u3002\u201d\u8463\u5341\u4e09\u5a18\u7684\u6697\u5668\u521a\u521a\u60f9\u51fa\u201c\u7978\u201d\u6765\uff0c\u5fc3\u4e2d\u6b63\u5728\u6c14\u607c\u4ea4 \u52a0\uff0c\u53c8\u6b63\u5728\u8e0c\u8e87\u672a\u51b3\u4e4b\u9645\uff0c\u53ea\u542c\u5f97\u53f2\u7ea2\u82f1\u53c8\u5df2\u5728\u51b7\u7b11\u9053\uff1a\u201c\u8463\u5341\u4e09\u5a18\uff0c\u4f60\u6562\u7528\u6697\u5668\u6253\u6211\uff01 \u597d\uff0c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u4f60\u4e5f\u63a5\u63a5\u6211\u7684\uff01\u201d \u3000\u3000\u5176\u5b9e\u8463\u5341\u4e09\u5a18\u7684\u6697\u5668\u6253\u7684\u662f\u91d1\u9010\u6d41\uff0c\u7ea2\u82f1\u63fd\u5230\u81ea\u5df1\u7684\u8eab\u4e0a\uff0c\u4e0d\u8fc7\u662f\u501f\u9898\u53d1\u6325\u800c\u5df2\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u65e0\u6687\u5206\u8fa9\uff0c\u53f2\u7ea2\u82f1\u4e5f\u4e0d\u5bb9\u5979\u5206\u8fa9\uff0c\u53ea\u542c\u5f97\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u6697\u5668\u5df2\u662f\u53d1\u4e86\u51fa \u6765\uff0c\u4e00\u56e2\u6d53\u5bc6\u7684\u70df\u96fe\uff0c\u767b\u65f6\u5728\u4ed6\u4eec\u9762\u524d\u6269\u5c55\uff01 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u6697\u5668\u4e43\u662f\u4e00\u4e2a\u7403\u72b6\u7269\u4f53\uff0c\u6253\u4e86\u51fa\u6765\uff0c\u4fbf\u5373\u7206\u88c2\uff0c\u53d1\u51fa\u70df\u96fe\uff0c\u5929\u9b54\u6559\u7956\u5e08\u5389\u80dc\u7537 \u5f53\u5e74\u6709\u4e00\u79cd\u6700\u5389\u5bb3\u7684\u6697\u5668\u540d\u4e3a\u201c\u6bd2\u96fe\u91d1\u9488\u70c8\u7130\u201d\uff0c\u516d\u5408\u5e2e\u5e2e\u4e3b\u53f2\u767d\u90fd\u4e0d\u77e5\u5982\u4f55\u5f97\u5230\u5236\u9020\u8fd9\u79cd \u6697\u5668\u7684\u65b9\u6cd5\u3002\u4e0d\u8fc7\uff0c\u73b0\u5728\u53f2\u7ea2\u82f1\u6240\u53d1\u7684\u6697\u5668\uff0c\u53ea\u662f\u5f62\u4f3c\u800c\u5b9e\u975e\uff0c\u6ca1\u6709\u91d1\u9488\uff0c\u6ca1\u6709\u70c8\u7130\uff0c\u53ea\u6709 \u70df\u96fe\uff0c\u800c\u4e14\u90a3\u70df\u96fe\u4e5f\u662f\u6ca1\u6709\u6bd2\u7684\uff0c\u8fd9\u662f\u56e0\u4e3a\u53f2\u7ea2\u82f1\u4e0d\u613f\u4f7f\u7528\u592a\u8fc7\u6b79\u6bd2\u7684\u6697\u5668\u7684\u7f18\u6545\uff0c\u5979\u53ea\u662f \u5e0c\u671b\u5229\u7528\u70df\u96fe\u7684\u63a9\u76d6\u8131\u8eab\u3002 \u3000\u3000\u867d\u7136\u4e0d\u662f\u6bd2\u96fe\uff0c\u4f46\u8463\u5341\u4e09\u5a18\u5374\u4e0d\u77e5\u9053\u662f\u6709\u6bd2\u65e0\u6bd2\uff0c\u5979\u662f\u8bc6\u5f97\u201c\u6bd2\u96fe\u91d1\u9488\u70c8\u7130\u5f39\u201d\u7684\u5389\u5bb3 \u7684\uff0c\u8fde\u5fd9\u628a\u5706\u6d77\u62c9\u4e0a\u9a6c\u80cc\uff0c\u4fbf\u5373\u62e8\u8f6c\u9a6c\u5934\uff0c\u5411\u540e\u8dd1\u4e86\u3002\u6c99\u5343\u5cf0\u5df2\u4e2d\u6bd2\u9488\uff0c\u5f53\u7136\u66f4\u662f\u4e0d\u6562\u604b \u6218\u3002\u5269\u4e0b\u4e00\u4e2a\u6587\u9053\u5e84\u5b64\u638c\u96be\u9e23\uff0c\u4ed6\u53d1\u4e86\u4e24\u8bb0\u5288\u7a7a\u638c\uff0c\u70df\u96fe\u592a\u6d53\uff0c\u4e4d\u6563\u5373\u805a\uff0c\u6587\u9053\u5e84\u751f\u6015\u4e2d \u6bd2\uff0c\u5fc3\u91cc\u53d1\u614c\uff0c\u53ea\u5f97\u4e5f\u8ddf\u7740\u8dd1\u4e86\u3002 \u3000\u3000\u70df\u96fe\u8ff7\u6f2b\u4e2d\u91d1\u9010\u6d41\u4e5f\u8ffd\u5230\u4e86\u6797\u4e2d\uff0c\u4ed6\u597d\u50cf\u662f\u542c\u5f97\u53f2\u7ea2\u82f1\u5411\u90a3\u8fb9\u8dd1\u7684\uff0c\u53ef\u662f\u5f85\u5f97\u96fe\u6563\u5929 \u6e05\uff0c\u91d1\u9010\u6d41\u5b9a\u775b\u4e00\u770b\uff0c\u5df2\u662f\u4e0d\u89c1\u4e86\u53f2\u7ea2\u82f1\u7684\u8e2a\u5f71\u3002\u6b63\u662f\uff1a \u3000\u3000\u70df\u96fe\u5f25\u6f2b\u8ff7\u671b\u773c\uff0c\u4e0d\u77e5\u4f55\u5904\u89c5\u82b3\u8e2a\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u5341\u56db\u56de\u3000\u5f26\u7d22\u58f0\u4e2d\u6765\u6076\u5ba2\u3000\u5927\u660e\u6e56\u7554\u7ed3\u826f\u670b \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u5341\u56db\u56de\u3000\u5f26\u7d22\u58f0\u4e2d\u6765\u6076\u5ba2\u3000\u5927\u660e\u6e56\u7554\u7ed3\u826f\u670b \u3000\u3000\u91d1\u9010\u6d41\u5403\u4e86\u4e00\u60ca\uff0c\u5fd9\u7528\u4f20\u97f3\u5165\u5bc6\u7684\u5185\u529f\u53e0\u58f0\u547c\u5524\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u53f2\u59d1\u5a18\uff01\u201d\u7a7a\u6797\u5bc2\u5bc2\uff0c\u54ea \u91cc\u542c\u5f97\u5230\u53f2\u7ea2\u82f1\u7684\u56de\u7b54\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5728\u6811\u91cc\u627e\u4e0d\u7740\u53f2\u7ea2\u82f1\uff0c\u8d70\u4e86\u51fa\u6765\uff0c\u53ea\u89c1\u9762\u524d\u6b63\u662f\u4e09\u5c94\u8def\u53e3\uff0c\u4e5f\u4e0d\u77e5\u53f2\u7ea2\u82f1\u8d70\u7684\u662f \u54ea\u4e00\u6761\u8def\uff1f\u91d1\u9010\u6d41\u60d8\u7136\u82e5\u5931\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u770b\u6765\u5979\u662f\u6709\u5fc3\u907f\u6211\u7684\u4e86\u3002\u5979\u6709\u5fc3\u907f\u6211\uff0c\u6211\u662f\u6ca1\u6cd5 \u627e\u5979\u7684\u4e86\u3002\u201d\u8981\u77e5\u9053\u53f2\u7ea2\u82f1\u7684\u8f7b\u529f\u4e0e\u91d1\u9010\u6d41\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u5982\u4eca\u91d1\u9010\u6d41\u624b\u4e0a\u62ff\u7740\u4e00\u5757\u7384\u94c1\uff0c\u7109\u80fd \u8ffd\u5f97\u4e0a\u5979\uff1f\u4f55\u51b5\u8fd8\u4e0d\u77e5\u9053\u5979\u8d70\u7684\u662f\u54ea\u4e00\u6761\u8def\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u53e3\u95f7\u6c14\u65e0\u5904\u53d1\u6cc4\uff0c\u6068\u6068\u9a82\u9053\uff1a\u201c\u90fd\u662f\u90a3\u6591\u738b\u516b\u7f94\u5b50\u80e1\u8bf4\u516b\u9053\uff0c\u628a\u53f2\u59d1\u5a18\u7ed9\u6c14\u8d70 \u4e86\u3002\u201d\u6068\u4e0d\u5f97\u8dd1\u56de\u6d77\u7802\u5e2e\u53bb\u628a\u6c99\u5343\u5cf0\u548c\u8463\u5341\u4e09\u5a18\u75db\u6253\u4e00\u987f\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u6c99\u3001\u8463\u4e8c\u4eba\u90fd\u5403\u4e86 \u4ed6\u7684\u5927\u4e8f\uff0c\u6c99\u5343\u5cf0\u4e2d\u4e86\u6bd2\u9488\uff0c\u5403\u4e8f\u5c24\u5927\uff0c\u201c\u597d\u6c49\u4e0d\u6253\u75c5\u592b\uff0c\u4ed6\u4eec\u4e5f\u7b97\u662f\u53d7\u4e86\u5e94\u5f97\u7684\u60e9\u6212 \u4e86\u3002\u201d\u8fd9\u4e48\u4e00\u60f3\uff0c\u5fc3\u4e2d\u4e4b\u6c14\u624d\u6e10\u6e10\u5e73\u4e86\u4e0b\u6765\uff0c\u8981\u4e0d\u7136\u4f9d\u7740\u91d1\u9010\u6d41\u7684\u6027\u60c5\uff0c\u5373\u4f7f\u660e\u77e5\u4f17\u5be1\u4e0d \u654c\uff0c\u4e5f\u4f1a\u8dd1\u56de\u53bb\u5927\u95f9\u4e00\u573a\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7684\u6012\u6c14\u5e73\u606f\u4e4b\u540e\uff0c\u51b7\u9759\u4e00\u60f3\uff0c\u53f2\u7ea2\u82f1\u867d\u662f\u907f\u5f00\u4e86\u4ed6\uff0c\u4f46\u5979\u521a\u624d\u4e0d\u987e\u90a3\u4e9b\u4eba\u7684\u51b7\u5632 \u70ed\u8bbd\u4e0e\u81ea\u5df1\u8054\u624b\u5bf9\u654c\uff0c\u663e\u7136\u5979\u5bf9\u81ea\u5df1\u662f\u6709\u4e86\u4e00\u4efd\u9887\u4e3a\u6df1\u539a\u7684\u53cb\u8c0a\u3002\u53c8\u60f3\u5230\u5979\u521a\u624d\u548c\u81ea\u5df1\u751f \u6c14\uff0c\u4e3a\u7684\u6b63\u662f\u56e0\u4e3a\u81ea\u5df1\u8bef\u4f1a\u4e86\u5979\u548c\u674e\u6566\u7684\u4ea4\u60c5\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u4ece\u8fd9\u4ef6\u4e8b\uff0c\u5df2\u7ecf\u53ef\u4ee5\u6e05\u695a\u7684\u8868\u660e \u4e86\u5979\u548c\u674e\u6566\u4e0d\u662f\u60c5\u4fa3\u4e86\u3002\u90a3\u4e48\u8fd9\u4e00\u6b21\u7684\u4f1a\u9762\u4e5f\u5e76\u975e\u6beb\u65e0\u6240\u83b7\uff0c\u6700\u5c11\u5df2\u7ecf\u62d4\u6e05\u4e86\u4ed6\u591a\u65e5\u6765\u7b3c\u7f69 \u5728\u5fc3\u91cc\u7684\u7591\u4e91\u3002\u91d1\u9010\u6d41\u60f3\u5230\u4e86\u8fd9\u5c42\uff0c\u4e0d\u4f46\u6012\u6c14\u5e73\u606f\uff0c\u5fc3\u4e2d\u4e5f\u8f6c\u800c\u611f\u5230\u4e00\u9635\u751c\u610f\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u60f3\u9053\uff1a\u201c\u5979\u53ea\u662f\u56e0\u4e3a\u7ed9\u90a3\u73ed\u738b\u516b\u7f94\u5b50\u80e1\u8bf4\u4e00\u987f\uff0c\u8bf4\u5f97\u96be\u4ee5\u4e3a\u60c5\uff0c\u8fd9\u624d\u907f\u5f00\u6211\u7684\u3002 \u5979\u662f\u4e2a\u723d\u6717\u7684\u59d1\u5a18\uff0c\u8fc7\u4e86\u4e00\u4e9b\u65e5\u5b50\uff0c\u81ea\u7136\u4e0d\u4f1a\u4ecb\u610f\u3002\u65e5\u540e\u76f8\u89c1\uff0c\u6211\u5148\u7ed9\u5979\u8d4c\u4e2a\u7f6a\u4fbf\u662f\u3002\u73b0\u5728 \u4e14\u5148\u8fdb\u4eac\u57ce\u529e\u6b63\u7ecf\u4e8b\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6e21\u6c5f\u5317\u4e0a\uff0c\u7ecf\u8fc7\u5f90\u5dde\u8fdb\u5165\u5c71\u4e1c\uff0c\u8fd9\u4e00\u65e5\u5230\u4e86\u6d4e\u5357\u3002\u6d4e\u5357\u662f\u4e2a\u201c\u5bb6\u5bb6\u6cc9\u6c34\uff0c\u6237\u6237\u5782 \u6768\u201d\u7684\u98ce\u666f\u5e7d\u7f8e\u7684\u57ce\u5e02\uff0c\u91d1\u9010\u6d41\u4e00\u7b97\u65e5\u671f\uff0c\u5c3d\u53ef\u4ee5\u8d74\u5f97\u4e0a\u8428\u798f\u9f0e\u7684\u5bff\u8fb0\uff0c\u8fd8\u53ef\u4ee5\u6709\u4e94\u516d\u5929\u7684 \u4f59\u88d5\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5230\u4e86\u6d4e\u5357\uff0c\u5927\u660e\u6e56\u662f\u4e0d\u53ef\u4ee5\u4e0d\u53bb\u4e00\u6e38\u7684\u3002\u201d\u4e8e\u662f\u627e\u4e2a\u5ba2\u5e97\uff0c\u6b47\u4e86\u4e00\u5bb5\uff0c \u7b2c\u4e8c\u5927\u4e00\u65e9\uff0c\u5403\u4e86\u65e9\u70b9\uff0c\u4fbf\u53bb\u901b\u5927\u660e\u6e56\u3002 \u3000\u3000\u5927\u660e\u6e56\u5728\u57ce\u7684\u5357\u8fb9\uff0c\u5343\u4f5b\u5c71\u4e0b\u3002\u91d1\u9010\u6d41\u8d70\u5230\u9e3d\u534e\u6865\u8fb9\uff0c\u96c7\u4e86\u4e00\u53ea\u5c0f\u8239\uff0c\u5411\u5bf9\u9762\u5212\u53bb\u3002\u6e56 \u5e73\u5982\u955c\uff0c\u5343\u4f5b\u5c71\u7684\u68b5\u5b57\u50e7\u505a\u82cd\u677e\u7fe0\u67cf\uff0c\u9ad8\u4e0b\u76f8\u95f4\uff0c\u5012\u5f71\u6e56\u5fc3\u3002\u53c8\u6709\u90a3\u6df1\u79cb\u7684\u6ee1\u5c71\u7ea2\u53f6\uff0c\u5728\u671d \u9633\u4e0b\u5c06\u6e56\u6c34\u6620\u5f97\u91d1\u78a7\uff0c\u8d5b\u8fc7\u5de5\u7b14\u753b\u56fe\u3002\u7aef\u7684\u662f\u6e56\u5149\u5c71\u8272\uff0c\u7f8e\u4e0d\u80dc\u6536\u3002\u8d5e\u53f9\u58f0\u4e2d\uff0c\u91d1\u9010\u6d41\u60a0\u95f2 \u81ea\u5728\u7684\u501a\u8237\u72ec\u5578\uff0c\u8d4f\u89c8\u5c71\u8272\u6e56\u5149\uff0c\u4e50\u4e5f\u65e0\u6daf\u3002\u7f8e\u4e2d\u4e0d\u8db3\u7684\uff0c\u53ea\u662f\u5411\u8237\u4e0b\u671b\uff0c\u6e56\u4e2d\u53ea\u6709\u4ed6\u7684\u5b64 \u5f71\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6b63\u81ea\u501a\u8237\u5174\u53f9\uff0c\u5ffd\u542c\u5f97\u6a79\u58f0\u54bf\u54d1\uff0c\u4e00\u53ea\u5c0f\u822c\u98ce\u5e06\u75be\u9a76\uff0c\u8fc7\u4e86\u4ed6\u7684\u524d\u5934\u3002\u91d1\u9010\u6d41\u773c \u5149\u4e00\u77a5\uff0c\u9690\u9690\u770b\u89c1\u8231\u4e2d\u6709\u4e2a\u4eba\u7684\u80cc\u5f71\u5f88\u662f\u773c\u719f\uff0c\u5c0f\u8239\u8fc7\u4e86\u4e4b\u540e\uff0c\u624d\u84e6\u5730\u60f3\u8d77\u8fd9\u4eba\u662f\u66fe\u7ecf\u548c\u81ea \u5df1\u4ea4\u8fc7\u624b\u7684\u90a3\u4e2a\u7ea2\u7f28\u4f1a\u7684\u9999\u4e3b\u5bab\u79c9\u85e9\uff0c\u5bab\u79c9\u85e9\u7684\u5c0f\u8239\u75be\u5982\u5954\u9a6c\uff0c\u8f6c\u773c\u95f4\u5c31\u8fc7\u4e86\u524d\u5934\uff0c\u4ed6\u662f\u80cc \u671d\u7740\u91d1\u9010\u6d41\u7684\uff0c\u91d1\u9010\u6d41\u770b\u89c1\u4ed6\uff0c\u4ed6\u6ca1\u6709\u770b\u89c1\u91d1\u9010\u6d41\u3002 \u3000\u3000\u8fd9\u4e2a\u591a\u6708\u6765\uff0c\u91d1\u9010\u6d41\u4f1a\u8fc7\u8bb8\u591a\u6c5f\u6e56\u9ad8\u624b\uff0c\u5176\u4e2d\u5305\u62ec\u6570\u5927\u5e2e\u4f1a\u7684\u8235\u4e3b\u5728\u5185\u3002\u5bab\u79c9\u85e9\u867d\u7136\u4e0d \u8fc7\u4ec5\u4ec5\u662f\u7ea2\u7f28\u4f1a\u4e2d\u7684\u4e00\u4e2a\u9999\u4e3b\uff0c\u4f46\u82e5\u8bba\u771f\u5b9e\u7684\u672c\u9886\uff0c\u4ed6\u51e0\u4e4e\u4e0e\u6587\u9053\u5e84\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u8fdc\u5728\u91d1\u9010\u6d41 \u6240\u4f1a\u8fc7\u7684\u90a3\u4e9b\u8235\u4e3b\u4e4b\u4e0a\u3002\u5c24\u5176\u662f\u5bab\u79c9\u85e9\u7684\u5251\u672f\u81ea\u6210\u4e00\u5bb6\uff0c\u6781\u4e3a\u7cbe\u5999\uff0c\u8fde\u91d1\u9010\u6d41\u5bf9\u4ed6\u7684\u5251\u672f\uff0c \u4e5f\u662f\u4e0d\u80fd\u4e0d\u6709\u51e0\u5206\u4f69\u670d\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5bab\u79c9\u85e9\u4e0d\u77e5\u662f\u4e3a\u4e86\u4f55\u4e8b\u5230\u8fd9\u91cc\u6765\uff1f\u4ed6\u4eec\u5ff5\u5ff5\u4e0d\u5fd8\u4e8e\u8981\u62a2\u516d\u5408\u5e2e\u7684\u7384 \u94c1\uff0c\u83ab\u975e\u662f\u5df2\u7ecf\u6253\u542c\u5230\u4e86\u6211\u7684\u884c\u8e2a\uff0c\u8ffd\u6211\u6765\u7684\uff1f\u5374\u4f55\u4ee5\u53ea\u662f\u4e00\u4eba\uff1f\u90a3\u65e5\u6597\u5251\uff0c\u4ed6\u867d\u7136\u8f93\u4e86\u4e00 \u62db\u7ed9\u6211\uff0c\u4f46\u4e5f\u662f\u6211\u5f52\u56fd\u4ee5\u6765\u6240\u4ec5\u89c1\u7684\u4e00\u4f4d\u5251\u672f\u9ad8\u624b\u4e86\u3002\u7ea2\u7f28\u4f1a\u5728\u6c5f\u6e56\u4e0a\u7684\u58f0\u8a89\u8fd8\u4e0d\u7b97\u574f\uff0c\u8981 \u662f\u4ed6\u80af\u5316\u654c\u4e3a\u53cb\uff0c\u8fd9\u4e2a\u4eba\u5012\u4e5f\u4e0d\u59a8\u4ea4\u4ea4\u3002\u55ef\uff0c\u4e14\u83ab\u7ba1\u4ed6\uff0c\u8fc7\u53bb\u518d\u8bf4\u3002\u201d \u3000\u3000\u5c0f\u8239\u8fc7\u4e86\u5927\u660e\u6e56\uff0c\u91d1\u9010\u6d41\u6253\u53d1\u4e86\u8239\u94b1\uff0c\u8d70\u4e0a\u5cb8\u6765\uff0c\u5374\u5df2\u4e0d\u89c1\u4e86\u5bab\u79c9\u85e9\u7684\u8e2a\u5f71\u3002\u91d1\u9010\u6d41\u6f2b \u6b65\u4ece\u6e56\u8fb9\u8d70\u53bb\uff0c\u8d70\u5230\u4e86\u201c\u5386\u4e0b\u4ead\u201d\u524d\uff0c\u4ead\u5b50\u91cc\u60ac\u6709\u4e00\u526f\u5bf9\u8054\uff0c\u5199\u7684\u662f\uff1a\u201c\u6d77\u53f3\u6b64\u4ead\u53e4\uff0c\u6d4e\u5357 \u540d\u58eb\u591a\u3002\u201d\u8fd9\u672c\u662f\u5510\u4ee3\u8bd7\u5723\u675c\u752b\u201c\u966a\u674e\u5317\u6d77\u5bb4\u5386\u4e0b\u4eab\u201d\u8bd7\u4e2d\u7684\u4e24\u53e5\uff0c\u672c\u5730\u4eba\u89c9\u5f97\u8fd9\u4e24\u53e5\u8bd7\u6b63 \u662f\u5408\u98ce\u62ff\u6765\u4f5c\u4e86\u5386\u4e0b\u4eab\u7684\u5bf9\u8054\u3002 \u3000\u3000\u8fd9\u5386\u4e0b\u4ead\u662f\u6d4e\u5357\u7684\u4e00\u5904\u540d\u80dc\uff0c\u6e38\u4eba\u5230\u6b64\uff0c\u90fd\u559c\u6b22\u5728\u4eab\u4e2d\u5c0f\u61a9\u7247\u523b\uff0c\u559d\u4e00\u559d\u707c\u7a81\u6cc9\u6240\u6ce1\u7684 \u540d\u8336\uff0c\u6b23\u8d4f\u5c71\u8272\u6e56\u5149\u3002\u91d1\u9010\u6d41\u5fc3\u671b\u60f3\u9053\uff1a\u201c\u8981\u662f\u78b0\u7740\u4e86\u5bab\u79c9\u85e9\uff0c\u5c31\u548c\u4ed6\u518d\u6597\u4e00\u6b21\u5251\uff0c\u78b0\u4e0d\u4e0a \u6211\u5c31\u81ea\u5df1\u6e38\u6e56\u3002\u201d\u6e38\u5c71\u73a9\u6c34\u548c\u6bd4\u6b66\u6597\u5251\u90fd\u662f\u91d1\u9010\u6d41\u6240\u6b22\u559c\u7684\u4e8b\u60c5\uff0c\u4ed6\u62b1\u7740\u65e0\u53ef\u65e0\u4e0d\u53ef\u7684\u5fc3 \u60c5\uff0c\u4e5f\u5230\u4ead\u4e2d\u6682\u65f6\u9a7b\u8db3\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u201c\u549a\u549a\u201d\u7684\u68a8\u82b1\u9f13\u54cd\uff0c\u5bab\u79c9\u85e9\u672a\u89c1\uff0c\u5012\u6709\u4e24\u4e2a\u8bf4\u4e66\u7684\u7236\u5973\u6765\u4e86\u3002\u5c31\u5728\u4ead\u5b50\u65c1\u8fb9\u6446 \u4e0b\u4e66\u575b\uff0c\u6572\u8d77\u9523\u9f13\uff0c\u62db\u5f95\u89c2\u4f17\u3002\u91d1\u9010\u6d41\u53cd\u6b63\u95f2\u7740\u6ca1\u4e8b\uff0c\u4e8e\u662f\u968f\u4f17\u53bb\u542c\u8bf4\u4e66\u3002 \u3000\u3000\u8bf4\u4e66\u7684\u662f\u4e2a\u5341\u516d\u516b\u5c81\u7684\u59d1\u5a18\uff0c\u6de1\u626b\u86fe\u7709\uff0c\u8346\u94a6\u88d9\u5e03\uff0c\u59ff\u8272\u6e05\u4e3d\u3002\u770b\u4f3c\u67d4\u5f31\uff0c\u4f46\u7709\u5b87\u4e4b \u95f4\uff0c\u5219\u9690\u9690\u8574\u7740\u4e00\u80a1\u82f1\u6c14\uff0c\u65c1\u8fb9\u7ed9\u5979\u5f39\u5f26\u5b50\u7684\u662f\u5979\u7684\u7236\u4eb2\uff0c\u6ee1\u8138\u7599\u7629\uff0c\u7eb5\u7136\u4e0d\u80fd\u8bf4\u662f\u201c\u4e11\u516b \u602a\u201d\uff0c\u548c\u5973\u513f\u6bd4\u8d77\u6765\u5374\u662f\u5927\u6709\u5929\u58e4\u4e4b\u522b\u4e86\u3002\u89c2\u4f17\u4e2d\u6709\u51e0\u4e2a\u8f7b\u8584\u7684\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4e4c\u9e26\u4e5f \u80fd\u517b\u51fa\u4e86\u51e4\u51f0\u3002\u201d \u3000\u3000\u65c1\u4eba\u53ea\u662f\u6ce8\u610f\u8fd9\u4e24\u7236\u5973\u7684\u76f8\u8c8c\u7f8e\u4e11\uff0c\u91d1\u9010\u6d41\u5fc3\u91cc\u53ef\u662f\u6697\u6697\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u8fd9\u4e24\u7236\u5973\u4e00\u5b9a\u662f \u7ec3\u8fc7\u6b66\u529f\u7684\uff0c\u770b\u6765\u8fd9\u6c49\u5b50\u8fd8\u662f\u4e2a\u5185\u5bb6\u9ad8\u624b\u54e9\uff01\u201d \u3000\u3000\u90a3\u51e0\u4e2a\u8f7b\u8584\u5c11\u5e74\u7684\u5632\u8c11\uff0c\u7236\u5973\u4fe9\u53ea\u5f53\u662f\u542c\u4e0d\u89c1\uff0c\u90a3\u6ee1\u9762\u7599\u7629\u7684\u6c49\u5b50\u8c03\u6574\u4e86\u4e00\u4e0b\u5f26\u7d22\uff0c\u8bf4 \u9053\uff1a\u201c\u591a\u8c22\u5217\u4f4d\u770b\u5b98\u6367\u573a\uff0c\u6211\u53eb\u8fd9\u4e2b\u5934\u5b5d\u656c\u5217\u4f4d\u4e24\u6bb5\u9f13\u4e66\uff0c\u5531\u5f97\u4e0d\u597d\uff0c\u8bf7\u5927\u5bb6\u5305\u6db5\u3002\u201d\u90a3\u51e0 \u4e2a\u5c11\u5e74\u6cb9\u5634\u6ed1\u820c\u5730\u8bf4\u9053\uff1a\u201c\u7f8e\u4eba\u513f\u5531\u7684\u66f2\u5b50\uff0c\u4e0d\u7528\u8bf4\uff0c\u90a3\u4e00\u5b9a\u662f\u597d\u7684\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u4e5f\u4e0d\u52a8\u6c14\uff0c\u62b1\u62f3\u4e00\u63d6\u8bf4\u9053\uff1a\u201c\u82e5\u7136\u5217\u4f4d\u770b\u5b98\u8ba4\u4e3a\u8fd8\u8fc7\u5f97\u53bb\uff0c\u90a3\u5c31\u8bf7\u5927\u5bb6\u968f\u4fbf\u8d4f\u8d50 \u8d4f\u8d50\u3002\u201d\u5f53\u4e0b\uff0c\u62ff\u8d77\u4e09\u5f26\uff0c\u94ee\u94ee\u742e\u742e\u7684\u5c31\u5f39\u4e86\u8d77\u6765\uff0c\u5c0f\u59d1\u5a18\u53ee\u53ee\u549a\u549a\u5730\u6572\u54cd\u4e86\u68a8\u82b1\u7b80\uff0c\u5f8b\u5415 \u8c03\u548c\uff0c\u5ffd\u5730\u63ed\u9f13\u4e00\u58f0\uff0c\u6b4c\u5589\u902e\u53d1\uff0c\u5531\u7684\u662f\u201c\u98ce\u5c18\u4e09\u4fa0\u201d\u4e2d\u866c\u9aef\u5ba2\u4e0e\u674e\u9756\u7ea2\u4f5b\u7ed3\u8bc6\u7684\u4e00\u6bb5\u6545 \u4e8b\u3002 \u3000\u3000\u8fd9\u5c0f\u5973\u6309\u62cd\u8f7b\u6b4c\uff0c\u5b9b\u5982\u65b0\u83ba\u51fa\u8c37\uff0c\u4e73\u71d5\u5f52\u5de2\uff0c\u58f0\u58f0\u5b9b\u8f6c\uff0c\u5b57\u5b57\u6e05\u8106\uff0c\u6291\u626c\u987f\u632b\uff0c\u5165\u8033\u52a8 \u5fc3\uff0c\u67d4\u548c\u4f4e\u5531\u4e4b\u65f6\uff0c\u5f53\u771f\u4fbf\u4ee5\u201c\u95f4\u5173\u83ba\u8bed\u82b1\u5e95\u6ed1\uff0c\u5e7d\u54bd\u6cc9\u6d41\u51b0\u4e0b\u6ee9\u201d\uff0c\u6025\u4ea2\u9ad8\u6b4c\u4e4b\u9645\uff0c\u5ffd\u5730 \u53c8\u5982\u201c\u94f6\u74f6\u4e4d\u7834\u6c34\u6d46\u8ff8\uff0c\u94c1\u9a91\u7a81\u51fa\u5200\u67aa\u9e23\u201d\u3002\u573a\u5b50\u91cc\u7684\u4eba\u90fd\u542c\u5f97\u5446\u4e86\uff0c\u8fde\u90a3\u51e0\u4e2a\u6cb9\u5634\u6ed1\u820c\u7684 \u5c11\u5e74\uff0c\u4e5f\u662f\u5927\u6c14\u513f\u90fd\u4e0d\u6562\u900f\uff0c\u751f\u6015\u6f0f\u8fc7\u4e86\u4e00\u4e2a\u97f3\u7b26\u3002 \u3000\u3000\u84e6\u5730\u91cc\u201c\u56db\u5f26\u4e00\u58f0\u5982\u88c2\u5e1b\u201d\uff0c\u6b4c\u58f0\u621b\u7136\u800c\u6b62\uff0c\u4f59\u97f3\u7ed5\u6881\uff0c\u5140\u662f\u4e0d\u7edd\u5982\u7f15\u3002\u8fc7\u4e86\u534a\u664c\uff0c\u4f17 \u4eba\u624d\u8f70\u7136\u5730\u53eb\u8d77\u201c\u597d\u201d\u6765\u3002\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4eba\u8bf4\u6d4e\u5357\u7684\u68a8\u82b1\u5927\u9f13\u4e43\u662f\u66f2\u827a\u4e00\u7edd\uff0c\u679c\u7136\u540d \u4e0d\u865a\u4f20\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u8131\u4e0b\u5e3d\u5b50\uff0c\u6b63\u8981\u8ba8\u8d4f\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u5927\u53eb\u9053\uff1a\u201c\u597d\uff0c\u5531\u5f97\u771f\u597d\uff01\u4f60\u4e0d\u5fc5\u5728\u8fd9\u91cc\u8ba8\u94b1 \u4e86\uff0c\u4e94\u6587\u5341\u6587\u7684\u6ca1\u6709\u4ec0\u4e48\u610f\u601d\uff0c\u5e26\u4e86\u4f60\u7684\u95fa\u5973\uff0c\u5230\u6211\u4eec\u5e9c\u4e2d\uff0c\u5411\u516c\u5b50\u7237\u8ba8\u8d4f\u5427\u3002\u516c\u5b50\u7237\u4e00\u6b22 \u559c\uff0c\u5305\u4f60\u4e00\u751f\u5403\u559d\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u9762\u8272\u4e00\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u548c\u4f60\u4eec\u7684\u516c\u5b50\u7237\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4e0d\u6562\u8e35\u5e9c\u9886\u8d4f\u3002\u201d\u90a3\u6559\u5934\u54c8\u54c8 \u7b11\u9053\uff1a\u201c\u4f60\u4eec\u53bb\u4e86\uff0c\u4e0d\u5c31\u8ba4\u8bc6\u4e86\u3002\u563f\uff0c\u563f\uff0c\u4f60\u4e0d\u8ba4\u8bc6\u6211\u4eec\u7684\u516c\u5b50\u7237\uff0c\u6211\u4eec\u7684\u516c\u5b50\u7237\u53ef\u65e9\u5c31\u8ba4 \u8bc6\u4f60\u7684\u95fa\u5973\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e00\u770b\uff0c\u53ea\u89c1\u8bf4\u8bdd\u7684\u8fd9\u4eba\u662f\u4e00\u4e2a\u9762\u8089\u6a2a\u751f\uff0c\u597d\u50cf\u6559\u5934\u6a21\u7965\u7684\u6c49\u5b50\uff0c\u540e\u9762\u8fd8\u8ddf\u7740\u4e03\u516b \u6761\u5927\u6c49\u3002\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u60f3\u5fc5\u662f\u54ea\u4e00\u4e2a\u6076\u9738\u7684\u5bb6\u5974\uff0c\u6211\u4e14\u522b\u5fd9\u6253\u53d1\u4ed6\u4eec\uff0c\u5148\u770b\u770b\u8fd9\u4e24\u7236 \u5973\u7684\u624b\u6bb5\u3002\u201d \u3000\u3000\u8fd9\u4e00\u4f19\u4eba\u4e00\u6765\uff0c\u6ee1\u573a\u89c2\u4f17\u767b\u65f6\u8dd1\u4e86\u4e2a\u5341\u4e4b\u516b\u4e5d\uff0c\u53ea\u5269\u4e0b\u90a3\u51e0\u4e2a\u6cb9\u5934\u6ed1\u9762\u7684\u5c11\u5e74\uff0c\u8fdc\u8fdc\u7684 \u9a7b\u8db3\u800c\u89c2\uff0c\u6307\u624b\u5212\u811a\u7684\u597d\u50cf\u5728\u770b\u628a\u620f\u3002\u6709\u4e00\u4e24\u4e2a\u8fd8\u5e2e\u8154\u9053\uff1a\u201c\u563f\uff0c\u563f\uff0c\u5f20\u5bb6\u7684\u516c\u5b50\u7237\u770b\u4e0a\u4e86 \u4f60\u7684\u95fa\u5973\uff0c\u4f60\u53ef\u662f\u5929\u5927\u7684\u9020\u5316\u5230\u5566\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u77e5\u9053\u65e0\u987b\u81ea\u5df1\u51fa\u624b\uff0c\u4f46\u6c14\u8fd9\u7fa4\u6076\u5974\u4e0d\u8fc7\uff0c\u6709\u610f\u548c\u4ed6\u4eec\u5f00\u5f00\u73a9\u7b11\uff0c\u968f\u624b\u6478\u4e86\u4e00\u952d\u91d1 \u5b50\u629b\u53bb\uff0c\u90a3\u6c49\u5b50\u628a\u76ae\u5e3d\u4e00\u515c\uff0c\u63a5\u4e86\u8fc7\u6765\uff0c\u5fc3\u91cc\u597d\u751f\u8be7\u5f02\uff0c\u4f46\u5f88\u5feb\u795e\u8272\u81ea\u5982\u7684\u6de1\u6de1\u5730\u8bf4\u4e86\u58f0 \u201c\u591a\u8c22\u3002\u201d\u597d\u50cf\u5e76\u4e0d\u628a\u4ed6\u8fd9\u952d\u91d1\u5b50\u653e\u5728\u773c\u5185\u3002 \u3000\u3000\u8fd9\u6c49\u5b50\u4e0d\u52a8\u58f0\u8272\uff0c\u90a3\u4e2a\u6559\u5934\u5219\u5df2\u662f\u52c3\u7136\u53d8\u8272\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u8981\u548c\u6211\u5bb6\u7684\u516c\u5b50 \u7237\u6bd4\u62fc\u6709\u94b1\u600e\u4e48\u7740\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u6211\u53ea\u662f\u4e2a\u7a77\u5c0f\u5b50\uff0c\u5c82\u6562\u72d7\u773c\u770b\u4eba\u4f4e\u5462\uff01\u201d\u8fd9\u6559\u5934\u521a\u624d\u66fe\u7528\u8fc7\u8f7b\u8511 \u7684\u53e3\u543b\uff0c\u8bf4\u662f\u542c\u8bf4\u4e66\u7684\u770b\u5ba2\uff0c\u51fa\u624b\u6700\u591a\u4e0d\u8fc7\u4e94\u6587\u5341\u6587\u94dc\u94b1\uff0c\u5982\u4eca\u91d1\u9010\u6d41\u5c31\u7528\u53cc\u5173\u7684\u8bf4\u8bdd\u7b54\u590d \u4ed6\uff0c\u5927\u5927\u7684\u8bbd\u523a\u4e86\u4ed6\u4e00\u4e0b\u3002 \u3000\u3000\u8fd9\u6559\u5934\u5927\u6012\uff0c\u5c31\u8981\u5411\u91d1\u9010\u6d41\u6251\u6765\u3002\u91d1\u9010\u6d41\u6447\u6447\u624b\u9053\uff1a\u201c\u54ce\u5440\uff01\u6211\u53ef\u662f\u4e0d\u4f1a\u6253\u67b6\u7684\u3002\u201d \u3000\u3000\u90a3\u8bf4\u4e66\u7684\u5c11\u5973\u4e00\u5ffd\u5730\u62e6\u5728\u4ed6\u4eec\u4e24\u4eba\u4e2d\u95f4\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u56de\u53bb\u62dc\u89c1\u4f60\u7684\u516c\u5b50\u7237\u5c31\u662f\uff0c\u4f60 \u53ef\u4e0d\u80fd\u8fc1\u6012\u65c1\u4eba\u5440\uff01\u201d \u3000\u3000\u8fd9\u6559\u5934\u6025\u7740\u8981\u628a\u8fd9\u5c11\u5973\u5e26\u56de\u53bb\uff0c\u5f53\u4e0b\u77aa\u4e86\u91d1\u9010\u6d41\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6709\u8fd9\u4f4d\u59d1\u5a18\u8bf4\u60c5\uff0c \u4fbf\u5b9c\u4e86\u4f60\u8fd9\u5c0f\u5b50\uff0c\u8d70\u5427\uff01\u201d\u4f38\u624b\u62c9\u90a3\u5c11\u5973\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u62d6\u62d6\u62c9\u62c9\uff0c\u4f60\u5f53\u6211\u662f\u4ec0\u4e48\u4eba\uff1f\u6211\u4e0d\u53bb\uff01\u201d\u5979\u624b\u4e0a\u7684\u90a3\u67c4\u9f13\u9524\u8fd8\u672a\u653e\u4e0b\uff0c\u9f13 \u9524\u8f7b\u8f7b\u4e00\u6572\uff0c\u6559\u5934\u7684\u4e00\u6761\u53f3\u81c2\u767b\u65f6\u9ebb\u6728\u3002\u8fd9\u6559\u5934\u5927\u5403\u4e00\u60ca\uff0c\u59cb\u77e5\u8fd9\u5c11\u5973\u8eab\u6000\u7edd\u6280\uff0c\u5f53\u4e0b\u4e5f\u987e \u4e0d\u5f97\u601c\u9999\u60dc\u7389\u4e86\uff0c\u4ed6\u7684\u53f3\u81c2\u9ebb\u6728\uff0c\u5de6\u81c2\u5c31\u6293\u8fc7\u6765\uff0c\u4f7f\u7684\u662f\u201c\u9e70\u722a\u529f\u201d\uff0c\u800c\u4e14\u52a0\u4e0a\u4e86\u4e00\u8bb0\u626b\u5802 \u817f\u3002 \u3000\u3000\u5c11\u5973\u7709\u5934\u4e00\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6700\u6068\u72d7\u6756\u4eba\u52bf\u7684\u5974\u624d\uff0c\u4f60\u65e2\u7136\u5b9a\u8981\u901e\u51f6\uff0c\u4e0d\u7ed9\u4f60\u4e00\u70b9\u5389\u5bb3\u77a7 \u77a7\uff0c\u4f60\u4e5f\u4e0d\u77e5\u6211\u7684\u672c\u9886\u3002\u53bb\u5427\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u8fd9\u6559\u5934\u7684\u4e00\u6293\u4e00\u8e22\uff0c\u5168\u90e8\u843d\u7a7a\u3002\u9661\u7136\u95f4\u53ea\u89c9\u8116 \u5b50\u75bc\u75db\uff0c\u90a3\u5c11\u5973\u5df2\u662f\u6293\u7740\u4e86\u4ed6\u9888\u540e\u7684\u4e00\u56e2\u80a5\u8089\uff0c\u628a\u4ed6\u5982\u540c\u6349\u5c0f\u9e21\u4f3c\u7684\u63d0\u4e86\u8d77\u6765\uff0c\u4e00\u4e2a\u65cb\u98ce\u6025 \u821e\uff0c\u6454\u4e86\u51fa\u53bb\u3002\u6b64\u65f6\uff0c\u5979\u624d\u521a\u597d\u8bf4\u51fa\u201c\u53bb\u5427\u201d\u4e8c\u5b57\u3002 \u3000\u3000\u53ea\u542c\u5f97\u535c\u901a\u3001\u535c\u901a\u7684\u4e00\u8fde\u4e32\u597d\u4f3c\u6728\u5934\u5012\u5730\u7684\u58f0\u97f3\uff0c\u5979\u6454\u51fa\u53bb\u53ea\u662f\u90a3\u4e2a\u6559\u5934\uff0c\u5012\u5728\u5730\u4e0a\u5374 \u6709\u4e94\u516d\u4e2a\u4e4b\u591a\u3002\u539f\u6765\u5c11\u5973\u662f\u628a\u8fd9\u4e2a\u6559\u5934\u5411\u7740\u90a3\u51e0\u4e2a\u770b\u70ed\u95f9\u65e0\u8d56\u5c11\u5e74\u63b7\u53bb\u7684\uff0c\u8fd9\u4e00\u4e0b\uff0c\u4ed6\u4eec\u53ef \u5c31\u60e8\u4e86\uff0c\u5168\u90fd\u53d8\u4f5c\u4e86\u6eda\u5730\u846b\u82a6\u3002\u91d1\u9010\u6d41\u4e50\u5f97\u62cd\u638c\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u90a3\u51e0\u4e2a\u65e0\u8d56\u5c11\u5e74\u672c\u6765\u662f\u7ad9\u5f97\u8fdc\u8fdc\u7684\u770b\u70ed\u95f9\uff0c\u8ddd\u79bb\u5c11\u8bf4\u4e5f\u5728\u5341\u4e08\u5f00\u5916\u3002\u8fd9\u5c11\u5973\u628a\u4e00\u4e2a\u767e\u591a \u65a4\u91cd\u7684\u6c49\u5b50\u63d0\u4e86\u8d77\u6765\uff0c\u6454\u5230\u5341\u4e08\u5f00\u5916\u3002\u800c\u4e14\u8fd8\u628a\u90a3\u51e0\u4e2a\u4eba\u5c3d\u7686\u6253\u5012\uff0c\u8fd9\u4e00\u624b\u529f\u592b\u9732\u4e86\u51fa\u6765\uff0c \u767b\u65f6\u628a\u90a3\u7fa4\u6076\u5974\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u53ea\u6068\u7239\u5a18\u751f\u5c11\u4e86\u4e24\u6761\u817f\uff0c\u53d1\u4e00\u58f0\u558a\uff0c\u5fd9\u4e0d\u8fed\u5730\u5954\u9003\uff0c\u8f6c\u773c\u95f4 \u8dd1\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u91d1\u9010\u6d41\u867d\u4e0d\u600e\u6837\u60ca\u5f02\uff0c\u4f46\u4e5f\u89c9\u5f97\u8fd9\u5c11\u5973\u7684\u6b66\u529f\u6709\u70b9\u51fa\u4e4e\u4ed6\u7684\u610f\u5916\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u59d1 \u5a18\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5916\u5bb6\u529f\u592b\u7ec3\u5230\u8fd9\u4e2a\u5730\u6b65\uff0c\u4e5f\u662f\u5f88\u96be\u5f97\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5c11\u5973\u9053\uff1a\u201c\u7239\uff0c\u54b1\u4eec\u4e0d\u80fd\u968f\u4fbf\u8981\u4eba\u5bb6\u7684\u91d1\u5b50\u3002\u201d\u62ff\u4e86\u90a3\u952d\u91d1\u5b50\uff0c\u6b63\u8981\u8fd8\u7ed9\u91d1\u9010\u6d41\u3002\u90a3 \u6c49\u5b50\u5df2\u5728\u795e\u8272\u5f20\u7687\u5730\u8bf4\u9053\uff1a\u201c\u51e4\u4e2b\u5934\uff0c\u4f60\u95ef\u4e86\u5927\u7978\u4e86\uff0c\u5feb\u8d70\uff0c\u5feb\u8d70\uff01\u201d \u3000\u3000\u8fd9\u5c11\u5973\u6241\u4e86\u6241\u5634\uff0c\u4e00\u526f\u8f7b\u8511\u7684\u795e\u6c14\u8bf4\u9053\uff1a\u201c\u5927\u4e0d\u4e86\u4e0d\u8fc7\u5f97\u7f6a\u4e00\u4e2a\u571f\u8c6a\u6076\u9738\uff0c\u6015\u4ed6\u4ec0 \u4e48\uff1f\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u5509\uff0c\u4f60\u8fd9\u91ce\u4e2b\u5934\u7b80\u76f4\u662f\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\uff0c\u90a3\u4e9b\u4eba\u662f\u4e0d\u80fd\u62db\u60f9\u7684\uff0c\u4ed6\u4eec\u7684\u597d \u624b\u8fd8\u672a\u6765\u5462\u3002\u4f60\u628a\u91d1\u5b50\u8fd8\u7ed9\u8fd9\u4f4d\u76f8\u516c\uff0c\u81ea\u4eec\u5feb\u8d70\u3002\u518d\u8fdf\u4e9b\u5c31\u6765\u4e0d\u53ca\u3002\u201d \u3000\u3000\u8fd9\u5c11\u5973\u542c\u5979\u7236\u4eb2\u8bf4\u5f97\u5982\u6b64\u7d27\u5f20\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u7239\u7239\u8d70\u5357\u95ef\u5317\uff0c\u4e0d\u77e5\u4f1a\u8fc7\u591a\u5c11\u597d\u6c49\uff0c\u4ece\u672a \u89c1\u4ed6\u4f3c\u4eca\u65e5\u8fd9\u822c\u5bb3\u6015\u7684\u3002\u83ab\u975e\u90a3\u4e2a\u571f\u9738\u5f53\u771f\u6709\u70b9\u95e8\u9053\uff1a\u201c\u5fc3\u91cc\u6709\u70b9\u60ca\u7591\uff0c\u5f53\u4e0b\u628a\u90a3\u952d\u91d1\u5b50\u629b \u7ed9\u91d1\u9010\u6d41\uff0c\u4fbf\u81ea\u8d70\u4e86\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u56de\u5934\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u76f8\u516c\u539a\u8d50\uff0c\u611a\u7236\u5973\u5fc3\u9886\u4e86\uff0c\u8bf7\u76f8\u516c\u4e5f\u5feb\u8d70\u5427\uff0c\u514d\u5f97\u53d7\u4e86\u7275 \u7d2f\u3002\u201d\u4ed6\u53e3\u91cc\u8bf4\u8bdd\uff0c\u811a\u6b65\u4e0d\u505c\uff0c\u597d\u4f3c\u751f\u6015\u8d70\u8fdf\u7247\u523b\uff0c\u5c31\u6709\u5927\u7978\u4e34\u5934\uff0c\u56e0\u6b64\u4e5f\u5c31\u987e\u4e0d\u5f97\u793c\u8c8c \u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e5f\u662f\u9887\u611f\u8be7\u5f02\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u59d1\u5a18\u90fd\u6709\u5982\u6b64\u672c\u4e8b\uff0c\u5979\u7684\u7236\u4eb2\u53ef\u60f3\u800c\u77e5\u3002\u4e3a\u4f55 \u8fd9\u6837\u5bb3\u6015\uff1f\u521a\u624d\u90a3\u4e2a\u6559\u5934\uff0c\u4e0d\u582a\u5c0f\u59d1\u5a18\u4e4b\u4e00\u51fb\uff0c\u91d1\u9010\u6d41\u5bf9\u8fd9\u73ed\u8c6a\u5974\u5f53\u7136\u662f\u66f4\u52a0\u8f7b\u89c6\u3002\u4ee5\u4e3a\u90a3 \u4e2a\u4ec0\u4e48\u201c\u516c\u5b50\u7237\u201d\u7eb5\u7136\u5bb6\u91cc\u8fd8\u517b\u6709\u6559\u5934\uff0c\u60f3\u6765\u4e5f\u4e0d\u4f1a\u6bd4\u8fd9\u4e2a\u6559\u5934\u9ad8\u660e\u591a\u5c11\u3002\u56e0\u4e3a\u7167\u5e38\u7406\u800c \u8bba\uff0c\u5982\u679c\u76f8\u5dee\u592a\u8fdc\uff0c\u8fd9\u4e2a\u6559\u5934\u8fd8\u600e\u80fd\u5728\u4ed6\u7684\u5bb6\u4e2d\u6df7\u5f97\u4e0b\u53bb\uff1f \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u4e09\u9a91\u5feb\u9a6c\u5954\u6765\uff0c\u8bf4\u4e66\u7684\u8fd9\u5bf9\u7236\u5973\u521a\u597d\u8dd1\u5230\u6e56\u8fb9\uff0c\u6e56\u4e0a\u7684\u8239\u5bb6\u89c1\u4e86\u90a3\u4e09\u4e2a \u4e00\u4eba\u6765\u5230\uff0c\u614c\u5fd9\u628a\u8239\u6491\u5f00\uff0c\u4e0d\u6562\u505a\u8fd9\u5bf9\u7236\u5973\u7684\u751f\u610f\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u90a3\u4e09\u4e2a\u4eba\u8df3\u4e0b\u9a6c \u6765\uff0c\u5c31\u5728\u6e56\u8fb9\u5c06\u7236\u5973\u4fe9\u622a\u4f4f\u3002 \u3000\u3000\u8d70\u5728\u524d\u9762\u7684\u662f\u4e00\u4e2a\u4e8c\u5341\u591a\u5c81\u7684\u5e74\u8f7b\u4eba\uff0c\u9762\u5982\u6577\u7c89\uff0c\u989c\u82e5\u6d82\u8102\uff0c\u624b\u8896\u6447\u7740\u4e00\u628a\u767d\u7eb8\u6298\u6247\uff0c \u5012\u6709\u51e0\u5206\u98ce\u6d41\u4fca\u4fcf\u7684\u6837\u5b50\u3002\u8ddf\u5728\u4ed6\u8eab\u540e\u7684\u4e24\u4e2a\u4eba\u4e00\u9ad8\u4e00\u77ee\uff0c\u9ad8\u7684\u90a3\u4e2a\u8eab\u6750\u9b41\u68a7\uff0c\u6ee1\u5934\u79c3\u5f97\u6cb9 \u5149\u6676\u4eae\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u4f55\u8001\u5927\uff0c\u201d\u77ee\u7684\u90a3\u4e2a\u53cc\u7738\u70af\u70af\uff0c\u4e24\u8fb9\u592a\u9633\u7a74\u575f \u8d77\uff0c\u8170\u91cc\u63d2\u7740\u4e00\u5bf9\u5224\u5b98\u7b14\uff0c\u4e00\u770b\u5c31\u77e5\u662f\u4e2a\u5185\u5bb6\u9ad8\u624b\u3002\u4ed6\u6ca1\u6709\u8bf4\u8bdd\uff0c\u53ea\u662f\u6253\u91cf\u7740\u5bf9\u65b9\u4e24\u7236\u5973\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u6cb9\u5934\u7c89\u9762\u7684\u5c11\u5e74\u60f3\u5fc5\u5c31\u662f\u90a3\u4e2a\u2018\u516c\u5b50\u7237\u2019\u4e86\u3002\u4ed6\u8fd9\u4e24\u4e2a\u4fdd\u7f25\u770b \u6765\u529f\u529b\u4e0d\u5f31\uff0c\u4e0d\u77e5\u6bd4\u90a3\u6559\u5934\u9ad8\u660e\u591a\u5c11\uff1f\u201d\u91d1\u9010\u6d41\u6b64\u65f6\u65b9\u77e5\u81ea\u5df1\u6599\u654c\u8fc7\u8f7b\uff0c\u4f46\u4e5f\u8fd8\u4e0d\u662f\u600e\u6837\u653e \u5728\u5fc3\u4e0a\uff0c\u4e8e\u662f\u5c31\u88c5\u4f5c\u770b\u70ed\u95f9\u7684\u6837\u5b50\uff0c\u6162\u6162\u5730\u8d70\u8fc7\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u201c\u516c\u5b50\u7237\u201d\u62e6\u4f4f\u4e86\u90a3\u4e2a\u5c11\u5973\uff0c\u6298\u6247\u8f7b\u6447\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5c0f\u53ef\u4ef0\u6155\u59d1\u5a18\u8272\u827a\u53cc\u7edd\uff0c \u7279\u9063\u5bb6\u4eba\u524d\u6765\u9080\u8bf7\uff0c\u8bf7\u59d1\u5a18\u79fb\u7389\u5bd2\u820d\uff0c\u5c0f\u53ef\u5f97\u4ee5\u7a0d\u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff0c\u4e0d\u6599\u8fd9\u4e9b\u5974\u624d\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u5f97 \u7f6a\u4e86\u59d1\u5a18\u3002\u5c0f\u53ef\u8fd9\u53a2\u8d54\u7f6a\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u5927\u523a\u523a\u7684\u53d7\u4ed6\u4e00\u63d6\uff0c\u4e5f\u4e0d\u8fd8\u793c\uff0c\u5374\u677f\u7740\u9762\u5b54\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7236\u5973\u662f\u5728\u6c5f\u6e56\u4e0a\u5356\u827a\u6df7 \u996d\u5403\u7684\uff0c\u4f46\u5374\u8fd8\u4e0d\u81f3\u4e8e\u90a3\u6837\u4e0b\u8d31\uff0c\u8981\u53bb\u5949\u627f\u5bcc\u8d35\u4eba\u5bb6\uff0c\u767b\u95e8\u4f91\u9152\u3002\u4f60\u786c\u6765\u4e5f\u597d\uff0c\u8f6f\u6765\u4e5f\u597d\uff0c \u6211\u5c31\u662f\u4e0d\u53bb\uff01\u4f60\u7ed9\u6211\u6eda\u5f00\uff01\u201d \u3000\u3000\u90a3\u201c\u516c\u5b50\u7237\u201d\u51e0\u66fe\u53d7\u8fc7\u4eba\u5982\u6b64\u595a\u843d\uff0c\u4ed6\u7709\u6bdb\u4e00\u626c\uff0c\u4f3c\u4e4e\u8981\u52a8\u6012\u7684\u795e\u6c14\uff0c\u5374\u4ecd\u662f\u8d54\u7b11\u8bf4 \u9053\uff1a\u201c\u59d1\u5a18\u8a00\u91cd\u4e86\uff0c\u5c0f\u53ef\u662f\u4e13\u8bda\u6765\u8bf7\u59d1\u5a18\u7684\uff0c\u5c82\u6562\u628a\u4f60\u5f53\u4f5c\u4e0b\u8d31\u7684\u827a\u4eba\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u7684\u8bdd\u8bf4\u5b8c\u4e86\u6ca1\u6709\uff0c\u4f11\u518d\u7f57\u54b3\uff0c\u6211\u53ef\u6ca1\u6709\u5de5\u592b\u4e0e\u4f60\u778e\u626f\u6717\u7f20\uff01\u201d \u3000\u3000\u90a3\u201c\u516c\u5b50\u7237\u201d\u843d\u4e0d\u4e86\u53f0\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u8fd9\u4e48\u4e0d\u7ed9\u9762\u5b50\uff1f\u5bf9\u4e0d\u4f4f\uff0c\u4f60\u4e0d\u53bb\u4e5f\u5f97\u53bb\u4e86\uff01\u201d \u52a8\u624b\u5c31\u6765\u62c9\u8fd9\u5c11\u5973\u3002 \u3000\u3000\u5c11\u5973\u67f3\u7709\u5012\u7ad6\uff0c\u65a5\u9053\uff1a\u201c\u653e\u8086\uff01\u597d\uff0c\u6211\u5012\u8981\u770b\u4f60\u6709\u4ec0\u4e48\u672c\u9886\u8bf7\u5f97\u52a8\u6211\uff01\u201d\u53cc\u6307\u631f\u7740\u90a3\u67c4 \u5c0f\u9f13\u9524\uff0c\u4e00\u4e2a\u8f6e\u8f6c\uff0c\u95ea\u7535\u822c\u7684\u5c31\u5411\u90a3\u4e2a\u201c\u516c\u5b50\u7237\u201d\u7684\u864e\u53e3\u6572\u4e0b\u3002\u5979\u521a\u624d\u5c31\u662f\u7528\u8fd9\u4e00\u624b\u6cd5\u6253\u5230 \u90a3\u4e2a\u6559\u5e08\u7237\u7684\uff0c\u5982\u4eca\u4f9d\u4f6f\u753b\u846b\u82a6\u7684\u7528\u6765\u70ae\u5236\u8fd9\u4e2a\u201c\u516c\u5b50\u7237\u201d\u3002 \u3000\u3000\u8fd9\u4e00\u5239\u90a3\uff0c\u6709\u4e24\u4e2a\u4eba\u540c\u65f6\u53eb\u9053\uff1a\u201c\u516c\u5b50\uff0c\u5c0f\u5fc3\uff01\u201d\u201c\u51e4\u513f\u5c0f\u5fc3\uff01\u201d\u4e00\u4e2a\u662f\u90a3\u79c3\u9876\u7684\u5927 \u6c49\uff0c\u53e6\u4e00\u4e2a\u662f\u8fd9\u5c11\u5973\u7684\u7236\u4eb2\u3002 \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u6240\u5f97\u90a3\u201c\u516c\u5b50\u7237\u201d\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u5c0f\u8d31\u4eba\u4e0d\u8bc6\u62ac\u4e3e\uff01\u201d\u6298\u6247\u500f\u5730\u5f20\u5f00\uff0c\u6321\u4f4f \u4e86\u5c11\u5973\u7684\u9f13\u9524\u3002 \u3000\u3000\u9f13\u9524\u867d\u7136\u4e0d\u662f\u950b\u5229\u7684\u6b66\u5668\uff0c\u53ef\u662f\u4e00\u5f20\u767d\u7eb8\u6298\u6247\uff0c\u88ab\u9f13\u9524\u4e00\u6572\uff0c\u81f3\u5c11\u4e5f\u5e94\u8be5\u7a7f\u4e00\u4e2a\u6d1e\u3002\u4f46 \u8bf4\u4e5f\u5947\u602a\uff0c\u53ea\u542c\u5f97\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u201c\u516c\u5b50\u7237\u201d\u7684\u6298\u6247\u4e0d\u7a7f\u4e0d\u70c2\uff0c\u53cd\u800c\u662f\u5c11\u5973\u7684\u9f13\u9524\u8131\u624b\u800c \u98de\uff01 \u3000\u3000\u90a3\u77ee\u5b50\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u8fd9\u4e2a\u96cc\u513f\u4e0d\u662f\u54b1\u4eec\u516c\u5b50\u7237\u7684\u5bf9\u624b\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8fd9\u624d\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u201c\u516c\u5b50\u7237\u201d\u7684\u672c\u9886\u5728\u91d1\u9010\u6d41\u7684\u773c\u4e2d\u867d\u7136\u8fd8\u7b97\u4e0d\u4e0a\u662f\u4ec0\u4e48\u9ad8\u660e\u7684 \u529f\u592b\uff0c\u4f46\u4e00\u4e2a\u5bcc\u8d35\u4eba\u5bb6\u7684\u201c\u516c\u5b50\u7237\u201d\u800c\u80fd\u591f\u6709\u8fd9\u6837\u7684\u529f\u592b\u5374\u662f\u51fa\u4e4e\u91d1\u9010\u6d41\u7684\u610f\u6599\u4e4b\u5916\u4e86\u3002 \u3000\u3000\u516c\u5b50\u7237\u4e00\u62db\u5f97\u624b\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u529d\u4f60\u4f11\u8981\u656c\u9152\u4e0d\u5403\u5403\u7f5a\u6d12\uff0c\u8fd8\u662f\u8ddf\u6211\u8d70\u5427\u3002\u201d \u3000\u3000\u53e3\u91cc\u8bf4\u8bdd\uff0c\u624b\u4e2d\u7684\u6298\u6247\u4e00\u4e3e\uff0c\u53cc\u6307\u631f\u770b\u6247\u67c4\uff0c\u548c\u90a3\u5c11\u5973\u521a\u624d\u4f7f\u7528\u9f13\u9524\u7684\u624b\u6cd5\u4e00\u6a21\u4e00\u6837\uff0c \u6247\u5934\u5411\u90a3\u5c11\u5973\u7684\u864e\u53e3\u6572\u4e0b\u6765\u3002 \u3000\u3000\u4f55\u8001\u5927\u773c\u770b\u5973\u513f\u53d7\u8fb1\uff0c\u660e\u77e5\u6253\u4e0d\u8fc7\u5bf9\u65b9\u7684\u4e09\u4e2a\u4eba\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u62fc\u4e00\u62fc\u4e86\u3002\u5c31\u5728\u201c\u516c\u5b50\u7237\u201d \u7684\u6247\u5934\u6572\u4e0b\u4e4b\u9645\uff0c\u4f55\u8001\u5927\u559d\u9053\uff1a\u201c\u5929\u4e0b\u6709\u8fd9\u6837\u6a2a\u86ee\u9738\u9053\u7684\u8bf7\u5ba2\u7684\u4e48\uff1f\u201d\u58f0\u5230\u4eba\u5230\uff0c\u4e94\u6307\u5982 \u94a9\uff0c\u500f\u5730\u5c31\u6293\u7740\u4e86\u90a3\u4e2a\u201c\u516c\u5b50\u7237\u201d\u7684\u6247\u5934\uff0c\u201c\u516c\u5b50\u7237\u201d\u7528\u529b\u4e00\u593a\uff0c\u201c\u556a\u201d\u7684\u4e00\u62dc\uff0c\u6298\u6247\u65ad\u4e3a \u4e24\u622a\u3002 \u3000\u3000\u90a3\u201c\u516c\u5b50\u7237\u201d\u4e5f\u662f\u597d\u751f\u4e86\u5f97\uff0c\u6298\u6247\u88ab\u593a\uff0c\u7acb\u5373\u4fbf\u662f\u53cd\u624b\u4e00\u638c\u3002\u4ed6\u867d\u662f\u5728\u53d7\u632b\u4e4b\u4f59\uff0c\u8fd9\u4e00 \u638c\u7684\u529b\u9053\u8fd8\u5f53\u771f\u4e0d\u5f31\uff0c\u53ea\u542c\u5f97\u638c\u98ce\u547c\u547c\uff0c\u6253\u5f97\u6c99\u98de\u77f3\u8d70\u3002 \u3000\u3000\u53cc\u638c\u76f8\u4ea4\uff0c\u201c\u516c\u5b50\u7237\u201d\u53ea\u89c9\u5f97\u81ea\u5df1\u8fd9\u4e00\u638c\u597d\u4f3c\u6253\u5230\u68c9\u82b1\u5806\u91cc\u4f3c\u7684\uff0c\u8f6f\u7ef5\u7ef5\u7684\u6beb\u4e0d\u53d7\u529b\u3002 \u5ffd\u542c\u5f97\u6709\u4eba\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d\u8bdd\u72b9\u672a\u4e86\u53ea\u542c\u5f97\u201c\u548b\u5693\u201d\u4e00\u58f0\uff0c\u8fd9\u201c\u516c\u5b50\u7237\u201d\u7684\u4e00\u6761\u5de6\u81c2 \u5df2\u662f\u7ed9\u4f55\u8001\u5927\u62d7\u8131\u4e86\u3002 \u3000\u3000\u539f\u6765\u4f55\u8001\u5927\u6df1\u77e5\u654c\u5f3a\u5df1\u5f31\uff0c\u8981\u60f3\u8131\u9669\uff0c\u975e\u5f97\u628a\u8fd9\u201c\u516c\u5b50\u7237\u201d\u6293\u4f5c\u4eba\u8d28\u4e0d\u53ef\u3002\u8fd9\u201c\u516c\u5b50 \u7237\u201d\u6b66\u529f\u4e0d\u5f31\uff0c\u4f55\u8001\u5927\u82e5\u662f\u548c\u4ed6\u786c\u6597\uff0c\u975e\u5f97\u5728\u4e8c\u4e8c\u4e09\u5341\u62db\u5347\u5916\uff0c\u4e0d\u6613\u8a00\u80dc\u3002\u4f46\u83ab\u8bf4\u4e8c\u4e09\u5341 \u62db\uff0c\u53ea\u8981\u5bb9\u4ed6\u8fc7\u5f97\u4e09\u62db\uff0c\u4ed6\u90a3\u4e24\u4e2a\u4fdd\u7f25\u770b\u51fa\u5c0f\u4e3b\u4eba\u62b5\u654c\u4e0d\u4f4f\u4fbf\u4f1a\u4e0a\u6765\u5e2e\u5fd9\u4e86\u3002 \u3000\u3000\u4f55\u8001\u5927\u6025\u4e2d\u751f\u667a\uff0c\u4f7f\u51fa\u5de7\u5999\u7684\u8bf1\u7740\uff0c\u4ea4\u638c\u4e4b\u9645\uff0c\u52b2\u529b\u5168\u655b\uff0c\u5f85\u5bf9\u65b9\u7684\u529b\u9053\u4f7f\u5f97\u8db3\u4e86\uff0c\u8fd9 \u624d\u84e6\u5730\u91cc\u771f\u529b\u4e00\u53d1\uff0c\u786c\u751f\u751f\u7684\u628a\u5bf9\u65b9\u7684\u624b\u81c2\u62d7\u8131\u4e86\u81fc\uff0c\u5c31\u5982\u7ef7\u7d27\u7684\u5f13\u5f26\u7a81\u7136\u65ad\u6298\u4e00\u822c\u3002\u8fd9 \u201c\u516c\u5b50\u7237\u201d\u672c\u9886\u867d\u7136\u4e0d\u5f31\uff0c\u5982\u662f\u7f3a\u4e4f\u5bf9\u654c\u7684\u7ecf\u9a8c\uff0c\u4f55\u51b5\u4f55\u8001\u5927\u8fd9\u4e00\u8bf1\u7740\uff0c\u4e5f\u662f\u6781\u4e3a\u5de7\u5999\u7684\u4e0a \u4e58\u529f\u592b\uff0c\u662f\u4ee5\u51b7\u4e0d\u9632\u5c31\u7740\u4e86\u9053\u513f\u3002 \u3000\u3000\u4f55\u8001\u5927\u4e00\u62db\u5f97\u624b\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u63c9\u8eab\u800c\u4e0a\uff0c\u63a5\u7740\u4fbf\u662f\u4e00\u62db\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u53ea\u8981\u7ed9\u4ed6\u6293\u7740\u8fd9 \u201c\u516c\u5b50\u7237\u201d\u7684\u7435\u7436\u9aa8\uff0c\u8fd9\u201c\u516c\u5b50\u7237\u201d\u4fbf\u51b3\u8ba1\u9003\u4e0d\u51fa\u4ed6\u7684\u638c\u63e1\uff0c\u90a3\u65f6\u654c\u65b9\u518d\u5f3a\uff0c\u4e5f\u662f\u5948\u4f55\u4ed6\u4e0d \u5f97\u7684\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u4f55\u8001\u5927\u867d\u7136\u662f\u52a8\u4f5c\u6781\u5feb\uff0c\u4e5f\u8fd8\u8fdf\u4e86\u4e00\u70b9\uff1b\u5c31\u5728\u4ed6\u7684\u624b\u6307\u582a\u582a\u8981\u6293\u5230\u8fd9\u201c\u516c\u5b50\u7237\u201d\u80a9 \u5934\u7684\u65f6\u5019\uff0c\u731b\u7136\u95f4\u4e00\u80a1\u5927\u529b\u63a8\u6765\uff0c\u903c\u5f97\u4f55\u8001\u5927\u53d8\u638c\u62db\u67b6\uff0c\u90a3\u201c\u516c\u5b50\u7237\u201d\u5df2\u662f\u7ed9\u4eba\u62c9\u5f00\u3002 \u3000\u3000\u903c\u9000\u4f55\u8001\u5927\u7684\u8fd9\u4e2a\u4eba\u6b63\u662f\u90a3\u4e2a\u8eab\u6750\u9b41\u6342\u7684\u79c3\u5934\u6c49\u5b50\uff0c\u4f55\u8001\u5927\u8fde\u7528\u4e09\u62db\u201c\u62c2\u4e91\u624b\u201d\uff0c\u610f\u6b32 \u4ee5\u67d4\u514b\u521a\uff0c\u4e0d\u6599\u7adf\u7136\u514b\u4ed6\u4e0d\u4f4f\u3002\u5bf9\u65b9\u7684\u638c\u529b\u604d\u82e5\u6392\u5c71\u5012\u6d77\u800c\u6765\uff0c\u4ed6\u7684\u201c\u62c2\u4e91\u624b\u201d\u4f7f\u51fa\u4e86\u4e0a\u4e58 \u5185\u529f\u201c\u5378\u201d\u5b57\u8bc0\uff0c\u4ecd\u662f\u96be\u4ee5\u5316\u89e3\uff0c\u53ea\u53ef\u4ee5\u52c9\u5f3a\u62db\u67b6\u3002 \u3000\u3000\u91d1\u9010\u6d41\u901a\u6653\u5404\u5bb6\u5404\u6d3e\u7684\u6b66\u529f\uff0c\u770b\u4e86\u51e0\u62db\uff0c\u5fc3\u4e2d\u4e5f\u662f\u5927\u5927\u5403\u60ca\u3002\u539f\u6765\u8fd9\u4e2a\u79c3\u5934\u53cc\u5b50\u7528\u7684\u7adf \u662f\u6b63\u5b97\u5c11\u6797\u6d3e\u7684\u5927\u529b\u91d1\u521a\u638c\u529f\u592b\uff01\u91d1\u521a\u638c\u4e43\u662f\u5929\u4e0b\u6700\u5a01\u731b\u7684\u638c\u529b\uff0c\u6b66\u5b66\u4e2d\u867d\u6709\u201c\u4ee5\u67d4\u514b\u521a\u201d \u4e4b\u8bf4\uff0c\u4f46\u90a3\u4e5f\u662f\u6307\u53cc\u65b9\u662f\u5728\u4f2f\u4ef2\u4e4b\u95f4\u7684\u5bf9\u624b\u800c\u8a00\uff0c\u5982\u4eca\u4f55\u8001\u5927\u7684\u529f\u529b\u663e\u7136\u4e0e\u5bf9\u65b9\u9887\u6709\u8ddd\u79bb\uff0c \u7109\u80fd\u5316\u89e3\u5f97\u4e86\uff1f \u3000\u3000\u628a\u201c\u516c\u5b50\u7237\u201d\u62c9\u5f00\u7684\u662f\u90a3\u4e2a\u76ee\u8574\u7cbe\u5149\u7684\u77ee\u5b50\uff0c\u4ed6\u63a5\u9a73\u65ad\u9aa8\u7684\u624b\u6cd5\u6781\u4e3a\u7eaf\u719f\uff0c\u8f6c\u773c\u95f4\u5c31\u628a \u201c\u516c\u5b50\u7237\u201d\u7684\u8131\u81fc\u63a5\u4e0a\u4e86\u3002\u8fd9\u624d\u5401\u4e86\u53e3\u6c14\uff0c\u5411\u201c\u516c\u5b50\u7237\u201d\u8bf7\u7f6a\u3002\u539f\u6765\u4ed6\u4eec\u6df1\u77e5\u8fd9\u516c\u5b50\u7237\u597d \u80dc\uff0c\u800c\u4ed6\u4eec\u4e5f\u6709\u81ea\u4fe1\u53ef\u4ee5\u5728\u201c\u516c\u5b50\u7237\u201d\u9047\u9669\u4e4b\u65f6\uff0c\u7ed9\u4ed6\u89e3\u6551\u3002\u4f46\u5374\u4e0d\u6599\u4f55\u8001\u5927\u4f7f\u4e86\u90a3\u4e48\u4e00\u62db \u5de7\u5999\u7684\u8bf1\u7740\uff0c\u867d\u7136\u4ecd\u662f\u5c06\u201c\u516c\u5b50\u7237\u201d\u6551\u4e86\u51fa\u6765\uff0c\u5374\u5df2\u662f\u4ee4\u4ed6\u5403\u4e86\u5927\u4e8f\u4e86\u3002 \u3000\u3000\u90a3\u201c\u516c\u5b50\u7237\u201d\u65ad\u81fc\u63a5\u597d\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u6c14\u51b2\u51b2\u5730\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u628a\u4f60\u8fd9\u4e2b\u5934\u6293\u5230\u6211\u7684\u624b\u4e2d\uff0c \u96be\u89e3\u6211\u5fc3\u5934\u4e4b\u6068\uff01\u201d\u90a3\u77ee\u5b50\u77e5\u9053\u5c0f\u4e3b\u4eba\u7684\u6b66\u529f\u8fdc\u5728\u90a3\u5c11\u5973\u4e4b\u4e0a\uff0c\u800c\u4f55\u8001\u5927\u53c8\u5df2\u7ecf\u7ed9\u4ed6\u7684\u540c\u4f34 \u6253\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u51b3\u4e0d\u80fd\u817e\u51fa\u624b\u6765\u52a9\u4ed6\u5973\u513f\uff0c\u662f\u4ee5\u653e\u5fc3\u8ba9\u90a3\u201c\u516c\u5b50\u7237\u201d\u4e0e\u5979\u76f8\u6597\u3002 \u3000\u3000\u8981\u77e5\u4ed6\u548c\u90a3\u4e2a\u79c3\u5934\u6c49\u5b50\u90fd\u662f\u6c5f\u6e56\u4e0a\u5927\u6709\u6765\u5934\u7684\u4eba\u7269\uff0c\u867d\u7136\u4ed6\u4eec\u73b0\u5728\u662f\u505a\u4e86\u8c6a\u95e8\u7684\u4fdd\u7f25\uff0c \u8eab\u4efd\u8fd8\u662f\u8981\u7ef4\u6301\u7684\uff0c\u975e\u5230\u4e07\u4e0d\u5f97\u5df2\u4e4b\u65f6\uff0c\u51b3\u4e0d\u613f\u610f\u6043\u5f3a\u6b3a\u5f31\uff0c\u4ee5\u4f17\u51cc\u5be1\u3002 \u3000\u3000\u90a3\u5c11\u5973\u6b63\u5728\u4e3a\u5979\u7236\u4eb2\u7740\u6025\uff0c\u53ea\u6068\u81ea\u5df1\u63d2\u4e0d\u8fdb\u624b\u53bb\uff0c\u90a3\u516c\u5b50\u7237\u5df2\u671d\u5979\u6251\u5230\u3002\u5c11\u5973\u5927\u6012\uff0c \u201c\u55d6\u201d\u7684\u62d4\u51fa\u4e00\u53e3\u67f3\u53f6\u5200\uff0c\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4eec\u64c5\u4f1a\u501a\u52bf\u51cc\u4eba\uff0c\u6211\u65a9\u4f60\u7684\u72d7\u722a\u5b50\uff01\u201d \u3000\u3000\u201c\u516c\u5b50\u7237\u201d\u624b\u8155\u4e00\u7ffb\uff0c\u6839\u672c\u5c31\u4e0d\u628a\u5979\u8fd9\u53e3\u67f3\u53f6\u5200\u653e\u5728\u773c\u5185\uff0c\u4e00\u7ffb\u4e00\u62ff\uff0c\u4fbf\u662f\u6b3a\u8eab\u76f4\u8fdb\uff0c \u6293\u90a3\u5c11\u5973\u7684\u864e\u53e3\uff0c\u5c11\u5973\u4e00\u5200\u5288\u7a7a\uff0c\u7693\u8155\u9669\u4e9b\u7ed9\u4ed6\u6293\u7740\u3002 \u3000\u3000\u201c\u516c\u5b50\u7237\u201d\u51fa\u624b\u4fbf\u80dc\u4e00\u62db\uff0c\u5fc3\u5934\u4e4b\u6c14\u6d88\u4e86\u51e0\u5206\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5c0f\u5a18\u5b50\uff0c\u4f60\u8fd9\u8bdd\u53ef\u662f\u8bf4\u5f97 \u4e0d\u5bf9\u4e86\u3002\u6211\u4eec\u4e00\u4e2a\u6253\u4f60\u4eec\u4e00\u4e2a\uff0c\u600e\u80fd\u8bf4\u662f\u501a\u52bf\u51cc\u4eba\uff1f\u54c8\u54c8\uff0c\u6211\u8fd8\u662f\u7a7a\u624b\u6597\u4f60\u7684\u5200\u5462\uff01\u201d \u3000\u3000\u5c11\u5973\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\u3001\u5374\u662f\u505a\u58f0\u4e0d\u5f97\uff0c\u53ea\u597d\u54ac\u7d27\u94f6\u7259\uff0c\u4e0e\u4ed6\u82e6\u6597\u3002\u53ef\u60dc\u5979\u7684\u6b66\u529f\u6bd5\u7adf\u662f\u76f8 \u5dee\u8fd8\u8fdc\uff0c\u4e00\u8def\u6cfc\u98ce\u5200\u6cd5\u5c55\u5f00\uff0c\u867d\u662f\u62db\u62db\u72e0\u8fa3\uff0c\u5374\u7adf\u7136\u5948\u4f55\u4e0d\u4e86\u5bf9\u65b9\u3002\u4e0d\u8fc7\u5341\u6570\u62db\uff0c\u4e1a\u5df2\u662f\u9669 \u8c61\u73af\u751f\u3002\u8fd8\u5e78\u4e8f\u90a3\u201c\u516c\u5b50\u7237\u201d\u521a\u521a\u63a5\u597d\u65ad\u81fc\uff0c\u53ea\u6709\u4e00\u6761\u624b\u81c2\u65b9\u4fbf\u4f7f\u7528\uff0c\u5426\u5219\u5979\u66f4\u96be\u62db\u67b6\u3002\u4f55 \u8001\u5927\u89c1\u5973\u513f\u9047\u9669\uff0c\u5fc3\u6025\u5982\u711a\uff0c\u53ef\u662f\u5728\u5bf9\u65b9\u7684\u638c\u529b\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u4ed6\u60f3\u8981\u8131\u8eab\u90fd\u96be\uff0c\u600e\u4e48\u53ef\u4ee5\u6551\u5f97 \u5973\u513f\uff1f\u4ed6\u5fc3\u91cc\u8d8a\u7740\u6025\uff0c\u624b\u4e0a\u7684\u62db\u6570\u5c31\u8d8a\u53d1\u4e71\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u662f\u65f6\u5019\u4e86\uff01\u201d\u6b63\u8981\u4e0a\u53bb\uff0c\u5ffd\u89c1\u6709\u4e2a\u76f8\u8bc6\u7684\u4eba\u98de\u6b65\u4e0a\u524d\uff0c\u6253\u4e86\u4e2a\u54c8\u54c8 \u8bf4\u9053\uff1a\u201c\u5f6d\u5be8\u4e3b\uff0c\u4eca\u65e5\u600e\u4e48\u6709\u8fd9\u6837\u597d\u7684\u5174\u81f4\u548c\u4eba\u6253\u67b6\uff1f\u4e3a\u7684\u662f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u53ef\u4ee5\u8bf4\u7ed9\u5144\u5f1f\u542c\u542c \u4e48\uff1f\u201d\u8fd9\u4e2a\u4eba\u6b63\u662f\u91d1\u9010\u6d41\u5728\u6e56\u4e2d\u76f8\u9047\u7684\u90a3\u4e2a\u5bab\u79c9\u85e9\u3002\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u4e14\u770b\u8fd9\u59d3\u5bab\u7684\u662f\u5e2e\u54ea\u4e00 \u8fb9\u3002\u201d \u3000\u3000\u4f55\u8001\u5927\u4e00\u542c\u5bab\u79c9\u85e9\u7684\u8bf4\u8bdd\uff0c\u58f0\u97f3\u9707\u8033\uff0c\u663e\u7136\u662f\u4e2a\u5185\u5bb6\u9ad8\u624b\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9 \u4e2a\u4eba\u662f\u548c\u4ed6\u4eec\u76f8\u8bc6\u7684\uff0c\u4ed6\u8fd9\u4e00\u6765\uff0c\u4e0d\u7528\u8bf4\u662f\u5e2e\u4ed6\u4eec\u7684\u4e86\u3002\u7f62\u4e86\uff0c\u7f62\u4e86\uff01\u4eca\u65e5\u6211\u7236\u5973\u53ea\u6015\u662f\u63d2 \u7ffc\u96be\u9003\u4e86\u3002\u201d \u3000\u3000\u6b8a\u4e0d\u77e5\u548c\u4ed6\u4ea4\u624b\u7684\u90a3\u4e2a\u79c3\u5934\u6c49\u5b50\u5403\u60ca\u66f4\u751a\uff0c\u8138\u4e0a\u9732\u51fa\u4e2a\u5c34\u5c2c\u7684\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u5bab\u9999\u4e3b\uff0c \u4ec0\u4e48\u98ce\u628a\u4f60\u5439\u5230\u8fd9\u513f\u6765\u4e86\uff1f\u4f60\u6b47\u4e00\u4f1a\u513f\uff0c\u5144\u5f1f\u5c31\u6765\u966a\u4f60\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u5bab\u79c9\u85e9\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u7684\u6027\u5b50\u6025\uff1f\u8fd9\u4e2a\u95f7\u846b\u82a6\u6ca1\u6253\u5f00\uff0c\u6211\u53ef\u662f\u6b47\u4e0d\u4e0b\u6765\u7684\u5475\uff01\u201d\u53e3\u91cc \u8bf4\u7740\u8bdd\uff0c\u773c\u775b\u53c8\u5df2\u671d\u7740\u90a3\u77ee\u5b50\u770b\u53bb\u3002 \u3000\u3000\u90a3\u77ee\u5b50\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5bab\u9999\u4e3b\uff0c\u6211\u529d\u4f60\u83ab\u7ba1\u95f9\u4e8b\u3002\u201d\u5bab\u79c9\u85e9\u7b11\u9053\uff1a\u201c\u539f\u6765\u8fde\u5be8\u4e3b\u4e5f\u5728\u8fd9 \u513f\uff0c\u771f\u662f\u5e78\u4f1a\u4e86\u3002\u600e\u4e48\uff0c\u8fd9\u4e2a\u4e8b\u662f\u7ba1\u4e0d\u5f97\u7684\u5417\uff1f\u4f46\u7a76\u7adf\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u60c5\uff0c\u4f60\u4e5f\u8be5\u8ba9\u6211\u77e5\u9053 \u554a\uff01\u201d\u90a3\u77ee\u5b50\u9053\uff1a\u201c\u6211\u8bf4\u7ba1\u4e0d\u5f97\u5c31\u7ba1\u4e0d\u5f97\uff01\u4f60\u60f3\u8981\u77e5\u9053\uff0c\u8fd9\u4e2a\u67b6\u6253\u5b8c\u4e86\uff0c\u6211\u966a\u4f60\u559d\u9152\u53bb\u3002\u201d \u3000\u3000\u5bab\u79c9\u85e9\u9053\uff1a\u201c\u559d\u9152\u4e0d\u6025\uff0c\u773c\u524d\u7684\u4e8b\u53ef\u662f\u7b49\u4e0d\u5f97\u7684\uff0c\u7b49\u4f60\u4eec\u8fd9\u4e00\u67b6\u6253\u5b8c\uff0c\u4eba\u5bb6\u7684\u5c0f\u59d1\u5a18\u53ef \u5c31\u8981\u5403\u4e86\u5927\u4e8f\u5566\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u51fa\u4e86\u5bab\u79c9\u85e9\u7684\u53e3\u6c14\uff0c\u5fc3\u4e2d\u4e00\u559c\uff0c\u60f3\u9053\uff1a\u201c\u6709\u8fd9\u59d3\u5bab\u7684\u51fa\u624b\uff0c\u5927\u7ea6\u53ef\u4ee5\u7528\u4e0d\u7740\u6211 \u4e86\u3002\u4f46\u542c\u4ed6\u7684\u8bf4\u8bdd\uff0c\u8fd9\u4e24\u4e2a\u4eba\u7adf\u662f\u4ec0\u4e48\u5be8\u4e3b\u8eab\u4efd\uff0c\u8fd9\u53ef\u5c31\u6709\u70b9\u5947\u602a\u4e86\u3002\u7eff\u6797\u4e2d\u7ad9\u5f97\u8d77\u6765\u7684\u811a \u8272\uff0c\u600e\u7684\u7ade\u4f1a\u7518\u5fc3\u53bb\u505a\u8c6a\u95e8\u7684\u9e70\u72ac\uff1f\u201d \u3000\u3000\u90a3\u77ee\u5b50\u542c\u4e86\u5bab\u79c9\u85e9\u8fd9\u51e0\u65ec\u8bdd\uff0c\u970d\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u6d53\u7709\u4e00\u626c\uff0c\u773c\u775b\u4e00\u8e6c\uff0c\u8bf4\u9053\uff1a\u201c\u5bab\u9999\u4e3b\uff0c \u4f60\u7a76\u7adf\u662f\u60f3\u8981\u600e\u6837\uff1f\u201d \u3000\u3000\u5bab\u79c9\u85e9\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u4eec\u4e0d\u8bf4\uff0c\u6211\u5c31\u4e0d\u77e5\u9053\u5417\uff1f\u4e0d\u8fc7\uff0c\u6211\u8981\u4f60\u4eec\u81ea\u5df1\u8bf4\u3002\u4f60\u8bf4\uff0c\u8fd9\u4e24\u4f4d\u8bf4 \u4e66\u7684\u7236\u5973\u7a76\u7adf\u6709\u4ec0\u4e48\u5730\u65b9\u5f97\u7f6a\u4e86\u4f60\uff1f\u8bf4\u51fa\u6765\u8ba9\u6211\u8bc4\u8bc4\u7406\uff0c\u5426\u5219\u2026\u2026\u201d \u3000\u3000\u90a3\u77ee\u5b50\u559d\u9053\uff1a\u201c\u5426\u5219\u600e\u6837\uff1f\u201d \u3000\u3000\u5bab\u79c9\u85e9\u7acb\u5373\u5e94\u58f0\u8bf4\u9053\uff1a\u201c\u5426\u5219\u8fd9\u4e2a\u95f2\u4e8b\u6211\u5bab\u67d0\u662f\u7ba1\u5b9a\u7684\u4e86\u3002\u201d \u3000\u3000\u7531\u4e8e\u5bab\u79c9\u85e9\u8fd9\u4e48\u4e00\u6765\uff0c\u8fd9\u4e2a\u59d3\u5f6d\u7684\u79c3\u5b50\u5fc3\u795e\u5927\u4e71\uff0c\u7ed9\u4f55\u8001\u5927\u6253\u6210\u4e86\u5e73\u624b\u3002\u90a3\u4e2a\u201c\u516c\u5b50 \u7237\u201d\u4e5f\u5206\u4e86\u5fc3\uff0c\u672c\u6765\u4e5f\u662f\u53ef\u4ee5\u65e9\u5c31\u6293\u5f97\u770b\u8fd9\u4e2a\u5c11\u5973\u7684\uff0c\u7565\u4e00\u5206\u795e\uff0c\u597d\u51e0\u6b21\u5e94\u8be5\u5f97\u624b\u7684\u90fd\u7ed9\u8fd9 \u5c11\u5973\u8eb2\u8fc7\u4e86\u3002 \u3000\u3000\u8fde\u3001\u5f6d\u4e8c\u4eba\u77e5\u9053\u5bab\u79c9\u85e9\u7684\u6765\u5934\uff0c\u5bf9\u4ed6\u4e0d\u80fd\u4e0d\u6709\u51e0\u5206\u987e\u5fcc\uff0c\u5373\u4f7f\u662f\u90a3\u59d3\u8fde\u7684\u77ee\u5b50\u6bd4\u8f83\u5f3a \u6a2a\uff0c\u4e5f\u8fd8\u4e0d\u6562\u592a\u8fc7\u653e\u8086\uff0c\u8fd9\u201c\u516c\u5b50\u7237\u201d\u5374\u662f\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u6012\u6c14\u4e00\u51b2\uff0c\u5c31\u9a82\u51fa\u6765\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c \u897f\uff0c\u4e5f\u914d\u6765\u7ba1\u4f60\u5bb6\u516c\u5b50\u7684\u4e8b\uff1f\u8fde\u5e08\u7236\uff0c\u628a\u4ed6\u62ff\u4e0b\uff01\u201d \u3000\u3000\u90a3\u59d3\u5f6d\u7684\u79c3\u5934\u6c49\u5b50\u8fde\u5fd9\u7ed9\u4e86\u90a3\u77ee\u5b50\u4e00\u4e2a\u773c\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u5b50\u6709\u6240\u4e0d\u77e5\uff0c\u8fd9\u4f4d\u5bab\u5148\u751f\u662f\u7ea2 \u7f28\u4f1a\u7684\u9999\u4e3b\uff0c\u563f\uff0c\u563f\uff0c\u90fd\u662f\u4e00\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff01\u201d \u3000\u3000\u5bab\u79c9\u85e9\u51b7\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u7ebf\u4e0a\u7684\u670b\u53cb\uff0c\u6211\u53ef\u9ad8\u6500\u4e0d\u8d77\uff01\u563f\uff0c\u563f\uff0c\u542c\u8bf4\u4f60\u4eec\u4e24\u4f4d\u5927\u5be8\u4e3b\u505a\u4e86 \u66f9\u5bb6\u7684\u4fdd\u7f25\uff0c\u529f\u540d\u5bcc\u8d35\uff0c\u662f\u6307\u65e5\u53ef\u5f85\u4e86\u54ea\uff01\u8fd9\u4f4d\u60f3\u5fc5\u5c31\u662f\u66f9\u516c\u5b50\u5427\uff1f\u5a01\u98ce\u597d\u5927\u554a\uff01\u8fde\u5be8\u4e3b\uff0c \u516c\u5b50\u7237\u6709\u4ee4\u4e86\uff0c\u4f60\u4e0a\u6765\u62ff\u6211\u554a\uff01\u201d \u3000\u3000\u8fde\u3001\u5f6d\u4e8c\u4eba\u9762\u4e0a\u4e00\u9635\u7ea2\u4e00\u9635\u9752\uff0c\u539f\u6765\u90a3\u4e2a\u79c3\u5934\u6c49\u5b50\u540d\u53eb\u5f6d\u5de8\u5cf0\uff0c\u90a3\u4e2a\u77ee\u5b50\u540d\u53eb\u8fde\u57ce\u864e\uff0c \u90fd\u66fe\u7ecf\u662f\u72ec\u9738\u4e00\u65b9\u7684\u5c71\u5be8\u4e4b\u4e3b\u3002 \u3000\u3000\u90a3\u201c\u516c\u5b50\u7237\u201d\u4e0d\u77e5\u5bab\u79c9\u85e9\u7684\u5389\u5bb3\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u7ea2\u7f28\u4f1a\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u5c0f\u5c0f\u4e00\u4e2a\u6c5f\u6e56\u4e0a\u7684 \u5e2e\u4f1a\u5c31\u80fd\u5413\u5012\u4e86\u6211\uff1f\u597d\u5440\uff0c\u4f60\u6562\u5728\u6211\u7684\u9762\u524d\u653e\u8086\uff0c\u6211\u5148\u8981\u4e86\u4f60\u7684\u8111\u888b\uff01\u201d\u4ed6\u6c14\u607c\u8fde\u57ce\u864e\u4e0d\u80af \u4e0a\u524d\u62ff\u4eba\uff0c\u4e00\u638c\u903c\u5f00\u4e86\u90a3\u4e2a\u5c11\u5973\uff0c\u81ea\u5df1\u5c31\u8dd1\u8fc7\u53bb\u8981\u6253\u5bab\u79c9\u85e9\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u516c\u5b50\u7237\u52b3\u795e\uff0c\u4f60\u8981\u6211\u7684\u8111\u888b\u662f\u5417\uff1f\u6211\u5949\u9001\u5c31\u662f\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c \u201c\u516c\u5b50\u7237\u201d\u5df2\u662f\u5411\u4ed6\u6251\u5230\uff0c\u8fde\u57ce\u864e\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4e0d\u53ef\uff01\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u9876\u5e3d\u5b50\u7a81\u7136\u4ece\u5bab\u79c9\u85e9\u7684\u624b\u4e2d\u98de\u4e86\u51fa\u53bb\uff0c\u539f\u6765 \u662f\u4ed6\u8131\u4e86\u5934\u4e0a\u7684\u6be1\u5e3d\u5f53\u4f5c\u6697\u5668\u6253\u51fa\uff0c\u4e0e\u6b64\u540c\u65f6\uff0c\u8fde\u57ce\u864e\u7684\u4e00\u67c4\u98de\u5200\u4ea6\u5df2\u98de\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u52b2\u98ce\u6251\u9762\uff0c\u201c\u516c\u5b50\u7237\u201d\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u4f4e\u5934\u3002\u53ea\u542c\u5f97\u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u9876\u6be1\u5e3d\u7ed9\u8fde\u57ce\u864e \u7684\u98de\u5200\u5288\u4e3a\u4e24\u534a\u3002\u4f46\u90a3\u628a\u98de\u5200\u4e5f\u7ed9\u6be1\u5e3d\u61c2\u5f97\u6539\u4e86\u65b9\u5411\uff0c\u659c\u98de\u51fa\u53bb\uff0c\u4e0e\u90a3\u4e24\u7247\u6be1\u5e3d\u540c\u65f6\u5760\u5730\u3002 \u4ee5\u6be1\u5e3d\u800c\u80fd\u649e\u843d\u98de\u5200\uff0c\u56e0\u6b64\u6be1\u5e3d\u867d\u662f\u7ed9\u5288\u4e3a\u4e24\u534a\uff0c\u4f46\u5bab\u79c9\u85e9\u7684\u529f\u529b\u5219\u663e\u7136\u662f\u5728\u8fde\u57ce\u864e\u4e4b\u4e0a\u3002 \u3000\u3000\u8fd8\u6709\u66f4\u4ee4\u4eba\u5403\u60ca\u7684\u662f\uff0c\u201c\u516c\u5b50\u7237\u201d\u4e00\u60ca\u8fc7\u540e\uff0c\u5ffd\u89c9\u5934\u76ae\u6c81\u51c9\uff0c\u7528\u624b\u4e00\u6478\uff0c\u8fd9\u624d\u53d1\u89c9\u4e00\u5927 \u7247\u5934\u53d1\u5df2\u7ed9\u6be1\u5e3d\u201c\u94f2\u201d\u53bb\uff0c\u6b64\u65f6\u5934\u53d1\u6b63\u5728\u4ed6\u7684\u9762\u524d\u7eb7\u7eb7\u843d\u4e0b\uff0c\u8981\u4e0d\u662f\u6709\u8fde\u57ce\u864e\u90a3\u67c4\u98de\u5200\uff0c\u53ca \u65f6\u6253\u843d\u6be1\u5e3d\uff0c\u634e\u89e3\u4e86\u5b83\u7684\u52b2\u9053\uff0c\u53ea\u6015\u8fde\u4ed6\u7684\u5934\u76ae\u4e5f\u8981\u88ab\u201c\u94f2\u201d\u53bb\u4e00\u5c42\uff01 \u3000\u3000\u201c\u516c\u5b50\u7237\u201d\u60ca\u4e0a\u52a0\u60ca\uff0c\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u8fde\u5fd9\u9000\u8fc7\u4e00\u65c1\u3002\u5bab\u79c9\u85e9\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u66f9\u5bb6\u7684 \u8001\u7956\u5b97\u66f9\u5b5f\u5fb7\u5272\u53d1\u4ee3\u9996\uff0c\u6211\u5982\u4eca\u6548\u6cd5\u4f60\u5bb6\u7684\u8001\u7956\u5b97\uff0c\u4ee5\u5e3d\u4ee3\u9996\uff0c\u53ef\u60dc\u4f60\u8fd9\u4f4d\u516c\u5b50\u7237\u5374\u8981\u4e0d\u4e86 \u6211\u7684\u5047\u8111\u888b\uff01\u201d\u8fd9\u201c\u516c\u5b50\u7237\u201d\u7ed9\u4ed6\u5927\u52a0\u5632\u8bbd\uff0c\u5374\u54ea\u91cc\u8fd8\u6562\u4f5c\u58f0\uff1f\u6b64\u65f6\u53cc\u65b9\u662f\u5df2\u6495\u7834\u4e86\u9762\u76ae\uff0c \u8fde\u57ce\u864e\u4e5f\u4e0b\u4e0d\u4e86\u53f0\u4e86\u3002\u94ee\u94ee\u4e24\u58f0\uff0c\u8fde\u57ce\u864e\u62d4\u51fa\u4e86\u5224\u5b98\u7b14\uff0c\u53cc\u7b14\u4ea4\u51fb\uff0c\u559d\u9053\uff1a\u201c\u59d3\u5bab\u7684\uff0c\u6211\u53ea \u662f\u770b\u5728\u7ea2\u7f28\u4f1a\u7684\u4efd\u4e0a\uff0c\u4e0d\u60f3\u4e0e\u4f60\u6293\u7834\u9762\u5b50\uff0c\u4f60\u4ee5\u4e3a\u6211\u5f53\u771f\u6015\u4e86\u4f60\u4e48\uff1f\u201d \u3000\u3000\u5bab\u79c9\u85e9\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u90a3\u4e48\u54b1\u4eec\u628a\u8bdd\u5148\u8bf4\u660e\u4e86\u3002\u4eca\u65e5\u4e4b\u4e8b\u4e0e\u7ea2\u7f28\u4f1a\u65e0\u5173\uff0c\u53ea\u662f\u6211\u5bab\u67d0 \u8def\u89c1\u4e0d\u5e73\uff0c\u8981\u627e\u4f60\u7684\u6666\u6c14\uff0c\u4f60\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u8fde\u57ce\u864e\u5927\u6012\u9053\uff1a\u201c\u5bab\u79c9\u85e9\uff0c\u4f60\uff0c\u4f60\u6b3a\u4eba\u592a\u751a\uff01\u597d\u5440\uff0c\u90a3\u54b1\u4eec\u5c31\u6bd4\u5212\u6bd4\u5212\uff0c\u8c01\u4e5f\u4e0d\u5fc5\u7275\u8fde \u4efb\u4f55\u4e00\u65b9\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5df2\u662f\u811a\u8e0f\u6d2a\u95e8\uff0c\u53cc\u7b14\u52b2\u63d2\u3002\u9ad8\u624b\u6bd4\u6597\uff0c\u5f88\u5c11\u4e00\u51fa\u624b\u5c31\u4ece\u6b63\u9762\u653b\u51fb \u7684\uff0c\u8fde\u57ce\u864e\u5206\u660e\u662f\u8981\u6fc0\u6012\u5bab\u79c9\u85e9\uff0c\u4ee5\u4fbf\u4ece\u4e2d\u53d6\u5229\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u5251\u672f\u4f55\u7b49\u7cbe\u5999\uff0c\u559d\u58f0\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u4e00\u62db\u201c\u591c\u6218\u516b\u65b9\u201d\uff0c\u5251\u5149\u5df1\u662f\u56db\u9762\u94fa\u5f00\u3002\u4ed6 \u672c\u6765\u4ee5\u4e3a\u53ef\u4ee5\u6253\u843d\u8fde\u57ce\u864e\u7684\u5224\u5b98\u7b14\uff0c\u53ef\u662f\u5728\u4e00\u7247\u94ee\u94ee\u4e4b\u58f0\u8fc7\u540e\uff0c\u8fde\u57ce\u864e\u7684\u90a3\u5bf9\u5224\u5b98\u7b14\u4ecd\u662f\u5728 \u5251\u5149\u4e4b\u4e2d\u6a2a\u4f38\u8fc7\u6765\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u5fc3\u5934\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u8fd9\u77ee\u5b50\u8fd9\u4e48\u6a2a\uff0c\u8fde\u5bb6\u7684\u5224\u5b98\u7b14\u70b9\u7a74\uff0c\u679c\u7136\u662f\u5929\u4e0b\u65e0 \u53cc\uff01\u201d\u4ed6\u660e\u77e5\u81ea\u5df1\u7684\u529f\u529b\u80dc\u8fc7\u5bf9\u65b9\uff0c\u4f46\u56e0\u4e3a\u6478\u4e0d\u900f\u5bf9\u65b9\u7684\u70b9\u7a74\u7b14\u6cd5\uff0c\u53ea\u80fd\u8c28\u614e\u4e3a\u5148\uff0c\u4f7f\u51fa\u4e00 \u8def\u653b\u4e2d\u5e26\u5b88\u7684\u5251\u6cd5\uff0c\u8bb8\u591a\u72e0\u8fa3\u7684\u6740\u624b\u62db\u6570\uff0c\u90fd\u4e0d\u6562\u4f7f\u7528\u3002\u4f46\u867d\u7136\u5982\u6b64\uff0c\u5bab\u79c9\u85e9\u7684\u5251\u6cd5\u4e5f\u8fd8\u662f \u6c5f\u6e56\u7f55\u89c1\u7684\u4e0a\u4e58\u5251\u6cd5\uff0c\u800c\u4e14\u529f\u529b\u80dc\u8fc7\u5bf9\u65b9\uff0c\u56e0\u6b64\u8fd8\u662f\u7a0d\u5fae\u5360\u4e86\u4e00\u70b9\u4e0a\u98ce\u3002\u4e24\u4eba\u4e92\u6709\u653b\u5b88\uff0c\u5251 \u5149\u7b14\u5f71\uff0c\u6253\u5f97\u96be\u89e3\u96be\u5206\uff0c\u8f6c\u773c\u95f4\u6253\u4e86\u4e09\u4e94\u5341\u62db\u3002 \u3000\u3000\u91d1\u9010\u6d41\u89c1\u4e86\u8fde\u57ce\u864e\u7684\u7b14\u6cd5\uff0c\u5fc3\u4e2d\u4e5f\u662f\u84e6\u5730\u4e00\u60ca\uff0c\u201c\u8fd9\u8def\u7b14\u6cd5\u597d\u719f\uff0c\u662f\u5728\u54ea\u91cc\u89c1\u8fc7\u7684 \u5462\uff1f\u201d\u4e0d\u77e5\u4e0d\u89c9\u5c31\u4ece\u9690\u853d\u4e4b\u5904\u8d70\u4e86\u51fa\u6765\uff0c\u8d70\u8fd1\u53bb\u4ed4\u7ec6\u89c2\u770b\u3002 \u3000\u3000\u770b\u4e86\u4e00\u4f1a\uff0c\u91d1\u9010\u6d41\u604d\u7136\u5927\u609f\uff1a\u201c\u539f\u6765\u662f\u56db\u7b14\u70b9\u516b\u8109\u7684\u7b14\u6cd5\uff0c\u4e0d\u8fc7\u4ed6\u53ea\u6709\u4e00\u5bf9\u5224\u5b98\u7b14\uff0c\u5982 \u662f\u4f7f\u4e0d\u5b8c\u5168\u3002\u602a\u4e0d\u5f97\u6211\u4e00\u4e0b\u5b50\u770b\u5b83\u4e0d\u51fa\u3002\u201d \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u7684\u7236\u4eb2\u91d1\u4e16\u9057\u5728\u4e8c\u5341\u5e74\u524d\uff0c\u66fe\u7ecf\u5728\u5317\u4eac\u4f1a\u8fc7\u4e00\u5bf9\u64c5\u4e8e\u70b9\u7a74\u7684\u5b6a\u751f\u5144\u5f1f\uff0c\u6b66\u6797 \u4e16\u5bb6\u7684\u8fde\u57ce\u58c1\u548c\u8fde\u57ce\u7389\u3002\u4ed6\u4eec\u4ee5\u56db\u652f\u5224\u5b98\u7b14\u5144\u5f1f\u914d\u5408\uff0c\u80fd\u5728\u4e00\u62db\u4e4b\u5185\uff0c\u70b9\u5bf9\u65b9\u7684\u5947\u7ecf\u516b\u8109\uff0c \u5389\u5bb3\u65e0\u6bd4\u3002\u91d1\u4e16\u9057\u8d77\u521d\u8fd8\u5f88\u5403\u4e86\u4ed6\u4eec\u7684\u4e8f\uff0c\u540e\u6765\uff0c\u5b66\u4f1a\u4e86\u4ed6\u4eec\u56db\u7b14\u70b9\u516b\u8109\u7684\u62db\u6570\uff0c\u8fd9\u624d\u628a\u4ed6 \u4eec\u6253\u8d25\u4e86\u3002 \u3000\u3000\u8fd9\u4e2a\u8fde\u57ce\u864e\u662f\u4ed6\u4eec\u7684\u5802\u5f1f\uff0c\u4f46\u5e74\u9f84\u76f8\u5dee\u5f88\u8fdc\uff0c\u4ed6\u7684\u4e24\u4e2a\u5802\u5144\u5982\u4eca\u5df2\u662f\u5e74\u8fc7\u516d\u65ec\uff0c\u4ed6\u5219\u8fd8 \u6ca1\u6709\u5230\u56db\u5341\u4e00\u5c81\u3002\u8fde\u57ce\u58c1\u3001\u8fde\u57ce\u7389\u53d7\u4e86\u91d1\u4e16\u9057\u632b\u8d25\u4e4b\u540e\uff0c\u65e9\u5df2\u5c01\u7b14\u95ed\u95e8\u3002\u8fde\u5bb6\u5b50\u5f1f\u4e4b\u4e2d\uff0c\u53ea \u6709\u8fde\u57ce\u864e\u5b66\u6210\u4e86\u5bb6\u4f20\u7edd\u6280\u3002\u53ef\u662f\u6ca1\u4eba\u548c\u4ed6\u914d\u5408\uff0c\u4ed6\u53ea\u80fd\u7528\u53cc\u7b14\u6765\u70b9\u56db\u8109\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5f97\u8fc7\u7236\u4eb2\u7684\u8bb2\u89e3\uff0c\u867d\u7136\u672a\u66fe\u7cbe\u7814\u8fd9\u201c\u56db\u7b14\u70b9\u516b\u8109\u201d\u7684\u529f\u592b\uff0c\u5374\u662f\u61c2\u5f97\u5176\u4e2d\u7684\u5de7 \u5999\u3002\u8fde\u57ce\u864e\u7684\u7b14\u6cd5\u4f7f\u5f97\u4e0d\u5b8c\u5168\uff0c\u7ed9\u4ed6\u770b\u51fa\u4e86\u4e0d\u5c11\u7834\u7efd\u3002\u53ef\u662f\u4e5f\u6709\u4e00\u4e9b\u7cbe\u5999\u7684\u53d8\u5316\u662f\u4ed6\u672a\u66fe\u5b66 \u8fc7\u7684\uff0c\u6b64\u65f6\u7559\u5fc3\u89c2\u770b\uff0c\u65b9\u59cb\u5fc3\u9886\u795e\u4f1a\u3002 \u3000\u3000\u91d1\u9010\u6d41\u770b\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u8d8a\u8d70\u8d8a\u8fd1\u3002\u8fde\u57ce\u864e\u3001\u5f6d\u5de8\u5cf0\u4e0d\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u4eba\uff0c\u53ea\u662f \u89c9\u5f97\u8fd9\u5c11\u5e74\u5927\u80c6\u51fa\u4e4e\u4ed6\u4eec\u7684\u610f\u5916\u800c\u5df2\uff0c\u5e76\u4e0d\u600e\u6837\u653e\u5728\u5fc3\u4e0a\uff0c\u5bab\u79c9\u85e9\u89c1\u4e86\u4ed6\uff0c\u5374\u662f\u4e0d\u7981\u5927\u5403\u4e00 \u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u82e5\u662f\u8bb0\u7740\u593a\u5b9d\u4e4b\u4ec7\uff0c\u6765\u627e\u6211\u7684\u6666\u6c14\uff0c\u90a3\u5c31\u7cdf\u4e86\uff01\u201d \u3000\u3000\u5f6d\u5de8\u5cf0\u7684\u672c\u9886\u8fdc\u5728\u90a3\u4f55\u8001\u5927\u4e4b\u4e0a\uff0c\u521d\u65f6\u4ed6\u987e\u5fcc\u7ea2\u7f28\u4f1a\u5bab\u79c9\u85e9\u51fa\u5934\u5e72\u4e00\u9884\uff0c\u4ed6\u7684\u5fc3\u795e\u6709\u70b9 \u4e0d\u5b81\u3002\u4f46\u6b64\u9645\uff0c\u53cc\u65b9\u5df2\u7ecf\u6495\u7834\u4e86\u9762\uff0c\u4ed6\u5012\u662f\u5b9a\u4e0b\u6765\u4e86\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u59d3\u5bab\u7684\u65e2\u7136\u628a\u4e8b\u60c5\u63fd \u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u6211\u53ea\u662f\u548c\u4ed6\u4f5c\u5bf9\uff0c\u60f3\u6765\u7ea2\u7f28\u4f1a\u4e5f\u4e0d\u4f1a\u4e3a\u4e86\u4e00\u4e2a\u9999\u4e3b\uff0c\u4fbf\u6765\u5927\u4e3e\u5bfb\u4ec7\u3002\u597d\uff0c\u4e14\u5148 \u628a\u4ed6\u6253\u8d25\u518d\u8bf4\u3002\u201d\u638c\u6cd5\u4e00\u7d27\uff0c\u4f55\u8001\u5927\u767b\u65f6\u5e94\u4ed8\u4e0d\u4e86\u3002\u201c\u4e52\u201d\u7684\u4e00\u58f0\uff0c\u4e2d\u4e86\u4e00\u638c\uff0c\u7ed9\u5f6d\u5de8\u5cf0\u51fb \u9000\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u53e3\u89d2\u6d41\u51fa\u8840\u6c34\u3002\u90a3\u5c11\u5973\u8fde\u5fd9\uff0c\u4e0a\u6765\u5c06\u5979\u7236\u4eb2\u6276\u7a33\uff0c\u60ca\u95ee\uff1a\u201c\u7239\uff0c\u4f60\u600e\u4e48 \u5566\uff1f\u201d \u3000\u3000\u4f55\u8001\u5927\u5598\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u51e4\u513f\uff0c\u4f60\u5feb\u8d70\u5427\uff01\u201d\u4ed6\u7ed9\u5f6d\u5de8\u5cf0\u4e00\u638c\u6253\u843d\u4e86\u4e24\u9f7f\u5927 \u7259\uff0c\u4f46\u4f24\u5f97\u8fd8\u4e0d\u7b97\u91cd\u3002\u4e0d\u8fc7\u90a3\u201c\u516c\u5b50\u7237\u201d\u5c1a\u5728\u4e00\u65c1\u864e\u89c6\u7708\u7708\uff0c\u4f55\u8001\u5927\u6015\u5973\u513f\u7ed9\u4ed6\u6293\u53bb\uff0c\u6545\u800c \u50ac\u4fc3\u5973\u513f\u5feb\u8d70\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u5462\uff1f\u201d\u4f55\u8001\u5927\u9053\uff1a\u201c\u50bb\u4e2b\u5934\uff0c\u7239\u7239\u54ea\u6709\u8d70\u7684\u9053\u7406\uff1f\u201d\u8981\u77e5\u4f55\u8001\u5927 \u662f\u4e2a\u6ce8\u91cd\u6c5f\u6e56\u9053\u4e49\u7684\u6c49\u5b50\uff0c\u5bab\u79c9\u85e9\u4e0e\u4ed6\u7d20\u4e0d\u76f8\u8bc6\u5c1a\u4e14\u4e3a\u4ed6\u6253\u62b1\u4e0d\u5e73\uff0c\u4ed6\u5c82\u80fd\u629b\u4e0b\u4e86\u5bab\u79c9\u85e9\u4e00 \u8d70\u4e86\u4e8b\u3002 \u3000\u3000\u53ef\u662f\u4f55\u8001\u5927\u867d\u7136\u6ca1\u53d7\u91cd\u4f24\uff0c\u6076\u6597\u4e86\u4e00\u573a\uff0c\u6c14\u529b\u4ea6\u5df2\u5dee\u4e0d\u591a\u7528\u5c3d\u4e86\u3002\u4ed6\u5598\u8fc7\u53e3\u6c14\uff0c\u4fbf\u8981\u4e0a \u53bb\uff0c\u521a\u8fc8\u51fa\u4e00\u6b65\uff0c\u4e0d\u89c9\u53c8\u662f\u6c14\u5598\u5401\u5401\u3002 \u3000\u3000\u5f6d\u5de8\u5ce8\u4e00\u638c\u51fb\u9000\u4e86\u4f55\u8001\u5927\uff0c\u628a\u4ed6\u4eec\u7236\u5973\u5f53\u4f5c\u56ca\u4e2d\u4e4b\u7269\uff0c\u5df2\u662f\u65e0\u987b\u518d\u52a0\u7406\u4f1a\uff0c\u5f53\u4e0b\uff0c\u53cc\u638c \u4e00\u9519\uff0c\u4fbf\u5411\u5bab\u79c9\u85e9\u5954\u53bb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u59d3\u5bab\u7684\uff0c\u4f60\u8fd8\u8981\u7ba1\u8fd9\u4e2a\u95f2\u4e8b\u5417\uff1f\u201d \u3000\u3000\u5b98\u79c9\u85e9\u53cc\u7709\u4e00\u8f69\uff0c\u8bf4\u9053\uff1a\u201c\u5e9f\u8bdd\u5c11\u8bf4\uff0c\u5e76\u80a9\u5b50\u4e0a\u5427\uff01\u201d\u5f6d\u5de8\u8776\u6012\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u65e2\u7136\u4e0d \u628a\u6211\u4eec\u5144\u5f1f\u653e\u5728\u773c\u5185\uff0c\u6211\u4eec\u4e5f\u53ea\u597d\u6210\u5168\u4f60\u4e86\uff01\u201d\u5355\u638c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u201c\u547c\u201d\u7684\u4fbf\u5288\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u4e43\u662f\u5c11\u6797\u5bfa\u201c\u5927\u529b\u91d1\u521a\u638c\u201d\u7684\u7cbe\u534e\u6240\uff0c\u5728\u638c\u529b\u6240\u53ca\uff0c\u5251\u5149\u6d41\u6563\uff0c\u8fde\u57ce\u864e\u7684\u5224\u5b98\u7b14 \u7acb\u5373\u4e58\u865a\u800c\u5165\uff0c\u70b9\u5230\u4e86\u5bab\u79c9\u85e9\u7684\u9762\u95e8\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u8eab\u5f62\u4f0a\u4f3c\u98ce\u4e2d\u4e4b\u70db\uff0c\u6643\u4e86\u51e0\u6643\u3002\u811a\u6b65\u4e5f\u662f\u8e09\u8e09\u8dc4\u8dc4\u7684\u597d\u4f3c \u9189\u6c49\u4e00\u822c\u3002\u84e6\u5730\u559d\u9053\uff1a\u201c\u770b\u5251\uff01\u201d\u4e00\u4e2a\u76d8\u65cb\uff0c\u5251\u5149\u5982\u7ec3\uff0c\u6307\u5230\u4e86\u5f6d\u5de8\u5cf0\u7684\u80f8\u53e3\uff0c\u5f6d\u5de8\u5cf0\u4ee5\u4e3a \u4ed6\u5df2\u7ecf\u652f\u6301\u4e0d\u4f4f\u4e86\uff0c\u6599\u4e0d\u5230\u4ed6\u7684\u5251\u6cd5\u5982\u6b64\u53e4\u602a\uff0c\u7a81\u7136\u5c31\u6740\u4e86\u5230\u6765\uff0c\u51b7\u4e0d\u53ca\u9632\uff0c\u51e0\u4e4e\u7ed9\u4ed6\u523a \u7740\u3002 \u3000\u3000\u5f6d\u5de8\u5cf0\u6325\u8896\u4e00\u62c2\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u8896\u5b50\u524a\u53bb\u4e86\u4e00\u5e45\u3002\u5f6d\u5de8\u5cf0\u9000\u5f00\u4e24\u6b65\uff0c\u8fd9\u624d\u6709\u8db3\u591f\u7684\u5730 \u65b9\u53d1\u51fa\u5927\u529b\u91d1\u521a\u638c\uff0c\u518d\u6b21\u628a\u5bab\u79c9\u85e9\u903c\u9000\u3002 \u3000\u3000\u539f\u6765\u5bab\u79c9\u85e9\u7528\u7684\u4e43\u662f\u201c\u9189\u516b\u4ed9\u201d\u5251\u6cd5\uff0c\u770b\u4f3c\u6447\u6447\u6b32\u5760\uff0c\u5176\u5b9e\u6b63\u662f\u548c\u8fd9\u4e00\u8def\u5251\u6cd5\u914d\u5408\u7684\u3002 \u53ea\u89c1\u4ed6\u4e1c\u6307\u897f\u5212\uff0c\u811a\u6b65\u8e09\u8dc4\uff0c\u6253\u8d77\u6765\u4e0d\u6210\u7ae0\u6cd5\uff0c\u6bcf\u4e00\u62db\u5374\u90fd\u662f\u5947\u8be1\u83ab\u6d4b\u7684\u4e0a\u4e58\u5251\u672f\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u5bf9\u65b9\u4e5f\u90fd\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u5bab\u79c9\u85e9\u4ed7\u7740\u8fd9\u5957\u5947\u8be1\u7684\u201c\u9189\u516b\u4ed9\u201d\u5251\u6cd5\uff0c\u53ef\u4ee5\u5413\u963b\u4e00 \u65f6\uff0c\u7a76\u7adf\u4e0d\u80fd\u4e00\u76f4\u652f\u6301\u4e0b\u53bb\u3002\u4e8c\u4e09\u5341\u62db\u8fc7\u540e\uff0c\u53c8\u7ed9\u5bf9\u65b9\u653b\u5f97\u4ed6\u9669\u8c61\u73af\u751f\u3002 \u3000\u3000\u4f55\u8001\u5927\u54ac\u4e86\u54ac\u7259\uff0c\u62fc\u7740\u8c41\u4e86\u6027\u547d\uff0c\u51b2\u4e0a\u524d\u53bb\uff0c\u90a3\u5c11\u5973\u77e5\u9053\u7236\u4eb2\u7684\u6027\u5b50\uff0c\u77e5\u9053\u662f\u51b3\u8ba1\u963b\u62e6 \u4e0d\u4f4f\u7684\u4e86\uff0c\u5f53\u4e0b\u4e5f\u54ac\u4e86\u54ac\u7259\uff0c\u63d0\u8d77\u4e86\u67f3\u53f6\u5200\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u8981\u6b7b\u54b1\u4eec\u7236\u5973\u4e5f\u6b7b\u5728\u4e00\u8d77\uff01\u201d \u3000\u3000\u5f6d\u5de8\u5cf0\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff0c\u8d76\u7740\u53bb\u89c1\u960e\u738b\u4e48\uff1f\u522b\u5fd9\uff0c\u522b\u5fd9\uff0c\u7b49 \u4f1a\u513f\u6211\u81ea\u4f1a\u62db\u547c\u4f60\u4eec\u3002\u201d\u4ed6\u5728\u548c\u5bab\u79c9\u85e9\u6fc0\u6218\u4e4b\u4e2d\uff0c\u968f\u624b\u5411\u90a3\u4e24\u7236\u5973\u6240\u5728\u7684\u65b9\u5411\u53d1\u51fa\u4e00\u638c\uff0c\u4f55 \u8001\u5927\u53c8\u4e0d\u7981\u8e09\u8e09\u8dc4\u8dc4\u5730\u9000\u4e86\u51e0\u6b65\uff0c\u5e78\u597d\u8fd9\u6b21\u5f6d\u5de8\u5cf0\u662f\u5728\u5168\u795e\u5bf9\u4ed8\u5bab\u79c9\u85e9\uff0c\u5411\u4ed6\u4eec\u6240\u53d1\u7684\u4e00 \u638c\uff0c\u4e0d\u8fc7\u662f\u4f59\u6ce2\u6240\u53ca\u800c\u5df2\u3002\u4f55\u8001\u5927\u56e0\u6b64\u624d\u6ca1\u6709\u4f24\u4e0a\u52a0\u4f24\u3002 \u3000\u3000\u4e0d\u8fc7\u5728\u5c61\u7ecf\u632b\u6298\u4e4b\u540e\uff0c\u4f55\u8001\u5927\u4ea6\u5df2\u77e5\u9053\u81ea\u5df1\u662f\u6709\u5fc3\u65e0\u529b\u4e86\u3002\u957f\u957f\u5730\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u90a3\u201c\u516c\u5b50\u7237\u201d\u8822\u8822\u6b32\u52a8\uff0c\u4f46\u4ed6\u66fe\u7ecf\u9886\u6559\u8fc7\u4f55\u8001\u5927\u7684\u5389\u5bb3\uff0c\u6b64\u65f6\u867d\u7136\u770b\u51fa\u4f55\u8001\u5927\u5df2\u662f\u5f3a\u5f29 \u4e4b\u672a\uff0c\u5fc3\u4e2d\u4ecd\u662f\u4e0d\u65e0\u4ff1\u602f\uff0c\u4e00\u65f6\u4e0d\u6562\u53d1\u96be\u3002 \u3000\u3000\u4f55\u8001\u5927\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u59d3\u5bab\u7684\u4e3a\u6211\u4eec\u6253\u62b1\u4e0d\u5e73\uff0c\u773c\u770b\u6027\u547d\u4e0d\u4fdd\u3002\u6211\u662f\u6709\u5fc3\u65e0\u529b\uff0c\u96be\u4ee5 \u52a9\u4ed6\u3002\u4ed6\u82e5\u7136\u4e3a\u6211\u800c\u6b7b\uff0c\u6211\u81ea\u6740\u62a5\u7b54\u4ed6\u4fbf\u662f\u3002\u201d\u624b\u63e1\u5200\u67c4\uff0c\u624b\u6307\u53d1\u6296\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u7239\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u4e00\u624b\u6309\u7740\u5979\u7239\u7239\u7684\u4f69\u5200\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u4e00\u9635\u54c8\u54c8\u7684 \u5927\u7b11\u58f0\u3002\u91d1\u9010\u6d41\u5df2\u7ecf\u8d70\u5230\u4ed6\u4eec\u7236\u5973\u7684\u9762\u524d\uff0c\u9762\u5411\u7740\u90a3\u6b63\u5728\u6fc0\u6597\u7684\u4e09\u4e2a\u4eba\u7eb5\u58f0\u5927\u7b11\uff01 \u3000\u3000\u4f55\u8001\u5927\u7236\u5973\u90fd\u662f\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u83ab\u975e\u662f\u4e2a\u75af\u5b50\uff1f\u8fd9\u4e48\u5927\u80c6\uff01\u201d\u4f46\u7531\u4e8e\u91d1 \u9010\u6d41\u8fd9\u4e48\u7a81\u5982\u5176\u6765\uff0c\u4f55\u8001\u5927\u5206\u4e86\u5fc3\uff0c\u7cbe\u795e\u8f6c\u79fb\u5230\u91d1\u9010\u6d41\u7684\u8eab\u4e0a\uff0c\u4e00\u65f6\u95f4\u5012\u5fd8\u8bb0\u4e86\u60f3\u81ea\u6740\u7684\u5ff5 \u5934\u3002 \u3000\u3000\u91d1\u9010\u6d41\u524d\u4fef\u540e\u4ef0\u5730\u7b11\u4e2a\u4e0d\u4f11\uff0c\u4f55\u8001\u5927\u770b\u51fa\u5f02\u5904\uff0c\u6084\u6084\u5bf9\u5973\u513f\u8bf4\u9053\uff1a\u201c\u5947\u602a\uff0c\u8fd9\u4e2a\u4eba\u7ad9\u5f97 \u8fd9\u4e48\u8fd1\uff0c\u7adf\u7136\u4e0d\u6015\u5f6d\u5de8\u5cf0\u7684\u5288\u7a7a\u638c\u529b\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7684\u7b11\u58f0\u94ff\u94ff\u9535\u9535\uff0c\u8fd9\u662f\u7528\u4e0a\u4e58\u5185\u529f\u53d1\u51fa\u7684\u7b11\u58f0\uff0c\u6fc0\u6218\u4e2d\u7684\u4e09\u4e2a\u4eba\u5fc3\u795e\u90fd\u53d7\u4e86\u4ed6\u7684 \u6270\u4e71\u3002\u5bab\u79c9\u85e9\u4e0d\u77e5\u9053\u4ed6\u7684\u6765\u610f\uff0c\u5fc3\u4e2d\u5927\u5927\u5403\u60ca\u3002 \u3000\u3000\u8fde\u57ce\u864e\u9996\u5148\u5fcd\u8010\u4e0d\u4f4f\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u5728\u8fd9\u91cc\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u6307\u7740\u4ed6\u8bf4\u9053\uff1a\u201c\u7b2c\u4e00\u4e2a\u7b11\u4f60\uff01\u201d \u3000\u3000\u8fde\u57ce\u864e\u6012\u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u7ed9\u4f60\u7b11\u7684\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u7b11\u4f60\u8fd9\u56db\u7b14\u70b9\u516b\u8109\u7684\u7b14\u6cd5\u53ea\u4f7f\u5f97\u51fa\u4e00\u534a\uff0c\u5374\u5c3e\u7136\u6562\u5728\u8fd9 \u91cc\u901e\u80fd\uff01\u5509\uff0c\u8fde\u5bb6\u4eba\u624d\u51cb\u8c22\uff0c\u53ef\u53f9\uff0c\u53ef\u53f9\uff01\u201d \u3000\u3000\u8fde\u57ce\u864e\u5927\u5403\u4e00\u60ca\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e5f\u77e5\u9053\u6211\u5bb6\u7684\u56db\u7b14\u70b9\u516b\u8109\u3002\u201d\u559d\u9053\uff1a\u201c\u6211\u5c31\u662f\u53ea\u4f7f\u5f97\u51fa\u4e00 \u534a\uff0c\u4f60\u4e5f\u7834\u89e3\u4e0d\u4e86\uff0c\u4e0d\u4fe1\u4f60\u6765\u8bd5\u8bd5\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u522b\u5fd9\uff0c\u522b\u5fd9\uff0c\u5f85\u6211\u7b11\u591f\u4e86\u518d\u8bf4\u3002\u201d \u3000\u3000\u5f6d\u5de8\u5cf0\u5fc3\u5934\u4e00\u51db\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u7b11\u8c01\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u7b2c\u4e8c\u4e2a\u5c31\u662f\u7b11\u4f60\u4e86\uff01\u201d \u3000\u3000\u5f6d\u5de8\u5cf0\u9053\uff1a\u201c\u4f60\u53c8\u7b11\u6211\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5168\u9010\u6d41\u9053\uff1a\u201c\u7b11\u4f60\u7684\u91d1\u521a\u638c\u706b\u5019\u592a\u5dee\uff0c\u53ea\u80fd\u79f0\u505a\u6ce5\u83e9\u8428\u638c\u3002\u5c11\u6797\u5bfa\u51fa\u4e86\u4f60\u8fd9\u6837\u7684\u5f1f\u5b50\uff0c \u8fbe\u6469\u7956\u5e08\u7684\u9762\u5b50\u4e5f\u7ed9\u4f60\u4e22\u5149\u4e86\uff01\u201d \u3000\u3000\u5f6d\u5de8\u5cf0\u7684\u5927\u529b\u91d1\u521a\u638c\u5176\u5b9e\u5df2\u5f97\u5c11\u6797\u5bfa\u7684\u771f\u4f20\uff0c\u81ea\u4ece\u51fa\u9053\u4ee5\u6765\uff0c\u78b0\u8fc7\u7684\u5f3a\u654c\u4e0d\u77e5\u591a\u5c11\uff0c\u5bf9 \u65b9\u4f7f\u7528\u5175\u5668\uff0c\u4ed6\u66fe\u7ecf\u8f93\u8fc7\u51e0\u573a\uff0c\u4f46\u6bd4\u6597\u638c\u529b\u5219\u662f\u4ece\u65e0\u654c\u624b\u3002\u4ed6\u4e00\u5411\u4e5f\u662f\u4ee5\u91d1\u521a\u638c\u81ea\u8d1f\u7684\u3002\u5982 \u4eca\u542c\u5f97\u91d1\u9010\u6d41\u8ba5\u7b11\u4ed6\u7684\u91d1\u521a\u638c\u662f\u201c\u6ce5\u83e9\u8428\u638c\u201d\uff0c\u7109\u80fd\u4e0d\u6012\uff1f\u5f53\u4e0b\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\u6211\u7684\u91d1\u521a \u638c\u4e0d\u6d4e\u4e8b\uff0c\u4f60\u5c31\u6765\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e00\u58f0\u957f\u7b11\uff0c\u7eb5\u5165\u5708\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\u4e48\uff1f\u8fd9\u5c31\u6765\u4e86\uff01\u201d \u3000\u3000\u5f6d\u5de8\u5cf0\u4e0d\u77e5\u91d1\u9010\u6d41\u6240\u63d0\u7684\u90a3\u4e2a\u5323\u5b50\uff0c\u5176\u4e2d\u85cf\u7684\u4e43\u662f\u7384\u94c1\uff0c\u53ea\u89c1\u91d1\u9010\u6d41\u63d0\u7740\u5323\u5b50\u5411\u4ed6\u63a8\u538b \u8fc7\u6765\uff0c\u7acb\u5373\u4e00\u58f0\u5927\u559d\uff0c\u4e00\u638c\u62cd\u53bb\u3002 \u3000\u3000\u5f6d\u5de8\u5cf0\u7684\u5927\u529b\u91d1\u521a\u638c\u6709\u5f00\u7891\u88c2\u77f3\u4e4b\u80fd\uff0c\u5fc3\u60f3\u4e0d\u7ba1\u4ed6\u7684\u5323\u91cc\u88c5\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u8fd9\u4e00\u638c\u51c6\u80fd \u5c06\u5b83\u6253\u4e2a\u7c89\u788e\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u5c0f\u5fc3\u4e86\uff0c\u8fd9\u4e1c\u897f\u662f\u4e0d\u80fd\u78b0\u7684\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u5f6d\u5de8\u5cf0\u5df2\u662f \u4e00\u638c\u6253\u4e0b\uff0c\u91d1\u9010\u6d41\u7684\u5323\u5b50\u88c2\u5f00\u4e00\u9762\uff0c\u5f6d\u5de8\u5cf0\u53ef\u5c31\u60e8\u4e86\uff0c\u638c\u5fc3\u8840\u8089\u6a21\u7cca\uff0c\u81c2\u9aa8\u51e0\u4e4e\u65ad\u6298\u3002\u8e6c\u3001 \u8e6c\u3001\u8e6c\u7684\uff0c\u9000\u51fa\u4e86\u516d\u4e03\u6b65\uff0c\u6781\u529b\u5fcd\u7740\u75bc\u75db\uff0c\u8fd9\u624d\u6ca1\u6709\u53eb\u51fa\u58f0\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u786c\u78b0\u7384\u94c1\uff0c\u5140\u81ea\u6ca1\u6709\u9707\u7ffb\uff0c\u679c\u7136\u662f\u6709\u70b9\u529f\u592b\u3002\u82e5\u7136\u5355\u6bd4\u638c\u529b\uff0c \u53ea\u6015\u6211\u8fd8\u672a\u5fc5\u662f\u4ed6\u5bf9\u624b\u3002\u201d\u4e0d\u8fc7\u91d1\u9010\u6d41\u662f\u6709\u610f\u6c14\u4ed6\u7684\uff0c\u53eb\u4ed6\u5403\u4e86\u4e2a\u54d1\u4e8f\u4e4b\u540e\uff0c\u7acb\u5373\u8ddf\u7740\u7b11 \u9053\uff1a\u201c\u5982\u4f55\uff1f\u6211\u8bf4\u4f60\u662f\u6ce5\u83e9\u8428\u638c\u4f60\u4e0d\u76f8\u4fe1\uff0c\u4f60\u770b\uff0c\u5982\u4eca\u662f\u4e0d\u662f\u81ea\u8eab\u96be\u4fdd\u4e86\uff1f\u201d \u3000\u3000\u5f6d\u5de8\u5cf0\u53c8\u60ca\u53c8\u6012\uff0c\u5fcd\u7740\u75bc\u75db\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u5f04\u5978\uff0c\u5323\u5b50\u91cc\u88c5\u7684\u4ec0\u4e48\u4e1c\u897f\uff0c\u53eb\u6211\u4e0a\u5f53\uff1f \u54fc\uff0c\u4f60\u6709\u672c\u9886\uff0c\u4f60\u6562\u4e0e\u6211\u786c\u62fc\u4e00\u638c\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5c31\u63d0\u9192\u8fc7\u4f60\u7684\uff0c\u8c01\u53eb\u4f60\u78b0\u6211\u7684\u5323\u5b50\uff1f\u201d\u5f6d\u5de8\u5cf0\u9762\u7ea2\u8033\u8d64\uff0c\u559d\u9053\uff1a \u201c\u95f2\u8bdd\u5c11\u8bf4\uff0c\u4f60\u6562\u4e0d\u6562\u4e0e\u6211\u89c1\u4e2a\u771f\u7ae0\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u4ef0\u5929\u5927\u58f0\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u6562\uff1f\u770b\u638c\uff01\u201d\u4e00\u526f\u201c\u773c\u9ad8\u4e8e\u9876\u201d\u7684\u795e\u6c14\uff0c\u773c\u89d2\u513f\u4e5f\u4e0d\u671d\u7740 \u5bf9\u65b9\uff0c\u770b\u4e5f\u4e0d\u770b\uff0c\u5c31\u662f\u4e00\u638c\u6253\u4e0b\u3002 \u3000\u3000\u5f6d\u5de8\u5cf0\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u559d\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u5355\u638c\u5212\u4e86\u4e00\u4e2a\u5706\u5f27\uff0c\u7acb\u5373\u8fce\u51fb\u3002 \u3000\u3000\u5f6d\u5de8\u5cf0\u89c1\u91d1\u9010\u6d41\u5e74\u7eaa\u4e0d\u8fc7\u4e8c\u5341\u5c81\u5de6\u53f3\uff0c\u5fc3\u60f3\u4e00\u4e2a\u4e73\u81ed\u672a\u5e72\u7684\u5c0f\u5b50\uff0c\u80fd\u6709\u591a\u5927\u672c\u9886\uff0c\u662f\u4ee5 \u4ed6\u867d\u7136\u53ea\u6709\u4e00\u6761\u5de6\u81c2\u597d\u7528\uff0c\u4ecd\u4e0d\u628a\u91d1\u9010\u6d41\u653e\u5728\u773c\u5185\u4e0a\u3002\u4ed6\u6c14\u6068\u4e4b\u6781\uff0c\u8fd9\u4e00\u638c\u7528\u8db3\u4e86\u529b\u9053\uff0c\u60f3 \u8981\u628a\u91d1\u9010\u6d41\u4e00\u638c\u51fb\u6bd9\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6709\u6837\u5b66\u6837\u7684\u4e5f\u662f\u602a\u58f0\u602a\u6c14\u5730\u53eb\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u53ef\u662f\u4ed6\u7684\u624b\u638c\u5374\u4e0d\u662f\u6253\u4e0b\u53bb\u800c\u662f \u7a81\u7136\u5411\u540e\u4e00\u7f29\u3002 \u3000\u3000\u5f6d\u5de8\u5cf0\u7684\u638c\u5200\u5982\u6392\u5c71\u5012\u6d77\u800c\u6765\uff0c\u6253\u5230\u7684\u5168\u662f\u865a\u7a7a\u4e4b\u5904\uff0c\u6b63\u8981\u559d\u9a82\uff0c\u91d1\u9010\u6d41\u559d\u9053\uff1a\u201c\u5c0f\u5fc3 \u4e86\uff01\u201d\u638c\u529b\u731d\u53d1\uff0c\u8fd9\u6b63\u662f\u201c\u907f\u5176\u671d\u9510\uff0c\u51fb\u5176\u66ae\u5f52\u201d\u7684\u6218\u672f\uff0c\u53ea\u542c\u5f97\u201c\u548b\u5693\u201d\u4e00\u58f0\uff0c\u5f6d\u5de8\u5cf0\u7684 \u5de6\u81c2\u81fc\u9aa8\u65ad\u6298\uff0c\u53ea\u6709\u4e00\u6839\u9aa8\u5934\u8fd8\u8fde\u7740\uff0c\u8f6f\u8f6f\u5730\u5782\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u80dc\u5f97\u867d\u7136\u6709\u70b9\u53d6\u5de7\uff0c\u4f46\u4ed6\u80fd\u591f\u5728\u8ddd\u79bb\u4e09\u5c3a\u4e4b\u5185\u4e0d\u53d7\u5bf9\u65b9\u638c\u529b\u7684\u4f24\u5bb3\uff0c\u800c\u4e14\u8fd8\u4e86\u4e00 \u638c\u4fbf\u62d7\u65ad\u5f6d\u5de8\u5cf0\u7684\u9aa8\u81c2\uff0c\u8fd9\u6837\u7684\u529f\u529b\uff0c\u4e5f\u8db3\u4ee5\u4ee4\u65c1\u89c2\u7684\u8fde\u57ce\u864e\u6697\u6697\u5403\u60ca\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u5982\u4f55\uff1f\u89c1\u4e86\u771f\u7ae0\uff0c\u4f60\u8fd8\u4e0d\u662f\u4e00\u6837\u7684\u81ea\u8eab\u96be\u4fdd\uff1f\u201d\u5f6d\u5de8\u5cf0\u75db\u5f97\u51b7\u6c57\u5982\u96e8\uff0c \u4ed6\u7684\u81fc\u9aa8\u5fc5\u987b\u9a6c\u4e0a\u63a5\u4e0a\uff0c\u5426\u5219\u4fbf\u6210\u6b8b\u5e9f\u3002\u4ed6\u53ea\u597d\u54ac\u7d27\u7259\u6839\uff0c\u5fcd\u53d7\u5267\u75db\uff0c\u5148\u63a5\u65ad\u9aa8\uff0c\u4e5f\u987e\u4e0d\u5f97 \u548c\u91d1\u9010\u6d41\u6597\u53e3\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u653e\u5fc3\u597d\u597d\u7684\u6cbb\u7406\u4f60\u81ea\u5df1\u5427\uff0c\u6211\u662f\u4e00\u5411\u4e0d\u6253\u843d\u6c34\u72d7\u7684\u3002\u201d\u8bf4\u7f62\u4e00\u4e2a\u8f6c\u8eab\uff0c \u5230\u4e86\u8fde\u57ce\u864e\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u8f6e\u5230\u4f60\u8fd9\u4e2a\u77ee\u5b50\u5566\uff01\u201d\u5bab\u5927\u54e5\uff0c\u8ba9\u6211\u4e00\u8ba9\u3002\u201d \u3000\u3000\u5bab\u79c9\u85e9\u9000\u8fc7\u4e00\u65c1\uff0c\u8fde\u57ce\u864e\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u8fc7\u8981\u7834\u6211\u8fd9\u60ca\u795e\u7b14\u6cd5\u7684\uff01\u201d\u539f\u6765\u8fde\u57ce\u864e\u7684\u672c\u9886\u867d \u7136\u662f\u6bd4\u5f6d\u5de8\u5cd9\u8fd8\u9ad8\uff0c\u4f46\u4ed6\u7684\u672c\u9886\u5168\u662f\u51ed\u4ed7\u4ed6\u8fd9\u4e00\u5bf9\u5224\u5b98\u7b14\uff0c\u8bba\u529f\u529b\u5374\u662f\u4e0d\u5982\u5f6d\u5de8\u5cf0\u3002\u4ed6\u89c1\u5f6d \u5de8\u5cf0\u6bd4\u638c\u53d7\u632b\uff0c\u7109\u6562\u548c\u91d1\u9010\u6d41\u6bd4\u62fc\u5185\u529b\uff1f\u8a00\u4e0b\u4e4b\u610f\uff0c\u5373\u662f\u53ea\u80fd\u548c\u91d1\u9010\u6d41\u8f83\u91cf\u7b14\u6cd5\u3002\u201c\u60ca\u795e\u7b14 \u6cd5\u201d\u4e43\u662f\u201c\u56db\u7b14\u70b9\u516b\u8109\u201d\u7684\u522b\u540d\uff0c\u8fde\u57ce\u864e\u53ea\u80fd\u53cc\u7b14\u70b9\u56db\u8109\uff0c\u4e0d\u597d\u610f\u601d\u8bf4\u51fa\u539f\u6765\u7684\u540d\u79f0\u3002 \u3000\u3000\u4f55\u8001\u5927\u5fcd\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u4eba\u5bb6\u624b\u91cc\u6ca1\u6709\u5224\u5b98\u7b14\uff0c\u4f60\u7ba1\u5f97\u7740\u4eba\u5bb6\u5982\u4f55\u6253\u6cd5\uff1f\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a \u201c\u4e0d\u6015\uff0c\u4e0d\u6015\u3002\u6211\u6ca1\u6709\u5224\u5b98\u7b14\u4e00\u6837\u53ef\u4ee5\u4f7f\u51fa\u56db\u7b14\u70b9\u516b\u8109\u7684\u529f\u592b\u3002\u6211\u8bf4\u8fc7\u7684\u8bdd\u5c31\u7b97\u6570\uff0c\u4e00\u5b9a\u8981 \u53eb\u8fd9\u77ee\u5b50\u8f93\u5f97\u5fc3\u670d\u53e3\u670d\uff01\u201d \u3000\u3000\u8fde\u57ce\u864e\u542c\u4e86\u8fd9\u8bdd\uff0c\u5982\u4f55\u80fd\u591f\u76f8\u4fe1\uff1f\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u83ab\u8bf4\u4f60\u662f\u4e00\u53cc\u7a7a\u624b\uff0c\u5c31\u662f\u6709\u4e86\u5224\u5b98\u7b14\uff0c \u53ea\u4e00\u4e2a\u4eba\uff0c\u4e5f\u51b3\u8ba1\u4e0d\u80fd\u4f7f\u51fa\u56db\u7b14\u70b9\u516b\u8109\u7684\u529f\u592b\u3002\u201d\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5c31\u7834\u5427\uff01\u201d\u53cc\u7b14\u659c\u5206\uff0c\u5de6 \u70b9\u671f\u95e8\uff0c\u6709\u70b9\u8840\u6d77\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9a88\u6307\u5982\u621f\uff0c\u5728\u5bf9\u65b9\u7684\u7b14\u5f71\u4ea4\u52a0\u4e4b\u4e0b\uff0c\u6b3a\u8eab\u8fdb\u53bb\uff0c\u8fd8\u4e86\u4e00\u62db\u201c\u6ce3\u9b3c\u60ca\u795e\u201d\uff0c\u5de6\u624b\u7684 \u6307\u5c16\u70b9\u8fde\u57ce\u864e\u7684\u201c\u5929\u7a81\u7a74\u201d\uff0c\u53f3\u624b\u7684\u6307\u5c16\u70b9\u8fde\u57ce\u864e\u7684\u201c\u9633\u767d\u7a74\u201d\uff0c\u4ed6\u4ee5\u6307\u4ee3\u7b14\uff0c\u4f7f\u51fa\u7684\u679c\u7136 \u662f\u8fde\u5bb6\u7684\u201c\u60ca\u795e\u7b14\u6cd5\u201d\uff0c\u800c\u4e14\u8fd8\u6b63\u597d\u662f\u8fde\u57ce\u864e\u8fd9\u4e00\u62db\u7684\u514b\u661f\u3002 \u3000\u3000\u8fde\u57ce\u864e\u5413\u4e86\u4e00\u60ca\uff1a\u201c\u8fd9\u5c0f\u5b50\u679c\u7136\u662f\u6709\u70b9\u90aa\u95e8\uff01\u201d\u8fde\u5fd9\u540e\u9000\uff0c\u6025\u6025\u53d8\u62db\u3002\u91d1\u9010\u6d41\u63a2\u8eab\u800c \u4e0a\uff0c\u638c\u6307\u98de\u821e\uff0c\u4e00\u62db\u4e4b\u95f4\uff0c\u8fde\u88ad\u5bf9\u65b9\u4efb\u7763\u4e8c\u8109\u7684\u516b\u5904\u7a74\u9053\uff0c\u653b\u5f97\u8fde\u57ce\u864e\u53ea\u6709\u62db\u67b6\u4e4b\u529f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u9762\u6253\uff0c\u4e00\u9762\u53e3\u8bb2\u6307\u5212\uff0c\u4e0d\u65ad\u7684\u6307\u51fa\u5bf9\u65b9\u7684\u7834\u7efd\u3002\u4f60\u8fd9\u4e00\u62db\u5206\u82b1\u62c2\u67f3\u4f7f\u5f97\u4e0d\u5230 \u5bb6\uff0c\u539f\u6765\u7684\u62db\u6570\u867d\u7136\u662f\u53f3\u7b14\u91cd\uff0c\u5de6\u7b14\u8f7b\uff0c\u4f46\u5bf9\u65b9\u653b\u4f60\u7684\u53f3\u81c2\uff0c\u4f60\u5c31\u5e94\u8be5\u91cd\u5de6\u8f7b\u53f3\u624d\u662f\uff01\u5bf9\u654c \u8d35\u5728\u89c1\u673a\uff0c\u5c82\u80fd\u62d8\u6ce5\u6210\u6cd5\uff1f\u563f\uff0c\u563f\uff0c\u4f60\u8fd9\u4e00\u62db\u4e09\u82b1\u805a\u9876\u53c8\u4f7f\u5f97\u4e0d\u5bf9\u4e86\uff01\u4e09\u82b1\u805a\u9876\u70b9\u7684\u5e94\u8be5\u662f \u534e\u76d6\u7a74\u3001\u592a\u9633\u7a74\u548c\u9876\u5fc3\u7684\u5929\u7075\u7a74\uff0c\u4f60\u600e\u4e48\u80e1\u70b9\u4e00\u901a\uff01\u201d\u6240\u8bb2\u7684\u679c\u7136\u90fd\u662f\u8fde\u5bb6\u7684\u201c\u60ca\u795e\u7b14\u6cd5\u201d \u7684\u4e0d\u4f20\u4e4b\u79d8\uff0c\u800c\u76ee\u6bd4\u539f\u6765\u7684\u7b14\u6cd5\u66f4\u8fdb\u4e00\u5c42\uff01 \u3000\u3000\u91d1\u9010\u6d41\u6307\u51fa\u7684\u8fd9\u4e9b\u7834\u7efd\uff0c\u6709\u4e9b\u7684\u786e\u662f\u8fde\u57ce\u864e\u4ece\u6765\u672a\u66fe\u60f3\u5230\u7684\u758f\u6f0f\u4e4b\u5904\uff0c\u6709\u7684\u5374\u662f\u4ed6\u77e5\u5176 \u7136\u800c\u529b\u4e0d\u902e\u7684\u5730\u65b9\uff0c\u4f8b\u5982\u90a3\u201c\u4e09\u82b1\u805a\u9876\u201d\u4e00\u62db\uff0c\u4ed6\u77e5\u9053\u662f\u8981\u70b9\u5bf9\u65b9\u7684\u534e\u76d6\u7a74\u3001\u592a\u9633\u7a74\u548c\u5929\u7075 \u7a74\uff0c\u4f46\u8981\u70b9\u5230\u5bf9\u65b9\u8fd9\u4e09\u4e2a\u7a74\u9053\uff0c\u5fc5\u987b\u51cc\u7a7a\u4e0b\u51fb\uff0c\u4ed6\u7684\u8f7b\u529f\u4e0d\u53ca\u91d1\u9010\u6d41\uff0c\u5982\u4f55\u80fd\u591f\u505a\u5230\uff1f \u3000\u3000\u4e0d\u8fc7\uff0c\u5c3d\u7ba1\u4ed6\u529b\u6240\u4e0d\u902e\u4e4b\u5904\uff0c\u91d1\u9010\u6d41\u5bf9\u4ed6\u7684\u201c\u60ca\u795e\u7b14\u6cd5\u201d\u6d1e\u82e5\u89c2\u706b\uff0c\u5256\u6790\u7cbe\u5fae\uff0c\u5374\u662f\u4ee4 \u4ed6\u4e0d\u80fd\u4e0d\u8877\u5fc3\u4f69\u670d\u4e86\u3002\u4ed6\u5728\u4e00\u7247\u832b\u7136\u4e4b\u540e\uff0c\u5fc3\u77e5\u81ea\u5df1\u7684\u70b9\u7a74\u529f\u592b\u8fdc\u8fdc\u4e0d\u53ca\u5bf9\u65b9\uff0c\u4eca\u65e5\u662f\u65e0\u8bba \u5982\u4f55\u96be\u514d\u4e00\u8d25\u7684\u4e86\uff0c\u518d\u6253\u4e0b\u53bb\uff0c\u53ea\u6709\u66f4\u53d7\u5bf9\u65b9\u7684\u595a\u843d\uff0c\u66f4\u589e\u81ea\u5df1\u7684\u7f9e\u6127\u800c\u5df2\u3002\u53ef\u662f\u4ed6\u662f\u4e2a\u6c5f \u6e56\u4e0a\u6210\u540d\u7684\u4eba\u7269\uff0c\u53c8\u4e0d\u7518\u5c31\u6b64\u8ba4\u8f93\u7ed9\u4e00\u4e2a\u201c\u4e0d\u89c1\u7ecf\u4f20\u201d\u7684\u540e\u751f\u5c0f\u5b50\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u8ba1\u4e0a\u5fc3\u6765\uff0c \u53cc\u7b14\u4e00\u6307\uff0c\u89e3\u5f00\u91d1\u9010\u6d41\u7684\u4e00\u62db\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u7684\u672c\u9886\u7684\u786e\u662f\u5728\u6211\u4e4b\u4e0a\uff0c\u4f46\u4f60\u4f7f\u7684\u5374\u4e5f\u8fd8 \u4e0d\u662f\u56db\u7b14\u70b9\u516b\u8109\u7684\u529f\u592b\uff01\u4f60\u82e5\u662f\u8981\u6253\u8d25\u6211\uff0c\u6211\u73b0\u5728\u5c31\u53ef\u4ee5\u62f1\u624b\u8ba4\u8f93\u3002\u82e5\u8981\u6211\u8f93\u5f97\u5fc3\u670d\u53e3\u670d\uff0c \u90a3\u4f60\u8fd8\u5f97\u518d\u663e\u672c\u9886\uff01\u201d\u91d1\u9010\u6d41\u8bf4\u8fc7\u7a7a\u624b\u4e5f\u53ef\u4ee5\u4fbf\u51fa\u201c\u56db\u7b14\u70b9\u516b\u8109\u201d\u7684\u529f\u592b\u7684\uff0c\u5982\u4eca\u8fde\u57ce\u864e\u5c31 \u662f\u6267\u7740\u4ed6\u8fd9\u53e5\u8bdd\u5411\u4ed6\u95ee\u96be\uff0c\u867d\u7136\u8ff9\u8fd1\u65e0\u8d56\uff0c\u4f46\u8981\u6362\u56de\u9762\u5b50\uff0c\u4e5f\u987e\u4e0d\u4e86\u8fd9\u8bb8\u591a\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4ee5\u6307\u4ee3\u7b14\uff0c\u53cc\u624b\u81f3\u591a\u80fd\u4f7f\u51fa\u201c\u53cc\u7b14\u70b9\u56db\u8109\u201d\u7684\u529f\u592b\uff0c\u8fd9\u662f\u4efb\u4f55\u4e00\u4e2a\u61c2\u5f97\u70b9\u7a74\u7684\u4eba \u90fd\u4f1a\u7406\u89e3\u7684\uff0c\u4f55\u8001\u5927\u4e0d\u7981\u9a82\u9053\uff1a\u201c\u4f60\u5206\u660e\u8f93\u4e86\uff0c\u8fd8\u8981\u5f3a\u8fa9\uff0c\u7f9e\u4e5f\u4e0d\u7f9e\uff1f\u54fc\uff0c\u4f60\u8981\u4eba\u5bb6\u4e00\u53cc\u7a7a \u624b\u6765\u70b9\u5947\u7ecf\u516b\u8109\uff0c\u8fd9\u4e0d\u662f\u5f3a\u4eba\u6240\u96be\u5417\uff1f\u201d\u8fde\u57ce\u864e\u51b7\u7b11\u9053\uff1a\u201c\u8c01\u53eb\u4ed6\u8bf4\u8fc7\u90a3\u53e5\u8bdd\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u83ab\u5435\uff0c\u83ab\u5435\uff01\u6211\u8bf4\u8fc7\u7684\u8bdd\uff0c\u5f53\u7136\u7b97\u6570\uff01\u201d \u3000\u3000\u84e6\u5730\u6b3a\u8eab\u76f4\u8fdb\uff0c\u4f7f\u4e86\u4e00\u62db\u8fde\u57ce\u864e\u4ece\u672a\u89c1\u8fc7\u7684\u53e4\u602a\u624b\u6cd5\uff0c\u4e00\u62db\u4e4b\u95f4\uff0c\u70b9\u4ed6\u56db\u5904\u7a74\u9053\uff0c\u8fde\u57ce \u864e\u4e0d\u77e5\u5982\u4f55\u62db\u67b6\uff0c\u6b63\u60f3\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u624b\u6cd5\u4e5f\u8fd8\u4e0d\u8fc7\u662f\u4ece\u53cc\u7b14\u70b9\u56db\u8109\u4e2d\u53d8\u5316\u51fa\u6765\u7684\u800c\u5df2\u3002\u201d\u8bdd \u672a\u51fa\u4e86\uff0c\u9661\u7136\u95f4\u53ea\u89c9\u624b\u4e0a\u4e00\u8f7b\uff0c\u4e00\u5bf9\u5224\u5b98\u7b14\u5df2\u7ed9\u91d1\u9010\u6d41\u593a\u4e86\u53bb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u53eb\u9053\uff1a\u201c\u56db\u7b14\u70b9\u516b\u8109\u7684\u529f\u592b\u6765\u5566\u3002\u201d\u53cc\u7b14\u63b7\u51fa\uff0c\u8ddf\u7740\u95ea\u7535\u822c\u7684\u63c9\u8eab\u800c\u4e0a\uff0c\u53cc\u7b14\u4ea4 \u53c9\u63a0\u8fc7\uff0c\u70b9\u4e86\u8fde\u57ce\u864e\u7684\u7763\u8109\u3001\u4efb\u8109\u3001\u51b2\u8109\u3001\u5e26\u80a0\u56db\u4e2a\u7a74\u9053\uff0c\u4ee5\u62ac\u4ee3\u7b14\uff0c\u53cc\u624b\u53c8\u70b9\u4e86\u4ed6\u7684\u9634 \u9a84\u3001\u9633\u8f7f\u3001\u9634\u7ef4\u3001\u9633\u7ef4\u56db\u80ba\u7684\u56db\u4e2a\u7a74\u9053\u3002\u8fd9\u6b63\u662f\u4e24\u4eba\u5408\u4f7f\u7684\u201c\u56db\u7b14\u70b9\u516b\u8109\u201d\u7684\u7edd\u9876\u70b9\u7a74\u529f \u592b\uff01 \u3000\u3000\u8fde\u57ce\u864e\u516b\u8109\u88ab\u70b9\uff0c\u4e00\u65f6\u4e0d\u80fd\u52a8\u5f39\u3002\u91d1\u9010\u6d41\u54c8\u4e00\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u670d\u4e86\u4e48\uff1f\u6211\u4e0d\u60f3\u4f60\u7684\u8fde\u5bb6\u7b14 \u6cd5\u5931\u4f20\uff0c\u4eca\u65e5\u6743\u4e14\u653e\u8fc7\u4e86\u4f60\uff01\u201d\u8d70\u4e0a\u524d\u53bb\uff0c\u968f\u624b\u62cd\u4e86\u51e0\u62cd\uff0c\u53c8\u628a\u8fde\u57ce\u864e\u88ab\u5c01\u7684\u7a74\u9053\u5c3d\u90fd\u89e3 \u5f00\u3002 \u3000\u3000\u5f6d\u5de8\u5cf0\u548c\u90a3\u201c\u516c\u5b50\u7237\u201d\u521a\u624d\u770b\u89c1\u60c5\u52bf\u4e0d\u5bf9\uff0c\u65e9\u5df2\u4e0a\u9a6c\u8d70\u4e86\uff01\u8fde\u57ce\u864e\u7a74\u9053\u4e00\u89e3\uff0c\u9762\u5982\u58eb \u8272\uff0c\u4e0d\u6562\u518d\u53d1\u4e00\u8a00\uff0c\u8ddf\u7740\u4e5f\u8d70\u3002\u6b63\u662f\uff1a \u3000\u3000\u8def\u89c1\u4e0d\u5e73\u65bd\u7edd\u6280\uff0c\u60ca\u795e\u7b14\u6cd5\u6151\u5f3a\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u5341\u4e94\u56de\u3000\u72ec\u884c\u957f\u5251\u4e00\u676f\u9152\u3000\u5b64\u5ba2\u9ad8\u697c\u4e07\u91cc\u5fc3 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u5341\u4e94\u56de\u3000\u72ec\u884c\u957f\u5251\u4e00\u676f\u9152\u3000\u5b64\u5ba2\u9ad8\u697c\u4e07\u91cc\u5fc3 \u3000\u3000\u90a3\u201c\u516c\u5b50\u7237\u201d\u548c\u4ed6\u7684\u624b\u4e0b\u8d70\u4e86\u4e4b\u540e\uff0c\u91d1\u9010\u6d41\u4e0a\u524d\u4e0e\u5bab\u79c9\u85e9\u76f8\u89c1\uff0c\u8bf4\u9053\uff1a\u201c\u5bab\u9999\u4e3b\uff0c\u60f3\u4e0d \u5230\u4f60\u4e5f\u5230\u4e86\u8fd9\u513f\u3002\u201d \u3000\u3000\u5bab\u79c9\u85e9\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u771f\u662f\u5e78\u4f1a\u3002\u591a\u8c22\u4f60\u62d4\u5200\u76f8\u52a9\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4ee5\u524d\u6211\u4e0d\u77e5\u9053\u4f60\u7684\u4e3a\u4eba\uff0c\u591a\u6709\u5f97\u7f6a\u3002\u73b0\u5728\u54b1\u4eec\u53ef\u4ee5\u4ea4\u4e2a\u670b\u53cb\u4e86\u3002\u5c0f\u5f1f\u59d3 \u91d1\uff0c\u540d\u9010\u6d41\u3002\u968f\u6ce2\u9010\u6d41\u7684\u2018\u9010\u6d41\u2019\u4e8c\u5b57\u3002\u201d\u90a3\u6b21\u5bab\u79c9\u85e9\u4e0e\u4ed6\u6bd4\u5251\u5931\u5229\u4e4b\u540e\uff0c\u66fe\u7ecf\u95ee\u8fc7\u4ed6\u7684\u540d \u5b57\uff0c\u5f53\u65f6\u91d1\u9010\u6d41\u8fd8\u662f\u5c06\u4ed6\u5f53\u4f5c\u654c\u4eba\u770b\u5f85\uff0c\u4e0d\u80af\u5c06\u59d3\u540d\u544a\u8bc9\u4ed6\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u629a\u5251\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u5bab\u67d0\u591a\u8c22\u9601\u4e0b\u76f8\u52a9\u4e4b\u5fb7\uff0c\u4e09\u5e74\u540e\u6bd4\u5251\u4e4b\u7ea6\u53d6\u6d88\u3002\u5c31\u6b64\u522b \u8fc7\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u4f60\u7684\u6c14\u8fd8\u6ca1\u6709\u6d88\u5417\uff1f\u4f60\u82e5\u662f\u60f3\u8981\u8fd9\u5757\u7384\u94c1\uff0c\u6211\u53ef\u4ee5\u7ed9 \u4f60\u3002\u8def\u89c1\u4e0d\u5e73\uff0c\u62d4\u5200\u76f8\u52a9\uff0c\u7406\u6240\u5e94\u4e3a\uff0c\u5ba2\u5957\u8bdd\u4f60\u5c31\u4e0d\u5fc5\u8bf4\u4e86\u3002\u201d\u4f55\u8001\u5927\u5728\u4e00\u65c1\u542c\u4ed6\u8bf4\u53ca\u7384 \u94c1\uff0c\u4e0d\u7981\u9762\u9732\u8be7\u5f02\u4e4b\u8272\uff0c\u6ce8\u89c6\u4ed6\u90a3\u5323\u5b50\u3002\u4f3c\u4e4e\u60f3\u8bf4\u4ec0\u4e48\uff0c\u53ef\u6ca1\u6709\u8bf4\u3002 \u3000\u3000\u91d1\u9010\u6d41\u867d\u7136\u7980\u6027\u4e0d\u7f81\uff0c\u5374\u4e5f\u662f\u4e2a\u6027\u60c5\u4e2d\u4eba\u3002\u5f53\u4ed6\u771f\u5fc3\u5b9e\u610f\u60f3\u548c\u5bf9\u65b9\u4ea4\u4e2a\u670b\u53cb\u7684\u65f6\u5019\uff0c\u4ed6 \u662f\u4ec0\u4e48\u90fd\u53ef\u4ee5\u727a\u7272\u7684\uff0c\u7384\u94c1\u867d\u662f\u4e16\u6240\u7f55\u89c1\u7684\u5b9d\u7269\uff0c\u4f46\u5e76\u4e0d\u653e\u5728\u4ed6\u7684\u5fc3\u4e0a\uff0c\u4f46\u5728\u5b9c\u79c9\u85e9\u542c\u6765\uff0c \u5374\u628a\u4ed6\u7684\u7531\u8877\u4e4b\u8a00\u5f53\u4f5c\u4e86\u8ba5\u523a\uff0c\u4e8e\u662f\u62c2\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5bab\u67d0\u662f\u66fe\u60f3\u62a2\u8fd9\u5757\u7384\u94c1\uff0c\u4f46\u662f\u8981\u51ed \u672c\u9886\u62a2\u7684\uff0c\u5c82\u80fd\u4e0d\u5ea6\u5fb7\u3001\u4e0d\u91cf\u529b\uff0c\u5984\u53d6\u522b\u4eba\u7684\u4e1c\u897f\uff1f\u9601\u4e0b\u7684\u672c\u9886\u8fdc\u80dc\u4e8e\u6211\uff0c\u6211\u4e5f\u81ea\u77e5\u4e0d\u914d\u6709 \u8fd9\u5b9d\u7269\u4e86\uff0c\u4f60\u8fd8\u8c03\u4f83\u6211\u505a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5509\uff0c\u6211\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u53c8\u5f97\u7f6a\u4e86\u4f60\u3002\u5728\u4e0b\u53ea\u662f\u4e00\u70b9\u7eb3\u4ea4\u4e4b\u610f\uff0c\u4f60 \u53ef\u4e0d\u8981\u8bef\u4f1a\u3002\u201d \u3000\u3000\u5bab\u79c9\u85e9\u51b7\u51b7\u8bf4\u9053\uff1a\u2018\u91d1\u5197\u6298\u8282\u4e0b\u4ea4\uff0c\u5c0f\u5f1f\u9ad8\u6500\u4e0d\u8d77\uff01\u201d\u8bf4\u7f62\uff0c\u8f6c\u8eab\u4fbf\u53bb\u3002\u4f55\u8001\u5927\u60f3\u8981\u5411 \u4ed6\u9053\u8c22\uff0c\u4ea6\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u8fd9\u4eba\u867d\u7136\u9a84\u50b2\u5f97\u7d27\uff0c\u5012\u4e5f\u6709\u70b9\u9aa8\u6c14\u3002\u201d\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u4ed6\u8d25\u5728\u6211\u7684\u5251\u4e0b\uff0c \u4e5f\u96be\u602a\u4ed6\u6709\u6b64\u8bef\u4f1a\u3002\u4fd7\u8bed\u8bf4\u65e5\u4e45\u89c1\u4eba\u5fc3\uff0c\u5c06\u6765\u4ed6\u81ea\u4f1a\u77e5\u9053\u6211\u7684\u4e3a\u4eba\uff0c\u90a3\u65f6\u6211\u518d\u4e0e\u4ed6\u89e3\u91ca\u3002\u201d \u8fd9\u4e48\u4e00\u60f3\uff0c\u5fc3\u4e2d\u867d\u7136\u8fd8\u6709\u4e00\u4e9b\u4e0d\u5feb\uff0c\u4e5f\u5c31\u4e0d\u53bb\u7ba1\u4ed6\u4e86\u3002 \u3000\u3000\u4f55\u8001\u5927\u7236\u5973\u4e0a\u524d\u62dc\u8c22\u4e86\u91d1\u9010\u6d41\u6551\u547d\u4e4b\u6069\uff0c\u4e92\u901a\u59d3\u540d\uff0c\u91d1\u9010\u6d41\u8fd9\u624d\u77e5\u9053\u7236\u4eb2\u540d\u53eb\u4f55\u5efa\u96c4\uff0c \u5973\u513f\u540d\u53eb\u4f55\u5f69\u51e4\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f55\u5927\u53d4\uff0c\u4f60\u7684\u4f24\u4e0d\u8981\u7d27\u5427\uff0c\u8fd9\u91cc\u6709\u9897\u5c0f\u8fd8\u4e39\uff0c\u8bf7\u4f60\u670d\u4e0b\u3002\u201d \u3000\u3000\u4f55\u5efa\u96c4\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u5c11\u6797\u5bfa\u7684\u5c0f\u8fd8\u4e39\uff0c\u4f60\u8fd9\u4e48\u8d35\u91cd\u7684\u7075\u4e39\uff0c\u522b\u7ed9\u6211\u7cdf\u8e4b\u4e86\u3002 \u6211\u7684\u4f24\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u4e00\u4f4d\u4e16\u4f2f\u5077\u6765\u7684\uff0c\u4f60\u5c3d\u7ba1\u62ff\u53bb\uff0c\u6211\u8fd8\u591a\u7740\u5462\u3002\u8eab\u4f53\u8981\u7d27\uff0c\u4e0d\u5fc5 \u73cd\u60dc\u5b83\u4e86\u3002\u201d \u3000\u3000\u4f55\u5efa\u96c4\u662f\u4e2a\u8c6a\u8fc8\u7684\u6c5f\u6e56\u6c49\u5b50\uff0c\u542c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u4e5f\u5c31\u4e0d\u518d\u5ba2\u6c14\uff0c\u628a\u90a3\u9897\u5c0f\u8fd8\u4e39\u670d\u4e0b\uff0c\u8bf4\u9053\uff1a \u201c\u5927\u6069\u4e0d\u8a00\u62a5\uff0c\u91d1\u76f8\u516c\u65e5\u540e\u82e5\u6709\u7528\u5f97\u7740\u5c0f\u4eba\u4e4b\u5904\uff0c\u4f55\u67d0\u5b9a\u6548\u72ac\u9a6c\u4e4b\u52b3\u3002\u597d\uff0c\u54b1\u4eec\u8d76\u5feb\u79bb\u5f00\u8fd9 \u662f\u975e\u4e4b\u5730\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u5df2\u7ecf\u7ed9\u6211\u6253\u5f97\u843d\u82b1\u6d41\u6c34\uff0c\u96be\u9053\u8fd8\u6562\u518d\u6765\uff1f\u201d\u4f55\u5efa\u96c4\u7236\u5973\u6536\u62fe\u4e86\u644a \u5b50\uff0c\u6025\u6025\u5fd9\u5fd9\u4fbf\u8d70\uff0c\u91d1\u9010\u6d41\u5fc3\u91cc\u867d\u4e0d\u670d\u6c14\uff0c\u4e5f\u53ea\u597d\u63d0\u7740\u7384\u94c1\u8ddf\u4e0a\u3002 \u3000\u3000\u4f55\u5efa\u96c4\u5e76\u65e0\u5185\u4f24\uff0c\u4f11\u606f\u8fc7\u540e\uff0c\u53c8\u670d\u4e86\u5c0f\u8fd8\u4e39\uff0c\u5065\u6b65\u5982\u98de\uff0c\u4f55\u5f69\u51e4\u7684\u8f7b\u529f\u4e5f\u5f88\u4e0d\u5f31\uff0c\u4e0d\u5373 \u4e0d\u79bb\u7684\u8ddf\u5728\u5979\u7236\u4eb2\u80cc\u540e\u3002\u4e0d\u8fc7\u91d1\u9010\u6d41\u8d70\u5f97\u66f4\u5feb\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5c31\u8d8a\u8fc7\u4e86\u4ed6\u4eec\u524d\u5934\u3002 \u3000\u3000\u4f55\u5efa\u96c4\u5df2\u77e5\u9053\u4ed6\u624b\u4e0a\u63d0\u7740\u7684\u662f\u7384\u94c1\uff0c\u51ed\u7740\u4ed6\u7684\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u4e00\u770b\u5c31\u77e5\u8fd9\u7384\u94c1\u6c89\u91cd\u975e\u5e38\uff0c\u800c \u91d1\u9010\u6d41\u63d0\u7740\u7384\u94c1\uff0c\u81ea\u5df1\u8fd8\u8d76\u4e0d\u4e0a\u4ed6\uff01\u8981\u4ed6\u65f6\u4e0d\u65f6\u653e\u6162\u811a\u6b65\uff0c\u624d\u80fd\u8ddf\u4e0a\uff0c\u5fc3\u91cc\u53c8\u662f\u8be7\u5f02\uff0c\u53c8\u662f \u4f69\u670d\u3002 \u3000\u3000\u4e09\u4eba\u65bd\u5c55\u8f7b\u529f\uff0c\u4e00\u53e3\u6c14\u8d70\u51fa\u4e8c\u5341\u591a\u91cc\uff0c\u7ed5\u8fc7\u4e86\u5343\u4f5b\u5c71\uff0c\u51fa\u4e86\u6d4e\u5357\u57ce\u5916\u3002\u4f55\u5efa\u96c4\u677e\u4e86\u53e3 \u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u53ef\u4ee5\u6b47\u6b47\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8bf4\u9053\uff1a\u201c\u90a3\u516c\u5b50\u7237\u662f\u4ec0\u4e48\u4eba\uff0c\u5982\u6b64\u5f3a\u6a2a\u9738\u9053\uff1f\u4ed6\u90a3\u4e24\u4e2a\u4fdd\u7f25\uff0c\u5012\u662f\u6c5f\u6e56\u4e0a\u7684\u4e00\u6d41 \u89d2\u8272\uff0c\u5374\u4e0d\u77e5\u4e5f\u4f55\u4ee5\u7518\u5fc3\u53d7\u4ed6\u6240\u7528\uff1f\u201d \u3000\u3000\u4f55\u5efa\u96c4\u7b11\u9053\uff1a\u201c\u8fd9\u516c\u5b50\u7237\u7684\u6765\u5934\u53ef\u5927\u7740\u5462\uff0c\u4ed6\u662f\u66f9\u632f\u955b\u7684\u5b9d\u8d1d\u513f\u5b50\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u503c\uff1a\u201c\u66f9\u632f\u955b\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4f55\u5efa\u96c4\u8be7\u9053\uff1a\u201c\u91d1\u76f8\u516c\u4ece\u4e0d\u8fc7\u95ee\u5916\u95f4\u4e4b\u4e8b\u5427\uff1f\u8fd9\u66f9\u632f\u955b\u5b98\u5c45\u6587\u534e\u6bbf\u5927\u5b66\u58eb\uff0c\u6b63\u662f\u5f53\u4eca\u7684 \u76f8\u56fd\u54ea\uff01\u5f53\u671d\u4e24\u4e2a\u6743\u76f8\uff0c\u6ee1\u4eba\u662f\u7a46\u5f70\u963f\uff0c\u6c49\u4eba\u5c31\u662f\u66f9\u632f\u955b\u3002\u6743\u67c4\u662f\u7a46\u5f70\u963f\u5927\u4e9b\uff0c\u4f46\u66f9\u632f\u955b\u9022 \u541b\u4e4b\u6076\uff0c\u52a9\u7ea3\u4e3a\u8650\uff0c\u4e13\u95e8\u7ed9\u9791\u5b50\u7687\u5e1d\u51fa\u4e3b\u610f\u6765\u6b3a\u538b\u6c49\u4eba\uff0c\u7f6a\u6076\u4e5f\u4e0d\u5728\u7a46\u5f70\u963f\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u6e05\u4ee3\u76f8\u6743\u5206\u6563\uff0c\u5185\u9601\u5927\u5b66\u58eb\u4e4b\u804c\uff0c\u5728\u8d5e\u7406\u673a\u52a1\uff0c\u8868\u7387\u767e\u50da\uff0c\u72b9\u53e4\u4e4b\u5bb0\u76f8\u3002\u6e05\u521d\u5b9a\u5236\uff0c\u5176 \u6570\u6ee1\u6c49\u5404\u4e8c\u5458\uff0c\u534f\u529e\u5927\u5b66\u58eb\uff0c\u6ee1\u6c49\u5404\u4e00\u5458\u3002\u7136\u5b9e\u6743\u591a\u5f52\u4e8e\u6ee1\u4eba\u5927\u5b66\u58eb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u6211\u4ece\u6d77\u5916\u56de\u6765\u6ca1\u6709\u591a\u4e45\uff0c\u8fd8\u672a\u77e5\u9053\u3002\u201d \u3000\u3000\u4f55\u5efa\u96c4\u9053\uff1a\u201c\u66f9\u632f\u955b\u53ea\u6709\u8fd9\u4e2a\u5b9d\u8d1d\u513f\u5b50\uff0c\u4efb\u4ed6\u5728\u4e61\u4e0b\u9c7c\u8089\u767e\u59d3\uff0c\u80e1\u4f5c\u975e\u4e3a\u3002\u4ed6\u8fd9\u5b9d\u8d1d\u513f \u5b50\u559c\u6b22\u7ec3\u6b66\uff0c\u95e8\u4e0b\u517b\u6709\u4e00\u73ed\u8d2a\u56fe\u5bcc\u8d35\u7684\u6c5f\u6e56\u4eba\u7269\uff0c\u6697\u4e2d\u4e5f\u4f5c\u671d\u5ef7\u7684\u8033\u76ee\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u65e9\u77e5\u5982\u6b64\uff0c\u6094\u4e0d\u8be5\u4e0d\u6253\u8fd9\u516c\u5b50\u7237\u4e00\u987f\u3002\u597d\uff0c\u5230\u4e86\u5317\u4eac\uff0c\u6211\u5012\u8981\u627e\u4ed6\u8001\u5b50 \u7684\u9ebb\u70e6\u3002\u201d\u201c \u3000\u3000\u4f55\u5efa\u96c4\u9053\uff1a\u201c\u91d1\u76f8\u516c\u662f\u8981\u5230\u5317\u4eac\u53bb\uff1f\u201d\u53f6\u91d1\u9010\u6d41\u9053\uff1a\uff1a\u201c\u6b63\u662f\u3002\u201d\u4f55\u5efa\u96c4\u9053\uff1a\u201c\u53ef\u662f\u4e0e \u8428\u603b\u7ba1\u4f5c\u5bff\u4e4b\u4e8b\u6709\u5173\uff1f\u201d\u91d1\u9010\u6d41\u8be7\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u4f55\u5efa\u96c4\u9053\uff1a\u201c\u8bf7\u95ee\u4f60\u7684\u8fd9\u5757\u7384\u94c1\u3001\u662f\u4e0d\u662f\u4ece\u516d\u5408\u5e2e\u7684\u624b\u4e0a\u593a\u8fc7\u6765\u7684\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u9519\u3002\u539f\u6765\u4f60\u4e5f\u77e5\u9053\u4e86\u8fd9\u5757\u7384\u94c1\u7684\u6765\u5386\u3002\u201d\u8fd9\u5757\u7384\u94c1\u662f\u516d\u5408\u5e2e\u4e4b\u5e2e\u4e3b\u8981\u9001 \u53bb\u7ed9\u8428\u603b\u7ba1\u505a\u5bff\u793c\u7684\uff0c\u4f55\u5efa\u96c4\u65e2\u7136\u77e5\u9053\u5b83\u7684\u6765\u5386\uff0c\u5f53\u7136\u53ef\u4ee5\u731c\u60f3\u5f97\u5230\u91d1\u9010\u6d41\u6b64\u6b21\u4e0a\u4eac\u662f\u4e0e\u8428 \u603b\u7ba1\u505a\u5bff\u6709\u5173\uff0c\u662f\u4ee5\u91d1\u9010\u6d41\u4e5f\u5c31\u4e0d\u7528\u518d\u95ee\u4e86\u3002 \u3000\u3000\u4f55\u5f69\u51e4\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u53f2\u767d\u90fd\u62ff\u53bb\u5df4\u7ed3\u8428\u603b\u7ba1\u7684\u7384\u94c1\u5417\uff1f\u53ef\u4e0d\u53ef\u4ee5\u7ed9\u6211\u5f00\u5f00 \u773c\u754c\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u884c\u5440\uff0c\u4e0d\u8fc7\u4f60\u53ef\u5f97\u5c0f\u5fc3\u62ff\u7740\uff0c\u7384\u94c1\u5f88\u91cd\uff0c\u522b\u5f04\u8dcc\u4e86\u3002\u201d \u3000\u3000\u4f55\u5f69\u51e4\u63a5\u4e86\u7384\u94c1\uff0c\u8eab\u5b50\u4fa7\u8fc7\u4e00\u8fb9\uff0c\u4e0d\u7531\u5f97\u5567\u5567\u79f0\u5947\uff1a\u201c\u679c\u7136\u771f\u662f\u5b9d\u8d1d\u3002\u90a3\u4e32\u591c\u660e\u73e0\u867d\u7136 \u4ef7\u503c\u8fde\u57ce\uff0c\u6bd4\u8d77\u8fd9\u5757\u7384\u94c1\u6765\uff0c\u53ea\u6015\u8fd8\u662f\u8fdc\u8fdc\u4e0d\u5982\u3002\u201d\u4f55\u5efa\u96c4\u7b11\u9053\uff1a\u201c\u4e0d\uff0c\u82e5\u662f\u5728\u8428\u798f\u9f0e\u773c \u4e2d\uff0c\u4f46\u770b\u4ed6\u4f1a\u66f4\u559c\u6b22\u90a3\u4e32\u660e\u73e0\u3002\u201d\u4f55\u5f69\u51e4\u9053\uff1a\u201c\u7239\u7239\u8bf4\u7684\u662f\u3002\u201d\u795e\u8272\u6709\u70b9\u9eef\u7136\uff0c\u968f\u5373\u628a\u7384\u94c1 \u4ea4\u8fd8\u91d1\u9010\u6d41\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u5bf9\u516d\u5408\u5e2e\u7684\u4e8b\u60c5\u5012\u662f\u6e05\u695a\u5f97\u5f88\uff0c\u5374\u4e0d\u77e5\u6709\u4f55\u5173\u7cfb\uff1f\u201d\u6b63\u60f3\u53d1 \u95ee\uff0c\u4f55\u5efa\u96c4\u5df2\u5148\u8bf4\u9053\uff1a\u201c\u91d1\u76f8\u516c\uff0c\u6211\u5411\u4f60\u6253\u542c\u4e00\u4e2a\u4eba\u3002\u516d\u5408\u5e2e\u4e2d\u6709\u4e2a\u674e\u6566\uff0c\u4f60\u53ef\u77e5\u9053\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u548c\u4ed6\u6b63\u662f\u9053\u4e0a\u7684\u670b\u53cb\u3002\u201d\u4f55\u5f69\u98ce\u767b\u65f6\u7709\u5fc3\u7ed3\u89e3\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u8fd9\u4e48 \u8bf4\uff0c\u4f60\u548c\u4ed6\u662f\u76f8\u8bc6\u7684\u4e86\u3002\u5374\u4e0d\u77e5\u662f\u4ec0\u4e48\u9053\u4e0a\u7684\u670b\u53cb\uff1f\u201d\u95ee\u5f97\u6709\u51e0\u5206\u7a1a\u6c14\uff0c\u4f46\u559c\u60a6\u4e4b\u60c5\u4ea6\u5df2\u8868 \u9732\u65e0\u9057\u3002 \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4ed6\u5077\u660e\u73e0\u3001\u6211\u5077\u7384\u94c1\uff0c\u6211\u548c\u4ed6\u6b63\u662f\u540c\u9053\uff0c\u5077\u7684\u90fd\u662f\u516d\u5408\u5e2e\u7684\u4e1c\u897f\uff0c \u4e0d\u8fc7\uff0c\u4ed6\u7684\u90a3\u4e32\u660e\u73e0\u5df2\u7ecf\u629b\u4e0b\u4e86\u65e0\u5e95\u6df1\u6f6d\uff0c\u5374\u662f\u6bd4\u4e0d\u4e0a\u6211\u7684\u8fd0\u9053\u4e86\u3002\u201d\u5f53\u4e0b\uff0c\u628a\u90a3\u6b21\u5728\u5f82\u5f95 \u5c71\u4e0a\u4e0e\u674e\u6566\u76f8\u9047\u7684\u4e8b\u60c5\u544a\u8bc9\u4e86\u4f55\u5bb6\u7236\u5973\u3002 \u3000\u3000\u4f55\u5efa\u96c4\u9053\uff1a\u201c\u591a\u4e8f\u91d1\u76f8\u516c\u5e2e\u4ed6\u5ea6\u8fc7\u4e86\u4e00\u6b21\u96be\u5173\u3001\u6211\u6b63\u62c5\u5fc3\u4ed6\u7ed9\u516d\u5408\u5e2e\u7684\u4eba\u6349\u56de\u53bb\u5462\uff0c\u4e0d \u77e5\u4ed6\u73b0\u5728\u4e0b\u843d\u5982\u4f55\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u8fc8\uff1a\u201c\u5f82\u5f95\u5c71\u5206\u624b\u4e4b\u540e\uff0c\u6211\u5c31\u4e0d\u77e5\u9053\u4ed6\u7684\u8baf\u606f\u4e86\u3002\u4e0d\u8fc7\uff0c\u4f60\u4e5f\u4e0d\u7528\u62c5\u5fc3\uff0c\u516d\u5408\u5e2e \u73b0\u5728\u6b63\u662f\u591a\u4e8b\u4e4b\u79cb\uff0c\u4ed6\u4eec\u8981\u53e6\u5916\u7b79\u529e\u793c\u7269\u9001\u4eac\uff0c\u8981\u627e\u5bfb\u5e2e\u4e3b\u7684\u59b9\u59b9\uff0c\u8fd8\u8981\u62a2\u56de\u8fd9\u5757\u7384\u94c1\uff0c\u54ea \u91cc\u8fd8\u6709\u7a7a\u6687\u53bb\u8ffd\u67e5\u674e\u6566\uff1f\u7384\u94c1\u6bd4\u660e\u73e0\u8d35\u91cd\uff0c\u6211\u53c8\u5e76\u4e0d\u662f\u9690\u7792\u884c\u8e2a\u7684\uff0c\u516d\u5408\u5e2e\u7684\u4eba\u8981\u5bf9\u4ed8\u6211\u4eec \u9996\u5148\u4e5f\u662f\u6765\u5bf9\u4ed8\u6211\uff01\u201d \u3000\u3000\u4f55\u5efa\u96c4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u5077\u4e86\u8fd9\u5757\u7384\u94c1\uff0c\u5bf9\u674e\u6566\u6765\u8bf4\uff0c\u7b49\u4e8e\u662f\u8f6c\u79fb\u4e86\u516d\u5408\u5e2e\u7684\u76ee\u6807\uff0c\u4f46\u5373 \u662f\u63a9\u62a4\u4e86\u4ed6\u3002\u4e0d\u8fc7\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u5374\u662f\u8981\u8fde\u7d2f\u4f60\u62c5\u5f53\u66f4\u591a\u7684\u98ce\u9669\uff0c\u6211\u4eec\u7236\u5973\u4e5f\u89c9\u5f97\u8fc7\u610f\u4e0d \u53bb\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u6015\u516d\u5408\u5e2e\u3002\u53f2\u767d\u90fd\u4e0d\u6765\u627e\u6211\uff0c\u6211\u4e5f\u60f3\u53bb\u627e\u4ed6\u5462\u3002\u4f55\u5927\u53d4\uff0c\u4f60\u5bf9\u674e\u6566 \u8fd9\u6837\u5173\u5fc3\uff0c\u4e0d\u77e5\u4ed6\u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u4f55\u5f69\u51e4\u7c89\u8138\u6cdb\u7ea2\uff0c\u4f4e\u4e0b\u5934\u6765\u3002\u4f55\u5efa\u96c4\u7b11\u9053\uff1a\u201c\u5bf9\u6069\u516c\u8fd8\u6015\u8bf4\u4e48\uff1f\u5b9e\u4e0d\u76f8\u7792\uff0c\u674e\u6566\u6b63\u662f\u5c0f \u5a7f\uff0c\u4ed6\u548c\u6211\u8fd9\u4e2b\u5934\u81ea\u5c0f\u8ba2\u4e0b\u7684\u4eb2\u4e8b\uff0c\u672c\u6765\u60f3\u5728\u4eca\u5e74\u7ed9\u4ed6\u4eec\u5c0f\u4fe9\u53e3\u5b8c\u5a5a\u7684\uff0c\u4e0d\u6599\u51fa\u4e86\u516d\u5408\u5e2e\u8fd9 \u4ef6\u4e8b\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5f97\u610f\u5fd8\u5f62\uff0c\u5927\u559c\u53eb\u9053\uff1a\u201c\u8fd9\u592a\u597d\u4e86\uff01\u201d\u8fd9\u4e48\u4e00\u53eb\uff0c\u66f4\u628a\u4f55\u5f69\u51e4\u7f9e\u5f97\u6ee1\u9762\u901a\u7ea2\u3002\u4e0d \u8fc7\uff0c\u5979\u53ea\u9053\u91d1\u9010\u6d41\u662f\u4e3a\u5979\u6b22\u559c\uff0c\u5374\u4e0d\u77e5\u9053\u91d1\u9010\u6d41\u662f\u4e3a\u81ea\u5df1\u6b22\u559c\u3002\u91d1\u9010\u6d41\u4e00\u76f4\u628a\u674e\u6566\u5f53\u4f5c\u53f2\u7ea2 \u82f1\u7684\u60c5\u4eba\uff0c\u5982\u4eca\u65b9\u59cb\u77e5\u9053\u5b8c\u5168\u662f\u5c5e\u4e8e\u8bef\u4f1a\u3002 \u3000\u3000\u4f55\u5efa\u96c4\u9053\uff1a\u201c\u51e4\u4e2b\u5934\u77e5\u9053\u4e86\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u62c5\u5fe7\u5f97\u4e0d\u5f97\u4e86\uff0c\u4e0d\u89c1\u7740\u674e\u6566\uff0c\u5979\u662f\u4e0d\u80fd\u5b89\u5fc3\u7684\u3002 \u6240\u4ee5\u6211\u53ea\u597d\u966a\u5979\u51fa\u6765\u5bfb\u627e\u3002\u4e3a\u4e86\u5bb9\u6613\u8ba9\u4ed6\u5f97\u77e5\u6d88\u606f\uff0c\u51e4\u4e2b\u5934\u51fa\u4e86\u4e2a\u4e3b\u610f\uff0c\u7531\u5979\u626e\u4f5c\u8bf4\u9f13\u4e66\u7684 \u59d1\u5a18\uff0c\u7a7f\u5dde\u8fc7\u5e9c\u7684\u5356\u5531\u3002\u4e5f\u5e78\u4e8f\u5979\u5c0f\u65f6\u5019\u559c\u6b22\u542c\u68a8\u82b1\u5927\u9f13\uff0c\u5531\u8d77\u6765\u4e5f\u8fd8\u52c9\u5f3a\u53ef\u4ee5\u5bf9\u4ed8\u8fc7\u53bb\uff01 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u5c82\u53ea\u5bf9\u4ed8\u8fc7\u53bb\uff0c\u7b80\u76f4\u7cbe\u5f69\u975e\u51e1\uff01\u4f60\u8fd9\u4e48\u5531\u5440\u5531\u7684\uff0c\u4e00\u5b9a\u4f1a\u628a\u674e\u6566\u5f15\u51fa \u6765\u3002\u201d\u4f55\u5f69\u51e4\u62bf\u7740\u5634\u513f\u9053\uff1a\u201c\u6069\u516c\u8bf4\u7b11\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u8981\u8d76\u5f80\u5317\u4eac\uff0c\u4f60\u4e5f\u8981\u5bfb\u627e\u674e\u6566\uff0c\u54b1\u4eec\u5c31\u5404\u5954\u524d\u7a0b\u4e86\u5427\u3002\u4f55\u59d1\u5a18\uff0c\u4f60\u82e5\u89c1 \u7740\u4e86\u674e\u6566\uff0c\u8bf7\u7ed9\u6211\u95ee\u4e00\u58f0\u597d\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u60c5\u6109\u5feb\uff0c\u8d70\u8def\u4e5f\u8d70\u5f97\u7279\u522b\u5feb\uff0c\u867d\u7136\u63d0\u7740\u6c89\u91cd\u7684\u7384\u94c1\uff0c\u4e00\u5929\u5de5\u592b\uff0c\u4e5f\u8d70\u4e86\u5c06\u8fd1\u4e09 \u767e\u91cc\u8def\uff0c\u7b2c\u4e8c\u5929\u4fbf\u6e21\u8fc7\u4e86\u9ec4\u6cb3\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u5230\u4e86\u79b9\u57ce\u3002 \u3000\u3000\u79b9\u57ce\u5728\u9ec4\u6cb3\u5317\u5cb8\uff0c\u76f8\u4f20\u662f\u5927\u79b9\u6cbb\u6c34\u65f6\u6240\u5efa\u7684\u57ce\u6c60\u3002\u57ce\u4e2d\u6709\u5ea7\u7740\u540d\u7684\u9152\u697c\uff0c\u540d\u201c\u4eea\u9192 \u697c\u201d\uff0c\u636e\u8bf4\u6700\u5148\u53d1\u660e\u917f\u9152\u7684\u4eba\u662f\u5927\u79b9\u7684\u81e3\u5b50\u4eea\u72c4\uff0c\u4ed6\u5236\u4f5c\u9152\u91aa\uff0c\u201c\u79b9\u8d4f\u4e4b\u5185\u7f8e\uff0c\u9042\u758f\u4eea \u72c4\u3002\u201d\u4f46\u5927\u79b9\u867d\u7136\u758f\u8fdc\u4eea\u72c4\u3001\u917f\u9152\u4e4b\u6cd5\u5374\u4f20\u4e86\u4e0b\u6765\uff0c\u7f8e\u9152\u9187\u91aa\uff0c\u4e16\u4eba\u65e0\u4e0d\u559c\u7231\u3002\u201c\u590f\u4eba\u597d \u9152\u201d\uff0c\u201c\u590f\u201d\u662f\u5927\u79b9\u6240\u5efa\u7684\u56fd\u53f7\uff0c\u53f2\u4e66\u4e0a\u4e5f\u662f\u6709\u8bb0\u8f7d\u7684\u3002\u79b9\u57ce\u4e2d\u7684\u8fd9\u5ea7\u201c\u4eea\u9192\u697c\u201d\u5f53\u7136\u4e5f\u5408 \u6709\u7eaa\u5ff5\u4eea\u72c4\u7684\u610f\u601d\u3002\u4e45\u800c\u4e45\u4e4b\uff0c\u4e5f\u5c31\u6210\u4e86\u79b9\u57ce\u7684\u4e00\u4e2a\u540d\u80dc\u4e86\u3002 \u3000\u3000\u8fd9\u5ea7\u9152\u91aa\u6bd4\u57ce\u4e2d\u7684\u6c11\u623f\u90fd\u9ad8\uff0c\u4fbf\u4e8e\u5ba2\u4eba\u773a\u671b\u9ec4\u6cb3\uff0c\u7f05\u6000\u5927\u79b9\u7684\u529f\u7ee9\u3002\u91d1\u9010\u6d41\u5230\u4e86\u79b9\u57ce\uff0c \u5c11\u4e0d\u514d\u8981\u5230\u4eea\u9192\u697c\u559d\u9152\u3002 \u3000\u3000\u8fd9\u4e00\u5929\u6e38\u4eba\u6781\u5c11\uff0c\u672c\u5730\u4eba\u5219\u662f\u4e60\u60ef\u5728\u665a\u4e0a\u624d\u559d\u9152\u7684\uff0c\u91d1\u9042\u6d41\u4e2d\u5348\u65f6\u5206\u8d70\u4e0a\u8fd9\u95f4\u9152\u697c\uff0c\u5ba2 \u4eba\u5c31\u53ea\u5f97\u4ed6\u4e00\u4e2a\uff0c\u91d1\u9010\u6d41\u66f4\u662f\u9ad8\u5174\uff0c\u5fc3\u60f3\u65e0\u4eba\u6270\u6211\u6e05\u5174\uff0c\u6b63\u597d\u75db\u996e\u4e00\u573a\uff0c\u4e8e\u662f\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u628a \u7384\u94c1\u653e\u5728\u684c\u5e95\uff0c\u4fbf\u5373\u53eb\u9152\u3002 \u3000\u3000\u91d1\u9010\u6d41\u628a\u7384\u94c1\u653e\u5728\u684c\u5e95\u4e0b\uff0c\u4e3a\u7684\u662f\u907f\u514d\u7ed9\u4eba\u6ce8\u76ee\uff0c\u5e78\u4e8f\u697c\u677f\u575a\u539a\uff0c\u627f\u53d7\u5f97\u8d77\uff0c\u4f46\u5f53\u4ed6\u8f7b \u8f7b\u653e\u4e0b\u4e4b\u65f6\uff0c\u4e5f\u53d1\u51fa\u4e86\u4e00\u9635\u8f67\u8f67\u7684\u58f0\u54cd\u3002\u9152\u4fdd\u770b\u4e86\u4e00\u773c\uff0c\u751a\u662f\u597d\u5947\uff0c\u5fc3\u60f3\uff1a\u201c\u4e00\u4e2a\u7834\u5323\u5b50\u600e \u7684\u5982\u6b64\u6c89\u91cd\uff1f\u201d\u4f46\u6599\u60f3\u7834\u5323\u5b50\u4e4b\u4e2d\u88c5\u7684\uff0c\u7edd\u4e0d\u4f1a\u662f\u4ec0\u4e48\u5b9d\u8d1d\uff0c\u770b\u4e86\u4e00\u773c\uff0c\u4e5f\u5c31\u7b97\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u628a\u9152\u51ed\u680f\uff0c\u8fdc\u773a\u9ec4\u6cb3\uff0c\u4f46\u89c1\u6d4a\u6d6a\u6ed4\u6ed4\uff0c\u6c34\u5929\u76f8\u63a5\uff0c\u671b\u773c\u96be\u7a77\u3002\u6bd4\u8d77\u4ed6\u6708\u524d\u6e21\u8fc7\u7684 \u6eda\u6eda\u957f\u6c5f\uff0c\u53c8\u662f\u522b\u6709\u4e00\u756a\u96c4\u4f1f\u7684\u6c14\u8c61\uff0c\u5168\u9010\u6d41\u6d6e\u60f3\u8fde\u7fe9\uff0c\u4e0d\u77e5\u4e0d\u89c9\u628a\u4e00\u58f6\u6c7e\u9152\u559d\u4e86\u4e00\u534a\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9152\u91cf\u4e0d\u5927\uff0c\u559d\u4e86\u534a\u58f6\u9152\uff0c\u5df2\u6709\u51e0\u5206\u9189\u610f\uff0c\u8fdc\u773a\u9ec4\u67ef\uff0c\u5fc3\u5934\u6005\u89e6\uff0c\u501a\u680f\u541f\u9053\uff1a\u201c\u4e09 \u5343\u5e74\u4e8b\u6b8b\u9e26\u5916\uff0c\u65e0\u97f3\u5026\u51ed\u79cb\u6811\uff0c\u901d\u6c34\u79fb\u5ddd\uff0c\u9ad8\u9675\u53d8\u8c37\uff0c\u90a3\u8bc6\u5f53\u65f6\u795e\u79b9\uff1f\u201d\u8fd9\u662f\u5357\u5b8b\u8bcd\u4eba\u5434\u68a6 \u7a97\u767b\u79b9\u9675\u6240\u4f5c\u7684\u8bcd\uff0c\u79b9\u9675\u5728\u6d59\u6c5f\u7ecd\u5174\u7684\u4f1a\u7a3d\u5c71\uff0c\u4e0e\u5c71\u4e1c\u7684\u79b9\u57ce\u76f8\u53bb\u4e0d\u6b62\u5343\u91cc\uff0c\u4e0d\u8fc7\u8fd9\u4e00\u9996\u8bcd \u5374\u6b63\u662f\u9002\u5408\u4ed6\u773c\u524d\u7684\u98ce\u5149\uff0c\u9053\u51fa\u4e86\u4ed6\u6b64\u9645\u5fc3\u4e2d\u7684\u611f\u6168\u3002 \u3000\u3000\u5927\u79b9\u6cbb\u6c34\uff0c\u662f\u4e09\u5343\u5e74\u4ee5\u524d\u7684\u5f80\u4e8b\u4e86\u3002\u4e09\u5343\u5e74\u6ca7\u6851\u53d8\u5316\uff0c\u5f80\u4e8b\u6d88\u6c89\uff0c\u65e9\u5df2\u6773\u4e0d\u53ef\u5bfb\uff0c\u6d88\u901d \u5728\u6b8b\u9e26\u5f71\u5916\u3002\u5f53\u5e74\u7684\u6c34\u9053\u4e0d\u77e5\u5df2\u7ecf\u51e0\u5ea6\u8fc1\u79fb\uff0c\u8038\u62d4\u7684\u9ad8\u5c71\u4e5f\u8bb8\u5df2\u6ca6\u4e3a\u6df1\u8c37\u4e86\u3002\u4f46\u5c3d\u7ba1\u5927\u79b9\u7684 \u529f\u8ff9\uff0c\u5982\u4eca\u5df2\u4e0d\u53ef\u5bfb\uff0c\u4ed6\u7684\u4e07\u4e16\u4e4b\u529f\uff0c\u6bd5\u7adf\u8fd8\u662f\u7559\u7ed9\u540e\u4eba\u8ffd\u601d\u6000\u5ff5\u3002\u201c\u90a3\u8bc6\u5f53\u65f6\u795e\u79b9\u201d\u8fd9\u4e00 \u53e5\u5c31\u542b\u6709\u4e24\u65b9\u9762\u7684\u610f\u601d\u3002\u91d1\u9010\u6d41\u8ffd\u601d\u5f80\u5723\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e3a\u767e\u59d3\u505a\u4e86\u597d\u4e8b\u7684\u4eba\uff0c\u767e\u59d3\u662f\u4e0d\u4f1a \u5fd8\u8bb0\u4ed6\u7684\u3002\u4e00\u4e2a\u4eba\u7684\u529b\u91cf\u6709\u5927\u5c0f\uff0c\u6211\u867d\u7136\u6bd4\u4e0d\u4e0a\u5927\u79b9\uff0c\u4e5f\u5e94\u8be5\u5b66\u4ed6\u7684\u6a21\u7965\uff0c\u7ed9\u767e\u59d3\u505a\u70b9\u597d \u4e8b\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6d6e\u60f3\u8fde\u7fe9\uff0c\u559d\u4e86\u51e0\u53e3\u9152\uff0c\u53c8\u7eed\u541f\u4e0b\u534a\u9619\u9053\uff1a\u201c\u5bc2\u5be5\u897f\u7a97\u5750\u4e45\uff0c\u6545\u4eba\u4fda\u4f1a\u9047\uff0c\u540c\u526a \u9563\u8bed\u3002\u79ef\u9f84\u6b8b\u7891\uff0c\u96f6\u4e3b\u65ad\u58c1\uff0c\u91cd\u62c2\u4eba\u95f4\u5c18\u58eb\u3002\u201d\u5434\u68a6\u7a97\u5f53\u5e74\u767b\u79b9\u9675\u4e4b\u65f6\uff0c\u662f\u548c\u597d\u53cb\u51af\u6df1\u539a\u540c \u53bb\u7684\uff0c\u56de\u5bb6\u4e4b\u540e\uff0c\u5c31\u548c\u597d\u53cb\u526a\u9519\u591c\u8bdd\uff0c\u6292\u53d1\u65e5\u95f4\u6240\u89c1\u6240\u89e6\u7684\u611f\u6168\u3001 \u3000\u3000\u91d1\u9010\u6d41\u5728\u541f\u5531\u8fd9\u53e5\u8bcd\u65f6\uff0c\u60f3\u8d77\u4e86\u53f2\u7ea2\u82f1\u6765\uff0c\u201c\u8981\u662f\u53f2\u59d1\u5a18\u4e5f\u5728\u8fd9\u513f\uff0c\u548c\u6211\u501a\u680f\u628a\u76cf\uff0c\u8c08 \u8bf4\u6ca7\u6851\uff0c\u8fd9\u610f\u5883\u8be5\u6709\u591a\u7f8e\uff01\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u4e0d\u7531\u5f97\u6005\u6005\u60d8\u60d8\uff0c\u8f7b\u62cd\u680f\u6746\uff0c\u4e00\u5531\u4e09\u53f9\u3002\u5e78\u4e8f\u8fd9\u9152 \u697c\u4e0a\u6ca1\u6709\u522b\u7684\u5ba2\u4eba\uff0c\u8981\u4e0d\u7136\u4e0d\u628a\u4ed6\u5f53\u4f5c\u75af\u5b50\u624d\u602a\u3002\u670d\u4f8d\u4ed6\u7684\u9152\u4fdd\uff0c\u5219\u662f\u770b\u60ef\u5ba2\u4eba\u7684\u9189\u6001\u7684\uff0c \u5012\u662f\u4e0d\u4ee5\u4e3a\u5f02\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u7b11\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u771f\u662f\u96c5\u4eba\uff01\u201d\u7b11\u58f0\u94ff\u94ff\u9535\u9535\uff0c\u5b9b\u5982\u91d1\u5c5e\u4ea4\u51fb\uff0c\u523a\u8033 \u975e\u5e38\u3002\u91d1\u9010\u6d41\u6115\u7136\u56de\u987e\uff0c\u53ea\u89c1\u6709\u4e24\u4e2a\u4eba\u5df2\u662f\u4e0a\u4e86\u9152\u697c\uff0c\u8bf4\u8bdd\u7684\u8fd9\u4e2a\u4eba\u8d70\u5728\u524d\u9762\uff0c\u7ea6\u56db\u5341\u5c81\u5de6 \u53f3\u5e74\u7eaa\uff0c\u9ad8\u989d\u864e\u9894\uff0c\u76f8\u8c8c\u54b8\u6b66\u3002\u8ddf\u5728\u4ed6\u540e\u9762\u7684\u90a3\u4e2a\u4eba\u5219\u662f\u5bab\u79c9\u85e9\u3002\u91d1\u9010\u6d41\u542c\u4e86\u8fd9\u4eba\u7684\u7b11\u58f0\u5df2 \u662f\u8be7\u5f02\uff0c\u770b\u5230\u5b98\u79c9\u85e9\uff0c\u66f4\u611f\u5230\u610f\u5916\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5df2\u6709\u516d\u4e03\u5206\u9152\u610f\uff0c\u5750\u56de\u539f\u5ea7\uff0c\u4e3e\u8d77\u9152\u58f6\uff0c\u7acb\u5373\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u771f\u662f\u4eba\u751f\u4f55\u5904\u4e0d\u76f8 \u9022\uff0c\u6765\u6765\u6765\u2026\u2026\u201d\u4ed6\u6b63\u8981\u62db\u547c\u5bab\u79c9\u85e9\u6765\u5e72\u4e00\u676f\uff0c\u5ffd\u89c1\u5bab\u79c9\u85e9\u671d\u7740\u4ed6\u6447\u4e86\u6447\u5934\uff0c\u6253\u4e86\u4e2a\u773c\u8272\uff0c \u8ddf\u7740\u6307\u5411\u7a97\u53e3\u3002\u5bab\u79c9\u85e9\u5728\u90a3\u4eba\u80cc\u540e\uff0c\u4ed6\u7ed9\u91d1\u9010\u6d41\u4f5c\u624b\u52bf\uff0c\u53ea\u6709\u5728\u4ed6\u524d\u9762\u7684\u91d1\u9010\u6d41\u770b\u5f97\u89c1\u3002 \u3000\u3000\u91d1\u9010\u6d41\u867d\u6709\u51e0\u5206\u9152\u610f\uff0c\u5bf9\u4ed6\u8fd9\u51e0\u4e0b\u6781\u4e3a\u666e\u901a\u7684\u624b\u52bf\u3001\u773c\u8272\u8fd8\u662f\u80fd\u591f\u9886\u609f\u7684\u3002\u7b2c\u4e00\u3001\u5bab\u79c9 \u85e9\u8981\u4ed6\u88c5\u4f5c\u4e24\u4e0d\u76f8\u8bc6\uff1b\u7b2c\u4e8c\u3001\u5bab\u79c9\u85e9\u8981\u4ed6\u5feb\u9003\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4e2a\u4eba\u662f\u4ec0\u4e48\u5389\u5bb3\u7684\u811a\u8272\uff0c\u8981\u6211\u6015\u4ed6\uff1f\u201d\u4f46\u5bab\u79c9\u85e9\u8fde\u4e00\u53e5\u8bdd\u4e5f \u4e0d\u6562\u548c\u91d1\u9010\u6d41\u8bf4\uff0c\u53ea\u6562\u5728\u90a3\u4eba\u80cc\u540e\u793a\u610f\uff0c\u663e\u7136\u5bab\u79c9\u85e9\u662f\u5bb3\u6015\u8fd9\u4e2a\u4eba\u7684\u4e86\u3002\u91d1\u9010\u6d41\u5927\u4e3a\u5947\u602a\uff0c \u5bab\u79c9\u85e9\u7684\u5251\u6cd5\u548c\u50b2\u6c14\u90fd\u662f\u4ed6\u9886\u6559\u8fc7\u7684\uff0c\u4ed6\u8d25\u5728\u81ea\u5df1\u7684\u5251\u4e0b\u5c1a\u4e14\u505a\u5cb8\u5982\u6545\uff0c\u5982\u4eca\u5374\u8868\u73b0\u5f97\u5982\u6b64 \u7684\u6015\u8fd9\u4e2a\u4eba\uff0c\u8fd9\u771f\u662f\u4ee4\u4eba\u610f\u60f3\u4e0d\u5230\u7684\u602a\u4e8b\uff01 \u3000\u3000\u672c\u6765\u91d1\u9010\u6d41\u5c31\u8981\u8bf4\u51fa\u201c\u5bab\u9999\u4e3b\u201d\u8fd9\u4e09\u4e2a\u5b57\uff0c\u8bf7\u5bab\u79c9\u85e9\u6765\u5e72\u4e00\u676f\u7684\uff0c\u770b\u89c1\u4e86\u4ed6\u7684\u624b\u52bf\uff0c\u5fc3 \u91cc\u867d\u7136\u89c9\u5f97\u5947\u602a\uff0c\u4f46\u4e5f\u4e0d\u613f\u4ee4\u5bab\u79c9\u85e9\u96be\u4e3a\uff0c\u4e8e\u662f\u7acb\u5373\u4f4f\u53e3\u3002\u4e0d\u8fc7\uff0c\u5bab\u79c9\u85e9\u793a\u610f\u8981\u4ed6\u9003\u8d70\uff0c\u4ed6 \u53ef\u8fd8\u662f\u5927\u9a6c\u91d1\u5200\u7684\u5750\u7740\uff0c\u800c\u4e14\u8fd8\u7279\u522b\u7528\u795e\u7684\u5411\u90a3\u4e2a\u4eba\u591a\u671b\u4e86\u4e24\u773c\uff01 \u3000\u3000\u90a3\u4e2a\u4eba\u8bef\u4f1a\u91d1\u9010\u6d41\u662f\u62db\u547c\u4ed6\uff0c\u5927\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u771f\u6709\u610f\u601d\u3002\u5bf9\u3001\u5bf9\u3001\u5bf9\uff01\u4eba\u751f\u4f55\u5904 \u4e0d\u76f8\u9022\uff0c\u76f8\u9022\u4f55\u5fc5\u66fe\u76f8\u8bc6\u3002\u6765\u3001\u6765\u3001\u6765\uff0c\u6211\u548c\u4f60\u559d\u9152\uff01\u6211\u5148\u656c\u4f60\u4e00\u676f\u3002\u201d \u3000\u3000\u5bab\u79c9\u85e9\u9762\u4e0a\u53d8\u8272\uff0c\u53c8\u518d\u6447\u9996\u793a\u610f\u3002\u91d1\u9010\u6d41\u4f6f\u4f5c\u4e0d\u77e5\uff0c\u8bf4\u9053\uff1a\u516c\u5b50\uff0c\u76f8\u8bf7\u4e0d\u5982\u5076\u9047\uff0c\u90a3\u5c31 \u8bf7\u8fc7\u6765\u5427\u3002\u83ab\u8bf4\u4e00\u676f\uff0c\u5341\u676f\u4e5f\u6210\uff01\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u770b\u6b64\u4eba\u6c14\u5b87\u4e0d\u51e1\uff0c\u5b9a\u662f\u6c5f\u6e56\u8c6a\u5ba2\u3002\u7ba1\u4ed6\u662f \u8c01\uff0c\u5148\u7ed3\u8bc6\u4e86\u518d\u8bf4\u3002\u201d\u91d1\u9010\u6d41\u9152\u610f\u5df2\u6d53\uff0c\u6367\u7740\u9152\u58f6\u7ad9\u4e86\u8d77\u6765\uff0c\u72c2\u6001\u6bd5\u73b0\u3002 \u3000\u3000\u90a3\u4eba\u8d8a\u53d1\u9ad8\u5174\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u9152\u91cf\u5982\u4f55\uff0c\u6562\u4e0d\u6562\u548c\u6211\u8d5b\u9152\uff1f\u201d\u8bf4\u7f62\uff0c\u53c8\u56de\u5934\u53bb\u5bf9 \u5bab\u79c9\u85e9\u8bf4\u9053\uff1a\u201c\u81ea\u4ece\u90a3\u5e74\u6211\u548c\u4f60\u4eec\u7684\u8235\u4e3b\u8d4c\u8d5b\u559d\u9152\u4e0d\u5206\u80dc\u8d1f\u4e4b\u540e\uff0c\u5341\u5e74\u6765\u5df2\u662f\u672a\u9022\u654c\u624b \u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9152\u610f\u4e0a\u6d8c\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u548c\u4f60\u8d4c\u9152\uff01\u201d\u90a3\u4eba\u7b54\u9053\uff1a\u201c\u8001\u5f1f\u8c6a\u60c5\u80dc\u6168\uff0c\u4e16\u6240\u7f55 \u89c1\u3002\u4eca\u65e5\u8d4c\u9152\uff0c\u8c01\u80dc\u8c01\u53d6\uff0c\u6211\u90fd\u4ea4\u4e0a\u4e86\u4f60\u8fd9\u4e2a\u670b\u53cb\u4e86\u3002\u597d\uff0c\u9152\u4fdd\uff0c\u62ff\u4e00\u7f38\u6700\u597d\u7684\u6c7e\u9152\u6765\uff01\u201d \u3000\u3000\u90a3\u4eba\u6367\u8d77\u9152\u7f38\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4e09\u5341\u65a4\u4e00\u7f38\u7684\uff0c\u6050\u6015\u4e0d\u591f\u6211\u559d\uff0c\u518d\u62ff\u4e00\u7f38\u6765\uff01\u201d\u9152\u4fdd\u5f20\u5927 \u773c\u775b\uff0c\u4f38\u51fa\u820c\u5934\uff0c\u5fc3\u60f3\uff1a\u201c\u6709\u751f\u610f\u597d\u505a\uff0c\u7ba1\u4f60\u559d\u5f97\u4e86\u559d\u4e0d\u4e86\uff1f\u201d\u4e8e\u662f\u518d\u6367\u51fa\u4e00\u7f38\u6c7e\u9152\uff0c\u653e\u5728 \u91d1\u9010\u6d41\u7684\u65c1\u8fb9\uff0c\u5e76\u5728\u4ed6\u4eec\u4e24\u4eba\u7684\u9762\u524d\uff0c\u90fd\u6446\u4e86\u4e00\u53ea\u6d77\u7897\u3002 \u3000\u3000\u90a3\u4eba\u8fd9\u624d\u62db\u547c\u5b98\u79c9\u85e9\u9053\uff1a\u201c\u5bab\u9999\u4e3b\uff0c\u4f60\u4e5f\u6765\u5427\uff01\u201d\u5bab\u79c9\u85e9\u7b11\u9053\uff1a\u201c\u6211\u7684\u9152\u91cf\u4e0d\u6210\uff0c\u8fd9\u4e2a \u70ed\u95f9\u6211\u662f\u4e0d\u80fd\u51d1\u4e86\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u9152\u91cf\u5927\u5c0f\uff0c\u52a0\u5165\u996e\u6c34\uff0c\u51b7\u6696\u81ea\u77e5\u3002\u8fd9\u662f\u52c9\u5f3a\u4e0d\u5f97\u7684\u3002\u597d\uff0c\u4f60\u4e0d\u53c2\u52a0\uff0c\u90a3 \u5c31\u8bf7\u5728\u4e00\u65c1\u7ed9\u6211\u4eec\u4f5c\u4e2a\u88c1\u5224\u5427\u3002\u6211\u548c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u8d4c\u8d5b\u559d\u9152\uff0c\u4e00\u4eba\u4e00\u7897\uff0c\u8f6e\u6d41\u5730\u559d\uff0c\u8c01\u82e5\u559d\u4e0d \u4e0b\u53bb\uff0c\u90a3\u5c31\u8f93\u4e86\u3002\u201d \u3000\u3000\u5bab\u79c9\u85e9\u7b11\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\uff0c\u4f60\u662f\u8457\u540d\u7684\u9152\u9738\uff0c\u6211\u4eec\u7684\u8235\u4e3b\u81ea\u5938\u9152\u575b\u65e0\u6545\uff0c\u5bf9\u4f60\u7684\u6d77\u91cf\u4e5f\u662f \u5341\u5206\u4f69\u670d\u7684\u3002\u8fd9\u573a\u8d4c\u8d5b\uff0c\u80dc\u8d1f\u65e9\u660e\uff0c\u8fd8\u7528\u5f97\u7740\u6211\u6765\u4f5c\u88c1\u5224\u5417\uff1f\u6211\u770b\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u5df2\u7ecf\u559d\u5f97\u5dee\u4e0d \u591a\u4e86\uff0c\u5f04\u9189\u4e86\u4ed6\u53ef\u4e0d\u597d\u3002\u4e0d\u5982\u4f60\u4eec\u90fd\u559d\u4e09\u7897\uff0c\u5f53\u4f5c\u662f\u4ea4\u4e2a\u670b\u53cb\u5982\u4f55\uff1f\u201d \u3000\u3000\u5bab\u79c9\u85e9\u8fd9\u756a\u8bf4\u8bdd\uff0c\u5176\u5b9e\u4e43\u662f\u5728\u6697\u4e2d\u544a\u8bc9\u91d1\u9010\u6d41\u8fd9\u6c49\u5b50\u662f\u4ec0\u4e48\u4eba\u3002\u91d1\u9010\u6d41\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c \u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4eba\u5c31\u662f\u7ea2\u82f1\u7684\u54e5\u54e5\u2014\u2014\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\u53f2\u767d\u90fd\uff01\u602a\u4e0d\u5f97\u5bab\u79c9\u85e9\u793a\u610f\u53eb\u6211\u5feb \u8d70\u4e86\u3002\u201d\u540c\u65f6\u4ed6\u4e5f\u660e\u767d\u4e86\u4f55\u4ee5\u5bab\u79c9\u85e9\u770b\u6765\u597d\u50cf\u6709\u70b9\u5bb3\u6015\u53f2\u767d\u90fd\u7684\u539f\u56e0\u4e86\uff0c\u56e0\u4e3a\u516d\u5408\u5e2e\u548c\u7ea2\u7f28 \u4f1a\u4e43\u662f\u6c5f\u6e56\u4e0a\u5206\u5ead\u6297\u793c\u7684\u5927\u5e2e\u4f1a\uff0c\u53f2\u767d\u90fd\u7684\u5730\u4f4d\u4e0e\u7ea2\u7f28\u4f1a\u7684\u8235\u4e3b\u662f\u76f8\u7b49\u7684\uff0c\u5bab\u79c9\u85e9\u53ea\u4e0d\u8fc7\u662f \u7ea2\u7f28\u4f1a\u4e0b\u9762\u7684\u4e00\u4e2a\u9999\u4e3b\uff0c\u6240\u4ee5\u4ed6\u4e0d\u4e00\u5b9a\u662f\u5bb3\u6015\u53f2\u767d\u90fd\uff0c\u4f46\u4e0d\u80fd\u4e0d\u5bf9\u53f2\u767d\u90fd\u6267\u793c\u751a\u606d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6b63\u60f3\u4f1a\u4e00\u4f1a\u53f2\u767d\u90fd\uff0c\u5018\u82e5\u4ed6\u4e0d\u77e5\u9053\u4e5f\u8fd8\u7f62\u4e86\u3002\u77e5\u9053\u4e86\u662f\u53f2\u767d\u90fd\uff0c\u4ed6\u8fd8\u5982\u4f55\u80af\u8d70\uff1f \u5f53\u4e0b\u9152\u610f\u4e0a\u6d8c\uff0c\u773c\u775b\u4e00\u77aa\uff0c\u8bf4\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u559d\u9189\u4e86\uff1f\u6211\u559d\u9152\u4ece\u6765\u662f\u4e0d\u670d\u8f93\u7684\uff0c\u7ba1\u4ed6\u9152\u738b\u4e5f \u597d\uff0c\u9152\u9738\u4e5f\u597d\uff0c\u975e\u5f97\u8f83\u91cf\u8f83\u91cf\u4e0d\u53ef\uff01\u201d\u672c\u6765\u8fd8\u53ea\u662f\u6709\u516d\u4e03\u5206\u7684\u9152\u610f\uff0c\u5374\u88c5\u51fa\u4e86\u6709\u516b\u4e5d\u5206\u7684\u9189 \u6001\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u6211\u5c31\u6b63\u662f\u559c\u6b22\u5c11\u5e74\u4eba\u6709\u8fd9\u4e48\u4e00\u80a1\u4e0d\u670d\u8f93\u7684\u813e\u6c14\u3002\u5bab\u9999\u4e3b\uff0c \u4f60\u4e0d\u7528\u66ff\u8fd9\u4f4d\u5c0f\u5144\u5f1f\u62c5\u5fc3\uff0c\u559d\u9189\u4e86\uff0c\u6211\u7167\u6599\u4ed6\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u793c\u5c1a\u5f80\u6765\uff0c\u4f60\u559d\u9189\u4e86\u6211\u4e5f\u4e00\u6837\u7167\u6599\u4f60\u3002\u597d\uff0c\u559d\u5427\uff01\u201d\u6367\u8d77\u9152\u575b\uff0c\u5012 \u6ee1\u6d77\u7897\uff0c\u4e00\u53e3\u6c14\u5148\u81ea\u559d\u5e72\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u7fd8\u8d77\u62c7\u6307\u8d5e\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u597d\u723d\u5feb\uff01\u201d\u8ddf\u7740\u559d\u4e86\u4e00\u5927\u7897\u3002\u4e24\u4eba\u8f6e\u6d41\u559d\u4e0b\u53bb\uff0c\u4e0d\u8fc7 \u7247\u523b\uff0c\u5df2\u662f\u5404\u81ea\u559d\u4e86\u5341\u7897\u4e4b\u591a\u3002\u4ed6\u4eec\u7528\u7684\u662f\u7279\u5927\u7684\u6d77\u7897\uff0c\u4e00\u7897\u8db3\u6709\u4e00\u65a4\u3002\u4e0d\u8fc7\u4ed6\u4eec\u9762\u524d\u5404\u6709 \u4e00\u7f38\u4e09\u5341\u65a4\u7684\u6c7e\u9152\uff0c\u559d\u4e86\u5341\u5927\u7897\uff0c\u4e5f\u4e0d\u8fc7\u559d\u4e86\u4e09\u5206\u4e4b\u4e00\u800c\u5df2\u3002 \u3000\u3000\u53ea\u89c1\u91d1\u9010\u6d41\u5927\u6c57\u6dcb\u6f13\uff0c\u5934\u4e0a\u597d\u50cf\u5f00\u4e86\u4e00\u4e2a\u84b8\u7b3c\u4f3c\u7684\uff0c\u5192\u51fa\u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u6e10\u6e10\u5c31\u53d8\u6210\u4e86 \u4e00\u56e2\u6d53\u96fe\u3002\u9152\u697c\u4e0a\u7684\u4f19\u8ba1\u90fd\u4e0d\u7981\u5567\u5567\u79f0\u5947\uff0c\u56f4\u62e2\u4e86\u6765\u770b\u3002\u539f\u6765\u91d1\u9010\u6d41\u867d\u6709\u9152\u610f\uff0c\u5e76\u4e0d\u7cca\u6d82\uff0c \u4ed6\u5f53\u7136\u77e5\u9053\u81ea\u5df1\u7684\u9152\u91cf\u4e0d\u884c\uff0c\u82e5\u7136\u771f\u4e2a\u4e0e\u53f2\u767d\u90fd\u8d4c\u8d5b\uff0c\u83ab\u8bf4\u4e00\u575b\uff0c\u53ea\u987b\u4e09\u7897\u53ea\u6015\u81ea\u5df1\u5c31\u8981\u9189 \u4e86\u3002\u4ed6\u662f\u7528\u6700\u4e0a\u4e58\u7684\u5185\u529f\uff0c\u628a\u559d\u4e0b\u53bb\u7684\u9152\u7acb\u5373\u5316\u6210\u6c57\u6c34\u84b8\u53d1\u51fa\u6765\uff0c\u6545\u6b64\u4ed6\u591a\u559d\u4e86\u4e00\u7897\uff0c\u5c31\u53cd \u800c\u591a\u6e05\u9192\u4e00\u5206\u3002 \u3000\u3000\u53f2\u767d\u90fd\u559d\u6ee1\u4e86\u5341\u7897\uff0c\u628a\u6d77\u7897\u4e00\u987f\uff0c\u5927\u7b11\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4f60\u4e0d\u662f\u4e0e\u6211\u8d4c\u8d5b\u559d\u9152\uff0c\u4f60\u662f\u8981\u4e0e \u6211\u6bd4\u62fc\u5185\u529f\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u53ea\u662f\u8bf4\u4e0e\u6211\u8d4c\u8d5b\u559d\u9152\uff0c\u53ea\u8981\u80fd\u591f\u559d\u4e0b\u53bb\u5c31\u884c\u3002\u81f3\u4e8e\u6211\u7528\u4ec0\u4e48\u65b9\u6cd5\u6765\u559d\uff0c \u90a3\u53ef\u5c31\u662f\u6211\u7684\u4e8b\u4e86\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u559c\u6b22\u600e\u4e48\u559d\u5c31\u600e\u4e48\u559d\uff0c\u53ea\u8981\u4f60\u8d62\u5f97\u4e86\u6211\uff0c\u6211\u4e0d\u4f1a\u4e0d\u8ba4\u8f93\u7684\u3002\u4f46 \u6211\u5374\u4e0d\u80fd\u5360\u4f60\u7684\u4fbf\u5b9c\uff0c\u4f60\u5c31\u7167\u73b0\u5728\u8fd9\u6837\u559d\u4e0b\u53bb\u5427\uff0c\u6211\u5012\u60f3\u770b\u770b\u662f\u6211\u7684\u9152\u91cf\u5927\u8fd8\u662f\u4f60\u7684\u5185\u529f \u6df1\uff1f\u201d\u8fd9\u8bdd\u7684\u610f\u601d\u5373\u662f\u4ed6\u8981\u4ec5\u51ed\u9152\u91cf\u53d6\u80dc\uff0c\u51b3\u4e0d\u8fd0\u7528\u5185\u529f\u53d6\u5de7\u3002\u518d\u8bf4\u5f97\u660e\u767d\u4e9b\uff0c\u8fd9\u4e00\u65b9\u9762\u662f \u4ed6\u5bf9\u4e8e\u81ea\u5df1\u9152\u91cf\u7684\u81ea\u8c6a\uff1b\u53e6\u4e00\u65b9\u9762\u5219\u662f\u4ed6\u5bf9\u81ea\u5df1\u7684\u5185\u529f\u4e5f\u662f\u6781\u4e3a\u81ea\u8d1f\uff0c\u8ba4\u4e3a\u82e5\u7528\u5185\u529f\u80dc\u4e86\u91d1 \u9010\u6d41\uff0c\u90a3\u5b9e\u5728\u662f\u80dc\u4e4b\u4e0d\u6b66\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u58f0\u4e0d\u54cd\uff0c\u6367\u8d77\u9152\u7f38\uff0c\u7d22\u6027\u7897\u4e5f\u4e0d\u7528\uff0c\u76f4\u63a5\u628a\u9152\u5012\u4eba\u53e3\u4e2d\u3002\u8f6c\u773c\u95f4\u809a\u76ae\u80c0\u5f97\u5706\u9f13 \u9f13\u7684\u4f3c\u4e2a\u76ae\u7403\u3002\u53f2\u767d\u90fd\u51fb\u6848\u8d5e\u9053\uff1a\u201c\u597d\uff0c\u996e\u5982\u957f\u9cb8\u5438\u767e\u5ddd\uff0c\u5c0f\u8001\u5f1f\uff0c\u6211\u53f2\u767d\u90fd\u7b97\u662f\u670d\u4e86\u4f60 \u4e86\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u5fc3\u4e2d\u5374\u6709\u6240\u7591\uff1a\u201c\u8fd9\u5c0f\u4f19\u5b50\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5185\u529f\u5374\u662f\u7cbe\u7eaf\u4e4b\u6781\uff0c\u4ed6\u662f\u8c01 \u5462\uff1f\u201d\u53f2\u767d\u90fd\u4ea4\u6e38\u5e7f\u9614\uff0c\u6b66\u6797\u4e2d\u7684\u540e\u8d77\u4e4b\u79c0\uff0c\u4ed6\u672a\u89c1\u8fc7\u4ea6\u6709\u6240\u95fb\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u90fd\u60f3\u4e0d\u5230\u6709\u54ea \u4e2a\u540d\u5bb6\u5f1f\u5b50\u4f1a\u6709\u5982\u6b64\u529f\u529b\u3002\u84e6\u5730\u5fc3\u5934\u4e00\u52a8\uff1a\u201c\u83ab\u975e\u5c31\u662f\u90a3\u4e2a\u5c0f\u53eb\u5316\u4e48\uff1f\u201d \u3000\u3000\u516d\u5408\u5e2e\u4e2d\u7684\u56db\u5927\u9ad8\u624b\uff1a\u9752\u7b26\u3001\u7126\u78ca\u3001\u5706\u6d77\u3001\u8463\u5341\u5a18\uff0c\u90fd\u66fe\u7ecf\u6216\u591a\u6216\u5c11\uff0c\u5403\u8fc7\u91d1\u9010\u6d41\u7684 \u4e8f\uff0c\u9752\u7b26\uff0c\u7126\u78ca\u6700\u521d\u5728\u5f82\u5f95\u5c71\u4e0a\u6240\u78b0\u89c1\u7684\u91d1\u9010\u6d41\u4e43\u662f\u4f5c\u5c0f\u53eb\u5316\u6253\u626e\u7684\uff0c\u662f\u4ee5\u5728\u53f2\u767d\u90fd\u7684\u8111\u6d77 \u4e2d\u4e00\u76f4\u7559\u4e0b\u4e86\u9752\u7b26\u3001\u7126\u78ca\u4e8c\u4eba\u6240\u63cf\u8ff0\u7684\u90a3\u4e2a\u201c\u5c0f\u53eb\u5316\u201d\u7684\u5f62\u8c61\uff0c\u867d\u7136\u4ed6\u540e\u6765\u4ea6\u5df2\u77e5\u9053\u4e86\u91d1\u9010 \u6d41\u5e76\u975e\u5c0f\u53eb\u5316\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7684\u90a3\u4e2a\u5323\u5b50\u653e\u5728\u684c\u5e95\uff0c\u53f2\u767d\u90fd\u5fc3\u6709\u6240\u7591\uff0c\u4e0d\u514d\u7559\u5fc3\u89c2\u5bdf\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u89c1\u7740\u4e86\u90a3\u4e2a \u5323\u5b50\u3002\u5323\u5b50\u5df2\u975e\u539f\u6765\u7684\u90a3\u4e2a\u7ea2\u6f06\u5323\u5b50\uff0c\u4f46\u5927\u5c0f\u5f62\u72b6\u5374\u662f\u76f8\u540c\u3002 \u3000\u3000\u53f2\u767d\u90fd\u4e0d\u52a8\u58f0\u8272\uff0c\u811a\u5c16\u4e00\u6843\uff0c\u90a3\u4e00\u767e\u591a\u65a4\u91cd\u7684\u7384\u94c1\u7ed9\u4ed6\u8e22\u5f97\u98de\u8d77\uff0c\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u628a\u684c \u5b50\u9707\u88c2\uff0c\u597d\u4f3c\u7a81\u7136\u4ece\u5730\u4e0a\u5192\u8d77\u6765\u4f3c\u7684\u3002 \u3000\u3000\u827e\u767d\u90fd\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u539f\u6765\u4f60\u5c31\u662f\u90a3\u59d3\u91d1\u7684\u5c0f\u5b50\uff01\u201d\u987e\u4e0d\u5f97\u6253\u91d1\u9010\u6d41\uff0c\u5148\u53bb\u6293\u90a3\u7384\u94c1\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5ffd\u5730\u4e00\u63c9\u809a\u76ae\uff0c\u53eb\u9053\uff1a\u201c\u554a\u5440\uff0c\u6211\u53ef\u771f\u662f\u559d\u9189\u4e86\uff01\u201d\u5634\u5df4\u4e00\u5f20\uff0c\u7a81\u7136\u4e00\u80a1\u9152\u6d6a\u55b7 \u4e86\u51fa\u6765\u3002 \u3000\u3000\u53f2\u767d\u90fd\u6325\u8896\u4e00\u62c2\uff0c\u9152\u6d6a\u5316\u4f5c\u4e86\u6ee1\u7a7a\u6d12\u4e0b\u7684\u96e8\u70b9\uff0c\u56db\u9762\u98de\u6563\uff0c\u56f4\u62e2\u5728\u8fd9\u5f20\u684c\u5b50\u5468\u56f4\u770b\u4ed6\u4eec \u8d4c\u9152\u7684\u90a3\u51e0\u4e2a\u4f19\u8ba1\uff0c\u7ed9\u91d1\u9010\u6d41\u7528\u4e0a\u4e58\u5185\u529f\u55b7\u51fa\u6765\u7684\u9152\u73e0\u6d12\u7740\uff0c\u75db\u5f97\u54c7\u54c7\u5927\u53eb\u3002\u53f2\u767d\u90fd\u867d\u7136\u5f97 \u514d\u9152\u6d6a\u6dcb\u5934\u4e4b\u8fb1\uff0c\u4f46\u773c\u524d\u767d\u832b\u832b\u4e00\u7247\uff0c\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u4e5f\u4e0d\u6562\u5f20\u5f00\u773c\u775b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e58\u673a\u5c31\u62a2\u7384\u94c1\uff0c\u53f2\u767d\u90fd\u542c\u58f0\u8fa8\u5411\uff0c\u201c\u547c\u201d\u7684\u4e00\u638c\u626b\u8fc7\u53bb\u3002\u91d1\u9010\u6d41\u63a5\u4e86\u4ed6\u7684\u4e00\u638c\uff0c \u53eb\u9053\uff1a\u201c\u597d\u529f\u592b\uff0c\u8fd9\u91cc\u4e0d\u4fbf\u65bd\u5c55\uff0c\u54b1\u4eec\u4e0b\u9762\u6253\u53bb\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u53ef\u522b\u9003\uff01\u201d\u4e00\u4e2a\u201c\u731b\u9e70\u6251\u5154\u201d\uff0c\u7a7f\u7a97\u800c\u51fa\uff0c\u7d27\u7d27\u8ddf\u5728\u91d1\u9010\u6d41\u7684\u540e \u9762\uff0c\u53cc\u53cc\u843d\u4e0b\u8857\u5fc3\u3002\u91d1\u9010\u6d41\u672a\u66fe\u62a2\u7740\u7384\u94c1\uff0c\u5148\u81ea\u8df3\u4e0b\uff0c\u53f2\u767d\u90fd\u6599\u60f3\u5bab\u79c9\u85e9\u81ea\u4f1a\u4ee3\u62ff\uff0c\u653e\u4e0b\u4e86 \u5fc3\uff0c\u53ea\u6015\u91d1\u9010\u6d41\u8981\u9003\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u67b6\u6211\u662f\u65e9\u5df2\u60f3\u548c\u4f60\u6253\u7684\u4e86\u3002\u6211\u600e\u4e48\u4f1a\u9003\uff1f\u201d\u53cd\u624b\u4e00\u62db\u201c\u9f99\u9888\u53d6 \u73e0\u201d\uff0c\u638c\u6307\u517c\u65bd\uff0c\u8d81\u7740\u53f2\u767d\u90fd\u7acb\u8db3\u672a\u7a33\uff0c\u4fbf\u653b\u4ed6\u7684\u4e0a\u76d8\u3002 \u3000\u3000\u53f2\u767d\u90fd\u6a2a\u80b1\u4e00\u6321\uff0c\u5de6\u81c2\u4e00\u5f2f\uff0c\u201c\u547c\u201d\u7684\u4e00\u638c\u51fb\u51fa\u3002\u91d1\u9010\u6d41\u53eb\u9053\uff1a\u201c\u54ce\u5440\uff0c\u4e0d\u5f97\u4e86\uff0c\u597d\u5389 \u5bb3\u7684\u638c\u529b\uff01\u201d\u8eab\u8eaf\u4e00\u77ee\uff0c\u4f5c\u51fa\u4f3c\u4e4e\u62b5\u53d7\u4e0d\u8d77\u4ed6\u7684\u638c\u529b\u7684\u6a21\u7965\uff0c\u53f2\u767d\u90fd\u201c\u54fc\u201d\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u9053 \u5389\u5bb3\u4e86\u4e48\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u91d1\u9010\u6d41\u6a2a\u638c\u4e00\u62b9\uff0c\u5de6\u624b\u9a88\u6307\u5982\u621f\uff0c\u5374\u5df2\u70b9\u5230\u4e86\u4ed6\u7684\u80c1\u4e0b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7528\u7684\u662f\u4ed6\u5bb6\u4f20\u7684\u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\uff0c\u53f2\u767d\u90fd\u7ed9\u4ed6\u7684\u90a3\u4e00\u62b9\u5f15\u5f00\u4e86\u89c6\u7ebf\uff0c\u6599\u4e0d\u5230\u4ed6\u7ade\u6562 \u6b3a\u8eab\u76f4\u8fdb\uff0c\u51b7\u4e0d\u53ca\u9632\u80c1\u4e0b\u5df2\u662f\u7740\u4e86\u4ed6\u7684\u4e00\u6307\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7ed9\u4ed6\u9707\u9000\u4e24\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u5012\u4e5f\uff0c\u5012\u4e5f\uff01\u201d\u53f2\u767d\u90fd\u80c1\u4e0b\u4e00\u9ebb\uff0c\u8fc5\u5373\u8fd0\u6c14\u51b2\u5f00\u7a74\u9053\uff0c \u5927\u6012\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u9b3c\u568e\u4f5c\u751a\uff1f\u201d\u975e\u4f46\u6ca1\u6709\u5012\u4e0b\uff0c\u638c\u529b\u53cd\u800c\u66f4\u52a0\u5f3a\u4e86\u3002 \u3000\u3000\u5168\u9010\u6d41\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u53f2\u767d\u90fd\u80fd\u591f\u96c4\u9738\u6c5f\u6e56\uff0c\u679c\u7136\u662f\u6709\u51e0\u5206\u771f\u5b9e\u529f \u592b\uff01\u201d\u8981\u77e5\u91d1\u9010\u6d41\u7684\u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\uff01\u66fe\u7ecf\u5c61\u80dc\u5f3a\u654c\uff0c\u8fde\u6587\u9053\u5e84\u90a3\u6837\u6b66\u5b66\u6df1\u6e5b\u4e4b\u4e0a\uff0c\u4e5f\u4e0d\u61c2\u5f97 \u89e3\u6cd5\uff0c\u4ed6\u7684\u513f\u5b50\u88ab\u91d1\u9010\u6d41\u70b9\u4e86\u7a74\u9053\uff0c\u4ed6\u53ea\u80fd\u4f4e\u58f0\u4e0b\u6c14\u7684\u53bb\u6c42\u91d1\u9010\u6d41\u3002\u60f3\u4e0d\u5230\u53f2\u767d\u90fd\u4e0d\u61c2\u5f97\u89e3 \u6cd5\u4e5f\u80fd\u591f\u81ea\u5df1\u8fd0\u6c14\u51b2\u5f00\u7a74\u9053\uff0c\u53ea\u6b64\u4e00\u7aef\uff0c\u663e\u7136\u4ed6\u7684\u5185\u529f\u9020\u8be3\u5df2\u662f\u5728\u91d1\u9010\u6d41\u4e4b\u4e0a\u3002 \u3000\u3000\u6b8a\u4e0d\u77e5\u91d1\u9010\u6d41\u56fa\u7136\u5403\u60ca\uff0c\u53f2\u767d\u90fd\u5374\u662f\u66f4\u4e3a\u6c14\u607c\u3002\u4ed6\u662f\u6c5f\u6e56\u4e0a\u6570\u4e00\u6570\u4e8c\u7684\u4eba\u7269\uff0c\u5982\u4eca\u4e3a\u4e00 \u4e2a\u540e\u751f\u5c0f\u5b50\u6240\u7b97\u8ba1\uff0c\u867d\u7136\u5e76\u65e0\u4f24\u635f\uff0c\u4f46\u7ed9\u70b9\u7740\u4e86\u7a74\u9053\uff0c\u4e5f\u603b\u662f\u8f93\u4e86\u4e00\u62db\u4e86\u3002\u5927\u6012\u4e4b\u4e0b\uff0c\u53cc\u638c \u5f00\u53d1\uff0c\u6068\u4e0d\u5f97\u4e00\u4e0b\u628a\u91d1\u9010\u6d41\u51fb\u6bd9\u3002 \u3000\u3000\u4f59\u9010\u6d41\u4f7f\u51fa\u201c\u5929\u7f57\u6b65\u6cd5\u201d\uff0c\u7565\u907f\u5176\u950b\uff0c\u4f46\u4ea6\u5e76\u4e0d\u793a\u5f31\uff0c\u4e00\u95ea\u5373\u4e0a\uff0c\u8fc5\u5373\u8fd8\u62db\uff0c\u53cc\u638c\u4e00 \u5206\uff0c\u4fbf\u628a\u53f2\u767d\u90fd\u7684\u638c\u529b\u5316\u89e3\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u91c7\u53d6\u7684\u662f\u201c\u907f\u5176\u671d\u9510\uff0c\u51fb\u5176\u66ae\u5f52\u201d\u7684\u6218\u672f\uff0c\u867d\u7136\u6709\u70b9\u53d6\u5de7\uff0c\u5374\u662f\u89e3\u5f97\u5341\u5206\u5947\u5999\u3002 \u53f2\u767d\u90fd\u4e5f\u4e0d\u5f97\u4e0d\u6697\u6697\u4f69\u670d\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6211\u5e2e\u4e2d\u7684\u56db\u5927\u9ad8\u624b\u548c\u6587\u9053\u5e84\u3001\u6c99\u5343\u5cf0\u7b49\u4eba\u90fd \u66fe\u5148\u540e\uff0c\u8d25\u5728\u4ed6\u7684\u624b\u4e0b\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u8fde\u51fb\u4e09\u638c\uff0c\u524d\u9762\u7684\u4e00\u9053\u638c\u529b\u672a\u66fe\u6d88\u901d\uff0c\u540e\u9762\u7684\u4e00\u9053\u638c\u529b\u53c8\u52a0\u4e0a\u6765\u3002\u8fd9\u8fde\u73af\u4e09\u638c\u6709 \u4e2a\u540d\u5802\uff0c\u53eb\u505a\u201c\u9f99\u95e8\u9f13\u6d6a\u201d\uff0c\u638c\u529b\u5c3d\u53d1\uff0c\u5f53\u771f\u662f\u6709\u5982\u6392\u5c71\u5012\u6d77\u800c\u6765\uff0c\u8840\u8089\u4e4b\u8eaf\uff0c\u5b9e\u96be\u62b5\u5fa1\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7ed9\u4ed6\u903c\u5f97\u53c8\u9000\u4e86\u513f\u6b65\uff0c\u53f2\u767d\u90fd\u559d\u9053\uff1a\u201c\u4f60\u628a\u6211\u7684\u59b9\u5b50\u62d0\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u5feb\u8bf4\uff01\u5426\u5219 \u6211\u5c31\u8981\u4f60\u6027\u547d\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u8d4c\u9152\u672a\u66fe\u5206\u80dc\u8d1f\uff0c\u957f\u8857\u518d\u6bd4\u6b66\u529f\u6765\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u5341\u516d\u56de\u3000\u7384\u94c1\u901e\u5a01\u6597\u5e2e\u4e3b\u3000\u9053\u65c1\u4ed7\u4e49\u4f5c\u5a92\u4eba \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u5341\u516d\u56de\u3000\u7384\u94c1\u901e\u5a01\u6597\u5e2e\u4e3b\u3000\u9053\u65c1\u4ed7\u4e49\u4f5c\u5a92\u4eba \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u60f3\u77e5\u9053\u7ea2\u82f1\u7684\u4e0b\u843d\u5462\uff0c\u4f60\u5012\u6765\u95ee\u6211\u3002\u563f\uff0c\u563f\uff0c\u4f60\u8fd9\u4eba\u6789\u4e3a\u4e00\u5e2e\u4e4b \u4e3b\uff0c\u5374\u662f\u4e13\u7231\u5439\u725b\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u77aa\u773c\u559d\u9053\uff1a\u201c\u6211\u600e\u4e48\u5439\u725b\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u51ed\u4f60\u8fd9\u70b9\u672c\u9886\uff0c\u4f60\u53c8\u600e\u80fd\u53d6\u5f97\u6211\u7684\u6027 \u547d\uff1f\u4f60\u8fd9\u4e0d\u662f\u5927\u8a00\u4e0d\u60ed\u4e48\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u66b4\u6012\u5982\u96f7\uff0c\u4e0d\u518d\u7b54\u8bdd\uff0c\u731b\u653b\u731b\u6253\u3002\u91d1\u9010\u6d41\u7684\u5185\u529f\u4e0d\u53ca\u4ed6\uff0c\u4f46\u91d1\u9010\u6d41\u6240\u4f1a\u7684\u5404\u79cd\u5947 \u5999\u6b66\u529f\uff0c\u5374\u662f\u975e\u4ed6\u6240\u53ca\u3002\u201d\u91d1\u9010\u6d41\u89c1\u62db\u62c6\u62db\uff0c\u89c1\u5f0f\u62c6\u5f0f\uff0c\u80fd\u89e3\u5219\u89e3\uff0c\u4e0d\u80fd\u89e3\u5219\u95ea\u907f\u5f00\u53bb\u3002\u8f6c \u773c\u6597\u4e86\u4e09\u5341\u4e94\u62db\uff0c\u53f2\u767d\u90fd\u7adf\u662f\u5948\u4ed6\u4e0d\u4f55\u3002 \u3000\u3000\u53ef\u662f\u53f2\u767d\u90fd\u867d\u7136\u53d6\u4e0d\u4e86\u91d1\u9010\u6d41\u7684\u6027\u547d\uff0c\u91d1\u9010\u6d41\u4e5f\u5e94\u4ed8\u5f97\u5403\u529b\u975e\u5e38\uff0c\u8868\u9762\u770b\u6765\uff0c\u4ed6\u662f\u4ece\u5bb9 \u6f47\u6d12\uff0c\u5b9e\u5219\u5df2\u662f\u7528\u5c3d\u5e73\u751f\u6240\u5b66\uff0c\u624d\u582a\u582a\u548c\u53f2\u767d\u90fd\u6253\u6210\u5e73\u624b\u7684\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u53ea\u542c\u5f97\u201c\u4e52\u4e52\u4e53\u4e53\u201d\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u4e24\u65c1\u5e97\u94fa\u7684\u62db\u724c\u7ed9\u4ed6\u4eec\u7684\u62f3\u98ce\u638c\u529b\u9707\u5f97\u8dcc\u843d \u8857\u4e0a\uff0c\u884c\u4eba\u8eb2\u907f\u4e00\u7a7a\uff0c\u8001\u677f\u5927\u53f9\u5012\u6963\uff0c\u8fd8\u751f\u6015\u7ed9\u4ed6\u4eec\u6ce2\u53ca\uff0c\u53ea\u597d\u7eb7\u7eb7\u628a\u94fa\u95e8\u5173\u4e0a\u3002 \u3000\u3000\u91d1\u9010\u6d41\u89e3\u4e86\u4e00\u62db\uff0c\u8bf4\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\uff0c\u6211\u6709\u4e00\u8a00\u76f8\u529d\uff0c\u542c\u4e0d\u542c\u968f\u4f60\u3002\u201d\u53f2\u767d\u90fd\u201c\u54fc\u201d\u4e00\u58f0 \u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u5012\u8981\u6559\u8bad\u6211\u4e48\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u6562\u3002\u4f46\u4f60\u53ef\u77e5\u9053\u4f60\u7684\u59b9\u5b50\u4e3a\u4f55\u8981\u8dd1\uff1f\u4f60\u8981\u95ee\u4eba\u5148\u8be5\u95ee\u4f60\u81ea\u5df1\uff01\u201d\u53f2\u767d\u90fd \u4e00\u62f3\u6363\u51fa\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u4f7f\u4e2a\u201c\u5929\u7f57\u6b65\u6cd5\u201d\u95ea\u5f00\u4ed6\u7684\u4e00\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4f60\u7684\u59b9\u5b50\u662f\u4e3a\u4e86\u6211\u9003\u8dd1\u7684\u4e48\uff1f \u9519\u4e86\uff0c\u5927\u5927\u7684\u9519\u4e86\uff01\u8fd9\u5b8c\u5168\u662f\u56e0\u4e3a\u4f60\u81ea\u5df1\u4e0d\u597d\uff0c\u6709\u8fb1\u5bb6\u95e8\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u559d\u9053\uff1a\u201c\u4f60\u518d\u80e1\u8bf4\uff0c\u6211\u2026\u2026\u201d\u4ed6\u672c\u6765\u60f3\u8bf4\u201c\u6211\u6bd9\u4e86\u4f60\u201d\u7684\uff0c\u8fd9\u662f\u4ed6\u7684\u53e3\u5934\u7985\u3002\u4f46 \u521a\u521a\u53d7\u4e86\u91d1\u9010\u6d41\u7684\u595a\u843d\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u60f3\u8d77\u81ea\u5df1\u4e5f\u5b9e\u5728\u6ca1\u6709\u628a\u63e1\u53d6\u4ed6\u7684\u6027\u547d\uff0c\u82e5\u7136\u518d\u8bf4\u4e00\u904d\uff0c \u53ea\u6709\u5f92\u62db\u5bf9\u65b9\u8baa\u7b11\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u786c\u751f\u751f\u5730\u541e\u4e86\u56de\u53bb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u8981\u600e\u6837\uff0c\u6211\u7ba1\u4e0d\u7740\u3002\u6211\u5374\u662f\u6709\u5982\u9aa8\u9ca4\u5728\u5589\uff0c\u4e0d\u5410\u4e0d\u5feb\u3002\u8001\u5b9e\u5bf9\u4f60\u8bf4 \u5427\uff0c\u6211\u548c\u4ee4\u59b9\u4e0d\u8fc7\u662f\u65b0\u76f8\u8bc6\u7684\u670b\u53cb\uff0c\u6211\u600e\u80fd\u628a\u5979\u62d0\u8dd1\uff1f\u4f60\u4e0d\u8be5\u903c\u5979\u5ac1\u7ed9\u5979\u6240\u4e0d\u559c\u6b22\u7684\u4eba\uff0c\u5979 \u8fd9\u624d\u4e00\u6c14\u800c\u8dd1\u7684\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u6c14\u5f97\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u4ec0\u4e48\u90fd\u5bf9\u4f60\u8bf4\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u548c\u4ee4\u59b9\u867d\u662f\u65b0\u4ea4\uff0c\u5374\u4e0d\u80fd\u4e0d\u4e3a\u5979\u6253\u62b1\u4e0d\u5e73\u3002\u60f3\u90a3\u59d3\u5e05\u7684\u4e0d\u8fc7\u662f\u4e2a\u6b66\u6797\u8d25 \u7c7b\uff0c\u4f60\u600e\u4e48\u53ef\u4ee5\u903c\u4f60\u59b9\u5b50\u5ac1\u4ed6\uff1f\u4ee5\u4f60\u7684\u6b66\u529f\uff0c\u672c\u6765\u53ef\u4ee5\u6210\u4e3a\u4e00\u4e2a\u5927\u6709\u4f5c\u4e3a\u7684\u4eba\u7269\uff0c\u5374\u53c8\u4f55\u987b \u53bb\u8c04\u5a9a\u6743\u8d35\uff0c\u5c48\u819d\u671d\u5ef7\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u5927\u6012\u559d\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u8fd9\u4e2a\u5b50\u5c45\u7136\u6562\u6559\u8bad\u6211\uff01\u201d\u91d1\u9010\u6d41\u4e00\u9762\u5316\u89e3\u4ed6\u731b\u653b\u8fc7\u6765 \u7684\u62db\u6570\uff0c\u4e00\u9762\u4ecd\u5728\u6162\u6761\u65af\u7406\u4ed6\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u6211\u6559\u8bad\u4f60\uff0c\u6211\u662f\u4e3a\u4f60\u7684\u597d\u3002\u53f2\u5e2e\u4e3b\uff0c\u6211\u8bda\u5fc3\u529d \u4f60\uff0c\u5018\u82e5\u4f60\u80fd\u591f\u9769\u9762\u6d17\u5fc3\uff0c\u4e0d\u4f46\u4f60\u4eec\u5144\u59b9\u53ef\u4ee5\u548c\u597d\u5982\u521d\uff0c\u6c5f\u6e56\u4e0a\u7684\u4fa0\u4e49\u9053\u4e5f\u5fc5\u5b9a\u53ef\u4ee5\u539f\u8c05\u4f60 \u7684\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u54c7\u54c7\u5927\u53eb\uff1a\u201c\u4f60\u80e1\u8bf4\u516b\u9053\uff0c\u6709\u5b8c\u7684\u6ca1\u6709\uff1f\u54fc\uff0c\u54fc\uff0c\u6211\u53f2\u767d\u90fd\u72ec\u6765 \u72ec\u5f80\uff0c\u543e\u884c\u543e\u7d2f\uff0c\u8981\u4ec0\u4e48\u4eba\u539f\u8c05\uff01\u201d\u91d1\u9010\u6d41\u53cc\u624b\u4e00\u644a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u542c\u5584\u8a00\uff0c\u6211\u4e5f\u62ff\u4f60\u6ca1 \u6cd5\u3002\u597d\uff0c\u591a\u8bf4\u65e0\u76ca\uff0c\u5b8c\u4e86\uff01\u201d\u53f2\u767d\u90fd\u66b4\u6012\u5982\u96f7\uff0c\u62f3\u638c\u517c\u65bd\uff0c\u7acb\u5373\u53c8\u662f\u4e00\u8f6e\u731b\u70c8\u7684\u653b\u51fb\u3002 \u3000\u3000\u53f2\u767d\u90fd\u52a8\u4e86\u771f\u6012\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u7eb5\u7136\u6740\u4e0d\u4e86\u8fd9\u5c0f\u5b50\uff0c\u81f3\u5c11\u4e5f\u80fd\u591f\u505a\u5f97\u5230\u4e24\u8d25\u4ff1\u4f24\uff1b\u8fd9\u5c0f\u5b50 \u7684\u529f\u529b\u4e0d\u53ca\u6211\uff0c\u5f7c\u6b64\u53d7\u4f24\uff0c\u4ed6\u5f53\u7136\u8981\u6bd4\u6211\u4f24\u5f97\u91cd\uff01\u201d\u53f2\u767d\u90fd\u52a8\u4e86\u8fd9\u4e2a\u5ff5\u5934\uff0c\u62db\u62db\u90fd\u662f\u6740\u624b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u51fa\u9053\u4ee5\u6765\uff0c\u4ece\u672a\u78b0\u8fc7\u5982\u6b64\u5389\u5bb3\u7684\u5bf9\u624b\u3002\u8fd9\u4e00\u6218\u6bd4\u4ed6\u5728\u5e08\u5144\u5bb6\u4e2d\u6076\u6597\u6587\u9053\u5e84\u7684\u90a3\u4e00 \u4ed7\u66f4\u4e3a\u51f6\u9669\uff0c\u9976\u662f\u91d1\u9010\u6d41\u51fa\u5c3d\u4e86\u5e73\u751f\u6240\u5b66\uff0c\u6597\u4e86\u4e94\u516d\u5341\u62db\uff0c\u5140\u662f\u4e0d\u80fd\u6273\u6210\u5e73\u624b\uff0c\u7d2f\u5f97\u5927\u6c57\u6dcb \u6f13\u3002\u8fd9\u662f\u7ed9\u5f3a\u654c\u903c\u51fa\u6765\u7684\u5927\u6c57\uff0c\u548c\u521a\u624d\u8d4c\u9152\u65f6\u7528\u5185\u529f\u628a\u9152\u84b8\u53d1\u51fa\u6765\u7684\u6c57\u6c34\u81ea\u662f\u4e0d\u540c\uff0c\u91d1\u9010\u6d41 \u5fc3\u60f3\uff1a\u201c\u4e45\u6218\u4e0b\u53bb\uff0c\u7eb5\u7136\u4e0d\u81f4\u843d\u8d25\uff0c\u53ea\u6015\u4e5f\u8981\u5143\u6c14\u5927\u4f24\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u63d0\u7740\u90a3\u4e2a\u85cf\u7740\u7384\u94c1\u7684\u5323\u5b50\uff0c\u7ad9\u5728\u4e00\u65c1\u89c2\u6218\uff0c\u770b\u89c1\u91d1\u9010\u6d41\u9010\u6e10\u5904\u4e8e\u52a3\u52bf\uff0c\u5fc3\u5ff5\u4e00 \u52a8\uff0c\u4fbf\u5373\u5411\u524d\u8d70\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\u522b\u614c\uff0c\u6211\u6765\u5e2e\u4f60\uff01\u597d\u5c0f\u5b50\uff0c\u4f60\u6562\u5f97\u7f6a\u53f2\u5e2e\u4e3b\uff0c\u6211\u5148\u628a\u4f60 \u7684\u72d7\u5934\u7838\u788e\uff01\u201d\u628a\u624b\u4e00\u626c\uff0c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u4fbf\u628a\u90a3\u4e2a\u88c5\u7740\u7384\u94c1\u7684\u5323\u5b50\u5411\u91d1\u9010\u6d41\u63b7\u51fa\u3002 \u3000\u3000\u53f2\u767d\u90fd\u559d\u9053\uff1a\u201c\u6211\u53f2\u67d0\u4f55\u987b\u522b\u4eba\u52a9\u62f3\uff0c\u4f60\u7ed9\u6211\u7ad9\u8fdc\u4e00\u4e9b\uff01\u522b\u5728\u8fd9\u91cc\u788d\u4e86\u6211\u7684\u62f3\u811a\uff01\u201d\u8981 \u77e5\u53f2\u767d\u90fd\u662f\u6c5f\u6e56\u4e0a\u6570\u4e00\u6570\u4e8c\u7684\u4eba\u7269\uff0c\u800c\u4e14\u6b64\u9645\u4ed6\u53c8\u6b63\u662f\u5360\u7740\u4e0a\u51e4\uff0c\u7109\u80fd\u4e0d\u987e\u8eab\u4efd\uff0c\u8981\u4eba\u5e2e \u5fd9\uff1f\u201d\u4ed6\u8fd8\u662f\u56e0\u4e3a\u770b\u5728\u5bab\u79c9\u85e9\u662f\u7ea2\u6a31\u4f1a\u9999\u4e3b\u7684\u4efd\u4e0a\uff0c\u4e0d\u60f3\u4ee4\u5bab\u79c9\u85e9\u592a\u8fc7\u96be\u582a\uff0c\u8981\u4e0d\u7136\u8fd9\u4e2a\u813e \u6c14\u4f1a\u53d1\u5f97\u66f4\u5927\u3002 \u3000\u3000\u53f2\u767d\u90fd\u54ea\u91cc\u77e5\u9053\uff0c\u5bab\u79c9\u85e9\u53e3\u91cc\u8bf4\u662f\u52a9\u4ed6\uff0c\u5176\u5b9e\u5374\u662f\u5728\u6697\u52a9\u91d1\u9010\u6d41\u3002\u4ed6\u628a\u90a3\u5757\u7384\u94c1\u63b7\u51fa\uff0c \u63b7\u5f97\u6070\u5230\u597d\u5904\uff0c\u91d1\u9010\u6d41\u4e00\u63a5\u5c31\u63a5\u5230\u4e86\u624b\u4e2d\u3002\u53f2\u767d\u90fd\u8981\u62a2\u5df2\u6765\u4e0d\u53ca\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u6545\u4f5c\u60ca\u60f6\uff0c\u53eb\u9053\uff1a\u201c\u54ce\u5440\uff0c\u4e0d\u597d\uff01\u4f24\u4e0d\u4e86\u8fd9\u5c0f\u5b50\uff0c\u7384\u94c1\u53cd\u7ed9\u4ed6\u62a2\u53bb\u4e86\uff01\u201d\u4f46\u63a5\u7740 \u4fbf\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\u81ea\u6709\u672c\u9886\u593a\u56de\uff0c\u7528\u4e0d\u7740\u62c5\u5fc3\uff0c\u7528\u4e0d\u7740\u62c5\u5fc3\uff01\u53f2\u5e2e\u4e3b\uff0c\u8bf7\u6055\u5c0f\u5f1f\u5931\u8a00 \u4e86\uff01\u201d\u8bf4\u7f62\uff0c\u88c5\u4f5c\u542c\u4ece\u53f2\u767d\u90fd\u7684\u5429\u5490\uff0c\u8fdc\u8fdc\u8eb2\u5f00\u3002 \u3000\u3000\u53f2\u767d\u90fd\u770b\u89c1\u7384\u94c1\u843d\u5728\u91d1\u9010\u6d41\u7684\u624b\u4e0a\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u6251\u4e0a\u6765\u62a2\u593a\uff0c\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u9a84\u6307 \u70b9\u5411\u91d1\u9010\u6d41\u7684\u9762\u95e8\uff0c\u5de6\u81c2\u4e00\u5708\uff0c\u53cd\u6263\u4ed6\u7684\u624b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u522b\u751f\u6c14\uff0c\u7384\u94c1\u7ed9\u4f60\uff01\u201d\u4fa7\u8eab\u4e00\u95ea\uff0c\u8fc5\u5373\u628a\u90a3\u5323\u5b50\u5f80\u524d\u4e00\u63a8\u3002\u53f2\u767d\u90fd\u53cc \u6307\u70b9\u7a7a\uff0c\u8ddf\u7740\u7684\u4e00\u638c\u5df2\u7ecf\u62cd\u51fa\u3002\u91d1\u9010\u6d41\u529f\u529b\u6bd4\u4e0d\u4e0a\u53f2\u767d\u90fd\uff0c\u4f46\u76f8\u5dee\u4e5f\u5e76\u4e0d\u5927\uff0c\u5018\u82e5\u53cc\u638c\u76f8 \u4ea4\uff0c\u4ed6\u662f\u975e\u8d25\u4e0d\u53ef\uff0c\u4f46\u6b64\u9645\u4ed6\u624b\u91cc\u62ff\u7740\u7384\u94c1\uff0c\u53f2\u767d\u90fd\u82e5\u7136\u7ed9\u4ed6\u6253\u7740\uff0c\u9aa8\u5934\u6050\u6015\u4e5f\u4f1a\u65ad\u6298\u3002\u53f2 \u767d\u90fd\u53ea\u597d\u4ed3\u60f6\u53d8\u62db\uff0c\u786c\u725b\u751f\u7684\u628a\u653b\u51fa\u53bb\u7684\u90a3\u4e00\u638c\u6536\u56de\uff0c\u6539\u4e86\u4e2a\u201c\u8131\u888d\u89e3\u7532\u201d\u7684\u62db\u6570\uff0c\u659c\u9000\u4e09 \u6b65\u3002 \u3000\u3000\u90a3\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\u672c\u6765\u53ef\u7834\u8098\u5bb3\u7684\u4e00\u62db\u6740\u7740\uff0c\u4e0d\u6599\u7ed9\u91d1\u9010\u6d41\u786c\u6253\u786c\u7838\u7684\u4e00\u4e3e\u624b\u4e4b\u95f4\u4fbf \u5373\u7834\u89e3\uff0c\u53f2\u767d\u90fd\u63a5\u7740\u7528\u7684\u201c\u8131\u888d\u89e3\u7532\u201d\u5e94\u4ed8\u5f97\u4e5f\u5f88\u6070\u5f53\uff0c\u4e0d\u8fc7\uff0c\u5374\u603b\u662f\u7ed9\u91d1\u9010\u6d41\u903c\u9000\u4e86\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u7ad9\u5f97\u8fdc\u8fdc\u7684\u89c2\u6218\uff0c\u62cd\u638c\u53eb\u9053\uff1a\u201c\u5999\u5440\uff0c\u5999\u5440\uff01\u201d\u4e5f\u4e0d\u77e5\u4ed6\u8d5e\u7684\u662f\u53f2\u767d\u90fd\u8fd8\u662f\u91d1\u9010 \u6d41\uff1f \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u8d5e\u8c01\u5462\uff1f\u4ed6\u7684\u62db\u6570\u56fa\u7136\u5f88\u5999\uff0c\u6211\u7684\u4e5f\u4e0d\u592a\u7b28\uff0c\u597d\uff0c\u54b1\u4eec\u7b97\u662f\u68cb\u9022\u5bf9 \u624b\uff0c\u518d\u6765\uff0c\u518d\u6765\uff01\u201d\u63d0\u8d77\u7384\u94c1\uff0c\u5212\u4e86\u4e00\u4e2a\u5708\u5708\uff0c\u671d\u7740\u53f2\u767d\u90fd\u53c8\u662f\u4e00\u62db\u201c\u6cf0\u5c71\u538b\u9876\u201d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7384\u94c1\u5728\u624b\uff0c\u5982\u864e\u6dfb\u7ffc\uff0c\u767b\u65f6\u8f6c\u5b88\u4e3a\u653b\uff0c\u53f2\u767d\u90e8\u7a7a\u81ea\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u5374\u662f\u65e0\u5948\u4ed6 \u4f55\u3002\u8981\u77e5\u6b66\u5b66\u4e4b\u4e2d\uff0c\u867d\u6709\u201c\u4ee5\u5de7\u964d\u529b\u201d\u7684\u6253\u6cd5\uff0c\u4f46\u8bb2\u5230\u4e00\u4e2a\u201c\u5de7\u201d\u5b57\uff0c\u53f2\u767d\u90fd\u5374\u662f\u8fdc\u8fdc\u4e0d\u53ca \u91d1\u9010\u6d41\u3002\u91d1\u9010\u6d41\u8f7b\u529f\u8d85\u5353\uff0c\u53c8\u4f1a\u201c\u5929\u7f57\u6b65\u6cd5\u201d\uff0c\u867d\u7136\u63d0\u7740\u7384\u94c1\uff0c\u8eab\u6cd5\u4e5f\u8fd8\u662f\u6bd4\u53f2\u767d\u90fd\u7075\u5de7\u5f97 \u591a\u3002 \u3000\u3000\u53f2\u767d\u90fd\u80cc\u8d1f\u957f\u5251\uff0c\u4f46\u5728\u91d1\u9010\u6d41\u6025\u653b\u4e4b\u4e0b\uff0c\u8fde\u62d4\u5251\u7684\u7a7a\u6687\u4e5f\u817e\u4e0d\u51fa\u6765\u3002\u4ed6\u7684\u6c14\u529b\u7ed9\u91d1\u9010\u6d41 \u7684\u7384\u94c1\u62b5\u6d88\uff0c\u53ea\u6709\u6328\u6253\u7684\u4efd\u513f\uff0c\u5fc3\u4e2d\u6697\u6697\u540e\u6094\u521d\u6765\u65f6\u592a\u8fc7\u8f7b\u654c\u3002\u4e0d\u8fc7\uff0c\u8bdd\u8bf4\u56de\u6765\uff0c\u91d1\u9010\u6d41\u4e0e \u4ed6\u521d\u4ea4\u624b\u65f6\u662f\u7a7a\u7740\u4e00\u53cc\u624b\u7684\uff0c\u4ee5\u4ed6\u7684\u8eab\u4f24\u53c8\u5c82\u80fd\u4f7f\u7528\u5175\u5668\uff1f \u3000\u3000\u8f6c\u773c\u53c8\u8fc7\u4e86\u4e09\u5341\u591a\u62db\uff0c\u53f2\u767d\u90fd\u5927\u6c41\u6dcb\u6f13\uff0c\u6e10\u6e10\u6709\u70b9\u62db\u67b6\u4e0d\u4f4f\u3002\u5bab\u79c9\u85e9\u8eb2\u5728\u8857\u89d2\u8fd8\u662f\u4e0d\u65ad \u5730\u5927\u53eb\u201c\u5999\u5440\uff0c\u5999\u5440\uff01\u201d\u53f2\u767d\u90fd\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u8d8a\u53d1\u96be\u4ee5\u5e94\u4ed8\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6325\u821e\u90a3\u85cf\u7740\u7384\u94c1\u7684\u5323\u5b50\uff0c\u5708\u5b50\u6269\u5927\uff0c\u628a\u53f2\u767d\u90fd\u6324\u51fa\u79bb\u8eab\u4e09\u4e08\u5f00\u5916\uff0c\u84e6\u5730\u4e00\u4e2a\u8f6c\u8eab \u4fbf\u8dd1\u3002 \u3000\u3000\u53f2\u767d\u90fd\u6b63\u81ea\u6015\u4ed6\u8ffd\u51fb\uff0c\u4e0d\u6599\u4ed6\u53cd\u800c\u8f6c\u8eab\u9003\u8dd1\uff0c\u53f2\u767d\u90fd\u83ab\u540d\u5176\u5999\uff0c\u5fc3\u91cc\u6b63\u60f3\uff1a\u201c\u8fd9\u5c0f\u5b50\u6363 \u4ec0\u4e48\u9b3c\uff1f\u201d\u53ea\u89c1\u91d1\u9010\u6d41\u98de\u8eab\u4e00\u8dc3\u3002\u5df2\u662f\u8df3\u4e0a\u4e86\u4ed6\u7684\u5750\u9a91\u3002 \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u591a\u8c22\u4f60\u7684\u7384\u94c1\uff0c\u591a\u8c22\u4f60\u7684\u5750\u9a91\u3002\u5b9d\u5251\u540d\u9a6c\u4e24\u7686\u5f97\u4e4b\uff0c\u770b\u5728\u8fd9\u4efd\u539a\u793c \u7684\u4efd\u4e0a\uff0c\u4e0d\u6253\u4f60\u4e86\u3002\u54c8\u54c8\uff0c\u6211\u53bb\u4e5f\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u8fd9\u5339\u5750\u9a91\u4e43\u662f\u4e00\u5339\u5343\u4e2d\u9009\u4e00\u7684\u5927\u5b9b\u540d\u9a79\uff0c\u7ed9\u91d1\u9010\u6d41\u62a2\u4e86\u53bb\uff0c\u5f53\u771f\u662f\u6c14\u6012\u4ea4\u52a0\uff0c\u4ed6 \u8fde\u53d1\u4e09\u679a\u94b1\u9556\uff0c\u8ffd\u4e0b\u53bb\u6253\u91d1\u9010\u6d41\uff0c\u91d1\u9010\u6d41\u7528\u7384\u94c1\u5323\u5b50\u6253\u843d\u4e00\u679a\uff0c\u63a5\u7740\u6765\u7684\u51e0\u679a\u94b1\u9556\u5df2\u662f\u843d\u5728 \u9a6c\u540e\uff0c\u5176\u5b9e\u53f2\u767d\u90fd\u4e5f\u662f\u77e5\u9053\u6253\u4e0d\u7740\u91d1\u9010\u6d41\u7684\uff0c\u4e0d\u8fc7\u804a\u4ee5\u6cc4\u6124\u800c\u5df2\u3002 \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u5927\u7b11\uff1a\u201c\u4f60\u4e0d\u8981\u8fd9\u6837\u5c0f\u6c14\uff0c\u7384\u94c1\u6211\u7b11\u7eb3\u4e86\uff0c\u8fd9\u5339\u5750\u9a91\u4f60\u820d\u4e0d\u5f97\uff0c\u6211\u5c31\u5f53\u662f\u501f \u7528\u4f60\u7684\u5427\u3002\u5230\u4e86\u5317\u4eac\uff0c\u6211\u8fd8\u7ed9\u4f60\uff01\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u4f46\u5374\u662f\u5feb\u9a6c\u52a0\u97ad\uff0c\u8bf4\u5230\u4e00\u4e2a\u201c\u8fd8\u201d\u5b57\uff0c\u65e9\u5df2 \u662f\u9a70\u51fa\u6570\u91cc\u4e4b\u5916\uff0c\u6240\u8bf4\u7684\u8bdd\uff0c\u4e5f\u4e0d\u77e5\u53f2\u767d\u90fd\u542c\u4e0d\u542c\u89c1\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6253\u8d25\u4e86\u53f2\u767d\u90fd\uff0c\u53c8\u5f97\u4e86\u5b9d\u9a6c\uff0c\u5fc3\u4e2d\u5f97\u610f\u4e4b\u6781\uff0c\u60f3\u9053\uff1a\u201c\u6211\u82e5\u662f\u89c1\u7740\u7ea2\u82f1\uff0c\u8bf4\u7ed9\u5979 \u542c\uff0c\u60f3\u5fc5\u5979\u4e5f\u8981\u7b11\u75db\u809a\u5b50\u3002\u5979\u53d7\u591f\u4e86\u54e5\u54e5\u7684\u6b3a\u4fae\uff0c\u8fd9\u5339\u5750\u9a91\u6211\u53ef\u4ee5\u8fd8\u7ed9\u5979\uff0c\u8ba9\u5979\u5728\u54e5\u54e5\u9762\u524d \u51fa\u4e00\u53e3\u6c14\u3002\u201d\u968f\u7740\u53c8\u60f3\uff1a\u201c\u4f46\u6211\u5f97\u7f6a\u4e86\u5979\u7684\u54e5\u54e5\uff0c\u53ea\u6015\u53f2\u767d\u90fd\u8fd9\u53ae\u66f4\u8981\u5343\u65b9\u767e\u8ba1\u7684\u963b\u6320\u5979\u548c \u6211\u597d\u4e86\u3002\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u53c8\u4e0d\u89c9\u54d1\u7136\u5931\u7b11\uff1a\u201c\u4eba\u5bb6\u548c\u4e0d\u548c\u4f60\u8981\u597d\u8fd8\u4e0d\u77e5\u9053\u5462\uff0c\u54ea\u91cc\u987e\u8651\u5f97\u8fd9\u8bb8 \u591a\uff1f\u201d \u3000\u3000\u9a8f\u9a6c\u5954\u817e\uff0c\u98ce\u9a70\u7535\u63a3\uff0c\u91d1\u9010\u6d41\u6b8b\u9189\u672a\u6d88\uff0c\u4e50\u5f97\u5728\u9a6c\u80cc\u4e0a\u624b\u821e\u8db3\u8e48\uff0c\u54fc\u7740\u53eb\u5316\u5b50\u559c\u6b22\u5531\u7684 \u201c\u83b2\u82b1\u843d\u201d\u5c0f\u8c03\uff0c\u8def\u4e0a\u884c\u4eba\uff0c\u53ea\u5f53\u4ed6\u662f\u75af\u5b50\uff0c\u7eb7\u7eb7\u8eb2\u907f\u3002\u5176\u5b9e\u91d1\u9010\u6d41\u9a91\u672f\u751a\u7cbe\uff0c\u4ed6\u662f\u7edd\u4e0d\u4f1a \u649e\u7740\u4e86\u8def\u4eba\u7684\u3002 \u3000\u3000\u4e00\u53e3\u6c14\u4e5f\u4e0d\u77e5\u8dd1\u4e86\u591a\u5c11\u8def\uff0c\u5ffd\u89c1\u4e00\u9a91\u9a6c\u8fce\u9762\u800c\u6765\uff0c\u7a81\u7136\u79bb\u5f00\u4e86\u5927\u8def\uff0c\u8dd1\u4e0a\u4e86\u5c71\u5761\u3002 \u3000\u3000\u4e00\u8def\u4e0a\u90fd\u6709\u4eba\u9a6c\u8eb2\u907f\u4ed6\uff0c\u91d1\u9010\u6d41\u672c\u6765\u662f\u4e0d\u5f53\u4f5c\u4e00\u56de\u4e8b\u7684\u3002\u53ef\u662f\u8fd9\u4e2a\u4eba\u5374\u6709\u70b9\u7279\u522b\uff0c\u4ed6\u89c1 \u7740\u91d1\u9010\u6d41\u4f3c\u4e4e\u662f\u9aa4\u7136\u53d7\u60ca\u7684\u6a21\u6837\uff0c\u628a\u5e3d\u6a90\u62c9\u4e0b\u6765\uff0c\u906e\u8fc7\u4e86\u4ed6\u7684\u534a\u8fb9\u9762\u5b54\uff0c\u91d1\u9010\u6d41\u672a\u770b\u6e05\u695a\u4ed6 \u7ea6\u5e90\u5c71\u771f\u76f8\uff0c\u4ed6\u5df2\u7ecf\u8dd1\u4e0a\u5c71\u5761\u4e86\u3002\u5176\u6b21\uff0c\u522b\u4eba\u8eb2\u907f\u4ed6\uff0c\u6700\u591a\u4e5f\u53ea\u662f\u79bb\u5f00\u5927\u8def\u5c11\u8bb8\uff0c\u7edd\u4e0d\u4f1a\u79bb \u5f00\u8fd9\u4e48\u8fdc\uff0c\u8dd1\u4e0a\u5c71\u5761\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u5ff5\u4e00\u52a8\uff1a\u201c\u8fd9\u4eba\u4f3c\u4e4e\u5728\u54ea\u91cc\u89c1\u8fc7\uff1f\u201d\u4e8e\u662f\u4e00\u62d4\u9a6c\u5934\uff0c\u4e5f\u8ffd\u4e0a\u4e86\u5c71\u5761\uff0c\u53eb\u9053\uff1a \u201c\u670b\u53cb\uff0c\u4f60\u7684\u5750\u9a91\u5728\u5e73\u5730\u4e0a\u662f\u8dd1\u4e0d\u8fc7\u6211\u7684\uff0c\u6211\u5c31\u548c\u4f60\u5728\u5c71\u8def\u4e0a\u8d5b\u4e00\u8d5b\u9a6c\u770b\u3002\u201d \u3000\u3000\u90a3\u4eba\u770b\u89c1\u91d1\u9010\u6d41\u8ffd\u6765\uff0c\u8d8a\u53d1\u60ca\u614c\uff0c\u60c5\u77e5\u9003\u8131\u4e0d\u4e86\uff0c\u53ea\u597d\u8df3\u4e0b\u9a6c\u6765\uff0c\u5411\u91d1\u9010\u6d41\u4f5c\u63d6\u6c42\u9976\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u770b\uff0c\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u3002\u201d \u3000\u3000\u8fd9\u4eba\u539f\u6765\u662f\u66fe\u7ecf\u7528\u201c\u5343\u65e5\u9189\u201d\u6697\u7b97\u8fc7\u79e6\u5143\u6d69\u7684\u90a3\u4e2a\u5c01\u5b50\u8d85\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u90a3\u6b21\u5728\u4ee4\u5e08\u5144\u5bb6\u91cc\u95f9\u4e8b\uff0c\u90fd\u662f\u6587\u9053\u5e84\u8fd9\u53ae\u7684\u4e3b\u610f\uff0c\u53ef\u4e0d\u5173 \u6211\u7684\u4e8b\u3002\u201d\u90a3\u6b21\u91d1\u9010\u6d41\u6253\u8d25\u6587\u9053\u5e84\u662f\u4ed6\u4eb2\u773c\u89c1\u7684\uff0c\u6240\u4ee5\u4ed6\u89c1\u7740\u4e86\u91d1\u9010\u6d41\u5c31\u4f3c\u8017\u5b50\u89c1\u7740\u4e86\u732b\u4e00 \u822c\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u548c\u6587\u9053\u5e84\u662f\u4e00\u4e18\u4e4b\u8c89\uff0c\u600e\u80fd\u8bf4\u662f\u5b8c\u5168\u65e0\u5173\uff1f\u4e0d\u8fc7\u4f60\u80c6\u5b50\u5c0f\uff0c\u4e34\u9635\u5148\u9003 \u7f62\u4e86\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u4ee5\u4e3a\u91d1\u9010\u6d41\u662f\u8981\u6740\u4ed6\uff0c\u5413\u5f97\u9762\u5982\u571f\u8272\u3002\u4e0d\u6599\u91d1\u9010\u6d41\u5ffd\u5730\u53c8\u662f\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a \u201c\u4f60\u8fd8\u503c\u4e0d\u5f97\u6c61\u6211\u7684\u5251\u5462\u3002\u4e0d\u8fc7\u4f60\u53ef\u5f97\u4f9d\u6211\u4e00\u6869\u4e8b\u60c5\u3002\u201d\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u8bf7\u8bf4\u3002\u201d\u91d1\u9010 \u6d41\u9053\uff1a\u201c\u4f60\u7684\u5973\u513f\u5462\uff1f\u201d\u5c01\u5b50\u8d85\u82e6\u7740\u8138\u9053\uff1a\u201c\u5c0f\u5973\u4e0d\u77e5\u53bb\u5411\uff0c\u6211\u4e5f\u6b63\u5728\u5bfb\u5979\u4e0b\u843d\u3002\u201d\u5fc3\u60f3\uff1a \u201c\u96be\u9053\u4ed6\u770b\u4e0a\u4e86\u6211\u7684\u5973\u513f\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u8981\u627e\u5979\u56de\u53bb\u903c\u5979\u5ac1\u7ed9\u6587\u80dc\u4e2d\u8fd9\u5c0f\u5b50\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u4e0d\u3001\u4e0d\u3001\u4e0d\uff01\u59d3\u6587\u8fd9\u5c0f\u5b50\u600e\u4e48\u6bd4\u5f97\u4e0a\u91d1\u5927\u4fa0\uff0c\u6211\u5c82\u4f1a\u770b\u4e2d\u4ed6\uff0c\u59d3\u6587 \u8fd9\u5c0f\u5b50\u4e0d\u8fc7\u766b\u86e4\u87c6\u60f3\u5403\u5929\u9e45\u8089\u800c\u5df2\uff0c\u5176\u5b9e\u6211\u4eec\u7236\u5973\u90fd\u662f\u9119\u89c6\u4ed6\u7684\u3002\u201d\u4ed6\u4e00\u7740\u6025\uff0c\u8bf4\u51fa\u7684\u8bdd\u7b80 \u76f4\u662f\u8bed\u65e0\u4f26\u6b21\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u4e48\u6211\u5c31\u7ed9\u4f60\u505a\u4e2a\u5a92\uff0c\u6211\u8981\u4f60\u628a\u5973\u513f\u5ac1\u7ed9\u79e6\u5143\u6d69\uff0c\u4f60\u4f9d\u5f97 \u8981\u4f9d\uff0c\u4f9d\u4e0d\u5f97\u4e5f\u8981\u4f9d\uff0c\u5426\u5219\u6211\u5c31\u6253\u65ad\u4f60\u7684\u4e24\u6761\u817f\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u8fd9\u624d\u77e5\u9053\u91d1\u9010\u6d41\u662f\u4e3a\u670b\u53cb\u505a\u5a92\uff0c\u5e76\u975e\u81ea\u5df1\u60f3\u8981\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5929\u4e0b\u7adf\u6709\u5982\u6b64\u9738\u9053 \u7684\u5a92\u4eba\uff0c\u6211\u8fd8\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\u3002\u201d\u53e3\u91cc\u5374\u4e0d\u80fd\u4e0d\u8bf4\uff1a\u201c\u662f\uff0c\u662f\uff0c\u662f\uff0c\u91d1\u5927\u4fa0\u505a\u5a92\uff0c\u8fd9\u662f\u7ed9\u6211\u5929 \u5927\u7684\u9762\u5b50\uff0c\u6211\u600e\u80fd\u4e0d\u4f9d\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u522b\u8981\u53e3\u91cc\u8fd9\u4e48\u8bf4\uff0c\u5fc3\u91cc\u53c8\u5728\u6253\u4e3b\u610f\u3002\u4f60\u82e5\u903c\u4f60\u5973\u513f\u5ac1\u7ed9\u7b2c\u4e8c\u4e2a\u4eba\uff0c\u4f60 \u5c31\u5f53\u5fc3\u4f60\u7684\u4e24\u6761\u817f\u5427\u3002\u6211\u8981\u6253\u65ad\u4f60\u7684\u817f\uff0c\u4e0d\u8bba\u4f60\u8eb2\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u4e5f\u662f\u9003\u4e0d\u8fc7\u7684\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u6211\u53ea\u8981\u627e\u7740\u5c0f\u5973\uff0c\u7acb\u5373\u5c06\u5979\u9001\u5230\u6b66\u5f53\u5c71\u53bb\u4e0e\u79e6\u5143\u6d69\u5b8c\u5a5a\u3002\u91d1\u5927\u4fa0\uff0c\u4f60\u653e\u5fc3 \u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u5fc5\u5982\u6b64\u7740\u6025\uff0c\u4f60\u53ea\u8981\u4e0d\u7ba1\u4ed6\u4eec\u7684\u4e8b\uff0c\u8ba9\u4ed6\u4eec\u4ec0\u4e48\u65f6\u5019\u559c\u6b22\u6210\u5a5a\u5c31\u4ec0 \u4e48\u65f6\u5019\u6210\u5a5a\u597d\u4e86\u3002\u201d\u5c01\u5b50\u8d85\u53c8\u8fde\u5fd9\u5e94\u4e86\u4e09\u4e2a\u201c\u662f\u201d\u5b57\uff0c\u8bf4\u9053\uff1a\u201c\u4e00\u5207\u4f9d\u4ece\u91d1\u5927\u4fa0\u7684\u5429\u5490\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u770b\u5728\u4f60\u5973\u513f\u4efd\u4e0a\uff0c\u4eca\u5929\u6211\u9976\u4e86\u4f60\u3002\u4e0d\u8fc7\uff0c\u6211\u8fd8\u5f97\u5411\u4f60\u501f\u4e00\u70b9 \u94b1\uff01 \u3000\u3000\u5c01\u5b50\u8d85\u8fde\u5fd9\u628a\u8eab\u4e0a\u7684\u91d1\u5b50\u90fd\u638f\u51fa\u6765\uff0c\u53cc\u624b\u5949\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u5c3d\u7ba1\u62ff\u53bb\u7528\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u8981\u9001\u7ed9\u6211\uff1f\u4e5f\u597d\uff01\u90a3\u5c31\u5f53\u4f5c\u8c22\u5a92\u5427\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e58\u7740\u9152\u610f\uff0c\u505a\u4e86\u8fd9\u4ef6\u5999\u4e8b\uff0c\u5341\u5206\u5f97\u610f\uff0c\u653e\u58f0\u5927\u7b11\uff0c\u4e0a\u9a6c\u800c\u53bb\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u79e6\u5143 \u6d69\u8fd9\u5c0f\u5b50\u5bb3\u7f9e\uff0c\u82e5\u662f\u8981\u4ed6\u81ea\u5df1\u6c42\u5a5a\uff0c\u4ed6\u4e00\u5b9a\u8bf4\u4e0d\u51fa\u53e3\u3002\u5982\u4eca\u6709\u6211\u7ed9\u4ed6\u4f5c\u5927\u5a92\uff0c\u4ed6\u8fd9\u6869\u5a5a\u4e8b\u662f \u51c6\u6210\u7684\u4e86\u3002\u53ea\u662f\u79e6\u5143\u6d69\u6709\u6211\u4f5c\u5a92\uff0c\u5374\u4e0d\u77e5\u6709\u8c01\u7ed9\u6211\u4f5c\u5a92\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5feb\u9a6c\u5636\u98ce\uff0c\u53c8\u8dd1\u4e86\u4e00\u7a0b\uff0c\u5230\u4e86\u4e00\u4e2a\u5e02\u9547\uff0c\u5929\u8272\u5df2\u9ed1\uff0c\u4fbf\u627e\u4e86\u4e00\u5bb6\u9547\u4e0a\u6700\u5927\u7684\u5ba2\u5e97 \u6295\u5bbf\u3002\u4ed6\u521a\u5728\u95e8\u80fd\u4e0b\u9a6c\uff0c\u90a3\u5ba2\u5e97\u7684\u8001\u677f\u5df2\u7ecf\u7387\u9886\u4f19\u8ba1\u5728\u90a3\u91cc\u606d\u5019\u4ed6\u4e86\u3002\u91d1\u9010\u6d41\u89c1\u4ed6\u4eec\u5982\u6b64\u6392 \u573a\uff0c\u5012\u662f\u6709\u70b9\u8be7\u5f02\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6253\u4e86\u4e00\u573a\u67b6\uff0c\u53c8\u9a91\u9a6c\u8dd1\u4e86\u4e00\u767e\u591a\u91cc\uff0c\u8eab\u4e0a\u6ee1\u8eab\u5c18\u58eb\u3002\u8df3\u4e0b\u9a6c\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6211\u53ea\u662f \u4e00\u4e2a\u7a77\u5c0f\u5b50\uff0c\u4f60\u4eec\u53ef\u7528\u4e0d\u7740\u5982\u6b64\u5ba2\u6c14\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u6115\u4e86\u4e00\u6115\uff0c\u7acb\u5373\u6253\u8eac\u4f5c\u63d6\u8bf4\u9053\uff1a\u201c\u4f60\u8001\u8bf4\u7b11\u4e86\uff0c\u5c0f\u5e97\u5f97\u4f60\u8001\u5149\u4e34\uff0c\u6df1\u611f\u8363\u5ba0\uff0c\u62db \u5f85\u4e0d\u5468\uff0c\u8fd8\u671b\u6055\u7f6a\u3002\u201d\u4f60\u8001\uff0c\u662f\u8fd9\u4e2a\u5730\u65b9\u5bf9\u4eba\u8868\u793a\u5c0a\u656c\u7684\u4e60\u60ef\u7528\u8bed\uff0c\u4f46\u91d1\u9010\u6d41\u5e74\u7ecf\u8f7b\u8f7b\uff0c\u542c \u5f97\u5e97\u4e3b\u53e3\u53e3\u58f0\u58f0\u4ee5\u201c\u8001\u201d\u76f8\u79f0\uff0c\u5374\u662f\u4e0d\u7981\u89c9\u5f97\u6709\u70b9\u6ed1\u7a3d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u5012\u662f\u62db\u5f95\u6709\u672f\uff0c\u53ef\u60dc\u6211\u53ea\u80fd\u4f4f\u4e00\u665a\u3002\u8fd9\u5339\u9a6c\u4f60\u7ed9\u6211\u597d\u597d\u7167\u6599\u3002\u201d\u5e97\u4e3b \u4eba\u8eac\u8170\u8bf4\u9053\uff1a\u201c\u4f60\u8001\u653e\u5fc3\uff0c\u8fd9\u5339\u5b9d\u9a91\u6211\u4eec\u5c82\u6562\u4e0d\u5c0f\u5fc3\u4f3a\u5019\u3002\u201d\u7acb\u5373\u5490\u5490\u4f19\u8ba1\u7ed9\u8fd9\u5339\u9a6c\u6d17\u5237\u5e72 \u51c0\uff0c\u7275\u5165\u9a6c\u53a9\u3002 \u3000\u3000\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u7ed9\u4f60\u8001\u51c6\u5907\u597d\u4e00\u95f4\u4e0a\u623f\uff0c\u8fd9\u662f\u5c0f\u5e97\u6700\u597d\u7684\u4e00\u95f4\u623f\u95f4\u3002\u201d\u91d1\u9010\u6d41\u6447\u4e86 \u6447\u5934\uff1a\u8bf4\u9053\uff1a\u201c\u4e0a\u623f\u4e0d\u4e0a\u623f\u6211\u4e0d\u5728\u4e4e\uff1b\u6211\u8981\u4e00\u95f4\u623f\u5b50\u5bf9\u7740\u9a6c\u53a9\u7684\u3002\u201d\u5e97\u4e3b\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373 \u660e\u767d\u4ed6\u7684\u610f\u601d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8001\u653e\u5fc3\uff0c\u7edd\u6ca1\u6709\u4eba\u6562\u90a3\u4e48\u5927\u80c6\u5077\u4f60\u8001\u7684\u5b9d\u9a91\u3002\u201d \u3000\u3000\u4f46\u90a3\u5e97\u4e3b\u4eba\u8fd8\u662f\u4f9d\u91d1\u9010\u6d41\u7684\u610f\u601d\uff0c\u7ed9\u4ed6\u627e\u4e86\u4e00\u95f4\u5bf9\u7740\u9a6c\u53a9\u7684\u4e0a\u623f\uff0c\u8ddf\u7740\u53c8\u4eb2\u81ea\u7ed9\u4ed6\u9001\u6765 \u4e86\u4e00\u5e2d\u9152\u83dc\u3002\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u600e\u4e48\u5403\u5f97\u4e86\u8fd9\u8bb8\u591a\uff1f\u201d\u5e97\u4e3b\u4eba\u606d\u606d\u656c\u656c\u5730\u7b54\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053 \u4f60\u8001\u53e3\u5473\uff0c\u53ea\u597d\u53eb\u4ed6\u4eec\u591a\u505a\u51e0\u6837\u3002\u8bf7\u4f60\u8001\u968f\u610f\u5c1d\u5c1d\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8fc7\u610f\u4e0d\u53bb\uff0c\u628a\u94f6\u5b50\u638f\u51fa\u6765\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5e78\u4e8f\u6211\u6709\u5c01\u5b50\u8d85\u7684\u2018\u8c22\u5a92\u2019\u94f6\u5b50\uff0c\u5426\u5219 \u5c31\u8981\u5403\u767d\u98df\u4e86\u3002\u201d\u539f\u6765\u91d1\u9010\u6d41\u8eab\u4e0a\u7684\u94f6\u5b50\u90fd\u5df2\u7528\u5149\uff0c\u53ea\u5269\u4e0b\u4e00\u5757\u91d1\u5b50\uff0c\u5374\u662f\u4e0d\u4fbf\u5728\u5c0f\u9547\u7684\u5ba2 \u5e97\u4f7f\u7528\u3002 \u3000\u3000\u90a3\u5e97\u4e3b\u4eba\u4e0d\u5f85\u91d1\u9010\u6d41\u5f00\u53e3\u8bf4\u8bdd\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u5c0f\u5e97\u5f97\u4f60\u8001\u8d4f\u9762\u5df2\u662f\u4e07\u5e78\uff0c\u94f6\u5b50\u662f\u65e0\u8bba\u5982 \u4f55\u4e5f\u4e0d\u6562\u8981\u7684\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u4e2a\u9053\u7406\uff0c\u4f60\u4eec\u505a\u7684\u662f\u8981\u672c\u94b1\u7684\u4e70\u5356\uff0c\u6211\u5c82\u80fd\u767d\u5403\u4f60\u4eec \u7684\u4e1c\u897f\uff1f\u201d\u4f46\u4e0d\u7ba1\u4ed6\u600e\u4e48\u8bf4\uff0c\u5e97\u4e3b\u4eba\u5374\u603b\u662f\u4e0d\u80af\u6388\u53d7\uff0c\u4e00\u8138\u60f6\u6050\u7684\u795e\u6c14\u3002\u91d1\u9010\u6d41\u4e0d\u8010\u70e6\uff0c\u628a \u94f6\u5b50\u6536\u56de\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u5e72\u8d54\u672c\u62db\u5f85\uff0c\u6211\u552f\u6709\u5374\u4e4b\u4e0d\u606d\u53d7\u4e4b\u6709\u6127\u4e86\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u5f85\u6211\u4e34 \u8d70\u7684\u65f6\u5019\uff0c\u6084\u6084\u7559\u5728\u4ed6\u7684\u67dc\u4e0a\u5c31\u662f\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5403\u8fc7\u665a\u996d\uff0c\u8d8a\u60f3\u8d8a\u89c9\u5f97\u5947\u602a\uff0c\u5b9e\u5728\u60f3\u4e0d\u51fa\u5e97\u4e3b\u4eba\u8981\u8fd9\u6837\u5949\u627f\u4ed6\u7684\u7406\u7531\u3002\u5fc3\u6709\u6240 \u7591\uff0c\u8fd9\u665a\u4ed6\u5c31\u5728\u5e8a\u4e0a\u95ed\u76ee\u6253\u5750\uff0c\u4e0d\u6562\u719f\u7761\u3002 \u3000\u3000\u7ea6\u83ab\u5230\u4e86\u4e09\u66f4\u65f6\u5206\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u9a6c\u5636\uff0c\u6709\u4e24\u4e2a\u5ba2\u4eba\u6765\u5230\uff0c\u5e97\u4e3b\u4eba\u53c8\u4eb2\u81ea\u8d77\u8eab\u62db\u5f85\uff0c\u91d1\u9010 \u6d41\u4e00\u542c\u4ed6\u4eec\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u539f\u6765\u8fd9\u4e24\u4e2a\u5ba2\u4eba\u4e00\u7537\u4e00\u5973\uff0c\u6b63\u662f\u516d\u5408\u5e2e\u4e2d\u7684\u9752\u7b26\u9053 \u4eba\u548c\u4e09\u5a18\u3002\u8463\u5341\u4e09\u5a18\u672c\u6765\u662f\u548c\u5706\u6d77\u540c\u884c\u7684\uff0c\u4e0d\u77e5\u600e\u7684\u6362\u4e86\u642d\u6321\uff0c\u6539\u4e3a\u548c\u9752\u7b26\u5728\u4e00\u8d77\u4e86\u3002 \u3000\u3000\u591c\u6df1\u4eba\u9759\uff0c\u52a0\u4ee5\u91d1\u9010\u6d41\u6709\u201c\u542c\u98ce\u8fa8\u5668\u201d\u7684\u529f\u592b\uff0c\u542c\u89c9\u81ea\u662f\u6bd4\u5e38\u4eba\u654f\u9510\uff0c\u5916\u9762\u8bf4\u8bdd\u7684\u58f0\u97f3 \u867d\u7136\u5f88\u5c0f\uff0c\u4ed6\u4e5f\u80fd\u542c\u5f97\u8bf7\u6e05\u695a\u695a\u3002 \u3000\u3000\u53ea\u542c\u5f97\u8463\u5341\u4e09\u5a18\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u91cc\u53ef\u4f4f\u6709\u53ef\u7591\u7684\u6c5f\u6e56\u5973\u5b50\u4e48\uff1f\u201d\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u8fde\u5355\u8eab\u7684\u5973 \u5ba2\u90fd\u6ca1\u6709\u3002\u201d\u9752\u7b26\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u53ef\u6709\u9053\u4e0a\u7684\u670b\u53cb\uff1f\u201d\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u5c0f\u4eba\u6b63\u8981\u7980\u544a\u4e24\u4f4d\u9999\u4e3b\uff0c \u6709\u4e00\u4f4d\u8d35\u5e2e\u7684\u5144\u5f1f\u5728\u5c0f\u5e97\u4f4f\u5bbf\u3002\u201d\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u54e6\uff0c\u90a3\u4eba\u662f\u8c01\uff1f\u201d\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u6211\u4e0d\u8ba4\u8bc6 \u7684\u3002\u201d\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u90a3\u4f60\u600e\u4e48\u77e5\u9053\u201d\uff1f\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u4ed6\u9a91\u7684\u662f\u53f2\u5e2e\u4e3b\u7684\u90a3\u5339\u5750\u9a91\u3002\u201d \u3000\u3000\u8bf4\u81f3\u6b64\u5904\uff0c\u542c\u5f97\u51fa\u8463\u5341\u4e09\u5a18\u662f\u5403\u4e86\u4e00\u60ca\u7684\u6837\u5b50\uff0c\u6025\u58f0\u95ee\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4e8b\uff1f\u4f60\u6ca1\u6709\u770b \u9519\uff1f\u201d\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\u66fe\u5728\u5c0f\u5e97\u4f4f\u8fc7\u51e0\u6b21\uff0c\u4ed6\u8001\u4eba\u5bb6\u90a3\u5339\u2018\u7167\u591c\u72ee\u5b50\u2019\u5c0f\u4eba\u7edd\u4e0d\u4f1a\u770b \u9519\u3002\u201d \u3000\u3000\u9752\u7b26\u9053\uff1a\u201c\u9a91\u8fd9\u5339\u9a6c\u7684\u662f\u600e\u4e48\u6837\u7684\u4e00\u4e2a\u4eba\uff1f\u201d\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u770b\u6765\u597d\u4f3c\u53ea\u6709\u4e8c\u5341\u5c81\u5de6\u53f3\u7684 \u5c0f\u4f19\u5b50\u3002\u201d\u9752\u7b26\u751a\u662f\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5012\u5947\u602a\u4e86\uff01\u96be\u9053\u2026\u2026\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u6211\u4e5f\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff0c\u4ed6\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5374\u5f97\u53f2\u5e2e\u4e3b\u5982\u6b64\u91cd\u7528\u3002\u4e0d\u8fc7\uff0c\u90a3\u5339\u5750\u9a91 \u7684\u786e\u662f\u2018\u7167\u591c\u72ee\u5b50\u201d\uff0c\u6240\u4ee5\u4ed6\u867d\u6ca1\u6709\u8868\u9732\u8eab\u4efd\uff0c\u5c0f\u7684\u4e5f\u4e0d\u6562\u6020\u6162\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u59cb\u604d\u7136\u5927\u609f\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u4e2a\u5e97\u4e3b\u4eba\u548c\u516d\u5408\u5e2e\u662f\u6709\u6765\u5f80\u7684\uff0c\u4ed6\u89c1\u4e86\u6211\u8fd9\u5339\u5750 \u9a91\uff0c\u53ea\u9053\u662f\u53f2\u767d\u90fd\u501f\u4e0e\u6211\u7684\uff0c\u6211\u80fd\u591f\u6709\u8fd9\u4e2a\u9762\u5b50\uff0c\u5f53\u7136\u662f\u516d\u5408\u5e2e\u4e2d\u91cd\u8981\u7684\u4eba\u7269\u4e86\uff0c\u6240\u4ee5\u4ed6\u624d \u5bf9\u6211\u8fd9\u6837\u6bd5\u606d\u6bd5\u656c\u3002 \u3000\u3000\u9752\u7b26\u8bf4\u9053\uff1a\u201c\u96be\u9053\u53c8\u662f\u90a3\u5c0f\u5b50\u5e72\u7684\u597d\u4e8b\uff1f\u201d\u8463\u5341\u4e09\u5a18\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u5e97\u4e3b\u4eba\u60ca\u7591\u4e0d\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u9999\u4e3b\u4e0d\u77e5\u9053\u6709\u8fd9\u4e2a\u4eba\u5417\uff1f\u8981\u4e0d\u8981\u6211\u8bf7\u4ed6\u4e0b\u6765\u4e0e\u4e24\u4f4d\u76f8 \u4f1a\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u60ca\u52a8\u4ed6\uff0c\u5f85\u6211\u4e0a\u53bb\u5148\u770b\u4e00\u770b\uff0c\u9752\u7b26\u9053\u957f\uff0c\u4f60\u4e5f\u5230\u9a6c\u53a9\u53bb\u770b\u770b\uff0c\u5018 \u82e5\u771f\u662f\u5e2e\u4e3b\u5750\u9a91\uff0c\u53ef\u4e0d\u80fd\u8ba9\u5b83\u843d\u5728\u5916\u4eba\u624b\u91cc\u3002\u201d \u3000\u3000\u9752\u7b26\u548c\u8463\u5341\u4e09\u5a18\u5df2\u7ecf\u731c\u60f3\u5230\u662f\u91d1\u9010\u6d41\u4e86\uff0c\u4e0d\u8fc7\u4e5f\u8fd8\u662f\u6709\u70b9\u6000\u7591\uff0c\u56e0\u4e3a\u4ee5\u91d1\u9010\u6d41\u7684\u6b66\u529f\uff0c \u4f3c\u4e4e\u8fd8\u4e0d\u80fd\u591f\u62a2\u5f97\u4e86\u4ed6\u4eec\u5e2e\u4e3b\u7684\u5750\u9a91\u3002\u8463\u5341\u4e09\u5a18\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5341\u5206\u96be\u6597\uff0c\u5047\u82e6\u771f\u7684\u662f \u4ed6\uff0c\u6211\u5148\u8ba9\u4ed6\u5c1d\u4e24\u679a\u6bd2\u9488\u3002\u201d\u5979\u4e0d\u8bb8\u5e97\u4e3b\u4eba\u53eb\u9192\u91d1\u9010\u6d41\uff0c\u539f\u6765\u5c31\u662f\u51c6\u5907\u53bb\u6697\u7b97\u7684\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u5bf9\u91d1\u9010\u6d41\u9887\u4e3a\u5fcc\u60ee\uff0c\u91d1\u9010\u6d41\u5bf9\u4ed6\u4eec\u4e5f\u6ca1\u6709\u53d6\u80dc\u7684\u628a\u63e1\u3002\u8463\u5341\u4e09\u5a18\u7684\u6b66\u529f\u5df2\u7ecf\u548c \u4ed6\u5dee\u4e0d\u591a\uff0c\u52a0\u4e0a\u9752\u7b26\u662f\u516d\u5408\u5e2e\u4e2d\u7684\u7b2c\u4e09\u9ad8\u624b\uff0c\u9664\u4e86\u53f2\u767d\u90fd\u4e0e\u8463\u5341\u4e09\u5a18\u5c31\u6570\u5230\u4ed6\u3002\u4ed6\u4eec\u4e8c\u4eba\u8054 \u624b\uff0c\u6211\u53ef\u6253\u4ed6\u4eec\u4e0d\u8fc7\u3002\u91d1\u9010\u6d41\u5fc3\u60f3\u3002\u56e0\u6b64\uff0c\u5f53\u8463\u5341\u4e09\u5a18\u4e0a\u697c\u4e4b\u65f6\uff0c\u4ed6\u5c31\u60f3\u597d\u4e86\u8131\u8eab\u4e4b\u8ba1\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u53d6\u51fa\u4e00\u652f\u7ec6\u957f\u7684\u5439\u7ba1\uff0c\u63d2\u5165\u95e8\u7f1d\uff0c\u5bf9\u51c6\u5367\u5e8a\uff0c\u5439\u4e86\u4e09\u652f\u6bd2\u9488\uff0c\u8fd9\u662f\u89c1\u8840\u5c01\u5589\u7684 \u6bd2\u9488\uff0c\u53ea\u8981\u523a\u7834\u4e00\u70b9\u76ae\u80a4\uff0c\u5c31\u96be\u6d3b\u547d\u3002\u5fc3\u60f3\uff1a\u201c\u5373\u4f7f\u5c04\u4e0d\u7740\u8fd9\u5c0f\u5b50\uff0c\u8fd9\u5c0f\u5b50\u4e5f\u4e00\u5b9a\u4f1a\u5413\u5f97\u8df3 \u8d77\u6765\uff0c\u4ed6\u4e00\u8df3\u8d77\u6765\uff0c\u6211\u7684\u6bd2\u9488\u7eed\u53d1\uff0c\u90a3\u5c31\u975e\u4e2d\u4e0d\u53ef\uff01\u201d \u3000\u3000\u54ea\u77e5\u6bd2\u9488\u5439\u4e86\u8fdb\u53bb\uff0c\u91cc\u9762\u7adf\u662f\u6beb\u65e0\u52a8\u9759\u3002\u8463\u5341\u4e09\u5a18\u60ca\u7591\u4e0d\u5b9a\uff0c\u201c\u96be\u9053\u6bd2\u9488\u5c04\u4e2d\u4e86\u4ed6\u7684\u54bd \u5589\uff0c\u4ed6\u54fc\u4e5f\u4e0d\u54fc\u4e00\u58f0\u5c31\u6b7b\u53bb\u4e86\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u51b3\u610f\u770b\u4e2a\u660e\u767d\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5373\u4f7f\u8fd9\u5c0f\u5b50\u672a\u7740\u6bd2\u9488\uff0c\u51ed\u6211\u7684\u6b66\u529f\uff0c\u4e09\u4e94\u5341\u62db\u4e4b \u5185\uff0c\u4e5f\u51b3\u4e0d\u4f1a\u8d25\u5728\u4ed6\u7684\u624b\u4e0b\u3002\u9752\u7b26\u4e00\u6765\uff0c\u6211\u4eec\u4fbf\u53ef\u7a33\u64cd\u80dc\u7b97\uff0c\u4e0d\u8fc7\u8d39\u529b\u4e00\u70b9\u7f62\u4e86\u3002\u4f46\u8fd9\u5c0f\u5b50 \u72e1\u733e\u5f97\u5f88\uff0c\u53ef\u83ab\u8981\u7740\u4e86\u4ed6\u7684\u9053\u513f\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u4e00\u624b\u6325\u821e\u8f6f\u97ad\uff0c\u4e00\u624b\u6301\u7740\u5439\u7ba1\uff0c\u653e\u5927\u80c6\u5b50\uff0c\u201c\u4e53\u201d\u7684\u4e00\u811a\u8e22\u5f00\u623f\u95e8\uff0c\u4fbf\u51b2\u8fdb \u53bb\uff01 \u3000\u3000\u5979\u5df2\u7ecf\u662f\u5c0f\u5fc3\u9632\u5907\u4e86\uff0c\u4e0d\u6599\u4ecd\u662f\u7740\u4e86\u91d1\u9010\u6d41\u7684\u9053\u513f\u3002 \u3000\u3000\u53ea\u542c\u5230\u201c\u94db\u5577\u201d\u58f0\u54cd\uff0c\u81ed\u6c14\u56db\u6ea2\u3002\u539f\u6765\u91d1\u9010\u6d41\u5728\u95e8\u5934\u6302\u4e86\u4e00\u4e2a\u5c3f\u58f6\uff0c\u5c3f\u58f6\u91cc\u6709\u4ed6\u521a\u521a\u64a4 \u4e86\u7684\u534a\u58f6\u70ed\u5c3f\uff0c\u8463\u5341\u4e09\u5a18\u8e22\u5f00\u623f\u95e8\uff0c\u7acb\u5373\u4fbf\u662f\u81ed\u5c3f\u6dcb\u5934\u3002\u8463\u5341\u4e09\u5a18\u7d20\u6765\u7231\u6d01\uff0c\u8fd9\u4e00\u4e0b\u53ef\u771f\u662f \u628a\u5979\u5f04\u5f97\u557c\u7b11\u7686\u975e\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u6c14\u6781\u6012\u6781\uff0c\u987e\u4e0d\u5f97\u63e9\u62b9\uff0c\u5530\u7684\u4e00\u97ad\u5c31\u6253\u4e0b\u53bb\uff0c\u8fd9\u4e00\u97ad\u6253\u4e0b\uff0c\u5f53\u7136\u4e5f\u5c31\u53d1\u89c9\u5e8a\u4e0a \u6ca1\u6709\u4eba\u4e86\u3002\u8463\u5341\u4e09\u5a18\u672c\u6765\u662f\u4e2a\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u5927\u6012\u4e4b\u540e\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u7acb\u5373\u60f3\u5230\uff1a\u201c\u8fd9\u5c0f\u5b50 \u620f\u800d\u4e86\u6211\uff0c\u7109\u6709\u8fd8\u8eb2\u5728\u5e8a\u4e0a\u6328\u6253\u4e4b\u7406\uff1f\u201d\u679c\u7136\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u9752\u7b26\u9053\u4eba\u5728\u4e0b\u9762\u53eb\u9053\uff1a\u201c\u56db \u59b9\u5feb\u6765\uff01\u8fd9\u5c0f\u5b50\u5728\u8fd9\u513f\uff01\u201d \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u5728\u6446\u597d\u5c3f\u58f6\u9635\u4e4b\u540e\uff0c\u65e9\u5df2\u4ece\u540e\u7a97\u6e9c\u4e86\u51fa\u53bb\u3002\u4ed6\u6bd4\u9752\u7b26\u9053\u4eba\u5feb\u4e00\u6b65\uff0c\u9752\u7b26\u9053\u4eba \u6b63\u662f\u5728\u9a6c\u53a9\u78b0\u7740\u4e86\u4ed6\u3002 \u3000\u3000\u9752\u7b26\u9053\u4eba\u62c2\u5c18\u4e00\u6296\uff0c\u8fce\u5934\u7f69\u4e0b\uff0c\u60f3\u8981\u963b\u6b62\u91d1\u9010\u6d41\u62a2\u9a6c\u3002\u4ed6\u7684\u8fd9\u67c4\u4f5b\u5c18\uff0c\u5c18\u5c3e\u4e43\u662f\u4e4c\u91d1\u7384 \u4e1d\uff0c\u53ef\u4ee5\u523a\u4eba\u7a74\u9053\uff0c\u4e5f\u7b97\u662f\u4e00\u4ef6\u7f55\u89c1\u7684\u5947\u95e8\u5175\u5668\u3002\u4f46\u78b0\u4e0a\u4e86\u91d1\u9010\u6d41\uff0c\u6bd5\u7adf\u8fd8\u662f\u6280\u900a\u4e00\u7b79\u3002 \u3000\u3000\u91d1\u9010\u6d41\u633d\u4e86\u4e00\u6735\u5251\u82b1\uff0c\u5251\u5149\u8fc7\u5904\uff0c\u98d2\u98d2\u8fde\u58f0\u3002\u9752\u7b26\u9053\u4eba\u7684\u62c2\u5c18\u672a\u6253\u7740\u4ed6\uff0c\u5c18\u5c3e\u5374\u7ed9\u4ed6\u524a \u53bb\u4e86\u4e00\u64ae\u3002\u91d1\u9010\u6d41\u63d0\u8d77\u90a3\u4e2a\u85cf\u7740\u7384\u94c1\u7684\u5323\u5b50\uff0c\u731b\u5730\u5f80\u524d\u4e00\u63a8\uff0c\u9752\u7b26\u9053\u4eba\u8bc6\u5f97\u5389\u5bb3\uff0c\u8fde\u5fd9\u540e \u9000\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u91d1\u9010\u6d41\u5df2\u662f\u8fdb\u4e86\u9a6c\u53a9\uff0c\u628a\u90a3\u5339\u9a6c\u7275\u4e86\u51fa\u6765\u3002\u9752\u7b26\u9053\u4eba\u5192\u9669\u62a2\u653b\uff0c\u963b\u6b62 \u4ed6\u8de8\u4e0a\u9a6c\u80cc\u3002 \u3000\u3000\u91d1\u9010\u6d41\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u8fd9\u725b\u9f3b\u5b50\u81ed\u9053\u58eb\u4e0d\u60f3\u6d3b\u5566\uff0c\u6211\u4e0d\u8981\u8fd9\u5339\u5750\u9a91\uff0c\u5148\u6bd9\u4e86\u4f60\uff01\u201d\u6325 \u821e\u7384\u94c1\uff0c\u5411\u9752\u7b26\u9053\u4eba\u786c\u51b2\uff0c\u9752\u7b26\u9053\u4eba\u5927\u60ca\uff0c\u53ea\u597d\u53c8\u518d\u540e\u9000\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u6012\u6c14\u51b2\u51b2\u8d76\u5230\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u80c6\u6562\u620f\u800d\u8001\u5a18\uff0c\u4f60\u53ef\u522b\u8dd1\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u903c\u9053\u4e86\u9752\u7b26\u9053\u4eba\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u65e9\u5df2\u8df3\u4e0a\u4e86\u9a6c\u80cc\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u81ed\uff0c\u597d\u81ed\uff01\u6211\u4e0d\u6562\u6cbe \u60f9\u4f60\u8fd9\u81ed\u5a46\u5a18\uff0c\u6211\u53ef\u8981\u8d70\u5566\uff01\u201d\u8463\u5341\u4e09\u5a18\u6325\u97ad\u6253\u53bb\uff0c\u6253\u4e86\u4e2a\u7a7a\uff0c\u53cd\u7ed9\u9a6c\u8e44\u8e22\u8d77\u7684\u5c18\u571f\u6cbe\u4e86\u6ee1 \u9762\uff0c\u5979\u8138\u4e0a\u5c3f\u6c34\u672a\u5e72\uff0c\u7ed9\u5c18\u58eb\u6cbe\u4e0a\u4e86\u7262\u7262\u9644\u7740\uff0c\u5c31\u4f3c\u6d82\u4e86\u4e2a\u5927\u82b1\u8138\u4e00\u822c\u3002\u8463\u5341\u4e09\u5a18\u6c14\u5f97\u4e03\u7a8d \u751f\u70df\uff0c\u53ea\u542c\u5f97\u91d1\u9010\u6d41\u7684\u7b11\u58f0\u8fdc\u8fdc\u4f20\u6765\uff0c\u4ed6\u90a3\u4e00\u4eba\u4e00\u9a91\uff0c\u5374\u65e9\u5df2\u5728\u591c\u8272\u6c89\u6c89\u4e4b\u4e2d\u6d88\u5931\u4e86\u3002 \u3000\u3000\u4f59\u9010\u6d41\u8dd1\u4e86\u4e00\u7a0b\uff0c\u84e6\u5730\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u4e00\u6765\u5c31\u5411\u5e97\u4e3b\u4eba\u67e5\u95ee\u6709\u6ca1\u6709\u6c5f\u6e56\u5973\u5b50\u6295\u5bbf\uff0c\u83ab\u975e\u662f \u53f2\u7ea2\u82f1\u4e5f\u5230\u4e86\u8fd9\u513f\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u8e44\u58f0\u9a70\u9aa4\uff0c\u9690\u9690\u4f20\u6765\u3002\u91d1\u9010\u6d41\u542c\u51fa\u4e86\u6709\u56db\u4e94\u9a91\u4e4b\u591a\uff0c\u597d\u751f\u5947\u602a\uff0c\u5fc3\u91cc \u60f3\u9053\uff1a\u201c\u4e09\u66f4\u534a\u591c\uff0c\u8fd9\u4e00\u4f19\u4eba\u5728\u9053\u4e0a\u5954\u9a70\uff0c\u4e0d\u77e5\u662f\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u662f\u4e2a\u7231\u7ba1\u95f2\u4e8b\u7684\u4eba\uff0c\u597d\u5947\u5fc3\u4e00\u8d77\uff0c\u5c31\u671d\u90a3\u4e2a\u65b9\u5411\u8ffd\u53bb\uff0c\u4ed6\u7684\u9a6c\u5feb\uff0c\u6ca1\u6709\u591a\u4e45\uff0c\u5c31 \u8ffd\u4e0a\u4e86\u3002 \u3000\u3000\u6de1\u6708\u758f\u661f\u4e4b\u4e0b\uff0c\u53ea\u89c1\u6700\u524d\u9762\u4e00\u9a91\u662f\u4e2a\u5973\u5b50\uff0c\u540e\u9762\u4e09\u9a91\u5feb\u9a6c\u8ffd\u5979\u3002\u91d1\u9010\u6d41\u8ba4\u5f97\u5176\u4e2d\u4e24\u4eba\u662f \u9752\u9f99\u5e2e\u7684\u8235\u4e3b\u9ad8\u5927\u6210\u548c\u767d\u864e\u5e2e\u7684\u8235\u4e3b\u675c\u5927\u4e1a\uff0c\u8fd9\u4e24\u4e2a\u4eba\u5c31\u662f\u90a3\u6b21\u5728\u82cf\u5dde\u57ce\u5916\uff0c\u548c\u5bab\u79c9\u85e9\u4e00\u8d77 \u62a2\u52ab\u53f2\u7ea2\u82f1\u4e4b\u65f6\uff0c\u7ed9\u4ed6\u78b0\u4e0a\uff0c\u548c\u4ed6\u4ea4\u8fc7\u624b\u7684\u3002\u4f46\u8dd1\u5728\u6700\u524d\u9762\u7684\u90a3\u4e2a\u5973\u5b50\uff0c\u56e0\u4e3a\u8ddd\u79bb\u592a\u8fdc\uff0c\u91d1 \u9010\u6d41\u53ea\u80fd\u4ece\u5979\u7684\u670d\u9970\u548c\u957f\u53d1\u770b\u51fa\u662f\u4e2a\u5973\u5b50\uff0c\u662f\u4e0d\u662f\u53f2\u7ea2\u82f1\uff0c\u4ed6\u8fd8\u672a\u77e5\u9053\u3002\u8ffd\u5175\u4e4b\u4e2d\uff0c\u8fd8\u6709\u4e00 \u4e2a\u4eba\u91d1\u9010\u6d41\u4e5f\u4e0d\u8ba4\u8bc6\uff0c\u8fd9\u4e2a\u4eba\u7684\u5750\u9a91\u6700\u5feb\uff0c\u6b64\u65f6\u4e0e\u90a3\u5973\u5b50\u7684\u8ddd\u79bb\uff0c\u5df2\u662f\u4e0d\u5230\u6570\u4e08\u4e4b\u9065\u3002 \u3000\u3000\u53ea\u542c\u5f97\u90a3\u4eba\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e2b\u5934\u597d\u5927\u7684\u80c6\u5b50\uff0c\u6211\u4eec\u7684\u4e1c\u897f\u4f60\u4e5f\u6562\u62a2\uff01\u201d\u8ddd\u79bb\u5df2\u8fd1\uff0c\u633a\u8d77\u957f \u67aa\uff0c\u4e00\u67aa\u5c31\u5411\u524d\u9762\u90a3\u9a91\u6311\u53bb\u3002\u90a3\u5973\u5b50\u56de\u5251\u4e00\u6321\uff0c\u4f7f\u4e86\u4e00\u62db\u201c\u8986\u96e8\u7ffb\u4e91\u201d\uff0c\u628a\u8fd9\u67c4\u957f\u67aa\u7ede\u5f97\u8f6c \u4e86\u4e24\u5708\u3001\u767b\u65f6\u628a\u4ed6\u8fd9\u62db\u7834\u4e86\uff0c\u53ef\u662f\u5979\u867d\u7136\u7834\u89e3\u654c\u4eba\u7684\u62db\u6570\uff0c\u5374\u654c\u4e0d\u4f4f\u90a3\u4eba\u7684\u6c14\u529b\uff0c\u6643\u4e86\u4e24 \u6643\uff0c\u5750\u4e0d\u7a33\u9a6c\u978d\uff0c\u53ea\u597d\u8df3\u4e0b\u9a6c\u6765\u3002 \u3000\u3000\u8fd9\u5973\u5b50\u4e00\u56de\u5934\uff0c\u91d1\u9010\u6d41\u5c31\u770b\u5f97\u6e05\u695a\u4e86\uff0c\u5374\u539f\u6765\u4e0d\u662f\u53f2\u7ea2\u82f1\uff0c\u800c\u662f\u5c01\u5999\u5ae6\u3002\u91d1\u9010\u6d41\u8fd9\u4e00\u5929 \u7684\u767d\u5929\u8fd8\u66fe\u7ecf\u5411\u5c01\u5b50\u8d85\u67e5\u95ee\u8fc7\u4ed6\u7684\u5973\u513f\uff0c\u4e0d\u6599\u665a\u4e0a\u5c31\u78b0\u4e0a\u4e86\u3002\u91d1\u9010\u6d41\u5728\u770b\u6e05\u695a\u4e86\u662f\u8c01\u4e4b\u540e\uff0c \u5fc3\u91cc\u867d\u7136\u6709\u70b9\u5931\u671b\uff0c\u5374\u4ed6\u6709\u51fa\u4e4e\u610f\u5916\u4e4b\u611f\u7684\u60ca\u559c\u3002 \u3000\u3000\u628a\u5c01\u5999\u5ae6\u6253\u5f97\u843d\u9a6c\u7684\u90a3\u4e2a\u4e00\u4eba\uff0c\u5ffd\u5730\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u53ef\u80fd\u627e\u9519\u4eba\u4e86\uff0c\u8fd9\u4e2a \u96cc\u513f\u7684\u5251\u6cd5\u4e0d\u5bf9\uff0c\u5979\u3001\u5979\u4f3c\u4e4e\u662f\u2026\u2026\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u5730\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u8ddf\u7740\u4e5f\u6454\u4e0b\u9a6c\u6765\u3002\u539f \u6765\u662f\u91d1\u9010\u6d41\u6015\u4ed6\u52a0\u5bb3\u5c01\u5999\u5ae6\uff0c\u6478\u51fa\u4e86\u5757\u788e\u94f6\uff0c\u4f5c\u4e3a\u6697\u5668\uff0c\u6253\u4e2d\u4e86\u4ed6\u540e\u5fc3\u7684\u7a74\u9053\u3002\u6b63\u662f\uff1a \u3000\u3000\u4e14\u628a\u5a92\u91d1\u5f53\u6697\u5668\uff0c\u60e9\u51f6\u52a9\u53cb\u64ae\u59fb\u7f18\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u5341\u516d\u56de\u3000\u503e\u56fd\u503e\u57ce\u96be\u4e0e\u9047\u3000\u4e50\u5c71\u4e50\u6c34\u6613\u5fd8\u5f52 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u5341\u516d\u56de\u3000\u503e\u56fd\u503e\u57ce\u96be\u4e0e\u9047\u3000\u4e50\u5c71\u4e50\u6c34\u6613\u5fd8\u5f52 \u3000\u3000\u91d1\u9010\u6d41\u6253\u7ffb\u4e86\u90a3\u4e2a\u6c49\u5b50\uff0c\u53cc\u817f\u4e00\u5939\uff0c\u80ef\u4e0b\u7684\u9a8f\u9a6c\u98de\u4e00\u822c\u7684\u8dd1\u8fc7\u53bb\u3002\u9ad8\u5927\u6210\u8d77\u521d\u4ee5\u4e3a\u91d1\u9010 \u6d41\u662f\u548c\u4ed6\u4e00\u4f19\u7684\u9ed1\u9053\u4e2d\u4eba\uff0c\u90fd\u662f\u6765\u8ffd\u6355\u8fd9\u4e2a\u5973\u5b50\u7684\uff0c\u6545\u800c\u867d\u7136\u77e5\u9053\u540e\u9762\u591a\u4e86\u4e00\u9a91\uff0c\u5374\u4e5f\u4e0d\u4ee5 \u4e3a\u610f\uff0c\u6b64\u65f6\u89c1\u524d\u9762\u90a3\u4e2a\u6c49\u5b50\u843d\u9a6c\uff0c\u65b9\u59cb\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u56de\u8fc7\u5934\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4eec\u771f\u662f\u8d3c\u6027\u4e0d\u6539\uff0c\u53c8\u5728\u8fd9\u91cc\u6b3a\u8d1f\u5973\u5b50\uff01\u201d\u5feb\u9a6c\u8d76\u4e0a\uff0c\u63d0\u8d77\u90a3\u4e2a \u7384\u94c1\u5323\u5b50\u4fbf\u662f\u4e00\u7838\u3002 \u3000\u3000\u9ad8\u5927\u6210\u4e3e\u8d77\u72fc\u7259\u68d2\u62db\u67b6\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u72e0\u7259\u68d2\u65ad\u4e3a\u4e24\u622a\uff0c\u9ad8\u5927\u6210\u864e\u53e3\u6d41\u8840\uff0c\u5413\u5f97\u9b44\u6563 \u9b42\u98de\uff0c\u62d4\u8f6c\u9a6c\u5934\uff0c\u614c\u5fd9\u9003\u8dd1\u3002 \u3000\u3000\u675c\u5927\u4e1a\u53cc\u94a9\u6325\u821e\uff0c\u659c\u523a\u7a9c\u51fa\u3002\u91d1\u9010\u6d41\u559d\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u662f\u597d\u4e1c\u897f\u3002\u591a\u5c11\u6302\u4e2a\u5f69\u5427\uff01\u201d\u4e00\u63d0 \u9a6c\u50f5\uff0c\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u201d\u4e00\u8df3\u6570\u4e08\uff0c\u91d1\u9010\u6d41\u5530\u7684\u4e00\u5251\u4fbf\u523a\u8fc7\u53bb\uff0c\u675c\u5927\u4e1a\u4fef\u978d\u800c\u9003\uff0c\u53cc\u94a9\u62a4 \u5934\uff0c\u5251\u5149\u8fc7\u675c\uff0c\u4e00\u5bf9\u94a9\u62a4\u624b\u90fd\u7ed9\u524a\u65ad\uff0c\u80a9\u5934\u7ed9\u5251\u5c16\u5212\u5f00\u4e86\u4e00\u9053\u4f24\u53e3\uff0c\u5e78\u800c\u672a\u7ed9\u523a\u7740\u5934\u9885\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u53c8\u60ca\u53c8\u559c\uff0c\u53eb\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u4e0d\u662f\u90a3\u5c0f\uff0c\u5c0f\u2014\u2014\u201d\u91d1\u9010\u6d41\u90a3\u6b21\u4e0e\u79e6\u5143\u6d69\u540c\u5230\u5c01\u5bb6\uff0c \u662f\u4f5c\u5c0f\u53eb\u5316\u6253\u626e\u7684\uff0c\u4f46\u73b0\u5728\u5374\u662f\u4ee5\u7fe9\u7fe9\u6d4a\u4e16\u4f73\u516c\u5b50\u7684\u6a21\u6837\u51fa\u73b0\uff0c\u6545\u800c\u201c\u5c0f\u53eb\u5316\u201d\u8fd9\u4e09\u4e2a\u5b57\u5230 \u4e86\u5c01\u5999\u5ae6\u7684\u5507\u8fb9\uff0c\u53ea\u662f\u5410\u51fa\u4e86\u4e00\u4e2a\u201c\u5c0f\u201d\u5b57\uff0c\u5c31\u505c\u6b62\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u548c\u79e6\u5143\u6d69\u540c\u5728\u4e00\u8d77\u7684\u90a3\u4e2a\u5c0f\u53eb\u5316\u3002\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u8ffd\u4f60\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u6069\u516c\u9ad8\u59d3\u5927\u540d\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u59d3\u91d1\uff0c\u540d\u9010\u6d41\uff0c\u6211\u4e0d\u559c\u6b22\u522b\u4eba\u5411\u6211\u79f0\u2018\u8001\u2019\uff0c\u628a\u6211\u53eb\u5f97\u597d\u50cf\u662f\u516d\u4e03\u5341 \u5c81\u7684\u8001\u5934\u513f\u4e86\u3002\u4f60\u6700\u597d\u8fd8\u662f\u53eb\u6211\u5c0f\u53eb\u5316\u3002\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u628a\u90a3\u6c49\u5b50\u4e00\u628a\u63d0\u4e86\u8d77\u6765\uff0c\u4e3e\u638c\u5728\u4ed6\u80cc\u5fc3\u4e00\u62cd\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8981\u6b3a\u4fae\u5c01\u59d1 \u5a18\uff0c\u8bf4\uff01\u201d \u3000\u3000\u90a3\u6c49\u5b50\u542c\u5f97\u4e00\u4e2a\u201c\u5c01\u201d\u5b57\uff0c\u9762\u9732\u559c\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u5c01\u59d1\u5a18\uff0c\u4ee4\u5c0a\u7684\u5927\u540d\u53ef\u662f\u5b50\u8d85\u4e8c\u5b57\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u7709\u5934\u4e00\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bc6\u5f97\u6211\u7684\u7239\u7239\uff1f\u4f60\u4ee5\u524d\u662f\u5e72\u4ec0\u4e48\u7684\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u771f\u662f\u5927\u6c34\u51b2\u5012\u4e86\u9f99\u738b\u5e99\uff0c\u81ea\u5bb6\u4eba\u8ba4\u4e0d\u5f97\u81ea\u5bb6\u4eba\u4e86\u3002\u6211\u548c\u4f60\u7684\u7239\u7239 \u662f\u8001\u670b\u53cb\u4e86\uff0c\u4ee5\u524d\u4ed6\u505a\u5927\u5185\u4f8d\u536b\u7684\u65f6\u5019\uff0c\u6211\u5728\u5180\u5317\u9053\u4e0a\u5e72\u6ca1\u672c\u94b1\u7684\u751f\u610f\uff0c\u591a\u8499\u4ed6\u7684\u7167\u6599\uff0c\u4ece \u6765\u6ca1\u6709\u5931\u8fc7\u624b\u3002\u521a\u624d\u6211\u5df2\u7ecf\u770b\u51fa\u4f60\u7684\u5251\u6cd5\uff0c\u679c\u7136\u4f60\u771f\u662f\u4ed6\u7684\u5973\u513f\u3002\u201d\u539f\u6765\u8fd9\u4eba\u4ee5\u524d\u505a\u72ec\u811a\u5927 \u76d7\uff0c\u5c01\u5b50\u8d85\u662f\u4ed6\u7684\u9760\u5c71\uff0c\u4ed6\u62a2\u52ab\u6240\u5f97\uff0c\u8981\u5206\u4e00\u534a\u7ed9\u5c01\u5b50\u8d85\u3002\u5c01\u5b50\u8d85\u518d\u7ed9\u4ed6\u6253\u70b9\u5b98\u5e9c\uff0c\u6545\u800c\u4ed6 \u7684\u672c\u9886\u867d\u7136\u4e0d\u662f\u5f88\u9ad8\uff0c\u5982\u5f97\u4ee5\u6a2a\u884c\u65e0\u963b\uff0c\u4ece\u672a\u53d7\u6355\u3002 \u3000\u3000\u8fd9\u4eba\u4ee5\u4e3a\u91d1\u9010\u6d41\u4e5f\u4e00\u5b9a\u662f\u548c\u5c01\u5b50\u8d85\u6709\u5173\u7cfb\u7684\u665a\u8f88\uff0c\u6240\u4ee5\u6025\u6025\u5fd9\u5fd9\u7684\u4fbf\u5957\u4ea4\u60c5\u3002\u54ea\u77e5\u91d1\u9010 \u6d41\u53cc\u670d\u4e00\u7ffb\uff0c\u559d\u9053\uff1a\u201c\u4f11\u8981\u7f57\u5506\uff0c\u5feb\u8bf4\uff01\u4f60\u4eec\u8ffd\u5979\uff0c\u5230\u5e95\u662f\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u90a3\u4eba\u8d54\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u4e2a\u8bef\u4f1a\uff0c\u8bef\u4f1a\uff0c\u6709\u597d\u51e0\u4e2a\u5e2e\u4f1a\u7684\u8235\u4e3b\uff0c\u9001\u8d3a\u793c\u4e0a\u4eac\u7ed9\u8428\u603b\u7ba1\u795d \u5bff\uff0c\u4e0d\u6599\u5728\u8def\u4e0a\u5148\u540e\u7ed9\u4e00\u4e2a\u5973\u5b50\u62a2\u4e86\u3002\u8fd9\u5973\u5b50\u795e\u51fa\u9b3c\u6ca1\uff0c\u6ca1\u4eba\u548c\u5979\u671d\u8fc7\u76f8\u3002\u6240\u4ee5\u9752\u9f99\u5e2e\u7684\u5e2e \u4e3b\u9ad8\u5927\u6210\u53d1\u4e0b\u4e86\u7eff\u6797\u5e16\uff0c\u8bf7\u9053\u4e0a\u7684\u670b\u53cb\u5e2e\u5e2e\u5fd9\uff0c\u56db\u5904\u641c\u67e5\u8fd9\u4e2a\u5973\u5b50\u3002\u51e1\u662f\u5f62\u8ff9\u53ef\u7591\u7684\u6c5f\u6e56\u5973 \u5b50\u90fd\u4e0d\u653e\u8fc7\uff0c\u6240\u4ee5\uff0c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u4eec\u4ee5\u4e3a\u6211\u662f\u90a3\u4e2a\u5973\u5b50\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u8428\u603b\u7ba1\u662f\u4ee4\u5c0a\u7684\u8001\u4e0a\u53f8\uff0c\u4f84\u5973\u600e\u4f1a\u62a2\u4ed6\u7684\u793c\u7269\u3002\u8fd9\u90fd\u602a\u6211\u4eec\u770b\u8d70\u4e86\u773c\uff0c\u5f97 \u7f6a\u4e86\u4f84\u5973\u4e86\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u51b7\u7b11\u9053\uff1a\u201c\u6211\u53ea\u6068\u6211\u6ca1\u6709\u90a3\u5973\u5b50\u7684\u672c\u9886\uff0c\u6211\u5018\u82e5\u6709\u5979\u7684\u672c\u9886\uff0c\u6211\u4e5f\u4f1a\u62a2\u7684\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u5403\u4e86\u4e00\u60ca\uff0c\u60f3\u4e0d\u5230\u5c01\u5999\u5ae6\u7adf\u4f1a\u5982\u6b64\u8bf4\u8bdd\u3002\u4e00\u65f6\u95f4\u4e0d\u77e5\u8bf4\u4e9b\u4ec0\u4e48\u624d\u597d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u516d\u5408\u5e2e\u4e5f\u63a5\u4e86\u7eff\u6797\u5e16\u5417\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u4e00\u542c\u91d1\u9010\u6d41\u8fd9\u6837\u53d1\u95ee\uff0c\u5c31\u77e5\u91d1\u9010\u6d41\u662f\u4e2a\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u5fc3\u91cc\u7a0d\u7a0d\u8f7b\u677e\uff0c\u8d76\u5fd9\u4fbf \u7b54\uff1a\u201c\u516d\u5408\u5e2e\u662f\u6c5f\u6e56\u5e2e\u4f1a\u4e4b\u9996\uff0c\u9ad8\u5927\u6210\u600e\u80fd\u968f\u4fbf\u5dee\u4e00\u4e2a\u4eba\u628a\u7eff\u6797\u5e16\u53d1\u7ed9\u53f2\u5e2e\u4e3b\uff1f\u4e0d\u8fc7\u516d\u5408\u5e2e \u7684\u56db\u5927\u9999\u4e3b\u5374\u662f\u6781\u91cd\u6c5f\u6e56\u4e49\u6c14\uff0c\u77e5\u9053\u4e86\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u90fd\u81ea\u544a\u594b\u52c7\u7684\u53c2\u52a0\u3002\u9ad8\u5927\u6210\u6b63\u56e0\u4e3a\u4e8b\u60c5\u7d27 \u6025\uff0c\u6765\u4e0d\u53ca\u5411\u53f2\u5e2e\u4e3b\u8bf7\u793a\u800c\u6709\u6240\u5fe7\u8651\uff0c\u5fe7\u8651\u53f2\u5e2e\u4e3b\u602a\u4ed6\u64c5\u53d1\u7eff\u6797\u5e16\u800c\u5174\u5e08\u95ee\u514d\u5f97\u4ed6\u624b\u4e0b\u7684\u9999 \u4e3b\u5e2e\u5fd9\u3002\u8fd9\u95ee\u9898\u5c31\u8fce\u5203\u800c\u89e3\u4e86\u3002\u201d\u8fd9\u4eba\u89c1\u91d1\u9010\u6d41\u95ee\u5f97\u201c\u5728\u884c\u201d\uff0c\u53ea\u9053\u4ed6\u548c\u516d\u5408\u5e2e\u591a\u5c11\u4e5f\u6709\u70b9 \u5173\u7cfb\uff0c\u6545\u800c\u4e0d\u538c\u5176\u8be6\u5730\u56de\u7b54\u3002\u5374\u4e0d\u77e5\u91d1\u9010\u6d41\u53ea\u662f\u60f3\u67e5\u95ee\u53f2\u7ea2\u82f1\uff0c\u4ed6\u5df2\u7ecf\u731c\u60f3\u5f97\u5230\uff0c\u62a2\u90a3\u4e9b\u5e2e \u4f1a\u793c\u7269\u7684\u5973\u5b50\u4e00\u5b9a\u662f\u53f2\u7ea2\u82f1\u65e0\u7591\uff0c\u5982\u4eca\u4ed6\u53ea\u662f\u591a\u65b9\u201c\u6c42\u8bc1\u201d\u800c\u5df2\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u90a3\u56db\u4e2a\u9999\u4e3b\u4e5f\u8981\u53bb\u8ffd\u6355\u8fd9\u4e2a\u5973\u5b50\uff0c\u4ed6\u4eec\u96be\u9053\u5c31\u6ca1\u6709\u4e00\u70b9\u5bb3\u6015\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c0f\u5b50\u597d\u50cf\u77e5\u9053\u8bb8\u591a\u4e8b\u60c5\uff0c\u4e00\u5b9a\u662f\u548c\u516d\u4ee4\u5e2e\u6709\u5173\u7cfb\u7684\u4e86\u3002\u201d \u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u90a3\u56db\u4f4d\u9999\u4e3b\u7b54\u5e94\u62d4\u5200\u76f8\u52a9\u4e4b\u65f6\uff0c\u662f\u66fe\u63d0\u51fa\u4e00\u4e2a\u6761\u4ef6\uff0c\u53ea\u8bb8\u6d3b\u64d2\uff0c\u51b3\u4e0d\u80fd\u4f24\u5bb3\u90a3 \u4e2a\u5973\u5b50\u3002\u6211\u4eec\u90fd\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u7f18\u6545\u3002\u91d1\u516c\u5b50\u8fd9\u6837\u95ee\uff0c\u60f3\u5fc5\u77e5\u9053\u5185\u91cc\u60c5\u7531\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\uff0c\u4f46\u6211\u4e0d\u544a\u8bc9\u4f60\uff01\u201d \u3000\u3000\u90a3\u6c49\u5b50\u751a\u662f\u5c34\u5c2c\uff0c\u5fd9\u53c8\u8d54\u7b11\u8bf4\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u6d89\u53ca\u516d\u5408\u5e2e\u7684\u9690\u60c5\uff0c\u5c0f\u4eba\u81ea\u662f\u4e0d\u914d\u77e5\u9053\u3002 \u91d1\u516c\u5b50\u8fd8\u6709\u4ec0\u4e48\u8981\u95ee\u7684\u4e48\uff1f\u5c0f\u4eba\u53ef\u4ee5\u8d70\u4e86\u5427\uff1f\u201d\u53e3\uff0c \u3000\u3000\u300d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u80fd\uff01\u201d \u3000\u3000\u90a3\u6c49\u5b50\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u516c\u5b50\u770b\u5728\u5c01\u5b50\u8d85\u548c\u516d\u5408\u5e2e\u7684\u4efd\u4e0a\uff0c\u54b1\u4eec\u603b\u662f\u81ea\u5df1\u4eba\u5427\uff1f\u201d \u3000\u3000\u5168\u9010\u6d41\u9053\uff1a\u201c\u6211\u770b\u5728\u5c01\u5b50\u8d85\u548c\u53f2\u767d\u90fd\u7684\u4efd\u4e0a\uff0c\u8d4f\u4f60\u4e24\u5df4\u638c\uff01\u201d\u90a3\u6c49\u5b50\u5927\u60ca\u5931\u8272\uff0c\u4e00\u4e2a \u201c\u9976\u2019\u5b57\u672a\u66fe\u53eb\u5f97\u51fa\u6765\uff0c\u91d1\u9010\u6d41\u556a\u556a\u4e24\u638c\u5df2\u662f\u6253\u4e86\u4e0b\u53bb\uff0c\u90a3\u6c49\u5b50\u767b\u65f6\u53d8\u4f5c\u4e86\u4e00\u56e2\u70c2\u6ce5\u4f3c\u5730\u5012 \u5728\u5730\u4e0a\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6b7b\u7f6a\u9976\u4e86\uff0c\u6d3b\u7f6a\u96be\u9976\u3002\u4f60\u597d\u597d\u7684\u5728\u8fd9\u91cc\u8eba\u5427\uff0c\u5341\u4e8c\u65f6\u8fb0\u4e4b\u540e\u3002\u7a74\u9053\u81ea \u89e3\u3002\u201d\u90a3\u53cc\u5b50\u7ed9\u91d1\u9010\u6d41\u7528\u91cd\u624b\u6cd5\u70b9\u4e86\u7a74\u9053\uff0c\u65e9\u5df2\u6655\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u5e9f\u4e86\u4ed6\u7684\u6b66\u529f\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4ed6\u7684\u7435\u7436\u9aa8\u5df2\u7ecf\u7ed9\u6211\u634f\u788e\uff0c\u4eca\u540e\u662f\u518d\u4e5f\u4e0d\u80fd\u4f5c\u6076\u7684\u4e86\u3002\u4ed6\u7684\u8fd9\u5339\u5750 \u9a91\u867d\u7136\u6bd4\u4e0d\u4e0a\u53f2\u767d\u90fd\u7684\u2018\u7167\u591c\u72ee\u5b50\u2019\u4e5f\u662f\u96be\u5f97\u7684\u9a8f\u9a6c\uff0c\u4f60\u5c31\u8981\u4e86\u4ed6\u8fd9\u5339\u5750\u9a91\u5427\u3002\u201d \u3000\u3000\u8fd9\u5339\u9a6c\u6b63\u5728\u5c71\u5761\u4e0a\u5403\u8349\uff0c\u91d1\u9010\u6d41\u521a\u8981\u4e0a\u53bb\u628a\u5b83\u7275\u4e0b\u6765\uff0c\u5ffd\u542c\u5f97\u8e44\u58f0\u5f97\u5f97\uff0c\u9053\u4e0a\u53c8\u6765\u4e86\u4e24 \u9a91\u5feb\u9a6c\uff0c\u8fd9\u4e24\u4e2a\u4eba\u6b63\u662f\u540d\u5217\u516d\u5408\u5e2e\u4e2d\u56db\u5927\u9999\u4e3b\u7684\u5706\u6d77\u548c\u7126\u78ca\u3002 \u3000\u3000\u5706\u6d77\u8fdc\u8fdc\u5730\u770b\u89c1\u4e86\u5c01\u5999\u5ae6\uff0c\u201c\u54a6\u201d\u7684\u4e00\u58f0\u53eb\u8d77\u6765\u9053\uff1a\u201c\u8fd9\u4e2a\u96cc\u513f\u53ef\u4e0d\u662f\u54b1\u4eec\u7684\u53f2\u5927\u5c0f\u59d0 \u5440\uff0c\u4ed6\u4eec\u6050\u6015\u662f\u8ffd\u9519\u4eba\u4e86\uff01\u201d\u7126\u78ca\u9053\uff1a\u201c\u5947\u602a\uff0c\u9ad8\u5e2e\u4e3b\u548c\u675c\u5e2e\u4e3b\u4ed6\u4eec\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u5706\u6d77\u662f\u4e2a\u8d2a\u82b1\u597d\u8272\u7684\u9152\u8089\u548c\u5c1a\uff0c\u89c1\u5366\u5999\u5ae6\u957f\u5f97\u6f02\u4eae\uff0c\u8bf4\u9053\uff1a\u201c\u7ba1\u8fd9\u96cc\u513f\u662f\u8c01\uff0c\u5148\u6349\u4e86\u5979 \u518d\u8bf4\u3002\u201d\u4ed6\u7684\u4e00\u5bf9\u773c\u775b\u53ea\u987e\u76ef\u7740\u5224\u5999\u5ae6\uff0c\u7126\u78ca\u5148\u53d1\u73b0\u4e86\u5c71\u5761\u4e0a\u7684\u91d1\u9010\u6d41\u3002 \u3000\u3000\u7126\u78ca\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d\u5706\u6d77\u5c1a\u672a\u77e5\u6b7b\u6d3b\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u597d\uff1f\u201d\u7126\u78ca\u6025\u58f0\u8bf4 \u9053\uff1a\u201c\u4f60\u770b\u770b\uff0c\u597d\u50cf\u662f\u59d3\u91d1\u7684\u90a3\u5c0f\u5b50\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u4e00\u7b11\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5c45\u7136\u8fd8\u8ba4\u5f97\u6211\u8fd9\u4e2a\u53eb\u5316\u4e48\uff1f\u9ad8\u5927\u6210\u3001\u675c\u5927\u4e1a\u90fd \u662f\u8113\u5305\uff0c\u4e00\u6253\u5c31\u8dd1\uff0c\u6211\u6b63\u5acc\u6253\u5f97\u4e0d\u8fc7\u763e\u5462\uff0c\u4f60\u4eec\u6765\u5f97\u6b63\u597d\uff01\u201d \u3000\u3000\u5168\u9010\u6d41\u8f6c\u8eab\u7684\u65f6\u5019\uff0c\u65e9\u5df2\u5728\u5c71\u5761\u4e0a\u62fe\u8d77\u5341\u51e0\u5757\u788e\u77f3\u5b50\uff0c\u5927\u7b11\u58f0\u4e2d\uff0c\u77f3\u5b50\u96e8\u70b9\u822c\u5730\u98de\u51fa \u53bb\u3002 \u3000\u3000\u5706\u6d77\u3001\u7126\u78ca\u540d\u5217\u56db\u5927\u9999\u4e3b\uff0c\u6b66\u529f\u5374\u662f\u4e0e\u5176\u4ed6\u4e24\u4f4d\u9999\u4e3b\u76f8\u5dee\u9887\u8fdc\uff0c\u4ed6\u4eec\u53c8\u90fd\u662f\u7ed9\u91d1\u9010\u6d41\u6253 \u5f97\u6015\u4e86\u7684\uff0c\u6b64\u65f6\u7a81\u7136\u78b0\u89c1\u4e86\u91d1\u9010\u6d41\uff0c\u5982\u4f55\u8fd8\u6562\u548c\u4ed6\u4ea4\u624b\u3002 \u3000\u3000\u7126\u78ca\u5e78\u4e8f\u662f\u5148\u770b\u89c1\u91d1\u9010\u6d41\uff0c\u65e9\u5df2\u52d2\u4f4f\u9a6c\u5934\uff0c\u91d1\u9010\u6d41\u4e00\u8f6c\u8eab\uff0c\u4ed6\u7acb\u5373\u62d4\u9a6c\u4fbf\u8dd1\uff0c\u6ca1\u7ed9\u77f3\u5934 \u6253\u7740\u3002 \u3000\u3000\u5706\u6d77\u53ef\u5012\u6963\u4e86\uff0c\u4ed6\u662f\u8dd1\u5230\u8ddd\u79bb\u5c01\u5999\u5ae6\u5341\u4e08\u4e4b\u5185\u624d\u770b\u89c1\u91d1\u9010\u6d41\u7684\u3002\u91d1\u9010\u6d41\u7684\u77f3\u5b50\u6253\u6765\uff0c\u5706 \u6d77\u821e\u8d77\u6212\u5200\u9632\u8eab\uff0c\u4f46\u5149\u5934\u4e0a\u4ecd\u7136\u662f\u7740\u4e86\u4e00\u9897\u77f3\u5b50\uff0c\u6253\u5f97\u4ed6\u5934\u7834\u8840\u6d41\u3002\u4ed6\u5728\u5feb\u6d3b\u6797\u65f6\u66fe\u7ecf\u7ed9\u91d1 \u9010\u6d41\u6253\u7a7f\u4ed6\u7684\u5149\u5934\uff0c\u5982\u4eca\u53c8\u5403\u4e86\u540c\u6837\u7684\u4e8f\u3002 \u3000\u3000\u5706\u6d77\u98de\u9a6c\u5954\u9003\uff0c\u6c14\u5f97\u5927\u53eb\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6709\u80c6\u7684\u4f60\u6562\u8ffd\u6765\u4e48\u3002\u201d\u4ed6\u662f\u60f3\u628a\u91d1\u9010\u6d41\u5f15\u53bb\u89c1 \u8463\u5341\u4e09\u5a18\u548c\u9752\u7b26\uff0c\u5374\u4e0d\u77e5\u4ed6\u7684\u8fd9\u4e24\u4e2a\u540c\u4f34\u4e5f\u662f\u521a\u521a\u5403\u8fc7\u91d1\u9010\u6d41\u7684\u4e8f\u3002 \u3000\u3000\u5168\u9010\u6d41\u7b11\u9053\uff1a\u201c\u8463\u5341\u4e09\u5a18\u6b63\u7b49\u7740\u4f60\u8fd9\u4f4d\u5927\u548c\u5c1a\u7ed9\u5979\u5012\u6d17\u811a\u6c34\u5462\uff0c\u6211\u53ef\u6ca1\u6709\u8fd9\u4e2a\u5174\u8da3\u5949 \u966a\u3002\u201d \u3000\u3000\u7126\u78ca\u662f\u4e0d\u60f3\u62db\u60f9\u91d1\u9010\u6d41\u7684\uff0c\u89c1\u91d1\u9010\u6d41\u6ca1\u6709\u8ffd\u6765\uff0c\u653e\u4e0b\u4e86\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5012\u662f\u98ce\u6d41\u5f97 \u7d27\uff01\u201d \u3000\u3000\u5706\u6d77\u53c8\u7fa1\u53c8\u5992\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u81ed\u5c0f\u5b50\u4e5f\u592a\u53ef\u6076\u4e86\uff0c\u624d\u9a97\u4e86\u54b1\u4eec\u5e2e\u4e3b\u7684\u59b9\u59b9\uff0c\u5982 \u4eca\u53c8\u94a9\u4e0a\u4e86\u8fd9\u4e2a\u96cc\u513f\u3002\u8981\u662f\u7ed9\u5e2e\u4e3b\u77e5\u9053\uff0c\u4e0d\u6c14\u6b7b\u4ed6\u624d\u602a\uff01\u4f60\u60f3\u60f3\u770b\uff1a\u201c\u8d54\u4e86\u592b\u4eba\u53c8\u6298\u5175\uff0c\u5df2 \u7ecf\u662f\u5012\u6963\u900f\u9876\u4e86\u3002\u54b1\u4eec\u7684\u5e2e\u4e3b\u7ed9\u8fd9\u81ed\u5c0f\u5b50\u76d7\u4e86\u7384\u94c1\uff0c\u9a97\u4e86\u59b9\u5b50\uff0c\u8fd9\u81ed\u5c0f\u5b50\u8fd8\u4e0d\u80af\u8981\u4ed6\u7684\u59b9\u5b50 \u505a\u592b\u4eba\u5462\uff01\u201d \u3000\u3000\u7126\u78ca\u7b11\u9053\uff1a\u201c\u6211\u53ea\u6015\u5e2e\u4e3b\u4e0d\u77e5\u9053\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u77e5\u9053\u4e86\u90a3\u5012\u597d\u4e86\u3002\u4f9d\u6211\u770b\u6765\uff0c\u5e2e\u4e3b\u56fa\u7136\u662f\u8981 \u751f\u6c14\u7684\uff0c\u4f46\u4e5f\u4e0d\u89c1\u5f97\u5c31\u4e0d\u4f1a\u6697\u6697\u6b22\u559c\u5427\uff1f\u201d \u3000\u3000\u5706\u6d77\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\uff01\u5bf9\uff01\u54b1\u4eec\u5411\u5e2e\u4e3b\u544a\u53d1\u5012\u4e5f\u662f\u529f\u52b3\u4e00\u4ef6\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u542c\u4e86\u4ed6\u4eec\u7684\u6c61\u8a00\u79fd\u8bed\uff0c\u6c14\u5f97\u67f3\u7709\u5012\u575a\uff0c\u53c8\u7f9e\u53c8\u607c\u3002\u4f46\u4ea6\u662f\u65e0\u53ef\u5948\u4f55\uff0c\u5706\u6d77\u548c\u7126\u78ca \u6b64\u65f6\u5df2\u7ecf\u662f\u8dd1\u5f97\u8fde\u80cc\u5f71\u4e5f\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u628a\u90a3\u5339\u9a6c\u7275\u4e0b\u5c71\u5761\uff0c\u4ea4\u7ed9\u4e86\u5c01\u5999\u5ae6\uff0c\u8bf4\u9053\uff1a\u201c\u72d7\u5634\u91cc\u4e0d\u957f\u8c61\u7259\uff0c\u8fd9\u4e24\u4e2a\u72d7\u4e1c\u897f\u4e71 \u56bc\u820c\u5934\uff0c\u7406\u5b83\u4f5c\u751a\uff1f\u201d\u91d1\u9010\u6d41\u662f\u4e2a\u6d12\u8131\u7684\u4eba\uff0c\u8fd9\u4e24\u4e2a\u4eba\u7684\u80e1\u8a00\u4e71\u8bed\u4ed6\u662f\u4e0d\u4f1a\u653e\u5728\u5fc3\u4e0a\u7684\u3002\u4e0d \u8fc7\uff0c\u4ed6\u4e5f\u6709\u70b9\u62c5\u5fe7\uff0c\u542c\u8fd9\u4e24\u4e2a\u4eba\u7684\u53e3\u6c14\uff0c\u5206\u660e\u662f\u8981\u6311\u62e8\u662f\u975e\uff0c\u79bb\u95f4\u4ed6\u548c\u53f2\u7ea2\u82f1\u7684\u4e86\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u4e0a\u54ea\u513f\uff1f\u201d\u539f\u6765\u5979\u53d7\u4e86\u8fd9\u4e24\u4e2a\u4eba\u7684\u5632\u7b11\uff0c\u5012\u662f\u72af\u4e86\u4e00\u70b9\u5fc3\u4e8b\uff0c \u82e5\u662f\u548c\u91d1\u9010\u6d41\u540c\u884c\uff0c\u6050\u6015\u4f1a\u62db\u60f9\u66f4\u591a\u7684\u95f2\u8bdd\uff1b\u82e5\u4e0d\u548c\u4ed6\u540c\u884c\uff0c\u53c8\u6015\u518d\u78b0\u4e0a\u4e0d\u6d4b\u7684\u707e\u7978\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u60e6\u8bb0\u7740\u79e6\u5143\u6d69\u5427\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u8bf4\u7b11\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e00\u672c\u6b63\u7ecf\u5730\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u3002\u6211\u867d\u7136\u559c\u6b22\u5f00\u73a9\u7b11\uff0c\u8fd9\u6b21\u53ef\u4e0d\u662f\u548c\u4f60\u8bf4\u7b11\u7684\u3002\u4f60\u975e \u7ed9\u6211\u9762\u5b50\u4e0d\u884c\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u83ab\u540d\u5176\u5999\uff0c\u4e0d\u89c9\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u9762\u5b50\uff1f\u6069\u516c\uff0c\u4f60\u6551\u4e86\u6211\u7684\u6027\u547d\uff0c\u6709\u8bdd\u5429\u5490\u5c31\u662f\uff0c\u6709 \u8bdd\u8fd8\u7528\u5f97\u8fd9\u6837\u5ba2\u6c14\u5417\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u8fd9\u624d\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6709\u4f60\u8fd9\u53e5\u8bf4\u8bdd\uff0c\u8fd9\u4ef6\u4e8b\u4f60\u5c31\u4e00\u5b9a\u8981\u542c\u6211\u7684\u4e86\u3002\u8fd9\u4ef6\u4e8b\u6211\u867d \u7136\u672a\u5148\u5f81\u6c42\u4f60\u7684\u540c\u610f\uff0c\u4f46\u6211\u60f3\u4f60\u4e5f\u4e00\u5b9a\u613f\u610f\u7684\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u5ae6\u60ca\u7591\u4e0d\u5b9a\uff0c\u95ee\u9053\uff1a\u201c\u5230\u5e95\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u7ed9\u4f60\u505a\u4e86\u5a92\u4e86\uff0c\u4f60\u7239\u7239\u5df2\u7136\u7b54\u5141\uff0c\u53ea\u80fd\u628a\u4f60\u8bb8\u7ed9\u79e6\u5143\u6d69\uff0c\u51b3\u4e0d\u4f1a\u903c\u4f60\u53e6 \u5a5a\u4ed6\u4eba\u4e86\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u6ee1\u9762\u901a\u7ea2\uff0c\u91d1\u9010\u6d41\u56b7\u9053\uff1a\u201c\u5582\uff0c\u4f60\u5230\u5e95\u662f\u613f\u610f\u4e0d\u613f\u610f\u5440\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u5728\u54ea\u513f\u9047\u4e0a\u6211\u7684\u7239\u7239\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u53cd\u5bf9\uff0c\u90a3\u5c31\u662f\u540c\u610f\u4e86\u3002\u4f60\u7684\u7239\u7239\u6b63\u4ece\u8fd9\u4e00\u6761\u8def\u6765\uff0c\u4f60\u7684\u9a6c\u5feb\uff0c \u8dd1\u56de\u53bb\u7528\u4e0d\u5230\u534a\u5929\u5de5\u592b\uff0c\u4e00\u5b9a\u53ef\u4ee5\u5728\u8def\u4e0a\u9047\u89c1\u4ed6\u3002\u201d\u8fd9\u624d\u628a\u6628\u65e5\u4e0e\u5979\u7239\u7239\u76f8\u9047\u786c\u505a\u6210\u4e86\u5a92\u7684 \u7ecf\u8fc7\u544a\u8bc9\u4e86\u5979\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u8138\u6cdb\u6843\u82b1\uff0c\u53c8\u7f9e\u53c8\u559c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u7239\u7239\u7ecf\u4ed6\u4e00\u5413\uff0c\u82e5\u7136\u4ece\u6b64\u6539\u90aa\u5f52\u6b63\uff0c\u90a3\u5012\u662f \u4e00\u4ef6\u597d\u4e8b\u3002\u4f46\u6211\u7239\u7239\u867d\u7136\u7b54\u5141\u4e86\u8fd9\u95e8\u4eb2\u4e8b\uff0c\u79e6\u5143\u6d69\u5374\u662f\u540d\u95e8\u6b63\u6d3e\u7684\u5f1f\u5b50\uff0c\u600e\u77e5\u4ed6\u7684\u5e08\u95e8\u957f\u8f88 \u70b9\u4e0d\u70b9\u5934\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u597d\u4f3c\u77e5\u9053\u5979\u7684\u5fc3\u601d\uff0c\u7b11\u9053\uff1a\u201c\u79e6\u5143\u6d69\u7684\u5e08\u7236\u662f\u6211\u7684\u665a\u8f88\uff0c\u6211\u505a\u7684\u5927\u5a92\uff0c\u4ed6\u7684\u5e08\u7236 \u4e0d\u70b9\u5934\u4e16\u5f97\u70b9\u5934\uff0c\u4f60\u653e\u5fc3\u5427\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9762\u7ea2\u8fc7\u8033\uff0c\u8bf4\u9053\uff1a\u201c\u6069\u516c\u53d6\u7b11\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9762\u5b54\u4e00\u677f\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff0c\u4f60\u600e\u4e48\u79f0\u6211\u6069\u516c\uff1f\u5143\u6d69\u7684\u5e08\u7236\u867d\u7136\u662f\u6211\u665a\u8f88\uff0c \u4f46\u6211\u548c\u5143\u6d69\u5374\u662f\u5e73\u8f88\u8bba\u4ea4\u7684\uff0c\u4ec0\u4e48\u2018\u6069\u516c\u2019\u5440\u2018\u5927\u4fa0\u2019\u5440\uff0c\u8fd9\u4e48\u4e00\u53eb\uff0c\u5c82\u4e0d\u662f\u53cd\u800c\u663e\u5f97\u751f\u758f \u4e86\u3002\u6211\u7ed9\u4f60\u505a\u8fd9\u4e2a\u5a92\uff0c\u4f60\u5df2\u7ecf\u540c\u610f\u4e86\uff0c\u90a3\u4e48\u4f60\u5c31\u662f\u6211\u7684\u5ac2\u5b50\u4e86\uff0c\u4f60\u5e94\u8be5\u53eb\u6211\u5927\u54e5\u624d\u5bf9\u3002\u201d\u8bf4 \u7f62\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u8de8\u4e0a\u9a6c\u80cc\uff0c\u4f4e\u4e86\u5934\u4e0d\u77e5\u8bf4\u4e9b\u4ec0\u4e48\u8bdd\u597d\u3002\u91d1\u9010\u6d41\u8bf4\u9053\uff1a\u201c\u4f60\u7239\u7239\u548c\u90a3\u4e9b\u4eba\u662f\u76f8\u8bc6 \u7684\uff0c\u4f60\u89c1\u7740\u4e86\u7239\u7239\uff0c\u5c31\u4e0d\u7528\u5bb3\u6015\u90a3\u4e9b\u4eba\u548c\u4f60\u4e3a\u96be\u4e86\u3002\u4e0d\u8fc7\uff0c\u6211\u5374\u60f3\u4f60\u529d\u529d\u4f60\u7684\u7239\u7239\uff0c\u8fd8\u662f\u56de \u5f82\u5f95\u5c71\u7684\u597d\uff0c\u4e0d\u8981\u518d\u8fdb\u4eac\u5df4\u7ed3\u6743\u8d35\u4e86\u3002\u201d\u91d1\u9010\u6d41\u521a\u521a\u5f00\u8fc7\u73a9\u7b11\uff0c\u4f46\u73b0\u5728\u8bf4\u7684\u5374\u53c8\u662f\u5341\u5206\u6b63\u7ecf \u7684\u8bf4\u8bdd\uff0c\u628a\u5c01\u5999\u5ae6\u5f04\u5f97\u557c\u7b11\u7686\u975e\uff0c\u5fc3\u91cc\u53c8\u4e0d\u80fd\u4e0d\u611f\u6fc0\u4ed6\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u5ae6\u88e3\u887d\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u54e5\uff0c\u4f60\u5bf9\u6211\u4eec\u7236\u5973\u7684\u597d\u610f\uff0c\u6211\u4e00\u751f\u611f\u6fc0\u4e0d\u5c3d\uff0c\u6211\u4e00\u5b9a \u529d\u5bb6\u7236\u542c\u4ece\u5927\u54e5\u7684\u8bdd\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u6765\u5ba2\u6c14\u4e86\u3002\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u5c31\u5404\u5954\u524d\u7a0b\u5427\u3002\u5f85\u4f60\u548c\u5143\u6d69\u6210\u4eb2\u4e4b\u65f6\uff0c\u6211 \u518d\u6765\u559d\u4f60\u7684\u559c\u9152\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u505a\u4e86\u8fd9\u4ef6\u5f97\u610f\u7684\u4e8b\u60c5\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u4e0a\u9a6c\u800c\u53bb\u3002 \u3000\u3000\u4e00\u8def\u4e0a\u91d1\u9010\u6d41\u5904\u5904\u7559\u5fc3\uff0c\u6253\u542c\u53f2\u7ea2\u82f1\u7684\u6d88\u606f\u3002\u53ef\u662f\u76f4\u5230\u4ed6\u62b5\u8fbe\u90fd\u95e8\u4e4b\u65e5\uff0c\u4ecd\u7136\u627e\u4e0d\u5230\u4e00 \u70b9\u7ebf\u7d22\u3002\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u62a2\u52ab\u90a3\u51e0\u4e2a\u5e2e\u4f1a\u9001\u7ed9\u8428\u798f\u9f0e\u7684\u793c\u7269\u7684\u5973\u5b50\u9664\u4e86\u7ea2\u82f1\u8fd8\u6709\u8c01\uff1f\u5979 \u65e2\u7136\u62a2\u4e86\u90a3\u4e9b\u4eba\u7684\u793c\u7269\uff0c\u60f3\u6765\u4e5f\u5fc5\u5b9a\u662f\u4f1a\u6765\u8d74\u8fd9\u8d9f\u70ed\u95f9\u7684\u4e86\uff0c\u6211\u5230\u4e86\u4eac\u4e2d\uff0c\u518d\u60f3\u6cd5\u5bfb\u8bbf\u5979\u5c31 \u662f\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7684\u9a6c\u5feb\uff0c\u63d0\u65e9\u5230\u4e86\u5317\u4eac\uff0c\u8ddd\u79bb\u8428\u798f\u9f0e\u7684\u5bff\u671f\u8fd8\u6709\u56db\u65e5\u4e4b\u591a\u3002\u91d1\u9010\u6d41\u8bb0\u7740\u5e08\u5144\u201c\u80c6 \u5927\u5fc3\u7ec6\u201d\u7684\u6559\u8bad\uff0c\u60f3\u9053\uff1a\u201c\u6211\u8fd9\u662f\u7b2c\u4e00\u6b21\u8fdb\u4eac\uff0c\u4eac\u4e2d\u9ad8\u624b\u5982\u4e91\uff0c\u6211\u867d\u7136\u4e0d\u6015\uff0c\u4e5f\u8fd8\u662f\u8c28\u614e\u4e00 \u70b9\u7684\u597d\u3002\u516d\u5408\u5e2e\u8033\u76ee\u751a\u591a\uff0c\u548c\u6c5f\u6e56\u5404\u5927\u5e2e\u4f1a\u53c8\u6709\u8054\u7edc\uff0c\u6211\u9a91\u7740\u4ed6\u4eec\u5e2e\u4e3b\u7684\u9a6c\uff0c\u82e5\u662f\u6295\u5bbf\u5ba2 \u5e97\uff0c\u53ea\u6015\u4f1a\u7ed9\u4eba\u8ba4\u5f97\uff0c\u8fd8\u662f\u627e\u4e00\u4e2a\u4e0e\u5e08\u95e8\u6709\u6e0a\u6e90\u7684\u524d\u8f88\u4f5c\u5c45\u505c\u4e3b\u4eba\u5427\u3002\u201d\u53ef\u662f\u4ed6\u60f3\u6765\u60f3\u53bb\uff0c \u5374\u60f3\u4e0d\u5230\u6709\u5408\u9002\u7684\u5c45\u505c\u4e3b\u4eba\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7684\u7236\u6267\u90fd\u662f\u5404\u6d3e\u638c\u95e8\uff0c\u8981\u4e0d\u7136\u5c31\u662f\u6297\u6e05\u7684\u524d\u8f88\u82f1\u96c4\uff0c\u8fd9\u4e9b\u4eba\u6b7b\u7684\u6b7b\u4e86\uff0c\u9690\u7684\u9690 \u4e86\uff0c\u8fd8\u6d3b\u7740\u7684\u4e5f\u4e0d\u4f1a\u4f4f\u5728\u4eac\u90fd\u3002 \u3000\u3000\u6700\u540e\u91d1\u9010\u6d41\u624d\u60f3\u8d77\u4e86\u4e00\u4e2a\u4eba\u6765\uff0c\u8fd9\u4e2a\u4eba\u548c\u4ed6\u5e76\u65e0\u5e08\u95e8\u6e0a\u6e90\uff0c\u4e0d\u8fc7\u4e5f\u6709\u70b9\u95f4\u63a5\u7684\u5173\u7cfb\u3002\u8fd9 \u4eba\u662f\u9707\u8fdc\u9556\u5c40\u65e9\u5df2\u9000\u4f11\u4e86\u7684\u8001\u9556\u5934\u6234\u5747\u3002 \u3000\u3000\u6234\u5747\u662f\u91d1\u9010\u6d41\u5e08\u4f84\u5b87\u6587\u96c4\u7684\u7236\u6267\uff0c\u5b87\u6587\u96c4\u7684\u7236\u4eb2\u5b87\u6587\u6717\u548c\u6234\u5747\u5728\u9707\u8fdc\u9556\u5c40\u540c\u4e8b\u591a\u5e74\uff0c\u5b87 \u6587\u96c4\u5c31\u662f\u5728\u9556\u5c40\u957f\u5927\u7684\uff0c\u6234\u5747\u5c06\u4ed6\u5f53\u4f5c\u5b50\u4f84\u822c\u770b\u5f85\u3002\u5341\u4e8c\u5e74\u524d\uff0c\u5b87\u6587\u6717\u8d70\u9556\u8fbd\u4e1c\u88ab\u5927\u76d7\u5c09\u8fdf \u8fdf\u6240\u52ab\uff0c\u5bb6\u4ea7\u5168\u90e8\u53d8\u5356\u5c1a\u4e0d\u8db3\u8d54\u507f\uff0c\u90c1\u90c1\u800c\u6ca1\u3002\u9707\u8fdc\u9556\u5c40\u4e5f\u56e0\u6b64\u5012\u95ed\u3002\u5b87\u6587\u96c4\u591a\u4e8f\u6234\u5747\u7167 \u987e\uff0c\u624d\u5e78\u514d\u51bb\u9981\u3002\u540e\u6765\u5b87\u6587\u96c4\u6295\u5165\u6c5f\u6d77\u5929\u95e8\u4e0b\uff0c\u4e0e\u5c09\u8fdf\u70af\u5316\u89e3\u4e86\u8fd9\u6bb5\u51a4\u4ec7\uff0c\u5c09\u8fdf\u70af\u8d54\u507f\u9556\u5c40 \u9884\u5931\uff0c\u9707\u8fdc\u9556\u5c40\u624d\u5f97\u91cd\u5f00\u3002\u4f46\u5c09\u8fdf\u70af\u90a3\u6b21\u4e5f\u56e0\u5165\u4eac\u529e\u7406\u6b64\u4e8b\uff0c\u88ab\u6c5f\u6d77\u5929\u7684\u53db\u5f92\u53f6\u51cc\u98ce\u6240\u5356\uff0c \u9014\u4e2d\u88ab\u6355\uff0c\u6253\u5165\u5927\u7262\u3002\u540e\u6765\u60f9\u51fa\u4e86\u6781\u5927\u98ce\u6ce2\uff0c\u6c5f\u6d77\u5929\u3001\u5b87\u6587\u96c4\u5148\u884c\u5165\u4eac\uff0c\u5927\u95f9\u5929\u7262\uff0c\u624d\u628a\u5c09 \u8fdf\u70af\u6551\u4e86\u51fa\u6765\u3002\u90a3\u6b21\u52ab\u7262\uff0c\u5f97\u6234\u5747\u7684\u5e2e\u5fd9\u4e5f\u5f88\u662f\u4e0d\u5c11\u3002\uff08\u4e8b\u8be6\u300a\u98ce\u96f7\u9707\u4e5d\u5dde\u300b\uff09 \u3000\u3000\u5168\u9010\u6d41\u60f3\u8d77\u6b64\u4eba\uff0c\u5fc3\u9053\uff1a\u201c\u5e08\u5144\u66fe\u8bf4\u6b64\u8001\u53e4\u9053\u70ed\u80a0\uff0c\u4e0d\u6127\u4e3a\u524d\u8f88\u6977\u6a21\u3002\u5b87\u6587\u96c4\u4e5f\u66fe\u6258\u6211 \u95ee\u5019\u4ed6\u3002\u6211\u4f55\u4e0d\u5c31\u53bb\u53e8\u6270\u4ed6\uff0c\u60f3\u6765\u4ed6\u4e0d\u4f1a\u5acc\u6211\u9ebb\u70e6\u4ed6\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6709\u5b87\u6587\u96c4\u7ed9\u4ed6\u7684\u5730\u5740\uff0c\u4e8e\u662f\u7acb\u5373\u5907\u529e\u62dc\u8d34\uff0c\u53bb\u627e\u6234\u5747\uff0c\u5230\u4e86\u6234\u5bb6\uff0c\u53ea\u6709\u5927\u95e8\u7d27 \u95ed\uff0c\u91d1\u9010\u6d41\u6572\u4e86\u51e0\u6b21\u95e8\uff0c\u624d\u89c1\u4e00\u4e2a\u4e2d\u5e74\u6c49\u5b50\u51fa\u6765\uff0c\u8fd9\u4eba\u770b\u4e86\u4e00\u770b\u91d1\u9010\u6d41\u548c\u4ed6\u7684\u90a3\u5339\u9a8f\u9a6c\uff0c\u8138 \u4e0a\u9732\u51fa\u8be7\u5f02\u4e4b\u8272\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u627e\u8c01\u5440\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9012\u4e0a\u62dc\u5e16\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u5b87\u6587\u96c4\u7684\u5e08\u53d4\uff0c\u8bf7\u95ee\u6234\u8001\u524d\u8f88\u5728\u4e0d\u5728\u5bb6\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7684\u5e74\u7eaa\u6bd4\u5b87\u6587\u96c4\u5c0f\uff0c\u90a3\u4eba\u542c\u4e86\u66f4\u662f\u5403\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5b87\u6587\u96c4\u54ea\u91cc\u6765\u7684\u8fd9\u4e2a\u5e08 \u53d4\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u76f8\u4fe1\u6211\u662f\u5b87\u6587\u96c4\u7684\u5e08\u53d4\u5427\uff1f\u8bf7\u8ba9\u6211\u8fdb\u53bb\u5411\u6234\u8001\u524d\u8f88\u9762\u9648\u4e00\u5207\uff0c\u4f60\u5c31 \u660e\u767d\u4e86\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u6234\u8001\u524d\u8f88\u53e4\u9053\u70ed\u80a0\uff0c\u6700\u4e3a\u559c\u5ba2\u3002\u600e\u7684\u4ed6\u7684\u5bb6\u4eba\u5bf9\u8fdc\u9053\u800c\u6765\u7684\u5ba2\u4eba\u5374\u8fd9\u4e48 \u51b7\u6de1\uff0c\u63a5\u4e86\u62dc\u5e16\uff0c\u4e5f\u4e0d\u8bf7\u6211\u8fdb\u53bb\uff1f\u5728\u95e8\u53e3\u7ad9\u7740\uff0c\u600e\u65b9\u4fbf\u8bf4\u8bdd\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u90a3\u4eba\u5ffd\u5730\u5c06\u62dc\u5323\u4ea4\u56de\u7ed9\u91d1\u9010\u6d41\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5bb6\u7236\u65e9\u5df2\u53bb\u4e16\uff0c\u9601\u4e0b\u8fdc\u9053\u6765 \u8bbf\uff0c\u60c5\u8c0a\u53ef\u611f\uff0c\u5728\u4e0b\u8c28\u4ee3\u5148\u7236\u62dc\u8c22\u3002\u62dc\u5e16\u6211\u53ef\u662f\u4e0d\u6562\u6536\u4e86\u3002\u201d\u8a00\u7f62\u4e00\u63d6\uff0c\u7adf\u662f\u6709\u9001\u5ba2\u4e4b\u4e8b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5927\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u6234\u8001\u524d\u8f88\u51e0\u65f6\u6b7b\u7684\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u5bb6\u7236\u901d\u4e16\uff0c\u5df2\u662f\u4e00\u6708\u6709\u591a\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8bf4\u9053\uff1a\u201c\u6211\u53d7\u4e86\u6c5f\u5e08\u5144\u4e4b\u6258\uff0c\u7279\u6765\u62dc\u5019\u4ee4\u5c0a\uff0c\u5b87\u6587\u5e08\u4f84\u4e5f\u66fe\u518d\u4e09\u8bf7\u6211\u4ee3\u4e3a\u5411\u4ee4\u5c0a \u81f4\u656c\u3002\u4e0d\u6599\u4ed6\u8001\u4eba\u5bb6\u5df1\u7136\u4ed9\u901d\u3002\u8bf7\u5bb9\u6211\u5230\u7075\u524d\u884c\u4e00\u4e2a\u793c\uff0c\u4ee3\u5e08\u5144\u5e08\u4f84\u7565\u5c3d\u5fc3\u4e8b\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6253\u51fa\u6c5f\u6d77\u5929\u7684\u65d7\u53f7\uff0c\u90a3\u6c49\u5b50\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u662f\u771f\u662f\u5047\uff0c\u4ed6\u5982\u4eca\u662f\u4ee3\u6c5f\u5927\u4fa0\u884c \u793c\uff0c\u8fd9\u5374\u662f\u96be\u4ee5\u63a8\u8f9e\u7684\u4e86\u3002\u201d\u4e8e\u662f\u53ea\u597d\u8bf7\u91d1\u9010\u6d41\u8fdb\u53bb\uff0c\u6253\u5b9a\u4e86\u4e3b\u610f\uff1a\u201c\u5b81\u53ef\u5192\u4e00\u5192\u7ed9\u4ed6\u7aa5\u63a2 \u865a\u5b9e\u7684\u5371\u9669\uff0c\u5f85\u4ed6\u8d70\u540e\uff0c\u518d\u8bbe\u6cd5\u6253\u542c\u4ed6\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8d70\u8fdb\u7075\u5802\uff0c\u53ea\u89c1\u679c\u7136\u662f\u6709\u4e00\u4e2a\u65b0\u6f06\u7684\u7075\u724c\uff0c\u5927\u4e66\u201c\u6234\u516c\u5b9c\u4e4b\u724c\u4f4d\u201d\u3002\u201c\u5b9c\u4e4b\u201d\u662f \u6234\u5747\u7684\u5b57\uff0c\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u8fd9\u53ef\u771f\u662f\u6765\u5f97\u592a\u4e0d\u5de7\u4e86\uff0c\u672c\u4ee5\u4e3a\u53ef\u4ee5\u627e\u5f97\u4e00\u4e2a\u5c45\u505c\u4e3b\u4eba\u7684\uff0c\u8c01\u77e5 \u5982\u4eca\u5374\u662f\u6765\u62dc\u4ed6\u7684\u724c\u4f4d\u3002\u201d \u3000\u3000\u8fd9\u6c49\u5b50\u7ad9\u5728\u4e00\u65c1\u7b54\u672d\uff0c\u91d1\u9010\u6d41\u884c\u8fc7\u793c\u540e\uff0c\u4ed6\u4ecd\u7136\u5728\u4e00\u65c1\u7ad9\u7acb\uff0c\u4e0d\u8fc7\u6539\u4e86\u4e2a\u65b9\u5411\uff0c\u8138\u671d\u7740 \u95e8\uff0c\u6446\u51fa\u6765\u7684\u59ff\u6001\uff0c\u5f53\u7136\u662f\u8981\u9001\u5ba2\u7684\u610f\u601d\u4e86\u3002\u91d1\u9010\u6d41\u5374\u4e0d\u7406\u4ed6\uff0c\u5927\u9a6c\u91d1\u5200\u7684\u4e00\u5c41\u80a1\u5c31\u5750\u5728\u6905 \u4e0a\u3002 \u3000\u3000\u8fd9\u6c49\u5b50\u6ca1\u6cd5\uff0c\u53ea\u597d\u5750\u4e0b\u6765\u548c\u91d1\u9010\u6d41\u8bf4\u8bdd\u3002\u4e92\u901a\u59d3\u540d\uff0c\u91d1\u9010\u6d41\u8fd9\u624d\u77e5\u9053\u4ed6\u540d\u53eb\u6234\u8c1f\uff0c\u662f\u6234 \u5747\u7684\u957f\u5b50\uff0c\u4ed6\u8fd8\u6709\u4e00\u4e2a\u5f1f\u5f1f\u540d\u53eb\u6234\u915a\uff0c\u4e0d\u5728\u5bb6\u4e2d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e0d\u5f85\u4ed6\u76d8\u95ee\uff0c\u81ea\u52a8\u7684\u544a\u8bc9\u4e86\u4ed6\u81ea\u5df1\u7684\u6765\u5386\u3002\u6234\u8c1f\u542c\u8bf4\u4ed6\u662f\u91d1\u4e16\u9057\u7684\u513f\u5b50\uff0c\u5fc3\u91cc\u60ca \u7591\u4e0d\u5b9a\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u9041\u8ff9\u6d77\u5916\uff0c\u4e8c\u5341\u5e74\u6765\u97f3\u51b5\u54cd\u7edd\uff0c\u7a76\u7adf\u6709\u6ca1\u6709\u513f\u5b50\uff0c\u4e5f\u65e0\u4eba\u77e5 \u9053\u3002\u600e\u77e5\u6b64\u4eba\u662f\u4e0d\u662f\u5047\u5192\uff1f\u201d\u8981\u77e5\u5f53\u65f6\u4ea4\u901a\u963b\u585e\uff0c\u91d1\u9010\u6d41\u4e0e\u6c5f\u6d77\u5929\u5e08\u5144\u5f1f\u76f8\u8ba4\u7684\u4e8b\uff0c\u6d88\u606f\u5c1a \u672a\u4f20\u5230\u5317\u4eac\u3002 \u3000\u3000\u6234\u8c1f\u53c8\u95ee\u4e86\u4e00\u4e9b\u6709\u5173\u6c5f\u6d77\u5929\u548c\u5b87\u6587\u96c4\u7684\u4e8b\u60c5\uff0c\u6709\u7684\u91d1\u9010\u6d41\u77e5\u9053\uff0c\u6709\u7684\u4ed6\u5374\u4e0d\u77e5\uff0c\u56e0\u4e3a\u4ed6 \u5728\u6c5f\u5bb6\u53ea\u662f\u4f4f\u4e86\u4e00\u5929\uff0c\u6240\u77e5\u7684\u5f53\u7136\u8fd8\u6ca1\u6709\u6234\u8c1f\u4e4b\u591a\u4e86\u3002 \u3000\u3000\u6234\u8c1f\u56fa\u7136\u611f\u5230\u6000\u7591\uff0c\u91d1\u9010\u6d41\u4e5f\u662f\u89c9\u5f97\u6709\u70b9\u53e4\u602a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u7684\u7236\u4eb2\u6b7b\u4e86\uff0c\u4e3a\u4f55\u4ed6\u5374 \u597d\u4f3c\u5e76\u4e0d\u600e\u6837\u60b2\u621a\uff1f\u6309\u7167\u5e38\u7406\uff0c\u5ba2\u4eba\u6765\u540a\u4e27\uff0c\u5b5d\u5b50\u603b\u5e94\u8be5\u8c08\u4e00\u8c08\u6b7b\u8005\u7684\u5f97\u75c5\u539f\u7531\u4ee5\u53ca\u6b7b\u8005\u7684 \u751f\u524d\u6b7b\u540e\u7b49\u7b49\uff0c\u4f46\u4ed6\u8fd9\u4e2a\u5b5d\u5b50\uff0c\u5374\u53ea\u987e\u76d8\u95ee\u5ba2\u4eba\uff0c\u867d\u8bf4\u6c5f\u6e56\u4e2d\u4eba\u4e0d\u62d8\u4fd7\u793c\uff0c\u5374\u4e5f\u672a\u514d\u592a\u4e0d\u4f9d \u793c\u4e86\u3002\u201d \u3000\u3000\u5728\u4ed6\u4eec\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u7075\u5802\u540e\u9762\u9690\u9690\u6709\u811a\u6b65\u7684\u58f0\u606f\uff0c\u58f0\u97f3\u6781\u5fae\uff0c\u91d1\u9010\u6d41\u4e00\u542c\u5c31\u77e5\u6b64\u4eba\u662f\u8f7b\u529f \u751a\u9ad8\uff0c\u4ed6\u8d70\u51fa\u6765\u662f\u4e0d\u613f\u610f\u7ed9\u5ba2\u4eba\u53d1\u89c9\u7684\u3002\u201c\u4f55\u4ee5\u4ed6\u8981\u5728\u6697\u4e2d\u7aa5\u63a2\u6211\u5462\uff1f\u201d\u91d1\u9010\u6d41\u5fc3\u60f3\u3002\u8d8a\u60f3 \u5c31\u6108\u89c9\u5f97\u4e8b\u6709\u8e4a\u8df7\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u89c1\u4e3b\u4eba\u6b8a\u65e0\u7559\u5ba2\u4e4b\u610f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6234\u5747\u53e4\u9053\u70ed\u80a0\uff0c\u4ed6\u7684\u513f\u5b50\u5374\u662f\u6beb\u65e0\u7236\u98ce\uff0c \u7f62\u3001\u7f62\uff0c\u4ed6\u65e2\u7136\u5982\u6b64\u6162\u5ba2\uff0c\u6211\u53c8\u4f55\u5fc5\u8d56\u5728\u8fd9\u513f\uff1f\u201d\u4e8e\u662f\u8d77\u7acb\u544a\u8f9e\u3002 \u3000\u3000\u6234\u8c1f\u8bf4\u9053\uff1a\u201c\u91d1\u5144\u8bf7\u518d\u5750\u4e00\u4f1a\u3002\u201d\u8fdb\u5165\u540e\u5802\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u548c\u4e00\u4e2a\u8001\u5bb6\u4eba\u51fa\u6765\uff0c\u8fd9\u8001\u5bb6\u4eba \u6367\u7740\u4e00\u4e2a\u6258\u76d8\uff0c\u76d8\u91cc\u6709\u4e00\u952d\u4e94\u5341\u4e24\u91cd\u7684\u5927\u5143\u5b9d\u3002\u201d \u3000\u3000\u6234\u8c1f\u8bf4\u9053\uff1a\u201c\u91d1\u5144\u8fdc\u9053\u800c\u6765\uff0c\u591a\u8499\u540a\u5501\uff0c\u5c24\u4ee5\u4e3a\u62a5\uff0c\u4e00\u70b9\u70b9\u7a0b\u4eea\uff0c\u8bf7\u91d1\u5144\u54c2\u7eb3\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u4e2d\u5927\u6012\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4ed6\u7adf\u7136\u5f53\u6211\u662f\u6253\u79cb\u98ce\u7684\u6765\u4e86\u3002\u201d\u5f53\u4e0b\u4e0d\u52a8\u58f0\u8272\uff0c\u628a\u90a3\u952d \u5143\u5b9d\u62ff\u4e86\u8d77\u6765\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u53ef\u867d\u662f\u7a77\u9178\uff0c\u5c1a\u4e0d\u81f3\u4e8e\u8981\u9760\u6253\u79cb\u98ce\u6765\u8fc7\u65e5\u5b50\uff0c\u5c0a\u5e9c\u539a \u8d50\uff0c\u4e0d\u6562\u9886\u53d7\u3002\u201d\u8bf4\u7f62\uff0c\u628a\u90a3\u952d\u5143\u5b9d\u653e\u56de\u6258\u76d8\uff0c\u5143\u5b9d\u672c\u6765\u662f\u4e24\u5934\u7fd8\u8d77\u7684\uff0c\u7ed9\u4ed6\u638c\u529b\u4e00\u6413\uff0c\u5df2 \u662f\u5377\u4e86\u8d77\u6765\uff0c\u53d8\u6210\u4e86\u68d2\u5f62\u7684\u957f\u6761\u3002 \u3000\u3000\u90a3\u8001\u5bb6\u4eba\u5374\u53c8\u628a\u5143\u5b9d\u62ff\u4e86\u8d77\u6765\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u91d1\u76f8\u516c\uff0c\u4f60\u751f\u6c14\u4e0d\u6253\u7d27\uff0c\u5374\u7d2f\u6211\u4e5f\u8981\u591a\u8d39 \u6c14\u529b\u4e86\u3002\u8fd9\u952d\u5143\u5b9d\u4e0d\u6062\u590d\u539f\u72b6\uff0c\u53ef\u662f\u4e0d\u4fbf\u4f7f\u7528\u7684\u5440\uff01\u201d\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u53cc\u624b\u628a\u90a3\u952d\u5143\u5b9d\u62c9\u5f00\uff0c\u6413 \u6413\u634f\u634f\uff0c\u7247\u523b\u95f4\u679c\u7136\u5c31\u6062\u590d\u4e86\u539f\u72b6\u3002\u628a\u5143\u5b9d\u5377\u6210\u957f\u6761\u8fd8\u6bd4\u8f83\u5bb9\u6613\uff0c\u60ed\u590d\u539f\u72b6\u66f4\u96be\uff0c\u663e\u7136\u8fd9 \u201c\u8001\u5bb6\u4eba\u201d\u7684\u5185\u529b\u662f\u53ea\u6709\u5728\u91d1\u9010\u6d41\u4e4b\u4e0a\uff0c\u51b3\u4e0d\u5728\u91d1\u9010\u6d41\u4e4b\u4e0b\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u672c\u6765\u662f\u8981\u8d70\u7684\uff0c\u7a81\u7136\u89c1\u201c\u8001\u5bb6\u4eba\u201d\u9732\u51fa\u8fd9\u624b\u529f\u592b\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u6b62\u6b65\uff0c \u62f1\u624b\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u8bf7\u6559\u8001\u82f1\u96c4\u9ad8\u59d3\u5927\u540d\u3002\u201d\u6b64\u65f6\u91d1\u9010\u6d41\u5f53\u7136\u77e5\u9053\u4ed6\u7edd\u4e0d\u4f1a\u662f\u4e00\u4e2a\u666e\u901a\u7684\u201c\u8001 \u5bb6\u4eba\u201d\u4e86\u3002 \u3000\u3000\u90a3\u201c\u8001\u5bb6\u4eba\u201d\u4e0d\u5148\u56de\u7b54\uff0c\u5374\u4f38\u51fa\u624b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5c11\u4fa0\uff0c\u8001\u673d\u4eca\u65e5\u5f97\u4e0e\u4f60\u76f8\u89c1\uff0c\u771f\u662f\u9ad8 \u5174\u975e\u5e38\u3002\u201d\u91d1\u9010\u6d41\u77e5\u9053\u4ed6\u662f\u8981\u6765\u8bd5\u81ea\u5df1\u7684\u529f\u592b\uff0c\u6697\u4e2d\u6212\u5907\uff0c\u548c\u4ed6\u76f8\u63e1\u3002 \u3000\u3000\u4e0d\u6599\u8fd9\u201c\u8001\u5bb6\u4eba\u201d\u5374\u53ea\u662f\u666e\u901a\u7684\u63e1\u624b\uff0c\u5e76\u6ca1\u6709\u4f7f\u4e0a\u5185\u529b\u3002\u4e0d\u8fc7\u5728\u63e1\u624b\u4e4b\u65f6\uff0c\u4ed6\u7684\u624b\u6307\u6478 \u4e86\u4e00\u6478\u91d1\u9010\u6d41\u6240\u6234\u7684\u90a3\u53ea\u7389\u6212\u3002\u8fd9\u53ea\u7389\u6212\u662f\u6d77\u5e95\u5bd2\u7389\u505a\u7684\uff0c\u91d1\u9010\u6d41\u4eca\u5929\u6765\u62dc\u5019\u6234\u5747\uff0c\u7279\u5730\u5c06 \u5b83\u6234\u4e0a\u3002 \u3000\u3000\u7389\u6212\u89e6\u6307\u751f\u5bd2\uff0c\u201c\u8001\u5bb6\u4eba\u201d\u628a\u624b\u7f29\u56de\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u91d1\u5144\u679c\u7136\u662f\u91d1\u5927\u4fa0\u7684\u516c\u5b50\uff0c\u8001\u673d\u5c31 \u662f\u6234\u5747\u3002\u201d\u539f\u6765\u6234\u5747\u867d\u7136\u7b97\u4e0d\u5f97\u662f\u91d1\u4e16\u9057\u7684\u670b\u53cb\uff0c\u4f46\u5f53\u5e74\u91d1\u4e16\u9057\u4e0e\u5b5f\u795e\u901a\u5728\u5d69\u5c71\u5c11\u6797\u5bfa\u5916\u9762 \u7684\u5343\u969c\u576a\u6bd4\u6b66\u4e4b\u65f6\uff0c\u4ed6\u4e5f\u662f\u5728\u573a\u7684\u4e00\u5343\u591a\u4e2a\u6b66\u6797\u4eba\u7269\u4e4b\u4e00\u3002\u4ed6\u8ba4\u5f97\u91d1\u4e16\u9057\uff0c\u91d1\u4e16\u9057\u4e0d\u8ba4\u5f97 \u4ed6\u3002\u91d1\u4e16\u9057\u6709\u4e54\u5317\u6e9f\u6240\u7559\u4e0b\u7684\u7389\u5f13\u7389\u7bad\uff0c\u4ed6\u4e5f\u90fd\u662f\u77e5\u9053\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5446\u4e86\u4e00\u5446\uff0c\u9661\u7136\u7701\u89c9\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u4ed6\u7684\u5bb6\u91cc\uff0c\u9664\u4e86\u6234\u5747\uff0c\u8fd8\u6709\u4f55\u4eba\u6709\u6b64\u529f \u529b\u3002\u201d \u3000\u3000\u6234\u8c1f\u8fde\u5fd9\u8fc7\u6765\u8d54\u7f6a\uff0c\u7b11\u9053\uff1a\u201c\u91d1\u5144\uff0c\u4f60\u83ab\u89c1\u602a\uff0c\u54b1\u4eec\u521d\u6b21\u76f8\u4f1a\uff0c\u6211\u4e0d\u80fd\u4e0d\u8bf7\u5bb6\u7236\u8bd5\u4f60\u4e00 \u8bd5\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u559c\u51fa\u671b\u5916\uff0c\u7b11\u9053\uff1a\u201c\u6211\u662f\u6765\u5f97\u5192\u6627\u4e86\u4e9b\uff0c\u4f46\u4e0d\u77e5\u540d\u524d\u8f88\u9f99\u9a6c\u7cbe\u795e\u5374\u4f55\u4ee5\u3001\u4f55 \u4ee5\u2026\u2026\u201d \u3000\u3000\u6234\u5747\u7b11\u9053\uff1a\u201c\u8001\u5f1f\u4e0d\u5fc5\u4e3a\u6211\u5fcc\u8bb3\u4e00\u4e2a\u6b7b\u5b57\uff0c\u6211\u4eca\u5e74\u5df2\u662f\u516d\u5341\u6709\u591a\uff0c\u867d\u7136\u8fd9\u51e0\u6839\u8001\u9aa8\u8fd8\u7b97 \u786c\u6717\uff0c\u4f46\u4e5f\u662f\u884c\u5c06\u5c31\u6728\u7684\u4e86\u3002\u4e0d\u8fc7\uff0c\u6211\u8fd9\u6b21\u5047\u6b7b\uff0c\u5374\u662f\u4e0d\u5f97\u5df2\u800c\u4e3a\u4e4b\uff0c\u8bf4\u6765\u8bdd\u957f\uff0c\u8bf7\u8001\u5f1f\u91cc \u9762\u5750\uff0c\u54b1\u4eec\u6162\u6162\u518d\u8c08\u3002\u201d \u3000\u3000\u5185\u5802\u65e9\u5df2\u6446\u4e86\u9152\u98df\uff0c\u6234\u5747\u8bf7\u91d1\u9042\u6d41\u5c31\u5ea7\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u662f\u5b87\u6587\u96c4\u7684\u5e08\u53d4\uff0c\u54b1\u4eec \u5c31\u662f\u81ea\u5df1\u4eba\u4e00\u822c\u4e86\u3002\u4f60\u4e0d\u8981\u53e6\u627e\u5ba2\u5e97\u4e86\uff0c\u5c31\u5728\u8fd9\u91cc\u4f4f\u4e0b\u5427\u3002\u6765\uff0c\u6765\uff0c\u6765\uff01\u5148\u559d\u4e09\u676f\uff0c\u66ff\u4f60\u6d17 \u5c18\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u9053\uff1a\u201c\u6b64\u8001\u679c\u7136\u662f\u8c6a\u723d\u559c\u5ba2\uff0c\u540d\u4e0d\u865a\u4f20\u3002\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u6b63\u662f\u8981\u6765 \u6253\u6270\u4f60\u7684\u3002\u201d\u8bf4\u7f62\uff0c\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u6234\u8c1f\u9053\uff1a\u201c\u542c\u8bf4\u5b87\u6587\u96c4\u5df2\u5b8c\u5a5a\u4e86\uff0c\u8001\u5f1f\u53ef\u6709\u53bb\u559d\u4ed6\u8fd9\u676f\u559c\u9152\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u90a3\u5929\u521a \u597d\u8d76\u4e0a\uff0c\u8fd8\u95f9\u4e86\u4e00\u70b9\u4e0d\u5927\u4e0d\u5c0f\u7684\u98ce\u6ce2\u5462\u3002\u201d\u8fd9\u624d\u628a\u5e08\u5144\u5f1f\u76f8\u8ba4\u7684\u7ecf\u8fc7\u544a\u8bc9\u6234\u5747\u7236\u5b50\u3002 \u3000\u3000\u6234\u8c1f\u53c8\u95ee\u9053\uff1a\u201c\u6797\u9053\u8f69\u548c\u674e\u5149\u590f\u8fd9\u4e24\u4e2a\u5b69\u5b50\u6211\u4e5f\u5f88\u662f\u60e6\u8bb0\uff0c\u60f3\u6765\u4ed6\u4eec\u90fd\u5df2\u957f\u5927\u6210\u4eba\u4e86\uff0c \u73b0\u5728\u8fd8\u5728\u6c5f\u5bb6\u4e48\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u662f\u5440\uff0c\u4ed6\u4eec\u7684\u5e74\u7eaa\u4e0d\u8fc7\u6bd4\u6211\u5c0f\u4e00\u4e8c\u5c81\uff0c\u90fd\u5df2\u957f\u5927\u6210\u4eba\u4e86\u3002 \u4e0d\u8fc7\uff0c\u73b0\u5728\u4ed6\u4eec\u5df2\u662f\u4e0d\u5728\u6c5f\u5bb6\uff0c\u800c\u662f\u8ddf\u4e86\u4e0a\u5b98\u6cf0\u5230\u897f\u661f\u53bb\u4e86\u3002\u8fd9\u4ef6\u4e8b\u4e5f\u4e3b\u662f\u53d1\u751f\u5728\u5b87\u6587\u96c4\u6210 \u4eb2\u7684\u90a3\u4e00\u5929\uff0c\u4e0a\u5b98\u6cf0\u5306\u5306\u8d76\u5230\uff0c\u5e26\u6765\u4e86\u7afa\u5c1a\u7236\u53d7\u4eba\u6697\u7b97\u7684\u6d88\u606f\uff0c\u897f\u661f\u5df2\u7ecf\u7ed9\u6e05\u5175\u593a\u53bb\u3002\u56e0\u6b64 \u6c5f\u5e08\u5144\u6d3e\u4e86\u53f6\u6155\u534e\u5e08\u4f84\u524d\u5f80\u897f\u661f\u76f8\u52a9\u4ed6\u4eec\uff0c\u6797\u3001\u674e\u4e24\u4f4d\u5e08\u4f84\u968f\u884c\u3002\u7b2c\u4e8c\u5929\u5c31\u8d70\u4e86\u3002\u4ed6\u4eec\u9664\u4e86 \u7ed9\u4ed6\u4eec\u7684\u5927\u5e08\u5144\u4f5c\u52a9\u624b\u4e4b\u5916\uff0c\u5230\u897f\u661f\u53bb\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a\u539f\u56e0\u2026\u2026\u201d \u3000\u3000\u6234\u5747\u62c8\u987b\u7b11\u9053\uff0c\u201c\u53ef\u4e5f\u662f\u53bb\u5b8c\u5a5a\u4e48\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6b63\u662f\u3002\u6797\u5e08\u4f84\u8ba2\u7684\u4eb2\u662f\u4e0a\u5b98\u6cf0\u7684\u5973 \u513f\uff0c\u674e\u5e08\u4f84\u8ba2\u7684\u4eb2\u662f\u7afa\u5c1a\u7236\u7684\u5973\u513f\u3002\u7afa\u5c1a\u7236\u4e0d\u80fd\u591f\u628a\u5973\u513f\u9001\u6765\uff0c\u4ed6\u4eec\u53ea\u597d\u524d\u5f80\u5c31\u4eb2\u4e86\u3002\u8fd9\u4e24 \u4e2a\u5b69\u5b50\u5f97\u4e00\u5230\u5e08\u5144\u7684\u5141\u8bb8\uff0c\u51c6\u4ed6\u4eec\u5f80\u897f\u661f\u5c31\u4eb2\u3002\u6b22\u559c\u5f97\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5a13\u5a13\u9053\u6765\uff0c\u6026\u7136\u4e00\u6d3e\u5c0f\u5e08\u53d4\u7684\u8eab\u4efd\u3002\u6234\u5747\u4e0d\u7981\u7b11\u9053\u3002\u201c\u91d1\u8001\u5f1f\uff0c\u4f60\u7684\u5e08\u4f84\u90fd\u6210\u4eb2 \u4e86\uff0c\u4f60\u81ea\u5df1\u5462\uff1f\u6709\u4e86\u5408\u9002\u7684\u4eba\u5bb6\u6ca1\u6709\uff1f\u201d\u6234\u8c1f\u7b11\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u662f\u60f3\u8981\u4e3a\u91d1\u5c11\u4fa0\u4f5c\u5a92\u4eba\u4e48\uff1f \u53ea\u6015\u91d1\u5c11\u4fa0\u662f\u7528\u4e0d\u7740\u4f60\u64cd\u8fd9\u4e2a\u5fc3\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u60f3\u8d77\u4e86\u53f2\u7ea2\u82f1\u6765\uff0c\u8138\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7239\u56db\u5341\u5c81\u624d\u5a36\u6211\u5988\uff0c\u6211\u624d\u4e0d\u8fc7\u4e8c\u5341\u5c81 \u5462\u3002\u54b1\u4eec\u8bf4\u6b63\u7ecf\u7684\uff0c\u5bf9\u5566\uff0c\u5b87\u6587\u5e08\u4f84\u6210\u5a5a\uff0c\u4f60\u8001\u60f3\u5df1\u6536\u5230\u4e86\u8bf7\u5e16\u5427\uff1f\u90a3\u5929\u4e0d\u89c1\u4f60\u8001\u5230\u6765\uff0c\u5927 \u5bb6\u90fd\u5f88\u5931\u671b\u3002\u201d \u3000\u3000\u6234\u5747\u7b11\u9053\uff1a\u201c\u6211\u90a3\u65f6\u6b63\u5728\u88c5\u6b7b\uff0c\u6b7b\u4eba\u600e\u80fd\u8d76\u53bb\u8d74\u5bb4\uff1f\u597d\uff0c\u4f60\u4e00\u5b9a\u662f\u6025\u7740\u8981\u77e5\u9053\u539f\u56e0\u7684\uff0c \u73b0\u5728\u6211\u5c31\u544a\u8bc9\u4f60\u5427\u3002\u201d \u3000\u3000\u6234\u5747\u559d\u4e86\u4e00\u676f\u9152\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u6b63\u662f\u548c\u4f60\u8fd9\u4e09\u4f4d\u5e08\u4f84\u6709\u70b9\u5173\u8054\u7684\u3002\u90a3\u5e74\u5b87\u6587\u96c4\u5230\u5317\u4eac \u6765\uff0c\u7ed9\u9707\u8fdc\u9556\u5c40\u7684\u4e00\u4e2a\u9556\u5934\u77e5\u9053\u6d88\u606f\uff0c\u8fd9\u9556\u5934\u540d\u53eb\u4e01\u56fa\uff0c\u662f\u548c\u5b98\u5e9c\u52fe\u7ed3\u7684\uff0c\u5b87\u6587\u96c4\u4e5f\u4e0d\u77e5 \u9053\u3002\u4e01\u56fa\u5c06\u4ed6\u8bf1\u5230\u9676\u7136\u4ead\uff0c\u4f0f\u5175\u5ffd\u51fa\uff0c\u5e78\u4e8f\u6211\u548c\u674e\u5149\u590f\u53ca\u65f6\u6765\u5230\uff0c\u662f\u6211\u4e00\u638c\u672a\u6bd9\u4e86\u4e01\u56fa\uff0c\u5927 \u5bb6\u624d\u9003\u4e86\u51fa\u6765\u3002\u53ef\u662f\u6797\u9053\u8f69\u5728\u5ba2\u5e97\u7ed9\u4ed6\u4eec\u7684\u4eba\u6349\u53bb\u4e86\u3002\u540e\u6765\u76f4\u5230\u4f60\u7684\u6c5f\u5e08\u5144\u5927\u52ab\u5929\u7262\uff0c\u6551\u5c09 \u8fdf\u70af\uff0c\u8fd9\u624d\u628a\u6797\u9053\u8f69\u4e5f\u6551\u4e86\u51fa\u6765\u3002\u201d\uff08\u4e8b\u8be6\u300a\u98ce\u96f7\u9707\u4e5d\u5dde\u300b\uff09\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u6211\u542c\u5f97\u5e08 \u5144\u8bf4\u8fc7\uff0c\u4e0d\u8fc7\u6ca1\u6709\u8001\u7684\u8f88\u8bf4\u5f97\u8fd9\u6837\u8be6\u7ec6\u3002\u201d \u3000\u3000\u6234\u5747\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4e01\u56fa\u6709\u4e2a\u513f\u5b50\u540d\u53eb\u4e01\u5f6d\uff0c\u4ed6\u7236\u4eb2\u7ed9\u6211\u51fb\u6bd9\u4e4b\u540e\uff0c\u4ed6\u6015\u6211\u52a0\u5bb3\u4e8e\u4ed6\uff0c\u8fde \u5fd9\u8dd1\u51fa\u5317\u4eac\u3002\u8fd9\u662f\u6211\u540e\u6765\u624d\u77e5\u9053\u7684\uff0c\u5176\u5b9e\u6211\u6740\u4e01\u56fa\uff0c\u90a3\u4e5f\u662f\u8feb\u4e8e\u65e0\u5948\uff0c\u6740\u4e00\u4e2a\u66fe\u7ecf\u548c\u81ea\u5df1\u5171 \u4e8b\u591a\u5e74\u7684\u4eba\uff0c\u867d\u7136\u8fd9\u4eba\u5df2\u662f\u574f\u5230\u65e0\u53ef\u6551\u836f\uff0c\u6bd5\u7adf\u4e5f\u8fd8\u662f\u6709\u70b9\u75db\u5fc3\u3002\u800c\u4e14\u7236\u4eb2\u662f\u7236\u4eb2\uff0c\u513f\u5b50\u662f \u513f\u5b50\uff0c\u6211\u53c8\u600e\u4f1a\u80e1\u4e71\u53bb\u52a0\u5bb3\u4e01\u56fa\u7684\u513f\u5b50\u5462\uff1f\u201d \u3000\u3000\u6234\u8c1f\u7b11\u9053\uff0c\u201c\u7239\u7239\uff0c\u4f60\u4e0d\u8981\u53ea\u662f\u53d1\u8bae\u8bba\u4e86\uff0c\u5feb\u628a\u4e8b\u5b9e\u544a\u8bc9\u91d1\u5c11\u4fa0\u5427\u3002\u201d \u3000\u3000\u6234\u5747\u8bf4\u9053\uff1a\u201c\u4e01\u5f6d\u9003\u51fa\u5317\u4eac\u4e4b\u540e\uff0c\u524d\u4e24\u5e74\u52a0\u5165\u4e86\u516d\u5408\u5e2e\uff0c\u5e2e\u4e3b\u540d\u53f2\u767d\u90fd\uff0c\u6b66\u529f\u542c\u8bf4\u6781\u4e3a \u4e86\u5f97\uff0c\u4e01\u5f6d\u5728\u4ed6\u624b\u4e0b\u505a\u4e86\u4e00\u4e2a\u4e0d\u5927\u4e0d\u5c0f\u7684\u5934\u76ee\uff0c\u4f46\u867d\u7136\u4e0d\u5f97\u91cd\u7528\uff0c\u5374\u4e5f\u7b97\u5f97\u662f\u53f2\u767d\u90fd\u7684\u4e00\u4e2a \u4eb2\u4fe1\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53f2\u767d\u90fd\u8fd9\u4e2a\u4eba\u6211\u77e5\u9053\u3002\u524d\u51e0\u5929\u6211\u624d\u548c\u4ed6\u4ea4\u8fc7\u624b\u3002\u4ed6\u7684\u6b66\u529f\u7684\u786e\u5f88\u5f3a\uff0c\u4e0d\u8fc7 \u4f9d\u6211\u770b\u6765\uff0c\u5374\u4e5f\u672a\u514d\u5c31\u80dc\u5f97\u8fc7\u6234\u8001\u524d\u8f88\u3002\u201d \u3000\u3000\u6234\u5747\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u91d1\u8001\u5f1f\u60f3\u5fc5\u4e5f\u77e5\u9053\u4e86\u5927\u5185\u603b\u7ba1\u8428\u798f\u9f0e\u8fc7\u51e0\u5929\u5c31\u8981\u505a\u516d\u5341\u5927\u5bff \u5427\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u662f\u3002\u53f2\u767d\u90fd\u8981\u6765\u7ed9\u8428\u798f\u9f0e\u8d3a\u5bff\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u6234\u5747\u8bf4\u9053\uff1a\u201c\u53f2\u767d\u90fd\u8fd9\u6b21\u5165\u4eac\uff0c\u516d\u5408\u5e2e\u4e2d\u7684\u91cd\u8981\u4eba\u7269\u90fd\u4f1a\u8ddf\u4ed6\u6765\u7684\u3002\u8fd9\u4e01\u5f6d\u867d\u7136\u672a\u80fd\u540d \u5217\u4ed6\u4eec\u5e2e\u4e2d\u7684\u56db\u5927\u9999\u4e3b\uff0c\u5374\u4e5f\u662f\u4ed6\u4eb2\u4fe1\u4e4b\u4e00\u3002\u6211\u542c\u5f97\u98ce\u58f0\uff0c\u4e01\u5f6d\u626c\u8a00\u8981\u62a5\u7236\u4ec7\uff0c\u5f88\u53ef\u80fd\u8d81\u6b64 \u673a\u4f1a\uff0c\u8ddf\u53f2\u767d\u90fd\u56de\u6765\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u8001\u4f2f\u4e43\u662f\u4e3a\u4e86\u907f\u4ec7\uff0c\u6545\u6b64\u4f2a\u79f0\u8eab\u6b7b\uff0c\u5047\u8bbe\u7075\u5802\u3002\u5176 \u5b9e\u8001\u4f2f\u662f\u65e0\u987b\u5982\u6b64\u5fcd\u8fb1\u3001\u793a\u5f31\u7684\u3002\u5373\u4f7f\u662f\u8fd9\u4e2a\u4e01\u5f6d\u8bf7\u5f97\u52a8\u53f2\u767d\u90fd\u6765\u5e2e\u4ed6\u62a5\u4ec7\uff0c\u54b1\u4eec\u4e5f\u53ef\u4ee5\u548c \u4ed6\u6253\u4e0a\u4e00\u67b6\u5475\uff01\u201d \u3000\u3000\u6234\u5747\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u8001\u4e86\uff0c\u6253\u5f97\u8fc7\u6253\u4e0d\u8fc7\u53f2\u767d\u90fd\u90a3\u662f\u53e6\u5916\u4e00\u56de\u4e8b\uff0c\u5728\u6211\u5df2\u662f\u6ca1\u6709\u6c5f \u6e56\u4e89\u80dc\u7684\u96c4\u5fc3\u4e86\u3002\u4f55\u51b5\u51a4\u5bb6\u5b9c\u89e3\u4e0d\u5b9c\u7ed3\uff0c\u53c8\u4f55\u5fc5\u65e0\u7aef\u7aef\u7684\u548c\u516d\u5408\u5e2e\u518d\u7ed3\u6881\u5b50\u5462\u3002\u6211\u5c31\u662f\u56e0\u4e3a \u8fd9\u6837\u60f3\uff0c\u6240\u4ee5\u60f3\u6765\u60f3\u53bb\u5c31\u53ea\u80fd\u51fa\u6b64\u4e0b\u7b56\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5982\u679c\u4ed6\u4eec\u4e0d\u80af\u76f8\u4fe1\u5462\uff1f\u201d\u6234\u5747\u7b11\u9053\uff1a\u201c\u6211\u7f20\u7ef5\u75c5\u584c\u4e4b\u65f6\uff0c\u9707\u8fdc\u9556\u5c40\u7684\u65e7\u4eba \u5dee\u4e0d\u591a\u90fd\u6765\u63a2\u8fc7\u6211\u7684\u75c5\uff1b\u51fa\u4e27\u4e4b\u65e5\uff0c\u4ed6\u4eec\u4e5f\u66fe\u6765\u7ed9\u6211\u6276\u68fa\u3002\u5f53\u7136\u6211\u7684\u75c5\u662f\u5047\u7684\uff0c\u5c38\u4f53\u4e5f\u662f\u5047 \u7684\uff0c\u68fa\u6750\u91cc\u653e\u7684\u4e0d\u8fc7\u662f\u51e0\u5757\u77f3\u5934\u3002\u4f46\u6211\u4e0d\u8bf4\u7a7f\uff0c\u5374\u600e\u4f1a\u6709\u4eba\u77e5\u9053\u6211\u662f\u5f04\u5047\uff1f\u201d\u91d1\u9010\u6d41\u53f9\u9053\uff1a \u201c\u8001\u524d\u8f88\u4e3a\u4e86\u606f\u4e8b\u5b81\u4eba\uff0c\u4e5f\u5f53\u771f\u662f\u715e\u8d39\u82e6\u5fc3\u4e86\u3002\u201d \u3000\u3000\u6234\u5747\u9053\uff1a\u201c\u4e01\u5f6d\u56de\u6765\uff0c\u4e00\u5b9a\u5148\u5411\u9707\u8fdc\u9556\u5c40\u7684\u65e7\u4eba\u63a2\u542c\u6211\u7684\u6d88\u606f\uff0c\u4ed6\u4eec\u5f02\u53e3\u540c\u58f0\u5730\u8bf4\u6211\u6b7b \u4e86\uff0c\u4ed6\u8fd8\u80fd\u591f\u4e0d\u76f8\u4fe1\u5417\uff1f\u4fd7\u8bed\u8bf4\u4e00\u6b7b\u767e\u4e86\uff0c\u4e01\u5f6d\u770b\u89c1\u4e86\u6211\u7684\u7075\u724c\uff0c\u4ed6\u8fd8\u80fd\u5c06\u6211\u600e\u6837\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5982\u679c\u4ed6\u8fd8\u662f\u4e0d\u80af\u5584\u7f62\u7518\u4f11\uff0c\u8981\u5411\u6234\u5927\u54e5\u62a5\u4ec7\u5462\u3002\u201d \u3000\u3000\u6234\u5747\u9053\uff1a\u201c\u53f2\u767d\u90fd\u662f\u6c5f\u6e56\u4e0a\u6570\u4e00\u6570\u4e8c\u7684\u4eba\u7269\uff0c\u4ed6\u53ea\u80fd\u548c\u6211\u4ea4\u624b\uff0c\u7edd\u4e0d\u4f1a\u6b3a\u8d1f\u6211\u7684\u513f\u5b50\uff0c \u8fd9\u662f\u53ef\u4ee5\u65ad\u8a00\u7684\u3002\u201d\u91d1\u9010\u6d41\u95ee\u7684\u662f\u4e01\u5f6d\uff0c\u622a\u5747\u7b54\u7684\u5374\u662f\u53f2\u767d\u90fd\uff0c\u770b\u4f3c\u7b54\u975e\u6240\u95ee\uff0c\u5176\u5b9e\u5df2\u662f\u89e3 \u9664\u4e86\u91d1\u9010\u6d41\u5fc3\u4e2d\u7684\u987e\u8651\u3002\u8981\u77e5\u6234\u5747\u7684\u4e24\u4e2a\u513f\u5b50\u672c\u9886\u90e8\u5f88\u4e0d\u5f31\uff0c\u53ea\u8981\u53f2\u767d\u90fd\u4e0d\u51fa\u624b\uff0c\u4e01\u5f6d\u600e\u6562 \u5411\u4ed6\u4eec\u6311\u8845\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u5018\u82e5\u53f2\u767d\u90e8\u6765\u4e86\uff0c\u6211\u53c8\u6070\u5de7\u4e0d\u5728\u8fd9\u513f\u7684\u8bdd\uff0c\u8fd9\u5339\u9a6c\u53ef\u4e0d\u80fd\u8ba9\u4ed6\u770b\u89c1\u3002\u201d \u6234\u5747\u9053\uff1a\u201c\u6211\u4f1a\u5c0f\u5fc3\u7684\u4e86\u3002\u8fd9\u5339\u9a6c\u6211\u53ef\u4ee5\u5bc4\u653e\u90bb\u5bb6\uff0c\u9694\u90bb\u5f20\u5bb6\uff0c\u4e0d\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u4f46\u5374\u662f\u6211\u51e0 \u5341\u5e74\u7684\u8001\u670b\u53cb\uff0c\u53ef\u4ee5\u4fe1\u6258\u5f97\u4e0b\u7684\u3002\u91d1\u8001\u5f1f\uff0c\u4f60\u8fd9\u6b21\u8fdb\u4eac\uff0c\u53ef\u6709\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e0d\u613f\u6234\u5bb6\u7236\u5b50\u62c5\u5fe7\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u7684\u4e8b\u60c5\uff0c\u6211\u65e2\u7136\u56de\u56fd\uff0c\u4eac\u90fd\u603b\u662f\u8981 \u6765\u73a9\u4e00\u6b21\u7684\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u4e14\u5f85\u8fc7\u4e86\u8428\u798f\u6676\u7684\u5bff\u671f\u4e4b\u540e\uff0c\u518d\u544a\u8bc9\u4ed6\u4eec\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d\u91d1\u9010\u6d41\u662f\u51c6 \u5907\u5728\u90a3\u4e00\u5929\u53bb\u5927\u95f9\u5bff\u5802\u7684\u3002 \u3000\u3000\u6234\u8c1f\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u6211\u73b0\u5728\u662f\u5b5d\u5b50\u7684\u8eab\u4efd\uff0c\u8981\u7559\u5728\u5bb6\u4e2d\u5b88\u7075\uff0c\u5982\u662f\u4e0d\u80fd\u8d54\u4f60\u51fa\u53bb\u73a9\u4e86\u3002\u201d \u3000\u3000\u8f7d\u5747\u9053\uff1a\u201c\u597d\u5728\u4f60\u4ece\u6765\u6ca1\u6709\u5230\u8fc7\u5317\u4eac\uff0c\u5927\u7ea6\u6ca1\u6709\u4ec0\u4e48\u4eba\u8ba4\u8bc6\u4f60\u3002\u4e0d\u8fc7\uff0c\u8fd9\u51e0\u5929\u4e09\u5c71\u4e94\u5cb3 \u7684\u4eba\u7269\u6765\u7ed9\u8428\u798f\u9f0e\u8d3a\u5bff\u7684\u5f88\u662f\u4e0d\u5c11\uff0c\u91d1\u8001\u5f1f\uff0c\u4f60\u7684\u672c\u9886\u867d\u7136\u9ad8\u5f3a\uff0c\u4e5f\u8fd8\u662f\u5c0f\u5fc3\u4e00\u70b9\u7684\u597d\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5e94\u4e86\u4e00\u4e2a\u201c\u662f\u201d\u5b57\u3002\u4f46\u4ed6\u662f\u4e2a\u95f2\u4e0d\u7740\u7684\u6027\u60c5\uff0c\u7b2c\u4e8c\u5929\u5c31\u62bd\u7a7a\u51fa\u53bb\u73a9\uff0c\u4e00\u8fde\u73a9\u4e86\u4e09 \u5929\uff0c\u4eac\u4e2d\u7684\u540d\u80dc\u5dee\u4e0d\u591a\u90fd\u901b\u8fc7\u4e86\uff0c\u7b2c\u56db\u5929\u6e38\u5174\u52c3\u53d1\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u660e\u5929\u5c31\u662f\u8428\u798f\u9f0e\u7684\u5bff\u671f\uff0c \u4eca\u5929\u53ef\u5f97\u5148\u53bb\u901b\u4e00\u901b\u4e07\u91cc\u957f\u57ce\u624d\u5bf9\u3002\u5426\u5219\u660e\u5929\u4e07\u4e00\u51fa\u4e86\u610f\u5916\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u7ed9\u4eba\u6253\u6b7b\u6253\u4f24\uff0c\u4e0d\u6e38 \u89c8\u8fc7\u4e07\u91cc\u957f\u57ce\uff0c\u5c82\u975e\u7ec8\u8eab\u9057\u61be\uff1f\u201d\u4e8e\u662f\u8fd9\u4e00\u5929\u7edd\u65e9\u8d77\u6765\uff0c\u57ce\u95e8\u4e00\u5f00\uff0c\u4ed6\u5c31\u5f80\u5c45\u5eb8\u5173\u53bb\u4e86\u3002 \u3000\u3000\u516b\u8fbe\u5cad\u4e0a\u7684\u5c45\u5eb8\u5173\u79bb\u4eac\u53ea\u6709\u4e00\u767e\u4f59\u91cc\uff0c\u4e07\u91cc\u957f\u57ce\u5c31\u5728\u90a3\u91cc\u873f\u8712\u800c\u8fc7\u3002\u91d1\u9010\u6d41\u6015\u6709\u4eba\u8ba4\u51fa \u4ed6\u90a3\u5339\u5750\u9a91\uff0c\u5f92\u6b65\u800c\u5f80\u3002\u4e00\u5927\u6e05\u65e9\uff0c\u8def\u4e0a\u8fd8\u6ca1\u6709\u4eba\u884c\uff0c\u91d1\u9010\u6d41\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u4e0d\u5230\u4e24\u4e2a\u65f6 \u8fb0\uff0c\u65e5\u5934\u521a\u51fa\u4e0d\u4e45\uff0c\u4ed6\u5c31\u5df2\u7ecf\u5230\u4e86\u516b\u8fbe\u5cad\u3002 \u3000\u3000\u4e07\u91cc\u957f\u57ce\u4ece\u5609\u5cea\u5173\u5230\u5c71\u6d77\u5173\uff0c\u5728\u4e1b\u5c71\u5cfb\u5cad\u4e2d\uff0c\u873f\u8712\u4e00\u4e07\u4e8c\u5343\u4f59\u91cc\uff0c\u5c45\u5eb8\u5173\u8fd9\u6bb5\u901a\u8fc7\u516b\u8fbe \u5cad\u3002\u91d1\u9010\u6d41\u722c\u4e0a\u9661\u5cfb\u7684\u5c71\u5c97\uff0c\u53ea\u89c1\u4e07\u91cc\u957f\u57ce\u5728\u7fa4\u5c71\u4e4b\u4e2d\u8d77\u4f0f\uff0c\u5c31\u50cf\u4e00\u6761\u5176\u957f\u65e0\u6bd4\u7684\u957f\u86c7\u3002\u5c45 \u5eb8\u5173\u57ce\u5173\u5c79\u7acb\u5728\u5357\u53e3\u5317\u9762\uff0c\u4e24\u65c1\u9ad8\u5c71\u5939\u7740\u4e00\u6761\u72ed\u5c0f\u7684\u5c71\u6c9f\uff0c\u5c71\u5c97\u4e0a\u5c71\u82b1\u70c2\u6f2b\uff0c\u9752\u8349\u90c1\u8302\uff0c\u597d \u50cf\u662f\u78a7\u6ce2\u7fe0\u6d6a\uff0c\u7ec7\u6210\u4e00\u5e45\u7f8e\u4e3d\u7684\u56fe\u6848\u3002\u8fd9\u5c31\u662f\u6709\u540d\u7684\u201c\u71d5\u4eac\u516b\u666f\u201d\u4e4b\u2014\u2014\u201c\u5c45\u5eb8\u53e0\u7fe0\u201d\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6e38\u8d4f\u4e86\u4e00\u4f1a\uff0c\u4ece\u5173\u57ce\u897f\u53bb\uff0c\u4e0d\u8fdc\u5904\u6709\u4e00\u5ea7\u77f3\u53f0\u53eb\u505a\u201d\u4e91\u53f0\u201d\uff0c\u5168\u90e8\u7528\u6c49\u767d\u7389\u780c \u6210\uff0c\u523b\u6709\u56db\u5927\u5929\u738b\u50cf\uff0c\u6d6e\u96d5\u7cbe\u82f1\uff0c\u795e\u60c5\u5a01\u731b\u3002\u56db\u5927\u65e0\u738b\u7684\u50cf\u95f4\uff0c\u523b\u7740\u68b5\u3001\u85cf\u3001\u897f\u590f\u3001\u8499\u3001\u6c49 \u4e94\u79cd\u6587\u5b57\u7684\u4f5b\u7ecf\u3002\u201c\u53f0\u9876\u201d\u4e0a\u8fd8\u6709\u201c\u66fc\u9640\u7f57\u82b1\u201d\u7684\u6d6e\u96d5\uff0c\u5176\u4e2d\u6709\u65e0\u6570\u5177\u4f53\u800c\u5fae\u7684\u4f5b\u50cf\u3002 \u3000\u3000\u8fd9\u5ea7\u201c\u4e91\u53f0\u201d\u662f\u4e2d\u56fd\u8457\u540d\u7684\u4e00\u4e2a\u4f5b\u6559\u5efa\u7b51\uff0c\u5bf9\u4f5b\u5178\u548c\u53e4\u4ee3\u6587\u5b57\u7684\u7814\u7a76\u5177\u6709\u5f88\u9ad8\u7684\u4ef7\u503c\u3002 \u4f46\u91d1\u9010\u6d41\u5bf9\u4f5b\u5b66\u4e43\u662f\u4e2a\u95e8\u5916\u6c49\uff0c\u53ea\u662f\u6b23\u8d4f\u4e86\u4e00\u4f1a\u90a3\u4e9b\u5de7\u593a\u5929\u5de5\u7684\u6d6e\u96d5\uff0c\u5bf9\u4e0a\u9762\u6240\u523b\u7684\u4f5b\u7ecf\u5374 \u662f\u6beb\u65e0\u5174\u8da3\u3002\u770b\u4e86\u4e00\u4f1a\uff0c\u4e5f\u5c31\u8d70\u4e86\u3002 \u3000\u3000\u4e00\u8def\u8d70\u53bb\uff0c\u603b\u8fc7\u4e86\u201c\u4e94\u90ce\u50cf\u201d\u201c\u516d\u90ce\u5f71\u201d\u201c\u7a46\u6842\u82f1\u70b9\u5c06\u53f0\u201d\u7b49\u5904\u540d\u80dc\u3002\u8fd9\u4e00\u8fde\u4e32\u540d\u80dc\u90fd \u662f\u5317\u672b\u6297\u8fbd\u540d\u5c06\u6768\u5bb6\u5c06\u7684\u201c\u9057\u8ff9\u201d\uff0c\u5176\u5b9e\u8bf4\u662f\u201c\u9057\u8ff9\u201d\uff0c\u6bcb\u4e8e\u8bf4\u662f\u6c11\u95f4\u9644\u4f1a\u7684\u4f20\u8bf4\uff0c\u4f8b\u5982 \u201c\u7a46\u6302\u82f1\u70b9\u5c06\u53f0\u201d\u4e0d\u8fc7\u662f\u4e00\u5757\u5927\u77f3\u5934\uff0c\u7a46\u6842\u82f1\u5f53\u5e74\u662f\u5426\u66fe\u7ecf\u5728\u8fd9\u5757\u77f3\u5934\u4e0a\u70b9\u8fc7\u5c06\uff0c\u8c01\u4e5f\u4e0d\u77e5 \u9053\u3002\u751a\u81f3\u6709\u6ca1\u6709\u7a46\u6842\u82f1\u6b64\u4eba\uff0c\u5728\u53f2\u4e66\u4e0a\u4e5f\u8fd8\u627e\u4e0d\u5230\u786e\u8bc1\u3002\u6050\u6015\u591a\u534a\u662f\u865a\u6784\u51fa\u6765\u7684\u4eba\u7269\u3002\u4e0d \u8fc7\uff0c\u91d1\u9010\u6d41\u6e38\u4e86\u8fd9\u51e0\u5904\u201c\u540d\u80dc\u201d\uff0c\u5fc3\u4e2d\u5374\u662f\u751a\u6709\u611f\u89e6\uff1a\u201c\u4f20\u8bf4\u4e5f\u597d\uff0c\u9644\u4f1a\u4e5f\u597d\uff0c\u8fd9\u603b\u662f\u4ee3\u8868 \u4e86\u6c11\u95f4\u5bf9\u6297\u654c\u82f1\u96c4\u7684\u666f\u4ef0\u3002\u201d\u5728\u201c\u7a46\u6842\u82f1\u70b9\u5c06\u53f0\u201d\u4e0b\uff0c\u4e0d\u7981\u601d\u6f6e\u8d77\u4f0f\uff0c\u4f4e\u56de\u826f\u4e45\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u94ee\u94ee\u742e\u742e\u4e4b\u58f0\uff0c\u5ffd\u9ad8\u5ffd\u4f4e\uff0c\u82e5\u9690\u82e5\u73b0\u3002\u91d1\u9010\u6d41\u77e5\u9053\u9644\u8fd1\u6709\u4e2a\u201c\u5f39\u7434\u5ce1\u201d\uff0c\u662f\u7531\u4e8e \u6c34\u6d41\u97f3\u54cd\u6e05\u8106\u5982\u7434\u97f3\u5f97\u540d\u3002\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u679c\u7136\u771f\u50cf\u7434\u58f0\u3002\u201d\u4e5f\u4e0d\u600e\u6837\u7559\u5fc3\u53bb\u542c\u3002 \u3000\u3000\u8fc7\u4e86\u201c\u7a46\u6842\u82f1\u70b9\u5c06\u53f0\u201d\uff0c\u5230\u4e86\u516b\u8fbe\u5cad\u7684\u9ad8\u5904\uff0c\u53ea\u89c1\u5728\u4e00\u5904\u60ac\u5d16\u4e0a\u51ff\u4e86\u201c\u5929\u9669\u201d\u4e8c\u5b57\uff0c\u5c71 \u52bf\u6781\u4e3a\u9669\u5cfb\uff0c\u4e07\u91cc\u957f\u57ce\u5c31\u5728\u5c71\u9698\u5904\u722c\u8fc7\u3002\u91d1\u9010\u6d41\u4e0a\u4e86\u57ce\u5899\uff0c\u7eb5\u76ee\u8fdc\u8df3\uff0c\u53ea\u89c1\u5c71\u5cf0\u91cd\u53e0\uff0c\u4e00\u671b \u65e0\u5c3d\uff0c\u5c45\u5eb8\u5173\u5c79\u7acb\u5317\u65b9\uff0c\u4e07\u91cc\u957f\u57ce\u6709\u5982\u4e00\u6761\u770b\u4e0d\u89c1\u9996\u5c3e\u7684\u957f\u86c7\u5728\u7ffb\u5c71\u8d8a\u5cad\uff0c\u5173\u5916\u83bd\u83bd\u5e73\u539f\u4f3c \u662f\u4e0e\u5929\u8fb9\u7684\u767d\u4e91\u76f8\u63a5\u3002\u91d1\u9010\u6d41\u62ab\u895f\u5f53\u98ce\uff0c\u8c6a\u60c5\u52c3\u53d1\uff0c\u987f\u89c9\u5929\u5730\u4e4b\u5927\u4e0e\u4e2a\u4eba\u4e4b\u5c0f\uff01 \u3000\u3000\u66ae\u542c\u5f97\u7434\u58f0\u53c8\u8d77\uff0c\u91d1\u9010\u6d41\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u6b21\u4ed6\u542c\u5f97\u6e05\u695a\u4e86\uff0c\u539f\u6765\u662f\u771f\u7684\u6709\u4eba\u5f39\u7434\uff0c\u5e76\u4e0d\u662f \u6c34\u6d41\u97f3\u54cd\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u9053\uff1a\u201c\u662f\u8c01\u4eba\u5728\u4e07\u91cc\u957f\u57ce\u4e4b\u4e0a\u5f39\u7434\uff1f\u60f3\u6765\u4e0d\u662f\u9ad8\u4eba\u5c31\u662f\u96c5\u58eb\u7684\u4e86\u3002\u6709\u7f18\u76f8\u4f1a\uff0c \u5012\u662f\u4e0d\u59a8\u53bb\u4e0e\u4ed6\u7ed3\u4ea4\u7ed3\u4ea4\u3002\u201d\u4e8e\u662f\u5bfb\u58f0\u89c5\u8ff9\uff0c\u5728\u57ce\u5899\u4e0a\u4e00\u8def\u8d70\u53bb\uff0c\u8d70\u5230\u8fd1\u5904\u4e00\u770b\uff0c\u4e0d\u7981\u5927\u611f \u610f\u5916\u3002 \u3000\u3000\u5728\u91d1\u9010\u6d41\u7684\u60f3\u8c61\u4e2d\uff0c\u4ee5\u4e3a\u8fd9\u4e2a\u5f39\u7434\u7684\u9ad8\u4eba\u5e94\u8be5\u662f\u4e2a\u6709\u4e09\u7edc\u957f\u987b\u7684\u9690\u571f\uff0c\u8c01\u77e5\u5374\u662f\u4e00\u4e2a\u5e74 \u7eaa\u548c\u4ed6\u5dee\u4e0d\u591a\u7684\u5e74\u8f7b\u4eba\uff0c\u81f3\u591a\u4e0d\u8fc7\u6bd4\u4ed6\u5927\u4e09\u4e24\u5c81\u800c\u5df2\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5411\u4ed6\u8d70\u53bb\uff0c\u8fd9\u5e74\u8f7b\u4eba\u4f3c\u662f\u89c6\u800c\u4e0d\u89c1\uff0c\u4e13\u5fc3\u6ce8\u610f\u7684\u53ea\u662f\u5f39\u7434\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7684\u6bcd\u4eb2\u8c37\u4e4b\u534e\u662f\u5415\u56db\u5a18\u6700\u5f97\u610f\u7684\u5f1f\u5b50\uff0c\u5415\u56db\u5a18\u5219\u662f\u660e\u672b\u6e05\u521d\u5927\u4fa0\u5415\u7559\u826f\u7684\u5973\u513f\u3002 \u56e0\u6b64\u8c37\u4e4b\u534e\u4e0d\u4f46\u5f97\u4e86\u5415\u56db\u5a18\u5251\u672f\u7684\u8863\u94b5\u771f\u4f20\uff0c\u7434\u68cb\u8bd7\u753b\u4ea6\u662f\u65e0\u6240\u4e0d\u80fd\uff0c\u91d1\u9010\u6d41\u5e7c\u627f\u5bb6\u5b66\uff0c\u5bf9 \u53e4\u7434\u4e00\u9053\uff0c\u867d\u9aa4\u672a\u6709\u6bcd\u4eb2\u7684\u9020\u8be3\uff0c\u5374\u4e5f\u662f\u5999\u89e3\u97f3\u5f8b\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u8fd9\u5e74\u8f7b\u4eba\u6b63\u5728\u5f39\u594f\u695a\u8f9e\u4e5d\u6b4c\u4e2d\u201c\u6e58\u541b\u201d\u7684\u4e00\u8282\uff1a\u201c\u541b\u4e0d\u884c\u516e\u5937\u72b9\uff0c\u8e47\u8c01\u7559\u516e\u4e2d \u5dde\uff1f\u7f8e\u8981\u7707\u516e\u5b9c\u4fee\uff0c\u6c9b\u543e\u4e58\u516e\u6842\u821f\u3002\u201d\u2026\u201d\u8fd9\u662f\u4e00\u5bf9\u5728\u604b\u7231\u4e2d\u7684\u7537\u5973\u5bf9\u8bdd\uff0c\u5973\u7684\u5728\u95ee\uff1a\u201c\u4f60 \u6709\u4ec0\u4e48\u5fc3\u4e8b\u72b9\u8c6b\u4e0d\u524d\uff1f\u4e3a\u4e86\u8c01\u628a\u5c0f\u821f\u6401\u6d45\u5728\u5dde\u4e2d\u5462\uff1f\u201d\u7537\u7684\u5728\u7b54\uff1a\u201c\u8fd8\u4e0d\u662f\u4e3a\u4e86\u4f60\u5417\uff1f\u4e3a\u4e86 \u4f60\u5999\u4e3d\u7684\u5bb9\u989c\uff0c\u6211\u4e58\u5750\u8d70\u5f97\u5f88\u5feb\u7684\u6842\u821f\u6765\u8ffd\u8d76\u4f60\uff0c\u89c1\u4e86\u4f60\u6211\u5c31\u4e0d\u60f3\u8d70\u4e86\u3002\u201c\u8981\u7707\u201d\u662f\u5f62\u5bb9\u5bb9 \u8c8c\u5999\u4e3d\uff0c\u201c\u5b9c\u8179\u201d\u5219\u8db3\u5986\u626e\u5f97\u6070\u5230\u597d\u5904\u7684\u610f\u601d\u3002\u91d1\u9010\u6d41\u542c\u4e86\u8fd9\u8282\u7434\u58f0\uff0c\u773c\u524d\u4e0d\u7981\u6d6e\u73b0\u53f2\u7ea2\u82f1 \u90a3\u201c\u8981\u7707\u5b9c\u4fee\u201d\u7684\u5a77\u5a77\u4fcf\u5f71\uff0c\u5fcd\u4e0d\u4f4f\u6309\u62cd\u4f4e\u548c\u3002 \u3000\u3000\u7434\u97f3\u4e00\u53d8\uff0c\u5982\u6028\u5982\u6155\uff0c\u5982\u6ce3\u5982\u8bc9\uff0c\u5f39\u7684\u4ecd\u662f\u695a\u8f9e\uff0c\u4e0d\u8fc7\u6539\u4e3a\u4e86\u201c\u79bb\u9a9a\u201d\u4e2d\u7684\u4e00\u4e66\uff1a \u201c\u2026\u2026\u6c5f\u79bb\u4e0e\u8f9f\u82b7\u516e\uff0c\u7eab\u79cb\u5170\u4ee5\u4e3a\u4f69\u3002\u6c68\u4f59\u82e5\u5c06\u4e0d\u53ca\u516e\uff0c\u6050\u5e74\u5c81\u4e0d\u543e\u4e0e\u3002\u671d\u6434\u965b\u4e4b\u6728\u5170\u516e\uff0c \u5915\u63fd\u5dde\u4e4b\u5bbf\u83bd\u3002\u65e5\u6708\u5ffd\u5176\u4e0d\u6df9\u516e\uff0c\u6625\u4e0e\u79cb\u5176\u4ee3\u5e8f\u3002\u60df\u8349\u6728\u4e4b\u96f6\u843d\u516e\uff0c\u6050\u7f8e\u4eba\u4e4b\u8fdf\u66ae\uff01\u201d \u3000\u3000\u201c\u5371\u201d\u662f\u201c\u5f7c\u5728\u8eab\u4e0a\u201d\u7684\u610f\u601d\u3002\u201c\u6c5f\u79bb\u201d\u662f\u4e00\u79cd\u9999\u8349\u540d\uff0c\u53c8\u540d\u85e4\u829c\u3002\u201c\u8f9f\u5728\u201d\u662f\u957f\u5728\u5e7d \u9690\u5730\u65b9\u7684\u9999\u8349\uff0c\u201c\u7eab\u201d\u662f\u201c\u7528\u7ebf\u7a7f\u4e0a\u201d\u3002\u201c\u585e\u201d\u662f\u201c\u62d4\u53d6\u201d\u3002\u201c\u6bd7\u201d\u662f\u201c\u5c0f\u5c71\u201d\u3002\u201c\u5bbf\u83bd\u201d \u662f\u4e00\u79cd\u80fd\u591f\u8010\u5bd2\u5728\u51ac\u5929\u751f\u957f\u7684\u91ce\u8349\u3002\u8fd9\u4e00\u8282\u201c\u79bb\u9a9a\u201d\u628a\u5b64\u81e3\u5b7d\u5b50\u4e4b\u5fc3\u5bc4\u6258\u4e8e\u7f8e\u4eba\u9999\u8349\uff0c\u6168\u65f6 \u5149\u4e4b\u6613\u901d\uff0c\u53f9\u7f8e\u4eba\u4e4b\u8fdf\u66ae\u3002 \u3000\u3000\u91d1\u9010\u6d41\u53cd\u590d\u541f\u54e6\u6700\u540e\u56db\u53e5\uff1a\u201c\u65e5\u6708\u5ffd\u5176\u4e0d\u6df9\u516e\uff0c\u6625\u52fe\u79cb\u5176\u4ee3\u5e8f\u3002\u60df\u9769\u6728\u4e4b\u96f6\u843d\u516e\uff0c\u6050\u7f8e \u4eba\u4e4b\u8fdf\u66ae\u3002\u201d\u4e0d\u7981\u53c8\u60f3\u8d77\u4e86\u53f2\u7ea2\u82f1\u6765\uff0c\u201c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u4e0e\u5979\u76f8\u89c1\uff1f\u201d\u5373\u4f7f\u662f\u5979\u8001\u4e86\uff0c\u65b9 \u5f97\u91cd\u9022\uff0c\u5979\u5728\u6211\u7684\u773c\u4e2d\u4e5f\u8fd8\u662f\u7f8e\u4eba\u7684\u3002\u201d\u201c\u6211\u6240\u5fe7\u8651\u7684\u53ea\u662f\u4e00\u4e8b\u65e0\u6210\u7684\u2018\u8fdf\u66ae\u2019\u4e4b\u611f\uff0c\u82e5\u53ea \u662f\u2018\u7f8e\u4eba\u8fdf\u66ae\u2019\uff0c\u90a3\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u867d\u7136\u91d1\u9010\u6d41\u5fc3\u4e2d\u7684\u611f\u60c5\u548c\u8fd9\u4eba\u6240\u5f39\u7684\u79bb\u9a9a\u5e76\u4e0d\u4e00\u6837\uff0c\u4f46\u8fd9\u4eba\u5f39\u5f97\u5b9e\u5728\u592a\u597d\u4e86\uff0c\u91d1\u9010\u6d41\u7adf \u4e5f\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\u53d7\u4ed6\u611f\u52a8\uff0c\u4f46\u89c9\u60b2\u4ece\u4e2d\u6765\uff0c\u96be\u4ee5\u65ad\u7edd\uff0c\u6f78\u7136\u6cea\u4e0b\u3002\u4e0d\u77e5\u4e0d\u89c9\u95f4\u5df2\u662f\u8d70\u5230\u8fd9 \u5c11\u5e74\u7684\u8eab\u8fb9\u3002\u5c11\u5e74\u6b64\u65f6\u65b9\u624d\u597d\u4f3c\u53d1\u89c9\u4e86\u91d1\u9010\u6d41\u7684\u5b58\u5728\uff0c\u4f46\u4e5f\u53ea\u4e0d\u8fc7\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u4f9d\u7136\u7ee7\u7eed\u5f39 \u7434\u3002 \u3000\u3000\u7434\u97f3\u8d8a\u53d1\u7f20\u7ef5\u5f98\u607b\uff0c\u8fd9\u5c11\u5e74\u8fb9\u5f39\u8fb9\u5531\uff1a\u201c\u767d\u9a79\u6b4c\u5df2\u901d\uff0c\u4f0a\u4eba\u6c34\u4e00\u65b9\uff1b\u6742\u63c9\u82b3\u4e0e\u6cfd\uff0c\u76f8\u89c1 \u5fcd\u76f8\u5fd8\uff1f\u201d\u7b2c\u4e00\u53e5\u7528\u7684\u662f\u8bd7\u7ecf\u201c\u767d\u9a79\u201d\u7bc7\u7684\u5178\u6545\uff0c\u8bf4\u662f\u4ed6\u60f3\u628a\u8fdc\u65b9\u7684\u5ba2\u4eba\u7559\u4f4f\uff0c\u628a\u5ba2\u4eba\u7684\u767d \u9a6c\u62f4\u8d77\u6765\uff0c\u53ef\u662f\u7ec8\u4e8e\u8fd8\u662f\u7559\u4e0d\u4f4f\u7684\uff0c\u56e0\u6b64\u8bf4\u662f\u201c\u767d\u9a79\u6b4c\u5df2\u901d\u201d\u3002\u7b2c\u4e8c\u53e5\u7528\u7684\u662f\u8bd7\u7ecf\u201c\u84b9\u971e\u201d \u7bc7\u7684\u66f2\u6545\uff0c\u201c\u517c\u8335\u82cd\u82cd\uff0c\u81ea\u9732\u4e3a\u971c\uff0c\u6240\u8c13\u4f0a\u4eba\uff0c\u5728\u6c34\u4e00\u65b9\u3002\u6eaf\u6cc5\u4ece\u4e4b\uff0c\u9053\u963b\u4e14\u957f\uff0c\u6eaf\u6e38\u4ece \u4e4b\uff0c\u5b9b\u5728\u6c34\u4e2d\u592e\u3002\u201d\u90a3\u610f\u601d\u662f\u8bf4\u4ed6\u6240\u4ef0\u5e55\u3001\u6240\u8981\u9053\u6c42\u7684\u4eba\u513f\uff0c\u53ef\u671b\u800c\u4e0d\u53ef\u5373\u3002\u7b2c\u4e09\u53e5\u7528\u7684\u662f \u695a\u8f9e\u201c\u601d\u7f8e\u4eba\u201d\u7bc7\u7684\u5178\u6545\uff0c\u610f\u601d\u662f\u8bf4\u7231\u4eba\u7231\u4e86\u59d4\u5c48\uff0c\u597d\u50cf\u9999\u82b1\uff08\u82b3\uff09\u6df7\u5728\u6d4a\u8349\uff08\u8bd1\uff09\u4e2d\u95f4\u3002 \u7b2c\u56db\u53e5\u662f\u8bf4\uff0c\u5728\u8fd9\u6837\u60c5\u52bf\u4e4b\u4e0b\uff0c\u76f8\u89c1\u4e4b\u540e\u4e5f\u8fd8\u662f\u4e92\u76f8\u5fd8\u6389\u7684\u597d\uff0c\u4f46\u53c8\u600e\u5fcd\u76f8\u5fd8\u5462\uff1f \u3000\u3000\u91d1\u9010\u6d41\u542c\u5f97\u75f4\u4e86\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ed6\u8fd9\u4e00\u66f2\u7adf\u4f3c\u662f\u4e3a\u6211\u800c\u6b4c\uff0c\u53f2\u59d1\u5a18\u4e0d\u662f\u6b63\u50cf\u6b4c\u4e2d\u90a3\u4f4d\u53d7 \u4e86\u59d4\u5c48\u7684\u83ab\u4eba\u4e48\uff1f\u4f46\u5374\u4e0d\u77e5\u4ed6\u6240\u601d\u5ff5\u7684\u4eba\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u7434\u666e\u621b\u7136\u800c\u6b62\uff0c\u91d1\u9010\u6d41\u8d5e\u9053\uff1a\u201c\u5f39\u7684\u597d\u7434\uff0c\u4f46\u4eba\u751f\u767e\u5e74\uff0c\u53c8\u4f55\u5fc5\u81ea\u82e6\u82e5\u662f\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u770b\u4e86\u91d1\u9010\u6d41\u4e00\u773c\uff0c\u63a8\u7434\u800c\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u542c\u5f97\u61c2\u6211\u7684\u7434\u97f5\uff0c\u60f3\u5fc5\u4ea6\u662f\u89e3\u4eba\u3002\u613f\u8046 \u96c5\u594f\u3002\u201d\u8bf4\u8bdd\u867d\u7136\u5ba2\u6c14\uff0c\u5374\u4e5f\u5e26\u6709\u51e0\u5206\u5014\u50b2\u7684\u5473\u9053\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e5f\u4e0d\u63a8\u8f9e\uff0c\u5750\u4e86\u4e0b\u6765\uff0c\u63a5\u8fc7\u90a3\u5f20\u65b9\u7434\uff0c\u653e\u5728\u819d\u4e0a\u3002\u91d1\u9010\u6d41\u662f\u4e2a\u8bc6\u8d27\u7684\u4eba\uff0c\u89c1\u8fd9\u7434 \u53e4\u8d28\u6591\u8c30\uff0c\u7434\u7684\u4e00\u7aef\uff0c\u6728\u5934\u4e0a\u6709\u706b\u70e7\u8fc7\u7684\u75d5\u8ff9\uff0c\u5728\u4e0d\u8bc6\u8d27\u7684\u4eba\u770b\u6765\uff0c\u8fd9\u4e0d\u8fc7\u662f\u4e00\u6bb5\u70e7\u7126\u4e86\u7684 \u70c2\u6728\u5934\uff0c\u91d1\u9010\u6d41\u5374\u77e5\u9053\u8fd9\u662f\u4e00\u5f20\u65e0\u4ef7\u4e4b\u5b9d\u7684\u53e4\u7434\uff0c\u5728\u7434\u8c31\u4e0a\u540d\u4e3a\u201c\u7126\u5c3e\u7434\u201d\u3002 \u3000\u3000\u4f59\u9010\u6d41\u8d5e\u4e86\u4e00\u58f0\uff1a\u201c\u597d\u7434\u3002\u8fd9\u5927\u6982\u662f\u6625\u79cb\u65f6\u4ee3\u7684\u53e4\u7269\u3002\u201d \u3000\u3000\u5c11\u5e74\u9732\u51fa\u51e0\u5206\u8be7\u610f\uff0c\u8bf4\u9053\uff0c\u201c\u4e0d\u9519\u3002\u636e\u8bf4\u8fd9\u5f20\u7434\u5c31\u662f\u4f2f\u7259\u7ed9\u949f\u5b50\u671f\u5f39\u594f\u9ad8\u5c71\u6d41\u6c34\u7684\u90a3\u5f20 \u7434\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u9ad8\u5c71\u6d41\u6c34\u7684\u7434\u97f5\u6211\u662f\u5f39\u79e6\u4e0d\u51fa\u6765\u7684\uff0c\u6211\u5f39\u7684\u53ea\u662f\u4e0b\u91cc\u5df4\u4eba\u4e4b\u8c03\uff0c\u5144\u53f0\u4f53 \u8981\u53d6\u7b11\u3002\u201d\u8bf4\u7f62\uff0c\u4e00\u62e8\u7434\u5f26\uff0c\u53ee\u53ee\u549a\u549a\u5730\u5f39\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5f39\u5230\u6025\u5904\uff0c\u604d\u5982\u4e07\u9a6c\u5954\u817e\uff0c\u5343\u519b\u8d74\u654c\u3002\u91d1\u9010\u6d41\u5f15\u542d\u9ad8\u6b4c\uff1a\u201c\u524d\u4e0d\u89c1\u53e4\u4eba\uff0c\u540e\u4e0d\u89c1\u6765\u8005\u3002 \u5ff5\u5929\u5730\u4e4b\u60a0\u60a0\uff0c\u72ec\u6006\u7136\u800c\u6d95\u4e0b\uff01\u201d\u7434\u97f5\u6b4c\u58f0\uff0c\u82cd\u51c9\u6c89\u90c1\uff0c\u4f46\u5982\u5e76\u65e0\u60b2\u4f24\u7684\u5473\u9053\uff0c\u6709\u51e0\u5206\u601d\u53e4 \u7684\u5e7d\u60c5\uff0c\u66f4\u591a\u7684\u5374\u662f\u6292\u53d1\u80f8\u4e2d\u7684\u8c6a\u6c14\uff01\u4e0e\u5c11\u5e74\u521a\u624d\u6240\u594f\u7684\u7f20\u7ef5\u5f98\u607b\u4e4b\u97f3\u5927\u5f02\u5176\u8da3\uff0c\u4f46\u5374\u4e5f\u662f \u5f02\u66f2\u540c\u5de5\u3002 \u3000\u3000\u8fd9\u5c11\u5e74\u9053\uff1a\u201c\u5144\u53f0\u679c\u662f\u77e5\u97f3\u3002\u4f60\u65e2\u7136\u559c\u6b22\u8fd9\u5f20\u7434\uff0c\u597d\uff0c\u8fd9\u5f20\u7434\u6211\u5c31\u9001\u7ed9\u4f60\u4e86\u3002\u201d\u91d1\u9010\u6d41 \u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u539a\u793c\uff0c\u5c0f\u5f1f\u600e\u53d7\u5f97\u8d77\uff1f\u201d \u3000\u3000\u5c11\u5e74\u4e00\u58f0\u957f\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u573a\u610f\u4e0d\u9022\uff0c\u629a\u51cc\u4e91\u800c\u4e14\u63aa\uff0c\u949f\u671f\u65e2\u9047\uff0c\u594f\u6d41\u6c34\u4ee5\u4f55\u6e10\uff0c\u4eba\u751f\u96be \u5f97\u77e5\u97f3\uff0c\u533a\u533a\u4e00\u5f20\u7126\u5c3e\u7434\u4f55\u8db3\u9053\u54c9\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u672c\u6765\u5c31\u662f\u4e2a\u6f47\u6d12\u4e0d\u7f81\u7684\u6027\u683c\uff0c\u89c1\u8fd9\u5c11\u5e74\u8bf4\u5f97\u8c6a\u723d\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u53d7\uff0c\u5012\u663e \u5f97\u6211\u662f\u6709\u4e16\u4fd7\u4e4b\u89c1\u4e86\u3002\u201d\u4e8e\u662f\u63a5\u8fc7\u53e4\u7434\uff0c\u7b11\u9053\uff1a\u201c\u5144\u53f0\u96c5\u594f\uff0c\u6015\u7259\u60f3\u4ea6\u4e0d\u8fc7\u5982\u662f\uff0c\u6211\u5374\u4e0d\u914d \u505a\u949f\u5b50\u671f\u5462\u3002\u627f\u4ee5\u77e5\u97f3\u76f8\u8bb8\uff0c\u6211\u662f\u65e2\u611f\u7f9e\u6127\u4e86\u3002\u5144\u53f0\u597d\u610f\uff0c\u5c0f\u5f1f\u4e0d\u6562\u63a8\u8f9e\uff0c\u53ea\u662f\u6211\u53d7\u4e86\u4f60\u7684 \u539a\u8d50\uff0c\u5374\u4e0d\u77e5\u5982\u4f55\u62a5\u7b54\u4e86\u3002\u201d \u3000\u3000\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u4f60\u8981\u62a5\u7b54\u4e48\uff1f\u90a3\u4e5f\u5bb9\u6613\u3002\u201d\u6307\u4e00\u6307\u91d1\u9010\u6d41\u8170\u60ac\u7684\u957f\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u543e\u5144\u4f69\u5251 \u72ec\u884c\uff0c\u60f3\u5fc5\u7cbe\u4e8e\u5251\u6cd5\u3002\u6211\u7ed9\u4f60\u5f39\u7434\uff0c\u4f60\u7ed9\u6211\u821e\u5251\u5982\u4f55\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u8c6a\u60c5\u987f\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u5b66\u8fc7\u51e0\u5e74\u5251\u672f\uff0c\u7c97\u6d45\u5f97\u5f88\u3002\u4e0d\u8fc7\uff0c\u6211\u542c\u4e86\u4f60\u7684\u4e09\u66f2\u7434 \u97f3\uff0c\u6211\u56de\u62a5\u4e86\u4e00\u66f2\uff0c\u4e5f\u662f\u6709\u70b9\u8bf4\u4e0d\u8fc7\u53bb\uff0c\u6211\u7684\u7434\u6280\u4e0e\u4f60\u76f8\u5dee\u592a\u8fdc\uff0c\u4e0d\u6562\u518d\u73ed\u95e8\u5f04\u65a7\u4e86\u3002\u597d \u5427\uff0c\u6211\u5144\u65e2\u7136\u559c\u6b22\u89c2\u8d4f\u821e\u5251\uff0c\u6211\u5c31\u800d\u4e00\u5957\u535a\u4f60\u4e00\u7b11\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u634f\u4e86\u4e00\u4e2a\u5251\u8bc0\uff0c\u9752\u94a2\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u767b\u65f6\u2018\u4fbf\u662f\u94f6\u5149\u531d\u5730\uff0c\u7d2b\u7535\u76d8\u7a7a\uff0c\u5251\u82b1\u9519\u843d\uff0c \u5251\u6c14\u7eb5\u6a2a\u3002\u5c11\u5e74\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u62ff\u8d77\u91d1\u9010\u6d41\u653e\u4e0b\u7684\u53e4\u7434\uff0c\u94ee\u94ee\u742e\u742e\u7684\u4e5f\u5f39\u8d77\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6709\u5fc3\u8868\u6f14\u770b\u5bb6\u672c\u9886\uff0c\u628a\u5929\u5c71\u5251\u6cd5\u4e2d\u6700\u7cbe\u5999\u7684\u201c\u5927\u987b\u5f25\u5251\u5f0f\u201d\u4f7f\u5c06\u51fa\u6765\uff0c\u5fc3\u65e0\u65c1 \u9a9b\uff0c\u90a3\u5c11\u5e74\u5f39\u4e9b\u4ec0\u4e48\uff0c\u4ed6\u53ef\u6ca1\u6709\u7559\u610f\u3002 \u3000\u3000\u821e\u5230\u6025\u5904\uff0c\u5ffd\u5730\u5fc3\u795e\u4e00\u5206\uff0c\u9669\u4e9b\u4e71\u4e86\u4e00\u62db\uff0c\u539f\u6765\u4ed6\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u53d7\u4e86\u7434\u97f3\u7684\u5f71\u54cd\uff0c \u5ffd\u89c9\u7434\u97f3\u548c\u4ed6\u7684\u5251\u672f\u4e0d\u5927\u5408\u62cd\uff0c\u4ed6\u8fd9\u624d\u7701\u609f\u8fc7\u6765\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5fae\u5fae\u4e00\u566b\uff0c\u8bf4\u9053\uff1a\u201c\u543e\u5144\u5251\u672f\u679c\u7136\u662f\u5f53\u505a\u65e0\u53cc\uff01\u201d\u91cd\u7406\u7434\u5f26\uff0c\u518d\u5f39\u8d77\u6765\uff0c\u8fd9\u6b21\u4ed6 \u5168\u795e\u8d2f\u6ce8\uff0c\u7434\u58f0\u987f\u632b\u6291\u626c\uff0c\u679c\u7136\u4e0e\u91d1\u9010\u6d41\u6240\u4f7f\u7684\u5251\u6728\u4e1d\u4e1d\u5165\u6263\u3002\u91d1\u9010\u6d41\u5927\u4e3a\u60ca\u5f02\uff0c\u5fc3\u60f3\u3002\u201d \u96be\u9053\u4ed6\u4e5f\u61c2\u5f97\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u5426\u5219\u4ed6\u7684\u7434\u97f3\u4f55\u4ee5\u7adf\u80fd\u5982\u6b64\u5408\u62cd\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u82e5\u6709\u6240\u601d\uff0c\u821e\u5251\u5c31\u672a\u80fd\u4e13\u6ce8\uff0c\u6b64\u65f6\u4ed6\u6b63\u4f7f\u5230\u6536\u5251\u4e4b\u524d\u7684\u4e00\u62db\u201c\u6a2a\u5377\u516d\u4ee4\u201d\uff0c\u8fd9\u4e00 \u62db\u5251\u672f\u662f\u8981\u4f7f\u5f97\u975e\u5e38\u7ef5\u5bc6\u7684\uff0c\u4ed6\u6025\u4e8e\u6536\u5f0f\uff0c\u4f7f\u5f97\u5feb\u4e86\u4e00\u4e9b\uff0c\u90a3\u5c11\u5e74\u5ffd\u5730\u6293\u8d77\u4e86\u4e00\u628a\u77f3\u5b50\uff0c\u5411 \u4ed6\u4e00\u6d12\u3002 \u3000\u3000\u53ea\u542c\u5f97\u53ee\u53ee\u549a\u549a\u8fd9\u58f0\uff0c\u5b9b\u5982\u7e41\u5f26\u6025\u594f\uff0c\u90a3\u4e00\u628a\u77f3\u5b50\u5728\u5251\u5149\u5708\u4e2d\u5316\u6210\u4e86\u7c89\u5c51\uff0c\u4f46\u6709\u4e00\u679a\u5c0f \u5c0f\u7684\u77f3\u5b50\u7a7f\u9699\u800c\u8fdb\uff0c\u6253\u4e2d\u4e86\u91d1\u9042\u6d41\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u6536\u5f0f\u3002\u8fd9\u4e00\u679a\u5c0f\u5c0f\u7684\u77f3\u5b50\uff0c\u5bf9\u4ed6\u6beb\u65e0\u4f24\u5bb3\uff0c\u4ee4\u4ed6\u5403\u60ca\u7684\u662f\uff0c\u4ed6\u7684 \u5251\u6cd5\u53ea\u662f\u7a0d\u9732\u7834\u7efd\uff0c\u4f7f\u7ed9\u8fd9\u5c11\u5e74\u770b\u4e86\u51fa\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u6536\u5f0f\uff0c\u53ea\u542c\u5f97\u8fd9\u5c11\u5e74\u7b11\u9053\uff0c\u201c\u521a\u624d\u662f\u6211\u9519\u4e86\uff0c\u8fd9\u4e00\u6b21\u5374\u6050\u6015\u662f\u4f60\u9519\u4e86\uff01\u201d \u3000\u3000\u5168\u9010\u6d41\u54c8\u54c8\u4e00\u7b11\uff0c\u6536\u4e86\u5251\u5f0f\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u5144\u53f0\u6cd5\u773c\uff0c\u660e\u9274\u79cb\u6beb\uff0c\u5c0f\u5f1f\u597d\u751f\u4f69\u670d\u3002\u539f\u6765 \u5144\u53f0\u4e5f\u662f\u4e2a\u5251\u672f\u7684\u5927\u884c\u5bb6\uff0c\u5374\u4e0d\u77e5\u5bb6\u5e08\u662f\u54ea\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u5927\u884c\u5bb6\u554a\uff1f\u6211\u8fd9\u4e0d\u8fc7\u662f\u5bb6\u4f20\u7684\u51e0\u624b\u4e09\u811a\u732b\u529f\u592b\u800c\u5df2\u3002\u6211\u662f\u6700\u4e0d\u4f1a\u5ba2\u6c14 \u7684\uff0c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u4f60\u7684\u7434\u6280\u6bd4\u6211\u7a0d\u6709\u4e0d\u5982\uff0c\u4f60\u7684\u5251\u672f\u5374\u662f\u6bd4\u6211\u9ad8\u660e\u591a\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60ca\u7591\u4e0d\u5b9a\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u5957\u5927\u987b\u5f25\u5f0f\u662f\u7239\u7239\u4ece\u5929\u5c71\u5251\u6cd5\u4e4b\u4e2d\u53d8\u5316\u51fa\u6765\u7684\uff0c \u5185\u4e2d\u8fd8\u63c9\u5408\u4e86\u4e54\u7956\u5e08\u7684\u79d8\u7b08\u4e2d\u7684\u62db\u6570\uff0c\u96be\u9053\u4ed6\u5bb6\u4f20\u7684\u5251\u672f\u7adf\u7136\u4e0e\u6211\u7239\u7239\u6240\u521b\u7684\u4e0d\u8c0b\u800c\u5408\uff1f\u201d \u4f46\u523a\u63a2\u522b\u4eba\u6b66\u5b66\u7684\u79d8\u5bc6\u4e43\u662f\u6c5f\u6e56\u7684\u7981\u5fcc\u4e4b\u4e00\uff0c\u662f\u4ee5\u91d1\u9010\u6d41\u867d\u6709\u6240\u7591\u3001\u77e5\u4e5f\u4e0d\u4fbf\u8ffd\u95ee\u4e0b\u53bb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u89c9\u5f97\u8fd9\u5c11\u5e74\u7684\u6027\u60c5\u548c\u81ea\u5df1\u5f88\u662f\u6295\u5408\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u8c2c\u627f\u543e\u5144\u4ee5\u77e5\u97f3\u76f8\u8bb8\uff0c\u82e5\u8499\u4e0d \u5f03\uff0c\u54b1\u4eec\u5c31\u7ed3\u4e3a\u5f02\u59d3\u5144\u5f1f\u5982\u4f55\uff1f\u201d\u5c0f\u5f1f\u59d3\u91d1\uff0c\u540d\u9010\u6d41\u3002\u4eca\u5e74\u521a\u6ee1\u4e8c\u5341\u3002\u201d \u3000\u3000\u5c11\u5e74\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u91d1\u2014\u2014\u9010\u6d41\uff1f\u6709\u4f4d\u540d\u6ee1\u5929\u4e0b\u7684\u91d1\u4e16\u9057\u5927\u4fa0\uff0c\u4e0d\u77e5\u662f\u91d1\u5144\u4f55\u4eba\uff1f\u201d \u91d1\u9010\u6d41\u9053\uff1a\u201c\u6b63\u662f\u5bb6\u7236\u3002\u201d\u5c11\u5e74\u9762\u8272\u5fae\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u6b64\uff0c\u6211\u53ef\u662f\u9ad8\u6500\u4e0d\u8d77\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u7b11\u9053\uff1a\u201c\u4f60\u521a\u624d\u8fd8\u8d23\u5907\u6211\u6709\u4e16\u4fd7\u4e4b\u89c1\uff0c\u600e\u7684\u4f60\u4e5f\u8bf4\u51fa\u8fd9\u7b49\u8bdd\u6765\uff1f\u6211\u7684\u7239\u7239\u662f\u4e2a \u540d\u6ee1\u5929\u4e0b\u7684\u5927\u4fa0\uff0c\u6211\u5374\u53ea\u662f\u4e2a\u4e0d\u89c1\u7ecf\u4f20\u7684\u5c0f\u53eb\u5316\uff01\u201d \u3000\u3000\u5c11\u5e74\u4e0d\u7981\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u8001\u5f1f\uff0c\u4f60\u771f\u6709\u610f\u601d\uff0c\u60f3\u4e0d\u5230\u4f60\u6211\u4e00\u89c1\u5982\u6545\uff0c\u77e5\u5df1\u96be\u6c42\uff0c \u6211\u662f\u975e\u548c\u4f60\u7ed3\u4ea4\u4e0d\u53ef\u4e86\u3002\u6211\u59d3\u674e\u540d\u5357\u661f\uff0c\u4eca\u5e74\u4e8c\u5341\u6709\u4e8c\uff0c\u6bd4\u4f60\u5927\u4e24\u5c81\uff0c\u6211\u4e0d\u5ba2\u6c14\uff0c\u53eb\u4f60\u4e00\u58f0 \u5c0f\u8001\u5f1f\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u4e3a\u6b22\u559c\uff0c\u5f53\u4e0b\u5728\u57ce\u5899\u4e0a\u64ae\u571f\u4e3a\u9999\uff0c\u4e24\u4eba\u76f8\u5bf9\u62dc\u4e86\u516b\u62dc\uff0c\u7ed3\u4e3a\u5f02\u59d3\u5144\u5f1f\uff0c\u91d1\u9010\u6d41 \u53eb\u4e86\u4e00\u58f0\u2018\u5927\u54e5\u2019\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5927\u54e5\u7684\u540d\u5b57\uff0c\u6211\u53ef\u4ece\u6765\u6ca1\u6709\u542c\u4eba\u8bf4\u8fc7\u3002\u6c5f\u5e08\u5144\u662f\u6700\u559c\u6b22\u540e \u8d77\u4e4b\u79c0\u7684\uff0c\u95ee\u4ed6\u6216\u8005\u53ef\u80fd\u77e5\u9053\u3002\u201d \u3000\u3000\u6b64\u65f6\u5df2\u662f\u65e5\u5f71\u897f\u7eda\uff0c\u91d1\u9010\u6d41\u662f\u51c6\u5907\u660e\u65e5\u53bb\u95ef\u8428\u603b\u7ba1\u7684\u5bff\u5802\u7684\uff0c\u5fc5\u987b\u65e9\u4e9b\u56de\u53bb\uff0c\u4e8e\u662f\u5411\u674e \u5357\u661f\u9053\u4e86\u4e2a\u6b49\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u4f4f\u5728\u76ae\u5e3d\u80e1\u540c\u4e00\u4f4d\u59d3\u6234\u7684\u670b\u53cb\u5bb6\u91cc\uff0c\u5927\u54e5\u82e5\u662f\u6709\u7a7a\uff0c\u8fc7\u4e24\u5929\u8bf7 \u6765\u4e00\u805a\uff01 \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6709\u4e8b\u4f60\u5148\u8d70\u5427\u3002\u6211\u8fd8\u60f3\u591a\u73a9\u4e00\u4f1a\u3002\u201d\u91d1\u9010\u6d41\u544a\u8bc9\u4e86\u4ed6\u7684\u5730\u5740\uff0c\u4ed6\u5374 \u6ca1\u6709\u628a\u81ea\u5df1\u7684\u5730\u5740\u544a\u8bc9\u91d1\u9010\u6d41\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6b63\u8981\u8d70\u4e0b\u53bb\uff0c\u674e\u5357\u661f\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u8001\u5f1f\u56de\u6765\uff0c\u5509\uff0c\u4f60\u8fd9\u4eba\u600e\u4e48\u8fd9\u6837\u7c97\u5fc3\u5927\u610f\uff01\u201d \u6b63\u662f\uff1a \u3000\u3000\u7434\u5251\u76f8\u4ea4\u6d51\u8131\u4fd7\uff0c\u5c11\u5e74\u610f\u6c14\u559c\u76f8\u6295\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u5341\u516b\u56de\u3000\u5f02\u5b9d\u7eb7\u9648\u62db\u5de8\u76d7\u3000\u534e\u5802\u55a7\u95f9\u73b0\u4f73\u4eba \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u5341\u516b\u56de\u3000\u5f02\u5b9d\u7eb7\u9648\u62db\u5de8\u76d7\u3000\u534e\u5802\u55a7\u95f9\u73b0\u4f73\u4eba \u3000\u3000\u91d1\u9010\u6d41\u5f81\u4e86\u4e00\u6014\uff0c\u6115\u7136\u56de\u987e\uff0c\u674e\u5357\u661f\u7b11\u9053\uff1a\u201c\u8fd9\u5f20\u7434\u4f60\u5fd8\u4e86\u5e26\u4e86\u3002\u201d\u91d1\u9010\u6d41\u6b49\u7136\u8bf4\u9053\uff1a \u201c\u4e0d\u662f\u5c0f\u5f1f\u4e0d\u770b\u91cd\u5927\u54e5\u7684\u793c\u7269\uff0c\u6b63\u56e0\u4e3a\u8fd9\u662f\u7a00\u4e16\u4e4b\u5b9d\uff0c\u6240\u4ee5\u5c0f\u5f1f\u2026\u2026\u201d\u674e\u5357\u661f\u602b\u7136\u4e0d\u60a6\uff0c\u8bf4 \u9053\uff1a\u201c\u8fd9\u5f20\u53e4\u7434\u96be\u9053\u6bd4\u54b1\u4eec\u7684\u4ea4\u60c5\u66f4\u5b9d\u8d35\u4e48\uff1f\u6211\u9001\u51fa\u7684\u4e1c\u897f\u662f\u51b3\u4e0d\u6536\u56de\u7684\uff0c\u4f60\u82e6\u5acc\u5f03\uff0c\u6211\u5c31 \u628a\u5b83\u6253\u788e\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u614c\u5fd9\u63a5\u8fc7\u53e4\u7434\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u4e0d\u8981\u751f\u6c14\uff0c\u5c0f\u5f1f\u62dc\u9886\u5c31\u662f\u3002\u5176\u5b9e\u2026\u2026\u201d\u5176\u4e70\u91d1\u9010\u6d41 \u5e76\u975e\u6545\u610f\u5ba2\u6c14\uff0c\u53ea\u56e0\u7434\u592a\u8fc7\u5b9d\u8d35\uff0c\u4ed6\u4e34\u8d70\u65f6\u5fc3\u795e\u53c8\u6709\u70b9\u604d\u60da\uff0c\u4e00\u65f6\u5fd8\u8bb0\u4e86\u8fd9\u5f20\u7434\u662f\u674e\u5357\u661f\u5df2 \u7ecf\u9001\u7ed9\u4ed6\u7684\u4e86\u3002 \u3000\u3000\u674e\u6d77\u661f\u9053\uff1a\u201c\u5176\u5b9e\u4ec0\u4e48\uff1f\u201d\u91d1\u9010\u6d41\u4e0d\u613f\u7ec6\u52a0\u89e3\u91ca\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u5c0f\u5f1f\u53ea\u662f\u60f3\u8d77\u4e00\u4ef6 \u5fc3\u4e8b\u3002\u201d\u674e\u5357\u661f\u9053\uff1a\u201c\u4ec0\u4e48\u5fc3\u4e8b\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u62cd\u4e00\u62cd\u5251\u9798\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\u6211\u8fd9\u628a\u9752\u94a2\u5251\u4e0d\u662f\u5b9d\u5251\uff0c\u4f46\u6211\u4e00\u5b9a\u8981\u9001\u5927\u54e5\u4e00\u628a\u5b9d \u5251\uff01\u201d\u674e\u5357\u661f\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u6211\u9001\u4f60\u4e1c\u897f\u662f\u56fe\u4f60\u56de\u62a5\u7684\u4e48\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u6837\u8bf4\uff0c\u8fd9\u53ea \u662f\u5404\u5c3d\u5fc3\u613f\u800c\u5df2\u3002\u4f60\u56e0\u4e3a\u6211\u542c\u5f97\u61c2\u4f60\u7684\u7434\u97f3\uff0c\u9001\u6211\u53e4\u7434\uff1b\u6211\u4e5f\u8ba4\u4e3a\u4f60\u662f\u6211\u5251\u672f\u4e0a\u7684\u2018\u77e5 \u97f3\u2019\uff0c\u6240\u4ee5\u6211\u975e\u9001\u4f60\u4e00\u628a\u5b9d\u5251\u4e0d\u53ef\uff01\u6211\u628a\u8bdd\u8bf4\u5728\u524d\u5934\uff0c\u5c06\u6765\u4f60\u82e5\u4e0d\u80af\u63a5\u53d7\u6211\u7684\u5b9d\u5251\uff0c\u7528\u4f60\u7684 \u8bdd\u6765\u8bf4\uff0c\u90a3\u4e5f\u5c31\u662f\u770b\u8f7b\u4e86\u6211\u7684\u4ea4\u60c5\u4e86\u3002\u201d \u3000\u3000\u674e\u6d77\u661f\u5fc3\u91cc\u6697\u7b11\uff1a\u201c\u4e00\u628a\u8fd8\u4e0d\u77e5\u662f\u5728\u54ea\u91cc\u7684\u5b9d\u5251\uff0c\u5374\u8bf4\u5f97\u5982\u6b64\u90d1\u91cd\u3002\u201d\u867d\u7136\u5982\u6b64\uff0c\u4f46\u4ed6 \u4e5f\u5f88\u611f\u6fc0\u91d1\u9010\u6d41\u7684\u8bda\u610f\uff0c\u4e8e\u662f\u4e5f\u4f5c\u51fa\u90d1\u91cd\u5176\u4e8b\u7684\u795e\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211\u5148\u5411\u8001\u5f1f\u9053\u8c22 \u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6ee1\u6000\u6b22\u559c\uff0c\u643a\u4e86\u53e4\u7434\uff0c\u7acb\u5373\u8d76\u56de\u4eac\u57ce\uff0c\u5e78\u597d\u57ce\u8fd8\u672a\u5173\u95ed\u3002\u56de\u5230\u6234\u5bb6\uff0c\u5df2\u8db3\u9ec4\u660f\u65f6 \u5206\uff0c\u6234\u5747\u7236\u5b50\u6b63\u5728\u7b49\u4ed6\u5403\u665a\u996d\u3002 \u3000\u3000\u6234\u5747\u9053\uff1a\u201c\u4f60\u53bb\u4e86\u54ea\u91cc\u4e00\u6574\u5929\uff1f\u201d\u91d1\u9010\u6d41\u9053\u4e86\u4e2a\u6b49\uff0c\u9053\uff1a\u201c\u6211\u5230\u4e07\u91cc\u957f\u57ce\u73a9\u800d\uff0c\u4ea4\u4e86\u4e00 \u4f4d\u670b\u53cb\uff0c\u56de\u6765\u8fdf\u4e86\u3002\u8fd9\u5f20\u53e4\u7434\uff0c\u5c31\u662f\u90a3\u5e55\u670b\u53cb\u9001\u7684\u3002\u201d \u3000\u3000\u6234\u5747\u4e0d\u61c2\u5f97\u53e4\u7434\u7684\u5b9d\u8d35\uff0c\u5374\u62c5\u5fc3\u4ed6\u95f9\u51fa\u4e71\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u5c11\u5e74\u4eba\u559c\u6b22\u73a9\u800d\u6211\u4e0d\u602a\u4f60\uff0c\u4f55\u51b5 \u4f60\u662f\u521d\u5230\u5317\u4eac\u3002\u4e0d\u8fc7\uff0c\u660e\u5929\u5c31\u662f\u8428\u798f\u9f0e\u7684\u5bff\u8fb0\u3002\u4e09\u5c71\u4e94\u5cb3\u7684\u4eba\u9a6c\u90fd\u4f1a\u5f00\u96c6\u4e50\u90e8\uff0c\u6211\u5e0c\u671b\u4f60\u8fd8 \u662f\u5c0f\u5fc3\u4e00\u70b9\u7684\u597d\u3002\u4e0d\u77e5\u9053\u5e95\u7ec6\u7684\u670b\u53cb\uff0c\u8fd9\u51e0\u5929\u6682\u65f6\u4e0d\u7ed3\u4ea4\u4e5f\u7f62\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u591a\u8c22\u8001\u524d\u8f88\u91d1\u7389\u826f\u8a00\uff0c\u4e0d\u8fc7\u8fd9\u4f4d\u670b\u53cb\u809d\u80c6\u7167\u4eba\uff0c\u5374\u662f\u53ef\u4ee5\u653e\u5fc3\u7684\u3002\u201d\u6234\u5747 \u9053\uff1a\u201c\u4f60\u7684\u89c1\u8bc6\u6211\u662f\u76f8\u4fe1\u5f97\u8fc7\u7684\uff0c\u6211\u4e5f\u662f\u5f88\u60f3\u4f60\u591a\u7ed3\u4ea4\u51e0\u4e2a\u670b\u53cb\uff0c\u53ea\u662f\u6211\u5e0c\u671b\u4f60\u8fd9\u51e0\u5929\u7a0d\u52a0 \u8c28\u614e\u7f62\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5403\u8fc7\u665a\u996d\uff0c\u8bf4\u9053\uff1a\u201c\u6234\u8001\u524d\u8f88\uff0c\u4f60\u662f\u8001\u5317\u4eac\u4e86\uff0c\u4eac\u4e2d\u7684\u4e09\u6559\u4e5d\u6d41\u4eba\u7269\uff0c\u60f3\u6765\u4f60\u90fd \u6709\u7ed3\u4ea4\u5427\uff1f\u201d\u6234\u5747\u62c8\u987b\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u4f60\u8981\u6253\u542c\u4ec0\u4e48\u4eba\uff1f\u5317\u4eac\u57ce\u4e2d\uff0c\u53ea\u8981\u662f\u7a0d\u5fae\u6709\u70b9\u540d\u6c14\u7684\uff0c \u5927\u7ea6\u6211\u603b\u4f1a\u77e5\u9053\uff1a\u201c \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u6709\u4e00\u5757\u7384\u94c1\uff0c\u60f3\u8bf7\u771f\u6709\u672c\u4e8b\u7684\u94f8\u5251\u5e08\u94f8\u4e00\u628a\u5b9d\u5251\u3002\u4e0d\u77e5\u5317\u4eac\u57ce\u91cc\u54ea\u4f4d\u94f8 \u5251\u5e08\u6700\u51fa\u540d\u3002\u201d\u6234\u5747\u7684\u513f\u5b50\u6234\u8c1f\u7b2c\u4e00\u6b21\u542c\u5f97\u201c\u7384\u94c1\u201d\u4e4b\u540d\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u53eb\u505a\u7384\u94c1\uff1f\u201d \u3000\u3000\u6234\u5747\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u636e\u8bf4\u7384\u94c1\u53ea\u51fa\u4ea7\u5728\u6606\u4ed1\u5c71\u9876\u7684\u661f\u5bbf\u6d77\uff0c\u6bd4\u5bfb\u5e38\u7684\u94c1\u8981\u91cd\u5341\u500d\uff0c \u60f3\u4e0d\u5230\u8001\u5f1f\u7ade\u6709\u8fd9\u79cd\u7a00\u4e16\u4e4b\u5b9d\u3002\u5317\u4eac\u57ce\u91cc\u6700\u8457\u540d\u7684\u94f8\u5251\u5e08\u6050\u6015\u4e5f\u4e0d\u914d\u7ed9\u4f60\u94f8\u8fd9\u628a\u5251\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u4e3a\u5931\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u82e5\u662f\u627e\u4e0d\u5230\u94f8\u5251\u7684\u9ad8\u624b\uff0c\u867d\u6709\u5b9d\u7269\uff0c\u4ea6\u662f\u65e0\u7528\u3002\u201d \u3000\u3000\u6234\u5747\u8bf4\u9053\uff1a\u201c\u5f85\u6211\u60f3\u4e00\u60f3\u3002\u201d\u534a\u664c\u8bf4\u9053\uff1a\u201c\u6211\u5fc3\u76ee\u4e2d\u6709\u4e00\u4e2a\u4eba\u53ef\u4ee5\u7ed9\u4f60\u94f8\u5251\uff0c\u4f46\u4ed6\u5374\u4e0d \u662f\u4ee5\u94f8\u5251\u4e3a\u4e1a\u7684\u3002\u51ed\u7740\u6211\u7684\u8001\u9762\u5b50\u6c42\u4ed6\uff0c\u6216\u8005\u4ed6\u53ef\u4ee5\u5e94\u627f\u3002\u53ef\u60dc\u76ee\u524d\u6211\u4e0d\u80fd\u51fa\u95e8\uff0c\u53ea\u6709\u7b49\u6211 \u907f\u8fc7\u4e86\u8fd9\u573a\u707e\u96be\u518d\u66ff\u8001\u5f1f\u8bbe\u6cd5\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u4e0a\u4e00\u5757\u77f3\u5934\u843d\u4e86\u5730\uff0c\u60f3\u9053\uff1a\u201c\u5f85\u6211\u94f8\u6210\u4e86\u5b9d\u5251\uff0c\u9001\u7ed9\u5927\u54e5\uff0c\u4e5f\u597d\u62a5\u7b54\u4ed6\u8d60\u7434\u4e4b \u5fb7\u3002\u201d\u4e8e\u662f\u90d1\u91cd\u7684\u62dc\u6258\u4e86\u6234\u5747\uff0c\u4f7f\u5373\u56de\u623f\u6b47\u606f\u3002 \u3000\u3000\u4e00\u5bbf\u65e0\u8bdd\uff0c\u7b2c\u4e8c\u5929\u91d1\u9010\u6d41\u4e00\u65e9\u8d77\u6765\uff0c\u5148\u7528\u201c\u6613\u5bb9\u4e39\u201d\u628a\u81ea\u5df1\u7684\u5bb9\u8c8c\u6539\u53d8\uff0c\u8fd9\u79cd\u201c\u6613\u5bb9 \u4e39\u201d\u5176\u5b9e\u5373\u662f\u53e4\u4ee3\u7684\u5316\u88c5\u54c1\uff0c\u53ef\u4ee5\u6539\u53d8\u80a4\u8272\uff0c\u4f46\u4e0d\u80fd\u6539\u53d8\u9762\u578b\u3002\u4e0d\u8fc7\u82e5\u662f\u5316\u88c5\u7684\u6280\u672f\u9ad8\u660e\uff0c \u7528\u4e0a\u4e86\u201c\u6613\u5bb9\u4e39\u201d\u4e5f\u53ef\u4ee5\u9690\u7792\u81ea\u5df1\u672c\u6765\u7684\u9762\u76ee\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6709\u59ec\u6653\u98ce\u9001\u4ed6\u7684\u5341\u9897\u6613\u5bb9\u4e39\uff0c\u59ec\u6653\u98ce\u5f53\u7136\u4e5f\u6559\u4f1a\u4e86\u4ed6\u5316\u88c5\u7684\u6cd5\u5b50\uff0c\u91d1\u9010\u6d41\u9009\u4e86\u4e00 \u9897\u53ef\u4ee5\u5316\u88c5\u6210\u4e2d\u5e74\u4eba\u7684\u201c\u6613\u5bb9\u4e39\u201d\u6d82\u4e0a\u9762\u5b54\uff0c\u628a\u672c\u6765\u662f\u767d\u7389\u822c\u7684\u4e00\u5f20\u8138\u53d8\u6210\u5fae\u5e26\u8721\u9ec4\uff0c\u7136\u540e \u7c98\u4e0a\u4e24\u6487\u5c0f\u80e1\u5b50\uff0c\u5bf9\u955c\u4e00\u7167\uff0c\u679c\u7136\u50cf\u662f\u4e2a\u56db\u5341\u6765\u5c81\u7684\u3001\u666e\u666e\u901a\u901a\u7684\u6beb\u65e0\u7279\u5f81\u7684\u4e2d\u5e74\u4eba\u3002 \u3000\u3000\u6234\u5747\u7236\u5b50\u6b63\u5728\u996d\u5385\u7b49\u91d1\u9010\u6d41\u6765\u5403\u65e9\u9910\uff0c\u5ffd\u7136\u770b\u89c1\u4e00\u4e2a\u201c\u964c\u751f\u4eba\u201d\u8fdb\u6765\uff0c\u6234\u8c1f\u5927\u5403\u4e00\u60ca \u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u662f\u6211\uff01\u201d\u6234\u5747\u9053\uff1a\u201c\u91d1\u8001\u5f1f\uff0c\u4f60\u7684\u5bb9\u8c8c\u624b\u6bb5\u662f\u5f88\u9ad8\u660e\u4e86\uff0c \u53ef\u60dc\u58f0\u97f3\u672a\u6539\uff0c\u8fd8\u5e94\u8be5\u82cd\u8001\u4e00\u4e9b\uff0c\u624d\u50cf\u662f\u4e2a\u4e0a\u4e86\u5e74\u7eaa\u7684\u4eba\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u591a\u8c22\u6307\u6559\u3002\u201d\u52b2\u6c14\u5185\u655b\uff0c\u8bf4\u51fa\u8bdd\u6765\uff0c\u679c\u7136\u6709\u4e86\u51e0\u5206\u82cd\u8001\u7684\u5473\u9053\u3002\u6234\u5747\u9053\uff1a \u201c\u8001\u5f1f\u6539\u5bb9\u6613\u8c8c\u4e3a\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u60f3\u51fa\u53bb\u8d70\u8d70\u3002\u201d\u6234\u6eaa\u9053\uff1a\u201c\u4eca\u65e5\u53ef\u6b63\u662f\u8428\u798f\u9f0e\u7684\u5bff\u8fb0\u5462\uff01\u201d\u91d1\u9010\u6d41\u9053\uff1a \u201c\u6211\u5c31\u662f\u4e3a\u4e86\u8fd9\u4e2a\u7f18\u6545\u624d\u5982\u6b64\u6253\u626e\u7684\uff0c\u5373\u4f7f\u78b0\u4e0a\u719f\u4eba\uff0c\u5927\u7ea6\u4e5f\u4e0d\u4f1a\u8ba4\u5f97\u6211\u4e86\u3002\u201d\u6234\u5747\u9053\uff1a \u201c\u4eca\u65e5\u6682\u4e14\u7559\u5728\u5bb6\u91cc\u4e00\u5929\u4e0d\u884c\u4e48\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u65e9\u4e0e\u670b\u53cb\u6709\u7ea6\uff0c\u4e0d\u4fbf\u4e34\u65f6\u66f4\u6539\u3002\u201d\u91d1\u9010 \u6d41\u4e3a\u4e86\u6015\u4ed6\u4eec\u7236\u5b50\u62c5\u5fc3\uff0c\u4e0d\u6562\u8bf4\u51fa\u5b9e\u8bdd\u3002 \u3000\u3000\u6234\u5747\u542c\u5f97\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u4e0d\u4fbf\u518d\u52a0\u963b\u6b62\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\u672c\u9886\u9ad8\u5f3a\uff0c\u53c8\u6539\u53d8\u4e86\u5bb9\u8c8c\uff0c\u60f3\u4e0d \u81f3\u4e8e\u51fa\u751a\u5c94\u5b50\uff0c\u4e0d\u8fc7\u603b\u8fd8\u662f\u5c0f\u5fc3\u4e00\u70b9\u7684\u597d\u3002\u201d\u91d1\u9010\u6d41\u5e94\u4e86\u4e00\u4e2a\u201c\u662f\u201d\u5b57\uff0c\u5306\u5306\u5403\u8fc7\u65e9\u9910\uff0c\u4fbf \u5411\u6234\u5747\u544a\u8f9e\u3002 \u3000\u3000\u6234\u5747\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u53ef\u80fd\u6709\u4f4d\u5ba2\u4eba\u8981\u6765\uff0c\u91d1\u8001\u5144\u8001\u662f\u6ca1\u6709\u522b\u7684\u4e8b\u60c5\uff0c\u4f1a\u8fc7\u4e86\u8d35 \u5ba2\uff0c\u8bf7\u65e9\u4e00\u70b9\u56de\u6765\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8001\u4f2f\u4e0d\u5fc5\u6302\u5ff5\uff0c\u6211\u5c3d\u5feb\u56de\u6765\u5c31\u662f\u3002\u201d\u51fa\u4e86\u6234\u5bb6\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4eca\u65e5\u53f2\u767d\u90fd \u662f\u4e00\u5b9a\u8981\u53bb\u7ed9\u8428\u798f\u9f0e\u8d3a\u5bff\u7684\uff0c\u4e01\u5f6d\u662f\u4ed6\u624b\u4e0b\u7684\u4e00\u4e2a\u5934\u76ee\uff0c\u5373\u4f7f\u6ca1\u8d44\u683c\u966a\u53f2\u767d\u90fd\u7684\u5f80\u8d3a\u5bff\uff0c\u4ed6 \u6ca1\u6709\u5e2e\u4e3b\u6491\u8170\uff0c\u5355\u72ec\u4e00\u4eba\u4e5f\u51b3\u4e0d\u6562\u6765\u6234\u5bb6\u5bfb\u4ec7\u3002\u6234\u5747\u8bf4\u7684\u90a3\u4f4d\u5ba2\u4eba\u4e0d\u77e5\u662f\u8c01\uff1f\u4e0d\u8fc7\uff0c\u60f3\u6765\u603b \u4e0d\u4f1a\u662f\u6307\u53f2\u767d\u90fd\u548c\u4e01\u5f6d\u4e86\u3002\u201d \u3000\u3000\u6234\u5747\u8fd9\u6b21\u529b\u6c42\u907f\u7978\uff0c\u5e76\u6ca1\u6709\u6c42\u8fc7\u91d1\u9010\u6d41\u5e2e\u5fd9\uff0c\u4f46\u91d1\u9010\u6d41\u5374\u662f\u6253\u7b97\u5e2e\u4ed6\u7684\u5fd9\u7684\u3002\u4ed6\u6240\u987e\u8651 \u7684\u53ea\u662f\u53f2\u767d\u90fd\u6765\u5230\u6234\u5bb6\uff0c\u65e2\u7136\u7b97\u51c6\u4e86\u53f2\u767d\u90fd\u4eca\u5929\u51b3\u65e0\u524d\u6765\u6234\u5bb6\u4e4b\u7406\uff0c\u4e5f\u5c31\u653e\u5fc3\u5730\u8d70\u4e86\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u4f1a\uff0c\u91d1\u9010\u6d41\u84e6\u5730\u60f3\u8d77\u4e00\u4e8b\uff1a\u201c\u8428\u798f\u9f0e\u662f\u5927\u5185\u603b\u7ba1\uff0c\u4eca\u65e5\u505a\u5bff\uff0c\u8d3a\u5ba2\u76c8\u95e8\uff0c\u90a3\u662f\u5fc5 \u7136\u7684\u4e86\u3002\u4e0d\u8fc7\uff0c\u6050\u6015\u4e5f\u4e0d\u662f\u4efb\u4f55\u4eba\u90fd\u6df7\u5f97\u8fdb\u53bb\u7684\u5427\uff1f\u82e5\u662f\u6709\u4eba\u67e5\u95ee\uff0c\u6211\u600e\u4e48\u5e94\u4ed8\u5462\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u5730\u770b\u89c1\u524d\u5934\u6709\u4e24\u4e2a\u4eba\uff0c\u4e00\u770b\u4ed6\u4eec\u7684\u6253\u626e\u5c31\u77e5\u662f\u6c5f\u6e56\u4eba\u7269\u3002\u91d1\u9010\u6d41\u7075\u673a\u4e00 \u52a8\uff0c\u8d70\u4e0a\u53bb\u9053\uff1a\u201c\u4e24\u4f4d\u53ef\u662f\u5f80\u8428\u5e9c\u8d3a\u5bff\u4e48\uff1f\u201d \u3000\u3000\u90a3\u4e24\u4eba\u56de\u8fc7\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u54e5\u662f\u54ea\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5c0f\u5f1f\u662f\u72ec\u811a\u5f00\u6252\u548c \u4e00\u4f4d\u59d3\u6587\u7684\u670b\u53cb\u76f8\u8bc6\uff0c\u8fd9\u4f4d\u670b\u53cb\u548c\u8428\u603b\u7ba1\u5f88\u6709\u4ea4\u60c5\uff0c\u627f\u4ed6\u76f8\u9080\uff0c\u6545\u6b64\u6211\u4eca\u65e5\u4e5f\u6765\u51d1\u70ed\u95f9\u3002\u201d \u3000\u3000\u90a3\u4e24\u4eba\u9732\u51fa\u7fa1\u6155\u795e\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8fd9\u4f4d\u6587\u670b\u53cb\u6562\u60c5\u5c31\u662f\u6587\u9053\u5e84\u4e48\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a \u201c\u6b63\u662f\uff0c\u4e24\u4f4d\u53ef\u662f\u4e0e\u4ed6\u76f8\u8bc6\uff1f\u201d \u3000\u3000\u90a3\u4e24\u4eba\u9053\uff1a\u201c\u6211\u4eec\u9ad8\u6500\u4e0d\u4e0a\u3002\u8001\u54e5\u9ad8\u59d3\u5927\u540d\uff1f\u201d\u91d1\u9010\u6d41\u80e1\u4e71\u634f\u4e86\u4e00\u4e2a\u5047\u540d\u8bf4\u4e86\uff0c\u8ddf\u7740\u5411 \u90a3\u4e24\u4eba\u8bf7\u6559\uff0c\u59cb\u77e5\u9ad8\u4e2a\u5b50\u540d\u53eb\u5f20\u5b8f\uff0c\u77ee\u4e2a\u5b50\u540d\u53eb\u674e\u58ee\u3002 \u3000\u3000\u5f20\u5b8f\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7684\u9760\u5c71\u6ca1\u6709\u8001\u5f1f\u7684\u786c\uff0c\u8428\u5e9c\u6709\u4f4d\u59d3\u94b1\u7684\u6267\u4e8b\u548c\u6211\u4eec\u4ee5\u524d\u66fe\u7ecf\u5728\u4e00\u8d77\u6df7 \u8fc7\u7684\uff0c\u627f\u4ed6\u7684\u60c5\uff0c\u6211\u4eec\u624d\u8ba8\u5f97\u4e24\u5f20\u8bf7\u5e16\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u4e2d\u4e00\u51db\uff1a\u201c\u679c\u7136\u662f\u8981\u6709\u8bf7\u5e16\u7684\u3002\u201d\u95ee\u9053\uff1a\u201c\u4e0d\u77e5\u4e24\u4f4d\u53c8\u662f\u4ec0\u4e48\u5e2e\u6d3e\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u8bf4\u9053\uff1a\u201c\u50cf\u8001\u5144\u4e00\u6837\uff0c\u6211\u4eec\u4e5f\u90fd\u662f\u72ec\u811a\u5f00\u6252\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u542c\u8bf4\u6709\u8bb8\u591a\u4f4d\u95fb\u540d\u6c5f\u6e56\u7684\u5e2e\u4f1a\u9996\u8111\u4eca\u65e5\u90fd\u8981\u6765\u7684\uff0c\u60f3\u5fc5\u4f1a\u5e26\u4e86\u4e0d\u5c11\u4eba\u6765 \u5427\u3002\u201d \u3000\u3000\u674e\u58ee\u9053\uff1a\u201c\u662f\u5440\uff0c\u542c\u8bf4\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\u53f2\u767d\u90fd\uff0c\u6d77\u7802\u5e2e\u7684\u5e2e\u4e3b\u6c99\u5343\u5cf0\uff0c\u9752\u9f99\u5e2e\u7684\u5e2e\u4e3b\u9ad8\u5927 \u6210\uff0c\u767d\u864e\u5e2e\u7684\u5e2e\u4e3b\u675c\u5927\u4e1a\uff0c\u5168\u90fd\u6765\u4e86\u3002\u53ea\u662f\u8fd9\u56db\u5927\u5e2e\u4f1a\uff0c\u6050\u6015\u5c31\u6709\u51e0\u5341\u4e2a\u4eba\u8ddf\u968f\u4ed6\u4eec\u7684\u5e2e\u4e3b \u6765\u8d3a\u5bff\u5462\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4eca\u5929\u6765\u8d3a\u5bff\u7684\u5ba2\u4eba\u8fd9\u4e48\u591a\uff0c\u4e0d\u6015\u6709\u610f\u6b32\u56fe\u8c0b\u4e0d\u8f68\u7684\u4eba\u6df7\u8fdb\u53bb\u5417\uff1f\u201d \u3000\u3000\u674e\u58ee\u7b11\u9053\uff1a\u201c\u653e\u5fc3\u597d\u4e86\uff0c\u4e0d\u4f1a\u6709\u7684\u3002\u5404\u5e2e\u4f1a\u7684\u4eba\u6709\u4ed6\u4eec\u7684\u5e2e\u4e3b\u5e26\u9886\uff0c\u50cf\u54b1\u4eec\u8fd9\u4e9b\u5355\u72ec\u9080 \u8bf7\u7684\u5ba2\u4eba\u53c8\u90fd\u662f\u6709\u8bf7\u5e16\u7684\uff0c\u6ca1\u6765\u5386\u7684\u4eba\u600e\u4e48\u6df7\u5f97\u8fdb\u53bb\uff1f\u201d \u3000\u3000\u5f20\u5b8f\u9053\uff1a\u201c\u50cf\u4eca\u5929\u8fd9\u6837\u7684\u5927\u573a\u9762\uff0c\u62c5\u4efb\u77e5\u5ba2\u7684\u5b9a\u7136\u4e0d\u5c11\u3002\u5373\u4f7f\u6709\u751f\u9762\u4eba\u6df7\u8fdb\u53bb\uff0c\u4e5f\u7792\u4e0d \u8fc7\u77e5\u5ba2\u7684\u773c\u775b\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5148\u628a\u8bf7\u5e16\u62ff\u5230\u624b\u518d\u8bf4\uff0c\u77e5\u5ba2\u8fd9\u4e00\u5173\u53ea\u597d\u4e34\u673a\u5e94\u53d8\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8ddf\u5728\u674e\u58ee\u7684\u540e\u9762\uff0c\u6697\u8fd0\u5185\u529b\uff0c\u6307\u5c16\u8f7b\u8f7b\u7684\u5728\u674e\u58ee\u5de6\u80c1\u4e00\u70b9\uff0c\u70b9\u4e86\u4ed6\u7684\u201c\u7275\u98ce \u7a74\u201d\u3002\u91d1\u9010\u6d41\u7684\u529b\u5ea6\u7528\u5f97\u6070\u5230\u597d\u5904\uff0c\u53ef\u7b11\u674e\u58ee\u7adf\u662f\u6beb\u65e0\u77e5\u89c9\u3002 \u3000\u3000\u201c\u7275\u98ce\u7a74\u201d\u662f\u548c\u5927\u80a0\u6709\u5173\u8fde\u7684\uff0c\u674e\u58ee\u8d70\u4e86\u4e00\u4f1a\uff0c\u5ffd\u89c9\u8179\u75db\u5982\u7ede\uff0c\u51b7\u6c57\u5982\u96e8\uff0c\u52c9\u5f3a\u60f3\u8d70\u90fd \u8d70\u4e0d\u52a8\u4e86\u3002 \u3000\u3000\u5f20\u5b8f\u5927\u60ca\u9053\uff1a\u201c\u674e\u5144\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u674e\u58ee\u53e3\u5410\u767d\u6cab\uff0c\u547b\u541f\u9053\uff1a\u201c\u6211\uff0c\u6211\u597d\u50cf\u662f\u751f\u4e86\u75c5 \u4e86\uff0c\u809a\u75db\u5f97\u5f88\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5c0f\u5f1f\u7565\u61c2\u533b\u7406\uff0c\u5f85\u6211\u7ed9\u674e\u5144\u4e00\u8bca\u3002\u201d\u88c5\u6a21\u4f5c\u6837\u5730\u53eb\u8d77\u6765\u9053\uff1a\u201c\u54ce\u5440\uff0c\u4e0d \u597d\uff01\u201d \u3000\u3000\u5f20\u5b8f\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u75c5\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u662f\u7ede\u80a0\u7600\u3002\u53ef\u5f97\u8d76\u5feb\u6551\u6cbb\u624d\u597d\uff01\u524d\u9762\u6709\u95f4\u836f\u94fa\uff0c\u6211\u770b\u674e\u5144\u8fd8\u662f\u5148\u627e\u8fd9\u836f\u94fa \u7684\u5927\u592b\u770b\u770b\uff0c\u5c31\u5728\u4ed6\u4eec\u7684\u94fa\u5b50\u62fe\u4e00\u5242\u836f\u5403\u5427\u3002\u5e0c\u671b\u5409\u4eba\u5929\u76f8\uff0c\u8fc7\u4e00\u4e2a\u65f6\u8fb0\u4e5f\u8bb8\u5c31\u4f1a\u597d\u4e86\u3002\u201d \u3000\u3000\u674e\u58ee\u6b63\u662f\u89c9\u5f97\u8179\u4e2d\u7edf\u75db\uff0c\u542c\u4e86\u91d1\u9010\u6d41\u7684\u8bdd\uff0c\u5413\u5f97\u9762\u5982\u571f\u8272\u3002\u5fc5\u6c42\u5f20\u5b8f\u9053\uff1a\u201c\u5f20\u5144\uff0c\u8bf7\u4f60 \u6276\u6211\u8fc7\u53bb\u3002\u6551\u3001\u6551\u547d\u8981\u7d27\uff0c\u5bff\u5bb4\u4e0d\uff0c\u4e0d\u5403\u4e5f\u7f62\u3002 \u3000\u3000\u5f20\u5b8f\u548c\u674e\u58ee\u662f\u7ed3\u62dc\u5144\u5f1f\uff0c\u5fc3\u91cc\u867d\u7136\u6709\u70b9\u4e0d\u5927\u613f\u610f\uff0c\u4e5f\u662f\u201c\u4e49\u4e0d\u5bb9\u8f9e\u201d\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5509\uff0c\u771f\u60f3\u4e0d\u5230\u674e\u58ee\u4f1a\u7a81\u7136\u751f\u75c5\uff0c\u5c0f\u5f1f\u8fd8\u4ee5\u4e3a\u53ef\u4ee5\u548c\u4f60\u4eec\u4e24\u4f4d\u6709\u4f34\u5462\u3002\u670b\u53cb \u8981\u7d27\uff0c\u6211\u4e5f\u4e0d\u53bb\u8d74\u5bb4\u4e86\u3002\u201d \u3000\u3000\u5f20\u5b8f\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u3002\u674e\u5144\u6709\u6211\u7167\u6599\u8db3\u5df2\u591f\u4e86\uff0c\u8bf7\u4f60\u5230\u8428\u5e9c\u7ed9\u6211\u4eec\u8bf4\u4e00\u58f0\uff0c\u514d\u5f97\u4ed6\u4eec\u8bef \u4f1a\uff0c\u4ee5\u4e3a\u6211\u4eec\u6446\u67b6\u5b50\uff0c\u793c\u7269\u5230\u4e86\uff0c\u4eba\u5374\u4e0d\u6765\u3002\u201d \u3000\u3000\u5168\u9010\u6d41\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u8981\u6709\u4e2a\u4eba\u7ed9\u4f60\u4eec\u7980\u62a5\u624d\u884c\u3002\u4e24\u4f4d\u653e\u5fc3\uff0c\u5c0f\u5f1f\u4f1a\u4eb2\u81ea \u8ddf\u6587\u9053\u5e84\u8bf4\u7684\u3002\u795d\u674e\u5144\u65e9\u65e5\u5eb7\u590d\uff0c\u5c0f\u5f1f\u5148\u8d70\u4e86\u3002\u201d\u5f20\u3001\u674e\u4e8c\u4eba\u8fde\u58f0\u9053\u8c22\uff0c\u91d1\u9010\u6d41\u5374\u662f\u4e00\u9762\u8d70 \u4e00\u9762\u6697\u6697\u5077\u7b11\u3002 \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u5728\u7ed9\u674e\u58ee\u628a\u8109\u7684\u65f6\u5019\uff0c\u65e9\u5df2\u65bd\u5c55\u7a7a\u7a7a\u5999\u624b\uff0c\u628a\u4ed6\u8eab\u4e0a\u90a3\u5f20\u8bf7\u5e16\u5077\u4e86\u8fc7\u6765\u3002\u91d1 \u9010\u6d41\u8d70\u8fdb\u4e00\u6761\u5c0f\u5df7\uff0c\u56db\u987e\u65e0\u4eba\uff0c\u5077\u50cf\u628a\u90a3\u5f20\u8bf7\u5e16\u62ff\u51fa\u6765\u4e00\u770b\uff0c\u5e78\u559c\u8bf7\u5e16\u4e0a\u662f\u6ca1\u6709\u586b\u4e0a\u59d3\u540d \u7684\uff0c\u91d1\u9010\u6d41\u653e\u4e0b\u4e00\u91cd\u5fc3\u4e8b\uff0c\u60f3\u9053\uff1a\u201c\u73b0\u5728\u5c31\u53ea\u8981\u95ef\u8fc7\u77e5\u5ba2\u8fd9\u4e00\u5173\u4e86\u3002\u201d \u3000\u3000\u5230\u4e86\u8428\u798f\u9f0e\u7684\u5b98\u90b8\uff0c\u6b63\u662f\u6700\u70ed\u95f9\u7684\u65f6\u5019\uff0c\u95e8\u5916\u5bbe\u5ba2\u4e91\u96c6\uff0c\u5927\u5bb6\u4e89\u7740\u8fd9\u53bb\uff0c\u628a\u5f53\u77e5\u5ba2\u7684\u5fd9 \u5f97\u4e0d\u4ea6\u4e50\u4e4e\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7559\u5fc3\u89c2\u5bdf\uff0c\u53ea\u662f\u51e1\u662f\u5355\u8eab\u7684\u8d3a\u5ba2\uff0c\u4e00\u8fdb\u5927\u95e8\uff0c\u5b9a\u6709\u76f8\u719f\u7684\u77e5\u5ba2\u548c\u4ed6\u6253\u4e2a\u62db\u547c\uff0c\u7136 \u540e\u624d\u6709\u4ec6\u4eba\u5e26\u5f15\u4ed6\u4eec\u8fdb\u53bb\u3002\u91d1\u9042\u6d41\u5fc3\u60f3\uff1a\u201c\u5f20\u5b8f\u3001\u674e\u58ee\u5728\u8428\u5bb6\u6709\u719f\u4eba\uff0c\u5192\u4ed6\u4eec\u7684\u540d\u5b57\u65e9\u4e0d\u884c \u4e86\u3002\u600e\u4e48\u6837\u6df7\u8fdb\u53bb\u5462\uff1f\u201d \u3000\u3000\u540e\u9762\u7684\u4eba\u6324\u7740\u8fd9\u6765\uff0c\u91d1\u9010\u6d41\u4e0d\u8d70\u4e5f\u4e0d\u884c\uff0c\u53ea\u597d\u786c\u7740\u5934\u76ae\u8fdb\u53bb\u3002\u4ed6\u60f3\u7740\u5fc3\u4e8b\uff0c\u65e0\u610f\u95f4\u8e29\u4e86 \u65c1\u8fb9\u7684\u4e00\u4e2a\u4eba\uff0c\u90a3\u4eba\u6012\u9053\uff1a\u201c\u4f60\u8d70\u8def\u4e0d\u5e26\u773c\u775b\u5417\uff1f\u201d\u53cd\u624b\u4e00\u6293\u6293\u7740\u4e86\u91d1\u9010\u6d41\u3002 \u3000\u3000\u91d1\u9010\u6d41\u548c\u90a3\u4eba\u6253\u4e86\u7167\u9762\uff0c\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u539f\u6765\u8fd9\u4eba\u662f\u5180\u5317\u7684\u72ec\u811a\u5927\u76d7\u90d1\u96c4\u56fe\uff0c\u66fe\u7ecf\u548c\u9ad8 \u5927\u6210\u3001\u675c\u5927\u4e1a\u3001\u5b98\u79c9\u85e9\u7b49\u4eba\u5728\u82cf\u5dde\u57ce\u5916\u7684\u677e\u6797\u4e4b\u4e2d\uff0c\u548c\u91d1\u9010\u6d41\u4ea4\u8fc7\u624b\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6015\u7ed9\u4ed6\u8bc6\u7834\uff0c\u4e0d\u6562\u51fa\u58f0\uff0c\u90d1\u96c4\u56fe\u6293\u7740\u4e86\u91d1\u9010\u6d41\uff0c\u4e5f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u539f\u6765\u90d1\u96c4\u56fe\u662f \u7ec3\u6709\u94c1\u7802\u638c\u7684\u529f\u592b\u7684\uff0c\u4ed6\u6293\u7740\u91d1\u9010\u6d41\uff0c\u6709\u5fc3\u8981\u628a\u4ed6\u634f\u5f97\u5fcd\u4e0d\u4f4f\u75db\u53eb\u51fa\u58f0\u6765\uff0c\u54ea\u77e5\u91d1\u9042\u6d41\u7adf\u4f3c \u6beb\u65e0\u77e5\u89c9\uff0c\u53cd\u800c\u662f\u90d1\u96c4\u56fe\u7684\u8109\u95e8\u9690\u9690\u611f\u5230\u9488\u523a\u80a1\u7684\u75bc\u75db\u3002 \u3000\u3000\u65c1\u8fb9\u7684\u4eba\u529d\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u662f\u6765\u7ed9\u8428\u5927\u4eba\u8d3a\u5bff\u7684\u5ba2\u4eba\uff0c\u522b\u95f9\u7b11\u8bdd\uff0c\u6740\u4e86\u98ce\u666f\u3002\u201d\u90d1\u96c4\u56fe\u6b63 \u597d\u8d81\u6b64\u4e0b\u53f0\uff0c\u8fde\u5fd9\u6536\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u4e0d\u8fc7\u60f3\u8bf7\u8fd9\u4f4d\u5927\u54e5\u5148\u8d70\u800c\u5df2\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c0f \u5b50\u597d\u90aa\u95e8\uff0c\u4e0d\u77e5\u662f\u54ea\u6761\u8def\u4e0a\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u90d1\u592a\u5e73\uff0c\u4f60\u6765\u4e86\u5440\uff01\u201d\u91d1\u9010\u6d41\u542c\u5f97\u8fd9\u4e2a\u58f0\u97f3\uff0c\u4e0d\u7981\u559c\u51fa\u671b\u5916\uff0c\u539f\u6765 \u548c\u90d1\u96c4\u56fe\u6253\u62db\u547c\u7684\uff0c\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u5bab\u79c9\u85e9\u3002 \u3000\u3000\u91d1\u9010\u6d41\u538b\u4f4e\u58f0\u97f3\u9053\uff1a\u201c\u90d1\u5927\u54e5\uff0c\u4f60\u5148\u8d70\u3002\u201d\u90d1\u96c4\u56fe\u89c1\u4e86\u719f\u4eba\uff0c\u559c\u5b5c\u5b5c\u5730\u8d70\u8fc7\u53bb\uff0c\u4e5f\u5c31\u987e \u4e0d\u5f97\u548c\u91d1\u9010\u6d41\u63d6\u8ba9\u4e86\u3002 \u3000\u3000\u90d1\u96c4\u56fe\u9053\uff1a\u201c\u5bab\u9999\u4e3b\uff0c\u539f\u6765\u4f60\u5728\u8fd9\u91cc\u4f5c\u77e5\u5ba2\u5440\u3002\u4f60\u4eec\u7684\u516c\u5b59\u8235\u4e3b\u4e5f\u6765\u4e86\u4e48\uff1f\u201d\u7ea2\u7f28\u4f1a\u7684 \u8235\u4e3b\u516c\u5b59\u5b8f\u662f\u6c5f\u6e56\u4e0a\u6570\u4e00\u6570\u4e8c\u7684\u4eba\u7269\uff0c\u548c\u53f2\u767d\u90fd\u5e76\u9a7e\u9f50\u540d\uff0c\u4f46\u6bd4\u53f2\u767d\u90fd\u6b63\u6d3e\u5f97\u591a\uff0c\u4e00\u5411\u81ea\u89c6 \u751a\u9ad8\u3002\u90d1\u96c4\u56fe\u4ee5\u4e3a\u4ed6\u4e00\u5b9a\u4e0d\u4f1a\u6765\u7684\uff0c\u662f\u4ee5\u89c1\u4e86\u7ea2\u7f28\u4f1a\u7684\u9999\u4e3b\u5bab\u79c9\u85e9\uff0c\u9042\u6709\u6b64\u4e00\u95ee\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u9053\uff1a\u201c\u6211\u4eec\u7684\u8235\u4e3b\u672c\u662f\u4e0d\u51c6\u5907\u6765\u7684\uff0c\u5374\u4e0d\u8fc7\u53f2\u8235\u4e3b\u4ee3\u9080\u7684\u76db\u610f\uff0c\u4e5f\u5c31\u6765\u4e86\u3002\u627e\u53cd \u6b63\u95f2\u7740\u6ca1\u4e8b\uff0c\u5728\u8fd9\u91cc\u5e2e\u5e2e\u5fd9\u3002\u201d\u539f\u6765\u7ea2\u7f28\u4f1a\u548c\u5404\u65b9\u9762\u7684\u4eba\u7269\u90fd\u6709\u5173\u7cfb\uff0c\u5728\u7ea2\u7f28\u4f1a\u7684\u9999\u4e3b\u4e4b \u4e2d\uff0c\u53c8\u4ee5\u5bab\u79c9\u85e9\u4ea4\u6e38\u6700\u4e3a\u5e7f\u6cdb\uff0c\u8428\u798f\u9f0e\u4e0d\u597d\u59d4\u800c\u7ea2\u7f28\u4f1a\u7684\u5e2e\u4e3b\u4f5c\u77e5\u5ba2\uff0c\u56e0\u6b64\u53ea\u80fd\u8bf7\u5bab\u79c9\u798f\u62c5 \u4efb\uff0c\u8981\u4ed6\u4e13\u95e8\u7559\u610f\u5f62\u8ff9\u53ef\u7591\u7684\u4eba\u7269\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8ddf\u7740\u8fdb\u53bb\uff0c\u5b88\u5728\u5927\u95e8\u7684\u77e5\u5ba2\u90fd\u4e0d\u8ba4\u8bc6\u4ed6\uff0c\u6709\u4e24\u4e2a\u77e5\u5ba2\u4fbf\u8d70\u8fc7\u6765\uff0c\u8d54\u7b11\u8bf4\u9053\uff1a\u201c\u5bf9 \u4e0d\u4f4f\uff0c\u8bf7\u4ea4\u8bf7\u67ec\u3002\u8001\u5144\u662f\u2026\u2026\u201d \u3000\u3000\u91d1\u9010\u6d41\u638f\u4e86\u8bf7\u5e16\u5f80\u4ed6\u624b\u4e0a\u4e00\u585e\uff0c\u88c5\u4f5c\u521a\u521a\u53d1\u73b0\u5bab\u79c9\u85e9\u7684\u795e\u6c14\uff0c\u4e0d\u7406\u4f1a\u90a3\u4e24\u4e2a\u77e5\u5ba2\uff0c\u5f84\u81ea \u8d70\u5230\u5bab\u79c9\u85e9\u9762\u524d\uff0c\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u5bab\u9999\u4e3b\uff0c\u4f60\u6765\u5f97\u65e9\u5440\uff01\u201d \u3000\u3000\u5bab\u79c9\u85e9\u4ea4\u6e38\u5e7f\u9614\uff0c\u4eba\u5bb6\u8ba4\u8bc6\u4ed6\u4ed6\u4e0d\u8ba4\u8bc6\u4eba\u5bb6\u7684\u8fd9\u79cd\u4e8b\u60c5\u662f\u5e38\u6709\u7684\uff0c\u5bab\u79c9\u85e9\u6b63\u81ea\u601d\u7d22\u201c\u8fd9 \u4eba\u662f\u8c01\u201d\uff0c\u91d1\u9010\u6d41\u4e0d\u5f85\u4ed6\u53d1\u95ee\uff0c\u5df2\u4f38\u51fa\u624b\u53bb\u4e0e\u4ed6\u4e00\u63e1\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u5929\u5728\u5927\u660e\u6e56\u7554\u7559\u4f60\u4e0d\u4f4f\uff0c \u4eca\u65e5\u53ef\u5f97\u75db\u75db\u5feb\u5feb\u7684\u548c\u4f60\u559d\u4e00\u987f\u4e86\u3002\u201d \u3000\u3000\u53cc\u624b\u4e00\u63e1\uff0c\u5bab\u79c9\u85e9\u4ece\u91d1\u9010\u6d41\u6240\u4f7f\u7528\u7684\u5185\u529b\uff0c\u5df2\u7ecf\u9690\u7ea6\u731c\u5230\u4e86\u51e0\u5206\uff0c\u56e0\u4e3a\u91d1\u9010\u6d41\u662f\u66fe\u7ecf\u597d \u51e0\u6b21\u548c\u4ed6\u4ea4\u8fc7\u624b\u7684\u3002\u518d\u542c\u4e86\u91d1\u9010\u6d41\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5f53\u7136\u5c31\u77e5\u9053\u4ed6\u662f\u8c01\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u6697\u6697\u5403\u60ca\uff1a\u201c\u8fd9\u5c0f\u5b50\u771f\u662f\u80c6\u5927\u5305\u5929\uff01\u201d\u4e00\u9762\u5403\u60ca\uff0c\u4e00\u9762\u4e5f\u4e0d\u80fd\u4e0d\u4f69\u670d\u91d1\u9010\u6d41\u7684\u80c6 \u5927\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u6709\u8fd9\u4e2a\u80c6\u91cf\u95ef\u6765\uff0c\u96be\u9053\u6211\u5c31\u4e0d\u6562\u7ed9\u4ed6\u62c5\u5f53\u4e00\u70b9\u5173\u7cfb\uff1f\u5927\u4e0d\u4e86\u662f\u548c\u53f2\u767d\u90fd \u95f9\u7ffb\uff0c\u53ef\u4e0d\u80fd\u8ba9\u4ed6\u770b\u5c0f\u4e86\u3002\u201d\u4e8e\u662f\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5144\u8bf7\u8fdb\uff0c\u4eca\u5929\u6050\u6015\u6211\u8fd8\u662f\u6ca1\u7a7a\u966a\u4f60 \u559d\u9152\uff0c\u4e0d\u8fc7\u6211\u4eec\u7684\u8235\u4e3b\u4e43\u662f\u6d77\u91cf\uff0c\u4f60\u53ea\u8981\u8bf4\u662f\u6211\u5bab\u67d0\u4eba\u7684\u670b\u53cb\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u548c\u4f60\u559d\u4e2a\u75db\u5feb\u3002\u201d \u8bdd\u4e2d\u6697\u793a\u7ed9\u91d1\u9010\u6d41\u77e5\u9053\uff0c\u4ed6\u4eec\u7684\u8235\u4e3b\u516c\u5b59\u5b8f\u5e76\u975e\u548c\u53f2\u767d\u90fd\u4e00\u8def\uff0c\u91d1\u9010\u6d41\u4e0d\u59a8\u5148\u4e0e\u4ed6\u7ed3\u8bc6\uff0c\u6709 \u4e8b\u4e4b\u65f6\uff0c\u5c31\u53ef\u80fd\u5f97\u5230\u516c\u5b59\u5b8f\u653e\u4e2a\u4ea4\u60c5\u3002 \u3000\u3000\u77e5\u5ba2\u4eec\u770b\u89c1\u4ed6\u4eec\u4eb2\u70ed\u7684\u60c5\u5f62\uff0c\u4eba\u4eba\u90fd\u4ee5\u4e3a\u91d1\u9010\u6d41\u662f\u5bab\u79c9\u85e9\u7684\u8001\u670b\u53cb\uff0c\u5f53\u7136\u4e5f\u5c31\u4e0d\u4f1a\u5bf9\u91d1 \u9010\u6d41\u518d\u52a0\u76d8\u95ee\u4e86\u3002\u4e8e\u662f\u91d1\u9010\u6d41\u8f7b\u8f7b\u6613\u6613\u7684\u5c31\u95ef\u8fc7\u4e86\u8fd9\u4e00\u5173\u3002 \u3000\u3000\u77e5\u5ba2\u5e26\u9886\u91d1\u9010\u6d41\u5148\u5230\u5ba2\u5385\u559d\u8336\uff0c\u53c8\u5fd9\u7740\u51fa\u53bb\u62db\u5f85\u522b\u7684\u5ba2\u4eba\u4e86\u3002\u91d1\u9010\u6d41\u4e3e\u76ee\u4e00\u770b\uff0c\u53ea\u89c1\u9ad8 \u5927\u6210\u3001\u675c\u5927\u4e1a\u3001\u90d1\u96c4\u56fe\u3001\u6c99\u5343\u5cf0\u7b49\u4eba\u90fd\u5728\u5ba2\u5385\u4e4b\u5185\uff0c\u5374\u4e0d\u89c1\u53f2\u767d\u90fd\u3002\u65e0\u610f\u95f4\u773c\u5149\u4e00\u77a5\uff0c\u5ffd\u89c1 \u4e00\u4e2a\u5bb9\u989c\u6e05\u79c0\u7684\u5c11\u5e74\u72ec\u81ea\u5750\u5728\u4e00\u4e2a\u89d2\u843d\uff0c\u4f4e\u4e0b\u5934\u53ea\u987e\u559d\u8336\uff0c\u4e5f\u4e0d\u548c\u65c1\u8fb9\u7684\u4eba\u8bf4\u8bdd\u3002\u91d1\u9010\u6d41\u5fc3 \u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4e2a\u4eba\u4f3c\u66fe\u76f8\u8bc6\uff0c\u5374\u662f\u5728\u54ea\u91cc\u89c1\u8fc7\u7684\u5462\uff1f\u201d\u60f3\u8fc7\u53bb\u4e0e\u4ed6\u6500\u8c08\uff0c\u53c8\u6015\u7ed9 \u4eba\u8bc6\u7834\uff0c\u4e00\u65f6\u4e0d\u6562\u9020\u6b21\u3002 \u3000\u3000\u53ea\u542c\u5f97\u65c1\u8fb9\u4e24\u4e2a\u5ba2\u4eba\u6b63\u5728\u5546\u91cf\uff0c\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u8be5\u8fdb\u5bff\u5802\u7ed9\u4e3b\u4eba\u62dc\u5bff\u7684\u5427\uff1f\u201d\u4e00\u4e2a\u8bf4 \u9053\uff1a\u201c\u542c\u8bf4\u4e3b\u4eba\u8fd8\u5728\u5185\u5802\u62db\u5f85\u8d35\u5ba2\uff0c\u6050\u6015\u4e0d\u4f1a\u8fd9\u6837\u65e9\u5c31\u51fa\u5bff\u5802\u53d7\u793c\u5427\uff1f\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u516d\u5408\u5e2e \u7684\u53f2\u5e2e\u4e3b\u548c\u7ea2\u7f28\u4f1a\u7684\u516c\u5b59\u8235\u4e3b\u90e8\u6765\u4e86\uff1f\u516c\u5b59\u8235\u4e3b\u662f\u4e00\u5411\u4e0d\u548c\u5b98\u5e9c\u7ed3\u4ea4\u7684\uff0c\u96be\u5f97\u4ed6\u4eca\u65e5\u4e5f\u6765\u8d3a \u5bff\uff0c\u8428\u603b\u7ba1\u8fd8\u80fd\u4e0d\u597d\u597d\u62db\u5f85\u4ed6\u5417\uff1f\u201d\u8fd9\u4eba\u81ea\u4ee5\u4e3a\u6d88\u606f\u7075\u901a\uff0c\u4e89\u7740\u62a5\u9053\u5185\u5e55\u6d88\u606f\u3002\u4ed6\u7684\u670b\u53cb\u7b11 \u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u4f46\u54b1\u4eec\u5148\u8fdb\u5bff\u5802\u5f00\u5f00\u773c\u754c\u4e0d\u4e5f\u597d\u4e48\uff1f\u201d \u3000\u3000\u90a3\u4eba\u95ee\u9053\uff1a\u201c\u5f00\u4ec0\u4e48\u773c\u754c\uff1f\u201d\u4ed6\u7684\u670b\u53cb\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u8fd8\u4e0d\u77e5\u9053\u5440\uff0c\u5404\u65b9\u7684\u8d3a\u793c\u90fd\u6446 \u5728\u5bff\u5802\u4e4b\u5185\uff0c\u542c\u8bf4\u8fd8\u6709\u7687\u4e0a\u5fa1\u8d50\u7684\u5b9d\u7269\u5462\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u4e86\u8fd9\u4e24\u4eba\u7684\u8bf4\u8bdd\uff0c\u56de\u5934\u4e00\u770b\uff0c\u4e0d\u89c1\u90a3\u4f3c\u66fe\u76f8\u8bc6\u7684\u5c11\u5e74\uff0c\u60f3\u662f\u5df2\u8fdb\u4e86\u5bff\u5802\u4e86\u3002\u4e8e \u662f\u91d1\u9010\u6d41\u4e5f\u8ddf\u5728\u90a3\u4e24\u4eba\u540e\u9762\uff0c\u8fdb\u5165\u5bff\u5802\u3002 \u3000\u3000\u5bff\u5802\u6bd4\u5ba2\u5385\u5927\u597d\u51e0\u500d\uff0c\u4e2d\u95f4\u5e76\u62e2\u516b\u5f20\u516b\u4ed9\u684c\u5b50\uff0c\u5806\u6ee1\u5404\u65b9\u9001\u6765\u7684\u793c\u7269\uff0c\u6700\u5f15\u4eba\u6ce8\u76ee\u7684\u5f53 \u7136\u662f\u7687\u5e1d\u6240\u8d50\u7684\u793c\u7269\uff0c\u90a3\u662f\u4e00\u5bf9\u901a\u4f53\u65e0\u6687\u7684\u78a7\u7389\u897f\u74dc\u3002\u5176\u6b21\u662f\u53f2\u767d\u90fd\u6240\u9001\u7684\u4e00\u652f\u4e1a\u5df2\u6210\u5f62\u7684 \u5343\u5e74\u4f55\u9996\u4e4c\uff0c\u8fd9\u79cd\u6210\u4e86\u4eba\u5f62\u7684\u4f55\u9996\u4e4c\u662f\u6700\u96be\u5f97\u7684\u8865\u836f\uff0c\u636e\u8bf4\u6709\u8d77\u6b7b\u56de\u751f\u7684\u529f\u7528\u3002\u539f\u6765\u53f2\u767d\u90fd \u5728\u5931\u4e86\u660e\u73e0\u4e0e\u7384\u94c1\u4e4b\u540e\uff0c\u5343\u65b9\u767e\u8ba1\uff0c\u624d\u6c42\u5f97\u8fd9\u652f\u4f55\u9996\u4e4c\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u79f0\u8d5e\u90a3\u5bf9\u78a7\u7389\u897f\u74dc\uff0c\u5176\u5b9e\u4e0d\u8fc7\u662f\u770b\u5728\u7687\u5e1d\u8001\u513f\u7684\u9762\u5b50\u7f62\u4e86\uff0c \u7ed9\u6211\u7684\u8bdd\uff0c\u6211\u5374\u5b81\u53ef\u8981\u8fd9\u652f\u4f55\u9996\u4e4c\u3002\u201d\u60f3\u81f3\u5176\u4ed6\uff0c\u53c8\u4e0d\u7981\u6697\u6697\u5077\u7b11\uff1a\u201c\u6211\u62a2\u4e86\u4ed6\u7684\u7384\u94c1\uff0c \u2018\u501f\u2019\u4e86\u4ed6\u7684\u5b9d\u9a6c\uff0c\u5982\u679c\u518d\u5077\u4ed6\u8fd9\u652f\u4f55\u9996\u4e4c\uff0c\u5c82\u4e0d\u628a\u4ed6\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff1f\u4f55\u9996\u4e4c\u56fa\u7136\u5b9d\u8d35\uff0c\u6bd4 \u8d77\u7384\u94c1\u5219\u53c8\u4e0d\u5982\uff0c\u6211\u4e5f\u4e0d\u8be5\u592a\u8fc7\u8d2a\u5f97\u65e0\u538c\u4e86\u3002\u4e0d\u8fc7\uff0c\u8bdd\u8bf4\u56de\u6765\uff0c\u53f2\u767d\u90fd\u8fd9\u53ae\u786e\u4e5f\u662f\u795e\u901a\u5e7f \u5927\uff0c\u5728\u63a5\u8fde\u5931\u4e86\u4e24\u4ef6\u73cd\u8d35\u7684\u8d3a\u793c\u4e4b\u540e\uff0c\u4e34\u65f6\u5907\u529e\u7684\u7b2c\u4e09\u4ef6\u8d3a\u672d\uff0c\u5c45\u7136\u4e5f\u662f\u7a00\u4e16\u4e4b\u73cd\u3002\u201d \u3000\u3000\u6570\u4e86\u78a7\u7389\u897f\u74dc\u548c\u4f55\u9996\u4e4c\uff0c\u518d\u5176\u6b21\u73cd\u8d35\u7684\u793c\u7269\u5f97\u5230\u5927\u5bb6\u516c\u8ba4\u7684\u662f\u4e00\u652f\u201c\u901a\u5929\u7280\u89d2\u201d\uff0c\u201c\u901a \u5929\u7280\u89d2\u201d\u662f\u897f\u85cf\u96ea\u5c71\u4e0a\u4e00\u79cd\u7f55\u89c1\u7684\u7280\u725b\uff0c\u636e\u8bf4\u9152\u98df\u4e4b\u5185\uff0c\u5982\u679c\u4e0b\u4e86\u6bd2\u836f\uff0c\u53ea\u8981\u628a\u201c\u901a\u5929\u7280 \u89d2\u201d\u63d2\u8fdb\u53bb\u4e00\u8bd5\uff0c\u7280\u89d2\u4fbf\u4f1a\u7acb\u5373\u53d8\u8272\uff0c\u7528\u901a\u5929\u7280\u89d2\u7814\u7c89\uff0c\u53c8\u6709\u80fd\u89e3\u767e\u6bd2\u4e4b\u529f\u3002\u4e16\u4e0a\u89e3\u6bd2\u7684\u5723 \u836f\uff0c\u7b2c\u4e00\u662f\u5929\u5c71\u96ea\u83b2\uff0c\u7b2c\u4e8c\u4e2a\u662f\u901a\u5929\u7280\u89d2\uff0c\u8fd9\u652f\u901a\u5929\u7280\u89d2\u662f\u897f\u85cf\u201c\u6d3b\u6cb8\u201d\u6240\u9001\u7684\u793c\u7269\u3002\u201c\u6d3b \u4f5b\u201d\u5f53\u7136\u4e0d\u4f1a\u4eb2\u6765\u8d3a\u5bff\uff0c\u4f46\u4ed6\u6d3e\u9063\u4e86\u624b\u4e0b\u5587\u561b\u9001\u6765\u8fd9\u6837\u540d\u8d35\u7684\u793c\u7269\uff0c\u5bf9\u8428\u798f\u9f0e\u4e5f\u662f\u4e00\u79cd\u201c\u6b8a \u8363\u201d\u4e86\u3002 \u3000\u3000\u4e09\u4ef6\u6700\u73cd\u8d35\u7684\u793c\u7269\u4e4b\u5916\uff0c\u5176\u4ed6\u73cd\u73e0\u3001\u7389\u77f3\u3001\u73ca\u745a\u3001\u739b\u7459\u4e4b\u7c7b\u7684\u5b9d\u8d1d\u6570\u4e0d\u80dc\u6570\uff0c\u91d1\u9010\u6d41\u5999 \u60f3\u5929\u5f00\uff1a\u201c\u5982\u679c\u59ec\u4f2f\u4f2f\u5728\u8fd9\u91cc\uff01\u5f53\u6ee1\u8f7d\u800c\u5f52\u4e86\u3002 \u3000\u3000\u5ba2\u4eba\u53c2\u89c2\u793c\u7269\uff0c\u5567\u5567\u79f0\u8d4f\uff0c\u4f46\u4e5f\u6709\u4eba\u5728\u7a83\u7a88\u79c1\u8bae\uff1a\u201c\u672c\u6765\u793c\u7269\u8fd8\u4e0d\u6b62\u8fd9\u6837\u591a\u7684\u5462\uff0c\u542c\u8bf4 \u9014\u4e2d\u5df2\u88ab\u4eba\u52ab\u53bb\u4e86\u8bb8\u591a\u5b97\u4e86\u3002\u201d\u201c\u9752\u9f99\u5e2e\u767d\u864e\u5e2e\u7684\u793c\u7269\u5c31\u662f\u7ed9\u4eba\u52ab\u53bb\u7684\uff0c\u4ed6\u4eec\u73b0\u5728\u9001\u7684\u793c\u7269 \u662f\u4e34\u65f6\u5728\u5317\u4eac\u7684\u53e4\u73a9\u94fa\u4e70\u7684\u3002\u8fd9\u4e24\u4ef6\u793c\u7269\u867d\u7136\u503c\u94b1\uff0c\u6bd4\u8d77\u5176\u4ed6\u540c\u7b49\u8eab\u4efd\u7684\u5e2e\u4e3b\u6240\u9001\u7684\u793c\u7269\uff0c \u53ef\u5c31\u5927\u5927\u900a\u8272\u4e86\u3002\u201d\u201c\u4e2d\u9014\u622a\u52ab\u8d3a\u793c\u7684\u662f\u4ec0\u4e48\u4eba\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u201d\u201c\u542c\u8bf4\u662f\u4e2a\u8499\u9762\u5973\u5b50\uff0c\u8c01 \u4e5f\u4e0d\u77e5\u5979\u7684\u6765\u5386\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u4e86\u90a3\u4e9b\u4eba\u7684\u7a83\u7a83\u79c1\u8bae\uff0c\u5fc3\u4e2d\u6697\u6697\u5077\u7b11\uff1b\u201c\u4f60\u4eec\u4e0d\u77e5\u9053\uff0c\u6211\u53ef\u77e5\u9053\u3002\u201d\u4f46\u5077\u7b11\u4e4b \u540e\uff0c\u5374\u53c8\u4e0d\u7981\u6709\u51e0\u5206\u5931\u671b\u3002\u56e0\u4e3a\u91d1\u9010\u6d41\u8fd9\u6b21\u5192\u9669\u800c\u6765\uff0c\u5176\u4e2d\u7684\u4e00\u4e2a\u4e3b\u8981\u76ee\u7684\u5c31\u662f\u5e0c\u671b\u5728\u8fd9\u91cc \u89c1\u7740\u53f2\u7ea2\u82f1\u7684\uff0c\u4f46\u76f4\u81f3\u73b0\u5728\uff0c\u8fd8\u6ca1\u89c1\u7740\u3002 \u3000\u3000\u201c\u5979\u662f\u56e0\u4e3a\u52ab\u4e86\u8d3a\u793c\u4e0d\u6562\u6765\u5462\uff1f\u8fd8\u662f\u6765\u4e86\u6211\u6ca1\u53d1\u73b0\uff1f\u201d\u91d1\u9010\u6d41\u5fc3\u60f3\uff0c\u4ed6\u6025\u4e8e\u5728\u4eba\u5806\u91cc\u627e \u51fa\u53f2\u7ea2\u82f1\uff0c\u5bf9\u90a3\u4e9b\u5947\u73cd\u5f02\u5b9d\u4e5f\u4e38\u5fc3\u89c2\u8d4f\u4e86\u3002 \u3000\u3000\u6765\u8428\u5e9c\u795d\u5bff\u7684\u5973\u5ba2\u53ef\u5206\u4e24\u7c7b\uff0c\u4e00\u7c7b\u662f\u5b98\u5bb6\u5185\u7737\uff0c\u968f\u540c\u4e08\u592b\u6765\u7684\uff0c\u8fd9\u7c7b\u5973\u5ba2\u85cf\u5728\u5185\u5802\uff0c\u4e0d \u4e0e\u5916\u95f4\u7684\u7537\u5ba2\u6df7\u6742\u3002\u4e00\u7c7b\u662f\u6c5f\u6e56\u4e0a\u7684\u4eba\u7269\uff0c\u4f8b\u5982\u516d\u5408\u5e2e\u4e2d\u7684\u8463\u5341\u4e09\u5a18\u5c31\u662f\u3002\u8fd9\u7c7b\u5973\u5ba2\u5012\u662f\u5728 \u6625\u5802\u4e4b\u5185\uff0c\u4f46\u5be5\u5be5\u53ef\u6570\uff0c\u4e00\u76ee\u4e86\u7136\uff0c\u5374\u6ca1\u6709\u53d1\u73b0\u4e00\u4e2a\u76f8\u8c8c\u548c\u53f2\u7ea2\u82f1\u7a0d\u5fae\u76f8\u4f3c\u7684\u4eba\u3002 \u3000\u3000\u201c\u96be\u9053\u7ea2\u82f1\u6df7\u5728\u5b98\u5bb6\u5185\u7737\u4e4b\u4e2d\uff1f\u8fd9\u600e\u4e48\u53ef\u80fd\uff1f\u201d\u91d1\u9010\u6d41\u6b63\u81ea\u80e1\u601d\u4e71\u60f3\uff0c\u4eba\u4e1b\u4e2d\u6709\u4e24\u4e2a\u4eba \u7684\u5bf9\u8bdd\u4f20\u5165\u4ed6\u7684\u8033\u6735\uff1a\u201c\u524d\u51e0\u5929\u542c\u8bf4\u4ed6\u4eec\u95f9\u4e86\u4e00\u4e2a\u5927\u7b11\u8bdd\uff0c\u628a\u5c01\u5b50\u8d85\u7684\u5973\u513f\u9519\u5f53\u4f5c\u90a3\u4e2a\u52ab\u5b9d \u7684\u5973\u8d3c\u4e86\u3002\u8001\u5f1f\uff0c\u4f60\u662f\u4ece\u90a3\u6761\u8def\u4e0a\u7ecf\u8fc7\u7684\uff0c\u53ef\u77e5\u8fd9\u4ef6\u4e8b\u60c5\uff1f\u201d\u201c\u662f\u4e48\uff1f\u6211\u8fd8\u672a\u77e5\u9053\u5462\uff01\u201d \u201c\u54e6\uff0c\u8fd9\u5c31\u5947\u4e86\uff0c\u6211\u4ee5\u4e3a\u4f60\u662f\u5e94\u8be5\u77e5\u9053\u7684\u5462\u3002\u201d\u201c\u6c99\u5e2e\u4e3b\uff0c\u4f60\u7684\u8bdd\u66f4\u5947\u602a\u4e86\uff0c\u4e3a\u4ec0\u4e48\u6211\u51c6\u4f1a \u77e5\u9053\uff1f\u201d \u3000\u3000\u540e\u9762\u8fd9\u4eba\u58f0\u97f3\u6e05\u8106\uff0c\u91d1\u9010\u6d41\u597d\u4f3c\u5728\u54ea\u91cc\u542c\u8fc7\uff0c\u8fde\u5fd9\u628a\u773c\u5149\u5411\u90a3\u8fb9\u641c\u7d22\uff0c\u53ea\u89c1\u8bf4\u8bdd\u7684\u90a3\u4e24 \u4e2a\u4eba\uff0c\u4e00\u4e2a\u662f\u6d77\u7802\u5e2e\u7684\u5e2e\u4e3b\u6c99\u5343\u5cf0\uff0c\u4e00\u4e2a\u6b63\u662f\u521a\u624d\u5728\u5ba2\u5385\u91cc\u72ec\u81ea\u5750\u5728\u4e00\u4e2a\u89d2\u843d\u4e0d\u7406\u4f1a\u65c1\u4eba\u7684 \u90a3\u4e2a\u5c11\u5e74\u3002\u6c99\u5343\u5cf0\u6b63\u5728\u7528\u7740\u6000\u7591\u7684\u773c\u5149\u5411\u90a3\u5c11\u5e74\u76d8\u95ee\u3002 \u3000\u3000\u91d1\u9010\u6d41\u767b\u65f6\u4e5f\u8d77\u4e86\u7591\u5fc3\uff0c\u6b63\u8981\u6324\u8fc7\u53bb\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u5bff\u5802\u91cc\u5608\u5608\u6742\u6742\u7684\u8c08\u8bdd\u58f0\u7a81\u7136\u9759\u6b62\uff0c \u6709\u4eba\u6084\u6084\u8bf4\u9053\uff1a\u201c\u5bff\u661f\u516c\u51fa\u6765\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u8eab\u62ab\u87d2\u888d\u8170\u56f4\u7389\u5e26\u7684\u5b98\u513f\u5728\u536b\u58eb\u547c\u62e5\u4e4b\u4e2d\u8fdb\u5165\u5bff\u5802\uff0c\u8fd9\u4e2a\u5b98\u513f\u4e0d\u95ee\u53ef\u77e5\u5f53\u7136\u662f \u8428\u798f\u9f0e\u4e86\u3002\u5728\u8428\u798f\u9f0e\u4e24\u65c1\u7684\u662f\u6587\u9053\u5e84\u548c\u53f2\u767d\u90fd\uff0c\u4ed6\u4eec\u7ad9\u5f97\u7a0d\u540e\u4e00\u4e9b\uff0c\u53e6\u4e00\u4e2a\u51e0\u4e4e\u662f\u548c\u8428\u798f\u9f0e \u5e76\u6392\u884c\u8fdb\u6765\u7684\u4e2d\u5e74\u4eba\u5374\u662f\u4e2a\u8eab\u7a7f\u7c97\u5e03\u5927\u8902\u7684\u6c49\u5b50\uff0c\u5341\u8db3\u50cf\u662f\u4e2a\u58eb\u91cc\u571f\u6c14\u7684\u4e61\u4e0b\u4eba\uff0c\u5728\u8fd9\u6837\u8c6a \u534e\u7684\u573a\u9762\u4e4b\u4e2d\uff0c\u6709\u8fd9\u4e48\u6837\u7684\u4e00\u4e2a\u201c\u4e61\u4e0b\u4eba\u201d\uff0c\u800c\u4e14\u662f\u548c\u8428\u798f\u9f0e\u4e00\u540c\u51fa\u6765\u7684\uff0c\u5f53\u7136\u6700\u4e3a\u60f9\u4eba\u6ce8 \u76ee\u3002\u91d1\u9010\u6d41\u95ee\u4e86\u65c1\u4eba\uff0c\u59cb\u77e5\u8fd9\u4eba\u5c31\u662f\u7ea2\u7f28\u4f1a\u7684\u8235\u4e3b\u516c\u5b59\u5b8f\u3002\u516c\u5b59\u5b8f\u4e00\u8fdb\u5165\u5bff\u5802\u5c31\u79bb\u5f00\u8428\u798f\u9f0e \u53bb\u627e\u4ed6\u76f8\u719f\u7684\u670b\u53cb\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u8fd9\u516c\u5b59\u5b8f\u679c\u7136\u662f\u548c\u53f2\u767d\u90fd\u4e0d\u540c\uff0c\u770b\u6765\u4ed6\u662f\u4e0d\u613f\u8d8b\u708e\u9644\u52bf\uff0c\u4f46\u65e2\u7136\u5982\u6b64\uff0c \u4e0d\u6765\u4e0d\u66f4\u597d\u5417\uff1f\u96be\u9053\u5f53\u771f\u53ea\u662f\u4e3a\u4e86\u53f2\u767d\u90fd\u4ee3\u9080\u7684\u60c5\u9762\uff1f\u201d \u3000\u3000\u8428\u798f\u9f0e\u51fa\u6765\u53d7\u793c\uff0c\u5ba2\u4eba\u4e89\u7740\u4e0a\u524d\u62dc\u5bff\u3002\u6c99\u5343\u5cf0\u987e\u4e0d\u5f97\u76d8\u95ee\u90a3\u4e2a\u5c11\u5e74\uff0c\u4e5f\u6324\u7740\u4e0a\u524d\u4e86\u3002\u6df7 \u4e71\u4e2d\u91d1\u9010\u6d41\u4e00\u4e2a\u758f\u795e\uff0c\u5931\u4e86\u90a3\u5c11\u5e74\u7684\u6240\u5728\u3002 \u3000\u3000\u5ba2\u4eba\u867d\u662f\u4e89\u7740\u62dc\u5bff\uff0c\u4e5f\u8fd8\u5927\u81f4\u6709\u4e2a\u79e9\u5e8f\uff0c\u5404\u4e2a\u5e2e\u4f1a\u7684\u8235\u4e3b\u5148\u4e0a\uff0c\u5176\u4ed6\u81ea\u95ee\u8d44\u683c\u7a0d\u5dee\u7684\u867d \u7136\u6324\u5230\u4e86\u524d\u9762\u4e5f\u4e0d\u6562\u4e0d\u8ba9\u4ed6\u4eec\u3002 \u3000\u3000\u6c99\u5343\u5cf0\u62dc\u8fc7\u4e86\u5bff\uff0c\u8f6e\u7740\u5c31\u662f\u9ad8\u5927\u6210\u548c\u675c\u5927\u4e1a\u4e8c\u4eba\uff0c\u5ffd\u5730\u6709\u4e2a\u9aef\u987b\u5927\u6c49\uff0c\u8d8a\u4f17\u800c\u51fa\uff0c\u62a2\u5728 \u9ad8\u3001\u675c\u4e8c\u4eba\u7684\u524d\u5934\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4ffa\u6765\u7ed9\u4f60\u62dc\u5bff\uff01\u201d\u5c31\u5728\u4f17\u4eba\u60ca\u6115\u4e4b\u4e2d\uff0c\u7a81\u7136\u5c31\u628a\u8428\u798f\u9f0e\u4e00 \u628a\u6293\u7740\u3002\u624b\u6cd5\u5f53\u771f\u662f\u5feb\u5f97\u96be\u4ee5\u5f62\u5bb9\uff01 \u3000\u3000\u8428\u798f\u9f0e\u8eab\u4e3a\u5927\u5185\u603b\u7ba1\uff0c\u6b66\u529f\u81ea\u975e\u6cdb\u6cdb\uff0c\u53ef\u662f\u7ed9\u8fd9\u9aef\u987b\u6c49\u5b50\u4e00\u628a\u6293\u7740\uff0c\u7adf\u662f\u75db\u5f7b\u5fc3\u80ba\uff0c\u6323 \u8131\u4e0d\u5f00\uff0c\u866c\u9aef\u6c49\u5b50\u559d\u9053\uff1a\u201c\u4f60\u518d\u52a8\u4e00\u52a8\uff0c\u6211\u5c31\u634f\u788e\u4f60\u7684\u9aa8\u5934\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u6a2a\u638c\u4e00\u649e\uff0c\u53c8\u628a \u9ad8\u5927\u6210\u5e9e\u5927\u7684\u8eab\u8eaf\u649e\u5f97\u98de\u4e86\u8d77\u6765\uff0c\u5728\u9ad8\u5927\u6210\u540e\u9762\u7684\u675c\u5927\u4e1a\u4e5f\u53d7\u4e86\u8fde\u73af\u649e\u51fb\uff0c\u53d8\u4f5c\u4e86\u6eda\u5730\u846b \u82a6\u3002\u539f\u6765\uff0c\u4ed6\u4eec\u4e8c\u4eba\u662f\u60f3\u5728\u8fd9\u6c49\u5b50\u7684\u80cc\u540e\u5077\u88ad\u7684\uff0c\u4e0d\u6599\u8fd9\u6c49\u5b50\u7adf\u4f3c\u80cc\u540e\u957f\u7740\u773c\u775b\uff0c\u4e00\u4e0b\u5b50\u5c31 \u628a\u4ed6\u4eec\u5f04\u7ffb\u4e86\u3002\u4e8b\u60c5\u6765\u5f97\u592a\u8fc7\u51fa\u4eba\u610f\u5916\uff0c\u5728\u8428\u798f\u9f0e\u65c1\u8fb9\u7684\u6587\u9053\u5e84\u8981\u60f3\u89e3\u6551\uff0c\u90fd\u6765\u4e0d\u53ca\uff01 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u6ee1\u5802\u5bbe\u5ba2\u90fd\u662f\u5446\u4e86\u4e00\u5446\uff0c\u7a81\u7136\u6709\u4eba\u53eb\u9053\uff1a\u201c\u662f\u5c09\u8fdf\u70af\uff01\u201d \u3000\u3000\u866c\u9aef\u53cc\u5b50\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ffa\u5c09\u8fdf\u70af\u7d2f\u5404\u4f4d\u53d7\u60ca\u4e86\uff01\u4ffa\u624b\u4e0b\u5f1f\u5144\u6ca1\u6709\u996d\u5403\uff0c\u4f60\u4eec\u4e0e\u5176 \u9001\u793c\u7ed9\u8fd9\u72d7\u5b98\uff0c\u4e0d\u5982\u9001\u7ed9\u4ffa\uff0c\u4ffa\u66f4\u9886\u4f60\u4eec\u7684\u60c5\uff01\u8bf7\u5404\u4f4d\u7ad9\u5728\u539f\u4f4d\u4e0d\u52a8\uff0c\u5426\u5219\u4f11\u602a\u4ffa\u5f97\u7f6a\u670b \u53cb\u3002\u201d \u3000\u3000\u8fd9\u5c09\u8fdf\u70af\u4e43\u662f\u5173\u5916\u8457\u540d\u7684\u5927\u76d7\uff0c\u4e94\u5e74\u524d\u8fdb\u5173\u4e4b\u540e\uff0c\u66fe\u5728\u5317\u4eac\u95f9\u5f97\u5730\u8986\u5929\u7ffb\uff0c\u5929\u7262\u4e5f\u5173\u4ed6 \u4e0d\u4f4f\u3002\u73b0\u5728\u4ed6\u662f\u5728\u5c0f\u91d1\u5ddd\u7684\u4e49\u519b\u4e4b\u4e2d\uff0c\u8fd9\u6b21\u8fdb\u4eac\uff0c\u6b63\u662f\u7279\u5730\u6765\u5411\u8428\u798f\u9f0e\u201c\u501f\u9977\u201d\u7684\u3002 \u3000\u3000\u5bff\u5802\u4e2d\u8fd9\u4e00\u73ed\u4e09\u5c71\u4e94\u5cb3\u7684\u4eba\u7269\uff0c\u8c01\u4e0d\u77e5\u9053\u5c09\u8fdf\u70af\u662f\u4e2a\u6740\u4eba\u4e0d\u7728\u773c\u7684\u5927\u76d7\uff0c\u679c\u7136\u7ed9\u4ed6\u5413\u5f97 \u52a8\u4e5f\u4e0d\u6562\u4e00\u52a8\u3002\u6709\u4e9b\u77e5\u9053\u4ed6\u5df2\u7ecf\u6539\u90aa\u5f52\u6b63\u505a\u4e86\u4e49\u519b\u5934\u76ee\u7684\u8428\u5e9c\u536b\u58eb\u66f4\u662f\u60ca\u614c\uff0c\u5fc3\u4e2d\u4ff1\u662f\u60f3 \u9053\uff1a\u201c\u8428\u5927\u4eba\u82e5\u662f\u843d\u5728\u5f3a\u76d7\u624b\u91cc\uff0c\u5012\u8fd8\u597d\u529e\u3002\u843d\u5728\u53db\u8d3c\u624b\u4e2d\uff0c\u53ea\u6015\u662f\u8981\u6d3b\u4e5f\u6d3b\u4e0d\u6210\u4e86\uff01\u201d\u5fc3 \u4e2d\u53c8\u90fd\u5728\u5947\u602a\uff0c\u8fd9\u4e2a\u5927\u540d\u9f0e\u9f0e\u7684\u9a6c\u8d3c\u662f\u600e\u4e48\u6837\u6df7\u5f97\u8fdb\u6765\u7684\uff1f\u201d \u3000\u3000\u5c09\u8fdf\u70af\u4ea4\u4ee3\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u53ea\u89c1\u5f97\u6709\u4e03\u516b\u4e2a\u7a7f\u7740\u8428\u5e9c\u4ec6\u5f79\u670d\u9970\u7684\u6c49\u5b50\u4e00\u62e5\u800c\u5165\uff0c\u6bcf\u4eba\u643a\u7740 \u4e00\u4e2a\u5927\u9ebb\u888b\uff0c\u7acb\u5373\u4fbf\u62a2\u63a0\u6446\u5728\u684c\u5b50\u4e0a\u7684\u793c\u7269\u3002 \u3000\u3000\u8fd9\u51e0\u4e2a\u4eba\u662f\u5c09\u8fdf\u70af\u9884\u5148\u57cb\u4f0f\u5728\u8428\u5e9c\u7684\u624b\u4e0b\u3002\u539f\u6765\u8d22\u53ef\u901a\u795e\uff0c\u8428\u5e9c\u7531\u4e8e\u8981\u5927\u6392\u5bff\u5bb4\uff0c\u4e34\u65f6 \u8981\u96c7\u7528\u8bb8\u591a\u5de5\u5f79\uff0c\u5c09\u8fdf\u70af\u8bf7\u65c1\u4eba\u51fa\u9762\uff0c\u8d3f\u8d42\u4e86\u8428\u5e9c\u7684\u7ba1\u4e8b\uff0c\u628a\u4ed6\u7684\u624b\u4e0b\u5b89\u63d2\u8fdb\u53bb\u3002\u4f46\u5c09\u8fdf\u70af \u672c\u4eba\u5219\u662f\u53e6\u7528\u5176\u4ed6\u6cd5\u5b50\u6df7\u8fdb\u6765\u7684\uff0c\u540e\u6587\u518d\u8868\u3002 \u3000\u3000\u5728\u5c09\u8fdf\u70af\u7684\u624b\u4e0b\u52a8\u624b\u6d17\u52ab\u4e4b\u65f6\uff0c\u5bbe\u5ba2\u4e2d\u6709\u4e24\u4e2a\u4eba\u4e0d\u77e5\u662f\u60f3\u51fa\u53bb\u963b\u6b62\u8fd8\u662f\u5076\u7136\u79fb\u52a8\u4e86\u811a \u6b65\uff0c\u5c31\u5728\u4ed6\u4eec\u8eab\u5f62\u521a\u521a\u4e00\u52a8\u4e4b\u9645\uff0c\u53ea\u542c\u5f97\u201c\u54ce\u54df\uff0c\u54ce\u54df\uff01\u201d\u4e24\u58f0\u60e8\u547c\uff0c\u7adf\u662f\u5728\u4f17\u76ee\u777d\u777d\u4e4b \u4e0b\uff0c\u83ab\u540d\u5947\u5999\u7684\u5c31\u5012\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u9ed1\u8863\u5973\u5b50\u7ad9\u5728\u5185\u9662\u8fdb\u5165\u5bff\u5802\u7684\u95e8\u53e3\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u5f53\u5bb6\u7684\u5df2\u7ecf\u6709\u8bdd\u5728\u5148\u4e86\uff0c \u8c01\u8981\u662f\u4e0d\u542c\u6211\u5f53\u5bb6\u7684\u5429\u5490\uff0c\u8fd9\u4e24\u4e2a\u4eba\u5c31\u662f\u699c\u6837\uff01\u201d \u3000\u3000\u4f17\u4eba\u89c1\u4e86\u8fd9\u4e2a\u5973\u5b50\uff0c\u66f4\u662f\u5012\u62bd\u4e00\u53e3\u51b7\u6c14\u3002\u539f\u6765\u8fd9\u4e2a\u5973\u5b50\u4e43\u662f\u5c09\u8fdf\u70af\u4e4b\u59bb\uff0c\u5916\u53f7\u201c\u5343\u624b\u89c2 \u97f3\u201d\u7684\u7948\u5723\u56e0\u3002\u7948\u5723\u56e0\u7684\u6697\u5668\u4e43\u662f\u6b66\u676f\u4e00\u7edd\uff0c\u53d6\u4eba\u6027\u547d\uff0c\u6613\u4e8e\u62ac\u82a5\uff01 \u3000\u3000\u7948\u5723\u56e0\u4e00\u51fa\u73b0\u5c31\u6740\u4e86\u4e24\u4e2a\u4eba\uff0c\u6ee1\u5802\u5bbe\u5ba2\uff0c\u8fde\u5979\u7528\u7684\u662f\u4ec0\u4e48\u6697\u5668\u90fd\u4e0d\u77e5\u9053\uff0c\u83ab\u4e0d\u76f8\u987e\u9a87 \u7136\uff0c\u5fc3\u5934\u98a4\u6144\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u597d\u529f\u592b\uff01\u201d\u8bf4\u8bdd\u8fd9\u4eba\u662f\u6587\u9053\u5e84\uff0c\u8bdd\u72b9\u672a\u4e86\uff0c\u201c\u94ee\u94ee\u201d\u4e24\u58f0\uff0c\u4e24\u679a\u94dc \u94b1\u5df2\u662f\u4ece\u4ed6\u624b\u4e2d\u98de\u51fa\u3002 \u3000\u3000\u6b64\u65f6\u5c09\u8fdf\u70af\u7684\u624b\u4e0b\u5df2\u628a\u684c\u4e0a\u6446\u8bbe\u7684\u8d3a\u793c\u90fd\u626b\u5165\u4e86\u9ebb\u888b\u4e4b\u4e2d\uff0c\u53ea\u5269\u4e0b\u6b63\u4e2d\u95f4\u7684\u90a3\u5bf9\u78a7\u7389\u897f \u74dc\u548c\u90a3\u652f\u4f55\u9996\u4e4c\u4e86\u3002 \u3000\u3000\u6587\u9053\u5e84\u7684\u94b1\u9556\u6765\u5f97\u51fa\u4e4e\u4ed6\u4eec\u610f\u6599\u4e4b\u5916\uff0c\u6709\u4e00\u4e2a\u4eba\u89c1\u673a\u5f97\u5feb\uff0c\u7acb\u5373\u62a2\u4e86\u90a3\u5bf9\u897f\u74dc\u3002\u53ef\u662f\u4e5f \u8fd8\u662f\u8fdf\u4e86\u4e00\u6b65\uff0c\u78a7\u7389\u897f\u74dc\u867d\u7136\u5230\u624b\uff0c\u90a3\u652f\u4f55\u9996\u4e4c\u5374\u5df2\u662f\u4e0d\u7ffc\u800c\u98de\u3002\u201d \u3000\u3000\u600e\u6837\u4f1a\u201c\u4e0d\u7ffc\u800c\u98de\u201d\u5462\u3002\u539f\u6765\u6587\u9053\u5e84\u7684\u94b1\u9556\u624b\u6cd5\u5de7\u5999\u4e4b\u6781\uff0c\u90a3\u4e24\u679a\u94dc\u94b1\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u631f \u7740\u90a3\u652f\u4f55\u9996\u4e4c\uff0c\u628a\u5b83\u5e26\u4e86\u8d77\u6765\uff0c\u515c\u4e86\u4e00\u4e2a\u5708\u5b50\uff0c\u7adf\u7136\u56de\u5230\u6587\u9053\u5e84\u624b\u4e2d\uff0c\u5c09\u8fdf\u70af\u7684\u624b\u4e0b\u6700\u91cd\u89c6 \u7687\u5e1d\u7684\u793c\u7269\uff0c\u5374\u4e0d\u77e5\u8fd9\u652f\u4f55\u9996\u4e4c\u66f4\u4e3a\u5b9d\u8d35\uff0c\u4ed6\u4eec\u5728\u90a3\u7d27\u8981\u7684\u5173\u5934\u5148\u62a2\u897f\u74dc\uff0c\u8fd9\u5c31\u53ea\u597d\u7ed9\u6587\u9053 \u5e84\u4ee5\u53ef\u4e58\u4e4b\u673a\u4e86\u3002 \u3000\u3000\u7948\u5723\u56e0\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u59d3\u6587\u7684\uff0c\u4f60\u662f\u60f3\u548c\u6211\u6bd4\u6697\u5668\u4e48\uff1f\u201d\u4e00\u6296\u624b\u4e09\u70b9\u5bd2\u661f\u7acb\u5373\u5c31\u5411\u6587 \u9053\u5e84\u98de\u53bb\u3002\u5ea7\u4e2d\u4e0d\u4e4f\u6697\u5668\u9ad8\u624b\uff0c\u770b\u51fa\u4e86\u8fd9\u662f\u4e13\u6253\u7a74\u9053\u7684\u4e09\u679a\u900f\u9aa8\u9489\u3002 \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u6b63\u662f\u60f3\u9886\u6559\u4f60\u5343\u624b\u89c2\u97f3\u7684\u6697\u5668\u529f\u592b\u3002\u201d\u5f39\u6307\u95f4\u4e09\u679a\u94dc\u94b1\u518d\u98de\u51fa \u53bb\u3002\u53ea\u542c\u5f97\u201c\u94ee\u94ee\u201d\u58f0\u54cd\uff0c\u4e24\u679a\u900f\u9aa8\u9489\u548c\u4e24\u7267\u94dc\u94b1\u534a\u7a7a\u4e2d\u649e\u4e2a\u6b63\u7740\uff0c\u540c\u65f6\u843d\u5730\u3002\u53ef\u662f\u7b2c\u4e09\u679a \u900f\u9aa8\u9488\u5728\u5373\u5c06\u88ab\u94dc\u94b1\u78b0\u7740\u7684\u5239\u90a3\uff0c\u5374\u5ffd\u7136\u6539\u6210\u4e86\u5f27\u5f62\u98de\u53bb\uff0c\u500f\u5730\u5c31\u5230\u4e86\u6587\u9053\u5e84\u9762\u524d\u3002\u6587\u900d\u5e84 \u6599\u4e0d\u5230\u5979\u7684\u624b\u6cd5\u5982\u6b64\u5947\u5999\uff0c\u8981\u63a5\u5979\u7684\u6697\u5668\u4e5f\u6765\u4e0d\u53ca\uff0c\u767e\u5fd9\u4e2d\u53ea\u597d\u4e00\u4e2a\u201c\u4e4c\u9f9f\u7f29\u9888\u201d\uff0c\u201c\u53ee\u201d \u7684\u4e00\u58f0\uff0c\u90a3\u679a\u900f\u9aa8\u9489\u63d2\u5165\u4e86\u4ed6\u6240\u5750\u7684\u90a3\u5f20\u6905\u80cc\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u8f83\u91cf\uff0c\u6b63\u53ef\u8bf4\u662f\u5404\u6709\u5404\u5343\u79cb\u3002\u94dc\u94b1\u7684\u4efd\u91cf\u6bd4\u900f\u9aa8\u9489\u8f7b\uff0c\u6587\u9053\u5e84\u80fd\u591f\u7528\u94dc\u94b1\u6253\u843d\u7948 \u5723\u56e0\u7684\u900f\u9aa8\u9489\uff0c\u663e\u7136\u662f\u6587\u9053\u5e84\u7684\u5185\u529b\u8f83\u80dc\u4e00\u7b79\uff0c\u4f46\u6587\u9053\u5e84\u6bd5\u7adf\u8fd8\u662f\u4e0d\u80fd\u5c06\u5979\u7684\u900f\u9aa8\u9489\u5168\u90e8\u6253 \u843d\uff0c\u8bf4\u5230\u6697\u5668\u7684\u624b\u6cd5\uff0c\u5374\u5c31\u662f\u8f93\u7ed9\u4e86\u7948\u5723\u56e0\u4e86\u3002 \u3000\u3000\u7948\u5723\u56e0\u7684\u6697\u5668\u7ed9\u4eba\u6253\u843d\uff0c\u81ea\u5df1\u5374\u89c9\u5f97\u5931\u4e86\u9762\u5b50\uff0c\u52c3\u7136\u5927\u6012\uff0c\u5c31\u8981\u53d1\u4f5c\uff0c\u5c09\u8fdf\u70af\u7b11\u9053\uff1a \u201c\u56e0\u59b9\uff0c\u4f55\u5fc5\u8fd9\u6837\u7740\u6025\uff1f\u8fd9\u513f\u7684\u4e8b\u60c5\u5b8c\u4e86\uff0c\u54b1\u4eec\u518d\u627e\u4ed6\u7b97\u5e10\uff0c\u4f60\u6015\u8fd9\u652f\u4f55\u9996\u4e4c\u4ed6\u5c31\u541e\u5f97\u4e0b\u53bb \u5417\uff1f\u201d\u7948\u5723\u56e0\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u514d\u5f97\u591a\u4f24\u65e0\u8f9c\u3002\u59d3\u6587\u7684\uff0c\u7b49\u4e0b\u54b1\u4eec\u5230\u5916\u9762\u51b3\u80dc\u8d1f\uff0c\u5730\u65b9\u968f\u4f60\u968f \u4fbf\u3002\u201d\u6587\u9053\u5e84\u9053\uff1a\u201c\u4f60\u5b9a\u8981\u8f83\u91cf\uff0c\u6211\u4e00\u5b9a\u5949\u966a\uff0c\u8981\u53bb\u54b1\u4eec\u73b0\u5728\u5c31\u53bb\u3002\u201d \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u4e0d\u8981\u4e2d\u4ed6\u6fc0\u5c06\u4e4b\u8ba1\u3002\u201d\u9661\u5730\u4e00\u58f0\u5927\u559d\uff0c\u8bf4\u9053\uff1a\u201c\u59d3\u6587\u7684\uff0c\u521a\u624d\u7684\u4e8b\uff0c\u6211\u6682 \u4e14\u4e0d\u4e0e\u4f60\u8ba1\u8f83\u3002\u4ece\u73b0\u5728\u8d77\uff0c\u4f60\u6562\u518d\u52a8\uff0c\u6211\u5c31\u628a\u4f60\u7684\u8428\u5927\u4eba\u6740\u4e86\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u53f1\u54a4\u534e\u5802\u6765\u52ab\u5b9d\uff0c\u96c4\u98ce\u4e0d\u51cf\u5c11\u5e74\u65f6\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u5341\u4e5d\u56de\u3000\u62ed\u76ee\u60ca\u770b\u9f99\u864e\u6597\u3000\u4f24\u5fc3\u7591\u662f\u51e4\u9e3e\u4fe6 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u5341\u4e5d\u56de\u3000\u62ed\u76ee\u60ca\u770b\u9f99\u864e\u6597\u3000\u4f24\u5fc3\u7591\u662f\u51e4\u9e3e\u4fe6 \u3000\u3000\u5bff\u5802\u91cc\u9ad8\u624b\u5982\u4e91\uff0c\u5176\u4e2d\u5982\u53f2\u767d\u90fd\u3001\u6587\u9053\u5e84\u3001\u6c99\u5343\u5cf0\u7b49\u4eba\uff0c\u8bba\u672c\u9886\u672a\u5fc5\u5728\u5c09\u8fdf\u70af\u592b\u59bb\u4e4b \u4e0b\uff0c\u4f46\u56e0\u6295\u9f20\u5fcc\u5668\uff0c\u751f\u6015\u5c09\u8fdf\u70af\u4e00\u6012\u4e4b\u4e0b\uff0c\u5f53\u771f\u6740\u4e86\u4ed6\u4eec\u4e3b\u5b50\u3002\u56e0\u6b64\uff0c\u7ed9\u5c09\u8fdf\u70af\u8fd9\u4e48\u4e00\u5413\u4e4b \u540e\uff0c\u679c\u7136\u90fd\u662f\u4e0d\u6562\u5984\u52a8\u3002 \u3000\u3000\u8f6c\u773c\u95f4\u516b\u4ed9\u684c\u4e0a\u7684\u793c\u7269\u90fd\u5df2\u7ed9\u5c09\u8fdf\u70af\u7684\u624b\u4e0b\u88c5\u5165\u9ebb\u888b\u3002\u5c09\u8fdf\u70af\u7b11\u9053\uff1a\u201c\u8428\u5927\u4eba\uff0c\u70e6\u4f60\u4f20 \u4ee4\u4e0b\u53bb\uff0c\u6253\u5f00\u5927\u95e8\uff0c\u8ba9\u4ed6\u4eec\u51fa\u53bb\u3002\u6211\u7684\u4eba\u82e5\u662f\u635f\u4e86\u4e00\u6839\u6beb\u6bdb\uff0c\u6211\u5c31\u5265\u4f60\u4e00\u5c42\u5934\u76ae\u3002\u542c\u6e05\u695a\u6ca1 \u6709\uff1f\u201d \u3000\u3000\u8428\u798f\u9f0e\u5413\u5f97\u9762\u5982\u571f\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u662f\uff0c\u662f\uff01\u4e00\u5207\u542c\u4ece\u5c09\u8fdf\u70af\u7684\u5429\u5490\uff01\u201d\u5c09\u8fdf\u70af\u65e9\u5df2\u51c6\u5907\u4e86 \u5feb\u9a6c\u5728\u5916\u9762\u63a5\u5e94\uff0c\u8fd9\u51e0\u4e2a\u4eba\u4e00\u51fa\u5927\u95e8\uff0c\u4e0a\u9a6c\u4fbf\u8d70\u3002\u4f46\u5c09\u8fdf\u70af\u592b\u59bb\u5219\u8fd8\u662f\u7559\u5728\u5bff\u5802\u4e4b\u4e2d\u3002 \u3000\u3000\u8428\u798f\u9f0e\u9053\uff1a\u201c\u5c09\u8fdf\u8235\u4e3b\uff0c\u4f60\uff0c\u4f60\u53ef\u4ee5\u9ad8\u62ac\u8d35\u624b\u4e86\u5427\u3002\u201d\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u6025\u4ec0\u4e48\uff0c\u518d\u7b49\u4e00\u4f1a \u513f\u3002\u201d\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u542c\u5f97\u201c\u545c\u545c\u201d\u7684\u54cd\u7bad\u4e4b\u58f0\uff0c\u8fdc\u8fdc\u4f20\u6765\uff0c\u5c09\u8fdf\u70af\u7b11\u9053\uff1a\u201c\u8fd8\u7b97\u4f60\u8bc6\u76f8\uff0c\u6ca1 \u6709\u6d3e\u4eba\u8ffd\u8e2a\u3002\u201d\u539f\u6765\u8fd9\u662f\u4ed6\u7684\u624b\u4e0b\u62a5\u544a\u5e73\u5b89\u7684\u4fe1\u53f7\u3002\u6b64\u65f6\u4ed6\u4eec\u5df2\u7ecf\u5230\u4e86\u5b89\u5168\u5904\u6240\u4e86\u3002 \u3000\u3000\u8428\u798f\u9f0e\u82e6\u7b11\u9053\uff1a\u201c\u73b0\u5728\u53ef\u4ee5\u653e\u6211\u4e86\u5427\uff1f\u201d\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u6211\u4f1a\u653e\u4f60\u7684\uff0c\u4e0d\u8fc7\u8fd8\u8981\u9ebb\u70e6\u4f60\u966a \u6211\u8d70\u4e00\u6bb5\u8def\uff0c\u9001\u6211\u51fa\u57ce\uff01\u201d\u8428\u798f\u9f0e\u55eb\u55eb\u5685\u5685\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d\u5c09\u8fdf\u70af\u51b7\u7b11\u9053\uff1a\u201c\u4ec0 \u4e48\u8fd9\u4e2a\u90a3\u4e2a\uff0c\u4f60\u4e0d\u76f8\u4fe1\u6211\u5417\uff1f\u201d\u8428\u798f\u9f0e\u9053\uff1a\u201c\u4e0d\u6562\u3002\u4f46\u8fd9\u6837\u5bf9\u6211\u7684\u9762\u5b50\u53ef\u662f\u592a\u96be\u770b\u5440\uff01\u201d\u5c09 \u8fdf\u70af\u9053\uff1a\u201c\u4f60\u8981\u9762\u5b50\u8fd8\u662f\u8981\u6027\u547d\uff1f\u201d\u8428\u798f\u9f0e\u4e0d\u6562\u591a\u8bdd\uff0c\u8bf4\u4e86\u4e00\u4e2a\u201c\u662f\u201d\u5b57\u3002\u5c09\u8fdf\u70af\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u52a0\u97ad\u3002\u51fa\u4e86\u57ce\u95e8\uff0c\u6211\u5c31\u653e\u4f60\u3002\u8d70\uff01\u201d \u3000\u3000\u7b11\u58f0\u672a\u4e86\uff0c\u53f2\u767d\u90fd\u5ffd\u5730\u4e00\u638c\u5411\u8428\u798f\u9f0e\u80cc\u5fc3\u62cd\u4e0b\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u6837\u7684\u5bb3\u6c11\u8d3c\u5c82\u80fd\u653e\u4e86\uff1f\u201d\u8fd9 \u4e00\u4e0b\u7a81\u5982\u5176\u6765\uff0c\u4e0d\u4f46\u8428\u5e9c\u7684\u4eba\u4eba\u51fa\u610f\u5916\uff0c\u5c09\u8fdf\u70af\u4e5f\u662f\u4e1d\u6beb\u6ca1\u6709\u6599\u5230\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u672c\u6765\u662f\u7262\u7262\u6293\u7740\u8428\u798f\u9f0e\u7684\uff0c\u53f2\u767d\u90fd\u8fd9\u4e00\u638c\u4e00\u62cd\u4e0b\u6765\uff0c\u5c09\u8fdf\u70af\u9661\u7136\u95f4\u53ea\u89c9\u4e00\u80a1\u5927\u529b \u9707\u64bc\u4ed6\u7684\u864e\u53e3\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u677e\u5f00\u4e86\u624b\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53f2\u767d\u90fd\u5df2\u662f\u4e00\u628a\u5c06\u8428\u798f\u9f0e\u62c9\u4e86\u8fc7 \u53bb\u3002 \u3000\u3000\u539f\u6765\u53f2\u767d\u90fd\u4e00\u76f4\u5728\u76d8\u7b97\u7ed9\u8428\u798f\u9f0e\u89e3\u56f0\u4e4b\u7b56\uff0c\u5f85\u5230\u4ed6\u542c\u5f97\u5c09\u8fdf\u70af\u8981\u8428\u798f\u9f0e\u9001\u4ed6\u51fa\u57ce\uff0c\u8fd9\u624d \u7075\u673a\u4e00\u52a8\uff0c\u60f3\u5230\u4e86\u8fd9\u4e2a\u5999\u8ba1\u3002 \u3000\u3000\u4ed6\u60f3\u5c09\u8fdf\u70af\u65e2\u7136\u8981\u628a\u8428\u798f\u9f0e\u5f53\u4f5c\u62a4\u8eab\u7b26\uff0c\u7edd\u4e0d\u80af\u8f7b\u6613\u5c31\u4f24\u4e86\u8428\u798f\u9f0e\u7684\u6027\u547d\u3002\u540c\u65f6\u4ed6\u4e5f\u4f30 \u8ba1\u5f97\u51c6\uff1a\u5c09\u8fdf\u70af\u53ea\u662f\u9632\u5907\u6709\u4eba\u5411\u4ed6\u5077\u88ad\uff0c\u7edd\u60f3\u4e0d\u5230\u6709\u4eba\u4f1a\u5411\u8428\u798f\u9f0e\u5077\u88ad\u7684\u3002\u4ed6\u6253\u8428\u798f\u9f0e\u7684\u8fd9 \u4e00\u638c\u7528\u7684\u4e43\u662f\u201c\u9694\u5c71\u6253\u725b\u201d\u7684\u529f\u592b\uff0c\u8428\u798f\u9f0e\u4e1d\u6beb\u4e0d\u4f1a\u53d7\u4f24\uff0c\u8981\u53d7\u4f24\u9664\u975e\u662f\u5c09\u8fdf\u70af\u53d7\u4f24\uff0c\u5982\u679c \u5c09\u8fdf\u70af\u7684\u5185\u529b\u6bd4\u4e0d\u4e0a\u4ed6\u7684\u8bdd\u3002 \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u8428\u5927\u4eba\uff0c\u8bf7\u6055\u65e0\u793c\uff01\u201d\u8f7b\u8f7b\u4e00\u63a8\uff0c\u628a\u8428\u798f\u9f0e\u63a8\u8fc7\u4e00\u8fb9\u3002\u5c09\u8fdf\u70af\u51b7\u4e0d\u53ca\u9632\uff0c \u7740\u4e86\u9053\u513f\uff0c\u8981\u60f3\u593a\u56de\u4eba\u8d28\uff0c\u5df2\u662f\u8fdf\u4e86\u4e00\u8fdf\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u4e00\u58f0\u5927\u543c\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u4ee3\u8428\u798f\u9f0e\u9886\u6b7b\u5427\uff01\u201d\u58f0\u5982\u9739\u96f3\uff0c\u638c\u4f3c\u5954\u96f7\uff0c\u7acb\u5373 \u5411\u53f2\u767d\u90fd\u75db\u4e0b\u6740\u624b\u3002 \u3000\u3000\u53f2\u767d\u90fd\u521a\u624d\u7528\u201c\u9694\u5c71\u6253\u725b\u201d\u672a\u80fd\u4f24\u7740\u5c09\u8fdf\u70af\uff0c\u5df2\u77e5\u53cc\u65b9\u529f\u529b\u76f8\u5f53\u3002\u53f2\u767d\u90fd\u7b11\u9053\uff1a\u201c\u597d\u5927 \u7684\u53e3\u6c14\uff0c\u4f60\u5982\u4eca\u5df2\u662f\u63d2\u7ffc\u96be\u98de\uff0c\u8fd8\u60f3\u901e\u51f6\u4e48\uff1f\u201d\u53cc\u638c\u4e00\u4ea4\uff0c\u5c09\u8fdf\u70af\u8eab\u5f62\u4e00\u6643\uff0c\u53f2\u767d\u90fd\u5012\u9000\u4e09 \u6b65\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c09\u8fdf\u70af\u4e00\u4e2a\u201c\u8de8\u864e\u767b\u5c71\u201d\uff0c\u5de6\u62f3\u53f3\u638c\uff0c\u8fde\u73af\u5288\u6253\uff0c\u5927\u559d\u9053\uff1a\u201c\u6211\u5c09\u8fdf \u70af\u4e0d\u6253\u7b97\u751f\u51fa\u6b64\u95e8\uff0c\u4f46\u4e5f\u8981\u6bd9\u4e86\u4f60\u8fd9\u5c0f\u5b50\uff01\u201d\u8fd9\u4e00\u62db\u7adf\u662f\u4e24\u8d25\u4ff1\u4f24\u7684\u6253\u6cd5\uff0c\u6bd4\u521a\u624d\u90a3\u62db\u6740 \u624b\uff0c\u8fd8\u8981\u9738\u9053\u5f97\u591a\u3002 \u3000\u3000\u53f2\u767d\u90fd\u7684\u672c\u9886\u5e76\u4e0d\u8f93\u4e8e\u5c09\u8fdf\u70af\uff0c\u4f46\u89c1\u5c09\u8fdf\u70af\u8fd9\u526f\u8c41\u51fa\u6027\u547d\u7684\u51f6\u795e\u6076\u715e\u7684\u6a21\u6837\uff0c\u4e5f\u4e0d\u7981\u6709 \u51e0\u5206\u80c6\u602f\u3002\u53cc\u65b9\u529f\u529b\u6089\u654c\uff0c\u80c6\u5c0f\u7684\u81ea\u662f\u5403\u4e8f\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u53f2\u767d\u90fd\u7684\u62a4\u80a9\u5df2\u7ed9\u5c09\u8fdf \u70af\u6495\u88c2\uff0c\u4e94\u6307\u62c2\u8fc7\uff0c\u80a9\u5934\u706b\u8fa3\u8fa3\u4f5c\u75db\uff0c\u5e78\u800c\u4ed6\u5df2\u7ecf\u5316\u89e3\u4e86\u5c09\u8fdf\u70af\u7684\u516d\u4e03\u5206\u529b\u5ea6\uff0c\u53ea\u662f\u7ed9\u6307\u5c16 \u522e\u7834\u4e00\u70b9\u76ae\u8089\uff0c\u4e0d\u7b97\u53d7\u4f24\u3002 \u3000\u3000\u8428\u798f\u9f0e\u60ca\u9b42\u7a0d\u5b9a\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u4e0d\u5feb\u5feb\u7ed9\u6211\u628a\u8fd9\u5f3a\u76d7\u62ff\u4e0b\uff0c\u6d3b\u7684\u62ff\u4e0d\u4e86\uff0c\u6b7b\u7684\u4e5f \u8981\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u66fe\u5411\u8428\u5e9c\u4e2d\u4eba\u81ea\u5938\u201c\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\u201d\uff0c\u4e0d\u597d\u610f\u601d\u548c\u53f2\u767d\u90fd\u8054\u624b\u5939\u653b\u5c09\u8fdf\u70af\uff0c\u5fc3 \u9053\uff1a\u201c\u6211\u6293\u4f4f\u90a3\u4e2a\u5973\u8d3c\uff0c\u4e5f\u662f\u4e00\u5927\u529f\u52b3\u3002\u201d\u4e8e\u662f\u4e00\u8dc3\u800c\u51fa\uff0c\u5411\u7948\u5723\u56e0\u6251\u53bb\u3002 \u3000\u3000\u7948\u5723\u56e0\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u73b0\u5728\u5c31\u548c\u4f60\u7b97\u5e10\uff01\u201d\u4e00\u6296\u624b\uff0c\u63a5\u8fde\u6253\u51fa\u4e86\u900f\u9aa8\u9489\u3001\u94c1\u8fde\u5b50\u3001\u6885\u82b1 \u9488\u3001\u98de\u9556\u3001\u8896\u7bad\u7b49\u4e03\u516b\u79cd\u6697\u5668\u3002\u6587\u9053\u5e84\u8d5e\u9053\uff1a\u201c\u5343\u624b\u89c2\u97f3\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01\u201d\u8fd0\u638c\u6210\u98ce\uff0c\u817e \u8eab\u98de\u8d77\uff0c\u6697\u5668\u4ece\u4ed6\u8eab\u8fb9\u98de\u8fc7\uff0c\u6765\u62dc\u5bff\u7684\u5ba2\u4eba\u4eec\u53ef\u5012\u6963\u4e86\uff0c\u4ed6\u4eec\u6ca1\u6709\u6587\u9053\u5e84\u53ef\u4ee5\u8fd0\u638c\u6210\u98ce\u3001\u626b \u8361\u6697\u5668\u7684\u672c\u9886\uff0c\u4eba\u7fa4\u62e5\u6324\uff0c\u8981\u907f\u4e5f\u907f\u4e0d\u5f00\uff0c\u7948\u5723\u56e0\u53d1\u51fa\u516b\u79cd\u6697\u5668\uff0c\u5012\u4e0b\u53bb\u7684\u5374\u6709\u5341\u4e8c\u4e2a\u4eba\uff01 \u6709\u4e09\u4e2a\u4eba\u662f\u7ed9\u81ea\u5df1\u4eba\u649e\u8dcc\u7684\uff0c\u8fd8\u6709\u4e24\u4e2a\u66f4\u662f\u51a4\u6789\uff0c\u662f\u7ed9\u6587\u9053\u5e84\u7684\u638c\u529b\u9707\u6655\u7684\u3002 \u3000\u3000\u5ba2\u4eba\u4eec\u53d1\u4e00\u58f0\u558a\uff0c\u80c6\u5c0f\u7684\u3001\u81ea\u95ee\u672c\u9886\u63d2\u4e0d\u4e0a\u624b\u7684\u3001\u8fd8\u6709\u4e0d\u613f\u5377\u5165\u6f29\u6da1\uff0c\u7eb7\u7eb7\u593a\u95e8\u800c\u9003\uff0c \u5bff\u5802\u4e2d\u5269\u4e0b\u7684\u53ea\u662f\u4e00\u6d41\u9ad8\u624b\u548c\u4e0d\u80fd\u4e0d\u62fc\u547d\u7684\u8428\u5e9c\u536b\u58eb\u4e86\u3002\u4f46\u4e5f\u8fd8\u6709\u4e09\u4e94\u5341\u4eba\u4e4b\u591a\u3002\u4e0d\u8fc7\uff0c\u8fd9 \u5bff\u5802\u662f\u53ef\u4ee5\u5bb9\u7eb3\u6570\u767e\u4eba\u7684\uff0c\u5ba2\u4eba\u8dd1\u4e86\u5341\u4e4b\u516b\u4e5d\uff0c\u5df2\u7ecf\u662f\u6709\u8db3\u591f\u7684\u5730\u65b9\u53ef\u4ee5\u65bd\u5c55\u62f3\u811a\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u624b\u4e0b\u7684\u56db\u5927\u9999\u4e3b\uff0c\u770b\u89c1\u5e2e\u4e3b\u4f3c\u4e4e\u4e0d\u654c\u5bf9\u65b9\uff0c\u5f53\u4e0b\u4e5f\u987e\u4e0d\u5f97\u4ee5\u4f17\u51cc\u5be1\u4e4b\u5acc\uff0c\u8463\u5341\u4e09 \u5a18\u3001\u5706\u6d77\u3001\u9752\u7b26\u3001\u7126\u78ca\u56db\u4eba\u4e00\u62e5\u800c\u4e0a\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u5be1\u4e0d\u654c\u4f17\uff0c\u767b\u65f6\u9669\u8c61\u73af\u751f\uff0c\u8463\u5341\u4e8c\u5a18\u6253\u5f97\u6700\u72e0\uff0c\u5c09\u8fdf\u70af\u89c1\u5979\u662f\u4e2a\u5973\u5b50\uff0c\u7a0d\u4e3a\u5ffd \u89c6\uff0c\u5374\u4e0d\u77e5\u5728\u516d\u5408\u5e2e\u7684\u56db\u5927\u9999\u4e3b\u4e4b\u4e2d\u5b9e\u662f\u4ee5\u5979\u7684\u672c\u9886\u6700\u9ad8\uff0c\u51b7\u4e0d\u53ca\u9632\uff0c\u5c31\u7ed9\u5979\u5530\u7684\u6253\u4e86\u4e00 \u97ad\u3002\u9976\u662f\u5c09\u8fdf\u70af\u94dc\u76ae\u94c1\u9aa8\uff0c\u8fd9\u4e00\u97ad\u6253\u4e0b\uff0c\u80cc\u4e0a\u4e5f\u8d77\u4e86\u4e00\u9053\u8840\u75d5\uff01 \u3000\u3000\u6b64\u65f6\u6587\u9053\u5e84\u548c\u7948\u5723\u56e0\u4e5f\u4ea4\u4e0a\u624b\uff0c\u7948\u5723\u56e0\u89c1\u4e08\u592b\u53d7\u4f24\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u60f3\u8981\u51b2\u8fc7\u53bb\u6551\u63f4\uff0c\u5374\u7ed9 \u6587\u9053\u5e84\u5f53\u4e2d\u9694\u4f4f\u3002\u6587\u9053\u5e84\u7684\u771f\u5b9e\u672c\u9886\u5728\u7948\u5723\u56e0\u4e4b\u4e0a\uff0c\u8fd1\u8eab\u640f\u6597\uff0c\u6697\u5668\u96be\u65bd\uff0c\u7948\u5723\u56e0\u7ed9\u4ed6\u5835 \u4f4f\uff0c\u592b\u59bb\u7adf\u662f\u4e0d\u80fd\u4f1a\u5408\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u592b\u59bb\u540c\u9677\u56f0\u5883\uff0c\u773c\u770b\u5df2\u662f\u96be\u4ee5\u652f\u6301\uff0c\u8428\u798f\u9f0e\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u5bf9\u8d3c\u592b\u59bb\u80c6\u5b50\u4e5f \u672a\u514d\u592a\u5927\u4e86\uff0c\u5c45\u7136\u52ab\u5230\u4e86\u6211\u7684\u5bb6\u4e2d\uff01\u563f\uff0c\u563f\uff0c\u4f60\u4eec\u62a2\u53bb\u4e86\u7684\u4e1c\u897f\uff0c\u6211\u8981\u4f60\u4eec\u4e00\u4ef6\u4ef6\u5410\u51fa \u6765\uff01\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u662f\u8981\u4ed6\u7684\u624b\u4e0b\u628a\u5c09\u8fdf\u70af\u592b\u59bb\u6d3b\u64d2\uff0c\u82e6\u5211\u8ffd\u8d43\u3002\u8428\u798f\u9f0e\u672c\u6765\u662f\u8bf4\u8fc7\u201c\u6b7b\u6d3b\u4e0d \u8bba\u201d\u7684\uff0c\u6b64\u65f6\u4e3a\u4e86\u75db\u60dc\u90a3\u4e9b\u5931\u53bb\u7684\u793c\u7269\uff0c\u53e3\u98ce\u6539\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5c09\u8fdf\u70af\u6765\u7ed9\u4e49\u519b\u52a8\u9977\uff0c\u4e0d\u6127\u662f\u4e2a\u82f1\u96c4\uff0c\u6211\u5c82\u80fd\u5750\u89c6\u4e0d\u6551\uff1f\u201d\u6b63\u8981\u51fa \u624b\uff0c\u4e0d\u6599\u5374\u6709\u4e00\u4e2a\u4eba\u5df2\u7ecf\u62a2\u5728\u4ed6\u7684\u7684\u5934\uff0c\u5148\u51fa\u6765\u4e86\u3002\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u91d1\u9010\u6d41\u5bf9\u4ed6\u8d77\u4e86\u6000\u7591\uff0c \u60f3\u8981\u548c\u4ed6\u7ed3\u8bc6\u7684\u90a3\u4e2a\u5c11\u5e74\u3002 \u3000\u3000\u53ea\u89c1\u8fd9\u201c\u5c11\u5e74\u201d\u4e00\u8dc3\u800c\u51fa\uff0c\u628a\u5e3d\u5b50\u8131\u4e0b\uff0c\u9732\u51fa\u4e86\u6ee1\u5934\u79c0\u53d1\uff0c\u53eb\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u4f55\u82e6\u52a9\u7ea3 \u4e3a\u8650\uff1f\u201d\u53f2\u7ea2\u82f1\u771f\u76f8\u4e00\u9732\uff0c\u6ee1\u5802\u5927\u60ca\uff0c\u91d1\u9010\u6d41\u5c24\u5176\u662f\u53c8\u60ca\u53c8\u559c\uff0c\u4e00\u65f6\u95f4\u4e0d\u89c9\u5446\u4e86\u3002 \u3000\u3000\u8428\u798f\u9f0e\u5403\u4e86\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u8c01\u662f\u4f60\u7684\u54e5\u54e5\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u662f\u516d\u5408\u5e2e\u5e2e\u4e3b\u53f2\u767d\u90fd\u4e4b\u59b9\uff0c\u52ab\u4f60\u8fd9\u72d7\u5b98\u7684\u793c\u7269\u7684\uff0c\u6211\u4e5f\u6709\u4efd\uff01\u201d \u3000\u3000\u8428\u798f\u9f0e\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\uff0c\u8fd9\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u6da8\u7ea2\u4e86\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u820d\u59b9\u80e1\u4f5c\u975e\u4e3a\uff0c\u6211\u81ea\u4f1a\u5c06\u5979\u60e9\u6cbb\uff01\u201d\u820d\u4e86\u5c09\u8fdf\u70af\uff0c\u6251\u4e0a\u53bb\u6293 \u4ed6\u59b9\u59b9\u3002\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u8bf7\u542c\u6211\u4e00\u8a00\u2026\u2026\u201d\u53f2\u767d\u90fd\u5927\u559d\u9053\uff1a\u201c\u6211\u6ca1\u6709\u4f60\u8fd9\u4e2a\u59b9\u59b9\uff01\u201d\u53f2 \u767d\u90fd\u751f\u6015\u5979\u8bf4\u51fa\u66f4\u5176\u4e0d\u4e2d\u542c\u7684\u8bdd\u6765\uff0c\u547c\u7684\u4e00\u638c\u5c31\u5288\u8fc7\u53bb\uff0c\u628a\u53f2\u7ea2\u82f1\u7684\u8bf4\u8bdd\u6253\u65ad\u4e86\uff01 \u3000\u3000\u53f2\u767d\u90fd\u4e00\u51fa\u638c\u6253\u4ed6\u59b9\u59b9\uff0c\u7acb\u5373\u6709\u4e24\u4e2a\u4eba\u540c\u65f6\u5411\u4ed6\u6251\u53bb\uff0c\u5176\u4e2d\u7684\u4e00\u4e2a\u5c31\u662f\u91d1\u9010\u6d41\u3002\u91d1\u9010\u6d41 \u8eab\u6cd5\u5feb\u6781\uff0c\u4f46\u53e6\u4e00\u4e2a\u4eba\u5374\u662f\u53f2\u7ea2\u82f1\u8ddd\u79bb\u8f83\u8fd1\uff0c\u6bd4\u91d1\u9010\u6d41\u5feb\u4e86\u4e00\u6b65\u3002\u3002 \u3000\u3000\u53f2\u767d\u90fd\u542c\u5f97\u80cc\u540e\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u201c\u54ea\u91cc\u6765\u7684\u8fd9\u4e2a\u9ad8\u624b\uff1f\u201d\u53cd\u624b\u4e00\u638c\uff0c\u6293\u90a3\u4eba \u7684\u7435\u7436\u9aa8\uff0c\u90a3\u4eba\u5251\u950b\u4e00\u8f6c\uff0c\u970e\u7684\u6307\u5230\u4e86\u4ed6\u80c1\u4e0b\u7684\u201c\u6108\u6c14\u7a74\u201d\uff0c\u8fd9\u4e00\u62db\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u53f2\u767d \u90fd\u8feb\u5f97\u95ea\u8fc7\u4e00\u8fb9\uff0c\u7acb\u5373\u4e00\u4e2a\u201c\u5f39\u817f\u201d\u8e22\u51fa\uff0c\u90a3\u4eba\u89c1\u4ed6\u6765\u52bf\u51f6\u731b\uff0c\u811a\u5c16\u4e00\u70b9\uff0c\u5e73\u5730\u62d4\u8d77\uff0c\u633d\u4e86 \u4e00\u6735\u5251\u82b1\uff0c\u5411\u4ed6\u5934\u9876\u523a\u4e0b\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53f2\u767d\u90fd\u5df2\u7ecf\u62d4\u5251\u51fa\u9798\uff0c\u4e00\u62db\u201c\u4e3e\u706b\u9563\u5929\u201d\uff0c\u53cc \u5251\u76f8\u4ea4\uff0c\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u9707\u5f97\u4f17\u4eba\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\u3002 \u3000\u3000\u8fd9\u51e0\u62db\u6025\u5982\u7535\u5149\u77f3\u706b\uff0c\u53cc\u65b9\u5404\u4ee5\u4e0a\u4e58\u7684\u6b66\u529f\u76f8\u640f\uff0c\u7a0d\u4e00\u4e0d\u614e\uff0c\u5c31\u6709\u8840\u6e85\u5c18\u57c3\u7684\u5371\u9669\u3002\u51e0 \u62db\u4e00\u8fc7\uff0c\u53f2\u767d\u90fd\u867d\u7136\u7a0d\u5360\u4e0a\u98ce\uff0c\u5374\u4e5f\u672a\u80fd\u4f24\u5f97\u90a3\u4eba\uff0c\u5fc3\u4e2d\u4e0d\u7981\u9a87\u7136\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u4eba\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u9e5e\u5b50\u7ffb\u8eab\uff0c\u5df2\u662f\u843d\u5728\u53f2\u7ea2\u82f1\u7684\u8eab\u8fb9\uff0c\u7b11\u9053\uff1a\u201c\u53f2\u59d1 \u5a18\uff0c\u54b1\u4eec\u5171\u8fc7\u5bcc\u8d35\uff0c\u4eca\u5929\u4e5f\u8be5\u5171\u540c\u60a3\u96be\u4e86\uff01\u201d\u53f2\u767d\u90fd\u5706\u7741\u4e86\u53cc\u773c\u559d\u9053\uff1a\u201c\u8d31\u4e2b\u5934\uff0c\u8fd9\u5c0f\u5b50\u662f \u8c01\uff1f\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u670b\u53cb\uff0c\u600e\u4e48\u6837\uff1f\u201d\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u95ee\u6211\u4e48\uff1f\u6211\u662f\u548c\u4ee4\u59b9\u5408\u4f19\u52ab \u8fd9\u4f4d\u8428\u5927\u4eba\u793c\u7269\u7684\u4eba\uff0c\u4f60\u4e0d\u5fc5\u751f\u6c14\uff0c\u6211\u672c\u6765\u51c6\u5907\u4e00\u4efd\u8d43\u7ed9\u4f60\u7684\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u539f\u6765\u662f\u4f60\u79bb\u95f4\u6211\u4eec\u5144\u59b9\uff0c\u6211\u975e\u6740\u4f60\u4e0d\u53ef\uff01\u201d\u5530\u5530\u4e24\u5251\uff0c\u5f3a \u653b\u8fc7\u53bb\uff0c\u6c14\u6d41\u6fc0\u8361\uff0c\u5251\u5c16\u4e0a\u53d1\u51fa\u201c\u55e4\u55e4\u201d\u58f0\u54cd\uff0c\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u662f\u4f60\u903c\u5f97\u6211\u975e\u548c\u4f60\u52a8\u624b \u4e0d\u884c\u4e86\uff01\u201d\u94f6\u97ad\u6325\u51fa\uff0c\u4e0e\u90a3\u4eba\u7684\u957f\u5251\u914d\u5408\uff0c\u654c\u4f4f\u4e86\u53f2\u767d\u90fd\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6b64\u65f6\u5df2\u8ba4\u51fa\u4e86\u8fd9\u4e2a\u4eba\uff0c\u4e0d\u89c9\u53c8\u60ca\u53c8\u559c\u53c8\u662f\u7eb3\u95f7\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u60c5\uff1f\u674e\u5927\u54e5\u548c \u7ea2\u82f1\u4e5f\u662f\u65e9\u5c31\u76f8\u8bc6\u4e86\u7684\u4e48\uff1f\u201d\u539f\u6765\u8fd9\u4e2a\u529b\u654c\u53f2\u767d\u90fd\u7684\u5c11\u5e74\uff0c\u6b63\u662f\u91d1\u9010\u6d41\u6628\u65e5\u5728\u957f\u57ce\u76f8\u8bc6\uff0c\u548c \u4ed6\u7ed3\u4e3a\u516b\u62dc\u4e4b\u4ea4\u7684\u90a3\u4e2a\u674e\u5357\u661f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u56e0\u4e3a\u5fc3\u4e2d\u7eb3\u95f7\uff0c\u4e0d\u89c9\u5446\u4e86\u4e00\u5446\u3002\u6709\u4e24\u4e2a\u536b\u58eb\u622a\u4f4f\u4e86\u4ed6\u3002\u91d1\u9010\u6d41\u556a\u556a\u4e24\u638c\uff0c\u628a\u8fd9\u4e24 \u4e2a\u536b\u58eb\u6253\u5f97\u53d8\u4f5c\u4e86\u6eda\u5730\u846b\u82a6\u3002\u6253\u8fc7\u4e4b\u540e\uff0c\u91d1\u9010\u6d41\u65b9\u59cb\u9192\u89c9\u81ea\u5df1\u51fa\u624b\u592a\u91cd\uff0c\u5bf9\u4ed8\u8fd9\u6837\u7684\u4e24\u4e2a\u536b \u58eb\u5176\u5b9e\u662f\u65e0\u987b\u4f7f\u7528\u6740\u624b\u7684\uff0c\u539f\u6765\u91d1\u9010\u6d41\u4e43\u662f\u5728\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u628a\u4e00\u8154\u95f7\u6c14\u90fd\u53d1\u6cc4\u5728\u8fd9\u4e24\u4e2a\u536b \u58eb\u8eab\u4e0a\u3002\u53ef\u662f\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u660e\u767d\uff0c\u4e3a\u4ec0\u4e48\u81ea\u5df1\u7684\u706b\u6c14\u7a81\u7136\u53d1\u4f5c\u6309\u637a\u4e0d\u4f4f\u7684\u7f18\u6545\u3002 \u3000\u3000\u674e\u5357\u661f\u548c\u53f2\u7ea2\u82f1\u540c\u65f6\u53d1\u73b0\u4e86\u91d1\u9010\u6d41\uff0c\u91d1\u9010\u6d41\u662f\u5316\u4e86\u88c5\u6765\u7684\uff0c\u53f2\u7ea2\u82f1\u4e00\u65f6\u8fd8\u672a\u8ba4\u51fa\uff0c\u674e\u5357 \u661f\u4e00\u89c1\u4ed6\u9732\u51fa\u8fd9\u624b\u529f\u592b\uff0c\u5374\u5df2\u77e5\u9053\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6253\u7ffb\u4e86\u4e24\u540d\u536b\u58eb\uff0c\u53eb\u9053\uff1a\u201c\u5927\u54e5\u2026\u2026\u201d\u674e\u5357\u661f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u4e5f\u6765\u4e86\u4e48\uff1f \u6709\u6211\u7167\u6599\u53f2\u59d1\u5a18\uff0c\u4e0d\u5fc5\u4f60\u6765\u5e2e\u624b\u4e86\uff01\u201d\u53f2\u7ea2\u82f1\u53eb\u9053\uff1a\u201c\u91d1\u5927\u54e5\uff0c\u539f\u6765\u662f\u4f60\u5440\uff01\u201d\u4e09\u4e2a\u4eba\u540c\u65f6 \u8bf4\u8bdd\uff0c\u5404\u8bf4\u5404\u7684\uff0c\u53ea\u662f\u91d1\u9010\u6d41\u5374\u6709\u70b9\u5fc3\u70e6\u610f\u4e71\uff0c\u53eb\u4e86\u4e00\u58f0\u201c\u5927\u54e5\u201d\u4e4b\u540e\uff0c\u5e95\u4e0b\u7684\u8bdd\u5c31\u8bf4\u4e0d\u4e0b \u53bb\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u653b\u5f97\u6781\u7d27\uff0c\u53f2\u7ea2\u82f1\u53ea\u8bf4\u5f97\u4e00\u53e5\u8bdd\uff0c\u4e5f\u5c31\u4e0d\u80fd\u4e0d\u7528\u5fc3\u5bf9\u4ed8\u4e86\u3002\u201d \u3000\u3000\u6b64\u65f6\u573a\u4e2d\u5f62\u52bf\uff0c\u5c09\u8fdf\u70af\u529b\u6218\u516d\u5408\u5e2e\u7684\u56db\u5927\u9999\u4e3b\uff0c\u7a0d\u5904\u4e0b\u98ce\uff0c\u4e5f\u4e0d\u600e\u6837\u5403\u4e8f\uff0c\u7948\u5723\u56e0\u72ec\u6597 \u6587\u9053\u5e84\uff0c\u5374\u662f\u6709\u70b9\u652f\u6301\u4e0d\u4f4f\u7684\u6a21\u6837\u3002 \u3000\u3000\u91d1\u9010\u6d41\u65e0\u6687\u7ec6\u60f3\uff0c\u674e\u5357\u661f\u548c\u53f2\u7ea2\u82f1\u7684\u672c\u9886\u4ed6\u662f\u77e5\u9053\u7684\uff0c\u4ed6\u4eec\u4e8c\u4eba\u8054\u624b\uff0c\u6599\u60f3\u53f2\u767d\u90fd\u4e5f\u5948 \u4f55\u4e0d\u4e86\u4ed6\u4eec\u3002\u7948\u5723\u56e0\u90a3\u8fb9\u7684\u5f62\u52bf\u5982\u662f\u6700\u4e3a\u5371\u9669\uff0c\u4e8e\u662f\u91d1\u9010\u6d41\u8eab\u5f62\u4e00\u6643\uff0c\u5c31\u671d\u6587\u9053\u5e84\u6251\u53bb\u3002 \u3000\u3000\u6587\u9053\u5e84\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u4e00\u89c9\u80cc\u540e\u5fae\u98ce\u98d2\u7136\uff0c\u53cd\u624b\u4fbf\u662f\u4e00\u6293\u3002\u8fd9\u4e00\u6293\u51c6\u786e\u65e0\u6bd4\uff0c\u4e09 \u53ea\u6307\u5934\uff0c\u6070\u6070\u6263\u4f4f\u4e86\u91d1\u9010\u6d41\u7684\u5bf8\u5173\u5c3a\u8109\u3002\u8fd9\u4e2a\u90e8\u4f4d\u4e43\u662f\u624b\u5c11\u9633\u7ecf\u8109\u6c47\u805a\u4e4b\u70b9\uff0c\u591a\u597d\u6b66\u529f\u82e5\u7ed9 \u5bf9\u65b9\u6293\u4f4f\u4e86\u8fd9\u4e2a\u90e8\u4f4d\u4e5f\u662f\u4e0d\u80fd\u52a8\u5f39\u3002 \u3000\u3000\u6587\u9053\u5e84\u5df2\u77e5\u6765\u8001\u662f\u91d1\u9010\u6d41\uff0c\u60f3\u4e0d\u5230\u4e00\u6293\u5c31\u80fd\u628a\u4ed6\u6293\u4f4f\uff0c\u5012\u662f\u5927\u5927\u51fa\u4e4e\u6587\u9053\u5e84\u610f\u6599\u4e4b\u5916\u3002 \u6587\u9053\u5e84\u7981\u4e0d\u4f4f\u5fc3\u5ff5\u4e00\u52a8\uff1a\u201c\u8fd9\u5c0f\u5b50\u7684\u672c\u9886\u51b3\u4e0d\u5728\u6211\u4e4b\u4e0b\uff0c\u4f55\u4ee5\u8fd9\u4e48\u5bb9\u6613\u7ed9\u6211\u6293\u4f4f\uff1f\u201d\u5fc3\u5ff5\u672a \u5df2\uff0c\u53ea\u89c9\u5c0f\u81c2\u4e00\u9ebb\uff0c\u91d1\u9010\u6d41\u7684\u6307\u5c16\u53cd\u800c\u6233\u7740\u4e86\u4ed6\u7684\u864e\u53e3\u3002 \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u6709\u98a0\u5012\u7a74\u9053\u7684\u529f\u592b\uff0c\u4e0d\u6015\u5bf9\u65b9\u5236\u7740\u4ed6\u7684\u7ecf\u8109\u3002\u4e0d\u8fc7\uff0c\u53cc\u65b9\u5185\u529b\u76f8\u5f53\uff0c\u8fd9\u7a76\u7adf \u8fd8\u662f\u5341\u5206\u5192\u9669\u7684\u4e00\u7740\uff0c\u91d1\u9010\u6d41\u8155\u8109\u88ab\u6263\uff0c\u7ecf\u8109\u867d\u7136\u4e0d\u6015\u53d7\u4f24\uff0c\u5185\u529b\u5374\u662f\u6253\u4e86\u6298\u6263\uff01\u4ed6\u672c\u6765\u8981 \u7528\u91cd\u624b\u6cd5\u70b9\u6587\u9053\u5e84\u7684\u7a74\u9053\uff0c\u7ed3\u679c\u53ea\u662f\u4ee4\u5f97\u6587\u9053\u5e84\u864e\u53e3\u53d7\u9707\uff0c\u672a\u80fd\u5c3d\u5982\u6240\u613f\u3002 \u3000\u3000\u4f46\u867d\u7136\u5982\u6b64\uff0c\u6587\u9053\u5e84\u5df2\u662f\u5403\u4e8f\u4e0d\u5c0f\u4e86\uff0c\u864e\u53e3\u4e00\u9707\uff0c\u604d\u5982\u89e6\u7535\uff0c\u5fd9\u4e0d\u8fed\u7684\u628a\u624b\u677e\u5f00\u3002\u7948\u5723 \u56e0\u5530\u7684\u4e00\u97ad\u626b\u5c06\u8fc7\u53bb\uff0c\u6587\u9053\u5e84\u65e0\u6cd5\u95ea\u907f\uff0c\u767e\u5fd9\u4e2d\u632f\u81c2\u906e\u62e6\uff0c\u7948\u5723\u56e0\u7684\u8f6f\u97ad\u7ed9\u4ed6\u8361\u5f00\uff0c\u6587\u9053\u5e84 \u7684\u53f3\u81c2\u8d77\u4e00\u9053\u6de1\u6de1\u7684\u8840\u75d5\u3002 \u3000\u3000\u7948\u5723\u56e0\u5f97\u91d1\u9010\u6d41\u4e4b\u52a9\uff0c\u6253\u9000\u4e86\u6587\u9053\u5e84\uff0c\u767b\u65f6\u8dc3\u51fa\u5708\u5b50\uff0c\u4e00\u626c\u624b\u4f7f\u51fa\u4e86\u201c\u5929\u5973\u6563\u82b1\u201d\u7684\u624b \u6cd5\uff0c\u6697\u5668\u96e8\u70b9\u822c\u7684\u5411\u7740\u56f4\u653b\u5c09\u8fdf\u70af\u7684\u90a3\u4e9b\u4eba\u6253\u53bb\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u6325\u821e\u957f\u97ad\uff0c\u906e\u62e6\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u7948\u5723\u56e0\u6240\u53d1\u7684\u6697 \u5668\u6ca1\u6709\u4e00\u679a\u6253\u5230\u5979\u7684\u8eab\u4e0a\u3002\u5706\u6d77\u5728\u5979\u63a9\u62a4\u4e4b\u4e0b\uff0c\u4e5f\u662f\u4e1d\u6beb\u6ca1\u6709\u53d7\u4f24\u3002\u9752\u7b26\u9053\u4eba\u5251\u672f\u751a\u9ad8\uff0c\u4e5f \u6253\u843d\u4e86\u7948\u5723\u56e0\u6253\u5411\u4ed6\u7684\u4e09\u679a\u900f\u9aa8\u9488\u3002\u4f46\u53e6\u4e00\u4f4d\u56db\u5927\u9999\u4e3b\u4e4b\u4e00\u7684\u7126\u78ca\uff0c\u5728\u6697\u5668\u6253\u6765\u4e4b\u65f6\uff0c\u6b63\u5728 \u5fd9\u4e8e\u62db\u67b6\u5c09\u8fdf\u70af\u5288\u4ed6\u7684\u4e00\u5200\uff0c\u5fc3\u96be\u4e24\u7528\uff0c\u4e2d\u4e86\u7948\u5723\u56e0\u7684\u4e00\u679a\u6885\u82b1\u9488\u3002 \u3000\u3000\u7126\u78ca\u662f\u72ec\u811a\u5927\u76d7\u51fa\u8eab\uff0c\u6b66\u529f\u672c\u6765\u4e0d\u5f31\uff0c\u5728\u516d\u5408\u5e2e\u7684\u56db\u5927\u9999\u4e3b\uff0c\u4ed6\u867d\u7136\u6bd4\u4e0d\u4e0a\u8463\u5341\u4e8c\u5a18\uff0c \u5374\u5728\u5706\u6d77\u4e4b\u4e0a\uff0c\u548c\u9752\u7b26\u9053\u4eba\u5728\u4f2f\u4ef2\u4e4b\u95f4\u3002\u4e0d\u6599\u4e2d\u4e86\u8fd9\u4e00\u679a\u5c0f\u5c0f\u7684\u6885\u82b1\u9488\uff0c\u6070\u6070\u5c04\u7740\u4ed6\u7684\u5173\u8282 \u8981\u5bb3\uff0c\u4e00\u6761\u624b\u81c2\u767b\u65f6\u4e0d\u80fd\u52a8\u5f39\u3002\u5c09\u8fdf\u70af\u4f55\u7b49\u5389\u5bb3\uff0c\u95ea\u7535\u822c\u4e00\u5200\u5288\u4e0b\uff0c\u7126\u78ca\u53d7\u4e86\u4f24\u7684\u624b\u81c2\u518d\u7740 \u4e00\u5200\uff0c\u75db\u5f97\u4ed6\u6740\u732a\u822c\u5730\u53eb\uff0c\u731b\u8df3\u51fa\u53bb\uff0c\u6d51\u8eab\u4e0a\u4e0b\u4e00\u7247\u901a\u7ea2\uff0c\u53d8\u6210\u4e86\u4e00\u4e2a\u8840\u4eba\u3002\u8df3\u51fa\u5708\u5b50\uff0c\u4eba \u4e5f\u5c31\u6655\u5012\u4e86\u3002 \u3000\u3000\u8bf4\u7684\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7948\u5723\u56e0\u5df2\u6765\u5230\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5973\u8d3c\uff0c\u4e5f\u914d\u4f7f\u97ad\uff0c\u6211\u5c31\u4e0e\u4f60\u8f83\u91cf\u8f83 \u91cf\u97ad\u6cd5\uff01\u201d\u7948\u5723\u56e0\u53f7\u79f0\u201c\u97ad\u5251\u53cc\u7edd\u201d\uff0c\u521a\u624d\u4e0e\u6587\u9053\u5e84\u4ea4\u624b\uff0c\u672a\u80fd\u5c3d\u5c55\u6240\u957f\uff0c\u5982\u4eca\u78b0\u4e0a\u4e86\u529f\u529b \u8f83\u6587\u9053\u5e84\u7a0d\u900a\u7684\u8463\u5341\u4e09\u5a18\uff0c\u53cc\u65b9\u65d7\u9f13\u76f8\u5f53\uff0c\u7948\u5723\u56e0\u7684\u5251\u6cd5\u5374\u6bd4\u8463\u5341\u4e09\u5a18\u66f4\u4e3a\u7cbe\u5999\uff0c\u767b\u65f6\u628a\u8463 \u5341\u4e09\u5a18\u6253\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u53ea\u6709\u62db\u67b6\u4e4b\u529f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u6307\u903c\u9000\u4e86\u6587\u9053\u5e84\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u591a\u8c22\uff01\u591a\u8c22\uff01\u201d\u539f\u6765\u5728\u53cc\u65b9\u4e00\u5408\u5373\u5206\u4e4b\u9645\uff0c\u91d1 \u9010\u6d41\u662f\u65bd\u5c55\u7a7a\u7a7a\u5999\u624b\uff0c\u5077\u4e86\u6587\u9053\u5e84\u8eab\u4e0a\u90a3\u652f\u4f55\u9996\u4e4c\u3002 \u3000\u3000\u6c99\u5343\u5cf0\u90d1\u96c4\u56fe\u53cc\u53cc\u62a2\u4e0a\uff0c\u6c99\u5343\u5cf0\u5148\u5230\uff0c\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u8def\u8fc7\u8d35\u5e2e\uff0c\u591a\u8499\u62db\u5f85\uff0c\u4eca\u65e5\u501f \u82b1\u732e\u4f5b\uff0c\u656c\u4f60\u4e00\u676f\u3002\u201d\u968f\u624b\u5728\u516b\u4ed9\u684c\u4e0a\u6293\u8d77\u4e00\u4e2a\u9152\u58f6\uff0c\u5411\u6c99\u5343\u5cf0\u5288\u9762\u6253\u53bb\u3002 \u3000\u3000\u6c99\u5343\u5cf0\u638c\u529b\u8db3\u4ee5\u5f00\u9614\u88c2\u77f3\uff0c\u6a2a\u638c\u4e00\u51fb\uff0c\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u9152\u58f6\u5f53\u4e2d\u88c2\u5f00\uff0c\u6c99\u5343\u5cf0\u7ed9\u8fd9\u4e00\u58f6 \u9152\u6cfc\u5f97\u6ee1\u5934\u6ee1\u9762\uff0c\u773c\u775b\u90fd\u51e0\u4e4e\u7741\u4e0d\u5f00\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u91d1\u9010\u6d41\u5df2\u7ecf\u843d\u5230\u4ed6 \u7684\u8eab\u524d\uff0c\u6c99\u5343\u5cf0\u84e6\u5730\u4e00\u62f3\u64d2\u51fa\uff0c\u91d1\u9010\u6d41\u4f7f\u4e86\u4e00\u62db\u201c\u5929\u4e3b\u6258\u5854\u201d\u7684\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u4e00\u6258\u8098\u5c16\uff0c\u559d \u9053\uff1a\u201c\u53bb\uff01\u201d\u6c99\u5343\u5cf0\u7684\u6c14\u529b\u5df2\u7ecf\u4f7f\u8db3\uff0c\u6536\u52bf\u4e0d\u4f4f\uff0c\u53ea\u662f\u7ed9\u91d1\u9010\u6d41\u8f7b\u8f7b\u7684\u4e00\u5e26\uff0c\u6574\u4e2a\u8eab\u5b50\u4fbf\u4f3c \u76ae\u7403\u822c\u7684\u7ed9\u629b\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u4e2a\u8f6c\u8eab\u53c8\u8fce\u4e0a\u4e86\u90d1\u96c4\u56fe\uff0c\u90d1\u96c4\u56fe\u8bc6\u5f97\u4ed6\u7684\u5389\u5bb3\uff0c\u614c\u5fd9\u4e00\u638c\u5288\u4e0b\uff0c\u559d\u9053\uff1a\u201c\u6211\u4e0e \u4f60\u62fc\u4e86\uff01\u201d\u90d1\u96c4\u56fe\u7684\u638c\u5fc3\u9c9c\u7ea2\u5982\u8840\uff0c\u638c\u98ce\u4e2d\u4e14\u9690\u9690\u5e26\u7740\u8165\u5473\u3002 \u3000\u3000\u539f\u6765\u90d1\u96c4\u56fe\u81ea\u6043\u7ec3\u6709\u6bd2\u7802\u638c\u7684\u529f\u592b\uff0c\u4ee5\u4e3a\u91d1\u9010\u6d41\u4e0d\u6562\u4e0e\u4ed6\u786c\u62fc\uff0c\u82e5\u7136\u786c\u62fc\uff0c\u81f3\u591a\u4e5f\u662f\u4e24 \u8d25\u4ff1\u4f24\uff0c\u81ea\u5df1\u8fd9\u8fb9\u4eba\u591a\uff0c\u540e\u63f4\u4e00\u81f3\uff0c\u91d1\u9010\u6d41\u5fc5\u5c06\u53d7\u56f0\uff0c\u800c\u81ea\u5df1\u5374\u53ef\u4ee5\u4ece\u5bb9\u7597\u4f24\u3002 \u3000\u3000\u90d1\u96c4\u56fe\u7684\u7b97\u76d8\u6253\u5f97\u5982\u610f\uff0c\u4e0d\u6599\u4e00\u638c\u5288\u4e0b\u4e00\u53ea\u89c1\u91d1\u9010\u6d41\u7fd8\u8d77\u4e2d\u6307\uff0c\u6307\u5c16\u5bf9\u6b63\u4ed6\u638c\u5fc3\u7684\u201c\u52b3 \u5bab\u7a74\u201d\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8981\u62fc\u547d\u4e48\uff1f\u6211\u628a\u4f60\u8fd9\u72d7\u722a\u5e9f\u4e86\uff0c\u770b\u4f60\u5982\u4f55\u62fc\u547d\uff1f\u201d\u90d1\u96c4\u56fe\u662f\u4e2a\u8bc6\u8d27\u7684 \u4eba\uff0c\u89c1\u4ed6\u8fd9\u4e00\u6307\u6233\u51fa\uff0c\u4e0d\u89c9\u5927\u60ca\u3002\u539f\u6765\u201c\u52b3\u5bab\u7a74\u201d\u662f\u624b\u5c11\u9633\u7ecf\u8109\u7684\u7ec8\u70b9\uff0c\u201c\u52b3\u5bab\u7a74\u201d\u82e5\u7ed9\u5bf9 \u65b9\u7528\u91cd\u624b\u6cd5\u6233\u4f24\uff0c\u771f\u6c14\u4e00\u6cc4\uff0c\u8fd9\u6bd2\u7802\u638c\u7684\u529f\u592b\u5c31\u8981\u7acb\u5373\u7834\u4e86\u3002\u4ee5\u540e\u518d\u7ec3\uff0c\u81f3\u5c11\u4e5f\u5f97\u82b1\u5341\u5e74\u65f6 \u95f4\u3002 \u3000\u3000\u90d1\u96c4\u56fe\u867d\u7136\u53e3\u8bf4\u8981\u62fc\uff0c\u4f46\u5403\u4e8f\u592a\u5927\uff0c\u4ed6\u5c31\u4e0d\u80af\u62fc\u4e86\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u614c\u5fd9\u63e1\u638c\u6210\u62f3\u3002\u4ed3\u7687\u5931 \u63aa\u4e4b\u4e2d\uff0c\u8fd9\u4e00\u62f3\u5c1a\u672a\u6253\u51fa\uff0c\u5df2\u7ed9\u91d1\u9010\u6d41\u53cd\u626f\u624b\u81c2\uff0c\u201c\u548b\u5693\u201d\u4e00\u58f0\uff0c\u786c\u751f\u751f\u7684\u628a\u4ed6\u7684\u81c2\u5f2f\u5173\u8282 \u4e4b\u5904\u6298\u65ad\uff01\u90d1\u96c4\u56fe\u5927\u543c\u4e00\u58f0\uff0c\u8eab\u8eaf\u5012\u5730\uff0c\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53f2\u767d\u90fd\u63a5\u4e0b\u4e86\u7ed9\u91d1\u9010\u6d41\u629b\u8d77\u7684\u6c99\u5343\u5cf0\uff0c\u4f46\u60f3\u8981\u62a2\u6551\u90d1\u96c4\u56e0\u5df2\u6765\u4e0d\u53ca\u3002\u53f2\u767d\u90fd\u5927\u6012\uff0c\u653e\u4e0b \u4e86\u6c99\u5343\u5cf0\uff0c\u8bf4\u9053\uff1a\u201c\u6c99\u5927\u54e5\uff0c\u4f60\u63a5\u6211\u7684\u624b\uff0c\u6211\u53bb\u6599\u7406\u8fd9\u4e2a\u5c0f\u5b50\uff01\u201d\u4e8e\u662f\u6c99\u5343\u5cf0\u4e0a\u524d\u654c\u4f4f\u674e\u5357 \u661f\uff0c\u53f2\u767d\u90fd\u5219\u5411\u91d1\u9010\u6d41\u6251\u53bb\u3002\u6587\u9053\u5e84\u6b64\u65f6\u5df2\u7ecf\u5598\u8fc7\u53e3\u6c14\uff0c\u864e\u53e3\u7684\u9178\u9ebb\u4ea6\u5df2\u6b62\u4e86\uff0c\u4ed6\u89c1\u53f2\u767d\u90fd \u548c\u91d1\u9010\u6d41\u4ea4\u4e86\u624b\uff0c\u4e0d\u613f\u4e0e\u53f2\u767d\u90fd\u4e89\u529f\uff0c\u4fbf\u8dd1\u53bb\u52a9\u6c99\u5343\u5cf0\u3002\u53f2\u7ea2\u82f1\u4e0e\u674e\u5357\u661f\u5e76\u80a9\u4f5c\u6218\uff0c\u4ee5\u4e8c\u654c \u4e8c\uff0c\u6253\u5f97\u96be\u89e3\u96be\u5206\u3002 \u3000\u3000\u91d1\u9010\u6d41\u907f\u8fc7\u4e24\u62db\uff0c\u53f2\u767d\u90fd\u8fd0\u5251\u5982\u98ce\uff0c\u7740\u7740\u7d27\u903c\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u5077\u4e86\u6211\u7684\u7384\u94c1\uff0c\u5077 \u4e86\u6211\u7684\u5750\u9a91\uff0c\u5982\u4eca\u53c8\u5077\u4e86\u6211\u732e\u7ed9\u8428\u5927\u4eba\u7684\u5bff\u793c\uff0c\u8fd9\u4e09\u6837\u4e1c\u897f\u4f60\u4e0d\u5410\u51fa\u6765\uff0c\u6211\u5c31\u8981\u4f60\u7684\u547d\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6789\u4f60\u662f\u4e00\u5e2e\u4e4b\u4e3b\uff0c\u9ed1\u9053\u4e0a\u7684\u89c4\u77e9\u4f60\u90fd\u4e0d\u61c2\u4e48\uff1f\u8d22\u5165\u5149\u68cd\u624b\uff0c\u54ea\u91cc\u8fd8\u6709\u5410 \u51fa\u6765\u7684\u9053\u7406\uff1f\u5018\u82e5\u4e8b\u524d\u8bf4\u662f\u501f\u7684\uff0c\u90a3\u8fd8\u53ef\u4ee5\u5546\u91cf\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u8bf4\u98ce\u51c9\u8bdd\u513f\uff01\u201d\u5251\u5149\u8fc7\u5904\uff0c \u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u91d1\u9010\u6d41\u7684\u8863\u88f3\uff0c\u5f53\u80f8\u4e4b\u5904\uff0c\u5df2\u662f\u7ed9\u4ed6\u7684\u5251\u5c16\u5212\u7834\u3002\u8fd9\u4e00\u62db\u5f53\u771f\u662f\u9669\u5230\u4e86\u6781 \u70b9\uff0c\u5e78\u4e8f\u91d1\u9010\u6d41\u7684\u201c\u5929\u7f57\u6b65\u6cd5\u201d\u9000\u5f97\u5feb\uff0c\u5426\u5219\u80f8\u53e3\u65e9\u5df2\u7ed9\u6420\u4e86\u4e2a\u900f\u660e\u7684\u7a9f\u7abf\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u89c1\u72b6\u4e0d\u7981\u5931\u58f0\u60ca\u547c\uff0c\u6587\u9053\u5e84\u8986\u638c\u4e00\u6309\uff0c\u6309\u7740\u5979\u7684\u94f6\u97ad\uff0c\u82e5\u4e0d\u662f\u674e\u5357\u661f\u51fa\u5251\u5f97\u5feb\uff0c \u8fd9\u6761\u94f6\u97ad\u9669\u4e9b\u7ed9\u4ed6\u593a\u53bb\u3002\u674e\u5357\u661f\u53eb\u9053\uff1a\u201c\u8d24\u5f1f\u3001\u5feb\u5411\u8fd9\u8fb9\u9760\u62e2\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u4e86\u8fd9\u4e24\u58f0\u547c\u5524\uff0c\u6df1\u611f\u4ed6\u4eec\u7684\u5173\u6000\u4e4b\u60c5\u3002\u5c24\u5176\u53f2\u7ea2\u82f1\u90a3\u4e00\u58f0\u5c16\u53eb\uff0c\u867d\u7136\u6ca1\u6709\u9644\u52a0 \u4e00\u53e5\u8bf4\u8bdd\uff0c\u5df2\u662f\u8db3\u4ee5\u4ee4\u91d1\u9010\u6d41\u7cbe\u795e\u9661\u632f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u9000\u590d\u4e0a\uff0c\u4e1a\u5df2\u62d4\u5251\u51fa\u9798\uff0c\u7b11\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\uff0c\u62f3\u811a\u5185\u529f\uff0c\u6211\u90fd\u9886\u6559\u8fc7\u4e86\uff0c\u4eca\u65e5\u518d \u4e0e\u4f60\u6bd4\u6bd4\u5251\u6cd5\u3002\u201d\u4ed6\u5fc3\u60c5\u6109\u5feb\uff0c\u867d\u7136\u5728\u5f3a\u654c\u731b\u653b\u4e4b\u4e0b\uff0c\u4f9d\u7136\u8c08\u7b11\u81ea\u5982\u3002 \u3000\u3000\u6b64\u65f6\u573a\u4e2d\u5df2\u6f14\u6210\u4e86\u5927\u6df7\u6218\u7684\u5c40\u9762\uff0c\u4f46\u771f\u6b63\u640f\u6597\u7684\u4e5f\u53ea\u662f\u4e00\u4e8c\u6d41\u7684\u9ad8\u624b\u800c\u5df2\uff0c\u8bb8\u591a\u63d2\u4e0d\u8fdb \u624b\u7684\u536b\u58eb\uff0c\u53ea\u80fd\u5728\u65c1\u8fb9\u6447\u65d7\u5450\u558a\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4f7f\u51fa\u9053\u98ce\u5251\u5f0f\uff0c\u77eb\u82e5\u6e38\u9f99\uff0c\u7728\u773c\u95f4\u653b\u51fa\u4e86\u516d\u516d\u4e09\u5341\u516d\u5251\u3002\u53f2\u767d\u90fd\u4e5f\u4e0d\u7531\u5f97\u6697\u6697\u5403 \u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u5929\u4e0b\u5251\u672f\u540d\u5bb6\uff0c\u6211\u4e5f\u89c1\u5f97\u591a\u4e86\uff0c\u8fd9\u5c0f\u5b50\u7eb5\u7136\u4e0d\u80fd\u8bf4\u662f\u5929\u4e0b\u7b2c\u4e00\uff0c\u5374\u5df2\u662f\u8fdc\u80dc\u4e8e \u6211\u6240\u76f8\u8bc6\u7684\u90a3\u4e9b\u540d\u5bb6\u3002\u53ea\u8bba\u5251\u672f\uff0c\u53ea\u6015\u6211\u4e5f\u6bd4\u4e0d\u4e0a\u4ed6\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8fde\u653b\u4e09\u5341\u516d\u5251\uff0c\u53f2\u767d\u90fd\u5140\u7acb\u5982\u5c71\uff0c\u4e00\u6b65\u4e5f\u4e0d\u9000\u8ba9\u3002\u91d1\u9010\u6d41\u4e5f\u4e0d\u7981\u6697\u6697\u5403\u60ca\u3002\u539f\u6765 \u53f2\u767d\u90fd\u7684\u5251\u672f\u867d\u662f\u4e0d\u5982\u91d1\u9010\u6d41\u7684\u7cbe\u5999\u3002\u4f46\u5982\u6df1\u5f97\u201c\u91cd\u62d9\u201d\u4e4b\u65e8\uff0c\u6700\u4e0a\u4e58\u7684\u6b66\u5b66\uff0c\u8bb2\u7a76\u7684\u5c31\u662f \u53cd\u749e\u5f52\u771f\uff0c\u4ee5\u62d9\u80dc\u5de7\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5230\u5e95\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u6b66\u5b66\u7684\u9020\u8be3\u5c1a\u672a\u80fd\u8fbe\u5230\u90a3\u4e2a\u5883\u754c\uff0c\u662f\u4ee5\u5251\u672f\u4e0a\u867d\u7136\u53d8\u5316\u83ab\u6d4b\uff0c\u5947 \u6ce5\u7edd\u4f26\uff0c\u5370\u4ecd\u662f\u7ed9\u53f2\u767d\u90fd\u201c\u91cd\u62d9\u201d\u7684\u5251\u6cd5\u6240\u5236\u3002\u5f80\u5f80\u4e00\u62db\u6781\u7cbe\u5999\u7684\u5251\u6cd5\uff0c\u7ed9\u53f2\u767d\u90fd\u666e\u666e\u901a\u901a \u7684\u968f\u624b\u4e00\u62db\u5c31\u5316\u89e3\u4e86\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u91d1\u9010\u6d41\u7684\u9752\u94a2\u5251\u7adf\u7ed9\u53f2\u767d\u90e8\u524a\u65ad\u3002\u53f2\u767d\u90fd\u62e6\u4f4f\u4e86\u4ed6\uff0c\u4e0d\u8bb8 \u4ed6\u8fc7\u53bb\u4e0e\u674e\u5357\u661f\u4f1a\u5408\u3002\u674e\u5357\u91cf\u3001\u53f2\u7ea2\u82f1\u60f3\u8981\u8fc7\u6765\uff0c\u4e5f\u7ed9\u6587\u9053\u5e84\u963b\u4f4f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4ed7\u7740\u5de7\u5999\u7684\u201c\u5929\u7f57\u6b65\u6cd5\u201d\uff0c\u7ed5\u7740\u516b\u4ed9\u684c\u4e0e\u53f2\u767d\u90fd\u6e38\u6597\uff0c\u4e00\u65f6\u95f4\u53f2\u767d\u90fd\u8981\u60f3\u62ff\u4ed6\u4e5f \u662f\u4e0d\u80fd\u3002\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u91cc\u6b66\u529f\u6700\u5f3a\u7684\u662f\u53f2\u767d\u90fd\uff0c\u6211\u628a\u4ed6\u7f20\u4f4f\uff0c\u5012\u662f\u53ef\u4ee5\u7ed9\u674e\u5927\u54e5\u548c \u53f2\u59d1\u5a18\u4e00\u4e2a\u8131\u8eab\u7684\u673a\u4f1a\u3002\u201d\u8fd9\u6837\u4e00\u60f3\uff0c\u4ed6\u53cd\u800c\u4e0d\u613f\u610f\u8fc7\u53bb\u548c\u4ed6\u4eec\u4f1a\u5408\u4e86\u3002\u5176\u5b9e\u4ee5\u91d1\u9010\u6d41\u8d85\u5353 \u7684\u8f7b\u529f\uff0c\u867d\u7136\u5728\u53f2\u767d\u90fd\u7684\u62e6\u963b\u4e4b\u4e0b\uff0c\u51b2\u8fc7\u53bb\u6709\u5f88\u5927\u7684\u56f0\u96be\uff0c\u4f46\u4e5f\u8fd8\u4e0d\u662f\u7edd\u5bf9\u505a\u4e0d\u5230\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8fd9\u8fb9\u5403\u7d27\uff0c\u5c09\u8fdf\u70af\u592b\u59bb\u90a3\u8fb9\u5374\u5df2\u662f\u5927\u5360\u4e0a\u98ce\u3002\u8463\u5341\u4e09\u5a18\u7ed9\u7948\u5723\u56e0\u6253\u5f97\u53ea\u80fd\u62db\u67b6\uff0c \u9752\u7b26\u3001\u5706\u6d77\u4e8c\u4eba\u66f4\u662f\u7ed9\u5c09\u8fdf\u70af\u7684\u6cfc\u98ce\u5200\u6cd5\u6740\u5f97\u80c6\u6218\u5fc3\u60ca\uff0c\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u3001\u674e\u5357\u661f\u90a3\u8fb9\u5219\u662f\u6253\u5f97\u96be\u89e3\u96be\u5206\u3002\u4ed6\u4eec\u7684\u5bf9\u624b\u662f\u6587\u9053\u5e84\u548c\u6c99\u5343\u5cf0\u4e8c\u4eba\u3002\u6587\u9053\u5e84\u7684 \u201c\u4e09\u8c61\u795e\u529f\u201d\u521a\u731b\u4e4b\u6781\uff0c\u624b\u811a\u8d77\u5904\uff0c\u5168\u5e26\u52b2\u98ce\uff0c\u674e\u5357\u661f\u4ee5\u5947\u8be1\u7edd\u4f26\u7684\u5251\u6cd5\u4e0e\u4ed6\u62a2\u653b\uff0c\u5140\u662f\u6709 \u70b9\u906e\u62e6\u4e0d\u4f4f\u3002\u4f46\u53f2\u7ea2\u82f1\u7684\u90a3\u6761\u94f6\u97ad\u77eb\u82e5\u6e38\u9f99\uff0c\u6c99\u5343\u5cf0\u53ea\u51ed\u4e00\u53cc\u8089\u638c\u5bf9\u4ed8\u5979\u7684\u94f6\u97ad\uff0c\u5374\u662f\u80dc\u5979 \u4e0d\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u7709\u5934\u4e00\u76b1\uff0c\u53eb\u9053\uff1a\u201c\u6c99\u5927\u54e5\uff0c\u4f60\u5c3d\u7ba1\u7ed9\u6211\u60e9\u6cbb\u8fd9\u4e2a\u4e2b\u5934\uff0c\u4f60\u628a\u5979\u6253\u6b7b\u6253\u4f24\uff0c\u6211\u90fd \u4e0d\u4f1a\u602a\u4f60\u3002\u201d\u4ed6\u4ee5\u4e3a\u6c99\u5343\u5cf0\u662f\u788d\u7740\u4ed6\u7684\u9762\u5b50\uff0c\u5bf9\u4ed6\u7684\u59b9\u59b9\u624b\u4e0b\u7559\u60c5\uff0c\u6b8a\u4e0d\u77e5\u6c99\u5343\u5cf0\u662f\u56e0\u4e3a\u7ed9 \u91d1\u9010\u6d41\u5148\u6454\u4e86\u4e00\u8de4\uff0c\u632b\u4e86\u9510\u6c14\uff0c\u518d\u6597\u53f2\u7ea2\u82f1\u4e4b\u65f6\uff0c\u529f\u592b\u5df2\u662f\u5927\u6253\u6298\u6263\u4e86\u3002 \u3000\u3000\u6c99\u5343\u5cf0\u5728\u6c5f\u6e56\u4e0a\u4e5f\u662f\u6709\u6570\u7684\u4eba\u7269\uff0c\u548c\u53f2\u767d\u90fd\u5dee\u4e0d\u4e86\u591a\u5c11\u7684\u3002\u4ed6\u7684\u672c\u9886\u672c\u6765\u8981\u6bd4\u53f2\u7ea2\u82f1\u7a0d \u80dc\u4e00\u7b79\uff0c\u5982\u4eca\u6218\u5979\u4e0d\u4e0b\uff0c\u5df2\u89c9\u9762\u76ee\u65e0\u5149\uff0c\u518d\u7ed9\u53f2\u767d\u90fd\u4e00\u50ac\uff0c\u66f4\u4e0d\u7531\u5f97\u5fc3\u5934\u7126\u8e81\u3002\u9ad8\u624b\u6bd4\u6597\uff0c \u54ea\u5bb9\u5f97\u6c14\u8e81\u5fc3\u6d6e\uff0c\u51b7\u4e0d\u9632\u5c31\u7ed9\u53f2\u7ea2\u82f1\u72e0\u72e0\u7684\u62bd\u4e86\u4e00\u97ad\uff0c\u6c14\u5f97\u6c99\u5343\u5cf0\u54c7\u54c7\u5927\u53eb\u3002 \u3000\u3000\u6070\u597d\u5c31\u5728\u6b64\u65f6\uff0c\u731b\u542c\u5f97\u4e00\u58f0\u60e8\u5389\u7684\u53eb\u58f0\u76d6\u8fc7\u4e86\u6c99\u5343\u5cf0\u5730\u53eb\u558a\uff0c\u5374\u539f\u6765\u662f\u5706\u6d77\u7ed9\u5c09\u8fdf\u70af\u5288 \u4e86\u4e00\u5200\uff0c\u4e00\u6761\u5de6\u81c2\u786c\u751f\u751f\u5730\u5288\u4e86\u4e0b\u6765\uff0c\u75db\u5f97\u4ed6\u5728\u5730\u4e0a\u6253\u6eda\u3002\u5c09\u8fdf\u70af\u5927\u559d\u9053\uff1a\u201c\u907f\u6211\u8005\u751f\uff0c\u6321 \u6211\u8005\u6b7b\uff0c\u5e76\u80a9\u5b50\u626f\u547c\uff01\u201d\u8463\u5341\u4e09\u5a18\u54ea\u6562\u604b\u6218\uff0c\u4fa7\u8eab\u4e00\u95ea\uff0c\u7948\u5723\u56e0\u65e9\u5df2\u4e0e\u4e08\u592b\u5e76\u80a9\u6740\u51fa\u3002 \u3000\u3000\u8428\u798f\u9f0e\u5927\u53eb\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\u6b63\u70b9\u513f\u8981\u7d27\uff01\u201d\u610f\u601d\u662f\u8981\u53f2\u767d\u90fd\u8ffd\u6355\u4e3b\u72af\u3002\u5728\u8428\u798f\u9f0e\u7684\u773c\u4e2d\uff0c \u5c09\u8fdf\u70af\u592b\u59bb\u81ea\u662f\u8981\u6bd4\u4e00\u4e2a\u65e0\u540d\u5c0f\u5b50\u91d1\u9010\u6d41\u7d27\u8981\u5f97\u591a\u3002 \u3000\u3000\u5728\u8428\u798f\u9f0e\u547c\u559d\u4e4b\u9645\uff0c\u4ed6\u7684\u624b\u4e0b\u4e5f\u5728\u7a83\u7a83\u79c1\u8bae\uff0c\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u53f2\u767d\u90fd\u600e\u4e48\u641e\u7684\uff0c\u6b63\u70b9\u513f\u4e0d \u7406\uff0c\u5374\u53bb\u548c\u4e00\u4e2a\u5c0f\u5b50\u6349\u8ff7\u85cf\uff1f\u5514\uff0c\u83ab\u975e\u4ed6\u53ea\u662f\u60f3\u62e3\u8f6f\u7684\u5403\uff1f\u201d\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u4ed6\u8981\u6c99\u5343\u5cf0\u7ed9\u4ed6 \u6267\u884c\u5bb6\u6cd5\uff0c\u8fd9\u4e0d\u662f\u7b11\u8bdd\u5417\uff1f\u4ed6\u81ea\u5df1\u7684\u59b9\u59b9\u4ed6\u90fd\u4e0d\u7ba1\uff0c\u522b\u4eba\u788d\u7740\u4ed6\u7684\u9762\u5b50\uff0c\u53c8\u600e\u597d\u8d8a\u4fce\u4ee3 \u75b1\uff1f\u201d\u53c8\u4e00\u4e2a\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u7b11\u8bdd\uff0c\u4f60\u7109\u77e5\u4ed6\u4e0d\u662f\u6545\u610f\u5982\u6b64\uff0c\u5426\u5219\u4ed6\u600e\u6837\u5411\u54b1\u4eec\u7684\u8428\u5927\u4eba\u4ea4 \u4ee3\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u8fd9\u4e9b\u4eba\u4ea4\u5934\u63a5\u8033\u7684\u8bf4\u8bdd\u58f0\u97f3\u867d\u4e0d\u662f\u600e\u4e48\u54cd\u4eae\u51fa\u90fd\u5df2\u542c\u89c1 \u4e86\u3002\u53f2\u767d\u90fd\u53c8\u60ca\u53c8\u607c\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u82e5\u662f\u8ba9\u8fd9\u4e2b\u5934\u8dd1\u6389\uff0c\u6ca1\u7684\u5012\u6559\u8428\u603b\u7ba1\u89c1\u7591\u4e86\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u4e00\u58f0\u5927\u543c\uff0c\u6380\u7ffb\u4e86\u4e00\u5f20\u684c\u5b50\uff0c\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u522b\u53d1\u813e\u6c14\uff0c\u54b1\u4eec\u80dc\u8d1f\u672a\u5206\uff0c\u597d\u597d\u7684 \u518d\u6253\u5427\u3002\u201d\u91d1\u9010\u6d41\u4fa7\u8eab\u4e00\u95ea\uff0c\u52a0\u4e0a\u4e00\u638c\uff0c\u90a3\u5f20\u684c\u5b50\u659c\u523a\u98de\u51fa\uff0c\u628a\u51e0\u4e2a\u536b\u58eb\u538b\u5f97\u5934\u7834\u8840\u6d41\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u592b\u5987\u6740\u51fa\u5927\u95e8\uff0c\u7948\u5723\u56e0\u56de\u5934\u53eb\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u8d70\u5427\uff01\u201d\u4e00\u626c\u624b\u4e03\u79cd\u4e0d\u540c\u7684\u6697\u5668\u5411 \u6587\u9053\u5e84\u98de\u53bb\uff0c\u6587\u9053\u5e84\u5e94\u4ed8\u4e0d\u6687\uff0c\u674e\u5357\u661f\u548c\u53f2\u7ea2\u82f1\u4e5f\u51b2\u51fa\u53bb\u4e86\u3002\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d \u9519\uff0c\u6211\u4e5f\u8be5\u8d70\u4e86\uff01\u201d \u3000\u3000\u5927\u7b11\u58f0\u4e2d\uff0c\u91d1\u9010\u6d41\u624b\u63a8\u811a\u8e22\uff0c\u628a\u516b\u5f20\u516b\u4ed9\u684c\u5168\u90fd\u6380\u7ffb\uff0c\u516b\u5f20\u684c\u5b50\u5728\u5bff\u5802\u4e2d\u6eda\u52a8\uff0c\u8bb8\u591a\u672c \u9886\u7a0d\u5dee\u7684\u536b\u58eb\u7ed9\u649e\u5f97\u5934\u7834\u8840\u6d41\uff0c\u7eb7\u7eb7\u8eb2\u95ea\u3002 \u3000\u3000\u7948\u5723\u56e0\u66f4\u72e0\uff0c\u7ad9\u5728\u95e8\u53e3\uff0c\u5e76\u4e0d\u7acb\u5373\u9003\u8d70\uff0c\u5374\u662f\u53cc\u624b\u8fde\u53d1\u6697\u5668\uff0c\u8f6c\u773c\u95f4\u5c31\u4f24\u4e86\u5341\u51e0\u4e2a\u4eba\uff0c \u5413\u5f97\u90a3\u4e9b\u6ca1\u6709\u53d7\u4f24\u7684\u536b\u58eb\u90fd\u4e89\u7740\u8eb2\u5230\u6697\u5668\u6253\u4e0d\u7740\u7684\u89d2\u843d\uff01 \u3000\u3000\u53f2\u767d\u90fd\u5927\u6012\uff0c\u957f\u5251\u821e\u8d77\u4e00\u9053\u94f6\u8679\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u7948\u5723\u56e0\u7684\u6697\u5668\u6253\u8fdb\u4ed6 \u7684\u5251\u5149\u5708\u4e2d\uff0c\u5168\u90fd\u7ed9\u4ed6\u524a\u65ad\u3002 \u3000\u3000\u53f2\u767d\u90fd\u8ffd\u51fa\u5927\u95e8\uff0c\u770b\u89c1\u516c\u5b59\u5b8f\u8896\u624b\u4e00\u65c1\uff0c\u53f2\u767d\u90fd\u629a\u5251\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u5b59\u5927\u54e5\uff0c\u4eca\u65e5\u65e0 \u8bba\u5982\u4f55\u8981\u8bf7\u5927\u54e5\u5e2e\u4e00\u4e2a\u5fd9\uff0c\u59d3\u91d1\u7684\u90a3\u5c0f\u5b50\u7559\u7ed9\u4f60\u5427\u3002\u201d\u65e0\u6687\u542c\u53d6\u516c\u5b59\u5b8f\u7684\u56de\u7b54\uff0c\u633a\u5251\u4fbf\u5373\u5411 \u524d\u8ffd\u53bb\u3002\u6b64\u65f6\u5c09\u8fdf\u70af\u592b\u59bb\u548c\u674e\u5357\u661f\u3001\u53f2\u7ea2\u82f1\u56db\u4eba\u5df2\u7ecf\u5206\u8def\u800c\u9003\u3002\u5c09\u8fdf\u70af\u592b\u59bb\u5411\u4e1c\uff0c\u674e\u3001\u53f2\u4e8c \u4eba\u5411\u897f\uff0c\u8428\u5e9c\u4e2d\u9053\u51fa\u6765\u7684\u9ad8\u624b\u6709\u5341\u6570\u4eba\u4e4b\u591a\uff0c\u4e5f\u662f\u6709\u7684\u5411\u4e1c\uff0c\u6709\u7684\u5411\u897f\u3002\u53f2\u767d\u90fd\u8ffd\u5230\u4e86\u5206\u5c94 \u8def\u53e3\uff0c\u4e0d\u89c9\u4e00\u9635\u8e0c\u8e87\uff0c\u4e0d\u77e5\u662f\u5411\u4e1c\u8fd8\u662f\u5411\u897f\uff1f \u3000\u3000\u91d1\u9010\u6d41\u6700\u540e\u4e00\u4e2a\u9003\u51fa\u5bff\u5802\uff0c\u516c\u5b59\u5b8f\u7ad9\u5728\u95e8\u53e3\u7b11\u9053\uff1a\u201c\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u6b66\u529f\u59d4\u5b9e\u4e0d\u5f31\uff0c\u4f60\u5e08 \u7236\u662f\u8c01\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6b64\u5730\u4f3c\u4e4e\u4e0d\u662f\u6500\u8bba\u4ea4\u60c5\u4e4b\u5730\uff01\u201d\u8a00\u4e0b\u4e4b\u610f\uff1a\u201c\u4f60\u5728\u8428\u5e9c\u4e4b\u4e2d\uff0c\u8981\u95ee \u6211\u7684\u5e08\u95e8\u6765\u5386\uff0c\u6211\u53ea\u80fd\u5f53\u4f5c\u4f60\u662f\u76d8\u95ee\u53e3\u4f9b\uff0c\u4f60\u82e5\u5f53\u771f\u662f\u548c\u6211\u8bba\u4ea4\u7684\u8bdd\uff0c\u90a3\u5c31\u4e0d\u5b9c\u5728\u8fd9\u79cd\u573a \u5408\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u4eba\u5012\u662f\u9a84\u50b2\u5f97\u5f88\u3002\u201d\u53cc\u638c\u4e00\u7acb\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4 \u96be\u9053\u6211\u5c31\u65e0\u6cd5\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e00\u638c\u5288\u53bb\uff0c\u516c\u5b59\u5b8f\u53cd\u624b\u4e00\u62ff\uff0c\u91d1\u9010\u6d41\u8fc5\u5373\u51fa\u6307\u70b9\u4ed6\u7a74\u9053\uff0c\u516c\u5b59\u5b8f\u5408\u638c\u4e00\u62ff\uff0c\u91d1\u9010 \u6d41\u7684\u6307\u5c16\u5df2\u70b9\u7740\u4e86\u4ed6\u638c\u5fc3\u7684\u201c\u52b3\u5bab\u7a74\u201d\uff0c\u8fd9\u201c\u52b3\u5bab\u7a74\u201d\u662f\u624b\u5c11\u9633\u7ecf\u8109\u7684\u7ec8\u70b9\uff0c\u7ed9\u70b9\u7740\u4e86\u81f3\u5c11 \u4e5f\u8981\u534a\u8eab\u4e0d\u9042\u3002\u4e0d\u6599\u516c\u5b59\u5b8f\u7adf\u4f3c\u6beb\u65e0\u77e5\u89c9\uff0c\u91d1\u9010\u6d41\u53d1\u89c9\u4e0d\u5999\uff0c\u7f29\u624b\u4e4b\u65f6\uff0c\u53ea\u89c9\u5bf9\u65b9\u7684\u638c\u5fc3\u9690 \u9690\u6709\u4e00\u80a1\u5438\u529b\uff0c\u7adf\u662f\u6446\u8131\u4e0d\u5f00\u3002\u91d1\u9010\u6d41\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u624d\u77e5\u9053\u516c\u5b59\u5b8f\u7684\u6b66\u529f\u8fd8\u5728\u53f2\u767d\u90fd\u4e4b\u4e0a\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u89c1\u72b6\u5927\u60ca\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u5272\u9e21\u7109\u7528\u725b\u5200\uff0c\u8ba9\u6211\u6765\u5427\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u516c \u5b59\u5b8f\u811a\u6b65\u4e00\u4e2a\u8e09\u8dc4\uff0c\u91d1\u9010\u6d41\u5df2\u662f\u51fa\u4e86\u5927\u95e8\u3002\u516c\u5b59\u5b8f\u5401\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u6ed1\u6e9c\u5f97\u5f88\uff0c\u7ed9 \u4ed6\u8dd1\u4e86\u3002\u4f60\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u7684\uff0c\u4f60\u8fd8\u662f\u8ddf\u6211\u53bb\u8ffd\u5c09\u8fdf\u70af\u5427\u3002\u201d\u5bab\u79c9\u85e9\u60ca\u559c\u4ea4\u96c6\u3001\u559c\u8005\u662f\u91d1\u9010\u6d41 \u5df2\u7ecf\u6323\u8131\uff0c\u60ca\u8005\u662f\u5e2e\u4e3b\u5c45\u7136\u4f1a\u8f93\u7ed9\u91d1\u9010\u6d41\u4e00\u62db\uff0c\u5927\u51fa\u4ed6\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u4e0d\u77e5\uff0c\u91d1\u9010\u6d41\u5fc3\u91cc\u5219\u662f\u660e\u767d\u7684\uff0c\u8fd9\u662f\u516c\u5b59\u5b8f\u6709\u610f\u8ba9\u4ed6\u9003\u8dd1\uff0c\u5426\u5219\u4ed6\u7109\u80fd\u6323\u8131\uff1f\u4f46 \u4ed6\u9003\u51fa\u4e86\u95e8\u5916\uff0c\u5374\u662f\u4e0d\u7981\u4e00\u9635\u832b\u7136\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u5e94\u8be5\u8d70\u4e86\uff0c\u4f46\u6211\u5e94\u5f80\u4f55\u65b9\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u8dd1\u4e0a\u5927\u8857\uff0c\u53ea\u89c1\u5f71\u7ef0\u7ef0\u7684\u4e00\u7c07\u7c07\u4eba\uff0c\u6709\u7684\u5411\u4e1c\uff0c\u6709\u7684\u5411\u897f\uff0c\u6709\u7684\u5219\u8fd8\u5728\u53fd\u53fd\u55b3\u55b3 \u7684\u5546\u91cf\uff1a\u201c\u4f60\u8bf4\u8be5\u662f\u5411\u4e1c\u5462\u8fd8\u662f\u5411\u897f\u5462\uff1f\u201d\u201c\u90a3\u5f3a\u76d7\u5934\u5b50\u5f88\u4e0d\u597d\u60f9\uff0c\u4f9d\u6211\u770b\u8fd8\u662f\u5411\u897f\u98ce\u9669\u8f83 \u5c0f\u3002\u201d\u201c\u4e0d\uff0c\u90a3\u96cc\u513f\u662f\u53f2\u767d\u90fd\u7684\u59b9\u59b9\uff0c\u54b1\u4eec\u4f55\u82e6\u53bb\u72af\u8fd9\u8d9f\u6d51\u6c34\uff1f\u8fd9\u662f\u5403\u529b\u4e0d\u8ba8\u597d\u7684\u4e8b\u60c5 \u5440\u3002\u201d\u201c\u54c8\uff0c\u4f60\u4eec\u65e2\u662f\u754f\u9996\u754f\u5c3e\uff0c\u6015\u4e1c\u6015\u897f\uff0c\u90a3\u4e48\u6700\u597d\u5c31\u662f\u865a\u5f20\u58f0\u52bf\uff0c\u6447\u65d7\u5450\u558a\uff0c\u5f80\u4e1c\u5f80\u897f \u90fd\u662f\u4e00\u6837\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u4e86\u8fd9\u4e9b\u4eba\u7684\u8c08\u8bba\uff0c\u5fc3\u4e2d\u5df2\u662f\u660e\u767d\uff1a\u5c09\u8fdf\u70af\u592b\u59bb\u662f\u5411\u4e1c\u65b9\u9003\u8d70\uff0c\u800c\u674e\u5357\u661f\u548c\u53f2\u7ea2 \u82f1\u5219\u662f\u5411\u897f\u65b9\u9003\u8d70\u4e86\u3002\u8fd9\u4e9b\u536b\u58eb\u6b63\u5728\u5206\u5934\u8ffd\u4eba\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u7ea2\u82f1\u6709\u5927\u54e5\u7167\u987e\uff0c\u6599\u60f3\u53f2\u767d\u90fd\u4e5f\u5948\u4f55\u4e0d\u4e86\u4ed6\u4eec\u3002\u6211\u2014\u2014\u5509\uff0c\u6211\u8fd8\u662f \u5f80\u4e1c\u8d70\u5427\uff01\u201d\u4ed6\u672c\u6765\u662f\u6e34\u671b\u89c1\u4e00\u89c1\u53f2\u7ea2\u82f1\u7684\uff0c\u4f46\u6b64\u9645\u5374\u662f\u4e0e\u53f2\u7ea2\u82f1\u80cc\u9053\u800c\u9a70\uff0c\u5fc3\u4e2d\u4f46\u89c9\u4e00\u7247 \u832b\u7136\uff0c\u4e5f\u4e0d\u77e5\u662f\u9178\u662f\u82eb\uff1f \u3000\u3000\u53f2\u767d\u90fd\u5728\u5c94\u8def\u4e0a\u6b63\u81ea\u8e0c\u8e87\uff0c\u4e0d\u77e5\u662f\u5f80\u4e1c\u8fd8\u662f\u5f80\u897f\uff0c\u5ffd\u89c1\u516c\u5b59\u5b8f\u5306\u5306\u8dd1\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5c09\u8fdf \u70af\u5411\u4e1c\u9762\u8dd1\u4e86\uff0c\u6211\u53bb\u8ffd\u4ed6\uff0c\u4f60\u4eec\u7684\u5bb6\u4e8b\u6211\u4e0d\u7ba1\u4e86\uff01\u201d\u53f2\u767d\u90fd\u5927\u559c\u8bf4\u9053\uff1a\u201c\u6709\u5927\u54e5\u51fa\u624b\uff0c\u5c09\u8fdf \u70af\u8fd9\u5bf9\u8d3c\u592b\u59bb\u5b9a\u8dd1\u4e0d\u4e86\u3002\u90a3\u5c0f\u5b50\u5462\uff1f\u201d\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u6e9c\u6ed1\u5f97\u5f88\uff0c\u6211\u4e00\u628a\u6293\u4ed6\u4e0d\u7740\uff0c\u4e0d \u77e5\u4ed6\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u53cd\u6b63\u4ed6\u4e5f\u4e0d\u662f\u6b63\u70b9\u513f\uff0c\u7531\u4ed6\u53bb\u5427\u3002\u4e0d\u8fc7\uff0c\u4f60\u82e5\u662f\u6015\u5bf9\u4ed8\u4e0d\u4e86\u4ed6\u4eec\uff0c\u6211\u53ef \u4ee5\u53eb\u6587\u9053\u5e84\u6765\u8fd9\u8fb9\u5e2e\u4f60\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u7b11\u8bdd\uff0c\u7b11\u8bdd\u3002\u90a3\u5c0f\u5b50\u5c82\u4f1a\u653e\u5728\u6211\u7684\u5fc3\u4e0a\uff1f\u597d\uff0c\u54b1\u4eec\u5206\u5934\u8ffd \u4eba\uff0c\u62ff\u4e86\u4eba\u56de\u6765\u76f8\u89c1\u3002\u201d\u53f2\u767d\u90fd\u6df1\u77e5\u91d1\u9010\u6d41\u7684\u8f7b\u529f\u8d85\u5999\uff0c\u6545\u6b64\u4e00\u70b9\u4e5f\u6ca1\u6709\u7591\u5fc3\u662f\u516c\u5b59\u5b8f\u6709\u610f \u653e\u8d70\u4ed6\u7684\u3002\u53cd\u800c\u771f\u662f\u6709\u70b9\u62c5\u5fc3\u91d1\u9010\u6d41\u5411\u4ed6\u8fd9\u4e00\u8fb9\u9003\uff0c\u82e5\u7136\u78b0\u4e0a\uff0c\u81ea\u5df1\u867d\u7136\u4e0d\u4f1a\u8f93\u7ed9\u4ed6\uff0c\u4e5f\u8981 \u7ed9\u4ed6\u7ea0\u7f20\u8bb8\u4e45\uff0c\u90a3\u5c31\u8ffd\u4e0d\u4e0a\u59b9\u59b9\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5c55\u5f00\u201c\u516b\u6b65\u8d76\u8749\u201d\u7684\u8f7b\u529f\uff0c\u4e00\u8def\u8ffd\u4e0b\u53bb\uff0c\u90a3\u4e9b\u6447\u65d7\u5450\u558a\u7684\u536b\u58eb\u53ea\u89c9\u4e00\u9635\u98ce\u4ece\u4ed6\u4eec \u8eab\u8fb9\u522e\u8fc7\uff0c\u4e00\u56e2\u9ed1\u5f71\u5df2\u662f\u8fdc\u5728\u524d\u5934\uff0c\u6839\u672c\u5c31\u770b\u4e0d\u6e05\u695a\u91d1\u9010\u6d41\u662f\u8c01\uff0c\u4e0d\u6d88\u591a\u4e45\uff0c\u91d1\u9010\u6d41\u5df2\u662f\u51fa \u4e86\u4e1c\u95e8\uff0c\u5230\u4e86\u90ca\u5916\uff0c\u9762\u524d\u53c8\u6709\u5c94\u8def\uff0c\u91d1\u9010\u6d41\u6b63\u81ea\u5fc3\u60f3\uff1a\u201c\u4e0d\u77e5\u8fd8\u627e\u5f97\u7740\u627e\u4e0d\u7740\u5c09\u8fdf\u70af\uff1f\u201d\u5ffd \u89c1\u4e24\u540d\u536b\u58eb\u76f8\u4e92\u6276\u6301\uff0c\u54fc\u54fc\u537f\u537f\u7684\u56de\u6765\uff0c\u539f\u6765\u4ed6\u4eec\u662f\u7740\u4e86\u7948\u5723\u56e0\u7684\u6697\u5668\uff0c\u53d7\u4e86\u4f24\u8dd1\u56de\u6765\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u628a\u6293\u7740\u4e00\u4e2a\u536b\u58eb\uff0c\u559d\u9053\uff1a\u201c\u5c09\u8fdf\u70af\u5f80\u54ea\u91cc\u8dd1\u4e86\uff1f\u201d\u90a3\u536b\u571f\u9053\uff1a\u201c\u4ed6\u4eec\u5df2\u8fc7\u4e86\u4e03 \u91cc\u94fa\u4e86\u3002\u4f46\u6211\u529d\u4f60\u8fd8\u662f\u522b\u53bb\u8ffd\u5427\uff0c\u90a3\u8d3c\u5a46\u5a18\u7684\u6697\u5668\u5389\u5bb3\u5f97\u5f88\u3002\u54ce\u5440\uff0c\u4f60\uff0c\u4f60\u662f\u2026\u2026\u201d \u3000\u3000\u90a3\u536b\u58eb\u8bf4\u4e86\u4e00\u5927\u5806\u8bdd\u624d\u53d1\u89c9\u91d1\u9010\u6d41\u662f\u4e2a\u964c\u751f\u9762\u5b54\uff0c\u4e0d\u89c9\u5927\u5403\u4e00\u60ca\u3002\u53e6\u4e00\u540d\u536b\u58eb\u5728\u91d1\u9010\u6d41 \u4fa7\u9762\u6b64\u65f6\u4ea6\u77e5\u8ba4\u9519\u4e86\u4eba\uff0c\u8fde\u5fd9\u62d4\u5251\u523a\u4ed6\u3002\u91d1\u9010\u6d41\u5934\u4e5f\u4e0d\u56de\uff0c\u53cd\u624b\u4e00\u62ff\uff0c\u5c31\u593a\u4e86\u4ed6\u624b\u4e2d\u7684\u5251\uff0c \u8fde\u9798\u62a2\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u591a\u8c22\u4f60\u4eec\u6307\u70b9\u3002\u201d\u628a\u6293\u7740\u7684\u90a3\u4e2a\u536b\u58eb\u63a8\u5012\uff0c\u4e00\u6e9c\u70df\u4fbf\u5f80\u524d\u8dd1\u3002\u201c\u4e03\u91cc \u94fa\u201d\u662f\u5728\u79bb\u57ce\u4e03\u91cc\u4e4b\u5730\u7684\u4eac\u4fdd\u9053\u4e0a\uff0c\u91d1\u9010\u6d41\u7528\u4e0d\u4e86\u4e00\u523b\u529f\u592b\uff0c\u5c31\u8fc7\u4e86\u4e03\u91cc\u94fa\u3002 \u3000\u3000\u8fc7\u4e86\u4e03\u91cc\u94fa\uff0c\u672a\u66fe\u8ffd\u4e0a\u5c09\u8fdf\u70af\uff0c\u5148\u53d1\u73b0\u4e86\u6587\u9053\u5e84\u548c\u6c99\u5343\u5cf0\u4e8c\u4eba\u3002\u6587\u3001\u6c99\u4e8c\u4eba\u662f\u65e9\u5df2\u8ffd\u51fa \u6765\u7684\uff0c\u4f46\u56e0\u4e3a\u4ed6\u4eec\u987e\u5fcc\u5c09\u8fdf\u70af\u592b\u59bb\u4e86\u5f97\uff0c\u662f\u4ee5\u4e0d\u6562\u8dd1\u5f97\u592a\u5feb\uff0c\u60f3\u7b49\u5f85\u5927\u961f\u5230\u6765\uff0c\u501a\u591a\u4e3a\u80dc\u3002 \u3000\u3000\u6587\u9053\u5e84\u56de\u5934\u4e00\u770b\uff0c\u89c1\u662f\u91d1\u9010\u6d41\u8ffd\u6765\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u7acb\u5373\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5929\u5802\u6709\u8def\u4f60\u4e0d\u8d70\uff0c\u5730 \u72f1\u65e0\u95e8\u4f60\u504f\u8981\u8fdb\u6765\u3002\u54c8\uff0c\u96be\u5f97\u964c\u8def\u76f8\u9022\uff0c\u4e14\u5148\u62ff\u4f60\u8fd9\u5c0f\u5b50\u6d88\u9063\u6d88\u9063\u3002\u201d\u6587\u9053\u5e84\u81ea\u5fd6\uff1a\u4ee5\u4ed6\u548c \u6c99\u5343\u5cf0\u8054\u624b\u4e4b\u529b\uff0c\u8981\u80dc\u5c09\u8fdf\u70af\u592b\u59bb\u6b8a\u65e0\u628a\u63e1\uff0c\u4f46\u8981\u5bf9\u4ed8\u91d1\u9010\u6d41\u4e00\u4eba\u5219\u662f\u7a33\u80dc\u65e0\u7591\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u59d3\u6587\u7684\uff0c\u4f60\u5fd8\u4e86\u6211\u7ed9\u4f60\u513f\u5b50\u7684\u89e3\u836f\u4e4b\u6069\u4e48\u3002\u201d\u6587\u9053\u5e84\u5927\u6012\uff0c\u6b63\u8981\u6251\u4e0a\u3002 \u91d1\u9010\u6d41\u4e5f\u6b63\u5728\u62d4\u5251\u51fa\u9798\uff0c\u51c6\u5907\u8fce\u654c\u3002\u5ffd\u542c\u5f97\u9a6c\u94c3\u58f0\u54cd\uff0c\u516c\u5b59\u5b8f\u4e0e\u5b98\u7c4d\u85e9\u9a91\u9a6c\u8ffd\u6765\uff0c\u8fdc\u8fdc\u7684\u626c \u58f0\u53eb\u9053\uff1a\u201c\u6587\u5c9b\u4e3b\u3001\u6c99\u5e2e\u4e3b\uff0c\u53f2\u5e2e\u4e3b\u8bf7\u4f60\u4eec\u5feb\u5feb\u53bb\u5e2e\u4ed6\u7684\u5fd9\uff01\u8fd9\u5c0f\u5b50\u4ea4\u7ed9\u6211\u5427\uff01\u6211\u62ff\u4e86\u8fd9\u5c0f \u5b50\u518d\u62ff\u5c09\u8fdf\u70af\uff0c\u8c05\u4ed6\u4eec\u4e5f\u9003\u8dd1\u4e0d\u4e86\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u5fc3\u60f3\uff1a\u201c\u53f2\u767d\u90fd\u96be\u9053\u8fd8\u6015\u5bf9\u4ed8\u4e0d\u4e86\u4ed6\u7684\u59b9\u59b9\uff1f\u548c\u4ed6\u59b9\u59b9\u4e00\u8d77\u7684\u90a3\u4e2a\u5c0f\u5b50\u5251\u6cd5\u867d\u4e5f \u4e0d\u5f31\uff0c\u603b\u6bd4\u4e0d\u4e0a\u5c09\u8fdf\u70af\u592b\u59bb\u3002\u4f55\u4ee5\u4ed6\u8fd8\u8981\u4ece\u8fd9\u8fb9\u8bf7\u63f4\uff1f\u201d\u4e0d\u8fc7\u4e00\u6765\u4ed6\u4e50\u5f97\u62e3\u8f6f\u7684\u5403\uff0c\u4e8c\u6765\u4ed6 \u4e5f\u4e0d\u6562\u6000\u7591\u516c\u5b59\u5b8f\u662f\u8bf4\u8c0e\u9a97\u4ed6\u3002\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u4e48\u6b63\u70b9\u513f\u5c31\u4ea4\u7ed9\u4f60\u4e86\u3002\u201d\u6587\u9053\u5e84\u5f80\u56de \u5934\u8def\u4e00\u8dd1\uff0c\u6c99\u5343\u5cf0\u662f\u5403\u8fc7\u91d1\u9010\u6d41\u7684\u4e8f\u7684\uff0c\u5f53\u7136\u4e5f\u662f\u5fd9\u4e0d\u8fed\u7684\u8ddf\u7740\u8dd1\u4e86\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u53eb\u9053\uff1a\u201c\u4f60\u53eb\u540e\u9762\u7684\u9ad8\u5e2e\u4e3b\u3001\u675c\u5e2e\u4e3b\u548c\u516d\u5408\u5e2e\u7684\u9999\u4e3b\u4eec\u7edf\u7edf\u90fd\u8ddf\u4f60\u53bb\u5427\u3002\u6211\u7528\u4e0d \u7740\u522b\u4eba\u5e2e\u5fd9\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u5fc3\u60f3\uff1a\u201c\u8fd9\u8001\u513f\u5f53\u771f\u662f\u9a84\u50b2\u5f97\u5f88\uff0c\u7adf\u662f\u6bd4\u53f2\u767d\u90fd\u8fd8\u8981\u81ea\u8d1f\u5f97\u591a\u3002\u597d\uff0c\u4f60\u82e5\u64d2\u5f97\u5c09 \u8fdf\u70af\u592b\u59bb\u6211\u4e5f\u5360\u4e00\u4efd\u529f\u52b3\uff0c\u4f60\u82e5\u662f\u7ed9\u6253\u8d25\u56de\u6765\uff0c\u6211\u4e50\u5f97\u770b\u4f60\u7684\u7b11\u8bdd\u3002\u201d\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u662f\uff0c \u662f\u3002\u6709\u4f60\u8001\u7237\u5b50\u4e00\u5200\u62c5\u627f\uff0c\u8c01\u4eba\u8fd8\u6562\u548c\u4f60\u4e89\u529f\uff01\u201d \u3000\u3000\u516c\u5b59\u5b8f\u7b56\u9a6c\u8ffd\u6765\uff0c\u5927\u547c\u5c0f\u53eb\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u522b\u8dd1\uff01\u54fc\uff0c\u54fc\uff0c\u7ed9\u4f60\u6e9c\u4e86\u4e00\u56de\uff0c\u8fd9\u4e00\u56de\u4f60\u8fd8\u60f3 \u6e9c\u5417\uff1f\u201d \u3000\u3000\u516c\u5b59\u5b8f\u53e3\u91cc\u5927\u547c\u5c0f\u53eb\uff0c\u624b\u4e2d\u5374\u52d2\u7740\u9a6c\u7586\uff0c\u4e0d\u8ba9\u90a3\u5339\u9a6c\u653e\u5c3d\u811a\u529b\u3002\u91d1\u9010\u6d41\u77a7\u5728\u773c\u91cc\uff0c\u5fc3\u4e2d \u5df2\u7136\u96ea\u4eae\uff1a\u201c\u8fd9\u8001\u513f\u662f\u6545\u610f\u558a\u7ed9\u6587\u9053\u5e84\u542c\u7684\u3002\u201d\u4e8e\u662f\u52a0\u5feb\u811a\u6b65\uff0c\u811a\u5e95\u5c31\u4f3c\u62b9\u4e86\u6cb9\u4f3c\u7684\u98de\u8dd1\u3002 \u91d1\u9010\u6d41\u5c55\u5f00\u4e86\u7edd\u9876\u8f7b\u529f\uff0c\u77ed\u7a0b\u4e4b\u5185\uff0c\u75be\u5982\u5954\u9a6c\uff0c\u516c\u5b59\u5b9a\u8d5e\u9053\uff1a\u201c\u597d\u8f7b\u529f\uff01\u201d\u8fd9\u624d\u653e\u9a6c\u8ffd\u6765\u3002 \u3000\u3000\u8ffd\u4e86\u4e00\u4f1a\uff0c\u6587\u9053\u5e84\u7684\u5f71\u5b50\u65e9\u5df2\u4e0d\u89c1\uff0c\u5c09\u8fdf\u70af\u592b\u59bb\u5219\u5df2\u5728\u8def\u4e0a\u505c\u4e0b\u6765\u7b49\u4ed6\u4eec\u4e86\u3002\u516c\u5b59\u5b8f\u7b11 \u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u73b0\u5728\u4e0d\u7528\u8dd1\u5f97\u8fd9\u4e48\u5feb\uff01\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u516c\u5b59\u5e2e\u4e3b\uff0c\u6253\u6211\u662f\u6253\u4e0d\u8fc7\u4f60\u7684\uff0c\u6253 \u4e0d\u8fc7\u4f60\uff0c\u6211\u4e0d\u8dd1\u600e\u884c\uff1f\u201d\u516c\u5b59\u5b8f\u5927\u7b11\uff1a\u201c\u8c01\u8981\u548c\u4f60\u6253\u67b6\u5440\uff01\u201d \u3000\u3000\u5c09\u8fdf\u70af\u54c8\u54c8\u5927\u7b11\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u4eca\u65e5\u5f97\u4f60\u62d4\u5251\u76f8\u52a9\uff0c\u6211\u5148\u5411\u4f60\u9053\u8c22\u3002\u8fd9\u4f4d\u516c\u5b59 \u5e2e\u4e3b\u662f\u6211\u7684\u8001\u670b\u53cb\uff0c\u4f60\u662f\u6211\u7684\u65b0\u670b\u53cb\uff0c\u5927\u5bb6\u90fd\u4e0d\u662f\u5916\u4eba\uff0c\u4f60\u82e5\u6ca1\u6709\u522b\u7684\u4e8b\u8bf7\uff0c\u54b1\u4eec\u5728\u6b64\u53d9\u53d9 \u5427\u3002\u516c\u5b59\u5927\u54e5\uff0c\u8fd9\u6b21\u5f97\u4f60\u7684\u5e2e\u5fd9\u592a\u5927\u4e86\uff0c\u6211\u4e5f\u8fd8\u6ca1\u6709\u5411\u4f60\u9053\u8c22\u5462\u3002\u201d \u3000\u3000\u539f\u6765\u5c09\u8fdf\u70af\u592b\u59bb\u8fd9\u6b21\u5f97\u4ee5\u6df7\u5165\u8428\u5e9c\uff0c\u5168\u9760\u516c\u5b59\u5b8f\u7684\u63a9\u62a4\u3002\u4ed6\u4eec\u662f\u5192\u5145\u7ea2\u7f28\u4f1a\u7684\u4eba\uff0c\u5927\u6447 \u5927\u6446\u5730\u8fdb\u53bb\u7684\u3002\u5728\u52ab\u8d3a\u793c\u4e4b\u65f6\uff0c\u624d\u9732\u51fa\u672c\u6765\u9762\u76ee\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u9053\uff1a\u201c\u539f\u6765\u8fd9\u8001\u513f\u662f\u7ed9\u5c09\u8fdf\u70af\u4f5c\u5185\u5e94\u7684\uff0c\u602a\u4e0d\u5f97\u4ed6\u80af\u5230\u8428\u5e9c\u795d\u5bff\u3002\u201d\u5f53\u4e0b\u4ee5\u665a \u8f88\u4e4b\u793c\uff0c\u89c1\u8fc7\u4e86\u516c\u5b59\u5b8f\uff0c\u7b11\u9053\uff1a\u201c\u9002\u624d\u591a\u6709\u5192\u72af\uff0c\u8001\u524d\u8f88\u6055\u7f6a\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u4f60\u7684\u6b66\u529f\u5f88\u4e0d\u9519\u554a\uff0c\u4ee4\u5e08\u662f\u8c01\uff0c\u53ef\u4ee5\u89c1\u544a\u4e86\u5427\uff1f\u201d \u3000\u3000\u5b98\u79c9\u85e9\u4e0a\u524d\u4e0e\u91d1\u9010\u6d41\u89c1\u8fc7\u4e86\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u6211\u7ed9\u4f60\u4ecb\u7ecd\u4ecb\u7ecd\uff0c\u8fd9\u4f4d\u91d1\u5144\u5c31\u662f\u6700\u8fd1\u5728 \u6c5f\u6e56\u4e0a\u95f9\u5f97\u5929\u7ffb\u5730\u8986\u7684\u91d1\u9010\u6d41\u91d1\u5c11\u4fa0\u3002\u201d \u3000\u3000\u5bab\u79c9\u85e9\u8bf4\u4e86\u91d1\u9010\u6d41\u7684\u59d3\u540d\u6765\u5386\uff0c\u5c09\u8fdf\u70af\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u6c5f\u5927\u4fa0\u7684\u5e08\u5f1f\uff0c\u602a\u4e0d\u5f97\u6b66 \u529f\u8fd9\u4e48\u4e86\u5f97\uff01\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u91d1\u4e16\u9057\u91d1\u5927\u4fa0\u662f\u4f60\u4f55\u4eba\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6b63\u662f\u5bb6\u7236\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u66f4\u4e3a\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u4ee4\u5c0a\u66fe\u5728\u5c11\u6797\u5bfa\u89c1\u8fc7\u4e00\u9762\uff0c\u7b97\u8d77\u6765\u5df2\u662f\u4e8c\u5341\u591a\u5e74\u524d\u7684\u4e8b \u60c5\u4e86\u3002\u4ee4\u5c0a\u662f\u6211\u6700\u4f69\u670d\u7684\u4eba\uff0c\u4f60\u6211\u80fd\u5e73\u8f88\u8bba\u4ea4\uff0c\u4f60\u8981\u79f0\u6211\u8001\u524d\u8f88\uff0c\u6211\u53ef\u662f\u4e0d\u6562\u5f53\u3002\u201d \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u53f6\u6155\u534e\u5df2\u7ecf\u5230\u4e86\u5c0f\u91d1\u5ddd\u4e86\u3002\u4f60\u7684\u53e6\u5916\u4e24\u4f4d\u5e08\u4f84\u674e\u5149\u590f\u548c\u6797\u9053\u8f69\u4e5f\u5df2\u5230\u4e86\u7afa \u5c1a\u7236\u90a3\u513f\u3002\u6211\u9a6c\u4e0a\u5c31\u8981\u8d76\u56de\u5c0f\u91d1\u5ddd\uff0c\u91d1\u8001\u5f1f\uff0c\u4f60\u4eca\u5929\u95f9\u4e86\u8fd9\u4e00\u573a\uff0c\u5728\u5317\u4eac\u662f\u4e0d\u5b9c\u4e45\u7559\u7684\u4e86\u3002 \u4f60\u548c\u6211\u540c\u5f80\u5c0f\u91d1\u5ddd\u5982\u4f55\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u8fd8\u6709\u70b9\u4e8b\u60c5\uff0c\u6050\u6015\u8fd8\u5f97\u5341\u5929\u534a\u6708\u624d\u80fd\u79bb\u5f00\u5317\u4eac\u3002\u6211\u4f4f\u5728\u6234\u8001\u9556\u5934\u7684\u5bb6 \u91cc\uff0c\u53ef\u4ee5\u653e\u5fc3\u5f97\u4e0b\u7684\u3002\u201d \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u5c31\u5148\u5230\u5c0f\u91d1\u5ddd\u7b49\u4f60\u5427\u3002\u6234\u8001\u9556\u5934\u90a3\u513f\u6211\u6765\u4e0d\u53ca\u53bb\u62dc\u5019\u4e86\uff0c\u8bf7 \u4f60\u7ed9\u6211\u4ee3\u4e3a\u95ee\u5019\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u8981\u8d70\u90a3\u5c31\u5feb\u4e9b\u8d70\u5427\u3002\u5426\u5219\u90a3\u4e9b\u4eba\u8ffd\u6765\u4e86\u53c8\u6709\u4e00\u573a\u9ebb\u70e6\u3002\u201d \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u516c\u5b59\u5927\u54e5\uff0c\u4f60\u5462\uff1f\u8fd9\u6b21\u4f60\u6697\u4e2d\u52a9\u6211\uff0c\u4ed6\u4eec\u73b0\u5728\u867d\u7136\u8fd8\u672a\u53d1\u89c9\uff0c\u5c06\u6765\u603b\u4f1a\u77e5 \u9053\u7684\u3002\u4f60\u4e5f\u6050\u6015\u4e0d\u597d\u56de\u53bb\u4e86\u5427\uff1f\u201d \u3000\u3000\u516c\u5b59\u5b8f\u7b11\u9053\uff1a\u201c\u6211\u53c8\u4e0d\u60f3\u505a\u8428\u798f\u9f0e\u7684\u95e8\u5ba2\uff0c\u6211\u56de\u53bb\u4f5c\u4ec0\u4e48\uff1f\u6211\u5012\u662f\u8981\u56de\u5230\u6211\u7684\u603b\u8235\u53bb\uff0c \u7d22\u6027\u6253\u660e\u4e86\u65d7\u53f7\u53cd\u6e05\uff0c\u770b\u8428\u798f\u9f0e\u548c\u53f2\u767d\u90fd\u80fd\u628a\u6211\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5c09\u8fdf\u70af\u559c\u51fa\u671b\u5916\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u66f4\u597d\u4e86\uff0c\u5927\u54e5\u6253\u660e\u4e86\u65d7\u53f7\u3002\u6c5f\u6e56\u4e0a\u7684\u5404\u5927\u5e2e\u4f1a\u81f3\u5c11\u6709\u4e00 \u5927\u534a\u4e0d\u4f1a\u518d\u8ddf\u53f2\u767d\u90fd\u8d70\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u5404\u4eba\u5206\u9053\u626c\u9570\uff0c\u91d1\u9010\u6d41\u5f85\u5f97\u5929\u9ed1\u4e4b\u540e\uff0c\u72ec\u81ea\u6f5c\u56de\u5317\u4eac\u3002\u6b63\u662f\uff1a \u3000\u3000\u4eac\u534e\u9f99\u864e\u6597\uff0c\u6e56\u6d77\u8d77\u6ce2\u6d9b\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56de\u3000\u613f\u62fc\u70ed\u8840\u916c\u77e5\u5df1\u3000\u8bef\u89e3\u82b3\u5fc3\u65ad\u4fa0\u80a0 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e8c\u5341\u56de\u3000\u613f\u62fc\u70ed\u8840\u916c\u77e5\u5df1\u3000\u8bef\u89e3\u82b3\u5fc3\u65ad\u4fa0\u80a0 \u3000\u3000\u7ecf\u8fc7\u4e86\u65e5\u95f4\u8fd9\u573a\u5927\u95f9\uff0c\u5317\u4eac\u57ce\u4e2d\uff0c\u5927\u8857\u5c0f\u5df7\uff0c\u5e03\u6ee1\u5de1\u903b\u7684\u5175\u58eb\u3002\u5e78\u597d\u8fd9\u665a\u6ca1\u6709\u6708\u4eae\uff0c\u91d1 \u9010\u6d41\u4ed7\u7740\u8d85\u5999\u7684\u8f7b\u529f\uff0c\u501f\u7269\u969c\u5f62\uff0c\u7a9c\u9ad8\u7eb5\u4f4e\uff0c\u7792\u8fc7\u5de1\u903b\u7684\u8033\u76ee\uff0c\u6084\u6084\u5730\u6a21\u9ed1\u56de\u5230\u6234\u5bb6\uff0c\u6b64\u65f6 \u5df2\u662f\u4e09\u66f4\u65f6\u5206\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5927\u54e5\u77e5\u9053\u6211\u7684\u4f4f\u5740\uff0c\u4e0d\u77e5\u4ed6\u4f1a\u4e0d\u4f1a\u548c\u7ea2\u82f1\u6765\u6b64\u627e\u6211\uff1f\u201d\u674e\u5357\u661f\u3001\u53f2 \u7ea2\u82f1\u7684\u8f7b\u529f\u90fd\u4ec5\u662f\u7565\u900a\u4e8e\u4ed6\uff0c\u6587\u9053\u5e84\u3001\u6c99\u5343\u5cf0\u8fd9\u4e9b\u4eba\u662f\u540e\u6765\u624d\u53bb\u5e2e\u5fd9\u53f2\u767d\u90fd\u8ffd\u8d76\u4ed6\u4eec\u7684\uff0c\u6545 \u6b64\u91d1\u9010\u6d41\u6599\u60f3\u4ed6\u4eec\u5b9a\u80fd\u8131\u9669\u3002 \u3000\u3000\u60f3\u8d77\u4e86\u53f2\u7ea2\u82f1\uff0c\u91d1\u9010\u6d41\u4e0d\u89c9\u60d8\u7136\u82e5\u5931\u3002\u5c3d\u7ba1\u65e5\u95f4\u4ed6\u907f\u5f00\u4e86\u4ed6\u4eec\uff0c\u4f46\u5728\u5185\u5fc3\u6df1\u5904\uff0c\u4ed6\u8fd8\u662f \u60f3\u8981\u89c1\u4e00\u89c1\u53f2\u7ea2\u82f1\u7684\u3002\u201c\u602a\u4e0d\u5f97\u6628\u65e5\u5927\u54e5\u7684\u7434\u97f5\u4e4b\u4e2d\u4e00\u7247\u601d\u6155\u4e4b\u60c5\uff0c\u539f\u6765\u4ed6\u6240\u601d\u5ff5\u7684\u2018\u4f0a \u4eba\u2019\u5c31\u662f\u53f2\u59d1\u5a18\u3002\u8fd9\u6b21\u6050\u6015\u4e0d\u662f\u6211\u7684\u591a\u7591\u4e86\u3002\u770b\u65e5\u95f4\u7684\u60c5\u666f\uff0c\u5927\u54e5\u4f3c\u4e4e\u8fd8\u672a\u77e5\u9053\u6211\u548c\u53f2\u59d1\u5a18 \u7684\u4e8b\u60c5\uff0c\u5982\u679c\u4ed6\u771f\u662f\u559c\u6b22\u53f2\u59d1\u5a18\u7684\u8bdd\uff0c\u5509\uff0c\u6211\u5c31\u6210\u5168\u4ed6\u5427\u3002\u201d\u91d1\u9010\u6d41\u5fc3\u60f3\u3002 \u3000\u3000\u5df7\u53e3\u6b63\u6709\u4e00\u4e2a\u5de1\u903b\u7684\u5175\u58eb\u8d70\u8fc7\uff0c\u91d1\u9010\u6d41\u4e0d\u613f\u58f0\u5f20\uff0c\u4e8e\u662f\u8dc3\u5899\u800c\u5165\u3002\u8fdb\u4e86\u5185\u9662\uff0c\u53ea\u89c1\u5ba2\u5385 \u706f\u706b\u72b9\u660e\uff0c\u7eb1\u7a97\u4e0a\u89c4\u51fa\u56db\u4e2a\u4eba\u5f71\uff0c\u622a\u5747\u3001\u6234\u8c1f\u7236\u5b50\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u8001\u5934\u4e00\u4e2a\u5c11\u5e74\u3002\u6234\u5747\u4e0e\u90a3 \u8001\u5934\u6b63\u5728\u4e0b\u68cb\uff0c\u6234\u8c1f\u4e0e\u90a3\u5c11\u5e74\u5728\u65c1\u89c2\u6218\u3002\u5c11\u5e74\u9762\u671d\u5916\u5750\uff0c\u76f8\u8c8c\u4e0e\u6234\u8c1f\u76f8\u4f3c\u3002\u91d1\u9010\u6d41\u770b\u89c1\u8fd9\u5c11 \u5e74\u4e0d\u662f\u674e\u5357\u661f\uff0c\u5fc3\u4e2d\u6709\u70b9\u5931\u671b\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u60f3\u5fc5\u662f\u6234\u5747\u7684\u7b2c\u4e8c\u4e2a\u513f\u5b50\uff0c\u8fd9\u8001\u5934\u5374\u4e0d\u77e5\u662f \u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u4ece\u5899\u4e0a\u8df3\u4e0b\uff0c\u8eab\u8f7b\u5982\u53f6\uff0c\u843d\u5730\u65e0\u58f0\u3002\u4f46\u90a3\u8001\u5934\u5df2\u7ecf\u60ca\u89c9\uff0c\u968f\u624b\u6293\u8d77\u4e00\u679a\u68cb\u5b50\uff0c\u5934 \u4e5f\u4e0d\u56de\uff0c\u53cd\u624b\u5c31\u6253\u51fa\u53bb\u3002\u6253\u7684\u662f\u91d1\u9010\u6d41\u80c1\u4e0b\u7684\u9ebb\u7a74\u3002\u9ed1\u591c\u4e4b\u4e2d\uff0c\u8ba4\u7a74\u7adf\u662f\u4e0d\u5dee\u6beb\u5398\u3002\u91d1\u9010\u6d41 \u5fc3\u4e2d\u4e00\u51db\uff1a\u201c\u8fd9\u8001\u513f\u672c\u9886\u5012\u662f\u4e0d\u5f31\uff0c\u4eca\u65e9\u6211\u51fa\u53bb\u7684\u65f6\u5019\uff0c\u6234\u5747\u8981\u6211\u65e9\u4e9b\u56de\u6765\uff0c\u4f1a\u89c1\u4e00\u4e2a\u5ba2 \u4eba\uff0c\u6562\u60c5\u5c31\u662f\u8fd9\u4e2a\u8001\u513f\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u521a\u521a\u63a5\u4e0b\u90a3\u679a\u68cb\u5b50\uff0c\u6234\u5747\u5df2\u5728\u7b11\u9053\uff1a\u201c\u5510\u5144\uff0c\u8fd9\u4f4d\u5c31\u662f\u6211\u6240\u8bf4\u7684\u91d1\u5c11\u4fa0\u4e86\u3002\u201d\u90a3 \u8001\u513f\u7ad9\u4e86\u8d77\u6765\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5f97\u7f6a\uff0c\u5f97\u7f6a\uff01\u91d1\u8001\u5f1f\u83ab\u602a\uff0c\u6211\u4ee5\u4e3a\u662f\u53f2\u767d\u90fd\u6765\u627e\u8001\u6234\u7684\u9ebb\u70e6 \u5462\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u8863\u88f3\u4e0a\u8840\u8ff9\u6591\u6591\uff0c\u6234\u5747\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u53d7\u4f24\u4e86\u4e48\uff1f\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u6740\u4f24 \u4e86\u8428\u798f\u9f0e\u7684\u51e0\u4e2a\u624b\u4e0b\uff0c\u4fa5\u5e78\u6ca1\u6709\u53d7\u4f24\u3002\u201d\u6234\u5747\u9053\uff1a\u201c\u4f60\u4e5f\u771f\u662f\u592a\u80c6\u5927\u4e86\uff0c\u6211\u4e00\u542c\u8428\u5e9c\u6709\u4eba\u5927 \u95f9\u5bff\u5802\uff0c\u5c31\u77e5\u9053\u51c6\u6709\u4f60\u7684\u4efd\u513f\u3002\u201d\u539f\u6765\u6234\u5bb6\u662f\u9556\u884c\u4e16\u5bb6\uff0c\u4ea4\u6e38\u6781\u5e7f\uff0c\u6234\u5747\u7236\u5b50\u867d\u7136\u8db3\u4e0d\u51fa \u6237\uff0c\u5916\u95f4\u7684\u6d88\u606f\u5374\u662f\u65e0\u4e00\u4e0d\u77e5\u3002 \u3000\u3000\u91d1\u9010\u6d41\u62a5\u544a\u4e86\u5927\u95f9\u5bff\u5802\u7684\u60c5\u5f62\u3002\u6234\u5747\u9053\uff1a\u201c\u5c09\u8fdf\u70af\u8fd8\u662f\u5f53\u5e74\u5927\u95f9\u5929\u7262\u7684\u96c4\u98ce\uff0c\u6211\u5374\u5df2\u7ecf \u662f\u8001\u4e86\u4e0d\u4e2d\u7528\u4e86\u3002\u53ef\u60dc\u4ed6\u5306\u5306\u6765\u53bb\uff0c\u6211\u672a\u80fd\u548c\u4ed6\u89c1\u4e0a\u4e00\u9762\u3002\u8981\u4e0d\u7136\u4e24\u4f4d\u8001\u670b\u53cb\u540c\u65e5\u4e0d\u7ea6\u800c \u6765\uff0c\u4eca\u665a\u4e4b\u4f1a\u5c31\u66f4\u96be\u5f97\u4e86\u3002\u201d\u5f53\u4e0b\u7ed9\u91d1\u9010\u6d41\u4ecb\u7ecd\u90a3\u4f4d\u8001\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5510\u6770\u592b\u5927\u54e5\u662f\u6211\u51e0 \u5341\u5e74\u7684\u8001\u670b\u53cb\uff0c\u6211\u7279\u5730\u53eb\u5c0f\u513f\u4e0a\u897f\u5c71\u8bf7\u4ed6\u6765\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5728\u9648\u5929\u5b87\u5bb6\u4e2d\u4f5c\u5ba2\u4e4b\u65f6\uff0c\u66fe\u542c\u5f97\u9648\u5929\u5b87\u8bf4\u8fc7\u8bb8\u591a\u6b66\u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\u4e4b\u4e2d\u5c31\u6709\u5510\u6770 \u592b\u6b64\u4eba\u5728\u5185\u3002\u91d1\u9010\u6d41\u5411\u4ed6\u884c\u8fc7\u665a\u8f88\u4e4b\u793c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u9648\u53d4\u53d4\u8bf4\u4ed6\u662f\u56db\u5ddd\u7684\u6697\u5668\u540d\u5bb6\uff0c\u5374\u600e \u7684\u4e5f\u5230\u4e86\u5317\u4eac\u6765\u4e86\u3002\u201d \u3000\u3000\u6234\u5747\u7b11\u9053\uff1a\u201c\u91d1\u8001\u5f1f\uff0c\u8bf4\u6765\u4e5f\u771f\u662f\u4f60\u7684\u8fd0\u6c14\u3002\u8fd9\u4f4d\u5510\u5927\u54e5\u5728\u5bb6\u7eb3\u798f\uff0c\u5341\u51e0\u5e74\u8db3\u8ff9\u4e0d\u51fa \u6237\uff0c\u8fd9\u6b21\u5374\u51d1\u5de7\u5230\u4e86\u5317\u4eac\u6765\u4e86\u3002\u4ed6\u662f\u4e0a\u4e2a\u6708\u6765\u7684\u3002\u4e0b\u584c\u897f\u5c71\u5367\u4f5b\u5bfa\u3002\u5367\u4f5b\u5bfa\u7684\u4e3b\u6301\u56db\u7a7a\u4e0a\u4eba \u662f\u5c0f\u513f\u6234\u8c1f\u7684\u5e08\u7236\uff0c\u4e5f\u662f\u5510\u5927\u54e5\u7684\u65b9\u5916\u4e4b\u4ea4\u3002\u6211\u672c\u6765\u8981\u4ed6\u4f4f\u5728\u8fd9\u513f\uff0c\u4ed6\u5acc\u8fd9\u91cc\u4e0d\u53ca\u5367\u4f5b\u5bfa\u7684 \u6e05\u51c0\u3002\u4eca\u5929\u8981\u4e0d\u662f\u51b2\u7740\u4f60\u8fd9\u5757\u7384\u94c1\uff0c\u4ed6\u8fd8\u4e0d\u80af\u6765\u5462\u3002\u201d \u3000\u3000\u5510\u6770\u592b\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u513f\u597d\u505a\u4e0d\u505a\uff0c\u4e3a\u4e86\u907f\u4ec7\uff0c\u5c45\u7136\u8bc8\u6b7b\u3002\u8981\u6211\u6765\u7ed9\u4f60\u5b88\u7075\u4e48\uff1f\u201d \u3000\u3000\u6234\u5747\u9053\uff1a\u201c\u8fd9\u6b21\u627e\u4e0d\u662f\u8981\u4f60\u6765\u5b88\u7075\uff0c\u662f\u8981\u4f60\u6765\u505a\u6253\u94c1\u5320\u4e86\uff01\u91d1\u8001\u5f1f\uff0c\u4f60\u6050\u6015\u8fd8\u4e0d\u77e5\u9053\uff0c \u8fd9\u4f4d\u5510\u5927\u54e5\u4e0d\u4ec5\u662f\u6697\u5668\u540d\u5bb6\uff0c\u4ed6\u8fd8\u662f\u5929\u4e0b\u7b2c\u4e00\u7684\u94f8\u5251\u5e08\u3002\u4f60\u6628\u5929\u548c\u6211\u8bf4\u7684\u65f6\u5019\uff0c\u6211\u8fd8\u6050\u6015\u8bf7 \u4e0d\u52a8\u4ed6\u5462\u3002\u201d \u3000\u3000\u5eb8\u6770\u592b\u9053\uff1a\u201c\u7384\u94c1\u662f\u7a00\u4e16\u4e4b\u73cd\uff0c\u6211\u6d3b\u4e86\u8fd9\u4e00\u5927\u628a\u5e74\u7eaa\u3002\u90fd\u8fd8\u672a\u66fe\u89c1\u8fc7\u5462\u3002\u5c82\u80fd\u4e0d\u6765\u5f00\u5f00 \u773c\u754c\uff1f\u91d1\u8001\u5f1f\uff0c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6211\u8fd9\u70b9\u624b\u827a\uff0c\u53ea\u6015\u7cdf\u8e4b\u4e86\u4f60\u7684\u7384\u94c1\uff0c\u4f60\u653e\u5fc3\u5f97\u4e0b\u5417\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u559c\u51fa\u671b\u5916\uff0c\u8bf4\u9053\uff1a\u201c\u5510\u8001\u524d\u8f88\u80af\u7ed9\u6211\u5e2e\u8fd9\u4e2a\u5927\u5fd9\uff0c\u665a\u8f88\u4e0d\u77e5\u5982\u4f55\u62a5\u7b54\u3002\u8001\u524d\u8f88\u4e0d \u8981\u592a\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u6234\u5747\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u90e8\u7528\u4e0d\u7740\u5ba2\u6c14\u3002\u8001\u5b9e\u8bf4\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u53ea\u6709\u4f60\u5510\u5927\u54e5\u624d\u914d\u94f8\u8fd9\u628a\u5b9d \u5251\u3002\u800c\u5510\u5927\u54e5\u89c1\u4e86\u8fd9\u5757\u7384\u94c1\uff0c\u4f60\u91d1\u8001\u4e09\u5f1f\u82e5\u662f\u4e0d\u7ed9\u4ed6\u4ee3\u4e3a\u94f8\u5251\uff0c\u4ed6\u4e5f\u8981\u6280\u75d2\u96be\u71ac\uff0c\u975e\u62a2\u4e86\u4f60 \u8fd9\u5757\u7384\u94c1\u6765\u94f8\u4e0d\u53ef\u3002\u201d \u3000\u3000\u5510\u6770\u592b\u7b11\u9053\uff1a\u201c\u4f60\u771f\u662f\u8bf4\u5230\u6211\u7684\u5fc3\u574e\u513f\u91cc\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5230\u623f\u95f4\u91cc\u6362\u8fc7\u8863\u88f3\uff0c\u8ddf\u7740\u628a\u90a3\u5757\u4ea5\u94c1\u62ff\u51fa\u6765\u7ed9\u5510\u6770\u592b\u770b\u3002\u5510\u6770\u672a\u628a\u73a9\u4e86\u597d\u4e00\u4f1a\uff0c \u8fde\u58f0\u8d5e\u53f9\uff1a\u201c\u771f\u662f\u5b9d\u8d1d\u3001\u5018\u82e5\u94f8\u6210\u5b9d\u5251\uff0c\u5b9a\u662f\u5929\u4e0b\u5175\u5668\u4e4b\u738b\uff01\u53ea\u662f\u8981\u94f8\u8fd9\u628a\u5b9d\u5251\uff0c\u6211\u8fd8\u5f97\u6709 \u4e00\u4e9b\u5de5\u5177\u624d\u884c\u3002\u201d \u3000\u3000\u6234\u5747\u9053\u4e86\uff1a\u201c\u8fd9\u4e2a\u4e0d\u7528\u4f60\u8bf4\uff0c\u6211\u65e9\u5df2\u7ed9\u4f60\u51c6\u5907\u597d\u4e86\uff0c\u6211\u8fd9\u91cc\u6709\u4e2a\u5730\u7a96\uff0c\u6211\u5df2\u88c5\u4e86\u4e00\u4e2a\u9f13 \u98ce\u7089\uff0c\u5927\u94c1\u9524\u4e5f\u7ed9\u4f60\u6253\u4e86\u4e24\u4e2a\u3002\u7b49\u4e0b\u4f60\u53bb\u770b\u770b\u5408\u4e0d\u5408\u7528\uff1f\u201d \u3000\u3000\u5f53\u4e0b\u5510\u6770\u592b\u62ff\u4e86\u7384\u94c1\uff0c\u548c\u4f17\u4eba\u5230\u5730\u7a96\u5de1\u89c6\u4e86\u4e00\u904d\uff0c\u7b11\u9053\uff1a\u201c\u8001\u6234\uff0c\u4f60\u771f\u662f\u60f3\u5f97\u5468\u5230\uff0c\u5728 \u8fd9\u5730\u7a96\u91cc\u6253\u94c1\uff0c\u58f0\u97f3\u4e0d\u4f1a\u4f20\u5230\u5916\u9762\uff0c\u771f\u662f\u6700\u5999\u4e0d\u8fc7\uff0c\u597d\uff0c\u6211\u660e\u5929\u4e00\u65e9\u5c31\u5f00\u5de5\u3002\u201d\u6234\u5747\u9053\uff1a \u201c\u5e78\u597d\u6211\u6709\u94c1\u5320\u670b\u53cb\uff0c\u8fd9\u9f13\u98ce\u7089\u662f\u501f\u6765\u7684\u3002\u4ed6\u521a\u521a\u642c\u6765\uff0c\u5916\u95f4\u5c31\u95f9\u4e8b\u4e86\uff0c\u771f\u662f\u597d\u9669\uff01\u5018\u82e5\u8fdf \u4e86\u534a\u523b\uff0c\u4e00\u5b9a\u4f1a\u7ed9\u5de1\u903b\u7684\u5175\u58eb\u622a\u7740\u76d8\u95ee\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u89c1\u6234\u5747\u4e3a\u4ed6\u5982\u6b64\u8d39\u5c3d\u5fc3\u529b\uff0c\u5fc3\u4e2d\u5341\u5206\u611f\u6fc0\uff0c\u4f46\u5982\u4e5f\u4e0d\u7981\u6709\u4e9b\u6005\u60d8\u3002 \u3000\u3000\u4ed6\u94f8\u8fd9\u67c4\u5b9d\u5251\uff0c\u662f\u51c6\u5907\u9001\u7ed9\u674e\u5357\u661f\u7684\u3002\u5982\u4eca\u674e\u5357\u661f\u4e0e\u53f2\u7ea2\u82f1\u5374\u5df2\u4e0d\u77e5\u4f55\u5f80\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u4eec \u4f1a\u4e0d\u4f1a\u5bfb\u6765\uff1f \u3000\u3000\u4e00\u5929\u3001\u4e24\u5929\u3001\u4e09\u5929\u2026\u2026\u91d1\u9010\u6d41\u6bcf\u4e00\u5929\u90fd\u5728\u76fc\u671b\u4ed6\u4eec\uff0c\u5374\u603b\u4e0d\u89c1\u4ed6\u4eec\u6765\u5230\u3002\u4e0d\u77e5\u4e0d\u89c9\u8fc7\u4e86 \u4e03\u5929\uff0c\u90a3\u5757\u7384\u94c1\u5df2\u662f\u70bc\u5f97\u7089\u706b\u7eaf\u9752\uff0c\u5b9d\u5251\u5c31\u5c06\u94f8\u6210\u4e86\u3002\u8fd8\u662f\u4e0d\u89c1\u4ed6\u4eec\u5230\u6765\u3002 \u3000\u3000\u674e\u5357\u661f\u4e0e\u53f2\u7ea2\u82f1\u5230\u4e86\u4ec0\u4e48\u5730\u65b9\u5462\uff1f\u4ed6\u4eec\u600e\u4e48\u6837\u4e86\uff1f \u3000\u3000\u82b1\u5f00\u4e24\u6735\uff0c\u5404\u8868\u4e00\u679d\uff0c\u91d1\u9010\u6d41\u5728\u6234\u5bb6\u7b49\u5f97\u5fc3\u7126\uff0c\u6682\u4e14\u4e0d\u8868\u3002\u56de\u8fc7\u5934\u6765\uff0c\u4e14\u5148\u8bf4\u8bf4\u674e\u3001\u53f2 \u4e8c\u4eba\u90a3\u65e5\u7684\u906d\u9047\u3002 \u3000\u3000\u90a3\u65e5\u53f2\u7ea2\u82f1\u9003\u51fa\u8428\u5e9c\uff0c\u674e\u5357\u661f\u4ed7\u5251\u7ed9\u5979\u65ad\u540e\uff0c\u4e14\u6218\u4e14\u8d70\uff0c\u51fa\u4e86\u57ce\u95e8\uff0c\u4e0d\u6d88\u591a\u4e45\uff0c\u5df2\u628a\u8ffd \u5175\u7529\u5728\u80cc\u540e\u3002\u56de\u5934\u671b\u53bb\uff0c\u8fdc\u8fdc\u7684\u5c31\u53ea\u89c1\u53f2\u767d\u90fd\u4e00\u4eba\u8ffd\u6765\u4e86\u3002\u53f2\u767d\u90fd\u7684\u672c\u9886\u867d\u7136\u5728\u59b9\u59b9\u4e4b\u4e0a\uff0c \u4f46\u8f7b\u529f\u5219\u7a0d\u6709\u4e0d\u5982\u3002\u4f46\u7531\u4e8e\u4ed6\u5728\u90a3\u5c94\u8def\u53e3\u66fe\u7ecf\u8fdf\u7591\u7247\u523b\uff0c\u53cc\u65b9\u7684\u8ddd\u79bb\u5c31\u66f4\u662f\u8d8a\u6765\u8d8a\u8fdc\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u751f\u6015\u7ed9\u54e5\u54e5\u8ffd\u4e0a\uff0c\u4e00\u53e3\u6c14\u8dd1\u4e86\u5341\u591a\u91cc\u8def\uff0c\u4e0d\u6562\u505c\u7559\uff0c\u4e5f\u6ca1\u6709\u548c\u674e\u5357\u661f\u4ea4\u8c08\u3002\u674e\u5357 \u661f\u7b11\u9053\uff1a\u201c\u53ef\u4ee5\u8d70\u6162\u4e00\u70b9\u4e86\uff0c\u521a\u624d\u8fd8\u53ef\u4ee5\u770b\u89c1\u4f60\u54e5\u54e5\u7684\u5f71\u5b50\uff0c\u73b0\u5728\u8fde\u5f71\u5b50\u4e5f\u770b\u4e0d\u89c1\u5566\u3002\u201d\u53f2 \u7ea2\u82f1\u8dd1\u5f97\u592a\u5feb\uff0c\u674e\u5357\u661f\u8ddf\u7740\u5979\u8dd1\uff0c\u4e5f\u611f\u5230\u6709\u70b9\u5403\u529b\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u677e\u4e86\u53e3\u6c14\uff0c\u84e6\u5730\u5fc3\u5934\u4e00\u52a8\uff0c\u77bf\u7136\u4e00\u7701\uff0c\u505c\u4e0b\u811a\u6b65\uff0c\u56de\u5934\u4e00\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u9010\u6d41 \u5462\uff1f\u4ed6\u9003\u51fa\u6765\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u6211\u770b\u89c1\u4ed6\u5df2\u7ecf\u8dd1\u51fa\u6765\u4e86\u7684\uff0c\u5374\u4e0d\u77e5\u4ed6\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u4e0d\u8fc7\uff0c\u4f60\u5927\u53ef\u653e\u5fc3\uff0c \u4ed6\u7684\u672c\u9886\u6bd4\u6211\u9ad8\u660e\uff0c\u4f60\u7684\u54e5\u54e5\u53c8\u4e0d\u662f\u53bb\u8ffd\u4ed6\uff0c\u4ed6\u4e00\u5b9a\u53ef\u4ee5\u8131\u9669\u7684\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u7684\u672c\u9886\uff0c\u53ea\u662f\u2026\u2026\u201d\u674e\u5357\u661f\u9053\uff1a\u201c\u53ea\u662f\u4ec0\u4e48\uff1f\u201d\u53f2\u7ea2\u82f1\u672c\u6765\u8981\u8bf4 \u7684\u662f\uff1a\u201c\u6211\u53ea\u662f\u60f3\u89c1\u4ed6\u4e00\u9762\u3002\u201d\u89c1\u674e\u5357\u661f\u53cc\u773c\u51dd\u89c6\u7740\u5979\uff0c\u773c\u5149\u4f3c\u4e4e\u6709\u4e9b\u5f02\u6837\uff0c\u5ffd\u5730\u611f\u5230\u6709\u70b9 \u5bb3\u7f9e\uff0c\u60f3\u9053\uff1a\u201c\u6211\u4f55\u5fc5\u5411\u4ed6\u5410\u9732\u6211\u7684\u5fc3\u4e8b\uff0c\u53eb\u4ed6\u4f20\u5230\u91d1\u9010\u6d41\u7684\u8033\u6735\u91cc\u53bb\u3002\u201d\u4e3a\u4e86\u4fdd\u6301\u5c11\u5973\u7684 \u77dc\u6301\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u6539\u6210\u4e86\uff1a\u201c\u53ea\u662f\uff0c\u53ea\u662f\u6211\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff0c\u4f60\u65e2\u7136\u770b\u89c1\u4e86\u4ed6\uff0c\u4ed6\u5e94\u8be5\u4e5f\u770b\u89c1 \u4f60\u7684\uff0c\u4e3a\u4ec0\u4e48\u4ed6\u5374\u4e0d\u6765\u5bfb\u4f60\uff1f\u201d\u5176\u5b9e\u53f2\u7ea2\u82f1\u662f\u60f3\u91d1\u9010\u6d41\u8dd1\u6765\u627e\u5979\u3002 \u3000\u3000\u674e\u5357\u661f\u4e5f\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff0c\u8bf4\u9053\uff1a\u201c\u6216\u8bb8\u4ed6\u662f\u8dd1\u53bb\u627e\u5c09\u8fdf\u70af\u53bb\u4e86\u3002\u4f60\u548c\u4ed6\u65e9\u5df2\u76f8\u8bc6\u7684 \u5417\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4ed6\u53eb\u4f60\u505a\u5927\u54e5\uff0c\u4f60\u4eec\u662f\u7ed3\u4e49\u7684\u5144\u5f1f\u5427\uff1f\u6211\u548c\u4ed6\u76f8\u8bc6\u8fd8\u4e0d\u5230\u4e00\u4e2a\u6708\u5440\uff01\u201d \u3000\u3000\u674e\u5357\u661f\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u548c\u4ed6\u6709\u4e86\u591a\u5c11\u5e74\u7684\u4ea4\u60c5\uff1f\u54c8\u54c8\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u548c\u4ed6 \u662f\u6628\u5929\u624d\u76f8\u8bc6\u7684\uff0c\u4f60\u5947\u602a\u4e0d\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e00\u89c1\u9762\u5c31\u7ed3\u4e3a\u5144\u5f1f\u4e86\uff1f\u201d\u6211\u7684\u786e\u662f\u610f\u60f3\u4e0d\u5230\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u4eba\u4e4b\u76f8\u77e5\uff0c\u8d35\u76f8\u77e5\u5fc3\u3002\u53e4\u4eba\u4e91\uff1a\u767d\u5934\u5982\u65b0\uff0c\u503e\u76d6\u5982\u6545\u3002\u4ea4\u60c5\u7684\u6df1\u6d45\u672c\u6765\u5c31 \u4e0d\u5728\u4e8e\u65f6\u65e5\u7684\u77ed\u957f\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9762\u4e0a\u4e00\u7ea2\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u96be\u9053\u4ed6\u5df2\u7ecf\u77e5\u9053\u4e86\u6211\u548c\u91d1\u9010\u6d41\u7684\u4e8b\u60c5\uff0c\u7279\u5730\u5f00\u6211\u7684\u73a9\u7b11 \u7684\uff1f\u54fc\u201d\u91d1\u9010\u6d41\u8fd9\u50bb\u5c0f\u5b50\u4e5f\u4e0d\u77e5\u80e1\u8bf4\u4e86\u4e9b\u4ec0\u4e48\uff0c\u771f\u662f\u4e0d\u8be5\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u662f\u4e2a\u51b0\u96ea\u806a\u660e\u7684\u5973\u5b50\uff0c\u91d1\u9010\u6d41\u4e00\u8def\u5bf9\u5979\u7ea0\u7f20\uff0c\u5979\u5f53\u7136\u660e\u767d\u91d1\u9010\u6d41\u5bf9\u5979\u7684\u5fc3\u610f\u3002\u4f46 \u5728\u672a\u6210\u201c\u5b9a\u5c40\u201d\u4e4b\u524d\uff0c\u5979\u5374\u4e0d\u613f\u91d1\u9010\u6d41\u5411\u65c1\u4eba\u5410\u9732\u3002\u6b64\u65f6\u5979\u53ea\u9053\u91d1\u9010\u6d41\u5df2\u7ecf\u628a\u5fc3\u4e8b\u544a\u8bc9\u4e86\u674e \u5357\u661f\uff0c\u674e\u5357\u661f\u8bf4\u7684\u8fd9\u4e9b\u8bdd\u662f\u5411\u81ea\u5df1\u201c\u8bd5\u63a2\u201d\u7684\uff0c\u6545\u800c\u5fc3\u91cc\u6709\u51e0\u5206\u7740\u607c\uff0c\u4e5f\u6709\u51e0\u5206\u60ca\u559c\u3002 \u3000\u3000\u6b8a\u4e0d\u77e5\u674e\u5357\u661f\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u5979\u548c\u91d1\u9010\u6d41\u4e4b\u95f4\u7684\u4e8b\u60c5\uff0c\u5f53\u7136\u66f4\u4e0d\u77e5\u9053\u4ed6\u4eec\u4e24\u4eba\u65e9\u5df2\u662f\u5fc3\u5fc3 \u76f8\u5370\u3002\u4ed6\u4e0d\u662f\u66ff\u91d1\u9010\u6d41\u8bd5\u63a2\u7684\uff0c\u800c\u662f\u4e3a\u4ed6\u81ea\u5df1\u8bd5\u63a2\u3002 \u3000\u3000\u4ed6\u89c1\u53f2\u7ea2\u82f1\u8138\u6cdb\u7ea2\u6f6e\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u6697\u559c\uff1a\u201c\u55ef\uff0c\u770b\u6765\u5979\u662f\u5df2\u7ecf\u61c2\u5f97\u6211\u7684\u610f\u601d\u4e86\uff0c\u4e0b\u4e00\u6b65 \u6211\u5e94\u8be5\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e24\u4eba\u5404\u6000\u5fc3\u4e8b\uff0c\u5374\u4e0d\u77e5\u90fd\u662f\u8bef\u4f1a\u4e86\u5bf9\u65b9\u7684\u610f\u601d\u3002\u6b63\u5728\u4ed6\u4eec\u5fc3\u4e8b\u5982\u9ebb\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u8e44\u58f0\u5f97 \u5f97\uff0c\u524d\u9762\u5c18\u5934\u5927\u8d77\uff0c\u6709\u516d\u4e03\u9a91\u5b98\u519b\u8fce\u9762\u800c\u6765\u3002\u800c\u540e\u9762\u7684\u53f2\u767d\u90fd\u4e5f\u5df2\u8ffd\u4e0a\u6765\u4e86\u3002\u53f2\u767d\u90fd\u7684\u8f7b\u529f \u53ea\u662f\u6bd4\u4ed6\u59b9\u59b9\u7a0d\u900a\u4e00\u7b79\uff0c\u4f46\u6c14\u529b\u60a0\u957f\uff0c\u82e5\u662f\u957f\u9014\u7adf\u8dd1\uff0c\u53f2\u7ea2\u82f1\u8fd8\u662f\u8dd1\u4e0d\u8fc7\u4ed6\u7684\u3002 \u3000\u3000\u53f2\u767d\u90fd\u628a\u773c\u671b\u53bb\uff0c\u5df2\u7ecf\u770b\u89c1\u4e86\u6765\u8005\u662f\u8c01\uff0c\u559c\u51fa\u671b\u5916\uff0c\u5927\u53eb\u9053\uff1a\u201c\u5e05\u5c06\u519b\uff0c\u5feb\u5feb\u622a\u4e0b\u8fd9\u4e2a \u5c0f\u5b50\uff01\u201d\u539f\u6765\u6765\u7684\u662f\u897f\u661f\u5c06\u519b\u5e05\u5b5f\u96c4\uff0c\u4ed6\u662f\u5949\u53ec\u56de\u4eac\uff0c\u62a5\u544a\u897f\u661f\u65b9\u9762\u7684\u519b\u4e8b\u60c5\u51b5\u7684\u3002\u540e\u9762\u90a3 \u51e0\u9a91\u662f\u4ed6\u7684\u968f\u4ece\u3002 \u3000\u3000\u524d\u65e0\u53bb\u8def\uff0c\u540e\u6709\u8ffd\u5175\u3002\u53f2\u7ea2\u82f1\u662f\u77e5\u9053\u5e05\u5b5f\u96c4\u6b64\u4eba\u7684\u672c\u9886\u4e0d\u5728\u5979\u54e5\u54e5\u4e4b\u4e0b\uff0c\u4e5f\u77e5\u9053\u5979\u7684\u54e5 \u54e5\u6b63\u662f\u8981\u903c\u5979\u5ac1\u7ed9\u6b64\u4eba\u3002\u6b64\u9645\u964c\u8def\u76f8\u9022\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u674e\u5357\u661f\u5374\u4e0d\u77e5\u9053\u5e05\u5b5f\u96c4\u7684\u5389\u5bb3\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u7740\u614c\uff0c\u95ef\u8fc7\u53bb\u5c31\u662f\uff01\u201d\u628a\u624b\u4e00\u626c\uff0c\u53ea \u542c\u5f97\u201c\u4e52\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u56e2\u70df\u96fe\u968f\u98ce\u5377\u53bb\uff0c\u70df\u96fe\u4e2d\u91d1\u5149\u95ea\u70c1\uff0c\u53d1\u51fa\u201c\u55e4\u55e4\u201d\u58f0\u54cd\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u4e5f\u4f1a\u4f7f\u7528\u8fd9\u79cd\u6b79\u6bd2\u7684\u6697\u5668\uff1f\u201d\u539f\u6765\u674e\u5357\u661f\u6240\u53d1\u7684\u6697\u5668 \u540d\u4e3a\u201c\u6bd2\u96fe\u91d1\u9488\u70c8\u7130\u5f39\u201d\uff0c\u53f2\u5bb6\u4e5f\u6709\u8fd9\u79cd\u6697\u5668\uff0c\u4f46\u53f2\u7ea2\u82f1\u4e00\u770b\uff0c\u5c31\u77e5\u9053\u674e\u5357\u661f\u6240\u53d1\u7684\u8fd9\u679a \u201c\u6bd2\u96fe\u91d1\u9488\u70c8\u7130\u5f39\u201d\u6bd4\u5979\u4eec\u5bb6\u4f20\u7684\u8fd8\u8981\u5389\u5bb3\u5f97\u591a\u3002\u2026 \u3000\u3000\u70df\u96fe\u5f25\u6f2b\u4e4b\u4e2d\u53ea\u89c1\u4eba\u4ef0\u9a6c\u7ffb\uff0c\u201c\u54ce\u54df\uff0c\u54ce\u54df\u201d\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\u3002\u5e05\u5b5f\u96c4\u7684\u51e0\u4e2a\u968f\u4ece\u90fd\u4e2d\u4e86 \u6742\u5728\u70df\u96fe\u4e4b\u4e2d\u7684\u6bd2\u9488\uff0c\u4f46\u5e05\u5b5f\u96c4\u5374\u8fd8\u662f\u9a91\u5728\u9a6c\u4e0a\uff0c\u800c\u4e14\u5df2\u7ecf\u51b2\u51fa\u96fe\u7f51\u4e86\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5e05\u76c2\u96c4\u5df2\u7ecf\u98de\u9a91\u6765\u5230\uff0c\u201c\u563f\u563f\u201d\u5730\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6253\u5f97\u597d\u6b79\u6bd2\u7684 \u6697\u5668\uff0c\u53ef\u60dc\u78b0\u4e0a\u4e86\u6211\uff0c\u4f60\u8fd9\u70b9\u4f0e\u4fe9\u53c8\u80fd\u5948\u6211\u4f55\u54c9\uff1f\u597d\uff0c\u5148\u53eb\u4f60\u5403\u6211\u4e00\u97ad\uff01\u201d\u4eba\u672a\u79bb\u978d\uff0c\u63d0\u8d77 \u9a6c\u97ad\uff0c\u5530\u7684\u5c31\u662f\u4e00\u97ad\u5411\u674e\u5357\u661f\u6253\u4e0b\u3002\u674e\u5357\u661f\u4e00\u5251\u524a\u51fa\uff0c\u559d\u9053\uff1a\u201c\u7ed9\u6211\u6eda\u4e0b\u9a6c\u6765\uff01\u201d \u3000\u3000\u5e05\u76c2\u96c4\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u559d\u9053\uff1a\u201c\u64a4\u5251\u3002\u201d\u9a6c\u97ad\u4e00\u6296\uff0c\u5df2\u662f\u5377\u4f4f\u4e86\u674e\u5357\u661f\u7684\u5251\u67c4\u3002 \u3000\u3000\u674e\u5357\u661f\u628a\u5251\u524a\u53bb\uff0c\u53ef\u662f\u7531\u4e8e\u5251\u67c4\u88ab\u4ed6\u5377\u4f4f\uff0c\u5251\u5cf0\u7528\u4e0d\u4e0a\u529b\uff0c\u867d\u7136\u78b0\u7740\u4e86\u540e\u534a\u622a\u9a6c\u97ad\uff0c\u5374 \u662f\u524a\u4e4b\u4e0d\u65ad\u3002 \u3000\u3000\u5e05\u5b5f\u96be\u672c\u6765\u8981\u5377\u4ed6\u7684\u624b\u8155\u7684\uff0c\u5dee\u4e4b\u6beb\u5398\uff0c\u5377\u7740\u4e86\u5251\u67c4\uff0c\u674e\u5357\u661f\u7684\u5251\u5e76\u6ca1\u6709\u8131\u624b\uff0c\u4ed6\u5374\u7ed9 \u674e\u5357\u661f\u731b\u7684\u4e00\u62c9\uff0c\u51e0\u4e4e\u5c06\u4ed6\u62c9\u4e0b\u9a6c\u6765\uff0c\u5e05\u5b5f\u96c4\u4e16\u4e0d\u7981\u6697\u6697\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u8fd9\u5c0f\u5b50\u5012\u662f\u6709\u51e0\u5206\u786c \u529f\uff01\u201d\u5f53\u4e0b\u53cc\u817f\u4e00\u5939\uff0c\u80ef\u4e0b\u7684\u5750\u9a91\u7ed9\u4ed6\u50ac\u5f97\u5411\u524d\u98de\u8dd1\u3002\u5e05\u76c2\u96c4\u7b11\u9053\uff1a\u201c\u5148\u6559\u4f60\u5403\u70b9\u82e6\u5934\uff0c\u770b \u4f60\u6492\u4e0d\u6492\u624b\uff1f\u201d \u3000\u3000\u54ea\u77e5\u4ed6\u7684\u5750\u9a91\u98de\u8dd1\uff0c\u5374\u7ed9\u4e86\u674e\u5357\u661f\u4e00\u4e2a\u53ef\u4e58\u4e4b\u673a\u3002\u674e\u5357\u661f\u811a\u8ddf\u4e00\u6491\uff0c\u5c31\u4f3c\u8361\u79cb\u5343\u4f3c\u7684\u8361 \u4e86\u8d77\u6765\u3002\u5e05\u5b5f\u96c4\u6765\u4e0d\u53ca\u628a\u9a6c\u97ad\u89e3\u5f00\uff0c\u674e\u5357\u661f\u5df2\u662f\u628a\u5251\u8fde\u97ad\uff0c\u671d\u4ed6\u523a\u4e0b\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u677e\u624b\u6254\u97ad\uff0c\u8df3\u4e0b\u9a6c\u80cc\uff0c\u674e\u5357\u661f\u4e5f\u50cf\u65ad\u4e86\u7ebf\u7684\u98ce\u7b5d\u4f3c\u671d\u4e0b\u843d\u4e0b\u5730\u6765\u3002\u5e05\u5b5f\u96c4\u559d\u9053\uff1a \u201c\u597d\u5c0f\u5b50\uff0c\u8fd8\u4e0d\u675f\u624b\u53d7\u64d2\uff1f\u201d\u58f0\u5230\u4eba\u5230\uff0c\u610f\u6b32\u8d81\u7740\u674e\u5357\u661f\u7acb\u8db3\u672a\u7a33\uff0c\u5c06\u4ed6\u624b\u5230\u64d2\u6765\u3002 \u3000\u3000\u674e\u5357\u661f\u559d\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u8eab\u5982\u9640\u87ba\u75be\u8f6c\uff0c\u770b\u4f3c\u5fa1\u6b65\u8dc4\u8e09\uff0c\u4f46\u90a3\u5251\u52bf\u5374\u662f\u5341\u5206\u51cc\u5389\u3002\u5e05 \u5b5f\u96c4\u662f\u4e2a\u8bc6\u8d27\u7684\u4eba\uff0c\u4e00\u770b\u5c31\u77e5\u674e\u5357\u661f\u7528\u7684\u662f\u9189\u516b\u4ed9\u5251\u6cd5\uff0c\u5f53\u4e0b\uff0c\u54ea\u91cc\u8fd8\u6562\u8f7b\u654c\uff0c\u8fde\u5fd9\u98de\u8d77\u4e00 \u811a\uff0c\u62f3\u811a\u90fd\u7528\u4e0a\u4e86\uff0c\u8fd9\u624d\u903c\u9000\u4e86\u674e\u5357\u661f\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53f2\u767d\u90fd\u5df2\u7136\u8d76\u5230\uff0c\u53eb\u9053\uff1a\u201c\u4e14\u6162\u52a8\u624b\uff01\u5f85\u6211\u5148\u95ee\u4e00\u95ee\u8fd9\u4e2a\u5c0f\u5b50\u3002\u5582\uff0c \u4f60\u8fd9\u6697\u5668\u7684\u529f\u592b\u662f\u8c01\u6559\u7ed9\u4f60\u7684\uff1f\u4f60\u548c\u5929\u9b54\u6559\u53ef\u662f\u6709\u751a\u6e0a\u6e90\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u51b7\u7b11\u9053\uff1a\u201c\u51ed\u4f60\u4e5f\u914d\u95ee\u6211\u7684\u6765\u5386\uff1f\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u6beb\u4e0d\u653e\u677e\uff0c\u4e00\u4e2a\u201c\u9634\u9633\u53cc\u649e\u638c\u201d\u63a5\u7740\u53c8\u662f\u201c\u9e33\u9e2f\u8fde\u73af\u817f\u201d\uff0c\u6253\u5f97\u674e\u5357\u661f\u53ea\u6709\u62db\u67b6 \u4e4b\u529f\u3002\u5e05\u5b5f\u96c4\u62a2\u4e86\u4e0a\u98ce\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u7ba1\u4ed6\u662f\u4ec0\u4e48\u4eba\uff0c\u5148\u628a\u4ed6\u62ff\u4e0b\u518d\u8bf4\uff01\u54fc\uff0c\u4ed6\u80c6\u6562\u6740\u5bb3\u6211 \u7684\u968f\u4ece\uff0c\u6211\u5c31\u6bd9\u4e86\u4ed6\u4e5f\u4e0d\u4e3a\u8fc7\uff01\u201d\u539f\u6765\u5e05\u76c2\u96c4\u770b\u89c1\u674e\u5357\u661f\u548c\u53f2\u7ea2\u82f1\u5728\u4e00\u8d77\uff0c\u4ed6\u662f\u60f3\u5a36\u53f2\u7ea2\u82f1 \u4e3a\u59bb\u7684\uff0c\u7109\u80fd\u4e0d\u5992\u706b\u4e2d\u70e7\uff0c\u6068\u4e0d\u5f97\u9a6c\u4e0a\u5c31\u6bd9\u4e86\u674e\u5357\u661f\uff1f \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u52ab\u4e86\u8428\u603b\u7ba1\u7684\u5bff\u793c\uff0c\u6700\u597d\u662f\u5c06\u4ed6\u6d3b\u64d2\uff0c\u4ea4\u7ed9\u8428\u603b\u7ba1\u5ba1\u95ee\u3002\u201d\u6b64\u65f6\u5e05 \u5b5f\u96c4\u867d\u7136\u9887\u5360\u4e0a\u98ce\uff0c\u4f46\u4e5f\u8fd8\u5948\u4f55\u4e0d\u4e86\u674e\u5357\u661f\u3002 \u3000\u3000\u53f2\u767d\u90fd\u60a3\u5f97\u60a3\u5931\uff0c\u65e2\u6015\u5e05\u5b5f\u96c4\u64d2\u5730\u4e0d\u4e86\u7ed9\u4ed6\u6e9c\u8d70\uff0c\u53c8\u6015\u5e05\u5b5f\u96c4\u628a\u4ed6\u6253\u6b7b\uff0c\u5c06\u81ea\u5df1\u8981\u7559\u6d3b \u53e3\u7684\u8ba1\u5212\u6bc1\u6389\uff0c\u4e8e\u662f\u628a\u8428\u603b\u7ba1\u7684\u62db\u724c\u6258\u51fa\u6765\uff0c\u8ddf\u7740\u7acb\u5373\u51fa\u624b\uff0c\u60f3\u7528\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\uff0c\u5148 \u6293\u7740\u4e86\u674e\u5357\u661f\u518d\u8bf4\u3002\u514d\u5f97\u5e05\u76c2\u96c4\u5f53\u771f\u6253\u6b7b\u4e86\u4ed6\u3002 \u3000\u3000\u674e\u5357\u661f\u600e\u7981\u5f97\u4e24\u5927\u9ad8\u624b\u7684\u5939\u653b\uff1f\u773c\u770b\u53f2\u767d\u90fd\u8fd9\u6293\u4e00\u6293\u4e0b\uff0c\u5c31\u8981\u6293\u88c2\u4ed6\u7684\u7435\u7436\u9aa8\uff0c\u5ffd\u542c\u5f97 \u201c\u5530\u201d\u7684\u4e00\u58f0\uff0c\u53f2\u7ea2\u82f1\u7684\u94f6\u97ad\u6253\u4e0b\uff0c\u8fd9\u4e00\u97ad\u6253\u5f97\u6070\u5230\u597d\u5904\uff0c\u62e6\u4f4f\u4e86\u5979\u7684\u54e5\u54e5\u3002 \u3000\u3000\u53f2\u767d\u90fd\u4e00\u6293\u6293\u7a7a\uff0c\u53f2\u7ea2\u82f1\u65e9\u5df2\u821e\u8d77\u94f6\u97ad\uff0c\u9694\u5728\u674e\u5357\u661f\u4e0e\u5979\u54e5\u54e5\u4e4b\u95f4\uff0c\u8bf4\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60 \u662f\u4e00\u5e2e\u4e4b\u4e3b\uff0c\u5c82\u80fd\u4e0d\u987e\u8eab\u4efd\uff0c\u4ee5\u5927\u6b3a\u5c0f\uff0c\u4ee5\u4f17\u51cc\u5be1\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u559d\u9053\uff1a\u201c\u4f60\u773c\u4e2d\u8fd8\u6709\u6211\u8fd9\u4e2a\u54e5\u54e5\u5417\uff1f\u54fc\uff0c\u4ed6\u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff0c\u8981\u4f60 \u8fd9\u6837\u5e2e\u4ed6\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4fd7\u8bed\u8bf4\u8ba4\u7406\u4e0d\u8ba4\u4eb2\uff0c\u9791\u5b50\u6b3a\u8d1f\u4e86\u54b1\u4eec\u6c49\u4eba\u4e00\u767e\u591a\u5e74\uff0c\u54e5\u54e5\uff0c\u96be\u9053\u4f60\u8fd8\u8981\u52a9 \u7ea3\u4e3a\u8650\uff1f\u4f60\u82e5\u52a9\u7ea3\u4e3a\u8650\uff0c\u6211\u5c31\u975e\u8981\u5e2e\u4ed6\u4e0d\u53ef\u4e86\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u6ee1\u9762\u901a\u7ea2\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e2b\u5934\u6559\u8bad\u8d77\u6211\u6765\u4e86\uff01\u56fd\u5bb6\u5927\u4e8b\uff0c\u4f60\u8fd9\u9ec4\u6bdb\u4e2b\u5934\u61c2\u5f97\u4ec0 \u4e48\uff1f\u4f60\u8ddf\u6211\u56de\u53bb\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4f60\u7b54\u5e94\u4e0d\u505a\u671d\u5ef7\u7684\u9e70\u72ac\u6211\u5c31\u8ddf\u4f60\u56de\u53bb\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u4e8b\uff0c\u7528\u4e0d\u7740\u4f60\u7ba1\uff01\u54fc\uff0c\u4f60\u5982\u4eca\u7fc5\u8180\u786c\u4e86\uff0c\u8981\u60f3\u98de\u4e86\u662f\u4e0d\u662f\uff1f \u6211\u4e14\u770b\u4f60\u98de\u4e0d\u98de\u5f97\u51fa\u6211\u7684\u638c\u5fc3\uff01\u201d\u53f2\u767d\u90fd\u751f\u6015\u59b9\u59b9\u8bf4\u51fa\u66f4\u4e0d\u4e2d\u542c\u7684\u8bdd\u6765\uff0c\u8bdd\u4e00\u672a\u8bf4\u5b8c\uff0c\u4e00\u638c \u5c31\u8361\u5f00\u4e86\u53f2\u7ea2\u82f1\u7684\u94f6\u97ad\uff0c\u4f7f\u51fa\u7a7a\u624b\u5165\u767d\u5203\u7684\u529f\u592b\u6765\u6293\u4ed6\u7684\u59b9\u59b9\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u6124\u7136\u8bf4\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u662f\u4f60\u903c\u5f97\u6211\u548c\u4f60\u52a8\u624b\u7684\u3002\u201d\u94f6\u97ad\u4e00\u6296\uff0c\u77eb\u82e5\u6e38\u9f99\uff0c\u53f2\u767d\u90fd\u6293 \u4e0d\u7740\u97ad\u5934\uff0c\u9669\u4e9b\u7ed9\u97ad\u68a2\u7f20\u4e0a\u624b\u8155\u3002 \u3000\u3000\u53f2\u767d\u90fd\u6012\u9053\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\uff01\u597d\u5440\uff0c\u4f60\u4e0d\u670d\u6211\u7684\u7ba1\u6559\uff0c\u6211\u53ea\u80fd\u7528\u5bb6\u6cd5\u60e9\u6cbb\u4f60\uff01\u201d\u638c\u529b\u50ac \u7d27\uff0c\u53cc\u638c\u76d8\u65cb\u98de\u821e\uff0c\u767b\u65f6\u628a\u53f2\u7ea2\u82f1\u6253\u5f97\u624b\u5fd9\u811a\u4e71\u3002\u53f2\u7ea2\u82f1\u7684\u529f\u592b\u662f\u54e5\u54e5\u6559\u7684\uff0c\u5979\u6240\u4f7f\u7684\u97ad\u6cd5 \u6bcf\u4e00\u62db\u6bcf\u4e00\u5f0f\u90fd\u5728\u5979\u54e5\u54e5\u610f\u6599\u4e4b\u4e2d\u3002 \u3000\u3000\u5e78\u800c\u53f2\u767d\u90fd\u6ca1\u6709\u4f7f\u7528\u5175\u5668\uff0c\u8981\u7a7a\u624b\u593a\u97ad\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e5f\u8fd8\u4e0d\u6613\uff1b\u4e8c\u6765\u53f2\u7ea2\u82f1\u8eab\u6cd5\u8f7b\u7075\uff0c \u5584\u4e8e\u95ea\u907f\u9669\u62db\uff1b\u4e09\u6765\u53f2\u767d\u90fd\u4e5f\u4e0d\u6562\u5f53\u771f\u5c31\u4f24\u4e86\u59b9\u59b9\u3002\u6545\u6b64\u53f2\u7ea2\u82f1\u8fd8\u53ef\u4ee5\u52c9\u5f3a\u652f\u6301\uff0c\u4e0d\u8fc7\uff0c\u5982 \u5df2\u662f\u4e0d\u80fd\u5206\u5fc3\u8bf4\u8bdd\u4e86 \u3000\u3000\u53f2\u767d\u90fd\u5144\u59b9\u76f8\u6597\uff0c\u6b63\u5408\u5e05\u5b5f\u96c4\u7684\u5fc3\u610f\u3002\u8981\u77e5\u5e05\u5b5f\u96c4\u8fd9\u6b21\u6765\u4eac\u3002\u5728\u516c\u4e8b\u4e0a\u662f\u5411\u671d\u5ef7\u7980\u62a5\u519b \u60c5\uff0c\u5728\u79c1\u4e8b\u4e0a\u5219\u662f\u60f3\u5411\u53f2\u5bb6\u6c42\u5a5a\u7684\u3002\u5e05\u5b5f\u96c4\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5e78\u4e8f\u6709\u53f2\u767d\u90fd\u5bf9\u4ed8\u4ed6\u7684\u59b9\u59b9\uff0c\u8981\u4e0d \u7136\u5012\u662f\u6559\u6211\u96be\u505a\u4eba\u4e86\u3002\u201d\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\u4e0d\u5fc5\u751f\u6c14\uff0c\u4ee4\u59b9\u5e74\u7eaa\u8fd8\u8f7b\uff0c\u4e00\u65f6\u7ed9\u5996\u8a00\u6240\u60d1\uff0c \u8fd9\u4e5f\u4e0d\u8db3\u4e3a\u602a\u3002\u8428\u603b\u7ba1\u9762\u524d\uff0c\u6211\u66ff\u4f60\u906e\u7792\u5c31\u662f\uff0c\u4f60\u53ef\u4e0d\u8981\u4f24\u5979\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u591a\u8c22\u5c06\u519b\u7167\u987e\u3002\u8fd9\u4e2a\u5c0f\u5b50\uff0c\u4e5f\u8bf7\u4f60\u7559\u4ed6\u4e00\u7559\u3002\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u7b11\u9053\uff1a\u201c\u77e5\u9053\u4e86\uff0c\u6211\u81ea\u4f1a\u5bf9\u4ed8\u4ed6\u7684\u3002\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u8fc7\u60f3\u7559\u4e0b\u4e00\u4e2a\u6d3b\u53e3\u76d8 \u95ee\u53e3\u4f9b\uff0c\u6211\u628a\u4ed6\u6253\u6210\u6b8b\u5e9f\uff0c\u5c31\u53ea\u7559\u4e0b\u4ed6\u7684\u4e00\u5f20\u5634\uff0c\u8ba9\u4ed6\u53ef\u4ee5\u8bf4\u8bdd\u4e5f\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u7684\u529f\u529b\u548c\u4e34\u654c\u7684\u7ecf\u9a8c\u90fd\u5728\u674e\u6d77\u661f\u4e4b\u4e0a\uff0c\u6240\u4ee5\u4e0b\u4e86\u8fd9\u4e2a\u72e0\u6bd2\u7684\u4e3b\u610f\uff0c\u51fa\u624b\u7684\u6bcf\u4e00\u62db \u90fd\u662f\u4f24\u6b8b\u7684\u624b\u6cd5\uff0c\u674e\u5357\u661f\u5168\u795e\u5e94\u6218\uff0c\u81ea\u5df1\u8fd8\u4e0d\u77e5\u9053\u5bb3\u6015\uff0c\u53f2\u7ea2\u82f1\u770b\u4e86\u5374\u662f\u6697\u6697\u5fc3\u60ca\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u5e05\u5b5f\u96c4\u53cc\u638c\u4e00\u5206\uff0c\u638c\u52bf\u98d8\u5ffd\u4e0d\u5b9a\uff0c\u731b\u5730\u559d\u58f0\u201c\u7740\uff01\u201d\u638c\u98ce\u4eba\u5f71\u4e4b\u4e2d\uff0c\u53ea\u89c1\u674e\u5357\u661f \u811a\u6b65\u4e00\u4e2a\u8dc4\u8ddf\uff0c\u5012\u7eb5\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u5e05\u76c2\u96c4\u7684\u8863\u895f\u5374\u7a7f\u4e86\u4e09\u4e2a\u6d1e\u3002\u539f\u6765\u674e\u5357\u661f\u7ed9\u4ed6\u7684\u638c\u950b\u626b\u4e86 \u4e00\u4e0b\uff0c\u4f46\u4ed6\u7684\u201c\u8ffd\u98ce\u5251\u5f0f\u201d\u5feb\u6377\u65e0\u6bd4\uff0c\u5e05\u5b5f\u96c4\u6b3a\u5230\u4ed6\u7684\u8eab\u524d\u4e5f\u9669\u4e9b\u7ed9\u4ed6\u6240\u4f24\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u8863\u895f\u7a7f\u6d1e\u53f2\u7ea2\u82f1\u770b\u4e0d\u89c1\uff0c\u5979\u53ea\u770b\u89c1\u674e\u5357\u661f\u7ed9\u5e05\u5b5f\u96c4\u51fb\u9000\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\u3002\u8bf4\u65f6 \u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c31\u5728\u5979\u4e00\u60ca\u4e4b\u9645\uff0c\u97ad\u68a2\u5df2\u662f\u7ed9\u53f2\u767d\u90fd\u6293\u7740\u3002 \u3000\u3000\u674e\u5357\u661f\u53eb\u9053\uff1a\u201c\u6211\u6ca1\u53d7\u4f24\uff0c\u4f60\u522b\u62c5\u5fc3\uff01\u201d\u53f2\u7ea2\u82f1\u5fc3\u4e2d\u4e00\u5bbd\uff0c\u62d4\u51fa\u4e00\u67c4\u5315\u9996\uff0c\u7acb\u5373\u622a\u65ad\u4e00 \u6bb5\u97ad\u68a2\uff0c\u8131\u51fa\u4e86\u54e5\u54e5\u7684\u638c\u63e1\u3002\u53f2\u767d\u90fd\u773c\u770b\u5c31\u53ef\u6293\u7740\u59b9\u59b9\uff0c\u4e0d\u6599\u5374\u7ed9\u5979\u7528\u8fd9\u4e2a\u6cd5\u5b50\u9003\u8131\uff0c\u4e0d\u7531 \u5f97\u5927\u6012\u8bf4\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4e00\u5fc3\u5411\u7740\u5916\u4eba\uff0c\u6211\u8fd8\u8981\u4f60\u8fd9\u59b9\u59b9\u4f55\u7528\uff1f\u8fd9\u5c0f\u5b50\u6ca1\u4f24\uff0c\u6211\u5148\u53eb\u4f60\u53d7 \u4f24\uff0c\u4f24\u4e86\u4f60\u6211\u518d\u7ed9\u4f60\u533b\u597d\u3002\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u53eb\u9053\uff1a\u201c\u4e0d\u53ef\u3001\u4e0d\u53ef\uff01\u770b\u5728\u6211\u7684\u4efd\u4e0a\uff0c\u53ef\u522b\u4f24\u4e86\u4f60\u7684\u59b9\u59b9\uff01\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u8eab\u5f62\u5df2 \u662f\u5411\u7740\u674e\u5357\u661f\u518d\u5ea6\u6251\u53bb\u3002\u672c\u6765\uff0c\u4ed6\u7684\u8863\u895f\u7ed9\u674e\u5357\u661f\u523a\u7a7f\u4e86\u4e09\u4e2a\u6d1e\uff0c\u5fc3\u91cc\u4e5f\u672a\u5c1d\u6ca1\u6709\u51e0\u5206\u602f \u610f\uff0c\u4f46\u542c\u4e86\u53f2\u767d\u90fd\u8bf4\u4ed6\u59b9\u59b9\u201c\u504f\u5411\u5916\u4eba\u201d\u7684\u8bf4\u8bdd\uff0c\u4e0d\u7531\u5f97\u5992\u706b\u5982\u711a\uff0c\u4e5f\u5c31\u987e\u4e0d\u4e86\u90a3\u8bb8\u591a\u4e86\u3002 \u3000\u3000\u674e\u5357\u661f\u5176\u5b9e\u5df2\u662f\u53d7\u4e86\u4e00\u70b9\u4f24\u7684\uff0c\u4e0d\u8fc7\u662f\u4e0d\u60f3\u53f2\u7ea2\u82f1\u4e3a\u4ed6\u62c5\u5fe7\u800c\u5df2\u3002\u6b64\u65f6\u4ed6\u89c1\u5e05\u5b5f\u96c4\u72e0\u72e0 \u6251\u6765\uff0c\u5fc3\u4e2d\u4e5f\u662f\u6c14\u6124\u4ea4\u52a0\uff0c\u60f3\u9053\uff1a\u201c\u4f60\u8fd9\u766b\u86e4\u87c6\u60f3\u5403\u5929\u9e45\u8089\uff0c\u6211\u5c31\u662f\u62fc\u4e86\u4e00\u6761\u6027\u547d\uff0c\u4e5f\u51b3\u4e0d \u80fd\u8ba9\u4f60\u5982\u613f\uff01\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u53cc\u638c\u9f50\u51fa\uff0c\u5e38\u529b\u5c3d\u53d1\uff0c\u5fc3\u4e2d\u76d8\u7b97\u674e\u5357\u661f\u4e00\u5b9a\u4e0d\u6562\u786c\u63a5\uff0c\u53ea\u8981\u4ed6\u4e00\u9000\u518d\u8ffd\u4e4b\u540e\uff0c\u81ea \u5df1\u5c31\u53ef\u4ee5\u5b8c\u5168\u63a7\u5236\u5c40\u52bf\uff0c\u53eb\u4ed6\u7684\u5251\u6cd5\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u54ea\u77e5\u674e\u5357\u661f\u7adf\u7136\u4e0d\u9000\u4e0d\u95ea\uff0c\u5251\u4e2d\u5939\u638c\uff0c\u53cd\u800c\u62a2\u4e0a\u53bb\u548c\u4ed6\u5bf9\u653b\u3002\u5e05\u5b5f\u96c4\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\uff0c \u6211\u5c31\u548c\u4f60\u6bd4\u6bd4\u638c\u529b\uff01\u201d\u638c\u98ce\u8361\u5f00\u4e86\u674e\u5357\u661f\u7684\u5251\u5c16\uff0c\u53cc\u638c\u76f8\u4ea4\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u674e\u5357\u661f\u8eab\u5f62\u62d4 \u8d77\uff0c\u4e00\u4e2a\u5012\u5934\u7b4b\u6597\u7ffb\u51fa\u6570\u4e08\u5f00\u5916\u3002\u8fd9\u4e00\u77ac\u95f4\uff0c\u674e\u5357\u661f\u53ea\u89c9\u5730\u8f6c\u5929\u65cb\uff0c\u5589\u5499\u53d1\u8165\uff0c\u674e\u5357\u661f\u786c\u633a \u4e0b\u6765\uff0c\u628a\u4e00\u53e3\u8840\u541e\u4e0b\u809a\u4e2d\uff0c\u4e0d\u8ba9\u5b83\u5410\u51fa\uff0c\u54c8\u54c8\u7b11\u9053\uff0c\u201c\u518d\u6765\uff0c\u518d\u6765\uff01\u770b\u770b\u662f\u8c01\u80fd\u591f\u6253\u5230\u6700 \u540e\uff01\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u521a\u521a\u4e00\u4e3e\u51fb\u9000\u4e86\u674e\u5357\u661f\uff0c\u9661\u7136\u611f\u89c9\u638c\u5fc3\u4e00\u9ebb\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u638c\u5fc3\u6709\u4e2a\u5c0f\u5c0f\u7684\u9488 \u5b54\uff0c\u9488\u5b54\u4e2d\u5df2\u6c81\u51fa\u9ed1\u8272\u7684\u8840\u6db2\u3002\u539f\u6765\u674e\u5357\u661f\u4e0e\u4ed6\u5bf9\u638c\u4e4b\u9645\uff0c\u638c\u5fc3\u4e2d\u662f\u6263\u7740\u4e00\u53e3\u6dec\u8fc7\u5267\u6bd2\u7684\u6885 \u82b1\u9488\u7684\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u60f3\u4e0d\u5230\u674e\u5357\u661f\u7adf\u6562\u7528\u4e86\u8fd9\u4e2a\u201c\u4e24\u8d25\u4ff1\u4f24\u201d\u7684\u624b\u6bb5\u5bf9\u4ed8\u4ed6\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u8fde\u5fd9\u8fd0\u6c14\u62a4 \u7740\u5fc3\u623f\uff0c\u8ffd\u4e0a\u524d\u53bb\uff0c\u559d\u9053\uff1a\u201c\u6211\u975e\u6bd9\u4e86\u4f60\u5c0f\u5b50\u4e0d\u53ef\uff01\u53f2\u5e2e\u4e3b\uff0c\u4e0d\u662f\u6211\u4e0d\u4e70\u4f60\u7684\u9762\u5b50\uff0c\u8fd9\u5c0f\u5b50 \u5b9e\u5728\u592a\u8fc7\u53ef\u6076\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u662f\u4e2a\u6b66\u5b66\u5927\u884c\u5bb6\uff0c\u674e\u5357\u661f\u7528\u6bd2\u9488\u6697\u7b97\u5e05\u5b5f\u96c4\u7684\u624b\u6cd5\u867d\u7136\u5341\u5206\u5de7\u5999\uff0c\u5374\u4e5f\u7792\u4e0d\u8fc7\u4ed6 \u7684\u773c\u775b\u3002\u53f2\u767d\u90fd\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u7684\u4f7f\u6bd2\u672c\u9886\u5982\u6b64\u9ad8\u660e\uff0c\u4e00\u5b9a\u662f\u548c\u5929\u9b54\u6559\u5927\u6709\u5173\u7cfb\u3002\u5e05\u5b5f \u96c4\u82e5\u662f\u6bd2\u53d1\u5728\u524d\uff0c\u6211\u4e00\u4e2a\u4eba\u53ef\u662f\u963b\u6b62\u4e0d\u4e86\u4ed6\u4eec\u9003\u8dd1\u3002\u6ca1\u5948\u4f55\uff0c\u5c31\u53ea\u597d\u8ba9\u5e05\u76c2\u96c4\u6bd9\u4e86\u8fd9\u4e2a\u5c0f\u5b50 \u5427\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u5374\u4e0d\u77e5\u9053\u5e05\u5b5f\u96c4\u4e2d\u4e86\u6bd2\u9488\uff0c\u89c1\u5e05\u5b5f\u96c4\u72c2\u653b\u731b\u6251\uff0c\u674e\u5357\u661f\u7ed9\u4ed6\u903c\u5f97\u6b65\u6b65\u540e\u9053\uff0c\u663e\u7136 \u5df2\u662f\u62db\u67b6\u4e0d\u4f4f\uff0c\u4e0d\u7531\u5f97\u5927\u4e3a\u7740\u6025\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ed6\u662f\u91d1\u9010\u6d41\u7684\u4e49\u5144\uff0c\u6211\u53c8\u53d7\u8fc7\u4ed6\u7684\u4eba\u60c5\uff0c\u5982 \u4eca\u6ca1\u6cd5\u6551\u4ed6\uff0c\u8fd9\u53ef\u5982\u4f55\u662f\u597d\uff1f\u201d \u3000\u3000\u6b63\u6240\u8c13\u201c\u798f\u65e0\u53cc\u81f3\uff0c\u7978\u4e0d\u5355\u884c\u201d\uff0c\u53f2\u7ea2\u82f1\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u9a6c\u94c3\u58f0\u54cd\uff0c\u4eba\u8bed\u55a7\u54d7\uff0c\u539f\u6765 \u662f\u6587\u9053\u5e84\u3001\u6c99\u5343\u5cf0\u548c\u516d\u5408\u5e2e\u7684\u56db\u5927\u5e2e\u4e3b\u5168\u90fd\u6765\u4e86\uff01 \u3000\u3000\u6587\u9053\u5e84\u8ba4\u8bc6\u5e05\u76c2\u96c4\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5e05\u5c06\u519b\uff0c\u4f60\u4e5f\u6765\u4e86\uff1f\u5272\u9e21\u7109\u7528\u725b\u5200\uff0c\u5f85\u6211\u6765\u7ed9\u4f60\u6253\u53d1 \u8fd9\u5c0f\u5b50\u5427\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u4eba\u6025\u667a\u751f\uff0c\u5ffd\u5730\u8dc3\u51fa\u5708\u5b50\uff0c\u628a\u90a3\u67c4\u5315\u9996\u6307\u7740\u81ea\u5df1\u7684\u54bd\u5589\uff0c\u53eb\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u4eec\u82e5 \u662f\u5b9a\u8981\u6740\u4ed6\uff0c\u6211\u5c31\u6b7b\u5728\u4f60\u7684\u9762\u524d\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u5e05\u5c06\u519b\uff0c\u6b64\u4e8b\u5982\u4f55\uff0c\u8bf7\u4f60\u4f5c\u4e3b\uff01\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u89c1\u53f2\u7ea2\u82f1\u4e3a\u4e86\u8fd9\u4e2a\u201c\u5c0f\u5b50\u201d\u7adf\u7136\u4e0d\u60dc\u4ee5\u6b7b\u76f8\u80c1\uff0c\u5fc3\u4e2d\u66f4\u662f\u5992\u706b\u5982\u711a\uff0c\u53ef\u662f\u4ed6\u53c8\u4e0d \u613f\u610f\u53f2\u7ea2\u82f1\u6b7b\u53bb\uff0c\u4e8e\u662f\u53eb\u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u6709\u8bdd\u6162\u8bf4\uff0c\u54b1\u4eec\u603b\u53ef\u4ee5\u597d\u597d\u5546\u91cf\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u5411\u4f60\u6c42\u60c5\uff0c\u4f60\u8981\u65a9\u5c3d\u6740\u7edd\uff0c\u6211\u81ea\u5df1\u52a8\u624b\uff0c\u7701\u4e86\u4f60\u7684\u6c14\u529b\uff0c\u90a3\u8fd8\u4e0d \u597d\uff1f\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u82e6\u7b11\u9053\uff1a\u201c\u4f55\u5fc5\u51fa\u6b64\u4e0b\u7b56\uff0c\u6211\u4f9d\u4f60\u7684\u8bdd\uff0c\u653e\u4e86\u8fd9\u5c0f\u5b50\u5982\u4f55\uff1f\u4e0d\u8fc7\uff0c\u4f60\u4e5f\u603b \u5f97\u2026\u2026\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u65a5\u9053\uff1a\u201c\u6211\u4e0d\u548c\u4f60\u8bf4\u8bdd\uff0c\u4f60\u4e5f\u4f11\u60f3\u6211\u7b54\u5141\u4f60\u4ec0\u4e48\u3002\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u7709\u5934\u4e00\u76b1\uff0c\u773c\u9732\u51f6\u5149\u5077\u5730\u53c8\u8d77\u4e86\u6740\u673a\u3002\u53ef\u662f\u4ed6\u6bd5\u7adf\u5fc3\u6709\u4e0d\u7518\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u4f60\u4e0d \u80af\u4f9d\u4ece\u6211\uff0c\u6211\u504f\u8981\u5a36\u4f60\uff0c\u53ea\u8981\u4f60\u6d3b\u4e0b\u6765\uff0c\u6211\u603b\u6709\u529e\u6cd5\u628a\u4f60\u9a97\u5230\u624b\u4e2d\u3002\u81f3\u4e8e\u8fd9\u4e2a\u5c0f\u5b50\uff0c\u53cd\u6b63\u4ed6 \u5df2\u53d7\u4e86\u91cd\u4f24\uff0c\u5c31\u6682\u4e14\u7531\u4ed6\u53bb\u5427\u3002\u201d \u3000\u3000\u4e3b\u610f\u6253\u5b9a\uff0c\u5e05\u5b5f\u96c4\u6545\u4f5c\u5927\u65b9\uff0c\u7b11\u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u6211\u662f\u4e0d\u4f1a\u548c\u4f60\u4e3a\u96be\u7684\uff0c\u4f60\u8981\u600e\u4e48\u6837\u5c31\u600e \u4e48\u6837\u3002\u4e0d\u8fc7\uff0c\u8fd9\u8fd8\u5f97\u7531\u4f60\u54e5\u54e5\u4f5c\u4e3b\u3002\u201d\u8f7b\u8f7b\u4e00\u63a8\uff0c\u53c8\u628a\u8fd9\u4ef6\u4e8b\u60c5\u63a8\u5230\u4e86\u53f2\u767d\u90fd\u8eab\u4e0a\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u8bf4\u4e86\u8fd9\u8bdd\uff0c\u4fbf\u5373\u9000\u4e86\u4e0b\u6765\uff0c\u4ed6\u5411\u6587\u9053\u5e84\u629b\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u8ba9\u6587\u9053\u5e84\u76d1\u89c6\u7740\u674e\u5357\u661f\u3002 \u539f\u6765\u5e05\u5b5f\u96c4\u6b64\u65f6\u4e5f\u5fc5\u987b\u8fd0\u529f\u62b5\u5fa1\u6bd2\u6c14\u7684\u4e0a\u4fb5\uff0c\u521a\u624d\u540e\u63f4\u672a\u5230\uff0c\u4ed6\u662f\u975e\u62fc\u547d\u4e0d\u53ef\uff0c\u73b0\u5728\u5219\u662f\u4e0d \u60f3\u62fc\u547d\u4e86\u3002\u4ed6\u4e4b\u613f\u610f\u653e\u8fc7\u674e\u5357\u661f\uff0c\u56fa\u7136\u662f\u4e3a\u4e86\u8981\u5411\u53f2\u7ea2\u82f1\u793a\u601d\uff0c\u4f46\u53e6\u5916\u7684\u4e00\u534a\u539f\u56e0\uff0c\u5374\u4e5f\u662f \u4e3a\u4e86\u81ea\u5df1\u8981\u6025\u4e8e\u7597\u6bd2\u7684\u3002 \u3000\u3000\u53f2\u767d\u90fd\u5df2\u7ecf\u5b8c\u5168\u61c2\u5f97\u4e86\u5e05\u5b5f\u96c4\u7684\u610f\u601d\uff0c\u5f53\u4e0b\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4e2b\u5934\u53ea\u77e5\u80f3\u818a\u5411 \u5916\u5f2f\uff0c\u6211\u53ef\u4e0d\u80fd\u4f9d\u4f60\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u51c4\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u5168\u65e0\u5144\u59b9\u4e4b\u60c5\uff0c\u6211\u53c8\u4f55\u5fc5\u6d3b\u5728\u4f60\u7684\u9762\u524d\u788d\u4f60\u7684\u773c\uff1f\u201d\u950b \u5229\u7684\u5315\u9996\u8d34\u7740\u5589\u5499\uff0c\u8bdd\u72b9\u672a\u4e86\uff0c\u76ae\u80a4\u5df2\u7ecf\u7a0d\u5fae\u5212\u7834\uff0c\u6ccc\u51fa\u4e86\u8840\u4e1d\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u53f2\u5927\u54e5\uff0c\u4ee4\u59b9\u5e74\u8f7b\u8bc6\u6d45\uff0c\u4f60\u597d\u597d\u7ba1\u6559\u5979\u4e5f\u5c31\u662f\u4e86\uff0c\u53ef\u4e0d\u8981\u903c\u5979\u5bfb\u6b7b \u5440\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u8d81\u52bf\u6536\u7bf7\uff0c\u8bf4\u9053\uff1a\u201c\u5509\uff0c\u4f60\u8fd9\u4e2b\u5934\uff0c\u6211\u771f\u662f\u8981\u7ed9\u4f60\u6d3b\u6d3b\u6c14\u6b7b\u4e86\u3002\u597d\u5566\uff0c\u4f60\u8ddf\u6211\u56de \u53bb\uff0c\u6211\u5c31\u4f9d\u4f60\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u77e5\u9053\u82e5\u662f\u4e0d\u4f9d\u4ece\u54e5\u54e5\u7684\u6761\u4ef6\uff0c\u4ed6\u4eec\u662f\u51b3\u4e0d\u4f1a\u653e\u8d70\u674e\u5357\u661f\u7684\u3002\u53f2\u7ea2\u82f1\u6697\u81ea\u5bfb\u601d\uff1a \u201c\u6211\u56de\u5bb6\u4e4b\u540e\uff0c\u8fd8\u53ef\u4ee5\u627e\u673a\u4f1a\u518d\u9003\u3002\u674e\u5927\u54e5\u53d7\u4e86\u91cd\u4f24\uff0c\u5374\u662f\u8d8a\u65e9\u8131\u51fa\u9b54\u638c\u8d8a\u597d\uff01\u4e8e\u662f\u8bf4\u9053\uff1a \u201c\u597d\uff0c\u4f60\u4eec\u9001\u4e00\u5339\u9a6c\u7ed9\u6211\u8fd9\u4f4d\u670b\u53cb\uff0c\u5f85\u4ed6\u8d70\u5f97\u770b\u4e0d\u89c1\u4e86\uff0c\u6211\u5c31\u653e\u4e0b\u5315\u9996\uff0c\u8ddf\u4f60\u56de\u5bb6\u53bb\u3002\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u5fc3\u91cc\u9178\u6e9c\u6e9c\u7684\uff0c\u8138\u4e0a\u5374\u4e0d\u80fd\u4e0d\u88c5\u51fa\u6ee1\u4e0d\u5728\u4e4e\u7684\u795e\u8272\u7b11\u9053\uff1a\u201c\u6210\uff0c\u6210\uff01\u4fd7\u8bed\u8bf4\u4e0d\u6253 \u4e0d\u76f8\u8bc6\uff0c\u8fd9\u4f4d\u670b\u53cb\u6b66\u827a\u9ad8\u5f3a\uff0c\u6211\u4e5f\u4e50\u610f\u591a\u7ed3\u4ea4\u4e00\u4f4d\u670b\u53cb\u3002\u6587\u5c9b\u4e3b\u5c31\u8bf7\u4f60\u628a\u4f60\u8fd9\u5339\u5750\u9a91\u8fed\u7ed9\u4ed6 \u5427\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u8df3\u4e0b\u9a6c\u6765\uff0c\u4e00\u62cd\u9a6c\u81c0\uff0c\u628a\u90a3\u5339\u9a6c\u8d76\u5230\u674e\u5357\u661f\u8eab\u8fb9\uff0c\u6124\u7136\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u770b\u5728\u5e05\u5c06\u519b \u4efd\u4e0a\uff0c\u4fbf\u5b9c\u4e86\u4f60\u8fd9\u5c0f\u5b50\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u5374\u4e0d\u63a5\u8fc7\u9a6c\u7f30\uff0c\u8bf4\u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u8fd9\u3001\u8fd9\u592a\u59d4\u5c48\u4f60\u4e86\uff01\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u7ba1 \u6211\uff0c\u4f60\u5feb\u8d70\uff01\u7559\u5f97\u9752\u5c71\u5728\uff0c\u4e0d\u6015\u6ca1\u67f4\u70e7\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u662f\u4e2a\u611f\u60c5\u5bb9\u6613\u6fc0\u52a8\u7684\u4eba\uff0c\u542c\u4e86\u8fd9\u6d3b\uff0c\u4e0d\u7531\u5f97\u70ed\u6cea\u76c8\u7736\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5979\u4e3a\u4e86\u6551 \u6211\uff0c\u4e0d\u60dc\u5c48\u4ece\u5979\u7684\u54e5\u54e5\uff0c\u8fd9\u4efd\u539a\u610f\u6df1\u60c5\uff0c\u6211\u4e0d\u77e5\u5982\u4f55\u62a5\u7b54\uff1f\u5979\u8bf4\u5f97\u4e0d\u9519\uff0c\u7559\u5f97\u9752\u5c71\u5728\uff0c\u4e2a\u6015 \u6ca1\u67f4\u70e7\u3002\u6211\u5e94\u8be5\u8d76\u5feb\u517b\u597d\u4e86\u4f24\uff0c\u624d\u597d\u6551\u5979\u51fa\u6765\u3002\u201d\u674e\u5357\u661f\u6700\u521d\u672c\u6765\u8fd8\u662f\u4e0d\u80af\u629b\u4e0b\u53f2\u7ea2\u82f1\u72ec\u81ea \u8dd1\u7684\uff0c\u4f46\u542c\u4e86\u53f2\u7ea2\u82f1\u7684\u8fd9\u756a\u529d\u8bf4\u4e4b\u540e\uff0c\u60f3\u5230\u540c\u6b7b\u65e0\u76ca\uff0c\u4e5f\u5c31\u6539\u53d8\u4e86\u4e3b\u610f\u4e86\u3002 \u3000\u3000\u674e\u5357\u661f\u8de8\u4e0a\u9a6c\u80cc\uff0c\u8bf4\u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u4f60\u5584\u81ea\u4fdd\u91cd\uff0c\u6211\u53bb\u4e86\uff01\u59d3\u5e05\u7684\uff0c\u6211\u53ef\u4e0d\u9886\u4f60\u7684\u60c5\uff0c \u4f60\u522b\u4ee5\u4e3a\u4eca\u65e5\u653e\u4e86\u6211\uff0c\u6211\u5c31\u53ef\u4ee5\u548c\u4f60\u5316\u654c\u4e3a\u53cb\uff01\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u597d\u5014\u5f3a\u7684\u5c0f\u5b50\uff0c\u597d\uff0c\u6211\u7b49\u4f60\u517b\u597d\u4e86\u4f24\uff0c\u56de\u6765\u518d\u627e\u6211\u8f83\u91cf\u5c31\u662f\u3002\u4f60 \u672c\u6765\u4e0d\u5fc5\u9886\u6211\u7684\u60c5\uff0c\u6211\u8fd9\u4efd\u4eba\u60c5\u4e5f\u53ea\u662f\u9001\u7ed9\u53f2\u59d1\u5a18\u7684\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u6b64\u65f6\u5df2\u662f\u6709\u70b9\u652f\u6301\u4e0d\u4f4f\uff0c\u4e8e\u662f\u626c\u97ad\u7b56\u9a6c\u3001\u5982\u98de\u75be\u8dd1\u3002\u53f2\u7ea2\u82f1\u84e6\u5730\u60f3\u8d77\u4e00\u4e8b\uff0c\u626c\u58f0 \u53eb\u9053\uff1a\u201c\u4f60\u517b\u597d\u4e86\u4f24\uff0c\u8bf7\u4f60\u628a\u6211\u7684\u6d88\u606f\u544a\u8bc9\u91d1\u9010\u6d41\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u56e0\u89c1\u674e\u5357\u661f\u5df2\u7ecf\u98de\u9a70\u800c\u53bb\uff0c\u6212\u5907\u4e0d\u514d\u7a0d\u5fae\u677e\u61c8\u3002\u54ea\u77e5\u53f2\u767d\u90fd\u8d81\u7740\u5979\u8bf4\u8bdd\u5206\u795e\u4e4b \u9645\uff0c\u7a81\u7136\u5c31\u62a2\u4e86\u5979\u7684\u5315\u9996\uff0c\u628a\u90a3\u67c4\u5315\u9996\u5411\u674e\u5357\u661f\u63b7\u53bb\u3002\u53f2\u7ea2\u82f1\u5931\u58f0\u60ca\u547c\uff0c\u4f46\u201c\u554a\u5440\u201d\u4e24\u5b57\u521a \u521a\u53eb\u51fa\uff0c\u53f2\u767d\u90fd\u5df2\u662f\u70b9\u4e86\u5979\u7684\u7a74\u9053\u3002\u6b63\u662f\uff1a \u3000\u3000\u6e05\u8005\u81ea\u6e05\u6d4a\u81ea\u6d4a\uff0c\u867d\u7136\u5144\u59b9\u4e5f\u6210\u4ec7\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u94f8\u6210\u5b9d\u5251\u8fd8\u5fc3\u613f\u3000\u638c\u51fb\u6850\u68fa\u62a5\u5bbf\u4ec7 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u94f8\u6210\u5b9d\u5251\u8fd8\u5fc3\u613f\u3000\u638c\u51fb\u6850\u68fa\u62a5\u5bbf\u4ec7 \u3000\u3000\u674e\u5357\u661f\u53d7\u4f24\u751a\u91cd\uff0c\u9a91\u4e0a\u9a6c\u4e00\u8dd1\uff0c\u53ea\u89c9\u8eab\u75db\u5934\u6655\uff0c\u8033\u9e23\u5fc3\u8df3\u3002\u53f2\u7ea2\u82f1\u5728\u4ed6\u540e\u9762\u53eb\u558a\uff0c\u4ed6\u9690 \u9690\u5982\u6709\u542c\u95fb\uff0c\u53f2\u7ea2\u82f1\u8bf4\u7684\u662f\u4e9b\u4ec0\u4e48\uff0c\u4ed6\u5374\u662f\u542c\u4e0d\u6e05\u695a\u4e86\u3002 \u3000\u3000\u674e\u5357\u661f\u7eb5\u9a6c\u75be\u9a70\uff0c\u6b64\u65f6\u4ee5\u662f\u4e0a\u4e86\u5b98\u9053\uff0c\u53cc\u65b9\u7684\u8ddd\u79bb\u5728\u767e\u6b65\u5f00\u5916\u4e86\u3002\u4e00\u822c\u7684\u6697\u5668\u529f\u592b\u662f\u51b3 \u4e0d\u80fd\u6253\u5230\u8fd9\u4e48\u8fdc\u7684\uff0c\u4f46\u53f2\u767d\u90fd\u529f\u529b\u975e\u51e1\uff0c\u4ed6\u7528\u5c3d\u6d51\u8eab\u6c14\u529b\u63b7\u51fa\u90a3\u67c4\u5315\u9996\uff0c\u8131\u624b\u4fbf\u5316\u4f5c\u4e86\u4e00\u9053 \u94f6\u8679\uff0c\u7adf\u7136\u8ffd\u4e0a\u4e86\u674e\u5357\u661f\u8fd9\u7247\u75be\u9a70\u7684\u9a8f\u9a6c\u3002 \u3000\u3000\u674e\u6d77\u661f\u5e78\u800c\u662f\u9690\u9690\u542c\u5f97\u53f2\u7ea2\u82f1\u7684\u53eb\u56b7\uff0c\u4ed6\u56de\u5934\u4e00\u770b\uff0c\u6070\u597d\u8fd9\u67c4\u5315\u9996\u98de\u5230\u4ed6\u7684\u80cc\u540e\u3002\u674e\u5357 \u661f\u628a\u5251\u4e00\u62e8\uff0c\u5315\u9996\u6b6a\u8fc7\u4e00\u8fb9\uff0c\u4f59\u529b\u672a\u8870\uff0c\u201c\u5657\u201d\u7684\u63d2\u5165\u4e86\u9a6c\u80cc\u3002 \u3000\u3000\u8fd9\u5339\u5750\u9a91\u662f\u4e00\u5339\u4e45\u7ecf\u8bad\u7ec3\u7684\u6218\u9a6c\uff0c\u53d7\u4e86\u5315\u9996\u523a\u4f24\uff0c\u8d1f\u75db\u72c2\u5954\uff0c\u8f6c\u773c\u95f4\u5df2\u662f\u8dd1\u51fa\u53f2\u767d\u90fd\u89c6 \u91ce\u4e4b\u5916\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u7ed9\u54e5\u54e5\u70b9\u7740\u7684\u662f\u9ebb\u7a74\uff0c\u8eab\u4f53\u4e0d\u80fd\u52a8\u5f39\uff0c\u5374\u8fd8\u80fd\u591f\u8bf4\u8bdd\uff0c\u6b64\u65f6\u6c14\u5f97\u8bf4\u8bdd\u7684\u58f0\u97f3\u90fd\u98a4 \u6296\u4e86\uff0c\u201c\u54e5\u54e5\uff0c\u4e8f\u4f60\u662f\u4e00\u5e2e\u4e4b\u4e3b\uff0c\u4f60\u8fd9\u6837\u80cc\u540e\u4f24\u4eba\uff0c\u53ef\u8fd8\u8981\u4e0d\u8981\u8138\uff1f\u4f60\u73b0\u5728\u867d\u7136\u5236\u4f0f\u4e86\u627e\uff0c \u4f46\u4f60\u603b\u4e0d\u80fd\u6c38\u8fdc\u4e0d\u7ed9\u6211\u89e3\u5f00\u7a74\u9053\u3002\u597d\u5427\uff0c\u4f60\u82e5\u662f\u4e0d\u80af\u653e\u8fc7\u6211\u7684\u670b\u53cb\uff0c\u4f60\u5c3d\u53ef\u4ee5\u53bb\u8ffd\u6740\u4ed6\u3002\u6211 \u4eca\u5929\u6b7b\u4e0d\u4e86\u660e\u5929\u4e5f\u8fd8\u662f\u53ef\u4ee5\u6b7b\u7684\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u6697\u7b97\u4e0d\u6210\uff0c\u53cd\u800c\u7ed9\u59b9\u59b9\u8d23\u9a82\u4e86\u4e00\u987f\uff0c\u4e0d\u7531\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u5f3a\u8fa9\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u662f\u671d\u5ef7 \u53db\u9006\uff0c\u6211\u548c\u4ed6\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u89c4\u77e9\uff1f\u597d\u5427\uff0c\u4f60\u65e2\u7136\u5bfb\u6b7b\u89c5\u6d3b\u7684\u8981\u5e87\u62a4\u8fd9\u4e2a\u5c0f\u5b50\uff0c\u6211\u4eca\u5929\u653e\u8fc7\u4ed6\u4fbf \u662f\u3002\u4f46\u4ee5\u540e\u82e5\u662f\u78b0\u4e0a\u4e86\u4ed6\uff0c\u6211\u7edd\u4e0d\u80fd\u8f7b\u9976\u3002\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u4e5f\u8bf4\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\u3002\u5927\u4e08\u592b\u4e00\u8a00\u65e2\u51fa\uff0c\u9a77\u9a6c\u96be\u8ffd\u3002\u8fd9\u5c0f\u5b50\u867d\u662f\u53db\u9006\uff0c\u4f46\u54b1\u4eec\u7b54\u5e94 \u4e86\u653e\u4ed6\uff0c\u90a3\u5c31\u5e94\u8be5\u653e\u4ed6\u4e86\u3002\u53f2\u5e2e\u4e3b\uff0c\u4f60\u8fd8\u56de\u53bb\u89c1\u8428\u5927\u4eba\u4e48\u3002\u201d \u3000\u3000\u539f\u6765\u5e05\u5b5f\u96c4\u6b64\u65f6\u5df2\u662f\u6bd2\u53d1\uff0c\u5168\u4ed7\u7740\u5185\u529f\u6df1\u539a\u624d\u80fd\u652f\u6301\u7684\u3002\u4f46\u8fd0\u529f\u5fa1\u6bd2\uff0c\u7a76\u7adf\u4e0d\u662f\u6cbb\u672c\u4e4b \u6cd5\uff0c\u6240\u4ee5\u4ed6\u5fc5\u987b\u8d76\u5feb\u5165\u4eac\uff0c\u597d\u8bf7\u540d\u533b\u7597\u6cbb\u3002\u4ed6\u81ea\u5df1\u65e2\u7136\u4e0d\u80fd\u53bb\u9053\u6355\u674e\u5357\u661f\uff0c\u5f53\u7136\u4e50\u5f97\u5728\u53f2\u7ea2 \u82f1\u9762\u524d\u505a\u4e2a\u201c\u597d\u4eba\u201d\u3002 \u3000\u3000\u6587\u9053\u5e84\u548c\u6c99\u5343\u5cf0\u7b49\u4eba\u4e00\u6765\u662f\u56e0\u4e3a\u5e05\u5b5f\u96c4\u5df2\u7ecf\u7b54\u5e94\u653e\u4eba\uff0c\u4ed6\u4eec\u65e0\u8c13\u518d\u53bb\u4e89\u529d\uff1b\u4e8c\u6765\u4ed6\u4eec\u4e5f \u5bb3\u6015\u674e\u5357\u661f\u8fd8\u6709\u63a5\u5e94\uff0c\u548c\u674e\u5357\u661f\u4e00\u4f19\u7684\u5df2\u77e5\u7684\u4fbf\u6709\u5c09\u8fdf\u70af\u592b\u59bb\u548c\u91d1\u9010\u6d41\u7b49\u4eba\uff0c\u8fd9\u4e9b\u4eba\u90fd\u4e0d\u662f \u597d\u60f9\u7684\u3002\u867d\u7136\u4ed6\u4eec\u9003\u8dd1\u7684\u65b9\u5411\u4e0d\u540c\uff0c\u4f46\u4e5f\u8fd8\u662f\u53ef\u4ee5\u4f1a\u5408\u7684\u3002\u6587\u9053\u5e84\u6ca1\u6709\u53f2\u767d\u90fd\u7684\u5e2e\u5fd9\uff0c\u53ea\u6709 \u6c99\u5343\u5cf0\u548c\u4ed6\u4f5c\u4f34\uff0c\u4ed6\u53ef\u662f\u58ee\u4e0d\u8d77\u8138\u5b50\u4e86\uff1b\u4e09\u6765\u674e\u5357\u661f\u5df2\u7ecf\u8d70\u5f97\u8fdc\u4e86\uff0c\u4ed6\u4eec\u518d\u627e\u5750\u9a91\u53bb\u8ffd\uff0c\u4e5f \u672a\u5fc5\u8ffd\u8d76\u5f97\u4e0a\u3002\u56e0\u6b64\u4e5f\u5c31\u5b81\u53ef\u591a\u4e00\u4e8b\u4e0d\u5982\u5c11\u4e00\u4e8b\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u8fd9\u6b21\u4e0a\u4eac\uff0c\u8fde\u906d\u632b\u6298\uff0c\u81ea\u89c9\u989c\u9762\u65e0\u5149\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u60f3\u5e26\u8fd9\u4e2b\u5934\u56de\u5bb6\uff0c\u8428\u5927\u4eba \u90a3\u513f\uff0c\u6211\u4e0d\u53bb\u8f9e\u884c\u4e86\u3002\u8bf7\u5e05\u5c06\u519b\u4ee3\u8bf4\u4e00\u58f0\u5427\u3002\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u9053\uff1a\u201c\u6211\u786e\u70b9\u4e8b\u60c5\uff0c\u60f3\u8981\u548c\u4f60\u5546\u8bae\u3002\u53f2\u5927\u54e5\uff0c\u4f60\u53ef\u5426\u4e3a\u6211\u5728\u4eac\u4e2d\u591a\u7559\u51e0\u65e5\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u4e00\u6765\u662f\u56e0\u8fde\u906d\u632b\u6298\uff0c\u81ea\u89c9\u65e0\u989c\uff1b\u4e8c\u6765\u7531\u4e8e\u53f2\u7ea2\u82f1\u5728\u5bff\u5802\u7684\u8fd9\u4e00\u573a\u5927\u95f9\uff0c\u4e5f\u662f\u4ee4\u4ed6 \u8fdb\u9053\u4e3a\u96be\u3002\u8981\u77e5\u53f2\u7ea2\u82f1\u4eca\u65e5\u4e4b\u95f9\u5bff\u5802\uff0c\u662f\u516c\u7136\u548c\u4e49\u519b\u65b9\u9762\u7684\u5c09\u8fdf\u70af\u592b\u59bb\u7b49\u4eba\u5728\u4e00\u8d77\u7684\uff0c\u5373\u4f7f \u53f2\u767d\u90fd\u672c\u4eba\u53ef\u4ee5\u514d\u53d7\u8fde\u7d2f\uff0c\u4ed6\u4e5f\u6015\u8428\u798f\u9f0e\u8ffd\u7a76\u4ed6\u7684\u59b9\u59b9\uff0c\u7ec8\u4e8e\u81ea\u5df1\u4e5f\u662f\u8131\u4e0d\u4e86\u5173\u7cfb\u3002\u6709\u8fd9\u4e24 \u4e2a\u539f\u56e0\uff0c\u56e0\u6b64\u53f2\u767d\u90fd\u610f\u6b32\u5148\u907f\u4e00\u907f\u98ce\u5934\uff0c\u56de\u8f6c\u672c\u5e2e\u518d\u8bf4\u3002 \u3000\u3000\u4f46\u73b0\u5728\uff0c\u4ed6\u542c\u4e86\u5e05\u5b5f\u96c4\u7684\u8bf4\u8bdd\uff0c\u5fc3\u4e2d\u53c8\u6709\u70b9\u6d3b\u52a8\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5e05\u5b5f\u96c4\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u548c \u6211\u5546\u8bae\u5462\uff1f\u4e0d\u7528\u8bf4\uff0c\u4e00\u5b9a\u662f\u60f3\u4e0e\u6211\u5546\u8c08\u7ea2\u82f1\u7684\u5a5a\u4e8b\u7684\u4e86\u3002\u8fd9\u5934\u5a5a\u4e8b\u5018\u82e5\u6210\u529f\uff0c\u6709\u5e05\u5b5f\u96c4\u4f5c\u9760 \u5c71\u6211\u5012\u662f\u4e0d\u7528\u591a\u6240\u987e\u8651\u4e86\u3002\u4e0d\u8fc7\u8fd9\u4e2b\u5934\u7684\u813e\u6c14\u6267\u62d7\u5f97\u5f88\uff0c\u73b0\u5728\u6211\u8fd8\u672a\u77e5\u5979\u7a76\u7adf\u662f\u7231\u4e0a\u4e86\u91d1\u9010 \u6d41\u8fd9\u4e2a\u5c0f\u5b50\u8fd8\u662f\u7231\u4e0a\u4e86\u521a\u624d\u9003\u8dd1\u7684\u90a3\u4e2a\u5c0f\u5b50\uff0c\u4f46\u65e0\u8bba\u5982\u4f55\u5979\u4e00\u5b9a\u662f\u4e0d\u80af\u5ac1\u7ed9\u5e05\u5b5f\u96c4\u7684\u3002\u903c\u5f97 \u6025\u4e86\uff0c\u53ea\u6015\u5979\u53c8\u8981\u95f9\u51fa\u4e8b\u6765\u3002\u800c\u4e14\uff0c\u5728\u5a5a\u4e8b\u672a\u6210\u4e4b\u524d\uff0c\u4e5f\u96be\u4fdd\u6ca1\u6709\u4eba\u5728\u8428\u603b\u7ba1\u9762\u524d\u6311\u62e8\u662f \u975e\uff0c\u8981\u8ffd\u7a76\u4eca\u65e5\u4e4b\u4e8b\uff0c\u8fd9\u4e2b\u5934\u82e5\u662f\u7559\u5728\u4eac\u4e2d\uff0c\u603b\u662f\u4e0d\u4fbf\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u60f3\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u79bb\u5e2e\u65e5\u4e45\uff0c\u53ea\u6015\u5e2e\u4e2d\u6709\u4e8b\u8981\u6211\u6599\u7406\uff0c\u4f46\u65e2\u627f\u5c06\u519b\u76f8\u9080\uff0c\u53f2 \u67d0\u600e\u6562\u4e0d\u53d7\u62ac\u4e3e\uff1f\u8fd9\u6837\u5427\uff0c\u6211\u53eb\u624b\u4e0b\u548c\u820d\u59b9\u5148\u56de\u53bb\uff0c\u6211\u5728\u4eac\u4e2d\u7b49\u5019\u5c06\u519b\u516c\u4e8b\u5b8c\u6bd5\uff0c\u968f\u65f6\u53ec \u5524\u3002\u4e0d\u8fc7\uff0c\u8428\u5927\u4eba\u90a3\u513f\u8fd8\u662f\u8bf7\u5c06\u519b\u4ee3\u4e3a\u5148\u5bb9\uff0c\u6211\u624d\u597d\u518d\u53bb\u89c1\u4ed6\u3002\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u542c\u5f97\u53f2\u767d\u90fd\u8981\u628a\u59b9\u59b9\u5148\u9001\u56de\u53bb\uff0c\u5fc3\u91cc\u6709\u70b9\u4e0d\u5927\u613f\u610f\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u81ea\u5df1\u4e00\u8981\u6cbb\u6bd2 \u7597\u4f24\uff0c\u4e8c\u8981\u5411\u671d\u5ef7\u7980\u62a5\u519b\u60c5\u3002\u5728\u4e00\u4e2a\u6708\u5185\u662f\u51b3\u4e0d\u80fd\u529e\u7406\u5a5a\u4e8b\u7684\u3002\u800c\u4e14\u897f\u661f\u65b9\u9762\u7684\u5f62\u52bf\u5916\u5f1b\u5185 \u5f20\uff0c\u53ea\u6015\u516c\u4e8b\u4e00\u4e86\uff0c\u671d\u5ef7\u5c31\u8981\u50ac\u81ea\u5df1\u9a6c\u4e0a\u56de\u4efb\uff0c\u90a3\u6837\uff0c\u5a5a\u4e8b\u5c31\u66f4\u8981\u62d6\u8fdf\u4e86\u3002\u5e05\u5b5f\u96c4\u5fc3\u60f3\uff1a \u201c\u77ed\u671f\u5185\u65e2\u662f\u4e0d\u80fd\u6210\u4eb2\uff0c\u7559\u4ed6\u7684\u59b9\u59b9\u5728\u4eac\u4e5f\u6ca1\u6709\u7528\uff0c\u8fd8\u8981\u6015\u5979\u95f9\u51fa\u4e8b\u6765\u3002\u201d\u4e8e\u662f\u4fbf\u540c\u610f\u4e86\u53f2 \u767d\u90fd\u7684\u505a\u6cd5\uff0c\u7b11\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\u53ef\u662f\u6015\u8428\u603b\u7ba1\u56e0\u4e86\u4eca\u65e5\u4e4b\u4e8b\u800c\u81f4\u5fc3\u6709\u82a5\u8482\u4e48\uff1f\u5176\u5b9e\u4f60\u5e76\u975e\u671d\u5ef7 \u547d\u5b98\uff0c\u8ffd\u6355\u5f3a\u76d7\u3002\u4e0d\u8fc7\u662f\u4f60\u89c1\u2018\u4e49\u2019\u52c7\u4e3a\u800c\u5df2\uff0c\u6349\u4e0d\u4f4f\u5c09\u8fdf\u70af\u8428\u603b\u7ba1\u4e5f\u4e0d\u80fd\u89c1\u602a\u4f60\u7684\u3002\u8fc7\u4e24 \u5929\u6211\u53bb\u62dc\u4f1a\u8428\u603b\u7ba1\uff0c\u6211\u5f53\u7136\u4e5f\u4f1a\u66ff\u4f60\u8bf4\u597d\u8bdd\u7684\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u8c22\u8fc7\u4e86\u5e05\u76c2\u96c4\uff0c\u968f\u5373\u5429\u5490\u8463\u5341\u4e09\u5a18\u548c\u5706\u6d77\u4e8c\u4eba\u9001\u53f2\u7ea2\u82f1\u5148\u56de\u516d\u5408\u5e2e\u603b\u8235\uff0c\u7559\u4e0b\u9752 \u7b26\u3001\u7126\u78ca\u4e8c\u4eba\u8ddf\u4ed6\u3002\u516d\u5408\u5e2e\u7684\u4eba\u5728\u5317\u4eac\u7684\u8fd8\u6709\u4e01\u5f6d\u7b49\u4eba\uff0c\u4f4f\u5728\u5206\u8235\uff0c\u8fd9\u4e9b\u4eba\u56e0\u4e3a\u804c\u4f4d\u8f83\u4f4e\uff0c \u4e0d\u591f\u8d44\u683c\u7ed9\u8428\u603b\u7ba1\u62dc\u5bff\uff0c\u6240\u4ee5\u4eca\u5929\u6ca1\u6709\u968f\u6765\u3002\u53f2\u767d\u90fd\u51c6\u5907\u5148\u56de\u5317\u4eac\u5206\u8235\u5c45\u4f4f\uff0c\u7b49\u5019\u5e05\u5b5f\u96c4\u517b \u597d\u4e86\u4f24\uff0c\u4e0e\u4ed6\u5546\u8c08\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u6025\u4e8e\u5165\u4eac\u5ef7\u533b\uff0c\u9a91\u9a6c\u5148\u8d70\u3002\u53f2\u767d\u90fd\u5728\u4e34\u884c\u4e4b\u9645\uff0c\u6084\u6084\u53ee\u5631\u8463\u5341\u4e8c\u5a18\u3001\u53eb\u5979\u597d\u751f\u770b \u5b88\u53f2\u7ea2\u82f1\uff0c\u6709\u4e9b\u8bdd\u4ed6\u4e0d\u65b9\u4fbf\u548c\u59b9\u59b9\u8bf4\u7684\u4e5f\u4ea4\u4ee3\u4e86\u8463\u5341\u4e09\u5a18\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u5fc3\u9886\u795e\u4f1a\uff0c\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\u653e\u5fc3\uff0c\u5973\u5b69\u513f\u5bb6\u8c01\u4e0d\u613f\u610f\u5ac1\u5f97\u4e00\u4e2a\u597d\u4e08\u5927\uff1f\u82f1\u59b9\u5b50\u4e00 \u65f6\u7cca\u6d82\uff0c\u53d7\u4eba\u8ff7\u60d1\uff0c\u603b\u6709\u4e00\u5927\u4f1a\u660e\u767d\u8fc7\u6765\u7684\u3002\u201d\u53f2\u767d\u90fd\u9053\uff1a\u201c\u6211\u5c31\u662f\u6015\u5979\u6267\u8ff7\u4e0d\u609f\u3002\u201d\u8463\u5341 \u4e09\u5a18\u9053\uff1a\u201c\u5f85\u6211\u6653\u4ee5\u5229\u5bb3\uff0c\u5584\u8a00\u76f8\u529d\u3002\u60f3\u6765\u5e94\u8be5\u53ef\u4ee5\u529d\u5f97\u5979\u56de\u5fc3\u8f6c\u610f\u3002\u201d\u53f2\u767d\u90fd\u9053\uff1a\u201c\u597d\uff0c \u90a3\u5c31\u4e00\u5207\u62dc\u6258\u4f60\u4e86\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u6839\u672c\u4e0d\u7406\u4f1a\u4ed6\u4eec\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u5fc3\u4e2d\u53ea\u662f\u601d\u5ff5\u7740\u91d1\u9010\u6d41\uff0c\u60f3\u9053\uff1a\u201c\u4f46\u613f\u4ed6\u65e9\u65e5\u77e5\u9053\u6211 \u7684\u6d88\u606f\uff0c\u8d76\u5728\u6211\u54e5\u54e5\u56de\u6765\u4e4b\u524d\uff0c\u5148\u6765\u6551\u6211\u3002\u201d\u8981\u77e5\u516d\u5408\u5e2e\u4e2d\uff0c\u53ea\u6709\u53f2\u767d\u90fd\u80dc\u5f97\u8fc7\u91d1\u9010\u6d41\uff0c\u5176 \u4ed6\u56db\u5927\u9999\u4e3b\u90fd\u4e0d\u662f\u9010\u9010\u6d41\u7684\u5bf9\u624b\u3002\u56e0\u6b64\u3002\u53f2\u7ea2\u82f1\u5bf9\u91d1\u9010\u6d41\u662f\u5145\u6ee1\u4fe1\u5fc3\u7684\u3002 \u3000\u3000\u5982\u6b64\u4e00\u60f3\uff0c\u53f2\u7ea2\u82f1\u5012\u662f\u89c9\u5f97\u5979\u54e5\u54e5\u8fd9\u6837\u5b89\u6392\u2014\u2014\u8ba9\u8463\u5341\u4e09\u5a18\u4e0e\u5706\u6d77\u62bc\u5979\u56de\u53bb\uff0c\u81ea\u5df1\u5219\u7559 \u5728\u4eac\u4e2d\u3002\u2014\u2014\u5bf9\u5979\u6765\u8bf4\uff0c\u5012\u662f\u4e0d\u5e78\u4e2d\u4e4b\u5e78\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u7a74\u9053\u672a\u89e3\uff0c\u65e0\u529b\u6297\u62d2\uff0c\u8463\u5341\u4e09\u5a18\u5c06\u5979\u62b1\u4e0a\u9a6c\u80cc\uff0c\u4fbf\u5373\u767b\u7a0b\u3002\u53f2\u7ea2\u82f1\u7531\u4e8e\u6000\u7740\u4e00\u4e2a \u5e0c\u671b\uff0c\u5e0c\u671b\u91d1\u9010\u6d41\u80fd\u8d81\u7740\u516d\u5408\u5e2e\u7a7a\u865a\u4e4b\u9645\u524d\u6765\u6551\u5979\uff0c\u4e5f\u613f\u610f\u5148\u56de\u603b\u8235\u3002\u5979\u672c\u6765\u662f\u6700\u8ba8\u538c\u8463\u5341 \u4e09\u5a18\u7684\uff0c\u73b0\u5728\u4e5f\u61d2\u5f97\u9a82\u5979\uff0c\u8ba9\u5979\u6446\u5e03\u4e86\u3002 \u3000\u3000\u6309\u4e0b\u53f2\u7ea2\u82f1\u4e0d\u8868\u3002\u4e14\u8bf4\u674e\u5357\u661f\u4eba\u9a6c\u5747\u5df2\u53d7\u4f24\uff0c\u5750\u9a91\u8d1f\u75db\u72c2\u5954\uff0c\u674e\u5357\u661f\u7d27\u7d27\u6293\u7262\u9a6c\u7f30\uff0c\u5c31 \u4f3c\u817e\u4e91\u9a7e\u96fe\u4e00\u822c\uff0c\u8ff7\u8ff7\u7cca\u7cca\u7684\u4e5f\u4e0d\u77e5\u5df2\u7ecf\u8dd1\u4e86\u591a\u5c11\u8def\u7a0b\uff1f \u3000\u3000\u674e\u5357\u661f\u8d8a\u6765\u8d8a\u662f\u652f\u6301\u4e0d\u4f4f\uff0c\u60f3\u8981\u627e\u4e2a\u5730\u65b9\u517b\u4f24\uff0c\u4f46\u5374\u63a7\u5236\u4e0d\u4f4f\u8fd9\u5339\u8d1f\u75db\u72c2\u5954\u7684\u5750\u9a91\u3002\u674e \u5357\u661f\u773c\u76ae\u90fd\u5feb\u8981\u7741\u4e0d\u5f00\u4e86\uff0c\u8fde\u5fd9\u54ac\u4e86\u54ac\u5634\u5507\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4e0d\u884c\uff0c\u6211\u4e0d\u80fd\u7761\u7740\u3002\u53f2\u59d1\u5a18\u8fd8 \u8981\u6211\u8bbe\u6cd5\u6551\u5979\u5462\uff0c\u6211\u4e00\u5b9a\u8981\u633a\u4f4f\u3001\u633a\u4f4f\uff01\u201d\u674e\u5357\u661f\u662f\u4e2a\u6b66\u5b66\u884c\u5bb6\uff0c\u77e5\u9053\u5728\u81ea\u5df1\u53d7\u4e86\u5185\u4f24\u4e4b \u540e\uff0c\u5018\u82e5\u7cbe\u795e\u4e00\u677e\uff0c\u5fcd\u4e0d\u4f4f\u5026\u610f\u800c\u660f\u7761\u7684\u8bdd\uff0c\u53ea\u6015\u5c31\u4e0d\u4f1a\u9192\u6765\u4e86\u3002 \u3000\u3000\u674e\u5357\u661f\u8bb0\u6302\u7740\u53f2\u7ea2\u82f1\uff0c\u4ee5\u4e3a\u53f2\u7ea2\u82f1\u4e5f\u4e00\u5b9a\u662f\u5728\u60f3\u5ff5\u7740\u4ed6\uff0c\u628a\u5e0c\u671b\u5bc4\u6258\u5728\u4ed6\u8eab\u4e0a\u3002\u5168\u51ed\u7740 \u8fd9\u4e00\u70b9\u7cbe\u795e\u529b\u91cf\uff0c\u53c8\u652f\u6301\u4ed6\u8dd1\u4e86\u4e00\u7a0b\u3002\u4ed6\u5374\u600e\u77e5\u53f2\u7ea2\u82f1\u6b64\u9645\u60f3\u7684\u5e76\u4e0d\u662f\u4ed6\uff0c\u800c\u662f\u91d1\u9010\u6d41\u5462\u3002 \u3000\u3000\u5750\u9a91\u597d\u50cf\u8dd1\u5f97\u6162\u4e86\u4e00\u4e9b\uff0c\u53ef\u662f\u674e\u5357\u65e9\u5df2\u7ecf\u652f\u6491\u4e0d\u4f4f\u4e86\u3002\u6b63\u5728\u795e\u667a\u8ff7\u7cca\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u8e44\u58f0 \u5f97\u5f97\uff0c\u524d\u9762\u6765\u4e86\u4e00\u9a91\uff0c\u9a91\u8005\u662f\u548c\u4ed6\u5e74\u7eaa\u76f8\u82e5\u7684\u5c11\u5e74\u3002\u8fd9\u5c11\u5e74\u89c1\u674e\u5357\u661f\u4f0f\u5728\u9a6c\u80cc\uff0c\u4f3c\u662f\u53d7\u4f24\u7684 \u6a21\u6837\uff0c\u4e0d\u514d\u597d\u5947\u5fc3\u8d77\uff0c\u5bf9\u4ed6\u683c\u5916\u7559\u795e\u3002\u4e24\u5339\u5750\u9a91\u51e0\u4e4e\u662f\u64e6\u978d\u800c\u8fc7\u4e4b\u9645\uff0c\u5c11\u5e74\u53c8\u53d1\u73b0\u4e86\u63d2\u5728\u674e \u5357\u661f\u9a6c\u80cc\u4e0a\u7684\u90a3\u67c4\u5315\u9996\uff0c\u66f4\u89c9\u5f97\u5947\u602a\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4fbf\u5373\u62e8\u8f6c\u9a6c\u5934\uff0c\u8ffd\u8d76\u674e\u5357\u661f\u3002 \u3000\u3000\u674e\u5357\u661f\u8fd9\u5339\u5750\u9a91\uff0c\u53d7\u4f24\u4e4b\u540e\uff0c\u72c2\u5954\u4e00\u7a0b\uff0c\u6d41\u8840\u8fc7\u591a\uff0c\u6b64\u65f6\u4ea6\u5df2\u662f\u7b4b\u75b2\u529b\u7aed\u3002\u5c31\u5728\u8fd9\u5c11\u5e74 \u8ffd\u4e0a\u4e4b\u6807\uff0c\u674e\u5357\u661f\u7684\u5750\u9a91\u5ffd\u5730\u9a6c\u5931\u524d\u8e44\uff0c\u6eda\u4e0b\u8def\u57fa\uff0c\u628a\u674e\u5357\u661f\u6454\u8dcc\u3002 \u3000\u3000\u8ff7\u7cca\u4e2d\uff0c\u674e\u5357\u661f\u597d\u4f3c\u7ed9\u4eba\u62b1\u4f4f\uff0c\u503e\u523b\u5c31\u5931\u4e86\u77e5\u89c9\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u674e\u5357\u661f\u624d\u9192\u8fc7\u6765\uff0c\u773c\u775b\u521a\u4e00\u5f20\u5f00\uff0c\u5c31\u89c9\u5200\u5149\u8000\u76ee\u3002\u5728\u4ed6\u7684\u9762\u524d\uff0c\u6709\u4e00 \u4e2a\u4eba\u62ff\u7740\u4e00\u67c4\u96ea\u4eae\u7684\u5315\u9996\u6b63\u5bf9\u7740\u4ed6\uff0c\u53e3\u4e2d\u5583\u5583\u81ea\u8bed\uff1a\u201c\u54a6\uff0c\u8fd9\u67c4\u5315\u9996\uff0c\u8fd9\u67c4\u5315\u9996\u2026\u2026\u201d \u3000\u3000\u674e\u5357\u661f\u795e\u667a\u672a\u6e05\uff0c\u4eac\u9053\u662f\u654c\u4eba\u8ffd\u6765\uff0c\u610f\u6b32\u52a0\u5bb3\u4e8e\u4ed6\uff0c\u8fde\u5fd9\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\u7ffb\u8d77\u8eab\u6765\uff0c \u4e00\u6307\u6233\u51fa\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6211\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u8fd9\u4e00\u6307\u70b9\u5f97\u53c8\u5feb\u53c8\u51c6\uff0c\u7ad9\u5728\u4ed6\u9762\u524d\u7684\u90a3\u4e2a\u5c11\u5e74 \u53ea\u89c9\u864e\u53e3\u4e00\u9ebb\uff0c\u5315\u9996\u94db\u5577\u5760\u5730\u3002 \u3000\u3000\u674e\u5357\u661f\u521a\u521a\u9192\u6765\uff0c\u8eab\u4f53\u8fd8\u662f\u5341\u5206\u865a\u5f31\uff0c\u7528\u4e86\u4e00\u70b9\u6c14\u529b\uff0c\u767b\u65f6\u53c8\u5012\u4e0b\u53bb\u4e86\u3002\u8fd9\u624d\u53d1\u89c9\u81ea\u5df1 \u662f\u8eba\u5728\u4e00\u5f20\u8f6f\u7ef5\u7ef5\u7684\u5e8a\u4e0a\u3002\u623f\u95f4\u91cc\u53ea\u6709\u4ed6\u548c\u90a3\u4e2a\u5c11\u5e74\u3002 \u3000\u3000\u674e\u5357\u661f\u6014\u4e86\u4e00\u6014\uff0c\u6e10\u6e10\u56de\u590d\u4e86\u8bb0\u5fc6\uff0c\u8bb0\u5f97\u8fd9\u4e2a\u5c11\u5e74\u5c31\u662f\u4ed6\u5728\u8def\u4e0a\u78b0\u89c1\u7684\u90a3\u4e2a\u5c11\u5e74\u3002\u5e05\u5b5f \u96c4\u6253\u4f24\u4ed6\uff0c\u53f2\u767d\u90fd\u7528\u90a3\u5315\u9996\u63b7\u4f24\u4ed6\u7684\u5750\u9a91\u4ee5\u53ca\u4ed6\u5931\u8db3\u843d\u9a6c\u7b49\u7b49\u4e8b\u60c5\uff0c\u4e00\u5239\u90a3\u95f4\u4e5f\u5168\u90e8\u8bb0\u8d77\u6765 \u4e86\u3002\u674e\u5357\u661f\u597d\u751f\u8be7\u5f02\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5df2\u7ecf\u6eda\u4e0b\u8def\u4e0a\u7684\u4e48\uff1f\u600e\u7684\u5374\u7761\u5230\u8fd9\u5f20\u5e8a\u4e0a\u6765\u4e86\uff1f \u83ab\u975e\u5c31\u662f\u8fd9\u5c11\u5e74\u6551\u6211\u4e0d\u6210\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u6b64\u65f6\u4e5f\u662f\u5341\u5206\u60ca\u8be7\uff0c\u5fc3\u60f3\uff1a\u201c\u6b64\u4eba\u53d7\u4e86\u91cd\u4f24\uff0c\u6709\u6c14\u6ca1\u529b\uff0c\u70b9\u7a74\u529f\u592b\u5c45\u7136\u8fd8\u662f\u5982\u6b64 \u5389\u5bb3\uff01\u5e78\u4e8f\u4ed6\u6c14\u529b\u672a\u66fe\u6062\u590d\uff0c\u8981\u4e0d\u7136\u53ea\u6015\u6211\u8fd9\u6761\u624b\u81c2\u5df2\u7ecf\u7ed9\u4ed6\u5e9f\u4e86\uff01\u201d\u5f53\u4e0b\u62fe\u8d77\u4e86\u90a3\u67c4\u5315 \u9996\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u60ca\u614c\uff0c\u6211\u4e0d\u662f\u4f60\u7684\u4ec7\u4eba\u3002\u4f60\u7684\u4ec7\u4eba\u662f\u516d\u5408\u5e2e\u7684\u4e0d\u662f\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u9053\u4e86\u4e00\u58f0\u201c\u60ed\u6127\uff01\u201d\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u5144\u53f0\u6551\u547d\u4e4b\u6069\uff0c\u4f60\u5374\u600e\u77e5\u6211\u7684\u4ec7\u4eba\u662f\u516d\u5408\u5e2e \u7684\uff1f\u201d \u3000\u3000\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u54b1\u4eec\u5012\u662f\u540c\u4e00\u4ec7\u4eba\u7684\u4e86\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u4e0e\u516d\u5408\u5e2e\u4e5f\u7ed3\u6709\u6881\u5b50\u3002 \u6211\u8ba4\u5f97\u516d\u5408\u5e2e\u6240\u7528\u7684\u5315\u9996\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u53c8\u60ca\u53c8\u559c\uff0c\u5148\u62a5\u4e86\u81ea\u5df1\u7684\u59d3\u540d\uff0c\u7136\u540e\u95ee\u90a3\u5c11\u5e74\uff1a\u201c\u4e0d\u6562\u8bf7\u6559\u5144\u53f0\u9ad8\u59d3\u5927\u540d\uff0c\u4e0e\u516d \u5408\u5e2e\u53c8\u662f\u600e\u5730\u7ed3\u7684\u6881\u5b50\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u9053\uff1a\u201c\u5c0f\u5f1f\u9648\u5149\u7167\u3002\u5149\u660e\u7684\u5149\uff0c\u7167\u8000\u7684\u7167\u3002\u6570\u6708\u524d\u6211\u5728\u5180\u9c81\u9053\u4e0a\u78b0\u4e0a\u516d\u5408\u5e2e\u4e2d\u7684 \u51f6\u50e7\u5706\u6d77\u6b63\u5728\u52ab\u6740\u5ba2\u5546\uff0c\u6211\u4e0e\u4ed6\u4ea4\u624b\uff0c\u4ed6\u7ed9\u6211\u523a\u4e86\u4e00\u5251\uff0c\u6211\u4e5f\u7ed9\u4ed6\u98de\u51fa\u7684\u5315\u9996\u6240\u4f24\u3002\u4ed6\u4f24\u6211 \u7684\u90a3\u67c4\u5315\u9996\u548c\u8fd9\u67c4\u5315\u9996\u6b63\u662f\u4e00\u5f0f\u4e00\u6837\uff0c\u5200\u67c4\u90fd\u6709\u516d\u5408\u5e2e\u7684\u6807\u8bb0\u7684\u3002\u4f60\u77a7\u3002\u201d\u674e\u5357\u661f\u4e00\u770b\uff0c\u53ea \u89c1\u5200\u67c4\u523b\u6709\u4e00\u4e2a\u9ab7\u9ac5\u5934\uff0c\u679c\u7136\u662f\u516d\u5408\u5e2e\u7684\u6807\u8bb0\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u5c11\u5e74\u6b63\u662f\u9648\u5929\u5b87\u7684\u513f\u5b50\u3002\u90a3\u6b21\u91d1\u9010\u6d41\u5230\u4ed6\u5bb6\u4e4b\u65f6\uff0c\u4ed6\u5df2\u7ecf\u517b\u597d\u4e86\u4f24\u79bb\u5f00\u5bb6\u4e86\uff0c \u6240\u4ee5\u4e24\u4eba\u6ca1\u6709\u78b0\u4e0a\u3002\u9648\u5929\u5b87\u66fe\u7ecf\u628a\u513f\u5b50\u4e0e\u516d\u5408\u5e2e\u7ed3\u4ec7\u4e4b\u4e8b\u544a\u8bc9\u91d1\u9010\u6d41\u3002\u4e0d\u8fc7\uff0c\u674e\u5357\u661f\u5374\u4e0d\u77e5 \u9053\u9648\u5929\u5b87\u7236\u5b50\u548c\u4ed6\u7684\u4e49\u5f1f\u6709\u6781\u6df1\u539a\u7684\u6e0a\u6e90\u3002 \u3000\u3000\u674e\u5357\u661f\u8c22\u8fc7\u4e86\u9648\u5149\u7167\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u4ec0\u4e48\u5730\u65b9\uff0c\u6211\u5230\u8fd9\u91cc\u5df2\u7ecf\u6709\u591a\u4e45\u4e86\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u8fd9\u91cc\u662f\u897f\u5c71\u5367\u4f5b\u5bfa\u3002\u5367\u4f5b\u5bfa\u7684\u4e3b\u6301\u4e0e\u5bb6\u7236\u662f\u65b9\u5916\u4e4b\u4ea4\u3002\u5bb6\u7236\u662f\u82cf\u5dde\u9648\u5929 \u5b87\u3002\u201d \u3000\u3000\u9648\u5929\u5b87\u5728\u6b66\u6797\u5927\u5927\u6709\u540d\uff0c\u4e0d\u8fc7\u9648\u5149\u7167\u8bf4\u51fa\u7236\u4eb2\u7684\u540d\u5b57\u5012\u4e0d\u662f\u8981\u5938\u8000\u4ed6\u7684\u8eab\u4e16\uff0c\u800c\u662f\u8981\u4f7f \u674e\u5357\u661f\u514d\u9664\u7591\u8651\u3002 \u3000\u3000\u9648\u5149\u7167\u4ee5\u4e3a\u674e\u5357\u661f\u542c\u4e86\u4ed6\u7236\u4eb2\u7684\u540d\u5b57\uff0c\u5373\u4f7f\u4e0d\u8083\u7136\u8d77\u656c\uff0c\u81f3\u5c11\u4e5f\u5f97\u8bf4\u4e9b\u201c\u4e45\u4ef0\u201d\u4e4b\u7c7b\u7684 \u5ba2\u6c14\u8bdd\uff0c\u54ea\u77e5\u674e\u5357\u661f\u5374\u662f\u8bf4\u9053\uff1a\u201c\u539f\u6765\u8fd9\u91cc\u5c31\u662f\u897f\u5c71\u5367\u4f5b\u5bfa\u4e48\uff1f\u6211\u672c\u6765\u60f3\u5230\u897f\u5c71\u627e\u4e2a\u5730\u65b9\u517b \u4f24\u7684\uff0c\u771f\u662f\u591a\u8c22\u9648\u5144\u4e86\uff01\u201d\u542c\u4ed6\u8a00\u8bed\uff0c\u4ed6\u7684\u60ca\u559c\u53ea\u662f\u4e3a\u4e86\u53d1\u89c9\u81ea\u5df1\u662f\u5728\u897f\u5c71\u7684\u5367\u4f5b\u5bfa\u517b\u4f24\uff0c \u800c\u4e0d\u662f\u56e0\u4e3a\u77e5\u9053\u4e86\u9648\u5149\u7167\u7684\u7236\u4eb2\u662f\u9648\u5929\u5b87\u3002 \u3000\u3000\u9648\u5149\u7167\u4e0d\u7981\u6709\u4e9b\u8be7\u5f02\uff0c\u5fc3\u60f3\uff1a\u201c\u4ed6\u6b66\u529f\u8fd9\u4e48\u597d\uff0c\u600e\u7684\u7adf\u4e0d\u77e5\u9053\u7239\u7239\u7684\u540d\u5b57\uff1f\u201d\u6c5f\u6e56\u4e0a\u7981 \u5fcc\u751a\u591a\uff0c\u662f\u4ee5\u9648\u5149\u7167\u867d\u662f\u5bf9\u674e\u5357\u661f\u6709\u6069\uff0c\u4e5f\u4e0d\u4fbf\u5c31\u5192\u6627\u7684\u67e5\u95ee\u4ed6\u7684\u6765\u5386\u3002\u5f53\u4e0b\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4 \u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u543e\u5144\u5012\u662f\u53ef\u4ee5\u5728\u8fd9\u91cc\u5b89\u5fc3\u517b\u75c5\u4e86\u3002\u8fd9\u91cc\u7684\u4e3b\u6301\u7cbe\u4e8e\u533b\u9053\uff0c\u6628\u665a\u4ed6\u5df2\u7ecf\u7ed9\u4f60\u8bca \u6cbb\u8fc7\u4e86\uff0c\u636e\u4ed6\u8bf4\u543e\u5144\u867d\u7136\u4f24\u5f97\u4e0d\u8f7b\uff0c\u5e78\u597d\u5185\u529f\u6df1\u539a\uff0c\u53ea\u8981\u518d\u670d\u51e0\u5242\u836f\uff0c\u5927\u7ea6\u7528\u4e0d\u4e86\u5341\u5929\uff0c\u5c31 \u53ef\u4ee5\u75ca\u6108\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u6211\u5728\u8fd9\u91cc\u5df2\u7ecf\u660f\u8ff7\u4e86\u4e00\u5929\u4e00\u591c\u4e86\u4e48\uff1f\u54e6\uff0c\u8fd8\u8981\u5341\u5929\u624d\u80fd \u75ca\u6108\uff0c\u8fd9\u5374\u662f\u6025\u715e\u4eba\u4e86\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u543e\u5144\u8eab\u4f53\u8981\u7d27\u3002\u4e3b\u6301\u6628\u665a\u8bca\u8fc7\u4f60\u7684\u8109\uff0c\u8bf4\u662f\u4f60\u516d\u8109\u4e0d\u8c03\uff0c\u9887\u6709\u90c1\u95f7\u4e4b\u8c61\uff0c \u5927\u7ea6\u662f\u6709\u5fc3\u4e8b\u6101\u70e6\uff0c\u53eb\u6211\u529d\u4f60\u52a1\u5fc5\u628a\u5fc3\u4e8b\u629b\u5f00\u4e00\u8fb9\uff0c\u517b\u597d\u4e86\u4f24\u518d\u8bf4\u3002\u8bf7\u6055\u6211\u4ea4\u6d45\u8a00\u6df1\uff0c\u5192\u6627 \u52a8\u95ee\uff0c\u5144\u53f0\u662f\u5426\u8bb0\u6302\u7740\u62a5\u4ec7\u4e4b\u4e8b\uff1f\u4f60\u7684\u4ec7\u4eba\u662f\u5426\u516d\u5408\u5e2e\u4e2d\u4e00\u4e2a\u53eb\u505a\u53f2\u7ea2\u82f1\u7684\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u9648\u5144\u4f55\u4ee5\u8ba4\u4e3a\u53f2\u7ea2\u82f1\u662f\u6211\u7684\u4ec7\u4eba\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u53f2\u7ea2\u82f1\uff1f\u55ef\uff0c\u4f60\u8bf4\u7684\u8fd9\u4e2a\u53f2\u7ea2\u82f1\u662f\u4e0d\u662f\u516d\u5408\u5e2e\u5e2e\u4e3b\u53f2\u767d\u90fd\u7684\u59b9\u59b9\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f46\u8fd9\u4f4d\u53f2\u59d1\u5a18\u5374\u5e76\u4e0d\u662f\u6211\u7684\u4ec7\u4eba\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u662f\u6211\u8bef\u4f1a\u4e86\u3002\u4f60\u6628\u665a\u5728\u660f\u8ff7\u4e2d\u4e0d\u65ad\u7684\u5728\u9a82\u7ea2\u82f1\uff0c\u6211\u4e00\u65f6\u60f3\u4e0d\u8d77\u662f \u53f2\u767d\u90fd\u7684\u59b9\u59b9\uff0c\u53ea\u9053\u4f60\u662f\u9a82\u4e00\u4e2a\u59d3\u2018\u5b8f\u2019\uff0c\u540d\u2018\u82f1\u2019\u7684\u4eba\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u8be7\u9053\uff1a\u201c\u6211\u9a82\u7ea2\u82f1\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u4f60\u9a82\u2018\u6d2a\u82f1\u4fd7\u6d41\u2019\u3002\u6211\u4ee5\u4e3a\u4f60\u9a82\u7684\u8fd9\u4e2a\u4eba\u5c31\u662f\u4f60\u7684\u4ec7\u4eba\uff0c\u4f46\u53c8\u6709\u70b9\u5947\u602a\uff0c \u9a82\u4ec7\u4eba\u4f55\u5fc5\u9a82\u4ed6\u2018\u4fd7\u6d41\u2019\uff1f\u516d\u5408\u5e2e\u7684\u532a\u7c7b\u5c82\u53ea\u5eb8\u4fd7\uff0c\u5e94\u8be5\u9a82\u4ed6\u72e0\u6bd2\u624d\u5bf9\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u5927\u7b11\u9053\uff1a\u201c\u9648\u5144\u542c\u9519\u4e86\u3002\u6211\u4e0d\u662f\u9a82\u4eba\u3002\u6211\u6709\u4e00\u4f4d\u597d\u670b\u53cb\u540d\u53eb\u91d1\u9010\u6d41\uff0c\u968f\u6ce2\u9010\u6d41\u7684 \u2018\u9010\u6d41\u2019\u4e8c\u5b57\uff0c\u60f3\u5fc5\u662f\u6211\u6240\u53d1\u7684\u5453\u8bed\u662f\u5728\u53eb\u4ed6\u7684\u540d\u5b57\uff0c\u543e\u5144\u542c\u6210\u4e86\u2018\u4fd7\u6d41\u2019\u4e86\u3002\u201d\u9648\u5149\u7167\u4e5f \u4e0d\u7981\u5927\u7b11\u8d77\u6765\u3002\u4f46\u8fd8\u662f\u6709\u70b9\u5947\u602a\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u53c8\u4e3a\u4f55\u628a\u4f60\u597d\u670b\u53cb\u7684\u540d\u5b57\u4e0e\u90a3\u9b54\u5973\u7684\u540d\u5b57 \u8054\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u516d\u5408\u5e2e\u867d\u662f\u5728\u6c5f\u6e56\u4e3a\u975e\u4f5c\u6b79\uff0c\u4f46\u5e2e\u4e2d\u4eba\u4f17\u5374\u4e0d\u53ef\u4e00\u6982\u800c\u8bba\u3002\u6bd4\u5982\u8fd9\u4f4d\u53f2\u59d1 \u5a18\u5c31\u548c\u5979\u7684\u54e5\u54e5\u5e76\u4e0d\u4e00\u6837\u3002\u53f2\u767d\u90fd\u7518\u5fc3\u505a\u8428\u798f\u9f0e\u7684\u722a\u7259\uff0c\u8fd9\u4f4d\u53f2\u59d1\u5a18\u6628\u5929\u5374\u548c\u6211\u4eec\u5927\u95f9\u4e86\u8428 \u798f\u9f0e\u7684\u5bff\u5802\uff0c\u52ab\u4e86\u4ed6\u7684\u5bff\u793c\uff01\u201d\u5f53\u4e0b\u5c06\u6628\u65e5\u5bff\u5802\u4e4b\u4e8b\uff0c\u7ea6\u7565\u5730\u544a\u8bc9\u4e86\u9648\u5149\u7167\uff0c\u9648\u5149\u7167\u8fd9\u624d\u77e5 \u9053\u674e\u5357\u661f\u53d7\u4f24\u7684\u7f18\u7531\u3002 \u3000\u3000\u674e\u5357\u661f\u7eed\u9053\uff1a\u201c\u6211\u4eec\u4e09\u4eba\u662f\u4e00\u540c\u9003\u51fa\u6765\u7684\uff0c\u53f2\u59d1\u5a18\u88ab\u5979\u54e5\u54e5\u6349\u4e86\u56de\u53bb\uff0c\u91d1\u9010\u6d41\u5219\u5c1a\u672a\u77e5 \u4e0b\u843d\uff0c\u60f3\u5fc5\u662f\u56e0\u4e3a\u6211\u8bb0\u6302\u7740\u4ed6\u4eec\uff0c\u6240\u4ee5\u5728\u68a6\u4e2d\u53eb\u51fa\u4e86\u4ed6\u4eec\u7684\u540d\u5b57\u6765\u4e86\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u8fd9\u6837\u8bf4\uff0c\u8fd9\u4f4d\u53f2\u59d1\u5a18\u5012\u662f\u4fa0\u4e49\u4eba\uff0c\u6211\u521a\u624d\u5374\u662f\u5931\u8a00\u4e86\uff0c\u55ef\uff0c\u4f60 \u90a3\u4f4d\u59d3\u91d1\u7684\u670b\u53cb\u5728\u5317\u4eac\u53ef\u6709\u5f88\u719f\u7684\u4eba\uff0c\u8981\u4e0d\u8981\u6211\u7ed9\u4f60\u6253\u542c\u4ed6\u7684\u6d88\u606f\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u91d1\u9010\u6d41\u7684\u672c\u9886\u6bd4\u6211\u9ad8\u660e\u5f97\u591a\uff0c\u4ed6\u65e2\u7136\u9003\u51fa\u8428\u5e9c\uff0c\u60f3\u5fc5\u662f\u53ef\u4ee5\u8131\u9669\u7684\u3002\u4e0d\u5fc5 \u9648\u5144\u8d39\u795e\uff0c\u5f85\u6211\u4f24\u597d\u4e4b\u540e\uff0c\u518d\u53bb\u5bfb\u8bbf\u4ed6\u5427\u3002\u201d \u3000\u3000\u8981\u77e5\u91d1\u9010\u6d41\u5bc4\u5bd3\u6234\u5bb6\uff0c\u8fd9\u662f\u4e00\u4e2a\u79d8\u5bc6\uff0c\u91d1\u9010\u6d41\u66fe\u53ee\u5631\u8fc7\u674e\u5357\u661f\u4e0d\u53ef\u544a\u8bc9\u5916\u4eba\u7684\u3002\u6240\u4ee5\u674e \u5357\u661f\u867d\u7136\u6025\u4e8e\u8981\u628a\u53f2\u7ea2\u82f1\u7684\u6d88\u606f\u544a\u8bc9\u91d1\u9010\u6d41\uff0c\u4f46\u5374\u4e0d\u4fbf\u8f6c\u6258\u9648\u5149\u7167\u53bb\u529e\u3002 \u3000\u3000\u674e\u5357\u661f\u6697\u81ea\u601d\u91cf\uff1a\u201c\u53cd\u6b63\u7ea2\u82f1\u5df2\u7ecf\u62bc\u56de\u4e86\u516d\u5408\u5e2e\u603b\u8235\uff0c\u53f2\u767d\u90fd\u4e00\u65f6\u4e0d\u4f1a\u56de\u53bb\uff0c\u6211\u5728\u5341\u5929 \u4e4b\u5185\u53ef\u4ee5\u75ca\u6108\uff0c\u82e5\u662f\u4e0d\u7b49\u5b8c\u5168\u4f24\u597d\uff0c\u8fc7\u4e86\u516d\u4e03\u5929\uff0c\u5927\u7ea6\u4e5f\u53ef\u4ee5\u8d70\u8def\u3002\u90a3\u65f6\u6211\u518d\u53bb\u7ea6\u91d1\u9010\u6d41\u540c \u5f80\u516d\u5408\u5e2e\u7684\u603b\u8235\uff0c\u6599\u60f3\u7ea2\u82f1\u4e5f\u4e0d\u81f3\u4e8e\u5c31\u6709\u4ec0\u4e48\u610f\u5916\u3002\u201d \u3000\u3000\u4e14\u8bf4\u91d1\u9010\u6d41\u5728\u6234\u5bb6\u7b49\u5019\u674e\u5357\u661f\u548c\u53f2\u7ea2\u82f1\u540c\u6765\u627e\u4ed6\uff0c\u65e5\u5b50\u4e00\u5929\u4e00\u5929\u7684\u8fc7\u53bb\uff0c\u5230\u4e86\u7b2c\u4e03\u5929\uff0c \u5140\u662f\u6773\u65e0\u97f3\u8baf\uff0c\u91d1\u9010\u6d41\u53ea\u9053\u4ed6\u4eec\u4e8c\u4eba\u5df2\u7ecf\u8fdc\u8d70\u9ad8\u98de\uff0c\u54ea\u91cc\u77e5\u9053\u4ed6\u4eec\u4e43\u662f\u4e00\u4e2a\u88ab\u56da\uff0c\u4e00\u4e2a\u53d7 \u4f24\uff1f\u662f\u4ee5\u91d1\u9010\u6d41\u5185\u5fc3\u6df1\u5904\u867d\u7136\u4e0d\u514d\u6709\u51e0\u5206\u5931\u671b\uff0c\u5374\u4e5f\u6697\u6697\u4e3a\u4ed6\u4eec\u611f\u5230\u6b23\u6170\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u91d1\u9010\u6d41\u5982\u5e38\u7684\u5230\u5730\u7a96\u4e2d\u770b\u5510\u6770\u592b\u94f8\u5251\uff0c\u5510\u6770\u592b\u5728\u4e00\u4e2a\u5927\u6c34\u7f38\u91cc\u628a\u65b0\u9648\u6210\u7684\u5b9d\u5251\u62ff \u51fa\u6765\uff0c\u7b11\u9053\uff1a\u201c\u603b\u7b97\u4e0d\u8d1f\u6240\u6258\uff0c\u8fd9\u70b3\u7384\u94c1\u5b9d\u5251\u5df2\u70bc\u6210\u4e86\u3002\u4e03\u65e5\u7684\u6dec\u783a\u4e4b\u529f\u7a0d\u5fae\u5acc\u5c11\u4e00\u70b9\uff0c\u597d \u5728\u7384\u94c1\u662f\u7a00\u4e16\u4e4b\u73cd\uff0c\u53ea\u8981\u70bc\u5f97\u57fa\u6cd5\uff0c\u5012\u4e5f\u65e0\u987b\u5343\u9524\u5de8\u70bc\u3002\u8001\u5f1f\uff0c\u4f60\u8bd5\u4e00\u8bd5\u8fd9\u67c4\u5b9d\u5251\uff0c\u770b\u770b\u6211 \u6709\u6ca1\u6709\u7cdf\u8e4b\u4e86\u4f60\u7684\u7384\u94c1\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u63a5\u8fc7\u5b9d\u5251\uff0c\u6382\u4e86\u4e00\u6382\uff0c\u6c89\u7538\u7538\u7684\u603b\u6709\u767e\u6765\u65a4\u91cd\uff0c\u4f46\u5251\u950b\u9690\u8574\u5149\u534e\uff0c\u604d\u5982\u4e00\u6795\u79cb \u6c34\uff0c\u5374\u53c8\u662f\u8584\u5f97\u597d\u4f3c\u900f\u660e\u4f3c\u7684\u3002\u91d1\u9010\u6d41\u968f\u624b\u4e00\u6325\uff0c\u628a\u4e00\u4e2a\u5927\u94c1\u9524\u4f3c\u524a\u8c46\u8150\u4f3c\u7684\u524a\u4e0b\u4e86\u5341\u51e0 \u7247\uff0c\u91d1\u9010\u6d41\u5927\u559c\u5982\u72c2\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u662f\u4e00\u628a\u524a\u94c1\u5982\u6ce5\u7684\u5b9d\u5251\uff01\u6211\u62ff\u53bb\u7ed9\u6234\u8001\u524d\u8f88\u770b\u53bb\uff0c\u8ba9\u4ed6\u4e5f \u9ad8\u5174\u9ad8\u5174\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5174\u51b2\u51b2\u7684\u62ff\u7740\u5b9d\u5251\uff0c\u8d70\u51fa\u5730\u7a96\uff0c\u6b63\u8981\u5927\u58f0\u53eb\u558a\uff0c\u5ffd\u89c1\u6234\u5747\u7684\u6b21\u5b50\u6234\u8c1f\u8fce\u9762\u800c\u6765\uff0c \u5411\u4ed6\u6447\u624b\u793a\u610f\uff0c\u8f7b\u8f7b\u4e00\u5618\uff0c\u8bf4\u9053\uff1a\u201c\u895f\u58f0\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u51fa\u4e86\u4ec0\u4e48\u4e8b\u4e86\uff1f\u201d\u6234\u7337\u5c06\u91d1\u9010\u6d41\u62c9\u8fc7\u4e00\u8fb9\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u53f2\u767d\u90fd\u548c\u4e01\u5f6d\u5df2 \u7ecf\u6765\u4e86\uff0c\u5982\u4eca\u6b63\u5728\u5916\u9762\u2018\u7075\u5802\u2019\u548c\u6211\u54e5\u54e5\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\uff0c\u6211\u8fd9\u67c4\u7384\u94c1\u5b9d\u5251\u5df2\u7ecf\u70bc\u6210\uff0c\u6b63\u597d\u8bf7\u4ed6\u8bd5\u8bd5\u8fd9\u628a\u5b9d\u5251\u7684\u5389\u5bb3\uff01\u201d \u3000\u3000\u6234\u7337\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u91d1\u5144\u4e0d\u53ef\u9020\u6b21\u3002\u5bb6\u7236\u662f\u4e0d\u60f3\u95f9\u4e8b\u624d\u8bc8\u6b7b\u7684\u3002\u53ea\u8981\u6211\u7684\u54e5\u54e5\u5e94\u4ed8\u5f97\u8fc7 \u53bb\uff0c\u8fd8\u662f\u4ee5\u4e0d\u52a8\u6b66\u4e3a\u4f73\u3002\u53f2\u767d\u90fd\u9664\u4e86\u4e01\u5f6d\u4e4b\u5916\uff0c\u8fd8\u5e26\u4e86\u4ed6\u7684\u4e24\u4e2a\u9999\u4e3b\u540c\u6765\u3002\u8ba4\u771f\u6253\u8d77\u6765\uff0c\u54b1 \u4eec\u4e5f\u672a\u5fc5\u80fd\u591f\u7a33\u64cd\u80dc\u5238\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u867d\u7136\u4e0d\u5927\u6ee1\u610f\u6234\u5bb6\u7236\u5b50\u7684\u793a\u5f31\u7684\u4f5c\u6cd5\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u6234\u5747\u5e74\u7eaa\u8001\u8fc8\uff0c\u800c\u4e14\u662f\u5728\u5317 \u4eac\u6709\u5bb6\u6709\u4e1a\u4e4b\u4eba\uff0c\u81ea\u5df1\u53ef\u4ee5\u4e00\u8d70\u4e86\u4e4b\uff0c\u6234\u5bb6\u7236\u5b50\u603b\u662f\u4e0d\u5bb9\u6613\u5f03\u5bb6\u800c\u9003\u7684\u3002 \u3000\u3000\u8bbe\u8eab\u5904\u5730\u66ff\u6234\u5bb6\u7236\u5b50\u7740\u60f3\u4e4b\u540e\uff0c\u91d1\u9010\u6d41\u53ea\u597d\u628a\u8dc3\u8dc3\u6b32\u8bd5\u7684\u5fc3\u60c5\u5f3a\u6291\u4e0b\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u4e3b\u4eba \u5bb6\u65e2\u7136\u4e0d\u60f3\u60f9\u4e8b\uff0c\u6211\u8fd9\u4e2a\u505a\u5ba2\u4eba\u7684\u5e94\u7136\u8be5\u542c\u4ece\u4e3b\u4eba\u7684\u610f\u601d\u3002\u4e0d\u8fc7\uff0c\u54b1\u4eec\u4e0d\u60f3\u60f9\u4e8b\uff0c\u53ea\u6015\u4ed6\u4eec \u5374\u8981\u751f\u4e8b\u3002\u6709\u5907\u65e0\u60a3\uff0c\u6211\u548c\u4f60\u5230\u2018\u7075\u5802\u2019\u4fa7\u9762\u7684\u90a3\u95f4\u53a2\u623f\u57cb\u4f0f\uff0c\u7aa5\u5bdf\u4ed6\u4eec\u7684\u52a8\u9759\uff0c\u4e07\u4e00\u95f9\u51fa \u4e8b\u6765\uff0c\u4e5f\u514d\u5f97\u4f60\u7684\u54e5\u54e5\u5403\u4e86\u773c\u524d\u4e8f\u3002\u201d \u3000\u3000\u6234\u7337\u9053\uff1a\u201c\u5bb6\u7236\u6b63\u662f\u8981\u91d1\u5144\u5982\u6b64\u3002\u201d\u91d1\u9010\u6d41\u548c\u4ed6\u6084\u6084\u8fdb\u5165\u90a3\u95f4\u53a2\u623f\uff0c\u53ea\u89c1\u6234\u5747\u65e9\u5df2\u8eb2\u5728 \u90a3\u91cc\u4e86\u3002 \u3000\u3000\u9f13\u5747\u62db\u4e86\u62db\u624b\uff0c\u91d1\u9010\u6d41\u8d70\u5230\u4ed6\u7684\u8eab\u8fb9\uff0c\u6234\u5747\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u770b\u6765\u8fd9\u5b97\u707e\u7978\u53ef\u4ee5\u907f\u8fc7 \u4e86\uff0c\u4ed6\u4eec\u770b\u4e0d\u51fa\u7834\u7efd\uff0c\u73b0\u5728\u5df2\u7ecf\u51c6\u5907\u8d70\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4ece\u95e8\u7f1d\u5077\u770b\u51fa\u53bb\uff0c\u53ea\u89c1\u6234\u5747\u7684\u957f\u5b50\u6234\u8c1f\u62ab\u9ebb\u5e26\u5b5d\u5b88\u5728\u7075\u524d\uff0c\u53f2\u767d\u90fd\u4e0e\u4e01\u5f6d\u5e76\u80a9\u800c \u7acb\uff0c\u9762\u5bf9\u68fa\u6750\uff0c\u7ad9\u5728\u4ed6\u4eec\u540e\u9762\u7684\u662f\u9752\u7b26\u9053\u4eba\u548c\u7126\u78ca\u3002 \u3000\u3000\u6b64\u65f6\u521a\u597d\u542c\u5f97\u53f2\u767d\u90fd\u8bf4\u9053\uff1a\u201c\u6234\u8001\u7237\u5b50\u7684\u5a01\u540d\u6211\u662f\u4ef0\u5e55\u5df2\u4e45\u7684\u4e86\uff0c\u53ef\u60dc\u7adf\u662f\u4e0d\u53ca\u4e00\u89c1\uff0c \u6234\u8001\u5df2\u7136\u4ed9\u6e38\uff0c\u771f\u662f\u6bd5\u751f\u9057\u61be\uff01\u4e01\u5f6d\uff0c\u4f60\u7ed9\u6211\u4e0a\u9999\uff0c\u5f85\u6211\u884c\u4e2a\u793c\u5427\uff01\u201d \u3000\u3000\u6234\u8c1f\u5fd9\u9053\uff1a\u201c\u4e0d\u6562\u5f53\u53f2\u5e2e\u4e3b\u7684\u5927\u793c\u3002\u201d\u53f2\u767d\u90fd\u9053\uff1a\u201c\u4ee4\u5c0a\u662f\u6b66\u6797\u524d\u8f88\uff0c\u6211\u4eca\u65e5\u7279\u6765\u62dc \u8c12\uff0c\u65e2\u662f\u751f\u524d\u4e0d\u80fd\u76f8\u89c1\uff0c\u5c31\u5f53\u4f5c\u662f\u6765\u540a\u4e27\u5427\u3002\u8fd9\u4e2a\u793c\u662f\u4e0d\u80fd\u5e9f\u7684\uff01\u201d\u8bf4\u7f62\uff0c\u606d\u606d\u656c\u656c\u7684\u4f5c\u4e86 \u4e09\u4e2a\u957f\u63d6\u3002 \u3000\u3000\u6234\u8c1f\u53ea\u597d\u5728\u7075\u65c1\u966a\u793c\uff0c\u53ea\u89c9\u638c\u98ce\u522e\u9762\uff0c\u9690\u9690\u751f\u75db\u3002\u6234\u8c1f\u5fcd\u7740\u6012\u6c14\uff0c\u5fc3\u4e2d\u60f3\uff1a\u201c\u5e78\u597d\u68fa\u6750 \u91cc\u88c5\u7684\u662f\u7816\u5934\uff0c\u5426\u5219\u5c31\u8981\u906d\u4e86\u8fd9\u53ae\u6bc1\u5c38\u7684\u6bd2\u624b\u4e86\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u4f5c\u4e86\u4e09\u4e2a\u63d6\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e01\u5f6d\uff0c\u4e00\u6b7b\u767e\u4e86\uff0c\u4f60\u548c\u6234\u5bb6\u7684\u6881\u5b50\u5c31\u6b64\u4f5c\u7ed3\uff0c\u4e0d\u8bb8\u4f60 \u518d\u591a\u4e8b\u4e86\uff0c\u8d70\u5427\uff01\u201d \u3000\u3000\u5916\u9762\u7684\u6234\u8c1f\uff0c\u91cc\u9762\u7684\u6234\u5747\u90fd\u677e\u4e86\u53e3\u6c14\u3002\u5374\u4e0d\u6599\u5c31\u5728\u6234\u8c1f\u6b63\u8981\u9001\u5ba2\u4e4b\u65f6\uff0c\u5ffd\u7136\u542c\u5230\u4e86\u5927\u95e8 \u5916\u6572\u95e8\u7684\u58f0\u97f3\u3002 \u3000\u3000\u9f13\u6f20\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u51e0\u65e5\u98ce\u58f0\u6b63\u7d27\uff0c\u76f8\u719f\u7684\u670b\u53cb\u6211\u90fd\u5df2\u6697\u5730\u91cc\u6253\u4e86\u62db\u547c\uff0c\u4ed6 \u4eec\u662f\u51b3\u4e0d\u4f1a\u5230\u8fd9\u91cc\u6765\u7684\u3002\u8fd9\u4e0d\u901f\u4e4b\u5ba2\uff0c\u5374\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u4f60\u6709\u5ba2\u4eba\uff0c\u6211\u4e0d\u6253\u6270\u4f60\u4e86\u3002\u4f60\u4e5f\u4e0d\u7528\u5ba2\u6c14\u9001\u6211\uff0c\u4f60\u62db\u5f85\u4f60\u7684\u5ba2\u4eba \u5427\u3002\u201d \u3000\u3000\u6234\u8c1f\u521a\u521a\u8d70\u51fa\u9662\u5b50\uff0c\u53f2\u767d\u90fd\u7b49\u4eba\u4e5f\u8d70\u4e0b\u53f0\u9636\u8ddf\u5728\u4ed6\u7684\u540e\u5934\uff0c\u53ea\u542c\u5f97\u201c\u4e53\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u5927 \u95e8\u5df2\u7ecf\u7ed9\u5ba2\u4eba\u63a8\u5f00\uff0c\u90a3\u4eba\u8d70\u4e86\u8fdb\u6765\uff0c\u56b7\u9053\uff1a\u201c\u6709\u4f4d\u91d1\u9010\u6d41\u5728\u8fd9\u91cc\u5417\uff1f\u201d \u3000\u3000\u6765\u7684\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u674e\u5357\u751f\u3002\u674e\u5357\u661f\u662f\u4e2a\u4e0d\u901a\u4e16\u6545\u7684\u4eba\uff0c\u4ed6\u6025\u4e8e\u77e5\u9053\u91d1\u9010\u6d41\u7684\u6d88\u606f\uff0c\u6572 \u4e86\u95e8\u4e0d\u89c1\u6709\u4eba\u7b54\u5e94\uff0c\u8feb\u4e0d\u53ca\u5f85\uff0c\u5c31\u5f84\u81ea\u95ef\u8fdb\u6765\u4e86\u3002 \u3000\u3000\u674e\u5357\u661f\u8fd9\u4e48\u4e00\u56b7\uff0c\u53eb\u51fa\u4e86\u91d1\u9010\u6d41\u7684\u540d\u5b57\uff0c\u5c4b\u91cc\u5c4b\u5916\uff0c\u51e0\u4e2a\u4eba\u90fd\u662f\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u674e\u5357\u661f\u8fd9\u65f6\u624d\u53d1\u73b0\u4e86\u53f2\u767d\u90fd\uff0c\u4ed6\u5f53\u7136\u662f\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u4f1a\u5728\u8fd9\u91cc\u78b0\u89c1\u53f2\u767d\u90fd\u7684\uff0c\u8fd9\u5239\u90a3\u95f4 \u4e0d\u89c9\u4e5f\u662f\u5446\u4e86\u3002 \u3000\u3000\u6234\u8c1f\u8bb7\u94a0\u8bf4\u9053\uff1a\u201c\u4f60\u627e\u9519\u4eba\u5bb6\u4e86\u5427\uff1f\u8fd9\u662f\u59d3\u6234\u7684\u5bb6\u91cc\uff0c\u5e76\u6ca1\u6709\u59d3\u91d1\u7684\u4eba\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u662f\u4eba\u751f\u4f55\u5904\u4e0d\u76f8\u9022\uff1f\u674e\u5144\uff0c\u4f60\u7684\u4f24\u597d\u4e86\u5417\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u624b\u6309\u5251\u67c4\uff0c\u4e5f\u662f\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u867d\u7136\u662f\u627e\u9519\u4eba\u5bb6\uff0c\u4f46\u627e\u7740\u4e86\u4f60\uff0c\u4e5f\u662f\u4e0d\u865a \u6b64\u884c\u4e86\u3002\u4f60\u7ba1\u6211\u6709\u4f24\u6ca1\u4f24\uff0c\u4f60\u5212\u51fa\u9053\u6765\uff0c\u4e0d\u8bba\u660e\u67aa\u6697\u7bad\uff0c\u6211\u90fd\u820d\u547d\u5949\u966a\u5c31\u662f\u3002\u201d\u674e\u5357\u661f\u8fd9\u756a \u8bf4\u8bdd\u5012\u662f\u9887\u80fd\u4e34\u673a\u5e94\u53d8\uff0c\u4e00\u9762\u66ff\u6234\u8c1f\u8f6c\u56fe\u3002\u4e00\u9762\u53c8\u8bbd\u523a\u4e86\u53f2\u767d\u90fd\u90a3\u65e5\u5728\u80cc\u540e\u4f24\u4eba\u4e4b\u4e8b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u60ca\u559c\u4ea4\u96c6\uff0c\u63d0\u8d77\u5b9d\u5251\uff0c\u5c31\u8981\u51fa\u53bb\uff0c\u5374\u7ed9\u6234\u5747\u6309\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u672a\u5230\u65f6\u5019\uff0c\u518d\u770b\u4e00 \u4f1a\u3002\u53f2\u767d\u90fd\u4f3c\u4e4e\u5e76\u4e0d\u60f3\u548c\u4f60\u8fd9\u4f4d\u670b\u53cb\u52a8\u624b\u3002\u201d \u3000\u3000\u6234\u5747\u8001\u4e8e\u4e16\u6545\uff0c\u5bdf\u8a00\u9274\u8272\uff0c\u6599\u5f97\u5f88\u51c6\u3002\u53f2\u767d\u90fd\u679c\u7136\u662f\u522b\u6709\u56fe\u8c0b\uff0c\u5e76\u4e0d\u60f3\u548c\u674e\u5357\u661f\u52a8\u624b\u3002 \u3000\u3000\u53ea\u542c\u5f97\u53f2\u767d\u90fd\u53c8\u662f\u4e00\u9635\u54c8\u54c8\u5927\u7b11\u3002\u7b11\u8fc7\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u5144\uff0c\u54b1\u4eec\u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\uff0c\u53f2\u67d0 \u5012\u662f\u4f69\u670d\u4f60\u8fd9\u80a1\u5b81\u6298\u4e0d\u5f2f\u7684\u813e\u6c14\u3002\u53ef\u662f\u4f60\u73b0\u5728\u53ea\u597d\u4e86\u4e03\u6210\uff0c\u5b9e\u662f\u4e0d\u5b9c\u548c\u4eba\u4ea4\u624b\uff0c\u6211\u53f2\u767d\u90fd\u4e5f \u662f\u4e0d\u613f\u4e58\u4eba\u4e4b\u5371\uff0c\u5373\u4f7f\u4f60\u8981\u5212\u51fa\u9053\u6765\uff0c\u6211\u4e5f\u662f\u4e0d\u80af\u5949\u966a\u7684\u4e86\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u613f\u4e58\u4eba\u4e4b\u5371\uff1f\u8fd9\u8bdd\u8bf4\u5f97\u5012\u662f\u5f88\u6f02\u4eae\u5440\uff01\u60f3\u4e0d\u5230\u6570\u65e5\u4e0d\u89c1\uff0c\u4f60\u5c45\u7136 \u53d8\u6210\u4e86\u2018\u6b63\u4eba\u541b\u5b50\u2019\u4e86\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u539a\u7740\u9762\u76ae\uff0c\u4e0d\u7406\u4ed6\u7684\u8bbd\u523a\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u65e5\u6709\u5e05\u5b5f\u96c4\u5728\u573a\uff0c\u6211\u662f\u4e0d\u5f97\u4e0d\u7136\u3002\u5982\u4eca\u6211 \u5012\u662f\u6709\u4e8b\u5949\u5546\uff0c\u4e0d\u77e5\u674e\u5144\u80af\u4e0d\u80af\u7b54\u5141\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u53f2\u5927\u5e2e\u4e3b\u6709\u6743\u6709\u52bf\u3002\u9ed1\u9053\u767d\u9053\uff0c\u8def\u8def\u7686\u901a\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\u8fd8\u8981\u6c42 \u6211\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u7b11\u9053\uff1a\u201c\u662f\u6211\u4e3a\u820d\u59b9\u6c42\u4f60\u3002\u6211\u4e0d\u6015\u5bf9\u4f60\u8bf4\u5b9e\u8bdd\uff0c\u6211\u672c\u6765\u662f\u4e0d\u559c\u6b22\u5979\u548c\u4f60\u7ed3\u4ea4\u7684\u3002 \u53ef\u662f\u8c01\u53eb\u6211\u548c\u5979\u662f\u5144\u59b9\u5462\uff0c\u6211\u53ea\u6b64\u4e00\u59b9\uff0c\u5979\u5bf9\u4f60\u5ff5\u5ff5\u4e0d\u5fd8\uff0c\u6211\u4e5f\u53ea\u597d\u6210\u5168\u5979\u4e86\u3002\u6211\u6253\u7b97\u660e\u5929 \u56de\u53bb\u3002\u548c\u820d\u59b9\u655e\u5f00\u5fc3\u8179\u4e00\u8c08\uff0c\u5e0c\u671b\u4f60\u4e5f\u80fd\u591f\u5728\u573a\u3002\u201d\u53f2\u767d\u90fd\u867d\u7136\u6ca1\u6709\u660e\u8bf4\u51fa\u6765\uff0c\u4f46\u8bdd\u4e2d\u4e4b \u610f\uff0c\u663e\u7136\u662f\u8981\u548c\u674e\u5357\u661f\u5546\u8c08\u59b9\u59b9\u7684\u5a5a\u4e8b\u4e86\u3002 \u3000\u3000\u674e\u5357\u661f\u60f3\u4e0d\u5230\u53f2\u767d\u90fd\u4f1a\u5bf9\u4ed6\u8bf4\u51fa\u8fd9\u756a\u8bf4\u8bdd\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u662f\u8981\u9a97\u6211\u5230 \u4f60\u4eec\u7684\u516d\u5408\u5e2e\u603b\u8235\u53bb\uff01\u563f\uff0c\u563f\uff01\u6211\u672c\u6765\u662f\u8981\u53bb\u7684\uff0c\u4f60\u8bbe\u4e0b\u5708\u5957\u6211\u4e5f\u4e0d\u60e7\uff01\u53ea\u662f\u6211\u72af\u4e0d\u7740\u548c\u4f60 \u540c\u884c\u3002\u6211\u8981\u53bb\u6211\u81ea\u5df1\u4f1a\u53bb\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u5f1f\u597d\u80c6\u91cf\uff01\u53ef\u662f\u4f60\u5374\u8bef\u4f1a\u4e86\u3002\u6211\u662f\u51fa\u4e8e\u7231\u59b9\u4e4b\u5fc3\uff0c\u624d\u9080\u8bf7\u4f60\u8fd9\u4f4d \u8d35\u5ba2\u7684\uff0c\u6211\u8fd8\u80fd\u591f\u9677\u5bb3\u4f60\u5417\uff1f\u597d\uff0c\u4f60\u65e2\u7136\u4eca\u65e5\u4e0d\u80fd\u52a8\u8eab\uff0c\u6211\u5148\u56de\u53bb\uff0c\u5728\u655d\u5e2e\u603b\u8235\u606d\u5019\u4f60\u7684\u5927 \u9a7e\u5c31\u662f\uff0c\u5f53\u7136\uff0c\u4f60\u82e5\u662f\u4fe1\u6211\u4e0d\u8fc7\uff0c\u6015\u6211\u9a97\u4f60\uff0c\u6765\u4e0d\u6765\u90a3\u4e5f\u968f\u4f60\u7684\u4fbf\uff01\u201d \u3000\u3000\u674e\u5357\u661f\u7ed9\u4ed6\u4e00\u6fc0\uff0c\u6c14\u51b2\u51b2\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u7b49\u7740\u5427\uff0c\u6211\u4e00\u5b9a\u8d74\u4f60\u7684\u7ea6\uff0c\u522b\u8bf4\u4f60\u53f2\u767d\u90fd\uff0c\u5c31\u662f \u960e\u738b\u8001\u5b50\u4e0b\u4e86\u8bf7\u5e16\uff0c\u6211\u674e\u67d0\u4eba\u4e5f\u6562\u5230\u9b3c\u95e8\u5173\u95ef\u5b83\u4e00\u95ef\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u7b11\u9053\uff1a\u201c\u58ee\u54c9\uff0c\u58ee\u54c9\uff01\u602a\u4e0d\u5f97\u820d\u59b9\u5bf9\u4f60\u5fc3\u6298\uff0c\u6211\u4e5f\u4e0d\u80fd\u4e0d\u4f69\u670d\u4f60\u4e86\u3002\u4eca\u65e5\u65e0\u6687\u591a \u8c08\uff0c\u5f85\u4f60\u6765\u5230\u655d\u5e2e\uff0c\u4f60\u5c31\u53ef\u4ee5\u77e5\u6211\u53f2\u67d0\u4eba\u662f\u597d\u5fc3\u8fd8\u662f\u574f\u610f\u4e86\u3002\u597d\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u9752\u7b26\u9053\u4eba\u548c\u7126\u78ca\u8bf4\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u54b1\u4eec\u5c31\u8d70\u4e86\u4e48\uff1f\u90a3\uff0c\u90a3\u4e2a\u59d3\u91d1\u7684\u5c0f\u5b50\u2026\u2026\u201d \u3000\u3000\u53f2\u767d\u90fd\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\u3002\u4f60\u4eec\u4e0d\u8bf4\u6211\u51e0\u4e4e\u5fd8\u4e86\u3002\u5c11\u9556\u5934\uff0c\u771f\u4eba\u9762\u524d\u4e0d\u8bf4\u5047 \u8bdd\uff0c\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\u548c\u6211\u6709\u70b9\u8fc7\u8282\uff0c\u4ed6\u65e2\u662f\u5728\u4f60\u8fd9\u91cc\uff0c\u5c31\u8bf7\u4f60\u628a\u4ed6\u4ea4\u51fa\u6765\u5427\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u6211\u627e\u9519\u4eba\u5bb6\uff0c\u4f60\u903c\u6234\u5c11\u9556\u5934\u4f5c\u751a\uff1f\u4f60\u60f3\u8981\u4eba\uff0c\u95ee\u6211\u5c31\u662f\uff01\u201d \u3000\u3000\u7126\u78ca\u662f\u4e2a\u83bd\u6c49\uff0c\u4ed6\u66fe\u7ecf\u4e24\u6b21\u5403\u8fc7\u91d1\u9010\u6d41\u7684\u4e8f\uff0c\u6068\u4e0d\u5f97\u5e2e\u4e3b\u7ed9\u4ed6\u62a5\u4ec7\uff0c\u662f\u674e\u5357\u661f\u51fa\u5934\u63fd \u4e8b\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u679c\u7136\u5c31\u95ee\u674e\u5357\u661f\u8981\u4eba\u3002 \u3000\u3000\u674e\u5357\u661f\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u77e5\u9053\u91d1\u9010\u6d41\u7684\u4e0b\u843d\uff0c\u4f46\u4f60\u8981\u6211\u544a\u8bc9\u4f60\uff0c\u53ef\u8fd8\u5f97\u5148\u95ee\u8fc7\u6211\u4e00 \u4f4d\u670b\u53cb\uff01\u201d \u3000\u3000\u7126\u78ca\u53cc\u773c\u4e00\u7ffb\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4f4d\u670b\u53cb\uff0c\u4f60\u53eb\u4ed6\u6765\uff01\u201d \u3000\u3000\u674e\u5357\u661f\u62cd\u4e00\u62cd\u8170\u60ac\u7684\u957f\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u91cc\uff0c\u8bf7\u4f60\u5148\u95ee\u4e00\u95ee\u6211\u8fd9\u4e00\u67c4\u5251\u7b54\u4e0d\u7b54\u5e94\uff01 \u3000\u3000\u7126\u78ca\u5927\u6012\uff0c\u5c31\u8981\u53d1\u4f5c\uff0c\u53f2\u767d\u90fd\u559d\u9053\uff1a\u201c\u6709\u6211\u5728\u6b64\uff0c\u7528\u4e0d\u7740\u4f60\u8bf4\u8bdd\uff0c\u4f60\u7ed9\u6211\u9000\u4e0b\uff01\u201d\u65a5\u9000 \u4e86\u7126\u78ca\u4e4b\u540e\uff0c\u5ffd\u5730\u9634\u9633\u602a\u6c14\u7684\u5411\u674e\u5357\u661f\u5927\u7b11\u4e09\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u9519\u4e86\uff01\u201d \u3000\u3000\u674e\u5357\u661f\u6012\u9053\uff1a\u201c\u6211\u600e\u5730\u9519\u4e86\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u5bf9\u4ed6\u5012\u662f\u5f88\u8bb2\u4e49\u6c14\uff0c\u53ef\u60dc\u2026\u2026\u201d\u674e\u5357\u661f\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u5fc3\u60f3\uff1a\u201c\u6211\u4e5f\u4e0d\u597d\u8bf4\u5f97\u5927\u8fc7\u9732\u9aa8\uff0c\u4e14\u5148\u5f15\u8d77\u4ed6\u7684\u731c\u7591\u5c31\u662f\uff0c\u514d\u5f97\u5931\u4e86\u6211\u4e00\u5e2e\u4e4b\u4e3b \u7684\u8eab\u4efd\u3002\u201d\u5f53\u4e0b\u76ae\u7b11\u8089\u4e0d\u7b11\u7684\u53c8\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u4e0d\u8fc7\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\u5bf9\u820d\u59b9 \u5b9e\u662f\u4e0d\u6000\u597d\u610f\uff0c\u66fe\u7ecf\u5728\u820d\u59b9\u624b\u4e0a\u9a97\u53d6\u4e86\u6211\u516d\u5408\u5e2e\u7684\u5b9d\u7269\uff0c\u6b64\u4e8b\u60f3\u6765\u4f60\u4e5f\u672a\u77e5\u5427\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u83ab\u540d\u5176\u5999\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u548c\u6211\u6709\u4ec0\u4e48\u76f8\u5e72\uff1f\u4f60\u4eec\u516d\u5408\u5e2e\u7684\u4e0d\u4e49\u4e4b\u8d22\uff0c\u4eba\u4eba\u53ef\u53d6\u3002\u91d1 \u9010\u6d41\u5077\u4e5f\u597d\uff0c\u9a97\u4e5f\u597d\uff0c\u4f9d\u6211\u770b\u6765\uff0c\u90fd\u662f\u5e94\u8be5\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u4f60\u504f\u8892\u8fd9\u5c0f\u5b50\u6211\u6ca1\u8bdd\u8bf4\u3002\u4f46\u6b64\u4e8b\u6211\u5374\u662f\u975e\u8ffd\u7a76\u4e0d\u53ef\uff01\u90a3\u5757\u7384\u94c1\u6211\u662f\u8981\u53d6\u56de \u6765\u94f8\u6210\u5b9d\u5251\uff0c\u7ed9\u820d\u59b9\u4f5c\u5ac1\u5986\u7684\uff0c\u53ef\u4e0d\u80fd\u843d\u5728\u8fd9\u9a97\u5b50\u4e4b\u624b\u3002\u5c11\u9556\u5934\uff0c\u4e0d\u662f\u6211\u4fe1\u4e0d\u8fc7\u4f60\uff0c\u6211\u4eca\u65e5 \u65e2\u7136\u662f\u5230\u4e86\u8fd9\u513f\uff0c\u53c8\u5f97\u4e86\u8fd9\u70b9\u7ebf\u7d22\uff0c\u597d\u574f\u662f\u8981\u641c\u4e0a\u4e00\u641c\u7684\u3002\u201d \u3000\u3000\u6234\u8c1f\u6012\u9053\uff1a\u201c\u6211\u6234\u5bb6\u5c82\u662f\u5bb9\u4eba\u4e71\u641c\u7684\u4e48\u3002\u201d\u674e\u5357\u661f\u4e5f\u52a8\u4e86\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6709\u8bdd\uff0c\u4f60\u8981\u6293 \u91d1\u9010\u6d41\uff0c\u51b2\u7740\u6211\u6765\u5c31\u662f\uff01\u201d \u3000\u3000\u53cc\u65b9\u6b63\u5728\u5435\u95f9\uff0c\u5ffd\u542c\u5f97\u91d1\u9010\u6d41\u7684\u58f0\u97f3\u7b11\u9053\uff1a\u201c\u53f2\u5927\u5e2e\u4e3b\uff0c\u4e0d\u7528\u8d39\u795e\uff0c\u91d1\u9010\u6d41\u6765\u4f1a\u4f60 \u4e86\u3002\u201d \u3000\u3000\u5927\u7b11\u58f0\u4e2d\uff0c\u53ea\u89c1\u91d1\u9010\u6d41\u624b\u4e2d\u63d0\u7740\u4e00\u628a\u5149\u8292\u95ea\u95ea\u7684\u5b9d\u5251\uff0c\u7f13\u7f13\u8d70\u4e0b\u53f0\u9636\u3002\u9752\u7b26\u4e0e\u7126\u78ca\u4ed7\u7740 \u6709\u5e2e\u4e3b\u6491\u8170\uff0c\u4e0d\u77e5\u91d1\u9010\u6d41\u8fd9\u628a\u7384\u94c1\u5b9d\u5251\u7684\u5389\u5bb3\uff0c\u9752\u7b26\u9053\u4eba\u7684\u957f\u5251\uff0c\u7126\u78ca\u7684\u539a\u80cc\u65ab\u5c71\u5200\uff0c\u4e0d\u7ea6 \u800c\u540c\u7684\u5c31\u5411\u91d1\u9010\u6d41\u5288\u523a\u8fc7\u53bb\uff01 \u3000\u3000\u53ea\u89c1\u5bd2\u5149\u4e00\u95ea\uff0c\u201c\u94db\uff0c\u94db\u201d\u4e24\u58f0\u3002\u4e00\u5200\u4e00\u5251\u90fd\u53ea\u5269\u4e0b\u4e86\u534a\u622a\u3002\u9752\u7b26\u9053\u4eba\u7684\u957f\u5251\u4e5f\u8fd8\u7f62 \u4e86\uff0c\u7126\u78ca\u90a3\u628a\u539a\u80cc\u65ab\u5c71\u5200\u53ef\u662f\u6709\u51e0\u5341\u65a4\u91cd\u7684\uff0c\u5982\u4eca\u7adf\u662f\u7ed9\u7384\u94c1\u5b9d\u5251\u6709\u5982\u6467\u67af\u62c9\u673d\u4e00\u822c\uff0c\u4e00\u524a \u5373\u65ad\uff01\u8fd9\u4e00\u6765\uff0c\u4e0d\u4f46\u9752\u7b26\u7126\u78ca\u5446\u82e5\u6728\u9e21\uff0c\u8fde\u53f2\u767d\u90fd\u4e5f\u662f\u77eb\u820c\u96be\u4e0b\u3002 \u3000\u3000\u53f2\u767d\u90fd\u5f53\u7136\u77e5\u9053\u8fd9\u662f\u7384\u94c1\u94f8\u6210\u7684\u5b9d\u5251\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5f97\u53c8\u7fa1\u53c8\u5992\u53c8\u607c\u53c8\u6068\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50 \u5012\u662f\u795e\u901a\u5e7f\u5927\uff0c\u4e0d\u77e5\u4ed6\u5728\u54ea\u91cc\u627e\u6765\u7684\u9ad8\u660e\u70bc\u5251\u5e08\uff0c\u8fd9\u4e48\u5feb\u5c31\u7ed9\u4ed6\u94f8\u6210\u4e86\u7384\u94c1\u5b9d\u5251\u4e86\u3002\u5982\u4eca\u4ed6 \u6709\u5229\u5668\u5728\u624b\uff0c\u53ea\u6015\u6211\u4e5f\u672a\u5fc5\u8d62\u5f97\u4e86\u4ed6\u3002\u201d\u672c\u6765\u53f2\u767d\u90fd\u5728\u8981\u641c\u91d1\u9010\u6d41\u4e4b\u65f6\uff0c\u662f\u81ea\u4ee5\u4e3a\u53ef\u4ee5\u7a33\u64cd \u80dc\u7b97\u7684\uff0c\u4ed6\u7684\u529f\u529b\u80dc\u91d1\u9010\u6d41\u4e0d\u6b62\u4e00\u7b79\uff0c\u800c\u674e\u5357\u661f\u53c8\u662f\u4f24\u8fd8\u672a\u6108\uff0c\u53ea\u8981\u9752\u7b26\u3001\u7126\u78ca\u4efb\u4f55\u4e00\u4e2a\u4e00 \u90fd\u53ef\u4ee5\u5bf9\u4ed8\u5f97\u4e86\u3002\u4f46\u73b0\u5728\u91d1\u9010\u6d41\u624b\u4e2d\u6709\u4e86\u5b9d\u5251\uff0c\u53f2\u767d\u90fd\u5374\u662f\u4e0d\u80fd\u4e0d\u6709\u51e0\u5206\u987e\u5fcc\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u6709\u6240\u987e\u5fcc\uff0c\u60f3\u62a2\u91d1\u9010\u6d41\u7684\u5b9d\u5251\u53c8\u4e0d\u6562\u62a2\u3002\u91d1\u9010\u6d41\u626c\u8d77\u5b9d\u5251\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53f2\u5927\u5e2e \u4e3b\uff0c\u4f60\u8981\u548c\u6211\u4ea4\u624b\u4e48\uff0c\u8bf7\u7b49\u4e00\u4f1a\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7eb3\u5251\u5165\u9798\uff0c\u8d70\u5230\u674e\u5357\u661f\u9762\u524d\u8bf4\u9053\uff1a\u201c\u516d\u54e5\uff0c\u8bf7\u6536\u4e0b\u8fd9\u67c4\u7384\u94c1\u5b9d\u5251\uff01\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u4f60\u9001\u7ed9\u6211\uff1f\u8fd9\u4e2a\u2026\u2026\u201d\u674e\u5357\u661f\u6b64\u65f6\u5df2\u77e5\u8fd9\u662f\u7384\u94c1\u5b9d\u5251\uff0c\u9976\u662f\u4ed6\u8d4b\u6027\u6d12\u8131\uff0c \u4e5f\u89c9\u5f97\u8fd9\u4e2a\u793c\u7269\u592a\u8fc7\u73cd\u8d35\uff01\u4e8c\u6765\u53f2\u767d\u90fd\u521a\u624d\u8a00\u8bf4\u4ed6\u662f\u8981\u628a\u7384\u94c1\u5b9d\u5251\u5f53\u4f5c\u59b9\u59b9\u7684\u5ac1\u5986\u7684\uff0c\u5982\u4eca \u4ece\u91d1\u9010\u6d41\u7684\u624b\u4e0a\u4ea4\u7ed9\u4ed6\uff0c\u4ed6\u4e5f\u662f\u4e0d\u5927\u597d\u610f\u601d\u63a5\u53d7\u3002 \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u96be\u9053\u4f60\u4e5f\u6709\u4e86\u4e16\u4fd7\u4e4b\u89c1\u4e86\uff1f\u90a3\u4f60\u9001\u6211\u7684\u53e4\u7434\uff0c\u6211\u5df2\u53d7\u4e0b\uff0c\u5982 \u4eca\u6211\u6295\u6843\u62a5\u674e\uff0c\u4f60\u5c82\u80fd\u4e0d\u53d7\uff1f\u4e0d\u9519\uff0c\u8fd9\u67c4\u5b9d\u5251\u662f\u7a00\u4e16\u4e4b\u73cd\uff0c\u4f46\u4f60\u6211\u7684\u4ea4\u60c5\u66f4\u80dc\u4e8e\u5341\u67c4\u8fd9\u6837\u7684 \u5b9d\u5251\uff01\u5927\u54e5\uff0c\u96be\u9053\u4f60\u4f1a\u91cd\u7269\u8f7b\u4eba\uff0c\u8fd9\u6837\u5c31\u662f\u5927\u5c0f\u89d1\u6211\u4e86\uff01\u201d\u90a3\u65e5\uff0c\u5728\u957f\u57ce\u4e0a\u91d1\u9010\u6d41\u5a49\u8f9e\u674e\u5357 \u661f\u6240\u8d60\u7684\u53e4\u7434\u4e4b\u65f6\uff0c\u674e\u5357\u661f\u4e5f\u66fe\u8bf4\u8fc7\u5927\u610f\u76f8\u540c\u7684\u4e00\u756a\u8bf4\u8bdd\uff0c\u73b0\u5728\u91d1\u9010\u6d41\u8d60\u4ed6\u5b9d\u5251\uff0c\u5c31\u5957\u7528\u4ed6 \u7684\u8bdd\u610f\u201c\u56de\u656c\u201d\u53eb\u4ed6\u63a8\u8f9e\u4e0d\u6389\u3002 \u3000\u3000\u674e\u5357\u661f\u5341\u5206\u611f\u52a8\uff0c\u63a5\u8fc7\u5b9d\u5251\u7b11\u9053\uff1a\u201c\u597d\u5144\u5f1f\uff0c\u597d\uff0c\u6211\u53d7\u4e0b\u4e86\uff01\u53ef\u662f\uff0c\u6211\u53d7\u4e86\u4f60\u7684\u5b9d\u5251\uff0c \u8fd9\u4e00\u67b6\uff0c\u4f60\u975e\u5f97\u8ba9\u6211\u66ff\u4f60\u6765\u6253\u4e0d\u6210\u3002\u201d \u3000\u3000\u5c31\u5728\u674e\u5357\u661f\u63a5\u8fc7\u5b9d\u5251\u4e4b\u65f6\uff0c\u5ffd\u542c\u5f97\u6709\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u58f0\u97f3\u4e2d\u5145\u6ee1\u4e86\u8be7\u5f02\uff0c \u53c8\u9690\u9690\u542b\u6709\u51e0\u5206\u53f9\u606f\u7684\u5473\u9053\u3002 \u3000\u3000\u91d1\u9010\u6d41\u56de\u5934\u4e00\u770b\uff0c\u539f\u6765\u662f\u5510\u6770\u592b\u4e5f\u51fa\u6765\u4e86\uff0c\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u7ed9\u4f60\u4ecb\u7ecd\u4ecb\u7ecd\uff0c\u8fd9 \u4f4d\u8001\u524d\u8f88\u662f\u56db\u5ddd\u6b66\u6797\u540d\u5bbf\u5510\u6770\u592b\u5510\u5927\u4fa0\u3002\u7384\u94c1\u5b9d\u5251\u662f\u4ed6\u8d39\u5c3d\u82e6\u5fc3\u94f8\u70bc\u7684\u3002\u54b1\u4eec\u90fd\u5e94\u8be5\u62dc\u8c22 \u4ed6\u3002\u201d \u3000\u3000\u5510\u6770\u592b\u634b\u987b\u7b11\u9053\uff1a\u201c\u5b9d\u5251\u8d60\u77e5\u5df1\uff0c\u7ea2\u7c89\u8d60\u4f73\u4eba\u3002\u8fd9\u4e5f\u662f\u6b66\u6797\u4f73\u8bdd\uff0c\u4f46\u613f\u674e\u5c11\u4fa0\u597d\u597d\u73cd\u60dc \u8fd9\u628a\u5b9d\u5251\uff01\u201d\u539f\u6765\u5510\u6770\u592b\u89c1\u91d1\u9010\u6d41\u5c06\u4ed6\u82e6\u5fc3\u94f8\u70bc\u7684\u5b9d\u5251\u6168\u8d60\u53cb\u4eba\uff0c\u5fc3\u91cc\u5b9e\u662f\u4e0d\u65e0\u51e0\u5206\u53f9\u606f\u4e4b \u611f\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5374\u4e5f\u4e3a\u4ed6\u4eec\u7684\u53cb\u8c0a\u611f\u52a8\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u77e5\u9053\u5510\u6770\u592b\u662f\u6b66\u6797\u4e2d\u6570\u4e00\u6570\u4e8c\u7684\u6697\u5668\u540d\u5bb6\uff0c\u8bba\u8d77\u771f\u5b9e\u7684\u529f\u592b\uff0c\u53ea\u6015\u201c\u5343\u624b\u89c2\u97f3\u201d \u7948\u5723\u56e0\u8fd8\u6bd4\u4ed6\u4e0d\u4e0a\u3002\u89c1\u4ed6\u51fa\u73b0\uff0c\u4e0d\u89c9\u751a\u662f\u5c34\u5c2c\uff0c\u5f3a\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\u6709\u8fd9\u672c\u9886\uff0c\u539f\u6765\u662f\u4f60 \u8fd9\u8001\u513f\u70bc\u7684\u5b9d\u5251\u3002\u201d \u3000\u3000\u5510\u6770\u592b\u8bf4\u9053\uff1a\u201c\u6211\u7d2f\u6765\u7231\u7ba1\u95f2\u4e8b\uff0c\u4f55\u51b5\u70bc\u5251\u6b63\u662f\u6211\u5f97\u610f\u7684\u624b\u827a\uff0c\u65e2\u6709\u7384\u94c1\uff0c\u4e0d\u628a\u5b83\u94f8\u6210 \u5b9d\u5251\uff0c\u5c82\u4e0d\u662f\u66b4\u6b84\u5929\u7269\uff1f\u53f2\u5927\u5e2e\u4e3b\u4f60\u4e0d\u4f1a\u602a\u5c0f\u8001\u513f\u7231\u7ba1\u95f2\u4e8b\u5427\uff1f\u201d\u4ed6\u63a5\u8fde\u8bf4\u4e24\u53e5\u201c\u7231\u7ba1\u95f2 \u4e8b\u201d\uff0c\u8868\u9762\u542c\u6765\uff0c\u4f3c\u4e4e\u662f\u6307\u70bc\u5251\u4e4b\u4e8b\uff0c\u5176\u5b9e\u8bdd\u4e2d\u6709\u8bdd\uff0c\u4e0d\u557b\u662f\u6697\u4e2d\u8b66\u544a\u4e86\u53f2\u767d\u90fd\uff1a\u4f60\u82e5\u6700\u8981 \u52a8\u624b\u7684\u8bdd\uff0c\u53ea\u6015\u6211\u4e5f\u8981\u7ba1\u4e00\u7ba1\u8fd9\u4e2a\u95f2\u4e8b\u7684\u4e86\uff01 \u3000\u3000\u53f2\u767d\u90fd\u5bf9\u4ed8\u6301\u6709\u5b9d\u5251\u7684\u674e\u5357\u661f\u4e0e\u91d1\u9010\u6d41\uff0c\u5df2\u65e0\u53d6\u80dc\u628a\u63e1\uff0c\u5982\u4eca\u53c8\u51fa\u73b0\u4e86\u4e2a\u5510\u6770\u592b\uff0c\u53f2\u767d \u90fd\u5f53\u7136\u662f\u4e0d\u80fd\u4e0d\u66f4\u591a\u8003\u8651\u4e86\uff01 \u3000\u3000\u53f2\u767d\u5bb6\u7684\u5927\u95e8\u88ab\u674e\u5357\u661f\u63a8\u5f00\u4e4b\u540e\uff0c\u4e00\u76f4\u672a\u66fe\u5173\u4e0a\uff0c\u6b64\u65f6\u5ffd\u542c\u5f97\u8e44\u58f0\u5f97\u5f97\uff0c\u6709\u4e00\u5339\u65e0\u978d\u7684 \u767d\u9a6c\u7a81\u7136\u4ece\u5916\u9762\u8dd1\u4e86\u8fdb\u6765\uff0c\u95ef\u5230\u4e86\u53f2\u767d\u90fd\u7b49\u4eba\u6240\u5728\u7684\u9662\u5b50\uff0c\u9752\u7b26\u9053\u4eba\u5931\u58f0\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u5e2e \u4e3b\uff0c\u8fd9\u4e0d\u662f\u4f60\u7684\u90a3\u5339\u5750\u9a91\u5417\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u5339\u9a6c\u672c\u6765\u662f\u5173\u5728\u90bb\u5bb6\u7684\u9a6c\u53a9\u7684\uff0c\u4ed6\u542c\u5f97\u65e7\u4e3b\u4eba\u7684\u58f0\u97f3\uff0c\u8e22\u5f00\u4e86\u9a6c\u53a9\u90a3\u4e24\u6247\u865a\u63a9\u7684 \u6728\u95e8\uff0c\u8dd1\u6765\u627e\u4e3b\u4eba\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u96be\u5f97\u5b83\u604b\u6545\u4e3b\uff01\u201d\u4e00\u4e2a\u7ffb\u8eab\u8de8\u4e0a\u9a6c\u80cc\u8bf4\u9053\uff1a\u201c\u91d1\u9042\u6d41\uff0c\u8fd9\u628a \u7384\u94c1\u5b9d\u5251\u4f60\u65e2\u7136\u4ea4\u7ed9\u4e86\u5357\u661f\u8001\u5f1f\uff0c\u6b64\u4e8b\u751a\u5408\u543e\u5fc3\uff0c\u6211\u5c31\u4e0d\u8ffd\u7a76\u4f60\u4e86\u3002\u4f46\u8fd9\u5339\u5750\u9a91\uff0c\u4f60\u4e5f\u8be5\u5f52 \u8fd8\u6211\u5427\uff01\u201d\u53f2\u767d\u90fd\u8fd9\u756a\u8bf4\u8bdd\u5b9e\u662f\u85cf\u6709\u79bb\u95f4\u91d1\u3001\u674e\u4e8c\u4eba\u7684\u7528\u610f\uff0c\u4ed6\u521a\u624d\u8bf4\u8fc7\u7384\u94c1\u5b9d\u5251\u4ed6\u662f\u51c6\u5907 \u7ed9\u59b9\u59b9\u4f5c\u5ac1\u5986\u7684\uff0c\u5982\u4eca\u91d1\u9010\u6d41\u62ff\u6765\u9001\u7ed9\u4e86\u674e\u5357\u661f\uff0c\u4ed6\u5374\u8bf4\u201c\u751a\u5408\u543e\u5fc3\u201d\uff0c\u53e3\u6c14\u4e4b\u4e2d\uff0c\u5df2\u662f\u9690 \u85cf\u6709\u613f\u628a\u59b9\u59b9\u8bb8\u7ed9\u674e\u5357\u57a6\u4e4b\u610f\u3002\u91d1\u9010\u6d41\u542c\u4e86\uff0c\u4e0d\u7531\u5f97\u4e00\u9635\u832b\u7136\uff0c\u4e5f\u4e0d\u77e5\u662f\u6b22\u559c\u8fd8\u662f\u8f9b\u9178\uff0c\u53f2 \u767d\u90fd\u5df2\u7ecf\u5e26\u4e86\u4e01\u5f6d\u7b49\u4eba\u8d70\u4e86\u3002 \u3000\u3000\u674e\u5357\u661f\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u771f\u662f\u795e\u901a\u5e7f\u5927\uff0c\u76d7\u4e86\u516d\u5408\u5e2e\u7684\u7384\u94c1\u8fd8\u5077\u4e86\u4ed6\u4eec\u5e2e\u4e3b\u7684\u5750\u9a91\uff0c\u53f2 \u767d\u90fd\u4e5f\u7b97\u5f97\u662f\u8fde\u683d\u7b4b\u6597\u4e86\u3002\u4e0d\u8fc7\uff0c\u8fd9\u5339\u5750\u9a91\u795e\u9a8f\u975e\u5e38\uff0c\u4f60\u8fd8\u4e86\u7ed9\u4ed6\u5374\u662f\u53ef\u60dc\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u5339\u9a6c\u548c\u7384\u94c1\u4e0d\u540c\uff0c\u6211\u662f\u6709\u8a00\u5728\u5148\uff0c\u53ea\u501f\u4ed6\u7684\uff0c\u8fd8\u7ed9\u4ed6\u4e5f\u662f\u5e94\u8be5\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u4f46\u8fd9\u4e2a\u65f6\u5019\u8fd8\u4ed6\uff0c\u5374\u662f\u5bf9\u54b1\u4eec\u6709\u70b9\u4e0d\u5229\u3002\u4ed6\u6709\u4e86\u8fd9\u5339\u5b9d\u9a6c\uff0c\u5c31\u53ef\u4ee5\u63d0\u65e9\u8bb8 \u591a\u5929\u56de\u8f6c\u4ed6\u7684\u603b\u8235\u4e86\u3002\u201d\u91d1\u9010\u6d41\u8fd9\u624d\u660e\u767d\u4e86\u674e\u5357\u661f\u7684\u610f\u601d\uff0c\u539f\u6765\u4ed6\u662f\u4e3a\u4e86\u8425\u6551\u53f2\u7ea2\u82f1\u4e4b\u4e8b\u8bbe \u60f3\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9ed8\u7136\u4e0d\u8bed\uff0c\u674e\u5357\u661f\u7b11\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u6211\u5df2\u7ecf\u7b54\u5e94\u4e86\u53f2\u767d\u90fd\u4e4b\u7ea6\uff0c\u5c31\u8ba9\u4ed6\u5148\u56de\u53bb\u4e5f \u597d\u3002\u54b1\u4eec\u5149\u660e\u6b63\u5927\u7684\u8d74\u4f1a\u4e5f\u4e0d\u6015\u4ed6\u3002\u201d \u3000\u3000\u6234\u5747\u6b64\u65f6\u5df2\u7ecf\u51fa\u5230\u201c\u7075\u5802\u201d\uff0c\u626c\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u6765\uff0c\u770b\u770b\u53f2\u767d\u90fd\u7684\u72e0\u6bd2\u624b\u6bb5\uff01\u201d \u3000\u3000\u4f17\u4eba\u56de\u8f6c\u201c\u7075\u5802\u201d\uff0c\u53ea\u89c1\u6234\u5747\u63ed\u5f00\u68fa\u76d6\uff0c\u7b11\u9053\uff1a\u201c\u5047\u5982\u662f\u6211\u7761\u5728\u68fa\u4e2d\uff0c\u53ea\u6015\u65e9\u5df2\u662f\u7c89\u8eab \u788e\u9aa8\u4e86\uff01\u201d\u4f17\u4eba\u4e00\u770b\uff0c\u539f\u6765\u653e\u5728\u68fa\u6750\u91cc\u5341\u51e0\u5757\u7ed3\u5b9e\u7684\u9752\u7816\uff0c\u90fd\u5df2\u7c89\u788e\uff0c\u53d8\u4f5c\u4e86\u4e00\u6dee\u6ce5\u6c99\uff01\u4ee5 \u91d1\u521a\u638c\u529b\u6253\u788e\u9752\u7816\u4e0d\u96be\uff0c\u96be\u5728\u9694\u7740\u4e00\u5c42\u68fa\u6728\uff0c\u68fa\u6728\u4e1d\u6beb\u6ca1\u6709\u88c2\u5f00\uff0c\u91cc\u9762\u7684\u9752\u7816\u5219\u5df2\u7c89\u788e\uff0c\u8fd9 \u662f\u6700\u4e0a\u4e58\u7684\u201c\u9694\u7269\u6253\u529b\u201d\u7684\u5185\u529f\uff0c\u9976\u662f\u5510\u6770\u592b\u8fd9\u6837\u7684\u6b66\u6797\u540d\u5bbf\uff0c\u770b\u4e86\u4e5f\u4e0d\u7531\u5f97\u77eb\u820c\u96be\u4e0b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4ecb\u7ecd\u4e86\u674e\u5357\u661f\u4e0e\u4ed6\u7684\u5173\u7cfb\uff0c\u674e\u5357\u661f\u9053\u4e86\u4e2a\u6b49\uff0c\u8bf4\u9053\uff1a\u201c\u6234\u8001\u7237\u5b50\uff0c\u6211\u8fd9\u6b21\u6765\u5f97\u771f \u662f\u5927\u83bd\u649e\u4e86\u3002\u201d \u3000\u3000\u6234\u5747\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u907f\u5f00\u4e86\u4e00\u573a\u707e\u7978\uff01\u53c8\u7ed3\u8bc6\u4e86\u4e00\u4f4d\u5c11\u5e74\u8c6a\u6770\uff0c\u8fd9\u6b63\u662f\u53cc\u559c\u4e34\u95e8\u4e4b\u4e8b \u554a\u3002\u8001\u5f1f\uff0c\u4f60\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u4f60\u548c\u91d1\u9010\u6d41\u662f\u516b\u62dc\u4e4b\u4ea4\uff0c\u54b1\u4eec\u5c31\u90fd\u662f\u81ea\u5df1\u4eba\u4e00\u6837\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53f2\u767d\u90fd\u5728\u8fd9\u91cc\u53d1\u73b0\u4e86\u6211\uff0c\u6050\u6015\u4eca\u540e\u8fd8\u6709\u9ebb\u70e6\u3002\u6234\u8001\u7684\u8f88\uff0c\u6211\u8fde\u7d2f\u4e86\u4f60\uff0c\u5b9e \u5728\u8fc7\u610f\u4e0d\u53bb\uff0c\u4f60\u8001\u4eba\u5bb6\u6253\u7b97\u5982\u4f55\uff1f\u82e5\u6709\u9700\u7528\u5c0f\u4f84\u4e4b\u5904\u2026\u2026\u201d \u3000\u3000\u6234\u5747\u7709\u5934\u4e00\u76b1\uff0c\u968f\u5373\u6717\u58f0\u7b11\u9053\uff1a\u201c\u91d1\u8001\u5f1f\uff0c\u4f60\u5927\u7ea6\u4ee5\u4e3a\u6211\u662f\u5e74\u8001\u6015\u4e8b\uff0c\u6240\u4ee5\u624d\u8bc8\u6b7b\u7684 \u5427\uff1f\u4e0d\u9519\uff0c\u6211\u662f\u5e74\u8001\u4f53\u8870\uff0c\u975e\u590d\u5f53\u5e74\u548c\u4f60\u5e08\u5144\u5927\u52ab\u5929\u7262\u65f6\u5019\u7684\u6234\u5747\u4e86\uff0c\u4f46\u6211\u53ea\u662f\u4e0d\u613f\u610f\u4e8b\uff0c \u4e8b\u5230\u4e34\u5934\uff0c\u975e\u5f97\u5e94\u4ed8\u4e0d\u53ef\u4e4b\u65f6\uff0c\u6211\u8fd8\u662f\u51c6\u5907\u62fc\u6389\u8fd9\u51e0\u6839\u8001\u9aa8\u5934\u7684\uff01\u6211\u4e4b\u6240\u4ee5\u8bc8\u6b7b\uff0c\u7528\u610f\u4e43\u662f \u5728\u4e8e\u5316\u89e3\u4e00\u6bb5\u51a4\u4ec7\uff0c\u4e01\u5f6d\u6076\u884c\u65e0\u591a\uff0c\u7f6a\u72b9\u53ef\u6055\uff0c\u6211\u5df2\u7ecf\u6740\u4e86\u4ed6\u7684\u7236\u4eb2\uff0c\u5c31\u4e0d\u613f\u518d\u4f24\u4ed6\u4e86\u3002\u4f60 \u4ee5\u4e3a\u6211\u662f\u6015\u4e86\u66ff\u4ed6\u6491\u8170\u7684\u53f2\u767d\u90fd\u4e48\uff1f\u521a\u624d\u4f60\u662f\u975e\u51fa\u53bb\u4e0d\u53ef\u7684\uff0c\u600e\u80fd\u8bf4\u662f\u8fde\u7d2f\u4e86\u6211\uff1f\u8001\u5f1f\uff0c\u4f60 \u8fd9\u51e0\u53e5\u8bdd\u6709\u70b9\u89c1\u5916\u4e86\u5427\uff1f\u201d \u3000\u3000\u5168\u9010\u6d41\u8d54\u793c\u9053\uff1a\u201c\u8bf7\u6055\u5c0f\u4f84\u5931\u8a00\u3002\u4e0d\u8fc7\u6211\u7684\u610f\u601d\u662f\u6709\u5907\u65e0\u60a3\uff0c\u8428\u798f\u9f0e\u8981\u662f\u77e5\u9053\u6211\u8eb2\u5728\u4f60 \u8fd9\u513f\uff0c\u603b\u4f1a\u6765\u627e\u9ebb\u70e6\u7684\u3002\u201d \u3000\u3000\u6234\u5747\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u60f3\u8fc7\u4e86\uff0c\u53f2\u767d\u90fd\u6b64\u65f6\u4e00\u6765\u662f\u6025\u4e8e\u56de\u53bb\uff1b\u4e8c\u6765\u4ed6\u81ea\u8d1f\u662f\u6c5f\u6e56\u4e0a\u6570\u4e00\u6570\u4e8c \u7684\u4eba\u7269\uff0c\u4ed6\u5728\u8fd9\u91cc\u94ba\u7fbd\u800c\u5f52\uff0c\u60f3\u4ed6\u4e5f\u4e0d\u4fbf\u53bb\u544a\u8bc9\u8428\u798f\u9f0e\uff1b\u4e09\u6765\u6211\u542c\u4ed6\u7684\u53e3\u98ce\uff0c\u4f3c\u4e4e\u4ed6\u5bf9\u5357\u661f \u8001\u5f1f\u9887\u6709\u62c9\u62e2\u4e4b\u610f\uff0c\u7ba1\u4ed6\u56fe\u8c0b\u5982\u4f55\uff0c\u76ee\u7684\u4f3c\u4e4e\u8fd8\u4e0d\u81f3\u4e8e\u4fbf\u6765\u6311\u8845\u3002\u4ed6\u4eec\u82e5\u662f\u8981\u6765\u7684\u8bdd\uff0c\u6211\u4e5f \u6709\u4e86\u51c6\u5907\u3002\u5c31\u5728\u94f8\u5251\u7684\u90a3\u4e2a\u5730\u7a96\u91cc\uff0c\u6709\u5730\u9053\u53ef\u4ee5\u901a\u5230\u5916\u9762\u7684\uff0c\u5f53\u771f\u6709\u4e8b\uff0c\u4e34\u65f6\u9003\u8d70\uff0c\u4e5f\u8fd8\u53ef \u4ee5\u8131\u8eab\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u4e86\u4ed6\u7684\u5206\u6790\uff0c\u5341\u5206\u8001\u5230\uff0c\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u5018\u82e5\u4e0d\u80fd\u5b89\u5c40\uff0c\u6211\u7684\u5e08 \u4f84\u5728\u5c0f\u91d1\u5ddd\uff0c\u6234\u8001\u524d\u8f88\u80af\u53bb\u90a3\u8fb9\u7684\u8bdd\uff0c\u4ed6\u4eec\u4e00\u5b9a\u6781\u8868\u6b22\u8fce\u3002\u201d \u3000\u3000\u6234\u5747\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u5317\u4eac\u57ce\u4e2d\uff0c\u4e09\u6559\u4e5d\u6d41\u90fd\u6709\u6211\u7684\u670b\u53cb\uff0c\u8981\u662f\u5728\u5317\u4eac\u90fd\u7acb\u8db3\u4e0d\u4e86\u7684\u8bdd\uff0c \u6211\u518d\u53bb\u5c0f\u91d1\u5ddd\u5427\u3002\u201d\u6234\u5747\u5e74\u7eaa\u8001\u4e86\uff01\u5fc5\u7adf\u8fd8\u662f\u4e0d\u5927\u613f\u610f\u79bb\u5f00\u4ed6\u4f4f\u60ef\u4e86\u7684\u8001\u5bb6\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u5728\u8fd9\u91cc\u4f4f\u4e00\u665a\u5427\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u6c89\u541f\u9053\uff1a\u201c\u8fd9\u4e2a\u2026\u2026\uff0c\u6211\u6050\u6015\u4eca\u5929\u8fd8\u5f97\u56de\u53bb\u3002\u6211\u8fd9\u6b21\u591a\u5f97\u4e00\u4f4d\u65b0\u4ea4\u7684\u670b\u53cb\u7167\u6599\uff0c \u4eca\u65e5\u6211\u8fdb\u57ce\u6765\uff0c\u4e8b\u524d\u5e76\u672a\u548c\u4ed6\u8bf4\u6e05\u695a\u7684\uff0c\u5018\u4e0d\u56de\u53bb\uff0c\u53ea\u6015\u7d2f\u4ed6\u6302\u5ff5\u3002\u201d \u3000\u3000\u6234\u5747\u8001\u4e8e\u4e16\u6545\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u7ecf\u8fc7\u4e86\u4e00\u573a\u60a3\u96be\uff0c\u60f3\u5fc5\u6709\u8bb8\u591a\u8bdd\u8981\u8bf4\uff0c\u6211\u4e5f\u6709\u70b9\u4e8b\u60c5 \u9700\u8981\u6599\u7406\uff0c\u8bf7\u6055\u6211\u4e0d\u5949\u966a\u4e86\u3002\u9010\u6d41\u4f60\u5c31\u66ff\u6211\u597d\u597d\u62db\u547c\u4f60\u7684\u628a\u5144\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5e26\u674e\u5357\u661f\u5230\u4ed6\u6240\u4f4f\u7684\u90a3\u95f4\u5ba2\u623f\uff0c\u674e\u5357\u661f\u9053\uff1a\u201c\u53f2\u59d1\u5a18\u5f88\u6302\u5ff5\u4f60\uff0c\u5979\u672c\u6765\u8981\u6211\u65e9\u65e5 \u6765\u627e\u4f60\u7684\uff0c\u53ef\u60dc\u6211\u53d7\u4e86\u4f24\uff0c\u76f4\u5230\u4eca\u5929\u624d\u80fd\u89c1\u4f60\u3002\u53f2\u59d1\u5a18\u5df2\u7ecf\u88ab\u5979\u54e5\u54e5\u6349\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53f2\u767d\u90fd\u521a\u624d\u7684\u8bdd\uff0c\u6211\u5df2\u7ecf\u542c\u89c1\u4e86\u201d\u3002\u5374\u4e0d\u77e5\u53f2\u59d1\u5a18\u6709\u6ca1\u6709\u53d7\u4f24\uff1f\u90a3\u65e5\u6211\u4ee5 \u4e3a\u4f60\u4eec\u4e00\u5b9a\u53ef\u4ee5\u8131\u9669\u7684\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u6211\u6094\u4e0d\u8be5\u4e0d\u548c\u4f60\u4eec\u4e00\u8def\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u7b11\u9053\uff1a\u201c\u597d\u5728\u53f2\u59d1\u5a18\u5e76\u6ca1\u53d7\u4f24\u3002\u6211\u867d\u7136\u53d7\u4e86\u4f24\uff0c\u5374\u56e0\u6b64\u53c8\u4ea4\u4e86\u4e00\u4f4d\u670b\u53cb\uff0c\u4e5f\u7b97\u662f \u56e0\u7978\u5f97\u798f\u4e86\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5927\u54e5\u4ea4\u7684\u90a3\u4f4d\u65b0\u670b\u53cb\u662f\u8c01\uff1f\u201d\u674e\u5357\u661f\u9053\uff1a\u201c\u662f\u9648\u5149\u7167\uff0c\u8d24\u5f1f\u53ef \u66fe\u542c\u8fc7\u4ed6\u7684\u540d\u5b57\u7684\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u6c89\u541f\u9053\uff1a\u201c\u9648\u5149\u7167\uff0c\u54a6\uff0c\u8fd9\u540d\u5b57\u597d\u719f\uff01\u201d\u84e6\u5730\u60f3\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u4ed6\u662f\u9648 \u5929\u5b87\u7684\u513f\u5b50\uff01\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u662f\u548c\u6211\u8bf4\u8fc7\u4ed6\u7236\u4eb2\u7684\u540d\u5b57\u3002\u4f46\u8fd9\u9648\u5929\u5b87\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4f4d\u9648\u53d4\u53d4\u548c\u6211\u7684\u7236\u4eb2\u662f\u81f3\u4ea4\u597d\u53cb\uff0c\u6211\u8fd9\u6b21\u4e0a\u4eac\u3002\u66fe\u5728\u4ed6\u7684\u5bb6\u91cc\u4f4f\u8fc7\u4e00\u665a \u7684\u3002\u9648\u53d4\u53d4\u8bf4\u4ed6\u548c\u516d\u5408\u5e2e\u7ed3\u4e86\u6881\u5b50\uff0c\u8fd8\u66fe\u6258\u6211\u5728\u6c5f\u6e56\u7559\u610f\u4ed6\u7684\u6d88\u606f\uff0c\u7167\u5e94\u4ed6\u5462\u3002\u60f3\u4e0d\u5230\u4ed6\u73b0 \u5728\u4e5f\u6765\u4e86\u5317\u4eac \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u4ed6\u548c\u6211\u4f4f\u5728\u897f\u5c71\u5367\u4f5b\u5bfa\uff0c\u597d\uff0c\u6211\u4eca\u665a\u56de\u53bb\u5c31\u628a\u8fd9\u4e2a\u559c\u8baf\u544a\u8bc9\u4ed6\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u662f\u600e\u4e48\u9047\u4e0a\u4ed6\u7684\uff1f\u201d\u674e\u5357\u661f\u8fd9\u624d\u6709\u7a7a\u628a\u90a3\u65e5\u7684\u906d\u9047\u8865\u8ff0\uff0c\u91d1\u9010\u6d41\u542c\u8bf4\u5e05 \u5b5f\u96c4\u5230\u4e86\u5317\u4eac\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u80fd\u591f\u6253\u4f24\u5927\u54e5\uff0c\u6b66\u529f\u786e\u662f\u4e0d\u53ef\u5c0f\u89d1\uff0c\u4ed6\u80af\u653e\u8d70\u5927\u54e5\uff0c\u4e0d\u7528 \u8bf4\u662f\u4e3a\u4e86\u8ba8\u597d\u7ea2\u82f1\u7684\u7f18\u6545\u4e86\u3002\u4f46\u8fd9\u91cc\u5374\u6709\u4e00\u4e2a\u7591\u70b9\u3002\u53f2\u767d\u90fd\u672c\u6765\u662f\u8981\u628a\u4ed6\u7684\u59b9\u59b9\u5ac1\u7ed9\u5e05\u5b5f\u96c4 \u7684\uff0c\u4f55\u4ee5\u73b0\u5728\u5374\u53c8\u6539\u4e86\u4e3b\u610f\uff1b\u542c\u4ed6\u521a\u624d\u7684\u53e3\u98ce\uff0c\u7adf\u4f3c\u4e4e\u662f\u5c5e\u610f\u5927\u54e5\u4f5c\u4ed6\u7684\u59b9\u5a7f\u5462\uff1f\u201d\u91d1\u9010\u6d41 \u767e\u601d\u83ab\u89e3\uff0c\u5fc3\u9053\uff1a\u201c\u5176\u4e2d\u5f88\u53ef\u80fd\u6709\u751a\u56fe\u8c0b\uff0c\u6211\u4e00\u5b9a\u8981\u8bbe\u6cd5\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\uff0c\u5927\u54e5\u6253\u4e0d\u8fc7\u5e05\u5b5f \u96c4\uff0c\u4ed6\u8fd9\u4e2a\u4ec7\u6211\u4e5f\u5e94\u8be5\u7ed9\u4ed6\u62a5\u590d\u3002\u4f46\u6211\u5982\u4e0d\u5fc5\u5148\u544a\u8bc9\u5927\u54e5\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u672c\u6765\u662f\u7b49\u5f85\u91d1\u9010\u6d41\u95ee\u4ed6\u548c\u53f2\u7ea2\u82f1\u4e4b\u4e8b\u7684\uff0c\u91d1\u9010\u6d41\u8c08\u6765\u8c08\u53bb\uff0c\u8bdd\u9898\u4e00\u76f4\u6ca1\u6709\u8f6c\u5230\u53f2 \u7ea2\u82f1\u8eab\u4e0a\uff0c\u674e\u5357\u661f\u6309\u637a\u4e0d\u4f4f\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6211\u6709\u4e00\u4ef6\u4e8b\u60f3\u544a\u8bc9\u4f60\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6765 \u4e86\uff01\u201d\u4ed6\u5fc3\u5934\u535c\u535c\u901a\u901a\u5730\u4e71\u8df3\uff0c\u5374\u4e0d\u613f\u610f\u7ed9\u674e\u5357\u661f\u770b\u51fa\u4ed6\u5185\u5fc3\u7684\u79d8\u5bc6\uff0c\u4e8e\u662f\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5927 \u54e5\uff0c\u8bf7\u8bf4\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u4f60\u548c\u53f2\u59d1\u5a18\u76f8\u8bc6\u591a\u4e45\u4e86\uff1f\u4f9d\u4f60\u770b\u6765\uff0c\u5979\u7684\u4e3a\u4eba\u600e\u6837\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u548c\u5979\u76f8\u8bc6\u624d\u4e0d\u8fc7\u4e00\u4e2a\u591a\u6708\uff0c\u6211\u53bb\u76d7\u53d6\u516d\u5408\u5e2e\u7684\u7384\u94c1\uff0c\u6070\u5de7\u78b0\u4e0a\u4e86\u5979\uff0c\u4ee5 \u540e\u603b\u5171\u4e0d\u8fc7\u89c1\u8fc7\u4e24\u4e09\u6b21\u9762\uff0c\u8bf4\u4e0d\u4e0a\u6709\u4ec0\u4e48\u4ea4\u60c5\u3002\u4e0d\u8fc7\uff0c\u5979\u65e2\u7136\u6562\u5728\u8428\u798f\u9f0e\u7684\u5bff\u5802\u4e4b\u4e0a\uff0c\u516c\u7136 \u548c\u5979\u9489\u53cd\u9762\uff0c\u4f9d\u6b64\u770b\u6765\uff0c\u6050\u6015\u4e5f\u8bf4\u5f97\u4e0a\u662f\u51fa\u4e8e\u6c61\u6ce5\u800c\u4e0d\u67d3\u4e86\u5427\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u62cd\u638c\u7b11\u9053\uff1a\u201c\u82f1\u96c4\u6240\u89c1\u7565\u540c\uff0c\u6211\u7684\u770b\u6cd5\u4e5f\u6b63\u662f\u548c\u8d24\u5f1f\u4e00\u6837\u3002\u8d24\u5f1f\uff0c\u4f60\u731c\u6211\u662f\u600e\u6837 \u548c\u5979\u7ed3\u8bc6\u7684\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\uff0c\u6211\u600e\u4e48\u731c\u5f97\u7740\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u5174\u9ad8\u91c7\u70c8\u5730\u8bf4\u9053\uff1a\u201c\u548c\u4f60\u7684\u60c5\u5f62\u5dee\u4e0d\u591a\u4e00\u6837\u3002\u534a\u4e2a\u6708\u524d\uff0c\u6211\u5728\u4fdd\u5b9a\u8def\u4e0a\u78b0\u7740\u5979\u52ab \u8428\u798f\u9f0e\u7684\u5bff\u793c\uff0c\u5bf9\u624b\u5f88\u5f3a\uff0c\u6211\u52a9\u5979\u6253\u9000\u4e86\u654c\u4eba\uff0c\u5c31\u8fd9\u6837\u76f8\u8bc6\u7684\u3002\u4f60\u8bf4\u662f\u4e0d\u662f\u548c\u4f60\u4e00\u6837\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u82e6\u7b11\u9053\uff1a\u201c\u6709\u76f8\u540c\u4e5f\u6709\u4e0d\u76f8\u540c\u5427\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u3002\u8d24\u5f1f\uff0c\u4f60\u4e0d\u8981\u7b11\u6211\u539a\u9762\u76ae\uff0c\u6211\u548c\u5979\u867d\u7136\u624d\u4e0d\u8fc7\u89c1\u4e86\u4e00\u9762\uff0c\u4f46\u6bd4\u4f60 \u66f4\u4e3a\u76f8\u719f\uff0c\u5374\u4f3c\u4e4e\u548c\u5979\u6709\u70b9\u7f18\u4efd\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u843d\u82b1\u867d\u6709\u5f71\uff0c\u6d41\u6c34\u5374\u65e0\u60c5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u62d4\u5251\u72c2\u6b4c\u4f24\u5f80\u4e8b\u3000\u629a\u7434\u65e0\u8bed\u5bf9\u826f\u670b \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u62d4\u5251\u72c2\u6b4c\u4f24\u5f80\u4e8b\u3000\u629a\u7434\u65e0\u8bed\u5bf9\u826f\u670b \u3000\u3000\u91d1\u9010\u6d41\u4f5c\u51fa\u6b22\u559c\u4e4b\u72b6\uff0c\u8bf4\u9053\uff1a\u201c\u606d\u559c\u5927\u54e5\u3002\u8fd9\u7f18\u4efd\u4e8c\u5b57\u6700\u662f\u96be\u6c42\uff0c\u60f3\u4e0d\u5230\u5927\u54e5\u5982\u4e8e\u65e0\u610f \u4e2d\u5f97\u4e4b\u4e86\uff01\u201d \u3000\u3000\u674e\u5357\u661f\u7b11\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u5417\uff1f\u6211\u7d20\u6765\u4e0d\u559c\u6b22\u5973\u5b50\u7684\uff0c\u60f3\u4e0d\u5230\u4e00\u89c1\u4e86\u8fd9\u4f4d\u53f2\u59d1\u5a18\uff0c\u5982\u662f\u9b42\u7275 \u68a6\u8426\uff0c\u65e5\u91cc\u591c\u91cc\u90fd\u60f3\u7740\u5979\uff0c\u8fd9\u53ef\u4e0d\u662f\u6709\u7f18\u5417\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f46\u4e0d\u77e5\u53f2\u59d1\u5a18\u53ef\u66fe\u5bf9\u4f60\u8868\u9732\u8fc7\u5fc3\u610f\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u53c8\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u6211\u81ea\u4f5c\u591a\u60c5\uff0c\u4f9d\u6211\u770b\uff0c\u6211\u7684\u5fc3\u4e0a\u6709\u5979\uff0c\u5979\u7684\u5fc3\u4e0a\u4e5f\u65e9\u5df2\u6709\u4e86\u6211 \u4e86\u3002\u8981\u4e0d\u7136\u90a3\u5929\u5979\u5c31\u4e0d\u4f1a\u4e3a\u6211\u820d\u547d\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u662f\u77e5\u9053\u53f2\u7ea2\u82f1\u7684\u6027\u683c\u7684\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u7ea2\u82f1\u662f\u4e2a\u6069\u6028\u5206\u660e\u3001\u662f\u975e\u6e05\u695a\uff0c\u7d20\u91cd\u53cb\u60c5 \u7684\u5973\u5b50\uff0c\u4ece\u524d\u5979\u4e0d\u662f\u4e5f\u66fe\u5c3d\u529b\u5e2e\u5fd9\u8fc7\u674e\u6566\uff0c\u6211\u8fd8\u56e0\u6b64\u8bef\u4f1a\u4e86\u5979\u5462\u3002\u201d \u3000\u3000\u53ef\u662f\u95ee\u9898\u5e76\u4e0d\u5728\u53f2\u7ea2\u82f1\u8eab\u4e0a\uff0c\u91d1\u9010\u6d41\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u4e0d\u7ba1\u5927\u54e5\u662f\u5426\u81ea\u4f5c\u591a\u60c5\uff0c\u4ed6\u5df2\u7ecf\u662f\u4e3a \u4e86\u7ea2\u82f1\u800c\u523b\u9aa8\u76f8\u601d\u7684\u4e86\u3002\u541b\u5b50\u4e0d\u593a\u4eba\u4e4b\u6240\u597d\uff0c\u4f55\u51b5\u6211\u548c\u4ed6\u662f\u516b\u62dc\u4e4b\u4ea4\uff1f\u201d\u53c8\u518d\u60f3\u9053\uff1a\u201c\u7ea2\u82f1 \u4f3c\u4e4e\u662f\u5bf9\u6211\u6709\u70b9\u610f\u601d\uff0c\u4f46\u4e5f\u8bf4\u4e0d\u5b9a\u662f\u6211\u81ea\u4f5c\u591a\u60c5\uff1f\u5509\uff0c\u7b97\u4e86\uff0c\u7b97\u4e86\uff01\u4e0d\u7ba1\u5979\u5bf9\u6211\u6709\u610f\u4e5f\u597d\uff0c \u65e0\u610f\u4e5f\u597d\uff0c\u4eca\u540e\u6211\u5f3a\u81ea\u6291\u5236\uff0c\u548c\u5979\u758f\u8fdc\uff0c\u8ba9\u5979\u7684\u4e00\u9897\u5fc3\u5b8c\u5168\u8f6c\u5230\u5927\u54e5\u8eab\u4e0a\u4e5f\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u674e\u5357\u57a6\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u5728\u60f3\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u662f\u5728\u60f3\u2014\u2014\u4ec0\u4e48\u65f6\u5019\u5403\u5927\u54e5\u7684\u559c\u9152\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u8fd8\u65e9\u7740\u5462\u3002\u53f2\u59d1\u5a18\u5982\u4eca\u88ab\u56da\u5728\u516d\u5408\u5e2e\u7684\u603b\u8235\uff0c\u4e0d\u628a\u5979\u6551\u51fa\u6765\uff0c\u4ec0\u4e48\u4e8b\u90fd\u8c08 \u4e0d\u5230\u3002\u8d24\u5f1f\uff0c\u4f60\u521a\u624d\u5df2\u7ecf\u542c\u5230\u53f2\u767d\u90fd\u7ea6\u4f1a\u6211\u7684\u4e8b\u4e86\u3002\u4f60\u548c\u6211\u4e00\u540c\u8d74\u4f1a\u5982\u4f55\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53f2\u767d\u90fd\u7684\u53e3\u98ce\u9887\u6709\u8bb8\u5a5a\u4e4b\u610f\uff0c\u5927\u54e5\u8fd8\u6015\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u6050\u9632\u6709\u8bc8\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f9d\u6211\u60f3\u53f2\u767d\u90fd\u662f\u4e0d\u4f1a\u52a0\u5bb3\u4f60\u7684\u3002\u6700\u521d\u95ef\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u53f2\u767d\u90fd\u5df2\u7ecf\u770b\u51fa\u4f60\u75c5 \u4f53\u672a\u6108\uff0c\u82e5\u662f\u4ed6\u6709\u5bb3\u4f60\u4e4b\u5fc3\uff0c\u90a3\u65f6\u5c31\u5e94\u52a8\u624b\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u5fc3\u91cc\u6709\u70b9\u4e0d\u5927\u9ad8\u5174\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u9010\u6d41\u7ecf\u8fc7\u4e86\u5bff\u5802\u4e4b\u4e8b\uff0c\u5df1\u662f\u60ca\u5f13\u4e4b\u9e1f\uff0c\u6015\u4e0e\u6211 \u518d\u53bb\u5192\u9669\u4e48\uff1f\u4e0d\u8fc7\uff0c\u4ed6\u8bf4\u7684\u8bdd\u4e5f\u672a\u5c1d\u6ca1\u6709\u9053\u7406\u3002\u53f2\u767d\u90fd\u6b66\u529f\u8fdc\u8fdc\u5728\u6211\u4e4b\u4e0a\uff0c\u4eca\u65e5\u5374\u4e00\u76f4\u5bf9\u6211 \u4f4e\u9996\u4e0b\u5fc3\uff0c\u770b\u6765\u53ea\u6015\u662f\u522b\u6709\u6240\u6c42\u7684\u4e86\uff01 \u3000\u3000\u91d1\u9010\u6d41\u53c8\u9053\uff1a\u201c\u5c0f\u5f1f\u5728\u5317\u4eac\u4e5f\u8fd8\u6709\u70b9\u4e8b\u60c5\uff0c\u6050\u6015\u6682\u65f6\u4e0d\u80fd\u79bb\u5f00\u3002\u201d\u91d1\u9010\u6d41\u5176\u5b9e\u662f\u6015\u81ea\u5df1 \u4e5f\u53bb\uff0c\u5bf9\u674e\u5357\u661f\u7684\u5a5a\u4e8b\uff0c\u975e\u4f46\u65e0\u52a9\uff0c\u53cd\u800c\u6709\u5bb3\u3002\u56e0\u6b64\u660e\u77e5\u674e\u5357\u661f\u8981\u8bef\u4f1a\u4ed6\uff0c\u4e5f\u53ea\u597d\u63a8\u642a\u4e86\u3002 \u3000\u3000\u674e\u5357\u661f\u662f\u4e2a\u5f88\u91cd\u611f\u60c5\u7684\u4eba\uff0c\u5fc3\u4e2d\u867d\u7136\u4e0d\u4e50\uff0c\u5374\u4e5f\u4e0d\u613f\u53e3\u51fa\u6028\u8a00\u3002\u53ea\u662f\u91d1\u9010\u6d41\u7684\u62d2\u7edd\u5374\u662f \u4ed6\u59cb\u6599\u4e4b\u6240\u4e0d\u53ca\u7684\uff0c\u56e0\u6b64\u5012\u662f\u4e0d\u65e0\u6709\u70b9\u201c\u8bf4\u50f5\u201d\u4e86\u7684\u611f\u89c9\u3002\u672c\u6765\u662f\u548c\u8c10\u7684\u6c14\u6c1b\u4e5f\u5f00\u59cb\u53d8\u5f97\u6709 \u4e9b\u5c34\u5c2c\uff0c\u4e00\u65f6\u95f4\u674e\u5357\u661f\u7adf\u4e0d\u77e5\u627e\u4e9b\u4ec0\u4e48\u8bdd\u6765\u548c\u91d1\u9010\u6d41\u8bf4\u597d\u3002 \u3000\u3000\u773c\u5149\u4e00\u77a5\uff0c\u674e\u5357\u661f\u770b\u89c1\u4ed6\u8d60\u4e0e\u91d1\u9010\u6d41\u7684\u90a3\u5f20\u53e4\u7434\u6b63\u653e\u5728\u684c\u4e0a\uff0c\u674e\u5357\u661f\u9053\uff1a\u201c\u8d24\u5f1f\u8fd1\u6765\u6709 \u5b66\u5f39\u7434\u4e48\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5b66\u8fc7\u51e0\u4e2a\u53e4\u8c31\uff0c\u603b\u662f\u5f39\u7684\u4e0d\u597d\u3002\u5927\u54e5\u4e0d\u65e5\u8fdc\u884c\uff0c\u4e0d\u77e5\u4f55\u65f6\u65b9\u5f97\u518d\u8046\u96c5\u594f\uff1f \u5206\u624b\u5728\u5373\uff0c\u8bf7\u5927\u54e5\u8d60\u6211\u4e00\u66f2\u5982\u4f55\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u6b63\u81ea\u6ee1\u6000\u5fc3\u4e8b\uff0c\u63a5\u8fc7\u7434\u6765\uff0c\u9053\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u4fbf\u5373\u629a\u5f26\u6b4c\u9053\uff1a\u201c\u82b3\u4e0e\u6cfd\u5176\u6742\u8e81 \u516e\uff0c\u7f8c\u82b3\u534e\u81ea\u4e2d\u51fa\uff0c\u7eb7\u90c1\u90c1\u5176\u8fdc\u627f\u516e\uff0c\u6ee1\u5185\u800c\u5916\u626c\u3002\u60c5\u4e0e\u8d28\u53ef\u4fdd\u516e\u3002\u7f8c\u5c45\u853d\u5176\u95fb\u7ae0\u3002\u201d \u3000\u3000\u8fd9\u662f\u695a\u8f9e\u201c\u601d\u7f8e\u4eba\u7bc7\u201d\u7684\u4e00\u8282\uff0c\u610f\u601d\u662f\u8bf4\uff1a\u201c\u9999\u7684\u548c\u81ed\u7684\u6df7\u5728\u4e00\u5757\u513f\uff0c\u50cf\u541b\u5b50\u548c\u5c0f\u4eba\u5171 \u5904\u4e00\u671d\u3002\u4f46\u6770\u51fa\u7684\u9999\u82b1\u5728\u51e1\u5349\u4e4b\u4e2d\u4e5f\u80fd\u81ea\u522b\uff0c\u5b83\u7684\u82b3\u9999\u56db\u6ea2\u600e\u4e5f\u4e0d\u4f1a\u6563\u6d88\u3002\u7f8e\u597d\u7684\u54c1\u8d28\u603b\u80fd \u4fdd\u6301\uff0c\u7f8e\u597d\u7684\u58f0\u540d\u5728\u8352\u50fb\u7684\u5730\u65b9\u4e5f\u603b\u80fd\u4f20\u51fa\u53bb\uff0c\u7528\u4e0d\u7740\u4f60\u66ff\u5979\u5fc3\u7126\u3002\u201d\u53f2\u7ea2\u82f1\u6df7\u5728\u516d\u5408\u5e2e \u4e2d\uff0c\u5c31\u50cf\u51fa\u4e8e\u6c61\u6ce5\u800c\u4e0d\u67d3\u7684\u9752\u83b2\u4e00\u6837\uff0c\u4e0d\u7528\u8bf4\u674e\u5357\u661f\u6240\u601d\u7684\u201c\u7f8e\u4eba\u201d\u4e43\u662f\u53f2\u7ea2\u82f1\u4e86\uff0c\u4ed6\u5f39\u594f \u8fd9\u8282\u695a\u8f9e\u4e5f\u9690\u9690\u542b\u6709\u7b54\u590d\u91d1\u9010\u6d41\u7684\u610f\uff1a\u201c\u6770\u51fa\u7684\u9999\u82b1\u5728\u51e1\u5349\u4e4b\u4e2d\u4e5f\u80fd\u81ea\u522b\u201d\uff0c\u53f2\u7ea2\u82f1\u603b\u80fd\u591f \u8131\u51fa\u516d\u5408\u5e2e\u8fd9\u5757\u6ce5\u6cbc\u7684\uff0c\u4f60\u4e0d\u80fd\u5e2e\u5fd9\u5979\u4e5f\u5c31\u662f\u4e86\uff0c\u201c\u7528\u4e0d\u7740\u4f60\u66ff\u5979\u5fc3\u7126\u201d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u5982\u4e71\u9ebb\uff0c\u9eef\u7136\u4e0d\u8bed\u3002\u674e\u5357\u661f\u628a\u53e4\u7434\u63a8\u5230\u4ed6\u7684\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u8d24\u5f1f\u5b66\u4e86\u4e9b\u4ec0\u4e48\u53e4 \u8c31\uff1f\u4f60\u4e5f\u5f39\u4e00\u66f2\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9ed8\u9ed8\u65e0\u8a00\u7684\u4fbf\u5f39\u594f\u8d77\u6765\uff0c\u674e\u5357\u661f\u662f\u4e2a\u53e4\u7434\u7684\u9ad8\u624b\uff0c\u91d1\u9010\u6d41\u867d\u7136\u53ea\u5f39\u4e0d\u5531\uff0c\u674e\u5357\u661f \u4e5f\u542c\u5f97\u51fa\u4ed6\u5f39\u7684\u662f\u8bd7\u7ecf\u4e2d\u7684\u4e00\u7ae0\uff0c\u4e8e\u662f\u4f9d\u7740\u7434\u97f5\u6b4c\u9053\uff1a\u201c\u65e5\u5c45\u6708\u8bf8\uff0c\u80e1\u8fed\u800c\u5fae\uff1f\u5fc3\u4e4b\u5fe7\u77e3\uff0c \u5982\u532a\u6c57\u70f7\u8863\uff0c\u9759\u8a00\u601d\u4e4b\uff0c\u4e0d\u80fd\u594b\u98de\u3002\u201d\u82e5\u7528\u73b0\u4ee3\u4eba\u7684\u8bf4\u8bdd\uff0c\u90a3\u610f\u601d\u5c31\u662f\u8bf4\uff1a\u201c\u95ee\u8fc7\u6708\u4eae\u95ee\u592a \u9633\uff0c\u4e3a\u4f55\u6709\u5149\u50cf\u65e0\u5149\uff1f\u5fc3\u4e0a\u70e6\u607c\u6d17\u4e0d\u51c0\uff0c\u597d\u50cf\u4e00\u5806\u810f\u8863\u88f3\u3002\u6211\u624b\u6309\u80f8\u819b\u7ec6\u7ec6\u60f3\u3002\u600e\u80fd\u9ad8\u98de\u5c55 \u7fc5\u8180\uff1f\u201d\uff08\u7528\u4f59\u51a0\u82f1\u8bd1\u6587\uff09 \u3000\u3000\u91d1\u9010\u6d41\u5f39\u8fd9\u4e00\u66f2\uff0c\u5176\u4e2d\u662f\u542b\u6709\u6df1\u610f\u7684\u3002\u4ed6\u82e6\u607c\u4e8e\u81ea\u5df1\u629b\u4e0d\u4e0b\u513f\u5973\u79c1\u60c5\uff0c\u89c9\u5f97\u8fd9\u662f\u81ea\u5df1\u7684 \u8fc7\u9519\uff0c\u5fc3\u4e2d\u7684\u70e6\u607c\u597d\u50cf\u4e00\u5806\u810f\u8863\u88f3\u4e00\u6837\uff0c\u5e94\u5f53\u6d17\u5e72\u51c0\u5b83\u3002\u591a\u5c11\u5927\u4e8b\u7b49\u770b\u81ea\u5df1\u53bb\u505a\uff0c\u6240\u4ee5\u4ed6\u8981 \u201c\u624b\u6309\u80f8\u77a0\u7ec6\u7ec6\u60f3\uff0c\u600e\u80fd\u9ad8\u98de\u5c55\u7fc5\u8180\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u5e76\u4e0d\u5b8c\u5168\u61c2\u5f97\u91d1\u9010\u6d41\u7684\u610f\u601d\uff0c\u4f46\u4e5f\u9690\u9690\u611f\u89c9\u5f97\u5230\u4ed6\u90a3\u6ee1\u8154\u90c1\u95f7\u800c\u53c8\u5728\u81ea\u7b56\u81ea\u52b1\u7684 \u5fc3\u60c5\u3002\u8fd9\u79cd\u79cd\u590d\u6742\u7684\u611f\u60c5\uff0c\u90fd\u4ece\u7434\u58f0\u4e2d\u53d1\u6cc4\u51fa\u6765\u3002 \u3000\u3000\u674e\u5357\u661f\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u9010\u6d41\u6709\u7740\u4ec0\u4e48\u5fc3\u4e8b\u4e0d\u80af\u5bf9\u6211\u8bf4\u5462\uff1f\u201d \u3000\u3000\u5ffd\u542c\u5f97\u95e8\u5916\u6709\u4eba\u8d5e\u9053\uff1a\u201c\u5f39\u5f97\u597d\u7434\uff01\u201d\u539f\u6765\u6b64\u65f6\u5df2\u662f\u665a\u9910\u65f6\u5019\uff0c\u6234\u5747\u6765\u8bf7\u4ed6\u4eec\u51fa\u53bb\u5403 \u996d\u3002 \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u53ef\u5f97\u8d76\u56de\u53bb\u4e86\u3002\u201d\u5306\u5306\u8d70\u51fa\u5ba2\u5385\uff0c\u5411\u6234\u5747\u544a\u8f9e\u3002 \u3000\u3000\u6234\u5747\u9053\uff1a\u201c\u9ec4\u9e21\u767d\u9152\uff0c\u4e0d\u8db3\u4ee5\u4e91\u5949\u5ba2\u3002\u4f46\u9152\u5df2\u70ed\uff0c\u9e21\u5df2\u719f\uff0c\u8001\u5f1f\u5403\u4e86\u518d\u8d70\u4e0d\u8fdf\uff01\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u591a\u8c22\u8001\u4e08\u76db\u60c5\uff1b\u665a\u8f88\u4f4f\u5728\u897f\u5c71\uff0c\u8fd8\u662f\u65e9\u4e9b\u8d76\u56de\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u6234\u5747\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u8001\u673d\u5e73\u751f\u522b\u65e0\u6240\u597d\uff0c\u53ea\u559c\u7ed3\u4ea4\u5929\u4e0b\u82f1\u8c6a\u3002\u4eca\u65e5\u65b0\u77e5\uff01\u65e5\u597d\u5171\u805a \u4e00\u5802\uff0c\u53ea\u6068\u672a\u80fd\u4e0e\u8001\u5f1f\u75db\u996e\u51e0\u676f\uff01\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u6211\u656c\u8001\u82f1\u96c4\u4e09\u676f\u518d\u8d70\uff01\u201d\u4e0e\u6234\u5747\u5bf9\u996e\u4e86\u4e09\u5927\u676f\uff0c\u53c8\u9053\uff1a\u201c\u7434\u5251\u76f8 \u4ea4\uff0c\u60c5\u6d53\u4f3c\u9152\u3002\u9010\u6d41\u8d24\u5f1f\uff0c\u4f60\u4e5f\u6765\u996e\u4e09\u676f\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5f53\u5f97\u5949\u966a\uff01\u201d\u5404\u81ea\u659f\u4e86\u6ee1\u6ee1\u4e09 \u676f\uff0c\u4e00\u996e\u800c\u5c3d\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u5f39\u5251\u6b4c\u9053\uff1a\u201c\u8131\u7565\u5f62\u9ab8\u8fc8\u4fd7\u6d41\uff0c\u76f8\u4ea4\u6bcb\u8d1f\u5c11\u5e74\u5934\u3002\u8c03\u5f26\u96c5\u97f5\u916c\u77e5\u5df1\uff0c\u51fa\u5323\u96c4\u8292\u65a9 \u5bc7\u4ec7\u3002\u4f11\u9053\u9f99\u86c7\u5982\u8349\u83bd\uff0c\u83ab\u6559\u7434\u5251\u4ed8\u9ad8\u697c\u3002\u4e2d\u539f\u81ea\u6709\u82f1\u8c6a\u5728\uff0c\u6d77\u5916\u5f52\u6765\u559c\u8c41\u7738\u3002\u201d\u72c2\u6b4c\u4e2d\u5df1 \u662f\u8d70\u51fa\u5927\u95e8\u53bb\u4e86\u3002 \u3000\u3000\u6234\u5747\u7b11\u9053\uff1a\u201c\u6b64\u4eba\u8c6a\u8fc8\u4e0d\u7f81\uff0c\u548c\u4f60\u7684\u6027\u60c5\u6b63\u662f\u4e00\u6837\uff0c\u602a\u4e0d\u5f97\u4f60\u4eec\u6c14\u5473\u76f8\u6295\uff0c\u7ed3\u4e3a\u5144 \u5f1f\u3002\u201d\u5510\u6770\u592b\u4e5f\u9053\uff1a\u201c\u6b64\u4eba\u6b66\u529f\u80c6\u8bc6\u5747\u662f\u4e0d\u51e1\uff0c\u96be\u5f97\u8bd7\u624d\u4e5f\u662f\u5982\u6b64\u654f\u6377\uff0c\u5f53\u771f\u53ef\u7b97\u5f97\u662f\u6587\u6b66 \u5168\u624d\u4e86\u3002\u91d1\u8001\u5f1f\uff0c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u4f60\u628a\u7384\u94c1\u5b9d\u5251\u8d60\u4ed6\uff0c\u6211\u672c\u6765\u662f\u6709\u51e0\u5206\u4e3a\u4f60\u53ef\u60dc\u7684\uff0c\u5982\u4eca\u6211\u5374\u4e3a \u8fd9\u5b9d\u5251\u5e86\u8d3a\u5f97\u4e3b\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5e73\u751f\u5f97\u4e00\u77e5\u5df1\uff0c\u6b7b\u53ef\u65e0\u61be\u3002\u533a\u533a\u4e00\u5251\uff0c\u53c8\u4f55\u8db3\u9053\u54c9\uff01\u201d \u3000\u3000\u5510\u6770\u592b\u5927\u7b11\u9053\uff1a\u201c\u8bf4\u5f97\u597d\uff0c\u8001\u5f1f\uff0c\u6211\u4e5f\u656c\u4f60\u4e09\u676f\uff01\u201d\u91d1\u9010\u6d41\u559d\u4e86\u5341\u51e0\u676f\u9152\uff0c\u9152\u610f\u6d8c\u4e0a\u5fc3 \u5934\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5927\u54e5\u8d60\u6211\u7684\u4f73\u53e5\uff0c\u6211\u83ab\u8981\u9189\u5fd8\u4e86\u3002\u8d81\u7740\u73b0\u5728\u672a\u9189\uff0c\u6211\u53ef\u5f97\u80cc\u719f\u4e86\u5b83\u3002\u201d\u5728 \u5fc3\u4e2d\u9ed8\u8bf5\u4e86\u51e0\u904d\uff0c\u7a81\u7136\u5982\u6709\u6240\u89e6\uff0c\u77bf\u7136\u4e00\u7701\uff0c\u60f3\u9053\uff1a\u201c\u5927\u54e5\u8bd7\u4e2d\u6709\u2018\u6d77\u5916\u5f52\u6765\u559c\u8c41\u7738\u2019\u4e00 \u53e5\uff0c\u96be\u9053\u4ed6\u4e5f\u662f\u548c\u6211\u4e00\u6837\uff0c\u662f\u5bb6\u5c45\u6d77\u5916\uff0c\u521d\u5c65\u4e2d\u539f\u7684\uff1f\u201d \u3000\u3000\u5510\u6770\u592b\u89c1\u91d1\u9010\u6d41\u53d1\u5446\u7684\u795e\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u8001\u5f1f\u4f60\u5728\u60f3\u4ec0\u4e48\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4eca\u5929\u662f\u4ec0\u4e48\u65e5 \u5b50\uff1f\u201d\u5510\u6770\u592b\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u6050\u6015\u771f\u662f\u6709\u51e0\u5206\u9189\u4e86\uff0c\u4eca\u5929\u662f\u6b63\u6708\u5341\u4e09\uff0c\u518d\u8fc7\u4e24\u65e5\u5c31\u662f\u5143 \u5bb5\uff0c\u4f60\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u771f\u662f\u6709\u51e0\u5206\u9189\u4e86\uff01\u201d\u5176\u5b9e\u91d1\u9010\u6d41\u5728\u5927\u95f9\u4e86\u8428\u5e9c\u4e4b\u540e\uff0c\u5c31\u4e00 \u76f4\u662f\u7b49\u5f85\u65e0\u5bb5\u8fd9\u4e00\u665a\u7684\u6765\u4e34\u7684\u3002\u201d\u6b63\u56e0\u4e3a\u4ed6\u8bb0\u7740\u8fd9\u4e2a\u65e5\u5b50\uff0c\u6240\u4ee5\u624d\u4f1a\u51b2\u53e3\u800c\u51fa\u7684\u95ee\u3002\u4ed6\u542c\u4e86 \u201c\u5143\u5bb5\u201d\u4e8c\u5b57\uff0c\u9152\u610f\u4e5f\u90fd\u6d88\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u60f3\u8d77\u4e86\u4ed6\u7236\u4eb2\u53eb\u4ed6\u5e26\u7ed9\u6c5f\u6d77\u5929\u7684\u90a3\u5c01\u4fe1\uff0c\u90a3\u5c01\u4fe1\u4e2d\u6700\u91cd\u8981\u7684\u3002\u4e00\u4ef6\u4e8b\u662f\u5429\u5490\u6c5f\u6d77 \u5929\u5728\u4eca\u5e74\u7684\u5143\u5bb5\u4e4b\u591c\uff0c\u4e09\u66f4\u5bf9\u5206\uff1b\u6234\u4e0a\u4e00\u679a\u5bd2\u7389\u6212\u6307\uff0c\u5230\u5317\u4eac\u897f\u5c71\u7684\u79d8\u9b54\u5d16\uff0c\u53bb\u4f1a\u4e00\u4e2a\u6234\u7740 \u540c\u6837\u6212\u6307\u7684\u4eba\u3002\u8fd9\u5c01\u4fe1\u662f\u6c5f\u6d77\u5929\u7ed9\u4ed6\u770b\u7684\uff0c\u4ed6\u7236\u4eb2\u53ef\u6ca1\u6709\u548c\u4ed6\u8bf4\u8fc7\u3002\u56e0\u6b64\u4ed6\u4e5f\u4e0d\u77e5\u9053\u4ed6\u7684\u7236 \u4eb2\u8981\u4ed6\u7684\u5e08\u5144\u4f1a\u89c1\u7684\u662f\u4ec0\u4e48\u4eba\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u540e\u5929\u5c31\u662f\u5143\u5bb5\u4e86\uff0c\u4e0d\u77e5\u5e08\u5144\u5df2\u7ecf\u5230\u4e86\u5317\u4eac\u6ca1\u6709\uff1f\u5e08\u5144\u4ea4\u6e38\u5e7f\u9614\uff0c\u6234 \u8001\u524d\u8f88\u4e5f\u662f\u6d88\u606f\u7075\u901a\u3002\u5018\u82e5\u5e08\u5144\u5230\u4e86\u5317\u4eac\uff0c\u4ed6\u4eec\u60f3\u5fc5\u4f1a\u63a5\u5f97\u4e0a\u5934\u3002\u201d\u9648\u5149\u7167\u8fd9\u4e24\u5929\u60f3\u5fc5\u4e5f\u4f1a \u5230\u6765\u627e\u6211\uff0c\u6211\u4e14\u5728\u5bb6\u4e2d\u7b49\u5019\uff0c\u8fc7\u4e86\u5143\u5bb5\uff0c\u518d\u5f80\u516d\u5408\u5e2e\u5427\u3002\u201d\u91d1\u9010\u6d41\u81ea\u5fd6\u8f7b\u529f\u8fdc\u80dc\u4e8e\u674e\u5357\u661f\uff0c \u5018\u82e5\u65e5\u95f4\u9a91\u9a6c\uff0c\u665a\u4e0a\u8dd1\u8def\uff0c\u8ba9\u674e\u5357\u661f\u5148\u8d70\u4e24\u5929\uff0c\u4ed6\u4e5f\u8fd8\u53ef\u4ee5\u8ffd\u5f97\u4e0a\u4ed6\u3002\u539f\u6765\u91d1\u9010\u6d41\u662f\u6253\u7b97\u6697 \u4e2d\u8ddf\u8e2a\uff0c\u5e76\u4e0d\u9732\u9762\uff0c\u5230\u4e86\u516d\u5408\u5e2e\u7684\u603b\u8235\uff0c\u89c1\u673a\u884c\u4e8b\u3002\u5018\u82e5\u674e\u5357\u661f\u6551\u4e0d\u51fa\u53f2\u7ea2\u82f1\uff0c\u4ed6\u518d\u51fa\u624b\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e0d\u89c1\u9648\u5149\u7167\u6765\u627e\uff0c\u4e5f\u6ca1\u6709\u6c5f\u6d77\u5929\u7684\u6d88\u606f\u3002\u91d1\u9010\u6d41\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u5e08\u5144\u7ed5 \u9053\u897f\u661f\uff0c\u53ef\u80fd\u662f\u7b97\u51c6\u4e86\u65f6\u95f4\uff0c\u660e\u5929\u624d\u5230\uff0c\u4f46\u9648\u5149\u7167\u4f55\u4ee5\u6ca1\u6709\u6765\u627e\u6211\u5462\uff1f\u662f\u5927\u54e5\u6ca1\u6709\u628a\u6211\u7684\u6d88 \u606f\u544a\u8bc9\u4ed6\u5462\uff1f\u8fd8\u662f\u4ed6\u53c8\u53e6\u5916\u6709\u4e8b\u8d70\u4e86\u3002\u201d\u91d1\u3001\u9648\u4e8c\u5bb6\u662f\u4e16\u4ea4\uff0c\u9648\u5929\u5b87\u53c8\u66fe\u6258\u8fc7\u91d1\u9010\u6d41\u7167\u987e\u4ed6 \u7684\u513f\u5b50\uff0c\u662f\u4ee5\u91d1\u9010\u6d41\u4e5f\u662f\u5f88\u60f3\u548c\u9648\u5149\u7167\u89c1\u4e00\u89c1\u9762\u7684\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u5929\u8fc7\u53bb\u4e86\uff0c\u5230\u4e86\u5143\u5bb5\u90a3\u665a\uff0c\u5927\u5df2\u7ecf\u9ed1\u4e86\uff0c\u4ecd\u7136\u6ca1\u6709\u4ed6\u5e08\u5144\u7684\u6d88\u606f\uff0c\u4e5f\u4e0d\u89c1\u9648\u5149\u7167\u5230 \u6765\u627e\u4ed6\u3002\u4e8e\u662f\u91d1\u9010\u6d41\u85c9\u53e3\u51fa\u53bb\u770b\u706f\uff0c\u4fbf\u5077\u5077\u7684\u51fa\u4e86\u57ce\u3002\u4eac\u4e2d\u4ed5\u5973\uff0c\u5bf9\u5143\u5bb5\u4f73\u8282\u662f\u6781\u4e3a\u91cd\u89c6 \u7684\uff0c\u4e00\u5230\u5165\u9ed1\u7684\u65f6\u5206\uff0c\u5c31\u6709\u5404\u5f0f\u7684\u82b1\u706f\u5728\u4e3e\u884c\u8d5b\u4f1a\u4e86\uff0c\u8981\u4e00\u76f4\u95f9\u5230\u5929\u4eae\u624d\u7f62\u7684\u3002\u662f\u4ee5\u91d1\u9010\u6d41 \u85c9\u53e3\u51fa\u53bb\u770b\u706f\uff0c\u53ef\u4ee5\u5230\u5929\u4eae\u624d\u56de\u53bb\uff01 \u3000\u3000\u9648\u5149\u7167\u4e3a\u4ec0\u4e48\u4e0d\u6765\u627e\u4ed6\u5462\uff1f\u8fd9\u91cc\u9762\u6709\u4e2a\u539f\u56e0\u3002 \u3000\u3000\u4e14\u8bf4\u674e\u5357\u661f\u90a3\u665a\u8d76\u56de\u897f\u5c71\uff0c\u5230\u4e86\u5367\u4f5b\u5bfa\uff0c\u5df2\u7ecf\u662f\u4e09\u66f4\u65f6\u5206\u3002\u5b88\u95e8\u7684\u5c0f\u6c99\u5f25\u8bf4\u9053\uff1a\u201c\u9648\u516c \u5b50\u4e0d\u89c1\u4f60\u56de\u6765\uff0c\u6ee1\u5c71\u627e\u4f60\u3002\u73b0\u5728\u4e5f\u4e0d\u77e5\u56de\u6765\u4e86\u6ca1\u6709\uff1f\u8981\u6211\u53bb\u7980\u65b9\u4e08\u4e48\uff1f\u201d\u674e\u5357\u661f\u9053\u4e86\u4e00\u58f0\u60ed \u6127\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u662f\u6709\u4e8b\u8fdb\u57ce\uff0c\u4ee5\u4e3a\u53ef\u4ee5\u4e00\u65e9\u56de\u6765\u7684\uff0c\u6240\u4ee5\u6ca1\u6709\u544a\u8bc9\u65b9\u4e08\u548c\u9648\u5144\u3002\u4e0d\u6599\u78b0\u4e0a\u670b \u53cb\uff0c\u803d\u6401\u4e86\u4e00\u4e9b\u65f6\u5019\uff0c\u56de\u6765\u665a\u4e86\uff0c\u4e0d\u5fc5\u60ca\u52a8\u4f60\u4eec\u7684\u65b9\u4e08\uff0c\u660e\u65e9\u6211\u53bb\u5411\u4ed6\u8c22\u7f6a\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u6084\u6084\u7684\u56de\u5230\u4ed6\u548c\u9648\u5149\u7167\u540c\u4f4f\u7684\u90a3\u95f4\u5ba2\u623f\uff0c\u9648\u5149\u7167\u679c\u7136\u8fd8\u6ca1\u6709\u56de\u6765\u3002\u674e\u5357\u661f\u5fc3\u91cc\u53c8 \u662f\u611f\u6fc0\u53c8\u662f\u60ed\u6127\uff0c\u7b49\u4e86\u4e00\u4f1a\uff0c\u6b63\u60f3\u6e9c\u51fa\u53bb\u627e\u9648\u5149\u7167\uff0c\u6070\u5de7\u9648\u5149\u7167\u5c31\u56de\u6765\u4e86\u3002 \u3000\u3000\u706f\u5149\u4e4b\u4e0b\uff0c\u53ea\u89c1\u9648\u5149\u7167\u6ee1\u9762\u60ca\u559c\u7684\u795e\u60c5\uff0c\u674e\u5357\u661f\u8fd8\u672a\u66fe\u5411\u4ed6\u9053\u6b49\uff0c\u4ed6\u5df2\u5148\u6293\u7740\u4e86\u674e\u5357\u661f \u7684\u624b\u8bf4\u9053\uff1a\u201c\u4f60\u56de\u6765\u4e86\uff1f\u6211\u8fd8\u4ee5\u4e3a\u4f60\u51fa\u4e86\u4e8b\u5462\uff01\u201d \u3000\u3000\u674e\u5357\u661f\u9053\u4e86\u4e2a\u6b49\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eca\u65e5\u8fdb\u57ce\uff0c\u5728\u8001\u9556\u5934\u6234\u5747\u5bb6\u91cc\uff0c\u89c1\u5230\u4e86\u91d1\u9010\u6d41\u4e86\u3002\u4f60\u731c\u4ed6 \u662f\u8c01\uff1f\u201d\u9648\u5149\u7167\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u544a\u8bc9\u8fc7\u6211\uff0c\u4ed6\u662f\u4f60\u7684\u5f02\u59d3\u5144\u5f1f\u3002\u201d\u674e\u5357\u661f\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u53ef\u662f \u4ed6\u4e5f\u662f\u4f60\u7684\u5f02\u59d3\u5144\u5f1f\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d\u9648\u5149\u7167\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u4f60\u7684\u5144\u5f1f \u672c\u6765\u4e5f\u5c31\u662f\u6211\u7684\u5144\u5f1f\u3002\u201d\u674e\u5357\u661f\u9053\uff1a\u201c\u8bdd\u53ef\u4ee5\u8fd9\u6837\u8bf4\uff0c\u4f46\u6211\u8bf4\u7684\u5374\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u4e8c\u5341\u5e74 \u524d\uff0c\u6709\u4e00\u4f4d\u540d\u6ee1\u5929\u4e0b\u7684\u91d1\u4e16\u9057\u5927\u4fa0\uff0c\u4ed6\u548c\u4f60\u7684\u7239\u7239\u662f\u5f88\u597d\u7684\u670b\u53cb\uff0c\u662f\u5417\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u6240\u8bf4\u7684\u8fd9\u4f4d\u91d1\u9010\u6d41\u6562\u60c5\u5c31\u662f\u91d1\u5927\u4fa0\u7684\u513f\u5b50\uff1f\u201d\u674e \u5357\u661f\u9053\uff1a\u201c\u6b63\u662f\u3002\u4ed6\u7ea6\u4f60\u53bb\u548c\u4ed6\u89c1\u9762\u5462\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u559c\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u548c\u6211\u5bb6\u662f\u4e16\u4ea4\uff0c\u6211\u7239\u7239\u65f6\u65f6\u63d0\u8d77\u4ed6\u7684\u3002\u8fd9\u4f4d\u91d1\u5144\u6211\u7406\u8be5\u53bb\u62dc\u4f1a \u4ed6\uff0c\u53ef\u60dc\u2026\u2026\u674e\u5357\u661f\u8be7\u9053\uff1a\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff0c\u201c\u8fd9\u4e24\u5929\u6211\u6050\u6015\u4e0d\u80fd\u79bb\u5f00\u8fd9\u91cc\u3002\u201d\u674e\u5357\u661f\u9053\uff1a\u201c\u8fd9\u91cc\u51fa\u4e86\u4ec0\u4e48\u4e8b\u4e86\uff1f\u201d\u9648\u5149 \u7167\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u4e0d\u8fc7\u4eca\u65e5\u53d1\u73b0\u4e86\u6709\u4e9b\u53ef\u7591\u7684\u4eba\u7269\u6765\u5230\u897f\u5c71\u3002\u5728\u4e0a\u5c71\u91c7\u836f\u7684\u548c\u5c1a\u5148\u540e\u89c1\u5230\u51e0 \u6279\uff0c\u6709\u9ed1\u9053\u4e0a\u7684\u5389\u5bb3\u89d2\u8272\uff0c\u6709\u5e2e\u4f1a\u4e2d\u7684\u9996\u9886\uff0c\u8fd8\u6709\u4e24\u4e2a\u4ed6\u4eec\u77e5\u9053\u662f\u5927\u5185\u9ad8\u624b\u7684\u8eab\u4efd\u7684\uff0c\u4e5f\u8ddf \u7740\u8fd9\u4e9b\u4eba\u6df7\u5728\u4e00\u8d77\u3002\u5982\u4eca\u8fd8\u4e0d\u662f\u6e38\u6625\u65f6\u8282\uff0c\u8fd9\u4e9b\u4eba\u805a\u96c6\u897f\u5c71\uff0c\u65b9\u4e08\u4e0d\u80fd\u4e0d\u52a0\u610f\u63d0\u9632\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u970d\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u4f60\u4eec\u6050\u6015\u8fd9\u4e9b\u4eba\u662f\u6765\u641c\u7d22\u6211\u7684\u5427\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u60f3\u6765\u4ed6\u4eec\u662f\u5b9a\u6709\u56fe\u8c0b\uff0c\u4e0d\u8fc7\u662f\u5426\u5bf9\u4ed8\u8001\u5144\uff0c\u5374\u4e5f\u96be\u8bf4\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u6084\u6084\u8d70\u4e86\u597d\u4e86\u3002\u514d\u5f97\u8fde\u7d2f\u4e86\u4e00\u4f17\u50e7\u4eba\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u538b\u4f4e\u4e86\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u674e\u5144\u4e0d\u662f\u5916\u4eba\uff0c\u6211\u53ef\u4ee5\u544a\u8bc9\u3002 \u6b64\u5904\u7684\u65b9\u4e08\u7a7a\u7167\u5927\u5e08\u548c\u6297\u6e05\u4e49\u58eb\u662f\u6709\u79d8\u5bc6\u6765\u5f80\u7684\u3002\u6545\u6b64\u65b9\u4e08\u5429\u5490\u52a0\u610f\u63d0\u9632\uff0c\u5012\u4e0d\u662f\u5b8c\u5168\u4e3a\u4e86 \u4f60\u7684\u7f18\u6545\uff0c\u4f60\u5c1a\u672a\u75ca\u6108\uff0c\u6b64\u65f6\u82e5\u8d70\uff0c\u65b9\u4e08\u5fc3\u4e2d\u66f4\u8981\u4e0d\u5b89\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u8fd8\u5728\u5c71\u4e0a\u5417\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u5165\u9ed1\u4e4b\u540e\uff0c\u5e99\u91cc\u7684\u548c\u5c1a\u6015\u5f15\u8d77\u4ed6\u4eec\u7684\u7591\u5fc3\uff0c\u4e0d\u6562\u51fa\u53bb\u3002\u4e5f\u4e0d\u77e5\u4ed6\u4eec\u8d70\u4e86\u6ca1 \u6709\uff1f\u4f46\u6211\u521a\u521a\u4ece\u5c71\u4e0a\u56de\u6765\uff0c\u5374\u6ca1\u6709\u78b0\u89c1\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u5341\u5206\u611f\u6fc0\uff0c\u8bf4\u9053\uff1a\u201c\u9648\u5144\uff0c\u4f60\u4e3a\u4e86\u6211\u7684\u7f18\u6545\uff0c\u4e0a\u5c71\u5192\u9669\u627e\u5bfb\uff0c\u6211\u771f\u4e0d\u77e5\u8be5\u5982\u4f55\u8c22 \u4f60\u624d\u597d\u3002\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53ef\u60dc\u8fd9\u628a\u7384\u94c1\u5b9d\u5251\u662f\u9010\u6d41\u9001\u7ed9\u6211\u7684\uff0c\u5374\u4e0d\u4fbf\u8f6c\u9001\u522b\u4eba\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u6211\u5728\u8fd9\u91cc\u4f5c\u5ba2\uff0c\u5bfa\u4e2d\u53ef\u80fd\u906d\u9047\u707e\u7978\u4e4b\u65f6\uff0c\u6211\u662f\u51b3\u4e0d\u80fd\u79bb\u5f00\u8fd9\u91cc\u7684\u3002\u6240\u4ee5\u5fc5 \u987b\u591a\u7b49\u51e0\u5929\uff0c\u67e5\u660e\u4e86\u8fd9\u4e9b\u4eba\u7684\u4e0b\u843d\uff0c\u77e5\u9053\u786e\u5b9e\u662f\u5e73\u5b89\u5927\u4e8b\u4e86\uff0c\u6211\u624d\u53ef\u4ee5\u5230\u4eac\u57ce\u53bb\u62dc\u8bbf\u91d1\u9010 \u6d41\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u6211\u60f3\u4f60\u4eec\u53ef\u4ee5\u653e\u5fc3\uff0c\u53ea\u8981\u6211\u4e0d\u662f\u5728\u5bfa\u4e2d\u516c\u7136\u9732\u9762\uff0c\u90a3\u4e9b\u4eba\u5927\u7ea6\u4e0d\u4f1a\u5230\u8fd9\u91cc \u641c\u6270\u7684\uff0c\u8fc7\u4e86\u5143\u5bb5\uff0c\u4ed6\u4eec\u60f3\u6765\u4e5f\u8be5\u8d70\u4e86\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u8be7\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u6211\u662f\u636e\u7406\u63a8\u6d4b\u3002\u5bfa\u4e2d\u4e0e\u6297\u6e05\u4e49\u58eb\u6697\u901a\u6d88\u606f\u7684\u79d8\u5bc6\u5018\u82e5\u662f\u7ed9\u5b98\u5e9c\u77e5\u9053\uff0c\u5b98\u5e9c \u4e00\u5b9a\u4f1a\u6d3e\u5175\u56f4\u5bfa\uff0c\u4e0d\u5fc5\u4f7f\u7528\u6c5f\u6e56\u4eba\u7269\u5148\u6765\u7aa5\u63a2\u7684\u3002\u5148\u6765\u7aa5\u63a2\uff0c\u90a3\u4e0d\u662f\u6253\u8349\u60ca\u86c7\u4e86\u4e48\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e9b\u4eba\u5728\u4e2d\u5348\u65f6\u5206\u5df2\u7ecf\u9646\u7eed\u4e0a\u4e86\u5c71\u7684\uff0c\u4ed6\u4eec\u8fdf\u81f3\u73b0\u5728\u8fd8\u6ca1\u6709\u5230\u5bfa\u4e2d \u62ff\u4eba\uff0c\u770b\u6765\u786e\u662f\u4e0d\u50cf\u8981\u6765\u5bf9\u4ed8\u5367\u4f5b\u5bfa\u7684\u4e86\u3002\u4f46\u4f60\u53c8\u600e\u77e5\u9053\u4ed6\u4eec\u81f3\u8fdf\u5728\u8fc7\u4e86\u5143\u5bb5\u4e4b\u540e\u3002\u5c31\u4f1a\u8d70 \u5462\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u4ed6\u4eec\u6216\u8bb8\u662f\u6765\u5c71\u4e2d\u641c\u67e5\u6709\u6ca1\u6709\u9003\u72af\u81e7\u533f\uff0c\u6216\u8bb8\u662f\u4e3a\u4e86\u522b\u4e8b\u3002\u5367\u4f5b\u5bfa\u662f\u8457\u540d \u7684\u4f5b\u6559\u5723\u5730\u4e4b\u4e00\uff0c\u6e05\u89c4\u6212\u5f8b\uff0c\u5353\u8457\u58f0\u8a89\u3002\u4ed6\u4eec\u4e0d\u6562\u6000\u7591\u5bfa\u4e2d\u65b9\u4e08\u4f1a\u6536\u5bb9\u6211\u8fd9\u4e2a\u9003\u72af\u3002\u6240\u4ee5\u53ea \u8981\u6211\u4e0d\u662f\u5728\u5bfa\u4e2d\u516c\u7136\u9732\u9762\uff0c\u6599\u60f3\u65e0\u59a8\u3002\u4f60\u4eec\u4eca\u5929\u53d1\u73b0\u7684\u8fd9\u4e9b\u4eba\u65e2\u7136\u5927\u534a\u662f\u6c5f\u6e56\u4eba\u7269\uff0c\u9ed1\u9053\u4e2d \u7684\u4e60\u60ef\uff0c\u5728\u4e00\u4e2a\u964c\u751f\u7684\u5730\u65b9\u505c\u7559\u662f\u4e0d\u4f1a\u8d85\u8fc7\u4e09\u65e5\u7684\u3002\u6545\u6b64\uff0c\u4e0d\u8bba\u4ed6\u4eec\u662f\u4e3a\u4e86\u4f55\u4e8b\uff0c\u4e09\u5929\u4e4b\u5185 \u65e0\u7ed3\u679c\uff0c\u4ed6\u4eec\u90fd\u4f1a\u8d70\u7684\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u4e5f\u6709\u7406\u3002\u4e0d\u8fc7\uff0c\u6211\u4eec\u8fd8\u662f\u8981\u9884\u9632\u4e07\u4e00\uff0c\u6700\u5c11\u8fd9\u4e9b\u5929\u6211\u4eec\u662f\u5e94\u5f53\u7559\u5728 \u5bfa\u4e2d\uff0c\u4e0e\u50e7\u4f17\u5171\u540c\u62c5\u5f53\u98ce\u9669\u7684\u4e86\u3002\u201d \u3000\u3000\u674e\u6709\u661f\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\u3002\u597d\u4e86\uff0c\u4e3a\u4e86\u6211\u7684\u7f18\u6545\uff0c\u7d2f\u4e86\u4f60\u4e00\u6574\u5929\uff0c\u73b0\u5728\u4f60\u4e5f\u8be5\u7761\u4e86\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u81ea\u5df1\u5374\u7761\u4e0d\u7740\u89c9\u3002\u539f\u6765\u4ed6\u6562\u65ad\u5b9a\u8fd9\u4e9b\u4eba\u4e0d\u662f\u5bf9\u4ed8\u5367\u4f5b\u5bfa\uff0c\u6562\u65ad\u5b9a\u4ed6\u4eec\u8fc7\u4e86\u5143\u5bb5\u5c31 \u8d70\uff0c\u8fd9\u5e76\u975e\u4ec5\u662f\u201c\u63a8\u6d4b\u201d\uff0c\u800c\u662f\u4ed6\u786e\u5207\u77e5\u9053\u7684\u3002\u65e9\u5728\u534a\u5e74\u4e4b\u524d\uff0c\u5df2\u7ecf\u6709\u4ed6\u7684\u5bf9\u5934\u4eba\u7269\uff0c\u7ea6\u4ed6 \u5728\u4eca\u5e74\u7684\u5143\u5bb5\u4e4b\u591c\uff0c\u4e09\u66f4\u65f6\u5206\uff0c\u5728\u897f\u5c71\u79d8\u9b54\u5d16\u76f8\u4f1a\u7684\u4e86\u3002 \u3000\u3000\u90a3\u5c01\u7ea6\u4f1a\u7684\u4e66\u4fe1\u662f\u4ed6\u7684\u5bf9\u5934\u8f97\u8f6c\u6258\u4eba\u9009\u5230\u4ed6\u7684\u624b\u4e0a\u7684\uff0c\u90a3\u4e2a\u5bf9\u5934\u4eba\u7269\u6599\u4ed6\u5fc3\u9ad8\u6c14\u50b2\u4e0d\u4f1a \u4e0d\u63a5\u53d7\u8fd9\u4e2a\u6311\u6218\u3002 \u3000\u3000\u674e\u5357\u661f\u8fd9\u6b21\u5165\u4eac\uff0c\u672c\u6765\u4e5f\u662f\u8981\u63a5\u53d7\u8fd9\u4e2a\u6311\u6218\u7684\uff0c\u52ab\u8428\u798f\u9f0e\u7684\u5bff\u793c\uff0c\u4e0d\u8fc7\u662f\u540e\u6765\u78b0\u89c1\u4e86\u53f2 \u7ea2\u82f1\u4e4b\u540e\uff0c\u624d\u751f\u51fa\u6765\u7684\u4e8b\u60c5\u3002\u51fa\u4e86\u8fd9\u4ef6\u4e8b\u6e05\u4e4b\u540e\uff0c\u674e\u5357\u661f\u66fe\u7ecf\u60f3\u8981\u6539\u53d8\u4e3b\u610f\uff0c\u4e0d\u8d74\u8fd9\u4e2a\u7ea6 \u4f1a\uff0c\u5148\u53bb\u6551\u53f2\u7ea2\u82f1\u3002\u4ed6\u662f\u51c6\u5907\u5728\u4eca\u65e5\u53bb\u89c1\u4e86\u91d1\u9010\u6d41\u56de\u6765\uff0c\u4fbf\u5373\u5230\u79d8\u9b54\u5d16\u523b\u77f3\u7559\u4e66\uff0c\u620f\u5f04\u90a3\u5bf9 \u5934\u4e00\u756a\uff0c\u8ba9\u4ed6\u6251\u4e00\u4e2a\u7a7a\uff0c\u81ea\u5df1\u5219\u4e00\u8d70\u4e86\u4e4b\u7684\u3002 \u3000\u3000\u4f46\u662f\u73b0\u5728\u4ed6\u5374\u662f\u4e0d\u80fd\u4e0d\u8d74\u8fd9\u4e2a\u7ea6\u4f1a\u4e86\u3002\u4e00\u6765\u56e0\u4e3a\u4ed6\u7684\u5bf9\u5934\u6bd4\u4ed6\u9884\u6599\u6765\u5f97\u66f4\u65e9\uff0c\u5982\u4eca\u5df2\u7ecf \u9080\u4e86\u8bb8\u591a\u6c5f\u6e56\u4eba\u7269\u85cf\u5728\u5c71\u4e0a\uff1b\u4e8c\u6765\u4ed6\u53d7\u4e86\u9648\u5149\u7167\u4e0e\u65b9\u4e08\u7684\u6551\u62a4\u4e4b\u6069\uff0c\u8fd9\u4ef6\u4e8b\u4e5f\u5e94\u8be5\u7531\u4ed6\u4e2a\u4eba \u4e86\u7ed3\uff0c\u514d\u5f97\u8fde\u7d2f\u4e8e\u4ed6\u6709\u6069\u4e4b\u4eba\u3002\u674e\u5357\u661f\u6253\u5b9a\u4e86\u8fd9\u4e2a\u4e3b\u610f\uff0c\u6545\u6b64\u5e76\u6ca1\u6709\u5411\u9648\u5149\u7167\u5410\u9732\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u5bfa\u4e2d\u4e0a\u5c71\u91c7\u836f\u7684\u548c\u5c1a\uff0c\u5e76\u6ca1\u53d1\u73b0\u90a3\u4e9b\u4eba\u7684\u8e2a\u8ff9\u3002\u4e5f\u4e0d\u77e5\u4ed6\u4eec\u662f\u8d70\u4e86\u8fd8\u662f\u85cf\u533f\u8d77 \u6765\u3002\u4e0d\u8fc7\u65e2\u7136\u6ca1\u6709\u4eba\u5230\u5bfa\u4e2d\u5bfb\u5448\uff0c\u4e00\u4f17\u50e7\u5f92\u5927\u90fd\u653e\u4e86\u51e0\u5206\u5fc3\u4e8b\u4e86\u3002 \u3000\u3000\u5143\u5bb5\u90a3\u65e5\uff0c\u65e5\u95f4\u4e5f\u662f\u5e73\u5b89\u65e0\u4e8b\u3002\u5230\u4e86\u665a\u4e0a\u5bfa\u4e2d\u867d\u7136\u4e0d\u884c\u6c11\u95f4\u98ce\u4fd7\uff0c\u5e86\u795d\u5143\u5bb5\uff0c\u4f46\u4e5f\u8981\u505a \u4e00\u5802\u4f5b\u4e8b\u3002\u9648\u3001\u674e\u4e8c\u4eba\u662f\u5916\u4eba\u3002\u4e0d\u4fbf\u53c2\u52a0\uff0c\u4e00\u65e9\u4fbf\u7761\u3002 \u3000\u3000\u9648\u5149\u7167\u62c5\u7740\u5fc3\u4e8b\uff0c\u95ed\u4e0a\u4e86\u773c\u775b\uff0c\u5374\u7761\u4e0d\u7740\u89c9\u3002\u7ea6\u83ab\u4e8c\u66f4\u65f6\u5206\uff0c\u5ffd\u542c\u5f97\u6089\u7d22\u58f0\u54cd\uff0c\u5bf9\u9762\u90a3 \u5f20\u5e8a\u4e0a\u7684\u674e\u5357\u661f\u4f3c\u4e4e\u6b63\u5728\u722c\u8d77\u6765\u3002\u9648\u5149\u7167\u6709\u70b9\u7eb3\u7f55\uff1a\u201c\u4e09\u66f4\u534a\u591c\uff0c\u4ed6\u8d77\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u6b63\u8981\u51fa \u58f0\uff0c\u5ffd\u89c9\u4e00\u7f15\u5e7d\u9999\uff0c\u5438\u5165\u9f3b\u89c2\uff0c\u6709\u8bf4\u4e0d\u51fa\u7684\u8212\u670d\uff0c\u9648\u5149\u7167\u660f\u660f\u5fcd\u7761\uff0c\u8fde\u5fd9\u4e00\u54ac\u820c\u5934\u3002\u6109\u5077\u7684 \u6478\u51fa\u4e00\u9897\u836f\u4e38\uff0c\u7eb3\u5165\u53e3\u4e2d\uff0c\u8fd9\u662f\u7528\u5929\u5c71\u96ea\u83b2\u52a0\u4e0a\u82e5\u5e72\u914d\u836f\u6240\u70ae\u5236\u7684\u78a7\u7075\u4e39\uff0c\u80fd\u89e3\u5de8\u6bd2\u3002 \u3000\u3000\u9648\u5149\u7167\u5026\u773c\u6b32\u7720\uff0c\u5fc3\u5934\u8fd8\u662f\u6e05\u9192\u7684\uff0c\u4ed6\u7b2c\u4e00\u4e2a\u53cd\u5e94\u662f\u51fa\u4e4e\u610f\u5916\u7684\u60ca\u60f6\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c0f\u5b50 \u96be\u9053\u662f\u6765\u5367\u4f5b\u5bfa\u5367\u5e95\u7684\u4e48\uff1f\u4e0d\u597d\uff0c\u4ed6\u7684\u6b66\u529f\u8fdc\u80dc\u4e8e\u6211\uff0c\u82e5\u662f\u7ed9\u4ed6\u77e5\u9053\u6211\u672a\u7761\u7740\uff0c\u53ea\u6015\u6211\u7684\u6027 \u547d\u96be\u4fdd\uff01\u201d \u3000\u3000\u9648\u5149\u7167\u5bb6\u5b66\u6e0a\u6e90\uff0c\u6545\u6b64\u867d\u7136\u51fa\u9053\u4e0d\u8fc7\u4e24\u5e74\uff0c\u4e5f\u53ef\u4ee5\u7b97\u5f97\u662f\u4e2a\u6c5f\u6e56\u4e0a\u7684\u884c\u5bb6\u4e86\u3002\u5bf9\u4e8e\u6c5f\u6e58 \u4e0a\u5e38\u7528\u7684\u8ff7\u9999\uff0c\u4ed6\u4e5f\u77e5\u9053\u4e00\u4e9b\u3002\u541e\u4e86\u78a7\u7075\u4e39\u4e4b\u540e\uff0c\u4ed6\u7acb\u5373\u5c31\u80fd\u8fa8\u522b\uff0c\u8fd9\u662f\u4e00\u79cd\u65e0\u6bd2\u7684\u8ff7\u9999\uff0c \u4f46\u836f\u6027\u5374\u6bd4\u4e00\u822c\u6c5f\u6e56\u4e0a\u5e38\u7528\u7684\u8ff7\u9999\u5389\u5bb3\u3002\u770b\u6765\u674e\u5357\u661f\u53ea\u662f\u8981\u4ed6\u719f\u7761\u4e0d\u9192\uff0c\u5374\u65e0\u610f\u4f24\u5bb3\u4ed6\u3002 \u3000\u3000\u9648\u5149\u7167\u6697\u81ea\u60f3\u9053\uff1a\u201c\u4ed6\u7684\u6b66\u529f\u8fdc\u80dc\u4e8e\u6211\uff0c\u8981\u6740\u6211\u90a3\u662f\u6613\u4e8e\u53cd\u638c\u3002\u5373\u4f7f\u4e0d\u60f3\u6740\u6211\uff0c\u53ea\u70b9\u4e86 \u6211\u7684\u6655\u7761\u7a74\u6211\u4e5f\u662f\u6beb\u65e0\u529e\u6cd5\u3002\u4ed6\u6539\u7528\u8fd9\u79cd\u65e0\u6bd2\u7684\u8ff7\u9999\uff0c\u6562\u60c5\u662f\u60f3\u7792\u7740\u6211\u53bb\u5e72\u4ec0\u4e48\u4e8b\u60c5\u5427\uff1f \u597d\uff0c\u6211\u4e14\u6682\u4e0d\u58f0\u5f20\uff0c\u770b\u770b\u4ed6\u5230\u5e95\u8981\u5e72\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u8981\u77e5\u4f7f\u7528\u65e0\u6bd2\u7684\u8ff7\u9999\u4e0e\u70b9\u6655\u7761\u7a74\u6240\u5f97\u7684\u6548\u679c\u867d\u7136\u76f8\u540c\uff0c\u4f7f\u7528\u70b9\u7a74\u7684\u624b\u6bb5\u65bd\u4e4b\u4e8e\u670b\u53cb\u5374\u662f \u5927\u5927\u7684\u4e0d\u656c\uff0c\u800c\u4e14\u5bf9\u8eab\u4f53\u4e5f\u591a\u5c11\u6709\u70b9\u635f\u5bb3\uff0c\u6545\u6b64\u9648\u5149\u7167\u636e\u6b64\u5224\u65ad\uff0c\u53ef\u77e5\u674e\u5357\u661f\u5b9e\u662f\u5bf9\u4ed6\u5e76\u65e0 \u6076\u610f\u3002 \u3000\u3000\u5f53\u4e0b\uff0c\u9648\u5149\u7167\u5047\u88c5\u719f\u7761\uff0c\u53ea\u89c1\u674e\u5357\u661f\u722c\u4e86\u8d77\u6765\uff0c\u201c\u5693\u201d\u7684\u4e00\u58f0\uff0c\u6253\u71c3\u706b\u77f3\uff0c\u5728\u684c\u4e0a\u53d6\u4e86 \u7eb8\u7b14\uff0c\u5306\u5306\u7684\u5199\u4e86\u51e0\u4e2a\u5b57\uff0c\u5c31\u6084\u6084\u7684\u4ece\u7a97\u5b50\u8df3\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u9648\u5149\u7167\u8ddf\u7740\u8d77\u6765\uff0c\u4eae\u706f\u4e00\u770b\uff0c\u53ea\u89c1\u674e\u5357\u661f\u5199\u7684\u662f\u201c\u5929\u660e\u5373\u56de\uff0c\u8bf7\u52ff\u58f0\u5f20\u201d\u516b\u4e2a\u5b57\uff0c\u770b\u6765 \u674e\u5357\u661f\u867d\u7136\u4f7f\u4e86\u8ff7\u9999\uff0c\u4e5f\u8fd8\u9632\u4ed6\u672a\u5230\u5929\u660e\u5373\u9192\uff0c\u662f\u4ee5\u7559\u5b57\u4ea4\u4ee3\u3002 \u3000\u3000\u9648\u5149\u7167\u541e\u4e86\u78a7\u7075\u4e39\uff0c\u6b64\u65f6\u5df2\u662f\u7761\u610f\u5168\u6d88\uff0c\u4e8e\u662f\u4fbf\u8ddf\u7740\u8ffd\u51fa\u53bb\u3002\u8fd9\u665a\u662f\u5143\u5bb5\uff0c\u6708\u5149\u660e\u4eae\uff0c \u9648\u5149\u7167\u7ad9\u5728\u5c4b\u9876\uff0c\u9690\u9690\u53ef\u89c1\u674e\u5357\u661f\u7684\u5f71\u5b50\u5df2\u7ecf\u51fa\u4e86\u5367\u4f5b\u5bfa\uff0c\u6ca1\u5165\u4e86\u6811\u6797\u4e2d\u4e86\u3002\u9648\u5149\u7167\u84e6\u5730\u60f3 \u8d77\u674e\u5357\u661f\u8bf4\u8fc7\u7684\u51e0\u53e5\u8bdd\uff0c\u4ed6\u8bf4\u53ea\u8981\u4ed6\u4e0d\u662f\u5728\u5bfa\u4e2d\u516c\u7136\u9732\u9762\uff0c\u90a3\u4e9b\u4eba\u5c31\u4e0d\u4f1a\u4fb5\u72af\u5367\u4f5b\u5bfa\u3002\u60f3\u8d77 \u6b64\u4e8b\uff0c\u9648\u5149\u7167\u5fc3\u5934\u4e00\u52a8\uff0c\u5bf9\u674e\u5357\u661f\u8fd9\u4e2a\u8be1\u79d8\u7684\u884c\u52a8\u5df2\u662f\u731c\u5230\u4e86\u51e0\u5206\uff0c\u5fc3\u60f3\uff1a\u201c\u83ab\u975e\u4ed6\u662f\u8981\u53bb \u5077\u4f1a\u90a3\u4e9b\u4eba\uff1f\u201d\u674e\u5357\u661f\u5df2\u7136\u7559\u5b57\u8bf7\u4ed6\u4e0d\u8981\u58f0\u5f20\uff0c\u9648\u5149\u7167\u60f3\u4e86\u4e00\u60f3\uff0c\u51b3\u5b9a\u72ec\u81ea\u4fa6\u5bdf\uff0c\u4e5f\u5c31\u4e0d\u53bb \u544a\u8bc9\u65b9\u4e08\u4e86\u3002 \u3000\u3000\u9648\u5149\u7167\u7684\u6bcd\u4eb2\u662f\u51b0\u5ddd\u5929\u5973\u7684\u4f8d\u5973\uff0c\u7279\u957f\u8f7b\u529f\uff0c\u662f\u4ee5\u9648\u5149\u7167\u7684\u6b66\u529f\u867d\u7136\u4e0d\u53ca\u674e\u5357\u661f\uff0c\u8f7b\u529f \u5374\u5dee\u4e0d\u4e86\u591a\u5c11\u3002\u4ed6\u5728\u540e\u9762\u8fdc\u8fdc\u7684\u8ddf\u7740\u674e\u5357\u661f\uff0c\u674e\u5357\u661f\u4e00\u5fc3\u8d74\u79d8\u9b54\u5d16\u4e4b\u7ea6\uff0c\u7adf\u6ca1\u53d1\u89c9\u540e\u9762\u6709 \u4eba\u3002 \u3000\u3000\u79d8\u9ebe\u5d16\u4e0e\u5367\u4f5b\u5bfa\u4e00\u5728\u5c71\u5317\uff0c\u4e00\u5728\u5c71\u5357\uff0c\u76f8\u53bb\u5341\u4f59\u91cc\u3002\u90a3\u4e00\u5e26\u4e71\u77f3\u5982\u7b0b\uff0c\u5bf8\u8349\u4e0d\u751f\uff0c\u662f\u897f \u5c71\u4e4b\u4e0a\u6700\u8352\u51c9\u9690\u50fb\u7684\u4e00\u4e2a\u5730\u65b9\uff0c\u5e73\u65f6\u730e\u4eba\u90fd\u4e0d\u4f1a\u5230\u7684\u3002\u9648\u5149\u7167\u89c1\u674e\u5357\u661f\u76f4\u5954\u79d8\u9b54\u5d16\uff0c\u751a\u89c9\u5947 \u602a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u53bb\u90a3\u91cc\u505a\u4ec0\u4e48\uff1f\u96be\u9053\u90a3\u4e9b\u4eba\u5c31\u85cf\u5728\u79d8\u9b54\u5d16\uff1f\u4f46\u4ed6\u53c8\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u811a\u6b65\u7a81\u7136\u52a0\u5feb\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5df2\u6ca1\u5165\u4e71\u77f3\u5806\u4e2d\u3002\u9648\u5149\u7167\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u82cd\u8001 \u7684\u58f0\u97f3\u5df2\u5728\u8bf4\u9053\uff1a\u201c\u5389\u516c\u5b50\u679c\u662f\u4fe1\u4eba\uff0c\u4f9d\u7ea6\u6765\u4e86\uff0c\u4f69\u670d\uff0c\u4f69\u670d\uff01\u201d \u3000\u3000\u9648\u5149\u7167\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u54a6\uff0c\u4ed6\u4eec\u600e\u4e48\u628a\u674e\u5357\u661f\u53eb\u4f5c\u5389\u516c\u5b50\uff1f\u201d\u201c\u674e\u201d\u201c\u5389\u201d\u4e8c\u5b57\uff0c \u53d1\u97f3\u76f8\u4f3c\uff0c\u4f46\u4e00\u4e2a\u662f\u201c\u53bb\u58f0\u201d\uff0c\u4e00\u4e2a\u662f\u201c\u5165\u58f0\u201d\uff0c\u82e5\u7528\u7eaf\u6b63\u7684\u5317\u4eac\u8bdd\u6765\u8bf4\uff0c\u662f\u53ef\u4ee5\u5206\u522b\u51fa\u6765 \u7684\u3002 \u3000\u3000\u674e\u5357\u661f\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u7ea6\u6211\u5230\u6b64\uff0c\u610f\u6b32\u4f55\u4e3a\uff1f\u201d \u3000\u3000\u90a3\u82cd\u8001\u7684\u58f0\u97f3\u9053\uff1a\u201c\u771f\u4eba\u9762\u524d\u4e0d\u8bf4\u5047\u8bdd\uff0c\u5389\u516c\u5b50\uff0c\u4f60\u5728\u6211\u4eec\u9762\u524d\u5927\u53ef\u4e0d\u5fc5\u9690\u7792\u8eab\u4efd\uff01\u5929 \u9b54\u6559\u7956\u5e08\u5389\u80dc\u7537\u662f\u4e0d\u662f\u4f60\u7684\u59d1\u5a46\uff1f\u201d \u3000\u3000\u5389\u80dc\u7537\u53bb\u4e16\u5df1\u6709\u4e8c\u5341\u4f59\u5e74\uff0c\u4f46\u5979\u5f53\u5e74\u66fe\u7ecf\u7edd\u9876\u6b66\u529f\u9547\u670d\u6b66\u6797\uff0c\u8fde\u5929\u5c71\u6d3e\u7684\u8001\u638c\u95e8\u5510\u6653\u6f9c \u90fd\u66fe\u8d25\u5728\u5979\u7684\u638c\u4e0b\u3002\u662f\u4ee5\u9648\u5149\u7167\u542c\u89c1\u8fd9\u8001\u8005\u8bf4\u51fa\u4e86\u674e\u5357\u661f\u7684\u8eab\u4efd\uff0c\u4e5f\u7981\u4e0d\u4f4f\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3 \u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u672c\u9886\u90a3\u4e48\u4e86\u5f97\uff0c\u539f\u6765\u662f\u5389\u5bb6\u7684\u4eba\uff0c\u54ce\u5440\uff0c\u5929\u9b54\u6559\u4e43\u662f\u88ab\u6d88\u706d\u4e86\u7684 \u90aa\u6559\uff0c\u5018\u82e5\u8fd9\u8001\u8005\u6240\u8bf4\u7684\u4ed6\u7684\u8eab\u4efd\u4e0d\u5047\uff0c\u4ed6\u5c82\u4e0d\u662f\u51fa\u8eab\u4e0d\u6b63\u7684\u90aa\u6d3e\u4e2d\u4eba\u4e86\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u662f\u53c8\u600e\u4e48\u6837\uff1f\u4e0d\u662f\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5389\u516c\u5b50\u4e0d\u5fc5\u591a\u7591\uff0c\u6211\u4eec\u5e76\u65e0\u6076\u610f\uff0c\u53ea\u662f\u60f3\u8bf7\u4f60\u5230\u5f82\u5f95\u5c71\u53bb\uff0c\u91cd\u632f\u5929 \u9b54\u6559\u7684\u58f0\u5a01\uff0c\u6211\u4eec\u613f\u610f\u62e5\u6234\u4f60\u7ee7\u4efb\u6559\u4e3b\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u505a\u4ec0\u4e48\u6559\u4e3b\uff0c\u6211\u4e5f\u6ca1\u5de5\u592b\u4e0a\u5f82\u5f95\u5c71\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u9053\uff1a\u201c\u5389\u516c\u5b50\u6b64\u8a00\u5dee\u77e3\uff0c\u4f60\u7684\u7236\u4eb2\u662f\u5389\u7956\u5e08\u552f\u4e00\u7684\u4eb2\u4f84\u513f\uff0c\u4f60\u7684\u6bcd\u4eb2\u4e5f\u66fe\u505a\u8fc7\u5929 \u9b54\u6559\u7684\u6559\u4e3b\uff0c\u96be\u9053\u4f60\u5c31\u4e0d\u60f3\u91cd\u632f\u5bb6\u58f0\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u6211\u7236\u6bcd\u65e9\u5df2\u91d1\u76c6\u6d17\u624b\uff0c\u9000\u51fa\u6b66\u6797\u3002\u8fd9\u5929\u9b54\u6559\u4e09\u5b57\u518d\u4e5f\u4f11\u63d0\uff01\u201d \u3000\u3000\u90a3\u8001\u8005\u9053\uff1a\u201c\u4ee4\u5c0a\u4ee4\u5802\u91d1\u76c6\u6d17\u624b\uff0c\u4f60\u4e0d\u53ef\u4ee5\u91cd\u8d77\u7089\u7076\u5417\uff1f\u5389\u516c\u5b50\uff0c\u673a\u4e0d\u53ef\u5931\uff0c\u6709\u6211\u4eec\u8fd9 \u4e9b\u4eba\u62e5\u6234\u4f60\uff0c\u4f55\u6101\u5927\u4e8b\u4e0d\u6210\uff1f\u201d \u3000\u3000\u674e\u5357\u661f\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e9b\u4ec0\u4e48\u4e1c\u897f\uff1f\u6211\u624d\u4e0d\u8010\u70e6\u505a\u4f60\u4eec\u7684\u5934\u513f\u5462\uff01\u201d \u3000\u3000\u90a3\u8001\u8005\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u5389\u516c\u5b50\uff0c\u4f60\u4e5f\u4e0d\u8981\u5c0f\u770b\u4eba\u4e86\u3002\u4f60\u53ef\u77e5\u9053\uff0c\u4f60\u7684\u7239\u5a18\u5f53\u5e74\u5bf9 \u6211\uff0c\u4e5f\u4e0d\u6562\u6020\u6162\u5206\u6beb\u4e48\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u662f\u8c01\uff0c\u6211\u5c31\u662f\u4e0d\u5356\u4f60\u7684\u5e10\uff0c\u4f60\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u51b7\u7b11\u9053\uff1a\u201c\u597d\u72c2\u5984\u7684\u5c0f\u5b50\uff0c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u5f53\u771f\u662f\u8981\u4e0d\u5403\u8bf7\u9152\u5403\u7f5a\u9152\u4e86\uff01\u201d \u3000\u3000\u674e\u5357\u661f\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e00\u9f50\u4e0a\u5462\u8fd8\u662f\u8f66\u8f6e\u6218\uff1f\u968f\u4f60\u5212\u51fa\u9053\u513f\uff01\u201d \u3000\u3000\u90a3\u8001\u8005\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u771f\u662f\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\uff0c\u7adf\u6562\u5c0f\u89d1\u8001\u592b\uff01\u563f\uff0c\u563f\uff0c\u53ea\u8981\u4f60\u5728\u6211 \u7684\u638c\u4e0b\u8fc7\u5f97\u4e00\u767e\u62db\uff0c\u6211\u9633\u67d0\u4eba\u5c31\u8ba9\u4f60\u4e0b\u5c71\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u4e5f\u4e0d\u518d\u627e\u4f60\u9ebb\u70e6\u3002\u4f60\u82e5\u662f\u5728\u767e\u62db\u4e4b\u5185 \u8f93\u4e86\u7ed9\u6211\uff0c\u563f\uff0c\u563f\uff0c\u6211\u4e5f\u4e0d\u8981\u4f60\u7684\u6027\u547d\uff0c\u53ea\u8981\u4f60\u8ddf\u6211\u8d70\uff0c\u6709\u4f60\u7684\u4fbf\u5b9c\u4e86\u5427\uff1f\u201d \u3000\u3000\u773c\u770b\u53cc\u65b9\u5982\u7bad\u5728\u5f26\uff0c\u4e00\u89e6\u5373\u53d1\uff0c\u9648\u5149\u7167\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u7ba1\u674e\u5357\u661f\u662f\u4ec0\u4e48\u51fa\u8eab\uff0c\u6211\u65e2\u7136\u548c \u4ed6\u4ea4\u4e86\u670b\u53cb\uff0c\u5c31\u4e0d\u80fd\u8ba9\u670b\u53cb\u5403\u4e8f\u3002\u5bf9\u65b9\u82e5\u662f\u5355\u6253\u72ec\u6597\u4fbf\u7f62\uff1b\u82e5\u662f\u7fa4\u6bb4\uff0c\u6211\u9648\u5149\u7167\u5c31\u662f\u820d\u4e86\u6027 \u547d\uff0c\u4e5f\u975e\u5e2e\u4ed6\u4e0d\u53ef\uff01\u201d\u9648\u5149\u7167\u89c1\u8fc7\u674e\u5357\u661f\u7684\u529f\u592b\uff0c\u867d\u7136\u4e0d\u77e5\u9053\u90a3\u8001\u8005\u662f\u4ec0\u4e48\u4eba\uff0c\u4f46\u6599\u60f3\u4ee5\u674e \u5357\u661f\u7684\u529f\u592b\u5bf9\u4ed8\u4e00\u4e2a\u6c14\u529b\u5df1\u8870\u7684\u8001\u5934\uff0c\u603b\u4e0d\u81f3\u4e8e\u5728\u767e\u62db\u4e4b\u5185\u4fbf\u5373\u8d25\u9635\u3002\u6545\u6b64\u9648\u5149\u7167\u6253\u7b97\u6682\u4e0d \u9732\u9762\uff0c\u4e14\u770b\u770b\u4ed6\u4eec\u7684\u5355\u6253\u72ec\u6597\u7ed3\u679c\u5982\u4f55\u518d\u8bf4\u3002 \u3000\u3000\u4e0d\u6599\u5fc3\u5ff5\u672a\u5df1\uff0c\u5ffd\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u6709\u4e24\u6761\u9ed1\u5f71\uff0c\u5df2\u662f\u5411\u7740\u9648\u5149\u7167\u85c9\u8eab\u4e4b\u5904\u6251\u6765\uff0c\u9f50\u58f0\u559d \u9053\uff1a\u201c\u662f\u90a3\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff1f\u201d\u8fd9\u4e00\u4e0b\uff0c\u9648\u5149\u7167\u60f3\u4e0d\u9732\u9762\u4e5f\u4e0d\u884c\u4e86\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u6c49\u5b50\u89c1\u9648\u5149\u7167\u662f\u4e2a\u964c\u751f\u9762\u5b54\uff0c\u53c8\u7aef\u4e0d\u51fa\u201c\u6d77\u5e95\u201d\uff0c\u7acb\u5373\u4fbf\u6251\u4e0a\u53bb\u52a8\u624b\u3002\u674e\u5357\u661f\u53eb \u9053\uff1a\u201c\u9648\u5144\uff0c\u6b64\u4e8b\u4e0e\u4f60\u65e0\u5173\uff0c\u4f60\u56de\u53bb\u5427\uff01\u201d\u8ddf\u7740\u5411\u90a3\u8001\u8005\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u6211\u7684\u670b\u53cb\uff0c\u4f46\u4ed6\u5e76 \u4e0d\u77e5\u9053\u4f60\u6211\u7ea6\u4f1a\u4e4b\u4e8b\u3002\u8bf7\u4f60\u4eec\u7684\u4eba\u4f4f\u624b\uff01\u201d \u3000\u3000\u90a3\u8001\u8005\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u76f8\u4fe1\u4f60\u7684\u8bdd\uff0c\u8fd9\u5c0f\u5b50\u6211\u4e5f\u4e0d\u80fd\u8ba9\u4ed6\u8f7b\u6613\u56de\u53bb\u3002\u627e\u5148\u8981\u628a\u4ed6\u62ff \u884c\uff0c\u95ee\u8fc7\u53e3\u4f9b\uff0c\u518d\u4f5c\u5b9a\u593a\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u4ed7\u7740\u8f7b\u7075\u7684\u8eab\u6cd5\uff0c\u95ea\u5f00\u90a3\u4e24\u4eba\u7684\u8fde\u756a\u6251\u51fb\uff0c\u53ef\u662f\u90a3\u4e24\u4eba\u4e16\u975e\u6cdb\u6cdb\u4e4b\u8f88\uff0c\u4e00\u5bf9\u5224\u5b98 \u7b14\uff0c\u4e00\u679d\u5c0f\u82b1\u62a2\uff0c\u62db\u62db\u90fd\u662f\u6307\u5411\u9648\u5149\u7167\u7684\u7d27\u8981\u7a74\u9053\u3002\u9648\u5149\u7167\u6012\u4ece\u5fc3\u8d77\uff0c\u559d\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u8ba9\u4f60 \u4eec\u51e0\u62db\uff0c\u4f60\u4eec\u5f53\u6211\u662f\u597d\u6b3a\u8d1f\u7684\u4e48\uff1f\u201d\u5530\u7684\u62d4\u5251\u51fa\u9798\uff0c\u4fbf\u5373\u8fd8\u51fb\u3002 \u3000\u3000\u9648\u5149\u7167\u7684\u771f\u5b9e\u672c\u9886\u672a\u5fc5\u80dc\u5f97\u8fc7\u8fd9\u4e24\u4e2a\u6c49\u5b50\uff0c\u4f46\u4ed6\u8fd9\u67c4\u5251\u5374\u662f\u4ef6\u5b9d\u7269\u3002\u4ed6\u7684\u6bcd\u4eb2\u662f\u51b0\u5ddd\u5929 \u5973\u7684\u4f8d\u5973\uff0c\u5f53\u5e74\u51b0\u5ddd\u5929\u5973\u91c7\u53d6\u51b0\u7a9f\u4e2d\u7684\u4e07\u5e74\u5bd2\u7389\u70bc\u6210\u4e86\u51e0\u67c4\u201c\u51b0\u9b44\u5bd2\u5149\u5251\u201d\uff0c\u5269\u4e0b\u7684\u788e\u7389\u70bc \u6210\u56db\u67c4\u5b9d\u5251\uff0c\u5206\u8d60\u56db\u4e2a\u4f8d\u5973\uff0c\u9648\u5149\u7167\u7684\u6bcd\u4eb2\u5206\u5f97\u4e00\u67c4\u3002\u8fd9\u56db\u67c4\u5b9d\u5251\u867d\u7136\u6bd4\u4e0d\u4e0a\u201c\u51b0\u9b44\u5bd2\u5149 \u5251\u201d\uff0c\u4f46\u5251\u4e00\u51fa\u9798\uff0c\u4e5f\u80fd\u8ba9\u5bf9\u65b9\u611f\u5230\u51b7\u6c14\u6d78\u80a4\uff0c\u5947\u5bd2\u523a\u9aa8\u3002 \u3000\u3000\u9648\u5149\u7167\u5251\u4e00\u51fa\u9798\uff0c\u8fd9\u4e24\u4e2a\u6c49\u5b50\u90fd\u662f\u4e0d\u7ea6\u800c\u540c\u5730\u6253\u4e86\u4e00\u4e2a\u673a\u4f36\u4f36\u7684\u51b7\u6218\uff0c\u5fc3\u5934\u5927\u9a87\uff0c\u8fde\u5fd9 \u540e\u9000\u3002\u5176\u4e2d\u4e00\u4e2a\u4f7f\u7528\u201c\u5012\u8e29\u4e03\u661f\u201d\u7684\u8f7b\u8eab\u529f\u592b\u5012\u7eb5\uff0c\u811a\u672a\u843d\u5730\uff0c\u7a81\u7136\u89c9\u5f97\u819d\u76d6\u4e00\u9ebb\uff0c\u4f7f\u5012\u4e0b \u53bb\u4e86\u3002\u539f\u6765\u4ed6\u662f\u56e0\u53d7\u5bd2\u6d41\u6240\u4fb5\uff0c\u8840\u6db2\u4e0d\u80fd\u6d41\u7545\uff0c\u624b\u811a\u90fd\u51b7\u5f97\u9ebb\u6728\u4e86\uff0c\u8f7b\u529f\u81ea\u662f\u4e0d\u80fd\u5982\u5e38\u65bd \u5c55\u3002\u4f46\u9644\u8fd1\u51e0\u4e2a\u628a\u98ce\u7684\u6c49\u5b50\uff0c\u5374\u4ee5\u4e3a\u4ed6\u662f\u4e2d\u4e86\u6697\u5668\u3002 \u3000\u3000\u8d1f\u8d23\u628a\u98ce\u7684\u5934\u5b50\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6562\u65bd\u6697\u7b97\uff01\u201d\u4e00\u58f0\u9669\u559d\uff0c\u6697\u5668\u7eb7\u98de\uff0c\u900f\u9aa8\u9489\u3001\u94c1\u83b2 \u5b50\u3001\u98de\u8757\u77f3\u3001\u6ca1\u7fbd\u7bad\u3001\u6bd2\u84ba\u85dc\uff0c\u5404\u5f0f\u5404\u6837\u7684\u6697\u5668\uff0c\u5e94\u6709\u5c3d\u6709\uff0c\u90fd\u5411\u9648\u5149\u7167\u98de\u53bb\u3002 \u3000\u3000\u9648\u5149\u7167\u51b7\u7b11\u9053\uff1a\u201c\u778e\u4e86\u773c\u7684\u5f3a\u76d7\uff0c\u8c01\u653e\u6697\u5668\u6765\u4e86\uff1f\u4f60\u4eec\u65e2\u7136\u5b9a\u8981\u8bec\u8d56\uff0c\u90a3\u4e5f\u597d\uff0c\u5c31\u8ba9\u4f60 \u4eec\u89c1\u8bc6\u89c1\u8bc6\u6211\u7684\u6697\u5668\u5427\uff01\u201d\u638f\u51fa\u4e86\u4e00\u628a\u51b0\u9b44\u795e\u5f39\uff0c\u4e00\u626c\u624b\uff0c\u4ee5\u5929\u5973\u6563\u82b1\u7684\u624b\u6cd5\u5411\u7fa4\u76d7\u6d12\u53bb\u3002 \u3000\u3000\u8fd9\u51b0\u9b44\u795e\u5f39\u4e43\u662f\u5929\u4e0b\u6700\u5947\u602a\u7684\u6697\u5668\uff0c\u4efb\u4f55\u6697\u5668\u8bb2\u7a76\u7684\u4e0d\u5916\u662f\u51c6\u5934\u548c\u52b2\u9053\uff0c\u53ea\u6709\u8fd9\u51b0\u9b44\u795e \u5f39\u662f\u4ed7\u7740\u672c\u8eab\u7684\u9634\u5bd2\u4e4b\u6c14\u514b\u654c\u5236\u80dc\u3002\u51b0\u5f39\u4e00\u6d12\uff0c\u90a3\u4e9b\u4eba\u4e0d\u77e5\u9053\u6697\u5668\u7684\u6765\u5386\uff0c\u6709\u7684\u8eb2\u95ea\uff0c\u6709\u7684 \u5c31\u7528\u5175\u5668\u62d4\u6253\uff0c\u8eb2\u95ea\u7684\u8fd8\u597d\u4e00\u4e9b\uff0c\u7528\u5175\u5668\u62d4\u6253\u7684\uff0c\u51b0\u5f39\u4e00\u89e6\u5373\u788e\uff0c\u6bd4\u4f5c\u4e86\u4e00\u56e2\u5bd2\u5149\u51b7\u6c14\uff0c\u767b \u65f6\u523b\u9aa8\u4fb5\u808c\uff0c\u8840\u6db2\u90fd\u51e0\u4e4e\u4e3a\u4e4b\u51b7\u51dd\uff01\u8fd8\u6709\u4e24\u4e2a\u8eb2\u95ea\u4e0d\u5f00\uff0c\u7ed9\u51b0\u5f39\u6253\u7740\u4e86\u7a74\u9053\u7684\u6c49\u5b50\uff0c\u66f4\u662f\u60e8 \u4e0d\u582a\u5947\uff0c\u5012\u5728\u5730\u4e0a\u53d1\u6296\uff0c\u5c31\u50cf\u60a3\u4e86\u4e25\u91cd\u7684\u53d1\u51b7\u75c5\u4e00\u6837\u3002 \u3000\u3000\u6ca1\u6709\u8dcc\u5012\u7684\u90a3\u51e0\u4e2a\u6c49\u5b50\u4e5f\u662f\u51b7\u5f97\u7259\u5173\u6253\u6218\uff0c\u6296\u6296\u7d22\u7d22\u5730\u8dd1\u56de\u53bb\uff0c\u65ad\u65ad\u7eed\u7eed\u5730\u53eb\u558a\uff1a \u201c\u54ce\u3001\u54ce\u3001\u54ce\u5440\uff01\u8fd9\u3001\u8fd9\u3001\u8fd9\u5c0f\u5b50\u4f1a\u3001\u4f1a\u5996\u6cd5\uff01\u201d\u9648\u5149\u7167\u8d76\u8dd1\u4e86\u628a\u98ce\u7684\u8fd9\u73ed\u4eba\u4e4b\u540e\uff0c\u4e00\u4e0d\u505a \u4e8c\u4e0d\u4f11\uff0c\u4ed7\u7740\u51b0\u5f39\u7389\u5251\uff0c\u7d22\u6027\u4fbf\u76f4\u95ef\u79d8\u9b54\u5d16\uff0c\u51c6\u5907\u7ed9\u674e\u5357\u661f\u63a0\u9635\u3002 \u3000\u3000\u674e\u5357\u661f\u672c\u6765\u60f3\u8981\u8dd1\u51fa\u53bb\u4e0e\u4ed6\u4f1a\u5408\u7684\uff0c\u6b64\u65f6\u89c1\u9648\u5149\u7167\u7684\u51b0\u9b44\u795e\u5f39\u5927\u663e\u795e\u901a\uff0c\u628a\u56f4\u653b\u4ed6\u7684\u90a3 \u4e9b\u4eba\u6253\u5f97\u4e03\u96f6\u516b\u843d\uff0c\u5df2\u662f\u95ef\u51fa\u91cd\u56f4\uff0c\u5148\u8dd1\u6765\u4e86\uff0c\u4e0d\u7531\u5f97\u53c8\u662f\u60ca\u5947\uff0c\u53c8\u662f\u6b22\u559c\uff0c\u4e50\u5f97\u54c8\u54c8\u5927 \u7b11\u3002 \u3000\u3000\u90a3\u8001\u8005\u9762\u8272\u4e00\u6c89\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u7c73\u7c92\u4e4b\u73e0\uff0c\u4e5f\u653e\u5149\u6bd5\u3002\u5f92\u513f\uff0c\u4f60\u66ff\u4e3a\u5e08\u7684\u628a\u8fd9\u5c0f\u5b50\u62ff \u4e0b\uff01\u201d\u4e00\u4e2a\u9762\u5e26\u75c5\u5bb9\uff0c\u8eab\u6750\u9ad8\u7626\u7684\u6c49\u5b50\u5e94\u58f0\u800c\u51fa\uff0c\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u9075\u547d\uff01\u201d\u58f0\u5230\u4eba\u5230\uff0c\u767b\u65f6\u62a2 \u5230\u4e86\u9648\u5149\u7167\u7684\u9762\u524d\u3002 \u3000\u3000\u9648\u5149\u7167\u89c1\u4ed6\u6765\u52bf\u8fc5\u731b\uff0c\u6599\u60f3\u662f\u4e2a\u52b2\u654c\uff0c\u6253\u7b97\u5148\u53d1\u5236\u4eba\uff0c\u4e8e\u662f\u4e0d\u5f85\u5bf9\u65b9\u51fa\u624b\uff0c\u5148\u53d1\u51fa\u4e86\u4e09 \u9897\u51b0\u9b44\u795e\u5f39\u3002\u201d \u3000\u3000\u8fd9\u4e2a\u9762\u5e26\u75c5\u5bb9\u7684\u6c49\u5b50\u6728\u7136\u6beb\u65e0\u8868\u60c5\uff0c\u90a3\u4e09\u9897\u4eae\u6676\u6676\u7684\u51b0\u9b44\u795e\u5f39\u6253\u5230\u4ed6\u7684\u9762\u524d\uff0c\u53ea\u89c1\u4ed6\u628a \u624b\u4e00\u62db\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u9053\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u539f\u6765\u4e0d\u8fc7\u662f\u51b0\u5ddd\u5929\u5973\u7684\u4e2b\u5934\u5c0f\u5b50\u6240\u7528\u7684\u51b0\u5f39\u3002\u54fc\uff0c \u54fc\uff0c\u4ec0\u4e48\u51b0\u9b44\u795e\u5f39\uff0c\u5c82\u80fd\u5948\u6211\u4f55\u6211\uff1f\u201d\u51b0\u5f39\u843d\u5165\u4ed6\u7684\u624b\u4e2d\uff0c\u53ea\u89c1\u4ed6\u628a\u624b\u638c\u4e00\u644a\u5f00\uff0c\u90a3\u4e09\u9897\u51b0 \u5f39\u5df2\u662f\u5168\u90e8\u6eb6\u5316\uff0c\u6ef4\u4e0b\u4e86\u4e00\u6ee9\u96ea\u6c34\uff01 \u3000\u3000\u51b0\u9b44\u795e\u5f39\u78b0\u5230\u5185\u529f\u9ad8\u660e\u4e4b\u58eb\uff0c\u4f24\u5bb3\u4e0d\u4e86\u5bf9\u65b9\uff0c\u90a3\u4e5f\u4e0d\u5947\u3002\u5947\u7684\u662f\u8fd9\u4e2a\u6c49\u5b50\u7684\u8eab\u4efd\u4e0d\u8fc7\u662f \u90a3\u8001\u8005\u7684\u5f92\u5f1f\uff0c\u5374\u7adf\u7136\u6562\u786c\u63a5\u51b0\u5f39\uff0c\u5927\u51fa\u9648\u5149\u7167\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u9648\u5149\u7167\u65b9\u81ea\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8fd9\u6c49\u5b50\u5df2\u662f\u4e00\u638c\u5411\u4ed6\u6253\u8fc7\u6765\u4e86\u3002 \u3000\u3000\u9648\u5149\u7167\u4e00\u4e2a\u6ed1\u6b65\u659c\u8eab\uff0c\u53cd\u624b\u5c31\u6700\u4e00\u5251\uff0c\u654c\u4eba\u6b63\u5728\u6251\u6765\uff0c\u8fd9\u4e00\u62db\u662f\u4ee5\u653b\u4e3a\u5b88\u7684\u6253\u6cd5\uff0c\u5251\u52bf \u8f7b\u7075\u7fd4\u52a8\uff0c\u662f\u201c\u51b0\u5ddd\u5251\u6cd5\u201d\u4e2d\u7684\u4e00\u62db\u6781\u7cbe\u5999\u7684\u62db\u6570\u3002 \u3000\u3000\u9648\u5149\u7167\u4ee5\u4e3a\u5bf9\u65b9\u975e\u5f97\u7ed9\u4ed6\u903c\u9000\u4e0d\u53ef\uff0c\u5426\u5219\u5b9a\u8981\u4e2d\u5251\u65e0\u7591\u3002\u54ea\u77e5\u5bf9\u65b9\u7adf\u7136\u4e0d\u9053\u4e0d\u95ea\uff0c\u53ea\u542c \u5f97\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u6c49\u5b50\u5316\u638c\u4e3a\u6307\uff0c\u5c0f\u6307\u53ea\u662f\u8f7b\u8f7b\u4e00\u5f39\uff0c\u5c31\u628a\u9648\u5149\u7167\u7684\u8fd9\u628a\u5bd2\u7389\u5251\u5f39\u5f00\u4e86\u3002 \u3000\u3000\u5bd2\u7389\u5251\u7684\u5389\u5bb3\u4e0d\u5728\u4e8e\u950b\u5229\uff0c\u800c\u5728\u4e8e\u5b83\u672c\u8eab\u6240\u5177\u7684\u9634\u5bd2\u4e4b\u6c14\u3002\u9648\u5149\u7167\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u521a \u63a5\u4e86\u6211\u7684\u51b0\u9b44\u795e\u5f39\uff0c\u5982\u4eca\u53c8\u78b0\u7740\u4e86\u6211\u7684\u5bd2\u7389\u5251\uff0c\u8fd9\u4e00\u4e0b\u603b\u6709\u4ed6\u96be\u53d7\u7684\u4e86\uff0c\u9664\u975e\u4ed6\u4e0d\u662f\u8840\u8089\u4e4b \u8eaf\u3002\u201d\u4e0d\u6599\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c9\u4e00\u7f15\u5947\u5bd2\u4e4b\u6c14\uff0c\u4ece\u5251\u67c4\u4f20\u5165\u4ed6\u7684\u638c\u5fc3\u3002\u90a3\u9762\u5e26\u75c5\u5bb9\u7684\u6c49\u5b50\u4ecd\u7136\u662f \u90a3\u6837\u6728\u7136\u7684\u795e\u8272\uff0c\u5e76\u6ca1\u53d1\u6296\uff0c\u5012\u662f\u9648\u5149\u7167\u89c9\u5f97\u51b7\u5f97\u96be\u53d7\uff0c\u4e0d\u7531\u5f97\u673a\u4f36\u4f36\u5730\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\u3002 \u3000\u3000\u9648\u5149\u7167\u672c\u6765\u662f\u7ec3\u8fc7\u201c\u5c11\u9633\u5185\u529f\u5fc3\u6cd5\u201d\u7684\uff0c\u8fd9\u662f\u51b0\u5ddd\u5929\u5973\u4f20\u7ed9\u4ed6\u6bcd\u4eb2\u7684\u4e00\u79cd\u62a4\u4f53\u795e\u529f\uff0c\u7ec3 \u8fc7\u8fd9\u79cd\u62a4\u4f53\u795e\u529f\uff0c\u624d\u80fd\u591f\u4f7f\u7528\u7389\u5251\u51b0\u5f39\uff0c\u4e0d\u81f3\u88ab\u5bd2\u6c14\u6240\u4fb5\u7684\u3002\u5982\u4eca\u4ed6\u7684\u5bd2\u7389\u5251\u7ed9\u90a3\u6c49\u5b50\u4e00\u6307 \u5f39\u5f00\uff0c\u5251\u67c4\u7a81\u7136\u53d8\u5f97\u51b7\u903e\u575a\u51b0\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u611f\u5230\u96be\u4ee5\u7981\u53d7\uff0c\u8fd9\u771f\u662f\u4ece\u6240\u672a\u6709\u4e4b\u4e8b\uff01 \u3000\u3000\u90a3\u6c49\u5b50\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u5bd2\u7389\u5251\u4e5f\u4e0d\u8fc7\u5982\u6b64\u800c\u5df2\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u4f0e\u4fe9\uff1f\u563f\uff0c\u563f\uff0c\u8fd9\u67c4\u5251\u4f60\u4e0d\u914d \u4f7f\u5b83\uff0c\u4e0d\u5982\u7ed9\u4e86\u6211\u5427\u3002\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u638c\u5e95\u6beb\u4e0d\u653e\u677e\uff0c\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u662f\u63a5\u8fde\u4f7f\u4e86\u4e09\u62db\u201c\u7a7a\u624b\u5165 \u767d\u5203\u201d\u7684\u529f\u592b\uff0c\u7adf\u7136\u5c31\u60f3\u786c\u62a2\u9648\u5149\u7167\u7684\u8fd9\u628a\u7389\u5251\u3002 \u3000\u3000\u9648\u5149\u7167\u89c1\u5bd2\u7389\u5251\u4f24\u4e0d\u4e86\u5bf9\u65b9\uff0c\u5fc3\u4e2d\u5927\u9a87\uff0c\u90a3\u4eba\u6765\u62a2\u4ed6\u7684\u5b9d\u5251\uff0c\u4ed6\u53d7\u8fc7\u4e00\u6b21\u6559\u8bad\uff0c\u4e0d\u6562\u8ba9 \u5bf9\u65b9\u6309\u89e6\uff0c\u53ea\u80fd\u51ed\u4ed7\u8f7b\u529f\uff0c\u4e1c\u8eb2\u897f\u95ea\uff0c\u624b\u4e2d\u7684\u5b9d\u5251\u7b49\u4e8e\u662f\u65e0\u7528\u7684\u5e9f\u7269\uff0c\u4e00\u65f6\u95f4\u7adf\u7136\u4e0d\u77e5\u8be5\u5982 \u4f55\u5e94\u4ed8\u624d\u597d\u3002 \u3000\u3000\u9648\u5149\u7167\u7ed9\u4ed6\u8d8a\u903c\u8d8a\u7d27\uff0c\u6fc0\u6218\u4e2d\u90a3\u6c49\u5b50\u4e00\u62db\u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u638c\u6307\u517c\u65bd\uff0c\u9648\u5149\u7167\u9000\u65e0\u53ef\u9000\uff0c \u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u4e00\u54ac\u7259\u6839\uff0c\u5251\u4e2d\u5939\u638c\uff0c\u4e0e\u5bf9\u65b9\u786c\u5bf9\u4e86\u4e00\u638c\u3002 \u3000\u3000\u53cc\u638c\u76f8\u4ea4\uff0c\u201c\u7bf7\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u6c49\u5b50\u9000\u4e86\u4e09\u6b65\uff0c\u9648\u5149\u7167\u4e5f\u662f\u63a5\u8fde\u6643\u4e86\u4e24\u6643\u3002\u8bba\u638c\u529b\u53cc\u65b9\u5012 \u662f\u76f8\u5dee\u4e0d\u4e86\u591a\u5c11\u3002\u53ef\u662f\u9648\u5149\u7167\u5df2\u662f\u5927\u611f\u610f\u5916\uff0c\u4e0d\u7531\u5f97\u60ca\u559c\u4ea4\u96c6\uff01 \u3000\u3000\u539f\u6765\u9648\u5149\u7167\u4ee5\u4e3a\u5bf9\u65b9\u65e2\u7136\u4e0d\u754f\u4ed6\u7684\u7389\u5251\u51b0\u5f39\uff0c\u5185\u529f\u5b9a\u7136\u662f\u975e\u5e38\u6df1\u6e5b\u7684\u4e86\u3002\u5982\u4eca\u4e00\u8bd5\u7684\u7ed3 \u679c\uff0c\u8fd9\u624d\u77e5\u9053\u5bf9\u65b9\u7684\u638c\u529b\u867d\u4e5f\u4e0d\u5f31\uff0c\u4f46\u4ea6\u4e0d\u8fc7\u5982\u6b64\u800c\u5df2\uff0c\u5e76\u4e0d\u89c1\u5f97\u5c31\u6bd4\u4ed6\u9ad8\u660e\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u5bf9\u65b9\u7684\u638c\u529b\u867d\u7136\u672a\u80fd\u80dc\u4ed6\uff0c\u4f46\u9648\u5149\u7167\u63a5\u4e86\u8fd9\u638c\u4e4b\u540e\uff0c\u8eab\u4e0a\u7684\u5bd2\u610f\u53c8\u589e\u4e86\u51e0\u5206\uff0c\u672c \u6765\u5df2\u7ecf\u662f\u51b7\u5f97\u76f8\u5f53\u96be\u53d7\u7684\u4e86\uff0c\u5982\u4eca\u66f4\u597d\u50cf\u662f\u9677\u8eab\u5728\u51b0\u7a9f\u4e00\u822c\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u89c9\u96be\u53d7\u4e86\u5427\uff1f\u563f\uff0c\u563f\uff0c\u8fd9\u5c31\u53eb\u505a\u4ee5\u5176\u4eba\u4e4b\u9053\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\uff0c\u4f60 \u7b49\u7740\u77a7\u5427\uff0c\u8fd8\u6709\u5389\u5bb3\u7684\u5728\u540e\u5934\u5462\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u51b0\u5f39\u7389\u5251\u6d88\u9634\u715e\uff0c\u4fa0\u58eb\u9b54\u5934\u5404\u901e\u80fd\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002"}),showOnHomepage:!0},{name:"\u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u79d8\u9b54\u5d16\u4e0b\u9664\u5996\u5b7d\u3000\u81ea\u7389\u73af\u4e2d\u8bc6\u6545\u4eba",content:(0,t.jsx)(t.Fragment,{children:"\u8fd9\u6c49\u5b50\u53cc\u638c\u4e00\u6413\uff0c\u7709\u5fc3\u73b0\u51fa\u9ed1\u6c14\uff0c\u638c\u529b\u4e00\u53d1\uff0c\u51b7\u98ce\u98d5\u98d5\uff0c\u9648\u5149\u7167\u624b\u4e2d\u62ff\u7740\u5bd2\u7389\u5251\uff0c\u66f4\u5176 \u89c9\u5f97\u51b7\u5f97\u96be\u53d7\u4e86\u3002 \u3000\u3000\u539f\u6765\u8fd9\u6c49\u5b50\u7ec3\u7684\u662f\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\u662f\u4e00\u79cd\u7eaf\u9634\u7684\u90aa\u6d3e\u5947\u529f\uff0c\u9648\u5149\u7167\u4f7f\u7528\u51b0\u5f39\u7389\u5251\uff0c\u53cd\u800c \u7ed9\u5bf9\u65b9\u589e\u52a0\u4e86\u5a01\u529b\u3002 \u3000\u3000\u674e\u6d77\u661f\u89c1\u9648\u5149\u7167\u62b5\u654c\u4e0d\u4f4f\uff0c\u5927\u6012\u8bf4\u9053\uff1a\u201c\u6211\u6765\u8d74\u4f60\u4eec\u7684\u7ea6\u4f1a\uff0c\u4f60\u4eec\u600e\u53ef\u96be\u4e3a\u6211\u7684\u670b\u53cb\uff1f \u6b64\u4e8b\u4e0e\u4ed6\u65e0\u5173\uff0c\u4f60\u4eec\u51b2\u7740\u6211\u6765\u5c31\u662f\uff01\u201d\u6b63\u8981\u98de\u8eab\u8fc7\u53bb\uff0c\u90a3\u59d3\u9633\u7684\u8001\u8005\u54c8\u54c8\u4e00\u7b11\uff0c\u8eab\u5f62\u4e00\u6643\uff0c \u5df2\u662f\u62e6\u5728\u4e86\u674e\u5357\u661f\u7684\u53bb\u8def\u8bf4\u9053\uff1a\u201c\u5389\u516c\u5b50\uff0c\u4f60\u522b\u5fd9\uff0c\u5148\u63a5\u4e86\u627e\u7684\u4e00\u767e\u62db\u518d\u8bf4\uff01\u53ea\u8981\u4f60\u63a5\u5f97 \u4e0b\uff0c\u4f60\u7684\u670b\u53cb\u6211\u4e5f\u4e00\u540c\u606d\u9001\u4e0b\u5c71\uff01\u201d \u3000\u3000\u8fd9\u8001\u8005\u8d64\u624b\u7a7a\u62f3\u5411\u674e\u5357\u661f\u6311\u6218\uff0c\u674e\u5357\u661f\u81ea\u662f\u4e0d\u4fbf\u4f7f\u7528\u5b9d\u5251\uff0c\u5f53\u4e0b\u53cc\u638c\u4e00\u5206\uff0c\u5de6\u638c\u5e73\u63a8\uff0c \u53f3\u638c\u659c\u62b9\uff0c\u8fd9\u4e00\u62db\u517c\u6709\u70b9\u7a74\u64d2\u62ff\u7684\u624b\u6cd5\uff0c\u715e\u662f\u5389\u5bb3\u3002\u8fd9\u8001\u8005\u82e5\u8981\u907f\u514d\u7ed9\u4ed6\u70b9\u4e2d\u7a74\u9053\uff0c\u5c31\u52bf\u5fc5 \u8981\u7ed9\u4ed6\u638c\u529b\u63a8\u5f00\u3002 \u3000\u3000\u8001\u8005\u8d5e\u9053\uff1a\u201c\u597d\u4e00\u62db\u659c\u9633\u4e00\u62b9\uff0c\u4f60\u8fd9\u4e00\u62db\u5df2\u8db3\u53ef\u4ee5\u53ca\u5f97\u4e0a\u4ee4\u5c0a\u5f53\u5e74\uff01\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u5355\u638c \u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u7f13\u7f13\u63a8\u51fa\u3002\u674e\u5357\u661f\u5fc3\u91cc\u6697\u7b11\uff1a\u201c\u4f60\u8fd9\u53ae\u501a\u8001\u5356\u8001\uff0c\u53ef\u60dc\u4f60\u867d\u7136\u8bc6\u5f97\u6211\u7684\u62db \u6570\uff0c\u5374\u4e0d\u61c2\u5982\u4f55\u7834\u89e3\u3002\u201d \u3000\u3000\u4e0d\u6599\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u89c9\u51b7\u98ce\u5982\u7bad\uff0c\u597d\u50cf\u5c04\u5165\u4e86\u9aa8\u9ad3\uff0c\u8fde\u8840\u6db2\u90fd\u4f3c\u4e4e\u8981\u51dd\u7ed3\u4e86\u3002\u674e\u5357\u661f\u8fd9\u4e00 \u62db\u672c\u6765\u662f\u62db\u91cc\u85cf\u62db\uff0c\u5f0f\u4e2d\u5957\u5f0f\uff0c\u5bf9\u65b9\u4e0d\u61c2\u7834\u89e3\uff0c\u4ed6\u5c31\u7acb\u5373\u53ef\u4ee5\u7528\u5927\u64d2\u62ff\u624b\u6293\u5bf9\u65b9\u7684\u201c\u66f2\u6c60 \u7a74\u201d\u7684\uff0c\u4f46\u56e0\u7a81\u7136\u611f\u5230\u5947\u5bd2\u5f7b\u9aa8\uff0c\u624b\u8155\u6296\u98a4\uff0c\u8fd9\u4e00\u6293\u5c31\u5931\u4e86\u51c6\u5934\uff0c\u53cd\u800c\u9669\u4e9b\u7ed9\u90a3\u8001\u8005\u6293\u4f4f\u3002 \u3000\u3000\u674e\u5357\u661f\u4e00\u6293\u4e0d\u4e2d\uff0c\u5df2\u77e5\u4e0d\u5999\uff0c\u8fde\u5fd9\u4f7f\u51fa\u201c\u5929\u7f57\u6b65\u6cd5\u201d\u4e2d\u79fb\u5f62\u6362\u4f4d\u7684\u529f\u592b\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d \u7684\u4e00\u58f0\uff0c\u867d\u7136\u95ea\u4e86\u8fc7\u53bb\uff0c\u4f46\u8863\u8896\u5df2\u88ab\u90a3\u8001\u8005\u6495\u4e0b\u4e86\u4e00\u5e45\u3002 \u3000\u3000\u90a3\u8001\u8005\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u77e5\u9053\u5389\u5bb3\u4e86\u5427\uff1f\u6211\u770b\u4f60\u5982\u4f55\u63a5\u5f97\u4e86\u6211\u7684\u4e00\u767e\u62db\uff1f\u201d\u8bf4\u8bdd\u4e4b \u95f4\uff0c\u8fde\u53d1\u4e09\u638c\uff0c\u767b\u65f6\u72c2\u98da\u5377\u5730\uff0c\u51b7\u6c14\u5f25\u6f2b\uff0c\u628a\u674e\u5357\u661f\u903c\u5f97\u6b65\u6b65\u540e\u9000\u3002\u539f\u6765\u8fd9\u4e2a\u8001\u8005\u7684\u201c\u4fee\u7f57 \u9634\u715e\u529f\u201d\u5df2\u662f\u7ec3\u5230\u4e86\u7b2c\u516b\u91cd\uff0c\u6bd4\u4ed6\u7684\u90a3\u4e2a\u5f92\u5f1f\u66f4\u662f\u5389\u5bb3\u5f97\u591a\u3002 \u3000\u3000\u674e\u5357\u661f\u6c89\u4f4f\u4e86\u6c14\uff0c\u9ed8\u8fd0\u7384\u529f\uff0c\u4f7f\u51fa\u4e86\u4e00\u5957\u865a\u865a\u5b9e\u5b9e\uff0c\u53d8\u5316\u83ab\u6d4b\u7684\u201c\u843d\u82f1\u638c\u6cd5\u201d\uff0c\u4e0e\u90a3\u8001 \u8005\u6e38\u6597\u3002\u867d\u7136\u51b7\u5f97\u7259\u5173\u6253\u6218\uff0c\u53cc\u638c\u4e5f\u6253\u4e0d\u5230\u90a3\u8001\u8005\u8eab\u4e0a\uff0c\u53ef\u662f\u90a3\u8001\u8005\u60f3\u8981\u628a\u4ed6\u6293\u4f4f\uff0c\u4e00\u65f6\u4e4b \u95f4\uff0c\u5374\u4e5f\u4e0d\u80fd\u3002 \u3000\u3000\u8fd9\u8001\u8005\u5728\u521d\u53d1\u7b2c\u4e00\u638c\u7684\u65f6\u5019\uff0c\u89c1\u674e\u5357\u661f\u5df2\u6709\u7981\u53d7\u4e0d\u8d77\u7684\u8ff9\u8c61\uff0c\u4ee5\u4e3a\u7528\u4e0d\u4e86\u51e0\u62db\uff0c\u5c31\u53ef\u4ee5 \u4f7f\u5f97\u4ed6\u675f\u624b\u53d7\u64d2\u7684\uff0c\u5982\u4eca\u89c1\u674e\u5357\u661f\u5c45\u7136\u62b5\u6321\u5f97\u4f4f\uff0c\u4e0d\u7981\u5927\u611f\u610f\u5916\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5947\u602a\uff0c\u4ed6\u7684 \u5185\u529f\u600e\u7684\u597d\u50cf\u6bd4\u4ed6\u7684\u7236\u6bcd\u8fd8\u5f3a\uff1f\u5e78\u4e8f\u6211\u662f\u9650\u4ed6\u767e\u62db\uff0c\u767e\u62db\u4e4b\u5185\u603b\u53ef\u4ee5\u6709\u53d6\u80dc\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u8fd9\u8001\u8005\u6709\u6240\u4e0d\u77e5\uff0c\u539f\u6765\u674e\u5357\u661f\u7684\u5185\u529f\u66fe\u5f97\u9ad8\u4eba\u6307\u70b9\uff0c\u5e76\u975e\u5b8c\u5168\u5f97\u81ea\u5bb6\u4f20\u3002\u4ee5\u4ed6\u73b0\u5728\u7684\u529f \u529b\u800c\u8bba\uff0c\u4e5f\u672a\u5fc5\u5c31\u80dc\u5f97\u8fc7\u4ed6\u7684\u7236\u6bcd\uff0c\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u4fee\u4e60\u7684\u662f\u6b63\u5b97\u7684\u5185\u529f\uff0c\u6545\u6b64\u8f83\u7eaf\uff0c\u7528\u6765\u5bf9\u4ed8 \u8fd9\u8001\u8005\u7684\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\uff0c\u4e5f\u5c31\u663e\u5f97\u6bd4\u4ed6\u7684\u7236\u6bcd\u66f4\u5f3a\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u674e\u5357\u661f\u4e5f\u53ea\u662f\u52c9\u5f3a\u80fd\u591f\u62b5\u6321\u800c\u5df2\uff0c\u65f6\u95f4\u4e00\u4e45\uff0c\u5bd2\u610f\u52a0\u6d53\uff0c\u62f3\u811a\u5c31\u6e10\u6e10\u65bd\u5c55\u4e0d\u5f00 \u4e86\u3002\u4ed6\u5728\u767e\u5fd9\u4e2d\u62bd\u773c\u5077\u671b\uff0c\u53ea\u89c1\u9648\u5149\u7167\u7684\u60c5\u5f62\u6bd4\u4ed6\u66f4\u7cdf\uff0c\u5df2\u662f\u7ed9\u90a3\u9762\u5e26\u75c5\u5bb9\u7684\u6c49\u5b50\u6253\u5f97\u8fde\u62db \u67b6\u90fd\u4e3a\u96be\u4e86\u3002\u674e\u5357\u661f\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u7cdf\u7cd5\uff01\u6211\u6253\u8d25\u4e86\u8fd8\u4e0d\u6253\u7d27\uff0c\u8fd9\u56de\u66f4\u662f\u8fde \u9648\u5927\u54e5\u90fd\u8fde\u7d2f\u4e86\uff01\u201d \u3000\u3000\u8fd9\u8001\u8005\u4e5f\u6697\u6697\u9053\u4e86\u4e00\u58f0\u201c\u60ed\u6127\u201d\u539f\u6765\u6b64\u65f6\u5df2\u5c06\u8fd1\u767e\u62db\uff0c\u53ef\u662f\u674e\u5357\u661f\u5374\u5e76\u4e0d\u77e5\u9053\u3002\u8001\u8005\u52a0 \u91cd\u638c\u529b\uff0c\u5fc3\u60f3\uff1a\u201c\u5018\u82e5\u8fc7\u4e86\u767e\u62db\uff0c\u8fd9\u5c0f\u5b50\u4e0d\u51fa\u58f0\u7684\u8bdd\uff0c\u6211\u4e5f\u8bc8\u4f5c\u4e0d\u77e5\u597d\u4e86\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u4e00\u9762\u5e94\u4ed8\u5f3a\u654c\uff0c\u4e00\u9762\u4e3a\u9648\u5149\u7167\u62c5\u5fe7\uff0c\u4e0d\u89c9\u62db\u6570\u6563\u4e71\uff0c\u4e00\u4e2a\u758f\u795e\uff0c\u7740\u4e86\u90a3\u8001\u8005\u4e00 \u638c\uff0c\u674e\u5357\u661f\u8dc4\u8dc4\u8e09\u8e09\u7684\u63a5\u8fde\u9000\u51fa\u4e86\u4e03\u516b\u6b65\uff0c\u773c\u770b\u5c31\u8981\u8dcc\u5012\uff0c\u8001\u8005\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u8dd1\u4e0d\u4e86 \u5566\uff0c\u8fd8\u662f\u8ddf\u6211\u8d70\u5427\uff01\u201d \u3000\u3000\u8fd9\u8001\u8005\u6b63\u8981\u4e00\u6293\u6293\u4e0b\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u8001\u5339\u592b\uff0c\u4f60\u8bf4\u8bdd\u7b97\u4e0d\u7b97\u8bdd\uff1f\u201d\u4eba\u8fd8\u672a\u89c1\uff0c \u8bdd\u58f0\u5c31\u4f3c\u5c31\u5728\u4ed6\u7684\u8033\u8fb9\u3002 \u3000\u3000\u8fd9\u8001\u8005\u5927\u5403\u4e00\u60ca\uff0c\u6050\u9632\u6709\u4eba\u5077\u88ad\uff0c\u90a3\u4e00\u6293\u4e0d\u6562\u6293\u4e0b\uff0c\u56de\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u4e71\u77f3\u5806\u4e2d\u7a81\u7136\u51fa\u73b0 \u4e86\u4e00\u4e2a\u4eba\u5f71\uff0c\u6708\u5149\u4e4b\u4e0b\uff0c\u770b\u5f97\u5206\u660e\uff0c\u4e0d\u8fc7\u662f\u4e2a\u4e8c\u5341\u5c81\u5de6\u53f3\u7684\u5c11\u5e74\u3002 \u3000\u3000\u8fd9\u8001\u8005\u677e\u4e86\u53e3\u6c14\uff0c\u60f3\u9053\uff1a\u201c\u6211\u53ea\u9053\u662f\u4ec0\u4e48\u9ad8\u4eba\u6765\u4e86\uff0c\u5374\u539f\u6765\u4e5f\u4e0d\u8fc7\u662f\u4e2a\u4e73\u81ed\u672a\u5e72\u7684\u5c0f \u5b50\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u81ed\u5c0f\u5b50\u5374\u662f\u6709\u70b9\u90aa\u95e8\uff0c\u4ed6\u5728\u90a3\u513f\u8bf4\u8bdd\uff0c\u58f0\u97f3\u5374\u4f3c\u5c31\u5728\u6211\u7684\u8033\u8fb9\uff0c\u8fd9\u662f\u4ec0\u4e48\u529f\u592b \u5462\uff1f\u201d \u3000\u3000\u6b64\u65f6\u674e\u5357\u661f\u4ea6\u5df2\u770b\u6e05\u695a\u4e86\uff0c\u4e0d\u7981\u559c\u51fa\u671b\u5916\uff0c\u5927\u53eb\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u4e5f\u6765\u4e86\uff1f\u4f60\u600e\u4e48\u77e5\u9053\u6211 \u5728\u8fd9\u513f\uff1f\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u91d1\u9010\u6d41\u4e00\u4e2a\u201c\u71d5\u5b50\u7a7f\u5e18\u5f0f\u201d\uff0c\u8eab\u5f62\u5e73\u5730\u62d4\u8d77\uff0c\u5728\u7a7a\u4e2d\u4e00\u4e2a\u7ffb\u8eab\uff0c\u5df2\u662f \u65e0\u58f0\u65e0\u606f\u5730\u843d\u5728\u8001\u8005\u4e0e\u674e\u5357\u661f\u4e4b\u95f4\uff0c\u8eab\u6cd5\u7f8e\u5999\u4e4b\u6781\uff01\u90a3\u8001\u8005\u4e5f\u4e0d\u7981\u6697\u6697\u559d\u91c7\uff0c\u5fc3\u4e2d\u66f4\u611f\u60ca \u5947\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u674e\u5927\u54e5\uff0c\u4f60\u8ba9\u6211\u6765\u5bf9\u4ed8\u8fd9\u4e2a\u8bf4\u8bdd\u5982\u540c\u653e\u5c41\u7684\u8001\u5339\u592b\uff01\u201d \u3000\u3000\u8fd9\u8001\u8005\u6012\u9053\uff1a\u201c\u6211\u600e\u4e48\u662f\u8bf4\u8bdd\u4e0d\u7b97\u8bdd\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u8bf4\u8981\u5728\u4e00\u767e\u62db\u5185\u6253\u80dc\u7684\u662f\u4e0d \u662f\uff1f\u54c8\uff0c\u54c8\uff0c\u6211\u5728\u65c1\u7ed9\u4f60\u6570\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4f60\u5df2\u7ecf\u7528\u4e86\u4e00\u767e\u96f6\u4e8c\u62db\u5566\uff01\u201d \u3000\u3000\u8001\u8005\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u80e1\u8bf4\uff0c\u6211\u53ea\u7528\u4e86\u4e5d\u5341\u516b\u62db\u3002\u201d\u91d1\u9010\u6d41\u51b7\u7b11\u9053\uff1a\u201c\u4e8f\u4f60\u662f\u4fee\u7f57 \u9634\u715e\u529f\u7684\u4f20\u4eba\u3002\u5982\u6b64\u6df7\u8d56\uff0c\u7b80\u76f4\u662f\u8fde\u4f60\u6b7b\u9b3c\u7956\u5e08\u5b5f\u795e\u901a\u7684\u9762\u5b50\u90fd\u7ed9\u4f60\u4e22\u5c3d\u4e86\uff0c\u5b5f\u795e\u901a\u867d\u7136\u662f \u4e2a\u5927\u9b54\u5934\uff0c\u8bf4\u8bdd\u603b\u8fd8\u7b97\u8bdd\uff0c\u90a3\u50cf\u4f60\u8fd9\u6837\u4e0d\u8981\u8138\u76ae\uff01\u201d \u3000\u3000\u90a3\u59d3\u9633\u7684\u8001\u8005\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5374\u600e\u77e5\u6211\u7684\u6765\u5386\uff1f\u201d\u5f53\u4e0b\u8001\u7f9e \u6210\u6012\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u80c6\u6562\u5230\u8fd9\u91cc\u6765\u80e1\u8bf4\u516b\u9053\uff1f\u201d \u3000\u3000\u548c\u9648\u5149\u7167\u4ea4\u624b\u7684\u90a3\u4e2a\u53cc\u5b50\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u8fd9\u5c0f\u5b50\u5c31\u662f\u91d1\u9010\u6d41\u3002\u4ed6\u6b63\u662f\u6211\u7684\u4ec7\u4eba\uff0c\u5e08 \u7236\uff0c\u4f60\u53ef\u4e0d\u8981\u8f7b\u6613\u7684\u653e\u8fc7\u4ed6\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u9762\u5e26\u75c5\u5bb9\u7684\u6c49\u5b50\u5c31\u662f\u91d1\u9010\u6d41\u90a3\u65e5\u591c\u5c01\u5999\u5ae6\u623f\u4e2d\u641c\u51fa\u7684\u90a3\u4e2a\u4eba\uff0c\u4ed6\u540d\u53eb\u9f9a\u5e73\u91ce\uff0c \u90a3\u65e5\u88ab\u91d1\u9010\u6d41\u6253\u4e86\u4ed6\u4e00\u638c\uff0c\u8c03\u517b\u4e86\u4e09\u4e2a\u591a\u6708\uff0c\u6700\u8fd1\u65b9\u59cb\u590d\u539f\u3002\u8fd9\u8001\u8005\u540d\u53eb\u9633\u6d69\uff0c\u4ed6\u7684\u7236\u4eb2\u9633 \u8d64\u7b26\u662f\u5b5f\u795e\u901a\u7684\u5e08\u5f1f\uff0c\u5f97\u4e86\u201d\u4fee\u7f57\u9634\u715e\u529f\u201d\u7684\u771f\u4f20\u3002\u9633\u6d69\u53ea\u6709\u9f9a\u5e73\u91ce\u8fd9\u4e2a\u5f92\u5f1f\uff0c\u81ea\u5b5f\u795e\u901a\u3001 \u9633\u8d64\u7b26\u76f8\u7ee7\u53bb\u4e16\uff0c\u4e2d\u539f\u7684\u6b66\u6797\u4eba\u7269\u61c2\u4fee\u7f57\u9634\u715e\u529f\u7684\u5c31\u53ea\u6709\u4ed6\u4eec\u5e08\u5f92\u4e8c\u4eba\u4e86\u3002 \u3000\u3000\u9f9a\u5e73\u91ce\u4e00\u89c1\u91d1\u9010\u6d41\u9732\u9762\uff0c\u5c31\u60f3\u5411\u5e08\u7236\u63a7\u8bc9\u7684\uff0c\u6b64\u65f6\u65b9\u6709\u673a\u4f1a\u5f00\u53e3\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4eec\u5e08\u5f92\u4e0d\u80af\u653e\u8fc7\u6211\u662f\u4e0d\u662f\uff1f\u6211\u4e5f\u4e0d\u60f3\u653e\u8fc7\u4f60\u4eec\u5462\uff01\u201d\u8bdd\u58f0\u672a \u4e86\uff0c\u5df2\u662f\u500f\u5730\u6251\u53bb\uff0c\u9f9a\u5e73\u91ce\u66fe\u5403\u8fc7\u4ed6\u7684\u5927\u4e8f\uff0c\u7109\u6562\u62b5\u654c\uff0c\u53ea\u597d\u653e\u5f00\u4e86\u9648\u5149\u7167\uff0c\u4e00\u8eb2\u8eb2\u5230\u5e08\u7236 \u80cc\u540e\u3002\u91d1\u9010\u6d41\u6545\u610f\u628a\u4ed6\u626f\u4e0a\uff0c\u6b63\u662f\u8981\u628a\u4ed6\u5413\u8d70\uff0c\u597d\u6bd4\u9648\u5149\u7167\u8131\u56f0\u7684\u3002 \u3000\u3000\u9633\u6d69\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u6562\u6b3a\u8d1f\u6211\u7684\u5f92\u5f1f\uff0c\u4f60\u6765\u63a5\u6211\u4e00\u767e\u62db\u5427\uff01\u201d\u91d1\u9010\u6d41\u7b11 \u9053\uff1a\u201c\u6211\u6709\u4ec0\u4e48\u4e0d\u6562\uff0c\u6211\u53ea\u6015\u4f60\u63a5\u4e0d\u8d77\u6211\u7684\u767e\u62db\uff01\u201d \u3000\u3000\u9633\u6d69\u4e00\u638c\u6253\u51fa\uff0c\u91d1\u9010\u6d41\u62ab\u895f\u8fce\u98ce\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u6b63\u70ed\u5f97\u96be\u53d7\uff0c\u591a\u8c22\u4f60\u9001\u6211\u4e00\u9635\u51c9 \u98ce\uff01\u201d\u9633\u6d69\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff1a\u201c\u8fd9\u5c0f\u5b50\u7adf\u7136\u4e0d\u6015\u4fee\u7f57\u9634\u715e\u529f\uff01\u201d \u3000\u3000\u53cc\u638c\u76f8\u4ea4\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u53cc\u65b9\u90fd\u662f\u4e0d\u7981\u8eab\u5f62\u4e00\u6643\uff0c\u5411\u540e\u9000\u5f00\uff0c\u4e0d\u8fc7\u91d1\u9010\u6d41\u5374\u591a\u9000\u4e86\u4e00 \u6b65\u3002 \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u5e7c\u627f\u5bb6\u5b66\uff0c\u65e9\u5df2\u7ec3\u6210\u4e86\u201c\u6b63\u90aa\u5408\u201d\u7684\u5185\u529f\u3002\u4ed6\u7684\u7236\u4eb2\u91d1\u4e16\u9057\u5f53\u5e74\u66fe\u548c\u5b5f\u795e\u901a \u6570\u6b21\u8f83\u91cf\uff0c\u6df1\u6089\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\u7684\u5965\u79d8\uff0c\u4ed6\u81ea\u5df1\u867d\u7136\u4e0d\u80af\u7ec3\u6210\u8fd9\u79cd\u6b79\u6bd2\u7684\u90aa\u6d3e\u529f\u592b\uff0c\u5374\u628a\u62b5 \u5fa1\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\u7684\u5185\u529f\u5fc3\u6cd5\u4f20\u7ed9\u4e86\u513f\u5b50\uff0c\u6545\u6b64\u91d1\u9010\u6d41\u81ea\u662f\u50b2\u7136\u4e0d\u60e7\u3002\u4e0d\u8fc7\uff0c\u4ed6\u7684\u529f\u529b\u6bd5\u7adf \u8fd8\u662f\u7565\u900a\u9633\u6d69\u4e00\u7b79\uff0c\u9633\u6d69\u7684\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\u4f24\u4ed6\u4e0d\u5f97\uff0c\u5355\u51ed\u638c\u529b\u4ecd\u7136\u53ef\u4ee5\u903c\u5f97\u4ed6\u591a\u9000\u4e00\u6b65\u3002 \u3000\u3000\u9633\u6d69\u662f\u8fd9\u5e2e\u4eba\u7684\u9996\u9886\uff0c\u4ed6\u548c\u91d1\u9010\u6d41\u5355\u6253\u72ec\u6597\uff0c\u65c1\u4eba\u4e0d\u4fbf\u63d2\u624b\u3002\u4f46\u662f\u8fd9\u4e9b\u4eba\u5374\u6015\u674e\u5357\u661f\u9003 \u8d70\uff0c\u4e8e\u662f\u7eb7\u7eb7\u6d8c\u4e0a\uff0c\u5411\u674e\u5357\u661f\u56f4\u653b\u3002\u4e3a\u4e86\u8981\u628a\u674e\u5357\u661f\u6d3b\u64d2\uff0c\u8fd9\u4e9b\u4eba\u987e\u4e0d\u5f97\u4ed6\u4eec\u7684\u9996\u9886\u66fe\u7ecf\u8bf4 \u8fc7\u7684\u201c\u7edd\u4e0d\u4ee5\u591a\u4e3a\u80dc\u201d\u7684\u8bdd\u3002\u9633\u6d69\u4e5f\u662f\u4e0d\u4f5c\u4e00\u58f0\uff0c\u9ed8\u8bb8\u515a\u7fbd\u7684\u4f5c\u4e3a\u3002 \u3000\u3000\u91d1\u9010\u6d41\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u7528\u5251\uff01\u201d\u674e\u5357\u661f\u62d4\u5251\u4e00\u6325\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u8981\u547d\u7684\u53ea\u7ba1\u6765\uff01\u201d \u5251\u5149\u8fc7\u5904\uff0c\u53ea\u542c\u5f97\u4e00\u7247\u65ad\u91d1\u621b\u7389\u4e4b\u58f0\uff0c\u7834\u94dc\u70c2\u94c1\uff0c\u5806\u4e86\u4e00\u5730\u3002 \u3000\u3000\u53ea\u4e0d\u8fc7\u4e00\u4e2a\u7167\u9762\uff0c\u8fd9\u51e0\u4e2a\u4eba\u7684\u5175\u5668\u4fbf\u7ed9\u524a\u65ad\uff0c\u4e0d\u7531\u5f97\u5927\u5730\u4e00\u60ca\uff0c\u8fde\u5fd9\u540e\u9000\u3002 \u3000\u3000\u9f9a\u5e73\u91ce\u7684\u6b66\u529f\u8f83\u9ad8\uff0c\u907f\u8fc7\u4e86\u7384\u94c1\u5b9d\u5251\uff0c\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u7ed5\u5230\u4e86\u674e\u5357\u661f\u7684\u80cc\u540e\uff0c\u7acb\u5373 \u53d1\u638c\u5077\u88ad\u3002 \u3000\u3000\u9f9a\u5e73\u91ce\u7684\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\u624d\u4e0d\u8fc7\u7ec3\u5230\u7b2c\u4e94\u91cd\uff0c\u4ed6\u53ef\u4ee5\u514b\u5236\u9648\u5149\u7167\uff0c\u5374\u514b\u5236\u4e0d\u4e86\u674e\u5357\u661f\uff0c \u674e\u5357\u661f\u5b9d\u5251\u524d\u6325\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u53cd\u624b\u5c31\u662f\u4e00\u638c\u3002\u53cc\u638c\u76f8\u4ea4\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u9f9a\u5e73\u91ce\u4e5f\u7ed9\u4ed6\u51fb\u9000 \u4e86\u3002 \u3000\u3000\u674e\u5357\u661f\u6b63\u8981\u8fc7\u53bb\u4e0e\u91d1\u9010\u6d41\u4f1a\u5408\uff0c\u5ffd\u89c1\u4e00\u6761\u4eba\u5f71\uff0c\u5154\u8d77\u9e58\u843d\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5df2\u662f\u6765\u5230 \u4ed6\u7684\u9762\u524d\uff0c\u4e00\u58f0\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7528\u7684\u6562\u60c5\u5c31\u662f\u7384\u94c1\u5251\u5427\uff1f\u597d\uff0c\u53f2\u767d\u90fd\u4fdd\u5b83\u4e0d\u4f4f\uff0c\u6b63\u597d\u7ed9 \u6211\uff01\u201d \u3000\u3000\u8fd9\u4eba\u53cc\u624b\u7a7a\u7a7a\uff0c\u674e\u5357\u661f\u4e00\u65f6\u95f4\u8fd8\u5728\u8e0c\u8e87\u672a\u51b3\uff0c\u4e0d\u77e5\u8be5\u4e0d\u8be5\u7528\u5b9d\u5251\u4f24\u4ed6\u6027\u547d\uff1f\u4e0d\u6599\u90a3\u4eba\u638c \u529b\u4e00\u53d1\uff0c\u70ed\u98ce\u547c\u547c\uff0c\u70ed\u6d6a\u56db\u6ea2\uff0c\u638c\u529b\u4e4b\u731b\uff0c\u604d\u4f3c\u6392\u5c71\u5012\u6d77\u800c\u6765\u3002\u674e\u5357\u661f\u7528\u4e0a\u4e86\u201c\u5343\u65a4\u5760\u201d\u7684 \u91cd\u8eab\u6cd5\uff0c\u811a\u6b65\u7adf\u7136\u8fd8\u662f\u4e0d\u80fd\u7ad9\u7a33\u3002\u8fd9\u4eba\u53cc\u638c\u4e00\u53d1\uff0c\u8ddf\u7740\u7acb\u5373\u4fbf\u662f\u4e00\u62db\u201c\u7a7a\u624b\u5165\u767d\u5203\u201d\u7684\u529f \u592b\u3002 \u3000\u3000\u674e\u5357\u661f\u6325\u52a8\u7384\u94c1\u5b9d\u5251\uff0c\u9752\u5149\u66b4\u957f\uff0c\u5408\u6210\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u8fd9\u4eba\u5df2\u77e5\u662f\u7384\u94c1\u5b9d\u5251\uff0c\u4f46\u5c1a\u672a\u6599\u5230 \u7384\u94c1\u5b9d\u5251\u662f\u5982\u6b64\u5389\u5bb3\uff0c\u614c\u5fd9\u7f29\u624b\u3002\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u8fd9\u4eba\u7684\u8863\u8896\u88ab\u5b9d\u5251\u524a\u53bb\u4e86\u4e00\u5e45\uff0c\u53ef\u662f\u674e\u5357 \u661f\u8fd9\u4e00\u628a\u91cd\u8fbe\u767e\u65a4\u7684\u7384\u94c1\u5b9d\u5251\uff0c\u7ed9\u4ed6\u7684\u8863\u8896\u8f7b\u8f7b\u4e00\u62c2\uff0c\u5251\u5cf0\u767b\u65f6\u4e5f\u6b6a\u8fc7\u4e86\u4e00\u8fb9\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u53cc\u65b9\u90fd\u662f\u4e0d\u6562\u8f7b\u654c\uff0c\u8fd9\u4eba\u60f3\u9053\uff1a\u2018\u8fd9\u5c0f\u5b50\u5c45\u7136\u6321\u5f97\u4f4f\u6211\u7684\u96f7\u795e\u638c\uff0c\u624b\u4e2d\u53c8\u6709\u7384\u94c1 \u5b9d\u5251\uff0c\u5012\u662f\u4e0d\u53ef\u5c0f\u89d1\u4e86\u3002\u201d\u674e\u5357\u661f\u5fc3\u60f3\uff1a\u201c\u8fd9\u4eba\u7684\u529f\u529b\u4f3c\u4e4e\u8fd8\u5728\u90a3\u59d3\u9633\u7684\u8001\u8005\u4e4b\u4e0a\u3002\u7cdf\u7cd5\uff0c \u66f4\u662f\u6211\u7684\u7384\u94c1\u5b9d\u5251\u80dc\u4e0d\u4e86\u4ed6\uff0c\u4eca\u665a\u53ea\u6015\u662f\u96be\u4ee5\u8131\u9669\u4e86\u3002\u201d \u3000\u3000\u9f9a\u5e73\u91ce\u4e0e\u90a3\u51e0\u4e2a\u6298\u65ad\u4e86\u5175\u5668\u7684\u6c49\u5b50\uff0c\u4e0d\u6562\u518d\u53bb\u60f9\u674e\u5357\u661f\uff0c\u4e8e\u662f\u53c8\u518d\u79fb\u8f6c\u76ee\u6807\uff0c\u8dd1\u53bb\u56f4\u653b \u9648\u5149\u7167\u3002\u9648\u5149\u7167\u6492\u51fa\u4e00\u628a\u51b0\u9b44\u795e\u5f39\uff0c\u9f9a\u5e73\u91ce\u4e0d\u60e7\u51b0\u9b42\u795e\u5f39\uff0c\u4f46\u5176\u4ed6\u7684\u4eba\u5374\u662f\u7981\u53d7\u4e0d\u8d77\uff0c\u9664\u4e86 \u4e24\u4e2a\u5185\u529f\u8f83\u9ad8\u7684\u4e4b\u5916\uff0c\u90fd\u7ed9\u4ed6\u7684\u51b0\u5f39\u6253\u5f97\u6d51\u8eab\u53d1\u6296\uff0c\u53ea\u597d\u8fdc\u8fdc\u907f\u5f00\u3002 \u3000\u3000\u9648\u5149\u7167\u672c\u6765\u6253\u4e0d\u8fc7\u9f9a\u5e73\u91ce\uff0c\u5e78\u4e8f\u674e\u5357\u661f\u521a\u521a\u548c\u9f9a\u5e73\u91ce\u5bf9\u4e86\u4e00\u638c\uff0c\u6d88\u8017\u4e86\u4ed6\u7684\u771f\u529b\uff0c\u9f9a\u5e73 \u91ce\u7684\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\u6253\u4e86\u6298\u6263\uff0c\u9648\u5149\u7167\u624d\u53ef\u4ee5\u548c\u4ed6\u6253\u6210\u5e73\u624b\u3002\u4f46\u662f\u8fd8\u6709\u4e24\u4e2a\u5185\u529f\u8f83\u9ad8\u7684\u4eba\u6ca1 \u7ed9\u51b0\u5f39\u4f24\u7740\uff0c\u8fd9\u4e24\u4e2a\u4eba\u9000\u800c\u590d\u4e0a\uff0c\u9648\u5149\u7167\u817e\u4e0d\u51fa\u624b\u6765\u518d\u53d1\u795e\u5f39\uff0c\u518d\u5ea6\u9677\u4e8e\u82e6\u6218\u3002 \u3000\u3000\u674e\u5357\u661f\u529b\u6597\u90a3\u4e2a\u6c49\u5b50\uff0c\u53cc\u65b9\u5404\u6709\u5fcc\u60ee\uff0c\u53ef\u662f\u674e\u5357\u661f\u56e0\u4e3a\u5148\u6597\u4e86\u4e00\u573a\uff0c\u4e0d\u514d\u7a0d\u5904\u4e0b\u98ce\u3002 \u3000\u3000\u674e\u5357\u661f\u4e0d\u8bc6\u8fd9\u4e2a\u6c49\u5b50\uff0c\u91d1\u9010\u6d41\u5374\u662f\u77e5\u9053\uff0c\u4e0d\u7531\u5f97\u4e0d\u6697\u6697\u66ff\u674e\u5357\u661f\u62c5\u5fc3\u3002\u539f\u6765\u8fd9\u4e2a\u6c49\u5b50\u5c31 \u662f\u4ed6\u5728\u51e0\u4e2a\u6708\u524d\uff0c\u5728\u5f82\u5f95\u5c71\u4e0a\u66fe\u7ecf\u89c1\u8fc7\u7684\u90a3\u4e2a\u6b27\u9633\u575a\u3002\u90a3\u6b21\u6b27\u9633\u575a\u662f\u7ed9\u4e10\u5e2e\u5e2e\u4e3b\u4ef2\u957f\u7edf\u6253\u8d25 \u7684\uff0c\u91d1\u9010\u6d41\u548c\u4ed6\u5e76\u672a\u4ea4\u8fc7\u624b\u3002 \u3000\u3000\u90a3\u4e00\u6218\u6b27\u9633\u575a\u867d\u7136\u662f\u7ed9\u4ef2\u957f\u7edf\u6253\u8d25\uff0c\u4f46\u4e5f\u662f\u8fc7\u4e86\u767e\u62db\u4e4b\u540e\uff0c\u4ef2\u957f\u7edf\u65b9\u80fd\u53d6\u80dc\u7684\u3002\u8bd5\u60f3\u4e10 \u5e2e\u5e2e\u4e3b\u4ef2\u957f\u7edf\u7684\u529f\u592b\u662f\u4f55\u7b49\u6df1\u6e5b\uff0c\u7b49\u95f2\u4e4b\u8f88\uff0c\u5c82\u80fd\u582a\u4ed6\u4e00\u51fb\uff1f\u662f\u4ee5\u91d1\u9010\u6d41\u867d\u7136\u672a\u66fe\u548c\u6b27\u9633\u575a \u4ea4\u8fc7\u624b\uff0c\u4e5f\u77e5\u4ed6\u7684\u672c\u9886\u975e\u51e1\uff0c\u4ee5\u674e\u5357\u661f\u7684\u529f\u592b\uff0c\u51b3\u8ba1\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u3002 \u3000\u3000\u9633\u6d69\u8d8a\u653b\u8d8a\u731b\uff0c\u628a\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\u53d1\u6325\u5f97\u6dcb\u6f13\u5c3d\u81f4\uff0c\u91d1\u9010\u6d41\u63a5\u8fde\u7528\u4e86\u51e0\u79cd\u4e0d\u540c\u7684\u8eab\u6cd5\uff0c \u638c\u6cd5\uff0c\u987b\u5f25\u638c\u3001\u6392\u4e91\u624b\u3001\u5929\u7f57\u6b65\u3001\u60ca\u795e\u6307\u2026\u2026\u6bcf\u4e00\u79cd\u90fd\u662f\u4e16\u6240\u7f55\u89c1\u7684\u4e0a\u4e58\u6b66\u529f\uff0c\u5f53\u771f\u662f\u5947\u62db \u5999\u7740\uff0c\u5c42\u51fa\u4e0d\u7a77\u3002\u4f46\u867d\u7136\u5982\u6b64\uff0c\u6bd5\u7adf\u662f\u529f\u529b\u7a0d\u900a\u4e00\u7b79\uff0c\u5728\u9633\u6d69\u731b\u653b\u4e4b\u4e0b\uff0c\u5140\u662f\u672a\u80fd\u6446\u8131\u56f0 \u5883\u3002 \u3000\u3000\u9633\u6d69\u4e45\u653b\u4e0d\u4e0b\uff0c\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e0d\u77e5\u662f\u4ec0\u4e48\u6765\u5386\uff0c\u5982\u6b64\u4e86\u5f97\uff01\u6211\u82e5\u662f\u5728 \u767e\u62db\u4e4b\u5185\u4e0d\u80fd\u53d6\u80dc\uff0c\u4e45\u6218\u4e0b\u53bb\uff0c\u53ea\u6015\u8fd8\u4f1a\u8d25\u5728\u4ed6\u7684\u624b\u4e0a\u3002\u201d\u8981\u77e5\u4fee\u7f57\u9634\u715e\u529f\u6781\u4e3a\u6d88\u8017\u5143\u6c14\uff0c \u662f\u4ee5\u9633\u6d69\u5fc5\u987b\u901f\u6218\u901f\u51b3\uff0c\u65b9\u80fd\u514b\u654c\u5236\u80dc\u3002\u5426\u5219\u5f85\u5230\u4ed6\u518d\u8870\u7aed\u4e4b\u65f6\uff0c\u91d1\u9010\u6d41\u53d8\u5316\u83ab\u6d4b\u7684\u62db\u6570\uff0c \u5c31\u4e0d\u662f\u4ed6\u6240\u80fd\u9632\u5fa1\u7684\u4e86\u3002 \u3000\u3000\u9633\u6d69\u8fde\u65bd\u6740\u624b\uff0c\u628a\u91d1\u9010\u6d41\u903c\u9000\u51e0\u6b65\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u8fd8\u4e0d\u675f\u624b\u5c31\u64d2\uff1f\u6211\u8981\u53eb\u4f60\u6c42\u751f \u4e0d\u5f97\uff0c\u6c42\u6b7b\u4e0d\u80fd\uff01\u201d\u9633\u6d69\u5916\u8c8c\u51f6\u6a2a\uff0c\u5185\u5fc3\u865a\u602f\u5730\u53d1\u52a8\u72e0\u653b\uff0c\u5927\u65bd\u606b\u5413\uff0c\u5b9e\u662f\u60f3\u74e6\u89e3\u5bf9\u65b9\u7684\u6218 \u610f\uff0c\u5e0c\u671b\u5bf9\u65b9\u4e0d\u6218\u800c\u6e83\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8bc6\u7834\u654c\u4eba\u7684\u7528\u610f\uff0c\u594b\u52c7\u529b\u6218\uff0c\u50b2\u7136\u4e0d\u4ff1\u3002\u4e0d\u8fc7\uff0c\u4ed6\u81ea\u5df1\u867d\u7136\u4e0d\u6015\uff0c\u5374\u4e0d\u80fd\u4e0d\u4e3a\u674e \u5357\u661f\u4e0e\u9648\u5149\u7167\u62c5\u5fe7\u3002\u6fc0\u6218\u4e4b\u4e2d\uff0c\u91d1\u9010\u6d41\u8033\u542c\u56db\u65b9\uff0c\u773c\u89c2\u516b\u9762\uff0c\u773c\u5149\u4e00\u77a5\uff0c\u53ea\u89c1\u674e\u5357\u661f\u4e2d\u6307\u6234 \u7740\u4e00\u53ea\u6212\u6307\uff0c\u5149\u8292\u95ea\u95ea\uff0c\u5728\u4ed6\u638c\u5288\u6307\u6233\u4e4b\u9645\uff0c\u8ddd\u79bb\u6570\u6587\u4e4b\u5916\u7684\u91d1\u9010\u6d41\uff0c\u4e5f\u9690\u9690\u611f\u5230\u4e86\u4e1d\u4e1d\u5bd2 \u610f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u65e9\u5c31\u6ce8\u610f\u5230\u8fd9\u53ea\u6212\u6307\u7684\u4e86\uff0c\u6b64\u65f6\u4ed4\u7ec6\u4e00\u77a7\uff0c\u53ef\u4e0d\u6b63\u662f\u548c\u4ed6\u81ea\u5df1\u6234\u7684\u90a3\u53ea\u5bd2\u7389\u6212\u6307 \u4e00\u6a21\u4e00\u6837\uff01\u81e7\u5728\u91d1\u9010\u6d41\u5fc3\u91cc\u591a\u65f6\u7684\u8c1c\u5e95\uff0c\u6b64\u65f6\u84e6\u5730\u63ed\u5f00\uff0c\u539f\u6765\u674e\u5357\u661f\u5c31\u662f\u4ed6\u7684\u7236\u4eb2\u8981\u4ed6\u7684\u6c5f \u5e08\u5144\u6240\u4f1a\u4e4b\u4eba\u3002 \u3000\u3000\u8c1c\u5e95\u7aed\u5f00\u4e4b\u540e\uff0c\u8ddf\u7740\u6765\u7684\u662f\u66f4\u591a\u7684\u7591\u95ee\uff1a\u201c\u674e\u5927\u54e5\u83ab\u975e\u65e9\u5c31\u548c\u7239\u7239\u76f8\u8bc6\u7684\u4e86\uff0c\u5426\u5219\u7239\u7239 \u7684\u5bd2\u7389\u6212\u6307\u600e\u4f1a\u7ed9\u4ed6\uff1f\u4f46\u674e\u5927\u54e5\u4e3a\u4ec0\u4e48\u4e0d\u544a\u8bc9\u6211\u5462\uff1f\u7239\u7239\u53eb\u6c5f\u5e08\u5144\u4eca\u665a\u6765\u79d8\u9b54\u5ca9\u4e0e\u4ed6\u76f8\u4f1a\uff0c \u96be\u9053\u662f\u65e9\u5df2\u77e5\u9053\u4e86\u6709\u4eca\u665a\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u7591\u4e91\u53e0\u8d77\uff0c\u4f46\u91d1\u9010\u6d41\u4ea6\u5df2\u65e0\u6687\u7ec6\u60f3\u4e86\u3002\u6fc0\u6218\u4e2d\u91d1\u9010\u6d41\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u6708\u4eae\u5df2\u5230\u4e2d\u5929\uff0c\u91d1 \u9010\u6d41\u5927\u559c\u7b11\u9053\uff1a\u201c\u9633\u6d69\uff0c\u4f60\u83ab\u7316\u72c2\uff0c\u770b\u662f\u8c01\u8981\u675f\u624b\u5c31\u64d2\u5427\uff1f\u674e\u5927\u54e5\uff0c\u522b\u6015\u4ed6\u4eec\u7684\u865a\u58f0\u606b\u5413\uff0c \u518d\u8fc7\u4e00\u523b\uff0c\u6211\u51c6\u4fdd\u53ef\u4ee5\u628a\u4ed6\u4eec\u6740\u5f97\u5927\u8d25\u4e8f\u8f93\uff01\u201d \u3000\u3000\u674e\u5357\u661f\u6b64\u65f6\u4ea6\u662f\u7591\u60d1\u4e0d\u5b9a\uff0c\u91d1\u9010\u6d41\u6240\u6234\u7684\u90a3\u53ea\u5bd2\u7389\u6212\u6307\u4ed6\u4e5f\u770b\u89c1\u4e86\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u53ea \u9053\u9010\u6d41\u4e0d\u77e5\u6211\u662f\u4f55\u4eba\uff0c\u5982\u4eca\u770b\u6765\u4ed6\u662f\u65e9\u5c31\u89c1\u8fc7\u6211\u7684\u7239\u5a18\u7684\u4e86\u3002\u4f46\u4e3a\u4ec0\u4e48\u4ed6\u5374\u8981\u7792\u7740\u6211\u5462\uff01\u201d \u3000\u3000\u539f\u6765\u5728\u674e\u5357\u661f\u79bb\u5bb6\u524d\u5915\uff0c\u4ed6\u7684\u7236\u4eb2\u628a\u90a3\u53ea\u5bd2\u7389\u6212\u6307\u7ed9\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5230\u4e2d\u539f\uff0c\u5018\u82e5\u89c1\u7740 \u6234\u7740\u540c\u6837\u6212\u6307\u7684\u4eba\uff0c\u4f60\u5c31\u53ef\u4ee5\u653e\u5fc3\u4f9d\u9760\u4ed6\uff0c\u6c42\u4ed6\u5e2e\u4f60\u89e3\u51b3\u4efb\u4f55\u56f0\u96be\u3002\u82e5\u662f\u5728\u4f60\u78b0\u7740\u5371\u9669\u4e4b \u9645\uff0c\u4f60\u6234\u7740\u8fd9\u53ea\u6212\u6307\uff0c\u8bf4\u4e0d\u5b9a\u4e5f\u53ef\u4ee5\u52a9\u4f60\u9022\u51f6\u5316\u5409\uff0c\u9047\u96be\u6210\u7965\u3002\u201d\u674e\u5357\u661f\u5c31\u662f\u56e0\u4e3a\u8bb0\u7740\u7236\u6bcd \u7684\u5429\u5490\uff0c\u6545\u6b64\u4eca\u665a\u6765\u8d74\u9633\u6d69\u4e4b\u7ea6\uff0c\u624d\u6234\u4e86\u4e86\u8fd9\u4e2a\u5bd2\u7389\u6212\u6307\u7684\u3002\u521d\u65f6\u4ed6\u8fd8\u4e0d\u514d\u6709\u201c\u8ff7\u4fe1\u201d\u4e4b \u611f\uff0c\u53ea\u56e0\u8fd9\u662f\u7236\u4eb2\u7684\u5429\u5490\uff0c\u6240\u4ee5\u59d1\u4e14\u6234\u5b83\u8bd5\u8bd5\uff0c\u4e0d\u6599\u679c\u7136\u5728\u5371\u6025\u4e4b\u9645\uff0c\u91d1\u9010\u6d41\u5c31\u7a81\u5982\u5176\u6765 \u4e86\u3002 \u3000\u3000\u674e\u5357\u661f\u89c1\u7740\u4e86\u91d1\u9010\u6d41\u8fd9\u4e2a\u6212\u6307\uff0c\u5f53\u7136\u4ee5\u4e3a\u4ed6\u662f\u53d7\u4e86\u81ea\u5df1\u7236\u6bcd\u4e4b\u6258\u800c\u6765\uff0c\u6b8a\u4e0d\u77e5\u91d1\u9010\u6d41\u4e5f \u662f\u50cf\u4ed6\u4e00\u6837\uff0c\u5fc3\u91cc\u6b63\u85cf\u7740\u4e00\u4e2a\u95f7\u846b\u82a6\u5462\u3002 \u3000\u3000\u674e\u5357\u661f\u53d7\u4e86\u91d1\u9010\u6d41\u7684\u9f13\u821e\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u628a\u7384\u94c1\u5b9d\u5251\u4f7f\u5f97\u864e\u864e\u751f\u51e4\uff0c\u53c8\u4e0e\u6b27\u9633\u575a\u6597\u4e86\u4e8c\u4e09 \u5341\u62db\u3002\u53ef\u662f\u6b27\u9633\u575a\u7684\u96f7\u795e\u638c\u5b9e\u5728\u5389\u5bb3\uff0c\u6bcf\u53d1\u4e00\u638c\uff0c\u5c31\u597d\u4f3c\u6709\u4e00\u4e2a\u70ed\u6d6a\u6253\u6765\uff0c\u4e00\u4e2a\u6d6a\u5934\u9ad8\u8fc7\u4e00 \u4e2a\u6d6a\u5934\uff0c\u52a0\u4ee5\u674e\u5357\u661f\u53c8\u5fc5\u987b\u7528\u529b\u6765\u6325\u52a8\u90a3\u67c4\u6c89\u91cd\u5f02\u5e38\u7684\u7384\u94c1\u5b9d\u5251\uff0c\u66f4\u52a0\u5176\u70ed\u4e0d\u53ef\u5f53\u3002\u4e09\u5341\u62db \u8fc7\u540e\uff0c\u674e\u5357\u661f\u6d51\u8eab\u6e7f\u900f\uff0c\u604d\u82e5\u7f6e\u8eab\u5728\u70d8\u7089\u4e4b\u4e2d\u3002 \u3000\u3000\u674e\u5357\u661f\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u9010\u6d41\u8bf4\u5f97\u597d\u50cf\u6781\u6709\u628a\u63e1\uff0c\u5374\u4e0d\u77e5\u4ed6\u846b\u82a6\u91cc\u5356\u7684\u662f\u4ec0\u4e48\u836f\uff1f\u4f46\u770b\u6765\u4ed6 \u4e5f\u4f3c\u4e4e\u662f\u6ce5\u83e9\u8428\u8fc7\u6c5f\uff0c\u81ea\u8eab\u96be\u4fdd\u3002\u5509\uff0c\u6050\u6015\u53ea\u662f\u7a7a\u8a00\u5b89\u6170\u4e8e\u6211\u7684\u5427\uff1f\u201d \u3000\u3000\u9633\u6d69\u628a\u91d1\u9010\u6d41\u903c\u5f97\u6b65\u6b65\u540e\u9000\uff0c\u5927\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6211\u770b\u4f60\u8fd8\u5439\u4e0d\u5439\u725b\uff1f\u201d\u6b64\u65f6\u5df2\u662f\u6708\u6b63 \u5f53\u5934\uff0c\u6070\u662f\u4e09\u66f4\u65f6\u5206\uff0c\u91d1\u9010\u6d41\u5fc3\u91cc\u6709\u70b9\u7740\u614c\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u662f\u5e08\u5144\u5728\u8def\u4e0a\u6709\u4e86\u803d\u6401\uff1f\u4ed6\u82e5\u8fd8 \u4e0d\u6765\uff0c\u6211\u4eec\u53ef\u8981\u7cdf\u4e86\u3002\u201d \u3000\u3000\u9633\u6d69\u7b11\u58f0\u672a\u5df2\uff0c\u5ffd\u89c1\u79d8\u9b54\u5d16\u524d\u9762\u7684\u5927\u77f3\u53f0\u4e0a\u51fa\u73b0\u4e86\u4e00\u4e2a\u4eba\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8bf7\u5404\u4f4d\u770b\u5728\u6c5f \u67d0\u9762\u4e0a\uff0c\u5c31\u6b64\u7f62\u624b\u5982\u4f55\uff1f\u9633\u5148\u751f\uff0c\u6b27\u9633\u5148\u751f\uff0c\u4f60\u4eec\u4e24\u4f4d\u662f\u6b66\u6797\u524d\u8f88\uff0c\u4f55\u4ee5\u5bf9\u4e24\u4e2a\u540e\u751f\u5c0f\u5b50\u4e3a \u96be\uff1f\u6709\u4ec0\u4e48\u8fc7\u4e0d\u53bb\u7684\u4e8b\u60c5\uff0c\u53ef\u4ee5\u548c\u6c5f\u67d0\u8bf4\u4e48\uff1f\u6211\u62c5\u4fdd\u5728\u6211\u7684\u8eab\u4e0a\uff0c\u8fd8\u4f60\u4eec\u4e00\u4e2a\u516c\u9053\u5c31\u662f\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7a81\u7136\u73b0\u51fa\u8eab\u5f62\uff0c\u9633\u6d69\u7684\u515a\u7fbd\u4e4b\u4e2d\uff0c\u53ea\u6709\u4e24\u4e09\u4e2a\u662f\u8ba4\u5f97\u4ed6\u7684\uff0c\u5176\u4ed6\u7684\u4eba\u5c1a\u672a\u77e5\u9053\u4ed6 \u662f\u8c01\uff0c\u4e00\u89c1\u6709\u4eba\u5728\u77f3\u53f0\u4e0a\u51fa\u73b0\uff0c\u4e0d\u5f85\u4ed6\u628a\u8bdd\u8bf4\u5b8c\uff0c\u5c31\u7eb7\u7eb7\u628a\u6697\u5668\u6253\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e0d\u52a8\u58f0\u8272\uff0c\u53e3\u4e2d\u4ecd\u5728\u7ee7\u7eed\u8bf4\u8bdd\uff0c\u968f\u624b\u5728\u4e00\u6839\u5c16\u524a\u7684\u77f3\u7b0b\u4e00\u6293\uff0c\u77f3\u7b0b\u4f3c\u673d\u6728\u4e00\u822c\u7ed9 \u4ed6\u6298\u65ad\uff0c\u53ea\u89c1\u4ed6\u628a\u624b\u638c\u644a\u5f00\uff0c\u90a3\u4e00\u6bb5\u77f3\u7b0b\u5df2\u53d8\u6210\u4e86\u65e0\u6570\u788e\u77f3\u3002\u6c5f\u6d77\u5929\u6ee1\u4e0d\u5728\u4e4e\u7684\u968f\u624b\u4e00\u64a4\u3002 \u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u90a3\u8bb8\u591a\u6697\u5668\uff0c\u90fd\u7ed9\u4ed6\u7684\u788e\u77f3\u6253\u4e86\u4e0b\u6765\u3002\u66f4\u5999\u7684\u662f\uff0c\u6bcf\u4e00\u679a\u6697 \u5668\uff0c\u90fd\u662f\u521a\u597d\u843d\u5728\u4e3b\u4eba\u7684\u8eab\u65c1\uff0c\u6beb\u65e0\u5dee\u9519\u3002\u8fd9\u624b\u7edd\u4e16\u7684\u6b66\u529f\u4e00\u663e\uff0c\u4efb\u662f\u6700\u7b28\u7684\u4eba\uff0c\u4e5f\u77e5\u9053\u6c5f \u6d77\u5929\u662f\u624b\u4e0b\u7559\u60c5\uff0c\u4e0d\u60f3\u4f24\u4eba\u7684\u4e86\u3002 \u3000\u3000\u7fa4\u76d7\u6b64\u65f6\u65b9\u59cb\u77e5\u9053\u6765\u8005\u662f\u6c5f\u6d77\u5929\u3002\u6c5f\u6d77\u5929\u662f\u6b66\u6797\u516c\u8ba4\u7684\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\uff0c\u8c01\u4e0d\u77e5\u9053\u4ed6\u7684\u540d \u5934\uff1f\u89c1\u4ed6\u663e\u9732\u4e86\u8fd9\u624b\u7edd\u4e16\u795e\u529f\uff0c\u4e0d\u7531\u5f97\u4eba\u4eba\u80c6\u6218\uff0c\u4e2a\u4e2a\u5fc3\u60ca\uff0c\u95f9\u54c4\u54c4\u7684\u7fa4\u76d7\uff0c\u767b\u65f6\u5664\u82e5\u5bd2 \u8749\u3002 \u3000\u3000\u7fa4\u76d7\u4e0d\u7ea6\u800c\u540c\u7684\u4e00\u9f50\u4f4f\u624b\uff0c\u53ea\u6709\u6b27\u9633\u575a\u6b63\u4f7f\u5230\u201c\u96f7\u795e\u638c\u201d\u7684\u4e00\u62db\u6740\u624b\uff0c\u638c\u950b\u5df2\u89e6\u53ca\u4e86\u674e \u5357\u661f\u7684\u8eab\u4f53\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u715e\u4e0d\u67f1\u638c\u52bf\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53cd\u6b63\u662f\u8981\u5f97\u7f6a\u6c5f\u6d77\u5929\u7684\u4e86\uff0c\u4e0d\u5982\u628a\u8fd9\u5c0f \u5b50\u64d2\u4e86\u6765\uff0c\u4e5f\u597d\u8ba8\u4ef7\u8fd8\u4ef7\u3002\u201d \u3000\u3000\u6b27\u9633\u575a\u638c\u5fc3\u4e00\u7ffb\uff0c\u5185\u529b\u521a\u521a\u5410\u51fa\uff0c\u5ffd\u89c9\u52b2\u98ce\u98d2\u7136\uff0c\u6c5f\u6d77\u5929\u5927\u8896\u4e00\u6325\uff0c\u6070\u597d\u9694\u5728\u4ed6\u4eec\u4e8c\u4eba \u4e4b\u95f4\u3002\u6b27\u9633\u575a\u5185\u529b\u53d1\u51fa\uff0c\u6026\u4f3c\u6ce5\u725b\u5165\u6d77\uff0c\u4e00\u53bb\u65e0\u8e2a\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u7f29\u624b\u3002\u674e\u5357\u661f\u672c\u6765\u662f\u5982 \u53d7\u714e\u71ac\uff0c\u95f7\u70ed\u4e0d\u582a\u7684\uff0c\u6b64\u65f6\u4e5f\u5ffd\u5730\u5982\u6c90\u6625\u98ce\uff0c\u904d\u4f53\u6e05\u51c9\uff0c\u7cbe\u795e\u5927\u632f\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fae\u7b11\u9053\uff1a\u201c\u597d\u5728\u4e24\u4f4d\u90fd\u6ca1\u53d7\u4f24\uff0c\u8bf7\u7ed9\u6c5f\u67d0\u4e00\u4e2a\u9762\u5b50\uff0c\u6709\u8bdd\u6162\u6162\u518d\u8bf4\u5982\u4f55\uff1f\u201d\u6b27\u9633 \u575a\u672c\u4ee5\u4e3a\u674e\u5357\u661f\u514d\u4e0d\u4e86\u4e3a\u4ed6\u6240\u4f24\uff0c\u662f\u4ee5\u624d\u4e00\u4e0d\u505a\u4e8c\u4e0d\u4f11\u7684\uff0c\u6b64\u65f6\u65e2\u7136\u6ca1\u6709\u4f24\u53ca\u674e\u5357\u661f\uff0c\u4e5f\u9042 \u6d88\u4e86\u987e\u8651\u3001\u4f9b\u624b\u8bf4\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u4e4b\u547d\uff0c\u5c82\u6562\u4e0d\u9075\uff1f\u201d\u9000\u8fc7\u4e00\u65c1\u3002 \u3000\u3000\u674e\u5357\u661f\u6697\u6697\u8be7\u5f02\uff0c\u5fc3\u60f3\uff1a\u201c\u600e\u7684\u6c5f\u6d77\u5929\u4e5f\u6709\u4e00\u679a\u5bd2\u7389\u6212\u6307\uff1f\u4ed6\u53c8\u600e\u5730\u4f1a\u77e5\u9053\u6211\u6709\u4eca\u665a\u7684 \u7ea6\u4f1a\uff1f\u201d \u3000\u3000\u9633\u6d69\u5927\u662f\u5c34\u5c2c\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u4e9b\u987b\u5c0f\u4e8b\uff0c\u60f3\u4e0d\u5230\u60ca\u52a8\u4e86\u6c5f\u5927\u4fa0\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u7a76\u7adf\u662f\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u9633\u6d69\u8f7b\u63cf\u6de1\u5199\u5730\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u4eec\u4e0d\u8fc7\u662f\u60f3\u63a8\u6234\u8fd9\u4f4d\u5389\u516c\u5b50\u4f5c\u6211\u4eec\u9996\u9886\uff0c\u5389\u516c\u5b50\u4e0d \u80af\u5e94\u627f\uff0c\u8fd9\uff0c\u8fd9\u2026\u2026\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u8fd9\u4f60\u5c31\u8981\u52a8\u624b\u4f24\u4eba\u4e86\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u9633\u5149\u751f\u4e5f\u662f\u4e00\u756a\u597d\u610f\u3002\u4e0d\u8fc7\u4eba\u5404\u6709\u5fd7\uff0c\u4f3c\u4e5f\u4e0d\u5b9c\u5f3a\u4eba\u6240\u96be\u3002 \u9633\u5148\u751f\uff0c\u4e0d\u77e5\u6211\u8bf4\u5f97\u5bf9\u4e0d\u5bf9\uff1f\u201d\u6c5f\u6d77\u5929\u8bf4\u5f97\u5341\u5206\u59d4\u5a49\uff0c\u987e\u5168\u4e86\u9633\u6d69\u7684\u9762\u5b50\uff0c\u597d\u8ba9\u4ed6\u81ea\u4e0b\u53f0 \u9636\u3002 \u3000\u3000\u9633\u6d69\u6b64\u65f6\u54ea\u91cc\u8fd8\u6562\u518d\u8bf4\u3002\u5f53\u7136\u662f\u8bfa\u8bfa\u8fde\u58f0\uff0c\u97a0\u8eac\u800c\u9000\u3002\u8f6c\u773c\u95f4\u7fa4\u76d7\u8d70\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e0a\u524d\u884c\u8fc7\u4e86\u793c\uff0c\u7b11\u9053\uff1a\u201c\u597d\u5728\u5e08\u5144\u6765\u5f97\u53ca\u65f6\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u548c\u8fd9\u4f4d\u5389\u516c\u5b50\u662f\u65e9\u5c31\u8ba4\u8bc6\u7684\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u900d\uff1a\u201c\u597d\u6559\u5e08\u5144\u5f97\u77e5\uff0c\u6211\u4eec\u4e8c\u4eba\u65e9\u5df2\u662f\u516b\u62dc\u4e4b\u4ea4\u4e86\u3002\u4e0d\u8fc7\uff0c\u521a\u624d\u6211\u624d\u77e5\u9053\uff0c\u539f\u6765 \u674e\u5927\u54e5\u5c31\u662f\u7239\u7239\u8981\u4f60\u76f8\u4f1a\u4e4b\u4eba\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u542c\u4e86\u9633\u6d69\u90a3\u756a\u8a00\u8bed\uff0c\u5df2\u77e5\u5389\u5357\u661f\u7684\u6765\u5386\uff0c\u5f53\u4e0b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u672c\u6765\u5e94\u8be5\u4eb2 \u5982\u624b\u8db3\u7684\uff0c\u8fd9\u4e5f\u771f\u662f\u65e0\u5de7\u4e0d\u6210\u4e66\u4e86\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u5e08\u7236\u884c\u4e8b\u4e5f\u602a\uff0c\u65e2\u7136\u6b64\u4eba\u662f\u5389\u590d\u751f\u4e4b\u5b50\uff0c\u4e3a \u4f55\u4e0d\u65e9\u544a\u8bc9\u5e08\u5f1f\uff0c\u6559\u6211\u715e\u8d39\u7591\u731c\u3002\u201d \u3000\u3000\u674e\u5357\u661f\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\uff0c\u539f\u6765\u662f\u91d1\u4e16\u9057\u53eb\u4f60\u4eec\u6765\u7684\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7709\u5934\u4e00\u76b1\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u82e5\u8bba\u8f88\u4efd\uff0c\u6211\u5e08\u7236\u6bd4\u4f60\u9ad8\u4e86\u4e24\u8f88\uff0c\u4f60\u4e0d\u611f\u6069\u4e5f\u8fd8\u7f62\u4e86\uff0c \u5c82\u80fd\u76f4\u547c\u6211\u5e08\u7236\u4e4b\u540d\uff1f\u201d\u539f\u6765\u674e\u5357\u661f\u672c\u59d3\u5389\uff0c\u4ed6\u7684\u7236\u4eb2\u5389\u590d\u751f\u4e43\u662f\u5929\u9b54\u6559\u5949\u4e3a\u7956\u5e08\u7684\u5389\u80dc\u7537 \u7684\u4f84\u513f\uff0c\u800c\u5389\u80dc\u7537\u5219\u662f\u91d1\u4e16\u9057\u7684\u65e7\u60c5\u4eba\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e0d\u77e5\u4ed6\u7684\u7236\u4eb2\u4e0e\u5389\u5bb6\u6709\u90a3\u91cd\u516c\u6848\uff0c\u542c\u5f97\u5389\u5357\u661f\u76f4\u547c\u201c\u91d1\u4e16\u9057\u201d\u7684\u540d\u5b57\uff0c\u6beb\u4e0d\u52a0\u4ee5 \u5c0a\u79f0\uff0c\u5fc3\u91cc\u4e5f\u662f\u5f88\u4e0d\u8212\u670d\u3002\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u7239\u7239\u7ed9\u4ed6\u5bd2\u7389\u6212\u6307\uff0c\u53c8\u8981\u5e08\u5144\u8001\u8fdc\u5730\u8dd1\u6765\u5317\u4eac\u4f1a \u4ed6\uff0c\u53ef\u89c1\u7239\u7239\u5bf9\u5927\u54e5\u4e5f\u662f\u5341\u5206\u7231\u62a4\u7684\u4e86\u3002\u6211\u5e94\u8be5\u4f53\u5ff5\u7239\u7239\u7684\u7528\u5fc3\u3002\u5927\u54e5\u6216\u8bb8\u662f\u5bf9\u7239\u7239\u6709\u751a\u8bef \u4f1a\uff0c\u5c06\u6765\u6211\u603b\u4f1a\u660e\u767d\u7684\uff0c\u6b64\u65f6\u53c8\u4f55\u5fc5\u803f\u803f\u4e8e\u5fc3\uff1f\u201d\u91d1\u9010\u6d41\u672c\u6765\u662f\u4e2a\u4e0d\u62d8\u5c0f\u8282\u7684\u4eba\uff0c\u8fd9\u4e48\u4e00 \u60f3\uff0c\u4e5f\u5c31\u60f3\u5f00\u4e86\u3002 \u3000\u3000\u9648\u5149\u7167\u4e0e\u6c5f\u6d77\u5929\u672c\u6765\u662f\u76f8\u8bc6\u7684\uff0c\u8ddf\u5728\u5389\u5357\u661f\u4e4b\u540e\u3002\u4e0a\u6765\u4e0e\u6c5f\u6d77\u5929\u76f8\u89c1\uff0c\u521a\u5bd2\u55a7\u4e86\u51e0\u53e5\uff0c \u5367\u4f5b\u5bfa\u7684\u4e3b\u6301\u7a7a\u7167\u5927\u5e08\u4e5f\u6765\u4e86\u3002\u4ed6\u662f\u53d1\u73b0\u9648\u3001\u5389\u4e8c\u4eba\u5931\u8e2a\uff0c\u653e\u5fc3\u4e0d\u4e0b\uff0c\u51fa\u6765\u63a2\u4e2a\u7a76\u7adf\u7684\uff0c\u6c5f \u6d77\u5929\u4e0e\u7a7a\u7167\u5927\u5e08\u4ea4\u60c5\u975e\u6d45\uff0c\u89c1\u9762\u4e4b\u4e0b\uff0c\u5f53\u7136\u53c8\u662f\u6709\u4e00\u9635\u5bd2\u55a7\u3002\u9648\u5149\u7167\u4e0e\u91d1\u9010\u6d41\u4e43\u662f\u7b2c\u4e00\u6b21\u89c1 \u9762\uff0c\u5c11\u4e0d\u514d\u4e5f\u6709\u8bb8\u591a\u8bdd\u8bf4\uff0c\u4e00\u65f6\u95f4\uff0c\u65b0\u77e5\u53cb\uff0c\u5f7c\u6b64\u6500\u8c08\u3002\u5012\u628a\u5389\u5357\u661f\u51b7\u843d\u4e00\u65c1\u4e86\u3002 \u3000\u3000\u5389\u5357\u661f\u5ffd\u5730\u62b1\u62f3\u4e00\u63d6\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\uff0c\u591a\u8c22\u4f60\u4eca\u665a\u76f8\u52a9\u4e4b\u5fb7\uff0c\u6211\u4e0d\u9886\u4f60\u5e08\u7236\u7684 \u60c5\uff0c\u4e5f\u8be5\u9886\u4f60\u7684\u60c5\uff0c\u5927\u6069\u5f90\u56fe\u540e\u62a5\uff0c\u8bf7\u6055\u6211\u6709\u4e8b\u5148\u8d70\u4e86\u3002\u201d\u4e0d\u5f85\u6c5f\u6d77\u5929\u7b54\u8bdd\uff0c\u4e00\u8f6c\u8eab\u4fbf\u5373\u98d8 \u7136\u800c\u53bb\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e0d\u7981\u4e3a\u4e4b\u6115\u7136\u3002\u4ed6\u6b63\u5728\u966a\u7740\u7a7a\u7167\u5927\u5e08\u8bf4\u8bdd\uff0c\u4e0d\u4fbf\u8dd1\u5f00\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u8bf7\u4f60 \u66ff\u6211\u9001\u5ba2\u3002\u55ef\u3001\u4f60\u548c\u4ed6\u662f\u516b\u62dc\u4e4b\u4ea4\uff0c\u4e5f\u8be5\u9001\u4ed6\u4e00\u7a0b\u3002\u5929\u660e\u4e4b\u540e\uff0c\u4f60\u518d\u56de\u5367\u4f5b\u5bfa\u5427\u3002\u201d\u8bdd\u4e2d\u4e4b \u610f\uff0c\u81ea\u662f\u8981\u91d1\u9010\u6d41\u53bb\u5411\u5389\u5357\u661f\u95ee\u4e2a\u6e05\u695a\u3002\u91d1\u9010\u6d41\u6ee1\u8179\u7591\u56e2\uff0c\u5176\u5b9e\u65e0\u987b\u5e08\u5144\u63d0\u793a\uff0c\u4ed6\u4e5f\u662f\u8981\u53bb \u95ee\u4e2a\u6e05\u695a\u7684\u4e86\u3002\u4ed6\u7684\u8f7b\u529f\u8fdc\u5728\u5389\u5357\u661f\u4e4b\u4e0a\uff0c\u5389\u5357\u661f\u4e5f\u4f3c\u4e4e\u6709\u610f\u7b49\u4ed6\uff0c\u53ea\u8ffd\u8fc7\u4e86\u4e00\u4e2a\u5c71\u5773\uff0c\u4fbf \u5df2\u8ffd\u4e0a\u3002 \u3000\u3000\u5389\u5357\u661f\u56de\u5934\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6211\u77e5\u9053\u4f60\u4f1a\u6765\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6709\u8bb8\u591a\u4e8b\u6211\u4e0d\u660e\u767d\u2026\u2026\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u4f60\u7239\u7239\u4ece\u6765\u6ca1\u6709\u548c\u4f60\u8bf4\u8d77\u6211\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6ca1\u6709\u3002\u7239\u7239\u53eb\u6211\u5e26\u4e00\u5c01\u4fe1\u7ed9\u5e08\u5144\uff0c\u8981\u6c5f\u5e08\u5144\u4eca\u665a\u5230\u6b64\u4f1a\u4f60\uff0c\u90a3\u5c01\u4fe1\u6211\u4e5f\u662f \u89c1\u7740\u4e86\u5e08\u5144\u624d\u62c6\u5f00\u6765\u770b\u7684\uff0c\u6211\u4e5f\u89c9\u5f97\u5947\u602a\uff0c\u7239\u7239\u597d\u4f3c\u65e9\u5c31\u6599\u5230\u4e86\u6709\u4eca\u665a\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u4f60\u662f\u51e0\u65f6\u79bb\u5bb6\u7684\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6709\u4e94\u4e2a\u591a\u6708\u4e86\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5982\u6b64\uff0c\u8fd9\u5c31\u602a\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4ec0\u4e48\u602a\u4e0d\u5f97\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u6211\u4e0e\u9633\u6d69\u4eca\u665a\u4e4b\u7ea6\uff0c\u662f\u534a\u5e74\u4e4b\u524d\u5c31\u5b9a\u4e0b\u7684\u3002\u4f60\u7684\u7239\u7239\u867d\u7136\u8eab\u5904\u6d77\u5916\uff0c\u4f46\u4ed6 \u5728\u4e2d\u539f\u7684\u6b66\u6797\u670b\u53cb\u6781\u591a\uff0c\u60f3\u5fc5\u662f\u65e9\u5df2\u77e5\u9053\u4e86\u8fd9\u4e2a\u6d88\u606f\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u548c\u6211\u7239\u7239\u662f\u65e9\u5df2\u76f8\u8bc6\u7684\u5417\uff1f\u4f60\uff0c\u4f60\u4f55\u4e0d\u65e9\u8bf4\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u4f60\u7684\u7239\u7239\u6bcf\u9694\u4e00\u4e24\u5e74\u5c31\u5230\u6211\u5bb6\u4e00\u6b21\uff0c\u6211\u5f97\u4ed6\u7684\u6307\u70b9\u5f88\u591a\uff0c\u5c24\u5176\u662f\u5185\u529f\u548c\u5251 \u672f\uff0c\u6211\u81ea\u5c0f\u5c31\u662f\u8ddf\u4f60\u7239\u5b66\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u604d\u7136\u5927\u609f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u90a3\u65e5\u5728\u957f\u57ce\u4e4b\u4e0a\uff0c\u6211\u821e\u5251\u5927\u54e5\u5f39\u7434\uff0c\u7434\u97f3\u7684\u8282\u62cd \u548c\u6211\u7684\u62db\u6570\u914d\u5408\u5f97\u4e1d\u4e1d\u5165\u6263\u3002\u8fd9\u4e48\u8bf4\u6765\uff0c\u4ed6\u5373\u4f7f\u672a\u66fe\u6b63\u5f0f\u62dc\u5e08\uff0c\u4e5f\u7b97\u5f97\u662f\u7239\u7239\u7684\u8bb0\u540d\u5f1f\u5b50 \u4e86\u3002\u5374\u4f55\u4ee5\u9002\u624d\u5728\u8a00\u8bed\u4e4b\u95f4\uff0c\u5bf9\u6211\u7684\u7239\u7239\u4f3c\u4e4e\u751a\u662f\u4e0d\u6ee1\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u6709\u6240\u7591\uff0c\u4f46\u4e3a\u4e86\u987e\u5168\u53cb\u9053\uff0c\u4e0d\u4fbf\u5766\u7387\u8bd8\u8d23\uff0c\u6b63\u5728\u76d8\u7b97\u5982\u4f55\u59d4\u5a49\u63aa\u8f9e\u4e4b\u9645\uff0c\u5389\u5357 \u661f\u53f9\u4e86\u53e3\u6c14\uff0c\u5148\u81ea\u8bf4\u4e86\u3002 \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u4ee4\u5c0a\u628a\u6211\u5f53\u4f5c\u5b50\u4f84\u4e00\u822c\u770b\u5f85\uff0c\u6211\u81ea\u5c0f\u5f97\u4ee4\u5c0a\u7231\u62a4\uff0c\u5fc3\u91cc\u4e5f\u662f\u5f88\u611f\u6fc0\u7684\u3002\u53ea \u56e0\u6211\u6709\u4e00\u4e8b\u90c1\u90c1\u4e8e\u5fc3\uff0c\u9002\u624d\u8a00\u8bed\u4e4b\u95f4\u5bf9\u4ee4\u5c0a\u5927\u662f\u4e0d\u656c\u3002\u5176\u5b9e\u505a\u5c0f\u8f88\u662f\u4e0d\u8be5\u8fd9\u7965\u7684\uff0c\u8fd9\u662f\u6211\u7684 \u8fc7\u9519\uff0c\u8bf7\u5411\u8d24\u5f1f\u8c22\u8fc7\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u4ece\u201c\u4f60\u7684\u7239\u7239\u201d\u6539\u53e3\u201c\u4ee4\u5c0a\u201d\uff0c\u53c8\u5411\u91d1\u9010\u6d41\u8d54\u4e86\u793c\uff0c\u8ba4\u4e86\u9519\uff0c\u91d1\u9010\u6d41\u7684\u6c14\u4e5f\u81ea\u5e73 \u4e86\u3002\u53ef\u662f\u5fc3\u91cc\u7684\u7591\u56e2\u4f9d\u7136\u672a\u91ca\uff0c\u95ee\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4f7f\u5927\u54e5\u90c1\u90c1\u4e8e\u5fc3\uff0c\u548c\u6211\u7239\u7239\u53c8\u6709\u4ec0\u4e48\u5173 \u7cfb\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4e8b\u60c5\u5df2\u7ecf\u8fc7\u53bb\u4e86\u5341\u5e74\u4e86\uff0c\u54b1\u4eec\u505a\u5c0f\u8f88\u7684\u4f55\u5fc5\u8fd8\u53bb\u8bf4\u5b83\uff1f\u7b97 \u4e86\u5427\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u518d\u95ee\u4ee4\u5c0a\u3002\u201d \u3000\u3000\u539f\u6765\u5389\u5357\u661f\u5728\u5bb6\u4e4b\u65f6\uff0c\u4ed6\u7236\u6bcd\u4ece\u5c0f\u548c\u4ed6\u8bf4\u8fc7\uff0c\u4ed6\u4e5f\u662f\u5e76\u4e0d\u77e5\u9053\u91d1\u4e16\u9057\u4e0e\u5389\u80dc\u7537\u90a3\u4ef6\u516c\u6848 \u7684\uff0c\u5230\u4e86\u4e2d\u539f\u4e4b\u540e\uff0c\u4ed6\u4f1a\u89c1\u4e86\u5929\u9b54\u6559\u7684\u4e00\u4e9b\u65e7\u4eba\uff0c\u5176\u4e2d\u6709\u51e0\u4e2a\u5bf9\u91d1\u4e16\u9057\u65e7\u6028\u672a\u6d88\uff0c\u628a\u8fd9\u4ef6\u4e8b \u60c5\u544a\u8bc9\u5389\u5357\u661f\uff0c\u53ef\u662f\u4ed6\u4eec\u4e5f\u4e0d\u662f\u77e5\u9053\u5f97\u5341\u5206\u6e05\u695a\uff0c\u4e8e\u662f\u8bf4\u8d77\u4e86\u201c\u91d1\u3001\u5389\u4e4b\u604b\u201d\u7684\u60c5\u53f2\uff0c\u5c31\u514d \u4e0d\u4e86\u52a0\u6cb9\u6dfb\u9171\uff0c\u7f16\u6d3e\u91d1\u4e16\u9057\u7684\u4e0d\u662f\u3002\u751a\u81f3\u628a\u5389\u80dc\u7537\u4e4b\u6b7b\uff0c\u8bf4\u6210\u662f\u7531\u4e8e\u4f1a\u4e16\u9057\u7684\u8d1f\u5fc3\u522b\u604b\uff0c\u4ee5 \u81f4\u4ee4\u5f97\u5389\u80dc\u7537\u81ea\u6740\u7684\u3002 \u3000\u3000\u5982\u679c\u5389\u5357\u661f\u7684\u7236\u6bcd\u65e9\u5c31\u544a\u8bc9\u4ed6\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u8ba9\u4ed6\u77e5\u9053\u4e8b\u60c5\u7684\u771f\u76f8\uff0c\u90a3\u5c31\u8981\u597d\u5f97\u591a\u3002\u504f\u504f\u4ed6 \u7684\u7236\u6bcd\u4e3a\u4e86\u907f\u5fcc\uff0c\u4ece\u6ca1\u548c\u4ed6\u8bf4\u8fc7\uff0c\u5982\u4eca\u4ed6\u8ba4\u5916\u4eba\u7684\u53e3\u4e2d\u542c\u5230\uff0c\u628a\u90a3\u4e9b\u4e0d\u5c3d\u4e0d\u5b9e\u7684\u8bf4\u8bdd\u90fd\u5f53\u4f5c \u4e3a\u771f\uff0c\u5fc3\u91cc\u53ef\u5c31\u5927\u53d7\u523a\u6fc0\u4e86\u3002\u5c24\u5176\u662f\u4ed6\u81ea\u5c0f\u5c31\u5d07\u62dc\u91d1\u4e16\u9057\u7684\uff0c\u4e00\u65e6\u53d1\u89c9\u81ea\u5df1\u6240\u5d07\u62dc\u7684\u4eba\u505a\u4e86 \u201c\u4e8f\u5fc3\u4e8b\u201d\uff0c\u201c\u5bb3\u6b7b\u201d\u7684\u4eba\u53c8\u662f\u4ed6\u7684\u59d1\u5a46\uff0c\u4ed6\u66f4\u662f\u6709\u4e86\u201c\u53d7\u9a97\u201d\u7684\u611f\u89c9\u3002\u5f88\u81ea\u7136\u7684\u5c31\u6709\u4e86\u8fd9 \u6837\u7684\u60f3\u6cd5\uff1a\u201c\u539f\u6765\u91d1\u4e16\u9057\u662f\u56e0\u4e3a\u5185\u759a\u4e8e\u5fc3\uff0c\u89c9\u5f97\u5bf9\u4e0d\u8d77\u6211\u5bb6\uff0c\u6240\u4ee5\u624d\u4f20\u6211\u6b66\u529f\uff0c\u4ee5\u56fe\u8865\u8fc7 \u7684\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u662f\u4e2a\u611f\u60c5\u5bb9\u6613\u51b2\u52a8\u7684\u4eba\uff0c\u4ed6\u6709\u4e86\u8fd9\u4e2a\u60f3\u6cd5\uff0c\u5bf9\u91d1\u4e16\u9057\u81ea\u662f\u96be\u4ee5\u8c05\u89e3\u3002\u4e0d\u8fc7\uff0c\u4e5f\u6b63 \u56e0\u4ed6\u662f\u4e2a\u611f\u60c5\u5bb9\u6613\u51b2\u52a8\u7684\u4eba\uff0c\u91d1\u9010\u6d41\u7684\u53cb\u8c0a\u611f\u52a8\u4e86\u4ed6\uff0c\u4ed6\u56de\u60f3\u8d77\u4f59\u4e16\u9057\u5bf9\u4ed6\u7231\u62a4\u4e4b\u8bda\uff0c\u4e5f\u5c31 \u4e0d\u89c9\u6709\u4e9b\u540e\u6094\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u662f\u4e2a\u6f47\u6d12\u8c41\u8fbe\uff0c\u4e0d\u62d8\u5c0f\u8282\u7684\u4eba\u3002\u5389\u5357\u661f\u5df2\u7ecf\u8d54\u4e86\u793c\uff0c\u4ed6\u5fc3\u91cc\u65e9\u5df2\u82a5\u8482\u5168\u6d88\u3002\u6b64\u65f6 \u867d\u7136\u7591\u56e2\u672a\u91ca\uff0c\u4f46\u5389\u5357\u661f\u4e0d\u613f\u8bf4\u90a3\u65e7\u4e8b\uff0c\u91d1\u9010\u6d41\u4e5f\u5c31\u4e0d\u518d\u95ee\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u5c31\u8fd9\u6837\u8d70\u4e86\u4e48\uff1f\u6c5f\u5e05\u5144\u4e5f\u8fd8\u60f3\u548c\u4f60\u8bf4\u8bf4\u8bdd\u5462\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u53f2\u59d1\u5a18\u5728\u516d\u5408\u5e2e\u603b\u8235\u5ea6\u65e5\u5982\u5e74\uff0c\u6211\u6068\u4e0d\u80fd\u63d2\u7fc5\u8d76\u53bb\u4f1a\u5979\u3002\u4ee5\u540e\u6211\u518d \u53bb\u4e13\u7a0b\u62dc\u8bbf\u4f60\u7684\u5e08\u5144\u5427\uff0c\u8d24\u5f1f\uff0c\u8bf7\u4f60\u4ee3\u6211\u5411\u4ee4\u5e08\u5144\u548c\u9648\u5927\u54e5\u544a\u7f6a\u4e00\u58f0\u3002\u4ed6\u4eec\u5728\u7b49\u7740\u4f60\uff0c\u4f60\uff0c \u4f60\u56de\u53bb\u5427\uff01 \u3000\u3000\u5389\u5357\u661f\u53e3\u91cc\u50ac\u4fc3\u91d1\u9010\u6d41\u56de\u53bb\uff0c\u5fc3\u91cc\u5374\u662f\u76fc\u671b\u4ed6\u548c\u81ea\u5df1\u540c\u5f80\u516d\u5408\u5e2e\u7684\u3002\u4e0d\u8fc7\uff0c\u56e0\u4e3a\u65e5\u524d\u5728 \u6234\u5bb6\u5df2\u7ecf\u906d\u4e86\u4e00\u6b21\u62d2\u7edd\uff0c\u4ed6\u4e5f\u662f\u4e2a\u81ea\u5c0a\u5fc3\u91cd\u7684\u4eba\uff0c\u662f\u4ee5\u4e0d\u613f\u518d\u5411\u91d1\u9010\u6d41\u6c42\u8bf7\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u9635\u8f9b\u9178\uff0c\u60d8\u7136\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f46\u613f\u5927\u54e5\u8bf8\u4e8b\u79f0\u5fc3\uff0c\u4e0e\u53f2\u59d1\u5a18\u540c\u5055\u767d\u9996\u3002\u6211\u56de\u53bb \u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u56de\u5934\u8d70\u4e86\u51e0\u6b65\uff0c\u53ea\u542c\u5f97\u5389\u5357\u661f\u7eb5\u58f0\u6b4c\u9053\uff1a\u201c\u84b9\u82cd\u82cd\uff0c\u767d\u9732\u4e3a\u971c\uff0c\u6240\u8c13\u4f0a\u4eba\uff0c\u5728\u6c34 \u4e00\u65b9\u3002\u6eaf\u6d41\u4ece\u4e4b\uff0c\u9053\u963b\u4e14\u957f\uff0c\u6eaf\u6e38\u4ece\u4e4b\uff0c\u5b9b\u5728\u6c34\u4e2d\u592e\uff01\u201d\u8fd9\u662f\u300a\u8bd7\u7ecf\u300b\u300a\u79e6\u98ce\u300b\u4e2d\u7684\u4e00\u8282\uff0c \u610f\u601d\u662f\u8bf4\uff0c\u201c\u82a6\u82b1\uff08\u517c\u859b\uff09\uff08\u4e00\u7247\u767d\u82cd\u82cd\uff0c\u6e05\u65e9\u9732\u6c34\u53d8\u6210\u971c\uff0c\u5fc3\u4e0a\u7684\u4eba\u513f\u54ea\u5728\u6c34\u7684\u90a3\u4e00\u65b9\u3002 \u6211\u9006\u7740\u6c34\u6d41\u53bb\u627e\u5979\uff0c\u7ed5\u6765\u7ed5\u53bb\u9053\u51e0\u5929\uff0c\u6211\u987a\u7740\u6c34\u6d41\u53bb\u627e\u5979\uff0c\u5979\u5440\u5374\u50cf\u5728\u56db\u8fb9\u4e0d\u7740\u7684\u6c34\u4e2d \u592e\u3002\u201d \u3000\u3000\u8fd9\u9996\u8bd7\u523b\u5212\u4e86\u9053\u6c42\u8005\u5fae\u5999\u7684\u5fc3\u4e8b\uff0c\u4ed6\u662f\u90a3\u6837\u503e\u6155\u4e8e\u90a3\u4e2a\u5973\u5b50\uff0c\u53c8\u6015\u8ffd\u4e0d\u7740\u5979\u3002\u610f\u4e2d\u4eba\u597d \u4f3c\u8fd1\u5728\u773c\u524d\uff0c\u53c8\u4f3c\u8fdc\u5728\u5929\u8fb9\uff0c\u603b\u4e4b\u662f\u201c\u53ef\u671b\u800c\u4e0d\u53ef\u5373\u201d\uff0c\u4ee4\u4ed6\u4e0d\u7981\u65e5\u601d\u591c\u60f3\u4e5d\u56de\u80a0\uff01 \u3000\u3000\u91d1\u9010\u6d41\u6014\u6014\u5730\u56de\u8fc7\u5934\u53bb\uff0c\u53ea\u89c1\u5389\u5357\u661f\u821e\u5251\u72c2\u6b4c\uff0c\u8f6c\u773c\u95f4\u5f71\u5b50\u5df2\u662f\u6ca1\u5165\u6797\u4e2d\uff0c\u770b\u4e0d\u89c1\u4e86\u3002 \u91d1\u9010\u6d41\u5fc3\u91cc\u6697\u81ea\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u5bf9\u7ea2\u82f1\u662f\u5982\u6b64\u4e00\u5f80\u60c5\u6df1\uff0c\u6211\u5c82\u80fd\u593a\u4ed6\u6240\u7231\uff1f\u5509\uff0c\u4ece \u4eca\u4e4b\u540e\uff0c\u6211\u53ef\u4e0d\u8981\u518d\u60f3\u53f2\u7ea2\u82f1\u4e86\u3002\u201d\u4ed6\u5fc3\u91cc\u7981\u6b62\u81ea\u5df1\u53bb\u60f3\uff0c\u8111\u6d77\u4e2d\u5374\u504f\u504f\u73b0\u51fa\u53f2\u7ea2\u82f1\u7684\u5f71 \u5b50\u3002 \u3000\u3000\u91d1\u9010\u6d41\u60c5\u601d\u60d8\u60d8\uff0c\u56de\u5230\u79d8\u9b54\u5d16\uff0c\u6c5f\u6d77\u5929\u548c\u7a7a\u7167\u5927\u5e08\u3001\u9648\u5149\u7167\u7b49\u4eba\u8fd8\u5728\u90a3\u513f\u3002\u6c5f\u6d77\u5929\u9053\uff1a \u201c\u4f60\u8fd9\u4e48\u5feb\u5c31\u56de\u6765\u4e86\uff1f\u201d\u9648\u5149\u7167\u9053\uff1a\u201c\u674e\u5927\u54e5\u5462\uff1f\u4ed6\u4e0d\u80af\u548c\u4f60\u56de\u6765\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u674e\u5927\u54e5\u53e6\u5916\u6709\u4e8b\uff0c\u4ed6\u8981\u8d76\u7740\u53bb\u4e00\u4e2a\u5730\u65b9\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u4eba\u4e5f\u662f\u6709\u70b9\u53e4\u602a\uff0c\u5e08\u5f1f\uff0c\u4ed6\u548c\u4f60\u8bf4\u4e86\u4e9b\u4ec0\u4e48\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u60f3\u90a3\u4e8b\u60f3\u6765\u5e08\u5144\u5f53\u4f1a\u77e5\u9053\uff0c\u4e8e\u662f\u95ee\u9053\uff1a\u201c\u4ed6\u8bf4\u6709\u4ef6\u4e8b\u4ee4\u4ed6\u90c1\u90c1\u4e8e\u5fc3\uff0c\u5374\u4e0d\u77e5\u6b64 \u4e8b\u4e0e\u7239\u7239\u6709\u751a\u5173\u8054\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u53f9\u606f\u9053\uff1a\u201c\u6211\u660e\u767d\u4e86\u3002\u60f3\u5fc5\u662f\u4ed6\u542c\u4e86\u4e9b\u4ec0\u4e48\u95f2\u8a00\u95f2\u8bed\uff0c\u4ee5\u81f4\u5fc3\u6709\u7599\u7629\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u53f9\u9053\uff1a\u201c\u4ec0\u4e48\u95f2\u8a00\u95f2\u8bed\uff0c\u4f1a\u4f7f\u5927\u54e5\u5fc3\u6709\u7599\u7629\uff1f\u8fd9\u4ef6\u4e8b\u4e00\u5b9a\u662f\u548c\u5389\u5bb6\u6709\u5173\u7684\u4e86\uff0c\u662f \u4e48\uff1f\u201d\u91d1\u9010\u6d41\u597d\u5947\u5fc3\u8d77\uff0c\u7ed9\u6c5f\u6d77\u5929\u6765\u4e2a\u6253\u7834\u6c99\u9505\u95ee\u5230\u5e95\uff0c\u5012\u6559\u6c5f\u6d77\u5929\u4e3a\u96be\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u7684\u5389\u5927\u54e5\u53ef\u80fd\u5bf9\u5e08\u7236\u6709\u70b9\u8bef\u4f1a\uff0c\u4f46\u8fd9\u4ef6\u4e8b\u60c5\u5e76\u4e0d\u662f\u4f60\u7239\u7239\u7684\u9519\uff0c\u5c06\u6765\u4ed6 \u4e00\u5b9a\u4f1a\u660e\u767d\u7684\u3002\u201d\u6c5f\u6d77\u5929\u4e0d\u4fbf\u8c08\u5e08\u7236\u7684\u201c\u60c5\u53f2\u201d\uff0c\u53ea\u597d\u5982\u6b64\u4f5c\u7b54\u3002 \u3000\u3000\u7a7a\u7167\u5927\u5e08\u5ffd\u9053\uff1a\u201c\u80fd\u6240\u53cc\u5fd8\uff0c\u8272\u7a7a\u5e76\u9053\u3002\u4e00\u5207\u56e0\u7f18\uff0c\u4e91\u70df\u8fc7\u773c\u3002\u5fc3\u65e0\u7a92\u788d\uff0c\u8bf4\u4ea6\u65e0 \u59a8\u3002\u201d\u4ed6\u662f\u5f97\u9053\u9ad8\u50e7\uff0c\u5fc3\u77e5\u91d1\u9010\u6d41\u597d\u5947\u4e4b\u5ff5\u4e00\u751f\uff0c\u82e5\u4e0d\u95ee\u4e2a\u7a76\u7adf\uff0c\u5fc3\u5934\u4e4b\u7ed3\u96be\u89e3\u3002\u662f\u4ee5\u8bf4\u4e86 \u51e0\u53e5\u8c12\u8bed\uff0c\u4e3b\u5f20\u628a\u4e8b\u60c5\u539f\u59d4\uff0c\u544a\u8bc9\u91d1\u9010\u6d41\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u672c\u6765\u662f\u4e2a\u723d\u6717\u7684\u4eba\uff0c\u542c\u4e86\u7a7a\u7167\u5927\u5e08\u4e4b\u8a00\uff0c\u7b11\u9053\uff1a\u201c\u5927\u5e08\u65e2\u8bf4\u65e0\u59a8\uff0c\u6211\u5c31\u8bf4\u5427\u3002\u4f60 \u7684\u7239\u7239\u548c\u6211\u662f\u540c\u4e00\u65e5\u7ed3\u5a5a\u7684\uff0c\u4f60\u53ef\u77e5\u9053\u4ec0\u4e48\u7f18\u6545\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u662f\u4e0d\u662f\u6211\u7239\u7239\u548c\u6211\u5a18\u76f8\u8bc6 \u5f97\u8fdf\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u662f\u3002\u5e08\u7236\u662f\u4e3a\u4e86\u4e00\u4e2a\u5973\u5b50\u7684\u7f18\u6545\uff0c\u4ee5\u81f4\u8fdf\u4e86\u4e8c\u5341\u5e74\u624d\u548c\u4f60\u6bcd\u4eb2\u6210 \u5a5a\u3002\u201d\u91d1\u9010\u6d41\u5927\u611f\u5174\u8da3\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5973\u5b50\u60f3\u5fc5\u662f\u5389\u5bb6\u7684\u4eba\uff0c\u5e08\u54e5\uff0c\u4f60\u544a\u8bc9\u6211\u8fd9\u4e2a\u6545\u4e8b\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u5973\u5b50\u540d\u53eb\u5389\u80dc\u7537\u3002\u4f60\u7684\u7239\u5a18\u76f8\u8bc6\u5728\u5148\uff0c\u548c\u5389\u80dc\u7537\u76f8\u8bc6\u5728\u540e\uff0c\u5389\u80dc \u7537\u75f4\u604b\u4f60\u7684\u7239\u7239\uff0c\u5374\u4e0d\u77e5\u4f60\u7684\u7239\u5a18\u65e9\u5df2\u5fc3\u5fc3\u76f8\u5370\u3002\u4e0d\u8fc7\u5e08\u7236\u5e08\u6bcd\u867d\u7136\u5fc3\u5fc3\u76f8\u5370\uff0c\u5c1a\u8fd8\u672a\u6709\u5a5a \u7ea6\uff0c\u540e\u6765\u5389\u80dc\u7537\u548c\u4f60\u7239\u7239\u8054\u624b\uff0c\u6253\u8d25\u4e86\u5927\u9b54\u5934\u5b5f\u795e\u901a\uff0c\u5176\u540e\u5389\u80dc\u7537\u53c8\u5728\u5929\u5c71\u6bd4\u6b66\uff0c\u80dc\u4e86\u5929\u5c71 \u6d3e\u8001\u638c\u95e8\u5510\u6653\u6f9c\uff0c\u593a\u4e86\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\u7684\u540d\u5934\uff0c\u5979\u81ea\u5df1\u4ea6\u53d7\u4e86\u91cd\u4f24\u3002\u4f60\u7239\u7239\u611f\u5979\u60c5\u75f4\uff0c\u548c\u5979\u4f5c \u4e86\u4e00\u591c\u592b\u59bb\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u600e\u4e48\u53ea\u662f\u4e00\u591c\u592b\u59bb\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5389\u80dc\u7537\u5728\u6d1e\u623f\u4e4b\u591c\u4fbf\u5373\u9999\u6d88\u7389\u6b92\uff0c\u662f\u4ee5\u548c\u4f60\u7239\u7239\u53ea\u6709\u592b\u59bb\u4e4b\u540d\uff0c\u5e76\u65e0\u592b\u59bb \u4e4b\u5b9e\u3002\u4f60\u7684\u7239\u7239\u4e3a\u5fcf\u60c5\u7f18\uff0c\u8fdf\u4e86\u4e8c\u5341\u5e74\u624d\u5a36\u4f60\u7684\u6bcd\u4eb2\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b80\u7565\u7684\u5c06\u91d1\u9010\u6d41\u7236\u6bcd\u4e0e\u5389\u80dc\u7537\u4e4b\u95f4\u7684\u60c5\u573a\u8ffd\u9010\u4e4b\u4e8b\u544a\u8bc9\u4e86\u91d1\u9010\u6d41\uff0c\u8a00\u8bed\u4e4b\u4e2d\uff0c\u81ea \u7136\u662f\u6bd4\u8f83\u504f\u8892\u5e08\u7236\u5e08\u6bcd\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e5f\u602a\u4e0d\u5f97\u6c5f\u6d77\u5929\uff0c\u6c5f\u6d77\u5929\u4e5f\u4e0d\u77e5\u9053\uff0c\u5c11\u5e74\u65f6\u5019\u7684\u4ed6\u7684\u5e08 \u7236\uff0c\u5fc3\u4e2d\u771f\u6b63\u7231\u7684\u5176\u5b9e\u8fd8\u662f\u5389\u80dc\u7537\u3002 \u3000\u3000\u91d1\u9010\u6d41\u542c\u5f97\u75f4\u4e86\u3002\u8fd9\u4e2a\u6545\u4e8b\uff0c\u7ed9\u4ed6\u8bb8\u591a\u611f\u89e6\uff0c\u4ed6\u7231\u4ed6\u7684\u7236\u6bcd\uff0c\u4f46\u542c\u4e86\u8fd9\u4e2a\u6545\u4e8b\uff0c\u5374\u4e5f\u5341 \u5206\u540c\u60c5\u5389\u80dc\u7537\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4f4d\u5389\u59d1\u5a18\u7528\u60c5\u4e4b\u4e13\uff0c\u5f53\u771f\u662f\u4eba\u95f4\u5c11\u6709\u3002\u5979\u4e3a\u4e86\u83b7\u5f97\u7239\u7239\uff0c\u4e0d \u60dc\u7528\u5c3d\u4e00\u5207\u624b\u6bb5\u3002\u4f46\u5728\u83b7\u5f97\u592b\u59bb\u540d\u4efd\u4e4b\u540e\uff0c\u5374\u53c8\u4e0d\u60dc\u727a\u7272\u81ea\u5df1\uff0c\u6210\u5168\u6211\u7684\u7239\u5a18\u3002\u56e0\u4e3a\u5979\u77e5\u9053 \u6211\u7684\u7239\u5a18\u65e9\u5df2\u5fc3\u5fc3\u76f8\u5370\uff0c\u80fd\u4f7f\u81ea\u5df1\u6240\u7231\u7684\u4eba\u5f97\u5230\u5e78\u798f\uff0c\u867d\u6b7b\u4f55\u61be\uff1f\u8fd9\u4f4d\u5389\u59d1\u5a18\u53ef\u8bf4\u662f\u61c2\u5f97\u4e86 \u7231\u60c5\u7684\u771f\u8c1b\u4e86\u3002\u201d \u3000\u3000\u60f3\u5230\u6b64\u5904\uff0c\u84e6\u5730\u5fc3\u5934\u4e00\u9707\uff0c\u81ea\u601d\u81ea\u5fd6\uff1a\u201c\u5982\u4eca\u6211\u548c\u674e\u5927\u54e5\u4e0e\u53f2\u59d1\u5a18\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u5c82\u4e0d\u662f \u4e5f\u5f88\u50cf\u4ed6\u4eec\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u5929\u82e5\u6709\u60c5\u5929\u4ea6\u8001\uff0c\u95ee\u8c01\u771f\u4e2a\u89e3\u75f4\u60c5\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u56db\u56de\u3000\u7985\u673a\u5999\u609f\u6e38\u65b9\u5916\u3000\u65e7\u68a6\u96be\u5fd8\u5760\u7b97\u4e2d \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e8c\u5341\u56db\u56de\u3000\u7985\u673a\u5999\u609f\u6e38\u65b9\u5916\u3000\u65e7\u68a6\u96be\u5fd8\u5760\u7b97\u4e2d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u90a3\u4f4d\u5389\u59d1\u5a18\u4e0d\u60dc\u727a\u7272\u81ea\u5df1\uff0c\u6210\u5168\u6211\u7684\u7239\u5a18\uff0c\u6211\u662f\u4e0d\u662f\u4e5f\u8be5\u727a\u7272\u81ea \u5df1\uff0c\u6210\u5168\u5927\u54e5\u5462\uff1f\u4f46\u90a3\u4f4d\u5389\u59d1\u59d1\u662f\u65e9\u5df2\u77e5\u9053\u6211\u7684\u7239\u5a18\u5fc3\u5fc3\u76f8\u5370\u7684\uff0c\u6211\u5374\u5c1a\u672a\u77e5\u9053\u7ea2\u82f1\u662f\u5426\u771f \u7684\u7231\u4e0a\u4e86\u5927\u54e5\u3002\u4e07\u4e00\uff0c\u5979\u662f\u5c5e\u610f\u4e8e\u6211\uff0c\u6211\u727a\u7272\u81ea\u5df1\u4e0d\u6253\u7d27\uff0c\u5927\u54e5\u5c06\u6765\u77e5\u9053\u4e86\u771f\u76f8\uff0c\u4ee5\u4ed6\u7684\u6027 \u683c\uff0c\u5c82\u4e0d\u662f\u53c8\u8981\u90c1\u90c1\u7ec8\u751f\uff1f\u201d\u91d1\u9010\u6d41\u53c8\u60f3\uff1a\u201c\u4e0d\u8fc7\uff0c\u82e5\u662f\u7531\u5927\u54e5\u626e\u6f14\u90a3\u4f4d\u5389\u59d1\u59d1\u7684\u89d2\u8272\uff0c\u6211 \u8fd9\u4e00\u751f\u4e5f\u662f\u5f97\u4e0d\u5230\u5feb\u4e50\u7684\u3002\u5509\uff0c\u96be\u9053\u4e0a\u4e00\u4ee3\u7684\u60b2\u5267\u53c8\u8981\u91cd\u6f14\u4e0d\u6210\uff1f\u201d \u3000\u3000\u60f3\u81f3\u6b64\u5904\uff0c\u91d1\u9010\u6d41\u5ffd\u5730\u89c9\u5f97\u50cf\u5389\u80dc\u7537\u90a3\u6837\u7684\u75f4\u60c5\uff0c\u4e5f\u4f3c\u4e4e\u4e0d\u5927\u5bf9\u4e86\u3002\u4f46\u7a76\u7adf\u662f\u4ec0\u4e48\u5730\u65b9 \u4e0d\u5bf9\uff0c\u4ed6\u4e00\u65f6\u4e5f\u8bf4\u4e0d\u4e0a\u6765\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u8bf4\u5b8c\u4e86\u8fd9\u4e2a\u6545\u4e8b\uff0c\u9648\u5149\u7167\u4e5f\u4f3c\u4e4e\u542c\u5f97\u75f4\u4e86\uff0c\u8138\u4e0a\u73b0\u51fa\u4e00\u6d3e\u832b\u7136\u7684\u795e\u8272\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u4e2d\u4e00\u52a8\uff1a\u201c\u54a6\uff0c\u9648\u5927\u54e5\u4e5f\u597d\u50cf\u6709\u751a\u5fc3\u4e8b\uff1f\u201d \u3000\u3000\u7a7a\u7167\u5927\u5e08\u5ffd\u5730\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u5584\u54c9\uff0c\u5584\u54c9\uff01\u4e00\u5760\u60c5\u52ab\uff0c\u70e6\u607c\u9042\u751f\u3002\u4ee5\u91d1\u5927\u4fa0\u8fd9\u6837\u6709\u6167\u6839\u7684 \u4eba\u4e5f\u514d\u4e0d\u4e86\u4e8c\u5341\u5e74\u7684\u82e6\u607c\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u95ee\u9053\uff1a\u201c\u8981\u5982\u4f55\u65b9\u80fd\u514d\u9664\u70e6\u607c\uff1f\u5047\u5982\u8bf4\uff0c\u5fc3\u5982\u6b62\u6c34\uff0c\u60c5\u6d77\u65e0\u6ce2\uff0c\u90a3\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u7a7a\u7167\u5927\u5e08\u9053\uff1a\u201c\u53e4\u5f80\u4eca\u6765\uff0c\u53c8\u6709\u513f\u4eba\u80fd\u591f\u592a\u4e0a\u5fd8\u60c5\uff1f\u4f55\u51b5\u2018\u60c5\u52ab\u2019\u4e0d\u8fc7\u662f\u6052\u5777\u6c99\u6570\u52ab\u4e2d \u7684\u4e00\u52ab\uff0c\u5982\u8d2a\u3001\u5982\u5506\u3001\u5982\u75f4\uff0c\u90fd\u662f\u2018\u5fc3\u9b54\u2019\uff0c\u2018\u5fc3\u9b54\u2019\u4e0d\u9664\uff0c\u7ec8\u987b\u5760\u52ab\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u96be\u5f97\u5927\u5e08\u6709\u6b64\u95f2\u60c5\uff0c\u7ed9\u6211\u4eec\u8bf4\u5927\u4e58\u4f5b\u6cd5\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4e00\u4e2a\u4eba\u603b\u662f\u65e0\u6cd5\u514d\u9664\u70e6\u607c\u7684\u4e86\uff1f\u201d \u3000\u3000\u7a7a\u7167\u5927\u5e08\u9053\uff1a\u201c\u8fd9\u53c8\u4e0d\u7136\uff0c\u6b32\u9664\u70e6\u607c\uff0c\u987b\u5f97\u4e00\u628a\u94c1\u626b\u5e1a\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u8fd9\u628a\u94c1\u626b\u5e1a\u54ea\u91cc\u53bb\u627e\uff1f\u201d \u3000\u3000\u7a7a\u7167\u5927\u5e08\u9053\uff1a\u201c\u7ecf\u4e91\uff1a\u82e5\u4eba\u6b32\u8bc6\u4f5b\u5883\u754c\uff0c\u5f53\u51c0\u5176\u610f\u5982\u865a\u7a7a\u3002\u543e\u7b49\u4f17\u751f\u4e4b\u5fc3\u5730\uff0c\u81ea\u65e0\u59cb \u6765\uff0c\u88ab\u70e6\u607c\u4e4b\u5c18\u57a2\u6240\u67d3\u6c61\uff0c\u987b\u5c06\u4e00\u628a\u94c1\u626b\u5e1a\uff0c\u626b\u9664\u81ea\u5fc3\u4e4b\u70e6\u607c\u3002\u626b\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u65b9\u540d\u81ea\u51c0\u5176 \u610f\u3002\u5f53\u77e5\u94c1\u4e66\u5e1a\u8005\u4f55\uff1f\u5373\u6b62\u89c2\u662f\u4e16\u3002\u201d\uff08\u6309\uff1a\u6b64\u6bb5\u7ecf\u6587\u5f15\u81ea\u5b9d\u9759\u6cd5\u5e08\u8bb2\u8ff0\u4e4b\u300a\u4fee\u4e60\u6b62\u89c2\u5750\u7985 \u6cd5\u8981\u300b\uff09 \u3000\u3000\u91d1\u9010\u6d41\u7684\u6bcd\u4eb2\u662f\u5415\u56db\u5a18\u7684\u8863\u94b5\u4f20\u4eba\uff0c\u7cbe\u7814\u4f5b\u6cd5\uff0c\u6545\u6b64\u91d1\u9010\u6d41\u4e5f\u8bfb\u8fc7\u4e00\u4e9b\u4f5b\u7ecf\uff0c\u77e5\u9053 \u201c\u6b62\u201d\u4e0e\u201c\u89c2\u201d\u4e43\u4f5b\u7ecf\u4e2d\u7684\u4e24\u4e2a\u540d\u8f9e\u3001\u4f46\u5374\u672a\u6089\u5176\u4e2d\u5965\u4e49\uff0c\u4e8e\u662f\u95ee\u9053\uff1a\u201c\u4f55\u8c13\u6b62\u89c2\uff1f\u201d \u3000\u3000\u7a7a\u7167\u5927\u5e08\u9053\uff1a\u201c\u6211\u4ece\u6839\u672c\u7ed9\u4f60\u8bf4\u8d77\u3002\u8bf8\u6076\u83ab\u4f5c\uff0c\u4f17\u5584\u5949\u884c\uff0c\u81ea\u51c0\u5176\u610f\uff0c\u662f\u8bf8\u4f5b\u6559\u3002\u5982\u8a00 \u8bf8\u6076\u83ab\u4f5c\uff0c\u5373\u662f\u2018\u6b62\u2019\u3002\u4f17\u5584\u5949\u884c\uff0c\u5373\u662f\u2018\u89c2\u2019\u3002\u53c8\u4e0d\u6740\u4e0d\u76d7\u7b49\u4e4b\u6b62\u5341\u5584\uff0c\u5373\u662f\u2018\u6b62\u2019\u3002\u4e0d \u4f46\u4e0d\u6740\uff0c\u800c\u4e14\u653e\u751f\u7b49\u4e4b\u884c\u5341\u5584\uff0c\u5373\u540d\u4e3a\u2018\u89c2\u2019\u3002\u662f\u6545\uff0c\u6b62\u4e43\u4f0f\u7ed3\u4e4b\u521d\u95e8\uff0c\u89c2\u662f\u65ad\u60d1\u4e4b\u6b63\u8981\u3002 \u6b62\u5219\u7231\u517b\u5fc3\u8bda\u4e4b\u5584\u8d44\uff0c\u89c2\u5219\u7b56\u53d1\u795e\u89e3\u4e4b\u5999\u672f\u3002\u6b62\u662f\u7985\u5b9a\u4e4b\u80dc\u56e0\uff0c\u89c2\u662f\u667a\u6167\u4e4b\u7531\u85c9\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u4f3c\u61c2\u975e\u61c2\uff0c\u832b\u7136\u7684\u70b9\u4e86\u70b9\u5934\u3002\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8bf7\u95ee\u5927\u5e08\uff0c\u53ef\u4e0d\u53ef\u4ee5\u8fd9\u6837\u8bf4\uff0c\u2018\u6b62\u2019 \u662f\u6d88\u6781\u7684\u2018\u9632\u6b62\u2019\uff0c\u2018\u89c2\u2019\u662f\u79ef\u6781\u7684\u5949\u884c\uff1f\u2018\u7ed3\u2019\u662f\u5fc3\u4e2d\u70e6\u607c\u6240\u6210\u4e4b\u2018\u7ed3\u2019\u4ee5\u2018\u6b62\u2019\u4e4b\u529f \u592b\uff0c\u4ec5\u80fd\u4f0f\u7ed3\u800c\u4e0d\u80fd\u65ad\uff0c\u72b9\u5982\u4ee5\u77f3\u538b\u8349\u3002\u6545\u7ecf\u4e91\uff1a\u6b62\u4e43\u4f0f\u7ed3\u4e4b\u521d\u95e8\u3002\u89c2\uff0c\u5373\u6b63\u53c8\u89c1\u6167\u7167\u3002\u6325 \u6167\u65a9\u5fc3\u9b54\uff0c\u5982\u4f3c\u5229\u5200\uff0c\u65a9\u8349\u9664\u6839\uff0c\u6c38\u4e0d\u518d\u751f\u3002\u6545\u7ecf\u4e91\uff1a\u89c2\u4e43\u65ad\u60d1\u8bc1\u771f\u4e4b\u6b63\u8981\u3002\u201d \u3000\u3000\u7a7a\u7167\u5927\u5e08\u5408\u8ba1\u8d5e\u9053\uff1a\u201c\u5584\u54c9\uff0c\u5584\u54c9\uff0c\u6210\u58eb\u5927\u6709\u6167\u6839\u3002\u592b\u81ea\u6027\u672c\u65e0\u70e6\u607c\uff0c\u4ea6\u65e0\u751f\u6b7b\u3002\u8ff7\u609f \u672c\u7a7a\uff0c\u4fee\u8bc1\u5982\u5e7b\u3002\u5982\u6469\u5c3c\u73e0\uff0c\u672c\u662f\u5706\u51c0\uff0c\u4f46\u65f7\u52ab\u6765\uff0c\u843d\u6c61\u6ce5\u4e2d\uff0c\u88ab\u5c18\u58eb\u67d3\u8986\u201d\u5c06\u672c\u6709\u6e05\u51c0\u5149 \u660e\uff0c\u4e0d\u853d\u800c\u853d\u3002\u4eca\u6b32\u663e\u73e0\u5149\uff0c\u975e\u52a0\u4ee5\u6469\u64e6\u9576\u6d17\u4e4b\u529f\u592b\u4e0d\u53ef\uff0c\u6545\u516d\u7956\u4e91\uff1a\u4f55\u671f\u81ea\u5fc3\uff0c\u672c\u81ea\u6e05 \u51c0\u3002\u4f55\u671f\u81ea\u5fc3\uff0c\u672c\u81ea\u5149\u660e\u3002\u5373\u6b64\u4e49\u4e5f\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8bf7\u95ee\u5927\u5e08\uff0c\u6240\u8c13\u2018\u4f17\u5584\u5949\u884c\u2019\uff0c\u521a\u624d\u5927\u5e08\u4e3e\u4f8b\u89e3\u91ca\uff0c\u5982\u4e0d\u4f46\u4e0d\u6740\u800c\u4e14\u653e\u751f \u5373\u662f\u884c\u5584\uff0c\u5373\u540d\u4e3a\u2018\u89c2\u2019\u3002\u4f46\u5929\u4e0b\u6709\u4e9b\u4eba\u662f\u4e0d\u80fd\u4e0d\u6740\u7684\uff0c\u6211\u8fd9\u4e00\u751f\u5c31\u6740\u4e86\u4e0d\u5c11\u4eba\uff0c\u50cf\u6211\u8fd9 \u6837\uff0c\u4f5b\u6cd5\u80fd\u4e0d\u80fd\u5bb9\uff1f\u201d \u3000\u3000\u7a7a\u7167\u5927\u5e08\u7b11\u9053\uff1a\u201c\u9664\u6076\u5373\u662f\u884c\u5584\uff0c\u6c5f\u5927\u4fa0\u6740\u7684\u4e43\u662f\u6076\u4eba\uff0c\u6b63\u5408\u6211\u4f5b\u65a9\u9b54\u9664\u5996\u4e4b\u610f\u3002\u201d\u6c5f \u6d77\u5929\u54c8\u54c8\u5927\u7b11\uff1a\u201c\u90a3\u6211\u5c31\u653e\u5fc3\u4e86\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5bf9\u4f5b\u7ecf\u65e0\u751a\u5174\u8da3\uff0c\u542c\u5f97\u6709\u70b9\u53d1\u95f7\u3002\u91d1\u9010\u6d41\u542c\u4e86\u7a7a\u7167\u5927\u5e08\u8bf4\u4e86\u5c16\u9995\u7ecf\uff0c\u5fc3\u4e2d\u5374\u662f\u5982 \u6709\u6240\u609f\uff1a\u201c\u7a7a\u7167\u5927\u5e08\u8bf4\u5f97\u4e0d\u9519\uff0c\u6211\u987b\u5f97\u4e00\u628a\u94c1\u626b\u5e1a\uff0c\u626b\u9664\u81ea\u5fc3\u7684\u70e6\u607c\u3002\u66f4\u8fdb\u4e00\u5c42\uff0c\u6211\u5fc3\u672c\u81ea \u6e05\u51c0\uff0c\u672c\u81ea\u5149\u660e\uff0c\u70e6\u607c\u53c8\u4f55\u7531\u800c\u751f\uff1f\u6211\u7684\u70e6\u607c\u662f\u56e0\u5bf9\u53f2\u7ea2\u82f1\u4e0d\u80fd\u5fd8\u60c5\u800c\u81f4\uff0c\u5982\u679c\u6211\u6839\u672c\u5c31\u4e0d \u628a\u53f2\u7ea2\u82f1\u5f53\u4f5c\u5973\u5b50\uff0c\u5fc3\u4e2d\u5e76\u65e0\u7537\u5973\u4e4b\u522b\uff0c\u90a3\u6837\u5979\u548c\u6211\u4e5f\u5c31\u5982\u540c\u674e\u5927\u54e5\u548c\u6211\u4e00\u6837\u4e86\u3002\u5927\u5bb6\u90fd\u662f \u5fd7\u540c\u9053\u5408\u7684\u670b\u53cb\uff0c\u65e0\u79c1\u5fc3\u6742\u5ff5\uff0c\u65e0\u7537\u5973\u4e4b\u89c1\uff0c\u7ea2\u82f1\u5c06\u6765\u548c\u8c01\u7ed3\u5a5a\uff0c\u542c\u5176\u81ea\u7136\uff0c\u90a3\u4e48\uff0c\u751a\u81f3\u5979 \u662f\u5ac1\u4e86\u6211\u4eec\u4e8c\u4eba\u4e4b\u5916\u7684\u53e6\u4e00\u4e2a\u4eba\uff0c\u6211\u4eec\u51e0\u4e2a\u4eba\u4e5f\u90fd\u8fd8\u662f\u53ef\u4ee5\u76f8\u5904\u5f97\u5feb\u5feb\u4e50\u4e50\u7684\u3002\u201d\u91d1\u9010\u6d41\u60f3 \u901a\u4e86\u8fd9\u4e00\u5c42\uff0c\u70e6\u607c\u5927\u5927\u51cf\u8f7b\uff0c\u5bf9\u4e8e\u7537\u5973\u4e4b\u60c5\uff0c\u6bd4\u8d77\u5389\u80dc\u7537\u5f53\u5e74\u6240\u80fd\u8fbe\u5230\u7684\u5883\u754c\uff0c\u90a3\u662f\u5927\u5927\u7684 \u8d85\u8fc7\u4e86\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\uff0c\u4e1c\u65b9\u5df2\u662f\u5929\u8272\u5927\u767d\u3002\u4e0a\u6d77\u5929\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u4ece\u897f\u661f\u6765\uff0c\u5728\u5c71\u4e2d\u66fe\u89c1\u8fc7\u7afa\u5c1a\u7236\u3002 \u4ed6\u7684\u4f24\u5df2\u5dee\u4e0d\u591a\u597d\u4e86\uff0c\u6b63\u5728\u8ba1\u5212\u4e0e\u5c0f\u91d1\u5ddd\u65b9\u9762\u7684\u4e49\u519b\u8054\u5408\u884c\u52a8\uff0c\u4ee5\u56fe\u593a\u56de\u897f\u661f\u3002\u5982\u4eca\u5c0f\u91d1\u5ddd \u65b9\u9762\uff0c\u6709\u4f60\u7684\u6155\u534e\u5e08\u4f84\u5728\u90a3\u91cc\u5e2e\u5fd9\u3002\u897f\u661f\u65b9\u9762\uff0c\u5219\u5c1a\u8d5a\u4eba\u529b\u4e0d\u8db3\u3002\u6211\u51c6\u5907\u5728\u4e09\u4e24\u5929\u4e4b\u540e\uff0c\u518d \u53bb\u897f\u661f\u5e2e\u7afa\u5c1a\u7236\u7684\u5fd9\uff0c\u5e08\u5f1f\uff0c\u4f60\u5728\u5317\u4eac\u8fd8\u6709\u6ca1\u6709\u672a\u4e86\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5728\u5317\u4eac\u6211\u662f\u65e0\u4e8b\u53ef\u4f5c\u4e86\uff0c\u4f46\u6211\u5374\u53ef\u80fd\u6709\u4e8b\u4e8e\u626c\u5dde\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u8981\u53bb\u626c\u5dde\uff0c\u626c\u5dde\u662f\u4e0d\u662f\u516d\u5408\u5e2e\u603b\u8235\u6240\u5728\u4e4b\u5730\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d \u9519\uff0c\u6211\u5c31\u662f\u51b2\u516d\u5408\u5e2e\u7684\u603b\u8235\u53bb\u7684\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u548c\u53f2\u767d\u90fd\u7ed3\u4e86\u7cb1\u5b50\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u662f\u548c\u53f2\u767d\u90fd\u7ed3\u4e86\u6881\u5b50\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u6b21\u6211\u53bb\u626c\u5dde\uff0c\u5374\u5e76\u975e\u662f\u627e\u4ed6\u51b3\u6597 \u7684\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u677e\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u53f2\u767d\u90fd\u7684\u672c\u9886\u867d\u7136\u4e0d\u662f\u767b\u5cf0\u9020\u6781\uff0c\u4f46\u9664\u4e86\u6b66\u6797\u51e0\u4e2a\u8001\u524d\u8f88\u4e4b \u5916\uff0c\u5728\u6c5f\u6e56\u4e0a\u4ed6\u4e5f\u7b97\u662f\u6570\u4e00\u6570\u4e8c\u7684\u4e86\uff01\u4ed6\u624b\u4e0b\u7684\u56db\u5927\u9999\u4e3b\uff0c\u6b66\u529f\u4ea6\u975e\u6cdb\u6cdb\u4e4b\u8f88\uff0c\u4f60\u82e5\u662f\u5355\u8eab \u4e00\u4eba\u8dd1\u53bb\u62db\u60f9\u53f2\u767d\u90fd\uff0c\u6211\u8fd8\u5f53\u771f\u653e\u5fc3\u4e0d\u4e0b\u5462\u3002\u4f60\u662f\u600e\u4e48\u548c\u4ed6\u7ed3\u4e0b\u6881\u5b50\u7684\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u5077\u4e86\u4ed6\u9001\u7ed9\u8428\u798f\u9f0e\u7684\u793c\u7269\uff0c\u5728\u8def\u4e0a\u548c\u5728\u8428\u5e9c\u53c8\u66fe\u5148\u540e\u548c\u4ed6\u4ea4\u8fc7\u4e24\u6b21\u624b\uff0c \u8fd8\u5e78\u6ca1\u6709\u5403\u4ed6\u7684\u4e8f\u3002\u201d\u91d1\u9010\u6d41\u4e0e\u53f2\u767d\u90fd\u7684\u7ed3\u6028\uff0c\u6700\u5927\u7684\u539f\u56e0\u5176\u5b9e\u8fd8\u662f\u7531\u4e8e\u53f2\u7ea2\u82f1\u800c\u8d77\uff0c\u91d1\u9010 \u6d41\u4e0d\u613f\u628a\u81ea\u5df1\u548c\u53f2\u7ea2\u82f1\u518d\u8fde\u5728\u4e00\u8d77\uff0c\u662f\u4ee5\u6ca1\u6709\u8be6\u7ec6\u544a\u8bc9\u5e08\u5144\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u4e5f\u771f\u662f\u592a\u987d\u76ae\u4e86\uff0c\u4f60\u8fd9\u4f6f\u4f5c\u5f04\u53f2\u767d\u90fd\uff0c\u4ed6\u5c82\u80fd\u4e0e\u4f60\u4e8e\u4f11\uff1f\u53ea\u6015 \u4f60\u4e0d\u6211\u4ed6\uff0c\u4ed6\u4e5f\u8981\u627e\u4f60\u7b97\u5e10\u7684\u3002\u4f60\u5230\u626c\u5dde\uff0c\u53ef\u6b63\u662f\u4ed6\u7684\u5730\u5934\u554a\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u6015\uff0c\u6211\u82e5\u662f\u6253\u4e0d\u8fc7\u4ed6\u4eec\uff0c\u6211\u4e0d\u4f1a\u8dd1\u5417\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4f60\u65e2\u7136\u4e0d\u662f\u8981\u53bb\u62db\u60f9\u53f2\u767d\u90fd\uff0c\u90a3\u4e48\u4f60\u5230\u626c\u5dde\u53c8\u662f\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53f2\u767d\u90fd\u7ea6\u4e86\u5389\u5927\u54e5\u5230\u4ed6\u7684\u603b\u8235\u76f8\u4f1a\uff0c\u4f01\u56fe\u672a\u660e\uff0c\u6211\u653e\u5fc3\u4e0d\u4e0b\uff0c\u662f\u4ee5\u4e5f\u60f3\u8ddf \u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u662f\u8981\u6697\u4e2d\u7ed9\u5389\u5357\u661f\u4f5c\u4fdd\u7f25\uff0c\u90a3\u4f60\u8fd8\u8bf4\u4e0d\u662f\u53bb\u62db\u60f9\u53f2\u767d\u90fd\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u51c6\u5907\u5148\u4e0d\u9732\u9762\uff0c\u89c1\u673a\u884c\u4e8b\u3002\u5018\u82e5\u53f2\u767d\u90fd\u6709\u6240\u4e0d\u5229\u4e8e\u5389\u5927\u54e5\uff0c\u8bf4\u4e0d\u5f97\u6211\u4e5f \u53ea\u597d\u51fa\u624b\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6c5f\u6e56\u4fd7\u8bed\u8bf4\uff0c\u7ea6\u65e0\u597d\u7ea6\uff0c\u4f1a\u65e0\u597d\u4f1a\u3002\u53f2\u767d\u90fd\u8fd9\u6837\u7684\u4eba\u8fd8\u80fd\u5b89\u7740\u4ec0\u4e48\u597d\u5fc3\u773c \u513f\uff1f\u4e0d\u8fc7\uff0c\u4e3a\u670b\u53cb\u4e24\u808b\u63d2\u5200\uff0c\u90a3\u4e5f\u662f\u4efd\u5c5e\u5e94\u4e3a\u4e4b\u4e8b\u3002\u4f55\u51b5\u4f60\u548c\u5389\u5357\u661f\u8fd8\u662f\u516b\u62dc\u4e4b\u4ea4\u5462\uff0c\u6211\u4e0d \u62e6\u963b\u4f60\u53bb\uff0c\u4f46\u4f60\u53ef\u8981\u8bb0\u4f4f\uff1a\u6212\u9a84\u6212\u8e81\uff0c\u51e1\u4e8b\u5c0f\u5fc3\u3002\u6253\u5f97\u8fc7\u5c31\u6253\uff0c\u6253\u4e0d\u8fc7\u5c31\u8dd1\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u591a\u8c22\u5e08\u5144\u6559\u5bfc\uff0c\u6211\u8d70\u4e86\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u5ffd\u9053\uff1a\u201c\u91d1\u5144\uff0c\u6211\u548c\u4f60\u4e00\u9053\u53bb\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u8981\u53bb\uff1f\u201d\u4ed6\u4e0e\u9648\u5149\u7167\u867d\u662f\u4e16\u4ea4\uff0c\u4eec\u6bd5\u7adf\u8fd8\u662f\u521d\u76f8\u8bc6\u7684\u670b \u53cb\uff0c\u8fd9\u6b21\u4ed6\u662f\u4e3a\u4e86\u5389\u5357\u661f\u4e0e\u53f2\u7ea2\u82f1\u7684\u4e8b\u60c5\u53bb\u7684\uff0c\u6545\u6b64\u4e0d\u5927\u613f\u610f\u9648\u5149\u7167\u53c2\u4e8e\u5176\u4e8b\u3002 \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u6211\u548c\u516d\u5408\u5e2e\u4e5f\u6709\u4e00\u70b9\u6881\u5b50\uff0c\u516d\u5408\u5e2e\u56db\u5927\u9999\u4e3b\u4e4b\u4e2d\u7684\u90a3\u4e2a\u51f6\u50e7\u5706\u6d77\uff0c\u66fe\u7ed9\u6211 \u523a\u4e86\u4e00\u5251\uff0c\u6211\u4e5f\u53d7\u4e86\u4ed6\u6697\u5668\u6240\u4f24\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u542c\u5f97\u9648\u53d4\u53d4\u8bf4\u8fc7\u3002\u4f60\u662f\u60f3\u627e\u5706\u6d77\u62a5\u4ec7\u4e48\uff1f\u5706\u6d77\u8fd9\u53ae\u5012\u4e5f\u4e0d\u96be\u5bf9\u4ed8\uff0c\u4e0d\u8fc7 \u4ed6\u5728\u516d\u5408\u5e2e\u603b\u8235\u4e4b\u4e2d\uff0c\u54b1\u4eec\u5374\u662f\u4e0d\u4fbf\u6253\u8349\u60ca\u86c7\uff0c\u9664\u975e\u662f\u8feb\u4e0d\u5f97\u5df2\u5728\u4ed6\u4eec\u7684\u603b\u8235\u4e4b\u4e2d\uff0c\u80fd\u591f\u907f \u514d\u52a8\u624b\uff0c\u603b\u662f\u907f\u514d\u7684\u597d\u3002\u9648\u5144\uff0c\u4f60\u4e0d\u5982\u53e6\u5f85\u9002\u5f53\u65f6\u673a\u62a5\u4ec7\uff0c\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u662f\u4e00\u5b9a\u8981\u5728\u8fd9\u6b21\u62a5\u4ec7\uff0c\u5389\u5927\u54e5\u548c\u4f60\u662f\u516b\u62dc\u4e4b\u4ea4\uff0c\u548c\u6211\u4e5f\u662f\u670b\u53cb\u3002\u6c5f \u5927\u4fa0\u521a\u624d\u8bf4\u5f97\u597d\uff0c\u4e3a\u670b\u53cb\u4e24\u808b\u63d2\u5200\uff0c\u8fd9\u662f\u6211\u8f88\u4efd\u6240\u5e94\u4e3a\u4e4b\u4e8b\u3002\u5018\u82e5\u5389\u5927\u54e5\u5728\u626c\u5dde\u906d\u9047\u4ec0\u4e48\u610f \u5916\uff0c\u6211\u867d\u7136\u672c\u9886\u4e0d\u6d4e\uff0c\u4e5f\u603b\u53ef\u4ee5\u51fa\u70b9\u529b\u554a\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53bb\u4e5f\u597d\u3002\u626c\u5dde\u8ddd\u79bb\u4f60\u7684\u5bb6\u4e61\u4e0d\u8fc7\u4e09\u4e24\u65e5\u8def\u7a0b\uff0c\u4f60\u53ef\u4ee5\u987a\u9053 \u56de\u5bb6\u7701\u4eb2\u3002\u4ee4\u5c0a\u662f\u6c5f\u5357\u7684\u6b66\u6797\u9886\u8896\uff0c\u4f60\u4eec\u8fd9\u6b21\u626c\u5dde\u4e4b\u884c\uff0c\u82e5\u662f\u4ee4\u5c0a\u4ece\u4e2d\u7167\u6599\uff0c\u5373\u6709\u7591\u96be\uff0c\u4e5f \u53ef\u8fce\u5203\u800c\u89e3\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u6211\u51c6\u5907\u5148\u5230\u626c\u5dde\uff0c\u56de\u6765\u65f6\u518d\u5230\u5bb6\u4e61\u4e00\u8f6c\u3002\u5728\u626c\u5dde\u82e5\u662f\u6709\u4e8b\uff0c\u518d\u8bf7\u4e10\u5e2e\u5411\u5bb6 \u7236\u901a\u4e2a\u6d88\u606f\uff0c\u4e5f\u5f88\u5bb9\u6613\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u5bf9\u670b\u53cb\u7684\u4e8b\u60c5\u90fd\u5f88\u70ed\u5fc3\u3002\u597d\uff0c\u8fd9\u6837\u5b89\u6392\uff0c\u66f4\u5468\u5230\u4e86\u3002\u4f60\u4eec\u53bb\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8d77\u521d\u4e0d\u4e0d\u613f\u610f\u9648\u5149\u7167\u540c\u53bb\uff0c\u540e\u4e00\u6765\u89c1\u4ed6\u5f88\u662f\u70ed\u5fc3\uff0c\u5e08\u5144\u4e5f\u8868\u8d5e\u540c\uff0c\u91d1\u9010\u6d41\u5f53\u7136\u4e0d \u4fbf\u53cd\u5bf9\u4e86\u3002 \u3000\u3000\u91d1\u3001\u9648\u4e8c\u4eba\u5e74\u7eaa\u76f8\u82e5\uff0c\u5e74\u8f7b\u4eba\u6700\u6613\u7ed3\u4ea4\u670b\u53cb\uff0c\u4f55\u51b5\u4ed6\u4eec\u53c8\u662f\u4e24\u4ee3\u4e16\u4ea4\uff0c\u56e0\u6b64\u5728\u7ed3\u8bc6\u4e4b \u540e\uff0c\u5f88\u5feb\u5c31\u4f3c\u8001\u670b\u53cb\u4e00\u822c\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u8def\u95ee\u884c\uff0c\u6de1\u5f97\u5f88\u662f\u6295\u673a\u3002\u8bf4\u8d77\u5389\u5357\u661f\u8d74\u53f2\u767d\u90fd\u4e4b\u7ea6\u7684\u4e8b\uff0c\u9648\u5149\u7167\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u53f2 \u767d\u90fd\u548c\u4f60\u4eec\u5df2\u662f\u5904\u5728\u654c\u5bf9\u5730\u4f4d\uff0c\u5389\u5927\u54e5\u613f\u610f\u8d74\u4ed6\u4e4b\u7ea6\uff0c\u4f9d\u6211\u770b\u6765\uff0c\u6050\u6015\u4e0d\u662f\u4e3a\u4e86\u53f2\u767d\u90fd\uff0c\u800c \u662f\u4e3a\u4e86\u4ed6\u7684\u59b9\u5b50\u5427\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u5389\u5927\u54e5\u90fd\u5df2\u5bf9\u4f60\u8bf4\u4e86\u4e48\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u548c\u6211\u8bf4\u4ec0\u4e48\uff0c\u4f46\u6211\u4ece\u4ed6\u68a6\u4e2d\u7684\u5403\u8bed\u5df2\u662f\u731c\u5230\u4e86\u51e0\u5206\u3002\u4f60\u8fd8\u4e0d\u77e5\u9053 \u5462\uff0c\u90a3\u65e5\u4ed6\u53d7\u4e86\u4f24\u660f\u8ff7\u4e0d\u9192\uff0c\u4e00\u4e2a\u665a\u4e0a\uff0c\u7ffb\u6765\u8986\u53bb\uff0c\u8bf4\u7684\u90fd\u662f\u4f60\u548c\u53f2\u59d1\u5a18\u7684\u540d\u5b57\u3002\u6211\u542c\u4e0d\u660e \u767d\uff0c\u8fd8\u4ee5\u4e3a\u4ed6\u8bf4\u7684\u662f\u4ec0\u4e48\u7ea2\u82f1\u4fd7\u6d41\uff0c\u95f9\u51fa\u4e86\u4e00\u4e2a\u5927\u5927\u7684\u7b11\u8bdd\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u4e5f\u4e0d\u7981\u54c8\u54c8\u5927\u7b11\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u539f\u6765\u5728\u5927\u54e5\u7684\u5fc3\u91cc\uff0c\u628a\u6211\u548c\u7ea2\u82f1\u662f \u653e\u5728\u540c\u7b49\u4f4d\u7f6e\u7684\u3002\u4ed6\u867d\u7136\u662f\u5728\u795e\u667a\u8ff7\u7cca\u4e4b\u9645\uff0c\u4e5f\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u6211\uff01\u201d\u5bf9\u5389\u5357\u661f\u7684\u91cd\u89c6\u53cb\u60c5\uff0c\u5fc3 \u4e2d\u5f88\u662f\u611f\u6fc0\u3002 \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u5389\u5927\u54e5\u5bf9\u8fd9\u4f4d\u53f2\u59d1\u5a18\u8d5e\u4e0d\u7edd\u53e3\uff0c\u6b23\u6155\u4e4b\u60c5\u51fa\u4e8e\u8f9e\u8272\u3002\u56e0\u6b64\u4ed6\u867d\u7136\u6ca1\u6709\u8bf4\uff0c \u6211\u4e5f\u731c\u60f3\u5f97\u5230\uff0c\u4ed6\u4eec\u5f7c\u6b64\u6709\u60c5\u3002\u5389\u5927\u54e5\u8fd9\u6b21\u4e4b\u53bb\u626c\u5dde\uff0c\u4e5f\u4e00\u5b9a\u662f\u4e3a\u4e86\u5979\u7684\u7f18\u6545\u4e86\uff0c\u4e0d\u77e5\u6211\u731c \u5f97\u5bf9\u4e0d\u5bf9\uff1f\u8fd9\u4f4d\u53f2\u59d1\u5a18\u4e5f\u5f53\u771f\u6709\u5389\u5927\u54e5\u8bf4\u5f97\u90a3\u4e48\u597d\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4f4d\u53f2\u59d1\u5a18\u7684\u786e\u662f\u5973\u4e2d\u82f1\u6770\uff0c\u4f60\u4e5f\u731c\u5f97\u5f88\u5bf9\uff0c\u5389\u5927\u54e5\u8fd9\u6b21\u6050\u6015\u5c31\u662f\u4e3a\u4e86\u5a5a \u4e8b\u53bb\u7684\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u5982\u6b64\u3002\u8fd9\u6211\u53ef\u6709\u70b9\u4e0d\u653e\u5fc3\u4e86\u3002\u53f2\u767d\u90fd\u73b0\u5728\u6b63\u60f3\u6295\u9760\u6e56\u5ef7\uff0c\u4ed6\u80af\u8ba9 \u59b9\u59b9\u5ac1\u4e0e\u671d\u5ef7\u4f5c\u5bf9\u7684\u4fa0\u4e49\u9053\uff1f\u5440\uff0c\u6050\u6015\u5389\u5927\u54e5\u8981\u4e0a\u4ed6\u7684\u5f53\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5389\u5927\u54e5\u4e5f\u66fe\u60f3\u5230\u4e86\u8fd9\u4e00\u5c42\u3002\u201d\u9648\u5149\u7167\u9053\uff1a\u201c\u90a3\u4e48\u4ed6\u4e3a\u4f55\u8fd8\u53bb\uff1f\u201d\u91d1\u9010\u6d41 \u9053\uff1a\u201c\u60c5\u4e4b\u6240\u949f\uff0c\u5373\u4f7f\u524d\u9762\u662f\u6709\u5200\u5c71\u706b\u6d77\uff0c\u4e5f\u662f\u8981\u53bb\u7684\u4e86\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u7a7a\u7167\u5927\u5e08\u8bf4\u5f97\u4e0d\u9519\uff0c\u53e4\u5f80\u4eca\u6765\u53c8\u6709\u51e0\u4eba\u80fd\u591f\u592a\u4e0a\u5fd8\u60c5\uff1f\u7a7a\u7167\u5927\u5e08\u662f \u5f97\u9053\u9ad8\u50e7\u5c1a\u4e14\u5982\u6b64\u8bf4\uff0c\u6211\u8f88\u51e1\u592b\u4fd7\u5b50\uff0c\u60f3\u8981\u52d8\u7834\u60c5\u5173\uff0c\u8c08\u4f55\u5bb9\u6613\uff1f\u8bf4\u8d77\u6765\u53ef\u4e5f\u771f\u602a\u4e0d\u5f97\u5389\u5927 \u54e5\u5462\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u6562\u60c5\u4ed6\u4e5f\u6b63\u662f\u4e3a\u60c5\u70e6\u607c\uff1f\u201d\u4e8e\u662f\u95ee\u9053\uff1a\u201c\u9648\u5927\u54e5\uff0c\u4f60\u53ef\u6709\u610f\u4e2d\u4eba\u4e48\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u4e5f\u66fe\u7ed3\u8bc6\u4e00\u4e2a\u5973\u5b50\uff0c\u6709\u8fc7\u4e00\u6bb5\u4e0d\u5bfb\u5e38\u7684\u4ea4\u60c5\uff0c\u4f46\u6b64\u4e8b\u65e9\u6210\u8fc7 \u53bb\uff0c\u5509\uff0c\u4e0d\u8c08\u4e5f\u7f62\u3002\u201d\u795e\u60c5\u9893\u4e27\uff0c\u601c\u7136\u541f\u9053\uff1a\u201c\u9ec4\u53f6\u65e0\u98ce\u81ea\u843d\uff0c\u79cb\u4e91\u4e0d\u96e8\u957f\u9634\uff0c\u5929\u82e5\u6709\u60c5\u5929 \u4ea6\u8001\uff0c\u9065\u9065\u5e7d\u6068\u96be\u7981\u3002\u60c6\u6005\u65e7\u6b22\u5982\u68a6\uff0c\u89c9\u6765\u65e0\u5904\u8ffd\u5bfb\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u4e2d\u4e00\u9635\u8f9b\u9178\uff0c\u52c9\u5f3a\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u771f\u5e94\u8be5\u53bb\u627e\u4e00\u628a\u94c1\u626b\u5e1a\u4e86\u3002\u201d\u9648\u5149\u7167\u82e6\u7b11\u9053\uff1a \u201c\u7a7a\u7167\u5927\u5e08\u867d\u7136\u7ed9\u54b1\u4eec\u8bf4\u4e86\u5927\u4e58\u4f5b\u6cd5\uff0c\u53ef\u60dc\u6211\u662f\u949d\u6839\uff0c\u96be\u6c42\u6167\u5251\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u65e7\u68a6\u5c18\u5c01 \u4f11\u518d\u542f\uff0c\u6b64\u5fc3\u5982\u51b0\u53ea\u4e1c\u6d41\u3002\u65e2\u6210\u8fc7\u53bb\uff0c\u90a3\u4e5f\u5c31\u7b97\u4e86\u5427\u3002\u70e6\u607c\u4e4b\u4e8b\u522b\u63d0\u4e86\uff0c\u54b1\u4eec\u8bf4\u4e9b\u6709\u8da3\u7684\u4e8b \u60c5\u3002\u201d\u9648\u5149\u7167\u7136\u53f9\u9053\uff1a\u201c\u4e5f\u53ea\u80fd\u4f5c\u5982\u6b64\u60f3\u4e86\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u610f\u5174\u9611\u73ca\uff0c\u91d1\u9010\u6d41\u4e5f\u662f\u522b\u6709\u5fc3\u4e8b\u3002\u91d1\u9010\u6d41\u6ca1\u6709\u518d\u95ee\u4e0b\u53bb\uff0c\u9648\u5149\u7167\u4e5f\u5c31\u4e0d\u63d0\u4e86\u3002\u4e24 \u4eba\u6539\u8f6c\u8bdd\u9898\uff0c\u8c08\u4e00\u4e9b\u6c5f\u6e56\u4e0a\u7684\u5947\u95fb\u5f02\u4e8b\uff0c\u628a\u5fc3\u4e8b\u6682\u4e14\u629b\u5f00\uff0c\u5012\u4e5f\u4e0d\u89c9\u5bc2\u5bde\u3002 \u3000\u3000\u4e24\u4eba\u517c\u7a0b\u8d76\u8def\uff0c\u81ea\u5317\u4eac\u5357\u4e0b\uff0c\u8fd9\u4e00\u65e5\u5230\u4e86\u6d4e\u5357\uff0c\u65e5\u5934\u8fd8\u672a\u843d\u5c71\uff0c\u672c\u6765\u8fd8\u53ef\u4ee5\u8d76\u4e00\u6bb5\u8def\u7a0b \u7684\uff0c\u91d1\u9010\u6d41\u8bf4\u9053\uff1a\u201c\u5230\u519c\u5bb6\u6c42\u5bbf\u4e0d\u5927\u65b9\u4fbf\uff0c\u4e0d\u5982\u4eca\u665a\u5c31\u4f4f\u5728\u57ce\u91cc\u5427\uff0c\u54b1\u4eec\u4e00\u8def\u4e0a\u6ca1\u6709\u53d1\u73b0\u5389 \u5927\u54e5\u7684\u8e2a\u8ff9\uff0c\u4e0d\u77e5\u4ed6\u662f\u5f80\u54ea\u6761\u8def\u8d70\uff1f\u4f46\u54b1\u4eec\u4e00\u76f4\u662f\u517c\u7a0b\u8d76\u8def\u7684\uff0c\u8bf4\u4e0d\u5b9a\u5df2\u7ecf\u8d76\u8fc7\u4e86\u4ed6\u7684\u524d\u5934 \u4e86\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u6d4e\u5357\u6709\u4e10\u5e2e\u7684\u5206\u8235\uff0c\u8235\u4e3b\u738b\u6cf0\uff0c\u4f60\u8ba4\u5f97\u5417\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6c5f\u5e08\u5144\u5ac1\u5973\u90a3 \u65e5\uff0c\u738b\u6cf0\u4e5f\u66fe\u6765\u559d\u559c\u9152\u3002\u6211\u548c\u4ed6\u867d\u6ca1\u4ea4\u8c08\uff0c\u89c1\u4e86\u9762\u662f\u4e00\u5b9a\u8ba4\u8bc6\u7684\u3002\u4f60\u7684\u610f\u601d\u662f\u60f3\u5230\u4e10\u5e2e\u5206\u8235 \u501f\u5bbf\u5417\uff1f\u201d\u9648\u5149\u7167\u7b11\u9053\uff1a\u201c\u6211\u6700\u6015\u5e94\u916c\uff0c\u4f4f\u5230\u4e10\u5e2e\u5206\u8235\uff0c\u4ed6\u4eec\u628a\u4f60\u6211\u5f53\u4f5c\u8d35\u5ba2\u62db\u5f85\uff0c\u90a3\u53ef\u5c31 \u53d7\u62d8\u675f\u4e86\u3002\u6211\u662f\u60f3\u548c\u4e10\u5e2e\u901a\u4e2a\u6d88\u606f\uff0c\u4e10\u5e2e\u5728\u626c\u5dde\u4e5f\u662f\u6ca1\u6709\u5206\u8235\u7684\uff0c\u4ed6\u4eec\u6709\u98de\u9e3d\u4f20\u4e66\uff0c\u6211\u60f3\u8bf7 \u4ed6\u4eec\u77e5\u4f1a\u626c\u5dde\u5206\u8235\uff0c\u4ee3\u4e3a\u7559\u610f\u5389\u5927\u54e5\u7684\u6d88\u606f\u3002\u8fd9\u6837\uff0c\u54b1\u4eec\u4e00\u51fa\u626c\u5dde\uff0c\u5c31\u53ef\u4ee5\u77e5\u9053\u5389\u5927\u54e5\u662f\u5426 \u5df2\u7ecf\u5230\u4e86\u3002\u987a\u4fbf\u54b1\u4eec\u4e5f\u53ef\u4ee5\u6253\u542c\u6253\u542c\u516d\u5408\u5e2e\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u4e8b\u4ea4\u7ed9\u6211\u529e\u3002\u54b1\u4eec\u5148\u627e\u5ba2\u5e97\u4f4f\u4e0b\uff0c\u6211\u5230\u4e10\u5e2e\u5206\u8235\u6253\u4e00\u4e2a\u8f6c\u5c31\u56de\u6765\uff0c \u665a\u4e0a\u54b1\u4eec\u8fd8\u53ef\u4ee5\u6e38\u6e56\u5462\u3002\u201d\u9648\u5149\u7167\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5174\u81f4\u8fd9\u4e48\u597d\uff01\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4e0a\u6b21\u6211\u6e38\u5927 \u660e\u6e56\uff0c\u9047\u5230\u4e00\u4ef6\u5927\u715e\u98ce\u666f\u4e4b\u4e8b\uff0c\u672a\u80fd\u5c3d\u5174\u3002\u96be\u5f97\u4eca\u665a\u6709\u4e2a\u65e7\u5730\u91cd\u6e38\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u54e6\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u6211\u5012\u8fd8\u6ca1\u6709\u542c\u4f60\u8bf4\u8fc7\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6d4e\u5357\u6709\u4e2a\u5bb0\u76f8\u66f9\u5bb6\uff0c\u4f60\u53ef\u77e5\u9053\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u53ef\u662f\u5b98\u5c45\u6587\u534e\u6bbf\u5927\u5b66\u58eb\u7684\u66f9\u632f\u955b\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u9648\u5149\u7167\u9053\uff1a\u201c\u8fd9\u4e2a\u66f9\u632f\u955b\u662f\u4e2a\u5927\u5978\u81e3\u3002\u4ed6\u548c\u6ee1\u4eba\u7a46\u5f70\u963f\u5206\u638c\u76f8 \u6743\uff0c\u4e13\u62cd\u9791\u5b50\u7687\u5e1d\u7684\u9a6c\u5c41\uff0c\u51fa\u4e3b\u610f\u6b3a\u538b\u6c49\u4eba\u3002\u4e0d\u8fc7\u66f9\u632f\u955b\u5f53\u671d\u4e3a\u76f8\uff0c\u548c\u4f60\u6e38\u5927\u660e\u6e56\u7684\u4e8b\u5374\u53c8 \u6709\u751a\u5173\u8054\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4ed6\u6709\u4e00\u4e2a\u513f\u5b50\uff0c\u5bb6\u5c45\u5728\u4e61\uff0c\u65e0\u6076\u4e0d\u4f5c\u3002\u6211\u90a3\u6b21\u6e38\u6e56\uff0c\u5c31\u662f\u78b0\u4e0a\u4e86\u4ed6\u7684\u513f \u5b50\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u95ee\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4e8b\u78b0\u4e0a\u7684\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u90a3\u65e5\u6709\u4e24\u4e2a\u8bf4\u9f13\u4e66\u7684\u7236\u5973\u5728\u6e56\u8fb9\u5356\u5531\uff0c\u8fd9\u4f4d\u2018\u66f9\u516c\u5b50\u2019\u770b\u4e0a\u90a3\u4e2a\u5973\u7684\uff0c\u7387 \u4eba\u6765\u67aa\u3002\u6070\u5de7\u7ed9\u6211\u548c\u4e00\u4f4d\u8fc7\u8def\u7684\u6c5f\u6e56\u670b\u53cb\u78b0\u89c1\uff0c\u8fd9\u4f4d\u670b\u53cb\u662f\u7ea2\u7f28\u4f1a\u7684\u9999\u4e3b\u5bab\u79c9\u85e9\u3002\u6211\u548c\u5bab\u79c9 \u85e9\u5e2e\u90a3\u4e24\u7236\u5973\uff0c\u628a\u66f9\u632f\u955b\u90a3\u4e2a\u5b9d\u8d1d\u513f\u5b50\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u6253\u4e86\u4e00\u987f\uff01 \u3000\u3000\u9648\u5149\u7167\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u662f\u5272\u9e21\u7528\u4e0a\u4e86\u725b\u5200\u4e86\u3002\u201d\u5bab\u79c9\u85e9\u662f\u6c5f\u6e56\u4e0a\u7684\u6210\u540d\u4eba\u7269\uff0c\u9648\u5149\u7167\u5f53\u7136 \u662f\u77e5\u9053\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u90a3\u6b21\u82e5\u4e0d\u662f\u6709\u4e2a\u5bab\u79c9\u85e9\u5e2e\u5fd9\uff0c\u6211\u4e00\u4e2a\u4eba\u53ea\u6015\u8fd8\u5bf9\u4ed8\u4e0d\u4e86\u4ed6\u4eec\u5462\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u8be7\u9053\uff1a\u201c\u96be\u9053\u90a3\u4f4d\u66f9\u5927\u5c11\u7237\u7684\u624b\u4e0b\u5c45\u7136\u8fd8\u6709\u4ec0\u4e48\u80fd\u4eba\u4e0d\u6210\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4ed6\u7684\u4e24\u4e2a\u4fdd\u7f25\u53ef\u662f\u5927\u6709\u6765\u5934\u7684\u4eba\u7269\u3002\u4e00\u4e2a\u662f\u5c11\u6797\u5bfa\u7684\u53db\u5f92\u5f6d\u5de8\u5cad\uff0c\u4e00\u4e2a\u662f \u4ee5\u2018\u56db\u7b14\u70b9\u516b\u8109\u2019\u8457\u540d\u6c5f\u6e56\u7684\u8fde\u5bb6\u5b50\u4f84\u8fde\u57ce\u864e\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u6447\u5934\u53f9\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\u3002\u8fd9\u4e24\u4e2a\u4eba\u7adf\u7136\u5982\u6b64\u4e0d\u77e5\u81ea\u7231\uff0c\u7518\u4f5c\u8c6a\u95e8\u7684\u9e70\u72ac\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u8bf4\u4e66\u7684\u7236\u5973\uff0c\u4e5f\u662f\u6709\u6765\u5386\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u4ed6\u4eec\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u662f\u4f55\u5efa\u96c4\u548c\u4ed6\u7684\u5973\u513f\u4f55\u5f69\u51e4\u3002\u4f60\u542c\u8fc7\u4ed6\u4eec\u7236\u5973\u7684\u540d\u5b57\u4e48\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u4f55\u5f69\u51e4\uff1f\u5440\uff01\u5979\u662f\u6211\u7684\u4e00\u4f4d\u670b\u53cb\u7684\u672a\u5a5a\u59bb\u5b50\u5440\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u559c\u9053\uff1a\u201c\u539f\u6765\u4f60\u548c\u674e\u6566\u4e5f\u662f\u670b\u53cb\u3002\u4f60\u53ef\u77e5\u9053\u4ed6\u7684\u6d88\u606f\u4e48\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u4e09\u4e2a\u6708\u524d\u6211\u89c1\u8fc7\u4ed6\uff0c\u4ed6\u5230\u897f\u661f\u6295\u5954\u4e49\u519b\u53bb\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u8fdb\u4e86\u6d4e\u5357\u57ce\uff0c\u91d1\u9010\u6d41\u627e\u5230\u4ed6\u4ee5\u524d\u4f4f\u8fc7\u7684\u90a3\u95f4\u5ba2\u5e97\uff0c\u521a\u5230\u95e8\u524d\uff0c\u53ea\u89c1\u90a3\u5e97\u4e3b\u4eba\u5df2\u7ecf\u51fa \u6765\u8fce\u5019\u3002\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u8bb0\u6027\u771f\u597d\uff0c\u8fd8\u8ba4\u5f97\u6211\u3002\u4f60\u597d\u50cf\u77e5\u9053\u6211\u4eca\u5929\u4f1a\u6765\u4f3c\u7684\uff0c\u8fd9\u662f\u600e\u4e48\u56de \u4e8b\uff1f\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u8d35\u5ba2\u91cd\u4e34\uff0c\u5c0f\u5e97\u4e0d\u80dc\u8363\u5ba0\u3002\u8fd9\u4f4d\u662f\u9648\u76f8\u516c\u5427\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u8be7\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u5df2\u7ecf\u6709\u4eba\u7ed9\u4f60\u4eec\u4e24\u4f4d\u5b9a\u597d\u623f\u95f4\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u4e3a\u5947\u602a\uff0c\u95ee\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\u7ed9\u6211\u4eec\u5b9a\u4e0b\u4e86\u623f\u95f4\uff1f\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u662f\u4e2a\u5c0f\u53ae\u3002\u4ed6\u662f\u5949\u4ed6\u4e3b\u4eba\u4e4b\u547d\u6765\u5b9a\u623f\u95f4\u7684\uff0c\u4ed6\u8bf4\u4ed6\u7684\u4e3b\u4eba\u548c\u4f60\u4eec\u4e24\u4f4d\u662f\u597d \u670b\u53cb\u3002\u6211\u8fd8\u4ee5\u4e3a\u4f60\u4eec\u65e9\u5df2\u77e5\u9053\u4e86\u5462\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u662f\u56e0\u4e3a\u8fd9\u95f4\u5ba2\u5e97\u5728\u6d4e\u5357\u6700\u4e3a\u6709\u540d\uff0c\u4ed6\u66fe\u7ecf\u4f4f\u8fc7\uff0c\u6545\u6b64\u6000\u7740\u5ff5\u65e7\u4e4b\u500d\uff0c\u518d\u6765\u6295\u5bbf\u3002 \u60f3\u4e0d\u5230\u6709\u4eba\u80fd\u6478\u5230\u4ed6\u7684\u5fc3\u7406\uff0c\u9884\u5148\u7ed9\u4ed6\u5b9a\u4e0b\u4e86\u623f\u95f4\uff0c\u4ee4\u4ed6\u60ca\u5f02\u4e0d\u5df2\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u96be\u9053\u662f\u66f9\u5bb6\u7684\u4eba\u5bdf\u77e5\u4e86\u6211\u7684\u884c\u8e2a\uff1f\u201d\u5f53\u4e0b\u795e\u8272\u4e0d\u9732\uff1a\u95ee\u9053\uff1a\u201c\u90a3\u5c0f \u53ae\u6709\u4ec0\u4e48\u8bf4\u8bdd\u7559\u4e0b\uff1f\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u4ed6\u7559\u4e0b\u4e00\u4e2a\u62dc\u5323\uff0c\u73b0\u5728\u623f\u4e2d\uff0c\u8bf7\u4f60\u4eec\u4e24\u4f4d\u5148\u8fdb\u53bb\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u548c\u9648\u5149\u7167\u8fdb\u4e86\u623f\u95f4\uff0c\u679c\u7136\u770b\u89c1\u684c\u5b50\u4e0a\u7aef\u7aef\u6b63\u6b63\u7684\u6446\u7740\u4e00\u4e2a\u62dc\u5323\uff0c\u91d1\u9010\u6d41\u5148\u4e0d\u6253\u5f00 \u6765\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u8fd9\u4f4d\u70ed\u5fc3\u7684\u670b\u53cb\u3002\u597d\uff0c\u8bf7\u4f60\u7ed9\u6211\u968f\u4fbf\u5f04\u4e24\u6837\u5c0f\u83dc\uff0c\u5f85\u6211\u5403\u8fc7\u4e86 \u996d\uff0c\u597d\u53bb\u56de\u62dc\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u9053\uff1a\u201c\u623f\u996d\u94b1\u8d35\u53cb\u65e9\u5df2\u4ed8\u4e86\u3002\u6211\u5df2\u7ed9\u4e24\u4f4d\u76f8\u516c\u51c6\u5907\u597d\u4e00\u684c\u9152\u5e2d\u3002\u4f60\u4eec\u62b9\u4e00\u628a\u9762\uff0c \u7a0d\u6b47\u4e00\u6b47\uff0c\u6211\u5c31\u53eb\u4eba\u7aef\u6765\u3002\u201d\u539f\u6765\u90a3\u4eba\u4ed8\u7ed9\u4ed6\u7684\u623f\u996d\u94b1\uff0c\u8db3\u591f\u6570\u65e5\u4e30\u76db\u7684\u9152\u9994\u4e4b\u7528\u3002 \u3000\u3000\u5e97\u4e2d\u4eba\u51fa\u53bb\u4e4b\u540e\uff0c\u9648\u5149\u7167\u63a9\u4e0a\u623f\u95e8\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5144\uff0c\u4f60\u77e5\u9053\u8fd9\u4eba\u662f\u8c01\u4e86\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u662f\u4e3a\u4e86\u514d\u5f97\u5e97\u4e3b\u4eba\u7591\u5fc3\uff0c\u88c5\u4f5c\u77e5\u9053\u7684\u3002\u597d\uff0c\u73b0\u5728\u5c31\u6253\u5f00\u8c1c\u5e95\u5427\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u4e14\u6162\uff0c\u6050\u9632\u8fd9\u62dc\u5323\u91cc\u6709\u53e4\u602a\uff0c\u54b1\u4eec\u8fd8\u662f\u5c0f\u5fc3\u4e3a\u597d\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6bd5\u7adf\u662f\u4f60\u6bd4\u6211\u5728\u884c\u3002\u597d\uff0c\u6211\u4e0d\u78b0\u5b83\u65b0\u662f\uff0c\u5148\u6253\u5f00\u4e86\u518d\u8bf4\u3002\u201d\u5f53\u4e0b\uff0c\u62d4\u5251 \u51fa\u9798\uff0c\u5728\u8ddd\u79bb\u5341\u6b65\u4e4b\u5185\uff0c\u98de\u51fa\u8fd9\u67c4\u957f\u5251\uff0c\u6070\u5230\u597d\u5904\u7684\u522e\u5f00\u62dc\u5323\u3002\u91cc\u9762\u5e76\u65e0\u6bd2\u7bad\u4e4b\u7c7b\u7684\u6697\u5668\u98de \u51fa\uff0c\u9648\u5149\u7167\u653e\u4e0b\u4e86\u5fc3\uff0c\u7b11\u9053\uff1a\u201c\u91d1\u5144\uff0c\u4f60\u8fd9\u624b\u98de\u5251\u5256\u5323\u7684\u529f\u592b\u771f\u662f\u5999\u5230\u6beb\u5dc5\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u62ff\u8d77\u62dc\u5323\u4e2d\u90a3\u5f20\u5927\u7ea2\u5e16\u5b50\uff0c\u53ea\u89c1\u4e0a\u9762\u53ea\u5199\u6709\u516b\u4e2a\u5927\u5b57\uff1a\u201c\u4eca\u665a\u9149\u65f6\uff0c\u6e56\u4e0a\u5019 \u6559\u3002\u201d\u4e66\u6cd5\u9887\u6709\u51e0\u5206\u79c0\u6c14\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u670b\u53cb\u5012\u662f\u96c5\u4eba\uff0c\u597d\u4f3c\u77e5\u9053\u6211\u4eca\u665a\u8981\u6e38\u6e56\u4f3c\u7684\uff0c\u62a2\u7740\u505a\u8fd9\u4e2a\u4e1c\u9053 \u4e3b\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u73b0\u51fa\u60f6\u60d1\u7684\u795e\u6c14\uff0c\u628a\u90a3\u5f20\u5e16\u5b50\u7aef\u8be6\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u4f46\u5374\u4e0d\u77e5\u4ed6\u4f55\u4ee5\u8981\u5982\u6b64 \u85cf\u5934\u9732\u5c3e\uff1f\u91d1\u5144\uff0c\u54b1\u4eec\u4eca\u665a\u662f\u53bb\u4e5f\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53bb\uff0c\u600e\u4e48\u4e0d\u53bb\uff1f\u4eba\u5bb6\u62db\u5f85\u4e86\u54b1\u4eec\u7684\u98df\u5bbf\uff0c\u54b1\u4eec\u4e5f\u8be5\u53bb\u89c1\u89c1\u4e3b\u4eba\u5440\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u4f60\u4e0d\u63d0\u9632\u66f9\u632f\u955b\u90a3\u4e2a\u5b9d\u8d1d\u513f\u5b50\u627e\u4f60\u7684\u9ebb\u70e6\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u4e0a\u6b21\u7ed9\u4ed6\u6253\u65ad\u4e86\u6e38\u5174\uff0c\u5018\u82e5\u662f\u4ed6\uff0c\u6211\u6b63\u597d\u627e\u4ed6\u7b97\u5e10\u3002\u4e0d\u8fc7\uff0c\u6211\u60f3\u90a3\u4f4d \u66f9\u5927\u5c11\u7237\u672a\u5fc5\u4f1a\u6709\u8fd9\u6837\u98ce\u96c5\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4eca\u665a\u4e09\u66f4\uff0c\u6574\u4e2a\u8c1c\u5e95\u4fbf\u4f1a\u63ed\u6653\uff0c\u73b0\u5728\u4e0d\u5fc5\u7740\u6025\u778e\u731c\u3002\u201d \u3000\u3000\u5e97\u4e3b\u4eba\u9001\u6765\u4e00\u684c\u9152\u5e2d\uff0c\u6709\u626c\u5dde\u8457\u540d\u7684\u201c\u53eb\u5316\u9e21\u201d\uff0c\u70e4\u9ca5\u9c7c\uff0c\u51b0\u7cd6\u71d5\u7a9d\u7b49\u7b49\u7cbe\u7f8e\u7684\u98df\u7269\u3002 \u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4f4d\u670b\u53cb\u5012\u662f\u5ba2\u6c14\u5f97\u7d27\uff0c\u628a\u54b1\u4eec\u5f53\u4f5c\u4e86\u4e0a\u5bbe\u62db\u5f85\u4e86\u3002\u5f85\u4f1a\u513f\u89c1\u4e86\u4ed6\uff0c\u53ef\u771f\u5f97\u597d \u597d\u591a\u8c22\u4ed6\u5462\u3002\u201d \u3000\u3000\u5403\u8fc7\u4e86\u665a\u996d\uff0c\u5df2\u662f\u9ec4\u660f\u65f6\u5206\u3002\u91d1\u9010\u6d41\u5f80\u4e10\u5e2e\u5206\u8235\u62dc\u4f1a\u738b\u6cf0\uff0c\u9648\u5149\u7167\u7559\u5728\u5e97\u4e2d\u770b\u5b88\u3002 \u3000\u3000\u738b\u6cf0\u77e5\u9053\u91d1\u9010\u6d41\u662f\u6c5f\u6d77\u5929\u7684\u5e08\u5f1f\uff0c\u5728\u6c5f\u5bb6\u559d\u559c\u9152\u90a3\u5929\uff0c\u91d1\u9010\u6d41\u662f\u548c\u4e10\u5e2e\u7684\u5e2e\u4e3b\u4ef2\u957f\u7edf\u540c \u5e2d\u7684\uff0c\u8d77\u521d\u8fd8\u662f\u91d1\u9010\u6d41\u5750\u9996\u5e2d\uff0c\u540e\u6765\u624d\u8ba9\u7ed9\u4e86\u4ef2\u957f\u7edf\uff0c\u5f53\u65f6\u738b\u6cf0\u8fd8\u6ca1\u6709\u8d44\u683c\u548c\u91d1\u9010\u6d41\u6500\u8c08\uff0c \u5982\u4eca\u89c1\u4ed6\u7a81\u5982\u5176\u6765\uff0c\u81ea\u662f\u559c\u51fa\u671b\u5916\uff0c\u5fd9\u628a\u91d1\u9010\u6d41\u8bf7\u8fdb\u5185\u5ba4\uff0c\u95ee\u4ed6\u6765\u610f\u3002 \u3000\u3000\u91d1\u9042\u6d41\u9053\uff1a\u201c\u4f60\u4eec\u4e10\u5e2e\u6d88\u606f\u7d20\u6765\u7075\u901a\uff0c\u6211\u662f\u7279\u5730\u6765\u62dc\u5019\u8001\u54e5\u5411\u4f60\u8001\u54e5\u6253\u542c\u6d88\u606f\u7684\u3002\u201d \u3000\u3000\u738b\u6cf0\u9053\uff1a\u201c\u91d1\u5c11\u4fa0\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u4e0d\u77e5\u4f60\u8981\u6253\u542c\u4ec0\u4e48\uff0c\u6211\u82e5\u6709\u6240\u77e5\u3002\u5b9a\u5f53\u5949\u544a\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u66f9\u5bb6\u90a3\u4f4d\u5927\u5c11\u7237\u73b0\u5728\u600e\u4e48\u6837\u4e86\uff1f\u4ed6\u90a3\u4e24\u4e2a\u4fdd\u9556\u53ef\u8fd8\u8ddf\u7740\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u738b\u6cf0\u65e9\u5df2\u77e5\u9053\u91d1\u9010\u6d41\u66fe\u5728\u5927\u660e\u6e56\u7554\u6253\u4e86\u66f9\u632f\u955b\u7684\u5b9d\u8d1d\u513f\u5b50\u4e4b\u4e8b\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u4f4d\u66f9\u5927\u5c11\u7237 \u7ed9\u4f60\u6253\u4e86\u4e00\u987f\uff0c\u597d\u51e0\u4e2a\u6708\u8db3\u4e0d\u51fa\u6237\uff0c\u884c\u4e3a\u5012\u662f\u6536\u655b\u8bb8\u591a\u4e86\u3002\u8fde\u57ce\u864e\u6298\u5728\u4f60\u7684\u624b\u91cc\uff0c\u81ea\u5b66\u65e0 \u989c\uff0c\u73b0\u5728\u5df2\u7ecf\u79bb\u5f00\u66f9\u5bb6\uff0c\u5f6d\u5de8\u5d02\u5219\u8fd8\u5728\u4ed6\u5bb6\uff0c\u4e0d\u8fc7\u4e5f\u5f88\u5c11\u89c1\u4ed6\u5728\u57ce\u91cc\u51fa\u73b0\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6d4e\u5357\u57ce\u91cc\uff0c\u6700\u8fd1\u53ef\u66fe\u53d1\u73b0\u6709\u4ec0\u4e48\u884c\u8e2a\u8be1\u79d8\u7684\u6c5f\u6e56\u5f02\u4eba\u4e48\u3002\u201d \u3000\u3000\u738b\u6cf0\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u5916\u8def\u7684\u6c5f\u6e56\u670b\u53cb\u5230\u6765\uff0c\u6211\u4eec\u5341\u4e5d\u90fd\u4f1a\u5f97\u77e5\u6d88\u606f\u7684\u3002\u5374\u6ca1\u542c\u8bf4\u6d4e \u5357\u57ce\u91cc\u6709\u751a\u5f02\u4eba\u51fa\u73b0\u3002\u91d1\u5c11\u4fa0\uff0c\u4f60\u6253\u542c\u8fd9\u4e2a\uff0c\u53ef\u662f\u6709\u751a\u98ce\u95fb\uff1f\u8fd8\u662f\u4eb2\u8eab\u9047\u4e0a\u4e86\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u90a3\u4eba\u7ea6\u6211\u4eec\u5728\u6e56\u4e0a\u76f8\u4f1a\uff0c\u60f3\u5fc5\u662f\u4e0d\u613f\u5916\u4eba\u77e5\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u5e08\u5144\u8981\u6211 \u968f\u5904\u7559\u610f\u98ce\u5c18\u5f02\u4eba\uff0c\u4ee5\u4fbf\u7ed3\u4ea4\u5fd7\u540c\u9053\u5408\u7684\u670b\u53cb\u3002\u6d4e\u5357\u662f\u5c71\u4e1c\u7701\u5e9c\uff0c\u5f80\u6765\u7684\u6c5f\u6e56\u670b\u53cb\u5b9a\u7136\u4e0d \u5c11\uff0c\u662f\u4ee5\u6211\u60f3\u6253\u542c\u660e\u767d\uff0c\u514d\u4e8e\u9519\u8fc7\u3002\u201d \u3000\u3000\u738b\u6cf0\u662f\u4e2a\u8001\u4e8e\u4e16\u6545\u7684\u4eba\uff0c\u5fc3\u77e5\u91d1\u9010\u6d41\u5fc5\u6709\u7f18\u6545\uff0c\u4f46\u91d1\u9010\u6d41\u65e2\u7136\u4e0d\u613f\u610f\u8bf4\u51fa\u6765\uff0c\u4ed6\u5f53\u7136\u4e5f \u4e0d\u4fbf\u518d\u95ee\u4e0b\u53bb\u3002\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u5e76\u65e0\u6709\u6765\u5934\u7684\u6c5f\u6e56\u670b\u53cb\u7ecf\u8fc7\u3002\u4e0d\u8fc7\u9ad8\u4eba\u5f02\u58eb\uff0c\u591a\u534a\u662f\u884c \u8e2a\u9690\u79d8\u7684\uff0c\u4e5f\u8bb8\u6765\u4e86\u6211\u4eec\u4e0d\u77e5\u9053\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u6211\u53eb\u5e2e\u4f17\u591a\u591a\u7559\u610f\u5c31\u662f\u3002\u91d1\u5c11\u4fa0\uff0c\u4f60\u6b64\u6b21\u662f\u8def\u8fc7 \u8fd8\u662f\u7279\u5230\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u662f\u524d\u5f80\u626c\u5dde\u8def\u7ecf\u8d35\u5730\u7684\uff0c\u6709\u4ef6\u4e8b\u60c5\u8fd8\u8981\u62dc\u6258\u4f60\u5462\u3002\u201d\u5f53\u4e0b\u628a\u5389\u5357\u661f\u8d74\u53f2 \u767d\u90fd\u4e4b\u7ea6\u7684\u4e8b\u544a\u8bc9\u738b\u6cf0\uff0c\u8bf7\u738b\u6cf0\u548c\u626c\u5dde\u5206\u4e00\u8235\u901a\u4e2a\u6d88\u606f\uff0c\u4ee3\u4e3a\u7559\u610f\u3002\u738b\u6cf0\u81ea\u662f\u4e00\u53e3\u5e94\u627f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u770b\u5929\u8272\u5df2\u9ed1\uff0c\u4fbf\u5373\u8d77\u7acb\u544a\u8f9e\u3002\u738b\u6cf0\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5c11\u4fa0\u5982\u4f55\u4fbf\u8d70\uff1f\u6211\u6b63 \u8981\u4e3a\u91d1\u5c11\u4fa0\u63a5\u98ce\uff0c\u5df2\u7ecf\u53eb\u4ed6\u4eec\u5b89\u6392\u4e0b\u9152\u5e2d\u4e86\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u6709\u70b9\u5c0f\u4e8b\u5728\u8eab\uff0c\u8235\u4e3b\u76db\u60c5\u5fc3 \u9886\uff0c\u6539\u65e5\u518d\u6765\u6253\u6270\u3002\u201d\u738b\u6cf0\u4e0d\u4fbf\u5f3a\u7559\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5c11\u4fa0\u4f4f\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u660e\u5929\u6211\u6765\u56de\u62dc\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8235\u4e3b\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u6211\u660e\u5929\u4e00\u65e9\u5c31\u8d70\u3002\u5f85\u5230\u626c\u5dde\u56de\u6765\uff0c\u6211\u518d\u6765\u6253\u6270\u3002\u201d\u738b\u6cf0\u6697 \u6697\u8d77\u7591\uff0c\u5fc3\u60f3\uff1a\u201c\u4ed6\u4e00\u6765\u5c31\u6253\u542c\u57ce\u4e2d\u6709\u751a\u5f02\u4eba\uff0c\u5982\u4eca\u8bbe\u5e2d\u4e0d\u6687\uff0c\u5306\u5306\u5c31\u8d70\uff0c\u770b\u6765\u5b9a\u662f\u4eca\u665a\u6709 \u4e8b\u7684\u4e86\u3002\u5374\u4e0d\u77e5\u662f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4e0d\u80af\u8ba9\u6211\u77e5\u9053\uff1f\u4e8e\u662f\u5728\u9001\u5ba2\u4e4b\u540e\uff0c\u53eb\u5e2e\u4e2d\u5f1f\u5b50\u6697\u4e2d\u6253\u63a2\u3002\u8fd9\u4e0d \u662f\u738b\u6cf0\u597d\u7ba1\u95f2\u4e8b\uff0c\u800c\u662f\u6015\u91d1\u9010\u6d41\u5728\u4ed6\u7684\u5730\u5934\u51fa\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4ed6\u5c06\u6765\u89c1\u4e86\u6c5f\u6d77\u5929\u4e0d\u597d\u4ea4\u4ee3\u3002 \u3000\u3000\u91d1\u9010\u6d41\u56de\u5230\u5ba2\u5e97\uff0c\u53ea\u89c1\u9648\u5149\u7167\u8fd8\u5728\u6367\u7740\u90a3\u5f20\u8bf7\u5e16\uff0c\u6765\u56de\u8e31\u7740\u65b9\u6b65\uff0c\u4f3c\u662f\u795e\u60c5\u604d\u60da\uff0c\u82e5\u6709 \u6240\u601d\u3002\u91d1\u9010\u6d41\u8fdb\u4e86\u623f\uff0c\u4ed6\u65b9\u624d\u77e5\u9053\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u8d39\u795e\u7422\u78e8\u4e86\uff0c\u54b1\u4eec\u8fd9\u5c31\u53bb\u63ed\u5f00\u8c1c\u5e95\u5427\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u5ffd\u9053\uff1a\u201c\u91d1\u5144\uff0c\u4f60\u53bb\u5427\u3002\u5c0f\u5f1f\u2026\u2026\u201c\u91d1\u9010\u6d41\u8be7\u9053\uff1a\u201c\u600e\u4e48\uff1f\u4e0d\u60f3\u53bb\uff1f\u201d\u9648\u5149\u7167\u8bb7 \u8bb7\u8bf4\u9053\uff1a\u4ed6\u8bf4\u662f\u5728\u6e56\u4e0a\u5019\u6559\uff0c\u5e76\u6ca1\u6709\u6307\u660e\u8981\u54b1\u4eec\u540c\u53bb\u3002\u53ea\u6015\u8fd9\u4eba\u662f\u4f60\u7684\u670b\u53cb\uff0c\u4e0d\u4e00\u5b9a\u60f3\u8981\u89c1 \u6211\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u8c1c\u5e95\u8fd8\u672a\u63ed\u6653\uff0c\u600e\u5374\u662f\u4f60\u7684\u670b\u53cb\u8fd8\u662f\u6211\u7684\u670b\u53cb\uff1f\u54b1\u4eec\u90fd\u53d7\u4e86\u4eba\u5bb6\u7684\u62db \u5f85\uff0c\u8fd8\u662f\u4e00\u540c\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u63a8\u5374\u4e0d\u4e86\uff0c\u53ea\u597d\u548c\u91d1\u9010\u6d41\u540c\u884c\u3002\u5927\u660e\u6e56\u5728\u57ce\u7684\u5357\u8fb9\uff0c\u5343\u4f5b\u5c71\u4e0b\uff0c\u5c71\u5149\u6c34\u8272\uff0c\u8d5b\u4f3c \u56fe\u753b\u3002\u82e5\u5728\u6691\u5929\uff0c\u665a\u4e0a\u6ee1\u6e58\u90fd\u662f\u5170\u6653\u753b\u822b\u3002\u73b0\u5728\u662f\u65e9\u6625\u4e8c\u6708\u7684\u65f6\u8282\uff0c\u6625\u5bd2\u6599\u5ced\uff0c\u665a\u4e0a\u5bd2\u98ce\u72b9 \u5728\uff0c\u5982\u662f\u6ca1\u6709\u6e38\u6e56\u7684\u5ba2\u4eba\u3002\u91d1\u3001\u9648\u4e8c\u4eba\u5230\u4e86\u6e56\u8fb9\uff0c\u96c7\u4e86\u4e00\u53ea\u5c0f\u8239\uff0c\u5411\u5bf9\u9762\u7684\u5343\u4f5b\u5c71\u811a\u5212\u4e48\u3002 \u8239\u5230\u4e2d\u6d41\uff0c\u653e\u773c\u4e00\u671b\uff0c\u6e56\u4e2d\u7a7a\u8361\u8361\u7684\uff0c\u53ea\u6709\u4ed6\u4eec\u8fd9\u53ea\u5c0f\u821f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4eba\u600e\u7684\u7ea6\u4e86\u54b1\u4eec\uff0c\u5374\u4e0d\u89c1\u4ed6\u6765\u8fce\u63a5\uff1f\u96be\u9053\u662f\u5f00\u73a9\u7b11\u7684\u4e0d\u6210\uff1f\u201d\u9648\u5149\u7167 \u9053\uff1a\u201c\u6050\u6015\u5f53\u771f\u662f\u5f00\u73a9\u7b11\u7684\u4e86\uff0c\u54b1\u4eec\u8fd8\u662f\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u65e2\u6765\u4e4b\uff0c\u5219\u5b89\u4e4b\u3002\u54b1\u4eec\u672c\u6765\u5c31\u662f\u8981\u6765\u6e38\u6e56\u7684\uff0c\u8fd9\u4f4d\u670b\u53cb\u4e0d\u6765\uff0c\u4e5f\u4e0d\u4f1a\u51cf \u4e86\u54b1\u4eec\u7684\u6e38\u5174\u3002\u201d\u62ac\u5934\u4e00\u671b\uff0c\u6708\u4eae\u5347\u8d77\u8fd8\u6ca1\u591a\u9ad8\uff0c\u91d1\u9010\u6d41\u63a5\u7740\u7b11\u9053\uff1a\u201c\u4ed6\u7ea6\u7684\u662f\u9149\u65f6\uff0c\u73b0\u5728 \u4e5f\u8fd8\u6ca1\u5230\u3002\u201d\u5fc3\u4e2d\u6709\u70b9\u5947\u602a\uff1a\u201c\u9648\u5927\u54e5\u597d\u50cf\u4e0d\u5927\u613f\u610f\u8d74\u4eca\u665a\u4e4b\u4f1a\u3002\u96be\u9053\u4ed6\u5df2\u77e5\u9053\u4e86\u8fd9\u4eba\u662f \u8c01\uff0c\u4e0d\u60f3\u89c1\u8fd9\u4e2a\u4eba\u4e48\u3002\u201d \u3000\u3000\u4e0a\u6b21\u91d1\u9010\u6d41\u662f\u5728\u65e5\u95f4\u6e38\u6e56\uff0c\u8fd9\u6b21\u6708\u591c\u91cd\u6e38\uff0c\u53c8\u662f\u4e00\u756a\u60c5\u666f\u3002\u53ea\u89c1\u6e56\u5e73\u5982\u955c\uff0c\u6708\u8272\u6726\u80e7\uff0c \u597d\u50cf\u4e00\u5c42\u8584\u96fe\u8f7b\u7ecd\u7b3c\u7f69\u8001\u6e56\u9762\u3002\u5343\u4f5b\u5c71\u7684\u68b5\u5b87\u50e7\u697c\uff0c\u5012\u5f71\u6e56\u5fc3\uff0c\u9690\u7ea6\u53ef\u89c1\u3002\u8f7b\u821f\u8fc7\u5904\uff0c\u8361\u8d77 \u53e0\u53e0\u6ce2\u7eb9\uff0c\u65f6\u4e0d\u65f6\u6709\u53d7\u60ca\u5413\u7684\u6e38\u9c7c\u8dc3\u51fa\u6c34\u9762\u3002\u91d1\u9010\u6d41\u6b63\u5728\u9a70\u76ee\u9a8b\u6000\uff0c\u5ffd\u542c\u5f97\u6a79\u58f0\u5636\u54d1\uff0c\u6709\u53ea \u753b\u8239\u5df2\u662f\u4ece\u82a6\u82b1\u6df1\u5904\u6447\u51fa\u6765\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6709\u8239\u6765\u4e86\uff0c\u5374\u4e0d\u77e5\u662f\u5426\u90a3\u4eba\uff1f\u201d\u4e8e\u662f\u7ad9\u51fa\u8239\u5934\uff0c\u5429\u5490\u821f\u5b50\u5411\u90a3\u53ea\u753b\u8239\u6447 \u53bb\u3002 \u3000\u3000\u8fd9\u665a\u6708\u8272\u5f88\u597d\uff0c\u91d1\u9010\u6d41\u62ac\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u90a3\u753b\u8239\u73e0\u5e18\u534a\u5377\uff0c\u5e18\u5185\u4e24\u4e2a\u5c11\u5973\u7684\u5f71\u5b50\u9690\u7ea6\u53ef \u89c1\u3002\u91d1\u9010\u6d41\u597d\u751f\u8be7\u5f02\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u96be\u9053\u4e0e\u6211\u4eec\u4f1a\u7ea6\u7684\u7adf\u7136\u662f\u5973\u5b50\u4e48\uff1f\u201d\u4ed4\u7ec6\u518d\u770b\uff0c\u8fd9\u4e24\u4e2a \u5973\u5b50\u4e00\u4e2a\u7ea2\u8863\u4e00\u4e2a\u7eff\u88f3\uff0c\u5934\u4e0a\u68b3\u7740\u540c\u6837\u7684\u53d1\u5f0f\u2014\u2014\u7ea2\u7ef3\u624e\u7740\u6761\u5c0f\u8fab\uff0c\u5927\u7ea6\u5341\u516d\u4e03\u5c81\u7684\u5e74\u7eaa\uff0c \u770b\u6765\u4f3c\u662f\u4e24\u4e2a\u5c0f\u9b13\u7684\u6a21\u6837\u3002\u91d1\u9010\u6d41\u4ece\u672a\u89c1\u8fc7\u5979\u4eec\uff0c\u5fc3\u4e2d\u66f4\u662f\u60ca\u8be7\u3002\u5bfb\u601d\uff1a\u201c\u6c5f\u6e56\u513f\u5973\u867d\u8bf4\u4e0d \u62d8\u4fd7\u793c\uff0c\u4f46\u7531\u5973\u65b9\u5148\u7ea6\u7537\u65b9\u7a76\u7adf\u662f\u6781\u4e3a\u5c11\u6709\u4e4b\u4e8b\u3002\u83ab\u975e\u53ea\u662f\u4e0d\u76f8\u5e72\u7684\u6e38\u6e56\u5973\u5ba2\uff1f\u201d\u4f46\u8f6c\u5ff5\u53c8 \u60f3\uff1a\u201c\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff01\u6625\u5bd2\u6599\u5ced\u7684\u665a\u4e0a\uff0c\u5bfb\u5e38\u4eba\u5bb6\u7684\u5973\u7737\uff0c\u54ea\u4f1a\u5192\u7740\u98ce\u5bd2\u6e38\u6e56\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u4e00\u4e2a\u4e2b\u9b1f\u5df2\u662f\u53ee\u53ee\u549a\u549a\u4ed6\u5f39\u8d77\u7434\u6765\uff0c\u53e6\u4e00\u4e2a\u4e2b\u9b1f\u8f7b\u542f\u6731\u5507\uff0c\u548c\u7740\u7434\u97f3\u6b4c \u9053\uff1a\u201c\u95ee\u8baf\u6e56\u8fb9\u6625\u8272\uff0c\u91cd\u6765\u53c8\u662f\u4e09\u5e74\u3002\u4e1c\u98ce\u5439\u6211\u8fc7\u6e56\u8239\uff0c\u6768\u67f3\u4e1d\u4e1d\u62c2\u9762\u3002 \u3000\u3000\u4e16\u8def\u5982\u4eca\u5df2\u60ef\uff0c\u6b64\u5fc3\u5230\u5904\u60a0\u7136\uff0c\u5bd2\u5149\u4ead\u4e0b\u6c34\u8fde\u5929\uff0c\u98de\u8d77\u6c99\u6b27\u4e00\u7247\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8d5e\u9053\uff1a\u201c\u5531\u5f97\u597d\u6b4c\uff0c\u5f39\u5f97\u597d\u7434\uff01\u201d\u5fc3\u60f3\uff1a\u201c\u53ef\u60dc\u6211\u6ca1\u6709\u5e26\u5927\u54e5\u9001\u7684\u90a3\u5f20\u9c7c\u5c3e\u7434\uff0c \u5426\u5219\u6211\u5012\u53ef\u548c\u5979\u4e00\u66f2\u3002\u201d \u3000\u3000\u80cc\u540e\u6709\u4e00\u58f0\u8f7b\u8f7b\u7684\u53f9\u606f\uff0c\u91d1\u9010\u6d41\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u9648\u5149\u7167\u5446\u5446\u5730\u671b\u7740\u524d\u9762\uff0c\u8868\u60c5\u5341\u5206\u53e4 \u602a\uff0c\u4f3c\u60ca\u4f3c\u559c\uff0c\u53c8\u4f3c\u5e26\u7740\u51e0\u5206\u60f6\u60d1\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8f7b\u8f7b\u62cd\u4e86\u62cd\u9648\u5149\u7167\u7684\u80a9\u5934\uff0c\u7b11\u9053\uff1a\u201c\u9648\u5927\u54e5\uff0c\u4f60\u597d\u50cf\u662f\u8ba4\u8bc6\u5979\u4eec\u7684\uff0c\u662f\u5417\uff1f\u5979\u4eec \u662f\u8c01\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u84e6\u7136\u4e00\u7701\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5979\u4eec\u662f\u971e\u59d1\u7684\u4e2b\u9b1f\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u971e\u59d1\u53c8\u662f\u8c01\uff1f\u201d\u9648 \u5149\u7167\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u6211\u90a3\u65e5\u548c\u4f60\u8bf4\u8fc7\u7684\u3001\u7684\u2026\u2026\u5979\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u7684\u610f\u4e2d\u4eba\u4e0e\u4f60\u7ea6\u4f1a\uff0c\u4f60\u5374\u8fd8\u4e0d\u60f3\u6765\u5462\u3002\u54c8\uff0c\u54c8\uff0c\u9648\u5144\uff0c\u4f60\u7792\u5f97 \u6211\u597d\u82e6\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u751a\u662f\u5c34\u5c2c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8d77\u521d\u4e5f\u4e0d\u77e5\u9053\u3002\u8bf7\u5e16\u4e0a\u7684\u5b57\u6709\u51e0\u5206\u4f3c\u5979\u7684\u7b14\u8ff9\uff0c\u4f46\u6211\u4e0d\u6562 \u65ad\u5b9a\u3002\u5979\u8bf4\u8fc7\u4e0d\u518d\u89c1\u6211\u7684\uff0c\u6211\u4eec\u5206\u624b\u6070\u6070\u5df2\u7ecf\u4e09\u5e74\u4e86\u3002\u201d\u8bf4\u8bdd\u541e\u541e\u5410\u5410\uff0c\u4f3c\u6709\u96be\u8a00\u4e4b\u9690\u3002 \u3000\u3000\u6b64\u65f6\u4e24\u53ea\u8239\u5df2\u7ecf\u6e10\u6e10\u63a5\u8fd1\uff0c\u753b\u8239\u4e2d\u900f\u51fa\u7089\u9999\u8885\u8885\uff0c\u968f\u98ce\u5439\u6765\u3002\u91d1\u9010\u6d41\u5438\u4e86\u4e00\u53e3\uff0c\u5c31\u77e5\u662f \u4e0a\u597d\u7684\u6a80\u9999\uff0c\u7b11\u9053\uff0c\u201c\u4f60\u8fd9\u4f4d\u971e\u59d1\u771f\u662f\u96c5\u4eba\uff0c\u4f60\u542c\u5f97\u5979\u7684\u5c0f\u4e2b\u9b1f\u521a\u624d\u5531\u5417\uff1f\u95ee\u8baf\u6e56\u8fb9\u6625\u8272\uff0c \u91cd\u6765\u53c8\u662f\u4e09\u5e74\u3002\u563f\uff0c\u563f\uff0c\u73b0\u5728\u662f\u8be5\u4f60\u4e0a\u53bb\u95ee\u8baf\u4e86\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u4e2b\u9b1f\u5df2\u7ecf\u7ad9\u51fa\u8239\u5934\uff0c\u4e0d\u5f85\u9648\u5149\u7167\u95ee\u8baf\uff0c\u5148\u81ea\u8bf4\u9053\uff1a\u201c\u9648\u516c\u5b50\uff0c\u4f60\u6765\u4e86\uff01\u8bf7\u548c\u8d35\u53cb \u8fc7\u8239\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u4e00\u9635\u8ff7\u832b\uff0c\u54ac\u4e86\u54ac\u820c\u5934\uff0c\u77e5\u9053\u4e0d\u662f\u505a\u68a6\uff0c\u8d76\u5fd9\u5b9a\u4e86\u5b9a\u795e\u3002\u4e00\u6396\u8863\u895f\uff0c\u8df3\u8fc7\u90a3\u53ea\u753b \u8239\u3002\u821f\u5b50\u6050\u6015\u91d1\u9010\u6d41\u4e5f\u8df3\u8fc7\u53bb\uff0c\u614c\u4e0d\u8fed\u7684\u4e00\u628a\u5c06\u4ed6\u62c9\u7740\uff0c\u53eb\u9053\uff1a\u201c\u76f8\u516c\uff0c\u8fd9\u53ea\u8239\u4f60\u4eec\u8fd8\u8981\u4e0d \u8981\uff0c\u53ef\u5f97\u8981\u6709\u4e2a\u4ea4\u4ee3\u5440\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u6015\u6211\u4eec\u8d70\u4e86\u4f60\u7684\u8239\u94b1\u5417\uff1f\u4f60\u653e\u624b\uff0c\u6211\u8fd9\u5c31\u7ed9\u4f60\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u7ea2\u8863 \u4e2b\u9b1f\u628a\u624b\u4e00\u626c\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u952d\u94f6\u5b50\u5df2\u662f\u629b\u4e86\u8fc7\u6765\uff0c\u843d\u5728\u8239\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6491\u56de\u53bb\u5427\uff0c \u4e0d\u7528\u4f60\u6765\u63a5\u4e86\u3002\u8fd9\u952d\u94f6\u5b50\u591f\u5417\uff1f\u201d\u9648\u5149\u7167\u8fd9\u624d\u7701\u8d77\u6765\u672a\u4ed8\u8239\u94b1\uff0c\u4e0d\u7531\u5f97\u8138\u4e0a\u53d1\u70e7\u3002\u821f\u5b50\u7709\u5f00 \u773c\u7b11\uff0c\u53e0\u58f0\u8bf4\u9053\uff1a\u201c\u591f\u4e86\uff01\u591f\u4e86\uff01\u201d\u4e00\u653e\u624b\uff0c\u91d1\u9010\u6d41\u4e5f\u5c31\u8df3\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4e00\u5ba2\u4e0d\u70e6\u4e8c\u4e3b\uff0c\u591a\u8499\u4f60\u5bb6\u5c0f\u59d0\u62db\u5f85\uff0c\u6211\u6cbe\u4e86\u9648\u5927\u54e5\u7684\u5149\uff0c\u53ea\u597d\u539a\u7740\u8138\u76ae \u767d\u5403\u767d\u559d\u767d\u4f4f\u767d\u73a9\u4e86\u3002\u563f\uff0c\u563f\uff0c\u6211\u4e0d\u53e6\u5916\u591a\u8c22\u5566\uff01\u201d\u53e3\u91cc\u8bf4\u7b11\uff0c\u5fc3\u91cc\u4e5f\u5728\u6697\u7b11\u9648\u5149\u7167\u90a3\u526f\u5931 \u9b42\u843d\u9b44\u7684\u6a21\u6837\u3002 \u3000\u3000\u8fd9\u53ea\u753b\u8239\u6bd4\u4ed6\u4eec\u5750\u7684\u90a3\u53ea\u5c0f\u8239\u5927\u4e00\u500d\u6709\u591a\uff0c\u90a3\u4e24\u4e2a\u5c0f\u4e2b\u9b1f\u63ed\u5f00\u73e0\u5e18\uff0c\u62db\u547c\u4ed6\u4eec\u5728\u524d\u8231\u5750 \u4e0b\uff0c\u9648\u5149\u7167\u8fd9\u624d\u53d1\u73b0\u6709\u4e24\u6247\u5c4f\u98ce\u9694\u4f4f\u540e\u8231\uff0c\u5728\u8239\u4e2d\u95f4\u5f00\u4e00\u95f4\u623f\u95f4\u3002\u9648\u5149\u7167\u77e5\u9053\u4ed6\u90a3\u65e5\u60f3\u591c\u60f3 \u7684\u610f\u4e2d\u4eba\u5c31\u5728\u8fd9\u8231\u623f\u91cc\u9762\uff0c\u4ed6\u60f3\u8981\u53d1\u95ee\uff0c\u53ef\u662f\u820c\u5934\u5c31\u4f3c\u50f5\u4e86\u4f3c\u7684\uff0c\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u4e2b\u9b1f\u7aef\u51fa\u4e86\u4e24\u676f\u70ed\u8336\uff0c\u7a7f\u7ea2\u8863\u88f3\u90a3\u4e2a\u4e2b\u9b1f\u628a\u8336\u676f\u653e\u5728\u91d1\u9010\u6d41\u9762\u524d\u7684\u5c0f\u51e0\u4e0a\uff0c\u8bf4\uff1a \u201c\u91d1\u76f8\u516c\uff0c\u8bf7\u7528\u8336\u3002\u96be\u5f97\u4f60\u548c\u9648\u516c\u5b50\u540c\u6765\uff0c\u6211\u4eec\u662f\u95fb\u540d\u5df2\u4e45\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u82e5\u77e5\u9053\u6211\u53ea\u662f\u4f5c\u966a\u5ba2\u7684\u8eab\u4efd\uff0c\u6211\u5c31\u4e0d\u8be5\u6765\u4e86\u3002\u4f46\u4f60\u4eec\u5374\u600e\u77e5\u9053\u6211\u7684\u59d3 \u540d\uff1f\u201d\u90a3\u4e2b\u9b1f\u7b11\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u540d\u6ee1\u6c5f\u6e56\uff0c\u6211\u4eec\u867d\u7136\u591f\u4e0d\u4e0a\u8d44\u683c\u5728\u6c5f\u6e56\u884c\u8d70\uff0c\u4e5f\u65e9\u5df2\u542c\u5f97\u5c0f\u59d0 \u8bf4\u8fc7\u4f60\u7684\u5927\u540d\u4e86\u3002\u91d1\u5927\u4fa0\uff0c\u4f60\u522b\u8bef\u4f1a\uff0c\u6211\u4eec\u5c0f\u59d0\u662f\u4e13\u8bda\u8bf7\u4f60\u4eec\u4e24\u4f4d\u7684\uff0c\u5e76\u6ca1\u4ec0\u4e48\u4e3b\u5ba2\u966a\u5ba2\u4e4b \u5206\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u559d\u4e86\u8336\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u4e2a\u4f1a\u8bf4\u8bdd\u7684\u5c0f\u4e2b\u5934\uff0c\u6211\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u4eba\u5bb6\u53eb\u6211\u4f5c\u2018\u5927 \u4fa0\u2019\u5462\u3002\u563f\uff0c\u563f\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u54ea\u662f\u4ec0\u4e48\u5927\u4fa0\uff0c\u6211\u53ea\u662f\u4e2a\u5c0f\u5077\u3002\u201d\u90a3\u7ea2\u8863\u4e2b\u9b1f\u9053\uff1a\u201c\u91d1\u5927\u4fa0 \u8bf4\u7b11\u4e86\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u9648\u5927\u54e5\u4e0d\u597d\u610f\u601d\u8bf4\uff0c\u6211\u53ef\u8981\u8bf4\u4e86\u3002\u4f60\u5bb6\u7684\u5c0f\u59d0\u8bf7\u6211\u4eec\u6765\uff0c\u73b0\u5728\u6211\u4eec \u6765\u4e86\uff0c\u8336\u4e5f\u559d\u8fc7\u4e86\uff0c\u53ef\u4ee5\u62dc\u89c1\u4e3b\u4eba\u4e86\u5427\uff1f\u201d \u3000\u3000\u5c4f\u98ce\u540e\u9762\u4f20\u6765\u4e24\u58f0\u54b3\u55fd\uff0c\u6b64\u65f6\u9648\u5149\u7167\u4e5f\u5df2\u5728\u7eff\u8863\u4e2b\u9b1f\u7684\u624b\u91cc\u63a5\u8fc7\u8336\u676f\uff0c\u559d\u8fc7\u4e86\u8336\uff0c\u5fcd\u4e0d \u4f4f\u8ddf\u7740\u95ee\u9053\uff1a\u201c\u5c0f\u59d0\u53ef\u662f\u6709\u70b9\u4e0d\u8212\u670d\u4e48\u3002\u201d \u3000\u3000\u7eff\u8863\u4e2b\u9b1f\u9053\uff1a\u201c\u5c0f\u59d0\u662f\u53d7\u4e86\u4e00\u70b9\u98ce\u5bd2\uff0c\u521a\u521a\u7761\u4e86\u4e00\u89c9\u3002\u55ef\uff0c\u73b0\u5728\u5df2\u7ecf\u8d77\u5e8a\u4e86\u3002\u4f60\u7b49\u4e00 \u7b49\uff0c\u6211\u8fd9\u5c31\u53bb\u8bf7\u5c0f\u59d0\u51fa\u6765\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u5ffd\u5730\u89c9\u5f97\u8179\u75db\u5982\u7ede\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u971e\u59d1\uff0c\u4f60\u8981\u6211\u7684\u547d\u4e0d\u6253\u7d27\uff0c\u4f60\u600e\u4e48\u53ef\u4ee5 \u5bb3\u6211\u670b\u53cb\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u5e7d\u6e05\u5bc6\u7ea6\u671f\u76f8\u4f1a\uff0c\u4e0d\u6599\u751c\u8a00\u6bd2\u4f3c\u5200\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u5c81\u6708\u6d88\u78e8\u55df\u767d\u53d1\u3000\u5fc3\u601d\u591a\u5c11\u4e3a\u91d1\u9497 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u5c81\u6708\u6d88\u78e8\u55df\u767d\u53d1\u3000\u5fc3\u601d\u591a\u5c11\u4e3a\u91d1\u9497 \u3000\u3000\u91d1\u9010\u6d41\u201c\u54ce\u54df\u201d\u4e00\u58f0\u53eb\u9053\uff1a\u201c\u597d\u3001\u597d\u5389\u5bb3\u7684\u6bd2\u836f\u3002\u201d\u8eab\u8eaf\u6643\u4e86\u4e24\u6643\uff0c\u5c31\u4f3c\u4e00\u6839\u6728\u5934\u4f3c\u5730 \u5012\u4e0b\u53bb\u4e86\uff01 \u3000\u3000\u9648\u5149\u7167\u53c8\u60ca\u53c8\u6012\uff0c\u53eb\u9053\uff1a\u201c\u971e\u59d1\u5a18\uff0c\u4f60\u51fa\u6765\uff0c\u6211\u6b7b\u4e5f\u8981\u6b7b\u5f97\u660e\u767d\uff01\u201d\u6b64\u65f6\u9648\u5149\u7167\u4ea6\u5df2\u89c9 \u5f97\u5934\u660f\u76ee\u7729\uff0c\u4ed6\u5f3a\u81ea\u4e00\u632f\u7cbe\u795e\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u63a8\u5f00\u90a3\u4e24\u6247\u5c4f\u98ce\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u9634\u607b\u607b\u5730\u51b7\u7b11\uff0c\u5c4f\u98ce\u540e\u9762\u9661\u7136\u8df3\u51fa\u4e00\u4e2a\u4eba\u6765\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u9648\u5149\u7167\u51e0\u4e4e\u60ca\u5f97\u5446 \u4e86\uff0c\u8df3\u51fa\u6765\u7684\u8fd9\u4eba\u4e0d\u662f\u4ed6\u7684\u971e\u59d1\uff0c\u7adf\u662f\u4e2a\u9e21\u76ae\u9e64\u53d1\u7684\u8001\u5987\u3002 \u3000\u3000\u9648\u5149\u7167\u5446\u4e86\u4e00\u5446\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5976\u5a18\uff0c\u662f\u4f60\uff01\u201d\u90a3\u8001\u5987\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u5976\u5a18\uff1f \u563f\uff0c\u4f60\u8fd9\u81ed\u5c0f\u5b50\u5c45\u7136\u8fd8\u4e0d\u6b7b\u5fc3\uff0c\u765e\u86e4\u87c6\u60f3\u5403\u5929\u9e45\u8089\u4e48\uff1f\u201d\u9648\u5149\u7167\u53eb\u9053\uff1a\u201c\u6211\u77e5\u9053\u6211\u914d\u4e0d\u4e0a\u4f60 \u5bb6\u5c0f\u59d0\uff0c\u4f46\u8fd9\u6b21\u662f\u971e\u59d1\u53eb\u6211\u6765\u7684\uff0c\u6211\u4e00\u5b9a\u8981\u4eb2\u81ea\u95ee\u5979\uff01\u201d \u3000\u3000\u90a3\u8001\u5987\u4eba\u770b\u4e86\u91d1\u9010\u6d41\u4e00\u773c\uff0c\u89c1\u91d1\u9010\u6d41\u8eba\u5728\u8239\u4e0a\uff0c\u4e00\u52a8\u4e5f\u4e0d\u80fd\u52a8\uff0c\u4e0d\u7531\u559c\u51fa\u671b\u5916\uff0c\u5fc3\u91cc\u60f3 \u9053\uff1a\u201c\u542c\u8bf4\u53f2\u767d\u90fd\u548c\u6587\u9053\u5e84\u90fd\u66fe\u8d25\u5728\u4ed6\u7684\u624b\u91cc\uff0c\u6211\u53ea\u9053\u8fd9\u5c0f\u5b50\u5341\u5206\u4e86\u5f97\uff0c\u5374\u539f\u6765\u662f\u4e2a\u94f6\u6837\u8721 \u67aa\u5934\uff0c\u8fde\u9648\u5149\u7167\u90fd\u6bd4\u4e0d\u4e0a\u3002\u201d \u3000\u3000\u8fd9\u8001\u5987\u4eba\u4ee5\u4e3a\u91d1\u9010\u6d41\u5df2\u7ecf\u4e2d\u6bd2\u8eab\u4ea1\uff0c\u5f53\u4e0b\u66f4\u65e0\u5fcc\u60ee\uff0c\u4f38\u51fa\u9e1f\u722a\u822c\u7684\u5341\u53ea\u6307\u5934\uff0c\u4e00\u6b65\u4e00\u6b65 \u7684\u5411\u9648\u5149\u7167\u903c\u8fd1\uff0c\u201c\u563f\uff0c\u563f\uff0c\u563f\u201d\u5730\u51b7\u7b11\u9053\uff1a\u201c\u662f\u6211\u53eb\u4f60\u6765\u7684\uff01\u6211\u4e0d\u5047\u5192\u5c0f\u59d0\u9a97\u4f60\uff0c\u4f60\u8fd9\u5c0f \u5b50\u600e\u4f1a\u4e0a\u6211\u7684\u5f53\uff1f\u73b0\u5728\u4f60\u8be5\u660e\u767d\u4e86\u5427\uff0c\u662f\u6211\u6765\u4ee3\u66ff\u5c0f\u59d0\u6253\u53d1\u4f60\uff0c\u514d\u5f97\u5c0f\u59d0\u53d7\u4f60\u7ea0\u7f20\uff01\u201d \u3000\u3000\u9648\u5149\u7167\u60f3\u8981\u62b5\u6297\uff0c\u624b\u811a\u5df2\u662f\u4e0d\u542c\u4f7f\u5524\uff0c\u773c\u89c1\u8fd9\u8001\u5987\u4eba\u7684\u6307\u722a\u582a\u582a\u5c31\u8981\u6293\u5230\u4ed6\u7684\u9876\u95e8\uff0c\u91d1 \u9010\u6d41\u5ffd\u5730\u4e00\u8dc3\u800c\u8d77\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u6bd2\u836f\u867d\u7136\u5389\u5bb3\uff0c\u8981\u60f3\u5bb3\u6211\uff0c\u5374\u8fd8\u4e0d\u80fd\uff01\u539f\u7269\u5949\u8fd8\uff01\u201d \u4e2d\u6307\u4e00\u7fd8\uff0c\u4e00\u6761\u6c34\u7ebf\u4ece\u4ed6\u6307\u5c16\u5c04\u51fa\u3002\u539f\u6765\u91d1\u9010\u6d41\u5047\u88c5\u6655\u5012\uff0c\u6697\u5730\u91cc\u5982\u5728\u9ed8\u8fd0\u7384\u529f\uff0c\u628a\u559d\u4e0b\u53bb \u7684\u6bd2\u8336\u4ece\u6307\u7aef\u903c\u51fa\u6765\uff0c\u55b7\u51fa\u6765\u7684\u6c34\u7ebf\u8fd8\u662f\u70ed\u817e\u817e\u7684\u3002 \u3000\u3000\u8001\u5a46\u5a46\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5979\u8bc6\u5f97\u6bd2\u8336\u7684\u5389\u5bb3\uff0c\u8eab\u4e0a\u867d\u6709\u89e3\u836f\u4e5f\u4e0d\u80fd\u8ba9\u5b83\u5c04\u7740\u773c\u775b\uff0c\u767e\u5fd9 \u4e2d\u8fde\u5fd9\u4f7f\u4e2a\u201c\u94c1\u677f\u6865\u201d\u7684\u8eab\u6cd5\uff0c\u8170\u5411\u540e\u5f2f\uff0c\u53cc\u624b\u6309\u7740\u8239\u677f\uff0c\u8eab\u5f62\u4f0a\u4f3c\u4e00\u5ea7\u677f\u6865\uff0c\u6c34\u7ebf\u4ece\u5979\u9762 \u95e8\u4e0a\u65b9\u5c04\u8fc7\u3002\u8fd9\u4e48\u4e00\u6765\uff0c\u5979\u5f53\u7136\u4e5f\u662f\u65e0\u6687\u518d\u6293\u9648\u5149\u7167\u7684\u4e86\u3002\u91d1\u9010\u6d41\u4e00\u8dc3\u800c\u4e0a\uff0c\u5148\u628a\u9648\u5149\u7167\u62c9 \u8fc7\u4e00\u8fb9\uff0c\u585e\u7ed9\u4ed6\u4e00\u9897\u836f\u4e38\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u78a7\u7075\u4e39\uff0c\u5feb\u5feb\u670d\u4e0b\u3002\u201d \u3000\u3000\u8fd9\u8001\u5a46\u5a46\u4e5f\u59d4\u5b9e\u4e86\u5f97\uff0c\u91d1\u9010\u6d41\u53ea\u662f\u6162\u4e86\u4e00\u6162\uff0c\u5979\u5df2\u4e00\u4e2a\u7b4b\u6597\u7ffb\u8f6c\u6765\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\u80c6 \u6562\u620f\u800d\u8001\u5a18\uff01\u201d\u5341\u6307\u9f50\u4f38\uff0c\u53d1\u51fa\u7206\u8c46\u4f3c\u7684\u58f0\u54cd\uff0c\u6307\u7532\u7a81\u7136\u66b4\u957f\u51e0\u5bf8\uff0c\u5c31\u50cf\u5341\u628a\u5c0f\u5200\uff0c\u5411\u91d1\u9010 \u6d41\u63d2\u4e0b\u3002\u539f\u6765\u5979\u7ec3\u7684\u662f\u201c\u9e1f\u722a\u529f\u201d\uff0c\u6307\u7532\u53ef\u4ee5\u5f53\u4f5c\u5175\u5668\u4f7f\u7528\uff0c\u5e73\u65f6\u53ef\u4ee5\u5377\u8d77\u6765\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u4e2a\u201c\u76d8\u9f99\u7ed5\u6b65\u201d\uff0c\u907f\u62db\u8fd8\u62db\u3002\u4ed6\u7684\u5929\u7f57\u6b65\u6cd5\u867d\u7136\u7cbe\u5999\uff0c\u4f46\u5728\u5c0f\u8239\u4e4b\u4e2d\u5374\u662f\u65bd\u5c55 \u4e0d\u5f00\uff0c\u9976\u662f\u4ed6\u95ea\u907f\u5f97\u5feb\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u8863\u88f3\u5df2\u662f\u7ed9\u90a3\u8001\u5a46\u5a46\u6495\u53bb\u4e86\u4e00\u5e45\u3002\u8001\u5a46\u5a46\u5f97 \u7406\u4e0d\u9976\u4eba\uff0c\u5de6\u81c2\u4e00\u5f2f\uff0c\u957f\u6307\u7532\u4fa7\u9762\u5212\u6765\uff0c\u51e0\u4e4e\u89e6\u53ca\u91d1\u9010\u6d41\u7684\u5589\u5499\uff0c\u91d1\u9010\u6d41\u95fb\u5f97\u4e00\u80a1\u6de1\u6de1\u7684\u8165 \u5473\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5927\u6012\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4ed7\u7740\u6bd2\u722a\u5bb3\u4eba\uff0c\u6211\u628a\u4f60\u7684\u722a\u5b50\u5e9f\u4e86\uff01\u201d\u62fc\u7740\u53d7\u5979\u6293\u4f24\uff0c\u5de6\u638c\u77f3 \u638c\uff0c\u731b\u51fb\u8fc7\u53bb\uff0c\u4fe8\u5982\u94c1\u65a7\u5f00\u5c71\uff0c\u5de8\u9524\u51ff\u77f3\uff0c\u90a3\u8001\u5a46\u5a46\u8fd9\u624d\u8bc6\u5f97\u4ed6\u7684\u5389\u5bb3\uff0c\u5413\u5f97\u614c\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u559d\u4e86\u6bd2\u8336\uff0c\u8fd8\u53ef\u4ee5\u5b89\u7136\u65e0\u4e8b\uff0c\u90a3\u8001\u5a46\u5a46\u5fc3\u60f3\u7eb5\u4f7f\u81ea\u5df1\u7684\u6bd2\u6307\u7532\u6293\u4f24\u4e86\u4ed6\uff0c\u4e5f\u672a\u5fc5 \u5c31\u80fd\u8981\u4e86\u4ed6\u7684\u6027\u547d\u3002\u82e5\u7ed9\u4ed6\u6253\u4e86\u4e00\u62f3\uff0c\u53ef\u4e0d\u662f\u5f53\u800d\u7684\u3002\u8fd9\u8001\u5a46\u5a46\u5e74\u8f7b\u7684\u65f6\u5019\uff0c\u672c\u6765\u4e5f\u662f\u4e2a\u6b66 \u6797\u8457\u540d\u7684\u5973\u9b54\u5934\uff0c\u4f46\u73b0\u5728\u5e74\u7eaa\u5927\u4e86\uff0c\u7cbe\u529b\u5df2\u8870\uff0c\u5374\u662f\u4e0d\u6562\u548c\u91d1\u9010\u6d41\u786c\u62fc\u4e86\u3002 \u3000\u3000\u8239\u4e2d\u80fd\u6709\u591a\u5927\u5730\u65b9\uff1f\u4e0d\u8fc7\u7247\u523b\uff0c\u53ea\u542c\u5f97\u4e52\u4e52\u4e53\u4e53\u7684\u4e00\u9635\u54cd\uff0c\u5c4f\u98ce\u63a8\u5012\uff0c\u8239\u8231\u7684\u677f\u58c1\u4e00\u5757 \u5757\u88c2\u5f00\u3002\u90a3\u8001\u5a46\u5a46\u5728\u91d1\u9010\u6d41\u62f3\u98ce\u638c\u52bf\u7684\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u5df2\u662f\u6ca1\u6709\u56de\u65cb\u7684\u4f59\u5730\uff01 \u3000\u3000\u201c\u78a7\u7075\u4e39\u201d\u662f\u7528\u5929\u5c71\u96ea\u83b2\u52a0\u4e0a\u5176\u4ed6\u73cd\u8d35\u7684\u836f\u7269\u70ae\u5236\u7684\uff0c\u867d\u7136\u4e0d\u662f\u90a3\u676f\u6bd2\u8336\u7684\u5bf9\u75c7\u89e3\u836f\uff0c \u4e5f\u6709\u6297\u6bd2\u4e4b\u529f\u3002\u9648\u5149\u7167\u541e\u4e0b\u4e86\u78a7\u7075\u4e39\u4e4b\u540e\uff0c\u80f8\u4e2d\u70e6\u95f7\u4e4b\u611f\u5927\u5927\u51cf\u8f7b\uff0c\u7cbe\u795e\u7a0d\u632f\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u5c0f\u4e2b\u9b1f\u51e0\u66fe\u89c1\u8fc7\u8fd9\u6837\u5389\u5bb3\u7684\u9635\u4ed7\uff0c\u5413\u5f97\u6296\u6296\u7d22\u7d22\u7684\u8eba\u5728\u540e\u8231\u7684\u89d2\u843d\u3002\u9648\u5149\u7167\u6000\u7740 \u6ee1\u8179\u7591\u56e2\uff0c\u8d70\u8fc7\u53bb\u6b63\u8981\u5411\u5979\u4eec\u76d8\u95ee\uff0c\u90a3\u4e24\u4e2a\u5c0f\u4e2b\u9b1f\u53ea\u9053\u9648\u5149\u7167\u662f\u8981\u6765\u6293\u5979\u4eec\uff0c\u614c\u5fd9\u53eb\u9053\uff1a \u201c\u4e0d\u5173\u6211\u4eec\u7684\u4e8b\uff01\u201d\u9648\u5149\u7167\u9053\uff1a\u201c\u6211\u53ea\u8981\u95ee\u4f60\u4eec\u51e0\u53e5\u8bdd\u3002\u201d\u90a3\u8001\u5a46\u5a46\u62a2\u7740\u559d\u9053\uff1a\u201c\u8c01\u6562\u591a \u5634\uff0c\u6211\u4e0d\u628a\u4f60\u6495\u5f00\u516b\u7247\u624d\u602a\uff01\u201d\u79ef\u5a01\u4e4b\u4e0b\uff0c\u90a3\u4e24\u4e2a\u4e2b\u9b1f\u867d\u7136\u77e5\u9053\u8fd9\u4e2a\u8001\u5a46\u5a46\u6253\u4e0d\u8fc7\u91d1\u9010\u6d41\uff0c \u4e5f\u8fd8\u662f\u7ed9\u5979\u5413\u5f97\u4e0d\u6562\u51fa\u58f0\u3002\u4f46\u5979\u4eec\u53c8\u6015\u9648\u5149\u7167\u6293\u7740\u5979\u4eec\u903c\u4f9b\uff0c\u5de6\u53f3\u4e3a\u96be\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u53cc\u53cc\u8dc3 \u4e0b\u6e56\u4e2d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6012\u9053\uff1a\u201c\u4f60\u662f\u6ce5\u83e9\u8428\u8fc7\u6c5f\uff0c\u81ea\u8eab\u96be\u4fdd\uff0c\u5c45\u7136\u8fd8\u6562\u901e\u5a01\u98ce\uff01\u201d\u53cc\u638c\u659c\u98de\uff0c\u76f4\u6b3a\u5230\u90a3 \u8001\u5a46\u5a46\u7684\u8eab\u524d\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u628a\u90a3\u8001\u5a46\u5a46\u7684\u8863\u88f3\u6495\u7834\uff0c\u8fde\u7d27\u8eab\u7684\u68c9\u7946\u90fd\u626f\u4e86\u4e0b\u6765\uff01\u8001\u5a46\u5a46 \u4e00\u638c\u906e\u80f8\uff0c\u53eb\u9053\uff1a\u201c\u81ed\u5c0f\u5b50\uff0c\u4f60\u3001\u4f60\u597d\u65e0\u793c\uff01\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e48\u5927\u628a\u5e74\u7eaa\uff0c\u96be\u9053\u8fd8\u6015 \u6211\u8c03\u620f\u4f60\u4e0d\u6210\u3002\u563f\uff0c\u563f\uff0c\u6211\u4e0d\u8fc7\u4ee5\u5176\u4eba\u4e4b\u9053\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\u800c\u5df2\u3002\u4eca\u665a\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4f60\u5feb\u8bf4\u5b9e \u8bdd\uff0c\u5426\u5219\u8fd8\u6709\u5389\u5bb3\u7684\u7ed9\u4f60\u5c1d\u5462\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b\u5e95\u6beb\u4e0d\u653e\u677e\uff0c\u628a\u90a3\u8001\u5a46\u5a46\u903c\u5f97\u72fc\u72c8\u4e0d\u582a\u3002\u9648\u5149\u7167\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u53eb\u9053\uff1a \u201c\u5979\u662f\u971e\u59d1\u5a18\u7684\u5976\u5988\uff0c\u91d1\u5144\uff0c\u8bf7\u4f60\u624b\u4e0b\u7559\u60c5\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u90a3\u8001\u5a46\u5a46\u7a81\u7136\u4f3c\u7740\u4e86\u5b9a\u8eab\u6cd5\u4f3c\u7684\uff0c\u4f5c\u7740\u5411\u524d\u653b\u51fb\u7684\u653b\u52bf\uff0c\u53cc\u624b\u5374\u662f\u505c\u5728 \u7a7a\u4e2d\uff0c\u4e0d\u80fd\u52a8\u5f39\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u559d\u9053\uff1a\u201c\u628a\u89e3\u836f\u7ed9\u6211\u6307\u51fa\u6765\uff0c\u6211\u770b\u5728\u9648\u5927\u54e5\u7684\u4efd\u4e0a\uff0c\u53ef\u4ee5\u9976\u4f60\u3002\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u7ed9\u91d1\u9010\u6d41\u70b9\u4e86\u9ebb\u7a74\uff0c\u8eab\u5b50\u4e0d\u80fd\u52a8\u5f39\uff0c\u4f46\u8fd8\u53ef\u4ee5\u8bf4\u8bdd\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u7ed9\u6211\u89e3\u7a74\uff0c \u6211\u600e\u4e48\u53ef\u4ee5\u62ff\u89e3\u836f\u7ed9\u4f60\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u542c\u6e05\u695a\u6ca1\u6709\uff0c\u6211\u662f\u53eb\u4f60\u6307\u51fa\u89e3\u836f\uff0c\u4e0d\u662f\u53eb\u4f60\u62ff\u51fa\u89e3\u836f\u3002\u4f60\u8eab\u4e0a\u7684\u4e1c\u897f \u6211\u65e9\u5df2\u62ff\u8fc7\u6765\u4e86\u3002\u201d\u8bf4\u7f62\uff0c\u53cc\u8896\u4e00\u6296\uff0c\u597d\u50cf\u53d8\u620f\u6cd5\u4f3c\u7684\uff0c\u54d7\u5566\u5566\u7684\u6296\u51fa\u4e86\u4e00\u5806\u7269\u4e8b\uff0c\u6709\u94dc \u94b1\uff0c\u6709\u788e\u94f6\u3001\u6709\u51e0\u4e2a\u74f6\u5b50\uff0c\u8fd8\u6709\u4e24\u4e2a\u5c0f\u5c0f\u7684\u7c89\u76d2\u3002\u9648\u5149\u7167\u7741\u5927\u4e86\u773c\u775b\uff0c\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6ca1\u5948 \u4f55\u505a\u4e00\u6b21\u5077\u513f\uff0c\u9648\u5927\u54e5\u4f60\u83ab\u89c1\u7b11\u3002\u201d\u539f\u6765\u91d1\u9010\u6d41\u5c31\u662f\u5728\u521a\u624d\u6495\u6bc1\u90a3\u8001\u5a46\u5a46\u5916\u8863\u7684\u65f6\u5019\uff0c\u505a\u4e86 \u624b\u811a\u5c06\u5979\u5e16\u8eab\u6536\u85cf\u7684\u4e1c\u897f\uff0c\u5168\u90e8\u6252\u8fc7\u6765\u7684\u3002 \u3000\u3000\u5f53\u4e0b\u91d1\u9010\u6d41\u628a\u74f6\u5b50\u548c\u7c89\u76d2\u6392\u5217\u5728\u8001\u5a46\u5a46\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u54ea\u4e00\u6837\u662f\u89e3\u836f\uff0c\u5982\u4f55\u7528\u6cd5\uff1f\u4f60\u8bf4 \u51fa\u6765\u5c31\u884c\u3002\u201d \u3000\u3000\u90a3\u8001\u5a46\u5a46\u773c\u73e0\u4e00\u8f6c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u90fd\u662f\u6bd2\u836f\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u89e3\u836f\u5462\uff1f\u201d\u8001\u5a46\u5a46\u9053\uff1a \u201c\u89e3\u836f\u6ca1\u6709\u5e26\u6765\u3002\u4f60\u653e\u4e86\u6211\uff0c\u6211\u56de\u53bb\u62ff\u7ed9\u4f60\u3002\u201d\u91d1\u9010\u6d41\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\uff0c\u8fd9\u91cc \u4e00\u5b9a\u6709\u4e00\u6837\u662f\u89e3\u836f\u3002\u201d\u8001\u5a46\u5a46\u9053\uff1a\u201c\u6211\u4e71\u8bf4\u4e0d\u6253\u7d27\uff0c\u4f46\u53ea\u6015\u5bb3\u4e86\u9648\u76f8\u516c\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5e26\u6211\u53bb\u89c1\u971e\u59d1\u5a18\u5427\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u8001\u5996\u5987\u5584\u4f1a\u9a97\u4eba\uff0c\u9648\u5927\u54e5\uff0c \u4f60\u53ef\u4e0d\u80fd\u5c31\u4fe1\u5979\u7684\u9b3c\u8bdd\uff0c\u5f85\u6211\u5148\u7ed9\u5979\u4e00\u70b9\u5389\u5bb3\u5c1d\u5c1d\u3002\u201d\u9648\u5149\u7167\u7ec8\u662f\u4e0d\u5fcd\uff0c\u62e6\u4f4f\u91d1\u9010\u6d41\u9053\uff1a \u201c\u4f60\u5df2\u7ecf\u70b9\u4e86\u5979\u7684\u7a74\u9053\uff0c\u5979\u8fd9\u4e00\u5927\u628a\u5e74\u7eaa\uff0c\u4e5f\u591f\u5979\u53d7\u7684\u4e86\uff0c\u4f55\u5fc5\u518d\u628a\u5979\u96be\u4e3a\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u4e00\u7247\u597d\u5fc3\uff0c\u62e6\u4f4f\u91d1\u9010\u6d41\uff0c\u4e0d\u6599\u90a3\u8001\u5a46\u5a46\u5ffd\u5730\u78d4\u78d4\u602a\u7b11\uff0c\u91d1\u9010\u6d41\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d \u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u91d1\u9010\u6d41\u521a\u521a\u628a\u9648\u5149\u7167\u63a8\u8fc7\u4e00\u8fb9\u3002\u90a3\u8001\u5a46\u5a46\u5df2\u662f\u98de\u51fa\u8863\u88d9\u8fb9\u89d2\uff0c\u628a\u74f6\u5b50\u76d2 \u5b50\uff0c\u5168\u90fd\u626b\u4e86\u843d\u6c34\uff0c\u53ea\u542c\u201c\u535c\u901a\u201d\u4e00\u58f0\uff0c\u4eba\u4e5f\u8df3\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u521a\u624d\u662f\u7528\u72ec\u95e8\u624b\u6cd5\u70b9\u4e86\u8fd9\u8001\u5a46\u5a46\u7684\u7a74\u9053\u7684\uff0c\u4ed6\u4ee5\u4e3a\u7528\u4e86\u72ec\u95e8\u7684\u70b9\u7a74\u624b\u6cd5\u5df2\u662f \u8db3\u4ee5\u5236\u4f0f\u8fd9\u4e2a\u8001\u5a46\u5a46\uff0c\u6240\u4ee5\u5e76\u6ca1\u6709\u65bd\u5c55\u91cd\u624b\u3002\u8fd9\u4e5f\u662f\u4e3a\u9648\u5149\u7167\u7ed9\u5979\u6c42\u60c5\u7684\u7f18\u6545\uff0c\u91d1\u9010\u6d41\u6050\u6015 \u7528\u4e86\u91cd\u624b\u6cd5\uff0c\u8fd9\u8001\u5a46\u5a46\u7981\u53d7\u4e0d\u8d77\u3002 \u3000\u3000\u6b8a\u4e0d\u77e5\u8fd9\u8001\u5a46\u5a46\u867d\u7136\u662f\u5e74\u8001\u4f53\u8870\uff0c\u4f46\u5185\u529f\u7684\u9020\u8be3\uff0c\u5374\u5e76\u4e0d\u5728\u91d1\u9010\u6d41\u4e4b\u4e0b\u3002\u90aa\u6d3e\u4e2d\u6709\u4e00\u79cd \u9006\u884c\u7ecf\u8109\u7684\u529f\u592b\uff0c\u80fd\u89e3\u4efb\u4f55\u4e00\u5bb6\u7684\u70b9\u7a74\uff0c\u521a\u624d\u8fd9\u8001\u5a46\u5a46\u6545\u610f\u4e1c\u62c9\u897f\u626f\uff0c\u4e3a\u7684\u5c31\u6b63\u662f\u8981\u6df7\u5f97\u8db3 \u591f\u7684\u65f6\u95f4\u6765\u65bd\u5c55\u8fd9\u79cd\u90aa\u6d3e\u5947\u529f\u3002 \u3000\u3000\u4e00\u5ff5\u6148\u60b2\uff0c\u53d8\u751f\u610f\u5916\u3002\u91d1\u9010\u6d41\u65e0\u6687\u653b\u654c\uff0c\u5148\u62a2\u89e3\u836f\uff0c\u8ddf\u7740\u5c31\u8df3\u4e0b\u6c34\u53bb\u3002\u4ed6\u662f\u5728\u6d77\u5c9b\u957f\u5927 \u7684\uff0c\u6c34\u6027\u5f53\u7136\u4e0d\u9519\u3002 \u3000\u3000\u5927\u660e\u6e56\u867d\u7136\u4e0d\u4f3c\u6d77\u4e2d\u7684\u6ce2\u6d9b\u6c79\u6d8c\uff0c\u4f46\u4e5f\u5e76\u975e\u6b7b\u6c34\u4e00\u6c60\u3002\u4f59\u9010\u6d41\u6f5c\u4e0b\u6c34\u5e95\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u627e \u7740\u4e00\u53ea\u76d2\u5b50\uff0c\u5176\u5b83\u7684\u4e1c\u897f\u5374\u4e0d\u77e5\u7ed9\u6c34\u6d41\u51b2\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6d6e\u4e0a\u6c34\u9762\u6362\u6c14\uff0c\u53ea\u89c1\u90a3\u8001\u5a46\u5a46\u5df2\u7ecf\u6d6e\u5230\u5bf9\u5cb8\uff0c\u90a3\u4e24\u4e2a\u5c0f\u9b1f\u5219\u65e9\u5df2\u4e0a\u4e86\u5cb8\u4e86\u3002\u91d1\u9010 \u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u77e5\u8fd9\u76d2\u5b50\u91cc\u662f\u4e0d\u662f\u89e3\u836f\uff0c\u5018\u82e5\u4e0d\u662f\u7684\u8bdd\uff0c\u53ef\u8fd8\u5f97\u53bb\u627e\u90a3\u8001\u5996\u5a46\u7b97\u5e10\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u521a\u624d\u548c\u90a3\u8001\u5a46\u5a46\u5728\u8239\u4e0a\u4e00\u573a\u6076\u6597\uff0c\u8239\u8231\u677f\u58c1\u5df2\u7ecf\u7ed9\u4ed6\u4eec\u6bc1\u4e86\u5341\u4e4b\u4e03\u516b\uff0c\u56db\u9762\u901a \u98ce\u3002\u6e56\u4e0a\u7684\u98ce\u867d\u7136\u4e0d\u5927\uff0c\u4f46\u56e0\u65e0\u4eba\u628a\u8235\u800c\u53c8\u56db\u9762\u901a\u98ce\uff0c\u8fd9\u53ea\u753b\u8239\u5728\u6e56\u4e2d\u5fc3\u7ed9\u5439\u5f97\u56e2\u56e2\u4e71\u8f6c\u3002 \u91d1\u9010\u6d41\u8bb0\u6302\u7740\u9648\u5149\u7167\uff0c\u5728\u6c34\u4e2d\u627e\u5bfb\u5931\u7269\u65e2\u662f\u5e0c\u671b\u6e3a\u832b\uff0c\u4e5f\u5c31\u53ea\u597d\u5148\u4e0a\u8239\u4e86\u3002 \u3000\u3000\u9648\u5149\u7167\u770b\u89c1\u91d1\u9010\u6d41\u4e00\u526f\u843d\u8361\u9e21\u7684\u6837\u5b50\uff0c\u597d\u751f\u8fc7\u610f\u4e0d\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5144\uff0c\u8f9b\u82e6\u4f60\u4e86\u3002\u6b7b\u751f \u6709\u547d\uff0c\u627e\u4e0d\u7740\u89e3\u836f\uff0c\u4e5f\u5c31\u7b97\u4e86\u3002\u4f60\u53eb\u4e10\u5e2e\u7684\u4eba\u9001\u6211\u56de\u5bb6\uff0c\u6211\u7684\u7239\u7239\u4e5f\u8bb8\u53ef\u4ee5\u6551\u6211\u3002\u201d\u8bf4\u8bdd\u4e4b \u65f6\uff0c\u5df2\u662f\u6709\u70b9\u4e0a\u6c14\u4e0d\u63a5\u4e0b\u6c14\u7684\u6a21\u6837\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u9759\u5750\u8fd0\u529f\u5427\uff0c\u4e0d\u8981\u5fd9\u7740\u8bf4\u8bdd\uff0c\u6211\u627e\u5230\u4e86\u4e00\u53ea\u76d2\u5b50\uff0c\u5c31\u4e0d\u77e5\u662f\u4e0d\u662f\u89e3 \u836f\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6253\u5f00\u90a3\u53ea\u76d2\u5b50\u4e00\u770b\uff0c\u4e0d\u89c9\u201c\u54a6\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\u3002\u9648\u5149\u7167\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u91cc\u9762\u662f \u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u91d1\u9010\u6d41\u62ff\u51fa\u4e00\u5bf9\u9f99\u773c\u6838\u822c\u5927\u5c0f\u7684\u591c\u660e\u73e0\uff0c\u9648\u5149\u7167\u7b11\u9053\uff1a\u201c\u971e\u59d1\u8fd9\u4e2a\u5976\u5a18\u79c1\u623f\u5012 \u662f\u4e0d\u5c11\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u5bf9\u660e\u73e0\u4e0d\u8db3\u4e3a\u5947\uff0c\u660e\u73e0\u538b\u7740\u7684\u5374\u662f\u4e00\u7eb8\u751f\u8fb0\u516b\u5b57\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u8c01\u7684\u751f\u8fb0\u516b\u5b57\uff0c\u7ed9\u6211\u770b\u770b\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8fdf\u7591\u4e86\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u51b3\u4e0d\u662f\u4f60\u90a3\u4f4d\u971e\u59d1\u7684\u751f\u8fb0\u516b\u5b57\uff0c\u4f60\u4e0d\u770b\u4e5f\u7f62\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4e0d\u662f\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u662f\u4e19\u5bc5\u5e74\u51fa\u751f\u7684\uff0c\u7b97\u8d77\u6765\u4eca\u5e74\u5df2\u7ecf\u6709\u4e09\u5341\u4e94\u5c81\u4e86\u3002\u4f60\u90a3\u4f4d\u971e\u59d1\u7684\u5e74 \u7eaa\u60f3\u6765\u603b\u662f\u548c\u4f60\u5dee\u4e0d\u591a\u5427\uff0c\u51b3\u4e0d\u4f1a\u6709\u4e09\u5341\u4e94\u5c81\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u5fc3\u91cc\u6709\u70b9\u7591\u60d1\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u770b\u6709\u4ec0\u4e48\u6253\u7d27\uff1f\u201d\u91d1\u9010\u6d41\u597d\u4f3c\u77e5\u9053\u4ed6\u7684\u5fc3\u601d\uff0c\u7b11\u9053\uff1a \u201c\u4f60\u4e0d\u5fc5\u7591\u60d1\uff0c\u6211\u662f\u60f3\u4f60\u4e13\u5fc3\u8fd0\u529f\u5fa1\u6bd2\u3002\u65e2\u7136\u8fd9\u4e2a\u76d2\u5b50\u91cc\u88c5\u7684\u662f\u4e2a\u54d1\u8c1c\uff0c\u8fd9\u4e2a\u54d1\u8c1c\u53ea\u6709\u6293\u7740 \u90a3\u4e2a\u8001\u5996\u5a46\u624d\u80fd\u63ed\u5f00\uff0c\u54b1\u4eec\u5c31\u65e0\u987b\u591a\u8d39\u5fc3\u601d\u4e86\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u4e00\u60f3\u4e0d\u9519\uff0c\u4eca\u665a\u4e4b\u4e8b\u6574\u4e2a\u5c31\u662f\u54d1\u8c1c\uff0c\u96be\u4ee5\u7d22\u89e3\u7684\u5730\u65b9\u592a\u591a\u4e86\u3002\u201c\u4f46\u613f\u6211\u80fd\u591f\u6d3b\u5f97 \u5230\u89c1\u7740\u971e\u59d1\uff0c\u8ba9\u6211\u77e5\u9053\u771f\u76f8\u3002\u201d\u9648\u5149\u7167\u5fc3\u60f3\u3002\u8fd9\u4e48\u4e00\u60f3\uff0c\u4ed6\u4e5f\u5c31\u5b89\u4e0b\u5fc3\u6765\u8fd0\u529f\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u8fd9\u5f20\u516b\u5b57\u7ed9\u9648\u5149\u7167\u770b\u5462\u3002\u8fd9\u91cc\u9762\u6709\u4e2a\u539f\u56e0\uff0c\u56e0\u4e3a\u8fd9\u662f\u4e00\u5f20\u201c\u5408\u5a5a\u201d\u4e4b \u7528\u7684\u7537\u65b9\u7684\u516b\u5b57\uff0c\u7537\u65b9\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\u53f2\u767d\u90fd\u3002 \u3000\u3000\u547d\u4e66\u4e0a\u5199\u5f97\u5206\u660e\u662f\u4e7e\u9020\u626c\u5dde\u53f2\u767d\u90fd\u3002\u540e\u9762\u8be6\u5217\u751f\u8fb0\u516b\u5b57\uff0c\u6d41\u5e74\u6279\u8bed\u3002\u53f2\u767d\u90fd\u8fd9\u5f20\u516b\u5b57\u5728 \u8fd9\u8001\u5a46\u5a46\u7684\u8eab\u4e0a\u53d1\u73b0\uff0c\u5f53\u7136\u662f\u6258\u5979\u4f5c\u5a92\u7684\u4e86\u3002\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u8001\u5996\u5a46\u662f\u9648\u5927\u54e5\u90a3\u4f4d\u971e \u59d1\u7684\u5976\u5a18\u3002\u54ce\u5440\uff0c\u4e0d\u597d\uff0c\u5973\u65b9\u6050\u6015\u5c31\u662f\u90a3\u5e55\u971e\u59d1\u4e86\u3002\u8001\u5996\u5a46\u662f\u4e3a\u5979\u7684\u5c0f\u59d0\u505a\u5a92\u3002\u602a\u4e0d\u5f97\u5979\u8981 \u5bb3\u9648\u5927\u54e5\u4e86\u3002\u8fd9\u4e8b\u6682\u65f6\u53ef\u4e0d\u80fd\u8ba9\u9648\u5927\u54e5\u77e5\u9053\uff1a\u201c \u3000\u3000\u91d1\u9010\u6d41\u638c\u8235\u5212\u8239\uff0c\u4ed6\u662f\u5728\u5927\u98ce\u5927\u6d6a\u4e2d\u9a7e\u8239\u60ef\u4e86\u7684\uff0c\u4f7f\u51fa\u4e86\u770b\u5bb6\u672c\u9886\uff0c\u5c0f\u821f\u75be\u5982\u5954\u9a6c\u7684\u5411 \u524d\u9a76\u53bb\u3002\u4e0d\u6d88\u591a\u4e45\uff0c\u5df2\u662f\u5230\u4e86\u5bf9\u5cb8\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6276\u9648\u5149\u7167\u4e0a\u4e86\u5cb8\uff0c\u6697\u81ea\u601d\u5fd6\uff1a\u201c\u8fd9\u8001\u5996\u5a46\u5143\u6c14\u5df2\u4f24\uff0c\u8c05\u5979\u4e5f\u8d70\u5f97\u4e0d\u8fdc\u3002\u201d\u5f53\u4e0b\u51dd \u795e\u9759\u6c14\uff0c\u542c\u4e00\u542c\u9644\u8fd1\u6709\u6ca1\u6709\u811a\u6b65\u58f0\u3002\u5ffd\u89c9\u8eab\u65c1\u7684\u4e00\u68f5\u67f3\u6811\uff0c\u6811\u53f6\u65e0\u98ce\u81ea\u843d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u559d\u9053\uff1a\u201c\u4f60\u8eb2\u4e0d\u4e86\u5566\uff0c\u51fa\u6765\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u6839\u7897\u53e3\u822c\u7c97 \u5927\u7684\u94c1\u6756\u5df2\u662f\u5411\u7740\u91d1\u9010\u6d41\u5288\u5934\u6253\u4e0b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u62d4\u5251\u51fa\u9798\uff0c\u4e00\u62db\u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u5c06\u94c1\u6756\u62e8\u5f00\uff0c\u5b9a\u775b\u770b\u65f6\uff0c\u53ea\u89c1\u4ece\u67f3\u6811\u540e\u9762\u51fa\u6765\u5411 \u4ed6\u5077\u88ad\u7684\u8fd9\u4e2a\u4eba\uff0c\u5374\u4e0d\u662f\u90a3\u4e2a\u8001\u5a46\u5a46\uff0c\u800c\u662f\u66f9\u5bb6\u7684\u90a3\u4e2a\u62a4\u9662\u5f6d\u5de8\u5d02\u3002 \u3000\u3000\u5f6d\u5de8\u5d02\u64ae\u5507\u4e00\u5578\uff0c\u67f3\u6811\u4e1b\u4e2d\u3002\u4f0f\u5175\u9f50\u51fa\uff0c\u9ed1\u538b\u538b\u7684\u4e5f\u4e0d\u77e5\u6709\u591a\u5c11\u4eba\u3002\u5f6d\u5de8\u5d02\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u5929\u5802\u6709\u8def\u4f60\u4e0d\u8d70\uff0c\u5730\u72f1\u65e0\u95e8\u4f60\u504f\u8fdb\u6765\u3002\u59d3\u91d1\u7684\u5c0f\u5b50\uff0c\u770b\u4f60\u8fd8\u8dd1\u5f97\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u563b\u7b11\u9053\uff1a\u201c\u4f60\u662f\u6211\u624b\u4e0b\u8d25\u5c06\uff0c\u4e5f\u6562\u901e\u80fd\uff1f\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u5530\u5530\u5530\u7684\u8fde\u73af\u4e09\u5251\uff0c\u6740\u5f97 \u5f6d\u5de8\u5d02\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u5f6d\u5de8\u5d02\u662f\u5c11\u6797\u6d3e\u7684\u5ae1\u4f20\u5f1f\u5b50\uff0c\u529f\u529b\u4e0d\u5728\u91d1\u9010\u6d41\u4e4b\u4e0b\uff0c\u4f46\u5374\u5e94\u4ed8\u4e0d\u4e86\u91d1\u9010\u6d41\u7684\u5feb\u5251\u3002\u91d1\u9010 \u6d41\u4ee5\u95ea\u7535\u822c\u7684\u5251\u6cd5\u903c\u9000\u4e86\u5f6d\u5de8\u5d02\uff0c\u8bf4\u9053\uff1a\u201c\u9648\u5927\u54e5\uff0c\u4f60\u7d27\u8ddf\u7740\u6211\uff01\u201d\u4e00\u62db\u201c\u591c\u6218\u516b\u65b9\u201d\u5251\u5149 \u56db\u9762\u5c55\u5f00\uff0c\u7acb\u5373\u5e26\u4e86\u9648\u5149\u7167\u4ece\u7f3a\u53e3\u51b2\u51fa\u3002 \u3000\u3000\u5ffd\u5730\u4e00\u6761\u85e4\u86c7\u68d2\u659c\u523a\u6253\u6765\uff0c\u52b2\u98ce\u5916\u9762\u3002\u91d1\u9010\u6d41\u53cd\u624b\u4e00\u5251\uff0c\u4f7f\u51fa\u4e86\u201c\u6a2a\u4e91\u65ad\u950b\u201d\u7684\u62db\u6570\u524a \u53bb\u3002\u8fd9\u4e00\u62db\u82e5\u662f\u7ed9\u4ed6\u524a\u5b9e\uff0c\u8db3\u53ef\u4ee5\u524a\u65ad\u90a3\u4eba\u7684\u68d2\uff0c\u4f46\u90a3\u4eba\u5374\u4e5f\u662f\u5341\u5206\u6e9c\u6ed1\uff0c\u85e4\u86c7\u68d2\u5f80\u5916\u4e00 \u6302\uff0c\u500f\u5730\u5df2\u53d8\u4e3a\u201c\u659c\u6302\u5355\u97ad\u201d\uff0c\u53cd\u7838\u91d1\u9010\u6d41\u7684\u5251\u67c4\u3002 \u3000\u3000\u68d2\u957f\u5251\u77ed\uff0c\u773c\u770b\u91d1\u9010\u6d41\u5c31\u8981\u5403\u4e8f\uff0c\u5e78\u800c\u91d1\u9010\u6d41\u7684\u5251\u6cd5\u5df2\u5230\u6536\u53d1\u968f\u5fc3\u7684\u5883\u754c\uff0c\u5f53\u4e0b\u4e00\u9886\u5251 \u950b\uff0c\u62db\u6570\u672a\u8001\uff0c\u4fbf\u5373\u5708\u56de\uff0c\u4e00\u62db\u201c\u957f\u6cb3\u843d\u65e5\u201d\uff0c\u9752\u94a2\u5251\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u767b\u7684\u2018\u53cd\u5ba2\u4e3a\u4e3b\u2019\u4e0d \u4f46\u7834\u4e86\u90a3\u4eba\u7684\u62db\u6570\uff0c\u800c\u4e14\u53cd\u524a\u4ed6\u7684\u819d\u76d6\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u4eba\u4e1b\u4e2d\u53c8\u8df3\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u4eba\u8fd8\u672a\u5230\uff0c\u624b\u4e2d\u7684\u94fe\u5b50\u9524\u5148\u6253\u51fa\u6765\u3002\u94fe\u5b50\u9524\u53ef\u6253\u5230 \u4e00\u4e08\u5f00\u5916\uff0c\u53ea\u542c\u5f97\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u91d1\u9010\u6d41\u7684\u957f\u5251\u7adf\u7136\u7ed9\u4ed6\u78d5\u5f00\uff0c\u6e85\u8d77\u4e86\u4e00\u84ec\u706b\u82b1\u3002\u91d1\u9010\u6d41\u867d \u7136\u5e76\u672a\u5403\u4e8f\uff0c\u4e5f\u4e0d\u7531\u5f97\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u66f9\u5bb6\u8d70\u4e86\u4e00\u4e2a\u8fde\u57ce\u864e\uff0c\u5374\u6dfb\u4e86\u8fd9\u4e24\u4e2a\u80fd\u4eba\uff0c\u5012\u662f\u4e0d\u53ef\u8f7b \u654c\u4e86\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u4eba\u6b63\u662f\u5f6d\u5de8\u5d02\u66ff\u66f9\u5bb6\u627e\u6765\uff0c\u9876\u66ff\u8fde\u57ce\u864e\u7684\u7f3a\u7684\u3002\u4f7f\u85e4\u86c7\u68d2\u7684\u90a3\u4e2a\u6c49\u5b50\u540d\u53eb\u7530\u5cfb\u3002 \u4f7f\u94fe\u5b50\u9524\u7684\u540d\u53eb\u9b4f\u501a\uff0c\u4e24\u4e2a\u90fd\u662f\u6c5f\u6e56\u4e0a\u7684\u72ec\u811a\u5927\u76d7\uff0c\u4e0d\u4e45\u4e4b\u524d\uff0c\u624d\u5408\u4f19\u4f5c\u4e86\u4e00\u4ef6\u5927\u6848\uff0c\u4ed6\u4eec \u4e4b\u6240\u4ee5\u613f\u610f\u6295\u9760\u66f9\u5bb6\uff0c\u4e00\u6765\u662f\u5374\u4e0d\u8fc7\u5f6d\u5de8\u5d02\u7684\u60c5\u9762\uff0c\u4e8c\u6765\u4e5f\u56e0\u4e3a\u201c\u5927\u6811\u5e95\u4e0b\u597d\u906e\u9634\u201d\uff0c\u85c9\u66f9 \u5bb6\u907f\u907f\u98ce\u5934\u3002 \u3000\u3000\u90a3\u8001\u5a46\u5a46\u548c\u66f9\u5bb6\u662f\u4e32\u901a\u597d\u7684\uff0c\u7531\u5979\u5c06\u91d1\u3001\u9648\u4e8c\u4eba\u9a97\u5230\u6e56\u4e2d\uff0c\u6697\u4e2d\u4e0b\u6bd2\uff0c\u8fd8\u6015\u4e07\u4e00\u6bd2\u4ed6\u4eec \u4e0d\u6b7b\uff0c\u53c8\u53eb\u66f9\u5bb6\u7684\u4eba\u57cb\u4f0f\u5cb8\u4e0a\u3002\u6ee1\u4ee5\u4e3a\u4ed6\u4eec\u4e2d\u6bd2\u4e4b\u540e\uff0c\u5373\u4f7f\u9003\u5230\u5cb8\u4e0a\uff0c\u4e5f\u662f\u65e0\u529b\u62b5\u6297\u7684\u4e86\uff0c \u54ea\u77e5\u91d1\u9010\u6d41\u4f1a\u4f7f\u6b63\u90aa\u5408\u4e00\u7684\u5185\u529f\uff0c\u628a\u6bd2\u8336\u4ece\u6307\u5c16\u5c04\u51fa\u6765\uff0c\u672c\u8eab\u7adf\u7136\u6ca1\u6709\u4e2d\u6bd2\u3002\u4e00\u4e0a\u5cb8\u6765\uff0c\u5148 \u6740\u9000\u4e86\u5f6d\u5de8\u5d02\uff0c\u7530\u5cfb\u548c\u9b4f\u501a\u4e5f\u662f\u4e00\u4e2a\u7167\u9762\u5c31\u9669\u4e9b\u5403\u4e86\u4ed6\u7684\u5927\u4e8f\u3002\u7530\u3001\u9b4f\u4e8c\u4eba\u662f\u9ed1\u9053\u4e0a\u65e9\u5df2\u6210 \u540d\u7684\u4eba\u7269\uff0c\u5e73\u751f\u7f55\u9047\u5bf9\u624b\uff0c\u521d\u65f6\u4ed6\u4eec\u8fd8\u4e0d\u6ee1\u4e8e\u5f6d\u5de8\u5d02\u7684\u5174\u5e08\u52a8\u4f17\uff0c\u89c9\u5f97\u4ed6\u8fc7\u4efd\u7d27\u5f20\u3002\u5982\u4eca\u5403 \u4e86\u91d1\u9010\u6d41\u7684\u4e8f\uff0c\u8fd9\u624d\u6697\u6697\u5fc3\u60ca\u3002 \u3000\u3000\u4f46\u8fd9\u4e24\u4eba\u6bd5\u7adf\u90fd\u662f\u6c5f\u6e56\u5927\u76d7\u51fa\u8eab\uff0c\u51f6\u608d\u60ef\u4e86\u7684\uff0c\u867d\u7136\u5fc3\u91cc\u5403\u60ca\uff0c\u5374\u4ecd\u7136\u4e0d\u7518\u7f62\u624b\u3002\u9b4f\u501a \u7528\u94fe\u5b50\u9524\u78d5\u5f00\u4e86\u91d1\u9010\u6d41\u7684\u957f\u5251\uff0c\u7530\u5cfb\u9a6c\u4e0a\u53c8\u6251\u4e0a\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5927\u6012\uff0c\u6b63\u62df\u65bd\u5c55\u6740\u624b\uff0c\u5404\u4e2a\u51fb\u7834\uff0c\u5ffd\u542c\u5f97\u80cc\u540e\u4e00\u547c\u547c\u98ce\u54cd\uff0c\u5f6d\u5de8\u5d02\u7684\u94c1\u6756\u53c8\u5df2\u6253 \u4e86\u5230\u6765\u3002 \u3000\u3000\u5c11\u676f\u5bfa\u771f\u4f20\u7684\u4f0f\u9b54\u6756\u6cd5\u521a\u731b\u65e0\u6bd4\uff0c\u91d1\u9010\u6d41\u82e5\u4ee5\u4e00\u5bf9\u4e00\uff0c\u53ef\u4ee5\u7528\u5feb\u5251\u5c06\u4ed6\u514b\u5236\uff0c\u4f46\u5982\u4eca\u5728 \u7530\u3001\u9b4f\u4e8c\u4eba\u5939\u653b\u4e4b\u4e0b\uff0c\u4ee5\u4e00\u654c\u4e09\uff0c\u53ef\u5c31\u6709\u70b9\u96be\u4e8e\u5e94\u4ed8\u4e86\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u7530\u5cfb\u4e00\u62db\u201c\u85e4\u86c7\u7f20\u6811\u201d\uff0c\u62e6\u8170\u5288\u6253\u3002\u85e4\u86c7\u68d2\u662f\u786c\u4e2d\u5e26\u8f6f\u7684\u5175\u5668\uff0c\u7ed9\u5b83\u7f20\u4e0a\u4e86\u53ef\u4e5f \u4e0d\u662f\u5f53\u800d\u7684\uff0c\u91d1\u9010\u6d41\u4e00\u4e2a\u201c\u5927\u5f2f\u8170\uff0c\u659c\u63d2\u67f3\u201d\u4fef\u8eab\u8fdb\u5251\uff0c\u907f\u8fc7\u4e86\u85e4\u86c7\u68d2\uff0c\u62e8\u5f00\u4e86\u94fe\u5b50\u9524\uff0c\u957f \u5251\u4e00\u62cd\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\uff0c\u53c8\u628a\u5f6d\u5de8\u5d02\u7684\u94c1\u6756\u6309\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u62db\u4e09\u5f0f\uff0c\u7834\u89e3\u4e09\u79cd\u4e0d\u540c\u7684\u5175\u5668\u653b\u51fb\uff0c\u786e\u662f\u7528\u5f97\u5999\u5230\u6beb\u98a0\u3002\u53ef\u662f\u4ed6\u4e5f\u6bd5\u7adf\u662f\u53ea\u6709 \u4e24\u53ea\u624b\uff0c\u5206\u8eab\u4e4f\u672f\uff0c\u529b\u654c\u4e09\u5927\u9ad8\u624b\u4e4b\u65f6\uff0c\u5bf9\u9648\u5149\u7167\u53ef\u5c31\u6709\u70b9\u7167\u987e\u4e0d\u5468\u4e86\u3002 \u3000\u3000\u9648\u5149\u7167\u638f\u51fa\u51b0\u9b44\u795e\u5f39\uff0c\u559d\u58f0\u201c\u6253\uff01\u201d\u653b\u4e0a\u6765\u7684\u4e09\u4e2a\u58ee\u6c49\u7ed9\u51b0\u5f39\u6253\u7740\uff0c\u5947\u5bd2\u900f\u9aa8\uff0c\u6d51\u8eab\u53d1 \u6296\uff0c\u91d1\u9010\u6d41\u8f6c\u8fc7\u8eab\u6765\uff0c\u4e00\u4e2a\u626b\u5802\u817f\uff0c\u628a\u8fd9\u4e09\u6761\u5927\u6c49\u8e22\u51fa\u6570\u4e08\u5f00\u5916\u3002 \u3000\u3000\u4f46\u9648\u5149\u7167\u662f\u4e2d\u4e86\u6bd2\uff0c\u529f\u529b\u5df2\u7ecf\u5927\u5927\u81e7\u5f31\uff0c\u4ed6\u7528\u4e86\u51b0\u9b44\u795e\u63b8\uff0c\u81ea\u5df1\u4e5f\u7981\u4e0d\u7740\u5bd2\u51b7\uff0c\u867d\u8fd8\u4e0d \u81f4\u6655\u5012\uff0c\u4e5f\u6253\u4e86\u51e0\u4e2a\u55b7\u568f\u3002 \u3000\u3000\u7530\u9b4f\u770b\u51fa\u4fbf\u5b9c\uff0c\u6570\u62db\u4e4b\u540e\u3002\u5f97\u5230\u4e00\u4e2a\u673a\u4f1a\uff0c\u7ed5\u5230\u91d1\u9010\u6d41\u80cc\u540e\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u8eba\u4e0b \u5427\uff01\u201d\u85e4\u86c7\u68d2\u970d\u5730\u5411\u9648\u5149\u7167\u4e0b\u4e09\u8def\u6765\u4e00\u4e2a\u201c\u76d8\u6253\u201d\u3002 \u3000\u3000\u9648\u5149\u7167\u53ea\u597d\u62d4\u51fa\u201c\u51b0\u9b44\u5bd2\u5149\u5251\u201d\u62db\u67b6\uff0c\u201c\u94db\u201d\u7684\u4e00\u58f0\uff01\u5bd2\u5149\u5251\u8131\u624b\u98de\u51fa\u3002\u7530\u5cfb\u5927\u559c\uff0c\u4e3e \u68d2\u5c31\u6253\u3002 \u3000\u3000\u773c\u770b\u8fd9\u4e00\u68d2\u6253\u4e86\u4e0b\u6765\uff0c\u9648\u5149\u7167\u5c31\u662f\u4e0d\u6b7b\uff0c\u4e5f\u5f97\u91cd\u4f24\uff0c\u7530\u5cfb\u5ffd\u7136\u6253\u4e86\u4e2a\u51b7\u6218\uff0c\u624b\u8155\u5fae\u5fae\u4e00 \u6296\uff0c\u8fd9\u4e00\u68d2\u5c31\u6253\u6b6a\u4e86\u3002\u539f\u6765\u4ed6\u7684\u819d\u86c7\u68d2\u63a5\u89e6\u4e86\u51b0\u9b44\u5bd2\u5149\u5251\uff0c\u5947\u5bd2\u4e4b\u6c14\uff0c\u4f20\u5165\u5730\u7684\u638c\u5fc3\uff0c\u6b64\u65f6 \u65b9\u59cb\u53d1\u4f5c\u3002 \u3000\u3000\u91d1\u9010\u6d41\u624b\u6025\u773c\u5feb\uff0c\u4e00\u4e2a\u201c\u9ec4\u9e64\u51b2\u9704\u201d\uff0c\u8eab\u5f62\u5e73\u5730\u62d4\u8d77\uff0c\u628a\u51b0\u9b44\u5bd2\u5149\u5251\u63a5\u5230\u624b\u4e2d\uff0c\u8bf4\u9053\uff1a \u201c\u5927\u54e5\uff0c\u501f\u4f60\u7684\u5251\u4e00\u7528\u3002\u201d\u53cc\u5251\u970d\u970d\u5c55\u5f00\uff0c\u65b9\u5706\u6570\u4e08\u4e4b\u5185\u90e8\u5728\u51b7\u6c14\u5bd2\u5149\u7b3c\u7f69\u4e4b\u4e0b\u3002\u6740\u5f97\u5f6d\u5de8 \u5d02\u7b49\u4eba\u8fd1\u4e0d\u4e86\u8eab\u3002 \u3000\u3000\u53ef\u662f\u5f6d\u3001\u7530\u3001\u9b4f\u4e09\u4eba\u4e5f\u90fd\u662f\u6c5f\u6e56\u4e0a\u7684\u4e00\u6d41\u811a\u8272\uff0c\u867d\u7136\u51b7\u5f97\u76ae\u80a4\u8d77\u6817\uff0c\u4e5f\u8fd8\u7981\u53d7\u5f97\u8d77\uff0c\u8fd1 \u4e0d\u4e86\u8eab\uff0c\u4f9d\u7136\u82e6\u7f20\u4e0d\u9000\u3002 \u3000\u3000\u66f9\u5bb6\u7684\u5bb6\u4e01\u63d2\u4e0d\u8fdb\u624b\uff0c\u8fdc\u8fdc\u7684\u56f4\u4f4f\u4ed6\u4eec\u3002\u5f6d\u5de8\u5d02\u559d\u9053\uff1a\u201c\u653e\u7bad\u5c04\u90a3\u59d3\u9648\u7684\u5c0f\u5b50\uff01\u201d\u9648\u5149 \u7167\u8ddf\u5728\u91d1\u9010\u6d41\u7684\u80cc\u540e\uff0c\u6781\u529b\u652f\u6491\uff0c\u6447\u6447\u6b32\u5760\uff0c\u5df2\u662f\u6709\u70b9\u652f\u6491\u4e0d\u4f4f\uff0c\u8ddf\u4e0d\u4e0a\u91d1\u9010\u6d41\u7684\u811a\u6b65\u3002\u66f9 \u5bb6\u7684\u5bb6\u4e01\u6709\u6570\u5341\u540d\u4e4b\u591a\uff0c\u4e71\u7bad\u9f50\u53d1\uff0c\u90fd\u662f\u5411\u7740\u9648\u5149\u7167\u6025\u5c04\u3002\u5f6d\u5de8\u5d02\u7b49\u4eba\u5728\u4e09\u4e08\u5f00\u5916\uff0c\u4e0d\u6015\u4e71 \u7bad\u8bef\u4f24\u3002 \u3000\u3000\u91d1\u9010\u6d41\u559d\u9053\uff1a\u201c\u6697\u7bad\u4f24\u4eba\uff0c\u7b97\u54ea\u95e8\u5b50\u597d\u6c49\uff01\u201d\u5f6d\u5de8\u5d02\u51b7\u7b11\u9053\uff1a\u201c\u8c01\u53eb\u4f60\u656c\u9152\u4e0d\u5403\u5403\u7f5a \u9152\u3002\u4f60\u6015\u4f24\u4e86\u8d35\u53cb\uff0c\u90a3\u5c31\u4e56\u4e56\u7684\u628a\u5251\u7ed9\u6211\u629b\u4e86\u3002\u6211\u4eec\u7684\u5f13\u7bad\u4e0d\u5c04\u624b\u65e0\u5bf8\u94c1\u4e4b\u4eba\u3002\u201d\u8a00\u4e0b\u4e4b \u610f\uff0c\u5373\u662f\u8981\u91d1\u9010\u6d41\u7f34\u68b0\u6295\u964d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6012\u9053\uff1a\u201c\u8981\u6211\u6254\u5251\u4e5f\u884c\uff0c\u4f60\u628a\u4eba\u5934\u6765\u6362\uff01\u201d\u8df3\u5c06\u8d77\u6765\uff0c\u8863\u8896\u4e00\u6325\uff0c\u8361\u5f00\u4e71\u7bad\uff0c\u4eba \u5728\u534a\u7a7a\uff0c\u5530\u7684\u5c31\u662f\u4e00\u5251\u671d\u7740\u5f6d\u5de8\u5d02\u523a\u4e0b\u53bb\u3002 \u3000\u3000\u5f6d\u5de8\u5d02\u8eab\u8eaf\u4e00\u77ee\uff0c\u53cc\u624b\u6267\u7740\u94c1\u6756\u7684\u4e24\u5934\uff0c\u94c1\u6756\u4e00\u4e3e\uff0c\u63a5\u4e86\u91d1\u9010\u6d41\u7684\u4e00\u5251\uff0c\u706b\u661f\u84ec\u98de\u4e4b \u4e2d\uff0c\u5f6d\u5de8\u5d02\u53ea\u89c9\u5934\u76ae\u9635\u9635\u9178\u9ebb\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7530\u5cfb\u7684\u85e4\u86c7\u68d2\uff0c\u9b4f\u501a\u7684\u94fe\u5b50\u9524\u53cc\u53cc\u6253\u5230\uff0c \u91d1\u9042\u6d41\u672c\u6765\u662f\u8981\u60f3\u4ee5\u95ea\u7535\u7684\u624b\u6cd5\uff0c\u4e00\u51fb\u6210\u529f\u7684\uff0c\u4e00\u51fb\u4e0d\u4e2d\uff0c\u53ea\u597d\u8d76\u5fd9\u53c8\u9000\u56de\u53bb\u4fdd\u62a4\u9648\u5149\u7167\u3002 \u3000\u3000\u5f6d\u5de8\u5d02\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u8131\u9669\u4e4b\u540e\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u5c45\u7136\u60f3\u8981\u6211\u7684\u5403\u996d\u5bb6\u4f19\uff01\u597d\uff0c \u4e14\u770b\u662f\u8c01\u6d3b\u5f97\u6210\u8c01\u6d3b\u4e0d\u6210\uff1f\u201d\u4e09\u4eba\u9996\u5c3e\u7167\u5e94\uff0c\u9010\u6b65\u63a8\u8fdb\u3002\u91d1\u9010\u6d41\u5fc5\u987b\u5168\u795e\u7167\u987e\u9648\u5149\u7167\uff0c\u4e3a\u4ed6 \u62e8\u5f00\u4e71\u7bad\uff0c\u5bd2\u5149\u5251\u7684\u5a01\u529b\u81ea\u662f\u65bd\u5c55\u4e0d\u5f00\uff0c\u5708\u5b50\u8d8a\u7f29\u8d8a\u5c0f\u3002 \u3000\u3000\u6b63\u5728\u4e07\u5206\u5403\u7d27\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u4e00\u7247\u5406\u559d\u4e4b\u58f0\uff1a\u201c\u6253\u72d7\u5440\uff01\u6253\u72d7\u5440\uff01\u201d\u8f6c\u773c\u4e4b\u95f4\uff0c\u53ea\u89c1\u4e00\u5927 \u7fa4\u53eb\u5316\u5b50\u4ece\u5c71\u4e0a\u4e0b\u6765\uff0c\u628a\u66f9\u5bb6\u7684\u5bb6\u4e01\u53cd\u5305\u56f4\u8d77\u6765\u4e86\u3002 \u3000\u3000\u5f6d\u5de8\u5d02\u53c8\u60ca\u53c8\u6012\uff0c\u559d\u9053\uff1a\u201c\u738b\u8235\u4e3b\uff0c\u6211\u4e0e\u4f60\u4eec\u4e95\u6c34\u4e0d\u72af\u6cb3\u6c34\uff0c\u4f60\u8fd9\u662f\u6765\u5e72\u4ec0\u4e48\uff1f\u201d\u738b\u6cf0 \u7b11\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u662f\u8bf4\u5f97\u6e05\u6e05\u695a\u695a\u4e86\u4e48\uff1f\u53eb\u5316\u5b50\u5929\u751f\u5c31\u662f\u8981\u6253\u6076\u72d7\uff01\u9664\u975e\u90a3\u53ea\u72d7\u4e0d\u54ac\u4eba\u4e86\uff0c\u6211 \u4eec\u624d\u4f1a\u653e\u4ed6\u8fc7\u53bb\uff01\u563f\uff0c\u54e9\uff0c\u5f6d\u5927\u62a4\u9662\uff0c\u4f60\u61c2\u4e86\u4e48\uff1f\u4f60\u8981\u6211\u4eec\u6253\u5462\u8fd8\u662f\u4e0d\u6253\uff0c\u73b0\u5728\u5c31\u53ea\u662f\u770b\u4f60 \u7684\u4e86\uff01\u201d \u3000\u3000\u738b\u6cf0\u4e00\u53d1\u8bdd\uff0c\u66f9\u5bb6\u7684\u5bb6\u4e01\u5413\u5f97\u8fde\u5fd9\u6536\u4e86\u5f13\u7bad\u3002\u539f\u6765\u8c6a\u95e8\u9e70\u72ac\uff0c\u6700\u6015\u7684\u5c31\u662f\u78b0\u4e0a\u4e10\u5e2e\u7684 \u4eba\u3002\u4e10\u5e2e\u662f\u6c5f\u6e56\u4e0a\u7b2c\u4e00\u5927\u5e2e\uff0c\u5b98\u5e9c\u7684\u52bf\u529b\u4e5f\u538b\u4e0d\u5012\u4ed6\u4eec\u3002\u4e8c\u6765\u53eb\u5316\u5b50\u4e00\u65e0\u6240\u6709\uff0c\u8859\u95e8\u91cc\u7684\u516c \u5dee\u4e00\u5411\u628a\u4e10\u5e2e\u5f1f\u5b50\u6bd4\u55bb\u4f5c\u201c\u7caa\u5751\u91cc\u7684\u77f3\u5934\u201d\uff0c\u53c8\u81ed\u53c8\u786c\uff0c\u53c8\u69a8\u4e0d\u51fa\u6cb9\u6c34\uff0c\u78b0\u4e0a\u4ed6\u4eec\uff0c\u53ea\u6709\u5012 \u6963\uff0c\u51b3\u65e0\u4fbf\u5b9c\u3002 \u3000\u3000\u5f6d\u5de8\u5d02\u9762\u8272\u94c1\u9752\uff0c\u5c3d\u7ba1\u5fc3\u4e2d\u6c14\u607c\uff0c\u5374\u662f\u4e0d\u6562\u53d1\u4f5c\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u7fa4\u53eb\u5316\u5b50\u96be\u7f20\u5f97\u7d27\uff0c\u597d\u6c49 \u4e0d\u5403\u773c\u524d\u4e4b\u4e8f\u3002\u4e8e\u662f\u53ea\u597d\u6325\u624b\u8bf4\u4e86\u4e00\u4e2a\u201c\u8d70\u201d\u5b57\uff0c\u8f6c\u773c\u95f4\u4e00\u5927\u7fa4\u8c6a\u5974\u8d70\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u5316\u5b57\u62cd \u638c\u7b11\u9053\uff1a\u201c\u75db\u5feb\uff0c\u75db\u5feb\uff01\u72d7\u513f\u90fd\u5377\u8d77\u5c3e\u5df4\u9003\u5566\uff01\u201d \u3000\u3000\u738b\u6cf0\u770b\u4e86\u770b\u9648\u5149\u7167\u7684\u9762\u8272\uff0c\u5403\u60ca\u9053\uff1a\u201c\u8fd9\u4f4d\u670b\u53cb\u4f3c\u4e4e\u662f\u4e2d\u4e86\u6bd2\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6b63\u662f\u3002 \u5ba2\u5e97\u6211\u4eec\u4e0d\u4fbf\u56de\u53bb\u4e86\uff0c\u738b\u8235\u4e3b\uff0c\u8fd9\u6b21\u53ef\u662f\u4e0d\u5f97\u4e0d\u6253\u6270\u4f60\u5566\u3002\u201d\u738b\u6cf0\u9053\uff1a\u201c\u6211\u6b63\u662f\u6765\u63a5\u4f60\u4eec \u7684\uff0c\u53ef\u60dc\u8fd8\u662f\u6765\u8fdf\u4e86\u4e00\u6b65\u3002\u201d \u3000\u3000\u53eb\u5316\u5b50\u4eba\u591a\u505a\u4e8b\u5feb\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u5df2\u662f\u65a9\u4e0b\u67f3\u679d\u7f16\u6210\u62c5\u67b6\uff0c\u62ac\u8d77\u4e86\u9648\u5149\u7167\u5c31\u8d70\u3002\u738b\u6cf0\u9053\uff1a \u201c\u91d1\u5c11\u4fa0\uff0c\u6211\u548c\u4f60\u8bf4\u513f\u53e5\u8bdd\u3002\u201d\u91d1\u9010\u6d41\u653e\u6162\u4e86\u811a\u6b65\uff0c\u738b\u6cf0\u8d70\u5230\u4ed6\u7684\u8eab\u8fb9\uff0c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9 \u4f4d\u670b\u53cb\u662f\u2026\u2026\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4ed6\u540d\u53eb\u9648\u5149\u7167\uff0c\u4ed6\u7684\u7236\u4eb2\u4f60\u4e00\u5b9a\u77e5\u9053\u7684\uff0c\u5c31\u662f\u2026\u2026\u201d\u8bdd\u672a\u8bf4 \u5b8c\uff0c\u738b\u6cf0\u5df2\u662f\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u53ef\u5c31\u662f\u82cf\u5dde\u9648\u5927\u4fa0\u9648\u5929\u5b87\u7684\u516c\u5b50\u4e48\uff1f\u201d\u539f\u6765\u738b\u6cf0\u89c1\u4e86\u90a3\u628a\u51b0\u9b44\u5bd2 \u5149\u5251\uff0c\u5df2\u662f\u731c\u5230\u4e86\u9648\u5149\u7167\u7684\u6765\u5386\u3002 \u3000\u3000\u738b\u6cf0\u5f97\u5230\u8bc1\u5b9e\u4e4b\u540e\uff0c\u987f\u8db3\u53f9\u9053\uff1a\u201c\u7cdf\u4e86\uff0c\u7cdf\u4e86\uff01\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u600e\u4e48\u7cdf\u4e86\uff1f\u201d\u738b\u6cf0\u9053\uff1a \u201c\u4f60\u8fd9\u4f4d\u670b\u53cb\u662f\u7ed9\u5929\u9b54\u6559\u4e0b\u7684\u6bd2\uff0c\u6050\u6015\u6d3b\u4e0d\u8fc7\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u6211\u4e0d\u77e5\u66f9\u5bb6\u7adf\u6536\u7f57\u6709\u5929\u9b54\u6559\u7684 \u4eba\uff0c\u65e9\u77e5\u5982\u6b64\uff0c\u521a\u624d\u6211\u4e5f\u4e0d\u653e\u4ed6\u4eec\u8d70\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u6211\u662f\u6765\u4e0d\u53ca\u5c06\u4ed6\u9001\u56de\u5bb6\u533b\u6cbb\u7684\u4e86\u3002\u201d\u738b\u6cf0\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u80fd\uff0c\u53ea\u6709 \u5728\u6b64\u5730\u60f3\u6cd5\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u6709\u5929\u5c71\u96ea\u83b2\u70ae\u5236\u7684\u78a7\u7075\u4e39\uff0c\u867d\u7136\u4e0d\u662f\u5bf9\u75c7\u89e3\u836f\uff0c\u603b\u8fd8\u53ef\u4ee5\u62d6 \u4e00\u4e24\u5929\uff0c\u5728\u8fd9\u4e24\u5929\u4e4b\u5185\uff0c\u6211\u8bbe\u6cd5\u628a\u89e3\u836f\u53d6\u6765\u3002\u201d\u738b\u6cf0\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u77e5\u9053\u4e0b\u6bd2\u7684\u662f\u4ec0\u4e48\u4eba \u4e48\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u662f\u4e00\u4e2a\u8001\u5a46\u5a46\uff0c\u4f46\u5979\u7684\u6765\u5386\u6211\u8fd8\u4e0d\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6697\u81ea\u601d\u91cf\uff1a\u201c\u8fd9\u8001\u5996\u5a46\u591a\u534a\u662f\u8eb2\u5728\u66f9\u5bb6\u3002\u5373\u4f7f\u4e0d\u5728\uff0c\u6211\u53ea\u8981\u6349\u4f4f\u4e86\u66f9\u5bb6\u7684\u4eba\uff0c\u4e5f \u53ef\u4ee5\u95ee\u51fa\u5979\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u56de\u5230\u4e10\u5e2e\u5206\u8235\uff0c\u91d1\u9010\u6d41\u8bf7\u738b\u6cf0\u62e8\u51fa\u4e00\u95f4\u9759\u5ba4\uff0c\u628a\u9648\u5149\u7167\u6276\u8fdb\u53bb\u3002\u91d1\u9010\u6d41\u4ee5\u4e0a\u4e58\u5185\u529f\u66ff\u4ed6 \u63a8\u8840\u8fc7\u5bab\uff0c\u53ef\u60dc\u9648\u5149\u7167\u672c\u8eab\u7684\u529f\u529b\u914d\u5408\u4e0d\u4e0a\uff0c\u6bd2\u6c14\u4e0d\u80fd\u5b8c\u5168\u53d1\u6563\u51fa\u6765\uff0c\u4f46\u4e5f\u597d\u4e86\u4e00\u4e9b\u3002\u5168\u9010 \u6d41\u518d\u8ba9\u4ed6\u670d\u4e86\u4e00\u9897\u78a7\u7075\u4e39\uff0c\u9648\u5149\u7167\u82cd\u767d\u7684\u8138\u4e0a\u6e10\u6e10\u6062\u590d\u4e86\u51e0\u5206\u8840\u8272\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8bf4\u9053\uff1a\u201c\u9648\u5927\u54e5\uff0c\u4e0d\u662f\u6211\u597d\u6253\u542c\u522b\u4eba\u79c1\u4e8b\uff0c\u4f46\u6211\u8981\u627e\u90a3\u8001\u5996\u5a46\u7b97\u5e10\uff0c\u5374\u662f\u4e0d\u80fd\u4e0d \u77e5\u9053\u5979\u7684\u6765\u5386\u3002\u4f60\u548c\u4f60\u90a3\u4f4d\u971e\u59d1\u662f\u600e\u6837\u7ed3\u8bc6\u7684\uff1f\u4f60\u53ef\u77e5\u9053\u5979\u7684\u5976\u5a18\u662f\u5929\u9b54\u6559\u7684\u5417\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u5979\u7684\u5976\u5a18\u7adf\u662f\u5929\u9b54\u6559\u7684\u9057\u5b7d\u4e48\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u738b\u6cf0\u770b\u51fa\u4f60\u662f\u7ed9\u5929\u9b54\u6559\u4e0b\u7684\u6bd2\uff0c\u4ed6\u89c1\u591a\u8bc6\u5e7f\uff0c\u4e8c\u5341\u5e74\u524d\uff0c\u4e5f\u662f\u548c\u5929\u9b54\u6559\u6253 \u8fc7\u4ea4\u9053\u7684\uff0c\u60f3\u5fc5\u4e0d\u4f1a\u770b\u9519\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u971e\u59d1\u7684\u5976\u5a18\u59d3\u8d3a\uff0c\u6211\u4eec\u53eb\u5979\u505a\u8d3a\u5927\u5988\u3002\u5728\u6b64\u4e4b\u524d\uff0c\u6211\u8fd8\u4e0d\u77e5\u9053\u5979 \u4f1a\u6b66\u529f\u5462\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5929\u9b54\u6559\u867d\u662f\u90aa\u6559\uff0c\u5374\u4e5f\u4e0d\u662f\u65e0\u6076\u4e0d\u4f5c\u7684\u90aa\u6559\u3002\u5389\u5927\u54e5\u7684\u6bcd\u4eb2\u5c31\u662f\u5f53\u5e74\u5929\u9b54 \u6559\u7684\u6559\u4e3b\uff0c\u5979\u4e5f\u65e9\u5df2\u6539\u90aa\u5f52\u6b63\u4e86\u3002\u53ef\u60dc\u54b1\u4eec\u4e0d\u77e5\u5389\u5927\u54e5\u5230\u4e86\u626c\u5dde\u6ca1\u6709\uff0c\u5426\u5219\u628a\u4ed6\u627e\u6765\uff0c\u4e00\u5b9a \u53ef\u4ee5\u7ed9\u4f60\u89e3\u6bd2\u3002\u201d\u53c8\u8bf4\uff1a\u201c\u5929\u9b54\u6559\u5f53\u7136\u5e76\u975e\u5168\u662f\u574f\u4eba\uff0c\u4e0d\u8fc7\u8fd9\u4e2a\u8001\u5996\u5a46\u7684\u624b\u6bb5\u5374\u662f\u53ef\u9119\u53ef \u6068\uff01\u201d \u3000\u3000\u9648\u5149\u7167\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u8d3a\u5927\u5988\u662f\u5929\u9b54\u6559\u7684\u4eba\uff0c\u53c8\u662f\u5982\u6b64\u5de5\u4e8e\u5fc3\u8ba1\uff0c\u8fd9\u5c31\u602a\u4e0d\u5f97 \u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff0c\u201c\u4ec0\u4e48\u602a\u4e0d\u5f97\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4e09\u5e74\u524d\u971e\u59d1\u8981\u4e0e\u6211\u65ad\u7edd\u5f80\u6765\uff0c\u6562\u60c5\u90fd\u662f\u4e3a\u4e86\u5979\u8fd9\u4e2a\u5976\u5a18\u7684\u7f18\u6545\u3002\u201d \u3000\u3000\u5f53\u4e0b\u9648\u5149\u7167\u8bf4\u51fa\u4ed6\u4e0e\u971e\u59d1\u76f8\u8bc6\u7684\u7ecf\u8fc7\uff0c\u90a3\u65f6\u9648\u5149\u7167\u521a\u521a\u51fa\u9053\uff0c\u6709\u4e00\u6b21\u8def\u8fc7\u4eba\u70df\u7a00\u5c11\u7684\u4e71 \u77f3\u8352\u539f\uff0c\u78b0\u4e0a\u51e0\u4e2a\u8d3c\u4eba\u5c3e\u968f\u4e00\u4e2a\u5c11\u5973\uff0c\u4e0d\u4f4f\u53e3\u5730\u8bf4\u4e9b\u4e0d\u4e09\u4e0d\u56db\u7684\u98ce\u8a00\u98ce\u8bed\uff0c\u8bf4\u4ed6\u4eec\u662f\u8c03\u620f\u5417 \u4ed6\u4eec\u5374\u53c8\u53ea\u662f\u52a8\u53e3\u800c\u4e0d\u52a8\u624b\uff0c\u4f46\u82e5\u8bf4\u4ed6\u4eec\u662f\u76f8\u8bc6\u7684\u5417\u5374\u53c8\u4e0d\u50cf\u3002\u56e0\u4e3a\u90a3\u5973\u5b50\u751a\u662f\u7aef\u5e84\uff0c\u800c\u4e14 \u4e00\u76f4\u6ca1\u6709\u7406\u776c\u4ed6\u4eec\u3002 \u3000\u3000\u9648\u5149\u7167\u6478\u4e0d\u6e05\u90a3\u513f\u4e2a\u4eba\u7684\u8def\u9053\uff0c\u4e0d\u8fc7\u770b\u4ed6\u4eec\u90a3\u526f\u4e0b\u6d41\u7684\u6837\u5b50\uff0c\u4e5f\u662f\u5fcd\u4e0d\u4f4f\u5fc3\u4e2d\u6709\u6c14\uff0c\u4e8e \u662f\u5c31\u8dd1\u8fc7\u53bb\u5e72\u6d89\uff0c\u65a5\u8d23\u4ed6\u4eec\u4e0d\u8be5\u8c03\u620f\u826f\u5bb6\u5987\u5973\u3002\u7ed3\u679c\u5f53\u7136\u662f\u5927\u6253\u4e00\u573a\uff0c\u90a3\u51e0\u4e2a\u8d3c\u4eba\u7ed9\u9648\u5149\u7167 \u6740\u5f97\u5934\u7834\u8840\u6d41\uff0c\u5927\u8d25\u800c\u9003\uff0c\u9648\u5149\u7167\u4e5f\u4e2d\u4e86\u5176\u4e2d\u4e00\u4eba\u7684\u6bd2\u9556\u3002 \u3000\u3000\u9648\u5149\u7167\u652f\u6301\u4e0d\u4f4f\uff0c\u987e\u4e0d\u5f97\u548c\u90a3\u5973\u5b50\u8bf4\u8bdd\uff0c\u53ea\u80fd\u53eb\u5979\u5feb\u8d70\u3002\u4e0d\u6599\u90a3\u5973\u5b50\u5ffd\u5730\u5c06\u4ed6\u6309\u4f4f\uff0c\u8bf4 \u9053\uff1a\u2018\u4f60\u522b\u52a8\uff0c\u6211\u7ed9\u4f60\u89e3\u6bd2\u7597\u4f24\u3002\u201d\u5979\u53d6\u51fa\u4e00\u652f\u94f6\u9488\uff0c\u624b\u6cd5\u975e\u5e38\u719f\u7ec3\uff0c\u66ff\u9648\u5149\u7167\u523a\u4e86\u51e0\u5904\u76f8 \u5173\u7684\u7a74\u9053\u7b11\u9053\uff1a\u2018\u8fd9\u70b9\u6bd2\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u4e0d\u7528\u5403\u836f\uff0c\u6211\u770b\u4e5f\u53ef\u4ee5\u597d\u4e86\u3002\u2019\u9648\u5149\u7167\u77e5\u9053\u6709\u4e00\u79cd\u9488 \u7078\u7597\u6bd2\u7684\u6cd5\u95e8\uff0c\u4f46\u4ece\u6ca1\u89c1\u8fc7\uff0c\u8fd9\u4e00\u6b21\u5374\u662f\u4eb2\u8eab\u7ecf\u53d7\u4e86\u3002 \u3000\u3000\u9648\u5149\u7167\u597d\u751f\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4f1a\u7597\u6bd2\uff0c\u60f3\u5fc5\u4e5f\u4f1a\u6b66\u529f\uff0c\u4e3a\u4f55\u4f60\u5fcd\u53d7\u5f97\u90a3\u4e9b\u8d3c\u4eba\u7684\u8c03 \u620f\uff1f\u201d \u3000\u3000\u90a3\u5973\u5b50\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6015\u8fd9\u51e0\u4e2a\u5c0f\u8d3c\uff0c\u53ea\u56e0\u4ed6\u4eec\u662f\u516d\u5408\u5e2e\u7684\u4eba\uff0c\u6211\u4e0d\u60f3\u548c\u516d\u5408\u5e2e\u7ed3\u4e0b\u51a4 \u4ec7\u3002\u4f46\u4f60\u65e2\u7136\u51fa\u4e86\u624b\uff0c\u6211\u4e5f\u5c31\u987e\u4e0d\u4e86\u90a3\u8bb8\u591a\u4e86\u3002\u8bf4\u8001\u5b9e\u8bdd\uff0c\u5982\u679c\u4f60\u521a\u624d\u4e0d\u51fa\u624b\u60e9\u6212\u4ed6\u4eec\uff0c\u7b49 \u5230\u4eca\u665a\uff0c\u6211\u4e5f\u4f1a\u7528\u6211\u81ea\u5df1\u7684\u6cd5\u5b50\u7ed3\u679c\u4ed6\u4eec\u7684\u3002\u201d\u5979\u6ca1\u6709\u8bf4\u51fa\u662f\u4ec0\u4e48\u201c\u6cd5\u5b50\u201d\uff0c\u4f46\u9648\u5149\u7167\u4e5f\u53ef \u4ee5\u731c\u60f3\u5f97\u5230\uff0c\u5979\u591a\u534a\u662f\u8981\u6697\u4e2d\u4e0b\u6bd2\uff0c\u8fd9\u624d\u80fd\u591f\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u5730\u6740\u4e86\u4ed6\u4eec\u3002 \u3000\u3000\u9648\u5149\u7167\u6697\u6697\u9053\u4e86\u4e00\u58f0\u201c\u60ed\u6127\u201d\uff0c\u8fd9\u624d\u77e5\u90a3\u5973\u5b50\u867d\u7136\u6bd4\u4ed6\u5e74\u8f7b\uff0c\u5374\u662f\u6bd4\u4ed6\u8001\u7ec3\u5f97\u591a\u3002 \u3000\u3000\u9648\u5149\u7167\u8bb2\u4e86\u8fd9\u4ef6\u4e8b\u60c5\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u548c\u971e\u59d1\u5c31\u662f\u8fd9\u6837\u76f8\u8bc6\u7684\u3002\u971e\u59d1\u7684\u6027\u60c5\u751a\u662f\u6e29\u67d4\uff0c \u4e00\u70b9\u4e5f\u4e0d\u50cf\u90aa\u6d3e\u4e2d\u4eba\u3002\u6211\u5e38\u5e38\u89c9\u5f97\u5947\u602a\uff0c\u4e3a\u4f55\u50cf\u5979\u8fd9\u6837\u4e00\u4e2a\u597d\u5973\u5b50\uff0c\u5374\u5b66\u5f97\u90aa\u6076\u7684\u4f7f\u6bd2\u529f \u592b\uff0c\u5982\u4eca\u6211\u59cb\u660e\u767d\u4e86\uff0c\u6562\u60c5\u5979\u7684\u4f7f\u6bd2\u529f\u592b\uff0c\u5c31\u662f\u5979\u7684\u5976\u5a18\u6559\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u5979\u7684\u5bb6\u4e16\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u5979\u59d3\u77f3\uff0c\u81ea\u5e7c\u7236\u6bcd\u53cc\u4ea1\u3002\u5979\u662f\u5976\u5a18\u629a\u517b\u6210\u4eba\u7684\u3002\u5979\u6709\u4e2a\u5802\u53d4\u540d\u53eb\u77f3\u8c37\u98ce\uff0c \u5728\u9ed1\u9053\u4e0a\u4e5f\u6709\u70b9\u540d\u6c14\uff0c\u4f46\u5728\u5979\u7236\u6bcd\u53cc\u4ea1\u4e4b\u540e\uff0c\u5c31\u5f88\u5c11\u6765\u5f80\u4e86\uff0c\u91d1\u5144\uff0c\u4f60\u89c9\u5f97\u5979\u7684\u5bb6\u4e16\u5982\u4f55\uff1f \u662f\u4e0d\u5b9a\u6709\u70b9\u7279\u522b\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u7684\u59ec\u4f2f\u4f2f\u5c31\u662f\u4e00\u4e2a\u5c0f\u5077\uff0c\u6211\u4e0d\u4f1a\u770b\u8f7b\u7eff\u6797\u51fa\u8eab\u7684\u3002\u6240\u4ee5\u6211\u5e76\u4e0d\u89c9\u5f97\u5979 \u7684\u5bb6\u4e16\u6709\u4ec0\u4e48\u2018\u7279\u522b\u201d\u3002\u5c31\u53ea\u6015\u5979\u8bf4\u7684\u4e0d\u5c3d\u5c5e\u5b9e\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u53ef\u60dc\u6211\u7239\u7239\u7684\u60f3\u6cd5\u548c\u4f60\u5e76\u4e0d\u4e00\u6837\u3002\u201d \u3000\u3000\u4f59\u9010\u6d41\u9053\uff1a\u201c\u4f2f\u7236\u4e0d\u8bb8\u4f60\u548c\u5979\u8c08\u5a5a\u8bba\u5a36\uff1f\u201d \u3000\u3000\u9648\u5149\u7199\u9053\uff1a\u201c\u6211\u7239\u7239\u4e00\u6765\u5acc\u5979\u51fa\u8eab\u4e0d\u6b63\uff1b\u4e8c\u6765\u53c8\u8bf4\u5979\u6765\u5386\u4e0d\u660e\u3002\u7239\u7239\u8ba4\u4e3a\u4e00\u4e2a\u5e74\u8f7b\u7684\u5973 \u5b50\u64c5\u4e8e\u4f7f\u6bd2\uff0c\u591a\u534a\u4e0d\u4f1a\u662f\u597d\u4eba\u5bb6\u7684\u5973\u5b50\u3002\u8fd8\u662f\u5c11\u60f9\u4e3a\u5999\u3002\u201d \u3000\u3000\u539f\u6765\u9648\u5149\u7167\u7684\u7236\u4eb2\u9648\u5929\u5b87\u662f\u8d35\u5bb6\u516c\u5b50\u51fa\u8eab\uff0c\u867d\u7136\u5230\u4e86\u4ed6\u8fd9\u4e00\u4ee3\u5df2\u7ecf\u4e0d\u518d\u4e3a\u5b98\uff0c\u53d8\u6210\u4e86\u7eaf \u7cb9\u7684\u6b66\u6797\u4eba\u7269\uff0c\u4f46\u95e8\u7b2c\u4e4b\u89c1\u8fd8\u662f\u4e0d\u80fd\u5c3d\u9664\uff0c\u6545\u6b64\u9648\u5929\u5b87\u53ef\u4ee5\u548c\u7eff\u6797\u4e2d\u4eba\u4ea4\u670b\u53cb\uff0c\u5374\u4e0d\u613f\u610f\u6709 \u4e2a\u6765\u5386\u4e0d\u660e\u7684\u5ab3\u5987\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53ea\u8981\u4f60\u4eec\u662f\u771f\u5fc3\u76f8\u7231\uff0c\u4f60\u53c8\u7684\u786e\u76f8\u4fe1\u5f97\u8fc7\u5979\u4e0d\u662f\u574f\u4eba\u7684\u8bdd\uff0c\u53ef\u4ee5\u4e3a\u4f60\u4f5c \u4fdd\uff0c\u8bf4\u670d\u4f2f\u7236\u7b54\u5141\u4f60\u4eec\u7684\u5a5a\u4e8b\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u6211\u5f53\u7136\u4fe1\u5f97\u8fc7\u971e\u59d1\u662f\u4e2a\u597d\u4eba\u3002\u6211\u4e5f\u66fe\u5411\u7239\u7239\u8bf4\u8fc7\uff0c\u6211\u8bf4\u971e\u59d1\u867d\u7136\u64c5\u4e8e\u4f7f \u6bd2\uff0c\u6211\u5374\u4ece\u672a\u89c1\u5979\u5bb3\u8fc7\u597d\u4eba\u3002\u6bd2\u836f\u5c31\u50cf\u5200\u5251\u4e00\u6837\uff0c\u90fd\u662f\u53ef\u4ee5\u7528\u6765\u6740\u4eba\u7684\u3002\u5728\u597d\u4eba\u624b\u91cc\u62ff\u6765\u6740 \u574f\u4eba\uff0c\u90a3\u53c8\u6709\u4f55\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u90a3\u4e48\u4f2f\u7236\u4e4b\u89c1\u5982\u4f55\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u7239\u7239\u62d7\u4e0d\u8fc7\u6211\uff0c\u4ed6\u7b54\u5e94\u5f85\u4ed6\u8c03\u67e5\u6e05\u695a\u4e86\u971e\u59d1\u7684\u5bb6\u4e16\u4e4b\u540e\uff0c\u53ef\u4ee5\u8003\u8651\u4e3a\u6211\u6c42 \u5a5a\u3002\u6211\u77e5\u9053\u7239\u7239\u53ea\u662f\u4e3a\u4e86\u75bc\u6211\u7684\u7f18\u6545\uff0c\u5bf9\u8fd9\u5934\u5a5a\u4e8b\uff0c\u4ed6\u5176\u5b9e\u8fd8\u662f\u5f88\u4e0d\u6ee1\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b54\u9053\uff1a\u201c\u4f60\u7239\u7239\u80af\u8ba9\u6b65\uff0c\u90a3\u5df2\u7ecf\u662f\u5f88\u4e0d\u9519\u7684\u4e86\u3002\u6211\u4e5f\u4e0d\u7528\u518d\u591a\u8bf4\u5566\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u6253\u7834\u4e86\u4e00\u91cd\u969c\u788d\u8fd8\u6709\u4e00\u91cd\u3002\u8d77\u521d\u6211\u4ee5\u4e3a\u53ea\u8981\u7239\u7239\u7b54\u5e94\u4e86\uff0c\u971e\u59d1\u90a3\u8fb9\u60f3\u6765\u662f \u5e94\u8be5\u6ca1\u6709\u95ee\u9898\u7684\u3002\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u9648\u5149\u7167\u89c1\u91d1\u9010\u6d41\u5fae\u7b11\u7684\u770b\u7740\u4ed6\uff0c\u8138\u4e0a\u4e00\u7ea2\uff0c\u63a5\u4e0b\u53bb\u4f4e\u58f0\u8bf4 \u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u6211\u81ea\u4f5c\u591a\u60c5\uff0c\u867d\u7136\u5979\u6ca1\u6709\u660e\u767d\u8bf4\u8fc7\uff0c\u4f46\u5fc3\u91cc\u4e5f\u662f\u611f\u89c9\u5f97\u5230\u7684\uff0c\u6211\u77e5\u9053\u5979\uff0c\u5979\u4e5f \u771f\u5fc3\u7231\u6211\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u90a3\u4e48\u5979\u540e\u6765\u4e3a\u4ec0\u4e48\u53c8\u62d2\u7edd\u4f60\u5462\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u6211\u8ba8\u5f97\u7239\u7239\u7684\u53e3\u98ce\u4e4b\u540e\uff0c\u5c31\u8dd1\u53bb\u627e\u971e\u59d1\u5546\u91cf\u3002\u4ee5\u524d\u6211\u548c\u5979\u603b\u662f\u5728\u5916\u5411\u76f8\u89c1 \u7684\uff0c\u8fd9\u6b21\u8fd8\u662f\u7b2c\u4e00\u6b21\u5230\u5979\u5bb6\u91cc\u627e\u5979\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5979\u77e5\u4e0d\u77e5\u9053\u4f60\u662f\u6765\u5411\u5979\u6c42\u5a5a\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u6211\u5fc3\u91cc\u559c\u6b22\u5979\uff0c\u5979\u5f53\u7136\u662f\u4f1a\u77e5\u9053\u7684\uff0c\u4e0d\u8fc7\uff0c\u6211\u90a3\u6b21\u5230\u6765\uff0c\u5982\u662f\u5927\u5927\u51fa\u5979\u610f \u6599\u4e4b\u5916\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u56e0\u4e3a\u5979\u4ece\u6765\u6ca1\u6709\u8bf7\u8fc7\u6211\u5230\u5979\u5bb6\u91cc\uff0c\u5979\u7684\u4f4f\u5740\u8fd8\u662f\u6211\u8f97\u8f6c\u6253\u542c\u51fa\u6765\u7684\uff0c\u4e0d\u8fc7 \u8fd9\u53ea\u662f\u6211\u5f53\u65f6\u7684\u60f3\u6cd5\uff0c\u73b0\u5728\u60f3\u6765\uff0c\u5979\u5f53\u65f6\u7684\u5927\u611f\u60ca\u8be7\uff0c\u6050\u6015\u8fd8\u4e0d\u4ec5\u4ec5\u662f\u56e0\u4e3a\u6211\u7a81\u5982\u5176\u6765\u7684\u7f18 \u6545\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53ef\u662f\u4e3a\u4e86\u5979\u7684\u5976\u5a18\u4e0d\u559c\u6b22\u4f60\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u672c\u6765\u6211\u4e00\u76f4\u6ca1\u6709\u60f3\u5230\u8fd9\u4e00\u5c42\u7684\uff0c\u4f46\u7ecf\u8fc7\u4e86\u4eca\u665a\u4e4b\u4e8b\uff0c\u6211\u731c\u60f3\u6050\u6015\u4e5f\u53ea\u662f\u4e3a \u4e86\u8fd9\u4e2a\u7f18\u6545\u4e86\u3002\u8981\u4e0d\u7136\u5979\u4e0d\u4f1a\u90a3\u6837\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5979\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u6211\u672c\u4ee5\u4e3a\u5979\u7236\u6bcd\u65e9\u5df2\u6545\uff0c\u867d\u7136\u6709\u4e2a\u5802\u5144\uff0c\u53c8\u4ece\u65e0\u6765\u5f80\uff0c\u5a5a\u59fb\u5927\u4e8b\uff0c\u5e94\u8be5\u53ef \u4ee5\u81ea\u5df1\u4f5c\u4e3b\uff0c\u54ea\u77e5\u6211\u5230\u4e86\u5979\u7684\u5bb6\u91cc\uff0c\u521a\u521a\u60f3\u8981\u5411\u5979\u8bf4\u6765\u610f\uff0c\u5979\u542c\u51fa\u4e86\u4e00\u70b9\u53e3\u98ce\uff0c\u9762\u8272\u90fd\u53d8 \u4e86\uff0c\u5979\u8fde\u5fd9\u4e71\u4ee5\u4ed6\u8bed\uff0c\u53c8\u4e00\u518d\u793a\u610f\u53eb\u6211\u4e0d\u8981\u518d\u8bf4\u4e0b\u53bb\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u89c1\u7740\u4e86\u5979\u7684\u5976\u5a18\u6ca1\u6709\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u89c1\u7740\u4e86\u3002\u6b63\u5f53\u6211\u8981\u8bf4\u5230\u2018\u6b63\u6587\uff01\u7684\u65f6\u5019\uff0c\u5979\u7684\u5976\u5a18\u5c31\u51fa\u6765\u89c1\u6211\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u8001\u5996\u5a46\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u5f53\u65f6\u5979\u5012\u662f\u548c\u548c\u6c14\u6c14\u7684\uff0c\u5012\u8336\u7ed9\u6211\u559d\uff0c\u5411\u6211\u95ee\u957f\u95ee\u77ed\u3002\u4e00\u9762\u53c8\u5938\u8000\u5979\u81ea\u5df1 \u7684\u529f\u52b3\uff0c\u8bf4\u5c0f\u59d0\u662f\u5979\u4e00\u624b\u629a\u517b\u5927\u7684\uff0c\u5979\u6ca1\u513f\u6ca1\u5973\uff0c\u8fd9\u4e00\u751f\u5c31\u53ea\u6709\u4f9d\u9760\u5c0f\u59d0\u4e86\u3002\u5176\u5b9e\u8fd9\u4e9b\u8bdd\u5979 \u4e0d\u7528\u8bf4\uff0c\u6211\u4e5f\u65e9\u5df2\u77e5\u9053\uff1a\u201c \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u540e\u6765\u600e\u6837\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u5979\u5520\u5520\u53e8\u53e8\u7684\u8bf4\u4e2a\u4e0d\u4f11\uff0c\u6211\u66f4\u6ca1\u6709\u673a\u4f1a\u4e0e\u971e\u59d1\u8c08\u8bba\u6211\u4eec\u7684\u4e8b\u60c5\u4e86\u3002\u6211\u53ea\u9053 \u8001\u4eba\u5bb6\u662f\u96be\u514d\u6709\u5520\u53e8\u7684\u6bdb\u75c5\uff0c\u5fc3\u4e2d\u867d\u662f\u5341\u5206\u538c\u70e6\uff0c\u770b\u5728\u971e\u59d1\u7684\u4efd\u4e0a\uff0c\u4e5f\u552f\u6709\u5fcd\u8010\u3002\u6ca1\u6709\u591a \u4e45\uff0c\u971e\u59d1\u7aef\u8336\u9001\u5ba2\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u4e00\u76f4\u6ca1\u673a\u4f1a\u548c\u5979\u8bf4\uff1f\u90a3\u53c8\u600e\u4f1a\u77e5\u9053\u5979\u8981\u4e0e\u4f60\u65ad\u7edd\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u5979\u7aef\u8336\u9001\u5ba2\uff0c\u627e\u5f53\u7136\u662f\u5927\u4e0d\u9ad8\u5174\u3002\u5927\u7ea6\u662f\u6211\u7684\u9762\u8272\u7ed9\u5979\u7684\u5976\u5a18\u770b\u4e86\u51fa\u6765\uff0c \u4e8e\u662f\u5979\u9053\uff1a\u2018\u9648\u516c\u5b50\u8fdc\u9053\u800c\u6765\uff0c\u4f60\u4e5f\u8be5\u9001\u4e00\u9001\u4ed6\u3002\u2019\u5509\uff0c\u5f53\u65f6\u6211\u8fd8\u4ee5\u4e3a\u5979\u6709\u5fc3\u7ed9\u6211\u4e00\u4e2a\u673a \u4f1a\uff0c\u8ba9\u6211\u548c\u971e\u59d1\u5355\u72ec\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u971e\u59d1\u6709\u6ca1\u6709\u5355\u72ec\u9001\u4f60\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u5979\u7684\u5976\u5a18\u53eb\u5979\u6362\u8fc7\u8863\u88f3\uff0c\u624d\u8ba9\u5979\u51fa\u6765\u9001\u5ba2\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u8bf4\uff0c\u8d70\u662f\u8fd9\u8001\u5996\u5a46\u6709\u79c1\u623f\u8bdd\u8981\u53ee\u5631\u4f60\u7684\u971e\u59d1\u4e86\uff0c\u4e0d\u8fc7\uff0c\u4f60\u4eec\u53ef\u4ee5\u5355 \u72ec\u89c1\u9762\uff0c\u603b\u662f\u597d\u4e9b\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u5e0c\u671b\u548c\u5979\u8bf4\u51e0\u53e5\u77e5\u5fc3\u7684\u8bf4\u8bdd\u3002\u53ef\u60dc\u5728\u5355\u72ec\u76f8\u5bf9\u4e4b\u65f6\uff0c\u6211\u542c\u5230\u7684\u5374 \u662f\u4ee4\u4eba\u80a0\u65ad\u7684\u8a00\u8bed\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5979\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u5979\u8981\u6211\u5fd8\u8bb0\u5979\uff0c\u53ea\u5f53\u662f\u4ece\u6765\u6ca1\u6709\u8ba4\u8bc6\u5979\u8fd9\u4e2a\u4eba\u3002\u6211\u8bf4\u9664\u975e\u7b49\u5230\u6211\u547c\u5438\u505c\u6b62 \u4e4b\u65f6\uff0c\u5426\u5219\u6211\u53c8\u600e\u80fd\u5fd8\u8bb0\uff1f\u6211\u53cd\u95ee\u5979\uff1a\u96be\u9053\u4f60\u5c31\u80fd\u591f\u5b8c\u5168\u5fd8\u8bb0\u6211\u4e48\uff1f\u5979\u53f9\u606f\u9053\uff1a\u4f60\u4eca\u5929\u7684\u6765 \u610f\u6211\u5df2\u7ecf\u660e\u767d\uff0c\u6211\u4eec\u662f\u51b3\u4e0d\u80fd\u76f8\u597d\u7684\u3002\u4e0d\u7ba1\u4f60\u80fd\u591f\u5fd8\u8bb0\u4e5f\u597d\uff0c\u4e0d\u80fd\u591f\u5fd8\u8bb0\u4e5f\u597d\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c \u54b1\u4eec\u603b\u662f\u8981\u65ad\u7edd\u7684\u4e86\u3002\u6211\u95ee\u5979\u662f\u4e0d\u662f\u53e6\u5916\u6709\u4e86\u610f\u4e2d\u4eba\uff1f\u5979\u8bf4\u5979\u7ec8\u751f\u4e0d\u4f1a\u518d\u5ac1\uff0c\u6211\u95ee\u5979\uff1a\u90a3\u4e48 \u8fd9\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u4ed6\u8bf4\u4e0d\u4e3a\u4ec0\u4e48\uff0c\u5c31\u4e0d\u613f\u610f\u548c\u6211\u518d\u89c1\u3002\u6211\u8bf4\uff1a\u4f60\u7adf\u662f\u8fd9\u6837\u8ba8\u538c\u6211\u4e48\uff1f\u5979\u54ac\u4e86 \u54ac\u7259\uff0c\u8bf4\u9053\uff1a\u2018\u4e0d\u9519\uff0c\u6211\u662f\u4e0d\u559c\u6b22\u4f60\u4e86\uff0c\u4f60\u53ef\u4ee5\u6b7b\u5fc3\u4e86\u5427\u3002\u2019\u6211\u77e5\u9053\u5979\u662f\u8fdd\u80cc\u81ea\u5df1\u7684\u826f\u5fc3\u8bf4 \u7684\uff0c\u6211\u8bf4\u6211\u4e0d\u76f8\u4fe1\uff0c\u4f60\u4e00\u5b9a\u8981\u544a\u8bc9\u6211\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff1f\u4e3a\u4ec0\u4e48\uff1f\u53ef\u662f\u5979\u5df2\u7ecf\u8dd1\u4e86\uff0c\u5979\u5df2\u7ecf\u56de\u53bb \u4e86\u3002\u5979\u5bb6\u7684\u5927\u95e8\u4e53\u7684\u4e00\u58f0\u54cd\uff0c\u628a\u6211\u5173\u5728\u95e8\u5916\uff0c\u628a\u6211\u7684\u58f0\u97f3\u5173\u5728\u95e8\u5916\uff0c\u5979\u5df2\u7ecf\u4e0d\u8981\u518d\u542c\u6211\u7684\u8bdd \u4e86\u3002\u6211\u6ca1\u6709\u52c7\u6c14\u518d\u95ef\u8fdb\u53bb\u3002\u4e3a\u4ec0\u4e48\uff1f\u4e3a\u4ec0\u4e48\uff1f\u8fd9\u4e2a\u7591\u95ee\u76f4\u5230\u4eca\u5929\u90fd\u6ca1\u6709\u5f97\u5230\u89e3\u7b54\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\u3002\u6211\u73b0\u5728\u5c31\u53bb\u7ed9\u4f60\u7d22\u53d6\u7b54\u6848\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u65e9\u5df2\u5411\u738b\u6cf0\u6253\u542c\u4e86\u66f9\u5bb6\u7684\u5730\u5740\uff0c\u4e8e\u662f\u7acb\u5373\u51fa\u57ce\uff0c\u5f84\u5954\u66f9\u5bb6\u3002\u66f9\u5bb6\u5728\u6d4e\u5357\u57ce\u897f\uff0c\u501a \u5c71\u800c\u7acb\uff0c\u8fdc\u8fdc\u7684\u5c31\u53ef\u4ee5\u770b\u89c1\u201c\u5927\u5b66\u58eb\u5e9c\u201d\u56db\u4e2a\u91d1\u78a7\u8f89\u714c\u7684\u5927\u5b57\uff0c\u91d1\u9010\u6d41\u56db\u66f4\u51fa\u57ce\uff0c\u4e00\u53e3\u6c14\u8dd1 \u4e86\u5341\u591a\u91cc\uff0c\u5230\u4e86\u66f9\u5bb6\uff0c\u5929\u8fd8\u672a\u4eae\u3002 \u3000\u3000\u5927\u95e8\u5916\u6709\u56db\u4e2a\u536b\u58eb\u4ea4\u53c9\u5de1\u903b\uff0c\u91d1\u9010\u6d41\u62fe\u8d77\u4e00\u9897\u5c0f\u5c0f\u7684\u77f3\u5b50\uff0c\u5411\u7a7a\u4e2d\u4e00\u5f39\uff0c\u5f15\u5f97\u56db\u4e2a\u536b\u58eb \u4ef0\u5934\u89c2\u770b\uff0c\u91d1\u9010\u6d41\u4e00\u4e2a\u98de\u8eab\uff0c\u5df2\u662f\u6500\u7740\u74e6\u6a90\uff0c\u8fc5\u5373\u5c31\u8df3\u8fc7\u5899\u5934\u53bb\u4e86\u3002\u90a3\u9897\u5c0f\u77f3\u5b50\u98de\u4e0a\u6811\u68a2\uff0c \u60ca\u8d77\u4e86\u6811\u4e0a\u5bbf\u9e1f\uff0c\u56db\u4e2a\u536b\u58eb\u7591\u795e\u7591\u9b3c\uff0c\u53c8\u6015\u8001\u662f\u8fdb\u53bb\u7980\u62a5\u7684\u8bdd\uff0c\u4e07\u4e00\u67e5\u4e0d\u51fa\u4ec0\u4e48\uff0c\u5927\u62a4\u9662\u5b9a \u8981\u8d23\u602a\u4ed6\u4eec\u201c\u5eb8\u4eba\u81ea\u6270\u201d\u3002\u65e2\u7136\u4e0d\u6562\u65ad\u5b9a\u6709\u4eba\uff0c\u6240\u4ee5\u4e5f\u5c31\u4e0d\u613f\u58f0\u5f20\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8fdb\u4e86\u82b1\u56ed\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u661f\u7f57\u68cb\u5e03\u7684\u623f\u5b50\u5927\u5927\u5c0f\u5c0f\uff0c\u4f55\u6b62\u767e\u95f4\uff0c\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u64d2 \u8d3c\u5148\u64d2\u738b\uff0c\u6349\u4f4f\u4e86\u66f9\u632f\u955b\u90a3\u5b9d\u8d1d\u513f\u5b50\uff0c\u4e0d\u6101\u903c\u4e0d\u51fa\u89e3\u836f\u6765\u3002\u53ef\u662f\u90a3\u5c0f\u5b50\u7a76\u7adf\u662f\u4f4f\u5728\u54ea\u95f4\u623f\u5b57 \u5462\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u6b63\u5728\u76d8\u7b97\u7528\u4ec0\u4e48\u6cd5\u5b50\u6253\u63a2\u6700\u597d\uff0c\u5ffd\u542c\u5f97\u9644\u8fd1\u4e00\u5904\u5047\u5c71\u540e\u9762\u6709\u6089\u6089\u7d22\u7d22\u7684\u58f0\u97f3\uff0c\u91d1 \u9010\u6d41\u8d77\u521d\u8fd8\u4ee5\u4e3a\u662f\u5b88\u591c\u7684\u5bb6\u4e01\uff0c\u6084\u6084\u5730\u8d70\u8fc7\u53bb\uff0c\u53ea\u89c1\u4e00\u7537\u4e00\u5973\uff0c\u8863\u88f3\u4e0d\u6574\uff0c\u5934\u53d1\u84ec\u677e\u7684\u4ece\u5c71 \u6d1e\u91cc\u94bb\u51fa\u6765\uff0c\u5973\u7684\u8bf4\u9053\uff1a\u201c\u5929\u5feb\u4eae\u4e86\uff0c\u4f60\u8d76\u5feb\u56de\u53bb\u5427\u3002\u201d\u539f\u6765\u662f\u66f9\u5bb6\u7684\u4e00\u4e2a\u4e2b\u5934\u548c\u4e00\u4e2a\u5c0f\u5b50 \u5728\u5c71\u6d1e\u5e7d\u4f1a\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fcd\u4f4f\u4e86\u7b11\uff0c\u84e6\u5730\u4e00\u628a\u5c06\u90a3\u5c0f\u5b50\u63ea\u4f4f\uff01\u6b63\u662f\uff1a \u3000\u3000\u65e0\u7aef\u6765\u6076\u5ba2\uff0c\u60ca\u6563\u91ce\u9e33\u9e2f\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516d\u56de\u3000\u6bd2\u9152\u788e\u60c5\u6006\u5f80\u4e8b\u3000\u826f\u5bb5\u60ca\u68a6\u6664\u4f73\u4eba \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e8c\u5341\u516d\u56de\u3000\u6bd2\u9152\u788e\u60c5\u6006\u5f80\u4e8b\u3000\u826f\u5bb5\u60ca\u68a6\u6664\u4f73\u4eba \u3000\u3000\u90a3\u4e2b\u5934\u5413\u5f97\u614c\u4e86\uff0c\u5f20\u5927\u4e86\u5634\u5df4\u60f3\u53eb\uff0c\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u53eb\u5427\uff0c\u4f60\u4e00\u53eb\uff0c\u5927\u5bb6\u90fd\u4f1a\u6765\u770b\u628a \u620f\u4e86\u3002\u201d\u5c0f\u4e2b\u5934\u8fd9\u624d\u7701\u8d77\u81ea\u5df1\u662f\u5728\u5e7d\u4f1a\uff0c\u5343\u4e07\u4e0d\u80fd\u8ba9\u4eba\u77e5\u9053\u5979\u7684\u5978\u60c5\uff0c\u8fde\u5fd9\u95ed\u4e86\u5634\u5df4\uff0c\u6d51\u8eab \u76f4\u6253\u54c6\u55e6\u3002 \u3000\u3000\u90a3\u5c0f\u5b50\u5012\u662f\u6bd4\u8f83\u9547\u5b9a\uff0c\u7ed9\u91d1\u9010\u6d41\u63ea\u4f4f\u4e86\uff0c\u5e76\u4e0d\u600e\u4e48\u614c\u5f20\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u54e5\uff0c\u522b\u5f00\u73a9\u7b11\u4e86\u3002 \u4f60\u8981\u591a\u5c11\u94f6\u5b50\uff0c\u5f00\u53e3\u5427\uff01\u201d\u539f\u6765\u8fd9\u5c0f\u5b50\u8fd8\u4ee5\u4e3a\u91d1\u9010\u6d41\u4e0d\u77e5\u662f\u54ea\u4e00\u95f4\u623f\u7684\u5c0f\u53ae\uff0c\u649e\u7834\u4e86\u4ed6\u7684\u5978 \u60c5\uff0c\u60f3\u8981\u52d2\u7d22\u4ed6\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u628a\u4ed6\u8f6c\u4e86\u4e2a\u8eab\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u770b\u770b\u6211\u662f\u8c01\uff1f\u6211\u624d\u6ca1\u5de5\u592b\u548c\u4f60\u5f00\u73a9\u7b11\u5462\uff01\u201d\u8fd9\u5c0f\u5b50\u770b \u6e05\u695a\u4e86\u662f\u4e00\u4e2a\u4ed6\u4e0d\u8ba4\u8bc6\u7684\u4eba\uff0c\u8fd9\u624d\u614c\u4e86\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff0c\u4f60\u8981\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u662f\u4f60\u5bb6\u66f9\u5927\u5c11\u7237\u7684\u670b\u53cb\u3002\u4f60\u542c\u7740\uff0c\u6211\u73b0\u5728\u8981\u53bb\u627e\u4ed6\uff0c\u4f60\u5e26\u6211\u53bb\uff01\u5426\u5219\u6211 \u5c31\u628a\u4f60\u4eec\u4e24\u4e2a\u7f1a\u5728\u8fd9\u513f\uff0c\u8ba9\u4eba\u5bb6\u6765\u770b\u628a\u620f\u3002\u201d \u3000\u3000\u8fd9\u5c0f\u5b50\u751a\u662f\u673a\u4f36\uff0c\u5f53\u7136\u4e0d\u76f8\u4fe1\u91d1\u9010\u6d41\u662f\u5c11\u7237\u7684\u670b\u53cb\uff0c\u4f46\u5728\u91d1\u9010\u6d41\u631f\u5236\u4e4b\u4e0b\uff0c\u5374\u4e5f\u4e0d\u6562\u4e0d \u4ece\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u6562\u5e26\u4f60\u53bb\u89c1\u5c11\u7237\uff0c\u53ea\u80fd\u544a\u8bc9\u4f60\u4ed6\u4f4f\u5728\u4ec0\u4e48\u5730\u65b9\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\u3002\u4f46\u4f60\u53ef\u4e0d\u80fd\u8bf4\u8c0e\uff0c\u4f60\u8bf4\u8c0e\u6211\u4e5f\u6709\u529e\u6cd5\u6cbb\u4f60\u3002\u201d\u8bf4\u7f62\u628a\u90a3\u5c0f\u5b50\u7684\u5916\u8863\u8131 \u4e86\u4e0b\u6765\uff0c\u53c8\u53d6\u4e86\u90a3\u4e2b\u5934\u7684\u7cfb\u8170\u5dfe\uff0c\u7528\u4e00\u5757\u5927\u77f3\u538b\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u662f\u8bf4\u8c0e\u9a97\u6211\uff0c\u6211\u7acb\u5373\u628a\u4f60 \u7684\u5978\u60c5\u63ed\u9732\uff0c\u77f3\u5934\u4e0b\u7684\u4e1c\u897f\u5c31\u662f\u8bc1\u7269\u3002\u4f60\u8bf4\u7684\u82e5\u662f\u5b9e\u8bdd\uff0c\u6211\u4e0d\u58f0\u5f20\uff0c\u8fc7\u540e\u4f60\u53ef\u4ee5\u6084\u6084\u5730\u6398\u51fa \u6765\u3002\u201d\u8fd9\u5757\u5927\u77f3\u5934\u5c11\u8bf4\u4e5f\u6709\u51e0\u767e\u65a4\u91cd\u3002\u91d1\u9010\u6d41\u91cf\u90a3\u5c0f\u4e2b\u5934\u4e5f\u642c\u4e0d\u5f00\u5b83\u3002 \u3000\u3000\u90a3\u5c0f\u5b50\u89c1\u91d1\u9010\u6d41\u7684\u6c14\u529b\u5982\u6b64\u4e4b\u5927\uff0c\u66f4\u662f\u5403\u60ca\uff0c\u614c\u4e0d\u8fed\u5730\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u8981\u505a\u4eba\u5462\uff0c\u6211\u600e\u6562 \u9a97\u4f60\uff0c\u4e5f\u5e78\u4e8f\u4f60\u662f\u78b0\u4e0a\u4e86\u6211\uff0c\u522b\u4eba\u8fd8\u672a\u5fc5\u77e5\u9053\u5c11\u7237\u6240\u5728\u5462\u3002\u4f60\u8ddf\u6211\u6765\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8ddf\u90a3\u5c0f\u5b50\u7ed5\u8fc7\u5047\u5c71\uff0c\u7a7f\u8fc7\u82b1\u5f84\uff0c\u8f6c\u4e86\u51e0\u4e2a\u5f2f\uff0c\u8d70\u5230\u4e00\u5ea7\u7ea2\u697c\u524d\u9762\u3002\u90a3\u5c0f\u5b50\u4f4e\u58f0\u8bf4 \u9053\uff1a\u201c\u5c11\u7237\u5728\u8fd9\u697c\u4e0a\uff0c\u8fd9\u662f\u6700\u5f97\u5ba0\u7684\u4e09\u59e8\u592a\u7684\u623f\u95f4\u3002\u201d\u539f\u6765\u548c\u4ed6\u76f8\u597d\u90a3\u4e2b\u5934\u5c31\u662f\u670d\u4f8d\u8fd9\u4e2a\u4e09 \u59e8\u592a\u7684\u5a62\u5973\uff0c\u6628\u665a\u5979\u670d\u4f8d\u5c11\u7237\u5165\u623f\u7761\u89c9\u4e4b\u540e\uff0c\u624d\u6e9c\u6b21\u6765\u548c\u8fd9\u5c0f\u5b50\u5e7d\u4f1a\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u56de\u53bb\u5427\u3002\u4e0b\u6b21\u53ef\u8981\u66f4\u5c0f\u5fc3\u4e86\u3002\u201d\u5f53\u4e0b\u65bd\u5c55\u4e00\u9e64\u51b2\u5929\u7684\u8f7b\u529f\uff0c\u6084\u65e0 \u58f0\u7684\u5c31\u4e0a\u4e86\u697c\u3002 \u3000\u3000\u91d1\u9010\u6d41\u65e9\u5df2\u5f97\u4e86\u795e\u5077\u59ec\u6653\u98ce\u7684\u8863\u94b5\u771f\u4f20\uff0c\u623f\u95e8\u867d\u7136\u5728\u91cc\u9762\u95e9\u4e0a\uff0c\u91d1\u9010\u6d41\u628a\u5b83\u5f04\u5f00\u5374\u4e5f\u662f \u6613\u5982\u53cd\u638c\u3002\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u66f9\u5927\u5c11\u7237\uff0c\u8be5\u8d77\u5e8a\u5566\uff01\u201d\u63ea\u4e0b\u5e10\u5b50\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u5987\u4eba\u7684\u58f0\u97f3\u8bf4 \u9053\uff1a\u201c\u4f60\uff0c\u4f60\u56de\u6765\u5566\uff01\u54ce\u5440\uff0c\u4f60\uff0c\u4f60\u662f\u2026\u2026\u201d\u91d1\u9010\u6d41\u4e00\u628a\u63a9\u7740\u5979\u7684\u5634\u5df4\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5728\u7b49 \u8c01\uff1f\u201d\u539f\u6765\u5e8a\u4e0a\u53ea\u6709\u4e09\u59e8\u592a\uff0c\u5e76\u65e0\u5927\u5c11\u7237\u3002 \u3000\u3000\u90a3\u5987\u4eba\u65b9\u59cb\u542c\u51fa\u662f\u4e2a\u964c\u751f\u7684\u58f0\u97f3\uff0c\u5413\u5f97\u6d51\u8eab\u53d1\u6296\uff0c\u8bed\u4e0d\u6210\u58f0\u7684\u4ece\u7259\u7f1d\u4e2d\u5410\u51fa\u6765\uff1a\u201c\u4f60\u3001 \u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u71c3\u8d77\u706b\u6298\uff0c\u5728\u5979\u9762\u95e8\u4e00\u6643\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u662f\u8c01\uff1f\u201d\u90a3\u5987\u4eba\u4e0d\u77e5\u91d1\u9010\u6d41\u610f\u6b32\u5982 \u4f55\uff0c\u6ee1\u9762\u901a\u7ea2\u7684\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u8bf7\u3001\u8bf7\u597d\u6c49\u653e\u8fc7\u6211\u5427\uff0c\u4f60\u3001\u4f60\u82e5\u8981\u94b1\uff0c\u5c3d\u3001\u5c3d\u597d\u5546\u91cf\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u6014\u4e86\u4e00\u6014\uff0c\u4f1a\u8fc7\u610f\u6765\uff0c\u201c\u5478\u201d\u7684\u5550\u5979\u4e00\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5f53\u6211\u662f\u91c7\u82b1\u8d3c\u4e48\uff1f\u6211\u4ec0\u4e48 \u90fd\u4e0d\u8981\uff0c\u53ea\u8981\u4f60\u8bf4\u5b9e\u8bdd\uff01\u5426\u5219\uff0c\u563f\uff0c\u563f\uff0c\u4f60\u53ef\u83ab\u602a\u6211\u8981\u4f60\u597d\u770b\u3002\u201d \u3000\u3000\u8fd9\u201c\u597d\u770b\u201d\u4e8c\u5b57\u542b\u4e49\u751a\u5e7f\uff0c\u53ef\u80fd\u662f\u8fd9\u6837\u7684\u51cc\u8fb1\uff0c\u53ef\u80fd\u662f\u90a3\u6837\u7684\u51cc\u8fb1\u3002\u90a3\u5987\u4eba\u60ca\u7591\u4e0d\u5b9a\uff0c \u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\uff0c\u6211\u4ee5\u4e3a\u662f\u5927\u5c11\u7237\u56de\u6765\u3002\u201d\u91d1\u9010\u6d41\u7684\u7b2c\u4e00\u53e5\u95ee\u8bdd\u8fd9\u624d\u5f97\u5230\u7b54 \u590d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e0d\u662f\u5728\u7b49\u5978\u592b\uff0c\u5927\u5c11\u7237\u6628\u665a\u786e\u662f\u7761\u5728\u4f60\u7684\u623f\u4e2d\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u90a3\u5c0f \u5b50\u5012\u662f\u6ca1\u6709\u9a97\u6211\u3002\u5982\u4eca\u603b\u53ef\u4ee5\u67e5\u51fa\u4ed6\u7684\u4e0b\u843d\u4e86\u3002\u201d \u3000\u3000\u90a3\u5987\u4eba\u9762\u7ea2\u7ea2\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5927\u5c11\u7237\u5462\uff1f\u201d \u3000\u3000\u90a3\u5987\u4eba\u9053\uff1a\u201c\u56db\u66f4\u5927\u7684\u65f6\u5206\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u201c\u53bb\u54ea\u513f\uff1f\u201d \u3000\u3000\u201c\u4ed6\u8bf4\u662f\u53bb\u770b\u4e00\u4f4d\u8d3a\u5927\u5a18\uff0c\u662f\u548c\u4ec0\u4e48\u516d\u5408\u5e2e\u6709\u5173\u7cfb\u7684\uff0c\u6211\u4e5f\u5f04\u4e0d\u6e05\u695a\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u559c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u90a3\u8001\u5996\u5987\u679c\u7136\u662f\u8eb2\u5230\u8fd9\u513f\u6765\u4e86\u3002\u201d\u91d1\u9010\u6d41\u7b97\u4e00\u7b97\u65f6\u95f4\uff0c\u90a3\u5976 \u5a18\u662f\u4e09\u66f4\u65f6\u5206\u7ed9\u4ed6\u6253\u5f97\u843d\u6c34\u800c\u9003\u7684\uff0c\u9003\u5230\u66f9\u5bb6\uff0c\u5927\u7ea6\u4e5f\u5e94\u8be5\u662f\u56db\u66f4\u7684\u65f6\u5019\u4e86\u3002\u201c\u5979\u82e5\u4e0d\u662f\u5df2 \u53d7\u4e86\u4f24\uff0c\u5c31\u4e00\u5b9a\u662f\u6709\u7d27\u8981\u7684\u4e8b\u60c5\u6025\u5f85\u5546\u91cf\uff0c\u5426\u5219\u4e0d\u4f1a\u628a\u8fd9\u4f4d\u66f9\u5927\u5c11\u7237\u4eba\u70ed\u70d8\u70d8\u7684\u88ab\u7a9d\u91cc\u62d6\u8d77 \u6765\u3002\u201d\u91d1\u9010\u6d41\u5fc3\u60f3\u3002 \u3000\u3000\u201c\u90a3\u8d3a\u5927\u5a18\u53c8\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u3002\u5c11\u7237\u5e76\u672a\u5bf9\u6211\u8bf4\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4ed6\u4e0d\u8bf4\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\u4ed6\u60ef\u5e38\u53bb\u4f1a\u5ba2\u7684\u5730\u65b9\u3002\u544a\u8bc9\u4f60\uff0c\u6211\u6b63\u662f\u8981\u627e\u8fd9\u4e2a\u8d3a \u5927\u5a18\uff0c\u4f60\u4e0d\u8bf4\u6211\u53ea\u597d\u62d6\u7740\u4f60\u966a\u6211\u4e00\u540c\u53bb\u6211\u4e86\uff01\u201d \u3000\u3000\u90a3\u5987\u4eba\u600e\u80af\u51fa\u4e56\u9732\u4e11\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u8d3a\u5927\u5a18\u662f\u4e24\u4e2a\u62a4\u9662\u966a\u5979\u6765\u7684\uff0c\u60f3\u5fc5\u662f\u5728\u56ed \u4e2d\u7684\u7fe0\u5fae\u8f69\u3002\u7fe0\u5fae\u8f69\u5728\u56ed\u5b50\u4e1c\u8fb9\uff0c\u540e\u9762\u6709\u4e24\u5ea7\u5047\u5c71\uff0c\u524d\u9762\u6709\u4e2a\u8377\u5858\uff0c\u5f88\u5bb9\u6613\u627e\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\uff0c\u6211\u82e5\u662f\u627e\u4e0d\u7740\u5979\u518d\u6765\u95ee\u4f60\u3002\u4f60\u7ee7\u7eed\u7761\u4f60\u7684\u89c9\u5427\u3002\u201d\u90a3\u5987\u4eba\u5fc3\u60f3\uff1a\u201c\u7ed9 \u4f60\u8fd9\u4e48\u4e00\u95f9\uff0c\u6211\u54ea\u8fd8\u80fd\u591f\u518d\u7761\uff1f\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u89c9\u80c1\u4e0b\u4e00\u9ebb\uff0c\u91d1\u9010\u6d41\u5df2\u662f\u70b9\u4e86\u5979\u7684\u6655\u7761\u7a74\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6b63\u8981\u8d70\u5f00\uff0c\u84e6\u5730\u53c8\u5f97\u4e86\u4e2a\u4e00\u4e3b\u610f\uff1a\u201c\u89e3\u836f\u4e0d\u77e5\u80fd\u5426\u5230\u624b\uff0c\u6211\u4e14\u91cd\u65bd\u6545\u6280\uff0c\u6349\u5f04\u4ed6 \u4eec\u4e00\u4e0b\u3002\u201d\u4e8e\u662f\u6413\u4e0b\u4e00\u56e2\u6ce5\u57a2\uff0c\u585e\u5165\u90a3\u5987\u4eba\u53e3\u4e2d\u3002\u4ed6\u66fe\u7ecf\u7528\u8fc7\u8fd9\u4e2a\u6cd5\u5b50\u5413\u8fc7\u6587\u9053\u5e84\uff0c\u6548\u679c\u5f88 \u662f\u4e0d\u9519\uff0c\u56e0\u6b64\u5982\u4eca\u53c8\u518d\u7528\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u653e\u4e0b\u90a3\u4e2a\u5a46\u5a18\uff0c\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u53c8\u6e9c\u51fa\u53bb\u3002\u5728\u56ed\u4e2d\u6253\u4e86\u4e00\u8f6c\uff0c\u679c\u7136\u5728\u8377\u5858\u65c1\u8fb9\u627e \u7740\u4e86\u90a3\u5ea7\u7fe0\u5fae\u8f69\u3002 \u3000\u3000\u521a\u8d70\u8fd1\u7fe0\u5fae\u8f69\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u719f\u6089\u7684\u58f0\u97f3\u7b11\u9053\uff1a\u201c\u5e2e\u4e3b\u8981\u6211\u6765\u8c22\u5927\u5a92\uff0c\u60f3\u4e0d\u5230\u4f60\u8fd9\u4f4d\u5a92\u5a46 \u5148\u53d8\u6210\u843d\u6c64\u9e21\u4e86\u3002\u4f46\u4f60\u4e3a\u5e2e\u4e3b\u8fd9\u6837\u5c3d\u529b\uff0c\u5e2e\u4e3b\u4e00\u5b9a\u4e0d\u4f1a\u4e8f\u5f85\u4f60\u7684\u3002\u201d\u91d1\u9010\u6d41\u5fc3\u5934\u4e00\u51db\uff0c\u60f3 \u9053\uff1a\u201c\u591a\u4e86\u8fd9\u4e2a\u8d3c\u5a46\u5a18\uff0c\u7528\u786c\u529f\u6050\u6015\u662f\u4ed8\u4e0d\u4e86\u597d\u4e86\u3002\u201d\u539f\u6765\u8bf4\u8bdd\u8fd9\u4e2a\u4eba\uff0c\u6b63\u662f\u516d\u5408\u5e2e\u4e2d\u7684\u8463 \u5341\u4e09\u5a18\u3002\u5728\u516d\u5408\u5e2e\u4e2d\uff0c\u8463\u5341\u4e09\u5a18\u7684\u6b66\u529f\u4ec5\u6b21\u4e8e\u53f2\u767d\u90fd\uff0c\u4e0e\u91d1\u9010\u6d41\u4e5f\u76f8\u5dee\u4e0d\u4e86\u591a\u5c11\u3002 \u3000\u3000\u6b64\u65f6\u5df2\u662f\u5929\u8499\u8499\u7684\u65f6\u5206\uff0c\u7fe0\u5fae\u8f69\u4e2d\u4e5f\u8fd8\u70b9\u7740\u706f\u70db\uff0c\u91d1\u9010\u6d41\u8eb2\u5728\u5047\u5c71\u540e\u9762\uff0c\u5077\u5077\u5730\u671b\u8fdb \u53bb\uff0c\u53ea\u89c1\u5c4b\u5b50\u91cc\u9ed1\u538b\u538b\u7684\u6324\u6ee1\u4e86\u4eba\uff0c\u90a3\u8d3a\u5927\u5a18\u8eba\u5728\u80e1\u5e8a\u4e0a\uff0c\u5728\u5979\u5468\u56f4\u7684\u6709\u90a3\u4f4d\u66f9\u5bb6\u7684\u5927\u5c11\u7237 \u66f9\u901a\uff0c\u6709\u66f9\u5bb6\u7684\u5927\u62a4\u9662\u5f6d\u5de8\u5d02\uff0c\u6709\u516d\u5408\u5e2e\u7684\u8463\u5341\u4e09\u5a18\uff0c\u8fd8\u6709\u66f9\u5bb6\u65b0\u8bf7\u6765\u7684\u90a3\u4e24\u4e2a\u9ed1\u9053\u4e0a\u7684\u4eba \u7269\u7530\u5cfb\u548c\u9b4f\u501a\u3002 \u3000\u3000\u8d3a\u5927\u5a18\u9053\u4e86\u4e00\u58f0\uff1a\u201c\u60ed\u6127\uff01\u201d\u8bf4\u9053\uff1a\u201c\u59d3\u91d1\u90a3\u5c0f\u5b50\u59d4\u5b9e\u4e86\u5f97\uff0c\u6628\u665a\u6211\u4eec\u90fd\u6298\u5728\u4ed6\u7684\u624b\u91cc \u4e86\u3002\u201d\u5f6d\u5de8\u5d02\u9053\uff1a\u201c\u6211\u4eec\u6298\u5728\u4ed6\u7684\u624b\u91cc\u8fd8\u4e0d\u6253\u7d27\uff0c\u542c\u8bf4\u53f2\u5927\u5e2e\u4e3b\u4e5f\u5f88\u5403\u4e86\u4ed6\u7684\u4e8f\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u7b11\u9053\uff1a\u201c\u53ea\u8981\u8fd9\u4e2a\u5a92\u505a\u5f97\u6210\uff0c\u53f2\u5e2e\u4e3b\u4e00\u5b9a\u4f1a\u7ed9\u4f60\u4eec\u51fa\u8fd9\u53e3\u6c14\u3002\u59d3\u91d1\u8fd9\u5c0f\u5b50\u672c\u9886 \u867d\u7136\u4e0d\u9519\uff0c\u60f3\u6bd4\u6211\u4eec\u7684\u5e2e\u4e3b\u8fd8\u5dee\u5f97\u8fdc\u5462\uff0c\u6211\u7684\u5e2e\u4e3b\u4e0d\u8fc7\u662f\u56e0\u4e3a\u6709\u66f4\u7d27\u8981\u7684\u4e8b\u60c5\uff0c\u4e00\u65f6\u672a\u80fd\u5f97 \u53ca\u7406\u4f1a\u4ed6\u7f62\u4e86\u3002\u201d \u3000\u3000\u66f9\u901a\u5fd9\u4e0d\u8fed\u7684\u5949\u627f\u9053\uff1a\u201c\u5f53\u7136\uff0c\u5f53\u7136\uff0c\u516d\u5408\u5e2e\u662f\u6c5f\u6e56\u4e0a\u6570\u4e00\u6570\u4e8c\u7684\u5927\u5e2e\u4f1a\uff0c\u4eba\u624d\u6d4e\u6d4e\uff0c \u9ad8\u624b\u5982\u4e91\uff0c\u5c82\u4ff1\u4e00\u4e2a\u4e73\u81ed\u672a\u5e72\u7684\u5c0f\u5b50\uff1f\u4e0d\u5fc5\u5e2e\u4e3b\u4eb2\u6765\uff0c\u6709\u4f60\u8463\u5341\u4e09\u5a18\u51fa\u9a6c\u5df2\u7ecf\u8db3\u4ee5\u5bf9\u4ed8\u8fd9\u4e2a \u5c0f\u5b50\u4e86\u3002\u4e0d\u8fc7\uff0c\u8bdd\u8bf4\u56de\u6765\uff0c\u516d\u5408\u5e2e\u867d\u7136\u4e0d\u4ff1\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\uff0c\u6709\u8fd9\u5c0f\u5b50\u4ece\u4e2d\u6363\u4e71\uff0c\u603b\u662f\u8ba8\u538c\uff0c \u4e0d\u5982\u8d81\u65e9\u5c06\u4ed6\u9664\u6389\uff0c\u5927\u5bb6\u53ef\u4ee5\u5b89\u5fc3\u3002\u8fd9\u5c0f\u5b50\u73b0\u5728\u6d4e\u5357\u7684\u4e10\u5e2e\u5206\u8235\uff0c\u6211\u4eec\u5df2\u7ecf\u6253\u542c\u6e05\u695a\uff0c\u81ea\u4e0b \u5728\u4ed6\u4eec\u90a3\u8fb9\u65e0\u751a\u80fd\u4eba\uff0c\u91d1\u9010\u6d41\u90a3\u4f4d\u670b\u53cb\u5df2\u53d7\u4e86\u4f24\uff0c\u8235\u4e3b\u738b\u6cf0\u4e0d\u8fc7\u662f\u4e8c\u4e09\u6d41\u7684\u89d2\u8272\uff0c\u5018\u82e5\u8981\u9664 \u6389\u91d1\u9010\u6d41\uff0c\u76ee\u524d\u6b63\u662f\u4e2a\u673a\u4f1a\u3002\u201d \u3000\u3000\u539f\u6765\u66f9\u901a\u56e0\u4e3a\u4e0a\u6b21\u5403\u4e86\u91d1\u9010\u6d41\u7684\u5927\u4e8f\uff0c\u628a\u91d1\u9010\u6d41\u6068\u5f97\u5165\u9aa8\u3002\u4ed6\u662f\u4e2a\u6709\u8eab\u5bb6\u7684\u4eba\uff0c\u4e0d\u6562\u62db \u60f9\u4e10\u5e2e\uff0c\u662f\u4ee5\u60f3\u6002\u607f\u8463\u5341\u4e09\u5a18\u51fa\u5934\uff0c\u7528\u516d\u5408\u5e2e\u7684\u540d\u4e49\u53bb\u548c\u4e10\u5e2e\u4f5c\u5bf9\u3002 \u3000\u3000\u6b8a\u4e0d\u77e5\u8463\u5341\u4e09\u5a18\u4e5f\u662f\u5403\u8fc7\u91d1\u9010\u6d41\u7684\u4e8f\u7684\uff0c\u5c3d\u7ba1\u5979\u5927\u8a00\u5556\u5556\uff0c\u5bf9\u91d1\u9010\u6d41\u4e0e\u4e10\u5e2e\u4e5f\u4e0d\u80fd\u4e0d\u987e \u5fcc\u51e0\u5206\uff0c\u66f9\u901a\u60f3\u6002\u607f\u5979\u51fa\u5934\uff0c\u5979\u53ef\u4e0d\u8096\u8f7b\u6613\u4e0a\u8fd9\u4e2a\u5f53\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u7b11\u9053\uff1a\u201c\u66f9\u516c\u5b50\u4e0d\u5fc5\u7740\u6025\uff0c\u6211\u4eec\u7684\u5e2e\u4e3b\u7b97\u51c6\u4e86\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\u4e00\u5b9a\u4f1a\u5230\u626c\u5dde\u53bb \u7684\uff0c\u5230\u4e86\u6211\u4eec\u7684\u5730\u5934\uff0c\u8fd8\u6015\u4ed6\u9003\u5f97\u51fa\u6211\u4eec\u7684\u624b\u5fc3\u4e48\uff1f\u4f55\u987b\u5728\u8fd9\u91cc\u6253\u8349\u60ca\u86c7\uff1f\u5728\u8fd9\u91cc\u4ed6\u6709\u4e10\u5e2e \u505a\u62a4\u7b26\uff0c\u4eba\u53bb\u5c11\u4e86\u4e0d\u6d4e\u4e8b\uff0c\u53bb\u591a\u5462\uff0c\u6211\u4eec\u7684\u5e2e\u4e3b\u73b0\u5728\u6b63\u5fd9\u4e8e\u66ff\u4ed6\u59b9\u59b9\u529e\u5a5a\u4e8b\uff0c\u6682\u65f6\u53ea\u6015\u4e5f\u8c03 \u4e0d\u51fa\u4eba\u6765\u3002\u201d \u3000\u3000\u66f9\u901a\u6b63\u8981\u501a\u4ed7\u516d\u5408\u5e2e\uff0c\u542c\u5f97\u8463\u5341\u4e09\u5a18\u8fd9\u4e48\u8bf4\uff0c\u5927\u4e3a\u5931\u671b\uff0c\u4f46\u4e5f\u4e0d\u4fbf\u76f8\u5f3a\uff0c\u4e8e\u662f\u8baa\u8baa\u8bf4 \u9053\uff1a\u201c\u54e6\uff0c\u53f2\u5e2e\u4e3b\u7684\u59b9\u59b9\u8981\u51fa\u9601\u4e86\u4e48\uff1f\u4e0d\u77e5\u662f\u8bb8\u7ed9\u54ea\u4e00\u5bb6\u7684\u7537\u513f\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u897f\u661f\u5c06\u519b\u5e05\u5b5f\u96c4\u3002\u201d \u3000\u3000\u8d3a\u5927\u5a18\u6709\u70b9\u7591\u60d1\u7684\u795e\u6c14\uff0c\u95ee\u9053\uff1a\u201c\u8bf7\u6055\u6211\u5192\u6627\uff0c\u6211\u5012\u542c\u5f97\u4e00\u4e2a\u8c23\u8a00\uff0c\u8bf4\u662f\u8d35\u5e2e\u4e3b\u8981\u628a\u59b9 \u59b9\u8bb8\u7ed9\u4e00\u4e2a\u59d3\u5389\u7684\u5c11\u5e74\uff0c\u8fd9\u4eba\u662f\u5927\u9b54\u6559\u7684\u65b0\u6559\u4e3b\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u7b11\u9053\uff1a\u201c\u8d3a\u5927\u5a18\uff0c\u4f60\u5f88\u5173\u5fc3\u4f60\u4eec\u7684\u65b0\u6559\u4e3b\u5427\uff1f\u4e0d\u8fc7\uff0c\u636e\u6211\u6240\u77e5\uff0c\u90a3\u59d3\u5389\u7684\u5c0f\u5b50 \u5e76\u65e0\u610f\u4e8e\u91cd\u671d\u5929\u9b54\u6559\uff0c\u9633\u6d69\u90a3\u5e2e\u4eba\u4e5f\u5e76\u4e0d\u662f\u771f\u7684\u60f3\u62e5\u4ed6\u505a\u6559\u4e3b\u7684\u3002\u201d \u3000\u3000\u8d3a\u5927\u5a18\u751f\u6015\u89c1\u7591\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u524d\uff0c\u5389\u590d\u751f\u592b\u5987\u4e0d\u542c\u6211\u4eec\u4e4b\u529d\uff0c\u628a\u504c\u5927\u7684\u4e00\u4e2a\u5929 \u9b54\u6559\u89e3\u6563\u4e86\u3002\u5929\u9b54\u6559\u7684\u65e7\u4eba\u5bf9\u4ed6\u4eec\u65e9\u5df2\u662f\u5931\u671b\u900f\u9876\uff0c\u5c31\u662f\u4ed6\u4eec\u56de\u6765\uff0c\u6211\u4eec\u4e5f\u4e0d\u80fd\u518d\u8981\u4ed6\u4eec\u505a \u6559\u4e3b\u4e86\u3002\u4f55\u51b5\u662f\u4ed6\u4eec\u7684\u513f\u5b50\uff1f\u66f4\u4f55\u51b5\u8fd9\u59d3\u5389\u7684\u5c0f\u5b50\u4e5f\u4e0d\u77e5\u662f\u5426\u5c31\u771f\u7684\u662f\u4ed6\u4eec\u7684\u513f\u5b50\u5462\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u5bf9\u5440\uff0c\u8d3a\u5927\u5a18\uff0c\u4e0d\u662f\u6211\u5949\u627f\u4f60\uff0c\u82e5\u7136\u53ef\u4ee5\u91cd\u7ec4\u5929\u9b54\u6559\u7684\u8bdd\uff0c\u4f60\u5c31\u5f88\u6709\u8d44 \u683c\u53ef\u4ee5\u505a\u6559\u4e3b\u3002\u4f55\u987b\u8ba9\u7ed9\u4e00\u4e2a\u540e\u751f\u5c0f\u5b50\uff1f\u201d \u3000\u3000\u8d3a\u5927\u5a18\u5927\u4e3a\u9ad8\u5174\uff0c\u8bf4\u9053\uff1a\u201c\u82e5\u7136\u5929\u9b54\u6559\u91cd\u7ec4\u6210\u529f\uff0c\u6211\u4eec\u5fc5\u5b9a\u552f\u8d35\u5e2e\u5e2e\u4e3b\u9a6c\u9996\u662f\u77bb\u3002\u90a3 \u4e48\uff0c\u8bdd\u8bf4\u56de\u6765\uff0c\u6211\u542c\u5230\u7684\u90a3\u4ef6\u4e8b\u60c5\uff0c\u679c\u7136\u662f\u8c23\u8a00\u4e86\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u7b11\u9053\uff1a\u201c\u4e5f\u4e0d\u5168\u662f\u8c23\u8a00\u3002\u4e0d\u8fc7\u662f\u6211\u4eec\u7684\u5e2e\u4e3b\u54c4\u90a3\u5c0f\u5b50\u559c\u6b22\uff0c\u8981\u4ed6\u6765\u4e0a\u5f53\u7684\u3002\u5c31 \u50cf\u4f60\u4eca\u665a\u54c4\u90a3\u59d3\u9648\u7684\u5c0f\u5b50\u4e00\u6837\u3002\u201d \u3000\u3000\u8d3a\u5927\u5a18\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u90a3\u5c0f\u5b50\u600e\u4e48\u914d\u5f97\u4e0a\u4f60\u4eec\u5e2e\u4e3b\u7684\u59b9\u59b9\u5462\uff1f\u539f\u6765\u662f\u8fd9\u6837\u3002\u201d \u3000\u3000\u66f9\u901a\u4e0d\u5591\u6c5f\u6e56\u4e0a\u7684\u4e8b\u60c5\uff0c\u4e5f\u4e0d\u77e5\u5979\u4eec\u6240\u8bf4\u7684\u90a3\u201c\u59d3\u5389\u7684\u5c0f\u5b50\u201d\u662f\u4ec0\u4e48\u4eba\uff0c\u4f46\u201c\u897f\u661f\u5c06\u519b \u5e05\u5b5f\u96c4\u201d\u4ed6\u662f\u77e5\u9053\u7684\uff0c\u5fd9\u4e0d\u8fed\u7684\u5df4\u7ed3\u9053\uff1a\u201c\u5e05\u5c06\u519b\u6b63\u662f\u6df1\u5f97\u7687\u4e0a\u501a\u91cd\u7684\u680b\u6881\uff0c\u53f2\u5e2e\u4e3b\u7ed3\u4e86\u8fd9 \u5934\u4eb2\u5bb6\uff0c\u53ef\u771f\u662f\u95e8\u5f53\u6237\u5bf9\uff0c\u5929\u4f5c\u4e4b\u5408\u4e86\uff01\u4f55\u65e5\u4f73\u671f\uff0c\u8bf7\u65e9\u901a\u77e5\uff0c\u6211\u4e00\u5b9a\u8981\u9001\u4e00\u4efd\u5927\u793c\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u7b11\u9053\uff1a\u201c\u8fd8\u65e9\u7740\u5462\u3002\u6211\u4eec\u7684\u5e2e\u4e3b\u6709\u4e2a\u79c1\u5fc3\uff0c\u4ed6\u5012\u662f\u60f3\u8ba9\u6211\u4eec\u5148\u559d\u4ed6\u7684\u559c\u9152\uff0c\u7136 \u540e\u624d\u529e\u559c\u4e8b\uff0c\u4e0d\u8fc7\u8fd9\u4e2a\u5982\u610f\u7b97\u76d8\u6253\u4e0d\u6253\u5f97\u901a\uff0c\u8fd9\u53ef\u5c31\u8981\u5168\u770b\u8d3a\u5927\u5a18\u4e86\u3002\u201d \u3000\u3000\u8d3a\u5927\u5a18\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u5305\u5728\u6211\u7684\u8eab\u4e0a\u3002\u5c0f\u59d0\u662f\u6211\u517b\u5927\u7684\uff0c\u6211\u7b54\u5e94\u4e86\uff0c\u5979\u4e0d\u80fd\u4e0d\u7b54\u5e94\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u5230\u8fd9\u91cc\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u679c\u7136\u8fd9\u8001\u5996\u5a46\u662f\u60f3\u628a\u971e\u59d1\u5ac1\u7ed9\u53f2\u767d\u90fd\uff0c\u602a\u4e0d\u5f97\u5979\u4eca\u665a\u8981 \u5bf9\u9648\u5927\u54e5\u4e0b\u6bd2\u624b\u3002\u201d \u3000\u3000\u6b64\u65f6\u5929\u8272\u5df1\u4eae\uff0c\u4e24\u4e2a\u5c0f\u4e2b\u5934\u6c14\u6025\u8d25\u574f\u5730\u8dd1\u6765\uff0c\u5979\u4eec\u5df2\u53d1\u73b0\u4e86\u4e09\u59e8\u592a\u53d7\u4eba\u6697\u7b97\uff0c\u662f\u4ee5\u8dd1\u6765 \u7ed9\u5c11\u7237\u62a5\u8baf\u7684\u3002\u201d \u3000\u3000\u8fd9\u4e2b\u5934\u4e00\u6765\uff0c\u91d1\u9010\u6d41\u7684\u884c\u85cf\u5c31\u8981\u8d25\u9732\u3002\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u654c\u4f17\u6211\u5be1\uff0c\u53ef\u5fc5\u987b\u5148\u4e0b\u624b\u4e3a \u5f3a\u4e86\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u8463\u5341\u4e09\u5a18\u5df2\u5728\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u66f9\u901a\u9694\u7740\u7a97\u5b50\u77a7\u89c1\u4e86\u90a3\u4e2a\u4e2b\u5934\uff0c\u7b11 \u9053\uff1a\u201c\u662f\u670d\u4f8d\u5c0f\u59be\u7684\u6625\u5170\u3002\u54a6\uff0c\u6625\u5170\uff0c\u4f60\u8dd1\u6765\u5e72\u561b\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u5ffd\u5728\u53eb\u9053\uff1a\u201c\u4e0d\u5bf9\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u4e00\u679a\u77f3\u5b50\u5df2\u662f\u63b7\u4e86\u8fdb \u6765\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u4e00\u638c\u628a\u66f9\u901a\u63a8\u5f00\uff0c\u5f6d\u5de8\u5d02\u7ad9\u5728\u66f9\u901a\u80cc\u540e\uff0c\u4f38\u624b\u4e00\u63a5\uff0c\u4ed6\u662f\u7ec3\u6709\u91d1\u521a\u638c\u529b\u7684\uff0c\u4e0d \u6599\u4ecd\u662f\u7ed9\u77f3\u5b50\u6253\u5f97\u638c\u5fc3\u706b\u8fa3\u8fa3\u4f5c\u75db\u3002\u5f6d\u5de8\u5d02\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u4e00\u5b9a\u662f\u90a3\u5c0f\u5b50\u6765\u4e86\uff01\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u8fde\u5fd9\u51b2\u51fa\uff0c\u53ea\u89c1\u91d1\u9010\u6d41\u7ad9\u5728\u5047\u5c71\u4e0a\u54c8\u54c8\u5927\u7b11\uff0c\u8463\u5341\u4e09\u5a18\u6012\u9053\uff1a\u201c\u597d\u5440\uff0c\u679c\u7136\u662f \u4f60\u8fd9\u5c0f\u5b50\uff01\u201d\u91d1\u9010\u6d41\u5c45\u9ad8\u4e34\u4e0b\uff0c\u4e00\u5251\u6311\u5f00\u8463\u5341\u4e09\u5a18\u7684\u957f\u97ad\uff0c\u7b11\u9053\uff1a\u201c\u7701\u5f97\u4f60\u56de\u626c\u5dde\u7b49\u6211\uff0c\u4e0d \u5f88\u597d\u4e48\u3002\u201d \u3000\u3000\u5f6d\u5de8\u5d02\u6284\u8d77\u7985\u6756\uff0c\u559d\u9053\uff1a\u201c\u597d\u5927\u80c6\u7684\u5c0f\u5b50\uff0c\u6628\u665a\u8ba9\u4f60\u4fa5\u5e78\u9003\u8131\uff0c\u5982\u4eca\u53ef\u8981\u53eb\u4f60\u6765\u5f97\u53bb\u4e0d \u5f97\u4e86\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u662f\u4e48\uff1f\u53ef\u6211\u8fd8\u4e0d\u60f3\u8dd1\u5462\uff01\u201d\u5f6d\u5de8\u5d02\u4e00\u62db\u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u6325\u6756\u4ef0\u653b\uff0c\u91d1 \u9010\u6d41\u5530\u5530\u4e24\u5251\uff0c\u4ece\u4e0a\u9762\u523a\u4e0b\u6765\uff0c\u5f6d\u5de8\u5d02\u7acb\u8db3\u4e0d\u7a33\uff0c\u9000\u540e\u4e24\u6b65\u3002\u91d1\u9010\u6d41\u5251\u950b\u4e00\u8f6c\uff0c\u53c8\u628a\u8463\u5341\u4e09 \u5a18\u7684\u957f\u97ad\u62e8\u8fc7\u4e00\u8fb9\u3002\u672c\u6765\u5f6d\u3001\u8463\u4e8c\u4eba\u8054\u624b\uff0c\u662f\u53ef\u4ee5\u80dc\u5f97\u91d1\u9010\u6d41\u7684\uff0c\u53ea\u56e0\u91d1\u9010\u6d41\u5c45\u9ad8\u4e34\u4e0b\uff0c\u5360 \u4e86\u5730\u5229\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u4ed6\u4eec\u653b\u4e0d\u4e0a\u53bb\uff0c\u53cd\u800c\u662f\u91d1\u9010\u6d41\u5360\u4e86\u4e0a\u98ce\u4e86\u3002 \u3000\u3000\u90a3\u4e2b\u5934\u8dd1\u8fdb\u7fe0\u5fae\u8f69\uff0c\u6c14\u547c\u547c\u5730\u62a5\u9053\uff1a\u201c\u516c\u5b50\uff0c\u4e0d\u597d\uff01\u201d\u66f9\u901a\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u597d\uff1f\u201d\u5c0f\u4e2b\u5934 \u9053\uff1a\u201c\u4e09\u59e8\u5927\u53e3\u5410\u767d\u6cab\uff0c\u4e0d\u4f1a\u52a8\u4e86\uff01\u201d\u66f9\u901a\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u5fd9\u4e0d\u8fed\u5730\u95ee\u9053\uff1a\u201c\u53ef\u8fd8\u6709\u6c14 \u606f\uff1f\u201d\u5c0f\u4e2b\u9b1f\u9053\uff1a\u201c\u6c14\u606f\u5012\u6709\uff0c\u53ea\u662f\u4e0d\u4f1a\u52a8\u4e5f\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u597d\u4f3c\u4e2d\u4e86\u90aa\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4e0d\u7792\u4f60\u8bf4\uff0c\u4f60\u90a3\u4f4d\u5982\u82b1\u4f3c\u7389\u7684\u7f8e\u4eba\u513f\uff0c\u6211\u7ed9\u5979\u670d\u4e86\u4e00\u9897\u5c0f\u5c0f\u7684\u4e38\u836f\uff0c\u4e00 \u65f6\u662f\u6b7b\u4e0d\u4e86\u7684\uff0c\u4e0d\u8fc7\u518d\u8fc7\u4e24\u4e2a\u65f6\u8fb0\uff0c\u6211\u53ef\u5c31\u4e0d\u80fd\u62c5\u4fdd\u5979\u4e0d\u7389\u6b92\u9999\u6d88\u4e86\uff01\u201d \u3000\u3000\u66f9\u901a\u53c8\u60ca\u53c8\u6012\uff0c\u9694\u7740\u7a97\u5b50\u9a82\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u6562\u5bb3\u6211\u5fc3\u7231\u59ec\u4eba\u3002\u6211\u8981\u4f60\u7684\u6027\u547d\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u8981\u5979\u6027\u547d\u7684\u8bdd\uff0c\u53ef\u8fd8\u5f97\u6c42\u6211\u5462\uff01\u4f60\u53eb\u90a3\u8001\u5996\u5a46\u628a\u89e3\u836f\u62ff\u6765\u4e0e\u6211\u4ea4 \u6362\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u4ee5\u4e3a\u91cd\u65bd\u6545\u6280\uff0c\u4e5f\u53ef\u4ee5\u50cf\u90a3\u6b21\u606b\u5413\u6587\u9053\u5e84\u4e00\u6837\uff0c\u8feb\u4f7f\u66f9\u901a\u4f9d\u4ed6\u6761\u4ef6\uff0c\u4e0d\u6599\u8d3a\u5927\u5a18 \u5374\u662f\u4e2a\u4f7f\u6bd2\u7684\u5927\u884c\u5bb6\uff0c\u4e00\u542c\u90a3\u5c0f\u4e2b\u5934\u6240\u8bf4\u7684\u60c5\u5f62\uff0c\u5c31\u77e5\u66f9\u901a\u7684\u4e09\u59e8\u592a\u53ea\u662f\u7ed9\u4eba\u70b9\u4e86\u7a74\u9053\uff0c\u800c \u975e\u4e2d\u6bd2\u3002\u8d3a\u5927\u5a18\u51b7\u7b11\u9053\uff1a\u201c\u66f9\u516c\u5b50\u4e0d\u5fc5\u7406\u4ed6\uff0c\u83ab\u8bf4\u4e0d\u662f\u4e2d\u6bd2\uff0c\u5c31\u662f\u771f\u7684\u4e2d\u6bd2\uff0c\u4e5f\u6ca1\u6709\u6211\u89e3\u4e0d \u4e86\u7684\uff01\u201d\u66f9\u901a\u89c1\u5979\u8bf4\u5f97\u5982\u6b64\u80af\u5b9a\uff0c\u653e\u4e0b\u4e86\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4ed6\u8981\u4e0d\u4e86\u5c0f\u59be\u7684\u547d\uff0c\u6211\u53ef\u5c31\u8981\u4ed6 \u7684\u547d\u4e86\uff01\u201d \u3000\u3000\u7530\u5cfb\u3001\u9b4f\u501a\u4e8c\u4eba\uff0c\u542c\u4e86\u4e3b\u5b50\u7684\u610f\u601d\uff0c\u4e0d\u5f85\u5429\u5490\uff0c\u6284\u8d77\u4e86\u5175\u5668\uff0c\u4fbf\u5411\u91d1\u9010\u6d41\u5954\u53bb\u3002\u8d3a\u5927\u5a18 \u4e5f\u6276\u7740\u62d0\u6756\u5dc5\u5dcd\u5dcd\u5730\u8d70\u6765\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u8981\u5728\u6211\u7684\u8ddf\u524d\u5356\u5f04\u4f7f\u6bd2\u7684\u529f\u592b\uff0c\u8fd9\u53eb\u505a\u5173 \u516c\u5e99\u524d\u800d\u5927\u5200\uff0c\u4e0d\u77e5\u81ea\u91cf\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5728\u4e24\u5927\u9ad8\u624b\u5939\u653b\u4e4b\u4e0b\uff0c\u4f18\u52bf\u9010\u6e10\u6d88\u5931\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4eca\u665a\u6050\u6015\u662f\u8ba8\u4e0d\u4e86\u4fbf\u5b9c\u4e86\uff0c \u4e14\u5148\u56de\u53bb\uff0c\u518d\u60f3\u529e\u6cd5\u3002\u201d\u9661\u5730\u4e00\u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\uff0c\u7ffb\u8fc7\u5047\u5c71\u3002\u8eab\u5f62\u79fb\u52a8\u4e4b\u65f6\uff0c\u4e00\u638c\u62cd\u51fa\uff0c \u5c06\u5047\u5c71\u4e0a\u9762\u7684\u4e00\u5757\u5927\u77f3\u5934\u63a8\u4e0b\u6765\u3002 \u3000\u3000\u5f6d\u5de8\u5d02\u6325\u6756\u4e00\u51fb\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u5c06\u77f3\u5934\u6311\u8fc7\u4e00\u8fb9\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u91d1\u9010\u6d41\u5df2\u662f\u4e0b\u4e86\u5047 \u5c71\uff0c\u94bb\u8fdb\u82b1\u5f84\uff0c\u4ece\u8fd9\u6761\u82b1\u5f84\u53ef\u4ee5\u901a\u5411\u56ed\u95e8\u3002 \u3000\u3000\u8d3a\u5927\u5a18\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4eba\u8fc7\u897f\u8fb9\u5835\u622a\uff01\u201d\u7530\u5cfb\u3001\u9b4f\u501a\u6b63\u5728\u82b1\u5f84\u4e24\u8fb9\u5305\u6284\uff0c\u542c\u5f97\u6b64\u8a00\uff0c\u4e0d \u89c9\u4e00\u6014\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u79bb\u5f00\u8fd9\u6761\u8def\uff0c\u8fd9\u4e0d\u662f\u6709\u610f\u653e\u4ed6\u8d70\u5417\uff1f\u201d\u4f46\u56e0\u8d3a\u5927\u5a18\u662f\u66f9\u5bb6\u7684\u8d35\u5bbe\uff0c\u8fd9 \u4e24\u4eba\u53ea\u597d\u4f9d\u8a00\u884c\u4e8b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6012\u9053\uff1a\u201c\u6211\u504f\u8981\u5728\u5173\u516c\u5e99\u524d\u800d\u5927\u5200\uff01\u201d\u811a\u5c16\u4e00\u70b9\uff0c\u7fe9\u5982\u98de\u9e1f\u7684\u5411\u8d3a\u5927\u5a18\u51b2\u6765\u3002\u91d1 \u9010\u6d41\u662f\u60f3\u7528\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6bb5\uff0c\u5c06\u8d3a\u5927\u5a18\u4fd8\u4e3a\u4eba\u8d28\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u8d3a\u5927\u5a18\u51b7\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u8981\u62fc\u547d\u5440\uff01\u4e24\u4eba\u8ddd\u79bb\u5728\u5341\u4e08\u5f00\u5916\uff0c\u91d1\u9010\u6d41 \u8eab\u6cd5\u867d\u5feb\uff0c\u603b\u4e0d\u80fd\u4e00\u4e0b\u5b50\u6765\u5230\u5979\u7684\u9762\u524d\uff0c\u53ea\u89c1\u5979\u628a\u624b\u4e00\u626c\uff0c\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u56e2\u70df\u96fe\uff0c\u5df2\u662f \u5411\u91d1\u9010\u6d41\u7b3c\u7f69\u4e0b\u6765\u3002\u70df\u96fe\u4e2d\u91d1\u5149\u95ea\u70c1\uff0c\u53d1\u51fa\u201c\u55e4\u55e4\u201d\u58f0\u54cd\u3002 \u3000\u3000\u8fd9\u4e2a\u6697\u5668\u540d\u53eb\u201c\u6bd2\u96fe\u91d1\u9488\u70c8\u7130\u5f39\u201d\uff0c\u91d1\u9010\u6d41\u66fe\u89c1\u53f2\u7ea2\u82f1\u4f7f\u8fc7\uff0c\u8bc6\u5f97\u5389\u5bb3\u3002\u614c\u5fd9\u5012\u7eb5\u907f \u5f00\u3002\u8d3a\u5927\u5a18\u8fde\u53d1\u4e09\u679a\u6697\u5668\uff0c\u82b1\u5f84\u5df2\u662f\u85cf\u8eab\u4e0d\u4f4f\u3002\u5317\u9762\u662f\u5185\u9662\u7684\u56f4\u5899\uff0c\u9000\u8fdb\u5185\u9662\u4e43\u662f\u81ea\u9677\u7262 \u7b3c\uff1b\u5357\u9762\u53c8\u662f\u8377\u5858\uff0c\u91d1\u9010\u6d41\u65e0\u8def\u53ef\u8d70\uff0c\u903c\u5f97\u9000\u5411\u897f\u8fb9\u3002 \u3000\u3000\u7530\u5cfb\u3001\u9b4f\u501a\u6b63\u597d\u5728\u8fd9\u4e00\u8fb9\u7b49\u7740\u4ed6\uff0c\u4e00\u4e2a\u821e\u8d77\u94fe\u5b50\u9524\uff0c\u4e00\u4e2a\u6325\u52a8\u85e4\u86c7\u68d2\uff0c\u9f50\u58f0\u559d\u9053\uff1a\u201c\u597d \u5c0f\u5b50\uff0c\u5f80\u54ea\u91cc\u8dd1\uff1f\u8bdd\u72b9\u672a\u4e86\uff0c\u8463\u5341\u4e09\u5a18\u4e0e\u5f6d\u5de8\u5d02\u4e5f\u5df2\u8ffd\u4e0a\u6765\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53ef\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u5408\u56f4\u3002\u201d\u5530\u5530\u4e24\u5251\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u5411\u4e24\u4e2a\u654c \u4eba\u540c\u65f6\u65bd\u5c55\u6740\u624b\uff0c\u53ef\u662f\u7530\u5cfb\u3001\u9b4f\u501a\u4ea6\u975e\u6cdb\u6cdb\u4e4b\u8f88\uff0c\u91d1\u9010\u6d41\u8bba\u672c\u9886\u53ef\u4ee5\u6253\u8d25\u4ed6\u4eec\uff0c\u4f46\u5374\u4e5f\u4e0d\u80fd \u4e09\u62db\u4e24\u5f0f\u53d6\u80dc\u3002 \u3000\u3000\u773c\u770b\u5f6d\u3001\u8463\u4e8c\u4eba\u5c31\u8981\u6765\u5230\uff0c\u7530\u5cfb\u5ffd\u5730\u201c\u554a\u5440\u201d\u4e00\u58f0\u5012\u4e86\u4e0b\u53bb\u3002\u9b4f\u501a\u9a82\u9053\uff1a\u201c\u5988\u5df4\u5b50\u7684\uff0c \u4f60\u8fd9\u5c0f\u5b50\u6697\u3001\u6697\u2026\u201c\u6697\u5668\u201d\u4e8c\u5b57\u8fd8\u672a\u66fe\u8bf4\u5f97\u51fa\u6765\uff0c\u5df2\u662f\u7740\u4e86\u91d1\u9010\u6d41\u4e00\u5251\uff0c\u8eab\u5f62\u6643\u4e86\u4e24\u6643\uff0c\u8ddf \u7740\u4e5f\u5012\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5927\u4e3a\u5947\u602a\uff0c\u60f3\u9053\uff1a\u201c\u662f\u8c01\u5077\u653e\u6697\u5668\u52a9\u6211\uff1f\u201d\u4f46\u6b64\u65f6\u4ea6\u5df2\u65e0\u6687\u5bfb\u89c5\u4e86\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u6781\u4e3a\u4e86\u5f97\uff0c\u4e00\u628a\u91d1\u9488\u5411\u4e24\u8fb9\u6d12\u51fa\uff0c\u957f\u97ad\u547c\u547c\u98ce\u54cd\uff0c\u6253\u5230\u4e86\u91d1\u9010\u6d41\u7684\u540e\u5fc3\u3002 \u3000\u3000\u91d1\u9010\u6d41\u53cd\u624b\u4e00\u5251\uff0c\u628a\u957f\u97ad\u62e8\u5f00\uff0c\u8bf4\u8fdf\u65f6\uff0c\u90a3\u65f6\u5feb\uff0c\u5f6d\u5de8\u5d02\u7684\u94c1\u6756\u53c8\u5df2\u6253\u5230\uff0c\u91d1\u9010\u6d41\u4f7f\u51fa \u7edd\u9876\u8f7b\u529f\uff0c\u547c\u5730\u8dc3\u8d77\u4e09\u4e08\u591a\u9ad8\uff0c\u811a\u5c16\u5728\u6756\u5934\u4e0a\u8f7b\u8f7b\u4e00\u8e0f\uff0c\u8eab\u5f62\u5df2\u662f\u5012\u7eb5\u51fa\u4e09\u4e08\u5f00\u5916\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u8d5e\u4e86\u4e00\u4e2a\uff1a\u201c\u597d\u201d\u5b57\uff0c\u731b\u5730\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u8dd1\u4e0d\u4e86\u5566\uff01\u201d\u4eba\u8fd8\u672a\u5230\uff0c\u957f\u97ad\u547c \u547c\u98ce\u54cd\uff0c\u5374\u5df2\u970d\u5730\u5377\u6765\u3002\u539f\u6765\u5979\u7684\u8f7b\u529f\u867d\u7136\u6bd4\u4e0d\u4e0a\u91d1\u9010\u6d41\uff0c\u4f46\u5374\u5360\u4e86\u5175\u5668\u7684\u4fbf\u5b9c\uff0c\u5979\u7684\u8f6f\u97ad \u53ef\u4ee5\u6253\u5230\u4e8c\u4e08\u8fdc\u8fd1\uff0c\u91d1\u9010\u6d41\u4e00\u7eb5\u4e09\u4e08\uff0c\u5979\u53ea\u987b\u8de8\u4e0a\u4e24\u6b65\uff0c\u957f\u97ad\u5c31\u53ef\u4ee5\u6253\u5230\u91d1\u9010\u6d41\u7684\u540e\u5fc3\u3002 \u3000\u3000\u91d1\u9010\u6d41\u811a\u5c16\u521a\u521a\u7740\u5730\uff0c\u8eb2\u95ea\u4e0d\u5f00\uff0c\u53ea\u597d\u56de\u8eab\u5e94\u6218\u3002\u53ea\u662f\u6162\u4e86\u7247\u523b\uff0c\u5f6d\u5de8\u5d02\u53c8\u5df2\u4ece\u4fa7\u9762\u6284 \u6765\uff0c\u622a\u4e86\u4ed6\u7684\u53bb\u8def\u3002\u91d1\u9010\u6d41\u6012\u9053\uff1a\u201c\u8d3c\u5a46\u5a18\uff0c\u9634\u9b42\u4e0d\u6563\uff01\u201d\u8463\u5341\u4e09\u5a18\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7f20\u4e0a\u4e86 \u4f60\u5566\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u75be\u653b\u51e0\u62db\uff0c\u5c06\u5f6d\u5de8\u5d02\u8feb\u9000\u4e86\u51e0\u6b65\uff0c\u53ef\u662f\u8463\u5341\u4e09\u5a18\u7684\u957f\u97ad\u592d\u77eb\u5982\u9f99\uff0c\u5140\u662f\u7d27\u7f20\u4e0d \u820d\u3002\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u97ad\u7a0d\u8fc7\u5904\uff0c\u91d1\u9010\u6d41\u7684\u4e00\u5e45\u8863\u88f3\uff0c\u5316\u4f5c\u4e86\u7247\u7247\u8774\u8776\u3002\u5e78\u800c\u4ed6\u7684\u201c\u5929\u7f57\u6b65 \u6cd5\u201d\u8d8b\u95ea\u5f97\u5b9c\uff0c\u53ea\u662f\u8863\u88f3\u7834\u788e\uff0c\u5c1a\u672a\u4f24\u7740\u76ae\u8089\u3002\u91d1\u9010\u6d41\u80cc\u8179\u53d7\u654c\u3002\u5012\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u6697\u81ea\u60f3 \u9053\uff1a\u201c\u90a3\u8001\u5996\u5a46\u82e5\u662f\u518d\u6765\u52a9\u9635\uff0c\u6211\u53ef\u5c31\u8981\u7cdf\u4ed6\u5988\u7684\u5927\u7cd5\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e00\u54ac\u7259\u6839\uff0c\u6b63\u5f85\u65bd\u5c55\u4e24\u8d25\u4ff1\u4f24\u7684\u5251\u6cd5\uff0c\u5ffd\u542c\u5f97\u8d3a\u5927\u5a18\u201c\u54a6\u201d\u7684\u4e00\u58f0\uff0c\u58f0\u97f3\u4e2d\u5145\u6ee1 \u8be7\u5f02\uff0c\u91d1\u9010\u6d41\u62bd\u773c\u4e00\u671b\uff0c\u53ea\u89c1\u5979\u6b63\u5728\u628a\u7530\u5cfb\uff0c\u9b4f\u501a\u4e8c\u4eba\u62c9\u8d77\u6765\uff0c\u4f3c\u662f\u53d1\u73b0\u4e86\u4ec0\u4e48\u4e0d\u5bf9\uff0c\u4e00\u624b \u62c9\u7740\u4e00\u4eba\uff0c\u5446\u5728\u90a3\u513f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u5ff5\u4e00\u52a8\uff1a\u201c\u6562\u60c5\u5979\u5df2\u77e5\u9053\u4e86\u53d1\u6697\u5668\u7684\u662f\u8c01\uff0c\u5979\u5bf9\u8fd9\u4eba\u9887\u4e3a\u5fcc\u60ee\uff1f\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c \u5ffd\u53c8\u542c\u5f97\u4eba\u58f0\u9f0e\u6cb8\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u4e0d\u597d\uff01\u5feb\u6765\u6551\u706b\uff01\u201d\u91d1\u9010\u6d41\u628a\u773c\u671b\u53bb\uff0c\u6b63\u662f\u5728\u4ed6\u521a\u624d\u51fa \u6765\u7684\u90a3\u4e2a\u5730\u65b9\u2014\u2014\u66f9\u901a\u7684\u4e09\u59e8\u592a\u6240\u4f4f\u7684\u90a3\u5ea7\u697c\u5b87\uff0c\u706b\u5934\u5df2\u7ecf\u70e7\u4e86\u8d77\u6765\u3002 \u3000\u3000\u66f9\u901a\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u53ea\u6015\u4ed6\u90a3\u5b9d\u8d1d\u59ec\u4eba\u906d\u4eba\u6240\u5bb3\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5f6d\u5148\u751f\uff0c\u4f60\u56de\u6765\uff0c\u6551\u706b \u8981\u7d27\uff01\u201d \u3000\u3000\u5f6d\u5de8\u5d02\u4e0e\u8463\u5341\u4e09\u5a18\u90fd\u4e0d\u7981\u5206\u4e86\u5206\u795e\uff0c\u91d1\u9010\u6d41\u54c8\u54c8\u4e00\u7b11\uff0c\u4ee5\u95ea\u7535\u822c\u7684\u624b\u6cd5\u4e00\u6301\u97ad\u68a2\uff0c\u628a\u8463 \u5341\u4e09\u5a18\u7684\u957f\u97ad\u7f20\u4e0a\u4e86\u5f6d\u5de8\u5d02\u7684\u94c1\u6756\uff0c\u5f6d\u5de8\u5d02\u529b\u5927\uff0c\u8463\u5341\u4e09\u5a18\u8eab\u4e0d\u7531\u5df1\u7684\u7ed9\u4ed6\u7275\u52a8\uff0c\u91d1\u9010\u6d41\u56de \u8eab\u4e00\u811a\uff0c\u5bf9\u51c6\u4e86\u8463\u5341\u4e09\u5a18\u7684\u5c41\u80a1\uff0c\u8e22\u4e2a\u6b63\u7740\uff01\u8463\u5341\u4e09\u5a18\u8dcc\u4e86\u4e2a\u72d7\u5403\u5c4e\uff0c\u5728\u5730\u4e0a\u6253\u4e86\u4e2a\u6eda\uff0c\u8fd9 \u624d\u89e3\u5f00\u957f\u97ad\u3002\u91d1\u9010\u6d41\u5728\u54c8\u54c8\u5927\u7b11\u58f0\u4e2d\uff0c\u5df2\u98de\u8fc7\u4e86\u5899\u5934\u4e86\u3002\u8463\u5341\u4e09\u5a18\u81ea\u77e5\u8ffd\u4ed6\u4e0d\u4e0a\uff0c\u6c14\u5f97\u53cc\u773c \u7ffb\u767d\uff01 \u3000\u3000\u91d1\u9010\u6d41\u51fa\u4e86\u66f9\u5bb6\uff0c\u4f46\u89c1\u661f\u6cb3\u803f\u803f\uff0c\u660e\u6708\u5728\u5929\uff0c\u5e76\u6ca1\u6709\u53d1\u73b0\u534a\u4e2a\u4eba\u5f71\u3002\u91d1\u9010\u6d41\u7528\u201c\u4f20\u58f0\u5165 \u5bc6\u201d\u7684\u5185\u529f\u53eb\u9053\uff1a\u201c\u54ea\u4f4d\u670b\u53cb\u6697\u4e2d\u76f8\u52a9\uff0c\u8bf7\u51fa\u6765\u4e00\u89c1\uff01\u201d\u7a7a\u6797\u5bc2\u53a9\uff0c\u552f\u6709\u4ed6\u7684\u56de\u58f0\u3002\u91d1\u9010\u6d41 \u542c\u4e0d\u89c1\u56de\u7b54\uff0c\u5fc3\u60f3\uff1a\u201c\u6b64\u4eba\u65e2\u662f\u4e0d\u613f\u76f8\u89c1\uff0c\u6211\u4e14\u5148\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u4e14\u8bf4\u9648\u5149\u7167\u81ea\u91d1\u9010\u6d41\u53bb\u540e\uff0c\u5fc3\u4e2d\u60f4\u60f4\u4e0d\u5b89\uff0c\u53ea\u6015\u91d1\u9010\u6d41\u5b64\u8eab\u72af\u9669\uff0c\u9677\u5728\u66f9\u5e9c\uff0c\u5fc3\u60ac\u597d \u53cb\uff0c\u8eba\u5728\u5e8a\u4e0a\uff0c\u8f97\u8f6c\u53cd\u4fa7\uff0c\u54ea\u91cc\u7761\u7684\u7740\u89c9\uff1f \u3000\u3000\u5ffd\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u4e00\u7f15\u5e7d\u9999\u6c81\u4eba\u9f3b\u89c2\uff0c\u9648\u5149\u7167\u5403\u4e86\u4e00\u60ca\uff0c\u5750\u8d77\u8eab\u6765\uff0c\u53ea\u89c1\u4e00\u6761\u9ed1\u5f71\u95ea\u5165\u623f \u4e2d\u3002\u9648\u5149\u7167\u559c\u9053\uff1a\u201c\u91d1\u5144\uff0c\u4f60\u56de\u6765\u4e86\uff01\u201d\u90a3\u4eba\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8ba4\u4e0d\u5f97\u6211\u4e86\u4e48\uff1f\u201d\u5254 \u4eae\u706f\u82b1\uff0c\u706f\u5149\u4e0b\u4e00\u4e2a\u4fcf\u751f\u751f\u7684\u7f8e\u4eba\u513f\u7ad9\u5728\u5e8a\u524d\uff0c\u53ef\u4e0d\u6b63\u662f\u4ed6\u65e5\u601d\u591c\u60f3\u7684\u4eba\u513f\uff1f\u9648\u5149\u7167\u559c\u51fa\u671b \u5916\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u971e\u59d1\uff0c\u662f\u4f60\uff01\u201d \u3000\u3000\u77f3\u971e\u59d1\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u7ed9\u4f60\u8d54\u7f6a\u6765\u5566\uff0c\u90fd\u662f\u6211\u7684\u4e0d\u597d\uff0c\u7d2f\u4f60\u5403\u82e6\u4e86\u3002\u201d\u9648\u5149\u7167\u9053\uff1a \u201c\u4f60\uff0c\u4f60\u2026\u2026\u201d\u4e07\u8bed\u5343\u8a00\uff0c\u4e0d\u77e5\u4ece\u54ea\u91cc\u8bf4\u8d77\u3002 \u3000\u3000\u77f3\u971e\u59d1\u9053\uff1a\u201c\u4f60\u5148\u522b\u95ee\uff0c\u5403\u4e86\u89e3\u836f\u518d\u8bf4\u5427\u3002\u201d\u638f\u51fa\u4e00\u9897\u7c89\u7ea2\u8272\u7684\u836f\u4e38\uff0c\u5012\u4e86\u4e00\u676f\u5f00\u6c34\uff0c \u670d\u4f8d\u9648\u5149\u7167\u670d\u4e0b\uff0c\u8fd9\u89e3\u836f\u679c\u7136\u7075\u6548\u65e0\u6bd4\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u9648\u5149\u7167\u53ea\u89c9\u8840\u8109\u7545\u901a\uff0c\u7cbe\u795e\u987f\u723d\u3002 \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u971e\u59d1\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u4f60\u90a3\u5976\u5988\u2026\u2026\u201d \u3000\u3000\u77f3\u971e\u59d1\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u4e0d\u60f3\u5bf9\u4f60\u8bf4\u7684\uff0c\u5982\u4eca\u53ea\u597d\u8bf4\u4e86\u3002\u4f60\u602a\u6211\u4e48\uff0c\u4e09\u5e74\u524d\u6211 \u5bf9\u4f60\u90a3\u6837\u7edd\u60c5\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e0d\u4f1a\u602a\u4f60\uff0c\u6211\u77e5\u9053\u4f60\u5b9a\u6709\u82e6\u8877\u3002\u662f\u4e0d\u662f\u4f60\u90a3\u5976\u5988\u4ece\u4e2d\u4f5c\u6897\uff1f\u4f46\u6211\u5374 \u4e0d\u660e\uff0c\u4f55\u4ee5\u4f60\u8981\u53d7\u5979\u631f\u5236\uff1f\u201d \u3000\u3000\u77f3\u971e\u59d1\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u524d\u6709\u4e2a\u5929\u9b54\u6559\uff0c\u5927\u9b54\u6559\u7684\u7956\u5e08\u540d\u53eb\u5389\u80dc\u7537\u60f3\u5fc5\u4f60\u4f1a\u77e5\u9053\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u6211\u542c\u5f97\u7239\u7239\u8bf4\u8fc7\uff0c\u5389\u80dc\u7537\u662f\u4ed6\u7684\u597d\u53cb\u91d1\u4e16\u9057\u91d1\u5927\u4fa0\u7684\u59bb\u5b50\uff0c\u751f\u524d\u66fe\u88ab\u63a8\u4e3a \u6b66\u6797\u7b2c\u4e00\u9ad8\u624b\uff0c\u6b7b\u540e\u624d\u88ab\u5929\u9b54\u6559\u5949\u4e3a\u7956\u5e08\u7684\u3002\u4f60\u7684\u5976\u5a18\u6562\u60c5\u548c\u8fd9\u4f4d\u5389\u7956\u5e08\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u77f3\u971e\u59d1\u9053\uff1a\u201c\u8d3a\u5927\u5a18\u6b63\u662f\u5389\u80dc\u7537\u7684\u4e00\u4e2a\u4f8d\u5973\u3002\u5389\u80dc\u7537\u6709\u56db\u4e2a\u5fc3\u8179\u4f8d\u5973\uff0c\u5982\u4eca\u5c31\u53ea\u662f\u8d3a\u5927 \u5a18\u7855\u679c\u4ec5\u5b58\u4e86\u3002\u201d \u3000\u3000\u77f3\u971e\u59d1\u559d\u4e86\u4e00\u53e3\u8336\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u7684\u6bcd\u4eb2\u4e5f\u662f\u5389\u80dc\u7537\u7684\u4f8d\u5973\uff0c\u548c\u8d3a\u5927\u5a18\u4ea4\u60c5\u6700\u597d\uff0c\u6211 \u81ea\u5c0f\u7236\u6bcd\u53cc\u4ea1\uff0c\u8d3a\u5927\u5a18\u5c31\u628a\u6211\u5f53\u4f5c\u5979\u7684\u5973\u513f\u629a\u517b\u3002 \u3000\u3000\u201c\u5929\u9b54\u6559\u7684\u6559\u4e3b\u5389\u80dc\u7537\u7684\u4f84\u5ab3\uff0c\u548c\u8d3a\u5927\u5a18\u662f\u5e73\u8f88\u3002\u4e8c\u5341\u5e74\u524d\uff0c\u5979\u53d7\u4e86\u91d1\u5927\u4fa0\u7684\u611f\u5316\uff0c\u89e3 \u6563\u4e86\u5929\u9b54\u6559\uff0c\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u8d3a\u5927\u5a18\u662f\u6781\u4e3a\u4e0d\u6ee1\u7684\u3002 \u3000\u3000\u201c\u4e8c\u5341\u5e74\u6765\uff0c\u8d3a\u5927\u5a18\u5ff5\u5ff5\u4e0d\u5fd8\u8981\u590d\u5174\u5929\u9b54\u6559\u3002\u4f46\u5179\u4e8b\u4f53\u5927\uff0c\u5979\u5fc5\u987b\u6c42\u5f97\u5f3a\u63f4\u3002\u5979\u5fc3\u76ee\u4e2d \u7684\u5f3a\u63f4\u662f\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\u53f2\u767d\u90fd\u3002\u5979\u3001\u5979\u7792\u7740\u6211\uff0c\u60f3\u628a\u6211\u8bb8\u7ed9\u53f2\u767d\u90fd\u505a\u7eed\u5f26\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u5927\u60ca\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5982\u6b64\uff0c\u602a\u4e0d\u5f97\u5979\u6628\u665a\u60f3\u5bb3\u6211\u3002\u201d \u3000\u3000\u77f3\u971e\u59d1\u9053\uff1a\u201c\u4e09\u5e74\u524d\u5979\u5c31\u60f3\u5bb3\u4f60\u4e86\u3002\u4e0d\u8fc7\uff0c\u5f53\u65f6\u6211\u8fd8\u672a\u77e5\u9053\u5979\u548c\u53f2\u767d\u90fd\u52fe\u7ed3\u7684\u4e8b\u3002\u201d \u3000\u3000\u77f3\u971e\u59d1\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u90a3\u6b21\u4f60\u5230\u6211\u5bb6\u6c42\u5a5a\uff0c\u5979\u672c\u6765\u5c31\u60f3\u6bd2\u5bb3\u4f60\u7684\uff0c\u6211\u8feb\u4e0d\u5f97\u5df2\uff0c\u7b54\u5e94\u4e86 \u5979\u7684\u6761\u4ef6\uff0c\u8fd9\u624d\u6362\u4e86\u4f60\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u4f60\u7b54\u5e94\u4e86\u5979\u7684\u4ec0\u4e48\u6761\u4ef6\uff1f\u201d \u3000\u3000\u77f3\u971e\u59d1\u9053\uff1a\u201c\u4ece\u4eca\u4e4b\u540e\uff0c\u4e0d\u518d\u4e0e\u4f60\u5f80\u6765\u3002\u6211\u4e00\u6765\u5ff5\u5979\u629a\u80b2\u4e4b\u6069\uff0c\u4e0d\u5fcd\u4e0e\u5979\u51b3\u7edd\uff1b\u4e8c\u6765\u4e3a \u4e86\u6551\u4f60\u4e00\u547d\uff0c\u53ea\u597d\u7b54\u5e94\u4e86\u5979\u3002\u5509\uff0c\u90a3\u65f6\u6211\u5fc3\u91cc\u65e0\u9650\u60b2\u75db\uff0c\u53ef\u53c8\u4e0d\u6562\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u971e\u59d1\uff0c\u771f\u662f\u96be\u4e3a\u4f60\u4e86\u3002\u4e0d\u8fc7\uff0c\u54b1\u4eec\u73b0\u5728\u6bd5\u7adf\u662f\u53c8\u5728\u4e00\u8d77\u4e86\u3002\u971e\u59d1\uff0c\u4f60\u6709\u52c7 \u6c14\u6446\u8131\u5979\u7684\u9b54\u638c\uff0c\u6211\u5f88\u4e3a\u4f60\u9ad8\u5174\u3002\u8fc7\u53bb\u7684\u4e8b\uff0c\u8ba9\u5b83\u8fc7\u53bb\u3002\u4ece\u4eca\u4e4b\u540e\uff0c\u54b1\u4eec\u518d\u4e5f\u4e0d\u4f1a\u5206\u5f00 \u4e86\u3002\u201d \u3000\u3000\u77f3\u971e\u59d1\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u60f3\u5f97\u5f88\u7f8e\uff0c\u53ef\u60dc\u6211\u4e0d\u80fd\u591f\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u4f60\u4eca\u665a\u9001\u89e3\u836f\u7ed9\u6211\uff0c\u8fd9\u4e0d\u662f\u5df2\u7ecf\u6253\u7834\u4e86\u5979\u7684\u7981\u5236\u4e86\u5417\uff1f\u96be\u5230\u4f60\u8fd8 \u8981\u56de\u53bb\u542c\u5979\u4e4b\u547d\u5ac1\u7ed9\u53f2\u767d\u90fd\uff1f\u201d \u3000\u3000\u77f3\u971e\u59d1\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u6211\u6b7b\u4e5f\u4e0d\u4f1a\u5ac1\u7ed9\u53f2\u767d\u90fd\u7684\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u7740\u5440\uff01\u90a3\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u591f\u7559\u4e0b\u6765\u4e0e\u6211\u4e00\u8d77\uff1f\u201d \u3000\u3000\u77f3\u971e\u59d1\u9053\uff1a\u201c\u6211\u672c\u6765\u7b54\u5e94\u4e86\u5979\u4e0d\u518d\u4e0e\u4f60\u5f80\u6765\u7684\uff0c\u8fd9\u6b21\u82e5\u4e0d\u662f\u56e0\u4e3a\u5979\u53c8\u8981\u5bb3\u4f60\uff0c\u6211\u53c8\u77e5\u9053 \u5979\u8981\u903c\u6211\u5ac1\u7ed9\u53f2\u767d\u90fd\uff0c\u6211\u4e5f\u4e0d\u4f1a\u8fdd\u80cc\u6211\u7684\u8bfa\u8a00\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u5bf9\u5440\uff0c\u8fd9\u662f\u5979\u4e0d\u5b88\u4fe1\u7528\u5728\u5148\uff0c\u602a\u4e0d\u5f97\u4f60\u2018\u53cd\u53db\u2019\u5979\u3002\u971e\u59d1\uff0c\u4f60\u548c\u6211\u8d70\u4e86 \u5427\uff01\u201d \u3000\u3000\u77f3\u971e\u59d1\u6447\u4e86\u6447\u5934\uff0c\u4ecd\u7136\u662f\u90a3\u4e00\u53e5\u8bdd\uff0c\u201c\u6211\u4e0d\u80fd\u591f\uff01\u201d \u3000\u3000\u9648\u5149\u7167\u4e0d\u89c9\u6709\u70b9\u7740\u607c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u8bfa\u8a00\u662f\u7ed9\u5979\u903c\u51fa\u6765\u7684\uff0c\u672c\u6765\u5c31\u65e0\u987b\u9075\u5b88\u3002\u4f60\u8fd9\u5976\u5a18 \u5fc3\u80a0\u72e0\u8fa3\uff0c\u53ea\u8bba\u5979\u8981\u5229\u7528\u4f60\u6765\u5df4\u7ed3\u53f2\u767d\u90fd\u4e00\u4e8b\uff0c\u5979\u5df2\u7ecf\u662f\u7f6a\u4e0d\u5bb9\u8d66\uff01\u79f0\u4e0d\u6740\u5979\uff0c\u5df2\u7ecf\u662f\u5bf9\u5f97 \u8d77\u5979\u7684\u517b\u80b2\u4e4b\u6069\u4e86\uff01\u201d \u3000\u3000\u77f3\u971e\u59d1\u53f9\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u77f3\u971e\u59d1\u9053\uff1a\u201c\u5f53\u5e74\u6211\u4e3a\u4e86\u963b\u6b62\u5979\u5bf9\u4f60\u4e0b\u6bd2\u624b\uff0c\u5728\u7b54\u5e94\u5979\u7684\u6761\u4ef6\u4e4b\u65f6\uff0c\u66fe\u559d\u4e86\u5979\u4e00\u676f\u6bd2 \u9152\uff01\u201d \u3000\u3000\u9648\u5149\u7167\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6bd2\u9152\u4f1a\u6709\u4ec0\u4e48\u6548\u679c\uff1f\u4f60\u53ef\u4ee5\u89e3\u6211\u4e4b\u6bd2\uff0c\u5c31\u4e0d\u80fd\u89e3\u4f60\u81ea\u5df1\u4e4b \u6bd2\u4e48\uff1f\u5509\uff0c\u4f60\u53c8\u4e3a\u4f55\u8981\u559d\u8fd9\u676f\u6bd2\u9152\uff1f\u201d \u3000\u3000\u77f3\u971e\u59d1\u9053\uff1a\u201c\u5f53\u65f6\u6211\u4e3a\u4e86\u6551\u4f60\uff0c\u6211\u8bf4\uff1a\u5976\u5a18\uff0c\u4f60\u82e5\u6740\u4e86\u4ed6\uff0c\u6211\u4e5f\u51b3\u4e0d\u80fd\u72ec\u6d3b\uff0c\u5979\u8bf4\uff1a \u597d\uff0c\u4f60\u65e2\u7136\u4e0d\u60dc\u4e00\u6b7b\u4e5f\u8981\u6551\u4ed6\uff0c\u90a3\u4f60\u5c31\u559d\u4e86\u6211\u8fd9\u676f\u6bd2\u9152\u3002\u5979\u4e0b\u6bd2\u7684\u672c\u9886\u6bd4\u6211\u9ad8\u660e\u767e\u500d\uff0c\u8fd9\u6bd2 \u9152\u662f\u4e09\u4e2a\u6708\u4e4b\u540e\u624d\u53d1\u4f5c\u7684\uff0c\u5230\u671f\u5979\u7ed9\u4e00\u9897\u836f\u4e38\uff0c\u53c8\u53ef\u4ee5\u518d\u5ef6\u4e09\u6708\u3002\u5979\u8bf4\uff1a\u6211\u5e76\u4e0d\u60f3\u8981\u4f60\u7684 \u547d\uff0c\u53ea\u662f\u8981\u7528\u8fd9\u4e2a\u529e\u6cd5\u5f3a\u5236\u4f60\u9075\u5b88\u4f60\u7684\u8bfa\u8a00\uff0c\u53ea\u8981\u4f60\u4e0d\u66f4\u4e0e\u4ed6\u5f80\u6765\uff0c\u6bcf\u4e09\u4e2a\u6708\u6211\u7ed9\u4f60\u670d\u4e00\u6b21 \u836f\uff0c\u4f60\u5b8c\u5168\u548c\u5e38\u4eba\u4e00\u6837\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u6068\u6068\u8bf4\u9053\uff1a\u201c\u597d\u72e0\u6bd2\u7684\u624b\u6bb5\uff01\u201d \u3000\u3000\u77f3\u971e\u59d1\u9053\uff1a\u201c\u8fd8\u4e0d\u4ec5\u4ec5\u662f\u8fd9\u6837\u5462\u3002\u8d3a\u5927\u5a18\u7ed9\u6211\u4e0b\u7684\u6bd2\u540d\u53eb\u5438\u8840\u6563\uff0c\u6211\u559d\u4e86\u5979\u7684\u6bd2\u9152\uff0c\u8840 \u6db2\u5df2\u7ecf\u4e2d\u6bd2\uff0c\u8981\u89e3\u6b64\u6bd2\uff0c\u53e6\u6709\u4e00\u5957\u2018\u91d1\u9488\u62d4\u6bd2\u2019\u4e4b\u6cd5\uff0c\u5e76\u975e\u4efb\u4f55\u836f\u7269\u6240\u80fd\u533b\u62ac\u3002\u8fd9\u5957\u91d1\u9488\u62d4 \u6bd2\u4e4b\u6cd5\u8f7d\u4e8e\u5389\u7956\u5e08\u7559\u4e0b\u7684\u79d8\u7b08\u2018\u767e\u6bd2\u771f\u7ecf\u2019\uff0c\u6211\u5374\u6ca1\u6709\u5b66\u8fc7\u3002\u5982\u679c\u6211\u4e0d\u542c\u8d3a\u5927\u5a18\u7684\u8bf4\u8bdd\uff0c\u548c \u4f60\u505a\u4e86\u592b\u59bb\uff0c\u6211\u56fa\u7136\u6d3b\u4e0d\u4e86\u4e09\u4e2a\u6708\uff0c\u4f60\u4e5f\u8981\u53d7\u6211\u8fde\u7d2f\uff0c\u4e2d\u6bd2\u800c\u4ea1\u3002\u73b0\u5728\u4f60\u660e\u767d\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd \u7559\u4e0b\u6765\u548c\u4f60\u4e00\u8d77\u4e86\u5427\uff1f\u201d \u3000\u3000\u521a\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u58f0\u97f3\u7b11\u9053\uff1a\u201c\u4e0d\uff0c\u77f3\u59d1\u5a18\uff0c\u4f60\u8fd8\u662f\u53ef\u4ee5\u7559\u4e0b\u6765\u7684\u3002\u4e0d\u4f46\u53ef\u4ee5\u7559 \u4e0b\u6765\uff0c\u548c\u9648\u5927\u54e5\u505a\u592b\u59bb\u4e5f\u4e0d\u788d\u4e8b\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u5927\u559c\u9053\uff1a\u201c\u91d1\u5144\uff0c\u4f60\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u53ea\u89c1\u7a97\u53e3\u4eba\u5f71\u4e00\u6643\uff0c\u91d1\u9010\u6d41\u5df1\u662f\u7ad9\u5728\u4ed6\u4eec\u9762\u524d\uff0c\u7b11\u9053\uff1a\u201c\u77f3\u59d1\u5a18\uff0c\u521a\u624d\u5728\u66f9\u5bb6\u53d1\u6697\u5668\u7684 \u4eba\u5c31\u662f\u4f60\u5427\uff1f\u6211\u8fd8\u6ca1\u6709\u591a\u8c22\u4f60\u5462\u3002\u201d \u3000\u3000\u77f3\u971e\u59d1\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4eba\u7684\u8f7b\u529f\u53ef\u662f\u6bd4\u6211\u9ad8\u660e\u591a\u4e86\uff0c\u6211\u8fde\u4e00\u70b9\u58f0\u606f\u90fd\u6ca1\u542c\u5230\u3002\u4f46 \u542c\u4ed6\u8fd9\u6837\u8bf4\uff0c\u96be\u9053\u4ed6\u4f1a\u7ed9\u6211\u89e3\u6bd2\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u8be7\u9053\uff1a\u201c\u971e\u59d1\uff0c\u539f\u6765\u4f60\u5df2\u7ecf\u5230\u8fc7\u66f9\u5bb6\u4e86\uff1f\u201d \u3000\u3000\u77f3\u971e\u59d1\u9053\uff1a\u201c\u6211\u5c31\u662f\u56e0\u4e3a\u8981\u63a2\u542c\u4f60\u7684\u6d88\u606f\uff0c\u624d\u53bb\u66f9\u5bb6\u7684\u3002\u6211\u5077\u542c\u4e86\u4ed6\u4eec\u7684\u8c08\u8bdd\uff0c\u59cb\u77e5\u4f60 \u662f\u5728\u8fd9\u513f\u7597\u4f24\uff0c\u8981\u4e0d\u7136\u6211\u600e\u4e48\u80fd\u627e\u5230\u4f60\u5462\uff1f\u6211\u672c\u6765\u4e0d\u8ba9\u8d3a\u5927\u5a18\u77e5\u9053\uff0c\u4f46\u73b0\u5728\u6211\u7528\u72ec\u95e8\u7684\u5582\u6bd2 \u6697\u5668\u6253\u4f24\u4e86\u4ed6\u4eec\u7684\u4e24\u4e2a\u4eba\uff0c\u8d3a\u5927\u5a18\u5f53\u7136\u4e5f\u4f1a\u77e5\u9053\u662f\u6211\u7684\u6240\u4e3a\u4e86\u3002\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u4e0d\u5e94\u8be5\u56de\u53bb\u4e86\u3002\u201d \u3000\u3000\u77f3\u971e\u59d1\u9053\uff1a\u201c\u6211\u56de\u4e0d\u56de\u53bb\uff0c\u5927\u4e0d\u4e86\u4e5f\u53ea\u662f\u4e00\u4e2a\u6b7b\u5b57\u3002\u4f46\u6211\u53ef\u4e0d\u80fd\u8fde\u7d2f\u4e86\u4f60\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u521a\u624d\u8bf4\u7684\u8bdd\u6211\u90fd\u542c\u89c1\u4e86\u3002\u4f46\u6211\u521a\u521a\u8bf4\u8fc7\u7684\u8bdd\u3001\u96be\u9053\u4f60\u8fd8\u6ca1\u6709\u542c\u6e05 \u695a\uff0c\u8981\u6211\u518d\u8bf4\u4e00\u904d\u4e48\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u91d1\u5144\uff0c\u83ab\u975e\u4f60\u61c2\u5f97\u90a3\u5957\u91d1\u9488\u62d4\u6bd2\u4e4b\u6cd5\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u4e0d\u61c2\uff0c\u4f46\u5929\u4e0b\u9664\u4e86\u90a3\u8001\u5996\u5a46\u4e4b\u5916\uff0c\u4e5f\u603b\u8fd8\u6709\u4eba\u61c2\u5f97\u3002\u4f60\u5fd8\u8bb0\u4e86\u5389\u5357\u661f\u5389 \u5927\u54e5\u4e86\u4e48\uff1f\u4ed6\u662f\u5929\u9b54\u6559\u4e3b\u7684\u513f\u5b50\uff0c\u90a3\u4ec0\u4e48\u767e\u6bd2\u771f\u7ecf\uff0c\u4ed6\u5c82\u6709\u4e0d\u7cbe\u901a\u4e4b\u7406\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u5927\u559c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u9a6c\u4e0a\u5230\u626c\u5dde\u53bb\uff0c\u54b1\u4eec\u4e3a\u4ed6\u89e3\u56f0\uff0c\u4e5f\u8bf7\u4ed6\u4e3a\u971e\u59d1\u89e3\u6bd2\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u5b8c\u5168\u597d\u4e86\uff1f\u201d \u3000\u3000\u9648\u5149\u7167\u9053\uff1a\u201c\u971e\u59d1\u7ed9\u6211\u7684\u89e3\u836f\u7075\u9a8c\u5f97\u5f88\uff0c\u6211\u60f3\u660e\u5929\u4e00\u65e9\uff0c\u6211\u53ef\u4ee5\u548c\u4f60\u4e00\u9053\u8d70\u4e86\u3002\u201d\u971e \u59d1\uff0c\u4f60\u4e5f\u548c\u6211\u4eec\u4e00\u8d77\u53bb\u5427\u3002\u201d \u3000\u3000\u77f3\u971e\u59d1\u7b49\u4e8e\u662f\u7edd\u5904\u9022\u751f\uff0c\u6709\u4e86\u8fd9\u4e2a\u5e0c\u671b\u5f53\u7136\u4e0d\u80fd\u653e\u8fc7\u4e86\uff0c\u5f53\u4e0b\u4e5f\u987e\u4e0d\u5f97\u7f9e\u6da9\uff0c\u6b23\u7136\u7b54 \u5e94\uff0c\u4e8e\u662f\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u4ed6\u4eec\u4e09\u4eba\u4fbf\u4e0e\u738b\u6cf0\u544a\u522b\uff0c\u79bb\u5f00\u4e86\u6d4e\u5357\uff0c\u5f84\u8d74\u626c\u5dde\u3002 \u3000\u3000\u91d1\u9010\u6d41\u65e9\u5df2\u77e5\u9053\u53f2\u767d\u90fd\u4e0e\u5389\u5357\u661f\u7684\u7ea6\u4f1a\u662f\u4e2a\u9a97\u5c40\uff0c\u751f\u6015\u5389\u5357\u661f\u4e0a\u5f53\uff0c\u6068\u4e0d\u5f97\u63d2\u7fc5\u98de\u5230\u626c \u5dde\u3002\u91d1\u9010\u6d41\u6697\u81ea\u601d\u91cf\uff1a\u201c\u4e0d\u77e5\u5389\u5927\u54e5\u53ef\u60f3\u5230\u8fd9\u4e00\u4e2a\u9a97\u5c40\uff1f\u4f46\u4ed6\u5bf9\u7ea2\u82f1\u4e00\u5f80\u60c5\u6df1\uff0c\u53ea\u6015\u660e\u77e5\u662f \u4e2a\u9a97\u5c40\uff0c\u4ed6\u4e5f\u662f\u8981\u53bb\u7684\u4e86\uff0c\u7ea2\u82f1\u7684\u5fc3\u610f\u77e5\u4e0d\u77e5\u600e\u6837\uff1f\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u4e0d\u89c9\u4e00\u7247\u60d8\u7136\uff0c\u53c8\u4e0d\u7981\u6697 \u81ea\u8d23\u5907\uff1a\u201c\u6211\u5df2\u7ecf\u51b3\u5b9a\u6210\u5168\u4ed6\u4eec\uff0c\u53c8\u4f55\u5fc5\u5984\u81ea\u63e3\u6d4b\uff1f\u6b64\u53bb\u626c\u5dde\uff0c\u6211\u53ea\u5f53\u5c3d\u529b\u800c\u4e3a\uff0c\u5e2e\u8fd9\u4e00\u5bf9 \u6709\u60c5\u4eba\u5f97\u6210\u7737\u5c5e\uff0c\u6211\u7edd\u4e0d\u80fd\u80e1\u601d\u4e71\u60f3\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5728\u9014\u4e2d\u4e3a\u5389\u5357\u661f\u7740\u6025\u7684\u65f6\u5019\uff0c\u6b63\u662f\u5389\u5357\u661f\u5728\u516d\u5408\u5e2e\u7684\u603b\u8235\u505a\u7740\u7f8e\u68a6\u4e4b\u65f6\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u5389\u5357\u661f\u6765\u5230\u4e86\u516d\u5408\u5e2e\u603b\u8235\uff0c\u5e2e\u4e3b\u53f2\u767d\u90fd\u6253\u5f00\u4e2d\u95e8\u8fce\u8f87\uff0c\u5f85\u4ed6\u5982\u540c\u8d35\u5bbe\u3002 \u3000\u3000\u5389\u5357\u661f\u60ca\u7591\u4e0d\u8db3\uff0c\u8ddf\u7740\u53f2\u767d\u90fd\u8fdb\u4e86\u5185\u82b1\u5385\uff0c\u5750\u8d70\u4e4b\u540e\uff0c\u4fbf\u5373\u95ee\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\u7ea6\u6211\u6b64\u6765\uff0c \u6709\u4f55\u89c1\u6559\uff0c\u671b\u53f2\u5e2e\u4e3b\u660e\u4ee5\u544a\u6211\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5389\u516c\u5b50\u662f\u806a\u660e\u4eba\uff0c\u8fd8\u7528\u5f97\u770b\u6211\u8bf4\u5417\uff1f\u201d\u5f53\u7136\u662f\u4e3a\u4e86\u820d\u59b9\u7684\u7ec8\u8eab\u5927\u4e8b \u4e86\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u662f\u4e2a\u70ed\u60c5\u800c\u53c8\u723d\u76f4\u7684\u4eba\uff0c\u4fbf\u5373\u8bf4\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\u5982\u6b64\u723d\u5feb\uff0c\u90a3\u6211\u4e5f\u4e0d\u60f3\u7ed5\u7740\u9053\u513f\u8bf4 \u8bdd\u4e86\uff0c\u6211\u5bf9\u4ee4\u59b9\u662f\u4e00\u89c1\u503e\u5fc3\uff0c\u5018\u82e5\u4e0d\u662f\u6211\u81ea\u4f5c\u591a\u60c5\uff0c\u4ee4\u59b9\u5bf9\u6211\u4e5f\u4f3c\u4e4e\u672a\u5c1d\u65e0\u610f\uff0c\u5982\u4eca\u5c31\u53ea\u770b \u53f2\u5e2e\u4e3b\u7684\u610f\u601d\u4e86\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u5f53\u521d\u6211\u662f\u4e0d\u5927\u8d5e\u540c\u7684\u3002\u4f46\u5982\u4eca\u6211\u5374\u662f\u4e0d\u80fd\u4e0d\u6539\u53d8\u4e3b\u610f\u4e86\u3002\u4e00\u6765 \u6211\u5df2\u7ecf\u77e5\u9053\u4f60\u4eec\u786e\u5b9e\u662f\u5f7c\u6b64\u76f8\u7231\uff0c\u6211\u53ea\u6709\u8fd9\u4e2a\u59b9\u5b50\uff0c\u6211\u53c8\u600e\u5fcd\u5fc3\u5c06\u4f60\u4eec\u62c6\u6563\uff0c\u4ee4\u5979\u4f24\u5fc3\uff1f\u4e8c \u6765\u6211\u5982\u4eca\u4e5f\u77e5\u9053\u5389\u516c\u5b50\u662f\u4e2a\u82f1\u96c4\u8c6a\u6770\uff0c\u53ea\u51ed\u4f60\u4eca\u65e5\u6562\u6765\u5355\u9a91\u8d74\u4f1a\uff0c\u6211\u5c31\u8981\u4f69\u670d\u4f60\u7684\u52c7\u6c14\u4e86\u3002 \u820d\u59b9\u5f97\u914d\u82f1\u96c4\uff0c\u7ec8\u8eab\u6709\u6258\uff0c\u6211\u505a\u54e5\u54e5\u7684\u4e5f\u4e3a\u5979\u6b22\u559c\u3002\u56e0\u6b64\u6211\u51b3\u610f\u6210\u5168\u4f60\u4eec\uff0c\u5e76\u4e3a\u4f60\u4eec\u4e3b\u6301\u5a5a \u793c\u3002\u201d \u3000\u3000\u4efb\u4f55\u4eba\u90fd\u662f\u559c\u6b22\u6234\u9ad8\u5e3d\u7684\uff0c\u5389\u5357\u661f\u5f97\u53f2\u767d\u90fd\u4e00\u8d5e\uff0c\u5bf9\u4ed6\u7684\u6076\u611f\u4e0d\u89c9\u51cf\u4e86\u51e0\u5206\u3002\u4f46\u53f2\u767d\u90fd \u7b54\u5e94\u5f97\u8fd9\u6837\u723d\u5feb\uff0c\u5389\u5357\u661f\u5374\u662f\u4e0d\u80fd\u4e0d\u6709\u201c\u5927\u51fa\u610f\u5916\u201d\u4e4b\u611f\u3002 \u3000\u3000\u53f2\u767d\u90fd\u7b11\u9053\uff1a\u201c\u5389\u516c\u5b50\u4f55\u4ee5\u6c89\u541f\u4e0d\u8bed\uff0c\u6562\u60c5\u662f\u6709\u4ec0\u4e48\u5fc3\u4e8b\u4e48\uff1f\u4f60\u6211\u5982\u4eca\u5df2\u6210\u4e86\u4eb2\u5bb6\uff0c\u6055 \u6211\u4e0d\u5ba2\u6c14\u79f0\u4f60\u4e00\u58f0\u8001\u5f1f\u4e86\uff0c\u4f60\u6709\u4ec0\u4e48\u8bdd\uff0c\u4e0d\u59a8\u5766\u76f4\u5730\u544a\u8bc9\u6211\u8fd9\u4e2a\u5927\u54e5\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u5927\u54e5\u8bb8\u5a5a\u3002\u90a3\u4e48\uff0c\u8bf7\u4f60\u6055\u6211\u76f4\u8a00\uff0c\u6211\u53ef\u4e0d\u60f3\u4eb2\u5bb6\u53d8\u6210\u4ec7 \u654c\u3002\u4f60\u5f53\u7136\u77e5\u9053\u6211\u662f\u4ec0\u4e48\u4eba\uff0c\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\u4eec\uff0c\u6211\u51b3\u610f\u53cd\u6e05\uff0c\u8fd9\u662f\u51b3\u4e0d\u4f1a\u6539\u53d8\u7684\uff01\u4f60\u82e5\u8981\u6094 \u5a5a\uff0c\u5982\u4eca\u672a\u665a\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u6211\u65e9\u5df2\u6599\u5230\u4f60\u8981\u8bf4\u8fd9\u756a\u8bdd\u4e86\u3002\u6211\u80fd\u591f\u7b54\u5e94\u4f60\u4eec\u7684\u5a5a\u4e8b\uff0c\u5f53\u7136\u6211\u66fe\u7ecf\u597d\u597d\u7684 \u60f3\u8fc7\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u90a3\u4e48\u6211\u5012\u60f3\u77e5\u9053\u4f60\u73b0\u5728\u7684\u60f3\u6cd5\u5982\u4f55\uff1f\u636e\u6211\u6240\u77e5\uff0c\u4e00\u4e2a\u6708\u524d\uff0c\u597d\u50cf\u4f60\u8fd8\u662f\u60f3 \u628a\u4ee4\u59b9\u8bb8\u7ed9\u897f\u661f\u5c06\u519b\u5e05\u5b5f\u96c4\u7684\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u4e00\u65f6\u7684\u7cca\u6d82\uff0c\u6211\u786e\u66fe\u6709\u8fc7\u6b64\u610f\u3002\u597d\u5728\u8fd9\u6708\u4eb2\u4e8b\u4e0d\u6210\uff0c \u5426\u5219\u771f\u8981\u6559\u5929\u4e0b\u82f1\u96c4\u7b11\u8bdd\u4e86\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u542c\u4ed6\u8a00\u8bf7\u4e4b\u4e2d\u9887\u6709\u6094\u8fc7\u4e4b\u6025\uff0c\u5fc3\u91cc\u6697\u6697\u6b22\u559c\uff0c\u4fbf\u5373\u53ee\u7d27\u4e00\u53e5\uff0c\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u867d\u7136\u8bf4\u4e0d\u4e0a\u662f\u80f8\u6000\u5927\u5fd7\uff0c\u5374\u4e5f\u4e0d\u7518\u4ee5\u4e00\u4e2a\u533a\u533a\u516d\u5408\u5e2e\u5e2e\u4e3b\u7684 \u8eab\u4efd\u865a\u5ea6\u6b64\u751f\u3002\u6211\u4e0a\u4eac\u7ed9\u8428\u798f\u9f0e\u795d\u5bff\uff0c\u4e3a\u7684\u5c31\u662f\u7ed3\u4ea4\u5929\u4e0b\u82f1\u96c4\uff0c\u95ef\u51fa\u4e00\u756a\u4e8b\u4e1a\uff01\u8c01\u77e5\u2026\u2026 \u5509\uff01\u201d \u3000\u3000\u5389\u5357\u5c4b\u9053\uff1a\u201c\u53f2\u5927\u54e5\u6709\u4f55\u611f\u89e6\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u84e6\u5730\u4e00\u62cd\u684c\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u8c01\u77e5\u90a3\u4e9b\u671d\u5ef7\u7684\u5927\u5b98\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u628a\u6211\u4eec\u5f53\u4f5c\u4e00\u4e2a\u6709\u9aa8 \u6c14\u7684\u4eba\u770b\u5f85\uff0c\u597d\u50cf\u6211\u4eec\u8d70\u8981\u6295\u9760\u4ed6\uff0c\u5411\u4ed6\u8ba8\u996d\u5403\u4f3c\u7684\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u5fc3\u91cc\u6697\u7b11\uff1a\u201c\u5728\u8428\u798f\u9f0e\u773c\u4e2d\uff0c\u4f60\u672c\u6765\u5c31\u662f\u4e00\u6761\u72d7\u3002\u201d\u5374\u6545\u610f\u95ee\u9053\uff1a\u201c\u6211\u770b\u8428\u798f\u9f0e \u5bf9\u4f60\uff0c\u4e5f\u597d\u50cf\u5f88\u4e0d\u9519\u561b\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u9519\uff1f\u201d\u6211\u73b0\u5728\u624d\u77e5\u9053\u4ed6\u4e0d\u8fc7\u53ea\u662f\u60f3\u5229\u7528\u6211\u7f62\u4e86\u3002\u4ed6\u53e3\u4e2d\u8bf4\u662f\u793c\u8d24\u4e0b \u58eb\uff0c\u9080\u6211\u4e0a\u4eac\uff0c\u5176\u5b9e\u8fd8\u4e0d\u662f\u90a3\u4e48\u4e00\u56de\u4e8b\uff0c\u8981\u6211\u505a\u4ed6\u7684\u5974\u624d\u800c\u5df2\uff0c\u54fc\uff0c\u6211\u597d\u6b79\u4e5f\u662f\u4e00\u5e2e\u4e4b\u4e3b\uff0c \u5c82\u80fd\u505a\u4ed6\u7684\u5974\u624d\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u90a3\u5929\u5c09\u8fdf\u70af\u592b\u59bb\u5927\u52ab\u5bff\u5802\uff0c\u4f60\u5e2e\u8428\u798f\u9f0e\u7684\u5fd9\u4e5f\u5f88\u4e0d\u5c0f\u554a\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u8001\u5f1f\u8bef\u4f1a\u6211\uff0c\u6211\u90a3\u6b21\u7684\u786e\u662f\u505a\u9519\u4e86\u3002\u4e0d\u8fc7\uff0c\u8fd9\u4e5f\u8bb8\u662f \u6211\u7684\u7cca\u6d82\u60f3\u6cd5\uff0c\u6211\u5f53\u65f6\u5e76\u4e0d\u662f\u4e3a\u4e86\u5df4\u7ed3\u4ed6\u624d\u7ed9\u4ed6\u51fa\u529b\u7684\u3002\u6211\u53ea\u662f\u770b\u5728\u4e00\u4e2a\u2018\u4e49\u6c14\u2019\u7684\u4efd\u4e0a\uff0c \u6211\u7ed9\u4ed6\u795d\u5bff\uff0c\u6211\u5c31\u662f\u4ed6\u7684\u5ba2\u4eba\uff0c\u4e3b\u4eba\u5bb6\u6709\u4e8b\uff0c\u5ba2\u4eba\u7406\u5e94\u5e2e\u5fd9\uff0c\u8c01\u77e5\u8fd9\u4e48\u4e00\u6765\uff0c\u4ed6\u66f4\u628a\u6211\u5f53\u4f5c \u8981\u6295\u9760\u4ed6\u7684\u5974\u624d\u4e86\u3002 \u3000\u3000\u201c\u8fd9\u5df2\u7ecf\u662f\u4ee4\u4eba\u6c14\u6124\uff0c\u4f46\u8fd8\u6709\u66f4\u4ee4\u4eba\u96be\u53d7\u7684\u5462\u3002\u8fd9\u4e9b\u505a\u5b98\u7684\u4eba\uff0c\u4e2a\u4e2a\u90fd\u662f\u751f\u6210\u7684\u7591\u5fc3\u75c5 \u91cd\u3002\u90a3\u65e5\u6211\u7684\u59b9\u59b9\u548c\u4f60\u4eec\u4e00\u8d77\u641e\u4e8b\uff0c\u8428\u798f\u9f0e\u8fde\u6211\u4e5f\u7591\u5fc3\u4e0a\u5566\uff0c\u540e\u6765\uff0c\u4ed6\u53eb\u4eba\u5411\u6211\u793a\u610f\uff0c\u8bf4\u662f \u5018\u82e5\u6211\u662f\u771f\u5fc3\u6548\u5fe0\u8428\u798f\u9f0e\u7684\u8bdd\uff0c\u5c31\u8be5\u628a\u59b9\u59b9\u9001\u56de\u6765\uff0c\u8ba9\u4ed6\u5ba1\u95ee\uff0c\u6211\u4e00\u6c14\u4e4b\u4e0b\uff0c\u628a\u90a3\u4eba\u6253\u4e86\u4e00 \u987f\uff0c\u5c31\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53f2\u767d\u90fd\u56fa\u7136\u662f\u540d\u5229\u5fc3\u8f88\uff0c\u5373\u4f7f\u73b0\u5728\u7ecf\u8fc7\u4e86\u8fd9\u6b21\u6559\u8bad\uff0c\u4ed6 \u7684\u60f3\u6cd5\u4e5f\u8fd8\u662f\u6709\u8bb8\u591a\u7cca\u6d82\u7684\u5730\u65b9\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u80fd\u591f\u6709\u8fd9\u756a\u6094\u609f\uff0c\u4e5f\u7b97\u662f\u5f88\u96be\u5f97\u4e86\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u53c8\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u4e0a\u4eac\u4e00\u8d9f\uff0c\u8fd8\u770b\u6e05\u695a\u4e86\u4e00\u4e2a\u4e8b\u5b9e\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u5b9e\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8bf4\u8fc7\uff0c\u6211\u662f\u60f3\u8d81\u6b64\u673a\u4f1a\uff0c\u7ed3\u4ea4\u5929\u4e0b\u82f1\u96c4\u7684\u5417\uff1f\u5230\u4e86\u90a3\u5929\u4e00\u770b\uff0c\u6765\u795d \u5bff\u7684\u4eba\u56fa\u7136\u4e0d\u5c11\uff0c\u53ef\u662f\u771f\u6b63\u6709\u4efd\u91cf\u7684\u6210\u540d\u8c6a\u6770\uff0c\u5374\u6ca1\u6709\u4e00\u4e2a\u3002\u7ea2\u7f28\u4f1a\u7684\u8235\u4e3b\u516c\u5b59\u5b8f\u7b97\u662f\u90a3\u5929 \u7684\u7b2c\u4e00\u53f7\u4eba\u7269\u4e86\uff0c\u5374\u539f\u6765\u516c\u5b59\u5b8f\u4e5f\u662f\u53e6\u6709\u6240\u4e3a\u800c\u6765\uff0c\u5e76\u975e\u771f\u7684\u662f\u4e3a\u5df4\u7ed3\u8428\u798f\u9f0e\u7684\u3002\u8001\u5f1f\uff0c\u4f60 \u77e5\u4e0d\u77e5\u9053\u516c\u5b59\u5b8f\u8fd9\u4ef6\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u867d\u7136\u6bd4\u8f83\u5355\u7eaf\uff0c\u5374\u4e5f\u5e76\u4e0d\u7cca\u6d82\uff0c\u5bf9\u53f2\u767d\u90fd\u4e5f\u8fd8\u4fdd\u7559\u6709\u51e0\u5206\u6212\u5907\uff0c\u4e8e\u662f\u4f6f\u4f5c\u4e0d\u77e5\uff0c \u8bf4\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u4f46\u90a3\u5929\u6211\u597d\u4f3c\u770b\u89c1\u516c\u5b59\u5b8f\u8fd9\u8001\u513f\u548c\u91d1\u9010\u6d41\u4ea4\u624b\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u5f53\u771f\u8fd8\u672a\u77e5\u9053\uff1f\u516c\u5b59\u5b9a\u8fd9\u8001\u513f\u662f\u5934\u8001\u72d0\u57cb\uff0c\u4ed6\u8868\u9762\u597d\u4f3c\u662f\u4e3a\u8428 \u798f\u9f0e\u51fa\u529b\uff0c\u5176\u5b9e\u5374\u662f\u548c\u5c09\u8fdf\u70af\u4e32\u901a\u4e86\u7684\u3002\u90a3\u5929\uff0c\u5c09\u8fdf\u70af\u592b\u59bb\u4e4b\u6240\u4ee5\u80fd\u591f\u6df7\u8fdb\u8428\u5e9c\uff0c\u5c31\u662f\u9760\u4e86 \u4ed6\u7528\u7ea2\u748e\u4f1a\u8fd9\u5757\u62db\u724c\u63a9\u62a4\u3002\u91d1\u9010\u6d41\u4e5f\u662f\u4ed6\u6697\u4e2d\u653e\u8d70\u7684\u3002\u54a6\uff0c\u8fd9\u4e9b\u4e8b\u60c5\u91d1\u9010\u6d41\u6ca1\u6709\u544a\u8bc9\u4f60 \u5417\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u6ca1\u6709\u3002\u4f46\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u8001\u513f\u5012\u662f\u503c\u5f97\u4ee4\u4eba\u94a6\u656c\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u8bf4\u9053\uff1a\u201c\u662f\u5440\u3002\u6240\u4ee5\u4ece\u8fd9\u4ef6\u4e8b\u5b9e\u6211\u5df2\u770b\u6e05\u695a\u4e86\uff1a\u771f\u6b63\u7684\u82f1\u96c4\u597d\u6c49\u662f\u7edd\u4e0d\u4f1a\u6295\u9760\u671d \u5ef7\u7684\uff0c\u6211\u53f2\u767d\u90fd\u867d\u7136\u7b97\u4e0d\u5f97\u82f1\u96c4\u597d\u6c49\uff0c\u4f46\u82e5\u518d\u4e0d\u56de\u5934\uff0c\u5c82\u4e0d\u662f\u53eb\u5929\u4e0b\u82f1\u96c4\u597d\u6c49\u7b11\u8bdd\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u5927\u559c\u9053\uff1a\u201c\u5bf9\u6781\uff0c\u5bf9\u6781\uff01\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6211\u672c\u6765\u662f\u60f3\u529d\u4f60\u6539\u90aa\u5f52\u6b63\u7684\uff0c\u4e0d\u6599\u4f60\u6bd4\u6211\u8bf4 \u5f97\u8fd8\u8981\u900f\u5f7b\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u73b0\u5728\u54b1\u4eec\u53ef\u662f\u4eb2\u5bb6\u4e0d\u662f\u654c\u4eba\u4e86\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u53f2\u5927\u54e5\uff0c\u4f60\u4e0d\u518d\u4e0e\u5b98\u5e9c\u5f80\u6765\uff0c\u8fd9\u56fa\u7136\u5f88\u597d\uff0c\u4f46\u8981\u4ee4\u5929\u4e0b\u82f1\u96c4\u5bf9\u4f60\u94a6\u656c\uff0c\u5374 \u8fd8\u4f3c\u4e4e\u4e0d\u591f\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u6211\u61c2\u5f97\u8001\u5f1f\u7684\u610f\u601d\u3002\u6211\u6b63\u60f3\u8bf7\u4f60\u5e2e\u5fd9\uff0c\u5e2e\u5fd9\u6211\u4e0e\u4e49\u519b\u8054\u7edc\u3002\u4ed6\u4eec\u672a\u5fc5\u80af\u76f8 \u4fe1\u6211\uff0c\u8fd9\u4e5f\u8981\u8bf7\u8001\u5f1f\u7ed9\u6211\u8868\u767d\u5fc3\u80a0\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u53f2\u5927\u54e5\u6709\u6b64\u51b3\u5fc3\uff0c\u5c06\u6765\u4e00\u5b9a\u53ef\u4ee5\u627e\u5230\u95e8\u8def\u7684\u3002\u6211\u548c\u4e49\u519b\u7684\u9996\u8111\u4eba\u7269\u5e76\u65e0\u5f80 \u6765\uff0c\u6162\u6162\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u53c8\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4e2a\u610f\u89c1\uff0c\u4f60\u770b\u53ef\u4e0d\u53ef\u884c\uff1f\u5929\u9b54\u6559\u662f\u4ee4\u5802\u6240\u521b\uff0c\u5f53\u5e74\u4ee4\u5802\u542c\u4e86\u91d1\u4e16 \u9057\u7684\u529d\u544a\uff0c\u672a\u66fe\u6df1\u601d\u719f\u8651\uff0c\u5c31\u628a\u5b83\u89e3\u6563\uff0c\u5b9e\u5728\u53ef\u60dc\u3002\u5176\u5b9e\u5929\u9b54\u6559\u867d\u662f\u90aa\u6559\uff0c\u4f46\u82e5\u7528\u5f97\u5176\u6b63\uff0c \u4e5f\u662f\u4e00\u6837\u53ef\u4ee5\u53cd\u6e05\u3002\u8001\u5f1f\uff0c\u4f60\u5982\u679c\u91cd\u7ec4\u5929\u9b54\u6559\uff0c\u6211\u4e5f\u53ef\u4ee5\u52a9\u4f60\u4e00\u81c2\u4e4b\u529b\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u7b11\u9053\uff1a\u201c\u9633\u6d69\u4ed6\u4eec\u4e5f\u60f3\u62e5\u6211\u4f5c\u6559\u4e3b\uff0c\u6211\u6ca1\u6709\u7b54\u5e94\uff0c\u8fd8\u56e0\u6b64\u548c\u4ed6\u4eec\u6253\u4e86\u4e00\u573a\u5462\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u9633\u6d69\u662f\u60f3\u5229\u7528\u4f60\u4f5c\u5080\u5121\uff0c\u4ed6\u81ea\u5df1\u8981\u505a\u5929\u9b54\u6559\u7684\u592a\u4e0a\u7687\u3002\u8fd9\u60f3\u6cd5\u548c\u6211\u521a\u624d\u6240 \u8bf4\u7684\u5b8c\u5168\u4e0d\u540c\u3002\u636e\u6211\u6240\u77e5\uff0c\u5929\u9b54\u6559\u6709\u51e0\u4e2a\u65e7\u4eba\uff0c\u91ce\u5fc3\u52c3\u52c3\uff0c\u5373\u523b\u6b63\u5728\u8fdb\u884c\u91cd\u65b0\u7ec4\u6559\u4e4b\u4e8b\u3002\u4f46 \u662f\u53ea\u8981\u4f60\u7ad9\u51fa\u53bb\uff0c\u65e0\u4eba\u53ef\u4e0e\u4f60\u4e89\u3002\u6211\u529d\u4f60\u4e0d\u8981\u653e\u5f03\u8fd9\u4e2a\u673a\u4f1a\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u7b11\u9053\uff1a\u201c\u6211\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u6211\u662f\u505a\u4e0d\u6765\u6559\u4e3b\u7684\u3002\u6211\u4e5f\u4e0d\u60f3\u505a\u6559\u4e3b\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u5929\u9b54\u6559\u82e5\u80fd\u91cd\u7ec4\uff0c\u5bf9\u4f60\u4eec\u7684\u4e8b\u4e1a\u5f88\u6709\u597d\u5904\u3002\u81f3\u4e8e\u8bf4\u4f60\u6311\u4e0d\u8d77\u91cd\u62c5\uff0c\u6211\u516d\u5408 \u5e2e\u7684\u4eba\uff0c\u90fd\u53ef\u4ee5\u8ba9\u4f60\u501f\u7528\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u60f3\u4e86\u4e00\u60f3\uff0c\u53f2\u767d\u90fd\u8bf4\u7684\u8bdd\u4e5f\u672a\u5c1d\u6ca1\u6709\u9053\u7406\uff0c\u201c\u4f46\u4ed6\u4e3a\u4ec0\u4e48\u8fd9\u6837\u70ed\u5fc3\u5462\uff1f\u201d\u8fd9\u4e48\u4e00 \u60f3\uff0c\u5389\u5357\u661f\u4e0d\u89c9\u6709\u70b9\u8d77\u7591\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u76ee\u524d\u65e0\u610f\u4e8e\u6b64\uff0c\u6b64\u4e8b\u8fd8\u662f\u6682\u4e14\u7f13\u63d0\u5427\u3002\u201d\u53f2\u767d \u90fd\u4e5f\u6015\u8fc7\u4efd\u70ed\u5fc3\u60f9\u4ed6\u8d77\u7591\uff0c\u7b11\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u90a3\u5c31\u7559\u5f85\u4f60\u4eec\u6210\u4eb2\u4e4b\u540e\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u60f3\u89c1\u89c1\u7ea2\u82f1\uff0c\u4e0d\u77e5\u53ef\u5426\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u5fae\u7b11\u9053\uff1a\u201c\u51fa\u9601\u524d\u5915\u7684\u59d1\u5a18\u603b\u662f\u96be\u514d\u6709\u70b9\u5bb3\u7f9e\uff0c\u8d24\u5f1f\u591a\u7b49\u4e00\u5929\uff0c\u660e\u5929\u665a\u4e0a\uff0c\u6d1e\u623f \u518d\u89c1\u597d\u4e48\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u7684\u610f\u601d\u662f\u2026\u2026\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u62e9\u65e5\u4e0d\u5982\u649e\u65e5\uff0c\u4eca\u5929\u6765\u4e0d\u53ca\u4e86\uff0c\u6211\u7684\u610f\u601d\u662f\u660e\u65e5\u5c31\u4e0e\u4f60\u4eec\u6210\u5a5a\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u8fd9\u4e2a\u592a\u5feb\u4e86\u5427\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u7537\u5927\u5f53\u5a5a\uff0c\u5973\u5927\u5f53\u5ac1\uff0c\u4f60\u4eec\u65e2\u7136\u4e24\u60c5\u76f8\u60a6\uff0c\u90a3\u53c8\u4f55\u5fc5\u62d6\u5ef6\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u5fc3\u5934\u535c\u535c\u4e71\u8df3\uff0c\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u8fd9\u6837\u201c\u987a\u5229\u201d\u3002\u53f2\u767d\u90fd\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5a5a\u4e8b\u6709\u6211\u5907 \u529e\uff0c\u4e0d\u5fc5\u8d24\u5f1f\u52b3\u795e\uff0c\u4f60\u4e00\u8def\u8f9b\u82e6\uff0c\u65e9\u70b9\u5b89\u6b47\uff0c\u51c6\u5907\u660e\u65e5\u4f5c\u65b0\u90ce\u5427\u3002\u6211\u4e5f\u8be5\u5411\u820d\u59b9\u62a5\u559c\u4e86\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u53eb\u4eba\u5e26\u9886\u5389\u5357\u661f\u5f80\u5ba2\u623f\u4f11\u606f\uff0c\u4fbf\u5230\u540e\u5802\u53bb\u89c1\u59b9\u59b9\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u88ab\u54e5\u54e5\u8f6f\u7981\u591a\u65e5\uff0c\u4e00\u5fc3\u53ea\u76fc\u91d1\u9010\u6d41\u80fd\u6765\u6551\u5979\uff0c\u7b49\u4e86\u534a\u4e2a\u591a\u6708\uff0c\u8fd8\u672a\u89c1\u91d1\u9010\u6d41\u6765\uff0c \u5fc3\u4e2d\u6b63\u81ea\u70e6\u95f7\uff0c\u89c1\u4e86\u54e5\u54e5\uff0c\u4e0d\u7406\u4e0d\u776c\u3002 \u3000\u3000\u53f2\u767d\u90fd\u7b11\u9053\uff1a\u201c\u4f60\u7684\u597d\u670b\u53cb\u6765\u4e86\uff0c\u4f60\u8be5\u9ad8\u5174\u4e86\u5427\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u9053\u662f\u91d1\u9010\u6d41\u6765\u4e86\uff0c\u5931\u624b\u88ab\u64d2\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u4f60\u4f1a\u820d\u4e86\u6027\u547d\u4e5f\u8981\u6551\u7684\u90a3\u4e2a\u4eba\uff0c\u8fd8\u80fd\u8bf4\u4ed6\u4e0d\u662f\u4f60\u7684\u597d\u670b\u53cb\u4e48\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u8bf4\u7684\u662f\u674e\u5357\u661f\uff1f\u4f60\u628a\u4ed6\u600e\u4e48\u6837\u4e86\uff1f\u201d\u5fc3\u60f3\uff1a\u201c\u674e\u5357\u661f\u6765\u4e86\u4e5f \u597d\uff0c\u4ece\u4ed6\u7684\u53e3\u4e2d\u603b\u53ef\u4ee5\u77e5\u9053\u4e00\u70b9\u91d1\u9010\u6d41\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u4ed6\u4e0d\u59d3\u674e\uff0c\u4ed6\u662f\u5389\u80dc\u7537\u7684\u4f84\u5b59\uff0c\u5389\u590d\u751f\u7684\u513f\u5b50\u3002\u4ed6\u7684\u7236\u6bcd\u662f\u5929\u9b54\u6559\u4ee5\u524d\u7684 \u6b63\u526f\u6559\u4e3b\u3002\u201d\u53f2\u7ea2\u82f1\u9887\u611f\u610f\u5916\uff0c\u4f46\u5374\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u4ed6\u662f\u4ec0\u4e48\u6765\u5386\uff0c\u6211\u53ea\u662f\u8981\u77e5\u9053\u4f60\u628a\u4ed6\u600e \u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u6240\u6b22\u559c\u7684\u4eba\u6765\u4e86\uff0c\u6211\u8fd8\u80fd\u4e0d\u597d\u62db\u5f85\u4ed6\u5417\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u67f3\u7709\u4e00\u575a\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u4ed6\u6765\u6c42\u5a5a\uff0c\u6211\u5df2\u7ecf\u7b54\u5e94\u628a\u4f60\u5ac1\u7ed9\u4ed6\u4e86\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u5403\u4e86\u4e00\u60ca\uff0c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4f60\u5f00\u7684\u4ec0\u4e48\u73a9\u7b11\uff1f\u201d\u6b63\u662f\uff1a \u3000\u3000\u4f46\u5f97\u6709\u60c5\u6210\u7737\u5c5e\uff0c\u955c\u82b1\u79cb\u6708\u5374\u4f55\u582a\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u6d1e\u623f\u4e00\u8bed\u60ca\u8ff7\u68a6\u3000\u82b1\u70db\u4eca\u5bb5\u9690\u6740\u673a \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u6d1e\u623f\u4e00\u8bed\u60ca\u8ff7\u68a6\u3000\u82b1\u70db\u4eca\u5bb5\u9690\u6740\u673a \u3000\u3000\u53f2\u767d\u90fd\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u677f\u8d77\u9762\u5b54\u8bf4\u9053\uff1a\u201c\u8c01\u548c\u4f60\u5f00\u73a9\u7b11\uff1f\u7239\u5a18\u5df2\u6b7b\uff0c\u4f60\u7684\u5a5a\u4e8b\u5c31\u8be5\u7531\u6211 \u4f5c\u4e3b\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u6012\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u80fd\u8ba9\u4f60\u6446\u5e03\uff01\u6211\u4e0d\u5ac1\u5389\u5357\u661f\uff01\u201d \u3000\u3000\u53f2\u91d1\u90fd\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5ac1\u59d3\u5389\u7684\u4e5f\u6210\uff0c\u90a3\u5c31\u5ac1\u7ed9\u5e05\u76c2\u96c4\u5427\u3002\u201d\u4e0d\u9519\uff0c\u505a\u4e00\u4e2a\u5c06\u519b\u592b\u4eba\u4e5f \u8bb8\u597d\u8fc7\u505a\u6559\u4e3b\u592b\u4eba\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u6012\u6781\u6c14\u6781\uff0c\u53cd\u800c\u51b7\u9759\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u662f\u4e0d\u662f\u8981\u628a\u6211\u903c\u6b7b\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u77e5\u9053\u4f60\u5341\u5206\u8ba8\u538c\u5e05\u5b5f\u96c4\uff0c\u6240\u4f3c\u624d\u8ba9\u4f60\u5ac1\u7ed9\u4f60\u559c\u6b22\u7684\u5389\u5357\u661f\u3002\u4f60\u4e0d \u6700\u66fe\u7ecf\u820d\u547d\u62a4\u8fc7\u4ed6\u7684\u4e48\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\u5ea6\u541b\u5b50\u4e4b\u8179\u3002\u5728\u4f60\u7684\u5fc3\u76ee\u4e4b\u4e2d\uff0c\u5927\u7ea6\u4ee5\u4e3a\u7537\u5973\u4e4b\u95f4\u662f \u53ea\u80fd\u505a\u592b\u59bb\u4e0d\u80fd\u505a\u670b\u53cb\u4e86\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u662f\u559c\u6b22\u5389\u5357\u661f\u4e5f\u597d\uff0c\u4e0d\u559c\u6b22\u4e5f\u597d\uff0c\u603b\u4e4b\u53ea\u6709\u4e24\u6761\u8def\u7ed9\u4f60\u9009\u62e9\uff0c\u8981\u561b \u5ac1\u7ed9\u5389\u5357\u661f\uff0c\u8981\u561b\u5ac1\u7ed9\u5e05\u5b5f\u96c4\uff01\u7b2c\u4e09\u4e2a\u4eba\u7edd\u5bf9\u4e0d\u884c\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u51b7\u7b11\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u5c45\u5fc3\u4e86\uff0c\u4f60\u77e5\u9053\u4e86\u5389\u5357\u661f\u662f\u5929\u9b54\u6559\u6559\u4e3b\u7684\u513f\u5b50\u3002\u4f60\u662f\u60f3\u5229 \u7528\u6211\u6765\u9a97\u53d6\u4ed6\u7684\u767e\u6bd2\u771f\u7ecf\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u5fc3\u91cc\u6697\u7b11\u3002\u201d\u767e\u6bd2\u771f\u7ecf\u56fa\u7136\u4e5f\u662f\u6211\u60f3\u8981\u7684\uff0c\u4f46\u8fd8\u6709\u66f4\u7d27\u8981\u7684\u4f60\u8fd8\u672a\u77e5\u9053\u5462\uff01\u6211\u7684 \u795e\u673a\u5999\u7b97\uff0c\u4f60\u53ea\u662f\u731c\u7740\u4e86\u4e00\u70b9\u513f\uff01\u201d\u5fc3\u91cc\u6697\u7b11\uff0c\u5374\u88c5\u4f5c\u7ed9\u5979\u8bf4\u4e2d\u7684\u6837\u5b50\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u662f\u6211\u517b \u5927\u7684\uff0c\u6211\u628a\u4f60\u8bb8\u914d\u7ed9\u4eba\uff0c\u6211\u603b\u5e94\u8be5\u5f97\u70b9\u597d\u5904\u3002\u4f46\u5ac1\u7ed9\u5389\u5357\u661f\u4e5f\u5e76\u4e0d\u59d4\u5c48\u4f60\u5440\uff01\u4ed6\u5e74\u7eaa\u4e0e\u4f60\u767b \u5bf9\uff0c\u6bd4\u8d77\u5e05\u5b5f\u96c4\u6765\u662f\u5e74\u8f7b\u6f02\u4eae\u591a\u4e86\u3002\u4ed6\u7684\u6b66\u529f\u4e5f\u5f88\u4e0d\u5f31\uff0c\u5c06\u6765\u6211\u8fd8\u53ef\u4ee5\u6276\u4ed6\u505a\u5929\u9b54\u6559\u7684\u6559 \u4e3b\uff01 \u3000\u3000\u53f2\u7ea2\u82f1\u9762\u8272\u6da8\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u628a\u6211\u5f53\u4f5c\u4ec0\u4e48\uff1f\u54fc\uff0c\u4f60\u662f\u628a\u6211\u5f53\u4f5c\u53ef\u4ee5\u4ea4\u6362\u7684\u8d27\u7269\u5417\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u6211\u662f\u4e3a\u4f60\u597d\u3002\u4e3a\u4f60\u7740\u60f3\u3002\u4f60\u5ac1\u7ed9\u59d3\u5389\u8fd9\u5c0f\u5b50\u603b\u80dc\u4e8e\u5ac1\u7ed9\u5e05\u5b5f\u96c4\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u6697\u81ea\u5728\u6253\u5982\u610f\u7b97\u76d8\uff0c\u54ea\u77e5\u53f2\u7ea2\u82f1\u4e5f\u5728\u5fc3\u4e2d\u76d8\u7b97\uff0c\u76ee\u524d\u5f62\u52bf\uff0c\u5979\u82e5\u662f\u4e0d\u4ece\u54e5\u54e5\u4e4b \u547d\uff0c\u90a3\u5c31\u975e\u5f97\u786c\u62fc\u4e0d\u53ef\u3002\u201c\u5389\u5357\u661f\u4e0d\u77e5\u662f\u4f55\u7528\u5fc3\uff0c\u4f46\u4ed6\u603b\u662f\u91d1\u9010\u6d41\u7684\u670b\u53cb\uff0c\u76ee\u524d\u6211\u5b64\u7acb\u5149 \u63f4\uff0c\u5982\u679c\u80fd\u591f\u6709\u4e00\u4e2a\u4eba\u548c\u6211\u5546\u91cf\u4e5f\u672a\u5c1d\u4e0d\u597d\u3002\u6700\u5c11\u5389\u5357\u661f\u4e0d\u81f3\u4e8e\u50cf\u6211\u54e5\u54e5\u4e00\u6837\u86ee\u4e0d\u8bb2\u7406\u3002\u201d \u53f2\u7ea2\u82f1\u5fc3\u60f3\u3002 \u3000\u3000\u53f2\u767d\u90fd\u89c1\u59b9\u59b9\u4f4e\u5934\u4e0d\u8bed\uff0c\u53ea\u9053\u5979\u5df2\u56de\u5fc3\u8f6c\u610f\uff0c\u4fbf\u9053\uff1a\u201c\u4f60\u60f3\u6e05\u695a\u4e86\u6ca1\u6709\uff1f\u6211\u770b\u4f60\u8fd8\u662f\u7b54 \u5e94\u7684\u597d\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u88c5\u4f5c\u8d4c\u6c14\u7684\u6837\u5b50\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u8981\u4ece\u6211\u7684\u5a5a\u59fb\u53d6\u5f97\u597d\u5904\uff0c\u6211\u8fd8\u80fd\u4e0d\u8ba9\u4f60\u6446\u5e03\u5417\uff1f \u597d\uff0c\u4ece\u4eca\u4e4b\u540e\u6211\u7b97\u662f\u62a5\u7b54\u4e86\u4f60\u7684\u517b\u80b2\u4e4b\u6069\uff0c\u4f60\u4e5f\u522b\u6307\u671b\u6211\u518d\u628a\u4f60\u5f53\u4f5c\u54e5\u54e5\u4e86\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u59b9\u59b9\u4e0d\u5fc5\u8bf4\u5f97\u8fd9\u6837\u7edd\u60c5\uff0c\u54b1\u4eec\u5144\u59b9\u603b\u662f\u5144\u59b9\uff0c\u8fd9\u6869\u5a5a\u4e8b\uff0c\u5bf9 \u53cc\u65b9\u90fd\u6709\u597d\u5904\u3002\u5389\u5357\u661f\u5bf9\u4f60\u4e00\u7248\u75f4\u5fc3\uff0c\u4ed6\u51b3\u4e0d\u4f1a\u4e8f\u5f85\u4f60\u7684\u3002\u5ac1\u4e86\u4e4b\u540e\uff0c\u4f60\u5f97\u5230\u5e78\u798f\uff0c\u5c31\u77e5\u9053 \u611f\u6fc0\u4f60\u7684\u54e5\u54e5\u5566\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u201c\u5927\u529f\u544a\u6210\u201d\uff0c\u6ee1\u5fc3\u6b22\u559c\u7684\u51fa\u53bb\u4e0e\u4ed6\u7684\u624b\u4e0b\u4e09\u5927\u9999\u4e3b\u5546\u91cf\uff0c\u8463\u4e09\u5a18\u5c1a\u672a\u56de\u6765\u3002\u9752 \u7b26\u9053\u4eba\u9053\uff1a\u201c\u5c31\u53ea\u6015\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\u6765\u6363\u4e71\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff0c\u201c\u6211\u5374\u53ea\u6015\u4ed6\u4e0d\u6765\uff0c\u4ed6\u80af\u6765\u81ea\u6295\u7f57\u7f51\uff0c\u5c82\u4e0d\u66f4\u5999\uff01\u201d \u3000\u3000\u5706\u6d77\u548c\u5c1a\u9053\uff1a\u201c\u4f46\u8fd9\u5c0f\u5b50\u795e\u51fa\u9b3c\u6ca1\uff0c\u53ea\u6015\u6349\u4e0d\u4f4f\u4ed6\uff0c\u5012\u662f\u8981\u591a\u4e9b\u5c0f\u5fc3\u7684\u597d\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u7b11\u9053\uff1a\u201c\u6349\u4e0d\u4f4f\u4ed6\u4e5f\u6709\u6349\u4e0d\u4f4f\u7684\u597d\u5904\uff0c\u4f60\u4eec\u60f3\u8fc7\u8fd9\u4e00\u5c42\u4e48\u3002\u201d \u3000\u3000\u5706\u6d77\u6293\u6293\u5149\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6211\u5c31\u4e0d\u61c2\u4e86\uff0c\u8bf7\u5e2e\u4e3b\u6307\u6559\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\u82e5\u662f\u770b\u5230\u7ea2\u82f1\u548c\u4ed6\u7684\u597d\u53cb\u6d1e\u623f\u82b1\u70db\uff0c\u4f60\u60f3\u4ed6\u7684\u5fc3\u91cc\u662f\u4ec0\u4e48\u6ecb \u5473\uff1f\u201d \u3000\u3000\u7126\u78ca\u7b11\u9053\uff1a\u201c\u6211\u61c2\u4e86\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u4ed6\u4eec\u597d\u670b\u53cb\u5c31\u8981\u53d8\u4f5c\u4ec7\u4eba\u5566\uff01\u201d \u3000\u3000\u5706\u6d77\u604d\u7136\u5927\u609f\uff1b\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u8fd9\u662f\u79bb\u95f4\u4e4b\u8ba1\u3002\u5e2e\u4e3b\u7684\u795e\u673a\u5999\u7b97\u786e\u975e\u5e38\u4eba\u6240\u53ca\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u4f46\u54b1\u4eec\u4e5f\u8fd8\u662f\u8981\u6709\u9632\u5907\uff0c\u53ea\u80fd\u8ba9\u4ed6\u77e5\u9053\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4e0d\u80fd\u8ba9\u4ed6\u548c\u7ea2\u82f1\u89c1 \u9762\u3002\u201d \u3000\u3000\u4e09\u4e2a\u9999\u4e3b\u9f50\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\u3002\u5e2e\u4e3b\u51c6\u5907\u600e\u6837\u5e03\u7f6e\uff0c\u6211\u4eec\u542c\u5e2e\u4e3b\u5206\u6d3e\u3002\u201d \u3000\u3000\u5706\u6d77\u53c8\u9053\uff1a\u201c\u53ef\u60dc\u8463\u5341\u4e09\u5a18\u6ca1\u6709\u56de\u6765\uff0c\u4e0d\u77e5\u5979\u548c\u5e2e\u4e3b\u8bf4\u4eb2\u7684\u4e8b\u60c5\u529e\u5f97\u600e\u4e48\u6837\u4e86\uff1f\u201d \u3000\u3000\u7126\u78ca\u7b11\u9053\uff1a\u201c\u90a3\u662f\u51c6\u80fd\u6210\u529f\u7684\u3002\u54b1\u4eec\u8fc7\u51e0\u5929\u518d\u559d\u5e2e\u4e3b\u7684\u559c\u9152\uff0c\u4e0d\u662f\u66f4\u70ed\u95f9\u5417\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\u3002\u4f9d\u6211\u63a8\u7b97\uff0c\u8463\u5341\u4e09\u5a18\u6700\u8fdf\u660e\u5929\u4e5f\u8be5\u56de\u6765\u4e86\u3002\u76ee\u524d\u6700\u7d27\u8981 \u7684\u8fd8\u662f\u600e\u6837\u5bf9\u4ed8\u91d1\u9010\u6d41\uff0c\u6211\u7684\u4e8b\u60c5\u5012\u4e0d\u5fc5\u4f60\u4eec\u7740\u6025\uff1f\u201d \u3000\u3000\u9752\u7b26\u9053\u4eba\u51d1\u8da3\u9053\uff1a\u201c\u5f53\u7136\uff0c\u5f53\u7136\u3002\u5e2e\u4e3b\u662f\u6210\u7af9\u5728\u80f8\uff0c\u8fd9\u676f\u559c\u9152\u8fdf\u65e9\u603b\u662f\u6709\u5f97\u559d\u7684\u3002\u201d\u5f53 \u4e0b\u5404\u81ea\u6563\u53bb\uff0c\u6309\u7167\u53f2\u767d\u90fd\u7684\u5429\u5490\u5e03\u7f6e\u2014\u2014\u201c\u5b89\u6392\u9999\u9975\u9493\u91d1\u9ccc\uff01\u201d \u3000\u3000\u5c31\u5728\u53f2\u767d\u90fd\u4ed6\u4eec\u60a3\u5f97\u60a3\u5931\uff0c\u65e2\u6015\u91d1\u9010\u6d41\u6765\u53c8\u6015\u91d1\u9010\u6d41\u4e0d\u6765\u7684\u65f6\u5019\uff0c\u91d1\u9010\u6d41\u548c\u9648\u5149\u7167\u3001\u77f3 \u971e\u59d1\u4e09\u4eba\u5230\u4e86\u626c\u5dde\u3002 \u3000\u3000\u8fd9\u65e5\u6b63\u662f\u53f2\u767d\u90fd\u6240\u5b89\u6392\u7684\uff0c\u53f2\u7ea2\u82f1\u548c\u5389\u5357\u661f\u6210\u5a5a\u7684\u65e5\u5b50\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6309\u7167\u539f\u8d70\u7684\u8ba1\u5212\uff0c\u5148\u5230\u4e10\u5e2e\u5206\u8235\u62dc\u8bbf\uff0c\u626c\u5dde\u7684\u4e10\u5e2e\u8235\u4e3b\u540d\u5524\u674e\u8302\uff0c\u4e5f\u662f\u66fe\u7ecf\u5728\u6c5f \u6d77\u5929\u5bb6\u671b\u89c1\u8fc7\u91d1\u9010\u6d41\u7684\u3002 \u3000\u3000\u674e\u8302\u4e00\u89c1\u4e86\u91d1\u9010\u6d41\u5c31\u9053\uff1a\u201c\u91d1\u5c11\u4fa0\uff0c\u4f60\u6765\u5f97\u771f\u662f\u5de7\u6781\u4e86\uff01\u4f60\u90a3\u4f4d\u59d3\u5389\u7684\u670b\u53cb\u5c31\u6b63\u662f\u4eca\u5929 \u4f5c\u516d\u5408\u5e2e\u7684\u5a07\u5ba2\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u5389\u5357\u661f\u4e0e\u53f2\u767d\u90fd\u7684\u59b9\u5b50\u5c31\u5728\u4eca\u5929\u6210\u4eb2\uff1f\u600e\u7684\u8fd9\u6837\u5feb\uff01\u201d \u3000\u3000\u674e\u8302\u9053\uff1a\u201c\u6211\u4eec\u6709\u4eba\u5728\u516d\u5408\u5e2e\u5367\u5e95\uff0c\u542c\u8bf4\u4f60\u90a3\u4f4d\u670b\u53cb\u662f\u6628\u665a\u624d\u5230\u7684\uff0c\u53f2\u767d\u90fd\u7acb\u5373\u5c31\u7b54\u5e94 \u4e86\u5a5a\u4e8b\u3002\u8fde\u591c\u53d1\u5e16\u8bf7\u5ba2\uff0c\u7ed3\u5f69\u5f20\u706f\uff0c\u516d\u5408\u5e2e\u4eba\u591a\u52bf\u5927\uff0c\u8bf8\u4e8b\u5484\u55df\u7acb\u529e\uff0c\u8fd9\u6869\u4e8b\u594f\u867d\u662f\u6765\u5f97\u4ed3 \u5352\uff0c\u4f46\u5374\u6beb\u4e0d\u8349\u7387\u3002\u4eca\u5929\u4e00\u65e9\uff0c\u90fd\u5df2\u5907\u529e\u597d\u4e86\u3002\u770b\u6837\u5b50\u4e0d\u50cf\u662f\u5047\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u6697\u81ea\u601d\u91cf\uff1a\u201c\u8fd9\u4e8b\u5b9a\u7136\u662f\u4e2a\u9a97\u5c40\u3002\u4e0d\u8fc7\uff0c\u4e07\u4e00\u662f\u771f\u7684\u8bdd\uff0c\u6211\u5e26\u4e86\u8bb8\u591a \u4eba\u53bb\u95f9\u6d1e\u623f\uff0c\u5c82\u4e0d\u574f\u4e86\u5389\u5927\u54e5\u7684\u597d\u4e8b\uff1f\u4e0d\u5982\u6211\u5355\u72ec\u524d\u5f80\uff0c\u89c1\u673a\u800c\u4e3a\u3002\u201d \u3000\u3000\u5f53\u4e0b\u91d1\u9010\u6d41\u628a\u81ea\u5df1\u7684\u4e3b\u610f\u548c\u9648\u5149\u7167\u3001\u77f3\u971e\u59d1\u7b49\u4eba\u8bf4\u4e86\uff0c\u5927\u5bb6\u4e5f\u90fd\u540c\u610f\u4e86\u4ed6\u7684\u5b89\u6392\u3002\u5373\u662f \u8ba9\u91d1\u9010\u6d41\u5148\u53bb\u6253\u542c\u865a\u5b9e\uff0c\u4e10\u5e2e\u7684\u4eba\u4e5f\u4f5c\u597d\u4e86\u63a5\u5e94\u7684\u51c6\u5907\u3002 \u3000\u3000\u8bdd\u5206\u4e24\u5934\u3002\u4e14\u8bf4\u5389\u5357\u661f\u8fd9\u65e5\u5f97\u507f\u5fc3\u613f\uff0c\u559c\u6c14\u6d0b\u6d0b\uff0c\u4f46\u5728\u62dc\u5802\u7684\u65f6\u5019\uff0c\u5374\u53d1\u73b0\u4e86\u4e00\u4ef6\u51fa\u4ed6 \u610f\u5916\u7684\u4e8b\u3002\u65b0\u5a18\u5b50\u662f\u7528\u7f57\u5e15\u8499\u5934\u7684\u9762\u8c8c\u770b\u4e0d\u89c1\uff0c\u4f46\u770b\u8fd9\u65b0\u5a18\u5b50\u7684\u4f53\u6001\u5374\u4e0d\u50cf\u662f\u53f2\u7ea2\u82f1\u3002 \u3000\u3000\u4f9d\u7167\u5a5a\u793c\u98ce\u4fd7\uff0c\u65b0\u90ce\u662f\u8981\u8fdb\u4e86\u6d1e\u623f\u4e4b\u540e\uff0c\u624d\u80fd\u63ed\u5f00\u65b0\u5a18\u7684\u201c\u8499\u5934\u201d\u7684\u3002\u662f\u4ee5\u5389\u6d77\u661f\u867d\u6709 \u6240\u7591\uff0c\u5374\u4e5f\u4e0d\u6562\u9020\u6b21\u3002 \u3000\u3000\u5389\u5357\u661f\u6000\u7740\u60f4\u60f4\u4e0d\u5b89\u7684\u5fc3\u60c5\uff0c\u597d\u4e0d\u5bb9\u6613\u7b49\u5230\u5929\u9ed1\u8fdb\u5165\u6d1e\u623f\u3002\u53ea\u89c1\u7ea2\u70db\u9ad8\u70e7\uff0c\u73e0\u5e18\u534a\u5377\uff0c \u65b0\u5a18\u5b50\u7aef\u7aef\u6b63\u6b63\u7684\u5750\u5728\u5e8a\u4e0a\uff0c\u53ef\u4e0d\u6b63\u662f\u53f2\u7ea2\u82f1\uff1f\u7167\u98ce\u4fd7\u662f\u8981\u65b0\u90ce\u66ff\u65b0\u5a18\u5b50\u63ed\u5f00\u7f57\u5e15\u7684\uff0c\u4f46\u5750 \u5728\u5e8a\u4e0a\u7684\u8fd9\u4e2a\u201c\u65b0\u5a18\u5b50\u201d\u53f2\u7ea2\u82f1\u5374\u6839\u672c\u5c31\u6ca1\u6709\u8499\u5934\uff01\u7a7f\u6234\u4e5f\u4e0d\u50cf\u65b0\u5a18\u5b50\u7684\u6a21\u6837\u3002 \u3000\u3000\u5389\u5357\u661f\u4e00\u76f8\u60c5\u613f\uff0c\u4e00\u770b\u89c1\u5728\u65b0\u623f\u4e2d\u7684\u662f\u53f2\u7ea2\u82f1\uff0c\u5fc3\u4e2d\u5df2\u662f\u6781\u4e3a\u6b22\u559c\u3002\u4ed6\u677e\u4e86\u53e3\u6c14\uff0c\u60f3 \u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u662f\u53f2\u767d\u90fd\u9a97\u6211\uff0c\u6765\u4e2a\u6389\u5305\u4e4b\u8ba1\u5462\uff0c\u5012\u662f\u6211\u7684\u591a\u7591\u4e86\u3002\u201d \u3000\u3000\u5f53\u7136\u5389\u5357\u661f\u4e5f\u4e0d\u80fd\u4e0d\u6709\u70b9\u731c\u7591\uff1a\u201c\u7ea2\u82f1\u4e3a\u4f55\u8fd9\u6837\u6253\u626e\uff0c\u5e76\u4e0d\u50cf\u4e2a\u65b0\u5a18\uff1f\u201d\u4f46\u4ed6\u81ea\u5df1\u53c8\u7ed9 \u81ea\u5df1\u5f00\u89e3\uff1a\u201c\u662f\u4e86\uff0c\u7ea2\u82f1\u672c\u6765\u5c31\u662f\u4e2a\u51fa\u5c18\u7edd\u4fd7\u4e0e\u4f17\u4e0d\u540c\u7684\u5973\u5b50\uff0c\u5979\u548c\u6211\u65e9\u5c31\u76f8\u8bc6\uff0c\u65e9\u5c31\u610f\u5408 \u60c5\u6295\uff0c\u90a3\u4ee4\u4eba\u6c14\u95f7\u7684\u635e\u4ec0\u5b50\uff1f\u65b0\u5a18\u5b50\u90fd\u662f\u6253\u626e\u5f97\u5341\u5206\u4fd7\u6c14\u7684\uff0c\u5979\u7528\u672c\u6765\u9762\u76ee\u89c1\u6211\uff0c\u5c82\u4e0d\u66f4 \u597d\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u60f3\u5f97\u5982\u610f\uff0c\u5fcd\u4e0d\u7740\u5fc3\u4e2d\u7684\u559c\u60a6\uff1a\u4e0a\u524d\u4f5c\u4e86\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u54b1\u4eec\u4f1a\u6709\u4eca\u5929\u3002 \u7ea2\u82f1\uff0c\u4f60\u90a3\u6b21\u6551\u4e86\u6211\u7684\u6027\u547d\uff0c\u6211\u8fd8\u672a\u66fe\u5411\u4f60\u9053\u8c22\u5462\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4f60\u662f\u91d1\u9010\u6d41\u7684\u670b\u53cb\uff0c\u6211\u6551\u4f60\u662f\u5e94\u8be5\u7684\u3002\u201d\u5389\u5357\u661f\u8fd8\u542c\u4e0d\u51fa\u8bdd\u4e2d\u4e4b\u610f\uff0c\u8bf4 \u9053\uff1a\u201c\u662f\u5440\uff0c\u9010\u6d41\u662f\u4f60\u6211\u5171\u540c\u7684\u670b\u53cb\u3002\u53ef\u60dc\u4ed6\u4eca\u5929\u4e0d\u80fd\u6765\u53c2\u52a0\u6211\u4eec\u7684\u5a5a\u793c\u3002\u7ea2\u82f1\uff0c\u4f60\u53ef\u77e5\u9053 \u6211\u662f\u5982\u4f55\u7231\u6155\u4f60\u5417\uff1f\u6211\u7684\u5fc3\u4e8b\u8fd8\u672a\u66fe\u5411\u4f60\u503e\u5410\u5462\uff1f\u4f46\u73b0\u5728\u4e5f\u4e0d\u7528\u6211\u518d\u591a\u8bf4\u4e86\uff0c\u6211\u662f\u592a\u9ad8\u5174 \u4e86\uff01\u6211\u53ea\u60f3\u6211\u7684\u670b\u53cb\u90fd\u80fd\u4e3a\u6211\u9ad8\u5174\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u5ffd\u5730\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u770b\u770b\u5916\u9762\u6709\u6ca1\u6709\u4eba\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u5e2e\u4e2d\u7684\u5e2e\u5144\u5927\u7ea6\u4e0d\u4f1a\u5f00\u8fd9\u79cd\u65e0\u804a\u7684\u73a9\u7b11\u7684\u3002\u201d\u4ed6\u53ea\u9053\u53f2\u7ea2\u82f1 \u6015\u6709\u4eba\u5077\u542c\u6d1e\u623f\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u6b63\u5bb9\u9053\uff1a\u201c\u8fd9\u53ef\u4e0d\u662f\u5f00\u73a9\u7b11\u7684\u4e8b\u60c5\uff0c\u4f60\u5feb\u53bb\u770b\uff0c\u770b\u4ed4\u7ec6\u4e9b\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u51fa\u53bb\u4e00\u770b\uff0c\u53ea\u89c1\u661f\u6cb3\u803f\u803f\uff0c\u660e\u6708\u5728\u5929\u3002\u5916\u95f4\u7e41\u6b4c\u672a\u6b47\uff0c\u72b9\u81ea\u9690\u9690\u53ef\u95fb\u3002\u4f46\u5374\u770b\u4e0d \u5230\u4eba\u7684\u5f71\u5b50\uff0c\u4e5f\u542c\u4e0d\u5230\u53ef\u7591\u7684\u58f0\u97f3\u3002\u5ead\u9662\u6df1\u6df1\uff0c\u5185\u5916\u9694\u65ad\u3002\u5389\u5357\u661f\u5fc3\u60f3\uff1a\u201c\u516d\u5408\u5e2e\u7684\u5e2e\u89c4\u751a \u4e25\uff0c\u5916\u9762\u7684\u95f9\u623f\u4eba\u60f3\u6765\u4e5f\u51b3\u4e0d\u6562\u95ef\u8fdb\u5185\u9662\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u56de\u6765\u968f\u624b\u5173\u4e0a\u623f\u95e8\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u653e\u5fc3\u4e86\uff0c\u5e76\u6ca1\u6709\u4eba\u5077\u542c\u6d1e\u623f\u3002\u65f6\u5019\u4e0d\u65e9\uff0c \u4f60\uff0c\u2026\u2026\u201d \u3000\u3000\u5389\u5357\u661f\u662f\u60f3\u8bf7\u53f2\u7ea2\u82f1\u5378\u88c5\uff0c\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u53f2\u7ea2\u82f1\u628a\u8896\u4e00\u6325\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6015\u6709\u4eba\u5077 \u542c\u6d1e\u623f\uff0c\u4eca\u665a\u4e5f\u5e76\u975e\u6d1e\u623f\u82b1\u70db\uff0c\u4f60\u5750\u8fc7\u6765\uff0c\u6211\u6709\u8bdd\u8bf4\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u5403\u4e86\u4e00\u60ca\uff0c\u770b\u4e86\u770b\u53f2\u7ea2\u82f1\uff0c\u4e00\u526f\u51db\u7136\u7684\u795e\u6001\uff0c\u4e0d\u50cf\u662f\u5f00\u4ed6\u73a9\u7b11\uff0c\u5389\u5357\u661f\u6ee1\u8179\u7591 \u4e91\uff0c\u5750\u4e86\u4e0b\u6765\uff0c\u8bb7\u8bb7\u8bf4\u8fdb\u3002\u201d\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u54b1\u4eec\u4e0d\u662f\u5df2\u7ecf\u4ea4\u62dc\u4e86\u5929\u5730\u4e48\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4eca\u5929\u548c\u4f60\u62dc\u5802\u7684\u662f\u6211\u7684\u4e2b\u9b1f\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u66f4\u662f\u5403\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u4f60\u3001\u4f60\u4e0d\u613f\u610f\u5ac1\u6211\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u5148\u95ee\u4f60\uff0c\u4f60\u4ee5\u4e3a\u4eca\u5929\u529e\u7684\u662f\u559c\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u96be\u9053\u4f1a\u662f\u7978\u4e8b\uff1f\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4e0d\u9519\u3002\u9664\u975e\u4f60\u613f\u610f\u505a\u6211\u54e5\u54e5\u7684\u722a\u7259\uff0c\u5426\u5219 \u5bf9\u4f60\u5c31\u662f\u6740\u8eab\u4e4b\u7978\uff01\u201d \u3000\u3000\u5389\u6d77\u661f\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u62c5\u5fc3\u8fd9\u4e2a\uff0c\u4f60\u7684\u54e5\u54e5\u5df2\u7ecf\u4eb2\u53e3\u7b54\u5e94\u4e86\u6211\uff0c\u4ee5\u540e\u6539\u90aa\u5f52\u6b63\uff0c\u8fd8\u60f3\u53c2 \u52a0\u4e49\u519b\uff0c\u8bf7\u6211\u7ed9\u4ed6\u758f\u901a\u5462\u3002\u5018\u82e5\u4e0d\u662f\u8fd9\u6837\uff0c\u6211\u4e5f\u4e0d\u6562\u5728\u516d\u5408\u5e2e\u4e2d\u8ddf\u4f60\u6210\u4eb2\u3002\u55ef\uff0c\u4f60\u73b0\u5728\u53ef\u4ee5 \u653e\u5fc3\u4e86\u5427\uff0c\u4e0d\u662f\u6211\u8ddf\u4f60\u7684\u54e5\u54e5\u540c\u6d41\u5408\u6c61\uff0c\u800c\u662f\u4f60\u7684\u54e5\u54e5\u6094\u6539\u524d\u975e\uff0c\u4e0e\u6211\u5408\u6d41\u4e86\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e8b\u6bd4\u6211\u8bbe\u60f3\u7684\u66f4\u574f\u3002\u770b\u6765\u6211\u7684\u54e5\u54e5\u4e0d\u4ec5\u662f\u60f3\u9a97\u53d6\u4f60\u7684\u767e\u6bd2 \u771f\u7ecf\uff0c\u8fd8\u6709\u66f4\u5927\u7684\u9634\u8c0b\u5728\u5185\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u534a\u4fe1\u534a\u7591\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u76f8\u4fe1\u4f60\u7684\u54e5\u54e5\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6628\u665a\u4ed6\u4e5f\u548c\u6211\u8bf4\u4e86\u4e00\u4e9b\u8bdd\uff0c\u6211\u8bf4\u7ed9\u4f60\u542c\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u542c\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4ed6\u662f\u4e00\u76f4\u6ca1\u6709\u653e\u5f03\u628a\u4f60\u5ac1\u7ed9\u5e05\u5b5f\u96c4\u7684\u5ff5 \u5934\uff0c\u53ea\u56e0\u4f60\u4e0d\u4f9d\u8ba4\uff0c\u624d\u65e0\u53ef\u5948\u4f55\u7b54\u5e94\u4e86\u4f60\u7684\u5a5a\u4e8b\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u9519\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u90a3\u4e48\u4ed6\u4e0e\u5e05\u5b5f\u96c4\u4e00\u76f4\u6ca1\u6709\u65ad\u7edd\u5f80\u6765\uff1f\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5df2\u77e5\u4e0d\u5999\uff0c\u58f0\u97f3\u90fd\u98a4\u6296 \u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u5c82\u53ea\u4e0e\u5e05\u5b5f\u96c4\u8fd8\u6709\u5f80\u6765\uff0c\u548c\u8428\u798f\u9f0e\u4e5f\u662f\u4e00\u76f4\u6697\u901a\u6d88\u606f\u3002\u65b0\u4efb\u7684\u5fa1\u6797\u519b\u526f\u7edf \u9886\uff0c\u8428\u798f\u9f0e\u624b\u4e0b\u7684\u7b2c\u4e00\u4f4d\u7ea2\u4eba\u6587\u9053\u5e84\u524d\u5929\u624d\u4ece\u4eac\u4e2d\u6765\u5230\uff0c\u73b0\u5728\u8fd8\u4f4f\u5728\u8fd9\u513f\u3002\u4e0d\u8fc7\uff0c\u4ed6\u77e5\u9053\u4f60 \u8ba4\u8bc6\u4ed6\uff0c\u6545\u800c\u907f\u4e0d\u89c1\u4f60\u7f62\u4e86\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\uff0c\u4f60\u7684\u54e5\u54e5\u662f\u5b8c\u5168\u9a97\u6211\u7684\u4e86\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u5f53\u7136\u662f\u4e2a\u9a97\u5c40\uff01\u6211\u548c\u4ed6\u505a\u4e86\u4e8c\u5341\u5e74\u5144\u59b9\uff0c\u96be\u9053\u8fd8\u4e0d\u77e5\u9053\u4ed6\u7684\u4e3a\u4eba\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u90a3\u600e\u4e48\u529e\uff1f\u55ef\uff0c\u7ea2\u82f1\uff0c\u54b1\u4eec\u4e00\u540c\u9003\u8d70\u4e86\u5427\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u7684\u54e5\u54e5\u4e0d\u4f1a\u6ca1\u6709\u9632\u5907\u7684\uff0c\u5c24\u5176\u662f\u5728\u4eca\u665a\uff0c\u9ad8\u624b\u4e91\u96c6\uff0c\u66f4\u8dd1\u4e0d\u6389\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u90a3\u4e48\u4f9d\u4f60\u4e4b\u89c1\u2026\u2026\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u7559\u4e0b\u6765\u5c06\u8ba1\u5c31\u8ba1\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u5fc3\u5934\u6026\u7136\u8df3\u52a8\uff0c\u6697\u81ea\u5fd6\u601d\uff0c\u201c\u5bf9\uff0c\u7559\u4e0b\u6765\u5148\u505a\u4e86\u592b\u59bb\u518d\u8bf4\uff0c\u83ab\u8f9c\u8d1f\u4eca\u5bb5\u82b1\u70db\u3002\u201d \u4f46\u8fd9\u8bdd\u4ed6\u5f53\u7136\u4e0d\u597d\u610f\u601d\u8bf4\u51fa\u6765\uff0c\u53ea\u80fd\u95ee\u9053\uff1a\u201c\u5982\u4f55\u5c06\u8ba1\u5c31\u8ba1\uff1f\u201d \u3000\u3000\u201c\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u54b1\u4eec\u5047\u610f\u505a\u4e00\u5bf9\u597d\u592b\u59bb\uff0c\u8ba9\u54e5\u54e5\u4e0d\u8d77\u7591\u5fc3\uff0c\u627e\u5230\u4e00\u4e2a\u597d\u673a\u4f1a\uff0c\u54b1\u4eec\u5c31\u8054 \u624b\u5236\u4f0f\u4ed6\u3002\u5e2e\u4e2d\u56db\u5927\u9999\u4e3b\u662f\u4ed6\u5fc3\u8179\uff0c\u4f46\u4e0b\u9762\u7684\u5f1f\u5144\u5374\u6709\u8bb8\u591a\u662f\u4e0d\u503c\u4ed6\u7684\u6240\u4e3a\u7684\uff0c\u5bf9\u4ed6\u52fe\u7ed3\u671d \u4e2d\u6743\u8d35\u4e4b\u4e8b\u5c24\u5176\u4e0d\u6ee1\uff0c\u53ea\u8981\u54b1\u4eec\u5236\u4f0f\u4e86\u4ed6\uff0c\u6211\u60f3\u5e2e\u4f17\u7edd\u5927\u591a\u6570\u4f1a\u62e5\u62a4\u54b1\u4eec\u3002\u6211\u5012\u4e0d\u662f\u5e0c\u7f55\u505a \u4e00\u4e2a\u5e2e\u4e3b\uff0c\u4f46\u516d\u5408\u5e2e\u6bd5\u7adf\u662f\u4e2a\u5927\u5e2e\uff0c\u82e5\u80fd\u4e3a\u6211\u6240\u7528\uff0c\u53d8\u4f5c\u4e49\u519b\u7684\u53cb\u4eba\uff0c\u5c82\u4e0d\u662f\u6709\u5927\u5927\u7684\u597d \u5904\uff1f\u5389\u5927\u54e5\uff0c\u4f60\u53ef\u613f\u610f\u4e0e\u6211\u622e\u529b\u540c\u5fc3\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u59d1\u5a18\u80c6\u8bc6\u8fc7\u4eba\uff0c\u4e0d\u6127\u5973\u4e2d\u8c6a\u6770\u3002\u6211\u662f\u4f69\u670d\u5f97\u5f88\uff0c\u4e00\u5207\u613f\u542c\u4f60\u7684\u8c03\u5ea6\u3002\u53ea\u662f \u4f60\u6709\u4e00\u53e5\u8bdd\u6211\u5374\u5374\u542c\u4e0d\u5927\u61c2\uff0c\u8fd9\uff0c\u8fd9\u2026\u2026\u201d \u3000\u3000\u5389\u5357\u661f\u4e0d\u61c2\u7684\u662f\u4f55\u4ee5\u53f2\u7ea2\u82f1\u53ea\u8981\u4e0e\u4ed6\u505a\u4e00\u5bf9\u6709\u540d\u65e0\u5b9e\u7684\u5047\u592b\u59bb\uff1f\u8fd9\u5047\u592b\u59bb\u53c8\u662f\u5982\u4f55\u505a \u6cd5\uff1f\u4ed6\u6ee1\u8179\u7591\u4e91\uff0c\u56e0\u6b64\u5728\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u5df2\u4e0d\u6562\u4ee5\u4e08\u592b\u81ea\u5c45\uff0c\u53e3\u79f0\u201c\u59d1\u5a18\u201d\uff0c\u5bf9\u53f2\u7ea2\u82f1\u5ba2\u5ba2\u6c14\u6c14 \u7684\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u91d1\u9010\u6d41\u7684\u597d\u670b\u53cb\uff0c\u6211\u60f3\u4f60\u4f1a\u4f53\u8c05\u6211\u7684\u3002\u5728\u8fd9\u4e2a\u623f\u95f4\u91cc\u54b1\u4eec\u662f\u670b \u53cb\uff0c\u51fa\u4e86\u8fd9\u4e2a\u623f\u95f4\uff0c\u4f60\u6211\u624d\u662f\u592b\u59bb\u3002\u54b1\u4eec\u95ee\u5fc3\u65e0\u6127\uff0c\u91d1\u9010\u6d41\u77e5\u9053\u4e86\uff0c\u6211\u60f3\u4ed6\u4e5f\u4f1a\u539f\u8c05\u6211\u4eec \u7684\u3002\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u4e00\u8bf4\uff0c\u604d\u5982\u7ed9\u5389\u5357\u661f\u70e7\u4e86\u4e00\u76c6\u51b7\u6c34\uff0c\u5389\u5357\u661f\u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u7ea2\uff0c\u60f3\u9053\uff1a\u201c\u602a\u4e0d \u5f97\u9010\u6d41\u4e0d\u80af\u4e0e\u6211\u540c\u6765\uff0c\u602a\u4e0d\u5f97\u6211\u628a\u5fc3\u4e8b\u544a\u8bc9\u4ed6\u65f6\uff0c\u4ed6\u662f\u90a3\u6837\u7684\u795e\u6c14\uff1b\u602a\u4e0d\u5f97\u7ea2\u82f1\u8981\u5979\u7684\u4e2b\u5934 \u4ee3\u5979\u62dc\u5802\uff1f\u5509\uff0c\u9010\u6d41\u5012\u662f\u60f3\u6210\u5168\u6211\u7684\uff0c\u6211\u5374\u662f\u592a\u5bf9\u4e0d\u4f4f\u4ed6\u4e86\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u662f\u4e2a\u70ed\u60c5\u800c\u5bb9\u6613\u6fc0\u52a8\u7684\u4eba\uff0c\u60f3\u81f3\u6b64\u5904\uff0c\u4e0d\u7981\u6376\u5934\u53eb\u9053\uff1a\u201c\u6211\u597d\u7cca\u6d82\uff0c\u6211\u65e9\u5c31\u5e94\u8be5 \u77e5\u9053\u4f60\u4eec\u662f\u4e00\u5bf9\u60c5\u4eba\u7684\u4e86\uff01\u6211\u53ef\u8981\u628a\u9010\u6d41\u627e\u56de\u6765\uff0c\u544a\u8bc9\u4ed6\uff0c\u4ed6\u624d\u662f\u4f60\u771f\u6b63\u6240\u7231\u7684\u4eba\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u8f7b\u8f7b\u4e00\u5618\uff0c\u8bf4\u9053\uff1a\u201c\u4f4e\u58f0\u70b9\u513f\uff0c\u8fd9\u4e2a\u65f6\u5019\uff0c\u54ea\u91cc\u53bb\u627e\u91d1\u9010\u6d41\uff1f\u4f46\u82e5\u6c89\u4e0d\u4f4f\u6c14\uff0c\u53ea \u6015\u81ea\u5df1\u5148\u8981\u88ab\u4eba\u6349\u4e86\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u54ea\u91cc\u77e5\u9053\uff0c\u91d1\u9010\u6d41\u5df2\u7ecf\u6765\u8fc7\uff0c\u53c8\u6084\u6084\u8d70\u4e86\u3002\u4f46\u4e5f\u8fd8\u6ca1\u6709\u8d70\u51fa\u8fd9\u4e2a\u5708\u5b50\u3002 \u3000\u3000\u91d1\u9010\u6d41\u533f\u5728\u540e\u7a97\u5077\u542c\uff0c\u4ed6\u5df2\u7ecf\u770b\u89c1\u4e86\u65b0\u623f\u91cc\u7684\u53f2\u7ea2\u82f1\uff0c\u4e5f\u5df2\u7ecf\u542c\u89c1\u4e86\u5389\u5357\u661f\u90a3\u756a\u60c5\u610f\u7ef5 \u7ef5\u7684\u8868\u767d\u3002\u53ef\u662f\u4ed6\u5374\u6ca1\u6709\u542c\u89c1\u53f2\u7ea2\u82f1\u540e\u6765\u7684\u8a00\u8bed\uff0c\u5728\u5389\u5357\u661f\u51fa\u6765\u5bdf\u89c6\u5916\u9762\u6709\u6ca1\u6709\u4eba\u7684\u65f6\u5019\uff0c \u4ed6\u5c31\u6084\u6084\u5730\u8d70\u4e86\u3002 \u3000\u3000\u6b63\u56e0\u4e3a\u4ed6\u6ca1\u6709\u542c\u89c1\u53f2\u7ea2\u82f1\u540e\u6765\u7684\u8bf4\u8bdd\uff0c\u4ee5\u81f4\u591a\u4e86\u8bb8\u591a\u8bef\u4f1a\u3002\u4ed6\u9eef\u7136\u795e\u4f24\u7684\u6084\u6084\u79bb\u5f00\uff0c\u6697 \u81ea\u601d\u91cf\uff1a\u201c\u539f\u6765\u7ea2\u82f1\u559c\u6b22\u7684\u4eba\u771f\u7684\u662f\u5389\u5927\u54e5\uff0c\u5426\u5219\u5979\u600e\u4f1a\u7518\u613f\u7684\u62dc\u5802\u6210\u4eb2\uff1f\u800c\u770b\u8fd9\u60c5\u5f62\uff0c\u53f2 \u767d\u90fd\u4e5f\u597d\u4f3c\u5f53\u771f\u662f\u613f\u610f\u628a\u59b9\u5b50\u5ac1\u7ed9\u5389\u5927\u54e5\u7684\u4e86\u3002\u5426\u5219\u5916\u9762\u4f55\u4ee5\u5168\u65e0\u57cb\u4f0f\uff1f\u201d\u91d1\u9010\u6d41\u6765\u7684\u65f6\u5019 \u5df2\u7ecf\u770b\u8fc7\u65b0\u623f\u5916\u9762\u65e0\u4eba\u5077\u542c\u3002\u4f46\u4ed6\u5374\u4e0d\u77e5\u9053\u3002\u65e5\u95f4\u4e0e\u5389\u5357\u661f\u62dc\u5802\u6210\u4eb2\u7684\u53ea\u662f\u53f2\u7ea2\u82f1\u7684\u4e2b\u5934\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6b63\u81ea\u9eef\u7136\u795e\u4f24\uff0c\u60f3\u8981\u56de\u53bb\uff0c\u5ffd\u5730\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u7ea2\u82f1\u82e5\u662f\u6ca1\u6709\u7591\u5fc3\uff0c \u5979\u4e0d\u4f1a\u53eb\u5389\u5927\u54e5\u51fa\u6765\u770b\u7684\u3002\u5979\u4e5f\u4e00\u5b9a\u662f\u770b\u51fa\u4e86\u8fd9\u4e2a\u9a97\u5c40\uff0c\u624d\u4f1a\u8d77\u4e86\u7591\u5fc3\u3002\u5982\u4eca\u771f\u76f8\u672a\u660e\uff0c\u6211 \u600e\u80fd\u5c31\u79bb\u5f00\u6b64\u5730\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u89c1\u4e00\u6761\u9ed1\u5f71\u5411\u65b0\u623f\u90a3\u8fb9\u8dd1\u53bb\uff0c\u91d1\u9010\u6d41\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u8fd9\u5996\u5987\u56de\u6765\u4e86\uff01\u201d\u4ed6\u662f \u81ea\u5c0f\u7ec3\u8fc7\u6697\u5668\u7684\uff0c\u76ee\u529b\u6781\u4f73\uff0c\u6de1\u6708\u758f\u661f\u4e4b\u4e0b\u8ba4\u51fa\u4e86\u8fd9\u5973\u4eba\u6b63\u662f\u8463\u5341\u4e09\u5a18\u3002\u5979\u56de\u6765\u4e0d\u6253\u7d27\uff0c\u4f46 \u56e0\u5979\u662f\u77e5\u9053\u91d1\u9010\u6d41\u7684\u884c\u8e2a\u7684\uff0c\u5979\u4e00\u56de\u6765\uff0c\u5f53\u7136\u4f1a\u628a\u91d1\u9010\u6d41\u8981\u5230\u626c\u5dde\u7684\u6d88\u606f\u544a\u8bc9\u53f2\u767d\u90fd\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u60f3\u9053\uff1a\u201c\u6211\u53cd\u6b63\u662f\u8c41\u51fa\u53bb\u4e86\uff0c\u4f46\u8fd9\u5a46\u5a18\u8be1\u8ba1\u591a\u7aef\uff0c\u4e0d\u77e5\u5979\u662f\u4e0d\u662f\u8981\u7b97\u8ba1\u5389\u5927\u54e5\u548c \u7ea2\u82f1\uff1f\u8fd9\u53ef\u4e0d\u80fd\u4e0d\u9632\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u63d0\u4e00\u53e3\u6c14\uff0c\u5c55\u5f00\u7edd\u9876\u8f7b\u529f\uff0c\u8ddf\u5728\u8463\u5341\u4e09\u5a18\u540e\u9762\uff0c\u4e0d\u6599\u4ed6\u811a\u5c16\u521a\u4e00\u7740\u5730\uff0c\u5ffd\u89c9\u6ce5\u571f \u677e\u6d6e\uff0c\u539f\u6765\u811a\u4e0b\u6b63\u662f\u4e00\u4e2a\u9677\u9631\u3002 \u3000\u3000\u8f70\u7684\u4e00\u58f0\uff0c\u4e00\u5757\u5927\u77f3\u5f53\u5934\u538b\u4e0b\u3002\u597d\u4e2a\u91d1\u9010\u6d41\uff0c\u5728\u8fd9\u7535\u5149\u706b\u77f3\u4e4b\u95f4\uff0c\u65e9\u5df2\u662f\u4e00\u4e2a\u201c\u9b41\u661f\u8e22 \u6597\u201d\uff0c\u8eab\u5b50\u60ac\u7a7a\uff0c\u4e00\u4e2a\u9e5e\u5b50\u7ffb\u8eab\uff0c\u53cc\u8db3\u5c31\u6253\u6a2a\u8e22\u51fa\uff0c\u6491\u4f4f\u4e86\u9677\u9631\u7684\u571f\u58c1\u3002\u77f3\u5934\u843d\u4e0b\uff0c\u7ed9\u4ed6\u8f7b \u8f7b\u4e00\u5e26\uff0c\u4f7f\u51fa\u4e86\u201c\u56db\u4e24\u62d4\u5343\u65a4\u201d\u7684\u5de7\u52b2\uff0c\u628a\u90a3\u5757\u5927\u77f3\u6309\u5230\u53e6\u4e00\u4fa7\u8fb9\uff0c\u63a5\u7740\u77f3\u5934\u672a\u66fe\u5760\u4e0b\u7684\u90a3 \u4e00\u77ac\u95f4\uff0c\u638c\u5fc3\u4e00\u6309\uff0c\u8eab\u5b50\u62d4\u8d77\uff0c\u51fa\u4e86\u9677\u9631\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8463\u5341\u4e09\u5a18\u7684\u957f\u97ad\u5df2\u662f\u65cb\u98ce\u4e00\u822c\u5730\u5377\u5230\uff0c\u9634\u607b\u607b\u5730\u8bf4\u9053\uff1a\u201c\u59d3\u91d1\u7684\u5c0f \u5b50\uff0c\u6211\u7b49\u4f60\u591a\u65f6\u4e86\uff01\u201d\u4e0e\u6b64\u540c\u5bf9\uff0c\u53e6\u4e00\u4e2a\u4eba\u4e5f\u4ece\u82b1\u6811\u4e1b\u4e2d\u8df3\u51fa\uff0c\u547c\u547c\u7684\u4e00\u638c\u5411\u91d1\u9010\u6d41\u6253\u6765\uff0c \u54c8\u54c8\u7b11\u9053\uff1a\u201c\u771f\u662f\u4eba\u751f\u4f55\u5904\u4e0d\u76f8\u9022\uff0c\u91d1\u9010\u6d41\uff0c\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u53c8\u89c1\u5230\u4e86\u4f60\uff01\u201d\u8fd9\u4e2a\u4eba\u4e0d\u662f\u522b \u4e2a\uff0c\u6b63\u662f\u91d1\u9010\u6d41\u7684\u6b7b\u5bf9\u5934\u6587\u9053\u5e84\u3002 \u3000\u3000\u5168\u9010\u6d41\u521a\u521a\u8df3\u51fa\u9677\u9631\uff0c\u811a\u6b65\u672a\u66fe\u7ad9\u7a33\uff0c\u7ed9\u6587\u9053\u5e84\u638c\u5200\u4e00\u63a8\uff0c\u8eab\u4e0d\u7531\u5df1\u7684\u5411\u524d\u503e\u4fa7\uff0c\u8463\u5341 \u4e09\u5a18\u4e00\u62db\u201c\u5341\u56de\u98ce\u626b\u67f3\u201d\uff0c\u957f\u97ad\u75be\u626b\u8fc7\u6765\u3002\u91d1\u9010\u6d41\u53eb\u9053\uff1a\u201c\u54ce\u54df\uff0c\u4e0d\u597d\u4e86\uff01\u201d\u6251\u901a\u8dcc\u5012\uff0c\u987a \u52bf\u5c31\u6293\u7740\u97ad\u68a2\uff0c\u5728\u5730\u4e0a\u4e00\u4e2a\u201c\u61d2\u9a74\u6253\u6eda\u201d\uff0c\u907f\u5f00\u4e86\u6587\u9053\u5e84\u7684\u4e00\u638c\uff0c\u5374\u628a\u8463\u5341\u4e09\u5a18\u626f\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u6587\u9053\u5e84\u7b2c\u4e09\u638c\u6b63\u8981\u6253\u51fa\uff0c\u614c\u5fd9\u6536\u56de\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\u3001\u91d1\u9010\u6d41\u5df2\u662f\u62d4\u5251\u51fa\u9798\uff0c\u5272\u65ad\u4e86\u7f20 \u7740\u4ed6\u5de6\u8155\u7684\u4e00\u6bb5\u8f6f\u97ad\uff0c\u8df3\u5c06\u8d77\u6765\uff0c\u95ea\u7535\u822c\u5411\u7740\u8463\u5341\u4e09\u5a18\u5c31\u662f\u4e00\u5251\u3002\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5e78\u597d\u6ca1\u6709\u7ed9 \u4f60\u6253\u4f24\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u6c14\u5f97\u67f3\u7709\u5012\u7ad6\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\u3001\u80c6\u6562\u620f\u800d\u8001\u5a18\uff1f\u201d\u6325\u97ad\u8fce\u51fb\uff0c\u4e00\u62db\u4e4b\u4e2d\uff0c\u85cf \u7740\u5708\u3001\u70b9\u3001\u7f20\u3001\u626b\u56db\u8def\u97ad\u6cd5\uff0c\u672c\u662f\u8463\u5341\u4e09\u5a18\u5341\u5206\u5f97\u610f\u7684\u4e00\u62db\u7edd\u6280\uff0c\u54ea\u77e5\u56e0\u4e3a\u5979\u7684\u8f6f\u97ad\u7ed9\u91d1\u9010 \u6d41\u5272\u65ad\u4e86\u4e00\u622a\uff0c\u4f7f\u8d77\u6765\u6070\u597d\u5dee\u4e86\u90a3\u4e48\u4e00\u70b9\uff0c\u672a\u80fd\u6253\u7740\u91d1\u9010\u6d41\u3002\u91d1\u9010\u6d41\u5251\u6cd5\u4f55\u7b49\u8fc5\u6377\uff0c\u8463\u5341\u4e09 \u5a18\u4e00\u51fb\u4e0d\u4e2d\uff0c\u4ed6\u5df2\u662f\u6b3a\u5230\u4e86\u5979\u8eab\u524d\uff0c\u5251\u5149\u95ea\u5904\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u8463\u5341\u4e09\u5a18\u8eab\u4e0a\u7684\u7f57 \u8863\uff0c\u5df2\u662f\u7ed9\u4ed6\u6311\u5f00\uff0c\u9732\u51fa\u4e86\u7c89\u7ea2\u8272\u7684\u809a\u515c\uff0c\u96ea\u767d\u7684\u808c\u80a4\u9690\u7ea6\u53ef\u89c1\u3002\u8fd9\u8fd8\u662f\u8463\u5341\u4e09\u5a18\u8eb2\u95ea\u5f97 \u5feb\uff0c\u5426\u5219\u8fd9\u4e00\u5251\u5df2\u662f\u7a7f\u5fc3\u5256\u8179\u4e4b\u707e\u3002 \u3000\u3000\u6587\u9053\u5e84\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\u4f11\u5f97\u901e\u5f3a\uff01\u201d\u53cc\u638c\u5f00\u51fa\uff0c\u8fd0\u8db3\u529f\u529b\uff0c\u9690\u9690\u631f\u7740\u98ce\u96f7\u4e4b\u58f0\u3002\u91d1\u9010\u6d41 \u8bc6\u5f97\u5389\u5bb3\uff0c\u653e\u8fc7\u4e86\u8463\u5341\u4e09\u5a18\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u523a\u6587\u9053\u5e84\u7684\u864e\u53e3\u3002\u6587\u9053\u5e84\u9707\u6b6a\u4e86\u91d1\u9010\u6d41\u7684\u5251\u5c16\uff0c\u6362 \u638c\u518d\u8d70\u3002\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u7684\u4e09\u8c61\u795e\u529f\uff0c\u53c8\u80fd\u5948\u6211\u4f55\u54c9\uff1f\u201d\u987a\u7740\u4ed6\u7684\u638c\u52bf\uff0c\u4e25\u5982\u67f3\u7d6e\u968f\u98ce\u7684 \u4e00\u98d8\u4e00\u95ea\uff0c\u500f\u5730\u5c31\u7ed5\u5230\u4e86\u6587\u9053\u5e84\u7684\u80cc\u540e\uff0c\u8fd0\u5251\u523a\u4ed6\u7684\u201c\u5927\u690e\u7a74\u201d\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u8ba4\u7a74\u7adf\u662f\u4e0d\u5dee \u6beb\u5398\u3002 \u3000\u3000\u6587\u9053\u5e84\u5fc3\u5934\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u76f8\u9694\u4e0d\u8fc7\u4e00\u6708\uff0c\u8fd9\u5c0f\u5b50\u7684\u529f\u529b\u7adf\u662f\u53c8\u957f\u8fdb\u4e86\uff01\u201d\u8fde\u5fd9\u4e00\u4e2a\u8eac \u8eab\uff0c\u8f6c\u8fc7\u4e00\u8fb9\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u5316\u89e3\u91d1\u9010\u6d41\u7684\u62db\u6570\u3002\u8463\u5341\u4e09\u5a18\u7684\u8f6f\u97ad\u4e5f\u6253\u4e86\u5230\u6765\uff0c\u5408\u4e8c\u4eba\u4e4b\u529b\uff0c \u8fd9\u624d\u6321\u4f4f\u4e86\u91d1\u9010\u6d41\u7684\u653b\u52bf\uff0c\u7a0d\u7a0d\u5360\u5f97\u4e86\u4e00\u70b9\u4e0a\u98ce\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5728\u56ed\u5b50\u91cc\u548c\u6587\u3001\u8463\u4e8c\u4eba\u6253\u5f97\u96be\u5206\u96be\u89e3\uff0c\u9ad8\u547c\u9163\u6597\u4e4b\u58f0\u8fdc\u8fdc\u7684\u4f20\u5230\u4e86\u65b0\u623f\u4e2d\uff0c\u53f2\u7ea2 \u82f1\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u597d\u50cf\u662f\u6709\u4eba\u5728\u5916\u9762\u53ae\u6740\uff0c\u5389\u5927\u54e5\uff0c\u4f60\u53bb\u770b\u770b\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u8bf4\u9053\uff1a\u201c\u597d\u50cf\u662f\u9010\u6d41\u7684\u58f0\u97f3\uff0c\u96be\u9053\u6211\u662f\u4f5c\u68a6\uff1f\u201d\u6b63\u8981\u63d0\u5251\u51fa\u53bb\uff0c\u5ffd\u542c\u5f97\u8f67\u8f67\u58f0 \u54cd\uff0c\u5899\u58c1\u4e0a\u7a81\u7136\u73b0\u51fa\u4e00\u9053\u6697\u5149\uff0c\u53f2\u767d\u90fd\u50cf\u9b3c\u9b45\u4f3c\u5730\u8df3\u4e86\u51fa\u6765\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u53bb\u4e86\uff0c\u91d1\u9010 \u6d41\u53c8\u5df2\u7ecf\u7ed9\u6293\u8d77\u6765\u5566\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u614c\u5fd9\u8fce\u654c\uff0c\u53f2\u767d\u90fd\u4e00\u638c\u5c06\u4ed6\u9707\u9000\uff0c\u8e0f\u6b65\u5411\u524d\uff0c\u5728\u6307\u5982\u94a9\uff0c\u671d\u7740 \u4ed6\u7684\u7435\u7436\u9aa8\u6293\u4e0b\uff0c\u53f2\u7ea2\u82f1\u4e00\u6307\u70b9\u51fa\uff0c\u70b9\u7740\u4e86\u4ed6\u7684\u201c\u51e4\u5e9c\u7a74\u201d\u3002\u53f2\u767d\u90fd\u6709\u95ed\u7a74\u7684\u529f\u72ac\uff0c\u4e0d\u6015\u59b9 \u59b9\u70b9\u7a74\uff0c\u4f46\u7ed9\u5979\u70b9\u7740\u4e86\u7a74\u9053\uff0c\u4e5f\u4e0d\u7981\u5fae\u611f\u9178\u9ebb\uff0c\u52b2\u529b\u4e00\u677e\uff0c\u6293\u7740\u4e86\u5389\u5357\u661f\u7684\u80a9\u5934\uff0c\u5374\u672a\u80fd\u634f \u788e\u4ed6\u7684\u7436\u7436\u9aa8\uff0c\u5c31\u7ed9\u5389\u5357\u661f\u8131\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u53eb\u9053\uff1a\u201c\u5feb\u62ff\u5b9d\u5251\uff01\u201d\u539f\u6765\u90a3\u67c4\u7384\u94c1\u5b9d\u5251\u672c\u662f\u5389\u5357\u661f\u968f\u8eab\u4f69\u6234\u7684\uff0c\u53ea\u56e0\u5728\u8fdb\u4e86\u6d1e \u623f\u4e4b\u540e\uff0c\u89c9\u5f97\u4f69\u5251\u4e0d\u4fbf\u4e8e\u592b\u59bb\u5e76\u5750\u8c08\u60c5\uff0c\u8fd9\u624d\u628a\u5b9d\u5251\u89e3\u4e86\u4e0b\u6765\uff0c\u6302\u5728\u5899\u4e0a\u7684\u3002\u5389\u5357\u661f\u5f97\u5979\u63d0 \u9192\uff0c\u6323\u8131\u4e86\u53f2\u767d\u90fd\u7684\u638c\u63e1\uff0c\u614c\u5fd9\u4e00\u4e2a\u7bad\u6b65\u4e0a\u524d\uff0c\u628a\u7384\u94c1\u5b9d\u5251\u6458\u4e86\u4e0b\u6765\u3002\u53f2\u767d\u90fd\u672c\u6765\u4e5f\u60f3\u62a2\u8fd9 \u5b9d\u5251\u7684\uff0c\u4f46\u7ed9\u5389\u5357\u661f\u5feb\u4e86\u4e00\u6b65\u3002\u53f2\u767d\u90fd\u6c14\u6781\u6012\u6781\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u771f\u662f\u6211\u7684\u597d\u59b9\u5b50\uff0c\u7adf\u7136\u505a\u5f97 \u51fa\u8fd9\u7b49\u65e0\u803b\u7684\u52fe\u5f53\uff0c\u548c\u5916\u4eba\u5408\u8c0b\u6765\u7b97\u8ba1\u54e5\u54e5\uff01\u201d\u51b7\u7b11\u58f0\u4e2d\uff0c\u4e00\u4e2a\u53cd\u624b\u64d2\u62ff\uff0c\u5c31\u5411\u53f2\u7ea2\u82f1\u6293 \u53bb\u3002\u5de6\u638c\u53c8\u5411\u5389\u6d77\u661f\u51fb\u51fa\u3002\u5389\u5357\u661f\u5fae\u4e00\u4fa7\u8eab\uff0c\u907f\u5f00\u4e86\u53f2\u767d\u90fd\u7684\u638c\u529b\uff0c\u559d\u9053\uff1a\u201c\u770b\u5251\uff01\u201d\u5bd2\u5149 \u4e00\u95ea\uff0c\u5b9d\u5251\u5df2\u662f\u51fa\u9798\uff01\u53f2\u767d\u90fd\u5f53\u7136\u8bc6\u5f97\u7384\u94c1\u5b9d\u5251\u7684\u5389\u5bb3\uff0c\u81ea\u5fd6\u53cc\u638c\u4e4b\u529b\uff0c\u51b3\u8ba1\u62b5\u6321\u4e0d\u4e86\uff0c\u53ea \u597d\u628a\u6293\u5411\u53f2\u7ea2\u82f1\u7684\u53f3\u624b\u7f29\u4e86\u56de\u6765\uff0c\u53cc\u638c\u9f50\u63a8\uff0c\u8fd9\u624d\u8361\u5f00\u4e86\u91cd\u8fbe\u767e\u65a4\u7684\u7384\u94c1\u5b9d\u5251\uff0c\u5389\u5357\u661f\u53eb \u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u4f60\u5148\u51fa\u53bb\uff01\u201d\u8fd0\u8db3\u4e86\u6c14\u529b\u3001\u5b9d\u5251\u62a1\u5706\uff0c\u5411\u53f2\u767d\u90fd\u8fde\u5288\u4e09\u5251\u3002\u53f2\u767d\u90fd\u53ea\u6015\u5288\u7a7a \u638c\u529b\u62b5\u6321\u4e0d\u4f4f\uff0c\u6293\u8d77\u4e86\u4e00\u5f20\u684c\u5b50\u62db\u67b6\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u684c\u5b50\u53d7\u4e86\u4e24\u5251\uff0c\u5288\u6210\u4e86\u56db\u5757\u3002\u8ddf\u5728\u59b9 \u59b9\u8eab\u540e\uff0c\u53f2\u767d\u90fd\u4e5f\u8df3\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u6b64\u65f6\u811a\u5c16\u521a\u521a\u7740\u5730\uff0c\u811a\u6b65\u8fd8\u672a\u7ad9\u7a33\uff0c\u5ffd\u89c9\u52b2\u98ce\u98d2\u7136\uff0c\u4e00\u6761\u8f6f\u97ad\u4ece\u89d2\u843d\u91cc\u7a81\u7136\u5411\u5979 \u6253\u6765\u3002\u539f\u6765\u662f\u8463\u5341\u4e09\u5a18\u8d76\u56de\u6765\u5411\u53f2\u767d\u90fd\u62a5\u4fe1\uff0c\u6070\u597d\u5c31\u5728\u5979\u8df3\u51fa\u7a97\u5b50\u7684\u65f6\u5019\u78b0\u5230\u4e86\u3002\u53f2\u7ea2\u82f1\u731d \u4e0d\u53ca\u9632\uff0c\u7740\u4e86\u4e00\u97ad\u3002\u9053\uff1a\u201c\u8463\u5341\u4e09\u5a18\uff0c\u4f60\u4e5f\u6562\u6765\u6b3a\u6211\uff01\u201d\u89e3\u4e0b\u4e86\u7f20\u8170\u7684\u94f6\u4e1d\u97ad\uff0c\u56de\u97ad\u8fd8\u51fb\uff0c \u4e24\u4eba\u90fd\u662f\u4f7f\u97ad\u7684\u80fd\u624b\uff0c\u5f7c\u6b64\u90fd\u77e5\u9053\u5bf9\u65b9\u7684\u8def\u6570\u3002\u97ad\u98ce\u547c\u54cd\u4e4b\u4e2d\uff0c\u53cc\u97ad\u7f20\u5728\u4e00\u8d77\u3002\u8463\u5341\u4e09\u5a18\u7b11 \u9053\uff1a\u201c\u7ea2\u82f1\u4f60\u53ef\u4f11\u8981\u602a\u6211\uff0c\u8c01\u53eb\u4f60\u80cc\u53db\u4f60\u7684\u54e5\u54e5\uff0c\u6211\u4e5f\u53ea\u597d\u5f97\u7f6a\u4f60\u4e86\uff01\u201d\u53f2\u7ea2\u82f1\u5fc3\u5934\u4e00\u51db\uff0c \u60f3\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u8fd9\u8d3c\u5a46\u5a18\u662f\u8981\u7f20\u4e0a\u4e86\u6211\uff0c\u6211\u53ef\u4e0d\u80fd\u4e2d\u5979\u7684\u8ba1\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c9\u4e00\u80a1\u5927\u529b\u63a8\u6765\uff0c\u53f2\u7ea2\u82f1\u4e0d\u7531\u81ea\u5df1\u5730\u6253\u4e86\u4e00\u4e2a\u76d8\u65cb\uff0c\u539f\u6765\u53f2\u767d\u90fd\u5df2\u7ecf\u5230\u4e86 \u5979\u7684\u80cc\u540e\uff0c\u53d1\u51fa\u4e86\u63a8\u78e8\u638c\u529b\uff0c\u903c\u5f97\u5979\u56e2\u56e2\u4e71\u8f6c\uff0c\u63a8\u78e8\u638c\u529b\uff0c\u4e92\u76f8\u7275\u5f15\uff0c\u5176\u529b\u4e0d\u8db3\u4f24\u4eba\uff0c\u4f46\u5982 \u80fd\u4f7f\u5bf9\u65b9\u7684\u8eab\u4f53\u5931\u5374\u91cd\u5fc3\uff0c\u8ddf\u7740\u81ea\u5df1\u7684\u638c\u529b\u65cb\u8f6c\u3002 \u3000\u3000\u5389\u5357\u661f\u5288\u70c2\u4e86\u684c\u5b50\uff0c\u4e00\u4e2a\u8ddf\u7740\u4e00\u4e2a\u7684\u4e5f\u8df3\u4e86\u51fa\u6765\uff0c\u53ef\u662f\u8fd9\u4e00\u6b21\u5374\u7ed9\u53f2\u767d\u90fd\u5feb\u4e86\u4e00\u6b65\uff0c\u5389 \u5357\u661f\u6b63\u81ea\u4e00\u5251\u523a\u51fa\uff0c\u53f2\u767d\u90fd\u5df2\u7ecf\u628a\u59b9\u59b9\u6293\u4e86\u8d77\u6765\uff0c\u4e00\u4e2a\u65cb\u98ce\u6025\u821e\uff0c\u628a\u53f2\u7ea2\u82f1\u7684\u8eab\u4f53\u8fce\u7740\u5389\u5357 \u661f\u7684\u5b9d\u5251\uff0c\u559d\u9053\uff1a\u201c\u4f60\u523a\uff01\u201d\u5389\u5357\u661f\u614c\u5fd9\u7f29\u624b\uff0c\u6c14\u5f97\u7834\u53e3\u5927\u9a82\uff1a\u201c\u54ea\u6709\u8fd9\u6837\u6b3a\u4fae\u81ea\u5df1\u7684\u59b9\u59b9 \u7684\uff0c\u4f60\u7b80\u76f4\u4e0d\u662f\u4eba\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u51b7\u7b11\u9053\uff1a\u201c\u6211\u597d\u610f\u628a\u59b9\u59b9\u76f8\u914d\u4e0e\u4f60\uff0c\u4f60\u4eec\u5374\u4e32\u901a\u4e86\u6765\u8c0b\u5bb3\u6211\uff0c\u8fd8\u8981\u6211\u624b\u4e0b\u7559\u60c5 \u4e48\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u5927\u6012\u9053\uff1a\u201c\u8c01\u8981\u4f60\u624b\u4e0b\u7559\u60c5\uff0c\u653e\u4e0b\u7ea2\u82f1\uff0c\u6211\u548c\u4f60\u5355\u6253\u72ec\u6597\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u6211\u5f53\u771f\u5c31\u6015\u4e86\u4f60\u7684\u7384\u94c1\u5b9d\u5251\u4e0d\u6210\uff1f\u201d\u632f\u81c2\u4e00\u629b\uff0c\u628a\u53f2\u7ea2\u82f1\u629b \u7ed9\u4e86\u8463\u5341\u4e09\u5a18\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u597d\u7ed9\u6211\u770b\u7ba1\u8fd9\u4e2a\u8d31\u4eba\uff0c\u5f85\u6211\u62ff\u4e86\u8fd9\u5c0f\u5b50\uff0c\u4e00\u5e76\u53d1\u843d\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u521a\u8981\u8dd1\u53bb\u62a2\u4eba\uff0c\u53ea\u542c\u5f97\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u53f2\u767d\u90fd\u5df2\u662f\u62d4\u5251\u51fa\u9798\uff0c\u62e6\u4f4f\u4ed6\u7684\u53bb\u8def\uff0c \u201c\u54fc\u2019\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u5b9d\u5251\u4e5f\u8be5\u7269\u5f52\u539f\u4e3b\u4e86\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6709\u672c\u9886\u4f60\u5c31\u62ff\u53bb\uff01\u201d\u62a1\u8d77\u7384\u94c1\u5b9d\u5251\uff0c\u5f53\u5934\u5288\u4e0b\u3002\u53f2\u767d\u90fd\u4f7f\u4e86\u4e00\u62db \u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u9752\u94a2\u5251\u642d\u7740\u4ed6\u7684\u5251\u810a\uff0c\u53cc\u5251\u76f8\u4ea4\uff0c\u4e00\u7ffb\u4e00\u7ede\u3002\u5389\u5357\u661f\u864e\u53e3\u53d1\u70ed\uff0c\u7384\u94c1\u5b9d\u5251\u9669 \u9669\u8131\u624b\u3002\u60c5\u6025\u4e4b\u4e0b\uff0c\u62fc\u7740\u8c41\u4e86\u6027\u547d\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u731b\u538b\u8fc7\u53bb\u3002\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00 \u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u53f2\u767d\u90fd\u7684\u9752\u94a2\u5251\u635f\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u5374\u6ca1\u6709\u7ed9\u4ed6\u524a\u65ad\u3002 \u3000\u3000\u5389\u5357\u661f\u811a\u6b65\u4e00\u8e09\u8dc4\uff0c\u659c\u7a9c\u4e09\u6b65\u3002\u53f2\u767d\u90fd\u8ddf\u8e2a\u6025\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u7384\u94c1\u5b9d\u5251\uff0c\u53c8\u80fd\u5948\u6211\u4f55 \u54c9\uff1f\u201d\u5389\u5357\u661f\u53cd\u624b\u4e00\u5251\uff0c\u4f7f\u51fa\u4e86\u201c\u9189\u516b\u4ed9\u201d\u7684\u4e00\u62db\u8eab\u6cd5\uff0c\u8d81\u7740\u90a3\u659c\u8eab\u4e4b\u52bf\uff0c\u7384\u94c1\u5b9d\u5251\u6a2a\u524a\u4e86 \u534a\u9053\u5f27\u5f62\u3002\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u53f2\u767d\u90fd\u7684\u9752\u94a2\u5251\u4f9d\u7136\u672a\u65ad\uff0c\u53ea\u662f\u53c8\u635f\u4e86\u4e00\u4e2a\u7f3a\u53e3\u3002 \u3000\u3000\u8fd9\u4e24\u62db\u6027\u547d\u76f8\u640f\uff0c\u5389\u5357\u661f\u524a\u4e0d\u65ad\u4ed6\u7684\u5175\u5203\uff0c\u56fa\u7136\u662f\u9887\u611f\u610f\u5916\uff0c\u53f2\u767d\u90fd\u4e5f\u662f\u5403\u60ca\u4e0d\u5c0f\u3002\u4ed6 \u66fe\u7ecf\u548c\u5389\u5357\u661f\u6570\u5ea6\u4ea4\u624b\uff0c\u8c01\u77e5\u81ea\u5df1\u7684\u5185\u529b\u5728\u5bf9\u65b9\u4e4b\u4e0a\u3002\u521a\u624d\u90a3\u4e24\u62db\u4ed6\u4f7f\u51fa\u4e86\u770b\u5bb6\u672c\u9886\uff0c\u6bcf\u4e00 \u62db\u90fd\u662f\u521a\u67d4\u76f8\u6d4e\uff0c\u65e2\u53ef\u4ee5\u5378\u53bb\u5bf9\u65b9\u7684\u52b2\u9053\uff0c\u53c8\u53ef\u4ee5\u7528\u201c\u9694\u7269\u4f20\u529f\u201d\u7684\u672c\u9886\u9707\u4f24\u5bf9\u65b9\u7684\u3002\u54ea\u77e5 \u5728\u5389\u5357\u661f\u7684\u91d1\u521a\u731b\u6251\u4e4b\u4e0b\uff0c\u4ed6\u7684\u5251\u4ecd\u7136\u8981\u53d7\u632b\u635f\u3002\u4ed6\u8fd9\u624d\u77e5\u9053\u5389\u5357\u661f\u7684\u5185\u529f\u867d\u7136\u4e0d\u5982\u4ed6\uff0c\u5374 \u4e5f\u5728\u4ed6\u4f30\u8ba1\u4e4b\u4e0a\u3002\u800c\u7384\u94c1\u5b9d\u5251\u7684\u5a01\u529b\uff0c\u4e5f\u51fa\u4e4e\u4ed6\u60f3\u8c61\u4e4b\u5916\u3002 \u3000\u3000\u53f2\u767d\u90fd\u5728\u7384\u94c1\u5b9d\u5251\u4e4b\u4e0b\u53d7\u632b\uff0c\u66f4\u60f3\u5f97\u56de\u5b9d\u7269\uff0c\u5f53\u4e0b\u64ae\u5507\u4e00\u5578\uff0c\u516d\u5408\u5e2e\u7684\u5927\u5c0f\u5934\u76ee\uff0c\u7eb7\u7eb7 \u8d76\u6765\u3002\u53f2\u5f52\u90fd\u9053\uff1a\u201c\u9752\u7b26\u4e0a\u6765\u3001\u5176\u4ed6\u7684\u4eba\u9000\u4e0b\u53bb\u628a\u5b88\uff0c\u6bd2\u7bad\u4f3a\u5019\uff01\u201d\u9752\u7b26\u9053\u4eba\u7684\u672c\u9886\u5728\u516d\u5408 \u5e2e\u4e2d\u5750\u7b2c\u4e09\u628a\u4ea4\u6905\uff0c\u5251\u672f\u5c24\u5176\u7cbe\u5999\uff0c\u53f2\u767d\u90fd\u77e5\u9053\u53ea\u6709\u4ed6\u53ef\u4ee5\u63d2\u5f97\u4e0a\u624b\uff0c\u5176\u4ed6\u7684\u4eba\u90fd\u4e0d\u6d4e\u4e8b\u3002 \u4e0d\u8fc7\u5374\u53ef\u4ee5\u7528\u6bd2\u7bad\u6765\u8bf8\u622a\u5389\u5357\u57a6\u9003\u8d70\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u80cc\u8179\u53d7\u654c\uff0c\u4ed7\u7740\u7384\u94c1\u5b9d\u5251\uff0c\u6c89\u7740\u5e94\u6218\u3002\u9752\u7b26\u9053\u4eba\u5251\u8d70\u8f7b\u7075\uff0c\u5389\u5357\u661f\u524a\u4e0d\u7740\u4ed6\u7684 \u5251\uff0c\u597d\u51e0\u6b21\u9669\u9669\u7ed9\u4ed6\u523a\u4e2d\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u5389\u5357\u661f\u542c\u51fa\u4e86\u662f\u91d1\u9010\u6d41\u7684\u5578\u58f0\uff0c\u5fc3\u4e2d\u4e00\u6025\uff0c\u60f3\u9053\uff1a\u201c\u9010\u6d41\u5c1a\u672a \u8131\u9669\uff0c\u6211\u5c82\u80fd\u5728\u8fd9\u91cc\u604b\u6218\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u9752\u7b26\u9053\u4eba\u4e00\u5251\u523a\u6765\uff0c\u5389\u5357\u661f\u6599\u60f3\u4ed6\u6015\u81ea\u5df1\u7684\u7384\u94c1\u5b9d\u5251\uff0c\u51b3\u4e0d\u6562\u6b3a\u8eab\u8fdb\u903c\uff0c\u8fd9 \u4e00\u62db\u591a\u534a\u4e43\u662f\u865a\u62db\u3002\u4e8e\u662f\u5192\u4e86\u4e2a\u9669\uff0c\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u5374\u731b\u7684\u548c\u8eab\u5411\u524d\u6251\u53bb\u3002 \u3000\u3000\u53f2\u767d\u90fd\u60f3\u4e0d\u5230\u4ed6\u7adf\u662f\u5982\u6b64\u820d\u547d\u76f8\u6251\uff0c\u9aa4\u5403\u4e00\u60ca\uff0c\u6536\u5251\u5df2\u6765\u4e0d\u53ca\uff0c\u53ea\u542c\u89c1\u65ad\u91d1\u621b\u7389\u4e4b\u58f0\u9707 \u5f97\u4f17\u4eba\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u8fd9\u4e00\u56de\u53f2\u767d\u90fd\u7684\u957f\u5251\u53ef\u662f\u7ed9\u5389\u5357\u661f\u524a\u65ad\u4e86\u3002 \u3000\u3000\u597d\u4e2a\u53f2\u767d\u90fd\uff0c\u65ad\u5251\u4e00\u629b\uff0c\u817e\u7684\u98de\u8d77\u4e00\u811a\uff0c\u53cc\u65b9\u8ddd\u79bb\u6781\u8fd1\uff0c\u5389\u5357\u661f\u8981\u8eb2\u4e5f\u8eb2\u95ea\u4e0d\u5f00\uff0c\u7ed9\u4ed6 \u4e00\u811a\u8e22\u4e2a\u6b63\u7740\uff0c\u6574\u4e2a\u8eab\u8eaf\uff0c\u5c31\u4f3c\u65ad\u7ebf\u98ce\u7b5d\u822c\u7684\u98de\u4e86\u8d77\u6765\uff0c\u53c8\u8fc5\u901f\u8dcc\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u53f2\u767d\u90fd\u90a3\u4e00\u5251\u662f\u5411\u524d\u523a\u51fa\u7684\uff0c\u8eab\u5f62\u81ea\u4e5f\u5411\u524d\u65b9\u503e\u4fa7\uff0c\u7a81\u7136\u5251\u7ed9\u524a\u65ad\uff0c\u8eab\u4f53\u5931\u4e86\u91cd\u5fc3\uff0c\u731b \u529b\u5730\u8e22\u51fa\u4e86\u90a3\u4e00\u811a\u4e4b\u540e\uff0c\u4e5f\u662f\u4e0d\u7531\u81ea\u5df1\u7684\u91cd\u91cd\u5730\u6454\u4e86\u4e00\u8de4\u3002 \u3000\u3000\u5389\u5357\u661f\u7ed9\u8e22\u4e86\u4e00\u811a\uff0c\u8e22\u7740\u8170\u80ef\uff0c\u5341\u5206\u75bc\u75db\u3002\u4f46\u4e0d\u5e78\u4e4b\u4e2d\u4e5f\u6709\u5927\u5e78\uff0c\u539f\u6765\u9752\u7b26\u9053\u4eba\u523a\u4ed6\u7684 \u90a3\u4e00\u5251\uff0c\u672c\u662f\u53ef\u4ee5\u865a\u5b9e\u4e92\u53d8\u7684\u62db\u6570\uff0c\u82e5\u4e0d\u662f\u5389\u5357\u661f\u7ed9\u8e22\u5f97\u98de\u4e86\u8d77\u6765\uff0c\u9752\u7b26\u9053\u4eba\u90a3\u4e00\u5251\u5c31\u53ef\u4ee5 \u523a\u7a7f\u4ed6\u7684\u7435\u7436\u9aa8\u3002 \u3000\u3000\u9752\u7b26\u9053\u4eba\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u53ef\u60dc\uff01\u201d\u5ffd\u89c1\u5389\u5357\u661f\u8dcc\u5012\uff0c\u4ee5\u4e3a\u4ed6\u5df2\u53d7\u4f24\uff0c\u673a\u4e0d\u53ef\u5931\uff0c\u7acb\u5373\u98de \u63a0\u8fc7\u53bb\uff0c\u8981\u7ed9\u5389\u5357\u661f\u8865\u4e0a\u4e00\u5251\u3002 \u3000\u3000\u4e00\u5251\u523a\u4e0b\uff0c\u5389\u5357\u661f\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\u5c31\u8df3\u8d77\u6765\uff0c\u559d\u9053\uff1a\u201c\u53eb\u4f60\u8fd9\u725b\u9f3b\u5b50\u81ed\u9053\u58eb\u77e5\u9053\u6211\u7684\u5389 \u5bb3\uff01\u201d\u4ed6\u521a\u624d\u9669\u4e9b\u7ed9\u9752\u7b26\u9053\u4eba\u523a\u4e2d\uff0c\u4e00\u809a\u76ae\u95f7\u6c14\u6b63\u81ea\u65e0\u5904\u53d1\u6cc4\uff0c\u8fd9\u4e00\u4e0b\u4f7f\u5f97\u53c8\u72e0\u53c8\u51c6\uff0c\u9976\u662f \u9752\u7b26\u9053\u4eba\u5251\u6cd5\u8f7b\u7075\uff0c\u4f46\u56e0\u4f30\u8ba1\u9519\u8bef\uff0c\u5982\u662f\u51b7\u4e0d\u53ca\u9632\u3002\u2018\u5f53\u2019\u7684\u4e00\u58f0\u54cd\u8fc7\uff0c\u9752\u7b26\u9053\u4eba\u7684\u5251\u4e5f\u7ed9 \u524a\u4e3a\u4e24\u6bb5\u3002\u8fd8\u5e78\u4e8f\u4ed6\u8eb2\u95ea\u5f97\u5feb\uff0c\u624d\u6ca1\u4f24\u7740\uff0c\u4f46\u7384\u94c1\u5b9d\u5251\u7684\u5a01\u529b\u975e\u540c\u5c0f\u53ef\uff0c\u9752\u7b26\u867d\u6ca1\u4f24\u7740\uff0c\u80f8 \u53e3\u4e5f\u662f\u7ed9\u9707\u5f97\u9661\u7684\u53d1\u70ed\uff0c\u54c7\u7684\u4e00\u53e3\u9c9c\u8840\u5410\u4e86\u51fa\u6765\u3002 \u3000\u3000\u53f2\u767d\u90fd\u722c\u4e86\u8d77\u6765\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u653e\u7bad\uff01\u201d\u57cb\u4f0f\u5728\u5404\u5904\u5899\u89d2\u548c\u5c4b\u9876\u7684\u5e2e\u4e2d\u5934\u76ee\uff0c\u6bd2\u7bad\u7eb7\u7eb7 \u5c04\u51fa\u3002 \u3000\u3000\u5389\u5357\u661f\u821e\u8d77\u5b9d\u5251\u9632\u8eab\uff0c\u731b\u5730\u559d\u9053\uff1a\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u5c31\u53ea\u4f60\u4eec\u6709\u6697\u5668\u4e48\uff1f\u201d\u628a\u624b\u4e00\u626c\uff0c \u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u679a\u6697\u5668\u98de\u4e0a\u5c4b\u9876\uff0c\u7206\u70b8\u5f00\u6765\uff0c\u8fd9\u662f\u5389\u5bb6\u5bb6\u4f20\u7684\u72ec\u95e8\u6697\u5668\u2014\u2014\u6bd2\u96fe\u91d1\u9488\u70c8\u7130 \u5f39\uff0c\u7206\u70b8\u53d1\u51fa\u7684\u70df\u96fe\u672c\u6765\u662f\u6709\u6bd2\u7684\uff0c\u5389\u5357\u661f\u5acc\u5b83\u6b79\u6bd2\uff0c\u6539\u4e3a\u65e0\u6bd2\u7684\u3002\u7206\u70b8\u53d1\u51fa\u7684\u70df\u96fe\u867d\u7136\u65e0 \u6bd2\uff0c\u90a3\u4e00\u5927\u628a\u5939\u5728\u70df\u96fe\u4e2d\u5c04\u51fa\u6885\u82b1\u9488\uff0c\u65e0\u58f0\u65e0\u606f\uff0c\u4f46\u662f\u6700\u96be\u9632\u5907\uff0c\u5c4b\u9876\u4e0a\u7684\u51e0\u4e2a\u5c0f\u5934\u76ee\u80fd\u6709 \u591a\u5927\u529f\u592b\uff0c\u53ea\u542c\u5f97\u4e00\u7247\u201c\u54ce\u54df\uff0c\u54ce\u54df\uff01\u201d\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u8c01\u90fd\u8eb2\u4e0d\u5f00\u6885\u82b1\u9488\uff0c\u4e00\u4e2a\u4e2a\u7684\u4ece\u5c4b \u9876\u4e0a\u6eda\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5389\u5357\u661f\u8fc5\u5373\u586b\u4e0a\u7a7a\u6863\uff0c\u8df3\u4e0a\u5c4b\u9876\u3002\u4f46\u5f53\u4ed6\u7ffb\u8fc7\u9ad8\u5899\uff0c\u8df3\u4e0b\u540e\u56ed\u7684\u65f6\u5019\uff0c\u811a\u7b4b\u5374\u84e6\u5730\u611f\u5230 \u4e00\u9635\u75bc\u75db\uff0c\u51e0\u4e4e\u6eda\u4e86\u4e0b\u53bb\u3002\u539f\u6765\u4ed6\u7ed9\u53f2\u767d\u90fd\u8e22\u4e86\u4e00\u811a\uff0c\u867d\u7136\u52c9\u5f3a\u7981\u53d7\u5f97\u8d77\uff0c\u4f46\u8df3\u8dc3\u5df2\u662f\u4e0d\u53ca \u5e73\u65f6\u7075\u6d3b\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u4f55\u7b49\u5389\u5bb3\uff0c\u4e00\u773c\u5c31\u770b\u4e86\u5389\u5357\u661f\u7684\u7834\u7efd\uff0c\u5f53\u4e0b\u54c8\u54c8\u5927\u7b11\uff0c\u4fbf\u5373\u8ffd\u6765\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f \u5b50\uff0c\u770b\u4f60\u8fd8\u80fd\u8dd1\u5f97\u5230\u54ea\u91cc\u53bb\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u559c\u4e8b\u8c01\u77e5\u6210\u7978\u4e8b\uff0c\u6d1e\u623f\u7ea2\u70db\u5251\u5149\u5bd2\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u516b\u56de\u3000\u6697\u4f7f\u9709\u9488\u65bd\u9709\u624b\u3000\u4e14\u770b\u795e\u5251\u663e\u795e\u5a01 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e8c\u5341\u516b\u56de\u3000\u6697\u4f7f\u9709\u9488\u65bd\u9709\u624b\u3000\u4e14\u770b\u795e\u5251\u663e\u795e\u5a01 \u3000\u3000\u5389\u5357\u661f\u521a\u81ea\u5899\u5934\u8df3\u4e0b\uff0c\u731b\u542c\u5f97\u4e00\u58f0\u559d\u9053\uff1a\u201c\u7167\u6253\uff01\u201d\u8fd9\u4eba\u662f\u516d\u5408\u5e2e\u7684\u4e00\u4e2a\u5927\u5934\u76ee\u3002\u8eab\u957f \u4e03\u5c3a\uff0c\u53cc\u81c2\u6709\u5343\u65a4\u4e4b\u529b\uff0c\u4f7f\u7684\u5175\u5668\u662f\u4e2a\u72ec\u811a\u94dc\u4eba\uff0c\u4e5f\u6709\u4e03\u5341\u4e8c\u65a4\u4e4b\u91cd\u3002\u4ed6\u5728\u56ed\u4e2d\uff0c\u5389\u5357\u661f\u5728 \u5185\u9662\u548c\u53f2\u767d\u90fd\u7684\u6076\u6597\u4ed6\u770b\u4e0d\u89c1\uff0c\u56e0\u6b64\u4e5f\u5c31\u4e0d\u77e5\u7384\u94c1\u5b9d\u5251\u7684\u5389\u5bb3\uff0c\u81ea\u6043\u68b0\u91cd\u529b\u6c89\uff0c\u4e1d\u6beb\u4e5f\u4e0d\u628a \u4f3c\u4e2a\u6587\u5f31\u4e66\u751f\u7684\u5389\u5357\u661f\u770b\u5728\u773c\u5185\u3002 \u3000\u3000\u5389\u5357\u660c\u559d\u9053\uff1a\u201c\u907f\u6211\u8005\u751f\uff0c\u6321\u6211\u8005\u6b7b\uff01\u201d\u90a3\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u597d\u4e2a\u4f1a\u5439\u725b\u76ae\u2026\u2026\u201d\u8bdd\u72b9\u672a \u4e86\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u5389\u5357\u661f\u5df2\u662f\u5251\u5288\u5728\u94dc\u4eba\u8eab\u4e0a\uff0c\u5288\u5f97\u94a2\u5c51\u7eb7\u98de\uff0c\u706b\u661f\u84ec\u6e85\uff0c\u90a3\u4e2a\u5927 \u5934\u76ee\u201c\u8e6c\u8e6c\u8e6c\u201d\u7684\u8fde\u9000\u4e09\u6b65\uff0c\u95f7\u54fc\u4e00\u58f0\uff0c\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u4fbf\u5373\u5012\u4e0b\u53bb\u4e86\u3002\u539f\u6765\u4ed6\u867d\u6ca1\u6709 \u7ed9\u5b9d\u5251\u65ab\u4e2d\uff0c\u4f46\u5df2\u662f\u9707\u5f97\u4e94\u810f\u516d\u8151\u5168\u90fd\u7ffb\u8f6c\uff0c\u6c14\u7edd\u800c\u4ea1\u3002\u53ef\u662f\u4ed6\u7684\u72ec\u811a\u94dc\u4eba\u5374\u8fd8\u6ca1\u6709\u7ed9\u5b9d\u5251 \u5288\u5f00\u3002 \u3000\u3000\u5389\u5357\u661f\u628a\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u91d1\u9010\u6d41\u5728\u5047\u5c71\u90a3\u8fb9\u6b63\u5728\u53d7\u7740\u4e09\u4e2a\u4eba\u7684\u56f4\u653b\uff0c\u4e00\u4e2a\u662f\u6587\u9053\u5e84\uff0c\u53e6\u5916 \u4e24\u4e2a\u662f\u5217\u540d\u516d\u5408\u5e2e\u56db\u5927\u9999\u4e3b\u4e2d\u7684\u7126\u78ca\u548c\u5706\u6d77\u3002\u8fd9\u4e24\u4eba\u662f\u9876\u66ff\u8463\u5341\u4e09\u5a18\u6765\u4f5c\u6587\u9053\u5e84\u7684\u52a9\u624b\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5728\u4e09\u5927\u9ad8\u624b\u56f4\u653b\u4e4b\u4e0b\uff0c\u9887\u5904\u4e0b\u98ce\uff0c\u4e0d\u8fc7\u4ed7\u7740\u8eab\u6d3b\u8f7b\u7075\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u6587\u9053\u5e84\u4e5f\u662f\u5948 \u4ed6\u4e0d\u5f97\u3002 \u3000\u3000\u5389\u5357\u661f\u53c8\u60ca\u53c8\u559c\uff0c\u53eb\u9053\uff0c\u201c\u8d24\u5f1f\uff0c\u6211\u6765\u4e86\uff01\u201d\u594b\u529b\u4e00\u8dc3\uff0c\u4e00\u5251\u5411\u6587\u9053\u5e84\u523a\u53bb\u3002\u6587\u9053\u5e84\u53cd \u624b\u4e00\u62c2\uff0c\u8fd9\u4e00\u62c2\u8574\u85cf\u7740\u201c\u4e09\u8c61\u795e\u529f\u201d\u7684\u5a01\u529b\uff0c\u5389\u5357\u661f\u8170\u80ef\u53d7\u4f24\uff0c\u4e0b\u76d8\u4e0d\u7a33\uff0c\u4e0d\u7531\u81ea\u5df1\u5730\u6253\u4e86 \u4e2a\u76d8\u65cb\uff0c\u8f6c\u8fc7\u4e00\u8fb9\u3002\u6070\u597d\u548c\u7126\u78ca\u6b63\u9762\u76f8\u5bf9\u3002 \u3000\u3000\u7126\u78ca\u662f\u6c5f\u6e56\u4e0a\u7684\u72ec\u811a\u5927\u76d7\u51fa\u8eab\uff0c\u6027\u6781\u51f6\u72e0\uff0c\u4ee5\u4e3a\u6709\u4fbf\u5b9c\u53ef\u62fe\uff0c\u731b\u7684\u4e00\u5200\u5c31\u5411\u5389\u5357\u5c3e\u65ab \u53bb\uff0c\u9752\u7b26\u9053\u4eba\u8fdc\u8fdc\u770b\u89c1\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u56db\u5f1f\uff0c\u5c0f\u5fc3\uff01\u4e0d\u53ef\u8f7b\u654c\uff01\u201d\u53ef\u662f\u5df2\u7ecf\u8fdf \u4e86\u3002\u5200\u5251\u76f8\u4ea4\uff0c\u4e00\u7247\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\u54cd\u8fc7\uff0c\u7126\u78ca\u7684\u539a\u80cc\u65ab\u5c71\u5200\u65ad\u4e3a\u4e24\u6bb5\uff0c\u8fd9\u8fd8\u4e0d\u6b62\uff0c\u8fde\u4ed6\u7684\u4e00 \u6761\u624b\u81c2\u4e5f\u7ed9\u7384\u94c1\u5b9d\u5251\u9f50\u80a9\u5207\u4e86\u4e0b\u6765\uff0c\u75db\u5f97\u4ed6\u767b\u65f6\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5389\u5357\u661f\u624b\u8d77\u5251\u843d\uff0c\u53c8\u5411\u5706\u6d77\u5288\u4e0b\uff0c\u5706\u6d77\u542c\u5f97\u9752\u7b26\u9053\u4eba\u7684\u8b66\u544a\uff0c\u5fc3\u4e2d\u4e00 \u51db\uff0c\u4e0d\u6562\u786c\u62fc\u3002\u53ef\u662f\u4f9d\u7136\u907f\u4e0d\u5f00\u5389\u5357\u661f\u8fd9\u4e00\u5251\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u5706\u6d77\u7684\u6212\u5200\u4e5f\u7ed9\u524a\u65ad\u3002\u5e78\u800c \u4ed6\u4e0d\u662f\u4ee5\u529b\u76f8\u62fc\uff0c\u6212\u5200\u867d\u65ad\uff0c\u5374\u6ca1\u53d7\u4f24\u3002\u4e0d\u8fc7\u4e5f\u7ed9\u9707\u6c14\u8840\u7ffb\u6d8c\uff0c\u63a5\u8fde\u9000\u51fa\u4e86\u4e03\u516b\u6b65\uff0c\u65b9\u80fd\u7a33 \u4f4f\u8eab\u5f62\u3002\u5706\u6d77\u65ad\u4e86\u6212\u5200\uff0c\u5c31\u50cf\u6597\u8d25\u4e86\u7684\u516c\u9e21\u6298\u65ad\u7fc5\u8180\u4e00\u6837\uff0c\u4e0d\u6562\u4e0a\u524d\u518d\u6218\u3002 \u3000\u3000\u53f2\u767d\u90fd\u98de\u5feb\u5730\u8d76\u4e86\u5230\u6765\uff0c\u624b\u4e2d\u63d0\u7740\u90a3\u4e2a\u5927\u5934\u76ee\u7684\u72ec\u811a\u94dc\u4eba\u3002\u539f\u6765\u4ed6\u662f\u56e0\u89c1\u7384\u94c1\u5b9d\u5251\u7684\u5a01 \u529b\u592a\u5f3a\uff0c\u666e\u901a\u7684\u5200\u5251\u5b9e\u662f\u96be\u4ee5\u62b5\u6321\u3002\u56e0\u6b64\u5728\u8fd9\u5927\u5934\u76ee\u8eab\u4ea1\u4e4b\u540e\uff0c\u4e34\u65f6\u7075\u9965\u4e00\u52a8\uff0c\u9042\u62fe\u8d77\u4e86\u4ed6 \u7684\u72ec\u811a\u94dc\u4eba\uff0c\u5e0c\u671b\u80fd\u591f\u4ed7\u7740\u8fd9\u4ef6\u5175\u5668\u6765\u514b\u5236\u5389\u5357\u661f\u7684\u7384\u94c1\u5b9d\u5251\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u8ddf\u7740\u6765\u5230\uff0c\u518d\u6b21\u4e0a\u524d\uff0c\u6362\u4e86\u4e00\u6761\u957f\u97ad\uff0c\u548c\u6587\u9053\u5e84\u8054\u624b\u5408\u6597\u91d1\u9010\u6d41\u3002 \u3000\u3000\u6076\u6597\u518d\u5ea6\u5c55\u5f00\uff0c\u53f2\u767d\u90fd\u9ad8\u4e3e\u72ec\u811a\u94dc\u4eba\uff0c\u4ee5\u6cf0\u5c71\u538b\u9876\u4e4b\u52bf\uff0c\u5411\u5389\u5357\u661f\u7838\u4e0b\u3002\u5389\u5357\u661f\u6a2a\u5251\u75be \u5288\uff0c\u91d1\u94c1\u4ea4\u9e23\uff0c\u5982\u96f7\u9707\u8033\u3002\u94dc\u4eba\u8eab\u4e0a\u62a5\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u4f46\u5389\u5357\u661f\u5374\u7ed9\u9707\u9000\u4e09\u6b65\u3002\u5389\u5357\u661f\u5fc3\u5934\u4e00 \u51db\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6211\u7684\u5185\u5bb6\u771f\u529b\u6bd4\u4e0d\u4e0a\u4ed6\uff0c\u5e94\u5f53\u5728\u5251\u6cd5\u4e0a\u6c42\u80dc\u3002\u201d\u53f2\u767d\u90fd\u559d\u58f0\uff1a\u201c\u64a4\u5251\uff01\u201d \u8de8\u6b65\u6b3a\u8eab\uff0c\u4e00\u62db\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\u94dc\u4eba\u62e6\u8170\u75be\u626b\u3002\u5389\u5357\u661f\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\uff01\u201d\u7384\u94c1\u5b9d\u5251\u626c\u7a7a\u4e00 \u5200\uff0c\u6296\u8d77\u4e86\u6ee1\u7a7a\u9519\u843d\u7684\u5251\u82b1\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u94dc\u5c51\u7eb7\u98de\uff0c\u5b9d\u5251\u4ecd\u7136\u7d27\u7d27\u63e1\u5728 \u5389\u5357\u661f\u624b\u4e2d\uff0c\u53f2\u767d\u90fd\u7684\u72ec\u811a\u94dc\u4eba\u5374\u5df2\u662f\u904d\u4f53\u9cde\u4f24\u4e86\u3002 \u3000\u3000\u539f\u6765\u5386\u5357\u661f\u7528\u7684\u8fd9\u62db\u4e43\u662f\u91d1\u4e16\u9057\u6240\u6388\u8ffd\u98ce\u5251\u5f0f\uff0c\u5f53\u771f\u75be\u4f3c\u98ce\u522e\uff0c\u5feb\u5982\u95ea\u7535\u3002\u77ac\u606f\u4e4b\u95f4\uff0c \u5df2\u5728\u94dc\u4eba\u8eab\u4e0a\u523a\u4e86\u5341\u4e03\u516b\u5251\u3002\u5251\u5149\u4fe8\u82e5\u873b\u8712\u70b9\u6c34\uff0c\u4e00\u63a0\u5373\u8fc7\u3002\u53f2\u767d\u90fd\u7684\u731b\u538b\u4e4b\u529b\uff0c\u8fd8\u672a\u80fd\u591f \u5b8c\u5168\u53d1\u6325\uff0c\u5389\u5357\u661f\u53c8\u5df2\u6536\u62db\u6362\u5f0f\u4e86\u3002\u8fd9\u4e48\u4e00\u6765\uff0c\u53f2\u767d\u90fd\u5c31\u6253\u4e0d\u843d\u4ed6\u7684\u5b9d\u5251\uff0c\u800c\u4ed6\u4ed7\u7740\u5b9d\u5251\u7684 \u950b\u5229\uff0c\u5374\u53ef\u4ee5\u201c\u622e\u4f24\u201d\u53f2\u767d\u90fd\u7684\u94dc\u4eba\u3002 \u3000\u3000\u4e0d\u8fc7\u53f2\u767d\u90fd\u6539\u7528\u4e86\u6c89\u91cd\u7684\u94dc\u4eba\uff0c\u6bd4\u8d77\u7528\u666e\u901a\u7684\u5200\u5251\u603b\u662f\u8981\u597d\u5f97\u591a\u4e86\uff0c\u94dc\u4eba\u867d\u7136\u904d\u4f53\u9cde \u4f24\uff0c\u5374\u8fd8\u53ef\u4ee5\u62b5\u6321\u7384\u94c1\u5b9d\u5251\u7684\u5a01\u529b\u3002 \u3000\u3000\u5389\u5357\u661f\u5403\u4e8f\u5728\u8170\u80ef\u53d7\u4f24\uff0c\u4e0b\u76d8\u4e0d\u7a33\uff0c\u8df3\u8dc3\u4e0d\u7075\uff0c\u65f6\u95f4\u7a0d\u957f\uff0c\u96be\u514d\u611f\u5230\u5403\u529b\uff0c\u597d\u5728\u53f2\u767d\u90fd \u6539\u7528\u4e86\u6c89\u91cd\u7684\u5175\u5668\uff0c\u4e00\u65f6\u95f4\u4e5f\u8fd8\u672a\u80fd\u719f\u624b\u3002\u94dc\u4eba\u7b28\u91cd\uff0c\u4e0d\u82e5\u5b9d\u5251\u7684\u8f7b\u7075\u3002\u662f\u4ee5\u5728\u6076\u6597\u4e4b\u4e2d\uff0c \u8fd8\u662f\u5389\u5357\u661f\u653b\u591a\u5b88\u5c11\u3002 \u3000\u3000\u53e6\u4e00\u8fb9\uff0c\u6587\u9053\u5e84\u4e0e\u8463\u5341\u4e09\u5a18\u8054\u624b\uff0c\u548c\u91d1\u9010\u6d41\u6253\u5f97\u96be\u89e3\u96be\u5206\u3002\u91d1\u9010\u6d41\u4f7f\u51fa\u6d51\u8eab\u672c\u9886\uff0c\u52c9\u5f3a \u6273\u6210\u5e73\u624b\uff0c\u4ed6\u662f\u80dc\u5728\u5251\u6cd5\u7cbe\u5947\uff0c\u8f7b\u529f\u8d85\u5999\uff0c\u4f46\u5185\u529b\u5219\u662f\u7a0d\u7a0d\u4e0d\u5982\u5bf9\u65b9\u3002\u6587\u9053\u5e84\u5f97\u8463\u5341\u4e09\u5a18\u4e4b \u52a9\uff0c\u5728\u9632\u5b88\u4e0a\u51cf\u4e86\u51e0\u5206\u5a01\u80c1\uff0c\u201c\u4e09\u8c61\u795e\u529f\u201d\u7684\u5a01\u529b\u53d1\u6325\u5f97\u6dcb\u6f13\u5c3d\u81f4\uff0c\u4e8c\u4e09\u5341\u62db\u8fc7\u540e\uff0c\u91d1\u9010\u6d41 \u867d\u7136\u8fd8\u53ef\u62b5\u6321\uff0c\u989d\u89d2\u4ea6\u5df2\u89c1\u6c57\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u5389\u5357\u661f\u53eb\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6211\u6709\u4e00\u4ef6\u4e8b\u60c5\u8981\u544a\u8bc9\u4f60\u3002\u7ea2\u82f1\uff0c\u5979\u2026\u2026\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5148 \u95ef\u51fa\u53bb\uff0c\u4f60\u518d\u6162\u6162\u548c\u6211\u8bf4\u5427\u3002\u201d\u5389\u5357\u661f\u9053\uff1a\u201c\u6211\u4e00\u523b\u4e0d\u544a\u8bc9\u4f60\uff0c\u4e00\u523b\u5fc3\u91cc\u4e0d\u80fd\u81ea\u5b89\u3002\u201d\u91d1\u9010 \u6d41\u82e6\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\uff0c\u4f60\u6211\u8fd8\u662f\u597d\u5144\u5f1f\uff0c\u6211\u4e0d\u4f1a\u602a\u4f60\u7684\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u53ea\u9053\u4ed6\u5df2\u542c\u5230\u4e86\u4ed6\u548c\u53f2\u7ea2\u82f1\u6240\u6709\u7684\u8bf4\u8bdd\uff0c\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u82e5\u662f \u7531\u6211\u8bf4\u51fa\uff0c\u5b9e\u4e5f\u96be\u514d\u5f7c\u6b64\u5c34\u5c2c\uff0c\u4ed6\u65e2\u7136\u90fd\u542c\u5230\u4e86\uff0c\u90a3\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u201d\u5982\u6b64\u4e00\u60f3\uff0c\u81ea\u6170\u81ea\u89e3\uff0c \u4e8e\u662f\u4e5f\u5c31\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u8bf4\u8bdd\u4e4b\u9645\uff0c\u5fc3\u60c5\u7565\u5206\uff0c\u7ed9\u53f2\u767d\u90fd\u4e58\u673a\u731b\u653b\uff0c\u767b\u65f6\u624b\u5fd9\u811a\u4e71\u3002\u8bf4\u8bdd\u8fc7\u540e\uff0c\u867d\u7136\u8fde\u5fd9\u9547 \u6151\u5fc3\u795e\uff0c\u5374\u5df2\u4e0d\u80fd\u6273\u56de\u5e73\u624b\u3002\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u662f\u4e2a\u6b66\u5b66\u5927\u884c\u5bb6\uff0c\u5fc3\u91cc\u60f3\u9053\uff0c\u201c\u8fd9\u6837\u6253\u4e0b\u53bb\uff0c\u65f6\u95f4\u4e45\u4e86\uff0c\u6211\u548c\u5389\u5927\u54e5\u53ea\u6015\u96be\u514d\u90fd \u8981\u5403\u4e8f\u3002\u201d\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5ffd\u5730\u659c\u8eab\u7a9c\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u5389\u5927\u54e5\uff0c\u6211\u548c\u4f60\u6362\u4e00\u4e2a\u5bf9\u624b\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u75be\u5982\u9e70\u96bc\u7a7f\u6797\uff0c\u5530\u7684\u4e00\u5251\uff0c\u5c31\u6307\u5230\u4e86\u53f2\u767d\u90fd\u80cc\u540e\u7684\u201c\u98ce\u5e9c\u7a74\u201d\u3002\u53f2\u767d\u90fd\u8eab\u624b\u7aef\u7684 \u4e0d\u51e1\uff0c\u5728\u524d\u8179\u53d7\u654c\u4e4b\u4e0b\uff0c\u970d\u5730\u4e00\u77ee\u8eab\u8eaf\uff0c\u62a1\u8d77\u94dc\u4eba\uff0c\u4e00\u4e2a\u76d8\u5934\u75be\u626b\uff0c\u628a\u5389\u5357\u661f\u903c\u9000\uff0c\u53ea\u542c\u5f97 \u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u91d1\u9010\u6d41\u4e00\u5251\u523a\u5728\u94dc\u4eba\u8eab\u4e0a\uff0c\u5251\u5c16\u4e5f\u7ed9\u4ed6\u8361\u6b6a\u4e86\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5389\u5357\u661f\u5df2 \u662f\u9000\u51fa\u4e86\u5708\u5b50\uff0c\u8fce\u6218\u6587\u3001\u8463\u4e8c\u4eba\u3002 \u3000\u3000\u53f2\u767d\u90fd\u8fd9\u4e00\u62db\u7528\u5f97\u9669\u6781\uff0c\u867d\u7136\u8361\u5f00\u4e86\u91d1\u9010\u6d41\u7684\u957f\u5251\uff0c\u5934\u76ae\u4e5f\u611f\u5230\u4e00\u7247\u6c81\u51c9\u3002\u53f2\u767d\u90fd\u5927\u6012 \u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u8fd9\u5c0f\u5b50\u8981\u6765\u9001\u6b7b\uff0c\u6211\u5148\u6210\u5168\u4f60\u5427\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u53ea\u6709\u4e00\u6761\u6027\u547d\uff0c\u6709\u672c\u9886\u4f60\u5c31\u62ff\u53bb\uff0c\u7528\u4e0d\u7740\u5148\u5439\u725b\u76ae\uff01\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u624b \u5e95\u4e1d\u6beb\u4e0d\u7f13\uff0c\u5c31\u5728\u8bf4\u8fd9\u51e0\u53e5\u8bdd\u7684\u7247\u523b\u4e4b\u95f4\uff0c\u5df1\u662f\u95ea\u7535\u822c\u7684\u653b\u51fa\u4e86\u516d\u516d\u4e09\u5341\u516d\u5251\uff0c\u6740\u5f97\u53f2\u767d\u90fd \u53ea\u6709\u62db\u67b6\u4e4b\u52bf\uff0c\u7adf\u65e0\u8fd8\u624b\u4e4b\u529b\u3002\u539f\u6765\u8bba\u771f\u5b9e\u7684\u672c\u9886\uff0c\u53f2\u767d\u90fd\u867d\u7136\u5728\u91d1\u9010\u6d41\u4e4b\u4e0a\uff0c\u4f46\u56e0\u4ed6\u4f7f\u7528 \u7684\u5175\u5668\u5341\u5206\u7b28\u91cd\uff0c\u7528\u6765\u514b\u5236\u5389\u5357\u661f\u7684\u7384\u94c1\u5b9d\u5251\u751a\u6709\u529f\u6548\uff0c\u7528\u6765\u5bf9\u4ed8\u8f7b\u529f\u8d85\u5353\u7684\u91d1\u9010\u6d41\uff0c\u5374\u662f \u4e0d\u591f\u7075\u6d3b\u4e86\u3002\u91d1\u9010\u6d41\u548c\u5389\u5357\u661f\u6389\u6362\u5bf9\u624b\uff0c\u5c31\u662f\u4e3a\u4e86\u8fd9\u4e2a\u7f18\u6545\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6599\u5f97\u5f88\u51c6\uff0c\u5389\u5357\u661f\u6446\u8131\u4e86\u53f2\u767d\u90fd\u7684\u7ea0\u7f20\u4e4b\u540e\uff0c\u6587\u3001\u8463\u4e8c\u4eba\u679c\u7136\u62e6\u4ed6\u4e0d\u4f4f\u3002\u5389\u5357\u661f \u4ed7\u7740\u7384\u94c1\u5b9d\u5251\u7684\u5a01\u529b\uff0c\u6a2a\u51b2\u76f4\u649e\uff0c\u6587\u9053\u5e84\u8fd8\u52c9\u5f3a\u53ef\u4ee5\u5468\u65cb\uff0c\u8463\u5341\u4e09\u5a18\u5374\u8fd1\u4e0d\u4e86\u4ed6\u7684\u8eab\u5b50\uff0c\u957f \u97ad\u8fdc\u8fdc\u6253\u6765\uff0c\u5389\u5357\u661f\u4fe1\u624b\u4e00\u5251\uff0c\u5c31\u628a\u5b83\u524a\u65ad\u4e86\u3002\u7384\u94c1\u5b9d\u5251\u4f7f\u5f00\uff0c\u5149\u8292\u56db\u5c04\uff0c\u5468\u56f4\u6570\u4e08\u4e4b\u5185\uff0c \u90fd\u5728\u4ed6\u7684\u51cc\u5149\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u666e\u901a\u5175\u5668\u4e00\u78b0\u5373\u6298\u3002 \u3000\u3000\u53f2\u767d\u90fd\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u60f3\u8981\u51b2\u6740\u8fc7\u53bb\uff0c\u963b\u62e6\u5389\u5357\u661f\u9003\u8d70\uff0c\u53ef\u662f\u91d1\u9010\u6d41\u7684\u8eab\u6cd5\u5374\u6bd4\u4ed6\u7075\u6d3b \u5f97\u591a\uff0c\u53f2\u767d\u90fd\u53ea\u8981\u4e00\u8fc8\u6b65\uff0c\u4e0d\u8bba\u8f6c\u90a3\u4e2a\u65b9\u5411\uff0c\u91d1\u9010\u6d41\u660e\u6643\u6643\u7684\u5251\u5c16\u603b\u662f\u5bf9\u51c6\u770b\u4ed6\u3002\u201d\u53f2\u767d\u90fd \u7a7a\u81ea\u66b4\u6012\u5982\u96f7\uff0c\u5374\u8fd8\u5fc5\u987b\u529b\u6c42\u81ea\u4fdd\u3002 \u3000\u3000\u5389\u5357\u661f\u633d\u8d77\u7384\u94c1\u5b9d\u5251\uff0c\u547c\u547c\u547c\u8fde\u5288\u4e09\u5251\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u5316\u4f5c\u4e86\u4e00\u9053\u767d\u5149\uff0c\u7bad\u4e00\u822c\u7684\u5411\u6587\u9053 \u5e84\u75be\u51b2\u8fc7\u53bb\uff0c\u6587\u9053\u5e84\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u8fd9\u5c0f\u5b50\u5f53\u771f\u662f\u8981\u62fc\u547d\uff01\u201d\u5f53\u4e0b\u6025\u8fde\u4e09\u8c61\u795e\u529f\uff0c\u53cc\u638c\u9f50\u51fa\uff0c \u5288\u7a7a\u638c\u529b\uff0c\u628a\u5389\u5357\u661f\u7684\u8eab\u5f62\u7a0d\u7a0d\u63a8\u5f00\uff0c\u8f6c\u8fc7\u4e00\u4e2a\u65b9\u5411\u3002\u8fd9\u4e00\u638c\u5df2\u662f\u7aed\u5c3d\u4ed6\u7684\u5168\u529b\uff0c\u4fa5\u5e78\u907f\u8fc7 \u4e86\u7384\u94c1\u5b9d\u5251\u7684\u51b2\u6740\uff0c\u54ea\u91cc\u8fd8\u6562\u518d\u53bb\u62fc\u547d\uff1f\u8f6c\u773c\u4e4b\u95f4\uff0c\u5389\u5357\u661f\u5df2\u662f\u6740\u5230\u5899\u8fb9\uff0c\u4e00\u5251\u5288\u5f00\u4e86\u56ed \u95e8\uff0c\u51b2\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u5927\u6012\u9053\uff1a\u201c\u8dd1\u4e86\u5389\u5357\u661f\u8fd9\u5c0f\u5b50\u4e5f\u8fd8\u7f62\u4e86\uff0c\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\u6211\u662f\u51b3\u4e0d\u80fd\u653e\u8fc7\u4e86\u4ed6\u3002\u4f60 \u4eec\u90fd\u56de\u6765\u7ed9\u6211\u62ff\u4eba\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u8981\u6765\u5c31\u6765\uff0c\u8981\u53bb\u5c31\u53bb\uff0c\u4f60\u5c82\u80fd\u5948\u6211\u4f55\uff1f\u201d\u865a\u6643\u4e00\u62db\uff0c\u201c\u55d6\u201d\u7684\u5c31\u63a0\u51fa \u53bb\uff0c\u8fce\u9762\u78b0\u4e0a\u4e86\u6587\u9053\u5e84\uff0c\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8fd8\u60f3\u5c1d\u6211\u7684\u4e38\u836f\uff1f\u201d\u811a\u5c16\u4e00\u70b9\uff0c\u8eab\u5f62\u5e73\u5730 \u62d4\u8d77\uff0c\u6070\u6070\u4ece\u53c8\u9053\u5e84\u7684\u5934\u9876\u98de\u8fc7\uff0c\u6587\u9053\u5e84\u4e00\u638c\u6253\u4ed6\u4e0d\u7740\uff0c\u91d1\u9010\u6d41\u501f\u4e86\u4ed6\u7684\u638c\u529b\uff0c\u53bb\u52bf\u66f4\u75be\uff0c \u534a\u7a7a\u4e2d\u4e00\u4e2a\u7b4b\u6597\u7ffb\u4e0b\u6765\uff0c\u5df2\u662f\u7ad9\u5728\u56f4\u5899\u4e4b\u4e0a\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\u5899\u5916\u9690\u9690\u4f20\u672a\u4e00\u58f0\u6025\u4fc3\u7684\u5578\u58f0\u3002\u53f2\u767d\u90fd\u9661\u5730\u559d\u9053\uff1a\u201c\u4e0b\u53bb\uff01\u201d\u628a\u5185\u5bb6\u771f\u529b\u90fd\u8fd0 \u5230\u53cc\u638c\u4e4b\u4e0a\uff0c\u4eba\u672a\u5230\uff0c\u638c\u5148\u53d1\u3002\u53f2\u767d\u90fd\u7684\u5288\u7a7a\u638c\u529b\u7aef\u7684\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u91d1\u9010\u6d41\u7ad9\u5728\u5899\u4e0a\uff0c\u76f8\u8ddd \u4e5f\u8fd8\u6709\u4e09\u4e08\u4e4b\u9065\uff0c\u53f2\u767d\u90fd\u5728\u5730\u4e0b\u53d1\u51fa\u7684\u5288\u7a7a\u638c\u529b\uff0c\u5c45\u7136\u9707\u5f97\u4ed6\u8eab\u5f62\u4e0d\u7a33\uff0c\u96be\u4ee5\u7acb\u8db3\u3002 \u3000\u3000\u91d1\u9010\u6d41\u660e\u77e5\u5916\u9762\u5fc5\u6709\u7406\u4f0f\uff0c\u90a3\u4e00\u58f0\u60f3\u5fc5\u5c31\u662f\u548c\u91cc\u9762\u7684\u4eba\u547c\u5e94\u7684\uff0c\u4f46\u91d1\u9010\u6d41\u4e5f\u5e76\u4e0d\u653e\u5728\u5fc3 \u4e0a\u3002\u91d1\u9010\u6d41\u70bc\u6709\u62a4\u4f53\u795e\u529f\uff0c\u53f2\u767d\u90fd\u7684\u5288\u7a7a\u638c\u529b\u53ea\u80fd\u4ee4\u4ed6\u7acb\u8db3\u4e0d\u7a33\uff0c\u4e0d\u80fd\u4eca\u4ed6\u53d7\u4f24\u3002\u91d1\u9010\u6d41\u4e58 \u7740\u4ed6\u7684\u638c\u529b\u4e00\u63a8\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8d76\u6211\uff0c\u6211\u4e5f\u8981\u4e0b\u53bb\u7684\uff0c\u4f55\u5fc5\u50ac\u4fc3\uff1f\u201d\u5f53\u4e0b\u4e00\u4e2a\u7b4b\u6597\u4ece\u5899 \u5934\u7ffb\u4e0b\u3002 \u3000\u3000\u4e0d\u6599\u4eba\u5728\u534a\u7a7a\uff0c\u5ffd\u542c\u5f97\u201c\u6ecb\u6ecb\u201d\u58f0\u54cd\uff0c\u91d1\u8292\u95ea\u707f\uff0c\u4e00\u84ec\u7ec6\u5982\u725b\u6bdb\u4f3c\u7684\u6885\u82b1\u9488\u7a81\u7136\u5411\u4ed6\u5c04 \u6765\u3002\u91d1\u9010\u6d41\u4e00\u6765\u662f\u8eab\u5b50\u60ac\u7a7a\uff0c\u4f7f\u4e0d\u4e0a\u529b\uff1b\u4e8c\u6765\u662f\u53d7\u4e86\u53f2\u767d\u90fd\u5288\u7a7a\u638c\u529b\u7684\u5f71\u54cd\uff0c\u90a3\u4e00\u4e2a\u7b4b\u6597\u4e00 \u65f6\u95f4\u8fd8\u672a\u66fe\u7ffb\u5f97\u8f6c\u6765\uff0c\u9aa4\u7136\u53d7\u88ad\uff0c\u9041\u65e0\u6240\u907f\uff0c\u767e\u5fd9\u4e2d\u53ea\u6709\u6325\u8896\u4e00\u62c2\uff0c\u4f46\u8fd8\u662f\u4e2d\u4e86\u4e24\u679a\u6885\u82b1 \u9488\u3002 \u3000\u3000\u6885\u82b1\u9488\u662f\u6700\u5fae\u7ec6\u7684\u6697\u5668\uff0c\u53ea\u6709\u5c04\u7740\u7a74\u9053\uff0c\u624d\u80fd\u4f24\u4eba\u3002\u91d1\u9010\u6d41\u5e76\u6ca1\u6709\u7ed9\u5c04\u7740\u7a74\u9053\uff0c\u6b63\u81ea\u4ee5 \u4e3a\u65e0\u59a8\uff0c\u4e0d\u6599\u811a\u5c16\u521a\u4e00\u7740\u5730\u3002\u84e6\u7136\u611f\u5230\u8eab\u4e0a\u6709\u9ebb\u75d2\u75d2\u7684\u611f\u89c9\uff0c\u539f\u6765\u4ed6\u7740\u7684\u4e0d\u662f\u666e\u901a\u7684\u6885\u82b1 \u9488\uff0c\u800c\u662f\u6dec\u6709\u5267\u6bd2\u7684\u6885\u82b1\u9488\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6012\u9053\uff1a\u201c\u662f\u8c01\u5077\u653e\u6697\u5668\uff1f\u6709\u80c6\u7684\u6562\u51fa\u6765\u548c\u6211\u4e00\u6218\u4e48\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u9634 \u607b\u607b\u7684\u58f0\u97f3\u5df2\u5728\u7b54\u9053\uff1a\u201c\u91d1\u9010\u6d41\uff0c\u4f60\u8fd8\u662f\u7559\u4e00\u53e3\u6c14\u51c6\u5907\u540e\u4e8b\u5427\uff0c\u6211\u53ef\u6ca1\u6709\u5de5\u592b\u5949\u966a\u4f60\u4e86\uff01\u201d \u91d1\u9010\u6d41\u773c\u5149\u4e00\u77a5\uff0c\u5728\u4eba\u4e1b\u4e2d\u627e\u51fa\u90a3\u4e2a\u4eba\u6765\uff0c\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u77f3\u971e\u59d1\u7684\u90a3\u4e2a\u5976\u5988\u8d3a\u5927\u5a18\u3002 \u3000\u3000\u91d1\u9010\u6d41\u51b7\u7b11\u9053\uff1a\u201c\u533a\u533a\u6bd2\u9488\uff0c\u7109\u80fd\u5bb3\u6211\uff1f\u201d\u6325\u5251\u6740\u5165\u4eba\u4e1b\uff0c\u5f84\u5954\u8d3a\u5927\u5a18\u3002\u90a3\u4e9b\u516d\u5408\u5e2e\u7684 \u5c0f\u5934\u76ee\u7109\u80fd\u62b5\u6321\u4ed6\u7684\u7cbe\u5999\u5251\u6cd5\uff0c\u53ea\u542c\u5f97\u201c\u54ce\u5537\uff0c\u54ce\u5537\uff01\u201d\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u8f6c\u773c\u95f4\u5c31\u6709\u4e03\u516b\u6761 \u6c49\u5b50\u53d8\u4f5c\u4e86\u6eda\u5730\u846b\u82a6\u3002\u8fd8\u5e78\u91d1\u9010\u6d41\u4e4b\u610f\u4e0d\u5728\u591a\u4f24\u4eba\u547d\uff0c\u53ea\u662f\u523a\u4f24\u4e86\u4ed6\u4eec\u7684\u5173\u8282\u7a74\u9053\u3002\u4ee5\u4fbf\u626b \u9664\u969c\u788d\u3002 \u3000\u3000\u8d3a\u5927\u5a18\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u8fd9\u6885\u82b1\u9488\u662f\u7528\u4e94\u6837\u6700\u5389\u5bb3\u7684\u6bd2\u836f\u6dec\u7ec3\u7684\uff0c\u6bd4\u90a3\u65e5\u7ed9\u4ed6\u559d\u7684 \u6bd2\u8336\u6bd2\u5f97\u591a\uff0c\u8fd9\u5c0f\u5b50\u5c45\u7136\u884c\u82e5\u65e0\u4e8b\uff0c\u96be\u9053\u4ed6\u662f\u91d1\u521a\u4e0d\u574f\u4e4b\u8eaf\uff1f\u201d\u5979\u662f\u9886\u6559\u8fc7\u91d1\u9010\u6d41\u7684\u5389\u5bb3 \u7684\uff0c\u54ea\u91cc\u8fd8\u6562\u604b\u6218\uff0c\u865a\u6321\u4e24\u62db\uff0c\u8fde\u5fd9\u8d70\u907f\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u53f2\u767d\u90fd\u5df2\u662f\u8ffd\u4e86\u51fa\u6765\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a \u201c\u91d1\u9010\u6d41\uff0c\u54b1\u4eec\u80dc\u8d1f\u672a\u5206\uff0c\u6709\u80c6\u7684\u4f60\u5c31\u522b\u8d70\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u6740\u9000\u4e86\u8d3a\u5927\u5a18\uff0c\u5ffd\u89c9\u773c\u795e\u4e00\u82b1\uff0c\u8111\u888b\u4e5f\u5fae\u6709\u6655\u7729\u4e4b\u611f\u3002\u539f\u6765\u4ed6\u7684\u62a4\u4f53\u795e\u529f\u53ea\u80fd\u62d6 \u5ef6\u6bd2\u6027\u7684\u53d1\u4f5c\uff0c\u5374\u4e0d\u80fd\u628a\u6bd2\u8d28\u9a71\u9664\u51fa\u53bb\u3002\u56e0\u6b64\uff0c\u5bf9\u4ed8\u6b66\u529f\u7a0d\u5f31\u4e8e\u4ed6\u7684\u4eba\u8fd8\u52c9\u5f3a\u53ef\u4ee5\uff0c\u5bf9\u4ed8\u53f2 \u767d\u90fd\u8fd9\u6837\u7684\u5f3a\u624b\uff0c\u90a3\u5374\u662f\u51b3\u8ba1\u4e0d\u80fd\u4e86\u3002\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u6211\u624d\u4e0d\u4e0a\u4f60\u7684\u5f53\u5462\uff01\u201d\u5f53\u4e0b\u5438\u4e00\u53e3 \u6c14\uff0c\u7b11\u9053\uff1a\u201c\u6709\u672c\u9886\u4f60\u5c31\u8ffd\u6765\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9ed8\u8fd0\u7384\u529f\uff0c\u62b5\u5fa1\u6bd2\u6c14\u7684\u4e0a\u4fb5\uff0c\u8f7b\u529f\u81ea\u662f\u4e0d\u514d\u7a0d\u53d7\u5f71\u54cd\uff0c\u53f2\u767d\u90fd\u4f55\u7b49\u5389\u5bb3\uff0c\u4e00\u773c\u5c31 \u770b\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8d3a\u5927\u5a18\uff0c\u8fd9\u5c0f\u5b50\u6562\u60c5\u662f\u4e2d\u4e86\u4f60\u7684\u6697\u5668\uff1f\u201d\u8d3a\u5927\u5a18\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u7740\u4e86\u6211 \u4e24\u679a\u6bd2\u9488\u3002\u8fd8\u6709\u2026\u2026\u201d\u8d3a\u5927\u5a18\u597d\u50cf\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8981\u8bf4\uff0c\u4f46\u53f2\u767d\u90fd\u5df2\u662f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u5148 \u62ff\u4e86\u8fd9\u5c0f\u5b50\u518d\u8bf4\u3002\u4eca\u665a\u662f\u51b3\u4e0d\u80fd\u8ba9\u4ed6\u8dd1\u4e86\uff01\u201d\u53e3\u4e2d\u8bf4\u8bdd\u3001\u811a\u6b65\u5df2\u662f\u5411\u7740\u91d1\u9010\u6d41\u9003\u8d70\u7684\u65b9\u5411\u8ffd \u53bb\uff0c\u628a\u8d3a\u5927\u5a18\u7529\u5728\u540e\u9762\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5728\u534a\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u8c05\u53f2\u767d\u90fd\u4e5f\u8ffd\u6211\u4e0d\u4e0a\u3002\u8fc7\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u53ef\u5c31\u96be\u8bf4 \u4e86\u3002\u201d\u5f53\u4e0b\u7528\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u529f\u592b\uff0c\u628a\u58f0\u97f3\u8fdc\u8fdc\u9001\u4e86\u51fa\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u5389\u5927\u54e5\uff0c\u5389\u5927\u54e5\uff01\u201d\u4ed6 \u653e\u5fc3\u4e0d\u4e0b\u5389\u5357\u661f\uff0c\u610f\u6b32\u4e0e\u4ed6\u4f1a\u5408\uff0c\u5f7c\u6b64\u597d\u6709\u4e2a\u7167\u5e94\u3002 \u3000\u3000\u8fde\u53eb\u4e09\u58f0\uff0c\u5374\u542c\u4e0d\u89c1\u56de\u7b54\u3002\u8d3a\u5927\u5a18\u968f\u540e\u8d76\u4e86\u4e0a\u6765\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u5389\u5357\u661f\u65e9\u5df2\u6b7b\u4e86\uff0c\u4f60\u5230 \u9ec4\u6cc9\u8def\u4e0a\u62e2\u4ed6\u53bb\u5427\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9a82\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u4f60\u8fd9\u5996\u5a46\u80c6\u6562\u8bc5\u5492\u6211\u7684\u5389\u5927\u54e5\uff0c\u56de\u5934\u6211\u518d\u4e0e\u4f60\u7b97\u5e10\u3002\u201d\u8d3a\u5927 \u5a18\u51b7\u7b11\u9053\uff1a\u201c\u662f\u6211\u4eb2\u624b\u6740\u4ed6\u7684\uff0c\u4f60\u600e\u4e48\u6837\uff1f\u8981\u7b97\u5e10\u4f60\u5c31\u6765\u5427\uff01\u54fc\uff0c\u53ea\u6015\u4f60\u8981\u8dd1\u4e5f\u8dd1\u4e0d\u4e86\uff01\u201d \u5979\u4ed7\u7740\u6709\u53f2\u767d\u90fd\u6491\u8170\uff0c\u80c6\u5b50\u5927\u4e86\u8bb8\u591a\uff0c\u4e5f\u5c31\u4e0d\u6015\u6fc0\u6012\u91d1\u9010\u6d41\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u80fd\u4e2d\u5979\u7684\u8ba1\u3002\u201d\u56de\u5934\u9a82\u9053\uff1a\u201c\u4f60\u6025\u4ec0\u4e48\uff0c\u8fd9\u7b14\u5e10\u6211\u8bb0\u4e0b\u4e86\uff0c \u6162\u6162\u548c\u4f60\u518d\u7b97\u3002\u201d \u3000\u3000\u5168\u9010\u6d41\u867d\u7136\u4e0d\u4fe1\u8d3a\u5927\u5a18\u7684\u8bc5\u5492\uff0c\u4f46\u542c\u4e0d\u5230\u5389\u5357\u661f\u7684\u56de\u58f0\uff0c\u5374\u662f\u96be\u514d\u6709\u70b9\u60ca\u7591\u3002\u4ed6\u7528\u4e0a\u4e86 \u201c\u4f20\u97f3\u5165\u5bc6\u201d\u4eec\u529f\u592b\uff0c\u5468\u56f4\u4e09\u4e94\u91cc\u4e4b\u5185\uff0c\u7a0d\u6709\u5185\u529f\u9020\u8be3\u7684\u4eba\u90fd\u53ef\u4ee5\u542c\u5230\u4ed6\u7684\u58f0\u97f3\u3002\u800c\u4ee5\u5389\u5357 \u661f\u7684\u9020\u8be3\uff0c\u5018\u82e5\u5728\u8fd9\u8303\u56f4\u4e4b\u5185\uff0c\u4e5f\u5fc5\u7136\u53ef\u4ee5\u7528\u540c\u6837\u7684\u529f\u592b\u5c06\u58f0\u97f3\u9001\u5230\u4ed6\u7684\u8033\u6735\u3002\u91d1\u9010\u6d41\u5fc3 \u60f3\uff1a\u201c\u96be\u9053\u4ed6\u5df2\u7ecf\u8dd1\u51fa\u4e86\u4e94\u91cc\u4e4b\u5916\uff1f\u4f46\u4ed6\u7ed9\u53f2\u767d\u90fd\u8e22\u4e86\u4e00\u811a\uff0c\u4ee5\u4ed6\u7684\u8f7b\u529f\u800c\u8bba\uff0c\u4f3c\u4e4e\u4e0d\u4f1a\u5c31 \u8dd1\u5f97\u8fd9\u4e48\u8fdc\u3002 \u3000\u3000\u91d1\u9010\u6d41\u60ca\u7591\u4e0d\u5b9a\uff0c\u7cbe\u795e\u4e0d\u80fd\u96c6\u4e2d\uff0c\u5ffd\u5730\u53c8\u6709\u70b9\u6655\u7729\u7684\u611f\u89c9\u3002\u91d1\u9010\u6d41\u8fde\u5fd9\u9547\u6151\u5fc3\u795e\uff0c\u56de\u5934 \u4e00\u671b\uff0c\u53f2\u767d\u90fd\u548c\u8d3a\u5927\u5a18\u5df2\u662f\u8d8a\u8ffd\u8d8a\u8fd1\u3002 \u3000\u3000\u91d1\u9010\u6d41\u81ea\u5fd6\u5df2\u662f\u4e0d\u80fd\u652f\u6301\u534a\u4e2a\u65f6\u8fb0\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e10\u5e2e\u7684\u63a5\u5e94\u600e\u4e48\u8fd8\u4e0d\u89c1\u6765\uff1f\u6211\u5f97\u5148\u60f3 \u4e2a\u6cd5\u5b50\u7529\u5f00\u4ed6\u4eec\u518d\u8bf4\u3002\u201d \u3000\u3000\u6b64\u65f6\u5df2\u662f\u8ffd\u5230\u8857\u4e0a\uff0c\u91d1\u9010\u6d41\u63d0\u4e00\u53e3\u6c14\uff0c\u98de\u8eab\u4e0a\u4e86\u6c11\u623f\uff0c\u4e1c\u7a9c\u897f\u95ea\uff0c\u5230\u4e86\u4e00\u5904\u6709\u51e0\u6761\u8857\u9053 \u4ea4\u53c9\u7684\u6240\u5728\uff0c\u91d1\u9010\u6d41\u8df3\u4e86\u4e0b\u6765\uff0c\u8eb2\u8fdb\u4e00\u6761\u51b7\u5df7\u3002 \u3000\u3000\u53f2\u767d\u90fd\u7a81\u7136\u4e0d\u89c1\u4e86\u91d1\u9010\u6d41\u7684\u8e2a\u8ff9\uff0c\u5374\u53cd\u800c\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e00\u5b9a\u662f\u5feb\u8981\u6bd2 \u53d1\uff0c\u81ea\u77e5\u4e0d\u80fd\u518d\u8dd1\uff0c\u6545\u800c\u8eb2\u85cf\u3002\u597d\uff0c\u6211\u5c31\u7ed9\u4ed6\u6765\u4e00\u4e2a\u74ee\u4e2d\u6349\u9cd6\uff01\u201d\u4ed6\u4e5f\u6709\u70b9\u5bb3\u6015\u91d1\u9010\u6d41\u8eb2\u5728 \u6697\u5904\u6697\u7b97\u4ed6\u3002\u5f53\u4e0b\u6b62\u4e86\u811a\u6b65\uff0c\u51c6\u5907\u7b49\u5f85\u5e2e\u4f17\u5927\u961f\u6765\u5230\uff0c\u518d\u5c01\u9501\u8fd9\u51e0\u6761\u8857\u9053\uff0c\u9010\u5c4b\u641c\u4eba\u3002 \u3000\u3000\u8fc7\u4e86\u4e0d\u4e45\uff0c\u8d3a\u5927\u5a18\u548c\u9752\u7b26\u3001\u5706\u6d77\u3001\u8463\u5341\u4e09\u5a18\u7b49\u4eba\u5148\u540e\u8d76\u5230\u3002\u53f2\u767d\u90fd\u5429\u5490\u4ed6\u624b\u4e0b\u7684\u4e09\u4e2a\u9999 \u4e3b\u7387\u4f17\u641c\u67e5\uff0c\u5e03\u7f6e\u505c\u5f53\u3001\u5598\u8fc7\u53e3\u6c14\uff0c\u60f3\u8d77\u4e00\u4e8b\uff0c\u56de\u5934\u95ee\u8d3a\u5927\u5a18\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u7684\u90a3\u4e8b\u662f\u771f\u662f \u5047\uff1f\u201d \u3000\u3000\u8d3a\u5927\u5a18\u7b11\u9053\uff1a\u201c\u6211\u867d\u7136\u6253\u4e0d\u8fc7\u91d1\u9010\u6d41\uff0c\u6740\u5389\u5357\u661f\u7684\u672c\u4e8b\u8fd8\u662f\u6709\u7684\uff0c\u5e2e\u4e3b\u4e0d\u5fc5\u7591\u5fc3\uff0c\u6211\u4eb2 \u773c\u770b\u89c1\u4ed6\u6b7b\u4e86\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u5140\u662f\u534a\u4fe1\u534a\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u6709\u7384\u94c1\u5b9d\u5251\uff0c\u4f60\u600e\u4e48\u6740\u4e86\u4ed6\u7684\uff1f\u201d\u5fc3\u60f3\uff1a\u201c\u4f60\u82e5\u662f\u4eb2 \u624b\u6740\u4e86\u4ed6\uff0c\u53c8\u4f55\u5fc5\u8bf4\u662f\u773c\u89c1\u4ed6\u6b7b\u7684\uff0c\u8fd9\u4e0d\u662f\u753b\u86c7\u6ee1\u8db3\u5417\uff1f\u201d \u3000\u3000\u8d3a\u5927\u5a18\u9053\uff1a\u201c\u662f\u8fd9\u6837\u7684\uff1a\u6211\u8eb2\u5728\u6697\u5904\uff0c\u51b7\u4e0d\u9632\u7684\u7ed9\u4ed6\u4e00\u624b\u6697\u5668\uff0c\u4ed6\u7740\u4e86\u6211\u7684\u4e00\u679a\u900f\u9aa8 \u9489\uff0c\u53c8\u7740\u4e86\u6211\u7684\u4e00\u679a\u8150\u9aa8\u73af\uff0c\u8fd9\u90fd\u662f\u6dec\u8fc7\u5267\u6bd2\u7684\u6697\u5668\uff0c\u6253\u6211\u4e0d\u8fc7\uff0c\u6211\u628a\u4ed6\u903c\u5230\u6cb3\u8fb9\uff0c\u6b63\u8981\u53d6 \u4ed6\u6027\u547d\uff0c\u4ed6\u5374\u5374\u5df1\u5148\u8df3\u4e0b\u6cb3\u4e2d\u53bb\u4e86\uff0c\u60f3\u5fc5\u4ed6\u662f\u8981\u4fdd\u4e2a\u5168\u5c38\u5427\u3002\u54e9\uff0c\u563f\uff0c\u8fd9\u5c0f\u5b50\u7684\u5185\u529f\u6bd4\u4e0d\u4e0a \u91d1\u9010\u6d41\uff0c\u4e2d\u4e86\u6211\u7684\u6697\u5668\uff0c\u8df3\u4e0b\u6e4d\u6025\u7684\u6cb3\u6d41\u4e4b\u4e2d\uff0c\u7eb5\u4f7f\u4ed6\u6709\u5341\u6761\u6027\u547d\uff0c\u4e5f\u662f\u51b3\u8ba1\u4e0d\u80fd\u518d\u6d3b\u7684 \u4e86\u3002\u5e2e\u4e3b\u4f60\u82e5\u662f\u8981\u5168\u5c38\u7684\u8bdd\uff0c\u5927\u53ef\u4ee5\u6d3e\u4eba\u9a7e\u5feb\u8239\u5230\u4e0b\u6e38\u53bb\u6253\u635e\u4ed6\u7684\u5c38\u4f53\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u4ed6\u7684\u7384\u94c1\u5b9d\u5251\u5462\uff1f\u201d \u3000\u3000\u8d3a\u5927\u5a18\u9053\uff1a\u201c\u8fde\u4eba\u5e26\u5251\u90fd\u6c89\u5230\u6c34\u5e95\u53bb\u4e86\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff0c\u5c38\u4f53\u662f\u4f1a\u6d6e\u8d77\u6765\u7684\uff0c\u53ef\u4ee5\u6253\u635e\uff0c\u7384\u94c1\u5b9d\u5251\u6c89\u5230\u6c34\u5e95\uff0c\u53ef\u662f\u4e0d \u6613\u5bfb\u627e\u4e86\u3002\u201d\u4f46\u867d\u6709\u60cb\u60dc\uff0c\u5374\u4e5f\u751a\u4e3a\u6b22\u559c\uff0c\u5fc3\u60f3\u5389\u5357\u661f\u5e26\u7740\u7384\u94c1\u5b9d\u5251\u8df3\u6c34\uff0c\u90a3\u662f\u5fc5\u6b7b\u65e0\u7591\u7684 \u4e86\u3002 \u3000\u3000\u6b63\u5728\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u542c\u5f97\u53f7\u89d2\u545c\u545c\u4e4b\u58f0\uff0c\u4ece\u4e1c\u897f\u4f20\u6765\uff0c\u8fd9\u662f\u516d\u5408\u5e2e\u4e2d\u62a5\u8b66\u7684\u53f7\u89d2\u3002 \u3000\u3000\u53f2\u767d\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u4eba\u80c6\u6562\u6765\u634b\u864e\u987b\u3002\u5feb\u53bb\u67e5\u95ee\uff01\u201d \u3000\u3000\u4e0d\u5f85\u67e5\u95ee\uff0c\u62a5\u4fe1\u7684\u4eba\u5df2\u7ecf\u6765\u5230\uff0c\u8bf4\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u4e0d\u597d\u4e86\uff0c\u4e10\u5e2e\u7684\u4eba\u548c\u6211\u4eec\u7684\u4eba\u6253\u8d77\u6765 \u4e86\uff0c\u4e3a\u9996\u7684\u662f\u4e00\u5bf9\u9752\u5e74\u7537\u5973\uff0c\u7537\u7684\u7528\u4e8c\u628a\u6781\u4e3a\u5947\u602a\u7684\u5251\uff0c\u597d\u50cf\u4f1a\u5996\u6cd5\u4f3c\u7684\uff0c\u4e0d\u7528\u523a\u7740\u4eba\uff0c\u90a3 \u4eba\u5c31\u50cf\u7a81\u7136\u60a3\u4e86\u53d1\u51b7\u75c5\u4e00\u822c\uff0c\u5012\u5728\u5730\u4e0a\u76f4\u6296\uff0c\u90a3\u5973\u7684\u66f4\u662f\u5389\u5bb3\uff0c\u6492\u51fa\u7684\u6885\u82b1\u9488\u6562\u60c5\u90fd\u662f\u6bd2 \u9488\uff0c\u6211\u4eec\u7684\u4eba\u5df2\u7ecf\u7ed9\u5979\u4f24\u4e86\u8bb8\u591a\u3002\u73b0\u5728\u5feb\u8981\u653b\u5230\u54b1\u4eec\u7684\u603b\u8235\u4e86\u3002\u5e2e\u4e3b\uff0c\u4f60\u5feb\u56de\u53bb\uff01\u201d \u3000\u3000\u8d3a\u5927\u5a18\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u5b9a\u662f\u971e\u59d1\u548c\u9648\u5149\u7167\u8fd9\u5c0f\u5b50\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4ed6\u4eec\u6765\u5f97\u6b63\u597d\uff01\u4f60\u4e0d\u5fc5\u62b1\u6b49\u505a\u4e0d\u6210\u5927\u5a92\u5566\uff0c\u6211\u4eb2\u81ea\u62a2\u4eb2\u53bb\uff01\u201d \u77f3\u971e\u59d1\u548c\u9648\u5149\u7167\u5728\u6d4e\u5357\u4e4b\u4e8b\uff0c\u53f2\u767d\u90fd\u65e9\u5df2\u4ece\u5148\u8d76\u56de\u6765\u7684\u8463\u5341\u4e09\u5a18\u53e3\u4e2d\u5f97\u77e5\u3002 \u3000\u3000\u5f53\u4e0b\u53f2\u767d\u90fd\u5e26\u4e86\u8d3a\u5927\u5a18\u56de\u53bb\u5e94\u4ed8\u4e10\u5e2e\uff0c\u5e76\u51c6\u5907\u6d3b\u64d2\u77f3\u971e\u59d1\u3002\u7559\u6587\u9053\u5e84\u4e3b\u6301\u641c\u6355\u91d1\u9010\u6d41\u4e4b \u4e8b\uff0c\u53f2\u767d\u90fd\u6599\u60f3\u91d1\u9010\u6d41\u5df2\u4e2d\u4e86\u6bd2\uff0c\u6709\u6587\u9053\u5e84\u548c\u4ed6\u624b\u4e0b\u7684\u4e09\u5927\u9999\u4e3b\u8db3\u53ef\u4ee5\u5bf9\u4ed8\u91d1\u9010\u6d41\u6709\u4f59\u3002 \u3000\u3000\u4e14\u8bf4\u91d1\u9010\u6d41\u8eb2\u8ff8\u4e86\u51b7\u5df7\uff0c\u542c\u5f97\u5916\u9762\u4eba\u58f0\u55a7\u95f9\uff0c\u6b63\u81ea\u7740\u6025\uff0c\u5ffd\u5730\u6709\u4e2a\u4eba\u4ece\u89d2\u843d\u91cc\u95ea\u4e86\u51fa \u6765\uff0c\u5c06\u4ed6\u4e00\u628a\u6293\u4f4f\u3002 \u3000\u3000\u5168\u9010\u6d41\u5403\u4e86\u4e00\u60ca\uff0c\u6b63\u8981\u65bd\u5c55\u53cd\u624b\u64d2\u62ff\u7684\u7edd\u6280\uff0c\u53ea\u542c\u5f97\u90a3\u4eba\u5df2\u5728\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u83ab\u614c\uff0c\u662f \u6211\uff01\u201d\u91d1\u9010\u6d41\u8fd9\u624d\u770b\u6e05\u695a\u4e86\u90a3\u4e2a\u4eba\uff0c\u559c\u51fa\u671b\u5916\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u3002\u674e\u5927\u54e5\uff0c\u4f60\u600e\u4e48\u4e5f\u5230\u4e86 \u8fd9\u513f\uff1f\u201d\u539f\u6765\u8fd9\u4eba\u4e43\u662f\u674e\u6566\u3002 \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u6211\u662f\u7279\u5730\u6765\u63a5\u5e94\u4f60\u7684\uff0c\u5feb\u8ddf\u6211\u6765\uff0c\u6709\u8bdd\u8fdb\u53bb\u518d\u8bf4\u3002\u201d\u6b64\u65f6\u5df2\u662f\u6df1\u591c\u56db\u66f4\u65f6 \u5206\uff0c\u5bb6\u5bb6\u95e8\u6237\u7d27\u95ed\uff0c\u5374\u5ffd\u6709\u4e00\u5bb6\u4eba\u5bb6\u6253\u5f00\u4e86\u5927\u95e8\uff0c\u5f00\u95e8\u7684\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u674e\u6566\u7684\u672a\u5a5a\u59bb\u4f55\u5f69 \u51e4\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8fdb\u4e86\u5c4b\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u516d\u5408\u5e2e\u7684\u4eba\u5c31\u5feb\u8981\u6765\u641c\u7d22\u4e86\uff0c\u8eb2\u5728\u5c4b\u5b50\u91cc\u4e5f\u4e0d\u662f\u529e\u6cd5\u3002\u201d \u3000\u3000\u674e\u6566\u7b11\u9053\uff1a\u201c\u6211\u81ea\u6709\u529e\u6cd5\u3002\u201d\u8bf4\u7f62\u642c\u5f00\u4e00\u4e2a\u6c34\u7f38\uff0c\u518d\u63ed\u5f00\u4e00\u5757\u77f3\u5934\uff0c\u4e0b\u9762\u73b0\u51fa\u9ed1\u9edd\u9edd\u7684 \u6d1e\u7a74\u3002\u674e\u6566\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5730\u9053\u53ef\u4ee5\u901a\u5230\u5c0f\u4e1c\u95e8\u7684\u5927\u8857\u3002\u5c31\u662f\u6709\u4eba\u5230\u6765\u641c\u67e5\uff0c\u4e5f\u4e0d\u4f1a\u77e5\u9053\u8fd9\u4e2a \u79d8\u5bc6\u3002\u201d \u3000\u3000\u4f55\u5f69\u51e4\u63d0\u4e86\u4e00\u76cf\u706f\u7b3c\uff0c\u524d\u9762\u7167\u8def\uff0c\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u539f\u6765\u8fd9\u5730\u9053\u91cc\u8fd8\u6709\u623f\u95f4\u5462\uff0c\u4f60\u600e\u4e48\u627e \u5f97\u8fd9\u4e2a\u597d\u6240\u5728\u7684\uff1f\u201d \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u8fd9\u662f\u6211\u4ee5\u524d\u5e03\u7f72\u4e0b\u6765\u7684\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u8fd9\u6b21\u91cd\u56de\u626c\u5dde\uff0c\u4e3a\u7684\u4e5f\u662f\u60f3\u628a\u53f2\u59d1\u5a18 \u6551\u51fa\u6765\uff0c\u5374\u6ca1\u5f97\u5230\u673a\u4f1a\uff0c\u597d\u5728\u4e10\u5e2e\u7684\u674e\u8235\u4e3b\u548c\u6211\u662f\u7ecf\u5e38\u4e92\u901a\u6d88\u606f\u7684\uff0c\u521a\u624d\u4ed6\u6d3e\u4e86\u4eba\u6765\uff0c\u6211\u624d \u77e5\u9053\u4f60\u4eca\u665a\u8981\u95ef\u8fdb\u516d\u5408\u5e2e\u67e5\u5bdf\u865a\u5b9e\u3002\u6211\u6015\u4f60\u5931\u4e8b\uff0c\u542c\u5f97\u4eba\u58f0\uff0c\u5c31\u8d76\u6765\u63a5\u5e94\uff0c\u679c\u7136\u63a5\u5230\u4e86\u4f60\uff0c \u8fd9\u53ef\u771f\u662f\u5de7\u6781\u4e86\u3002\u201d \u3000\u3000\u4f55\u5f69\u51e4\u9053\uff1a\u201c\u542c\u8bf4\u53f2\u59d1\u5a18\u8981\u548c\u4e00\u4e2a\u59d3\u5389\u7684\u6210\u5a5a\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u6211\u7684\u7ed3\u62dc\u5144\u5f1f\uff0c\u53f2\u767d\u90fd\u60f3\u5229\u7528\u4ed6\uff0c\u662f\u4ee5\u4f6f\u4f5c\u5141\u5a5a\uff0c\u5b9e\u662f\u4e00\u4e2a\u8981\u5751\u5bb3 \u4ed6\u7684\u9a97\u5c40\u3002\u201d \u3000\u3000\u4f55\u5f69\u51e4\u9053\uff1a\u201c\u90a3\u4e48\u53f2\u59d1\u5a18\u73b0\u5728\u600e\u4e48\u6837\u4e86\uff1f\u201d\u5509\uff0c\u53f2\u59d1\u5a18\u771f\u662f\u597d\u4eba\uff0c\u8981\u4e0d\u662f\u5979\u4ee5\u524d\u6697\u4e2d\u4fdd \u62a4\u6566\u54e5\uff0c\u6211\u4eec\u4e5f\u6ca1\u6709\u4eca\u5929\u5462\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53f2\u59d1\u5a18\u672a\u66fe\u9003\u51fa\uff0c\u4e0d\u8fc7\u6599\u60f3\u53f2\u767d\u90fd\u4e5f\u4e0d\u6562\u600e\u6837\u5c06\u5979\u96be\u4e3a\uff0c\u56e0\u4e3a\u4ed6\u8fd8\u8981\u5229\u7528 \u59b9\u59b9\u6765\u5df4\u7ed3\u897f\u660c\u5c06\u519b\u5e05\u5b5f\u96c4\u3002\u54b1\u4eec\u6162\u6162\u518d\u60f3\u529e\u6cd5\u6551\u5979\u3002\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u60f3\u8d77\u4f55\u5f69\u51e4\u521a\u624d\u7684\u8bdd\uff0c \u95ee\u9053\uff1a\u201c\u4f60\u4eec\u5df2\u7ecf\u6210\u4eb2\u4e86\uff1f\u201d \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u591a\u8c22\u4f60\u5728\u6d4e\u5357\u62d4\u5200\u76f8\u52a9\uff0c\u5f69\u51e4\u5f97\u514d\u843d\u4eba\u66f9\u5bb6\u8d3c\u5b50\u4e4b\u624b\u3002\u5728\u90a3\u4ef6\u4e8b\u60c5\u4e4b\u540e\u4e0d\u5230 \u4e00\u4e2a\u6708\uff0c\u5979\u7236\u5973\u4fe9\u5c31\u627e\u7740\u4e86\u6211\uff0c\u53ef\u60dc\u8bf7\u4e0d\u5230\u4f60\u6765\u559d\u559c\u9152\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u606d\u559c\uff0c\u606d\u559c\uff0c\u4f60\u7684\u5cb3\u7236\u5927\u4eba\u5462\uff1f\u201d\u60f3\u8d77\u5f53\u521d\u8bef\u4f1a\u674e\u6566\u4e0e\u53f2\u7ea2\u82f1\u76f8\u597d\u4e4b\u4e8b\uff0c \u4e0d\u7981\u54d1\u7136\u5931\u7b11\u3002 \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u5979\u7239\u7239\u73b0\u5728\u897f\u660c\uff0c\u6211\u4eec\u4e0d\u4e45\u4e5f\u8981\u53bb\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6b63\u8981\u95ee\u4ed6\u53bb\u897f\u660c\u662f\u5426\u53c2\u52a0\u4e49\u519b\uff0c\u674e\u6566\u5ffd\u5730\u54a6\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u54e5\uff0c\u4f60\u662f\u4e0d\u662f \u4e2d\u4e86\u6bd2\uff1f\u5f69\u51e4\uff0c\u628a\u706f\u7b3c\u62ff\u8fd1\u4e00\u4e9b\uff0c\u8ba9\u6211\u770b\u770b\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u770b\u4e86\uff0c\u6211\u662f\u4e2d\u4e86\u4e00\u679a\u5c0f\u5c0f\u7684\u6bd2\u9488\uff0c\u54b1\u4eec\u51fa\u53bb\u518d\u8bf4\uff0c\u6211\u8fd8\u633a\u5f97\u4f4f\u3002\u201d \u3000\u3000\u674e\u6566\u201c\u54ce\u54df\u201d\u4e00\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u679a\u6bd2\u9488\u4f60\u53ef\u4e0d\u80fd\u5c0f\u770b\uff0c\u8fd9\u662f\u5929\u9b54\u6559\u7684\u4e94\u6bd2\u9488\uff01\u8d76\u5feb\u5750\u4e0b\uff0c \u6211\u66ff\u4f60\u6cbb\u3002\u82e5\u518d\u62d6\u5ef6\uff0c\u5373\u4f7f\u4ee5\u4f60\u6df1\u539a\u7684\u5185\u529f\u53ef\u4ee5\u65e0\u59a8\uff0c\u4f46\u8981\u62d4\u9664\u4f59\u6bd2\uff0c\u81f3\u5c11\u4e5f\u5f97\u533b\u4e0a\u4e09\u4e2a \u6708\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u4ed6\u8bf4\u5f97\u5982\u6b64\u9669\u6076\uff0c\u53ea\u597d\u76d8\u819d\u5750\u4e0b\uff0c\u4e00\u9762\u8fd0\u529f\uff0c\u8ba9\u4ed6\u6cbb\u7597\u3002 \u3000\u3000\u674e\u6566\u62ff\u51fa\u4e00\u5757\u78c1\u77f3\uff0c\u628a\u90a3\u679a\u6bd2\u9488\u5438\u4e86\u51fa\u6765\u3002\u968f\u5373\u53d6\u51fa\u4e00\u652f\u7ec6\u957f\u7684\u94f6\u9488\uff0c\u7528\u9488\u7078\u62d4\u6bd2\u4e4b \u6cd5\uff0c\u523a\u4e86\u76f8\u5e94\u7684\u7a74\u9053\u3002\u91d1\u9010\u6d41\u9ed8\u8fd0\u7384\u529f\u4e0e\u4ed6\u914d\u5408\uff0c\u4e00\u7f15\u6d53\u5982\u6d53\u58a8\u7684\u8840\u6db2\u968f\u7740\u94f6\u9488\u62d4\u8d77\u6d41\u4e86\u51fa \u6765\uff0c\u91d1\u9010\u6d41\u987f\u89c9\u795e\u6e05\u6c14\u723d\u3002 \u3000\u3000\u674e\u6566\u5401\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5e78\u4e8f\u4f60\u5185\u529f\u6df1\u539a\u624d\u597d\u5f97\u8fd9\u6837\u5feb\uff0c\u4f46\u4f60\u8fd8\u8bb0\u5f97\u5728\u5f82\u5f95\u5c71\u90a3\u665a\u6211\u548c \u4f60\u8bf4\u8fc7\u7684\u8bdd\u5417\uff1f\u90a3\u65f6\u4f60\u4e0d\u80af\u5077\u5b66\u5929\u9b54\u6559\u7684\u767e\u6bd2\u771f\u7ecf\uff0c\u6211\u8bf4\u54b1\u4eec\u867d\u7136\u4e0d\u60f3\u4f7f\u6bd2\u5bb3\u4eba\uff0c\u4f46\u5b66\u4f1a\u4e86 \u5374\u662f\u6709\u5907\u65e0\u60a3\u3002\u8fd9\u7b49\u4e8e\u4e00\u628a\u5200\u5251\u4e00\u6837\uff0c\u5728\u574f\u4eba\u624b\u91cc\u62ff\u6765\u5bb3\u4eba\uff0c\u5728\u597d\u4eba\u624b\u91cc\u5219\u53ef\u4ee5\u6551\u4eba\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u6709\u7406\u3002\u4f46\u4f60\u5b66\u4f1a\u4e86\u4e5f\u662f\u4e00\u6837\u3002\u5bf9\u5566\uff0c\u8bf4\u8d77\u6551\u4eba\uff0c\u6211\u5012\u60f3\u8bf7\u4f60\u5e2e\u4e00 \u4e2a\u5fd9\uff0c\u6211\u6709\u4e00\u4f4d\u670b\u53cb\uff0c\u4ed6\u7684\u672a\u5a5a\u59bb\u7ed9\u4eba\u4e0b\u4e86\u6bd2\uff0c\u8fd9\u4e5f\u662f\u4e09\u4e2a\u6708\u4e4b\u540e\u624d\u53d1\u4f5c\u7684\uff0c\u4f60\u80fd\u6cbb\u5417\uff1f\u201d \u3000\u3000\u674e\u6566\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u79cd\u53ef\u4ee5\u4ee4\u5f97\u5b9a\u65f6\u6bd2\u53d1\u7684\u672c\u9886\uff0c\u53ea\u6709\u5929\u9b54\u6559\u7684\u9ad8\u624b\u624d\u4f1a\u4f7f\u7528\u3002 \u4e0b\u6bd2\u7684\u4eba\u6562\u60c5\u5c31\u662f\u90a3\u4e2a\u59d3\u8d3a\u7684\u5996\u5a46\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u5389\u80dc\u7537\u4ece\u524d\u7684\u90a3\u4e2a\u59d3\u8d3a\u4f8d\u5973\u3002\u6211\u6240\u4e2d\u7684\u6bd2\u9488\u5c31\u662f\u5979\u5c04\u7684\u3002\u539f\u6765 \u4f60\u4e5f\u77e5\u9053\u5979\uff1f\u201d \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u6211\u521a\u624d\u8fd8\u770b\u89c1\u5979\u5462\uff0c\u4e0d\u8fc7\u5979\u5374\u6ca1\u6709\u770b\u89c1\u6211\uff0c\u56e0\u4e3a\u6211\u77e5\u9053\u5979\u6b63\u8981\u5bfb\u627e\u6211\u8ba8\u90a3\u767e \u6bd2\u771f\u7ecf\uff0c\u6240\u4ee5\u907f\u5f00\u4e86\u5979\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u521a\u624d\u5728\u54ea\u91cc\u89c1\u7740\u5979\u7684\uff1f\u9664\u4e86\u5979\u53ef\u8fd8\u6709\u65c1\u4eba\uff1f\u201d \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u5c31\u5728\u524d\u9762\u90a3\u6761\u7af9\u897f\u5df7\u5bf9\u7740\u7684\u6cb3\u8fb9\uff0c\u5979\u6b63\u5728\u548c\u4e00\u4e2a\u4eba\u4ea4\u624b\uff0c\u90a3\u4e2a\u4eba\u7ed9\u5979\u903c\u843d\u4e86 \u6c34\u3002\u6211\u4f0f\u5728\u5df7\u4e2d\uff0c\u76f8\u9694\u9887\u8fdc\uff0c\u8981\u6551\u4e5f\u6765\u4e0d\u53ca\u3002\u6211\u81ea\u95ee\u4e5f\u4e0d\u662f\u5979\u7684\u5bf9\u624b\uff0c\u53ea\u597d\u907f\u5f00\u5979\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4eba\u662f\u4ec0\u4e48\u6837\u5b50\uff1f\u4ed6\u624b\u91cc\u62ff\u7684\u662f\u4e0d\u662f\u4e00\u628a\u5b9d\u5251\u3002\u201d \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u770b\u4e0d\u6e05\u695a\u3002\u4f46\u90a3\u4eba\u7684\u957f\u5251\u5728\u9ed1\u591c\u4e2d\u53d1\u51fa\u5149\u8292\uff0c\u8001\u8fdc\u90fd\u770b\u5f97\u89c1\uff0c\u4f3c\u4e4e\u662f\u628a\u5b9d \u5251\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u53eb\u9053\uff1a\u201c\u82e6\u4e5f\uff0c\u82e6\u4e5f\uff01\u201d\u674e\u6566\u5403\u60ca\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5c31\u662f\u6211\u7684\u90a3 \u4f4d\u7ed3\u62dc\u5144\u5f1f\u5389\u5357\u661f\uff0c\u4ed6\u624b\u91cc\u62ff\u7684\u6b63\u662f\u5929\u4e0b\u65e0\u53cc\u7684\u7384\u94c1\u5b9d\u5251\u3002\u201d \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u5144\u5f1f\u61c2\u4e0d\u61c2\u6c34\u6027\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u970d\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u5389\u5357\u661f\u548c\u6211\u4e00\u6837\uff0c\u90fd\u662f\u5728\u6d77\u5c9b\u957f\u5927\u7684\uff0c\u60f3\u6765\u4ed6\u7684\u6c34\u6027\u4e0d\u4e9a\u4e8e \u6211\u3002\u6211\u5728\u5927\u6d77\u7684\u6ce2\u6d9b\u4e2d\u4e5f\u662f\u53ef\u4ee5\u51fa\u6ca1\u81ea\u5982\u7684\u3002\u201d\u674e\u6566\u9053\uff1a\u201c\u8fd9\u6761\u6cb3\u53ea\u6709\u4e00\u6bb5\u6c34\u6d41\u6e4d\u6025\uff0c\u4e0b\u6e38 \u6c34\u52bf\u5219\u5f88\u5e73\u5766\u3002\u800c\u4e14\u9760\u57ce\u7684\u6cbf\u6cb3\u4e24\u5cb8\uff0c\u7ecf\u5e38\u6709\u5546\u8d3e\u7684\u5927\u8239\u505c\u6cca\u3002\u90a3\u4f4d\u5389\u5144\u53ea\u8981\u4e0d\u662f\u53d7\u4e86\u91cd \u4f24\uff0c\u4ed6\u65e2\u7136\u662f\u7cbe\u901a\u6c34\u6027\uff0c\u4e0d\u60e7\u5927\u6d77\u6ce2\u6d9b\uff0c\u90a3\u4e48\u4e00\u6761\u5c0f\u6cb3\u4e4b\u4e2d\uff0c\u81ea\u662f\u6df9\u4ed6\u4e0d\u6b7b\u3002\u54b1\u4eec\u56de\u5230\u4e10 \u5e2e\uff0c\u518d\u8bf7\u674e\u8302\u5e2e\u5fd9\uff0c\u6d3e\u4eba\u53bb\u627e\u4ed6\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u6015\u53ea\u6015\u4ed6\u5e26\u4e86\u90a3\u67c4\u6c89\u91cd\u7684\u7384\u94c1\u5b9d\u5251\uff0c\u4e0d\u6613\u6cc5\u6c34\u9003\u751f\u3002\u4e0d\u8fc7\u5389\u5927\u54e5\u4e5f\u7edd\u4e0d \u4f1a\u90a3\u4e48\u7b28\uff0c\u5230\u4e86\u529b\u96be\u517c\u987e\u4e4b\u65f6\uff0c\u81ea\u5fc5\u4ed6\u662f\u4f1a\u653e\u5f03\u90a3\u628a\u5b9d\u5251\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u653e\u4e86\u4e00\u70b9\u5fc3\uff0c\u95ee\u9053\uff1a\u201c\u4e10\u5e2e\u90a3\u8fb9\u6709\u4ec0\u4e48\u6d88\u606f\uff0c\u4ed6\u4eec\u63a5\u5e94\u7684\u4eba\u4e0d\u77e5\u6765\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u674e\u8302\u6d3e\u6765\u8054\u7edc\u7684\u4eba\u544a\u8bc9\u6211\uff0c\u4ed6\u4eec\u51c6\u5907\u4ece\u5c0f\u4e1c\u95e8\u90a3\u8fb9\u8fdb\u6765\uff0c\u653b\u516d\u5408\u5e2e\u7684\u540e\u8def\uff0c \u4e09\u66f4\u4e00\u8fc7\uff0c\u4fbf\u5373\u53d1\u96be\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u62cd\u4e86\u62cd\u8111\u888b\uff0c\u60f3\u4e86\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6211\u771f\u662f\u6253\u5f97\u7cca\u6d82\u4e86\u3002\u4e0d\u9519\uff0c\u6211\u662f\u548c\u4ed6\u4eec\u7ea6\u597d\uff0c \u5018\u82e5\u4e09\u66f4\u4e0d\u89c1\u6211\u56de\u6765\uff0c\u4ed6\u4eec\u5c31\u8981\u52a8\u624b\u7684\u3002\u201d \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u4ece\u8fd9\u6761\u5730\u9053\u51fa\u53bb\uff0c\u6b63\u662f\u5c0f\u4e1c\u95e8\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e00\u8dc3\u800c\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u5feb\u53bb\uff0c\u514d\u5f97\u4ed6\u4eec\u6302\u8651\u3002\u201d \u3000\u3000\u6b64\u65f6\u4e10\u5e2e\u4e0e\u516d\u5408\u5e2e\u6b63\u5728\u5c55\u5f00\u6df7\u6218\uff0c\u4ed6\u4eec\u8fd8\u672a\u66fe\u653b\u5230\u516d\u5408\u5e2e\u7684\u603b\u8235\uff0c\u5728\u8def\u4e0a\u5c31\u78b0\u5230\u4e86\u8d76\u56de \u6765\u7684\u53f2\u767d\u90fd\u3002 \u3000\u3000\u53f2\u767d\u90fd\u559d\u9053\uff1a\u201c\u674e\u8302\uff0c\u6211\u8ba9\u4f60\u4eec\u4e10\u5e2e\u5728\u626c\u5dde\u8bbe\u7acb\u5206\u8235\uff0c\u5f7c\u6b64\u76f8\u5b89\u65e0\u4e8b\uff0c\u4f60\u5374\u4e3a\u4f55\u5148\u6765\u72af \u6211\uff1f\u201d \u3000\u3000\u674e\u8302\u559d\u9053\uff1a\u201c\u4f60\u52fe\u7ed3\u5b98\u5e9c\uff0c\u501f\u5b98\u5e9c\u4e4b\u529b\u6b3a\u538b\u4e10\u5e2e\uff0c\u6211\u65e9\u5c31\u8981\u627e\u4f60\u7b97\u5e10\u3002\u5982\u4eca\u4f60\u53c8\u8bbe\u4e0b\u9677 \u9631\uff0c\u8c0b\u5bb3\u5389\u516c\u5b50\u548c\u91d1\u5c11\u4fa0\uff0c\u8fd9\u4e24\u4eba\u662f\u4e10\u5e2e\u7684\u597d\u670b\u53cb\uff0c\u6211\u5c82\u80fd\u4e0e\u4f60\u7518\u4f11\uff1f\u4f60\u82e5\u60f3\u8981\u8bb2\u548c\uff0c\u628a\u8fd9 \u4e24\u4e2a\u4eba\u5148\u653e\u51fa\u6765\u518d\u8bf4\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u80c6\u6562\u4e0e\u6211\u6273\u5e73\u4e86\u8eab\u4efd\u8bb2\u548c\uff1f\u6211\u5e73\u65f6\u4e0d\u8fc7\u662f\u770b\u5728\u4f60\u4eec \u4e10\u5e2e\u4e3b\u7684\u4efd\u4e0a\uff0c\u7ed9\u4f60\u51e0\u5206\u9762\u5b50\u800c\u5df2\u3002\u4f60\u7adf\u6562\u4e0d\u81ea\u91cf\u529b\uff0c\u5728\u592a\u5c81\u5934\u4e0a\u52a8\u571f\uff1f\u597d\uff0c\u8001\u5b9e\u544a\u8bc9\u4f60 \u5427\uff0c\u5389\u5357\u661f\u3001\u91d1\u9010\u6d41\u90fd\u7ed9\u6211\u6740\u4e86\uff0c\u4f60\u5230\u9ec4\u6cc9\u8def\u4e0a\uff0c\u966a\u4ed6\u4eec\u53bb\u5427\uff01\u201d \u3000\u3000\u674e\u8302\u662f\u4e10\u5e2e\u7b2c\u516b\u4ee3\u5f1f\u5b50\uff0c\u5728\u5e2e\u4e2d\u4e5f\u7b97\u5f97\u662f\u6709\u6570\u7684\u4eba\u7269\uff0c\u4f46\u6bd4\u4e4b\u53f2\u767d\u90fd\u5374\u8fd8\u662f\u8fdc\u8fdc\u4e0d\u5982\uff0c \u53f2\u767d\u90fd\u4eba\u672a\u5230\uff0c\u638c\u5148\u53d1\uff0c\u4e00\u8bb0\u5288\u7a7a\u638c\u674e\u8302\u5df2\u662f\u7981\u53d7\u4e0d\u8d77\uff0c\u53ea\u89c9\u80f8\u4e2d\u6c14\u8840\u7ffb\u6d8c\uff0c\u5982\u53d7\u9524\u51fb\u3002\u53ef \u662f\u674e\u8302\u4ecd\u662f\u987d\u5f3a\u4e0d\u9000\u3002 \u3000\u3000\u53f2\u767d\u90fd\u6b63\u8981\u4e0a\u53bb\u53d6\u4ed6\u6027\u547d\uff0c\u5ffd\u89c1\u5bd2\u5149\u4e00\u95ea\uff0c\u51b7\u6c14\u68ee\u68ee\uff0c\u9648\u5149\u7167\u7684\u51b0\u9b44\u5bd2\u5149\u5251\u6307\u5230\u4e86\u4ed6\u7684 \u80f8\u524d\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u662f\u8981\u5411\u6211\u5bfb\u4ec7\u4e48\uff0c\u597d\uff0c\u6211\u73b0\u5728\u81ea\u5df1\u6765\u4e86\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u5185\u529f\u6df1\u6e5b\uff0c\u51b0\u9b44\u5bd2\u5149\u5251\u4f24\u4ed6\u4e0d\u5f97\uff0c\u4f46\u5374\u4e5f\u4ee4\u4ed6\u611f\u5230\u4e00\u9635\u5947\u5bd2\u3002\u53f2\u767d\u90fd\u53c8\u60ca\u53c8\u559c\uff0c \u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u5931\u4e86\u7384\u94c1\u5b9d\u5251\u3002\u6b63\u597d\u593a\u4ed6\u8fd9\u67c4\u5b9d\u5251\u3002\u201d\u5f53\u4e0b\uff0c\u89d1\u4e2a\u771f\u5207\uff0c\u4e2d\u6307\u4e00\u5f39\uff0c\u201c\u94ee\u201d \u7684\u4e00\u58f0\u628a\u9648\u5149\u7167\u7684\u51b0\u9b44\u5bd2\u5149\u5251\u5f39\u5f00\u3002 \u3000\u3000\u53f2\u767d\u90fd\u8fd9\u4e00\u4e0b\u7528\u4e0a\u4e86\u201c\u9694\u7269\u4f20\u529f\u201d\u7684\u672c\u9886\uff0c\u9648\u5149\u7167\u53ea\u89c9\u864e\u53e3\u4e00\u9ebb\uff0c\u5bd2\u70ed\u4ea4\u4f5c\uff0c\u4fbf\u4f3c\u5927\u70ed \u5929\u65f6\uff0c\u7a81\u7136\u8dcc\u8fdb\u51b0\u7a9f\u91cc\u4f3c\u7684\uff0c\u4e0d\u7531\u5f97\u673a\u4f36\u4f36\u5730\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u539f\u6765\u662f\u4ed6\u51b0\u5251\u4e0a\u7684\u90a3\u80a1\u5947\u5bd2\u4e4b \u6c14\uff0c\u7ed9\u53f2\u767d\u90fd\u4ee5\u6df1\u6e5b\u7684\u5185\u529b\u53cd\u903c\u56de\u6765\u3002 \u3000\u3000\u53f2\u767d\u90fd\u5927\u7b11\u9053\uff1a\u201c\u51b0\u9b44\u5bd2\u5149\u5251\u5c82\u80fd\u5948\u6211\u4f55\u54c9\uff1f\u4f60\u4e0d\u914d\u7528\u5b83\uff0c\u4e0d\u5982\u7ed9\u4e86\u6211\u5427\uff01\u201d\u4e00\u4e2a\u8de8\u6b65 \u6b3a\u8eab\uff0c\u4e94\u6307\u9f50\u4f38\uff0c\u4fbf\u6765\u786c\u6293\u9648\u5149\u7167\u7684\u5b9d\u5251\u3002 \u3000\u3000\u53f2\u767d\u90fd\u89c1\u9762\u4e00\u62db\u5c31\u903c\u9000\u4e86\u9648\u5149\u7167\uff0c\u4e0d\u514d\u9887\u6709\u8f7b\u654c\u4e4b\u5fc3\uff0c\u5c82\u77e5\u9648\u5149\u7167\u7684\u529f\u529b\u867d\u7136\u662f\u8fdc\u4e0d\u5982 \u4ed6\uff0c\u5374\u4e5f\u81ea\u6709\u770b\u5bb6\u7684\u672c\u9886\u3002\u53f2\u767d\u90fd\u4e00\u6293\u6293\u53bb\uff0c\u9648\u5149\u7167\u559d\u9053\uff1a\u201c\u6709\u672c\u9886\u4f60\u5c31\u62ff\u53bb\uff01\u201d\u51b0\u5251\u626c\u7a7a \u4e00\u95ea\uff0c\u4f46\u89c1\u4e00\u7247\u5bd2\u5149\uff0c\u8000\u773c\u751f\u8f89\uff0c\u9976\u662f\u53f2\u767d\u90fd\u8fd9\u6837\u7684\u672c\u9886\uff0c\u7adf\u4e5f\u4e0d\u77e5\u4ed6\u7684\u5b9d\u5251\u662f\u4ece\u4f55\u5904\u88ad \u6765\uff0c\u9661\u7136\u95f4\uff0c\u53f2\u767d\u90fd\u53ea\u89c9\u201c\u7ae0\u95e8\u201d\u201c\u7389\u77ac\u201d\u201c\u5f52\u85cf\u201d\u4e09\u5904\u7a74\u9053\u90fd\u6709\u51b7\u51b0\u51b0\u7684\u611f\u89c9\uff0c\u53f2\u767d\u90fd\u5403 \u4e86\u4e00\u60ca\uff0c\u4e0d\u6562\u786c\u6293\uff0c\u8fde\u5fd9\u4e00\u638c\u628a\u9648\u5149\u7167\u9707\u5f00\u3002 \u3000\u3000\u539f\u6765\u9648\u5149\u7167\u8fd9\u5957\u5251\u6cd5\u4e43\u662f\u5f97\u81ea\u51b0\u5ddd\u5929\u5973\u7684\u5ae1\u4f20\uff0c\u4ece\u51b0\u5ddd\u7684\u6d41\u6c34\u4e4b\u4e2d\u53c2\u609f\u51fa\u6765\u7684\uff0c\u770b\u4f3c\u7b28 \u62d9\uff0c\u5176\u5b9e\u5374\u662f\u6df1\u5f97\u8f7b\u7075\u7fd4\u52a8\u7684\u7cbe\u9ad3\uff0c\u53f2\u767d\u90fd\u672a\u66fe\u89c1\u8fc7\u8fd9\u5957\u5251\u6cd5\uff0c\u4e00\u5ff5\u8f7b\u654c\uff0c\u51e0\u4e4e\u7740\u4e86\u9053\u513f\u3002 \u3000\u3000\u5e78\u800c\u53f2\u767d\u90fd\u529f\u529b\u6df1\u6e5b\uff0c\u95ed\u4e86\u7a74\u9053\uff0c\u8fd0\u6c14\u4e09\u8f6c\uff0c\u4fb5\u5165\u4f53\u4e2d\u7684\u5bd2\u6c14\u5df2\u662f\u5c3d\u90fd\u7ed9\u4ed6\u70bc\u5316\u3002\u9648\u5149 \u7167\u7acb\u8db3\u672a\u7a33\uff0c\u53f2\u767d\u90fd\u53c8\u5df2\u6251\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u53f2\u767d\u90fd\u53bb\u4e86\u8f7b\u654c\u4e4b\u5fc3\uff0c\u653b\u5b88\u517c\u65bd\uff0c\u4e0d\u8fc7\u6570\u62db\uff0c\u628a\u9648\u5149\u7167\u903c\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u6fc0\u6597\u4e2d\u53f2\u767d\u90fd\u627e \u7740\u4e86\u9648\u5149\u7167\u7684\u4e00\u4e2a\u7834\u7efd\uff0c\u731b\u5730\u559d\u9053\uff1a\u201c\u64a4\u5251\uff01\u201d\u4e00\u6293\u5411\u4ed6\u7684\u7436\u7436\u9aa8\u6293\u4e0b\u3002\u8fd9\u662f\u58f0\u4e1c\u51fb\u897f\u4e4b \u6cd5\uff0c\u9648\u5149\u7167\u82e5\u8981\u4fdd\u5168\u5de6\u81c2\uff0c\u52bf\u5fc5\u56de\u5251\u906e\u62e6\uff0c\u53f2\u767d\u90fd\u6697\u81e7\u7684\u540e\u770b\uff0c\u7acb\u5373\u5c31\u53ef\u4ee5\u593a\u4e86\u4ed6\u7684\u5b9d\u5251\u3002 \u3000\u3000\u773c\u770b\u9648\u5149\u7167\u5c31\u8981\u4e2d\u8ba1\uff0c\u53f2\u767d\u90fd\u5ffd\u89c1\u773c\u524d\u8272\u5f69\u7f24\u7eb7\uff0c\u597d\u4f3c\u6709\u4e00\u6761\u4e03\u5f69\u6591\u6593\u7684\u957f\u86c7\u7a81\u7136\u7a9c\u6765 \u556e\u4ed6\uff0c\u53f2\u767d\u90fd\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u602a\u5175\u5668\uff1f\u201d\u4e00\u65f6\u6478\u4e0d\u7740\u5e95\u7ec6\uff0c\u4e0d\u6562\u786c\u6293\uff0c\u53ea\u597d\u8eb2\u907f\u3002\u9976 \u662f\u4ed6\u8eb2\u95ea\u5f97\u5feb\uff0c\u80cc\u810a\u5df2\u662f\u7ed9\u90a3\u6761\u602a\u5175\u5668\u62bd\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u53f2\u767d\u90fd\u5b9a\u6674\u4e00\u770b\uff0c\u53ea\u89c1\u6765\u52a9\u9648\u5149\u7167\u7684\u7adf\u662f\u4e2a\u8c8c\u7f8e\u5982\u82b1\u7684\u5c11\u5973\uff0c\u53f2\u767d\u90fd\u767b\u65f6\u6012\u6c14\u51b0\u6d88\uff0c\u7b11 \u563b\u563b\u5730\u9053\uff1a\u201c\u4f60\u662f\u971e\u59d1\u5417\uff1f\u4f60\u53ef\u77e5\u9053\u6211\u662f\u8c01\uff1f\u8d3a\u5927\u5a18\uff0c\u4f60\uff0c\u4f60\u6765\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5ffd\u89c9\u80cc\u4e0a \u5947\u75d2\u96be\u5fcd\uff0c\u7981\u4e0d\u4f4f\u5f13\u8d77\u8170\u4f3c\u751f\u867e\u822c\u5730\u8df3\u4e86\u51e0\u8df3\u3002 \u3000\u3000\u9648\u5149\u7167\u5fcd\u4fca\u4e0d\u7981\uff0c\u7b11\u9053\uff1a\u201c\u971e\u59d1\uff0c\u6211\u4e0d\u77e5\u9053\uff0c\u539f\u6765\u4f60\u8fd8\u4f1a\u800d\u7334\u513f\u54e9\uff01\u201d\u539f\u6765\u77f3\u971e\u59d1\u7684\u90a3 \u4ef6\u602a\u5175\u5668\u540d\u4e3a\u201c\u91d1\u86c7\u7d22\u201d\uff0c\u4e43\u662f\u7528\u91d1\u5c5e\u5236\u6210\u7684\u86c7\u72b6\u5175\u5668\uff0c\u86c7\u5934\u4e2d\u7a7a\uff0c\u5185\u8d2e\u836f\u7c89\uff0c\u7ed9\u8fd9\u836f\u7c89\u6cbe \u7740\u8eab\u4f53\uff0c\u5947\u75d2\u96be\u71ac\uff0c\u81ea\u7136\u5c31\u4f1a\u65e0\u529b\u518d\u6218\uff0c\u800c\u4e14\u82e5\u662f\u6ca1\u6709\u5bf9\u75c7\u7684\u89e3\u836f\uff0c\u8fc7\u4e86\u4e09\u65e5\uff0c\u808c\u8089\u5c31\u8981\u6e83 \u70c2\u800c\u4ea1\u3002 \u3000\u3000\u8d3a\u5927\u5a18\u53eb\u9053\uff1a\u201c\u5e2e\u4e3b\u522b\u614c\uff0c\u6211\u6709\u89e3\u836f\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u653e\u4e0b\u4e86\u5fc3\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e14\u5f85\u6211\u6536\u62fe\u4e86\u8fd9\u4e2a\u5c0f\u5b50\uff0c\u56de\u53bb\u518d\u5411\u8d3a\u5927\u5a18\u8ba8\u89e3\u836f\uff0c\u4e5f\u8fd8 \u4e0d\u8fdf\u3002\u201d\u4ed6\u662f\u4e2a\u6b7b\u8981\u9762\u5b50\u7684\u4eba\uff0c\u4e0d\u613f\u5728\u4eba\u524d\u793a\u5f31\uff0c\u5f53\u4e0b\u8fd0\u8d77\u62a4\u4f53\u795e\u529f\uff0c\u4f7f\u5f97\u5947\u75d2\u4e4b\u611f\u51cf\u8f7b\u4e4b \u540e\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u533a\u533a\u4e00\u70b9\u6bd2\u836f\uff0c\u7109\u80fd\u5948\u5f97\u6211\u4f55\uff1f\u597d\uff0c\u53eb\u4f60\u8fd9\u5c0f\u5b50\u77e5\u9053\u6211\u7684\u5389\u5bb3\uff01\u201d\u547c\u7684\u4e00 \u638c\uff0c\u8361\u5f00\u4e86\u51b0\u9b44\u5bd2\u5149\u5251\uff0c\u9648\u5149\u7167\u7981\u4e0d\u8d77\u4ed6\u90a3\u6392\u5c71\u5012\u6d77\u7684\u638c\u529b\uff0c\u8e09\u8e09\u8dc4\u8dc4\u5730\u9000\u51fa\u4e86\u516d\u4e03\u6b65\u3002\u539f \u6765\u4ee5\u53f2\u767d\u90fd\u672c\u8eab\u7684\u529f\u529b\uff0c\u867d\u7136\u4e0d\u80fd\u81ea\u884c\u89e3\u6bd2\uff0c\u5374\u53ef\u4ee5\u62b5\u5fa1\u4e00\u65f6\u3002\u4ed6\u65e2\u77e5\u8d3a\u5927\u5a18\u5907\u6709\u89e3\u836f\uff0c\u81ea \u662f\u4e50\u5f97\u5439\u5439\u725b\u76ae\u4e86\u3002 \u3000\u3000\u77f3\u971e\u59d1\u8fde\u5fd9\u4e0e\u9648\u5149\u7167\u5e76\u80a9\u5fa1\u654c\uff0c\u4f46\u8fd9\u6b21\u53f2\u767d\u90fd\u5374\u662f\u6709\u4e86\u51c6\u5907\uff0c\u4e00\u89c1\u77f3\u971e\u59d1\u7684\u201c\u91d1\u86c7\u7d22\u201d \u6253\u6765\uff0c\u5927\u8896\u4e00\u6325\u5c31\u628a\u5b83\u88f9\u4f4f\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e1c\u897f\u5012\u5f88\u597d\u73a9\uff0c\u7ed9\u4e86\u6211\u5427\uff01\u201d\u4e00\u6325\u4e00\u5377\uff0c\u77f3\u971e\u638c\u63e1 \u4e0d\u7262\uff0c\u91d1\u86c7\u7d22\u767b\u65f6\u63a8\u5f00\uff0c\u53f2\u767d\u90fd\u4e00\u4e2a\u201c\u5de6\u53f3\u5f00\u5f13\u201d\u53cc\u638c\u5f00\u53d1\uff0c\u7528\u4e03\u6210\u529b\u9053\u5bf9\u4ed8\u9648\u5149\u7167\uff0c\u786c\u751f \u751f\u7684\u63d2\u8ff8\u4ed6\u4eec\u4e4b\u95f4\uff0c\u767b\u65f6\u628a\u9648\u3001\u77f3\u4e8c\u4eba\u5206\u5f00\u4e86\u3002 \u3000\u3000\u8d3a\u5927\u5a18\u5feb\u6b65\u62a2\u4e0a\uff0c\u53f2\u767d\u90fd\u7b11\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u4f60\u7ed9\u627e\u597d\u597d\u529d\u4e00\u529d\u4f60\u5bb6\u5c0f\u59d0\u5427\uff01\u5979\u600e\u80fd\u548c\u6211\u6253 \u67b6\u5462\uff0c\u8fd9\u4e0d\u662f\u8981\u95f9\u51fa\u7b11\u8bdd\u4e48\u3002\u201d \u3000\u3000\u8d3a\u5927\u5a18\u7b11\u9053\uff1a\u201c\u4fd7\u8bed\u8bf4\u5f97\u597d\uff1a\u5e8a\u5934\u6253\u67b6\u5e8a\u5c3e\u548c\u3002\u592b\u59bb\u6253\u67b6\u4e4b\u4e8b\u90a3\u4e5f\u5e38\u6709\u7684\uff0c\u53ea\u8981\u4ee5\u540e\u548c \u597d\u5c31\u884c\u3002\u201d \u3000\u3000\u77f3\u971e\u59d1\u6c14\u5f97\u67f3\u7709\u5012\u602a\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u80e1\u8bf4\u4ec0\u4e48\u3002\u201d \u3000\u3000\u8d3a\u5927\u5a18\u62e6\u4f4f\u4e86\u77f3\u971e\u59d1\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u8fd8\u4e0d\u77e5\u9053\u4e48\uff1f\u6211\u7ed9\u4f60\u8bf4\u7684\u5a92\u5c31\u662f\u8fd9\u4f4d\u53f2\u5e2e\u4e3b \u5440\uff01\u4f60\u4eec\u5c06\u6765\u662f\u8981\u505a\u592b\u59bb\u7684\uff0c\u600e\u597d\u6253\u67b6\uff1f\u201d \u3000\u3000\u77f3\u971e\u59d1\u5927\u6012\u9053\uff1a\u201c\u8c01\u548c\u4ed6\u505a\u592b\u59bb\uff1f\u4f60\u559c\u6b22\u4ed6\uff0c\u4f60\u81ea\u5df1\u5ac1\u7ed9\u4ed6\uff01\u201d \u3000\u3000\u8d3a\u5927\u5a18\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u5927\u628a\u5e74\u7eaa\uff0c\u505a\u4ed6\u7684\u5cb3\u6bcd\u5012\u5dee\u4e0d\u591a\uff0c\u4ed6\u600e\u80af\u8981\u6211\uff1f\u5bf9\u5566\uff0c\u6211\u4e00\u5411\u628a \u4f60\u5f53\u5973\u513f\u770b\u5f85\uff0c\u4f60\u5c31\u8ba9\u6211\u4e0b\u534a\u4e16\u597d\u6709\u4e2a\u4f9d\u9760\u5427\uff01\u518d\u8bf4\u4ee5\u53f2\u5e2e\u4e3b\u7684\u8eab\u4efd\uff0c\u4f60\u5ac1\u7ed9\u4ed6\u4e5f\u4e0d\u8fb1\u6ca1\u4e86 \u4f60\uff01 \u3000\u3000\u6b64\u65f6\u53ea\u6709\u9648\u5149\u7167\u5355\u72ec\u5bf9\u4ed8\u53f2\u767d\u90fd\uff0c\u5f62\u52bf\u81ea\u662f\u5341\u5206\u4e0d\u5999\u3002\u77f3\u971e\u59d1\u7ed9\u5976\u5988\u62e6\u4f4f\uff0c\u95ef\u4e0d\u8fc7\u53bb\uff0c \u6c14\u5f97\u53ea\u597d\u53d8\u4e86\u9762\u8bf4\u9053\uff1a\u201c\u5927\u5a18\uff0c\u4f60\u4e8e\u6211\u6709\u517b\u80b2\u4e4b\u6069\uff0c\u6211\u4f1a\u62a5\u7b54\u4f60\u7684\u3002\u4f46\u4f60\u8981\u903c\u6211\u5ac1\u7ed9\u8fd9\u4e2a\u59d3 \u53f2\u7684\u8d3c\u5b50\uff0c\u5374\u662f\u4e07\u4e07\u4e0d\u80fd\uff01\u8bdd\u5c31\u8bf4\u5230\u8fd9\u91cc\uff0c\u4f60\u518d\u903c\u6211\uff0c\u53ef\u4f11\u602a\u6211\u53cd\u8138\uff01\u201d \u3000\u3000\u8d3a\u5927\u5a18\u88c5\u6a21\u4f5c\u6837\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6307\u671b\u4f60\u7684\u5c31\u53ea\u6709\u8fd9\u6869\u4e8b\u60c5\uff0c\u8fd8\u8981\u4f60\u4ec0\u4e48\u62a5\u7b54\uff1f \u5509\uff0c\u4f60\u73b0\u5728\u7fc5\u8180\u786c\u4e86\uff0c\u4e0d\u80af\u542c\u4f60\u5976\u5a18\u7684\u8bdd\uff0c\u6211\u4e5f\u6ca1\u6709\u529e\u6cd5\uff0c\u4f46\u53ea\u6015\u4f60\u7fc5\u8180\u867d\u7136\u786c\u4e86\uff0c\u4e5f\u672a\u5fc5 \u5c31\u98de\u5f97\u8d77\u6765\uff01\u4f60\u522b\u5fd8\u4e86\uff0c\u518d\u8fc7\u4e03\u65e5\u5c31\u662f\u4f60\u6bd2\u53d1\u4e4b\u671f\uff0c\u4f60\u8fd8\u9700\u8981\u6211\u7684\u89e3\u836f\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u53ea\u9053\u5976\u5a18\u6069\u4e49\u539a\uff0c\u8c01\u77e5\u6000\u7684\u6bd2\u5fc3\u80a0\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u6c89\u6c5f\u5e78\u6709\u6e14\u821f\u8fc7\u3000\u641c\u5323\u72b9\u60ca\u5b9d\u5251\u5bd2 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u6c89\u6c5f\u5e78\u6709\u6e14\u821f\u8fc7\u3000\u641c\u5323\u72b9\u60ca\u5b9d\u5251\u5bd2 \u3000\u3000\u77f3\u971e\u59d1\u6c14\u6012\u4ea4\u52a0\uff0c\u6124\u7136\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4f60\u629a\u517b\u5927\u7684\uff0c\u6700\u591a\u628a\u8fd9\u6761\u6027\u547d\u4ea4\u56de\u7ed9\u4f60\uff0c\u7edd\u4e0d\u5411\u4f60 \u4e5e\u6c42\u89e3\u836f\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u8d5e\u9053\uff1a\u201c\u5bf9\uff0c\u6709\u5fd7\u6c14\u3002\u77f3\u59d1\u5a18\uff0c\u4e0d\u5fc5\u6015\u5979\u606b\u5413\uff0c\u5979\u4e0b\u7684\u5949\uff0c\u5e76\u4e0d \u89c1\u5f97\u53ea\u6709\u5979\u624d\u80fd\u89e3\uff01\u201d\u4eba\u8fd8\u672a\u66fe\u9732\u9762\uff0c\u58f0\u97f3\u5df2\u662f\u4f20\u4e86\u5230\u6765\u3002\u77f3\u971e\u59d1\u559c\u51fa\u671b\u5916\uff0c\u8d3a\u5927\u5a18\u5374\u662f\u5413 \u5f97\u9b44\u6563\u9b42\u98de\u3002\u539f\u6765\u662f\u91d1\u9010\u6d41\u6765\u4e86\uff01 \u3000\u3000\u8d3a\u5927\u5a18\u6b63\u8981\u9003\u65f6\uff0c\u4f46\u89c1\u4e00\u6761\u4eba\u5f71\uff0c\u500f\u5730\u5df2\u5230\u4e86\u5979\u7684\u9762\u524d\u3002\u8d3a\u5927\u5a18\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6211\u4e0e \u4f60\u62fc\u4e86\uff01\u201d\u5341\u6307\u5f00\u4f38\uff0c\u9e1f\u722a\u822c\u5411\u91d1\u9010\u6d41\u6293\u4e0b\u3002\u5979\u7684\u5341\u53ea\u957f\u6307\u90fd\u662f\u6d78\u8fc7\u6bd2\u836f\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5996\u5a46\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u8981\u5bb3\u4eba\uff01\u201d\u58f0\u51fa\u5251\u51fa\uff0c\u8fd9\u4e00\u5251\u771f\u662f\u524a\u5f97\u5999\u5230\u6beb \u5dc5\uff0c\u8d3a\u5927\u5a18\u7684\u5341\u53ea\u957f\u6307\u7532\u6070\u6070\u7ed9\u4ed6\u9f50\u6839\u524a\u65ad\u3002 \u3000\u3000\u77f3\u971e\u59d1\u614c\u5fd9\u53eb\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u624b\u4e0b\u7559\u60c5\uff01\u201d\u91d1\u9010\u6d41\u817e\u7684\u98de\u8d77\u4e00\u811a\uff0c\u5c06\u8d3a\u5927\u5a18\u8e22\u4e86\u4e00\u4e2a\u7b4b \u6597\uff0c\u6309\u5251\u65a5\u9053\uff1a\u201c\u4f60\u8fd9\u5996\u5987\u7b80\u76f4\u6bd4\u62ff\u517b\u5973\u5f53\u4f5c\u6447\u94b1\u6811\u7684\u8001\u9e28\u5a46\u8fd8\u66f4\u53ef\u6076\uff01\u5982\u4eca\u770b\u5728\u77f3\u59d1\u5a18\u7ed9 \u4f60\u8bf4\u60c5\u7684\u4efd\u4e0a\uff0c\u6211\u53ea\u524a\u4e86\u4f60\u7684\u6bd2\u722a\uff1b\u4f60\u82e5\u4e0d\u77e5\u6094\u6539\uff0c\u8fd8\u8981\u5411\u5979\u7ea0\u7f20\uff0c\u4e0b\u6b21\u6211\u5c31\u8981\u65ab\u65ad\u4f60\u7684\u72d7 \u5934\u4e86\uff01\u5413\u5f97\u5927\u5a18\u722c\u4e86\u8d77\u6765\uff0c\u53ea\u6068\u7239\u5a18\u5c11\u751f\u4e24\u6761\u817f\uff0c\u614c\u614c\u5fd9\u5fd9\u7684\u9003\u56de\u516d\u5408\u5e2e\u603b\u8235\uff0c\u54ea\u91cc\u8fd8\u6562\u7b54 \u8bdd\uff1f \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u91d1\u9010\u6d41\u8d76\u8dd1\u4e86\u8d3a\u5927\u5a18\uff0c\u8eab\u5f62\u4e00\u95ea\uff0c\u7acb\u5373\u53c8\u5230\u4e86\u53f2\u767d\u90fd\u7684\u9762\u524d\u3002\u53f2\u767d\u90fd \u6b63\u81ea\u4e00\u638c\u5411\u9648\u5149\u7167\u6253\u4e0b\uff0c\u5ffd\u89c1\u9752\u5149\u4e00\u6641\uff0c\u91d1\u9010\u6d41\u7684\u957f\u5251\u5df2\u6a2a\u524a\u8fc7\u6765\uff0c\u5251\u52bf\u53d8\u5e7b\u65e0\u4e07\uff0c\u53f2\u767d\u90fd \u614c\u5fd9\u7f29\u624b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u9648\u5927\u54e5\uff0c\u8bf7\u4f60\u548c\u971e\u59d1\u53bb\u7167\u5e94\u4e10\u5e2e\u7684\u670b\u53cb\uff0c\u8ba9\u6211\u6597\u4e00\u6597\u8fd9\u4f4d\u53f2\u5927\u5e2e\u4e3b\u3002\u201d\u9648 \u5149\u7167\u4e0e\u53f2\u767d\u90fd\u82e6\u6597\u4e86\u4e8c\u4e09\u5341\u62db\uff0c\u65e9\u5df2\u662f\u7d2f\u5f97\u7b4b\u75b2\u529b\u7aed\uff0c\u81ea\u77e5\u5e2e\u4e0d\u4e86\u91d1\u9010\u6d41\u7684\u5fd9\uff0c\u53ea\u597d\u542c\u4ed6\u7684 \u8bdd\u9000\u4e0b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53f2\u5927\u5e2e\u4e3b\uff0c\u4f60\u4e0d\u662f\u8981\u627e\u6211\u4e00\u51b3\u96cc\u96c4\u7684\u4e48\uff1f\u600e\u4e48\u8fd8\u4e0d\u6765\u5440\uff1f\u6765\u5427\uff0c\u6211 \u8ba9\u4f60\u4e09\u62db\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u771f\u662f\u6709\u70b9\u90aa\uff0c\u4ed6\u4e2d\u4e86\u8d3a\u5927\u5a18\u7684\u6bd2\u9488\uff0c\u5206\u660e\u5df1\u662f\u6709 \u4e86\u53d7\u4f24\u7684\u8ff9\u8c61\uff0c\u6240\u4ee5\u521a\u624d\u4e0d\u6562\u548c\u6211\u4ea4\u624b\u3002\u4f55\u4ee5\u624d\u8fc7\u4e86\u8fd9\u4e00\u4f1a\u513f\uff0c\u4ed6\u7adf\u4f3c\u6ca1\u4e8b\u4eba\u4f3c\u7684\u5b8c\u5168\u597d \u4e86\uff01\u201d\u4f46\u4ed6\u4ee5\u4e00\u5e2e\u4e4b\u4e3b\u7684\u8eab\u4efd\uff0c\u867d\u7136\u5fc3\u5185\u60ca\u7591\uff0c\u5374\u4e5f\u4e0d\u7518\u5728\u5e2e\u4f17\u4e4b\u524d\u53d7\u91d1\u9010\u6d41\u7684\u595a\u843d\uff0c\u5f53\u4e0b \u559d\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u662f\u6211\u624b\u4e0b\u8d25\u5c06\uff0c\u8c01\u8981\u4f60\u8ba9\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u662f\u4f53\u6064\u4f60\uff0c\u4f60\u5df2\u7ecf\u548c\u9648\u5149\u7167\u6253\u4e86\u4e00\u573a\uff0c\u6211\u4e0d\u80fd\u5360\u4f60\u7684\u4fbf\u5b9c\uff0c\u8fd8\u662f\u8ba9 \u4f60\u4e09\u62db\u7684\u597d\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5c31\u8ba9\u5427\uff01\u53ea\u6015\u4f60\u5403\u4e0d\u4e86\u515c\u7740\u8d70\uff01\u201d\u6697\u8fd0\u7384\u529f\uff0c\u628a\u5185\u529b \u51dd\u805a\u638c\u5fc3\uff0c\u500f\u5730\u4e00\u4e2a\u76d8\u65cb\uff0c\u53cc\u638c\u4fbf\u5411\u91d1\u9010\u6d41\u51fb\u4e0b\u3002\u91d1\u9010\u6d41\u53eb\u9053\uff1a\u201c\u54ce\u5440\uff0c\u597d\u72e0\uff01\u201d\u4ed6\u8fd9\u4e00\u53eb \u4e0d\u6253\u7d27\uff0c\u5012\u628a\u65c1\u8fb9\u7684\u9648\u5149\u7167\u5413\u4e86\u4e00\u8df3\u3002 \u3000\u3000\u53f2\u767d\u90fd\u8fd9\u4e00\u638c\u6253\u5f97\u6c99\u98de\u77f3\u8d70\uff0c\u91d1\u9010\u6d41\u811a\u6b65\u6b6a\u659c\uff0c\u4f3c\u662f\u7ad9\u7acb\u4e0d\u7a33\uff0c\u5ffd\u5730\u4e00\u4e2a\u7b4b\u6597\u4ece\u4ed6\u4fa7\u8fb9 \u7ffb\u8fc7\u3002\u65c1\u4eba\u770b\u6765\uff0c\u53f2\u767d\u90fd\u90a3\u4e00\u638c\u4f3c\u4e4e\u5df2\u7ecf\u6253\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u5176\u5b9e\u5374\u662f\u8fde\u4ed6\u7684\u8863\u88f3\u90fd\u672a\u6cbe\u7740\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\u8df3\u8d77\u8eab\u6765\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u597d\u5389\u5bb3\uff0c\u5e78\u4e8f\u6ca1\u6709\u7ed9\u6211\u6253\u7740\u3002\u201d\u9648\u5149\u7167\u8fd9 \u624d\u653e\u4e0b\u4e86\u5fc3\uff0c\u201c\u539f\u6765\u4ed6\u662f\u548c\u53f2\u767d\u90fd\u620f\u800d\u7684\u3002\u201d\u6b64\u65f6\u4e24\u5e2e\u4eba\u9a6c\u5df2\u662f\u9677\u4e8e\u5927\u6df7\u6218\u4e4b\u4e2d\uff0c\u516d\u5408\u5e2e\u7684 \u4eba\u6570\u6bd4\u4e10\u5e2e\u591a\u4e0a\u51e0\u500d\u3002\u4e10\u5e2e\u53ea\u80fd\u7ed3\u9635\u81ea\u4fdd\uff0c\u5f62\u52bf\u751a\u4e3a\u4e0d\u5229\u3002\u9648\u5149\u7167\u653e\u4e0b\u4e86\u5fc3\uff0c\u5598\u606f\u8fc7\u540e\uff0c\u9042 \u4e0e\u77f3\u971e\u59d1\u4e0a\u524d\u52a9\u6218\u3002 \u3000\u3000\u53f2\u767d\u90fd\u4e00\u51fb\u4e0d\u4e2d\uff0c\u7b2c\u4e8c\u638c\u7b2c\u4e09\u638c\u8fde\u73af\u7eed\u53d1\uff0c\u524d\u4e00\u62db\u662f\u201c\u9f99\u95e8\u9f13\u6d6a\u201d\uff0c\u540e\u4e00\u62db\u662f\u201c\u5927\u6f20\u98de \u6c99\u201d\u7684\u4e00\u91cd\u638c\u529b\u52a0\u4e0a\u540e\u4e00\u91cd\u638c\u529b\uff0c\u5f53\u771f\u662f\u6709\u5982\u60ca\u6d9b\u62cd\u5cb8\uff0c\u72c2\u6c99\u6251\u9762\u3002\u4f46\u638c\u529b\u867d\u731b\uff0c\u4ecd\u7136\u662f\u4f24 \u4e0d\u4e86\u91d1\u9010\u6d41\u3002 \u3000\u3000\u53f2\u767d\u90fd\u6b63\u4ee5\u4e3a\u53ef\u4ee5\u56f0\u7740\u91d1\u9010\u6d41\u4e4b\u9645\uff0c\u9661\u7136\u95f4\u53ea\u89c1\u56db\u9762\u516b\u65b9\uff0c\u90fd\u662f\u91d1\u9010\u6d41\u7684\u5f71\u5b50\u3002\u53f2\u767d\u90fd \u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u77e5\u4ed6\u8981\u4ece\u4f55\u5904\u88ad\u6765\uff0c\u6025\u5fd9\u56de\u638c\u81ea\u4fdd\u3002\u5ffd\u89c9\u9888\u7a9d\u4e00\u51c9\uff0c\u539f\u6765\u662f\u91d1\u9010\u6d41\u7ed5\u5230\u4ed6\u7684\u80cc \u540e\uff0c\u5439\u4e86\u4e00\u53e3\u51c9\u6c14\u3002\u53f2\u767d\u90fd\u5927\u6012\u559d\u9053\uff1a\u201c\u5c0f\u8d3c\u80c6\u6562\u620f\u6211\uff01\u201d\u53cd\u624b\u4e00\u4e2a\u64d2\u62ff\uff0c\u4eba\u672a\u56de\u5934\uff0c\u80cc\u540e \u5c31\u4f3c\u957f\u7740\u773c\u775b\u4f3c\u7684\uff0c\u638c\u6307\u6309\u62cd\u4e4b\u5904\uff0c\u5168\u662f\u5411\u7740\u91d1\u9010\u6d41\u7684\u8981\u5bb3\u90e8\u4f4d\u3002\u53cc\u65b9\u8ddd\u79bb\u592a\u8fd1\uff0c\u9976\u662f\u91d1\u9010 \u6d41\u95ea\u8eb2\u5f97\u5feb\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\u54cd\u8fc7\uff0c\u8863\u895f\u4ea6\u5df2\u88ab\u4ed6\u6293\u88c2\u4e00\u5e45\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8fde\u8eb2\u4e09\u62db\uff0c\u867d\u6ca1\u53d7\u4f24\uff0c\u4ea6\u5df2\u662f\u6c57\u6d41\u5939\u80cc\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u4e5f\u8be5\u9002\u53ef\u800c\u6b62\u4e86\u3002\u201d\u5f53 \u4e0b\u62d4\u5251\u51fa\u6765\uff0c\u7b11\u9053\uff1a\u201c\u53f2\u5927\u5e2e\u4e3b\uff0c\u6211\u5df2\u7ecf\u8ba9\u8db3\u4e09\u62db\uff0c\u793c\u5c1a\u5f80\u6765\uff0c\u6055\u6211\u4e0d\u518d\u8ba9\u4e86\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u5931\u4e86\u9762\u5b50\uff0c\u66b4\u6012\u5982\u96f7\uff0c\u8fde\u73af\u8fdb\u640f\uff0c\u731b\u5982\u6012\u72ee\u3002\u5c82\u77e5\u91d1\u9010\u6d41\u6b63\u662f\u8981\u4ed6\u5982\u6b64\uff0c\u5bf9\u65b9\u903c \u4ed6\u51b3\u6218\uff0c\u4ed6\u5374\u504f\u504f\u91c7\u53d6\u5982\u8eab\u6e38\u6597\u7684\u6253\u6cd5\uff0c\u4f7f\u5f00\u4e86\u201c\u5929\u7f57\u6b65\u6cd5\u201d\uff0c\u7a7f\u82b1\u8774\u8776\u822c\u7684\u5728\u53f2\u767d\u90fd\u7684\u8eab \u524d\u8eab\u540e\u8eab\u5de6\u8eab\u53f3\u7a7f\u6765\u63d2\u53bb\uff0c\u4e00\u53e3\u9752\u94a2\u5251\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u5251\u5149\u4e5f\u662f\u604d\u82e5\u7a7f\u68ad\uff0c\u6240\u6307\u4e4b \u5904\uff0c\u5c3d\u662f\u53f2\u767d\u90fd\u7684\u8981\u5bb3\u3002 \u3000\u3000\u53cc\u65b9\u518d\u5ea6\u4ea4\u950b\uff0c\u6070\u597d\u662f\u6613\u4f4d\u800c\u5904\u3002\u521a\u624d\u7b2c\u4e00\u6b21\u4ea4\u624b\u7684\u65f6\u5019\uff0c\u662f\u91d1\u9010\u6d41\u5fc5\u987b\u8fd0\u529f\u5fa1\u6bd2\uff0c\u96be \u4e0e\u4e89\u96c4\u3002\u73b0\u5728\u5219\u662f\u53f2\u767d\u90fd\u56e0\u4e3a\u7740\u4e86\u77f3\u971e\u59d1\u7684\u6bd2\u836f\uff0c\u96be\u4ee5\u6301\u4e45\u4e86\u3002\u4f46\u4e0d\u540c\u7684\u662f\uff1a\u91d1\u9010\u6d41\u521a\u624d\u81ea \u77e5\u4e0d\u654c\uff0c\u4fbf\u5373\u907f\u6218\uff1b\u800c\u73b0\u5728\u7684\u53f2\u767d\u90fd\u5374\u662f\u4e0d\u81ea\u91cf\u529b\uff0c\u5f3a\u653b\u5f3a\u62fc\u3002 \u3000\u3000\u638c\u98ce\u5251\u5f71\u4e4b\u4e2d\uff0c\u53f2\u767d\u90fd\u5ffd\u5730\u5f13\u8170\u8df3\u8dc3\uff0c\u5f62\u72b6\u6ed1\u7a3d\u4e4b\u6781\u3002\u539f\u6765\u77f3\u971e\u59d1\u6d12\u5728\u4ed6\u8eab\u4e0a\u7684\u836f\u7c89\uff0c \u662f\u53ef\u4ee5\u4fb5\u8680\u76ae\u80a4\uff0c\u4ee4\u4eba\u53d1\u751f\u5947\u75d2\u7684\u3002\u53f2\u767d\u90fd\u5168\u529b\u5e94\u4ed8\u91d1\u9010\u6d41\uff0c\u5143\u6687\u8fd0\u529f\u5fa1\u6bd2\uff0c\u5947\u75d2\u96be\u71ac\uff0c\u7981 \u4e0d\u7740\u8038\u80a9\u6296\u80cc\uff0c\u5f13\u8170\u8df3\u8dc3\uff0c\u660e\u77e5\u4e0d\u80fd\u628a\u836f\u7c89\u6296\u843d\uff0c\u4e5f\u89c9\u597d\u8fc7\u4e00\u4e9b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u731b\u864e\u53d8\u4f5c\u4e86\u7334\u513f\u5566\uff0c\u6211\u53ef\u6ca1\u6709\u800d\u7334\u513f\u7684\u5174\u8da3\uff0c\u4f60\u8fd8\u8981\u518d\u6253\u4e0b\u53bb\u5417\uff1f\u201d\u53e3 \u91cc\u8bf4\u662f\u4e0d\u60f3\u518d\u6253\uff0c\u624b\u4e2d\u7684\u5251\u5374\u662f\u53cd\u5b88\u4e3a\u653b\uff0c\u8d8a\u53d1\u51cc\u5389\u3002 \u3000\u3000\u53f2\u767d\u90fd\u624b\u4e0b\u7684\u4e09\u4e2a\u9999\u4e3b\u548c\u6587\u9053\u5e84\u8fd8\u5728\u897f\u57ce\u9010\u5c4b\u641c\u7d22\uff0c\u5374\u4e0d\u77e5\u91d1\u9010\u6d41\u65e9\u5df2\u5230\u4e86\u4e1c\u95e8\uff0c\u548c\u53f2 \u767d\u90fd\u4ea4\u624b\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u7b49\u4e0d\u89c1\u4ed6\u4eec\u56de\u6765\uff0c\u6697\u6697\u6094\u6068\u81ea\u5df1\u5b89\u6392\u7684\u9519\u8bef\u3002\u91d1\u9010\u6d41\u4e00\u8f6e\u731b\u653b\uff0c\u653b\u5f97\u4ed6\u72fc\u72c8\u4e4b \u6781\u3002\u4ed6\u53ea\u597d\u5fcd\u4f4f\u4e86\u6c14\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5927\u4e08\u592b\u80fd\u5c48\u80fd\u4f38\uff0c\u6211\u82e5\u4e0d\u53ca\u65e9\u56de\u53bb\uff1b\u53ea\u6015\u8fd8\u8981\u5403\u8fd9\u5c0f\u5b50 \u7684\u5927\u4e8f\u3002\u201d\u8981\u77e5\u4ed6\u4e2d\u6bd2\u4e4b\u540e\uff0c\u5230\u73b0\u5728\u5df2\u662f\u5dee\u4e0d\u591a\u6709\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4ed6\u662f\u5fc5\u987b\u56de\u53bb\u5411\u8d3a\u5927\u5a18\u8ba8\u53d6\u89e3 \u836f\u7684\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u594b\u529b\u4e00\u638c\uff0c\u628a\u91d1\u9010\u6d41\u903c\u9000\u4e00\u6b65\uff0c\u559d\u9053\uff1a\u201c\u4eca\u665a\u6682\u4e14\u8ba9\u4f60\uff0c\u6162\u6162\u548c\u4f60\u7b97\u5e10\uff0c\u674e\u8235 \u4e3b\uff0c\u4f60\u81ea\u5df1\u5e94\u8be5\u660e\u767d\uff0c\u6253\u4e0b\u53bb\u4f60\u4eec\u51b3\u4e0d\u80fd\u5360\u5f97\u4fbf\u5b9c\u3002\u770b\u5728\u4f60\u6211\u4e24\u5e2e\u4e00\u5411\u76f8\u5b89\u7684\u4efd\u4e0a\uff0c\u4eca\u665a\u4e4b \u4e8b\uff0c\u5c31\u6b64\u4f5c\u7f62\uff0c\u4f60\u610f\u5982\u4f55\uff1f\u201d \u3000\u3000\u6b64\u65f6\u5f62\u52bf\uff0c\u53f2\u767d\u90fd\u867d\u7136\u6253\u4e0d\u8fc7\u91d1\u9010\u6d41\uff0c\u4f46\u4e24\u5e2e\u7684\u6df7\u6218\uff0c\u5374\u8fd8\u662f\u516d\u5408\u5e2e\u7684\u4eba\u591a\u5360\u4e86\u4e0a\u98ce\u3002 \u674e\u8302\u89c1\u91d1\u9010\u6d41\u5df2\u7ecf\u56de\u6765\uff0c\u4ed6\u4e5f\u4e0d\u613f\u5e2e\u4f17\u6709\u8fc7\u591a\u7684\u4f24\u4ea1\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u65e2\u6c42\u9976\uff0c\u6211\u5c31 \u653e\u4f60\u5427\u3002\u4ee5\u540e\u5982\u4f55\uff0c\u4ee5\u540e\u518d\u8bf4\u3002\u4f60\u62a5\u590d\u4e5f\u597d\uff0c\u4e0d\u62a5\u590d\u4e5f\u597d\uff0c\u4efb\u4ece\u4f60\u6765\uff0c\u4e10\u5e2e\u4e5f\u7edd\u4e0d\u6015\u4f60\u3002\u201d \u3000\u3000\u4e8e\u662f\u53cc\u65b9\u5404\u81ea\u6536\u5175\uff0c\u53f2\u767d\u90fd\u8d76\u5fd9\u56de\u53bb\u8ba8\u53d6\u89e3\u836f\uff0c\u91d1\u9010\u6d41\u4e0e\u9648\u5149\u7167\u7b49\u4eba\uff0c\u4e5f\u968f\u7740\u674e\u8302\uff0c\u56de \u8f6c\u4e10\u5e2e\u5206\u8235\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8bb2\u4e86\u5728\u516d\u5408\u5e2e\u7684\u906d\u9047\u4e4b\u540e\uff0c\u9648\u5149\u7167\u60f3\u8d77\u4e00\u4e8b\uff0c\u95ee\u9053\uff1a\u201c\u91d1\u5144\uff0c\u4f60\u521a\u624d\u595a\u843d\u90a3\u4e2a\u5996 \u5a46\uff0c\u8bf4\u662f\u5979\u7ed9\u971e\u59d1\u6240\u4e0b\u7684\u6bd2\uff0c\u672a\u5fc5\u53ea\u6709\u5979\u53ef\u89e3\uff0c\u8fd9\u8bdd\u662f\u771f\u662f\u5047\uff1f\u5389\u5927\u54e5\u53ef\u662f\u8fd8\u6ca1\u627e\u7740\u554a\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u5f53\u7136\u662f\u771f\u3002\u8fd9\u4e2a\u80fd\u89e3\u5929\u9b54\u6559\u79d8\u4f20\u7684\u6bd2\u836f\u4e4b\u4eba\uff0c\u5982\u4eca\u5c31\u5728\u8fd9\u513f\uff0c\u674e\u5144\uff0c\u8bf7 \u51fa\u6765\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u66ff\u674e\u6566\u548c\u9648\u3001\u77f3\u4e8c\u4eba\u4ecb\u7ecd\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u5389\u5927\u54e5\u867d\u6ca1\u627e\u7740\uff0c\u4f46\u6709\u4e86\u8fd9\u4f4d\u674e\u5144\u4e5f\u662f \u4e00\u6837\uff0c\u8fd9\u4f4d\u674e\u5144\u719f\u8bfb\u5929\u9b54\u6559\u7684\u767e\u6bd2\u771f\u7ecf\uff0c\u89e3\u6bd2\u7684\u672c\u9886\u53ea\u6709\u5728\u90a3\u8001\u5996\u5a46\u4e4b\u4e0a\u3002\u201d \u3000\u3000\u9648\u3001\u77f3\u4e8c\u4eba\u559c\u51fa\u671b\u5916\uff0c\u5fd9\u5411\u674e\u6566\u9884\u5148\u9053\u8c22\u3002\u674e\u6566\u8bca\u8fc7\u4e86\u77f3\u971e\u59d1\u7684\u8109\uff0c\u7ed9\u4e86\u5979\u4e00\u5305\u89e3\u836f\uff0c \u53c8\u4ed4\u7ec6\u7684\u4f20\u6388\u4e86\u9648\u5149\u7167\u89e3\u8fd9\u79cd\u6bd2\u7684\u63a8\u8840\u8fc7\u5bab\u4e4b\u6cd5\uff0c\u5c31\u8ba9\u9648\u5149\u7167\u81ea\u5df1\u53bb\u7ed9\u77f3\u971e\u59d1\u89e3\u6bd2\u3002\u597d\u5728\u8fd9 \u79cd\u63a8\u8840\u8fc7\u5bab\u4e4b\u6cd5\u5e76\u4e0d\u590d\u6742\uff0c\u9648\u5149\u7167\u4e00\u542c\u5c31\u61c2\u3002 \u3000\u3000\u9648\u5149\u7167\u5411\u674e\u8302\u8ba8\u4e86\u4e00\u95f4\u9759\u5ba4\uff0c\u4fbf\u4e0e\u77f3\u971e\u59d1\u8fdb\u53bb\u4f9d\u6cd5\u6cbb\u7597\u3002\u91d1\u3001\u674e\u4e8c\u4eba\u5219\u548c\u674e\u8302\u7ee7\u7eed\u5546\u8ba8 \u4eca\u540e\u7684\u884c\u6b62\u3002 \u3000\u3000\u674e\u6566\u8bf4\u9053\uff1a\u201c\u91d1\u5144\uff0c\u6211\u521a\u624d\u8fd8\u672a\u66fe\u544a\u8bc9\u4f60\uff0c\u6211\u5728\u897f\u660c\uff0c\u5df2\u7ecf\u89c1\u8fc7\u4f60\u7684\u5e08\u5144\u6c5f\u5927\u4fa0\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u559c\u9053\uff1a\u201c\u662f\u4e48\uff0c\u4ed6\u53ef\u6709\u4ec0\u4e48\u6258\u4f60\u8f6c\u544a\u6211\u7684\uff1f\u201d \u3000\u3000\u674e\u6566\u7b11\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u5f53\u7136\u4e0d\u4f1a\u77e5\u9053\u6211\u4f1a\u9047\u89c1\u4f60\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u90a3\u8fb9\u5374\u662f\u5f88\u9700\u8981\u4eba\u3002\u770b\u60c5 \u5f62\uff0c\u7ecf\u8fc7\u4e86\u4eca\u665a\u8fd9\u573a\u5927\u95f9\uff0c\u53f2\u767d\u90fd\u5fc5\u7136\u66f4\u4e3a\u6212\u5907\u68ee\u4e25\uff0c\u516d\u5408\u5e2e\u7684\u597d\u624b\u6bd4\u6211\u4eec\u591a\u5f97\u591a\uff0c\u6682\u65f6\u53ea \u6015\u662f\u96be\u4ee5\u6551\u51fa\u53f2\u59d1\u5a18\u4e86\u3002\u4f9d\u6211\u4e4b\u89c1\uff0c\u4e0d\u5982\u54b1\u4eec\u5230\u897f\u660c\u53bb\u6765\u4e2a\u4ee5\u9038\u5f85\u52b3\u3002\u201d \u3000\u3000\u674e\u8302\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u4ee5\u9038\u5f85\u52b3\uff1f\u201d\u91d1\u9010\u6d41\u542c\u4e86\u674e\u6566\u7684\u8bdd\uff0c\u5374\u662f\u4e00\u70b9\u5373\u900f\uff0c\u7b11 \u9053\uff1a\u201c\u4e0d\u9519\uff0c\u95f9\u51fa\u4e86\u8fd9\u573a\u5a5a\u53d8\uff0c\u53f2\u767d\u90fd\u8d76\u8d70\u4e86\u5389\u5927\u54e5\uff0c\u548c\u4ed6\u7684\u59b9\u5b50\u4e5f\u5df2\u7ecf\u6495\u7834\u4e86\u8138\uff0c\u770b\u6765\u4ed6 \u662f\u4e00\u5b9a\u4f1a\u628a\u53f2\u7ea2\u82f1\u9001\u5230\u897f\u660c\uff0c\u903c\u5979\u5ac1\u7ed9\u90a3\u4e2a\u897f\u660c\u5c06\u519b\u5e05\u5b5f\u96c4\u7684\u4e86\u3002\u54b1\u4eec\u5728\u8fd9\u91cc\u6597\u4e0d\u8fc7\u4ed6\uff0c\u5230 \u4e86\u897f\u660c\u548c\u4ed6\u518d\u6597\u3002\u201d\u674e\u6566\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u897f\u660c\u5982\u4eca\u662f\u5728\u6e05\u519b\u624b\u4e2d\uff0c\u4f46\u5728\u57ce\u5916\u7684\u5927\u51c9\u5c71\u5c31\u662f\u4e49\u519b \u57fa\u5730\u3002\u4e49\u519b\u9996\u9886\u7afa\u5c1a\u7236\u662f\u6b66\u6797\u7684\u8001\u524d\u8f88\u3001\u5927\u5b97\u5e08\uff0c\u6c5f\u5927\u4fa0\u53c8\u5df2\u5230\u4e86\u90a3\u513f\uff0c\u53f2\u767d\u90fd\u9001\u4ed6\u7684\u59b9\u59b9 \u5230\u897f\u660c\uff0c\u54b1\u4eec\u6b63\u597d\u8054\u7edc\u4e49\u519b\uff0c\u593a\u57ce\u52ab\u4eba\uff0c\u4e00\u4e3e\u4e24\u5f97\u3002\u201d \u3000\u3000\u674e\u8302\u7b11\u9053\uff1a\u201c\u53ea\u987b\u4e00\u4e2a\u6c5f\u5927\u4fa0\uff0c\u5c31\u662f\u518d\u591a\u51e0\u4e2a\u53f2\u767d\u90fd\uff0c\u4e5f\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u3002\u597d\uff0c\u5c31\u8fd9\u4e48 \u6837\uff0c\u6211\u4e5f\u8ddf\u4f60\u4eec\u4e00\u8d77\u53bb\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6b63\u81ea\u62c5\u5fe7\u4ed6\u4eec\u8d70\u4e86\u4e4b\u540e\uff0c\u53f2\u767d\u90fd\u6765\u5411\u4e10\u5e2e\u62a5\u590d\uff0c\u674e\u8302\u53ef\u662f\u4e0d\u6613\u62b5\u6321\uff0c\u542c\u4e86\u674e\u8302\u8fd9 \u6837\u8bf4\uff0c\u7b11\u9053\uff1a\u201c\u5bf9\uff0c\u541b\u5b50\u62a5\u4ec7\uff0c\u5341\u5e74\u672a\u665a\u3002\u53f2\u767d\u90fd\u662f\u626c\u5dde\u7684\u5730\u5934\u86c7\uff0c\u9ed1\u767d\u4e24\u9053\u5168\u90fd\u662f\u548c\u4ed6\u6709 \u52fe\u7ed3\u7684\uff0c\u4f60\u4eec\u6682\u4e14\u8ba9\u4ed6\u4e00\u8ba9\uff0c\u5230\u4e86\u897f\u660c\uff0c\u518d\u548c\u4ed6\u7b97\u603b\u5e10\u3002\u201d \u3000\u3000\u8ba1\u8bae\u5df2\u5b9a\uff0c\u5f85\u5230\u5929\u660e\uff0c\u4fbf\u5373\u52a8\u8eab\uff0c\u77f3\u971e\u59d1\u6240\u4e2d\u7684\u6bd2\uff0c\u65e9\u5df2\u7531\u9648\u5149\u7167\u6309\u7167\u674e\u6566\u6240\u6388\u7684\u201c\u91d1 \u9488\u62d4\u6bd2\u201d\u4e4b\u6cd5\u66ff\u5979\u62d4\u6e05\u3002\u9648\u3001\u77f3\u4e8c\u4eba\u4e5f\u968f\u5927\u4f19\u540c\u5f80\u897f\u660c\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8fd9\u6b21\u5927\u95f9\u516d\u5408\u5e2e\uff0c\u6551\u4eba\u867d\u6ca1\u6210\u529f\uff0c\u4e5f\u603b\u7b97\u662f\u7834\u4e86\u53f2\u767d\u90fd\u7684\u9634\u8c0b\u3002\u53f2\u7ea2\u82f1\u76ee\u524d\u6682\u65f6 \u53d7\u56f0\uff0c\u4f46\u6599\u60f3\u51b3\u65e0\u751f\u547d\u4e4b\u5fe7\u3002\u552f\u4e00\u4f7f\u4ed6\u653e\u5fc3\u4e0d\u4e0b\u7684\u5c31\u53ea\u6709\u5389\u5357\u661f\u4e86\u3002 \u3000\u3000\u82b1\u5f00\u4e24\u6735\uff0c\u5404\u8868\u4e00\u6280\u3002\u91d1\u9010\u6d41\u7b49\u4eba\u524d\u5f80\u897f\u660c\uff0c\u6682\u4e14\u6309\u4e0b\u4e0d\u8868\uff0c\u4e14\u8bf4\u8bf4\u5389\u5357\u661f\u7684\u906d\u9047\u3002 \u3000\u3000\u4e14\u8bf4\u5389\u5357\u661f\u90a3\u665a\u906d\u8d3a\u5927\u5a18\u7684\u6697\u7b97\uff0c\u4e2d\u4e86\u4e00\u53e3\u6bd2\u9488\uff0c\u4e3a\u4e86\u6446\u8131\u8d3a\u5927\u5a18\u7684\u7ea0\u7f20\uff0c\u4e5f\u4e3a\u4e86\u6050\u6015 \u53f2\u767d\u90fd\u8d76\u5230\uff0c\u5389\u5357\u660c\u8feb\u4e0d\u5f97\u5df2\uff0c\u53ea\u597d\u8df3\u4e0b\u6c5f\u4e2d\u3002 \u3000\u3000\u8fd9\u4e00\u6bb5\u6c5f\u9762\u6c34\u6d41\u6e4d\u6025\uff0c\u597d\u5728\u5389\u5357\u661f\u7cbe\u901a\u6c34\u6027\u3001\u6e4d\u6025\u7684\u6c34\u6d41\u6b63\u597d\u4e3a\u4ed6\u6240\u7528\uff0c\u7701\u5374\u4ed6\u5212\u6c34\u7684 \u6c14\u529b\u3002\u4ed6\u6697\u8fd0\u5185\u529f\uff0c\u95ed\u4e86\u4e2d\u6bd2\u4e4b\u5904\u7684\u9644\u8fd1\u7a74\u9053\uff0c\u968f\u7740\u6ce2\u6d9b\uff0c\u987a\u6d41\u800c\u4e0b\uff0c\u624b\u4e2d\u4ecd\u7136\u7d27\u7d27\u63e1\u7740\u90a3 \u628a\u7384\u94c1\u5b9d\u5251\uff0c\u6c34\u662f\u6709\u6d6e\u529b\u7684\uff0c\u53ea\u8981\u662f\u7cbe\u901a\u6c34\u6027\u7684\u4eba\uff0c\u5728\u6c34\u4e2d\u643a\u5e26\u91cd\u7269\uff0c\u6bd4\u5728\u5cb8\u4e0a\u7701\u529b\u5f97\u591a\uff0c \u5389\u5357\u661f\u867d\u662f\u53d7\u4f24\uff0c\u4e5f\u8fd8\u652f\u6301\u5f97\u4f4f\u3002\u6c34\u6d41\u6e4d\u6025\uff0c\u4e0d\u6d88\u534a\u4e2a\u65f6\u8fb0\uff0c\u5df2\u628a\u4ed6\u51b2\u5230\u4e0b\u6e38\uff0c\u626c\u5dde\u57ce\u5df2\u662f \u8fdc\u8fdc\u5730\u629b\u5728\u540e\u9762\u3002 \u3000\u3000\u4f46\u5389\u5357\u661f\u6bd5\u7adf\u662f\u7ecf\u8fc7\u4e86\u4e00\u665a\u7684\u6fc0\u6218\uff0c\u5982\u4eca\u5728\u6ce2\u6d9b\u4e2d\u98d8\u8361\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u624b\u4e2d\u53c8\u662f\u63d0\u7740\u767e \u591a\u65a4\u91cd\u7684\u7384\u94c1\u5b9d\u5251\u5230\u4e86\u6c5f\u6d41\u5e73\u7f13\u4e4b\u5904\uff0c\u5fc5\u987b\u4ed6\u5212\u6c34\u524d\u8fdb\uff0c\u6e10\u6e10\u4e5f\u611f\u5230\u4e86\u6c14\u529b\u4e0d\u652f\uff0c\u96be\u4ee5\u4e3a\u7ee7 \u4e86\u3002 \u3000\u3000\u5389\u5357\u661f\u820d\u4e0d\u5f97\u653e\u5f03\u5b9d\u5251\uff0c\u8fdc\u8fdc\u7684\u770b\u89c1\u6c5f\u9762\u4f3c\u6709\u6e14\u706b\uff0c\u5fc3\u91cc\u60f3\u5230\uff1a\u201c\u6211\u53ea\u987b\u652f\u6301\u7247\u523b\uff0c\u6216 \u8bb8\u8fd8\u80fd\u591f\u6e38\u5230\u90a3\u513f\u3002\u201d\u54ea\u77e5\u6c14\u529b\u51cf\u5f31\u4e4b\u540e\uff0c\u7a74\u9053\u5c01\u95ed\u4e0d\u4f4f\uff0c\u6bd2\u6027\u4fbf\u5373\u53d1\u4f5c\u3002 \u3000\u3000\u5389\u5357\u661f\u6b63\u5728\u6c34\u4e2d\u6f5c\u6cf3\uff0c\u5ffd\u89c9\u8111\u888b\u6655\u7729\uff0c\u5fc3\u77e5\u4e0d\u5999\uff0c\u60f3\u8981\u8dc3\u51fa\u6c34\u9762\u53eb\u56b7\uff0c\u54ea\u77e5\u4e0d\u7528\u529b\u8fd8 \u597d\uff0c\u4e00\u4e2a\u7528\u529b\uff0c\u53cd\u800c\u6c89\u4e0b\u53bb\u4e86\u3002\u8fd9\u662f\u56e0\u4e3a\u4ed6\u5e26\u7740\u7384\u94c1\u5b9d\u5251\u7684\u7f18\u6545\uff0c\u5e26\u7740\u8fd9\u67c4\u5b9d\u5251\uff0c\u7b49\u4e8e\u8eab\u4e0a \u7f1a\u7740\u767e\u591a\u65a4\u91cd\u7684\u77f3\u5934\uff0c\u4e00\u65e6\u6c14\u529b\u6d88\u5931\uff0c\u54ea\u91cc\u8fd8\u80fd\u6d6e\u5f97\u8d77\u6765\uff1f \u3000\u3000\u672c\u6765\u5389\u5357\u661f\u5e94\u8be5\u5728\u7d27\u8981\u7684\u5173\u5934\u629b\u5f00\u5b9d\u5251\u7684\uff0c\u53ea\u56e0\u6bd2\u53d1\u5f97\u5feb\uff0c\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\uff0c\u4e00\u89c9\u4e0d \u5999\uff0c\u8f6c\u77ac\u4fbf\u5df2\u795e\u667a\u8ff7\u7cca\u3002\u5728\u795e\u667a\u8ff7\u7cca\u4e2d\uff0c\u5389\u5357\u661f\u672c\u80fd\u7684\u4ecd\u7136\u63e1\u7262\u8fd9\u67c4\u5b9d\u5251\u3002 \u3000\u3000\u6b64\u65f6\u521a\u662f\u9ece\u660e\u65f6\u5206\uff0c\u4e5f\u662f\u5389\u5357\u57a6\u547d\u4e0d\u8be5\u7edd\uff0c\u78b0\u4e0a\u4e86\u4e00\u8258\u4e00\u65e9\u51fa\u6765\u6253\u9c7c\u7684\u6e14\u8239\u3002 \u3000\u3000\u8239\u4e0a\u662f\u7236\u5b50\u4e09\u4eba\uff0c\u8001\u6e14\u592b\u6492\u4e0b\u4e86\u7f51\uff0c\u6070\u597d\u7f51\u7740\u4e86\u5389\u5357\u661f\u3002\u4ed6\u7528\u529b\u4e00\u62c9\uff0c\u5389\u5357\u91cf\u7ffb\u4e86\u4e2a \u8eab\uff0c\u51e0\u4e4e\u628a\u4ed6\u62d6\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8001\u6e14\u592b\u53c8\u60ca\u53c8\u559c\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u6709\u4e00\u5c3e\u5927\u9c7c\u5165\u7f51\u4e86\uff0c\u4f60\u4eec\u5feb\u6765\u5e2e\u5fd9\uff01\u201d \u3000\u3000\u4ed6\u7684\u4e24\u4e2a\u513f\u5b50\u5e74\u8f7b\u529b\u58ee\uff0c\u4e09\u4eba\u5408\u529b\uff0c\u624d\u628a\u9c7c\u7f51\u7f13\u7f13\u62c9\u8d77\u3002\u5927\u513f\u5b50\u9053\uff1a\u201c\u4e0d\u5bf9\uff0c\u54ea\u6709\u8fd9\u6837 \u91cd\u7684\u5927\u9c7c\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5c0f\u513f\u5b50\u5df2\u5728\u53eb\u9053\uff1a\u201c\u5440\uff0c\u539f\u6765\u662f\u4e00\u4e2a\u4eba\uff01\u201d\u5927\u513f\u5b50\u5600\u5495\u9053\uff1a\u201c\u662f\u4eba \u4e5f\u4e0d\u8be5\u8fd9\u4e48\u91cd\uff01\u201d \u3000\u3000\u8001\u6e14\u592b\u9053\uff1a\u201c\u6551\u4eba\u4e00\u547d\uff0c\u80dc\u9020\u4e03\u7ea7\u6d6e\u5c60\u3002\u628a\u4ed6\u62d6\u4e0a\u6765\u5427\u3002\u201d\u62c9\u4e86\u4e0a\u6765\uff0c\u89e3\u5f00\u9c7c\u7f51\uff0c\u4e24\u4e2a \u513f\u5b50\u5567\u5567\u79f0\u5f02\uff0c\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u5e76\u4e0d\u7c97\u58ee\uff0c\u770b\u6765\u5012\u50cf\u662f\u4e2a\u6e05\u79c0\u6587\u5f31\u7684\u4e66\u751f\uff0c\u600e\u7684\u5374\u4f1a\u8fd9\u4e48 \u91cd\u5462\uff1f\u6211\u770b\u6700\u5c11\u4e5f\u6709\u4e24\u767e\u65a4\uff0c\u83ab\u975e\u4ed6\u7684\u8eab\u4e0a\u5e26\u6709\u91d1\u94f6\u73e0\u5b9d\uff1f\u201d \u3000\u3000\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u54ea\u6709\u4eba\u5e26\u7740\u4e00\u767e\u51e0\u5341\u65a4\u91cd\u7684\u91d1\u94f6\u8df3\u6c34\u7684\uff1f\u6211\u6562\u65ad\u5b9a\u4ed6\u6ca1\u6709\u3002\u4e0d\u8fc7\uff0c\u5251\u5012\u662f \u6709\u4e00\u628a\u3002\u54ce\u5440\uff0c\u6562\u60c5\u4ed6\u662f\u6d77\u76d7\uff1f\u5f3a\u76d7\u4e5f\u6709\u6839\u8c8c\u65af\u6587\u7684\u554a\uff01\u201d\u8bf4\u7f62\uff0c\u4f38\u624b\u5728\u5389\u5357\u661f\u8eab\u4e0a\u4e00\u6478\uff0c \u7b11\u9053\uff1a\u201c\u4e0d\u51fa\u6240\u6599\uff0c\u8eab\u4e0a\u8fde\u4e00\u4e2a\u94b1\u90fd\u6ca1\u6709\u3002\u54fc\uff0c\u7a7f\u7684\u8863\u670d\u5012\u662f\u6f02\u4eae\u3002\u201d\u539f\u6765\u5389\u5357\u661f\u8fd8\u662f\u7a7f\u7740 \u6628\u65e5\u505a\u65b0\u90ce\u7684\u90a3\u8eab\u8863\u670d\uff0c\u4ed6\u5e76\u6ca1\u60f3\u5230\u8981\u4ece\u6d1e\u623f\u4e2d\u9003\u51fa\u6765\uff0c\u8eab\u4e0a\u54ea\u4f1a\u5e26\u5f97\u6709\u94b1\uff1f \u3000\u3000\u8001\u6e14\u592b\u9053\uff1a\u201c\u6211\u4eec\u662f\u5b89\u4efd\u5b88\u5df1\u7684\u6e14\u4eba\uff0c\u4f46\u6c42\u79ef\u70b9\u9634\u5fb7\uff0c\u5c82\u80fd\u5e0c\u7f55\u4eba\u5bb6\u7684\u94b1\u8d22\uff1f\u4e0d\u7ba1\u4ed6\u662f \u4ec0\u4e48\u4eba\uff0c\u5148\u6551\u4e86\u518d\u8bf4\u3002\u201d \u3000\u3000\u505c\u6cca\u5728\u5cb8\u8fb9\u7684\u4e00\u8258\u5546\u8239\u4f3c\u662f\u5df2\u7ed9\u60ca\u52a8\uff0c\u6b63\u5728\u5411\u8fd9\u8fb9\u5212\u6765\u3002\u8001\u6e14\u592b\u6551\u4eba\u8981\u7d27\uff0c\u4e5f\u4e0d\u653e\u5728\u5fc3 \u4e0a\u3002 \u3000\u3000\u8001\u6e14\u592b\u6307\u6325\u4e24\u4e2a\u513f\u5b50\u628a\u5389\u5357\u661f\u809a\u5b50\u91cc\u7684\u6c34\u538b\u69a8\u51fa\u6765\uff0c\u8fd9\u6837\u5c31\u5148\u8981\u62ff\u5f00\u5389\u5357\u661f\u624b\u4e2d\u7684\u5251\uff0c \u4ed6\u7684\u5927\u513f\u5b50\u5b66\u8fc7\u51e0\u5929\u628a\u5f0f\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u67c4\u5251\u6211\u5f53\u7136\u4e0d\u80fd\u8981\u4ed6\u7684\uff0c\u4f46\u62ff\u6765\u73a9\u73a9\u4e5f\u597d\u3002\u201d\u5f53\u4e0b\u6273 \u5f00\u4e86\u5389\u5357\u661f\u7684\u624b\u6307\uff0c\u628a\u7384\u94c1\u5b9d\u5251\u4e00\u63d0\u3002 \u3000\u3000\u4ed6\u54ea\u91cc\u60f3\u5f97\u5230\u8fd9\u67c4\u5b9d\u5251\u7adf\u6709\u767e\u591a\u65a4\u91cd\uff0c\u5c82\u662f\u4ed6\u4e00\u53ea\u624b\u63d0\u5f97\u8d77\u6765\uff1f\u53ea\u542c\u5f97\u201c\u54ce\u5537\u201d\u4e00\u58f0\uff0c \u7384\u94c1\u5b9d\u5251\u4ece\u5389\u5357\u661f\u8eab\u8fb9\u79fb\u5f00\uff0c\u53ef\u662f\u8fd9\u4e2a\u7c97\u58ee\u7684\u5c0f\u4f19\u5b50\u4e5f\u95ea\u4e86\u8170\u9aa8\uff0c\u91cd\u91cd\u5730\u6454\u4e86\u4e00\u8de4\u3002\u7384\u94c1\u5b9d \u5251\u201c\u549a\u201d\u7684\u4e00\u58f0\u8dcc\u4e86\u4e0b\u6765\uff0c\u538b\u88c2\u4e86\u4e00\u5757\u8239\u677f\u3002 \u3000\u3000\u8001\u6e14\u592b\u5403\u4e86\u4e00\u60ca\uff0c\u614c\u5fd9\u62c9\u8d77\u513f\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u7684\u8fd9\u6837\u4e0d\u5c0f\u5fc3\uff0c\u8dcc\u7740\u54ea\u513f\u4e86\uff1f\u201d\u8001\u5927\u7ad9 \u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u4e8b\uff0c\u73b0\u5728\u6211\u77e5\u9053\u4e86\uff0c\u539f\u6765\u4e0d\u662f\u4eba\u91cd\uff0c\u662f\u8fd9\u67c4\u5251\u91cd\u3002\u201d\u8001\u4e8c\u597d\u5947\u5fc3\u8d77\uff0c\u5c0f \u5fc3\u7ffc\u7ffc\u7684\u8fc7\u53bb\u8bd5\u4e86\u4e00\u8bd5\uff0c\u679c\u7136\u63d0\u4e0d\u8d77\u6765\uff0c\u8be7\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u62ff\u8d77\u4e00\u767e\u65a4\u91cd\u7684\u4e1c\u897f\uff0c\u8fd9\u67c4\u5251\u4e0d\u8fc7 \u4e09\u5c3a\u957f\u5427\uff0c\u6211\u7adf\u7136\u52a8\u4e5f\u52a8\u4e0d\u4e86\u5b83\uff0c\u4e00\u67c4\u5251\u6709\u8fd9\u4e48\u91cd\uff0c\u771f\u662f\u53e4\u602a\uff01\u201d \u3000\u3000\u7236\u5b50\u4e09\u4eba\u6b63\u5728\u5567\u5567\u79f0\u5f02\uff0c\u90a3\u8258\u5546\u8239\u5df2\u7ecf\u9760\u5728\u6e14\u821f\u4e4b\u65c1\uff0c\u4e00\u4e2a\u77ed\u9aef\u5982\u671d\u7684\u9ed1\u8863\u6c49\u5b50\u5ffd\u5730\u8df3 \u8fc7\u8239\u6765\u3002 \u3000\u3000\u8001\u6e14\u592b\u5413\u4e86\u4e00\u8df3\uff0c\u4e0d\u77e5\u6b64\u4eba\u662f\u4f55\u8def\u9053\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u6551\u4eba\u603b\u6ca1\u72af\u6cd5\u3002\u201d\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u5148 \u751f\uff0c\u6709\u4f55\u8d35\u5e72\uff1f\u201d \u3000\u3000\u90a3\u9ed1\u8863\u6c49\u5b50\u9053\uff1a\u201c\u6211\u770b\u89c1\u4f60\u4eec\u6551\u4eba\uff0c\u7279\u5730\u8fc7\u6765\u770b\u770b\uff0c\u6216\u8bb8\u4f60\u4eec\u8981\u4eba\u5e2e\u5fd9\uff1f\u54ce\u5440\uff0c\u8fd9\u4e0d\u662f \u5c0f\u738b\u5417\uff1f\u5c0f\u738b\uff0c\u5c0f\u738b\uff01\u201d \u3000\u3000\u8001\u6e14\u592b\u53c8\u60ca\u53c8\u559c\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u548c\u4ed6\u662f\u670b\u53cb\uff1f\u201d\u9ed1\u8863\u6c49\u5b50\u9053\uff1a\u201c\u6570\u65e5\u524d\u6211\u8fd8\u548c\u4ed6\u5728\u626c\u5dde\u4e00 \u540c\u6e38\u73a9\u7684\uff0c\u600e\u7684\u4ed6\u5374\u4f1a\u5728\u6c5f\u4e2d\u7ed9\u4f60\u4eec\u635e\u8d77\u6765\uff1f\u5509\uff0c\u6211\u77e5\u9053\u4ed6\u6709\u70b9\u4e8b\u60c5\u70e6\u607c\uff0c\u4f46\u90a3\u4e5f\u4e0d\u662f\u4ec0\u4e48 \u4e0d\u5f97\u4e86\u7684\u5927\u4e8b\uff0c\u96be\u9053\u4ed6\u4e00\u65f6\u770b\u4e0d\u5f00\u5c31\u81ea\u5bfb\u77ed\u89c1\u4e86\uff01\u5509\uff0c\u4ed6\u662f\u5931\u8db3\u843d\u6c34\u7684\u5462\uff0c\u8fd8\u662f\u81ea\u5bfb\u77ed\u89c1\u7684 \u5462\uff1f\u201d \u3000\u3000\u8fd9\u6c49\u5b50\u76f8\u8c8c\u7c97\u8c6a\uff0c\u5374\u4f3c\u4e2a\u8001\u5a46\u5a46\u4f3c\u7684\u5520\u5520\u53e8\u53e8\u8bf4\u4e2a\u4e0d\u4f11\uff0c\u663e\u7136\u662f\u5bf9\u8fd9\u4f4d\u8001\u670b\u53cb\u5173\u5fc3\u4e4b \u6781\u3002\u5927\u513f\u5b50\u5fcd\u4e0d\u7740\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u600e\u4e48\u77e5\u9053\uff0c\u4f60\u5f85\u4ed6\u9192\u4e86\u4eb2\u81ea\u95ee\u4ed6\u5427\u3002\u201d\u8001\u6e14\u592b\u62c5\u5fe7\u9053\uff1a \u201c\u6211\u4eec\u5df2\u7ecf\u628a\u4ed6\u809a\u5b50\u91cc\u7684\u79ef\u6c34\u69a8\u51fa\u6765\u4e86\uff0c\u5947\u602a\uff0c\u600e\u7684\u73b0\u5728\u8fd8\u672a\u9192\u6765\uff1f\u201d \u3000\u3000\u9ed1\u8863\u6c49\u5b50\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u6211\u7684\u670b\u53cb\u7ed9\u4f60\u4eec\u518d\u6dfb\u9ebb\u70e6\uff0c\u591a\u8c22\u4f60\u4eec\u5c06\u4ed6\u6551\u4e86\u8d77\u6765\uff0c\u4ee5\u540e\u7684\u4e8b\u7531 \u6211\u6599\u7406\u5427\u3002\u4ed6\u7684\u8fd9\u67c4\u5251\u6211\u4e5f\u4e00\u5e76\u62ff\u4e86\u3002\u201d\u8bf4\u7f62\u638f\u51fa\u4e00\u952d\u5143\u5b9d\u9012\u7ed9\u90a3\u8001\u6e14\u592b\uff0c\u9053\uff1a\u201c\u4e00\u70b9\u5c0f\u5c0f \u610f\u601d\uff0c\u8bf7\u4f60\u6536\u4e0b\u3002\u201d \u3000\u3000\u8001\u6e14\u592b\u6b63\u81ea\u5bb3\u6015\u6551\u4e0d\u6d3b\u8fd9\u4e2a\u4eba\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u6551\u4eba\u662f\u5e94\u8be5\u7684\uff0c\u54ea\u80fd\u8981\u4f60\u7684\u94b1\u3002\u6211\u7ed9 \u4f60\u80cc\u8fc7\u53bb\u5427\uff0c\u55ef\uff0c\u4f46\u8fd9\u67c4\u5251\u53ef\u600e\u4e48\u529e\u5462\uff1f\u963f\u5927\u963f\u4e8c\uff0c\u4f60\u4eec\u4e24\u4eba\u625b\u5b83\uff0c\u5c0f\u5fc3\u4e00\u70b9\u513f\u8df3\u8fc7\u53bb\u3002\u201d \u3000\u3000\u9ed1\u8863\u53cc\u5b50\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u8d39\u795e\u3002\u201d\u4e00\u53ea\u624b\u631f\u7740\u5389\u5357\u661f\uff0c\u4e00\u53ea\u624b\u63d0\u8d77\u7384\u94c1\u5b9d\u5251\uff0c\u201c\u55d6\u201d\u7684\u5c31 \u8df3\u56de\u4e86\u539f\u6765\u7684\u8239\u3002 \u3000\u3000\u6e14\u821f\u4e09\u7236\u5b50\u60ca\u5f97\u5446\u4e86\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u60f3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u6c49\u5b50\u8fd9\u4e48\u5927\u7684\u6c14\u529b\uff0c\u4ed6\u548c\u8fd9\u4e2a\u5c0f\u738b \u6050\u6015\u591a\u534a\u662f\u540c\u4e00\u4f19\u7684\u5f3a\u76d7\u3002\u201d \u3000\u3000\u9ed1\u8863\u6c49\u5b50\u8df3\u4e0a\u4e86\u8239\uff0c\u8239\u5934\u6643\u4e86\u4e00\u6643\u3002\u6709\u4e2a\u5c11\u5973\u65e9\u5df2\u5728\u8239\u5934\u7b49\u4ed6\uff0c\u597d\u5947\u95ee\u9053\uff1a\u201c\u8fd9\u4eba\u662f \u8c01\uff1f\u4ed6\u8fd9\u628a\u5251\u2026\u2026\u201d\u8fd9\u5c11\u5973\u662f\u4ece\u5c0f\u7ec3\u6b66\u7684\uff0c\u867d\u7136\u4e0d\u662f\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u4e5f\u770b\u5f97\u51fa\u7384\u94c1\u5b9d\u5251\u51b3\u975e \u51e1\u7269\uff0c\u8981\u4e0d\u7136\u4e0d\u4f1a\u90a3\u6837\u6c89\u91cd\u3002\u4f46\u66f4\u4eca\u5979\u5947\u602a\u7684\u662f\u5979\u7239\u7239\u5411\u6765\u4e0d\u505a\u597d\u4e8b\uff0c\u8fd9\u6b21\u5374\u4f1a\u6551\u4eba\u3002 \u3000\u3000\u9ed1\u8863\u6c49\u5b50\u9053\uff1a\u201c\u4f60\u5148\u522b\u7ba1\uff0c\u628a\u8fd9\u4eba\u642c\u8fdb\u8231\u53bb\uff0c\u5173\u597d\u7a97\u6237\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u81ea\u662f\u4e0d\u60f3\u4eba\u770b\u89c1 \u7684\u4e86\u3002\u5c11\u5973\u6ee1\u8138\u7591\u60d1\uff0c\u4f46\u5fc3\u60f3\u6551\u4eba\u8981\u7d27\uff0c\u53ea\u597d\u4f9d\u8a00\u884c\u4e8b\u3002\u8fd9\u6c49\u5b50\u5429\u5490\u5973\u513f\u5b8c\u6bd5\uff0c\u7acb\u5373\u559d\u9053\uff1a \u201c\u5f00\u8239\uff01\u201d\u81ea\u5df1\u4e5f\u62ff\u8fc7\u4e00\u652f\u6868\uff0c\u5e2e\u821f\u5b50\u5212\u3002\u821f\u5b50\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\u5e73\u65f6\u67b6\u5b50\u5f88\u5927\uff0c\u6211\u8fd8\u4ee5 \u4e3a\u4ed6\u662f\u5f53\u5b98\u7684\u5462\uff0c\u8c01\u77e5\u4ed6\u5374\u4e5f\u4f1a\u6491\u8239\u3002\u201d \u3000\u3000\u5c11\u5973\u628a\u5389\u6d77\u661f\u642c\u8fdb\u8231\u623f\uff0c\u63a2\u4e86\u63a2\u4ed6\u7684\u9f3b\u606f\uff0c\u77e5\u9053\u8fd8\u6709\u547c\u5438\uff0c\u653e\u4e0b\u4e86\u5fc3\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u4e00\u5b9a \u662f\u5728\u6c34\u91cc\u51bb\u5f97\u50f5\u4e86\uff0c\u6211\u4e14\u8ba9\u4ed6\u559d\u70b9\u9152\u6696\u548c\u6696\u548c\u3002\u201d\u5f53\u4e0b\u628a\u7236\u4eb2\u559d\u7684\u4e00\u58f6\u6842\u82b1\u9152\u53d6\u6765\uff0c\u64ac\u5f00\u7259 \u5173\uff0c\u704c\u7ed9\u5389\u5357\u661f\u559d\u4e0b\u3002\u5979\u54ea\u77e5\u5389\u5357\u661f\u8eab\u4e0a\u4e2d\u6bd2\uff0c\u9152\u4e00\u4e0b\u809a\uff0c\u6bd2\u53d1\u4f5c\u5f97\u66f4\u5feb\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5389\u5357\u661f\u4ecd\u7136\u6655\u8ff7\u4e0d\u9192\uff0c\u5c11\u5973\u4ed4\u7ec6\u4e00\u770b\uff0c\u53ea\u89c1\u4ed6\u7709\u5fc3\u9690\u9690\u6cdb\u6709\u9ed1\u6c14\u3002\u6478\u6478\u4ed6\u7684 \u989d\u5934\uff0c\u70ed\u5f97\u7099\u624b\u3002\u5c11\u5973\u5413\u5f97\u614c\u4e86\uff0c\u5fc3\u60f3\uff1a\u201c\u600e\u7684\u8fd8\u4e0d\u9192\u5462\uff1f\u4e0d\u77e5\u662f\u4e2d\u4e86\u6bd2\u8fd8\u662f\u7ed9\u70b9\u4e86\u7a74 \u9053\uff1f\u201d \u3000\u3000\u5e78\u4e8f\u5389\u5357\u661f\u66fe\u5f97\u91d1\u4e16\u9057\u6240\u6388\u5185\u529f\u5fc3\u6cd5\uff0c\u5185\u606f\u7ef5\u7ef5\u4e0d\u606f\uff0c\u867d\u5728\u660f\u8ff7\u4e4b\u4e2d\uff0c\u4e5f\u4f1a\u53d1\u6325\u6297\u6bd2\u7684 \u672c\u80fd\uff0c\u4e0d\u8fc7\u4e0d\u5982\u7740\u610f\u8fd0\u529f\u4e4b\u6709\u6548\u529b\u7f62\u4e86\u3002\u8ff7\u8ff7\u7cca\u7cca\u4e2d\uff0c\u5386\u5357\u661f\u611f\u5230\u8179\u75db\u5982\u7ede\uff0c\u4e0d\u89c9\u547b\u541f\u51fa \u58f0\uff0c\u53eb\u9053\uff1a\u201c\u9010\u6d41\uff0c\u9010\u6d41\uff01\u201d \u3000\u3000\u5c11\u5973\u559c\u9053\uff1a\u201c\u9192\u9192\uff0c\u9192\u9192\uff01\u54a6\uff0c\u4f60\u5728\u53eb\u8c01\uff1f\u201d\u5389\u5357\u661f\u53eb\u4e86\u4e24\u58f0\u201c\u9010\u6d41\u201d\uff0c\u75db\u5f97\u5389\u5bb3\uff0c\u53c8 \u6655\u8fc7\u53bb\u4e86\u3002\u201d \u3000\u3000\u5c11\u5973\u675f\u624b\u65e0\u7b56\uff0c\u53ea\u597d\u51fa\u6765\u5411\u7236\u4eb2\u8ba8\u4e3b\u610f\u3002\u6b64\u65f6\u5929\u8272\u8fd8\u672a\u5927\u4eae\uff0c\u6c5f\u9762\u9644\u8fd1\u5e76\u65e0\u522b\u7684\u8239\u53ea\uff0c \u9ed1\u8863\u6c49\u5b50\u7528\u529b\u5212\u6868\uff0c\u5c0f\u8239\u5411\u82a6\u82c7\u4e1b\u4e2d\u8361\u53bb\uff0c\u821f\u5b50\u8be7\u9053\uff1a\u201c\u8001\u7237\uff0c\u4f60\u4e0d\u662f\u8981\u8d76\u5230\u626c\u5dde\u7684\u4e48\uff1f\u4e3a \u4f55\u5728\u8fd9\u91cc\u505c\u8239\uff1f\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c\u821f\u5b50\u5df2\u7ed9\u70b9\u4e86\u7a74\u9053\uff0c\u5012\u4e0b\u53bb\u4e86\u3002\u5c11\u5973\u521a\u521a\u51fa\u6765\uff0c\u5927\u5403 \u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u7239\uff0c\u4f60\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u9ed1\u8863\u6c49\u5b50\u628a\u90a3\u821f\u5b50\u5589\u5934\u4e00\u52d2\uff0c\u4e00\u628a\u4e3e\u4e86\u8d77\u6765\uff0c\u5c31\u6254\u4e0b \u6c5f\u5fc3\uff0c\u5c11\u5973\u8d76\u53bb\u62a2\u6551\uff0c\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u5c11\u5973\u987f\u8db3\u9053\uff1a\u201c\u7239\uff0c\u4f60\u4e3a\u4f55\u6740\u4eba\uff1f\u8fd9\u821f\u5b50\u4e5f\u6ca1\u72af\u4e86\u4f60\uff01\u201d\u9ed1\u8863\u6c49\u5b50\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u7239\u5e73 \u751f\u6740\u4eba\uff0c\u6ca1\u6709\u4e00\u5343\uff0c\u4e5f\u6709\u516b\u767e\u3002\u90a3\u4e9b\u4eba\u90fd\u662f\u6ca1\u6709\u72af\u8fc7\u6211\u7684\uff0c\u4e00\u4e2a\u5c0f\u5c0f\u7684\u821f\u5b50\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4eba\u5df2\u6740\u4e86\uff0c\u5c11\u5973\u6025\u4e5f\u6ca1\u6709\u529e\u6cd5\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u7239\u7239\u53d7\u8fc7\u91d1\u9010\u6d41\u7684\u4e00\u6b21\u6559\u8bad\u4e4b\u540e\uff0c\u6211\u53ea\u9053 \u4ed6\u5f53\u771f\u662f\u75db\u4e0b\u51b3\u5fc3\u6539\u90aa\u5f52\u6b63\u7684\u4e86\uff0c\u8c01\u77e5\u4ed6\u4f9d\u7136\u662f\u6076\u6027\u4e0d\u6539\uff0c\u8bf4\u7684\u8bdd\u90fd\u662f\u9a97\u6211\u7684\uff01\u201d\u4e0d\u7981\u4f24\u5fc3 \u6b32\u7edd\uff0c\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u9ed1\u8863\u6c49\u5b50\u9053\uff1a\u201c\u50bb\u4e2b\u5934\uff0c\u6b7b\u7684\u53c8\u4e0d\u662f\u4f60\u7684\u4eb2\u7239\uff0c\u4f60\u8fd9\u4e48\u4f24\u5fc3\uff1f\u201d\u5c11\u5973\u9053\uff1a\u201c\u867d\u7136\u662f\u4e2a\u821f \u5b50\uff0c\u4e5f\u662f\u4e00\u6761\u6027\u547d\u5440\uff0c\u4f60\u4e3a\u4ec0\u4e48\u65e0\u7f18\u65e0\u6545\u6740\u4e86\u4ed6\uff1f\u201d \u3000\u3000\u9ed1\u8863\u53f9\u5b50\u9053\uff1a\u201c\u6211\u6551\u4eba\u6740\u4eba\uff0c\u90fd\u662f\u6709\u7f18\u6545\u7684\uff01\u6211\u6740\u4e86\u8fd9\u4e2a\u821f\u5b50\uff0c\u624d\u80fd\u544a\u8bc9\u4f60\u4e3a\u4ec0\u4e48\u8981\u6551 \u90a3\u5c0f\u5b50\uff01\u5bf9\u5566\uff0c\u4f60\u51fa\u6765\u505a\u4ec0\u4e48\uff0c\u90a3\u5c0f\u5b50\u9192\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5c11\u5973\u970d\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4eba\u597d\u50cf\u662f\u4e2d\u4e86\u6bd2\uff0c\u6551\u4ed6\u4e0d\u9192\uff01\u201d\u9ed1\u8863\u6c49\u5b50\u9053\uff1a\u201c\u54e6\uff0c\u5f85\u6211\u770b \u770b\u3002\u201d\u5f53\u4e0b\u628a\u8239\u6447\u8fdb\u82a6\u82c7\u6df1\u5904\uff0c\u7136\u540e\u63d0\u8d77\u7384\u94c1\u5b9d\u5251\uff0c\u548c\u5973\u513f\u8d70\u8fdb\u8231\u623f\u3002 \u3000\u3000\u9ed1\u8863\u6c49\u5b50\u4ed4\u7ec6\u7684\u5bdf\u770b\u4e86\u5389\u5357\u661f\u4e4b\u540e\uff0c\u6c89\u541f\u9053\uff1a\u201c\u4e0d\u9519\u3002\u662f\u597d\u50cf\u4e2d\u4e86\u6bd2\uff0c\u4e0d\u8fc7\u4ed6\u6b7b\u4e86\u4e5f \u7f62\uff0c\u53ea\u6c42\u8fd9\u67c4\u5251\u6ca1\u6709\u5931\u6389\u5c31\u884c\u3002\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8fd8\u6ca1\u6709\u544a\u8bc9\u6211\u6551\u4eba\u6740\u4eba\u7684\u7f18\u6545\u5462\uff01\u8fd9\u4eba\u5230\u5e95\u662f\u8c01\uff1f\u4e3a\u4ec0\u4e48\u4f60\u6551\u4e86\u4ed6\u5c31 \u8981\u6740\u90a3\u821f\u5b50\u3002\u201d \u3000\u3000\u9ed1\u8863\u6c49\u5b50\u7b11\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u6211\u4e0d\u8ba4\u8bc6\uff0c\u4ed6\u8fd9\u67c4\u7384\u94c1\u5b9d\u5251\u6211\u5374\u77e5\u9053\uff01\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4ec0\u4e48\u7384\u94c1\u5b9d\u5251\uff1f\u201d \u3000\u3000\u9ed1\u8863\u6c49\u5b50\u9053\uff1a\u201c\u53f2\u767d\u90fd\u5f97\u4e86\u4e00\u4ef6\u5b9d\u7269\uff0c\u540d\u4e3a\u7384\u94c1\uff0c\u540c\u6837\u7684\u4e00\u5757\u7384\u94c1\u8981\u6bd4\u5e73\u5e38\u7684\u94c1\u91cd\u5341 \u500d\u3002\u6211\u65e9\u5df2\u77e5\u9053\u53f2\u767d\u90fd\u8981\u628a\u7384\u94c1\u94f8\u6210\u5b9d\u5251\u732e\u7ed9\u8428\u603b\u7ba1\u7684\uff0c\u540e\u6765\u542c\u8bf4\u7ed9\u4eba\u76d7\u4e86\uff0c\u6709\u4eba\u8bf4\u662f\u4ed6\u7684 \u59b9\u59b9\u5077\u7684\uff0c\u6709\u4eba\u8bf4\u662f\u7ed9\u91d1\u9010\u6d41\u62a2\u53bb\u7684\uff0c\u771f\u60c5\u6211\u5c31\u4e0d\u77e5\u5176\u8be6\u4e86\u3002\u4e0d\u8fc7\uff0c\u54b1\u4eec\u8fd9\u6b21\u6b63\u662f\u8981\u6765\u6c42\u89c1 \u53f2\u767d\u90fd\u7684\uff0c\u7384\u94c1\u5b9d\u5251\u4e0d\u77e5\u4f55\u4ee5\u843d\u5728\u8fd9\u5c0f\u5b50\u624b\u4e2d\uff0c\u8fd9\u5c0f\u5b50\u53c8\u4e0d\u77e5\u662f\u4ec0\u4e48\u4eba\uff0c\u6211\u4e3a\u4e86\u8fd9\u67c4\u7384\u94c1\u5b9d \u5251\uff0c\u53ea\u597d\u6551\u4ed6\uff0c\u597d\u628a\u4ed6\u8fde\u4eba\u5e26\u5251\u732e\u7ed9\u53f2\u767d\u90fd\u5440\uff01\u563f\uff0c\u563f\uff0c\u6211\u6b63\u6101\u6ca1\u6709\u5b9d\u8d35\u7684\u793c\u7269\uff0c\u5982\u4eca\u8fd9\u4efd \u793c\u7269\u5230\u4e86\u53f2\u767d\u90fd\u7684\u624b\u91cc\uff0c\u53ef\u8981\u8d5b\u8fc7\u4e16\u4e0a\u4efb\u4f55\u73cd\u5b9d\u4e86\uff01\u54c8\u54c8\uff0c\u8fd9\u5f53\u771f\u662f\u5929\u8001\u7237\u6709\u5fc3\u7167\u987e\uff0c\u8d50\u7ed9 \u6211\u7684\uff01\u201d \u3000\u3000\u5c11\u5973\u9053\uff1a\u201c\u4f46\u90a3\u821f\u5b50\u5462\uff0c\u4f60\u4e3a\u4f55\u53c8\u8981\u6740\u4ed6\uff1f\u201d \u3000\u3000\u9ed1\u8863\u6c49\u5b50\u9053\uff1a\u201c\u8fd9\u67c4\u7384\u94c1\u5b9d\u5251\u662f\u6b66\u6797\u4e2d\u4eba\u68a6\u5bd0\u4ee5\u6c42\u7684\u5b9d\u8d1d\u3002\u4e0d\u80fd\u6cc4\u6f0f\u98ce\u58f0\uff0c\u8ba9\u4eba\u77e5\u9053\u662f \u5728\u6211\u7684\u624b\u4e0a\u3002\u6211\u4e0d\u6740\u8fd9\u821f\u5b50\uff0c\u7109\u80fd\u548c\u4f60\u8bb2\u8fd9\u5b9d\u5251\u7684\u6765\u5386\uff1f\u201d \u3000\u3000\u5c11\u5973\u53f9\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u5012\u662f\u6211\u7684\u8fc7\u9519\u4e86\uff01\u201d \u3000\u3000\u9ed1\u8863\u6c49\u5b50\u9053\uff1a\u201c\u4e0d\u7136\uff0c\u5c31\u662f\u53f2\u767d\u90fd\u5927\u7ea6\u4e5f\u4e0d\u613f\u610f\u8ba9\u4eba\u77e5\u9053\u91cd\u83b7\u5b9d\u5251\u3002\u8fd9\u53eb\u505a\u6740\u4eba\u706d\u53e3\uff0c \u4f60\u61c2\u4e0d\u61a7\uff1f\u54c8\u54c8\uff0c\u6211\u5e26\u4e86\u4f60\u62ff\u7384\u94c1\u5b9d\u5251\u53bb\u89c1\u53f2\u767d\u90fd\uff0c\u53f2\u767d\u90fd\u975e\u5f97\u559c\u6b22\u4f60\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5c11\u5973\u55d4\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u80e1\u8bf4\u4ec0\u4e48\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u90a3\u59d3\u53f2\u7684\u559c\u6b22\uff1f\u201d \u3000\u3000\u9ed1\u8863\u6c49\u5b50\u9053\uff1a\u201c\u5ae3\u513f\uff0c\u4f60\u8fd8\u5728\u5ff5\u5ff5\u4e0d\u5fd8\u79e6\u5143\u6d69\u8fd9\u5c0f\u5b50\u4e48\uff1f\u6211\u529d\u4f60\u6b7b\u4e86\u8fd9\u6761\u5fc3\u5427\uff0c\u4eba\u5bb6\u662f \u540d\u95e8\u6b63\u6d3e\u7684\u5f1f\u5b50\uff0c\u7109\u80fd\u8981\u4f60\uff1f\u5c31\u662f\u4ed6\u8981\u4f60\uff0c\u6211\u4e5f\u4e0d\u80fd\u8ba9\u4f60\u5ac1\u7ed9\u4ed6\uff01\u6211\u8fd9\u6b21\u548c\u4f60\u6765\u627e\u53f2\u767d\u90fd\uff0c \u4e3a\u7684\u5c31\u662f\u60f3\u4ed6\u505a\u6211\u7684\u9760\u5c71\uff0c\u514d\u5f97\u91d1\u9010\u6d41\u591a\u7ba1\u95f2\u4e8b\uff01\u54fc\uff0c\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\u5f3a\u81ea\u51fa\u5934\u8981\u505a\u5927\u5a92\uff0c\u4f60 \u4ee5\u4e3a\u6211\u5c31\u5f53\u771f\u5fc3\u7518\u60c5\u613f\u7684\u670d\u4ed6\u5417\uff1f\u201d \u3000\u3000\u539f\u672a\u8fd9\u9ed1\u8863\u6c49\u5b50\u4e43\u662f\u66fe\u7ecf\u505a\u8fc7\u5927\u5185\u536b\u58eb\u7684\u5c01\u5b50\u8d85\uff0c\u8fd9\u5c11\u5973\u662f\u4ed6\u7684\u5973\u513f\u5c01\u5999\u5ae6\uff0c\u90a3\u65e5\u5c01\u5b50 \u8d85\u4e0e\u91d1\u9010\u6d41\u964c\u8def\u76f8\u9022\uff0c\u7ed9\u91d1\u9010\u6d41\u5413\u4e86\u4ed6\u4e00\u987f\uff0c\u5413\u5f97\u4ed6\u4e0d\u6562\u5165\u4eac\u7ed9\u8428\u798f\u9f0e\u795d\u5bff\uff0c\u53ea\u597d\u56de\u5bb6\u3002\u4f46 \u56de\u5bb6\u4e4b\u540e\uff0c\u5374\u662f\u8d8a\u60f3\u8d8a\u6c14\u3002 \u3000\u3000\u56de\u5bb6\u4e0d\u4e45\uff0c\u5c01\u5b50\u8d85\u542c\u5230\u4eac\u4e2d\u4f20\u6765\u7684\u6d88\u606f\uff0c\u8bf4\u662f\u91d1\u9010\u6d41\u5927\u95f9\u8428\u5e9c\u5bff\u5802\uff0c\u7ed9\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\u53f2 \u767d\u90fd\u6253\u5f97\u5927\u8d25\u800c\u9003\u3002\u8fd9\u4e2a\u6d88\u606f\uff0c\u867d\u7136\u662f\u4e0d\u5c3d\u4e0d\u5b9e\u7684\uff0c\u4f46\u5c01\u5b50\u8d85\u5374\u4fe1\u4ee5\u4e3a\u771f\uff0c\u542c\u4e86\u8fd9\u4e2a\u6d88\u606f\uff0c \u4ed6\u6697\u81ea\u601d\u91cf\uff0c\u5f97\u4e86\u4e00\u4e2a\u4e3b\u610f\u3002\u8fd9\u4e2a\u4e3b\u610f\u662f\uff1a\u8bbe\u6cd5\u8ba9\u53f2\u767d\u90fd\u5a36\u4ed6\u7684\u5973\u513f\uff0c\u4e8b\u60c5\u82e5\u679c\u6210\u529f\uff0c\u4ed6\u5c31 \u662f\u53f2\u767d\u90fd\u7684\u6cf0\u5c71\uff0c\u4e0d\u7528\u5bb3\u6015\u91d1\u9010\u6d41\u6765\u627e\u4ed6\u7684\u9ebb\u70e6\u4e86\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u548c\u53f2\u767d\u90fd\u672c\u6765\u662f\u76f8\u8ba4\u8bc6\u7684\uff0c\u4e0d\u8fc7\u4ea4\u60c5\u4e0d\u6df1\u800c\u5df2\u3002\u4ed6\u77e5\u9053\u53f2\u767d\u90fd\u7684\u51e0\u5e74\u6b7b\u4e86\u59bb\u5b50\uff0c \u81f3\u4eca\u5c1a\u672a\u7eed\u5f26\uff0c\u524d\u59bb\u4e5f\u6ca1\u7559\u4e0b\u5b50\u5973\uff0c\u5e74\u7eaa\u867d\u7136\u6bd4\u4ed6\u5973\u513f\u5927\u4e9b\uff0c\u5bf9\u5973\u513f\u4e5f\u4e0d\u7b97\u662f\u59d4\u5c48\u4e86\u3002\u4f46\u600e \u6837\u624d\u80fd\u4f7f\u5f97\u53f2\u767d\u90fd\u5a36\u5730\u7684\u5973\u513f\u5462\uff1f \u3000\u3000\u5c01\u5b50\u8d85\u6697\u81ea\u601d\u91cf\uff1a\u201c\u6687\u513f\u957f\u5f97\u4e0d\u9519\uff0c\u9996\u5148\u5f97\u8981\u53f2\u767d\u90fd\u548c\u5979\u89c1\u9762\uff0c\u6709\u70b9\u610f\u601d\u4e4b\u540e\uff0c\u90a3\u65f6\u4e0d \u6101\u6ca1\u4eba\u51fa\u6765\u505a\u5a92\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u5c31\u54c4\u9a97\u5973\u513f\uff0c\u8bf4\u662f\u8981\u5e26\u5979\u51fa\u53bb\uff0c\u6253\u63a2\u79e6\u5143\u6d69\u7684\u4e0b\u843d\uff0c\u5c01\u5999\u5ae6\u662f\u77e5\u9053\u91d1\u9010\u6d41\u5f3a\u81ea\u51fa \u5934\u505a\u5a92\uff0c\u8981\u5979\u7236\u4eb2\u5c06\u5979\u8bb8\u7ed9\u79e6\u5143\u6d69\u4e4b\u4e8b\u7684\u3002\u867d\u7136\u5979\u89c9\u5f97\u91d1\u9010\u6d41\u6b64\u4e8b\u672a\u514d\u505a\u5f97\u5b5f\u6d6a\uff0c\u4f46\u5fc3\u5e95\u91cc \u5374\u662f\u6697\u6697\u6b22\u559c\u7684\u3002\u8fd9\u6b21\u5979\u7236\u4eb2\u548c\u5979\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5979\u4ee5\u4e3a\u5c01\u5b50\u8d85\u5f53\u771f\u662f\u4e3a\u4e86\u5bb3\u6015\u91d1\u9010\u6d41\uff0c\u4e0d\u80fd\u4e0d \u627e\u79e6\u5143\u6d69\u6765\u505a\u5973\u5a7f\uff0c\u56e0\u6b64\u4e5f\u5c31\u7f9e\u6da9\u6da9\u7684\u7b54\u5e94\u4e86\u3002\u5979\u5e76\u4e0d\u662f\u4e00\u4e2a\u5e38\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u7684\u4eba\uff0c\u6839\u672c\u5c31 \u4e0d\u77e5\u9053\u53f2\u767d\u90fd\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u6b64\u6b21\u201c\u8def\u8fc7\u201d\u626c\u5dde\uff0c\u7236\u4eb2\u8bf4\u53f2\u767d\u90fd\u4ea4\u6e38\u5e7f\u9614\uff0c\u5e94\u8be5\u53bb\u62dc\u8bbf\u4ed6\uff0c\u987a \u4fbf\u6253\u542c\u79e6\u5143\u6d69\u7684\u6d88\u606f\uff0c\u5c01\u5999\u5ae6\u4e5f\u5c31\u65e0\u53ef\u65e0\u4e0d\u53ef\u5730\u7b54\u5e94\u4e0b\u6765\u3002\u5979\u54ea\u91cc\u77e5\u9053\u8fd9\u6b21\u5e76\u975e\u5076\u7136\u201c\u8def \u8fc7\u201d\uff0c\u800c\u662f\u5c01\u5b50\u8d85\u65e9\u5c31\u6253\u8d70\u4e86\u4e3b\u610f\u7684\u201c\u5b89\u6392\u201d\u3002 \u3000\u3000\u60f3\u4e0d\u5230\u672a\u5230\u626c\u5dde\uff0c\u9047\u4e0a\u4e86\u5389\u5357\u661f\u906d\u96be\u800c\u53c8\u88ab\u6551\u4e4b\u4e8b\uff0c\u5c01\u5b50\u8d85\u9a97\u4e86\u90a3\u5bb6\u6e14\u4eba\uff0c\u628a\u5389\u5357\u661f\u63a5 \u5230\u4ed6\u7684\u8239\u4e0a\uff0c\u7384\u94c1\u5b9d\u5251\u843d\u5230\u4e86\u4ed6\u7684\u624b\u4e2d\u3002\u5c01\u5b50\u8d85\u8ba4\u4e3a\u8fd9\u662f\u5929\u8d50\u7684\u793c\u7269\uff0c\u559c\u4e0a\u52a0\u559c\u3002 \u3000\u3000\u4e14\u8bf4\u5c01\u5999\u5ae6\u542c\u4e86\u5979\u7236\u4eb2\u7684\u8bf4\u8bdd\uff0c\u8a00\u8bed\u4e4b\u4e2d\u5df2\u662f\u9690\u9690\u900f\u9732\u4e86\u4e00\u70b9\u53e3\u98ce\uff1a\u662f\u8981\u62ff\u5979\u53bb\u5df4\u7ed3\u53f2 \u767d\u90fd\u7684\u3002\u5c01\u5999\u5ae6\u4e0d\u89c9\u60f4\u60f4\u4e0d\u5b89\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u7239\u7239\u6076\u6027\u4e0d\u6539\uff0c\u6211\u6709\u4ec0\u4e48\u529e\u6cd5\u8eb2\u8fc7\u8fd9\u573a\u707e\u96be \u5462\uff1f\u5509\uff0c\u4ee5\u524d\u4e24\u6b21\u662f\u91d1\u9010\u6d41\u89e3\u6551\u4e86\u6211\u7684\u707e\u96be\uff0c\u4f46\u5982\u4eca\u5374\u53c8\u600e\u80fd\u627e\u5f97\u4e00\u4e2a\u91d1\u9010\u6d41\u51fa\u6765\uff1f\u201d \u3000\u3000\u60f3\u8d77\u4e86\u91d1\u9010\u6d41\uff0c\u5c01\u5999\u8d4c\u84e6\u5730\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u8fd9\u4e2a\u4eba\u660f\u8ff7\u4e0d\u9192\uff0c\u600e\u529e\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u60f3\u7559\u4e2a\u6d3b\u53e3\uff0c\u8ba9\u53f2\u767d\u90fd\u53bb\u5ba1\u95ee\u4ed6\u7684\uff0c\u5982\u4eca\u770b\u6765\u4ed6\u662f \u6551\u4e0d\u6d3b\u4e86\uff0c\u6ca1\u529e\u6cd5\u53ea\u597d\u8ba9\u4ed6\u65e9\u8131\u82e6\u6d77\u4e86\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u5ffd\u5730\u62e6\u5728\u4e8c\u4eba\u4e4b\u95f4\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u8fd9\u4e2a\u4eba\u4f60\u4e0d\u80fd\u6740\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u77aa\u773c\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u96be\u9053\u4f60\u77e5\u9053\u4e48\u3002\u201d \u3000\u3000\u6070\u597d\u6b64\u65f6\uff0c\u5389\u5357\u661f\u53c8\u7ffb\u4e86\u4e2a\u8eab\uff0c\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u4f60\u8ba9\u6211\u8bd5\u8bd5\uff0c\u6211\u8ba9\u4ed6\u81ea\u5df1\u8bf4\u51fa\u6765\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u8fd8\u5728\u660f\u8ff7\u4e0d\u9192\uff0c\u773c\u89c1\u662f\u6b7b\u7684\u591a\u6d3b\u7684\u5c11\u4e86\uff0c\u4f60\u600e\u80fd\u8ba9\u4ed6\u81ea\u5df1\u8bf4\u51fa \u6765\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u4fef\u4e0b\u67f3\u8170\uff0c\u5728\u5389\u5357\u661f\u8033\u8fb9\u53eb\u9053\uff1a\u201c\u91d1\u9010\u6d41\u6765\u4e86\uff0c\u91d1\u9010\u6d41\u6765\u4e86\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u5728\u8ff7\u8ff7\u7cca\u7cca\u4e4b\u4e2d\u542c\u5f97\u8fd9\u4e48\u4e00\u53eb\uff0c\u84e6\u5730\u9192\u6765\uff0c\u53eb\u9053\uff1a\u201c\u5728\u54ea\u513f\uff0c\u5728\u54ea\u513f\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u9010\u6d41\u662f\u4f60\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u7741\u5f00\u4e86\u773c\u775b\uff0c\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u6211\u600e\u4e48\u4f1a\u5728\u8fd9\u513f\uff1f\u8fd9\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u4f60\u4eec\u662f\u4ec0\u4e48\u4eba\uff1f\u91d1 \u9010\u6d41\u5462\uff1f\u201d \u3000\u3000\u539f\u6765\u4ed6\u56e0\u4e3a\u7ec3\u7684\u662f\u6b63\u5b97\u7684\u5185\u529f\u5fc3\u6cd5\uff0c\u867d\u5728\u660f\u8ff7\u4e4b\u4e2d\uff0c\u5185\u606f\u4ecd\u662f\u7ef5\u7ef5\u4e0d\u7edd\uff0c\u90a3\u7897\u9152\u7684\u9152\u529b \u5df2\u8fc7\uff0c\u4ed6\u6240\u7ec3\u7684\u5185\u529f\u53c8\u81ea\u81ea\u7136\u7136\u7684\u53d1\u6325\u4e86\u6297\u6bd2\u7684\u529f\u80fd\u3002\u672c\u6765\u4ed6\u8fd8\u8981\u8fdf\u4e00\u4e9b\u65f6\u5019\u624d\u80fd\u9192\u8fc7\u6765 \u7684\uff0c\u53ea\u56e0\u5fc3\u4e2d\u5ff5\u5ff5\u4e0d\u5fd8\u91d1\u9010\u6d41\uff0c\u5c01\u5999\u5ae6\u5728\u4ed6\u8033\u8fb9\u8fd9\u4e48\u4e00\u53eb\uff0c\u4ed6\u597d\u4f3c\u662f\u5728\u68a6\u4e2d\u542c\u5f97\u4eb2\u4eba\u7684\u540d \u5b57\uff0c\u672c\u80fd\u7684\u5c31\u9192\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6211\u662f\u91d1\u9010\u6d41\u7684\u670b\u53cb\uff0c\u4f60\u544a\u8bc9\u6211\u91d1\u9010\u6d41\u73b0\u5728\u54ea\u513f\uff0c\u6211\u7ed9\u4f60\u628a\u4ed6\u8bf7\u6765\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u71c3\u8d77\u4e86\u5e0c\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u548c\u91d1\u9010\u6d41\u662f\u7ed3\u62dc\u5144\u5f1f\uff0c\u4ed6\u73b0\u5728\u626c\u5dde\uff0c\u4f60\u627e\u7740\u4e10\u5e2e\u7684\u8235\u4e3b \u674e\u8302\uff0c\u5c31\u53ef\u4ee5\u77e5\u4f1a\u91d1\u9010\u6d41\u4e86\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u4e00\u5b9a\u66ff\u4f60\u8bbe\u6cd5\u901a\u77e5\u91d1\u9010\u6d41\u3002\u4f60\u6b47\u4e00\u4f1a\u513f\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u5173\u4e0a\u623f\u95e8\uff0c\u628a\u7236\u4eb2\u62c9\u5230\u524d\u8231\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u73b0\u5728\u77e5\u9053\u4e86\u4ed6\u662f\u4ec0\u4e48\u4eba\u4e86\u5427\uff0c\u600e\u4e48 \u8fd8\u80fd\u6740\u4ed6\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u4ed6\u662f\u91d1\u9010\u6d41\u7684\u5144\u5f1f\uff0c\u6211\u66f4\u662f\u975e\u6740\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u4f60\u60f9\u5f97\u8d77\u91d1\u9010\u6d41\uff1f\u4f60\u60f3\u60f3\uff0c\u522b\u7684\u4e8b\u60c5\u4f60\u4e0d\u4f9d\u4ece\u91d1\u9010\u6d41\u7684\u5429\u5490\uff0c\u90a3\u8fd8\u597d\u4e9b\uff0c \u9876\u591a\u4ed6\u662f\u627e\u4f60\u9ebb\u70e6\uff1b\u4f46\u4f60\u82e5\u6740\u4e86\u4ed6\u7684\u4e49\u5144\uff0c\u4f60\u9003\u5230\u5929\u6daf\u6d77\u89d2\uff0c\u53ea\u6015\u4e5f\u8eb2\u4e0d\u8fc7\u4ed6\uff01\u4ed6\u4e0d\u8981\u4e86\u4f60 \u7684\u6027\u547d\u624d\u602a\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u60f3\u8d77\u91d1\u9010\u6d41\u90a3\u4e00\u8eab\u795e\u51fa\u9b3c\u6ca1\u7684\u8f7b\u529f\uff0c\u4e5f\u4e0d\u7981\u6709\u70b9\u6bdb\u9aa8\u609a\u7136\uff0c\u4f46\u5374\u786c\u7740\u5934\u76ae\u8bf4\u9053\uff1a \u201c\u6211\u6740\u4e86\u4ed6\uff0c\u8c01\u4eba\u77e5\u9053\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u91d1\u9010\u6d41\u4e00\u5b9a\u4f1a\u8bbf\u67e5\u4e49\u5144\u7684\u4e0b\u843d\u7684\uff0c\u90a3\u4e00\u5bb6\u6e14\u4eba\u4e0d\u4f1a\u8bf4\u51fa\u53bb\u5417\uff1f\u53ea\u8981\u4ed6\u4eec\u63d0 \u53ca\u90a3\u67c4\u91cd\u5f97\u51fa\u5947\u7684\u5b9d\u5251\uff0c\u91d1\u9010\u6d41\u8fd8\u80fd\u4e0d\u8ffd\u67e5\u5230\u4f60\u8eab\u4e0a\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u6068\u6068\u8bf4\u9053\uff0c\u201c\u6094\u4e0d\u8be5\u521a\u624d\u4e0d\u6740\u4e86\u90a3\u5bb6\u6e14\u4eba\uff01\u201d\u5176\u5b9e\u4ed6\u662f\u52a8\u5ff5\u5934\u8981\u6740\u90a3\u5bb6\u6e14\u4eba\uff0c\u53ea \u56e0\u9644\u8fd1\u8fd8\u6709\u51e0\u8258\u5546\u822c\uff0c\u4ed6\u6015\u7ed9\u4eba\u770b\u89c1\uff0c\u8fd9\u624d\u4e0d\u6562\u6740\u7684\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u53c8\u9053\uff1a\u3002\u201d\u800c\u4e14\u5373\u4f7f\u4f60\u6740\u4e86\u90a3\u5bb6\u6e14\u4eba\uff0c\u4e5f\u6ca1\u6709\u7528\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u56e0\u4e3a\u8fd8\u662f\u6709\u4eba\u77e5\u9053\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u8c01\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6211\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u4f60\uff1f\u4f60\u8981\u548c\u6211\u4f5c\u5bf9\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u5b69\u513f\u4e0d\u6562\u3002\u4f46\u5b69\u513f\u6b63\u662f\u4e3a\u4e86\u7239\u7239\u7740\u60f3\uff0c\u975e\u5f97\u529d\u963b\u7239\u7239\u4e0d\u53ef\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u5012\u662f\u4e2a\u5b5d\u987a\u7684\u5973\u513f\u4e86\uff01\u6211\u770b\u4f60\u662f\u4e3a\u4e86\u79e6\u5143\u6d69\u8fd9\u5c0f \u5b50\u5427\uff1f\u91d1\u9010\u6d41\u662f\u79e6\u5143\u6d69\u7684\u670b\u53cb\uff0c\u8fd9\u4eba\u662f\u91d1\u9010\u6d41\u7684\u4e49\u5144\uff0c\u56e0\u6b64\u4f60\u5c31\u4e0d\u60dc\u80f3\u818a\u5411\u5916\u5f2f\uff0c\u5411\u7740\u5916\u4eba \u4e86\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u626f\u5230\u54ea\u513f\u53bb\u4e86\u3002\u6211\u8bf4\u7684\u662f\u6b63\u7406\u3002\u4f60\u7b54\u5e94\u8fc7\u6211\u6539\u90aa\u5f52 \u6b63\u7684\uff0c\u521a\u624d\u4f60\u6740\u90a3\u821f\u5b50\u5df2\u662f\u4e0d\u8be5\uff0c\u5982\u4f55\u8fd8\u80fd\u518d\u6740\u6b64\u4eba\uff1f\u83ab\u8bf4\u4ed6\u662f\u91d1\u9010\u6d41\u7684\u4e49\u5144\uff0c\u4f60\u6740\u4e86\u4ed6\u5fc5 \u6709\u540e\u60a3\uff1b\u5373\u4f7f\u4e0d\u662f\uff0c\u4f60\u4e5f\u4e0d\u80fd\u4f5c\u6b64\u4f24\u5929\u5bb3\u7406\u7684\u4e8b\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u51b7\u7b11\u9053\uff1a\u201c\u53cd\u6b63\u5728\u4f60\u7684\u773c\u4e2d\u6211\u5df2\u662f\u4f5c\u6076\u591a\u7aef\u7684\u4e86\uff0c\u4e0d\u5dee\u6211\u591a\u6740\u4e00\u4eba\uff0c\u8981\u662f\u6211\u504f\u504f \u4e0d\u542c\u4f60\u7684\u529d\u963b\uff0c\u5b9a\u8981\u6740\u4ed6\uff0c\u4f60\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u9664\u975e\u4f60\u628a\u6211\u4e5f\u6740\u4e86\uff0c\u5426\u5219\u6211\u7684\u53e3\u4f60\u5c01\u4e0d\u4f4f\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u65e0\u53ef\u5948\u4f55\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e0d\u6740\u4ed6\uff0c\u53cd\u6b63\u6b64\u5730\u5df2\u79bb\u626c\u5dde\u4e0d\u8fdc\uff0c\u6211\u628a\u4ed6\u9001\u5230\u626c\u5dde\uff0c \u8ba9\u53f2\u767d\u90fd\u53d1\u843d\u3002\u6211\u60f9\u4e0d\u8d77\u91d1\u9010\u6d41\uff0c\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\u53f2\u767d\u90fd\u603b\u60f9\u5f97\u8d77\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8001\u5b9e\u544a\u8bc9\u6211\uff0c\u4f60\u8981\u6211\u5230\u626c\u5dde\u89c1\u53f2\u767d\u90fd\uff0c\u5230\u5e95\u662f\u4e3a\u4e86\u4ec0\u4e48\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u6211\u4e0d\u662f\u65e9\u5df2\u544a\u8bc9\u4e86\u4f60\u5417\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u4e0d\uff0c\u521a\u624d\u4f60\u5df2\u9732\u51fa\u53e3\u98ce\uff0c\u6b64\u53bb\u626c\u5dde\uff0c\u5e76\u975e\u662f\u4e3a\u4e86\u627e\u53f2\u767d\u90fd\u6253\u542c\u79e6\u5143\u6d69\u7684\u6d88 \u606f\u7684\u4e86\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u7239\u7239\u7684\u4e8b\u4e0d\u7528\u4f60\u591a\u7ba1\u3002\u53f2\u767d\u90fd\u662f\u4f4d\u5927\u82f1\u96c4\uff0c\u522b\u4eba\u60f3\u89c1\u4ed6\u8fd8\u4e0d\u5bb9\u6613\u5462\uff01\u201d\u5c01 \u5b50\u8d85\u77e5\u9053\u82e5\u7136\u628a\u8bdd\u660e\u8bf4\uff0c\u5973\u513f\u5b9a\u4e0d\u4f9d\u4ece\uff0c\u4e0d\u5982\u5230\u4e86\u626c\u5dde\uff0c\u8ba9\u5979\u4e0a\u4e86\u5708\u5957\u518d\u8bf4\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u5df2\u7ecf\u60f3\u597d\u4e3b\u610f\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f9d\u5b69\u5b50\u4e4b\u89c1\uff0c\u7239\u7239\u8fd8\u662f\u4e0d\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u91d1\u9010\u6d41\u6b63\u5728\u626c\u5dde\uff0c\u4f60\u6b64\u53bb\u6070\u597d\u662f\u9001\u4e0a\u95e8\u6765\u7ed9\u4ed6\u63ea\u4f60\u7b97\u5e10\u3002\u4f60\u8bf4\u53f2\u767d\u90fd\u60f9\u5f97 \u8d77\u91d1\u9010\u6d41\uff0c\u8fd9\u53ea\u662f\u4f60\u7684\u5047\u8bbe\u800c\u5df2\uff0c\u4f60\u53c8\u7109\u77e5\u4e0d\u662f\u91d1\u9010\u6d41\u6253\u8d25\u4e86\u53f2\u767d\u90fd\uff1f\u8bf4\u4e0d\u5b9a\u6b64\u9645\u8fde\u53f2\u767d\u90fd \u4e5f\u8981\u8eb2\u907f\u91d1\u9010\u6d41\u5462\uff1f\u90a3\u4e48\u4f60\u53c8\u8bf7\u8c01\u4fdd\u62a4\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u662f\u7ed9\u91d1\u9010\u6d41\u5413\u6015\u4e86\u7684\uff0c\u5176\u5b9e\u4ed6\u521a\u624d\u542c\u8bf4\u91d1\u9010\u6d41\u5728\u626c\u5dde\u4e4b\u65f6\uff0c\u5fc3\u4e2d\u5df2\u662f\u98a4\u7c9f\u4e0d\u5b89\u7684 \u4e86\u3002\u4f46\u6743\u8861\u5229\u5bb3\uff0c\u53c8\u4e0d\u80af\u820d\u6389\u8fd9\u4e2a\u53ef\u4ee5\u5df4\u7ed3\u53f2\u767d\u90fd\u7684\u5927\u597d\u673a\u4f1a\u3002\u5f53\u4e0b\u81ea\u5df1\u7ed9\u81ea\u5df1\u58ee\u80c6\uff0c\u786c\u7740 \u5934\u76ae\u8bf4\u9053\uff1a\u201c\u8fd9\u51b3\u4e0d\u81f3\u4e8e\uff0c\u51b3\u4e0d\u81f3\u4e8e\uff01\u53f2\u767d\u90fd\u662f\u5f53\u4eca\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\uff0c\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\u672c\u9886\u518d \u5f3a\uff0c\u4e5f\u52a8\u4e0d\u4e86\u592a\u5c81\u5934\u4e0a\u7684\u571f\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u89c1\u4e00\u6761\u8239\u98ce\u5e06\u75be\u9a76\uff0c\u987a\u6d41\u800c\u4e0b\uff0c\u8239\u5934\u6302\u6709\u4e00\u9762\u9ed1\u65d7\uff0c\u65d7\u4e0a\u7ed8\u6709\u516d\u4e2a\u9ab7\u9ac5\uff0c \u8fd9\u6b63\u662f\u516d\u5408\u5e2e\u7684\u6807\u5fd7\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u559c\u9053\uff1a\u201c\u516d\u5408\u5e2e\u7684\u8239\u6765\u4e86\u3002\u201d\u628a\u8239\u6447\u51fa\u82a6\u82c7\uff0c\u516d\u5408\u5e2e\u90a3\u6761\u8239\u5df2\u7ecf\u6765\u5230\u3002\u7ad9\u5728\u8239\u5934 \u7684\u90a3\u4e2a\u5c0f\u5934\u76ee\uff0c\u6070\u5de7\u662f\u5c01\u5b50\u8d85\u76f8\u8bc6\u7684\u3002\u6b63\u662f\uff1a \u3000\u3000\u9f99\u6e38\u6d45\u6c34\u906d\u867e\u620f\uff0c\u864e\u843d\u5e73\u9633\u88ab\u72ac\u6b3a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e09\u5341\u56de\u3000\u8986\u96e8\u7ffb\u4e91\u65bd\u6bd2\u624b\u3000\u5149\u98ce\u9701\u6708\u89c1\u4ec1\u5fc3 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e09\u5341\u56de\u3000\u8986\u96e8\u7ffb\u4e91\u65bd\u6bd2\u624b\u3000\u5149\u98ce\u9701\u6708\u89c1\u4ec1\u5fc3 \u3000\u3000\u5c01\u5b50\u8d85\u78b0\u89c1\u516d\u5408\u5e2e\u7684\u8239\uff0c\u559c\u51fa\u671b\u5916\u3002\u8fd9\u5c0f\u5934\u76ee\u770b\u89c1\u4e86\u4ed6\uff0c\u5374\u662f\u5fc3\u4e2d\u6697\u6697\u5600\u5495\uff1a\u201c\u771f\u4e2a\u6666 \u6c14\uff0c\u597d\u4e0d\u5bb9\u6613\u907f\u5f00\u4e86\u540c\u4f34\uff0c\u504f\u504f\u53c8\u78b0\u4e0a\u4e86\u719f\u4eba\u3002\u8fd9\u4e2a\u5bb6\u4f19\u6050\u6015\u6b63\u662f\u8981\u6765\u626c\u5dde\u5df4\u7ed3\u5e2e\u4e3b\u7684\u3002\u201d \u4e3a\u4f55\u4ed6\u6015\u78b0\u89c1\u719f\u4eba\uff1f\u539f\u6765\u4ed6\u6b64\u65f6\u6b63\u5728\u4f01\u56fe\u53db\u5e2e\u9003\u8d70\u3002 \u3000\u3000\u8fd9\u5c0f\u5934\u76ee\u540d\u53eb\u738b\u5409\uff0c\u5f53\u674e\u6566\u8fd8\u5728\u516d\u5408\u5e2e\u7684\u65f6\u5019\uff0c\u4ed6\u548c\u674e\u6566\u662f\u76f8\u5f53\u8981\u597d\u7684\u670b\u53cb\uff0c\u53d7\u4e86\u674e\u6566 \u7684\u5f71\u54cd\uff0c\u65e9\u5df2\u6709\u4e86\u6539\u90aa\u5f52\u6b63\u7684\u5fc3\u610f\u3002\u8fd9\u4e24\u5e74\u6765\uff0c\u5e2e\u4e3b\u53f2\u767d\u90fd\u5012\u884c\u9006\u65bd\uff0c\u867d\u8fd8\u672a\u5230\u4f17\u53db\u4eb2\u79bb\u7684 \u5730\u6b65\uff0c\u5e2e\u4e2d\u4e0a\u4e0b\u5bf9\u4ed6\u4e0d\u6ee1\u7684\u5df2\u662f\u65e5\u76ca\u589e\u591a\uff0c\u738b\u5409\u7531\u4e8e\u65e9\u6709\u89c9\u609f\uff0c\u66f4\u662f\u4e0d\u9f7f\u53f2\u767d\u90fd\u7684\u6240\u4e3a\uff0c\u6025 \u60f3\u6446\u8131\u53f2\u767d\u90fd\u7684\u63a7\u5236\uff0c\u53ea\u662f\u82e6\u4e8e\u6ca1\u6709\u673a\u4f1a\u800c\u5df2\u3002 \u3000\u3000\u8fd9\u6b21\u53f2\u767d\u90fd\u56e0\u4e3a\u60f3\u8981\u5f97\u56de\u7384\u94c1\u5b9d\u5251\uff0c\u6d3e\u51fa\u4e86\u5341\u51e0\u6761\u8239\uff0c\u6cbf\u6c5f\u800c\u4e0b\uff0c\u5e0c\u671b\u80fd\u591f\u53d1\u73b0\u5389\u5357\u661f \u7684\u5c38\u4f53\uff0c\u627e\u5230\u6c89\u5728\u6c34\u5e95\u7684\u5b9d\u5251\u3002\u626c\u5dde\u4f4d\u4e8e\u957f\u6c5f\u5317\u5cb8\uff0c\u6b63\u5f53\u957f\u6c5f\u548c\u8fd0\u6cb3\u7684\u4ea4\u53c9\u70b9\uff0c\u6c34\u9053\u7eb5\u6a2a\uff0c \u6e2f\u6ca1\u4ea4\u9519\uff0c\u5927\u8239\u4e0d\u6613\u641c\u7d22\uff0c\u662f\u4ee5\u6d3e\u51fa\u7684\u90fd\u662f\u4e00\u4eba\u638c\u7ba1\u7684\u5feb\u8247\u3002\u5949\u6d3e\u7684\u4eba\u5f53\u7136\u4e5f\u90fd\u662f\u5584\u4e8e\u9a7e \u8239\u3001\u53c8\u4f1a\u6f5c\u6c34\u7684\u597d\u624b\u3002\u738b\u5409\u5c31\u662f\u5176\u4e2d\u4e4b\u4e00\u3002 \u3000\u3000\u738b\u5409\u672c\u6765\u662f\u548c\u53e6\u5916\u4e00\u6761\u8239\u4e00\u540c\u51fa\u53d1\u7684\uff0c\u4ed6\u5229\u7528\u6cb3\u9053\u7684\u590d\u6742\u5730\u5f62\uff0c\u4e2d\u9014\u6446\u8131\u4e86\u540c\u4f34\u7684\u76d1 \u89c6\uff0c\u72ec\u81ea\u4e00\u6761\u8239\u987a\u6d41\u800c\u4e0b\uff0c\u6b64\u65f6\u5df2\u662f\u79bb\u5f00\u4e86\u626c\u5dde\u516d\u4e03\u5341\u4f59\u91cc\uff0c\u8d70\u51fa\u4e86\u516d\u5408\u5e2e\u52bf\u529b\u7b3c\u7f69\u7684\u6c34\u57df \u4e86\u3002\u53ea\u8981\u518d\u8d70\u4e94\u5341\u91cc\u6c34\u8def\uff0c\u5c31\u53ef\u4ee5\u4ece\u957f\u6c5f\u53e3\u51fa\u6d77\uff0c\u90a3\u65f6\u6d77\u9614\u5929\u7a7a\uff0c\u81ea\u662f\u9003\u751f\u6709\u8def\u3002 \u3000\u3000\u4e0d\u6599\u5c31\u5728\u8fd9\u4e2a\u65f6\u5019\u78b0\u89c1\u4e86\u5c01\u5b50\u8d85\uff0c\u738b\u5409\u60f3\u8981\u8eb2\u56de\u8231\u4e2d\uff0c\u53ea\u542c\u5f97\u5c01\u5b50\u8d85\u5df2\u7ecf\u53eb\u9053\uff1a\u201c\u8001 \u738b\uff0c\u8fd8\u8ba4\u5f97\u6211\u5417\uff1f\u6211\u662f\u5c01\u5b50\u8d85\u5440\uff01\u201d \u3000\u3000\u738b\u5409\u7709\u5934\u4e00\u76b1\uff0c\u5f97\u4e86\u4e00\u4e2a\u4e3b\u610f\uff0c\u628a\u8239\u6447\u4e86\u4e0a\u53bb\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u5c01\u5927\u4eba\uff0c\u4ec0\u4e48\u98ce\u628a \u4f60\u5439\u6765\u7684\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u4e0d\u613f\u548c\u516d\u5408\u5e2e\u7684\u4eba\u4f1a\u9762\uff0c\u6e9c\u56de\u8231\u623f\uff0c\u770b\u62a4\u5389\u5357\u661f\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u6211\u662f\u7279\u5730\u6765\u62dc\u4f1a\u4f60\u4eec\u5e2e\u4e3b\u7684\u3002\u53f2\u5e2e\u4e3b\u53ef\u597d\u4e86\u96be\u5f97\u76f8\u9047\uff0c\u8bf7\u8fc7\u6765\u53d9\u53d9\u5982 \u4f55\uff1f\u201d \u3000\u3000\u738b\u5409\u9053\uff1a\u201c\u6211\u8fd8\u8981\u8d76\u7740\u8fc7\u6c5f\u5317\u66ff\u5e2e\u4e3b\u529e\u4e8b\u5462\uff0c\u5c31\u8fd9\u513f\u7a0d\u8c08\u4e00\u4f1a\u513f\u5427\u3002\u5509\uff0c\u5c01\u5927\u4eba\uff0c\u4f60\u6765 \u5f97\u6b63\u597d\uff0c\u6211\u4eec\u7684\u5e2e\u4e3b\u53ef\u662f\u4e0d\u5927\u597d\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u51fa\u4e86\u4ec0\u4e48\u4e8b\u60c5\u4e86\uff1f\u201d \u3000\u3000\u738b\u5409\u9053\uff1a\u201c\u8bf4\u6765\u60ed\u6127\uff0c\u5c31\u5728\u6628\u5929\u665a\u4e0a\uff0c\u6211\u4eec\u7684\u516d\u5408\u5e2e\u603b\u8235\u7ed9\u654c\u4eba\u95f9\u5f97\u4e2a\u5929\u7ffb\u5730\u8986\uff0c\u6211\u4eec \u7684\u5e2e\u4e3b\u4e5f\u53d7\u4e86\u4f24\u4e86\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u5927\u60ca\u9053\uff1a\u201c\u8d35\u5e2e\u96c4\u9738\u6c5f\u6e56\uff0c\u4ec0\u4e48\u4eba\u8fd9\u6837\u5927\u80c6\u548c\u4f60\u4eec\u4f5c\u5bf9\uff1f\u201d \u3000\u3000\u738b\u5409\u9053\uff1a\u201c\u548c\u6211\u4eec\u4f5c\u5bf9\u7684\u662f\u4e10\u5e2e\uff0c\u4ed6\u4eec\u8bf4\u6211\u4eec\u6295\u9760\u671d\u5ef7\uff0c\u52fe\u7ed3\u5b98\u5ead\uff0c\u8a93\u8981\u628a\u6211\u4eec\u94f2 \u9664\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u4e10\u5e2e\u5728\u626c\u5dde\u7684\u8235\u4e3b\u662f\u674e\u8302\u5427\uff1f\u4ed6\u7684\u672c\u9886\u548c\u4f60\u4eec\u7684\u5e2e\u4e3b\u76f8\u5dee\u5f97\u5f88\u8fdc\u5440\uff0c\u96be\u9053 \u662f\u4ef2\u957f\u7edf\u8fd9\u8001\u53eb\u5316\u6765\u4e86\uff1f\u201d\u738b\u5409\u6447\u4e86\u6447\u5934\u3002\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u90a3\u4e48\u5374\u662f\u8c01\u4eba\u6709\u6b64\u672c\u9886\uff0c\u80fd\u591f\u4f24\u5f97 \u4e86\u4f60\u4eec\u7684\u5e2e\u4e3b\uff1f\u201d \u3000\u3000\u738b\u5409\u9053\uff1a\u201c\u8001\u53eb\u5316\u6ca1\u6709\u6765\uff0c\u662f\u53e6\u4e00\u4e2a\u5c0f\u53eb\u5316\u6765\u4e86\u3002\u8fd9\u4e2a\u5c0f\u53eb\u5316\u7684\u672c\u9886\u53ef\u5389\u5bb3\u5462\uff0c\u6211\u4eec\u5e2e \u4e2d\u7684\u56db\u5927\u9999\u4e3b\u90fd\u66fe\u5403\u4e86\u4ed6\u7684\u4e8f\uff0c\u6628\u665a\u8fde\u6211\u4eec\u7684\u5e2e\u4e3b\u4e5f\u53d7\u4e86\u4ed6\u7684\u4f24\u4e86\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u542c\u4e86\u738b\u5409\u7684\u8bdd\uff0c\u4ed6\u5df2\u7ecf\u60f3\u5f97\u5230\u8fd9\u4eba\u662f\u8c01\u4e86\uff0c\u4f46\u8fd8\u662f\u95ee\u9053\uff1a\u201c\u4f60 \u8bf4\u7684\u662f\u8c01\uff1f\u4e10\u5e2e\u54ea\u6709\u8fd9\u6837\u7684\u4eba\u7269\uff1f\u201d \u3000\u3000\u738b\u5409\u9053\uff1a\u201c\u8fd9\u4eba\u5e76\u975e\u4e10\u5e2e\u5f1f\u5b50\uff0c\u4f46\u5728\u6c5f\u6e56\u884c\u8d70\uff0c\u5374\u559c\u6b22\u6253\u626e\u6210\u5c0f\u53eb\u5316\u7684\u6a21\u4f6f\u3002\u5c01\u5927\u4eba\uff0c \u542c\u8bf4\u4f60\u66fe\u7ecf\u548c\u6587\u9053\u5e84\u6587\u5927\u4eba\u5230\u8fc7\u6c5f\u6d77\u5929\u7684\u5bb6\u91cc\uff0c\u6587\u5927\u4eba\u8fd8\u66fe\u7ecf\u8d25\u5728\u8fd9\u5c0f\u53eb\u5316\u4e4b\u624b\u7684\uff0c\u4f60\u5e94\u8be5 \u77e5\u9053\u662f\u8c01\u4e86\u5427\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u5931\u58f0\u53eb\u9053\uff1a\u201c\u662f\u91d1\u9010\u6d41\u4e48\uff1f\u201d\u53f2\u5e2e\u4e3b\u600e\u80fd\u8d25\u5728\u4ed6\u7684\u624b\u4e0b\uff0c\u771f\u662f\u4ee4\u4eba\u4e0d\u6562\u76f8\u4fe1\uff01\u201d \u5176\u5b9e\u4ed6\u5df2\u662f\u6df1\u4fe1\u65e0\u7591\u3002 \u3000\u3000\u738b\u5409\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u91d1\u4e16\u9057\u7684\u513f\u5b50\u3001\u6c5f\u6d77\u5929\u7684\u5e08\u5f1f\u91d1\u9010\u6d41\uff01\u4f60\u83ab\u770b\u8f7b\u4e86\u4ed6\uff0c\u4ed6\u867d\u7136\u5e74 \u7eaa\u8f7b\u8f7b\uff0c\u5185\u529f\u7684\u6df1\u539a\uff0c\u5374\u662f\u5230\u4e86\u767b\u5cf0\u9020\u6781\u7684\u5730\u6b65\uff0c\u6211\u4eec\u7684\u5e2e\u4e3b\u548c\u4ed6\u62fc\u4e86\u4e00\u638c\uff0c\u5f53\u573a\u6ca1\u4e8b\uff0c\u4f46 \u4e00\u56de\u5230\u5bb6\u91cc\uff0c\u5c31\u5410\u4e86\u4e00\u5927\u6d77\u7897\u7684\u9c9c\u8840\uff0c\u539f\u6765\u4ed6\u662f\u4e0d\u613f\u5f53\u573a\u4e22\u8138\uff0c\u5f3a\u81ea\u5fcd\u4f4f\u7684\u3002\u770b\u4ed6\u7684\u4f24\u52bf\uff0c \u6050\u6015\u5c31\u662f\u533b\u5f97\u597d\u81f3\u5c11\u4e5f\u8981\u4e00\u5e74\uff01\u201d \u3000\u3000\u5176\u5b9e\uff0c\u53f2\u767d\u90fd\u53d7\u7684\u662f\u77f3\u971e\u59d1\u7684\u6bd2\u7c89\u4e4b\u4f24\uff0c\u8fd9\u4f24\u4e5f\u65e9\u5df2\u597d\u4e86\u3002\u738b\u5409\u80e1\u8bf4\u4e00\u901a\uff0c\u5c3d\u91cf\u5938\u5f20\u91d1 \u9010\u6d41\u7684\u5389\u5bb3\uff0c\u7528\u610f\u4e0d\u8fc7\u662f\u60f3\u5413\u8d70\u5c01\u5b50\u8d85\u7684\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u542c\u4e86\u505a\u58f0\u4e0d\u5f97\uff0c\u5fc3\u4e2d\u53ea\u662f\u6697\u81ea\u53f9\u6c14\uff1a\u201c\u7cdf\u7cd5\uff0c\u7cdf\u7cd5\uff01\u6211\u8fd8\u4ee5\u4e3a\u6211\u53ef\u4ee5\u627e\u53f2\u767d\u90fd\u4f5c \u6211\u7684\u9760\u5c71\uff0c\u8c01\u77e5\u8fd9\u9760\u5c71\u4e5f\u7ed9\u4eba\u63a8\u5012\u4e86\uff01\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\u679c\u7136\u662f\u5728\u626c\u5dde\uff0c\u800c\u4e14\u8fd8\u548c\u4e10\u5e2e\u8054\u4e86\u624b\uff0c \u6211\u8fd9\u4e00\u53bb\u3002\u53ef\u4e0d\u6b63\u662f\u81ea\u6295\u7f57\u7f51\u5417\uff1f\u201d \u3000\u3000\u738b\u5409\u53c8\u9053\uff1a\u201c\u5e2e\u4e3b\u5982\u4eca\u5367\u75c5\u5728\u5e8a\uff0c\u6df1\u6015\u4e10\u5e2e\u548c\u90a3\u59d3\u91d1\u7684\u5c0f\u5b50\u518d\u6765\u5f3a\u653b\uff0c\u662f\u4ee5\u56db\u51fa\u6c42\u63f4\u3002 \u6211\u5c31\u662f\u5949\u4e86\u5e2e\u4e3b\u4e4b\u547d\uff0c\u8fc7\u6c5f\u53bb\u8bf7\u6551\u5175\u7684\u3002\u5c01\u5927\u4eba\uff0c\u6240\u4ee5\u6211\u8bf4\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u60a3\u96be\u89c1\u771f\u60c5\uff0c\u8fd9\u6b21 \u4f60\u662f\u5e94\u8be5\u52a9\u6211\u4eec\u5e2e\u4e00\u81c2\u4e4b\u529b\u4e86\uff0c\u597d\uff0c\u6211\u8981\u8d76\u53bb\u8bf7\u6551\u5175\uff0c\u5931\u966a\u4e86\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u72ec\u7acb\u8239\u5934\uff0c\u4e00\u7247\u832b\u7136\uff0c\u76ee\u9001\u738b\u5409\u7684\u4e00\u53f6\u8f7b\u821f\u5728\u6ce2\u5149\u4e91\u5f71\u4e4b\u4e2d\u8fdc\u53bb\u3002\u4ed6\u54ea\u77e5\u738b\u5409\u6b64 \u65f6\u6b63\u5728\u5fc3\u4e2d\u6697\u7b11\uff1a\u201c\u770b\u4f60\u8fd9\u5bb6\u4f19\u8fd8\u6562\u4e0d\u6562\u5230\u626c\u5dde\u53bb\u548c\u53f2\u767d\u90fd\u5171\u540c\u60a3\u96be\uff1f\u201d\u539f\u6765\u738b\u5409\u662f\u6015\u4ed6\u89c1 \u4e86\u53f2\u767d\u90fd\uff0c\u6cc4\u6f0f\u4e86\u81ea\u5df1\u9003\u8d70\u7684\u79d8\u5bc6\u3002\u867d\u7136\u8fd9\u79d8\u5bc6\u8fdf\u65e9\u8981\u88ab\u63ed\u7a7f\uff0c\u4f46\u603b\u662f\u8fdf\u4e00\u5929\u7ed9\u53f2\u767d\u90fd\u77e5\u9053 \u597d\u8fc7\u65e9\u4e00\u5929\u8ba9\u4ed6\u77e5\u9053\u3002 \u3000\u3000\u4e14\u8bf4\u5389\u5357\u661f\u9192\u6765\u4e4b\u540e\uff0c\u4fbf\u5373\u76d8\u819d\u800c\u5750\uff0c\u9ed8\u8fd0\u7384\u529f\uff0c\u628a\u771f\u6c14\u4e00\u70b9\u4e00\u6ef4\u7684\u51dd\u805a\u8d77\u6765\u3002\u53ef\u662f\u8fd9 \u53ea\u80fd\u6682\u65f6\u6297\u6bd2\u800c\u5df2\uff0c\u82e5\u8981\u89e3\u6bd2\uff0c\u8fd8\u5fc5\u987b\u7528\u201c\u91d1\u9488\u62d4\u6bd2\u201d\u7684\u7597\u6cd5\uff0c\u8fd9\u5374\u662f\u8981\u522b\u4eba\u66ff\u4ed6\u9488\u7078\u7684\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u8d70\u4e86\u8fdb\u6765\uff0c\u559c\u9053\uff1a\u201c\u4f60\u7684\u6c14\u8272\u597d\u8bb8\u591a\u4e86\u3002\u201d\u5389\u5357\u661f\u9053\uff1a\u201c\u4f60\u6709\u78c1\u77f3\u6ca1\u6709\uff1f\u201d\u5c01\u5999 \u5ae6\u9053\uff1a\u201c\u4f60\u662f\u8981\u7528\u78c1\u77f3\u5438\u51fa\u6697\u5668\u5417\uff1f\u597d\uff0c\u6211\u7ed9\u4f60\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u8e0c\u8e87\u9053\uff1a\u201c\u8fd8\u662f\u8bf7\u4f60\u7239\u7239\u6765\u5427\u3002\u201d\u5c01\u5999\u5ae6\u5ae3\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u6b63\u5728\u78b0\u89c1\u4e00\u4f4d\u76f8 \u719f\u7684\u670b\u53cb\uff0c\u548c\u4ed6\u8bf4\u8bdd\u3002\u54b1\u4eec\u90fd\u662f\u6c5f\u6e56\u513f\u5973\uff0c\u96be\u9053\u4f60\u8fd8\u907f\u5fcc\u4ec0\u4e48\u7537\u5973\u4e4b\u5acc\u4e48\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u627e\u51fa\u4e86\u78c1\u77f3\uff0c\u95ee\u9053\uff1a\u201c\u4f24\u5728\u54ea\u91cc\uff1f\u201d\u5389\u5357\u661f\u8f6c\u8eab\u4fef\u5367\uff0c\u8bf4\u9053\uff1a\u201c\u5de6\u80a9\u80a9\u5934\u7435\u7436\u9aa8 \u4e0b\u9762\u4e00\u5bf8\u4e4b\u5904\uff0c\u4f60\u628a\u6e83\u70c2\u7684\u808c\u8089\u523a\u6389\uff0c\u5c31\u53ef\u4ee5\u628a\u6bd2\u9488\u5438\u51fa\u6765\u4e86\u3002\u201d\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6211\u61c2\u3002\u201d\u6495 \u7834\u4ed6\u7684\u8863\u88f3\uff0c\u53ea\u6709\u4f24\u53e3\u5468\u56f4\u7600\u9ed1\u575f\u80bf\uff0c\u5c01\u5999\u5ae1\u5403\u60ca\u9053\uff1a\u201c\u8fd9\u6bd2\u9488\u597d\u5389\u5bb3\u3002\u201d\u6309\u4e86\u6309\u65c1\u8fb9\u7684\u808c \u8089\uff0c\u95ee\u9053\uff1a\u201c\u75db\u4e0d\u75db\uff1f\u201d\u5389\u5357\u661f\u9053\uff1a\u201c\u4e0d\u5fc5\u987e\u5fcc\uff0c\u525c\u5427\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u628a\u6e83\u70c2\u7684\u808c\u8089\u523a\u6389\uff0c\u8110\u51fa\u7684\u6bd2\u8840\uff0c\u8db3\u6709\u4e00\u8336\u676f\u4e4b\u591a\uff0c\u8ddf\u4f4f\u7528\u78c1\u77f3\u5438\u51fa\u6bd2\u9488\uff0c\u5389\u5357 \u661f\u4e1d\u6beb\u4e5f\u6ca1\u547b\u541f\u3002\u5c01\u5999\u5ae6\u597d\u751f\u4f69\u670d\uff0c\u5fc3\u60f3\uff1a\u201c\u771f\u4e0d\u6127\u662f\u91d1\u9010\u6d41\u7684\u4e49\u5144\uff0c\u770b\u6765\u4ed6\u7684\u529f\u529b\u53ea\u6015\u4e5f \u4e0d\u5728\u91d1\u9010\u6d41\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u62b9\u4e86\u62b9\u6c57\uff0c\u5979\u521a\u624d\u5c3d\u5fc3\u4e3a\u5389\u5357\u661f\u5438\u53d6\u6bd2\u9488\uff0c\u5bf9\u5916\u95f4\u7684\u8bf4\u8bdd\uff0c\u542c\u800c\u4e0d\u95fb\u3002\u6b64\u65f6\u65b9\u59cb \u542c\u5f97\u8fdb\u53bb\uff0c\u521a\u597d\u738b\u5409\u8bf4\u5230\u53f2\u767d\u90fd\u7ed9\u91d1\u9010\u6d41\u6253\u8d25\uff0c\u5c01\u5999\u5ae6\u4e0d\u7981\u5927\u559c\u3002 \u3000\u3000\u53ea\u8d1d\u5389\u5357\u661f\u95ed\u76ee\u5782\u9996\uff0c\u4f3c\u4e4e\u6b63\u5728\u517b\u795e\uff0c\u5c01\u5999\u5ae6\u4e0d\u6562\u60ca\u52a8\u4ed6\uff0c\u628a\u559c\u60a6\u85cf\u5728\u5fc3\u91cc\u3002\u534a\u664c\uff0c\u5389 \u5357\u661f\u5f20\u5f00\u773c\u775b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u9488\u7078\u7528\u7684\u94f6\u9488\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u7f1d\u8863\u7684\u7ee3\u82b1\u9488\u5c31\u6709\u3002\u201d\u5389\u5357\u661f\u9053\uff1a\u201c\u6211\u662f\u8981\u7528\u6765\u62d4\u6bd2\u7684\uff0c\u7ee3\u82b1\u9488\u4e0d\u80fd \u7528\u3002\u201d\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u90a3\u600e\u4e48\u529e\u5462\uff1f\u201d\u5ffd\u89c9\u5389\u5357\u661f\u7684\u76ee\u5149\u4f3c\u4e4e\u662f\u5728\u671d\u7740\u5979\u7684\u5934\u4e0a\u671b\uff0c\u5c01\u5999\u5ae6\u5fc3 \u4e2d\u4e00\u52a8\uff0c\u62d4\u4e0b\u4e86\u5934\u4e0a\u7684\u94f6\u7c2a\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u53ef\u4ee5\u4ee3\u66ff\u5427\uff1f\u201d\u5389\u5357\u661f\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u7528\u5012 \u662f\u53ef\u4ee5\u7528\uff0c\u4e0d\u8fc7\u2026\u2026\u201d\u5c01\u5999\u5ae6\u731c\u5230\u4ed6\u7684\u5fc3\u601d\uff0c\u7b11\u9053\uff1a\u201c\u80fd\u7528\u5c31\u884c\u3002\u4f60\u544a\u8bc9\u6211\u5982\u4f55\u65bd\u672f\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u602a\u8d77\u8033\u6735\u4e00\u542c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7239\u7239\u90a3\u4f4d\u670b\u53cb\u4f3c\u4e4e\u5df2\u7ecf\u8d70\u4e86\u3002\u201d\u539f\u6765\u8981\u7528\u91d1\u9488\u62d4\u6bd2\u4e4b \u6cd5\uff0c\u5389\u5357\u661f\u5fc5\u987b\u8131\u5149\u4e0a\u8eab\uff0c\u8ba9\u5979\u523a\u6709\u5173\u7684\u4e03\u5904\u7a74\u9053\uff0c\u8fd8\u8981\u5377\u8d77\u88e4\u811a\uff0c\u8ba9\u5979\u523a\u4e24\u8fb9\u819d\u76d6\u7684\u201c\u73af \u8df3\u7a74\u201d\u3002\u867d\u8bf4\u6c5f\u6e56\u513f\u5973\u4e0d\u8bb2\u7a76\u7537\u5973\u4e4b\u8d5a\uff0c\u603b\u662f\u89c9\u5f97\u4e0d\u5927\u96c5\u89c2\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u54b3\u55fd\uff0c\u5c01\u5b50\u8d85\u63a8\u5f00\u623f\u95e8\u8fdb\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u606d\u559c\uff0c\u606d\u559c\uff0c\u4f60\u597d\u591a\u4e86\u3002\u4f60\u73b0\u5728\u53ef\u662f \u8981\u4eba\u66ff\u4f60\u91d1\u9488\u62d4\u6bd2\uff1f\u201d\u5389\u6d77\u661f\u542c\u4ed6\u8bf4\u5f97\u51fa\u201c\u91d1\u9488\u62d4\u6bd2\u201d\u8fd9\u4e2a\u540d\u8bcd\uff0c\u77e5\u9053\u4ed6\u591a\u5c11\u4e5f\u662f\u4e2a\u884c\u5bb6\uff0c \u559c\u9053\uff1a\u201c\u6b63\u662f\u3002\u4e0d\u77e5\u8001\u7237\u5b50\u2026\u2026\u201d\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u597d\uff0c\u8ba9\u6211\u7ed9\u4f60\u5e2e\u5fd9\u5427\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u5ffd\u9053\uff1a\u201c\u7239\uff0c\u4e0d\u884c\u5440\uff01\u201d\u5c01\u5b50\u8d85\u6115\u7136\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u884c\u5440\uff1f\u201d\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u7239\uff0c\u4f60 \u5fd8\u8bb0\u4e86\u4e48\uff1f\u4f60\u7684\u53f3\u624b\u60a3\u6709\u98ce\u6e7f\u75c5\uff0c\u7d27\u5f20\u7684\u65f6\u5019\uff0c\u4f1a\u6253\u51b7\u98a4\u7684\u3002\u8fd8\u662f\u8ba9\u6211\u6765\u5427\uff01\u201d\u5c01\u5b50\u8d85\u6014\u4e86 \u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u51e0\u65f6\u60a3\u4e86\u8fd9\u4e2a\u6bdb\u75c5\uff1f\u201d\u968f\u5373\u604d\u7136\u5927\u609f\uff1a\u201c\u662f\u662f\uff0c\u8fd9\u4e2b\u5934\u6050\u6015\u6211\u5bb3\u4e86\u8fd9\u5c0f \u5b50\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u5f53\u7136\u4e0d\u4f1a\u5bf9\u5389\u5357\u661f\u5b58\u6709\u4ec0\u4e48\u597d\u5fc3\u80a0\uff0c\u4f46\u8981\u4e0d\u8981\u6697\u4e0b\u6bd2\u624b\uff0c\u6b64\u9645\u4ed6\u4ecd\u662f\u8e0c\u8e87\u672a\u51b3\u3002 \u4e00\u6765\u4ed6\u987e\u5fcc\u91d1\u9010\u6d41\u5c06\u6765\u627e\u4ed6\u7b97\u5e10\uff1b\u4e8c\u6765\u4ed6\u4e5f\u77e5\u9053\u5973\u513f\u5b9a\u7136\u4e0d\u4f9d\u3002\u73b0\u5728\u7ed9\u5973\u513f\u8bf4\u7834\uff0c\u5389\u5357\u661f\u4e0d \u660e\u767d\uff0c\u4ed6\u5fc3\u91cc\u53ef\u662f\u660e\u767d\u7684\uff0c\u4e0b\u624b\u5f53\u7136\u66f4\u4e0d\u6210\u4e86\u3002\u4e8e\u662f\u5c01\u5b50\u8d85\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\u6211 \u51e0\u4e4e\u5fd8\u4e86\u3002\u5bf9\uff0c\u8fd8\u662f\u4f60\u7ed9\u5389\u516c\u5b50\u533b\u6cbb\u597d\u4e9b\u3002\u6211\u51fa\u53bb\u628a\u8235\u5427\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u6559\u4e86\u5979\u91d1\u9488\u62d4\u6bd2\u4e4b\u6cd5\uff0c\u5c01\u5999\u5ae6\u5fc3\u7075\u624b\u5de7\uff0c\u4e00\u5b66\u5c31\u61c2\u3002\u5f53\u4e0b\u5389\u5357\u661f\u8131\u5149\u4e0a\u8eab\uff0c\u8ba9\u5979 \u7528\u94f6\u7c2a\u523a\u7a74\u3002 \u3000\u3000\u523a\u7a74\u3001\u62d4\u6bd2\uff0c\u5fc5\u987b\u5168\u795e\u8d2f\u6ce8\uff0c\u4e0d\u80fd\u6709\u4e1d\u6beb\u7684\u5dee\u9519\u3002\u5c01\u5999\u5ae6\u867d\u7136\u5b66\u4f1a\u91d1\u9488\u62d4\u6bd2\u4e4b\u6cd5\uff0c\u5728\u5c0f \u5fc3\u7ffc\u7ffc\u5730\u523a\u4e86\u5389\u5357\u661f\u4e0a\u8eab\u7684\u4e03\u5904\u7a74\u9053\u4ee5\u53ca\u4e24\u8fb9\u819d\u76d6\u7684\u201c\u73af\u8df3\u7a74\u201d\u4e4b\u540e\uff0c\u4e5f\u5df2\u7d2f\u5f97\u6ee1\u5934\u5927\u6c57\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u6b47\u4e86\u4e00\u6b47\uff0c\u5f85\u5389\u5357\u661f\u7a7f\u4e0a\u4e0a\u8863\u4e4b\u540e\uff0c\u8fd9\u624d\u548c\u4ed6\u8bf4\u9053\uff1a\u201c\u7239\u7239\u548c\u4ed6\u90a3\u4f4d\u670b\u53cb\u8bf4\u7684 \u8bdd\uff0c\u4f60\u53ef\u542c\u89c1\u4e86\u4e48\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u6ca1\u6709\u542c\u89c1\u3002\u53ef\u6709\u4ec0\u4e48\u6d88\u606f\u4e48\u3002\u201d\u539f\u6765\u5389\u5357\u661f\u4e00\u76f4\u5728\u8fd0\u529f\u6297\u6bd2\uff0c\u5bf9\u5916\u9762\u8bf4\u8bdd \u7684\u58f0\u97f3\uff0c\u4ed6\u6839\u672c\u662f\u542c\u800c\u4e0d\u95fb\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u6b63\u8981\u628a\u559c\u8baf\u544a\u8bc9\u4ed6\uff0c\u5ffd\u89c1\u5c01\u5b50\u8d85\u53c8\u8d70\u4e86\u8fdb\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6b63\u8981\u544a\u8bc9\u5389\u516c\u5b50\uff0c\u54b1\u4eec \u53ef\u4e0d\u80fd\u524d\u5f80\u626c\u5dde\u53bb\u4e86\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u4f60\u7684\u628a\u5144\u5f1f\u91d1\u9010\u6d41\u5728\u626c\u5dde\u548c\u53f2\u767d\u90fd\u6253\u4e86\u4e00\u67b6\uff0c\u53cc\u65b9\u90fd\u53d7\u4e86\u91cd\u4f24\u3002\u91d1\u9010\u6d41\u867d \u6709\u4e10\u5e2e\u4e4b\u52a9\uff0c\u4f46\u516d\u5408\u5e2e\u7684\u52bf\u529b\u4ecd\u662f\u5927\u5f97\u591a\uff0c\u542c\u8bf4\u4e10\u5e2e\u5df2\u9003\u51fa\u4e86\u626c\u5dde\uff0c\u91d1\u9010\u6d41\u4e5f\u65e9\u5df2\u8d70\u4e86\uff0c\u6240 \u4ee5\u6211\u60f3\uff0c\u8fd8\u662f\u628a\u4f60\u9001\u5230\u91d1\u9675\u7684\u597d\u3002\u90a3\u91cc\u4e5f\u6709\u4e10\u5e2e\u7684\u5206\u8235\uff0c\u800c\u4e14\u6bd4\u626c\u5dde\u7684\u5927\u5f97\u591a\uff0c\u4f60\u5728\u91d1\u9675\uff0c \u8f83\u6613\u5f97\u5230\u91d1\u9010\u6d41\u7684\u6d88\u606f\u3002\u4f60\u8bf4\u597d\u4e48\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u7f16\u9020\u51fa\u6765\u7684\u8fd9\u4e00\u756a\u8bf4\u8bdd\uff0c\u65e0\u610f\u4e2d\u5374\u662f\u6bd4\u8f83\u7b26\u5408\u4e8b\u5b9e\u7684\u771f\u76f8\u3002\u5389\u5357\u661f\u90a3\u665a\u662f\u773c\u89c1\u91d1 \u9010\u6d41\u72fc\u72c8\u7a81\u56f4\u7684\uff0c\u5bf9\u4ed6\u7684\u8bdd\u81ea\u662f\u76f8\u4fe1\u4e0d\u7591\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u4e0d\u77e5\u90a3\u4e2a\u516d\u5408\u5e2e\u5c0f\u5934\u76ee\u738b\u5409\u8bf4\u7684\u624d\u662f\u5047\u8bdd\uff0c\u53ea\u77e5\u7239\u7239\u7f16\u7684\u4e43\u662f\u8c0e\u8a00\uff0c\u5fc3\u91cc\u5f88\u4e0d\u9ad8 \u5174\u3002\u60f3\u8981\u6233\u7834\uff0c\u53ea\u89c1\u7236\u4eb2\u7684\u76ee\u5149\u6b63\u5728\u76ef\u7740\u5979\uff0c\u597d\u50cf\u662f\u8bf4\uff1a\u201c\u4f60\u8bf4\u4e86\u4e00\u6b21\u8c0e\uff0c\u96be\u9053\u6211\u5c31\u4e0d\u80fd\u8bf4 \u5417\uff1f\u201d\u5389\u5357\u661f\u9053\uff1a\u201c\u591a\u8c22\u6069\u516c\u76f8\u6551\uff0c\u4e00\u5207\u4f46\u51ed\u6069\u516c\u4f5c\u4e3b\u3002\u5bf9\u5566\uff0c\u6211\u8fd8\u6ca1\u6709\u8bf7\u6559\u601d\u516c\u7684\u9ad8\u6027\u5927 \u540d\u5462\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u6050\u6015\u91d1\u9010\u6d41\u66fe\u7ecf\u548c\u4ed6\u8bf4\u8fc7\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u80e1\u4e71\u634f\u4e86\u4e00\u4e2a\u5047\u540d\u3002\u8bf4\u9053\uff1a\u201c\u4f60\u5728\u6211\u7684\u8239 \u4e2d\u5c31\u4e0d\u5fc5\u62c5\u5fe7\u5bb3\u6015\u4e86\uff0c\u6211\u4e00\u5b9a\u8d1f\u8d23\u628a\u4f60\u9001\u5230\u91d1\u9675\u3002\u4f60\u597d\u597d\u517b\u4f24\u5427\u3002\u5ae6\u513f\uff0c\u4f60\u548c\u6211\u51fa\u53bb\uff0c\u4f60\u4e5f \u8be5\u5f04\u70b9\u4e1c\u897f\u7ed9\u5389\u516c\u5b50\u5403\u4e86\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u60f3\u4e86\u4e00\u60f3\uff0c\u89c9\u5f97\u82e5\u679c\u5f53\u7740\u5389\u5357\u661f\u7684\u9762\u62c6\u7a7f\u7236\u4eb2\u7684\u8c0e\u8a00\u4e5f\u662f\u4e0d\u597d\uff0c\u4e8e\u662f\u8ddf\u4ed6\u51fa\u53bb\u3002 \u3000\u3000\u5230\u4e86\u524d\u8231\uff0c\u5c01\u5999\u5ae6\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u8bf4\u8c0e\u8bdd\uff01\u201d\u5c01\u5b50\u8d85\u5728\u5979\u8033\u8fb9\u7b11\u9053\uff1a\u201c\u4f60 \u4e0d\u662f\u4e0d\u613f\u610f\u89c1\u53f2\u767d\u90fd\u7684\u4e48\uff1f\u73b0\u5728\u4e0d\u53bb\u626c\u5dde\uff0c\u53ef\u4e0d\u6b63\u662f\u5408\u4e86\u4f60\u7684\u5fc3\u613f\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u867d\u7136\u76f8\u4fe1\u53f2\u767d\u90fd\u662f\u53d7\u4e86\u4f24\uff0c\u4f46\u53f2\u767d\u90fd\u7a76\u7adf\u8fd8\u662f\u6d3b\u5728\u626c\u5dde\uff0c\u5979\u4e5f\u6709\u70b9\u5bb3\u6015\u5230\u626c\u5dde\u4f1a \u6709\u9ebb\u70e6\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u6211\u4e0d\u62c6\u7a7f\u4f60\u7684\u8c0e\u8bdd\uff0c\u4f60\u5230\u91d1\u9675\uff0c\u6211\u4e5f\u4f9d\u4f60\uff0c\u4f46\u4f60\u53ef\u4e0d\u80fd\u6697\u5bb3\u4eba \u5bb6\u3002\u201c \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u50bb\u4e2b\u5934\uff0c\u8fd9\u6b63\u662f\u6211\u8ba8\u597d\u91d1\u9010\u6d41\u7684\u597d\u673a\u4f1a\uff0c\u5f85\u8fd9\u59d3\u5389\u7684\u5b8c\u5168\u597d\u4e86\uff0c\u6211\u8fd8\u8981\u6258 \u4ed6\u5411\u91d1\u9010\u6d41\u8bf4\u597d\u8bdd\u5462\uff0c\u6211\u600e\u4f1a\u5bb3\u4ed6\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u542c\u5f97\u7236\u4eb2\u8fd9\u6837\u8bf4\uff0c\u53ea\u9053\u5c01\u5b50\u8d85\u771f\u7684\u662f\u6709\u8bda\u610f\uff0c\u559c\u9053\uff1a\u201c\u7239\uff0c\u4f60\u80fd\u591f\u8fd9\u6837\u5c31\u597d\u3002\u4f46 \u4f60\u521a\u624d\u4e3a\u4ec0\u4e48\u4e0d\u80af\u628a\u771f\u540d\u5b9e\u59d3\u544a\u8bc9\u4ed6\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u8fd8\u672a\u75ca\u6108\uff0c\u544a\u8bc9\u4e86\u4ed6\u6050\u4ed6\u72af\u7591\u3002\u5f85\u4ed6\u5b8c\u5168\u597d\u4e86\uff0c\u90a3\u65f6\u548c\u76d8\u6258\u51fa\uff0c\u4e5f \u8fd8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u542c\u542c\u4e5f\u6709\u4e00\u70b9\u9053\u7406\uff0c\u653e\u4e0b\u4e86\u5fc3\uff0c\u4fbf\u53bb\u7ed9\u5389\u5357\u661f\u5f04\u996d\u3002 \u3000\u3000\u5389\u5357\u661f\u9ed8\u8fd0\u7384\u529f\uff0c\u51dd\u805a\u771f\u529b\uff0c\u8fc7\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u7cbe\u795e\u53c8\u60ed\u590d\u4e86\u51e0\u5206\u3002\u4e0d\u8fc7\u8eab\u4f53\u8fd8\u662f\u865a\u8f6f\uff0c \u4f7f\u4e0d\u4e0a\u6c14\u529b\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u5f04\u597d\u4e86\u4e00\u9505\u7a00\u996d\uff0c\u51e0\u6837\u5c0f\u83dc\uff0c\u5df2\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u4e09\u4e2a\u4eba\u5c31\u5728\u5389\u5357\u661f\u7684\u8231\u623f\u540c\u5403\u665a \u996d\u3002 \u3000\u3000\u5389\u5357\u661f\u5403\u9971\u4e86\u809a\u5b50\uff0c\u8212\u5c55\u4e00\u4e0b\u624b\u8db3\uff0c\u7b11\u9053\uff1a\u201c\u51af\u8001\u7237\u5b50\uff0c\u4f60\u9a7e\u8239\u7684\u672c\u9886\u5f88\u662f\u4e0d\u9519\u5440\uff01\u521a \u624d\u8fc7\u7684\u4e00\u9053\u9669\u6ee9\uff0c\u6211\u8fd8\u771f\u7684\u6709\u70b9\u4e3a\u4f60\u62c5\u5fc3\u5462\u3002\u201d\u5c01\u5b50\u8d85\u634f\u9020\u7684\u5047\u59d3\u662f\u548c\u201c\u5c01\u201d\u5b57\u58f0\u97f3\u76f8\u8fd1\u7684 \u201c\u51af\u201d\u5b57\uff0c\u6545\u800c\u5389\u5357\u661f\u79f0\u4ed6\u201c\u51af\u8001\u7237\u5b50\u201d\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u5c11\u5e74\u65f6\u5019\u5728\u6c34\u9646\u4e24\u8def\u7684\u9ed1\u9053\u90fd\u66fe\u6df7\u8fc7\uff0c\u9a7e\u8239\u7684\u672c\u9886\u8fd8\u672a\u5fd8\u8bb0\uff0c\u4e0d\u8fc7\u5df2\u7ecf\u4e0d\u662f\u600e\u6837 \u7cbe\u901a\uff0c\u542c\u4e86\u5389\u5357\u661f\u7684\u8bf4\u8bdd\uff0c\u77e5\u9053\u4ed6\u662f\u4e00\u4e2a\u9a76\u8239\u7684\u5927\u884c\u5bb6\uff0c\u5fc3\u91cc\u6697\u6697\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u6211\u7684\u6bd2\u90fd\u5df2\u62d4\u6e05\uff0c\u770b\u6765\u6211\u660e\u5929\u53ef\u4ee5\u66ff\u4f60\u638c\u8235\u4e86\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u522b\u5fd9\uff0c\u4f60\u8fd8\u662f\u591a\u517b\u606f\u51e0\u5929\u7684\u597d\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5f85\u6211\u8bd5\u8bd5\u6c14\u529b\u3002\u201d\u5f53\u4e0b\u4e00\u628a\u6293\u8d77\u90a3\u67c4\u7384\u94c1\u5b9d\u5251\u3002 \u3000\u3000\u7384\u94c1\u5b9d\u5251\u867d\u7136\u63d0\u4e86\u8d77\u6765\uff0c\u4f46\u5389\u5357\u661f\u4e5f\u4e0d\u7981\u6709\u70b9\u6c14\u5598\uff0c\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u5feb\u653e\u4e0b\u6765\u5427\uff0c\u522b\u7d2f\u574f \u4e86\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u653e\u4e0b\u5b9d\u5251\u7b11\u9053\uff1a\u201c\u52a8\u7528\u8fd9\u67c4\u5b9d\u5251\u7684\u6c14\u529b\u672a\u6709\uff0c\u638c\u8239\u7684\u6c14\u529b\u603b\u662f\u6709\u4e86\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u597d\u5f97\u8fd9\u6837\u5feb\uff0c\u518d\u8fc7\u4e00\u5929\uff0c\u53ea\u6015\u6211\u5c31\u4e0d\u662f\u4ed6\u7684\u5bf9 \u624b\u3002\u6211\u82e5\u662f\u8981\u628a\u4ed6\u9664\u6389\uff0c\u53ef\u5f97\u8d81\u65e9\u3002\u4f46\u4e0d\u77e5\u662f\u5bb3\u4e86\u4ed6\u597d\u5462\uff1f\u8fd8\u662f\u4e0d\u5bb3\u4ed6\u597d\u5462\uff1f\u82e5\u8981\u5bb3\u4ed6\uff0c\u53c8 \u600e\u80fd\u7792\u5f97\u8fc7\u8fd9\u4e2b\u5934\u5462\uff1f\u201d \u3000\u3000\u5403\u8fc7\u665a\u677f\uff0c\u5c01\u5999\u5ae6\u628a\u8231\u623f\u8ba9\u7ed9\u5389\u5357\u661f\u7761\uff0c\u5979\u548c\u7236\u4eb2\u540c\u4f4f\u8239\u8231\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u7ffb\u6765\u8986\u53bb\uff0c\u54ea\u91cc\u7761\u5f97\u7740\u89c9\uff1f\u5fc3\u4e2d\u53ea\u662f\u4e0d\u4f4f\u7684\u5728\u76d8\u7b97\uff1a\u6740\u4e0d\u6740\u5389\u5357\u661f\u5462\uff1f\u54ea\u4e00\u6837\u5bf9 \u81ea\u5df1\u66f4\u4e3a\u6709\u5229\u3002\u201d \u3000\u3000\u4e0d\u6740\u5389\u5357\u661f\uff0c\u8fd9\u5f53\u7136\u662f\u5356\u7ed9\u91d1\u9010\u6d41\u7684\u4e00\u4e2a\u5927\u5927\u7684\u4ea4\u60c5\uff0c\u91d1\u9010\u6d41\u6b64\u540e\u4e0d\u4f46\u4e0d\u4f1a\u627e\u4ed6\u9ebb\u70e6\uff0c \u8fd8\u4e00\u5b9a\u4f1a\u611f\u6fc0\u4ed6\u3002\u53ef\u662f\u8fd9\u6837\u4e00\u6765\uff0c\u6211\u5c82\u4e0d\u662f\u8981\u548c\u91d1\u9010\u6d41\u8d70\u4e0a\u4e86\u4e00\u6761\u8def\uff0c\u6211\u54ea\u91cc\u8fd8\u6709\u51fa\u5934\u4e4b \u65e5\uff1f\u201d\u5c01\u5b50\u8d85\u5fc3\u60f3\u3002\u5f53\u7136\u5728\u4ed6\u5fc3\u76ee\u4e2d\u7684\u201c\u51fa\u5934\u201d\u5c31\u662f\u8981\u730e\u53d6\u529f\u540d\u5bcc\u8d35\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u60f3\u8d77\u4e86\u90a3\u628a\u7384\u94c1\u5b9d\u5251\uff0c\u6697\u81ea\u601d\u91cf\uff1a\u201c\u6211\u4e0d\u6740\u59d3\u5389\u7684\u5c0f\u5b50\uff0c\u8fd9\u628a\u7384\u94c1\u5b9d\u5251\u4ed6\u5f53\u7136\u662f \u8981\u5e26\u8d70\u7684\u4e86\u3002\u5982\u6b64\u7a00\u4e16\u4e4b\u73cd\uff0c\u5230\u4e86\u624b\u53c8\u518d\u5931\u6389\uff0c\u5c82\u4e0d\u53ef\u60dc\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u84e6\u5730\u5f97\u4e86\u4e00\u4e2a\u4e3b\u610f\uff1a\u201c\u5bf9\u4e86\uff0c\u8fd9\u628a\u7384\u94c1\u5b9d\u5251\u53f2\u767d\u90fd\u672c\u6765\u662f\u8981\u9001\u7ed9\u8428\u603b\u7ba1\u7684\uff0c\u5982\u4eca \u53f2\u767d\u90fd\u81ea\u8eab\u6027\u547d\u96be\u4fdd\uff0c\u6211\u5f53\u7136\u4e0d\u80fd\u628a\u5b9d\u5251\u4ea4\u8fd8\u7ed9\u4ed6\u3002\u4f46\u6211\u53ef\u4ee5\u5f53\u4f5c\u81ea\u5df1\u593a\u6765\u7684\u5c06\u5b83\u9001\u7ed9\u8428\u603b \u7ba1\u5440\u3002\u91d1\u9010\u6d41\u5728\u5927\u95f9\u8428\u5802\u4e4b\u540e\uff0c\u5982\u4eca\u60f3\u5fc5\u4e0d\u6562\u518d\u7559\u5728\u4eac\u4e2d\u3002\u6211\u5165\u4eac\u732e\u793c\uff0c\u4ed6\u53c8\u600e\u80fd\u77e5\u6653\uff1f\u8428 \u603b\u7ba1\u6536\u4e86\u6211\u7684\u539a\u793c\uff0c\u5fc5\u8981\u7ed9\u6211\u916c\u52b3\uff0c\u6700\u5c11\u6211\u53ef\u4ee5\u5b98\u590d\u539f\u804c\u3002\u90a3\u65f6\u6211\u5728\u5fa1\u6797\u519b\u4e2d\uff0c\u4e5f\u5c31\u4e0d\u6015\u91d1 \u9010\u6d41\u6765\u627e\u6211\u7b97\u5e10\u4e86\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u60f3\u5f97\u5982\u610f\uff0c\u6740\u673a\u9661\u8d77\uff0c\u53ea\u5269\u4e0b\u4e00\u4e2a\u95ee\u9898\uff1a\u600e\u6837\u624d\u80fd\u7792\u5f97\u8fc7\u5973\u513f\uff0c\u6beb\u65e0\u75d5\u8ff9\u7684\u5c31\u5bb3 \u4e86\u5389\u5357\u661f\uff1f \u3000\u3000\u5c01\u5b50\u8d85\u5fc3\u60f3\uff1a\u201c\u5982\u679c\u8fd9\u5c0f\u5b50\u8fd8\u5728\u660f\u8ff7\u4e4b\u4e2d\uff0c\u6211\u5012\u662f\u5927\u6709\u6697\u4e0b\u6bd2\u624b\u7684\u673a\u4f1a\uff01\u201d\u60f3\u81f3\u6b64\u5904\uff0c \u5fc3\u4e2d\u4e00\u52a8\uff0c\u7acb\u5373\u53c8\u5f97\u4e86\u4e00\u4e2a\u4e3b\u610f\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u5750\u8d77\u8eab\u6765\uff0c\u8bd5\u63a2\u5973\u513f\u7761\u7740\u4e86\u6ca1\u6709\uff0c\u521a\u4e00\u5750\u8d77\uff0c\u679c\u7136\u5c31\u542c\u5f97\u5c01\u5999\u5ae6\u53eb\u9053\uff1a\u201c\u7239\uff0c\u4f60 \u8fd8\u6ca1\u7761\u5417\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u6211\u559d\u4e00\u676f\u8336\u3002\u4f60\u65e9\u70b9\u7761\u5427\u3002\u201d\u5fc3\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u5f53\u771f\u662f\u5728\u63d0\u9632\u7740\u6211\uff0c\u597d\uff0c\u6211 \u5df2\u770b\u5979\u71ac\u5f97\u51e0\u65f6\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u559d\u4e86\u8336\uff0c\u7eb3\u5934\u4fbf\u7761\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6545\u610f\u88c5\u4f5c\u5df2\u7ecf\u719f\u7761\u7684\u6837\u5b50\uff0c\u547c\u547c\u7684\u53d1\u51fa\u9f3e\u58f0\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u65e5\u95f4\u66ff\u5389\u5357\u661f\u7528\u91d1\u9488\u62d4\u6bd2\uff0c\u5fc3\u529b\u4ea4\u7601\uff0c\u5b9e\u5728\u4e5f\u662f\u7d2f\u5f97\u5f88\u4e86\uff0c\u542c\u5f97\u7236\u4eb2\u7684\u9f3e\u58f0\u5927 \u4f5c\uff0c\u8fd9\u9f3e\u58f0\u662f\u6709\u611f\u67d3\u7684\u4f5c\u7528\u7684\uff0c\u5c01\u5999\u5ae6\u4e0d\u77e5\u4e0d\u89c9\u7cbe\u795e\u677e\u61c8\u4e0b\u6765\uff0c\u4e0d\u4e45\u4e5f\u5c31\u9163\u7136\u5165\u68a6\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u4f4e\u58f0\u5524\u9053\uff1a\u201c\u5ae6\u513f\uff01\u201d\u542c\u4e0d\u5230\u5979\u7684\u56de\u7b54\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u5f53\u4e0b\u722c\u4e86\u8d77\u6765\uff0c\u62ff\u8d77\u4e86\u4e00\u6837 \u4e1c\u897f\uff0c\u8e51\u624b\u8e51\u811a\u7684\u5c31\u8d70\u5411\u524d\u8231\u3002 \u3000\u3000\u4ed6\u624b\u4e2d\u62ff\u7684\u662f\u4e2a\u5439\u7ba1\u51fa\u7ba1\u91cc\u88c5\u7684\u662f\u201c\u9e21\u9e23\u4e94\u9f13\u8fd4\u9b42\u9999\u3002\u201d\u539f\u6765\u5c01\u5b50\u8d85\u867d\u7136\u4e0d\u662f\u4f7f\u6bd2\u7684\u884c \u5bb6\uff0c\u4f46\u8fd9\u79cd\u8ff7\u9999\u662f\u6c5f\u6e56\u4e0a\u6700\u5e38\u7528\u7684\uff0c\u5c01\u5b50\u8d85\u51fa\u8eab\u9ed1\u9053\uff0c\u8fd8\u4fdd\u7559\u6709\u201c\u9e21\u9e23\u4e94\u9f13\u8fd4\u9b42\u9999\u201d\u7684\u5168\u526f \u7528\u5177\u3002 \u3000\u3000\u8fd9\u79cd\u8ff7\u9999\u867d\u5f88\u666e\u901a\uff0c\u4f46\u5728\u5438\u4e86\u8fd9\u79cd\u8ff7\u9999\u4e4b\u540e\uff0c\u4e0d\u5230\u5929\u660e\uff0c\u5c31\u4e0d\u4f1a\u9192\uff0c\u6545\u6b64\u540d\u4e3a\u201c\u9e21\u9e23\u4e94 \u9f13\u8fd4\u9b42\u9999\u201d\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u53e3\u4e2d\u542b\u4e86\u89e3\u836f\uff0c\u70b9\u4e86\u8ff7\u9999\uff0c\u5c06\u5439\u7ba1\u6084\u6084\u7684\u63d2\u8fdb\u95e8\u7f1d\uff0c\u628a\u8ff7\u9999\u5439\u8fdb\u5389\u5357\u660c\u7684\u8231\u623f\u3002 \u3000\u3000\u4ed6\u662f\u6253\u7b97\u5728\u5389\u5357\u661f\u660f\u8ff7\u4e4b\u540e\uff0c\u4fbf\u5373\u6697\u65bd\u6bd2\u624b\uff0c\u70b9\u4ed6\u7684\u6b7b\u7a74\u3002\u8fd9\u6837\u5c31\u53ef\u4ee5\u4e1d\u6beb\u4e0d\u7559\u75d5\u8ff9\uff0c \u5c06\u5389\u5357\u661f\u7f6e\u4e4b\u6b7b\u5730\u3002\u4ee5\u4ed6\u5973\u513f\u7684\u6b66\u5b66\u9020\u8be3\uff0c\u51b3\u8ba1\u770b\u4e0d\u51fa\u6765\uff0c\u7b2c\u4e8c\u5929\u4ed6\u53ef\u4ee5\u88c5\u4f5c\u6beb\u4e0d\u77e5\u60c5\uff0c\u5c06 \u5389\u5357\u661f\u7684\u6b7b\u56e0\u5f53\u505a\u662f\u4f59\u6bd2\u672a\u6e05\uff0c\u7a81\u7136\u66b4\u6bd9\u3002\u201c\u8fd9\u4e2b\u5934\u5373\u4f7f\u6709\u6240\u6000\u7591\uff0c\u4f46\u62ff\u4e0d\u5230\u51ed\u8bc1\uff0c\u5979\u4e5f\u4e0d \u80fd\u8bf4\u6211\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u7684\u7b97\u76d8\u6253\u5f97\u5982\u610f\uff0c\u4f46\u4ed6\u5374\u6ca1\u60f3\u5230\uff1a\u5389\u5357\u661f\u662f\u4e00\u4e2a\u4f7f\u6bd2\u7684\u5927\u884c\u5bb6\u3002\u867d\u7136\u4ed6\u4ece\u6765\u4e0d\u7528 \u6bd2\u836f\uff0c\u4f46\u5bf9\u4e8e\u5404\u79cd\u836f\u7269\u5374\u662f\u6781\u6709\u7814\u7a76\u3002\u8fd9\u79cd\u666e\u901a\u7684\u8ff7\u9999\uff0c\u5bf9\u4ed6\u6765\u8bf4\uff0c\u7b80\u76f4\u5c31\u50cf\u662f\u5c0f\u5b69\u5b50\u624b\u4e2d \u7684\u7eb8\u5200\uff0c\u5bb3\u4e0d\u4e86\u4eba\u7684\u3002 \u3000\u3000\u5389\u5357\u661f\u6b63\u5728\u5c06\u7761\u672a\u7761\u4e4b\u9645\uff0c\u6726\u80e7\u4e2d\u95fb\u5f97\u8fd9\u80a1\u9999\u6c14\uff0c\u53cd\u800c\u6e05\u9192\u8fc7\u6765\u3002\u5f53\u4e0b\u597d\u751f\u8be7\u5f02\uff1a\u201c\u662f \u4ec0\u4e48\u4eba\u5077\u4e0a\u8239\u6765\u6697\u7b97\u6211\u5462\uff1f\u201d\u5f53\u7136\u4ed6\u662f\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u4f1a\u662f\u5c01\u5b50\u8d85\u7684\u3002 \u3000\u3000\u5389\u5357\u661f\u6050\u6015\u4e00\u58f0\u5f20\u8fd9\u4eba\u5c31\u4f1a\u8df3\u6c34\u8dd1\u6389\uff0c\u4ed6\u81ea\u5df1\u6bd2\u4f24\u521d\u6108\uff0c\u867d\u7136\u7cbe\u901a\u6c34\u6027\uff0c\u4e5f\u662f\u96be\u4ee5\u64d2 \u51f6\u3002\u4e8e\u662f\u4e1d\u6beb\u4e0d\u52a8\u58f0\u8272\uff0c\u88c5\u4f5c\u4e1a\u5df2\u660f\u8ff7\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u5439\u4e86\u534a\u5929\uff0c\u628a\u4e00\u7b52\u8ff7\u9999\u5168\u90fd\u5439\u4e86\u8fdb\u53bb\uff0c\u89c1\u91cc\u9762\u6beb\u65e0\u52a8\u9759\uff0c\u5fc3\u4e2d\u5927\u559c\uff1a\u201c\u597d\uff0c\u6211\u8fd9 \u5c31\u8fdb\u53bb\uff0c\u8ba9\u8fd9\u5c0f\u5b50\u505a\u4e2a\u7cca\u6d82\u9b3c\u5427\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u5077\u5077\u5730\u6478\u8fdb\u53bb\uff0c\u89e6\u7740\u4e86\u5389\u5357\u661f\u7684\u8eab\u4f53\uff0c\u6b63\u8981\u627e\u5bfb\u6b7b\u7a74\u7684\u90e8\u4f4d\uff0c\u9661\u7136\u95f4\u53ea\u89c9\u80c1\u4e0b\u4e00 \u9ebb\uff0c\u60f3\u53eb\u90fd\u53eb\u4e0d\u51fa\u58f0\uff0c\u5c31\u5012\u4e0b\u53bb\u4e86\u3002\u4ed6\u672a\u66fe\u70b9\u7740\u5389\u5357\u661f\u7684\u6b7b\u7a74\u5374\u7ed9\u5389\u5357\u661f\u5148\u70b9\u4e86\u4ed6\u7684\u9ebb\u7a74\u3002 \u3000\u3000\u672c\u6765\u4ee5\u5c01\u5b50\u8d85\u7684\u672c\u9886\u5389\u5357\u661f\u4e0d\u5e94\u8fd9\u6837\u5bb9\u6613\u5f97\u624b\u7684\u3002\u8fd9\u90fd\u662f\u5c01\u5b50\u8d85\u4ee5\u4e3a\u5389\u5357\u661f\u4e1a\u5df2\u660f\u8ff7\uff0c \u662f\u4ee5\u6beb\u65e0\u9632\u5907\u7684\u7f18\u6545\u3002\u5047\u5982\u4ed6\u4e0d\u7528\u8ff7\u9999\uff0c\u800c\u7528\u7384\u94c1\u5b9d\u5251\u7684\u8bdd\uff0c\u5389\u5357\u661f\u529f\u529b\u672a\u590d\uff0c\u4e00\u5b9a\u4f1a\u7ed9\u4ed6 \u6740\u6389\u3002\u4f46\u662f\u5c01\u5b50\u8d85\u7684\u7b97\u76d8\u6253\u5f97\u592a\u5982\u610f\u4e86\uff0c\u4ed6\u8981\u6740\u5389\u5357\u661f\uff0c\u53c8\u8981\u7792\u8fc7\u5973\u513f\uff0c\u7ec8\u4e8e\u53d8\u6210\u4e86\u5bb3\u4eba\u4e0d \u6210\u53cd\u5bb3\u5df1\u3002 \u3000\u3000\u5389\u5357\u661f\u8df3\u8d77\u6765\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e0b\u4e09\u6ee5\u7684\u5c0f\u8d3c\uff0c\u5c45\u7136\u6562\u7528\u8ff7\u9999\u5bb3\u6211\u3002\u597d\uff0c\u4e14\u5f85\u6211\u6162\u6162\u7684 \u6d88\u9063\u4f60\u3002\u201d\u5389\u5357\u661f\u7684\u70b9\u7a74\u624b\u6cd5\u51fa\u81ea\u91d1\u4e16\u9057\u6240\u4f20\uff0c\u70b9\u4e86\u5c01\u5b50\u8d85\u7684\u9ebb\u7a74\uff0c\u5c01\u5b50\u8d85\u52a8\u5f39\u4e0d\u5f97\uff0c\u4f46\u795e \u667a\u8fd8\u662f\u6e05\u9192\u7684\uff0c\u53ea\u89c9\u4f53\u5185\u5982\u6709\u65e0\u6570\u5229\u9488\uff0c\u5728\u523a\u4ed6\u7684\u4e94\u810f\u516d\u8151\uff0c\u75db\u82e6\u4e4b\u6781\uff0c\u60f3\u53eb\u53c8\u53eb\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u5389\u5357\u661f\u4e00\u9762\u6253\u71c3\u706b\u77f3\uff0c\u70b9\u4eae\u6cb9\u706f\uff0c\u4e00\u9762\u53eb\u9053\uff1a\u201c\u51af\u8001\u7237\u5b50\u5feb\u6765\u3002\u6211\u6349\u5230\u4e00\u4e2a\u5c0f\u8d3c\u4e86\uff0c \u54a6\uff0c\u4f60\uff1b\u4f60\u600e\u4e48\u662f\u4f60\uff01\u201d\u706f\u5149\u4e00\u4eae\uff0c\u5389\u5357\u661f\u770b\u6e05\u695a\u4e86\u8fd9\u4e2a\u201c\u5c0f\u8d3c\u201d\u6b63\u662f\u4ed6\u5728\u53eb\u7740\u7684\u201c\u51af\u8001\u7237 \u5b50\u201d\uff0c\u4e0d\u89c9\u5446\u4e86\u3002 \u3000\u3000\u5389\u5357\u661f\u5446\u4e86\u4e00\u5446\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u51af\u8001\u7237\u5b50\uff0c\u4f60\u65e2\u7136\u6551\u4e86\u6211\u7684\u6027\u547d\u3001\u56e0\u4f55\u53c8\u8981\u5bb3\u6211\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u7741\u5927\u4e86\u773c\u775b\uff0c\u5589\u5934\u54af\u54af\u4f5c\u54cd\uff0c\u5fc3\u91cc\u5df2\u5728\u60f3\u597d\u8fa9\u8f9e\u3001\u82e6\u4e8e\u8bf4\u4e0d\u51fa\u53e3\u3002 \u3000\u3000\u5389\u5357\u661f\u84e6\u5730\u60f3\u8d77\u4ed6\u662f\u7ed9\u81ea\u5df1\u7528\u91cd\u624b\u6cd5\u70b9\u4e86\u7a74\u9053\u7684\uff0c\u7a74\u9053\u672a\u89e3\uff0c\u7109\u80fd\u8bf4\u8bdd\uff1f\u6b63\u8981\u7ed9\u5c01\u5b50\u8d85 \u89e3\u5f00\u7a74\u9053\uff0c\u5ffd\u542c\u5f97\u6709\u4e2a\u98a4\u6296\u7684\u58f0\u97f3\u5c16\u53eb\uff1a\u201c\u5389\u516c\u5b50\uff0c\u624b\u4e0b\u7559\u60c5\uff01\u201d\u539f\u6765\u662f\u5c01\u5999\u5ae6\u7ed9\u524d\u8231\u7684\u58f0 \u97f3\u60ca\u9192\uff0c\u7a7f\u4e86\u7761\u8863\u5c31\u8dd1\u51fa\u6765\uff0c\u6070\u597d\u770b\u89c1\u5389\u5357\u661f\u4e3e\u8d77\u624b\u638c\u5411\u5979\u7236\u4eb2\u62cd\u4e0b\uff0c\u5979\u53ea\u9053\u5389\u5357\u661f\u662f\u8981\u53d6 \u5979\u7236\u4eb2\u7684\u6027\u547d\uff0c\u5374\u4e0d\u77e5\u5389\u5357\u661f\u5728\u7ed9\u5979\u7236\u4eb2\u89e3\u7a74\u3002 \u3000\u3000\u5389\u5357\u661f\u5fc3\u5934\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u5bf9\uff0c\u5f85\u6211\u95ee\u660e\u771f\u76f8\uff0c\u518d\u7ed9\u4ed6\u89e3\u7a74\u4e5f\u8fd8\u4e0d\u8fdf\uff0c\u4fd7\u8bed\u8bf4\u5f97\u597d\uff0c\u5bb3 \u4eba\u4e4b\u5fc3\u4e0d\u53ef\u6709\uff0c\u9632\u4eba\u4e4b\u5fc3\u4e0d\u53ef\u65e0\u3002\u6211\u73b0\u5728\u6b66\u529f\u672a\u66fe\u6062\u590d\uff0c\u603b\u662f\u5c0f\u5fc3\u4e3a\u4e0a\u3002\u8fd9\u8001\u5934\u76ee\u524d\u4e5f\u8fd8\u4e0d \u77e5\u662f\u53cb\u662f\u654c\uff0c\u5c31\u8ba9\u4ed6\u591a\u5403\u4e00\u70b9\u82e6\u5934\uff0c\u4e5f\u4e0d\u4e3a\u8fc7\u3002\u201d\u5f53\u4e0b\u7f29\u56de\u624b\u638c\uff0c\u8bf4\u9053\uff1a\u201c\u51af\u59d1\u5a18\uff0c\u4f60\u6765\u5f97 \u597d\uff0c\u6211\u6b63\u8981\u8bf7\u6559\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u5fcd\u4f4f\u773c\u6cea\u8bf4\u9053\uff1a\u201c\u7239\u7239\u505a\u51fa\u8fd9\u6837\u7684\u4e8b\u60c5\u6765\uff0c\u6211\u5b9e\u5728\u6ca1\u6709\u989c\u9762\u6c42\u4f60\u2026\u2026\u201d \u3000\u3000\u5389\u5357\u661f\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u7239\u7239\u662f\u5b58\u5fc3\u8981\u5bb3\u6211\u7684\u4e86\uff1f\u51af\u59d1\u5a18\uff0c\u8fd9\u53c8\u662f\u4e3a\u4f55 \u5462\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6211\u4e0d\u59d3\u51af\uff0c\u6211\u59d3\u5c01\uff0c\u5c01\u95ed\u7684\u5c01\u3002\u6211\u540d\u53eb\u5c01\u5999\u5ae6\uff0c\u6211\u7239\u7239\u540d\u53eb\u5c01\u5b50\u8d85\u3002\u4f60\u542c \u8fc7\u6211\u4eec\u7236\u5973\u7684\u540d\u5b57\u5417\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u66fe\u542c\u5f97\u91d1\u9010\u6d41\u8bf4\u8fc7\u5c01\u5999\u5ae6\u548c\u79e6\u5143\u6d69\u7684\u4e8b\u60c5\uff0c\u5f53\u4e0b\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60 \u662f\u5c01\u59d1\u5a18\u3002\u201d\u4e0d\u5fc5\u5c01\u5999\u5ae6\u89e3\u91ca\uff0c\u5fc3\u91cc\u5df2\u662f\u660e\u767d\u51e0\u5206\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5c01\u5b50\u8d85\u8fd9\u53ae\u672c\u6765\u662f\u671d\u5ef7\u9e70 \u5927\uff0c\u602a\u4e0d\u5f97\u4ed6\u8981\u5bb3\u6211\u3002\u4f46\u8fd9\u4f4d\u5c01\u59d1\u5a18\u5374\u662f\u79e6\u5143\u6d69\u7684\u672a\u5a7f\u59bb\u5b50\uff0c\u548c\u5979\u7236\u4eb2\u5927\u4e0d\u76f8\u540c\u3002\u6211\u867d\u7136\u4e0d \u8ba4\u8bc6\u79e6\u5143\u6d69\uff0c\u4f46\u79e6\u5143\u6d69\u662f\u91d1\u9010\u6d41\u7684\u597d\u670b\u53cb\uff0c\u8fd9\u4f4d\u5c01\u59d1\u5a18\u4e5f\u5c31\u7b49\u4e8e\u662f\u6211\u7684\u5f1f\u59b9\u4e00\u822c\u4e86\uff0c\u6211\u7684\u6027 \u547d\u4e5f\u662f\u591a\u4e8f\u4e86\u5979\u6551\u7684\uff0c\u770b\u5728\u5979\u7684\u4efd\u4e0a\uff0c\u6211\u5012\u662f\u4e0d\u80fd\u4e0d\u624b\u4e0b\u7559\u60c5\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u5389\u5357\u661f\u7ed9\u5c01\u5b50\u8d85\u89e3\u5f00\u4e86\u7a74\u9053\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u9010\u6d41\u548c\u6211\u8bf4\u8fc7\u4f60\u7684\u4e8b\u60c5\uff0c\u4ed6\u4e0a\u6b21\u9976\u4f60\uff0c\u672c \u662f\u671b\u4f60\u9769\u9762\u6d17\u5fc3\u7684\uff0c\u8c01\u77e5\u4f60\u8fd8\u662f\u4e0d\u77e5\u6094\u6539\u3002\u4f60\u8bf4\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u5bb3\u6211\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u6ee1\u9762\u901a\u7ea2\uff0c\u5f3a\u8fa9\u9053\uff1a\u201c\u5389\u76f8\u516c\uff0c\u6211\u5e76\u975e\u60f3\u5bb3\u4f60\u7684\u6027\u547d\uff0c\u6211\u53ea\u662f\u60f3\u628a\u4f60\u660f\u8ff7\u4e4b\u540e\uff0c \u5c06\u4f60\u629b\u5f03\u5cb8\u4e0a\u3002\u56e0\u4e3a\uff0c\u56e0\u4e3a\u6211\u6015\u516d\u5408\u5e2e\u627e\u6211\u7684\u9ebb\u70e6\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u4e16\u6545\u4e0d\u6df1\uff0c\u542c\u4e86\u5c01\u5b50\u8d85\u8fd9\u4e48\u8bf4\uff0c\u5012\u662f\u89c9\u5f97\u60c5\u6709\u53ef\u539f\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u4e0d\u60f3\u8fde \u7d2f\u4f60\uff0c\u4f46\u73b0\u5728\u5374\u4e0d\u80fd\u4e0d\u501f\u7528\u4f60\u8fd9\u6761\u8239\u4e86\u3002\u4f60\u662f\u4e0d\u662f\u8fd8\u8981\u5bb3\u6211\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u6211\u5f88\u662f\u540e\u6094\uff0c\u4e3a\u4e86\u4e00\u70b9\u79c1\u5fc3\uff0c\u7adf\u60f3\u628a\u4f60\u629b\u5f03\u3002\u73b0\u5728\u6211\u5df2\u7ecf\u60f3\u901a\u4e86\uff0c\u6551\u4eba\u5e94 \u8be5\u6551\u5230\u5e95\uff0c\u5c31\u662f\u62c5\u5f53\u4e00\u70b9\u98ce\u9669\uff0c\u90a3\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u5389\u516c\u5b50\uff0c\u8ba9\u4f60\u539f\u8c05\u6211\u7684\u4e00\u65f6\u7cca\u6d82\u5427\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u4f60\u5f53\u771f\u662f\u60f3\u901a\u4e86\uff1f\u82e5\u6709\u6211\u7684\u4ec7\u5bb6\u8ffd\u5230\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u6697\u7b97\u6211\u4e48\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u53cc\u819d\u8dea\u4e0b\uff0c\u5bf9\u5929\u53d1\u8a93\uff1a\u201c\u82e5\u6709\u5f02\u5fc3\uff0c\u6559\u6211\u4e0d\u5f97\u597d\u6b7b\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u4e0d\u613f\u770b\u7236\u4eb2\u7684\u4e11\u6001\uff0c\u522b\u8f6c\u4e86\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f46\u613f\u4f60\u771f\u7684\u6094\u609f\uff0c\u4eca\u540e\u505a\u4e2a\u597d\u4eba\u3002 \u5426\u5219\u591a\u884c\u4e0d\u4e49\u5fc5\u81ea\u6bd9\uff0c\u91d1\u5927\u4fa0\u9976\u4e86\u4f60\uff0c\u5389\u516c\u5b50\u9976\u4e86\u4f60\uff0c\u7b2c\u4e09\u6b22\u78b0\u4e0a\u4fa0\u4e49\u9053\uff0c\u4eba\u5bb6\u5c31\u4e0d\u4e00\u5b9a\u4f1a \u9976\u4f60\u4e86\u3002\u201d\u5c01\u5999\u5ae6\u5fc3\u60c5\u751a\u4e3a\u590d\u6742\uff0c\u5979\u660e\u77e5\u7236\u4eb2\u8a00\u4e0d\u7531\u8877\uff0c\u4f46\u53c8\u4e0d\u613f\u5f53\u7740\u5389\u5357\u661f\u7684\u9762\u63ed\u7834\u3002\u53ea \u597d\u59d4\u5a49\u5730\u8bf4\u51fa\u8fd9\u756a\u8bdd\u6765\uff0c\u4e00\u9762\u662f\u529d\u8c0f\u5979\u7684\u7236\u4eb2\uff0c\u4e00\u9762\u662f\u6697\u4e2d\u63d0\u9192\u5389\u5357\u57a6\uff0c\u5bf9\u5979\u7236\u4eb2\u7684\u8bdd\uff0c\u4e0d \u53ef\u5168\u4fe1\u3002 \u3000\u3000\u5389\u5357\u661f\u5f53\u7136\u4e5f\u4e0d\u4f1a\u5b8c\u5168\u76f8\u4fe1\u5c01\u5b50\u8d85\uff0c\u4f46\u4ed6\u65e2\u7136\u51b3\u5b9a\u4e86\u4e0d\u6740\u5c01\u5b50\u8d85\uff0c\u5f53\u4e0b\u4e5f\u5c31\u4e0d\u60f3\u4ee4\u4ed6\u592a \u96be\u582a\u4e86\u3002\u5389\u5357\u661f\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u7684\u529f\u529b\u867d\u7136\u672a\u66fe\u6062\u590d\uff0c\u4f46\u4e5f\u7528\u4e0d\u4e86\u51e0\u5929\u5de5\u592b\u3002\u4e09\u5929\u4e4b\u5185\uff0c\u81f3 \u5c11\u53ef\u4ee5\u6062\u590d\u516b\u6210\u3002\u5c01\u5b50\u8d85\u6b66\u529f\u5e73\u5eb8\uff0c\u7ed9\u6211\u7528\u91cd\u624b\u6cd5\u70b9\u4e86\u7a74\u9053\uff0c\u81f3\u5c11\u4e5f\u5f97\u4e09\u5929\u624d\u80fd\u5b8c\u5168\u6062\u590d\u5143 \u6c14\u3002\u5373\u4f7f\u4ed6\u6709\u5f02\u5fc3\uff0c\u6211\u4e5f\u65e0\u987b\u60e7\u4ed6\u3002\u4f55\u51b5\u8fd8\u6709\u4ed6\u7684\u5973\u513f\u63a3\u8098\u4ed6\u5462\u3002\u201d \u3000\u3000\u8fd9\u4e48\u4e00\u60f3\uff0c\u5389\u5357\u661f\u5012\u662f\u5fc3\u5e73\u6c14\u548c\u7684\u548c\u5c01\u5b50\u8d85\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u4e09\u5929\u5c31\u53ef\u4ee5\u5230\u91d1\u9675\u4e86\uff0c\u4e00\u5230\u91d1 \u9675\uff0c\u6211\u5c31\u4f1a\u8d70\uff0c\u51b3\u4e0d\u8fde\u7d2f\u4e8e\u4f60\u3002\u6b64\u5730\u79bb\u6768\u5dde\u5df2\u8fdc\uff0c\u4e07\u4e00\u6709\u516d\u5408\u5e2e\u7684\u8239\u9053\u6765\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u662f\u5927\u5e2e \u7684\u8239\u961f\u3002\u5230\u65f6\u6211\u4e00\u4e2a\u4eba\u5e94\u4ed8\u5c31\u884c\uff0c\u4e0d\u5fc5\u4f60\u4eec\u7236\u5973\u51fa\u5934\u9732\u9762\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u88c5\u51fa\u4e00\u526f\u201c\u611f\u6fc0\u6d95\u96f6\u201d\u7684\u6837\u5b50\u8bf4\u9053\uff1a\u201c\u5389\u516c\u5b50\u8fd9\u6837\u4e3a\u6211\u4eec\u7740\u60f3\uff0c\u6211\u5b9e\u662f\u611f\u6fc0\u4e0d \u5c3d\u3002\u4f46\u6211\u591a\u8499\u4f60\u9976\u6055\u4e86\u6211\uff0c\u6211\u5374\u662f\u4e00\u5b9a\u8981\u5bf9\u5f97\u4f4f\u4f60\u7684\uff0c\u5018\u82e5\u6709\u4e8b\uff0c\u4f60\u4e0d\u8bb8\u6211\u51fa\u6765\uff0c\u6211\u4e5f\u7edd\u4e0d \u80fd\u8896\u624b\u65c1\u89c2\u3002\u201d \u3000\u3000\u5f53\u4e0b\u4e00\u573a\u98ce\u6ce2\u5c31\u6b64\u63ed\u8fc7\uff0c\u5c01\u5999\u5ae6\u4e0e\u7236\u4eb2\u56de\u8f6c\u524d\u8231\uff0c\u5389\u5357\u661f\u4e5f\u5c31\u653e\u5fc3\u7684\u7761\u89c9\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\u7ee7\u7eed\u5f00\u8239\uff0c\u5389\u5357\u661f\u5b9e\u8df5\u4ed6\u7684\u8bfa\u8a00\uff0c\u4e0e\u5c01\u5b50\u8d85\u8f6e\u6d41\u638c\u8236\u3002\u5bf9\u6628\u665a\u7684\u4e8b\uff0c\u5f7c\u6b64\u90fd \u907f\u514d\u518d\u63d0\u3002\u5c01\u5b50\u8d85\u7684\u6001\u5ea6\u751a\u4e3a\u606d\u987a\uff0c\u8fd9\u4e00\u5929\u5e73\u5b89\u5ea6\u8fc7\uff0c\u5e76\u6ca1\u6709\u4e8b\u60c5\u53d1\u751f\u3002 \u3000\u3000\u7b2c\u4e09\u5929\u7ee7\u7eed\u524d\u884c\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u8fdb\u5165\u5185\u6cb3\u822a\u9053\uff0c\u5c01\u5b50\u8d85\u6b63\u5728\u638c\u8235\uff0c\u5ffd\u5730\u770b\u89c1\u5cb8\u4e0a\u6709\u4eba\u9a91\u9a6c \u7ecf\u8fc7\uff0c\u5c01\u5999\u5ae6\u672a\u66fe\u7559\u795e\uff0c\u4ed6\u7684\u773c\u5c16\uff0c\u5df2\u7ecf\u770b\u89c1\uff0c\u8fd9\u4e2a\u4eba\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u6587\u9053\u5e84\u7684\u513f\u5b50\u6587\u80dc \u4e2d\u3002\u5c01\u5b50\u8d85\u66fe\u7ecf\u4e00\u5ea6\u6709\u8fc7\u610f\u601d\uff0c\u60f3\u628a\u5973\u513f\u5ac1\u7ed9\u4ed6\u7684\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u559c\u51fa\u671b\u5916\uff0c\u5374\u4e0d\u9732\u58f0\u8272\u5730\u8bf4\u9053\uff1a\u201c\u5389\u76f8\u516c\uff0c\u9ebb\u70e6\u4f60\u66ff\u6211\u638c\u4e00\u638c\u8235\u3002\u201d\u672c\u6765\u662f\u5c01\u5b50 \u8d85\u63a5\u624b\u672a\u4e45\uff0c\u4e0d\u5e94\u53c8\u8f6e\u5230\u5389\u5357\u661f\u638c\u8235\u7684\uff0c\u4f46\u5389\u5357\u661f\u5374\u6ca1\u7591\u5fc3\uff0c\u53ea\u9053\u4ed6\u5e74\u8001\u4f53\u8870\uff0c\u6545\u6b64\u8981\u81ea\u5df1 \u66ff\u6362\uff0c\u5f53\u4e0b\u5c31\u5766\u7136\u7684\u8fc7\u53bb\u63a5\u624b\u628a\u8235\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u628a\u8235\u4e00\u63a8\uff0c\u5ffd\u5730\u9a88\u6307\u5982\u621f\uff0c\u5411\u5389\u5357\u661f\u8170\u95f4\u4e00\u6233\u3002\u5389\u5357\u661f\u521a\u521a\u63a5\u8fc7\u4e86\u8235\uff0c\u505a\u68a6\u4e5f\u60f3 \u4e0d\u5230\u8a00\u72b9\u5728\u8033\uff0c\u5c01\u5b50\u8d85\u53c8\u4f1a\u5bf9\u4ed6\u6697\u7b97\uff01\u51b7\u4e0d\u53ca\u9632\uff0c\u53ea\u89c9\u80c1\u4e0b\u4e00\u9ebb\uff0c\u7384\u94c1\u5b9d\u5251\u5df2\u662f\u7ed9\u4ed6\u593a\u53bb\u3002 \u4e0d\u8fc7\uff0c\u5c01\u5b50\u8d85\u60f3\u8981\u70b9\u5389\u5357\u661f\u7684\u7a74\u9053\uff0c\u5374\u4e5f\u672a\u80fd\u5f97\u624b\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u62a2\u4e86\u5b9d\u5251\uff0c\u7acb\u5373\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u6587\u4e16\u5144\uff0c\u5feb\u6765\uff0c\u5feb\u6765\uff01\u201d\u6587\u80dc\u4e2d\u6b63\u662f\u5949\u4e86\u53f2\u767d\u90fd\u4e4b \u547d\uff0c\u4e00\u8def\u6eaf\u6c5f\u800c\u4e0a\uff0c\u641c\u67e5\u5389\u5357\u661f\u7684\u4e0b\u843d\u7684\u3002\u542c\u5f97\u5c01\u5b50\u8d85\u53eb\u5524\uff0c\u98de\u9a6c\u8d76\u56de\uff0c\u4e00\u773c\u770b\u89c1\u5c01\u5999\u5ae6\u548c \u5389\u5357\u661f\u90fd\u5728\u8239\u4e0a\uff0c\u4e0d\u7531\u5f97\u5fc3\u82b1\u6012\u653e\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5c01\u8001\u524d\u8f88\uff0c\u4f60\u5feb\u628a\u8239\u9760\u5cb8\uff0c\u6211\u624d\u597d\u5e2e\u4f60\u7684 \u624b\u5440\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u5927\u6012\uff0c\u62ff\u8d77\u4e86\u4e00\u679d\u6868\uff0c\u559d\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u8fd8\u7b97\u662f\u4eba\u5417\uff1f\u5feb\u5feb\u628a\u5251\u4ea4\u56de\uff0c\u5426\u5219 \u53ef\u4f11\u602a\u6211\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u7b11\u9053\uff1a\u201c\u6709\u672c\u9886\u4f60\u5c31\u62ff\u53bb\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5251\u5df2\u51fa\u9798\uff0c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u5148\u4e0b \u624b\u4e3a\u5f3a\uff0c\u4fbf\u5411\u5389\u5357\u661f\u524a\u53bb\u3002 \u3000\u3000\u5389\u5357\u661f\u4e3e\u8d77\u4e86\u6868\uff0c\u5f53\u4f5c\u5c0f\u82b1\u67aa\u4f7f\uff0c\u4e00\u62db\u201c\u795e\u9f99\u5165\u6d77\u201d\uff0c\u523a\u5411\u5bf9\u65b9\u5c0f\u8179\uff0c\u662f\u4e00\u6280\u6728\u5934\u505a\u7684 \u6868\uff0c\u5374\u600e\u654c\u5f97\u4f4f\u7384\u94c1\u5b9d\u5251\uff0c\u53ea\u542c\u5f97\u201c\u548b\u5693\u201d\u4e00\u58f0\uff0c\u6868\u7ed9\u524a\u4e86\u4e00\u622a\u3002 \u3000\u3000\u5389\u5357\u661f\u8eab\u5f62\u4e00\u6643\uff0c\u4e0d\u9000\u53cd\u8fdb\uff0c\u534a\u679d\u65ad\u6868\u604d\u4f3c\u7075\u86c7\u6e38\u8d70\uff0c\u4f38\u7f29\u4e0d\u5b9a\uff0c\u4f3c\u5de6\u4f3c\u53f3\u4f3c\u4e2d\uff0c\u904d\u88ad \u5c01\u5b50\u8d85\u8eab\u4e0a\u7684\u4e03\u5904\u7a74\u9053\uff0c\u8fd9\u662f\u4ece\u8ffd\u98ce\u5251\u5f0f\u5316\u51fa\u6765\u7684\u4e00\u62db\uff0c\u5c01\u5b50\u8d85\u51e0\u66fe\u89c1\u8fc7\uff0c\u53ea\u597d\u6500\u5251\u9632\u8eab\u3002 \u8fd9\u4e00\u6b21\u5389\u5357\u661f\u4e00\u51fb\u4e0d\u4e2d\uff0c\u5373\u628a\u65ad\u6868\u6536\u56de\uff0c\u6ca1\u6709\u7ed9\u4ed6\u524a\u7740\u3002 \u3000\u3000\u5389\u5357\u661f\u770b\u89c1\u6587\u80dc\u4e2d\u5df2\u7ecf\u7b56\u9a6c\u6765\u5230\u5cb8\u8fb9\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u8001\u8d3c\u5012\u4e16\u9887\u6709\u51e0\u5206\u786c\u4efd\uff0c \u8bf4\u4e0d\u5f97\u6211\u53ea\u597d\u5192\u70b9\u98ce\u9669\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u5389\u5357\u661f\u56e0\u4e3a\u90a3\u665a\u5236\u4f0f\u5c01\u5b50\u8d85\u592a\u8fc7\u5bb9\u6613\uff0c\u662f\u4ee5\u5e76\u4e0d\u600e\u6837\u628a\u4ed6\u653e\u5728\u5fc3\u4e0a\u3002\u4e5f\u6b63\u662f\u56e0\u4e3a\u8fd9 \u4e2a\u7f18\u6545\uff0c\u5389\u5357\u661f\u5728\u906d\u53d7\u4ed6\u4e00\u6b21\u6697\u7b97\u4e4b\u540e\uff0c\u8fd8\u6562\u5766\u7136\u4e0e\u4ed6\u540c\u5904\u4e00\u8239\u3002\u6b8a\u4e0d\u77e5\u5c01\u5b50\u8d85\u7684\u672c\u9886\u867d\u7136 \u6bd4\u4e0d\u4e0a\u4ed6\uff0c\u5374\u4e5f\u5e76\u975e\u6cdb\u6cdb\u4e4b\u8f88\u3002\u5982\u4eca\u4ed6\u6709\u7384\u94c1\u5b9d\u5251\u5728\u624b\uff0c\u800c\u5389\u5357\u661f\u7684\u529f\u529b\u53c8\u672a\u6062\u590d\uff0c\u6b64\u6d88\u5f7c \u957f\uff0c\u5f62\u52bf\u53cd\u800c\u662f\u5bf9\u5389\u5357\u661f\u5927\u5927\u7684\u4e0d\u5229\u4e86\u3002 \u3000\u3000\u4f46\u5c01\u5b50\u8d85\u7ed9\u4ed6\u51e0\u8bb0\u7cbe\u5999\u7684\u62db\u6570\uff0c\u653b\u5f97\u4e5f\u662f\u6709\u70b9\u5fc3\u614c\u3002\u5f53\u4e0b\u4ed6\u4e00\u9762\u821e\u5251\u9632\u8eab\uff0c\u4e00\u9762\u628a\u8239\u6447 \u5411\u5bf9\u5cb8\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u89c1\u4e24\u4eba\u52a8\u8d77\u624b\u6765\uff0c\u66f4\u662f\u5413\u5f97\u5446\u4e86\uff0c\u534a\u664c\u65b9\u59cb\u53eb\u5f97\u51fa\u6765\uff1a\u201c\u7239\uff0c\u4f60\u600e\u4e48\u53ef\u4ee5\u8fd9 \u6837\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u5973\u751f\u5916\u5411\uff0c\u8fd9\u8bdd\u5f53\u771f\u8bf4\u5f97\u4e0d\u9519\u3002\u597d\u554a\uff0c\u4f60\u82e5\u662f\u770b\u4e0d\u987a\u773c\uff0c\u4f60\u5c31\u548c\u8fd9 \u5c0f\u5b50\u5e76\u80a9\u513f\u4e0a\uff0c\u5bf9\u4ed8\u4f60\u7684\u8001\u5b50\u5427\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u6c14\u607c\u4e4b\u6781\uff0c\u53ef\u662f\u5979\u53c8\u600e\u80fd\u548c\u81ea\u5df1\u7684\u4eb2\u751f\u7236\u4eb2\u52a8\u624b\uff0c\u6c14\u6025\u4e4b\u4e0b\uff0c\u4e0d\u7981\u201c\u54c7\u201d\u7684\u4e00\u58f0 \u54ed\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5389\u5357\u661f\u6b3a\u8eab\u8fdb\u62db\uff0c\u5c01\u5b50\u8d85\u5251\u5149\u4e00\u5408\uff0c\u201c\u548b\u5693\u201d\u4e00\u54cd\uff0c\u534a\u679d\u65ad\u6868\uff0c\u53c8\u518d\u524a\u53bb\u4e86\u4e00\u534a\uff0c\u5269\u4e0b \u6765\u7684\u5df2\u4e0d\u5230\u4e24\u5c3a\u957f\u4e86\u3002 \u3000\u3000\u773c\u770b\u5389\u5357\u661f\u5c31\u8981\u4e27\u547d\u5b9d\u5251\u4e4b\u4e0b\uff0c\u5c01\u5999\u5ae6\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u8981\u6740 \u4ed6\uff0c\u8fde\u6211\u4e5f\u6740\u4e86\u5427\uff01\u201d\u594b\u4e0d\u987e\u8eab\u7684\u5c31\u6251\u4e0a\u53bb\uff0c\u60f3\u7528\u81ea\u5df1\u7684\u8eab\u4f53\u66ff\u5389\u5357\u661f\u6321\u8fd9\u4e00\u5251\uff0c\u597d\u53eb\u5c01\u5b50 \u8d85\u6709\u6240\u987e\u5fcc\uff0c\u672a\u5fc5\u6562\u4e0b\u6740\u624b\u3002 \u3000\u3000\u54ea\u77e5\u5c01\u5999\u5ae6\u7684\u52a8\u4f5c\u867d\u5feb\uff0c\u5374\u4ecd\u662f\u6162\u4e86\u4e00\u6b65\uff0c\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u53ea\u89c1\u5251\u5149\u4eba\u5f71\uff0c\u5df2\u662f\u7ea0\u7f20 \u5728\u4e00\u8d77\u4e86\uff0c\u5c01\u5999\u5ae6\u5fc3\u5934\u4e00\u60ca\uff0c\u65b9\u81ea\u6697\u53eb\uff1a\u201c\u7cdf\u7cd5\uff01\u201d\u5ffd\u542c\u5f97\u5c01\u5b50\u8d85\u7a81\u7136\u4e00\u58f0\u5c16\u53eb\uff0c\u7384\u94c1\u5b9d\u5251 \u8131\u624b\u98de\u51fa\uff01 \u3000\u3000\u539f\u6765\u5389\u5357\u661f\u5728\u90a3\u679d\u6868\u7ed9\u524a\u6210\u4e86\u77ed\u77ed\u7684\u4e00\u622a\u4e4b\u540e\uff0c\u7d22\u6027\u4e00\u4e0d\u505a\u4e8c\u4e0d\u4f11\uff0c\u5c31\u62ff\u8fd9\u4e00\u622a\u6868\u5f53\u4f5c \u5224\u5b98\u7b14\u4f7f\u7528\uff0c\u500f\u5730\u6b3a\u8eab\u8fdb\u62db\uff0c\u524a\u5c16\u4e86\u7684\u6868\u6b63\u597d\u53ef\u5f53\u7b14\u5c16\uff0c\u4e00\u70b9\u5c31\u70b9\u7740\u4e86\u5c01\u5b50\u8d85\u7684\u624b\u8155\u3002 \u3000\u3000\u6b64\u65f6\u5c0f\u8239\u5df2\u7ecf\u9760\u62e2\u5cb8\u8fb9\uff0c\u5c01\u5b50\u8d85\u7684\u5b9d\u5251\u8131\u624b\u98de\u51fa\uff0c\u5374\u662f\u5411\u5cb8\u4e0a\u98de\u53bb\u7684\u3002\u6587\u80dc\u4e2d\u4fa7\u8eab\u4e00 \u95ea\uff0c\u6293\u7740\u5251\u67c4\uff0c\u628a\u7384\u94c1\u5b9d\u5251\u63a5\u5230\u624b\u4e2d\uff0c\u54c8\u54c8\u5927\u7b11\u3002\u5c01\u5b50\u8d85\u8eab\u5f62\u4e00\u8d77\uff0c\u4f7f\u51fa\u201c\u4e00\u9e64\u51b2\u5929\u201d\u7684\u8f7b \u529f\uff0c\u8ddf\u7740\u4e5f\u8df3\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5389\u5357\u661f\u6697\u6697\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u53ef\u60dc\uff01\u201d\u539f\u6765\u5730\u662f\u51c6\u5907\u5728\u593a\u5251\u4e4b\u540e\uff0c\u63a5\u7740\u5c31\u8981\u6293\u788e\u5c01\u5b50\u8d85\u7684\u7435 \u7436\u9aa8\u7684\uff0c\u4f46\u7531\u4e8e\u5c01\u5999\u5ae6\u5df2\u7ecf\u6251\u5230\uff0c\u906e\u5728\u4ed6\u7684\u8eab\u524d\uff0c\u5c01\u5999\u5ae6\u7684\u539f\u610f\u662f\u8981\u63a9\u62a4\u4ed6\u7684\uff0c\u5f62\u52bf\u4e00\u53d8\uff0c \u53cd\u800c\u53d8\u6210\u4e86\u66ff\u5979\u7236\u4eb2\u63a9\u62a4\uff0c\u4ee5\u81f4\u5389\u5357\u661f\u8ba1\u5212\u843d\u7a7a\uff0c\u5b9d\u5251\u593a\u4e0d\u6210\uff0c\u4eba\u4e5f\u6ca1\u6293\u5230\u3002 \u3000\u3000\u5389\u5357\u661f\u5931\u4e86\u7384\u94c1\u5b9d\u5251\uff0c\u7109\u80af\u7f62\u4f11\uff0c\u5c01\u5b50\u8d85\u524d\u811a\u7740\u5730\uff0c\u4ed6\u8ddf\u7740\u540e\u811a\u8ffd\u4e0a\u5cb8\u6765\u3002\u6587\u80dc\u4e2d\u9053\uff1a \u201c\u5c01\u8001\u4f2f\uff0c\u4f60\u8fd9\u67c4\u5251\u501f\u6211\u4e00\u7528\u3002\u8bf7\u4f60\u529d\u529d\u4ee4\u5a9b\uff0c\u81f3\u4e8e\u8fd9\u5c0f\u5b50\u5c31\u4ea4\u7ed9\u6211\u597d\u5566\uff01\u201d\u5b9d\u5251\u5df2\u7ecf\u843d\u5165 \u6587\u80dc\u4e2d\u4e4b\u624b\uff0c\u5c01\u5b50\u8d85\u81ea\u662f\u4e0d\u4fbf\u8ba8\u56de\uff0c\u660e\u77e5\u8fd9\u662f\u201c\u5218\u5907\u501f\u8346\u5dde\u201d\uff0c\u5374\u4e5f\u4e0d\u80fd\u4e0d\u201c\u501f\u201d\u3002\u201c\u5973\u513f \u5ac1\u4e0d\u6210\u53f2\u767d\u90fd\u5ac1\u7ed9\u6587\u80dc\u4e2d\u4e5f\u5f88\u4e0d\u9519\uff0c\u4f46\u6c42\u4ed6\u6740\u4e86\u8fd9\u59d3\u5389\u7684\u5c0f\u5b50\uff0c\u7384\u94c1\u5b9d\u5251\u5c31\u5f53\u4f5c\u6211\u7ed9\u5973\u513f\u7684 \u966a\u5ac1\u5427\u3002\u201d\u5c01\u5b50\u8d85\u5fc3\u60f3\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u6b64\u65f6\u4ea6\u5df2\u4e0a\u4e86\u5cb8\uff0c\u89e3\u4e0b\u4f69\u5251\uff0c\u9012\u7ed9\u5389\u6d77\u661f\u9053\uff1a\u201c\u5389\u516c\u5b50\uff0c\u6211\u8fd9\u628a\u5251\u501f\u7ed9\u4f60\u3002\u201d\u7236 \u5973\u5404\u81ea\u628a\u5251\u201c\u501f\u201d\u7ed9\u4e00\u65b9\uff0c\u6b63\u597d\u662f\u5531\u4e0a\u4e86\u5bf9\u53f0\u620f\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u5403\u4e0a\u4e86\u65e0\u540d\u918b\uff0c\u4e0d\u7531\u5f97\u6740\u673a\u9661\u8d77\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u5c01\u59d1\u5a18\uff0c\u539f\u6765\u4f60\u5df2\u7ecf\u629b\u5f00\u4e86\u79e6\u5143 \u6d69\u90a3\u5c0f\u5b50\u5417\uff1f\u4e0b\u4e00\u6b21\u5e94\u8be5\u8f6e\u5230\u6211\u4e86\u5427\uff01\u201d\u5c01\u5999\u5ae6\u6c14\u5f97\u7834\u53e3\u5927\u9a82\uff1a\u201c\u72d7\u5634\u91cc\u4e0d\u957f\u8c61\u7259\uff0c\u653e\u4f60\u7684 \u5c41\uff01\u201d \u3000\u3000\u6587\u80dc\u4e2d\u607c\u6012\u4e4b\u6781\uff0c\u4f46\u5374\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u59d3\u5389\u8fd9\u5c0f\u5b50\u8fd8\u6d3b\u5728\u8fd9\u513f\uff0c\u5f53\u7136\u8f6e\u4e0d\u5230\u6211\u3002\u6ca1 \u6cd5\u5b50\uff0c\u6211\u53ea\u597d\u66ff\u960e\u738b\u53d1\u8bf7\u5e16\u4e86\u3002\u201d\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u4e00\u6b65\u4e00\u6b65\u7684\u5411\u5389\u5357\u661f\u903c\u8fd1\u3002 \u3000\u3000\u5389\u5357\u661f\u60c5\u77e5\u654c\u5f3a\u5df1\u5f31\uff0c\u5fc5\u987b\u51b7\u9759\u5bf9\u4ed8\uff0c\u662f\u4ee5\u4ed6\u53cc\u76ee\u6ce8\u89c6\u6587\u80dc\u4e2d\u7684\u52a8\u4f5c\uff0c\u4e1d\u6beb\u4e5f\u4e0d\u52a8\u3002\u5f85 \u5230\u6587\u80dc\u4e2d\u8d70\u5f97\u8fd1\u4e86\uff0c\u5389\u5357\u661f\u9661\u5730\u559d\u58f0\uff1a\u201c\u4f4f\u5634\uff01\u201d\u5530\u7684\u4e00\u5251\u5c31\u523a\u8fc7\u53bb\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u6a2a\u5251\u4e00\u5c01\uff0c\u8fc5\u5373\u4e00\u62db\u201c\u63a8\u7a97\u95ed\u6708\u201d\u5f84\u524a\u51fa\u53bb\uff0c\u5634\u91cc\u51b7\u7b11\u9053\uff1a\u201c\u81ed\u5c0f\u5b50\uff0c\u4f60\u627e\u6b7b\uff01 \u54ce\u5440\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u6587\u80dc\u4e2d\u7684\u8863\u895f\u5df2\u662f\u7ed9\u5251\u5c16\u6311\u7834\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u53eb\u9053\uff1a\u201c\u6587\u4e16\u5144\u4e0d\u53ef\u8f7b\u654c\uff0c\u8fd9\u5c0f\u5b50\u662f\u91d1\u9010\u6d41\u7684\u628a\u5144\uff01\u201d\u6587\u80dc\u4e2d\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u6c89 \u4f4f\u4e86\u6c14\uff0c\u53cd\u624b\u5251\u4e00\u6325\uff0c\u9752\u7b4b\u66b4\u6da8\uff0c\u4f7f\u7684\u662f\u201c\u7389\u5e26\u56f4\u8170\u201d\u7684\u62db\u6570\uff0c\u5b9d\u5251\u5149\u8292\u597d\u50cf\u4e00\u5708\u94f6\u73af\uff0c\u62a4 \u4f4f\u81ea\u8eab\uff0c\u53cd\u51fb\u654c\u4eba\u3002 \u3000\u3000\u5389\u5357\u661f\u624b\u4e2d\u62ff\u7684\u662f\u4e00\u628a\u666e\u666e\u901a\u901a\u7684\u9752\u94a2\u5251\uff0c\u9012\u4e0d\u8fdb\u5251\u62db\uff0c\u53ea\u597d\u64a4\u56de\u3002\u6587\u80dc\u4e2d\u5f97\u7406\u4e0d\u9976 \u4eba\uff0c\u5530\u5530\u6dae\u8fde\u73af\u4e09\u5251\uff0c\u5389\u5357\u661f\u5c55\u5f00\u4e86\u201c\u5de7\u6c14\u7f57\u6b65\u6cd5\u201d\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u867d\u7136\u4e0d\u80fd\u653b\u8fdb\u5bf9 \u65b9\u7684\u9632\u5fa1\u5708\u5185\uff0c\u5374\u4e5f\u6781\u5c3d\u58f0\u4e1c\u51fb\u897f\u7684\u80fd\u4e8b\u3002\u6587\u80dc\u4e2d\u8fd9\u8fde\u73af\u4e09\u5251\uff0c\u8fde\u4ed6\u7684\u8863\u89d2\u4e5f\u6ca1\u6cbe\u4e0a\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u903c\u9000\u4e86\u5389\u5357\u661f\u4e00\u6b65\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u91d1\u9010\u6d41\u7684\u628a\u5144\u53c8\u600e\u4e48\u6837\uff1f\u201d\u8bdd\u867d \u5982\u6b64\uff0c\u5fc3\u91cc\u5374\u4e5f\u4e0d\u65e0\u602f\u610f\uff0c\u81ea\u5fd6\uff1a\u4e8f\u5728\u6211\u593a\u4e86\u8fd9\u628a\u5b9d\u5251\uff0c\u5426\u5219\u53ea\u6015\u5f53\u771f\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u3002\u201d\u5389 \u5357\u661f\u6bd2\u4f24\u521d\u6108\uff0c\u529f\u529b\u672a\u590d\uff0c\u52a0\u4ee5\u5728\u8239\u4e0a\u53c8\u548c\u5c01\u5b50\u8d85\u6253\u4e86\u4e00\u573a\uff0c\u6c14\u529b\u81ea\u662f\u662f\u96be\u4ee5\u6301\u4e45\u3002\u6587\u80dc\u4e2d \u7684\u5bb6\u4f20\u5251\u6cd5\u867d\u7136\u4e0d\u53ca\u4ed6\u7684\u7cbe\u5999\uff0c\u4f46\u4e5f\u662f\u81ea\u6210\u4e00\u5bb6\u7684\u4e0a\u4e58\u5251\u6cd5\uff0c\u4ed7\u7740\u7384\u94c1\u5b9d\u5251\u4e4b\u5229\uff0c\u4e09\u5341\u62db\u8fc7 \u540e\uff0c\u5389\u5357\u661f\u5df2\u662f\u4e0d\u77e5\u4e0d\u89c9\u989d\u5934\u89c1\u6c57\uff0c\u5251\u62db\u4f7f\u51fa\uff0c\u6bcf\u6bcf\u529b\u4e0d\u4ece\u5fc3\u3002\u4e8f\u4e86\u4ed6\u7684\u201c\u5929\u7f57\u6b65\u6cd5\u201d\u8f7b\u7075 \u8fc5\u6377\uff0c\u8fd8\u8db3\u4ee5\u81ea\u4fdd\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u8d70\u5230\u5973\u513f\u9762\u524d\uff0c\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u963f\u5ae6\uff0c\u4f60\u4e0d\u8981\u751f\u6c14\uff0c\u7239\u7239\u662f\u4e3a\u4e86\u4f60\u597d\u3002\u201d\u5c01\u5999\u5ae6\u51b7 \u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u628a\u6211\u5f80\u706b\u5751\u91cc\u63a8\uff01\u201d\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u5982\u4eca\u6211\u4e0d\u8981\u4f60\u5f80\u626c\u5dde\u89c1\u53f2\u767d\u90fd\u4e86\uff0c\u8fd8\u80fd\u8bf4 \u6211\u4e0d\u987a\u4ece\u4f60\u7684\u610f\u601d\u5417\uff1f\u79e6\u5143\u6d69\u8fd9\u5c0f\u5b50\u6709\u4ec0\u4e48\u597d\uff0c\u8fd9\u5c0f\u5b50\u662f\u548c\u671d\u5ef7\u53db\u9006\u505a\u4e00\u8def\u7684\uff0c\u6211\u8bf4\uff0c\u4f60\u82e5 \u662f\u5ac1\u7ed9\u4ed6\u624d\u771f\u662f\u5f80\u706b\u5751\u91cc\u8df3\u5462\uff01\u8fd9\u4f4d\u6587\u4e16\u5144\u4e0e\u4f60\u65e9\u5df2\u76f8\u8bc6\uff0c\u4ed6\u662f\u6b66\u5b66\u540d\u5bb6\u4e4b\u5b50\uff0c\u5bf9\u4f60\u53c8\u662f\u8fd9 \u4e48\u597d\u2026\u2026\u201d \u3000\u3000\u5c01\u5999\u5ae6\u63a9\u8033\u9053\uff1a\u201c\u4f60\u8fd9\u4e9b\u8bdd\u6c61\u4e86\u6211\u7684\u8033\u6735\uff0c\u6211\u4e0d\u8981\u542c\uff01\u201d\u5c01\u5b50\u8d85\u5927\u4e3a\u607c\u6012\uff0c\u4f46\u4e3a\u4e86\u60f3\u8981 \u5973\u513f\u4f9d\u987a\uff0c\u5374\u8fd8\u4e0d\u6562\u9a6c\u4e0a\u53d1\u4f5c\u3002\u5c01\u5999\u5ae6\u53c8\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u8981\u9a82\u6211\u662f\u4e0d\u5b5d\u7684\u5973\u513f\uff0c\u597d\u5427\uff0c\u4f60\u5c31 \u5f53\u8fd9\u4e2a\u5973\u513f\u65e9\u5df2\u6b7b\u4e86\uff0c\u4f60\u6839\u672c\u5c31\u6ca1\u6709\u6211\u8fd9\u4e2a\u5973\u513f\uff01\u201d\u5c01\u5b50\u8d85\u6012\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u3001\u4f60\u3001\u4f60 \u8fd9\u4e2b\u5934\u7adf\u6562\u9876\u649e\u7236\u4eb2\u4e86\u4e48\uff1f\u201d \u3000\u3000\u521a\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u542c\u5f97\u6709\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\u683c\u683c\u7b11\u9053\uff1a\u201c\u6709\u4eba\u6253\u67b6\u53c8\u6709\u4eba\u5435\u67b6\uff0c\u8fd9\u91cc\u53ef\u771f\u662f\u70ed \u95f9\u5f97\u7d27\u554a\uff01\u7239\uff0c\u4e0d\u8981\u8d70\u4e86\uff0c\u505c\u4e0b\u6765\u77a7\u77a7\u70ed\u95f9\u5427\uff01\u201d\u5c01\u5b50\u8d85\u62ac\u5934\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u6765\u4e86\u4e24\u7236\u5973\u3002 \u7236\u4eb2\u5927\u7ea6\u6709\u4e94\u5341\u591a\u5c81\u5e74\u7eaa\uff0c\u8eab\u7a7f\u84dd\u5e03\u5927\u8902\uff0c\u811a\u767b\u516d\u8033\u9ebb\u978b\uff0c\u624b\u4e2d\u62ff\u7740\u4e00\u652f\u7897\u53e3\u822c\u7c97\u5927\u7684\u65f1\u70df \u888b\uff0c\u5341\u8db3\u50cf\u662f\u4e2a\u58eb\u8001\u513f\u7684\u6a21\u6837\u3002\u5973\u513f\u5374\u662f\u4e2a\u4e8c\u5341\u5c81\u4e0d\u5230\u7684\u5c11\u5973\uff0c\u7a7f\u5f97\u6734\u7d20\u5927\u65b9\uff0c\u660e\u8273\u52a8\u4eba\u3002 \u7236\u5973\u7ad9\u5728\u4e00\u8d77\uff0c\u6b8a\u4e0d\u76f8\u79f0\uff0c\u82e5\u4e0d\u662f\u5979\u53eb\u8fd9\u4e00\u58f0\u201c\u7239\u201d\uff0c\u522b\u4eba\u771f\u60f3\u4e0d\u5230\u4ed6\u4eec\u662f\u7236\u5973\u4fe9\uff0c\u6b63\u662f\uff1a \u3000\u3000\u5343\u91cc\u59fb\u7f18\u7275\u4e00\u7ebf\uff0c\u6d88\u707e\u8131\u56f0\u4f1a\u4f73\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e00\u56de\u3000\u51e0\u756a\u60c6\u6005\u6b4c\u91d1\u7f15\u3000\u65e0\u9650\u4f24\u5fc3\u4ed8\u7389\u8427 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e09\u5341\u4e00\u56de\u3000\u51e0\u756a\u60c6\u6005\u6b4c\u91d1\u7f15\u3000\u65e0\u9650\u4f24\u5fc3\u4ed8\u7389\u8427 \u3000\u3000\u5c01\u5b50\u8d85\u89c1\u4e86\u8fd9\u7236\u5973\u4fe9\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff01\u539f\u6765\u8fd9\u4e2a\u4f3c\u571f\u8001\u513f\u6a21\u6837\u7684\u4eba\uff0c\u7adf\u662f\u7ea2\u7f28\u4f1a\u7684\u8235 \u4e3b\u516c\u5b59\u5b8f\uff01\u7ea2\u7f28\u4f1a\u662f\u8db3\u4ee5\u5728\u6c5f\u6e56\u4e0a\u4e0e\u516d\u5408\u5e2e\u5206\u5ead\u6297\u793c\u7684\u4e00\u5927\u5e2e\u4f1a\uff0c\u800c\u4e14\u5c01\u5b50\u8d85\u77e5\u9053\uff0c\u516c\u5b59\u5b8f \u7684\u672c\u9886\u6df1\u4e0d\u53ef\u6d4b\uff0c\u4ed6\u867d\u7136\u6ca1\u6709\u89c1\u8fc7\uff0c\u4e5f\u66fe\u542c\u4eba\u8bf4\u8fc7\uff0c\u8bf4\u662f\u53ea\u6709\u5728\u53f2\u767d\u90fd\u4e4b\u4e0a\uff0c\u7edd\u4e0d\u5728\u53f2\u767d\u90fd \u4e4b\u4e0b\uff01 \u3000\u3000\u5c01\u5b50\u8d85\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e2b\u5934\u60f3\u5fc5\u662f\u4ed6\u7684\u5973\u513f\u516c\u5b59\u71d5\u4e86\u3002\u542c\u8bf4\u5979\u6700\u5f97\u7236\u4eb2\u7684\u5ba0\u7231\uff0c\u8ddf\u5979\u7236\u4eb2\u51fa \u6765\u8d70\u9053\u8fd8\u4e0d\u5230\u4e00\u5e74\uff0c\u5374\u6bd4\u5979\u7236\u4eb2\u66f4\u7231\u7ba1\u95f2\u4e8b\uff0c\u8bb8\u591a\u6c5f\u6e56\u4e0a\u6210\u540d\u7684\u4eba\u7269\u90fd\u6015\u4e86\u5979\u3002\u7cdf\u7cd5\uff0c\u7cdf \u7cd5\uff0c\u5979\u8fd9\u4e48\u4e00\u770b\u70ed\u95f9\uff0c\u53ea\u6015\u4f1a\u770b\u51fa\u5c94\u5b50\u6765\uff01\u800c\u4e14\u4e48\u5b59\u5b8f\u89c1\u591a\u8bc6\u5e7f\uff0c\u6587\u80dc\u4e2d\u7684\u6b66\u529f\u6765\u5386\u53ea\u6015\u4e5f \u7792\u4e0d\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u679c\u7136\u4fbf\u542c\u5f97\u516c\u5b59\u5b8f\u8bf4\u9053\uff1a\u201c\u5514\uff0c\u8fd9\u573a\u67b6\u6253\u5f97\u679c\u7136\u662f\u6709\u51e0\u5206\u7cbe\u5f69\u3002\u71d5\u513f\uff0c\u4f60\u6ca1 \u6709\u89c1\u8fc7\u5929\u5c71\u5251\u6cd5\uff0c\u8fd9\u6b21\u53ef\u4ee5\u5f00\u5f00\u773c\u754c\u4e86\uff0c\u548c\u4ed6\u4f5c\u5bf9\u624b\u7684\u8fd9\u5c0f\u5b50\u5251\u6cd5\u5dee\u4e9b\uff0c\u4f46\u7ec3\u7684\u4e09\u8c61\u795e\u529f\u4e5f \u4f3c\u4e4e\u5df2\u662f\u4e4d\u7aa5\u85e9\u7bf1\u4e86\u3002\u201d\u516c\u5b59\u5b8f\u662f\u5728\u7fa4\u96c4\u5927\u95f9\u8428\u5e9c\u4e4b\u65f6\uff0c\u89c1\u8fc7\u5389\u5357\u661f\u7684\uff0c\u4f46\u5374\u6ca1\u6709\u89c1\u8fc7\u6587\u80dc \u4e2d\u3002\u90a3\u6b21\u8428\u5e9c\u8d3a\u5bff\uff0c\u6587\u80dc\u4e2d\u6ca1\u6709\u968f\u4ed6\u7236\u4eb2\u540c\u5f80\u3002\u516c\u5b59\u5b8f\u662f\u4ece\u4ed6\u7684\u6b66\u529f\u5bb6\u6570\u770b\u51fa\u4ed6\u7684\u6765\u5386\u7684\u3002 \u516c\u5b59\u5b8f\u5fc3\u91cc\u60f3\uff1a\u201c\u6587\u9053\u5e84\u7684\u513f\u5b50\u6599\u60f3\u4e0d\u4f1a\u662f\u4ec0\u4e48\u597d\u4e1c\u897f\uff0c\u4f46\u6211\u600e\u80fd\u548c\u4e00\u4e2a\u540e\u751f\u665a\u8f88\u52a8\u624b\uff0c\u53ef \u6709\u4ec0\u4e48\u529e\u6cd5\u5e2e\u4e00\u5e2e\u5389\u5357\u661f\u7684\u5fd9\u5462\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u5929\u5c71\u5251\u6cd5\u7684\u786e\u5f88\u662f\u5947\u5999\uff0c\u53ef\u662f\u770b\u8d77\u6765\u4ed6\u597d\u50cf\u8fd8\u6253\u4e0d\u8fc7\u4ed6\u7684\u5bf9\u624b\uff0c\u8fd9\u662f\u4ec0\u4e48 \u7f18\u6545\uff1f\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u8fd9\u662f\u56e0\u4e3a\u4ed6\u65b0\u8fd1\u75c5\u4e86\u4e00\u573a\u7684\u7f18\u6545\u3002\u800c\u4e14\u4ed6\u7684\u5bf9\u624b\u7528\u7684\u662f\u7384\u94c1\u5b9d\u5251\uff0c\u6bd4\u666e\u901a \u7684\u5251\u8981\u91cd\u5341\u500d\uff0c\u4f60\u770b\u4e0d\u51fa\u6765\u4e48\uff1f\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4ee4\u5f97\u6587\u80dc\u4e2d\u5927\u5927\u5403\u60ca\u3002\u5fc3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8fd9\u571f\u8001\u513f\u7adf\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u5bb6\uff01\u4ed6\u4e0d\u4f46 \u4e00\u773c\u5c31\u77a7\u51fa\u6211\u7684\u529f\u592b\u6df1\u6d45\uff0c\u800c\u4e14\u8fd8\u77e5\u9053\u8fd9\u662f\u7384\u94c1\u5b9d\u5251\u3002\u201d\u5389\u6d77\u661f\u4e5f\u662f\u597d\u751f\u60ca\u8be7\uff0c\u5fc3\u60f3\uff1a\u201c\u516c \u5b59\u5b8f\u786e\u662f\u540d\u4e0d\u865a\u4f20\uff0c\u53ea\u4e00\u773c\u5c31\u77e5\u9053\u6211\u66fe\u7ecf\u53d7\u8fc7\u4f24\u3002\u542c\u9010\u6d41\u8bf4\uff0c\u90a3\u6b21\u4ed6\u95ef\u51fa\u8428\u5e9c\uff0c\u66fe\u5f97\u5230\u516c\u5b59 \u5b8f\u5f88\u5927\u7684\u5e2e\u5fd9\u3002\u4e0d\u8fc7\u6211\u5374\u4e0d\u80fd\u5b58\u7740\u501a\u8d56\u522b\u4eba\u7684\u5ff5\u5934\u3002\u201d \u3000\u3000\u6587\u80dc\u4e2d\u7d20\u6765\u72c2\u5984\uff0c\u542c\u4e86\u516c\u5b59\u5b8f\u5728\u65c1\u8fb9\u7684\u8bc4\u8bba\u201d\u8bed\u6c14\u4e4b\u4e2d\uff0c\u5206\u660e\u662f\u62ac\u9ad8\u4e86\u5389\u5357\u661f\u800c\u538b\u4f4e \u4ed6\uff0c\u5018\u82e5\u4e0d\u662f\u4ed6\u542c\u51fa\u516c\u5b59\u5b8f\u662f\u4e2a\u6b66\u5b66\u5927\u884c\u5bb6\uff0c\u5f53\u65f6\u5c31\u60f3\u53d1\u4f5c\u3002\u4f46\u867d\u7136\u4e0d\u6562\u53d1\u4f5c\uff0c\u4e5f\u662f\u6c14\u6124\u4e0d \u582a\u3002\u5f53\u4e0b\u628a\u6d51\u8eab\u672c\u9886\u90fd\u4f7f\u51fa\u6765\uff0c\u6325\u52a8\u7384\u94c1\u5b9d\u5251\uff0c\u7740\u7740\u8fdb\u903c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u6bd4\u4e0d\u4e0a\u8fd9\u5c0f \u5b50\uff0c\u6211\u5c31\u628a\u8fd9\u5c0f\u5b50\u6740\u4e86\u7ed9\u4f60\u770b\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u4e0d\u613f\u5728\u516c\u5b59\u5b8f\u9762\u524d\u4e22\u8138\uff0c\u5f53\u4e0b\u4e5f\u662f\u6296\u64de\u7cbe\u795e\uff0c\u62fc\u529b\u6076\u6597\u3002\u4f46\u53ef\u60dc\u4ed6\u6c14\u529b\u4e0d\u4f73\uff0c\u5175 \u5668\u4e0a\u53c8\u5403\u4e86\u4e8f\uff0c\u7ec8\u4e8e\u8fd8\u662f\u7ed9\u6587\u80dc\u4e2d\u903c\u5f97\u6b65\u6b65\u540e\u9000\u3002\u201d \u3000\u3000\u6fc0\u6218\u4e2d\u6587\u80dc\u4e2d\u4e00\u62db\u201c\u529b\u5288\u534e\u5c71\u201d\uff0c\u628a\u5b9d\u5251\u62a1\u5706\uff0c\u5f53\u4f5c\u5927\u5200\u6765\u4f7f\uff0c\u6a2a\u6240\u8fc7\u53bb\u3002\u5389\u5357\u661f\u95ea\u5230 \u4e00\u68f5\u67f3\u6811\u540e\u9762\uff0c\u53ea\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u7384\u94c1\u5b9d\u5251\u7adf\u7136\u628a\u8fd9\u68f5\u67f3\u6811\u5f53\u4e2d\u65ab\u65ad\u3002 \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u7684\u786e\u662f\u4e00\u628a\u4e16\u6240\u7f55\u89c1\u7684\u5b9d\u5251\u3002\u201d\u5ffd\u5730\u4e00\u8dc3\u800c\u51fa\uff0c\u8bf4\u9053\uff1a\u201c\u5582\uff0c\u4f60 \u8fd9\u628a\u5b9d\u5251\u7ed9\u6211\uff01\u201d \u3000\u3000\u6587\u80dc\u4e2d\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u6211\u8981\u7ed9\u4f60\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4f60\u7684\u5bf9\u624b\u5927\u75c5\u521d\u6108\uff0c\u4f60\u5df2\u7ecf\u662f\u5360\u4e86\u4fbf\u5b9c\u4e86\u3002\u4f60\u8fd8\u8981\u518d\u4f7f\u7384\u94c1\u5b9d\u5251\uff0c\u8fd9\u6837\u7684 \u6253\u6cd5\u5c82\u4e0d\u662f\u592a\u4e0d\u516c\u5e73\u4e86\u5417\uff1f\u201d \u3000\u3000\u6587\u80dc\u4e2d\u6012\u9053\uff1a\u201c\u8981\u4f60\u591a\u7ba1\u95f2\u4e8b\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u7b11\u9053\uff0c\u201c\u6211\u751f\u6765\u5c31\u662f\u7231\u7ba1\u95f2\u4e8b\uff0c\u4f60\u4e0d\u8ba9\u6211\u7ba1\u4e5f\u4e0d\u884c\u3002\u6253\u67b6\u4e0d\u7d27\u8981\uff0c\u4f46\u5fc5\u987b\u6253\u5f97\u516c \u5e73\u3002\u4f60\u6756\u7740\u5b9d\u5251\u901e\u80fd\uff0c\u6211\u770b\u4e0d\u987a\u773c\uff01\u73b0\u5728\u6211\u5212\u51fa\u4e24\u6761\u9053\u513f\u968f\u4f60\u9009\u62e9\uff1a\u7b2c\u4e00\u6761\uff0c\u4f60\u628a\u5b9d\u5251\u7ed9 \u6211\uff0c\u6362\u8fc7\u4e00\u67c4\u666e\u901a\u7684\u5251\u548c\u8fd9\u4eba\u6253\u3002\u6253\u8d62\u4e86\u6211\u8fd8\u7ed9\u4f60\uff0c\u6253\u8f93\u4e86\u8fd9\u67c4\u5b9d\u5251\u5c31\u8be5\u7ed9\u4f60\u7684\u5bf9\u624b\u5f53\u4f5c\u793c \u7269\u3002\u6211\u53ea\u662f\u5f53\u4e2a\u4e3b\u6301\u516c\u9053\u7684\u8bc1\u4eba\uff0c\u5e76\u975e\u60f3\u8981\u4f60\u7684\u5b9d\u5251\u3002\u201d \u3000\u3000\u6587\u80dc\u4e2d\u9053\uff1a\u201c\u8c01\u8bf7\u4f60\u4e3b\u6301\u516c\u9053\u4e86\uff1f\u4f60\u8d70\u8fdc\u4e00\u4e9b\uff0c\u5426\u5219\u4f11\u602a\u6211\u7684\u5b9d\u5251\u4e0d\u957f\u773c\u775b\uff01\u201d\u6587\u80dc\u4e2d \u53e3\u91cc\u8bf4\u8bdd\uff0c\u624b\u5e95\u6beb\u4e0d\u653e\u677e\u3002\u516c\u5b59\u71d5\u8d8a\u8d70\u8d8a\u8fd1\uff0c\u6b64\u65f6\u5374\u5df2\u662f\u8d70\u5230\u4e86\u5389\u5357\u661f\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u516c\u5b59\u71d5\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u7b2c\u4e00\u6761\u9053\u513f\u4f60\u4e0d\u80af\u8d70\uff0c\u90a3\u5c31\u53ea\u6709\u8d70\u7b2c\u4e8c\u6761\u4e86\u3002\u6211\u548c\u4f60\u6253\uff0c\u4f60\u867d\u7136 \u5148\u6253\u4e86\u4e00\u573a\uff0c\u4f46\u6709\u7684\u662f\u5b9d\u5251\uff0c\u4e0d\u80fd\u7b97\u662f\u4e0d\u516c\u5e73\u4e86\u3002 \u3000\u3000\u8bf4\u7f62\uff0c\u4e0d\u7531\u5206\u8bf4\u7684\u5c31\u63d2\u8fdb\u4e8c\u4eba\u4e2d\u95f4\uff0c\u628a\u5389\u5357\u661f\u786c\u6324\u51fa\u53bb\u3002\u5389\u5357\u661f\u77e5\u9053\u5979\u662f\u516c\u5b59\u5b8f\u7684\u5973 \u513f\uff0c\u6599\u60f3\u4e0d\u81f3\u4e8e\u5403\u6587\u80dc\u4e2d\u7684\u4e8f\uff0c\u4e8e\u662f\u653e\u5fc3\u8ba9\u5979\u63a5\u53d7\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u5012\u662f\u6709\u70b9\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u5f53\u4e0b\u6309\u5251\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u548c\u6211\u6253\u4e5f\u6210\uff0c\u4f46\u4f60\u82e5\u662f\u8f93\u4e86\uff0c\u53ef\u4e0d\u8bb8 \u53c8\u518d\u8282\u5916\u751f\u679d\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4f60\u6015\u6211\u7239\u7239\u5e2e\u6211\u5417\uff1f\u54fc\uff0c\u8c05\u4f60\u4e5f\u4e0d\u914d\u3002\u7239\uff0c\u4f60\u8bf4\u53e5\u8bdd\uff0c\u8ba9\u8fd9\u5c0f\u5b50\u653e\u5fc3\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u6253\u4e86\u4e2a\u54c8\u54c8\u8bf4\u9053\uff1a\u201c\u8001\u592b\u4ece\u6765\u4e0d\u4e0e\u5c0f\u8f88\u52a8\u624b\u3002\u8fd9\u662f\u6211\u5973\u513f\u7ba1\u7684\u95f2\u4e8b\uff0c\u4f60\u6709\u672c\u4e8b\u628a \u6211\u5973\u513f\u6740\u4e86\uff0c\u6211\u4e5f\u53ea\u662f\u8896\u624b\u65c1\u89c2\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u4f60\u653e\u5fc3\u3002\u53ef\u4ee5\u4f60\u5c3d\u7ba1\u628a\u5b9d\u5251\u671d\u6211\u523a\u6765\u5427\uff0c\u6211\u8ba9\u4f60\uff0c\u5148\u51fa\u62db\uff01\u201d \u3000\u3000\u6587\u80dc\u4e2d\u6012\u4ece\u5fc3\u8d77\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u8001\u513f\u4f3c\u4e4e\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\u3002\u54fc\uff0c\u53ea\u8981\u4ed6\u5f53\u771f\u662f\u8896\u624b\u65c1 \u89c2\uff0c\u96be\u9053\u6211\u8fd8\u6015\u4f60\u8fd9\u9ec4\u6bdb\u4e2b\u5934\u4e0d\u6210\uff1f\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\uff01\u4f60\u4eae\u5175 \u5668\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4f60\u5c3d\u7ba1\u53d1\u62db\u5c31\u662f\uff0c\u7f57\u5506\u4f5c\u751a\uff1f\u201d\u6587\u80dc\u4e2d\u51e0\u66fe\u53d7\u8fc7\u5982\u6b64\u8511\u89c6\uff0c\u6c14\u5f80\u4e0a\u51b2\uff0c\u4e00 \u5251\u5c31\u523a\u8fc7\u53bb\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u7ed9\u5979\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u9ec4\u6bdb\u4e2b\u5934\u80c6\u6562\u770b\u4e0d\u8d77\u6211\uff0c\u53eb\u4f60\u77e5\u9053\u6211\u7684\u5389 \u5bb3\uff01\u201d\u4fa7\u8eab\u8fdb\u903c\uff0c\u5f62\u5982\u96c1\u7fc5\u659c\u63a0\uff0c\u7384\u94c1\u5b9d\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u659c\u524a\u800c\u4e0b\u3002\u8fd9\u4e00\u62db\u6709\u4e2a\u540d\u5802\uff0c\u53eb\u505a \u201c\u5f2f\u5f13\u5c04\u96d5\u201d\uff0c\u8bb2\u7a76\u7684\u662f\u201c\u72e0\u201d\u201c\u51c6\u201d\u4e8c\u5b57\uff0c\u6b63\u662f\u6587\u80dc\u4e2d\u6700\u5f97\u610f\u7684\u5251\u6cd5\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u4ee5\u4e3a\u4e00\u4e2a\u201c\u9ec4\u6bdb\u5382\u5934\u201d\u80fd\u6709\u591a\u5927\u672c\u9886\uff0c\u8fd9\u4e00\u5251\u524a\u4e0b\u53bb\uff0c\u5373\u4f7f\u4e0d\u524a\u65ad\u5979\u7684\u81c2\u818a\uff0c\u81f3 \u5c11\u4e5f\u80fd\u4ee4\u5979\u53d7\u4f24\u3002\u6587\u80dc\u4e2d\u5bf9\u90a3\u201c\u571f\u8001\u513f\u201d\u591a\u5c11\u6709\u51e0\u5206\u987e\u5fcc\uff0c\u7528\u610f\u4e5f\u53ea\u662f\u60f3\u4eca\u516c\u5b59\u71d5\u53d7\u70b9\u8f7b \u4f24\uff0c\u597d\u53eb\u5979\u77e5\u96be\u800c\u9000\u7684\u3002\u54ea\u77e5\u516c\u5b59\u71d5\u4e00\u98d8\u4e00\u95ea\uff0c\u6587\u80dc\u4e2d\u8fd9\u4e00\u5251\u5df2\u662f\u523a\u4e86\u4e2a\u7a7a\u3002\u53ea\u542c\u5f97\u516c\u5b59\u71d5 \u683c\u683c\u7b11\u9053\uff1a\u201c\u4e5f\u4e0d\u89c1\u5f97\u600e\u4e48\u6837\u5389\u5bb3\u5440\u3002\u597d\uff0c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff0c\u8fd8\u62db\uff01\u201d\u8eab\u5f62\u4e00\u8f6c\uff0c\u4e00\u6761\u675f\u8170 \u7684\u7ef8\u5e26\u5df2\u662f\u89e3\u4e86\u4e0b\u6765\uff0c\u7528\u529b\u4e00\u6296\uff0c\u8170\u5e26\u7ed9\u5979\u4f7f\u5f97\u5982\u540c\u8f6f\u97ad\u4e00\u822c\uff0c\u7acb\u5373\u5411\u6587\u80dc\u4e2d\u6a2a\u5377\u8fc7\u53bb\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u5fc3\u60f3\uff1a\u201c\u6211\u8fd9\u5b9d\u5251\u6709\u65ad\u91d1\u622a\u94c1\u4e4b\u80fd\uff0c\u5439\u6bdb\u7acb\u65ad\u4e4b\u5229\uff0c\u4f55\u60e7\u4f60\u4e00\u6839\u8170\u5e26\uff1f\u201d\u54ea\u77e5\u516c \u5b59\u71d5\u7684\u7ef8\u5e26\u6d3b\u4f3c\u7075\u86c7\uff0c\u6587\u80dc\u4e2d\u4e00\u5251\u6ca1\u6709\u524a\u7740\uff0c\u7ef8\u5e26\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u8f6c\u6298\uff0c\u201c\u556a\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c \u6587\u80dc\u4e2d\u7684\u989d\u89d2\u5df2\u662f\u7ed9\u7ef8\u5e26\u62c2\u4e86\u4e00\u4e0b\u3002\u867d\u662f\u4e00\u6839\u7ef8\u5e26\uff0c\u6253\u4e0b\u6765\u5374\u4e0d\u4e9a\u8f6f\u97ad\uff0c\u6587\u80dc\u4e2d\u7684\u989d\u89d2\u767b\u65f6 \u80bf\u4e86\u4e00\u5927\u5757\u3002 \u3000\u3000\u516c\u5b59\u71d5\u201c\u5657\u55e4\u201d\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u6ca1\u6709\u4e00\u4e2a\u955c\u5b50\u7ed9\u4f60\u7167\u7167\uff0c\u4f60\u5934\u4e0a\u957f\u4e86\u89d2\u5566\u3002\u4f60\u542c\u8fc7\u8fd9\u53e5\u4fd7 \u8bdd\u6ca1\u6709\uff1f\u4f60\u82e5\u53bb\u7167\u955c\u5b50\u5440\uff0c\u8fd9\u5c31\u53eb\u505a\uff1a\u732a\u516b\u6212\u7167\u955c\u5b50\uff0c\u91cc\u5916\u4e0d\u662f\u4eba\u3002\u201d \u3000\u3000\u201c\u6587\u80dc\u4e2d\u53c8\u60ca\u53c8\u6012\uff0c\u5fd9\u628a\u7384\u94c1\u5b9d\u5251\u821e\u5f97\u6cfc\u98ce\u4e5f\u4f3c\uff0c\u4f7f\u51fa\u4e86\u4e00\u5957\u653b\u5b88\u517c\u5907\u7684\u201c\u4e09\u624d\u5251 \u6cd5\u201d\u3002\u6b64\u65f6\u4ed6\u5df2\u9886\u6559\u4e86\u516c\u5b59\u71d5\u7684\u672c\u9886\uff0c\u54ea\u91cc\u8fd8\u6562\u518d\u6709\u534a\u70b9\u8f7b\u654c\u4e4b\u5fc3\uff1f\u6c14\u6012\u4e4b\u4e0b\uff0c\u6740\u673a\u9661\u8d77\uff0c \u4e5f\u987e\u4e0d\u5f97\u8981\u4fdd\u5168\u516c\u5b59\u71d5\u7684\u6027\u547d\u4e86\u3002 \u3000\u3000\u5728\u6587\u80dc\u4e2d\u5168\u529b\u65bd\u4e3a\u4e4b\u4e0b\uff0c\u516c\u5b59\u71d5\u7684\u7ef8\u5e26\u4e00\u65f6\u653b\u4e0d\u8fdb\u4ed6\u7684\u5251\u5149\u5708\u5185\u3002\u4f46\u6587\u80dc\u4e2d\u7684\u5b9d\u5251\u60f3\u8981 \u524a\u65ad\u5979\u7684\u7ef8\u5e26\u5374\u4e5f\u4e0d\u80fd\u3002\u7ef8\u5e26\u98d8\u98d8\uff0c\u6beb\u4e0d\u53d7\u529b\uff0c\u7384\u94c1\u5b9d\u5251\u6325\u821e\u8d77\u6765\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u7ef8\u5e26\u968f\u98ce\u6447 \u6446\uff0c\u672a\u78b0\u4e0a\u5373\u5df2\u8361\u5f00\u3002 \u3000\u3000\u7384\u94c1\u5b9d\u5251\u91cd\u8fbe\u4e00\u767e\u591a\u65a4\uff0c\u6587\u80dc\u4e2d\u867d\u7136\u4f7f\u5f97\u52a8\uff0c\u4e5f\u611f\u5403\u529b\u3002\u4e0d\u6d88\u591a\u4e45\uff0c\u6587\u80dc\u4e2d\u4e5f\u662f\u5927\u6c57\u6dcb \u6f13\uff0c\u6c14\u5598\u5982\u725b\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u5df2\u77e5\u516c\u5b59\u71d5\u662f\u516c\u5b59\u5b8f\u7684\u5973\u513f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5c31\u662f\u6587\u9053\u5e84\u548c\u53f2\u767d\u90fd\u5230\u6765\uff0c\u53ea\u6015\u4e5f\u662f \u60f9\u8fd9\u8001\u513f\u4e0d\u8d77\u3002\u770b\u6765\u6587\u80dc\u4e2d\u8fd9\u5c0f\u5b50\u5403\u4e8f\u662f\u5403\u5b9a\u7684\u4e86\u3002\u5509\uff0c\u8fd9\u7384\u94c1\u5b9d\u5251\u4e0d\u8981\u4e5f\u7f62\uff0c\u5170\u5341\u516d\u8ba1\uff0c \u6211\u8fd8\u662f\u4ee5\u8d70\u4e3a\u4e0a\u8ba1\uff0c\u4e3b\u610f\u6253\u5b9a\uff0c\u7acb\u5373\u8df3\u4e0a\u6587\u80dc\u4e2d\u9a91\u6765\u7684\u90a3\u5339\u5750\u9a91\uff0c\u53eb\u9053\uff1a\u201c\u6587\u4e16\u5144\uff0c\u4f60\u597d\u597d \u6253\u5427\uff0c\u8bf7\u6055\u8001\u592b\u5c11\u966a\u4e86\u3002\u201d\u5feb\u9a6c\u52a0\u97ad\uff0c\u75be\u9a70\u800c\u53bb\u3002\u4ed6\u987e\u7740\u9003\u547d\uff0c\u8fde\u5973\u513f\u4e5f\u629b\u5f03\u4e86\u3002\u5c01\u5999\u5ae6\u53c8 \u662f\u6c14\u6124\uff0c\u53c8\u662f\u96be\u582a\uff0c\u832b\u7136\u5730\u671b\u7740\u7236\u4eb2\u79bb\u5979\u800c\u53bb\u3002\u5389\u5357\u661f\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5c01\u59d1\u5a18\uff0c\u4e0d\u8981\u96be\u8fc7\uff0c\u7531 \u4ed6\u53bb\u5427\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u4e00\u8d70\uff0c\u6587\u80dc\u4e2d\u60ca\u6012\u4e4b\u4e0b\uff0c\u5fc3\u795e\u66f4\u4e71\u3002 \u3000\u3000\u6b64\u6d88\u5f7c\u957f\uff0c\u6587\u80dc\u4e2d\u6325\u52a8\u6c89\u91cd\u7684\u7384\u94c1\u5b9d\u5251\uff0c\u8d8a\u6765\u8d8a\u662f\u529b\u4e0d\u4ece\u5fc3\u3002\u516c\u5b59\u71d5\u8fd9\u6761\u8f7b\u98d8\u98d8\u7684\u7ef8\u5e26 \u5374\u662f\u67d4\u5982\u67f3\u7d6e\uff0c\u7fe9\u82e5\u60ca\u9e3f\uff0c\u8f7b\u7075\u98d8\u996d\uff0c\u62db\u6570\u8d8a\u6765\u8d8a\u662f\u795e\u5999\uff01 \u3000\u3000\u516c\u5b59\u71d5\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u672c\u4e8b\u4f4e\u5fae\uff0c\u4e0d\u914d\u4f7f\u8fd9\u67c4\u7384\u94c1\u5b9d\u5251\u3002\u5b9d\u5251\u62ff\u6765\uff0c\u8d76\u5feb\u7ed9\u6211\u6eda\u7f62\uff01\u201d\u8bdd \u58f0\u672a\u4e86\uff0c\u7ea2\u7ef8\u4e00\u5377\uff0c\u5c31\u50cf\u4e00\u7247\u7ea2\u971e\u88f9\u4f4f\u4e00\u9053\u767d\u5149\u4f3c\u7684\uff0c\u7ef8\u5e26\u5377\u7740\u5251\u67c4\uff0c\u767b\u65f6\u5c31\u628a\u6587\u80dc\u4e2d\u7684\u7384 \u94c1\u5b9d\u5251\u593a\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u62d4\u8db3\u98de\u9003\uff0c\u4e00\u9762\u8dd1\u4e00\u9762\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u516d\u5408\u5e2e\u53f2\u5e2e\u4e3b\u7684\u4e1c\u897f\uff0c\u4f60\u8fd9\u4e2b\u5934\u6709\u80c6\u62ff\u53bb\uff0c \u53ef\u6709\u80c6\u62a5\u4e2a\u4e07\u513f\u4e48\uff1f\u201d\u4ed6\u5403\u4e86\u8d25\u4ed7\u3002\u4e00\u9762\u9003\uff0c\u53ef\u8fd8\u662f\u5fc3\u4e2d\u4e0d\u5fff\uff0c\u60f3\u4f9d\u4ed7\u53f2\u767d\u90fd\u7684\u58f0\u5a01\uff0c\u627e\u56de \u51e0\u5206\u9762\u5b50\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u592b\u516c\u5b59\u5b8f\uff0c\u4f60\u56de\u53bb\u544a\u8bc9\u53f2\u767d\u90fd\uff0c\u8fd9\u70b3\u5b9d\u5251\u6211\u662f\u8981\u5b9a\u7684\u4e86\uff0c \u4ed6\u4e0d\u670d\u6c14\uff0c\u53eb\u4ed6\u524d\u6765\u4f1a\u6211\u3002\u82e5\u662f\u4ed6\u5355\u8eab\u4e0d\u6562\u524d\u6765\uff0c\u548c\u4f60\u7684\u7236\u4eb2\u6587\u9053\u5e84\u4e00\u540c\u6765\u6211\u4e5f\u4e00\u6837\u62db \u5f85\uff01\u201d \u3000\u3000\u6587\u80dc\u4e2d\u8fd9\u624d\u77e5\u9053\u8fd9\u4e2a\u201c\u8c8c\u4e0d\u60ca\u4eba\u201d\u7684\u201c\u571f\u8001\u513f\u201d\uff0c\u7adf\u7136\u662f\u540d\u9707\u6c5f\u6e56\u7684\u7ea2\u7f28\u4f1a\u603b\u8235\u4e3b\uff0c\u8fd9 \u4e00\u5413\u5413\u5f97\u4ed6\u53ea\u6068\u7239\u5a18\u5c11\u751f\u4e86\u4e24\u6761\u817f\uff0c\u54ea\u91cc\u8fd8\u6562\u8fd8\u5634\uff1f\u811a\u5e95\u62b9\u4e86\u6cb9\u4f3c\u7684\uff0c\u4e00\u6e9c\u70df\u98de\u8dd1\uff0c\u5b59\u5b8f\u5b8f \u54c8\u5927\u7b11\uff0c\u4e5f\u4e0d\u53bb\u62e6\u963b\u4ed6\u3002 \u3000\u3000\u5389\u5357\u661f\u4e0e\u5c01\u5999\u5ae6\u4e0a\u524d\u5411\u516c\u5b59\u5b8f\u7236\u5973\u9053\u8c22\uff0c\u516c\u5b59\u5b8f\u5341\u5206\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u71d5\u513f\uff0c\u8fd9\u4f4d\u5389\u516c\u5b50 \u5c31\u662f\u6211\u548c\u4f60\u8bf4\u8fc7\u7684\u90a3\u4f4d\u548c\u91d1\u9010\u6d41\u4e00\u8d77\u5927\u95f9\u8428\u5e9c\u5bff\u5802\u7684\u5389\u5c11\u4fa0\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u76f8\u52a9\u4e4b\u5fb7\u3002\u201d\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u5e78\u4e0d\u8fb1\u547d\uff0c\u539f\u7269\u5949\u8fd8\u3002\u201d\u5389\u5357\u661f \u9053\uff1a\u201c\u5929\u4e0b\u795e\u7269\u5229\u5668\uff0c\u5e94\u5c5e\u6709\u5fb7\u8005\u5c45\u4e4b\uff0c\u8fd9\u67c4\u5b9d\u5251\u662f\u59d1\u5a18\u593a\u6765\u7684\uff0c\u8bf7\u4ee4\u5c0a\u8d4f\u6536\u5427\u3002\u201d\u516c\u5b59\u71d5 \u7b11\u9053\uff1a\u201c\u7239\u7239\u4ece\u6765\u4e0d\u7528\u5175\u5668\uff0c\u4ed6\u8bf4\u8981\u8fd9\u5b9d\u5251\uff0c\u4e0d\u8fc7\u662f\u7528\u4ed6\u7684\u540d\u5b57\uff0c\u5413\u552c\u53f2\u767d\u90fd\u800c\u5df2\u3002\u4f60\u5f53\u4ed6 \u771f\u7684\u60f3\u8981\u4f60\u7684\u5b9d\u5251\u5417\uff1f\u201d\u516c\u5b59\u71d5\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5389\u5357\u661f\u82e5\u518d\u63a8\u8f9e\uff0c\u90a3\u5c31\u662f\u770b\u4e0d\u8d77\u516c\u5b59\u5b8f\u4e86\u3002\u5389\u5357 \u661f\u53ea\u597d\u6536\u4e0b\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u5389\u5144\uff0c\u4f60\u600e\u7684\u5728\u8fd9\u91cc\u548c\u6587\u80dc\u4e2d\u6253\u8d77\u67b6\u6765\uff1f\u201d\u5386\u5357\u661f\u9053\uff1a\u201c\u8bf4\u6765\u8bdd\u957f\u3002\u201d\u5f53 \u4e0b\u5c06\u5728\u626c\u5dde\u5386\u9669\u7684\u7ecf\u8fc7\uff0c\u539f\u539f\u672c\u672c\u5730\u544a\u8bc9\u4e86\u516c\u5b59\u5b8f\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u6211\u9053\u4e10\u5e2e\u54ea\u91cc\u6765\u7684\u4e24\u4e2a\u5c11\u5e74\u9ad8\u624b\uff1f\u539f\u6765\u5728\u626c\u5dde\u5927\u95f9\u4e86\u516d\u5408\u5e2e\u7684\u5c31\u662f\u4f60\u548c\u91d1 \u9010\u6d41\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u516c\u5b59\u524d\u8f88\u542c\u5230\u4e86\u4ec0\u4e48\u6d88\u606f\uff1f\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u6211\u6b63\u662f\u524d\u4e24\u5929\u4ece\u626c\u5dde\u6765\u7684\uff0c\u542c\u8bf4\u53f2\u767d\u90fd\u907f\u4e0d\u89c1\u5ba2\uff0c\u6211\u4e5f\u61d2\u5f97\u53bb\u627e\u4ed6\u3002\u626c\u5dde \u7684\u670b\u53cb\u544a\u8bc9\u6211\uff0c\u5982\u679c\u6211\u65e9\u6765\u4e24\u5929\uff0c\u5c31\u521a\u597d\u53ef\u4ee5\u78b0\u4e0a\u90a3\u573a\u70ed\u95f9\u3002\u6211\u90a3\u4f4d\u670b\u53cb\u4e0d\u662f\u5e2e\u4f1a\u4e2d\u4eba\uff0c\u4ed6 \u53ea\u9053\u662f\u4e10\u5e2e\u4e0e\u516d\u5408\u5e2e\u706b\u62fc\uff0c\u53f2\u767d\u90fd\u5f88\u5403\u4e86\u70b9\u4e8f\uff0c\u4f46\u5374\u4e0d\u77e5\u5176\u8be6\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u91d1\u9010\u6d41\u4e0d\u77e5\u662f\u5426\u8fd8\u5728\u626c\u5dde\uff1f\u8001\u524d\u8f88\u5230\u8fc7\u4e10\u5e2e\u7684\u5206\u8235\u6ca1\u6709\uff1f\u201d \u3000\u3000\u516c\u5251\u5b8f\u9053\uff1a\u201c\u4e10\u5e2e\u5206\u8235\u5df2\u7ecf\u8fc1\u79fb\uff0c\u6211\u6251\u4e86\u4e2a\u7a7a\uff0c\u4e00\u4e2a\u4eba\u4e5f\u627e\u4e0d\u7740\u3002\u201d\u65e2\u7136\u627e\u4e0d\u7740\u4e10\u5e2e\uff0c \u5f53\u7136\u662f\u6ca1\u6709\u91d1\u9010\u6d41\u7684\u6d88\u606f\u7684\u4e86\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u4e0e\u5389\u5357\u661f\u5728\u4e00\u8fb9\u8bf4\u8bdd\uff0c\u516c\u5b59\u71d5\u548c\u5c01\u5999\u5ae6\u4e5f\u5728\u4e00\u8fb9\u6500\u8c08\u8d77\u6765\u3002\u516c\u5b59\u71d5\u6027\u60c5\u723d\u6717\uff0c\u5fc3 \u5730\u4ec1\u6148\uff0c\u62c9\u7740\u5c01\u5999\u5ae6\u7684\u624b\u8bf4\u9053\uff1a\u201c\u5c01\u59d0\u59d0\uff0c\u4f60\u548c\u7239\u7239\u5435\u67b6\uff0c\u6211\u90fd\u542c\u89c1\u4e86\u3002\u201d\u5c01\u5999\u5ae6\u6ee1\u9762\u901a \u7ea2\uff0c\u6cea\u73e0\u5728\u773c\u7736\u91cc\u6253\u8f6c\u3002 \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u5c01\u59d0\u59d0\uff0c\u4f60\u4e0d\u5fc5\u96be\u8fc7\uff0c\u4f60\u662f\u83b2\u51fa\u6c61\u6ce5\u800c\u4e0d\u67d3\uff0c\u6211\u5bf9\u4f60\u4f69\u670d\u8fd8\u6765\u4e0d\u53ca\u5462\uff0c\u51b3 \u4e0d\u4f1a\u770b\u8f7b\u4f60\u7684\u3002\u5c01\u59d0\u59d0\uff0c\u4f60\u51c6\u5907\u4e0a\u54ea\u513f\uff1f\u201d\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6211\u662f\u65e0\u5bb6\u53ef\u5f52\u4e4b\u4eba\uff0c\u53ea\u80fd\u968f\u9047\u800c\u5b89 \u4e86\u3002\u201d\u516c\u5b59\u71d5\u9053\uff1a\u201c\u82e5\u662f\u4f60\u4e0d\u8d5a\u5f03\uff0c\u8bf7\u4f60\u548c\u6211\u4f5c\u4f34\u5982\u4f55\uff1f\u201d\u5c01\u5999\u5ae6\u559c\u51fa\u671b\u5916\uff0c\u60a6\u9053\uff1a\u201c\u5f97\u59d0 \u59d0\u63d0\u643a\uff0c\u6211\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u662f\u53ea\u6015\u7ed9\u4f60\u6dfb\u4e86\u7d2f\u8d58\u3002\u201d\u516c\u5b59\u71d5\u9053\uff1a\u201c\u54ea\u513f\u7684\u8bdd\uff0c\u6211\u884c\u8d70\u6c5f\u6e56\uff0c\u4e0d \u8fc7\u4e00\u5e74\uff0c\u9605\u5386\u751a\u6d45\uff0c\u4eca\u540e\u54b1\u4eec\u7ed3\u4f34\u540c\u884c\uff0c\u8fd8\u5f97\u8bf7\u5c01\u59d0\u59d0\u6307\u6559\u6211\u5462\u3002\u201d\u5c01\u5999\u5ae6\u751a\u611f\u8be7\u5f02\uff0c\u5fc3\u91cc \u60f3\u9053\uff1a\u201c\u5979\u7684\u7236\u4eb2\u662f\u540d\u9707\u6c5f\u6e56\u7684\u7ea2\u7f28\u4f1a\u5e2e\u4e3b\uff0c\u600e\u7684\u5374\u8bf4\u8fd9\u6837\u7684\u8bf4\u8bdd\uff1f\u4f46\u5979\u8bf4\u5f97\u5341\u5206\u8bda\u6073\uff0c\u5374 \u53c8\u4e0d\u4f3c\u865a\u4f2a\u7684\u5ba2\u5957\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u71d5\u513f\uff0c\u4f60\u8fd8\u662f\u5ff5\u5ff5\u4e0d\u5fd8\u8d74\u7afa\u6e05\u534e\u4e4b\u7ea6\u4e48\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u88c5\u7740\u8d4c\u6c14\u7684\u6837\u5b50\u561f\u5c0f\u5634\u513f\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u8c01\u53eb\u4f60\u4e0d\u80af\u966a\u6211\uff0c\u6211\u53ea\u597d\u81ea\u5df1\u627e\u4e2a\u4f34\u513f\u53bb \u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u5fae\u5fae\u4e00\u7b11\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u5bf9\u5389\u5357\u661f\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u53c8\u51c6\u5907\u4e0a\u54ea\u513f\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u6211\u60f3\u5230\u897f\u660c\u53bb\u8d70\u4e00\u8d9f\u3002\u201d\u539f\u6765\u5389\u5357\u661f\u4f30\u8ba1\u53f2\u767d\u90fd\u8fdf\u65e9\u4f1a\u9001\u59b9\u59b9\u5230\u897f\u660c\u53bb\uff0c \u903c\u59b9\u59b9\u4e0e\u897f\u660c\u5c06\u519b\u5e05\u5b5f\u96c4\u6210\u5a5a\uff0c\u4ed6\u77e5\u9053\u4ed6\u60f3\u5f97\u5230\u7684\u91d1\u9010\u6d41\u4e5f\u4e00\u5b9a\u60f3\u5f97\u5230\uff0c\u662f\u4ee5\u4ed6\u5230\u897f\u660c\uff0c\u4e5f \u5c31\u5f88\u6709\u53ef\u80fd\u548c\u91d1\u9010\u6d41\u4f1a\u9762\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u7b11\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u4f60\u4eec\u4e09\u4eba\u6b63\u597d\u4f5c\u4f34\u540c\u884c\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u4ee4\u5a9b\u4e5f\u662f\u8981\u5f80\u897f\u660c\u4e48\u3002\u201d\u516c\u5b59\u5b9a\u9053\uff1a\u201c\u5979\u662f\u8981\u5230\u897f\u660c\u5317 \u9762\u7684\u5927\u51c9\u5c71\u53bb\u7684\u3002\u5927\u51c9\u5c71\u662f\u7afa\u5c1a\u7236\u8fd9\u652f\u4e49\u519b\u7684\u57fa\u5730\u3002\u7afa\u5c1a\u7236\u8fd9\u4f4d\u6b66\u5b66\u5927\u5e08\u7684\u540d\u5b57\u60f3\u5fc5\u4f60\u66fe\u542c \u8fc7\u5427\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u542c\u5f97\u91d1\u9010\u6d41\u8bf4\u8fc7\u3002\u542c\u8bf4\u8fd9\u4f4d\u7afa\u8001\u524d\u8f88\u548c\u4ed6\u7684\u5927\u5e08\u5144\u6c5f\u6d77\u5929 \u662f\u597d\u670b\u53cb\uff0c\u8eab\u5177\u7edd\u4e16\u6b66\u529f\uff0c\u4e0d\u5728\u6c5f\u5927\u4fa0\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u7afa\u5c1a\u7236\u6709\u4e2a\u5973\u513f\uff0c\u540d\u5524\u7afa\u6e05\u534e\uff0c\u524d\u5e74\u6211\u548c\u5c0f\u5973\u5728\u7afa\u5bb6\u4f5c\u5ba2\uff0c\u5979\u4eec\u4e8c\u4eba\u6027\u60c5 \u76f8\u6295\uff0c\u4eb2\u5982\u59d0\u59b9\uff0c\u53bb\u5e74\u7afa\u5c1a\u7236\u7ed9\u5b98\u519b\u7528\u8be1\u8ba1\u593a\u4e86\u897f\u660c\uff0c\u9000\u5165\u5927\u51c9\u5c71\u4e2d\uff0c\u97f3\u8baf\u9694\u7edd\uff0c\u5c0f\u5973\u5bf9\u7afa \u6e05\u534e\u601d\u5ff5\u5f97\u7d27\u3002\u6700\u8fd1\u624d\u63a5\u5f97\u6d88\u606f\uff0c\u8bf4\u662f\u7afa\u6e05\u534e\u5c06\u5728\u660e\u5e74\u51fa\u9601\uff0c\u4e0e\u6c5f\u6d77\u5929\u7684\u5f92\u5f1f\u674e\u5149\u590f\u6210 \u5a5a\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u559c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9010\u6d41\u548c\u6211\u8bf4\u8fc7\uff0c\u4ed6\u6709\u4e00\u4e2a\u5e08\u4f84\u540d\u53eb\u674e\u5149\u590f\uff0c\u662f\u6297\u6e05\u82f1\u96c4\u674e\u4e49\u6210\u7684 \u9057\u5b64\u3002\u4ed6\u7684\u5e08\u4f84\u65e2\u7136\u5c06\u5728\u660e\u6625\u4e0e\u7afa\u5c1a\u7236\u7684\u5973\u513f\u6210\u5a5a\uff0c\u4ed6\u4e00\u5b9a\u4e5f\u662f\u4f1a\u53bb\u559d\u559c\u9152\u7684\u4e86\u3002\u201d\u5fc3\u60f3\u6709 \u8fd9\u6869\u559c\u4e8b\uff0c\u6b64\u884c\u524d\u5f80\u897f\u660c\uff0c\u548c\u91d1\u9010\u6d41\u89c1\u9762\u7684\u673a\u4f1a\u5f53\u7136\u662f\u66f4\u5927\u4e86\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u53ef\u60dc\u6211\u4e22\u4e0d\u4e0b\u7ea2\u7f28\u4f1a\u7684\u4e8b\u52a1\uff0c\u6700\u8fd1\u5c40\u52bf\u52a8\u8361\uff0c\u975e\u5f97\u6211\u8d76\u56de\u53bb\u4e3b\u6301\u4e0d\u53ef\uff0c\u4ed6 \u4eec\u7684\u559c\u9152\u6211\u662f\u4e0d\u80fd\u559d\u4e86\u3002\u4f46\u5c0f\u5973\u5982\u662f\u975e\u53bb\u4e0d\u53ef\uff0c\u6211\u6b63\u6101\u6ca1\u4eba\u548c\u5979\u4f5c\u4f34\uff0c\u73b0\u5728\u597d\u4e86\uff0c\u6709\u4f60\u4eec\u4e24 \u4eba\u4e0e\u5979\u4e00\u8def\uff0c\u6211\u53ef\u4ee5\u653e\u5fc3\u4e86\u3002\u5389\u8001\u5f1f\uff0c\u5979\u6700\u4e2a\u4e0d\u61c2\u4e8b\u7684\u91ce\u4e2b\u5934\uff0c\u53ef\u8981\u52b3\u70e6\u4f60\u591a\u7167\u987e\u5979\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u7236\u5973\u5206\u9053\u626c\u9573\uff0c\u516c\u5b59\u5b8f\u56de\u8f6c\u4ed6\u7684\u7ea2\u7f28\u8235\uff0c\u516c\u5b59\u71d5\u5219\u4e0e\u5389\u5357\u661f\u3001\u5c01\u5999\u5ae6\u4e00\u8d77\uff0c\u524d\u5f80\u897f \u660c\u3002 \u3000\u3000\u5389\u5357\u661f\u8eab\u4e0a\u7684\u521b\u4f24\u5012\u662f\u597d\u4e86\uff0c\u4f46\u56e0\u5fc3\u4e0a\u7684\u521b\u4f24\u672a\u6108\uff0c\u60c5\u6000\u843d\u5bde\uff0c\u4e00\u8def\u4e0a\u90fd\u662f\u6c89\u9ed8\u5be1\u8a00\u3002 \u5c01\u5999\u5ae6\u65b0\u906d\u5bb6\u5ead\u53d8\u6545\uff0c\u5fc3\u60c5\u4e5f\u662f\u6291\u90c1\u4e0d\u6b22\u3002\u597d\u5728\u516c\u5b59\u71d5\u5374\u662f\u4e2a\u5929\u771f\u70c2\u6f2b\uff0c\u6027\u60c5\u723d\u6717\u7684\u59d1\u5a18\uff0c \u559c\u6b21\u8bf4\u7b11\uff0c\u51cf\u5c11\u4e86\u4e0d\u5c11\u5bc2\u5bde\u3002 \u3000\u3000\u4e00\u884c\u4e09\u4eba\uff0c\u517c\u7a0b\u8d76\u8def\uff0c\u4e0d\u5230\u4e00\u4e2a\u6708\u7684\u5de5\u592b\uff0c\u5df2\u7ecf\u8fc7\u4e86\u6c5f\u82cf\u3001\u5b89\u5fbd\u3001\u6cb3\u5357\u4e09\u4e2a\u7701\u4efd\uff0c\u8e0f\u8fdb \u4e86\u9655\u897f\u754c\u5185\u3002\u6b64\u65f6\u5df2\u662f\u51c9\u79cb\u4e5d\u6708\u7684\u5929\u6c14\u4e86\u3002 \u3000\u3000\u5230\u4e86\u9655\u897f\uff0c\u884c\u7684\u591a\u662f\u5c71\u8def\uff0c\u5389\u5357\u661f\u65e9\u5df2\u7f6e\u5907\u4e86\u4e24\u5f20\u9732\u5bbf\u7684\u5e10\u5e55\uff0c\u4e3a\u4e86\u8d76\u8def\uff0c\u6709\u65f6\u9519\u8fc7\u5bbf \u5934\uff0c\u5c31\u5728\u6797\u4e2d\u9732\u5bbf\u3002\u597d\u5728\u662f\u4e8c\u5973\u4e00\u7537\uff0c\u53ef\u4ee5\u51cf\u5c11\u8bb8\u591a\u907f\u5fcc\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u4ed6\u4eec\u7ecf\u8fc7\u201c\u4e03\u76d8\u5cad\u201d\uff0c\u7ffb\u8fc7\u4e00\u4e2a\u9669\u9661\u7684\u5c71\u5761\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u662f\u9ec4\u660f\u65f6\u5206\u3002\u516c\u5b59\u71d5\u628a \u773c\u4e00\u770b\uff0c\u524d\u9762\u6709\u5757\u8349\u5730\uff0c\u91ce\u82b1\u6742\u5f00\uff0c\u6e05\u6eaa\u5982\u5e26\uff0c\u897f\u5317\u9ad8\u539f\u7279\u6709\u7684\u4e00\u79cd\u201c\u5927\u9752\u6811\u201d\u8471\u5ba0\u8038\u7acb\uff0c \u6d53\u836b\u853d\u5730\u3002\u4f59\u971e\u6563\u9a91\uff0c\u8fdc\u5904\u5c42\u5c42\u7684\u96ea\u5cf0\uff0c\u96c4\u5cd9\u5728\u591a\u4e91\u7684\u84dd\u5929\u91cc\uff0c\u6cdb\u7740\u6de1\u6de1\u7684\u7d2b\u8272\u3002\u6709\u4e9b\u5730\u65b9 \u5df2\u7ecf\u5206\u4e0d\u51fa\u662f\u5c71\u8fd8\u662f\u4e91\u3002\u516c\u5b59\u71d5\u559c\u9053\uff1a\u201c\u771f\u662f\u4e2a\u597d\u5730\u65b9\uff0c\u867d\u7136\u672a\u9ed1\uff0c\u6211\u4e5f\u4e0d\u60f3\u8d70\u4e86\u3002\u5c31\u5728\u8fd9 \u91cc\u8fc7\u4e00\u665a\u5427\u3002\u201d \u3000\u3000\u642d\u597d\u5e10\u7bf7\uff0c\u516c\u5b59\u71d5\u9053\uff1a\u201c\u5c01\u59d0\u59d0\uff0c\u4f60\u5f04\u996d\uff0c\u6211\u53bb\u627e\u70b9\u91ce\u5473\u3002\u201d\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u5389\u5927\u54e5\uff0c\u4f60 \u966a\u516c\u5b59\u59d0\u59d0\u53bb\u5427\u3002\u201d\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4e0d\u5fc5\uff0c\u6253\u730e\u662f\u6211\u62ff\u624b\u597d\u620f\uff0c\u7528\u4e0d\u7740\u591a\u4e00\u4e2a\u4eba\u3002\u4f46\u4ed6\u4e5f\u4e0d\u80fd \u767d\u5403\uff0c\u4ed6\u5e94\u8be5\u5e2e\u4f60\u751f\u706b\u3001\u6253\u6c34\u3001\u6dd8\u7c73\uff0c\u5403\uff0c\u6709\u8fd9\u8bb8\u591a\u4e8b\u60c5\uff0c\u4e5f\u591f\u4ed6\u505a\u7684\u4e86\u3002\u201d\u5389\u5357\u661f\u65e0\u53ef\u65e0 \u4e0d\u53ef\uff0c\u516c\u5b59\u71d5\u65e2\u7136\u4e0d\u8981\u4ed6\u4f5c\u4f34\uff0c\u4ed6\u5c31\u4e0d\u53bb\u4e86\u3002 \u3000\u3000\u516c\u5b59\u71d5\u6709\u5fc3\u8ba9\u4ed6\u4eec\u6709\u8f83\u591a\u7684\u65f6\u95f4\u76f8\u5904\uff0c\u5979\u867d\u7136\u5f88\u5feb\u7684\u5c31\u6253\u4e86\u4e24\u53ea\u91ce\u5154\uff0c\u5374\u6545\u610f\u6328\u5230\u5929\u9ed1 \u7684\u65f6\u5206\u624d\u56de\u6765\u3002\u8d70\u5230\u6797\u8fb9\uff0c\u53ea\u542c\u5f97\u4e00\u7247\u6291\u626c\u987f\u632b\u7684\u8427\u58f0\uff0c\u6709\u8bf4\u4e0d\u5c3d\u7684\u82cd\u51c9\u610f\u5473\u3002 \u3000\u3000\u539f\u6765\u5389\u5357\u661f\u6027\u559c\u97f3\u4e50\uff0c\u4ed6\u7684\u53e4\u7434\u5df2\u7ecf\u9001\u7ed9\u4e86\u91d1\u9010\u6d41\uff0c\u524d\u4e9b\u5929\uff0c\u4ed6\u5728\u5c71\u4e0a\u627e\u5230\u597d\u7684\u7af9\u5b50\uff0c \u81ea\u5df1\u505a\u4e86\u4e00\u652f\u8427\u3002\u996d\u8336\u90fd\u5df2\u5f04\u597d\uff0c\u672a\u89c1\u516c\u5b59\u71d5\u56de\u6765\uff0c\u7b49\u5f97\u5143\u804a\uff0c\u9042\u5439\u8d77\u8427\u6765\u3002 \u3000\u3000\u5389\u5357\u661f\u81ea\u4ece\u77e5\u9053\u53f2\u7ea2\u82f1\u4e0e\u91d1\u9010\u6d41\u7684\u5173\u7cfb\u4e4b\u540e\uff0c\u867d\u7136\u662f\u65e9\u65e0\u6742\u5ff5\uff0c\u51b3\u610f\u6325\u6167\u5251\u800c\u65a9\u60c5\u4e1d\uff0c \u4f46\u60c5\u4e1d\u53ef\u65a9\uff0c\u5fc3\u4e0a\u7684\u521b\u4f24\u5374\u662f\u4e0d\u80fd\u5728\u77ed\u671f\u95f4\u533b\u5f97\u597d\u7684\u3002\u4ed6\u8fd9\u843d\u5bde\u7684\u60c5\u6000\uff0c\u8ff7\u832b\u7684\u5fc3\u4e8b\uff0c\u4e0d\u77e5 \u4e0d\u89c9\u5c31\u4ece\u8427\u58f0\u4e2d\u900f\u9732\u51fa\u6765\u3002\u5439\u5f97\u5f53\u771f\u662f\u5982\u6028\u5982\u6155\uff0c\u5982\u6ce3\u5982\u8bc9\uff0c\u4f7f\u5f97\u4e00\u5411\u4e50\u89c2\uff0c\u4e0d\u89e3\u6101\u4e3a\u4f55\u7269 \u7684\u516c\u5b59\u71d5\uff0c\u542c\u4e86\u4ed6\u7684\u8427\u58f0\uff0c\u7ade\u4e5f\u4e0d\u81ea\u7981\u7684\u4e3a\u4e4b\u5fc3\u9178\u3002 \u3000\u3000\u516c\u5b59\u71d5\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5389\u5927\u54e5\u4e00\u5b9a\u662f\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff0c\u5426\u5219\u4ed6\u4e0d\u4f1a\u4e00\u8def\u6c89\u9ed8\u5be1\u8a00\u3002\u5509\uff0c\u8fd9\u8427\u58f0 \u771f\u662f\u5439\u5f97\u51c4\u51c9\uff0c\u4ee4\u4eba\u96be\u53d7\u3002\u5389\u5927\u54e5\u4f55\u82e6\u5982\u6b64\u5462\uff1f\u201d\u5979\u8eb2\u5728\u6797\u8fb9\uff0c\u542c\u4e86\u4e00\u4f1a\uff0c\u518d\u628a\u773c\u5149\u671d\u5c01\u5999 \u5ae6\u671b\u53bb\uff0c\u53ea\u89c1\u5c01\u5999\u5ae6\u80cc\u671d\u7740\u5389\u5357\u661f\uff0c\u9eef\u7136\u81ea\u5750\uff0c\u6b63\u62bd\u51fa\u4e00\u6761\u624b\u5e15\u62b9\u5979\u7684\u773c\u6cea\u3002 \u3000\u3000\u516c\u5b59\u71d5\u604d\u7136\u5982\u6709\u6240\u609f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4e00\u5b9a\u662f\u4ed6\u4eec\u5728\u95f9\u4ec0\u4e48\u522b\u626d\uff0c\u4ed6\u4ee5\u4e3a\u5c01\u59d0\u59d0\u4e0d \u559c\u6b22\u4ed6\uff0c\u6240\u4ee5\u624d\u5982\u6b64\u4f24\u5fc3\u3002\u4f46\u5c01\u59d0\u59d0\u53c8\u4e3a\u4ec0\u4e48\u8981\u54ed\u5462\uff1f\u5979\u662f\u4e0d\u559c\u6b22\u5389\u5927\u54e5\u5462\uff1f\u8fd8\u662f\u56e0\u4e3a\u5389\u5927 \u54e5\u4e0d\u61c2\u4f53\u8d34\uff0c\u4ee5\u81f4\u751f\u4ed6\u7684\u6c14\u5462\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u5f3a\u4f5c\u201c\u89e3\u4eba\u201d\uff0c\u5979\u54ea\u91cc\u77e5\u9053\u5c01\u5999\u5ae6\u662f\u56e0\u4e3a\u53d7\u4e86\u8427\u58f0\u7684\u611f\u89e6\uff0c\u60f3\u8d77\u4e86\u79e6\u5143\u6d69\u6765\uff0c\u56e0 \u800c\u4f24\u5fc3\u843d\u6cea\u7684\u3002\u8981\u77e5\u5979\u548c\u79e6\u5143\u6d69\u867d\u7136\u662f\u5fc3\u5fc3\u76f8\u5370\uff0c\u4f46\u5374\u8fd8\u6ca1\u6709\u673a\u4f1a\u7ed9\u4ed6\u4eec\u5410\u9732\u3002\u79e6\u5143\u6d69\u662f\u6b63 \u6d3e\u540d\u95e8\u7684\u5f1f\u5b50\uff0c\u7eb5\u7136\u6709\u91d1\u9010\u6d41\u505a\u5a92\uff0c\u8fd9\u5a5a\u4e8b\u4e5f\u672a\u5fc5\u80fd\u6210\u3002\u5c01\u5999\u5ae6\u56e0\u4e3a\u7236\u4eb2\u884c\u4e3a\u53ef\u6076\u7684\u7f18\u6545\uff0c \u96be\u514d\u6709\u81ea\u5351\u7684\u5fc3\u7406\u3002\u6b64\u65f6\u5979\u60a3\u5f97\u60a3\u5931\uff0c\u53ea\u89c9\u524d\u9014\u751a\u662f\u6e3a\u832b\uff0c\u4e8e\u662f\u4e0d\u7981\u60b2\u4ece\u4e2d\u6765\uff0c\u96be\u4ee5\u65ad\u7edd\u3002 \u3000\u3000\u516c\u5b59\u71d5\u54b3\u55fd\u4e00\u58f0\uff0c\u8d70\u5165\u6797\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u5389\u5927\u54e5\uff0c\u4f60\u8fd9\u8427\u5439\u5f97\u4e0d\u52a8\u542c\uff0c\u4f60\u770b\u90fd\u628a\u5c01\u59d0\u59d0\u5f04 \u54ed\u4e86\u3002\u4f60\u5439\u4e00\u652f\u597d\u542c\u7684\u8c03\u5b50\u5427\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u62b9\u4e86\u773c\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u89c9\u5f97\u5439\u5f97\u5f88\u597d\u542c\u3002\u521a\u521a\u6709\u4e00\u9897\u6c99\u8fdb\u4e86\u6211\u7684\u773c\u775b\uff0c\u6211\u54ea \u91cc\u662f\u54ed\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u6536\u4e86\u8427\u58f0\uff0c\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u597d\u542c\u7684\u8c03\u5b50\u6211\u53ef\u5439\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u597d\u4e86\uff0c\u597d\u4e86\uff0c\u4f60\u4e0d\u5439\u4e5f\u7f62\uff0c\u5e2e\u5fd9\u6211\u70e4\u91ce\u5154\u5427\u3002\u201d \u3000\u3000\u5403\u8fc7\u4e86\u665a\u996d\uff0c\u5389\u5357\u661f\u8d70\u8fdb\u5e10\u5e55\u6b47\u606f\u3002\u516c\u5b59\u71d5\u9053\uff1a\u201c\u8fd9\u91cc\u65e0\u7591\u662f\u4e16\u5916\u6843\u6e90\uff0c\u96be\u5f97\u6708\u8272\u53c8\u8fd9 \u6837\u597d\uff0c\u5c01\u59d0\u59d0\u4f60\u966a\u6211\u5230\u90a3\u8fb9\u6458\u91ce\u82b1\u53bb\uff0c\u8fc7\u4e00\u4f1a\u518d\u7761\u5982\u4f55\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u4e0d\u60f3\u7761\u89c9\uff0c\u597d\uff0c\u966a\u4f60\u8fd9\u4e2b\u5934\u75af\u53bb\u3002\u201d \u3000\u3000\u4e24\u4eba\u8d70\u5230\u79bb\u5f00\u5e10\u5e55\u767e\u6b65\u5f00\u5916\uff0c\u516c\u5b59\u71d5\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u5c01\u59d0\u59d0\uff0c\u4f60\u770b\u5389\u5927\u54e5\u8fd9\u4eba\u5982\u4f55\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u8bef\u4f1a\u4e86\u516c\u5b59\u71d5\u7684\u610f\u601d\uff0c\u6697\u81ea\u597d\u7b11\uff1a\u201c\u539f\u6765\u662f\u8fd9\u5c0f\u59ae\u5b50\u5fc3\u52a8\u4e86\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u5389 \u5927\u54e5\u6587\u6b66\u5168\u624d\uff0c\u5f53\u7136\u662f\u5f88\u4e0d\u9519\u5440\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u4e86\uff0c\u6bd5\u7adf\u5957\u51fa\u4f60\u7684\u771f\u8bdd\u6765\u4e86\u3002\u90a3\u4f60\u8ba9\u6211\u544a\u8bc9\u5389\u5927\u54e5\uff0c \u597d\u53eb\u4ed6\u6b22\u559c\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u5c01\u59d0\u59d0\uff0c\u4f60\u522b\u88c5\u50bb\u3002\u4f60\u4eec\u4e24\u4eba\u95f9\u522b\u626d\uff0c\u4f60\u5f53\u6211\u770b\u4e0d\u51fa\u6765\u5417\uff1f\u5509\uff0c\u5c01\u59d0\u59d0\uff0c \u4e0d\u662f\u6211\u8bf4\u4f60\uff0c\u4f60\u65e2\u7136\u559c\u6b22\u4eba\u5bb6\uff0c\u5c31\u4e0d\u8be5\u6349\u5f04\u4eba\u5bb6\u3002\u8fd9\u4e00\u4e2a\u6708\u6765\uff0c\u6211\u51b7\u773c\u65c1\u89c2\uff0c\u5389\u5927\u54e5\u56fa\u7136\u662f \u95f7\u95f7\u4e0d\u4e50\uff0c\u4f60\u4e5f\u662f\u4e0d\u597d\u8fc7\uff1f\u8fd9\u5982\u662f\u4f55\u82e6\u5462\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u8fd9\u662f\u4e00\u4e2a\u83ab\u540d\u5176\u5999\u7684\u8bef\u4f1a\uff0c\u4e0d\u89c9\u7b11\u9053\uff1a\u201c\u4f60\u51b7\u773c\u65c1\u89c2\uff0c\u5168\u770b \u9519\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u96be\u9053\u4f60\u4e0d\u559c\u6b22\u5389\u5927\u54e5\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5389\u5927\u54e5\u8fd8\u672a\u6709\u610f\u4e2d\u4eba\uff0c\u6211\u4f55\u4e0d\u5c31\u66ff\u4ed6\u64ae\u5408\uff1f\u516c\u5b59\u71d5\u65e2\u7136\u6709\u4e86\u8bef\u4f1a\uff0c \u6211\u5e94\u5f53\u5c06\u5fc3\u4e8b\u544a\u8bc9\u5979\u3002\u201d\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u7ed9\u6211\u4f5c\u7ea2\u5a18\u662f\u4e0d\u662f\uff1f\u4f46\u6211\u4e5f\u6b63\u60f3\u7ed9\u4f60\u505a\u7ea2\u5a18 \u5462\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u4e0d\u89c9\u4e5f\u662f\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u548c\u4f60\u8bf4\u6b63\u7ecf\u8bdd\uff0c\u4f60\u600e\u80fd\u548c\u6211\u5f00\u8fd9\u6837\u7684\u73a9\u7b11\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6211\u8bf4\u7684\u6b63\u662f\u6b63\u7ecf\u8bdd\u3002\u4f60\u4e0d\u662f\u540c\u610f\u5389\u5927\u54e5\u4eba\u5f88\u4e0d\u9519\u5417\uff1f\u6240\u4ee5\uff0c\u6211\u7528\u4e0d\u7740\u4f60\u4f5c \u7ea2\u5a18\uff0c\u5012\u662f\u4f60\u7528\u5f97\u7740\u6211\u4f5c\u7ea2\u5a18\u5462\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u602b\u7136\u4e0d\u60a6\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u6211\u5c82\u80fd\u62a2\u4f60\u6240\u559c\u6b22\u7684\u4eba\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u7b11\u9053\uff1a\u201c\u4f60\u5b8c\u5168\u5f04\u9519\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4f60\u4e0d\u559c\u6b22\u4ed6\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6211\u656c\u91cd\u5389\u5927\u54e5\uff0c\u4f46\u4e0d\u662f\u7537\u5973\u76f8\u60a6\u7684\u90a3\u79cd\u2018\u559c\u6b22\u2019\uff0c\u4f60\u660e\u767d\u4e86\u5417\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u65b9\u59cb\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u53e6\u6709\u5fc3\u4e0a\u4eba\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u988a\u6655\u8f7b\u7ea2\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8bf4\u4f60\u7528\u4e0d\u7740\u64cd\u5fc3\u4e86\u3002\u8fd9\u2018\u7ea2\u5a18\u2019\u65e9\u5df2\u6709\u4eba\u7ed9\u6211\u4f5c \u5566\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u559c\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u4e9b\u544a\u8bc9\u6211\uff0c\u90a3\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u662f\u6b66\u5f53\u6d3e\u7684\u79e6\u5143\u6d69\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u79e6\u5143\u6d69\uff1f\u55ef\uff0c\u8fd9\u540d\u5b57\u597d\u719f\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u662f\u6b66\u5f53\u7684\u540e\u8d77\u4e4b\u79c0\uff0c\u5c01\u5999\u5ae6\u5fc3\u60f3\u516c\u5b59\u71d5\u542c\u8fc7\u4ed6\u7684\u540d\u5b57\u5c5e\u5bfb\u5e38\uff0c\u56e0\u6b64\u5e76\u4e0d\u8ffd\u95ee\u4e0b \u53bb\u3002 \u3000\u3000\u516c\u5b59\u71d5\u5374\u5728\u7d6e\u7d6e\u53e8\u53e8\u5730\u95ee\u5979\uff0c\u5c01\u5999\u5ae6\u4e5f\u4e0d\u9690\u7792\uff0c\u628a\u5979\u548c\u79e6\u5143\u6d69\u7684\u6240\u6709\u7ecf\u8fc7\u90fd\u5bf9\u516c\u5b59\u71d5\u8bf4 \u4e86\u3002\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u662f\u4e3a\u4e86\u4e0d\u80fd\u5f97\u5230\u79e6\u5143\u6d69\u7684\u6d88\u606f\u800c\u95f7\u95f7\u4e0d\u4e50\u3002\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u5ffd \u5730\u53eb\u9053\uff1a\u201c\u6211\u60f3\u8d77\u6765\u4e86\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u8be7\u9053\uff1a\u201c\u4f60\u60f3\u8d77\u4ec0\u4e48\u6765\u4e86\uff0c\u5982\u6b64\u5927\u60ca\u5c0f\u602a\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u6b66\u5f53\u638c\u95e8\u96f7\u9707\u5b50\u7684\u95e8\u4e0b\u662f\u4e0d\u662f\u53ea\u6709\u4e00\u4e2a\u59d3\u79e6\u7684\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u516c\u5b59\u71d5\u9053\uff1a\u201c\u90a3\u5c31\u4e00\u5b9a\u662f\u79e6\u5143\u6d69\u3002\u201d\u7239\u7239\u5f53\u65f6\u542c\u4e86\u8fd9\u6869\u4e8b\u60c5\uff0c\u4e5f \u66fe\u63d0\u53ca\u79e6\u5143\u6d69\u8fd9\u4e2a\u540d\u5b57\u3002\u602a\u4e0d\u5f97\u6211\u542c\u6765\u597d\u719f\uff0c\u4f46\u5374\u4e00\u65f6\u60f3\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u53c8\u60ca\u53c8\u559c\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u9053\u79e6\u5143\u6d69\u7684\u4e0b\u843d\uff1f\u90a3\u53c8\u662f\u4ec0\u4e48\u4e00\u6869\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4e09\u5929\u4e4b\u5185\uff0c\u6211\u5305\u7ba1\u4f60\u89c1\u7740\u79e6\u5143\u6d69\uff01\u54c8\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u559c\u6b22\u5f97\u50bb\u4e86\u5417\uff1f\u201d\u5c01\u5999 \u5ae6\u51e0\u4e4e\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u65b9\u59cb\u5410\u5f97\u51fa\u4e24\u4e2a\u5b57\u6765\uff1a\u201c\u771f\u7684\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u5f53\u7136\u662f\u771f\u7684\uff0c\u6211\u9a97\u4f60\u505a\u4ec0\u4e48\u3002\u4f60\u7a0d\u5b89\u52ff\u8e81\uff0c\u4ed4\u7ec6\u542c\u5427\u3002\u201d \u3000\u3000\u201c\u5728\u5b9d\u9e21\u5357\u9762\u3001\u79e6\u5cad\u811a\u4e0b\uff0c\u6709\u4e00\u6761\u5c71\u6751\uff0c\u6751\u4e2d\u6709\u4e2a\u5e84\u5b50\uff0c\u540d\u53eb\u6c34\u4e91\u5e84\u3002\u5e84\u4e3b\u4e91\u9f99\u4e43\u662f\u6b66 \u6797\u4e16\u5bb6\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e5f\u5f88\u6709\u70b9\u540d\u6c14\u7684\u3002\u4f60\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6211\u5f88\u5c11\u5728\u6c5f\u6e56\u8d70\u52a8\uff0c\u5b9e\u5728\u662f\u5b64\u964b\u5be1\u95fb\uff0c\u5bf9\u6c34\u4e91\u5e84\u6beb\u65e0\u6240\u77e5\u3002\u4f60\u8fd9\u4e48\u8bf4\uff0c\u83ab \u975e\u79e6\u5143\u6d69\u5c31\u5728\u8fd9\u6c34\u4e91\u5e84\u4e48\u3002\u201d\u516c\u5b59\u71d5\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u4f55\u4ee5\u4ed6\u4f1a\u5728\u6c34\u4e91\u5e84\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u79e6\u5cad\u6709\u5e2e\u5f3a\u4eba\uff0c\u8fd9\u5e2e\u5f3a\u4eba\u7684\u5934\u5b50\u540d\u5524\u7f57\u5927\u9b41\uff0c\u662f\u4f7f\u2018\u4e94\u864e\u65ad\u95e8\u5200\u2019\u7684\u9ad8 \u624b\uff0c\u4ed6\u6709\u4e09\u4e2a\u7ed3\u62dc\u5144\u5f1f\uff0c\u4e2a\u4e2a\u6b66\u827a\u4e0d\u51e1\u3002\u201d \u3000\u3000\u201c\u4e91\u9f99\u6709\u4e2a\u5973\u513f\u540d\u5524\u4e91\u4e2d\u71d5\u3002\u5979\u7684\u540d\u5b57\u4e2d\u6709\u4e2a\u2018\u71d5\u2019\u5b57\u548c\u6211\u624d\u76f8\u540c\uff0c\u6240\u4ee5\u6211\u8bb0\u5f97\u5f88\u6e05 \u695a\u3002\u201d\u516c\u5b59\u71d5\u662f\u4e2a\u7231\u8bf4\u8bdd\u7684\u59d1\u5a18\uff0c\u5e38\u5e38\u559c\u6b22\u8282\u5916\u751f\u679d\uff0c\u5979\u56e0\u4e3a\u81ea\u5df1\u8bb0\u4e0d\u8d77\u79e6\u5143\u6d69\u7684\u540d\u5b57\u5374\u8bb0 \u5f97\u4e91\u4e2d\u71d5\u7684\u540d\u5b57\uff0c\u662f\u4ee5\u52a0\u4ee5\u89e3\u91ca\uff0c\u5c01\u5999\u5ae6\u662f\u201c\u6025\u60ca\u98ce\u78b0\u7740\u4e86\u6162\u90ce\u4e2d\u201d\uff0c\u5fc3\u91cc\u597d\u4e0d\u7740\u6025\uff0c\u95ee \u9053\uff1a\u201c\u8fd9\u4e91\u4e2d\u71d5\u53c8\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u6162\u6761\u65af\u7406\u5730\u8bf4\u9053\uff1a\u201c\u8fd9\u4e91\u4e2d\u71d5\u957f\u5f97\u5f88\u662f\u7f8e\u8c8c\uff0c\u54c8\uff0c\u6709\u4eba\u770b\u4e2d\u5979\u4e86\uff01\u201d\u8bf4\u81f3\u6b64\u5904\uff0c \u6545\u610f\u505c\u4e86\u4e0b\u6765\uff0c\u88c5\u51fa\u6b32\u8bf4\u8fd8\u4f11\u7684\u795e\u6c14\u3002\u201d\u5c01\u5999\u5ae6\u679c\u7136\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u683c\u683c\u7b11\u9053\uff1a\u201c\u4f60\u522b\u5fc3\u614c\uff0c\u4e0d\u662f\u4f60\u90a3\u4f4d\u79e6\u516c\u5b50\uff0c\u662f\u79e6\u5cad\u4e0a\u7684\u90a3\u4e2a\u5f3a\u76d7\u5934\u5b50\u7f57\u5927\u9b41\u3002 \u3000\u3000\u201c\u7f57\u5927\u9b41\u6d3e\u4eba\u5230\u6c34\u4e91\u5e84\u63d0\u4eb2\uff0c\u4e91\u5e84\u4e3b\u5f53\u7136\u4e0d\u80af\u5e94\u5141\uff0c\u7f57\u5927\u9b41\u4e0d\u6b7b\u5fc3\uff0c\u4e09\u65e5\u4e4b\u540e\uff0c\u53c8\u6d3e\u4eba \u9001\u4e00\u5c01\u4fe1\u6765\uff0c\u626c\u8a00\u8981\u62e9\u5409\u8fce\u65b0\u3002 \u3000\u3000\u201c\u7f57\u5927\u9b41\u4e3a\u4ec0\u4e48\u4e0d\u9a6c\u4e0a\u6765\u62a2\u4eb2\u5462\uff1f\u90a3\u662f\u56e0\u4e3a\u4ed6\u5bf9\u6c34\u4e91\u5e84\u4e5f\u9887\u6709\u987e\u5fcc\u4e4b\u6545\u3002\u662f\u4ee5\uff0c\u4ed6\u5fc5\u987b \u9080\u8bf7\u5e2e\u624b\u62a2\u4eb2\u3002\u540c\u6837\uff0c\u6c34\u4e91\u5e84\u4e3a\u4e86\u5e94\u4ed8\u8fd9\u5e2e\u5f3a\u4eba\uff0c\u4e5f\u8d76\u5fd9\u53bb\u8bf7\u5404\u65b9\u597d\u53cb\u52a9\u62f3\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7ea2\u7f28\u4f1a\u6709\u4f4d\u9999\u4e3b\u540d\u5524\u77f3\u7384\uff0c\u5f53\u65f6\u6070\u5de7\u5728\u5b9d\u9e21\u8bbf\u53cb\uff0c\u4ed6\u8fd9\u4f4d\u670b \u53cb\u548c\u6c34\u4e91\u5e84\u5e84\u4e3b\u4e91\u9f99\u5f88\u6709\u4ea4\u60c5\uff0c\u63a5\u5230\u4e86\u4e91\u9f99\u6c42\u63f4\u7684\u4e66\u4fe1\uff0c\u4fbf\u62c9\u4e86\u77f3\u76f4\u540c\u53bb\u52a9\u62f3\u3002 \u3000\u3000\u201c\u636e\u77f3\u7384\u8bf4\u90a3\u5929\u7f57\u5927\u9b41\u5e26\u4eba\u524d\u6765\u62a2\u4eb2\uff0c\u53cc\u65b9\u5c55\u5f00\u4e86\u4e00\u573a\u6fc0\u6218\u3002\u5bf9\u65b9\u4eba\u591a\u52bf\u5927\uff0c\u8d77\u521d\u6c34\u4e91 \u5e84\u65b9\u9762\u751a\u4e3a\u4e0d\u5229\u3002\u773c\u770b\u5c31\u8981\u5927\u8d25\u4e8f\u8f93\u4e4b\u9645\uff0c\u5e78\u5f97\u4e00\u4f4d\u5c11\u5e74\u82f1\u96c4\u53ca\u65f6\u8d76\u5230\u3002\u8fd9\u4f4d\u5c11\u5e74\u82f1\u96c4\u529b\u640f \u5f3a\u76d7\u5934\u5b50\u7f57\u5927\u9b41\uff0c\u4ed6\u8eab\u4e0a\u88ab\u7f57\u5927\u9b41\u65ab\u4e86\u4e03\u5200\uff0c\u4f46\u6700\u540e\u5374\u662f\u7f57\u5927\u9b41\u5403\u4e86\u4ed6\u7684\u7a7f\u5fc3\u5251\uff0c\u4e00\u5251\u5c31\u7ed3 \u679c\u4e86\u7f57\u5927\u9b41\u7684\u6027\u547d\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u5df2\u731c\u5230\u51e0\u5206\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u5c11\u5e74\u82f1\u96c4\u662f\u2026\u2026\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u5c31\u662f\u4f60\u7684\u5fc3\u4e0a\u4eba\u79e6\u5143\u6d69\u4e86\u3002\u6b66\u5f53\u6d3e\u638c\u95e8\u4eba\u96f7\u9707\u5b50\u548c\u4e91\u8c0a\u662f\u5f7c\u6b64\u6155\u540d\u7684\u670b \u53cb\uff0c\u662f\u4ee5\u4ed6\u867d\u7136\u6ca1\u6709\u63a5\u5230\u4e91\u9f99\u6c42\u63f4\u7684\u4e66\u4fe1\uff0c\u4f46\u77e5\u9053\u4e86\u8fd9\u4e2a\u6d88\u606f\u4e4b\u540e\uff0c\u4fbf\u7acb\u5373\u6d3e\u51fa\u4e86\u4ed6\u6700\u5f97\u610f \u7684\u5f1f\u5b50\u79e6\u5143\u6d69\u6765\u3002 \u3000\u3000\u201c\u77f3\u7384\u548c\u79e6\u5143\u6d69\u5e76\u4e0d\u76f8\u8bc6\uff0c\u5f53\u65f6\u53ea\u542c\u5f97\u6c34\u4e91\u5e84\u7684\u4eba\u53eb\u4ed6\u505a\u79e6\u5143\u6d69\uff0c\u56de\u6765\u548c\u6211\u7239\u7239\u4e00\u8bf4\uff0c \u6211\u7239\u7239\u8bf4\u9053\u4e00\u5b9a\u662f\u79e6\u5143\u6d69\u65e0\u7591\u3002\u5c01\u59d0\u59d0\uff0c\u6c34\u4e91\u5e84\u79bb\u6b64\u4e0d\u8fc7\u4e09\u65e5\u8def\u7a0b\uff0c\u8fd9\u53ef\u4e0d\u662f\u8e0f\u7834\u94c1\u978b\u65e0\u89c5 \u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\u5417\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u53c8\u60ca\u53c8\u559c\uff0c\u65e0\u5fc3\u542c\u516c\u5b59\u71d5\u7684\u95f2\u8bdd\uff0c\u6253\u65ad\u5979\u7684\u8bdd\u5934\u95ee\u9053\uff1a \u3000\u3000\u201c\u79e6\u5143\u6d69\u4e2d\u4e86\u4e03\u5200\uff0c\u90a3\u4e48\u4ed6\u7684\u4f24\u2026\u2026\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u79e6\u5143\u6d69\u867d\u7136\u4f24\u5f97\u4e0d\u8f7b\uff0c\u5374\u8fd8\u6ca1\u6709\u6027\u547d\u4e4b\u5fe7\u3002\u6c34\u4e91\u5e84\u6709\u7684\u662f\u4e0a \u597d\u7684\u91d1\u521b\u836f\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u79e6\u5143\u6d69\u662f\u7559\u5728\u6c34\u4e91\u5e84\u517b\u4f24\u4e86\uff1f\u8fd9\u662f\u4ec0\u4e48\u65f6\u5019\u7684\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u8fd9\u662f\u516d\u4e2a\u6708\u524d\u7684\u4e8b\u60c5\uff0c\u636e\u77f3\u7384\u8bf4\uff0c\u79e6\u5143\u6d69\u6027\u547d\u53ef\u4ee5\u65e0\u5fe7\uff0c\u4f46\u56e0\u6d41\u8840\u8fc7\u591a\uff0c \u6050\u6015\u4e5f\u5f97\u517b\u4f24\u534a\u622a\u3002\u54b1\u4eec\u4e09\u5929\u4e4b\u540e\u8d76\u5230\u6c34\u4e91\u5e84\uff0c\u4e5f\u8bb8\u79e6\u5143\u544a\u8fd8\u6ca1\u6709\u5b8c\u5168\u4f24\u597d\u5462\uff0c\u4f60\u51c6\u53ef\u4ee5\u89c1 \u5f97\u7740\u4ed6\u7684\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u5fc3\u4e0a\u7684\u4e00\u5757\u77f3\u5934\u843d\u4e86\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u4f46\u613f\u6ca1\u6709\u4ec0\u4e48\u610f\u5916\u624d\u597d\u3002\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u5ffd\u89c1\u516c \u5b59\u71d5\u82e5\u6709\u6240\u601d\u7684\u6837\u5b50\uff0c\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u71d5\u59d0\uff0c\u4f60\u597d\u50cf\u8fd8\u6709\u4ec0\u4e48\u8bdd\u8981\u548c\u6211\u8bf4\uff0c\u662f\u4e48\uff1f\u201d\u516c\u5b59\u71d5 \u9053\uff1a\u201c\u6211\u60f3\uff0c\u4e0d\u4f1a\u6709\u4ec0\u4e48\u610f\u5916\u7684\u4e86\u3002\u7f57\u5927\u9b41\u5df2\u7ecf\u88ab\u6740\uff0c\u79e6\u5cad\u90a3\u5e2e\u5f3a\u4eba\u4ea6\u90fd\u5df2\u74e6\u89e3\u4e86\u3002\u79e6\u5143\u6d69 \u5728\u6c34\u4e91\u5e84\u517b\u4f24\uff0c\u6c34\u4e91\u5e84\u7684\u4eba\u81ea\u4f1a\u59a5\u52a0\u7167\u6599\u3002\u201d \u3000\u3000\u5176\u5b9e\u516c\u5b59\u71d5\u7684\u786e\u662f\u60f3\u5230\u4e86\u4e00\u4e2a\u53ef\u80fd\u53d1\u751f\u7684\u201c\u610f\u5916\u201d\uff0c\u5c01\u5999\u5ae6\u4e0d\u8bf4\u5979\u60f3\u4e0d\u8d77\uff0c\u5c01\u5999\u5ae6\u4e00\u8bf4 \u5230\u8fd9\u201c\u610f\u5916\u201d\u4e8c\u5b57\uff0c\u516c\u5b59\u71d5\u84e6\u5730\u60f3\u8d77\u77f3\u7384\u544a\u8bc9\u5979\u7684\u4e00\u4ef6\u4e8b\u60c5\u3002\u6c34\u4e91\u5e84\u7684\u5e84\u4e3b\u4e91\u9f99\u611f\u6fc0\u79e6\u5143\u6d69 \u7684\u5927\u6069\uff0c\u6709\u610f\u628a\u4ed6\u7684\u5973\u513f\u8bb8\u7ed9\u79e6\u5143\u6d69\uff0c\u8fd9\u662f\u6c34\u4e91\u5e84\u7684\u4eba\u79c1\u5e95\u4e0b\u8c08\u8bba\uff0c\u7ed9\u77f3\u7384\u542c\u89c1\u4e86\u3002\u4f46\u516c\u5b59 \u71d5\u5f53\u7136\u4e0d\u4f1a\u628a\u8fd9\u4e9b\u542c\u6765\u7684\u8bf4\u8bdd\u544a\u8bc9\u5c01\u5999\u5ae6\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u516c\u5b59\u71d5\u548c\u5389\u5357\u661f\u8bf4\u4e86\uff0c\u5389\u5357\u661f\u4e5f\u5f88\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u5c31\u5230\u6c34\u4e91 \u5e84\u53bb\uff0c\u627e\u7740\u4e86\u79e6\u5143\u6d69\uff0c\u4e00\u540c\u524d\u5f80\u897f\u660c\u5427\u3002\u5c01\u59d1\u5a18\uff0c\u4f60\u4e0e\u79e6\u5144\u56e2\u5706\u5728\u671b\uff0c\u606d\u559c\u4f60\u4e86\u3002\u201d\u5c01\u5999\u5ae6 \u6ee1\u9762\u901a\u7ea2\uff0c\u5fc3\u4e2d\u5374\u662f\u65e0\u9650\u9ad8\u5174\u3002\u201d \u3000\u3000\u4e09\u4eba\u517c\u7a0b\u8d76\u8def\uff0c\u7b2c\u4e8c\u5929\u508d\u665a\u65f6\u5206\u5c31\u5230\u4e86\u9647\u53bf\u3002\u6c34\u4e91\u5e84\u5728\u79e6\u5cad\u5c71\u811a\u3002\u8e0f\u5165\u4e86\u9647\u53bf\uff0c\u79e6\u5cad\u5c71 \u8109\uff0c\u5df2\u662f\u9065\u9065\u53ef\u89c1\u3002\u5389\u5357\u661f\u9053\uff1a\u201c\u4eca\u665a\u54b1\u4eec\u5728\u53bf\u57ce\u8fc7\u4e00\u665a\uff0c\u660e\u5929\u4e2d\u5348\uff0c\u5c31\u53ef\u4ee5\u5230\u6c34\u4e91\u5e84\u4e86\u3002 \u672c\u6765\u82e5\u662f\u8981\u8d76\u8def\u7684\u8bdd\uff0c\u4eca\u665a\u4e09\u66f4\u8fc7\u540e\uff0c\u4e5f\u53ef\u4ee5\u8d74\u5f97\u5230\u6c34\u4e91\u5e84\u7684\u3002\u4f46\u5348\u591c\u767b\u95e8\uff0c\u4f3c\u4e4e\u6709\u70b9\u4e0d\u5927 \u65b9\u4fbf\u3002\u5c01\u59d1\u5a18\uff0c\u4f60\u4e0d\u6025\u5728\u8fd9\u534a\u5929\u5427\uff1f\u201d\u5c01\u5999\u5ae6\u988a\u6655\u5a07\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u5389\u5927\u54e5\u8bf4\u7b11\u4e86\uff0c\u5f53\u7136\u662f\u767d \u5929\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u5ffd\u9053\uff1a\u201c\u5389\u5927\u54e5\uff0c\u4f60\u6709\u6ca1\u6709\u6ce8\u610f\u5230\u4e00\u6869\u4e8b\u60c5\uff1f\u201d\u5389\u5357\u661f\u9053\uff1a\u201c\u4f60\u662f\u6307\u54b1\u4eec\u5728\u4e00\u8def \u4e0a\u78b0\u89c1\u7684\u90a3\u4e9b\u4eba\u5417\uff1f\u201d\u539f\u6765\u8fd9\u4e24\u5929\u4ed6\u4eec\u4e00\u8def\u884c\u6765\uff0c\u5df2\u7ecf\u78b0\u89c1\u4e86\u597d\u51e0\u62e8\u9a91\u7740\u9a6c\u5e26\u6709\u6b66\u5668\u7684\u4eba\u9a6c \u7ecf\u8fc7\uff0c\u8fd9\u4e9b\u4eba\u4e00\u770b\u5c31\u77e5\u662f\u6c5f\u6e56\u4eba\u7269\u3002 \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u662f\u5440\uff01\u8fd9\u6761\u8def\u4e43\u662f\u5c71\u8def\uff0c\u7167\u7406\u662f\u5e94\u8be5\u884c\u4eba\u7a00\u5c11\u7684\u3002\u4e0d\u77e5\u4f55\u4ee5\u4f1a\u6709\u8fd9\u8bb8\u591a\u6c5f \u6e56\u4eba\u7269\u7ecf\u8fc7\uff1f\u96be\u9053\u662f\u5728\u79e6\u5cad\u4e4b\u4e2d\uff1b\u6709\u4ec0\u4e48\u5e2e\u6d3e\u7684\u805a\u4f1a\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u54b1\u4eec\u5230\u4e86\u6c34\u4e91\u5e84\uff0c\u95ee\u4e00\u95ee\u4e91\u5e84\u4e3b\uff0c\u60f3\u5fc5\u4ed6\u4f1a\u77e5\u9053\uff1a\u201c \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u7591\u5fc3\u5417\uff1f\u5176\u5b9e\u8fd9\u4e9b\u95f2\u4e8b\uff0c\u54b1\u4eec\u4e0d\u7ba1\u4e5f\u7f62\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u662f\u60f3\u591a\u7ba1\u95f2\u4e8b\uff0c\u53ea\u662f\u6709\u70b9\u597d\u5947\u3002\u201d \u3000\u3000\u6b63\u5728\u8bf4\u8bdd\u4e4b\u9645\uff0c\u53c8\u6709\u4e24\u9a91\u9a6c\u8def\u6765\uff0c\u5176\u4e2d\u4e00\u4eba\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u516c\u5b59\u59d1\u5a18\u5417\uff1f\u516c\u5b59\u59d1 \u5a18\uff0c\u4f60\u8fd8\u8ba4\u5f97\u6211\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u4e24\u4eba\u8ba4\u51fa\u4e86\u516c\u5b59\u71d5\uff0c\u7acb\u5373\u4e0b\u9a6c\uff0c\u5411\u5979\u65bd\u793c\u3002\u516c\u5b59\u71d5\u5446\u4e86\u4e00\u5446\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u996e\u9a6c\u5ddd\u7684 \u674e\u5be8\u4e3b\u5427\uff1f\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u59d1\u5a18\u597d\u8bb0\u6027\uff0c\u6211\u6b63\u662f\u674e\u864e\u513f\u3002\u8fd9\u4f4d\u662f\u6211\u7684\u670b\u53cb\uff0c\u8df3\u864e\u6da7\u7684\u5f20\u5be8\u4e3b\u5f20\u9e4f\u98de\u3002\u201d \u539f\u6765\u8fd9\u674e\u864e\u513f\u53bb\u5e74\u66fe\u7ecf\u5230\u8fc7\u7ea2\u7f28\u4f1a\u603b\u8235\u62dc\u89c1\u516c\u5b59\u5b8f\uff0c\u516c\u5b59\u71d5\u6367\u8fc7\u8336\u7ed9\u4ed6\u559d\u7684\u3002 \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4f60\u4eec\u5230\u54ea\u513f\u53bb\u5440\uff1f\u8fd9\u4e48\u5306\u5306\u5fd9\u5fd9\u7684\u8d76\u8def\uff01\u201d \u3000\u3000\u674e\u864e\u513f\u9053\uff1a\u201c\u6211\u4eec\u662f\u53bb\u559d\u559c\u9152\u7684\u3002\u660e\u5929\u53ef\u5f97\u8d76\u5230\u6c34\u4e91\u5e84\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u559d\u8c01\u5bb6\u7684\u559c\u9152\uff1f\u201d \u3000\u3000\u674e\u864e\u513f\u9053\uff1a\u201c\u6c34\u4e91\u5e84\u4e3b\u4e91\u9f99\u7684\u5973\u513f\u660e\u5929\u51fa\u9601\u3002\u4f60\u60f3\u5fc5\u77e5\u9053\u8fd9\u4f4d\u4e91\u5e84\u4e3b\u5427\uff1f\u8d35\u4f1a\u7684\u77f3\u9999\u4e3b \u534a\u5e74\u524d\u66fe\u7ecf\u5230\u8fc7\u6c34\u4e91\u5e84\u7684\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u6211\u542c\u5f97\u77f3\u7384\u8bf4\u8fc7\u3002\u4ed6\u7684\u5973\u513f\u53eb\u505a\u4e91\u4e2d\u71d5\u662f\u4e0d\u662f\uff1f\u201d\u674e\u864e\u513f\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u660e \u5929\u6b63\u662f\u5979\u7684\u4f73\u671f\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4e0d\u77e5\u4e91\u5e84\u4e3b\u7684\u4e58\u9f99\u5feb\u5a7f\u5374\u662f\u4f55\u4eba\uff1f\u201d \u3000\u3000\u6b63\u662f\uff1a\u53ea\u6015\u65e7\u6b22\u5982\u68a6\u901d\uff0c\u9192\u6765\u65e0\u5904\u53ef\u8ffd\u5bfb\u3002 \u3000\u3000\u6b32\u77e5\u540e\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e8c\u56de\u3000\u4e5d\u5dde\u60ef\u94f8\u4eba\u95f4\u9519\u3000\u4e00\u7f15\u96be\u62bd\u8327\u5e95\u4e1d \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e09\u5341\u4e8c\u56de\u3000\u4e5d\u5dde\u60ef\u94f8\u4eba\u95f4\u9519\u3000\u4e00\u7f15\u96be\u62bd\u8327\u5e95\u4e1d \u3000\u3000\u674e\u864e\u513f\u9053\uff1a\u201c\u542c\u8bf4\u662f\u4e00\u4f4d\u59d3\u79e6\u7684\u5c11\u5e74\u82f1\u96c4\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u604d\u4f3c\u6674\u5929\u6253\u4e86\u4e2a\u9739\u96f3\uff0c\u4e0d\u4f46\u5c01\u5999\u5ae6\u767b\u65f6\u9762\u8272\u7070\u767d\uff0c\u516c\u5b59\u71d5\u7684\u53cc\u7709\u4e5f\u7ad6\u4e86\u8d77 \u6765\uff0c\u60c5\u6025\u4e4b\u4e0b\uff0c\u51b2\u53e3\u800c\u51fa\u7684\u5c31\u95ee\uff1a\u201c\u79e6\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u674e\u864e\u513f\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u60f3\uff1a\u201c\u96be\u9053\u5979\u548c\u90a3\u59d3\u79e6\u7684\u6709\u4ec0\u4e48\u5173\u7cfb\u4e0d\u6210\uff1f\u201d\u5f53\u4e0b\u7b54\u9053\uff1a\u201c\u6211\u53ea\u77e5 \u9053\u4ed6\u662f\u59d3\u79e6\uff0c\u4ec0\u4e48\u540d\u5b57\u6211\u53ef\u4e0d\u77e5\u9053\u4e86\u3002\u6211\u4eec\u662f\u542c\u5f97\u4e91\u5e84\u4e3b\u5ac1\u5973\u7684\u6d88\u606f\u5c31\u8d77\u6765\u7684\uff0c\u5e76\u6ca1\u6709\u6536\u5230 \u4ed6\u7684\u8bf7\u5e16\u3002\u201d \u3000\u3000\u5f20\u9e4f\u98de\u6bd4\u8f83\u8d63\u9053\uff0c\u8fd8\u6ca1\u6709\u89c9\u5bdf\u5230\u516c\u5b59\u71d5\u7684\u9762\u8272\u5df2\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u4f4d\u59d3\u79e6\u7684\u5c11\u5e74\u82f1\u96c4 \u66fe\u7ecf\u5e2e\u8fc7\u6c34\u4e91\u5e84\u5f88\u5927\u7684\u5fd9\uff0c\u4eca\u5e74\u6625\u5929\uff0c\u79e6\u5cad\u7684\u7f57\u5927\u9b41\u5230\u6c34\u4e91\u5e84\u62a2\u4eb2\u7684\u4e8b\u60f3\u5fc5\u4f60\u5df2\u77e5\u9053\uff0c\u90a3\u7f57 \u5927\u9b41\u5c31\u662f\u7ed9\u59d3\u79e6\u7684\u6740\u6389\u7684\uff0c\u6211\u4eec\u996e\u9a6c\u5ddd\u548c\u8df3\u864e\u6d1e\u90fd\u66fe\u53d7\u8fc7\u7f57\u5927\u9b41\u7684\u6b3a\u8d1f\uff0c\u90a3\u6b21\u62a2\u4eb2\u4e4b\u4e8b\uff0c\u6211 \u4eec\u53c8\u8d76\u4e0d\u53ca\u7ed9\u4e91\u5e84\u4e3b\u5e2e\u5fd9\uff0c\u662f\u4ee5\u8fd9\u6b21\u559c\u9152\uff0c\u6211\u4eec\u867d\u7136\u6ca1\u6709\u8bf7\u5e16\uff0c\u4e5f\u8be5\u53bb\u5411\u4ed6\u9053\u8d3a\u548c\u81f4\u8c22\u4e86\uff01 \u516c\u5b59\u59d1\u5a18\uff0c\u4f60\u4eec\u53c8\u662f\u4e0a\u54ea\u513f\uff1f\u8fd9\u4f4d\u4e91\u5e84\u4e3b\u7d20\u6765\u597d\u5ba2\uff0c\u548c\u8d35\u4f1a\u53c8\u66fe\u7ecf\u6709\u8fc7\u4e00\u4efd\u4ea4\u60c5\uff0c\u4f60\u82e5\u662f\u80af \u9a7e\u4e34\u6c34\u4e91\u5e84\uff0c\u4e91\u5e84\u4e3b\u4e00\u5b9a\u5341\u5206\u9ad8\u5174\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8fdf\u65e9\u6211\u4f1a\u5230\u6c34\u4e91\u5e84\u7684\uff0c\u4e0d\u5fc5\u4f60\u4ee3\u4e91\u9f99\u9080\u5ba2\u3002\u201d \u3000\u3000\u5f20\u9e4f\u98de\u78b0\u4e86\u4e2a\u9489\u5b50\uff0c\u83ab\u540d\u5176\u5999\u3002\u674e\u864e\u513f\u5fc3\u77e5\u4e0d\u5999\uff0c\u5fd9\u9053\uff1a\u201c\u597d\uff0c\u516c\u5b59\u59d1\u5a18\u65e2\u662f\u53e6\u5916\u6709 \u4e8b\uff0c\u6211\u4eec\u5148\u8d70\u4e86\uff01\u201d\u5f53\u4e0b\u8fde\u5fd9\u4e0a\u9a6c\u5c31\u8dd1\uff0c\u8fde\u5389\u5357\u661f\u548c\u5c01\u5999\u5ae6\u7684\u59d3\u540d\u90fd\u987e\u4e0d\u5f97\u8bf7\u6559\u4e86\u3002 \u3000\u3000\u516c\u5b59\u71d5\u5341\u5206\u7740\u607c\uff0c\u8bf4\u9053\uff1a\u201c\u5c01\u59d0\u59d0\uff0c\u4f60\u522b\u96be\u8fc7\uff0c\u6211\u548c\u4f60\u5230\u6c34\u4e91\u5e84\u53bb\u627e\u79e6\u5143\u6d69\u7b97\u5e10\u53bb\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u6b64\u65f6\u72b9\u5982\u4e07\u7bad\u94bb\u5fc3\uff0c\u96be\u582a\u4e4b\u6781\uff0c\u5fcd\u4f4f\u4e86\u6cea\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u53bb\u6c34\u4e91\u5e84\u505a\u4f5c\u4e48\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u627e\u79e6\u5143\u6d69\u7406\u8bba\u53bb\u5440\uff01\u8fd9\u6837\u8d1f\u5fc3\u7684\u6c49\u5b50\uff0c\u4f60\u7eb5\u7136\u9976\u4ed6\uff0c\u6211\u4e5f\u4e0d\u80fd\u9976\u4ed6\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u4e0d\u7981\u773c\u6cea\u52b3\u7736\u800c\u51fa\uff0c\u8bf4\u9053\uff1a\u201c\u71d5\u59d0\uff0c\u4f60\u522b\u8fd9\u6837\uff0c\u6211\u53ea\u6028\u81ea\u5df1\u547d\u82e6\uff0c\u53ef\u602a\u4e0d\u5f97\u79e6\u5143 \u6d69\u3002\u6211\u4eec\u672c\u6765\u5c31\u6ca1\u6709\u5a5a\u59fb\u4e4b\u7ea6\uff0c\u600e\u80fd\u7981\u6b62\u4ed6\u53e6\u914d\u9ad8\u95e8\uff1f\u6c42\u6c42\u4f60\u987e\u5168\u6211\u7684\u989c\u9762\uff0c\u522b\u53bb\u95f9\u4e86\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u4ecd\u7136\u6124\u6124\u4e0d\u5e73\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u5a5a\u59fb\u4e4b\u7ea6\u4ed6\u4e5f\u5e94\u8be5\u77e5\u9053\u4f60\u5bf9\u4ed6\u7684\u60c5\u610f\u5440\uff01\u54fc\uff0c\u4f60\u8fd8 \u7ed9\u4ed6\u8fa9\u89e3\uff0c\u82e5\u662f\u4f9d\u6211\u7684\u6027\u5b50\uff0c\u8fd9\u6837\u8d1f\u5fc3\u7684\u7537\u5b50\uff0c\u6211\u4e0d\u6740\u4ed6\uff0c\u4e5f\u8981\u6253\u4ed6\u4e24\u8bb0\u8033\u5149\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u4e0b\u4e0d\u5982\u610f\u4e8b\u5e38\u516b\u4e5d\uff0c\u60c5\u573a\u7684\u53d8\u5316\uff0c\u66f4\u662f\u5f80\u5f80\u51fa\u4eba\u610f\u6599\u4e4b \u5916\u3002\u5a5a\u59fb\u4e0d\u53ef\u5f3a\u6c42\uff0c\u90a3\u4e5f\u662f\u65e0\u53ef\u5948\u4f55\u4e4b\u4e8b\u3002\u5c01\u59d1\u5a18\u8bf4\u5f97\u5bf9\uff0c\u8fc7\u53bb\u7684\u8fd8\u662f\u8ba9\u5b83\u8fc7\u53bb\u5427\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4f9d\u4f60\u4e4b\u89c1\uff0c\u8fd9\u4ef6\u4e8b\u54b1\u4eec\u662f\u6492\u624b\u4e0d\u5fc5\u7ba1\u4e86\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u522b\u5b69\u5b50\u6c14\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u6485\u7740\u5c0f\u5634\u513f\u9053\uff1a\u201c\u4f60\u8bf4\u8fd9\u662f\u4e00\u4ef6\u5c0f\u4e8b\uff0c\u503c\u4e0d\u5f97\u4f60\u8fd9\u4f4d\u5927\u82f1\u96c4\u4f38\u624b\u53bb\u7ba1\u4e48\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u82e6\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u4f46\u8fd9\u662f\u65c1\u4eba\u7ba1\u4e0d\u4e86\u7684\u4e8b\u60c5\uff0c\u800c\u4e14\u4e5f\u8fd8\u6709\u6bd4\u8fd9\u4ef6\u4e8b\u60c5\u66f4 \u7d27\u8981\u7684\u5462\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4f60\u662f\u6307\u54b1\u4eec\u524d\u5f80\u897f\u660c\u8fd9\u4e00\u6869\u4e8b\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5230\u897f\u660c\u53bb\u5e2e\u5fd9\u4e49\u519b\u6253\u6ee1\u6d32\u9791\u5b50\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u4e0d\u662f\u66f4\u7d27\u8981\u5417\uff1f\u5929\u4e0b\u4e0d\u5982 \u610f\u4e8b\u5e38\u516b\u4e5d\uff0c\u65e2\u7136\u662f\u96be\u4ee5\u633d\u56de\u7684\u4e8b\uff0c\u90a3\u5c31\u53ea\u597d\u629b\u5f00\u4e0d\u7ba1\uff0c\u4e5f\u4e0d\u5fc5\u518d\u53bb\u60f3\u5b83\u4e86\u3002\u201d\u5389\u5357\u661f\u6df1\u611f \u540c\u75c5\u76f8\u601c\u4e4b\u82e6\uff0c\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5176\u5b9e\u662f\u8bf4\u7ed9\u5c01\u5999\u5ae6\u542c\u7684\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5389\u5927\u54e5\u8bf4\u7684\u662f\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u4eca\u665a\u8fd8\u8fdb\u4e0d\u8ff8\u57ce\u6b47\u5bbf\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u7b11\u9053\uff1a\u201c\u65e2\u7136\u6765\u5230\u8fd9\u513f\uff0c\u96be\u9053\u8fd8\u5728\u91ce\u5916\u9732\u5bbf\u4e0d\u6210\uff1f\u4ece\u9647\u53bf\u4e5f\u6709\u4e00\u6761\u8def\u5230\u897f\u660c\u7684\uff0c \u54b1\u4eec\u660e\u5929\u4e00\u65e9\u5c31\u8d70\uff0c\u4f46\u5374\u4e0d\u5fc5\u5230\u6c34\u4e91\u5e84\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f60\u4e0d\u7ba1\u6211\u504f\u8981\u7ba1\u3002\u597d\uff0c\u4e14\u5f85\u5230\u4e86\u9647\u53bf\uff0c\u6211\u518d\u89c1\u673a\u884c\u4e8b\u3002\u201d \u3000\u3000\u4e00\u884c\u4e09\u4eba\u8fdb\u5165\u9647\u53bf\u53bf\u57ce\uff0c\u6211\u4e86\u4e00\u95f4\u5ba2\u5e97\u6295\u5bbf\u3002\u5728\u4ed6\u4eec\u8fdb\u53bb\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u6709\u4e24\u4e2a\u5ba2\u4eba\u5148\u5728 \u90a3\u513f\u3002\u8fd9\u4e24\u4eba\u90fd\u662f\u67cf\u8c8c\u7c97\u8c6a\u7684\u6c49\u5b50\uff0c\u770b\u6765\u4e5f\u662f\u6c5f\u6e56\u4eba\u7269\u3002 \u3000\u3000\u8e0f\u8fdb\u5e97\u95e8\uff0c\u521a\u597d\u542c\u5f97\u5e74\u7eaa\u8f83\u5c0f\u7684\u90a3\u4e2a\u95ee\u9053\uff1a\u201c\u6b64\u53bb\u6c34\u4e91\u5e84\u600e\u4e48\u8d70\u6cd5\uff1f\u201d \u3000\u3000\u638c\u67dc\u7684\u8bf4\u9053\uff1a\u201c\u4ece\u5357\u95e8\u51fa\u53bb\uff0c\u4e00\u76f4\u5411\u5357\u8d70\uff0c\u8d70\u5230\u5c71\u811a\uff0c\u518d\u62d0\u5411\u4e1c\uff0c\u6709\u4e00\u6761\u6751\u5b50\uff0c\u90a3\u5c31\u662f \u6c34\u4e91\u5e84\u4e86\u3002\u201d \u3000\u3000\u5e74\u957f\u7684\u90a3\u4e2a\u8bf4\u9053\uff1a\u201c\u4f60\u8bb0\u7d27\u660e\u65e9\u5929\u8272\u672a\u4eae\uff0c\u5c31\u8981\u53eb\u6211\u8d77\u6765\u3002\u6211\u4eec\u662f\u8981\u5728\u4e2d\u5348\u4e4b\u524d\u8d76\u5230\u6c34 \u4e91\u5e84\u7684\u3002\u201d \u3000\u3000\u638c\u67dc\u7684\u8bf4\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u4f60\u8bf7\u653e\u5fc3\u3002\u6211\u54ea\u80fd\u8bef\u4e86\u4f60\u4eec\u5230\u4e91\u8001\u82f1\u96c4\u5bb6\u91cc\u53bb\u559d\u559c\u9152\u7684\u8fd9\u4ef6\u5927 \u4e8b\u3002\u201d \u3000\u3000\u4f19\u8ba1\u628a\u8fd9\u4e24\u4e2a\u5ba2\u4eba\u5e26\u8d70\u4e4b\u540e\uff0c\u516c\u5b59\u71d5\u95ee\u9053\uff1a\u201c\u6c34\u4e91\u5e84\u79bb\u8fd9\u91cc\u4e0d\u8fdc\u5427\uff1f\u201d \u3000\u3000\u638c\u67dc\u8bf4\u9053\uff1a\u201c\u8bf4\u8fdc\u4e0d\u8fdc\uff0c\u8bf4\u8fd1\u4e0d\u8fd1\uff0c\u5927\u7ea6\u6709\u56db\u4e94\u5341\u91cc\u8def\u7a0b\u3002\u660e\u5929\u8d77\u4e2a\u65e9\uff0c\u4e2d\u5348\u65f6\u5206\u603b\u53ef \u4ee5\u8d76\u5230\u7684\u3002\u4f60\u4eec\u4e5f\u662f\u5230\u6c34\u4e91\u5e84\u559d\u559c\u9152\u7684\u5417\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u53ea\u662f\u95ee\u95ee\u800c\u5df2\u3002\u56e0\u4e3a\u6c34\u4e91\u5e84\u4e91\u5e84\u4e3b\u7684\u5927\u540d\u6211\u662f\u81ea\u5c0f\u5c31\u542c\u719f\u7684\u4e86\u3002\u201d \u3000\u3000\u638c\u67dc\u7684\u7b11\u9053\uff0c\u201c\u662f\u5440\uff0c\u8fd9\u4f4d\u4e91\u5e84\u4e3b\u6709\u5c0f\u76c2\u5c1d\u4e4b\u79f0\uff0c\u8fd9\u4e24\u5929\u4ece\u8fd9\u513f\u7ecf\u8fc7\u7684\u5ba2\u4eba\u53ef\u771f\u4e0d\u5c11 \u5462\u3002\u6240\u4ee5\u6211\u624d\u4ee5\u4e3a\u4f60\u4eec\u4e5f\u662f\u53bb\u559d\u559c\u9152\u7684\u3002\u201d\u638c\u67dc\u7684\u56e0\u4e3a\u4ed6\u4eec\u8fd9\u4e2a\u5c0f\u5730\u65b9\u6709\u8fd9\u6837\u4e00\u4f4d\u6c5f\u6e56\u95fb\u540d \u7684\u4eba\u7269\uff1b\u6b64\u8a00\u8bed\u4e4b\u4e2d\uff0c\u9887\u662f\u5f15\u4ee5\u4e3a\u8363\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5ba2\u5e97\u95e8\u53e3\uff0c\u53c8\u6709\u4e00\u4e2a\u5ba2\u4eba\u7ecf\u8fc7\u3002\u5389\u5357\u661f\u548c\u5c01\u5999\u5ae6\u6b63\u5728\u9762\u5411\u7740\u638c\u67dc\u8bf4\u8bdd\uff0c\u6ca1\u6709 \u770b\u89c1\u8fd9\u4eba\u3002\u8fd9\u4eba\u5728\u95e8\u524d\u7ad9\u4e86\u4e00\u7ad9\uff0c\u8138\u4e0a\u4f3c\u4e4e\u9732\u51fa\u751a\u4e3a\u60ca\u8be7\u7684\u795e\u60c5\uff0c\u4f4e\u4f4e\u7684\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u5c31 \u5306\u5306\u5fd9\u5fd9\u5730\u8d70\u5f00\u4e86\uff0c\u516c\u5b59\u71d5\u53ea\u9053\u662f\u4e2a\u627e\u5bfb\u65c5\u5e97\u7684\u8fc7\u8def\u5ba2\u4eba\uff0c\u867d\u7136\u89c9\u5f97\u4ed6\u7684\u52a8\u4f5c\u6709\u70b9\u5947\u602a\uff0c\u4e5f \u4e0d\u600e\u6837\u653e\u5728\u5fc3\u4e0a\u3001 \u3000\u3000\u5389\u5357\u661f\u8981\u4e86\u76f8\u90bb\u7684\u4e24\u95f4\u623f\u95f4\uff0c\u4e24\u4e2a\u5973\u7684\uff0c\u540c\u4f4f\u4e00\u95f4\u3002\u5c01\u5999\u5ae6\u8fdb\u623f\u4e4b\u540e\uff0c\u5173\u4e0a\u95e8\uff0c\u4f4e\u58f0\u8bf4 \u9053\uff1a\u201c\u71d5\u59d0\uff0c\u4f60\u53ef\u4e0d\u8bb8\u80e1\u95f9\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u4f60\u5f53\u6211\u771f\u7684\u662f\u5c0f\u5b69\u5b50\u4e48\u3002\u201d\u5fc3\u91cc\u5374\u5728\u60f3\u9053\uff1a\u201c\u4f60\u9047\u4e0a\u8d1f\u5fc3\u7684\u7537 \u5b50\uff0c\u4f60\u81ea\u5df1\u4e0d\u4fbf\u51fa\u5934\uff0c\u6211\u4e0d\u66ff\u4f60\u51fa\u5934\uff0c\u90a3\u8fd8\u6210\u4ec0\u4e48\u597d\u59d0\u59b9\uff1f\u201d\u53c8\u60f3\uff1a\u201c\u597d\u5728\u79e6\u5143\u6d69\u662f\u660e\u5929\u624d \u6210\u5a5a\uff0c\u4e5f\u8bb8\u4ed6\u8fd8\u672a\u77e5\u9053\u5c01\u59d0\u59d0\u5bf9\u4ed6\u7684\u60c5\u610f\uff0c\u5f85\u6211\u53bb\u548c\u4ed6\u4e00\u8bf4\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u53ef\u4ee5\u633d\u56de\u3002\u201d\u516c\u5b59\u71d5 \u662f\u81ea\u5c0f\u7ed9\u7236\u4eb2\u5ba0\u574f\u4e86\u7684\uff0c\u4e3a\u4eba\u70ed\u5fc3\uff0c\u5bf9\u4e8b\u60c5\u4e5f\u770b\u5f97\u592a\u6613\uff0c\u5e38\u5e38\u6709\u4e9b\u53e4\u602a\u7684\u60f3\u6cd5\uff0c\u5979\u81ea\u5df1\u4ee5\u4e3a \u4e0d\u662f\u201c\u5c0f\u5b69\u5b50\u201d\uff0c\u5176\u5b9e\u5374\u8fd8\u662f\u672a\u8131\u5b69\u5b50\u6c14\u3002\u5403\u8fc7\u665a\u996d\uff0c\u5389\u5357\u661f\u56e0\u4e3a\u660e\u65e5\u8fd8\u8981\u8d76\u8def\uff0c\u4e00\u65e9\u5c31 \u7761\u3002\u5c01\u5999\u5ae6\u6ee1\u6000\u5fc3\u4e8b\uff0c\u5374\u662f\u8f97\u8f6c\u53cd\u4fa7\uff0c\u96be\u4ee5\u4eba\u5bd0\u3002 \u3000\u3000\u5f85\u5230\u5c06\u8fd1\u4e09\u66f4\u65f6\u5206\uff0c\u516c\u5b59\u71d5\u5ffd\u5730\u8f7b\u8f7b\u63a8\u4e86\u5c01\u5999\u5ae6\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u5c01\u59d0\u59d0\uff0c\u4f60\u522b\u60f3\u4e86\uff0c\u7761 \u5427\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u4f60\u522b\u7ba1\u6211\uff0c\u4f60\u5148\u7761\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u4f60\u7761\u4e0d\u7740\u6211\u4e5f\u7761\u4e0d\u7740\u3002\u6211\u8981\u4f60\u7761\u4e86\u6211\u624d\u653e\u5fc3\u3002\u5c01\u4e8c\u59d0\uff0c\u4e56\uff0c\u542c\u6211\u7684\u8bdd\uff0c \u7761\u5427\uff01\u201d\u7a81\u7136\u4f38\u624b\u4e00\u6307\uff0c\u70b9\u4e86\u5938\u5999\u5ae6\u7684\u660f\u7761\u7a74\u3002\u5c01\u5999\u5ae6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u516c\u5b59\u71d5\u4f1a\u70b9\u5979\u7684\u7a74\u9053\uff0c \u7ed9\u5979\u4e00\u70b9\u5c31\u7740\u3002 \u3000\u3000\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u5c01\u59d0\u59d0\u4f60\u522b\u602a\u6211\uff0c\u5f85\u6211\u5929\u4eae\u56de\u6765\uff0c\u8bf4\u4e0d\u5b9a\u4f60\u5c31\u8981\u591a\u8c22\u6211\u4e86\u3002\u201d\u5979\u7528\u7684\u4e0d \u662f\u91cd\u624b\u6cd5\u70b9\u7a74\uff0c\u53ea\u987b\u8fc7\u4e24\u4e09\u4e2a\u65f6\u8fb0\uff0c\u7a74\u9053\u5c31\u4f1a\u81ea\u5df1\u89e3\u5f00\u7684\u3002 \u3000\u3000\u516c\u5b59\u71d5\u6362\u4e0a\u4e86\u591c\u884c\u8863\uff0c\u63a8\u5f00\u7a97\u5b50\uff0c\u6084\u6084\u5730\u6e9c\u51fa\u53bb\uff0c\u5979\u7684\u8f7b\u529f\u751a\u4e3a\u9ad8\u660e\uff0c\u5389\u5357\u661f\u7761\u5728\u90bb \u623f\uff0c\u4e1d\u6beb\u4e5f\u6ca1\u77e5\u89c9\u3002 \u3000\u3000\u516c\u5b59\u71d5\u5c55\u5f00\u4e86\u8d85\u5353\u7684\u8f7b\u529f\uff0c\u56db\u4e94\u5341\u91cc\u8def\u7a0b\uff0c\u4e00\u4e2a\u591a\u65f6\u8fb0\u4fbf\u5df2\u8d76\u5230\uff0c\u53ea\u8d1d\u4e91\u5bb6\u5927\u95e8\u5f20\u706f\u7ed3 \u5f69\uff0c\u679c\u7136\u662f\u529e\u559c\u4e8b\u7684\u6a21\u6837\u3002\u4f46\u56e0\u6b64\u65f6\u4e09\u66f4\u5df2\u8fc7\uff0c\u9884\u5148\u5230\u6765\u9053\u8d3a\u7684\u5ba2\u4eba\u4e5f\u65e9\u5df2\u7761\u4e86\uff0c\u5927\u95e8\u5df2\u7ecf \u5173\u4e0a\uff0c\u5e76\u65e0\u770b\u95e8\u7684\u4eba\u3002\u516c\u5b59\u71d5\u4fa7\u8033\u4e00\u542c\uff0c\u9759\u6084\u68a2\u7684\u4e5f\u6ca1\u542c\u5230\u4ec0\u4e48\u58f0\u606f\u3002 \u3000\u3000\u516c\u5b59\u71d5\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u53ef\u4e0d\u80fd\u660e\u6765\uff0c\u5e94\u8be5\u627e\u5230\u4e86\u79e6\u5143\u6d69\uff0c\u548c\u4ed6\u4e00\u4e2a\u4eba\u8bf4\u3002\u201d\u84e6\u5730\u60f3 \u8d77\u81ea\u5df1\u5e76\u4e0d\u8ba4\u8bc6\u79e6\u5143\u6d69\uff0c\u53ef\u600e\u4e48\u6837\u627e\u4ed6\u5462\uff1f\u4f46\u65e2\u7136\u6765\u5230\uff0c\u4e5f\u53ea\u597d\u8fdb\u53bb\u518d\u60f3\u529e\u6cd5\u4e86\u3002\u54ea\u77e5\u521a\u521a \u8df3\u8fdb\u9662\u5b50\uff0c\u7acb\u5373\u4fbf\u6709\u4eba\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff0c\u4e0d\u8bb8\u52a8\uff01\u201d \u3000\u3000\u987f\u7136\u95f4\uff0c\u5728\u82b1\u6811\u4e1b\u4e2d\uff0c\u5047\u5c71\u77f3\u540e\uff0c\u8df3\u51fa\u4e86\u4e03\u516b\u4e2a\u4eba\u3002\u5176\u4e2d\u6709\u4e24\u4e2a\u9c81\u83bd\u7684\u6c49\u5b50\uff0c\u5df2\u7136\u51fa \u624b\uff0c\u4e00\u4e2a\u6253\u51fa\u6697\u5668\uff0c\u4e00\u4e2a\u6301\u5200\u65ab\u6765\u3002 \u3000\u3000\u516c\u5b59\u71d5\u6325\u8896\u4e00\u62c2\uff0c\u556a\u556a\u556a\u4e09\u58f0\uff0c\u628a\u4e09\u679a\u900f\u9aa8\u9489\u90fd\u6253\u843d\u3002\u5f85\u5230\u90a3\u6c49\u5b50\u628a\u5200\u65ab\u6765\uff0c\u5979\u4ea6\u5df1\u62d4 \u5251\u51fa\u9798\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u521a\u597d\u8fce\u4e0a\u3002\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u4e00\u7f05\u5200\uff0c\u4e5f\u98de\u4e0a\u4e86\u534a\u7a7a\u3002\u5979\u7528\u7684\u662f \u4e2a\u201c\u7ede\u201d\u5b57\u8bc0\uff0c\u628a\u90a3\u4eba\u7684\u7f05\u5200\u7ede\u8131\u4e86\u624b\uff0c\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6253\u67b6\u6765\u7684\uff01\u201d \u3000\u3000\u90a3\u4e9b\u4eba\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u539f\u6765\u662f\u4e2a\u5973\u5b50\uff01\u201d\u201c\u6211\u8fd8\u53ea\u9053\u662f\u79e6\u5cad\u7684\u9057\u5b7d\u5462\uff01\u201d\u6709\u4e00\u4e2a\u4eba\u51b7\u51b7\u8bf4 \u9053\uff1a\u201c\u7109\u77e5\u79e6\u5cad\u7684\u9057\u5b7d\u4e4b\u4e2d\u5c31\u6ca1\u6709\u5973\u532a\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u6012\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff01\u79e6\u5cad\u7f57\u5927\u9b41\u90a3\u5e2e\u532a\u5f92\uff0c\u7ed9\u6211\u505a\u542c\u5dee\u8fd8\u4e0d\u914d\u5462\uff01\u4f60\u4eec\u6562\u8bf4\u6211\u662f \u5973\u532a\u3002\u54fc\uff0c\u54fc\uff0c\u6211\u672c\u6765\u4e0d\u662f\u6253\u67b6\u6765\u7684\uff0c\u4f46\u4f60\u4eec\u4e00\u5b9a\u8981\u6253\u67b6\u4e48\uff0c\u6211\u4e5f\u53ef\u4ee5\u6cf0\u966a\uff01\u201d \u3000\u3000\u8fd9\u4e9b\u4eba\u90fd\u662f\u4e91\u5e84\u4e3b\u7684\u95e8\u4eba\u5f1f\u5b50\uff0c\u770b\u8fd9\u4e2a\u5e74\u7eaa\u8f7b\u8f7b\u7684\u59d1\u5a18\uff0c\u53e3\u6c14\u7adf\u662f\u5982\u6b64\u4e4b\u5927\uff0c\u90fd\u5413\u4e86\u4e00 \u8df3\u3002\u4e91\u9f99\u7684\u4e8c\u5f92\u5f1f\u6797\u5c97\u662f\u8fd9\u5e2e\u4eba\u7684\u9996\u9886\uff0c\u5f53\u4e0b\u6309\u7740\u5251\u67c4\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u4e3a\u4e86\u4f55\u4e8b\u800c\u6765\uff0c\u53ef \u80fd\u89c1\u544a\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u4e00\u60f3\uff0c\u4e8b\u60c5\u5df2\u7ecf\u95f9\u5f00\uff0c\u4e5f\u53ea\u597d\u548c\u4ed6\u4eec\u660e\u8bf4\u4e86\u3002\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u628a\u65b0\u90ce\u53eb\u6765\uff0c\u6211\u6709\u8bdd \u548c\u4ed6\u8bf4\uff01\u201d \u3000\u3000\u4f17\u4eba\u542c\u4e86\uff0c\u90fd\u662f\u4e0d\u7981\u5927\u4e3a\u5947\u602a\u3002\u6797\u5c97\u9053\uff1a\u201c\u65b0\u90ce\u5df2\u7ecf\u7761\u4e86\uff0c\u4f60\u8981\u89c1\u4ed6\u505a\u4ec0\u4e48\uff1f\u201d\u516c\u5b59\u71d5 \u9053\uff1a\u201c\u6211\u5f53\u7136\u662f\u6709\u4e8b\u624d\u6765\u627e\u4ed6\uff0c\u7761\u4e86\u4e5f\u8981\u628a\u4ece\u88ab\u7a9d\u91cc\u62c9\u51fa\u6765\uff01\u201d\u6797\u5c97\u9053\uff1a\u201c\u597d\uff0c\u8bf7\u59d1\u5a18\u7a0d \u5019\uff0c\u6211\u8fd9\u5c31\u53bb\u62c9\u4ed6\u3002\u201d \u3000\u3000\u6797\u5c97\u6b63\u60f3\u8fdb\u53bb\u62a5\u544a\u5e08\u7236\uff0c\u6c34\u4e91\u5e84\u7684\u5e84\u4e3b\u4e91\u9f99\u5df2\u7ecf\u95fb\u58f0\u800c\u51fa\u3002\u5f97\u77e5\u6709\u4e2a\u5e74\u8f7b\u8c8c\u7f8e\u7684\u5973\u5b50\u8981 \u627e\u4ed6\u7684\u5973\u5a7f\uff0c\u4e5f\u662f\u5927\u4e3a\u5947\u602a\u3002 \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4e91\u5e84\u4e3b\u4e48\uff1f\u8bf7\u6055\u665a\u8f88\u79c1\u95ef\u8d35\u5e84\uff0c\u60ca\u52a8\u4e86\u524d\u8f88\u4e86\u3002\u201d\u5979\u4ece\u8fd9\u4e9b\u4eba\u7684\u8a00 \u8bed\u4e4b\u4e2d\uff0c\u5df2\u7ecf\u77e5\u9053\u6765\u7684\u4f55\u4eba\u3002\u4e91\u9f99\u662f\u5979\u7684\u7236\u4eb2\u540c\u4e00\u8f88\u4efd\u7684\uff0c\u516c\u5b59\u71d5\u81ea\u662f\u4e0d\u6562\u65e0\u793c\u3002 \u3000\u3000\u4e91\u9f99\u6253\u91cf\u4e86\u516c\u5b59\u71d5\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8001\u592b\u6b63\u662f\u4e91\u9f99\u3002\u8bf7\u95ee\u5a18\u8d35\u59d3\u5927\u540d\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u665a\u8f88\u590d\u59d3\u516c\u5b59\uff0c\u7ef0\u540d\u4e00\u4e2a\u71d5\u5b57\u3002\u6211\u7239\u7239\u662f\u7ea2\u7f28\u4f1a\u7684\u8235\u4e3b\u516c\u5b59\u5b8f\uff0c\u4e91\u5e84\u4e3b\u60f3 \u5fc5\u77e5\u9053\uff1a\u201c \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4e91\u9f99\u4e5f\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4ee4\u5c0a\u5c31\u662f\u516c\u5b59\u5b8f\u4e48\uff1f\u4e91\u67d0\u5bf9\u4ee4\u5c0a\u4ef0\u6155\u5df2 \u4e45\uff0c\u53c8\u66fe\u53d7\u8fc7\u8d35\u4f1a\u7684\u6069\u5fb7\uff0c\u96be\u5f97\u59d1\u5a18\u5149\u4e34\uff0c\u8bf7\u6055\u5c0f\u5f92\u65e0\u77e5\u4e4b\u7f6a\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\u3002\u6211\u6ca1\u6709\u901a\u540d\u6c42\u89c1\uff0c\u672c\u6765\u662f\u6211\u4e0d\u8be5\u3002\u56e0\u6709\u4ef6\u6025\u4e8b\uff0c\u4e5f\u5c31\u987e\u4e0d\u4e86 \u8fd9\u8bb8\u591a\u4e86\u3002\u201d \u3000\u3000\u4e91\u9f99\u60ca\u7591\u4e0d\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u8bf7\u95ee\u59d1\u5a18\u9a7e\u4e34\uff0c\u6709\u4f55\u8d35\u5e72\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u542c\u8bf4\u4ee4\u5a9b\u660e\u5929\u51fa\u9601\uff0c\u662f\u4e48\uff1f\u201d\u4e91\u9f99\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u516c\u71d5\u9053\uff1a\u201c\u6211\u5c31\u662f\u4e3a\u4e86 \u6b64\u4e8b\u800c\u6765\u3002\u8bf7\u4f60\u53eb\u4ee4\u5a7f\u51fa\u6765\uff0c\u6211\u548c\u4ed6\u5f53\u9762\u8bf4\uff0c\u5c31\u4f1a\u660e\u767d\u7684\u4e86\u3002\u201d \u3000\u3000\u5219\u521a\u8bf4\u5230\u6b64\u5904\uff0c\u4e91\u9f99\u7684\u5973\u513f\u548c\u5973\u5a7f\u90fd\u5df2\u8d76\u5230\u6765\uff0c\u4e91\u9f99\u9053\uff1a\u201c\u4f60\u8fc7\u6765\uff0c\u8fd9\u4f4d\u516c\u5b59\u59d1\u5a18\u8981\u89c1 \u4f60\u4eec\u3002\u201d\u516c\u5b59\u71d5\u8981\u89c1\u7684\u672c\u6765\u53ea\u662f\u4ed6\u7684\u5973\u5a7f\uff0c\u4f46\u4e91\u9f99\u5374\u4e0d\u653e\u5fc3\u8ba9\u4ed6\u4eec\u5355\u72ec\u8c08\u8bdd\uff0c\u56e0\u6b64\u628a\u5973\u513f\u4e5f \u62c9\u4e0a\u4e86\u3002 \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4e91\u4e2d\u71d5\u59d0\u59d0\u5417\uff1f\u597d\uff0c\u4f60\u6765\u4e86\u66f4\u597d\uff0c\u54b1\u4eec\u53ef\u4ee5\u4e09\u9762\u8a00\u660e\u3002\u201d \u3000\u3000\u4e91\u4e2d\u71d5\u7591\u5fc3\u5927\u8d77\uff0c\u918b\u6c14\u52c3\u53d1\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u627e\u4ed6\u505a\u4ec0\u4e48\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u6211\u529d\u4f60\u4e0d\u8981\u5ac1\u4ed6\u7684\u597d\uff0c\u4ed6\u662f\u4e2a\u8d1f\u5fc3\u6c49\u5b50\uff01\u201d \u3000\u3000\u4e91\u4e2d\u71d5\u6c14\u5f97\u7389\u5bb9\u53d8\u8272\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u597d\u5440\uff0c\u5927\u5e08\u54e5\uff0c\u539f\u6765\u4f60\u65e9\u5c31\u6709\u4e86\u76f8\u597d\u7684\u59d1\u5a18\uff0c\u5374\u7792 \u7740\u6211\uff0c\u4e0d\u8ba9\u6211\u77e5\u9053\uff01\u201d \u3000\u3000\u65b0\u90ce\u53c8\u60ca\u53c8\u6025\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u516c\u5b59\u59d1\u5a18\uff0c\u6b64\u8bdd\u4ece\u4f55\u8bf4\u8d77\u3002\u6211\u548c\u4f60\u53ef\u662f\u7d20\u4e0d\u76f8\u8bc6\u7684\u5440\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u6014\u4e86\u4e00\u6014\uff0c\u987f\u8db3\u8bf4\u9053\uff1a\u201c\u4f60\u8ba8\u4ec0\u4e48\u4fbf\u5b9c\uff1f\u6211\u5f53\u7136\u548c\u4f60\u6beb\u65e0\u74dc\u845b\uff0c\u6211\u8bf4\u7684\u662f\u5c01\u5999 \u5ae6\u3002\u96be\u9053\u4f60\u6562\u8bf4\u4f60\u548c\u5c01\u5999\u5ae6\u4e5f\u662f\u6beb\u4e0d\u76f8\u8bc6\u4e48\uff1f\u201d \u3000\u3000\u65b0\u90ce\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5c01\u5999\u5ae6\u662f\u4ec0\u4e48\u4eba\uff0c\u6211\u8fde\u5979\u7684\u540d\u5b57\u90fd\u6ca1\u542c\u8fc7\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u6012\u6c14\u4e0a\u51b2\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5012\u8d56\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u597d\uff0c\u4f60\u662f\u79e6\u5143\u6d69\u4e0d\u662f\uff1f\u201d \u3000\u3000\u65b0\u90ce\u6115\u7136\u9053\uff1a\u201c\u54e6\uff0c\u73b0\u5728\u6211\u6709\u70b9\u660e\u767d\u4e86\uff0c\u4f60\u8981\u627e\u7684\u4eba\u662f\u79e6\u5143\u6d69\uff0c\u662f\u4e48\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u8be7\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u79e6\u5143\u6d69\uff1f\u201d \u3000\u3000\u65b0\u90ce\u7b11\u9053\uff1a\u201c\u6211\u5012\u662f\u59d3\u79e6\uff0c\u4f46\u79e6\u5143\u6d69\u53ef\u4e0d\u662f\u6211\uff01\u201d \u3000\u3000\u4e91\u9f99\u9053\uff1a\u201c\u4ed6\u662f\u5c0f\u5f92\u79e6\u5c11\u9633\u3002\u79e6\u5143\u6d69\u662f\u6b66\u5f53\u6d3e\u638c\u95e8\u4eba\u96f7\u9707\u5b50\u7684\u9ad8\u8db3\u3002\u516c\u5b59\u59d1\u5a18\uff0c\u4f60\u8981\u627e \u7684\u662f\u8c01\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u6ee1\u9762\u901a\u7ea2\uff0c\u8fd9\u624d\u77e5\u9053\u81ea\u5df1\u8ba4\u9519\u4e86\u4eba\uff0c\u95f9\u51fa\u4e86\u5929\u5927\u7684\u7b11\u8bdd\u3002 \u3000\u3000\u6b64\u65f6\u6709\u82e5\u5e72\u5bbe\u5ba2\u4ea6\u5df2\u5230\u6765\uff0c\u4ed6\u4eec\u4e0d\u77e5\u53d1\u751f\u4f55\u4e8b\uff0c\u53ea\u9053\u662f\u6709\u4e3b\u4eba\u7684\u4ec7\u5bb6\u95ef\u5e84\u95f9\u4e8b\uff0c\u6545\u6b64\u8d76 \u6765\u52a9\u62f3\u3002\u516c\u5b59\u71d5\u65e5\u95f4\u5728\u8def\u4e0a\u9047\u89c1\u7684\u90a3\u4e24\u4e2a\u4eba\u674e\u864e\u513f\u548c\u5f20\u9e4f\u98de\u4e5f\u5728\u5176\u4e2d\u3002 \u3000\u3000\u516c\u5b59\u71d5\u7f9e\u5f97\u65e0\u5730\u81ea\u5bb9\uff0c\u89c1\u4e86\u8fd9\u4e24\u4e2a\u4eba\uff0c\u4e0d\u89c9\u5c31\u628a\u6012\u6c14\u53d1\u6cc4\u5728\u4ed6\u4eec\u8eab\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u90fd\u662f\u4f60 \u4eec\u7cca\u6d82\uff0c\u4e3a\u4ec0\u4e48\u4f60\u4eec\u8bf4\u65b0\u90ce\u662f\u79e6\u5143\u6d69\uff1f\u201d \u3000\u3000\u674e\u864e\u513f\u5fcd\u4f4f\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u4f60\u8bb0\u9519\u4e86\u5427\uff1f\u6211\u53ea\u8bf4\u65b0\u90ce\u59d3\u79e6\uff0c\u53ef\u5e76\u6ca1\u6709\u8bf4\u662f\u79e6\u5143\u6d69\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u65b0\u90ce\u5c31\u662f\u6740\u6389\u7f57\u5927\u9b41\u7684\u90a3\u4f4d\u5c11\u5e74\u82f1\u96c4\u3002\u90a3\u8fd8\u4e0d\u662f\u79e6\u5143\u6d69\uff1f\u201d \u3000\u3000\u5f20\u9e4f\u98de\u6bd4\u8f83\u723d\u76f4\uff0c\u5148\u8ba4\u4e86\u4e2a\u9519\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u7cca\u6d82\u3002\u6211\u4e5f\u50cf\u59d1\u5a18\u4e00\u6837\uff0c\u628a\u4f60\u4eec\u4e24\u4e2a \u4eba\u9519\u5f53\u505a\u4e00\u4e2a\u4eba\u4e86\u3002\u201d \u3000\u3000\u4e91\u9f99\u5e2e\u4ed6\u89e3\u91ca\u9053\uff1a\u201c\u662f\u8fd9\u6837\u7684\uff0c\u5c11\u9633\u662f\u6211\u7684\u5927\u5f1f\u5b50\uff0c\u65e9\u5df2\u51fa\u5e08\uff0c\u8fd9\u4e24\u5e74\u90fd\u662f\u5728\u5bb6\u7684\u65f6\u5019 \u5c11\uff0c\u5728\u5916\u7684\u65f6\u5019\u591a\u3002\u4eca\u5e74\u6625\u5929\uff0c\u79e6\u5cad\u8fd9\u5e2e\u5f3a\u4eba\u524d\u6765\u751f\u4e8b\uff0c\u5c11\u9633\u5e76\u6ca1\u5728\u573a\uff0c\u4ed6\u662f\u4e0a\u4e2a\u6708\u624d\u4ece\u5c0f \u91d1\u5ddd\u56de\u6765\u7684\u3002\u5c0f\u5f92\u7684\u540d\u6c14\u5f53\u7136\u6bd4\u4e0d\u4e0a\u6b66\u5f53\u6d3e\u7684\u79e6\u5c11\u4fa0\uff0c\u662f\u4ee5\u6709\u8bb8\u591a\u4e0d\u5f88\u76f8\u719f\u7684\u670b\u53cb\uff0c\u53ef\u80fd\u4ee5 \u8bb9\u4f20\u8bb9\uff0c\u628a\u4ed6\u4eec\u9519\u5f53\u4f5c\u4e00\u4e2a\u4eba\u4e86\u3002\u8bf4\u8d77\u6765\u8fd9\u4e5f\u5e94\u8be5\u602a\u6211\uff0c\u6211\u56e0\u4e3a\u4e0d\u60f3\u60ca\u52a8\u8fd9\u4e9b\u4e0d\u5927\u76f8\u719f\u7684\u670b \u53cb\uff0c\u6ca1\u6709\u7ed9\u4ed6\u4eec\u5bc4\u51fa\u8bf7\u5e16\uff0c\u5f04\u5f97\u4ed6\u4eec\u5230\u4e86\u6c34\u4e91\u5e84\u4e4b\u540e\uff0c\u624d\u77e5\u9053\u65b0\u90ce\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u505a\u65b0\u90ce\u7684\u79e6\u5c11\u9633\u542c\u4e86\uff0c\u5fc3\u91cc\u5f53\u7136\u662f\u6709\u70b9\u4e0d\u5927\u8212\u670d\u3002\u4e91\u4e2d\u71d5\u5219\u63ea\u7740\u4ed6\u7684\u8033\u6735\uff0c\u6084\u6084\u8bf4\u9053\uff1a \u201c\u90a3\u4e9b\u52bf\u5229\u7684\u4eba\u53ea\u77e5\u9053\u6b66\u5f53\u6d3e\u6709\u4e2a\u79e6\u5c11\u4fa0\uff0c\u4f46\u5728\u6211\u7684\u5fc3\u4e2d\u5374\u662f\u53ea\u77e5\u6709\u4f60\uff01\u201d\u5439\u6c14\u5982\u5170\uff0c\u767b\u65f6 \u628a\u79e6\u5c11\u9633\u5fc3\u4e2d\u7684\u95f7\u6c14\u5439\u5f97\u4e00\u5e72\u4e8c\u51c0\u3002 \u3000\u3000\u539f\u6765\u4e91\u9f99\u7684\u786e\u662f\u60f3\u8fc7\u628a\u5973\u513f\u8bb8\u914d\u7ed9\u79e6\u5143\u6d69\u7684\uff0c\u4f46\u4e91\u4e2d\u71d5\u548c\u5979\u7684\u5927\u5e08\u5144\u65e9\u5df2\u6709\u4e86\u79c1\u60c5\uff0c\u5bdf \u77e5\u7239\u7239\u6709\u6b64\u5fc3\u610f\uff0c\u4e0d\u5f85\u7239\u7239\u5f00\u53e3\uff0c\u5c31\u5148\u81ea\u8868\u767d\u4e86\u81ea\u5df1\u7684\u5fc3\u4e8b\u3002\u4e91\u9f99\u53c8\u6697\u6697\u8bd5\u63a2\u8fc7\u79e6\u5143\u6d69\u7684\u53e3 \u98ce\uff0c\u77e5\u9053\u79e6\u5143\u6d69\u53e6\u6709\u6240\u5c5e\uff0c\u65e2\u7136\u53cc\u65b9\u90fd\u4e0d\u613f\u610f\uff0c\u4e91\u9f99\u5f53\u7136\u4e5f\u53ea\u597d\u7b97\u4e86\u3002\u5176\u5b9e\u4ed6\u4e5f\u5e76\u975e\u4e0d\u559c\u6b22 \u79e6\u5c11\u9633\uff0c\u5426\u5219\u4ed6\u4e0d\u4f1a\u7acb\u79e6\u5c11\u9633\u505a\u638c\u95e8\u5f1f\u5b50\uff1b\u4ed6\u4e4b\u60f3\u628a\u5973\u513f\u8bb8\u914d\u7ed9\u79e6\u5143\u6d69\uff0c\u4e0d\u8fc7\u662f\u62a5\u7b54\u79e6\u5143\u6d69 \u7684\u6069\u5fb7\u800c\u5df2\u3002\u540e\u6765\u4ed6\u77e5\u9053\u4e86\u5973\u513f\u7684\u5fc3\u4e8b\u4e4b\u540e\uff0c\u7acb\u5373\u6d3e\u4eba\u5230\u5c0f\u91d1\u5ddd\u628a\u4ed6\u7684\u5927\u5f1f\u5b50\u53eb\u56de\u6765\u6210\u5a5a\u3002 \u3000\u3000\u4e14\u8bf4\u516c\u5b59\u71d5\u5728\u5f04\u6e05\u695a\u771f\u76f8\u4e4b\u540e\uff0c\u4e0d\u7531\u5f97\u81ca\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u53ea\u597d\u5411\u65b0\u90ce\u65b0\u5a18\u8fde\u8fde\u8d3b\u7f6a\u3002\u4e91\u9f99 \u7b11\u9053\uff1a\u201c\u5c0f\u5c0f\u4e00\u70b9\u8bef\u4f1a\uff0c\u54c8\u54c8\u4e00\u7b11\u4fbf\u4e86\uff0c\u52ff\u987b\u82a5\u8482\u4e8e\u5fc3\uff1f\u96be\u5f97\u59d1\u5a18\u6765\u5230\uff0c\u8bf7\u59d1\u5a18\u7559\u4e0b\u6765\uff0c\u660e \u65e5\u540c\u559d\u4e00\u676f\u559c\u9152\u5982\u4f55\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4e0d\u77e5\u79e6\u5143\u6d69\u662f\u5426\u8fd8\u5728\u8d35\u5e84\uff1f\u6211\u60f3\u8bf7\u4ed6\u548c\u6211\u4e00\u540c\u89c1\u5c01\u5999\u5ae6\uff0c\u7136\u540e\u6211\u4eec\u90fd\u6765\u559d \u4ee4\u5a9b\u7684\u559c\u9152\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u4e91\u9f99\u9053\uff1a\u201c\u79e6\u5c11\u4fa0\u662f\u5728\u655d\u5e84\uff0c\u4e0d\u8fc7\u4ed6\u4eca\u5929\u6709\u70b9\u4e8b\u60c5\uff0c\u51fa\u4e86\u53bf\u57ce\u5728\u8fd8\u6ca1\u56de\u6765\u3002\u201d \u3000\u3000\u4e91\u9f99\u7684\u4e8c\u5f92\u5f1f\u6797\u5c97\u8bf4\u9053\uff1a\u201c\u501a\u602a\uff0c\u79e6\u5c11\u4fa0\u600e\u7684\u5230\u73b0\u5728\u8fd8\u6ca1\u56de\u6765\uff1f\u660e\u5929\u8fd8\u8981\u505a\u966a\u90ce\u5462\u3002\u8981 \u4e0d\u8981\u6d3e\u51e0\u4e2a\u4eba\u5230\u57ce\u91cc\u53bb\u627e\u4ed6\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u5fc3\u4e2d\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u79e6\u5143\u6d69\u4eca\u5929\u51fa\u57ce\u7684\u65f6\u5019\u7a7f\u7684\u662f\u4ec0\u4e48\u8863\u88f3\uff1f\u201d \u3000\u3000\u6797\u5170\u9053\uff1a\u201c\u7a7f\u7684\u662f\u4e00\u4ef6\u65b0\u505a\u7684\u84dd\u7f0e\u957f\u9971\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u201c\u554a\u5440\u201d\u4e00\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u79e6\u5143\u6d69\u53ef\u80fd\u5df2\u89c1\u7740\u6211\u4eec\u4e86\u3002\u6211\u53ef\u5f97\u8d76\u5feb\u56de \u53bb\uff01\u201d\u5979\u662f\u4e2a\u6025\u6027\u5b50\u7684\u59d1\u5a18\uff0c\u4ea4\u4ee3\u4e86\u8fd9\u51e0\u53e5\u8bdd\uff0c\u5306\u5fd9\u5c31\u8dd1\u3002\u5f04\u5f97\u6c34\u4e91\u5e84\u7684\u4e00\u73ed\u4eba\u83ab\u540d\u5176\u5999\u3002 \u4e91\u9f99\u53ea\u597d\u53eb\u51e0\u4e2a\u5f92\u5f1f\u8ddf\u7740\uff0c\u770b\u770b\u5230\u5e95\u662f\u4ec0\u4e48\u4e00\u56de\u4e8b\u60c5\u3002 \u3000\u3000\u516c\u5b59\u71d5\u731c\u5f97\u4e0d\u9519\uff0c\u90a3\u4e2a\u66fe\u7ecf\u5728\u5ba2\u5e97\u95e8\u5916\u505c\u7559\u8fc7\u7247\u523b\u7684\u201c\u5ba2\u4eba\u201d\uff0c\u679c\u7136\u662f\u79e6\u5143\u6d69\u3002 \u3000\u3000\u5f53\u65f6\u79e6\u5143\u6d69\u56e0\u4e3a\u770b\u89c1\u5c01\u5999\u5ae6\u548c\u4e00\u4e2a\u5c11\u5e74\u7537\u5b50\u540c\u5728\u4e00\u8d77\uff0c\u662f\u4ee5\u6ca1\u6709\u8fdb\u53bb\u53eb\u5979\u3002\u4f46\u8d70\u5f00\u4e4b \u540e\uff0c\u4e00\u9897\u5fc3\u5374\u662f\u65e0\u6cd5\u5b89\u9759\u4e0b\u6765\u3002 \u3000\u3000\u79e6\u5143\u6d69\u8fd8\u672a\u77e5\u9053\u91d1\u9010\u6d41\u7ed9\u4ed6\u5f3a\u4f5c\u5a92\u4eba\u7684\u4e8b\uff0c\u4f46\u53e6\u6709\u4e00\u4e2a\u6bd4\u91d1\u9010\u6d41\u66f4\u559c\u6b22\u505a\u5a92\u4eba\u7684\u4eba\uff0c\u5df2 \u7ecf\u5230\u8fc7\u6b66\u5f53\u5c71\u5411\u4ed6\u7684\u5e08\u7236\u63d0\u4eb2\u4e86\u3002\u8fd9\u4e2a\u4eba\u662f\u4e10\u5e2e\u7684\u5e2e\u4e3b\u4ef2\u957f\u7edf\u3002\u539f\u6765\u4ef2\u957f\u7edf\u8d77\u521d\u8fd8\u662f\u4e0d\u8d5e\u540c \u8fd9\u6869\u5a5a\u4e8b\u7684\uff0c\u4f46\u81ea\u90a3\u6b21\u5728\u5f82\u5f95\u5c71\u7ed9\u91d1\u9010\u6d41\u8bf4\u4e86\u4e00\u987f\u4e4b\u540e\uff0c\u4ed6\u81ea\u5df1\u89c9\u5f97\u7406\u4e8f\uff0c\u4e8e\u662f\u7d22\u6027\u8d76\u5728\u91d1 \u9010\u6d41\u7684\u524d\u9762\uff0c\u62a2\u7740\u5148\u5230\u6b66\u5f53\u6d3e\u505a\u5a92\u3002 \u3000\u3000\u79e6\u5143\u6d69\u7684\u5e08\u7236\u96f7\u9707\u5b50\u89c9\u5f97\u6b64\u4e8b\u5f88\u662f\u53ef\u7b11\uff0c\u7b11\u4ed6\u8fd8\u6ca1\u6709\u95ee\u8fc7\u5973\u65b9\u5c31\u6765\u4f5c\u5a92\u3002\u4ef2\u957f\u7edf\u8bb2\u660e\u4e86 \u5973\u65b9\u5bb6\u957f\u7684\u8eab\u4efd\uff0c\u8bf4\u9053\uff1a\u201c\u5c01\u5b50\u8d85\u662f\u4e2a\u574f\u86cb\uff0c\u4f46\u4ed6\u7684\u5973\u513f\u5374\u662f\u51fa\u4e8e\u6c61\u6ce5\u800c\u4e0d\u67d3\u3002\u6240\u4ee5\u4e0d\u5fc5\u95ee \u5973\u65b9\u7684\u5bb6\u957f\uff0c\u53ea\u8981\u4ed6\u7684\u5973\u513f\u613f\u610f\u5c31\u884c\uff0c\u90a3\u4f4d\u5c01\u59d1\u5a18\u5bf9\u4ee4\u5f92\u4e00\u7247\u75f4\u60c5\uff0c\u8fd9\u4e2a\u5374\u662f\u8001\u53eb\u5316\u77e5\u9053 \u7684\u3002\u73b0\u5728\u5c31\u53ea\u770b\u4f60\u8fd9\u4e2a\u505a\u5e08\u7236\u7684\u53cd\u4e0d\u53cd\u5bf9\u4e86\u3002\u201d\u96f7\u9707\u5b50\u62b9\u4e0d\u5f00\u4ef2\u957f\u7edf\u7684\u60c5\u9762\uff0c\u53ea\u597d\u65e0\u53ef\u65e0\u4e0d \u53ef\u5730\u7b54\u5e94\u4efb\u4ece\u4ed6\u4eec\uff0c\u4e0d\u52a0\u5e72\u6d89\uff0c\u867d\u7136\u4ed6\u7684\u5fc3\u91cc\u5176\u5b9e\u8fd8\u662f\u4e0d\u5927\u540c\u610f\u3002 \u3000\u3000\u5a5a\u4e8b\u867d\u7136\u8fd8\u4e0d\u80fd\u7b97\u662f\u5df2\u7ecf\u5b9a\u4e0b\uff0c\u4f46\u81f3\u5c11\u79e6\u5143\u6d69\u662f\u5df2\u7ecf\u77e5\u9053\u4e86\u5c01\u5999\u5ae6\u5bf9\u4ed6\u7684\u5fc3\u610f\u4e86\u3002 \u3000\u3000\u8fd9\u4e24\u5e74\u6765\uff0c\u79e6\u5143\u6d69\u4e5f\u662f\u6e34\u671b\u5f97\u77e5\u5c01\u5999\u5ae6\u7684\u4e0b\u843d\uff0c\u60f3\u4e0d\u5230\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f \u592b\uff0c\u4ed6\u5728\u6c34\u4e91\u5e84\u517b\u4e86\u534a\u5e74\u7684\u75c5\uff0c\u7b2c\u4e00\u6b21\u51fa\u9647\u53bf\u53bf\u57ce\uff0c\u5c31\u78b0\u89c1\u4e86\u5c01\u5999\u5ae6\u3002 \u3000\u3000\u4f46\u66f4\u60f3\u4e0d\u5230\u7684\u662f\uff1a\u5c01\u5999\u5ae6\u6709\u53e6\u5916\u4e00\u4e2a\u7537\u5b50\u4f34\u7740\u5979\u3002 \u3000\u3000\u79e6\u5143\u6d69\u79bb\u5f00\u90a3\u95f4\u5ba2\u5e97\u4e4b\u540e\uff0c\u5fc3\u91cc\u731c\u7591\u4e0d\u5b9a\uff1a\u201c\u96be\u9053\u5979\u5df2\u7ecf\u7ed3\u5a5a\u4e86\uff1f\u4e0d\u7136\u4f55\u4ee5\u4f1a\u4e0e\u4e00\u4e2a\u7537 \u4e86\u4e00\u540c\u6295\u5bbf\uff1f\u201d\u201c\u4f46\u4e5f\u8bf4\u4e0d\u5b9a\u90a3\u4e2a\u7537\u5b50\u4e0e\u5979\u5728\u5ba2\u5e97\u91cc\u5076\u7136\u76f8\u9047\uff0c\u867d\u7136\u76f8\u8bc6\uff0c\u5374\u975e\u592b\u5987\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u80e1\u601d\u4e71\u60f3\uff0c\u4e00\u9897\u5fc3\u4e0d\u77e5\u5f80\u54ea\u91cc\u653e\u624d\u597d\uff0c\u4ed6\u5728\u8336\u9986\u91cc\u5446\u5750\u4e86\u4e00\u4f1a\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5929\u8272\u5df2 \u9ed1\uff0c\u79e6\u5143\u6d69\u84e6\u5730\u5f97\u4e86\u4e00\u4e2a\u4e3b\u610f\uff1a\u201c\u6211\u4f55\u5fc5\u5728\u8fd9\u91cc\u80e1\u731c\uff0c\u4e0d\u5982\u4eca\u665a\u5077\u5077\u53bb\u7aa5\u63a2\u4e00\u4e0b\uff0c\u5018\u82e5\u4ed6\u4eec \u662f\u540c\u623f\u7684\u8bdd\uff0c\u90a3\u5c31\u662f\u592b\u5987\u4e86\u3002\u5f53\u7136\u6211\u53ef\u5f97\u8c28\u614e\u4e00\u4e9b\uff0c\u5343\u4e07\u4e0d\u80fd\u7ed9\u4ed6\u4eec\u53d1\u89c9\u3002\u201d \u3000\u3000\u5f85\u5230\u4e09\u66f4\u65f6\u5206\uff0c\u79e6\u5143\u6d69\u6084\u6084\u8fdb\u5165\u8fd9\u95f4\u5ba2\u5e97\uff0c\u5176\u65f6\u6070\u5de7\u662f\u516c\u5b59\u71d5\u521a\u521a\u6e9c\u51fa\u5ba2\u5e97\uff0c\u4e24\u4eba\u5374\u6ca1 \u78b0\u4e0a\u3002 \u3000\u3000\u8fd9\u95f4\u5ba2\u5e97\u603b\u5171\u4e0d\u8fc7\u5341\u591a\u95f4\u623f\u95f4\uff0c\u79e6\u5143\u6d69\u9010\u95f4\u524d\u53bb\u7aa5\u63a2\uff0c\u4ed6\u662f\u81ea\u5c0f\u7ec3\u8fc7\u6885\u82b1\u9488\u6697\u5668\u7684\u4eba\uff0c \u53ea\u8981\u7a0d\u5fae\u6709\u4e00\u70b9\u5149\u7ebf\uff0c\u5c31\u53ef\u4ee5\u6697\u4e2d\u89c6\u7269\u3002\u8fd9\u665a\u6708\u8272\u5f88\u597d\uff0c\u79e6\u5143\u6d69\u6765\u5230\u4e86\u5c01\u5999\u5a7f\u4f4f\u7684\u90a3\u95f4\u623f \u95f4\uff0c\u6311\u7834\u7eb8\u7cca\u7684\u7a97\u5b50\uff0c\u6084\u6084\u5f20\u671b\u3002\u5c01\u5999\u5ae6\u6070\u597d\u662f\u8138\u513f\u671d\u5916\uff0c\u7761\u7684\u6b63\u9163\u3002\u79e6\u5143\u6d69\u8ba4\u51fa\u4e86\u5979\uff0c\u7d27 \u5f20\u7684\u5fc3\u60c5\u677e\u4e86\u4e0b\u6765\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u5979\u548c\u90a3\u4e2a\u4eba\u8fd8\u672a\u66fe\u662f\u592b\u5987\u3002\u201d\u6b22\u559c\u4e4b\u4e0b\uff0c\u4e00\u4e2a\u4e0d\u614e\uff0c\u7f29\u624b \u56de\u6765\u4e4b\u65f6\uff0c\u78b0\u7740\u7a97\u683c\uff0c\u5f04\u51fa\u4e86\u4e00\u70b9\u58f0\u54cd\u3002 \u3000\u3000\u5389\u5357\u661f\u7761\u5728\u90bb\u623f\uff0c\u542c\u5f97\u58f0\u54cd\uff0c\u4ed6\u662f\u4e2a\u884c\u5bb6\uff0c\u7acb\u5373\u77e5\u9053\u662f\u6709\u591c\u884c\u4eba\u5230\u4e86\u3002 \u3000\u3000\u5389\u5357\u661f\u63a8\u5f00\u7a97\u5b50\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u4ed6\u56e0\u4e3a\u5c1a\u672a\u6478\u6e05\u5bf9\u65b9\u7684\u5e95\u7ec6\uff0c\u81ea\u662f\u4e0d\u597d\u4e71\u53d1 \u6697\u5668\u3002\u79e6\u5143\u6d69\u7684\u6765\u5077\u7aa5\u5c01\u5999\u5ae6\uff0c\u5f53\u7136\u4e5f\u662f\u4e0d\u4fbf\u5411\u4ed6\u89e3\u91ca\uff0c\u89c1\u4ed6\u53d1\u73b0\uff0c\u5927\u5403\u4e00\u60ca\u3002\u8fde\u5fd9\u9003\u8d70\u3002 \u3000\u3000\u79e6\u5143\u6d69\u8fd9\u4e00\u9003\uff0c\u5389\u5357\u661f\u7acb\u5373\u77e5\u9053\u4ed6\u662f\u201c\u5fc3\u6000\u4e0d\u8f68\u201d\uff0c\u6b63\u8981\u53bb\u8ffd\uff0c\u84e6\u5730\u5fc3\u5ff5\u4e00\u52a8\uff01\u201c\u4e0d \u597d\uff0c\u4e3a\u4f55\u4e0d\u89c1\u5979\u4eec\u58f0\u5f20\uff1f\u201d\u5979\u4eec\u4e8c\u4eba\u7684\u672c\u9886\u90fd\u5f88\u4e0d\u5f31\uff0c\u5373\u4f7f\u6ca1\u6709\u53d1\u89c9\u591c\u884c\u4eba\uff0c\u542c\u5f97\u6211\u7684\u53eb \u58f0\uff0c\u73b0\u5728\u4e5f\u8be5\u6709\u4e2a\u52a8\u9759\u5440\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u60ca\u7591\u4e0d\u5b9a\uff0c\u5f53\u4e0b\u53ea\u597d\u5148\u5165\u623f\u4e2d\u5bdf\u770b\u3002\u63a8\u5f00\u623f\u95e8\uff0c\u53eb\u4e86\u58f0\uff1a\u201c\u516c\u5b59\u59d1\u5a18\uff0c\u5c01\u59d1 \u5a18\uff01\u201d\u4ecd\u7136\u542c\u4e0d\u5230\u56de\u7b54\u3002\u5389\u5357\u661f\u8d8a\u53d1\u5403\u60ca\uff0c\u4e5f\u987e\u4e0d\u5f97\u907f\u5acc\u4e86\u3002\u4ed6\u8d70\u5165\u623f\u4e2d\uff0c\u70b9\u4eae\u6cb9\u706f\u4e00\u770b\uff0c \u53ea\u89c1\u5e8a\u4e0a\u7761\u7684\uff0c\u53ea\u662f\u5c01\u5999\u5ae6\u4e00\u4eba\u3002 \u3000\u3000\u5389\u5357\u661f\u662f\u4e2a\u6b66\u5b66\u884c\u5bb6\uff0c\u4e00\u770b\u5c31\u77e5\u5c01\u5999\u5ae6\u662f\u7ed9\u4eba\u70b9\u4e86\u6655\u7761\u7a74\uff0c\u53ef\u662f\u4ed6\u5374\u4e0d\u77e5\u8fd9\u662f\u516c\u5b59\u71d5\u70b9 \u7684\u3002\u5f53\u4e0b\u614c\u5fd9\u7ed9\u5c01\u5999\u5ae6\u89e3\u7a74\uff0c\u7acb\u5373\u4fbf\u95ee\uff1a\u201c\u4f60\uff0c\u4f60\u53ef\u906d\u4e86\u90a3\u8d3c\u5b50\u7684\u6b3a\u4fae\uff1f\u201d\u5c01\u5999\u5a7f\u63c9\u63c9\u773c \u775b\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u8d3c\u5b50\uff1f\u54a6\uff0c\u71d5\u59d0\u54ea\u91cc\u53bb\u4e86\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u653e\u4e0b\u4e86\u5fc3\uff0c\u65e0\u6687\u5411\u5c01\u5999\u5ae6\u591a\u95ee\uff0c\u4fbf\u9053\uff1a\u201c\u597d\uff0c\u6211\u53bb\u628a\u90a3\u8d3c\u5b50\u6293\u56de\u6765\uff01\u201d\u5fc3\u60f3\uff1a \u201c\u53ef\u80fd\u6765\u7684\u4e0d\u53ea\u4e00\u4eba\uff0c\u5c01\u5999\u5ae6\u7ed9\u4ed6\u4eec\u70b9\u4e86\u7a74\u9053\uff0c\u516c\u5b59\u71d5\u672c\u9886\u8f83\u5f3a\uff0c\u672a\u66fe\u53d7\u5230\u6697\u7b97\uff0c\u5148\u8ffd\u51fa\u53bb \u4e86\u3002\u54fc\uff0c\u4ed6\u4eec\u534a\u591c\u4e09\u66f4\uff0c\u8dd1\u8fdb\u5973\u5ba2\u4eba\u7684\u623f\u95f4\uff0c\u5b9a\u662f\u91c7\u82b1\u8d3c\u65e0\u7591\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u5ac9\u6076\u5982\u4ec7\uff0c\u7acb\u5373\u4fbf\u8ffd\u51fa\u53bb\uff01 \u3000\u3000\u5389\u5357\u661f\u521a\u624d\u8d70\u5165\u5c01\u5999\u5ae6\u623f\u95f4\u7684\u65f6\u5019\uff0c\u6b63\u662f\u79e6\u5143\u6d69\u8df3\u4e0a\u5c4b\u9876\u4e4b\u65f6\u3002\u4ed6\u770b\u89c1\u5389\u5357\u661f\u8fdb\u5165\u5c01\u5999 \u5ae6\u7684\u623f\u95f4\uff0c\u4e0d\u7981\u5fc3\u91cc\u4e00\u9178\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u5373\u4f7f\u4e0d\u662f\u592b\u59bb\uff0c\u81f3\u5c11\u4e5f\u662f\u60c5\u4fa3\u4e86\u3002\u5509\uff0c\u6211\u53ef\u4e0d \u80fd\u8ba9\u4ed6\u4eec\u8ffd\u4e0a\uff01\u5999\u5ae6\u5018\u82e5\u89c1\u7740\u6211\uff0c\u5979\u4f1a\u600e\u6837\u60f3\u5462\uff1f\u8fd9\uff0c\u8fd9\u771f\u662f\u592a\u4e0d\u597d\u610f\u601d\u4e86\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u7684\u8f7b\u529f\u6bd4\u5389\u5357\u661f\u7a0d\u900a\u4e00\u7b79\uff0c\u4f46\u56e0\u4e3a\u4ed6\u5148\u8dd1\u4e86\u4e00\u7a0b\uff0c\u5389\u5357\u661f\u5728\u6025\u5207\u4e4b\u95f4\uff0c\u5374\u662f\u8ffd\u4ed6 \u4e0d\u4e0a\u3002 \u3000\u3000\u4e14\u8bf4\u516c\u5b59\u71d5\u4ece\u6c34\u4e91\u5e84\u5306\u5306\u8d76\u56de\uff0c\u89c1\u4e00\u4e2a\u5c11\u5e74\u7537\u5b50\u5728\u8def\u4e0a\u98de\u8dd1\u3002\u79e6\u5143\u6d69\u662f\u6362\u4e0a\u4e86\u591c\u884c\u8863 \u7684\uff0c\u516c\u5b59\u71d5\u770b\u4e86\u770b\u597d\u50cf\u662f\u65e5\u95f4\u6240\u89c1\u7684\u90a3\u4e2a\u7537\u5b50\uff0c\u4f46\u8fd8\u4e0d\u6562\u65ad\u5b9a\u3002 \u3000\u3000\u516c\u5b59\u71d5\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u5389\u5357\u661f\u5df2\u5728\u540e\u9762\u626c\u58f0\u53eb\u9053\uff1a\u201c\u5feb \u622a\u4f4f\u4ed6\uff0c\u4ed6\u662f\u91c7\u82b1\u8d3c\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u6697\u6697\u53eb\u82e6\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u7684\u786e\u662f\u534a\u591c\u4e09\u66f4\u5728\u5c01\u5999\u5ae6\u7684\u7a97\u5916\u5077\u770b\uff0c\u8fd9\u91c7\u82b1\u8d3c\u7684\u5acc \u7591\u5982\u4f55\u80fd\u591f\u5411\u5979\u7684\u60c5\u4eba\u89e3\u91ca\u660e\u767d\uff1f\u5509\uff0c\u8d81\u7740\u5999\u5ae6\u672a\u5230\uff0c\u8dd1\u5f97\u8131\u8fd8\u662f\u8dd1\u4e86\u7684\u597d\uff01\u201d \u3000\u3000\u54ea\u77e5\u516c\u5b59\u71d5\u7684\u8f7b\u529f\u751a\u662f\u4e86\u5f97\uff0c\u79e6\u5143\u6d69\u626d\u5934\u4e00\u8dd1\uff0c\u811a\u6b65\u672a\u66fe\u7ad9\u7a33\uff0c\u516c\u5b59\u71d5\u53c8\u5df2\u62e6\u5728\u4ed6\u7684\u9762 \u524d\uff0c\u559d\u9053\uff1a\u201c\u7ad9\u4f4f\uff0c\u6211\u6709\u8bdd\u95ee\u4f60\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u53eb\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6211\u4e0d\u662f\u91c7\u82b1\u8d3c\uff01\u201d\u8eab\u5f62\u4e00\u95ea\uff0c\u8f6c\u8fc7\u4e00\u4e2a\u65b9\u5411\u53c8\u8dd1\u3002 \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u91c7\u82b1\u8d3c\u4f55\u5fc5\u5fc3\u614c\uff1f\u201d\u5389\u5357\u661f\u53eb\u9053\uff1a\u201c\u5148\u628a\u4ed6\u6293\u7740\u518d\u8bf4\uff01\u201d\u516c\u5b59\u71d5\u4e00 \u60f3\u4e0d\u9519\uff0c\u514d\u5f97\u8ba4\u9519\u4e86\u4eba\uff0c\u53c8\u95f9\u51fa\u7b11\u8bdd\u3002 \u3000\u3000\u516c\u5b59\u71d5\u5982\u5f71\u968f\u5f62\u5730\u8ddf\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u5feb\u8bf4\uff0c\u5426\u5219\u4f11\u602a\u59d1\u5a18\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u6211\u662f\u8fc7\u8def\u7684\u5ba2\u4eba\uff0c\u6211\u6709\u7d27\u8981\u7684\u4e8b\uff0c\u59d1\u5a18\uff0c\u4f60\u9976\u4e86\u6211\u5427\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u4f60\u4e0d\u8bf4\u5b9e\u8bdd\uff0c\u5c31\u662f\u4e0d\u884c\uff01\u201d\u8ffd\u5230\u80cc\u540e\uff0c\u4e00\u6307\u5c31\u70b9\u4ed6\u7684\u540e\u5fc3\u3002 \u3000\u3000\u79e6\u5143\u6d69\u65e0\u53ef\u5948\u6cb3\uff0c\u53ea\u597d\u53cd\u624b\u4e00\u6293\uff0c\u4ee5\u653b\u4e3a\u5b88\u7684\u89e3\u5f00\u516c\u5b59\u71d5\u7684\u4e00\u62db\u3002 \u3000\u3000\u516c\u5b59\u71d5\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u4f46\u56e0\u662f\u6b66\u5b66\u540d\u5bb6\u4e4b\u5973\uff0c\u89c1\u591a\u8bc6\u5e7f\uff0c\u548c\u79e6\u5143\u6d69\u62c6\u4e86\u51e0\u62db\uff0c\u770b\u51fa\u4e86\u4ed6\u662f\u6b66 \u5f53\u6d3e\u7684\u5bb6\u6570\u3002 \u3000\u3000\u5389\u5357\u661f\u8ffd\u4e86\u4e0a\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u5b59\u59d1\u5a18\uff0c\u4f60\u628a\u8fd9\u8d3c\u5b50\u4ea4\u7ed9\u6211\u5427\u3002\u201d\u4ed6\u81ea\u5fd6\u53ef\u4ee5\u6293\u5f97\u7740\u79e6\u5143 \u6d69\uff0c\u4f46\u5374\u4e0d\u613f\u610f\u4ee5\u4e8c\u654c\u4e00\u3002 \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4f60\u662f\u4e0d\u662f\u8981\u8dd1\u56de\u6c34\u4e91\u5e84\u53bb\u7684\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u90a3\u4e48\uff0c\u4f60\u662f\u4e0d\u662f\u79e6\u2026\u2026\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u5c01\u5999\u5ae6\u6c14\u5598\u5401\u5401\u5730\u8dd1\u6765\uff0c\u53eb\u9053\uff1a \u201c\u71d5\u59d0\u624b\u4e0b\u7559\u60c5\uff0c\u4ed6\uff0c\u4ed6\u662f\u79e6\u5143\u6d69\uff01\u201d\u5c01\u5999\u5ae6\u4e0d\u77e5\u9053\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u53ea\u5f53\u662f\u516c\u5b59\u71d5\u53bb\u627e\u79e6 \u5143\u6d69\u7684\u6666\u6c14\uff0c\u4ece\u6c34\u4e91\u5e84\u6253\u5230\u8fd9\u513f\u6765\u4e86\u3002 \u3000\u3000\u5389\u5357\u661f\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4ed6\uff0c\u4ed6\u662f\u79e6\u5143\u6d69\uff1f\u90a3\u4e48\u4ed6\u4e3a\u4ec0\u4e48\u70b9\u4e86\u4f60\u7684\u7a74\u9053\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u201c\u5657\u55e4\u201d\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c01\u59d0\u59d0\u7684\u7a74\u9053\u662f\u6211\u70b9\u7684\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u6ee1\u9762\u901a\u7ea2\uff0c\u5411\u5c01\u5999\u5ae6\u4e00\u4e2a\u957f\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u795d\u4f60\u4eec\u767d\u5934\u5055\u8001\u3002\u6211\u4eca\u665a\u53ea\u662f\u60f3\u89c1\u4f60\u4e00 \u9762\uff0c\u5e76\u65e0\u6076\u610f\uff0c\u4f60\u53ef\u4ee5\u8ba9\u6211\u8d70\u4e86\u5427\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u832b\u7136\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u4f60\u662f\u6c34\u4e91\u5e84\u7684\u5a07\u5ba2\uff0c\u5e94\u8be5\u662f\u6211\u795d\u8d3a\u4f60\u548c\u4e91\u59d1\u5a18\u767d\u5934\u8c10\u8001\u624d \u5bf9\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u54c8\u54c8\u5927\u7b11\uff0c\u6025\u5207\u95f4\u4e5f\u4e0d\u77e5\u5411\u8c01\u5148\u8bf4\u624d\u597d\uff0c\u5f53\u4e0b\u53ea\u597d\u53eb\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u65b0\u90ce\uff01\u201d\u7b11\u8fc7 \u4e4b\u540e\uff0c\u624d\u52a0\u4ee5\u89e3\u91ca\u9053\uff1a\u201c\u6211\u5230\u8fc7\u6c34\u4e91\u5e84\u4e86\uff0c\u65b0\u90ce\u540d\u53eb\u79e6\u5c11\u9633\uff0c\u662f\u4e91\u5e84\u4e3b\u7684\u638c\u95e8\u5927\u5f1f\u5b50\u3002\u5c01\u59d0 \u59d0\uff0c\u4f60\u4eec\u8fd9\u4e00\u573a\u8bef\u4f1a\u53ef\u771f\u662f\u95f9\u5f97\u5927\u4e86\uff0c\u8fde\u6211\u4e5f\u95f9\u51fa\u4e86\u7b11\u8bdd\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u8be7\u9053\uff1a\u201c\u8bf7\u95ee\u59d1\u5a18\u8d35\u59d3\u5927\u540d\uff0c\u4f60\u6211\u7d20\u4e0d\u76f8\u8bc6\uff0c\u4f55\u4ee5\u4f60\u5230\u6c34\u4e91\u5e84\u627e\u6211\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u4e00\u9762\u7b11\uff0c\u4e00\u9762\u8bf4\u9053\uff1a\u201c\u6211\u53eb\u516c\u5b59\u71d5\uff0c\u6211\u662f\u4f60\u7684\u5c01\u59d0\u59d0\u7684\u597d\u670b\u53cb\u3002\u5979\u4ee5\u4e3a\u4f60\u505a\u4e86\u4e91 \u5bb6\u7684\u5973\u5a7f\uff0c\u53ef\u662f\u5979\u5bb3\u7f9e\u53c8\u4e0d\u6562\u53bb\u5411\u4f60\u95ee\u4e2a\u660e\u767d\u3002\u6ca1\u5948\u4f55\u6211\u53ea\u597d\u66ff\u5979\u53bb\u4e86\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u5979\u8fd8\u6ca1\u6709\u53d8\u5fc3\uff0c\u8981\u4e0d\u7136\u5979\u4e0d\u4f1a\u5bb3\u6015\u6211\u505a\u4e86\u522b\u5bb6\u7684\u5973\u5a7f\u3002 \u4f46\u5374\u4e0d\u77e5\u8fd9\u4e2a\u4eba\u548c\u5979\u53c8\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u5e73\u65f6\u4e0d\u901a\u4e16\u6545\uff0c\u4f46\u8fd9\u56de\u5374\u662f\u201c\u61c2\u4e8b\u201d\u5f97\u5f88\uff0c\u5979\u77e5\u9053\u79e6\u5143\u6d69\u8d77\u4e86\u8bef\u4f1a\uff0c\u4fbf\u9053\uff1a\u201c\u79e6 \u5c11\u4fa0\uff0c\u4f60\u548c\u91d1\u9010\u6d41\u662f\u4e0d\u662f\u597d\u670b\u53cb\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u4e0d\u9519\u3002\u6211\u6b63\u60f3\u6253\u542c\u4ed6\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u4f60\u4eec\u4e24\u4eba\u4e5f\u5e94\u8be5\u662f\u597d\u670b\u53cb\u4e86\u3002\u4f60\u77e5\u9053\u4ed6\u662f\u8c01\u4e48\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u5389\u5927\u54e5\u548c\u91d1\u9010\u6d41\u662f\u516b\u62dc\u4e4b\u4ea4\uff0c\u6211\u591a\u4e8f\u4ed6\u6551\u4e86\u6211\u7684\u6027\u547d\uff0c\u5426\u5219 \u4eca\u5929\u5c31\u4e0d\u80fd\u548c\u4f60\u76f8\u89c1\u4e86\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u7b11\u9053\uff1a\u201c\u4f60\u53ea\u8bf4\u4e86\u4e09\u5206\u4e4b\u4e00\uff0c\u5176\u5b9e\u662f\u4f60\u5148\u6551\u4e86\u6211\u7684\u6027\u547d\uff0c\u800c\u5e2e\u4e86\u4f60\u7684\u5927\u5fd9\u7684\uff0c\u4e5f \u4e0d\u662f\u6211\u800c\u662f\u8fd9\u4f4d\u516c\u5b59\u59d1\u5a18\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4f60\u522b\u7ed9\u6211\u8138\u4e0a\u8d34\u91d1\uff0c\u662f\u6211\u8981\u5c01\u59d0\u59d0\u548c\u6211\u4f5c\u4f34\uff0c\u624d\u628a\u5979\u62c9\u6765\u7684\u3002\u6211\u4e0d\u662f\u5e2e\u5979 \u7684\u5fd9\uff0c\u6211\u662f\u5e2e\u81ea\u5df1\u7684\u5fd9\u3002\u201d\u5f53\u4e0b\u54ad\u54ad\u5471\u5471\u5730\u62a2\u7740\u628a\u90a3\u65e5\u9047\u89c1\u5c01\u5999\u5ae6\u4e4b\u4e8b\u8bf4\u4e86\u51fa\u6765\uff0c\u5c01\u5999\u5ae6\u8ddf \u7740\u4e5f\u628a\u4e0e\u5389\u5357\u661f\u7ed3\u8bc6\u4e4b\u4e8b\u544a\u8bc9\u79e6\u5143\u6d69\u3002 \u3000\u3000\u79e6\u5143\u6d69\u8fd9\u624d\u77e5\u9053\u662f\u4e00\u573a\u8bef\u4f1a\uff0c\u8fde\u5fd9\u5411\u5389\u5357\u661f\u548c\u516c\u5b59\u71d5\u4e8c\u4eba\u9053\u8c22\u3002 \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u6211\u4eec\u4e09\u4eba\u6b63\u662f\u8981\u4e00\u9053\u5230\u897f\u660c\u53bb\u627e\u91d1\u9010\u6d41\uff0c\u4f60\u53bb\u4e0d\u53bb\uff1f\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u81ea\u5df1\u5148 \u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u662f\u660e\u77e5\u6545\u95ee\uff0c\u5c01\u59d0\u59d0\u5728\u8fd9\u513f\uff0c\u53ea\u6015\u6211\u4e0d\u8bb8\u4f60\u53bb\uff0c\u4f60\u4e5f\u662f\u975e\u53bb\u4e0d\u53ef\u7684 \u4e86\uff01\u79e6\u5c11\u4fa0\uff0c\u6211\u4e00\u5411\u559c\u6b22\u548c\u4eba\u5bb6\u5f00\u73a9\u7b11\uff0c\u4f60\u522b\u89c1\u602a\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u8bef\u4f1a\u51b0\u6d88\uff0c\u5fc3\u91cc\u65e9\u5df2\u662f\u522e\u4e1d\u4e1d\u7684\uff0c\u6b64\u65f6\u7ed9\u5979\u9017\u5f97\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u72f2\u59d1\u5a18\uff0c \u4f60\u731c\u9519\u4e86\u3002\u6211\u53ef\u662f\u8981\u56de\u6c34\u4e91\u538b\u7684\u5462\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4f60\u5f53\u771f\u4e0d\u53bb\u897f\u660c\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u660e\u5929\u6211\u8fd8\u8981\u505a\u4f34\u90ce\u5462\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5982\u6b64\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u6c38\u8fdc\u4e0d\u53bb\u5462\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u4e00\u672c\u6b63\u7ecf\u5730\u8bf4\u9053\uff1a\u201c\u8fc7\u4e86\u660e\u5929\uff0c\u6211\u5f53\u7136\u662f\u8981\u53bb\u7684\u3002\u201d\u8fd9\u56de\u8f6e\u5230\u4ed6\u628a\u516c\u5b59\u71d5\u9017\u5f97\u7b11 \u4e86\u3002 \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u4e91\u5e84\u4e3b\u5341\u5206\u597d\u5ba2\uff0c\u4f60\u4eec\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53ea\u89c1\u6797\u5c97\u7b49\u4eba\u5df2\u7ecf\u5411\u7740\u4ed6\u4eec\u8dd1 \u6765\u3002\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u4f60\u66ff\u4e91\u5e84\u4e3b\u4ee3\u9080\uff1b\u9080\u5ba2\u7684\u4eba\u5df2\u7ecf\u6765\u5566\uff01\u201d \u3000\u3000\u5f53\u4e0b\u4e00\u884c\u4eba\u540c\u5230\u6c34\u4e91\u5e84\uff0c\u7b2c\u4e8c\u65e5\u559d\u8fc7\u4e86\u4e91\u4e2d\u71d5\u7684\u559c\u9152\uff0c\u5389\u5357\u661f\u3001\u516c\u5b59\u71d5\u3001\u79e6\u5143\u6d69\u3001\u5c01\u5999 \u5ae6\u56db\u4eba\u53c8\u5373\u767b\u7a0b\uff0c\u8d76\u5f80\u897f\u660c\u3002 \u3000\u3000\u79e6\u5143\u6d69\u4e0e\u5c01\u5999\u5ae6\u7ecf\u8fc7\u8bb8\u591a\u963b\u6320\u65b9\u5f97\u4e00\u8d77\uff0c\u672a\u5a5a\u592b\u59bb\u7684\u5173\u7cfb\u5df2\u7ecf\u786e\u5b9a\uff0c\u4e00\u8def\u4e4b\u662f\u81ea\u7136\u662f\u5c11 \u4e0d\u4e86\u60c5\u4fa3\u5e94\u6709\u7684\u775b\u6625\u98ce\u5149\u3002\u5c3d\u7ba1\u4ed6\u4eec\u5df2\u7ecf\u662f\u5728\u4eba\u524d\u63a9\u9970\uff0c\u4e5f\u8fd8\u662f\u5904\u5904\u900f\u9732\u51fa\u6765\u3002\u5389\u5357\u661f\u89e6\u666f \u4f24\u60c5\uff0c\u66f4\u589e\u6005\u60d8\u3002 \u3000\u3000\u516c\u5b59\u71d5\u770b\u5728\u773c\u5185\uff0c\u597d\u751f\u7eb3\u7f55\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u4ee5\u524d\u80e1\u4e71\u731c\u7591\uff0c\u4ee5\u4e3a\u5389\u5927\u54e5\u662f\u548c\u5c01\u59d0\u59d0\u76f8 \u7231\uff0c\u771f\u662f\u53ef\u7b11\uff0c\u4f46\u5389\u5927\u54e5\u5374\u53c8\u56e0\u4f55\u95f7\u95f7\u4e0d\u4e50\u5462\uff1f\u201d\u5979\u6000\u7740\u8fd9\u4e2a\u7591\u95ed\uff0c\u4e00\u76f4\u5230\u4e86\u897f\u660c\uff0c\u8fd8\u672a\u66fe \u5f97\u6709\u673a\u4f1a\u53bb\u95ee\u5389\u5357\u661f\u3002 \u3000\u3000\u8fd9\u65e5\uff0c\u4ed6\u4eec\u7ed5\u8fc7\u4e86\u897f\u660c\uff0c\u5230\u4e86\u7afa\u5c1a\u7236\u8fd9\u652f\u4e49\u519b\u7684\u6839\u636e\u5730\u5927\u51c9\u5c71\u3002 \u3000\u3000\u7afa\u5c1a\u7236\u7684\u5973\u513f\u7afa\u6e05\u534e\u548c\u91d1\u9010\u6d41\u7684\u5e08\u4f84\u674e\u5149\u590f\u7b49\u4eba\u51fa\u6765\u8fce\u63a5\uff0c\u516c\u5b59\u71d5\u89c1\u4e86\u7afa\u6e05\u534e\uff0c\u5341\u5206\u6b22 \u559c\uff0c\u7b11\u9053\uff1a\u201c\u6211\u7279\u5730\u8d76\u6765\u559d\u4f60\u7684\u559c\u9152\u6765\u5566\uff0c\u4f60\u4eec\u7684\u5a5a\u4e8b\u5b9a\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u7afa\u6e05\u534e\u9762\u4e0a\u4e00\u7ea2\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u3002\u7239\u7239\u7684\u610f\u601d\u662f\u8981\u7b49\u5230\u593a\u56de\u897f\u660c\u518d\u7ed9\u6211\u4eec\u5b89\u6392\u3002\u201d \u7afa\u6e05\u534e\u7684\u6027\u60c5\u548c\u516c\u5b59\u71d5\u4e00\u6837\uff0c\u662f\u4e2a\u7eaf\u771f\u723d\u6717\u7684\u59d1\u5a18\uff0c\u6545\u6b64\u5728\u597d\u53cb\u95ee\u5979\u5a5a\u671f\u7684\u65f6\u5019\uff0c\u5979\u867d\u7136\u662f \u514d\u4e0d\u4e86\u6709\u51e0\u5206\u5973\u5b69\u513f\u7684\u7f9e\u6001\uff0c\u5374\u8fd8\u662f\u7167\u76f4\u8bf4\u4e86\u3002 \u3000\u3000\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u5feb\u4e86\uff0c\u4f60\u7239\u7239\u6b66\u529f\u7edd\u4e16\uff0c\u53c8\u6709\u8fd9\u8bb8\u591a\u8c6a\u6770\u76f8\u52a9\uff0c\u5c0f\u5c0f\u4e00\u5ea7\u897f\u660c\u57ce\u8fd8 \u6015\u593a\u4e0d\u56de\u6765\u3002\u201d \u3000\u3000\u7afa\u6e05\u534e\u9053\uff1a\u201c\u4f60\u83ab\u5c0f\u89d1\u4e86\u654c\u4eba\uff0c\u897f\u660c\u7684\u6ee1\u5dde\u5c06\u519b\u5e05\u5b5f\u96c4\u6b66\u529f\u5f88\u662f\u4e0d\u5f31\uff0c\u6211\u7684\u7239\u7239\u4e5f\u66fe\u53d7 \u4e86\u4ed6\u7684\u6697\u7b97\u5462\u3002\u8fd1\u6708\u6765\u6e05\u5ef7\u53c8\u9646\u7eed\u5411\u897f\u660c\u589e\u5175\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u4eec\u8fd8\u4f1a\u5148\u6765\u653b\u6253\u6211\u4eec\u5462\uff0c\u55ef\uff0c\u54b1\u4eec \u522b\u53ea\u662f\u987e\u7740\u81ea\u5df1\u8bf4\u8bdd\u4e86\uff0c\u4f60\u8fd9\u51e0\u4f4d\u670b\u53cb\u6211\u8fd8\u6ca1\u6709\u8bf7\u6559\u2026\u2026\u201d \u3000\u3000\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u4e0d\u9519\u3002\u8fd9\u91cc\u6709\u4e00\u4f4d\u4f60\u7684\u957f\u8f88\uff0c\u4f60\u5e94\u8be5\u5148\u6765\u5411\u957f\u8f88\u884c\u793c\u3002\u201d \u3000\u3000\u7afa\u6e05\u534e\u4e00\u770b\uff0c\u5389\u5357\u661f\u3001\u79e6\u5143\u6d69\u7684\u5e74\u7eaa\u548c\u5979\u76f8\u5dee\u4e0d\u4e86\u591a\u5c11\uff0c\u5c01\u5999\u5ae6\u5219\u4f3c\u4e4e\u6bd4\u5979\u8fd8\u8981\u5e74\u8f7b\uff0c \u4e0d\u89c9\u7eb3\u7f55\u9053\uff1a\u201c\u54ea\u4f4d\u662f\u6211\u957f\u8f88\uff0c\u6055\u6211\u4e0d\u77e5\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u674e\u5149\u590f\u5df2\u7ecf\u8d70\u4e86\u8fc7\u6765\uff0c\u606d\u606d\u656c \u656c\u5730\u5411\u5389\u5357\u661f\u884c\u4e86\u53c2\u62dc\u4e4b\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u5389\u53d4\u53d4\u9a7e\u4e34\uff0c\u5c0f\u4f84\u674e\u5149\u590f\u53c2\u8c12\u3002\u201d\u539f\u6765\u674e\u5149\u590f\u4e0e\u79e6\u5143 \u6d69\u662f\u5728\u6c5f\u5bb6\u53ea\u5df2\u76f8\u8bc6\u7684\uff0c\u79e6\u5143\u6d69\u5df2\u7ecf\u544a\u8bc9\u4e86\u4ed6\u5389\u5357\u661f\u548c\u91d1\u9010\u6d41\u7684\u5173\u7cfb\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5389\u5927\u54e5\u662f\u91d1\u9010\u6d41\u7684\u7ed3\u4e49\u5144\u957f\uff0c\u4f60\u8fd8\u4e0d\u5e94\u8be5\u8ddf\u968f\u5149\u590f\u5c0a\u4ed6\u4e00\u58f0\u53d4\u4e48\uff1f \u563f\uff0c\u563f\uff0c\u6211\u597d\u5728\u548c\u4ed6\u5e76\u65e0\u5e08\u95e8\u6e0a\u6e90\uff0c\u6240\u4ee5\u6211\u53ea\u987b\u53eb\u4ed6\u4e00\u58f0\u5927\u54e5\u4fbf\u884c\u3002\u4f60\u5374\u5e73\u767d\u7684\u8981\u6bd4\u6211\u77ee\u4e00 \u8f88\u4e86\u3002\u201d \u3000\u3000\u7afa\u6e05\u534e\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u4ece\u524d\u90a3\u6837\u559c\u6b22\u5f00\u73a9\u7b11\u7684\u5b69\u5b50\u813e\u6c14\u3002\u54b3\uff0c\u771f\u4e0d\u77e5\u4f60\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u957f \u5927\uff1f\u5176\u5b9e\u4f60\u4e5f\u90fd\u53ef\u4ee5\u505a\u65b0\u5a18\u5b50\u5566\uff01\u201d\u516c\u5b59\u71d5\u7ed9\u5979\u8c03\u4f83\u4e86\u51e0\u53e5\uff0c\u4e0d\u7531\u5f97\u4e5f\u662f\u5426\u8138\u6cdb\u7ea2\u3002\u7afa\u6e05\u534e \u7b11\u9053\uff1a\u201c\u8fd9\u5012\u5947\u4e86\uff0c\u4f60\u8fd9\u6837\u539a\u7684\u8138\u76ae\u4e5f\u4f1a\u5bb3\u7f9e\u3002\u7b11\u8fc7\u4e4b\u540e\uff0c\u8fd9\u624d\u5411\u5389\u5357\u661f\u884c\u793c\u3002 \u3000\u3000\u5389\u5357\u661f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u8bb8\u591a\u8bb2\u7a76\uff1f\u54b1\u4eec\u5404\u4ea4\u5404\u7684\uff0c\u5927\u5bb6\u90fd\u4ee5\u5e73\u8f88\u8bba\u4ea4\uff0c\u7701\u5f97\u53d7\u4e86\u62d8 \u675f\uff0c\u4e0d\u66f4\u597d\u4e48\uff1f\u5176\u5b9e\u6b66\u6797\u4e2d\u7684\u4ec0\u4e48\u8f88\u4efd\uff0c\u4e5f\u5f53\u771f\u662f\u62ff\u8d77\u7b97\u76d8\u4e5f\u6253\u4e0d\u6e05\u7684\u3002\u201d\u4f17\u4eba\u542c\u4ed6\u8bf4\u5f97\u6709 \u8da3\uff0c\u90fd\u4e0d\u7981\u7b11\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8fdb\u4e86\u5c71\u5be8\uff0c\u7afa\u5c1a\u7236\u542c\u8bf4\u5389\u5357\u661f\u662f\u91d1\u9010\u6d41\u7684\u4e49\u5144\uff0c\u6709\u5fc3\u8bd5\u4ed6\u672c\u9886\uff0c\u5728\u4ed6\u884c\u793c\u7684\u65f6\u5019\uff0c\u8f7b\u8f7b \u7528\u624b\u4e00\u6276\uff0c\u5389\u5357\u661f\u53ea\u89c9\u4e00\u80a1\u5927\u529b\u8981\u628a\u4ed6\u63d0\u4e86\u8d77\u6765\uff0c\u5f53\u4e0b\u8fde\u5fd9\u7528\u91cd\u8eab\u6cd5\u7a33\u4f4f\u8eab\u5f62\uff0c\u4f46\u4e5f\u53ea\u80fd\u5c48 \u4e2a\u534a\u819d\uff0c\u4e0d\u80fd\u884c\u53c2\u62dc\u7684\u5927\u793c\u4e86\u3002 \u3000\u3000\u7afa\u5c1a\u7236\u6380\u987b\u7b11\u9053\uff1a\u201c\u5389\u8001\u5f1f\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u6211\u542c\u8bf4\u4f60\u548c\u91d1\u9010\u6d41\u5927\u95f9\u4eac\u57ce\uff0c\u5f53\u771f\u662f\u82f1\u96c4\u51fa \u5c11\u5e74\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u8c26\u865a\u4e86\u51e0\u53e5\uff0c\u95ee\u9053\uff1a\u201c\u4e0d\u77e5\u9010\u6d41\u6765\u8fc7\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u7afa\u5c1a\u7236\u8be7\u9053\uff1a\u201c\u8c01\u8bf4\u4ed6\u8981\u6765\u7684\uff1f\u53ef\u8fd8\u6ca1\u89c1\u7740\u5440\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u6a35\u516c\u5b59\u8235\u4e3b\u5728\u626c\u5dde\u63a2\u542c\u5230\u7684\u6d88\u606f\uff0c\u8bf4\u662f\u9010\u6d41\u548c\u4e10\u5e2e\u7684\u4e00\u4e9b\u4eba\uff0c\u65e9\u5df2\u79bb\u5f00\u4e86 \u626c\u5dde\u3002\u5728\u5317\u4eac\u7684\u65f6\u5019\uff0c\u6211\u4e5f\u66fe\u542c\u4ed6\u8bf4\u8fc7\u8981\u5230\u8001\u524d\u8f88\u8fd9\u513f\uff0c\u6211\u53ea\u9053\u4ed6\u5df2\u7ecf\u6765\u4e86\u3002\u201d \u3000\u3000\u7afa\u5c1a\u7236\u9053\uff1a\u201c\u6216\u8bb8\u4ed6\u4eec\u5728\u8def\u4e0a\u6709\u4ec0\u4e48\u4e8b\u60c5\u803d\u6401\uff0c\u8fc7\u51e0\u5929\u5c31\u4f1a\u6765\u4e86\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u89c1\u4e0d\u7740\u91d1\u9010\u6d41\uff0c\u9887\u662f\u6709\u70b9\u611f\u5230\u610f\u5916\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u4eec\u8fd9\u4e00\u5e2e\u4eba\u52a8\u8eab\u5728\u6211\u4e4b\u524d\uff0c \u4e3a\u4ec0\u4e48\u8fd8\u672a\u5230\u5462\uff1f\u96be\u9053\u9010\u6d41\u5077\u8ff8\u897f\u660c\u53bb\u4e86\uff1f\u201d \u3000\u3000\u7afa\u5c1a\u7236\u4f3c\u4e4e\u77e5\u9053\u4ed6\u7684\u5fc3\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u81ea\u4e0b\u6709\u5341\u51e0\u4e2a\u5927\u5185\u9ad8\u624b\u4e00\u5230\u4e86\u897f\u660c\uff0c\u5175\u529b\u4e5f\u6bd4\u4ee5\u524d \u589e\u5f3a\u4e86\u51e0\u500d\uff0c\u57ce\u4e2d\u6212\u5907\u68ee\u4e25\uff0c\u6211\u6b63\u7b49\u5f85\u5c0f\u91d1\u5ddd\u65b9\u9762\u7684\u4e49\u519b\u6765\u4e86\uff0c\u624d\u597d\u5408\u5175\u653b\u57ce\u3002\u82e5\u662f\u6ca1\u6709\u5fc5 \u8981\u7684\u4e8b\uff0c\u54b1\u4eec\u7684\u4eba\u8fd8\u662f\u6700\u597d\u4e0d\u8981\u5230\u897f\u660c\u53bb\u3002\u201d\u5389\u5357\u661f\u672c\u6765\u60f3\u8981\u6c42\u5230\u897f\u660c\u53bb\u6253\u542c\u6d88\u606f\u7684\uff0c\u542c\u5f97 \u7afa\u5c1a\u7236\u8fd9\u4e48\u8bf4\uff0c\u4e5f\u53ea\u597d\u6682\u4e14\u4f5c\u7f62\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u51e0\u5929\uff0c\u4ecd\u7136\u4e0d\u89c1\u91d1\u9010\u6d41\u6765\u5230\uff0c\u5389\u5357\u661f\u751a\u662f\u5fc3\u7126\uff0c\u4f46\u662f\u8981\u9075\u5b88\u4e49\u519b\u7684\u7eaa\u5f8b\uff0c\u4e0d\u80fd\u79c1\u81ea \u4e0b\u5c71\uff0c\u53ea\u597d\u5728\u5be8\u4e2d\u7b49\u5f85\u3002 \u3000\u3000\u8fd8\u6709\u4e00\u4e2a\u95f7\u5f97\u65e0\u804a\u7684\u4eba\u662f\u516c\u5b59\u71d5\u3002\u7afa\u6e05\u534e\u548c\u5c01\u5999\u5ae6\u90fd\u662f\u6709\u4e86\u672a\u5a5a\u592b\u7684\u4eba\uff0c\u867d\u7136\u5979\u4eec\u5e38\u5e38 \u9080\u5979\u4e00\u540c\u6e38\u73a9\uff0c\u516c\u5b59\u71d5\u5374\u662f\u4e0d\u4fbf\u63d2\u5728\u4ed6\u4eec\u4e2d\u95f4\u3002\u516c\u5b59\u71d5\u4f4f\u5728\u5973\u8425\uff0c\u548c\u5389\u5357\u661f\u4e5f\u662f\u4e0d\u80fd\u7ecf\u5e38\u89c1 \u9762\u3002\u4e0d\u77e5\u600e\u7684\uff0c\u5979\u6bcf\u5f53\u95f7\u5f97\u5143\u804a\u7684\u65f6\u5019\uff0c\u603b\u662f\u60f3\u89c1\u4e00\u89c1\u5389\u5357\u661f\u3002\u4f46\u8fd9\u5fc3\u4e8b\u5979\u53c8\u4e0d\u6562\u51fa\u53e3\uff0c\u7afa \u6e05\u534e\u4e0e\u5979\u4eb2\u5982\u59d0\u59b9\uff0c\u5979\u4e5f\u6ca1\u6709\u544a\u8bc9\u7afa\u6e05\u534e\u3002\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u6837\u5462\uff1f\u8fde\u5979\u767d\u5df1\u4e5f\u89c9\u5f97\u6709\u70b9\u5947\u602a\u3002\u5728 \u672a\u7ed3\u8bc6\u5389\u5357\u661f\u4e4b\u524d\uff0c\u5979\u672c\u6765\u662f\u4e2a\u4e0d\u89e3\u6101\u95f7\u4e3a\u4f55\u7269\uff0c\u6210\u5929\u7b11\u53e3\u5e38\u5f00\u7684\u59d1\u5a18\u3002 \u3000\u3000\u4e00\u665a\uff0c\u516c\u5b59\u71d5\u95f7\u5750\u5143\u804a\uff0c\u72ec\u81ea\u5230\u6797\u4e2d\u6563\u6b65\uff0c\u5ffd\u542c\u5f97\u4e00\u7f15\u8427\u58f0\uff0c\u5982\u6028\u5982\u6068\uff0c\u5982\u6ce3\u5982\u8bc9\uff0c\u4ece \u6797\u4e2d\u4e00\u5904\u4f20\u6765\u3002\u516c\u5b59\u71d5\u60f3\u9053\uff1a\u201c\u5389\u5927\u54e5\u60f3\u5fc5\u4e5f\u662f\u548c\u6211\u4e00\u6837\uff0c\u95f7\u5f97\u65e0\u804a\uff0c\u4e00\u4e2a\u4eba\u8eb2\u5728\u6797\u5b50\u4e3e\u5439 \u8427\u3002\u4f46\u4ed6\u4e3a\u4ec0\u4e48\u8001\u662f\u5439\u8fd9\u6837\u54c0\u6028\u7684\u66f2\u5b50\u5462\uff1f\u201d\u4e0d\u77e5\u4e0d\u89c9\u5c31\u5411\u8427\u58f0\u6765\u5904\u8d70\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u5389\u5357\u661f\u5439\u4e86\u4e00\u4f1a\u8427\uff0c\u5ffd\u5730\u53f9\u4e86\u53e3\u6c14\u201d\u66fc\u58f0\u8bf4\u9053\uff1a\u201c\u8bb0\u7389\u5173\u8e0f\u96ea\u4e8b\u6e05\u6e38\uff0c\u5bd2\u6c14\u8106\u8c82 \u88d8\u3002\u508d\u67af\u6797\u5360\u9053\uff0c\u957f\u6cb3\u996e\u9a6c\uff0c\u6b64\u610f\u60a0\u60a0\u3002\u77ed\u68a6\u4f9d\u7136\u6c5f\u5357\uff0c\u8001\u6cea\u6d12\u897f\u5dde\u3002\u4e00\u5b57\u65e0\u9898\u5904\uff0c\u843d\u53f6\u90fd \u6101\u3002\u8f7d\u53d6\u767d\u4e91\u5f52\u53bb\uff0c\u95ee\u8bfd\u7559\u695a\u4f69\uff0c\u5f04\u5f71\u4e2d\u5dde\uff1f\u6298\u82a6\u82b1\u8d60\u8fdc\uff0c\u96f6\u843d\u4e00\u8eab\u79cb\u3002\u5411\u5bfb\u5e38\u3001\u91ce\u6865\u6d41 \u6c34\uff0c\u5f85\u62db\u6765\u3001\u4e0d\u662f\u65e7\u6c99\u9e25\u3002\u7a7a\u6000\u611f\uff0c\u6709\u659c\u9633\u5904\uff0c\u5374\u6015\u767b\u697c\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u4e0d\u89e3\u8bcd\u4e2d\u6df1\u610f\uff0c\u4f46\u89c9\u541f\u58f0\u51c4\u82e6\uff0c\u6bd4\u521a\u624d\u90a3\u4ed6\u5439\u7684\u8427\u58f0\u8fd8\u8981\u54c0\u4f24\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u65e7\u68a6\u5c18\u5c01\u4f11\u518d\u542f\uff0c\u6b64\u5fc3\u5982\u6c34\u53ea\u4e1c\u6d41\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e09\u56de\u3000\u56db\u91ce\u9f99\u86c7\u541f\u5bc2\u5bde\u3000\u4e5d\u8fb9\u98ce\u96ea\u8def\u79bb\u8ff7 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e09\u5341\u4e09\u56de\u3000\u56db\u91ce\u9f99\u86c7\u541f\u5bc2\u5bde\u3000\u4e5d\u8fb9\u98ce\u96ea\u8def\u79bb\u8ff7 \u3000\u3000\u516c\u5b59\u71d5\u4e0d\u5fcd\u518d\u542c\u4e0b\u53bb\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5fe7\u80fd\u4f24\u4eba\uff0c\u6211\u4e14\u548c\u4ed6\u5f00\u4e2a\u73a9\u7b11\uff0c\u4e5f\u514d\u5f97\u4ed6\u518d\u82e6 \u541f\u3002\u201d\u5f53\u4e0b\u5077\u5077\u5730\u6293\u8d77\u4e00\u628a\u6c99\u5b50\uff0c\u5411\u5389\u5357\u661f\u4e00\u6d12\u3002 \u3000\u3000\u4ee5\u5389\u5357\u661f\u7684\u672c\u9886\uff0c\u6709\u4eba\u8eb2\u5728\u9644\u8fd1\uff0c\u5411\u4ed6\u5077\u88ad\uff0c\u4ed6\u672c\u6765\u662f\u5e94\u8be5\u77e5\u9053\u7684\u3002\u4f46\u6b64\u9645\u4ed6\u4e00\u6765\u662f\u56e0 \u4e3a\u6ee1\u8154\u5fc3\u4e8b\uff0c\u6c89\u6d78\u5728\u81ea\u5df1\u7684\u54c0\u601d\u4e4b\u4e2d\uff1b\u4e8c\u6765\u8fd9\u662f\u5728\u4e49\u519b\u57fa\u5730\u7684\u8179\u5fc3\uff0c\u4ed6\u6839\u672c\u5c31\u65e0\u9632\u8303\u654c\u4eba\u5077 \u88ad\u4e4b\u610f\u3002\u662f\u4ee5\u76f4\u5230\u516c\u5b59\u71d5\u51fa\u624b\u4e86\uff0c\u4ed6\u65b9\u624d\u77e5\u9053\u6709\u4eba\u3002 \u3000\u3000\u516c\u5b59\u71d5\u7684\u4e00\u628a\u6c99\u5b50\u6b63\u6253\u7740\u4ed6\u7684\u201c\u7b11\u8170\u7a74\u201d\uff0c\u5389\u5357\u661f\u4e0d\u7981\u201c\u563b\u201d\u7684\u4e00\u58f0\u7b11\u4e86\u51fa\u6765\u3002\u6253\u7740 \u201c\u7b11\u8170\u7a74\u201d\u672c\u6765\u4f1a\u7b11\u4e2a\u4e0d\u505c\uff0c\u76f4\u5230\u6c14\u7edd\u7684\u3002\u4f46\u56e0\u516c\u5b59\u71d5\u6d12\u7684\u53ea\u662f\u4e00\u628a\u6c99\u5b50\uff0c\u7528\u7684\u5e76\u975e\u91cd\u624b \u6cd5\uff0c\u5389\u5357\u661f\u7684\u529f\u529b\u53c8\u5728\u5979\u4e4b\u4e0a\uff0c\u6545\u6b64\u53ea\u662f\u7b11\u4e86\u4e00\u58f0\uff0c\u7a74\u9053\u4fbf\u5df2\u7ed9\u4ed6\u8fd0\u6c14\u51b2\u5f00\uff0c\u6ca1\u6709\u518d\u7b11\u4e0b \u53bb\u3002 \u3000\u3000\u516c\u5b59\u71d5\u563b\u563b\u54c8\u54c8\u5730\u8df3\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e0b\u5b50\u4f60\u53ef\u7ed9\u6211\u9017\u5f97\u4e50\u4e86\u3002\u201d \u3000\u3000\u5389\u6d77\u661f\u7ed9\u5979\u5f04\u5f97\u557c\u7b11\u7686\u975e\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u8fd9\u4e2b\u5934\u6363\u7684\u9b3c\uff0c\u4f60\u4e5f\u771f\u662f\u592a\u987d\u76ae\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u559c\u6b22\u542c\u8fd9\u6837\u51c4\u51c9\u7684\u66f2\u5b50\uff0c\u4f60\u7ed9\u6211\u5531\u4e00\u9996\u597d\u542c\u7684\u6109\u5feb\u66f2\u5b50\u597d\u4e48\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u53ef\u4e0d\u4f1a\u5531\u597d\u542c\u7684\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u90a3\u4e48\u6211\u7ed9\u4f60\u5531\u4e00\u9996\u5982\u4f55\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u4e0d\u60f3\u62c2\u5979\u7684\u5174\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\u3002\u4f60\u5c31\u5531\u5427\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u8f7b\u542f\u6731\u5507\uff0c\u7b11\u541f\u541f\u5730\u5531\u9053\uff1a\u201c\u5c11\u5e74\u4e0d\u8bc6\u6101\u6ecb\u5473\uff0c\u7231\u4e0a\u5c42\u697c\uff0c\u7231\u4e0a\u5c42\u697c\uff0c\u6b32\u8d4b\u65b0\u8bcd \u5f3a\u8bf4\u6101\u3002\u800c\u4eca\u8bc6\u5f97\u6101\u6ecb\u5473\uff0c\u6b32\u8bf4\u8fd8\u4f53\uff0c\u6b32\u8bf4\u8fd8\u4f11\uff0c\u5370\u9053\u5929\u51c9\u597d\u4e2a\u79cb\u3002\u201d \u3000\u3000\u8fd9\u9996\u8bcd\u662f\u53d6\u7b11\u90a3\u4e9b\u65e0\u75c5\u547b\u541f\u7684\u5c11\u5e74\u4eba\u7684\uff0c\u5389\u5357\u661f\u5fc3\u60f3\uff1a\u201c\u6211\u7684\u5fc3\u4e8b\u4f60\u5c82\u80fd\u77e5\uff1f\u201d\u82e6\u7b11 \u9053\uff0c\u201c\u516c\u5b59\u59d1\u5a18\uff0c\u6211\u771f\u7fa1\u6155\u4f60\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u7fa1\u52df\u6211\u4e48\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u7fa1\u8c6a\u4f60\u662f\u4e2a\u4e0d\u8bc6\u5fe7\u4e0d\u8bc6\u6101\u7684\u5c0f\u59d1\u5a18\u3002\u201d \u3000\u3000\u516c\u5251\u71d5\u9053\uff1a\u201c\u4f60\u83ab\u501a\u8001\u5356\u8001\uff0c\u4f60\u4e5f\u957f\u4e0d\u4e86\u6211\u51e0\u5c81\u3002\u6211\u4eca\u5e74\u5341\u4e5d\u5c81\u4e86\uff0c\u65e9\u5df2\u4e0d\u662f\u5c0f\u59d1\u5a18 \u5566\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u7b11\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u7b97\u662f\u5c0f\u5927\u59d0\u5427\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u6485\u7740\u5c0f\u5634\u513f\u9053\uff1a\u201c\u54b1\u4eec\u522b\u6597\u5634\u4e86\uff0c\u8bf4\u6b63\u7ecf\u8bdd\u513f\uff0c\u4f60\u8bf4\u6211\u4e0d\u8bc6\u5fe7\u6101\uff0c\u90a3\u4e48\u4f60\u53c8\u6709\u4ec0 \u4e48\u5fe7\u6101\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u6709\u5fe7\u6101\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4f60\u9a97\u4e0d\u8fc7\u6211\u7684\uff0c\u4f60\u4e00\u8def\u4e0a\u95f7\u95f7\u4e0d\u4e50\uff0c\u8c01\u8fd8\u770b\u4e0d\u51fa\u6765\uff1f\u4f60\u6bcf\u6b21\u5439\u8427\uff0c\u53c8\u603b\u662f \u559c\u6b22\u5439\u90a3\u6837\u60b2\u4f24\u7684\u66f2\u8c03\u3002\u201d \u3000\u3000\u5386\u5357\u8c61\u5fc3\u60f3\u3002\u201d\u60f3\u4e0d\u5230\u8fd9\u4e2a\u5c0f\u59d0\u5012\u662f\u5f88\u5173\u5fc3\u6211\u3002\u201d\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u7684\u4e8b\u60c5\u5fe7 \u6101\u3002\u4e0d\u8fc7\u4e16\u754c\u4e0a\u603b\u662f\u6709\u4e24\u7c7b\u4eba\u7684\uff0c\u4e00\u79cd\u4eba\u50cf\u4f60\u5bf9\u4e00\u5207\u90fd\u611f\u4e50\u89c2\uff0c\u4e00\u7c7b\u4eba\u50cf\u6211\uff0c\u60b2\u89c2\u7684\u65f6\u5019 \u591a\uff0c\u4e50\u89c2\u7684\u65f6\u5019\u5c11\uff0c\u8fd9\u5927\u6982\u51fa\u4e8e\u6027\u60c5\u7684\u5173\u7cfb\u5427\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\u3002\u201d\u4e2a\u4eba\u7684\u6027\u60c5\u4e5f\u4e2a\u89c1\u5f97\u5c31\u662f\u672c\u6765\u4e0d\u53d8\u7684\uff1f\u4f60\u4e3a\u4ec0 \u4e48\u8001\u662f\u95f7\u95f7\u4e0d\u4e50\uff0c\u5176\u4e2d\u4e00\u5b9a\u53e6\u6709\u539f\u56e0\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u90a3\u4e5f\u8bb8\u56e0\u4e3a\u6211\u7684\u51fa\u8eab\u73af\u5883\u548c\u4f60\u4e0d\u540c\u5427\u3002\u6211\u662f\u5728\u6d77\u5916\u7684\u5b64\u5c9b\u957f\u5927\u7684\uff0c\u81ea\u5c0f\u6ca1 \u6709\u670b\u53cb\uff0c\u6240\u4ee5\u517b\u6210\u4e86\u6bd4\u8f83\u5b64\u50fb\u7684\u6027\u60c5\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u4f46\u5728\u6211\u770b\u6765\uff0c\u4f60\u5916\u8868\u79c0\u51b7\uff0c\u5fc3\u80a0\u5230\u662f\u5f88\u70ed\u3002\u6211\u542c\u7239\u7239\u8bf4\u8fc7\u4f60\u548c\u91d1\u9010\u6d41\u5927 \u95f9\u4eac\u5e05\u4e4b\u4e8b\uff0c\u7a81\u56f4\u4e4b\u65f6\uff0c\u4f60\u4eec\u90fd\u662f\u4e0d\u987e\u5371\u9669\u4e3a\u5bf9\u65b9\u63a9\u62a4\u3002\u8fd9\u6837\u7684\u53cb\u8c0a\u5c31\u5f88\u4ee4\u4eba\u611f\u52a8\u3002\u5c01\u59d0\u59d0 \u548c\u4f60\u7d20\u4e0d\u76f8\u8bc6\uff0c\u53ea\u56e0\u4e3a\u5979\u662f\u91d1\u9010\u6d41\u7684\u670b\u53cb\u7684\u670b\u53cb\uff0c\u4f60\u4e5f\u7ed9\u4e86\u5979\u6781\u5927\u7684\u5e2e\u5fd9\u3002\u6240\u4ee5\u6211\u6562\u65ad\u5b9a\u4f60 \u662f\u4e2a\u6781\u5176\u5fc3\u70ed\u7684\u4eba\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u7b11\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u4f60\u5012\u597d\u50cf\u6bd4\u6211\u66f4\u6e05\u695a\u6211\u81ea\u5df1\u5462\uff01\u201d\u5fc3\u60f3\uff1a\u201c\u6211\u53ea\u9053\u5979\u662f\u4e2a\u4e0d\u61c2 \u4e8b\u7684\u5c0f\u59ae\u5b50\uff0c\u5374\u539f\u6765\u5979\u5f88\u633a\u61c2\u89c2\u5bdf\u4eba\uff0c\u5012\u53ef\u4ee5\u7b97\u5f97\u662f\u6211\u7684\u77e5\u5df1\u5462\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u63a5\u7740\u8bf4\u9053\uff0c\u201c\u5389\u5927\u54e5\uff0c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u670b\u53cb\u662f\u5f88\u7d27\u8981\u7684\u3002\u4f60\u82e5\u6709\u4ec0\u4e48\u4e0d\u5982\u610f\u7684\u4e8b \u60c5\uff0c\u627e\u4e00\u4e2a\u670b\u53cb\u8c08\u8c08\uff0c\u603b\u6bd4\u95f7\u5728\u5fc3\u91cc\u597d\u4e9b\u3002\u53ea\u6050\u6015\u5728\u4f60\u7684\u5fc3\u76ee\u4e4b\u4e2d\uff0c\u6211\u8fd8\u591f\u4e0d\u4e0a\u505a\u4f60\u7684\u53ef\u4ee5 \u8c08\u5fc3\u7684\u670b\u53cb\u5427\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u4e48\u8bf4\uff0c\u516c\u5b59\u59d1\u5a18\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u516c\u5b59\u71d5\u62ac\u8d77\u5934\u6765\u671d\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u6211\u5f88\u611f\u6fc0\u4f60\u7684\u597d\u610f\u3002\u4e0d\u9519\uff0c\u6211\u662f\u6709\u4e9b\u70e6\u607c\uff0c\u4e0d\u8fc7\u8fd9\u70e6\u607c\u662f\u6211\u81ea\u5df1\u627e\u6765\u53d7 \u7684\uff0c\u8c01\u4eba\u65e0\u5fe7\u3002\u6211\u76f8\u4fe1\uff0c\u8fd9\u70e6\u607c\u6162\u6162\u4e5f\u5c31\u4f1a\u8fc7\u4e86\u7684\u3002\u5c06\u6765\uff0c\u5c06\u6765\u6211\u518d\u544a\u8bc9\u4f60\u5427\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4f60\u73b0\u5728\u4e0d\u613f\u610f\u544a\u8bc9\u6211\uff0c\u6211\u4e5f\u4e0d\u52c9\u5f3a\u4f60\uff0c\u4e5f\u613f\u4f60\u7684\u70e6\u607c\u65e9\u70b9\u8fc7\u4e86\u5c31\u597d\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u566b\uff0c\u597d\u50cf\u6709\u4eba\u53eb\u6211\u3002\u591c\u5df2\u6df1\u4e86\uff0c\u4f60\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u4fa7\u8033\u7ec6\u542c\uff0c\u679c\u7136\u542c\u5f97\u597d\u50cf\u662f\u6cf0\u5143\u6d69\u7684\u58f0\u97f3\u5728\u53eb\u7740\u5389\u5357\u661f\u3002\u5979\u867d\u7136\u4e00\u5411\u5929\u771f\u70c2\u6f2b\uff0c \u4e0d\u907f\u7537\u5973\u4e4b\u8d5a\uff0c\u4eec\u6b64\u9645\u60c5\u7aa6\u521d\u5f00\uff0c\u5374\u4e5f\u6709\u70b9\u5bb3\u6015\u7ed9\u79e6\u5143\u6d69\u78b0\u4e0a\u4e86\u96be\u4ee5\u4e3a\u60c5\u3002\u4e8e\u662f\u8bf4\u9053\uff1a \u201c\u597d\uff0c\u90a3\u4e48\u6211\u5148\u56de\u53bb\u4e86\uff0c\u4f60\u597d\u597d\u7761\u4e00\u89c9\uff0c\u660e\u5929\u6211\u518d\u627e\u4f60\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u8d70\u540e\uff0c\u5389\u5357\u661f\u5446\u4e86\u4e00\u4f1a\uff0c\u770b\u4e86\u770b\u5929\u8fb9\u7684\u5317\u6597\u661f\uff0c\u5fc3\u4e2d\u9ed8\u5ff5\uff1a\u201c\u66fe\u7ecf\u6ca7\u6d77\u96be\u4e3a\u6c34\uff0c \u9664\u5374\u5deb\u5c71\u4e0d\u662f\u4e91\u3002\u201d\u4e0d\u77e5\u4e0d\u89c9\uff0c\u773c\u89d2\u6709\u4e86\u6676\u83b9\u7684\u6cea\u73e0\u3002 \u3000\u3000\u79e6\u5143\u6d69\u53eb\u9053\uff1a\u201c\u5389\u5927\u54e5\uff01\u201d\u811a\u6b65\u58f0\u6765\u5f97\u8fd1\u4e86\u3002\u5389\u5357\u661f\u62b9\u5e72\u773c\u6cea\uff0c\u5e94\u9053\uff1a\u201c\u6211\u5728\u8fd9\u513f\u3002\u201d 55 \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e00\u4e2a\u4eba\u8eb2\u5728\u8fd9\u91cc\u5439\u8427\uff0c\u627e\u5f97\u6211\u597d\u82e6\u3002\u5feb\u56de\u53bb\uff0c\u674e\u8302\u4ed6\u4eec\u6765\u4e86\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u53c8\u60ca\u53c8\u559c\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u662f\u626c\u5345\u4e10\u5e2e\u5206\u8235\u7684\u674e\u8235\u4e3b\u4e48\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u6b63\u662f\u6211\u548c\u8bb8\u591a\u4e10\u5e2e\u7684\u5f1f\u5144\u90fd\u6765\u4e86\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u91d1\u9010\u6d41\u5462\uff1f\u4ed6\u6765\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u5c31\u53ea\u662f\u91d1\u9010\u6d41\u6ca1\u6765\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u8be7\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u5382 \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u4e00\u4e2a\u4eba\u5230\u897f\u660c\u53bb\u4e86\u3002\u674e\u8302\u4ed6\u4eec\u4e00\u5230\uff0c\u6211\u5c31\u51fa\u6765\u627e\u4f60\u7684\uff0c\u4f60\u6b32\u77e5\u8be6 \u60c5\uff0c\u8fd8\u662f\u56de\u53bb\u95ee\u674e\u8302\u5427\u3002\u201d \u3000\u3000\u4e14\u8bf4\u516c\u5b59\u71d5\u56de\u8f6c\u5973\u8425\u4e4b\u540e\uff0c\u8fd9\u4e00\u665a\u8eba\u5728\u5e8a\u4e0a\uff0c\u5fc3\u6ce2\u8361\u6f3e\uff0c\u8f97\u8f6c\u53cd\u4fa7\uff0c\u597d\u4e0d\u5bb9\u6613\u65b9\u624d\u7761 \u7740\u3002\u4e00\u89c9\u9192\u6765\uff0c\u4fbf\u542c\u5f97\u5230\u5999\u5ae6\u53eb\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\uff0c\u5feb\u5feb\u8d77\u5e8a\u3002\u6211\u548c\u4f60\u53bb\u89c1\u4e00\u4f4d\u65b0\u6765\u7684\u59d0\u59d0\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u8df3\u8d77\u8eab\u6765\uff0c\u95ee\u9053\uff1a\u201c\u662f\u90a3\u4e00\u4f4d\u65b0\u6765\u7684\u59d0\u59d0\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u8fd9\u4f4d\u59d0\u59d0\u540d\u53eb\u77f3\u971e\u59d1\uff0c\u5979\u662f\u9648\u5149\u7167\u7684\u672a\u5a5a\u59bb\u3002\u9648\u5149\u7167\u4e5f\u662f\u91d1\u9010\u6d41\u7684\u597d\u670b \u53cb\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u77f3\u971e\u59d1\u548c\u9648\u5149\u7167\u6765\u4e86\uff01\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u4eec\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u6211\u542c\u7239\u7239\u8bf4\u8fc7\u4ed6\u4eec\u7684\u540d\u5b57\u3002\u7239\u7239\u8bf4\u4ed6\u4eec\u4e0d\u6127\u662f\u6c5f\u6e56\u4e0a\u7684\u540e\u8d77\u4e4b\u79c0\u3002\u9648\u5149\u7167 \u662f\u82cf\u5dde\u9648\u5927\u4fa0\u9648\u5927\u5b57\u7684\u513f\u5b50\u3002\u77f3\u971e\u59d1\u7684\u6765\u5386\u6211\u7239\u7239\u5219\u4e0d\u751a\u6e05\u695a\uff0c\u53ea\u77e5\u9053\u5979\u64c5\u4e8e\u4f7f\u6bd2\uff0c\u731c\u6d4b\u53ef \u80fd\u662f\u5929\u9b54\u6559\u7684\u4f20\u4eba\u3002\u4e0d\u8fc7\u6211\u7239\u7239\u4e5f\u8bf4\uff0c\u5373\u4f7f\u5979\u771f\u7684\u662f\u51fa\u8eab\u90aa\u6559\uff0c\u4f46\u4ee5\u5979\u5728\u6c5f\u6e56\u4e0a\u7684\u884c\u4e8b\u800c \u8bba\uff0c\u4e5f\u7b97\u5f97\u662f\u4e2a\u6b63\u6d3e\u7684\u5973\u4fa0\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u6bd4\u6211\u8fd8\u66f4\u6e05\u695a\u4ed6\u4eec\uff0c\u8fd9\u5c31\u597d\u4e86\uff0c\u7afa\u59d0\u59d0\u5b89\u6392\u5979\u6765\u548c\u4f60\u540c\u4f4f\uff0c\u4f60\u4eec \u5f88\u5feb\u5c31\u53ef\u4ee5\u76f8\u719f\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4ed6\u4eec\u600e\u4e48\u6765\u7684\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u548c\u626c\u5dde\u4e10\u5e2e\u7684\u674e\u8302\u4e00\u540c\u6765\u7684\u3002\u6628\u665a\u4e09\u66f4\u8fc7\u540e\u624d\u5230\uff0c\u542c\u8bf4\u548c\u7afa\u4f2f\u4f2f\u3001 \u5389\u5927\u54e5\u4ed6\u4eec\u4e00\u76f4\u8c08\u5230\u5929\u4eae\u6ca1\u6709\u7761\u89c9\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u5fc3\u5934\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u626c\u5dde\u7684\u674e\u8235\u4e3b\uff1f\u55ef\uff0c\u6211\u8bb0\u5f97\u5389\u5927\u54e5\u4f3c\u4e4e\u8bf4\u8fc7\uff0c\u4ed6\u7684\u4e49\u5f1f\u91d1\u9010 \u6d41\u5c31\u662f\u548c\u626c\u5dde\u7684\u4e10\u5e2e\u540c\u5728\u4e00\u8d77\u7684\u3002\u91d1\u9010\u6d41\u53ef\u6765\u4e86\u4e48\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u91d1\u9010\u6d41\u82e5\u7136\u6765\u4e86\uff0c\u5389\u5927\u54e5\u6709 \u4e2a\u77e5\u5df1\u503e\u8c08\uff0c\u5c31\u4e0d\u81f3\u4e8e\u90a3\u4e48\u70e6\u95f7\u4e86\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u6765\u4e86\uff0c\u6765\u4e86\uff01\u201d\u516c\u5b59\u71d5\u8be7\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u91d1\u9010\u6d41\u4f1a\u5230\u54b1\u4eec\u7684\u5973\u8425\u6765\uff1f\u201d\u5c01\u5999 \u5ae6\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u7684\u4e0d\u662f\u91d1\u9010\u6d41\u3002\u662f\u77f3\u59d0\u59d0\u548c\u7afa\u59d0\u59d0\u5df2\u7ecf\u6765\u4e86\u3002\u201d\u539f\u6765\u516c\u5b59\u71d5\u5bf9\u955c\u68b3\u5934\uff0c \u672a\u77a7\u89c1\u7afa\u6e05\u534e\u548c\u77f3\u971e\u59d1\u4ece\u9662\u5b50\u8fdb\u6765\u3002 \u3000\u3000\u516c\u5b59\u71d5\u548c\u77f3\u971e\u59d1\u89c1\u4e86\u9762\u540e\uff0c\u6b63\u5f85\u95ee\u5979\uff0c\u7afa\u6e05\u534e\u5df1\u5728\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u6240\u6302\u5ff5\u7684\u91d1\u9010\u6d41\u6ca1\u6709 \u6765\uff0c\u54b1\u4eec\u8fd9\u91cc\u6709\u4e00\u4e2a\u4eba\u5374\u4e3a\u4ed6\u8d70\u4e86\u3002\u201d\u516c\u5b59\u71d5\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u7afa\u6e05\u534e\u9053\uff1a\u201c\u662f\u5389\u5357\u661f\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5389\u5927\u54e5\u8d70\u4e86\uff1f\u4ed6\u4e0a\u54ea\u513f\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u7b11\u9053\uff1a\u201c\u4f60\u522b\u5fc3\u6025\uff0c\u77f3\u59d0\u59d0\u4f1a\u544a\u8bc9\u4f60\u7684\u3002\u201d \u3000\u3000\u77f3\u971e\u59d1\u9053\uff1a\u201c\u662f\u8fd9\u6837\u7684\uff1a\u6211\u4eec\u8def\u8fc7\u6caa\u5dde\u7684\u65f6\u5019\uff0c\u4ece\u4e10\u5e2e\u5206\u8235\u542c\u5230\u4e00\u4e2a\u786e\u5b9e\u7684\u6d88\u606f\uff0c\u8bf4\u662f \u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\u53f2\u767d\u90fd\u5144\u59b9\uff0c\u548c\u4ed6\u624b\u4e0b\u7684\u56db\u5927\u9999\u4e3b\uff0c\u8d70\u53e6\u4e00\u6761\u8def\uff0c\u8d76\u5728\u6211\u4eec\u7684\u524d\u5934\uff0c\u5df2\u7ecf\u8fdb\u5e7f \u897f\u660c\u4e86\u3002\u636e\u8bf4\u53f2\u767d\u90fd\u662f\u8981\u628a\u4ed6\u7684\u59b9\u59b9\u5ac1\u7ed9\u897f\u660c\u5c06\u519b\u5e05\u5b5f\u96c4\u3002\u91d1\u9010\u6d41\u542c\u5230\u4e86\u8fd9\u4e2a\u6d88\u606f\uff0c\u5f88\u662f\u7740 \u6025\u3002\u672c\u6765\u6211\u4eec\u662f\u51c6\u5907\u7ed5\u8fc7\u897f\u660c\uff0c\u5f84\u76f4\u6765\u8fd9\u91cc\u7684\uff0c\u91d1\u9010\u6d41\u77e5\u9053\u6b64\u4e8b\u4e4b\u540e\uff0c\u5c31\u4e00\u4e2a\u4eba\u8dd1\u53bb\u897f\u660c \u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u4e48\u90a3\u6837\u7740\u6025\uff1f\u201d \u3000\u3000\u77f3\u971e\u59d1\u7b11\u9053\uff1a\u201c\u53f2\u767d\u90fd\u7684\u59b9\u59b9\u548c\u4ed6\u54e5\u54e5\u5e76\u4e0d\u4e00\u6837\uff0c\u5979\u662f\u4e2a\u624d\u8c8c\u53cc\u5168\u7684\u4fa0\u5973\uff0c\u542c\u8bf4\u91d1\u9010\u6d41 \u548c\u5979\u7684\u4ea4\u60c5\u5f88\u597d\uff0c\u5f88\u53ef\u80fd\u4e24\u4eba\u5df1\u662f\u79c1\u8ba2\u9e33\u76df\u7684\u4e86\uff0c\u4e0d\u8fc7\u91d1\u9010\u6d41\u4e0d\u80af\u627f\u8ba4\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u4f46\u91d1\u9010\u6d41\u4e00\u4e2a\u4eba\u8dd1\u53bb\u897f\u660c\uff0c\u4e0d\u662f\u5f88\u5371\u9669\u5417\uff1f\u201d \u3000\u3000\u77f3\u971e\u59d1\u9053\uff1a\u201c\u662f\u5440\uff01\u6211\u548c\u5149\u7167\u672c\u6765\u8981\u8ddf\u4ed6\u53bb\u7684\uff0c\u53ef\u662f\u4ed6\u4e0d\u7b54\u5e94\u3002\u4e5f\u8bb8\u4ed6\u662f\u56e0\u4e3a\u6211\u4eec\u672c\u9886 \u4f4e\u5fae\uff0c\u5e2e\u4e0d\u4e0a\u4ed6\u7684\u5fd9\u5427\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u77f3\u59d0\u59d0\u592a\u5ba2\u6c14\u4e86\u3002\u4e0d\u8fc7\u91d1\u5c11\u4fa0\u7684\u4e3a\u4eba\u6211\u5374\u7565\u6709\u6240\u77e5\uff0c\u4ed6\u867d\u7136\u653e\u8361\u4e0d\u7f81\uff0c\u5bf9 \u670b\u53cb\u53ef\u662f\u5341\u5206\u597d\u7684\uff0c\u6709\u5371\u9669\u7684\u5730\u65b9\uff0c\u4ed6\u4e00\u5b9a\u662f\u72ec\u53bb\u72ec\u6765\uff0c\u4e0d\u613f\u610f\u8fde\u7d2f\u670b\u53cb\u3002\u201d\u5c01\u5999\u5ae6\u56e0\u4e3a\u91d1 \u9010\u6d41\u66ff\u5979\u64ae\u5408\u5a5a\u4e8b\uff0c\u662f\u4ee5\u5bf9\u4ed6\u6781\u6709\u597d\u611f\u3002 \u3000\u3000\u77f3\u971e\u59d1\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u6211\u4e5f\u66fe\u5f97\u8fc7\u4ed6\u4e0d\u5c11\u5e2e\u5fd9\u3002\u201d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6628\u5929\u6211\u4eec\u5c31\u662f\u548c\u7afa\u8001 \u524d\u8f88\u5546\u91cf\uff0c\u5982\u4f55\u53bb\u63a5\u5e94\u91d1\u9010\u6d41\uff0c\u897f\u660c\u6709\u6e05\u5ef7\u7684\u6570\u4e07\u5927\u519b\uff0c\u800c\u4e14\u9ad8\u624b\u4e91\u96c6\uff0c\u7afa\u8001\u524d\u8f88\u4e0d\u80fd\u4e3a\u4e86 \u4e00\u4e2a\u4eba\u5174\u5e08\u52a8\u4f17\uff0c\u7b49\u95f2\u4e4b\u8f88\u7236\u51b3\u4e2d\u80fd\u8fdb\u5f97\u897f\u660c\uff0c\u662f\u4ee5\u5389\u5927\u54e5\u81ea\u544a\u594b\u52c7\uff0c\u8981\u4e00\u4e2a\u4eba\u53bb\uff0c\u7afa\u8001\u524d \u8f88\u8d77\u521d\u8fd8\u662f\u4e0d\u80af\u7b54\u5e94\u7684\uff0c\u540e\u6765\u5389\u5927\u54e5\u59cb\u7ec8\u575a\u6301\u8981\u53bb\uff0c\u7afa\u8001\u524d\u8f88\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u7b54\u5e94\u4ed6\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u6c89\u541f\u4e0d\u8bed\uff0c\u5c01\u5999\u5ae6\u77e5\u5979\u5fc3\u610f\uff0c\u7b11\u9053\uff1a\u201c\u71d5\u59d0\uff0c\u7afa\u8001\u524d\u8f88\u4e0d\u4f1a\u8ba9\u4f60\u53bb\u7684\u3002\u201d\u516c\u5b59\u71d5 \u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u8981\u53bb\u5462\u3002\u201d\u5176\u5b9e\u5979\u6b63\u662f\u5728\u8003\u8651\u8981\u5411\u7afa\u5c1a\u7236\u6c42\u60c5\uff0c\u4f46\u5374\u6015\u4eba\u7b11\u8bdd\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u5389\u5927\u54e5\u672c\u9886\u9ad8\u5f3a\uff0c\u53c8\u6709\u7384\u94c1\u5b9d\u5251\uff0c\u4ed6\u548c\u91d1\u9010\u6d41\u8054\u624b\uff0c\u5929\u4e0b\u65e0\u4eba\u80fd\u654c\u3002\u897f\u660c \u9ad8\u624b\u867d\u591a\uff0c\u8c05\u4e5f\u56f0\u4e0d\u4f4f\u4ed6\u4eec\uff0c\u71d5\u59b9\u3001\u4f60\u4e5f\u7528\u4e0d\u7740\u592a\u8fc7\u62c5\u5fc3\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u7ea2\u971e\u6ee1\u8138\uff0c\u5550\u9053\uff1a\u201c\u4e71\u56bc\u820c\u5934\uff0c\u8c01\u62c5\u5fc3\u4ed6\u4e86\uff1f\u4ed6\u53c8\u4e0d\u662f\u6211\u4e00\u4e2a\u4eba\u7684\u5927\u54e5\uff0c\u4f60\u4e5f\u53eb \u4ed6\u5927\u54e5\u7684\u3002\u201d \u3000\u3000\u7afa\u6e05\u534e\u5ffd\u5730\u5509\u55e4\u4e00\u7b11\uff0c\u8bf4\u903c\uff1a\u201c\u71d5\u59b9\uff0c\u4f60\u7792\u5f97\u6211\u597d\u82e6\uff0c\u539f\u6765\u4f60\u60f3\u5360\u6211\u4fbf\u5b9c\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u6014\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u7afa\u6e05\u534e\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u660e\u767d\u4e48\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u7b11\u9053\uff1a\u201c\u71d5\u59d0\uff0c\u5979\u662f\u5992\u5fcc\u4f60\u5e73\u65e5\u6bd4\u5979\u957f\u4e86\u4e00\u8f88\u3002\u201d\u8981\u77e5\u91d1\u9010\u6d41\u662f\u674e\u5149\u590f\u7684\u5e08\u53d4\uff0c \u5389\u5357\u661f\u662f\u91d1\u9010\u6d41\u7684\u4e49\u5144\uff0c\u5018\u82e5\u516c\u5b59\u71d5\u5ac1\u7ed9\u5389\u5357\u661f\u7684\u8bdd\uff0c\u53d9\u8d77\u8f88\u4efd\u6765\uff0c\u674e\u5149\u590f\u548c\u7afa\u6e05\u534e\u8fd9\u5bf9\u5c0f \u592b\u59bb\uff0c\u5f53\u7136\u662f\u8981\u6bd4\u4ed6\u4eec\u77ee\u4e86\u4e00\u8f88\u3002 \u3000\u3000\u516c\u5b59\u71d5\u604d\u7136\u5927\u609f\uff0c\u5a07\u55d4\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u4f60\u4eec\u60f3\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u770b\u6211\u4e0d\u6495\u7834\u4f60\u4eec\u7684\u5c0f \u5634\uff01\u201d\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u522b\u95f9\uff0c\u522b\u95f9\uff0c\u77f3\u59d0\u59d0\u65b0\u6765\u4e4d\u5230\uff0c\u4f60\u600e\u80fd\u4e0d\u62db\u5f85\u5ba2\u4eba\uff1f\u201d \u3000\u3000\u51e0\u4e2a\u5e74\u9f84\u76f8\u82e5\u7684\u5c11\u5973\u563b\u563b\u54c8\u54c8\u95f9\u4e86\u4e00\u573a\uff0c\u53ef\u662f\u516c\u5b59\u71d5\u5c3d\u7ba1\u548c\u5979\u4eec\u563b\u563b\u54c8\u54c8\uff0c\u5fc3\u4e2d\u7684\u6101\u95f7 \u5374\u662f\u96be\u89e3\u3002\u5389\u5357\u661f\u5b64\u8eab\u72af\u9669\uff0c\u6f5c\u5f80\u897f\u660c\uff0c\u5c01\u5999\u5ae6\u867d\u7136\u767e\u822c\u529d\u6170\uff0c\u53eb\u5979\u653e\u5fc3\uff0c\u5979\u53c8\u600e\u80fd\u653e\u5fc3\u5f97 \u4e0b\uff1f \u3000\u3000\u8fd9\u665a\u516c\u5b59\u71d5\u8f97\u8f6c\u53cd\u4fa7\uff0c\u4e0d\u80fd\u5165\u7720\uff0c\u84e6\u5730\u60f3\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u6211\u8fd9\u6837\u7275\u6302\u7740\u4ed6\uff0c\u83ab\u975e\u6211\u5f53\u771f\u662f \u559c\u6b22\u4e0a\u5389\u5927\u54e5\u4e86\uff1f\u201d\u9661\u7136\u95f4\u53d1\u73b0\u4e86\u5fc3\u5e95\u7684\u79d8\u5bc6\uff0c\u4e0d\u7981\u9762\u7ea2\u8033\u70ed\u3002 \u3000\u3000\u4f46\u516c\u5b59\u71d5\u662f\u4e2a\u6562\u7231\u6562\u6068\u7684\u5973\u5b50\uff0c\u968f\u5373\u60f3\u9053\uff1a\u201c\u7537\u5973\u76f8\u60a6\uff0c\u4eba\u4e4b\u5e38\u60c5\uff0c\u6211\u5c31\u662f\u559c\u6b22\u5389\u5927 \u54e5\uff0c\u90a3\u4e5f\u4e0d\u662f\u4ec0\u4e48\u53ef\u7f9e\u4e4b\u4e8b\uff0c\u4f46\u5927\u54e5\u53ef\u4ee5\u4e3a\u4e86\u597d\u670b\u53cb\u5192\u9669\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u4e3a\u4e86\u4ed6\u5192\u9669\uff1f\u6211\u6c42 \u7afa\u8001\u524d\u8f88\u8bb8\u6211\u524d\u5f80\u897f\u660c\uff0c\u6599\u96be\u5141\u51c6\uff0c\u6211\u7d22\u4ef6\u7792\u7740\u4ed6\u4eec\uff0c\u81ea\u5df1\u53bb\u5427\uff0c\u8981\u7b11\u8bdd\u4efb\u4ed6\u4eec\u7b11\u8bdd\u597d\u4e86\u3002 \u5bf9\u5c31\u662f\u8fd9\u6837\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u60f3\u5230\u5c31\u505a\uff0c\u5f53\u4e0b\u91cd\u65bd\u5979\u5bf9\u4ed8\u5c01\u5999\u5ae6\u7684\u6545\u6280\uff0c\u70b9\u4e86\u77f3\u971e\u59d1\u7684\u660f\u7761\u7a74\uff0c\u7a7f\u7a97\u800c\u51fa\uff0c\u6084 \u6084\u4e0b\u5c71\u3002 \u3000\u3000\u516c\u5b59\u71d5\u5306\u5306\u5fd9\u5fd9\u7684\u51fa\u8d70\uff0c\u6ca1\u6709\u643a\u5e26\u5e72\u7cae\u3002\u5979\u65bd\u5c55\u8f7b\u529f\uff0c\u8dd1\u5230\u4e86\u5929\u4eae\u4e4b\u540e\uff0c\u4e0d\u89c9\u611f\u5230\u6709\u70b9 \u9965\u997f\u3002\u8352\u5c71\u91ce\u5cad\u4e4b\u4e2d\uff0c\u627e\u4e0d\u5230\u4eba\u5bb6\uff0c\u516c\u5b59\u71d5\u53ea\u597d\u8dd1\u5230\u6c9f\u6797\u91cc\u627e\u91ce\u751f\u7684\u679c\u5b50\u5403\u3002 \u3000\u3000\u65f6\u5e8f\u867d\u5c5e\u6df1\u79cb\uff0c\u672a\u4ea4\u51ac\u5929\uff0c\u4f46\u897f\u5317\u9ad8\u539f\u7684\u6c14\u5019\u5df2\u6bd4\u6c5f\u5357\u7684\u51ac\u5929\u8fd8\u8981\u5bd2\u51b7\u3002\u516c\u5b59\u71d5\u627e\u4e86\u534a \u5929\uff0c\u4e5f\u627e\u4e0d\u5230\u4e00\u4e2a\u53ef\u4ee5\u5403\u7684\u679c\u5b50\uff0c\u8fde\u91ce\u5154\u9e7f\u7350\u4e4b\u7c7b\u7684\u5c0f\u91ce\u517d\u4e5f\u6ca1\u78b0\u4e0a\u4e00\u53ea\u3002\u516c\u5b59\u71d5\u53f9\u4e86\u53e3 \u6c14\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u53ea\u597d\u997f\u7740\u809a\u76ae\u518d\u8dd1\u4e00\u7a0b\u4e86\u3002\u201d \u3000\u3000\u521a\u8981\u8d70\u51fa\u6797\u5b50\uff0c\u5929\u6c14\u5ffd\u7136\u53d8\u574f\uff0c\u98d8\u4e0b\u9e45\u6bdb\u822c\u7684\u96ea\u82b1\u3002\u516c\u5b59\u71d5\u6b63\u81ea\u6c14\u95f7\uff0c\u5ffd\u542c\u5f97\u4f3c\u6709\u8f66\u9a6c \u7684\u58f0\u97f3\u3002\u516c\u5b59\u71d5\u559c\u51fa\u671b\u5916\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6709\u4eba\u6765\u4e86\uff0c\u90a3\u5c31\u597d\u4e86\u3002\u597d\u574f\u53ef\u4ee5\u8ba8\u70b9\u98df\u7269\u3002\u201d\u5fc3\u5ff5 \u672a\u5df2\uff0c\u5ffd\u53c8\u542c\u5f97\u547c\u559d\u7684\u58f0\u97f3\u3002 \u3000\u3000\u516c\u5b59\u71d5\u722c\u4e0a\u4e00\u68f5\u5927\u6811\u4e0a\uff0c\u9ad8\u4e34\u4e34\u4e0b\uff0c\u671b\u51fa\u6797\u5916\u3002\u53ea\u89c1\u6709\u4e24\u4e2a\u519b\u5b98\u6a21\u6837\u7684\u4eba\uff0c\u6b63\u5728\u62e6\u7740\u4e00 \u8f86\u655e\u7bf7\u7684\u9aa1\u8f66\u76d8\u95ee\u3002\u8f66\u4e0a\u6709\u4e03\u516b\u4e2a\u4eba\uff0c\u5176\u4e2d\u53ea\u6709\u4e00\u4e2a\u5e74\u8001\u7684\u7537\u5b50\uff0c\u5176\u4f59\u90fd\u662f\u5973\u5b50\uff0c\u624b\u4e0a\u62ff\u7740 \u5404\u5f0f\u5404\u6837\u7684\u4e50\u5668\uff0c\u4f3c\u4e4e\u662f\u4e00\u961f\u6c5f\u6e56\u5356\u827a\u7684\u827a\u4eba\uff01 \u3000\u3000\u90a3\u4e24\u4e2a\u519b\u5b98\u559d\u9053\uff1a\u201c\u4e0b\u6765\uff0c\u4e0b\u6765\uff01\u4f60\u4eec\u662f\u4e9b\u4ec0\u4e48\u4eba\uff0c\u4ece\u4ec0\u4e48\u5730\u65b9\u6765\uff0c\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\uff1f\u201d \u516c\u5b59\u71d5\u8fdc\u8fdc\u5730\u77a7\u89c1\u8fd9\u4e24\u4e2a\u519b\u5b98\uff0c\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u519b\u5b98\u4e00\u9ad8\u4e00\u77ee\uff0c\u9ad8\u7684\u90a3\u4e2a\u8eab\u6750\u9b41\u68a7\uff0c\u6ee1\u5934\u79c3\u5f97\u6cb9\u5149\u6676\u4eae\uff0c\u77ee\u7684\u90a3\u4e2a\u4e24\u8fb9\u592a\u9633\u7a74\u575f \u8d77\uff0c\u8170\u91cc\u63d2\u7740\u4e00\u5bf9\u5224\u5b98\u7b14\u3002\u516c\u5b59\u71d5\u4ee5\u524d\u867d\u6ca1\u89c1\u8fc7\u8fd9\u4e24\u4e2a\u4eba\uff0c\u4f46\u56e0\u4ed6\u4eec\u7684\u5f02\u76f8\uff0c\u516c\u5b59\u71d5\u4e00\u89c1\uff0c \u5c31\u731c\u5230\u4e86\u4ed6\u4eec\u7684\u6765\u5386\u3002 \u3000\u3000\u516c\u5b59\u71d5\u7684\u7236\u4eb2\u516c\u5b59\u5b8f\u8eab\u4e3a\u7ea2\u7f28\u4f1a\u7684\u603b\u8235\u4e3b\uff0c\u5927\u51e1\u6c5f\u6e56\u4e0a\u6709\u70b9\u6765\u5934\u7684\u4eba\u7269\uff0c\u4e0d\u8bba\u662f\u9ed1\u9053\u767d \u9053\uff0c\u4ed6\u51e0\u4e4e\u65e0\u4e00\u4e0d\u77e5\uff0c\u65e0\u4e00\u4e0d\u6653\u3002\u516c\u5b59\u71d5\u66fe\u542c\u5f97\u7236\u4eb2\u8bf4\u8fc7\uff0c\u5c11\u6797\u5bfa\u6709\u4e2a\u53db\u5f92\u540d\u53eb\u5f6d\u5de8\u5d58\uff0c\u4ee5 \u53ca\u53f7\u79f0\u201c\u5929\u4e0b\u70b9\u7a74\u7b2c\u4e00\u5bb6\u201d\u7684\u9752\u5dde\u8fde\u5bb6\u6709\u4e2a\u5b50\u5f1f\u540d\u53eb\u8fde\u57ce\u864e\uff0c\u8fd9\u4e24\u4e2a\u4eba\u662f\u5f53\u671d\u5978\u76f8\u66f9\u632f\u5858\u7684 \u722a\u7259\uff0c\u4ed7\u7740\u76f8\u5e9c\u52bf\u529b\uff0c\u9887\u662f\u6a2a\u884c\u9738\u9053\u3002\u516c\u5b59\u5b8f\u5c61\u6b21\u60f3\u8981\u94f2\u9664\u4ed6\u4eec\uff0c\u8fd8\u672a\u5f97\u6709\u673a\u4f1a\u3002\u4ed6\u5429\u5490\u5973 \u513f\u5728\u6c5f\u6e56\u4e0a\u5018\u82e5\u78b0\u4e0a\u8fd9\u4e24\u4e2a\u4eba\uff0c\u987b\u5f97\u7559\u5fc3\u3002 \u3000\u3000\u516c\u5b59\u71d5\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u8fd9\u4e24\u4e2a\u4eba\u597d\u6b79\u4e5f\u662f\u6c5f\u6e56\u4e0a\u7684\u6210\u540d\u4eba\u7269\uff0c\u7adf\u7136\u8fde\u5356\u5531\u7684\u5f31 \u5973\u4e5f\u8981\u6b3a\u8d1f\u3002\u8fd9\u4ef6\u4e8b\u7ed9\u6211\u78b0\u4e0a\u4e86\uff0c\u6211\u53ef\u4e0d\u80fd\u4e0d\u7ba1\u3002\u4f46\u7239\u7239\u8bf4\u8fd9\u4e24\u4e2a\u4eba\u7684\u672c\u9886\u90fd\u662f\u5728\u6211\u4e4b\u4e0a\u3002 \u6211\u82e5\u8981\u7ba1\u7684\u8bdd\uff0c\u53ea\u6015\u4e0d\u80fd\u529b\u654c\uff0c\u53ea\u80fd\u667a\u53d6\uff01 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u9aa1\u8f66\u4e0a\u7684\u7537\u5973\uff0c\u90fd\u5df2\u4e0b\u6765\u3002\u90a3\u8001\u8005\u7b54\u9053\uff1a\u201c\u6211\u4eec\u662f\u5ddd\u897f\u7684\u4e50\u5bb6\u73ed\u5b50\uff0c\u5230 \u897f\u660c\u53bb\u7684\u3002\u201d \u3000\u3000\u5f6d\u5de8\u5d58\u9053\uff1a\u201c\u54e6\uff0c\u5230\u897f\u660c\u53bb\u7684\u3002\u4f60\u662f\u73ed\u4e3b\u5417\uff1f\u201d\u8001\u8005\u8eac\u8170\u7b54\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u4e0d\u77e5\u4ed6\u662f\u5426 \u60ca\u60f6\u8fc7\u751a\uff0c\u7b54\u4e86\u8bdd\u8fde\u8fde\u5598\u6c14\u3002\u5f6d\u5de8\u5d58\u5c06\u4ed6\u4e86\u62c9\u8fc7\u4e00\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6b47\u6b47\u5427\u3002\u201d\u62c9\u4ed6\u4e4b \u65f6\uff0c\u6307\u5934\u6697\u6697\u6263\u4e00\u7740\u4ed6\u7684\u8109\u95e8\uff0c\u4e00\u8bd5\u4e4b\u4e0b\uff0c\u4fbf\u77e5\u8fd9\u8001\u8005\u6beb\u65e0\u5185\u529f\u3002\u8fd9\u8001\u8005\u4e5f\u4f3c\u4e4e\u6beb\u4e0d\u77e5\u9053\u5bf9 \u65b9\u53ea\u8981\u6307\u5934\u4e00\u52a8\u4fbf\u53ef\u4ee5\u81f4\u4ed6\u6b7b\u547d\uff0c\u4e56\u4e56\u5730\u7ad9\u8fc7\u4e00\u8fb9\u3002\u5f6d\u5de8\u5d58\u653e\u677e\u4e86\u624b\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f55\u8001\u5927 \u70e7\u4e86\u53d8\u6210\u7070\u6211\u4e5f\u8ba4\u5f97\uff0c\u8fd9\u4eba\u65e2\u4e0d\u61c2\u6b66\u529f\uff0c\u53e3\u97f3\u53c8\u4e0d\u5bf9\uff0c\u51b3\u4e0d\u4f1a\u662f\u4f55\u8001\u5927\u4e86\u3002\u201d \u3000\u3000\u8fde\u57ce\u864e\u53cc\u7738\u70af\u70af\uff0c\u5ffd\u5730\u6307\u7740\u4e00\u4e2a\u5973\u5b50\u95ee\u9053\uff1a\u201c\u5979\u662f\u8c01\uff1f\u201d\u90a3\u8001\u8005\u7b54\u9053\uff1a\u201c\u662f\u6211\u7684\u517b \u5973\u3002\u201d\u8fd9\u5973\u5b50\u624b\u4e0a\u62ff\u7740\u68a8\u82b1\u7b80\uff0c\u8fde\u57ce\u864e\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u4e66\u7684\u4e48\uff1f\u201d\u90a3\u5973\u5b50\u4f4e\u5934\u8bf4\u9053\uff1a\u201c\u5b66\u4e86\u51e0 \u5e74\uff0c\u5531\u5f97\u4e0d\u597d\u3002\u201d\u8fde\u57ce\u864e\u9053\uff1a\u201c\u5531\u4e00\u6bb5\u7ed9\u6211\u542c\u542c\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u4e00\u5f20\u8721\u9ec4\u7684\u8138\u4e0a\u6cdb\u8d77\u7ea2\u6655\uff0c\u62ff\u7740\u68a8\u82b1\u7b80\u7684\u624b\u76f4\u6253\u54c6\u55e6\u3002\u90a3\u8001\u8005\u9053\uff1a\u201c\u4e0d\u8981\u5bb3\u6015\uff0c \u8fd9\u4f4d\u5927\u4eba\u4e0d\u4f1a\u96be\u4e3a\u4f60\u7684\uff0c\u4f60\u5c31\u5531\u4e00\u6bb5\u5427\u3002\u201d \u3000\u3000\u90a3\u5973\u5b50\u98a4\u58f0\u5531\u9053\uff1a\u201c\u90a3\u5f20\u751f\u4e00\u5c01\u4e66\u6562\u4e8e\u9000\u8d3c\u5bc7\uff1b\u90a3\u83ba\u83ba\uff0c\u516b\u884c\u7b3a\u4eba\u7ea6\u9ec4\u660f\u540e\uff0c\u90a3\u7ea2\u5a18\uff0c \u4e09\u5bf8\u820c\u964d\u4f0f\u8001\u592b\u4eba\uff0c\u90a3\u60e0\u660e\uff0c\u4e94\u5343\u5175\u9985\u4f5c\u8089\u9992\u5934\u3002\u6211\u4ee5\u4e3a\u4f60\u4e5f\u80c6\u5982\u6597\uff0c\u5478\uff0c\u539f\u6765\u662f\u4e2a\u94f6\u6837\u8721 \u67aa\u5934\u3002\u8fd9\u662f\u201c\u897f\u53a2\u8bb0\u201d\u5531\u8f9e\u7684\u4e00\u6bb5\uff0c\u867d\u7136\u58f0\u97f3\u6296\u98a4\uff0c\u5531\u6765\u4e5f\u662f\u5a13\u5a13\u52a8\u542c\u3002 \u3000\u3000\u8fde\u3001\u5f6d\u4e8c\u4eba\u4ed4\u7ec6\u542c\u5979\u53e3\u97f3\uff0c\u786e\u662f\u5ddd\u897f\u4e00\u5e26\u7684\u571f\u97f3\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f55\u8001\u5927\u90a3\u5973\u513f\u8bf4\u7684\u662f\u5c71 \u4e1c\u9f13\u4e66\uff0c\u6bd4\u8fd9\u4e2a\u96cc\u513f\u4e5f\u8981\u6f02\u4eae\u5f97\u591a\u3002\u4f46\u8eab\u6750\u4f53\u6001\u5374\u6709\u51e0\u5206\u76f8\u4f3c\u3002\u5979\u4eec\u8fd9\u4e9b\u8d70\u6c5f\u6e56\u7684\u5973\u5b50\u5584\u4e8e \u6539\u5bb9\u6613\u8c8c\uff0c\u987b\u5f97\u4ed4\u7ec6\u4e00\u4e9b\uff0c\u83ab\u7ed9\u5979\u9a97\u8fc7\u4e86\u3002\u201d \u3000\u3000\u8fde\u57ce\u864e\u53cc\u7738\u70af\u70af\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u5730\u6253\u91cf\u4e86\u90a3\u5973\u5b50\u4e00\u756a\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u628a\u4f60\u5934\u4e0a\u8fd9\u652f\u94f6\u7c2a\u7ed9\u6211 \u770b\u770b\uff01\u201d \u3000\u3000\u94f6\u7c2a\u5e76\u4e0d\u7a00\u5947\uff0c\u4f46\u7c2a\u4e0a\u96d5\u5230\u7684\u4e00\u5934\u5f69\u98ce\u5374\u662f\u5177\u4f53\u800c\u5fae\uff0c\u6829\u6829\u5982\u751f\u3002\u94f6\u7c2a\u8fd8\u6ca1\u6709\u5c0f\u6307\u5934 \u7c97\uff0c\u8fde\u7fbd\u6bdb\u4e5f\u770b\u5f97\u5206\u660e\uff01\u8fde\u57ce\u864e\u8d5e\u9053\uff1a\u201c\u597d\u7cbe\u81f4\u7684\u624b\u827a\u3002\u201d\u5f6d\u5de8\u5d58\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u8bf4\u9053\uff1a\u201c\u4f60 \u4e00\u4e2a\u5356\u5531\u7684\u5973\u5b50\uff0c\u600e\u7684\u4f1a\u6709\u8fd9\u6837\u73cd\u8d35\u7684\u9996\u9970\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u8d54\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u5979\u5a46\u5bb6\u7ed9\u8058\u5979\u7684\u793c\uff0c\u5979\u90a3\u5973\u5a7f\u662f\u94f6\u697c\u7684\u4f19\u8ba1\uff0c\u624b\u827a\u4e0d\u9519\uff0c\u8fd9\u662f\u4ed6 \u81ea\u5df1\u96d5\u523b\u7684\u3002\u201d \u3000\u3000\u8fde\u57ce\u864e\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u522b\u7684\u4e0d\u96d5\uff0c\u5355\u5355\u96d5\u4e0a\u4e86\u4e00\u5934\u5f69\u51e4\uff1f\u201d \u3000\u3000\u8001\u8005\u8bf4\u9053\uff1a\u201c\u8fd9\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\uff0c\u6216\u8bb8\u662f\u53d6\u9e2f\u98ce\u548c\u9e23\u7684\u597d\u5146\u5934\u5427\u3002\u201d \u3000\u3000\u8eb2\u5728\u6811\u4e0a\u7684\u516c\u5b59\u71d5\u5f53\u7136\u770b\u4e0d\u6e05\u695a\u8fd9\u652f\u94f6\u7c2a\uff0c\u4f46\u542c\u4e86\u4ed6\u4eec\u7684\u95ee\u7b54\uff0c\u5374\u662f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u60f3 \u9053\uff1a\u201c\u539f\u6765\u662f\u5f69\u51e4\u59d0\u59d0\u3002\u5979\u7684\u6539\u5bb9\u6613\u8c8c\u4e4b\u672f\u4e5f\u771f\u662f\u5de7\u5999\uff0c\u82e5\u975e\u4eae\u51fa\u8fd9\u652f\u94f6\u7c2a\uff0c\u8fde\u6211\u4e5f\u4e0d\u6562\u8ba4 \u5979\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u5973\u5b50\u5c31\u662f\u53bb\u5e74\u5728\u6d4e\u5357\u5927\u660e\u6e56\u7554\u8bf4\u9f13\u4e66\u7684\u90a3\u4e2a\u4f55\u5f69\u51e4\u3002\u90a3\u6b21\u5979\u548c\u7236\u4eb2\u626e\u4f5c\u4e00\u5bf9\u5356 \u5531\u7684\u8d70\u6c5f\u6e56\u7236\u5973\uff0c\u7a7f\u5dde\u8fc7\u53bf\uff0c\u627e\u5bfb\u5979\u7684\u672a\u5a5a\u592b\u674e\u6566\u3002\u8def\u7ecf\u6d4e\u5357\uff0c\u88ab\u66f9\u632f\u8054\u7684\u513f\u5b50\u7740\u4e0a\uff0c\u5e26\u9886 \u5bb6\u4e01\u5c31\u6765\u62a2\u5979\u3002\u540e\u6765\u5e78\u4e8f\u9047\u4e0a\u4e86\u91d1\u9010\u6d41\u548c\u7ea2\u7f28\u4f1a\u7684\u5bab\u79c9\u85e9\uff0c\u8def\u89c1\u4e0d\u5e73\uff0c\u62d4\u5203\u76f8\u52a9\uff0c\u8fd9\u624d\u5c06\u5979 \u6551\u51fa\u864e\u53e3\u3002\u5f6d\u5de8\u5d58\u548c\u8fde\u57ce\u864e\u5c31\u662f\u5f53\u65f6\u966a\u540c\u90a3\u66f9\u516c\u5b50\u524d\u6765\u62a2\u5979\u7684\u4eba\u3002 \u3000\u3000\u5f6d\u3001\u8fde\u4eba\u8fd9\u6b21\u662f\u6765\u897f\u660c\u66ff\u66f9\u632f\u8054\u9001\u8d3a\u672d\u7ed9\u5e05\u5b5f\u96c4\u7684\u3002\u5e05\u5b5f\u96c4\u662f\u624b\u63e1\u91cd\u5175\u7684\u5c06\u519b\uff0c\u66f9\u632f\u8054 \u8eab\u4e3a\u5bb0\u76f8\uff0c\u60f3\u7ed3\u7eb3\u4ed6\u4f5c\u4e3a\u5916\u63f4\uff0c\u662f\u4ee5\u4e0d\u501f\u7ea4\u5c0a\u964d\u8d35\uff0c\u6d3e\u4e86\u8fd9\u4e24\u4e2a\u6700\u5f97\u529b\u7684\u624b\u4e0b\u5343\u91cc\u8fe2\u8fe2\u7684\u4ece \u4eac\u4e2d\u8d76\u6765\u7ed9\u4ed6\u9001\u793c\u3002 \u3000\u3000\u4e14\u8bf4\u5f6d\u3001\u8fde\u4e8c\u4eba\u89c1\u4e86\u8fd9\u652f\u94f6\u7c2a\uff0c\u6000\u7591\u4e0d\u5b9a\uff0c\u5fc3\u91cc\u5374\u9053\uff1a\u201c\u53e3\u97f3\u548c\u9762\u8c8c\u867d\u7136\u4e0d\u5bf9\uff0c\u4f46\u540c\u662f \u8bf4\u9f13\u4e66\u7684\uff0c\u800c\u4e14\u94f6\u7c2a\u4e0a\u96d5\u523b\u7684\u5f69\u51e4\u53c8\u6b63\u7b26\u5408\u5979\u7684\u540d\u5b57\u3002\u5018\u82e5\u771f\u662f\u90a3\u4e2a\u96cc\u513f\uff0c\u62ff\u56de\u53bb\u732e\u7ed9\u66f9\u516c \u5b50\u5012\u662f\u529f\u52b3\u4e00\u4ef6\u3002\u201d \u3000\u3000\u8fde\u57ce\u864e\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5230\u897f\u660c\u4f5c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u9053\uff1a\u201c\u5e05\u5c06\u519b\u540e\u5929\u5927\u5a5a\uff0c\u8981\u8bb8\u591a\u620f\u73ed\u5b50\u53bb\u51d1\u70ed\u95f9\uff0c\u6211\u4eec\u8fd9\u4e2a\u5c0f\u5c0f\u7684\u73ed\u5b50\uff0c\u4e5f\u627f\u5c06 \u519b\u5e9c\u7684\u7ba1\u4e8b\u770b\u5f97\u8d77\uff0c\u7279\u5730\u6d3e\u4e86\u4eba\u6765\u9080\u6211\u4eec\u53bb\u8f67\u4e0a\u4e00\u811a\uff0c\u7ed9\u5e05\u5c06\u519b\u5531\u4e24\u652f\u8d3a\u5a5a\u7684\u66f2\u5b50\u3002\u558f\uff0c\u8fd9 \u662f\u5c06\u519b\u5e9c\u7ba1\u4e8b\u7684\u5e16\u5b50\uff0c\u4e24\u4f4d\u5927\u4eba\u8bf7\u770b\uff0c\u5c31\u77e5\u6211\u4eec\u8bf4\u7684\u4e0d\u662f\u5047\u8bc1\u4e86\u3002\u201d \u3000\u3000\u8fde\u57ce\u864e\u628a\u624b\u4e00\u6325\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u770b\u4e86\uff0c\u6211\u5e76\u975e\u6000\u7591\u4f60\u4eec\u8bf4\u8c0e\uff0c\u4f46\u8fd9\u4e2a\u5973\u7684\u6211\u5374\u8981\u628a\u5979\u5e26 \u53bb\u3002\u201d \u3000\u3000\u8001\u8005\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5979\u6b63\u662f\u6211\u4eec\u73ed\u4e2d\u7684\u53f0\u67f1\uff0c\u8fd9\u4e2a\u2026\u2026\u201d \u3000\u3000\u8fde\u57ce\u864e\u7b11\u9053\uff1a\u201c\u5c31\u662f\u6b63\u4e3a\u8fd9\u4e2a\uff0c\u6211\u624d\u8981\u628a\u5979\u5e26\u53bb\uff01\u201d \u3000\u3000\u90a3\u8001\u8005\u9053\uff1a\u201c\u4f46\u6211\u4eec\u4e5f\u662f\u5728\u5f80\u897f\u660c\u7684\u5440\uff0c\u4f55\u4ee5\u8981\u628a\u5979\u5355\u72ec\u5206\u5f00\uff1f\u201d \u3000\u3000\u8fde\u57ce\u864e\u9053\uff1a\u201c\u4f60\u4eec\u7684\u9aa1\u8f66\u8d70\u5f97\u6162\uff0c\u6211\u7684\u9a6c\u8dd1\u5f97\u5feb\uff0c\u6211\u628a\u5979\u5e26\u53bb\uff0c\u660e\u5929\u5c31\u53ef\u4ee5\u5230\u8fbe\u897f\u660c\u3002 \u5979\u5531\u5f97\u65fa\uff0c\u53eb\u5979\u5148\u7ed9\u5e05\u5c06\u519b\u5531\u4e2a\u66f2\u5b50\uff0c\u4e5f\u597d\u8ba8\u5e05\u5c06\u519b\u7684\u559c\u6b22\u3002\u201d\u539f\u6765\u8fde\u57ce\u864e\u5df2\u7ecf\u6000\u7591\u8fd9\u5973\u5b50 \u662f\u4f55\u5f69\u98ce\u5316\u88c5\u7684\u4e86\uff0c\u56e0\u6b64\u4ed6\u6253\u7b97\u628a\u5979\u5148\u884c\u5e26\u8d70\uff0c\u5230\u4e86\u5c06\u519b\u5e9c\uff0c\u53ea\u8981\u7528\u4e00\u76d8\u6e05\u6c34\uff0c\u5c31\u53ef\u4ee5\u4ee4\u5979 \u73b0\u51fa\u672c\u6765\u9762\u76ee\u3002 \u3000\u3000\u4f55\u5f69\u51e4\u6697\u6697\u5403\u60ca\uff0c\u6b63\u5728\u601d\u91cf\u5982\u4f55\u5e94\u4ed8\uff0c\u8fde\u57ce\u864e\u7b11\u9053\uff1a\u201c\u6765\u5427\uff0c\u6211\u548c\u4f60\u5408\u4e58\u4e00\u9a91\uff0c\u4f60\u4e0d\u5fc5 \u5bb3\u6015\uff0c\u6211\u4e0d\u4f1a\u6b3a\u4fae\u4f60\u7684\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u9a6c\u5636\u4e4b\u58f0\uff0c\u5176\u58f0\u751a\u54c0\u3002\u5f6d\u3001\u8fde\u4e8c\u4eba\u8fde\u5fd9\u56de\u5934\u53bb \u770b\uff0c\u8fd9\u4e00\u770b\u767b\u65f6\u4ee4\u5f97\u4ed6\u4eec\u9762\u4e0a\u53d8\u8272\uff0c\u8fde\u57ce\u864e\u7684\u5634\u5df4\u4e5f\u4f3c\u7ed9\u5c01\u4f4f\u4e00\u6837\uff0c\u7b11\u4e0d\u51fa\u6765\u4e86\u3002 \u3000\u3000\u539f\u6765\u5728\u4ed6\u4eec\u4e0b\u9a6c\u4e4b\u540e\uff0c\u90a3\u4e24\u5339\u5750\u9a91\u672c\u6765\u662f\u5728\u6797\u8fb9\u5403\u8349\u7684\uff0c\u6b64\u65f6\u5374\u6b63\u5728\u8d1f\u75db\u72c2\u5954\uff0c\u4e24\u5339\u9a6c \u7684\u81c0\u90e8\u90fd\u63d2\u6709\u4e00\u628a\u660e\u6643\u6643\u7684\u5315\u9996\u3002 \u3000\u3000\u5f6d\u3001\u8fde\u4e8c\u4eba\u53c8\u60ca\u53c8\u6012\uff0c\u9f50\u58f0\u559d\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\u5c0f\u8d3c\uff0c\u80c6\u6562\u6697\u7b97\u6211\u7684\u5750\u9a91\uff1f\u201d\u987e\u4e0d\u5f97\u7406\u4f1a \u4f55\u5f69\u51e4\uff0c\u8fde\u5fd9\u5c31\u8ffd\u3002 \u3000\u3000\u516c\u5b59\u71d5\u53d1\u51fa\u4e24\u67c4\u5315\u9996\uff0c\u4f24\u4e86\u4ed6\u4eec\u7684\u5ea7\u9a91\u4e4b\u540e\uff0c\u6545\u610f\u5728\u6811\u6797\u91cc\u53d1\u51fa\u5403\u5403\u7684\u7b11\u58f0\u3002\u5f6d\u3001\u8fde\u4e8c \u4eba\uff0c\u4e00\u4e2a\u53bb\u8ffd\u5954\u9a6c\uff0c\u4e00\u4e2a\u5230\u6797\u4e2d\u641c\u7d22\u3002 \u3000\u3000\u516c\u5b59\u71d5\u7684\u771f\u5b9e\u672c\u9886\u4e0d\u5982\u5f6d\u81e3\u5cd9\uff0c\u4f46\u8f7b\u529f\u5374\u662f\u5728\u4ed6\u4e4b\u4e0a\u3002\u800c\u4e14\u5f6d\u5de8\u5d58\u5728\u660e\u5904\uff0c\u5979\u5728\u6697\u5904\uff0c \u6811\u6797\u58c1\u53e4\u6728\u53c2\u5929\uff0c\u6d53\u9634\u853d\u65e5\uff0c\u516c\u5b59\u71d5\u6709\u5fc3\u6349\u5f04\u4ed6\uff0c\u7109\u80fd\u8ba9\u4ed6\u641c\u7740\u3002 \u3000\u3000\u516c\u5b59\u71d5\u5728\u6811\u6797\u91cc\u515c\u4e86\u4e24\u4e2a\u5708\u5b50\uff0c\u628a\u5f6d\u5de8\u5d58\u5f15\u8d70\uff0c\u770b\u4ed6\u8d70\u5f97\u8fdc\u4e86\uff0c\u8fd9\u624d\u6084\u6084\u7684\u4ece\u53e6\u4e00\u9762\u51fa \u6765\u3002 \u3000\u3000\u5f6d\u5de8\u5d58\u8fde\u9b3c\u5f71\u4e5f\u4e0d\u89c1\u4e00\u4e2a\uff0c\u4e0d\u7531\u5f97\u5fc3\u91cc\u6697\u6697\u5403\u60ca\u3002\u4ed6\u53ea\u9053\u654c\u4eba\u7684\u672c\u9886\u8fdc\u8fdc\u5728\u4ed6\u4e4b\u4e0a\uff0c\u751f \u6015\u5728\u6811\u6797\u91cc\u906d\u53d7\u6697\u7b97\uff0c\u8fde\u5fd9\u8dd1\u51fa\u6765\u4e0e\u8fde\u57ce\u864e\u4f1a\u5408\u3002 \u3000\u3000\u6b64\u65f6\u8fde\u57ce\u864e\u5df2\u7ecf\u8ffd\u4e0a\u5954\u9a6c\uff0c\u4f46\u90a3\u4e24\u5339\u9a6c\u56e0\u4e3a\u6d41\u8840\u8fc7\u591a\uff0c\u867d\u7136\u672a\u6b7b\uff0c\u5374\u5df2\u4e0d\u582a\u518d\u7528\u3002\u4e24\u4eba \u5546\u8bae\u4e86\u4e00\u4f1a\uff0c\u8fde\u57ce\u864e\u4e5f\u662f\u6709\u70b9\u80c6\u602f\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4eba\u8f7b\u529f\u8fd9\u6837\u597d\uff0c\u4e0d\u77e5\u4f1a\u4e0d\u4f1a\u662f\u91d1\u9010\u6d41\u8fd9\u5c0f \u5b50\uff1f\u201d \u3000\u3000\u5f6d\u5de8\u5d58\u9053\uff1a\u201c\u53ea\u8981\u54b1\u4eec\u7d27\u7d27\u9760\u5728\u4e00\u8d77\uff0c\u4e0d\u8981\u8d70\u5355\uff0c\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\u4e5f\u672a\u5fc5\u5948\u4f55\u5f97\u4e86\u54b1 \u4eec\u3002\u201d\u8fde\u57ce\u864e\u9053\uff1a\u201c\u4f46\u4e0d\u77e5\u4ed6\u662f\u5426\u8fd8\u6709\u515a\u7fbd\uff0c\u4f9d\u6211\u4e4b\u89c1\uff0c\u54b1\u4eec\u8fd8\u662f\u8d76\u7d27\u8dd1\u5230\u897f\u660c\u4e3a\u5999\u3002\u90a3\u4e2a \u96cc\u513f\u53cd\u6b63\u4e5f\u662f\u8981\u5230\u897f\u660c\u7684\uff0c\u5c31\u8ba9\u5979\u81ea\u5df1\u53bb\u5427\u3002\u5230\u4e86\u897f\u660c\uff0c\u4e0d\u6101\u6ca1\u6cd5\u76d8\u67e5\u5979\u7684\u6839\u5e95\u3002\u201d\u8981\u77e5\u4ed6 \u4eec\u6b64\u65f6\u5df2\u662f\u5931\u4e86\u5750\u9a91\uff0c\u5018\u82e5\u5e26\u4e0a\u4e00\u4e2a\u5973\u7684\uff0c\u53ea\u6709\u53cd\u6dfb\u7d2f\u8d58\uff0c\u53ea\u597d\u653e\u5f03\u4e86\u628a\u4f55\u5f69\u98ce\u5148\u884c\u5e26\u8d70\u7684 \u8ba1\u5212\u3002 \u3000\u3000\u516c\u5b59\u71d5\u770b\u4ed6\u4eec\u8d70\u5f97\u8fdc\u4e86\uff0c\u8fd9\u624d\u51fa\u6765\u4e0e\u4f55\u5f69\u51e4\u76f8\u4f1a\uff0c\u4f55\u5f69\u98ce\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u5b59\u59b9 \u5b50\uff0c\u539f\u6765\u662f\u4f60\u8eb2\u5728\u6811\u6797\u91cc\u7ed9\u6211\u5e2e\u4e0a\u8fd9\u4e2a\u5927\u5fd9\uff0c\u4f46\u4f60\u4f55\u4ee5\u53c8\u4f1a\u6765\u5230\u6b64\u95f4\u5462\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u6211\u7684\u8bf4\u6765\u8bdd\u957f\uff0c\u5148\u8bf4\u4f60\u7684\u3002\u201d \u3000\u3000\u4f55\u5f69\u51e4\u7b11\u9053\uff1a\u201c\u4f60\u521a\u624d\u4e0d\u662f\u542c\u89c1\u4e86\u4e48\uff0c\u6211\u662f\u5230\u897f\u660c\u5356\u5531\u7684\u5440\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\u4f60\u80af\u7ed9\u5e05\u5b5f\u96c4\u8d3a\u559c\u3002\u5feb\u8bf4\u5b9e\u8bdd\uff01\u201d \u3000\u3000\u4f55\u5f69\u51e4\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u795d\u5a5a\u662f\u5047\uff0c\u884c\u523a\u662f\u771f\u3002\u201d\u516c\u5b59\u71d5\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5e05\u5b5f\u96c4\u6b66\u827a\u9ad8 \u5f3a\uff0c\u8fd9\u53ef\u4e0d\u662f\u5f53\u800d\u7684\u554a\uff01\u201d \u3000\u3000\u4f55\u5f69\u51e4\u9053\uff1a\u201c\u6b63\u56e0\u4e3a\u4ed6\u6b66\u827a\u9ad8\u5f3a\uff0c\u6240\u4ee5\u624d\u8981\u5927\u5bb6\u5408\u529b\u3002\u201d\u8ddf\u7740\u7ed9\u516c\u5b59\u71d5\u89e3\u91ca\u9053\uff1a\u201c\u8fd9\u662f \u674e\u6566\u5b9a\u7684\u8ba1\u5212\uff0c\u540e\u65e5\u4f1a\u6709\u8bb8\u591a\u597d\u6c49\u53bb\u7ed9\u5e05\u5b5f\u96c4\u2018\u8d3a\u559c\u2019\u7684\u3002\u6709\u7684\u660e\u6765\uff0c\u6709\u7684\u6697\u5f80\uff0c\u7528\u7684\u65b9\u6cd5 \u4e5f\u4e0d\u4e00\u6837\u3002\u6211\u4f1a\u9f13\u4e66\uff0c\u6240\u4ee5\u626e\u4f5c\u8d70\u6c5f\u6e56\u7684\u6b4c\u5973\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u542c\u8bf4\u4f60\u548c\u674e\u6566\u5df2\u7ecf\u6210\u4e86\u4eb2\u3002\u59d0\u592b\u5462\uff1f\u201d \u3000\u3000\u4f55\u5f69\u98ce\u9053\uff1a\u201c\u4ed6\u5148\u53bb\u4e86\u3002\u8fd9\u4e2a\u73ed\u5b50\u9664\u4e86\u73ed\u4e3b\u4e4b\u5916\uff0c\u90fd\u662f\u5973\u7684\u3002\u4ed6\u5f53\u7136\u4e0d\u597d\u548c\u6211\u4e00\u8d77\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u671b\u4e86\u671b\u90a3\u4f4d\u767d\u987b\u70ef\u786b\u7684\u73ed\u4e3b\uff0c\u72d0\u7591\u4e0d\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u4f2f\u662f\u2026\u2026\u201d \u3000\u3000\u4f55\u5f69\u98ce\u7b11\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7239\u7239\u7684\u597d\u670b\u53cb\uff0c\u771f\u7684\u662f\u8fd9\u4e00\u73ed\u4e50\u5bb6\u73ed\u7684\u4e3b\u3002\u4f60\u4ee5\u4e3a\u2026\u2026\u201d \u3000\u3000\u516c\u5b59\u71d5\u5927\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u662f\u4f60\u7239\u7239\u5047\u626e\u7684\u5462\u3002\u4f60\u7684\u6539\u5bb9\u6613\u8c8c\u4e4b\u672f\u771f\u662f\u5de7\u5999\uff0c\u5c31\u50cf\u6362\u4e86 \u4e2a\u4eba\u4f3c\u7684\u3002\u521a\u624d\u4e0d\u662f\u542c\u5f97\u90a3\u4e24\u4e2a\u5bb6\u4f19\u76d8\u95f4\u4f60\u8fd9\u652f\u94f6\u7c2a\uff0c\u6211\u4e5f\u4e0d\u77e5\u662f\u4f60\u3002\u201d \u3000\u3000\u539f\u6765\u4f55\u5f69\u98ce\u66fe\u7ecf\u8ddf\u5979\u7236\u4eb2\u5230\u8fc7\u7ea2\u7f28\u4f1a\u4f5c\u5ba2\uff0c\u8fd9\u652f\u94f6\u7c2a\u6b63\u662f\u516c\u5b59\u71d5\u7684\u6bcd\u4eb2\u9001\u7ed9\u4f55\u5f69\u98ce\u7684\u89c1 \u9762\u793c\u3002\u5188\u4e3a\u5979\u7684\u540d\u5b57\u4e2d\u6709\u4e2a\u201c\u98ce\u201d\u5b57\uff0c\u800c\u516c\u5b59\u71d5\u7684\u6bcd\u4eb2\u6070\u5de7\u6709\u4e00\u652f\u7cbe\u96d5\u7f15\u51e4\u7684\u94f6\u7c2a\u3002\u201d \u3000\u3000\u4f55\u5f69\u51e4\u9053\uff1a\u201c\u597d\u4e86\uff0c\u6211\u7684\u4e8b\u60c5\u8bf4\u5b8c\u4e86\uff0c\u8be5\u4f60\u8bf4\u5427\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u60f3\u8bf7\u4f60\u5e2e\u6211\u6539\u4e00\u6539\u5bb9\uff0c\u8ba9\u6211\u8ddf\u968f\u4f60\u4eec\u8fd9\u4e2a\u73ed\u5b50\u540c\u53bb\u3002\u201d \u3000\u3000\u4f55\u5f69\u51e4\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u4e5f\u662f\u8981\u5f80\u897f\u660c\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u5f53\u4e0b\u628a\u522b\u540e\u7684\u7ecf\u8fc7\u7b80\u7565\u5730\u544a\u8bc9\u4f55\u5f69\u51e4\u3002\u4f55\u5f69\u98ce\u9053\uff1a\u201c\u8fd9\u4e2a\u5bb9\u6613\uff0c \u6211\u6709\u6613\u5bb9\u4e39\uff0c\u4f60\u6539\u88c5\u4e4b\u540e\uff0c\u5305\u7ba1\u6ca1\u4eba\u8ba4\u5f97\u4f60\u3002\u201d\u53c8\u9053\uff1a\u201c\u5176\u5b9e\u5982\u679c\u4f60\u4e0d\u5fd9\u7740\u8d70\u7684\u8bdd\uff0c\u540e\u5929\u53ef \u4ee5\u548c\u5927\u51c9\u5c71\u7684\u4e49\u519b\u4e00\u540c\u53bb\u653b\u6253\u897f\u660c\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u8be7\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4e49\u519b\u540e\u5929\u8981\u653b\u6253\u897f\u660c\uff1f\u6211\u662f\u521a\u4ece\u5927\u51c9\u5c71\u6765\u7684\uff0c\u90fd\u4e0d\u77e5\u9053\u8fd9\u4e2a \u6d88\u606f\u3002\u201d \u3000\u3000\u4f55\u5f69\u51e4\u9053\uff1a\u201c\u5c0f\u91d1\u5ddd\u65b9\u9762\u7684\u51b7\u94c1\u6a35\u8ba1\u5212\u5728\u540e\u5929\u665a\u4e0a\u653b\u6253\u897f\u660c\uff0c\u4ed6\u5df2\u5e26\u9886\u4e00\u5b9a\u4e49\u519b\uff0c\u6b63\u5728 \u8d76\u5f80\u5927\u51c9\u5c71\u4e0e\u7afa\u5c1a\u7236\u4f1a\u516c\uff0c\u6211\u7684\u7239\u7239\u5c31\u662f\u5c0f\u91d1\u5ddd\u548c\u5927\u51c9\u5c71\u4e24\u5730\u7684\u8054\u7edc\uff0c\u9884\u8ba1\u4eca\u5929\u508d\u665a\u65f6\u5206\uff0c \u5c31\u53ef\u4ee5\u5230\u5927\u51c9\u5c71\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u5b5f\u96c4\u5728\u540e\u5929\u65e5\u95f4\u6210\u5a5a\uff0c\u4e49\u519b\u665a\u4e0a\u624d\u5230\uff0c\u6253\u654c\u4eba\u8fd9\u573a\u70ed\u95f9\u3002\u6240\u4ee5\u6211\u60f3\u6211\u8fd8 \u662f\u548c\u4f60\u4eec\u5148\u53bb\u7684\u597d\u3002\u201d \u3000\u3000\u4f55\u5f69\u98ce\u7b11\u9053\uff1a\u201c\u4f60\u5012\u8bf4\u5f97\u8f7b\u677e\uff0c\u4f60\u53ef\u77e5\u9053\u6211\u4eec\u8fd9\u6279\u5148\u884c\u6df7\u5165\u897f\u660c\u7684\u4eba\u80a9\u4e0a\u7684\u62c5\u5b50\u6709\u591a \u91cd\uff1f\u6240\u5192\u7684\u5371\u9669\u6709\u591a\u5927\u5417\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u54b1\u4eec\u82e5\u7136\u80fd\u591f\u523a\u6740\u5e05\u5b5f\u96c4\u56fa\u7136\u6700\u597d\uff0c\u5018\u82e5\u4e0d\u80fd\uff0c\u4e5f\u5f97\u8d1f\u8d77\u91cc\u5e94\u5916 \u5408\u7684\u4efb\u52a1\u3002\u201d \u3000\u3000\u963f\u5f69\u98ce\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5c31\u597d\uff0c\u4f60\u60f3\u60f3\u8fd9\u53ef\u662f\u5f53\u800d\u7684\u5417\uff1f\u897f\u660c\u5927\u519b\u4e91\u96c6\uff0c\u6709\u5982\u91d1\u57ce\u6c64\u6c60\uff0c\u4e49 \u519b\u82e5\u7136\u5f3a\u653b\uff0c\u53ea\u6015\u5f88\u96be\u653b\u7834\u3002\u662f\u5426\u80fd\u591f\u6253\u5f97\u5f00\u57ce\u95e8\uff0c\u90a3\u5c31\u5f97\u9760\u54b1\u4eec\u4f5c\u5185\u5e94\u7684\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u5165\u57ce\u4e4b\u540e\uff0c\u6211\u4e00\u5b9a\u574e\u6b65\u5c0f\u5fc3\uff0c\u51b3\u4e0d\u8ba9\u654c\u4eba\u770b\u51fa\u7834\u7efd\u3002\u201d \u3000\u3000\u5316\u88c5\u4e4b\u540e\uff0c\u516c\u5b59\u71d5\u4e34\u6d41\u7167\u5f71\uff0c\u679c\u7136\u597d\u50cf\u6362\u4e86\u4e2a\u4eba\u4f3c\u7684\uff0c\u4e0d\u7981\u62cd\u638c\u7b11\u9053\uff1a\u201c\u5999\u6781\uff0c\u5999\u6781\uff0c \u8fde\u6211\u81ea\u5df1\u90fd\u8ba4\u4e0d\u5f97\u81ea\u5df1\u4e86\uff0c\u4e00\u5b9a\u53ef\u4ee5\u6df7\u5f97\u8fc7\u53bb\u3002\u201d \u3000\u3000\u4f55\u5f69\u51e4\u5ffd\u5730\u60f3\u8d77\u4e00\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u554a\u5440\uff0c\u4e0d\u5999\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u5999\uff1f\u201d \u3000\u3000\u4f55\u5f69\u51e4\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4e2a\u73ed\u5b50\u4e00\u5171\u662f\u516b\u4e2a\u4eba\uff0c\u4e03\u4e2a\u5973\u7684\uff0c\u4e00\u4e2a\u7537\u7684\u3002\u521a\u624d\u90a3\u4e24\u4e2a\u5bb6\u4f19\u76d8\u67e5 \u6211\u4eec\uff0c\u5373\u4f7f\u4ed6\u4eec\u8bb0\u4e0d\u6e05\u6bcf\u4e2a\u4eba\u7684\u5bb9\u8c8c\uff0c\u4f46\u5171\u6709\u591a\u5c11\u4e2a\u4eba\uff0c\u60f3\u6765\u4ed6\u4eec\u662f\u5e94\u8be5\u8bb0\u5f97\u7684\u3002\u5982\u4eca\u591a\u51fa \u4e86\u4e00\u4e2a\u4eba\u6765\uff0c\u8fd9\u3001\u8fd9\u4e0d\u662f\u4e2a\u5929\u5927\u7684\u7834\u7efd\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u6014\u4e86\u4e00\u6014\uff0c\u4e5f\u81ea\u89c9\u5f97\u6709\u70b9\u53ef\u8651\uff0c\u53ef\u5979\u53c8\u4e0d\u613f\u610f\u653e\u5f03\u8fd9\u4e00\u4e2a\u53ef\u4ee5\u6df7\u8fdb\u897f\u660c\u7684\u673a\u4f1a\uff0c \u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u5bb6\u4f19\u521a\u624d\u6709\u6ca1\u6709\u70b9\u8fc7\u6570\uff0c\u6216\u8bb8\u4ed6\u4eec\u6ca1\u6709\u7559\u610f\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u8fd9\u6837\u5427\uff0c \u6211\u88c5\u4f5c\u75af\u5b50\uff0c\u5018\u82e5\u8fdb\u57ce\u7684\u65f6\u5019\uff0c\u5f53\u771f\u78b0\u5230\u4ed4\u7ec6\u68c0\u67e5\u7684\u8bdd\uff0c\u4f60\u5c31\u8bf4\u662f\u8def\u4e0a\u78b0\u89c1\u6211\uff0c\u89c1\u6211\u751f\u75c5\u53ef \u601c\uff0c\u56e0\u6b64\u8f7d\u6211\u8fdb\u57ce\u3002\u8fd9\u6837\u5c31\u4e0d\u81f3\u4e8e\u8fde\u7d2f\u4f60\u4eec\u4e86\u3002\u201d \u3000\u3000\u4f55\u5f69\u51e4\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6050\u6015\u4e0d\u5927\u59a5\u5f53\uff01\u201d\u516c\u5b59\u71d5\u5341\u5206\u7740\u6025\uff0c\u8bf4\u9053\uff1a\u201c\u53bb\uff0c\u6211\u662f\u4e00\u5b9a \u8981\u53bb\u7684\uff0c\u65e2\u7136\u8fd9\u4e2a\u529e\u6cd5\u4e0d\u59a5\u5f53\uff0c\u90a3\u6211\u53ea\u597d\u548c\u4f60\u4eec\u5206\u5f00\u6765\u8d70\u4e86\uff0c\u53cd\u6b63\u6211\u73b0\u5728\u5df2\u7ecf\u6539\u53d8\u4e86\u9762\u8c8c\uff0c \u897f\u660c\u57ce\u91cc\u4e5f\u6295\u6709\u8ba4\u8bc6\u6211\u7684\u719f\u4eba\uff01 \u3000\u3000\u4f55\u5f69\u51e4\u6447\u624b\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff01\u4f60\u4e00\u4e2a\u4eba\u6211\u4eec\u66f4\u4e0d\u653e\u5fc3\u3002\u8fd9\u6837\u597d\u4e86\u3002\u4f60\u53ef\u4ee5\u88c5\u4f5c\u662f\u6211\u4eec\u73ed\u5b50 \u91cc\u7684\u75c5\u4eba\uff0c\u4e07\u4e00\u5f6d\u5de8\u5d58\u548c\u8fde\u57ce\u864e\u5728\u6211\u4eec\u8fdb\u57ce\u7684\u65f6\u5019\u4eb2\u6765\u67e5\u70b9\uff0c\u6211\u4eec\u53ef\u4ee5\u8bf4\u4f60\u662f\u4e00\u76f4\u8eba\u5728\u8f66\u4e0a \u7684\uff0c\u5728\u8def\u4e0a\u7684\u90a3\u6b21\u76d8\u67e5\uff0c\u4f60\u5e76\u6ca1\u6709\u4e0b\u8f66\u3002\u5f53\u7136\u8fd8\u662f\u8981\u5192\u4e00\u70b9\u98ce\u9669\uff0c\u4f46\u6216\u8bb8\u53ef\u4ee5\u6df7\u5f97\u8fc7\u53bb\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53ea\u8981\u89c1\u5f97\u7740\u5389\u5927\u54e5\uff0c\u5192\u5929\u5927\u7684\u5371\u9669\u6211\u4e5f\u613f\u610f\u3002\u201d\u4e8e\u662f\u4f9d\u8ba1\u884c\u4e8b\uff0c\u6309 \u4e0b\u4e0d\u63d0\u3002 \u3000\u3000\u4e14\u8bf4\u5389\u5357\u661f\u4e00\u4e2a\u4eba\u524d\u5f80\u897f\u660c\uff0c\u6b64\u65f6\u4e5f\u6b63\u662f\u78b0\u4e86\u96be\u9898\uff0c\u8fdb\u4e0d\u4e86\u57ce\uff01 \u3000\u3000\u4ed6\u672c\u6765\u662f\u60f3\u51ed\u4ed7\u8f7b\u529f\uff0c\u534a\u591c\u4e09\u66f4\u5077\u5077\u8fdb\u5165\u897f\u660c\u7684\uff0c\u4f46\u5230\u4e86\u57ce\u6c60\u5bf9\u9762\u7684\u4e00\u5ea7\u5c71\u5934\u4e00\u671b\uff0c\u4e0d \u89c9\u5012\u62bd\u4e86\u4e00\u53e3\u51b7\u6c14\u3002\u6b63\u662f\uff1a \u3000\u3000\u8f7b\u529f\u5353\u7edd\u90fd\u65e0\u7528\uff0c\u6212\u5907\u68ee\u4e25\u8c01\u80fd\u8fdb\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e09\u5341\u56db\u56de\u3000\u8054\u624b\u53cc\u96c4\u64d2\u6076\u8d3c\u3000\u540c\u5fc3\u610f\u5171\u5b9a\u826f\u8c0b \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e09\u5341\u56db\u56de\u3000\u8054\u624b\u53cc\u96c4\u64d2\u6076\u8d3c\u3000\u540c\u5fc3\u610f\u5171\u5b9a\u826f\u8c0b \u3000\u3000\u53ea\u89c1\u57ce\u5899\u4e0a\u706f\u706b\u8f89\u714c\uff0c\u5200\u67aa\u5982\u96ea\uff0c\u5899\u5934\u4e0a\u5e03\u6ee1\u536b\u5175\uff0c\u5982\u4e34\u5927\u654c\u3002\u5728\u8fd9\u6837\u60c5\u5f62\u4e4b\u4e0b\uff0c\u5389\u5357 \u661f\u5f53\u7136\u662f\u6df7\u4e0d\u8fc7\u53bb\u7684\u4e86\u3002 \u3000\u3000\u5389\u5357\u661f\u4e5f\u66fe\u60f3\u5230\u5728\u767d\u5929\u626e\u4f5c\u4e61\u6c11\u8fdb\u57ce\uff0c\u4f46\u4e00\u6765\u4ed6\u4e0d\u4f1a\u8bf4\u897f\u660c\u7684\u571f\u8bdd\uff0c\u4e8c\u6765\u4ed6\u4f69\u7740\u7384\u94c1\u5b9d \u5251\uff0c\u54ea\u6709\u4e00\u4e2a\u4e61\u6c11\u4f1a\u4f69\u5251\u7684\uff1f\u5018\u82e5\u4e0d\u5e26\u8fd9\u628a\u5b9d\u5251\u8fdb\u57ce\u5427\uff0c\u4ed6\u53c8\u600e\u820d\u5f97\u5c06\u5b83\u629b\u6389\uff1f \u3000\u3000\u5389\u5357\u661f\u82e6\u601d\u65e0\u8ba1\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u662f\u6708\u8fc7\u4e2d\u5929\uff0c\u5c06\u8fd1\u56db\u66f4\u7684\u65f6\u5206\u4e86\u3002\u5929\u4e0a\u4e0b\u4e86\u4e00\u573a\u5927\u96ea\uff0c\u628a \u9644\u8fd1\u7684\u5c71\u5934\uff0c\u67d3\u5f97\u4e00\u7247\u94f6\u767d\uff0c\u5389\u5357\u661f\u5076\u5c14\u4e00\u62ac\u5934\uff0c\u5ffd\u89c1\u6709\u4e24\u4e2a\u4eba\u5728\u5bf9\u9762\u7684\u5c71\u5934\u51fa\u73b0\uff0c\u7a7f\u7684\u662f \u519b\u5b98\u7684\u670d\u9970\u3002\u5389\u5357\u661f\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u77e5\u662f\u4e0d\u662f\u6765\u641c\u67e5\u7684\u519b\u5b98\uff0c\u5f53\u4e0b\u614c\u5fd9\u8eb2\u85cf\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u8f7b\u8f7b\u62cd\u4e86\u4e09\u4e0b\u624b\u638c\uff0c\u90a3\u4e24\u4e2a\u519b\u5b98\u4e5f\u62cd\u4e86\u4e09\u4e0b\u624b\u638c\uff0c\u638c\u58f0\u8fc7\u540e\uff0c\u4e71\u8349\u4e1b\u4e2d\u8df3\u51fa \u4e00\u4e2a\u4eba\u6765\uff0c\u5389\u5357\u661f\u5fc3\u9053\uff1a\u201c\u539f\u6765\u662f\u8fd9\u4e24\u4e2a\u519b\u5b98\u548c\u4eba\u805a\u4f1a\u3002\u5947\u602a\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u548c\u4e00\u4e2a\u4e61\u4e0b\u4eba\u5077 \u5077\u5728\u534a\u591c\u4e09\u66f4\u7ea6\u4f1a\u5462\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u519b\u5b98\u559d\u9053\uff1a\u201c\u54c8\uff0c\u674e\u6566\uff0c\u539f\u7c73\u662f\u4f60\uff01\u4f60\u770b\u770b\u6211\u662f\u8c01\uff1f\u201d\u628a\u62ab\u98ce\u4e00 \u8131\uff0c\u73b0\u51fa\u4e00\u4e2a\u6cb9\u5149\u6676\u4eae\u7684\u79c3\u5934\u3002\u539f\u6765\u8fd9\u4e2a\u79c3\u5934\u6c49\u5b50\u6b63\u662f\u5f6d\u5de8\u5d58\uff0c\u53e6\u4e00\u4e2a\u519b\u5b98\u662f\u8fde\u57ce\u864e\u3002\u4ed6\u95e8 \u4e8c\u4eba\u56e0\u4e3a\u5728\u8def\u4e0a\u7ed9\u516c\u5b59\u71d5\u88c5\u795e\u5f04\u9b3c\u7684\u5413\u4e86\u4e00\u573a\uff0c\u5413\u5f97\u4e0d\u6562\u5728\u8def\u4e0a\u9017\u7559\uff0c\u9ed1\u591c\u8d76\u6765\u897f\u660c\u3002\u4e0d\u6599 \u5230\u4e86\u897f\u660c\u57ce\u5916\u5bf9\u9762\u7684\u8fd9\u5ea7\u5c71\u5934\uff0c\u5374\u542c\u5230\u4e86\u674e\u6566\u8fde\u62cd\u4e09\u4e0b\u7684\u638c\u58f0\u3002 \u3000\u3000\u5f6d\u3001\u8fde\u4e8c\u4eba\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u4e00\u542c\u5c31\u77e5\u662f\u6709\u4eba\u51fb\u638c\u4e3a\u53f7\uff0c\u60f3\u5fc5\u662f\u7ea6\u597d\u4e86\u5728\u8fd9\u91cc\u7ea6\u4f1a \u7684\u3002\u4e8e\u662f\u4ed6\u4eec\u5c31\u56de\u4e86\u4e09\u4e0b\u638c\u58f0\uff0c\u628a\u8fd9\u4e2a\u4eba\u5f15\u51fa\u6765\u3002 \u3000\u3000\u674e\u6566\u7ea6\u7684\u672c\u6765\u662f\u53e6\u5916\u4e24\u4e2a\u4eba\uff0c\u8fd9\u4e24\u4e2a\u4eba\u662f\u57ce\u4e2d\u7684\u4e0b\u7ea7\u519b\u5b98\uff0c\u66ff\u4e49\u519b\u4f5c\u201c\u5367\u5e95\u201d\u7684\u3002\u53ea\u56e0 \u9ed1\u591c\u4e4b\u4e2d\uff0c\u4ed6\u8eb2\u5728\u8305\u8349\u4e1b\u91cc\uff0c\u4e00\u65f6\u770b\u4e0d\u6e05\u695a\uff0c\u89c1\u6709\u4e24\u4e2a\u5f71\u5b50\u51fa\u73b0\uff0c\u5c31\u4ee5\u4e3a\u662f\u6240\u7ea6\u7684\u90a3\u4e24\u4e2a \u4eba\uff0c\u542c\u5f97\u5bf9\u65b9\u56de\u4e86\u4e09\u4e0b\u638c\u58f0\uff0c\u4fbf\u8df3\u51fa\u6765\u3002 \u3000\u3000\u5f6d\u3001\u8fde\u4e8c\u4eba\u5bb3\u6015\u7684\u53ea\u662f\u91d1\u9010\u6d41\uff0c\u5bf9\u674e\u6566\u4ed6\u4eec\u5e76\u4e0d\u653e\u5728\u773c\u5185\u3002\u4e00\u53d1\u89c9\u662f\u674e\u6566\uff0c\u8fde\u57ce\u864e\u9996\u5148 \u5c31\u6251\u8fc7\u53bb\u3002 \u3000\u3000\u674e\u6566\u6697\u6697\u53eb\u58f0\u4e0d\u5999\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5f6d\u5de8\u5d58\u5df2\u7ecf\u6251\u5230\u4ed6\u7684\u8ddf\u524d\u3002\u674e\u6566\u559d\u9053\uff1a\u201c\u7167 \u6253\uff01\u201d\u628a\u624b\u4e00\u626c\uff0c\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u4e2a\u7403\u5f62\u7684\u6697\u5668\u8131\u624b\u4fbf\u5373\u70b8\u5f00\uff0c\u767b\u65f6\u70df\u96fe\u8ff7\u6f2b\uff0c\u4e00\u6e9c\u706b \u5149\uff0c\u76f4\u55b7\u8fc7\u6765\uff0c\u54bd\u96fe\u4e4b\u4e2d\u4e14\u6742\u7740\u55e4\u55e4\u7684\u58f0\u54cd\uff01 \u3000\u3000\u5f6d\u5de8\u5d58\u4e00\u4e2a\u5012\u7eb5\uff0c\u8fc5\u5373\u8fde\u73af\u53cc\u638c\u62cd\u51fa\uff0c\u559d\u9053\uff1a\u201c\u597d\u72e0\u6bd2\u7684\u6697\u5668\uff0c\u4f46\u53c8\u80fd\u5948\u6211\u4f55\u54c9\uff1f\u201d\u638c \u98ce\u547c\u547c\uff0c\u706b\u5149\u7184\u706d\u3002\u70df\u96fe\u56db\u6563\uff0c\u6742\u5728\u70df\u96fe\u4e2d\u6253\u6765\u7684\u4e00\u628a\u6885\u82b1\u9488\u4e5f\u90a3\u7ed9\u4ed6\u6253\u843d\uff01 \u3000\u3000\u53ef\u662f\u5728\u96fe\u6563\u70df\u6d88\u4e4b\u540e\uff0c\u674e\u6566\u7684\u5f71\u5b50\u5df2\u7ecf\u4e0d\u89c1\u3002 \u3000\u3000\u5f6d\u5de8\u5cd9\u51b7\u7b11\u9053\uff1a\u201c\u770b\u4f60\u8eb2\u5f97\u4e0a\u5929\uff01\u8fde\u5144\uff0c\u54b1\u4eec\u5206\u5934\u641c\u7d22\uff01\u201d\u8fde\u57ce\u864e\u66f4\u5de5\u5fc3\u8ba1\uff0c\u7b11\u9053\uff1a \u201c\u4e0d\u5fc5\u5982\u6b64\u8d39\u529b\uff0c\u54b1\u4eec\u7528\u6349\u7530\u9f20\u7684\u529e\u6cd5\u628a\u8fd9\u53ae\u903c\u51fa\u6765\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u521d\u65f6\u672c\u6765\u4e0d\u60f3\u591a\u4e8b\uff0c\u540e\u6765\u770b\u89c1\u674e\u6566\u53d1\u51fa\u7684\u6697\u5668\uff0c\u4e0d\u89c9\u6709\u70b9\u5947\u602a\uff1a\u201c\u8fd9\u79cd\u6bd2\u96fe\u91d1\u9488 \u70c8\u7130\u5f39\uff0c\u4e43\u662f\u5929\u9b54\u6559\u7684\u72ec\u95e8\u6697\u5668\uff0c\u600e\u7684\u6b64\u4eba\u4e5f\u4f1a\u4f7f\u7528\uff1f\u201d\u5fc3\u5934\u4e00\u52a8\uff0c\u8fd9\u624d\u84e6\u5730\u60f3\u8d77\uff01\u201c\u602a\u4e0d \u5f97\u6211\u89c9\u5f97\u4ed6\u7684\u540d\u5b57\u597d\u719f\uff0c\u539f\u6765\u4ed6\u5c31\u662f\u5728\u5f82\u5f95\u5c71\u4e0a\u5077\u5b66\u4e86\u767e\u6bd2\u771f\u7ecf\u7684\u90a3\u4e2a\u674e\u6566\u3002\u201d\u8fd9\u4ef6\u4e8b\u662f\u91d1 \u9010\u6d41\u544a\u8bc9\u4ed6\u7684\u3002\u4ed6\u77e5\u9053\u4e86\u674e\u6566\u662f\u91d1\u9010\u6d41\u7684\u670b\u53cb\uff0c\u5f53\u7136\u4e0d\u80fd\u8896\u624b\u65c1\u89c2\u4e86\u3002 \u3000\u3000\u5f6d\u5de8\u5d58\u6b63\u8981\u64e6\u71c3\u706b\u77f3\uff0c\u4f7f\u7528\u706b\u653b\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u559d\u9053\uff1a\u201c\u9f20\u8f88\u6562\u5c14\uff01\u201d\u5389\u5357\u661f\u8df3\u4e86\u51fa\u6765\uff0c \u62d4\u5251\u5c31\u5411\u4ed6\u5288\u53bb\u3002 \u3000\u3000\u5f6d\u5de8\u5cd9\u770b\u89c1\u4e0d\u662f\u91d1\u9010\u6d41\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\u5c0f\u5b50\uff0c\u4e5f\u6562\u591a\u7ba1\u95f2\u4e8b\uff1f\u201d\u51b7\u7b11\u58f0\u4e2d\uff0c\u63a5 \u8fde\u7684\u53d1\u51fa\u4e86\u4e24\u8bb0\u5288\u7a7a\u638c\u3002 \u3000\u3000\u5f6d\u5de8\u5d58\u7684\u91d1\u521a\u638c\u5200\u6709\u5f00\u7891\u88c2\u77f3\u4e4b\u80fd\uff0c\u5018\u82e5\u662f\u5bfb\u5e38\u7684\u5200\u5251\uff0c\u7ed9\u4ed6\u7684\u638c\u98ce\u4e00\u8361\uff0c\u5373\u4f7f\u4e0d\u6253\u843d \u4e5f\u4f1a\u9707\u6b6a\u5251\u5c16\uff0c\u51b3\u8ba1\u4f24\u4e0d\u4e86\u4ed6\u3002\u4f46\u5389\u5357\u661f\u7528\u7684\u4e43\u662f\u7384\u94c1\u5b9d\u5251\uff0c\u91cd\u8fbe\u4e00\u767e\u591a\u65a4\uff0c\u5f6d\u5de8\u5d58\u7684\u91d1\u521a \u638c\u529b\u53ef\u5c31\u62d4\u4e0d\u52a8\u5b83\u4e86\u3002 \u3000\u3000\u638c\u98ce\u5251\u5f71\u4e4b\u4e2d\uff0c\u53ea\u89c1\u5389\u5357\u661f\u8eab\u5f62\u4e00\u6643\uff0c\u7384\u94c1\u5b9d\u5251\u4ecd\u7136\u662f\u5288\u4e0b\u6765\u3002\u5f6d\u5de8\u5d58\u5927\u5403\u4e00\u60ca\uff0c\u8981\u8dd1 \u5df2\u672a\u4e0d\u53ca\uff0c\u5389\u5357\u661f\u4e00\u5251\u5288\u4e0b\uff0c\u7adf\u7136\u6d3b\u751f\u751f\u7684\u628a\u4ed6\u7684\u8eab\u5b50\u5206\u4f5c\u4e24\u8fb9\uff01 \u3000\u3000\u5389\u5357\u661f\u80f8\u53e3\u5982\u53d7\u9524\u51fb\uff0c\u4e5f\u81ea\u6697\u6697\u5403\u60ca\uff0c\u8fd9\u624d\u77e5\u9053\u7ed9\u81ea\u5df1\u6740\u6b7b\u7684\u7adf\u662f\u6b66\u6797\u4e2d\u7684\u4e00\u6d41\u9ad8\u624b\u3002 \u3000\u3000\u8fde\u57ce\u864e\u521d\u65f6\u4e5f\u662f\u4e0d\u628a\u5389\u5357\u661f\u653e\u5728\u773c\u5185\uff0c\u4ee5\u4e3a\u8fd9\u6837\u4e00\u4e2a\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5c0f\u5b50\uff0c\u5f6d\u5de8\u5d58\u5f53\u7136\u5bf9\u4ed8 \u5f97\u4e86\u3002\u54ea\u77e5\u4e0d\u8fc7\u4e00\u4e2a\u7167\u9762\uff0c\u5f6d\u5de8\u5d58\u5c31\u7ed9\u8fd9\u201c\u5c0f\u5b50\u201d\u4e00\u5251\u5288\u4e86\u3002\u5f85\u5230\u8fde\u57ce\u864e\u53d1\u89c9\uff0c\u62a2\u6551\u5df2\u6765\u4e0d \u53ca\u3002 \u3000\u3000\u8fde\u57ce\u864e\u53c8\u60ca\u53c8\u6012\uff0c\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4eca\u65e5\u4e0d\u662f\u4f60\u6b7b\uff0c\u4fbf\u662f\u6211\u4ea1\uff01\u201d\u53cc\u7b14\u4ea4\u53c9\u70b9\u5230\u3002\u5389\u5357 \u661f\u53cd\u624b\u4e00\u5251\uff0c\u524a\u4e86\u4e2a\u7a7a\u3002\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u8863\u895f\u7ed9\u4ed6\u5de6\u7b14\u7684\u7b14\u5c16\u7a7f\u8fc7\u3002 \u3000\u3000\u5389\u5357\u661f\u8fde\u5fd9\u6539\u53d8\u6218\u672f\uff0c\u628a\u7384\u94c1\u5b9d\u5251\u821e\u8d77\u4e00\u9053\u5149\u5708\uff0c\u5168\u8eab\u906e\u62e6\u5f97\u6beb\u65e0\u7834\u7efd\uff0c\u4e00\u4e2a\u4e2a\u5706\u5708\u9996 \u5c3e\u76f8\u63a5\uff0c\u7a33\u6b65\u5411\u8fde\u57ce\u864e\u8fdb\u903c\u3002\u8fde\u57ce\u864e\u7684\u53cc\u7b14\u70b9\u56db\u7a74\u4e43\u662f\u6b66\u6797\u7edd\u6280\uff0c\u8f7b\u7075\u8fc5\u6377\uff0c\u72e0\u51c6\u517c\u5907\uff0c\u4f46 \u5728\u7384\u94c1\u5b9d\u5251\u4e4b\u4e0b\uff0c\u5374\u662f\u53d1\u6325\u4e0d\u4e86\u4ed6\u7684\u6240\u957f\u3002 \u3000\u3000\u8fde\u57ce\u864e\u5df2\u77e5\u5bf9\u65b9\u4f7f\u7684\u662f\u628a\u5b9d\u5251\uff0c\u4f46\u8fd8\u4e0d\u77e5\u7384\u94c1\u5b9d\u5251\u662f\u90a3\u6837\u6c89\u91cd\uff0c\u4ed6\u4f7f\u7528\u8f7b\u7075\u7684\u62db\u6570\uff0c\u5c3d \u91cf\u907f\u514d\u548c\u5bf9\u65b9\u7684\u5b9d\u5251\u78b0\u8d70\uff0c\u4f46\u5389\u5357\u661f\u4ea6\u975e\u5eb8\u624b\uff0c\u8fde\u57ce\u864e\u907f\u5f97\u4e86\u4e00\u62db\u907f\u4e0d\u4e86\u7b2c\u4e8c\u62db\uff0c\u6fc0\u6218\u4e2d\u5389 \u5357\u661f\u5251\u5149\u66b4\u6da8\uff0c\u4e00\u62db\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff0c\u5251\u5149\u62e6\u8170\u5288\u5230\u3002\u8fde\u57ce\u864e\u8feb\u4e8e\u65e0\u5948\uff0c\u53ea\u5f97\u628a\u53cc\u7b14\u4e00\u67b6\uff0c\u4ed6 \u6043\u7740\u81ea\u5df1\u8fd9\u53cc\u5224\u5b98\u7b14\u662f\u7cbe\u94a2\u94f8\u7684\uff0c\u5373\u4f7f\u78b0\u4e0a\u5b9d\u5251\uff0c\u4e5f\u672a\u5fc5\u7acb\u5373\u4fbf\u4f1a\u524a\u65ad\u3002\u54ea\u77e5\u78b0\u4e0a\u4e86\u7384\u94c1\u5b9d \u5251\uff0c\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u8fde\u57ce\u864e\u7684\u53cc\u7b14\u4e0d\u4f46\u4e00\u9f50\u6298\u65ad\uff0c\u864e\u53e3\u4e5f\u7ed9\u9707\u88c2\uff01 \u3000\u3000\u674e\u6566\u53eb\u9053\uff1a\u201c\u7559\u4e2a\u6d3b\u53e3\uff01\u201d\u5389\u5357\u661f\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u4ee5\u5176\u4eba\u4e4b\u9053\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\uff01\u201d\u5251\u5c16\u8f7b \u8f7b\u5f80\u524d\u4e00\u9001\uff0c\u70b9\u4e86\u8fde\u57ce\u864e\u7684\u7a74\u9053\u3002 \u3000\u3000\u674e\u6566\u559c\u51fa\u671b\u5916\uff0c\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u53ef\u662f\u91d1\u9010\u6d41\u7684\u4e49\u5144\u5389\u5357\u661f\u4e48\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u4e0d\u9519\u3002\u4f60\u4f1a\u4f7f\u6bd2\u96fe\u91d1\u9488\u70c8\u7130\u5f39\uff0c\u60f3\u5fc5\u662f\u674e\u6566\u5927\u54e5\u4e86\u3002\u4f46\u4f60\u5982\u600e\u4e48\u77e5\u9053\u5c0f\u5f1f \u662f\u5389\u5357\u661f\uff1f\u201d \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u6211\u8ba4\u5f97\u4f60\u8fd9\u628a\u7384\u94c1\u5b9d\u5251\u3002\u4f60\u4eec\u5728\u626c\u5dde\u5927\u95f9\u516d\u5408\u5e2e\u603b\u8235\u7684\u90a3\u5927\u665a\u4e0a\uff0c\u6211\u4e5f\u6b63\u5728 \u626c\u5dde\uff0c\u6211\u5df2\u7ecf\u89c1\u8fc7\u91d1\u9010\u6d41\u4e86\uff0c\u4ed6\u77e5\u9053\u4f60\u53d7\u4e86\u90a3\u59d3\u8d3a\u7684\u5996\u5a46\u6697\u7b97\uff0c\u5341\u5206\u4e3a\u4f60\u62c5\u5fc3\uff0c\u5e78\u559c\u4f60\u5df2\u5e73 \u5b89\u65e0\u4e8b\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u559c\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u89c1\u8fc7\u91d1\u9010\u6d41\u4e86\uff1f\u90a3\u4e48\u4f60\u73b0\u5728\u60f3\u5fc5\u662f\u8981\u5230\u897f\u660c\u53bb\u4f1a\u4ed6\u5427\uff1f\u201d \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u897f\u660c\u9632\u5b88\u5f97\u6781\u5176\u4e25\u5bc6\uff0c\u53ea\u6015\u82cd\u8747\u4e5f\u98de\u4e0d\u8fdb\u53bb\uff01\u201d \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u4e0d\u6015\uff0c\u6211\u6709\u529e\u6cd5\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u201c\u556a\u556a\u556a\u201d\u4e09\u4e0b\u638c\u58f0\uff0c\u5c71\u5773\u8f6c\u89d2\u5904\u73b0\u51fa\u4e24\u6761\u4eba\u5f71\u3002\u5389\u5357\u661f\u53ea\u9053\u6765\u7684\u53c8 \u662f\u654c\u4eba\uff0c\u6b63\u8981\u62d4\u5251\uff0c\u53ea\u89c1\u674e\u6566\u5df2\u7ecf\u8fce\u4e86\u4e0a\u53bb\uff0c\u56de\u4e86\u4e09\u4e0b\u638c\u58f0\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u4eba\u6c14\u5598\u5401\u5401\u5730\u8dd1\u6765\uff0c\u4e00\u6765\u5c31\u8bf4\uff1a\u201c\u674e\u5927\u54e5\uff0c\u4e0d\u597d\u4e86\uff01\u5509\uff0c\u6ca1\u6709\u529e\u6cd5\uff01\u201d \u3000\u3000\u8fd9\u65f6\u4ed6\u4eec\u65b9\u624d\u53d1\u73b0\u4e86\u5730\u4e0a\u7684\u5c38\u9996\u548c\u5389\u5357\u661f\uff0c\u4e0d\u7981\u90fd\u662f\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u4f4f\u53e3\u3002 \u3000\u3000\u674e\u6566\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u5389\u5927\u54e5\u662f\u54b1\u4eec\u7684\u597d\u670b\u53cb\u3002\u5730\u4e0a\u8fd9\u4e24\u4e2a\u4e00\u6b7b\u4e00\u4f24\u7684\u5bb6\u4f19\u662f\u5978\u76f8\u66f9\u632f\u8054\u7684 \u722a\u7259\uff0c\u521a\u624d\u6211\u8ba4\u9519\u4e86\u4eba\uff0c\u9669\u4e9b\u906d\u4e86\u4ed6\u4eec\u7684\u6bd2\u624b\uff0c\u5e78\u4e8f\u5f97\u8fd9\u4f4d\u5389\u5927\u54e5\u62d4\u5251\u76f8\u52a9\u3002\u201d \u3000\u3000\u90a3\u4e24\u4eba\u4e0d\u7ea6\u800c\u540c\u7684\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5bb6\u4f19\u4e0d\u5c31\u662f\u5f6d\u5de8\u5d58\u548c\u8fde\u57ce\u864e\u5417\uff1f\u201d \u3000\u3000\u674e\u6566\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u5230\u5427\uff0c\u8fd9\u4e24\u4e2a\u9ed1\u9053\u4e0a\u9f0e\u9f0e\u5927\u540d\u7684\u4eba\u7269\uff0c\u53ea\u4e0d\u8fc7\u624d\u4e00\u4e2a\u7167\u9762\uff0c\u5c31\u7ed9\u5389 \u5927\u54e5\u6740\u7684\u6740\u4e86\uff0c\u4f24\u7684\u4f24\u4e86\uff01\u201d \u3000\u3000\u90a3\u4e24\u4eba\u542c\u4e86\uff0c\u767b\u65f6\u5bf9\u5389\u5357\u661f\u53e6\u773c\u76f8\u770b\uff0c\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\u3002\u5389\u5357\u661f\u5fc3\u91cc\u5374\u662f\u6697\u6697\u53eb\u4e86\u4e00\u58f0 \u60ed\u6127\uff0c\u60f3\u9053\uff1a\u201c\u5018\u82e5\u4e0d\u662f\u6709\u7384\u94c1\u5b9d\u5251\u5728\u624b\uff0c\u53ea\u6015\u73b0\u5728\u53d7\u4f24\u7684\u5c31\u662f\u6211\u4e86\u3002\u201d \u3000\u3000\u674e\u6566\u8ddf\u7740\u4ecb\u7ecd\u90a3\u4e24\u4e2a\u4eba\uff1a\u201c\u8fd9\u4f4d\u662f\u5218\u5927\u54e5\uff0c\u8fd9\u4f4d\u662f\u5173\u5927\u54e5\u3002\u5173\u3001\u5218\u4e24\u4f4d\u5927\u54e5\u90fd\u662f\u81ea\u5df1 \u4eba\uff0c\u5728\u897f\u660c\u57ce\u91cc\u2018\u5367\u5e95\u2019\u7684\u3002\u54b1\u4eec\u8981\u8fdb\u897f\u660c\uff0c\u5c31\u9760\u4ed6\u4eec\u4e24\u4f4d\u63a5\u5e94\u4e86\u3002\u201d \u3000\u3000\u59d3\u5173\u7684\u90a3\u4e2a\u82e6\u7b11\u9053\uff1a\u201c\u53ea\u6015\u63a5\u5e94\u4e0d\u6765\u5566\uff01\u201d \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u4f60\u4eec\u5404\u81ea\u5e26\u4e00\u4f4d\u670b\u53cb\u8fdb\u53bb\u4e5f\u4e0d\u884c\u4e48\u3002\u201d \u3000\u3000\u59d3\u5218\u7684\u90a3\u4e2a\u8bf4\u9053\uff1a\u201c\u540e\u5929\u5c31\u662f\u5e05\u5b5f\u96c4\u7ed3\u5a5a\u7684\u65e5\u5b50\uff0c\u4ed6\u4e5f\u6015\u6709\u6c5f\u6e56\u597d\u6c49\u4e58\u673a\u6df7\u5165\u57ce\u4e2d\u6363\u4ed6 \u7684\u86cb\uff0c\u662f\u4ee5\u8fd9\u4e24\u5929\u7279\u522b\u4e25\u683c\uff0c\u53ea\u8bb8\u57ce\u91cc\u7684\u4eba\u51fa\u6765\uff0c\u4e0d\u8bb8\u57ce\u5916\u7684\u4eba\u8fdb\u53bb\u3002\u5c31\u662f\u4ed6\u624b\u4e0b\u519b\u5b98\u8981\u5e26 \u4eb2\u53cb\u8fdb\u57ce\uff0c\u4e5f\u5f97\u5411\u4ed6\u8bf7\u51c6\u624d\u884c\uff0c\u4f60\u4eec\u4e24\u4f4d\u90fd\u662f\u5916\u8def\u53e3\u97f3\u7684\u964c\u751f\u4eba\uff0c\u8fd9\u4e2a\u3001\u8fd9\u4e2a\uff0c\u53ea\u6015\u662f\u6ca1\u6709 \u529e\u6cd5\u597d\u60f3\u4e86\uff01\u201d \u3000\u3000\u674e\u6566\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5012\u6709\u4e00\u4e2a\u529e\u6cd5\u3002\u201d\u8bf4\u7f62\u5728\u8fde\u57ce\u864e\u8eab\u4e0a\u4e00\u641c\uff0c\u641c\u51fa\u4e00\u5323\u793c\u7269\u3002 \u3000\u3000\u6253\u5f00\u5323\u5b50\u4e00\u770b\uff0c\u53ea\u89c1\u662f\u4e00\u5bf9\u901a\u4f53\u78a7\u7eff\u7684\u7389\u897f\u74dc\u3002\u674e\u6566\u7b11\u9053\uff1a\u201c\u8fd9\u5bf9\u7389\u897f\u74dc\u5c11\u8bf4\u4e5f\u8981\u503c\u5f97 \u4e00\u4e07\u4e24\u94f6\u5b50\uff0c\u5bb0\u76f8\u9001\u7684\u793c\u7269\uff0c\u679c\u7136\u662f\u51fa\u624b\u4e0d\u51e1\uff0c\u4f46\u5374\u4e0d\u77e5\u8981\u641c\u62ec\u4e86\u591a\u5c11\u6c11\u8102\u6c11\u818f\u4e86\u3002\u201d \u3000\u3000\u5389\u5357\u660c\u9053\uff1a\u201c\u674e\u5927\u54e5\u7684\u610f\u601d\u6562\u60c5\u662f\u8981\u5192\u5145\u76f8\u5e9c\u9001\u793c\u7684\u4eba\u4e48\uff1f\u53ef\u662f\u53f2\u767d\u90fd\u548c\u5e05\u76c2\u96c4\u90fd\u662f\u8ba4 \u8bc6\u5c0f\u5f1f\u7684\u554a\uff01\u201d \u3000\u3000\u674e\u6566\u518d\u53bb\u641c\u4e86\u5f6d\u5de8\u5d58\u7684\u5c38\u4f53\uff0c\u5e76\u65e0\u53d1\u73b0\u4e66\u4fe1\uff0c\u90a3\u5206\u793c\u5355\u4e5f\u53ea\u662f\u7531\u66f9\u632f\u8054\u5177\u540d\uff0c\u5e76\u6ca1\u6ce8\u660e \u662f\u7531\u8c01\u9001\u6765\u3002 \u3000\u3000\u539f\u6765\u66f9\u632f\u8054\u56e0\u4e3a\u5f6d\u3001\u8fde\u4e8c\u4eba\u90fd\u662f\u6c5f\u6e56\u5927\u76d7\u51fa\u8eab\uff0c\u548c\u5973\u65b9\u7684\u5bb6\u957f\u53f2\u767d\u90fd\u53c8\u662f\u7d20\u6765\u76f8\u8bc6\uff0c\u76f8 \u5e9c\u4e2d\u6536\u5bb9\u6709\u6c5f\u6e56\u5927\u76d7\uff0c\u8fd9\u662f\u4e2a\u4e0d\u80fd\u516c\u5f00\u7684\u79d8\u5bc6\uff0c\u66f9\u632f\u8054\u4e0d\u613f\u610f\u6709\u628a\u67c4\u843d\u5728\u4eba\u5bb6\u624b\u65f6\uff0c\u65e2\u7136\u53f2 \u767d\u90fd\u4e0e\u8fd9\u4e24\u4eba\u76f8\u8bc6\uff0c\u4ed6\u81ea\u662f\u4ee5\u4e0d\u843d\u6587\u5b57\u4e3a\u597d\u3002 \u3000\u3000\u674e\u6566\u7b11\u9053\uff1a\u201c\u66f9\u632f\u8054\u6ca1\u6709\u5199\u660e\u7531\u8c01\u9001\u793c\uff0c\u9001\u793c\u7684\u5171\u6709\u51e0\u4eba\uff0c\u8fd9\u5c31\u6709\u529e\u6cd5\u53ef\u60f3\u4e86\u3002\u54b1\u4eec\u53ef \u4ee5\u6539\u5bb9\u6613\u8c8c\uff0c\u5192\u5145\u8fde\u57ce\u864e\u7684\u968f\u4ece\u3002\u201d \u3000\u3000\u5173\u3001\u5218\u4e8c\u4eba\u90fd\u62cd\u624b\u9053\uff1a\u201c\u8fd9\u4e2a\u6cd5\u5b50\u5999\uff0c\u7ed3\u5a5a\u524d\u5915\uff0c\u5e05\u76c2\u96c4\u4e00\u5b9a\u662f\u5fd9\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u76f8\u5e9c \u7684\u4f7f\u8005\u4ed6\u662f\u8981\u4ee5\u4e0a\u5bbe\u4e4b\u793c\u63a5\u5f85\u7684\uff0c\u4f7f\u8005\u7684\u968f\u4ece\u5c31\u53ea\u80fd\u4f4f\u5728\u5bbe\u9986\u671b\uff0c\u7531\u4ed6\u7684\u4e0b\u4eba\u62db\u547c\u4e86\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u8fde\u57ce\u864e\u4f1a\u4e56\u4e56\u7684\u4efb\u7531\u54b1\u4eec\u6446\u5e03\u5417\uff1f\u201d \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u5389\u5927\u54e5\u7cbe\u901a\u6bd2\u529f\uff0c\u5c82\u4e0d\u95fb\u6709\u4ee5\u6bd2\u653b\u6bd2\u7684\u6cd5\u95e8\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\u6211\u56e0\u4e3a\u8ba8\u538c\u4f7f\u6bd2\u5bb3\u4eba\uff0c\u968f\u8eab\u5e76\u6ca1\u643a\u5e26\u6bd2\u7269\u3002\u201d \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u6bd2\u7269\u6709\u5982\u5200\u5251\uff0c\u53ea\u8981\u7528\u5f97\u5176\u5f53\uff0c\u90a3\u53c8\u4f55\u59a8\uff1f\u5389\u5927\u54e5\uff0c\u8bf7\u4f60\u5148\u7ed9\u8fd9\u53ae\u89e3\u5f00\u7a74 \u9053\u3002\u201d\u5389\u5357\u661f\u542c\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5c31\u77e5\u4ed6\u7684\u8eab\u4e0a\u5b9a\u7136\u5907\u6709\u3002 \u3000\u3000\u679c\u7136\u5728\u5389\u5357\u661f\u89e3\u5f00\u8fde\u57ce\u864e\u7684\u7a74\u9053\u7684\u540c\u65f6\uff0c\u674e\u6566\u53cc\u6307\u4e00\u6309\uff0c\u4e00\u679a\u5c0f\u5c0f\u7684\u6bd2\u9488\uff0c\u63d2\u8fdb\u4e86\u8fde\u57ce \u864e\u7684\u8eab\u4f53\u3002 \u3000\u3000\u8fde\u57ce\u864e\u53ea\u89c9\u80f8\u53e3\u5fae\u9ebb\uff0c\u8f6c\u77ac\u5373\u8fc7\u3002\u4ed6\u662f\u4e2a\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u60c5\u77e5\u6bd2\u6027\u8d8a\u662f\u5389\u5bb3\uff0c\u8eab\u4e0a\u8d8a \u662f\u6ca1\u6709\u75db\u695a\u7684\u611f\u89c9\uff0c\u4e0d\u7981\u5927\u6012\u9053\uff1a\u201c\u4f60\u8981\u6740\u4fbf\u6740\uff0c\u56e0\u4f55\u5c06\u6211\u6298\u78e8\uff1f\u201d \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u4f60\u5e26\u6211\u4eec\u4e8c\u4eba\u8fdb\u53bb\uff0c\u4e8b\u60c5\u8fc7\u540e\uff0c\u51fa\u57ce\u4e4b\u65f6\uff0c\u6211\u7ed9\u4f60\u89e3\u836f\uff0c\u5426\u5219\u4f60\u7684\u6027\u547d\u5c31\u53ea \u6709\u4e09\u5929\u4e86\u3002\u201d \u3000\u3000\u8fde\u57ce\u864e\u534a\u4fe1\u534a\u7591\uff0c\u6c89\u541f\u4e0d\u8bed\u3002 \u3000\u3000\u674e\u6566\u9053\uff1a\u201c\u6211\u4eec\u4e49\u519b\u7684\u4eba\uff0c\u8a00\u51fa\u5fc5\u884c\u3002\u4f60\u53c8\u4e0d\u662f\u6ca1\u6709\u548c\u4e49\u519b\u7684\u4eba\u6253\u8fc7\u4ea4\u9053\uff0c\u5c82\u80fd\u4e0d \u77e5\uff1f\u201d \u3000\u3000\u8fde\u57ce\u864e\u5fc3\u4e71\u5982\u9ebb\uff0c\u4f9d\u7136\u4e0d\u8bed\uff0c\u674e\u6566\u731c\u4e2d\u4ed6\u7684\u5fc3\u601d\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u7136\u8fd9\u6869\u4e8b\u60c5\u8fc7\u540e\uff0c\u4f60\u662f\u4e0d \u80fd\u518d\u56de\u76f8\u5e9c\u7684\u4e86\u3002\u4f46\u4f60\u672c\u6765\u662f\u6b66\u5b66\u4e16\u5bb6\uff0c\u53c8\u4f55\u82e6\u505a\u4eba\u5bb6\u7684\u5974\u624d\uff1f\u65f6\u523b\u8fd8\u8981\u62c5\u5fc3\u6709\u4eba\u53d6\u4f60\u6027 \u547d\uff1f\u4f60\u4ece\u6b64\u6539\u90aa\u5f52\u6b63\uff0c\u5bcc\u8d35\u867d\u7136\u4e0e\u4f60\u65e0\u7f18\uff0c\u81f3\u5c11\u5728\u665a\u4e0a\u5374\u662f\u53ef\u4ee5\u5b89\u5fc3\u7761\u89c9\u4e86\uff0c\u8fd9\u53c8\u6709\u4ec0\u4e48\u4e0d \u597d\uff1f\u201d \u3000\u3000\u8fde\u57ce\u864e\u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u7ea2\uff0c\u6068\u4e0d\u5f97\u811a\u5e95\u4e0b\u6709\u4e2a\u5730\u6d1e\u94bb\u8fdb\u53bb\u3002\u8981\u77e5\u8fde\u57ce\u864e\u867d\u7136\u4e5f\u662f\u6295\u8eab\u76f8 \u5e9c\uff0c\u4f46\u548c\u5f6d\u5de8\u5d58\u6bd5\u7adf\u662f\u6709\u6240\u4e0d\u540c\u3002\u5f6d\u5de8\u5d58\u662f\u5229\u7984\u4e4b\u5fc3\uff0c\u4e0d\u60dc\u80cc\u53db\u5e08\u95e8\uff0c\u7518\u4e3a\u9e70\u72ac\uff1b\u8fde\u57ce\u864e\u672c \u6765\u662f\u5f0f\u5b66\u4e16\u5bb6\u7684\u5b50\u5f1f\uff0c\u53ea\u56e0\u8ba4\u8bc6\u4e0d\u6e05\uff0c\u8bef\u4ea4\u532a\u4eba\uff0c\u8fd9\u624d\u4e00\u6b65\u6b65\u8d70\u5165\u6b67\u9014\u7684\u3002\u8fd9\u51e0\u5e74\u6765\u5728\u5b98\u573a \u4e2d\u4ed6\u4e5f\u66fe\u53d7\u5230\u4e86\u8bb8\u591a\u7a9d\u56ca\u6c14\uff0c\u6bcf\u5f53\u6e05\u591c\u81ea\u601d\uff0c\u672a\u5c1d\u4e5f\u4e0d\u611f\u5230\u6709\u8fb1\u5bb6\u95e8\u3002 \u3000\u3000\u674e\u6566\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5230\u4e86\u4ed6\u7684\u5fc3\u574e\u4e0a\uff0c\u4e0d\u7531\u5f97\u4ed6\u4e0d\u6697\u81ea\u7f9e\u60ed\uff0c\u60f3\u9053\uff1a\u201c\u5927\u4e0d\u4e86\u662f\u4e2a\u6b7b\uff0c\u4e0e\u5176 \u7ed9\u4eba\u9a82\u4f5c\u9e70\u72ac\uff0c\u4e00\u5200\u5bb0\u4e86\uff0c\u5012\u4e0d\u5982\u4e3a\u4e86\u5e2e\u5fd9\u4fa0\u4e49\u9053\u800c\u6b7b\uff0c\u8fd8\u53ef\u4ee5\u7559\u4e2a\u7f8e\u540d\u3002\u4f55\u51b5\u4e5f\u672a\u5fc5\u5c31\u4f1a \u6b7b\u5462\uff01\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u80f8\u4e2d\u8c41\u7136\u5f00\u6717\uff0c\u62b9\u4e86\u628a\u6c57\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u542c\u674e\u5927\u54e5\u7684\u5429\u5490\uff01\u201d \u3000\u3000\u674e\u6566\u64c5\u4e8e\u6539\u5bb9\u6613\u8c8c\u4e4b\u672f\uff0c\u4e8e\u662f\u4e24\u4eba\u626e\u4f5c\u4e86\u8fde\u57ce\u864e\u7684\u968f\u4ece\uff0c\u679c\u7136\u987a\u5229\u7684\u8fdb\u4e86\u897f\u660c\u3002 \u3000\u3000\u8fdb\u57ce\u4e4b\u540e\uff0c\u672c\u6765\u5e94\u8be5\u5148\u5230\u5ba2\u6808\u6b47\u606f\uff0c\u7136\u540e\u7531\u8fde\u57ce\u864e\u5230\u5c06\u519b\u5e9c\u9001\u793c\u7684\u3002\u6309\u7167\u674e\u6566\u7684\u60f3\u6cd5\uff0c \u8fd9\u4e24\u5929\u9001\u793c\u7684\u4eba\u4e00\u5b9a\u5f88\u591a\uff0c\u867d\u8bf4\u76f8\u5e9c\u4f7f\u8005\u7684\u8eab\u4efd\u4e0d\u6bd4\u5bfb\u5e38\uff0c\u4f46\u4ee5\u5e05\u5b5f\u96c4\u7684\u8eab\u4efd\uff0c\u4e5f\u672a\u5fc5\u5c31\u4f1a \u5148\u6765\u5ba2\u6808\u62dc\u89c1\u4f7f\u8005\uff0c\u9876\u591a\u662f\u5728\u8fde\u57ce\u864e\u5230\u8fbe\u5c06\u519b\u5e9c\u7684\u65f6\u5019\uff0c\u4ed6\u6253\u5f00\u4e2d\u95e8\uff0c\u5355\u72ec\u63a5\u89c1\uff0c\u5df2\u7b97\u5f97\u662f \u4f18\u793c\u6709\u52a0\u4e86\u3002 \u3000\u3000\u54ea\u77e5\u674e\u6566\u53ea\u6599\u5230\u4e86\u4e00\u534a\u3002\u5e05\u5b5f\u96c4\u6ca1\u6709\u6765\uff0c\u4f46\u4ed6\u5374\u8bf7\u53f2\u767d\u90fd\u517c\u4f5c\u4ed6\u7684\u4ee3\u8868\uff0c\u5148\u6765\u8fce\u63a5\u3002 \u3000\u3000\u4ed6\u4eec\u672a\u5230\u5ba2\u6808\uff0c\u53f2\u767d\u90fd\u5df2\u7ecf\u5728\u90a3\u91cc\u7b49\u5019\u3002\u4ed6\u548c\u8fde\u57ce\u864e\u672c\u6765\u662f\u76f8\u8bc6\u7684\uff0c\u4e00\u89c1\u4e86\u8fde\u57ce\u864e\uff0c\u4fbf \u5373\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u3002\u4f46\u4f60\u548c\u8001\u5f6d\u4e00\u5411\u662f\u7126\u4e0d\u79bb\u5b5f\uff0c\u5b5f\u4e0d\u79bb\u7126\u707c\uff0c\u600e\u7684\u8fd9\u4e00\u6b21\u8001\u5f6d\u5374\u6ca1 \u6709\u6765\uff1f\u201d \u3000\u3000\u8fde\u57ce\u864e\u53ea\u597d\u4e34\u65f6\u7f16\u9020\u8c0e\u8bdd\uff1a\u201c\u66f9\u516c\u5b50\u8bf4\u8981\u8fdb\u4eac\uff0c\u76f8\u7237\u6d3e\u8001\u5f6d\u5230\u6d4e\u5357\u4f5c\u4ed6\u516c\u5b50\u7684\u4fdd\u9556\u53bb \u4e86\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u8fde\u5144\uff0c\u4f60\u8fd9\u6b21\u6765\u4e86\uff0c\u53ef\u5f97\u591a\u9017\u7559\u51e0\u65e5\u624d\u597d\uff0c\u4f60\u662f\u5929\u4e0b\u7b2c\u4e00\u70b9\u7a74 \u540d\u5bb6\uff0c\u96be\u5f97\u6709\u8fd9\u673a\u4f1a\u4e0e\u4f60\u76f8\u805a\uff0c\u6211\u8fd8\u60f3\u5411\u4f60\u8ba8\u4e00\u4efd\u793c\u7269\u5462\u3002\u4f60\u6307\u70b9\u6211\u51e0\u8def\u7b14\u6cd5\u884c\u4e0d\u884c\uff1f\u201d \u3000\u3000\u6b66\u5b66\u540d\u5bb6\u4f1a\u9762\uff0c\u5c11\u4e0d\u514d\u8981\u6367\u4e00\u6367\u5bf9\u65b9\u7684\u7edd\u6280\uff0c\u8fd9\u5728\u53f2\u767d\u90fd\u7eaf\u7136\u662f\u4e00\u79cd\u5ba2\u6c14\u7684\u8bf4\u8bdd\uff0c\u5f53\u7136 \u5e76\u975e\u771f\u7684\u8981\u4ed6\u6307\u70b9\uff0c\u4f46\u8a00\u82e5\u65e0\u5fc3\uff0c\u542c\u8005\u6709\u610f\uff0c\u8fde\u57ce\u864e\u542c\u4e86\uff0c\u5374\u662f\u4e0d\u7531\u5f97\u9762\u4e0a\u4e00\u7ea2\u3002\u4ed6\u7684\u90a3\u4e00 \u5bf9\u5224\u5b98\u7b14\u5df2\u7ecf\u7ed9\u5389\u5357\u661f\u7684\u7384\u94c1\u5b9d\u5251\u65a9\u65ad\u4e86\uff0c\u624b\u91cc\u8fd8\u80fd\u6307\u70b9\u4ec0\u4e48\u201c\u7b14\u6cd5\u201d\u5462\uff1f\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53ef \u83ab\u7ed9\u4ed6\u770b\u51fa\u7834\u7efd\u624d\u597d\u3002\u201d\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u8fd9\u70b9\u5fae\u672b\u4e4b\u6280\uff0c\u600e\u6562\u5728\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\u7684\u9762\u524d\u732e \u62d9\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u662f\u4e2a\u6b66\u5b66\u5927\u884c\u5bb6\uff0c\u542c\u5f97\u8fde\u57ce\u864e\u8fd9\u4e48\u8bf4\uff0c\u7a0d\u5fae\u7559\u610f\uff0c\u5c31\u77a7\u51fa\u4e86\u4ed6\u7684\u8eab\u4e0a\u6ca1\u5e26\u5175\u5668\uff0c \u4e0d\u89c9\u6709\u70b9\u5947\u602a\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u70b9\u5927\u540d\u5bb6\u600e\u7684\u628a\u5224\u5b98\u7b14\u4e5f\u4e22\u4e86\uff1f\u8fd9\u4e0d\u597d\u4f3c\u505a\u5b98\u7684\u5fd8\u8bb0\u5e26\u5370 \u5417\uff1f\u201d \u3000\u3000\u8fde\u57ce\u864e\u5c34\u5c2c\u7b11\u9053\uff1a\u201c\u897f\u660c\u57ce\u4e2d\uff0c\u9ad8\u624b\u5982\u4e91\uff0c\u6211\u5230\u4e86\u8fd9\u513f\uff0c\u4f55\u987b\u518d\u5e26\u5175\u5668\uff1f\u201d\u6b64\u8bdd\u5b9e\u662f\u4e0d \u80fd\u81ea\u5706\u5176\u8bf4\uff0c\u53f2\u767d\u90fd\u5fc3\u60f3\uff1a\u201c\u5728\u8fd9\u513f\u4f60\u53ef\u4ee5\u4e0d\u5e26\u5175\u5668\uff0c\u96be\u9053\u5728\u8def\u4e0a\u4f60\u4e5f\u53ef\u4ee5\u8fd9\u6837\u6258\u5927\uff1f\u201d\u4e0d \u8fc7\u8fde\u57ce\u864e\u662f\u66f9\u76f8\u56fd\u7684\u62a4\u9662\uff0c\u8fd9\u8eab\u4efd\u53ef\u662f\u5047\u4e0d\u4e86\u7684\u3002\u53f2\u767d\u90fd\u867d\u7136\u60f3\u5230\u5176\u4e2d\u5b9a\u6709\u8e4a\u8df7\uff0c\u4f46\u4ed6\u600e\u4e5f \u4e0d\u6562\u60f3\u5230\u8fde\u57ce\u864e\u6b64\u6765\u5c06\u5bf9\u5e05\u5b5f\u96c4\u6709\u6240\u4e0d\u5229\u3002 \u3000\u3000\u53f2\u767d\u90fd\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u6b64\u9645\u4eba\u591a\uff0c\u6b63\u5f85\u4ed6\u5230\u4e86\u5c06\u519b\u5e9c\u91cc\uff0c\u6211\u518d\u4ed4\u7ec6\u95ee\u4ed6\u3002\u201d \u3000\u3000\u8fde\u57ce\u864e\u751f\u6015\u9732\u51fa\u9a6c\u811a\uff0c\u8d76\u5fd9\u8bf4\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\uff0c\u4ee4\u59b9\u660e\u65e5\u6210\u5a5a\uff0c\u4f60\u4e00\u5b9a\u662f\u8d35\u4eba\u4e8b\u5fd9\u7684\u4e86\u3002 \u6211\u4e0d\u6562\u591a\u82b1\u4f60\u7684\u65f6\u95f4\uff0c\u6211\u5728\u5ba2\u6808\u5378\u4e0b\u884c\u674e\uff0c\u5c31\u53bb\u62dc\u89c1\u5e05\u5c06\u519b\uff0c\u8bf7\u4f60\u5148\u56de\u5e9c\u5427\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u773c\u5149\u4e00\u77a5\uff0c\u76ee\u5149\u4ece\u674e\u6566\u7684\u8eab\u4e0a\u8f6c\u5230\u5389\u5357\u661f\u7684\u8eab\u4e0a\uff0c\u5fc3\u4e2d\u66f4\u662f\u611f\u5230\u8be7\u5f02\uff1a\u201c\u8fd9\u4e24\u4eba \u597d\u50cf\u662f\u5728\u54ea\u91cc\u89c1\u8fc7\u4f3c\u7684\uff1f\u201d\u8bf4\u9053\uff1a\u201c\u4e0d\u5fd9\uff0c\u4e0d\u5fd9\u3002\u8fd9\u4e24\u4f4d\u662f\u2026\u2026\u201d \u3000\u3000\u8fde\u57ce\u864e\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u6211\u7684\u968f\u4ece\uff0c\u4e00\u5411\u5728\u76f8\u5e9c\u5f53\u5dee\u7684\u3002\u55ef\uff0c\u4f60\u4eec\u600e\u4e48\u8fd9\u6837\u4e0d\u61c2\u793c\u8c8c\uff0c\u53f2\u5e2e \u4e3b\u5728\u95ee\u4f60\u4eec\uff0c\u4f60\u4eec\u8fd8\u4e0d\u4e0a\u6765\u56de\u8bdd\uff01\u201d\u674e\u6566\u548c\u5389\u5357\u661f\u7684\u771f\u59d3\u540d\u5f53\u7136\u662f\u4e0d\u80fd\u8bf4\u7684\uff0c\u8fde\u57ce\u864e\u60c5\u77e5\u8eb2 \u907f\u4e0d\u4e86\uff0c\u6050\u6015\u88ab\u81ea\u5df1\u8bf4\u9519\u4e86\u8bdd\uff0c\u53ea\u597d\u63a8\u7ed9\u4ed6\u4eec\u56de\u7b54\u3002 \u3000\u3000\u674e\u3001\u5389\u4e8c\u4eba\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u4e0a\u524d\u89c1\u793c\uff0c\u5404\u81ea\u80e1\u4e71\u634f\u4e86\u4e00\u4e2a\u5047\u540d\u3002\u53f2\u767d\u90fd\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5bb0 \u76f8\u5bb6\u4eba\u4e03\u54c1\u5b98\uff0c\u4f60\u4eec\u4e0d\u5fc5\u591a\u793c\uff0c\u6211\u627f\u53d7\u4e0d\u8d77\u3002\u563f\uff0c\u563f\uff0c\u53ea\u6015\u6211\u65e5\u540e\u8fd8\u6709\u501f\u91cd\u4f60\u4eec\u7684\u5730\u65b9\u5462\uff0c \u54b1\u4eec\u4eb2\u8fd1\u4eb2\u8fd1\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u7684\u7384\u94c1\u5b9d\u5251\u85cf\u5728\u8eab\u4e0a\uff0c\u5f53\u7136\u7792\u4e0d\u8fc7\u53f2\u767d\u90fd\u7684\u773c\u775b\uff0c\u662f\u4ee5\u53f2\u767d\u90fd\u5148\u4e0e\u4ed6\u63e1\u624b\uff0c\u8bd5\u8bd5 \u4ed6\u7684\u672c\u9886\u3002 \u3000\u3000\u53cc\u624b\u4e00\u63e1\uff0c\u53f2\u767d\u90fd\u5410\u51fa\u4e86\u4e09\u5206\u5185\u529b\uff0c\u53cc\u6307\u53c8\u642d\u4e0a\u4e86\u4ed6\u7684\u8109\u95e8\uff0c\u770b\u4ed6\u53cd\u5e94\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u5389\u6d77\u661f\u5f53\u771f\u662f\u9762\u4e34\u751f\u6b7b\u5173\u5934\uff0c\u906d\u9047\u4e86\u6700\u4e25\u91cd\u7684\u8003\u9a8c\uff01 \u3000\u3000\u4ed6\u4e0d\u77e5\u53f2\u767d\u90fd\u662f\u5426\u5df2\u7ecf\u8ba4\u51fa\u4e86\u4ed6\uff0c\u5982\u679c\u4e0d\u8fd0\u5185\u529b\u62b5\u6297\uff0c\u6050\u6015\u53f2\u767d\u90fd\u6697\u4e0b\u6bd2\u624b\uff0c\u4f46\u5982\u679c\u4e00 \u8fd0\u5185\u529b\uff0c\u8eab\u4efd\u7acb\u5373\u4fbf\u4f1a\u6cc4\u9732\u3002\u4ed6\u662f\u66fe\u7ecf\u548c\u53f2\u767d\u90fd\u4ea4\u8fc7\u51e0\u6b21\u624b\u7684\uff0c\u4ed6\u8fd9\u4e00\u8eab\u6b63\u90aa\u5408\u4e00\u7684\u5185\u529f\u53ef \u7792\u4e0d\u4e86\u53f2\u767d\u90fd\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\u5389\u5357\u661f\u8f6c\u4e86\u597d\u51e0\u4e2a\u5ff5\u5934\uff0c\u7ec8\u4e8e\u8fd8\u662f\u51b3\u5b9a\u5192\u4e00\u5192\u9669\uff0c\u88c5\u4f5c\u4e0d\u77e5\u5185\u529f\u7684\u4eba\u201c\u54df\u201d\u4e00 \u58f0\u53eb\u4e86\u51fa\u6765\uff1a\u201c\u53f2\u5e2e\u4e3b\u597d\u5927\u7684\u529b\u6c14\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u867d\u7136\u8d77\u4e86\u7591\u5fc3\uff0c\u4f46\u4e5f\u6709\u70b9\u5bb3\u6015\u4f24\u4e86\u76f8\u5e9c\u7684\u5bb6\u4eba\u53ef\u4e0d\u662f\u5f53\u800d\u7684\uff0c\u89c1\u4ed6\u7684\u786e\u4e0d\u4f1a\u5185 \u529f\uff0c\u9042\u54c8\u54c8\u4e00\u7b11\uff0c\u677e\u5f00\u4e86\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u4eba\u7c97\u9c81\u60ef\u4e86\uff0c\u8001\u54e5\u4f60\u522b\u89c1\u602a\uff0c\u4e0d\u77e5\u8001\u54e5\u662f\u54ea\u4e00\u6d3e \u7684\u5f1f\u5b50\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u6211\u5728\u76f8\u5e9c\u80e1\u4e71\u8ddf\u51e0\u4f4d\u6559\u5e08\u7237\u5b66\u8fc7\u51e0\u624b\u4e09\u811a\u732b\u7684\u529f\u592b\uff0c\u5374\u4e0d\u77e5\u4ed6\u4eec\u662f\u54ea\u4e00\u6d3e \u7684\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u8001\u54e5\u662f\u7528\u5251\u7684\u5427\uff0c\u53ef\u5426\u501f\u4f60\u7684\u5b9d\u5251\u4e00\u89c2\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u600e\u6562\u628a\u7384\u94c1\u5b9d\u5251\u53d6\u51fa\u6765\u4ea4\u7ed9\u4ed6\u770b\uff0c\u5f53\u4e0b\u5f3a\u81ea\u9707\u6151\u5fc3\u60c5\u8bf4\u9053\uff1a\u201c\u5728\u5175\u5668\u5e97\u58c1\u4e70\u7684\u4e00 \u628a\u666e\u666e\u901a\u901a\u7684\u9752\u94a2\u5251\uff0c\u4e0d\u503c\u5e2e\u4e3b\u4e00\u770b\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u770b\u770b\u4f55\u59a8\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u7ed9\u4ed6\u5f3a\u8feb\u4e0d\u8fc7\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ed6\u82e5\u4e00\u5b9a\u8981\u770b\uff0c\u6211\u53ea\u597d\u5c31\u5728\u6b64\u5904\u4e0e\u4ed6\u62fc\u547d\u4e86\u3002\u201d \u3000\u3000\u6b63\u8981\u62d4\u5251\uff0c\u5ffd\u89c1\u516d\u5408\u5e2e\u56db\u5927\u9999\u4e3b\u4e4b\u4e00\u7684\u5706\u6d77\u548c\u5c1a\u5306\u5306\u8dd1\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5e2e\u4e3b\uff0c\u8463\u5341\u4e09\u5a18\u8bf7 \u4f60\u8d76\u5feb\u56de\u53bb\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u5706\u6d77\u9053\uff1a\u201c\u8fd9\u4e2a\u3001\u8fd9\u4e2a\u6211\u4e5f\u4e0d\u5927\u6e05\u695a\uff0c\u542c\u8bf4\u662f\u4ee4\u59b9\u7684\u4e8b\u60c5\uff0c\u8463\u5341\u4e09\u5a18\u4e0d\u6562\u4f5c\u4e3b\uff0c\u975e\u5f97\u548c \u5e2e\u4e3b\u9762\u8bf4\u4e0d\u884c\u3002\u201d\u5706\u6d77\u8bf4\u8bdd\u541e\u541e\u5410\u5410\uff0c\u4f3c\u4e4e\u662f\u6709\u751a\u4e3a\u96be\u4e4b\u4e8b\uff0c\u4e0d\u4fbf\u5f53\u7740\u5916\u4eba\u7684\u9762\u544a\u8bc9\u53f2\u767d \u90fd\u3002 \u3000\u3000\u53f2\u767d\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u83ab\u975e\u8fd9\u4e2b\u5934\u53c8\u5728\u5bfb\u6b7b\u89c5\u6d3b\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u987a\u624b\u63a8\u821f\uff0c\u8bf4\u9053\uff1a\u201c\u5e2e\u4e3b\u6709\u4e8b\uff0c\u5f85\u4f1a\u513f\u6211\u5230\u8d35\u5904\uff0c\u518d\u987a\u4fbf\u8bf7\u5e2e\u4e3b\u6307\u6559\u6211\u51e0\u8def\u5251 \u6cd5\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u5fc3\u60f3\u8fd9\u4e24\u4e2a\u968f\u4ece\u5373\u4f7f\u662f\u5047\u5192\u7684\uff0c\u5728\u8fd9\u897f\u660c\u57ce\u4e2d\u4e5f\u662f\u63d2\u7fc5\u96be\u98de\u3002\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u4e0d\u6562 \u6709\u52b3\u5927\u9a7e\uff0c\u8bf7\u4e24\u4f4d\u5927\u54e5\u5728\u5ba2\u6808\u7a0d\u5019\uff0c\u6211\u53bb\u53bb\u5c31\u6765\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u56de\u5230\u5bd3\u6240\uff0c\u89c1\u4e86\u8463\u5341\u4e09\u5a18\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u51fa\u4e86\u4ec0\u4e48\u4e8b\u4e86\uff1f\u201d\u8463\u5341\u4e09\u5a18\u51b7\u7b11\u9053\uff1a \u201c\u8fd9\u4e2b\u5934\u7684\u82b1\u6837\u591a\u7740\u5462\uff0c\u4f60\u81ea\u5df1\u95ee\u5979\u5427\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u8d70\u5165\u59b9\u59b9\u7684\u623f\u95f4\uff0c\u89c1\u53f2\u7ea2\u82f1\u6b63\u5728\u5bf9\u955c\u68b3\u5986\uff0c\u795e\u8272\u5982\u5e38\u3002\u53f2\u767d\u90fd\u7a0d\u7a0d\u653e\u4e0b\u4e86\u5fc3\uff0c\u8bf4 \u9053\uff1a\u201c\u59b9\u59b9\uff0c\u540e\u5929\u5c31\u662f\u4f60\u5927\u559c\u4e4b\u65e5\u4e86\uff0c\u4f60\u53ef\u4e0d\u8981\u80e1\u95f9\u554a\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u8c01\u80e1\u95f9\u4e86\uff1f\u4f46\u4f60\u8981\u60f3\u6211\u6210\u5168\u4f60\u7684\u529f\u540d\u5bcc\u8d35\uff0c\u4f60\u4eec\u4e5f\u5f97\u7b54\u5e94\u6211\u51e0\u6869\u4e8b\u60c5\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u8d54\u7b11\u9053\uff1a\u201c\u59b9\u59b9\uff0c\u4f60\u53ef\u4e0d\u8981\u51fa\u4ec0\u4e48\u96be\u9898\u554a\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u662f\u5426\u96be\u9898\uff0c\u4f46\u4f9d\u6211\u60f3\u4f60\u4eec\u662f\u5f88\u5bb9\u6613\u529e\u5230\u7684\u3002\u201d\u53f2\u767d\u90fd\u9053\uff1a\u201c\u529e\u4e0d \u5230\u5462\uff1f\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u8fd9\u51e0\u6837\u5bb9\u6613\u7684\u4e8b\u60c5\u4f60\u4eec\u90fd\u529e\u4e0d\u5230\uff0c\u90a3\u5c31\u4f11\u60f3\u6211\u5ac1\u7ed9\u5e05\u5b5f\u96c4\u3002\u201d\u53f2\u767d\u90fd \u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u4f60\u8bf4\u6765\u542c\u542c\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u516d\u5408\u5e2e\u662f\u6c5f\u6e56\u4e0a\u4ec5\u6b21\u4e8e\u4e10\u5e2e\u7684\u4e00\u5927\u5e2e\u4f1a\uff0c\u5e05\u5b5f\u96c4\u662f\u624b\u63e1\u91cd\u5175\uff0c\u9547\u5b88\u4e00\u65b9\u7684 \u5c06\u519b\uff0c\u8fd9\u6b21\u5a5a\u4e8b\u4e00\u5b9a\u8981\u529e\u5f97\u5341\u5206\u98ce\u5149\u70ed\u95f9\u624d\u884c\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\uff0c\u4f55\u987b\u4f60\u505a\u65b0\u5a18\u5b50\u7684\u64cd\u5fc3\uff0c\u5c06\u519b\u5e9c\u7684\u4eba\u81ea\u4f1a\u7ed9\u4f60\u529e\u5f97\u5341\u5206 \u98ce\u5149\u70ed\u95f9\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u90a3\u4f55\u4ee5\u53c8\u5c06\u897f\u660c\u57ce\u95e8\u5173\u95ed\uff0c\u4e0d\u8bb8\u767e\u59d3\u8fdb\u57ce\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u8c01\u544a\u8bc9\u4f60\u7684\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u7ba1\u6211\u5982\u4f55\u77e5\u9053\uff0c\u8fd9\u4e8b\u603b\u662f\u771f\u7684\u5427\uff1f\u201d \u3000\u3000\u53f2\u81ea\u90fd\u5fc3\u60f3\uff1a\u201c\u4e0d\u77e5\u662f\u54ea\u4e2a\u591a\u5634\u7684\u4e0b\u4eba\u544a\u8bc9\u4e86\u5979\uff0c\u597d\u5728\u8fd9\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u4e8b\u3002\u201d\u4e8e\u662f\u8bf4 \u9053\uff1a\u201c\u59b9\u59b9\u6709\u6240\u4e0d\u77e5\uff0c\u8fd9\u6b63\u662f\u5e05\u5c06\u519b\u4e3a\u4e86\u8981\u4f7f\u540e\u5929\u7684\u559c\u4e8b\u4e0d\u51fa\u4e71\u5b50\uff0c\u624d\u8fd9\u6837\u5c0f\u5fc3\u8c28\u614e\u7684\u554a\uff01 \u4f60\u60f3\u60f3\uff0c\u897f\u660c\u57ce\u5916\u4e0d\u8fc7\u4e00\u767e\u591a\u5ea7\u7684\u5927\u51c9\u5c71\uff0c\u5c31\u6709\u7afa\u5c1a\u7236\u8fd9\u80a1\u5f3a\u76d7\uff0c\u5018\u82e5\u6253\u5f00\u57ce\u95e8\uff0c\u7ed9\u5f3a\u76d7\u6df7 \u8fdb\u6765\u4e86\uff0c\u6717\u4f7f\u4e0d\u80fd\u5174\u98ce\u4f5c\u6d6a\uff0c\u4e5f\u603b\u662f\u5927\u6740\u98ce\u666f\u7684\u554a\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u51b7\u7b11\u9053\uff1a\u201c\u5173\u8d77\u95e8\u6765\u5077\u5077\u6478\u6478\u7684\u529e\u559c\u4e8b\uff0c\u8fd8\u6709\u4ec0\u4e48\u98ce\u5149\u70ed\u95f9\u53ef\u8a00\uff1f\u897f\u660c\u57ce\u4e2d\u6709\u5e05 \u5b5f\u96c4\u7684\u5341\u4e07\u5927\u519b\uff0c\u53c8\u6709\u4f60\u8fd9\u4f4d\u81ea\u8d1f\u662f\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\u7684\u516d\u5408\u5e2e\u5e2e\u4e3b\uff0c\u7adf\u4f1a\u6015\u4eba\u6363\u4e71\u4e0d\u6210\uff1f\u54fc\uff0c \u4f20\u4e86\u51fa\u53bb\uff0c\u7ed9\u6c5f\u6e56\u597d\u6c49\u77e5\u9053\uff0c\u5c82\u4e0d\u7b11\u8bdd\uff01\u53ea\u6015\u4ed6\u4eec\u4e0d\u4f1a\u79f0\u8d5e\u5e05\u5b5f\u96c4\u7684\u5c0f\u5fc3\u8c28\u614e\uff0c\u800c\u662f\u8981\u7b11\u4f60 \u548c\u5e05\u5b5f\u96c4\u80c6\u5c0f\u5982\u9f20\u5462\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u53cc\u773c\u4e00\u7ffb\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u7528\u6fc0\u5c06\u4e4b\u8ba1\uff0c\u53ea\u8bf4\u4f60\u60f3\u600e\u6837\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u8981\u5e05\u5c06\u519b\u6cbb\u4e0b\u7684\u767e\u59d3\u4e5f\u4e00\u540c\u9ad8\u5174\uff0c\u4ece\u660e\u5929\u8d77\u5c31\u6253\u5f00\u57ce\u95e8\uff0c\u51c6\u8bb8\u8001\u767e\u59d3\u8fdb \u57ce\uff0c\u540e\u5929\u4e00\u5929\uff0c\u57ce\u4e2d\u7684\u9152\u697c\u8336\u9986\u4efb\u51ed\u8001\u767e\u59d3\u5403\u559d\uff0c\u7531\u5c06\u519b\u5e9c\u8bf7\u5ba2\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u4e5f\u8fd9\u6837\u559c\u7231\u865a\u8363\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u51b7\u7b11\u9053\uff1a\u201c\u5426\u5219\u6211\u4f55\u5fc5\u5ac1\u7ed9\u4e00\u4e2a\u5c06\u519b\uff1f\u8fd9\u6837\u529e\uff0c\u624d\u591f\u5f97\u4e0a\u8bf4\u662f\u2018\u98ce\u5149\u2019\uff01\u5e05\u5b5f\u96c4 \u628a\u6bcf\u4e2a\u6708\u514b\u6263\u519b\u664c\u7684\u94b1\uff0c\u62ff\u4e86\u4e00\u70b9\u51fa\u6765\uff0c\u8fd9\u4e2a\u5ba2\u4e5f\u603b\u53ef\u4ee5\u8bf7\u5f97\u8d77\u4e86\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u5bb3\u6015\u7834\u949e\u7684\u95ee\u9898\uff0c\u4f46\u4f60\u65e2\u7136\u575a\u6301\u8fd9\u6837\uff0c\u90a3\u6211\u5c31\u548c\u4f60\u5411\u5e05\u5c06\u519b\u8bf4 \u5427\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4f60\u544a\u8bc9\u4ed6\uff0c\u529e\u4e0d\u5230\u7684\u8bdd\uff0c\u4f53\u60f3\u5a36\u6211\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u6761\u4ef6\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u7b2c\u4e8c\u6869\u5c31\u53ea\u662f\u6211\u7684\u79c1\u4e8b\u4e86\u3002\u4f60\u77e5\u9053\u828d\u836f\u662f\u4e00\u5411\u670d\u4f8d\u6211\u7684\u4e2b\u5934\uff0c\u5979\u4e0e\u5979\u7684\u8868 \u54e5\u5df2\u6709\u7ec8\u8eab\u4e4b\u7ea6\uff0c\u5979\u4e0d\u613f\u610f\u968f\u6211\u966a\u5ac1\uff0c\u6211\u4e5f\u4e0d\u60f3\u5979\u56f0\u5728\u4faf\u95e8\uff0c\u8bef\u4e86\u7ec8\u8eab\u3002\u662f\u4ee5\u6211\u60f3\u8bf7\u4f60\u653e\u5979 \u56de\u53bb\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u53ef\u4ee5\u7b54\u5e94\u3002\u559c\u4e8b\u529e\u5b8c\u4e86\uff0c\u6211\u5e26\u5979\u56de\u53bb\u4fbf\u662f\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4e0d\uff0c\u5979\u660e\u5929\u5c31\u8981\u56de\u53bb\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u4e3a\u4f55\u5982\u6b64\u5306\u5fd9\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u77e5\u9053\u5e05\u76c2\u96c4\u8981\u4f60\u52a9\u4ed6\u5b88\u57ce\uff0c\u4f60\u4eec\u662f\u51b3\u4e0d\u4f1a\u5728\u4e00\u4e24\u4e2a\u6708\u4e4b\u5185\u56de\u53bb\u7684\u3002\u8fd9\u4e2b \u5934\u601d\u5bb6\u5fc3\u5207\uff0c\u6211\u65e2\u7136\u7b54\u5e94\u4e86\u8ba9\u5979\u81ea\u4e3b\uff0c\u90a3\u53c8\u4f55\u4e0d\u65e9\u4e9b\u653e\u5979\u56de\u53bb\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u4e0d\u613f\u4e3a\u4e86\u5c0f\u4e8b\u4e89\u6267\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u4e3a\u4e86\u828d\u836f\u7740\u60f3\uff0c\u5728\u8fd9\u5175\u8352\u9a6c\u4e71\u7684\u5e74\u5934\uff0c\u5979\u4e00 \u4e2a\u4eba\u56de\u53bb\u6050\u6015\u4e0d\u4fbf\u5427\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u5979\u591a\u5c11\u8ddf\u6211\u5b66\u4e86\u4e00\u70b9\u6b66\u827a\uff0c\u53ea\u987b\u4f60\u5411\u5e05\u5b5f\u96c4\u8ba8\u4e00\u679d\u4ee4\u7bad\u7ed9\u5979\uff0c\u6cbf\u9014\u6ca1\u6709\u5b98 \u5175\u9a9a\u6270\u5979\uff0c\u90a3\u5c31\u884c\u4e86\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f9d\u4f60\u5c31\u662f\uff0c\u8fd8\u6709\u7b2c\u4e09\u6869\u4e48\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u7b2c\u4e09\u6869\uff0c\u6211\u7684\u5175\u5668\u4f60\u5e94\u5f53\u8fd8\u6211\uff01\u201d\u539f\u6765\u53f2\u7ea2\u82f1\u6240\u7528\u7684\u4e00\u6839\u8f6f\u97ad\uff0c\u4e00\u67c4\u77ed\u5251 \u5728\u90a3\u65e5\u53d7\u64d2\u4e4b\u540e\uff0c\u65e9\u5df2\u88ab\u5979\u54e5\u54e5\u7f34\u53bb\u3002 \u3000\u3000\u827e\u767d\u90fd\u7b11\u9053\uff1a\u201c\u4f60\u662f\u5feb\u8981\u505a\u65b0\u5a18\u5b50\u7684\u4eba\u4e86\uff0c\u8fd8\u8981\u4e52\u5668\u505a\u4ec0\u4e48\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u660e\u767d\u4f60\u7684\u5fc3\u601d\uff0c\u4f60\u662f\u6015\u6211\u884c\u523a\u5e05\u5b5f\u96c4\u4e0d\u662f\uff1f\u54fc\uff01\u5982\u679c\u6211\u4e0d\u662f\u7518\u5fc3\u60c5\u613f\u5ac1 \u4ed6\u7684\u8bdd\uff0c\u6ca1\u6709\u5175\u5668\u96be\u9053\u5c31\u4e0d\u80fd\u5bb3\u6b7b\u4ed6\u4e48\uff1f\u4f60\u628a\u6211\u7684\u5175\u5668\u7f34\u53bb\uff0c\u8fd9\u5c31\u662f\u628a\u6211\u5f53\u4f5c\u56da\u72af\u770b\u5f85\uff0c\u6211 \u51b3\u4e0d\u80fd\u53d7\u4e86\u4f60\u7684\u4fae\u8fb1\u8fd8\u8981\u5e2e\u4f60\u730e\u53d6\u5bcc\u8d35\u529f\u540d\uff0c\u540e\u5929\u4f60\u53eb\u8463\u5341\u4e09\u5a18\u53eb\u82b1\u8f7f\u5427\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u6293\u6293\u5934\u76ae\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u4ece\u4f55\u8bf4\u8d77\uff0c\u5e05\u5c06\u519b\u8981\u7684\u662f\u4f60\uff0c\u53ef\u4e0d\u662f\u8463\u5341\u4e09\u5a18 \u554a\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4f60\u544a\u8bc9\u4ed6\uff0c\u4ed6\u82e5\u6015\u6211\u884c\u523a\uff0c\u5c31\u4e0d\u5fc5\u5a36\u6211\u3002\u6211\u7ec3\u4e86\u4e00\u8eab\u6b66\u827a\uff0c\u5175\u5668\u662f\u4e0d\u80fd\u4e0d \u5e26\u7684\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u597d\u5427\uff0c\u4f60\u7684\u97ad\u5251\u6211\u4ea4\u8fd8\u4f60\u4fbf\u662f\u3002\u4f46\u5728\u4f60\u505a\u65b0\u5a18\u5b50\u90a3\u5929\uff0c\u5175\u5668\u53ef\u4e0d\u80fd\u5e26\u5728\u8eab \u4e0a\u3002\u65b0\u5a18\u5b50\u5e26\u7740\u5175\u5668\u62dc\u5802\uff0c\u8fd9\u662f\u4f1a\u7ed9\u4eba\u7b11\u8bdd\u7684\u554a\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4f60\u7ed9\u56de\u5175\u5668\u518d\u8bf4\u3002\u5176\u5b9e\u4f60\u65e0\u987b\u66ff\u5e05\u5b5f\u96c4\u8fd9\u6837\u62c5\u5fc3\uff0c\u4ed6\u7684\u6b66\u529f\u8fdc\u80dc\u4e8e\u6211\uff0c\u6211 \u5c82\u80fd\u5728\u62dc\u5802\u7684\u65f6\u5019\u884c\u523a\u4ed6\uff0c\u4e0d\u6015\u767d\u767d\u9001\u547d\u4e48\uff1f\u672c\u6765\u6211\u53ef\u4ee5\u7b54\u5e94\u4f60\u90a3\u5929\u4e0d\u5e26\u5175\u5668\u7684\uff0c\u4f46\u4f60\u603b\u662f \u4fe1\u4e0d\u8fc7\u6211\uff0c\u6211\u5c31\u504f\u8981\u4e0d\u7b54\u5e94\u4f60\u4e86\u3002\u54fc\uff0c\u5e26\u4e0d\u5e26\u8981\u770b\u6211\u90a3\u5929\u7684\u9ad8\u5174\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u662f\u62ff\u4f60\u8fd9\u4e2b\u5934\u90fd\u6ca1\u6709\u529e\u6cd5\uff0c\u597d\uff0c\u90a3\u4f9d\u4f60\u5c31\u662f\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6700\u540e\u4e00\u6869\uff0c\u6211\u4e0d\u559c\u6b22\u8463\u5341\u4e09\u5a18\uff0c\u6211\u8981\u7261\u4e39\u966a\u6211\u3002\u4ece\u73b0\u5728\u8d77\uff0c\u4e0d\u8bb8\u8463\u5341\u4e09\u5a18 \u8e0f\u5165\u6211\u7684\u623f\u4e2d\u3002\u201d\u539f\u6765\u81ea\u5230\u897f\u660c\u4e4b\u540e\uff0c\u90fd\u662f\u7531\u8463\u5341\u4e09\u5a18\u966a\u5979\uff0c\u665a\u4e0a\u5c31\u5728\u5979\u7684\u623f\u4e2d\u7761\u89c9\u7684\u3002\u7261 \u4e39\u548c\u828d\u836f\u5219\u662f\u53f2\u7ea2\u82f1\u7684\u5fc3\u8179\u4e2b\u5934\uff0c\u4f46\u662f\u7ed9\u8463\u5341\u4e09\u5a18\u9694\u79bb\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u5fc3\u60f3\uff1a\u201c\u6211\u662f\u8981\u8463\u5341\u4e09\u5a18\u76d1\u89c6\u5979\u7684\uff0c\u82e5\u679c\u7531\u7261\u4e39\u8ddf\u5979\uff0c\u5979\u4eec\u4e3b\u4ec6\u540c\u8c0b\uff0c\u53ea\u6015\u4f1a\u95f9 \u51fa\u5c94\u5b50\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u82e5\u8981\u5bfb\u6b7b\uff0c\u65e9\u5c31\u53ef\u4ee5\u6b7b\u4e86\u3002\u4f60\u8981\u8463\u5341\u4e09\u5a18\u76d1\u89c6\u6211\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u54fc\uff0c\u6211\u5c31 \u662f\u4e0d\u670d\u6c14\u4f60\u628a\u6211\u5f53\u4f5c\u72af\u4eba\u770b\u7ba1\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u60f3\u60f3\u4e5f\u6709\u9053\u7406\uff0c\u4fbf\u9053\uff1a\u201c\u597d\u5427\uff0c\u597d\u5427\uff0c\u4f60\u65e2\u7136\u559c\u6b22\u7261\u4e39\uff0c\u5c31\u53eb\u7261\u4e39\u6765\u966a\u4f60\u5427\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u5f00\u57ce\u4e4b\u4e8b\u548c\u7ed9\u828d\u836f\u8ba8\u4ee4\u7bad\u4e4b\u4e8b\u5982\u4f55\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u6211\u73b0\u5728\u5c31\u548c\u5e05\u5c06\u519b\u8bf4\u53bb\uff0c\u55ef\uff0c\u8463\u5341\u4e09\u5a18\uff0c\u8bf7\u4f60\u628a\u7ea2\u82f1\u7684\u5175\u5668\u62ff\u6765\u5427\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u5728\u623f\u5916\u5b88\u5019\uff0c\u53f2\u7ea2\u82f1\u7684\u8bf4\u8bdd\u5979\u5168\u90e8\u542c\u89c1\u4e86\u3002\u5f53\u4e0b\u628a\u7ea2\u82f1\u7684\u8f6f\u97ad\u548c\u77ed\u5251\u9012\u8fdb\u623f \u6765\uff0c\u7531\u53f2\u767d\u90fd\u4ea4\u7ed9\u59b9\u59b9\uff0c\u5979\u81ea\u5df1\u5219\u662f\u6ee1\u9762\u6012\u5bb9\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u811a\u6b65\u4e5f\u6ca1\u6709\u8e0f\u8fc7\u95e8\u69db\u3002 \u3000\u3000\u53f2\u767d\u90fd\u8d70\u540e\uff0c\u53f2\u7ea2\u82f1\u628a\u7261\u4e39\u3001\u828d\u836f\u4e24\u4e2a\u4e2b\u5934\u53eb\u6765\uff0c\u5173\u4e0a\u623f\u95e8\u3002\u5979\u6df1\u77e5\u8463\u5341\u4e09\u5a18\u5fc5\u5b9a\u5728\u5916 \u9762\u5077\u542c\uff0c\u56e0\u6b64\u4e3b\u4ec6\u4e09\u4eba\u5728\u623f\u4e2d\u8bf4\u7684\u90fd\u662f\u65e0\u5173\u7d27\u8981\u7684\u95f2\u8bdd\u3002\u4f46\u53f2\u7ea2\u82f1\u53e3\u4e2d\u6ca1\u8bf4\u8bdd\uff0c\u624b\u6307\u5374\u662f\u8638 \u4e86\u8336\u6c34\u5728\u684c\u4e0a\u4e66\u5199\uff0c\u7528\u8fd9\u4e2a\u529e\u6cd5\u4e09\u4eba\u6697\u5546\u5bf9\u7b56\uff0c\u6309\u4e0b\u6162\u8868\u3002 \u3000\u3000\u4e14\u8bf4\u53f2\u767d\u90fd\u79bb\u5f00\u5bd3\u6240\uff0c\u5306\u5306\u5730\u8d76\u5230\u4e86\u5c06\u519b\u5e9c\u3002\u4ed6\u662f\u5c06\u519b\u7684\u5927\u8205\u7237\uff0c\u76f4\u8fdb\u76f4\u51fa\uff0c\u65e0\u987b\u901a \u62a5\uff0c\u8fdb\u5165\u5185\u5802\uff0c\u603b\u7ba1\u544a\u8bc9\u4ed6\u9053\uff1a\u201c\u5c06\u519b\u6b63\u5728\u63a5\u89c1\u76f8\u5e9c\u7684\u4f7f\u8005\uff0c\u53f2\u5e2e\u4e3b\u4f60\u662f\u5728\u8fd9\u91cc\u5f85\u4e00\u4f1a\u5462\uff0c \u8fd8\u662f\u73b0\u5728\u5c31\u8981\u89c1\u5e05\u5c06\u519b\u5462\uff1f\u90a3\u4f4d\u4f7f\u8005\u53cd\u6b63\u662f\u4f60\u7684\u719f\u4eba\uff0c\u4f60\u5c31\u662f\u8fdb\u53bb\u548c\u4ed6\u4eec\u8bf4\u8bdd\uff0c\u4e5f\u662f\u65e0 \u59a8\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u8fde\u57ce\u864e\u65e9\u5df2\u6765\u4e86\u3002\u55ef\uff0c\u4e0d\u4f46\u4ed6\u90a3\u4e24\u4e2a\u968f\u4ece\u5f62\u8ff9\u53ef\u7591\uff0c\u4ed6\u672c\u8eab\u4e5f\u662f\u6709 \u70b9\u53ef\u7591\u3002\u201d\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8fdb\u53bb\u4e86\u3002\u8bf7\u4f60\u628a\u8d3a\u5927\u5a18\u7ed9\u6211\u53eb\u6765\u3002\u201d \u3000\u3000\u8d3a\u5927\u5a18\u5c31\u662f\u77f3\u971e\u59d1\u90a3\u4e2a\u5584\u4e8e\u4f7f\u6bd2\u7684\u5976\u5a18\uff0c\u8fd9\u6b21\u4e5f\u968f\u53f2\u767d\u90fd\u6765\u4e86\u897f\u660c\u3002\u5e05\u5b5f\u96c4\u4e45\u95fb\u5979\u7684\u5927 \u540d\uff0c\u628a\u5979\u8bf7\u5230\u5c06\u519b\u5e9c\u4e2d\uff0c\u5949\u4e3a\u4e0a\u5ba2\u3002\u51c6\u5907\u5728\u7ed3\u5a5a\u7684\u90a3\u5929\u665a\u4e0a\uff0c\u5229\u7528\u5979\u7684\u4f7f\u6bd2\u672c\u9886\uff0c\u7ed9\u4ed6\u5236\u4f0f \u5014\u5f3a\u7684\u65b0\u5a18\u3002 \u3000\u3000\u8d3a\u5927\u5a18\u89c1\u4e86\u53f2\u767d\u90fd\uff0c\u7b11\u9053\uff1a\u201c\u53f2\u5e2e\u4ea4\uff0c\u4f60\u662f\u8d35\u4eba\u4e8b\u5fd9\uff0c\u600e\u7684\u8fd9\u4e2a\u65f6\u5019\uff0c\u8fd8\u6709\u5de5\u592b\u89c1\u6211\u8fd9 \u4e2a\u8001\u5a46\u5b50\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u66f9\u76f8\u56fd\u6d3e\u6765\u4e86\u4e00\u4e2a\u4f7f\u8005\uff0c\u6211\u5bf9\u4ed6\u6709\u70b9\u8d77\u7591\u3002\u201d \u3000\u3000\u8d3a\u5927\u5a18\u9053\uff1a\u201c\u8fd9\u4f7f\u8005\u4e0d\u662f\u8fde\u57ce\u864e\u5417\uff1f\u4ed6\u662f\u8ddf\u968f\u66f9\u76f8\u56fd\u591a\u5e74\u7684\u4e86\uff0c\u600e\u7684\u4f60\u4f1a\u5bf9\u4ed6\u7591\u5fc3\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u8fde\u5bb6\u4ee5\u56db\u7b14\u70b9\u516b\u8109\u7684\u7edd\u6280\u9a70\u540d\u6b66\u6797\uff0c\u8fde\u57ce\u864e\u53ef\u7b97\u5f97\u662f\u5f53\u4eca\u4e4b\u4e16\u6570\u4e00\u6570\u4e8c\u7684 \u70b9\u7a74\u540d\u5bb6\uff0c\u53ef\u662f\u4ed6\u7684\u5224\u5b98\u7b14\u5374\u6ca1\u5e26\u6765\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\u6709\u70b9\u5947\u602a\uff1f\u201d \u3000\u3000\u8d3a\u5927\u5a18\u9053\uff1a\u201c\u55ef\uff0c\u8fd9\u4e48\u8bf4\u662f\u6709\u70b9\u5947\u602a\u4e86\u3002\u4e0d\u8fc7\u8fde\u57ce\u864e\u603b\u4e0d\u81f3\u4e8e\u5fc3\u6000\u4e0d\u8f68\u5427\uff1f\u4ed6\u82e5\u662f\u610f\u6b32 \u4e0d\u5229\u4e8e\u5e05\u5c06\u519b\uff0c\u8fd9\u5bf9\u5224\u5b98\u7b14\u662f\u4e0d\u80fd\u5c11\u4e86\u7684\u554a\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u5bf9\u8fde\u57ce\u864e\u672c\u8eab\u6211\u5012\u662f\u5e76\u65e0\u6000\u7591\uff0c\u4f46\u6211\u6000\u7591\u4ed6\u662f\u53d7\u4eba\u631f\u5236\u3002\u4ed6\u90a3\u4e24\u4e2a\u968f\u4ece\u6211 \u597d\u50cf\u5728\u54ea\u91cc\u89c1\u8fc7\u4f3c\u7684\uff0c\u4e00\u65f6\u5374\u60f3\u4e0d\u8d77\u6765\u3002\u201d \u3000\u3000\u8d3a\u5927\u5a18\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u6562\u60c5\u4f60\u662f\u60f3\u8981\u6211\u5e2e\u4e00\u5e2e\u4f60\uff0c\u77a7\u4e00\u77a7\u8fde\u57ce\u864e\u662f\u5426\u4e2d\u6bd2\uff0c\u662f \u5417\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u4e0d\u9519\u3002\u8fde\u57ce\u864e\u6b66\u529f\u751a\u9ad8\uff0c\u82e5\u7136\u4ed6\u771f\u7684\u662f\u53d7\u4eba\u631f\u5236\uff0c\u90a3\u5c31\u591a\u534a\u662f\u7740\u4e86\u6bd2\u836f\u7684 \u6697\u7b97\u4e86\u3002\u4f60\u662f\u5927\u884c\u5bb6\uff0c\u4e00\u773c\u5c31\u53ef\u4ee5\u770b\u5f97\u51fa\u6765\u3002\u53c8\u5373\u4f7f\u4e0d\u662f\u4e2d\u6bd2\uff0c\u800c\u662f\u7740\u4e86\u522b\u7684\u9053\u513f\uff0c\u4f60\u7684\u9605 \u5386\u7ecf\u9a8c\u4e5f\u6bd4\u6211\u9ad8\u3002\u201d \u3000\u3000\u8d3a\u5927\u5a18\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u73b0\u5728\u6b63\u548c\u5e05\u5c06\u519b\u8bf4\u8bdd\uff0c\u6211\u600e\u597d\u65e0\u7aef\u7aef\u7684\u95ef\u8fdb\u53bb\u4ed4\u7ec6\u770b\u4ed6\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u7b11\u9053\uff1a\u201c\u8fd9\u8fd8\u4e0d\u5bb9\u6613\uff1f\u5982\u6b64\u5982\u6b64\uff0c\u8fd9\u822c\u8fd9\u822c\uff01\u201d\u8d3a\u5927\u5a18\u8fde\u8bf4\u597d\u8ba1\uff0c\u5f53\u4e0b\u4fbf\u5373\u4f9d\u8ba1 \u884c\u4e8b\u3002\u6b63\u662f\uff1a \u3000\u3000\u86e4\u87c6\u60f3\u5403\u5929\u9e45\u8089\u3002\u6597\u89d2\u52fe\u5fc3\u53c8\u4e00\u573a\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e94\u56de\u3000\u62fc\u6559\u7389\u788e\u6b7c\u5f3a\u654c\u3000\u59cb\u4fe1\u91d1\u575a\u662f\u65e7\u60c5 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e09\u5341\u4e94\u56de\u3000\u62fc\u6559\u7389\u788e\u6b7c\u5f3a\u654c\u3000\u59cb\u4fe1\u91d1\u575a\u662f\u65e7\u60c5 \u3000\u3000\u8fde\u57ce\u864e\u6b63\u5728\u548c\u5e05\u5b5f\u96c4\u8bf4\u8bdd\uff0c\u5ffd\u89c1\u4e00\u4e2a\u8001\u5a46\u5a46\u6367\u770b\u8336\u76d8\u98a4\u5dcd\u5dcd\u5730\u8d70\u5230\u4ed6\u7684\u9762\u524d\uff0c\u8bf4\u9053\uff1a \u201c\u8fde\u5927\u4eba\uff0c\u8bf7\u7528\u8336\uff01\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u5927\u529b\u8be7\u5f02\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8d3a\u5927\u5a18\uff0c\u4f60\uff0c\u4f60\u600e\u4e48\u5566\u2026\u2026\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u8d3a\u5927 \u5a18\u5df1\u5411\u4ed6\u4f7f\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u51e0\u4e2a\u5c0f\u4e2b\u5934\u90fd\u5077\u5077\u53bb\u73a9\u4e86\uff0c\u6ca1\u4eba\u4f8d\u5019\u8d35\u5ba2\uff0c\u53ea\u597d\u7531 \u6211\u5012\u8336\u5566\u3002\u201d \u3000\u3000\u8fde\u57ce\u864e\u4e00\u65f6\u8fd8\u672a\u60f3\u5230\u5176\u4e2d\u53e6\u6709\u8e4a\u8df7\uff0c\u542c\u4e86\u5e05\u5b5f\u96c4\u90a3\u6837\u8bf4\u8bdd\uff0c\u53ea\u9053\u8fd9\u4e2a\u8d3a\u5927\u5a18\u662f\u4e2a\u6709\u8eab\u4efd \u7684\u8001\u4ec6\u4eba\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u4e0d\u6562\u5f53\uff01\u201d\u6b63\u8981\u63a5\u8fc7\u8336\u676f\uff0c\u8d3a\u5927\u5a18\u624b\u8155\u6296\u98a4\uff0c\u90a3\u676f\u70ed\u8336\u6cfc \u5230\u8fde\u57ce\u864e\u8eab\u4e0a\u3002\u8d3a\u5927\u5a18\u4f6f\u4f5c\u60ca\u60f6\uff0c\u4f38\u624b\u66ff\u8fde\u57ce\u864e\u63e9\u62b9\u3002\u8fde\u57ce\u864e\u751a\u662f\u5c34\u5c2c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c \u4f60\u8001\u4eba\u5bb6\u8bf7\u56de\u53bb\u5427\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u8d3a\u5927\u5a18\u7684\u624b\u6307\u5df2\u662f\u88c5\u4f5c\u6beb\u4e0d\u7ecf\u610f\u7684\u4ece\u4ed6\u624b\u8155\u62c2\u8fc7\u3002 \u3000\u3000\u5b98\u573a\u89c4\u77e9\uff0c\u7b2c\u4e8c\u6b21\u7ed9\u5ba2\u4eba\u9001\u8336\uff0c\u90a3\u5c31\u662f\u4e3b\u4eba\u9001\u5ba2\u7684\u8868\u793a\u3002\u56e0\u6b64\u8d3a\u5927\u5a18\u8fdb\u53bb\u4e4b\u540e\uff0c\u8fde\u57ce\u864e \u5c31\u8d77\u7acb\u544a\u8f9e\u3002 \u3000\u3000\u8fde\u57ce\u864e\u662f\u66ff\u5bb0\u76f8\u9001\u793c\u6765\u7684\uff0c\u4f9d\u793c\u5e05\u5b5f\u96c4\u5e94\u8be5\u9001\u51fa\u5927\u95e8\uff0c\u4e0d\u6599\u521a\u521a\u9001\u4e0b\u53f0\u9636\uff0c\u53ea\u89c1\u53f2\u767d\u90fd \u5306\u5306\u8d76\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fde\u5144\uff0c\u6162\u8d70\uff01\u201d \u3000\u3000\u8fde\u57ce\u864e\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\u6709\u4f55\u89c1\u6559\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u8bf7\u8fde\u5144\u6307\u6559\u51e0\u8def\u70b9\u7a74\u624b\u6cd5\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u4f38\u624b\u5c31\u5411\u8fde\u57ce\u864e\u6293\u6765\uff0c\u7adf\u662f\u4e00\u62db \u6781\u4e3a\u5389\u5bb3\u7684\u5927\u64d2\u62ff\u624b\u6cd5\uff01 \u3000\u3000\u8fde\u57ce\u864e\u5927\u5403\u4e00\u60ca\uff0c\u9a88\u6307\u659c\u6233\uff0c\u6b63\u4e2d\u53f2\u767d\u90fd\u7684\u864e\u53e3\u3002\u53f2\u767d\u90fd\u624b\u8155\u4e00\u7ffb\uff0c\u5374\u7acb\u5373\u6293\u7740\u4e86\u4ed6\u7684 \u8109\u95e8\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u9053\uff1a\u201c\u53f2\u5927\u54e5\uff0c\u4f60\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u5fc3\u60f3\u3002\u201d\u4f60\u4eec\u867d\u7136\u662f\u76f8\u719f\u7684\u670b\u53cb\uff0c\u8fd9\u4e2a\u73a9\u7b11\u4e5f \u672a\u514d\u5f00\u5f97\u592a\u8fc7\u4efd\u4e86\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u54c8\u54c8\u4e00\u7b11\uff0c\u677e\u5f00\u4e86\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u8fde\u5144\u6055\u7f6a\uff0c\u975e\u662f\u5c0f\u5f1f\u80c6\u6562\u65e0\u793c\uff0c\u53ea\u56e0\u8fde\u5144\u8bb3\u75be\u5fcc \u533b\uff0c\u5c0f\u5f1f\u4e3a\u4e86\u633d\u6551\u8fde\u5144\uff0c\u53ea\u597d\u5982\u6b64\u5192\u72af\u4e86\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u8fde\u57ce\u864e\u767b\u65f6\u5413\u5f97\u9762\u5982\u571f\u8272\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u6b64\u65f6\u5df2\u77e5\u5176\u4e2d\u8d70\u6709\u8e4a\u8df7\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u8fde\u5927\u4eba\u662f\u6709\u75c5\u5728\u8eab\u4e48\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u75c5\uff0c\u662f\u4e2d\u4e86\u4eba\u5bb6\u7684\u6697\u7b97\u3002\u4e0d\u8fc7\u8fde\u5144\u4e5f\u4e0d\u7528\u60ca\u614c\uff0c\u521a\u624d\u7ed9\u4f60\u9001\u8336\u7684\u90a3 \u4f4d\u8001\u5a46\u5a46\uff0c\u662f\u5929\u9b54\u6559\u7684\u9ad8\u624b\uff0c\u5979\u64c5\u4e8e\u4f7f\u6bd2\uff0c\u4e5f\u64c5\u4e8e\u89e3\u6bd2\uff01\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u5403\u60ca\u9053\uff1a\u201c\u8fde\u5927\u4eba\u4e2d\u4e86\u6bd2\u4e48\uff1f\u201d \u3000\u3000\u8fde\u57ce\u864e\u671f\u671f\u827e\u827e\uff0c\u4e0d\u6562\u56de\u7b54\uff0c\u53f2\u767d\u90fd\u4ee3\u4ed6\u7b54\u9053\uff1a\u201c\u636e\u8d3a\u5927\u5a18\u8bf4\uff0c\u4ed6\u4e2d\u7684\u6bd2\uff0c\u82e5\u65e0\u89e3\u836f\uff0c \u4e09\u65e5\u4e4b\u540e\uff0c\u5b9a\u5c06\u6bd2\u53d1\u8eab\u4ea1\uff01\u4ed6\u521a\u624d\u70b9\u4e2d\u6211\u7a74\u9053\uff0c\u624b\u6307\u7a00\u6d6e\u65e0\u529b\uff0c\u770b\u6765\u8d3a\u5927\u5a18\u6240\u8bf4\uff0c\u51b3\u975e\u606b\u5413 \u4e4b\u8f9e\uff01\u201d\u5e05\u5b5f\u96c4\u8fd9\u624d\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u53f2\u767d\u90fd\u521a\u624d\u7684\u4e3e\u52a8\uff0c\u4e43\u662f\u5728\u8bd5\u4e00\u8bd5\u8fde\u57ce\u864e\u7684\u5185\u529f\u3002 \u3000\u3000\u53f2\u767d\u90fd\u7b11\u9053\uff1a\u201c\u8fde\u5144\uff0c\u54b1\u4eec\u90fd\u662f\u8001\u670b\u53cb\u4e86\uff0c\u4f60\u6709\u4ec0\u4e48\u4e3a\u96be\u4e4b\u4e8b\uff0c\u54b1\u4eec\u6162\u6162\u5546\u91cf\u3002\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8d3a\u5927\u5a18\u662f\u4e0d\u65b9\u4fbf\u5230\u5ba2\u6808\u7ed9\u4f60\u6cbb\u75c5\u7684\uff0c\u8bf7\u4f60\u5728\u8fd9\u513f\u7559\u4e0b\uff0c\u54b1\u4eec\u4e5f\u53ef\u4ee5 \u65b9\u4fbf\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u53f2\u3001\u5e05\u4e8c\u4eba\u534a\u63a8\u534a\u62e5\u7684\u628a\u8fde\u57ce\u864e\u62e5\u5165\u5bc6\u5ba4\uff0c\u53f2\u767d\u90fd\u4fbf\u5373\u95ee\u9053\uff1a\u201c\u8fde\u5144\uff0c\u4f60\u4e0d\u5fc5\u7792\u6211\u4e86\uff0c \u4f60\u90a3\u4e24\u4e2a\u968f\u4ece\u662f\u5047\u5192\u7684\u5427\uff1f\u4f60\u662f\u4e0d\u662f\u53d7\u4e86\u4ed6\u4eec\u6697\u7b97\uff0c\u4ee5\u81f4\u4e3a\u4ed6\u4eec\u631f\u5f85\uff1f\u201d \u3000\u3000\u8fde\u57ce\u864e\u867d\u6709\u5f03\u6697\u6295\u660e\u4e4b\u5fc3\uff0c\u4f46\u5fc3\u5fd7\u4e5f\u8fd8\u4e0d\u662f\u5341\u5206\u575a\u5b9a\u7684\uff0c\u6b64\u65f6\u60c5\u77e5\u9690\u7792\u4e0d\u8fc7\uff0c\u5fc3\u60f3\uff1a \u201c\u65e2\u7136\u6709\u8d3a\u5927\u5a18\u53ef\u4ee5\u7ed9\u6211\u89e3\u6bd2\uff0c\u6211\u5c31\u4e0d\u5fc5\u4f9d\u9760\u674e\u6566\u4e86\u3002\u201d\u7adf\u7136\u4e00\u4e94\u4e00\u5341\u5730\u62db\u4f9b\u51fa\u6765\u3002 \u3000\u3000\u53f2\u767d\u90fd\u542c\u4e86\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u8bf4\u9053\uff1a\u201c\u54fc\uff0c\u539f\u6765\u662f\u8fd9\u4e24\u4e2a\u5c0f\u5b50\uff01\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u96be\u5f97\u4ed6\u4eec\u81ea\u6295\u7f57\u7f51\uff0c\u8fd9\u6b21\u5b9a\u53eb\u4ed6\u4eec\u63d2\u7ffc\u96be\u9003\uff01\u8fde\u5927\u4eba\uff0c\u4f60\u5728\u8fd9\u91cc\u6b47 \u6b47\uff0c\u5f85\u6211\u4eec\u64d2\u4e86\u90a3\u4e24\u4e2a\u5c0f\u5b50\uff0c\u5c31\u53eb\u8d3a\u5927\u5a18\u7ed9\u4f60\u89e3\u6bd2\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u7adf\u662f\u8981\u628a\u8fde\u57ce\u864e\u7559\u4f5c\u4eba \u8d28\uff0c\u8fde\u57ce\u864e\u6697\u6697\u53eb\u82e6\uff0c\u540e\u6094\u5df2\u7ecf\u8fdf\u4e86\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u4e0e\u53f2\u767d\u90fd\u8d70\u5165\u540e\u5802\uff0c\u5e05\u5b5f\u96c4\u8bf4\u9053\uff1a\u201c\u53f2\u5927\u54e5\uff0c\u591a\u4e8f\u4f60\u8bc6\u7834\u4e86\u654c\u4eba\u7684\u5978\u8ba1\uff0c\u5389\u5357\u661f \u8fd9\u5c0f\u5b50\u60f3\u5fc5\u662f\u4e3a\u4ee4\u59b9\u800c\u6765\uff0c\u54fc\uff0c\u5728\u6211\u6210\u5a5a\u7684\u524d\u5915\uff0c\u4ed6\u5c45\u7136\u8fd8\u6562\u6765\u6b64\u80e1\u95f9\uff0c\u6211\u4e0d\u628a\u4ed6\u5316\u9aa8\u626c \u7070\uff0c\u96be\u6d88\u6211\u80f8\u4e2d\u4e4b\u6c14\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u5389\u5357\u661f\u8fd9\u5c0f\u5b50\u76d7\u4e86\u6211\u7684\u7384\u94c1\u5b9d\u5251\uff0c\u6211\u4e5f\u6b63\u662f\u6068\u4e0d\u5f97\u628a\u4ed6\u5316\u9aa8\u626c\u7070\uff01\u8fd8\u6709\u674e\u6566 \u8fd9\u5c0f\u5b50\u4e5f\u6781\u53ef\u6076\uff0c\u4ed6\u672c\u6765\u662f\u6211\u7684\u8bb0\u5ba4\uff0c\u7adf\u7136\u76d7\u4e86\u6211\u7684\u5b9d\u7269\u53db\u6211\uff0c\u6211\u4e5f\u540c\u6837\u4e0d\u80fd\u5c06\u4ed6\u653e\u8fc7\u3002\u5f85 \u4f1a\u513f\u6211\u4eb2\u81ea\u5230\u5ba2\u6808\u6349\u62ff\u4ed6\u4eec\uff01\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u73b0\u5728\u5c31\u53bb\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5c0f\u5b50\u51b3\u8ba1\u6599\u60f3\u4e0d\u5230\u54b1\u4eec\u5df2\u7ecf\u8bc6\u7834\u4e86\u4ed6\u4eec\u7684\u5978\u8ba1\uff0c\u5728\u8fd9\u897f\u660c\u57ce\u4e2d\uff0c\u8c05 \u4ed6\u4eec\u4e5f\u9003\u4e0d\u6389\u3002\u201d \u3000\u3000\u5e05\u76c2\u96c4\u9053\uff1a\u201c\u6562\u60c5\u53f2\u5927\u54e5\u53e6\u6709\u7d27\u8981\u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u82e6\u7b11\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u4ec0\u4e48\u7d27\u8981\u4e4b\u4e8b\uff0c\u54b3\uff0c\u54b3\uff0c\u8bf4\u6765\u4e0d\u597d\u610f\u601d\uff0c\u820d\u59b9\u5f53\u771f\u662f\u5b69\u5b50\u813e \u6c14\u2026\u2026\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u5bf9\u8fd9\u5a5a\u4e8b\uff0c\u5979\u3001\u5979\u8981\u53cd\u6094\u4e48\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u662f\uff0c\u820d\u59b9\u662f\u6c42\u5e05\u5c06\u519b\u4e24\u6869\u4e8b\u60c5\u3002\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684\u77f3\u5934\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53ea\u8981\u4ee4\u59b9\u5e94\u5141\u4e0e\u6211\u6210\u5a5a\uff0c\u592b\u59bb\u5982\u540c\u4e00\u4f53\uff0c\u83ab\u8bf4 \u4e24\u6869\uff0c\u5341\u6869\u6211\u4e5f\u53ef\u4ee5\u7b54\u5e94\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u5979\u8981\u5c06\u519b\u5927\u5f00\u57ce\u95e8\uff0c\u4e0e\u6c11\u540c\u4e50\u3002\u53e6\u4e00\u6869\u5979\u8981\u8ba8\u4e00\u679d\u4ee4\u7bad\u3002\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u53f2\u767d\u90fd\u9053\uff1a\u201c\u5979\u8981\u653e\u4e00\u4e2a\u5c0f\u4e2b\u5934\u56de\u53bb\u4e86\u3002\u201d\u5f53\u4e0b\u5c06\u53f2\u7ea2\u82f1\u6240\u8981 \u6c42\u7684\u8fd9\u4e24\u4ef6\u4e8b\u60c5\uff0c\u518d\u52a0\u8be6\u8bf4\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u542c\u4e86\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u8fd9\u6837\u4e24\u4ef6\u5c0f\u4e8b\uff0c\u8bf7\u4f60\u56de\u53bb\u544a\u8bc9\u4ee4\u59b9\uff0c\u6211\u9075\u547d\u5c31\u662f\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u5012\u6709\u70b9\u653e\u5fc3\u4e0d\u4e0b\uff0c\u8bf4\u8fc7\uff1a\u201c\u5927\u5f00\u57ce\u95e8\uff0c\u4e0d\u6015\u6709\u4eba\u6df7\u8fdb\u6765\u6363\u4e71\u4e48\uff1f\u800c\u4e14\u8fdb\u57ce\u7684\u4eba\uff0c \u4f60\u8fd8\u5f97\u8ba9\u4ed6\u4eec\u5403\u559d\u5462\uff0c\u8fd9\u4e2a\u592a\u4e0d\u5212\u7b97\u4e86\u3002\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u7b11\u9053\uff1a\u201c\u57ce\u4e2d\u6212\u5907\u68ee\u4e25\uff0c\u666e\u901a\u7684\u8001\u767e\u59d3\u8c01\u6562\u8fdb\u6765\uff1f\u8fdb\u6765\u7684\u4eba\u53c8\u8c01\u6562\u8981\u6211\u8bf7\u4ed6\u7684 \u5ba2\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u53ea\u6015\u4e5f\u6709\u4e00\u4e9b\u8feb\u4e8e\u751f\u8ba1\u7684\u5c0f\u767e\u59d3\uff0c\u8981\u8fdb\u57ce\u6765\u505a\u4e70\u5356\u3002\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u9053\uff1a\u201c\u6211\u53eb\u624b\u4e0b\u4e25\u52a0\u76d8\u67e5\uff0c\u5018\u6709\u6c5f\u6e56\u4eba\u7269\u6df7\u8fdb\u6765\uff0c\u987b\u7792\u4e0d\u8fc7\u6211\u90a3\u4e9b\u7cbe\u660e\u5e72\u7ec3\u7684\u624b \u4e0b\u7684\u773c\u775b\u3002\u800c\u4e14\u54b1\u4eec\u53e3\u5934\u4e0a\u7b54\u5e94\u4e86\u4ee4\u59b9\uff0c\u5018\u82e5\u53d1\u73b0\u6709\u4ec0\u4e48\u4e0d\u5b89\uff0c\u96be\u9053\u4e0d\u4f1a\u968f\u65f6\u5173\u95ed\u57ce\u95e8 \u4e48\u3002\u201d\u53f2\u767d\u90fd\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\uff01\u6211\u5230\u5e95\u662f\u76f4\u5fc3\u773c\u513f\uff0c\u8fdc\u4e0d\u5982\u5c06\u519b\u7684\u968f\u673a\u5e94\u53d8\u3002\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u9053\uff1a\u201c\u5012\u662f\u4ee4\u59b9\u60f3\u8981\u653e\u51fa\u7684\u90a3\u4e2a\u5c0f\u4e2b\u5934\uff0c\u54b1\u4eec\u5374\u662f\u4e0d\u80fd\u4e0d\u9632\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u5c06\u519b\u601d\u8651\u5468\u5bc6\uff0c\u662f\u8be5\u63d0\u9632\u4e9b\u513f\u3002\u8fd9\u5c0f\u4e2b\u5934\u662f\u81ea\u5c0f\u5356\u8eab\u7ed9\u6211\u5bb6\u7684\uff0c\u5e73\u65e5\u5012\u65e0 \u53ef\u7591\u7684\u884c\u8ff9\uff0c\u6b66\u529f\u4e5f\u4e0d\u9ad8\u5f3a\u3002\u4f46\u820d\u59b9\u8feb\u4e0d\u53ca\u5f85\u7684\u8981\u653e\u5979\u56de\u53bb\uff0c\u8fd9\u5c31\u6709\u70b9\u53ef\u7591\u4e86\u3002\u4f46\u820d\u59b9\u4e4b \u610f\uff0c\u5bf9\u6b64\u8d23\u751a\u662f\u575a\u6301\uff0c\u8fd9\u679d\u4ee4\u7bad\u662f\u7ed9\u5979\u8fd8\u662f\u4e0d\u7ed9\uff1f\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u7b11\u9053\uff1a\u201c\u5f53\u7136\u7ed9\u5979\u3002\u4eca\u59b9\u82e5\u662f\u6709\u4ec0\u4e48\u56fe\u8c0b\uff0c\u5012\u53ef\u4ee5\u4ece\u8fd9\u5c0f\u4e2b\u5934\u8eab\u4e0a\u5f97\u5230\u7ebf\u7d22 \u5462\uff01\u201d\u53f2\u767d\u90fd\u4f5c\u51fa\u5fc3\u9886\u795e\u4f1a\u7684\u795e\u6c14\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u662f\u5c06\u8ba1\u5c31\u8ba1\u7684\u5999\u6cd5\uff0c\u54b1\u4eec\u53ef\u4ee5\u6d3e\u4e00\u4e2a\u4eba \u8ddf\u8e2a\u5979\uff0c\u591a\u8c22\u5c06\u519b\u63d0\u9192\u6211\u4e86\u3002\u201d\u5176\u5b9e\u5e05\u5b5f\u96c4\u987e\u8651\u7684\u8fd9\u5c42\uff0c\u53f2\u767d\u90fd\u4e5f\u662f\u65e9\u5df2\u60f3\u5230\u4e86\u7684\u3002 \u3000\u3000\u53f2\u767d\u90fd\u5f97\u5230\u4e86\u6ee1\u610f\u7684\u56de\u7b54\uff0c\u5f53\u4e0b\u4fbf\u5373\u544a\u8f9e\u3002\u5e05\u5b5f\u96c4\u9053\uff1a\u201c\u53ef\u8981\u6211\u6d3e\u51e0\u4e2a\u5f97\u529b\u7684\u5e2e\u624b \u4e48\uff1f\u201d\u53f2\u767d\u90fd\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5c0f\u5b50\u5c1a\u672a\u77e5\u9053\u6211\u5df2\u7ecf\u53d1\u73b0\u4ed6\u4eec\u7684\u79d8\u5bc6\uff0c\u6211\u6b64\u53bb\u51fa\u5176\u4e0d\u610f\uff0c\u5b9a\u7136\u624b \u5230\u64d2\u6765\u3002\u4eba\u53bb\u591a\u4e86\uff0c\u53cd\u800c\u6253\u8349\u60ca\u86c7\u3002\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u5728\u8fd9\u91cc\u9759\u5f85\u4f73\u97f3\u4e86\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u81ea\u4f8d\u6b66\u529f\uff0c\u5373\u4f7f\u5389\u5357\u661f\u6709\u7384\u94c1\u5b9d\u5251\u5728\u624b\uff0c\u6253\u8d77\u6765\u7684\u8bdd\uff0c\u4ed6\u4e5f\u53ef\u4ee5\u7a33\u64cd\u80dc\u7b97\u3002\u81f3\u4e8e \u674e\u6566\uff0c\u4ed6\u66f4\u4e0d\u653e\u5728\u773c\u5185\u3002\u4f55\u51b5\u5ba2\u6808\u91cc\u4e5f\u6709\u4e0d\u5c11\u597d\u624b\uff0c\u5389\u3001\u674e\u4e8c\u4eba\u53c8\u65e0\u9632\u5907\u3002 \u3000\u3000\u53f2\u767d\u90fd\u6ee1\u809a\u5bc6\u5708\uff0c\u5f84\u5954\u5ba2\u6b8b\u3002\u4e0d\u6599\u5230\u4e86\u5ba2\u6808\uff0c\u5374\u5df2\u4e0d\u89c1\u5389\u3001\u674e\u4e8c\u4eba\u3002\u5ba2\u6808\u7684\u7ba1\u4e8b\u8bf4\u9053\uff1a \u201c\u8fd9\u4e24\u4e2a\u4eba\u5403\u8fc7\u665a\u996d\uff0c\u5c31\u51fa\u53bb\u4e86\u3002\u4ed6\u4eec\u8bf4\u662f\u51fa\u53bb\u968f\u4fbf\u901b\u901b\u5c31\u56de\u6765\u7684\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u5728\u8fd9\u91cc\u7a0d\u7b49\u7247\u523b\uff0c\u4f60\u8d76\u5feb\u6d3e\u4eba\u627e\u4ed6\u4eec\u56de\u6765\u3002\u201d \u3000\u3000\u4e0d\u6599\u7b49\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4ecd\u7136\u4e0d\u89c1\u5389\u5357\u661f\u548c\u674e\u6566\u56de\u6765\u3002\u6d3e\u51fa\u53bb\u627e\u4ed6\u4eec\u7684\u4eba\u9646\u7eed\u56de\u6765\uff0c\u4e5f\u90fd\u662f \u6ca1\u6709\u53d1\u73b0\u4ed6\u4eec\u7684\u8e2a\u8ff9\u3002 \u3000\u3000\u539f\u6765\u53f2\u767d\u90fd\u4ee5\u4e3a\u4ed6\u4eec\u6ca1\u6709\u9632\u5907\uff0c\u5176\u5b9e\u4ed6\u4eec\u662f\u65e9\u5df2\u6709\u4e86\u9632\u5907\u3002\u6b64\u523b\u4ed6\u4eec\u5df2\u8eb2\u5728\u674e\u6566\u76f8\u719f\u7684 \u4e00\u4e2a\u5728\u897f\u660c\u57ce\u4e2d\u201c\u5367\u5e95\u201d\u7684\u4eba\u7684\u5bb6\u91cc\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u7b49\u5230\u4e8c\u66f4\u65f6\u5206\uff0c\u4ecd\u7136\u4e0d\u89c1\u674e\u3001\u5389\u4e8c\u4eba\u56de\u6765\uff0c\u60c5\u77e5\u4e2d\u8ba1\uff0c\u4ea6\u662f\u65e0\u53ef\u5982\u4f55\uff0c\u53ea\u597d\u5429\u5490 \u5ba2\u6808\u7684\u536b\u58eb\u51fa\u53bb\u4e25\u52a0\u641c\u7d22\uff0c\u5fc3\u60f3\uff1a\u201c\u4ed6\u65e2\u662f\u4e3a\u7ea2\u82f1\u800c\u6765\uff0c\u8c05\u4ed6\u4e5f\u4e0d\u4f1a\u4fbf\u5373\u9003\u8d70\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u53f2\u7ea2\u82f1\u5411\u54e5\u54e5\u8ba8\u4e86\u4ee4\u7bad\uff0c\u5e76\u8ba8\u4e24\u5339\u5750\u9a91\u3002\u53f2\u767d\u90fd\u9053\uff1a\u201c\u8981\u4e24\u5339\u5750\u9a91\u505a\u4ec0 \u4e48\u3002\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u9001\u5979\u51fa\u57ce\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u76b1\u4e86\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u660e\u5929\u5c31\u8981\u505a\u65b0\u5a18\u5b50\u4e86\uff0c\u600e\u597d\u629b\u5934\u9732\u9762\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u8c01\u4e0d\u77e5\u9053\u6211\u662f\u4e00\u4e2a\u66fe\u7ecf\u95ef\u8361\u6c5f\u6e56\u7684\u5973\u5b50\uff0c\u6015\u4ec0\u4e48\u629b\u5934\u9732\u9762\uff1f\u5e05\u5b5f\u96c4\u7b54\u5e94\u6211 \u6253\u5f00\u57ce\u95e8\uff0c\u6211\u8fd8\u8981\u5230\u5404\u4e2a\u57ce\u95e8\u5de1\u89c6\u4e00\u904d\uff0c\u770b\u770b\u4ed6\u662f\u5426\u9633\u5949\u9634\u8fdd\u5462\uff1f\u201d \u3000\u3000\u53f2\u767d\u90fd\u62ff\u5979\u6ca1\u6709\u529e\u6cd5\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u966a\u4f60\u540c\u53bb\uff01\u201d\u53f2\u7ea2\u82f1\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u653e\u5fc3\u4e0d\u4e0b\uff0c\u6015 \u6211\u9003\u8d70\u4e48\uff1f\u54fc\uff0c\u6211\u82e5\u8981\u9003\u8d70\uff0c\u4e5f\u4e0d\u4e0e\u4f60\u4e00\u540c\u6765\u897f\u660c\u4e86\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u4e00\u5728\u8857\u5934\u51fa\u73b0\uff0c\u767b\u65f6\u8f70\u52a8\u5168\u57ce\u3002\u519b\u6c11\u4eba\u7b49\uff0c\u4e89\u7740\u51fa\u6765\u770b\u65b0\u5a18\u5b50\u3002\u867d\u7136\u6709\u5c06\u519b\u5e9c\u7684 \u536b\u58eb\u524d\u547c\u540e\u62e5\uff0c\u4e0d\u8bb8\u95f2\u6742\u4e4b\u4eba\u6321\u9053\uff0c\u4f46\u5728\u53f2\u7ea2\u82f1\u6240\u8fc7\u4e4b\u5904\uff0c\u8857\u9053\u4e24\u8fb9\u8fde\u5c4b\u9876\u4e0a\u4e5f\u90fd\u6324\u6ee1\u4e86 \u4eba\uff0c\u53ea\u662f\u4e0d\u80fd\u63a5\u8fd1\u53f2\u7ea2\u82f1\u800c\u5df2\u3002 \u3000\u3000\u5230\u4e86\u57ce\u6c60\uff0c\u53ea\u89c1\u57ce\u95e8\u679c\u7136\u5927\u5f00\uff0c\u51fa\u51fa\u8fdb\u8ff8\u7684\u4eba\u867d\u7136\u4e0d\u591a\uff0c\u4e5f\u662f\u5ddd\u6d41\u4e0d\u606f\u3002\u6709\u4e00\u8f86\u9aa1\u8f66\u521a \u597d\u8fdb\u57ce\u3001\u8f66\u4e0a\u6709\u4e00\u4e2a\u8001\u4eba\uff0c\u516d\u4e03\u4e2a\u5973\u5b50\uff0c\u5b88\u57ce\u7684\u5175\u58eb\u6b63\u8981\u76d8\u67e5\uff0c\u770b\u89c1\u53f2\u7ea2\u82f1\u5230\u6765\uff0c\u8fde\u5fd9\u4e0a\u524d \u8fce\u63a5\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u8fd9\u662f\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d\u5b88\u57ce\u7684\u519b\u5b98\u7b54\u9053\uff1a\u201c\u662f\u4e00\u73ed\u5973\u4e50\uff0c\u5c06\u519b\u5e9c\u603b\u7ba1\u8bf7\u6765\u52a9\u5174 \u7684\u3002\u201d\u53f2\u7ea2\u82f1\u51b7\u7b11\u9053\uff1a\u201c\u65e2\u662f\u4e00\u73ed\u5973\u5b50\uff0c\u53c8\u662f\u5c06\u519b\u5e9c\u8bf7\u6765\u7684\uff0c\u4f60\u4eec\u8fd8\u8981\u76d8\u67e5\uff0c\u5bf9\u4ed8\u8001\u767e\u59d3\u4f60 \u4eec\u66f4\u4e0d\u77e5\u662f\u5982\u4f55\u7684\u5201\u96be\u4e86\uff01\u54fc\uff0c\u8fd9\u6837\u8fd8\u4f55\u5fc5\u6253\u5f00\u57ce\u95e8\uff0c\u5e72\u8106\u5173\u4e0a\u597d\u4e86\u3002\u201d \u3000\u3000\u6b63\u56e0\u4e3a\u8fd9\u4e2a\u73ed\u5b50\u7684\u786e\u6709\u5c06\u519b\u5e9c\u7684\u8bf7\u5e16\uff0c\u4e8c\u6765\u53c8\u6709\u53f2\u7ea2\u82f1\u51fa\u5934\u5e72\u6d89\uff0c\u90a3\u4e2a\u519b\u5b98\u8bfa\u8bfa\u8fde\u58f0\uff0c \u4fbf\u5373\u653e\u8fd9\u8f86\u9aa1\u8f66\uff0c\u4e0d\u518d\u76d8\u67e5\u3002\u6df7\u5728\u8fd9\u4e2a\u73ed\u5b50\u91cc\u7684\u4f55\u5f69\u51e4\u4e0e\u516c\u5b59\u71d5\u65b9\u59cb\u677e\u4e86\u53e3\u6c14\u3002\u4f55\u5f69\u51e4\u62b9\u5e72 \u989d\u89d2\u51b7\u6c57\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5728\u5f6d\u5de8\u5d58\u548c\u8fde\u57ce\u864e\u6ca1\u6709\u4eb2\u6765\u76d8\u67e5\uff0c\u53c8\u8fd9\u4e48\u5e78\u8fd0\u7684\u521a\u597d\u78b0\u4e0a\u4e86\u5c06\u519b\u7684\u65b0 \u5a18\u5b50\uff01\u201d\u5979\u600e\u77e5\u5f6d\u5de8\u5d58\u5df2\u7ecf\u4e27\u547d\uff0c\u8fde\u57ce\u864e\u6b63\u88ab\u56da\u7981\uff0c\u54ea\u91cc\u8fd8\u6709\u5fc3\u601d\u8bb0\u8d77\u8fd9\u4ef6\u5c0f\u4e8b\u3002 \u3000\u3000\u516c\u5b59\u71d5\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u542c\u8bf4\u8fd9\u4f4d\u516d\u5408\u5e2e\u5e2e\u4e3b\u7684\u59b9\u59b9\u4e0e\u5979\u7684\u54e5\u54e5\u4e0d\u5927\u76f8\u540c\uff0c\u5374\u600e\u7684\u5c31\u7518\u5fc3 \u505a\u5e05\u5b5f\u96c4\u7684\u65b0\u5a18\u5b50\u4e86\uff1f\u201d\u4f55\u5f69\u98ce\u9053\uff1a\u201c\u4e0d\u5fc5\u7ba1\u5979\uff0c\u54b1\u4eec\u8981\u5bf9\u4ed8\u7684\u53ea\u662f\u5e05\u76c2\u96c4\u3002\u201d\u516c\u5b59\u71d5\u9053\uff1a \u201c\u5979\u82e5\u662f\u4e00\u5fc3\u4ece\u8d3c\uff0c\u660e\u5929\u6211\u987a\u624b\u4e5f\u9001\u5979\u4e00\u67c4\u98de\u5200\uff01\u201d \u3000\u3000\u4e0d\u8bf4\u516c\u5b59\u71d5\u4e0e\u4f55\u5f69\u51e4\u7a83\u7a83\u79c1\u8bae\uff0c\u4e14\u8bf4\u5728\u7eb7\u95f9\u4e4b\u4e2d\uff0c\u53f2\u7ea2\u82f1\u5ffd\u542c\u5f97\u8033\u8fb9\u4f3c\u6709\u4eba\u5c0f\u58f0\u8bf4\u9053\uff1a \u201c\u63a5\u4f4f\uff01\u201d\u53f2\u7ea2\u82f1\u53c8\u559c\u53c8\u60ca\uff0c\u53ea\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u5979\u5df2\u628a\u98de\u6765\u7684\u4e1c\u897f\u63a5\u5230\u624b\u4e2d\uff0c\u8f7b\u8f7b\u4e00\u634f\uff0c\u662f\u4e2a \u7eb8\u56e2\uff01 \u3000\u3000\u53f2\u7ea2\u82f1\u63a5\u8fc7\u7eb8\u56e2\uff0c\u751f\u6015\u7ed9\u4eba\u53d1\u89c9\u3002\u614c\u5fd9\u85cf\u5165\u6000\u4e2d\uff0c\u6e38\u76ee\u56db\u987e\uff0c\u53ea\u89c1\u5979\u7684\u54e5\u54e5\u6b63\u5728\u548c\u5b88\u57ce \u7684\u519b\u5b98\u8bf4\u8bdd\uff0c\u80cc\u5411\u7740\u5979\u3002\u7261\u4e39\u3001\u828d\u836f\u4e24\u4e2a\u4e2b\u5934\u5728\u5979\u4fa7\u9762\uff0c\u795e\u8272\u5982\u5e38\u3002\u5468\u56f4\u7684\u536b\u58eb\u6bcf\u4e2a\u4eba\u90fd\u662f \u5200\u51fa\u9798\u5251\uff0c\u4e25\u5bc6\u6212\u5907\uff0c\u770b\u60c5\u5f62\u8fd9\u4e9b\u4eba\u90fd\u662f\u4e1d\u6beb\u672a\u89c9\uff0c\u5426\u5219\u65e9\u5df2\u662f\u5316\u4f5c\u4e00\u56e2\u4e86\u3002 \u3000\u3000\u4f46\u53f2\u7ea2\u82f1\u4e5f\u627e\u4e0d\u5230\u90a3\u4e2a\u5411\u5979\u629b\u63b7\u7eb8\u56e2\u7684\u4eba\u3002 \u3000\u3000\u201c\u8fd9\u4eba\u53d1\u6697\u5668\u7684\u529f\u592b\u5f53\u771f\u662f\u795e\u51fa\u9b3c\u6ca1\uff0c\u5982\u679c\u4e0d\u662f\u4ed6\u5148\u6253\u4e2a\u62db\u547c\uff0c\u8fde\u6211\u4e5f\u4e1d\u6beb\u6ca1\u6709\u53d1\u89c9\u3002 \u5de7\u4eca\u4e4b\u4e16\uff0c\u6709\u8c01\u6709\u8fd9\u6837\u7684\u529f\u592b\u5462\uff1f\u201d \u3000\u3000\u66f4\u4ee4\u5f97\u53f2\u7ea2\u82f1\u60ca\u9a87\u7684\u662f\u8fd9\u4e2a\u4eba\u6df1\u4e0d\u53ef\u6d4b\u7684\u4f20\u97f3\u5165\u5bc6\u7684\u5185\u529f\uff0c\u5979\u56de\u60f3\u521a\u624d\u7684\u7ecf\u8fc7\uff0c\u90a3\u58f0\u97f3 \u7ec6\u82e5\u6e38\u4e1d\u94bb\u5165\u5979\u7684\u8033\u4e2d\uff0c\u5c31\u4f3c\u8d34\u7740\u5979\u7684\u8033\u6735\u8bf4\u8bdd\uff0c\u4f46\u8bf4\u8bdd\u7684\u4eba\u5374\u4e0d\u77e5\u662f\u5728\u4f55\u5904\uff1f\u201c\u4f20\u97f3\u5165 \u5bc6\u201d\u7684\u529f\u592b\u8fd8\u4e0d\u7b97\u5f88\u96be\uff0c\u5185\u529f\u6709\u6839\u5e95\u7684\u4eba\u90e8\u53ef\u4ee5\u5c06\u58f0\u97f3\u9001\u5230\u8fdc\u5904\uff0c\u53ea\u662f\u8ddd\u79bb\u6709\u8f83\u8fdc\u8f83\u8fd1\u4e4b\u5206 \u800c\u5df2\uff1b\u4f46\u96be\u5c31\u96be\u5728\u8bf4\u51fa\u7684\u58f0\u97f3\u53ea\u8ba9\u4e00\u4e2a\u4eba\u542c\u89c1\uff0c\u65c1\u8fb9\u7684\u4eba\uff0c\u5185\u529f\u82e5\u4e0d\u662f\u5728\u8bf4\u8bdd\u90a3\u4eba\u4e4b\u4e0a\uff0c\u4fbf \u6beb\u65e0\u6240\u89c9\u3002\u8fd9\u4e0d\u662f\u666e\u901a\u7684\u201c\u4f20\u97f3\u5165\u5bc6\u201d\uff0c\u800c\u662f\u4e00\u79cd\u7279\u5f02\u7684\u201c\u5929\u9041\u4f20\u97f3\u201d\u7684\u529f\u592b\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u4e00\u7247\u832b\u7136\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u96be\u9053\uff0c\u96be\u9053\u5f53\u771f\u662f\u4ed6\u6765\u4e86\uff1f\u201d \u3000\u3000\u51fa\u4e86\u57ce\u95e8\uff0c\u53f2\u7ea2\u82f1\u628a\u4ee4\u7bad\u4ea4\u7ed9\u828d\u836f\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u4e00\u522b\uff0c\u6b64\u540e\u53ea\u6015\u76f8\u4f1a\u65e0\u671f\u3002\u795d\u4f60\u4e00\u8def \u5e73\u5b89\uff0c\u6709\u60c5\u4eba\u7ec8\u6210\u7737\u5c5e\u3002\u201d\u828d\u836f\u9053\uff1a\u201c\u5c0f\u59d0\u8a8a\u81ea\u6df1\u91cd\uff0c\u795d\u4f60\u4e5f\u662f\u6709\u60c5\u4eba\u7ec8\u6210\u7737\u5c5e\u3002\u201d\u8bdd\u4e2d\u6709 \u8bdd\uff0c\u65c1\u4eba\u53ea\u9053\u5979\u662f\u795d\u8d3a\u53f2\u7ea2\u82f1\u4e0e\u5e05\u5b5f\u96c4\u7684\u5a5a\u4e8b\uff0c\u53ea\u6709\u53f2\u7ea2\u82f1\u81ea\u5df1\u660e\u767d\u828d\u836f\u795d\u8d3a\u7684\u662f\u8c01\uff0c\u82e6\u7b11 \u9053\uff1a\u201c\u53ea\u6015\u6211\u6ca1\u6709\u4f60\u8fd9\u6837\u7684\u798f\u6c14\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u597d\u4e86\uff0c\u53ef\u4ee5\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u4e0e\u828d\u836f\u6325\u6cea\u800c\u522b\uff0c\u56de\u5230\u4f4f\u6240\uff0c\u5173\u4e0a\u623f\u95e8\uff0c\u628a\u90a3\u4e2a\u7eb8\u56e2\u6253\u5f00\u6765\u4e00\u770b\uff0c\u53ea\u89c1\u91cc\u9762\u88f9\u4f4f \u4e00\u53e3\u94f6\u9488\uff0c\u9488\u5c16\u5374\u662f\u9ed1\u9edd\u9edd\u7684\u3002\u94fa\u5e73\u4e86\u7eb8\u56e2\u7ec6\u770b\uff0c\u4e0a\u9762\u8fd8\u5199\u6709\u5341\u4e8c\u4e2a\u8747\u5934\u5c0f\u5b57\uff1a\u201c\u6211\u5df2\u6765\uff0c \u6bcb\u60ca\u6050\u3002\u6b64\u6bd2\u9488\uff0c\u7559\u5907\u7528\u3002\u201d\u6b63\u662f\u91d1\u9010\u6d41\u7684\u7b14\u8ff9\u3002\u53f2\u7ea2\u82f1\u5927\u559c\u8fc7\u671b\uff0c\u5fc3\u60f3\uff1a\u201c\u679c\u7136\u662f\u4ed6\u6765 \u4e86\u3002\u4f46\u4ed6\u4ece\u6765\u4e0d\u7528\u5582\u6bd2\u7684\u6697\u5668\u7684\uff0c\u8fd9\u6bd2\u9488\u5374\u662f\u4ece\u4f55\u800c\u6765\uff1f\u96be\u9053\u5389\u5357\u661f\u4e5f\u6765\u4e86\u4e48\uff1f\u4ed6\u4eec\u4e24\u4eba\u5df2 \u7ecf\u89c1\u4e86\u9762\uff0c\u8fd9\u6bd2\u9488\u662f\u5389\u5357\u661f\u4ea4\u7ed9\u4ed6\u7684\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u731c\u5bf9\u4e86\u4e00\u534a\uff0c\u91d1\u9010\u6d41\u548c\u5389\u5357\u661f\u5168\u90fd\u6765\u4e86\uff0c\u4f46\u4ed6\u4eec\u4e8c\u4eba\u5374\u672a\u66fe\u89c1\u9762\u3002 \u3000\u3000\u8fd9\u652f\u6bd2\u9488\u662f\u91d1\u9010\u6d41\u5728\u626c\u5dde\u5927\u95f9\u516d\u820d\u5e2e\u603b\u8235\u4e4b\u65f6\uff0c\u7ed9\u8d3a\u5927\u5a18\u6697\u7b97\uff0c\u6253\u5728\u4ed6\u8eab\u4e0a\u7684\u90a3\u652f\u6bd2 \u9488\u3002\u540e\u6765\u674e\u6566\u7528\u78c1\u94c1\u7ed9\u4ed6\u5438\u51fa\u6765\u7684\u3002\u91d1\u9010\u6d41\u6536\u85cf\u8d77\u6765\uff0c\u539f\u610f\u662f\u5411\u8d3a\u5927\u5a18\u62a5\u590d\u7684\uff0c\u73b0\u5728\uff0c\u6070\u6070 \u6d3e\u4e0a\u4e86\u7528\u573a\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u91d1\u9010\u6d41\u4e0d\u6127\u662f\u6211\u7684\u77e5\u5df1\uff0c\u4ed6\u5df2\u7ecf\u77e5\u9053\u4e86\u6211\u5047\u610f\u7b54\u5e94\u5a5a \u4e8b\uff0c\u4e3a\u7684\u662f\u8981\u884c\u523a\u5e05\u5b5f\u96c4\u3002\u6211\u6b63\u6101\u65e0\u6cd5\u4e0b\u624b\uff0c\u6709\u4e86\u8fd9\u652f\u6bd2\u9488\uff0c\u53ef\u65b9\u4fbf\u591a\u4e86\u3002\u201d \u3000\u3000\u8bdd\u5206\u4e24\u5934\uff0c\u4e14\u8bf4\u828d\u836f\u51fa\u57ce\u4e4b\u540e\uff0c\u5feb\u9a6c\u75be\u9a70\uff0c\u8dd1\u4e86\u4e00\u7a0b\uff0c\u90a3\u5339\u5750\u9a91\u5ffd\u7136\u8d8a\u8d70\u8d8a\u6162\uff0c\u518d\u8d70\u4e00 \u4f1a\uff0c\u7adf\u7136\u53e3\u5410\u767d\u6cab\uff0c\u8d70\u4e0d\u52a8\u4e86\u3002\u539f\u6765\u53f2\u767d\u90fd\u7ed9\u5979\u7684\u8fd9\u5339\u5750\u9a91\uff0c\u662f\u6697\u4e2d\u4e0b\u4e86\u836f\u7684\u3002 \u3000\u3000\u6b64\u65f6\u6b63\u8d70\u5230\u8352\u50fb\u7684\u5c71\u91ce\u4e4b\u5730\uff0c\u828d\u836f\u867d\u65e0\u6c5f\u6e56\u7ecf\u9a8c\uff0c\u89c1\u5750\u9a91\u5012\u6bd9\uff0c\u4ea6\u5df2\u77e5\u9053\u4e0d\u5999\u3002\u5fc3\u5ff5\u672a \u5df2\uff0c\u53ea\u542c\u5f97\u8e44\u58f0\u6025\u9aa4\uff0c\u9a91\u9a6c\u5df2\u7ecf\u8ffd\u4e0a\u5c71\u5c97\uff0c\u6765\u7684\u6b63\u662f\u53f2\u767d\u90fd\u6700\u4eb2\u4fe1\u7684\u9999\u4e3b\u8463\u5341\u4e09\u5a18\u3002 \u3000\u3000\u828d\u836f\u614c\u5fd9\u8dd1\u5165\u6797\u4e2d\uff0c\u8463\u5341\u4e09\u5a18\u559d\u9053\uff1a\u201c\u8dd1\u4e0d\u4e86\u5566\uff0c\u8fd8\u4e0d\u8d76\u5feb\u7ed9\u6211\u7ad9\u4f4f\u3002\u201d \u3000\u3000\u828d\u836f\u5f3a\u81ea\u9547\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u8463\u9999\u4e3b\uff0c\u539f\u6765\u662f\u4f60\uff0c\u6211\u8fd8\u6015\u662f\u5f3a\u4eba\u5462\u3002\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u6211\u7684\u9a6c\u4e0d \u77e5\u4f55\u6545\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u662f\u4e56\u4e56\u542c\u8bdd\uff0c\u6211\u5012\u53ef\u4ee5\u9001\u7ed9\u4f60\u4e00\u5339\u5750\u9a91\uff0c\u8ba9\u4f60\u56de\u5bb6\u3002\u201d \u3000\u3000\u828d\u836f\u9053\uff1a\u201c\u8463\u9999\u4e3b\u6709\u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u628a\u5c0f\u59d0\u7ed9\u4f60\u7684\u4e1c\u897f\u4ea4\u51fa\u6765\uff01\u201d \u3000\u3000\u828d\u836f\u638f\u51fa\u4e86\u4e00\u628a\u94f6\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u5c0f\u59d0\u7ed9\u6211\u505a\u8def\u8d39\u7684\uff0c\u8463\u9999\u4e3b\u4f60\u62ff\u53bb\u4e0d\u6253\u7d27\uff0c\u6211\u5728\u8def \u4e0a\u53ef\u6ca1\u5f97\u7528\u4e86\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u6012\u9053\uff1a\u201c\u8c01\u8981\u4f60\u7684\u94f6\u5b50\uff0c\u6709\u4e66\u4fe1\u6ca1\u6709\uff1f\u201d \u3000\u3000\u828d\u836f\u9053\uff1a\u201c\u90a3\u6765\u7684\u4e66\u4fe1\uff1f\u4f60\u662f\u77e5\u9053\u7684\uff0c\u5c0f\u59d0\u623f\u4e2d\u53c8\u6ca1\u6709\u7b14\u58a8\u3002\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u5c0f\u59d0\u6709\u4ec0\u4e48\u7684\u8bdd\u4ea4\u4ee3\u4f60\u3002\u201d \u3000\u3000\u828d\u836f\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u3001\u8fd9\u4e2a\u2026\u2026\u201d\u8463\u5341\u4e09\u5a18\u559d\u9053\uff1a\u4ec0\u4e48\u8fd9\u4e2a\u90a3\u4e2a\uff0c\u5feb \u8bf4\u2026\u2026\u201d\u828d\u836f\u4f5c\u51fa\u5bb3\u7f9e\u800c\u53c8\u65e0\u53ef\u5948\u95ee\u7684\u795e\u6c14\u8bf4\u9053\uff1a\u201c\u5c0f\u59d0\u77e5\u9053\u6211\u4e0e\u8868\u54e5\u6709\u5a5a\u59fb\u4e4b\u7ea6\uff0c\u5979\u3001\u5979 \u4f53\u8d34\u6211\uff0c\u8fd9\u3001\u8fd9\u624d\u2026\u2026\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u51b7\u7b11\u9053\uff1a\u201c\u8c01\u95ee\u4f60\u7684\u79c1\u60c5\uff1f\u6211\u662f\u95ee\u5c0f\u59d0\u7684\u79c1\u60c5\uff01\u5979\u8981\u4f60\u7ed9\u8c01\u901a\u98ce\u62a5\u60c5\uff1f\u201d \u3000\u3000\u828d\u836f\u9053\uff1a\u201c\u6ca1\u6709\u5440\uff01\u201d\u8463\u5341\u4e09\u5a18\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4e0d\u7ed9\u4f60\u4e00\u70b9\u989c\u8272\u77a7\u77a7\uff0c\u4f60\u4e5f\u4e0d\u77e5\u9053\u6211\u7684 \u5389\u5bb3\uff01\u201d\u8df3\u4e0b\u9a6c\u6765\uff0c\u567c\u567c\u556a\u556a\u5730\u6253\u4e86\u52fa\u836f\u51e0\u8bb0\u8033\u5149\uff0c\u828d\u836f\u5fcd\u7740\u75bc\u75db\uff0c\u53ea\u662f\u4e0d\u8bf4\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u6012\u9053\uff1a\u201c\u8d31\u9aa8\u5934\u5012\u662f\u5f88\u786c\uff0c\u597d\uff0c\u4e14\u5f85\u6211\u641c\u4e86\u51fa\u6765\uff0c\u518d\u6162\u6162\u5730\u6298\u78e8\u4f60\uff01\u201d\u51fa\u6307\u70b9 \u4e86\u828d\u836f\u7684\u9ebb\u7a74\uff0c\u4fbf\u5373\u641c\u8eab\u3002 \u3000\u3000\u828d\u836f\u7684\u8eab\u4e0a\u9664\u4e86\u94f6\u5b50\u4e4b\u5916\uff0c\u5e76\u65e0\u5176\u4ed6\u4e1c\u897f\u3002\u8463\u5341\u4e09\u5a18\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\u6211\u628a\u4f60\u7684\u8863\u88f3\u5c3d \u90fd\u5265\u5149\uff01\u201d\u55e4\u7684\u4e00\u58f0\uff0c\u6495\u88c2\u4e86\u5979\u7684\u4e00\u4ef6\u8863\u88f3\uff0c\u828d\u836f\u53eb\u9053\uff1a\u201c\u4f60\u628a\u6211\u4e00\u5251\u6740\u4e86\u5427\uff0c\u4f55\u82e6\u8fd9\u6837\u7684 \u8fb1\u6211\uff01\u201d\u5979\u4f9d\u7136\u4e0d\u80af\u62db\u4f9b\uff0c\u770b\u795e\u6c14\u663e\u7136\u5df2\u662f\u5341\u5206\u5bb3\u6015\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u9053\uff1a\u201c\u54ea\u6709\u8fd9\u6837\u4fbf\u5b9c\uff01\u201d\u201c\u55e4\u201d\u4e00\u58f0\uff0c\u53c8\u6495\u88c2\u4e86\u5979\u7684\u4e2d\u8863\u3002\u828d\u836f\u5c16\u53eb\u4e00\u58f0\uff0c\u6655 \u4e86\u8fc7\u53bb\u3002\u4e00\u5757\u6298\u6210\u65b9\u5f62\u7684\u9999\u7f57\u624b\u5e15\u8dcc\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u62fe\u8d77\u624b\u5e15\uff0c\u6b63\u5f85\u6253\u5f00\u6765\u770b\uff0c\u5ffd\u542c\u5f97\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u6765\u5f97\u6781\u5feb\uff0c\u8463\u5341\u4e09\u5a18\u7adf\u7136\u8eb2 \u907f\u4e0d\u5f00\uff0c\u7ed9\u4e00\u679a\u5c0f\u5c0f\u7684\u77f3\u5b50\u6253\u7740\u4e86\u624b\u8155\u3002\u624b\u5e15\u55d6\u7684\u6389\u5728\u5730\u4e0a\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e00\u6761\u4eba\u5f71\u5df2 \u662f\u65cb\u98ce\u8239\u5730\u6251\u5230\uff01 \u3000\u3000\u8463\u5341\u4e09\u5a18\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u6761\u4eba\u5f71\u5df2\u7ecf\u6251\u5230\u5979\u7684\u9762\u524d\uff0c\u6765\u7684\u4eba\u662f\u522b \u4eba\uff0c\u6b63\u662f\u5979\u7684\u51a4\u5bb6\u5bf9\u5934\u91d1\u9010\u6d41\u3002 \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u65e9\u5df2\u6f5c\u5165\u6811\u4e1b\uff0c\u4ed6\u629b\u4e86\u90a3\u4e2a\u7eb8\u56e2\u7ed9\u53f2\u7ea2\u82f1\u4e4b\u540e\uff0c\u672c\u6765\u5c31\u60f3\u56de\u5c45\u6240\u7684\u3002\u4f46\u5fc3\u91cc \u4e00\u60f3\uff1a\u201c\u7ea2\u82f1\u8fd9\u6837\u90d1\u91cd\u5176\u4e8b\u5730\u9001\u4e2a\u4e2b\u5934\u51fa\u57ce\uff0c\u5176\u4e2d\u5b9a\u6709\u7f18\u6545\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u6211\u60f3\u5f97\u5230\u7684\u53f2\u767d\u90fd \u4e00\u5b9a\u4e5f\u4f1a\u60f3\u5f97\u5230\u3002\u7ea2\u82f1\u5728\u5979\u54e5\u54e5\u770b\u7ba1\u4e4b\u4e0b\uff0c\u662f\u4e0d\u80fd\u4fdd\u62a4\u8fd9\u4e2a\u4e2b\u5934\u7684\u4e86\u3002\u6211\u65e2\u7136\u731c\u5230\u4e86\u5979\u7684\u5fc3 \u610f\uff0c\u5c82\u80fd\u8896\u624b\u65c1\u89c2\uff1f\u201d\u4e3a\u4e86\u907f\u514d\u7ed9\u53f2\u767d\u90fd\u53d1\u73b0\uff0c\u4ed6\u7ed5\u8fc7\u7b2c\u4e8c\u5ea7\u57ce\u95e8\u540e\u5077\u51fa\u57ce\u3002\u56e0\u6b64\u803f\u6401\u4e86\u4e00 \u4e9b\u65f6\u5019\u3002\u800c\u8fd8\u80fd\u591f\u53ca\u65f6\u8d76\u5230\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u6df1\u77e5\u91d1\u9010\u6d41\u7684\u8f7b\u529f\u6781\u662f\u9ad8\u660e\uff0c\u8fdc\u8fdc\u5728\u5979\u4e4b\u4e0a\uff0c\u6599\u60f3\u8981\u8eb2\u4e5f\u662f\u8eb2\u4e0d\u5f00\u4e86\uff0c\u65e2\u7136\u8eb2 \u907f\u4e0d\u5f00\uff0c\u53ea\u597d\u628a\u5fc3\u4e00\u6a2a\uff0c\u548c\u91d1\u9010\u6d41\u62fc\u6253\u3002 \u3000\u3000\u5251\u5149\u97ad\u5f71\u4e4b\u4e2d\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u8463\u5341\u4e09\u5a18\u7684\u8170\u5e26\u7ed9\u91d1\u9010\u6d41\u5272\u65ad\uff0c\u8463\u5341\u4e09\u5a18\u6ee1\u9762\u901a \u7ea2\uff0c\u9a82\u9053\uff1a\u201c\u8d3c\u5c0f\u5b50\uff0c\u80c6\u6562\u8c03\u620f\u8001\u5a18\uff01\u201d\u91d1\u9010\u6d41\u563b\u563b\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u662f\u4f60\u8001\u4eba\u5bb6\u9519\u602a\u6211\u4e86\uff0c\u6211 \u91d1\u9010\u6d41\u7eb5\u7136\u597d\u8272\uff0c\u4e5f\u4e0d\u4f1a\u8c03\u620f\u4f60\u8001\u4eba\u5bb6\u554a\uff01\u563f\uff0c\u563f\uff0c\u53ea\u56e0\u4f60\u8001\u4eba\u5bb6\u5584\u4f1a\u5265\u4eba\u5bb6\u7684\u8863\u88f3\uff0c\u6211\u8fd9 \u4e0d\u8fc7\u662f\u4ee5\u5176\u4eba\u4e4b\u9053\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\u800c\u5df2\uff0c\u5c82\u6709\u4ed6\u54c9\uff01\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u6c14\u5f97\u4e00\u4f5b\u51fa\u4e16\uff0c\u4e8c\u4f5b\u5347\u5929\uff0c\u53ef\u662f\u5979\u8fd8\u672a\u66fe\u9a82\u5f97\u51fa\u53e3\uff0c\u91d1\u9010\u6d41\u500f\u5730\u5c31\u6b3a\u5230\u4e86\u5979\u7684 \u8eab\u524d\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u5411\u5979\u80a9\u4e0a\u7684\u7435\u7436\u9aa8\u6293\u4e0b\u3002\u8463\u5341\u4e09\u5a18\u970d\u5730\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u957f\u97ad\u5530\u5730\u626b\u4e86\u56de \u6765\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u91d1\u9010\u6d41\u7684\u4e24\u53ea\u6307\u5934\u5df2\u662f\u94b3\u7740\u5979\u7684\u8863\u9886\uff0c\u8eab\u5f62\u4e00\u65cb\uff0c \u628a\u5979\u7684\u4e00\u4ef6\u5916\u8863\u5265\u4e86\u4e0b\u6765\u3002\u8463\u5341\u4e09\u5a18\u4e5f\u597d\u751f\u4e86\u5f97\uff0c\u5de6\u8098\u4e00\u649e\uff0c\u91d1\u9010\u6d41\u7eb5\u8eab\u8dc3\u8d77\uff0c\u5377\u56de\u6765\u7684\u957f \u97ad\u4ece\u91d1\u9010\u6d41\u7684\u811a\u5e95\u63a0\u8fc7\u3002\u91d1\u9010\u6d41\u5012\u4e0d\u6562\u518d\u6293\u5979\u7684\u7435\u7436\u9aa8\uff0c\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7b4b\u6597\u907f\u5f00\u4e86\u5979\u7684\u8098\u9524\uff0c \u8f7b\u8f7b\u5de7\u5de7\u5730\u843d\u5728\u4e00\u4e08\u5f00\u5916\u3002\u7b11\u9053\uff1a\u201c\u4f60\u6495\u70c2\u4e86\u4eba\u5bb6\u7684\u8863\u88f3\uff0c\u4e0d\u8981\u8d54\u4e48\uff1f\u6211\u8fd9\u662f\u4e3b\u6301\u516c\u9053\uff0c\u4f60 \u8001\u4eba\u5bb6\u53ef\u4f11\u8981\u60f3\u6b6a\u4e86\u3002\u201d \u3000\u3000\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u4eba\u5df2\u5230\u4e86\u90a3\u4e2b\u5934\u7684\u8eab\u8fb9\uff0c\u7ed9\u5979\u89e3\u4e86\u7a74\u9053\u3002\u8bf4\u9053\uff1a\u201c\u8463\u9999\u4e3b\u7684\u8eab\u6750\u548c\u4f60\u5dee\u4e0d \u591a\uff0c\u8fd9\u4ef6\u8863\u88f3\u4f60\u4e00\u5b9a\u5408\u8eab\u3002\u201d \u3000\u3000\u828d\u836f\u7a7f\u4e0a\u4e86\u8463\u5341\u4e09\u5a18\u7684\u8863\u88f3\uff0c\u5fc3\u4e2d\u75db\u5feb\u4e4b\u6781\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u7ed9\u6211\u6253\u5979\u4e24\u8bb0\u8033 \u5149\uff01\u201d \u3000\u3000\u8463\u5341\u4e09\u5a18\u5927\u6012\u559d\u9053\uff1a\u201c\u6211\u62fc\u4e86\u8fd9\u6761\u6027\u547d\u4e0d\u8981\uff0c\u4e5f\u975e\u6740\u4f60\u8fd9\u81ed\u4e2b\u5934\u4e0d\u53ef\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u957f\u5251\u6325\u821e\uff0c\u628a\u8463\u5341\u4e09\u5a18\u6240\u53d1\u7684\u6697\u5668\u5168\u90e8\u53cd\u6253\u56de\u53bb\uff0c\u8463\u5341\u4e09\u5a18\u903c\u5f97\u6b65\u6b65\u540e\u9000\uff0c\u91d1\u9010 \u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e8f\u4f60\u8eab\u4e3a\u516d\u5408\u5e2e\u7684\u56db\u5927\u9999\u4e3b\u4e4b\u9996\uff0c\u6043\u5f3a\u6b3a\u5f31\uff0c\u81ea\u5df1\u4e5f\u4e0d\u89c9\u5f97\u5bb3\u7f9e\u4e48\uff1f\u54fc\uff0c\u6709 \u6211\u5728\u6b64\uff0c\u4f60\u60f3\u8981\u6740\u4eba\uff0c\u53c8\u7109\u80fd\u591f\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u4e00\u6325\u957f\u5251\uff0c\u5339\u7ec3\u822c\u7684\u5251\u5149\u53c8\u5377\u5230\u4e86\u8463\u5341\u4e09\u5a18 \u7684\u8eab\u540e\u3002\u8463\u5341\u4e09\u5a18\u53cd\u624b\u4e09\u97ad\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u89e3\u4e86\u4e00\u62db\uff0c\u4f46\u957f\u97ad\u53c8\u5df2\u7ed9\u91d1\u9010\u6d41\u524a\u53bb\u4e86\u4e00\u6bb5\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u5728\u91d1\u9010\u6d41\u7684\u5251\u5149\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u60f3\u62fc\u547d\u4e5f\u65e0\u4ece\u62fc\u8d77\uff0c\u5fc3\u91cc\u4e00\u51c9\uff0c\u4f46\u6c42\u901f\u6b7b\uff0c\u84e6\u5730\u56de \u8f6c\u5251\u950b\uff0c\u5411\u81ea\u5df1\u7684\u80f8\u53e3\u4fbf\u6233\u3002\u4e0d\u6599\u5979\u6c42\u751f\u4e0d\u5f97\uff0c\u6c42\u6b7b\u4ea6\u662f\u4e0d\u80fd\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u91d1\u9010\u6d41\u5df2 \u662f\u6b3a\u5230\u5979\u7684\u8eab\u524d\uff0c\u593a\u4e86\u5979\u7684\u77ed\u5251\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u53eb\u9053\uff1a\u201c\u6211\u8981\u6b7b\u4f60\u4e5f\u4e0d\u8bb8\u6211\u4e48\uff1f\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u7528\u4e0d\u7740\u6b7b\u3002\u201d\u4e2d\u6307\u4e00\u5f39\uff0c\u6b63 \u4e2d\u8463\u5341\u4e09\u5a18\u864e\u53e3\u7684\u201c\u5173\u5143\u7a74\u201d\uff0c\u8463\u5341\u4e09\u5a18\u957f\u97ad\u5760\u5730\uff0c\u6d51\u8eab\u9178\u8f6f\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u9996\u6076\uff0c\u6b7b\u7f6a\u53ef\u514d\uff1b\u4f46\u4f60\u6043\u5f3a\u51cc\u5f31\uff0c\u6d3b\u7f6a\u5374\u662f\u96be\u9976\uff01\u201d\u5de6\u53f3\u5f00\u5f13\uff0c\u567c \u567c\u556a\u556a\u5730\u6253\u4e86\u8463\u5341\u4e09\u5a18\u56db\u8bb0\u8033\u5149\u3002\u56de\u8fc7\u5934\u6765\uff0c\u7b11\u95f4\u828d\u836f\u9053\uff1a\u201c\u591f\u4e86\u4e48\u3002\u201d\u828d\u836f\u8fde\u547c\u75db\u5feb\uff0c\u7b11 \u591f\u4e4b\u540e\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u4e0d\u8981\u518d\u6253\u5979\u4e86\uff0c\u5c0f\u59d0\u6709\u8bdd\u53eb\u6211\u8ddf\u4f60\u8bf4\u5462\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u628a\u8463\u5341\u4e09\u5a18\u629b\u5165\u4e71\u8349\u4e1b\u4e2d\uff0c\u4ed6\u70b9\u7684\u7a74\u9053\u662f\u8981\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u540e\u65b9\u80fd\u81ea\u89e3\u7684\u3002\u56de\u8fc7\u5934 \u6765\uff0c\u53ea\u89c1\u828d\u836f\u5df2\u7ecf\u62fe\u8d77\u90a3\u6761\u9999\u7f57\u624b\u5e15\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5c0f\u59d0\u662f\u53eb\u4f60\u51fa\u6765\u627e\u5bfb\u6211\u7684\u4e48\uff1f\u201d \u3000\u3000\u828d\u836f\u9053\uff1a\u201c\u6b63\u662f\u3002\u5979\u53eb\u6211\u5411\u4e10\u5e2e\u6253\u542c\u4f60\u7684\u6d88\u606f\uff0c\u60f3\u4e0d\u5230\u5728\u8fd9\u65f6\u5c31\u9047\u89c1\u4f60\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u521a\u624d\u5728\u57ce\u91cc\u5df2\u7ecf\u89c1\u4e86\u5979\u4e86\u3002\u6211\u8fd8\u5077\u5077\u7684\u5199\u4e86\u51e0\u4e2a\u5b57\u629b\u7ed9\u5979\u5462\uff0c\u53ea\u53ef\u60dc \u6ca1\u6709\u673a\u4f1a\u548c\u5979\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u828d\u836f\u9053\uff1a\u201c\u8fd9\u53ef\u771f\u662f\u5de7\u6781\u4e86\uff0c\u6211\u4e5f\u6b63\u662f\u66ff\u5979\u634e\u4fe1\u7ed9\u4f60\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u662f\u4e48\uff0c\u4fe1\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u828d\u836f\u5c06\u9999\u841d\u624b\u5e15\u9012\u7ed9\u91d1\u9010\u6d41\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u5199\u5728\u8fd9\u6761\u624b\u5e15\u4e0a\u3002\u201d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5c0f\u59d0\u4e5f\u66fe\u731c \u60f3\u4f60\u53ef\u80fd\u5df2\u5230\u4e86\u897f\u660c\u7684\uff0c\u6240\u4ee5\u5979\u4eca\u5929\u624d\u7279\u5730\u85c9\u53e3\u9001\u6211\u51fa\u57ce\uff0c\u5728\u57ce\u4e2d\u9732\u9762\u3002\u4e0d\u8fc7\uff0c\u5979\u4e5f\u6050\u9632\u4f60 \u6ca1\u6709\u6765\uff0c\u56e0\u6b64\u53c8\u5199\u4e86\u8fd9\u5c01\u4fe1\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u5f97\u53f2\u7ea2\u82f1\u7528\u5fc3\u5982\u6b64\u5468\u5bc6\uff0c\u5927\u4e3a\u611f\u52a8\u3002\u5f53\u4e0b\u89e3\u5f00\u90a3\u6761\u9999\u841d\u624b\u5e15\uff0c\u53ea\u89c1\u4e0a\u9762\u6709\u51e0\u884c \u9c9c\u7ea2\u7684\u5c0f\u5b57\uff0c\u8fd9\u662f\u7528\u6307\u7532\u8638\u4e86\u80ed\u8102\u5199\u7684\uff0c\u841d\u5e15\u4e00\u89e3\uff0c\u5e7d\u9999\u6251\u9f3b\u3002 \u3000\u3000\u624b\u5e15\u4e0a\u5199\u7684\u662f\uff1a\u201c\u751f\u975e\u7537\u5b50\uff0c\u613f\u4f5c\u8346\u963f\uff1b\u6b7b\u4ea6\u9b3c\u96c4\uff0c\u65e0\u60ed\u77e5\u5df1\u3002\u5c82\u8346\u749e\u4e4b\u8f7b\u6cbd\uff0c\u60b2\u6d66\u73e0 \u4e4b\u96be\u8fd4\u3002\u77e5\u6211\u8005\u5176\u552f\u541b\u4e4e\uff1f\u55df\u55df\uff0c\u638f\u6c34\u4e2d\u4e4b\u6708\uff0c\u53ea\u63a5\u6e05\u8f89\uff1b\u96e8\u5929\u4e0a\u4e4b\u82b1\uff0c\u4f46\u95fb\u9999\u6c14\u3002\u601d\u672a\u6562 \u8a00\uff0c\u8c01\u80fd\u9080\u4e86\u5fc3\u540c\u6240\u613f\uff0c\u82e6\u5524\u5948\u4f55\uff1f\u4f46\u53e5\u5949\u5448\uff0c\u804a\u8868\u8877\u66f2\u3002\u201d \u3000\u3000\u540e\u9762\u9644\u4e00\u9996\u4e03\u8a00\u7edd\u53e5\uff0c\u8bd7\u9053\uff1a\u201c\u613f\u4f5c\u8346\u8f72\u8a93\u5165\u79e6\uff0c\u4f55\u60ed\u6d41\u6c34\u901a\u77e5\u97f3\u3002\u6b64\u751f\u5df2\u77e3\u4ed6\u751f\u5728\uff0c \u72b9\u6709\u5bd2\u6885\u4e00\u7247\u5fc3\u3002\u201d \u3000\u3000\u8fd9\u5c01\u4fe1\u662f\u53f2\u7ea2\u82f1\u8868\u660e\u81ea\u5df1\u7684\u5fc3\u4e8b\u7684\uff0c\u542b\u6709\u4e24\u6bb5\u610f\u601d\u3002\u524d\u4e00\u6bb5\u89e3\u91ca\u5979\u4e3a\u4f55\u201c\u5ac1\u201d\u7ed9\u5e05\u5b5f \u96c4\uff1a\u201c\u6211\u867d\u7136\u4e0d\u662f\u7537\u5b50\uff0c\u4e5f\u613f\u610f\u6548\u6cd5\u8346\u8f72\u90a3\u6837\u505a\u4e2a\u523a\u5ba2\u3002\u8346\u8f72\u5f53\u5e74\u662f\u4e3a\u62a5\u71d5\u592a\u5b50\u4e39\u77e5\u9047\u4e4b \u5fb7\uff0c\u884c\u523a\u79e6\u59cb\u7687\uff1b\u6211\u5219\u662f\u4e3a\u4e86\u4e0d\u8f9c\u8d1f\u4f60\u7684\u671f\u671b\uff0c\u6765\u884c\u523a\u5e05\u5b5f\u96c4\u3002\u6211\u672c\u662f\u65e0\u6687\u749e\u7f8e\u7389\uff08\u8346 \u749e\uff09\uff0c\u54ea\u4f1a\u8f7b\u6613\u51fa\u5356\u81ea\u5df1\u5462\uff1f\u6211\u7684\u7528\u5fc3\u4f60\u662f\u5e94\u8be5\u61c2\u5f97\u7684\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u6bb5\u5219\u662f\u5411\u91d1\u9010\u6d41\u8bc9\u8bf4\u5979\u7684\u60c5\u601d\uff1a\u201c\u6211\u662f\u62fc\u4e86\u4e00\u6b7b\u6765\u884c\u523a\u5e05\u5b5f\u96c4\u7684\uff0c\u53ea\u6015\u662f\u4e0d\u80fd\u5408\u7389 \u73e0\u8fd8\uff0c\u91cd\u56de\u5230\u4f60\u7684\u8eab\u8fb9\u4e86\u3002\u5509\uff0c\u6211\u6709\u610f\u548c\u4f60\u7ed3\u4ea4\uff0c\u5927\u5bb6\u7684\u5fc3\u4e8b\u867d\u7136\u90fd\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u76f8\u4fe1\u4f60\u4e5f \u4f1a\u660e\u767d\u7684\u5427\uff1f\u4f46\u53ea\u6015\u54b1\u4eec\u7684\u7f18\u4efd\uff0c\u5374\u662f\u5982\u6c34\u6708\u955c\u82b1\u822c\u7684\u865a\u5e7b\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5c01\u4fe1\u5199\u5f97\u60c5\u610f\u7f20\u7ef5\uff0c\u91d1\u9010\u6d41\u8bfb\u6765\u4e0d\u89c9\u6f78\u7136\u6cea\u4e0b\u3002\u5c24\u5176\u8bfb\u5230\u201c\u638f\u6c34\u4e2d\u4e4b\u6708\uff0c\u53ea\u63a5\u6e05\u8f89\uff1b \u96e8\u5929\u4e0a\u4e4b\u82b1\uff0c\u4f46\u95fb\u9999\u6c14\u3002\u201d\u4e24\u53e5\uff0c\u66f4\u662f\u60b2\u4ece\u4e2d\u6765\u3001\u96be\u4ee5\u65ad\u7edd\uff0c\u89c9\u5f97\u81ea\u5df1\u5b9e\u5728\u7cca\u6d82\uff0c\u5bf9\u4e0d\u8d77\u53f2 \u7ea2\u82f1\u3002 \u3000\u3000\u8fd9\u4e24\u53e5\u5199\u5f97\u5341\u5206\u542b\u84c4\uff0c\u542b\u6709\u4e24\u5c42\u610f\u601d\u3002\u53f2\u7ea2\u82f1\u628a\u4ed6\u4eec\u7684\u4ea4\u60c5\u6bd4\u4f5c\u6c34\u4e2d\u4e4b\u6708\uff0c\u5929\u4e0a\u4e4b\u82b1\u3002 \u201c\u6c34\u4e2d\u4e4b\u6708\u201d\u867d\u7136\u638f\u4e0d\u5230\u624b\uff0c\u4f46\u4e5f\u201c\u63a5\u201d\u5230\u4e86\u660e\u6708\u7684\u201c\u6e05\u8f89\u201d\uff1b\u5929\u4e0a\u96e8\u82b1\uff0c\u8fd9\u662f\u7f8e\u4e3d\u7684\u795e \u8bdd\uff0c\u5929\u4e0a\u7684\u82b1\u662f\u4e0d\u4f1a\u843d\u5230\u4eba\u95f4\u7684\uff0c\u4f46\u5f53\u6211\u548c\u4f60\u5728\u4e00\u8d77\u7684\u65f6\u5019\uff0c\u4e5f\u4f3c\u4e4e\u5230\u4e86\u8fd9\u4e2a\u5883\u754c\uff0c\u95fb\u5230\u4e86 \u82b1\u7684\u9999\u6c14\u3002\u8fd9\u4e00\u5c42\u7684\u610f\u601d\u5f71\u6df1\u8868\u4ef0\u6155\u4e4b\u60c5\uff1b\u7b2c\u4e8c\u5c42\u7684\u610f\u601d\u5374\u662f\u57cb\u6028\u91d1\u9010\u6d41\u6ca1\u6709\u5c06\u81ea\u5df1\u7684\u60c5\u610f \u5766\u767d\u5730\u8bf4\u51fa\u6765\u4e86\u3002\u4e0d\u8fc7\u867d\u7136\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u5979\u4e5f\u662f\u77e5\u9053\u7684\u3002\u201c\u6e05\u8f89\u201d\u5df2\u63a5\uff0c\u201c\u9999\u6c14\u201d\u5df1\u95fb\uff0c\u8fd9 \u5c31\u662f\u8868\u793a\u5979\u5df1\u7ecf\u77e5\u9053\u4e86\u3002\u4f46\u867d\u7136\u77e5\u9053\uff0c\u4e5f\u8fd8\u662f\u8bf4\u51fa\u6765\u7684\u597d\u3002\u5979\u7528\u4e0a\u4e00\u4e2a\u201c\u53ea\u201d\u5b57\uff0c\u4e00\u4e2a \u201c\u4f46\u201d\u5b57\uff1b\u5c31\u9690\u9690\u542b\u6709\u57cb\u6028\u91d1\u9010\u6d41\u7684\u610f\u601d\u3002 \u3000\u3000\u5be5\u5be5\u5341\u6570\u5b57\u4e2d\uff0c\u6709\u601d\u6155\uff0c\u6709\u5e7d\u6028\uff0c\u66f4\u6709\u65e0\u9650\u75f4\u60c5\u3002\u6cea\u773c\u6a21\u7cca\u4e2d\uff0c\u91d1\u9010\u6d41\u4eff\u4f5b\u770b\u5230\u53f2\u7ea2\u82f1 \u7d27\u9501\u53cc\u7709\u7684\u5f71\u5b50\u5728\u4ed6\u9762\u524d\u6447\u6643\uff0c\u4e0d\u7981\u53f9\u4e86\u53e3\u6c14\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6211\u4f55\u5c1d\u4e0d\u60f3\u5411\u4f60\u503e\u5410\u5fc3\u66f2\uff0c\u53ea \u56e0\u6211\u77e5\u9053\u5389\u5927\u54e5\u5bf9\u4f60\u4e5f\u662f\u4e00\u7247\u75f4\u60c5\uff0c\u800c\u6211\u53c8\u8fd8\u672a\u77e5\u9053\u4f60\u5bf9\u6211\u7adf\u662f\u60c5\u6df1\u5982\u6b64\uff0c\u5509\uff0c\u91d1\u9010\u6d41\u5440\u91d1 \u9010\u6d41\uff0c\u4f60\u771f\u662f\u7cca\u6d82\uff0c\u7537\u5973\u4e4b\u7231\uff0c\u7eaf\u51fa\u81ea\u7136\uff0c\u5c82\u80fd\u5f53\u4f5c\u8d27\u7269\u4e00\u6837\u8ba9\u7ed9\u4eba\u5462\uff1f\u201d \u3000\u3000\u201c\u4fe1\u201d\u5199\u5f97\u542b\u84c4\uff0c\u4e00\u5c42\u4e00\u5c42\u7684\u610f\u601d\u8981\u7ec6\u52a0\u5480\u56bc\u624d\u4f53\u4f1a\u51fa\u6765\uff0c\u4f46\u90a3\u9996\u8bd7\u5374\u5c31\u5199\u5f97\u5341\u5206\u660e\u663e \u4e86\u3002\u7b2c\u4e00\u53e5\u201c\u613f\u4f5c\u8346\u8f72\u8a93\u5165\u79e6\u201d\uff0c\u8fd9\u662f\u91cd\u590d\u4fe1\u4e2d\u7684\u610f\u601d\uff0c\u4e0d\u5fc5\u89e3\u91ca\u3002\u7b2c\u4e8c\u53e5\u201c\u4f55\u60ed\u6d41\u6c34\u9047\u77e5 \u97f3\u201d\u7528\u7684\u662f\u201c\u949f\u671f\u5df2\u9047\uff0c\u79e6\u6d41\u6c34\u4ee5\u4f55\u60ed\uff1f\u201d\u7684\u5178\u6545\uff0c\u76f4\u9648\u5979\u662f\u628a\u91d1\u9010\u6d41\u5f53\u4f5c\u77e5\u5df1\uff0c\u4e0d\u6015\u5411\u4ed6 \u5410\u9732\u5fc3\u4e8b\u3002\u7b2c\u4e09\u53e5\u201c\u6b64\u751f\u5df2\u77e3\u4ed6\u751f\u5728\u201d\uff0c\u90a3\u5c31\u66f4\u662f\u5927\u80c6\u7684\u76f4\u5410\u80f8\u81c6\u4e86\uff0c\u201c\u4eca\u751f\u6211\u662f\u4e0d\u80fd\u548c\u4f60 \u505a\u592b\u59bb\u4e86\uff0c\u8fd9\u5fc3\u613f\u4f46\u613f\u5728\u6765\u751f\u507f\u8fd8\u5427\u3002\u201d\u7b2c\u56db\u53e5\u201c\u72b9\u6709\u5bd2\u6885\u4e00\u7247\u5fc3\u201d\uff0c\u628a\u8fd9\u756a\u60c5\u610f\u52a0\u6df1\u4e00 \u5c42\uff0c\u201c\u4eca\u751f\u867d\u7136\u4e0d\u80fd\u548c\u4f60\u505a\u592b\u59bb\uff0c\u4f46\u6211\u6b3a\u971c\u505a\u971c\u50cf\u6885\u82b1\u4e00\u6837\u7684\u7cbe\u795e\uff0c\u6b7b\u4e86\u4e5f\u8fd8\u662f\u5b58\u5728\u7684\uff0c\u8fd9 \u5fc3\u4e8b\u4f60\u662f\u5e94\u8be5\u660e\u767d\u554a\uff01\u201d \u3000\u3000\u82e5\u5728\u5e73\u65f6\uff0c\u53f2\u7ea2\u82f1\u8fd9\u7247\u6df1\u60c5\uff0c\u662f\u51b3\u4e0d\u4f1a\u8fd9\u6837\u5927\u80c6\u5411\u91d1\u9010\u6d41\u503e\u5410\u7684\uff0c\u53ea\u6709\u5728\u5979\u51b3\u6025\u4e00\u6b7b\u7684 \u65f6\u5019\uff0c\u8fd9\u624d\u6562\u4e8e\u5199\u51fa\u6765\u3002 \u3000\u3000\u828d\u836f\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u54ed\u4ec0\u4e48\u5462\uff1f\u54ed\u53c8\u6709\u4ec0\u4e48\u7528\uff0c\u4f60\u5e94\u8be5\u8bbe\u6cd5\u6551\u6211\u4eec\u7684\u5c0f\u59d0\u554a\u3002\u201d\u5979\u4e0d \u89e3\u91d1\u9010\u6d41\u56e0\u4f55\u6d41\u6cea\uff0c\u53ea\u9053\u91d1\u9010\u6d41\u662f\u5728\u4f24\u5fc3\u4e8e\u6b7b\u522b\u751f\u79bb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u970d\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5e94\u8be5\u56de\u53bb\u8bbe\u6cd5\u6551\u4f60\u5bb6\u5c0f\u59d0\uff0c\u4f60\u4e5f\u5e94\u8be5\u8d76\u5feb\u8d70\u4e86\u3002\u201d \u828d\u836f\u90a3\u5339\u5750\u9a91\u5df2\u7ecf\u4e2d\u6bd2\u6b7b\u4e86\uff0c\u5e78\u597d\u6709\u8463\u5341\u4e09\u5a18\u7559\u4e0b\u7684\u4e00\u5339\u5750\u9a91\uff0c\u828d\u836f\u4fbf\u4e58\u4e86\u5979\u7684\u5750\u9a91\uff0c\u75be\u9a70 \u800c\u53bb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5c06\u90a3\u65b9\u9999\u841d\u624b\u5e15\u8d34\u8089\u6536\u85cf\uff0c\u9999\u841d\u624b\u5e15\u5374\u5728\u4ed6\u7684\u5fc3\u5934\u3002\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u611f\u5230\u751c\u4e1d\u4e1d\u7684\u3002 \u53ef\u662f\u5728\u4ed6\u6ee1\u6000\u559c\u60a6\u4e4b\u4e2d\uff0c\u5ffd\u5730\u5c31\u6709\u4e00\u4e2a\u5ff5\u5934\u5347\u8d77\uff1a\u201c\u7ea2\u82f1\u5bf9\u6211\u4e00\u7247\u6df1\u60c5\uff0c\u4f46\u5389\u5927\u54e5\u5374\u672a\u5fc5\u77e5 \u9053\u3002\u5728\u4ed6\u7684\u5fc3\u4e2d\uff0c\u53ea\u6015\u8fd8\u662f\u4e00\u79cd\u60c5\u613f\u7684\u9519\u628a\u7ea2\u82f1\u7684\u53cb\u8c0a\u5f53\u4f5c\u4e86\u7231\u60c5\u5462\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u770b\u4e86\u90a3\u65b9\u8bd7\u5e16\uff0c\u8fc7\u53bb\u7684\u79cd\u79cd\u8bef\u4f1a\u90fd\u5df2\u51b0\u6d88\uff0c\u4e00\u5207\u4e5f\u90fd\u4e86\u7136\u4e8e\u80f8\u4e86\u3002\u4ed6\u77e5\u9053\u53f2\u7ea2\u82f1 \u5bf9\u5389\u5357\u661f\u7684\u611f\u60c5\u7eaf\u662f\u53cb\u8c0a\uff0c\u5bf9\u53f2\u3001\u5389\u90a3\u6b21\u7684\u201c\u5a5a\u4e8b\u201d\uff0c\u4e0d\u5fc5\u53f2\u7ea2\u82f1\u5411\u4ed6\u89e3\u91ca\uff0c\u4ed6\u4e5f\u731c\u60f3\u5f97\u5230 \u53f2\u7ea2\u82f1\u7684\u7528\u5fc3\uff0c\u5bf9\u5979\u5b8c\u5168\u8c05\u89e3\u3002 \u3000\u3000\u53ef\u662f\u60f3\u5230\u4e86\u90a3\u6869\u201c\u5a5a\u4e8b\u201d\uff0c\u91d1\u9010\u6d41\u5fc3\u4e0a\u7684\u4e00\u4e2a\u201c\u7ed3\u201d\u4ecd\u662f\u672a\u80fd\u89e3\u9664\u3002\u201c\u90a3\u6869\u5a5a\u4e8b\u201d\u4e8b\u5b9e \u5df2\u81ea\u8bc1\u660e\u662f\u53f2\u767d\u90fd\u6446\u4e0b\u7684\u5708\u5957\uff0c\u7528\u6765\u8bf1\u9a97\u5389\u5927\u54e5\u4e0a\u5f53\u7684\u3002\u7ea2\u82f1\u4e4b\u6240\u4ee5\u5047\u610f\u7b54\u5e94\u5a5a\u4e8b\uff0c\u6599\u60f3\u4e5f \u662f\u56e0\u4e3a\u5389\u5927\u54e5\u662f\u6211\u7684\u597d\u53cd\u7684\u7f18\u6545\uff0c\u5979\u5f53\u65f6\u5b64\u7acb\u65e0\u63f4\uff0c\u5047\u610f\u7b54\u5141\u5a5a\u4e8b\u5c31\u5bf9\u4ee5\u548c\u5389\u5927\u54e5\u8054\u624b\u5bf9\u4ed8 \u5979\u7684\u54e5\u54e5\u3002\u4f46\u5f53\u665a\u4ed6\u4eec\u624d\u5165\u2018\u6d1e\u623f\u2019\u53f2\u767d\u90fd\u7684\u4f0f\u5175\u5df2\u51fa\uff0c\u5979\u7684\u8fd9\u756a\u7528\u5fc3\uff0c\u5374\u4e0d\u77e5\u5df2\u7ecf\u548c\u5389\u5927 \u54e5\u8bf4\u4e86\u6ca1\u6709\u3002\u5389\u5927\u54e5\u662f\u548c\u5979\u884c\u4e86\u793c\u7684\uff0c\u540d\u4efd\u4e0a\u7ea2\u82f1\u8fd8\u662f\u4ed6\u7684\u59bb\u5b50\uff0c\u6211\u600e\u80fd\u593a\u2018\u5ac2\u2019\u4e3a\u59bb\uff1f\u5373 \u4f7f\u53ef\u4ee5\u5411\u4ed6\u89e3\u91ca\uff0c\u4f46\u6211\u5374\u53c8\u600e\u751f\u5f00\u53e3\uff1f\u5509\uff0c\u8fd9\u4e0d\u4f46\u8981\u4f7f\u5389\u5927\u54e5\u96be\u4ee5\u4e3a\u60c5\uff0c\u6211\uff0c\u6211\u4e5f\u4e0d\u613f\u4ed6\u5fc3 \u53d7\u521b\u4f24\u7684\u554a\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u54ea\u91cc\u77e5\u9053\uff0c\u90a3\u65e5\u7684\u201c\u5a5a\u793c\u201d\uff0c\u53f2\u7ea2\u82f1\u662f\u7528\u4e00\u4e2a\u4e2b\u5934\u66ff\u5979\u62dc\u5802\uff1b\u5389\u5357\u661f\u4e0d\u4f46\u65e9\u5df2\u5c3d \u6089\u5176\u4e2d\u539f\u59d4\uff0c\u800c\u4e14\u6b63\u662f\u6df1\u81ea\u62b1\u6127\uff0c\u7279\u5730\u8d76\u6765\u897f\u660c\uff0c\u60f3\u627e\u91d1\u9010\u6d41\u8bf4\u660e\u6b64\u4e8b\u7684\u3002 \u3000\u3000\u53ef\u60dc\uff0c\u4ed6\u867d\u7136\u77e5\u9053\u4e86\u91d1\u9010\u6d41\u5df2\u7ecf\u5230\u4e86\u897f\u660c\uff0c\u5374\u662f\u65e0\u6cd5\u4e0e\u91d1\u9010\u6d41\u89c1\u9762\u3002 \u3000\u3000\u4e14\u8bf4\u5389\u5357\u661f\u4e0e\u674e\u6566\u90a3\u665a\u4ece\u5ba2\u6808\u9003\u4e86\u51fa\u6765\u5728\u674e\u6566\u4e00\u4f4d\u670b\u53cb\u5bb6\u4e2d\uff0c\u8fd9\u4eba\u540d\u53eb\u5173\u5927\u4f26\uff0c\u662f\u4e49\u519b \u6d3e\u5728\u897f\u660c\u201c\u5367\u5e95\u201d\u7684\u4e00\u4e2a\u4eba\uff0c\u5728\u5c06\u519b\u5e9c\u4e2d\u62c5\u4efb\u4e00\u4e2a\u4e0d\u5927\u4e0d\u5c0f\u7684\u5dee\u4e8b\u3002\u6b63\u56e0\u4e3a\u4ed6\u5728\u5c06\u519b\u5e9c\u4e2d\u6709 \u4e2a\u6302\u540d\u5dee\u4e8b\uff0c\u90a3\u665a\u5728\u57ce\u4e2d\u5927\u52a0\u641c\u7d22\u7684\u5b98\u5175\uff0c\u5728\u4ed6\u7684\u5bb6\u4e2d\u53ea\u662f\u7565\u7565\u4e00\u770b\uff0c\u5e76\u6ca1\u4ed4\u7ec6\u641c\u67e5\uff0c\u5389\u3001 \u674e\u4e8c\u4eba\u8fd9\u624d\u5f97\u4ee5\u8eb2\u8fc7\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u9001\u828d\u836f\u51fa\u57ce\uff0c\u4ee5\u201c\u65b0\u5a18\u5b50\u201d\u7684\u8eab\u4efd\u5728\u8857\u4e0a\u629b\u5934\u9732\u9762\uff0c\u6b64\u4e8b\u8f70\u52a8\u5168\u57ce\uff0c\u5389\u3001\u674e\u4e8c\u4eba \u8eb2\u5728\u5173\u5927\u4f26\u5bb6\u4e2d\u4e5f\u77e5\u9053\u4e86\u3002\u5389\u5357\u661f\u6599\u60f3\u91d1\u9010\u6d41\u4e00\u5b9a\u4f1a\u5728\u51fa\u7ea2\u82f1\u6240\u7ecf\u4e4b\u5904\u51fa\u73b0\u7684\uff0c\u53ef\u60dc\u4ed6\u5374\u4e0d \u80fd\u51fa\u73b0\u3002 \u3000\u3000\u4e2d\u5348\u65f6\u5206\uff0c\u5173\u5927\u4f26\u5e26\u56de\u6765\u4e00\u4e2a\u6d88\u606f\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u5927\u54e5\uff0c\u4f60\u53ef\u4ee5\u653e\u5fc3\u4e86\uff0c\u5927\u5ac2\u5df2\u7ecf\u5e73\u5b89\u8fdb \u4e86\u57ce\u5566\u3002\u5979\u662f\u6df7\u5728\u4e50\u5bb6\u7684\u73ed\u5b50\u91cc\u8fdb\u6765\u7684\uff0c\u8fdb\u57ce\u7684\u65f6\u5019\uff0c\u6b63\u597d\u78b0\u7740\u53f2\u7ea2\u82f1\u51fa\u57ce\uff0c\u5f97\u4ee5\u514d\u53d7\u76d8 \u67e5\u3002\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a\u4eba\u4e5f\u8ddf\u5979\u6df7\u4e86\u8fdb\u6765\uff0c\u674e\u5927\u54e5\uff0c\u4f60\u731c\u6e05\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u54c8\uff0c\u53ea\u6015\u4f60\u4e5f\u6599\u60f3\u4e0d \u5230\uff01\u201d \u3000\u3000\u674e\u6566\u542c\u8bf4\u59bb\u5b50\u5df2\u7ecf\u5e73\u5b89\u8fdb\u4e86\u57ce\uff0c\u5fc3\u91cc\u751a\u4e3a\u9ad8\u5174\uff0c\u7b11\u9053\uff1a\u201c\u8ddf\u5979\u4e00\u8d77\u6765\u7684\uff0c\u90a3\u4e00\u5b9a\u662f\u4e2a\u5973 \u5b50\u4e86\u3002\u662f\u7afa\u5c1a\u7236\u7684\u5973\u513f\u7afa\u6e05\u534e\u5417\uff1f\u201d\u5173\u5927\u4f26\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u662f\u7ea2\u7f28\u4f1a\u603b\u8235\u4e3b\u516c\u5b59\u5b8f\u7684\u5973\u513f\u516c\u5b59 \u71d5\u3002\u54c8\u54c8\uff0c\u8fd9\u4f60\u53ef\u6ca1\u6709\u6599\u5230\u5427\uff1f\u201d \u3000\u3000\u674e\u6566\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u662f\u6ca1\u6709\u6599\u5230\u3002\u516c\u5b59\u8235\u4e3b\u4e5f\u5230\u4e86\u5927\u51c9\u5c71\u4e48\u3002\u201d \u3000\u3000\u5173\u5927\u4f26\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u77e5\u3002\u4e0d\u8fc7\u6709\u4ed6\u5973\u513f\u6765\u5230\uff0c\u4ea6\u5df2\u53ef\u4ee4\u53f2\u767d\u90fd\u80c6\u5bd2\u4e86\u3002\u201d\u8981\u77e5\u7ea2\u7f28\u4f1a\u4e43 \u662f\u6c5f\u6e56\u4e0a\u7684\u7b2c\u4e00\u5927\u5e2e\u4f1a\uff0c\u52bf\u529b\u8fd8\u5728\u516d\u5408\u5e2e\u4e4b\u4e0a\uff0c\u516c\u5b59\u5b8f\u7684\u5973\u513f\u82e5\u662f\u633a\u8eab\u800c\u51fa\uff0c\u76f8\u52a9\u4e49\u519b\uff0c\u53f2 \u767d\u90fd\u81ea\u662f\u4e0d\u80fd\u4e0d\u987e\u5fcc\u4e09\u5206\u3002 \u3000\u3000\u5173\u5927\u4f26\u9053\uff1a\u201c\u54b1\u4eec\u7684\u4eba\u5df2\u7ecf\u548c\u4e50\u5bb6\u73ed\u5b50\u63a5\u4e0a\u4e86\u5934\uff0c\u5927\u5ac2\u4e5f\u77e5\u9053\u4f60\u662f\u5728\u6211\u8fd9\u91cc\u4e86\u3002\u4e0d\u8fc7\u6211 \u4e3a\u4e86\u8c28\u614e\u8d77\u89c1\uff0c\u8fd8\u662f\u8bf7\u5979\u6682\u65f6\u4e0d\u8981\u6765\u6b64\u770b\u4f60\uff0c\u4f60\u4e0d\u4f1a\u602a\u6211\u963b\u62e6\u4f60\u4eec\u592b\u59bb\u76f8\u4f1a\u5427\uff1f\u201d \u3000\u3000\u674e\u6566\u7b11\u9053\uff1a\u201c\u5c0f\u5fc3\u4e3a\u4e0a\uff0c\u8fd9\u662f\u5e94\u8be5\u7684\u3002\u5173\u5927\u54e5\u8bf7\u522b\u53d6\u7b11\u3002\u201d \u3000\u3000\u5173\u5927\u4f26\u53c8\u9053\uff1a\u201c\u4e0d\u77e5\u600e\u7684\uff0c\u5389\u5927\u54e5\u5230\u4e86\u897f\u660c\uff0c\u8fd9\u4ef6\u4e8b\u5979\u4eec\u4e5f\u77e5\u9053\u4e86\u3002\u4f46\u548c\u5979\u4eec\u63a5\u5934\u7684\u90a3 \u4e00\u4eba\uff0c\u5982\u4e0d\u77e5\u9053\u5389\u5927\u54e5\u4e5f\u662f\u5728\u6211\u8fd9\u513f\u3001\u5979\u5012\u8fd8\u8bf7\u4ed6\u6253\u63a2\u5389\u5927\u54e5\u7684\u6d88\u606f\u5462\u3002\u201d \u3000\u3000\u674e\u6566\u8be7\u900d\uff1a\u201c\u62d9\u8346\u4ece\u672a\u89c1\u8fc7\u5389\u5927\u54e5\uff0c\u5979\u5374\u662f\u600e\u5730\u77e5\u9053\u7684\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u516c\u5b59\u71d5\u662f\u4ece\u5927\u51c9\u5c71\u6765\u7684\uff0c\u60f3\u5fc5\u662f\u5979\u544a\u8bc9\u4e86\u674e\u5927\u5ac2\u3002\u201d \u3000\u3000\u5173\u5927\u4f26\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u516c\u5b59\u5c0f\u59d0\u5012\u662f\u5f88\u6302\u5ff5\u4f60\u5462\uff0c\u8981\u4e0d\u8981\u544a\u8bc9\u5979\u4f60\u5728\u8fd9\u513f\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u6447\u624b\u9053\uff1a\u201c\u6211\u770b\u4e0d\u5fc5\u591a\u6b64\u4e00\u4e3e\u4e86\u3002\u201d\u674e\u6566\u4e5f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u4eec\u867d\u7136\u662f\u53d7\u8058\u800c\u6765\uff0c \u4f46\u4e00\u5b9a\u4e5f\u662f\u6709\u4eba\u76d1\u89c6\u7684\uff0c\u54b1\u4eec\u7684\u4eba\u4e0d\u5b9c\u548c\u5979\u4eec\u591a\u901a\u6d88\u606f\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u77e5\u9053\u4e86\u516c\u5b59\u71d5\u5df2\u7ecf\u6765\u5230\u897f\u660c\u4e4b\u540e\uff0c\u5fc3\u7eea\u751a\u4e0d\u5b89\u4e86\u3002\u8fd9\u4e00\u665a\u7ffb\u6765\u8986\u53bb\uff0c\u7761\u4e0d\u7740\u89c9\uff0c \u6697\u81ea\u60f3\u9053\uff1a\u201c\u5979\u4e00\u5b9a\u662f\u7792\u7740\u7afa\u5c1a\u7236\u5077\u5077\u7684\u6765\u627e\u6211\u7684\uff0c\u54b3\uff0c\u60f3\u4e0d\u5230\u5979\u5bf9\u6211\u7adf\u662f\u5982\u6b64\u5173\u5fc3\uff0c\u4e0d\u60dc \u4e3a\u6211\u5192\u6027\u547d\u4e4b\u9669\uff01\u53ea\u53ef\u60dc\u6211\u662f\u66fe\u7ecf\u6ca7\u6d77\u96be\u4e3a\u6c34\uff0c\u9664\u5374\u5deb\u5c71\u4e0d\u662f\u4e91\uff0c\u6050\u6015\u662f\u8981\u8f9c\u8d1f\u5979\u7684\u5fc3\u4e8b\u7684 \u4e86\u3002\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u5389\u5357\u661f\u4e00\u95ed\u4e0a\u4e86\u773c\u775b\uff0c\u516c\u5b59\u71d5\u90a3\u5a07\u61a8\u53ef\u7231\u7684\u5f71\u5b50\u5c31\u5728\u4ed6\u7684\u773c\u524d\u6447\u6643\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u5df1\u662f\u5230\u4e86\u5e05\u5b5f\u96c4\u7ed3\u5a5a\u7684\u201c\u5409\u65e5\u201d\uff0c\u5a5a\u793c\u5b9a\u4e8e\u4e2d\u5348\u4e3e\u884c\u3002\u5389\u5357\u661f\u3001\u674e\u6566\u4e8c\u4eba\u626e\u4f5c\u5173 \u5927\u4f26\u7684\u968f\u4ece\uff0c\u8ddf\u7740\u4ed6\u8fdb\u4e86\u5c06\u519b\u5e9c\u3002 \u3000\u3000\u5c06\u519b\u5e9c\u6324\u6ee1\u4e86\u672c\u5730\u5b98\u5458\u4e0e\u5404\u65b9\u8d3a\u5ba2\uff0c\u793c\u5802\u5916\u9762\u662f\u4e00\u4e2a\u5927\u9662\u5b50\uff0c\u4e1c\u9762\u6709\u4e00\u53f0\u620f\u4e0a\u6f14\uff0c\u897f\u9762 \u5219\u662f\u8bf4\u9f13\u4e66\u548c\u6e05\u5531\u7684\u4e50\u5bb6\u73ed\u5b50\uff0c\u53e6\u5916\u82b1\u56ed\u91cc\u8fd8\u6709\u51e0\u53f0\u620f\u3002\u81ea\u95ee\u6ca1\u6709\u8d44\u683c\u8fdb\u793c\u5802\u89c2\u793c\u7684\u4eba\uff0c\u90fd \u96c6\u4e2d\u5728\u9662\u5b50\u548c\u82b1\u56ed\u91cc\u770b\u620f\u542c\u6b4c\u3002 \u3000\u3000\u5173\u5927\u4f26\u7b49\u4eba\u6324\u5230\u4e86\u9662\u5b50\uff0c\u53ea\u89c1\u5468\u56f4\u5df2\u7ecf\u5e03\u6ee1\u4e86\u4fbf\u8863\u536b\u58eb\u3002\u5173\u5927\u4f26\u662f\u5728\u5c06\u519b\u5e9c\u5f53\u5dee\u7684\u8ba4\u5f97 \u8fd9\u4e9b\u536b\u58eb\uff0c\u5176\u4ee4\u5f97\u4ed6\u4eec\u5403\u60ca\u7684\u662f\uff0c\u5728\u793c\u5802\u95e8\u53e3\uff0c\u7ad9\u7740\u4e00\u4e2a\u516d\u5408\u5e2e\u7684\u9999\u4e3b\u8463\u5341\u4e09\u5a18\u3002 \u3000\u3000\u5728\u8463\u5341\u4e09\u7684\u4e24\u65c1\u7ad9\u7acb\u7684\u662f\u9752\u7b26\u9053\u4eba\u4e0e\u5706\u6d77\u548c\u5c1a\uff0c\u8fd9\u4e09\u4e2a\u4eba\u90fd\u662f\u91d1\u775b\u706b\u773c\u7684\u6ce8\u89c6\u7740\u6bcf\u4e00\u4e2a \u8fdb\u5165\u793c\u5802\u7684\u4eba\u3002\u5389\u5357\u661f\u6d8c\u5230\u4e86\u53f0\u9636\u4e0b\u9762\uff0c\u6b63\u597d\u542c\u5f97\u5706\u6d77\u7c97\u58f0\u7c97\u6c14\u5730\u8bf4\u9053\uff1a\u201c\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\u5316 \u4e86\u7070\u6211\u4e5f\u8ba4\u5f97\uff0c\u4ed6\u82e5\u6562\u6765\uff0c\u6211\u820d\u4e86\u547d\u4e5f\u5f97\u66ff\u4f60\u62a5\u6628\u65e5\u4e4b\u4ec7\u3002\u201d\u8463\u5341\u4e09\u5a18\u9053\u201c\u4f60\u56b7\u4ec0\u4e48\uff1f\u662f\u8981 \u51fa\u6211\u7684\u4e11\u5417\uff01\u54fc\uff0c\u6211\u53ea\u6015\u8fd9\u5c0f\u5b50\u4e0d\u6765\uff01\u201d\u5706\u6d77\u9053\uff1a\u201c\u662f\uff0c\u662f\uff0c\u4f60\u4e0d\u8bb8\u6211\u8bf4\u8bdd\u6211\u5c31\u4e0d\u8bf4\u597d \u5566\uff01\u201d\u53ef\u662f\u4ed6\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u5495\u54dd\u4e00\u53e5\u9053\uff1a\u201c\u4e5f\u96be\u602a\u4f60\u751f\u6c14\uff0c\u4f60\u6628\u5929\u5403\u7684\u4e8f\u59d4\u5b9e\u662f\u592a\u5927\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u8463\u5341\u4e09\u5a18\u7ed9\u91d1\u9010\u6d41\u7528\u91cd\u624b\u6cd5\u70b9\u4e86\u7a74\u9053\uff0c\u672c\u6765\u662f\u8981\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u540e\u65b9\u80fd\u81ea\u89e3\u7684\uff0c\u53f2\u767d \u90fd\u7b49\u4e0d\u89c1\u5979\u56de\u6765\uff0c\u6d3e\u4e86\u9752\u7b26\u3001\u5706\u6d77\u4e24\u4eba\u6765\u627e\uff0c\u627e\u7740\u4e86\u5979\uff0c\u66ff\u5979\u89e3\u4e86\u7a74\u9053\uff0c\u8fd9\u624d\u80fd\u591f\u53ca\u65f6\u8d76 \u5230\u3002\u5979\u5403\u4e86\u5982\u6b64\u5927\u4e8f\uff0c\u5f53\u7136\u662f\u4e0d\u80af\u628a\u91d1\u9010\u6d41\u653e\u8fc7\u4e86\u3002\u5e05\u76c2\u96c4\u5f97\u77e5\u91d1\u9010\u6d41\u786e\u5b9e\u5df2\u5230\u897f\u660c\uff0c\u5fc3\u91cc \u4e5f\u4e0d\u7981\u6697\u6697\u5403\u60ca\uff0c\u56e0\u6b64\u4e5f\u5c31\u66f4\u52a0\u5f3a\u4e86\u9632\u5907\u3002 \u3000\u3000\u592b\u5927\u4f26\u672c\u4eba\u662f\u6709\u8d44\u683c\u8ff8\u793c\u5802\u89c2\u793c\u7684\uff0c\u4f46\u5374\u4e0d\u4fbf\u5e26\u968f\u4ece\u8fdb\u53bb\u3002\u8463\u5341\u4e09\u5a18\u7b49\u4eba\u5728\u793c\u5802\u95e8\u53e3\u864e \u89c6\u803d\u803d\uff0c\u674e\u6566\u548c\u5389\u5357\u661f\u867d\u7136\u4e1a\u5df2\u6539\u5bb9\u6613\u8c8c\uff0c\u4e5f\u6015\u7792\u4e0d\u8fc7\u5979\u7684\u773c\u775b\u3002\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u653e\u5f03\u8fdb\u5165 \u793c\u5802\u7684\u6253\u7b97\uff1a\u5728\u9662\u5b50\u671b\u5047\u88c5\u770b\u620f\uff0c\u6df7\u8fdb\u4e86\u4eba\u4e1b\u4e4b\u4e2d\u3002 \u3000\u3000\u5389\u5357\u661f\u53c8\u60ca\u53c8\u559c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u9010\u6d41\u679c\u7136\u662f\u6765\u4e86\uff0c\u793c\u5802\u770b\u5b88\u5f97\u8fd9\u6837\u4e25\u5bc6\uff0c\u4ed6\u82e5\u662f\u5df2\u7ecf\u6df7 \u5165\u793c\u5802\uff0c\u4e00\u5b9a\u4f1a\u7ed9\u4eba\u53d1\u73b0\u3002\u91cc\u9762\u65e2\u7136\u6ca1\u6709\u95f9\u4e8b\uff0c\u60f3\u5fc5\u4ed6\u662f\u5728\u8fd9\u9662\u5b50\u4e4b\u4e2d\u3002\u201d\u624b\u4e0a\u6234\u8d77\u4e86\u91d1\u9010 \u6d41\u7236\u4eb2\u7ed9\u4ed6\u7684\u90a3\u4e2a\u621b\u7389\u6212\u6307\uff0c\u5e0c\u671b\u91d1\u9010\u6d41\u89c1\u4e86\u8fd9\u4e2a\u6212\u6307\uff0c\u8ba4\u51fa\u662f\u4ed6\u3002\u540c\u65f6\u4ed6\u81ea\u5df1\u4e5f\u5728\u6697\u4e2d\u7559 \u610f\u9662\u5b50\u91cc\u7684\u5ba2\u4eba\u3002 \u3000\u3000\u770b\u6765\u770b\u53bb\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u50cf\u91d1\u9010\u6d41\uff0c\u4e5f\u6ca1\u6709\u5f62\u8ff9\u53ef\u7591\u7684\u4eba\u62e5\u5230\u4ed6\u7684\u8eab\u8fb9\u3002\u5389\u5357\u661f\u597d\u751f\u5931 \u671b\uff0c\u5fc3\u60f3\uff1a\u201c\u9010\u6d41\u4e00\u5b9a\u4f1a\u6765\u7684\uff0c\u5374\u600e\u7684\u8fd8\u4e0d\u89c1\u4ed6\u6765\u5462\uff1f\u201d \u3000\u3000\u6b64\u65f6\u4e50\u5bb6\u73ed\u5b50\u7684\u59d1\u5a18\u90fd\u5df2\u6392\u5217\u53f0\u4e0a\uff0c\u674e\u6566\u7684\u59bb\u5b50\u4f55\u5f69\u51e4\u6b63\u5728\u8bf4\u9f13\u4e66\u3002\u674e\u6566\u62e5\u5230\u4e86\u53f0\u4e0b\uff0c \u5389\u5357\u661f\u7b49\u4e0d\u89c1\u91d1\u9010\u6d41\uff0c\u4e5f\u53ea\u597d\u59d1\u4e14\u542c\u4e66\u3002 \u3000\u3000\u516c\u5b59\u71d5\u7528\u9752\u5e03\u5305\u5934\uff0c\u624b\u62b1\u7435\u7436\uff0c\u626e\u6210\u4e00\u4e2a\u73ed\u5b50\u91cc\u7684\u59d1\u5a18\u3002\u5979\u867d\u7136\u5316\u4e86\u88c5\uff0c\u4f46\u90a3\u53cc\u7075\u6d3b\u7684 \u773c\u775b\uff0c\u5389\u5357\u661f\u4e00\u770b\u5c31\u8ba4\u51fa\u6765\u4e86\u3002 \u3000\u3000\u5389\u5357\u661f\u6b63\u5728\u76d8\u7b97\u7528\u4ec0\u4e48\u65b9\u6cd5\u548c\u516c\u5b59\u71d5\u6253\u4e2a\u62db\u547c\uff0c\u5ffd\u542c\u5f97\u54e8\u5450\u58f0\u54cd\uff0c\u9f13\u4e50\u9f50\u9e23\uff0c\u97ad\u70ae\u567c\u567c \u556a\u556a\u7684\u7206\u4e86\u8d77\u6765\u3002\u65b0\u5a18\u7684\u82b1\u8f7f\u5df2\u7ecf\u62ac\u5230\u5e9c\u95e8\u3002 \u3000\u3000\u53f2\u767d\u90fd\u62a4\u9001\u59b9\u59b9\u7d27\u8ddf\u5728\u82b1\u8f7f\u540e\u5934\uff0c\u9662\u5b50\u91cc\u7684\u5ba2\u4eba\u95ea\u5f00\u6761\u8def\uff0c\u53f2\u767d\u90e8\u628a\u59b9\u59b9\u6276\u51fa\u82b1\u8f7f\uff0c\u4e00 \u4e2a\u4f34\u5a18\u4e00\u4e2a\u4e2b\u5934\u4e00\u5148\u3001\u4e00\u540e\u7684\u7275\u7740\u65b0\u5a18\u6b65\u5165\u793c\u5802\u3002\u8fd9\u4e2a\u4e2b\u5934\u5c31\u662f\u53f2\u7ea2\u82f1\u90a3\u4e2a\u5fc3\u8179\u5382\u9b1f\u7261\u4e39\u3002 \u5979\u662f\u4e0b\u4e86\u51b3\u5fc3\u6765\u4e0e\u53f2\u7ea2\u82f1\u540c\u751f\u5171\u6b7b\u7684\u3002\u6b63\u662f\uff1a \u3000\u3000\u4e3b\u5a62\u540c\u5fc3\u95ef\u864e\u7a74\uff0c\u8981\u5c06\u70ed\u8840\u6d12\u534e\u5802\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u4e14\u542c\u4e0b\u95ee\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e09\u5341\u516d\u56de\u3000\u5e15\u4e0a\u8102\u75d5\u5200\u4e0a\u8840\u3000\u955c\u4e2d\u4fea\u5f71\u96fe\u4e2d\u82b1 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e09\u5341\u516d\u56de\u3000\u5e15\u4e0a\u8102\u75d5\u5200\u4e0a\u8840\u3000\u955c\u4e2d\u4fea\u5f71\u96fe\u4e2d\u82b1 \u3000\u3000\u5f53\u53f2\u7ea2\u82f1\u8e0f\u4e0a\u53f0\u9636\uff0c\u7f13\u7f13\u8d70\u5165\u793c\u5802\u7684\u65f6\u5019\uff0c\u53f0\u4e0a\u53f0\u4e0b\u6025\u715e\u4e86\u51e0\u4e2a\u4eba\u3002 \u3000\u3000\u5728\u53f0\u4e0a\u7740\u6025\u7684\u662f\u516c\u5b59\u71d5\u4e0e\u4f55\u5f69\u51e4\uff0c\u5728\u53f0\u4e0b\u7740\u6025\u7684\u662f\u5389\u5357\u661f\u548c\u674e\u6566\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u4eba\u90fd\u662f\u60f3\u534f\u52a9\u53f2\u7ea2\u82f1\u884c\u523a\u5e05\u5b5f\u96c4\u7684\uff0c\u53ef\u662f\u4ed6\u4eec\u6839\u672c\u5c31\u6ca1\u6709\u63a5\u8fd1\u5e05\u5b5f\u96c4\u7684\u673a\u4f1a\uff0c\u53ea \u80fd\u773c\u7741\u7741\u5730\u770b\u7740\u53f2\u7ea2\u82f1\u8d70\u5165\u793c\u5802\u3002 \u3000\u3000\u4f55\u5f69\u51e4\u53c2\u52a0\u7684\u8fd9\u4e2a\u4e50\u5bb6\u73ed\u5b50\u662f\u6e05\u4e00\u8272\u7684\u5973\u73ed\uff0c\u672c\u6765\u4ee5\u4e3a\u53ef\u4ee5\u8fdb\u5165\u5185\u5802\u6f14\u5531\uff0c\u4ee5\u5a31\u5b98\u7737 \u7684\uff0c\u8c01\u77e5\u5374\u88ab\u5b89\u6392\u5728\u9662\u5b50\u91cc\u767b\u53f0\uff0c\u548c\u672c\u5730\u7684\u51e0\u4e2a\u620f\u73ed\u540c\u6837\u770b\u5f85\u3002\u4f17\u76ee\u77aa\u77aa\u4e4b\u4e0b\uff0c\u5728\u53f0\u4e0a\u6f14\u5531 \u7684\u4f55\u5f69\u51e4\u5fc3\u91cc\u7740\u6025\uff0c\u53ef\u8fd8\u4e0d\u80fd\u4e0d\u5f3a\u989c\u6b22\u7b11\uff0c\u6309\u62cd\u8f7b\u6b4c\uff0c\u751f\u6015\u5531\u6f0f\u4e86\u8bcd\u513f\uff0c\u548c\u9519\u4e86\u8282\u62cd\uff0c\u7ed9\u4eba \u5bb6\u770b\u51fa\u4e86\u7834\u7efd\u3002 \u3000\u3000\u53f0\u4e0b\u4eba\u5934\u6324\u6324\uff0c\u5389\u5357\u661f\u8ba4\u51fa\u4e86\u516c\u5b59\u71d5\uff0c\u516c\u5b59\u71d5\u5c1a\u672a\u53d1\u73b0\u5389\u5357\u661f\uff0c\u5979\u5fc3\u4e2d\u7684\u7126\u8651\uff0c\u4ea6\u662f\u4e0d \u5728\u4f55\u5f69\u51e4\u4e4b\u4e0b\u3002 \u3000\u3000\u4f46\u6700\u7740\u6025\u7684\u8fd8\u662f\u5389\u5357\u661f\uff0c\u4ed6\u662f\u6000\u7740\u8d4e\u7f6a\u7684\u5fc3\u60c5\uff0c\u51b3\u610f\u820d\u4e86\u81ea\u5df1\u7684\u6027\u547d\uff0c\u6765\u6551\u53f2\u7ea2\u82f1\u7684\u3002 \u4f46\u793c\u5802\u95e8\u53e3\u6709\u8463\u5341\u4e09\u5a18\u7b49\u4eba\u628a\u5173\uff0c\u53f2\u7ea2\u82f1\u65c1\u8fb9\u53c8\u6709\u53f2\u767d\u90fd\u76d1\u62a4\uff0c\u4ed6\u627e\u4e0d\u7740\u91d1\u9010\u6d41\uff0c\u5374\u662f\u5b64\u638c \u96be\u9e23\uff0c\u5373\u4f7f\u4e0d\u987e\u6027\u547d\uff0c\u4ea6\u662f\u65e0\u6d4e\u4e8e\u4e8b\uff0c\u5389\u5357\u661f\u5728\u4e00\u65f6\u6fc0\u52a8\u4e4b\u4e0b\uff0c\u672c\u6765\u5c31\u60f3\u4e0d\u987e\u4e00\u5207\u51b2\u8fdb\u53bb \u7684\uff0c\u5e78\u4e8f\u674e\u6566\u5c06\u4ed6\u62c9\u5f80\uff0c\u5389\u5357\u57a6\u542c\u4e86\u674e\u6566\u7684\u529d\u8bf4\uff1a\u201c\u5192\u6627\u51fa\u624b\uff0c\u53ea\u4f1a\u6253\u8349\u60ca\u86c7\uff0c\u53cd\u800c\u8bef\u4e86\u5927 \u4e8b\u3002\u201d\u8fd9\u624d\u7a0d\u7a0d\u51b7\u9759\u4e0b\u6765\u3002 \u3000\u3000\u53ea\u6709\u53f2\u7ea2\u82f1\u7684\u5fc3\u60c5\u5374\u662f\u5341\u5206\u5e73\u9759\u3002\u5979\u4e2d\u6307\u5957\u7740\u7684\u6307\u73af\u538b\u7740\u4e00\u679a\u6bd2\u9488\uff0c\u8fd9\u662f\u91d1\u9010\u6d41\u7ed9\u5979 \u7684\u3002\u91d1\u9010\u6d41\u5979\u7ed9\u5979\u7684\u90a3\u4e2a\u7eb8\u56e2\u85cf\u5728\u888b\u4e2d\uff0c\u90a3\u5341\u4e8c\u4e2a\u5b57\u6df1\u6df1\u7684\u5370\u5728\u5979\u7684\u8111\u6d77\uff1a\u201c\u6211\u5df2\u6765\uff0c\u6bcb\u60ca \u6050\uff0c\u6b64\u6bd2\u9488\uff0c\u7559\u5907\u7528\u3002\u201d \u3000\u3000\u5979\u6709\u7740\u4e00\u4efd\u5bf9\u91d1\u9010\u6d41\u7684\u4fe1\u8d56\uff0c\u5979\u77e5\u9053\u91d1\u9010\u6d41\u8bf4\u51fa\u4e86\u8fd9\u6837\u7684\u8bdd\uff0c\u90a3\u5c31\u662f\u820d\u4e86\u6027\u547d\u4e5f\u4e00\u5b9a\u8981 \u4fdd\u62a4\u5979\u7684\u4e86\uff01 \u3000\u3000\u4f46\u662f\u53f2\u7ea2\u82f1\u4e5f\u5e76\u4e0d\u4f01\u6c42\u4fa5\u5e78\uff0c\u5982\u679c\u91d1\u9010\u6d41\u80fd\u591f\u6551\u5f97\u4e86\u5979\u56fa\u7136\u5f88\u597d\uff0c\u6551\u4e0d\u4e86\u5979\uff0c\u5979\u4e0e\u5e05\u5b5f \u96c4\u540c\u5f52\u4e8e\u5c3d\uff0c\u90a3\u4e5f\u6b63\u662f\u5979\u7684\u6240\u613f\u3002\u5bf9\u5979\u6765\u8bf4\uff0c\u6700\u91cd\u8981\u7684\u662f\uff1a\u8981\u91d1\u9010\u6d41\u660e\u767d\u5979\u7684\u5fc3\u8ff9\uff0c\u77e5\u9053\u5979 \u662f\u7231\u4ed6\u3002\u5982\u4eca\u5979\u5df2\u7ecf\u77e5\u9053\u91d1\u9010\u6d41\u662f\u4e00\u5b9a\u4f1a\u6765\u7684\u4e86\uff0c\u5979\u80fd\u591f\u8ba9\u91d1\u9010\u6d41\u4eb2\u773c\u770b\u89c1\u5979\u884c\u523a\u5e05\u5b5f\u96c4\uff0c \u5979\u5199\u7684\u90a3\u5c01\u4fe1\u5373\u4f7f\u4ea4\u4e0d\u5230\u91d1\u9010\u6d41\u624b\u4e0a\uff0c\u91d1\u9010\u6d41\u4e5f\u4f1a\u660e\u767d\u5979\u7684\u5fc3\u8ff9\u7684\u4e86\u3002\u6b7b\u6709\u91cd\u4e8e\u6cf0\u5c71\uff0c\u6709\u8f7b \u4e8e\u9e3f\u6bdb\u3002\u8fd9\u6837\u7684\u6b7b\uff0c\u65e0\u8d1f\u4e8e\u77e5\u5df1\uff0c\u6709\u52a9\u4e8e\u4e49\u519b\uff0c\u8fd9\u8fd8\u4e0d\u662f\u6700\u5927\u7684\u5e78\u798f\u5417\uff1f \u3000\u3000\u53f2\u7ea2\u82f1\u8f7b\u8f7b\u634f\u4e86\u4e00\u4e0b\u7261\u4e39\u7684\u624b\uff0c\u8fd9\u4e2a\u4e0e\u5979\u60c5\u540c\u59d0\u59b9\u7684\u4e2b\u5934\u662f\u51b3\u610f\u6765\u966a\u5979\u540c\u6b7b\u7684\uff0c\u6b64\u9645\u5979 \u552f\u4e00\u7684\u5fc3\u4e8b\u5c31\u662f\u89c9\u5f97\u8fde\u7d2f\u4e86\u8fd9\u4e2a\u4e2b\u5934\u4e86\u3002\u5979\u53d1\u89c9\u7261\u4e39\u7684\u638c\u5fc3\u6dcc\u7740\u51b7\u6c57\uff0c\u5979\u8f7b\u8f7b\u634f\u4e86\u5979\u4e00\u4e0b\u7684 \u624b\u638c\uff0c\u8fd9\u662f\u4e00\u4e2a\u65e0\u8a00\u7684\u5b89\u6170\uff0c\u8fd9\u4e5f\u662f\u7ed9\u4e86\u5979\u4e00\u822c\u65e0\u5f62\u7684\u529b\u91cf\uff0c\u4f7f\u5f97\u7261\u4e39\u60ed\u590d\u4e86\u9547\u5b9a\u3002 \u3000\u3000\u793c\u5802\u91cc\u594f\u8d77\u7434\u745f\u8c03\u548c\u7684\u4e50\u66f2\uff0c\u5a5a\u793c\u5c31\u8981\u5f00\u59cb\u4e86\u3002 \u3000\u3000\u6324\u5728\u9662\u5b50\u91cc\u7684\u6ca1\u8d44\u683c\u8fdb\u53bb\u89c2\u793c\u7684\u5ba2\u4eba\uff0c\u6b64\u65f6\u90fd\u5df2\u65e0\u5fc3\u770b\u620f\uff0c\u6bcf\u4e00\u4e2a\u4eba\u90fd\u662f\u4f38\u957f\u4e86\u8116\u5b50\u671b \u5165\u793c\u5802\u3002\u867d\u7136\u9694\u7740\u6570\u5341\u7ea7\u7684\u53f0\u9636\uff0c\u793c\u5802\u4e2d\u7684\u60c5\u5f62\uff0c\u5728\u9662\u5b50\u91cc\u5176\u5b9e\u662f\u4e00\u70b9\u4e5f\u770b\u4e0d\u89c1\u7684\uff0c\u770b\u89c1\u7684 \u4e0d\u8fc7\u662f\uff0c\u628a\u95e8\u7684\u536b\u571f\uff0c\u548c\u9760\u8fd1\u95e8\u8fb9\u7684\u4e00\u4e9b\u5ba2\u4eba\u7684\u80cc\u5f71\u800c\u5df2\u3002 \u3000\u3000\u5389\u5357\u661f\u7d27\u7d27\u6293\u7740\u674e\u6566\u7684\u624b\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u674e\u6566\u4e5f\u60f3\u4e0d\u51fa\u597d\u4e3b\u610f\uff0c\u82e6\u7b11\u7b54\u9053\uff1a\u201c\u53ea\u597d\u89c1\u673a\u884c\u4e8b\u5427\uff01\u201d \u3000\u3000\u4e50\u58f0\u60a0\u626c\u4e2d\uff0c\u5ffd\u7136\u6709\u4e09\u4e2a\u4eba\u6765\u5230\u4e50\u5bb6\u73ed\u5b50\u7684\u8fd9\u5ea7\u620f\u53f0\u4e4b\u4e0b\uff0c\u6b64\u65f6\u4f55\u5f69\u51e4\u8fd8\u5728\u53f0\u4e0a\u8bf4\u4e66\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u4eba\u4e00\u4e2a\u662f\u5c06\u519b\u5e9c\u7684\u603b\u7ba1\u5b89\u4fca\u5ead\uff0c\u4e00\u4e2a\u662f\u8fde\u57ce\u864e\uff0c\u8fd8\u6709\u4e00\u4e2a\u5219\u662f\u64c5\u4e8e\u4f7f\u6bd2\u7684\u8d3a\u5927 \u5a18\u3002 \u3000\u3000\u4ed6\u4eec\u4e09\u4eba\u6084\u6084\u800c\u6765\uff0c\u9662\u5b50\u91cc\u70ed\u5fc3\u4e8e\u201c\u89c2\u671b\u201d\u5a5a\u793c\u7684\u5ba2\u4eba\u90fd\u6ca1\u6709\u7559\u610f\uff0c\u674e\u6566\u5374\u662f\u65e9\u5c31\u770b \u89c1\u3002 \u3000\u3000\u674e\u6566\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8fde\u57ce\u864e\u662f\u7ed9\u4ed6\u6536\u670d\u4e86\u7684\uff0c\u6b64\u9645\u7adf\u7136\u548c\u5c06\u519b\u5e9c\u7684\u603b\u7ba1\u5b89\u4fca\u5ead\u540c\u6765\uff0c \u90a3\u8fd8\u80fd\u5426\u4ec0\u4e48\u597d\u4e8b\uff1f\u674e\u6566\u60c5\u77e5\u4e0d\u5999\uff0c\u4fbf\u4e0e\u5389\u5357\u661f\u6697\u6697\u8ddf\u5728\u4ed6\u4eec\u540e\u9762\u3002 \u3000\u3000\u4e50\u5bb6\u73ed\u7684\u73ed\u4e3b\u770b\u89c1\u603b\u7ba1\u6765\u5230\uff0c\u8fde\u5fd9\u4e0a\u524d\u62db\u547c\u3002\u5b89\u4fca\u5ead\u9053\uff1a\u201c\u522b\u6253\u65ad\u8fd9\u4f4d\u59d1\u5a18\u7684\u8bf4\u4e66\uff0c\u7167 \u5e38\u7684\u5531\u4e0b\u53bb\u5427\uff01\u201d \u3000\u3000\u4f55\u5f69\u98ce\u52c9\u5f3a\u5531\u5b8c\u4e00\u6bb5\uff0c\u6b63\u8981\u6362\u4eba\uff0c\u5b89\u4fca\u5ead\u53c8\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u5531\u5f97\u5f88\u597d\uff0c\u6211\u8981\u8bf7\u5979\u8d4f\u9762\uff0c \u518d\u7ed9\u6211\u5531\u4e00\u6bb5\u7ea2\u62c2\u591c\u5954\uff01\u201d \u3000\u3000\u201c\u7ea2\u62c2\u591c\u5954\u201d\u6b63\u662f\u4f55\u5f69\u51e4\u90a3\u65e5\u5728\u5927\u660e\u6e56\u7554\u5531\u8fc7\u7684\u4e00\u6bb5\u9f13\u4e66\uff0c\u90a3\u65e5\u66f9\u632f\u5d58\u7684\u513f\u5b50\u5e26\u4e86\u62a4\u9662 \u4e0e\u5bb6\u4e01\u524d\u6765\u62a2\u5979\uff0c\u8fd9\u6bb5\u9f13\u4e66\u662f\u8fde\u57ce\u864e\u66fe\u7ecf\u542c\u5979\u5531\u8fc7\u7684\u3002 \u3000\u3000\u5982\u4eca\u5b89\u4fca\u5ead\u6307\u540d\u70b9\u5531\u8fd9\u6bb5\u9f13\u4e66\uff0c\u4e0d\u7528\u8bf4\u662f\u8fde\u57ce\u864e\u51fa\u7684\u4e3b\u610f\uff0c\u4e5f\u5206\u660e\u662f\u8981\u8bd5\u63a2\u4e8e\u5979\u7684\u4e86\u3002 \u3000\u3000\u4f55\u5f69\u98ce\u60c5\u77e5\u4ed6\u4eec\u6765\u610f\u4e0d\u5584\uff0c\u4f46\u5374\u4e0d\u80fd\u4e0d\u5531\uff0c\u5979\u6697\u81ea\u54ac\u4e86\u54ac\u7259\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u8981\u9547 \u5b9a\u3001\u9547\u5b9a\u3002\u51b3\u4e0d\u80fd\u9732\u51fa\u4e1d\u6beb\u7834\u7efd\uff01\u201d \u3000\u3000\u5b89\u4fca\u5ead\u5706\u7741\u7740\u9aa8\u788c\u788c\u7684\u4e00\u53cc\u773c\u775b\uff0c\u9510\u5229\u7684\u76ee\u5149\u5411\u7740\u53f0\u4e0a\u626b\u53bb\uff0c\u4f3c\u4e4e\u50cf\u5728\u641c\u7d22\u4ec0\u4e48\u3002\u8fc7\u4e86 \u4e00\u4f1a\uff0c\u4e0e\u8fde\u57ce\u864e\u4ea4\u6362\u4e86\u4e00\u4e2a\u8ddf\u8272\uff0c\u5404\u81ea\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u539f\u6765\u8fde\u57ce\u864e\u6015\u6b7b\u8d2a\u751f\uff0c\u5728\u5b89\u4fca\u5ead\u3001\u53f2\u767d\u90fd\u903c\u4f9b\u4e4b\u4e0b\uff0c\u4e0d\u4f46\u5c06\u674e\u6566\u4e0e\u5389\u5357\u661f\u62db\u4e86\u51fa\u6765\uff0c\u800c \u4e14\u5c06\u4ed6\u548c\u5f6d\u5de8\u5d58\u90a3\u65e5\u9047\u89c1\u4e50\u5bb6\u73ed\u5b50\u6240\u53d1\u73b0\u7684\u4e00\u4e9b\u53ef\u7591\u60c5\u8282\u90fd\u4e00\u4e00\u5730\u5410\u51fa\u6765\u4e86\u3002\u5b89\u4fca\u5ead\u6349\u4e0d\u5230 \u674e\u3001\u5389\u4e8c\u4eba\uff0c\u5f97\u4e86\u8fd9\u6761\u7ebf\u7d22\uff0c\u81ea\u662f\u4e0d\u80af\u653e\u8fc7\u3002 \u3000\u3000\u4f55\u5f69\u51e4\u4e00\u66f2\u672a\u7ec8\uff0c\u5b89\u4fca\u5ead\u5ffd\u5730\u559d\u9053\uff1a\u201c\u505c\uff01\u201d \u3000\u3000\u73ed\u4e3b\u5927\u5403\u4e00\u60ca\uff0c\u60f6\u7136\u8bf4\u9053\uff1a\u201c\u5979\u5531\u5f97\u4e0d\u597d\uff0c\u8981\u4e0d\u8981\u6362\u2026\u2026\u201d \u3000\u3000\u5b89\u4fca\u5ead\u78d4\u78d4\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u8c01\u8bf4\u5979\u5531\u5f97\u4e0d\u597d\uff1f\u6b63\u56e0\u4e3a\u5979\u5531\u5f97\u592a\u597d\u4e86\uff0c\u6240\u4ee5\u6211\u8bf7 \u5979\u4e0b\u6765\u9886\u8d4f\uff01\u201d \u3000\u3000\u4f55\u5f69\u51e4\u653e\u4e0b\u4e86\u68a8\u82b1\u7b80\uff0c\u8f7b\u63a0\u4e91\u9b03\uff0c\u4f5c\u51fa\u7f9e\u6da9\u7684\u6837\u5b50\u8bf4\u9053\uff1a\u201c\u5c0f\u5973\u5b50\u5531\u5f97\u4e0d\u597d\uff0c\u5927\u4eba\u8c2c\u8d4f \u4e86\u3002\u201d\u6b64\u65f6\u5979\u5df2\u53d1\u73b0\u4eba\u4e1b\u4e2d\u7684\u674e\u6566\uff0c\u674e\u6566\u548c\u5389\u5357\u661f\u4e8c\u4eba\u6b63\u5728\u5411\u53f0\u8fb9\u6324\u6765\u3002\u4f55\u5f69\u51e4\u5fc5\u987b\u8c8c\u4f5c\u4ece \u5bb9\uff0c\u62d6\u5ef6\u65f6\u523b\u3002 \u3000\u3000\u5b89\u4fca\u5ead\u5c31\u50cf\u4e00\u53ea\u4e1a\u5df2\u53d1\u73b0\u4e86\u8001\u9f20\u7684\u732b\u513f\u4f3c\u7684\uff0c\u6599\u60f3\u4f55\u5f69\u51e4\u9003\u4e0d\u8131\u4ed6\u7684\u9b54\u722a\uff0c\u4e0d\u59a8\u5c3d\u60c5\u620f \u5f04\u53c8\u518d\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4e2a\u5927\u8001\u7c97\uff0c\u4e0d\u89e3\u5999\u5904\uff0c\u597d\u5728\u8fd9\u91cc\u6709\u4e2a\u77e5\u97f3\u4e4b\u4eba\uff01\u8fde\u5927\u4eba\uff0c\u8fd8\u662f\u4f60\u6765\u8bf4 \u8bf4\u5979\u7684\u597d\u5904\u5427\uff0c\u4e5f\u597d\u53eb\u5979\u4eec\u77e5\u9053\u54b1\u4eec\u662f\u8d4f\u7f5a\u5206\u660e\uff01\u201d \u3000\u3000\u8fde\u57ce\u864e\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u542c\u5230\u4e86\u5c71\u4e1c\u7684\u68a8\u82b1\u5927\u9f13\uff0c\u8fd9\u662f\u9f13\u4e66\u4e2d\u7684\u2018\u5999\u54c1\u2019\u554a\uff01 \u4f55\u59d1\u5a18\uff0c\u4f60\u6df7\u5728\u5ddd\u897f\u7684\u4e00\u4e2a\u5c0f\u73ed\u5b50\u91cc\uff0c\u4e0d\u8d5a\u592a\u59d4\u5c48\u4e86\u81ea\u5df1\u5417\uff1f\u563f\u563f\uff0c\u54c8\u54c8\uff01\u771f\u4eba\u9762\u524d\u4f55\u5fc5\u518d \u8bf4\u5047\u8bdd\uff0c\u5feb\u5feb\u968f\u6211\u8fdb\u53bb\u9886\u8d4f\u5427\uff01\u201d \u3000\u3000\u539f\u6765\u4f55\u5f69\u51e4\u751a\u6709\u8bed\u8a00\u5929\u624d\uff0c\u5979\u6539\u7528\u5ddd\u897f\u7684\u571f\u97f3\u8bf4\u4e66\uff0c\u8154\u8c03\u6a21\u62df\u5f97\u7ef4\u5999\u7ef4\u8096\uff0c\u65c1\u4eba\u90fd\u662f\u542c \u4e0d\u51fa\u6765\u3002\u53ef\u662f\u8fde\u57ce\u864e\u70b9\u7684\u662f\u5979\u90a3\u65e5\u5531\u8fc7\u7684\u90a3\u6bb5\u201c\u7ea2\u62c2\u591c\u5954\u201d\uff0c\u5979\u867d\u7136\u529b\u6301\u9547\u5b9a\uff0c\u7ec8\u662f\u4e0d\u514d\u9732 \u51fa\u4e9b\u8bb8\u7834\u7efd\uff0c\u7ed9\u8fde\u57ce\u864e\u542c\u51fa\u4e86\u5979\u539f\u6765\u7684\u4e61\u97f3\u3002 \u3000\u3000\u5b89\u4fca\u5ead\u8ddf\u7740\u51b7\u7b11\u9053\uff1a\u201c\u4e50\u8001\u5934\uff0c\u4f60\u8fd9\u4e2a\u73ed\u5b50\u91cc\u4f3c\u4e4e\u591a\u51fa\u4e00\u4f4d\u59d1\u5a18\uff0c\u563f\u563f\uff0c\u5c31\u662f\u8fd9\u4f4d\u59d1 \u5a18\uff01\u4f60\u53eb\u5979\u4e5f\u4e00\u540c\u4e0b\u6765\u9886\u8d4f\u5427\uff01\u201d\u7528\u624b\u4e00\u6307\uff0c\u6307\u7684\u6b63\u662f\u516c\u5b59\u71d5\uff01 \u3000\u3000\u539f\u6765\u5b89\u4fca\u5ead\u4e5f\u662f\u4e00\u4f4d\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u516c\u5b59\u71d5\u8eab\u4e0a\u85cf\u6709\u8f6f\u5251\uff0c\u7ed9\u4ed6\u770b\u51fa\u6765\u4e86\uff01 \u3000\u3000\u516c\u5b59\u71d5\u6ca1\u6709\u4f55\u5f69\u98ce\u7684\u6c89\u7740\uff0c\u767b\u65f6\u62bd\u51fa\u5229\u5251\uff0c\u6251\u4e0b\u53f0\u6765\uff01\u4f55\u5f69\u98ce\u53ea\u597d\u8ddf\u7740\u51fa\u624b\uff0c\u51b7\u7b11\u9053\uff1a \u201c\u8fde\u5927\u4eba\uff0c\u4f60\u8981\u9886\u8d4f\uff0c\u6211\u5c31\u6210\u5168\u4f60\u5427\uff01\u201d\u4e00\u626c\u624b\uff0c\u628a\u90a3\u67c4\u8bf4\u9f13\u4e66\u7528\u7684\u5c0f\u817f\u5b50\u98de\u51fa\uff0c\u5411\u8fde\u57ce\u864e \u6253\u53bb\u3002\u8fd9\u662f\u5979\u7684\u72ec\u95e8\u6697\u5668\uff0c\u5916\u9762\u52a0\u4e0a\u4e00\u5c42\u9ed1\u6f06\uff0c\u770b\u4f3c\u6728\u5934\uff0c\u5176\u5b9e\u5370\u662f\u7cbe\u94c1\u6240\u94f8\u3002 \u3000\u3000\u516c\u5b59\u71d5\u811a\u672a\u6cbe\u5730\uff0c\u8d3a\u5927\u5a18\u4ea6\u5df2\u626c\u624b\u53d1\u51fa\u6697\u5668\uff0c\u662f\u4e09\u67c4\u6bd2\u84ba\u85dc\u3002\u8fd9\u662f\u4e00\u79cd\u4efd\u91cf\u6c89\u91cd\u7684\u6697 \u5668\uff0c\u8d3a\u5927\u5a18\u56e0\u89c1\u5979\u8f7b\u529f\u4e86\u5f97\uff0c\u672c\u9886\u6599\u60f3\u4e0d\u5dee\uff0c\u53ea\u6050\u7528\u6885\u82b1\u9488\u4e4b\u7c7b\u7684\u6697\u5668\u4f1a\u7ed9\u5979\u7684\u638c\u98ce\u626b\u843d\uff0c \u662f\u4ee5\u4fbf\u51fa\u8fd9\u79cd\u6c89\u91cd\u7684\u6bd2\u84ba\u85dc\uff0c\u800c\u4e14\u4e00\u53d1\u5c31\u662f\u4e09\u67c4\uff01\u60f3\u5979\u8eab\u5b50\u60ac\u7a7a\uff0c\u8f7b\u529f\u518d\u597d\uff0c\u4e5f\u662f\u96be\u4ee5\u5c3d\u6570 \u95ea\u5f00\uff1b\u8eab\u5b50\u60ac\u7a7a\uff0c\u6709\u529b\u4ea6\u662f\u65e0\u5904\u65bd\u5c55\uff0c\u8fd9\u79cd\u6c89\u91cd\u7684\u6697\u5668\uff0c\u51b3\u8ba1\u96be\u4ee5\u6253\u843d\u3002\u4e09\u67c4\u6bd2\u84ba\u85dc\uff0c\u81f3\u5c11 \u975e\u4e2d\u4e00\u67c4\u4e0d\u53ef\uff01 \u3000\u3000\u8d3a\u5927\u5a18\u6253\u7684\u5982\u610f\u7b97\u76d8\uff0c\u5374\u4e0d\u77e5\u87b3\u8782\u6355\u8749\uff0c\u9ec4\u96c0\u5728\u540e\uff0c\u5c31\u5728\u5979\u7684\u6bd2\u84ba\u85dc\u6b63\u81ea\u5411\u53f0\u4e0a\u98de\u53bb\uff0c \u773c\u770b\u5c31\u8981\u6253\u7740\u6b63\u5728\u5411\u53f0\u4e0b\u6765\u7684\u516c\u5b59\u71d5\u4e4b\u65f6\uff0c\u731b\u542c\u5f97\u4e00\u58f0\u5927\u559d\uff01 \u3000\u3000\u9739\u96f3\u7684\u4e00\u58f0\u5927\u559d\u968f\u7740\u4e00\u9053\u767d\u5149\u98de\u8d77\uff0c\u7aef\u7684\u4f3c\u662f\u96f7\u9e23\u7535\u95ea\uff0c\u53ea\u89c1\u5389\u5357\u661f\u8fde\u4eba\u5e26\u5251\uff0c\u5316\u4f5c\u4e86 \u957f\u8679\uff0c\u6a2a\u7a7a\u63a0\u8fc7\uff0c\u4e00\u7247\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u9707\u5f97\u4f17\u4eba\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\u3002\u8d3a\u5927\u5a18\u6240\u53d1\u7684\u4e09\u67c4\u6bd2\u84ba\u85dc\uff0c \u7ed9\u4ed6\u7684\u7384\u94c1\u5ba4\u5251\u4e00\u6325\uff0c\u65ad\u4e3a\u516d\u622a\uff0c\u56db\u65b9\u98de\u51fa\uff0c\u9662\u4e2d\u5bbe\u5ba2\uff0c\u7eb7\u7eb7\u8eb2\u907f\uff01 \u3000\u3000\u5389\u5357\u661f\u62c9\u7740\u516c\u5b59\u71d5\u7684\u624b\uff0c\u4e24\u4eba\u4f7f\u4e86\u4e2a\u201c\u6bd4\u7ffc\u53cc\u98de\u201d\u8eab\u6cd5\uff0c\u8f7b\u8f7b\u5de7\u5de7\u7684\u843d\u5728\u5730\u4e0a\u3002\u516c\u5b59\u71d5 \u60ca\u559c\u4ea4\u96c6\uff0c\u53eb\u9053\uff1a\u201c\u5389\u5927\u54e5\uff0c\u662f\u4f60\uff01\u201d\u51e0\u4e4e\u7591\u662f\u68a6\u4e2d\uff01 \u3000\u3000\u8fd9\u4e00\u8fb9\uff0c\u5389\u5357\u661f\u7834\u4e86\u8d3a\u5927\u5a18\u7684\u6697\u5668\uff1b\u90a3\u4e00\u8fb9\uff0c\u4f55\u5f69\u51e4\u98de\u51fa\u7684\u6253\u7a74\u9524\u5b50\u5374\u4e5f\u7ed9\u5b89\u4fca\u5ead\u6253\u843d \u4e86\u3002 \u3000\u3000\u5b89\u4fca\u5ead\u8eab\u4e3a\u5c06\u519b\u5e9c\u603b\u7ba1\uff0c\u6b66\u529f\u81ea\u975e\u5eb8\u624b\u3002\u4e00\u6253\u843d\u4e86\u4f55\u5f69\u51e4\u7684\u6697\u5668\uff0c\u7acb\u5373\u4fbf\u662f\u4e00\u6293\u5411\u5979\u6293 \u53bb\u3002\u8fd9\u4e00\u6293\u52b2\u98ce\u547c\u547c\uff0c\u7adf\u662f\u72e0\u8fa3\u5f02\u5e38\u7684\u5927\u529b\u9e70\u722a\u529f\uff01 \u3000\u3000\u674e\u6566\u559d\u9053\uff1a\u201c\u7ed9\u6211\u8eba\u4e0b\u5427\uff01\u201d\u5b89\u4fca\u5ead\u90a3\u4e00\u6293\u582a\u582a\u5c31\u8981\u6293\u5230\u4f55\u5f69\u51e4\u7684\u9762\u95e8\uff0c\u5ffd\u89c9\u5fae\u98ce\u98d2 \u7136\uff0c\u9690\u9690\u5e26\u7740\u4e00\u80a1\u8165\u6c14\uff0c\u674e\u6566\u53d1\u51fa\u7684\u6885\u82b1\u9488\u4ea6\u5df2\u5c04\u5230\u4e86\u4ed6\u7684\u540e\u5fc3\uff01\u5b89\u4fca\u5ead\u542c\u98ce\u8fa8\u5668\uff0c\u77e5\u9053\u8fd9 \u6bd2\u9488\u4e43\u662f\u5c04\u4ed6\u80cc\u5fc3\u7684\u4e09\u9053\u5927\u7a74\uff01 \u3000\u3000\u5b89\u4fca\u5ead\u7109\u6562\u8ba9\u674e\u6566\u7684\u6bd2\u9488\u5c04\u8fdb\u4ed6\u7684\u7a74\u9053\uff1f\u767e\u5fd9\u4e2d\u4f7f\u51fa\u4e2a\u201c\u9ec4\u9e4a\u51b2\u9704\u201d\u7684\u8eab\u6d01\uff0c\u5e73\u5730\u62d4\u8d77 \u4e08\u8bb8\uff0c\u4e09\u679a\u6bd2\u9488\uff0c\u4ece\u4ed6\u811a\u5e95\u98de\u8fc7\u3002 \u3000\u3000\u5b89\u4fca\u5ead\u907f\u8fc7\u4e86\u6bd2\u9488\uff0c\u90a3\u4e00\u6293\u4e5f\u5c31\u672a\u80fd\u6293\u7740\u4f55\u5f69\u51e4\u4e86\u3002\u4f55\u5f69\u51e4\u8f7b\u529f\u4e0d\u5f31\uff0c\u8fc5\u5373\u63a0\u8fc7\u4e00\u8fb9\uff0c \u62d4\u5251\u5c31\u523a\u8fde\u57ce\u864e\u3002 \u3000\u3000\u8fde\u57ce\u864e\u4e2d\u6bd2\u5df2\u6709\u4e24\u5929\uff0c\u7a7a\u81ea\u4e00\u8eab\u6b66\u529f\uff0c\u5df2\u65e0\u6c14\u529b\u4f7f\u7528\uff0c\u5fc3\u91cc\u4e00\u51c9\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u53cd\u6b63\u6211 \u4e5f\u4e0d\u60f3\u6d3b\u4e86\uff0c\u4f60\u5c31\u6740\u4e86\u6211\u5427\u3002\u201d\u4f55\u5f69\u51e4\u662f\u4e2a\u4ece\u672a\u6740\u8fc7\u4eba\u7684\u5973\u5b50\uff0c\u89c1\u654c\u4eba\u6beb\u65e0\u62b5\u6297\uff0c\u8fd9\u4e00\u5251\u5012 \u662f\u4e0b\u4e0d\u4e86\u624b\u3002\u6b63\u8e0c\u8e87\u95f4\uff0c\u5b89\u4fca\u5ead\u5df2\u662f\u731b\u6251\u8fc7\u6765\uff0c\u6325\u5200\u5411\u5979\u65a9\u4e0b\u3002\u674e\u6566\u8d70\u4e0a\u524d\u6765\uff0c\u5728\u8fde\u57ce\u864e\u80a9 \u5934\u8f7b\u8f7b\u4e00\u62cd\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u8fde\u5927\u4eba\uff0c\u4f60\u53ef\u771f\u662f\u5bf9\u5f97\u4f4f\u670b\u53cb\u554a\uff01\u4f60\u65e2\u7136\u53ea\u8981\u5bcc\u8d35\u529f\u540d\uff0c\u90a3\u6211\u7684 \u89e3\u836f\u4e5f\u4e0d\u80fd\u7ed9\u4f60\u4e86\u3002\u4f46\u6211\u4e5f\u4e0d\u6740\u4f60\uff0c\u8ba9\u4f60\u81ea\u5df1\u5fcf\u6094\u53bb\u5427\uff01\u201d\u674e\u6566\u62d4\u5251\u51fa\u9798\uff0c\u592b\u59bb\u8054\u624b\uff0c\u5e76\u80a9 \u5fa1\u654c\u3002\u8fde\u57ce\u864e\u8eb2\u8fc7\u4e00\u8fb9\uff0c\u53c8\u662f\u60ed\u6127\uff0c\u53c8\u662f\u540e\u6094\u3002 \u3000\u3000\u8d3a\u5927\u5a18\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u59d3\u5389\u7684\u5c0f\u5b50\uff0c\u4f60\u5c45\u7136\u8fd8\u6ca1\u6709\u6b7b\uff0c\u8001\u5a18\u5c31\u6210\u5168\u4f60\u4eec\uff0c\u8ba9\u4f60\u4eec\u505a\u4e2a\u540c \u547d\u9e33\u9e2f\u5427\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff0c\u8fd9\u5996\u5987\u722a\u4e0a\u6709\u6bd2\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8d3a\u7a74\u5a18\u5df2\u662f\u6293\u5230\u4e86\u516c\u5b59\u71d5 \u7684\u80cc\u5fc3\uff0c\u516c\u5b59\u71d5\u4e00\u4e2a\u659c\u8eab\u6ed1\u6b65\uff0c\u95ea\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u5389\u5357\u661f\u6012\u9053\uff1a\u201c\u4eca\u65e5\u975e\u5207\u4e0b\u4f60\u7684\u6bd2\u722a\u4e0d\u53ef\uff01\u201d\u9000\u540e\u4e09\u6b65\uff0c\u6325\u8d77\u7384\u94c1\u5b9d\u5251\u3002\u8d3a\u5927\u5a18\u6df1\u77e5\u7384 \u94c1\u5b9d\u5251\u7684\u5389\u5bb3\uff0c\u5c82\u6562\u8ba9\u4ed6\u65bd\u5c55\u3002\u201d \u3000\u3000\u8d3a\u5927\u5a18\u7684\u52fe\u62ff\u6495\u6251\u529f\u592b\u6781\u4e3a\u72e0\u8fa3\uff0c\u5389\u5357\u661f\u7ed9\u4ed6\u8fd1\u8eab\u7f20\u6597\uff0c\u7384\u94c1\u5b9d\u5251\u7adf\u7136\u65bd\u5c55\u4e0d\u5f00\u3002 \u3000\u3000\u6742\u5728\u5bbe\u5ba2\u4e4b\u4e2d\u7684\u4fbf\u8863\u536b\u58eb\u7eb7\u7eb7\u4eae\u51fa\u5175\u5668\uff0c\u4e00\u62e5\u800c\u4e0a\u3002\u516c\u5b59\u71d5\u51b7\u7b11\u9053\uff1a\u201c\u53eb\u4f60\u4eec\u77e5\u9053\u59d1\u5a18 \u7684\u5389\u5bb3\uff01\u201d\u9661\u7136\u95f4\u53ea\u89c1\u5bd2\u5149\u95ea\u95ea\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u516c\u5b59\u71d5\u5c55\u5f00\u4e86\u72ec\u95e8\u7684\u8f7b\u529f\u8eab\u6cd5\uff0c\u5f53\u771f\u4f3c\u662f\u8774\u8776 \u7a7f\u82b1\uff0c\u873b\u8712\u70b9\u6c34\uff0c\u4e00\u53e3\u957f\u5251\u5728\u4eba\u4e1b\u4e2d\u5de6\u7a7f\u77f3\u63d2\uff0c\u56db\u4e0b\u6e38\u8d70\uff0c\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u201c\u554a \u54df\uff01\u201d\u201c\u4e0d\u597d\uff01\u201d\u4e4b\u58f0\u6b64\u8d77\u5f7c\u843d\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u4f17\u536b\u58eb\u7684\u5200\u5251\u5806\u6ee1\u4e00\u5730\uff01\u516c\u5b59\u71d5\u7684\u5251\u62db\u5feb\u5982\u95ea \u7535\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u523a\u5411\u5bf9\u624b\u7684\u8109\u95e8\u3002\u4f17\u536b\u58eb\u53ea\u89c1\u773c\u524d\u5bd2\u5149\u4e00\u95ea\uff0c\u624b\u8155\u5df2\u662f\u4e2d\u5251\u3002\u7b80\u76f4\u6ca1\u6709\u62db\u67b6 \u7684\u4f59\u5730\u3002 \u3000\u3000\u516c\u5b59\u71d5\u6740\u5f97\u5174\u8d77\uff0c\u559d\u9053\uff1a\u201c\u8001\u5996\u5a46\uff0c\u4f60\u4e5f\u5403\u6211\u4e00\u5251\uff01\u201d\u9752\u94a2\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u5530\u7684\u5411\u8d3a\u5927\u5a18 \u523a\u53bb\u3002\u5ffd\u89c9\u52b2\u98ce\u98d2\u7136\uff0c\u4e00\u6761\u5927\u6c49\u7a81\u7136\u4ece\u4eba\u4e1b\u4e2d\u6251\u51fa\u6765\uff0c\u9694\u5728\u516c\u5b59\u71d5\u4e0e\u8d3a\u5927\u5a18\u4e4b\u95f4\uff0c\u53cd\u624b\u4e00 \u62cd\uff0c\u4e09\u6307\u64d2\u62ff\uff0c\u7adf\u7136\u628a\u5979\u7684\u957f\u5251\u593a\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u4e2a\u4eba\u662f\u5180\u5317\u7684\u72ec\u811a\u5927\u76d7\u90d1\u96c4\u56fe\uff0c\u672c\u6765\u662f\u7ed9\u5927\u5185\u603b\u7ba1\u8428\u798f\u9f0e\u6536\u4e70\u4e86\u7684\uff0c\u53bb\u5e74\u5e05\u5b5f\u96c4\u5230\u4eac \u7ed9\u8428\u798f\u9f0e\u795d\u5bff\uff0c\u89c1\u4ed6\u6b66\u827a\u9ad8\u5f3a\uff0c\u53c8\u8f6c\u8058\u4ed6\u81f3\u897f\u660c\u7684\u5c06\u519b\u5e9c\u4e2d\uff0c\u505a\u4e86\u536b\u58eb\u7684\u6559\u5934\u3002 \u3000\u3000\u90d1\u96c4\u56fe\u624b\u8155\u4e5f\u4e2d\u4e86\u4e00\u5251\uff0c\u4f46\u4e0d\u662f\u6070\u597d\u523a\u7740\u8109\u95e8\uff0c\u4ed6\u7ec3\u6709\u94c1\u7802\u638c\u529f\u592b\uff0c\u76ae\u7c97\u8089\u539a\uff0c\u867d\u7136\u7ed9 \u5251\u5c16\u5212\u7834\u4e86\u76ae\u8089\uff0c\u4ecd\u7136\u628a\u516c\u5b59\u71d5\u7684\u5251\u593a\u4e86\u3002 \u3000\u3000\u65c1\u8fb9\u4e24\u4e2a\u536b\u58eb\u770b\u51fa\u4fbf\u5b9c\uff0c\u6325\u5251\u6025\u4e0a\uff0c\u8fd9\u4e24\u4eba\u5728\u5c06\u519b\u5e9c\u7684\u4f17\u536b\u58eb\u4e4b\u4e2d\uff0c\u4e5f\u7b97\u5f97\u662f\u5251\u672f\u597d \u624b\uff0c\u4e24\u4eba\u5de6\u53f3\u5939\u653b\uff0c\u53cc\u5251\u540c\u65f6\u523a\u5230\u3001\u5389\u5357\u661f\u770b\u5f97\u6035\u76ee\u60ca\u5fc3\uff0c\u4e0d\u7981\u201c\u554a\u5440\u201d\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u4f46 \u4ed6\u7ed9\u8d3a\u5927\u5a18\u7f20\u4f4f\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u5374\u662f\u51b2\u4e0d\u8fc7\u53bb\u3002 \u3000\u3000\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u4e0d\u788d\u4e8b\uff0c\u4e14\u8ba9\u4f60\u4eec\u4e5f\u770b\u770b\u6211\u7684\u593a\u5251\u529f\u592b\uff01\u201d\u4e24\u4e2a\u536b\u58eb\u6b63\u81ea\u53cc\u5251\u4ea4\u53c9\u523a \u51fa\uff0c\u5ffd\u89c9\u624b\u4e0a\u4e00\u8f7b\uff0c\u4e24\u53e3\u957f\u5251\u540c\u65f6\u8131\u624b\u3002\u516c\u5b59\u71d5\u8fd9\u4e00\u62db\u201c\u7a7a\u624b\u5165\u767d\u5203\u201d\u7684\u529f\u592b\uff0c\u6bd4\u8d77\u521a\u624d\u90d1 \u96c4\u56fe\u7528\u786c\u529f\u593a\u5979\u7684\u5251\u3002\u624b\u6cd5\u66f4\u4e3a\u2018\u5e72\u51c0\u5229\u843d\uff01 \u3000\u3000\u516c\u5b59\u71d5\u53cc\u5251\u5230\u624b\uff0c\u7b11\u9053\uff1a\u201c\u4ee5\u4e00\u6362\u4e8c\uff0c\u7b97\u6765\u8fd8\u662f\u6211\u5360\u4e86\u4fbf\u5b9c\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5df2\u662f\u8eab\u4e00\u968f \u5251\u8d70\uff0c\u5835\u5f80\u4e86\u90ca\u96c4\u56fe\u7684\u53bb\u8def\u3002 \u3000\u3000\u90d1\u96c4\u56fe\u559d\u9053\uff1a\u201c\u64a4\u5251\uff01\u201d\u91cd\u65bd\u6545\u6280\uff0c\u4f7f\u51fa\u94c1\u7802\u638c\u7684\u529f\u592b\u62bd\u5979\u7684\u5251\u67c4\uff0c\u516c\u5b59\u71d5\u5de6\u624b\u5251\u500f\u5730 \u53cd\u624b\u4e00\u523a\uff0c\u5feb\u5982\u95ea\u7535\uff0c\u540e\u9762\u201c\u54ce\u54df\uff0c\u54ce\u54df\uff01\u201d\u4e4b\u58f0\u8fde\u8d77\uff0c\u8eab\u540e\u90a3\u4e24\u540d\u536b\u58eb\u5df2\u662f\u4e2d\u5251\u5012\u5730\u3002\u8fd9 \u4e00\u62db\u201c\u58f0\u4e1c\u51fb\u897f\u201d\u7684\u5feb\u5251\uff0c\u5f53\u771f\u662f\u532a\u5937\u6240\u601d\uff0c\u9662\u4e2d\u4e0d\u4e4f\u5251\u672f\u597d\u624b\uff0c\u7adf\u7136\u770b\u4e0d\u51fa\u5979\u8fd9\u4e00\u5251\u662f\u600e \u4e48\u523a\u7684\uff01 \u3000\u3000\u90d1\u96c4\u56fe\u4e00\u638c\u6253\u7a7a\uff0c\u90a3\u4e24\u540d\u536b\u58eb\u5df2\u7ecf\u5012\u5730\u3002\u90d1\u96c4\u56fe\u4e0d\u7531\u5f97\u5fc3\u5934\u4e00\u51db\uff0c\u8fd9\u624d\u77e5\u9053\u516c\u5b59\u71d5\u7684\u5251 \u6cd5\u8fdc\u8fdc\u5728\u4ed6\u4f30\u8ba1\u4e4b\u4e0a\uff0c\u53bb\u4e86\u8f7b\u654c\u4e4b\u5fc3\u3002\u516c\u5b59\u71d5\u51b7\u7b11\u9053\uff1a\u201c\u73b0\u5728\u8f6e\u5230\u4f60\u4e86\uff0c\u6709\u672c\u9886\u7684\u5c31\u518d\u6765\u593a \u5251\u5427\uff01\u201d\u811a\u6b65\u5fae\u52a8\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u90d1\u96c4\u56fe\u76ee\u6ce8\u5251\u5c16\uff0c\u94c1\u7802\u638c\u521a\u8981\u518d\u53d1\uff0c\u9661\u7136\u95f4\u53ea\u89c9\u80a9\u5934\u75bc\u75db\uff0c \u5df2\u662f\u4e2d\u4e86\u516c\u5b59\u71d5\u7684\u4e00\u5251\uff01 \u3000\u3000\u539f\u6765\u516c\u5b59\u71d5\u7684\u5251\u672f\u672c\u6765\u4e0d\u662f\u90d1\u96c4\u56fe\u6240\u80fd\u514b\u5236\u7684\uff0c\u53ea\u56e0\u5979\u521a\u624d\u4e0d\u613f\u591a\u6240\u6740\u4f24\uff0c\u6bcf\u4e00\u5251\u90fd\u53ea \u662f\u523a\u5bf9\u65b9\u7684\u624b\u8155\uff0c\u5374\u4e0d\u77e5\u90d1\u96c4\u56fe\u7684\u672c\u9886\u5728\u4f17\u536b\u58eb\u4e4b\u4e0a\uff0c\u662f\u4ee5\u4e00\u4e2a\u51b7\u4e0d\u53ca\u9632\uff0c\u4f30\u8ba1\u9519\u8bef\uff0c\u8fd9\u624d \u7ed9\u90d1\u96c4\u56fe\u593a\u4e86\u5979\u7684\u5251\u7684\u3002 \u3000\u3000\u5982\u4eca\u5979\u5df2\u6709\u4e86\u51c6\u5907\uff0c\u51fa\u624b\u53c8\u5feb\u53c8\u72e0\uff0c\u90d1\u96c4\u56fe\u8fd8\u5982\u4f55\u80fd\u591f\u593a\u5979\u7684\u5251\uff1f\u975e\u4f46\u593a\u4e0d\u4e86\u5251\uff0c\u81ea\u8eab \u4e5f\u96be\u4fdd\u4e86\u3002 \u3000\u3000\u90d1\u96c4\u56fe\u80a9\u5934\u4e2d\u5251\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u597d\u4e2b\u5934\uff0c\u6211\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u6043\u7740\u94c1\u7802\u638c\u7684\u529f\u592b\uff0c\u5fc3\u60f3\u62fc\u7740 \u518d\u53d7\u4e00\u5251\uff0c\u4e5f\u8981\u5c06\u5979\u6bd9\u4e8e\u638c\u4e0b\u3002\u54ea\u77e5\u516c\u5b59\u71d5\u7684\u8eab\u6cd5\u53e4\u602a\u4e4b\u6781\uff0c\u90d1\u96c4\u56fe\u53cc\u638c\u6253\u6765\uff0c\u5979\u7adf\u7136\u4e00\u4e2a \u8f6c\u8eab\uff0c\u80cc\u5411\u654c\u4eba\u3002\u90d1\u96c4\u56fe\u4ece\u6765\u672a\u89c1\u8fc7\u8fd9\u79cd\u6253\u6cd5\uff0c\u4e0d\u89c9\u4e00\u6014\u3002\u5fc3\u795e\u7a0d\u5206\uff0c\u53cc\u638c\u867d\u7136\u4ecd\u4ee5\u6781\u731b\u70c8 \u4e4b\u52bf\u6253\u51fa\uff0c\u53bb\u52bf\u5df2\u662f\u7a0d\u5fae\u7f13\u4e86\u4e00\u7f13\u3002\u5c31\u5728\u8fd9\u4e00\u77ac\u4e4b\u95f4\uff0c\u9661\u5730\u542c\u5f97\u516c\u5b59\u71d5\u558a\u58f0\uff1a\u201c\u7740\uff01\u201d\u53cc\u5251 \u53cd\u81c2\u523a\u624e\uff0c\u5feb\u5f97\u96be\u4ee5\u5f62\u5bb9\uff0c\u201c\u535c\u535c\u201d\u4e24\u58f0\uff0c\u968f\u7740\u201c\u5f53\u201d\u7684\u4e00\u54cd\uff0c\u90d1\u96c4\u56fe\u5de6\u638c\u638c\u5fc3\u88ab\u5229\u5251\u523a \u7a7f\uff0c\u53f3\u638c\u638c\u5fc3\u88ab\u5212\u4e86\u4e00\u4e2a\u201c\u5341\u201d\u5b57\uff0c\u56e0\u4ed6\u53f3\u638c\u7684\u638c\u529b\u8f83\u5f3a\uff0c\u662f\u4ee5\u516c\u5b59\u71d5\u7684\u4e00\u67c4\u5251\u5374\u4e5f\u7ed9\u4ed6\u6253 \u843d\u3002 \u3000\u3000\u4f46\u516c\u5b59\u71d5\u4e0d\u8fc7\u5931\u4e86\u4e00\u5251\uff0c\u90d1\u96c4\u56fe\u5374\u662f\u53cc\u638c\u9f50\u4f24\uff0c\u8fd9\u4e2a\u4f24\u6bd4\u521a\u624d\u80a9\u5934\u4e2d\u7684\u4e00\u5251\u53ef\u662f\u5389\u5bb3\u591a \u4e86\u3002\u4fd7\u8bed\u8bf4\u201c\u5341\u6307\u75db\u5f52\u5fc3\u201d\uff0c\u4f55\u51b5\u638c\u5fc3\u88ab\u5229\u5251\u7a7f\u8fc7\u3002\u90d1\u96c4\u56fe\u5fcd\u4e0d\u4f4f\u75bc\u75db\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u5012\u8dc3\u4e09 \u6b65\uff0c\u5411\u540e\u4fbf\u5012\u3002 \u3000\u3000\u8d3a\u5927\u5a18\u5728\u4ed6\u540e\u9762\uff0c\u8fd9\u4e00\u5012\u6070\u597d\u5c31\u649e\u7740\u4e86\u8d3a\u5927\u5a18\u3002\u8d3a\u5927\u5a18\u4e0d\u77e5\u662f\u53cb\u662f\u654c\uff0c\u5ffd\u89c9\u80cc\u540e\u6709\u4eba\u6251 \u6765\uff0c\u5f53\u7136\u4e0d\u80fd\u4e0d\u536b\u62a4\u81ea\u5df1\uff0c\u4e8e\u662f\u4fe1\u624b\u4e00\u6293\u4e00\u63a8\uff0c\u559d\u58f0\uff1a\u201c\u53bb\uff01\u201d\u628a\u90d1\u96c4\u56fe\u5e9e\u5927\u7684\u8eab\u8eaf\uff0c\u63a8\u51fa \u4e86\u4e00\u4e08\u5f00\u5916\u3002 \u3000\u3000\u516c\u5b59\u71d5\u8eab\u6cd5\u4f55\u7b49\u5feb\u6377\uff0c\u8ddf\u7740\u6251\u51fb\uff0c\u5982\u5f71\u968f\u5f62\uff0c\u201c\u55d6\u201d\u7684\u53c8\u662f\u4e00\u5251\u3002\u90d1\u96c4\u56fe\u624b\u638c\u5df2\u4f38\u4e0d \u5f00\uff0c\u53cc\u81c2\u63e1\u62f3\u51fb\u4e0b\uff0c\u8eab\u4e0a\u53c8\u4e2d\u4e86\u4e00\u5251\u3002\u90d1\u96c4\u56fe\u672c\u6765\u662f\u62fc\u7740\u4e0e\u516c\u5b59\u71d5\u4e24\u8d25\u4ff1\u4f24\u7684\uff0c\u662f\u4ee5\u7adf\u7136\u4e0d \u987e\u8eab\u4e0a\u4e2d\u5251\uff0c\u62f3\u5934\u4ecd\u6253\u4e0b\u6765\u3002\u516c\u5b59\u71d5\u89c1\u4ed6\u5982\u6b64\u51f6\u608d\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u7981\u6697\u6697\u5403\u60ca\uff01 \u3000\u3000\u4e0d\u6599\u90d1\u96c4\u56fe\u7684\u62f3\u5934\u8fd8\u672a\u6253\u5230\u516c\u5b59\u71d5\u8eab\u4e0a\uff0c\u53cc\u81c2\u5ffd\u5730\u8f6f\u7ef5\u7ef5\u5730\u5782\u4e86\u4e0b\u6765\u3002\u516c\u5b59\u71d5\u4e00\u4e2a\u201c\u88d9 \u8fb9\u817f\u201d\u8e22\u51fa\uff0c\u6251\u5730\u4e00\u52fe\uff0c\u90d1\u96c4\u56fe\u6c34\u725b\u822c\u7684\u8eab\u8eaf\u5012\u4e86\u4e0b\u53bb\uff0c\u53ea\u662f\u53d1\u51fa\u4e00\u58f0\u547b\u541f\uff0c\u7adf\u7136\u5c31\u65ad\u4e86 \u6c14\u3002\u8fd9\u4e00\u4e0b\u5012\u662f\u516c\u5b59\u71d5\u59cb\u6599\u4e4b\u6240\u4e0d\u53ca\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u5251\u4e5f\u8fd8\u4e0d\u662f\u81f4\u547d\u4e4b\u4f24\uff0c\u600e\u7684\u5730\u5c31\u6b7b \u4e86\u3002 \u3000\u3000\u539f\u6765\u4ed6\u7ed9\u8d3a\u5927\u5a18\u6293\u4e86\u4e00\u4e0b\u3002\u8d3a\u5927\u5a18\u7684\u6307\u7532\u662f\u6709\u6bd2\u7684\uff0c\u90a3\u4e00\u6293\u6070\u6070\u6293\u7740\u4ed6\u80a9\u4e0a\u7684\u4f24\u53e3\uff0c\u4f24 \u4e0a\u52a0\u4f24\uff0c\u5267\u6bd2\u6e17\u5165\u8840\u7ba1\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5df2\u662f\u6bd2\u53d1\u8eab\u4ea1\uff01 \u3000\u3000\u9ad8\u624b\u640f\u6597\uff0c\u54ea\u5bb9\u5f97\u6709\u4e1d\u6beb\u5931\u8bef\uff0c\u8d3a\u5927\u5a18\u5728\u63a8\u5f00\u90d1\u96c4\u56fe\u4e4b\u65f6\uff0c\u62db\u6570\u4e0d\u514d\u7a0d\u7f13\uff0c\u8fd1\u8eab\u7f20\u6597\uff0c \u8bb2\u7a76\u7684\u4ee5\u5feb\u6253\u6162\uff0c\u62db\u6570\u4e00\u7f13\uff0c\u767b\u65f6\u5c31\u7ed9\u4e86\u5389\u5357\u661f\u4e00\u4e2a\u53cd\u6251\u7684\u673a\u4f1a\u3002\u5389\u5357\u661f\u4e00\u638c\u62cd\u51fa\uff0c\u7acb\u5373\u628a \u8d3a\u5927\u5a18\u63a8\u5f00\uff0c\u8ddf\u7740\u4fbf\u662f\u4e00\u5251\uff01 \u3000\u3000\u8d3a\u5927\u5a18\u4e0d\u8fc7\u662f\u4ed7\u7740\u6bd2\u722a\u7684\u5389\u5bb3\uff0c\u7109\u80fd\u6321\u5f97\u7384\u94c1\u5b9d\u5251\u7684\u4e00\u51fb\uff1f\u5979\u53cc\u638c\u9f50\u63a8\uff0c\u4f46\u638c\u529b\u5374\u4e0d\u8db3 \u8361\u5f00\u5251\u5c16\uff0c\u53ea\u542c\u5f97\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u8d3a\u5927\u5a18\u53cc\u638c\u9f50\u65ad\u3002\u516c\u5b59\u71d5\u987a\u624b\u8865\u4e0a\u4e00\u5251\uff0c\u7a7f\u8fc7\u4e86\u5979\u7684\u7435\u7436 \u9aa8\uff0c\u4e5f\u5c31\u4e0d\u518d\u7406\u4f1a\u5979\u7684\u6b7b\u6d3b\u4e86\u3002 \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u5feb\u53bb\u5e2e\u5fd9\u674e\u5927\u54e5\uff01\u201d\u516c\u5b59\u71d5\u9053\uff1a\u201c\u597d\uff01\u201d\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00 \u58f0\uff0c\u968f\u5373\u60ca\u53eb\u4e4b\u58f0\u56db\u8d77\u3002\u539f\u6765\u662f\u8fde\u57ce\u864e\u56e0\u89c1\u8d3a\u5927\u5a18\u5df2\u7ecf\u6bd9\u547d\uff0c\u4e0d\u7531\u5f97\u5fc3\u5ff5\u5168\u7070\u3002\u60f3\u9053\uff1a\u201c\u8d3a \u5927\u5a18\u6b7b\u4e86\uff0c\u6211\u8fd8\u5411\u4f55\u4eba\u53bb\u8ba8\u89e3\u836f\uff1f\u674e\u6566\u867d\u7136\u9976\u6211\uff0c\u6211\u4e5f\u662f\u6d3b\u4e0d\u6210\u4e86\u3002\u53c8\u4f55\u5fc5\u518d\u53d7\u6570\u65e5\u4e4b \u82e6\uff1f\u201d\u4e8e\u662f\u4e00\u5934\u78b0\u5728\u5047\u77f3\u5c71\u4e0a\uff0c\u81ea\u6740\u800c\u4ea1\uff01 \u3000\u3000\u6324\u5728\u9662\u5b50\u91cc\u7684\u5bbe\u5ba2\uff0c\u51e0\u66fe\u89c1\u8fc7\u5982\u6b64\u60e8\u9177\u6076\u6597\u7684\u573a\u9762\uff1f\u4eba\u4eba\u90fd\u662f\u53ea\u6028\u7239\u5a18\u751f\u5c11\u4e86\u4e24\u6761\u817f\uff0c \u8f6c\u773c\u95f4\u9003\u5f97\u5e72\u5e72\u51c0\u51c0\u3002\u6709\u51e0\u4e2a\u80c6\u5c0f\u7684\uff0c\u60f3\u9003\u90fd\u8dd1\u4e0d\u52a8\uff0c\u53cc\u817f\u4e00\u8f6f\uff0c\u762b\u5728\u5730\u4e0a\u5c31\u5413\u6655\u4e86\u3002 \u3000\u3000\u5bbe\u5ba2\u5c3d\u9003\uff0c\u9662\u4e2d\u5012\u817e\u51fa\u4e86\u4e00\u7247\u7a7a\u5730\uff0c\u53f2\u767d\u90fd\u7ea6\u675f\u7684\u4e24\u4e2a\u9ad8\u624b\u2014\u2014\u9752\u9f99\u5e2e\u7684\u5e2e\u4e3b\u9ad8\u5927\u6210\u548c \u767d\u864e\u5e2e\u7684\u5e2e\u4e3b\u675c\u5927\u4e1a\u2014\u2014\u2014\u4e2a\u624b\u4f7f\u72fc\u7259\u68d2\uff0c\u4e00\u4e2a\u6325\u821e\u62a4\u624b\u94a9\uff0c\u53cc\u53cc\u62a2\u5230\uff0c\u62e6\u963b\u5389\u5357\u661f\u3001\u516c\u5b59 \u71d5\u3002 \u3000\u3000\u5389\u5357\u661f\u4e00\u5251\u5288\u53bb\uff0c\u9ad8\u5927\u6210\u4e3e\u68d2\u906e\u62e6\uff0c\u9ad8\u5927\u6210\u81ea\u8d1f\u5927\u751f\u795e\u529b\uff0c\u4e0d\u6599\u5251\u68d2\u4ea4\u51fb\uff0c\u201c\u5f53\u201d\u7684\u4e00 \u58f0\u5de8\u54cd\uff0c\u9ad8\u5927\u6210\u7684\u72e0\u7259\u68d2\u7adf\u7ed9\u7384\u94c1\u5b9d\u5251\u524a\u53bb\u4e86\u4e00\u622a\u3002\u9ad8\u5927\u6210\u864e\u53e3\u6d41\u8840\uff0c\u75be\u5fd9\u95ea\u5f00\u3002\u4f46\u4ed6\u7684\u72fc \u7259\u68d2\u5374\u672a\u8131\u624b\uff0c\u5389\u5357\u661f\u4e5f\u611f\u864e\u53e3\u9178\u9ebb\uff0c\u987e\u4e0d\u5f97\u518d\u5288\u7b2c\u4e8c\u5251\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u5c31\u4ece\u9ad8\u5927\u6210\u8eab\u8fb9\u63a0 \u8fc7\u3002 \u3000\u3000\u516c\u5b59\u71d5\u8ddf\u7740\u4e00\u5251\u523a\u53bb\uff0c\u9ad8\u5927\u6210\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u4e2b\u5934\u4e5f\u6765\u6b3a\u6211\uff01\u201d\u72fc\u7259\u68d2\u6a2a\u68d2\u4e00\u6321\uff0c\u54ea\u77e5 \u516c\u5b59\u71d5\u7684\u957f\u5251\u7adf\u4f3c\u4f1a\u62d0\u5f2f\u4f3c\u7684\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u9ad8\u5927\u6210\u5de6\u81c2\u7740\u4e86\u4e00\u5251\u3002 \u3000\u3000\u675c\u5927\u4e1a\u53cc\u94a9\u76d8\u65cb\u523a\u51fa\uff0c\u53cc\u94a9\u4e43\u662f\u58f3\u5236\u5200\u5251\u7684\u4e00\u79cd\u5175\u5668\uff0c\u516c\u5b59\u71d5\u75be\u523a\u4e03\u5251\uff0c\u5251\u5c16\u867d\u6ca1\u7ed9\u4ed6 \u53cc\u94a9\u9501\u4f4f\uff0c\u5374\u4e5f\u7834\u4e0d\u4e86\u4ed6\u7684\u62db\u6570\u3002\u9ad8\u5927\u6210\u53d7\u4e86\u4e00\u70b9\u8f7b\u4f24\uff0c\u8d8a\u53d1\u5927\u6012\uff0c\u72e0\u7259\u68d2\u821e\u5f97\u547c\u547c\u98ce\u54cd\uff0c \u6d51\u8eab\u4e0a\u4e0b\uff0c\u6cfc\u6c34\u4e0d\u8fdb\u3002\u516c\u5b59\u71d5\u518d\u60f3\u4f24\u4ed6\uff0c\u5df2\u662f\u4e0d\u80fd\u3002\u8fd9\u4e24\u4eba\u90fd\u662f\u4e00\u5e2e\u4e4b\u4e3b\uff0c\u6b66\u529f\u751a\u9ad8\uff0c\u4e8c\u4eba \u8054\u624b\u628a\u516c\u5b59\u71d5\u903c\u5f97\u6b65\u6b65\u540e\u9000\u3002\u516c\u5b59\u71d5\u95ea\u7535\u5251\u6cd5\u7684\u5a01\u529b\u53d1\u6325\u4e0d\u51fa\uff0c\u6e10\u6e10\u843d\u5728\u4e0b\u98ce\u3002 \u3000\u3000\u9662\u5b50\u91cc\u6b66\u529f\u6700\u5f3a\u7684\u662f\u5c06\u519b\u5e9c\u7684\u603b\u7ba1\u5b89\u4fca\u5ead\uff0c\u674e\u6566\u592b\u5987\u8054\u624b\u6218\u4ed6\uff0c\u5140\u662f\u611f\u5230\u5403\u529b\u3002 \u3000\u3000\u5389\u5357\u661f\u8d76\u5230\uff0c\u7acb\u5373\u4fbf\u662f\u4e00\u62db\u201c\u529b\u5288\u534e\u5c71\u201d\uff0c\u5b89\u4fca\u5ead\u542c\u8fd9\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\u6781\u4e3a\u5f3a\u52b2\uff0c\u5403\u4e86\u4e00 \u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u597d\u6a2a\uff0c\u53ea\u6015\u4e0d\u80fd\u529b\u654c\u3002\u201d\u4ed6\u5e94\u62db\u4e5f\u662f\u5f53\u771f\u8fc5\u901f\uff0c\u4e00\u4e2a\u201c\u79fb\u5f62\u6362\u6b65\u201d\uff0c\u907f \u5f00\u4e86\u674e\u6566\u592b\u5987\u7684\u53cc\u5251\uff0c\u4e03\u8282\u97ad\u8f7b\u8f7b\u4e00\u6325\uff0c\u4f7f\u4e86\u4e2a\u201c\u5e26\u201d\u5b57\u8bc0\uff0c\u5c06\u5389\u5357\u661f\u7684\u7384\u94c1\u5b9d\u5251\u62e8\u8fc7\u4e00 \u8fb9\u3002\u53ef\u662f\u5389\u5357\u661f\u7684\u7384\u94c1\u5251\u5b9e\u5728\u662f\u592a\u8fc7\u950b\u5229\uff0c\u7aef\u7684\u6709\u201c\u5439\u6bdb\u7acb\u65ad\u201d\u4e4b\u80fd\uff0c\u5b89\u4fca\u5ead\u867d\u7136\u80fd\u591f\u89e3\u5f00 \u4ed6\u7684\u62db\u6570\uff0c\u4e03\u8282\u97ad\u5374\u7ed9\u4ed6\u7684\u5b9d\u5251\u524a\u53bb\u4e86\u4e00\u8282\u3002\u6b64\u65f6\u9662\u5b50\u91cc\u7684\u536b\u58eb\u5341\u4e5d\u53d7\u4f24\uff0c\u6ca1\u4f24\u7684\u4e5f\u63d2\u4e0d\u8fdb \u624b\u3002 \u3000\u3000\u5389\u5357\u661f\u75be\u5288\u4e09\u5251\uff0c\u5b89\u4fca\u5ead\u7684\u957f\u97ad\u53c8\u65ad\u4e86\u4e24\u8282\uff0c\u201c\u4e03\u8282\u97ad\u201d\u53d8\u6210\u4e86\u56db\u8282\u97ad\uff0c\u5b89\u4fca\u5ead\u5927\u53eb \u9053\uff1a\u201c\u6765\u4eba\u5566\uff01\u201d\u8981\u77e5\u9662\u5b50\u4e2d\u7684\u9ad8\u5927\u6210\u3001\u675c\u5927\u4e1a\u3001\u90d1\u96c4\u56fe\u7b49\u4eba\uff0c\u672c\u9886\u867d\u7136\u4e0d\u5dee\uff0c\u5374\u53ea\u80fd\u7b97\u662f \u7b2c\u4e8c\u6d41\u7684\u9ad8\u624b\u3002\u7b2c\u4e00\u6d41\u9ad8\u624b\u90fd\u5728\u793c\u5802\u4e4b\u4e2d\u3002 \u3000\u3000\u9662\u5b50\u91cc\u5df2\u7ecf\u6253\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u793c\u5802\u91cc\u7684\u4eba\u4e0d\u4f1a\u4e0d\u77e5\uff0c\u4f46\u5374\u4e0d\u89c1\u6709\u4eba\u6765\u63f4\u3002\u5b89\u4fca\u5ead\u89c9\u5f97\u5341\u5206 \u5947\u602a\uff0c\u8feb\u4e0d\u5f97\u5df1\uff0c\u53ea\u597d\u51fa\u58f0\u53ec\u5524\u3002 \u3000\u3000\u4e0d\u6599\u5c31\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u53ea\u542c\u5f97\u793c\u5802\u4e2d\u4e5f\u662f\u4eba\u58f0\u9f0e\u6cb8\uff0c\u540c\u6837\u7684\u6709\u4eba\u5927\u53eb\uff1a\u201c\u6765\u4eba\u5566\uff01\u201d \u3000\u3000\u793c\u5802\u4e2d\u6709\u4eba\u51b2\u51fa\u6765\u4e86\uff0c\u5047\u5982\u662f\u4ed3\u7687\u9003\u547d\u7684\u4e00\u4f17\u5bbe\u5ba2\uff0c\u539f\u6765\u793c\u5802\u91cc\u4e5f\u51fa\u4e86\u4e8b\u60c5\uff0c\u800c\u4e14\u662f\u66f4 \u4e3a\u60ca\u4eba\u7684\u4e8b\u60c5\uff01 \u3000\u3000\u4e14\u8bf4\u53f2\u7ea2\u82f1\u6276\u7740\u7261\u4e39\uff0c\u7f13\u7f13\u8d70\u5165\u793c\u5802\u3002\u4ece\u8499\u5934\u7684\u7f57\u5e15\u7f1d\u9699\u5077\u7aa5\u51fa\u53bb\uff0c\u53ea\u89c1\u6587\u9053\u5e84\u3001\u6587\u80dc \u4e2d\u53d4\u4f84\u3001\u6d77\u7802\u5e2e\u7684\u6c99\u5343\u5cf0\u3001\u6c99\u91cd\u5c71\u7236\u5b50\u4ee5\u53ca\u5f53\u4eca\u4e4b\u4e16\u552f\u4e00\u628a\u4fee\u7f57\u9634\u715e\u529f\u7ec3\u5230\u7b2c\u4e5d\u91cd\u7684\u9633\u6d69\u7b49 \u4eba\uff0c\u90fd\u5728\u5bbe\u5ba2\u4e4b\u4e2d\u3002\u518d\u52a0\u4e0a\u5979\u7684\u54e5\u54e5\u53f2\u767d\u90fd\u4ee5\u53ca\u516d\u5408\u5e2e\u7684\u4e09\u5927\u9999\u4e3b\uff0c\u793c\u5802\u4e2d\u5f53\u771f\u53ef\u4ee5\u8bf4\u5f97\u662f \u9ad8\u624b\u5982\u4e91\u3001\u7fa4\u96c4\u4e91\u96c6\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u89c1\u4e86\u5982\u6b64\u9635\u4ed7\uff0c\u5fc3\u91cc\u4e5f\u4e0d\u7531\u5f97\u6697\u6697\u5403\u60ca\uff01\u8fd9\u5403\u60ca\u5e76\u975e\u662f\u4e3a\u4e86\u81ea\u5df1\uff0c\u5979\u81ea\u5df1\u662f\u65e9\u5df2 \u62fc\u7740\u8c41\u4e86\u6027\u547d\u7684\u4e86\u3002\u5979\u662f\u4e3a\u91d1\u9010\u6d41\u62c5\u5fe7\uff01 \u3000\u3000\u5979\u8fd8\u672a\u66fe\u53d1\u73b0\u91d1\u9010\u6d41\uff0c\u4f46\u5979\u575a\u4fe1\u91d1\u9010\u6d41\u662f\u4e00\u5b9a\u4f1a\u6765\u7684\u3002\u4f46\u5728\u8fd9\u8bb8\u591a\u9ad8\u624b\u73af\u4f3a\u4e4b\u4e0b\uff0c\u91d1\u9010 \u6d41\u9664\u975e\u4e0d\u9732\u9762\uff0c\u4e00\u9732\u9762\u53ea\u6015\u4e5f\u662f\u96be\u514d\u6709\u6027\u547d\u4e4b\u5fe7\uff01 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u201c\u8d5e\u793c\u751f\u201d\u53eb\u9053\uff1a\u201c\u65b0\u4eba\u4e0a\u5802\uff0c\u65b0\u90ce\u8bf7\u51fa\uff01\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u559c\u6d0b\u6d0b\u5730\u8d70\u4e0a\u7684\u6765\uff0c\u624b\u4e2d\u62ff\u7740\u4e00\u628a\u6298\u6247\uff0c\u6309\u7167\u5f53\u65f6\u6240\u884c\u7684\u4e60\u4fd7\uff0c\u4ed6\u5e94\u8be5\u7528\u6298\u6247\u6311 \u5f00\u65b0\u5a18\u7684\u7f57\u5e15\uff0c\u7136\u540e\u548c\u65b0\u5a18\u62dc\u5802\u3002 \u3000\u3000\u65b0\u90ce\u5df2\u6765\u8fce\u63a5\u65b0\u5a18\uff0c\u62a4\u9001\u65b0\u5a18\u7684\u5927\u8205\u5b50\u53f2\u767d\u90fd\u81ea\u662f\u8981\u9000\u8fc7\u4e00\u8fb9\u4e86\u3002\u53f2\u7ea2\u82f1\u643a\u6765\u7684\u201c\u966a\u5ac1 \u5382\u9b1f\u201d\u5219\u8fd8\u662f\u8ddf\u5728\u5979\u7684\u540e\u9762\u3002 \u3000\u3000\u6b63\u5f53\u65b0\u90ce\u4f38\u51fa\u6298\u6247\u7684\u65f6\u5019\uff0c\u5ffd\u542c\u5f97\u65b0\u5a18\u4e00\u58f0\u51b7\u7b11\uff0c\u65b0\u90ce\u5927\u53eb\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d\u9661\u7136\u95f4\u53ea\u89c1 \u65b0\u5a18\u5df1\u662f\u81ea\u5df1\u7529\u5f00\u4e86\u7f57\u5e15\uff0c\u53f3\u624b\u62ff\u7740\u4e00\u628a\u660e\u6643\u6643\u7684\u77ed\u5251\uff0c\u5de6\u624b\u63e1\u7740\u4e00\u6839\u957f\u97ad\u3002\u957f\u97ad\u6a2a\u626b\uff0c\u77ed \u5251\u5411\u7740\u65b0\u90ce\u7684\u80f8\u53e3\u76f4\u523a\uff01 \u3000\u3000\u539f\u6765\u53f2\u7ea2\u82f1\u5df2\u628a\u90a3\u53e3\u6bd2\u9488\u63d2\u8fdb\u4e86\u5e05\u5b5f\u96c4\u7684\u80a9\u5934\u3002 \u3000\u3000\u5e05\u76c2\u96c4\u4e5f\u4e0d\u662f\u6beb\u65e0\u6212\u5907\u7684\uff0c\u4f46\u5c0f\u5c0f\u7684\u4e00\u53e3\u6bd2\u9488\uff0c\u85cf\u5728\u53f2\u7ea2\u82f1\u7684\u6307\u7532\u7f1d\u4e2d\uff0c\u8fd9\u5374\u662f\u4ed6\u6599\u60f3 \u4e0d\u5230\uff0c\u4e5f\u770b\u4e0d\u51fa\u6765\u7684\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u7684\u8f6f\u97ad\u3001\u77ed\u5251\u5219\u662f\u85cf\u5728\u5979\u90a3\u201c\u966a\u5ac1\u4e2b\u5934\u201d\u7261\u4e39\u7684\u8eab\u4e0a\u3002\u5e05\u5b5f\u96c4\u3001\u53f2\u767d\u90fd\u53ea\u6ce8\u610f\u5230 \u53f2\u7ea2\u82f1\u8eab\u4e0a\u6ca1\u85cf\u5175\u5668\uff0c\u5374\u6ca1\u6709\u6ce8\u610f\u5979\u7684\u4e2b\u5934\u3002\u8fd9\u4e2a\u4e2b\u5934\u7adf\u6562\u4e0e\u53f2\u7ea2\u82f1\u540c\u8c0b\uff0c\u4e0d\u60dc\u727a\u7272\u81ea\u5df1\u7684 \u6027\u547d\uff0c\u8fd9\u4e5f\u662f\u4ed6\u4eec\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u7684\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u5c04\u51fa\u4e86\u6bd2\u9488\uff0c\u8ddf\u7740\u53d6\u97ad\u3001\u62d4\u5251\u3001\u8fdb\u62db\uff0c\u51e0\u4e2a\u52a8\u4f5c\u4e00\u6c14\u5475\u6210\uff0c\u5feb\u5982\u95ea\u7535\uff01 \u3000\u3000\u60ca\u547c\u9a87\u53eb\u4e4b\u4e2d\uff0c\u53ea\u89c1\u5bd2\u5149\u4e00\u95ea\uff0c\u53f2\u7ea2\u82f1\u7684\u77ed\u5251\u5df2\u662f\u523a\u5230\u4e86\u5e05\u5b5f\u96c4\u7684\u80f8\u53e3\uff0c\u7eb5\u6709\u6ee1\u5802\u9ad8 \u624b\uff0c\u4e5f\u662f\u96be\u4ee5\u6551\u4ed6\u6027\u547d\u7684\u4e86\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u6bcf\u4e00\u9897\u5fc3\u90fd\u597d\u50cf\u8981\u4ece\u8154\u5b50\u91cc\u8df3\u51fa\u6765\uff0c\u6bcf\u4e00\u4e2a\u4eba\u90fd\u4ee5\u4e3a\u8840\u6e85\u534e\u5802\u52bf\u6240\u4e0d\u514d\uff01\u4f46 \u4e0d\u6599\u8fd9\u4e00\u5251\u523a\u4e0b\uff0c\u5374\u53ea\u662f\u542c\u5f97\u8f7b\u8f7b\u7684\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5e05\u5b5f\u96c4\u5012\u8dc3\u4e09\u6b65\uff0c\u95ea\u8fc7\u4e00\u8fb9\uff0c\u7adf\u7136\u6ca1\u6709 \u5012\u4e0b\uff0c\u8eab\u4e0a\u4e5f\u65e0\u534a\u70b9\u8840\u8ff9\uff01 \u3000\u3000\u539f\u6765\u5e05\u5b5f\u96c4\u8eab\u7ecf\u767e\u6218\uff0c\u6b66\u529f\u53c8\u9ad8\uff0c\u867d\u7136\u53d8\u8d77\u4ed3\u5352\uff0c\u4e2d\u4e86\u6bd2\u9489\uff0c\u5c45\u7136\u4e34\u5371\u4e0d\u4e71\u3002\u767e\u5fd9\u4e2d\u4ed6 \u6765\u4e0d\u53ca\u62d4\u51fa\u968f\u8eab\u4f69\u5251\uff0c\u5c31\u7528\u90a3\u67c4\u6298\u6247\u5f53\u4f5c\u5175\u5668\uff0c\u6298\u6247\u4e00\u5f20\uff0c\u4f7f\u51fa\u6700\u4e0a\u4e58\u7684\u5378\u529b\u5316\u52b2\u7684\u529f\u592b\uff0c \u53f2\u7ea2\u82f1\u4e00\u5251\u4ece\u4ed6\u7684\u6247\u9762\u5212\u8fc7\u3002\u5251\u5c16\u767b\u65f6\u5c31\u6ed1\u8fc7\u4e86\u4e00\u8fb9\u3002\u6298\u6247\u867d\u7ed9\u6233\u7a7f\uff0c\u5374\u6ca1\u6709\u523a\u5230\u4ed6\u7684\u8eab \u4e0a\u3002 \u3000\u3000\u4f46\u8fd9\u8f7b\u8f7b\u7684\u201c\u55e4\u201d\u7684\u4e00\u54cd\u8fc7\u540e\uff0c\u53ea\u542c\u5f97\u201c\u54ce\u54df\uff0c\u54ce\u54df\uff01\u201d\u201c\u535c\u901a\uff0c\u535c\u901a\uff01\u201d\u4e4b\u58f0\u6b64\u8d77\u5f7c \u843d\u3002\u539f\u6765\u53f2\u7ea2\u82f1\u8fd9\u4e00\u62db\u4e43\u662f\u5de6\u97ad\u53f3\u5251\u540c\u65f6\u65bd\u5c55\u7684\uff0c\u5728\u77ed\u5251\u5411\u524d\u76f4\u523a\u4e4b\u65f6\uff0c\u5979\u7684\u957f\u97ad\u4e5f\u5728\u540c\u65f6 \u6a2a\u626b\u51fa\u53bb\u3002\u8fd9\u4e00\u97ad\u5c31\u5377\u7ffb\u4e86\u51e0\u4e2a\u89c2\u793c\u7684\u5ba2\u4eba\uff0c\u6070\u6070\u6784\u6210\u4e86\u5e05\u5b5f\u96c4\u4e0e\u5979\u4e4b\u95f4\u7684\u969c\u788d\u3002\u5e05\u5b5f\u96c4\u4fa5 \u5e78\u9003\u4e86\u6027\u547d\uff0c\u5927\u6012\u4e4b\u4e0b\uff0c\u6b63\u8981\u51fa\u624b\u64d2\u62ff\uff0c\u6709\u4e24\u4e2a\u88ab\u7eca\u7ffb\u7684\u5ba2\u4eba\uff0c\u5374\u6070\u5de7\u5411\u4ed6\u5012\u4e0b\u3002 \u3000\u3000\u6709\u8d44\u683c\u8fdb\u8fd9\u793c\u5802\u89c2\u793c\u7684\u90fd\u662f\u8fbe\u5b98\u8d35\u4eba\uff1b\u5e05\u5b5f\u96c4\u4e0d\u6562\u4f24\u8d35\u5bbe\u7684\u6027\u547d\uff0c\u53cc\u638c\u4e00\u51fa\uff0c\u6293\u4f4f\u4e86\u90a3 \u4e24\u4e2a\u5ba2\u4eba\uff0c\u8f7b\u8f7b\u63a8\u8fc7\u4e00\u8fb9\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53f2\u7ea2\u82f1\u53c8\u5df2\u662f\u201c\u56de\u98ce\u5377\u67f3\u201d\uff0c\u75be\u626b\u4e09\u97ad\uff0c\u8fd9\u4e00 \u6765\u906d\u53d7\u201c\u65e0\u5984\u4e4b\u707e\u201d\u7684\u201c\u8d35\u5ba2\u201d\u66f4\u591a\uff0c\u6a2a\u4e03\u7ad6\u516b\u7684\u5012\u6ee1\u4e86\u4e00\u5730\u3002 \u3000\u3000\u53f2\u767d\u90fd\u53c8\u60ca\u53c8\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u8d31\u4e2b\u5934\u53cd\u4e86\u53cd\u4e86\uff01\u4f60\uff0c\u4f60\uff0c\u4f60\uff0c\u4f60\u662f\u4e0d\u60f3\u6d3b\u5566\uff01\u201d\u53f2\u7ea2 \u82f1\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u53cd\u4e86\uff0c\u6211\u662f\u4e0d\u60f3\u6d3b\u4e86\u3002\u4f46\u8fd9\u91cc\u6700\u5c11\u6709\u4e00\u4e2a\u4eba\u8981\u966a\u6211\u6b7b\u6389\uff0c\u4ed6\u5c31\u662f\u4f60 \u6240\u8981\u5df4\u7ed3\u7684\u5e05\u5b5f\u96c4\uff01\u201d \u3000\u3000\u5e05\u76c2\u96c4\u6545\u4f5c\u9547\u5b9a\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u533a\u533a\u4e00\u53e3\u6bd2\u9488\uff0c\u8c05\u5b83\u4e5f\u8fd8\u4e0d\u80fd\u5c31\u8981\u4e86\u6211\u7684\u6027\u547d\u3002\u53f2\u59d1 \u5a18\uff0c\u4f60\u6254\u6389\u5175\u5668\uff0c\u8d76\u5feb\u5411\u5ba2\u4eba\u8d54\u7f6a\uff0c\u54b1\u4eec\u8fd8\u53ef\u5546\u91cf\u3002\u201d\u6b64\u65f6\u5e05\u5b5f\u96c4\u4f55\u5c1d\u4e0d\u77e5\u53f2\u7ea2\u82f1\u5df2\u662f\u51b3\u4e0d \u80fd\u518d\u505a\u4ed6\u7684\u65b0\u5a18\uff0c\u4f46\u4ed6\u5374\u662f\u6709\u6240\u987e\u5fcc\uff0c\u6015\u903c\u5f97\u72e0\u4e86\uff0c\u53f2\u7ea2\u82f1\u51fa\u624b\u5927\u4f24\u5bbe\u5ba2\uff01\u5e05\u5b5f\u96c4\u4ee5\u4e3a\u81ea\u5df1 \u7684\u5185\u529f\u6df1\u539a\uff0c\u521d\u65f6\u7684\u786e\u662f\u4e0d\u5927\u628a\u8fd9\u6bd2\u9488\u653e\u5728\u5fc3\u4e0a\uff0c\u4e0d\u6599\u4ed6\u7b11\u58f0\u672a\u4e86\uff0c\u53ea\u89c9\u534a\u8fb9\u8eab\u5b50\u5df2\u7ecf\u9ebb \u6728\uff0c\u8fd9\u624d\u77e5\u9053\u8fd9\u679a\u6bd2\u9488\u975e\u6bd4\u5bfb\u5e38\u3002\u8fde\u5fd9\u8c03\u5300\u6c14\u606f\uff0c\u8fd0\u529f\u5fa1\u6bd2\uff0c\u4e0d\u6562\u518d\u52a8\u3002 \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u5e05\u5c06\u519b\u4f60\u8fdb\u53bb\u6b47\u6b47\uff0c\u8fd9\u8d31\u5a62\u6211\u6765\u66ff\u4f60\u60e9\u6cbb\uff01\u201d\u53f2\u7ea2\u82f1\u97ad\u4e0d\u505c\u6325\uff0c\u559d\u9053\uff1a \u201c\u4f60\u6562\u4e0a\u6765\uff01\u4f60\u8981\u4e0a\u6765\uff0c\u966a\u6211\u6b7b\u7684\u5c31\u4e0d\u53ea\u4e00\u4e2a\u4e86\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7684\u529f\u592b\u662f\u6211\u6559\u7684\uff0c\u5c82\u80fd\u5728\u6211\u9762\u524d\u901e\u5f3a\uff1f\u54fc\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u6562\u4e0a\u6765\uff1f\u201d \u5288\u7a7a\u638c\u53d1\u51fa\uff0c\u4e00\u80a1\u638c\u98ce\u8361\u5f00\u53f2\u7ea2\u82f1\u7684\u957f\u97ad\uff0c\u5927\u8e0f\u6b65\u5c31\u4e0a\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u7684\u4e00\u4e2a\u526f\u5b98\u60ca\u53eb\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\uff0c\u522b\u9c81\u83bd\uff01\u201d\u6b64\u65f6\u4f17\u5bbe\u5ba2\u5df2\u662f\u7eb7\u7eb7\u593a\u95e8\u5954\u9003\uff0c\u4f46\u7ed9 \u53f2\u7ea2\u82f1\u6253\u7ffb\u7684\u90a3\u5341\u51e0\u4e2a\u5ba2\u4eba\uff0c\u8fd8\u662f\u6a2a\u4e03\u7ad6\u516b\u7684\u8eba\u5728\u5730\u4e0a\uff0c\u6025\u5207\u95f4\u54ea\u91cc\u80fd\u591f\u6323\u624e\u8d77\u6765\uff1f \u3000\u3000\u793c\u5802\u4e2d\u9ad8\u624b\u867d\u591a\uff0c\u4f46\u8fd9\u5341\u51e0\u4e2a\u5012\u6ee1\u4e00\u5730\u7684\u5ba2\u4eba\uff0c\u5374\u7b49\u4e8e\u662f\u5b89\u7f6e\u5728\u53f2\u7ea2\u82f1\u5468\u56f4\u7684\u7eca\u811a\u77f3\u3002 \u5f53\u771f\u5927\u6253\u8d77\u6765\uff0c\u8fd9\u4e9b\u4eba\u7109\u80fd\u4fdd\u5f97\u6027\u547d\uff1f\u90a3\u4e2a\u526f\u5b98\u62c5\u5fe7\u7684\u6b63\u662f\u8fd9\u5c42\u3002 \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u4e0d\u788d\u4e8b\uff01\u201d\u53ea\u89c1\u4ed6\u8fc8\u6b65\u4e0a\u524d\uff0c\u8fde\u73af\u8d77\u811a\uff0c\u628a\u8eba\u5728\u5730\u4e0a\u7684\u5ba2\u4eba\u4e00\u4e2a\u4e2a\u5730\u8e22\u5f97 \u98de\u5411\u5927\u5802\u7684\u95e8\u53e3\u3002\u8bf4\u4e5f\u5947\u602a\uff0c\u90a3\u4e9b\u4eba\u6328\u4e86\u4ed6\u7684\u4e00\u811a\uff0c\u843d\u4e0b\u5730\u65f6\uff0c\u5374\u662f\u7ad9\u5f97\u5e73\u5e73\u7a33\u7a33\u3002\u5012\u597d\u50cf \u53f2\u767d\u90fd\u4e0d\u662f\u7528\u811a\uff0c\u800c\u662f\u7528\u624b\u5c06\u4ed6\u4eec\u8f7b\u8f7b\u63d0\u8d77\uff0c\u518d\u653e\u4e0b\u6765\u4f3c\u7684\u3002\u539f\u6765\u53f2\u767d\u90fd\u7684\u529b\u9053\u7528\u5f97\u5de7\u5999\u4e4b \u6781\uff0c\u8e22\u5728\u4ed6\u4eec\u7684\u8eab\u4e0a\uff0c\u5374\u80fd\u591f\u4eca\u5f97\u4ed6\u4eec\u4e1d\u6beb\u65e0\u4f24\u3002\u53f2\u7ea2\u82f1\u7684\u957f\u97ad\u7ed9\u4ed6\u7684\u638c\u98ce\u8361\u5f00\uff0c\u4e5f\u662f\u65bd\u5c55 \u4e0d\u4e86\u8fa3\u624b\u3002 \u3000\u3000\u90a3\u4e9b\u5ba2\u4eba\u5e76\u6ca1\u53d7\u4f24\uff0c\u4f46\u6709\u51e0\u4e2a\u5374\u5413\u7834\u4e86\u80c6\uff0c\u843d\u5728\u95e8\u8fb9\uff0c\u7adf\u7136\u4e0d\u4f1a\u9003\u8d70\uff0c\u8f6f\u7ef5\u7ef5\u7684\u9760\u5728\u522b \u4eba\u8eab\u4e0a\uff0c\u56e0\u6b64\u53c8\u8dcc\u4e0b\u6765\u3002\u90a3\u4e2a\u526f\u5b98\u5fd9\u53eb\u536b\u58eb\u6276\u4ed6\u4eec\u51fa\u53bb\u3002\u8f6c\u773c\u95f4\u6ee1\u5802\u5bbe\u5ba2\u8d70\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u90a3\u4e2a\u5c0f\u4e2b\u9b1f\u8fd8\u5728\u53f2\u7ea2\u82f1\u8eab\u8fb9\uff0c\u53f2\u767d\u90fd\u626b\u6e05\u4e86\u201c\u7eca\u811a\u77f3\u201d\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u817e\u5730\u98de\u8d77\u4e00\u811a\uff0c\u5c31 \u5411\u5979\u8e22\u53bb\u3002\u4ed6\u6068\u8fd9\u4e2a\u4e2b\u9b1f\u4e0e\u53f2\u7ea2\u82f1\u540c\u8c0b\uff0c\u8fd9\u4e00\u811a\u53ef\u5c31\u4e0d\u662f\u513f\u620f\u7684\u4e86\uff0c\u800c\u662f\u5f53\u771f\u8981\u53d6\u8fd9\u5c0f\u4e2b\u5934 \u7684\u6027\u547d\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u5438\u4e86\u53e3\u6c14\uff0c\u4f7f\u51fa\u6d51\u8eab\u6c14\u529b\uff0c\u53cd\u624b\u4e00\u97ad\uff0c\u53f2\u767d\u90fd\u5355\u51ed\u638c\u98ce\u8361\u5b83\u4e0d\u5f00\uff0c\u4f38\u624b\u4e00\u6293\uff0c\u6293 \u4f4f\u4e86\u5979\u7684\u97ad\u68a2\u3002\u90a3\u4e00\u811a\u52bf\u9053\u7565\u7f13\uff0c\u4f46\u4ecd\u7136\u5411\u8fd9\u5c0f\u4e2b\u5934\u8e22\u53bb\u3002 \u3000\u3000\u773c\u770b\u8fd9\u5c0f\u4e2b\u5934\u6027\u547d\u4e0d\u4fdd\uff0c\u53f2\u7ea2\u82f1\u4e5f\u96be\u9003\u9b54\u722a\uff0c\u5c31\u5728\u8fd9\u5371\u673a\u77ac\u606f\u4e4b\u95f4\uff0c\u5ffd\u6240\u5f97\u201c\u8f70\u9686\u201d\u4e00 \u58f0\uff0c\u7a81\u7136\u6709\u4e2a\u4eba\u4ece\u7a7a\u4e2d\u8df3\u4e0b\u6765\u3002\u8fd9\u4e2a\u4eba\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u91d1\u9010\u6d41\u3002 \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u6628\u665a\u8fdb\u57ce\u4e4b\u540e\uff0c\u5c31\u6084\u6084\u7684\u6f5c\u5165\u8fd9\u4e2a\u793c\u5802\uff0c\u8eb2\u5728\u5c06\u519b\u5e9c\u7684\u533e\u989d\u540e\u9762\u3002\u4ed6\u662f\u65e9\u5df2 \u4f30\u8ba1\u5230\u4e86\u4eca\u5929\u4e0d\u5bb9\u6613\u6df7\u8fdb\u6765\u7684\u3002 \u3000\u3000\u53f2\u767d\u90fd\u4e00\u811a\u8e22\u51fa\uff0c\u9661\u7136\u95f4\u53ea\u89c9\u8db3\u5fc3\u4e00\u9707\uff0c\u539f\u6765\u662f\u7ed9\u91d1\u9010\u6d41\u7684\u4e00\u679a\u94dc\u94b1\u6253\u7740\u4e86\u4ed6\u7684\u8db3\u5fc3\u3002 \u53f2\u767d\u90fd\u7a7f\u7740\u539a\u5e95\u7c89\u978b\uff0c\u53c8\u6b63\u662f\u7528\u731b\u529b\u8e22\u51fa\u53bb\u7684\uff0c\u5c0f\u5c0f\u4e00\u679a\u94dc\u94b1\u81ea\u662f\u4e0d\u80fd\u4eca\u4ed6\u53d7\u4f24\uff0c\u4f46\u91d1\u9010\u6d41 \u7a81\u7136\u51fa\u73b0\uff0c\u9976\u662f\u53f2\u767d\u90fd\u80c6\u5927\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u5413\u4e86\u4e00\u8df3\u3002\u6b64\u65f6\u53d7\u4e86\u6697\u88ad\uff0c\u53c8\u4e0d\u77e5\u91d1\u9010\u6d41\u8fd8\u6709\u4ec0\u4e48\u5389 \u5bb3\u7684\u540e\u7740\uff0c\u53ea\u597d\u8d76\u7d27\u7f29\u811a\u677e\u624b\uff0c\u95ea\u8fc7\u4e00\u8fb9\u3002 \u3000\u3000\u90a3\u5757\u201c\u5c06\u519b\u5e9c\u201d\u7684\u5927\u533e\u989d\u7ed9\u91d1\u9010\u6d41\u4e00\u62f3\u6253\u70c2\uff0c\u4ece\u534a\u7a7a\u4e2d\u8dcc\u4e0b\u6765\uff0c\u7ad9\u5728\u95e8\u53e3\u7684\u536b\u58eb\u8fde\u5fd9\u95ea \u907f\u3002\u6709\u4e24\u4e2a\u8dd1\u5728\u6700\u540e\u9762\u7684\u5bbe\u5ba2\u7ed9\u6728\u5757\u6253\u7a7f\u4e86\u5934\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8d81\u7740\u53f2\u767d\u90fd\u731b\u7136\u53d7\u60ca\uff0c\u95ea\u8eab\u8eb2\u4ed6\u4e4b\u9645\uff0c\u95ea\u7535\u822c\u7684\u63a0\u8fc7\u53bb\uff0c\u62e6\u8170\u62b1\u8d77\u90a3\u4e2a\u4e2b\u5934\uff0c\u5728 \u5979\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u6709\u6211\u4fdd\u62a4\u4f60\u7684\u5c0f\u59d0\uff0c\u4f60\u5feb\u8d70\u5427\uff01\u201d\u632f\u81c2\u4e00\u629b\uff0c\u628a\u8fd9\u5c0f\u4e2b\u5934\u629b\u51fa\u4e86\u95e8\u5916\u3002 \u3000\u3000\u53f2\u767d\u90fd\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u5f53\u771f\u662f\u80c6\u5927\u5305\u5927\uff01\u201d\u91d1\u9010\u6d41\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u591a\u627f\u5938\u5956\uff0c\u597d\uff0c \u8fd9\u5c31\u8bf7\u4f60\u770b\u6211\u864e\u53e3\u62d4\u7259\u7684\u624b\u6bb5\uff01\u201d\u7b11\u58f0\u672a\u5df2\uff0c\u53cc\u65b9\u5df2\u662f\u95ea\u7535\u822c\u7684\u4ea4\u4e0a\u4e86\u624b\uff0c\u4ee5\u5251\u5bf9\u5251\uff0c\u4ee5\u638c \u5bf9\u638c\uff0c\u5feb\u901f\u65e0\u4f26\u7684\u6597\u4e86\u4e09\u62db\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5de6\u62f3\u4e00\u6643\uff0c\u6a2a\u8098\u649e\u51fa\uff0c\u9661\u7684\u4e00\u62f3\u8d70\u4e0a\uff0c\u4e2d\u6307\u7684\u8282\u9aa8\u51f8\u51fa\uff0c\u5982\u540c\u68f1\u89d2\u4f3c\u7684\uff0c\u6572\u6253\u53f2 \u767d\u90fd\u7684\u8033\u95e8\u3002\u8fd9\u4e00\u62db\u6709\u4e2a\u540d\u5802\uff0c\u53eb\u505a\u201c\u7f9a\u7f8a\u6302\u89d2\u201d\uff0c\u770b\u4f3c\u62f3\u6253\u4e0b\u5df4\uff0c\u771f\u6b63\u5389\u5bb3\u4e4b\u5904\u5374\u5728\u4ed6\u4e2d \u6307\u7684\u6572\u8d70\u3002\u8033\u95e8\u7684\u8f6f\u9aa8\u6700\u4e3a\u8106\u5f31\uff0c\u5018\u7ed9\u51fb\u788e\uff0c\u4e0d\u6b7b\u4e5f\u8981\u53d8\u6210\u767d\u75f4\u3002 \u3000\u3000\u53f2\u767d\u90fd\u8bc6\u5f97\u5389\u5bb3\uff0c\u7109\u80fd\u7ed9\u4ed6\u6253\u4e2d\uff1f\u5f53\u4e0b\u8fd8\u4e86\u4e00\u62db\u201c\u5929\u738b\u6258\u5854\u201d\uff0c\u77f3\u81c2\u4e00\u5708\uff0c\u638c\u80cc\u4e00\u6325\uff0c \u53cd\u624b\u4fbf\u65bd\u64d2\u62ff\u7edd\u6280\u3002 \u3000\u3000\u53f2\u767d\u90fd\u4ee5\u653b\u4e3a\u5b88\uff0c\u89e3\u62c6\u5f97\u5f53\u771f\u662f\u6c89\u7a33\u72e0\u8fa3\u517c\u800c\u6709\u4e4b\u3002\u4e0d\u6599\u91d1\u9010\u6d41\u8fd9\u4e00\u62db\u5374\u662f\u865a\u62db\uff0c\u53f2\u767d \u90fd\u4e00\u6293\u6293\u7a7a\uff0c\u53ea\u542c\u5f97\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u65c1\u8fb9\u6709\u4e00\u4e2a\u4eba\u8dcc\u8dcc\u649e\u649e\u7684\u659c\u7a9c\u51fa\u53bb\uff0c\u5634\u5df4\u4e00\u5f20\uff0c\u55b7\u51fa\u4e00 \u53e3\u9c9c\u8840\u3002\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\uff0c\u4f60\u770b\u6211\u864e\u53e3\u62d4\u7259\u7684\u4e0b\u6bb5\u5982\u4f55\uff1f\u201d\u628a\u624b\u4e00\u626c\uff0c\u4e00\u80a1\u8165\u98ce\u5411\u53f2 \u767d\u90fd\u5288\u9762\u6253\u53bb\uff0c\u53f2\u767d\u90fd\u53ea\u9053\u662f\u4ec0\u4e48\u5582\u6bd2\u7684\u6697\u5668\uff0c\u4e0d\u6562\u624b\u63a5\uff0c\u5f53\u4e0b\u6325\u8896\u4e00\u5377\uff0c\u5377\u6765\u4e00\u770b\uff0c\u5374\u539f \u6765\u662f\u4e24\u53ea\u5e26\u8840\u7684\u95e8\u7259\uff01\u8fd9\u4e2a\u88ab\u6253\u843d\u95e8\u7259\u7684\u4eba\u662f\u6d77\u7802\u5e2e\u7684\u5e2e\u4e3b\u6c99\u5343\u5cf0\uff0c\u539f\u6765\u6c99\u5343\u5cf0\u770b\u89c1\u91d1\u9010\u6d41 \u5df2\u5728\u548c\u53f2\u767d\u90fd\u6076\u6597\uff0c\u91d1\u9010\u6d41\u4f3c\u662f\u5904\u5728\u4e0b\u98ce\uff0c\u4ed6\u60f3\u6361\u8fd9\u4e2a\u4fbf\u5b9c\uff0c\u4e0a\u6765\u5077\u88ad\u3002\u54ea\u77e5\u91d1\u9010\u6d41\u773c\u89c2\u516d \u8def\uff0c\u8033\u542c\u516b\u65b9\uff0c\u5374\u6545\u610f\u88c5\u4f5c\u4e0d\u77e5\uff0c\u53e3\u4e2d\u548c\u53f2\u767d\u90fd\u8bf4\u8bdd\uff0c\u5251\u638c\u4e5f\u90fd\u5728\u5411\u53f2\u767d\u90fd\u653b\u53bb\uff0c\u5f85\u5230\u6c99\u5343 \u5cf0\u6765\u81f3\u80cc\u540e\uff0c\u8fd9\u624d\u84e6\u5730\u5c06\u51fb\u5411\u53f2\u767d\u90fd\u7684\u90a3\u62db\u865a\u62db\u79fb\u524d\u4f5c\u540e\uff0c\u5316\u865a\u4e3a\u5b9e\uff0c\u79fb\u6765\u7ed9\u6c99\u5343\u5cf0\u201c\u53d7 \u7528\u201d\uff0c\u4e00\u62f3\u5c31\u6253\u843d\u4e86\u4ed6\u7684\u95e8\u7259\u3002\u6c99\u5343\u5cf0\u662f\u4e00\u5e2e\u4e4b\u4e3b\uff0c\u6b66\u529f\u751a\u662f\u4e0d\u5f31\uff0c\u5018\u82e5\u4e0e\u91d1\u9010\u6d41\u9762\u5bf9\u9762\u7684 \u8ba4\u771f\u8f83\u91cf\uff0c\u867d\u7136\u4ecd\u662f\u6253\u4e0d\u8fc7\u91d1\u9010\u6d41\uff0c\u4f46\u4e5f\u51b3\u4e0d\u81f3\u4e8e\u4e00\u4e2a\u7167\u9762\u5c31\u5403\u5927\u4e8f\u3002 \u3000\u3000\u6587\u9053\u5e84\u559d\u9053\uff1a\u201c\u91d1\u9010\u6d41\u4f11\u5f97\u901e\u80fd\uff01\u201d\u5982\u98de\u6251\u4e0a\u3002\u53f2\u7ea2\u82f1\u8f6f\u97ad\u4e00\u6325\uff0c\u5411\u6c99\u5343\u5cf0\u62e6\u8170\u75be\u5377 \u5f00\uff0c\u6c99\u5343\u5cf0\u811a\u6b65\u5c1a\u672a\u7ad9\u7a33\uff0c\u54ea\u91cc\u95ea\u907f\u5f97\u4e86\uff0c\u53ea\u89c9\u808b\u9aa8\u4e00\u9ebb\uff0c\u5df2\u7ed9\u53f2\u7ea2\u82f1\u8f6f\u97ad\u5377\u7740\uff0c\u5012\u63d0\u8d77 \u6765\u3002 \u3000\u3000\u6587\u9053\u5e84\u6b63\u5728\u8dd1\u6765\uff0c\u53f2\u7ea2\u82f1\u957f\u97ad\u4e00\u6296\u3001\u628a\u6c99\u5343\u5cf0\u5f53\u4f5c\u201c\u4eba\u7403\u201d\uff0c\u5411\u6587\u9053\u5e84\u629b\u53bb\u3002\u6587\u9053\u5e84\u5f53 \u7136\u4e0d\u80fd\u8ba9\u6c99\u5343\u5cf0\u53d7\u4f24\uff0c\u53ea\u597d\u53cc\u638c\u5e73\u4f38\uff0c\u4f7f\u51fa\u5378\u529b\u6d88\u52b2\u7684\u529f\u592b\uff0c\u4f63\u67d4\u548c\u7684\u529b\u9053\u628a\u6c99\u5343\u5cf0\u63a5\u4e0b \u6765\u3002\u6c99\u5343\u5cf0\u63a5\u8fde\u5403\u4e8f\uff0c\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\u3002 \u3000\u3000\u53f2\u767d\u90fd\u671d\u529f\u529b\u672c\u5728\u91d1\u9010\u6d41\u4e4b\u4e0a\uff0c\u4f46\u89c1\u91d1\u9010\u6d41\u5982\u6b64\u795e\u5999\u83ab\u6d4b\u7684\u624b\u6cd5\uff0c\u4e5f\u81ea\u4e0d\u7981\u6697\u6697\u5403\u60ca\u3002 \u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\uff0c\u6211\u8fd9\u864e\u53e3\u62d4\u7259\u7684\u624b\u6bb5\uff0c\u4f60\u8981\u4e0d\u8981\u4e5f\u5c1d\u4e00\u5c1d\u3002\u201d\u53f2\u767d\u90fd\u6012\u9053\uff1a\u201c\u5c82\u6709 \u6b64\u7406\uff0c\u4f60\u8fd9\u5c0f\u5b50\u4e5f\u6562\u6765\u6b3a\u6211\uff1f\u201d\u4f46\u4ed6\u867d\u7136\u5927\u8a00\u708e\u708e\uff0c\u5fc3\u91cc\u5374\u4e5f\u7740\u5b9e\u6709\u70b9\u5bb3\u6015\uff0c\u751f\u6015\u91d1\u9010\u6d41\u4f7f \u51fa\u4ec0\u4e48\u602a\u62db\uff0c\u5373\u4f7f\u4e0d\u80fd\u6253\u843d\u4ed6\u7684\u95e8\u7259\uff0c\u5403\u4e86\u4e8f\u4e5f\u4e0d\u662f\u5f53\u800d\u7684\u3002 \u3000\u3000\u6b64\u65f6\u91d1\u9010\u6d41\u5728\u5f62\u52bf\u4e0a\u662f\u4ee5\u5be1\u654c\u4f17\uff0c\u5341\u5206\u4e0d\u5229\u3002\u53f2\u767d\u90fd\u6599\u4ed6\u9003\u4e0d\u51fa\u53bb\uff0c\u4e8e\u662f\u6253\u5b9a\u4e86\u4e0d\u6c42\u80dc \u5148\u9632\u8d25\u7684\u4e3b\u610f\uff0c\u8d1d\u4ed6\u6251\u6765\uff0c\u672c\u80fd\u7684\u659c\u8eab\u4e00\u95ea\u3002\u4e0d\u6599\u91d1\u9010\u6d41\u53c8\u662f\u865a\u62db\uff0c\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u53ea\u542c \u5f97\u4ed6\u4e00\u58f0\u957f\u5578\uff0c\u8eab\u5f62\u62d4\u8d77\uff0c\u4fe8\u5982\u9e70\u96bc\u7a7f\u6797\uff0c\u63a0\u6ce2\u6d77\u71d5\uff0c\u500f\u5730\u63a0\u5230\u4e86\u5e05\u5b5f\u96c4\u7684\u8eab\u8fb9\uff01 \u3000\u3000\u53f2\u7ea2\u82f1\u53eb\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5c04\u4eba\u5148\u5c04\u9a6c\uff0c\u64d2\u8d3c\u5148\u64d2\u738b\uff01\u4ed6\u4e2d\u4e86\u6211\u7684\u6bd2\u9488\uff0c\u4e0d\u8981\u8ba9\u4ed6\u6709\u5598\u606f\u7684 \u673a\u4f1a\uff01\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u60f3\u4e0d\u5230\u91d1\u9010\u6d41\u6765\u5f97\u5982\u6b64\u4e4b\u5feb\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u91d1\u9010\u6d41\u53cd\u624b\u4e00\u62ff\uff0c\u5df2 \u662f\u6293\u7740\u4e86\u4ed6\u7684\u624b\u8155\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u4e5f\u662f\u597d\u751f\u4e86\u5f97\uff0c\u624b\u8155\u5df2\u7ed9\u654c\u4eba\u6293\u4f4f\uff0c\u5c45\u7136\u5e76\u4e0d\u614c\u4e71\uff0c\u767e\u5fd9\u4e2d\u4f7f\u51fa\u8d25\u4e2d\u6c42\u80dc\u7684\u4e0a\u4e58 \u6b66\u529f\uff0c\u4e00\u4e2a\u201c\u8131\u888d\u89e3\u7532\u201d\uff0c\u8eab\u8eaf\u4e00\u77ee\uff0c\u529b\u8d2f\u53cc\u81c2\uff0c\u624b\u8155\u4e00\u6c89\uff0c\u4ea4\u53c9\u9519\u6b65\uff0c\u501f\u7740\u8170\u90e8\u7684\u4e00\u8f6c\u4e4b \u529b\u7adf\u7136\u628a\u91d1\u9010\u6d41\u5f39\u5f00\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u53ef\u60dc\uff0c\u8fd9\u4e00\u62db\u82e5\u662f\u4ed6\u5251\u638c\u517c\u65bd\u7684\u8bdd\uff0c\u65e9\u5c31\u53ef\u4ee5\u5728\u5e05\u5b5f\u96c4\u8eab\u4e0a\u63b7\u4e2a\u900f \u660e\u7684\u7a9f\u9686\u3002\u53ea\u56e0\u4ed6\u60f3\u64d2\u4f4f\u5e05\u5b5f\u96c4\u4f5c\u4e3a\u4eba\u8d28\uff0c\u5374\u4e0d\u6599\u5e05\u76c2\u96c4\u867d\u7136\u4e2d\u4e86\u6bd2\u9488\uff0c\u4f9d\u7136\u8fd8\u80fd\u8fd0\u7528\u771f \u529b\u3002\u91d1\u9010\u6d41\u4e00\u638c\u4e4b\u529b\u5236\u4e0d\u4f4f\u4ed6\uff0c\u826f\u673a\u5df2\u662f\u9519\u8fc7\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u4e24\u6761\u4eba\u5f71\u5411\u91d1\u9010\u6d41\u6251\u6765\uff0c\u5de6\u9762\u7684\u662f\u9633\u6d69\uff0c\u53f3\u9762\u7684\u662f\u6587\u9053\u5e84\uff0c\u9633\u6d69\u7684\u201c\u4fee\u7f57\u9634 \u715e\u529f\u201d\u5df1\u7ec3\u5230\u4e86\u7b2c\u4e5d\u91cd\u5883\u754c\uff0c\u4eba\u672a\u5230\uff0c\u638c\u5148\u53d1\uff0c\u9976\u662f\u91d1\u9010\u6d41\u8eab\u6709\u62a4\u4f53\u795e\u529f\uff0c\u4e5f\u4e0d\u7531\u5f97\u673a\u4f36\u4f36 \u5730\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\u3002 \u3000\u3000\u6587\u9053\u5e84\u559d\u9053\uff1a\u201c\u54ea\u91cc\u8d70\uff01\u201d\u4f7f\u51fa\u4e86\u201c\u4e09\u8c61\u795e\u529f\u201d\uff0c\u62f3\u98ce\u547c\u547c\uff0c\u4e00\u62db\u201c\u6a2a\u8eab\u6253\u864e\u201d\u5411\u91d1\u9010 \u6d41\u80cc\u5fc3\u731b\u51fb\uff01 \u3000\u3000\u597d\u4e2a\u91d1\u9010\u6d41\uff0c\u5728\u80cc\u8170\u53d7\u653b\u4e4b\u4e0b\uff0c\u8eab\u5f62\u5e73\u5730\u62d4\u8d77\uff0c\u4f7f\u51fa\u4e86\u5353\u7edd\u7684\u8f7b\u529f\uff01\u6587\u9053\u5e84\u7684\u201c\u4e09\u8c61\u795e \u529f\u201d\u4e0e\u9633\u6d69\u7684\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\u5404\u6709\u5e72\u79cb\uff0c\u529f\u529b\u6089\u654c\uff0c\u62f3\u98ce\u4e0e\u638c\u98ce\u78b0\u649e\uff0c\u53d1\u51fa\u4e86\u90c1\u96f7\u822c\u7684\u58f0 \u54cd\uff0c\u53cc\u65b9\u90fd\u662f\u4e0d\u7531\u81ea\u5df1\u7684\u540e\u9000\u4e09\u6b65\u3002\u91d1\u9010\u6d41\u5374\u5df2\u662f\u6377\u5982\u98de\u9e1f\u822c\u7684\u4ece\u6587\u9053\u5e84\u7684\u5934\u9876\u98de\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7684\u5077\u88ad\u867d\u7136\u672a\u80fd\u6210\u529f\uff0c\u5374\u4e5f\u6536\u4e86\u4e86\u201c\u56f4\u9b4f\u6551\u8d75\u201d\u4e4b\u6548\u3002\u793c\u5802\u4e2d\u51e0\u4e2a\u9876\u513f\u5c16\u513f\u7684\u9ad8 \u624b\uff0c\u90fd\u5fd9\u7740\u6765\u6551\u62a4\u5e05\u5b5f\u96c4\uff0c\u4e00\u65f6\u95f4\u5374\u662f\u65e0\u6687\u53bb\u653b\u6253\u53f2\u7ea2\u82f1\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u4e0e\u53f2\u7ea2\u82f1\u4ea4\u624b\u7684\u53ea\u6709\u4e00\u4e2a\u53f2\u767d\u90fd\u3002\u53f2\u767d\u90fd\u7684\u672c\u9886\u867d\u662f\u8fdc\u8fdc\u5728\u4ed6\u59b9\u59b9\u4e4b\u4e0a\uff0c\u4f46\u5728\u91d1\u9010 \u6d41\u7a81\u88ad\u5e05\u5b5f\u96c4\u7684\u8fd9\u7247\u523b\u4e4b\u95f4\uff0c\u4ed6\u4e5f\u662f\u5fc3\u795e\u4e0d\u5b9a\uff0c\u4e0d\u77e5\u662f\u53bb\u8d74\u63f4\u7684\u597d\uff0c\u8fd8\u662f\u5148\u628a\u59b9\u59b9\u64d2\u4e0b\u7684 \u597d\uff1f\u53f2\u7ea2\u82f1\u6253\u4e0d\u8fc7\u54e5\u54e5\uff0c\u4f46\u62b5\u6321\u5341\u62db\u516b\u62db\u7684\u672c\u4e8b\u603b\u662f\u6709\u7684\uff0c\u53f2\u767d\u90fd\u7a0d\u4e00\u8e0c\u8e87\uff0c\u91d1\u9010\u6d41\u5df2\u662f\u95ea \u7535\u822c\u7684\u53c8\u56de\u6765\u4e86\uff01 \u3000\u3000\u5e05\u5b5f\u96c4\u6323\u8131\u4e86\u91d1\u9010\u6d41\u7684\u638c\u63e1\uff0c\u53ea\u89c9\u5168\u8eab\u53d1\u9ebb\uff0c\u663e\u7136\u662f\u6bd2\u6c14\u4e0a\u5347\u7684\u8ff9\u8c61\u3002\u5e05\u5b5f\u96c4\u5403\u4e86\u4e00 \u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5f97\u8d76\u5feb\u628a\u8d3a\u5927\u5a18\u627e\u6765\u624d\u597d\uff01\u201d\u4ed6\u8fd8\u672a\u77e5\uff0c\u8d3a\u5927\u5a18\u65e9\u5df2\u7ed9\u5389\u5357\u661f\u6740\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u4f17\u5bbe\u5ba2\u6b63\u5728\u7eb7\u7eb7\u5411\u5916\u9003\u8dd1\uff0c\u5e05\u5b5f\u96c4\u7ad9\u5728\u95e8\u53e3\u671b\u51fa\u53bb\uff0c\u770b\u4e0d\u89c1\u8d3a\u5927\u5a18\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a \u201c\u5feb\u5feb\u628a\u8d3a\u5927\u5a18\u627e\u56de\u6765\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u9053\u84dd\u8272\u7684\u706b\u7130\u98de\u4e0a\u5929\u7a7a\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u53ea\u89c1\u5357\u5317\u897f\u4e1c \u98de\u8d77\u4e86\u65e0\u6570\u6d41\u661f\u82b1\u70ae\uff0c\u6b64\u8d77\u5f7c\u843d\uff0c\u5728\u5929\u7a7a\u4e0a\u851a\u6210\u5947\u666f\uff0c\u5143\u5bb5\u4e4b\u591c\u7684\u70df\u82b1\uff0c\u4e5f\u65e0\u5982\u6b64\u70ed\u95f9\uff01 \u3000\u3000\u9662\u4e2d\u7684\u536b\u571f\u54d7\u7136\u5927\u547c\uff1a\u201c\u6709\u5978\u7ec6\uff0c\u6709\u201c\u5978\u7ec6\uff01\u201d\u547c\u558a\u58f0\u4e2d\uff0c\u5df2\u662f\u9690\u9690\u542c\u5f97\u6709\u8f70\u8f70\u9686\u9686\u7684 \u571f\u70ae\u653b\u57ce\u7684\u58f0\u54cd\u3002 \u3000\u3000\u57ce\u4e2d\u5404\u5904\u653e\u8d77\u7684\u6d41\u661f\u82b1\u70ae\uff0c\u663e\u7136\u662f\u63a5\u5e94\u7684\u4fe1\u53f7\u3002\u4e0d\u7528\u8bf4\u662f\u6709\u201c\u5978\u7ec6\u201d\u57cb\u4f0f\u57ce\u4e2d\u7684\u4e86\uff0c\u800c \u4e14\u4e3a\u6570\u8fd8\u4e0d\u5c11\u5462\uff01 \u3000\u3000\u539f\u6765\u6700\u521d\u5347\u8d77\u7684\u90a3\u9053\u84dd\u706b\u662f\u4f55\u5f69\u51e4\u5c04\u51fa\u7684\u201c\u86c7\u7130\u7bad\u201d\uff0c\u57ce\u91cc\u9690\u85cf\u7684\u4e49\u519b\u65b9\u9762\u7684\u4eba\uff0c\u4e00\u89c1 \u4e86\u8fd9\u679d\u86c7\u7130\u7bad\u5c31\u653e\u8d77\u6d41\u661f\u82b1\u70ae\uff0c\u8fd9\u662f\u4ed6\u4eec\u65e9\u5df2\u7ea6\u597d\u4e86\u7684\u201c\u91cc\u5e94\u5916\u5408\u201d\u7684\u4fe1\u53f7\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u4e0d\u6127\u6709\u5927\u5c06\u4e4b\u624d\uff0c\u867d\u60ca\u4e0d\u4e71\uff0c\u4e00\u9762\u53eb\u4eba\u4f20\u4ee4\u51fa\u53bb\uff0c\u547d\u4ee4\u5b88\u519b\u9547\u5b9a\u5bf9\u4ed8\uff0c\u4e00\u9762\u8c03\u6d3e \u597d\u624b\uff0c\u4e0a\u524d\u6349\u62ff\u5389\u5357\u661f\u7b49\u4eba\u3002\u53ef\u662f\u4ed6\u81ea\u5df1\u56e0\u4e3a\u4e2d\u4e86\u6bd2\u9488\uff0c\u5374\u4e0d\u80fd\u4eb2\u81ea\u53bb\u6307\u6325\u4e86\u3002\u6b64\u65f6\u4ed6\u56fa\u7136 \u62c5\u5fc3\u5916\u654c\uff0c\u4f46\u66f4\u7d27\u8981\u7684\u5219\u662f\u7ed9\u81ea\u5df1\u627e\u5bfb\u89e3\u836f\uff0c\u4e8e\u662f\u4e0d\u987e\u793c\u5802\u4e2d\u7684\u6253\u6597\uff0c\u5c31\u51fa\u53bb\u627e\u5bfb\u8d3a\u5927\u5a18\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5927\u559c\u9053\uff1a\u201c\u5389\u5927\u54e5\u5728\u5916\u9762\uff01\u201d\u8d81\u7740\u8fd9\u4e2a\u6df7\u4e71\u65f6\u673a\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u5c31\u6740\u51fa\u53bb\u3002 \u3000\u3000\u53f2\u767d\u90fd\u62b5\u654c\u4e0d\u4f4f\u4ed6\u4e0e\u53f2\u7ea2\u82f1\u7684\u8054\u624b\u6025\u653b\uff0c\u53ea\u597d\u8ba9\u5f00\u6761\u8def\u3002 \u3000\u3000\u793c\u5802\u4e2d\u597d\u624b\u5982\u4e91\uff0c\u4f46\u771f\u6b63\u4e00\u7b49\u7684\u9ad8\u624b\u4e5f\u4e0d\u8fc7\u5be5\u5be5\u6570\u4eba\uff0c\u9633\u6d69\u4e0e\u6587\u9053\u5e84\u8bef\u62fc\u4e86\u4e00\u638c\uff0c\u6b64\u65f6 \u6b63\u5728\u5fd9\u4e8e\u8c03\u5300\u6c14\u606f\uff0c\u4ee5\u514d\u53d7\u4e86\u5185\u4f24\uff0c\u6c99\u5343\u5cf0\u63a5\u8fde\u5403\u4e86\u4e24\u6b21\u5927\u4e8f\uff0c\u4f24\u5f97\u867d\u7136\u4e0d\u91cd\uff0c\u4ea6\u5df2\u662f\u60ca\u5f13 \u4e4b\u9e1f\uff0c\u4e00\u65f6\u95f4\u7adf\u662f\u4e0d\u6562\u5411\u524d\u3002\u5176\u4ed6\u8bf8\u4eba\uff0c\u5982\u8463\u5341\u4e09\u5a18\u3001\u9752\u5cf0\u9053\u4eba\u3001\u5706\u6d77\u548c\u5c1a\u3001\u9633\u6d69\u7684\u5f1f\u5b50\u9f9a \u5e73\u91ce\u3001\u6587\u9053\u5e84\u7684\u513f\u5b50\u6587\u80dc\u4e2d\u7b49\u7b49\uff0c\u6b66\u529f\u867d\u7136\u5404\u6709\u6240\u957f\uff0c\u5374\u90fd\u53ea\u80fd\u7b97\u662f\u4e00\u6d41\u4e4b\u95f4\u7684\u811a\u8272\uff0c\u8fd9\u4e9b \u4eba\u4e5f\u90fd\u662f\u7ed9\u91d1\u9010\u6d41\u6740\u6015\u4e86\u7684\uff0c\u89c1\u91d1\u9010\u6d41\u4f3c\u75af\u864e\u822c\u5730\u51b2\u51fa\u6765\uff0c\u4eba\u4eba\u90fd\u662f\u6709\u70b9\u5bb3\u6015\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u58f0\u5927\u559d\uff0c\u633a\u5251\u5411\u5b88\u5728\u95e8\u53e3\u7684\u5706\u6d77\u523a\u53bb\uff0c\u5706\u6d77\u786c\u7740\u5934\u76ae\uff0c\u6a2a\u5200\u4e00\u6321\uff0c\u201c\u5580\u5693\u201d\u4e00 \u58f0\uff0c\u6212\u5200\u6298\u65ad\uff0c\u5706\u6d77\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u8fde\u5fd9\u7a9c\u8fc7\u4e00\u65c1\uff0c\u4e52\u4e53\u4e24\u58f0\u649e\u5012\u4e86\u4e24\u540d\u536b\u58eb\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u91d1\u9010\u6d41\u4e00\u5251\u8d76\u8dd1\u4e86\u5706\u6d77\uff0c\u7b2c\u4e8c\u5251\u5df2\u662f\u5411\u7740\u8463\u5341\u4e09\u5a18\u523a\u53bb\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a \u201c\u81ed\u5a46\u5a18\uff0c\u6628\u65e5\u9976\u4e86\u4f60\u7684\u547d\uff0c\u4f60\u5c55\u7136\u8fd8\u6562\u56de\u6765\u4e0e\u6211\u4f5c\u5bf9\uff0c\u8981\u4e0d\u8981\u6211\u518d\u5265\u6389\u4f60\u7684\u8863\u88f3\uff1f\u201d \u3000\u3000\u4ee5\u8463\u5341\u738b\u5a18\u7684\u672c\u9886\u672c\u6765\u53ef\u4ee5\u62b5\u6321\u91d1\u9010\u6d41\u7684\u5341\u62db\u7684\uff0c\u4f46\u6b64\u9645\u5fc3\u4e2d\u53c8\u662f\u7f9e\u6127\u53c8\u662f\u5bb3\u6015\uff0c\u6c14\u9981 \u4e86\u8fde\u6700\u5f97\u610f\u7684\u795e\u97ad\u7edd\u6280\u4f7f\u51fa\u6765\u90fd\u662f\u4e0d\u6210\u7ae0\u6cd5\uff0c\u91d1\u9010\u6d41\u4e00\u5251\u62e8\u5f00\u5979\u7684\u957f\u97ad\uff0c\u8ddf\u7740\u5c31\u662f\u4e00\u62db\u201c\u5251 \u4e2d\u5939\u638c\u201d\uff01 \u3000\u3000\u8463\u5341\u4e09\u5a18\u970d\u7684\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u8eb2\u5f97\u867d\u5feb\uff0c\u8fd8\u662f\u95ea\u907f\u4e0d\u5f00\uff0c\u53ea\u542c\u5f97\u201c\u556a\u201d\u7684\u4e00\u58f0\uff0c\u5df2\u662f \u7ed9\u91d1\u9010\u6d41\u7528\u91cd\u624b\u6cd5\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u6253\u4e86\u4e00\u8bb0\u8033\u5149\uff0c\u8138\u4e0a\u767b\u65f6\u5f00\u4e86\u82b1\uff01 \u3000\u3000\u53f2\u7ea2\u82f1\u957f\u97ad\u6325\u821e\uff0c\u6587\u80dc\u4e2d\u4e0d\u77e5\u5979\u7684\u5389\u5bb3\uff0c\u4e0a\u524d\u62e6\u622a\uff0c\u672a\u80fd\u8fd1\u8eab\uff0c\u5df2\u662f\u7740\u4e86\u4e00\u97ad\uff0c\u6253\u5f97\u4ed6 \u54c7\u54c7\u5927\u53eb\uff0c\u9752\u7b26\u9053\u4eba\u6325\u5251\u88ad\u51fb\uff0c\u53f2\u7ea2\u82f1\u51b7\u7b11\u9053\uff1a\u201c\u6811\u5012\u7322\u72f2\u6563\uff0c\u6211\u770b\u4f60\u8fd8\u662f\u56de\u53bb\u771f\u6b63\u5f53\u9053\u58eb \u7684\u597d\uff01\u201d\u9752\u7b26\u9053\u4eba\u5728\u516d\u5408\u5e2e\u4e2d\u6076\u8ff9\u4e0d\u591a\uff0c\u548c\u53f2\u7ea2\u82f1\u4e5f\u6ca1\u6709\u4ec0\u4e48\u6069\u6028\uff0c\u89c1\u6b64\u60c5\u666f\uff0c\u5fc3\u91cc\u4e00\u51c9\uff0c \u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u5584\u8a00\u76f8\u529d\u3002\u201d\u679c\u7136\u5c31\u8ba9\u5f00\u4e86\u8def\u3002 \u3000\u3000\u4ee5\u9752\u7b26\u9053\u4eba\u7684\u5251\u6cd5\u800c\u8bba\uff0c\u53f2\u7ea2\u82f1\u672a\u5fc5\u53ef\u4ee5\u80dc\u4ed6\uff0c\u4e0d\u6599\u4ed6\u4e0d\u6218\u800c\u9000\uff0c\u8fd9\u5c31\u7ed9\u53f2\u7ea2\u82f1\u8f7b\u8f7b\u6613 \u6613\u5730\u95ef\u4e86\u51fa\u53bb\u3002\u53f2\u767d\u90fd\u5927\u6012\uff0c\u201c\u54fc\u201d\u7684\u4e00\u58f0\u98de\u8eab\u63a0\u51fa\uff0c\u628a\u9752\u7b26\u9053\u4eba\u8e22\u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u8ddf\u7740\u5c31\u5411 \u53f2\u7ea2\u82f1\u7684\u540e\u5fc3\u6293\u4e0b\u3002\u559d\u9053\uff1a\u201c\u5f85\u6211\u6293\u4e86\u8fd9\u4e2a\u4e2b\u5934\uff0c\u518d\u548c\u4f60\u8fd9\u725b\u9f3b\u5b50\u7b97\u5e10\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u53cd\u624b\u4e00\u638c\uff0c\u66ff\u53f2\u7ea2\u82f1\u63a5\u4e86\u4e00\u62db\u3002\u9752\u7b26\u9053\u4eba\u722c\u4e86\u8d77\u6765\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u662f\u5916\u4eba\u4e5f\u4e0d \u5fcd\u4f24\u5bb3\u53f2\u59d1\u5a18\uff0c\u4f60\u662f\u54e5\u54e5\uff0c\u5982\u8981\u62ff\u59b9\u59b9\u5df4\u7ed3\u6743\u8d35\uff0c\u6211\u770b\u4e0d\u8fc7\u773c\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u5404\u8d70\u5404\u7684\u8def\u5427\u3002 \u6211\u5728\u4e09\u6e05\u89c2\u7b49\u4f60\uff0c\u4f46\u53ea\u6015\u4f60\u6ca1\u627e\u6211\u7b97\u5e10\u7684\u673a\u4f1a\u4e86\uff01\u201d \u3000\u3000\u9752\u7b26\u9053\u4eba\u4e4b\u6240\u4ee5\u6562\u4e8e\u6bc5\u7136\u53cd\u53db\u53f2\u767d\u90fd\uff0c\u56fa\u7136\u662f\u7531\u4e8e\u7ed9\u4ed6\u8e22\u4e86\u4e00\u811a\uff0c\u6c14\u6124\u96be\u5f53\u6240\u81f3\uff1b\u4f46\u53e6 \u4e00\u4e2a\u66f4\u91cd\u8981\u7684\u539f\u56e0\u5219\u662f\u4ed6\u770b\u6e05\u4e86\u5927\u52bf\u7684\u786e\u4e0d\u597d\uff0c\u6b64\u65f6\u57ce\u5916\u5343\u519b\u4e07\u9a6c\u53ae\u6740\u7684\u58f0\u97f3\u90fd\u53ef\u4ee5\u542c\u5f97\u89c1 \u4e86\uff0c\u770b\u6765\u4e49\u519b\u7834\u57ce\u5df2\u662f\u6307\u987e\u95f4\u4e8b\uff0c\u4ed6\u751f\u6015\u4e49\u519b\u8fdb\u4e86\u57ce\uff0c\u628a\u4ed6\u7b97\u505a\u53f2\u767d\u90fd\u7684\u6b7b\u515a\uff0c\u662f\u4ee5\u4e0d\u60dc\u548c \u53f2\u767d\u90fd\u53cd\u8138\uff0c\u548c\u5411\u53f2\u7ea2\u82f1\u8ba8\u597d\u3002 \u3000\u3000\u6b64\u65f6\u91cc\u91cc\u5916\u5916\u90fd\u5728\u6df7\u6218\uff0c\u5c06\u519b\u5e9c\u7684\u536b\u58eb\u5fd9\u4e8e\u5bf9\u4ed8\u654c\u4eba\uff0c\u8c01\u4e5f\u65e0\u6687\u53bb\u7406\u4f1a\u80cc\u53db\u516d\u5408\u5e2e\u7684\u9752 \u7b26\u9053\u4eba\uff0c\u4e0d\u8fc7\u9752\u7b26\u9053\u4eba\u4e5f\u4e0d\u6562\u5411\u53f2\u767d\u90fd\u53cd\u6208\u4e00\u51fb\uff0c\u4ed6\u6084\u6084\u5730\u6e9c\u8d70\u4e86\uff01 \u3000\u3000\u53f2\u767d\u90fd\u7ed9\u91d1\u9010\u6d41\u51fb\u4e86\u4e00\u638c\uff0c\u5927\u6012\u8bf4\u9053\uff1a\u201c\u5373\u4f7f\u897f\u660c\u7ed9\u4f60\u4eec\u7684\u4eba\u653b\u7834\uff0c\u57ce\u7834\u4e4b\u524d\uff0c\u6211\u4e5f\u8981 \u53d6\u4f60\u8fd9\u5c0f\u5b50\u7684\u6027\u547d\uff01\u201d\u6587\u9053\u5e84\u63a5\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u4e0d\u80fd\u653e\u8fc7\u4ed6\u4eec\uff01\u4e2d\u513f\uff0c\u968f\u6211 \u6765\u62a5\u8fd9\u4e00\u97ad\u4e4b\u4ec7\u5427\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u529f\u529b\u6df1\u539a\uff0c\u6b64\u65f6\u5df2\u662f\u8c03\u5300\u6c14\u606f\uff0c\u8d74\u4e86\u51fa\u6765\uff0c\u53cc\u638c\u76d8\u65cb\u98de\u821e\uff0c\u5de6\u653b\u91d1\u9010\u6d41\uff0c\u53f3\u653b\u53f2 \u7ea2\u82f1\u3002\u4ed6\u6068\u53f2\u7ea2\u82f1\u6253\u4e86\u4ed6\u7684\u513f\u5b50\u4e00\u97ad\uff0c\u5341\u6210\u653b\u52bf\u4e2d\u5012\u662f\u6709\u4e03\u6210\u5411\u7740\u53f2\u7ea2\u82f1\u7684\u3002\u53f2\u767d\u90fd\u5fc3\u91cc\u60f3 \u9053\uff1a\u201c\u4f60\u77e5\u9053\u91d1\u9010\u6d41\u7684\u5389\u5bb3\uff0c\u5374\u8ba9\u6211\u6765\u5bf9\u4ed8\u4ed6\u3002\u201d\u4e0d\u8fc7\u4ed6\u6bd5\u7adf\u4e5f\u8fd8\u662f\u6709\u70b9\u4e0d\u5927\u613f\u610f\u5411\u59b9\u59b9\u75db \u4e0b\u6bd2\u624b\uff0c\u5b81\u53ef\u8ba9\u522b\u4eba\u5bf9\u4ed8\u5979\u3002\u662f\u4ee5\u4e5f\u5e76\u4e0d\u62b1\u6028\u6587\u9053\u5e84\uff0c\u5f53\u4e0b\u6325\u5251\u8fd0\u638c\uff0c\u63a5\u4e0b\u4e86\u91d1\u9010\u6d41\u7684\u516b\u6210 \u653b\u52bf\u3002 \u3000\u3000\u8ddf\u7740\u9633\u6d69\u4ea6\u5df1\u8c03\u5300\u6c14\u606f\uff0c\u8fdb\u4e86\u51fa\u6765\u3002\u91d1\u3001\u53f2\u4e8c\u4eba\u5728\u4e09\u5927\u9ad8\u624b\u56f4\u653b\u4e4b\u4e0b\uff0c\u518d\u60f3\u5411\u5916\u95ef\u51fa\u53bb \u5df2\u662f\u4e0d\u80fd\u4e86\u3002\u6b63\u662f\uff1a \u3000\u3000\u77f3\u7834\u5929\u60ca\u6765\u523a\u5ba2\uff0c\u5200\u5149\u5251\u5f71\u95f9\u534e\u5802\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e03\u56de\u3000\u5999\u821e\u6e05\u6b4c\u817e\u6740\u6c14\u3000\u795e\u62f3\u5b9d\u5251\u62fc\u5b58\u4ea1 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e09\u5341\u4e03\u56de\u3000\u5999\u821e\u6e05\u6b4c\u817e\u6740\u6c14\u3000\u795e\u62f3\u5b9d\u5251\u62fc\u5b58\u4ea1 \u3000\u3000\u6b64\u65f6\u5389\u5357\u661f\u6b63\u5728\u9010\u6b65\u5411\u516c\u5b59\u71d5\u79fb\u8fd1\uff0c\u5c1a\u672a\u4f1a\u5408\u3002\u516c\u5b59\u71d5\u7684\u5bf9\u624b\u662f\u9ad8\u5927\u6210\u3001\u675c\u5927\u4e1a\u4e8c\u4eba\uff0c \u8fd9\u4e24\u4eba\u90fd\u662f\u4e00\u5e2e\u4e4b\u4e3b\u7684\u8eab\u4efd\uff0c\u6b66\u529f\u5f88\u662f\u4e0d\u5f31\u3002\u9ad8\u5927\u6210\u7684\u72fc\u7259\u68d2\u68b0\u91cd\u529b\u6c89\uff0c\u62db\u6570\u7eaf\u719f\uff1b\u675c\u5927\u4e1a \u7684\u4e00\u5bf9\u62a4\u624b\u94a9\u8f7b\u7075\u7fd4\u52a8\uff0c\u4e13\u514b\u5200\u5251\u3002\u53cc\u94a9\u4e00\u68d2\u3002\u201d\u914d\u5408\u5f97\u5f88\u597d\u3002\u516c\u5b59\u71d5\u4f7f\u51fa\u6d51\u8eab\u89e3\u6570\uff0c\u5140\u662f \u4e0d\u80fd\u7a81\u7834\u4ed6\u4eec\u7684\u94a9\u68d2\u8054\u624b\u3002\u5979\u7684\u5251\u672f\u867d\u7136\u7cbe\u5999\uff0c\u5374\u5403\u4e8f\u5728\u6c14\u529b\u8f83\u5c0f\uff0c\u65f6\u95f4\u4e00\u957f\uff0c\u4e0d\u89c9\u9999\u6c57\u6dcb \u6f13\uff0c\u6e10\u6e10\u6709\u70b9\u652f\u6301\u4e0d\u4f4f\u3002 \u3000\u3000\u5389\u5357\u661f\u8fd9\u8fb9\uff0c\u672c\u6765\u662f\u548c\u674e\u6566\u592b\u5987\u5408\u6218\u5b89\u4fca\u5ead\u7684\uff0c\u6b64\u65f6\u5df2\u7ecf\u6765\u4e86\u53e6\u5916\u56db\u540d\u5c06\u519b\u5e9c\u7684\u6b66\u58eb\uff0c \u6210\u4e3a\u4ee5\u4e09\u5bf9\u4e94\u7684\u5c40\u9762\uff0c\u8fd9\u56db\u540d\u6b66\u58eb\u4e0d\u8fc7\u662f\u4e8c\u6d41\u811a\u8272\uff0c\u4f46\u4ed6\u4eec\u5374\u7ec3\u6709\u4e00\u5957\u56db\u4eba\u5408\u4f7f\u7684\u68cd\u6cd5\uff0c\u56db \u4eba\u5982\u540c\u4e00\u4f53\u3002\u653b\u5b88\u8c28\u4e25\uff0c\u8db3\u53ef\u4ee5\u5f53\u5f97\u4e00\u4e2a\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\u3002\u4ed6\u4eec\u6240\u4f7f\u7684\u9f50\u7709\u68cd\uff0c\u90fd\u662f\u91cd\u5175\u5668\uff0c \u5389\u5357\u661f\u7684\u7384\u94c1\u5ba4\u5251\u53ef\u4ee5\u635f\u4f24\u4ed6\u4eec\u7684\u5175\u5668\uff0c\u4f46\u5374\u4e0d\u80fd\u5728\u4e00\u62db\u4e4b\u95f4\uff0c\u5c06\u5b83\u524a\u65ad\u3002\u5b89\u4fca\u5ead\u7684\u6b66\u529f\u6781 \u9ad8\uff0c\u5f97\u4e86\u8fd9\u56db\u4e2a\u6b66\u58eb\u7684\u534f\u52a9\uff0c\u4ed6\u4ece\u4e2d\u7b56\u5e94\uff0c\u7167\u987e\u56db\u65b9\uff0c\u767b\u65f6\u4e5f\u6273\u56de\u52a3\u52bf\uff0c\u6210\u4e86\u76f8\u5f85\u7684\u5c40\u9762\u3002 \u3000\u3000\u5389\u5357\u661f\u542c\u5f97\u91d1\u9010\u6d41\u5728\u53eb\u201c\u5389\u5927\u54e5\u201d\uff0c\u62ac\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u91d1\u9010\u6d41\u4e0e\u53f2\u7ea2\u82f1\u5728\u4e09\u5927\u9ad8\u624b\u56f4\u653b\u4e4b \u4e0b\uff0c\u6fc0\u6597\u65b9\u9163\uff0c\u770b\u60c5\u5f62\u4f3c\u4e4e\u4ed6\u4eec\u4e5f\u662f\u81ea\u987e\u4e0d\u6687\u3002\u8fd9\u5239\u90a3\u95f4\u5389\u5357\u661f\u53c8\u60ca\u53c8\u559c\uff0c\u53c8\u662f\u5fae\u611f\u8f9b\u9178\u3002 \u7a0d\u4e00\u5206\u795e\uff0c\u5b89\u4fca\u5ead\u89c5\u5f97\u4ed6\u7684\u7834\u7efd\uff0c\u970d\u7684\u4e00\u97ad\u6253\u6765\uff0c\u5728\u5389\u5357\u661f\u7684\u80a9\u5934\u62bd\u51fa\u4e86\u4e00\u6761\u6de1\u6de1\u7684\u8840\u75d5\uff01 \u3000\u3000\u4f46\u8fd9\u4e00\u97ad\u4e5f\u628a\u5389\u5357\u661f\u6253\u5f97\u9192\u4e86\u8fc7\u6765\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e3a\u62a5\u77e5\u5df1\uff0c\u867d\u6b7b\u4f55\u8f9e\u3002\u201d\u601d\u5ff5\u53ca\u6b64\uff0c \u767b\u65f6\u8840\u8109\u8d32\u5f20\uff0c\u4e5f\u4e0d\u77e5\u54ea\u91cc\u6765\u7684\u795e\u529b\uff0c\u547c\u7684\u4e00\u5251\uff0c\u5c31\u628a\u4e00\u6839\u4e09\u5341\u591a\u65a4\u91cd\u7684\u9f50\u7709\u6367\u5288\u4e3a\u4e24\u6bb5\uff0c \u5251\u950b\u4e00\u5e26\uff0c\u53c8\u5c06\u5b89\u4fca\u5ead\u7684\u4e03\u8282\u97ad\u524a\u4e86\u4e00\u8282\u3002\u6b64\u65f6\u5b89\u4fca\u5ead\u7684\u4e03\u8282\u97ad\u5df2\u7ecf\u662f\u53ea\u5269\u4e0b\u4e09\u8282\u4e86\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u536b\u58eb\u662f\u540c\u8fdb\u540c\u9000\uff0c\u5b9b\u5982\u4e00\u4f53\u7684\uff0c\u4e00\u6839\u9f50\u7709\u68cd\u7ed9\u65a9\u65ad\u4e4b\u540e\uff0c\u9635\u6cd5\u7acb\u5373\u7834\u4e86\u3002\u5389\u5357\u661f \u4ece\u7f3a\u53e3\u51b2\u51fa\uff0c\u4e00\u4e2a\u8d77\u4f0f\uff0c\u5df2\u662f\u5982\u63a0\u6ce2\u5de8\u9e1f\u822c\u5730\u51b2\u5230\u4e86\u516c\u5b59\u71d5\u8fd9\u8fb9\u3002 \u3000\u3000\u9ad8\u5927\u6210\u542c\u5f97\u80cc\u540e\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u56de\u8eab\u62db\u67b6\u3002\u5e94\u62db\u867d\u5feb\uff0c\u8fd8\u662f\u6162\u4e86\u534a\u6b65\uff0c \u72fc\u7259\u68d2\u521a\u521a\u4e3e\u8d77\uff0c\u5389\u5357\u661f\u5df2\u662f\u4e00\u5251\u5288\u4e0b\u6765\uff01\u9ad8\u5927\u6210\u54ea\u6321\u5f97\u4f4f\u7384\u94c1\u5b9d\u5251\u7684\u5a01\u529b\uff0c\u201c\u5f53\u201d\u7684\u4e00 \u58f0\uff0c\u72fc\u7259\u68d2\u5f53\u4e2d\u5256\u5f00\uff0c\u9ad8\u5927\u6210\u864e\u53e3\u6d41\u8840\uff0c\u8dcc\u7ffb\u51fa\u4e00\u4e08\u5f00\u5916\u3002 \u3000\u3000\u675c\u5927\u4e1a\u8fde\u5fd9\u9003\u8dd1\uff0c\u516c\u5b59\u71d5\u5251\u62db\u4f55\u7b49\u8fc5\u6377\uff0c\u201c\u535c\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff01\u5251\u5c16\u5df2\u523a\u8fdb\u4e86\u4ed6\u819d\u76d6\u7684\u73af \u8df3\u7a74\uff0c\u675c\u5927\u4e1a\u53cc\u817f\u4e00\u8f6f\uff0c\u8dea\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u71d5\u59b9\uff0c\u4f60\u5feb\u53bb\u63a5\u5e94\u674e\u6566\u592b\u59bb\uff01\u201d\u5306\u5306\u8bf4\u4e86\u8fd9\u53e5\u8bdd\uff0c\u4fbf\u5373\u5411\u91d1\u9010\u6d41\u4e0e\u53f2\u7ea2\u82f1 \u90a3\u8fb9\u5954\u53bb\u3002 \u3000\u3000\u516c\u5b59\u71d5\u672c\u6765\u662f\u8ddf\u968f\u5389\u5357\u661f\u7684\uff0c\u53ef\u662f\u56de\u5934\u4e00\u770b\uff0c\u5374\u89c1\u674e\u6566\u592b\u59bb\u6b63\u5728\u9677\u4e8e\u9669\u8c61\u73af\u751f\u7684\u82e6\u6218\u4e4b \u4e2d\uff01 \u3000\u3000\u516c\u5b59\u71d5\u4e0e\u4f55\u5f69\u51e4\u60c5\u540c\u59d0\u59b9\uff0c\u89c1\u6b64\u60c5\u666f\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u597d\u8fde\u5fd9\u56de\u53bb\uff0c\u6551\u63f4\u5979\u4e0e\u674e\u6566\u3002 \u3000\u3000\u5389\u5357\u661f\u63a0\u8fc7\u4e24\u5ea7\u5047\u5c71\uff0c\u6070\u597d\u78b0\u4e0a\u4e86\u51fa\u6765\u5bfb\u89c5\u8d3a\u5927\u5a18\u7684\u5e05\u5b5f\u96c4\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u4ec7\u4eba\u89c1\u9762\uff0c\u5206\u5916\u773c\u7ea2\uff0c\u5e05\u5b5f\u96c4\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u8fd9\u5c0f\u5b50\u4e5f\u6765\u9001\u6b7b\uff01\u201d\u62a2\u4e0a\u5047\u5c71\uff0c \u5c45\u9ad8\u4e34\u4e0b\uff0c\u8981\u628a\u5389\u5357\u661f\u6253\u4e0b\u53bb\u3002 \u3000\u3000\u5389\u5357\u661f\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u59d3\u8d3a\u7684\u8001\u5996\u5987\u65e9\u5df2\u6b7b\u4e86\uff0c\u4f60\u5230\u9ec4\u6cc9\u8def\u4e0a\u4f1a\u5979\u53bb\u5427\uff01\u201d\u731b\u6325\u7384\u94c1\u5b9d \u5251\uff0c\u7acb\u5373\u62a2\u653b\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e14\u628a\u8fd9\u5c0f\u5b50\u6253\u53d1\u4e86\u518d\u8bf4\u3002\u201d\u4ed6\u4ee5\u524d\u548c\u5389\u5357\u661f\u66fe\u7ecf\u4ea4 \u8fc7\u4e00\u6b21\u624b\uff0c\u90a3\u6b21\u5389\u5357\u661f\u7ed9\u4ed6\u4e00\u638c\u6253\u5f97\u91cd\u4f24\uff0c\u662f\u4ee5\u4ed6\u867d\u7136\u77e5\u9053\u5389\u5357\u661f\u624b\u4e2d\u62ff\u7684\u662f\u7384\u94c1\u5b9d\u5251\uff0c\u5374 \u4e5f\u50b2\u7136\u4e0d\u60e7\u3002 \u3000\u3000\u4e0d\u6599\u5200\u5251\u76f8\u4ea4\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u5e05\u5b5f\u96c4\u624b\u4e2d\u7684\u4e00\u628a\u957f\u5200\u5df2\u662f\u6298\u4e3a\u4e24\u6bb5\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u81ea\u4ee5\u4e3a\u529f\u529b\u80dc\u8fc7\u5bf9\u65b9\u4e0d\u6b62\u4e00\u7b79\uff0c\u5374\u4e0d\u77e5\u90a3\u4e00\u6b21\u4ed6\u4e4b\u6240\u4ee5\u5f97\u80dc\u662f\u56e0\u4e3a\u5389\u5357\u661f\u53d7\u4f24\u5728 \u5148\uff0c\u8fd9\u4e00\u6b21\u60c5\u5f62\u6070\u6070\u76f8\u53cd\uff0c\u662f\u4ed6\u5148\u4e2d\u4e86\u6bd2\u9488\uff0c\u6b64\u6d88\u5f7c\u957f\uff0c\u5982\u4f55\u8fd8\u80fd\u6321\u5f97\u4f4f\u5389\u5357\u661f\u7384\u94c1\u5b9d\u5251\u7684 \u4e00\u5288\uff1f \u3000\u3000\u5e05\u5b5f\u96c4\u5927\u60ca\u4e4b\u4e0b\uff0c\u4eba\u6025\u667a\u751f\uff0c\u6043\u7740\u5360\u4e86\u5c45\u9ad8\u4e34\u4e0b\u7684\u5730\u5229\uff0c\u811a\u5c16\u4e00\u6311\uff0c\u628a\u5047\u5c71\u4e0a\u7684\u4e00\u5757\u77f3 \u5934\u6311\u52a8\uff0c\u5389\u5357\u661f\u6b63\u5728\u4ef0\u653b\uff0c\u8fd9\u5757\u77f3\u5934\u7167\u9762\u5c31\u6253\u4e0b\u6765\u3002 \u3000\u3000\u5389\u5357\u661f\u628a\u5251\u4e00\u6325\uff0c\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u706b\u661f\u7bf7\u98de\uff0c\u8fd9\u5757\u5927\u77f3\u5934\u53c8\u7ed9\u4ed6\u7684\u5b9d\u5251\u5288\u4e3a\u4e24\u534a\u3002\u5e05\u5b5f \u96c4\u8fde\u73af\u8d77\u811a\uff0c\u7b2c\u4e8c\u5757\u3001\u7b2c\u4e09\u5757\u77f3\u5934\uff0c\u2026\u2026\u63a5\u7eed\u800c\u6765\uff01 \u3000\u3000\u5389\u5357\u661f\u4e00\u53e3\u6c14\u8fde\u5288\u4e94\u5757\u77f3\u5934\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5df2\u662f\u62a2\u4e0a\u4e86\u8fd9\u5ea7\u5047\u5c71\u3002\u5e05\u5b5f\u96c4\u4fa7\u8eab\u4e00 \u95ea\uff0c\u53cd\u624b\u64d2\u62ff\u4ed6\u7684\u5de6\u8155\u3002\u5389\u5357\u661f\u4e00\u5251\u5288\u7a7a\uff0c\u5288\u5728\u5047\u5c71\u77f3\u4e0a\uff0c\u58f0\u5982\u5de8\u96f7\u3002 \u3000\u3000\u5e05\u76c2\u96c4\u7684\u5927\u64d2\u62ff\u624b\u6cd5\u6781\u4e3a\u4e86\u5f97\uff0c\u5389\u5357\u661f\u4e00\u5251\u5288\u7a7a\uff0c\u4ed6\u5df2\u7ecf\u62ff\u7740\u4e86\u5389\u5357\u661f\u7684\u624b\u8155\u3002\u53ef\u662f\u5389 \u5357\u661f\u8fd9\u4e00\u5288\u4e4b\u529b\u5a01\u731b\u65e0\u4f26\uff0c\u5e05\u5b5f\u96c4\u7ed9\u4ed6\u7684\u5251\u98ce\u4e00\u8361\uff0c\u811a\u6b65\u5df2\u662f\u7ad9\u7acb\u4e0d\u7a33\uff0c\u62d6\u7740\u5389\u5357\u661f\u5c31\u6eda\u4e0b \u6765\u3002\u5389\u5357\u661f\u65e0\u6687\u8f6c\u8eab\u6325\u5251\uff0c\u5927\u559d\u4e86\u58f0\u201c\u53bb\uff01\u201d\u4e00\u4e2a\u5fc3\u7a9d\u817f\u8e22\u51fa\uff0c\u767b\u65f6\u628a\u5e05\u5b5f\u96c4\u8e22\u4e0b\u4e86\u5047\u5c71\u3002 \u3000\u3000\u8fd9\u51e0\u4e0b\u5154\u8d77\u9e58\u843d\uff0c\u8fc5\u75be\u5f02\u5e38\u3002\u5f53\u5e05\u5b5f\u96c4\u4e0e\u5389\u5357\u661f\u4ea4\u624b\u4e4b\u65f6\uff0c\u5e9c\u4e2d\u597d\u624b\u5df2\u662f\u7eb7\u7eb7\u8d76\u6765\uff0c\u53ef \u662f\u4ecd\u662f\u89e3\u6551\u4e0d\u4e86\u4ed6\u8fd9\u4e00\u817f\u4e4b\u707e\u3002 \u3000\u3000\u4f46\u662f\u5389\u5357\u661f\u867d\u7136\u8e22\u4f24\u4e86\u5e05\u5b5f\u96c4\uff0c\u62a5\u590d\u4e86\u4e00\u638c\u4e4b\u4ec7\uff0c\u4ed6\u8981\u95ef\u8fc7\u53bb\u4e0e\u91d1\u9010\u6d41\u4f1a\u5408\uff0c\u5374\u4e5f\u662f\u4e0d \u80fd\u591f\u4e86\u3002 \u3000\u3000\u53ea\u89c1\u8dd1\u6765\u6551\u62a4\u5e05\u5b5f\u96c4\u7684\u4e00\u7fa4\u4eba\u4e2d\uff0c\u6709\u6d77\u7802\u5e2e\u7684\u5e2e\u4e3b\u6c99\u5343\u5cf0\uff0c\u6709\u9633\u6d69\u7684\u7b2c\u5b50\u9f9a\u5e73\u91ce\uff0c\u6709\u5706 \u6d77\u4e0e\u8463\u5341\u4e09\u5a18\u3002\u5389\u5357\u57a6\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\u8df3\u8d77\u6765\u65f6\uff0c\u8fd9\u4e9b\u4eba\u90fd\u5df2\u5230\u4e86\u5047\u5c71\u4e0b\u9762\uff0c\u8f6c\u773c\u95f4\u53f2\u767d \u90fd\u4e5f\u98de\u4e00\u822c\u5730\u8d76\u5230\u4e86\u3002 \u3000\u3000\u5389\u5357\u661f\u5012\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5355\u53f2\u767d\u90fd\u4e00\u4eba\uff0c\u6211\u6709\u7384\u94c1\u5b9d\u5251\u5728\u624b\uff0c\u53ea\u6015\u8fd8\u662f\u6253 \u4ed6\u4e0d\u8fc7\uff0c\u4f55\u51b5\u8fd8\u6709\u6c99\u5343\u5cf0\u7b49\u8bb8\u591a\u597d\u624b\uff1f\u7f62\u4e86\uff0c\u7f62\u4e86\uff0c\u62fc\u5c06\u4e00\u6b7b\u916c\u77e5\u5df1\uff0c\u4f46\u6c42\u9010\u6d41\u8d24\u5f1f\u4e0e\u53f2\u59d1 \u5a18\u80fd\u591f\u8131\u9669\uff0c\u6211\u4eca\u65e5\u867d\u6b7b\uff0c\u6b7b\u4ea6\u7791\u76ee\uff01\u201d\u53f2\u767d\u90fd\u540e\u53d1\u5148\u81f3\uff0c\u6b64\u65f6\u5df2\u7ecf\u62a2\u4e0a\u5047\u5c71\u3002 \u3000\u3000\u5389\u5357\u661f\u601d\u5ff5\u53ca\u6b64\uff0c\u5fc3\u610f\u7acb\u51b3\uff0c\u5927\u53eb\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u63a5\u5251\uff01\u201d\u7384\u94c1\u5b9d\u5251\u5316\u4f5c\u4e86\u4e00\u9053\u94f6\u8679\uff0c\u9690 \u9690\u631f\u7740\u98ce\u9704\u4e4b\u58f0\uff0c\u7b14\u76f4\u7684\u5411\u91d1\u9010\u6d41\u98de\u53bb\uff01 \u3000\u3000\u8fd9\u6837\u6c89\u91cd\u7684\u7384\u94c1\u5b9d\u5251\uff0c\u4ece\u9ad8\u5904\u98de\u4e0b\u6765\uff0c\u5a01\u52bf\u4f55\u7b49\u60ca\u4eba\uff1f\u53f2\u767d\u90fd\u6df1\u77e5\u7384\u94c1\u5b9d\u5251\u7684\u5389\u5bb3\uff0c\u529f \u5200\u867d\u9ad8\uff0c\u4e5f\u4e0d\u6562\u62a2\u63a5\u3002\u4e00\u4fa7\u8eab\uff0c\u7384\u94c1\u5b9d\u5251\u4ece\u4ed6\u5934\u9876\u98de\u8fc7\u3002 \u3000\u3000\u6b64\u65f6\u53ea\u5269\u4e0b\u6587\u9053\u5e84\u4e0e\u9633\u6d69\u548c\u91d1\u3001\u53f2\u4ea4\u624b\uff0c\u91d1\u9010\u6d41\u7684\u672c\u9886\u7565\u9ad8\u4ed6\u4eec\u4e00\u7b79\uff0c\u53f2\u7ea2\u82f1\u5219\u662f\u4e0d\u53ca \u4ed6\u4eec\u3002\u4ee5\u4e00\u654c\u4e8c\uff0c\u4e00\u65f6\u95f4\u96be\u5206\u9ad8\u4e0b\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7384\u94c1\u5b9d\u5251\u5df1\u662f\u6276\u7740\u98ce\u96f7\u4e4b\u58f0\u7b14\u76f4\u98de\u6765\uff0c\u6587\u9053\u5e84\u4e5f\u662f\u77e5\u9053\u7384\u94c1\u5b9d\u5251\u7684\u5389 \u5bb3\u7684\uff0c\u53f2\u767d\u90fd\u90fd\u4e0d\u6562\u63a5\uff0c\u4ed6\u5982\u4f55\u6562\u63a5\uff1f\u5f53\u4e0b\u4ed6\u548c\u9633\u6d69\u4e0d\u7ea6\u800c\u540c\u7684\u5de6\u53f3\u5206\u5f00\uff0c\u7384\u94c1\u5b9d\u5251\u98de\u5230\u4e86 \u91d1\u9010\u6d41\u7684\u9762\u524d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8eab\u5f62\u5fae\u4fa7\uff0c\u4f38\u638c\u4e00\u62cd\uff0c\u62cd\u7740\u5251\u67c4\uff0c\u7384\u94c1\u5b9d\u5251\u8f6c\u4e86\u4e2a\u65b9\u5411\u53bb\u52bf\u7acb\u7f13\uff0c\u91d1\u9010\u6d41\u5de6\u624b\u4e00 \u6284\uff0c\u5df2\u662f\u7d27\u7d27\u5730\u63e1\u7262\u4e86\u5251\u67c4\u3002\u539f\u6765\u5389\u5357\u661f\u66fe\u5f97\u91d1\u9010\u6d41\u7684\u7236\u4eb2\u4f20\u6388\u5251\u6cd5\uff0c\u8fd9\u4e00\u62db\u63b7\u5251\u7684\u624b\u6cd5\u6b63 \u662f\u201c\u5927\u987b\u5f25\u5251\u5f0f\u201d\u7684\u4e00\u62db\uff0c\u91d1\u9010\u6d41\u5bf9\u8fd9\u4e00\u62db\u63b7\u5251\u63a5\u5251\u7684\u624b\u6cd5\u6bd4\u5389\u5357\u661f\u8fd8\u66f4\u7eaf\u719f\uff0c\u662f\u4ee5\u5389\u5357\u661f \u6562\u4e8e\u63b7\u5251\u7ed9\u4ed6\uff0c\u800c\u91d1\u9010\u6d41\u4e5f\u679c\u7136\u4e0d\u8d1f\u6240\u671b\uff0c\u8f7b\u63cf\u6de1\u5199\u7684\u5c31\u628a\u7384\u94c1\u5b9d\u5251\u63a5\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53ef\u662f\u91d1\u9010\u6d41\u63a5\u4e86\u5b9d\u5251\uff0c\u5374\u662f\u4e0d\u7531\u5f97\u5fc3\u5934\u4e00\u51db\uff0c\u6beb\u4e0d\u6b22\u559c\uff0c\u53cd\u800c\u614c\u4e86\u8d77\u6765\uff0c\u60f3\u9053\uff1a\u201c\u5389\u5927 \u54e5\u5f03\u4e86\u5b9d\u5251\uff0c\u5982\u4f55\u5bf9\u4ed8\u53f2\u767d\u90fd\uff1f\u201d \u3000\u3000\u8fd9\u67c4\u7384\u94c1\u5b9d\u5251\u91cd\u8fbe\u4e00\u767e\u591a\u65a4\uff0c\u4ece\u5047\u5c71\u4e0a\u63b7\u4e0b\u6765\u5bb9\u6613\uff0c\u629b\u4e0a\u53bb\u5f53\u7136\u96be\u5f97\u591a\u3002\u91d1\u9010\u6d41\u6b63\u8981\u4e0d \u987e\u4e00\u5207\uff0c\u629b\u56de\u53bb\u7ed9\u5389\u5357\u661f\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u5389\u5357\u661f\u5927\u543c\u4e00\u58f0\uff0c\u539f\u6765\u4ed6\u5df2\u7ed9\u53f2\u767d\u90fd\u4e00\u638c\u51fb\u4e2a \u6b63\u7740\uff0c\u9aa8\u788c\u788c\u7684\u4ece\u5047\u5c71\u7684\u53e6\u4e00\u9762\u6eda\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5389\u5357\u661f\u5760\u5730\u4e4b\u5904\u4e43\u662f\u5728\u4e24\u5ea7\u5047\u5c71\u4e4b\u95f4\uff0c\u91d1\u9010\u6d41\u548c\u516c\u5b59\u71d5\u5404\u5728\u4e00\u8fb9\uff0c\u5389\u5357\u661f\u5760\u5730\u4e4b\u540e\uff0c\u60c5 \u5f62\u5982\u4f55\uff0c\u4ed6\u4eec\u90fd\u770b\u4e0d\u89c1\uff01\u4f46\u53ea\u89c1\u5230\u53f2\u767d\u90fd\u5df2\u7ecf\u4ece\u5047\u5c71\u4e0a\u8df3\u4e0b\u53bb\uff0c\u4e0d\u7528\u8bf4\u662f\u53bb\u6349\u62ff\u5389\u5357\u661f\u7684 \u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u75db\u5982\u5272\uff0c\u7259\u6839\u4e00\u54ac\uff0c\u60f3\u9053\uff1a\u201c\u6211\u51b3\u4e0d\u80fd\u8ba9\u5389\u5927\u54e5\u4e3a\u6211\u9001\u547d\uff01\u201d\u5f53\u4e0b\u8fd0\u5251\u5982\u98ce\uff0c \u9739\u96f3\u4e00\u58f0\u5927\u559d\uff1a\u201c\u6321\u6211\u8005\u6b7b\uff0c\u8ba9\u6211\u8005\u751f\uff01\u201d\u53f2\u7ea2\u82f1\u8ddf\u5728\u4ed6\u7684\u540e\u9762\uff0c\u4fbf\u5373\u5411\u524d\u786c\u95ef\uff0c \u3000\u3000\u9633\u6d69\u4f7f\u51fa\u4e86\u7b2c\u4e5d\u91cd\u7684\u4fee\u7f57\u9634\u715e\u529f\uff0c\u8fd8\u60f3\u963b\u6b62\u4ed6\u4eec\uff0c\u53ef\u662f\u4fee\u7f57\u9634\u715e\u529f\u7684\u5389\u5bb3\u4e4b\u5904\u4e0d\u5728\u4e8e\u638c \u529b\uff0c\u800c\u662f\u5728\u4e8e\u90a3\u80a1\u9634\u5bd2\u4e4b\u6c14\uff0c\u91d1\u9010\u6d41\u4e0d\u60e7\u4fee\u7f57\u9634\u715e\u529f\uff0c\u6325\u5251\u786c\u5288\uff0c\u9633\u6d69\u7684\u638c\u529b\u7109\u80fd\u6321\u5f97\u4f4f\u8fd9 \u6837\u6c89\u91cd\u7684\u5b9d\u5251\uff1f \u3000\u3000\u9633\u6d69\u773c\u770b\u62b5\u6321\u4e0d\u4f4f\uff0c\u84e6\u5730\u4e00\u58f0\u5927\u543c\uff0c\u6293\u8d77\u4e86\u4e00\u4e2a\u536b\u58eb\u5f53\u4f5c\u76fe\u724c\uff0c\u91d1\u9010\u6d41\u4e00\u5251\u5288\u4e0b\uff0c\u53ea\u89c1 \u8840\u5149\u8ff8\u73b0\uff0c\u8fd9\u4e2a\u536b\u58eb\u5f53\u573a\u4e86\u7ed3\u3002\u4f46\u9633\u6d69\u81ea\u5df1\u5374\u62fe\u56de\u4e86\u6027\u547d\uff0c\u6254\u4e0b\u4e86\u536b\u58eb\u7684\u5c38\u4f53\u8dd1\u4e86\u3002\u5468\u56f4\u7684 \u536b\u58eb\u770b\u89c1\u9633\u6d69\u5982\u6b64\u6b8b\u5fcd\uff0c\u751f\u6015\u65e0\u8f9c\u966a\u4e27\uff0c\u54ea\u4e2a\u8fd8\u6562\u5411\u524d\uff1f \u3000\u3000\u6587\u9053\u5e84\u81ea\u6043\u4e09\u8c61\u795e\u529f\u5df2\u7ecf\u7ec3\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u4f17\u536b\u58eb\u6563\u5f00\uff0c\u4ed6\u4e00\u4eba\u72ec\u4e0a\uff0c\u201c\u54fc\u201d\u4e86\u4e00 \u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5012\u8981\u8bd5\u8bd5\u4f60\u8fd9\u7384\u94c1\u5b9d\u5251\u7684\u5a01\u529b\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u559d\u9053\uff1a\u201c\u597d\uff0c\u90a3\u5c31\u6765\u5427\uff01\u201d\u4e00\u62db\u201c\u6a2a\u4e91\u65ad\u5cf0\u201d\uff0c\u7384\u94c1\u5b9d\u5251\u631f\u7740\u98ce\u96f7\u4e4b\u58f0\u62e6\u8170\u622a \u65a9\u3002\u201d\u6587\u9053\u5e84\u4eae\u51fa\u4e00\u53e3\u7ec6\u957f\u7684\u5175\u5203\uff0c\u8fd8\u4e86\u4e00\u62db\u201c\u5927\u6f20\u5b64\u70df\u201d\uff0c\u5176\u76f4\u5982\u77e2\u7684\u5411\u91d1\u9010\u6d41\u7684\u91cd\u5251\u8f7b \u8f7b\u70b9\u4e0b\u3002 \u3000\u3000\u8fd9\u53e3\u5175\u5203\u5f62\u5f0f\u53e4\u602a\uff0c\u4f3c\u5200\u975e\u5200\uff0c\u4f3c\u5251\u975e\u5251\uff0c\u53ea\u6709\u4e8c\u6307\u4e4b\u5bbd\uff0c\u5374\u6709\u4e94\u5c3a\u591a\u957f\u3002\u539f\u6765\u662f\u6587\u9053 \u5e84\u7528\u5341\u51e0\u628a\u7f05\u5200\u6253\u6210\u7684\u4e00\u628a\u8f6f\u5251\u3002\u7f05\u5200\u672c\u8eab\u5df2\u662f\u767e\u70bc\u7cbe\u94a2\uff0c\u805a\u5341\u51e0\u628a\u7f05\u5200\u7684\u7cbe\u534e\u94f8\u70bc\u800c\u6210\u7684 \u8fd9\u67c4\u8f6f\u5251\uff0c\u5f53\u771f\u662f\u5230\u4e86\u201c\u767e\u70bc\u94a2\u5316\u4f5c\u7ed5\u6307\u67d4\u201d\u7684\u5730\u6b65\u3002 \u3000\u3000\u539f\u6765\u6587\u9053\u5e84\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u6df1\u77e5\u4ee5\u7384\u94c1\u5b9d\u5251\u7684\u5a01\u529b\uff0c\u4e16\u95f4\u4efb\u4f55\u5175\u5203\u90fd\u4e0d\u80fd\u4e0e\u5b83\u786c \u78b0\uff0c\u53ea\u6709\u4ece\u201c\u4ee5\u67d4\u514b\u521a\u201d\u65b9\u9762\u7740\u60f3\uff0c\u4ed6\u94f8\u6210\u7684\u8fd9\u628a\u8f6f\u5251\uff0c\u5c31\u6b63\u662f\u8981\u7528\u6765\u5bf9\u4ed8\u7384\u94c1\u5b9d\u5251\u7684\u3002\u8f6f \u5251\u6709\u4e2a\u597d\u5904\uff0c\u5f39\u529b\u6781\u5f3a\uff0c\u4e0e\u786c\u7269\u76f8\u78b0\uff0c\u51b3\u4e0d\u4f1a\u4e00\u78b0\u5373\u65ad\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u8f6f\u5251\u7684\u5251\u5c16\u8f7b\u8f7b\u70b9\u5728\u7384\u94c1\u5b9d\u5251\u7684\u5251\u80cc\u4e0a\u3002\u8f6f\u5251\u767b\u65f6\u5f2f\u66f2\u5982\u5f13\uff0c\u4f46 \u7384\u94c1\u5b9d\u5251\u7684\u90a3\u80a1\u5927\u529b\uff0c\u5374\u4e5f\u7ed9\u4ed6\u5378\u53bb\u4e86\u51e0\u5206\uff0c\u8fd9\u4e00\u62db\u6587\u9053\u5e84\u867d\u7136\u662f\u7565\u5904\u4e0b\u98ce\uff0c\u4f46\u603b\u7b97\u662f\u62db\u67b6 \u5f97\u4f4f\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9690\u9690\u611f\u5230\u4ed6\u7684\u53cd\u51fb\u4e4b\u529b\uff0c\u4e5f\u7981\u4e0d\u4f4f\u5fc3\u5934\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u4e3e\u91cd\u82e5\u8f7b\u8fd8\u6613\uff0c\u4e3e\u8f7b\u82e5\u91cd \u66f4\u96be\uff0c\u8fd9\u53ae\u80fd\u591f\u7528\u4e00\u70b3\u8f6f\u5251\u53d1\u6325\u51fa\u5a01\u529b\u6781\u5927\u7684\u4e09\u8c61\u795e\u529f\uff0c\u59d4\u5b9e\u662f\u4e0d\u53ef\u5c0f\u89d1\uff01\u4f46\u6211\u82e5\u6740\u4ed6\u4e0d \u9000\uff0c\u7109\u80fd\u6551\u5f97\u5389\u5927\u54e5\u8131\u9669\uff1f\u7ffb\u4e86\u6027\u547d\uff0c\u4e5f\u8981\u4e0e\u4ed6\u62fc\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e00\u58f0\u5927\u559d\uff0c\u7384\u94c1\u5b9d\u5251\u75be\u5288\u51fa\u53bb\u3002\u60c5\u6025\u4e4b\u4e0b\uff0c\u4e5f\u4e0d\u77e5\u662f\u54ea\u91cc\u6765\u7684\u795e\u529b\uff0c\u767e\u591a\u65a4\u91cd\u7684 \u91cd\u5251\u62ff\u5728\u4ed6\u7684\u624b\u91cc\uff0c\u7adf\u4f3c\u8f7b\u82e5\u67f3\u679d\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u6a2a\u5288\u767e\u523a\uff0c\u5f53\u771f\u662f\u7fe9\u52a0\u98de\u51e4\uff0c\u77eb\u82e5\u6e38\u9f99\uff0c\u8f6c \u773c\u4e4b\u95f4\uff0c\u5df2\u662f\u5288\u523a\u65ab\u4ea4\u4e92\u8fd0\u7528\uff0c\u4f7f\u51fa\u4e86\u4e03\u62db\u8fdb\u624b\u7684\u62db\u5f0f\uff01 \u3000\u3000\u53ee\u53ee\u4e4b\u58f0\uff0c\u5b9b\u5982\u7435\u7436\u9ad8\u624b\u7684\u8f6e\u6307\u75be\u5f39\uff0c\u9707\u5f97\u6587\u9053\u5e84\u7684\u5251\u5c16\u55e1\u55e1\u4f5c\u54cd\uff0c\u98a4\u52a8\u4e0d\u4f11\uff01\u6587\u9053\u5e84 \u672c\u6765\u81ea\u6043\u529f\u529b\u8981\u6bd4\u91d1\u9010\u6d41\u7565\u9ad8\u7684\uff0c\u54ea\u77e5\u4e03\u62db\u8fc7\u540e\uff0c\u7adf\u662f\u864e\u53e3\u53d1\u70ed\uff0c\u8f6f\u5251\u90fd\u51e0\u4e4e\u638c\u63e1\u4e0d\u7262\u3002\u6bcf \u4e00\u6b21\u78b0\u5728\u4e4b\u540e\uff0c\u8f6f\u5251\u5c31\u591a\u5f2f\u66f2\u4e00\u5206\u3002\u6587\u9053\u5e84\u5927\u5403\u4e00\u60ca\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u82e5\u518d\u786c\u62fc\u4e0b\u53bb\uff0c\u53ea\u6015\u5f53 \u771f\u8981\u5251\u6298\u4eba\u4ea1\uff01\u201d\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u7a9c\u8fc7\u4e00\u8fb9\uff0c\u8ba9\u5f00\u6761\u8def\uff0c\u653e\u91d1\u9010\u6d41\u8fc7\u53bb\u3002 \u3000\u3000\u6b64\u65f6\u53f2\u7ea2\u82f1\u4ea6\u5df2\u6740\u9000\u4e86\u6587\u80dc\u4e2d\u7b49\u4eba\uff0c\u8ffd\u4e0a\u4e86\u91d1\u9010\u6d41\u3002\u91d1\u9010\u6d41\u542c\u5f97\u5979\u7684\u547c\u5438\u58f0\u606f\u4f3c\u4e4e\u6709\u70b9 \u5f02\u6837\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u53f2\u7ea2\u82f1\u7259\u5173\u6253\u6218\uff0c\u683c\u683c\u4f5c\u54cd\uff0c\u5e94\u9053\uff1a\u201c\u6ca1\uff0c\u6ca1\u4ec0 \u4e48\u3002\u201d\u539f\u6765\u5979\u662f\u53d7\u4e86\u9633\u6d69\u6240\u53d1\u7684\u4fee\u7f57\u9634\u715e\u529f\u7684\u5bd2\u6c14\u6240\u4fb5\uff0c\u8ddf\u7740\u53c8\u662f\u4e00\u573a\u6fc0\u6218\uff0c\u5979\u7684\u5185\u529f\u9020\u8be3 \u8fdc\u8fdc\u4e0d\u53ca\u91d1\u9010\u6d41\uff0c\u81ea\u7136\u7981\u53d7\u4e0d\u8d77\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u770b\u5979\u7684\u9762\u8272\uff0c\u5df2\u77e5\u7f18\u6545\uff0c\u5f53\u4e0b\u7d27\u7d27\u63e1\u770b\u53f2\u7ea2\u82f1\u7684\u5de6\u624b\uff0c\u4e00\u80a1\u5185\u529b\u900f\u8fc7\u5979\u7684\u638c \u5fc3\uff0c\u52a9\u5979\u9a71\u9664\u5bd2\u6c14\u3002\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u522b\u4e3a\u6211\u803d\u8bef\u4e86\uff0c\u5feb\u53bb\u6551\u5389\u5927\u54e5\u8981\u7d27\uff01\u201d\u91d1\u9010\u6d41\u9053\uff1a \u201c\u662f\uff01\u201d\u62c9\u7740\u53f2\u7ea2\u82f1\u4fbf\u8dd1\uff0c\u4e24\u4eba\u8f7b\u529f\u4e0d\u76f8\u4f2f\u4ef2\uff0c\u53f2\u7ea2\u82f1\u5f97\u4e86\u91d1\u9010\u6d41\u5185\u529b\u76f8\u52a9\uff0c\u8dd1\u8d77\u6765\u4e0d\u900a\u4e8e \u7cbe\u529b\u5145\u6c9b\u4e4b\u65f6\uff0c\u4e24\u4eba\u540c\u4f7f\u201c\u6bd4\u7ffc\u53cc\u98de\u201d\u7684\u8eab\u6cd5\uff0c\u8f6c\u773c\u95f4\u4fbf\u5230\u8fbe\u90a3\u5ea7\u5047\u5c71\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u3001\u5706\u6d77\u3001\u6c99\u5343\u5cf0\u3001\u9f9a\u5e73\u91ce\u7b49\u4eba\u4e00\u89c1\u91d1\u9010\u6d41\u6765\u5230\uff0c\u90fd\u7740\u4e86\u614c\uff0c\u8fde\u5fd9\u56db\u4e0b\u6563\u5f00\uff0c\u4e0d \u6562\u63a5\u6218\u3002\u91d1\u9010\u6d41\u7ffb\u8fc7\u5047\u5c71\uff0c\u53eb\u9053\uff1a\u201c\u5389\u5927\u54e5\uff0c\u5389\u5927\u54e5\uff01\u201d \u3000\u3000\u53ea\u89c1\u53f2\u767d\u90fd\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5389\u5357\u661f\u65e9\u5df2\u7ed9\u6211\u6740\u6389\u5566\uff0c\u4f60\u5230\u9ec4\u6cc9\u8def\u4e0a\u4f1a\u4ed6\u53bb\u5427\uff01\u201d\u91d1\u9010\u6d41\u6e38 \u76ee\u56db\u987e\uff0c\u5728\u8fd9\u4e24\u5ea7\u5047\u5c71\u4e4b\u95f4\u7684\u5e73\u5730\u4e0a\uff0c\u5f71\u5f71\u7ef0\u7ef0\u7684\u6709\u5341\u591a\u4e2a\u4eba\uff0c\u5e76\u65e0\u5389\u5357\u661f\u5728\u5185\uff0c\u5730\u4e0a\u6a2a\u4e03 \u575a\u516b\u7684\u6709\u51e0\u5177\u5c38\u4f53\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u662f\u5389\u5357\u661f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u5389\u5927\u54e5\u60f3\u5fc5\u662f\u7ffb\u8fc7\u90a3\u4e00\u9635\u5047\u5c71\u548c\u674e\u6566\u4ed6\u4eec\u4f1a\u5408\u4e86\u3002\u201d\u4f46\u8fd9\u53ea\u662f\u4ece\u597d\u5904\u7740 \u800c\u5df2\uff0c\u82e5\u4ece\u574f\u5904\u7740\u60f3\uff0c\u5389\u6d77\u661f\u5931\u4e86\u7384\u94c1\u5b9d\u5251\uff0c\u51b3\u8ba1\u6253\u4e0d\u8fc7\u53f2\u767d\u90fd\uff0c\u5728\u8fd9\u8bb8\u591a\u5f3a\u654c\u56f4\u653b\u4e4b\u4e0b\uff0c \u4ed6\u53c8\u7109\u80fd\u9003\u8131\uff1f \u3000\u3000\u91d1\u9010\u6d41\u731c\u7591\u4e0d\u5b9a\uff0c\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f60\u6562\u8bc5\u5492\u6211\u7684\u5389\u5927\u54e5\uff0c\u5403\u6211\u4e00\u5251\uff01\u201d\u5f53\u4e0b\u4e5f\u4e0d\u7406 \u4f1a\u53f2\u767d\u90fd\u8bf4\u7684\u662f\u771f\u662f\u5047\uff0c\u7acb\u5373\u4fbf\u6325\u52a8\u7384\u94c1\u5b9d\u5251\uff0c\u75db\u4e0b\u6740\u624b\uff01 \u3000\u3000\u53f2\u767d\u90fd\u628a\u957f\u5251\u63b7\u51fa\uff0c\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u91d1\u9010\u6d41\uff0c\u4f60\u5077\u4e86\u6211\u7684\u7384\u94c1\u5b9d\u5251\uff0c\u4f60\u4ee5\u4e3a\u6211\u5c31\u5948\u4f55\u4e0d\u4e86 \u4f60\u5417\uff1f\u4f11\u8bf4\u4e00\u5251\uff0c\u5341\u5251\u767e\u5251\uff0c\u53ea\u6709\u4f55\u59a8\uff1f\u201d \u3000\u3000\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u53f2\u767d\u90fd\u63b7\u6765\u7684\u957f\u5251\uff0c\u5df2\u7ed9\u91d1\u9010\u6d41\u524a\u4e3a\u4e24\u6bb5\uff0c\u4f46\u53f2\u767d\u90fd\u529f\u529b\u5374\u5728\u91d1\u9010\u6d41\u4e4b \u4e0a\uff0c\u8fd9\u4e00\u63b7\u4e4b\u5a01\u4ea6\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u91d1\u9010\u6d41\u7adf\u7136\u7ed9\u4ed6\u9707\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u8fde\u5fd9\u8d76\u6765\u7ed9\u4ed6\u63a0\u9635\uff0c\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u53bb\u627e\u5389\u5927\u54e5\u5427\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53f2\u767d\u90fd \u5df2\u662f\u4ece\u516d\u5408\u5e2e\u4e00\u4e2a\u5934\u76ee\u7684\u624b\u4e2d\uff0c\u63a5\u8fc7\u4e86\u4e00\u4e2a\u72ec\u811a\u94dc\u4eba\uff0c\u559d\u9053\uff1a\u201c\u81ed\u4e2b\u5934\uff0c\u8eb2\u5f00\uff01\u6211\u4e0d\u60f3\u4eb2\u624b \u6740\u4f60\uff01\u201d \u3000\u3000\u8fd9\u4e2a\u72ec\u811a\u94dc\u4eba\u867d\u7136\u4e0d\u53ca\u7384\u94c1\u5b9d\u5251\u7684\u6c89\u91cd\uff0c\u4e5f\u6709\u4e94\u5341\u6765\u65a4\u3002\u91d1\u9010\u6d41\u4e00\u5251\u523a\u53bb\uff0c\u53ea\u542c\u5f97 \u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u706b\u661f\u98de\u6e85\uff0c\u94dc\u4eba\u8eab\u4e0a\u7ed9\u524a\u53bb\u4e86\u4e00\u5c0f\u7247\uff0c\u91d1\u9010\u6d41\u864e\u53e3\u4e5f\u662f\u5fae\u611f\u9178\u9ebb\u3002\u53f2\u767d\u90fd\u51c6 \u5907\u4e86\u8fd9\u4e2a\u72ec\u811a\u94dc\u4eba\uff0c\u6b63\u662f\u8981\u7528\u6765\u5bf9\u4ed8\u7384\u94c1\u5b9d\u5251\u7684\u3002 \u3000\u3000\u8f6c\u773c\u4e4b\u95f4\uff0e\u91d1\u9010\u6d41\u4f7f\u51fa\u4e86\u5341\u4e03\u516b\u5251\uff0c\u53f2\u767d\u90fd\u6325\u821e\u94a2\u4eba\uff0c\u4e00\u4e00\u62b5\u4f4f\u3002\u53ea\u542c\u5f97\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0 \u9707\u8033\u6b32\u804b\uff0c\u94dc\u4eba\u8eab\u4e0a\u4f24\u75d5\u6591\u9a73\uff0c\u4f46\u4e5f\u6bd5\u7adf\u662f\u628a\u7384\u94c1\u91cd\u5251\u7684\u5a01\u529b\u6291\u5236\u4e86\u3002 \u3000\u3000\u6c99\u5343\u5cf0\u521a\u624d\u88ab\u53f2\u7ea2\u82f1\u6253\u4e86\u4e00\u97ad\uff0c\u5fc3\u4e2d\u542b\u6068\uff0c\u89c1\u91d1\u9010\u6d41\u5df2\u7ecf\u548c\u53f2\u767d\u90fd\u4ea4\u4e0a\u4e86\u624b\uff0c\u5f53\u4e0b\u4fbf\u653e \u5fc3\u4e0a\u6765\u6349\u62ff\u53f2\u7ea2\u82f1\u3002\u9634\u607b\u607b\u5730\u51b7\u53eb\u9053\uff1a\u201c\u4e56\u4f84\u5973\uff0c\u6709\u672c\u9886\u4f60\u5c31\u518d\u6253\u6211\u4e00\u97ad\u5427\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u7684\u672c\u9886\u4e0d\u5728\u6c99\u5343\u5cf0\u4e4b\u4e0b\uff0c\u4f46\u5374\u5403\u4e8f\u5728\u4e45\u6218\u4e4b\u540e\uff0c\u6c14\u529b\u4e0d\u52a0\u3002\u5341\u6570\u62db\u4e00\u8fc7\uff0c\u9999\u6c57\u6dcb \u6f13\uff0c\u7f57\u886b\u5c3d\u6e7f\u3002\u6c99\u5343\u5cf0\u53cc\u638c\u76d8\u65cb\u98de\u821e\uff0c\u8feb\u5f97\u5979\u6b65\u6b65\u540e\u3002 \u3000\u3000\u9f9a\u5e73\u91ce\u89c1\u6709\u673a\u53ef\u4e58\uff0c\u9f13\u8d77\u52c7\u6c14\uff0c\u4e5f\u6765\u52a0\u5165\u6218\u56e2\uff0c\u4ece\u65c1\u53d1\u638c\uff1b\u534f\u52a9\u6c99\u5343\u5cf0\uff0c\u5939\u653b\u53f2\u7ea2\u82f1\u3002 \u3000\u3000\u9f9a\u5e73\u91ce\u7684\u4fee\u7f57\u9634\u715e\u529f\u4e0d\u53ca\u4e43\u5e08\u6df1\u539a\uff0c\u4f46\u4ea6\u5df2\u7ec3\u5230\u4e86\u7b2c\u4e03\u91cd\u3002\u638c\u98ce\u53d1\u51fa\uff0c\u5bd2\u98ce\u4fb5\u808c\u3002\u53f2\u7ea2 \u82f1\u5728\u6fc0\u6218\u4e2d\u6b63\u81ea\u6d51\u8eab\u53d1\u70ed\uff0c\u7a81\u7136\u7ed9\u51b7\u6c14\u4e00\u51b2\uff0c\u4e0d\u7531\u5f97\u7259\u5173\u6253\u6218\uff0c\u82b1\u5bb9\u5931\u8272\uff0c\u66f4\u611f\u652f\u6301\u4e0d\u751f\u3002 \u3000\u3000\u6c99\u5343\u5cf0\u559d\u9053\uff1a\u201c\u64a4\u97ad\uff01\u201d\u5de6\u638c\u4e00\u62e8\uff0c\u53f3\u624b\u5df2\u662f\u4f7f\u51fa\u201c\u864e\u722a\u624b\u201d\u7684\u529f\u592b\uff0c\u6293\u4f4f\u4e86\u53f2\u7ea2\u82f1\u7684 \u97ad\u68a2\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u94f6\u7259\u4e00\u54ac\uff0c\u529b\u8d2f\u97ad\u68a2\uff0c\u575a\u4e0d\u653e\u624b\u3002\u5979\u7684\u97ad\u6cd5\u59d4\u5b9e\u662f\u5201\u94bb\u65e0\u6bd4\uff0c\u867d\u7136\u843d\u5165\u4e86\u5bf9\u65b9\u7684 \u638c\u63e1\uff0c\u4f9d\u7136\u80fd\u591f\u53cd\u51fb\u654c\u4eba\u3002\u6c99\u5343\u5cf0\u53ea\u89c9\u6307\u7f1d\u95f4\u597d\u50cf\u6709\u4e00\u6761\u5c0f\u86c7\u8981\u94bb\u51fa\u53bb\uff0c\u54ac\u5f97\u4ed6\u624b\u6307\u9690\u9690\u4f5c \u75db\u3002 \u3000\u3000\u6c99\u5343\u5cf0\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u8fd9\u4e2a\u81ed\u4e2b\u5934\u8fd8\u8981\u901e\u5f3a\u3002\u201d\u4e00\u4e2a\u6c89\u80a9\u5750\u9a6c\uff0c\u4f7f\u51fa\u4e86\u5343\u65a4\u5760 \u7684\u529f\u592b\uff0c\u7528\u529b\u4e00\u62c9\u3002\u53f2\u7ea2\u82f1\u5df2\u662f\u7528\u5c3d\u4e86\u6c14\u529b\uff0c\u8fd9\u4e00\u62c9\u4e4b\u4e0b\uff0c\u767b\u65f6\u5c06\u5979\u7275\u52a8\uff0c\u8eab\u5411\u524d\u503e\u3002\u9f9a\u5e73 \u91ce\u4e00\u8dc3\u800c\u4e0a\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u671d\u7740\u5979\u7684\u7435\u7436\u9aa8\u6293\u4e0b\u3002 \u3000\u3000\u773c\u770b\u53f2\u7ea2\u82f1\u96be\u9003\u9b54\u638c\uff0c\u5ffd\u542c\u5f97\u91d1\u9010\u6d41\u4e00\u58f0\u5927\u559d\uff0c\u58f0\u5230\u4eba\u5012\u3002\u811a\u672a\u6cbe\u5730\uff0c\u5df2\u662f\u6325\u5251\u5411\u6c99\u5343 \u5cf0\u65a9\u53bb\u3002 \u3000\u3000\u539f\u6765\u53f2\u767d\u90fd\u7684\u72ec\u811a\u94dc\u4eba\u867d\u7136\u8db3\u4ee5\u4e0e\u7384\u94c1\u5b9d\u5251\u76f8\u6297\uff0c\u4f46\u5374\u6bd5\u7adf\u4e0d\u7075\u6d3b\u3002\u91d1\u9010\u6d41\u80dc\u4ed6\u4e0d\u5f97\uff0c \u8981\u6446\u8131\u4ed6\u5374\u5e76\u4e0d\u96be\u3002 \u3000\u3000\u6c99\u5343\u5cf0\u672c\u4ee5\u4e3a\u91d1\u9010\u6d41\u65e0\u6687\u517c\u987e\u7684\uff0c\u6b64\u65f6\u89c1\u4ed6\u7a81\u5982\u800c\u6765\uff0c\u7109\u5f97\u4e0d\u614c\uff1f\u53f2\u7ea2\u82f1\u90a3\u6761\u8f6f\u97ad\u5df2\u7ed9 \u4ed6\u6253\u5f97\u50cf\u7ef7\u7d27\u4e86\u7684\u5f13\u5f26\uff0c\u53ea\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u97ad\u68a2\u65ad\u4e86\u4e00\u622a\uff0c\u6c99\u5343\u5cf0\u659c\u8eab\u7a9c\u51fa\uff0c\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff01 \u539f\u6765\u91d1\u9010\u6d41\u7684\u5251\u6765\u5f97\u592a\u5feb\uff0c\u4ed6\u672c\u6765\u662f\u8981\u7acb\u5373\u677e\u624b\u7684\uff0c\u7ec8\u4e8e\u8fd8\u662f\u7ed9\u4ed6\u65ad\u4e86\u97ad\u68a2\u3002\u4e5f\u5e78\u597d\u4ed6\u8eb2\u907f \u5f97\u5feb\uff0c\u5426\u5219\u624b\u638c\u4e5f\u8981\u7ed9\u5272\u4e86\u4e0b\u6765\uff01 \u3000\u3000\u9f9a\u5e73\u91ce\u90a3\u4e00\u6293\u672c\u6765\u53ef\u4ee5\u6293\u4f4f\u53f2\u7ea2\u82f1\u7684\u7435\u7436\u9aa8\uff0c\u4fbf\u7ed9\u91d1\u9010\u6d41\u4e00\u58f0\u5927\u559d\uff0c\u4e0d\u7531\u5f97\u4ed6\u4e0d\u5fc3\u5934\u4e00 \u9707\uff0c\u8fd9\u4e00\u6293\u52bf\u9053\u7565\u7f13\uff0c\u91d1\u9010\u6d41\u5de6\u624b\u4e00\u638c\uff0c\u201c\u7830\u201d\u7684\u628a\u4ed6\u6253\u5f97\u7ffb\u4e86\u4e00\u4e2a\u7b4b\u6597\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u8f6f\u97ad\u75be\u626b\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u6c99\u4f2f\u4f2f\uff0c\u8fd9\u4e00\u97ad\u662f\u4f60\u53eb\u6211\u6253\u7684\uff01\u201d\u6c99\u5343\u5cf0\u521a\u521a\u7a9c\u8fc7\u4e00 \u65c1\uff0c\u60ca\u9b42\u672a\u8d70\uff0c\u907f\u8fc7\u4e86\u7384\u94c1\u5b9d\u5251\uff0c\u5374\u907f\u4e0d\u5f00\u53f2\u7ea2\u82f1\u7684\u8f6f\u97ad\uff0c\u8fd9\u4e00\u97ad\u521a\u597d\u6253\u7740\u4ed6\u7684\u9762\u95e8\uff0c\u6253\u5f97 \u4ed6\u76ae\u8089\u5f00\u88c2\uff0c\u773c\u6cea\u9f3b\u6d95\u9f50\u6d41\uff0c\u9762\u4e0a\u597d\u50cf\u6d82\u4e0a\u5404\u79cd\u989c\u6599\u7684\u753b\u5e03\u3002\u53ef\u60dc\u53f2\u7ea2\u82f1\u6c14\u529b\u4e0d\u8db3\uff0c\u672a\u80fd\u6253 \u88c2\u4ed6\u7684\u5934\u9885\u3002\u4f46\u4e5f\u662f\u591f\u4ed6\u53d7\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u63e1\u7740\u53f2\u7ea2\u82f1\u5de6\u624b\uff0c\u4e00\u80a1\u5176\u529b\u8f93\u9001\u8fdb\u53bb\uff0c\u52a9\u5979\u9a71\u9664\u5bd2\u6c14\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53f2\u767d\u90fd \u53c8\u5df2\u6251\u5230\uff0c\u94dc\u4eba\u51fb\u4e0b\uff0c\u9690\u9690\u631f\u7740\u98ce\u96f7\u4e4b\u58f0\u3002\u6b64\u65f6\u4ed6\u5df2\u662f\u6025\u6012\u4ea4\u52a0\uff0c\u5373\u4f7f\u628a\u59b9\u59b9\u4e00\u9f50\u51fb\u6bd9\uff0c\u4e5f \u662f\u987e\u4e0d\u5f97\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u53eb\u9053\uff1a\u201c\u522b\u518d\u987e\u6211\u4e86\uff0c\u4f60\u81ea\u5df1\u9003\u5427\u3002\u201d\u5979\u6df1\u77e5\u91d1\u9010\u6d41\u7684\u8f7b\u529f\u5353\u7edd\uff0c\u624b\u4e2d\u53c8\u6709\u7384\u94c1 \u5b9d\u5251\uff0c\u8981\u9003\u7684\u8bdd\uff0c\u65e0\u4eba\u80fd\u591f\u5c06\u4ed6\u62e6\u963b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u751f\u5219\u540c\u751f\uff0c\u6b7b\u5219\u540c\u6b7b\uff01\u201d\u4ecd\u7136\u7d27\u7d27\u63e1\u7740\u53f2\u7ea2\u82f1\u7684\u5de6\u624b\uff0c\u53ea\u7528\u53f3\u81c2\u4e4b\u529b\uff0c\u6325 \u5251\u62b5\u6321\u94dc\u4eba\u3002 \u3000\u3000\u5f53\u5f53\u5f53\u51e0\u58f0\u54cd\u8fc7\uff0c\u53f2\u767d\u90fd\u628a\u72ec\u811a\u94dc\u4eba\u5411\u524d\u63a8\u538b\uff0c\u91d1\u9010\u6d41\u62b5\u6321\u4e0d\u4f4f\u8fd9\u80a1\u731b\u529b\uff0c\u8eab\u5f62\u867d\u672a\u79fb \u52a8\uff0c\u53cc\u8db3\u5df2\u662f\u9677\u5728\u6ce5\u4e2d\u3002 \u3000\u3000\u53f2\u767d\u90fd\u53eb\u9053\uff1a\u201c\u5feb\u6765\u4eba\u5566\uff01\u201d\u8981\u77e5\u91d1\u9010\u6d41\u662f\u628a\u771f\u529b\u5206\u4f5c\u4e24\u80a1\u4f7f\u7528\u7684\uff0c\u6b64\u65f6\u5df2\u662f\u53ea\u80fd\u52c9\u5f3a \u652f\u6491\u3002\u5018\u82e5\u6709\u4eba\u5411\u4ed6\u5077\u88ad\u7684\u8bdd\uff0c\u6b64\u4eba\u4e0d\u5fc5\u6b66\u529f\u5f88\u5f3a\uff0c\u5df1\u8db3\u5236\u4ed6\u6b7b\u547d\uff0c\u53ef\u60dc\u8ddd\u79bb\u6700\u8fd1\u7684\u6c99\u5343\u5cf0 \u4e0e\u9f9a\u5e73\u91ce\u4e8c\u4eba\uff0c\u53d7\u632b\u4e4b\u4f59\uff0c\u5df2\u662f\u7ed9\u91d1\u9010\u6d41\u5413\u7834\u4e86\u670d\uff0c\u4f24\u5f97\u867d\u7136\u4e0d\u91cd\uff0c\u4f46\u542c\u4e86\u53f2\u767d\u90fd\u7684\u53eb\u558a\uff0c \u4f9d\u7136\u662f\u8fdf\u8fdf\u7591\u7591\u7684\u4e0d\u6562\u5411\u524d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u611f\u89c9\u53f2\u7ea2\u82f1\u7684\u638c\u5fc3\u5df2\u7ecf\u5fae\u5fae\u53d1\u70ed\uff0c\u8fd9\u624d\u653e\u5f00\u4e86\u624b\u3002\u4e00\u58f0\u5927\u559d\uff0c\u62d4\u51fa\u53cc\u8db3\uff0c\u5168\u8eab\u6c14 \u529b\u90fd\u8fd0\u5230\u5251\u4e0a\uff0c\u731b\u7684\u4e00\u633a\u4e00\u6325\uff0c\u53f2\u767d\u90fd\u7684\u94dc\u4eba\u8eab\u4e0a\u53c8\u6dfb\u4e86\u4e00\u9053\u4f24\u75d5\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u9000\u5f00\u4e00\u6b65\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff0c\u6211\u6765\u8fdf\u4e86\u4e00\u6b65\uff01\u4f46\u4e5f\u4e0d\u7b97\u592a\u8fdf\u3002\u201d\u539f\u6765\u662f\u6587\u9053\u5e84\u5306\u5306 \u8d76\u5230\u3002 \u3000\u3000\u6587\u9053\u4e3b\u5f53\u7136\u4e0d\u662f\u6c99\u5343\u5cf0\u7b49\u4eba\u53ef\u6bd4\uff0c\u4ed6\u4ee5\u4e09\u8c61\u795e\u529f\u8fd0\u5251\uff0c\u5251\u51fa\u5982\u98ce\uff0c\u786c\u63d2\u8fdb\u6765\uff0c\u4e0d\u8fc7\u6570 \u62db\uff0c\u5df2\u662f\u628a\u53f2\u7ea2\u82f1\u548c\u4f59\u9042\u6d41\u53c8\u9694\u5f00\u4e24\u8fb9\u3002 \u3000\u3000\u53f2\u767d\u90fd\u9053\uff1a\u201c\u4f60\u66ff\u6211\u62ff\u8fd9\u4e2a\u81ed\u4e2b\u5934\uff0c\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\u4e0d\u662f\u6211\u7684\u5bf9\u624b\u3002\u201d\u8981\u77e5\u91d1\u9010\u6d41\u4e3a\u53f2\u7ea2 \u82f1\u9a71\u9664\u5bd2\u6c14\uff0c\u4e1a\u5df2\u8017\u4e86\u51e0\u5206\u771f\u529b\uff0c\u4ed6\u7684\u529f\u529b\u672c\u6765\u5c31\u4e0d\u5982\u53f2\u767d\u90fd\uff0c\u6b64\u65f6\u81ea\u8db3\u76f8\u5dee\u66f4\u8fdc\u4e86\u3002\u7eb5\u6709 \u7384\u94c1\u5b9d\u5251\u5728\u624b\uff0c\u53f2\u767d\u90fd\u81ea\u6043\u4ea6\u53ef\u80dc\u4ed6\uff0c\u662f\u4ee5\u4e0d\u613f\u6587\u9053\u5e84\u548c\u81ea\u5df1\u4e89\u529f\u3002\u6587\u9053\u5e84\u770b\u51fa\u4fbf\u5b9c\uff0c\u4e0d\u8fc7 \u4ed6\u5bf9\u91d1\u9010\u6d41\u603b\u4e5f\u8fd8\u6709\u51e0\u5206\u987e\u5fcc\uff0c\u4e8e\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u65e2\u7136\u662f\u4f60\u7684\u5429\u5490\uff0c\u90a3\u6211\u5c31\u53ea \u597d\u5f97\u7f6a\u4ee4\u59b9\u4e86\uff01\u201d\u8fd9\u6837\u4e00\u6765\uff0c\u53d8\u6210\u4e86\u5404\u81ea\u4e3a\u6218\u7684\u5c40\u9762\uff0c\u5f3a\u5f31\u4e4b\u52bf\uff0c\u8d8a\u53d1\u60ac\u6b8a\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u4e86\u4e2a\u5c11\u5973\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u5389\u5927\u54e5\uff0c\u5389\u5927\u54e5\uff01\u54a6\uff01\u5389\u5927\u54e5\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u4f60\u5728\u54ea \u513f\uff1f\u201d\u539f\u6765\u662f\u516c\u5b59\u71d5\u6740\u6765\u4e86\u3002 \u3000\u3000\u516c\u5b59\u71d5\u88ab\u5047\u5c71\u9694\u5728\u4e00\u8fb9\uff0c\u5389\u5357\u661f\u521a\u624d\u548c\u53f2\u767d\u90fd\u4ea4\u624b\u7684\u60c5\u5f62\uff0c\u5979\u4e5f\u6ca1\u6709\u77a7\u89c1\uff0c\u4f46\u5389\u5357\u661f\u90a3 \u4e00\u58f0\u88c2\u4eba\u5fc3\u80ba\u7684\u547c\u53eb\uff0c\u5979\u662f\u542c\u89c1\u4e86\u7684\u3002 \u3000\u3000\u516c\u5b59\u71d5\u4e00\u6025\u4e4b\u4e0b\uff0c\u8c41\u4e86\u6027\u547d\uff0c\u62db\u62db\u90fd\u662f\u6740\u624b\uff0c\u5979\u672c\u6765\u662f\u548c\u674e\u6566\u592b\u5987\u5408\u6218\u5b89\u4fca\u5ead\u4ee5\u53ca\u53e6\u5916 \u56db\u4e2a\u6b66\u58eb\u7684\u3002\u90a3\u56db\u4e2a\u6b66\u58eb\u4e2d\u6709\u4e00\u4eba\u7684\u9f50\u7709\u68cd\u65e9\u4e9b\u65f6\u5019\u5df2\u7ed9\u5389\u5357\u661f\u7684\u7384\u94c1\u5b9d\u5251\u5288\u6495\uff0c\u4ed6\u4eec\u7ec3\u70ed \u4e86\u7684\u4e00\u5957\u68cd\u6cd5\uff0c\u7f3a\u4e86\u4e00\u4eba\uff0c\u5a01\u529b\u5927\u6253\u62c6\u6263\u3002\u4e0d\u8fc7\u7247\u523b\uff0c\u53e6\u5916\u4e09\u4eba\u4ea6\u90fd\u4f24\u5728\u516c\u5b59\u71d5\u7684\u5251\u4e0b\uff0c\u5bf9 \u65b9\u9ad8\u624b\uff0c\u53ea\u5269\u4e0b\u5b89\u4fca\u5ead\u4e00\u4eba\u672a\u4f24\uff0c\u516c\u5b59\u71d5\u6599\u60f3\u674e\u6566\u592b\u5987\u8db3\u53ef\u4ee5\u5e94\u4ed8\u5f97\u4e86\uff0c\u4e8e\u662f\u8d76\u5fd9\u8fc7\u6765\u627e\u5bfb \u5389\u5357\u661f\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u5bb6\u4f20\u7684\u5feb\u5251\u4e43\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u53ea\u89c1\u5979\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u8863\u8882\u98d8\u98d8\uff0c\u5251\u5149\u52a0\u7ec3\uff0c \u7aef\u7684\u4fbf\u4f3c\u6c34\u86c7\u6e38\u8d70\u4e00\u822c\uff0c\u9f9a\u5e73\u91ce\u3001\u6587\u80dc\u4e2d\u7b49\u4eba\u54ea\u91cc\u62e6\u5f97\u4f4f\u5979\uff1f\u8f6c\u773c\u4e4b\u95f4\uff0c\u53c8\u6709\u4e09\u540d\u6b66\u58eb\u4e2d \u5251\uff0c\u516c\u5b59\u71d5\u5df2\u662f\u8d8a\u8fc7\u5047\u5c71\uff0c\u5230\u6765\u4e0e\u91d1\u53f2\u4e8c\u4eba\u4f1a\u5408\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u89c1\u7740\u5389\u5927\u54e5\uff1f\u201d\u8981\u77e5\u91d1\u9010\u6d41\u4e00\u76f4\u4ee5\u4e3a\u662f\u5389\u5357\u661f\u5df2\u7ecf\u8dd1\u53bb\u548c \u516c\u5b59\u71d5\u4f1a\u5408\u7684\uff0c\u54ea\u77e5\u6b64\u523b\u5ffd\u7136\u89c1\u5230\u516c\u5b59\u71d5\u8dd1\u6765\u627e\u4ed6\uff0c\u91d1\u9010\u6d41\u7109\u5f97\u4e0d\u60ca\uff1f\u662f\u4ee5\u660e\u77e5\u516c\u5b59\u71d5\u6ca1\u6709 \u9047\u5230\u5389\u5357\u661f\uff0c\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u95ee\u5979\u4e00\u95ee\u3002 \u3000\u3000\u9ad8\u624b\u6bd4\u6597\uff0c\u54ea\u5bb9\u5f97\u5206\u4e86\u5fc3\u795e\uff1f\u53f2\u767d\u90fd\u8d81\u6b64\u65f6\u673a\uff0c\u9661\u5730\u4e00\u58f0\u5927\u559d\uff0c\u94dc\u4eba\u4f7f\u52b2\u538b\u4e0b\uff0c\u91d1\u9010\u6d41 \u7684\u7384\u94c1\u5b9d\u5251\u7adf\u4e5f\u7981\u53d7\u4e0d\u8d77\uff0c\u7ed9\u4ed6\u538b\u5f97\u5012\u6454\u56de\u6765\u3002\u53f2\u767d\u90fd\u4f7f\u4e0a\u4e86\u201c\u9694\u7269\u4f20\u529f\u201d\u7684\u672c\u9886\uff0c\u91d1\u9010\u6d41 \u7d27\u7d27\u63e1\u7740\u5251\u67c4\uff0c\u53ea\u89c9\u4e00\u80a1\u5de8\u529b\u5012\u649e\u56de\u6765\uff0c\u80f8\u53e3\u7adf\u4f3c\u5982\u53d7\u94c1\u9525\u731b\u51fb\u4e00\u822c\uff0c\u9707\u5f97\u4ed6\u80f8\u4e2d\u6c14\u8840\u7ffb \u6d8c\uff0c\u4e94\u810f\u516d\u8151\u90fd\u597d\u50cf\u79fb\u4e86\u4f4d\u7f6e\uff01 \u3000\u3000\u773c\u770b\u91d1\u9010\u6d41\u5c31\u8981\u7ed9\u94dc\u4eba\u538b\u5f97\u91cd\u4f24\uff0c\u53f2\u767d\u90fd\u6b63\u81ea\u5f97\u610f\uff0c\u53eb\u9053\uff1a\u201c\u64a4\u5251\uff01\u201d\u5ffd\u89c9\u5fae\u98ce\u98d2\u7136\uff0c \u516c\u5b59\u71d5\u4e00\u62db\u201c\u4e03\u661f\u805a\u4f1a\u201d\uff0c\u957f\u5251\u6307\u5230\u4e86\u53f2\u767d\u90fd\u7684\u540e\u5fc3\u3002 \u3000\u3000\u53f2\u767d\u90fd\u662f\u4e2a\u6b66\u5b66\u5927\u884c\u5bb6\uff0c\u4e00\u542c\u8fd9\u91d1\u5200\u5288\u98ce\u4e4b\u58f0\uff0c\u4fbf\u5982\u516c\u5b59\u71d5\u8fd9\u62db\uff0c\u7adf\u662f\u540c\u65f6\u523a\u4ed6\u80cc\u5fc3\u7684 \u4e03\u5904\u7a74\u9053\uff01 \u3000\u3000\u6b64\u662f\u53f2\u767d\u90fd\u6b66\u827a\u9ad8\u5f3a\uff0c\u4e5f\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff1a\u201c\u60f3\u4e0d\u5230\u516c\u5b59\u5b8f\u7684\u5973\u513f\uff0c\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u7adf\u662f\u5982\u6b64 \u5389\u5bb3\uff01\u4f46\u4e0d\u77e5\u516c\u5b59\u5b8f\u8fd9\u8001\u513f\u6765\u4e86\u6ca1\u6709\uff1f\u201d\u5f53\u4e0b\u53ea\u597d\u653e\u677e\u5bf9\u91d1\u9010\u6d41\u7684\u538b\u529b\uff0c\u5de6\u624b\u53cd\u624b\u4e00\u638c\u6263 \u51fa\uff0c\u8361\u5f00\u4e86\u516c\u5b59\u71d5\u7684\u5251\u5c16\u3002 \u3000\u3000\u6b64\u65f6\u53f2\u7ea2\u82f1\u5728\u6587\u9053\u5e84\u6025\u653b\u4e4b\u4e0b\uff0c\u4e5f\u53c8\u662f\u5403\u7d27\u975e\u5e38\uff0c\u91d1\u9010\u6d41\u6b63\u9762\u7684\u538b\u529b\u4e00\u677e\uff0c\u7acb\u5373\u4fbf\u56de\u8f6c \u5934\u6765\uff0c\u62a1\u8d77\u7384\u94c1\u5b9d\u5251\uff0c\u5f53\u4f5c\u5927\u5200\u6765\u4f7f\uff0c\u4e00\u62db\u201c\u72ec\u5288\u534e\u5c71\u201d\uff0c\u671d\u7740\u6587\u9053\u5e84\u5288\u4e0b\u3002\u6587\u9053\u5e84\u5403\u4e86\u4e00 \u60ca\uff0c\u8fde\u5fd9\u95ea\u8fc7\u4e00\u8fb9\uff0c\u5176\u5b9e\u91d1\u9010\u6d41\u6b64\u65f6\u6c14\u606f\u5c1a\u672a\u8c03\u5300\uff0c\u6587\u9053\u5e84\u5018\u82e5\u662f\u6562\u4e8e\u548c\u91d1\u9042\u6d41\u76f8\u62fc\u7684\u8bdd\uff0c \u91d1\u9010\u6d41\u51b3\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u3002 \u3000\u3000\u53f2\u767d\u90fd\u4e00\u638c\u8361\u5f00\u4e86\u516c\u5b59\u71d5\u7684\u5251\u5c16\uff0c\u7acb\u5373\u53d8\u4e3a\u5927\u64d2\u62ff\u624b\uff0c\u6b3a\u8eab\u8fdb\u8feb\uff0c\u5f3a\u62a2\u516c\u5b59\u71d5\u7684\u5175\u5203\uff0c \u66f2\u81c2\u56de\u8098\uff0c\u6309\u62cd\u52fe\u6293\uff0c\u638c\u5288\u6307\u6233\uff0c\u62db\u62db\u9669\u72e0\uff0c\u6bcf\u638c\u90fd\u4f3c\u4e4e\u5c31\u8981\u6253\u5230\u516c\u5b59\u71d5\u7684\u8eab\u4e0a\u3002\u516c\u5b59\u71d5\u5251 \u8d70\u8f7b\u7075\uff0c\u5c45\u7136\u5728\u5341\u62db\u4e4b\u4e2d\u8fd8\u653b\u4e86\u56db\u62db\u3002\u4f46\u56e0\u8db4\u79bb\u592a\u8fd1\uff0c\u53ea\u89c9\u638c\u98ce\u5288\u9762\uff0c\u51e0\u4e4e\u900f\u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u516c\u5b59\u71d5\u4e00\u98d8\u4e00\u95ea\uff0c\u659c\u7a9c\u4e09\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u53f2\u767d\u90fd\u8fd9\u53ae\u6b3a\u4fae\u5b69\u513f\uff0c\u7239\u7239\uff0c\u4f60\u8fd8\u4e0d\u5feb\u6765\u5e2e \u6211\uff1f\u201d\u53f2\u767d\u90fd\u5fc3\u4e2d\u4e00\u51db\uff0c\u4e00\u62db\u51cc\u5389\u4e4b\u6781\u7684\u653b\u52bf\u6309\u4f4f\u4e0d\u53d1\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u548c\u4f60\u7239\u7239\u662f\u597d\u670b\u53cb\uff0c\u4f60 \u522b\u80e1\u95f9\uff0c\u8d76\u5feb\u8d70\u5427\uff01\u201d\u8981\u77e5\u516c\u5b59\u71d5\u7684\u7236\u4eb2\u4e43\u662f\u7ea2\u7f28\u4f1a\u7684\u5e2e\u4e3b\uff0c\u7ea2\u7f28\u4f1a\u662f\u6c5f\u6e56\u4e0a\u7b2c\u4e00\u5927\u5e2e\u4f1a\uff0c \u52bf\u529b\u5c1a\u5728\u516d\u5408\u5e2e\u4e4b\u4e0a\uff0c\u516c\u5b59\u5b8f\u672c\u8eab\u7684\u6b66\u529f\u4e5f\u8fdc\u8fdc\u80dc\u8fc7\u53f2\u767d\u90fd\uff0c\u6b63\u662f\u53f2\u767d\u90fd\u6240\u6700\u987e\u5fcc\u7684\u4eba\u7269\u4e4b \u4e00\u3002 \u3000\u3000\u53f2\u767d\u90fd\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u516c\u5b59\u5b8f\u8fd9\u8001\u513f\u96be\u7f20\u5f97\u5f88\uff0c\u4e0d\u77e5\u4ed6\u6765\u4e86\u6ca1\u6709\uff1f\u4f46\u4e0d\u7ba1\u662f\u5047\u662f\u771f\uff0c\u5374\u4f55 \u82e6\u4e0e\u4ed6\u7ed3\u6028\u3002\u8fd9\u5c0f\u59ae\u5b50\u653e\u8fc7\u4e5f\u4e0d\u6253\u7d27\uff0c\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\u5374\u51b3\u4e0d\u80fd\u8ba9\u4ed6\u8d70\u4e86\u3002\u201d\u4e8e\u662f\u5728\u653e\u6389\u516c\u5b59 \u71d5\u4e4b\u540e\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u53c8\u518d\u4e0e\u6587\u9053\u5e84\u8054\u624b\uff0c\u653b\u6253\u91d1\u9010\u6d41\u3002 \u3000\u3000\u5e78\u4e8f\u6709\u516c\u5b59\u71d5\u7f20\u4e86\u53f2\u767d\u90fd\u7247\u523b\uff0c\u91d1\u9010\u6d41\u5598\u606f\u4e00\u8fc7\uff0c\u62a4\u4f53\u795e\u529f\u81ea\u7136\u53d1\u6325\u4f5c\u7528\uff0c\u5185\u529b\u6e90\u6e90\u800c \u6765\uff0c\u867d\u6ca1\u6709\u5145\u5168\u6062\u590d\uff0c\u4ea6\u5df2\u6062\u590d\u4e86\u4e03\u516b\u6210\u3002\u5f53\u4e0b\u957f\u5251\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u5251\u5149\u95ea\u7f29\u4e0d\u5b9a\uff0c\u5de6\u653b\u53f2 \u767d\u90fd\uff0c\u53f3\u653b\u6587\u9053\u5e84\u3002\u653b\u5411\u53f2\u767d\u90fd\u7684\u662f\u865a\u62db\uff0c\u4e0d\u8ba9\u4ed6\u7684\u94dc\u4eba\u78b0\u7740\uff0c\u4f46\u5251\u51cc\u52bf\u5389\uff0c\u5374\u662f\u5728\u4e00\u62db\u4e4b \u5185\u88ad\u4ed6\u4e03\u5904\u5927\u7a74\u3002\u53f2\u767d\u90fd\u56de\u8f6c\u94dc\u4eba\u62a4\u8eab\uff0c\u53ea\u542c\u5f97\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u6587\u9053\u5e84\u5374\u5df2\u7ed9\u91d1\u9010\u6d41\u903c\u9000\u4e24 \u6b65\u3002\u539f\u6765\u91d1\u9010\u6d41\u653b\u5411\u6587\u9053\u5e84\u7684\u4e43\u662f\u5b9e\u62db\uff0c\u6587\u9053\u5e84\u7684\u8f6f\u5251\u867d\u7136\u4e0d\u81f4\u7ed9\u4ed6\u6298\u65ad\uff0c\u5374\u662f\u62b5\u6321\u4e0d\u4f4f\u7384 \u94c1\u5b9d\u5251\u7684\u5a01\u529b\u3002 \u3000\u3000\u516c\u5b59\u71d5\u5bfb\u89c5\u5389\u5357\u661f\u4e0d\u89c1\uff0c\u6587\u80dc\u4e2d\u51b7\u7b11\u9053\uff1a\u201c\u59d3\u5389\u8fd9\u5c0f\u5b50\u65e9\u5df2\u7ed9\u53f2\u5e2e\u4e3b\u6740\u5566\uff0c\u4f60\u8fd9\u5c0f\u599e\u513f \u8fd8\u662f\u8ddf\u4e86\u6211\u5427\uff01\u201d\u6587\u80dc\u4e2d\u751f\u6027\u597d\u8272\uff0c\u867d\u77e5\u516c\u5b59\u5b8f\u672c\u9886\u4e86\u5f97\uff0c\u4f46\u6b3a\u5979\u4e00\u4e2a\u5355\u8eab\u5973\u5b50\uff0c\u5df2\u9677\u91cd \u56f4\uff0c\u63d2\u7ffc\u96be\u98de\u3002\u4e8e\u662f\u5c31\u5927\u7740\u80c6\u5b50\uff0c\u4e0e\u90d1\u96c4\u56fe\u8054\u624b\uff0c\u4e0a\u6765\u62ff\u5979\u3002 \u3000\u3000\u516c\u5b59\u71d5\u521a\u624d\u548c\u91d1\u9010\u6d41\u8bf4\u6ca1\u6709\u89c1\u7740\u5389\u5357\u661f\uff0c\u5fc3\u4e2d\u5df2\u662f\u9690\u9690\u611f\u5230\u4e0d\u5999\u3002\u6587\u80dc\u4e2d\u6b64\u8a00\u4e00\u51fa\uff0c\u4e25 \u5982\u6674\u5929\u6253\u4e86\u4e2a\u9739\u96f3\uff0c\u516c\u5b59\u71d5\u8111\u888b\u91cc\u201c\u55e1\u201d\u7684\u4e00\u58f0\uff0c\u9669\u9669\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u89c1\u5979\u60ca\u5f97\u597d\u4f3c\u5446\u4e86\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u7acb\u5373\u5411\u5979\u6251\u53bb\uff0c\u60f3\u6361\u8fd9\u4e2a\u73b0\u6210\u7684\u4fbf\u7a74\u3002\u516c\u5b59\u71d5\u77bf \u7136\u4e00\u7701\uff0c\u6012\u6c14\u9661\u751f\u3002\u90d1\u96c4\u56fe\u53eb\u9053\uff1a\u201c\u5c0f\u5fc3\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u516c\u5b59\u70ed\u5df2\u662f\u75db\u4e0b\u6740 \u624b\uff0c\u201c\u5657\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u5251\u5c16\u7a7f\u8fc7\u4e86\u6587\u80dc\u4e2d\u7684\u5de6\u80a9\u3002 \u3000\u3000\u516c\u5b59\u71d5\u4e0d\u7406\u6587\u80dc\u4e2d\u6b7b\u6d3b\uff0c\u62bd\u51fa\u5251\u6765\uff0c\u7b2c\u4e8c\u5251\u4fbf\u5411\u53f2\u767d\u90fd\u653b\u53bb\u3002\u53f2\u767d\u90fd\u6012\u9053\uff1a\u201c\u6211\u770b\u5728\u4f60 \u7239\u7239\u4efd\u4e0a\uff0c\u6307\u70b9\u4f60\u4e00\u6761\u751f\u8def\uff0c\u4f60\u81ea\u5df1\u8981\u6765\u627e\u6b7b\uff0c\u6211\u53ef\u4e0d\u548c\u4f60\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u54d1\u58f0\u53eb\u9053\uff1a\u201c\u5389\u5357\u661f\u662f\u4e0d\u662f\u4f60\u6740\uff0c\u662f\u4e0d\u662f\u4f60\u6740\u7684\uff1f\u201d\u53f2\u767d\u90fd\u5fc3\u60f3\uff1a\u201c\u6211\u82e5\u8bf4\u4e0d\u662f \u6211\u6740\u7684\uff0c\u65c1\u4eba\u4e00\u5b9a\u4ee5\u4e3a\u6211\u662f\u6015\u4e86\u516c\u5b59\u5b8f\u3002\u201d\u5f53\u4e0b\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u559c\u6b22\u4e0a\u8fd9\u5c0f\u5b50\u5440\uff0c\u8fd9 \u5c0f\u5b50\u6709\u4ec0\u4e48\u597d\uff1f\u4e0d\u9519\uff0c\u662f\u6211\u6740\u7684\uff01\u4e56\u4f84\u5973\uff0c\u4f60\u83ab\u4f24\u5fc3\uff0c\u6211\u8d54\u4f60\u4e00\u4e2a\u5973\u5a7f\u5c31\u662f\u3002\u201d\u5176\u5b9e\u5389\u5357\u661f \u7684\u786e\u662f\u7ed9\u53f2\u767d\u90fd\u6253\u5f97\u91cd\u4f24\uff0c\u4f46\u6b7b\u6d3b\u5982\u4f55\uff0c\u53f2\u767d\u90fd\u90fd\u8fd8\u4e0d\u77e5\u3002\u4e0d\u8fc7\u6599\u60f3\u4ed6\u662f\u5fc5\u6b7b\u65e0\u7591\u7684\u4e86\u3002 \u3000\u3000\u516c\u5b59\u71d5\u53eb\u9053\uff1a\u201c\u5389\u5927\u54e5\u6b7b\u4e86\uff0c\u6211\u4e5f\u4e0d\u60f3\u6d3b\u5566\uff01\u4f60\u6740\u4e86\u6211\uff0c\u81ea\u6709\u7239\u7239\u7ed9\u6211\u62a5\u4ec7\u3002\u53f2\u767d\u90fd\uff0c \u4eca\u65e5\u6211\u548c\u4f60\u62fc\u4e86\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5df2\u662f\u5411\u53f2\u767d\u90fd\u75be\u653b\u4e86\u5341\u4e03\u516b\u5251\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e0e\u53f2\u7ea2\u82f1\u90fd\u662f\u4e0d\u7ea6\u800c\u540c\u5730\u60f3\u9053\uff1a\u201c\u539f\u6765\u5389\u5927\u54e5\u5df2\u6709\u4e86\u610f\u4e2d\u4eba\uff0c\u8fd9\u4f4d\u516c\u5b59\u5a18\u5bf9\u4ed6\u5f53 \u771f\u662f\u6df1\u60c5\u65e0\u9650\u3002\u201d\u5fc3\u4e2d\u751a\u611f\u5b89\u6170\uff0c\u4f46\u60f3\u53ca\u5389\u5357\u661f\u751f\u6b7b\u672a\u535c\uff0c\u5374\u53c8\u4e0d\u514d\u5fc3\u4e2d\u62c5\u5fe7\u3002\u4f46\u5728\u9762\u5bf9\u5f3a \u654c\u4e4b\u4e0b\uff0c\u4e5f\u53ea\u6709\u632f\u594b\u7cbe\u795e\u4f5c\u6218\uff0c\u65e0\u6687\u591a\u6240\u601d\u8651\u4e86\u3002 \u3000\u3000\u4e24\u5ea7\u5047\u5c71\u4e4b\u95f4\u5730\u65b9\u6709\u9650\uff0c\u91d1\u9010\u6d41\u3001\u53f2\u767d\u90fd\u3001\u6587\u9053\u5e84\u90fd\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u5728\u8fd9\u4e09\u5927\u9ad8\u624b\u5267 \u6218\u4e4b\u4e0b\uff0c\u65c1\u4eba\u54ea\u91cc\u63d2\u5f97\u8fdb\u624b\u6765\uff1f\u90d1\u96c4\u56fe\u4e0d\u81ea\u91cf\u529b\uff0c\u6043\u7740\u6709\u94c1\u7802\u638c\u7684\u529f\u592b\uff0c\u4e0a\u6765\u52a9\u6218\u3002\u7ed9\u7384\u94c1 \u5b9d\u5251\u626c\u8d77\u7684\u52b2\u98ce\u4e00\u649e\uff0c\u6070\u597d\u649e\u5728\u53f2\u767d\u90fd\u7684\u94dc\u4eba\u8eab\u4e0a\uff0c\u767b\u65f6\u4e00\u547d\u545c\u547c\u3002 \u3000\u3000\u6587\u9053\u5e84\u6302\u5ff5\u513f\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u80dc\u4e2d\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u6587\u80dc\u4e2d\u5636\u58f0\u53eb\u9053\uff1a\u201c\u6211\u7ed9\u90a3\u81ed\u4e2b\u5934\u4f24 \u4e86\uff0c\u7239\uff0c\u4f60\u8981\u4e3a\u6211\u62a5\u4ec7\uff01\u201d\u6587\u9053\u5e84\u4e0d\u77e5\u513f\u5b50\u4f24\u5f97\u5982\u4f55\uff0c\u4e0d\u89c9\u5fc3\u614c\u610f\u4e71\u3002\u53f2\u767d\u90fd\u8bf4\u9053\uff1a\u201c\u4ee4\u90ce \u51b3\u6b7b\u4e0d\u4e86\uff0c\u6211\u542c\u5f97\u51fa\u6765\uff0c\u4f60\u653e\u5fc3\u5427\u3002\u201d\u6587\u9053\u5e84\u9053\uff1a\u201c\u4ed6\u597d\u50cf\u4f24\u5f97\u5f88\u91cd\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6b7b\u4e0d\u4e86\u7684\u3002\u5c31\u662f\u6b7b\u4e86\uff0c\u8fd9\u4e5f\u662f\u4e3a\u7687\u4e0a\u5c3d\u529b\u3002\u201d\u6587\u9053\u5e84\u970d\u7136\u4e00\u60ca\uff0c\u5fc3 \u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u8fd9\u7d27\u8981\u5173\u5934\uff0c\u6211\u662f\u51b3\u4e0d\u80fd\u9000\u7f29\u7684\u4e86\u3002\u6211\u987e\u5f97\u4e86\u513f\u5b50\uff0c\u53ef\u5c31\u987e\u4e0d\u4e86\u5bcc\u8d35\u529f\u540d \u5566\uff01\u201d\u4f46\u8bdd\u867d\u5982\u6b64\uff0c\u603b\u662f\u4e0d\u514d\u6709\u70b9\u6302\u5fc3\u3002 \u3000\u3000\u6b64\u65f6\u53cc\u65b9\u6210\u4e86\u4ee5\u4e8c\u654c\u4e09\u7684\u5c40\u9762\uff0c\u91d1\u9010\u6d41\u8fd9\u8fb9\u867d\u591a\u4e86\u4e00\u4eba\uff0c\u4f46\u53f2\u7ea2\u82f1\u3001\u516c\u5b59\u71d5\u4e0d\u4f46\u672c\u9886\u8f83 \u5f31\uff0c\u800c\u4e14\u90fd\u662f\u5728\u4e45\u6218\u4e4b\u540e\u6c14\u529b\u96be\u4ee5\u652f\u6301\u7684\u4e86\u3002\u5e78\u4e8f\u53f2\u767d\u90fd\u5bf9\u516c\u5b59\u5b8f\u6709\u70b9\u987e\u5fcc\uff0c\u4e0d\u6562\u4f24\u4ed6\u5973 \u513f\u3002\u800c\u6587\u9053\u5e84\u6302\u5ff5\u513f\u5b50\uff0c\u7cbe\u795e\u4e5f\u662f\u4e0d\u80fd\u96c6\u4e2d\uff0c\u53cc\u65b9\u8fd9\u624d\u6253\u6210\u4e86\u5e73\u624b\u3002 \u3000\u3000\u6fc0\u6218\u4e4b\u4e2d\u5ffd\u542c\u5f97\u70ae\u58f0\u9686\u9686\uff0c\u8d8a\u6765\u8d8a\u54cd\u3002\u4e0d\u4e45\uff0c\u8fde\u5927\u519b\u53ae\u6740\u4e4b\u58f0\uff0c\u9a6c\u7fa4\u5954\u9a70\u4e4b\u58f0\uff0c\u4e5f\u9690\u9690 \u53ef\u95fb\u4e86\u3002 \u3000\u3000\u518d\u8bf4\u5e05\u5b5f\u96c4\u6b63\u5728\u5230\u5904\u627e\u5bfb\u8d3a\u5927\u5a18\uff0c\u542c\u5f97\u70ae\u58f0\uff0c\u60f4\u60f4\u4e0d\u5b89\uff0c\u5ffd\u89c1\u5b89\u4fca\u5ead\u7ed9\u4e00\u53cc\u7537\u5973\u8ffd\u7740\u9003 \u8dd1\uff0c\u539f\u6765\u4ed6\u7684\u52a9\u624b\u7ed9\u516c\u5b59\u71d5\u5168\u90e8\u6740\u4f24\u4e4b\u540e\uff0c\u5df2\u662f\u6253\u4e0d\u8fc7\u674e\u6566\u592b\u59bb\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u8fce\u4e0a\u524d\u53bb\uff0c\u66ff\u5b89\u4fca\u5ead\u6321\u4e86\u4e00\u62db\uff0c\u4ed6\u867d\u7136\u53d7\u4e86\u4f24\uff0c\u529f\u529b\u8fd8\u662f\u8fdc\u5728\u674e\u6566\u592b\u59bb\u4e4b\u4e0a\u3002\u5e05 \u5b5f\u96c4\u51fb\u9000\u4e86\u674e\u6566\u592b\u59bb\uff0c\u7acb\u5373\u95ee\u9053\uff1a\u201c\u8d3a\u5927\u5a18\u5462\uff1f\u201d \u3000\u3000\u5b89\u4fca\u5ead\u5598\u8fc7\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8d3a\u5927\u5a18\u5df2\u7ed9\u90a3\u59d3\u5389\u7684\u5c0f\u5b50\u6740\u4e86\u3002\u201d\u5e05\u5b5f\u96c4\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4ec0 \u4e48\uff0c\u5979\u5df2\u7ecf\u6b7b\u4e86\uff1f\u201d\u5fc3\u4e2d\u4e00\u6025\uff0c\u54c7\u7684\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5b89\u4fca\u5ead\u5927\u60ca\u9053\uff1a\u201c\u5e05\u5c06\u519b\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u6709\u4e2a\u4eba\u5306\u5306\u8dd1\u6765\uff0c\u4e0a\u6c14\u4e0d\u63a5\u4e0b \u6c14\u7684\u4e5f\u5728\u53eb\u9053\uff1a\u201c\u5e05\u5c06\u519b\uff0c\u5e05\u5c06\u519b\uff01\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u5438\u4e86\u53e3\u6c14\uff0c\u5b9a\u4e00\u5b9a\u795e\uff0c\u7741\u773c\u4e00\u770b\uff0c\u8ba4\u5f97\u8fd9\u4eba\u662f\u4ed6\u6d3e\u51fa\u53bb\u5bdf\u770b\u519b\u60c5\u7684\u4e00\u4e2a\u53c2\u5c06\uff0c\u8fde \u5fd9\u95ee\u9053\uff1a\u201c\u5916\u9762\u6253\u5f97\u600e\u4e48\u6837\u4e86\uff1f\u201d\u90a3\u53c2\u5c06\u9053\uff1a\u201c\u4e1c\u897f\u4e24\u9762\u57ce\u95e8\u5df2\u7ed9\u2018\u8d3c\u5175\u2019\u653b\u7834\uff0c\u5f20\u3001\u5b5f\u4e24 \u4f4d\u7edf\u9886\u8bf7\u5c06\u519b\u51fa\u53bb\u4eb2\u81ea\u7763\u5e08\uff0c\u5426\u5219\u6050\u6015\u662f\u5b88\u4e0d\u4f4f\u4e86\u3002\u201d \u3000\u3000\u5e05\u76c2\u96c4\u4e2d\u4e86\u6bd2\u9488\uff0c\u4ed7\u7740\u6df1\u539a\u7684\u5185\u529f\u652f\u6301\u4e86\u8fd9\u8bb8\u591a\u65f6\u5019\uff0c\u5df2\u7ecf\u662f\u5143\u6c14\u5927\u4f24\u3002\u8d3a\u5927\u5a18\u4e00\u6b7b\uff0c \u89e3\u836f\u53c8\u5df2\u65e0\u671b\u3002\u6b64\u65f6\u4ed6\u6b63\u662f\u610f\u4e71\u5fc3\u70e6\uff0c\u5f3a\u81ea\u652f\u6301\uff0c\u54ea\u91cc\u8fd8\u6709\u7cbe\u795e\u53bb\u4eb2\u81ea\u7763\u5e08\uff1f\u4f46\u4e49\u519b\u4e1a\u5df2\u8fdb \u57ce\uff0c\u6307\u987e\u4e4b\u95f4\u5c31\u8981\u6253\u6765\uff0c\u5b88\u5728\u5c06\u519b\u5e9c\u4e5f\u4e0d\u662f\u529e\u6cd5\u3002\u5f53\u4e0b\u5f3a\u4f5c\u9547\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u9a6c\u4e0a\u51fa\u53bb \u7763\u519b\u3002\u5b89\u526f\u5c06\uff0c\u4f60\u628a\u5e9c\u4e2d\u7684\u5f13\u7bad\u624b\u5c3d\u90fd\u8c03\u6765\uff0c\u53eb\u4ed6\u4eec\u6362\u4e0a\u6bd2\u7bad\uff0c\u5c06\u8fd9\u51e0\u4e2a\u5c0f\u8d3c\u4e71\u7bad\u5c04\u6740\uff01\u201d \u3000\u3000\u5b89\u4fca\u5ead\u9053\uff1a\u201c\u4e0d\u6015\u8bef\u4f24\u4e86\u53f2\u767d\u90fd\u548c\u6587\u9053\u5e84\u5417\uff1f\u201d\u5e05\u5b5f\u96c4\u6012\u9053\uff1a\u201c\u54ea\u8fd8\u987e\u5f97\u4e86\u8fd9\u4e48\u591a\uff01\u201d \u8981\u77e5\u4ed6\u6068\u91d1\u9010\u6d41\u5982\u540c\u523a\u9aa8\uff0c\u5b81\u53ef\u7389\u77f3\u4ff1\u711a\uff0c\u4e5f\u51b3\u4e0d\u80fd\u5bb9\u5fcd\u91d1\u9010\u6d41\u4e0e\u53f2\u7ea2\u82f1\u7ed3\u6210\u8fde\u7406\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u53e3\u91cc\u8bf4\u662f\u201c\u7763\u519b\u201d\uff0c\u5fc3\u4e2d\u5374\u5728\u60f3\u9053\uff1a\u201c\u5317\u95e8\u672a\u7834\uff0c\u8d81\u7740\u6df7\u6218\u4e4b\u9645\uff0c\u6211\u4e14\u626e\u4f5c\u4e00\u4e2a \u5c0f\u5175\u9003\u8d70\u3002\u6211\u4e2a\u5b89\u5168\u7684\u5730\u65b9\u7597\u4f24\uff0c\u6027\u547d\u8981\u7d27\uff0c\u529f\u540d\u5bcc\u8d35\u4e0d\u8981\u4e5f\u7f62\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u53ae\u6740\u4e4b\u58f0\u5df2\u662f\u8d8a\u6765\u8d8a\u8fd1\uff0c\u770b\u60c5\u5f62\u4f3c\u4e4e\u662f\u5df2\u5728\u8fdb\u884c\u5df7\u6218\u3002\u5e05\u5b5f\u96c4\u84e6\u5730\u6293 \u7740\u4e00\u4e2a\u536b\u5175\uff0c\u5265\u4e0b\u4ed6\u7684\u53f7\u8863\uff0c\u62ab\u5728\u81ea\u5df1\u7684\u8eab\u4e0a\uff0c\u5306\u5306\u4fbf\u8dd1\u3002 \u3000\u3000\u521a\u8de8\u51fa\u9662\u95e8\uff0c\u8fce\u9762\u6765\u4e86\u4e00\u4e2a\u8eab\u6750\u9b41\u4f1f\uff0c\u9aef\u987b\u5982\u621f\u7684\u8001\u8005\uff0c\u4e24\u4eba\u6253\u4e86\u4e00\u4e2a\u7167\u9762\uff0c\u90a3\u8001\u8005\u731b \u5730\u559d\u9053\uff1a\u201c\u5e05\u5b5f\u96c4\uff0c\u5403\u6211\u4e00\u638c\u3002\u201d\u8fd9\u8001\u8005\u6b63\u662f\u53d7\u8fc7\u4ed6\u4e00\u638c\u4e4b\u4f24\u7684\u4e0a\u5b98\u6cf0\u3002 \u3000\u3000\u53cc\u638c\u76f8\u4ea4\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u5e05\u5b5f\u96c4\u5012\u8dc3\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u201c\u54c7\u201d\u7684\u53c8\u662f\u4e00\u53e3\u8840\u5410\u4e86\u51fa\u6765\u3002 \u4e0a\u5b98\u6cf0\u4e0d\u77e5\u4ed6\u4e1a\u5df2\u5143\u6c14\u5927\u4f24\uff0c\u4e00\u638c\u5c06\u4ed6\u9707\u9000\uff0c\u5012\u662f\u9887\u611f\u610f\u5916\u3002\u6115\u4e86\u4e00\u6115\uff0c\u5e05\u5b5f\u96c4\u94bb\u8fdb\u4e71\u519b\u4e4b \u4e2d\uff0c\u51fa\u4e86\u9662\u95e8\u3002 \u3000\u3000\u8463\u5341\u4e09\u5a18\u4e0d\u77e5\u4e0a\u5b98\u6cf0\u7684\u5389\u5bb3\uff0c\u559d\u9053\uff1a\u201c\u54ea\u91cc\u6765\u7684\u8fd9\u4e2a\u8001\u5339\u592b\uff1f\u201d\u98de\u8eab\u6251\u4e0a\uff0c\u4e00\u62db\u201c\u56de\u98ce \u626b\u67f3\u201d\uff0c\u957f\u97ad\u6325\u821e\uff0c\u4fbf\u5411\u4e0a\u5b98\u6cf0\u6253\u6765\u3002 \u3000\u3000\u4f55\u5f69\u51e4\u53eb\u9053\uff1a\u201c\u8fd9\u5996\u5987\u6700\u4e3a\u53ef\u6076\uff0c\u4e0a\u5b98\u524d\u8f88\uff0c\u4e0d\u80fd\u9976\u5979\uff01\u201d\u4e0a\u5b98\u6cf0\u9053\uff1a\u201c\u662f\u5417\uff1f\u201d\u53e3\u4e2d \u8bf4\u8bdd\uff0c\u5de6\u624b\u4e00\u6284\uff0c\u5df2\u662f\u63e1\u7740\u97ad\u68a2\uff0c\u5728\u638c\u65a9\u4e0b\uff0c\u957f\u97ad\u65ad\u4e3a\u4e24\u622a\uff0c\u8463\u5341\u4e09\u5a18\u8dcc\u4e86\u4e2a\u56db\u811a\u671d\u5929\u3002\u674e \u6566\u3001\u4f55\u5f69\u51e4\u4e24\u67c4\u98de\u5200\u540c\u65f6\u63b7\u51fa\uff0c\u767b\u65f6\u53d6\u4e86\u5979\u7684\u6027\u547d\u3002\u674e\u6566\u5728\u516d\u5408\u5e2e\u4e4b\u65f6\uff0c\u66fe\u53d7\u8fc7\u5979\u4e0d\u5c11\u7684 \u6c14\uff0c\u6b64\u65f6\u65b9\u5f97\u6cc4\u6124\u3002\u5706\u6d77\u89c1\u8463\u5341\u4e09\u5a18\u6bd9\u547d\uff0c\u5413\u5f97\u626d\u5934\u4fbf\u8dd1\u3002 \u3000\u3000\u5c06\u519b\u5e9c\u4e2d\u672c\u662f\u597d\u624b\u5982\u4e91\uff0c\u4f46\u6b64\u9645\u4eba\u5fc3\u614c\u4e71\uff0c\u81ea\u987e\u4e0d\u6687\uff0c\u54ea\u8fd8\u80af\u4e3a\u5e05\u5b5f\u96c4\u5356\u547d\uff1f\u5b89\u4fca\u5ead\u8d39 \u5c3d\u529b\u6c14\uff0c\u624d\u627e\u5f97\u5341\u51e0\u540d\u5f13\u7bad\u624b\uff0c\u53eb\u4ed6\u4eec\u8eb2\u5728\u5047\u5c71\u4e0a\u653e\u7bad\u3002 \u3000\u3000\u53f2\u767d\u90fd\u5927\u6012\u559d\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u8fde\u6211\u4f60\u4eec\u4e5f\u8981\u5c04\u6740\u4e86\uff01\u201d\u5012\u63d0\u72ec\u811a\u94dc\u4eba\uff0c\u8dc3\u4e0a\u5047\u5c71\uff0c\u6b63 \u8981\u627e\u5b89\u4fca\u5ead\u7b97\u5e10\uff0c\u5ffd\u5730\u91cc\u4e00\u9635\u9aa4\u96e8\u822c\u5730\u6697\u5668\u6253\u6765\uff0c\u4f46\u8fd9\u4e9b\u6697\u5668\u5374\u4e0d\u662f\u5411\u5047\u5c71\u4e0b\u9762\u7684\u91d1\u9010\u6d41\u5c04 \u53bb\u7684\uff0c\u53ea\u542c\u5f97\u201c\u54ce\u54df\uff0c\u54ce\u54df\uff01\u201d\u201c\u4e0d\u597d\u4e86\uff0c\u4e0d\u597d\u4e86\uff01\u201d\u7684\u60ca\u53eb\u4e4b\u58f0\u56db\u8d77\uff0c\u90a3\u5341\u51e0\u540d\u5f13\u7bad\u624b\u90fd \u7740\u4e86\u6697\u5668\uff0c\u6eda\u4e0b\u5047\u5c71\u3002 \u3000\u3000\u53ea\u89c1\u5899\u5934\u5c4b\u9876\uff0c\u4e1c\u9762\u51e0\u4e2a\uff0c\u897f\u9762\u51e0\u4e2a\u51e0\u6761\u4eba\u5f71\u6377\u5982\u98de\u9e1f\u822c\u7684\u6251\u4e0b\u6765\uff0c\u4ed6\u4eec\u662f\uff1a\u5b87\u6587\u96c4\u3001 \u6c5f\u6653\u8299\u3001\u674e\u5149\u590f\u3001\u6797\u9053\u8f69\u3001\u4e0a\u5b98\u7ea8\u3001\u7afa\u6e05\u534e\u2014\u2014\u8fd9\u4e09\u5bf9\u592b\u59bb\u662f\u6c5f\u6d77\u5929\u7684\u95e8\u4eba\u5f1f\u5b50\u3002\u5973\u5a7f\u3001\u5973 \u513f\u3002\u2014\u2014\u8fd8\u6709\u9648\u5149\u9634\u3001\u77f3\u971e\u59d1\u548c\u79e6\u5143\u6d69\u3001\u5c01\u5999\u5ae6\u4e24\u5bf9\u60c5\u4fa3\u3002 \u3000\u3000\u4e49\u519b\u5927\u961f\u672a\u81f3\uff0c\u4f46\u8fd9\u5341\u51e0\u4e2a\u4eba\u5982\u98de\u5c06\u519b\u7684\u4ece\u5929\u800c\u964d\uff0c\u5df2\u662f\u8db3\u4ee5\u5927\u5bd2\u654c\u670d\uff1b\u4e0a\u5b98\u6cf0\u559d\u9053\uff1a \u201c\u4e49\u519b\u5df2\u7ecf\u7834\u57ce\uff0c\u8981\u6027\u547d\u7684\u5feb\u5feb\u6295\u964d\u3002\u201d\u5c06\u519b\u5e9c\u7684\u536b\u571f\u6709\u7684\u9003\u8dd1\uff0c\u6709\u7684\u4f24\u4ea1\uff0c\u8fd8\u6709\u66f4\u591a\u7684\u4eba \u81ea\u5fd6\u987d\u6297\u65e0\u76ca\uff0c\u4e56\u4e56\u7684\u542c\u8bdd\u7684\u653e\u4e0b\u4e86\u6b66\u5668\u3002\u5e05\u76c2\u96c4\u5ef6\u63fd\u6765\u7684\u90a3\u4e9b\u597d\u624b\uff0c\u5219\u4f3c\u6ca1\u5934\u82cd\u8747\u7684\u5230\u5904 \u4e71\u94bb\u3002 \u3000\u3000\u53f2\u767d\u90fd\u62a1\u8d77\u72ec\u811a\u94dc\u4eba\uff0c\u6cfc\u98ce\u822c\u5730\u6253\u5c06\u51fa\u53bb\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u8981\u547d\u7684\u5c31\u6765\u9001\u6b7b\uff01\u201d\u5b87\u6587\u96c4\u9053\uff1a \u201c\u597d\uff0c\u4f60\u662f\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\u662f\u5417\uff1f\u6211\u5012\u8981\u770b\u770b\u4f60\u6709\u4ec0\u4e48\u5389\u5bb3\uff1f\u201d\u5251\u8eab\u5408\u4e00\uff0c\u4e00\u9053\u9752\u5149\u6eda\u6765\uff0c \u201c\u55d6\u201d\u4e00\u58f0\uff0c\u9752\u94a2\u5251\u5288\u5728\u94dc\u4eba\u4e4b\u4e0a\uff0c\u53ea\u89c9\u864e\u53e3\u9178\u9ebb\uff0c\u5b87\u6587\u96c4\u8eab\u5f62\u4e00\u6643\uff0c\u7acb\u5373\u53c8\u662f\u4e00\u62db\u201c\u9189\u516b \u4ed9\u201d\u7684\u62db\u5f0f\uff0c\u659c\u8eab\u8fdb\u5251\u3002 \u3000\u3000\u53f2\u767d\u90fd\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u8fd9\u5c0f\u5b50\u5c45\u7136\u6ca1\u6709\u8dcc\u5012\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u7afa\u6e05\u534e\u7684\u957f\u5251 \u523a\u5230\u4e86\u4ed6\u7684\u80cc\u540e\u3002\u8fd9\u4e00\u5251\u8f7b\u7075\u8fc5\u6377\uff0c\u59d4\u5b9e\u662f\u4e00\u62db\u6781\u7cbe\u5999\u7684\u5251\u6cd5\u3002\u53f2\u767d\u90fd\u4e00\u4e2a\u201c\u65f1\u5730\u62d4\u8471\u201d\uff0c \u7a9c\u8d77\u4e03\u516b\u5c3a\u9ad8\uff0c\u94dc\u4eba\u51fb\u4e0b\uff0c\u7afa\u6e05\u534e\u7ed9\u94dc\u4eba\u51fb\u4e0b\u7684\u90a3\u80a1\u52b2\u51e4\u4e00\u649e\uff0c\u80f8\u53e3\u597d\u4f3c\u53d7\u4e86\u4e00\u9524\uff0c\u51e0\u4e4e\u900f \u4e0d\u8fc7\u6c14\uff0c\u77e5\u9053\u5389\u5bb3\uff0c\u8eab\u5f62\u98d8\u95ea\u53f2\u767d\u90fd\u7684\u94dc\u4eba\u51fb\u4e86\u4e2a\u7a7a\uff0c\u4e0d\u7981\u53c8\u662f\u4e00\u60ca\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u674e\u5149\u590f\uff0c\u6797\u9053\u8f69\u5de6\u53f3\u9f50\u4e0a\uff0c\u4e00\u5200\u4e00\u5251\uff0c\u5168\u90fd\u653b\u5411\u53f2\u767d\u90fd\u7684\u4e0b\u76d8\uff0c\u5200\u81c2 \u8154\u9aa8\uff0c\u5251\u523a\u819d\u76d6\u3002\u53f2\u767d\u90fd\u7684\u94dc\u4eba\u662f\u91cd\u5175\u5668\u3002\u4e0d\u591f\u7075\u6d3b\uff0c\u4e0b\u76d8\u6b63\u662f\u5f31\u70b9\u3002\u53f2\u767d\u90fd\u53c8\u662f\u4e00\u60ca\uff1a \u201c\u8fd9\u4e24\u4e2a\u5c0f\u9b3c\u7684\u773c\u5149\u5012\u662f\u5389\u5bb3\u3002\u201d\u53cc\u8db3\u8fde\u73af\u8e22\u51fa\uff0c\u6797\u9053\u8f69\u957f\u5251\u7ed9\u4ed6\u8e22\u98de\uff0c\u674e\u5149\u590f\u6c14\u529b\u8f83\u5927\uff0c \u4e00\u5200\u65ab\u7740\u4ed6\u7684\u978b\u8ddf\uff0c\u7ed9\u4ed6\u9707\u9000\u6570\u6b65\u3002\u8fd9\u4e00\u62db\u56e0\u4e3a\u662f\u4ee5\u529b\u78b0\u529b\uff0c\u53f2\u767d\u90fd\u978b\u8ddf\u7ed9\u5200\u5c16\u5212\u7834\uff0c\u7acb\u5373 \u4fbf\u5c06\u4ed6\u9707\u9000\uff0c\u662f\u4ee5\u6ca1\u6709\u53d7\u4f24\uff0c\u4f46\u53f2\u767d\u90fd\u4ea6\u5df2\u662f\u5403\u60ca\u4e0d\u5c0f\uff0c\u5fc3\u60f3\uff1a\u201c\u54ea\u91cc\u6765\u7684\u8fd9\u51e0\u4e2a\u5c0f\u9b3c\u5934\uff0c \u7adf\u7136\u4e00\u4e2a\u6bd4\u4e00\u4e2a\u5389\u5bb3\uff01\u201c\u82e5\u4e0d\u5feb\u8d70\uff0c\u53ea\u6015\u662f\u8981\u5728\u9634\u6c9f\u91cc\u7ffb\u8239\u4e86\u3002\u201d\u539f\u6765\u53f2\u767d\u90fd\u5728\u4e0e\u91d1\u9010\u6d41\u6076 \u6597\u4e4b\u540e\uff0c\u529f\u529b\u5df2\u4e0d\u53ca\u539f\u6765\u7684\u4e00\u534a\uff0c\u5b87\u6587\u96c4\u7b49\u4eba\u5374\u662f\u751f\u529b\u519b\uff0c\u8fd9\u624d\u80fd\u591f\u548c\u4ed6\u52c9\u5f3a\u62b5\u654c\u7684\u3002 \u3000\u3000\u674e\u5149\u590f\u53eb\u9053\uff1a\u201c\u5c0f\u5e08\u53d4\uff0c\u5c0f\u5e08\u53d4\uff01\u201d\u4ed6\u548c\u91d1\u9010\u6d41\u611f\u60c5\u6700\u597d\uff0c\u4e00\u522b\u5e74\u4f59\uff0c\u5bf9\u8fd9\u201c\u5c0f\u5e08\u53d4\u201d \u5341\u5206\u6302\u5ff5\u3002\u6b64\u9645\u4ed6\u5c1a\u672a\u77e5\u91d1\u9010\u6d41\u662f\u5426\u5e73\u5b89\uff0c\u662f\u4ee5\u6025\u4e8e\u60f3\u4e0e\u91d1\u9010\u6d41\u89c1\u9762\u3002 \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u91d1\u9010\u6d41\u5e94\u9053\uff1a\u201c\u6765\u5566\uff01\u201d\u4e00\u4eba\u5e94\u58f0\uff0c\u5374\u6709\u4e24\u6761\u4eba\u5f71\u817e\u7a7a\u98de \u8d77\uff0c\u4e00\u4e2a\u4ece\u5047\u5c71\u4fa7\u9762\u63a0\u8fc7\uff0c\u4e00\u4e2a\u8dc3\u4e0a\u5047\u5c71\u3002\u8dc3\u4e0a\u5047\u5c71\u7684\u662f\u91d1\u9010\u6d41\uff0c\u63a0\u8fc7\u5047\u5c71\u7684\u5374\u662f\u6587\u9053\u5e84\u3002 \u3000\u3000\u6587\u9053\u5e84\u65e0\u5fc3\u604b\u6218\uff0c\u907f\u5f00\u4e86\u5b87\u6587\u96c4\u8fd9\u73ed\u5c0f\u8c6a\u6770\uff0c\u4fbf\u53bb\u627e\u5bfb\u513f\u5b50\u3002\u6b64\u65f6\u5c06\u519b\u5e9c\u7684\u8d3a\u5ba2\u548c\u536b\u58eb \u6216\u6b7b\u6216\u4f24\uff0c\u5269\u4e0b\u6765\u7684\u4e5f\u5927\u90fd\u9003\u4e86\u3002\u6587\u9053\u5e84\u6e38\u76ee\u56db\u987e\uff0c\u4e0d\u4e45\u5c31\u53d1\u73b0\u4e86\u6587\u80dc\u4e2d\uff0c\u6587\u80dc\u4e2d\u6b63\u5728\u7ed9\u4e00 \u4e2a\u5973\u5b50\u6740\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u8863\u88f3\u6ee1\u662f\u8840\u6c61\u3002 \u3000\u3000\u8fd9\u4e2a\u5973\u5b50\u662f\u5c01\u5999\u5ae6\u3002 \u3000\u3000\u539f\u6765\u548c\u6587\u80dc\u4e2d\u540c\u5728\u4e00\u8d77\u7684\u9f9a\u5e73\u91ce\u5148\u81ea\u9003\u4e86\uff0c\u6587\u80dc\u4e2d\u8eab\u4e0a\u53d7\u4f24\uff0c\u751f\u6015\u6b7b\u5728\u4e71\u5175\u4e4b\u4e2d\uff0c\u53ea\u597d \u52c9\u529b\u6323\u624e\uff0c\u722c\u8d77\u6765\u60f3\u627e\u4e2a\u5730\u65b9\u8eb2\u907f\u3002\u4ed6\u8fd8\u672a\u94bb\u5165\u5c71\u6d1e\uff0c\u5c31\u78b0\u4e0a\u4e86\u5c01\u5999\u5ae6\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u6068\u4ed6\u5f80\u65e5\u903c\u5a5a\u4e4b\u8fb1\uff0c\u4f46\u4e5f\u5e76\u65e0\u4e00\u5b9a\u5c31\u8981\u6740\u4ed6\u4e4b\u5fc3\uff0c\u6587\u80dc\u4e2d\u81ea\u5df1\u60ca\u614c\uff0c\u4e00\u89c1\u4e86\u5979\u5c31 \u53e3\u4e0d\u62e9\u8a00\u5730\u8bf4\u9053\uff1a\u201c\u5ae6\u59b9\uff0c\u8bf7\u5ff5\u5728\u6614\u65e5\u4e4b\u60c5\uff0c\u653e\u6211\u8fc7\u53bb\u5427\uff01\u201d\u5728\u4ed6\u4ee5\u4e3a\u8fd9\u662f\u6c42\u60c5\uff0c\u5728\u5c01\u5999\u5ae6 \u5ae6\u662f\u52fe\u8d77\u4e86\u65e7\u6068\u3002\u5c01\u5999\u5ae6\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u6709\u4ec0\u4e48\u60c5\uff1f\u54fc\uff0c\u6211\u82e5\u653e\u4f60\u8fc7\u53bb\uff0c\u5012\u663e\u5f97\u662f\u6211\u884c\u4e3a \u4e0d\u7aef\u4e86\uff01\u201d\u4e0d\u7531\u5206\u8bf4\uff0c\u5530\u7684\u4fbf\u662f\u4e00\u5251\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u4e1a\u5df2\u53d7\u4f24\uff0c\u5982\u4f55\u80fd\u662f\u5c01\u5999\u5ae6\u7684\u5bf9\u624b\uff1f\u4e0d\u8fc7\u6570\u62db\uff0c\u5c01\u5999\u5ae6\u4e00\u5251\u523a\u7740\u4ed6\u7684\u624b\u8155\uff0c\u53ea\u542c \u5f97\u201c\u55d6\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u6587\u80dc\u4e2d\u624b\u4e2d\u7684\u957f\u5251\u8131\u624b\uff0c\u98de\u4e0a\u7a7a\u4e2d\uff0c\u4f46\u4ed6\u8eab\u4e0a\u7684\u8840\u6c41\uff0c\u5374\u662f\u521a\u624d\u53d7\u4e86\u516c \u5b59\u71d5\u7684\u5251\u4f24\u6240\u81f3\u3002 \u3000\u3000\u6587\u9053\u5e84\u559d\u9053\uff1a\u201c\u4f11\u5f97\u4f24\u5bb3\u6211\u513f\uff01\u201d\u58f0\u672a\u5230\uff0c\u638c\u5148\u53d1\uff0c\u4e00\u8bb0\u5288\u7a7a\u638c\u628a\u6587\u80dc\u4e2d\u98de\u4e0a\u7a7a\u4e2d\u7684\u90a3 \u67c4\u957f\u5251\u6539\u4e86\u4e00\u4e2a\u65b9\u5411\uff0c\u6d41\u661f\u95ea\u7535\u822c\u7684\u5411\u5c01\u5999\u5ae6\u80cc\u540e\u7684\u79e6\u5143\u6d69\u5c04\u53bb\uff0c\u8fc5\u5373\u5954\u5230\uff0c\u5de6\u624b\u62b1\u4e86\u513f \u5b50\uff0c\u53f3\u624b\u7684\u8f6f\u5251\u5df2\u662f\u6296\u5f97\u7b14\u76f4\uff0c\u653b\u5411\u5c01\u5999\u5ae6\u3002 \u3000\u3000\u53d1\u638c\u3001\u6551\u5b50\u3001\u653b\u654c\uff0c\u4e09\u4e2a\u52a8\u4f5c\u4e00\u6c14\u5475\u6210\uff0c\u7684\u786e\u4e0d\u6127\u662f\u6b66\u5b66\u540d\u5bb6\u7684\u8eab\u624b\u3002\u6587\u9053\u5e84\u867d\u7136\u521a\u521a \u662f\u7ecf\u8fc7\u4e00\u573a\u6076\u6597\uff0c\u4f46\u529f\u529b\u4e4b\u9ad8\uff0c\u4ecd\u662f\u8fdc\u5728\u5c01\u3001\u79e6\u4e8c\u4eba\u4e4b\u4e0a\u3002 \u3000\u3000\u5e78\u4e8f\u79e6\u5143\u6d69\u5df1\u5f97\u6b66\u5f53\u6d3e\u7684\u5185\u529f\u5fc3\u6cd5\uff0c\u5f53\u4e0b\u957f\u5251\u4e00\u5708\uff0c\u6d88\u4e86\u90a3\u67c4\u9752\u94a2\u5251\u98de\u6765\u7684\u52b2\u9053\uff0c\u4fa7\u8eab \u4e00\u95ea\uff0c\u90a3\u67c4\u9752\u94a2\u5251\u63d2\u5728\u5730\u4e0a\u5251\u67c4\u5140\u81ea\u98a4\u52a8\u4e0d\u4f11\uff01 \u3000\u3000\u79e6\u5143\u6d69\u5316\u89e3\u4ed6\u63b7\u6765\u7684\u4e00\u5251\u8f83\u6613\uff0c\u5c01\u5999\u5ae6\u8981\u62b5\u6321\u4ed6\u4ece\u624b\u4e2d\u523a\u51fa\u7684\u4e00\u5251\uff0c\u53ef\u5c31\u96be\u5f97\u591a\u4e86\u3002\u53ea \u89c1\u5251\u5149\u95ea\u5904\uff0c\u5c01\u5999\u5ae6\u7684\u534a\u622a\u8863\u8896\u5df2\u662f\u5316\u4f5c\u7247\u7247\u8774\u8776\uff0c\u968f\u98ce\u98de\u8d77\u3002\u79e6\u5143\u6d69\u8d76\u5230\uff0c\u4e00\u62db\u201c\u767d\u8679\u8d2f \u65e5\u201d\u5411\u4ed6\u62b1\u7740\u7684\u6587\u80dc\u4e2d\u523a\u53bb\u3002\u6587\u9053\u5e84\u903c\u5f97\u56de\u5251\u906e\u62e6\uff0c\u5c01\u5999\u5ae6\u8fd9\u624d\u5f97\u4ee5\u8131\u79bb\u9669\u5883\u3002 \u3000\u3000\u6587\u80dc\u4e2d\u60ca\u9b42\u7a0d\u8d70\uff0c\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u4f60\u66ff\u6211\u628a\u8fd9\u5c0f\u5b50\u6740\u4e86\uff01\u201d \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u7978\u798f\u65e0\u95e8\u552f\u81ea\u62db\uff1f\u6b7b\u5230\u4e34\u5934\u5c1a\u4e0d\u77e5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e09\u5341\u516b\u56de\u3000\u4f17\u53db\u4eb2\u79bb\u7ec8\u81ea\u6bd9\u3000\u4eba\u4ea1\u57ce\u5931\u53f9\u9014\u7a77 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e09\u5341\u516b\u56de\u3000\u4f17\u53db\u4eb2\u79bb\u7ec8\u81ea\u6bd9\u3000\u4eba\u4ea1\u57ce\u5931\u53f9\u9014\u7a77 \u3000\u3000\u6587\u80dc\u4e2d\u58f0\u98a4\u6c14\u4fc3\uff0c\u663e\u7136\u662f\u4f24\u5f97\u4e0d\u8f7b\uff0c\u6587\u9053\u5e84\u4e5f\u4e0d\u77e5\u80fd\u5426\u4fdd\u5f97\u4f4f\u513f\u5b50\u7684\u6027\u547d\uff0c\u5fc3\u4e2d\u4e00\u75db\uff0c \u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u66ff\u4f60\u4e86\u8fd9\u5fc3\u613f\uff01\u201d\u58f0\u51fa\u62db\u53d1\uff0c\u90a3\u628a\u767e\u70bc\u7cbe\u94a2\u7684\u8f6f\u5251\u5212\u51fa\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u5c06\u79e6\u5143 \u6d69\u4e0e\u5c01\u5999\u5ae6\u90fd\u56f4\u5728\u5f27\u5f62\u5708\u5185\u3002 \u3000\u3000\u53e6\u4e00\u8fb9\u9648\u5149\u7167\u4e5f\u78b0\u4e0a\u4e86\u4ec7\u4eba\uff0c\u2014\u2014\u516d\u5408\u5e2e\u56db\u5927\u9999\u4e3b\u4e4b\u4e2d\u7855\u679c\u4ec5\u5b58\u7684\u5706\u6d77\u548c\u5c1a\uff0c\u4e24\u5e74\u524d\u5706 \u6d77\u5728\u5180\u9c81\u9053\u4e0a\u52ab\u6740\u5ba2\u5546\uff0c\u6070\u503c\u9648\u5149\u7167\u8def\u8fc7\uff0c\u4e8c\u4eba\u4ea4\u624b\uff0c\u5706\u6d77\u7ed9\u9648\u5149\u7167\u523a\u4e86\u4e00\u5251\uff0c\u9648\u5149\u7167\u4e5f\u7ed9 \u4ed6\u98de\u51fa\u7684\u6bd2\u5315\u9996\u6240\u4f24\uff0c\u9669\u4e9b\u9001\u4e86\u6027\u547d\u3002 \u3000\u3000\u9648\u5149\u7167\u9047\u4e0a\u4ec7\u4eba\uff0c\u7109\u80fd\u653e\u4ed6\u8fc7\u53bb\uff1f\u4e00\u58f0\u53f1\u54a4\uff0c\u9752\u94a2\u5251\u5316\u4f5c\u4e00\u9053\u94f6\u8679\uff0c\u5377\u5c06\u8fc7\u53bb\u3002\u5706\u6d77\u6212 \u5200\u4e00\u7acb\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u5200\u5251\u76f8\u4ea4\uff0c\u9648\u5149\u7167\u7684\u5251\u5c16\u987a\u7740\u4e00\u8361\u4e4b\u52bf\u659c\u98de\uff0c\u5706\u6d77\u9661\u7136\u95f4\u53ea\u89c9\u80a9\u5934\u4e00 \u75db\uff0c\u5df2\u662f\u7740\u4e86\u4e00\u5251\u3002\u8bba\u7406\u4e24\u4eba\u7684\u672c\u9886\u76f8\u5dee\u5e76\u4e0d\u592a\u8fdc\uff0c\u5706\u6d77\u7eb5\u7136\u8f83\u5f31\uff0c\u4e5f\u4e0d\u8be5\u5728\u89c1\u9762\u7b2c\u4e00\u62db\u4fbf \u7ed9\u9648\u5149\u7167\u523a\u4f24\u7684\uff0c\u53ea\u56e0\u4ed6\u5728\u8463\u5341\u4e09\u5a18\u60e8\u906d\u8bdb\u622e\u4e4b\u540e\uff0c\u65e9\u5df2\u662f\u610f\u4e71\u5fc3\u614c\uff0c\u9648\u5149\u7167\u5219\u662f\u84c4\u610f\u62a5 \u4ec7\uff0c\u4e00\u7167\u9762\u5c31\u4f7f\u51fa\u4e86\u7edd\u5999\u7684\u6740\u624b\uff01 \u3000\u3000\u5251\u4ece\u4e2d\u8def\u523a\u6765\uff0c\u5ffd\u5730\u80a9\u5934\u4e2d\u5251\uff0c\u8fd9\u4e00\u4e0b\u5927\u51fa\u5706\u6d77\u610f\u6599\u4e4b\u5916\uff01\u5706\u6d77\u5fc3\u80c6\u4ff1\u5bd2\uff0c\u593a\u8def\u800c\u9003\uff0c \u9648\u5149\u7167\u65bd\u5c55\u8fde\u73af\u6740\u624b\uff0c\u8ffd\u4e0a\u53bb\u5530\u5530\u6dae\u75be\u523a\u4e09\u5251\uff0c\u7b2c\u4e09\u5251\u5706\u6d77\u5df2\u662f\u8eb2\u4e0d\u8fc7\u53bb\uff0c\u80cc\u5fc3\u7684\u5927\u690e\u7a74\u4e2d \u5251\uff0c\u4e00\u6761\u6027\u547d\u767b\u65f6\u4e86\u7ed3\u3002 \u3000\u3000\u7ad9\u5728\u4e00\u65c1\u66ff\u9648\u5149\u7167\u63a0\u9635\u7684\u77f3\u971e\u59d1\uff0c\u6b64\u65f6\u5df2\u770b\u6e05\u695a\u4e86\u5404\u65b9\u6df7\u6218\u7684\u5f62\u52bf\uff0c\u8bf4\u9053\uff1a\u201c\u5b87\u6587\u96c4\u4ed6 \u4eec\u56f4\u653b\u53f2\u767d\u90fd\uff0c\u6709\u60ca\u65e0\u9669\uff0c\u201c\u79e6\u5143\u6d69\u548c\u5c01\u5999\u5ae6\u5200\u654c\u6587\u9053\u5e84\uff0c\u53ea\u6015\u4f1a\u6709\u6027\u547d\u4e4b\u5fe7\uff01\u201d\u9648\u5149\u7167 \u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u5feb\u53bb\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u53ea\u9053\u4e09\u62db\u4e24\u5f0f\u5c31\u53ef\u4ee5\u53d6\u4e86\u79e6\u5143\u6d69\u7684\u6027\u547d\uff0c\u54ea\u5374\u79e6\u5143\u6d69\u7684\u672c\u9886\u867d\u7136\u8fdc\u4e0d\u5982\u4ed6\uff0c\u5374\u4e5f \u4e0d\u662f\u4ed6\u5728\u5341\u62db\u4e4b\u5185\u6240\u80fd\u6253\u53d1\uff0c\u6b64\u65f6\u9648\u5149\u7167\u3001\u77f3\u971e\u59d1\u5df2\u662f\u53cc\u53cc\u8d76\u5230\uff0c\u90a3\u4e00\u8fb9\uff0c\u91d1\u9010\u6d41\u4ea6\u5df2\u8dc3\u4e0a \u4e86\u5047\u5c71\uff0c\u53d1\u51fa\u4e86\u4e00\u58f0\u957f\u5578\u3002 \u3000\u3000\u6587\u9053\u5e84\u77e5\u9053\u9648\u5149\u7167\u662f\u6c5f\u5357\u5927\u4fa0\u9648\u5929\u5b87\u4e4b\u5b50\uff0c\u672c\u9886\u4e4b\u5f3a\u66f4\u5728\u79e6\u5143\u6d69\u4e4b\u4e0a\uff0c\u8fd8\u6709\u4e00\u4e2a\u77f3\u971e\u59d1 \u64c5\u4e8e\u4f7f\u6bd2\uff0c\u4e5f\u662f\u4e0d\u53ef\u5c0f\u89d1\u3002\u6587\u9053\u5e84\u867d\u7136\u4e0d\u6015\u4ed6\u4eec\uff0c\u4f46\u82e5\u4ed6\u4eec\u56db\u4eba\u8054\u624b\uff0c\u6587\u9053\u5e84\u60f3\u8981\u53d6\u80dc\uff0c\u53ef \u4e5f\u5e76\u4e0d\u5bb9\u6613\u3002\u4f55\u51b5\u91d1\u9010\u6d41\u5df2\u7ecf\u8131\u9669\uff0c\u5018\u82e5\u7ed9\u91d1\u9010\u6d41\u8ffd\u4e0a\uff0c\u540e\u679c\u4e0d\u582a\u60f3\u8c61\u3002 \u3000\u3000\u6587\u9053\u5e84\u6697\u6697\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u4e2d\u513f\uff0c\u4e0d\u662f\u4e3a\u7236\u4e0d\u60f3\u66ff\u4f60\u62a5\u4ec7\uff0c\u5b9e\u662f\u654c\u4eba\u592a\u5f3a\uff0c\u53ea\u80fd\u5148 \u4fdd\u4f60\u7684\u6027\u547d\u4e86\u3002\u201d\u5f53\u4e0b\u632f\u81c2\u4e00\u6325\uff0c\u4e00\u62db\u201c\u6597\u8f6c\u661f\u6a2a\u201d\uff0c\u628a\u79e6\u5143\u6d69\u3002\u5c01\u5999\u5ae6\u4e00\u540c\u903c\u9000\uff0c\u51b2\u4e86\u51fa \u53bb\u3002 \u3000\u3000\u9648\u5149\u7167\u6025\u4e8e\u6551\u53cb\uff0c\u4eba\u672a\u5230\u5df2\u662f\u628a\u624b\u4e00\u626c\uff0c\u53d1\u51fa\u4e86\u4e16\u4e0a\u65e0\u53cc\u7684\u6697\u5668\u201c\u51b0\u9b44\u795e\u5f39\u201d\u3002 \u3000\u3000\u201c\u51b0\u9b44\u795e\u5f39\u201d\u4e0e\u4efb\u4f55\u6697\u5668\u4e0d\u540c\uff0c\u662f\u4ed7\u7740\u4e07\u8f7d\u7384\u51b0\u7684\u90a3\u80a1\u5947\u5bd2\u4e4b\u6c14\u4f24\u4eba\u7684\u3002\u4f46\u6587\u9053\u5e84\u65e9\u5df2 \u7ec3\u6210\u4e86\u4e09\u8c61\u795e\u529f\uff0c\u51b0\u9b44\u795e\u5f39\u867d\u7136\u5389\u5bb3\uff0c\u4e5f\u8fd8\u662f\u96be\u5948\u4ed6\u4f55\u3002\u6587\u9053\u5e84\u4e00\u638c\u62cd\u51fa\uff0c\u51b0\u5f39\u7c89\u788e\uff0c\u5316\u4f5c \u4e86\u4e00\u56e2\u5bd2\u96fe\u3002 \u3000\u3000\u6587\u9053\u5e84\u51b7\u7b11\u9053\uff1a\u201c\u7c73\u7c92\u4e4b\u73e0\uff0c\u4e5f\u653e\u5149\u534e\uff01\u201d\u6d53\u96fe\u4e2d\u8eab\u5f62\u7a9c\u51fa\uff0c\u4f5c\u52bf\u6251\u5411\u77f3\u971e\u59d1\uff0c\u9648\u5149\u7167 \u8fde\u5fd9\u4e0a\u6765\u7b56\u5e94\uff0c\u54ea\u77e5\u6587\u9053\u5e84\u4e43\u662f\u58f0\u4e1c\u51fb\u897f\u4e4b\u8ba1\uff0c\u8bf1\u4ed6\u4eec\u4e8c\u4eba\u805a\u5728\u4e00\u8def\uff0c\u4ed6\u65e9\u5df2\u62b1\u7740\u513f\u5b50\uff0c\u4ece \u53e6\u4e00\u6761\u8def\u51b2\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u6587\u9053\u5e84\u51fa\u4e86\u5c06\u519b\u5e9c\uff0c\u5fc3\u91cc\u7a0d\u5bbd\uff0c\u91d1\u9010\u6d41\u5e76\u6ca1\u6709\u8ffd\u51fa\u6765\uff0c\u4ed6\u4ee5\u4e3a\u662f\u53ef\u4ee5\u8131\u9669\u4e86\uff0c\u6b63\u60f3\u5582\u4ed6 \u513f\u5b50\u5403\u836f\uff0c\u5ffd\u7136\u53d1\u89c9\u6587\u80dc\u4e2d\u7684\u8eab\u4f53\u5df2\u7ecf\u50f5\u786c\u3002\u539f\u6765\u9648\u5149\u7167\u6240\u53d1\u7684\u51b0\u9b44\u795e\u5f39\uff0c\u6587\u9053\u5e84\u867d\u7136\u4e0d \u60e7\uff0c\u4ed6\u7684\u513f\u5b50\u53ef\u662f\u7981\u53d7\u4e0d\u8d77\u3002\u6587\u59d3\u4e2d\u5047\u5982\u672a\u66fe\u53d7\u4f24\u7684\u8bdd\uff0c\u6216\u8bb8\u6587\u9053\u5e84\u65bd\u5c55\u7384\u529f\uff0c\u8fd8\u53ef\u4ee5\u633d\u6551 \u4ed6\u7684\u6027\u547d\uff1b\u53d7\u4f24\u4e4b\u540e\uff0c\u518d\u7ed9\u5947\u5bd2\u4e4b\u6c14\u4fb5\u5165\uff0c\u8840\u6db2\u767b\u65f6\u51b7\u51dd\uff0c\u5373\u4f7f\u6241\u9e4a\u91cd\u751f\uff0c\u534e\u4f26\u518d\u4e16\uff0c\u90a3\u4e5f \u662f\u56de\u5929\u4e4f\u672f\u7684\u4e86\u3002 \u3000\u3000\u6587\u9053\u5e84\u53d1\u89c9\u513f\u5b50\u5df2\u6b7b\uff0c\u5fc3\u4e2d\u4f24\u75db\u81ea\u662f\u96be\u4ee5\u5f62\u5bb9\uff0c\u4f46\u6b64\u9645\u4ed6\u5b64\u638c\u96be\u9e23\uff0c\u7109\u6562\u56de\u53bb\u62a5\u4ec7\uff0c\u53ea \u6709\u62b1\u4e86\u513f\u5b50\u7684\u5c38\u4f53\u5148\u9003\u547d\u4e86\u3002 \u3000\u3000\u4e14\u8bf4\u53f2\u767d\u90fd\u5728\u4e00\u73ed\u5c0f\u8c6a\u6770\u56f4\u653b\u4e4b\u4e0b\uff0c\u6325\u821e\u72ec\u811a\u94a2\u4eba\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u5012\u4e5f\u672a\u9732\u8d25 \u8c61\uff0c\u4f46\u867d\u7136\u5982\u6b64\uff0c\u5fc3\u5185\u4ea6\u5df2\u6697\u6697\u5403\u60ca\u3002\u4e0d\u4e45\uff0c\u5c31\u89c1\u5230\u6587\u9053\u5e84\u4ece\u5047\u5c71\u65c1\u8fb9\u63a0\u8fc7\uff0c\u91d1\u9010\u6d41\u5374\u8df3\u4e0a \u6765\u3002 \u3000\u3000\u53f2\u767d\u90fd\u66f4\u662f\u7740\u6025\uff0c\u201c\u6587\u9053\u5e84\u8fd9\u53ae\u771f\u4e0d\u662f\u4e2a\u4e1c\u897f\uff0c\u53ea\u987e\u81ea\u5df1\u9003\u547d\u3002\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c9\u5fae\u98ce \u98d2\u7136\uff0c\u4e00\u53e3\u660e\u6643\u6643\u7684\u5229\u5251\u500f\u7136\u95f4\u5c31\u6307\u5230\u4e86\u4ed6\u7684\u80f8\u819b\uff0c\u53f2\u767d\u90fd\u4e0d\u7981\u53c8\u662f\u4e00\u60ca\uff1a\u201c\u8fd9\u5973\u5a03\u513f\u7684\u5251 \u6cd5\u7adf\u5982\u6b64\u4e86\u5f97\uff01\u201d\u539f\u6765\u662f\u5b87\u6587\u96c4\u7684\u59bb\u5b50\uff0c\u6c5f\u6d77\u5929\u7684\u5973\u513f\u6c5f\u6653\u8299\u5230\u4e86\u3002\u6c5f\u6653\u8299\u81ea\u5c0f\u5f97\u7236\u4eb2\u4f20 \u6388\uff0c\u5bb6\u5b66\u6e0a\u6e90\uff0c\u62db\u6570\u7684\u7cbe\u5999\uff0c\u8fd8\u5728\u5b87\u6587\u96c4\u7b49\u4eba\u4e4b\u4e0a\uff0c\u4e0d\u4e9a\u4e8e\u91d1\u9010\u6d41\u3002 \u3000\u3000\u53f2\u767d\u90fd\u8fde\u5fd9\u541e\u80f8\u5438\u8179\uff0c\u8eab\u8eaf\u9661\u7136\u632a\u540e\u534a\u5c3a\uff0c\u9976\u662f\u4ed6\u5316\u89e3\u5f97\u5b9c\uff0c\u5de6\u80a9\u4ea6\u5df2\u7ed9\u6c5f\u6653\u8299\u7684\u5251\u5c16 \u5212\u7834\u3002 \u3000\u3000\u53f2\u767d\u90fd\u5927\u543c\u4e00\u58f0\uff0c\u817e\u8eab\u800c\u8d77\uff0c\u5012\u63d0\u72ec\u811a\u94dc\u4eba\uff0c\u62fc\u7740\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u5c31\u8981\u5411\u6c5f\u6653\u8299\u75db\u4e0b\u6740 \u624b\uff01\u91d1\u9010\u6d41\u559d\u9053\uff1a\u201c\u4f60\u6b7b\u5230\u4e34\u5934\uff0c\u8fd8\u6562\u7316\u72c2\uff01\u201d\u53f2\u767d\u90fd\u4eba\u5728\u534a\u7a7a\uff0c\u5df2\u81ea\u611f\u5230\u7384\u94c1\u5b9d\u5251\u523a\u6765\u7684 \u4e00\u80a1\u52b2\u98ce\u3002\u4ed6\u7684\u94dc\u4eba\u82e5\u662f\u51fb\u4e0b\u6765\uff0c\u56fa\u7136\u53ef\u4ee5\u4f24\u4e86\u6c5f\u6653\u82f1\u7684\u6027\u547d\uff0c\u4f46\u81ea\u5df1\u4e5f\u5fc5\u5c06\u6b7b\u5728\u91d1\u9010\u6d41\u7684 \u5251\u4e0b\u3002\u53f2\u767d\u90fd\u786c\u751f\u751f\u7684\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u5012\u7ffb\uff0c\u94dc\u4eba\u5411\u91d1\u9010\u6d41\u629b\u53bb\uff0c\u91d1\u9010\u6d41\u6325\u5251\u6253\u843d\u94dc\u4eba\u3002\u53f2\u767d \u90fd\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7b4b\u6597\uff0c\u5df2\u662f\u7ffb\u8fc7\u4e86\u53e6\u4e00\u5ea7\u5047\u5c71\uff0c\u8131\u51fa\u4e86\u5305\u56f4\u5708\u5916\u3002 \u3000\u3000\u6b64\u65f6\u53f2\u7ea2\u82f1\u4e5f\u5df2\u4e0a\u4e86\u5047\u5c71\uff0c\u89c1\u54e5\u54e5\u8d25\u5f97\u5982\u6b64\u72fc\u72c8\uff0c\u4e0d\u7981\u53f9\u9053\uff1a\u201c\u4f60\u82e5\u65e9\u77e5\u6094\u6539\uff0c\u4e5f\u4e0d\u81f3 \u4e8e\u4f1a\u6709\u4eca\u65e5\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4f4e\u58f0\u9053\uff1a\u201c\u7ea2\u82f1\u4f60\u6b47\u4e00\u6b47\u3002\u201d\u63e1\u7740\u5979\u7684\u624b\uff0c\u52a9\u5979\u8c03\u5300\u6c14\u606f\uff0c\u60ed\u590d\u7cbe\u795e\uff0c\u539f\u6765\u4ed6\u4eec \u4e8c\u4eba\u5fc3\u610f\u76f8\u901a\uff0c\u91d1\u9010\u6d41\u4e00\u770b\u5979\u7684\u9762\u8272\uff0c\u5c31\u77e5\u5979\u7684\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\uff0c\u662f\u4ee5\u85c9\u7740\u4e3a\u5979\u8c03\u5300\u6c14\u606f\uff0c\u4f7f \u5979\u9759\u4e0b\u6765\u4e0d\u81f4\u80e1\u601d\u4e71\u60f3\u3002\u540c\u65f6\u4e5f\u53ef\u4ee5\u907f\u514d\u81ea\u5df1\u4eb2\u624b\u53bb\u6740\u53f2\u767d\u90fd\u3002 \u3000\u3000\u53f2\u767d\u90fd\u7ffb\u8fc7\u4e24\u5ea7\u5047\u5c71\uff0c\u521a\u521a\u677e\u4e00\u53e3\u6c14\uff0c\u4e0d\u6599\u4e00\u6ce2\u672a\u5e73\uff0c\u4e00\u6ce2\u53c8\u8d77\uff1b\u80cc\u540e\u7684\u5b87\u6587\u96c4\u7b49\u4eba\u867d \u7136\u5c1a\u672a\u8ffd\u5230\uff0c\u9648\u5149\u7167\u548c\u77f3\u971e\u59d1\u8fd9\u4e00\u5bf9\u672a\u5a5a\u592b\u5987\u5374\u5df2\u662f\u8fce\u9762\u800c\u6765\u3002 \u3000\u3000\u77f3\u971e\u59d1\u66fe\u7ecf\u53d7\u8fc7\u4ed6\u7684\u6b3a\u51cc\uff0c\u6b64\u65f6\u6b63\u662f\u4ec7\u4eba\u89c1\u9762\uff0c\u5206\u5916\u773c\u7ea2\uff0c\u4e00\u58f0\u559d\u9053\uff1a\u201c\u5978\u8d3c\uff0c\u5f80\u54ea\u91cc \u8d70\uff1f\u201d\u4e00\u6761\u91d1\u5149\u707f\u70c2\u7684\u86c7\u5f62\u5175\u5668\u767b\u65f6\u5c31\u5377\u8fc7\u6765\uff01 \u3000\u3000\u8fd9\u662f\u77f3\u971e\u59d1\u7279\u522b\u94f8\u6210\u7684\u5947\u95e8\u5175\u5668\uff0c\u540d\u4e3a\u91d1\u86c7\u7d22\uff0c\u7528\u4e03\u4e2a\u91d1\u73af\u6263\u6210\u86c7\u8eab\uff0c\u53ef\u4ee5\u6296\u5f00\u6765\u5f53\u4f5c \u6697\u5668\u4f7f\u7528\u3002\u86c7\u5934\u53c8\u85cf\u6709\u836f\u7c89\uff0c\u80fd\u4eca\u4eba\u4e2d\u6bd2\u4e8e\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\u3002\u8fd9\u6761\u91d1\u86c7\u7d22\uff0c\u77f3\u971e\u59d1\u672c\u6765\u5c31\u662f\u51c6 \u5907\u7528\u6765\u5bf9\u4ed8\u53f2\u767d\u90fd\u7684\uff0c\u5728\u626c\u5dde\u4e4b\u65f6\uff0c\u672a\u6709\u673a\u4f1a\u4f7f\u7528\uff0c\u6b64\u65f6\u65b9\u59cb\u7528\u4e0a\u3002 \u3000\u3000\u53f2\u767d\u90fd\u6b64\u65f6\u5df2\u7ecf\u4e22\u4e86\u72ec\u811a\u94dc\u4eba\uff0c\u4ed6\u6df1\u77e5\u77f3\u971e\u59d1\u64c5\u4e8e\u4f7f\u6bd2\uff0c\u751f\u6015\u7740\u4e86\u5979\u7684\u9053\u513f\uff0c\u5f53\u4e0b\u5ba0\u624b \u8896\u4e2d\uff0c\u559d\u9053\uff1a\u201c\u971e\u59d1\uff0c\u4f60\u4e5f\u6562\u6765\u62e6\u6211\uff01\u201d\u6325\u8896\u4e00\u5377\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6492\u624b\uff01\u201d\u767b\u65f6\u628a\u77f3\u971e\u59d1\u7684\u91d1 \u86c7\u7d22\u5377\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4e0d\u6599\u77f3\u971e\u59d1\u7684\u5175\u5668\u867d\u7136\u8131\u624b\uff0c\u4f46\u90a3\u4e03\u4e2a\u91d1\u73af\u5df2\u662f\u6296\u5f00\u3002\u53f2\u767d\u90fd\u6076\u6597\u8fde\u573a\uff0c\u6c14\u529b\u4e0d\u52a0\uff0c\u8863 \u8896\u53ea\u5377\u7740\u4e86\u201c\u86c7\u5934\u201d\uff0c\u90a3\u4e03\u4e2a\u91d1\u574f\uff0c\u4ed6\u5df2\u662f\u65e0\u529b\u7528\u8896\u98ce\u62c2\u5f00\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u4e5f\u5f53\u771f\u4e86\u5f97\uff0c\u867d\u7136\u65e0\u529b\u6253\u843d\u91d1\u73af\uff0c\u4f46\u5728\u8fd9\u5371\u673a\u77ac\u606f\u4e4b\u95f4\uff0c\u5c45\u7136\u8fd8\u80fd\u591f\u63d0\u8d77\u4e00\u53e3\u771f \u6c14\uff0c\u8eab\u5f62\u5e73\u5730\u62d4\u8d77\u4e00\u4e08\u591a\u3002\u53ea\u542c\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u4e00\u5708\u4e00\u5708\u7684\u91d1\u5149\u5305\u56f4\u7740\u53f2\u767d\u90fd\uff0c\u51e0\u4e4e\u662f\u5939\u7740\u4ed6 \u7684\u8eab\u5b50\u4ea4\u53c9\u7a7f\u63d2\u800c\u8fc7\uff0c\u53f2\u767d\u90fd\u5927\u543c\u4e00\u58f0\uff0c\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7b4b\u6597\u5012\u7ffb\u4e0b\u6765\uff0c\u989d\u89d2\u5f00\u4e86\u4e2a\u6d1e\uff0c\u8840\u6d41\u5982 \u6ce8\uff0c\u4f46\u5c45\u7136\u8fd8\u6ca1\u5012\u4e0b\u3002\u539f\u6765\u77f3\u971e\u59d1\u7279\u522b\u94f8\u9020\u7684\u8fd9\u4e03\u4e2a\u91d1\u73af\uff0c\u8fb9\u7f18\u90fd\u662f\u78e8\u5f97\u950b\u5229\u7684\uff0c\u53f2\u767d\u90fd\u907f \u8fc7\u516d\u4e2a\u91d1\u73af\uff0c\u6700\u540e\u4e00\u4e2a\u5374\u5272\u4f24\u4e86\u4ed6\u7684\u989d\u89d2\u3002\u5e78\u4e8f\u4ed6\u90a3\u4e2a\u7b4b\u6597\u7ffb\u5f97\u5feb\uff0c\u5426\u5219\u82e5\u7ed9\u91d1\u73af\u7838\u7740\u5929\u7075 \u76d6\uff0c\u66f4\u662f\u4e0d\u582a\u8bbe\u60f3\u3002 \u3000\u3000\u53f2\u767d\u90fd\u968f\u8eab\u5e26\u6709\u91d1\u521b\u836f\uff0c\u767e\u5fd9\u4e2d\u8fde\u5fd9\u53d6\u836f\u6577\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u9648\u5149\u7167\u89c1\u77f3\u971e\u59d1\u7684\u5175\u5668 \u8131\u624b\uff0c\u6050\u9632\u77f3\u971e\u59d1\u906d\u4ed6\u53cd\u556e\uff0c\u4e00\u4e2a\u201c\u71d5\u5b50\u4e09\u63a0\u6c34\u201d\u4fbf\u63a0\u8fc7\u6765\uff0c\u957f\u5251\u5411\u53f2\u767d\u90fd\u75be\u523a\u3002 \u3000\u3000\u53f2\u767d\u90fd\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u8fd9\u5c0f\u5b50\u4e5f\u6562\u6765\u6b3a\u6211\uff01\u53f2\u67d0\u7eb5\u7136\u4e0d\u6d4e\uff0c\u6740\u4f60\u8fd9\u5c0f\u5b50\u8c05\u8fd8\u53ef\u4ee5\uff01\u201d \u5251\u5149\u4eba\u5f71\u4e4b\u4e2d\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u9648\u5149\u7167\u7684\u957f\u5251\u7adf\u7136\u7ed9\u53f2\u767d\u90fd\u7684\u4e2d\u6307\u5f39\u4e2a\u6b63\u7740\uff0c\u8fd9\u4e00\u5f39 \u4e43\u662f\u53f2\u767d\u90fd\u6bd5\u751f\u529f\u529b\u4e4b\u6240\u805a\uff0c\u867d\u7136\u4e34\u6b7b\u6323\u624e\uff0c\u529b\u9053\u4e5f\u5927\u5f97\u60ca\u4eba\uff0c\u9648\u5149\u7167\u864e\u53e3\u8ff8\u88c2\uff0c\u957f\u5251\u638c\u63e1 \u4e0d\u7262\uff0c\u5f53\u5373\u5760\u5730\u3002 \u3000\u3000\u53f2\u767d\u90fd\u4e00\u62db\u5f97\u624b\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u5f97\u4e0d\u5230\u971e\u59d1\uff0c\u4e5f\u4e0d\u80fd\u4fbf\u5b9c\u4e86\u4f60\u8fd9\u5c0f\u5b50\u3002\u201d\u6b63\u62df\u6251\u4e0a\u524d\u53bb\uff0c \u75db\u4e0b\u6740\u624b\u3002\u5ffd\u89c9\u6d51\u8eab\u53d1\u75d2\uff0c\u4e00\u53e3\u771f\u6c14\u63d0\u4e0d\u8d77\u6765\uff0c\u811a\u6b65\u521a\u8d77\uff0c\u4fbf\u5373\u843d\u5730\u3002\u672c\u6765\u4ed6\u662f\u51c6\u5907\u4e00\u8dc3\u4e08 \u8bb8\u7684\uff0c\u7ed3\u679c\u8fd9\u4e00\u6b65\u5374\u53ea\u8de8\u51fa\u4e86\u4e09\u5c3a\u4e4b\u5730\uff0c\u90a3\u4e00\u8bb0\u5288\u7a7a\u638c\uff0c\u5f53\u7136\u4e5f\u5c31\u4f24\u4e0d\u7740\u9648\u5149\u7167\u4e86\u3002 \u3000\u3000\u539f\u6765\u77f3\u971e\u59d1\u7528\u7684\u90a3\u6761\u201c\u91d1\u86c7\u7d22\u201d\uff0c\u201c\u86c7\u5934\u201d\u4e2d\u7a7a\uff0c\u85cf\u6709\u836f\u7c89\uff0c\u8fd9\u79cd\u836f\u7c89\u867d\u7136\u4e0d\u662f\u81f4\u4eba\u6b7b \u547d\u7684\u6bd2\u836f\uff0c\u4f46\u53ea\u8981\u6cbe\u4e0a\u4e00\u70b9\uff0c\u5374\u80fd\u4ee4\u4eba\u5947\u75d2\u96be\u5f53\uff0c\u53f2\u767d\u90fd\u521a\u624d\u62e9\u8896\u5377\u4e86\u201c\u86c7\u5934\u201d\u4e0d\u77e5\u4e0d\u89c9\u4e4b \u95f4\uff0c\u5df2\u662f\u7ed9\u836f\u7c89\u6cbe\u4e0a\u3002 \u3000\u3000\u75bc\u75db\u8fd8\u6613\u5fcd\u53d7\uff0c\u9ebb\u75d2\u6700\u662f\u96be\u582a\u3002\u53f2\u767d\u90fd\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u5c31\u8981\u6293\u75d2\u3002\u9648\u5149\u7167\u957f\u5251\u843d\u5730\uff0c\u9632\u4ed6\u53cd \u6251\uff0c\u626c\u624b\u6253\u51fa\u4e09\u9897\u51b0\u5f39\u3002 \u3000\u3000\u53f2\u767d\u90fd\u673a\u4f36\u4f36\u6253\u4e86\u4e00\u4e2a\u51b7\u6218\uff0c\u4e0d\u4f46\u662f\u808c\u80a4\u8d77\u6817\uff0c\u800c\u4e14\u662f\u51b7\u610f\u76f4\u900f\u5fc3\u5934\u3002\u8981\u77e5\u82e5\u5728\u5e73\u65f6\uff0c \u533a\u533a\u4e09\u9897\u51b0\u9b44\u795e\u5f39\uff0c\u6050\u6015\u53ea\u80fd\u4ee4\u5730\u7cbe\u795e\u723d\u5feb\u800c\u5df2\uff0c\u5c82\u80fd\u4f24\u5f97\u4e86\u4ed6\uff1f\u5982\u4eca\u4ed6\u7ade\u4f1a\u611f\u5230\u5947\u51b7\u96be \u582a\uff0c\u90a3\u5f53\u7136\u662f\u5143\u6c14\u5927\u4f24\uff0c\u5feb\u5230\u4ed6\u5c3d\u706f\u6cb9\u67af\u7684\u5f81\u5146\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u54ac\u7834\u820c\u5c16\uff0c\u4e00\u9635\u75bc\u75db\u4e4b\u611f\u4ee4\u4ed6\u7a0d\u7a0d\u611f\u5230\u8212\u670d\u4e00\u4e9b\u3002\u56e0\u4e3a\u8fd9\u662f\u8f6c\u79fb\u6ce8\u610f\u529b\u7684\u65b9\u6cd5\uff0c \u6709\u4e86\u75bc\u75db\u7684\u611f\u89c9\uff0c\u9ebb\u75d2\u7684\u611f\u89c9\u5c31\u53ef\u4ee5\u7565\u4e3a\u51cf\u8f7b\uff0c\u8eab\u4e0a\u4e5f\u6ca1\u90a3\u4e48\u51b7\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u4e0d\u6562\u604b\u6218\uff0c\u9648\u5149\u7167\u4e5f\u56e0\u4e0d\u77e5\u4ed6\u7684\u865a\u5b9e\uff0c\u6709\u6240\u987e\u5fcc\uff0c\u4e0d\u6562\u5f3a\u653b\uff0c\u53f2\u767d\u90fd\u8c03\u5300\u6c14\u606f\uff0c \u8fde\u5fd9\u51b2\u4e86\u51fa\u53bb\uff0c\u4f46\u8fd9\u4e48\u4e00\u6765\uff0c\u4ed6\u5374\u662f\u81ea\u66b4\u5f31\u70b9\u4e86\u3002\u9648\u5149\u7167\u51b7\u7b11\u9053\uff1a\u201c\u53f2\u5927\u5e2e\u4e3b\uff0c\u4f60\u4e0d\u662f\u8981\u53d6 \u6211\u6027\u547d\u7684\u5417\uff1f\u600e\u7684\u5374\u53d8\u6210\u4e86\u4e27\u5bb6\u4e4b\u72ac\u4e86\uff1f\u201d\u77f3\u971e\u59d1\u9053\uff1a\u201c\u7ba1\u4ed6\u662f\u4e27\u5bb6\u72ac\u4e5f\u597d\uff0c\u843d\u6c34\u72d7\u4e5f\u8981 \u6253\uff01\u201d\u9648\u5149\u7167\u9053\uff1a\u201c\u5bf9\uff0c\u5927\u4f19\u513f\u6253\u843d\u6c34\u72d7\u554a\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u4f46\u5728\u6b64\u65f6\uff0c\u518d\u5df2\u4e0d\u7531\u4ed6\u901e\u51f6\u4f5c\u6076\u4e86\uff0c\u4ed6\u53ea\u597d\u5fcd\u4f4f\u4e86\u6c14\uff0c\u8d76\u5feb\u9003 \u547d\u3002\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u5b88\u7740\u5927\u95e8\uff0c\u7b11\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u60f3\u6253\u843d\u6c34\u72d7\uff0c\u4f46\u4f60\u8981\u95ef\u8fc7\u6211\u8fd9\u4e00\u5173\uff0c\u4e5f\u5f97\u63a5\u6211\u4e00 \u638c\uff01\u201d\u53f2\u767d\u90fd\u54ac\u4e00\u54ac\u7259\uff0c\u628a\u6b8b\u5b58\u7684\u6c14\u529b\u51dd\u805a\u638c\u5fc3\uff0c\u201c\u84ec\u201d\u7684\u4e0e\u4e0a\u5b98\u6cf0\u5bf9\u4e86\u4e00\u638c\uff0c\u4e0a\u5b98\u6cf0\u9000\u4e86 \u4e09\u6b65\uff0c\u53f2\u767d\u90fd\u5374\u5df2\u662f\u53e3\u55b7\u9c9c\u8840\u3002\u201d \u3000\u3000\u4e0a\u5b98\u6cf0\u662f\u6b66\u6797\u524d\u8f88\u7684\u8eab\u4efd\uff0c\u6545\u6b64\u4e0d\u5927\u613f\u610f\u6253\u843d\u6c34\u72d7\uff0c\u89c9\u5f97\u53f2\u767d\u90fd\u5728\u8fde\u756a\u82e6\u6218\u4e4b\u540e\uff0c\u5c45\u7136 \u8fd8\u6709\u5982\u6b64\u638c\u529b\uff0c\u5012\u4e5f\u4e0d\u7981\u6709\u70b9\u4f69\u670d\uff0c\u4e8e\u662f\u5bf9\u8fc7\u4e86\u4e00\u638c\uff0c\u4fbf\u4e0d\u4e3a\u5df1\u751a\uff0c\u653e\u4ed6\u8fc7\u53bb\u3002 \u3000\u3000\u54ea\u77e5\u53f2\u767d\u90fd\u5374\u662f\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\u7231\u541b\u5b50\u4e4b\u8179\uff0c\u4ed6\u5f53\u7136\u4e0d\u4f1a\u76f8\u4fe1\u4e0a\u5b98\u6cf0\u7684\u8bdd\uff0c\u53ea\u9053\u4e0a\u5b98\u6cf0\u8fd9\u4e00 \u9000\u4e00\u95ea\uff0c\u4e43\u662f\u84c4\u52b2\u5f85\u53d1\uff0c\u5b9a\u662f\u5389\u5bb3\u7684\u6740\u7740\u7559\u5728\u540e\u5934\u3002\u4ed6\u662f\u4e2a\u6b66\u5b66\u540d\u5bb6\uff0c\u6df1\u660e\u201c\u5236\u654c\u673a\u5148\u201d\u7684 \u8bc0\u7a8d\uff0c\u4e00\u638c\u5288\u51fa\uff0c\u7d27\u63a5\u7740\u5c31\u65bd\u5c55\u201c\u9694\u7269\u4f20\u529f\u201d\u7684\u672c\u9886\uff0c\u6b64\u65f6\u6070\u5de7\u6709\u4e00\u4e2a\u5c06\u519b\u5e9c\u7684\u5c0f\u519b\u5b98\uff0c\u5f53 \u4e0b\u4ee5\u85c9\u4ed6\u63a9\u62a4\uff0c\u8ddf\u5728\u4ed6\u7684\u80cc\u540e\u9003\u8d70\uff0c\u53f2\u767d\u90fd\u53cd\u624b\u4e00\u6293\uff0c\u628a\u8fd9\u5c0f\u519b\u5b98\u6293\u4e86\u8d77\u6765\uff0c\u7acb\u5373\u628a\u4eba\u5f53\u4f5c \u6697\u5668\uff0c\u5411\u4e0a\u5b98\u6cf0\u6253\u53bb\u3002\u90a3\u5c0f\u519b\u5b98\u5413\u5f97\u5c16\u58f0\u60ca\u53eb\u3002 \u3000\u3000\u4e0a\u5b98\u6cf0\u5fc3\u4e2d\u4e00\u8f6f\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u519b\u5b98\u7f6a\u4e0d\u8981\u6b7b\uff0c\u4f55\u5fc5\u591a\u4f24\u6027\u547d\uff1f\u201d\u6b64\u65f6\u4ed6\u5df2\u95ea\u8eb2\u4e0d\u53ca\uff0c \u53ea\u597d\u628a\u8fd9\u5c0f\u519b\u5b98\u63a5\u4e0b\u6765\uff0c\u5c82\u77e5\u4ed6\u4e00\u5ff5\u4e4b\u6148\uff0c\u51e0\u4e4e\u91cd\u4f24\u5728\u53f2\u767d\u90fd\u7684\u201c\u9694\u7269\u4f20\u529f\u201d\u4e4b\u4e0b\u3002 \u3000\u3000\u8981\u77e5\u53f2\u767d\u90fd\u867d\u7136\u662f\u5f3a\u5f29\u4e4b\u672a\uff0c\u4f46\u4ed6\u8fd9\u4e00\u624b\u201c\u9694\u7269\u4f20\u529f\u201d\u4ecd\u7136\u662f\u6781\u9ad8\u660e\u7684\u6b66\u6797\u7edd\u6280\uff0c\u4e0a\u5b98 \u6cf0\u5018\u82e5\u4ee5\u529b\u78b0\u529b\uff0c\u628a\u8fd9\u5c0f\u519b\u5b98\u9707\u5f00\uff0c\u81ea\u8eab\u5f53\u7136\u4e0d\u4f1a\u53d7\u4f24\uff0c\u5982\u4eca\u4ed6\u4e3a\u4e86\u4fdd\u5168\u8fd9\u5c0f\u519b\u5b98\u7684\u6027\u547d\uff0c \u63a5\u4ed6\u4e0b\u6765\uff0c\u8fd9\u4e00\u4e0b\u53f2\u767d\u90fd\u6240\u53d1\u7684\u529b\u9053\u52a0\u4e0a\u90a3\u5c0f\u519b\u5b98\u767e\u591a\u65a4\u91cd\u7684\u8eab\u4f53\uff0c\u767b\u65f6\u5c31\u4f3c\u5de8\u77f3\u822c\u7684\u538b\u5230 \u4ed6\u7684\u8eab\u4e0a\uff0c\u8fd9\u80a1\u51b2\u51fb\u4e4b\u529b\u975e\u540c\u5c0f\u53ef\uff0c\u9976\u662f\u4e0a\u5b98\u6cf0\u529f\u529b\u6df1\u6e5b\uff0c\u4ea6\u662f\u7981\u53d7\u4e0d\u8d77\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u4e0a\u5b98\u6cf0\u53ea\u89c9\u5982\u53d7\u9524\u51fb\uff0c\u773c\u524d\u91d1\u661f\u4e71\u5192\uff0c\u53cc\u624b\u4e00\u677e\uff0c\u90a3\u5c0f\u519b\u5b98\u8dcc\u4e86\u4e0b\u6765\uff0c\u4e00\u547d \u545c\u547c\u3002\u4e0a\u5b98\u6cf0\u867d\u7136\u4e00\u5ff5\u6148\u60b2\uff0c\u4ecd\u7136\u6551\u4e0d\u4e86\u4ed6\uff0c\u81ea\u5df1\u5374\u53cd\u800c\u53d7\u4e86\u4e00\u70b9\u5185\u4f24\u3002\u5e78\u4e8f\u4ed6\u662f\u7acb\u5373\u677e\u624b \u540e\u9000\uff0c\u6d88\u89e3\u4e86\u5bf9\u65b9\u4e00\u63b7\u7684\u51e0\u5206\u529b\u9053\uff0c\u867d\u7136\u53d7\u4f24\uff0c\u4f24\u5f97\u8fd8\u4e0d\u7b97\u91cd\u3002 \u3000\u3000\u4e0a\u5bab\u7ea8\u3001\u7afa\u6e05\u534e\u3001\u4e0b\u5b98\u96c4\u7b49\u4eba\u89c1\u4e0a\u5b98\u6cf0\u53d7\u4f24\uff0c\u8fde\u5fd9\u8d76\u6765\u3002\u4e0a\u5b98\u7ea8\u9053\uff1a\u201c\u7239\uff0c\u4f60\u600e\u4e48 \u5566\uff1f\u201d\u4e0a\u5b98\u6cf0\u5e72\u7b11\u9053\uff1a\u201c\u4e0d\u788d\u4e8b\u3002\u4f46\u6211\u60f3\u4e0d\u5230\u4ed6\u7adf\u4f3c\u75af\u72ac\u4e00\u822c\uff0c\u653e\u4ed6\u8fc7\u53bb\uff0c\u4ed6\u8fd8\u8981\u53cd\u6251\u3002\u201d \u7afa\u6e05\u534e\u9053\uff1a\u201c\u4e0a\u5b98\u4f2f\u4f2f\uff0c\u9648\u5927\u54e5\u8bf4\u5f97\u597d\uff0c\u662f\u843d\u6c34\u72d7\u4e5f\u8981\u6253\uff0c\u8c01\u53eb\u4f60\u4e0d\u6253\u843d\u6c34\u62d8\u554a\uff1f\u201d\u4e0a\u5b98\u6cf0 \u632f\u8d77\u7cbe\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\uff0c\u54b1\u4eec\u8fd9\u5c31\u6253\u843d\u6c34\u72d7\u53bb\uff01\u8c05\u5b83\u8fd9\u6761\u843d\u6c34\u72d7\u4e5f\u9003\u4e0d\u4e86\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u786c\u62fc\u4e00\u638c\uff0c\u53d7\u7684\u4f24\u6bd4\u4e0a\u5b98\u6cf0\u66f4\u91cd\u3002\u4ed6\u9003\u51fa\u4e86\u5927\u95e8\uff0c\u53ea\u89c9\u534a\u8fb9\u8eab\u5b50\u5df2\u7ecf\u9ebb\u6728\uff0c\u539f\u6765 \u4ed6\u6240\u7740\u7684\u836f\u7c89\u4e0e\u53ca\u6240\u53d7\u7684\u51b0\u5f39\u5bd2\u6c14\u3002\u5728\u4ed6\u8eab\u4f53\u7684\u62b5\u6297\u529b\u5927\u51cf\u4e4b\u9645\uff0c\u9f50\u90fd\u53d1\u4f5c\u3002 \u3000\u3000\u53f2\u767d\u90fd\u5f3a\u8fd0\u7384\u529f\uff0c\u4e00\u9762\u62b5\u5fa1\u5947\u51b7\u5947\u75d2\uff0c\u4e00\u9762\u63d0\u4e86\u53e3\u6c14\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u516d\u5408\u5e2e\u7684\u5144\u5f1f\u8ddf\u6211 \u7a81\u56f4\uff01\u201d\u4ed6\u8fd9\u6b21\u6765\u7684\u662f\u66ff\u59b9\u59b9\u4e3b\u5a5a\uff0c\u5e2e\u4e2d\u7684\u5927\u5c0f\u5934\u76ee\u90fd\u5e26\u4e86\u6765\uff0c\u4ed6\u7684\u56db\u5927\u9999\u4e3b\u867d\u7136\u4e00\u9003\u4e09 \u6b7b\uff0c\u5927\u5c0f\u5934\u76ee\u4e5f\u8fd8\u6709\u4e00\u767e\u591a\u4eba\u3002\u8fd9\u4e00\u767e\u591a\u4eba\u4e2a\u4e2a\u90fd\u4f1a\u6b66\u529f\uff0c\u7eb5\u7136\u4e0d\u662f\u5f88\u5f3a\uff0c\u4e5f\u53ef\u5f53\u5f97\u5343\u591a\u52b2 \u5352\u3002\u53f2\u767d\u90fd\u5018\u82e5\u5f97\u5230\u8fd9\u4e00\u767e\u591a\u4eba\u8ddf\u4ed6\u7a81\u56f4\uff0c\u90a3\u5c31\u53ef\u80fd\u6709\u4e00\u7ebf\u751f\u673a\u4e86\u3002 \u3000\u3000\u516d\u5408\u5e2e\u8fd9\u4e00\u767e\u591a\u4eba\uff0c\u6b64\u65f6\u6b63\u805a\u5728\u5c06\u519b\u5e9c\u5916\u7684\u5e7f\u573a\u3002\u53f2\u767d\u90fd\u5c31\u662f\u56e0\u4e3a\u770b\u89c1\u4ed6\u4eec\u805a\u5728\u4e00\u8d77\uff0c \u8fd9\u624d\u547c\u558a\u4ed6\u4eec\u7684\u3002\u867d\u7136\u4ed6\u4e5f\u6709\u70b9\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u8fd9\u4e9b\u4eba\u4e0d\u662f\u5404\u81ea\u9003\u4ea1\uff0c\u5374\u805a\u5728\u4e00\u8d77\u5462\uff1f \u3000\u3000\u53f2\u767d\u90fd\u4e00\u5411\u53f7\u4ee4\u751a\u4e25\uff0c\u4ee5\u4e3a\u5728\u81ea\u5df1\u7684\u79ef\u5a01\u4e4b\u4e0b\uff0c\u5e2e\u4f17\u4e0d\u4f1a\u4e0d\u542c\u4ed6\u7684\u8bf4\u8bdd\u3002\u54ea\u77e5\u4ed6\u7684\u8bdd\u72b9 \u672a\u4e86\uff0c\u53ea\u542c\u5f97\u8fd9\u4e00\u767e\u591a\u4eba\u5f00\u58f0\u53eb\u9053\uff1a\u201c\u53f2\u767d\u90fd\uff0c\u4f60\u5012\u884c\u9006\u65bd\uff0c\u8c01\u8fd8\u8ba4\u4f60\u505a\u5e2e\u4e3b\u3002\u6211\u4eec\u62e5\u62a4\u53f2 \u59d1\u5a18\u505a\u5e2e\u4e3b\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e00\u4ef6\u4e8b\u4e43\u662f\u674e\u6566\u7684\u529f\u52b3\uff0c\u8fd9\u767e\u591a\u4e2a\u4eba\u5728\u5927\u6df7\u4e71\u4e4b\u9645\u672c\u6765\u8981\u9003\u8d70\u7684\uff0c\u662f\u674e\u6566\u5c06\u4ed6\u4eec \u529d\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u4e49\u519b\u8fdb\u57ce\uff0c\u8981\u9003\u662f\u9003\u4e0d\u4e86\u7684\u3002\u4f60\u4eec\u5145\u5176\u91cf\u53ea\u662f\u4ece\u72af\uff0c\u53ea\u987b\u6539\u90aa\u5f52\u6b63\uff0c\u5b9a\u5f97\u5bbd \u5bb9\u3002\u201d \u3000\u3000\u516d\u5408\u5e2e\u7684\u5934\u76ee\u4e4b\u4e2d\uff0c\u4e0d\u5c11\u4eba\u662f\u674e\u6566\u7684\u670b\u53cb\uff0c\u672c\u6765\u5c31\u5728\u7b49\u5f85\u65f6\u673a\u73ab\u90aa\u5f52\u6b63\u7684\uff0c\u6b64\u65f6\u89c1\u5927\u52bf \u5df2\u53bb\uff0c\u518d\u52a0\u4e0a\u674e\u6566\u4e00\u529d\uff0c\u5f53\u7136\u662f\u4e2a\u4e2a\u4f9d\u4ece\u4e86\u3002 \u3000\u3000\u53f2\u767d\u90fd\u7ea2\u4e86\u773c\u775b\uff0c\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4eec\u80c6\u6562\u53db\u6211\uff0c\u6211\u8981\u628a\u4f60\u4eec\u4e00\u4e2a\u4e2a\u6740\u4e86\uff01\u201d\u54ac\u7834\u820c \u5934\uff0c\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u62ab\u5934\u6563\u53d1\uff0c\u597d\u50cf\u4e00\u53ea\u53d7\u4f24\u7684\u91ce\u517d\u4f3c\u5730\u6251\u4e0a\u524d\u53bb\u3002\u539f\u6765\u4ed6\u5728\u4f17\u53db\u4eb2\u79bb\u4e4b \u4e0b\uff0c\u5df2\u662f\u6c14\u5f97\u75af\u4e86\u3002 \u3000\u3000\u516d\u5408\u5e2e\u7684\u4e00\u4f17\u5934\u76ee\u5e73\u7d20\u53d7\u4ed6\u6b3a\u538b\u60ef\u4e86\uff0c\u6b64\u9645\u89c1\u4ed6\u75af\u72c2\u6251\u6765\uff0c\u867d\u7136\u660e\u77e5\u4ed6\u662f\u5782\u6b7b\u6323\u624e\uff0c\u4e5f \u662f\u4e0d\u7981\u6709\u70b9\u754f\u60e7\u3002\u53f2\u767d\u90fd\u628a\u773c\u4e00\u770b\uff0c\u770b\u89c1\u4ed6\u90a3\u5339\u5750\u9a91\u6b63\u5728\u7531\u4ed6\u7684\u9a6c\u592b\u7275\u7740\uff0c\u745f\u745f\u7f29\u7f29\u7684\u8eb2\u5728 \u5e7f\u573a\u7684\u4e00\u4e2a\u89d2\u843d\u3002\u53f2\u767d\u90fd\u559d\u9053\uff1a\u201c\u8c01\u6562\u4e0a\u6765\uff0c\u6211\u6253\u6b7b\u4e00\u4e2a\u591f\u672c\uff0c\u6253\u6b7b\u4e24\u4e2a\u5c31\u6709\u5f97\u8d5a\uff01\u201d\u4e00\u58f0 \u5927\u543c\uff0c\u7a81\u7136\u659c\u8eab\u7a9c\u51fa\uff0c\u5954\u5411\u5750\u9a91\u3002\u8fd9\u5339\u5750\u9a91\u540d\u4e3a\u201c\u7167\u591c\u72ee\u5b50\u201d\uff0c\u662f\u5343\u4e2d\u9009\u4e00\u7684\u826f\u9a79\uff0c\u82e5\u7ed9\u4ed6 \u593a\u56de\u5750\u9a91\uff0c\u9003\u751f\u5c31\u53ef\u80fd\u6709\u671b\u3002 \u3000\u3000\u90a3\u9a6c\u592b\u62d4\u51fa\u77ed\u5200\uff0c\u5413\u5f97\u9762\u8272\u94c1\u9752\uff0c\u4f46\u4ecd\u7136\u62e6\u4f4f\u9a6c\u5934\u3002\u53f2\u767d\u90fd\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u4f60 \u4e5f\u6562\u53cd\u6211\u4e48\uff1f\u98de\u6b65\u62a2\u4e0a\uff0c\u547c\u7684\u4e00\u62f3\u5c31\u5411\u90a3\u9a6c\u592b\u51fb\u53bb\u3002 \u3000\u3000\u53f2\u767d\u90fd\u4ee5\u4e3a\u5728\u4ed6\u7684\u79ef\u5a01\u4e4b\u4e0b\uff0c\u8fd9\u4e2a\u9a6c\u592b\u51b3\u8ba1\u4e0d\u6562\u53cd\u6297\u3002\u4e0d\u6599\u8fd9\u9a6c\u5929\u7adf\u7136\u559d\u9053\uff1a\u201c\u4f60\u4e0d\u628a \u6211\u5f53\u4eba\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u6562\u53cd\u4f60\uff1f\u597d\uff0c\u4f60\u51f6\u4f60\u72e0\uff0c\u6211\u7684\u6027\u547d\u4e0d\u503c\u94b1\uff0c\u6211\u5c31\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u53f2\u767d\u90fd\u4e00 \u62f3\u5411\u4ed6\u6253\u51fa\uff0c\u4ed6\u4e5f\u4e00\u5200\u5411\u53f2\u767d\u90fd\u5288\u53bb\uff01 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u9a6c\u592b\u8d77\u521d\u672c\u6765\u662f\u60f3\u9003\u8d70\u7684\uff0c\u4ed6\u62d4\u51fa\u77ed\u5200\uff0c\u53ea\u662f\u4e3a\u4e86\u81ea\u536b\u800c\u5df2\uff0c\u4f46\u89c1\u53f2\u767d\u90fd\u5982\u6b64 \u51f6\u72e0\u7684\u5bf9\u4ed6\uff0c\u8981\u53d6\u4ed6\u7684\u6027\u547d\u3002\u8fd9\u5239\u90a3\u95f4\uff0c\u4ed6\u60f3\u8d77\u4e86\u53f2\u767d\u90fd\u5e73\u65e5\u5bf9\u4ed6\u7684\u79cd\u79cd\u51cc\u8fb1\uff0c\u4e0d\u7531\u5f97\u6012\u6c14 \u9661\u751f\uff0c\u4ec7\u6068\u597d\u50cf\u70c8\u706b\u4e00\u822c\u4ece\u5fc3\u4e2d\u70e7\u8d77\uff0c\u767b\u65f6\u628a\u61e6\u592b\u53d8\u6210\u4e86\u52c7\u58eb\uff0c\u8fd9\u5239\u90a3\u95f4\u4ed6\u5df2\u662f\u5fd8\u8bb0\u4e86\u6050 \u60e7\u3002 \u3000\u3000\u53f2\u767d\u90fd\u89c1\u9a6c\u592b\u80c6\u6562\u548c\u4ed6\u52a8\u624b\uff0c\u5012\u662f\u4e0d\u7981\u4e00\u6014\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c9\u4e00\u9635\u523a\u75db\uff0c\u6253\u51fa\u53bb \u7684\u62f3\u5934\u5df2\u662f\u7740\u4e86\u4e00\u5200\uff0c\u542c\u5f97\u4ed6\u6307\u9aa8\u788e\u88c2\uff0c\u8840\u8089\u6a21\u7cca\u3002\u4f46\u53f2\u767d\u90fd\u662f\u4f55\u7b49\u529f\u592b\uff0c\u7740\u4e86\u4e00\u5200\uff0c\u7acb\u5373 \u4e00\u4e2a\u8fdb\u6b65\u6b3a\u8eab\uff0c\u53cd\u624b\u593a\u5200\uff0c\u628a\u90a3\u4e00\u77ed\u5200\u62a2\u4e86\u8fc7\u6765\uff0c\u201c\u7830\u201d\u7684\u4e00\u811a\u8e22\u51fa\uff0c\u5c06\u90a3\u9a6c\u592b\u8e22\u4e86\u4e00\u4e2a\u7b4b \u6597\u3002 \u3000\u3000\u53f2\u767d\u90fd\u54c8\u54c8\u5927\u7b11\uff0c\u8df3\u4e0a\u9a6c\u80cc\u3002\u4e0d\u6599\u7b11\u58f0\u672a\u7edd\uff0c\u4eba\u4e5f\u672a\u66fe\u843d\u4e0b\u9a6c\u978d\uff0c\u7a81\u7136\u53cc\u817f\u4e00\u8f6f\uff0c\u7adf\u7136 \u4e5f\u662f\u4e00\u4e2a\u5012\u7b4b\u6597\u8dcc\u4e86\u4e0b\u6765\u3002\u539f\u6765\u4ed6\u65e9\u5df2\u662f\u6cb9\u5c3d\u706f\u6854\uff0c\u53ea\u4ed7\u7740\u4e00\u80a1\u6c14\u75af\u72c2\u53cd\u6251\u7684\u3002\u7ed9\u90a3\u9a6c\u592b\u65ab \u4e86\u4e00\u5200\u4e4b\u540e\uff0c\u9510\u6c14\u987f\u632b\uff0c\u9042\u50cf\u6cc4\u4e86\u6c14\u7684\u76ae\u7403\u4e00\u6837\uff0c\u652f\u6301\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u90a3\u9a6c\u592b\u722c\u4e86\u8d77\u6765\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53f2\u5927\u5e2e\u4e3b\uff0c\u4f60\u4e5f\u6709\u4eca\u65e5\u4e48\uff1f\u4f60\u6740\u6211\u554a\uff0c\u4f60\u6740\u6211\u554a\uff01\u4f60\u6740 \u4e0d\u4e86\u6211\uff0c\u6211\u53ef\u5c31\u8981\u6740\u4f60\u4e86\uff01\u201d \u3000\u3000\u53f2\u767d\u90fd\u4ea6\u5df2\u722c\u4e86\u8d77\u6765\uff0c\u4ed6\u77aa\u7740\u53cc\u773c\u770b\u90a3\u9a6c\u592b\u7f13\u7f13\u5411\u4ed6\u8d70\u6765\uff0c\u4e0d\u89c9\u53f9\u4e86\u53e3\u6c14\u3002\u4ed6\u8fd9\u4e00\u811a\u8e22 \u4e0d\u6b7b\u9a6c\u592b\uff0c\u5df2\u77e5\u81ea\u5df1\u662f\u65e0\u80fd\u4e3a\u529b\u4e86\u3002 \u3000\u3000\u516d\u5408\u5e2e\u7684\u4e00\u4f17\u5934\u76ee\u89c1\u4e00\u4e2a\u9a6c\u592b\u4e5f\u6562\u4e0e\u53f2\u767d\u90fd\u786c\u62fc\uff0c\u5fc3\u4e2d\u90fd\u662f\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u60ed\u6127\u201d\uff0c\u5f53 \u4e0b\u53d1\u4e00\u58f0\u558a\uff0c\u90fd\u56f4\u62e2\u4e86\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u53eb\u9053\uff1a\u201c\u53f2\u767d\u90fd\uff0c\u5230\u4e86\u5982\u4eca\uff0c\u8fd8\u4e0d\u77e5\u9053\u4fae\u8fc7\u4e48\u3002\u201d \u3000\u3000\u53f2\u767d\u90fd\u4e0e\u90a3\u9a6c\u592b\u9762\u9762\u76f8\u5bf9\uff0c\u5bf9\u65b9\u90a3\u71c3\u7740\u4ec7\u6068\u7684\u773c\u5149\uff0c\u4ee4\u4ed6\u4e0d\u7981\u5fc3\u5934\u98a4\u6218\uff0c\u60f3\u9053\uff1a\u201c\u6211\u6a2a \u884c\u534a\u4e16\uff0c\u5e73\u65e5\u5bf9\u8fd9\u4e9b\u4eba\u662f\u8981\u6253\u5c31\u6253\uff0c\u8981\u9a82\u5c31\u9a82\uff0c\u4e5f\u96be\u602a\u4ed6\u4eec\u6068\u6211\u3002\u91d1\u9010\u6d41\u80af\u9976\u6055\u6211\uff0c\u8fd9\u4e9b\u4eba \u80af\u9976\u6055\u6211\u5417\uff1f\u5373\u4f7f\u8fd9\u4e9b\u4eba\u80af\u9976\u6055\u6211\uff0c\u6211\u4e5f\u662f\u5a01\u98ce\u626b\u5730\uff0c\u4eca\u540e\u518d\u4e5f\u633a\u4e0d\u8d77\u8170\u677f\u4e86\u3002\u201d \u3000\u3000\u5728\u53f2\u767d\u90fd\u8fd9\u4e00\u751f\u4e2d\u4e0d\u77e5\u66fe\u78b0\u8fc7\u591a\u5c11\u5f3a\u654c\uff0c\u5374\u4ece\u65e0\u4eca\u65e5\u8fd9\u6837\u7684\u4ee4\u4ed6\u611f\u5230\u5bb3\u6015\u3002\u4e00\u4e2a\u201c\u5c0f \u5c0f\u201d\u7684\u9a6c\u592b\uff0c\u4e00\u4e2a\u5e73\u65e5\u4ed6\u6839\u672c\u5c31\u4e0d\u4f1a\u653e\u5728\u773c\u5185\u7684\u9a6c\u592b\uff0c\u628a\u4ed6\u9707\u6151\u4f4f\u4e86\uff0c\u4e0d\u662f\u56e0\u4e3a\u8fd9\u4e2a\u9a6c\u592b\u7684 \u672c\u9886\u9ad8\u5f3a\uff0c\u800c\u662f\u56e0\u4e3a\u4ece\u8fd9\u4e2a\u9a6c\u592b\u7684\u8eab\u4e0a\uff0c\u4ed6\u611f\u5230\u4e86\u4f17\u53db\u4eb2\u79bb\u7684\u6050\u6016\uff0c\u611f\u5230\u4e86\u4e0e\u4f17\u4e3a\u654c\u7684\u6050 \u6016\uff01\u201c\u53ef\u60dc\u201d\u4ed6\u73b0\u5728\u624d\u61c2\u5f97\u8fd9\u4e00\u5b58\uff0c\u8fd9\u5df2\u7ecf\u662f\u592a\u8fdf\u4e86\u3002 \u3000\u3000\u5c3d\u7ba1\u4ed6\u662f\u987d\u56fa\u4e4b\u6781\u7684\u4e00\u4e2a\u4eba\uff0c\u5c3d\u7ba1\u4ed6\u5728\u4e34\u6b7b\u4e4b\u524d\u8fd8\u60f3\u5145\u4e00\u5145\u82f1\u96c4\u597d\u6c49\uff0c\u4f46\u5728\u5341\u76ee\u6240\u89c2\uff0c \u5341\u624b\u6240\u6307\u4e4b\u4e0b\uff0c\u4ed6\u5df2\u7981\u4e0d\u4f4f\u5185\u5fc3\u7684\u9707\u60ca\uff0c\u5728\u4f17\u4eba\u7684\u9762\u524d\u4f4e\u4e0b\u4e86\u5934\u4e86\u3002\u4ed6\u907f\u5f00\u4e86\u90a3\u9a6c\u592b\u7684\u76ee \u5149\uff0c\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u4e0d\u5fc5\u4f60\u6765\u6740\u6211\uff0c\u6211\u628a\u8fd9\u6761\u6027\u547d\u4ea4\u7ed9\u4f60\u4eec\u5c31\u662f\uff01\u201d\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u53f2\u767d\u90fd \u5c31\u7528\u4ece\u9a6c\u592b\u624b\u4e2d\u593a\u6765\u7684\u90a3\u628a\u77ed\u5200\uff0c\u63d2\u8fdb\u4e86\u81ea\u5df1\u7684\u80f8\u53e3\uff0c\u7ed3\u675f\u4e86\u81ea\u5df1\u7684\u6027\u547d\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u65e9\u5df2\u77e5\u9053\u54e5\u54e5\u4f1a\u6709\u5982\u6b64\u4e0b\u573a\uff0c\u4f46\u4e5f\u4e0d\u5fcd\u89c1\u5979\u54e5\u54e5\u7684\u60e8\u72b6\u3002\u5f53\u4e0b\u626d\u8f6c\u4e86\u5934\uff0c\u6276\u7740\u91d1 \u9010\u6d41\u3002\u91d1\u9010\u6d41\u8bf4\u9053\uff1a\u201c\u5e05\u5b5f\u96c4\u8fd8\u6ca1\u64d2\u83b7\uff0c\u54b1\u4eec\u627e\u4ed6\u53bb\u3002\u8fd8\u6709\u2026\u2026\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5e05\u5b5f \u96c4\u662f\u9996\u6076\uff0c\u51b3\u4e0d\u80fd\u8ba9\u4ed6\u6f0f\u7f51\uff0c\u8fd8\u6709\u5389\u5927\u54e5\u7684\u4e0b\u843d\uff0c\u54b1\u4eec\u4e5f\u5e94\u8be5\u5bfb\u4e2a\u6c34\u843d\u77f3\u51fa\u3002\u201d\u91d1\u9010\u6d41\u662f\u6015 \u53f2\u7ea2\u82f1\u56e0\u54e5\u54e5\u4e4b\u6b7b\u800c\u5f15\u8d77\u4f24\u611f\uff0c\u4e8e\u662f\u63d0\u51fa\u8fd9\u4e24\u6869\u4e8b\u60c5\u8f6c\u79fb\u5979\u7684\u6ce8\u610f\u3002 \u3000\u3000\u90a3\u9a6c\u592b\u628a\u53f2\u767d\u90fd\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u201d\u7275\u5230\u53f2\u7ea2\u82f1\u8ddf\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u4f60\u4e00\u5411\u5f85\u6211\u4eec \u597d\uff0c\u4f60\u505a\u5e2e\u4e3b\uff0c\u6211\u4eec\u90fd\u662f\u5fc3\u60a6\u8bda\u670d\u3002\u6211\u903c\u6b7b\u53f2\u767d\u90fd\uff0c\u59d1\u5a18\u82e5\u662f\u8ba4\u4e3a\u4e0d\u5f53\u3001\u6211\u7518\u53d7\u2026\u2026\u201d\u53f2\u7ea2 \u82f1\u4f4e\u58f0\u9053\uff1a\u201c\u8fd9\u4e0d\u80fd\u602a\u4f60\uff0c\u8fd9\u662f\u6211\u54e5\u54e5\u7f6a\u6709\u5e94\u5f97\u3002\u4f60\u5b89\u5fc3\u4e3a\u672c\u5e2e\u6548\u529b\u5427\u3002\u201d\u90a3\u9a6c\u592b\u9053\uff1a \u201c\u662f\uff0c\u8fd9\u5339\u9a6c\u8bf7\u4f60\u9a91\u53bb\u3002\u201d \u3000\u3000\u201c\u7167\u591c\u72ee\u5b50\u201d\u662f\u5339\u65e5\u884c\u5343\u91cc\u7684\u9a8f\u9a6c\u3001\u7528\u5b83\u8ffd\u654c\u81ea\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u53f2\u7ea2\u82f1\u5fc3\u60c5\u5c1a\u672a\u5e73\u9759\uff0c\u5f53 \u4e0b\u4e0d\u613f\u591a\u8bf4\uff0c\u5411\u90a3\u9a6c\u592b\u70b9\u4e86\u70b9\u5934\u8868\u793a\u8c22\u610f\uff0c\u4fbf\u5373\u8de8\u4e0a\u5750\u9a91\u3002 \u3000\u3000\u6b64\u65f6\u897f\u660c\u57ce\u5185\u5df7\u6218\u4e5f\u5dee\u4e0d\u591a\u8981\u7ed3\u675f\u4e86\uff0c\u5b98\u519b\u4f24\u4ea1\u7684\u548c\u6295\u964d\u7684\u7ea6\u5360\u4e00\u534a\uff0c\u8fd8\u6709\u4e00\u534a\u5f03\u57ce\u800c \u9003\uff0c\u8981\u77e5\u897f\u660c\u7684\u5b88\u519b\u6709\u5341\u4e07\u4e4b\u4f17\uff0c\u6bd4\u653b\u57ce\u7684\u4e49\u519b\u591a\u4e00\u500d\u6709\u591a\uff0c\u4e49\u519b\u5947\u88ad\u6210\u529f\uff0c\u4e0d\u613f\u903c\u4ed6\u4eec\u4f5c \u56f0\u517d\u4e4b\u6597\uff0c\u662f\u4ee5\u7f51\u5f00\u4e00\u9762\uff0c\u4e0d\u613f\u6295\u964d\u7684\u5b98\u519b\u5c31\u8ba9\u4ed6\u4eec\u9003\u751f\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u5411\u4e00\u4e2a\u4e49\u519b\u5934\u76ee\u6253\u542c\uff0c\u542c\u8bf4\u6e05\u519b\u5927\u961f\u662f\u4ece\u5317\u95e8\u9003\u8dd1\uff0c\u4fbf\u5373\u653e\u9a6c\u8ffd\u53bb\u3002\u8f6c\u773c\u4e4b\u95f4\uff0c \u5df2\u662f\u628a\u91d1\u9010\u6d41\u7529\u5728\u80cc\u540e\u3002\u91d1\u9010\u6d41\u6015\u5979\u5355\u9a91\u6df1\u5165\uff0c\u5927\u4e3a\u7740\u6025\uff0c\u53ea\u597d\u5728\u4e71\u519b\u4e2d\u62a2\u4e86\u4e00\u5339\u5750\u9a91\uff0c\u968f \u540e\u8ffd\u6765\u3002 \u3000\u3000\u897f\u660c\u57ce\u5916\uff0c\u6b63\u5728\u5c55\u5f00\u4e00\u573a\u8ffd\u51fb\u6218\u3002\u6e05\u519b\u58eb\u65e0\u6597\u5fd7\uff0c\u56db\u6563\u5954\u9003\uff0c\u4e49\u519b\u76ee\u7684\u5728\u4e8e\u9a71\u9010\u654c\u4eba\uff0c \u662f\u4ee5\u8ffd\u5230\u4e86\u57ce\u90ca\u5341\u4f59\u91cc\u4e4b\u5916\uff0c\u4fbf\u5373\u9e23\u91d1\u6536\u5175\u3002\u53ea\u5269\u4e0b\u4e00\u90e8\u4efd\u62c5\u4efb\u8b66\u6212\u7684\u5c0f\u90e8\u961f\u5728\u524d\u65b9\u5de1\u903b\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u4e00\u8def\u8ffd\u53bb\uff0c\u65e2\u6ca1\u6709\u53d1\u73b0\u5e05\u5b5f\u96c4\uff0c\u4e5f\u6ca1\u6709\u89c1\u7740\u5389\u5357\u661f\u3002\u53f2\u7ea2\u82f1\u8ffd\u51fa\u4e86\u5341\u4f59\u91cc\uff0c\u78b0\u89c1 \u4e00\u4e2a\u5728\u524d\u65b9\u5de1\u903b\u7684\u5934\u76ee\uff0c\u8fd9\u5934\u76ee\u8ba4\u4e0d\u5f97\u5e05\u5b5f\u96c4\uff0c\u53ea\u662f\u544a\u8bc9\u5979\u9053\uff1a\u201c\u6709\u51e0\u4e2a\u6e05\u519b\u7684\u519b\u5b98\u9003\u5165\u5c71 \u533a\uff0c\u54b1\u4eec\u6709\u4e2a\u5c0f\u961f\u5df2\u7ecf\u8fdb\u53bb\u641c\u7d22\u4e86\u3002\u654c\u4eba\u4e4b\u4e2d\u6709\u6ca1\u6709\u897f\u660c\u5c06\u519b\u5e05\u76c2\u96c4\u5728\u5185\uff0c\u8fd9\u6211\u5c31\u4e0d\u77e5 \u9053\u3002\u201d\u8fd9\u5934\u76ee\u529d\u53f2\u7ea2\u82f1\u56de\u53bb\uff0c\u53f2\u7ea2\u82f1\u54ea\u91cc\u80af\u542c\uff0c\u4e8e\u662f\u53c8\u518d\u7b56\u9a6c\u8ffd\u8fdb\u5c71\u533a\u3002 \u3000\u3000\u5230\u4e86\u5bc6\u6797\u6df1\u5904\uff0c\u542c\u5f97\u6797\u4e2d\u6709\u9ad8\u547c\u9163\u6597\u4e4b\u58f0\uff0c\u53f2\u7ea2\u82f1\u5feb\u9a6c\u8d76\u53bb\uff0c\u5230\u8fbe\u4e4b\u65f6\uff0c\u6218\u6597\u5df2\u7ecf\u7ed3 \u675f\u3002\u53ea\u89c1\u4e00\u961f\u4e49\u519b\u6349\u83b7\u4e86\u4e09\u4e2a\u4fd8\u864f\uff0c\u4e49\u519b\u53d7\u4f24\u7684\u5374\u6709\u4e03\u516b\u4eba\u4e4b\u591a\uff0c\u8fd9\u4e09\u4e2a\u4fd8\u864f\u5df2\u7ecf\u95ee\u660e\u8eab \u4efd\uff0c\u90fd\u662f\u5e05\u5b5f\u96c4\u624b\u4e0b\u7684\u9ad8\u7ea7\u519b\u5b98\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u5927\u5931\u6240\u671b\uff0c\u95ee\u9053\uff1a\u201c\u5e05\u5b5f\u96c4\u5462\uff1f\u201d\u90a3\u4e09\u4e2a\u4fd8\u864f\u95ed\u53e3\u4e0d\u8a00\u3002\u53f2\u7ea2\u82f1\u6012\u9053\uff1a\u201c\u597d\u5440\uff0c \u4f60\u4eec\u662f\u4e0d\u662f\u60f3\u7ed9\u5e05\u5b5f\u96c4\u966a\u4e27\uff1f\u201d\u6325\u52a8\u957f\u97ad\uff0c\u5c31\u60f3\u903c\u4f9b\u3002 \u3000\u3000\u90a3\u4e49\u519b\u5934\u76ee\u529d\u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u4ed6\u4eec\u5df2\u7ecf\u653e\u4e0b\u6b66\u5668\uff0c\u505a\u4e86\u4fd8\u864f\uff0c\u54b1\u4eec\u53ef\u4e0d\u80fd\u5c06\u4ed6\u5f53\u4f5c\u5728\u6218 \u573a\u4e0a\u7684\u654c\u4eba\u770b\u5f85\u4e86\uff0c\u4ed6\u4eec\u81ea\u613f\u7ed9\u53e3\u4f9b\u56fa\u7136\u6700\u597d\uff0c\u82e5\u662f\u4e0d\u613f\uff0c\u4e5f\u53ea\u597d\u7531\u5f97\u4ed6\u4eec\uff0c\u54b1\u4eec\u53ea\u6c42\u6253\u57ae \u654c\u4eba\uff0c\u5c31\u662f\u8dd1\u6389\u4e00\u4e2a\u897f\u660c\u5c06\u519b\uff0c\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u4e0d\u4e86\u7684\u4e8b\u3002\u201d\u539f\u6765\u8fd9\u662f\u4e49\u519b\u7edf\u9886\u53f6\u6155\u534e\u9881\u5e03\u7684 \u547d\u4ee4\uff0c\u547d\u4ee4\u4ea4\u4ee3\u4e0d\u53ef\u8650\u5f85\u4fd8\u864f\uff0c\u8fd9\u4e49\u519b\u5934\u76ee\u56e0\u4e3a\u53f2\u7ea2\u82f1\u5e76\u4e0d\u5728\u4e49\u519b\u4e4b\u4e2d\u4efb\u804c\uff0c\u6545\u6b64\u8bf4\u5f97\u5f88\u662f \u59d4\u5a49\uff0c\u4f46\u8fd9\u4e00\u6761\u4f18\u5f85\u4fd8\u864f\u7684\u653f\u7b56\u4e5f\u8fd8\u662f\u4ea4\u4ee3\u660e\u767d\u4e86\u3002 \u3000\u3000\u90a3\u4e09\u4e2a\u4fd8\u864f\u8d77\u521d\u4ee5\u4e3a\u843d\u5728\u654c\u4eba\u4e4b\u624b\uff0c\u5fc5\u6b7b\u65e0\u7591\uff0c\u6b64\u65f6\u89c1\u4e49\u519b\u6349\u4e86\u4ed6\u4eec\u4e0d\u6253\u4e0d\u9a82\uff0c\u8fde\u53f2\u7ea2 \u82f1\u8981\u5411\u4ed6\u4eec\u903c\u4f9b\u4e5f\u53d7\u5230\u963b\u6b62\uff0c\u4e0d\u89c9\u559c\u51fa\u671b\u5916\uff0c\u8fd9\u624d\u4e89\u7740\u53d1\u8a00\uff0c\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u5e05\u5b5f\u96c4\u9003\u5411\u4f55 \u65b9\uff0c\u6211\u4eec\u59d4\u5b9e\u4e0d\u77e5\u3002\u201d\u53e6\u4e00\u4e2a\u8bf4\u9053\uff1a\u201c\u4f46\u6211\u77e5\u9053\u4ed6\u4e1a\u5df2\u53d7\u4e86\u91cd\u4f24\uff0c\u4e00\u5b9a\u8dd1\u4e0d\u8fdc\u7684\u3002\u201d\u4e00\u4e2a\u8bf4 \u9053\uff1a\u5e05\u5b5f\u96c4\u7684\u4e0b\u843d\u6211\u867d\u7136\u4e0d\u77e5\uff0c\u4f46\u521a\u9003\u8131\u7684\u90a3\u4e2a\u4eba\uff0c\u5374\u662f\u5c06\u519b\u5e9c\u7684\u603b\u7ba1\u5b89\u4fca\u5ead\u3002\u201d \u3000\u3000\u867d\u7136\u4ecd\u662f\u4e0d\u77e5\u5e05\u5b5f\u96c4\u7684\u53bb\u5411\uff0c\u4f46\u603b\u7b97\u662f\u83b7\u5f97\u6761\u7ebf\u7d22\u3002\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5b89\u4fca\u5ead\u521a\u521a \u8dd1\u6389\u3002\u201d\u4e49\u519b\u5934\u76ee\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u90a3\u4eba\u624b\u6bb5\u5982\u6b64\u72e0\u8fa3\uff0c\u539f\u6765\u662f\u5b89\u4fca\u5ead\u3002\u3002\u4f46\u4ed6\u5df2\u7ecf\u8dd1\u5f97\u8fdc\u4e86\u3002 \u6050\u6015\u8ffd\u4e0d\u4e0a\u5566\u3002\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4e0d\u6015\uff0c\u6211\u53bb\u8ffd\u4ed6\uff0c\u4e00\u5b9a\u8ffd\u5f97\u4e0a\u7684\uff01\u201d \u3000\u3000\u4e49\u519b\u5934\u76ee\u529d\u9053\uff1a\u201c\u53f2\u59d1\u5a18\u4f55\u5fc5\u5b64\u8eab\u72af\u9669\uff1f\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u77e5\u9053\u6253\u4e00\u573a\u4ed7\u4e0d\u5728\u4e4e\u8dd1\u6389\u654c \u65b9\u7684\u4e00\u4e24\u4e2a\u5c06\u519b\uff0c\u4f46\u6211\u4e0e\u5e05\u5b5f\u96c4\u4ec7\u6df1\u4f3c\u6d77\uff0c\u82e5\u4e0d\u5c06\u4ed6\u64d2\u83b7\uff0c\u6211\u5b9e\u662f\u96be\u4ee5\u7518\u5fc3\u3002\u201d \u3000\u3000\u4e49\u519b\u5934\u76ee\u89c1\u5979\u4e0d\u542c\u529d\u963b\uff0c\u53ea\u597d\u5c06\u5b89\u4fca\u5ead\u9003\u8dd1\u7684\u65b9\u5411\u544a\u8bc9\u5979\uff0c\u5e76\u8bf4\u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u6211\u77e5\u9053 \u4f60\u672c\u9886\u9ad8\u5f3a\uff0c\u4f46\u8fd8\u662f\u8bf7\u4f60\u591a\u52a0\u5c0f\u5fc3\u7684\u597d\u3002\u90a3\u53ae\u6b66\u529f\u5f88\u662f\u5389\u5bb3\uff0c\u6211\u4eec\u4e03\u516b\u4e2a\u4eba\uff0c\u90fd\u662f\u4ed6\u6253\u4f24 \u7684\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u8c22\u8fc7\u4e86\u8fd9\u4e2a\u5934\u76ee\uff0c\u7acb\u5373\u4e0a\u9a6c\u5c31\u8ffd\u3002\u4e49\u519b\u8fd9\u4e00\u5c0f\u961f\u4e0d\u8fc7\u5341\u591a\u4e2a\u4eba\uff0c\u53d7\u4f24\u7684\u4eba\u6570\u5df2\u8fbe \u4e00\u534a\uff0c\u5fc5\u987b\u9001\u53d7\u4f24\u7684\u4eba\u56de\u53bb\u6551\u6cbb\uff0c\u4ed6\u4eec\u7684\u5750\u9a91\u4e5f\u8ffd\u4e0d\u4e0a\u53f2\u7ea2\u82f1\u7684\u201c\u7167\u591c\u72ee\u5b50\u201d\uff0c\u53ea\u597d\u5148\u884c\u56de \u53bb\uff0c\u6253\u7b97\u5728\u4e0e\u5927\u961f\u4f1a\u5408\u4e4b\u540e\uff0c\u518d\u6d3e\u4eba\u6765\u63a5\u5e94\u5979\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u5feb\u9a6c\u75be\u9a70\uff0c\u8dd1\u4e86\u4e00\u4f1a\uff0c\u679c\u7136\u89c1\u7740\u5b89\u4fca\u5ead\u9a91\u4e00\u5339\u52a3\u9a6c\uff0c\u843d\u8352\u800c\u9003\u3002\u53f2\u7ea2\u82f1\u559d\u9053\uff1a \u201c\u5b89\u4fca\u5ead\uff0c\u4f60\u8dd1\u4e0d\u6389\u7684\uff01\u201d \u3000\u3000\u5b89\u4fca\u5ead\u7684\u672c\u9886\u5176\u5b9e\u4e0d\u5728\u53f2\u7ea2\u82f1\u4e4b\u4e0b\uff0c\u4f46\u56e0\u4e0d\u77e5\u53f2\u7ea2\u82f1\u7684\u540e\u9762\u6709\u6ca1\u6709\u4eba\uff0c\u4ed6\u5df2\u662f\u60ca\u5f13\u4e4b \u9e1f\uff0c\u5f53\u7136\u4e0d\u6562\u604b\u6218\uff0c\u770b\u89c1\u53f2\u7ea2\u82f1\u903c\u5f97\u8fd1\u4e86\uff0c\u626c\u624b\u5c31\u662f\u4e09\u67c4\u98de\u5200\u3002 \u3000\u3000\u8fd9\u5339\u201c\u7167\u591c\u72ee\u5b50\u201d\u60ef\u7ecf\u6218\u9635\uff0c\u795e\u9a8f\u5f02\u5e38\uff0c\u4e00\u89c1\u5371\u9669\uff0c\u56db\u8e44\u79bb\u5730\uff0c\u9a6e\u7740\u53f2\u7ea2\u82f1\uff0c\u5c31\u50cf\u817e\u4e91 \u9a7e\u96fe\u4e00\u822c\uff0c\u8df3\u8fc7\u4e86\u4e00\u8fb9\u3002\u53f2\u7ea2\u82f1\u4ed7\u7740\u9a6c\u5339\uff0c\u907f\u5f00\u4e86\u4e24\u67c4\u98de\u5200\uff0c\u7b2c\u4e09\u67c4\u672c\u6765\u4e5f\u6253\u4e0d\u7740\u5979\u7684\uff0c\u5979 \u5374\u6325\u51fa\u957f\u97ad\uff0c\u7279\u5730\u5c06\u90a3\u67c4\u98de\u5200\u5377\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u7684\u5750\u9a91\u8df3\u8fc7\u4e00\u8fb9\uff0c\u4e24\u4eba\u4e4b\u95f4\u7684\u8ddd\u79bb\u53c8\u62c9\u8fdc\u4e86\u4e00\u4e9b\u3002\u53f2\u7ea2\u82f1\u559d\u9053\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c \u4e5f\uff0c\u539f\u7269\u5949\u8fd8\uff01\u201d\u97ad\u68a2\u4e00\u6296\uff0c\u5c06\u90a3\u67c4\u98de\u5200\u53cd\u5c04\u56de\u53bb\u3002\u53ea\u542c\u5f97\u201c\u535c\u201d\u7684\u4e00\u58f0\uff0c\u98de\u5200\u523a\u7740\u4e86\u5b89\u4fca \u5ead\u90a3\u5339\u9a6c\u7684\u81c0\u90e8\u3002\u4f46\u56e0\u53f2\u7ea2\u82f1\u6c14\u529b\u4e0d\u8db3\uff0c\u98de\u5200\u53ea\u80fd\u5728\u9a6c\u81c0\u5212\u5f00\u4e00\u9053\u4f24\u53e3\uff0c\u63d2\u4e0d\u8fdb\u53bb\u3002\u53f2\u7ea2\u82f1 \u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u53ef\u60dc\u201d\uff01\u62e8\u8f6c\u9a6c\u5934\u7ee7\u7eed\u518d\u8ffd\u3002 \u3000\u3000\u98de\u5200\u867d\u672a\u4f24\u7740\u5b89\u4fca\u5ead\uff0c\u5b89\u4fca\u5ead\u4ea6\u5df2\u5403\u60ca\u4e0d\u5c0f\u3002\u5fc3\u60f3\uff1a\u201c\u6211\u7684\u9a6c\u6ca1\u53d7\u4f24\u4e5f\u8dd1\u4e0d\u8fc7\u5979\uff0c\u5982\u4eca \u662f\u51b3\u8ba1\u8eb2\u4e0d\u5f00\u4e86\u3002\u201d\u6709\u5fc3\u4e0e\u53f2\u7ea2\u82f1\u4e00\u62fc\uff0c\u53c8\u6015\u5979\u7684\u5f3a\u63f4\u5728\u540e\uff0c\u59cb\u7ec8\u63d0\u4e0d\u8d77\u52c7\u6c14\u3002 \u3000\u3000\u4e0d\u8fc7\u7247\u523b\uff0c\u53f2\u7ea2\u82f1\u7684\u5feb\u9a6c\u53c8\u5df2\u8ffd\u5230\u4e86\u5b89\u4fca\u5ead\u540e\u9762\uff0c\u8ddd\u79bb\u53ea\u4e0d\u8fc7\u6570\u4e08\u4e4b\u9065\u4e86\u3002\u5b89\u4fca\u5ead\u76ee\u5149 \u4e00\u77a5\uff0c\u5ffd\u5730\u6709\u6240\u53d1\u73b0\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u4f60\u8ffd\u6211\u5e72\u561b\uff1f\u6211\u5145\u5176\u91cf\u4e0d\u8fc7\u52a9\u7ea3\u4e3a\u8650\u800c\u5df2\uff0c\u5e05 \u5b5f\u96c4\u624d\u662f\u4f60\u7684\u4ec7\u4eba\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u597d\uff0c\u4f60\u628a\u5e05\u5b5f\u96c4\u7684\u4e0b\u843d\u8bf4\u51fa\u6765\uff0c\u6211\u5c31\u653e\u4f60\u8fc7\u53bb\u3002\u201d\u5b89\u4fca\u5ead\u9053\uff1a\u201c\u8fdc\u5728\u5929 \u8fb9\uff0c\u8fd1\u5728\u773c\u524d\uff0c\u90a3\u8fb9\u8eb2\u7740\u51e0\u4e2a\u4eba\uff0c\u5e05\u5b5f\u96c4\u5c31\u5728\u91cc\u9762\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u597d\uff0c\u8c05\u4f60\u4e5f\u8dd1\u4e0d\u6389\u3002\u5018\u82e5\u4f60\u8bf4\u7684\u662f\u5047\u8bdd\uff0c\u56de\u5934\u6211\u518d\u627e\u4f60\u51fa\u6c14\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u5411\u5b89\u4fca\u5ead\u6240\u6307\u7684\u65b9\u5411\u8dd1\u53bb\uff0c\u53ea\u89c1\u6709\u51e0\u4e2a\u6e05\u5175\u8eb2\u5728\u4e71\u8349\u4e1b\u4e2d\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u5927\u5931\u6240\u671b\uff0c\u5fc3\u9053\uff1a\u201c\u5b89\u4fca\u5ead\u8fd9\u53ae\u679c\u7136\u662f\u8c0e\u8a00\u9a97\u6211\u3002\u201d\u6b63\u60f3\u56de\u53bb\u627e\u5b89\u4fca\u5ead\u7684\u6666\u6c14\uff0c \u9769\u4e1b\u91cc\u51e0\u4e2a\u6e05\u5175\u5df2\u662f\u4e00\u54c4\u800c\u4e0a\uff0c\u539f\u6765\u8fd9\u51e0\u4e2a\u6e05\u5175\u8ba4\u4e0d\u5f97\u53f2\u7ea2\u82f1\uff0c\u89c1\u5979\u662f\u4e2a\u5e74\u8f7b\u6f02\u4eae\u7684\u5c0f\u59d1 \u5a18\uff0c\u9669\u5883\u672a\u8131\uff0c\u8272\u80c6\u53c8\u751f\uff0c\u7adf\u7136\u4e0a\u6765\u60f3\u8981\u6349\u4eba\u3001\u62a2\u9a6c\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u4e0d\u613f\u6ee5\u5f00\u6740\u6212\uff0c\u957f\u97ad\u4e00\u6325\uff0c\u5728\u9a6c\u80cc\u4e0a\u6253\u4e86\u4e00\u4e2a\u76d8\u65cb\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\u4e0d\u7edd\u4e8e \u8033\uff0c\u90a3\u51e0\u4e2a\u6e05\u5175\u7684\u5200\u67aa\u5251\u621f\uff0c\u90fd\u7ed9\u5979\u5377\u8131\u4e86\u624b\uff0c\u98de\u51fa\u8001\u8fdc\u3002\u8fd9\u624d\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u62b1\u5934\u9f20\u7a9c\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u6b63\u60f3\u56de\u53bb\uff0c\u76ee\u5149\u4e00\u77a5\uff0c\u5ffd\u89c1\u8349\u4e1b\u91cc\u8fd8\u4f0f\u6709\u4e00\u4e2a\u6e05\u5175\uff0c\u4f3c\u4e4e\u662f\u53d7\u4e86\u91cd\u4f24\u7684\u6a21\u6837\uff0c\u4fef \u5367\u5730\u4e0a\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u4e5f\u4e0d\u77e5\u662f\u6b7b\u662f\u6d3b\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u8d77\u4e86\u607b\u9690\u4e4b\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u4e49\u519b\u4e0d\u6740\u4fd8\u864f\uff0c\u4f60\u53d7\u4e86\u4f24\uff0c\u6211\u9001\u4f60\u53bb\u7ed9\u4e49\u519b\u533b\u6cbb\u5427\u3002\u201d \u90a3\u4eba\u4ecd\u7136\u662f\u52a8\u4e5f\u4e0d\u52a8\uff0c\u53f2\u7ea2\u82f1\u5fc3\u60f3\uff1a\u201c\u53ea\u6015\u662f\u5f53\u771f\u6b7b\u4e86\u3002\u201d\u5ffd\u5730\u53d1\u89c9\u8fd9\u4eba\u7684\u80cc\u5f71\u4f3c\u4e4e\u5f88\u719f\uff0c \u53f2\u7ea2\u82f1\u4f38\u51fa\u957f\u97ad\uff0c\u5c31\u60f3\u628a\u4ed6\u62c9\u8d77\u6765\u770b\u4e00\u770b\u4ed6\u7684\u5e90\u5c71\u771f\u8c8c\u3002 \u3000\u3000\u4e0d\u6599\u90a3\u4eba\u5ffd\u5730\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\u7ffb\u8d77\u8eab\u6765\uff0c\u53cd\u624b\u4e00\u62ff\uff0c\u6293\u7740\u4e86\u97ad\u68a2\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4e0b \u9a6c\uff01\u201d\u8fd9\u4e00\u62c9\u7684\u529b\u9053\u53c8\u6025\u53c8\u731b\uff0c\u53f2\u7ea2\u82f1\u6beb\u65e0\u9632\u5907\uff0c\u9aa4\u5403\u4e00\u60ca\uff0c\u7adf\u7136\u7ed9\u4ed6\u62c9\u4e0b\u9a6c\u6765\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u4eba\u6b63\u662f\u5e05\u5b5f\u96c4\u3002\u4ed6\u6362\u4e86\u666e\u901a\u5175\u58eb\u7684\u8863\u88f3\uff0c\u6d82\u9ed1\u7684\u8138\u5b54\uff0c\u9003\u51fa\u897f\u660c\u3002\u8d77\u521d\u4ed6\u672c\u662f \u548c\u5b89\u4fca\u5ead\u5728\u4e00\u8d77\u7684\uff0c\u4f46\u4e8b\u6025\u4e4b\u9645\uff0c\u5b89\u4fca\u5ead\u5374\u53ea\u987e\u81ea\u5df1\u9003\u547d\uff0c\u5c06\u4ed6\u629b\u4e0b\u4e86\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u4f19\u5728\u4e71\u8349\u4e1b\u4e2d\uff0c\u770b\u89c1\u53f2\u7ea2\u82f1\u8ffd\u672a\uff0c\u60c5\u77e5\u81ea\u5df1\u5018\u82e5\u9003\u8dd1\u7684\u8bdd\uff0c\u4e00\u5b9a\u4f1a\u7ed9\u5979\u8bc6\u7834\uff0c\u53ea \u597d\u88c5\u6b7b\uff0c\u6697\u8fd0\u7384\u529f\uff0c\u79ef\u8d2e\u5185\u529b\uff0c\u51c6\u5907\u9a97\u5f97\u8fc7\u56fa\u7136\u6700\u597d\uff0c\u9a97\u4e0d\u8fc7\u5c31\u548c\u5979\u4e00\u62fc\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u4e2d\u4e86\u6bd2\u9488\uff0c\u529f\u529b\u5df2\u4e0d\u5230\u539f\u6765\u7684\u4e24\u6210\uff0c\u4f46\u53f2\u7ea2\u82f1\u4e5f\u662f\u7ecf\u8fc7\u8fde\u756a\u5267\u6218\uff0c\u75b2\u52b3\u5c1a\u672a\u6062\u590d \u7684\u3002\u5e05\u5b5f\u96c4\u7684\u529f\u529b\u672c\u6765\u6bd4\u53f2\u7ea2\u82f1\u9ad8\u5f97\u591a\uff0c\u5982\u4eca\u5269\u4e0b\u4e86\u4e24\u6210\uff0c\u6070\u597d\u4e0e\u53f2\u7ea2\u82f1\u529f\u529b\u6089\u654c\u3002\u4f46\u56e0\u4ed6 \u662f\u51fa\u5176\u4e0d\u610f\u7684\u5947\u88ad\uff0c\u6545\u6b64\u5927\u5927\u5360\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u5229\u4e8e\u6025\u653b\uff0c\u4e00\u628a\u53f2\u7ea2\u82f1\u62c9\u4e0b\u9a6c\u6765\uff0c\u7acb\u5373\u4fbf\u662f\u5288\u80f8\u4e00\u638c\uff0c\u53f2\u7ea2\u82f1\u8eab\u5f62\u4e00\u4fa7\uff0c\u53f3\u624b\u593a \u97ad\uff0c\u5de6\u624b\u70b9\u4ed6\u7a74\u9053\u3002\u5e05\u5b5f\u96c4\u6c89\u81c2\u4e00\u538b\uff0c\u638c\u950b\u659c\u62b9\uff0c\u4ee5\u8fd1\u8eab\u640f\u6597\u7684\u5c0f\u64d2\u62ff\u624b\u6cd5\u53cd\u6293\u5979\u7684\u9165\u80f8\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u7109\u80fd\u7ed9\u4ed6\u6293\u7740\uff1f\u67f3\u8170\u4e00\u6446\uff0c\u9a88\u6307\u5982\u621f\uff0c\u5df2\u662f\u70b9\u5411\u4ed6\u638c\u5fc3\u7684\u201c\u52b3\u5bab\u7a74\u201d\uff0c\u8fd1\u8eab\u640f \u6597\uff0c\u529b\u5f3a\u8005\u80dc\uff0c\u529b\u5f31\u8005\u8d25\u3002\u53f2\u7ea2\u82f1\u4e00\u6307\u70b9\u7740\u4e86\u5e05\u5b5f\u96c4\u7684\u624b\u5fc3\uff0c\u5e05\u5b5f\u96c4\u624b\u8155\u4e00\u98a4\uff0c\u638c\u950b\u524a\u8fc7\uff0c \u4ea6\u5df2\u62c2\u7740\u4e86\u5979\u7684\u864e\u53e3\u3002\u53f2\u7ea2\u82f1\u957f\u97ad\u5760\u5730\uff0c\u8eab\u4e0d\u7531\u5df1\u5730\u9000\u4e86\u4e09\u6b65\u3002\u5e05\u5b5f\u96c4\u7ed9\u70b9\u4e2d\u4e86\u201c\u52b3\u5bab \u7a74\u201d\uff0c\u8f6c\u773c\u95f4\u4e00\u6761\u53f3\u81c2\u5df2\u662f\u5982\u540c\u762b\u75ea\u4e00\u822c\uff0c\u4f7f\u4e0d\u4e0a\u529b\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u751f\u6015\u53f2\u7ea2\u82f1\u8fd8\u6709\u5389\u5bb3\u7684\u540e \u7740\uff0c\u8fde\u5fd9\u8df3\u8fc7\u4e00\u8fb9\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u5fc3\u601d\u8f6c\u5f97\u6781\u5feb\uff0c\u8d77\u521d\u4ed6\u672c\u662f\u60f3\u628a\u53f2\u7ea2\u82f1\u64d2\u4e3a\u4eba\u8d28\u7684\uff0c\u4e00\u53d1\u89c9\u96be\u4ee5\u5c06\u5979\u5236\u4f0f\uff0c\u7acb\u5373 \u8f6c\u4e86\u5ff5\u5934\uff0c\u62a2\u53f2\u7ea2\u82f1\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u201d\u3002 \u3000\u3000\u4e0d\u6599\u8fd9\u5339\u201c\u7167\u591c\u72ee\u5b50\u201d\u4e43\u662f\u53ea\u8ba4\u4e3b\u4eba\u7684\u826f\u9a79\uff0c\u9664\u4e86\u53f2\u767d\u90fd\u3001\u53f2\u7ea2\u82f1\u5144\u59b9\u53ef\u4ee5\u9a91\u5b83\u4e4b\u5916\uff0c \u522b\u7684\u4eba\u9a91\u5b83\uff0c\u5b83\u975e\u53d1\u813e\u6c14\u4e0d\u53ef\u3002\u4ece\u524d\u91d1\u9010\u6d41\u62a2\u8fd9\u5339\u5750\u9a91\u4e4b\u65f6\uff0c\u5b83\u4e5f\u66fe\u8e22\u4e86\u91d1\u9010\u6d41\u4e00\u811a\uff0c\u91d1\u9010 \u6d41\u51ed\u7740\u4e00\u8eab\u6b66\u529f\u624d\u5236\u4f0f\u5f97\u4e86\u5b83\u3002\u5b83\u7684\u813e\u6c14\u4e4b\u70c8\uff0c\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u82e5\u662f\u6ca1\u53d7\u91cd\u4f24\uff0c\u8981\u5236\u4f0f\u201c\u7167\u591c\u72ee\u5b50\u201d\u4e0d\u96be\uff0c\u5982\u4eca\u5374\u662f\u53ea\u80fd\u81ea\u8ba8\u82e6\u5403\u4e86\uff0c\u5e05\u5b5f\u96c4\u624b \u6309\u9a6c\u978d\uff0c\u4fa7\u8eab\u8df3\u4e0a\u9a6c\u80cc\uff0c\u201c\u7167\u591c\u72ee\u4e8e\u201d\u5ffd\u5730\u56db\u8e44\u79bb\u5730\uff0c\u540e\u811a\u4e00\u8e22\uff0c\u81c0\u80cc\u4e00\u6380\uff0c\u4e00\u8df3\u8df3\u8d77\u4e08\u591a \u9ad8\uff0c\u5e05\u5b5f\u96c4\u8eab\u5f62\u521a\u521a\u8dc3\u8d77\uff0c\u989d\u89d2\u7ed9\u8e22\u4e2a\u6b63\u7740\uff0c\u767b\u65f6\u53c8\u518d\u8dcc\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u542c\u5f97\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\u500f\u7136\u800c\u81f3\uff0c\u53f2\u7ea2\u82f1\u5df2\u662f\u62d4\u51fa\u77ed\u5251\uff0c\u6251\u4e0a\u524d\u6765\u3002\u5e05 \u5b5f\u96c4\u5728\u5730\u4e0a\u6253\u4e86\u51e0\u4e2a\u6eda\uff0c\u62fe\u8d77\u4e00\u6839\u6811\u679d\uff0c\u8df3\u8d77\u6765\u7b11\u9053\uff1a\u201c\u53f2\u7ea2\u82f1\uff0c\u4f60\u820d\u4e0d\u5f97\u6211\u662f\u4e0d\u662f\uff1f\u4fd7\u8bed \u8bf4\u5f97\u597d\uff0c\u5ac1\u9e21\u968f\u9e21\uff0c\u5ac1\u72ac\u968f\u72ac\u3002\u597d\uff0c\u90a3\u4f60\u5c31\u968f\u6211\u5230\u9ec4\u6cc9\u8def\u4e0a\u505a\u5bf9\u592b\u59bb\u5427\uff01\u201d \u3000\u3000\u6b64\u65f6\u4ed6\u53c8\u5df2\u53d7\u4e86\u53f2\u7ea2\u82f1\u7684\u4e24\u5904\u5251\u4f24\uff0c\u6740\u673a\u9661\u8d77\uff0c\u6012\u53d1\u5982\u72c2\uff0c\u5f53\u771f\u662f\u4e0b\u4e86\u51b3\u5fc3\uff0c\u8981\u4e0e\u53f2\u7ea2 \u82f1\u540c\u5f52\u4e8e\u5c3d\u4e86\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u867d\u7136\u5df2\u662f\u5f3a\u5f29\u4e4b\u672a\uff0c\u4f46\u4ee5\u4ed6\u7684\u6b66\u5b66\u9020\u8be3\uff0c\u62fc\u8d77\u547d\u6765\uff0c\u4e5f\u5b9e\u662f\u4e0d\u53ef\u5c0f\u89d1\uff0c\u4e00\u6839\u6811 \u679d\uff0c\u5728\u4ed6\u624b\u4e2d\u4f7f\u51fa\uff0c\u5c45\u7136\u517c\u6709\u5200\u5251\u4e0e\u5224\u5b98\u7b14\u7684\u62db\u6570\uff0c\u62db\u62db\u90fd\u662f\u6307\u5411\u53f2\u7ea2\u82f1\u7684\u8981\u5bb3\u7a74\u9053\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u521a\u624d\u7ed9\u4ed6\u6253\u4e86\u4e00\u638c\uff0c\u867d\u7136\u672a\u53d7\u5185\u4f24\uff0c\u6c14\u529b\u53c8\u5df1\u635f\u8017\u4e0d\u5c11\uff0c\u5728\u4ed6\u731b\u653b\u4e4b\u4e0b\uff0c\u4e0d\u8fc7\u7247 \u523b\uff0c\u66f4\u662f\u9669\u8c61\u73af\u751f\uff0c\u53ea\u6709\u62db\u67b6\u7684\u4efd\u513f\u3002 \u3000\u3000\u5e78\u800c\u5728\u5f7c\u6b64\u90fd\u662f\u5f3a\u5f29\u4e4b\u672b\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c\u4e00\u6765\u662f\u5e05\u5b5f\u96c4\u53d7\u7684\u6bd2\u4f24\u6bd4\u5979\u91cd\u5f97\u591a\uff1b\u4e8c\u6765\u5e05\u5b5f\u96c4 \u521a\u521a\u7ed9\u5979\u70b9\u4e2d\u638c\u5fc3\u7684\u201c\u52b3\u5bab\u7a74\u201d\uff0c\u53f3\u81c2\u5982\u540c\u762b\u75ea\uff0c\u867d\u7136\u7ecf\u8fc7\u4ed6\u8fd0\u6c14\u6d3b\u8840\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u8fd9\u6761\u53f3 \u81c2\u4e5f\u8fd8\u672a\u80fd\u7075\u6d3b\u4f7f\u7528\u3002\u8fd9\u4e48\u4e00\u6765\uff0c\u5c31\u7b49\u4e8e\u7f1a\u4e86\u4e00\u53ea\u624b\u6765\u5bf9\u4ed8\u53f2\u7ea2\u82f1\u3002\u4e09\u6765\u5e05\u5b5f\u96c4\u4e43\u662f\u8d25\u519b\u4e4b \u5c06\uff0c\u7eb5\u7136\u51b3\u610f\u4e0e\u53f2\u7ea2\u82f1\u62fc\u547d\uff0c\u5fc3\u4e2d\u4e5f\u96be\u514d\u6709\u4e9b\u865a\u602f\uff0c\u6015\u6709\u4e49\u519b\u968f\u540e\u8ffd\u6765\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u770b\u51fa\u5e05\u5b5f\u96c4\u7684\u6bd2\u4f24\u5c31\u5feb\u53d1\u4f5c\uff0c\u5f53\u4e0b\u6c89\u7740\u5e94\u4ed8\uff0c\u5979\u7684\u8f7b\u529f\u662f\u6bd4\u5bf9\u65b9\u9ad8\u660e\u7684\uff0c\u5728\u6570\u5341 \u62db\u4e4b\u5185\uff0c\u53ea\u6709\u817e\u632a\u95ea\u5c55\u7684\u5c0f\u5de7\u529f\u592b\u62db\u67b6\uff0c\u5927\u7ea6\u5728\u8fc7\u4e86\u4e94\u5341\u62db\u4e4b\u540e\uff0c\u5e05\u5b5f\u96c4\u731b\u653b\u4e0d\u901e\uff0c\u5df2\u662f\u518d \u8870\u4e09\u7aed\uff0c\u53f2\u7ea2\u82f1\u89c5\u5f97\u4e00\u4e2a\u7834\u7efd\uff0c\u5530\u7684\u4e00\u5251\uff0c\u524a\u65ad\u4ed6\u7684\u6811\u679d\u3002\u5e05\u5b5f\u96c4\u4e5f\u771f\u591f\u51f6\u608d\uff0c\u629b\u4e0b\u6811\u679d\uff0c \u53c8\u5c55\u5f00\u4e86\u7a7a\u624b\u5165\u767d\u5203\u7684\u62db\u6570\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u8e44\u58f0\u5f97\u5f97\uff0c\u6709\u4e00\u9a91\u9a6c\u5728\u6811\u6797\u4e2d\u51fa\u73b0\uff0c\u53f2\u7ea2\u82f1\u5fc3\u4e2d\u5927\u559c\uff0c\u53eb\u9053\uff1a\u201c\u9010\u6d41\uff0c\u5feb\u6765\uff01\u201d \u4e0d\u6599\u62bd\u773c\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u5b89\u4fca\u5ead\u53bb\u800c\u590d\u6765\uff01 \u3000\u3000\u539f\u6765\u5b89\u4fca\u5ead\u90a3\u5339\u5750\u9a91\u53d7\u4f24\u4e4b\u540e\uff0c\u8d8a\u8d70\u8d8a\u6162\u3002\u53f2\u7ea2\u82f1\u4e0e\u5e05\u5b5f\u96c4\u82e6\u6597\uff0c\u7ecf\u8fc7\u4e86\u5dee\u4e0d\u591a\u4e00\u67f1\u9999 \u7684\u65f6\u523b\uff0c\u5b89\u4fca\u5ead\u8fd8\u672a\u8d70\u51fa\u8fd9\u4e2a\u6797\u5b50\u3002 \u3000\u3000\u5b89\u4fca\u5ead\u8d77\u521d\u5bb3\u6015\u6709\u8ffd\u5175\u8ddf\u540e\uff0c\u672c\u6765\u662f\u53ea\u60f3\u9003\u547d\u7684\uff0c\u4f46\u7ecf\u8fc7\u4e86\u4e00\u67f1\u9999\u7684\u65f6\u523b\uff0c\u4ed6\u542c\u5f97\u51fa\u8fd8 \u53ea\u662f\u53f2\u7ea2\u82f1\u4e0e\u5e05\u5b5f\u96c4\u5355\u6253\u72ec\u6597\uff0c\u800c\u4e14\u4f3c\u4e4e\u8fd8\u662f\u5e05\u5b5f\u96c4\u5360\u4e86\u4e0a\u98ce\u3002\u56e0\u6b64\u4ed6\u53c8\u5927\u7740\u773c\u5b50\u56de\u6765\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u56de\u6765\u7684\u76ee\u7684\uff0c\u5374\u5e76\u4e0d\u662f\u4e00\u5b9a\u8981\u5e2e\u5fd9\u5e05\u5b5f\u96c4\uff0c\u4ed6\u53ea\u662f\u60f3\u770b\u98ce\u4f7f\u8235\uff0c\u5047\u5982\u5e05\u5b5f\u96c4\u5df2\u662f\u7a33 \u64cd\u80dc\u5238\uff0c\u4ed6\u5c31\u987a\u624b\u628a\u53f2\u7ea2\u82f1\u6740\u6389\uff0c\u5047\u5982\u770b\u90a3\u5f62\u52bf\uff0c\u4e00\u65f6\u4e09\u523b\u5c1a\u672a\u80fd\u5236\u4f0f\u53f2\u7ea2\u82f1\u7684\u8bdd\uff0c\u4ed6\u5c31\u6253 \u7b97\u62a2\u4e86\u53f2\u7ea2\u82f1\u90a3\u5339\u201c\u7167\u591c\u72ee\u5b50\u201d\uff0c\u72ec\u81ea\u9003\u751f\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u770b\u89c1\u6765\u7684\u4e0d\u662f\u91d1\u9010\u6d41\uff1b\u53cd\u800c\u662f\u5e05\u76c2\u96c4\u7684\u5f97\u529b\u5e2e\u624b\uff0c\u4e0d\u7981\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\u3002\u4f46\u4e5f\u5e78\u4e8f \u5979\u6709\u90a3\u4e48\u4e00\u53eb\uff0c\u53eb\u5f97\u5b89\u4fca\u5ead\u4e0d\u80fd\u4e0d\u6709\u51e0\u5206\u987e\u5fcc\u3002\u5b89\u4fca\u5ead\u6697\u81ea\u601d\u91cf\uff1a\u201c\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\u679c\u7136\u662f\u548c \u5979\u4e00\u540c\u8ffd\u6765\u7684\uff0c\u8981\u4e0d\u7136\u5979\u4e0d\u4f1a\u4ee5\u4e3a\u662f\u91d1\u9010\u6d41\u6765\u4e86\u3002\u5927\u7ea6\u662f\u56e0\u4e3a\u7167\u591c\u72ee\u5b50\u8dd1\u5f97\u592a\u5feb\uff0c\u91d1\u9010\u6d41\u4e00 \u65f6\u8ddf\u4e0d\u4e0a\u3002\u4f46\u8fd9\u5c0f\u5b50\u8f7b\u529f\u5353\u7edd\uff0c\u884c\u52a8\u5982\u540c\u9b3c\u9b45\uff0c\u5374\u4e5f\u96be\u4fdd\u4ed6\u4e0d\u4f1a\u968f\u65f6\u6765\u5230\u3002\u201d \u3000\u3000\u6b64\u65f6\u6b63\u662f\u53f2\u7ea2\u82f1\u5df2\u7ecf\u524a\u65ad\u4e86\u5e05\u5b5f\u96c4\u7684\u6811\u679d\uff0c\u5927\u5360\u4f18\u52bf\u7684\u65f6\u5019\u3002\u5b89\u4fca\u5ead\u81ea\u5fd6\u8981\u6740\u6389\u53f2\u7ea2\u82f1 \u4e0d\u96be\uff0c\u4f46\u53ea\u6015\u4e5f\u5f97\u5728\u6570\u5341\u62db\u5f00\u5916\uff01\u662f\u4ee5\u5c31\u4e0d\u514d\u6709\u70b9\u8e0c\u8e87\u4e86\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u89c1\u5b89\u4fca\u5ead\u8e0c\u8e87\u4e0d\u524d\uff0c\u4e0d\u7981\u5927\u4e3a\u7740\u6025\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5b89\u4fca\u5ead\uff0c\u4f60\u5feb\u4e0a\u6765\u628a\u8fd9\u4e2b\u5934\u6740 \u6389\uff0c\u6211\u4e00\u5b9a\u4fdd\u4e3e\u4f60\u5347\u5b98\uff0c\u81f3\u5c11\u4e5f\u505a\u4e2a\u53c2\u5c06\u3002\u201d\u5fc3\u91cc\u5373\u5728\u6697\u6697\u5492\u9a82\uff1a\u201c\u4f60\u770b\u6211\u52bf\u7a77\u529b\u8e59\uff0c\u5c45\u7136 \u60f3\u8981\u51fa\u5356\u6211\u3002\u6211\u82e5\u80fd\u9003\u51fa\u6027\u547d\uff0c\u6162\u6162\u518d\u548c\u4f60\u7b97\u5e10\u3002\u201d \u3000\u3000\u8981\u77e5\u9053\u5e05\u5b5f\u96c4\u4e54\u88c5\u6253\u626e\uff0c\u9003\u5411\u4f55\u65b9\u7684\uff0c\u53ea\u6709\u4e00\u4e2a\u5b89\u4fca\u5ead\u77e5\u9053\uff0c\u662f\u4ee5\u5e05\u5b5f\u96c4\u867d\u7136\u6ca1\u6709\u770b\u89c1 \u5b89\u4fca\u5ead\u7ed9\u53f2\u7ea2\u82f1\u6307\u8def\uff0c\u4f46\u5df2\u77e5\u9053\u5fc5\u662f\u5b89\u4fca\u5ead\u51fa\u5356\u81ea\u5df1\u65e0\u7591\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u4e0d\u5c01\u5b98\u8bb8\u613f\u4e5f\u8fd8\u7f62\u4e86\uff0c\u4e00\u5c01\u5b98\u8bb8\u613f\uff0c\u5012\u662f\u4ee4\u5f97\u5b89\u4fca\u5ead\u5fc3\u91cc\u6697\u6697\u53d1\u6bdb\u3002\u5fc3\u60f3\uff1a\u201c\u6211 \u7ed9\u53f2\u7ea2\u82f1\u6307\u8def\uff0c\u5373\u4f7f\u4ed6\u4e0d\u77e5\u9053\uff0c\u4f46\u6211\u4eca\u65e5\u4e5f\u66fe\u629b\u4e86\u4ed6\u4e0d\u7406\uff0c\u4ed6\u5c82\u80fd\u4e0d\u8bb0\u6068\u4e8e\u5fc3\uff1f\u6b64\u523b\u4ed6\u6709\u6c42 \u4e8e\u6211\uff0c\u5f53\u7136\u662f\u4ec0\u4e48\u90fd\u53ef\u7b54\u5e94\u3002\u4e8b\u60c5\u8fc7\u540e\uff0c\u5374\u53c8\u8c01\u80fd\u4fdd\u5f97\u4ed6\u4e0d\u7ffb\u8138\uff01\u6587\u9053\u5e84\u3001\u53f2\u767d\u90fd\u4e8c\u4eba\u4e0e\u4ed6 \u662f\u4f55\u7b49\u4ea4\u60c5\uff0c\u4ed6\u4e5f\u66fe\u60f3\u8981\u5c06\u4ed6\u4eec\u4e71\u7bad\u5c04\u6b7b\uff0c\u4f55\u51b5\u4e8e\u6211\uff1f\u201d\u5b89\u4fca\u5ead\u56e0\u4e3a\u592a\u719f\u6089\u8fd9\u4f4d\u957f\u5b98\u7684\u813e\u6c14 \u4e86\uff0c\u4ed6\u60f3\u8d77\u521a\u624d\u5728\u5c06\u519b\u5e9c\u4e4b\u65f6\uff0c\u5e05\u5b5f\u96c4\u5429\u5490\u4ed6\u62db\u96c6\u5f13\u7bad\u624b\uff0c\u5c06\u6587\u9053\u5e84\u3001\u53f2\u767d\u90fd\u548c\u91d1\u9010\u6d41\u53f2\u7ea2 \u82f1\u56db\u4eba\u4e00\u9f50\u4e71\u7bad\u5c04\u6b7b\u4e4b\u4e8b\uff0c\u4e0d\u7531\u5f97\u66f4\u662f\u5bd2\u5fc3\u3002\u53f2\u7ea2\u82f1\u4e03\u7a8d\u73b2\u73d1\uff0c\u89c1\u5b89\u4fca\u5ead\u5f90\u5f90\u4e0d\u6562\u5411\u524d\uff0c\u5df2 \u662f\u731c\u5230\u4ed6\u4eec\u4e8c\u4eba\u4e4b\u95f4\u5b9a\u6709\u5fc3\u75c5\uff0c\u767b\u65f6\u8ba1\u4e0a\u5fc3\u5934\uff0c\u4fbf\u5373\u8bf4\u9053\uff1a\u201c\u5b89\u603b\u7ba1\uff0c\u591a\u8c22\u4f60\u5566\uff0c\u4f60\u679c\u7136\u6ca1 \u6709\u9a97\u6211\uff0c\u8ba9\u6211\u627e\u5230\u4e86\u8fd9\u4e2a\u5978\u8d3c\uff01\u201d \u3000\u3000\u5b89\u4fca\u5ead\u5403\u4e86\u4e00\u60ca\uff0c\u5fd9\u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u4f60\u522b\u80e1\u8bf4\u516b\u9053\uff01\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u5e05\u5b5f\u96c4\u5df2\u7ecf\u662f\u4e00\u53ea \u5feb\u8981\u6b7b\u7684\u8001\u864e\u4e86\uff0c\u4f60\u8fd8\u6015\u4ed6\u4f5c\u751a\uff1f\u597d\uff0c\u4f60\u82e5\u662f\u6495\u4e0d\u4e0b\u9762\u5b50\uff0c\u90a3\u4f60\u5c31\u7ad9\u5728\u4e00\u8fb9\uff0c\u4e5f\u672a\u5c1d\u4e0d\u53ef\u3002 \u4f60\u7ed9\u6211\u6307\u8def\u7684\u529f\u52b3\uff0c\u6211\u5f53\u7136\u8fd8\u662f\u4e0d\u4f1a\u9690\u7792\u7684\u3002\u4e0d\u8fc7\u4f60\u82e5\u5e2e\u5fd9\u6211\u6253\u8fd9\u53ea\u6b7b\u8001\u864e\uff0c\u529f\u52b3\u5c82\u4e0d\u66f4\u5927 \u4e86\uff1f\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u542c\u4fe1\u8fd9\u9b3c\u4e2b\u5934\u7684\u6311\u62e8\u7684\uff0c\u5b89\u4fca\u5ead\u4f60\u4e0d\u5fc5\u6709\u6240\u731c\u5fcc\uff0c\u5feb\u5feb\u4e0a\u6765 \u628a\u8fd9\u4e2b\u5934\u6740\u6389\uff01\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u5b89\u4fca\u5ead\u5df2\u662f\u542c\u5f97\u51fa\u6765\uff0c\u4ed6\u5206\u660e\u662f\u5df2\u7ecf\u5bf9\u81ea\u5df1\u6709\u6240\u731c\u7591\uff0c\u5426\u5219 \u4e0d\u4f1a\u7528\u8fd9\u6837\u7684\u53e3\u6c14\u592e\u6c42\u81ea\u5df1\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u91d1\u9010\u6d41\u7684\u58f0\u97f3\u4f3c\u7bad\u4e00\u822c\u7684\u7a7f\u8fc7\u6811\u6797\uff0c\u8bf4\u9053\uff1a\u201c\u7ea2\u82f1\uff0c\u522b\u614c\uff0c\u6211\u6765 \u4e86\uff01\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u4e5f\u53eb\u9053\uff1a\u201c\u4fca\u5ead\uff0c\u522b\u614c\uff0c\u8fd9\u5c0f\u5b50\u8d77\u7801\u8fd8\u5728\u6570\u91cc\u4e4b\u5916\uff0c\u4f60\u6211\u5408\u529b\uff0c\u5feb\u5feb\u628a\u8fd9\u4e2b\u5934\u6740 \u4e86\uff0c\u54b1\u4eec\u53ef\u4ee5\u7528\u5979\u8fd9\u5339\u7167\u591c\u72ee\u5b50\u9003\u8d70\uff01\u201d \u3000\u3000\u5e05\u5b5f\u96c4\u867d\u7136\u53d7\u4f24\u6781\u91cd\uff0c\u4f46\u4ed6\u4ecd\u7136\u662f\u4e2a\u6b66\u5b66\u5927\u884c\u5bb6\uff0c\u8fd9\u4e2a\u5224\u65ad\u5e76\u6ca1\u9519\u8bef\u3002\u539f\u6765\u91d1\u9010\u6d41\u8fc4\u4eca \u5c1a\u672a\u53d1\u73b0\u53f2\u7ea2\u82f1\u7684\u6240\u5728\uff0c\u4ed6\u662f\u7528\u4f20\u97f3\u5165\u5bc6\u7684\u529f\u592b\uff0c\u5728\u6570\u91cc\u4e4b\u5916\u7684\u5730\u65b9\uff0c\u5c06\u58f0\u97f3\u9001\u8fc7\u6765\u7684\u3002 \u3000\u3000\u4f46\u5b89\u4fca\u5ead\u6700\u6015\u7684\u5c31\u662f\u91d1\u9010\u6d41\uff0c\u4ed6\u5374\u662f\u4e0d\u80fd\u4e0d\u614c\u3002\u800c\u4e14\u4ed6\u672c\u6765\u4e5f\u53ea\u662f\u51c6\u5907\u770b\u98ce\u4f7f\u8235\u7684\uff0c\u98ce \u5411\u4e0d\u5bf9\uff0c\u5e05\u5b5f\u96c4\u8981\u60f3\u4ed6\u5356\u547d\u7109\u8fd8\u80fd\u591f\uff1f\u5e05\u5b5f\u96c4\u63d0\u8d77\u4e86\u201c\u7167\u591c\u72ee\u5b50\u201d\uff0c\u6070\u6070\u4e5f\u662f\u63d0\u9192\u4e86\u4ed6\uff0c\u5b89 \u4fca\u5ead\u5fc3\u610f\u7acb\u51b3\uff0c\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6709\u8fd9\u6837\u7684\u597d\u9a6c\uff0c\u6211\u4f55\u5fc5\u548c\u4ed6\u5408\u4e58\uff1f\u4e00\u4e2a\u4eba\u9003\u8dd1\u4e0d\u66f4\u597d\u5417\uff1f\u7701 \u5f97\u8fd8\u8981\u7167\u987e\u4ed6\u8fd9\u4e2a\u75c5\u592b\uff01\u201d\u5f53\u4e0b\u7acb\u5373\u5411\u201c\u7167\u591c\u72ee\u5b50\u201d\u8dd1\u53bb\uff0c\u4e00\u4e2a\u98de\u8eab\uff0c\u8df3\u4e0a\u9a6c\u80cc\u3002 \u3000\u3000\u201c\u7167\u591c\u72ee\u5b50\u201d\u53d1\u8d77\u813e\u6c14\uff0c\u4e00\u6380\u4e00\u8dc3\uff0c\u4f46\u5b89\u4fca\u5ead\u672a\u66fe\u53d7\u4f24\uff0c\u6b66\u529f\u4e4b\u5f3a\uff0c\u5374\u8db3\u4ee5\u5236\u4f0f\u8fd9\u5339\u9a8f \u9a6c\u3002\u4ed6\u63ea\u7740\u9a6c\u9b03\uff0c\u53cd\u624b\u4e00\u62cd\u9a6c\u81c0\uff0c\u201c\u7167\u591c\u72ee\u5b50\u201d\u4e0d\u7531\u5f97\u4e0d\u8d1f\u75db\u72c2\u5954\uff0c\u8dd1\u51fa\u6811\u6797\u3002 \u3000\u3000\u5b89\u4fca\u5ead\u8fd9\u624d\u8fdc\u8fdc\u7684\u626c\u58f0\u53eb\u9053\uff1a\u201c\u5e05\u5c06\u519b\uff0c\u4f60\u6b66\u529f\u76d6\u4e16\uff0c\u4e00\u4e2a\u5c0f\u4e2b\u5934\u54ea\u6709\u5bf9\u4ed8\u4e0d\u4e86\u4e4b\u7406\uff1f \u5373\u4f7f\u518d\u52a0\u4e0a\u91d1\u9010\u6d41\u8fd9\u4e2a\u5c0f\u5b50\uff0c\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff01\u5351\u804c\u4e0d\u6562\u4e0e\u5c06\u519b\u4e89\u529f\uff0c\u8bf7\u6055\u5351\u804c\u5c11\u966a\u4e86\uff01\u201d\u628a \u5e05\u5b5f\u96c4\u6c14\u5f97\u53d1\u660f\u3002 \u3000\u3000\u53ea\u542c\u5f97\u91d1\u9010\u6d41\u7684\u58f0\u97f3\u4e00\u5ffd\u513f\u4e1c\uff0c\u4e00\u5ffd\u513f\u897f\uff0c\u63a5\u8fde\u53eb\u4e86\u51e0\u6b21\uff1a\u201c\u7ea2\u82f1\uff0c\u522b\u614c\uff0c\u6211\u6765\u4e86\uff01\u201d \u58f0\u5df2\u5230\uff0c\u4eba\u5374\u4f9d\u7136\u672a\u89c1\u3002\u539f\u6765\u4ed6\u6b63\u5728\u591a\u65b9\u63a2\u6d4b\u53f2\u7ea2\u82f1\u7684\u6240\u5728\uff0c\u65b9\u5411\u5c1a\u672a\u8d70\u5bf9\uff0c\u4e0d\u8fc7\u8ddd\u79bb\u5374\u53c8 \u5df2\u662f\u8fd1\u4e86\u4e00\u4e9b\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u5438\u4e86\u53e3\u6c14\uff0c\u53eb\u9053\uff1a\u201c\u9010\u6d41\uff0c\u6211\u5728\u8fd9\u513f\uff0c\u5feb\u6765\uff01\u5feb\u6765\uff01\u201d\u58f0\u97f3\u752b\u51fa\uff0c\u968f\u5373\u4fbf\u542c\u5f97\u91d1 \u9010\u6d41\u53eb\u9053\uff1a\u201c\u6765\u4e86\u3002\u201d\u5176\u5b9e\u4ed6\u8fd8\u53ea\u662f\u542c\u89c1\u4e86\u53f2\u7ea2\u82f1\u7684\u56de\u58f0\u800c\u5df2\uff0c\u6025\u5207\u4e4b\u95f4\uff0c\u54ea\u91cc\u5c31\u80fd\u6765\u5230\uff1f \u5c31\u5728\u91d1\u9010\u6d41\u8fd9\u4e00\u58f0\u201c\u6765\u201d\uff01\u4f20\u6765\u4e4b\u9645\uff0c\u5e05\u5b5f\u96c4\u4e5f\u662f\u84e6\u5730\u51b7\u7b11\u9053\uff1a\u201c\u6765\u4e0d\u53ca\u4e86\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u4e3a\u4e86\u8ba9\u91d1\u9010\u6d41\u542c\u5f97\u89c1\u5979\u7684\u58f0\u97f3\uff0c\u5979\u662f\u7528\u5c3d\u4e86\u6c14\u529b\u53eb\u558a\u7684\u3002\u8fd9\u4e00\u4e0b\u767b\u65f6\u7ed9\u4e86\u5e05\u5b5f\u96c4 \u4ee5\u53ef\u4e58\u4e4b\u673a\uff0c\u53ea\u542c\u5f97\u4ed6\u4e00\u58f0\u5927\u543c\uff0c\u8eab\u5f62\u9aa4\u8d77\uff0c\u5de6\u638c\u9a88\u6307\u5982\u621f\uff0c\u70b9\u5411\u53f2\u7ea2\u82f1\u9762\u4e0a\u53cc\u775b\uff0c\u53f3\u638c\u6a2a \u638c\u5982\u5200\uff0c\u8ff3\u524a\u53f2\u7ea2\u82f1\u7684\u624b\u8155\uff0c\u8fd9\u4e00\u62db\u201c\u6491\u693d\u624b\u201d\u529b\u96c4\u52bf\u6377\uff0c\u53f2\u7ea2\u82f1\u53ea\u89c9\u864e\u53e3\u4e00\u9ebb\uff0c\u77ed\u5251\u867d\u7136 \u524a\u4e86\u51fa\u53bb\uff0c\u4f46\u5df2\u662f\u4f24\u4e0d\u4e86\u5e05\u76c2\u96c4\u3002\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u53f2\u7ea2\u82f1\u7684\u77ed\u5251\u638c\u63e1\u4e0d\u7262\uff0c\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u77ed\u5251\u8131\u624b\uff0c\u56de\u8eab\u4fbf\u8dd1\u3002\u5e05\u5b5f\u96c4\u559d\u9053\uff1a\u201c\u5f80\u54ea\u91cc\u8d70\uff1f\u563f\uff0c\u563f\uff0c\u8fd8\u662f\u8ddf\u6211\u5230\u9ec4\u6cc9\u8def\u4e0a \u505a\u4e86\u597d\u592b\u59bb\u5427\uff01\u201d\u811a\u5c16\u4e00\u6311\uff0c\u628a\u90a3\u67c4\u77ed\u5251\u6311\u4e86\u8d77\u6765\uff0c\u6309\u5230\u624b\u4e2d\uff0c\u7acb\u5373\u5c31\u5411\u53f2\u7ea2\u82f1\u63b7\u53bb\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u970d\u5730\u4e00\u4e2a\u51e4\u70b9\u5934\uff0c\u5251\u950b\u51e0\u4e4e\u662f\u64e6\u7740\u5979\u7684\u9b13\u8fb9\u98de\u8fc7\u3002\u53f2\u7ea2\u82f1\u4e0d\u6562\u518d\u8017\u6c14\u529b\u53eb\u5524\uff0c\u552f \u6709\u5411\u91d1\u9010\u6d41\u53d1\u58f0\u7684\u65b9\u5411\u98de\u8dd1\u3002\u5979\u7684\u8f7b\u529f\u672c\u6765\u6bd4\u5e05\u5b5f\u96c4\u9ad8\u660e\uff0c\u4f46\u53ef\u60dc\u5df2\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u5e05\u5b5f\u96c4\u5982 \u9f13\u8d77\u4e86\u6700\u540e\u4e00\u53e3\u6c14\uff0c\u53d1\u8a93\u8981\u4e0e\u5979\u540c\u5f52\u4e8e\u5c3d\u3002\u53f2\u7ea2\u82f1\u4e0d\u6562\u56de\u5934\uff0c\u53ea\u542c\u5f97\u80cc\u540e\u7684\u811a\u6b65\u58f0\u8d8a\u6765\u8d8a \u8fd1\u3002 \u3000\u3000\u5ffd\u89c1\u5bc6\u6797\u6df1\u5904\u4eba\u5f71\u4e00\u6643\uff0c\u91d1\u9010\u6d41\u7684\u8eab\u5f62\u5df2\u7ecf\u51fa\u73b0\uff0c\u53f2\u7ea2\u82f1\u5927\u559c\u53eb\u9053\uff1a\u201c\u9010\u6d41\uff0c\u5feb\u6765\u3002\u201d \u4e0d\u6599\u91d1\u9010\u6d41\u7684\u8eab\u5f62\u867d\u7136\u51fa\u73b0\uff0c\u4f46\u4e24\u4eba\u4e4b\u95f4\u7684\u8ddd\u79bb\uff0c\u4e5f\u8fd8\u6709\u5341\u6570\u4e08\u4e4b\u9065\uff0c\u53f2\u7ea2\u82f1\u731b\u529b\u4e00\u51b2\uff0c\u6251 \u4e0a\u524d\u5929\uff0c\u4e00\u4e0d\u7559\u795e\uff0c\u811a\u5c16\u7eca\u770b\u77f3\u5934\uff0c\u4e00\u4e2a\u8e09\u8dc4\uff0c\u51b2\u51fa\u4e86\u51e0\u6b65\uff0c\u6536\u4e0d\u4f4f\u52bf\uff0c\u8dcc\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5e05\u5b5f\u96c4\u54c8\u54c8\u5927\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u770b\u4f60\u8fd8\u9003\u5f97\u51fa\u6211\u7684\u638c\u5fc3\uff01\u201d\u4e00\u4e2a\u7bad\u6b65\u51b2\u524d\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u6307\u5c16 \u5df2\u662f\u89e6\u53ca\u53f2\u7ea2\u82f1\u7684\u5934\u53d1\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u6253\u4e86\u4e2a\u6eda\uff0c\u5e05\u5b5f\u96c4\u4e00\u6293\u6293\u7a7a\uff0c\u5e05\u5b5f\u96c4\u642c\u8d77\u4e00\u5757\u5927\u77f3\uff0c\u7528\u529b\u63b7\u51fa\uff0c\u72de\u7b11\u8bf4\u9053\uff1a\u201c\u975e \u5f97\u8ba9\u4f60\u8fd9\u81ed\u4e2b\u5934\u6b7b\u5728\u6211\u7684\u524d\u5934\u4e0d\u53ef\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u5c1a\u672a\u66fe\u722c\u8d77\uff0c\u8fd9\u5757\u5927\u77f3\u843d\u4e0b\uff0c\u5979\u662f\u5fc5\u6b7b\u65e0\u7591\u3002\u53f2\u7ea2\u82f1\u542c\u5f97\u98ce\u58f0\uff0c\u5fc3\u91cc\u4e00\u51c9\uff0c\u60f3 \u9053\uff1a\u201c\u597d\u5728\u9010\u6d41\u5df2\u7ecf\u6765\u4e86\uff0c\u4ed6\u4f1a\u7ed9\u6211\u62a5\u4ec7\u7684\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u95ed\u4e86\u53cc\u76ee\uff0c\u7b49\u5f85\u90a3\u5757\u5927\u77f3\u843d\u4e0b\uff0c\u4e0d\u6599\u5ffd\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u90a3\u5757\u77f3\u5934\u5e76\u6ca1\u843d\u4e0b\uff0c \u5374\u597d\u4f3c\u70b8\u5f00\u4e86\u4f3c\u7684\uff0c\u788e\u77f3\u597d\u50cf\u96e8\u70b9\u4e00\u822c\u4ece\u5979\u5934\u9876\u98de\u8fc7\uff0c\u867d\u7136\u4e5f\u6709\u51e0\u9897\u788e\u77f3\u5b50\u843d\u5728\u5979\u7684\u8eab\u4e0a\uff0c \u5374\u53ea\u662f\u4ee4\u5979\u7a0d\u7a0d\u611f\u5230\u75bc\u75db\u800c\u5df2\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\u8df3\u8d77\u8eab\u6765\uff0c\u8eab\u5f62\u672a\u7a33\uff0c\u5ffd\u5730\u7ed9\u4eba\u62b1\u7740\u3002\u53f2\u7ea2\u82f1\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u542c\u5f97\u4e00 \u58f0\u60e8\u547c\uff0c\u542c\u5f97\u51fa\u8fd9\u662f\u5e05\u76c2\u96c4\u7684\u53eb\u58f0\uff0c\u968f\u540e\u624d\u542c\u5f97\u91d1\u9010\u6d41\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u7ea2\u82f1\uff0c\u6ca1\u4e8b\u4e86\u3002\u90fd\u602a\u6211 \u4e0d\u597d\uff0c\u6765\u8fdf\u7247\u523b\u3002\u7d2f\u4f60\u53d7\u4e86\u60ca\u4e86\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u8fd9\u624d\u77e5\u9053\u662f\u5728\u91d1\u9010\u6d41\u7684\u6000\u91cc\uff0c\u7741\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u5c31\u5728\u4ed6\u4eec\u524d\u9762\u4e08\u8bb8\u4e4b\u5730\uff0c\u5e05\u5b5f\u96c4\u4fef \u5367\u5730\u4e0a\uff0c\u80cc\u5fc3\u4e0a\u63d2\u7740\u4e00\u53e3\u957f\u5251\uff0c\u5251\u67c4\u5140\u81ea\u98a4\u52a8\u4e0d\u4f11\u3002 \u3000\u3000\u539f\u6765\u5c31\u5728\u5e05\u5b5f\u96c4\u63b7\u51fa\u5927\u77f3\u4e4b\u65f6\uff0c\u91d1\u9010\u6d41\u5728\u4e03\u516b\u4e08\u4e4b\u5916\u4e5f\u628a\u7384\u94c1\u5b9d\u5251\u63b7\u51fa\uff0c\u5b9d\u5251\u649e\u788e\u4e86\u77f3 \u5934\uff0c\u4f59\u529b\u672a\u8870\uff0c\u597d\u50cf\u7bad\u4e00\u822c\u5730\u5c04\u8fc7\u53bb\uff0c\u4ece\u5e05\u5b5f\u96c4\u7684\u540e\u5fc3\u63d2\u5165\uff0c\u524d\u5fc3\u7a7f\u51fa\uff0c\u5c06\u4ed6\u7262\u7262\u5730\u8ba2\u5728\u5730 \u4e0a\u3002 \u3000\u3000\u6b63\u662f\uff1a \u3000\u3000\u96e8\u8fc7\u5929\u6674\u9022\u7231\u4fa3\uff0c\u8bdb\u5978\u6551\u7f8e\u5e86\u56e2\u5706\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e09\u5341\u4e5d\u56de\u3000\u5e7d\u8c37\u843d\u82b1\u57cb\u4fa0\u9aa8\u3000\u9752\u5929\u78a7\u6d77\u8bc1\u4e39\u5fc3 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e09\u5341\u4e5d\u56de\u3000\u5e7d\u8c37\u843d\u82b1\u57cb\u4fa0\u9aa8\u3000\u9752\u5929\u78a7\u6d77\u8bc1\u4e39\u5fc3 \u3000\u3000\u53f2\u7ea2\u82f1\u60ca\u9b42\u672a\u5b9a\u8f6f\u7ef5\u7ef5\u5730\u4f0f\u5728\u91d1\u9010\u6d41\u6000\u4e2d\uff0c\u53ea\u89c9\u8212\u7545\u65e0\u6bd4\u3002\u91d1\u9010\u6d41\u5728\u5979\u8033\u8fb9\u6084\u58f0\u8bf4\u9053\uff1a \u201c\u7ea2\u82f1\uff0c\u591a\u8c22\u4f60\u7684\u8bd7\u5e15\uff0c\u6211\u624d\u77e5\u9053\u6211\u81ea\u5df1\u662f\u4e2a\u5927\u50bb\u74dc\u3002\u54b1\u4eec\u4e0d\u5fc5\u6765\u751f\uff0c\u4eca\u751f\u5df2\u53ef\u7ed3\u4e3a\u8fde \u7406\u3002\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u8fd8\u62c5\u5fc3\u828d\u836f\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u89c1\u7740\u4f60\u5462\uff0c\u539f\u6765\u4f60\u4eec\u90a3\u5929\u5c31\u89c1\u7740\u4e86\u3002\u201d \u3000\u3000\u90a3\u6761\u624b\u5e15\u662f\u53f2\u7ea2\u82f1\u53eb\u5979\u7684\u4e2b\u5934\u828d\u836f\u62ff\u53bb\u627e\u91d1\u9010\u6d41\u7684\uff0c\u624b\u5e15\u4e0a\u5199\u7684\u90a3\u9996\u8bd7\u662f\uff1a\u201c\u613f\u4f5c\u8346\u8f72 \u8a93\u5165\u79e6\uff0c\u4f55\u6e10\u6d41\u6c34\u9047\u77e5\u97f3\u3002\u6b64\u751f\u5df2\u77e3\u4ed6\u751f\u5728\uff0c\u72b9\u6709\u5bd2\u6885\u4e00\u7247\u5fc3\u3002\u201d\u5982\u4eca\u53f2\u7ea2\u82f1\u542c\u5f97\u4ed6\u63d0\u8d77\u8bd7 \u4e2d\u7684\u8a00\u8bed\uff0c\u5df2\u77e5\u4ed6\u6536\u5230\u4e86\u8fd9\u6761\u8bd7\u5e15\uff0c\u4e0d\u7981\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u5bb3\u7f9e\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u638f\u6c34\u4e2d\u4e4b\u6708\uff0c\u53ea\u63a5\u6e05\u8f89\uff1b\u96e8\u5927\u4e0a\u4e4b\u82b1\uff0c\u4f46\u95fb\u9999\u6c14\u3002\u7ea2\u82f1\uff0c\u82e5\u4e0d\u662f\u4f60\u8bf4\u51fa \u6765\uff0c\u6211\u5f53\u771f\u8fd8\u4e0d\u77e5\u9053\u4f60\u7231\u6211\u7adf\u662f\u7231\u5f97\u5982\u6b64\u4e4b\u6df1\uff01\u4f46\u73b0\u5728\u53ef\u4e0d\u662f\u955c\u82b1\u6c34\u6708\u4e86\uff0c\u4f60\u8ba9\u6211\u95fb\u4e00\u95fb\uff0c \u5514\uff0c\u597d\u9999\uff0c\u597d\u9999\uff01\u201d\u53f2\u7ea2\u82f1\u7ea2\u6655\u6ee1\u9762\u3002\u55d4\u9053\uff1a\u201c\u8ba9\u4f60\u77e5\u9053\u6211\u7684\u5fc3\u4e8b\uff0c\u4f60\u5012\u53d6\u7b11\u8d77\u6211\u6765\u4e86\u3002 \u55ef\uff0c\u522b\u80e1\u95f9\u5566\uff0c\u6709\u4eba\u6765\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u653e\u5f00\u4e86\u53f2\u7ea2\u82f1\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u53f6\u6155\u534e\u5e26\u9886\u4e00\u961f\u4e49\u519b\uff0c\u6b63\u5728\u5411\u4ed6\u4eec\u6240\u5728\u4e4b\u5904\u9a70 \u6765\u3002\u91d1\u9010\u6d41\u5728\u5e05\u5b5f\u96c4\u7684\u5c38\u8eab\u4e0a\u62d4\u8d77\u7384\u94c1\u5b9d\u5251\uff0c\u62b9\u53bb\u8840\u75d5\uff0c\u7eb3\u5165\u5251\u9798\uff0c\u8fce\u4e0a\u524d\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u6155 \u534e\uff0c\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u6211\u9001\u7ed9\u4f60\u4eec\u4e49\u519b\u4e00\u4ef6\u793c\u7269\u3002\u201d \u3000\u3000\u53f6\u6155\u534e\u8df3\u4e0b\u9a6c\u6765\uff0c\u8eac\u8eab\u884c\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u5e05\u5b5f\u96c4\u8fd9\u8d3c\u5b50\u5df2\u7ed9\u5e08\u53d4\u6740\u4e86\uff0c\u606d\u559c\u5e08\u53d4\u7acb\u4e86 \u5927\u529f\uff0c\u8fd9\u4f4d\u662f\u53f2\u59d1\u5a18\u5427\uff1f\u201d\u91d1\u9010\u6d41\u8bf4\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u5f53\u4e0b\u7ed9\u53f2\u7ea2\u82f1\u4ecb\u7ecd\u53f6\u6155\u534e\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6 \u867d\u7136\u662f\u6211\u7684\u5e08\u4f84\uff0c\u6253\u4ed7\u7684\u672c\u9886\u53ef\u6bd4\u6211\u5927\u5f97\u591a\uff0c\u8fd9\u4e00\u573a\u5927\u6218\u5c31\u662f\u4ed6\u6307\u6325\u7684\u3002\u201d\u53f6\u6155\u534e\u9053\uff1a\u201c\u5e08 \u53d4\u592a\u5938\u5956\u4e86\uff0c\u8fd9\u4e00\u573a\u4ed7\u56fa\u7136\u662f\u9760\u4e49\u519b\u7684\u5144\u5f1f\u4eec\u4e2a\u4e2a\u594b\u52c7\u4e89\u5148\uff0c\u4f46\u53f2\u59d1\u5a18\u5728\u5c06\u519b\u5e9c\u4e2d\u9996\u5148\u53d1 \u96be\uff0c\u8fd9\u671b\u5e94\u5916\u5408\u7684\u529f\u52b3\u4e5f\u662f\u4e0d\u5c0f\uff0c\u6211\u8fd8\u672a\u66fe\u5f97\u5411\u53f2\u59d1\u5a18\u9053\u8c22\u5462\u3002\u201d \u3000\u3000\u674e\u5149\u590f\u3001\u6797\u9053\u8f69\u4e8c\u4eba\u6b64\u65f6\u4ea6\u5df2\u6765\u5230\uff0c\u674e\u5149\u590f\u8bf4\u9053\uff1a\u201c\u5c0f\u5e08\u53d4\uff0c\u4f60\u5230\u73b0\u5728\u8fd8\u5206\u4ec0\u4e48\u2018\u4f60 \u4eec\u2019\u2018\u6211\u4eec\u2019\uff1f\u6211\u4eec\u65e9\u5df2\u628a\u4f60\u548c\u53f2\u59d1\u5a18\u5f53\u4f5c\u54b1\u4eec\u4e49\u519b\u7684\u81ea\u5df1\u4eba\u4e86\u3002\u201d\u6797\u9053\u8f69\u4e5f\u7b11\u9053\uff1a\u201c\u5927\u5e08 \u54e5\u3001\u4f60\u8fd8\u4e0d\u77e5\u9053\u5417\uff0c\u8fd9\u4f4d\u53f2\u59d1\u5a18\u662f\u54b1\u4eec\u7684\u957f\u8f88\uff0c\u4f60\u5e94\u8be5\u53eb\u5979\u4e00\u58f0\u5e08\u5a76\u624d\u662f\u3002\u201d\u539f\u6765\u676f\u3001\u674e\u4e8c \u4eba\u65e9\u5df2\u4ece\u674e\u6566\u7b49\u4eba\u53e3\u4e2d\u5f97\u77e5\u4ed6\u4eec\u662f\u4e00\u5bf9\u7231\u4fa3\u3002\u53f2\u7ea2\u82f1\u7f9e\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u5fc3\u4e2d\u53ef\u662f\u5145\u6ee1\u751c\u610f\u3002 \u3000\u3000\u53f6\u6155\u534e\u53eb\u4eba\u628a\u5e05\u5b5f\u96c4\u7684\u9996\u7ea7\u5272\u4e0b\uff0c\u62ff\u56de\u53bb\u793a\u4f17\uff0c\u8ddf\u7740\u8bf4\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u7afa\u8001\u524d\u8f88\u4e5f\u5df2\u8fdb\u4e86 \u57ce\u4e86\uff0c\u5f88\u60f3\u89c1\u4f60\uff0c\u54b1\u4eec\u8fd9\u5c31\u4e00\u540c\u56de\u53bb\u5427\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\u3002\u4f60\u7684\u5e08\u7236\u6765\u4e86\u6ca1\u6709\uff1f\u201d\u53f6\u6155\u534e \u9053\uff1a\u201c\u5e08\u7236\u7559\u5728\u5c0f\u91d1\u5ddd\u8fd8\u6709\u4e00\u70b9\u4e8b\u60c5\u8981\u529e\uff0c\u6050\u6015\u8981\u8fdf\u51e0\u5929\u624d\u6765\u3002\u201d \u3000\u3000\u53f6\u6155\u534e\u6311\u4e86\u4e24\u5339\u5750\u9a91\u7ed9\u91d1\u9010\u6d41\u548c\u53f2\u7ea2\u82f1\u4e58\u5750\uff0c\u6545\u610f\u7a0d\u7a0d\u843d\u540e\uff0c\u8ba9\u4ed6\u4eec\u8d70\u5728\u524d\u5934\u3002\u91d1\u3001\u53f2 \u4e8c\u4eba\u5e76\u8f94\u800c\u884c\uff0c\u56de\u6765\u65f6\u5019\u7684\u5fc3\u60c5\u548c\u53bb\u65f6\u5f53\u7136\u662f\u5927\u4e0d\u4e00\u6837\u3002 \u3000\u3000\u6b64\u65f6\u4e24\u4eba\u5e76\u8f94\u5954\u9a70\uff0c\u7aef\u7684\u662f\uff1a\u201c\u8eab\u5982\u5f69\u98ce\u53cc\u98de\u7ffc\uff0c\u5fc3\u6709\u7075\u7280\u4e00\u70b9\u901a\u3002\u201d\u591a\u5c11\u8bef\u4f1a\uff0c\u90fd\u5df2 \u51b0\u6d88\uff1b\u65e0\u9650\u76f8\u601d\uff0c\u7ec8\u507f\u5bbf\u613f\u3002\u79f0\u5fc3\u4e50\u610f\uff0c\u53ef\u60f3\u800c\u77e5\uff0c\u6b64\u65f6\u4ed6\u4eec\u53ea\u5269\u4e0b\u4e00\u6869\u5fc3\u4e8b\uff0c\u90a3\u5c31\u662f\u5173\u4e8e \u5389\u5357\u661f\u7684\u4e0b\u843d\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4f60\u5f97\u5230\u4e86\u5389\u5927\u54e5\u7684\u6d88\u606f\u6ca1\u6709\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6ca1\u6709\u3002\u8d76\u7740\u6765\u627e\u4f60\uff0c\u4e5f\u8bb8\u662f \u770b\u5f97\u4e0d\u4ed4\u7ec6\uff0c\u4e00\u8def\u4e0a\u6ca1\u89c1\u7740\u5389\u5927\u54e5\u3002\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u5c06\u519b\u5e9c\u4e2d\u5e76\u6ca1\u53d1\u73b0\u4ed6\u7684\u5c38\u4f53\uff0c\u4f46\u613f\u4ed6\u662f \u5df2\u7ecf\u9003\u51fa\u53bb\u4e86\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8001\u5929\u7237\u4e00\u5b9a\u4f1a\u4fdd\u4f51\u597d\u4eba\u7684\uff0c\u54b1\u4eec\u56de\u53bb\u8bf7\u5927\u4f19\u513f\u4e00\u540c\u5e2e\u5fd9\u627e \u4ed6\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u90a3\u4f4d\u516c\u5b59\u59d1\u5a18\u662f\u7ea2\u7f28\u4f1a\u8235\u4e3b\u516c\u5b59\u5b8f\u7684\u5973\u513f\u5427\u3002\u6211\u770b\u5979\u53ef\u662f\u5f88\u5173\u5fc3\u5389\u5927\u54e5 \u554a\uff01\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f55\u53ea\u5173\u5fc3\uff0c\u6211\u66fe\u7ecf\u95ee\u8fc7\u674e\u6566\uff1b\u4ed6\u8bf4\u770b\u60c5\u5f62\u4ed6\u4eec\u6050\u6015\u65e9\u5df2\u662f\u4e00\u5bf9\u7231\u4fa3 \u4e86\u3002\u4e0d\u7792\u4f60\u8bf4\uff0c\u6211\u4e00\u76f4\u89c9\u5f97\u6709\u70b9\u5bf9\u4e0d\u4f4f\u5389\u5927\u54e5\uff0c\u4ee5\u4e3a\u4ed6\u5fc3\u91cc\u53ea\u6709\u4e00\u4e2a\u4f60\uff0c\u5982\u4eca\u624d\u77e5\u9053\u4e0d\u662f\u8fd9 \u6837\uff0c\u4ed6\u7684\u5fc3\u4e0a\u4eba\u539f\u6765\u662f\u516c\u5b59\u59d1\u5a18\uff0c\u8fd9\u6211\u5c31\u653e\u4e0b\u5fc3\u4e86\u3002\u201d\u53f2\u7ea2\u82f1\u55d4\u9053\uff1a\u201c\u4f60\u7684\u6bdb\u75c5\u5c31\u662f\u8001\u662f\u5bf9 \u6211\u653e\u5fc3\u4e0d\u4e0b\uff0c\u5982\u679c\u4f60\u76f8\u4fe1\u6211\uff0c\u5373\u4f7f\u6709\u522b\u4eba\u6b22\u559c\u6211\uff0c\u90a3\u53c8\u4e0e\u6211\u4f55\u5e72\uff1f\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u662f\uff0c\u4f60 \u8d23\u5907\u5f97\u5bf9\uff0c\u6211\u73b0\u5728\u660e\u767d\u4e86\uff1a\u4f60\u7684\u5fc3\u4e0a\u53ea\u6709\u4e00\u4e2a\u6211\uff0c\u6211\u7684\u5fc3\u4e0a\u4e5f\u53ea\u6709\u4e00\u4e2a\u4f60\uff01\u201d\u53f2\u7ea2\u82f1\u9053\uff1a \u201c\u522b\u53eb\u53eb\u56b7\u56b7\u4e86\uff0c\u5f53\u5fc3\u4eba\u5bb6\u542c\u89c1\u4e86\u7b11\u8bdd\u3002\u201d \u3000\u3000\u4e00\u884c\u4eba\u56de\u5230\u897f\u660c\uff0c\u79e6\u5143\u6d69\u3001\u5c01\u5999\u5ae6\u3001\u5b87\u6587\u96c4\u3001\u6c5f\u6653\u8299\u7b49\u4eba\u51fa\u6765\u8fce\u63a5\u3002\u91d1\u9010\u6d41\u4e0d\u89c1\u674e\u6566\u592b \u59bb\uff0c\u6b63\u5f85\u8be2\u95ee\uff0c\u79e6\u5143\u6d69\u5df2\u5148\u544a\u8bc9\u4ed6\u9053\uff1a\u201c\u674e\u6566\u592b\u59bb\u548c\u9648\u5149\u7167\u3001\u77f3\u971e\u59d1\u7b49\u4eba\u5df2\u7ecf\u5206\u5934\u51fa\u53d1\uff0c\u53bb \u627e\u5389\u5357\u661f\u4e86\u3002\u8fd8\u6709\u516d\u5408\u5e2e\u7684\u5e2e\u4f17\u6570\u767e\u4eba\uff0c\u51e1\u662f\u8ba4\u8bc6\u5389\u5357\u661f\u7684\u4e5f\u90fd\u968f\u7740\u4ed6\u4eec\u53bb\u5bfb\u627e\u4e86\u3002\u91d1\u5144\uff0c \u4f60\u5927\u53ef\u653e\u5fc3\uff0c\u6709\u8fd9\u4e48\u591a\u4eba\u5bfb\u627e\uff0c\u53ea\u8981\u5389\u5927\u54e5\u8fd8\u6d3b\u5728\u4eba\u95f4\uff0c\u4e00\u5b9a\u4f1a\u627e\u5f97\u7740\u4ed6\u7684\u201d\u91d1\u9010\u6d41\u9053\uff1a \u201c\u90a3\u4f4d\u516c\u5b59\u59d1\u5a18\u5462\uff1f\u201d\u77f3\u971e\u59d1\u7b11\u9053\uff1a\u201c\u516c\u5b59\u59d0\u59d0\u5728\u6218\u4e8b\u5c1a\u672a\u7ed3\u675f\u7684\u65f6\u5019\uff0c\u65e9\u5df2\u51fa\u57ce\u53bb\u4e86\u3002\u5979 \u662f\u7b2c\u4e00\u4e2a\u53bb\u627e\u5389\u5357\u661f\u7684\uff01 \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u5e86\u529f\u5bb4\u5df2\u7ecf\u6446\u597d\uff0c\u6b63\u7b49\u5f85\u4f60\u4eec\u56de\u6765\u3002\u201d\u91d1\u9010\u6d41\u77e5\u9053\u6709\u8fd9\u4e48\u591a\u4eba\u53bb\u627e\u5389\u5357\u661f \u4e4b\u540e\uff0c\u7a0d\u7a0d\u653e\u4e0b\u5fc3\u4e8b\uff0c\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5148\u53bb\u62dc\u89c1\u7afa\u8001\u524d\u8f88\u5427\u3002\u201d \u3000\u3000\u7afa\u5c1a\u7236\u5df2\u5f97\u901a\u62a5\uff0c\u4eb2\u81ea\u51fa\u6765\u8fce\u63a5\u3001\u751f\u9010\u6d41\u5411\u4ed6\u884c\u665a\u8f88\u4e4b\u793c\uff0c\u7afa\u5c1a\u7236\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u548c\u4f60 \u7684\u6c5f\u5e08\u5144\u662f\u513f\u5973\u4eb2\u5bb6\uff0c\u54b1\u4eec\u53ef\u662f\u5e73\u8f88\u5440\uff0c\u4f60\u522b\u5ba2\u6c14\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff01\u201d\u5f53\u4e0b\u4ecd\u7136\u5c48 \u4e86\u534a\u819d\uff0c\u4f5c\u4e86\u4e00\u4e2a\u957f\u63d6\uff0c\u7afa\u5c1a\u7236\u53cc\u624b\u4e00\u62ac\uff0c\u5c06\u4ed6\u6276\u8d77\uff0c\u7b11\u9053\uff1a\u201c\u81ea\u53e4\u82f1\u96c4\u51fa\u5c11\u5e74\uff0c\u8fd9\u8bdd\u5f53\u771f \u4e0d\u9519\u3002\u91d1\u5c11\u4fa0\uff0c\u7528\u4e0d\u7740\u518d\u8fc7\u5341\u5e74\uff0c\u4f60\u7684\u6210\u5c31\u53ea\u6015\u5c31\u8981\u8d85\u8fc7\u4f60\u7684\u5e08\u5144\u4e86\uff01\u201d\u539f\u6765\u7afa\u5c1a\u7236\u8fd9\u4e00\u6276 \u4e43\u662f\u6709\u610f\u8bd5\u91d1\u9010\u6d41\u7684\u529f\u529b\u7684\u3002 \u3000\u3000\u5e86\u529f\u5bb4\u5c31\u8bbe\u5728\u5c06\u519b\u5e9c\u4e2d\uff0c\u5f53\u4e0b\u5404\u4eba\u4f9d\u6b21\u5165\u5ea7\u3002\u91d1\u9010\u6d41\u3001\u53f2\u7ea2\u82f1\u3001\u53f6\u6155\u534e\u7b49\u4eba\u966a\u7afa\u5c1a\u7236\u540c \u5750\u9996\u5e2d\u3002 \u3000\u3000\u7afa\u5c1a\u7236\u81ea\u4ece\u524d\u5e74\u5931\u4e86\u897f\u660c\u4e4b\u540e\uff0c\u56f4\u5904\u5927\u51c9\u5c71\u4e2d\uff0c\u5386\u65f6\u4e24\u8f7d\uff0c\u4eca\u65e5\u65b9\u5f97\u91cd\u4e34\u65e7\u5730\uff0c\u5fc3\u4e2d\u5feb \u610f\uff0c\u81ea\u662f\u53ef\u60f3\u800c\u77e5\uff0c\u559d\u4e86\u51e0\u676f\uff0c\u8c6a\u5174\u52c3\u53d1\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u5e74\u6211\u5c31\u662f\u5728\u8fd9\u5ea7\u5ba2\u5385\u4e4b\u4e2d\uff0c\u906d\u4e86\u5e05\u5b5f \u96c4\u7684\u6697\u7b97\u7684\u3002\u5982\u4eca\u54b1\u4eec\u5728\u8fd9\u91cc\u559d\u5e86\u529f\u9152\uff0c\u5e05\u5b5f\u96c4\u5219\u5df2\u8eab\u9996\u5f02\u5904\uff0c\u867d\u7136\u4e0d\u662f\u6211\u4eb2\u624b\u6240\u6740\uff0c\u8fd9\u4e2a \u4ec7\u4e5f\u603b\u7b97\u62a5\u5f97\u75db\u5feb\u4e86\uff01\u6765\uff0c\u6765\uff0c\u6765\uff01\u91d1\u5c11\u4fa0\u6211\u5148\u656c\u4f60\u4e09\u676f\uff0c\u591a\u8c22\u4f60\u4e3a\u6211\u53d6\u4e86\u5e05\u5b5f\u96c4\u7684\u9996 \u7ea7\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u5927\u4f19\u513f\u7684\u529f\u52b3\uff0c\u54b1\u4eec\u5728\u5ea7\u7684\u90fd\u559d\u4e09\u676f\u5427\u3002\u201d\u4f17\u4eba\u8fde\u5c3d\u4e09\u676f\uff0c\u7afa\u5c1a\u7236 \u53c8\u9053\uff1a\u201c\u5f53\u5e74\u6211\u758f\u4e8e\u9632\u8303\uff0c\u5931\u4e86\u897f\u660c\uff0c\u60ed\u6127\u5f97\u5f88\uff0c\u4eca\u65e5\u5e78\u5f97\u5927\u4f19\u513f\u5e2e\u5fd9\u6211\u593a\u4e86\u56de\u6765\uff0c\u7afa\u67d0\u662f \u51b3\u4e0d\u80fd\u5bb9\u5b83\u518d\u5931\u4e86\uff0c\u6e05\u519b\u82e5\u6562\u6765\u72af\uff0c\u6211\u8a93\u5fc5\u6b7b\u5b88\u6b64\u57ce\uff01\u201d\u8bf4\u7f62\uff0c\u6284\u8d77\u4e00\u53ea\u9152\u676f\uff0c\u671d\u7740\u77f3\u9636\u7528 \u529b\u6454\u4e0b\u3002 \u3000\u3000\u5ffd\u5730\u4e00\u6761\u4eba\u5f71\u79bb\u5ea7\u800c\u8d77\uff0c\u521a\u597d\u5728\u9152\u676f\u843d\u5730\u4e4b\u524d\u7684\u4e00\u5239\u90a3\u63a5\u4e86\u4e0b\u6765\uff0c\u8fd9\u4eba\u662f\u53f6\u6155\u534e\u3002 \u3000\u3000\u7afa\u5c1a\u7236\u8be7\u9053\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u4f55\u4ee5\u4e0d\u8bb8\u7afa\u67d0\u53d1\u6b64\u8a93\u613f\uff1f\u201d \u3000\u3000\u53f6\u6155\u534e\u5fae\u7b11\u9053\uff1a\u201c\u7afa\u8001\u524d\u8f88\u8a93\u5b88\u6b64\u57ce\uff0c\u58ee\u5fd7\u53ef\u5609\u3002\u4e0d\u8fc7\uff0c\u4f9d\u665a\u8f88\u770b\u6765\uff0c\u8fd8\u662f\u653e\u5f03\u897f\u660c\u7684 \u597d\u3002\u201d \u3000\u3000\u8fd9\u8bdd\u82e5\u662f\u4ece\u53e6\u4e00\u4e2a\u4eba\u53e3\u4e2d\u8bf4\u51fa\uff0c\u7afa\u5c1a\u7236\u4e00\u5b9a\u4f1a\u52c3\u7136\u5927\u6012\uff0c\u4f46\u53f6\u6155\u534e\u5374\u662f\u5b9e\u9645\u6307\u6325\u8fd9\u573a\u6218 \u4e8b\u7684\u4eba\uff0c\u7afa\u5c1a\u7236\u7d20\u77e5\u4ed6\u7cbe\u901a\u5175\u6cd5\uff0c\u8ba1\u8651\u5468\u8be6\uff0c\u542c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u5fc3\u91cc\u867d\u7136\u4e0d\u540c\u610f\uff0c\u5374\u4e0d\u80fd\u4e0d\u5411\u4ed6 \u8bf7\u95ee\uff1a\u201c\u53f6\u5144\uff0c\u4f60\u8fd9\u4e48\u4e00\u8bf4\uff0c\u6211\u53ef\u662f\u6709\u70b9\u7cca\u6d82\u4e86\u3002\u65e2\u7136\u8981\u653e\u5f03\u897f\u660c\uff0c\u4f55\u5fc5\u4eca\u65e5\u8d39\u8fd9\u6837\u5927\u7684\u6c14 \u529b\u5c06\u5b83\u593a\u56de\u3002\u7afa\u67d0\u613f\u8046\u9ad8\u89c1\uff01\u201d \u3000\u3000\u53f6\u6155\u534e\u9053\uff1a\u201c\u665a\u8f88\u8c08\u4e0d\u4e0a\u6709\u4f55\u9ad8\u660e\u7684\u89c1\u89e3\uff0c\u53ea\u662f\u4f9d\u636e\u5175\u6cd5\u800c\u8a00\u3002\u76ee\u4e0b\u54b1\u4eec\u867d\u7136\u593a\u56de\u897f \u660c\uff0c\u4f46\u6e05\u519b\u7684\u5175\u529b\u8fd8\u662f\u6bd4\u54b1\u4eec\u5927\u5f97\u591a\u3002\u4ed6\u4eec\u5018\u82e5\u91cd\u65b0\u96c6\u5408\u5175\u529b\uff0c\u6765\u653b\u897f\u660c\uff0c\u54b1\u4eec\u6b7b\u5b88\u6b64\u57ce\uff0c \u90a3\u5c31\u53d8\u6210\u6328\u6253\u7684\u5c40\u9762\u4e86\u3002\u201d \u3000\u3000\u7afa\u5c1a\u7236\u602b\u7136\u4e0d\u60a6\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u867d\u7136\u8bf4\u5f97\u4e0d\u9519\uff0c\u4f46\u82e5\u6015\u4e86\u654c\u4eba\u7684\u5f3a\u5927\uff0c\u54b1\u4eec\u8fd8\u7b97\u4ec0\u4e48\u82f1\u96c4 \u597d\u6c49\uff1f\u752d\u8bf4\uff0c\u4ee5\u5f31\u654c\u5f3a\uff0c\u6709\u51b3\u5fc3\u548c\u5f3a\u654c\u62fc\u7684\u8bdd\uff0c\u4e5f\u672a\u5fc5\u5c31\u62fc\u4ed6\u4eec\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u53f6\u6155\u534e\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u6015\u4e86\u654c\u4eba\uff0c\u4e0d\u8fc7\u6253\u4ed7\u4e5f\u7b49\u4e8e\u505a\u751f\u610f\uff0c\u8981\u770b\u5408\u4e0d\u5408\u7b97\u3002\u654c\u4eba\u5175\u529b\u6bd4\u54b1 \u4eec\u5927\u5f97\u591a\uff0c\u54b1\u4eec\u5373\u4f7f\u4e00\u4e2a\u62fc\u6389\u4ed6\u51e0\u4e2a\uff0c\u4e5f\u8fd8\u662f\u4e0d\u5408\u7b97\u7684\u3002 \u3000\u3000\u201c\u56e0\u6b64\u4f9d\u665a\u8f88\u4e4b\u89c1\uff0c\u8981\u6253\u80dc\u4ed7\uff0c\u6700\u597d\u662f\u9009\u62e9\u6709\u5229\u4e8e\u6211\u7684\u5730\u70b9\uff0c\u6709\u5229\u4e8e\u6211\u7684\u65f6\u95f4\uff0c\u6709\u628a\u63e1 \u624d\u6253\uff0c\u6ca1\u6709\u628a\u63e1\u5c31\u4e0d\u6253\uff0c\u9a71\u9664\u5308\u864f\uff0c\u6062\u590d\u4e2d\u534e\uff0c\u8fd9\u662f\u4e00\u4ef6\u5927\u4e8b\uff0c\u5e94\u5f53\u4ece\u5168\u5c40\u7740\u60f3\uff0c\u4e0d\u5728\u4e4e\u4e00 \u57ce\u4e00\u6c60\u7684\u5f97\u5931\u3002\u5728\u654c\u5f3a\u6211\u5f31\u7684\u5f62\u52bf\u4e4b\u4e0b\uff0c\u91cd\u8981\u7684\u5728\u4e8e\u6d88\u706d\u654c\u4eba\u7684\u529b\u91cf\uff0c\u9010\u6b65\u53d8\u54b1\u4eec\u7684\u52a3\u52bf\u4e3a \u4f18\u52bf\uff0c\u8fd9\u624d\u662f\u4e0a\u4e0a\u4e4b\u7b56\u3002\u201d \u3000\u3000\u7afa\u5c1a\u7236\u604d\u7136\u5982\u6709\u6240\u609f\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u8bf7\u4f60\u518d\u8bf4\u4e0b\u53bb\u3002\u201d \u3000\u3000\u53f6\u6155\u534e\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u6b21\u5947\u88ad\u6210\u529f\uff0c\u4e43\u662f\u56e0\u4e3a\u5404\u65b9\u9762\u7684\u6761\u4ef6\u90fd\u5f88\u6709\u5229\u3002\u4e00\u6765\u5e05\u5b5f\u96c4\u81ea\u6043\u5175 \u591a\u7cae\u8db3\uff0c\u4ee5\u4e3a\u54b1\u4eec\u4e0d\u6562\u653b\u575a\uff0c\u4e8e\u662f\u4e00\u5fc3\u5907\u529e\u559c\u4e8b\uff0c\u9632\u5fa1\u677e\u61c8\uff1b\u4e8c\u6765\u54b1\u4eec\u65e9\u5c31\u4f0f\u6709\u5185\u5e94\uff0c\u53f2\u59d1 \u5a18\u53c8\u523a\u4f24\u4e86\u5e05\u5b5f\u96c4\uff0c\u5b98\u519b\u7fa4\u9f99\u65e0\u9996\uff0c\u54b1\u4eec\u7684\u8fdb\u653b\u624d\u6bd4\u539f\u6765\u7684\u4f30\u8ba1\u66f4\u4e3a\u5bb9\u6613\u3002\u4e09\u6765\u6709\u674e\u5927\u54e5\u62db \u964d\u4e86\u516d\u5408\u5e2e\u7684\u5f1f\u5144\uff0c\u7b49\u4e8e\u662f\u65ad\u4e86\u5e05\u76c2\u96c4\u7684\u4e00\u6761\u81c2\u818a\uff0c\u5c06\u519b\u5e9c\u4e0d\u5fc5\u600e\u4e48\u8d39\u529b\u5c31\u653b\u4e0b\u4e86\uff0c\u8fd9\u4e00\u573a \u54b1\u4eec\u7684\u5f1f\u5144\u635f\u4f24\u65e0\u591a\uff0c\u5982\u74e6\u89e3\u4e86\u654c\u4eba\u7684\u4e00\u534a\u4ee5\u4e0a\u7684\u5175\u529b\uff0c\u8fd9\u7b49\u4e8e\u7528\u5c0f\u672c\u94b1\u505a\u4e86\u5927\u751f\u610f\uff0c\u8fd9\u6837 \u7684\u4ed7\u5f53\u7136\u662f\u4e0d\u6015\u6253\u3002 \u3000\u3000\u201c\u4f46\u5947\u88ad\u4e4b\u4e8b\uff0c\u53ef\u4e00\u800c\u4e0d\u53ef\u518d\u3002\u54b1\u4eec\u82e5\u662f\u6b7b\u5b88\u897f\u660c\uff0c\u90a3\u5c31\u53d8\u6210\u662f\u654c\u4eba\u4e3b\u52a8\uff0c\u54b1\u4eec\u88ab\u52a8 \u4e86\u3002\u8fd9\u6837\u7684\u4ed7\u5bf9\u54b1\u4eec\u5f88\u4e0d\u5408\u7b97\u3002 \u3000\u3000\u201c\u6240\u4ee5\uff0c\u6211\u7684\u610f\u601d\u662f\u4e0d\u5fc5\u5728\u4e4e\u4e00\u57ce\u4e00\u4ed6\u7684\u5f97\u5931\uff0c\u4e0d\u5982\u5230\u5916\u9762\u53bb\uff0c\u5728\u5c71\u533a\uff0c\u5728\u4e61\u6751\uff0c\u9009\u62e9 \u6709\u5229\u4e8e\u54b1\u4eec\u7684\u5730\u70b9\u6765\u548c\u5b98\u519b\u89c1\u4e2a\u9ad8\u4e0b\u3002\u5916\u9762\u6709\u5e7f\u9614\u7684\u5929\u5730\uff0c\u8001\u767e\u59d3\u7edd\u5927\u591a\u6570\u662f\u5e2e\u54b1\u4eec\u7684\uff0c\u54b1 \u4eec\u5728\u8001\u767e\u59d3\u4e2d\u95f4\uff0c\u5982\u9c7c\u5f97\u6c34\uff01\u800c\u4e14\u54b1\u4eec\u8fd8\u53ef\u4ee5\u6253\u5f97\u8fc7\u5c31\u6253\uff0c\u6253\u4e0d\u8fc7\u5c31\u8dd1\u3002\u5982\u6b64\u6253\u4ed7\uff0c\u5c82\u4e0d\u662f \u5bf9\u54b1\u4eec\u5927\u5927\u6709\u5229\u3002\u201d \u3000\u3000\u4e00\u5e2d\u8bdd\u8bf4\u5f97\u7afa\u5c1a\u7236\u4e94\u4f53\u6295\u5730\uff0c\u7afa\u5c1a\u7236\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5f53\u771f\u662f\u542c\u541b\u4e00\u5e2d\uff0c\u80dc\u8bfb\u5341\u5e74\u4e66\u3002\u8fd9\u4e48 \u8bf4\u6765\uff0c\u54b1\u4eec\u8fd8\u662f\u56de\u5927\u51c9\u5c71\u7684\u597d\uff1f\u201d \u3000\u3000\u53f6\u6155\u534e\u9053\uff1a\u201c\u665a\u8f88\u4e4b\u89c1\uff0c\u6b63\u662f\u5982\u6b64\uff0c\u4f46\u4e5f\u4e0d\u5fc5\u6025\u5728\u4e00\u65f6\uff0c\u5b98\u519b\u65b0\u8d25\uff0c\u6025\u5207\u4e4b\u95f4\u5c1a\u96be\u91cd\u805a \u5175\u529b\uff0c\u54b1\u4eec\u53ef\u4ee5\u5728\u897f\u660c\u9a7b\u624e\u4e2a\u5341\u5929\u516b\u5929\uff0c\u5b89\u987f\u767e\u59d3\u3002\u767e\u59d3\u6709\u613f\u610f\u8ddf\u968f\u54b1\u4eec\u5230\u5927\u51c9\u5c71\u7684\uff0c\u54b1\u4eec \u4e5f\u53ef\u4ee5\u8ba9\u4ed6\u4eec\u6709\u4e2a\u51c6\u5907\u3002\u201d\u7afa\u5c1a\u7236\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u6b63\u4e3a\u653e\u5f03\u897f\u660c\u4e4b\u540e\uff0c\u8001\u767e\u59d3\u5982\u4f55\u5b89\u987f\u800c\u62c5 \u5fe7\u3002\u8fd9\u6837\u5012\u662f\u4e2a\u4e24\u5168\u4e4b\u7b56\u4e86\u3002\u201d \u3000\u3000\u8ba1\u8bae\u5df2\u5b9a\uff0c\u5927\u5bb6\u5f00\u6000\u75db\u996e\u3002\u53ea\u6709\u91d1\u9010\u6d41\u4e0e\u53f2\u7ea2\u82f1\u8bb0\u6302\u7740\u5389\u5357\u661f\uff0c\u5374\u662f\u96be\u514d\u6709\u70b9\u90a3\u90c1\u4e0d \u6b22\u3002\u7afa\u5c1a\u7236\u7b11\u9053\uff1a\u201c\u5df2\u6709\u6570\u767e\u4eba\u51fa\u53bb\u627e\u5bfb\u5389\u5c11\u4fa0\u4e86\uff0c\u53ea\u8981\u5389\u5c11\u4fa0\u8fd8\u6d3b\u5728\u4eba\u95f4\uff0c\u4e03\u5929\u4e4b\u5185\uff0c\u6570 \u767e\u4eba\u627e\u4e00\u4e2a\uff0c\u603b\u4f1a\u627e\u5f97\u7740\u7684\uff01\u6765\uff0c\u6765\uff0c\u6765\uff0c\u54b1\u4eec\u8fd8\u662f\u75db\u75db\u5feb\u5feb\u5730\u559d\u9152\u5427\u3002\u201d \u3000\u3000\u4e0d\u6599\u63a5\u8fde\u8fc7\u4e86\u51e0\u5929\uff0c\u51fa\u53bb\u627e\u5bfb\u5389\u5357\u661f\u7684\u4eba\u4e00\u6279\u4e00\u6279\u7684\u56de\u6765\u4e86\uff0c\u5389\u5357\u661f\u7684\u4e0b\u843d\u4ecd\u662f\u672a\u77e5\u3002 \u3000\u3000\u5230\u4e86\u7b2c\u516d\u5929\uff0c\u674e\u6566\u592b\u59bb\u548c\u9648\u5149\u7167\u3001\u77f3\u971e\u59d1\u6700\u540e\u4e5f\u56de\u6765\u4e86\u3002\u4ed6\u4eec\u4e5f\u6ca1\u6709\u627e\u5230\u5389\u5357\u661f\u3002 \u3000\u3000\u51fa\u53bb\u627e\u5bfb\u5389\u5357\u661f\u7684\u4eba\u90fd\u56de\u6765\u4e86\uff0c\u53ea\u6709\u4e00\u4e2a\u4eba\u4e0d\u89c1\u9762\uff0c\u8fd9\u4eba\u662f\u516c\u5b59\u71d5\u3002\u5979\u662f\u5728\u6218\u4e8b\u5c1a\u672a\u7ed3 \u675f\u4e4b\u9645\u5c31\u5355\u72ec\u51fa\u57ce\u7684\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u5979\u7684\u53bb\u5411\u3002 \u3000\u3000\u627e\u4e0d\u7740\u5389\u5357\u661f\uff0c\u516c\u5b59\u71d5\u53c8\u5931\u8e2a\uff0c\u91d1\u9010\u6d41\u7684\u7126\u6025\u81ea\u662f\u53ef\u60f3\u800c\u77e5\uff0c\u4e8e\u662f\u4ed6\u548c\u53f2\u7ea2\u82f1\u51b3\u610f\u53bb\u627e \u5bfb\u4ed6\u4eec\uff0c\u4e00\u5b9a\u8981\u5728\u627e\u7740\u4ed6\u4eec\u540e\uff0c\u624d\u56de\u5927\u51c9\u5c71\u7684\u4e49\u519b\u57fa\u5730\u3002 \u3000\u3000\u6682\u4e14\u6309\u4e0b\u91d1\u9010\u6d41\u4e0e\u53f2\u7ea2\u82f1\u8fd9\u4e00\u8def\u4e0d\u8868\uff0c\u5148\u8bf4\u516c\u5b59\u71d5\u7684\u906d\u9047\uff1a\u5979\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u5979\u6709\u6ca1\u6709\u89c1 \u7740\u5389\u5357\u661f\u5462\uff1f \u3000\u3000\u91d1\u9010\u6d41\u4e0e\u53f2\u7ea2\u82f1\u4fea\u5f71\u53cc\u53cc\u4e4b\u9645\uff0c\u6b63\u662f\u516c\u5b59\u71d5\u8e3d\u8e3d\u72ec\u884c\u4e4b\u65f6\u3002\u4e14\u8bf4\u516c\u5b59\u71d5\u90a3\u65e5\u72ec\u81ea\u51fa\u57ce\uff0c \u4e00\u8def\u5bfb\u89c5\uff0c\u4e0d\u77e5\u4e0d\u89c9\u79bb\u5f00\u4e86\u4e49\u519b\u7684\u961f\u4f0d\u3002 \u3000\u3000\u516c\u5b59\u71d5\u6697\u81ea\u601d\u91cf\uff0c\u5389\u5357\u661f\u662f\u5728\u5c06\u519b\u5e9c\u6fc0\u6218\u4e4b\u9645\uff0c\u7a81\u7136\u4e0d\u77e5\u53bb\u5411\u7684\u3002\u90a3\u4e48\uff0c\u5982\u679c\u4ed6\u6ca1\u6709\u6b7b \u7684\u8bdd\uff0c\u90a3\u5c31\u4e00\u5b9a\u662f\u7ed9\u654c\u4eba\u4fd8\u864f\u53bb\u4e86\u3002\u56e0\u4e3a\u91d1\u9010\u6d41\u7b49\u4eba\u90fd\u5728\u5c06\u519b\u5e9c\u4e2d\uff0c\u5389\u5357\u661f\u5982\u679c\u662f\u9003\u8131\u7684 \u8bdd\uff0c\u4ed6\u4e0d\u4f1a\u4e0d\u56de\u6765\u7684\u3002 \u3000\u3000\u516c\u5b59\u71d5\u8fd8\u53ef\u4ee5\u65ad\u5b9a\uff0c\u5389\u5357\u661f\u82e5\u662f\u88ab\u4fd8\uff0c\u4fd8\u4ed6\u7684\u4eba\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u662f\u666e\u901a\u6e05\u5175\uff0c\u6e05\u5175\u9003\u547d\u4e0d \u6687\uff0c\u8c01\u80af\u5e26\u4e00\u4e2a\u53d7\u4f24\u7684\u4fd8\u864f\u8d70\u8def\uff1f\u56e0\u6b64\u628a\u5389\u5357\u661f\u4fd8\u864f\u7684\u4eba\uff0c\u82e5\u4e0d\u662f\u5e05\u5b5f\u96c4\u624b\u4e0b\u7684\u519b\u5b98\uff0c\u5c31\u4e00 \u5b9a\u662f\u5e05\u5b5f\u96c4\u9080\u8bf7\u6765\u7684\u6c5f\u6e56\u597d\u624b\u3002\u8fd9\u4e9b\u4eba\u77e5\u9053\u5389\u5357\u661f\u7684\u8eab\u4efd\uff0c\u6349\u4e86\u5389\u5357\u661f\u5c31\u53ef\u4ee5\u5c06\u529f\u8d4e\u7f6a\u3002 \u3000\u3000\u516c\u5b59\u71d5\u8ddf\u7740\u6e05\u519b\u6e83\u9003\u7684\u65b9\u5411\u8ffd\u53bb\uff0c\u5374\u4e0d\u7406\u4f1a\u6cbf\u9014\u7684\u6e83\u519b\u3002\u6e83\u519b\u4e09\u4e94\u6210\u7fa4\uff0c\u96c6\u5408\u4e0d\u6210\u5927 \u961f\uff0c\u6709\u4e9b\u4e0d\u77e5\u6b7b\u6d3b\u7684\u4e0a\u6765\u62db\u60f9\u516c\u5b59\u71d5\uff0c\u90fd\u7ed9\u516c\u5b59\u71d5\u6740\u4e86\u3002 \u3000\u3000\u516c\u5b59\u71d5\u4e00\u8def\u62a2\u6e83\u519b\u7684\u5750\u9a91\uff0c\u9891\u9891\u6362\u9a6c\uff0c\u8dd1\u4e86\u4e24\u5929\uff0c\u6e83\u519b\u90fd\u5df2\u7ed9\u5979\u7529\u5728\u80cc\u540e\uff0c\u4f46\u5979\u4e5f\u6ca1\u6709 \u53d1\u73b0\u5389\u5357\u661f\u7684\u8e2a\u8ff9\u3002\u5c06\u519b\u5e9c\u90a3\u4e9b\u9ad8\u624b\u4e5f\u6ca1\u6709\u89c1\u7740\u4e00\u4e2a\u3002 \u3000\u3000\u7b2c\u56db\u5929\u516c\u5b59\u71d5\u6b63\u5728\u8def\u4e0a\u884c\u8d70\uff0c\u5ffd\u5730\u78b0\u4e0a\u4e00\u4e2a\u719f\u4eba\uff0c\u8fd9\u4eba\u662f\u7ea2\u7f28\u4f1a\u7684\u9996\u5e2d\u9999\u4e3b\u5bab\u79c9\u85e9\u3002\u7ea2 \u7f28\u4f1a\u4e2d\u9664\u4e86\u5979\u7684\u7236\u4eb2\u516c\u5b59\u5b8f\u4e4b\u5916\uff0c\u5730\u4f4d\u6700\u9ad8\u6b66\u529f\u6700\u5f3a\u7684\u5c31\u662f\u4ed6\u4e86\u3002 \u3000\u3000\u516c\u5b59\u71d5\u559c\u51fa\u671b\u5916\uff0c\u8bf4\u9053\uff1a\u201c\u5bab\u53d4\u53d4\uff0c\u600e\u4e48\u4f60\u4e5f\u6765\u4e86\uff1f\u201d \u3000\u3000\u5bab\u79c9\u85e9\u7b11\u9053\uff1a\u201c\u6b63\u662f\u4e3a\u4e86\u4f60\u5440\uff0c\u4f60\u7239\u653e\u5fc3\u4e0d\u4e0b\uff0c\u53eb\u6211\u6765\u627e\u4f60\u7684\u3002\u542c\u8bf4\u897f\u660c\u5df2\u7ed9\u4e49\u519b\u653b \u4e0b\uff0c\u4e0d\u77e5\u662f\u771f\u662f\u5047\uff0c\u6211\u6b63\u60f3\u5230\u897f\u660c\u53bb\u5462\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u5f53\u7136\u662f\u771f\u7684\uff0c\u4f60\u7684\u597d\u670b\u53cb\u91d1\u9010\u6d41\u4e5f\u5728\u90a3\u513f\u3002\u201d \u3000\u3000\u5bab\u79c9\u85e9\u9053\uff1a\u201c\u7136\u5219\u4f60\u4f55\u4ee5\u4e0d\u5728\u897f\u660c\uff0c\u5374\u72ec\u81ea\u4e00\u4e2a\u4eba\u8dd1\u5230\u8fd9\u91cc\u6765\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u6211\u5728\u627e\u4e00\u4f4d\u53d7\u4f24\u7684\u670b\u53cb\uff0c\u6211\u6000\u7591\u4ed6\u5df2\u7ed9\u654c\u4eba\u4fd8\u864f\u4e86\u3002\u5bab\u53d4\u53d4\uff0c\u4f60\u4e00\u8def\u4e0a\u53ef \u66fe\u53d1\u73b0\u6709\u4eba\u62bc\u89e3\u7740\u4e00\u4e2a\u53d7\u4f24\u7684\u5c11\u5e74\u4e48\u3002\u201d \u3000\u3000\u5bab\u79c9\u85e9\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u89c1\u7740\uff0c\u4f46\u4e0d\u77e5\u4f60\u8bf4\u7684\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d\u516c\u5b59\u71d5\u9053\uff1a\u201c\u662f\u5389 \u5357\u661f\u3002\u201d\u5bab\u79c9\u85e9\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u4ed6\u3002\u5389\u5357\u661f\u7684\u6b66\u529f\u5f88\u4e0d\u9519\u5440\uff0c\u662f\u8c01\u628a\u4ed6\u63b3\u53bb\u7684\uff1f\u201d\u516c\u5b59\u71d5 \u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u4ed6\u662f\u7ed9\u53f2\u767d\u90fd\u6253\u4f24\uff0c\u8c01\u4fd8\u864f\u4ed6\u6211\u53ef\u5c31\u4e0d\u77e5\u9053\u4e86\uff0c\u5bab\u53d4\u53d4\uff0c\u4f60\u8ba4\u8bc6\u4ed6\uff1f\u201d \u3000\u3000\u5bab\u79c9\u85e9\u9053\uff1a\u201c\u4ed6\u53bb\u5e74\u548c\u91d1\u9010\u6d41\u5927\u95f9\u8428\u5e9c\u4e4b\u65f6\uff0c\u6211\u66fe\u7ecf\u89c1\u8fc7\u4ed6\u3002\u5982\u679c\u6211\u5728\u8def\u4e0a\u78b0\u4e0a\u6b64\u4eba\uff0c \u4e0d\u4f1a\u4e0d\u8bb0\u5f97\u7684\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u5927\u5931\u6240\u671b\uff0c\u59d1\u4e14\u518d\u95ee\u4e00\u95ee\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5728\u8def\u4e0a\u53ef\u66fe\u78b0\u89c1\u8fc7\u5f62\u8ff9\u53ef\u7591\u7684\u4eba\uff1f\u201d \u3000\u3000\u5bab\u79c9\u85e9\u60f3\u4e86\u4e00\u60f3\uff0c\u7b11\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u7684\u4eba\u624d\u662f\u5f62\u8ff9\u53ef\u7591\uff0c\u6211\u5012\u6ca1\u6709\u7559\u610f\u3002\u4f46\u542c\u4f60\u8fd9\u4e48\u4e00 \u8bf4\uff0c\u6211\u5374\u662f\u60f3\u8d77\u6765\u4e86\u3002\u6211\u66fe\u78b0\u4e0a\u4e00\u5bf9\u5f88\u5c11\u5728\u6c5f\u6e56\u4e0a\u9732\u9762\u7684\u5e08\u5f92\uff0c\u6216\u8005\u53ef\u4ee5\u8bf4\u5f97\u662f\u6709\u70b9\u5f62\u8ff9\u53ef \u7591\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u8fd9\u4e24\u5e05\u5f92\u662f\u8c01\uff1f\u201d \u3000\u3000\u5bab\u79c9\u85e9\u9053\uff1a\u201c\u662f\u9633\u6d69\u548c\u9f9a\u5e73\u91ce\u3002\u542c\u8bf4\u4ed6\u4eec\u662f\u5b5f\u795e\u901a\u4e00\u8109\u6240\u4f20\uff0c\u9633\u6d69\u7684\u7236\u4eb2\u9633\u8d64\u7b26\u5c31\u662f\u5b5f \u795e\u901a\u7684\u5e08\u5f1f\u3002\u5b5f\u795e\u901a\u3001\u9633\u8d64\u7b26\u76f8\u7ee7\u6b7b\u540e\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u4e2d\u539f\u6b66\u6797\u4eba\u7269\u61c2\u5f97\u4fee\u7f57\u9634\u715e\u653b\u7684\u5c31\u53ea\u6709 \u4ed6\u4eec\u5e08\u5f92\u3002\u201d\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4f60\u522b\u5fd9\u8bf4\u4ed6\u4eec\u7684\u6765\u5386\uff0c\u8bf7\u4f60\u5148\u544a\u8bc9\u6211\uff0c\u4f60\u662f\u5728\u4ec0\u4e48\u65f6\u5019\uff0c\u4ec0\u4e48\u5730\u65b9\u78b0\u4e0a\u4ed6 \u4eec\u7684\uff1f\u4f55\u4ee5\u89c9\u5f97\u4ed6\u4eec\u5f62\u8ff9\u53ef\u7591\uff1f\u201d \u3000\u3000\u5bab\u79c9\u85e9\u9053\uff1a\u201c\u524d\u9762\u6709\u4e2a\u5c0f\u9547\u540d\u5524\u9ed1\u77f3\u5c97\uff0c\u6211\u5c31\u662f\u5728\u6628\u5929\u665a\u4e0a\u5728\u9ed1\u77f3\u5c97\u7684\u4e00\u95f4\u5c0f\u5ba2\u6808\u78b0\u4e0a \u4ed6\u4eec\u7684\u3002\u6211\u4e0e\u9633\u6d69\u5e76\u65e0\u4ea4\u60c5\uff0c\u4f46\u4e5f\u66fe\u6709\u8fc7\u4e00\u9762\u4e4b\u7f18\u3002\u6211\u8e0f\u8fdb\u5ba2\u6b8b\u4e4b\u65f6\uff0c\u6b63\u5de7\u4ed6\u5728\u5916\u95f4\u548c\u638c\u67dc \u8bf4\u8bdd\u3002\u6309\u8bf4\u4ed6\u662f\u5e94\u8be5\u5411\u6211\u6253\u62db\u547c\u7684\uff0c\u5374\u4e0d\u77e5\u4f55\u6545\uff0c\u4ed6\u88c5\u4f5c\u770b\u4e0d\u89c1\u6211\uff0c\u5c31\u5306\u5306\u5730\u8eb2\u8fdb\u623f\u91cc\u53bb \u4e86\u3002\u6211\u9690\u7ea6\u542c\u9f9a\u5e73\u91ce\u5728\u623f\u95f4\u91cc\u95ee\u4ed6\u5e08\u7236\uff0c\u5916\u9762\u662f\u6765\u4e86\u4ec0\u4e48\u4eba\uff0c\u9633\u6d69\u5618\u4e86\u4e00\u58f0\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u5f88 \u4f4e\uff0c\u6211\u6ca1\u5b58\u5fc3\u5077\u542c\u4ed6\u4eec\u7684\u8bf4\u8bdd\uff0c\u5fc3\u60f3\u8fd9\u53ae\u65e2\u662f\u81ea\u9ad8\u81ea\u5927\uff0c\u4e0d\u7406\u4f1a\u6211\uff0c\u6211\u53c8\u4f55\u5fc5\u776c\u4ed6\uff0c\u56e0\u6b64\u4e5f \u5c31\u4e0d\u53bb\u7559\u610f\u4ed6\u4eec\u662f\u5728\u8bf4\u4e9b\u4ec0\u4e48\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u5927\u559c\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u4ed6\u4eec\u4e86\u3002\u53ef\u60dc\uff0c\u53ef\u60dc\uff0c\u4f60\u6ca1\u6709\u7aa5\u63a2\u4ed6\u4eec\u7684\u623f\u95f4\uff0c\u5389\u5357\u661f\u591a\u534a\u662f \u7ed9\u4ed6\u4eec\u70b9\u4e86\u7a74\u9053\uff0c\u853d\u5728\u91cc\u9762\u3002\u201d \u3000\u3000\u5bab\u79c9\u85e9\u8be7\u9053\uff1a\u201c\u9633\u6d69\u597d\u50cf\u662f\u5f88\u5c11\u4e0e\u5b98\u5e9c\u5f80\u6765\u7684\uff0c\u600e\u4e48\u4ed6\u8fd9\u6b21\u4e5f\u66fe\u51fa\u73b0\u5728\u897f\u660c\u7684\u5c06\u519b\u5e9c\u4e2d \u4e48\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4ed6\u4eec\u4fe9\u5e08\u5f92\u6b63\u662f\u5e05\u5b5f\u96c4\u5f85\u5982\u4e0a\u5bbe\u7684\u4eba\u7269\uff0c\u5728\u5c06\u519b\u5e9c\u6fc0\u6218\u4e4b\u5bf9\uff0c\u9633\u6d69\u66fe\u5306\u5306 \u4e00\u73b0\uff0c\u4f3c\u4e4e\u662f\u4e0e\u91d1\u9010\u6d41\u5bf9\u8fc7\u4e00\u638c\uff0c\u540e\u6765\u5c31\u4e0d\u89c1\u4e86\uff0c\u9f9a\u5e73\u91ce\u5219\u662f\u4e0e\u6211\u4ea4\u8fc7\u624b\uff0c\u7a0d\u540e\u624d\u4e0d\u89c1\u7684\u3002 \u73b0\u5728\u542c\u4f60\u8fd9\u4e48\u4e00\u8bf4\uff0c\u6b64\u4e8b\u5df2\u662f\u65e0\u53ef\u7f6e\u7591\uff0c\u4e00\u5b9a\u662f\u9633\u6d69\u6349\u4e86\u5389\u5357\u661f\uff0c\u8d81\u7740\u6df7\u4e71\uff0c\u6084\u6084\u5730\u5c31\u5148\u6e9c \u4e86\u3002\u201d \u3000\u3000\u5bab\u79c9\u85e9\u77bf\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u4f60\u8bf4\u5f97\u662f\u6709\u9053\u7406\u3002\u602a\u4e0d\u5f97\u9633\u6d69\u8fd9\u53ae\u4e0d\u6562\u89c1\u6211\uff0c \u6562\u60c5\u5c31\u662f\u6015\u6211\u76d8\u67e5\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u54b1\u4eec\u73b0\u5728\u53bb\u8ffd\uff0c\u6216\u8bb8\u8fd8\u6765\u5f97\u53ca\u3002\u4f60\u53ef\u77e5\u9053\u4ed6\u4eec\u8d70\u54ea\u4e00\u65b9\u5417\uff1f\u201d \u3000\u3000\u5bab\u79c9\u85e9\u9053\uff1a\u201c\u6211\u6025\u4e8e\u5230\u897f\u660c\u627e\u4f60\uff0c\u4eca\u5929\u4e00\u65e9\uff0c\u6211\u662f\u7b2c\u4e00\u4e2a\u5ba2\u4eba\u79bb\u5f00\u90a3\u95f4\u5ba2\u6808\u7684\uff0c\u90a3\u65f6\u9633 \u6d69\u5e05\u5f92\u8fd8\u672a\u8d77\u6765\u3002\u9ed1\u77f3\u5c97\u524d\u9762\u6709\u4e24\u6761\u8def\uff0c\u4e00\u6761\u8def\u901a\u5411\u5170\u5dde\uff0c\u4e00\u6761\u8def\u901a\u5411\u6e5f\u4e2d\uff0c\u53ef\u4e0d\u77e5\u4ed6\u4eec\u8d70 \u7684\u662f\u54ea\u4e00\u6761\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u5c31\u5206\u5934\u53bb\u8ffd\uff01\u597d\u5728\u53ea\u6709\u4e24\u6761\u5c94\u8def\uff0c\u6ca1\u6709\u7b2c\u4e09\u6761\uff01\u201d \u3000\u3000\u5bab\u79c9\u85e9\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u5bab\u53d4\u53d4\u6709\u4f55\u5429\u5490\uff1f\u201d \u3000\u3000\u5bab\u79c9\u85e9\u9053\uff1a\u201c\u9633\u6d69\u6b66\u529f\u6df1\u6d45\u5982\u4f55\uff0c\u6211\u4e0d\u77e5\u9053\u3002\u4f46\u60f3\u6765\u4ed6\u65e2\u7136\u662f\u7ec3\u6210\u4e86\u4fee\u7f57\u9634\u715e\u529f\uff0c\u54b1\u4eec \u4e5f\u4e0d\u80fd\u5c0f\u89d1\u4ed6\u4e86\u3002\u54b1\u4eec\u53ef\u4ee5\u5206\u5934\u53bb\u8ffd\uff0c\u4f46\u4f60\u82e5\u53d1\u73b0\u4e86\u4ed6\uff0c\u53ef\u4e0d\u8981\u6025\u7740\u548c\u4ed6\u52a8\u624b\u3002\u6211\u7ed9\u4f60\u4e00\u652f \u86c7\u7130\u7bad\uff0c\u4f60\u4e00\u53d1\u73b0\u4ed6\u4eec\u8e2a\u8ff9\uff0c\u7acb\u5373\u53d1\u7bad\u4ee5\u4f5c\u4fe1\u53f7\u3002\u9ed1\u77f3\u5c97\u4e1c\u53bb\u8fd9\u4e00\u5e26\u7a7a\u65f7\u65e0\u4eba\uff0c\u86c7\u7130\u7bad\u4e00 \u53d1\uff0c\u5341\u91cc\u4e4b\u5185\uff0c\u662f\u53ef\u4ee5\u770b\u5f97\u89c1\u7684\u3002\u6211\u82e5\u53d1\u73b0\u4ed6\u4eec\uff0c\u4e5f\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u597d\uff01\u201d\u63a5\u8fc7\u4e86\u86c7\u7130\u7bad\uff0c\u4fbf\u4e0e\u5bab\u79c9\u85e9\u5206\u624b\uff0c\u4e00\u4e2a\u5f80\u4e1c\uff0c\u4e00\u4e2a\u5f80\u897f\uff0c\u6284\u6377\u5f84\u7ed5 \u8fc7\u9ed1\u77f3\u5c97\uff0c\u5206\u5934\u53bb\u5bfb\u627e\u5389\u5357\u661f\u7684\u4e0b\u843d\u3002 \u3000\u3000\u516c\u5b59\u71d5\u8fd9\u5339\u5750\u9a91\u662f\u524d\u5929\u62a2\u81ea\u4e00\u4e2a\u6e05\u519b\u7684\u519b\u5b98\u7684\uff0c\u867d\u7136\u662f\u4e00\u5339\u5f88\u4e0d\u9519\u7684\u6218\u9a6c\uff0c\u4f46\u8dd1\u4e86\u4e24 \u5929\uff0c\u4e5f\u662f\u6709\u70b9\u7d2f\u4e86\u3002\u516c\u5b59\u71d5\u4e00\u8def\u5feb\u9a6c\u75be\u5954\uff0c\u8dd1\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u5750\u9a91\u6e10\u6e10\u6162\u4e86\u4e0b\u6765\uff0c\u524d\u9762\u7684\u5c71 \u8def\uff0c\u5374\u662f\u8d8a\u6765\u8d8a\u89c1\u5d0e\u5c96\u3002 \u3000\u3000\u516c\u5b59\u71d5\u5927\u4e3a\u7740\u6025\uff0c\u5fc3\u60f3\uff1a\u201c\u53ef\u60dc\u5b98\u519b\u90fd\u5728\u540e\u9762\uff0c\u6ca1\u6cd5\u518d\u62a2\u4e00\u5339\u5750\u9a91\uff0c\u6362\u6362\u811a\u529b\uff0c\u53ea\u6015\u662f \u8ffd\u4e0d\u4e0a\u4e86\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u4e00\u7f15\u8427\u58f0\uff0c\u5982\u6028\u5982\u6155\uff0c\u5982\u6ce3\u5982\u8bc9\uff0c\u968f\u98ce\u98d8\u6765\uff0c\u9690\u9690\u53ef\u95fb\u3002\u516c\u5b59\u71d5\u4e00\u9635 \u72c2\u559c\uff0c\u4e0d\u89c9\u5fd8\u4e86\u5bab\u79c9\u85e9\u7684\u5429\u5490\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u5389\u5927\u54e5\uff0c\u5389\u5927\u54e5\uff01\u201d\u4e5f\u4e0d\u77e5\u524d\u9762\u5439\u8427\u90a3\u4eba\u662f\u5426 \u5389\u5357\u661f\uff0c\u5389\u5357\u661f\u53c8\u662f\u5426\u542c\u89c1\u4e86\u5979\uff0c\u4f46\u5979\u8fd9\u4e24\u58f0\u201c\u5389\u5927\u54e5\u201d\u4e00\u53eb\u4e4b\u540e\u8427\u58f0\u5374\u7a81\u7136\u6b62\u4e86\u3002 \u3000\u3000\u516c\u5b59\u71d5\u84e6\u7136\u4e00\u7701\uff0c\u5fc3\u60f3\uff1a\u201c\u4e0d\u597d\uff0c\u6211\u8fd9\u4e48\u4e00\u53eb\uff0c\u9633\u6d69\u77e5\u662f\u6211\u5df2\u8ffd\u6765\uff0c\u53ea\u6015\u5bf9\u5389\u5927\u54e5\u6709\u6240 \u4e0d\u5229\uff01\u201d\u4f46\u4e0d\u9519\u4e5f\u5df2\u9519\u4e86\uff0c\u5979\u53ea\u597d\u7acb\u5373\u53d1\u51fa\u4e86\u86c7\u7130\u7bad\uff0c\u731b\u6325\u76ae\u97ad\uff0c\u50ac\u903c\u5750\u9a91\u6025\u8d76\u4e0a\u53bb\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4e2a\u5c71\u5773\uff0c\u679c\u7136\u770b\u89c1\u6709\u4e09\u4e2a\u4eba\u5728\u5c71\u5c97\u4e0a\uff0c\u8fd9\u4e09\u4e2a\u4eba\u4e5f\u662f\u6b63\u5982\u5979\u7684\u6240\u6599\uff0c\u662f\u9633\u6d69\u3001\u9f9a \u5e73\u91ce\u4e24\u5e08\u5f92\u548c\u5389\u5357\u661f\u3002 \u3000\u3000\u9633\u6d69\u672c\u662f\u70b9\u4e86\u5389\u5357\u661f\u7684\u7a74\u9053\u7684\uff0c\u4f46\u56e0\u6b64\u5730\u79bb\u897f\u660c\u5df2\u8fdc\uff0c\u5c71\u8def\u53c8\u5d0e\u5c96\u96be\u884c\uff0c\u9633\u6d69\u5e08\u5f92\u4e0d\u613f \u80cc\u7740\u5389\u5357\u661f\u8d70\u8def\uff0c\u662f\u4ee5\u9633\u6d69\u624d\u89e3\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u903c\u4ed6\u81ea\u5df1\u6b65\u884c\u3002\u4ed6\u6599\u60f3\u5389\u5357\u661f\u5df2\u53d7\u4e86\u4f24\uff0c\u89e3\u4e86 \u4ed6\u7684\u7a74\u9053\u4ed6\u4e5f\u662f\u65e0\u80fd\u4e3a\u529b\uff0c\u540c\u65f6\u9633\u6d69\u8fd8\u60f3\u5bf9\u5389\u5357\u661f\u6709\u6240\u5229\u7528\uff0c\u539f\u6765\u4ed6\u8fd9\u6b21\u4fd8\u864f\u5389\u5357\u661f\uff0c\u5012\u4e0d \u662f\u60f3\u732e\u7ed9\u671d\u5ef7\uff0c\u800c\u662f\u60f3\u5229\u7528\u4ed6\u4f5c\u4e3a\u5080\u5121\uff0c\u91cd\u7ec4\u201c\u5929\u9b54\u6559\u201d\u7684\u3002\u540c\u8d70\u957f\u9014\uff0c\u81ea\u4e0d\u80fd\u8001\u662f\u70b9\u4e86\u4ed6 \u7684\u7a74\u9053\u3002\u53cd\u6b63\u4ed6\u8dd1\u4e0d\u4e86\uff0c\u4e50\u5f97\u5411\u4ed6\u793a\u60e0\u3002 \u3000\u3000\u5389\u5357\u661f\u6c42\u751f\u4e0d\u5f97\uff0c\u6c42\u6b7b\u4e0d\u80fd\uff0c\u6ee1\u6000\u90c1\u95f7\uff0c\u5439\u8427\u81ea\u9063\u6101\u6000\uff0c\u4e0d\u6599\u5374\u628a\u4e00\u4e2a\u516c\u5b59\u71d5\u5f15\u6765\u4e86\uff0c \u5389\u5357\u661f\u89c1\u4e86\u5979\uff0c\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u71d5\u59b9\uff0c\u4f60\u5feb\u56de\u53bb\uff01\u201d \u3000\u3000\u9633\u6d69\u559d\u9053\uff1a\u201c\u4e0d\u8bb8\u4f60\u8bf4\u8bdd\uff01\u201d\u9f9a\u5e73\u91ce\u201c\u55d6\u201d\u5730\u4fbf\u62d4\u51fa\u4e00\u67c4\u5315\u9996\uff0c\u6307\u7740\u5389\u5357\u661f\u7684\u5634\u5df4\uff0c\u51b7 \u51b7\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u53eb\u4f60\u95ed\u4e0a\u9e1f\u5634\uff0c\u4f60\u542c\u89c1\u6ca1\u6709\uff1f\u4f60\u6562\u5f20\u53e3\uff0c\u6211\u8fd9\u67c4\u5315\u9996\u5c31\u63d2\u8fdb\u53bb\uff01\u201d\u5176\u5b9e\u5389\u5357 \u661f\u5df2\u65e0\u62b5\u6297\u4e4b\u529b\uff0c\u9f9a\u5e73\u91ce\u5927\u53ef\u4e0d\u5fc5\u5982\u6b64\u505a\u4f5c\uff0c\u8ff3\u81ea\u70b9\u4ed6\u54d1\u7a74\uff0c\u5982\u6b64\u505a\u4f5c\uff0c\u53ea\u662f\u505a\u7ed9\u516c\u5b59\u71d5\u770b \u7684\u800c\u5df2\u3002\u4ed6\u77e5\u9053\u5e08\u7236\u5fc3\u610f\u662f\u60f3\u8feb\u4f7f\u516c\u5b59\u71d5\u5c31\u8303\u3002 \u3000\u3000\u516c\u5b59\u71d5\u679c\u7136\u53c8\u60ca\u53c8\u6012\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u6562\u52a8\u4ed6\u4e00\u6839\u6beb\u6bdb\uff0c\u6211\u53eb\u7239\u7239\u628a\u4f60\u4eec\u5168\u90fd\u6740\u4e86\uff01\u201d \u3000\u3000\u9633\u6d69\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7239\u7239\u53c8\u600e\u4e48\u6837\uff0c\u4f60\u7528\u4f60\u7239\u7239\u7684\u540d\u5934\u5c31\u5413\u5f97\u4f4f\u6211\u4e48\uff1f\u518d\u8bf4\uff0c\u4f60 \u7239\u7239\u7684\u624b\u4e5f\u4f38\u4e0d\u5f97\u8fd9\u4e48\u957f\u5427\uff0c\u5373\u4f7f\u5f53\u771f\u5982\u4f60\u6240\u613f\uff0c\u4f60\u7239\u7239\u5c06\u6765\u53ef\u4ee5\u6740\u4e86\u6211\u4eec\uff0c\u4f46\u6b64\u523b\u6211\u5374\u53ef \u4ee5\u5148\u628a\u4f60\u7684\u60c5\u90ce\u6740\u6389\uff0c\u4f60\u7239\u7239\u53c8\u80fd\u5948\u6211\u4f55\u54c9\uff1f\u5c0f\u599e\u513f\uff0c\u6211\u770b\u4f60\u8fd8\u662f\u522b\u8bf4\u5927\u8bdd\uff0c\u54b1\u4eec\u597d\u597d\u7684\u5546 \u91cf\u5546\u91cf\u5427\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u7ed9\u4ed6\u4e00\u5413\uff0c\u4e0d\u89c9\u6709\u51e0\u5206\u5bb3\u6015\uff0c\u5634\u91cc\u867d\u7136\u5f3a\u786c\uff0c\u6c14\u5df2\u9981\u4e86\u3002\u8bf4\u9053\uff1a\u201c\u548c\u4f60\u4eec\u6709\u4ec0\u4e48 \u597d\u5546\u91cf\u7684\uff1f\u201d \u3000\u3000\u9633\u6d69\u8bf4\u9053\uff1a\u201c\u8001\u5b9e\u5bf9\u4f60\u8bf4\u5427\uff0c\u6211\u4eec\u5bf9\u5389\u516c\u5b50\u5176\u5b9e\u5e76\u65e0\u6076\u610f\uff0c\u975e\u4f46\u6ca1\u6709\u6076\u610f\uff0c\u6211\u4eec\u8fd8\u60f3\u6367 \u4ed6\u4f5c\u5929\u9b54\u6559\u7684\u6559\u4e3b\u5462\uff01\u65e0\u5948\u4ed6\u4e0d\u53d7\u62ac\u4e3e\uff0c\u6211\u4eec\u53ea\u597d\u6682\u4e14\u59d4\u5c48\u4ed6\u4e86\u3002\u516c\u5b59\u59d1\u5a18\uff0c\u4f60\u5e2e\u6211\u4eec\u529d\u529d \u4ed6\u597d\u4e0d\u597d\uff0c\u529d\u5f97\u4ed6\u505a\u6559\u4e3b\uff0c\u4f60\u505a\u6559\u4e3b\u5a18\u5a18\uff0c\u5c82\u4e0d\u7f8e\u54c9\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u53eb\u9053\uff1a\u201c\u71d5\u59b9\uff0c\u522b\u53d7\u4ed6\u7684\u751c\u8a00\u871c\u8bed\u6b3a\u54c4\uff0c\u542c\u6211\u7684\u8bdd\uff0c\u8d76\u5feb\u56de\u53bb\uff01\u201d \u3000\u3000\u9f9a\u5e73\u91ce\u4e00\u628a\u63ea\u7740\u5389\u5357\u661f\uff0c\u567c\u567c\u556a\u556a\uff0c\u6b63\u624b\u53cd\u624b\uff0c\u63b4\u4e86\u4ed6\u4e24\u8bb0\u8033\u5149\uff0c\u559d\u9053\uff1a\u201c\u4e0d\u53d7\u62ac\u4e3e\u7684 \u4e1c\u897f\uff0c\u4f60\u662f\u656c\u9152\u4e0d\u5403\u504f\u504f\u8981\u5403\u7f5a\u9152\u662f\u4e0d\u662f\uff1f\u95ed\u4e0a\u4f60\u7684\u9e1f\u5634\uff0c\u5426\u5219\uff0c\u54fc\uff0c\u54fc\uff01\u6211\u8bf4\u7684\u8bdd\u53ef\u662f\u7b97 \u6570\u7684\uff01\u201d\u5315\u9996\u5728\u4ed6\u9762\u524d\u6643\u4e86\u4e24\u6643\u3002\u5389\u5357\u661f\u5634\u89d2\u6c81\u51fa\u9c9c\u8840\uff0c\u5374\u8fd8\u662f\u6302\u7740\u8f7b\u8511\u7684\u51b7\u7b11\u3002 \u3000\u3000\u9633\u6d69\u8bf4\u9053\uff1a\u201c\u5e73\u91ce\uff0c\u522b\u5fd9\u6253\u4ed6\uff01\u201d\u516c\u5b59\u71d5\u6c14\u5f97\u6d51\u8eab\u53d1\u6296\uff0c\u53eb\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u4eec\u7adf\u6562\u8fd9 \u6837\u6b3a\u4fae\u4ed6\uff01\u201d \u3000\u3000\u9633\u6d69\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u5f92\u513f\u7c97\u9c81\u5f97\u5f88\uff0c\u4f60\u522b\u751f\u6c14\u3002\u516c\u5b59\u59d1\u5a18\uff0c\u8fd9\u6837\u597d\u4e0d\u597d\uff0c\u6211\u8ba9\u4f60\u8ddf\u7740\u6211\u4eec \u4e00\u540c\u8d70\uff0c\u4f60\u4e0d\u80af\u529d\u4ed6\u6211\u4e5f\u4e0d\u52c9\u5f3a\uff0c\u4f60\u5374\u53ef\u4ee5\u5728\u4e00\u8def\u4e0a\u7167\u987e\u4ed6\u4e86\u3002\u201d\u4e00\u9762\u8bf4\u8bdd\u3001\u4e00\u9762\u5411\u516c\u5b59\u71d5 \u8d70\u53bb\uff0c\u597d\u50cf\u662f\u5728\u8fce\u63a5\u5979\u3002 \u3000\u3000\u516c\u5b59\u71d5\u7a0d\u4e00\u8fdf\u7591\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u597d\uff01\u201d\u5530\u7684\u62bd\u4e86\u4e00\u97ad\uff0c\u679c\u7136\u7b56\u9a6c\u8dd1\u4e0a\u5c71\u5c97\u3002\u539f\u6765\u516c\u5b59\u71d5 \u5c1a\u672a\u6df1\u77e5\u9633\u6d69\u7684\u5389\u5bb3\uff0c\u5979\u662f\u60f3\u4e58\u5176\u4e0d\u5907\uff0c\u51fa\u5176\u4e0d\u610f\u5730\u7a81\u7136\u6349\u4f4f\u9633\u6d69\uff0c\u8feb\u4ed6\u4ea4\u6362\u5389\u5357\u661f\u3002\u5c82\u77e5 \u9633\u6d69\u4e5f\u6b63\u662f\u5728\u540c\u6837\u7684\u6253\u7b97\uff0c\u8981\u628a\u516c\u5b59\u71d5\u6349\u67f1\uff0c\u597d\u5a01\u80c1\u5979\u7684\u7236\u4eb2\u3002 \u3000\u3000\u5389\u5357\u661f\u84e6\u5730\u7eb5\u8eab\u4e00\u8dc3\uff0c\u4ece\u60ac\u5d16\u4e0a\u8df3\u4e0b\u53bb\uff0c\u4eba\u5728\u534a\u7a7a\uff0c\u5140\u81ea\u53eb\u9053\uff1a\u201c\u71d5\u59b9\uff0c\u542c\u6211\u7684\u8bdd\uff0c\u8d76 \u5feb\u56de\u53bb\uff01\u201d \u3000\u3000\u5728\u5389\u5357\u661f\u7684\u60f3\u6cd5\uff0c\u4ee5\u4e3a\u81ea\u5df1\u4e00\u6b7b\u4e4b\u540e\uff0c\u516c\u5b59\u71d5\u7eb5\u7136\u4f24\u5fc3\u4e4b\u6781\uff0c\u4e5f\u662f\u53ea\u597d\u56de\u53bb\u7684\u4e86\uff0c\u5389\u5357 \u661f\u4e00\u6765\u56e0\u4e3a\u4e1a\u5df2\u53d7\u4e86\u91cd\u4f24\uff0c\u4e8c\u6765\u4e0d\u7518\u53d7\u8fb1\uff0c\u4e09\u6765\u4ed6\u53c8\u6df1\u77e5\u516c\u5b59\u71d5\u7edd\u8ba1\u6597\u4e0d\u8fc7\u9633\u6d69\uff0c\u4e0d\u613f\u610f\u8fde \u7d2f\u516c\u5b59\u71d5\u4e5f\u843d\u654c\u4eba\u4e4b\u624b\uff0c\u56e0\u6b64\u51b3\u610f\u4e00\u6b7b\u4e86\u4e4b\u3002 \u3000\u3000\u9f9a\u5e73\u91ce\u5949\u547d\u76d1\u89c6\u4ed6\uff0c\u5374\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4f1a\u81ea\u5df1\u8f7b\u751f\uff0c\u5f85\u5230\u542c\u5f97\u4ed6\u7684\u53eb\u58f0\uff0c\u8981\u60f3\u4f38\u624b\u62c9 \u4ed6\uff0c\u5df2\u662f\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u516c\u5b59\u71d5\u4e0e\u9633\u6d69\u90fd\u60ca\u5f97\u5446\u4e86\uff01 \u3000\u3000\u516c\u5b59\u71d5\u5446\u4e86\u4e00\u5446\u4e4b\u540e\uff0c\u53ea\u89c9\u773c\u524d\u5730\u8f6c\u5929\u65cb\uff0c\u8111\u4e2d\u4e00\u7247\u7a7a\u767d\uff0c\u597d\u50cf\u8fde\u81ea\u5df1\u4e5f\u6d88\u5931\u4e86\u3002\u5979\u8ff7 \u8ff7\u7cca\u7cca\u5730\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u5357\u54e5\uff01\u201d\u4e5f\u4e0d\u77e5\u662f\u4f24\u5fc3\u8fd8\u662f\u60b2\u6124\uff0c\u672c\u80fd\u7684\u5c31\u51b2\u4e0a\u524d\u53bb\uff01 \u3000\u3000\u9633\u6d69\u4e00\u5446\u4e4b\u540e\uff0c\u5374\u662f\u52a8\u4e86\u6740\u673a\uff0c\u4ed6\u5fc5\u987b\u6740\u6389\u516c\u5b59\u71d5\u706d\u53e3\uff0c\u4ee5\u514d\u65e5\u540e\u516c\u5b59\u71d5\u4f1a\u628a\u4ed6\u8feb\u6b7b\u5389 \u5357\u661f\u7684\u6d88\u606f\u6cc4\u6f0f\u51fa\u53bb\uff0c\u60f9\u6765\u516c\u5b59\u5b8f\u548c\u91d1\u9010\u6d41\u7b49\u5f3a\u654c\u6765\u5411\u4ed6\u5bfb\u4ec7\u3002 \u3000\u3000\u4e24\u4eba\u90fd\u662f\u5404\u81ea\u5411\u5bf9\u65b9\u5954\u53bb\uff0c\u9633\u6d69\u529b\u8d2f\u638c\u5fc3\uff0c\u84e6\u5730\u4fbf\u53d1\u51fa\u4e86\u7b2c\u516b\u91cd\u7684\u4fee\u7f57\u9634\u715e\u529f\uff01 \u3000\u3000\u516c\u5b59\u71d5\u7684\u5750\u9a91\u7ed9\u8fd9\u80a1\u5288\u7a7a\u638c\u529b\u4e00\u9707\uff0c\u767b\u65f6\u628a\u516c\u5b59\u71d5\u629b\u4e86\u8d77\u6765\u3002\u4e5f\u5e78\u800c\u6709\u6b64\u4e00\u629b\uff0c\u516c\u5b59\u71d5 \u7684\u5750\u9a91\u867d\u7ed9\u9633\u6d69\u7684\u638c\u529b\u51fb\u6bd9\uff0c\u4f46\u516c\u5b59\u71d5\u5374\u5f97\u4ee5\u907f\u5f00\u6b63\u9762\uff0c\u53ea\u89c9\u5bd2\u98ce\u4ece\u5979\u811a\u5e95\u5377\u8fc7\uff0c\u9633\u6d69\u7684\u4fee \u7f57\u9634\u715e\u529f\u5374\u5c1a\u672a\u66fe\u4f24\u7740\u4e86\u5979\u3002 \u3000\u3000\u4e00\u80a1\u5947\u5bd2\u4e4b\u6c14\u523a\u9aa8\u4fb5\u80a4\uff0c\u4f7f\u5f97\u516c\u5b59\u71d5\u7a0d\u7a0d\u6e05\u9192\u4e86\u4e9b\uff0c\u534a\u7a7a\u4e2d\u4e00\u4e2a\u7ffb\u8eab\uff0c\u7acb\u5373\u4fbf\u662f\u4e00\u62db \u201c\u9e70\u51fb\u957f\u7a7a\u201d\uff0c\u5934\u4e0b\u811a\u4e0a\u7684\u5012\u6301\u5251\u67c4\u5411\u9633\u6d69\u523a\u53bb\u3002 \u3000\u3000\u4fee\u7f57\u9634\u715e\u529f\u9887\u4e3a\u6d88\u8017\u771f\u529b\uff0c\u9633\u6d69\u6765\u4e0d\u53ca\u51dd\u805a\u771f\u529b\u7eed\u53d1\u7b2c\u516b\u91cd\u7684\u4fee\u7f57\u9634\u715e\u529f\uff0c\u53ea\u89c1\u5bd2\u5149\u4e00 \u95ea\uff0c\u516c\u5b59\u71d5\u7684\u5251\u5c16\u5df2\u523a\u5230\u4e86\u4ed6\u7684\u9876\u5fc3\u3002 \u3000\u3000\u9633\u6d69\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u767e\u5fd9\u4e2d\u4e00\u62db\u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u6325\u8896\u4e00\u62c2\uff0c\u8896\u4e2d\u7b3c\u6307\u5f39\u51fa\u3002\u53ea\u542c\u5f97 \u201c\u94ee\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u9633\u6d69\u7684\u8863\u8896\u7ed9\u524a\u53bb\u4e86\u4e00\u5e45\uff0c\u4e2d\u6307\u6307\u5c16\u4e5f\u7ed9\u5251\u950b\u5212\u7834\uff0c\u4f46\u516c\u5b59\u71d5\u8fd9\u4e00\u5251\u4ed6 \u5374\u4e5f\u6bd5\u7adf\u907f\u5f00\u4e86\u3002 \u3000\u3000\u9633\u6d69\u624b\u6bb5\u4f55\u7b49\u72e0\u8fa3\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u4fbf\u5373\u75db\u4e0b\u6740\u624b\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u4e00\u62c2\u4e00\u62ff\uff0c\u516c\u5b59\u71d5\u5251\u8d70\u8f7b \u7075\uff0c\u907f\u5f00\u4e86\u4ed6\u6293\u5411\u7435\u7436\u9aa8\u7684\u4e00\u62ff\uff0c\u4f46\u864e\u53e3\u7ed9\u4ed6\u6307\u5c16\u62c2\u7740\uff0c\u4ea6\u662f\u706b\u8fa3\u8fa3\u7684\u4f5c\u75db\u3002 \u3000\u3000\u9633\u6d69\u72de\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u8981\u62a5\u4ec7\u662f\u62a5\u4e0d\u4e86\u7684\uff0c\u4e0d\u5982\u5f85\u6211\u6210\u5168\u4f60\u4eec\uff0c\u8ba9\u4f60\u548c\u5389\u5357\u661f\u505a\u4e00\u5bf9\u540c\u547d \u9e33\u9e2f\u5427\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u84e6\u5730\u60f3\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u5e94\u8be5\u770b\u770b\u5357\u54e5\u53bb\u4e86\u3002\u201d\u5927\u53eb\u9053\uff1a\u201c\u6eda\u5f00\uff01\u201d\u5530\u5530\u6dae\u8fde\u73af \u4e09\u5251\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u4e24\u8d25\u4ff1\u4f24\u7684\u5251\u6cd5\uff0c\u9633\u6d69\u4e5f\u6709\u70b9\u5403\u60ca\uff0c\u8fde\u5fd9\u4fa7\u8eab\u4e00\u95ea\u3002 \u3000\u3000\u516c\u5b59\u71d5\u4f0a\u5982\u6c34\u86c7\u6e38\u8d70\uff0c\u201c\u55d6\u201d\u7684\u5c31\u7a9c\u8fc7\u53bb\uff0c\u9f9a\u5e73\u91ce\u5927\u5403\u4e00\u60ca\uff0c\u53ea\u9053\u5979\u662f\u8981\u8dd1\u6765\u53d6\u81ea\u5df1\u7684 \u6027\u547d\uff0c\u6025\u5207\u95f4\u65e0\u6687\u8fd0\u6c14\u884c\u529f\uff0c\u53ea\u597d\u786c\u7740\u5934\u76ae\u4e0a\u524d\u62b5\u6321\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4f46\u89c1\u5251\u5149\u4e00\u95ea\uff0c\u516c \u5b59\u71d5\u5df2\u662f\u5530\u7684\u4e00\u5251\u6307\u5230\u4e86\u4ed6\u7684\u9762\u95e8\u3002\u9f9a\u5e73\u91ce\u53f3\u638c\u659c\u52fe\uff0c\u5de6\u638c\u4ece\u8098\u5e95\u7a7f\u51fa\u6258\u5979\u624b\u8155\uff0c\u8fd9\u4e00\u62db\u672c \u6765\u662f\u201c\u7a7a\u624b\u5165\u767d\u5203\u201d\u7684\u9ad8\u660e\u624b\u6cd5\uff0c\u5c82\u77e5\u516c\u5b59\u71d5\u7684\u5251\u6765\u5f97\u592a\u5feb\uff0c\u9f9a\u5e73\u91ce\u7684\u62db\u6570\u672a\u66fe\u4f7f\u8db3\uff0c\u80a9\u5934 \u5df2\u662f\u7740\u4e86\u4e00\u5251\u3002\u516c\u5b59\u71d5\u5de6\u638c\u4e00\u6325\uff0c\u53c8\u662f\u4e00\u58f0\u559d\u9053\uff1a\u201c\u6eda\u5f00\uff01\u201d\u9f9a\u5e73\u91ce\u7684\u9762\u95e8\u767b\u65f6\u8d77\u4e86\u4e94\u6761\u7ea2 \u5370\uff0c\u7ed9\u516c\u5b59\u71d5\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u6253\u4e86\u4e00\u8bb0\u8033\u5149\uff0c\u201c\u6251\u901a\u201d\u5012\u5730\u3002 \u3000\u3000\u9f9a\u5e73\u91ce\u5fc3\u5934\u4e00\u60ca\uff0c\u6697\u53eb\u9053\uff1a\u201c\u6211\u547d\u4f11\u77e3\uff01\u201d\u4e0d\u6599\u516c\u5b59\u71d5\u7adf\u4e0d\u7406\u4ed6\uff0c\u5c06\u4ed6\u51fb\u5012\u4e4b\u540e\uff0c\u8eab\u5f62 \u4e00\u7eb5\uff0c\u5df2\u662f\u4ece\u4ed6\u8eab\u4e0a\u8de8\u8fc7\uff0c\u5f84\u81ea\u5954\u5230\u4e86\u60ac\u5d16\u7684\u8fb9\u6cbf\u3002 \u3000\u3000\u516c\u5b59\u71d5\u4fef\u8eab\u4e00\u770b\uff0c\u53ea\u89c1\u4e0b\u9762\u4e91\u5c01\u96fe\u9501\uff0c\u9690\u9690\u95fb\u5f97\u6251\u9f3b\u7684\u82b1\u9999\uff0c\u5374\u54ea\u91cc\u770b\u5f97\u89c1\u5389\u5357\u661f\u7684\u5f71 \u5b50\uff1f\u867d\u7136\u662f\u770b\u4e0d\u89c1\uff0c\u4f46\u516c\u5b59\u71d5\u7684\u773c\u524d\u5374\u5e7b\u51fa\u4e86\u4e00\u5e45\u753b\u56fe\uff0c\u597d\u4f3c\u4e0b\u9762\u5c31\u662f\u4e16\u5916\u6843\u6e90\uff0c\u5389\u5357\u661f\u5728 \u7e41\u82b1\u5982\u6d77\u4e4b\u4e2d\u7b11\u5f97\u6bd4\u82b1\u66f4\u7f8e\uff0c\u5f20\u5f00\u4e86\u53cc\u81c2\u5728\u8fce\u63a5\u5979\u3002 \u3000\u3000\u516c\u5b59\u71d5\u53eb\u9053\uff1a\u201c\u5357\u54e5\uff0c\u4f60\u7b49\u7b49\u6211\u3002\u201d\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u5bab\u79c9\u85e9\u7684\u58f0\u97f3\u5927\u53eb\u9053\uff1a\u201c\u71d5\u4f84\uff01 \u4e0d\u53ef\uff01\u201d\u53ef\u662f\u5df2\u7ecf\u8fdf\u4e86\uff0c\u516c\u5b59\u71d5\u5df2\u7ecf\u4ece\u60ac\u5d16\u4e0a\u8df3\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u4e00\u89c1\u86c7\u7130\u7bad\u5347\u8d77\uff0c\u7acb\u5373\u98de\u9a91\u8d76\u6765\uff0c\u4e0d\u6599\u4ecd\u662f\u8fdf\u4e86\u4e00\u6b65\uff0c\u773c\u7741\u7741\u5730\u770b\u7740\u516c\u5b59\u71d5\u6295\u5ca9 \u81ea\u5c3d\uff0c\u65e0\u6cd5\u633d\u6551\u5979\u7684\u6027\u547d\uff0c\u4e0d\u7531\u5f97\u5fc3\u5934\u5927\u6012\uff0c\u201c\u94ee\u201d\u7684\u4fbf\u4f3c\u4e00\u679d\u79bb\u5f26\u4e4b\u7bad\uff0c\u4ece\u9a6c\u80cc\u4e0a\u5c04\u51fa\uff0c \u534a\u7a7a\u4e2d\u5251\u5df2\u51fa\u9798\uff0c\u95ea\u7535\u822c\u5730\u76f4\u5954\u9633\u6d69\u523a\u53bb\u3002 \u3000\u3000\u9633\u6d69\u53eb\u9053\uff1a\u201c\u5bab\u9999\u4e3b\uff0c\u8fd9\u53ef\u662f\u4f60\u4eb2\u773c\u770b\u89c1\u7684\uff0c\u516c\u5b59\u59d1\u5a18\u81ea\u5df1\u8df3\u5d16\uff0c\u53ef\u4e0d\u5173\u6211\u7684\u4e8b\uff01\u201d \u3000\u3000\u5bab\u79c9\u85e9\u6027\u70c8\u5982\u706b\uff0c\u559d\u9053\uff1a\u201c\u653e\u5c41\uff0c\u4e0d\u662f\u4f60\u903c\u6b7b\u4e86\u5979\uff0c\u5979\u600e\u4f1a\u81ea\u5df1\u5bfb\u6b7b\uff1f\u201d\u4e00\u53e5\u8bdd\u672a\u66fe\u8bf4 \u5b8c\uff0c\u5df2\u662f\u95ea\u7535\u822c\u7684\u75be\u523a\u4e86\u516d\u516d\u4e09\u5341\u516d\u5251\uff01 \u3000\u3000\u5bab\u79c9\u85e9\u662f\u7ea2\u7f28\u4f1a\u4e2d\u7b2c\u4e00\u628a\u5251\u672f\u9ad8\u624b\uff0c\u6bd4\u516c\u5b59\u71d5\u5f3a\u5f97\u591a\u4e86\u3002\u9633\u6d69\u5728\u4ed6\u6025\u653b\u4e4b\u4e0b\uff0c\u54ea\u6709\u7a7a\u6687 \u4ece\u5bb9\u8fd0\u529f\uff0c\u53ea\u80fd\u89c1\u62db\u62c6\u62db\uff0c\u89c1\u5f0f\u62c6\u5f0f\uff0c\u52c9\u5f3a\u62db\u67b6\uff0c\u4f46\u6c42\u6682\u4e14\u4fdd\u7740\u4e00\u6761\u6027\u547d\u4e86\u3002 \u3000\u3000\u9633\u6d69\u7684\u7edd\u6280\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\u4f7f\u4e0d\u51fa\u6765\uff0c\u53ea\u89c9\u51c9\u610f\u98d3\u98d3\uff0c\u767d\u5149\u8000\u773c\uff0c\u5934\u76ae\u8d77\u6817\u3002\u597d\u50cf\u5bf9\u65b9 \u7684\u5251\u5c16\u5c31\u5728\u4ed6\u7684\u9762\u95e8\u5212\u6765\u5212\u53bb\uff0c\u968f\u65f6\u90fd\u53ef\u53d6\u4ed6\u6027\u547d\u3002\u9633\u6d69\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5e73 \u91ce\uff0c\u5feb\u7528\u4fee\u7f57\u9634\u715e\u529f\uff01\u201d \u3000\u3000\u9f9a\u5e73\u91ce\u4e5f\u5413\u5f97\u614c\u4e86\uff0c\u4f46\u4ed6\u77e5\u9053\u5e08\u7236\u82e5\u7136\u6b7b\u5728\u5bab\u79c9\u85e9\u5251\u4e0b\uff0c\u81ea\u5df1\u51b3\u96be\u9003\u547d\uff0c\u5f53\u4e0b\u52c9\u5f3a\u589e\u6151 \u5fc3\u795e\uff0c\u8fd0\u6c14\u8d2e\u529b\u3002\u8eb2\u5728\u4e00\u8fb9\uff0c\u4e58\u673a\u5077\u88ad\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u5229\u4e8e\u901f\u6218\u901f\u51b3\uff0c\u5251\u62db\u8d8a\u5c55\u8d8a\u5feb\uff0c\u731b\u5730\u559d\u58f0\u201c\u7740\uff01\u201d\u53cd\u624b\u4e00\u5251\u523a\u51fa\uff0c\u9633\u6d69\u970d\u5730\u4e00\u4e2a \u201c\u51e4\u70b9\u5934\u201d\uff0c\u53ea\u89c9\u5934\u76ae\u4e00\u7247\u8840\u51c9\uff0c\u5934\u4e0a\u7684\u4e71\u53d1\u5df2\u7ed9\u5251\u950b\u524a\u53bb\u4e86\u4e00\u5927\u7247\uff01 \u3000\u3000\u5bab\u79c9\u85e9\u8fd9\u4e00\u5251\u672a\u80fd\u6740\u6389\u9633\u6d69\uff0c\u6697\u81ea\u53eb\u58f0\uff1a\u201c\u53ef\u60dc\uff01\u201d\u6b63\u5f85\u518d\u65bd\u6740\u624b\uff0c\u84e6\u5730\u53ea\u89c9\u4e00\u80a1\u5bd2\u98ce \u88ad\u5230\uff0c\u539f\u6765\u662f\u9f9a\u5e73\u91ce\u8eb2\u5728\u4e00\u68f5\u6811\u540e\uff0c\u5df2\u662f\u4f7f\u51fa\u4e86\u4fee\u7f57\u9634\u715e\u529f\u3001\u5077\u5077\u5411\u4ed6\u53d1\u638c\u3002 \u3000\u3000\u9f9a\u5e73\u91ce\u7684\u4fee\u7f57\u9634\u715e\u529f\u4e0d\u8fc7\u7ec3\u5230\u7b2c\u4e94\u91cd\uff0c\u672a\u8db3\u4ee5\u5236\u5bab\u79c9\u85e9\u7684\u6b7b\u547d\u3002\u4f46\u5bab\u79c9\u85e9\u88ab\u8fd9\u5947\u5bd2\u4e4b\u6c14 \u4e00\u88ad\uff0c\u4e5f\u4e0d\u7531\u5f97\u5fae\u5fae\u53d1\u6296\uff0c\u5251\u62db\u7565\u7f13\uff0c\u8ba9\u9633\u6d69\u677e\u4e86\u53e3\u6c14\uff0c\u8f6c\u5371\u4e3a\u5b89\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u8fd0\u6c14\u4e09\u8f6c\uff0c\u9a71\u656c\u4e86\u4f53\u5185\u5bd2\u6bd2\uff0c\u653b\u52bf\u521a\u521a\u53c8\u8981\u5c55\u5f00\uff0c\u5077\u5728\u6811\u540e\u7684\u9f9a\u5e73\u91ce\u5374\u4e5f\u51dd\u805a\u4e86 \u771f\u6c14\uff0c\u518d\u6b21\u4f7f\u51fa\u4e86\u4fee\u7f57\u9634\u715e\u529f\u5411\u4ed6\u5077\u88ad\u3002 \u3000\u3000\u9633\u6d69\u672c\u8eab\u7684\u4fee\u7f57\u9634\u715e\u529f\u5df2\u7ec3\u5230\u4e86\u7b2c\u516b\u91cd\uff0c\u5f92\u5f1f\u7528\u7b2c\u4e94\u91cd\u4fee\u7f57\u9634\u715e\u529f\u6240\u53d1\u7684\u9634\u5bd2\u4e4b\u6c14\uff0c\u5bf9 \u4ed6\u6beb\u65e0\u5f71\u54cd\uff0c\u53d7\u5230\u5f71\u54cd\u7684\u53ea\u6709\u5bab\u79c9\u85e9\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u8fde\u7eed\u53d7\u4e86\u4e24\u6b21\u5e72\u6270\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u731b\u5730\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6211\u5148\u6bd9\u4e86\u4f60\uff01\u201d\u8bf4\u5230\u4e00\u4e2a \u201c\u6bd9\u201d\u5b57\uff0c\u5df2\u662f\u8eab\u79fb\u6b65\u6362\uff0c\u6251\u5230\u4e86\u9f9a\u5e73\u91ce\u7684\u85cf\u8eab\u4e4b\u5904\u3002\u9f9a\u5e73\u91ce\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u8981\u60f3\u8eb2\u65f6\uff0c\u54ea \u91cc\u8fd8\u6765\u5f97\u53ca\uff1f\u53ea\u542c\u5f97\u4e00\u58f0\u60e8\u547c\uff0c\u5251\u5149\u5339\u7ec3\u4f3c\u7684\u5377\u6765\uff0c\u5df2\u662f\u628a\u4ed6\u62e6\u8170\u65a9\u4e3a\u4e24\u622a\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u867d\u7136\u662f\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\u5c31\u65a9\u4e86\u9f9a\u5e73\u91ce\uff0c\u4f46\u5728\u8fd9\u4e00\u8fdb\u4e00\u9000\u4e4b\u95f4\uff0c\u5374\u7ed9\u4e86\u9633\u6d69\u8fd0\u7528\u4fee\u7f57 \u9634\u715e\u529f\u7684\u673a\u4f1a\u3002 \u3000\u3000\u9633\u6d69\u7684\u4fee\u7f57\u9634\u715e\u529f\u5f53\u7136\u8fdc\u975e\u5f92\u5f1f\u53ef\u6bd4\uff0c\u5bab\u79c9\u85e9\u521a\u4e00\u8f6c\u8eab\uff0c\u53ea\u89c9\u5bd2\u98ce\u6251\u9762\uff0c\u4e00\u80a1\u6392\u5c71\u5012\u6d77 \u822c\u7684\u638c\u529b\u5df2\u662f\u5411\u4ed6\u6d8c\u6765\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u5927\u559d\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u62fc\u4e86\uff01\u201d\u638c\u98ce\u5251\u5f71\u4e4b\u4e2d\uff0c\u9633\u6d69\u5927\u53eb\u4e00\u58f0\uff0c\u5012\u8dc3\u51fa\u6570\u4e08\u6c57\u5916\uff0c\u8eab \u4e0a\u53d7\u4e86\u4e09\u5904\u5251\u4f24\uff01 \u3000\u3000\u5bab\u79c9\u85e9\u6b63\u8981\u8fd0\u5251\u518d\u523a\uff0c\u624b\u8155\u5ffd\u5730\u4e00\u9635\u9ebb\u6728\uff0c\u957f\u5251\u51e0\u4e4e\u638c\u63e1\u4e0d\u7262\u3002\u539f\u6765\u4ed6\u53d7\u4e86\u7b2c\u516b\u91cd\u4fee\u7f57 \u660e\u715e\u529f\u4e4b\u4f24\uff0c\u8840\u6db2\u51e0\u4e4e\u4e3a\u4e4b\u51b7\u51dd\uff0c\u5173\u8282\u4e5f\u90fd\u50f5\u786c\u4e86\u3002 \u3000\u3000\u9633\u6d69\u501a\u7740\u4e00\u5757\u5927\u77f3\uff0c\u5598\u5401\u5401\u5730\u53eb\u9053\uff1a\u201c\u4f60\u8fc7\u6765\uff01\u201d\u4ed6\u4e0d\u77e5\u9053\u5bab\u79c9\u85e9\u4f24\u5f97\u5982\u4f55\uff0c\u4e0d\u6562\u5192\u9669 \u8fdb\u653b\uff0c\u4f46\u6599\u60f3\u5bab\u79c9\u85e9\u4f24\u5f97\u4e0d\u8f7b\uff0c\u80dc\u8d1f\u7684\u5173\u952e\u5728\u4e8e\u8c01\u80fd\u652f\u6301\u66f4\u4e45\uff0c\u6545\u800c\u4ed6\u51b3\u5b9a\u4e86\u4ee5\u9038\u5f85\u52b3\u7684\u6218 \u7565\u3002 \u3000\u3000\u5e78\u800c\u9633\u6d69\u4e0d\u6562\u5192\u9669\u8fdb\u653b\uff0c\u5bab\u79c9\u85e9\u53d1\u89c9\u4e0d\u5999\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u770b\u6765\u6b64\u4ec7\u4eca\u65e5\u662f\u4e0d\u80fd\u62a5\u4e86\uff0c\u6211 \u5f97\u7559\u7740\u4e00\u6761\u6027\u547d\uff0c\u56de\u53bb\u7980\u544a\u8235\u4e3b\u3002\u201d\u5f53\u4e0b\u4f5c\u52bf\u524d\u6251\uff0c\u5374\u7a81\u7136\u4e00\u4e2a\u8f6c\u8eab\uff0c\u548c\u8eab\u6eda\u4e0b\u5c71\u5761\uff0c\u4ed6\u90a3 \u5339\u5750\u9a91\u662f\u4e45\u7ecf\u8bad\u7ec3\u7684\u6218\u9a6c\uff0c\u89c1\u4e3b\u4eba\u6eda\u4e0b\u6765\uff0c\u7acb\u5373\u5c31\u8dd1\u4e0a\u53bb\u8fce\u63a5\u4ed6\u3002\u5bab\u79c9\u85e9\u5f3a\u529b\u652f\u6301\uff0c\u8fd0\u4e00\u53e3 \u6c14\uff0c\u624b\u638c\u6309\u5730\uff0c\u6323\u624e\u8d77\u6765\uff0c\u8df3\u4e0a\u9a6c\u80cc\u3002 \u3000\u3000\u9633\u6d69\u8fd9\u624d\u77e5\u9053\u5bab\u79c9\u85e9\u786e\u5b9e\u662f\u6bd4\u4ed6\u4f24\u5f97\u66f4\u91cd\uff0c\u6697\u81ea\u6094\u6068\u521a\u624d\u4e0d\u6562\u4e0b\u624b\u9664\u4ed6\uff0c\u7ed9\u4ed6\u8dd1\u4e86\u3002\u4f46 \u968f\u5373\u5fc3\u60f3\uff1a\u201c\u4ed6\u53d7\u4e86\u6211\u7684\u4fee\u7f57\u9634\u715e\u529f\u4e4b\u4f24\uff0c\u65e0\u836f\u53ef\u6cbb\uff0c\u8c05\u4ed6\u4e5f\u8dd1\u5f97\u4e0d\u8fdc\uff0c\u7ec8\u5fc5\u6bd2\u53d1\u800c\u4ea1\u3002\u201d \u9633\u6d69\u53d7\u4e86\u4e09\u5904\u5251\u4f24\uff0c\u4f24\u5f97\u4e5f\u5f88\u4e0d\u8f7b\uff0c\u5e78\u800c\u4e0d\u662f\u5185\u4f24\uff0c\u6577\u4e0a\u4e86\u91d1\u521b\u836f\uff0c\u8fd8\u80fd\u591f\u884c\u8d70\u3002\u4ed6\u6015\u6709\u4e49 \u519b\u627e\u6765\uff0c\u53ea\u6c42\u8d70\u5f97\u8d8a\u8fdc\u8d8a\u597d\uff0c\u81ea\u662f\u4e0d\u6562\u518d\u8d70\u56de\u5934\u8def\u53bb\u8ffd\u8e2a\u5bab\u79c9\u85e9\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u4e0a\u9a6c\u5954\u9a70\uff0c\u679c\u7136\u4e0d\u51fa\u9633\u6d69\u6240\u6599\uff0c\u8dd1\u4e86\u4e00\u7a0b\uff0c\u53ea\u89c9\u6d51\u8eab\u53d1\u51b7\uff0c\u8d8a\u6765\u8d8a\u662f\u96be\u53d7\uff0c\u7ec8\u4e8e \u652f\u5f85\u4e0d\u4f4f\uff0c\u4ece\u9a6c\u80cc\u4e0a\u6454\u4e86\u4e0b\u6765\uff0c\u4fbf\u5373\u6655\u4e86\u8fc7\u5927\u3002 \u3000\u3000\u4e14\u8bf4\u91d1\u9010\u6d41\u4e0e\u53f2\u7ea2\u82f1\u8054\u8882\u53bb\u627e\u5389\u5357\u661f\uff0c\u8fd9\u65f6\u6b63\u5728\u8def\u4e0a\u884c\u8d70\uff0c\u5ffd\u5730\u6709\u4e2a\u519c\u592b\u8fce\u9762\u524d\u6765\uff0c\u5411 \u4ed6\u4eec\u6253\u91cf\u4e00\u7ffb\uff0c\u4fbf\u95f4\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u662f\u4ece\u897f\u660c\u6765\u7684\u4e48\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u90a3\u519c\u592b \u9053\uff1a\u201c\u4e49\u519b\u4e0d\u77e5\u662f\u5426\u5c1a\u5728\u897f\u660c\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u627e\u4e49\u519b\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d\u90a3\u519c\u592b\u9053\uff1a\u201c\u4e0d\u662f\u6211 \u8981\u627e\u4e49\u519b\uff0c\u662f\u6211\u4eec\u6751\u5b50\u91cc\u7684\u5f20\u5927\u4f2f\u6258\u6211\u5e26\u4e2a\u53e3\u4fe1\u7ed9\u4e49\u519b\u91cc\u7684\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u4eec\u6b63\u662f\u4e49\u519b\uff0c\u4e0d\u77e5\u90a3\u4f4d\u5f20\u5927\u4f2f\u8981\u6211\u7684\u662f\u8c01\uff0c\u4f60\u8bf4\u7ed9\u6211\u542c\u542c\uff0c\u6216\u8bb8\u6211\u4f1a\u77e5 \u9053\uff1a\u201c \u3000\u3000\u90a3\u8863\u592b\u559c\u51fa\u671b\u5916\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u731c\u4f60\u4eec\u662f\u4e49\u519b\u4e2d\u4eba\uff0c\u679c\u7136\u731c\u5f97\u4e0d\u9519\u3002\u5f20\u5927\u4f2f\u6258\u6211\u5e26\u7684\u53e3 \u4fe1\uff0c\u662f\u5e26\u7ed9\u4e00\u4e2a\u540d\u53eb\u91d1\u9010\u6d41\u7684\u597d\u6c49\uff0c\u4e0d\u77e5\u4f60\u53ef\u8ba4\u5f97\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u4e3a\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8fd9\u4e2a\u91d1\u9010\u6d41\u6b63\u662f\u6211\u6700\u8981\u597d\u7684\u670b\u53cb\uff0c\u4f60\u8981\u5e26\u4ec0\u4e48\u53e3\u4fe1\u7ed9 \u4ed6\uff0c\u544a\u8bc9\u6211\u5427\uff0c\u6211\u7ed9\u4f60\u8f6c\u8fbe\uff0c\u4e5f\u7701\u5f97\u4f60\u8dd1\u4e00\u8d9f\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u90a3\u4e2a\u4ec0\u4e48\u5f20\u5927\u4f2f\u4f55\u4ee5\u4f1a\u77e5\u9053\u6211 \u5462\uff1f\u201d \u3000\u3000\u90a3\u519c\u592b\u9053\uff1a\u201c\u8fd9\u5c31\u6700\u597d\u4e0d\u8fc7\u4e86\u3002\u5f20\u5927\u4f2f\u524d\u65e5\u6551\u4e86\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4eba\u53d7\u4e86\u91cd\u4f24\uff0c\u4e0d\u80fd\u79fb\u52a8\u3002\u4ed6 \u8bf4\u4ed6\u5728\u4e49\u519b\u4e2d\u6709\u4e2a\u597d\u670b\u53cb\u540d\u53eb\u91d1\u9010\u6d41\u3001\u8bf7\u5f20\u5927\u4f2f\u5e26\u8bdd\u7ed9\u91d1\u9010\u6d41\uff0c\u53eb\u91d1\u9010\u6d41\u9a6c\u4e0a\u6765\u770b\u4ed6\u3002\u5f20\u5927 \u4f2f\u5bb6\u91cc\u53ea\u6709\u4e00\u4e2a\u5e7c\u5b59\uff0c\u4ed6\u81ea\u5df1\u8dd1\u4e0d\u5f00\uff0c\u56e0\u6b64\u53c8\u8f6c\u6258\u6211\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u53c8\u60ca\u53c8\u559c\uff0c\u53ea\u9053\u8fd9\u4e2a\u4eba\u662f\u5389\u5357\u661f\uff0c\u8fde\u5fd9\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u662f\u4e49\u519b\u7684\u670b\u53cb\u5728\u5f20\u5927\u4f2f\u5bb6\u91cc \u517b\u4f24\uff0c\u6211\u4eec\u5e94\u8be5\u5148\u53bb\u770b\u770b\u6b64\u4eba\uff0c\u8bbe\u6cd5\u66ff\u4ed6\u6cbb\u4f24\u3002\u201d \u3000\u3000\u90a3\u519c\u592b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u8be5\u5982\u6b64\u3002\u8bf7\u4e24\u4f4d\u968f\u6211\u6765\u3002\u201d\u5f53\u4e0b\u5728\u524d\u9886\u8def\uff0c\u628a\u91d1\u3001\u53f2\u4e8c\u4eba\u5e26\u5230\u4e86 \u4e00\u5bb6\u519c\u5bb6\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8fdb\u53bb\u4e00\u770b\uff0c\u53ea\u89c1\u7095\u4e0a\u8eba\u7740\u4e00\u4e2a\u4eba\uff0c\u5374\u539f\u6765\u662f\u5bab\u79c9\u85e9\u3002\u91d1\u9010\u6d41\u5927\u5403\u4e00\u60ca\uff0c\u53eb\u9053\uff1a \u201c\u5bab\u5144\uff0c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u90a3\u59d3\u5f20\u7684\u8001\u519c\u592b\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4f4d\u670b\u53cb\u6050\u6015\u662f\u4e0d\u6210\u4e86\uff0c\u4eca\u5929\u4e00\u65e9\u5c31 \u660f\u8ff7\u8fc7\u53bb\uff0c\u73b0\u5728\u624b\u811a\u90fd\u50f5\u786c\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8bd5\u63a2\u4ed6\u7684\u8109\u606f\uff0c\u53ea\u89c9\u89e6\u4f53\u751f\u5bd2\uff0c\u5176\u51b7\u5982\u51b0\uff0c\u4f46\u8109\u606f\u5c1a\u672a\u65ad\u7edd\u3002\u53f2\u7ea2\u82f1\u6084\u58f0\u95ee\u9053\uff1a \u201c\u8fd8\u6709\u5f97\u6551\u4e48\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4ed6\u662f\u53d7\u4e86\u4fee\u7f57\u9634\u715e\u529f\u4e4b\u4f24\uff0c\u5bd2\u6bd2\u5df2\u4fb5\u5165\u810f\u8151\uff0c\u53ea\u6015\u4ed6\u8fd9\u4e00\u8eab \u6b66\u529f\u662f\u96be\u4ee5\u5b8c\u5168\u590d\u539f\u4e86\u3002\u201d\u53f2\u7ea2\u82f1\u559c\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6027\u547d\u662f\u53ef\u4fdd\u65e0\u865e\u4e86\uff0c\u90a3\u4f60\u5c31\u8d76\u5feb\u65bd\u6551 \u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9ed8\u8fd0\u7384\u529f\uff0c\u4ee5\u672c\u8eab\u771f\u529b\u66ff\u5bab\u79c9\u85e9\u63a8\u8840\u8fc7\u5b98\u3002\u8fc7\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u53ea\u89c1\u91d1\u9010\u6d41\u5934\u9876\u5192\u51fa \u70ed\u817e\u817e\u7684\u767d\u6c14\uff0c\u6c57\u5982\u800c\u4e0b\uff1b\u5bab\u79c9\u85e9\u672c\u6765\u50cf\u662f\u4e00\u5f20\u767d\u7eb8\u7684\u8138\u4e0a\uff0c\u6e10\u6e10\u6709\u4e86\u51e0\u5206\u8840\u8272\uff0c\u7ec8\u4e8e\u7f13\u7f13 \u5f20\u5f00\u4e86\u773c\u775b\uff0c\u90a3\u8001\u519c\u592b\u53c8\u60ca\u53c8\u559c\uff0c\u53eb\u9053\uff1a\u201c\u6d3b\u8fc7\u6765\u5566\uff01\u6d3b\u8fc7\u6765\u5566\uff01\u201d \u3000\u3000\u5bab\u79c9\u85e9\u8ba4\u51fa\u4e86\u91d1\u9010\u6d41\uff0c\u8138\u4e0a\u7efd\u51fa\u4e00\u6735\u7b11\u5bb9\uff0c\u5634\u5507\u5fae\u5fae\u5f00\u5408\uff0c\u91d1\u9010\u6d41\u628a\u8033\u6735\u51d1\u5230\u4ed6\u7684\u5634 \u8fb9\uff0c\u53ea\u542c\u5f97\u4ed6\u8bf4\u9053\uff1a\u201c\u4f60\u6765\u5566\uff0c\u6211\u6709\u4e00\u4e8b\u6073\u6c42\u2026\u2026\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5bab\u5144\uff0c\u4f60\u6b47\u6b47\uff0c\u597d\u4e86\u518d \u8bf4\u3002\u201d\u5bab\u79c9\u85e9\u8138\u4e0a\u7684\u7b11\u5bb9\u6d88\u5931\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u597d\u4e0d\u4e86\u5566\uff0c\u9664\u975e\u4f60\u6709\u8d77\u6b7b\u56de\u751f\u7684\u7075\u836f\u3002\u8fd9\uff0c\u8fd9 \u53ea\u662f\u5f92\u8017\u4f60\u7684\u529f\u529b\u800c\u5df2\u3002\u4f60\u542c\u6211\u8bf4\u2026\u2026\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u6b63\u662f\u6709\u8d77\u6b7b\u56de\u751f\u7684\u7075\u836f\uff0c\u4f60\u4e0d\u8981 \u62c5\u5fe7\uff0c\u5929\u5927\u7684\u4e8b\u60c5\u90fd\u6682\u4e14\u6401\u5728\u540e\u5934\uff0c\u4fdd\u517b\u4f60\u7684\u8eab\u4f53\u8981\u7d27\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u628a\u91d1\u9010\u6d41\u62c9\u8fc7\u4e00\u8fb9\uff0c\u8f7b\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u54ea\u91cc\u6765\u7684\u7075\u836f\uff1f\u201d\u8981\u77e5\u4fee\u7f57\u9634\u715e\u529f\u7684\u5bd2\u6bd2\u4fb5 \u5165\u4e86\u810f\u8151\uff0c\u5373\u4f7f\u91d1\u9010\u6d41\u7684\u5185\u529f\u591a\u597d\uff0c\u4e5f\u51b3\u4e0d\u80fd\u9a71\u9664\u51c0\u5c3d\uff0c\u53ea\u80fd\u66ff\u75c5\u4eba\u82df\u5ef6\u6b8b\u5598\u800c\u5df2\u3002\u6545\u6b64\u53f2 \u7ea2\u82f1\u534a\u4fe1\u534a\u7591\uff0c\u53ea\u9053\u91d1\u9010\u6d41\u7684\u8bf4\u8bdd\u662f\u8bf4\u6765\u5b89\u6170\u75c5\u4eba\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4e00\u70b9\u4e0d\u5047\u3002\u8fd9\u7075\u836f\u8fd8\u662f\u4f60\u5bb6\u7684\u4e1c\u897f\uff0c\u4f60\u600e\u4e48\u5fd8\u4e86\u3002\u201d\u53f2\u7ea2\u82f1\u6014\u4e86\u4e00\u6014\uff0c \u9053\uff1a\u201c\u6211\u5bb6\u7684\u4e1c\u897f\u3002\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u5fd8\u8bb0\u4f60\u54e5\u54e5\u9001\u7ed9\u8428\u798f\u9f0e\u7684\u5bff\u793c\u4e86\u4e48\uff1f\u201d\u53f2\u7ea2\u82f1\u8fd9\u624d \u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u4f60\u8bf4\u7684\u662f\u90a3\u652f\u5343\u5e74\u4f55\u9996\u4e4c\uff01\u201d \u3000\u3000\u539f\u6765\u53f2\u767d\u90fd\u5728\u53bb\u5e74\u7ed9\u8428\u798f\u9f0e\u795d\u5bff\u4e4b\u65f6\uff0c\u5bff\u793c\u66fe\u7ecf\u4e09\u6b21\u66f4\u6362\uff0c\u7531\u4e8e\u591c\u660e\u73e0\u548c\u7384\u94c1\u5148\u540e\u88ab\u674e \u6566\u4e0e\u91d1\u9010\u6d41\u76d7\u53bb\uff0c\u4ed6\u5343\u65b9\u767e\u8ba1\uff0c\u6700\u540e\u53c8\u627e\u5230\u4e86\u4e00\u652f\u4e1a\u5df2\u6210\u5f62\u7684\u5343\u5e74\u4f55\u9996\u4e4c\u5f53\u4f5c\u5bff\u672d\uff0c\u4e0d\u6599\u8fd9 \u652f\u4f55\u9996\u4e4c\u5728\u7fa4\u96c4\u5927\u52ab\u5bff\u5802\u4e4b\u65f6\uff0c\u53c8\u843d\u5165\u4e86\u91d1\u9010\u6d41\u4e4b\u624b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u652f\u4f55\u9996\u4e4c\u6211\u672c\u6765\u662f\u51c6\u5907\u7559\u7ed9\u4f60\u7528\u7684\uff0c\u4e00\u76f4\u5e26\u5728\u8eab\u8fb9\u3002\u8fd9\u6b21\u5e78\u4e8f\u4f60\u6ca1\u53d7 \u4f24\uff0c\u7528\u4e0d\u7740\u5b83\uff0c\u6b63\u597d\u7ed9\u5bab\u5927\u54e5\u6551\u547d\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u53d6\u51fa\u90a3\u652f\u4f55\u8425\u4e4c\uff0c\u5207\u6210\u7247\u72b6\uff0c\u8bf7\u90a3\u8001\u519c\u592b\u5e2e\u5fd9\uff0c\u714e\u6210\u836f\u8336\uff0c\u7ed9\u5bab\u4e58\u85e9\u559d\u4e0b\u3002\u4e00\u652f \u4f55\u9996\u4e4c\u53ef\u4f9b\u4e09\u6b21\u670d\u7528\u3002\u91d1\u9010\u6d41\u9053\uff1a\u201c\u82e5\u65e0\u610f\u5916\u7684\u53d8\u5316\uff0c\u4e09\u65e5\u4e4b\u540e\uff0c\u4ed6\u5e94\u5f53\u53ef\u4ee5\u8d77\u5e8a\u4e86\u3002\u201d \u3000\u3000\u60c5\u51b5\u6bd4\u91d1\u9010\u6d41\u9884\u6599\u7684\u8fd8\u8981\u597d\u4e9b\uff0c\u5bab\u79c9\u85e9\u670d\u4e86\u4e00\u5242\u4e4b\u540e\uff0c\u7b2c\u4e8c\u5929\u4e00\u65e9\u5df2\u662f\u6c14\u723d\u795e\u6e05\uff0c\u8bf4\u8bdd \u4e5f\u4e0d\u7528\u600e\u6837\u8d39\u529b\u4e86\u3002\u4e8e\u662f\u4ed6\u8feb\u4e0d\u53ca\u5f85\u7684\u4fbf\u5c06\u4ed6\u8981\u592e\u6c42\u91d1\u9010\u6d41\u7684\u4e8b\u60c5\u8bf4\u51fa\u6765\u3002\u6b63\u662f\uff1a \u3000\u3000\u9e33\u9e2f\u60a3\u96be\u7518\u540c\u547d\uff0c\u77e5\u5df1\u76f8\u9022\u8bf4\u6b64\u60c5\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u56db\u5341\u56de\u3000\u5c82\u77e5\u964c\u8def\u9022\u5f3a\u654c\u3000\u5374\u559c\u8352\u6751\u9047\u6545\u4eba \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u56db\u5341\u56de\u3000\u5c82\u77e5\u964c\u8def\u9022\u5f3a\u654c\u3000\u5374\u559c\u8352\u6751\u9047\u6545\u4eba \u3000\u3000\u5bab\u79c9\u85e9\u9053\uff1a\u201c\u91d1\u5144\uff0c\u6211\u60f3\u8bf7\u4f60\u62a5\u4e00\u4e2a\u4fe1\u7ed9\u627e\u4eec\u7ea2\u7f28\u4f1a\u7684\u8235\u4e3b\uff0c\u516c\u5b59\u8235\u4e3b\u7684\u5973\u513f\u4e0d\u5e78\u3001\u4e0d \u5e78\uff0c\u5df2\u662f\u8eab\u906d\u60e8\u6b7b\u3002\u8feb\u6b7b\u5979\u7684\u4ec7\u4eba\u662f\u9633\u6d69\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u60ca\u9053\uff1a\u201c\u516c\u5b59\u71d5\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u5bab\u79c9\u85e9\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4eb2\u773c\u770b\u89c1\u5979\u4ece\u4e00\u4e2a\u60ac\u5d16\u4e0a\u8df3\u4e0b\u53bb\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5979\u4e00\u8eab\u8f7b\u529f\uff0c\u8bf4\u4e0d\u5b9a\u6216\u8bb8\u4f1a\u6b7b\u91cc\u9003\u751f\u3002\u201d \u3000\u3000\u5ba6\u79c9\u85e9\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\u3002\u4e0d\u8fc7\uff0c\u5f53\u65f6\u7684\u60c5\u5f62\uff0c\u56fa\u7136\u5979\u662f\u53d7\u9633\u6d69\u6240\u903c\uff0c\u4f46\u770b\u6765\u5979\u4e5f\u662f\u81ea\u613f \u6c42\u6b7b\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u5b59\u71d5\u662f\u51fa\u53bb\u627e\u5bfb\u5389\u5357\u661f\u7684\uff0c\u83ab\u975e\u5979\u5f97\u5230\u4e86\u5389\u5357\u661f\u7684\u4ec0\u4e48\u4e0d \u5e78\u7684\u6d88\u606f\uff1f\u201d\u601d\u5ff5\u53ca\u6b64\uff0c\u58f0\u97f3\u4e0d\u89c9\u90fd\u98a4\u6296\u4e86\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6b63\u662f\u5982\u4f60\u6240\u6599\uff01\u4ed6\u5728\u8df3\u5d16\u4e4b\u65f6\uff0c\u53e3\u4e2d\u8fd8\u5728\u53eb\u7740\u5389\u5357\u661f\u7684\u540d\u5b57\u3002\u6211\u4e00 \u76f4\u6ca1\u6709\u77e5\u9053\uff0c\u539f\u6765\u4ed6\u4eec\u65e9\u5df2\u662f\u4e00\u5bf9\u60c5\u4fa3\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u89c1\u7740\u5389\u6d77\u661f\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5bab\u79c9\u85e9\u9053\uff1a\u201c\u6ca1\u6709\u3002\u4f46\u6211\u542c\u5f97\u516c\u5b59\u59d1\u5a18\u53eb\u558a\u7740\u2018\u5357\u54e5\u3001\u5357\u54e5\uff01\u4f60\u7b49\u7b49\u6211\uff01\u2019\u4f9d\u6b64\u770b\u6765\uff0c \u6050\u6015\u5389\u5357\u661f\u5df2\u662f\u5728\u5979\u4e4b\u524d\uff0c\u547d\u4e27\u5e7d\u8c37\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u90a3\u4e2a\u5730\u65b9\u5728\u4f55\u5904\uff1f\u6211\u8981\u53bb\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\u3002\u5373\u4f7f\u4ed6\u4eec\u771f\u7684\u6b7b\u4e86\uff0c\u6211\u4e5f\u8be5\u6536\u846c \u4ed6\u4eec\u7684\u9ab8\u9aa8\u3002\u201d \u3000\u3000\u5bab\u79c9\u85e9\u8bf4\u4e86\u5f53\u65e5\u7684\u60c5\u5f62\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u81ea\u6127\u672c\u9886\u4e0d\u6d4e\uff0c\u7ed9\u9633\u6d69\u6253\u5f97\u91cd\u4f24\uff0c\u4f0f\u5728\u9a6c\u80cc\u4e0a \u9003\u547d\u4e4b\u65f6\uff0c\u5df2\u662f\u795e\u667a\u8ff0\u7cca\u3002\u53ea\u77e5\u90a3\u4e2a\u5730\u65b9\u662f\u5728\u9ed1\u77f3\u5c97\u7684\u4e1c\u9762\uff0c\u662f\u4e71\u5c71\u4e4b\u4e2d\u4e00\u4e2a\u9661\u5c96\u7684\u5c71\u5934\uff0c \u4e5f\u4e0d\u77e5\u79bb\u6b64\u591a\u8fdc\u3002\u201d \u3000\u3000\u5bab\u79c9\u85e9\u8bf4\u4e0d\u51fa\u5177\u4f53\u7684\u6240\u5728\uff0c\u8981\u5728\u4e71\u5c71\u4e4b\u4e2d\u627e\u5230\u516c\u5b59\u71d5\u8df3\u5d16\u7684\u5730\u65b9\u53ef\u662f\u6781\u4e0d\u5bb9\u6613\uff0c\u91d1\u9010\u6d41 \u60f3\u9053\uff1a\u201c\u8981\u5f85\u5bab\u79c9\u85e9\u8eab\u4f53\u590d\u539f\uff0c\u6050\u6015\u81f3\u5c11\u4e5f\u5f97\u5728\u534a\u6708\u4e4b\u540e\u3002\u201d\u4ed6\u6025\u4e8e\u77e5\u9053\u771f\u76f8\uff0c\u54ea\u80fd\u7b49\u5230\u5bab \u79c9\u85e9\u590d\u539f\u4e4b\u540e\u624d\u5e26\u4ed6\u53bb\uff0c\u4e0d\u7531\u5f97\u5fc3\u6025\u5982\u711a\uff0c\u9891\u9891\u6413\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\uff0c\u600e\u4e48\u529e\uff1f\u4e07\u4e00\u4ed6\u4eec\u662f\u53d7 \u4e86\u91cd\u4f24\uff0c\u4fa5\u5e78\u672a\u6b7b\uff0c\u54b1\u4eec\u53bb\u4e5f\u5f97\u8fdf\u4e86\uff0c\u4ed6\u4eec\u4e5f\u997f\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5bab\u79c9\u85e9\u84e6\u5730\u60f3\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u5ea7\u5c71\u5934\u7684\u58eb\u8272\u4e0e\u522b\u5904\u4e0d\u540c\uff01\u201d \u3000\u3000\u5168\u9010\u6d41\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u540c\uff1f\u201d \u3000\u3000\u5bab\u79c9\u85e9\u9053\uff1a\u201c\u571f\u8272\u6bb7\u7ea2\u5982\u8840\uff0c\u5c71\u4e0a\u904d\u662f\u91ce\u82b1\u3002\u201d \u3000\u3000\u90a3\u59d3\u5f20\u7684\u8001\u519c\u592b\u5ffd\u9053\uff1a\u201c\u6211\u77e5\u9053\u90a3\u4e2a\u5730\u65b9\uff0c\u90a3\u5ea7\u5c71\u53eb\u8d6d\u77f3\u5c71\uff0c\u4e0b\u9762\u6709\u4e00\u4e2a\u6df1\u8c37\u540d\u4e3a\u6843\u82b1 \u8c37\u3002\u5982\u679c\u662f\u8d6d\u77f3\u5c71\u4e0a\u7684\u60ac\u5d16\u4e0a\u8df3\u4e0b\u53bb\uff0c\u90a3\u4e00\u5b9a\u662f\u8df3\u843d\u82b1\u8c37\u4e86\uff0c\u6843\u82b1\u8c37\u79bb\u6b64\u4e0d\u8fc7\u4e03\u5341\u91cc\u8def\uff0c\u4ece \u8fd9\u91cc\u6751\u53e3\u51fa\u53bb\uff0c\u4e00\u76f4\u5411\u5357\u8d70\uff0c\u6cbf\u9014\u53ef\u4ee5\u55c5\u5230\u82b1\u9999\u3001\u5f88\u5bb9\u6613\u627e\u5f97\u5230\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u559c\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u9a6c\u4e0a\u5c31\u53bb\uff01\u201d \u3000\u3000\u90a3\u8001\u519c\u5927\u9053\uff1a\u201c\u4f46\u8fd9\u5730\u65b9\uff0c\u8fd9\u5730\u65b9\u5374\u662f\u4e0d\u597d\u53bb\u7684\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u8001\u519c\u592b\u9053\uff0c\u201c\u6843\u82b1\u8c37\u662f\u7fa4\u5c71\u73af\u62b1\u4e2d\u7684\u4e00\u4e2a\u5e7d\u8c37\uff0c\u6ca1\u6709\u5165\u53e3\u7684\u3002\u8981\u4e0b\u53bb\u5fc5\u987b\u4ece\u8d6d\u77f3\u5c71\u7684 \u5c71\u9876\u722c\u4e0b\u53bb\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u96be\u4e0d\u5012\u6211\u3002\u201d\u90a3\u8001\u519c\u592b\u9053\uff1a\u201c\u4ece\u5c71\u4e0a\u722c\u4e0b\u53bb\u6216\u8bb8\u8fd8\u4e0d\u7b97\u592a\u96be\uff0c\u4f46\u8fd9\u8c37\u5e95 \u5374\u662f\u5947\u9669\u4e4b\u5730\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u5371\u9669\uff1a\u8bf7\u8001\u4e08\u89c1\u544a\u3002\u201d \u3000\u3000\u90a3\u8001\u519c\u592b\u9053\uff1a\u201c\u8fd9\u6843\u82b1\u8c37\u4e2d\u6709\u5343\u4e07\u68f5\u91ce\u751f\u7684\u6843\u82b1\uff0c\u73b0\u5728\u6b63\u662f\u6625\u5929\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8be7\u9053\uff1a\u201c\u548c\u5b63\u8282\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u90a3\u8001\u519c\u592b\u9053\uff1a\u201c\u6bcf\u5230\u6625\u5929\uff0c\u6843\u82b1\u8c37\u4e2d\u5c31\u4f1a\u6709\u6843\u82b1\u7634\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6843\u82b1\u7634\uff1f\u53ef\u662f\u4e00\u79cd\u90aa\u6bd2\u7684\u7634\u96fe\u4e48\uff1f\u201d \u3000\u3000\u90a3\u8001\u519c\u592b\u9053\uff1a\u201c\u6b63\u662f\u3002\u8c37\u4e2d\u5343\u4e07\u682a\u6843\u82b1\u81ea\u5f00\u81ea\u843d\uff0c\u8c37\u5e95\u5730\u6c14\u6e7f\u70ed\uff0c\u6bcf\u5230\u6625\u5929\uff0c\u843d\u82b1\u8150 \u70c2\uff0c\u7634\u6c14\u84b8\u53d1\u5f97\u7279\u522b\u5389\u5bb3\uff0c\u597d\u50cf\u4e91\u96fe\u4f3c\u7684\uff0c\u989c\u8272\u5341\u5206\u7f8e\u4e3d\uff01\u591a\u5e74\u524d\u6211\u4eec\u6751\u5b50\u91cc\u6709\u51e0\u4e2a\u80c6\u5927\u7684 \u5c11\u5e74\uff0c\u770b\u5230\u8fd9\u79cd\u9c9c\u8273\u7684\u7634\u96fe\uff0c\u60f3\u4e0b\u53bb\u770b\u4e2a\u7a76\u7adf\uff0c\u4e00\u53bb\u65e0\u56de\uff01\u201d \u3000\u3000\u5168\u9010\u6d41\u9053\uff1a\u201c\u8001\u4e08\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u6211\u6709\u89e3\u6bd2\u7684\u7075\u4e39\uff0c\u7634\u6c14\u7eb5\u7136\u5389\u5bb3\uff0c\u6599\u60f3\u4e5f\u96be\u5948\u6211\u4f55\u3002\u201d \u3000\u3000\u8fd9\u8001\u519c\u592b\u89c1\u4ed6\u80fd\u591f\u628a\u5782\u6b7b\u7684\u5bab\u79c9\u85e9\u6551\u6d3b\uff0c\u5bf9\u4ed6\u7684\u8bdd\u4e5f\u6709\u51e0\u5206\u76f8\u4fe1\u3002\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60 \u65e2\u662f\u4e3a\u4e86\u6551\u4eba\u800c\u53bb\uff0c\u6211\u4e0d\u4fbf\u62e6\u963b\u3002\u4f46\u4f60\u53ef\u5f97\u5343\u4e07\u591a\u52a0\u5c0f\u5fc3\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u8c22\u8fc7\u4e86\u90a3\u8001\u519c\u592b\uff0c\u7559\u4e0b\u53f2\u7ea2\u82f1\u5e2e\u4ed6\u4e00\u540c\u7167\u6599\u5bab\u79c9\u85e9\uff0c\u4fbf\u5373\u5230\u8d6d\u77f3\u5c71\u53bb\u3002 \u3000\u3000\u4f9d\u7167\u90a3\u8001\u519c\u592b\u7684\u6307\u70b9\uff0c\u91d1\u9010\u6d41\u4e0a\u4e86\u8d6d\u77f3\u5c71\uff0c\u679c\u7136\u5f88\u5bb9\u6613\u7684\u5c31\u627e\u5230\u4e86\u6843\u82b1\u8c37\u3002 \u3000\u3000\u4ece\u5f53\u65e5\u516c\u5b59\u71d5\u7acb\u8db3\u7684\u60ac\u5d16\u4e4b\u5904\u671b\u4e0b\u53bb\uff0c\u53ea\u89c1\u8c37\u4e2d\u7634\u6c14\u679c\u7136\u662f\u851a\u82e5\u4e91\u971e\uff0c\u6d53\u70c8\u7684\u82b1\u9999\u76f4\u51b2 \u9f3b\u89c2\uff0c\u91d1\u9010\u6d41\u6df1\u6df1\u5438\u4e86\u4e24\u53e3\u6c14\uff0c\u8111\u888b\u5fae\u89c9\u6655\u7729\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6709\u6df1\u539a\u7684\u5185\u529f\uff0c\u81ea\u662f\u4e0d\u4ff1\u4e2d\u6bd2\uff0c\u4f46\u4ed6\u4e3a\u4e86\u5c0f\u5fc3\u8d77\u89c1\uff0c\u53e3\u91cc\u8fd8\u662f\u542b\u4e86\u4e00\u9897\u80fd\u89e3\u767e\u6bd2 \u7684\u7528\u5929\u5c71\u96ea\u83b2\u70ae\u5236\u7684\u201c\u78a7\u7075\u4e39\u201d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4ee5\u7edd\u9876\u8f7b\u529f\uff0c\u6377\u82e5\u733f\u7334\u7684\u4ece\u5c96\u58c1\u722c\u4e0b\u53bb\uff0c\u4e5f\u5dee\u4e0d\u591a\u7528\u4e86\u4e00\u67f1\u9999\u7684\u65f6\u523b\uff0c\u65b9\u624d\u811a\u8e0f \u5e73\u5730\u3002\u91d1\u9010\u6d41\u4e0d\u7531\u5f97\u6697\u6697\u5fc3\u60ca\uff1a\u201c\u4ece\u767e\u4e08\u60ac\u5d16\u4e4b\u4e0a\u8dcc\u4e0b\u6df1\u8c37\uff0c\u7634\u6c14\u53c8\u662f\u5982\u6b64\u6d53\u70c8\uff0c\u53ea\u6015\u4ed6\u4eec \u662f\u51f6\u591a\u5409\u5c11\u7684\u4e86\uff01\u201d \u3000\u3000\u4f46\u811a\u8e0f\u5e73\u5730\uff0c\u91d1\u9010\u6d41\u5374\u662f\u4e0d\u7531\u5f97\u5567\u5567\u79f0\u5947\u3002\u539f\u6765\u5730\u4e0a\u662f\u539a\u539a\u7684\u4e00\u5c42\u82b1\u74e3\uff0c\u5c31\u4f3c\u8d70\u5728\u8f6f\u7ef5 \u7ef5\u7684\u9e45\u7ed2\u94fa\u6210\u7684\u5730\u6be1\u4e0a\u4f3c\u7684\u3002\u8bd5\u4e00\u7528\u529b\uff0c\u8e0f\u4e0b\u53bb\u5374\u662f\u4e00\u56e2\u6b8b\u6ce5\uff0c\u91d1\u9010\u6d41\u5fc3\u60f3\uff1a\u201c\u843d\u7ea2\u672c\u662f\u65e0 \u60c5\u7269\uff0c\u5316\u4f5c\u6625\u6ce5\u66f4\u62a4\u82b1\u3002\u8fd9\u5927\u7ea6\u5c31\u662f\u843d\u82b1\u6240\u5316\u7684\u6625\u6ce5\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6717\u58f0\u53eb\u9053\uff1a\u201c\u5389\u5927\u54e5\uff01\u516c\u5b59\u59d1\u5a18\uff01\u201d\u542c\u4e0d\u5230\u56de\u7b54\u3002\u8e0f\u904d\u4e86\u6843\u82b1\u8c37\uff0c\u4e5f\u770b\u4e0d\u5230\u4e00\u4e2a \u4eba\u5f71\u3002 \u3000\u3000\u884c\u5230\u6843\u6797\u5c3d\u5934\uff0c\u53ea\u89c1\u4e00\u9053\u7011\u5e03\uff0c\u5374\u4f3c\u94f6\u6cb3\u5012\u62e7\uff0c\u4ece\u5ced\u58c1\u4e0a\u5954\u817e\u800c\u4e0b\uff0c\u53d1\u51fa\u8f70\u8f70\u9686\u9686\u7684\u58f0 \u54cd\uff0c\u524d\u9762\u5df2\u65e0\u53bb\u8def\u3002 \u3000\u3000\u91d1\u9010\u6d41\u597d\u751f\u5947\u602a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5373\u4f7f\u4ed6\u4eec\u6b7b\u4e86\uff0c\u4e5f\u5e94\u8be5\u9057\u4e0b\u9ab8\u9aa8\u3002\u201d\u4f46\u91d1\u9010\u6d41\u4e5f\u4e0d\u6562\u5b58 \u7740\u4fa5\u5e78\u7684\u5ff5\u5934\uff0c\u867d\u7136\u4e0d\u89c1\u9ab8\u9aa8\uff0c\u4e5f\u53ea\u597d\u5f53\u4f5c\u4ed6\u4eec\u6b7b\u4e86\u3002\u8981\u77e5\u4ed6\u4eec\u4e8c\u4eba\u8df3\u5d16\u4e4b\u65f6\uff0c\u8eab\u4e0a\u90fd\u662f\u53d7 \u4e86\u4f24\u7684\u3002\u5389\u5357\u661f\u53d7\u7684\u662f\u4fee\u7f57\u9634\u715e\u529f\u4e4b\u4f24\uff0c\u4f24\u5f97\u5c24\u5176\u4e25\u91cd\u3002\u4ece\u90a3\u4e48\u9ad8\u7684\u60ac\u5d16\u4e0a\u8df3\u4e0b\u53bb\uff0c\u4e0b\u9762\u53c8 \u6709\u7634\u6c14\uff0c\u5982\u4f55\u80fd\u591f\u6b7b\u91cc\u9003\u751f\uff1f\u8fd9\u6839\u672c\u5c31\u662f\u4e0d\u53ef\u60f3\u8c61\u4e4b\u4e8b\uff01 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u75db\u5982\u7ede\uff0c\u60f3\u8d77\u5728\u5c06\u519b\u5e9c\u6fc0\u6218\u4e4b\u9645\uff0c\u5389\u5357\u661f\u628a\u7384\u94c1\u5b9d\u5251\u629b\u7ed9\u4ed6\uff0c\u4ed6\u5f97\u4e86\u7384\u94c1\u5b9d \u5251\uff0c\u65b9\u80fd\u529b\u654c\u53f2\u767d\u90fd\u3001\u6587\u9053\u5e84\u4e24\u5927\u9ad8\u624b\uff0c\u81ea\u5df1\u8fd9\u6761\u6027\u547d\u53ef\u4ee5\u8bf4\u662f\u5389\u5357\u661f\u6551\u7684\u3002\u201c\u54b3\uff0c\u5389\u5927\u54e5 \u82e5\u4e0d\u662f\u628a\u7384\u94c1\u5b9d\u5251\u7ed9\u6211\uff0c\u9633\u6d69\u672a\u5fc5\u4f24\u5f97\u4e86\u4ed6\u3002\u4ed6\u4e3a\u6211\u800c\u6b7b\uff0c\u6211\u5374\u8fde\u4ed6\u7684\u9ab8\u9aa8\u90fd\u627e\u4e0d\u7740\u3002\u201d\u91d1 \u9010\u6d41\u8d8a\u60f3\u8d8a\u662f\u4f24\u5fc3\uff0c\u4ece\u8c37\u5e95\u722c\u4e0a\u5c71\u5934\uff0c\u6574\u6574\u82b1\u4e86\u4e00\u5929\u5de5\u592b\u3002 \u3000\u3000\u7b2c\u4e09\u5929\u56de\u5230\u90a3\u59d3\u5f20\u7684\u8001\u519c\u592b\u5bb6\u91cc\uff0c\u5bab\u79c9\u85e9\u5df2\u80fd\u6276\u7740\u5899\u58c1\u8bd5\u7740\u8d70\u52a8\u3002\u91d1\u9010\u6d41\u628a\u5728\u6843\u82b1\u8c37\u4e2d \u6240\u89c1\u7684\u60c5\u5f62\uff0c\u544a\u8bc9\u4e86\u5bab\u79c9\u85e9\u548c\u53f2\u7ea2\u82f1\uff0c\u5bab\u79c9\u85e9\u4e5f\u4ee5\u4e3a\u4ed6\u4eec\u662f\u5fc5\u6b7b\u65e0\u7591\uff0c\u60f3\u8d77\u81ea\u5df1\u6709\u8d1f\u5e2e\u4e3b\u6240 \u6258\uff0c\u4e0d\u7981\u9eef\u7136\u6cea\u4e0b\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u5446\u4e86\u534a\u664c\uff0c\u5374\u9053\uff1a\u201c\u4f60\u8bf4\u8c37\u5e95\u94fa\u6ee1\u843d\u82b1\uff0c\u6ce5\u571f\u53c8\u5f88\u677e\u8f6f\uff0c\u8fd9\u6837\u7684\u60c5\u5f62\uff0c\u4ece\u9ad8\u5904\u8dcc \u4e0b\uff0c\u8bf4\u4e0d\u5b9a\u4e5f\u672a\u5fc5\u5c31\u4e00\u5b9a\u4f1a\u6b7b\u3002\u201d \u3000\u3000\u5168\u9010\u6d41\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\u3002\u4e0d\u8fc7\uff0c\u4ed6\u4eec\u662f\u53d7\u4e86\u4f24\u7684\uff0c\u5982\u679c\u8fd8\u6d3b\u7740\u7684\u8bdd\uff0c\u90a3\u4ed6\u51b3\u4e0d\u4f1a\u9003\u5f97\u51fa \u6843\u82b1\u8c37\u7684\u3002\u5982\u4eca\u4e0d\u89c1\u4ed6\u4eec\uff0c\u53ea\u6015\u662f\u2026\u2026\u201d\u201c\u51f6\u591a\u5409\u5c11\u201d\u56db\u5b57\uff0c\u4e0d\u5fcd\u8bf4\u51fa\u53e3\u6765\u3002 \u3000\u3000\u5bab\u79c9\u85e9\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u662f\u8bbe\u6cd5\u66ff\u4ed6\u4eec\u4e8c\u4eba\u62a5\u4ec7\u5427\u3002\u6211\u8fd9\u4f24\u6050\u6015\u4e0d\u662f\u77ed\u671f\u95f4\u597d\u5f97\u4e86 \u7684\uff0c\u8fd9\u62a5\u4fe1\u4e4b\u4e8b\u2026\u2026\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u7ed9\u516c\u5b59\u5b8f\u8001\u80fd\u8f88\u62a5\u4fe1\uff0c\u6b64\u4e8b\u6211\u81ea\u662f\u4e49\u4e0d\u5bb9\u8f9e\u3002\u4e0d\u8fc7\uff0c\u4f60\u4e5f\u5e94\u8be5\u5148\u627e\u4e2a\u5730\u65b9 \u5b89\u987f\u4e0b\u6765\u624d\u884c\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8bf7\u90a3\u8001\u519c\u592b\u4ee3\u96c7\u4e00\u8f86\u9aa1\u8f66\uff0c\u5c06\u5bab\u79c9\u85e9\u62a4\u9001\u5230\u4e49\u519b\u7684\u57fa\u5730\u5927\u51c9\u5c71\u53bb\u3002\u4f17\u4eba\u5f97\u77e5\u5389\u5357 \u661f\u4e0e\u516c\u5b59\u71d5\u547d\u4e27\u6843\u82b1\u8c37\u7684\u6d88\u606f\uff0c\u65e0\u4e0d\u55df\u53f9\u3002 \u3000\u3000\u4e49\u519b\u653e\u5f03\u4e86\u897f\u660c\uff0c\u519b\u4e8b\u884c\u52a8\uff0c\u6682\u65f6\u505c\u4e0b\u3002\u91d1\u9010\u6d41\u4e0e\u7afa\u5c1a\u7236\u76f8\u7ea6\uff0c\u66ff\u5389\u5357\u661f\u62a5\u4e86\u4ec7\u4e4b\u540e\uff0c \u4fbf\u5373\u56de\u6765\uff0c\u800c\u62a5\u4ec7\u7684\u6b65\u9aa4\uff0c\u9996\u5148\u5c31\u662f\u5411\u516c\u5b59\u5b8f\u62a5\u4fe1\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5357\u5f52\u62a5\u4fe1\uff0c\u53f2\u7ea2\u82f1\u81ea\u7136\u548c\u4ed6\u540c\u884c\u3002\u674e\u6566\u592b\u5987\u5df2\u7ecf\u5e26\u9886\u516d\u5408\u5e2e\u5e2e\u4f17\u5148\u56de\u626c\u5dde\u53bb\u4e86\uff0c \u7559\u4e0b\u8bdd\u7ed9\u53f2\u7ea2\u82f1\uff0c\u8bf7\u5979\u5055\u540c\u91d1\u9010\u6d41\u56de\u626c\u5dde\u6b63\u5f0f\u63a5\u4efb\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\u4e4b\u4f4d\u3002 \u3000\u3000\u91d1\u3001\u53f2\u4e8c\u4eba\u79bb\u5f00\u5927\u539f\u5c71\uff0c\u6b64\u65f6\u5df1\u662f\u5389\u5357\u661f\u51fa\u4e8b\u4e4b\u540e\u7684\u4e00\u4e2a\u6708\u4e86\u3002\u91d1\u9010\u6d41\u7684\u8ba1\u5212\u662f\u5148\u89c1\u516c \u5b59\u5b8f\uff0c\u7136\u540e\u624d\u5f80\u626c\u5dde\uff0c\u53f2\u7ea2\u82f1\u5f53\u7136\u662f\u6beb\u65e0\u5f02\u8bae\u3002 \u3000\u3000\u4e00\u8def\u5e73\u5b89\u65e0\u4e8b\uff0c\u8fd9\u65e5\u5230\u4e86\u9655\u897f\u7684\u534e\u9634\u53bf\uff0c\u8457\u540d\u7684\u897f\u5cb3\u534e\u5c71\uff0c\u5c31\u5728\u534e\u9634\u53bf\u7684\u5357\u8fb9\u3002 \u3000\u3000\u6b64\u65f6\u6b63\u662f\u9633\u6625\u4e09\u6708\u7684\u5b63\u8282\uff0c\u6742\u82b1\u751f\u6811\uff0c\u7fa4\u8425\u4e71\u98de\u3002\u4e24\u4eba\u4ece\u534e\u5c71\u811a\u4e0b\u7ecf\u8fc7\uff0c\u7eb5\u89c2\u5c71\u666f\uff0c\u7cbe \u795e\u4e3a\u4e4b\u4e00\u7545\u3002\u91d1\u9010\u6d41\u8c08\u8d77\u6b66\u6797\u65e7\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u534e\u5c71\u4e0a\u4ece\u524d\u6709\u4f4d\u5929\u4e0b\u7b2c\u4e00\u7684\u540d\u533b\uff0c\u662f\u6211\u7239\u7239\u7684 \u8001\u670b\u53cb\uff0c\u53c8\u662f\u6211\u5927\u5e08\u5144\u7684\u4e49\u7236\u3002\u636e\u8bf4\u6b64\u4eba\u533b\u672f\u901a\u795e\uff0c\u4ec0\u4e48\u7591\u96be\u6742\u75c7\uff0c\u5230\u4e86\u4ed6\u7684\u624b\u91cc\u3002\u90fd\u80fd\u533b \u597d\u3002\u53ef\u60dc\u4ed6\u7684\u533b\u672f\uff0c\u73b0\u5728\u5df2\u7ecf\u5931\u4f20\u4e86\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u53ef\u662f\u534e\u5c71\u533b\u9690\u534e\u5929\u98ce\u4e48\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6b63\u662f\u3002\u4f60\u4e5f\u542c\u8fc7\u4ed6\u7684\u4e8b \u8ff9\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4ed6\u867d\u7136\u65e9\u901d\u4e16\uff0c\u4f46\u4ed6\u7684\u533b\u672f\u53ef\u5e76\u6ca1\u6709\u5931\u4f20\u554a\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ed6\u662f\u6709\u4e00\u4e2a\u5973\u513f\uff0c\u4f20\u4e86\u4ed6\u7684\u533b\u672f\u3002\u4f46\u6211\u542c\u5f97\u7239\u7239\u8bf4\uff0c\u8fd9\u4f4d\u534e\u5973\u4fa0\u5ac1 \u4e86\u897f\u57df\u4e00\u4e2a\u5c0f\u56fd\u7684\u56fd\u738b\uff0c\u505a\u4e86\u738b\u540e\uff0c\u5f53\u7136\u662f\u4e0d\u4f1a\u66ff\u4eba\u770b\u75c5\u7684\u4e86\u3002\u800c\u4e14\u534e\u8001\u7684\u533b\u672f\u6d41\u5165\u4e86\u897f \u57df\uff0c\u5bf9\u4e2d\u539f\u800c\u8a00\uff0c\u4e5f\u53ef\u4ee5\u8bf4\u662f\u5931\u4f20\u4e86\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4e0d\uff0c\u8fd9\u4f4d\u534e\u8001\u524d\u8f88\u8fd8\u6709\u4e00\u4f4d\u4f20\u4eba\uff0c\u4e0d\u8fc7\uff0c\u6216\u8bb8\u4e0d\u5982\u4ed6\u7684\u5973\u513f\u533b\u6728\u4e4b\u7cbe\u5999\u7f62 \u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u662f\u4e48\uff0c\u8fd9\u6211\u5012\u4e0d\u6653\u5f97\u4e86\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff0c\u201c\u8fd9\u4eba\u662f\u670d\u4f8d\u4ed6\u7684\u4e00\u4e2a\u9053\u7ae5\uff0c\u73b0\u5728\u6050\u6015\u4e5f\u6709\u4e94\u5341\u5f00\u5916\u7684\u5e74\u7eaa\u4e86\uff0c\u8fd9\u4f4d\u9053\u957f\u6cd5 \u540d\u6f31\u77f3\uff0c\u6211\u4eec\u5e2e\u4e2d\u7684\u9752\u5cf0\u9053\u4eba\u5341\u5e74\u524d\u4e2d\u4e86\u6dee\u9634\u53cc\u715e\u7684\u6bd2\u9556\uff0c\u5c31\u662f\u4ed6\u533b\u597d\u7684\u3002\u6240\u4ee5\u6211\u77e5\u9053\u4ed6\u7684 \u6765\u5386\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53ef\u80fd\u662f\u56e0\u4e3a\u7239\u7239\u8ba4\u8bc6\u534e\u8001\u524d\u8f88\u7684\u65f6\u5019\uff0c\u8fd9\u4f4d\u6f31\u77f3\u9053\u957f\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u6240\u4ee5\u7239\u7239 \u6ca1\u6709\u548c\u6211\u9053\u53ca\u3002\u4f46\u534e\u5c71\u533b\u9690\u4e0e\u6211\u7239\u7239\u6e0a\u6e90\u6781\u6df1\uff0c\u53c8\u662f\u6211\u5927\u5e08\u5144\u7684\u4e49\u7236\uff0c\u8fd9\u4f4d\u6f31\u77f3\u9053\u957f\u65e2\u7136\u662f \u534e\u5c71\u533b\u9690\u7684\u5f1f\u5b50\uff0c\u90a3\u4e5f\u5c31\u662f\u6211\u7684\u4e16\u4ea4\u957f\u8f88\u4e86\u3002\u53ef\u60dc\u54b1\u4eec\u8fd8\u8981\u8d76\u8def\uff0c\u5426\u5219\u5012\u662f\u5e94\u8be5\u4e0a\u5c71\u53bb\u62dc\u89c1 \u8fd9\u4f4d\u9053\u957f\u7684\u3002\u201d \u3000\u3000\u6b63\u5728\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u89c1\u4e00\u4e2a\u519c\u5987\uff0c\u54ed\u54ed\u557c\u557c\u7684\u4ece\u5c71\u4e0a\u4e0b\u6765\u3002\u53f2\u7ea2\u82f1\u4e0d\u89c9\u52a8\u4e86\u601c\u60af\u4e4b\u5fc3\uff0c\u4e0a \u524d\u529d\u6170\u5979\u9053\uff1a\u201c\u5927\u5a76\u4f55\u4e8b\u4f24\u5fc3\uff0c\u53ef\u4ee5\u8bf4\u7ed9\u6211\u542c\u4e48\uff1f\u5018\u82e5\u6709\u4ec0\u4e48\u4e3a\u96be\u4e4b\u5904\uff0c\u53ea\u8981\u662f\u6211\u529b\u6240\u80fd \u53ca\uff0c\u6211\u4e00\u5b9a\u7ed9\u4f60\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u90a3\u519c\u5987\u54ed\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\uff0c\u591a\u8c22\u4f60\u7684\u597d\u5fc3\u3002\u4f46\u4f60\u662f\u5e2e\u5fd9\u4e0d\u4e86\u7684\u3002\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u7a76\u7adf\u662f \u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u90a3\u519c\u5987\u8fb9\u54ed\u8fb9\u9053\uff1a\u201c\u6211\u7684\u547d\u597d\u82e6\u554a\uff01\u6211\u7684\u513f\u5b50\u60a3\u4e86\u6d9d\u75c5\uff0c\u597d\u4e0d\u5bb9\u6613\u6c42\u4eb2\u95ee\u53cb\uff0c\u501f\u4e86\u94b1\u8bf7 \u4e00\u4f4d\u57ce\u91cc\u7684\u5927\u592b\u6765\u770b\uff0c\u8fd9\u5927\u592b\u4e00\u770b\u5c31\u6447\u5934\uff0c\u8bf4\u662f\u7edd\u75c7\uff0c\u65e0\u6cd5\u53ef\u533b\u3002\u9664\u975e\u534e\u5c71\u4e0a\u7684\u4e00\u4f4d\u9053\u957f\u884c \u533b\uff0c\u6216\u8005\u8fd8\u6709\u5f97\u6551\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u63d2\u53e3\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\uff0c\u4f60\u662f\u6765\u534e\u5c71\u6c42\u533b\u7684\u4e86\uff1f\u600e\u4e48\uff0c\u8fd9\u4f4d\u9053\u957f\u4e0d\u80af\u533b\u4e48\uff1f\u4f46\u636e\u6211\u6240 \u77e5\uff0c\u8fd9\u4f4d\u9053\u957f\u4e00\u5411\u662f\u6148\u60b2\u4e3a\u6000\uff0c\u5bf9\u7a77\u82e6\u4eba\u5bb6\uff0c\u8fd8\u8d60\u533b\u8d60\u836f\u7684\u5440\uff01\u201d \u3000\u3000\u519c\u5987\u62bd\u62bd\u564e\u564e\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u8fd9\u4f4d\u9053\u957f\u5fc3\u5730\u6148\u60b2\uff0c\u53ea\u662f\u602a\u6211\uff0c\u602a\u6211\u8fd0\u6c14\u4e0d \u597d\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u4f4d\u9053\u957f\u51fa\u5916\u4e91\u6e38\u53bb\u4e86\uff1f\u201d \u3000\u3000\u519c\u5987\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u51fa\u5916\u4e91\u6e38\uff0c\u4e5f\u4e0d\u662f\u4ed6\u4e0d\u80af\u533b\uff0c\u662f\u8fd9\u4f4d\u9053\u957f\u6070\u5de7\u6628\u5929\u6b7b\u4e86\u3002\u201d\u8bf4\u81f3\u6b64 \u5904\uff0c\u4e0d\u89c9\u53c8\u54ed\u8d77\u6765\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u5927\u5403\u4e00\u60ca\u9053\uff1a\u201c\u8fd9\u4f4d\u9053\u957f\u6b7b\u4e86\uff1f\u5fc3\u60f3\uff1a\u201c\u6f31\u77f3\u9053\u4eba\u4e0d\u8fc7\u4e94\u5341\u591a\u5c81\u5e74\u7eaa\uff0c\u672c\u8eab\u65e2\u61c2 \u6b66\u529f\u53c8\u901a\u533b\u672f\uff0c\u4eba\u672a\u8870\u8001\uff0c\u600e\u7684\u5c31\u4f1a\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u90a3\u519c\u5987\u9053\uff1a\u201c\u4ed6\u7684\u68fa\u6750\u8fd8\u505c\u5728\u89c2\u4e2d\uff0c\u540a\u4e27\u7684\u4eba\u4e5f\u8fd8\u672a\u6563\u5462\uff0c\u54ea\u80fd\u6709\u5047\uff1f\u545c\u54c7\uff0c\u545c\u54c7\uff0c\u8fd9 \u4f4d\u9053\u957f\u6b7b\u4e86\uff0c\u6211\u7684\u513f\u5b50\u4e5f\u4fdd\u4e0d\u4f4f\u4e86\u6211\u3001\u6211\u4e5f\u4e0d\u60f3\u6d3b\u5566\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5927\u5a76\u83ab\u8981\u4f24\u5fc3\uff0c\u4f46\u75c5\u5e76\u975e\u7edd\u75c7\uff0c\u6211\u4e5f\u4f1a\u533b\u3002\u201d \u3000\u3000\u90a3\u519c\u5987\u767b\u65f6\u6536\u4e86\u773c\u6cea\uff0c\u534a\u4fe1\u4e0d\u4fe1\u7684\u795e\u6c14\uff0c\u7785\u7740\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u4f1a\u533b\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u9897\u836f\u4e38\u4f60\u62ff\u56de\u53bb\u7ed9\u4f60\u513f\u5b50\u670d\u4e0b\uff0c\u522b\u5916\u6211\u9001\u4f60\u5341\u4e24\u94f6\u5b50\uff0c\u591a\u4e70\u70b9\u6ecb\u8865\u7684\u4e1c \u897f\u7ed9\u4f60\u513f\u5b50\u8865\u8eab\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u9897\u836f\u4e38\u4e43\u662f\u4ece\u524d\u59ec\u6653\u98ce\u4ece\u5c11\u6797\u5bfa\u5077\u6765\u7684\uff0c\u201c\u5c0f\u8fd8\u4e39\u201d\uff0c\u201c\u5c0f\u8fd8\u4e39\u201d\u529f\u80fd\u57f9\u5143\u56fa \u672c\uff0c\u662f\u533b\u6cbb\u5185\u4f24\u6700\u597d\u7684\u7075\u836f\u3002\u59ec\u6653\u98ce\u6700\u75bc\u7231\u91d1\u9010\u6d41\uff0c\u6240\u4ee5\u5f53\u91d1\u9010\u6d41\u548c\u4ed6\u5206\u624b\u4e4b\u65f6\uff0c\u4ed6\u628a\u5077\u81ea \u5c11\u6797\u5bfa\u7684\u201c\u5c0f\u8fd8\u4e39\u201d\u4e00\u53e4\u8111\u513f\u90fd\u8d60\u7ed9\u4e86\u91d1\u9010\u6d41\u3002 \u3000\u3000\u91d1\u9010\u6d41\u867d\u7136\u4e0d\u61c2\u533b\u672f\uff0c\u4f46\u5fc3\u60f3\u4fd7\u8bed\u8bf4\uff1a\u201c\u4e94\u75e8\u4e03\u4f24\u201d\uff0c\u75e8\u75c5\u4e5f\u662f\u5185\u4f24\u7684\u4e00\u79cd\uff0c\u201c\u5c0f\u8fd8 \u4e39\u201d\u60f3\u5fc5\u80fd\u591f\u6cbb\u597d\u3002\u57ce\u91cc\u7684\u90a3\u4e2a\u5927\u592b\u4e0d\u80af\u533b\uff0c\u4e0d\u8fc7\u662f\u5acc\u4e00\u4e2a\u519c\u5bb6\u4ed8\u4e0d\u8d77\u53ef\u89c2\u7684\u8bca\u91d1\u800c\u5df2\u3002 \u3000\u3000\u8fd9\u519c\u5987\u89c1\u91d1\u9010\u6d41\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u96be\u514d\u534a\u4fe1\u534a\u7591\uff0c\u4f46\u4eba\u5bb6\u65e2\u662f\u4e00\u7247\u597d\u5fc3\uff0c\u81ea\u5df1\u7684\u513f\u5b50\u53c8\u522b\u65e0\u6307 \u671b\uff0c\u53ea\u597d\u62b1\u7740\u59d1\u4e14\u4e00\u8bd5\u7684\u5fc3\u7406\uff0c\u5bf9\u91d1\u9010\u6d41\u5343\u591a\u8c22\u4e07\u591a\u8c22\uff0c\u62ff\u4e86\u836f\u4e38\u548c\u94f6\u5b50\u56de\u5bb6\u3002 \u3000\u3000\u8fd9\u8863\u5987\u8d70\u540e\uff0c\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u9010\u6d41\uff0c\u6211\u89c9\u5f97\u8fd9\u4e8b\u60c5\u6709\u70b9\u5947\u602a\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6f31\u77f3\u9053\u4eba\u4e4b\u6b7b\u5b9a\u6709\u8e4a\u8df7\u3002\u54b1\u4eec\u867d\u7136\u662f\u8981\u8d76\u8def\uff0c\u4f46\u4e00\u4f4d\u4e16\u4ea4\u524d\u8f88\u6b7b \u4e86\uff0c\u8bba\u7406\u54b1\u4eec\u4e5f\u8be5\u53bb\u7075\u524d\u884c\u4e2a\u793c\u3002\u201d \u3000\u3000\u4e8e\u662f\u4e24\u4eba\u4e00\u540c\u4e0a\u5c71\uff0c\u5230\u4e86\u534a\u5c71\uff0c\u53ea\u89c1\u4e00\u5ea7\u9053\u89c2\uff0c\u95e8\u53e3\u6302\u7740\u84dd\u706f\u7b3c\u3002\u91cc\u9762\u9690\u9690\u6709\u5435\u95f9\u7684\u58f0 \u97f3\u4f20\u51fa\u3002\u539f\u6765\u534e\u5929\u98ce\u672c\u662f\u4f4f\u5728\u534e\u51fa\u7edd\u9876\u7684\uff0c\u5230\u4e86\u6f31\u77f3\u9053\u4eba\uff0c\u4e3a\u4e86\u65b9\u4fbf\u4e61\u6c11\u524d\u6765\u6c42\u533b\uff0c\u5728\u534a\u5c71 \u5efa\u4e86\u4e00\u5ea7\u9053\u89c2\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u60f3\u5fc5\u5c31\u662f\u6f31\u77f3\u9053\u957f\u7684\u9053\u89c2\u4e86\u3002\u5947\u602a\uff0c\u4ed6\u4eba\u5df2\u6b7b\u4e86\uff0c\u5374\u4e3a\u4f55\u6709\u4eba\u5728\u4ed6\u89c2\u4e2d \u5435\u67b6\uff1f\u201d \u3000\u3000\u4e24\u4eba\u8d70\u8fdb\u9053\u89c2\uff0c\u53ea\u542c\u5f97\u6709\u4e2a\u4eba\u7c97\u58f0\u56b7\u9053\uff1a\u201c\u6211\u4e0d\u76f8\u4fe1\uff0c\u628a\u68fa\u6750\u6253\u5f00\u6765\u7ed9\u6211\u770b\uff01\u201d\u53e6\u4e00\u4e2a \u58f0\u97f3\u9053\uff1a\u201c\u5bb6\u5e08\u59d4\u5b9e\u662f\u5df2\u7ecf\u6b7b\u4e86\uff0c\u68fa\u6750\u4e5f\u90fd\u5df2\u9489\u4e0a\u4e86\u76d6\u4e86\u3002\u201d\u90a3\u4eba\u53eb\u9053\uff1a\u201c\u5c01\u4e86\u4e5f\u8981\u6253 \u5f00\uff01\u201d\u968f\u7684\u542c\u5230\u201c\u84ec\u201d\uff0c\u7684\u4e00\u58f0\uff0c\u91d1\u3001\u53f2\u4e8c\u4eba\u8fdb\u5165\u7075\u5802\u4e4b\u65f6\uff0c\u6b63\u597d\u89c1\u7740\u90a3\u4eba\u63ed\u5f00\u4e86\u3002 \u3000\u3000\u90a3\u4eba\u6253\u5f00\u4e86\u68fa\u76d6\uff0c\u4e24\u65c1\u7684\u51e0\u4e2a\u5927\u6c49\uff0c\u9f50\u90fd\u62e5\u4e0a\u53bb\u770b\u3002\u201c\u54a6\uff0c\u5f53\u771f\u662f\u6f31\u77f3\u9053\u4eba\uff01\u201d\u201c\u6211\u8bf4 \u5bb6\u5e08\u6b7b\u4e86\uff0c\u4f60\u4eec\u4e0d\u80af\u76f8\u4fe1\uff0c\u504f\u8981\u6253\u5f00\u4ed6\u7684\u68fa\u6750\u3002\u54fc\uff0c\u73b0\u5728\u4f60\u4eec\u76f8\u4fe1\u4e86\u5427\uff1f\u201d\u201c\u5947\u602a\uff0c\u597d\u50cf\u662f \u4e2d\u6bd2\u6b7b\u7684\uff01\u201d\u201c\u4e0d\u7528\u8bf4\u4e86\uff0c\u4e00\u5b9a\u53c8\u662f\u5929\u9b54\u6559\u4e0b\u7684\u6bd2\u624b\uff01\u201d\u7fa4\u8c6a\u7684\u60ca\u8be7\u3001\u5931\u671b\u4e0e\u90a3\u5c0f\u9053\u58eb\u7684\u57cb \u6028\u3001\u4f24\u5fc3\uff0c\u5728\u63ed\u5f00\u68fa\u6750\u7684\u8fd9\u4e00\u77ac\u95f4\u90fd\u56b7\u4e86\u51fa\u6765\uff0c\u4e71\u54c4\u54c4\u95f9\u6210\u4e00\u7247\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4f55\u4ee5\u4ed6\u4eec\u6000\u7591\u662f\u5929\u9b54\u6559\u4e0b\u7684\u6bd2\u624b\uff1f\u201d\u5f53\u4e0b\u8fde\u5fd9\u6324\u4e86\u8fdb \u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u8ba9\u6211\u4e5f\u6765\u770b\u770b\u3002\u201d \u3000\u3000\u53ea\u89c1\u68fa\u6751\u91cc\u8eba\u7740\u7684\u5c38\u4f53\uff0c\u7709\u5fc3\u7684\u4e00\u56e2\u9ed1\u6c14\u4ecd\u7136\u6d53\u5f97\u50cf\u58a8\u4e00\u822c\uff0c\u9f3b\u5b54\u4e5f\u8fd8\u5728\u6d41\u51fa\u4e00\u6ef4\u6ef4\u7684 \u8840\u6c34\u3002\u91d1\u9010\u6d41\u66fe\u7ecf\u89c1\u8bc6\u8fc7\u5929\u9b54\u6559\u6bd2\u9488\u7684\u5389\u5bb3\uff0c\u770b\u4e86\u8fd9\u4e2a\u60c5\u5f62\uff0c\u66f4\u662f\u60ca\u7591\u4e0d\u5b9a\u3002\u60f3\u9053\uff1a\u201c\u770b\u6765 \u5012\u662f\u6709\u70b9\u50cf\u4e2d\u4e86\u5929\u9b54\u6559\u7684\u6bd2\u9488\uff0c\u4f46\u90a3\u59d3\u8d3a\u7684\u8001\u5996\u5a46\u5df2\u7ecf\u6b7b\u4e86\uff0c\u8fd8\u6709\u8c01\u4f1a\u7528\u8fd9\u79cd\u6bd2\u9488\u5bb3\u4eba\u5462\uff1f \u674e\u6566\u5f53\u7136\u662f\u51b3\u8ba1\u4e0d\u4f1a\u7684\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u7ed9\u91d1\u9010\u6d41\u6324\u5f00\u7684\u4eba\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u662f\u54ea\u6761\u7ebf\u7684\uff0c\u5e72\u5417\u4e71\u51b2\u4e71\u649e\uff1f\u201d\u63ed\u5f00\u68fa\u6750\u76d6 \u7684\u90a3\u4eba\u56de\u8fc7\u5934\u6765\uff0c\u5ffd\u5730\u5931\u58f0\u53eb\u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u4f60\u4e5f\u6765\u4e86\uff01\u8fd9\u4f4d\u670b\u53cb\u662f\u2026\u2026\u201d\u53f2\u7ea2\u82f1\u6014\u4e86\u4e00 \u6014\uff0c\u84e6\u5730\u60f3\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u957f\u9cb8\u5e2e\u7684\u5b59\u9999\u4e3b\u5417\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u59d1\u5a18\u597d\u8bb0\u6027\uff0c\u6211\u6b63\u662f\u957f \u9cb8\u5e2e\u7684\u5b59\u767e\u5bff\u3002\u5404\u4f4d\u522b\u95f9\uff0c\u8fd9\u4f4d\u59d1\u5a18\u662f\u516d\u5408\u5e2e\u53f2\u5e2e\u4e3b\u7684\u59b9\u59b9\u3002\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u8fd9\u4f4d\u91d1\u5c11\u4fa0\u662f \u6c5f\u6d77\u5929\u7684\u5e08\u5f1f\u91d1\u9010\u6d41\u3002\u201d \u3000\u3000\u539f\u6765\u957f\u9cb8\u5e2e\u4e43\u662f\u9ec4\u6cb3\u4e24\u5cb8\u7684\u4e00\u4e2a\u5c0f\u5e2e\u4f1a\uff0c\u5e2e\u4e3b\u5b59\u767e\u7984\u6b63\u5904\u8fd9\u4e2a\u5b59\u767e\u5bff\u7684\u54e5\u54e5\u3002\u957f\u9cb8\u5e2e\u662f \u516d\u5408\u5e2e\u7684\u201c\u5c5e\u5e2e\u201d\uff0c\u6bcf\u4e00\u5e74\u90fd\u8981\u5411\u516d\u5408\u5e2e\u8ff8\u8d21\u7684\u3002\u5b59\u767e\u5bff\u66fe\u7ecf\u5230\u8fc7\u516d\u5408\u5e2e\u66ff\u4ed6\u54e5\u54e5\u9001\u793c\uff0c\u662f \u4ee5\u8ba4\u5f97\u53f2\u7ea2\u82f1\u3002\u4f46\u4ed6\u5374\u4e0d\u77e5\u9053\u53f2\u767d\u90fd\u5df2\u7ecf\u5728\u897f\u660c\u6b7b\u4e86\uff0c\u73b0\u5728\u662f\u53f2\u7ea2\u82f1\u63a5\u4efb\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e0e\u53f2\u767d\u90fd\u7684\u540d\u5934\uff0c\u6c5f\u6e56\u4e2d\u4eba\u8c01\u4e2a\u4e0d\u77e5\uff0c\u54ea\u4e2a\u4e0d\u6653\uff1f\u8fd9\u4e9b\u4eba\u542c\u5f97\u4ed6\u4eec\u4e8c\u4eba\u4e00\u4e2a\u662f\u6c5f \u6d77\u5929\u7684\u5e08\u5f1f\uff0c\u4e00\u4e2a\u662f\u53f2\u767d\u90fd\u7684\u59b9\u59b9\uff0c\u90fd\u662f\u53c8\u60ca\u53c8\u559c\uff0c\u8fde\u5fd9\u8d54\u7f6a\u3002 \u3000\u3000\u5b59\u767e\u5bff\u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u600e\u7684\u4f60\u4e5f\u6765\u627e\u6f31\u77f3\u9053\u4eba\uff0c\u96be\u9053\u5929\u9b54\u6559\u7adf\u6562\u5728\u592a\u5c81\u5934\u4e0a\u52a8\u571f\uff0c\u6b3a\u8d1f \u5230\u4f60\u4eec\u516d\u5408\u5e2e\u7684\u5934\u4e0a\u4e48\u3002\u201d\u53c8\u9053\uff1a\u201c\u91d1\u5c11\u4fa0\uff0c\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6050\u6015\u4e5f\u53ea\u6709\u4ee4\u5e08\u5144\u6c5f \u5927\u4fa0\u624d\u80fd\u7ed9\u6211\u4eec\u4e3b\u6301\u516c\u9053\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u60c5\uff0c\u6b63\u8981\u8bf7\u6559\u3002\u201d \u3000\u3000\u5b59\u767e\u5bff\u76d6\u4e0a\u68fa\u6750\uff0c\u5411\u90a3\u5c0f\u9053\u58eb\u8d54\u4e86\u7f6a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4e9b\u4eba\u90fd\u662f\u6765\u627e\u6f31\u77f3\u9053\u957f\u6cbb\u75c5\u7684\uff0c \u60f3\u4e0d\u5230\u4ed6\u4e5f\u906d\u4e86\u6bd2\u624b\uff0c\u4e00\u65f6\u7740\u6025\uff0c\u4ee5\u81f3\u5931\u4eea\uff0c\u6559\u91d1\u5c11\u4fa0\u89c1\u7b11\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u770b\u770b\u8fd9\u4e9b\u4eba\uff0c\u5374\u4e0d\u4f3c\u6709\u75c5\u7684\u6837\u5b50\uff0c\u6b63\u8981\u52a8\u95ee\uff0c\u5b59\u767e\u5bff\u5df2\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u6211\u4eec\u81ea \u5df1\u770b\u75c5\u3002\u6211\u4eec\u662f\u5206\u5c5e\u4e94\u4e2a\u5e2e\u4f1a\u7684\uff0c\u4e00\u662f\u6211\u4eec\u7684\u5e2e\u4e3b\u90fd\u906d\u4e86\u5929\u9b54\u6559\u7684\u4e0b\u6bd2\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5929\u9b54\u6559\u4e0d\u662f\u65e9\u5df2\u5728\u4e8c\u5341\u5e74\u524d\u5c31\u5df2\u70df\u6d88\u4e91\u6563\u4e86\u7684\u4e48\uff0c\u600e\u7684\u73b0\u5728\u53c8\u6b7b\u7070\u590d\u71c3\uff0c \u95f9\u5f97\u5982\u6b64\u7316\u7357\uff1f\u8fd8\u6709\uff0c\u4f60\u4eec\u53c8\u600e\u77e5\u9053\u51c6\u662f\u5929\u9b54\u6559\u4e0b\u7684\u6bd2\u5462\uff1f\u201d \u3000\u3000\u5b59\u767e\u5bff\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5929\u9b54\u6559\u5728\u4e8c\u5341\u5e74\u524d\u662f\u542c\u4ece\u4ee4\u5c0a\u7684\u529d\u8c15\uff0c\u89e3\u6563\u4e86\u7684\u3002\u4f46\u73b0\u5728\u51fa\u4e86\u4e00\u4e2a \u65b0\u6559\u4e3b\uff0c\u5929\u9b54\u6559\u53c8\u5df2\u91cd\u5f00\u9999\u5802\u4e86\u3002\u6211\u4eec\u8fd9\u51e0\u4e2a\u5e2e\u4f1a\u4e0e\u5929\u9b54\u6559\u7ed3\u6028\uff0c\u5c31\u662f\u56e0\u4e3a\u6b64\u4e8b\u800c\u8d77\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5929\u9b54\u6559\u7684\u65b0\u6559\u4e3b\u662f\u8c01\uff1f\u201d \u3000\u3000\u5b59\u767e\u5bff\u9053\uff1a\u201c\u542c\u8bf4\u662f\u5929\u9b54\u6559\u7956\u5e08\u5389\u80dc\u7537\u7684\u4f84\u5b59\uff0c\u540d\u5b57\u53eb\u5389\u5357\u661f\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u60ca\u9053\uff1a\u201c\u600e\u4e48\u662f\u5389\u5357\u661f\uff1f\u4f60\u4eec\u6709\u6ca1\u6709\u5f04\u9519\uff1f\u201d \u3000\u3000\u5b59\u767e\u5bff\u9053\uff1a\u201c\u4ed6\u91cd\u5f00\u9999\u5802\u4e4b\u65f6\uff0c\u66fe\u6709\u5e16\u5b50\u53eb\u6211\u4eec\u524d\u53bb\u89c2\u793c\uff0c\u5e16\u4e0a\u7684\u5177\u540d\u2018\u5389\u5357\u661f\u2019\u4e09\u4e2a \u5b57\u5199\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u600e\u4f1a\u5f04\u9519\uff1f\u4e0d\u8fc7\uff0c\u4ed6\u7684\u8eab\u4efd\uff0c\u5374\u662f\u6211\u4eec\u6253\u542c\u5230\u7684\uff0c\u60f3\u5fc5\u4e5f\u4e0d\u4f1a\u662f\u5047\u3002\u5426\u5219 \u5929\u9b54\u6559\u7684\u65e7\u5c5e\u600e\u80af\u5949\u8fd9\u6837\u7684\u4e00\u4e2a\u5c0f\u4f19\u5b50\u505a\u6559\u4e3b\uff1f\u91d1\u5c11\u4fa0\uff0c\u4f60\u53ef\u662f\u8ba4\u8bc6\u6b64\u4eba\uff1f\u201d \u3000\u3000\u8fd9\u73ed\u4eba\u6240\u5c5e\u7684\u5e2e\u4f1a\u90fd\u4e0d\u662f\u5149\u660e\u6b63\u5927\u7684\u5e2e\u4f1a\uff0c\u91d1\u9010\u6d41\u4e0d\u613f\u548c\u8fd9\u73b0\u4eba\u8bf4\u660e\u4ed6\u4e0e\u5389\u5357\u661f\u7684\u5173 \u7cfb\uff0c\u5f53\u4e0b\u542b\u7cca\u7b54\u9053\uff1a\u201c\u6211\u77e5\u9053\u6709\u8fd9\u4e2a\u4eba\uff0c\u4ed6\u66fe\u7ecf\u5728\u4eac\u4e2d\u95f9\u8fc7\u8428\u798f\u9f0e\u7684\u6625\u5802\uff0c\u5f53\u65f6\u6211\u4e5f\u5728\u573a \u7684\u3002\u6309\u8bf4\u4ed6\u4e0d\u4f3c\u662f\u4e00\u4e2a\u6697\u4e2d\u4e0b\u6bd2\u6697\u7b97\u4eba\u5bb6\u7684\u5351\u9119\u4e4b\u5f92\u3002\u201d \u3000\u3000\u5b59\u767e\u5bff\u6124\u7136\u8bf4\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u4f1a\u662f\u4ed6\u4eb2\u624b\u4e0b\u6bd2\uff0c\u4f46\u4ed6\u7684\u624b\u4e0b\uff0c\u82e5\u4e0d\u662f\u5949\u4e86\u4ed6\u7684\u610f\u65e8\uff0c\u60f3\u6765 \u4e5f\u4e0d\u6562\u5982\u6b64\u80e1\u4f5c\u975e\u4e3a\u3002\u800c\u4e14\u6211\u4eec\u90fd\u662f\u4e8b\u5148\u53d7\u8fc7\u8b66\u544a\u7684\uff0c\u8b66\u544a\u6211\u4eec\u7684\u4eba\uff0c\u6b63\u662f\u4ed6\u6240\u6d3e\u6765\u7684\u4f7f \u8005\u3002\u91d1\u5c11\u4fa0\uff0c\u4fd7\u8bdd\u8bf4\u5f97\u597d\uff0c\u77e5\u4eba\u77e5\u9762\u4e0d\u77e5\u5fc3\uff0c\u5373\u4f7f\u8fd9\u5389\u5357\u661f\u662f\u4f60\u7684\u670b\u53cb\uff0c\u4ed6\u5982\u4eca\u505a\u4e86\u90aa\u6559\u7684 \u6559\u4e3b\uff0c\u4f60\u4e5f\u4e0d\u80fd\u592a\u76f8\u4fe1\u4ed6\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6025\u4e8e\u77e5\u9053\u771f\u76f8\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u4e5f\u8bf4\u5f97\u662f\u3002\u4e0d\u8fc7\uff0c\u4f60\u4eec\u5230\u5e95\u662f\u600e\u6837\u548c\u5929\u9b54\u6559\u7ed3\u6028 \u7684\uff0c\u53ef\u5426\u8bf7\u9053\u5176\u8be6\uff1f\u201d \u3000\u3000\u5b59\u767e\u5bff\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u628a\u6211\u4eec\u957f\u9cb8\u5e2e\u7684\u906d\u9047\u8bf4\u7ed9\u91d1\u5c11\u4fa0\u542c\u5427\u3002\u4ed6\u4eec\u7684\u906d\u9047\u5927\u6982\u4e5f\u662f\u548c \u6211\u4eec\u957f\u9cb8\u5e2e\u4e00\u6837\u3002 \u3000\u3000\u8fd9\u662f\u4e0a\u4e2a\u6708\u521d\u7684\u4e8b\u60c5\uff0c\u6709\u4e00\u5929\u6765\u4e86\u4e00\u4e2a\u81ea\u79f0\u662f\u5929\u9b54\u6559\u4f7f\u8005\u7684\u4eba\uff0c\u4ed6\u8bf4\u5929\u9b54\u6559\u5373\u5c06\u91cd\u5f00\u9999 \u5802\uff0c\u5230\u65f6\u8bf7\u4f60\u4eec\u7684\u5e2e\u4e3b\u524d\u53bb\u89c2\u793c\u3002\u8fd8\u8bf4\uff1a\u4f60\u4eec\u957f\u9cb8\u5e2e\u4ee5\u524d\u662f\u670d\u4ece\u5929\u9b54\u6559\u7684\uff0c\u73b0\u5728\u6709\u4e86\u65b0\u6559\u4e3b \u4e86\uff0c\u4f60\u4eec\u5e94\u8be5\u7528\u4ec0\u4e48\u793c\u6570\u524d\u5f80\u62dc\u8c12\uff0c\u4f60\u4eec\u81ea\u5df1\u659f\u914c\u5427\u3002\u8a00\u4e0b\u4e4b\u610f\uff0c\u5929\u9b54\u6559\u7adf\u662f\u85c9\u9080\u8bf7\u89c2\u793c\u4e3a \u540d\uff0c\u8981\u6211\u4eec\u91cd\u5f52\u5b83\u7684\u7edf\u5c5e\u3002 \u3000\u3000\u201c\u53f2\u59d1\u5a18\uff0c\u6211\u4eec\u957f\u9cb8\u5e2e\u8fd9\u5341\u591a\u5e74\u6765\u662f\u5df2\u7ecf\u5f52\u5c5e\u4e86\u8d35\u5e2e\u4e86\uff0c\u600e\u80fd\u518d\u542c\u5929\u9b54\u6559\u7684\u53f7\u4ee4\u3002\u56e0 \u6b64\uff0c\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u6211\u4eec\u7d22\u6027\u8fde\u89c2\u793c\u4e5f\u4e0d\u53bb\u4e86\u3002\u201d\u4ed6\u8bf4\u8fd9\u8bdd\uff0c\u5f53\u7136\u662f\u60f3\u5f97\u53f2\u7ea2\u82f1\u8d5e\u4ed6\u4e00\u53e5\u201c\u5fe0 \u5fc3\u201d\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u5fc3\u9053\uff1a\u201c\u53ef\u60dc\u4ed6\u6ca1\u6709\u524d\u53bb\u89c2\u793c\uff0c\u5426\u5219\u5c31\u53ef\u4ee5\u77e5\u9053\u662f\u771f\u662f\u5047\u4e86\u3002\u4f46\u76f8\u4fe1\u8fd9\u4e2a\u5929\u9b54\u6559 \u7684\u65b0\u6559\u4e3b\u4e00\u8d70\u4e0d\u4f1a\u662f\u5389\u5927\u54e5\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u662f\u4e00\u5e2e\u4e4b\u4e3b\uff0c\u5e2e\u4f1a\u7eb5\u6709\u5927\u5c0f\u4e4b\u5206\uff0c\u5207\u65e0 \u5c0a\u5351\u4e4b\u522b\uff0c\u4ed6\u65e2\u7136\u4ed7\u52bf\u6b3a\u4eba\uff0c\u90a3\u4e5f\u5c31\u602a\u4e0d\u5f97\u4f60\u4e0d\u7ed9\u4ed6\u9762\u5b50\u4e86\u3002\u201d \u3000\u3000\u5b59\u767e\u5bff\u9053\uff1a\u201c\u59d1\u5a18\u4f60\u662f\u660e\u7406\u7684\u4eba\uff0c\u8bf4\u51fa\u7684\u8bdd\uff0c\u4ee4\u4eba\u5fc3\u670d\u3002\u53ef\u662f\u5389\u5357\u661f\u8fd9\u5c0f\u5b50\u5374\u662f\u4e1d\u6beb\u4e0d \u8bb2\u9053\u7406\uff0c\u72d7\u5634\u91cc\u4e0d\u5410\u4eba\u8a00\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5148\u522b\u9a82\u4eba\uff0c\u4ed6\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u5b59\u767e\u5bff\u9053\uff1a\u201c\u4ed6\u6d3e\u6765\u7684\u4f7f\u8005\u8bf4\u9053\uff1a\u5389\u5e2e\u4e3b\u6709\u8a00\u5728\u5148\uff0c\u8bf7\u4f60\u4eec\u89c2\u793c\u662f\u7ed9\u4f60\u4eec\u7684\u9762\u5b50\uff0c\u4f60\u4eec \u613f\u610f\u559d\u656c\u9152\u6700\u597d\uff0c\u613f\u610f\u559d\u7f5a\u9152\u4e5f\u884c\u3002\u53bb\u4e0d\u53bb\u968f\u4f60\u4eec\u7684\u4fbf\uff0c\u4f60\u4eec\u81ea\u5df1\u4ed4\u7ec6\u7422\u78e8\u5427\u3002\u201d \u3000\u3000\u201c\u6211\u4eec\u90fd\u662f\u5728\u5200\u5c16\u4e0a\u8ba8\u751f\u6d3b\u7684\u4eba\uff0c\u4e0d\u81f3\u4e8e\u5c31\u6015\u4e86\u4ed6\u7684\u606b\u5413\u3002\u4e0d\u8fc7\u6211\u4eec\u5bf9\u5929\u9b54\u6559\u4ea6\u4e0d\u6562\u5c0f \u89d1\uff0c\u5c0f\u5fc3\u6212\u5907\uff0c\u81ea\u4e5f\u4e0d\u5728\u8bdd\u4e0b\u3002\u4e0d\u6599\u4ed6\u679c\u7136\u4e0d\u662f\u865a\u58f0\u606b\u5413\uff0c\u6211\u4eec\u867d\u7136\u6709\u4e86\u4e25\u5bc6\u7684\u6212\u5907\uff0c\u5e2e\u4e3b \u8fd8\u662f\u906d\u4e86\u5b83\u9b54\u6559\u5996\u5f92\u7684\u6bd2\u624b\u3002 \u3000\u3000\u201c\u90a3\u65e5\u6211\u4eec\u7684\u5e2e\u4e3b\u4ece\u5916\u5730\u505a\u4e86\u4e00\u4ef6\u6848\u5b50\u56de\u6765\uff0c\u5728\u8def\u8fb9\u7684\u8336\u4ead\u559d\u4e86\u4e00\u7897\u8336\uff0c\u8336\u4ead\u7684\u8001\u5934\u513f \u662f\u548c\u6211\u4eec\u5341\u5206\u719f\u7a14\u7684\u4eba\uff0c\u6709\u65f6\u8fd8\u66ff\u6211\u4eec\u505a\u773c\u7ebf\u7684\uff0c\u5e2e\u4e3b\u5bf9\u4ed6\u5f53\u7136\u662f\u6ca1\u6709\u7591\u5fc3\u3002\u5c82\u77e5\u559d\u4e86\u8fd9\u7897 \u8336\u4e4b\u540e\uff0c\u53ea\u4e0d\u8fc7\u8d70\u4e86\u4e00\u7a0b\uff0c\u5e2e\u4e3b\u4f53\u5185\u5982\u711a\uff0c\u8d8a\u6765\u8d8a\u89c9\u4e0d\u5bf9\uff0c\u8fd9\u624d\u77e5\u9053\u662f\u4e2d\u4e86\u6bd2\u3002\u597d\u5728\u6709\u4e24\u4e2a \u5f1f\u5144\u8ddf\u7740\u5e2e\u4e3b\uff0c\u4e00\u4e2a\u9001\u5e2e\u4e3b\u56de\u5bb6\uff0c\u4e00\u4e2a\u4fbf\u53bb\u627e\u90a3\u8001\u5934\u7b97\u5e10\u3002 \u3000\u3000\u201c\u56de\u5230\u90a3\u5ea7\u8336\u4ead\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u8001\u5934\u513f\u5df2\u662f\u7ed9\u4eba\u6740\u4e86\uff0c\u5c38\u4f53\u65c1\u8fb9\u7559\u6709\u4e00\u5c01\u4fe1\uff0c\u4fe1\u4e2d\u53ea\u662f\u5be5 \u5be5\u6570\u5b57\uff1a\u2018\u6b32\u6c42\u6d3b\u547d\uff0c\u901f\u5230\u5929\u9b54\u6559\u6c42\u533b\u3002\u2019\u7528\u610f\u4e0d\u95ee\u53ef\u77e5\uff0c\u662f\u8981\u631f\u6211\u4eec\u957f\u9cb8\u5e2e\u5c48\u670d\u4e8e\u5929\u9b54\u6559 \u7684\u4e86\u3002\u6211\u4eec\u4e0d\u613f\u5411\u4ed6\u5c48\u670d\uff0c\u8fd9\u624d\u5230\u6b64\u6c42\u6cbb\u4e8e\u6f31\u77f3\u9053\u957f\u7684\uff0c\u5509\uff0c\u4e0d\u6599\u5982\u4eca\u6f31\u77f3\u9053\u957f\u4e5f\u7ed9\u4ed6\u4eec\u5bb3 \u6b7b\u4e86\u3002\u201d \u3000\u3000\u5b59\u767e\u5bff\u8bf4\u5b8c\u4e4b\u540e\uff0c\u5176\u4ed6\u5404\u4eba\u4f9d\u6b21\u8bf4\u51fa\u4ed6\u4eec\u5e2e\u4e2d\u7684\u906d\u9047\uff0c\u679c\u7136\u7941\u957f\u9cb8\u5e2e\u906d\u53d7\u7684\u5927\u540c\u5c0f\u5f02\u3002 \u3000\u3000\u5b59\u767e\u5bff\u9053\uff1a\u201c\u91d1\u5c11\u4fa0\uff0c\u4f60\u77a7\u8fd9\u59d3\u5389\u7684\u5c0f\u5b50\u624b\u6bb5\u662f\u4f55\u7b49\u72e0\u6bd2\uff0c\u4ed6\u65b0\u4efb\u5e2e\u4e3b\uff0c\u4e3a\u8981\u626c\u5a01\u7acb \u4e07\uff0c\u7adf\u4e0d\u60dc\u6b8b\u5bb3\u6c5f\u6e56\u540c\u9053\uff0c\u8fd8\u5bb3\u6b7b\u4e86\u4e00\u4e2a\u65e0\u8f9c\u7684\u6f31\u77f3\u9053\u4eba\u3002\u4f7f\u6211\u4eec\u6c42\u6551\u65e0\u95e8\uff0c\u53ea\u80fd\u5411\u4ed6\u5c48 \u670d\u3002\u6211\u4eec\u81ea\u95ee\u6597\u4e0d\u8fc7\u5929\u9b54\u6559\uff0c\u5982\u4eca\u53ea\u6709\u8bf7\u4f60\u4eec\u4e24\u4f4d\u5e2e\u5fd9\uff0c\u4ee3\u6211\u4eec\u6073\u6c42\u6c5f\u5927\u4fa0\u548c\u53f2\u5e2e\u4e3b\u51fa\u5934\u4e3b \u6301\u516c\u9053\u4e86\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u6211\u4e00\u5b9a\u7ed9\u4f60\u4eec\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\u3002\u4e0d\u8fc7\uff0c\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b \u53f2\u767d\u90fd\u5219\u662f\u65e9\u5df2\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5b59\u767e\u5bff\u5927\u60ca\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\u6b66\u529f\u76d6\u4e16\uff0c\u96be\u9053\u3001\u96be\u9053\u4e5f\u7740\u4e86\u5389\u5357\u661f\u8fd9\u5c0f\u5b50\u7684\u6697\u7b97\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6740\u53f2\u767d\u90fd\u7684\u4e0d\u662f\u5389\u5357\u661f\uff0c\u662f\u4ed6\u81ea\u5df1\uff01\u201d \u3000\u3000\u5b59\u767e\u5bff\u83ab\u540d\u5947\u5999\uff0c\u7741\u5927\u4e24\u53cc\u773c\u775b\u8be7\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u591a\u884c\u4e0d\u4e49\u5fc5\u81ea\u6bd9\uff0c\u8fd9\u53e5\u8bdd\u4f60\u4eec\u603b\u542c\u8fc7\u5427\uff1f\u53f2\u767d\u90fd\u7a7a\u6709\u4e00\u8eab\u6b66\u529f\uff0c\u53ef\u60dc\u4ed6\u7adf \u7518\u613f\u505a\u671d\u5ef7\u7684\u9e70\u72ac\uff0c\u5230\u5934\u6765\u4f17\u53db\u4eb2\u79bb\uff0c\u8d70\u6295\u65e0\u8def\uff0c\u7ec8\u4e8e\u4ed6\u81ea\u5df1\u7ed3\u675f\u4e86\u81ea\u5df1\u7684\u6027\u547d\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u4eba\u542c\u4e86\u91d1\u9010\u6d41\u7684\u8bdd\uff0c\u4e0d\u7531\u5f97\u90fd\u662f\u9762\u9762\u76f8\u89d1\uff0c\u4e0d\u6562\u642d\u8bdd\u3002 \u3000\u3000\u91d1\u9010\u6d41\u77e5\u9053\u4ed6\u4eec\u662f\u987e\u5fcc\u7740\u53f2\u7ea2\u82f1\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5927\u6982\u8fd8\u672a\u77e5\u9053\u5728\u897f\u660c\u53d1\u751f\u7684\u4e8b\u60c5\u5427\uff0c\u53f2 \u767d\u90fd\u8dd1\u53bb\u505a\u897f\u660c\u5c06\u519b\u5e05\u5b5f\u96c4\u7684\u5e2e\u51f6\uff0c\u516d\u5408\u5e2e\u7684\u5927\u5c0f\u5934\u76ee\u4e0d\u503c\u4ed6\u7684\u6240\u4e3a\uff0c\u5728\u4ed6\u81ea\u5c3d\u4e4b\u540e\uff0c\u5df2\u7ecf \u62e5\u7acb\u53f2\u59d1\u5a18\u505a\u5e2e\u4e3b\u4e86\u3002\u8fd9\u4f4d\u53f2\u59d1\u5a18\u548c\u5979\u7684\u54e5\u54e5\u867d\u662f\u4e00\u6bcd\u6240\u751f\uff0c\u53ef\u6beb\u4e0d\u4e00\u6837\u3002\u5e05\u5b5f\u96c4\u5c31\u662f\u5979\u523a \u6740\u7684\uff0c\u516d\u5408\u5e2e\u5982\u4eca\u4ea6\u5df2\u52a0\u5165\u4e86\u4e49\u519b\u4e86\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u54e5\u54e5\u7f6a\u6709\u5e94\u5f97\uff0c\u6211\u4e0d\u80fd\u529d\u4ed6\u6539\u90aa\u5f52\u6b63\uff0c\u6211\u4e5f\u5f88\u662f\u60ed\u6127\uff0c\u4f46\u613f\u4f60\u4eec\u80fd\u591f\u628a \u4ed6\u5f53\u4f5c\u4e00\u9762\u955c\u5b50\uff0c\u4e0d\u8981\u518d\u8e48\u4ed6\u7684\u8986\u8f99\u3002\u201d \u3000\u3000\u5b59\u767e\u5bff\u542c\u4e86\u53f2\u7ea2\u82f1\u8fd9\u4e48\u8bf4\uff0c\u8fd9\u624d\u7387\u9886\u4f17\u4eba\uff0c\u5411\u53f2\u7ea2\u82f1\u884c\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u5c5e\u4e0b\u53c2\u89c1\u5e2e\u4e3b\uff0c\u8c28 \u9075\u5e2e\u4e3b\u6559\u8bf2\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u521a\u521a\u8bf4\u8fc7\uff0c\u5e2e\u4f1a\u7eb5\u6709\u5927\u5c0f\u4e4b\u4e45\u5374\u65e0\u5c0a\u5351\u4e4b\u522b\uff0c\u6211\u54e5\u54e5\u4ee5\u524d\u505a\u516d\u5408\u5e2e\u7684\u5e2e \u4e3b\uff0c\u6043\u5f3a\u51cc\u5f31\uff0c\u8981\u4f60\u4eec\u542c\u4ed6\u7684\u53f7\u4ee4\uff0c\u5e74\u5e74\u7eb3\u8d21\uff0c\u505a\u4ed6\u7684\u5c5e\u5e2e\uff0c\u4ece\u4eca\u4e4b\u540e\uff0c\u4e00\u5207\u964b\u89c4\uff0c\u5168\u90e8\u514d \u4e86\u3002\u516d\u5408\u5e2e\u548c\u4f60\u4eec\u5404\u5e2e\uff0c\u613f\u610f\u7ed3\u4e3a\u5144\u5f1f\u76df\u5e2e\uff0c\u6709\u4e8b\u5927\u5bb6\u5546\u91cf\uff0c\u5f7c\u6b64\u5171\u52c9\u3002\u8ba2\u4e86\u76df\u7ea6\uff0c\u5927\u5bb6\u90fd \u4e00\u6837\u9075\u5b88\uff0c\u4f60\u4eec\u8bf4\u597d\u4e48\u3002\u201d \u3000\u3000\u5b59\u767e\u5bff\u8fd9\u4e9b\u4eba\u4e00\u5411\u81e3\u670d\u4e8e\u516d\u5408\u5e2e\uff0c\u5176\u5b9e\u90fd\u53ea\u662f\u53e3\u670d\u5fc3\u4e0d\u670d\u7684\uff0c\u53ea\u56e0\u52bf\u529b\u4e0d\u654c\uff0c\u53d7\u4e86\u6b3a\u538b \u65e0\u53ef\u5948\u4f55\u800c\u5df2\u3002\u542c\u4e86\u53f2\u7ea2\u82f1\u7684\u8bdd\uff0c\u7686\u5927\u6b22\u559c\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u655d\u5e2e\u65b0\u4efb\u526f\u5e2e\u4e3b\u7684\u674e\u6566\uff0c\u80fd\u89e3\u5929\u9b54\u6559\u6240\u4e0b\u7684\u6bd2\uff0c\u4f60\u4eec\u4e0d\u7528\u62c5\u5fc3\uff0c\u6211\u53eb\u4ed6\u66ff \u4f60\u4eec\u7684\u5e2e\u4e3b\u89e3\u6bd2\u4fbf\u662f\u3002\u5404\u5e2e\u8ba2\u76df\u4e4b\u4e8b\uff0c\u90a3\u5c31\u4ee5\u540e\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u4f17\u4eba\u8d8a\u53d1\u5927\u559c\uff0c\u518d\u6b21\u5411\u53f2\u7ea2\u82f1\u9053\u8c22\u3002\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u5bf9\u4ed8\u5929\u9b54\u6559\u4e4b\u4e8b\uff0c\u655d\u5e2e\u81ea\u7136\u4e5f\u5f53\u5c3d \u529b\u3002\u4f46\u5176\u4e2d\u7591\u7aa6\u9887\u591a\uff0c\u6211\u548c\u91d1\u5c11\u4fa0\u610f\u6b32\u5148\u884c\u63a2\u660e\u771f\u76f8\uff0c\u5728\u672a\u66fe\u6c34\u843d\u77f3\u51fa\u4e4b\u524d\uff0c\u8bf7\u5404\u4f4d\u7a0d\u5b89\u6bcb \u8e81\uff0c\u4e0d\u5fc5\u4e0e\u5929\u9b54\u6559\u51b2\u7a81\u3002\u201d\u5b59\u767e\u5bff\u9053\uff1a\u201c\u662f\uff0c\u4e00\u5207\u542c\u53f2\u5e2e\u4e3b\u5b89\u6392\u3002\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48 \u54b1\u4eec\u540e\u4f1a\u6709\u671f\uff0c\u6211\u548c\u91d1\u5c11\u4fa0\u5148\u8d70\u4e86\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u7434\u5251\u76f8\u4ea4\u60c5\u8c0a\u539a\uff0c\u6b7b\u751f\u672a\u535c\u81ea\u7d22\u5fc3\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e00\u56de\u3000\u8c6a\u6770\u80f8\u6000\u906d\u8bef\u89e3\u3000\u9b3c\u9b45\u4f0e\u4fe9\u6700\u96be\u9632 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u56db\u5341\u4e00\u56de\u3000\u8c6a\u6770\u80f8\u6000\u906d\u8bef\u89e3\u3000\u9b3c\u9b45\u4f0e\u4fe9\u6700\u96be\u9632 \u3000\u3000\u4e8c\u4eba\u79bb\u5f00\u9053\u89c2\uff0c\u8def\u4e0a\u53f2\u7ea2\u82f1\u95ee\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u5f53\u771f\u662f\u5947\u602a\u6781\u4e86\uff0c\u9010\u6d41\uff0c\u4f60\u7684\u770b\u6cd5\u600e\u4e48 \u6837\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5b9a\u662f\u5047\u7684\u65e0\u7591\uff0c\u636e\u6211\u6240\u77e5\uff0c\u9633\u6d69\u66fe\u7ecf\u7528\u8fc7\u79cd\u79cd\u5a01\u80c1\uff0c\u5229\u8bf1\u7684\u624b\u6bb5\uff0c\u8981\u5389\u5927 \u54e5\u505a\u5929\u9b54\u6559\u7684\u6559\u4e3b\uff0c\u5f53\u65f6\u5389\u5927\u54e5\u5b81\u53ef\u548c\u4ed6\u4eec\u7ffb\u8138\uff0c\u4ee5\u5be1\u654c\u4f17\uff0c\u5728\u79d8\u9b54\u5d16\u548c\u4ed6\u4eec\u6076\u6597\u4e00\u573a\uff0c\u6b7b \u4e5f\u4e0d\u80af\u7b54\u5e94\u3002\u4f60\u60f3\uff0c\u522b\u4eba\u62e5\u7acb\u4ed6\uff0c\u4ed6\u90fd\u4e0d\u5c51\uff0c\u5c82\u6709\u81ea\u5df1\u53bb\u627e\u9ebb\u70e6\uff0c\u91cd\u7ec4\u5929\u9b54\u6559\u4e4b\u7406\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\u8fd9\u662f\u5047\u7684\uff0c\u4ee5\u5389\u5927\u54e5\u7684\u4e3a\u4eba\uff0c\u4ed6\u51b3\u4e0d\u4f1a\u505a\u51fa\u90a3\u4e9b\u72e0\u6bd2\u7684\u4e8b\u60c5\u3002 \u4e0d\u8fc7\uff0c\u6211\u73b0\u6709\u4e00\u4e2a\u7591\u56e2\uff0c\u767e\u601d\u83ab\u5f97\u5176\u89e3\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u662f\u5426\u6000\u7591\u5389\u5927\u54e5\u53ef\u80fd\u8fd8\u6d3b\u5728\u4eba\u95f4\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u662f\u5440\u3002\u82e5\u7136\u4ed6\u771f\u7684\u6b7b\u4e86\uff0c\u8fd9\u4e2a\u5047\u7684\u5389\u5357\u661f\u53c8\u662f\u4ece\u54ea\u91cc\u94bb\u51fa\u6765\u7684\uff1f\u6b66\u6797\u4e2d\u4eba \u8ba4\u8bc6\u5389\u5927\u54e5\u7684\u4eba\u867d\u7136\u4e0d\u662f\u592a\u591a\uff0c\u5374\u4e5f\u4e0d\u6b62\u4e09\u4e2a\u4e94\u4e2a\u3002\u6bd4\u5982\u8bf4\u4f60\u7684\u5e05\u5144\u6c5f\u5927\u4fa0\u548c\u7ea2\u7f28\u4f1a\u7684\u5e2e\u4e3b \u516c\u5b59\u5b8f\u90fd\u662f\u8ba4\u8bc6\u4ed6\u7684\uff0c\u8fd9\u4e2a\u5047\u7684\u5389\u5357\u661f\u96be\u9053\u4e0d\u6015\u7ed9\u4eba\u77a7\u51fa\u7834\u7efd\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u5389\u5927\u54e5\u53ef\u80fd\u53d7\u4e86\u522b\u4eba\u7684\u631f\u6301\uff1f\u4e0d\u8fc7\u2026\u2026\uff1f \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u77e5\u9053\u5389\u5927\u54e5\u7684\u5014\u5f3a\u813e\u6c14\uff0c\u51b3\u4e0d\u80af\u53d7\u4eba\u631f\u6301\u3002\u4e0d\u5047\u5982\u5982\u8bf4\u9633\u6d69\u662f\u7ed9\u4ed6\u670d\u4e86 \u4e00\u79cd\u4ec0\u4e48\u836f\uff0c\u4f7f\u4ed6\u795e\u667a\u4e0d\u6e05\uff0c\u5c06\u4ed6\u5f53\u4f5c\u5080\u5121\uff0c\u662f\u4e0d\u662f\u4e5f\u6709\u8fd9\u4e2a\u53ef\u80fd\u5462\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u9b54\u6559\u4f7f\u6bd2\u7684\u6cd5\u5b50\u7a00\u5947\u53e4\u602a\uff0c\u96be\u4fdd\u6ca1\u6709\u8fd9\u4e2a\u53ef\u80fd\u3002\u4e0d\u8fc7\uff0c\u4ecd \u662f\u6709\u4e2a\u8001\u5927\u7684\u7834\u7efd\uff0c\u8fd9\u4e2a\u63a8\u60f3\u6050\u6015\u3001\u6050\u6015\u4e0d\u80fd\u6210\u7acb\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4ec0\u4e48\u7834\u7efd\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u636e\u5bab\u79c9\u85e9\u8bf4\uff0c\u90a3\u65e5\u4ed6\u548c\u9633\u6d69\u6597\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u5bab\u79c9\u85e9\u56fa\u7136\u4f24\u7684\u5f88\u91cd\uff0c\u9633\u6d69\u5e26 \u4e86\u51e0\u5904\u5251\u4f24\uff0c\u4e5f\u51b3\u4e0d\u4f1a\u592a\u8f7b\u3002\u5f53\u65e5\u7684\u5904\u5883\u3002\u4e49\u519b\u53ef\u80fd\u968f\u65f6\u6765\u5230\u3002\u9633\u6d69\u53d7\u4e86\u4f24\uff0c\u4ed6\u8fd8\u4e0d\u8d76\u7d27\u9003 \u8dd1\uff1f\u518d\u8bf4\uff0c\u5373\u4f7f\u4ed6\u5b58\u5fc3\u8981\u628a\u5389\u5927\u54e5\u4f5c\u4e3a\u4eba\u8d28\uff0c\u90a3\u767e\u4e08\u60ac\u5d16\uff0c\u5e7d\u8c37\u4e2d\u53c8\u6709\u6bd2\u96fe\u7b3c\u7f69\uff0c\u4ed6\u4e00\u4e2a\u53d7 \u4f24\u7684\u4eba\u6562\u4e0b\u53bb\u5417\uff1f\u5c31\u7b97\u4ed6\u6709\u8fd9\u4e2a\u80c6\u91cf\uff0c\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u672c\u9886\u4e86\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u53ea\u6709\u54b1\u4eec\u4eb2\u81ea\u5230\u5929\u9b54\u6559\u53bb\u6c42\u89c1\u8fd9\u4f4d\u65b0\u6559\u4e3b\uff0c\u65b9\u80fd\u63ed\u5f00\u771f\u76f8\u4e86\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53bb\u603b\u662f\u8981\u53bb\u7684\uff0c\u4f46\u6211\u4ee5\u4e3a\u8fd8\u662f\u591a\u83b7\u5f97\u4e00\u4e9b\u7ebf\u7d22\u624d\u53bb\u8f83\u597d\uff0c\u514d\u5f97\u5760\u5165\u4eba\u5bb6\u7684 \u9677\u9631\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4f9d\u4f60\u4e4b\u89c1\u5982\u4f55\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd8\u662f\u4f9d\u7167\u539f\u6765\u7684\u8ba1\u5212\uff0c\u5148\u53bb\u89c1\u4e86\u516c\u5b59\u5b8f\u518d\u8bf4\u3002\u672c\u6765\u5bab\u79c9\u85e9\u6258\u54b1\u4eec\u66ff\u4ed6\u62a5 \u4fe1\uff0c\u6b64\u4e8b\u4e0d\u5b9c\u803d\u6401\uff1b\u4e8c\u6765\u7ea2\u7f28\u4f1a\u662f\u6c5f\u6e56\u4e0a\u7b2c\u4e00\u5927\u5e2e\u4f1a\uff1b\u5929\u9b54\u6559\u91cd\u5f00\u9999\u5802\uff0c\u4e00\u5b9a\u4f1a\u8bf7\u516c\u5b59\u5e2e\u4e3b \u524d\u53bb\u89c2\u793c\u7684\u3002\u4f46\u4e0d\u77e5\u516c\u5b59\u5b8f\u662f\u5426\u80af\u53bb\uff0c\u53bb\u4e86\u53c8\u662f\u5426\u5df2\u7ecf\u89c1\u5230\u4e86\u90a3\u65b0\u6559\u4e3b\u4e86\uff1f\u8fd9\u4e24\u4eec\u4e8b\u60c5\uff0c\u89c1 \u4e86\u516c\u5b59\u5b8f\u5c31\u53ef\u4ee5\u95ee\u4e2a\u660e\u767d\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u5bf9\uff0c\u8fd8\u662f\u4f60\u60f3\u5f97\u5468\u5230\uff0c\u5c31\u8fd9\u6837\u5427\u3002\u516c\u5b59\u5b8f\u5bb6\u4f4f\u5c71\u4e1c\u6b66\u9091\uff0c\u4e0e\u5929\u9b54\u6559\u603b\u8235\u6240 \u5728\u7684\u5f82\u5f95\u5c71\u4e5f\u4e0d\u8fc7\u53ea\u662f\u6570\u767e\u91cc\u4e4b\u9065\u3002\u201d \u3000\u3000\u8ba1\u8bae\u5df2\u8d70\uff0c\u4e24\u5165\u9042\u5373\u517c\u7a0b\u8d76\u8def\uff0c\u524d\u5f80\u6b66\u9091\u3002\u4e00\u8def\u65e0\u4e8b\uff0c\u5e73\u5b89\u62b5\u8fbe\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u7684\u540d\u5b57\u5728\u6b66\u9091\u4e43\u662f\u5bb6\u55bb\u6237\u6653\uff0c\u91d1\u9010\u6d41\u6beb\u4e0d\u8d39\u529b\u5c31\u6253\u542c\u5230\u4ed6\u7684\u4f4f\u5740\u3002 \u3000\u3000\u4e00\u8def\u884c\u6765\uff0c\u63a5\u8fde\u78b0\u5230\u597d\u51e0\u4e2a\u9a91\u9a6c\u7684\u4eba\u8d76\u8fc7\u4ed6\u4eec\u7684\u524d\u5934\uff0c\u6bcf\u4e2a\u4eba\u90fd\u56de\u5934\u5411\u4ed6\u4eec\u5f20\u671b\uff0c\u597d\u4f3c \u5bf9\u4ed6\u4eec\u751a\u4e3a\u6ce8\u610f\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u591a\u534a\u662f\u7ea2\u7f28\u4f1a\u7684\uff0c\u77e5\u9053\u54b1\u4eec\u8981\u53bb\u62dc\u8bbf\u4ed6\u4eec\u7684\u53bb\uff0c\u8d76\u56de\u53bb\u62a5\u4fe1\u4e86\u3002\u201d \u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u672c\u6765\u4e0d\u60f3\u5f20\u626c\u7684\uff0c\u60f3\u4e0d\u5230\u8fd8\u662f\u60ca\u52a8\u4e86\u4ed6\u8001\u4eba\u5bb6\u3002\u4e0d\u8fc7\u4ed6\u8001\u4eba\u5bb6\u6050\u6015\u8fd8\u672a \u60f3\u5230\u7adf\u4f1a\u662f\u54b1\u4eec\u4e00\u540c\u6765\u770b\u4ed6\u5462\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f60\u662f\u540d\u95e8\u5927\u4fa0\u7684\u5f1f\u5b50\uff0c\u6211\u5374\u662f\u6076\u540d\u662d\u5f70\u7684\u516d\u5408\u5e2e\u5e2e\u4e3b\u7684\u59b9\u5b50\uff0c\u4ed6\u5f53 \u7136\u60f3\u4e0d\u5230\u54b1\u4eec\u4f1a\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u8bf4\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u6211\u53ef\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u6211\u662f\u8bf4\u2026\u2026\u563b\u563b\uff0c\u54b1\u4eec\u7684\u4e8b\u60c5\uff0c\u4ed6 \u4e00\u5b9a\u8fd8\u672a\u77e5\u9053\uff1a\u201c \u3000\u3000\u53f2\u7ea2\u82f1\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u522b\u80e1\u626f\u4e86\u3002\u8bf4\u771f\u4e2a\u7684\uff0c\u6211\u5012\u6709\u70b9\u62c5\u5fc3\u5462\u3002\u7ea2\u7f28\u4f1a\u4e0e\u516d\u5408\u5e2e\u4e00 \u5411\u662f\u4e0d\u5927\u548c\u597d\u7684\uff0c\u4e0d\u77e5\u4ed6\u4eec\u6b22\u4e0d\u6b22\u8fce\u6211\u5462\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u5982\u679c\u77e5\u9053\u4e86\u4f60\u7684\u8eab\u4efd\uff0c\u6b22\u8fce\u90fd\u6050\u6015\u6765\u4e0d\u53ca\u5462\uff0c\u4f60\u662f\u516d\u5408\u5e2e\u7684\u65b0\u5e2e \u4e3b\uff0c\u4f60\u4e00\u505a\u4e86\u201c\u6076\u540d\u662d\u5f70\u2019\u7684\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\uff0c\u5c31\u8981\u53d8\u6210\u4e86\u5584\u540d\u662d\u5f70\u5566\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u7ea2\u7f28\u4f1a\u7684\u6d88\u606f\u867d\u7136\u7075\u901a\uff0c\u897f\u660c\u6240\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u6599\u60f3\u4ed6\u4eec\u8fd8\u4e0d\u4f1a\u8fd9\u6837\u5feb\u5c31\u77e5 \u9053\u4e86\u3002\u4e0d\u8fc7\uff0c\u597d\u5728\u6211\u662f\u8ddf\u4f60\u6765\u7684\uff0c\u4f60\u7684\u5e08\u5144\u4e0e\u516c\u5b59\u5b8f\u4ea4\u60c5\u975e\u6d45\uff0c\u4ed6\u4e0d\u6b22\u8fce\u6211\uff0c\u4e5f\u4f1a\u6b22\u8fce \u4f60\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u5bb6\u4f4f\u57ce\u5357\u7684\u4e00\u6761\u5c71\u6751\uff0c\u91d1\u3001\u53f2\u4e8c\u4eba\u7a7f\u8fc7\u4e00\u4e2a\u677e\u6797\uff0c\u8fdc\u8fdc\u5730\u671b\u89c1\u4e00\u5ea7\u5927\u5c4b\uff0c\u91d1\u9010\u6d41 \u7b11\u9053\uff1a\u201c\u54b1\u4eec\u53ea\u987e\u8c08\u8bdd\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u7ecf\u5230\u4e86\u3002\u90a3\u4eba\u8bf4\u6751\u91cc\u6700\u5927\u7684\u4e00\u5ea7\u5c4b\u5b50\u5c31\u662f\u516c\u5b59\u5e2e\u4e3b\u7684\uff0c \u60f3\u5fc5\u662f\u8fd9\u4e00\u95f4\u4e86\u3002\u54a6\uff0c\u4f60\u77a7\uff0c\u6709\u4eba\u51fa\u6765\u5566\uff01 \u3000\u3000\u53f2\u7ea2\u82f1\u51dd\u795e\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u5e2e\u4eba\u5df2\u7ecf\u5728\u5c71\u5761\u4e0a\u5217\u9635\u4ee5\u5f85\uff0c\u53f2\u7ea2\u82f1\u8ba4\u5f97\u5176\u4e2d\u4e8c\u4eba\u662f\u5728\u7ea2\u7f28\u4f1a \u4e2d\u5750\u7b2c\u4e09\u628a\u4ea4\u6905\u548c\u7b2c\u56db\u628a\u652f\u6905\u7684\u5e84\u8fdc\u548c\u79e6\u51b2\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u8fd9\u5012\u5947\u4e86\uff0c\u521a\u624d\u5728\u8def\u4e0a\u78b0\u89c1\u7684\u90a3\u51e0\u4e2a\u4eba\u90fd\u662f\u4e0d\u8ba4\u8bc6\u6211\u7684\uff0c\u9010\u6d41\uff0c\u4f46\u5374\u4e0d\u77e5 \u4ed6\u4eec\u6700\u4e0d\u662f\u8ba4\u8bc6\u4f60\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5f53\u7136\u4e5f\u662f\u4e0d\u8ba4\u8bc6\u7684\uff0c\u5426\u5219\u4ed6\u4eec\u8fd8\u4e0d\u548c\u6211\u6253\u62db\u547c\u5417\uff1f\u4f46\u8fd9\u5374\u6709\u4ec0\u4e48\u5947\u602a\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u8fd9\u5e84\u8fdc\u548c\u79e6\u51b2\u4e8c\u4eba\uff0c\u5728\u7ea2\u7f28\u4f1a\u4e2d\u7684\u5730\u4f4d\u4ec5\u6b21\u4e8e\u5e2e\u4e3b\u548c\u5bab\u79c9\u85e9\uff0c\u4ed6\u4eec\u82e5\u662f\u4e8b \u5148\u4e0d\u77e5\u9053\u6765\u7684\u662f\u4f60\uff0c\u51b3\u4e0d\u4f1a\u7387\u4f17\u51fa\u8fce\u7684\u3002\u9010\u6d41\uff0c\u8fd9\u6b21\u53ef\u662f\u6cbe\u4e86\u4f60\u7684\u5149\u5566\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4e0d\uff0c\u662f\u6211\u6cbe\u4f60\u7684\u5149\uff0c\u90a3\u51e0\u4e2a\u4eba\u867d\u7136\u4e0d\u8ba4\u8bc6\u4f60\uff0c\u4f46\u95ef\u8361\u6c5f\u6e56\u7684\u5973\u5b50\u80fd\u6709\u51e0 \u4eba\uff0c\u4e00\u4e2a\u5973\u5b50\u6765\u62dc\u4f1a\u4ed6\u4eec\u7684\u5e2e\u4e3b\uff0c\u4ed6\u4eec\u56de\u53bb\u4e00\u8bf4\uff0c\u516c\u5b59\u5b8f\u8fd9\u8001\u513f\u731c\u4e5f\u731c\u5f97\u5230\u662f\u4f60\u4e86\u3002\u6211\u5012\u6709 \u70b9\u5947\u602a\u4ed6\u4e3a\u4ec0\u4e48\u4e0d\u4eb2\u81ea\u51fa\u8fce\u5462\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u5fc3\u82b1\u6012\u653e\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u8fd9\u4e24\u4f4d\u9999\u4e3b\u51fa\u8fce\uff0c\u5df2\u7ecf\u662f\u7ed9\u4e86\u54b1\u4eec\u5929\u5927\u7684\u9762\u5b50\u5566\u3002\u793c\u5c1a\u5f80 \u6765\uff0c\u54b1\u4eec\u5e94\u8be5\u5feb\u53bb\u7b54\u8c22\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u4e8e\u662f\u4e24\u4eba\u52a0\u6289\u811a\u6b65\uff0c\u8fce\u4e0a\u524d\u53bb\uff0c\u91d1\u9010\u6d41 \u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u2026\u2026\u54a6\uff0c\u4f60\u4eec\u8fd9\uff0c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u5e84\u8fdc\u3001\u79e6\u51b2\u5e26\u9886\u7684\u51e0\u5341\u4e2a\u4eba\uff0c\u500f\u5730\u4ece\u4e24 \u7ffc\u5305\u6284\u4e0a\u6765\uff0c\u5c06\u4ed6\u4eec\u56f0\u5728\u6838\u5fc3\uff0c\u4eba\u4eba\u90fd\u662f\u54ac\u7259\u5207\u9f7f\uff0c\u5bf9\u4ed6\u4eec\u6012\u76ee\u800c\u89c6\u3002 \u3000\u3000\u5e84\u8fdc\u9053\uff1a\u201c\u91d1\u9010\u6d41\uff0c\u4e0d\u5173\u4f60\u7684\u4e8b\uff0c\u4f60\u7ad9\u8fc7\u4e00\u8fb9\u3002\u201d\u79e6\u51b2\u5219\u5df2\u6307\u7740\u53f2\u7ea2\u82f1\u9a82\u9053\uff1a\u201c\u7ea2\u7f28\u4f1a \u8fd8\u4e0d\u81f3\u4e8e\u6015\u4e86\u4f60\u4eec\u516d\u5408\u5e2e\uff0c\u4f60\u8fd9\u81ed\u4e2b\u5934\u7adf\u6562\u5982\u6b64\u7316\u72c2\uff0c\u6b3a\u4fae\u4e0a\u95e8\u6765\u5566\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u5927\u60ca\u9053\uff1a\u201c\u8fd9\u8bdd\u4ece\u54ea\u91cc\u8bf4\u8d77\uff1f\u6211\u662f\u6765\u62dc\u89c1\u8d35\u4f1a\u7684\u603b\u8235\u4e3b\u7684\uff01\u655d\u5e2e\u8fc7\u53bb\u884c\u4e8b\u4e0d\u5f53\uff0c \u5bb9\u6211\u89c1\u4e86\u516c\u5b59\u8235\u4e3b\u3002\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53ea\u542c\u5f97\u559d\u9a82\u4e4b\u58f0\u5df2\u662f\u95f9\u6210\u4e00\u7247\u3002\u5e84\u8fdc\u5c24\u5176\u6012\u5f97\u53cc\u773c\u597d\u4f3c\u5c31\u8981\u55b7\u51fa\u706b\u6765\uff0c\u621f\u6307 \u9a82\u9053\uff1a\u201c\u516c\u5b59\u8235\u4e3b\u8fd8\u6ca1\u6709\u6b7b\uff0c\u4f60\u6765\u6253\u542c\u6d88\u606f\u672a\u514d\u65e9\u4e86\u70b9\u513f\uff01\u201d\u79e6\u51b2\u9053\uff1a\u201c\u4ec0\u4e48\u6253\u542c\u6d88\u606f\uff1f\u5979 \u8bf4\u8fd9\u4e9b\u98ce\u51c9\u8bdd\u513f\uff0c\u5206\u660e\u662f\u6765\u620f\u4fae\u54b1\u4eec\uff01\u5e84\u5927\u54e5\uff0c\u4e0d\u7528\u548c\u5979\u591a\u8bf4\u5e9f\u8bdd\uff0c\u5979\u65e2\u6562\u5982\u6b64\u7316\u72c2\uff0c\u54b1\u4eec \u5c31\u4e0d\u80fd\u8ba9\u5979\u770b\u5c0f\u4e86\uff01\u662f\u4f60\u4e0a\u8fd8\u662f\u6211\u4e0a\uff1f\u201d \u3000\u3000\u5e84\u8fdc\u559d\u9053\uff1a\u201c\u53f2\u7ea2\u82f1\uff0c\u6211\u548c\u4f60\u5355\u6253\u72ec\u6597\uff0c\u7701\u5f97\u4f60\u8bf4\u6211\u4eec\u4ee5\u591a\u6b3a\u5c11\u3002\u4eae\u5175\u5668\u5427\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u82e5\u7136\u53ea\u662f\u4e3a\u4e86\u4e24\u4e2a\u5e2e\u4f1a\u95f4\u7684\u5bbf\u6028\uff0c\u4ed6\u4eec\u51b3\u4e0d\u4f1a\u5982\u6b64\u6c14\u6012\uff0c\u5185\u4e2d\u60f3\u5fc5 \u53e6\u6709\u539f\u56e0\u3002\u201d\u5f53\u4e0b\u633a\u8eab\u4e0a\u524d\uff0c\u53eb\u9053\uff1a\u201c\u6709\u8bdd\u597d\u8bf4\uff0c\u5bb9\u6211\u4eec\u5148\u89c1\u4e86\u516c\u5b59\u8235\u4e3b\u5982\u4f55\uff1f\u201d \u3000\u3000\u79e6\u51b2\u559d\u9053\uff1a\u201c\u91d1\u9010\u6d41\uff0c\u6211\u662f\u770b\u5728\u4ee4\u5e08\u5144\u4efd\u4e0a\uff0c\u624d\u6ca1\u6709\u5c06\u4f60\u548c\u8fd9\u5996\u5973\u4e00\u6837\u770b\u5f85\uff0c\u4f60\u53ef\u8981\u8bc6 \u76f8\u70b9\u513f\uff01\u5018\u82e5\u4f60\u5b9a\u8981\u536b\u62a4\u8fd9\u4e2a\u5996\u5973\uff0c\u90a3\u5c31\u4f11\u602a\u6211\u4e0d\u5ba2\u6c14\u4e86\uff01\u201d \u3000\u3000\u8fd9\u4e00\u8fb9\u8bdd\u72b9\u672a\u4e86\uff0c\u90a3\u4e00\u8fb9\u5e84\u8fdc\u5df1\u7136\u5bf9\u53f2\u7ea2\u82f1\u51fa\u624b\u3002\u5e84\u8fdc\u559d\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u5175\u5668\uff0c\u54b1\u4eec\u5c31\u5728 \u638c\u4e0a\u89c1\u4e2a\u9ad8\u4e0b\uff01\u201d\u5de6\u624b\u4e00\u62ac\uff0c\u4e00\u62db\u201c\u7384\u9e1f\u5212\u7802\u201d\uff0c\u62c7\u6307\u548c\u98df\u6307\u6263\u6210\u4e00\u4e2a\u7f3a\u53e3\u7684\u73af\u5f62\uff0c\u6309\u4e0b\u7684 \u65b9\u4f4d\u6b63\u5f53\u53f2\u7ea2\u82f1\u80f8\u53e3\u7684\u201c\u91d1\u697c\u201d\u201c\u7389\u95fa\u201d\u4e24\u5904\u9ebb\u7a74\u3002\u53f3\u624b\u5219\u662f\u6a2a\u638c\u5982\u5200\uff0c\u201c\u5200\u201d\u524a\u5411\u53f2\u7ea2\u82f1 \u7684\u9888\u9879\u3002\u5e84\u8fdc\u7684\u5927\u64d2\u62ff\u624b\u6cd5\u548c\u7ef5\u638c\u529f\u592b\u4e43\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u53f2\u7ea2\u82f1\u7684\u957f\u5904\u5728\u4e8e\u97ad\u6cd5\u5251\u6cd5\uff0c\u62f3\u811a\u4e0a \u7684\u529f\u592b\u8fdc\u8fdc\u6bd4\u4e0d\u4e0a\u4ed6\u3002\u4ed7\u7740\u8f7b\u529f\uff0c\u4e00\u4e2a\u201c\u98ce\u522e\u843d\u82b1\u201d\u7684\u5f0f\u5b50\uff0c\u6070\u6070\u907f\u5f00\uff0c\u4f46\u7ed9\u5e84\u8fdc\u638c\u98ce\u522e\u9762 \u800c\u8fc7\uff0c\u4ea6\u5df2\u7a33\u9690\u751f\u75db\u3002 \u3000\u3000\u91d1\u9010\u6d41\u559d\u9053\uff1a\u201c\u4f4f\u624b\uff01\u4f60\u4eec\u8bb2\u4e0d\u8bb2\u7406\uff1f\u53f2\u59d1\u5a18\u7684\u6765\u610f\u4f60\u4eec\u5c1a\u672a\u77e5\u9053\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u5979\u8bf4 \u8bdd\uff01\u201d\u6b64\u65f6\uff0c\u91d1\u9010\u6d41\u4ea6\u5df2\u5fcd\u4e0d\u4f4f\u53d1\u6012\u4e86\u3002 \u3000\u3000\u79e6\u51b2\u62d4\u51fa\u4e86\u6298\u94c1\u5200\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u7684\u6765\u610f\u6211\u4eec\u65e9\u5df2\u77e5\u9053\uff0c\u5012\u662f\u9601\u4e0b\u7684\u6765\u610f\u6211\u4eec\u672a \u77e5\uff01\u4f60\u7a76\u7adf\u662f\u5e2e\u54ea\u4e00\u8fb9\u7684\uff01\u201d\u7ea2\u7f28\u4f1a\u7684\u5e2e\u4f17\u6709\u4eba\u53eb\u9053\uff1a\u201c\u8fd9\u8fd8\u7528\u95ee\uff0c\u8fd9\u5c0f\u5b50\u53d7\u4e86\u5996\u5973\u7684\u8ff7 \u60d1\uff0c\u5f53\u7136\u662f\u5e2e\u5979\u6765\u6b3a\u538b\u6211\u4eec\u7684\u4e86\u3002\u201d \u3000\u3000\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u4f46\u542c\u8bf4\u8fd9\u5c0f\u5b50\u4e5f\u662f\u53f2\u767d\u90fd\u7684\u5bf9\u5934\uff0c\u4ed6\u603b\u4e0d\u8be5\u90aa\u6b63\u4e0d\u5206\u5427\uff1f\u201d\u53e6\u4e00\u4e2a\u8bf4\u9053\uff1a \u201c\u4f60\u662f\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\u3002\u4ed6\u662f\u53f2\u767d\u90fd\u7684\u5bf9\u5934\uff0c\u4f46\u4e5f\u662f\u5389\u5357\u661f\u7684\u597d\u670b\u53cb\u3002\u201d\u5148\u524d\u90a3\u4eba\u201c\u554a \u5440\u201d\u4e00\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4ed6\u4e5f\u662f\u54b1\u4eec\u7684\u4ec7\u4eba\u4e86\uff0c\u548c\u4ed6\u5ba2\u6c14\u4f5c\u751a\u3002\u201d \u3000\u3000\u79e6\u51b2\u6a2a\u5200\u62e6\u7740\u91d1\u9010\u6d41\uff0c\u60f3\u662f\u56e0\u4e3a\u770b\u5728\u6c5f\u6d77\u5929\u7684\u9762\u5b50\uff0c\u53ea\u60f3\u963b\u6b62\u4ed6\u53bb\u6551\u63f4\u53f2\u7ea2\u82f1\uff0c\u5c1a\u672a\u66fe \u5411\u4ed6\u52a8\u624b\u3002\u7ea2\u7f28\u4f1a\u7684\u5e2e\u4f17\u5f1f\u5b50\u5bf9\u4ed6\u8bae\u8bba\u7eb7\u7eb7\uff0c\u6709\u4e9b\u4eba\u4e14\u5df2\u54ac\u5b9a\u4ed6\u662f\u53d8\u8282\uff0c\u4e3b\u5f20\u628a\u91d1\u9010\u6d41\u4e5f\u4e00 \u5e76\u62ff\u4e0b\u3002 \u3000\u3000\u5e84\u8fdc\u7684\u5927\u64d2\u62ff\u624b\u6cd5\u4f55\u7b49\u5389\u5bb3\uff0c\u5c31\u5728\u91d1\u9010\u6d41\u8fd9\u8fb9\u95f9\u54c4\u54c4\u7684\u65f6\u5019\u3001\u4ed6\u5df2\u662f\u628a\u53f2\u7ea2\u82f1\u903c\u5f97\u9000\u65e0 \u53ef\u9000\uff0c\u8981\u77e5\u7ea2\u7f28\u4f1a\u7684\u5e2e\u4f17\u662f\u5217\u6210\u9635\u52bf\uff0c\u5c06\u4ed6\u4eec\u56f4\u5728\u6838\u5fc3\u7684\uff0c\u6545\u6b64\u867d\u7136\u8bf4\u662f\u5355\u6253\u72ec\u6597\uff0c\u4f46\u53f2\u7ea2 \u82f1\u5374\u7ed9\u9650\u5236\u5728\u5305\u56f4\u5708\u5185\uff0c\u56db\u5468\u90fd\u662f\u4eba\u5899\uff0c\u8f7b\u529f\u518d\u597d\uff0c\u4e5f\u65e0\u56de\u65cb\u7684\u4f59\u5730\uff0c\u81ea\u662f\u96be\u514d\u5927\u5927\u5403\u4e8f\u3002 \u3000\u3000\u5728\u8fd9\u6837\u60c5\u5f62\u4e4b\u4e0b\uff0c\u91d1\u9010\u6d41\u77e5\u9053\u5df2\u662f\u4e0d\u80fd\u8ba9\u4ed6\u4ece\u5bb9\u8fa9\u89e3\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u89c1\u5e84\u8fdc\u4e00\u4e2a\u8fdb\u6b65\u6b3a \u8eab\uff0c\u4f7f\u51fa\u4e86\u201c\u8fde\u73af\u5954\u96f7\u638c\u201d\u7684\u624b\u6cd5\uff0c\u53cc\u638c\u9690\u9690\u631f\u7740\u98ce\u96f7\u4e4b\u58f0\uff0c\u773c\u770b\u5c31\u8981\u6253\u5230\u4e86\u53f2\u7ea2\u82f1\u7684\u8eab \u4e0a\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5143\u6687\u601d\u7d22\uff0c\u4e00\u58f0\u559d\u9053\uff1a\u201c\u8ba9\u5f00\uff01\u201d\u9661\u7136\u95f4\u8eab\u5f62\u4e00\u8d77\uff0c\u6ed1\u4f3c\u6e38\u9c7c\uff0c\u4ece\u79e6\u51b2\u8098\u4e0b\u7a7f \u8fc7\uff0c\u79e6\u51b2\u60f3\u4e0d\u5230\u4ed6\u8eab\u6cd5\u5982\u6b64\u53e4\u602a\uff0c\u6298\u94c1\u5200\u672a\u66fe\u65a9\u4e0b\uff0c\u91d1\u9010\u6d41\u65e9\u5df2\u5230\u4e86\u53f2\u7ea2\u82f1\u7684\u8eab\u65c1\u3002 \u3000\u3000\u91d1\u9010\u6d41\u968f\u624b\u4e00\u62db\u201c\u516b\u65b9\u98ce\u96e8\u201d\uff0c\u53cc\u638c\u8d77\u843d\u5982\u73af\uff0c\u638c\u529b\u5411\u56db\u9762\u516b\u65b9\u53cd\u51fb\u51fa\u53bb\uff0c\u5e84\u8fdc\u53ea\u89c9\u4e00 \u80a1\u67d4\u548c\u7684\u638c\u529b\u7a81\u7136\u6321\u5728\u81ea\u5df1\u9762\u524d\uff0c\u8fd9\u80a1\u638c\u529b\u867d\u7136\u5e76\u4e0d\u9738\u9053\uff0c\u4f46\u5e84\u8fdc\u672c\u8eab\u6240\u53d1\u7684\u638c\u529b\u5982\u7ed9\u8361\u4e86 \u56de\u6765\uff0c\u53cd\u9707\u81ea\u8eab\uff0c\u4e0d\u7531\u5f97\u5012\u9000\u4e09\u6b65\uff0c\u539f\u6765\u91d1\u9010\u6d41\u7528\u7684\u662f\u53ea\u5b88\u4e0d\u653b\u7684\u5927\u987b\u5f25\u638c\u5f0f\uff0c\u867d\u7136\u4e5f\u80fd\u53cd \u51fb\u5bf9\u65b9\uff0c\u4f46\u5374\u4e0d\u80fd\u4f24\u4eba\u7684\u3002 \u3000\u3000\u8fd9\u80a1\u638c\u529b\u662f\u5411\u56db\u9762\u516b\u65b9\u53cd\u8d70\u51fa\u53bb\u7684\uff0c\u4e0d\u4f46\u9707\u9000\u4e86\u79e6\u51b2\uff0c\u56db\u5468\u7684\u5e2e\u4f17\u4e5f\u7ed9\u8fd9\u80a1\u638c\u529b\u63a8\u52a8\uff0c \u4e0d\u7ea6\u800c\u540c\u7684\u90fd\u5411\u540e\u9000\uff0c\u5305\u56f4\u7684\u5708\u5b50\u767b\u65f6\u6269\u5927\u3002 \u3000\u3000\u79e6\u51b2\u5927\u6012\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u7ed9\u4f60\u9762\u5b50\u4f60\u4e0d\u8981\uff0c\u8fd9\u4f60\u53ef\u5c31\u83ab\u602a\u6211\u8981\u5bf9\u4e0d\u4f4f\u4f60\u4e86\uff01\u201d\u731b\u5730\u6251 \u6765\uff0c\u4e00\u5200\u65a9\u4e0b\u3002\u4e0d\u8fc7\u4ed6\u53e3\u91cc\u9a82\u7684\u662f\u91d1\u9010\u6d41\uff0c\u5200\u950b\u5374\u662f\u671d\u7740\u53f2\u7ea2\u82f1\u63d2\u53bb\u7684\u3002\u4ed6\u5bf9\u6c5f\u5927\u4fa0\u7684\u5e08 \u5f1f\uff0c\u8fd8\u662f\u4e0d\u80fd\u4e0d\u6709\u70b9\u513f\u987e\u5fcc\u3002 \u3000\u3000\u91d1\u9010\u6d41\u542c\u5f97\u80cc\u540e\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\uff0c\u4e0d\u7ba1\u4ed6\u662f\u5411\u8c01\u65ab\u6765\uff0c\u90fd\u4e0d\u80fd\u4e0d\u51fa\u624b\u4e86\u3002\u5f53\u4e0b\uff0c\u91d1\u9010\u6d41\u5934 \u4e5f\u4e0d\u56de\uff0c\u968f\u624b\u593a\u8fc7\u4e00\u540d\u5e2e\u4f17\u7684\u9752\u94dc\u950f\uff0c\u8fd9\u67c4\u950f\u6b63\u662f\u5728\u4ed6\u524d\u9762\u6266\u6765\u7684\uff0c\u593a\u8fc7\u4e86\u950f\uff0c\u53cd\u624b\u4e00\u64a9\uff0c \u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u79e6\u51b2\u7684\u6298\u94c1\u5200\u98de\u4e0a\u4e86\u534a\u7a7a\uff0c\u4f46\u8fd9\u67c4\u9752\u94dc\u950f\u4e5f\u7ed9\u4ed6\u5288\u5f00\u4e24\u534a\u3002\u91d1\u9010\u6d41\u4e3e\u950f\u4e00 \u64a9\uff0c\u7acb\u5373\u629b\u5f00\uff0c\u6ca1\u6709\u7ed9\u4ed6\u4f24\u7740\uff0c\u5bf9\u79e6\u51b2\u7684\u6c14\u529b\uff0c\u4e5f\u662f\u76f8\u5f53\u4f69\u670d\u3002 \u3000\u3000\u7ea2\u7f28\u4f1a\u7684\u5e2e\u4f17\u89c1\u8fd9\u67c4\u6298\u94c1\u5200\u5728\u7a7a\u4e2d\u843d\u4e0b\uff0c\u4e0d\u7981\u90fd\u662f\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u95ea\u8eb2\u3002 \u3000\u3000\u91d1\u3001\u53f2\u4e8c\u4eba\u624b\u633d\u7740\u624b\uff0c\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u4f7f\u51fa\u4e86\u201c\u6bd4\u7ffc\u53cc\u98de\u201d\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u6377\u5982\u98de\u9e1f\u822c \u7684\u4ece\u4f17\u4eba\u5934\u9876\u8d8a\u8fc7\uff0c\u843d\u5728\u4e00\u5ea7\u7b14\u67b6\u5f62\u7684\u77f3\u53f0\u4e4b\u4e0a\u3002 \u3000\u3000\u79e6\u51b2\u4e00\u7eb5\u8eab\u6293\u7740\u90a3\u67c4\u8dcc\u4e0b\u6765\u7684\u5355\u5200\uff0c\u6c14\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u6307\u7740\u91d1\u9010\u6d41\u559d\u9053\uff1a\u201c\u6709\u79cd\u7684\u4f60\u522b \u8dd1\uff0c\u54b1\u4eec\u518d\u6765\u51b3\u4e2a\u96cc\u96c4\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u662f\u7279\u5730\u5730\u62dc\u89c1\u8d35\u4f1a\u7684\u603b\u8235\u4e3b\u7684\uff0c\u516c\u5b59\u8235\u4e3b\u672a\u66fe\u89c1\u7740\uff0c\u4f60\u8d76\u6211\u6211\u4e5f\u4e0d \u8dd1\uff01\u4e0d\u8fc7\uff0c\u4f60\u6211\u65e0\u51a4\u65e0\u4ec7\uff0c\u6211\u53c8\u4f55\u5fc5\u4e0e\u4f60\u51b3\u751a\u96cc\u96c4\uff1f\u201d \u3000\u3000\u79e6\u51b2\u6012\u9053\uff1a\u201c\u516c\u5b59\u8235\u4e3b\u4e0d\u89c1\u4f60\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u5373\u4f7f\u4ed6\u5f53\u771f\u4e0d\u80af\u89c1\u6211\uff0c\u6211\u4e5f\u5f97\u95ee\u4ed6\u4e00\u58f0\u3002\u201d \u3000\u3000\u5e84\u8fdc\u505a\u597d\u505a\u6b79\u5730\u529d\u9053\uff1a\u201c\u91d1\u5c11\u4fa0\uff01\u4ee4\u5c0a\u4e0e\u4ee4\u5e08\u5144\u4e0e\u655d\u5e2e\u4e43\u662f\u4e24\u4ee3\u4ea4\u60c5\uff0c\u4f60\u65e2\u7136\u4e0d\u662f\u84c4\u610f \u548c\u6211\u4eec\u4f5c\u5bf9\uff0c\u6211\u4eec\u4e5f\u4e0d\u80fd\u96be\u4e3a\u4f60\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u83ab\u7ba1\u95f2\u4e8b\uff0c\u8d81\u65e9\u8d70\u5427\u3002\u4f60\u662f\u65e0\u8bba\u5982\u4f55\u4e0d\u80fd\u89c1\u7740 \u6211\u4eec\u8235\u4e3b\u7684\u4e86\uff0c\u6211\u4eec\u4e0d\u4f1a\u66ff\u4f60\u901a\u62a5\u7684\u3002\u201d \u3000\u3000\u5e84\u8fdc\u7684\u6b66\u5b66\u9020\u8be3\u6bd4\u79e6\u51b2\u9ad8\u5f97\u591a\uff0c\u521a\u624d\u91d1\u9010\u6d41\u7528\u5927\u987b\u5f25\u638c\u5200\u5c06\u4ed6\u9707\u9000\uff0c\u4ed6\u5df2\u77e5\u9053\u91d1\u9010\u6d41\u4e43 \u662f\u624b\u4e0b\u7559\u60c5\uff0c\u4e0d\u80af\u4f24\u4ed6\u3002\u4ed6\u963b\u6b62\u79e6\u51b2\u4e0e\u91d1\u9010\u6d41\u52a8\u6b66\uff0c\u56fa\u7136\u662f\u56e0\u4e3a\u660e\u77e5\u79e6\u51b2\u7edd\u975e\u654c\u624b\uff0c\u4f46\u4e5f\u662f \u56e0\u4e3a\u77e5\u9053\u91d1\u9010\u6d41\u5e76\u65e0\u654c\u610f\u7684\u7f18\u6545\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u591a\u8c22\u597d\u610f\uff0c\u4f46\u6211\u89c1\u4e0d\u7740\u516c\u5b59\u524d\u8f88\uff0c\u6211\u4e5f\u662f\u65e0\u8bba\u5982\u4f55\u4e0d\u80fd\u8d70\u7684\u3002\u4e0d\u52b3\u4f60\u4eec\u901a \u62a5\uff0c\u6211\u81ea\u5df1\u901a\u540d\u6c42\u89c1\u5c31\u662f\u3002\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u84e6\u5730\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u91d1\u9010\u6d41\u3001\u53f2\u7ea2\u82f1\u6c42\u89c1\u516c\u5b59\u8235\u4e3b\uff0c\u4e0d\u77e5\u4f55\u6545\uff0c\u8d35\u4f1a\u9999\u4e3b\u52a0\u4ee5\u963b \u62e6\uff0c\u8bf7\u516c\u5b59\u8235\u4e3b\u51c6\u4e88\u62dc\u8c12\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7528\u7684\u662f\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u5185\u529f\uff0c\u58f0\u97f3\u5e76\u4e0d\u5f88\u5927\uff0c\u4f46\u5374\u9707\u5f97\u4f17\u4eba\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\u3002\u91d1\u9010 \u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u542c\u4ed6\u4eec\u7684\u53e3\u6c14\uff0c\u516c\u5b59\u5b8f\u4f3c\u4e4e\u906d\u4e86\u4ec0\u4e48\u610f\u5916\uff0c\u4e5f\u53ef\u80fd\u662f\u6b63\u5728\u75c5\u4e2d\u3002\u4f46\u53ea\u8981\u4ed6\u5728 \u5bb6\u91cc\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u542c\u5230\u6211\u7684\u58f0\u97f3\u3002\u201d \u3000\u3000\u7ea2\u7f28\u4f1a\u8bf8\u4eba\u7ed9\u91d1\u9010\u6d41\u7528\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u5185\u529f\u9707\u5f97\u55e1\u55e1\u4f5c\u53eb\u3002\u65e0\u4e0d\u9a87\u7136\u5931\u8272\uff0c\u91d1\u9010\u6d41\u6709\u610f \u70ab\u9732\u6b66\u529f\uff0c\u4e00\u4e0d\u505a\u4e8c\u4e0d\u4f11\uff0c\u62d4\u51fa\u4e86\u7384\u94c1\u5b9d\u5251\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u8fd9\u5757\u77f3\u5934\u4e0d\u597d\u5750\uff0c\u6211\u53ea\u597d\u591a\u8d39 \u70b9\u529f\u592b\u4e86\u3002\u201d\u6325\u52a8\u7384\u94c1\u5b9d\u5251\uff0c\u4e00\u9635\u4e71\u524a\uff0c\u53ea\u89c1\u5251\u5149\u98de\u821e\uff0c\u77f3\u5c51\u7eb7\u98de\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u90a3\u9675\u7b14\u67b6\u5f62 \u7684\u5927\u77f3\u5934\uff0c\u51f8\u51fa\u7684\u68f1\u89d2\uff0c\u90fd\u5df2\u7ed9\u524a\u5f97\u5e73\u5e73\u6574\u6574\uff0c\u7b14\u67b6\u5f62\u7684\u77f3\u53f0\uff0c\u53d8\u6210\u4e00\u9762\u7855\u5927\u65e0\u6bd4\u7684\u660e\u955c\uff01 \u3000\u3000\u79e6\u51b2\u672c\u6765\u5df2\u662f\u7387\u9886\u4e86\u5e2e\u4f17\uff0c\u56f4\u7740\u77f3\u53f0\uff0c\u60f3\u8981\u6349\u62ff\u53f2\u7ea2\u82f1\u7684\u3002\u6b64\u9645\uff0c\u89c1\u4e86\u7384\u94c1\u5b9d\u5251\u7684\u5a01 \u529b\uff0c\u65e0\u4e0d\u5413\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u4e0d\u5f85\u5e84\u8fdc\u529d\u963b\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u6562\u5192\u6627\u4e0a\u524d\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u7ea2\u82f1\uff0c\u54b1\u4eec\u5c31\u6682\u4e14\u6b47\u4e00\u4f1a\u513f\uff0c\u7b49\u5019\u516c\u5b59\u8235\u4e3b\u4f20\u89c1\u5427\u3002\u201d\u4e24\u4eba \u5927\u9a6c\u91d1\u5200\u5730\u5750\u4e86\u4e0b\u6765\uff0c\u6c14\u5f97\u79e6\u51b2\u6562\u6012\u800c\u4e0d\u6562\u8a00\u3002 \u3000\u3000\u91d1\u9010\u6d41\u901a\u540d\u6c42\u89c1\u4e4b\u540e\uff0c\u4e0d\u5230\u534a\u67f1\u9999\u7684\u65f6\u523b\uff0c\u679c\u7136\u4fbf\u6709\u4e00\u4e2a\u4eba\u51fa\u6765\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u91d1\u5c11 \u4fa0\uff0c\u655d\u4f1a\u516c\u5b59\u8235\u4e3b\u8bf7\u4f60\u8fdb\u53bb\u3002\u201d\u8fd9\u4eba\u662f\u5728\u7ea2\u7f28\u4f1a\u5750\u7b2c\u4e94\u628a\u4ea4\u6905\u7684\u5185\u4e09\u5802\u9999\u4e3b\u77f3\u7384\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4f4d\u53f2\u59d1\u5a18\u5462\uff1f\u201d\u77f3\u572d\u8bf4\u9053\uff1a\u201c\u53f2\u59d1\u5a18\u8bf7\u5728\u5916\u9762\u7a0d\u5019\uff0c\u516c\u5b59\u8235\u4e3b\u60f3\u4e0e\u91d1\u5c11 \u4fa0\u5355\u72ec\u8c08\u8c08\u3002\u201d\u91d1\u9010\u6d41\u6e38\u76ee\u56db\u987e\uff0c\u89c1\u79e6\u51b2\u7b49\u4eba\u7684\u8138\u4e0a\u9887\u6709\u60bb\u60bb\u4e4b\u8272\uff0c\u91d1\u9010\u6d41\u5b9e\u5728\u6709\u70b9\u653e\u5fc3\u4e0d \u4e0b\uff0c\u6697\u81ea\u601d\u91cf\uff1a\u201c\u5355\u72ec\u7559\u4e0b\u7ea2\u82f1\uff0c\u8981\u662f\u8fd9\u4e9b\u4eba\u4e0e\u5979\u4e3a\u96be\uff0c\u5c82\u4e0d\u7cdf\u7cd5\u3002\u201d \u3000\u3000\u77f3\u7384\u4f3c\u4e4e\u77e5\u9053\u91d1\u9010\u6d41\u7684\u5fc3\u91cc\uff0c\u8ddf\u7740\u5c31\u9053\uff1a\u201c\u8235\u4e3b\u6709\u4ee4\uff0c\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\u53f2\u767d\u90fd\u867d\u662f\u54b1\u4eec\u7684 \u4ec7\u4eba\uff0c\u5374\u4e0d\u5e94\u8fc1\u6012\u5230\u4ed6\u59b9\u59b9\u8eab\u4e0a\u3002\u53f2\u59d1\u5a18\u65e2\u7136\u662f\u4e0e\u91d1\u5c11\u4fa0\u540c\u6765\u51fa\u95e8\uff0c\u5bf9\u5979\u4e5f\u5e94\u8be5\u597d\u597d\u7684\u4ee5\u793c \u76f8\u5f85\u3002\u201d\u5e84\u8fdc\u4f4e\u5934\u5e94\u4e86\u4e00\u4e2a\u201c\u662f\u201d\u5b57\uff0c\u79e6\u51b2\u867d\u4e0d\u5e94\u58f0\uff0c\u5374\u4e5f\u4e0d\u6562\u518d\u8bf4\u8bdd\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\uff0c\u5f53\u4e0b\u5c31\u8ddf\u77f3\u7384\u8d70\u8fdb\u516c\u5b59\u5b8f\u7684\u4f4f\u5b85\u3002\u9014\u4e2d\uff0c\u91d1\u9010\u6d41\u8bf7\u6559\u4ed6\u7684\u59d3\u540d\uff0c \u59cb\u77e5\u77f3\u7384\u5c31\u662f\u548c\u79e6\u5143\u6d69\u540c\u65f6\u5728\u6c34\u4e91\u5e84\u505a\u8fc7\u5ba2\u4eba\u7684\u90a3\u4f4d\u77f3\u9999\u4e3b\uff0c\u6c34\u4e91\u5e84\u5e84\u4e3b\u7684\u5973\u513f\u4e91\u4e2d\u71d5\u88ab\u5927 \u76d7\u7f57\u5927\u9b41\u6043\u5f3a\u8feb\u5a5a\uff0c\u4ed6\u4e0e\u79e6\u5143\u6d69\u66fe\u7ecf\u5e2e\u8fc7\u4e91\u5e84\u4e3b\u5f88\u5927\u7684\u5fd9\u3002 \u3000\u3000\u91d1\u9010\u6d41\u77e5\u9053\u4ed6\u662f\u77f3\u7384\u4e4b\u540e\uff0c\u5bf9\u4ed6\u5f88\u6709\u597d\u611f\uff0c\u5fc3\u60f3\u77f3\u7384\u662f\u4e2a\u5fe0\u539a\u6b63\u76f4\u7684\u4eba\uff0c\u6216\u8005\u4f1a\u8bf4\u5b9e \u8bdd\uff0c\u56e0\u6b64\u95ee\u4ed6\u9053\uff1a\u201c\u8d35\u4f1a\u4e0e\u516d\u5408\u5e2e\u7d20\u6709\u9699\u5acc\uff0c\u8fd9\u4e2a\u6211\u4e5f\u77e5\u9053\u3002\u4f46\u8fd9\u4e5f\u662f\u7531\u6765\u5df2\u4e45\u7684\u4e86\u3002\u4eca\u65e5 \u8d24\u4f1a\u51e0\u4f4d\u9999\u4e3b\u5bf9\u53f2\u59d1\u5a18\u597d\u4f3c\u5341\u5206\u75db\u6068\uff0c\u4f3c\u4e4e\u4e0d\u8be5\u662f\u7531\u4e8e\u4e24\u5e2e\u7684\u65e7\u6028\u800c\u8d77\uff0c\u4e0d\u77e5\u662f\u5426\u53e6\u6709\u539f \u56e0\uff1f\u201d \u3000\u3000\u77f3\u7384\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u73b0\u5728\u8fd8\u4e0d\u4fbf\u8bf4\uff0c\u91d1\u5c11\u4fa0\u89c1\u4e86\u516c\u5b59\u8235\u4e3b\uff0c\u8235\u4e3b\u60f3\u662f\u4f1a\u544a\u8bc9\u4f60\u7684\u3002\u201d\u91d1\u9010 \u6d41\u9053\uff1a\u201c\u8bf7\u6055\u6211\u80e1\u4e71\u731c\u7591\uff0c\u516c\u5b59\u8235\u4e3b\u4e0d\u77e5\u662f\u5426\u75c5\u4e86\uff1f\u201d\u77f3\u7384\u8bf4\u9053\uff1a\u201c\u8981\u8bf4\u662f\u75c5\u4e5f\u672a\u5c1d\u4e0d\u53ef\uff0c \u53cd\u6b63\u4f60\u5c31\u53ef\u4ee5\u89c1\u7740\u4ed6\u4e86\u3002\u201d\u8fd9\u6837\u542b\u7cca\u7684\u7b54\u590d\uff0c\u4ee4\u5f97\u91d1\u9010\u6d41\u66f4\u8d77\u7591\u5fc3\uff0c\u5fc3\u60f3\uff1a\u201c\u75c5\u5c31\u662f\u75c5\uff0c\u4ec0 \u4e48\u53eb\u505a\u672a\u5c1d\u4e0d\u53ef\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u6000\u7591\u6ee1\u8179\uff0c\u4f46\u77f3\u7384\u65e2\u4e0d\u80af\u591a\u8bf4\uff0c\u4ed6\u81ea\u4e5f\u4e0d\u4fbf\u518d\u95ee\u3002\u77f3\u7384\u5e26\u9886\u4ed6\u81f3\u516c\u5b59\u5b8f\u7684\u5367\u5e8a\uff0c \u4fbf\u5373\u9000\u4e0b\u3002 \u3000\u3000\u53ea\u89c1\u516c\u5b59\u5b8f\u8eba\u5728\u5e8a\u4e0a\uff0c\u9762\u5982\u9ec4\u8721\uff0c\u7709\u5fc3\u6709\u4e00\u80a1\u6de1\u6de1\u7684\u9ed1\u6c14\uff0c\u91d1\u9010\u6d41\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u624d\u77e5\u516c \u5b59\u5b8f\u662f\u4e2d\u4e86\u6bd2\u3002\u201c\u96be\u9053\u4ed6\u4e5f\u662f\u53d7\u4e86\u90a3\u4e2a\u5929\u9b54\u6559\u65b0\u6559\u4e3b\u7684\u6697\u7b97\u4e0d\u6210\uff1f\u4f46\u4ed6\u8fd9\u4e48\u9ad8\u5f3a\u7684\u6b66\u529f\uff0c\u5c82 \u80fd\u8f7b\u6613\u53d7\u4eba\u6697\u7b97\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u6709\u6c14\u6ca1\u5200\u4ed6\u8bf4\u9053\uff1a\u201c\u9010\u6d41\uff0c\u4f60\u6765\u4e86\uff0c\u5f88\u597d\u3002\u5750\u4e0b\u6765\u5427\uff0c\u54b1\u4eec\u8c08\u8c08\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u884c\u8fc7\u4e86\u793c\uff0c\u6b63\u60f3\u95ee\u4ed6\uff0c\u516c\u5b59\u5b8f\u5df2\u5148\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5b9a\u6709\u7591\u56e2\uff0c\u60f3\u8981\u95ee\u6211\uff0c\u6211\u4e5f \u6b63\u6709\u51e0\u4e2a\u7591\u95ee\uff0c\u60f3\u8981\u95ee\u4f60\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u77e5\u516c\u5b59\u524d\u8f88\u60f3\u8981\u77e5\u9053\u4ec0\u4e48\uff1f\u201d\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u4f60\u662f\u4ece\u897f\u660c\u90a3\u513f\u6765\u7684\u5427\uff1f\u201d \u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u6211\u6709\u4e2a\u5973\u513f\u540d\u53eb\u516c\u5b59\u71d5\uff0c\u4e5f\u5728\u897f\u660c\uff0c\u4e0d\u77e5\u4f60\u89c1\u8fc7\u5979\u6ca1 \u6709\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5df2\u9887\u611f\u8e0c\u8e87\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u672c\u6765\u662f\u8981\u66ff\u5bab\u79c9\u85e9\u62a5\u4fe1\u7684\uff0c\u4f46\u60f3\u4e0d\u5230\u516c\u5b59\u524d\u8f88\u4f1a\u4e2d \u4e86\u6bd2\uff0c\u6b64\u9645\u4ed6\u6b63\u5728\u75c5\u4e2d\uff0c\u6211\u82e5\u628a\u4ed6\u5973\u513f\u7684\u4e0d\u5e78\u6d88\u606f\u544a\u8bc9\u4ed6\uff0c\u53ea\u6015\u4f1a\u52a0\u91cd\u4ed6\u7684\u75c5\u60c5\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u6211\u7684\u5973\u513f\u5df2\u906d\u4e0d\u5e78\u4e86\u4e48\uff1f\u91d1\u5c11\u4fa0\uff0c\u4f60\u4e0d\u5fc5\u7792\u6211\uff0c\u6211\u5df2\u7ecf \u77e5\u9053\u4e86\uff0c\u53ea\u662f\u6211\u8fd8\u5b58\u7740\u4e00\u7ebf\u5e0c\u671b\uff0c\u5e0c\u671b\u8fd9\u6d88\u606f\u4e0d\u662f\u771f\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e0d\u89c9\u597d\u751f\u7eb3\u7f55\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u6d88\u606f\u662f\u8c01\u544a\u8bc9\u4ed6\u7684\uff1f\u9664\u4e86\u6211\u4e0e\u7ea2\u82f1\u4e4b\u5916\uff0c\u4e49\u519b\u4e2d \u4eba\uff0c\u53ef\u5e76\u6ca1\u6709\u8c01\u56de\u6765\u554a\uff01\u201d\u4f46\u542c\u5f97\u516c\u5b59\u5b8f\u5df2\u7ecf\u77e5\u9053\uff0c\u53ea\u597d\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u8001\u524d\u8f88\u65e2\u7136\u4e1a\u5df2\u77e5 \u9053\uff0c\u90a3\u6211\u5c31\u4e0d\u7528\u8bf4\u4e86\u3002\u4f46\u6211\u66fe\u7ecf\u5728\u6843\u82b1\u8c37\u4e2d\u627e\u8fc7\u4ee4\u5a9b\uff0c\u5374\u5e76\u672a\u53d1\u73b0\u4ee4\u5a9b\u7684\u5c38\u8eab\uff0c\u8bf4\u4e0d\u5b9a\u6b63\u5982 \u8001\u524d\u8f88\u6240\u8bf4\uff0c\u8fd8\u6709\u4e00\u7ebf\u5e0c\u671b\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u83ab\u540d\u5176\u5999\uff0c\u8be7\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u6211\u7684\u5973\u513f\u4e0d\u662f\u5728\u897f\u660c\u57ce\u4e2d\u6b7b\u7684\u4e48\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u66f4\u662f\u5947\u602a\uff0c\u8fde\u5fd9\u95ee\u9053\uff1a\u201c\u516c\u5b59\u524d\u8f88\uff0c\u4f60\u542c\u5230\u7684\u662f\u4ec0\u4e48\u6837\u7684\u6d88\u606f\uff1f\u201d\u516c\u5b59\u5b8f\u9053\uff1a \u201c\u5979\u82e5\u4e0d\u662f\u5728\u897f\u660c\u6b7b\u7684\uff0c\u5bb3\u6b7b\u5979\u7684\u53c8\u662f\u4f55\u4eba\uff1f\u201d \u3000\u3000\u539f\u6765\u4e24\u4eba\u90fd\u662f\u6025\u4e8e\u77e5\u9053\u771f\u76f8\uff0c\u4e0d\u89c9\u540c\u65f6\u53d1\u95ee\u3002 \u3000\u3000\u91d1\u9010\u6d41\u60c5\u77e5\u5185\u4e2d\u8d70\u6709\u8df7\u8e4a\uff0c\u5148\u56de\u7b54\u9053\uff1a\u201c\u662f\u9633\u6d69\u903c\u5f97\u4ee4\u5a9b\u548c\u5389\u5357\u661f\u5760\u4e0b\u6df1\u8c37\u7684\uff01\u201d \u3000\u3000\u516c\u5b59\u5b8f\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u8bf4\u6211\u5973\u513f\u7684\u4ec7\u4eba\u662f\u9633\u6d01\uff1f\u5389\u5357\u661f\u4e5f\u4e0e\u6211\u7684\u5973\u513f\u540c\u65f6\u9047\u5bb3\u3002 \u8fd9\uff0c\u8fd9\u672a\u514d\u592a\u96be\u4ee4\u4eba\u76f8\u4fe1\u4e86\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u90a3\u4e48\u636e\u8001\u524d\u8f88\u6240\u77e5\uff0c\u8fd9\u4ec7\u4eba\u5374\u53c8\u662f\u8c01\uff1f\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u4e0d\u662f\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\u53f2\u767d\u90fd\u5417\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u6d88\u606f\u662f\u8c01\u544a\u8bc9\u4f60\u7684\uff1f\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u5c31\u662f\u5389\u5357\u661f\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u4e3a\u60ca\u8be7\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u89c1\u5230\u7684\u5f53\u771f\u662f\u5389\u5357\u661f\u5417\uff1f\u201d \u3000\u3000\u516c\u5b59\u5b8f\u62c2\u7136\u4e0d\u60a6\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u5728\u5341\u5929\u4e4b\u524d\uff0c\u6211\u66fe\u4e0e\u4ed6\u76f8\u4f1a\uff0c\u4ed6\u4eb2\u53e3\u5bf9\u6211\u8bf4\u7684\uff0c\u7109\u80fd\u6709 \u5047\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u5b59\u524d\u8f88\uff0c\u4f60\u597d\u50cf\u662f\u4e2d\u4e86\u6bd2\uff0c\u8fd9\u6bd2\u53c8\u662f\u8c01\u4eba\u4e0b\u7684\uff1f\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u4e5f\u662f\u5389\u5357\u661f\u6240\u4e0b\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u53ef\u4ee4\u665a\u8f88\u7cca\u6d82\u4e86\uff0c\u5389\u5357\u661f\u65e2\u7136\u66ff\u4f60\u4f20\u4fe1\uff0c\u90a3\u662f\u51fa\u4e8e\u597d\u610f\u7684\u4e86\uff0c\u4f55\u4ee5\u53c8\u6697 \u4e2d\u4e0b\u6bd2\uff1f\u201d \u3000\u3000\u516c\u5b59\u5b8f\u662f\u4e2a\u8001\u7ecf\u4e16\u6545\u7684\u4eba\uff0c\u542c\u5f97\u91d1\u9010\u6d41\u8fd9\u6837\u8bf4\uff0c\u4ea6\u5df2\u731c\u60f3\u5f97\u5230\u5185\u4e2d\u5b9a\u6709\u8e4a\u8df7\uff0c\u5f53\u4e0b\u8bf4 \u9053\uff1a\u201c\u6b64\u4e8b\u8bf4\u6765\u8bdd\u957f\u3002\u54b1\u4eec\u8fd8\u662f\u4e00\u6b65\u4e00\u6b65\u5f04\u6e05\u771f\u76f8\u5427\u3002\u91d1\u5c11\u4fa0\uff0c\u4f60\u8bf4\u6211\u7684\u5973\u513f\u662f\u9633\u6d69\u5bb3\u6b7b \u7684\uff0c\u662f\u4f60\u4eb2\u773c\u89c1\u5230\u7684\u5417\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u662f\u5bab\u79c9\u85e9\u4eb2\u773c\u89c1\u5230\u7684\u3002\u903c\u53f2\u767d\u90fd\u81ea\u6740\u8eab\u4ea1\uff0c\u5219\u662f\u6211\u4eb2\u773c\u89c1\u5230\u7684\u3002\u53f2\u767d\u90fd\u6b7b \u7684\u65f6\u5019\uff0c\u4ee4\u5a9b\u53ef\u8fd8\u662f\u6d3b\u7740\u7684\u554a\uff01\u56e0\u6b64\u4e0d\u7ba1\u90a3\u4e2a\u5929\u9b54\u6559\u7684\u65b0\u6559\u4e3b\u662f\u5426\u771f\u7684\u5389\u5357\u661f\uff0c\u4ed6\u544a\u8bc9\u8001\u524d \u8f88\u7684\u8fd9\u4e2a\u6d88\u606f\uff0c\u5219\u7edd\u5bf9\u662f\u5047\u7684\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u65e2\u7136\u662f\u5bab\u79c9\u85e9\u4eb2\u773c\u89c1\u5230\u7684\uff0c\u4ed6\u4e3a\u4f55\u4e0d\u81ea\u5df1\u56de\u6765\u62a5\u4fe1\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4e5f\u53d7\u4e86\u9633\u6d69\u7684\u4fee\u7f57\u9634\u715e\u529f\u4e4b\u4f24\u3002\u201d\u5f53\u4e0b\u5c06\u5bab\u79c9\u85e9\u90a3\u65e5\u7684\u906d\u9047\uff0c\u548c\u81ea \u5df1\u5728\u6843\u82b1\u8c37\u4e2d\u7684\u6240\u89c1\uff0c\u539f\u539f\u672c\u672c\u5730\u544a\u8bc9\u4e86\u516c\u5b59\u5b8f\u3002 \u3000\u3000\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u82e5\u662f\u4ece\u53e6\u4e00\u4e2a\u4eba\u53e3\u4e2d\u8bf4\u51fa\uff0c\u516c\u5b59\u5b8f\u4e00\u5b9a\u4e0d\u4f1a\u76f8\u4fe1\uff1b\u4f46\u5982\u4eca\u662f\u91d1\u9010\u6d41\u544a\u8bc9\u4ed6\uff0c \u4ed6\u77e5\u9053\u91d1\u9010\u6d41\u662f\u51b3\u4e0d\u4f1a\u8bf4\u8c0e\u7684\uff0c\u662f\u4ee5\u867d\u7136\u8be7\u5f02\u4e4b\u6781\uff0c\u4e5f\u662f\u4e0d\u80fd\u4e0d\u4fe1\u4e86\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u6211\u662f\u5f53\u771f\u4e0a\u4e86\u4ed6\u4eec\u7684\u5f53\u4e86\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u542c\u8bf4\u6709\u4e00\u79cd \u6539\u5bb9\u6613\u8c8c\u4e4b\u672f\uff0c\u7cbe\u901a\u6b64\u672f\u4e4b\u4eba\u53ef\u4ee5\u626e\u5f97\u50cf\u53e6\u4e00\u4e2a\u4eba\uff0c\u7ef4\u5999\u7ef4\u8096\u3002\u8001\u524d\u8f88\u6240\u89c1\u7684\u90a3\u4e2a\u5389\u5357\u661f\uff0c \u6211\u60f3\u4e00\u5b9a\u4e0d\u662f\u771f\u7684\u3002\u201d\u516c\u5b59\u5b8f\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u4f60\u8fd9\u4e48\u4e00\u8bf4\uff0c\u6211\u662f\u6709\u70b9\u7591\u5fc3\u4e86\uff0c\u597d\uff0c\u6211 \u5c31\u628a\u90a3\u65e5\u7684\u7ecf\u8fc7\u544a\u8bc9\u4f60\u5427\uff0c\u54b1\u4eec\u4e00\u540c\u53c2\u8be6\u53c2\u8be6\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u6b47\u4e86\u4e00\u6b47\uff0c\u559d\u4e86\u534a\u7897\u53c2\u6c64\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u53bb\u5e74\u6211\u548c\u5c0f\u5973\u5728\u957f\u6c5f\u8fb9\u78b0\u89c1\u5389\u5357\u661f\u4e0e\u5c01 \u5999\u5ae6\uff0c\u5c0f\u5973\u8ddf\u968f\u4ed6\u4eec\u540c\u5f80\u897f\u660c\uff0c\u6b64\u4e8b\u60f3\u5fc5\u4f60\u5df2\u77e5\u9053\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5c01\u59d1\u5a18\u5df2\u7ecf\u544a\u8bc9\u6211\u4e86\u3002\u90a3\u65e5\u5389\u5357\u661f\u8eab\u4e0a\u8d1f\u4f24\uff0c\u6597\u4e0d\u8fc7\u6587\u9053\u5e84 \u90a3\u4e2a\u5b9d\u8d1d\u513f\u5b50\uff0c\u5979\u4e5f\u51e0\u4e4e\u843d\u5728\u6587\u80dc\u4e2d\u7684\u624b\u4e0a\uff0c\u5e78\u4e8f\u5f97\u4ee4\u5a9b\u62d4\u5251\u76f8\u52a9\uff0c\u8d76\u8dd1\u4e86\u6587\u80dc\u4e2d\u3002\u8bf4\u8d77\u6b64 \u4e8b\uff0c\u5c01\u59d1\u5a18\u5bf9\u4f60\u8001\u548c\u4ee4\u5a9b\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u6b64\u7b49\u5c0f\u4e8b\uff0c\u4f55\u8db3\u6302\u9f7f\u3002\u90a3\u4f4d\u5c01\u59d1\u5a18\u597d \u5427\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5c01\u59d1\u5a18\u548c\u6b66\u5f53\u6d3e\u7684\u79e6\u5143\u6d69\u4e0a\u4e2a\u6708\u5df2\u5728\u5927\u51c9\u5c71\u6210\u5a5a\uff0c\u6211\u5c31\u662f\u5728\u559d\u4e86\u4ed6\u4eec\u7684 \u559c\u9152\u4e4b\u540e\u624d\u56de\u6765\u7684\u3002\u4ed6\u4eec\u7684\u59fb\u7f18\u975e\u5e38\u7f8e\u6ee1\uff0c\u6240\u4ee5\u5c01\u59d1\u5a18\u5e38\u8bf4\uff0c\u5979\u4e4b\u5f97\u6709\u4eca\u65e5\uff0c\u90fd\u662f\u51fa\u81ea\u8001\u524d \u8f88\u7236\u5973\u6240\u8d50\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u5fae\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5c01\u59d1\u5a18\u5df2\u7ecf\u5f97\u4e86\u79f0\u5fc3\u7684\u5973\u5a7f\uff0c\u8fd9\u5012\u662f\u53ef\u559c\u53ef\u8d3a\u7684\u7f8e\u4e8b\u3002\u201d\u5fc3 \u91cc\u60f3\u9053\uff1a\u201c\u6211\u53ea\u9053\u8fd9\u4f4d\u5c01\u59d1\u5a18\u548c\u5389\u5357\u661f\u662f\u5bf9\u60c5\u4fa3\uff0c\u539f\u6765\u4e0d\u662f\u3002\u201d\u539f\u6765\u516c\u5b59\u5b8f\u4e5f\u66fe\u6709\u8fc7\u60f3\u628a\u5973 \u513f\u8bb8\u914d\u4e0e\u5389\u5357\u661f\u4e4b\u610f\u7684\uff0c\u53ea\u56e0\u6709\u6b64\u8bef\u4f1a\uff0c\u4e0d\u6562\u51fa\u4e4b\u4e8e\u53e3\u3002\u5982\u4eca\u542c\u8bf4\u5c01\u5999\u5ae6\u4e0e\u79e6\u5143\u6d69\u5df2\u7ecf\u6210 \u5a5a\uff0c\u4e0d\u89c9\u52fe\u8d77\u4ed6\u5f53\u521d\u7684\u8fd9\u6bb5\u5fc3\u4e8b\uff0c\u4f46\u968f\u5373\u60f3\u9053\uff1a\u201c\u6211\u7684\u5973\u513f\u6b7b\u4e86\uff0c\u5389\u5357\u661f\u662f\u771f\u662f\u5047\uff0c\u662f\u5584\u662f \u6076\uff0c\u5982\u4eca\u72b9\u672a\u53ef\u77e5\uff0c\u8fd9\u4e8b\u6211\u8fd8\u60f3\u5b83\u4f5c\u751a\uff1f\u201d\u601d\u5ff5\u53ca\u6b64\uff0c\u4e0d\u89c9\u9ed8\u7136\u3002 \u3000\u3000\u91d1\u9010\u6d41\u77e5\u9053\u4ed6\u5728\u4f24\u5fc3\uff0c\u5b89\u6170\u4ed6\u9053\uff1a\u201c\u5389\u5357\u661f\u82e5\u7136\u672a\u6b7b\uff0c\u4ee4\u5a9b\u5c31\u53ef\u80fd\u8fd8\u5728\u4eba\u95f4\u3002\u5982\u4eca\u54b1\u4eec \u5148\u8981\u67e5\u660e\uff0c\u90a3\u4e2a\u5929\u9b54\u6559\u7684\u65b0\u6559\u4e3b\u2018\u5389\u5357\u661f\u2019\u7a76\u7adf\u662f\u771f\u662f\u5047\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u8fd8\u662f\u56de\u5230\u539f\u6765\u7684\u8bdd\u9898\u5427\u3002\u201d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5c0f\u5973\u548c\u4ed6\u4eec\u53bb\u4e86\u897f \u660c\uff0c\u4e45\u65e0\u97f3\u8baf\uff0c\u6211\u5f88\u6302\u5ff5\uff0c\u4e0d\u6599\u6211\u628a\u5bab\u79c9\u85e9\u6d3e\u5230\u897f\u660c\u4e4b\u540e\uff0c\u5bab\u79c9\u85e9\u8fd8\u672a\u56de\u6765\uff0c\u90a3\u4e00\u65e5\u6211\u5374\u63a5 \u5230\u4e86\u5389\u5357\u661f\u7684\u4e00\u5c01\u8bf7\u67ec\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u9080\u8bf7\u4f60\u89c2\u793c\u7684\u8bf7\u67ec\uff1f\u201d\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u6b63\u662f\u3002\u4e0d\u8fc7\uff0c\u4ed6\u6d3e\u6765\u7684\u4f7f\u8005\u7279 \u522b\u58f0\u660e\uff0c\u8981\u8bf7\u6211\u65e9\u4e24\u5929\u53bb\uff0c\u8bf4\u662f\u6709\u8981\u4e8b\u548c\u6211\u5546\u91cf\u3002\u7ed3\u679c\u6211\u53ea\u662f\u548c\u4ed6\u89c1\u4e00\u9762\uff0c\u89c2\u793c\u5374\u662f\u6ca1\u6709\u4efd \u4e86\u3002\u5509\uff0c\u5176\u4e2d\u7f18\u6545\uff0c\u6211\u4e0d\u8bf4\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\uff0c\u90a3\u662f\u56e0\u4e3a\u6211\u5df2\u7ecf\u4e2d\u4e86\u6bd2\u4e86\uff01\u201d\u60f3\u8d77\u81ea\u5df1\u51e0\u5341\u5e74\u7684 \u9605\u5386\uff0c\u8eab\u4e3a\u6c5f\u6e56\u4e0a\u7b2c\u4e00\u5927\u5e2e\u4f1a\u7684\u603b\u8235\u4e3b\uff0c\u5230\u5934\u6765\u7adf\u7136\u4f1a\u7740\u4e86\u4e00\u4e2a\u5c0f\u5b50\u7684\u6697\u7b97\uff0c\u4e0d\u7981\u82e6\u7b11\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u90a3\u4e2a\u65b0\u6559\u4e3b\u4e0e\u4f60\u5546\u91cf\u7684\u662f\u4ec0\u4e48\u201d\u8981\u4e8b\u2019\uff1f\u4f60\u53c8\u662f\u600e\u6837\u7740\u4e86\u4ed6\u7684\u6697\u7b97\u7684\uff1f\u201d \u3000\u3000\u516c\u5b59\u5b8f\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u5389\u5357\u661f\u63a5\u624b\u5929\u9b54\u6559\u4e4b\u4e8b\uff0c\u6211\u662f\u6781\u4e0d\u8d5e\u540c\u7684\u3002\u5f53\u5e74\u4ed6\u7684\u7236 \u6bcd\u7ec4\u6559\u4e4b\u65f6\uff0c\u6ee5\u6536\u5f92\u4f17\uff0c\u4ee5\u81f3\u9f99\u86c7\u6df7\u6742\uff0c\u826f\u83a0\u4e0d\u9f50\uff0c\u7eb5\u6709\u597d\u4eba\uff0c\u4e5f\u662f\u6781\u96be\u6574\u987f\u3002\u6545\u6b64\u4ee4\u5c0a\u65e9 \u5728\u4e8c\u5341\u5e74\u524d\uff0c\u5c31\u529d\u4ed6\u4eec\u89e3\u6563\u4e86\u3002\u5982\u4eca\u5404\u5904\u90fd\u6709\u4e49\u519b\uff0c\u6c5f\u6e56\u4e0a\u4e5f\u6709\u4e86\u51e0\u4e2a\u5149\u660e\u6b63\u5927\u7684\u5e2e\u4f1a\uff0c\u4f55 \u5fc5\u8fd8\u8981\u8d39\u504c\u5927\u7684\u5fc3\u529b\uff0c\u628a\u65e9\u5df2\u70df\u6d88\u4e91\u6563\u4e86\u7684\u5929\u9b54\u6559\u91cd\u7ec4\u8d77\u6765\uff0c\u5f04\u5f97\u4e0d\u597d\uff0c\u53cd\u4f1a\u7ed9\u5996\u90aa\u4e4b\u8f88\u5229 \u7528\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8001\u524d\u8f88\u8bf4\u5f97\u4e0d\u9519\uff0c\u5389\u5357\u661f\u66fe\u7ecf\u62d2\u7edd\u8fc7\u9633\u6d69\u9080\u4ed6\u91cd\u5f00\u9999\u5802\u4e4b\u8bf7\uff0c\u5c31\u662f\u56e0\u4e3a\u8fd9 \u4e2a\u7f18\u6545\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u65e9\u5c31\u6709\u8fc7\u4e00\u6b21\u8fd9\u6837\u7684\u4e8b\u5417\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u662f\u5440\uff01\u6545\u6211\u4e0d\u76f8\u4fe1\u5389 \u5357\u661f\u4f1a\u5728\u9633\u6d69\u7684\u80c1\u6301\u4e4b\u4e0b\uff0c\u6539\u53d8\u521d\u8877\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u6211\u867d\u4e0d\u8d5e\u540c\u6b64\u4e8b\uff0c\u4f46\u56e0\u6211\u8981\u77e5\u5c0f\u5973\u7684\u6d88\u606f\uff0c\u6240\u4ee5\u6211\u8fd8\u662f\u5982\u4ed6\u6240 \u8bf7\uff0c\u63d0\u65e9\u4e24\u5929\uff0c\u5230\u5f82\u5f95\u5c71\u53bb\u4e0e\u4ed6\u76f8\u4f1a\u3002\u540c\u65f6\uff0c\u6211\u4e5f\u60f3\u529d\u4ed6\u6253\u6d88\u8fd9\u4e2a\u91cd\u5f00\u9999\u5802\u7684\u5ff5\u5934\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u5ff5\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u5230\u4e86\u5f82\u5f95\u5c71\uff0c\u53ef\u89c1\u7740\u4e86\u9633\u6d69\u6ca1\u6709\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u5c31\u662f\u4ed6\u51fa\u6765\u63a5\u5f85\u6211\u7684\u3002\u53ef\u60dc\u6211\u5f53\u65f6\u4e0d\u77e5\u9053\u4ed6\u662f\u5bb3\u6211\u5973\u513f\u7684\u4ec7\u4eba\uff0c\u5426\u5219\u65e9\u628a \u4ed6\u4e00\u638c\u6253\u6b7b\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u9633\u6d69\u548c\u4f60\u600e\u4e48\u8bf4\uff1f\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u4ed6\u8bf4\u5389\u6559\u4e3b\u65b0\u4ece\u897f\u660c\u56de\u6765\uff0c\u4ec6\u4ec6\u98ce\u5c18\uff0c\u9014\u4e2d\u67d3\u75c5\uff0c\u73b0\u5728\u5c1a\u672a\u75ca\u6108\uff0c\u6050\u6015\u4e0d \u80fd\u591a\u8bf4\u8bdd\u3002\u201d\u91d1\u9010\u6d41\u63d2\u53e3\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u8fd9\u91cc\u5c31\u662f\u4e00\u4e2a\u7834\u7efd\u3002\u4ed6\u6050\u6015\u90a3\u4e2a\u5047\u7684\u5389\u5357\u661f\uff0c\u591a\u8bf4\u4e86 \u5c31\u4f1a\u9732\u51fa\u7834\u7efd\uff01\u201d \u3000\u3000\u516c\u5b59\u5b8f\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u5f53\u65f6\u6211\u8bf4\uff0c\u8d35\u6559\u4e3b\u6b20\u5b89\uff0c\u6211\u7406\u8be5\u63a2\u75c5\u3002\u6211\u4e5f\u4e0d\u4f1a\u8981\u4ed6\u591a\u8bf4\u8bdd\u7684\uff0c \u53ea\u60f3\u77e5\u9053\u4ed6\u8981\u548c\u6211\u5546\u91cf\u7684\u662f\u4ec0\u4e48\u8981\u4e8b\uff0c\u4e5f\u5c31\u884c\u4e86\uff0c\u9633\u6d69\u8bf4\u9053\uff1a\u2018\u8fd9\u4e2a\u5f53\u7136\u3002\u8001\u524d\u8f88\u5c48\u9a7e\u5230 \u6b64\uff0c\u655d\u6559\u4e3b\u5c82\u80fd\u4e0d\u89c1\uff1f\u6211\u4e0d\u8fc7\u8bf4\u660e\u4e00\u4e0b\uff0c\u4ee5\u514d\u8001\u524d\u8f88\u602a\u4ed6\u5931\u672d\u800c\u5df2\u3002\u2019\u5f53\u4e0b\u4ed6\u5c31\u966a\u6211\u5230\u5185\u5802 \u4e0e\u5389\u5357\u661f\u76f8\u89c1\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e0d\u7981\u53c8\u518d\u95ee\u9053\uff1a\u201c\u4f60\u770b\u6e05\u695a\uff0c\u5f53\u771f\u662f\u5389\u5357\u661f\uff1f\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u6b64\u4eba\u9762\u5e26\u75c5\u5bb9\uff0c\u76f8\u8c8c\u4e0e\u5389\u5357\u661f\u5012\u662f\u5f88\u50cf\uff0c\u53ea\u662f\u7626\u524a\u4e00\u4e9b\u3002\u6211\u5f53\u65f6\u4ee5\u4e3a\u8fd9\u662f \u56e0\u75c5\u6240\u81f4\uff0c\u6ca1\u6709\u600e\u6837\u7559\u5fc3\u8fa8\u522b\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u91cc\u5fc3\u60f3\u9053\uff1a\u201c\u8fd9\u5c31\u602a\u4e0d\u5f97\u4e86\uff0c\u516c\u5b59\u524d\u8f88\u5148\u540e\u548c\u5389\u5927\u54e5\u4e0d\u8fc7\u89c1\u8fc7\u4e24\u6b21\u9762\uff0c\u4e00\u6b21\u662f\u5927 \u95f9\u8428\u5e9c\u90a3\u5929\uff0c\u5f53\u65f6\u53cc\u65b9\u6b63\u662f\u5728\u6df7\u6218\u4e4b\u4e2d\uff0c\u53ea\u80fd\u7b97\u662f\u5306\u5306\u4e00\u9762\uff1b\u7b2c\u4e8c\u6b21\u662f\u5728\u6c5f\u8fb9\uff0c\u4ed6\u4eec\u7236\u5973\u6551 \u4e86\u5389\u5927\u54e5\u4e4b\u540e\uff0c\u4fbf\u5373\u5206\u9053\u626c\u9573\uff0c\u4ed6\u4e0e\u5389\u5927\u54e5\u867d\u7136\u5df2\u662f\u76f8\u8bc6\uff0c\u4e5f\u8fd8\u672a\u66fe\u7a14\u719f\u3002\u9633\u6d69\u627e\u4e00\u4e2a\u76f8\u8c8c \u76f8\u4f3c\u7684\u4eba\u5192\u5145\u5389\u5927\u54e5\uff0c\u8fd9\u4e2a\u4eba\u53c8\u5047\u88c5\u6709\u75c5\uff0c\u516c\u5b59\u524d\u8f88\u4e8b\u5148\u6ca1\u8d77\u7591\u5fc3\uff0c\u5f53\u7136\u5c31\u5bb9\u6613\u5c06\u4ed6\u9a97\u8fc7 \u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u6211\u60f3\u8d77\u6765\u4e86\uff0c\u9664\u4e86\u76f8\u8c8c\u6bd4\u5389\u5357\u661f\u7626\u524a\u4e4b\u5916\uff0c\u8fd9\u4e2a\u65b0\u6559\u4e3b\u8fd8\u6709 \u4e00\u4e2a\u53ef\u7591\u4e4b\u5904\uff0c\u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\u5636\u54d1\uff0c\u548c\u5386\u5357\u661f\u7684\u53e3\u97f3\u4e5f\u5f88\u4e0d\u76f8\u540c\u3002\u53ef\u60dc\u6211\u5f53\u65f6\u53ea\u9053\u662f\u75c5\u4eba\u5e94 \u6709\u7684\u73b0\u8c61\uff0c\u4e1d\u6beb\u6ca1\u6709\u5bf9\u4ed6\u8d77\u7591\uff0c\u4ee5\u81f4\u906d\u4e86\u4ed6\u7684\u6bd2\u624b\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53ea\u8981\u67e5\u660e\u771f\u76f8\uff0c\u54b1\u4eec\u6162\u6162\u548c\u4ed6\u7b97\u5e10\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u5bf9\uff0c\u4e8b\u60c5\u5df2\u7ecf\u8fc7\u53bb\uff0c\u8ffd\u6094\u4e5f\u662f\u6ca1\u7528\u3002\u6211\u8fd8\u662f\u544a\u8bc9\u4f60\u90a3\u4e00\u5929\u7684\u4e8b\u60c5\u5427\u3002 \u3000\u3000\u201c\u90a3\u4e2a\u65b0\u6559\u4e3b\u8bf4\uff0c\u4ed6\u4e0e\u5c0f\u5973\u5230\u4e86\u897f\u660c\u4e4b\u540e\uff0c\u4fbf\u5373\u53c2\u4e0e\u4e49\u519b\u653b\u6253\u5c06\u519b\u5e9c\u4e4b\u5f79\u3001\u5c0f\u5973\u4e0d\u5e78\u6b7b \u5728\u53f2\u767d\u90fd\u4e4b\u624b\uff0c\u4ed6\u4e5f\u53d7\u4e86\u4f24\uff0c\u5e78\u5f97\u5e08\u53d4\u9633\u6d69\u4e4b\u52a9\uff0c\u9003\u4e86\u51fa\u6765\u3002 \u3000\u3000\u201c\u6211\u542c\u4e86\u8fd9\u4e2a\u6d88\u606f\uff0c\u5f53\u7136\u662f\u60b2\u6124\u4ea4\u52a0\uff0c\u4ed6\u5c31\u4e58\u673a\u529d\u6211\uff0c\u7ea2\u7f28\u4f1a\u4e0e\u5929\u9b54\u6559\u8054\u624b\u53bb\u5bf9\u4ed8\u516d\u5408 \u5e2e\uff0c\u8d81\u53f2\u767d\u90fd\u5c1a\u672a\u56de\u6765\uff0c\u5148\u628a\u516d\u5408\u5e2e\u541e\u5e76\u3002\u526a\u9664\u4e86\u4ed6\u7684\u7fbd\u7ffc\uff0c\u8fd9\u5c31\u66f4\u5bb9\u6613\u62a5\u4ec7\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u5931\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u6bd2\u8fa3\u7684\u4e00\u6761\u8ba1\u7b56\uff01\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u6211\u5bf9\u6b64\u4e8b\u6b63\u662f\u60f3\u5f97\u4e0d\u5f88\u660e\u767d\uff0c\u8981\u5411\u8001\u5f1f\u8bf7\u6559\u3002\u53f2\u767d\u90fd\u7684\u516d\u5408\u5e2e\u662f\u4f9d\u9644\u671d\u5ef7 \u7684\uff0c\u5982\u4eca\u8001\u5f1f\u5df2\u8bc1\u5b9e\u4e86\u9633\u6d69\u548c\u53f2\u767d\u90fd\u4e43\u662f\u4e00\u8def\uff0c\u8fd9\u4e2a\u65b0\u6559\u4e3b\u65e2\u7136\u662f\u9633\u6d69\u7684\u5080\u5121\uff0c\u4f55\u4ee5\u4ed6\u53c8\u8981 \u541e\u5e76\u516d\u5408\u5e2e\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8001\u7684\u8f88\u6709\u6240\u4e0d\u77e5\uff0c\u516d\u5408\u5e2e\u73b0\u5728\u5df2\u7ecf\u6362\u4e86\u5e2e\u4e3b\uff0c\u65b0\u5e2e\u4e3b\u5c31\u662f\u53f2\u7ea2\u82f1\u59d1\u5a18\u3002\u53f2 \u767d\u90fd\u65e9\u5df2\u5728\u897f\u660c\u6b7b\u4e86\uff0c\u9633\u6d69\u4ed6\u4eec\u4e00\u5b9a\u6599\u60f3\u5f97\u5230\uff1a\u53f2\u59d1\u5a18\u63a5\u4efb\u4e86\u5e2e\u4e3b\uff0c\u516d\u5408\u5e2e\u51b3\u4e0d\u4f1a\u518d\u4f9d\u9644\u671d \u5ef7\u800c\u662f\u8981\u52a0\u5165\u4e49\u519b\u7684\u4e86\uff0c\u6545\u6b64\u4ed6\u4eec\u5c31\u8981\u5148\u4e0b\u624b\u4e3a\u5f3a\uff0c\u7528\u8fd9\u4e2a\u501f\u5200\u6740\u4eba\u4e4b\u8ba1\uff0c\u8ba9\u4f60\u4eec\u7ea2\u7f28\u4f1a\u66ff \u4ed6\u53bb\u6536\u7f16\u516d\u5408\u5e2e\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u5e78\u4e8f\u6211\u6ca1\u6709\u4e0a\u4ed6\u7684\u5f53\u3002\u5f52\u65f6\u6211\u867d\u7136\u662f\u76f8\u4fe1\u4ed6\u7684\u8bdd\uff0c\u4f46\u6211\u7684\u4e3a\u4eba\uff0c\u8001\u5f1f\u4f60\u662f \u77e5\u9053\u7684\uff0c\u6211\u8981\u62a5\u4ec7\uff0c\u5c31\u5f97\u5149\u660e\u78ca\u843d\u7684\u53bb\u62a5\u4ec7\uff0c\u5c82\u80fd\u4e58\u7740\u53f2\u767d\u90fd\u4e0d\u5728\uff0c\u53bb\u6b3a\u8d1f\u4ed6\u7684\u5929\u4e0b\uff1f\u4f55\u51b5 \u516d\u5408\u5e2e\u4e2d\u4e5f\u5e76\u975e\u5168\u662f\u7518\u5fc3\u4f9d\u9644\u671d\u5ef7\u4e4b\u4eba\uff1f \u3000\u3000\u201c\u56e0\u6b64\u6211\u5f53\u65f6\u5c31\u62d2\u7edd\u4e86\u4ed6\u8fd9\u4e2a\u63d0\u8bae\uff0c\u53cd\u8fc7\u6765\u6211\u4ee5\u957f\u8f88\u7684\u8d44\u683c\uff0c\u529d\u4ed6\u6253\u6d88\u4e86\u91cd\u7ec4\u5929\u9b54\u6559\u7684 \u4f01\u56fe\u3002 \u3000\u3000\u4e00\u6765\u53cc\u65b9\u8bdd\u4e0d\u6295\u673a\uff0c\u4e8c\u6765\u6211\u4e5f\u4e0d\u613f\u591a\u8017\u75c5\u4eba\u7684\u7cbe\u795e\uff0c\u5f53\u4e0b\u4fbf\u8981\u544a\u8f9e\u3002\u90a3\u65b0\u6559\u4e3b\u4f9d\u7167\u793c \u8282\uff0c\u7aef\u8336\u9001\u5ba2\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u867d\u7136\u65e9\u5df2\u77e5\u9053\u7ed3\u679c\uff0c\u542c\u5230\u6b64\u5904\uff0c\u4ecd\u662f\u4e0d\u7981\u5927\u4e3a\u7d27\u5f20\uff0c\u53eb\u8d77\u6765\u9053\uff1a\u201c\u8fd9\u676f\u8336\u4e00\u5b9a\u6709 \u9b3c\uff01\u4f60\u559d\u4e86\u5566\uff1f\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u6211\u4e00\u76f4\u628a\u4ed6\u5f53\u4f5c\u5389\u5357\u661f\uff0c\u867d\u7136\u8bdd\u4e0d\u6295\u673a\uff0c\u4f46\u51b3\u60f3\u4e0d\u5230\u4ed6\u4f1a\u4e0b\u6bd2\uff0c\u4ed6\u5411\u6211\u656c \u8336\uff0c\u6211\u5f53\u7136\u662f\u6beb\u4e0d\u6000\u7591\u7684\u5c31\u559d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u201c\u4e00\u559d\u4e0b\u53bb\uff0c\u6211\u5c31\u89c9\u5f97\u6709\u70b9\u4e0d\u5bf9\uff0c\u53ef\u662f\u5df2\u7ecf\u8fdf\u4e86\uff0c\u53ea\u542c\u5f97\u5f53\u561f\u4e00\u58f0\uff0c\u90a3\u53ae\u6454\u4e0b\u8336\u676f\uff0c\u5154 \u5b50\u4f3c\u7684\u7acb\u5373\u6e9c\u8fdb\u5185\u5ba4\uff0c\u5728\u4ed6\u90a3\u5f20\u75c5\u5e8a\u7684\u540e\u9762\uff0c\u539f\u6765\u662f\u6697\u85cf\u95e8\u6237\u7684\u3002 \u3000\u3000\u201c\u6211\u4e00\u6293\u6293\u7a7a\uff0c\u9633\u6d69\u7acb\u5373\u4f7f\u51fa\u4e86\u4fee\u7f57\u9634\u715e\u529f\u5411\u6211\u6253\u6765\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u2018\u516c\u5b59\u5e2e\u4e3b\uff0c\u4f60\u65e2\u7136 \u6765\u4e86\uff0c\u5c31\u8bf7\u4f60\u7559\u4e0b\u6765\u5427\uff01\u2019 \u3000\u3000\u201c\u54ce\uff0c\u6211\u867d\u7136\u662f\u4e2d\u4e86\u6bd2\uff0c\u51ed\u7740\u9633\u6d69\u8fd9\u70b9\u529f\u592b\uff0c\u60f3\u8981\u7559\u6211\uff0c\u53ef\u8fd8\u4e0d\u80fd\uff01\u4ed6\u7b11\u58f0\u672a\u7edd\uff0c\u6211\u5df2 \u6253\u65ad\u4e86\u4ed6\u7684\u4e24\u6761\u808b\u9aa8\uff0c\u53eb\u4ed6\u7684\u72c2\u7b11\u53d8\u6210\u4e86\u60e8\u53f7\uff01\u53ea\u53ef\u60dc\u6211\u7684\u638c\u529b\u53d1\u6325\u4e0d\u5230\u4e94\u6210\uff0c\u672a\u80fd\u53d6\u4ed6\u6027 \u547d\uff01 \u3000\u3000\u201c\u90a3\u95f4\u5bc6\u5ba4\u662f\u85cf\u6709\u673a\u5173\u7684\uff0c\u9633\u6d69\u7ed9\u6211\u9707\u51fa\u95e8\u5916\uff0c\u7acb\u5373\u5f00\u52a8\u673a\u5173\uff0c\u843d\u4e0b\u4e86\u4e09\u91cd\u94c1\u95f8\uff0c\u5c06\u6211 \u56f0\u4f4f\u3002\u4ed6\u5728\u5916\u9762\u72de\u7b11\u9053\uff1a\u2018\u8fd9\u676f\u8336\u91cc\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u4e0d\u8fc7\u653e\u4e0b\u4e86\u4e00\u64ae\u65ad\u80a0\u6563\uff0c\u516c\u5b59\u5e2e\u4e3b\uff0c\u4f60\u5185\u529f \u6df1\u539a\uff0c\u6216\u8005\u65e0\u9700\u6211\u4eec\u7684\u89e3\u836f\u3002\u4f46\u4e07\u4e00\u4f60\u62b5\u53d7\u4e0d\u4f4f\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u4e0d\u5fc5\u901e\u5f3a\uff0c\u548c\u6211\u4eec\u597d\u597d\u7684\u8c08\u4e00 \u8c08\u6761\u4ef6\uff01\u2019\u54fc\uff0c\u4ed6\u4ee5\u4e3a\u8fd9\u6837\u5c31\u53ef\u4ee5\u8981\u631f\u6211\uff0c\u771f\u662f\u592a\u4e0d\u61c2\u5f97\u6211\u516c\u5b59\u5b8f\u7684\u813e\u6c14\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5403\u60ca\u9053\uff1a\u201c\u4f46\u4ed6\u4eec\u5e03\u7f6e\u5f97\u5982\u6b64\u5468\u5bc6\uff0c\u4f60\u540e\u6765\u662f\u600e\u4e48\u8131\u56f0\u7684\uff1f\u201d \u3000\u3000\u516c\u5b59\u5b8f\u7b11\u9053\uff1a\u201c\u5e03\u7f6e\u5f97\u867d\u7136\u5468\u5bc6\uff0c\u5374\u4e5f\u6709\u767e\u5bc6\u4e00\u758f\u3002\u4ed6\u4eec\u6ca1\u6709\u60f3\u5230\u6211\u4f1a\u5728\u5c4b\u9876\u5f00\u4e2a\u5929 \u7a97\uff0c\u786c\u51b2\u51fa\u53bb\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u60ca\u9053\uff1a\u201c\u4f60\u662f\u7528\u7ef5\u638c\u51fb\u77f3\u5982\u7c89\u7684\u529f\u592b\uff0c\u628a\u5c4b\u9876\u786c\u63ed\u4e86\u4e00\u5757\uff1f\u201d \u3000\u3000\u516c\u5b59\u5b8f\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u51b2\u4e86\u51fa\u53bb\uff0c\u8fd8\u62a2\u4e86\u4ed6\u4eec\u7684\u4e00\u5339\u5750\u9a91\uff0c\u65e0\u4eba\u6562\u963b\u62e6\u6211\u3002\u6211\u8dd1\u56de\u5bb6 \u91cc\uff0c\u8fd9\u624d\u6ca1\u6709\u529e\u6cd5\u4e0d\u8eba\u4e0b\u6765\u7684\u3002\u4f46\u5728\u4ed6\u4eec\u7684\u9b54\u7a9f\u91cc\uff0c\u6211\u5374\u662f\u8fde\u8170\u4e5f\u672a\u66fe\u4e00\u5f2f\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u4e3a\u4f69\u670d\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u5e76\u975e\u4ed6\u4eec\u7684\u5e03\u7f6e\u767e\u5bc6\u4e0d\u758f\uff0c\u4ed6\u4eec\u600e\u4f1a\u60f3\u5230\uff0c\u4f60\u670d\u4e86\u65ad\u80a0 \u6563\uff0c\u5c45\u7136\u8fd8\u80fd\u591f\u4f7f\u51fa\u7ef5\u638c\u51fb\u77f3\u5982\u7c89\u7684\u529f\u592b\uff0c\u4ed6\u4eec\u7684\u4e09\u91cd\u94c1\u95f8\u52a0\u4e0a\u4e86\u9633\u6d69\u7684\u4fee\u7f57\u9634\u715e\u529f\u4ecd\u7136\u56f0 \u4e0d\u4e86\u4f60\uff01\u201d \u3000\u3000\u516c\u5b59\u5b8f\u82e6\u7b11\u9053\uff1a\u201c\u4f46\u5982\u6b64\u4e00\u6765\uff0c\u6211\u8981\u51ed\u672c\u8eab\u529f\u529b\u89e3\u6bd2\uff0c\u53ef\u80fd\u8981\u591a\u82b1\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\u4e86\uff01\u201d \u5f53\u4e0b\u53c8\u5411\u91d1\u9010\u6d41\u62b1\u6b49\u9053\uff1a\u201c\u53ea\u56e0\u6211\u4e0d\u80fd\u8d77\u5e8a\uff0c\u81f3\u6709\u4eca\u65e5\u7684\u8bef\u4f1a\uff0c\u5426\u5219\u6211\u51b3\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u5bf9\u4f60\u548c \u53f2\u59d1\u5a18\u5982\u6b64\u5931\u793c\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4e5f\u602a\u4e0d\u5f97\u4ed6\u4eec\uff0c\u6211\u548c\u5389\u5357\u661f\u7684\u4ea4\u60c5\uff0c\u4ed6\u4eec\u662f\u77e5\u9053\u7684\uff0c\u771f\u5047\u672a\u66fe\u6e05\u695a\u4e4b \u524d\uff0c\u4ed6\u4eec\u5f53\u7136\u4e0d\u6562\u8ba9\u6211\u89c1\u4f60\u3002\u4f55\u51b5\u4ed6\u4eec\u4e5f\u4e00\u5b9a\u662f\u628a\u53f2\u767d\u90fd\u5f53\u4f5c\u6740\u5bb3\u4ee4\u5a9b\u7684\u4ec7\u4eba\u5462\uff01\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u867d\u7136\u5982\u6b64\uff0c\u4e5f\u662f\u4e0d\u8c05\u3002\u201d\u5f53\u4e0b\u628a\u77f3\u7384\u53eb\u6765\uff0c\u95ee\u9053\uff1a\u201c\u5e84\u8fdc\u3001\u79e6\u51b2\u4e8c\u4eba\u662f\u5426 \u5728\u5916\u9762\u76d1\u89c6\u7740\u53f2\u59d1\u5a18\uff1f\u201d\u77f3\u7384\u751a\u662f\u5c34\u5c2c\uff0c\u8bf4\u9053\uff1a\u201c\u5e84\u3001\u79e6\u4e24\u4f4d\u9999\u4e3b\u662f\u5728\u5ba2\u5385\u966a\u53f2\u59d1\u5a18\u5750\uff0c\u4ed6 \u4eec\u9075\u5b88\u8235\u4e3b\u7684\u547d\u4ee4\uff0c\u5bf9\u53f2\u59d1\u5a18\u4e0d\u6562\u65e0\u793c\u3002\u201d\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u53eb\u4ed6\u4eec\u548c\u53f2\u59d1\u5a18\u8fdb\u6765\u3002\u201d\u77f3\u7384\u5e94 \u9053\uff1a\u201c\u662f\u3002\u201d \u3000\u3000\u53cc\u65b9\u628a\u4e8b\u5b9e\u8bdd\u4e00\u8bf4\u4e86\u51fa\u6765\uff0c\u771f\u76f8\u867d\u7136\u5c1a\u672a\u5927\u767d\uff0c\u4f46\u90a3\u5929\u9b54\u6559\u7684\u65b0\u6559\u4e3b\u4e43\u662f\u5192\u540d\u884c\u9a97\u4e4b \u5f92\uff0c\u8fd9\u4e00\u70b9\u5df2\u662f\u65e0\u7591\u7684\u4e86\u3002\u4e8e\u662f\u5e84\u8fdc\u3001\u79e6\u51b2\u4e24\u4f4d\u9999\u4e3b\u3002\u5728\u516c\u5b59\u5b8f\u75c5\u69bb\u4e4b\u524d\uff0c\u5f53\u9762\u5411\u91d1\u3001\u53f2\u4e8c \u4eba\u8d54\u7f6a\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4e8b\u60c5\u5f04\u6e05\u695a\u4e86\u5c31\u597d\uff0c\u4e9b\u987b\u8bef\u4f1a\uff0c\u4f55\u8db3\u4ecb\u6000\uff1f\u201d \u3000\u3000\u516c\u5b59\u5b8f\u53f9\u9053\uff1a\u201c\u53ef\u60dc\u6211\u8bef\u906d\u8d3c\u5b50\u4e4b\u7b97\uff0c\u6050\u6015\u8fd8\u5f97\u5367\u5e8a\u5341\u5929\u534a\u4e2a\u6708\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u52b3\u524d\u8f88\u8d39\u795e\uff0c\u6211\u6253\u7b97\u548c\u53f2\u59d1\u5a18\u9a6c\u4e0a\u5c31\u5230\u5f82\u5f95\u5c71\u53bb\u3002\u9633\u6d69\u8fd9\u53ae\uff0c\u665a\u8f88\u6599\u60f3 \u8fd8\u5bf9\u4ed8\u5f97\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u9633\u6d69\u5229\u7528\u90a3\u4e2a\u5047\u5389\u5357\u661f\u4f5c\u4e3a\u5080\u5121\uff0c\u6253\u51fa\u4e86\u5929\u9b54\u6559\u7684\u65d7\u53f7\uff0c\u91cd\u5f00\u79c0\u5802\uff0c\u636e\u6211 \u6240\u77e5\uff0c\u4ed6\u6240\u805a\u96c6\u7684\u79c1\u90b8\uff0c\u4e3a\u6570\u6050\u6015\u8fd8\u771f\u7684\u4e0d\u5c11\u5462\u3002\u91d1\u5c11\u4fa0\u6df1\u5165\u864e\u7a74\uff0c\u987b\u5f97\u5f53\u5fc3\uff01\u201d \u3000\u3000\u77f3\u7384\u8bf4\u9053\uff1a\u201c\u4e0d\u5982\u5f85\u54b1\u4eec\u7684\u8235\u4e3b\u75c5\u597d\u4e86\uff0c\u5927\u4f19\u513f\u90fd\u53bb\uff0c\u90a3\u5c31\u53ef\u4ee5\u7a33\u64cd\u80dc\u7b97\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\u867d\u662f\u597d\uff0c\u4f46\u4e00\u6765\u6211\u60f3\u65e9\u4e9b\u63a2\u660e\u771f\u76f8\uff0c\u4e8c\u6765\u8d81\u4ed6\u4eec\u6839\u57fa\u672a\u56fa\uff0c\u52a8\u624b\u4e5f\u6bd4\u8f83 \u5bb9\u6613\uff0c\u5018\u82e5\u5047\u4ee5\u65f6\u65e5\uff0c\u9633\u6d69\u7fbd\u7ffc\u5df2\u6210\uff0c\u4ee5\u8d35\u4f1a\u4e4b\u529b\uff0c\u867d\u7136\u53ef\u4ee5\u526a\u9664\u4ed6\u4eec\uff0c\u4f46\u53ea\u6015\u4f24\u4ea1\u5c31\u8981\u591a \u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u4f46\u4f60\u4eec\u53ea\u6709\u4e24\u4eba\uff0c\u8fd9\u4e2a\u2026\u2026\u201d\u79e6\u51b2\u662f\u4e2a\u76f4\u6027\u5b50\u7684\u4eba\uff0c\u8bf4\u9053\uff1a \u201c\u6211\u613f\u610f\u966a\u91d1\u5c11\u4fa0\u571f\uff0c\u5c06\u529f\u8d4e\u7f6a\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u82e5\u7136\u53ea\u662f\u53bb\u63a2\u67e5\u771f\u76f8\uff0c\u4eba\u591a\u4e86\u6050\u6015\u53cd\u800c\u4e0d\u597d\u3002\u516c\u5b59\u8235\u4e0d\u653e\u5fc3\uff0c\u665a\u8f88\u4e0d\u4f1a\u548c \u4ed6\u4eec\u7fa4\u6bb4\u7684\u3002\u665a\u8f88\u7684\u6253\u7b97\u662f\u667a\u53d6\u800c\u975e\u529b\u593a\u3002\u201d\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u8bf7\u9053\u5176\u8be6\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u610f\u6b32\u6f5c\u5165\u5929\u9b54\u6559\u7684\u9999\u5802\uff0c\u5c06\u90a3\u5192\u5145\u7684\u65b0\u6559\u4e3b\u63ea\u51fa\u6765\uff0c\u53ea\u8981\u63ed\u7a7f\u4e86\u4ed6\u662f\u5047 \u5192\u7684\uff0c\u5929\u9b54\u6559\u7684\u65e7\u90e8\u5b9a\u7136\u5012\u6208\u76f8\u5411\uff0c\u90a3\u65f6\u53ea\u5269\u9633\u6d69\u4e00\u73ed\u5996\u90aa\uff0c\u4e5f\u5c31\u65e0\u80fd\u4e3a\u529b\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u4f1a\u9053\uff1a\u201c\u4ee5\u4f60\u7684\u8f7b\u529f\u800c\u8bba\uff0c\u672a\u59cb\u6ca1\u6709\u6210\u529f\u7684\u5e0c\u671b\u3002\u4e0d\u8fc7\u4e5f\u8981\u770b\u673a\u7f18\u662f\u5426\u51d1\u5de7\uff0c\u98ce\u9669 \u6050\u6015\u8fd8\u662f\u76f8\u5f53\u5927\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u5192\u70b9\u98ce\u9669\uff0c\u5018\u80fd\u514d\u6389\u5927\u52a8\u5e72\u6208\uff0c\u8fd9\u4e2a\u751f\u610f\u4e5f\u8fd8\u662f\u5f88\u4e0a\u7b97\u5440\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u6211\u4ece\u5929\u9b54\u6559\u7684\u603b\u8235\u95ef\u51fa\u6765\uff0c\u5bf9\u91cc\u9762\u7684\u5efa\u7b51\u3001\u5730\u5f62\u5927\u81f4\u8fd8\u8bb0\u5f97\u4e00\u4e9b\uff0c\u6211\u7ed8\u4e00 \u5f20\u5730\u56fe\u7ed9\u4f60\uff0c\u6216\u8005\u5bf9\u4ee5\u5bf9\u4f60\u6709\u70b9\u5e2e\u52a9\u3002\u201d\u91d1\u9010\u6d41\u559c\u9053\uff1a\u201c\u90a3\u5c31\u66f4\u597d\u4e86\uff01\u201d \u3000\u3000\u8ba1\u8bae\u5df2\u5b9a\uff0c\u4e8e\u662f\u91d1\u9010\u6d41\u5728\u516c\u5b59\u5b8f\u5bb6\u91cc\u4f4f\u4e86\u4e00\u665a\uff0c\u7b2c\u4e8c\u5929\u53d6\u4e86\u5730\u56fe\u5c31\u548c\u53f2\u7ea2\u82f1\u7684\u53bb\u63a2\u5929\u9b54 \u6559\u7684\u603b\u8235\u3002\u6b63\u662f\uff1a \u3000\u3000\u9ed1\u767d\u6df7\u6dc6\u5bb9\u4e0d\u5f97\uff0c\u4e3a\u660e\u771f\u76f8\u63a2\u9b54\u5bab\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e8c\u56de\u3000\u7591\u96e8\u7591\u4e91\u7ec8\u5927\u767d\u3000\u4ea6\u771f\u4ea6\u5e7b\u8bf4\u524d\u56e0 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u56db\u5341\u4e8c\u56de\u3000\u7591\u96e8\u7591\u4e91\u7ec8\u5927\u767d\u3000\u4ea6\u771f\u4ea6\u5e7b\u8bf4\u524d\u56e0 \u3000\u3000\u4e00\u4e2a\u6708\u8272\u6726\u80e7\u7684\u665a\u4e0a\uff0c\u5f82\u5f95\u5c71\u7684\u9ed1\u4e1b\u6797\u4e2d\uff0c\u98ce\u4e0d\u5439\uff0c\u8349\u4e0d\u52a8\uff0c\u5374\u5076\u5c14\u6709\u51e0\u7247\u6811\u53f6\u843d\u4e0b\uff0c \u4f34\u968f\u7740\u4e00\u9635\u6781\u8f7b\u5fae\u7684\u6c99\u6c99\u58f0\u54cd\uff0c\u8fd9\u4e0d\u662f\u5bbf\u9e1f\u60ca\u98de\uff0c\u800c\u662f\u6709\u4e24\u4e2a\u8f7b\u529f\u6781\u9ad8\u660e\u7684\u591c\u884c\u4eba\u7ecf\u8fc7\uff0c\u8fd9 \u4e24\u4e2a\u4eba\u5c31\u662f\u91d1\u9010\u6d41\u548c\u53f2\u7ea2\u82f1\u4e86\u3002 \u3000\u3000\u5f82\u5f95\u5c71\u662f\u91d1\u9010\u6d41\u65e7\u6e38\u4e4b\u5730\uff0c\u6b64\u9645\u91cd\u6765\uff0c\u5fc3\u60c5\u5374\u662f\u975e\u540c\u65e7\u65f6\u3002\u8fc7\u53bb\u4ed6\u662f\u4e2a\u6e38\u620f\u4eba\u95f4\u7684\u5c0f\u53eb \u5316\uff0c\u5728\u5f82\u5f95\u5c71\u4e0a\u6f2b\u6e38\uff0c\u4e43\u662f\u968f\u5174\u4e4b\u6240\u81f3\uff0c\u5982\u4eca\u5219\u662f\u6709\u6240\u4e3a\u800c\u6765\uff0c\u6068\u4e0d\u5f97\u9a6c\u4e0a\u8d76\u5230\u5929\u9b54\u6559\u7684\u603b \u8235\uff0c\u53bb\u63ed\u5f00\u5389\u5357\u661f\u7684\u751f\u6b7b\u4e4b\u8c1c\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8d70\u5728\u524d\u5934\u5e26\u8def\uff0c\u8d70\u4e86\u4e00\u4f1a\uff0c\u9690\u7ea6\u5df2\u53ef\u89c1\u5230\u524d\u9762\u5c71\u5c97\u9ad8\u5904\u7684\u4e00\u5ea7\u7834\u5e99\u3002\u91d1\u9010\u6d41\u8bf4 \u9053\uff1a\u201c\u8fd9\u5ea7\u7834\u5e99\u672c\u662f\u5929\u9b54\u6559\u65e7\u90e8\u7684\u795e\u5e99\uff0c\u542c\u516c\u5b59\u8235\u4e3b\u8bf4\uff0c\u9633\u6d69\u5df2\u5728\u5c71\u4e0a\u5929\u9b54\u6559\u7684\u9057\u5740\u91cd\u5efa\u9999 \u5802\uff0c\u4f46\u8fd9\u5ea7\u7834\u5e99\uff0c\u60f3\u662f\u65e0\u6687\u53ca\u6b64\uff0c\u4ecd\u662f\u4efb\u5b83\u6401\u7f6e\uff0c\u672a\u66fe\u8695\u4fee\u3002\u8bf4\u8d77\u8fd9\u5ea7\u7834\u5e99\uff0c\u5012\u662f\u6709\u4e00\u6bb5\u6545 \u4e8b\uff0c\u548c\u6211\u6709\u5173\u3002\u95f4\u63a5\u4e5f\u548c\u4f60\u6709\u5173\u7cfb\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u6084\u58f0\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u4ec0\u4e48\u6545\u4e8b\u548c\u4f60\u6211\u90fd\u6709\u5173\u7684\uff0c\u6211\u5012\u60f3\u542c\u542c\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u5c31\u662f\u5728\u8fd9\u5ea7\u7834\u5e99\u4e2d\u8ba4\u8bc6\u674e\u6566\u7684\uff0c\u90a3\u665a\u4ed6\u8eb2\u5728\u5e99\u91cc\u70e4\u5c71\u828b\uff0c\u6211\u8fdb\u53bb\u5411\u4ed6\u8ba8 \u5403\uff0c\u6070\u5de7\u78b0\u7740\u4f60\u7684\u54e5\u54e5\u6d3e\u6765\u8ffd\u6740\u4ed6\u7684\u9752\u5cf0\u9053\u4eba\u548c\u7126\u78ca\uff0c\u6211\u628a\u5e99\u91cc\u7684\u4e00\u53e3\u5927\u949f\u7f69\u7740\u4ed6\uff0c\u620f\u5f04\u4e86 \u9752\u5cf0\u548c\u7126\u78ca\u4e00\u987f\uff0c\u8fd9\u624d\u5e2e\u5fd9\u4ed6\u8eb2\u8fc7\u4e86\u90a3\u6b21\u96be\u5173\uff0c\u90a3\u53e3\u5927\u949f\u91cc\u523b\u6709\u5929\u9b54\u6559\u7684\u767e\u6bd2\u771f\u7ecf\uff0c\u674e\u6566\u53cd \u800c\u56e0\u7978\u5f97\u798f\u3002 \u3000\u3000\u201c\u540e\u6765\u6211\u624d\u77e5\u9053\u4ed6\u662f\u56e0\u4e3a\u5077\u4e86\u4f60\u54e5\u54e5\u7684\u4e00\u4e32\u591c\u660e\u73e0\uff0c\u8fd9\u4e32\u591c\u660e\u73e0\u662f\u4f60\u7684\u54e5\u54e5\u51c6\u5907\u9001\u7ed9\u8428 \u798f\u9f0e\u7684\u5bff\u793c\uff0c\u6545\u6b64\u975e\u8981\u5c06\u4ed6\u6349\u56de\u53bb\u4e0d\u53ef\uff01 \u3000\u3000\u53f2\u7ea2\u82f1\u7b11\u9053\uff1a\u201c\u90a3\u4e32\u591c\u660e\u73e0\u662f\u6211\u5e2e\u4ed6\u5077\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u662f\u5440\uff0c\u6240\u4ee5\u6211\u8bf4\u4e0e\u4f60\u4e5f\u95f4\u63a5\u6709\u5173\u3002\u82e5\u4e0d\u662f\u4e3a\u4e86\u90a3\u665a\u4e4b\u4e8b\uff0c\u5f15\u8d77\u4e86\u6211\u4e5f\u60f3\u52ab \u593a\u53f2\u767d\u90fd\u9001\u4eac\u7684\u5bff\u793c\uff0c\u540e\u6765\u6211\u8fd8\u4e0d\u4f1a\u8ba4\u8bc6\u4f60\u5462\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u8ddd\u79bb\u90a3\u5ea7\u7834\u5e99\u5df2\u662f\u8d8a\u6765\u8d8a\u8fd1\uff0c\u53f2\u7ea2\u82f1\u5ffd\u5730\u54a6\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5e99\u91cc\u6709\u706b\u5149\uff01 \u6211\u4f3c\u4e4e\u95fb\u5230\u4e00\u80a1\u9999\u5473\uff0c\u96be\u9053\u53c8\u6709\u4eba\u5728\u91cc\u9762\u70e4\u4e1c\u897f\u5403\u4e0d\u6210\uff1f\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u91d1\u9010\u6d41\u8eab\u5f62\u75be\u8d77\uff0c\u5df2\u662f\u7bad\u4e00\u822c\u7684\u5411\u524d\u5c04\u51fa\uff0c\u53f2\u7ea2\u82f1\u5374\u89c9\u5f97\u91d1\u9010\u6d41\u597d\u4f3c\u8fd8 \u5728\u5979\u7684\u8033\u8fb9\u4f4e\u58f0\u8bf4\u8bdd\u4e00\u6837\uff1a\u201c\u4f60\u5feb\u53bb\u641c\u90a3\u5ea7\u7834\u5e99\uff0c\u5c0f\u5fc3\u4e00\u4e9b\uff01\u201d\u539f\u6765\u91d1\u9010\u6d41\u4e00\u9762\u4f7f\u51fa\u7edd\u9876\u7684 \u8f7b\u529f\u5411\u524d\u8ffd\u53bb\uff0c\u4e00\u9762\u5374\u7528\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u5185\u529f\u5411\u5979\u4f20\u8bdd\uff0c\u662f\u4ee5\u4ed6\u7684\u8eab\u5f62\u867d\u7136\u65e9\u5df2\u8ddd\u79bb\u5341\u6570\u4e08 \u4e4b\u9065\uff0c\u4ecd\u597d\u50cf\u662f\u5728\u5979\u8033\u8fb9\u8bf4\u8bdd\u4e00\u822c\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u611f\u5230\u6709\u70b9\u5947\u602a\uff1a\u201c\u4e3a\u4ec0\u4e48\u4ed6\u4e0d\u8fdb\u53bb\uff1f\u201d\u5979\u7684\u8f7b\u529f\u7a0d\u900a\u4e8e\u91d1\u9010\u6d41\uff0c\u5728\u5979\u8fdb\u4e86\u90a3\u5ea7\u7834 \u5e99\u4e4b\u540e\uff0c\u4e0d\u8fc7\u4e00\u4f1a\uff0c\u91d1\u9010\u6d41\u4e5f\u5c31\u56de\u6765\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u5e99\u91cc\u53ea\u89c1\u6709\u8fd9\u5806\u706b\uff0c\u5374\u4e0d\u89c1\u6709\u4eba\u3002\u4f60\u521a\u624d\u53bb\u54ea\u91cc\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u5230\u6797\u5b50\u91cc\u6211\u4e00\u4e2a\u4eba\u3002\u201d\u53f2\u7ea2\u82f1\u8be7\u9053\uff1a\u201c\u627e\u4ec0\u4e48\u4eba\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u521a \u624d\u6709\u6ca1\u6709\u770b\u5230\u4e00\u4e2a\u4eba\u5f71\uff0c\u8eb2\u5728\u5e99\u80cc\u7684\u90a3\u5835\u77ed\u5899\u540e\u9762\uff0c\u9690\u9690\u7ea6\u7ea6\u7684\u597d\u4f3c\u9732\u51fa\u534a\u4e2a\u5934\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u5927\u4e3a\u5947\u602a\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u7684\u5417\uff1f\u6211\u5374\u6ca1\u6709\u770b\u89c1\u3002\u55ef\uff0c\u4e5f\u8bb8\u662f\u56e0\u4e3a\u6211\u672a\u66fe\u600e\u6837\u7559\u610f \u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u521a\u4e00\u53d1\u73b0\uff0c\u90a3\u4eba\u5c31\u50cf\u9b3c\u5f71\u4f3c\u7684\u4e00\u95ea\u4e0d\u89c1\u4e86\u3002\u5e99\u540e\u5e76\u65e0\u53ef\u4ee5\u85cf\u8eab\u4e4b\u5904\uff0c\u9664 \u975e\u662f\u8eb2\u8fdb\u6811\u6797\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u7b11\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4f60\u7a81\u7136\u8dd1\u4e0a\u524d\u53bb\uff0c\u5012\u5413\u4e86\u6211\u4e00\u8df3\uff0c\u4f60\u5728\u6811\u6797\u91cc\u53d1\u73b0\u4ec0\u4e48\u4e86\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4ec0\u4e48\u4e5f\u6ca1\u6709\u53d1\u73b0\uff0c\u8fde\u534a\u70b9\u58f0\u606f\u4e5f\u6ca1\u6709\u542c\u5230\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4ee5\u4f60\u7684\u8f7b\u529f\u800c\u8bba\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u53ca\u5f97\u4e0a\u4f60\u7684\u5be5\u5be5\u53ef\u6570\uff0c\u65e2\u7136\u4f60\u662f\u4e00\u53d1\u89c9\u5c31\u8ffd \u4e0a\u53bb\uff0c\u8ddd\u79bb\u53c8\u4e0d\u5230\u534a\u91cc\u4e4b\u9065\uff0c\u6309\u8bf4\u662f\u5e94\u8be5\u8ffd\u5f97\u4e0a\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u662f\u5440\uff0c\u5c31\u662f\u8ffd\u4e0d\u4e0a\u4e5f\u8be5\u770b\u89c1\u90a3\u4eba\u7684\u80cc\u5f71\u3002\u6797\u4e2d\u7684\u6811\u6728\u5e76\u4e0d\u5f88\u5bc6\uff0c\u6211\u515c\u4e86\u4e00 \u4e2a\u5708\u5b50\u65b9\u624d\u56de\u6765\uff0c\u4ec0\u4e48\u4e5f\u6ca1\u53d1\u73b0\u3002\u6211\u771f\u4e0d\u76f8\u4fe1\u8fd9\u4e2a\u4eba\u4f1a\u8eb2\u5f97\u8fd9\u6837\u5feb\uff0c\u4e0d\u8fc7\uff0c\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916 \u6709\u4eba\u2026\u2026\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u5047\u5982\u771f\u7684\u662f\u6709\u4e00\u4e2a\u8f7b\u529f\u6bd4\u4f60\u66f4\u9ad8\u660e\u7684\u4eba\uff0c\u8fd9\u4ef6\u4e8b\u5c31\u66f4\u5947\u602a\u4e86\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u60f3\u5230\u7684\u91d1\u9010\u6d41\u4e5f\u60f3\u5230\u4e86\uff0c\u4e8e\u662f\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5047\u5982\u771f\u7684\u6709\u4eba\uff0c\u8fd9\u4e2a\u4eba\u662f\u670b \u53cb\u7684\u8bdd\uff0c\u5c31\u8be5\u51fa\u6765\u76f8\u89c1\uff0c\u662f\u654c\u4eba\u7684\u8bdd\uff0c\u5c31\u8be5\u51fa\u58f0\u62a5\u8b66\uff0c\u4f46\u5982\u4eca\u5df2\u8fc7\u4e86\u8fd9\u8bb8\u591a\u65f6\u5019\uff0c\u8fd8\u662f\u6beb\u65e0 \u52a8\u9759\uff0c\u8fd9\u53ef\u771f\u53eb\u6211\u731c\u60f3\u4e0d\u900f\u4e86\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u82e5\u6709\u6240\u601d\uff0c\u5ffd\u5730\u62ac\u8d77\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u83ab\u975e\u662f\u2026\u2026\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u731c\u7591\u662f\u5389\u5357 \u661f\uff1f\u201d\u53f2\u7ea2\u82f1\u70b9\u4e86\u70b9\u5934\uff1b\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u600e\u7684\uff0c\u6211\u597d\u50cf\u6709\u4e2a\u9884\u611f\uff0c\u5389\u5927\u54e5\u4e00\u5b9a\u8fd8\u6ca1\u6709\u6b7b\uff0c\u4ed6\u77e5 \u9053\u6709\u4eba\u5192\u5145\u4ed6\uff0c\u4ed6\u4e5f\u4e00\u5b9a\u4f1a\u6765\u67e5\u63a2\u7684\u3002\u8bf4\u4e0d\u5b9a\u4ed6\u4e5f\u662f\u51d1\u5de7\u5728\u4eca\u665a\u6765\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u662f\u76fc\u4f60\u7684\u613f\u671b\u6210\u771f\u7684\u3002\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4f60\u4e0d\u76f8\u4fe1\u4ed6\u8fd8\u6d3b\u7740\uff1f\u201d\u91d1\u9010\u6d41 \u9053\uff1a\u201c\u5373\u4f7f\u4ed6\u8fd8\u6d3b\u7740\uff0c\u4f46\u4ed6\u662f\u53d7\u4e86\u9633\u6d69\u7684\u4fee\u7f57\u9634\u715e\u529f\u4e4b\u4f24\u7684\uff0c\u5c82\u80fd\u8d1f\u4e86\u91cd\u4f24\uff0c\u4ece\u6570\u5343\u91cc\u5916\u7684 \u897f\u660c\u6765\u5230\u6b64\u5730\uff1f\u6765\u5230\u6b64\u5730\uff0c\u8fd8\u80fd\u65bd\u5c55\u5982\u6b64\u9ad8\u660e\u7684\u8f7b\u529f\uff1f\u518d\u8bf4\uff0c\u5389\u5927\u54e5\u89c1\u4e86\u54b1\u4eec\uff0c\u8fd8\u6709\u4e0d\u559c\u51fa \u671b\u5916\u7684\u8d76\u5feb\u51fa\u6765\u548c\u54b1\u4eec\u4f1a\u9762\u5417\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u731c\u60f3\u4e0d\u900f\uff0c\u90a3\u5c31\u6682\u4e14\u4e0d\u5fc5\u7406\u5b83\uff0c\u53cd\u6b63\u54b1\u4eec\u4eca\u665a\u5c31\u662f\u8981\u6765\u63a2\u67e5\u771f\u76f8\u7684\uff0c\u4e0d \u8fc7\uff0c\u8fd9\u5e99\u5b50\u91cc\u521a\u624d\u5374\u5b9a\u662f\u6709\u4eba\u65e0\u7591\u3002\u4f60\u770b\uff0c\u8fd9\u5806\u706b\u8fd8\u672a\u7184\u706d\uff0c\u70e4\u719f\u4e86\u7684\u5c71\u828b\u4e5f\u8fd8\u672a\u5403\u5b8c \u5462\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u8fd9\u60c5\u666f\u5012\u662f\u548c\u6211\u90a3\u6b21\u4f1a\u89c1\u674e\u6566\u7684\u60c5\u666f\u4e00\u6a21\u4e00\u6837\u3002\u4f46\u8fd9\u4e2a\u4eba\u5f53\u7136\u51b3\u4e0d\u4f1a\u662f \u674e\u6566\u3002\u4ed6\u7684\u8f7b\u529f\u548c\u90a3\u4e2a\u4eba\u5dee\u5f97\u592a\u8fdc\uff01 \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u5728\u5e99\u91cc\u7684\u8fd9\u4e2a\u4eba\uff0c\u53ef\u80fd\u662f\u5728\u54b1\u4eec\u672a\u66fe\u4e0a\u6765\u4e4b\u524d\uff0c\u5c31\u5df2\u8dd1\u4e86\u7684\uff0c\u5012\u662f\u4f60\u8ffd\u8e2a \u7684\u90a3\u4e2a\u4eba\uff0c\u4e0d\u77e5\u662f\u771f\u662f\u5e7b\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u8fd9\u4eba\u795e\u51fa\u9b3c\u6ca1\uff0c\u7ed9\u4f60\u8fd9\u4e48\u4e00\u8bf4\uff0c\u6211\u4e5f\u6709\u70b9\u6000\u7591\uff0c\u4e0d\u77e5\u662f\u5426\u771f\u6709\u5176\u4eba\u4e86\u3002 \u55ef\uff0c\u6216\u8bb8\u662f\u6211\u773c\u82b1\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u4e0d\u5fc5\u7ba1\u5b83\uff0c\u8fd9\u51e0\u4e2a\u70e4\u7684\u5c71\u828b\u597d\u9999\uff0c\u6211\u5012\u662f\u4e0d\u7531\u5f97\u98df\u6b32\u5927\u52a8\u4e86\u3002 \u4f60\u4e00\u4e2a\u6211\u4e00\u4e2a\u5206\u98df\u4e86\u5427\u3002\u201d\u53f2\u7ea2\u82f1\u7b11\u9053\uff1a\u201c\u77a7\u4f60\u8fd9\u526f\u8c17\u76f8\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5403\u9971\u4e86\u809a\u5b50\uff0c\u6b63\u597d\u5230\u5929\u9b54\u6559\u7684\u603b\u8235\u53bb\u5927\u95f9\u4e00\u573a\u3002\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4f60\u522b\u5fd8 \u4e86\uff0c\u54b1\u4eec\u662f\u4e0d\u80fd\u60ca\u52a8\u4f17\u4eba\u7684\u5440\uff0c\u600e\u4e48\u53ef\u4ee5\u5927\u95f9\u4e00\u573a\u5462\uff1f\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u4e2a\u4eba\u6027\u559c\u80e1 \u95f9\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u8bf4\u6e9c\u4e86\u5634\u4e86\u3002\u4f46\u8bdd\u8bf4\u56de\u6765\uff0c\u54b1\u4eec\u867d\u662f\u4e0d\u60f3\u6253\u8349\u60ca\u86c7\uff0c\u4f46\u4e8b\u5230\u5176\u65f6\uff0c\u53ea\u6015\u672a\u5fc5 \u80fd\u5982\u54b1\u4eec\u6240\u613f\u3002\u201d \u3000\u3000\u6b64\u65f6\u5df2\u662f\u5c06\u8fd1\u4e09\u66f4\u65f6\u5206\uff0c\u5bd2\u610f\u52a0\u6d53\uff0c\u5929\u8272\u4e5f\u53d8\u5f97\u66f4\u9634\u6c89\u4e86\uff0c\u8fd9\u665a\u662f\u4e09\u6708\u521d\u56db\uff0c\u4e00\u5f2f\u7709 \u6708\uff0c\u6708\u8272\u672c\u5c31\u6726\u80e7\uff0c\u53d8\u4e86\u5929\u8272\uff0c\u8fde\u6de1\u6708\u758f\u661f\u4e5f\u5df2\u7ed9\u4e4c\u4e91\u906e\u5e55\u3002\u5929\u4e0a\u843d\u4e0b\u96f3\u9739\u7ec6\u96e8\uff0c\u5341\u6570\u6b65\u4e4b \u5916\uff0c\u89c6\u7ebf\u5df2\u662f\u6a21\u7cca\u3002\u91d1\u9010\u6d41\u559c\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u591c\u884c\u4eba\u7684\u597d\u5929\u6c14\uff0c\u54b1\u4eec\u53bb\u5427\uff01\u201d \u3000\u3000\u5230\u4e86\u5c71\u4e0a\uff0c\u53ea\u89c1\u4e00\u5ea7\u5821\u5792\u5f62\u7684\u5efa\u7b51\uff0c\u77d7\u7acb\u5c71\u5934\uff0c\u91d1\u9010\u6d41\u9053\uff1a\u201c\u9633\u6d69\u8fd9\u53ae\u5012\u4e5f\u771f\u6700\u4e0d\u5bb9\u5ffd \u89c6\uff0c\u5728\u77ed\u77ed\u7684\u4e24\u4e09\u4e2a\u6708\u4e4b\u4e2d\uff0c\u5c45\u7136\u80fd\u591f\u91cd\u5efa\u5929\u9b54\u6559\u7684\u9999\u5802\uff0c\u770b\u6765\u4ed6\u6240\u7ea0\u96c6\u7684\u5996\u90aa\u4e3a\u6570\u7684\u786e\u662f \u4e0d\u5c11\u4e86\u3002\u201d \u3000\u3000\u5f53\u4e0b\u4e24\u4eba\u65bd\u5c55\u8f7b\u529f\uff0c\u6500\u4e0a\u4e00\u68f5\u6570\u4e08\u9ad8\u7684\u53c2\u5929\u53e4\u6811\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u4fef\u77b0\u5821\u4e2d\u5f62\u52bf\u3002\u53ea\u662f\u5916\u9762 \u662f\u4e00\u9053\u56f4\u5899\uff0c\u6709\u56db\u5ea7\u94c1\u95e8\u5206\u7acb\u56db\u65b9\uff0c\u56f4\u5899\u4e4b\u5185\uff0c\u53c2\u5dee\u4e0d\u9f50\u7684\u7ea6\u83ab\u6709\u6570\u5341\u5e62\u623f\u5c4b\uff0c\u5f53\u4e2d\u4e00\u5ea7\u6700 \u9ad8\u7684\uff0c\u4f9d\u7167\u516c\u5b59\u5b8f\u7684\u56fe\u793a\uff0c\u5c31\u662f\u90a3\u4e2a\u5047\u5389\u5357\u661f\u6240\u4f4f\u7684\u6559\u4e3b\u201c\u5185\u9999\u5802\u201d\u4e86\u3002 \u3000\u3000\u56db\u5ea7\u94c1\u95e8\u662f\u4e1a\u5df2\u5173\u95ed\u7684\uff0c\u94c1\u95e8\u5916\u9762\uff0c\u5404\u6709\u4e00\u4e2a\u770b\u5b88\uff0c\u62b1\u67dd\u6253\u66f4\uff0c\u6765\u56de\u8e0f\u6b65\u3002\u95e8\u5e18\u6302\u7740\u4e00 \u76cf\u98ce\u706f\uff0c\u751a\u4e3a\u5149\u4eae\uff0c\u82e5\u662f\u6709\u4eba\u60f3\u5077\u8fdb\u53bb\uff0c\u51b3\u9003\u4e0d\u8fc7\u4ed6\u7684\u773c\u775b\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u56f4\u5899\u867d\u9ad8\uff0c\u96be\u4e0d\u5012\u54b1\u4eec\u3002\u96be\u7684\u662f\u600e\u6837\u6253\u53d1\u770b\u5b88\u3002\u201d\u8981\u77e5\u770b\u5b88\u662f\u517c\u7ba1 \u6253\u66f4\u7684\uff0c\u82e5\u662f\u628a\u4ed6\u6740\u4e86\uff0c\u91cc\u9762\u542c\u4e0d\u5230\u51fb\u67dd\u4e4b\u58f0\uff0c\u767b\u65f6\u5c31\u4f1a\u53d1\u89c9\u3002 \u3000\u3000\u4ed6\u4eec\u9762\u5bf9\u7740\u4e1c\u9762\u7684\u4e00\u5ea7\u94c1\u95e8\uff0c\u90a3\u770b\u5b88\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u5e94\u8be5\u662f\u6362\u73ed\u7684\u65f6\u5019\u4e86\uff0c\u600e\u4e48\u8fd8\u4e0d\u89c1 \u6765\uff1f\u8fc7\u4e86\u7247\u523b\uff0c\u679c\u7136\u89c1\u6709\u4e24\u4e2a\u6c49\u5b50\u6765\u5230\uff0c\u4e00\u4e2a\u662f\u5de1\u591c\u7684\u5927\u5934\u76ee\uff0c\u4e00\u4e2a\u662f\u63a5\u73ed\u7684\u770b\u5b88\uff0c\u90a3\u5934\u76ee \u95ee\u9053\uff1a\u201c\u53ef\u6709\u53d1\u73b0\u4ec0\u4e48\u53ef\u7591\u7684\u8ff9\u8c61\u6ca1\u6709\uff1f\u201d\u770b\u5b88\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u7684\u843d\u96e8\u5929\uff0c\u53c8\u51b7\u53c8\u6e7f\uff0c\u8fde\u591c \u795e\u90fd\u8eb2\u8fdb\u5792\u91cc\u53bb\u4e86\uff0c\u54ea\u4f1a\u6709\u4ec0\u4e48\u591c\u884c\u4eba\u6765\u5462\uff1f\u201d \u3000\u3000\u90a3\u5934\u76ee\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u4eec\u5c31\u6362\u73ed\u5427\u3002\u5929\u8272\u867d\u7136\u4e0d\u597d\uff0c\u4f46\u4e0b\u534a\u591c\u4ecd\u662f\u8981\u5c0f\u5fc3\u9632\u5907\u3002\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u5230\u522b\u5904\u5de1\u67e5\uff0c\u63a5\u73ed\u7684\u90a3\u4e2a\u770b\u5b88\u53f9\u6c14\u9053\uff1a\u201c\u771f\u5012\u9709\uff0c\u521a\u8f6e\u5230\u6211\u63a5\u73ed\u5c31\u4e0b\u96e8\u3002\u4f60\u53ef\u4ee5 \u6b47\u606f\u4e86\uff0c\u6211\u5374\u4e0d\u77e5\u600e\u6837\u624d\u80fd\u6328\u5230\u5929\u4eae\u3002\u201d \u3000\u3000\u63a5\u73ed\u7684\u8fd9\u4e2a\u5b88\u536b\u8eab\u4f53\u6bd4\u8f83\u7626\u5f31\uff0c\u4f3c\u4e4e\u6bd4\u4e0a\u4e00\u4e2a\u770b\u5b88\u66f4\u6015\u5bd2\u51b7\uff0c\u53ea\u89c1\u4ed6\u5728\u5bd2\u98ce\u51bb\u96e8\u4e4b\u4e2d \u201c\u535c\u535c\u535c\u201d\u7684\u4e00\u58f0\u58f0\u6253\u66f4\uff0c\u201c\u5509\u54b3\u5509\u201d\u7684\u4e00\u58f0\u58f0\u53f9\u6c14\u3002 \u3000\u3000\u8fd9\u4e2a\u5b88\u536b\u5509\u58f0\u53f9\u6c14\uff0c\u91d1\u9010\u6d41\u5374\u662f\u559c\u7b11\u989c\u5f00\uff0c\u84e6\u5730\u91cc\u8ba1\u4e0a\u5fc3\u6765\uff1a\u201c\u6709\u4e86\uff0c\u6709\u4e86\uff01\u201d\u6458\u4e0b\u4e00 \u9897\u677e\u5b50\uff0c\u5f53\u8fd9\u5b88\u536b\u8f6c\u8fc7\u8eab\u7684\u65f6\u5019\uff0c\u5bf9\u51c6\u4e86\u4ed6\u80cc\u5fc3\u7684\u6655\u7761\u7a74\u4e00\u5f39\u3002 \u3000\u3000\u8fd9\u5b88\u536b\u53eb\u90fd\u672a\u66fe\u53eb\u5f97\u51fa\u6765\uff0c\u7a81\u7136\u5c31\u50cf\u7740\u9b54\u4f3c\u7684\uff0c\u6643\u4e86\u4e24\u6643\uff0c\u8eab\u8eaf\u5012\u4e0b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u81ea\u6811\u9876\u4e00\u8dc3\u800c\u4e0b\uff0c\u6377\u5982\u98de\u9e1f\uff0c\u4e0d\u5f85\u4ed6\u7684\u8eab\u5b50\u5012\u5730\uff0c\u5df2\u662f\u6293\u7740\u4e86\u4ed6\u3002\u4e00\u624b\u62a2\u8fc7\u4e86\u6253 \u66f4\u7528\u7684\u201c\u67dd\u201d\uff0c\u8ddf\u7740\u201c\u535c\u535c\u535c\u201d\u5730\u6253\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6b64\u65f6\uff0c\u90a3\u4e2a\u5de1\u591c\u7684\u5927\u5934\u76ee\u65e9\u5df2\u56de\u53bb\u4e86\u3002\u5821\u5792\u7684\u56db\u5ea7\u95e8\u867d\u7136\u5404\u6709\u4e00\u4e2a\u770b\u5b88\uff0c\u4f46\u5374\u53ea\u662f\u51fb\u67dd \u4e4b\u58f0\u5f7c\u6b64\u76f8\u95fb\uff0c\u4e0d\u80fd\u76f8\u89c1\u7684\u3002\u6253\u66f4\u7684\u58f0\u97f3\u5e76\u6ca1\u4e2d\u65ad\uff0c\u5176\u4ed6\u4e09\u4e2a\u770b\u5b88\u5f53\u7136\u662f\u4e0d\u77e5\u9053\u8fd9\u91cc\u53d1\u751f\u7684 \u4e8b\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u8ddf\u7740\u8dc3\u4e0b\uff0c\u6084\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u6253\u7b97\u600e\u6837\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5feb\uff0c\u6362\u4e0a\u4ed6\u7684\u5916\u8863\uff0c\u62ab\u4e0a \u4ed6\u7684\u6597\u7bf7\u3002\u201d \u3000\u3000\u8fd9\u5b88\u536b\u8eab\u6750\u7626\u5c0f\uff0c\u53f2\u7ea2\u82f1\u7a7f\u4e0a\u4ed6\u7684\u5916\u8863\uff0c\u62ab\u4e0a\u4ed6\u7684\u5934\u7b20\uff0c\u53ea\u662f\u7a0d\u5acc\u5bbd\u5927\u4e00\u4e9b\uff0c\u4f46\u6597\u7bf7\u906e \u8fc7\u4e86\u534a\u8fb9\u8138\u5b54\uff0c\u5728\u9634\u6697\u7684\u96e8\u591c\uff0c\u82e5\u4e0d\u662f\u8d70\u8fd1\u4e86\u5c31\u7740\u706f\u5149\u6765\u770b\uff0c\u6025\u5207\u95f4\u90a3\u662f\u51b3\u8ba1\u770b\u4e0d\u51fa\u7834\u7efd\u7684 \u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u7ea2\u82f1\uff0c\u4f60\u6743\u4e14\u5192\u5145\u66f4\u592b\u5427\u3002\u201d\u53f2\u7ea2\u82f1\u63a5\u8fc7\u67dd\u6728\uff0c\u535c\u535c\u535c\u5730\u6253\u8d77\u6765\uff1b\u91d1\u9010 \u6d41\u63d0\u8d77\u90a3\u4e2a\u770b\u5b88\uff0c\u8dd1\u5230\u6797\u4e2d\uff0c\u628a\u4ed6\u85cf\u5728\u4e24\u5757\u5ca9\u77f3\u5408\u62b1\u7684\u7a7a\u9699\u91cc\uff0c\u7b11\u9053\uff1a\u201c\u670b\u53cb\uff0c\u8fd9\u91cc\u6696\u548c\u591a \u4e86\uff0c\u4fbf\u5b9c\u4f60\u5566\uff01\u201d \u3000\u3000\u5904\u7f6e\u4e86\u90a3\u4e2a\u5b88\u536b\uff0c\u91d1\u9010\u6d41\u56de\u5230\u53f2\u7ea2\u82f1\u8ddf\u524d\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u574f\u7684\u5929\u6c14\uff0c\u6599\u60f3\u5de1\u591c\u7684\u5934 \u76ee\u4e0d\u4f1a\u8fd9\u6837\u5feb\u53c8\u51fa\u6765\u7684\u3002\u82e5\u7136\u6709\u4eba\u51fa\u6765\uff0c\u4f60\u628a\u4ed6\u6740\u4e86\u5c31\u662f\u3002\u53ea\u987b\u534a\u4e2a\u65f6\u8fb0\u4e4b\u5185\u6211\u6ca1\u7ed9\u4eba\u53d1 \u89c9\uff0c\u6211\u60f3\u4e5f\u8db3\u591f\u6211\u7528\u6765\u8c03\u67e5\u771f\u76f8\u4e86\u3002\u201d \u3000\u3000\u5929\u9b54\u6559\u7684\u603b\u8235\u9632\u8303\u5f97\u76f8\u5f53\u4e25\u5bc6\uff0c\u4e09\u4e08\u591a\u9ad8\u7684\u56f4\u5899\u4e0a\u9762\u8fd8\u63d2\u6ee1\u4e86\u94c1\u9489\uff0c\u4f46\u8fd9\u6240\u4ee5\u96be\u5012\u522b \u4eba\uff0c\u5374\u96be\u4e0d\u5012\u91d1\u9010\u6d41\uff0c\u4ed6\u6839\u672c\u4e0d\u7528\u6500\u767b\uff0c\u4e00\u4e2a\u201c\u9ec4\u9e4a\u51b2\u9704\u201d\uff0c\u5df2\u662f\u6377\u5982\u98de\u9e1f\u822c\u5730\u8d8a\u8fc7\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4f9d\u7167\u5730\u56fe\u7684\u6307\u793a\uff0c\u86c7\u884c\u5154\u4f0f\uff0c\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u5230\u4e86\u5929\u9b54\u6559\u6559\u4e3b\u6240\u4f4f\u7684\u5185\u9999\u5802\u3002 \u3000\u3000\u53ea\u89c1\u623f\u4e2d\u706f\u706b\u672a\u706d\uff0c\u7eb1\u7a97\u4e0a\u73b0\u51fa\u4e00\u4e2a\u4eba\u5f71\uff0c\u91d1\u9010\u6d41\u4f0f\u5728\u4e00\u5757\u5047\u5c71\u77f3\u7684\u540e\u9762\uff0c\u51dd\u7738\u770b\u53bb\uff0c \u4e0d\u7531\u5f97\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u8fd9\u53ae\u679c\u7136\u662f\u5047\u5f97\u7ef4\u5999\u7ef4\u8096\uff0c\u82e5\u7136\u4e0d\u662f\u6211\u65e9\u5c31\u77e5\u9053\u4ed6\u662f\u5192\u5145\uff0c\u5728\u522b\u5904\u89c1\u7740 \u4ed6\uff0c\u4e00\u5b9a\u4f1a\u628a\u4ed6\u5f53\u4f5c\u5389\u5927\u54e5\u4e86\uff01\u201d \u3000\u3000\u90a3\u4eba\u597d\u4f3c\u53d1\u89c9\u4e86\u4ec0\u4e48\uff0c\u4f5c\u51fa\u4fa7\u8033\u7ec6\u542c\u7684\u6a21\u6837\uff0c\u5ffd\u5730\u5439\u706d\u4e86\u706f\u3002\u91d1\u9010\u6d41\u6280\u9ad8\u80c6\u5927\uff0c\u4e0d\u7406\u4ed6 \u623f\u4e2d\u6709\u6ca1\u6709\u57cb\u4f0f\uff0c\u7acb\u5373\u8df3\u51fa\uff0c\u4e00\u638c\u63a8\u5f00\u4e86\u7a97\u6237\uff0c\u7eb5\u8eab\u8dc3\u5165\uff0c\u90a3\u4eba\u51b5\u58f0\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u58f0\u72b9 \u672a\u4e86\uff0c\u91d1\u9010\u6d41\u5df2\u662f\u4e00\u628a\u6293\u4f4f\u4e86\u4ed6\u3002\u53ef\u662f\u91d1\u9010\u6d41\u542c\u5230\u4ed6\u7684\u58f0\u97f3\uff0c\u5374\u662f\u7981\u4e0d\u4f4f\u597d\u751f\u8be7\u5f02\uff01 \u3000\u3000\u8fd9\u4e2a\u5047\u5192\u5389\u5357\u661f\u7684\u4eba\uff0c\u4e0d\u4f46\u662f\u76f8\u8c8c\u7ef4\u5999\u7ef4\u8096\uff0c\u8fde\u8bf4\u8bdd\u7684\u58f0\u97f3\u4e5f\u662f\u4e00\u6a21\u4e00\u6837\uff01 \u3000\u3000\u91d1\u9010\u6d41\u8bb0\u5f97\u516c\u5b59\u5b8f\u66fe\u7ecf\u5bf9\u4ed6\u8bf4\u8fc7\uff0c\u8bf4\u662f\u90a3\u4e2a\u65b0\u6559\u4e3b\u58f0\u97f3\u5636\u54d1\uff0c\u548c\u5389\u5357\u661f\u5e76\u975e\u4e00\u6837\u7684\u3002\u4ed6 \u5728\u4e8b\u540e\u60f3\u8d77\uff0c\u5140\u662f\u4e00\u76f4\u540e\u6094\uff0c\u540e\u6094\u5f53\u65f6\u6ca1\u6709\u770b\u51fa\u8fd9\u4e2a\u7834\u7efd\u3002 \u3000\u3000\u4f46\u6b64\u523b\uff0c\u91d1\u9010\u6d41\u542c\u5230\u7684\u5374\u662f\u5389\u5357\u661f\u7684\u58f0\u97f3\uff01 \u3000\u3000\u201c\u5929\u4e0b\u54ea\u6709\u5047\u5f97\u5982\u6b64\u76f8\u4f3c\u7684\u4eba\uff1f\u201d\u91d1\u9010\u6d41\u5f53\u7136\u662f\u7981\u4e0d\u4f4f\u6014\u4e86\u4e00\u6014\u4e86\u3002 \u3000\u3000\u90a3\u4eba\u7684\u6b66\u529f\u5f88\u662f\u4e0d\u5f31\uff0c\u7ed9\u91d1\u9010\u6d41\u4e00\u628a\u6293\u4f4f\uff0c\u8fc5\u5373\u5c31\u662f\u4e00\u4e2a\u201c\u8131\u888d\u89e3\u7532\u201d\u53cd\u624b\u70b9\u5411\u91d1\u9010\u6d41 \u80c1\u4e0b\u7684\u6108\u6c14\u7a74\uff0c\u9ed1\u591c\u4e4b\u4e2d\uff0c\u8ba4\u7a74\u7adf\u662f\u4e0d\u5dee\u6beb\u5398\u3002 \u3000\u3000\u91d1\u9010\u6d41\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u4e00\u62db\u201c\u62c2\u4e91\u624b\u201d\u8361\u5f00\u90a3\u4eba\u7684\u6307\u6293\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5e76\u975e\u9707\u60ca\u4e8e\u90a3\u4eba\u7684\u6b66\u529f\uff0c\u800c\u662f\u56e0\u4e3a\u90a3\u4eba\u4f7f\u51fa\u7684\u62db\u6570\uff0c\u6b63\u662f\u4ed6\u7236\u4eb2\u72ec\u521b\u7684\u4e00\u95e8\u638c \u6cd5\uff01 \u3000\u3000\u4e0d\u7ea6\u800c\u540c\u7684\u90a3\u4eba\u4e5f\u5728\u9a87\u7136\u60ca\u53eb\u9053\uff1a\u201c\u4f60\u662f\u9010\u6d41\u8d24\u5f1f\u4e48\uff1f\u201d\u4ed6\u4e0e\u91d1\u9010\u6d41\u95ea\u7535\u822c\u7684\u4ea4\u624b\u4e24 \u62db\uff0c\u4e5f\u5df1\u8ba4\u51fa\u4e86\u91d1\u9010\u6d41\u7684\u62db\u6570\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5403\u60ca\u66f4\u751a\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u5f53\u771f\u662f\u5389\u5927\u54e5\u4e0d\u6210\uff1f\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff0c\u5389\u5927\u54e5\u5c82\u80fd\u53d8\u8282\u6295 \u964d\uff0c\u81ea\u7518\u5815\u843d\uff0c\u4e0e\u9633\u6d69\u8fd9\u8001\u8d3c\u540c\u6d41\u5408\u6c61\uff1f\u4f46\u4e3a\u4ec0\u4e48\u4ed6\u4e5f\u4f1a\u5927\u987b\u5f25\u638c\u5f0f\uff1f\u201d\u5fc3\u91cc\u60ca\u7591\u4e0d\u5b9a\uff0c\u5148 \u95ea\u8fc7\u4e00\u8fc7\uff0c\u6a2a\u638c\u80f8\u524d\uff0c\u63d0\u9632\u5077\u88ad\uff0c\u53e6\u4e00\u53ea\u624b\u5c31\u53bb\u6478\u7d22\u706f\u53f0\uff0c\u51c6\u5907\u70b9\u7740\u4e86\u706f\uff0c\u518d\u770b\u4e2a\u4ed4\u7ec6\u3002 \u3000\u3000\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u7740\u706f\uff01\u201d\u968f\u5373\u4f4e\u58f0\u541f\u4e00\u9996\uff1a\u201c\u8131\u7565\u5f62\u9ab8\u8fc8\u4fd7\u6ca5\uff0c\u76f8\u4ea4\u6bcb\u8d1f\u5c11\u5e74\u5934\u3002\u8c03 \u5f26\u96c5\u97f5\u916c\u77e5\u5df1\uff0c\u51fa\u5323\u96c4\u8292\u65a9\u5bc7\u4ec7\u3002\u4f11\u9053\u9f99\u86c7\u5f52\u8349\u83bd\uff0c\u83ab\u6559\u7434\u5251\u5fd6\u9ad8\u697c\u3002\u4e2d\u539f\u81ea\u6709\u82f1\u8c6a\u5728\uff0c\u6d77 \u5916\u5f52\u6765\u559c\u8c41\u7738\u3002\u201d \u3000\u3000\u8fd9\u662f\u5389\u5357\u661f\u9001\u7ed9\u91d1\u9010\u6d41\u7684\u4e00\u9996\u8bd7\uff0c\u5f53\u5e74\u4ed6\u4eec\u7434\u5251\u76f8\u4ea4\uff0c\u5389\u5357\u661f\u8c31\u4e86\u8fd9\u9996\u8bd7\u9001\u7ed9\u91d1\u9010\u6d41\u8868 \u793a\u53cb\u8c0a\u7684\u3002\u8fd9\u662f\u5389\u5357\u661f\u81ea\u5df1\u505a\u7684\u8bd7\uff0c\u9664\u4e86\u4ed6\u548c\u91d1\u9010\u6d41\u4e4b\u5916\uff0c\u522b\u4eba\u51b3\u8ba1\u5ff5\u4e0d\u51fa\u6765\uff01 \u3000\u3000\u91d1\u9010\u6d41\u542c\u4e86\u8fd9\u9996\u8bd7\uff0c\u5df2\u662f\u4e0d\u5bb9\u4ed6\u518d\u6709\u6000\u7591\u4e86\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u539f\u6765\u4f60\u679c\u7136\u662f\u5389\u5927\u54e5\uff0c\u4f46 \u8fd9\uff0c\u8fd9\u5374\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u5462\uff1f\u201d\u8981\u77e5\u91d1\u9010\u6d41\u867d\u7136\u4e0d\u518d\u6000\u7591\u8fd9\u4eba\u662f\u5047\u5389\u5357\u661f\uff0c\u4f46\u5389\u5357\u661f\u4f55\u4ee5\u4f1a\u7ed9 \u9633\u6d69\u5229\u7528\uff0c\u505a\u4e86\u5929\u9b54\u6559\u7684\u6559\u4e3b\uff0c\u4ed6\u4ecd\u662f\u767e\u601d\u4e0d\u5f97\u5176\u89e3\uff01 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u53c8\u6709\u811a\u6b65\u58f0\u8d70\u6765\uff0c\u5389\u5357\u661f\u9053\uff1a\u201c\u6211\u8bf7\u4f60\u770b\u4e00\u573a\u628a\u620f\uff0c\u4f60\u5c31\u4f1a\u660e\u767d \u4e86\u3002\u201d\u628a\u91d1\u9010\u6d41\u4e00\u62c9\uff0c\u4e24\u4eba\u8eb2\u5230\u5e8a\u58c1\u540e\u9762\u3002 \u3000\u3000\u53ea\u542c\u5f97\u9633\u6d69\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4eca\u665a\u4f60\u597d\u597d\u60f3\u4e00\u60f3\uff0c\u660e\u513f\u54b1\u4eec\u518d\u8c08\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u8d34\u7740\u91d1\u9010\u6d41\u7684\u8033\u6735\u8bf4\u9053\uff1a\u201c\u548c\u9633\u6d01\u4e00\u8d77\u7684\u8fd9\u4e2a\u4eba\uff0c\u5c31\u662f\u5192\u6211\u4e4b\u540d\u7684\u90a3\u4e2a\u6559\u4e3b\u4e86\uff01 \u3000\u3000\u90a3\u6559\u4e3b\u5ffd\u5730\u8f7b\u8f7b\u5730\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u9633\u5e08\u4f2f\uff0c\u6211\u8fd8\u60f3\u548c\u4f60\u8c08\u8c08\uff0c\u8bf7\u4f60\u8fdb\u6765\u518d\u5750\u4e00 \u4f1a\u3002\u201d\u539f\u6765\u4ed6\u8bb0\u5f97\u51fa\u6765\u4e4b\u65f6\u95e8\u7a97\u90fd\u662f\u5df2\u7ecf\u5173\u597d\u4e86\u7684\uff0c\u73b0\u5728\u53d1\u73b0\u7a97\u5b50\u6253\u5f00\uff0c\u5df2\u77e5\u5185\u91cc\u5b9a\u7136\u6709 \u53d8\uff0c\u4e0d\u80fd\u4e0d\u63d0\u9632\u6709\u4eba\u85cf\u5728\u623f\u4e2d\u3002\u4ed6\u4e0d\u6562\u660e\u8a00\uff0c\u53ea\u80fd\u5411\u9633\u6d69\u6697\u793a\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5728\u5389\u5357\u661f\u8033\u8fb9\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u6765\u5f97\u6b63\u597d\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u9633\u6d69 \u5df2\u662f\u4e00\u638c\u63a8\u5f00\u623f\u95e8\uff0c\u53cc\u811a\u672a\u66fe\u8e0f\u8fdb\uff0c\u4fee\u7f57\u9634\u715e\u529f\u7684\u638c\u529b\u5df2\u7136\u53d1\u51fa\u4e86\u3002 \u3000\u3000\u9633\u6d69\u4ee5\u4e3a\u8eb2\u5728\u623f\u4e2d\u7684\u662f\u5185\u5978\uff0c\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u662f\u91d1\u9010\u6d41\u548c\u5389\u5357\u661f\u4e8c\u4eba\u3002\u4ed6\u7684\u4fee\u7f57\u9634\u715e\u529f\u5728 \u5929\u9b54\u6559\u4e2d\u662f\u65e0\u4eba\u80fd\u654c\u7684\uff0c\u5fc3\u60f3\u6211\u5148\u53eb\u8fd9\u53ae\u5403\u70b9\u82e6\u5934\u518d\u8bf4\u3002 \u3000\u3000\u4e0d\u6599\u5403\u82e6\u5934\u7684\u4e0d\u662f\u5978\u7ec6\uff0c\u53cd\u800c\u662f\u4ed6\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u91d1\u9010\u6d41\u65e9\u5df2\u4e00\u8dc3\u800c\u51fa\uff0c\u9a88\u6307\u5982 \u621f\uff0c\u70b9\u5411\u4ed6\u80f8\u53e3\u7487\u7391\u7a74\u3002 \u3000\u3000\u9633\u6d69\u4e5f\u5f53\u771f\u4e86\u5f97\uff0c\u9aa4\u7136\u9047\u88ad\uff0c\u867d\u60ca\u4e0d\u4e71\uff0c\u53cd\u624b\u4e00\u52fe\uff0c\u4f7f\u51fa\u5c0f\u64d2\u62ff\u624b\u6cd5\u53cd\u6263\u91d1\u9010\u6d41\u7684\u8109 \u95e8\uff01\u91d1\u9010\u6d41\u5316\u6307\u4e3a\u638c\uff0c\u4e00\u4e2a\u5927\u987b\u5f25\u638c\u5f0f\u5411\u4ed6\u80f8\u819b\u5370\u4e0b\u3002 \u3000\u3000\u53cc\u638c\u76f8\u4ea4\uff0c\u9633\u6d69\u7981\u4e0d\u4f4f\u8fde\u9000\u4e09\u6b65\uff0c\u7ed9\u91d1\u9010\u6d41\u7684\u638c\u529b\u5c06\u4ed6\u9707\u51fa\u4e86\u95e8\u5916\u3002\u4f46\u91d1\u9010\u6d41\u4e5f\u53ea\u662f\u7565 \u5360\u4e0a\u98ce\uff0c\u672a\u80fd\u5c06\u4ed6\u6293\u4f4f\u3002 \u3000\u3000\u9633\u6d69\u8fd9\u4e00\u60ca\u624d\u5f53\u771f\u662f\u975e\u540c\u5c0f\u53ef\uff01\u8981\u77e5\u4ed6\u7684\u4fee\u7f57\u9634\u715e\u529f\u5df2\u7ecf\u7ec3\u5230\u4e86\u7b2c\u516b\u91cd\uff0c\u5bfb\u5e38\u4e4b\u8f88\uff0c\u5728 \u4ed6\u638c\u98ce\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u5df2\u662f\u8981\u51b7\u5f97\u50f5\u786c\uff0c\u54ea\u91cc\u8fd8\u80fd\u548c\u4ed6\u52a8\u624b\uff1f\u4f46\u5982\u4eca\u8fd9\u4e2a\u4eba\u975e\u4f46\u80fd\u591f\u548c\u4ed6\u52a8\u624b\uff0c \u800c\u4e14\u8fd8\u80fd\u591f\u786c\u63a5\u4ed6\u7684\u7b2c\u516b\u91cd\u7684\u4fee\u7f57\u9634\u715e\u529f\u7684\u638c\u5200\uff0c\u786c\u78b0\u786c\u7684\u5c06\u4ed6\u4e00\u638c\u51fb\u9000\uff01 \u3000\u3000\u9633\u6d69\u662f\u66fe\u7ecf\u548c\u91d1\u9010\u6d41\u4ea4\u8fc7\u51e0\u6b21\u624b\u7684\uff0c\u6b64\u65f6\u867d\u7136\u672a\u66fe\u770b\u89c1\u91d1\u9010\u6d41\u7684\u9762\u8c8c\uff0c\u4ea6\u5df2\u77e5\u9053\u6765\u7684\u662f \u4ed6\u4e86\u3002 \u3000\u3000\u90a3\u6559\u4e3b\u8ddf\u5728\u9633\u6d69\u540e\u9762\uff0c\u6b63\u8981\u8fdb\u6765\uff0c\u9633\u6d69\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5feb\u8dd1\uff01\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u8dd1\u4e0d\u4e86 \u5566\uff01\u201d\u8eab\u5f62\u75be\u8d77\uff0c\u5140\u9e70\u6251\u5154\u822c\u7684\u51cc\u7a7a\u5411\u90a3\u6559\u4e3b\u6251\u4e0b\uff0c\u9633\u6d69\u60c5\u77e5\u4ed6\u7684\u5e08\u4f84\u51b3\u7981\u4e0d\u8d77\u91d1\u9010\u6d41\u7684\u8fd9 \u4e00\u638c\uff0c\u53ea\u597d\u4e5f\u662f\u4f9d\u6837\u753b\u846b\u82a6\u5730\u8df3\u8d77\u8eab\u6765\uff0c\u548c\u91d1\u9010\u6d41\u5728\u7a7a\u4e2d\u5bf9\u4e86\u4e00\u638c\u3002 \u3000\u3000\u90a3\u6559\u4e3b\u4e00\u9762\u8dd1\u4e00\u9762\u53eb\u9053\uff1a\u201c\u6709\u5978\u7ec6\uff0c\u6765\u4eba\u54ea\uff01\u201d\u521a\u8dd1\u5f97\u51e0\u6b65\uff0c\u9661\u7136\u95f4\u53ea\u89c9\u80a9\u4e0a\u7684\u7435\u7436\u9aa8 \u4e00\u9ebb\uff0c\u539f\u6765\u5df2\u662f\u7ed9\u5389\u5357\u661f\u5c06\u4ed6\u6293\u4f4f\u4e86\uff01 \u3000\u3000\u9633\u6d69\u548c\u91d1\u9010\u6d41\u5bf9\u4e86\u4e00\u638c\uff0c\u80f8\u53e3\u5982\u53d7\u91cd\u538b\uff0c\u843d\u4e86\u4e0b\u6765\uff0c\u7ffb\u8fc7\u4e00\u5ea7\u5047\u5c71\uff0c\u5360\u4e86\u6709\u5229\u7684\u5730\u5f62\uff0c \u51c6\u5907\u5e94\u4ed8\u91d1\u9010\u6d41\u7684\u653b\u51fb\u3002\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u91d1\u9010\u6d41\uff0c\u4f60\u7eb5\u6709\u4e09\u5934\u516d\u81c2\uff0c\u4eca\u665a\u4e5f\u662f\u9003\u4e0d\u51fa\u53bb\u7684\u4e86\uff01 \u4f60\u4e0d\u8981\u4ee5\u4e3a\u62ff\u4f4f\u4e86\u6211\u4eec\u7684\u6559\u4e3b\uff0c\u5c31\u53ef\u4ee5\u8981\u631f\u6211\u4eec\uff0c\u54b1\u4eec\u8fd8\u662f\u597d\u597d\u7684\u5546\u91cf\u5546\u91cf\u5427\uff01\u201d \u3000\u3000\u5929\u9b54\u6559\u4e0a\u4e0b\u4eba\u7b49\uff0c\u542c\u5230\u4e86\u6559\u4e3b\u7684\u53eb\u58f0\uff0c\u6b64\u65f6\u5df2\u662f\u7eb7\u7eb7\u5730\u8d76\u6765\u6349\u62ff\u5978\u7ec6\u3002\u5389\u5357\u661f\u628a\u90a3\u6559\u4e3b \u62d6\u8fdb\u623f\u4e2d\uff0c\u53eb\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u56de\u6765\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u83ab\u540d\u5176\u5999\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5389\u5927\u54e5\u597d\u7cca\u6d82\uff0c\u654c\u4f17\u6211\u5be1\uff0c\u62fc\u547d\u51b2\u51fa\u53bb\u6216\u8005\u8fd8\u53ef\u4ee5\u6b7b\u91cc \u9003\u751f\uff0c\u8eb2\u8fdb\u623f\u4e2d\uff0c\u90a3\u5c82\u4e0d\u662f\u53d8\u6210\u8ba9\u4eba\u5bb6\u74ee\u4e2d\u6349\u9cd6\u4e86\uff1f\u201d\u4f46\u56e0\u5389\u5357\u661f\u5df2\u7ecf\u8fdb\u53bb\uff0c\u4ed6\u81ea\u662f\u4e0d\u80fd\u5355 \u72ec\u7a81\u56f4\uff0c\u53ea\u597d\u4e5f\u8ddf\u7740\u8fdb\u53bb\u3002 \u3000\u3000\u9633\u6d69\u672c\u6765\u6709\u70b9\u5bb3\u6015\u91d1\u9010\u6d41\u51b2\u8fc7\u6765\u548c\u4ed6\u62fc\u547d\uff0c\u6b64\u65f6\u89c1\u91d1\u3001\u5389\u4e8c\u4eba\u90fd\u5df2\u8eb2\u5165\u623f\u4e2d\uff0c\u4e0d\u7981\u54c8\u54c8 \u5927\u7b11\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u91d1\u9010\u6d41\uff0c\u4f60\u4eec\u603b\u4e0d\u80fd\u505a\u4e00\u8f88\u5b50\u5f53\u7f29\u5934\u4e4c\u9f9f\u5427\uff1f\u4eba\u6765\uff0c\u6bd2\u7bad\u3001\u55b7\u7b52\u4f3a \u5019\uff01\u201d\u6b64\u65f6\u9633\u6d69\u7684\u515a\u7fbd\u548c\u5929\u9b54\u6559\u7684\u5927\u5c0f\u5934\u76ee\u90fd\u5df2\u6765\u5230\uff0c\u5728\u9633\u6d69\u6307\u6325\u4e4b\u4e0b\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u662f\u628a \u90a3\u95f4\u623f\u5b50\u56e2\u56e2\u56f4\u7740\uff01\u9633\u6d69\u5f97\u610f\u4e4b\u6781\uff0c\u5927\u7b11\u8bf4\u9053\uff1a\u201c\u5148\u8ba9\u4f60\u4eec\u77e5\u9053\u4e00\u70b9\u5389\u5bb3\uff01\u201d\u4ece\u4e00\u4e2a\u5934\u76ee\u624b \u4e2d\u53d6\u8fc7\u4e00\u526f\u5f13\u7bad\uff0c\u201c\u54e9\u54e9\u201d\u4e24\u58f0\uff0c\u4e24\u679d\u7bad\u7834\u7a97\u800c\u5165\uff0c\u63d2\u5728\u5899\u4e0a\u3002\u9633\u6d69\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u5389\u5357\u661f\uff0c \u4f60\u662f\u4f7f\u6bd2\u7684\u884c\u5bb6\uff0c\u4f60\u53ef\u4ee5\u9a8c\u770b\u8fd9\u4e24\u679d\u6bd2\u7bad\uff0c\u662f\u4e0d\u662f\u89c1\u8840\u5c01\u5589\u7684\u6bd2\u7bad\u3002\u63a5\u7740\u53c8\u53d6\u8fc7\u4e00\u53ea\u55b7\u7b52\uff0c \u4e00\u6309\u673a\u5173\uff0c\u55b7\u51fa\u4e00\u6e9c\u706b\u5149\uff0c\u767b\u65f6\u7a97\u5b50\u7740\u706b\u3002\u91d1\u9010\u6d41\u4e00\u8bb0\u5288\u7a7a\u638c\u6253\u51fa\uff0c\u628a\u70e7\u7740\u7684\u6728\u5934\u6253\u6389\uff0c\u843d \u5728\u7a97\u5916\uff0c\u90a3\u4e00\u6e9c\u706b\u5149\uff0c\u8f6c\u77ac\u5373\u706d\uff0c\u6ca1\u6709\u70e7\u8fdb\u623f\u6765\u3002\u4f46\u4e00\u80a1\u7126\u81ed\u7684\u6c14\u5473\uff0c\u5df2\u662f\u5f25\u6f2b\u623f\u4e2d\uff0c\u663e\u7136 \u4ece\u8fd9\u55b7\u7b52\u55b7\u51fa\u7684\u4e5f\u662f\u6bd2\u706b\u3002 \u3000\u3000\u9633\u6d69\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u91d1\u9010\u6d41\uff0c\u82e5\u53ea\u662f\u51e0\u526f\u5f13\u7bad\u51e0\u53ea\u55b7\u7b52\uff0c\u90a3\u81ea\u662f\u5948\u4f60\u4e0d\u4f55\uff0c\u4f46\u73b0\u5728\u4e0d\u662f\u51e0 \u526f\u51e0\u53ea\uff0c\u800c\u662f\u6210\u5343\u4e0a\u767e\uff0c\u4f60\u7eb5\u6709\u4e09\u5934\u516d\u81c2\uff0c\u5341\u6761\u6027\u547d\uff0c\u4e5f\u662f\u7edd\u8ba1\u96be\u9003\u7684\u4e86\uff01\u563f\u563f\uff0c\u518d\u8bf4\u4f60\u60f3 \u505a\u7f29\u5934\u4e4c\u9f9f\u4e5f\u4e0d\u6210\uff0c\u5927\u4e0d\u4e86\u6211\u8ba9\u5e08\u4f84\u966a\u846c\uff0c\u4e00\u628a\u706b\u5c31\u628a\u8fd9\u623f\u5b50\u70e7\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u4ece\u70e7\u7834\u7684\u7a97\u53e3\u671b\u51fa\u53bb\uff0c\u53ea\u89c1\u7bad\u7c07\u7684\u5bd2\u8292\u5b9b\u4f3c\u7e41\u661f\uff0c\u4e00\u53ea\u53ea\u4e4c\u9ed1\u7684\u55b7\u7b52\u4fe8\u5982\u65e0\u6570\u6bd2 \u86c7\u6602\u5934\u5bf9\u7740\u7a97\u53e3\u3002 \u3000\u3000\u9633\u6d69\u7b11\u9053\uff1a\u201c\u770b\u6e05\u695a\u6ca1\u6709\uff1f\u73b0\u5728\u6211\u7ed9\u534a\u4e2a\u65f6\u8fb0\u8ba9\u4f60\u4eec\u5546\u91cf\uff0c\u8bc6\u8da3\u7684\u4e56\u4e56\u6295\u964d\uff0c\u5426\u5219\u4f11\u602a \u6211\u4e0b\u8fa3\u624b\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u6697\u81ea\u5bfb\u601d\uff1a\u201c\u5982\u6b64\u9635\u4ed7\uff0c\u770b\u6765\u51b2\u51fa\u53bb\u4e5f\u662f\u96be\u9003\u6027\u547d\u7684\u4e86\u3002\u4e0d\u8fc7\uff0c\u603b\u80dc\u4e8e\u675f\u624b\u5f85 \u6bd9\uff01\u201d\u6b63\u60f3\u4e0e\u5389\u5357\u661f\u8bf4\u8bdd\uff0c\u5389\u5357\u661f\u5df2\u5728\u4ed6\u8033\u8fb9\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u6709\u529e\u6cd5\u5e73\u5b89\u8131\u9669\uff0c\u4f60\u770b\u597d\u8fd9 \u53ae\uff0c\u4e14\u4e0d\u5fc5\u70b9\u4ed6\u7a74\u9053\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6293\u7740\u90a3\u6559\u4e3b\u7684\u7435\u7436\u9aa8\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u52a8\u4e00\u52a8\u6211\u5c31\u8981\u4f60\u7684\u547d\uff01\u201d\u53ea\u89c1\u5389\u5357\u661f\u642c\u5f00\u90a3\u5f20 \u5927\u5e8a\uff0c\u4f0f\u5728\u5730\u4e0a\u6478\u7d22\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u79bb\u5899\u4e09\u5c3a\u516d\u5bf8\uff0c\u9752\u7816\u4e0a\u6709\u73af\u72b6\u51f9\u75d5\u4e3a\u8bb0\u3002\u662f\u8fd9\u91cc \u4e86\uff01\u201d\u5f53\u5e74\u5389\u5357\u661f\u7684\u7236\u6bcd\u5efa\u9020\u8fd9\u95f4\u6559\u4e3b\u7684\u5bdd\u623f\u4e4b\u65f6\uff0c\u7528\u7684\u662f\u7279\u522b\u575a\u539a\u7684\u5927\u9752\u7816\uff0c\u6545\u6b64\u7ecf\u8fc7\u4e8c \u5341\u4f59\u5e74\uff0c\u5c1a\u65e0\u635f\u574f\u3002\u6b64\u6b21\u9633\u6d69\u91cd\u4fee\u623f\u5c4b\uff0c\u53ea\u662f\u52a0\u4e0a\u4e0a\u76d6\uff0c\u5730\u4e0b\u7684\u7816\u5934\u5e76\u65e0\u6389\u6362\u3002 \u3000\u3000\u5389\u5357\u661f\u63ed\u5f00\u4e86\u4e24\u5757\u9752\u7816\uff0c\u9732\u51fa\u4e00\u4e2a\u6d1e\u53e3\uff0c\u4e00\u80a1\u9709\u81ed\u7684\u6c14\u5473\u51b2\u4e86\u51fa\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u53d6\u51fa\u4e24\u9897\u989c\u8272\u78a7\u989c\u7684\u4e38\u836f\uff0c\u8fd9\u662f\u7528\u5929\u5c71\u96ea\u83b2\u70ae\u5236\u7684\u78a7\u7075\u4e39\uff0c\u529f\u80fd\u795b\u6bd2\u89e3\u79fd\uff0c\u5206\u4e86 \u4e00\u9897\u7ed9\u5389\u5357\u661f\uff0c\u7eb3\u5165\u53e3\u4e2d\uff0c\u5f53\u4e0b\u4fbf\u628a\u90a3\u4e2a\u5047\u5192\u5389\u5357\u661f\u7684\u5929\u9b54\u6559\u6559\u4e3b\uff0c\u4e00\u540c\u62d6\u8fdb\u5730\u6d1e\u3002 \u3000\u3000\u5389\u5357\u661f\u4eae\u8d77\u706b\u6298\uff0c\u53ea\u89c1\u662f\u4e00\u6761\u671b\u4e0d\u5c3d\u5934\u7684\u5730\u9053\u3002\u5165\u53e3\u5904\u6709\u4e24\u6247\u77f3\u95e8\uff0c\u5389\u5357\u661f\u4ece\u91cc\u9762\u628a\u77f3 \u95e8\u5173\u4e0a\uff0c\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u61c2\u5f00\u5173\u4e4b\u6cd5\uff0c\u8981\u51ff\u5f00\u8fd9\u4e2a\u77f3\u95e8\uff0c\u81f3\u5c11\u4e5f\u5f97\u82b1\u4e09\u4e24\u5929\u5de5\u592b\u3002\u201d \u3000\u3000\u90a3\u4e2a\u6559\u4e3b\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u5c31\u5728\u4ed6\u7684\u5367\u5e8a\u5e95\u4e0b\uff0c\u7adf\u7136\u85cf\u6709\u8fd9\u4e48\u4e00\u6761\u79d8\u5bc6\u7684\u5730\u9053\uff0c\u4e0d\u7981\u201c\u54a6\u201d \u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u53e3\u4e00\u5f20\u5f00\uff0c\u79ef\u805a\u5728\u5730\u9053\u4e2d\u7684\u79fd\u6c14\u5438\u8fdb\u7684\u5c31\u66f4\u591a\u4e86\uff0c\u8fd9\u4e00\u4e0b\u718f\u5f97\u4ed6\u7684\u4e94\u810f\u516d \u8151\u5c31\u4f3c\u8981\u5728\u809a\u5b50\u91cc\u9020\u53cd\u4e00\u6837\uff0c\u987f\u65f6\u5927\u5455\u7279\u5455\u3002\u91d1\u9010\u6d41\u53e3\u91cc\u542b\u7740\u78a7\u7075\u4e39\uff0c\u4e5f\u4e0d\u7981\u634f\u7740\u9f3b\u5b50\u3002 \u3000\u3000\u5389\u5357\u661f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u867d\u7136\u5192\u5145\u6211\u7684\u8eab\u4efd\uff0c\u505a\u4e86\u6559\u4e3b\uff0c\u8c05\u4f60\u4e5f\u4e0d\u77e5\u9053\u8fd9\u4e2a\u6240\u5728\u3002\u5feb\u5feb\u4ece\u5b9e \u62db\u6765\uff0c\u4f60\u4e0e\u9633\u6d69\u4e32\u901a\uff0c\u5e72\u4e0b\u8fd9\u7b49\u5143\u803b\u7684\u52fe\u5f53\uff0c\u6709\u4ec0\u4e48\u9634\u8c0b\uff1f\u201d \u3000\u3000\u90a3\u4e2a\u6559\u4e3b\u53ea\u597d\u5fcd\u53d7\u79fd\u6c14\uff0c\u82e6\u7740\u8138\u6c42\u9976\uff1a\u201c\u8fd9\u4e0d\u5173\u6211\u7684\u4e8b\uff0c\u8fd9\u90fd\u662f\u9633\u6d69\u6446\u5e03\u7684\u3002\u4ed6\u662f\u6211\u7684 \u5e08\u4f2f\uff0c\u4ed6\u8bf4\u6211\u7684\u76f8\u8c8c\u6709\u70b9\u50cf\u4f60\uff0c\u8981\u6211\u5192\u5145\u6559\u4e3b\uff0c\u6211\u662f\u4e0d\u6562\u4e0d\u4ece\u3002\u4ed6\u60f3\u5229\u7528\u6211\u4f5c\u5080\u5121\uff0c\u91cd\u7ec4\u4e86 \u5929\u9b54\u6559\u4e4b\u540e\uff0c\u5c31\u53ef\u4ee5\u5411\u671d\u5ef7\u5356\u8eab\u6295\u9760\uff0c\u8ba8\u5f97\u66f4\u5927\u7684\u4ef7\u94b1\u3002\u8bf7\u6559\u4e3b\u6653\u547d\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u624d\u4e0d\u5e0c\u7f55\u5f53\u8fd9\u6559\u4e3b\uff01\u54fc\uff0c\u4f60\u8fd9\u53ae\u867d\u7136\u4e0d\u662f\u9996\u6076\uff0c\u4f46\u8d2a \u56fe\u5bcc\u8d35\uff0c\u4e5f\u5e94\u8be5\u8ba9\u4f60\u5403\u4e00\u70b9\u82e6\u5934\uff0c\u6b7b\u7f6a\u9976\u4e86\uff0c\u6d3b\u7f6a\u96be\u9976\u3002\u201d\u5f53\u4e0b\u70b9\u4e86\u4ed6\u7684\u9ebb\u7a74\uff0c\u53ea\u662f\u4ee4\u4ed6\u4e0d \u80fd\u52a8\u5f39\uff0c\u77e5\u89c9\u5219\u672a\u6d88\u5931\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u8eba\u4e24\u5929\u5427\uff0c\u9633\u6d69\u5f04\u5f97\u5f00\u77f3\u95e8\uff0c\u81ea\u7136\u4f1a\u653e\u4f60\u51fa \u53bb\uff0c\u5f04\u4e0d\u5f00\u77f3\u95e8\uff0c\u90a3\u5c31\u6d3b\u8be5\u4f60\u5012\u6963\u4e86\uff01\u201d\u90a3\u6559\u4e3b\u6697\u6697\u53eb\u82e6\uff0c\u5fc3\u60f3\u8981\u5728\u8fd9\u4e8c\u5341\u5e74\u4ece\u672a\u6253\u5f00\u8fc7\u7684 \u5730\u9053\u4e2d\uff0c\u5fcd\u53d7\u4e24\u65e5\u4e24\u591c\u7684\u81ed\u6c14\uff0c\u8fd9\u5df2\u7ecf\u662f\u5012\u6963\u900f\u4e86\u3002 \u3000\u3000\u5389\u5357\u661f\u5904\u7f6e\u4e86\u8fd9\u4e2a\u5047\u5192\u4ed6\u7684\u6559\u4e3b\u4e4b\u540e\uff0c\u8fd9\u624d\u5f97\u6709\u7a7a\u6687\u95ee\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u662f\u600e\u4e48\u77e5 \u9053\u8fd9\u91cc\u7684\u4e8b\u60c5\u7684\uff1f\u53f2\u59d1\u5a18\u5462\uff0c\u600e\u7684\u4e0d\u89c1\u5979\u4e0e\u4f60\u540c\u6765\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5979\u5728\u5916\u9762\uff0c\u4e0d\u77e5\u7ed9\u4eba\u53d1 \u73b0\u4e86\u6ca1\u6709\uff1f\u201d\u53c8\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u89c1\u8fc7\u516c\u5b59\u5b8f\u4e86\u3002\u516c\u5b59\u71d5\u5462\uff1f\u8be5\u4e0d\u81f3\u4e8e\u5df2\u906d\u4e0d\u5e78\u5427\uff1f\u201d\u5fc3\u60f3\u5389\u5357 \u661f\u65e2\u7136\u6ca1\u6b7b\uff0c\u516c\u5b59\u71d5\u60f3\u5fc5\u4e5f\u8fd8\u6d3b\u7740\u3002\u679c\u7136\u4fbf\u4e00\u542c\u5f97\u5389\u5357\u661f\u8bf4\u9053\uff1a\u201c\u5979\u4e5f\u662f\u5728\u5916\u9762\u7b49\u6211\u3002\u597d\uff0c \u54b1\u4eec\u8fd9\u5c31\u51fa\u53bb\u63a5\u5e94\u5979\u4eec\u5427\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u5e26\u9886\u7740\u91d1\u9010\u6d41\uff0c\u4e00\u9762\u884c\u8d70\uff0c\u4e00\u9762\u8bf4\u51fa\u4ed6\u4eec\u90a3\u65e5\u7684\u906d\u9047\u3002 \u3000\u3000\u90a3\u65e5\u4ed6\u5728\u8d6d\u77f3\u5c97\u4e0a\uff0c\u4ece\u60ac\u5d16\u4e0a\u8df3\u4e0b\u53bb\uff0c\u81ea\u4efd\u5fc5\u6b7b\u65e0\u7591\uff0c\u4e0d\u6599\u8eab\u4f53\u7740\u5730\u4e4b\u65f6\uff0c\u53ea\u89c9\u597d\u4f3c\u8dcc \u843d\u5728\u4e00\u5f20\u539a\u539a\u7684\u5730\u6be1\u4e0a\u4e00\u6837\uff0c\u867d\u7136\u8fd8\u662f\u4e0d\u514d\u6709\u70b9\u75bc\u75db\uff0c\u4f46\u5374\u6beb\u53d1\u65e0\u4f24\uff0c\u8fc7\u540e\u4ed6\u624d\u77e5\u9053\uff0c\u539f\u6765 \u8fd9\u662f\u5e7d\u8c37\u4e2d\u5316\u4f5c\u6625\u6ce5\u7684\u843d\u82b1\uff0c\u4fdd\u5168\u4e86\u4ed6\u7684\u6027\u547d\u3002 \u3000\u3000\u6843\u82b1\u8c37\u4e2d\u5730\u6c14\u6e7f\u70ed\uff0c\u6b64\u65f6\u65b9\u662f\u51ac\u5c3d\u6625\u6765\u7684\u65f6\u5019\uff0c\u5916\u9762\u72b9\u81ea\u82e6\u5bd2\uff0c\u8c37\u4e2d\u7684\u5343\u6811\u4e07\u6811\u6843\u82b1\u5df2 \u5728\u76db\u5f00\u3002\u5389\u5357\u661f\u7f13\u7f13\u5750\u4e86\u8d77\u6765\uff0c\u653e\u773c\u4e00\u770b\uff0c\u4f46\u89c1\u82b1\u5149\u5982\u6d77\uff0c\u7cbe\u795e\u4e3a\u4e4b\u4e00\u723d\u3002\u53f9\u4e3a\u5e73\u751f\u672a\u6709\u4e4b \u5947\u9047\u3002 \u3000\u3000\u4e0d\u6599\u8fd8\u6709\u66f4\u5927\u7684\u5947\u9047\u5c1a\u5728\u540e\u5934\uff01\u5c71\u98ce\u5439\u8fc7\uff0c\u9690\u9690\u542c\u5f97\u4e0a\u9762\u547c\u559d\u4e4b\u58f0\uff0c\u5389\u5357\u661f\u5403\u4e86\u4e00\u60ca\uff1a \u201c\u600e\u7684\u516c\u5b59\u71d5\u597d\u4f3c\u8fd8\u6ca1\u6709\u8d70\uff1f\u201d\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u4e00\u4e2a\u4eba\u8dcc\u4e86\u4e0b\u6765\uff0c\u521a\u597d\u8dcc\u5728\u5389 \u5357\u661f\u7684\u8eab\u65c1\u3002\u5389\u5357\u661f\u8fde\u5fd9\u5c06\u5979\u6276\u8d77\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u53ef\u4e0d\u6b63\u662f\u516c\u5b59\u71d5\u662f\u8c01\uff1f \u3000\u3000\u4e24\u4eba\u6b7b\u91cc\u9003\u751f\uff0c\u76f8\u9022\u5982\u5728\u68a6\u4e2d\uff01\u5389\u5357\u661f\u5fc3\u60c5\u5c24\u5176\u6fc0\u52a8\u65e0\u6bd4\uff0c\u8981\u77e5\u4ed6\u8df3\u4e0b\u5e7d\u8c37\uff0c\u672c\u6765\u662f\u4e0d \u60f3\u8fde\u7d2f\u516c\u5b59\u71d5\u966a\u4ed6\u9001\u547d\u7684\uff0c\u6ee1\u4ee5\u4e3a\u516c\u5b59\u71d5\u89c1\u4ed6\u6b7b\u4e86\uff0c\u4fbf\u4f1a\u81ea\u5df1\u9003\u751f\uff0c\u54ea\u77e5\u516c\u5b59\u71d5\u7adf\u7136\u8ddf\u7740\u8df3 \u4e86\u4e0b\u6765\uff0c\u4e0e\u4ed6\u6599\u60f3\u7684\u521a\u521a\u76f8\u53cd\uff01 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u4e24\u4eba\u90fd\u5df2\u662f\u6ee1\u7736\u6cea\u6c34\uff0c\u53cc\u624b\u7d27\u7d27\u76f8\u63e1\uff0c\u5389\u5357\u5c4b\u9053\uff1a\u201c\u71d5\u59b9\uff0c\u4f60\uff0c\u4f60\u4f55\u82e6 \u5982\u6b64\uff1f\u201d\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4f60\u6b7b\u4e86\u6211\u5c82\u80fd\u72ec\u6d3b\uff01\u201d\u5389\u5357\u661f\u9053\uff1a\u201c\u53ef\u662f\u60f3\u4e0d\u5230\u54b1\u4eec\u90fd\u6ca1\u6709\u6b7b\u3002\u4f46\u5c96 \u58c1\u5343\u4e08\uff0c\u54b1\u4eec\u53c8\u90fd\u662f\u53d7\u4e86\u4fee\u7f57\u9634\u715e\u529f\u4e4b\u4f24\u7684\uff0c\u53ea\u6015\u662f\u9003\u4e0d\u51fa\u8fd9\u5e7d\u8c37\u4e86\u3002\u600e\u4e48\u529e\u5462\uff1f\u201d\u516c\u5b59\u71d5 \u9053\uff1a\u201c\u53cd\u6b63\u6211\u5df2\u6253\u5b9a\u4e86\u4e3b\u610f\uff0c\u662f\u6b7b\u662f\u6d3b\uff0c\u54b1\u4eec\u90fd\u5728\u4e00\u8d77\u3002\u9003\u4e0d\u51fa\u53bb\uff0c\u90a3\u53c8\u6709\u4ec0\u4e48\u6253\u7d27\uff1f\u201d \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u8bf4\u5f97\u65a9\u9489\u622a\u94c1\uff0c\u4ee4\u5f97\u5389\u6d77\u661f\u53c8\u662f\u611f\u6127\uff0c\u53c8\u662f\u81ea\u60ed\u3002\u672c\u6765\u4ed6\u662f\u6000\u7740\u201c\u66fe\u7ecf\u6ca7\u6d77\u96be \u4e3a\u6c34\u201d\u7684\u5fc3\u60c5\uff0c\u5728\u6b64\u4e4b\u524d\uff0c\u867d\u7136\u660e\u77e5\u516c\u5b59\u71d5\u5bf9\u4ed6\u6709\u610f\uff0c\u4ed6\u5374\u4e00\u76f4\u4f6f\u4f5c\u4e0d\u77e5\uff0c\u5c06\u516c\u5b59\u71d5\u5f53\u4f5c\u59b9 \u59b9\u770b\u5f85\u7684\uff0c\u6b64\u9645\u6df1\u6df1\u53d7\u5979\u611f\u52a8\uff0c\u4e0d\u7981\u60f3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5979\u5bf9\u6211\u7adf\u662f\u5982\u6b64\u75f4\u60c5\uff01\u4e66\u4e2d\u8bf4\u5f97\u597d\uff1a\u6613 \u6c42\u65e0\u4ef7\u5b9d\uff0c\u96be\u5f97\u6709\u60c5\u90ce\u3002\u5728\u5973\u5b50\u800c\u8a00\u3002\u56fa\u7136\u5982\u6b64\uff0c\u5728\u7537\u5b50\u800c\u8a00\uff0c\u4f55\u5c1d\u4e0d\u4e5f\u4e00\u6837\uff1f\u71d5\u59b9\u4e3a\u6211\u4e0d \u60dc\u8f7b\u751f\uff0c\u6211\u53ef\u4e0d\u80fd\u518d\u8f9c\u8d1f\u5979\u7684\u82b3\u5fc3\u4e86\u3002\u201d\u60f3\u81f3\u6b64\u5904\uff0c\u4e0d\u89c9\u628a\u6b7b\u751f\u7f6e\u4e4b\u5ea6\u5916\uff0c\u5c06\u516c\u5b59\u71d5\u63fd\u5728\u6000 \u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u73b0\u5728\u6211\u5012\u4e0d\u60f3\u6b7b\u4e86\u3002\u4f60\u5462\uff1f\u201d\u516c\u5b59\u71d5\u4e5f\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u65e9\u8bf4\u8fc7\u4e86\u5417\uff0c\u4f60\u6d3b\u7740\u6211 \u5f53\u7136\u4e5f\u966a\u4f60\u6d3b\u7740\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u5c31\u4e0d\u77e5\u5929\u516c\u662f\u5426\u80fd\u5982\u54b1\u4eec\u6240\u613f\uff1f\u201d\u516c\u5b59\u71d5\u9053\uff1a\u201c\u80fd\u6d3b\u5f97\u4e00\u5929\u5c31\u662f\u4e00\u5929\u3002\u6211 \u60f3\u5927\u96be\u4e0d\u6b7b\uff0c\u5fc5\u6709\u540e\u798f\uff0c\u54b1\u4eec\u4e00\u5b9a\u4f1a\u9003\u5f97\u51fa\u53bb\u7684\u3002\u201d\u5389\u5357\u661f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5728\u8fd9\u91cc\u5148\u517b\u597d\u4e86 \u4f24\uff0c\u6162\u6162\u518d\u60f3\u529e\u6cd5\u3002\u201d \u3000\u3000\u8bdd\u867d\u5982\u6b64\uff0c\u8fd9\u4e0d\u8fc7\u662f\u5389\u5357\u661f\u5728\u65e0\u53ef\u5948\u4f55\u4e4b\u4e2d\uff0c\u59d1\u4e14\u5b89\u6170\u516c\u5b59\u71d5\u7684\u8bf4\u8bdd\u800c\u5df2\u3002\u6709\u4ec0\u4e48\u529e\u6cd5 \u53ef\u60f3\u5462\uff1f\u4fee\u7f57\u9634\u715e\u529f\u4e4b\u4f24\uff0c\u662f\u6ca1\u6709\u836f\u7269\u533b\u5f97\u597d\u7684\u3002\u9664\u975e\u672c\u8eab\u7684\u5185\u529f\u5df2\u5230\u767b\u5cf0\u9020\u6781\u7684\u5730\u6b65\uff0c\u65b9 \u624d\u53ef\u4ee5\u81ea\u5df1\u8fd0\u529f\uff0c\u9a71\u9664\u5bd2\u6bd2\u3002\u83ab\u8bf4\u516c\u5b59\u71d5\u505a\u4e0d\u5230\u5389\u5357\u661f\u4e5f\u8fd8\u5dee\u5f97\u8fdc\u3002\u4fee\u7f57\u9634\u715e\u529f\u65e0\u836f\u53ef\u533b\uff0c \u516c\u5b59\u71d5\u6216\u8bb8\u4e0d\u77e5\uff0c\u5389\u5357\u661f\u662f\u61c2\u5f97\u5404\u79cd\u90aa\u6076\u6bd2\u529f\u7684\u5927\u884c\u5bb6\uff0c\u5374\u662f\u5341\u5206\u660e\u767d\u7684\u3002 \u3000\u3000\u4f46\u6b63\u5982\u516c\u5b59\u71d5\u6240\u8bf4\uff1a\u201c\u6d3b\u5f97\u4e00\u5929\u5c31\u662f\u4e00\u5929\u3002\u201d\u5389\u5357\u661f\u4f46\u613f\u5728\u6bd9\u547d\u4e4b\u524d\uff0c\u591a\u8fc7\u51e0\u5929\u5e78\u798f\u7684 \u65e5\u5b50\uff0c\u5f53\u7136\u662f\u8981\u60f3\u6cd5\u6c42\u6d3b\u7684\u4e86\u3002 \u3000\u3000\u8981\u6d3b\u4e0b\u53bb\uff0c\u9996\u5148\u5c31\u8981\u627e\u5bfb\u98df\u7269\u3002\u4ed6\u4eec\u4e8c\u4eba\u53d7\u7684\u90fd\u662f\u4fee\u7f57\u9634\u715e\u529f\u4e4b\u4f24\uff0c\u8fd9\u4f24\u4e43\u662f\u5bd2\u6bd2\u4e4b \u4f24\uff0c\u867d\u7136\u65e0\u836f\u53ef\u6551\uff0c\u4f46\u5728\u5bd2\u6bd2\u672a\u53d1\u4f5c\u4e4b\u65f6\uff0c\u5374\u5e76\u4e0d\u600e\u6837\u75db\u82e6\uff0c\u884c\u52a8\u4e5f\u65e0\u59a8\u788d\uff0c\u53ea\u662f\u4e0d\u80fd\u8fd0\u7528 \u5185\u529f\u7f62\u4e86\u3002 \u3000\u3000\u6843\u82b1\u8c37\u4e2d\u7634\u6c14\u6781\u6d53\uff0c\u4e0d\u4f46\u4eba\u755c\u96be\u4ee5\u5b58\u6d3b\uff0c\u98de\u9e1f\u4e5f\u4e0d\u80fd\u727a\u606f\u3002\u5389\u5357\u661f\u662f\u61c2\u5f97\u6bd2\u7269\u5b66\u7684\u884c \u5bb6\uff0c\u5728\u6843\u6797\u4e2d\u8d70\u4e86\u4e00\u4f1a\uff0c\u5df2\u77e5\u8fd9\u5e7d\u8c37\u4e2d\u7684\u6843\u82b1\u7634\u5389\u5bb3\u65e0\u6bd4\uff0c\u53ea\u80fd\u91c7\u6458\u91ce\u751f\u7684\u6843\u5b50\u5145\u9965\u4e86\u3002\u4f46 \u65e2\u7136\u6709\u6843\u82b1\u7634\uff0c\u6843\u5b50\u5f53\u7136\u4e5f\u662f\u6709\u6bd2\u7684\u3002 \u3000\u3000\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u7ba1\u5b83\u6709\u6bd2\u65e0\u6bd2\uff0c\u53cd\u6b63\u54b1\u4eec\u53ea\u662f\u6253\u7b97\u6709\u4e00\u5929\u5c31\u6d3b\u4e00\u5929\u3002\u201d\u5389\u5357\u661f\u9053\uff1a\u201c\u4e14 \u6162\u5403\u5b83\uff0c\u5f85\u6211\u518d\u627e\u4e00\u627e\uff0c\u770b\u770b\u8fd8\u6709\u4ec0\u4e48\u53ef\u5403\u7684\u4e1c\u897f\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u6697\u81ea\u601d\u91cf\uff1a\u201c\u8fd9\u771f\u662f\u798f\u65e0\u53cc\u81f3\uff0c\u7978\u4e0d\u5355\u884c\uff0c\u8dcc\u843d\u8fd9\u5e7d\u8c37\u4e4b\u4e2d\u5df2\u662f\u7edd\u8def\uff0c\u8c37\u4e2d\u8fd8\u6709 \u5947\u6bd2\u7684\u6843\u82b1\u7634\uff0c\u83ab\u8bf4\u627e\u4e0d\u5230\u98df\u7269\uff0c\u5c31\u662f\u627e\u5f97\u5230\u53ef\u4ee5\u5403\u7684\u4e1c\u897f\uff0c\u6050\u6015\u6700\u591a\u4e5f\u53ea\u80fd\u6d3b\u4e09\u4e24\u5929 \u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u53c8\u7b11\u9053\uff1a\u201c\u5389\u5927\u54e5\u4f55\u5fc5\u6101\u7709\u4e0d\u5c55\uff0c\u4f60\u770b\u8fd9\u91cc\u7684\u6843\u82b1\u5f00\u5f97\u591a\u7f8e\uff0c\u54b1\u4eec\u82e5\u80fd\u5728\u8fd9\u6d1e\u5929 \u798f\u5730\u4e4b\u4e2d\u6b7b\u53bb\uff0c\u4e5f\u4e0d\u6789\u6b64\u4e00\u751f\u5440\uff01\u201d\u5389\u5357\u661f\u542c\u5979\u628a\u8fd9\u7634\u6c14\u79ef\u805a\u7684\u5e7d\u8c37\u79f0\u4f5c\u6d1e\u5929\u798f\u5730\uff0c\u4e0d\u89c9\u82e6 \u7b11\u3002 \u3000\u3000\u5389\u5357\u661f\u6b63\u81ea\u4ee5\u4e3a\u7edd\u671b\uff0c\u5ffd\u5730\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u5947\u602a\uff0c\u6211\u4e3a\u4f55\u5438\u4e86\u7634\u6c14\uff0c\u5e76\u4e0d\u89c9\u5f97\u5934\u660f \u76ee\u7729\uff1f\u201d\u4e00\u770b\u516c\u5b59\u71d5\u7684\u9762\u8272\uff0c\u53ea\u89c1\u5979\u4e5f\u597d\u50cf\u662f\u53cd\u800c\u66f4\u7cbe\u795e\u4e86\u3002 \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u71d5\u59b9\uff0c\u4f60\u8bd5\u6df1\u6df1\u5438\u6c14\uff0c\u80f8\u4e2d\u6709\u65e0\u70e6\u95f7\u4e4b\u611f\u3002\u201d\u516c\u5b59\u71d5\u5e76\u4e0d\u77e5\u9053\u6709\u6843\u82b1\u7634\uff0c \u6df1\u6df1\u547c\u5438\u51e0\u6b21\uff0c\u7b11\u9053\uff1a\u201c\u597d\u9999\uff01\u771f\u662f\u8212\u670d\u6781\u4e86\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u4e00\u65f6\u60f3\u4e0d\u5230\u5176\u4e2d\u9053\u7406\uff0c\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u5f53\u771f\u662f\u8001\u5929\u4fdd\u4f51\uff0c\u672c\u6765\u662f\u5e94\u8be5\u53d7\u7634\u6c14\u4e4b\u6bd2 \u7684\uff0c\u52a0\u53cd\u800c\u8fde\u539f\u6765\u7684\u5bd2\u6bd2\u4e5f\u51cf\u8f7b\u4e86\u3002\u201d \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\uff0c\u8d70\u5230\u4e86\u90a3\u9053\u7011\u5e03\u65c1\u8fb9\uff0c\u516c\u5b59\u71d5\u62cd\u624b\u7b11\u9053\uff1a\u201c\u5357\u54e5\u4f60\u5feb\u6765\u770b\uff0c\u6709\u4e1c\u897f\u5403\u4e86\u3002\u201d \u539f\u6765\u7011\u5e03\u4e0b\u9762\u7684\u5bd2\u6f6d\uff0c\u6e38\u9c7c\u65e0\u6570\u3002 \u3000\u3000\u5389\u5357\u661f\u4e0d\u7981\u5927\u4e3a\u5947\u602a\uff0c\u5728\u8fd9\u6843\u82b1\u8c37\u4e2d\uff0c\u98de\u9e1f\u4e0d\u80fd\u6816\u606f\uff0c\u6c34\u4e2d\u5374\u6709\u6e38\u9c7c\uff0c\u5927\u5927\u51fa\u4e4e\u4ed6\u6025\u6599 \u4e4b\u5916\u3002 \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u53ef\u60dc\u627e\u4e0d\u5230\u9493\u94a9\u3002\u597d\uff0c\u6211\u5148\u559d\u9971\u6c34\u5427\u3002\u201d\u5389\u5357\u661f\u9053\uff1a\u201c\u4e0d\u53ef\uff01\u201d\u516c\u5b59\u71d5\u5df2 \u7ecf\u4f0f\u5728\u6f6d\u8fb9\uff0c\u559d\u4e86\u597d\u51e0\u53e3\u6c34\u4e86\u3002\u62ac\u8d77\u5934\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u53ef\uff0c\u8fd9\u6c34\u6e05\u751c\u5f97\u5f88\u5462\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u60f3\u9053\uff1a\u201c\u6843\u82b1\u7634\u6bd2\u5bb3\u4e0d\u4e86\u6211\u4eec\uff0c\u8fd9\u6c34\u60f3\u5fc5\u4e5f\u662f\u53ef\u559d\u7684\u4e86\u3002\u6c34\u4e2d\u7684\u6e38\u9c7c\u6050\u6015\u4e5f\u662f\u4e00 \u79cd\u7279\u522b\u7684\u9c7c\u7c7b\u3002\u53cd\u6b63\u662f\u8981\u6b7b\u7684\uff0c\u6f6d\u6c34\u6bd2\u9c7c\uff0c\u559d\u4e86\u5403\u4e86\uff0c\u5927\u4e0d\u4e86\u4e5f\u662f\u6b7b\u5f97\u5feb\u4e9b\u800c\u5df2\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u7cbe\u901a\u6c34\u6027\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u9493\u7aff\uff0c\u6211\u7ed9\u4f60\u6349\u9c7c\u3002\u201d\u8fd9\u4e00\u665a\u4ed6\u4eec\u5c31\u7528\u70e4\u9c7c\u4f5c\u4e3a\u665a\u9910\u3002 \u3000\u3000\u4ece\u6843\u82b1\u6f6d\u4e2d\u6355\u83b7\u7684\u8fd9\u51e0\u6761\u9c9c\u9c7c\uff0c\u53c8\u80a5\u53c8\u5ae9\uff0c\u5403\u5230\u53e3\u4e2d\uff0c\u8fd8\u6709\u4e00\u79cd\u7279\u6b8a\u7684\u9999\u5473\uff0c\u4f3c\u662f\u4e2d\u4eba \u6b32\u9189\u7684\u82b1\u9999\u4e00\u822c\u3002\u4fd7\u8bed\u8bf4\u9965\u4e0d\u62e9\u98df\uff0c\u4f55\u51b5\u662f\u8fd9\u6837\u9c9c\u7f8e\u7684\u73cd\u9990\uff1f\u4e24\u4eba\u628a\u9c7c\u9aa8\u90fd\u541e\u54bd\u4e0b\u53bb\uff0c\u5403\u5f97 \u5e72\u5e72\u51c0\u51c0\u3002\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u6bcf\u5929\u6709\u8fd9\u6837\u7684\u9c9c\u9c7c\u53ef\u5403\uff0c\u6211\u548c\u4f60\u5728\u8fd9\u6843\u82b1\u8c37\u4e2d\u8fc7\u4e00\u4e16\uff0c\u5df2\u662f\u5fc3\u6ee1 \u610f\u8db3\u4e86\u3002\u201d \u3000\u3000\u4e0d\u6599\u4e50\u6781\u751f\u60b2\uff0c\u5403\u8fc7\u4e86\u70e4\u9c7c\u4e4b\u540e\uff0c\u5ffd\u89c9\u4e39\u7530\u6709\u80a1\u70ed\u6c14\u4e0a\u5347\uff0c\u4e0d\u591a\u4e00\u4f1a\uff0c\u7adf\u7136\u6d51\u8eab\u53d1\u70ed\u8d77 \u6765\u3002\u70ed\u5f97\u96be\u53d7\uff0c\u516c\u5b59\u71d5\u547b\u541f\u9053\uff1a\u201c\u6211\u8981\u6b7b\u4e86\uff0c\u6211\u8981\u6b7b\u4e86\uff01\u201d\u4e0d\u987e\u4e00\u5207\uff0c\u8df3\u8fdb\u6f6d\u4e2d\uff0c\u8ba9\u51b0\u51b7\u7684 \u6f6d\u6c34\u6d78\u7740\u81ea\u5df1\u3002\u5389\u5357\u661f\u60ca\u9053\uff1a\u201c\u6f6d\u6c34\u6050\u6015\u662f\u6709\u6bd2\u7684\uff01\u201d\u8ddf\u7740\u8df3\u4e0b\u53bb\u60f3\u628a\u5979\u62c9\u8d77\u6765\uff0c\u516c\u5b59\u71d5\u7b11 \u9053\uff1a\u201c\u8212\u670d\u6781\u4e86\uff0c\u6211\u5b81\u613f\u4e2d\u6bd2\u800c\u6b7b\uff0c\u80dc\u4e8e\u53d7\u4f53\u5185\u5982\u711a\u4e4b\u82e6\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u84e6\u5730\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u6211\u4eec\u672c\u6765\u662f\u4e2d\u4e86\u4fee\u7f57\u9634\u715e\u529f\u7684\u5bd2\u6bd2\u7684\uff0c\u4f55\u4ee5\u6d78\u5728\u6f6d\u6c34\u4e4b \u4e2d\uff0c\u4e1d\u6beb\u4e0d\u89c9\u5bd2\u51b7\uff1f\u96be\u9053\u8fd9\u7634\u6bd2\u548c\u6f6d\u4e2d\u7684\u6bd2\u82d7\uff0c\u7adf\u7136\u662f\u53ef\u6cbb\u5bd2\u6bd2\u7684\u7075\u836f\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u719f\u8bfb\u767e\u6bd2\u771f\u7ecf\uff0c\u6b64\u9645\u66ae\u7136\u7701\u609f\u4e86\u201c\u4ee5\u6bd2\u653b\u6bd2\u201d\u7684\u9053\u7406\uff0c\u4e8e\u662f\u4e0d\u518d\u963b\u62e6\u516c\u5b59\u71d5\u7684\u6240 \u4e3a\uff0c\u548c\u5979\u540c\u5728\u5bd2\u6f6d\u620f\u6c34\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u5929\u65e0\u7edd\u4eba\u4e4b\u8def\uff0c\u770b\u6765\u54b1\u4eec\u662f\u53ef\u4ee5\u5f97\u6551\u4e86\u3002\u201d \u3000\u3000\u679c\u7136\u5728\u8fde\u5403\u4e86\u4e09\u5929\u70e4\u9c7c\u4e4b\u540e\uff0c\u4e24\u4eba\u8bd5\u4e00\u8fd0\u529f\uff0c\u771f\u6c14\u5df2\u662f\u80fd\u591f\u8fd0\u884c\u65e0\u963b\u3002\u5230\u4e86\u7b2c\u4e94\u5929\uff0c\u4f53 \u5185\u7684\u5bd2\u6bd2\u5df2\u7ecf\u53bb\u51c0\u3002 \u3000\u3000\u5389\u5357\u661f\u8bd5\u51fa\u4e86\u5bd2\u6bd2\u5df2\u7ecf\u53bb\u51c0\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u9c7c\u662f\u4e0d\u80fd\u518d\u5403\u4e86\uff0c\u518d\u5403\uff0c\u54b1\u4eec\u5c31\u8981\u4e2d\u70ed\u6bd2 \u5566\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4e5f\u89c9\u5f97\u4eca\u5929\u6709\u70b9\u4e0d\u5bf9\uff0c\u597d\u50cf\u7cbe\u795e\u53cd\u800c\u4e0d\u5982\u524d\u4e24\u5929\u4e86\u3002\u5389\u5927\u54e5\uff0c\u4f60 \u662f\u5426\u611f\u5230\u6709\u4e9b\u513f\u6655\u7729\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u8fd9\u662f\u56e0\u4e3a\u54b1\u4eec\u7684\u5bd2\u6bd2\u5df2\u7ecf\u53bb\u51c0\uff0c\u5f00\u59cb\u611f\u53d7\u5230\u7634\u6c14\u7684\u4fb5\u88ad\u4e86\u3002\u5c31\u662f\u6709\u53ef\u5403\u7684 \u4e1c\u897f\uff0c\u8fd9\u4e2a\u6843\u82b1\u8c37\u4e5f\u4e0d\u80fd\u518d\u4f4f\u4e0b\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u7cdf\u7cd5\uff0c\u90a3\u600e\u4e48\u529e\uff1f\u54a6\uff0c\u5389\u5927\u54e5\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u8981\u77e5\u6c42\u751f\u4e43\u662f\u4eba\u7c7b\u672c\u80fd\uff0c \u516c\u5b59\u71d5\u867d\u7136\u8bf4\u8fc7\u613f\u5728\u8fd9\u6843\u82b1\u8c37\u4e2d\u8fc7\u4e00\u4e16\uff0c\u4f46\u6b64\u9645\u5df2\u7ecf\u6709\u4e86\u751f\u673a\uff0c\u5979\u81ea\u662f\u4e0d\u613f\u56f0\u5728\u8c37\u4e2d\u5f85\u6bd9\u3002 \u3000\u3000\u5389\u5357\u661f\u82e5\u6709\u6240\u601d\uff0c\u5ffd\u5730\u8df3\u8d77\u6765\u9053\uff1a\u201c\u54b1\u4eec\u53ef\u4ee5\u8131\u56f0\u4e86\uff0c\u4f60\u8ddf\u6211\u6765\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u534a\u4fe1\u534a\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53d1\u73b0\u4e86\u4ec0\u4e48\uff1f\u6211\u7684\u529f\u529b\u5c1a\u672a\u5b8c\u5168\u6062\u590d\uff0c\u8981\u722c\u4e0a\u53bb\u6050\u6015\u8fd8\u4e0d \u80fd\u591f\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u90a3\u6837\u8d39\u529b\uff0c\u8fd9\u5e7d\u8c37\u662f\u53e6\u6709\u51fa\u8def\u7684\u3002\u201d\u516c\u5b59\u71d5\u559c\u51fa\u671b\u5916\uff0c\u8fd8\u7591\u662f\u5389\u5357 \u661f\u54c4\u5979\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u7684\uff1f\u4f46\u8fd9\u8c37\u5e95\u7684\u6843\u6797\uff0c\u54b1\u4eec\u90fd\u5df2\u8e0f\u904d\u4e86\uff0c\u51fa\u8def\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u4e00\u6307\u7011\u5e03\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u7011\u5e03\u540e\u9762\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u5c71\u7a77\u6c34\u5c3d\u7591\u65e0\u8def\uff0c\u67f3\u6697\u82b1\u660e\u53c8\u4e00\u6751\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e09\u56de\u3000\u5e7d\u8c37\u843d\u82b1\u85cf\u4fa0\u4fa3\u3000\u6653\u661f\u6b8b\u6708\u8bc1\u9e33\u76df \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u56db\u5341\u4e09\u56de\u3000\u5e7d\u8c37\u843d\u82b1\u85cf\u4fa0\u4fa3\u3000\u6653\u661f\u6b8b\u6708\u8bc1\u9e33\u76df \u3000\u3000\u516c\u5b59\u71d5\u53c8\u6700\u6b22\u559c\uff0c\u53c8\u662f\u60ca\u5947\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u6f6d\u91cc\u7684\u9c7c\u662f\u968f\u7740\u7011\u5e03\u51b2\u4e0b\u6765\u7684\uff0c\u4f60\u6ce8\u610f\u5230\u4e86\u6ca1\u6709\uff1f\u201d\u516c\u5b59\u71d5\u9053\uff1a\u201c\u8fd9\u53c8\u600e \u6837\uff1f\u201d\u5389\u5357\u661f\u9053\uff1a\u201c\u9c7c\u5728\u7011\u5e03\u4e2d\u51b3\u96be\u5b58\u6d3b\uff0c\u53ef\u4ee5\u63a8\u60f3\u51b3\u4e0d\u662f\u5728\u5c71\u4e0a\u51b2\u4e0b\u6765\u7684\uff0c\u6211\u8fd9\u51e0\u65e5\u7559\u5fc3 \u89c2\u5bdf\uff0c\u968f\u7740\u7011\u5e03\u6d41\u4e0b\u7684\u9c7c\uff0c\u6700\u9ad8\u4e5f\u4e0d\u8fc7\u662f\u5728\u79bb\u5730\u4e09\u4e08\u591a\u7684\u9ad8\u5904\u51fa\u73b0\uff0c\u5728\u8fd9\u4e2a\u9ad8\u5ea6\u4ee5\u4e0b\uff0c\u6c34\u91cf \u7a81\u7136\u52a0\u5927\uff0c\u6c34\u6d41\u7684\u8272\u6cfd\u4e5f\u7a0d\u5fae\u6df1\u6697\uff0c\u8fd9\u662f\u4e24\u80a1\u6c34\u6d41\u4f1a\u5408\u5728\u4e00\u8d77\u4e4b\u65f6\u624d\u4f1a\u53d1\u751f\u7684\u73b0\u8c61\u3002\u56e0\u6b64\u6211 \u63a8\u60f3\u7011\u5e03\u540e\u9762\uff0c\u5b9a\u7136\u53e6\u6709\u4e00\u5904\u6d3b\u6c34\u6c34\u6e90\uff0c\u54b1\u4eec\u53ea\u987b\u63a2\u660e\u8fd9\u80a1\u6d3b\u6c34\u7684\u6c34\u6e90\u901a\u5411\u4f55\u5904\uff0c\u5c31\u53ef\u627e\u5230 \u51fa\u8def\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u7cbe\u901a\u6c34\u6027\uff0c\u516c\u5b59\u71d5\u662f\u5728\u957f\u6c5f\u8fb9\u957f\u5927\u7684\uff0c\u6c34\u6027\u867d\u4e0d\u5982\u4ed6\uff0c\u4e5f\u4e0d\u5f88\u5f31\u3002\u5f53\u4e0b\u4e24\u4eba\u65bd\u5c55 \u8f7b\u529f\uff0c\u722c\u4e0a\u4e09\u4e08\u591a\u7684\u9ad8\u5904\uff0c\u4ee5\u201c\u71d5\u5b50\u7a7f\u5e18\u201d\u5f0f\u8dc3\u8fdb\u7011\u5e03\uff0c\u679c\u7136\u7a7f\u8fc7\u4e86\u4e00\u9053\u6c34\u5e18\u3002\u53d1\u73b0\u4e86\u7011\u5e03 \u540e\u9762\u522b\u6709\u6d1e\u5929\u3002 \u3000\u3000\u90a3\u662f\u4e00\u4e2a\u5c71\u6d1e\uff0c\u6709\u4e00\u80a1\u6d3b\u6c34\u4ece\u6d1e\u4e2d\u6d41\u51fa\uff0c\u597d\u5728\u6d1e\u4e2d\u7684\u6c34\u5e76\u4e0d\u5f88\u6df1\uff0c\u4ec5\u662f\u9f50\u8170\u800c\u5df2\u3002\u516c\u5b59 \u71d5\u5728\u5389\u5357\u661f\u5e2e\u52a9\u4e4b\u4e0b\uff0c\u8d70\u51fa\u4e86\u8fd9\u4e2a\u72ed\u957f\u7684\u5c71\u6d1e\uff0c\u679c\u7136\u53d1\u73b0\u4e86\u4e00\u6761\u51fa\u8def\uff0c\u4ece\u5c71\u7684\u53e6\u4e00\u8fb9\u94bb\u51fa\uff0c \u91cd\u89c1\u5929\u65e5\u4e86\uff01 \u3000\u3000\u91d1\u9010\u6d41\u542c\u5230\u8fd9\u91cc\uff0c\u7b11\u9053\uff1a\u201c\u6211\u90a3\u5929\u5230\u6843\u82b1\u8c37\u4e2d\u5bfb\u627e\u4f60\u4eec\uff0c\u4e5f\u66fe\u53d1\u73b0\u8fd9\u6761\u7011\u5e03\uff0c\u53ef\u60dc\u6211\u6ca1 \u6709\u8df3\u8fdb\u53bb\u770b\uff0c\u5374\u60f3\u4e0d\u5230\u7011\u5e03\u540e\u9762\u522b\u6709\u6d1e\u5929\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u6309\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8131\u56f0\u4e4b\u540e\uff0c\u672c\u6765\u60f3\u627e\u4f60\u7684\u3002\u4f46\u5728\u8def\u4e0a\u4e00\u6253\u542c\uff0c\u897f\u660c\u7684\u4e49\u519b\u5df2 \u7ecf\u64a4\u9000\uff0c\u5927\u51c9\u5c71\u7684\u4e49\u519b\u57fa\u5730\u4ea6\u5df2\u8fc1\u79fb\u3002\u6211\u4eec\u65e0\u6cd5\u6253\u542c\u5230\u4e49\u519b\u7684\u6d88\u606f\uff0c\u53ea\u597d\u6682\u4e14\u653e\u5f03\u5bfb\u627e\u4f60\u7684 \u5ff5\u5934\u3002\u516c\u5b59\u71d5\u6015\u5979\u7239\u7239\u7275\u6302\uff0c\u8981\u6211\u4e0e\u5979\u5357\u5f52\uff0c\u4e5f\u597d\u8bf7\u5979\u7239\u7239\u62a5\u4ec7\u3002\u4f46\u6211\u4eec\u6ca1\u6709\u53bb\u7ea2\u7f28\u4f1a\uff0c\u5148 \u5230\u4e86\u6b64\u5730\uff0c\u8fd9\u5374\u662f\u59cb\u6599\u4e0d\u53ca\u7684\u3002\u4f60\u5df2\u7ecf\u89c1\u8fc7\u5979\u7684\u7239\u7239\uff0c\u5185\u91cc\u539f\u56e0\u3002\u60f3\u5fc5\u4f60\u662f\u5e94\u8be5\u77e5\u9053\u7684 \u4e86\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u4eec\u5728\u5357\u5f52\u9014\u4e2d\uff1b\u5df2\u7ecf\u77e5\u9053\u4e86\u516c\u5b59\u8235\u4e3b\u906d\u53d7\u6697\u7b97\u4e4b\u4e8b\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u77e5\u9053\u4e86\u6709\u4eba\u5192\u5145\u6211\u91cd\u7ec4\u5929\u9b54\u6559\uff0c\u516c\u5b59\u71d5\u7684\u7239\u7239\u53c8\u53d7\u4e86\u4f24\uff0c\u6743\u8861 \u7f13\u6025\u8f7b\u91cd\uff0c\u56de\u53bb\u63a2\u75c5\u4e4b\u4e8b\u53ef\u4ee5\u4ece\u7f13\uff0c\u8fd9\u4e2a\u5192\u540d\u884c\u9a97\u4e4b\u5f92\uff0c\u5219\u975e\u9a6c\u4e0a\u63ed\u7834\u4e0d\u53ef\uff0c\u56e0\u6b64\u6211\u4eec\u5c31\u5148 \u6765\u8fd9\u91cc\u4e86\u3002\u6211\u60f3\u4ee5\u516c\u5b59\u5b8f\u8001\u524d\u8f88\u7684\u529f\u529b\uff0c\u9633\u6d69\u7684\u4fee\u7f57\u9634\u715e\u529f\u7eb5\u7136\u80fd\u591f\u4eca\u4ed6\u53d7\u4f24\uff0c\u4e5f\u51b3\u4e0d\u80fd\u81f4 \u4ed6\u6b7b\u547d\uff0c\u8fd9\u4e00\u70b9\u4f24\u516c\u5b59\u5b8f\u8001\u524d\u8f88\u81ea\u5df1\u5c31\u53ef\u4ee5\u533b\u597d\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u6599\u5f97\u4e0d\u5dee\uff0c\u516c\u5b59\u524d\u8f88\u6700 \u591a\u5728\u5341\u5929\u534a\u6708\u4e4b\u540e\uff0c\u4fbf\u53ef\u6062\u590d\u5982\u5e38\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u6211\u79bb\u5bb6\u4e4b\u65f6\uff0c\u5bb6\u6bcd\u66fe\u7ed9\u6211\u4e00\u5e45\u5929\u9b54\u6559\u603b\u8235\u7684\u79d8\u5bc6\u5730\u56fe\uff0c\u672c\u6765\u5979\u662f\u8981\u6211\u6765\u67e5 \u63a2\u90a3\u91cc\u523b\u6709\u767e\u6bd2\u771f\u7ecf\u7684\u5927\u949f\u7684\u4e0b\u843d\u7684\uff0c\u987a\u4fbf\u53eb\u6211\u5bdf\u89c6\u4e00\u4e0b\u65e7\u5740\uff0c\u7ed9\u6211\u8fd9\u5e45\u5730\u56fe\uff0c\u540e\u6765\u6211\u77e5\u9053 \u767e\u6bd2\u771f\u7ecf\u5df2\u7ed9\u674e\u6566\u53d6\u53bb\uff0c\u94dc\u949f\u4ea6\u5df2\u6bc1\u4e86\uff0c\u6211\u4e00\u76f4\u6ca1\u6709\u6765\uff0c\u60f3\u4e0d\u5230\u8fd9\u5e45\u5730\u56fe\u5982\u4eca\u5374\u6d3e\u4e0a\u4e86\u7528\u573a \u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u602a\u4e0d\u5f97\u4f60\u6709\u6043\u65e0\u6050\u3002\u8fd9\u4e48\u8bf4\u6765\uff0c\u516c\u5b59\u59d1\u5a18\u60f3\u5fc5\u662f\u5728\u8fd9\u5730\u9053\u51fa \u53e3\u4e4b\u5904\u7b49\u5019\u4f60\u4e86\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u53eb\u5979\u5728\u5916\u9762\u628a\u98ce\uff0c\u4e00\u6709\u5371\u9669\uff0c\u5c31\u8eb2\u5165\u5730\u9053\uff0c\u51fa\u53e3\u4e4b\u5904\u662f\u540e\u5c71\u4e00\u4e2a \u50fb\u9759\u6240\u5728\uff0c\u4e5f\u662f\u85cf\u6709\u673a\u5173\uff0c\u5916\u4eba\u51b3\u4e0d\u4f1a\u77e5\u9053\u7684\u3002\u53f2\u59d1\u5a18\u5462\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5979\u626e\u4f5c\u5b88\u591c\u7684\u66f4\u592b\uff0c\u5982\u4eca\u91cc\u9762\u5df2\u7ecf\u95f9\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u5979\u5728\u5916\u9762\uff0c\u4e0d\u77e5\u7ed9\u4eba\u53d1 \u73b0\u4e86\u6ca1\u6709\uff1f\u201d\u6b64\u65f6\u4ed6\u4eec\u5df2\u7ecf\u8d70\u5230\u63a5\u8fd1\u5730\u9053\u7684\u51fa\u53e3\uff0c\u91d1\u9010\u6d41\u5982\u6709\u6240\u89c9\uff0c\u5ffd\u5730\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4 \u9053\uff1a\u201c\u5916\u9762\u4f3c\u4e4e\u6709\u5175\u5668\u78b0\u51fb\u4e4b\u58f0\uff01\u201d \u3000\u3000\u4e14\u8bf4\u53f2\u7ea2\u82f1\u5728\u5916\u9762\u6253\u66f4\uff0c\u5929\u4e0a\u4e0b\u7740\u7c73\u7c92\u822c\u7684\u7ec6\u96e8\uff0c\u5bd2\u98ce\u51b7\u95f2\u4e4b\u4e2d\uff0c\u53f2\u7ea2\u82f1\u5982\u662f\u5fc3\u6025\u5982 \u711a\uff0c\u8fdf\u8fdf\u4e0d\u89c1\u91d1\u9010\u6d41\u51fa\u6765\uff0c\u4e0d\u5982\u4ed6\u5728\u91cc\u9762\u600e\u4e48\u6837\u4e86\uff1f\u8fc7\u4e86\u662f\u5dee\u4e0d\u591a\u76f8\u8fd1\u4e00\u4e2a\u66f4\u6b21\u4e86\uff0c\u4ecd\u7136\u542c \u4e0d\u5230\u6709\u4ec0\u4e48\u52a8\u9759\uff0c\u53f2\u7ea2\u82f1\u4e0d\u6562\u64c5\u81ea\u79bb\u5f00\uff0c\u53ea\u597d\u7b49\u5f85\u3002 \u3000\u3000\u6b63\u81ea\u7b49\u5f97\u5fc3\u7126\uff0c\u5ffd\u89c1\u6709\u56db\u4e2a\u5934\u76ee\u6a21\u6837\u7684\u4eba\uff0c\u4e24\u4eba\u4e00\u8fb9\uff0c\u4ece\u5e84\u7384\u4e24\u8fb9\u5411\u5979\u8d70\u6765\u3002\u53f2\u7ea2\u82f1\u5fc3 \u5934\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u5de1\u591c\u7684\u5934\u76ee\u521a\u624d\u53ea\u662f\u4e00\u4eba\uff0c\u4f55\u4ee5\u5982\u4eca\u589e\u5230\u56db\u4eba\u4e4b\u591a\uff1f\u201d\u611f\u5230\u6709\u70b9\u4e0d\u5999\uff0c\u4f46 \u53c8\u6015\u6253\u8349\u60ca\u86c7\uff0c\u8bef\u4e86\u5927\u4e8b\uff0c\u4e00\u65f6\u95f4\u8e0c\u8e87\u672a\u51b3\uff0c\u4e0d\u6562\u51fa\u624b\u3002 \u3000\u3000\u54ea\u77e5\u53f2\u7ea2\u82f1\u4e0d\u6562\u51fa\u624b\uff0c\u5bf9\u65b9\u5df2\u662f\u5148\u4e0b\u624b\u4e3a\u5f3a\u4e86\u3002 \u3000\u3000\u9661\u7136\u95f4\u53ea\u542c\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u90a3\u56db\u4e2a\u4eba\u95f4\u65f6\u51fa\u624b\uff0c\u56db\u679a\u6697\u5668\u4e00\u9f50\u6253\u6765\uff0c\u914d\u5408\u5f97\u6070\u5230\u597d\u5904\uff0c\u53f2 \u7ea2\u82f1\u7684\u524d\u540e\u5de6\u53f3\uff0c\u90fd\u6709\u6697\u5668\u5c01\u7740\u53bb\u8def\uff0c\u4e0d\u8bba\u8eb2\u5411\u54ea\u4e00\u65b9\uff0c\u90fd\u662f\u96be\u514d\u53d7\u4f24\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u4e00\u542c\u8fd9\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u5c31\u77e5\u6765\u7684\u90fd\u662f\u9ad8\u624b\u3002\u5979\u7684\u957f\u5251\u5c1a\u672a\u51fa\u9798\uff0c\u7a7a\u624b\u53ea\u6015\u63a5\u5b83\u4e0d \u4f4f\u3002 \u3000\u3000\u597d\u4e2a\u53f2\u7ea2\u82f1\uff0c\u5251\u672a\u51fa\u9798\uff0c\u8eab\u5f62\u4e00\u8f6c\uff0c\u62ab\u7740\u7684\u6597\u7bf7\u5df2\u662f\u6296\u5f00\uff0c\u970d\u7684\u4e00\u4e2a\u201c\u51e4\u70b9\u5934\u201d\uff0c\u6597\u9022 \u98de\u821e\uff0c\u767b\u65f6\u53d8\u6210\u4e86\u4e00\u9762\u76fe\u724c\uff0c\u56db\u679a\u6697\u5668\u7adf\u7136\u7ed9\u5979\u7684\u4e00\u5f20\u6597\u7bf7\u5c3d\u6570\u8361\u5f00\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u884c\u85cf\u987f\u9732\uff0c\u90a3\u56db\u4e2a\u4eba\u7eb7\u7eb7\u559d\u9a82\uff1a\u201c\u8fd9\u5c0f\u5b50\u679c\u7136\u662f\u5978\u7ec6\uff01\u201d\u201c\u4ec0\u4e48\u5c0f\u5b50\uff0c\u5979\u662f\u53f2\u5bb6 \u8d31\u5a62\uff01\u201d\u201c\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u662f\u516d\u5408\u5e2e\u5403\u91cc\u722c\u5916\uff0c\u8c0b\u5bb3\u5144\u957f\u7684\u5996\u5973\uff01\u201d\u201c\u9633\u8001\u524d\u8f88\u795e\u673a\u5999\u7b97\uff0c \u679c\u7136\u6240\u6599\u4e0d\u5dee\uff01\u201d \u3000\u3000\u539f\u6765\u6b64\u65f6\u5728\u5821\u4e2d\u6b63\u662f\u9633\u6d69\u5f00\u59cb\u53d1\u73b0\u91d1\u3001\u5389\u4e8c\u4eba\u7684\u65f6\u5019\uff0c\u9633\u6d69\u5df2\u7ecf\u5728\u8c03\u5175\u9063\u5c06\u4e86\uff0c\u4f46\u53f2\u7ea2 \u82f1\u5728\u5916\u9762\u5c1a\u672a\u77e5\u9053\u3002 \u3000\u3000\u9633\u6d69\u662f\u4e2a\u8001\u8c0b\u6df1\u7b97\u7684\u4eba\uff0c\u91d1\u9010\u6d41\u4f55\u4ee5\u80fd\u591f\u9003\u5f97\u8fc7\u5b88\u536b\u7684\u8033\u76ee\uff0c\u6f5c\u5165\u8fd9\u5821\u5792\u6765\u5462\uff1f\u4ed6\u4e00\u52a0 \u7422\u78e8\uff0c\u7acb\u5373\u6599\u5230\u91d1\u9010\u6d41\u5728\u5916\u9762\u5b9a\u6709\u515a\u7fbd\uff0c\u81f3\u5c11\u6709\u4e00\u4e2a\u5b88\u591c\u517c\u6253\u66f4\u7684\u4eba\u662f\u7ed9\u91d1\u9010\u6d41\u7684\u540c\u515a\u66ff \u6362\u3002\u56e0\u6b64\u4ed6\u6d3e\u51fa\u7684\u8fd9\u56db\u4e2a\u4eba\uff0c\u5f53\u7136\u5c31\u4e0d\u662f\u5bfb\u5e38\u7684\u5934\u76ee\uff0c\u800c\u662f\u5929\u9b54\u6559\u4e2d\u7b2c\u4e00\u6d41\u7684\u9ad8\u624b\uff01\u53f2\u7ea2\u82f1 \u6253\u843d\u6697\u5668\u7684\u529f\u592b\uff0c\u4e43\u662f\u53f2\u5bb6\u7684\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684\u5bb6\u4f20\u7edd\u6280\uff0c\u8fd9\u56db\u540d\u9ad8\u624b\uff0c\u6709\u4e09\u4e2a\u4eba\u662f\u66fe\u7ecf\u89c1 \u53f2\u767d\u90fd\u4f7f\u8fc7\u7684\uff0c\u5f53\u7136\u4e5f\u5c31\u7acb\u5373\u77e5\u9053\u4e86\u53f2\u7ea2\u82f1\u7684\u8eab\u4efd\u4e86\u3002 \u3000\u3000\u8fd9\u56db\u4e2a\u4eba\u559d\u7834\u4e86\u53f2\u7ea2\u82f1\u7684\u884c\u85cf\uff0c\u7acb\u5373\u4e00\u62e5\u800c\u4e0a\u3002\u4e00\u4e2a\u4f7f\u7684\u662f\u539a\u80cc\u65ab\u5c71\u5200\uff0c\u4e00\u4e2a\u4f7f\u7684\u662f\u6c34 \u78e8\u94a2\u97ad\uff0c\u4e00\u4e2a\u4f7f\u7684\u662f\u9752\u94dc\u950f\uff1b\u6700\u540e\u4e00\u4e2a\u5374\u662f\u53cc\u624b\u7a7a\u7a7a\uff0c\u4ec0\u4e48\u5175\u5668\u90fd\u6ca1\u6709\u7684\u9ec4\u8863\u8001\u8005\u3002\u4f46\u5728\u56db \u4e2a\u4eba\u4e2d\uff0c\u5374\u4ee5\u4ed6\u7684\u672c\u9886\u6700\u4e3a\u5389\u5bb3\u3002 \u3000\u3000\u9ec4\u8863\u8001\u8005\u540e\u53d1\u5148\u81f3\uff0c\u53f2\u7ea2\u82f1\u628a\u6597\u7bf7\u4e00\u6325\uff0c\u4f7f\u51fa\u4e86\u201c\u591c\u6218\u516b\u65b9\u201d\u7684\u62db\u6570\uff0c\u914d\u5408\u4e0a\u72ec\u95e8\u7684 \u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684\u529f\u592b\uff0c\u8361\u5f00\u4e86\u65ab\u5c71\u5200\u548c\u6c34\u78e8\u94a2\u97ad\uff0c\u4e0d\u6599\u5374\u6321\u4e0d\u4f4f\u90a3\u53cc\u624b\u7a7a\u7a7a\u7684\u8001\u8005\uff0c\u53ea\u542c \u5f97\u58f0\u5982\u88c2\u5e1b\uff0c\u90a3\u5f20\u539a\u539a\u7684\u6597\u7bf7\uff0c\u7adf\u7ed9\u8fd9\u9ec4\u8863\u8001\u8005\u4ee5\u9e70\u722a\u529f\u786c\u751f\u751f\u5730\u6495\u6210\u4e24\u7247\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u4e00\u4e2a\u79fb\u6b65\u6362\u5f62\uff0c\u5524\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u9661\u7136\u95f4\uff0c\u53ea\u89c1\u5251\u6c14\u68ee\u68ee\uff0c\u767d\u5203\u8000\u773c\uff0c\u4e00\u67c4\u660e \u6643\u6643\u7684\u5229\u5251\u5df2\u5728\u6597\u7bf7\u88c2\u5f00\u4e4b\u5904\u4f38\u4e86\u51fa\u6765\u3002 \u3000\u3000\u9ec4\u8863\u8001\u8005\u60f3\u4e0d\u5230\u5979\u51fa\u5251\u5982\u6b64\u4e4b\u5feb\uff0c\u614c\u5fd9\u4e00\u7f29\u53f3\u624b\uff0c\u5de6\u81c2\u4e00\u5f2f\uff0c\u5374\u4ee5\u8098\u6376\u653b\u53bb\u3002\u53ea\u89c1\u5bd2\u5149 \u4e00\u95ea\uff0c\u9ec4\u8863\u8001\u8005\u7684\u957f\u8896\u7ed9\u524a\u53bb\u4e86\u4e00\u622a\uff0c\u5e78\u4e8f\u4ed6\u7b3c\u624b\u8896\u4e2d\uff0c\u5251\u950b\u524a\u5f97\u5dee\u4e86\u534a\u5bf8\uff0c\u5426\u5219\u8fde\u4ed6\u7684\u624b \u6307\u4e5f\u5c06\u5272\u6389\uff0c\u53f2\u7ea2\u82f1\u4e5f\u9669\u4e9b\u7ed9\u4ed6\u7684\u8098\u6376\u649e\u4e2d\uff0c\u8dc4\u8dc4\u8e09\u8e09\u7684\u659c\u8d70\u4e24\u6b65\uff0c\u629b\u5f00\u6495\u7834\u7684\u6597\u7bf7\uff0c\u5de6\u624b \u89e3\u4e0b\u56f4\u8170\u7684\u8f6f\u97ad\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u53cc\u65b9\u5404\u4ee5\u51cc\u5389\u7684\u6740\u624b\u653b\u6251\uff0c\u5f53\u771f\u662f\u9669\u5230\u4e86\u6781\u70b9\uff01\u53f2\u7ea2\u82f1\u56fa\u7136\u662f\u5fc3\u5934\u6697\u63aa\u53eb\u82e6\uff0c\u53ea \u6015\u4e0d\u80fd\u5728\u8fd9\u56db\u540d\u9ad8\u624b\u56f4\u653b\u4e4b\u4e0b\u7a81\u56f4\uff1b\u90a3\u9ec4\u8863\u8001\u8005\u4e5f\u662f\u4e0d\u7531\u5f97\u4e0d\u5403\u4e86\u4e00\u60ca\uff0c\u672c\u6765\u4ed6\u7684\u9e70\u722a\u624b\u662f \u8fde\u73af\u4e09\u62db\u7684\uff0c\u7ed9\u53f2\u7ea2\u82f1\u4ee5\u51cc\u5389\u7684\u5251\u6cd5\uff0c\u8feb\u4ed6\u7f29\u624b\u4e4b\u540e\uff0c\u7b2c\u4e09\u62db\u5df2\u662f\u53d8\u4e3a\u53cc\u638c\u62a4\u8eab\uff0c\u4e0d\u6562\u653b\u654c \u4e86\u3002 \u3000\u3000\u4f7f\u9752\u94dc\u950f\u7684\u90a3\u4e2a\u6c49\u5b50\u89c1\u53f2\u7ea2\u82f1\u4f3c\u4e4e\u811a\u6b65\u4e0d\u7a33\uff0c\u4ee5\u4e3a\u6709\u673a\u53ef\u4e58\uff0c\u559d\u9053\uff1a\u201c\u5e76\u80a9\u5b50\u4e0a\u5440\uff01\u201d \u4e00\u62db\u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u9752\u94dc\u950f\u5411\u4e0a\u78d5\u53bb\uff0c\u51c6\u5907\u778c\u5f00\u53f2\u7ea2\u82f1\u7684\u957f\u5251\uff0c\u9752\u94dc\u950f\u5c31\u53ef\u4ee5\u6253\u788e\u5979\u7684\u7435\u7436 \u9aa8\uff1b\u4f7f\u6c34\u78e8\u94a2\u97ad\u90a3\u4e2a\u6c49\u5b50\u548c\u4ed6\u4e00\u5411\u662f\u2018\u7126\u4e0d\u79bb\u5b5f\uff0c\u5b5f\u4e0d\u79bb\u7126\u201d\u7684\u4f19\u4f34\uff0c\u4e24\u4eba\u914d\u5408\u6709\u7d20\uff0c\u6c34\u78e8 \u94a2\u97ad\u7684\u6c49\u5b50\u542c\u5f97\u4ed6\u4e00\u6253\u62db\u547c\uff0c\u6839\u672c\u4e0d\u7528\u770b\u4ed6\u51fa\u7684\u662f\u4ec0\u4e48\u62db\u6cd5\uff0c\u4fbf\u5373\u4f7f\u51fa\u4e00\u62db\u201c\u94c1\u68a8\u8015\u5730\u201d\uff0c \u957f\u97ad\u970d\u5730\u6765\u6253\u53f2\u7ea2\u82f1\u7684\u53cc\u8db3\u3002 \u3000\u3000\u4e00\u4e2a\u662f\u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u4e00\u4e2a\u662f\u201c\u94c1\u7281\u8015\u5730\u201d\uff0c\u914d\u5408\u5f97\u6070\u5230\u597d\u5904\uff0c\u4f46\u82e5\u53f2\u7ea2\u82f1\u5e94\u4ed8\u5f97\u7a0d\u5fae \u5931\u5b9c\uff0c\u987e\u5f97\u4e86\u5934\uff0c\u987e\u4e0d\u4e86\u811a\uff0c\u987e\u4e86\u811a\uff0c\u987e\u4e0d\u4e86\u5934\uff0c\u90a3\u5c31\u4e00\u5b9a\u8981\u91cd\u4f24\u5728\u8fd9\u4e24\u4eba\u7684\u97ad\u950f\u4e4b\u4e0b\u4e86\u3002 \u3000\u3000\u4e0d\u6599\u53f2\u7ea2\u82f1\u7684\u811a\u6b65\u770b\u4f3c\u8e09\u8dc4\uff0c\u5176\u5b9e\u5370\u662f\u5947\u5999\u83ab\u6d4b\u7684\u201c\u9189\u516b\u4ed9\u201d\u6b65\u6cd5\uff01 \u3000\u3000\u5979\u8f7b\u529f\u8d85\u5999\uff0c\u97ad\u5251\u53cc\u7edd\uff0c\u8fd9\u4e24\u4eba\u914d\u5408\u5f97\u867d\u7136\u6781\u597d\uff0c\u4e5f\u8fd8\u662f\u96be\u5948\u5979\u4f55\u3002\u6b64\u65f6\u5979\u5df2\u89e3\u4e0b\u8f6f \u97ad\uff0c\u4ee5\u97ad\u5bf9\u97ad\uff0c\u8f6f\u97ad\u4e00\u7ed5\uff0c\u7f20\u4e0a\u90a3\u4eba\u7684\u6c34\u78e8\u94a2\u97ad\uff1b\u4ee5\u5251\u654c\u950f\uff0c\u5251\u950b\u4e00\u6643\uff0c\u504f\u65c1\u4e00\u5f15\uff0c\u4f7f\u4e86\u4e2a \u201c\u5378\u201d\u5b57\u8bc0\uff0c\u8f7b\u63cf\u6de1\u5199\u7684\u5c31\u628a\u9752\u94dc\u950f\u62e8\u8fc7\u4e00\u8fb9\u53bb\u4e86\u3002\u4f7f\u6c34\u78e8\u94a2\u97ad\u90a3\u4e2a\u6c49\u5b50\u6c89\u8170\u5750\u9a6c\uff0c\u731b\u529b\u4e00 \u62c9\uff01 \u3000\u3000\u53f2\u7ea2\u82f1\u5403\u4e0d\u4f4f\u8fd9\u80a1\u731b\u52b2\uff0c\u8eab\u5411\u524d\u503e\uff0c\u811a\u4e0b\u4ecd\u7136\u8e0f\u7740\u201c\u9189\u516b\u4ed9\u201d\u6b65\u6cd5\uff0c\u987a\u7740\u5d29\u503e\u4e4b\u52bf\uff0c\u5530 \u7684\u4e00\u5251\uff0c\u4ece\u90a3\u4f7f\u9752\u94dc\u950f\u7684\u6c49\u5b50\u6240\u610f\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\u523a\u6765\u3002\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u5251\u5c16\u7a7f\u8fc7\u8fd9\u4eba\u7684\u201c\u62a4 \u80a9\u201d\uff0c\u8fd9\u4eba\u672c\u6765\u662f\u60f3\u6253\u788e\u5979\u7684\u7435\u7436\u9aa8\u7684\uff0c\u53cd\u800c\u51e0\u4e4e\u7ed9\u53f2\u7ea2\u82f1\u523a\u7a7f\u4ed6\u7684\u7435\u7436\u9aa8\u3002\u5e78\u4e8f\u4ed6\u7684\u201c\u62a4 \u80a9\u201d\u4e43\u662f\u4e09\u5bf8\u591a\u539a\u7684\u76ae\u9769\u6240\u5236\uff0c\u53f2\u7ea2\u82f1\u7684\u5251\u5c16\u523a\u5165\u4e86\u4e00\u5bf8\u6709\u591a\uff0c\u5c1a\u672a\u7a7f\u8fc7\uff0c\u90a3\u9ec4\u8863\u8001\u8005\u53c8\u5df2 \u6251\u4e0a\u6765\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u9661\u89c9\u52b2\u98ce\u98d2\u7136\uff0c\u4e0d\u7528\u56de\u5934\uff0c\u5df2\u77e5\u662f\u672c\u9886\u6700\u5f3a\u7684\u90a3\u4e2a\u9ec4\u8863\u8001\u8005\u5728\u5979\u80cc\u540e\u653b\u5230\uff0c\u5f53\u4e0b \u8ddf\u4e0d\u5f97\u4f24\u8fd9\u4f7f\u9752\u94dc\u950f\u7684\u6c49\u5b50\uff0c\u7acb\u5373\u53cd\u624b\u4e00\u5251\uff0c\u5316\u89e3\u4e86\u5bf9\u65b9\u201c\u9e70\u722a\u529f\u201d\u7684\u62ff\u624b\u7edd\u62db\uff01\u8fc5\u5373\u8f6f\u97ad \u6296\u5f00\uff0c\u653e\u677e\u4e86\u90a3\u4f7f\u6c34\u78e8\u94a2\u97ad\u7684\u6c49\u5b50\uff0c\u8eab\u5f62\u4e00\u98d8\u4e00\u95ea\uff0c\u8f6f\u97ad\u4ee5\u201c\u98ce\u522e\u843d\u82b1\u201d\u7684\u62db\u6570\u626b\u51fa\uff0c\u6070\u6070 \u53c8\u626b\u5f00\u4e86\u4ece\u4fa7\u9762\u65ab\u6765\u7684\u4e00\u67c4\u539a\u80cc\u65ab\u5c71\u5200\u3002 \u3000\u3000\u4ee5\u53f2\u7ea2\u82f1\u7684\u672c\u9886\uff0c\u82e5\u7136\u662f\u5355\u6253\u72ec\u6597\uff0c\u8fd9\u56db\u4e2a\u4eba\u90fd\u4e0d\u662f\u5979\u7684\u5bf9\u624b\u3002\u4f46\u5728\u4ed6\u4eec\u8054\u624b\u56f4\u653b\u4e4b \u4e0b\uff0c\u53f2\u7ea2\u82f1\u5374\u662f\u6709\u70b9\u5e94\u4ed8\u4e0d\u6687\uff0c\u60c5\u77e5\u4e45\u6218\u4e0b\u53bb\uff0c\u5b9a\u8981\u5403\u4e8f\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u77e5\u9010\u6d41\u5728\u91cc\u9762\u600e \u4e48\u6837\u4e86\uff1f\u4f46\u6211\u5982\u4eca\u5df2\u7ecf\u7ed9\u4eba\u53d1\u73b0\uff0c\u90a3\u4e5f\u65e0\u987b\u987e\u5fcc\u6253\u8349\u60ca\u86c7\u4e86\u3002\u201d\u5f53\u4e0b\u4fbf\u5373\u7528\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u5185 \u529f\u53eb\u9053\uff1a\u201c\u9010\u6d41\uff0c\u5feb\u6765\uff01\u201d\u5979\u54ea\u91cc\u77e5\u9053\uff0c\u91d1\u9010\u6d41\u6b64\u65f6\u5df2\u662f\u548c\u5389\u5357\u661f\u5728\u90a3\u5730\u9053\u4e4b\u4e2d\uff0c\u201c\u4f20\u97f3\u5165 \u5bc6\u201d\u4e5f\u4f20\u4e0d\u5230\u4ed6\u7684\u8033\u6735\uff0c \u3000\u3000\u8fd9\u56db\u4eba\u4e43\u662f\u5929\u9b54\u6559\u4e2d\u4e00\u6d41\u9ad8\u624b\u7684\u8eab\u4efd\uff0c\u89c9\u5f97\u4ee5\u56db\u4eba\u56f4\u653b\u4e00\u4e2a\u5973\u5b50\uff0c\u5df2\u662f\u6709\u5931\u4f53\u9762\u7684\u4e8b\uff0c \u65e2\u7136\u80dc\u7b97\u5728\u63e1\uff0c\u4e3a\u4e86\u4fdd\u6301\u8eab\u4efd\uff0c\u81ea\u662f\u4e0d\u613f\u518d\u5411\u5821\u4e2d\u6c42\u63f4\u3002 \u3000\u3000\u90a3\u9ec4\u8863\u8001\u8005\u8fde\u4f7f\u51e0\u62db\u6781\u4e3a\u51cc\u5389\u7684\u64d2\u62ff\u624b\u6cd5\uff0c\u628a\u53f2\u7ea2\u82f1\u903c\u5f97\u4e1c\u8eb2\u897f\u95ea\uff0c\u5f97\u610f\u6d0b\u6d0b\uff0c\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u4f60\u90a3\u76f8\u597d\u7684\u59d3\u91d1\u7684\u5c0f\u5b50\u65e9\u5df2\u5728\u91cc\u9762\u675f\u624b\u5c31\u64d2\u5566\uff0c\u4f60\u558a\u7834\u4e86\u5589\u5499\u4e5f\u6ca1\u4eba\u6765\u6551\u4f60\u4e86\u3002\u4f60\u8981 \u89c1\u8fd9\u59d3\u91d1\u7684\u5c0f\u5b50\uff0c\u53ea\u6709\u4e56\u4e56\u7684\u653e\u4e0b\u5175\u5668\uff0c\u8ddf\u6211\u4eec\u8fdb\u53bb\u5427\uff01\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u8ddf\u7740\u4e00\u4e2a\u94f6\u94c3\u4f3c\u7684\u58f0\u97f3\u51b7\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u6211\u5728\u8fd9\u91cc\u5462\uff01 \u7528\u4e0d\u7740\u91d1\u9010\u6d41\u4eb2\u81ea\u52a8\u624b\uff0c\u6211\u548c\u53f2\u59d0\u59d0\u5c31\u53ef\u4ee5\u5c06\u4f60\u4eec\u63a5\u73ed\u5996\u4eba\u6536\u62fe\u3002\u201d \u3000\u3000\u4f7f\u539a\u80cc\u65ab\u5c71\u5200\u7684\u90a3\u4e2a\u6c49\u5b50\u5728\u56db\u4eba\u4e4b\u4e2d\u6c14\u529b\u6700\u5927\uff0c\u8eab\u6cd5\u5374\u662f\u6700\u7b28\uff0c\u542c\u5f97\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u811a \u6b65\u5c1a\u672a\u8fc8\u5f00\uff0c\u53ea\u89c9\u8170\u95f4\u4e00\u9ebb\uff0c\u5df2\u662f\u7ed9\u4e00\u679a\u94b1\u9556\u6253\u4e2d\uff0c\u54ce\u54df\u53eb\u9053\uff1a\u201c\u597d\u4e2b\u5934\uff0c\u4f60\u3001\u4f60\u6562\u6697 \u7b97\u2026\u2026\u201d\u201c\u8001\u5b50\u201d\u4e24\u5b57\u672a\u80fd\u5410\u51fa\u53e3\u6765\uff0c\u5df2\u662f\u201c\u535c\u901a\u201d\u5012\u4e0b\u3002\u8fd9\u679a\u94b1\u895f\u6b63\u597d\u6253\u4e2d\u4e86\u4ed6\u7684\u6108\u6c14 \u7a74\uff01 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u4e00\u4e2a\u9ed1\u8863\u5973\u5b50\uff0c\u624b\u6301\u53cc\u5251\uff0c\u65cb\u98ce\u822c\u7684\u6740\u4e86\u5230\u6765\uff01\u53f2\u7ea2\u82f1\u53c8\u60ca\u53c8 \u559c\uff0c\u53eb\u9053\uff1a\u201c\u516c\u5b59\u59d0\u59d0\uff0c\u4f60\u3001\u4f60\uff0c\u539f\u6765\u2026\u2026\u201d\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5c1a\u672a\u62a5\u4ec7\uff0c\u8fd8\u820d\u4e0d\u5f97 \u6b7b\u5462\uff01\u201d\u56f4\u653b\u53f2\u7ea2\u82f1\u7684\u8fd9\u56db\u540d\u9ad8\u624b\uff0c\u770b\u89c1\u516c\u5b59\u71d5\u7a81\u7136\u6765\u5230\uff0c\u4e0d\u7531\u5f97\u90fd\u662f\u5927\u5403\u4e00\u60ca\uff01\u4ed6\u4eec\u5e76\u975e \u662f\u5bb3\u6015\u516c\u5b59\u71d5\uff0c\u800c\u662f\u5bb3\u6015\u5979\u7684\u7236\u4eb2\u2014\u2014\u5728\u6b66\u6797\u4e2d\u58f0\u540d\u4ec5\u6b21\u4e8e\u6c5f\u6d77\u5929\u7684\u7ea2\u7f28\u4f1a\u603b\u8235\u4e3b\u516c\u5b59\u5b8f\uff01 \u3000\u3000\u90a3\u6b21\u516c\u5b59\u5b8f\u5728\u6b64\u8bef\u906d\u6697\u7b97\uff0c\u4e2d\u4e86\u6bd2\u53c8\u53d7\u4e86\u4fee\u7f57\u9634\u715e\u529f\u4e4b\u4f24\u4e4b\u540e\uff0c\u4ecd\u7136\u80fd\u591f\u53ea\u51ed\u4e00\u53cc\u8089 \u638c\uff0c\u72ec\u81ea\u4e00\u4eba\u5c31\u95ef\u51fa\u4e86\u5929\u9b54\u6559\u603b\u8235\uff0c\u90a3\u4e00\u4ed7\u6740\u5f97\u5929\u9b54\u6559\u4e0a\u4e0b\u4eba\u7b49\uff0c\u4eba\u4eba\u90fd\u662f\u80c6\u6218\u5fc3\u60ca\uff01\u751f\u6015 \u4ed6\u4f24\u597d\u4e4b\u540e\uff0c\u5c31\u8981\u8d76\u6765\u62a5\u4ec7\u3002 \u3000\u3000\u6b64\u65f6\u4ed6\u4eec\u770b\u89c1\u516c\u5b59\u71d5\u6765\u5230\uff0c\u5fc3\u4e2d\u90fd\u662f\u4e0d\u514d\u5982\u6b64\u60f3\u9053\uff1a\u201c\u516c\u5b59\u5b8f\u8fd9\u8001\u513f\u51b3\u4e0d\u4f1a\u8ba9\u4ed6\u7684\u5973\u513f \u72ec\u81ea\u6765\u7684\uff0c\u4e00\u5b9a\u662f\u5730\u7684\u4f24\u5df2\u7ecf\u597d\u4e86\uff0c\u54ce\u5440\uff0c\u8bf4\u4e0d\u5b9a\u8fd9\u8001\u513f\u5c31\u8eb2\u5728\u4e00\u65c1\uff0c\u770b\u54b1\u4eec\u662f\u600e\u6837\u5bf9\u4ed8\u4ed6 \u7684\u5973\u513f\u5462\uff01\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u516c\u5b59\u71d5\u5df2\u662f\u65cb\u98ce\u822c\u5730\u6251\u5230\uff0c\u53cc\u5251\u77eb\u82e5\u6e38\u9f99\uff0c\u5de6\u4e00\u62db\u201c\u5927\u6f20\u5b64\u70df\u201d\uff0c\u5251 \u76f4\u5982\u77e2\uff0c\u6307\u5411\u90a3\u9752\u8863\u8001\u8005\u7684\u54bd\u5589\uff0c\u53f3\u4e00\u62db\u201c\u957f\u6cb3\u843d\u65e5\u201d\uff0c\u5251\u52bf\u5982\u73af\uff0c\u5708\u4f4f\u90a3\u4e2a\u4f7f\u539a\u80cc\u65ab\u5c71\u5200 \u7684\u6c49\u5b50\u3002 \u3000\u3000\u9752\u8863\u8001\u8005\u5413\u788d\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u6211\u5bf9\u4ee4\u5c0a\u7d20\u6765\u94a6\u656c\uff0c\u4e0d\u6562\u5f97\u7f6a\u59d1\u5a18\u3002\u59d1\u5a18\u6709\u8bdd\u597d\u8bf4\uff01\u201d\u4f46\u516c \u5b59\u71d5\u51fa\u5251\u4f55\u7b49\u4e4b\u5feb\uff0c\u8fd9\u9752\u8863\u8001\u8005\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c9\u80c1\u4e0b\u4e00\u9ebb\uff0c\u5df2\u662f\u7ed9\u516c\u5b59\u71d5\u523a\u4e2d\u4e86\u7a74\u9053\u3002\u672c\u6765 \u4ee5\u8fd9\u9752\u8863\u8001\u8005\u7684\u529f\u592b\uff0c\u867d\u7136\u4e0d\u53ca\u516c\u5b59\u71d5\uff0c\u4f46\u4e5f\u76f8\u5dee\u4e0d\u8fdc\uff0c\u81f3\u5c11\u53ef\u4ee5\u6597\u5230\u767e\u62db\u5f00\u5916\u7684\uff0c\u53ea\u56e0\u5fc3 \u91cc\u4e00\u614c\uff0c\u6597\u5fd7\u6d88\u5931\uff0c\u8fd9\u5c31\u51b7\u4e0d\u9632\u7684\u4e00\u4e2a\u7167\u9762\u4fbf\u7740\u4e86\u516c\u5b59\u71d5\u7684\u9053\u513f\u4e86\u3002 \u3000\u3000\u4f7f\u539a\u80cc\u65ab\u5c71\u5200\u7684\u90a3\u4e2a\u6c49\u5b50\uff0c\u672c\u9886\u8f83\u5f31\uff0c\u4f46\u5374\u662f\u9633\u6d69\u7684\u5fc3\u8179\uff0c\u80c6\u5b50\u4e5f\u8f83\u90a3\u9752\u8863\u8001\u8005\u5927\u4e9b\uff0c \u662f\u4ee5\u5f53\u516c\u5b59\u71d5\u7684\u5de6\u624b\u5251\u5411\u4ed6\u523a\u6765\u4e4b\u65f6\uff0c\u4ed6\u7acb\u5373\u5c31\u4f7f\u51fa\u521a\u731b\u7684\u5200\u6cd5\u62db\u67b6\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5c31\u7b97\u516c \u5b59\u5b8f\u8fd9\u8001\u513f\u6765\u4e86\uff0c\u6211\u4e5f\u5f97\u628a\u5979\u7684\u5251\u6253\u843d\u518d\u8bf4\uff0c\u603b\u4e0d\u80fd\u5e73\u767d\u8ba9\u5979\u4f24\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u4e00\u5251\u5251\u4e2d\u90a3\u9752\u8863\u8001\u8005\u7684\u7a74\u9053\uff0c\u8bf4\u9053\uff1a\u201c\u770b\u5728\u4f60\u94a6\u656c\u6211\u7239\u7239\u7684\u4efd\u4e0a\uff0c\u9976\u4f60\u4e0d\u6b7b\uff01\u201d \u8ddf\u7740\u4e00\u58f0\u51b7\u7b11\uff0c\u5de6\u5251\u4e00\u5708\uff0c\u5708\u7740\u4e86\u90a3\u4eba\u7684\u539a\u80cc\u65ab\u5c71\u5200\uff0c\u53f3\u5251\u62bd\u4e86\u51fa\u6765\uff0c\u5530\u7684\u5c31\u4ece\u5708\u4e2d\u523a\u8fdb\uff0c \u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u65e0\u793c\uff0c\u6211\u53ef\u4e0d\u80fd\u9976\u4f60\u4e86\uff01\u201d \u3000\u3000\u4f7f\u539a\u80cc\u65ab\u5c71\u5200\u7684\u8fd9\u4e2a\u6c49\u5b50\uff0c\u5728\u5929\u9b54\u6559\u4e2d\u867d\u7136\u7b97\u5f97\u662f\u4e2a\u9ad8\u624b\uff0c\u5728\u6b66\u6797\u4e2d\u53ea\u4e0d\u8fc7\u662f\u4e8c\u4e09\u6d41\u7684 \u811a\u8272\uff0c\u516c\u5b59\u71d5\u7684\u5251\u6cd5\u5df1\u5c3d\u5f97\u4e43\u7236\u771f\u4f20\uff0c\u72e0\u8fa3\u5947\u8be1\uff0c\u5c82\u662f\u4ed6\u6240\u80fd\u62b5\u654c\uff1f\u53ea\u542c\u5f97\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c \u8fd9\u4eba\u7684\u4e00\u6761\u624b\u81c2\u5df2\u7ed9\u516c\u5b59\u71d5\u65a9\u6389\uff0c\u80f8\u53e3\u4e5f\u7740\u4e86\u4e00\u5251\uff0c\u767b\u65f6\u75db\u5f97\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4efb\u516c\u5b59\u71d5\u6536\u62fe\u8fd9\u4e24\u4e2a\u6c49\u5b50\u4e4b\u9645\uff0c\u53f2\u7ea2\u82f1\u4e5f\u662f\u5f53\u4ec1\u4e0d\u8ba9\uff0c\u5979\u50cf\u548c\u516c\u5b59\u71d5\u7ade\u8d5b\u4f3c\u7684\uff0c\u97ad\u5251\u9f50 \u4e0a\uff0c\u4e00\u97ad\u6253\u788e\u4e86\u90a3\u4e2a\u4f7f\u6c34\u78e8\u955c\u97ad\u7684\u6c49\u5b50\u7684\u7435\u7436\u9aa8\uff0c\u8ddf\u7740\u53c8\u4e00\u5251\u523a\u4e2d\u4e86\u90a3\u4e2a\u4f7f\u9752\u94dc\u950f\u7684\u6c49\u5b50\u3002 \u8fd9\u4e24\u4e2a\u4eba\u4e5f\u90fd\u5012\u5728\u5730\u4e0a\uff0c\u8981\u722c\u4e5f\u722c\u4e0d\u8d77\u6765\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u6b22\u559c\u5f97\u51e0\u4e4e\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u5230\u4f1a\u5728\u8fd9\u91cc\u89c1\u7740\u6211\u5427\uff1f\u542c\u8bf4\u4f60\u505a \u4e86\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\uff0c\u6211\u8fd8\u672a\u66fe\u5411\u4f60\u9053\u8d3a\u5462\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u8fd9\u4e9b\u4e8b\u6162\u6162\u518d\u8bf4\uff0c\u5389\u5927\u54e5\u5462\uff1f\u201d\u516c\u5b59\u71d5\u9053\uff1a\u201c\u6709\u4eba\u5192\u4ed6\u7684\u540d\u505a\u5929\u9b54\u6559\u7684\u6559 \u4e3b\uff0c\u4ed6\u8dd1\u53bb\u627e\u8fd9\u4e2a\u4eba\u7b97\u5e10\u4e86\u3002\u201d \u3000\u3000\u6b64\u65f6\u6b63\u662f\u9633\u6d69\u6307\u6325\u515a\u7fbd\u5305\u56f4\u91d1\u3001\u5389\u4e8c\u4eba\u7684\u65f6\u5019\uff0c\u9999\u5802\u91cc\u9762\u5450\u558a\u7684\u58f0\u97f3\u5df2\u662f\u9690\u9690\u53ef\u95fb\u3002\u53f2 \u7ea2\u82f1\u9053\uff1a\u201c\u6211\u548c\u9010\u6d41\u6b63\u662f\u4e3a\u4e86\u6b64\u4e8b\u800c\u6765\u3002\u55ef\uff0c\u4f60\u542c\uff01\u91cc\u9762\u597d\u50cf\u5df2\u7ecf\u6253\u8d77\u6765\uff0c\u54b1\u4eec\u8d76\u5feb\u8fdb\u53bb \u5427\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u8fdb\u53bb\uff0c\u4f60\u8ddf\u6211\u6765\uff0c\u5305\u7ba1\u4f60\u89c1\u5f97\u7740\u4ed6\u4eec\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u597d\u51e0\u4e2a\u58f0\u97f3\u540c\u65f6\u53eb\u9053\uff1a\u201c\u6349\u5978\u7ec6\uff0c\u5feb\u6765\u6349\u5978\u7ec6\u5440\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u5feb\u8d70\uff0c\u5feb\u8d70\uff01\u54b1\u4eec\u72af\u4e0d\u7740\u5728\u8fd9\u91cc\u548c\u4ed6\u4eec\u786c\u62fc\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u4e0d\u77e5\u5979\u7684\u846b\u82a6\u91cc\u5356\u4ec0\u4e48\u836f\uff0c\u53ea\u597d\u8ddf\u7740\u5979\u8dd1\uff0c\u9633\u6d69\u7684\u5f97\u529b\u624b\u4e0b\uff0c\u9664\u4e86\u6d3e\u51fa\u6765\u90a3\u56db\u4e2a \u9ad8\u624b\u4e4b\u5916\uff0c\u5176\u4f59\u7684\u4eba\u90fd\u5728\u91cc\u9762\uff0c\u6b64\u9645\u8dd1\u51fa\u6765\u6349\u62ff\u201c\u5978\u7ec6\u201d\u53ea\u4e0d\u8fc7\u662f\u51e0\u4e2a\u5de1\u591c\u5934\u76ee\uff0c\u54ea\u91cc\u80fd\u591f \u8ffd\u5f97\u4e0a\u5979\u4eec\uff1f \u3000\u3000\u516c\u5b59\u71d5\u8dd1\u5728\u524d\u5934\u5e26\u8def\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u6b63\u662f\u5230\u4e86\u540e\u5c71\uff0c\u628a\u8ffd\u5175\u8fdc\u8fdc\u7529\u5728\u80cc\u540e\uff0c\u8fde\u547c\u558a\u58f0\u97f3\u4e5f \u542c\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u516c\u5b59\u71d5\u505c\u4e0b\u811a\u6b65\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u8fd9\u91cc\u4e86\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u8be7\u9053\uff1a\u201c\u4f60\u5e26\u6211\u5230\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u539f\u6765\u5979\u4eec\u7acb\u8db3\u4e4b\u5904\uff0c\u6b63\u662f\u8346\u68d8\u4e1b\u4e2d\u3002 \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u4e00\u6761\u5730\u9053\uff0c\u53ef\u4ee5\u8fdb\u53bb\u5929\u9b54\u6559\u7684\u5185\u9999\u5802\uff0c\u5389\u5927\u54e5\u548c\u6211\u7ea6\u597d\uff0c\u91cc\u9762\u5018\u82e5 \u51fa\u4e86\u4e8b\u60c5\uff0c\u4ed6\u4f1a\u4ece\u5730\u9053\u8d70\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u8fd9\u624d\u660e\u767d\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u4f46\u4e0d\u77e5\u4ed6\u4eec\u4e8c\u4eba\u5df2\u7ecf\u4f1a\u9762\u6ca1\u6709\uff1f\u201d \u3000\u3000\u516c\u5b59\u71d5\u9053\uff1a\u201c\u4f60\u82e5\u662f\u5fc3\u6025\uff0c\u552f\u4eec\u4e5f\u4e0d\u59a8\u8fdb\u53bb\u770b\u770b\uff0c\u53cd\u6b63\u8fd9\u91cc\u6ca1\u6709\u4eba\uff0c\u4e0d\u6015\u6cc4\u6f0f\u79d8\u5bc6\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u6b63\u8981\u6559\u5979\u5f00\u542f\u5730\u9053\u7684\u65b9\u6cd5\uff0c\u53f2\u7ea2\u82f1\u5ffd\u5730\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u50cf\u6709\u4ec0\u4e48\u58f0 \u606f\uff0c\u83ab\u975e\u662f\u2026\u2026\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u8863\u895f\u5e26\u98ce\u4e4b\u58f0\uff0c\u679c\u7136\u662f\u6709\u4e00\u4e2a\u591c\u884c\u4eba\u6765\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u6b64\u4eba\u4e0d\u77e5\u662f\u8c01\uff0c\u8f7b\u529f\u53ef\u662f\u9ad8\u660e\u4e4b\u6781\u3002\u201d\u516c\u5b59\u71d5\u559d\u9053\uff1a\u201c\u662f \u8c01\uff1f\u201d\u9661\u7136\u95f4\u4e00\u6761\u9ed1\u5f71\u51fa\u73b0\u5728\u5979\u4eec\u7684\u9762\u524d\uff0c\u9634\u607b\u607b\u5730\u51b7\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u4eec\u8fd9\u4e24\u4e2a\u81ed\u4e2b\u5934\uff0c \u597d\u5440\uff0c\u4eca\u665a\u4f60\u4eec\u649e\u7740\u4e86\u6211\u3002\u6b63\u597d\u53eb\u4f60\u4eec\u62b5\u507f\u4e2d\u513f\u7684\u6027\u547d\uff01\u201d \u3000\u3000\u8fd9\u4e2a\u4eba\u4e0d\u662f\u522b\u4e2a\uff0c\u6b63\u662f\u5728\u897f\u660c\u6f0f\u7f51\u7684\u6587\u9053\u5e84\u3002\u4ed6\u7684\u513f\u5b50\u6587\u80dc\u4e2d\u6b7b\u5728\u4e49\u519b\u4e4b\u624b\uff0c\u4ed6\u7acb\u8a93\u8981 \u4e3a\u513f\u5b50\u62a5\u4ec7\uff0c\u770b\u89c1\u4e49\u519b\u7684\u4eba\u5c31\u6740\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u77e5\u4ed6\u672c\u9886\u4e86\u5f97\uff0c\u7acb\u5373\u5148\u53d1\u5236\u4eba\uff0c\u5530\u7684\u4e00\u5251\u5c31\u653b\u8fc7\u53bb\uff0c\u523a\u4ed6\u7684\u5de6\u80a9\u4e95\u7a74\u3002\u516c\u5b59\u71d5\u659c \u8eab\u63a0\u8fdb\uff0c\u5251\u5982\u98de\u51e4\uff0c\u4e0e\u53f2\u7ea2\u82f1\u914d\u5408\uff0c\u523a\u4ed6\u53f3\u80a9\u3002 \u3000\u3000\u4ed6\u4eec\u4e8c\u4eba\u7684\u5251\u6cd5\u90fd\u662f\u4ee5\u8f7b\u7075\u8fc5\u6377\u89c1\u957f\uff0c\u4e0d\u6599\u5979\u4eec\u51fa\u5251\u867d\u5feb\uff0c\u4f9d\u7136\u662f\u523a\u4e86\u4e2a\u7a7a\u3002 \u3000\u3000\u638c\u98ce\u5251\u5f71\u4e4b\u4e2d\uff0c\u53ea\u542c\u5f97\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u6761\u7c97\u5982\u4eba\u81c2\u7684\u6811\u679d\u5e94\u624b\u800c\u6298\uff0c\u6587\u9053\u5e84\u7684\u638c\u529b\u6392 \u5c71\u5012\u6d77\u822c\u7684\u6d8c\u6765\uff0c\u53f2\u7ea2\u82f1\u7ed5\u6811\u75be\u8d70\uff0c\u5e78\u800c\u6ca1\u6709\u7ed9\u4ed6\u4f24\u7740\uff0c\u4f46\u89c1\u4ed6\u5982\u6b64\u58f0\u52bf\uff0c\u4e5f\u662f\u4e0d\u7531\u5f97\u6697\u6697 \u5403\u60ca\u3002 \u3000\u3000\u6587\u9053\u5e84\u72c2\u7b11\u9053\uff1a\u201c\u77e5\u9053\u5389\u5bb3\u4e86\u4e48\uff1f\u201d\u547c\u7684\u4e00\u638c\u53c8\u5411\u516c\u5b59\u71d5\u6253\u53bb\u3002\u516c\u5b59\u71d5\u56de\u5251\u9632\u8eab\uff0c\u4f46\u542c \u5f97\u55e1\u55e1\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u5251\u5c16\u7adf\u662f\u88ab\u638c\u529b\u9707\u8361\u5f97\u6643\u523b\u4e0d\u4f11\uff01 \u3000\u3000\u53f2\u7ea2\u82f1\u89c1\u52bf\u4e0d\u5999\uff0c\u8fde\u5fd9\u6325\u5251\u62a2\u653b\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6587\u9053\u5e84\u7684\u7b2c\u4e09\u638c\u53c8\u81f3\uff0c\u9002\u624d\u90a3\u4e24\u638c \u5a01\u731b\u4e4b\u6781\uff0c\u8fd9\u4e00\u638c\u6253\u51fa\uff0c\u5374\u662f\u65e0\u58f0\u65e0\u610f\uff0c\u53f2\u7ea2\u82f1\u6014\u4e86\u4e00\u6014\uff0c\u9661\u5730\u5fc3\u4e2d\u4e00\u51db\uff0c\u53ea\u89c9\u90a3\u80a1\u638c\u529b\u6709 \u5982\u6697\u6d41\u6025\u6e4d\uff0c\u529b\u53ef\u541e\u821f\u3002\u5e78\u4e8f\u53f2\u7ea2\u82f1\u8f7b\u529f\u8d85\u5353\uff0c\u4e00\u89c9\u4e0d\u5999\uff0c\u7acb\u5373\u4fbf\u662f\u4e00\u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\u5012 \u7eb5\u907f\u5f00\u3002\u95ea\u907f\u5f97\u867d\u7136\u5de7\u5999\uff0c\u4f46\u80f8\u53e3\u4e5f\u597d\u4f3c\u53d7\u4e86\u5de8\u9524\u4e00\u51fb\u4f3c\u7684\uff0c\u4e94\u810f\u516d\u8151\u90fd\u51e0\u4e4e\u7ffb\u4e86\u8f6c\u6765\uff0c\u539f \u6765\u6587\u9053\u5e84\u5df2\u662f\u7528\u4e0a\u4e86\u201c\u4e09\u8c61\u795e\u529f\u201d\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u53c8\u662f\u5403\u60ca\uff0c\u53c8\u662f\u8be7\u5f02\u3002\u5979\u5e76\u4e0d\u662f\u6ca1\u6709\u89c1\u8bc6\u8fc7\u6587\u9053\u5e84\u7684\u201c\u4e09\u8c61\u795e\u529f\u201d\uff0c\u5728\u897f\u660c\u4e4b \u65f6\uff0c\u5979\u4e5f\u66fe\u4e0e\u6587\u9053\u5e84\u5355\u72ec\u4ea4\u8fc7\u624b\uff0c\u5f53\u65f6\u867d\u662f\u654c\u4e0d\u8fc7\u4ed6\uff0c\u4f46\u5728\u5341\u6570\u62db\u4e4b\u5185\uff0c\u4e5f\u8fd8\u62b5\u6321\u5f97\u4f4f\uff0c\u8fdc \u4e0d\u82e5\u4eca\u665a\u7684\u5403\u529b\uff0c\u53ea\u4e0d\u8fc7\u4e24\u4e2a\u7167\u9762\uff0c\u5c31\u51e0\u4e4e\u4f24\u5728\u4ed6\u7684\u638c\u4e0b\u3002\u53f2\u7ea2\u82f1\u5fc3\u60f3\uff0c\u600e\u7684\u76f8\u9694\u8fd8\u672a\u6709\u4e09 \u4e2a\u6708\uff0c\u4ed6\u7684\u4e09\u8c61\u795e\u529f\u7adf\u7136\u7cbe\u8fdb\u5982\u65af\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u6709\u6240\u4e0d\u77e5\uff0c\u539f\u6765\u6587\u9053\u5e84\u6240\u7ec3\u7684\u201c\u4e09\u8c61\u795e\u529f\u201d\u4e43\u662f\u4e00\u79cd\u4ecb\u4e4e\u90aa\u6b63\u4e4b\u95f4\u7684\u5185\u529f\uff0c\u53ef\u4ee5 \u6709\u4e24\u79cd\u7ec3\u6cd5\uff0c\u8d70\u6b63\u5b97\u5185\u529f\u7684\u8def\u5b50\u6765\u7ec3\uff0c\u529f\u529b\u53ea\u80fd\u6e10\u8fdb\uff0c\u4f46\u5374\u7cbe\u7eaf\uff0c\u800c\u5bf9\u8eab\u4f53\u6ca1\u6709\u59a8\u5bb3\uff1b\u5018\u82e5 \u8d70\u90aa\u6d3e\u7684\u9738\u9053\u8def\u5b50\u6765\u7ec3\uff0c\u89c1\u6548\u6781\u5feb\uff0c\u4f46\u5bf9\u8eab\u4f53\u5374\u6781\u4e3a\u6709\u5bb3\u3002 \u3000\u3000\u6587\u9053\u5e84\u7684\u706b\u5019\u8ddd\u79bb\u7089\u706b\u7eaf\u9752\u7684\u5883\u754c\u5c1a\u8fdc\uff0c\u672c\u6765\u4e0d\u6562\u7528\u90aa\u6d3e\u7684\u65b9\u6cd5\u7ec3\u529f\u7684\uff0c\u4f46\u5728\u4ed6\u7684\u513f\u5b50 \u6b7b\u540e\uff0c\u4ed6\u4e00\u5fc3\u53ea\u60f3\u62a5\u4ec7\uff0c\u5df2\u662f\u9677\u4e8e\u534a\u75af\u72c2\u7684\u72b6\u6001\uff0c\u7adf\u7136\u4e0d\u62e9\u624b\u6bb5\u5730\u8d70\u6700\u9738\u9053\u7684\u8def\u5b50\u6765\u7ec3\u201c\u4e09 \u8c61\u795e\u529f\u201d\u3002\u5927\u529f\u544a\u6210\u4e4b\u540e\uff0c\u65b9\u59cb\u53d1\u89c9\u5df2\u6709\u8d70\u706b\u4eba\u9b54\u7684\u9884\u5146\uff0c\u591a\u5219\u4e00\u5e74\uff0c\u5c11\u5219\u534a\u8f7d\uff0c\u5c31\u5c06\u6210\u4e3a \u5e9f\u4eba\u3002\u4ed6\u4e1a\u5df2\u8d70\u5165\u9b54\u9053\uff0c\u81ea\u662f\u4e0d\u77e5\u540e\u6094\uff0c\u53d1\u89c9\u4e86\u6709\u8d70\u706b\u5165\u9b54\u7684\u9884\u5146\uff0c\u66f4\u662f\u6025\u4e8e\u8981\u5728\u8fd9\u4e00\u5e74\u534a \u622a\u4e4b\u5185\uff0c\u6740\u5c3d\u4ec7\u4eba\u4e86\u3002\u8fd9\u6b21\u4ed6\u6765\u5f82\u5f95\u5c71\uff0c\u5c31\u662f\u60f3\u4e0e\u9633\u6d69\u8054\u624b\uff0c\u8ba1\u5212\u600e\u6837\u628a\u4ed6\u5fc3\u76ee\u4e2d\u7684\u5f3a\u4ec7\u5927 \u654c\u4e00\u4e00\u9664\u6389\u7684\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u8fd9\u51e0\u4e2a\u6708\u4e0e\u91d1\u9010\u6d41\u671d\u5915\u4e00\u8d77\uff0c\u5f97\u76ca\u4e0d\u5c11\uff0c\u5c24\u5176\u5728\u6b63\u5b97\u7684\u5185\u529f\u5fc3\u6cd5\u4e0a\uff0c\u5f97\u4e86\u91d1\u9010\u6d41 \u7684\u4f20\u6388\uff0c\u5df2\u6709\u5c0f\u6210\uff0c\u662f\u4ee5\u867d\u7136\u611f\u5230\u5403\u529b\u975e\u5e38\uff0c\u4e5f\u8fd8\u53ef\u4ee5\u52c9\u5f3a\u62b5\u53d7\uff0c\u5f53\u4e0b\u8fd0\u6c14\u4e09\u8f6c\uff0c\u6c14\u6c89\u4e39 \u7530\uff0c\u80f8\u53e3\u7684\u75db\u695a\u767b\u65f6\u6d88\u5931\u3002 \u3000\u3000\u516c\u5b59\u71d5\u5251\u6cd5\u6781\u4e3a\u7cbe\u5999\uff0c\u4f46\u529f\u529b\u4e0d\u8db3\uff0c\u6bd4\u4e4b\u53f2\u7ea2\u82f1\u5c1a\u8981\u7a0d\u900a\u4e00\u7b79\u3002\u5728\u6587\u9053\u5e84\u7684\u4e09\u8c61\u795e\u529f\u731b \u653b\u4e4b\u4e0b\uff0c\u53f2\u7ea2\u82f1\u52c9\u5f3a\u53ef\u4ee5\u652f\u6301\uff0c\u516c\u5b59\u71d5\u5374\u5df2\u662f\u611f\u5230\u6c14\u4e5f\u900f\u4e0d\u8fc7\u6765\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u516c\u5b59\u71d5\u6b63\u7ad9\u5728\u5730\u9053\u7684\u51fa\u53e3\uff0c\u76fc\u671b\u91d1\u9010\u6d41\u4e0e\u5389\u5357\u661f\u51fa\u6765\u3002\u8fdf\u8fdf\u4e0d\u89c1\uff0c\u5fc3\u91cc\u5927\u4e3a\u7740\u6025\u3002 \u3000\u3000\u5f53\u4e0b\u865a\u6643\u4e00\u62db\uff0c\u7ed5\u6811\u800c\u8d70\uff0c\u7a0d\u68a2\u677e\u4e86\u53e3\u6c14\uff0c\u8fde\u5fd9\u53d1\u51fa\u4e00\u4e32\u957f\u5578\u3002 \u3000\u3000\u5979\u53d1\u5578\u7684\u7528\u610f\u5f53\u7136\u662f\u5411\u91d1\u3001\u5389\u4e8c\u4eba\u62a5\u8b66\uff0c\u6587\u9053\u5e84\u5374\u4ee5\u4e3a\u662f\u5979\u50ac\u4fc3\u7236\u4eb2\u5feb\u6765\u7684\u8baf\u53f7\uff0c\u5fc3\u91cc \u60f3\u9053\uff1a\u201c\u516c\u5b59\u5b8f\u8fd9\u8001\u513f\u6599\u60f3\u4e0d\u4f1a\u8ba9\u5979\u5973\u513f\u72ec\u81ea\u6765\u6b64\uff0c\u5821\u4e2d\u6709\u5450\u558a\u53ae\u6740\u4e4b\u58f0\uff0c\u60f3\u5fc5\u662f\u8fd9\u8001\u513f\u5df2 \u5728\u91cc\u9762\u548c\u9633\u6d69\u4ed6\u4eec\u6253\u8d77\u6765\u4e86\u3002\u8fd9\u8001\u513f\u82e5\u662f\u8d76\u6765\u52a9\u8fd9\u4e24\u4e2a\u4e2b\u5934\uff0c\u5012\u662f\u6709\u70b9\u68d8\u624b\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u71d5\u751a\u662f\u673a\u7075\uff0c\u5bdf\u89c9\u6587\u9053\u5e84\u6014\u4e86\u4e00\u6014\uff0c\u638c\u529b\u4e5f\u4f3c\u5e73\u7a0d\u5fae\u51cf\u5f31\u4e00\u4e9b\uff0c\u7acb\u5373\u731c\u5230\u4e86\u5bf9\u65b9 \u7684\u5fc3\u601d\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u5feb\u6765\uff01\u201d \u3000\u3000\u516c\u5b59\u71d5\u539f\u662f\u60f3\u6270\u4e71\u5bf9\u65b9\u7684\u5fc3\u795e\uff0c\u53ea\u76fc\u80fd\u591f\u591a\u652f\u6301\u4e00\u523b\u4fbf\u6709\u8f6c\u673a\u3002\u54ea\u77e5\u5979\u4e0d\u53eb\u558a\u8fd8\u597d\uff0c\u4e00 \u558a\u51fa\u6765\uff0c\u53cd\u800c\u6fc0\u4f7f\u6587\u9053\u5e84\u5fc5\u987b\u75db\u4e0b\u6740\u624b\u4e86\u3002 \u3000\u3000\u6587\u9053\u5e84\u731b\u5730\u4e00\u58f0\u72de\u7b11\uff0c\u9634\u607b\u607b\u5730\u8bf4\u9053\uff1a\u201c\u5373\u4f7f\u516c\u5b59\u8001\u513f\u6765\u4e86\uff0c\u6211\u4e5f\u4e0d\u60e7\u3002\u4f46\u4f60\u5374\u5df2\u662f\u4e0d \u80fd\u6d3b\u7740\u7b49\u5230\u89c1\u4f60\u7684\u7239\u7239\u4e86\uff01\u201d \u3000\u3000\u731b\u542c\u5f97\u201c\u8f70\u9686\u201d\u4e00\u58f0\uff0c\u6587\u9053\u5e84\u4e00\u638c\u5288\u5012\u4e86\u4e00\u68f5\u6811\uff0c\u516c\u5b59\u71d5\u6b63\u5728\u8fd9\u68f5\u6811\u7684\u540e\u9762\uff0c\u51e0\u4e4e\u7ed9\u5b83 \u538b\u7740\u3002 \u3000\u3000\u5730\u9053\u7684\u51fa\u53e3\u662f\u4e00\u7247\u957f\u6ee1\u8346\u68d8\u8305\u8349\u7684\u8352\u5730\uff0c\u53ea\u6709\u8fd9\u4e00\u68f5\u6811\u53ef\u4ee5\u7528\u6765\u63a9\u853d\uff0c\u6811\u4e00\u5012\u4e0b\uff0c\u516c\u5b59 \u71d5\u7684\u8f7b\u529f\u5df2\u662f\u96be\u4ee5\u95ea\u6e38\u3002\u6587\u9053\u5e84\u547c\u547c\u7684\u8fde\u53d1\u4e09\u638c\uff0c\u7adf\u662f\u9690\u9690\u631f\u7740\u98ce\u96f7\u4e4b\u58f0\uff01 \u3000\u3000\u53f2\u7ea2\u82f1\u97ad\u5251\u9f50\u65bd\uff0c\u957f\u97ad\u7f20\u8db3\uff0c\u77ed\u5251\u6b3a\u8eab\u800c\u8fdb\uff0c\u5192\u9669\u653b\u4ed6\u7684\u4e0a\u4e09\u8def\u3002\u8fd9\u4e00\u62db\u662f\u5979\u5bb6\u4f20\u5251\u6cd5 \u7684\u7cbe\u534e\u6240\u5728\uff0c\u5251\u5c16\u98a4\u52a8\uff0c\u540c\u65f6\u653b\u4ed6\u7684\u4e09\u5904\u8981\u5bb3\u3002\u5de6\u523a\u80f8\u524d\u7684\u201c\u4e73\u7a81\u7a74\u201d\uff0c\u53f3\u523a\u4ed6\u7684\u7435\u7436\u9aa8\u5f53 \u4e2d\u7684\u201c\u80a9\u4e95\u7a74\u201d\uff0c\u4e2d\u523a\u4ed6\u5c0f\u8179\u7684\u201c\u6108\u6c14\u7a74\u201d\u3002\u800c\u4ee5\u523a\u201c\u80a9\u4e95\u7a74\u201d\u4e3a\u4e3b\uff0c\u5176\u4ed6\u4e24\u5904\u4f5c\u4e3a\u966a\u886c\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u6740\u624b\u5251\u62db\u51cc\u5389\u975e\u5e38\uff0c\u6587\u9053\u5e84\u4e0d\u5f97\u4e0d\u6682\u65f6\u653e\u677e\u516c\u5b59\u71d5\uff0c\u4e00\u4e2a\u8f6c\u8eab\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u5148 \u6bd9\u4e86\u4f60\u8fd9\u81ed\u4e2b\u5934\uff01\u201d\u638c\u529b\u5954\u96f7\u95ea\u7535\u822c\u7684\u500f\u7136\u800c\u81f3\uff0c\u5251\u5149\u767b\u65f6\u53c8\u88ab\u9707\u6563\uff0c\u6709\u5982\u6ce2\u5fc3\u8361\u6708\uff0c\u95ea\u8d77 \u4e86\u5343\u70b9\u94f6\u5149\uff0c\u53c8\u5982\u9ed1\u591c\u7e41\u661f\u6b92\u843d\u5982\u96e8\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u4e2a\u5f53\u513f\uff0c\u53f2\u7ea2\u82f1\u53ea\u89c9\u80a9\u5934\u5fae\u75db\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u53f3\u80a9\u7684\u4e0a\u8863\u5df2\u662f\u7ed9\u6587\u9053\u5e84\u6495\u7834\uff01 \u539f\u6765\u6587\u9053\u5e84\u662f\u8981\u6293\u788e\u5979\u7684\u7435\u7436\u9aa8\uff0c\u4ee5\u62a5\u590d\u5979\u5251\u523a\u81ea\u5df1\u7684\u80a9\u4e95\u7a74\u7684\uff0c\u53f2\u7ea2\u82f1\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c \u6070\u6070\u907f\u5f00\uff0c\u540c\u65f6\u516c\u5b59\u71d5\u4ea6\u5df2\u5feb\u5251\u75be\u653b\uff0c\u5979\u8fd9\u4e00\u62db\u4e5f\u6b63\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\u7684\u7cbe\u5999\u5251\u6cd5\u3002 \u3000\u3000\u4e09\u65b9\u9762\u52a8\u4f5c\u90fd\u662f\u5feb\u5982\u95ea\u7535\uff0c\u6587\u9053\u5e84\u6765\u4e0d\u53ca\u5411\u53f2\u7ea2\u82f1\u8ffd\u51fb\uff0c\u4e00\u4e2a\u8f6c\u8eab\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u5f39\u5f00 \u516c\u5b59\u71d5\u7684\u9752\u94a2\u5251\uff01\u8fc5\u5373\u5316\u6307\u4e3a\u638c\uff0c\u5212\u4e86\u534a\u9053\u5f27\u5f62\uff0c\u53cc\u638c\u540c\u65f6\u51fb\u4e0b\uff0c\u53c8\u662f\u4e00\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\uff01 \u3000\u3000\u516c\u5b59\u71d5\u7684\u529f\u529b\u6bd4\u4e4b\u53f2\u7ea2\u82f1\u5c1a\u8981\u7a0d\u900a\u4e00\u7b79\uff0c\u8fd9\u4e00\u62db\u201c\u96f7\u7535\u4ea4\u8f70\u201d\u4e43\u662f\u5a01\u731b\u65e0\u4f26\u7684\u6740\u624b\uff0c\u6587 \u9053\u5e84\u5df2\u7ecf\u628a\u201c\u4e09\u8c61\u795e\u529f\u201d\u53d1\u6325\u5f97\u6dcb\u6f13\u5c3d\u81f4\uff0c\u516c\u5b59\u71d5\u5982\u4f55\u80fd\u591f\u62b5\u6321\uff1f \u3000\u3000\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u516c\u5b59\u71d5\u7684\u9752\u94a2\u5251\u5df2\u662f\u7ed9\u6587\u9053\u5e84\u6253\u843d\uff01\u516c\u5b59\u71d5\u8eab\u5f62\u6025\u8d77\uff0c\u79bb\u5f26\u7bad\u822c \u7684\u5411\u524d\u75be\u7a9c\uff0c\u6587\u9053\u5e84\u559d\u9053\uff1a\u201c\u54ea\u91cc\u8dd1\uff01\u201d\u5982\u5f71\u968f\u5f62\u7684\u4e00\u4e2a\u8d77\u4f0f\u5c31\u8ffd\u4e86\u4e0a\u6765\uff01 \u3000\u3000\u773c\u770b\u6587\u9053\u5e84\u5c31\u8981\u6293\u7740\u4e86\u516c\u5b59\u71d5\u7684\u80cc\u5fc3\uff0c\u731b\u6240\u5f97\u53c8\u662f\u201c\u8f70\u201d\u7684\u4e00\u58f0\u5de8\u54cd\uff0c\u9707\u8033\u6b32\u804b\uff01\u5730\u9053 \u51fa\u53e3\u5904\u7684\u90a3\u5757\u5927\u77f3\u6eda\u5f00\uff0c\u91d1\u9010\u6d41\u3001\u5389\u5357\u661f\u4e00\u9f50\u51b2\u51fa\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u53d7\u4e86\u638c\u529b\u7684\u9707\u8361\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u5728\u5730\u4e0a\u6253\u4e86\u51e0\u4e2a\u76d8\u65cb\uff0c\u6b64\u65f6\u5140\u662f\u672a\u80fd\u7a33\u4f4f\u8eab\u5f62\u3002 \u3000\u3000\u91d1\u3001\u5389\u4e8c\u4eba\u90fd\u662f\u5927\u5403\u4e00\u60ca\uff0c\u91d1\u9010\u6d41\u5954\u5411\u53f2\u7ea2\u82f1\uff0c\u5389\u5357\u661f\u6025\u5fd9\u8dd1\u8fc7\u53bb\u6321\u4f4f\u6587\u9053\u5e84\u3002 \u3000\u3000\u6587\u9053\u5e84\u72de\u7b11\u53eb\u9053\uff1a\u201c\u8fd8\u6211\u513f\u5b50\u7684\u547d\u6765\uff01\u201d\u53cc\u81c2\u7b95\u5f20\uff0c\u5de6\u624b\u662f\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u5411 \u5389\u5357\u661f\u7684\u5929\u7075\u76d6\u6293\u4e0b\uff1b\u53f3\u638c\u8574\u85cf\u7740\u2018\u4e09\u8c61\u795e\u529f\u2019\u7684\u5a01\u529b\uff0c\u5288\u5411\u4ed6\u7684\u80f8\u819b\u3002\u5de6\u811a\u540c\u65f6\u98de\u8d77\uff0c\u8e22 \u4ed6\u5c0f\u8179\uff01\u8fd9\u4e00\u62db\u4e09\u5f0f\u5168\u662f\u62fc\u4e86\u6027\u547d\u7684\u6253\u6cd5\u3002 \u3000\u3000\u5389\u5357\u661f\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4eba\u6562\u60c5\u662f\u75af\u4e86\uff01\u201d\u767e\u5fd9\u4e2d\u4f7f\u51fa\u201c\u5929\u7f57\u6b65\u6cd5\u201d\uff0c\u907f\u5f00\u4e86\u6587\u9053 \u5e84\u98de\u811a\uff0c\u53cc\u638c\u5408\u62b1\u5982\u73af\uff0c\u4ee5\u67d4\u52b2\u8361\u5f00\u4e86\u6587\u9053\u5e84\u7684\u4e00\u6293\u3002\u4f46\u6587\u9053\u5e84\u5411\u4e2d\u8def\u5288\u6765\u7684\u638c\u529b\u5389\u5357\u661f\u4ecd \u662f\u4e0d\u80fd\u8eb2\u8fc7\u3002\u4e24\u6761\u4eba\u5f71\u500f\u5206\u500f\u5408\uff0c\u5389\u5357\u661f\u5927\u53eb\u4e00\u58f0\uff0c\u9661\u5730\u4e00\u4e2a\u7b4b\u6597\u5012\u7ffb\u51fa\u4e00\u4e08\u3002\u201c\u5929\u7f57\u6b65 \u6cd5\u201d\u548c\u5927\u987b\u5f25\u638c\u5f0f\u4e43\u662f\u91d1\u4e16\u9057\u4eb2\u81ea\u4f20\u6388\u7684\u4e0a\u4e58\u6b66\u529f\uff0c\u5389\u5357\u661f\u7528\u51fa\u8fd9\u4e24\u79cd\u6b66\u6797\u7edd\u5b66\uff0c\u7adf\u7136\u53ea\u662f \u4e00\u4e2a\u7167\u9762\u5c31\u8d25\u5728\u6587\u9053\u5e84\u7684\u624b\u4e0b\uff0c\u6b64\u65f6\u8fde\u91d1\u9010\u6d41\u90fd\u662f\u4e0d\u7531\u5f97\u5927\u5927\u5403\u60ca\u4e86\uff01 \u3000\u3000\u53f2\u7ea2\u82f1\u7529\u5f00\u91d1\u9010\u6d41\u7684\u624b\uff0c\u6025\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u6ca1\u53d7\u4f24\uff0c\u4f60\u5feb\u53bb\uff01\u201d\u65e0\u987b\u5979\u7684\u50ac\u4fc3\uff0c\u91d1\u9010\u6d41\u5df2 \u662f\u4e00\u8dc3\u800c\u8d77\uff01 \u3000\u3000\u6587\u9053\u5e84\u5927\u559d\u9053\uff1a\u201c\u597d\u5440\uff0c\u91d1\u9010\u6d41\u4f60\u8fd9\u5c0f\u5b50\u4e5f\u6765\u4e86\uff01\u54c8\u54c8\uff0c\u54c8\u54c8\uff0c\u54c8\u54c8\u54c8\uff01\u8fd9\u6b63\u662f\u5929\u5802\u6709 \u8def\u4f60\u4e0d\u8fdb\uff0c\u5730\u72f1\u65e0\u95e8\u4f60\u504f\u8fdb\u6765\uff01\u597d\u6781\u4e86\uff0c\u597d\u6781\u4e86\uff01\u8fd8\u6211\u513f\u5b50\u7684\u547d\u6765\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e00\u6e9c\u70df\u822c\u5730\u6eda\u5230\uff0c\u95ea\u7535\u822c\u7684\u5728\u5730\u4e0a\u6253\u4e86\u51e0\u4e2a\u76d8\u65cb\uff0c\u6587\u9053\u5e84\u53cc\u638c\u5feb\u5200\u4e5f\u4f3c\u7684\u5288\u4e0b\uff0c \u72c2\u7b11\u58f0\u4e2d\uff0c\u91d1\u9010\u6d41\u9661\u7136\u8dc3\u8d77\uff0c\u53ea\u5728\u8fd9\u4e00\u8d77\u4e00\u4f0f\u7684\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u5df2\u662f\u63a5\u8fde\u4f7f\u51fa\u4e86\u4e03\u624b\u602a\u62db\uff0c\u628a \u6587\u9053\u5e84\u7684\u521a\u731b\u7edd\u4f26\u7684\u638c\u52bf\u4e00\u4e00\u5316\u89e3\u5f00\u53bb\uff01 \u3000\u3000\u53f2\u7ea2\u82f1\u53eb\u9053\uff1a\u201c\u4f7f\u7384\u94c1\u5b9d\u5251\uff0c\u7384\u94c1\u5b9d\u5251\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u548c\u6587\u9053\u5e84\u4ea4\u4e0a\u4e86\u624b\uff0c\u5fc3\u4e2d\u4e5f\u662f\u597d\u751f\u8be7\u5f02\uff0c\u4e0d\u89e3\u4ed6\u7684\u6b66\u529f\u4f55\u4ee5\u4f1a\u5ffd\u7136\u9ad8\u4e86\u8fd9\u4e48\u591a\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u6587\u9053\u5e84\u4e00\u638c\u5288\u4e0b\uff0c\u91d1\u9010\u6d41\u201c\u54ce\u54df\u201d\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\uff01\u5389\u5357\u661f\u5931\u58f0\u60ca\u547c\uff0c\u6b63\u8981\u8dd1\u4e0a \u53bb\uff0c\u4e0d\u6599\u521a\u521a\u8fc8\u51fa\u4e00\u6b65\uff0c\u53ea\u89c9\u80f8\u4e2d\u6c14\u8840\u7ffb\u6d8c\uff0c\u539f\u6765\u4ed6\u521a\u624d\u53d7\u4e86\u6587\u9053\u5e84\u638c\u529b\u7684\u9707\u8361\uff0c\u867d\u672a\u81f3\u4e8e \u53d7\u4e86\u5185\u4f24\uff0c\u4f46\u6025\u5207\u4e4b\u95f4\uff0c\u6c14\u606f\u5df2\u662f\u96be\u4ee5\u8c03\u5300\uff0c\u4e00\u53cc\u811a\u90fd\u4e0d\u542c\u81ea\u5df1\u7684\u4f7f\u5524\u4e86\u3002 \u3000\u3000\u5389\u5357\u661f\u81ea\u77e5\u6709\u5fc3\u65e0\u529b\uff0c\u6697\u53eb\u201c\u7cdf\u7cd5\uff01\u201d\u5ffd\u542c\u5f97\u53f2\u7ea2\u82f1\u7b11\u9053\uff1a\u201c\u5389\u5927\u54e5\u4e0d\u7528\u62c5\u5fc3\uff01\u201d\u5389\u5357 \u661f\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u91d1\u9010\u6d41\u5df2\u7ecf\u8df3\u8d77\u8eab\u6765\uff0c\u624b\u4e2d\u62ff\u7740\u7384\u94c1\u5b9d\u5251\uff0c\u90ce\u58f0\u8bf4\u9053\uff1a\u201c\u6587\u9053\u5e84\uff0c\u4eae\u5175\u5668 \u5427\uff0c\u54b1\u4eec\u8f83\u91cf\u8f83\u91cf\u5251\u6cd5\uff01\u201d\u6587\u9053\u5e84\u624b\u91cc\u5374\u62ff\u7740\u4e00\u5e45\u7834\u5e03\u3002 \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u5f97\u53f2\u7ea2\u82f1\u63d0\u9192\uff0c\u4f46\u5374\u817e\u4e0d\u51fa\u624b\u6765\u62d4\u5251\uff0c\u56e0\u6b64\u53ea\u597d\u7528\u4e00\u4e2a\u53e4\u602a\u7684\u8eab\u6cd5\uff0c\u4f6f\u4f5c\u8dcc \u5012\uff0c\u4f0f\u5730\u6253\u4e86\u4e2a\u6eda\uff0c\u8fd9\u624d\u80fd\u591f\u62bd\u7a7a\u62d4\u5251\u3002\u6587\u9053\u5e84\u8eab\u624b\u4f55\u7b49\u77eb\u6377\uff0c\u7acb\u5373\u75be\u6293\u4e0b\u53bb\uff0c\u6495\u7834\u4e86\u91d1\u9010 \u6d41\u7684\u8863\u88f3\u3002\u4e0d\u8fc7\u8fd9\u4e5f\u5728\u91d1\u9010\u6d41\u610f\u6599\u4e4b\u4e2d\uff0c\u7b97\u51c6\u4e86\u6709\u60ca\u65e0\u9669\u7684\u3002\u6b64\u65f6\u4ed6\u5df1\u62d4\u5251\u51fa\u9798\uff0c\u6587\u9053\u5e84\u4ecd \u662f\u7a7a\u624b\uff0c\u672c\u6765\u5730\u53ef\u4ee5\u51ed\u7740\u7384\u94c1\u5b9d\u5251\u7684\u5a01\u529b\uff0c\u7acb\u5373\u8fdb\u62db\uff0c\u6740\u4e2a\u6587\u9053\u5e84\u63aa\u624b\u4e0d\u53ca\u7684\uff0c\u4f46\u91d1\u9010\u6d41\u5374 \u4e0d\u613f\u610f\u6709\u5931\u540d\u5bb6\u98ce\u8303\u3002 \u3000\u3000\u6587\u9053\u5e84\u9886\u6559\u8fc7\u7384\u94c1\u5b9d\u5251\u7684\u5389\u5bb3\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5e78\u4e8f\u8fd9\u5c0f\u5b50\u9a84\u50b2\u5f97\u5f88\uff0c\u5426\u5219\u7ed9\u5730\u62a2\u4e86\u5148\u624b \u653b\u52bf\uff0c\u4eca\u665a\u53ea\u6015\u96be\u9003\u4e00\u8d25\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d\u6587\u9053\u5e84\u5927\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u5df2\u7ecf\u8fdf\u4e86\uff01\u201d\u7b11\u58f0\u4e2d \u53ea\u89c1\u4e00\u9053\u9ed1\u6cb9\u6cb9\u7684\u5149\u534e\u5df2\u662f\u500f\u5730\u5411\u91d1\u9010\u6d41\u5377\u53bb\uff01 \u3000\u3000\u539f\u6765\u6587\u9053\u5e84\u6240\u7528\u7684\u8f6f\u5251\u4e5f\u662f\u4e00\u4ef6\u5b9d\u7269\uff0c\u90a3\u662f\u767e\u70bc\u7cbe\u94a2\u5316\u6210\u7684\u7ed5\u6307\u5251\uff0c\u4e0d\u7528\u4e4b\u65f6\u53ef\u4ee5\u5f53\u4f5c \u8170\u5e26\u7684\uff0c\u5728\u897f\u660c\u90a3\u6b21\u4ea4\u624b\uff0c\u6587\u9053\u5e84\u7528\u8f6f\u5251\u5bf9\u4ed8\u91d1\u9010\u6d41\u7684\u7384\u94c1\u5b9d\u5251\u867d\u7136\u4e0d\u654c\uff0c\u4f46\u5403\u4e8f\u4ea6\u975e\u5e38 \u5927\u3002 \u3000\u3000\u5389\u5357\u661f\u53eb\u9053\uff1a\u201c\u8d24\u5f1f\u5c0f\u5fc3\uff0c\u8fd9\u662f\u6bd2\u5251\uff01\u201d\u539f\u6765\u6587\u9053\u5e84\u5728\u90a3\u6b21\u6597\u5251\u8d25\u7ed9\u91d1\u9010\u6d41\u4e4b\u540e\uff0c\u91cd\u65b0 \u7528\u6bd2\u836f\u6dec\u8fc7\u8f6f\u5251\u3002\u53ea\u8981\u7ed9\u4ed6\u4f24\u4e86\u4e00\u70b9\u76ae\u8089\uff0c\u5c31\u4f1a\u89c1\u8840\u5c01\u5589\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4ed6\u8fd9\u628a\u5251\u73b0\u51fa\u9ed1\u6cb9\u6cb9\u7684\u5149\u534e\uff0c\u539f\u6765\u662f\u2018\u5582\u2019\u4e86\u6bd2\u7684\u3002\u201d\u5f53\u4e0b\u52a0\u591a \u4e86\u51e0\u5206\u63d0\u9632\uff0c\u4f46\u5374\u4e5f\u50b2\u7136\u4e0d\u60e7\uff0c\u6043\u7740\u7384\u94c1\u5b9d\u5251\u7684\u5a01\u529b\uff0c\u4e00\u62db\u201c\u4e94\u4e01\u5f00\u5c71\u201d\uff0c\u5c31\u5288\u4e0b\u53bb\uff01\u54ea\u77e5 \u6587\u9053\u5e84\u5df2\u7ecf\u7ec3\u6210\u4e86\u90aa\u95e8\u9738\u9053\u7684\u201c\u4e09\u8c61\u795e\u529f\u201d\uff0c\u540c\u6837\u7684\u4e00\u628a\u5251\uff0c\u5728\u4ed6\u624b\u4e2d\u5df2\u662f\u5927\u5927\u7684\u4e0d\u540c\u4e86\uff01 \u3000\u3000\u53cc\u5251\u4e00\u4ea4\uff0c\u53ea\u542c\u5f97\u94ee\u7684\u4e00\u58f0\uff0c\u6587\u9053\u5e84\u7684\u8f6f\u5251\u5f2f\u66f2\u5982\u5f13\uff0c\u4f46\u7384\u94c1\u5b9d\u5251\u5374\u4e5f\u4e0d\u80fd\u5c06\u5b83\u524a\u65ad\u3002 \u6587\u9053\u5e84\u559d\u9053\uff1a\u201c\u770b\u5251\uff01\u201d\u500f\u7136\u95f4\u90a3\u67c4\u8f6f\u5251\u5f39\u4e86\u8d77\u6765\uff0c\u523a\u5411\u91d1\u9010\u6d41\u7684\u80f8\u53e3\uff0c\u91d1\u9010\u6d41\u5e73\u5251\u4e00\u5815\uff0c \u8f6f\u5251\u518d\u6b21\u5f39\u5f00\uff0c\u4f46\u8fc5\u5373\u53c8\u523a\u5230\u4e86\u4ed6\u7684\u80a9\u4e95\u7a74\uff0c\u91d1\u9010\u6d41\u4ee5\u5929\u7f57\u6b65\u6cd5\u6e38\u5f00\uff0c\u89e3\u62db\u53cd\u62db\uff0c\u4e0d\u8fc7\u7247 \u523b\uff0c\u53cc\u5251\u5df2\u662f\u78b0\u51fb\u4e86\u5341\u4e03\u516b\u4e0b\uff01 \u3000\u3000\u6700\u521d\u51e0\u4e0b\uff0c\u6ca1\u6709\u4ec0\u4e48\u3002\u4ea4\u624b\u4e86\u5341\u6570\u62db\u4e4b\u540e\uff0c\u91d1\u9010\u6d41\u53ea\u89c9\u5bf9\u65b9\u7684\u529b\u9053\u9010\u6e10\u52a0\u5f3a\uff0c\u4fe8\u5982\u4e00\u80a1 \u80a1\u7684\u6d6a\u6f6e\u51b2\u6765\uff0c\u4e00\u6d6a\u9ad8\u8fc7\u4e00\u6d6a\uff01 \u3000\u3000\u6587\u9053\u5e84\u7684\u8f6f\u5251\u53ea\u6709\u4e8c\u6307\u4e4b\u5bbd\uff0c\u8584\u5982\u6728\u7247\uff0c\u6bd4\u8d77\u91d1\u9010\u6d41\u7684\u7384\u94c1\u5b9d\u5251\uff0c\u7b80\u76f4\u4e0d\u6210\u6bd4\u4f8b\u3002\u91d1\u9010 \u6d41\u4ee5\u5168\u529b\u6325\u52a8\u7384\u94c1\u5b9d\u5251\uff0c\u521d\u65f6\u8fd8\u80fd\u628a\u4ed6\u7684\u8f6f\u5251\u538b\u5f2f\uff0c\u5230\u4e86\u540e\u6765\uff0c\u6bcf\u4e00\u4e0b\u91cd\u624b\u6cd5\u7684\u5288\u523a\uff0c\u7adf\u7136 \u90fd\u7ed9\u4ed6\u6321\u4f4f\u3002 \u3000\u3000\u5230\u4e86\u4e09\u5341\u62db\u8fc7\u540e\uff0c\u91d1\u9010\u6d41\u7684\u5251\u5c16\u5c31\u50cf\u9644\u4e86\u4e00\u5757\u5927\u77f3\u4f3c\u7684\uff0c\u8d8a\u6765\u8d8a\u89c9\u6c89\u91cd\uff0c\u5251\u6cd5\u7684\u7075\u6d3b\u5df2 \u662f\u5927\u4e0d\u5982\u524d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e0d\u7531\u5f97\u6697\u6697\u5403\u60ca\uff0c\u4ed6\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u4e1a\u5df2\u77e5\u9053\u6587\u9053\u5e84\u662f\u4f7f\u51fa\u9694\u7269\u4f20\u529f\u7684\u672c \u9886\uff0c\u60f3\u628a\u4ed6\u7684\u4e94\u810f\u516d\u8151\u9707\u4f24\u3002\u91d1\u9010\u6d41\u6697\u81ea\u601d\u91cf\uff1a\u201c\u5728\u4e2d\u539f\u7684\u6b66\u6797\u4eba\u58eb\u4e4b\u4e2d\uff0c\u9694\u7269\u4f20\u529f\u7684\u672c \u9886\uff0c\u5f53\u63a8\u53f2\u767d\u90fd\u7b2c\u4e00\u3002\u4f46\u73b0\u5728\u770b\u6765\uff0c\u8fd9\u53ae\u7684\u9694\u7269\u4f20\u529f\uff0c\u5373\u4f7f\u53f2\u767d\u90fd\u590d\u751f\uff0c\u53ea\u6015\u4e5f\u662f\u4e0d\u53ca\u4ed6 \u4e86\uff01\u82e5\u4e0d\u51fa\u5947\u5236\u80dc\uff0c\u4e45\u6218\u4e0b\u53bb\uff0c\u5b9a\u5fc5\u5403\u4e8f\u3002\u201d \u3000\u3000\u6fc0\u6218\u4e2d\uff0c\u91d1\u9010\u6d41\u8fde\u4f7f\u51e0\u4e2a\u53e4\u602a\u7684\u8eab\u6cd5\uff0c\u6bcf\u4e00\u5251\u90fd\u4ece\u6587\u9053\u5e84\u610f\u6599\u4e0d\u5230\u7684\u65b9\u4f4d\u523a\u6765\uff0c\u6740\u5f97\u6587 \u9053\u5e84\u4e5f\u4e0d\u7981\u6709\u70b9\u5403\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u5f53\u4eca\u4e4b\u4e16\uff0c\u82e5\u8bba\u62db\u6570\u7684\u7cbe\u5999\uff0c\u53ea\u6015\u8fde\u6c5f\u6d77\u5929\u5728\u5185\uff0c\u8c01\u4e5f\u6bd4\u4e0d \u4e0a\u8fd9\u4e2a\u5c0f\u5b50\uff01\u201d \u3000\u3000\u4f46\u6587\u9053\u5e84\u4e5f\u662f\u4e2a\u6b66\u5b66\u7684\u884c\u5bb6\uff0c\u5728\u672a\u6478\u6e05\u5bf9\u65b9\u7684\u8def\u6570\u4e4b\u524d\uff0c\u4fbf\u5373\u6539\u53d8\u6218\u672f\uff0c\u6682\u91c7\u5b88\u52bf\u3002\u91d1 \u9010\u6d41\u7684\u7384\u94c1\u5b9d\u5251\u5288\u5f97\u864e\u864e\u751f\u98ce\uff0c\u5374\u603b\u662f\u5288\u6240\u4e0d\u5230\u4ed6\u7684\u8eab\u4e0a\u3002\u5728\u79bb\u8eab\u4e09\u5c3a\u5f00\u5916\uff0c\u5c31\u7ed9\u4ed6\u7684\u8f6f\u5251 \u8361\u5f00\u3002 \u3000\u3000\u5389\u5357\u661f\u7b49\u4eba\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u4f46\u516c\u5b59\u71d5\u5df2\u662f\u7a0d\u7a0d\u53d7\u4e86\u4e00\u70b9\u4f24\u3002\u5389\u5357\u661f\u548c\u53f2\u7ea2\u82f1\u7684\u6c14\u529b\u4ea6\u5c1a \u672a\u987f\u590d\uff0c\u81ea\u77e5\u63d2\u4e0d\u8fdb\u624b\u53bb\u3002\u786c\u8981\u63d2\u624b\u7684\u8bdd\uff0c\u53cd\u800c\u4f1a\u7ed9\u91d1\u9010\u6d41\u589e\u52a0\u56f0\u96be\u3002\u4e0d\u4ec5\u5e2e\u4e0d\u4e86\uff0c\u800c\u4e14\u8981 \u53d8\u6210\u4ed6\u7684\u7d2f\u8d58\u3002 \u3000\u3000\u5389\u5357\u661f\u65e0\u8ba1\u53ef\u65bd\uff0c\u53ea\u597d\u7d22\u6027\u95ed\u76ee\u8fd0\u529f\uff0c\u514d\u5f97\u89c2\u6218\u5206\u795e\u3002\u53ea\u76fc\u771f\u529b\u65e9\u70b9\u51dd\u805a\uff0c\u624d\u53ef\u4ee5\u5e2e\u5f97 \u4e0a\u5fd9\u3002 \u3000\u3000\u53cc\u65b9\u8d8a\u6597\u8d8a\u7d27\uff0c\u731b\u542c\u5f97\u6587\u9053\u5e84\u4e00\u58f0\u5927\u559d\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4e14\u53eb\u4f60\u4e5f\u77e5\u9053\u6211\u7684\u5389\u5bb3\uff01\u201d\u53cc\u5251\u76f8 \u4ea4\uff0c\u201c\u545c\u201d\u7684\u4e00\u58f0\uff0c\u8fd9\u4e00\u6b21\u4e0d\u662f\u8f6f\u5251\u538b\u5f2f\uff0c\u800c\u662f\u91d1\u9010\u6d41\u7684\u7384\u94c1\u5b9d\u5251\u53d7\u4e0d\u4f4f\u5bf9\u65b9\u7684\u538b\u529b\uff0c\u4e0d\u7531 \u81ea\u5df1\u7684\u8981\u8fde\u8fde\u540e\u9000\u4e86\uff01 \u3000\u3000\u539f\u6765\u8fd9\u662f\u56e0\u4e3a\u91d1\u9010\u6d41\u7684\u6c14\u529b\u5df2\u7ed9\u4ed6\u6d88\u8017\u4e86\u4e00\u534a\uff0c\u6545\u800c\u7384\u94c1\u5b9d\u5251\u7684\u5a01\u529b\u4e5f\u5927\u5927\u7684\u6253\u4e86\u6298 \u6263\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u672c\u6765\u662f\u95ed\u76ee\u8fd0\u529f\uff0c\u6b64\u65f6\u542c\u5f97\u516c\u5b59\u71d5\u4e0e\u53f2\u7ea2\u82f1\u7684\u60ca\u53eb\u4e4b\u58f0\uff0c\u60c5\u4e0d\u81ea\u7981\u5730\u7741\u5f00\u773c\u775b\u6765 \u770b\uff0c\u4e00\u770b\u4e4b\u4e0b\uff0c\u4e5f\u662f\u4e0d\u7531\u5f97\u6697\u6697\u5403\u60ca\u3002\u518d\u60f3\u9759\u5fc3\u8fd0\u529f\uff0c\u5df2\u662f\u4e0d\u80fd\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u8fd8\u672a\u77e5\u9053\uff0c\u91d1\u9010\u6d41\u6b64\u9645\u6240\u53d7\u7684\u5a01\u80c1\u8fd8\u8d85\u4e4e\u4ed6\u4eec\u7684\u60f3\u8c61\u4e4b\u4e0a\u3002 \u3000\u3000\u6587\u9053\u5e84\u7684\u5251\u662f\u6dec\u4e86\u5267\u6bd2\u7684\uff0c\u867d\u7136\u672a\u80fd\u523a\u7834\u91d1\u9010\u6d41\u7684\u76ae\u8089\uff0c\u4f46\u4e5f\u4ee4\u5f97\u91d1\u9010\u6d41\u53d7\u5230\u4e86\u5f71\u54cd\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5ffd\u89c9\u638c\u5fc3\u6709\u9ebb\u75d2\u75d2\u7684\u611f\u89c9\uff0c\u539f\u6765\u5728\u53cc\u5251\u5bc6\u5982\u8054\u73e0\u7684\u78b0\u51fb\u4e4b\u4e0b\uff0c\u91d1\u9010\u6d41\u7684\u7384\u94c1\u5b9d\u5251 \u4e5f\u6cbe\u4e86\u6bd2\uff0c\u6bd2\u8d28\u4fb5\u5165\u4ed6\u7684\u638c\u5fc3\u3002 \u3000\u3000\u8fd9\u662f\u89c1\u8840\u5c01\u5589\u7684\u5267\u6bd2\uff0c\u5e78\u4e8f\u91d1\u9010\u6d41\u6ca1\u6709\u53d7\u4f24\u672a\u66fe\u89c1\u8840\uff0c\u5c1a\u65e0\u5927\u788d\uff0c\u4f46\u662f\u867d\u7136\u5982\u6b64\uff0c\u5fc5\u987b \u8fd0\u529f\u9632\u6bd2\uff0c\u4ee5\u514d\u6bd2\u6027\u8513\u5ef6\u3002 \u3000\u3000\u6b64\u65f6\u4ed6\u4eec\u5df2\u6076\u6597\u4e86\u5c06\u8fd1\u534a\u4e2a\u65f6\u8fb0\uff0c\u91d1\u9010\u6d41\u7684\u653b\u52bf\u53d7\u632b\uff0c\u672c\u6765\u5c31\u5df2\u662f\u5904\u4e8e\u4e0b\u98ce\u7684\u4e86\uff0c\u518d\u52a0 \u4e0a\u5fc5\u987b\u8fd0\u529f\u9632\u6bd2\uff0c\u5f53\u7136\u662f\u611f\u5230\u5403\u529b\u975e\u5e38\uff0c\u663e\u5f97\u5de6\u652f\u53f3\u7ecc\u3002\u4e0d\u8fc7\uff0c\u6587\u9053\u5e84\u7a76\u7adf\u4e5f\u8fd8\u6709\u4e9b\u987e\u5fcc\u4ed6 \u7684\u7384\u94c1\u5b9d\u5251\uff0c\u91d1\u9010\u6d41\u201c\u5929\u7f57\u6b65\u6cd5\u201d\u7684\u5947\u5999\u4ea6\u975e\u6587\u9053\u5e84\u6240\u80fd\u76f8\u6bd4\uff0c\u662f\u4ee5\u867d\u7136\u5904\u5728\u4e0b\u98ce\uff0c\u4f46\u5728\u6025 \u5207\u4e4b\u95f4\uff0c\u4e5f\u8fd8\u80dc\u4ed6\u4e0d\u5f97\u3002 \u3000\u3000\u5389\u5357\u661f\u7684\u529f\u529b\u4e0d\u8fc7\u6062\u590d\u56db\u4e94\u5206\uff0c\u81ea\u77e5\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u6b63\u81ea\u7740\u6025\u3002\u4e0d\u6599\u798f\u65e0\u53cc\u81f3\uff0c\u7978\u4e0d\u5355\u884c\uff0c \u53c8\u6709\u4e00\u4e2a\u5f3a\u654c\u6765\u5230\u3002 \u3000\u3000\u4e14\u8bf4\u9633\u6d69\u5728\u91cc\u9762\u7387\u9886\u624b\u4e0b\uff0c\u5305\u56f4\u4e86\u90a3\u95f4\u5c4b\u5b50\uff0c\u8fc7\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u4e0d\u89c1\u91d1\u9010\u6d41\u51fa\u6765\u56de\u7b54\uff0c\u9650 \u671f\u5df2\u5230\uff0c\u4fbf\u5373\u559d\u4ee4\u4f17\u4eba\uff0c\u7834\u95e8\u800c\u5165\u3002 \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u8eb2\u5728\u5c4b\u5b50\u91cc\u7684\u91d1\u3001\u5389\u4e8c\u4eba\u65e9\u5df2\u8d70\u4e86\uff0c\u7834\u95e8\u4e4b\u9645\uff0c\u5982\u4e34\u5927\u654c\uff0c\u6bd2\u7bad\u55b7\u7b52\u9f50\u5bf9\u51c6\u4e86 \u95e8\u7a97\uff0c\u5927\u95e8\u649e\u7834\uff0c\u91cc\u9762\u9b3c\u5f71\u4e5f\u6ca1\u4e00\u4e2a\uff0c\u9633\u6d69\u59cb\u77e5\u4e2d\u8ba1\u3002 \u3000\u3000\u623f\u4e2d\u53d1\u73b0\u4e86\u5730\u9053\u7684\u4eba\u53e3\uff0c\u4f46\u5730\u9053\u4e2d\u7684\u90a3\u4e24\u6247\u77f3\u95e8\u5df2\u7ecf\u7ed9\u5389\u5357\u661f\u5728\u91cc\u9762\u5173\u95ed\uff0c\u8981\u51ff\u5f00\u77f3 \u95e8\uff0c\u53ef\u5c31\u6ca1\u6709\u90a3\u4e48\u5bb9\u6613\u4e86\u3002 \u3000\u3000\u9633\u6d69\u5e76\u975e\u7b28\u86cb\uff0c\u5f53\u4e0b\u4fbf\u5373\u60f3\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5c0f\u5b50\u51b3\u4e0d\u4f1a\u5728\u5730\u9053\u4e2d\u675f\u624b\u5f85\u6bd9\uff0c\u6b64\u65f6\u6599\u60f3\u5df2\u7ecf \u4ece\u5730\u9053\u7684\u53e6\u4e00\u7aef\u51fa\u53bb\u4e86\uff0c\u4ed6\u4e0d\u77e5\u9053\u51fa\u53e3\u4e4b\u5904\uff0c\u4e8e\u662f\u4fbf\u662f\u51fa\u53bb\u641c\u67e5\uff0c\u6587\u9053\u5e84\u4e0e\u91d1\u9010\u6d41\u9ad8\u547c\u9163\u6597 \u4e4b\u58f0\uff0c\u4ece\u540e\u5c71\u9690\u9690\u4f20\u6765\uff0c\u7ec8\u4e8e\u7ed9\u9633\u6d69\u627e\u5230\u4e86\u4ed6\u4eec\u7684\u6240\u5728\u3002 \u3000\u3000\u9633\u6d69\u4e5f\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u4e00\u770b\u4fbf\u770b\u51fa\u4e86\u6587\u9053\u5e84\u5df2\u662f\u7a33\u64cd\u80dc\u7b97\uff0c\u4e0d\u7981\u559c\u51fa\u671b\u5916\u3002\u8981\u77e5\u4ed6 \u6700\u5fcc\u60ee\u7684\u53ea\u662f\u91d1\u9010\u6d41\uff0c\u5389\u5357\u661f\u3001\u516c\u5b59\u71d5\u3001\u53f2\u7ea2\u82f1\u4e09\u4eba\uff0c\u4ed6\u662f\u4e0d\u653e\u5728\u5fc3\u4e0a\u7684\u3002\u4f55\u51b5\u4ed6\u4ee5\u4e3a\u5389\u5357 \u661f\uff0c\u516c\u5b59\u71d5\u53d7\u4e86\u4ed6\u7684\u4fee\u7f57\u9634\u715e\u529f\u4e4b\u4f24\uff0c\u4e00\u5b9a\u8fd8\u672a\u75ca\u6108\u3002\u53ea\u987b\u4ed6\u4e00\u51fa\u624b\uff0c\u4e0d\u96be\u5c06\u654c\u4eba\u4e00\u7f51\u6253 \u5c3d\u3002\u5f53\u4e0b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u59d3\u5389\u7684\u5c0f\u5b50\uff0c\u6211\u4ee5\u4e3a\u4f60\u5df2\u7ecf\u5939\u7740\u5c3e\u5df4\u8d70\u4e86\uff0c\u5374\u539f\u6765\u4f60\u8fd8\u672a\u9003\u51fa\u6211\u7684\u638c \u5fc3\uff01\u597d\uff0c\u4f60\u5c31\u548c\u8fd9\u4e24\u4e2a\u4e2b\u5934\u4e00\u9f50\u4e0a\u6765\u9001\u6b7b\u5427\uff01\u201d \u3000\u3000\u5389\u5357\u7687\u5927\u6012\u9053\uff1a\u201c\u6211\u6b63\u8981\u627e\u4f60\u7b97\u5e10\uff01\u201d\u4e0e\u516c\u5b59\u71d5\u5e76\u80a9\u4e00\u7ad9\uff0c\u5360\u4f4f\u4e86\u5730\u5229\uff0c\u4ee5\u9038\u5f85\u52b3\u3002\u9633 \u6d69\u4e00\u638c\u62cd\u51fa\uff0c\u5bd2\u98ce\u5377\u5730\uff0c\u767b\u65f6\u628a\u65b9\u5706\u5341\u4e08\u4e4b\u5185\uff0c\u53d8\u5f97\u5982\u540c\u51b0\u7a9f\u4e00\u822c\uff01 \u3000\u3000\u5389\u5357\u661f\u4e0e\u516c\u5b59\u71d5\u6643\u4e86\u4e24\u6643\uff0c\u4f46\u5374\u8fde\u201c\u5657\u55e4\u201d\u4e5f\u6ca1\u6253\u4e00\u58f0\uff0c\u5012\u662f\u5728\u65c1\u8fb9\u7684\u53f2\u7ea2\u82f1\uff0c\u7ed9\u51b7\u5f97 \u7981\u4e0d\u4f4f\u7259\u5173\u6253\u6218\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4eec\u4e8c\u4eba\u5728\u6843\u82b1\u8c37\u4e2d\u6cbb\u597d\u4e86\u4fee\u7f57\u9634\u715e\u529f\u4e4b\u4f24\u540e\uff0c\u8eab\u4f53\u4e2d\u5df2\u662f\u81ea\u7136\u800c\u7136\u7684\u4ea7\u751f\u4e86\u4e00\u79cd \u62b5\u6297\u5bd2\u6bd2\u7684\u529f\u80fd\uff0c\u4e0d\u518d\u6015\u9633\u6d69\u7684\u4fee\u7f57\u9634\u715e\u529f\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\uff0c\u5389\u5357\u661f\u867d\u7136\u4e0d\u6015\u5bd2\u6bd2\uff0c\u4f46\u56e0\u6c14\u529b\u672a\u66fe\u60ed\u590d\uff0c\u4ecd\u662f\u4e0d\u514d\u5403\u4e8f\u3002\u9633\u6d69\u7684\u4fee\u7f57\u9634\u715e\u529f\u5e76 \u975e\u4ee5\u638c\u529b\u79f0\u96c4\uff0c\u4f46\u529f\u529b\u6bd5\u7adf\u662f\u5728\u4ed6\u4eec\u4e09\u4eba\u4e4b\u4e0a\u3002 \u3000\u3000\u516c\u5b59\u71d5\u4e1d\u6beb\u4e0d\u89c9\u5bd2\u51b7\uff0c\u77e5\u9053\u5bf9\u65b9\u7684\u4fee\u7f57\u9634\u715e\u529f\u5df2\u662f\u4f24\u5bb3\u4e0d\u4e86\u81ea\u5df1\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u80c6\u6c14\u58ee\u4e86 \u8bb8\u591a\u3002\u5251\u6cd5\u4e00\u5c55\uff0c\u8eab\u4f3c\u6c34\u86c7\u6e38\u8d70\uff0c\u7b11\u9053\uff1a\u201c\u9633\u8001\u8d3c\uff0c\u591a\u8c22\u4f60\u7ed9\u6211\u6247\u51c9\uff0c\u597d\u51c9\u5feb\u554a\uff01\u201d\u6e05\u8106\u7684 \u7b11\u58f0\u4e2d\uff0c\u6296\u8d77\u4e86\u4e09\u6735\u5251\u82b1\uff0c\u8fde\u88ad\u9633\u6d69\u8170\u90e8\u4ee5\u4e0b\u7684\u98ce\u5e02\u3001\u73af\u8df3\u3001\u7ef4\u9633\u738b\u5904\u9ebb\u7a74\uff0c\u53f2\u7ea2\u82f1\u7684\u5251\u6cd5 \u548c\u5979\u540c\u5c5e\u4e8e\u8f7b\u7075\u8fc5\u6377\u4e00\u8def\uff0c\u4e0d\u7528\u4e8b\u5148\u7ec3\u4e60\uff0c\u81ea\u7136\u914d\u5408\u5f97\u4e1d\u4e1d\u4eba\u6263\u3002\u5530\u3001\u5530\u3001\u5530\u4e09\u5251\u8fde\u73af\u523a \u53bb\uff0c\u5251\u5c16\u70b9\u7684\u662f\u9633\u6d69\u8170\u90e8\u4ee5\u4e0a\u7684\u60ac\u67a2\u3001\u4e2d\u9675\u3001\u5d07\u660e\u4e09\u5904\u9ebb\u7a74\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u7684\u5185\u529f\u9887\u6709\u6839\u5e95\uff0c\u8fd9\u51e0\u4e2a\u6708\u53c8\u5f97\u4e86\u91d1\u9010\u6d41\u4ee5\u6b63\u90aa\u5408\u4e00\u7684\u5185\u529f\u5fc3\u6cd5\u76f8\u8d60\uff0c\u6839\u57fa\u7262 \u56fa\u3002\u662f\u4ee5\u867d\u7136\u611f\u5230\u5bd2\u6c14\u4fb5\u80a4\uff0c\u4f46\u5374\u4e5f\u8fd8\u53ef\u4ee5\u52c9\u5f3a\u7981\u53d7\u5f97\u8d77\u3002\u5c31\u50cf\u5389\u5357\u661f\u4e00\u6837\uff0c\u5403\u4e8f\u7684\u53ea\u662f\u6c14 \u529b\u4e0d\u52a0\u3002 \u3000\u3000\u9633\u6d69\u53cc\u638c\u4ea4\u53c9\u62cd\u51fa\uff0c\u5de6\u638c\u8361\u5f00\u4e86\u516c\u5b59\u71d5\u7684\u957f\u5251\uff0c\u53f3\u638c\u4ee5\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u65bd\u5c55\u7a7a\u624b\u5165\u767d\u5203\u7684 \u529f\u592b\uff0c\u903c\u9000\u4e86\u53f2\u7ea2\u82f1\uff0c\u9661\u7684\u4e00\u4e2a\u9e33\u9e2f\u8fde\u73af\u817f\uff0c\u53c8\u89e3\u5f00\u4e86\u5389\u5357\u661f\u7684\u62db\u6570\uff0c\u4ee5\u4e00\u654c\u4e09\uff0c\u4f9d\u7136\u7a0d\u5360 \u4e0a\u98ce\u3002 \u3000\u3000\u867d\u7136\u7a0d\u5360\u4e0a\u98ce\uff0c\u6709\u4e00\u5904\u7a74\u9053\u5374\u9669\u4e9b\u7ed9\u516c\u5b59\u71d5\u523a\u7740\uff0c\u9633\u6d69\u4e5f\u7981\u4e0d\u7740\u5fc3\u5934\u4e00\u51db\uff1a\u201c\u5947\u602a\uff0c\u4f55 \u4ee5\u5979\u4eec\u90fd\u4e0d\u6015\u6211\u7684\u4fee\u7f57\u9634\u715e\u529f\u4e86\uff1f\u201d\u4ed6\u7684\u72ec\u95e8\u529f\u592b\u5931\u4e86\u529f\u6548\uff0c\u4e0d\u89c9\u6709\u70b9\u602f\u610f\u3002\u6b63\u662f\u56e0\u6b64\uff0c\u75b2 \u5175\u594b\u6218\u7684\u5389\u5357\u661f\u3001\u516c\u5b59\u71d5\u3001\u53f2\u7ea2\u82f1\uff0c\u624d\u80fd\u591f\u548c\u4ed6\u9010\u6e10\u6253\u6210\u5e73\u624b\u3002 \u3000\u3000\u4ed6\u4eec\u8fd9\u8fb9\u6253\u6210\u4e86\u5e73\u624b\uff0c\u91d1\u9010\u6d41\u90a3\u8fb9\u7684\u5f62\u52bf\u5374\u662f\u8d8a\u53d1\u5371\u9669\u4e86\u3002\u6b64\u65f6\u91d1\u9010\u6d41\u7684\u771f\u529b\u5df2\u7ecf\u7ed9\u6587 \u9053\u5e84\u6d88\u8017\u4e86\u5927\u534a\u4ee5\u4e0a\uff0c\u6587\u9053\u5e84\u7684\u771f\u529b\u5f53\u7136\u4e5f\u6709\u6d88\u8017\uff0c\u4f46\u4e0d\u5982\u91d1\u9010\u6d41\u4e4b\u751a\u3002\u4ed6\u7ec3\u7684\u662f\u6781\u9738\u9053\u7684 \u90aa\u6d3e\u5185\u529f\uff0c\u6b64\u65f6\u628a\u4e09\u8c61\u795e\u529f\u53d1\u6325\u5f97\u6dcb\u6f13\u5c3d\u81f4\uff0c\u6b65\u6b65\u7d27\u903c\uff0c\u91d1\u9010\u6d41\u7684\u7384\u94c1\u5b9d\u5251\u6e10\u6e10\u65bd\u5c55\u4e0d\u5f00\uff0c \u867d\u7136\u4ecd\u80fd\u6325\u52a8\uff0c\u62db\u6570\u4f7f\u51fa\uff0c\u5df2\u662f\u96be\u4ee5\u5f97\u5fc3\u5e94\u624b\u3002 \u3000\u3000\u6587\u9053\u5e84\u80dc\u5229\u5728\u671b\uff0c\u72c2\u6001\u6bd5\u9732\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e2d\u513f\uff0c\u4e2d\u513f\uff0c\u4e3a\u7236\u7ed9\u4f60\u62a5\u4ec7\uff0c\u5148\u6740\u91d1\u9010\u6d41\u8fd9 \u5c0f\u5b50\uff0c\u518d\u6740\u53f2\u7ea2\u82f1\u8fd9\u81ed\u4e2b\u5934\uff0c\u516c\u5b59\u5b8f\u7684\u5973\u513f\u548c\u5389\u5357\u661f\u8fd9\u5c0f\u5b50\u5f53\u7136\u6211\u4e5f\u4e0d\u80fd\u653e\u8fc7\u3002\u54c8\u54c8\uff0c\u54c8 \u54c8\uff0c\u56db\u6761\u6027\u547d\u4e3a\u4f60\u966a\u4e27\uff0c\u4f60\u4e5f\u8be5\u7791\u76ee\u4e86\u3002\u4f60\u82e5\u5acc\u4e0d\u591f\uff0c\u6211\u8fd8\u53ef\u4ee5\u628a\u5230\u5999\u5ae6\u6293\u6765\uff0c\u5728\u4f60\u575f\u524d\u711a \u5316\uff01\u201d\u795e\u60c5\u4fe8\u4f3c\u75af\u4eba\uff0c\u4f46\u90a3\u67c4\u8f6f\u5251\u7684\u529b\u9053\u5374\u662f\u4e1d\u6beb\u4e0d\u51cf\u3002 \u3000\u3000\u539f\u6765\u6587\u9053\u5e84\u6b64\u9645\u5df2\u662f\u5f00\u59cb\u611f\u5230\u80f8\u53e3\u4e00\u56e2\u706b\u70ed\uff0c\u8fd9\u662f\u8d70\u706b\u5165\u9b54\u5c06\u8981\u53d1\u4f5c\u7684\u9884\u5146\u4e86\uff01\u672c\u6765\u4ed6 \u7684\u8d70\u706b\u5165\u9b54\u662f\u5e94\u8be5\u5728\u4e09\u4e2a\u6708\u4e4b\u540e\u624d\u53d1\u4f5c\u7684\uff0c\u53ea\u56e0\u8fd9\u4e00\u573a\u6076\u6218\u7684\u89e6\u53d1\uff0c\u4f7f\u5f97\u4ed6\u96be\u4ee5\u63a7\u5236\uff0c\u4e0d\u80fd \u4e0d\u63d0\u524d\u53d1\u4f5c\u4e86\u3002\u6b64\u65f6\u6587\u9053\u5e84\u7684\u795e\u667a\u5df2\u662f\u6e10\u6e10\u6a21\u7cca\uff0c\u53ea\u6709\u4e00\u4e2a\u5ff5\u5934\u975e\u5e38\u6e05\u6f88\uff0c\u90a3\u5c31\u662f\u8981\u4e3a\u513f\u5b50 \u62a5\u4ec7\u3002\u6b63\u662f\uff1a \u3000\u3000\u7978\u798f\u65e0\u552f\u81ea\u53ec\uff0c\u65e0\u540d\u5984\u52a8\u4fbf\u906d\u6b83\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u56db\u5341\u56db\u56de\u3000\u8d70\u706b\u5165\u9b54\u96be\u81ea\u62d4\u3000\u4f20\u52a8\u8fd0\u5251\u663e\u795e\u901a \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u56db\u5341\u56db\u56de\u3000\u8d70\u706b\u5165\u9b54\u96be\u81ea\u62d4\u3000\u4f20\u52a8\u8fd0\u5251\u663e\u795e\u901a \u3000\u3000\u91d1\u9010\u6d41\u672c\u6765\u662f\u5929\u4e0d\u6015\u5730\u4e0d\u6015\u7684\uff0c\u4f46\u89c1\u6587\u9053\u5e84\u597d\u50cf\u75af\u72c2\u7684\u91ce\u517d\u4e00\u822c\uff0c\u8fde\u58f0\u72de\u7b11\uff0c\u5411\u4ed6\u731b \u6251\uff0c\u4e5f\u662f\u4e0d\u7981\u6709\u70b9\u5fc3\u60b8\u3002\u5ffd\u89c9\u5bf9\u65b9\u653b\u6765\u7684\u529b\u9053\u597d\u50cf\u6f6e\u6c34\u822c\u7684\u4ece\u56db\u9762\u516b\u65b9\u6d8c\u6765\uff0c\u91d1\u9010\u6d41\u65bd\u5c55\u5929 \u7f57\u6b65\u6cd5\uff0c\u90fd\u907f\u4e0d\u5f00\uff0c\u767b\u65f6\u5c31\u4f3c\u4e00\u53f6\u8f7b\u821f\uff0c\u5728\u60ca\u6d9b\u9a87\u6d6a\u4e4b\u4e2d\u6323\u624e\uff0c\u7981\u4e0d\u4f4f\u6447\u6447\u6643\u6643\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5927\u5403\u4e00\u60ca\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5947\u602a\uff0c\u600e\u7684\u4ed6\u7684\u6c14\u529b\u7a81\u7136\u589e\u5f3a\u4e86\u8fd9\u4e48\u591a\uff1f\u201d\u539f\u6765\u5728\u8d70\u706b \u5165\u9b54\u53d1\u4f5c\u4e4b\u524d\uff0c\u4e00\u4e2a\u6b66\u529f\u9ad8\u660e\u4e4b\u58eb\uff0c\u9677\u4e8e\u75af\u72c2\u7684\u72b6\u6001\uff0c\u8eab\u4f53\u8574\u85cf\u7684\u6f5c\u529b\u5c31\u4f1a\u5168\u90e8\u53d1\u6325\u51fa\u6765\u3002 \u4f46\u8fd9\u5374\u662f\u201c\u56de\u5149\u8fd4\u7167\u201d\u7684\u73b0\u8c61\u3002 \u3000\u3000\u51fa\u7ea2\u82f1\u773c\u5149\u4e00\u77a5\uff0c\u89c1\u91d1\u9010\u6d41\u88ab\u653b\u5f97\u597d\u50cf\u62db\u67b6\u90fd\u62db\u67b6\u4e0d\u4f4f\u4e86\uff0c\u75db\u75d2\u76f8\u5173\uff0c\u4e0d\u89c9\u5931\u58f0\u60ca\u547c\u3002 \u9ad8\u624b\u640f\u6597\uff0c\u54ea\u5bb9\u5f97\u7a0d\u6709\u5206\u795e\uff1f\u9633\u6d69\u731b\u7684\u4e00\u4e2a\u8098\u6376\u649e\u51fa\uff0c\u903c\u9000\u4e86\u6b3a\u8eab\u8fdb\u5251\u7684\u516c\u5b59\u71d5\uff0c\u500f\u5730\u5316\u4e3a \u201c\u9f99\u722a\u97e6\u201d\u6293\u4e0b\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u6495\u7834\u4e86\u53f2\u7ea2\u82f1\u7684\u4e00\u6761\u8896\u5b50\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5e76\u4e0d\u77e5\u9053\u6587\u9053\u5e84\u5df2\u662f\u201c\u56de\u5149\u8fd4\u7167\u201d\uff0c\u5fc3\u91cc\u53ea\u662f\u60f3\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u4ee4\u7ea2\u82f1\u4e3a\u6211\u62c5 \u5fc3\uff01\u201d\u5f53\u4e0b\u62b1\u5143\u5b88\u4e00\uff0c\u4f7f\u51fa\u4e86\u4e00\u5957\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u8fd9\u662f\u4e00\u5957\u9632\u5b88\u5f97\u975e\u5e38\u4e25\u5bc6\u7684\u5251\u5f0f\uff0c\u521b\u81ea\u5929\u5c71 \u6d3e\u7684\u59cb\u7956\u51cc\u672a\u98ce\uff0c\u540e\u6765\u91d1\u9010\u6d41\u7684\u7236\u4eb2\u91d1\u4e16\u9057\u5f97\u4e86\u5929\u5c71\u6d3e\u638c\u95e8\u5510\u6653\u6f9c\u6240\u4f20\uff0c\u53c8\u52a0\u4ee5\u6539\u8fdb\u7684\u3002\u4f7f \u51fa\u8fd9\u5957\u5929\u4e0b\u65e0\u53cc\u7684\u9632\u5fa1\u5251\u6cd5\uff0c\u53ef\u4ee5\u62b5\u6321\u6b66\u529f\u6bd4\u81ea\u5df1\u9ad8\u660e\u5f97\u591a\u7684\u5f3a\u624b\uff0c\u52a0\u4e0a\u91d1\u9010\u6d41\u6709\u7384\u94c1\u5b9d \u5251\uff0c\u4f7f\u4e86\u8fd9\u5957\u5251\u6cd5\uff0c\u9632\u5fa1\u7684\u529b\u91cf\u6bd4\u7528\u666e\u901a\u7684\u5200\u5251\u4f55\u6b62\u500d\u589e\uff1f\u56e0\u6b64\u4ed6\u867d\u7136\u5e76\u4e0d\u77e5\u9053\u5bf9\u65b9\u662f\u201c\u56de \u5149\u8fd4\u7167\u201d\uff0c\u4f46\u91c7\u7528\u7684\u6218\u672f\u5374\u6070\u6070\u5bf9\u4e86\u3002\u867d\u7136\u4ecd\u662f\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u5e76\u65e0\u8fd8\u624b\u4e4b\u529b\uff0c\u6587\u9053\u5e84\u7684\u5f3a \u653b\u731b\u6251\uff0c\u4e00\u65f6\u95f4\u5374\u4e5f\u96be\u5948\u4ed6\u4f55\uff0c\u5f62\u52bf\u6bd4\u521a\u624d\u7a33\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u6587\u9053\u5e84\u731b\u653b\u4e86\u6570\u5341\u62db\u4e4b\u540e\uff0c\u6e10\u6e10\u6210\u4e86\u5f3a\u5f29\u4e4b\u672b\uff0c\u91d1\u9010\u6d41\u677e\u4e86\u53e3\u6c14\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u7ea2\u82f1\uff0c \u6211\u5bf9\u4ed8\u5f97\u4e86\uff0c\u4f60\u4e0d\u5fc5\u4e3a\u6211\u62c5\u5fe7\uff01\u4f60\u4eec\u6253\u53d1\u4e86\u9633\u6d69\u8fd9\u8001\u8d3c\uff0c\u518d\u6765\u52a9\u6211\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u9633\u6d69\u4ee5\u4e00\u654c\u4e09\uff0c\u7a0d\u5360\u4e00\u70b9\u4e0a\u98ce\uff0c\u4f46\u4e45\u653b\u4e0d\u4e0b\uff0c\u4ea6\u662f\u5fc3\u6025\u3002\u672c\u6765\u4ed6\u4ee5\u4e3a\u6587\u9053\u5e84\u5f88\u5feb\u5c31\u53ef\u4ee5 \u6253\u53d1\u91d1\u9010\u6d41\u7684\uff0c\u6b64\u65f6\u4e0d\u7981\u5927\u5931\u6240\u671b\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u91d1\u9010\u6d41\u8fd9\u5c0f\u5b50\u6b66\u529f\u6781\u662f\u90aa\u95e8\uff0c\u5206\u660e\u8d25\u8c61\u6bd5 \u9732\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5374\u53c8\u7ed9\u4ed6\u7a33\u4f4f\u4e86\u3002\u4e07\u4e00\u6587\u9053\u5e84\u6253\u4e0d\u8fc7\u5730\uff0c\u8fd9\u53ef\u7cdf\u7cd5\uff01\u201d\u6b64\u65f6\u5f62 \u52bf\uff0c\u53ea\u8981\u4efb\u4f55\u4e00\u65b9\u7684\u4e00\u4e2a\u4eba\u5148\u884c\u83b7\u80dc\uff0c\u5c31\u53ef\u4ee5\u5e2e\u52a9\u540c\u4f34\uff0c\u638c\u63e1\u5168\u5c40\u3002\u9633\u6d69\u5bb3\u6015\u6587\u9053\u5e84\u514b\u5236\u4e0d \u4f4f\u91d1\u9010\u6d41\uff0c\u66f4\u662f\u8981\u6025\u4e8e\u6c42\u80dc\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u653e\u4e0b\u4e86\u5fc3\uff0c\u51dd\u795e\u5bf9\u654c\uff0c\u5389\u5357\u661f\u3001\u516c\u5b59\u71d5\u6c14\u529b\u672a\u66fe\u6062\u590d\uff0c\u4e09\u4eba\u4e4b\u4e2d\uff0c\u4ee5\u5979\u529f\u529b\u8f83 \u9ad8\u3002\u9633\u6d69\u9891\u9891\u4f7f\u51fa\u6740\u624b\uff0c\u90fd\u7ed9\u5979\u5316\u89e3\u5f00\u53bb\u3002\u516c\u5b59\u71d5\u907f\u5f00\u6b63\u9762\uff0c\u91c7\u7528\u7ed5\u8eab\u6e38\u6597\u7684\u6218\u672f\uff0c\u5251\u8d70\u8f7b \u7075\uff0c\u4e58\u80fa\u62b5\u9699\uff0c\u4e13\u88ad\u9633\u6d69\u7684\u8981\u5bb3\u7a74\u9053\uff0c\u5979\u4e0e\u5389\u5357\u661f\u4e0d\u60e7\u9633\u6d69\u7684\u4fee\u7f57\u9634\u715e\u529f\uff0c\u6562\u4e8e\u6b3a\u8eab\u8fdb\u88ad\uff0c \u4e5f\u7ed9\u4e86\u9633\u6d69\u5f88\u5927\u7684\u5a01\u80c1\u3002 \u3000\u3000\u9633\u6d69\u5f3a\u653b\u4e0d\u4e0b\uff0c\u4e0d\u7531\u5f97\u5fc3\u6025\u5982\u711a\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u89c1\u4ed6\u4eec\u6765\u5462\uff1f\u201d\u8981\u77e5 \u4ed6\u4eec\u6076\u6597\u4e86\u5df2\u5c06\u534a\u4e2a\u65f6\u8fb0\uff0c\u540e\u5c71\u7684\u5730\u9053\u51fa\u53e3\u4e4b\u5904\uff0c\u8ddd\u79bb\u524d\u5c71\u4e0d\u8fc7\u6570\u91cc\u4e4b\u9065\uff0c\u6309\u7406\u9633\u6d69\u7684\u624b\u4e0b \u5e94\u8be5\u662f\u65e9\u5df2\u542c\u5230\u58f0\u97f3\uff0c\u8d76\u6765\u7684\u4e86\u3002 \u3000\u3000\u6b8a\u4e0d\u77e5\u9633\u6d69\u56fa\u7136\u7740\u6025\uff0c\u5389\u5357\u661f\u5374\u6bd4\u4ed6\u66f4\u4e3a\u5fc3\u7126\u3002\u6b64\u9645\uff0c\u91d1\u9010\u6d41\u7684\u5f62\u52bf\u867d\u662f\u8f83\u4e3a\u597d\u8f6c\uff0c\u4f46 \u4e5f\u4e0d\u8fc7\u52c9\u5f3a\u7a33\u4f4f\u800c\u5df2\u3002\u8868\u9762\u770b\u6765\uff0c\u8fd8\u662f\u6587\u9053\u5e84\u5360\u5c3d\u4e0a\u98ce\u7684\u3002 \u3000\u3000\u5389\u5357\u661f\u5341\u5206\u73cd\u89c6\u91d1\u9010\u6d41\u5bf9\u4ed6\u7684\u53cb\u8c0a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u9010\u6d41\u8fd9\u6b21\u4e3a\u4e86\u6211\u4e0d\u60dc\u5192\u9669\u72af\u96be\uff0c\u6df1\u5165 \u864e\u7a74\uff0c\u63a2\u67e5\u771f\u76f8\u3002\u6211\u5c82\u80fd\u8ba9\u4ed6\u4f24\u5728\u6587\u9053\u5e84\u7684\u624b\u4e0b\uff1f\u201d\u6b64\u65f6\u4ed6\u7684\u6c14\u529b\u5df2\u7ecf\u6062\u590d\u4e86\u4e94\u6210\uff0c\u96c6\u5408\u4e09 \u4eba\u4e4b\u529b\uff0c\u5f53\u7136\u53ef\u4ee5\u5e2e\u5fd9\u91d1\u9010\u6d41\u53d6\u80dc\uff0c\u4f46\u5fc5\u987b\u5148\u628a\u9633\u6d69\u51fb\u8d25\u624d\u884c\u3002 \u3000\u3000\u5389\u5357\u661f\u60c5\u6025\u4e4b\u4e0b\uff0c\u4e00\u4e2a\u6b3a\u8eab\u62a2\u8fdb\uff0c\u5192\u9669\u6025\u653b\uff0c\u7ed9\u9633\u6d69\u627e\u5230\u4e86\u7834\u7efd\uff0c\u53ea\u542c\u5f97\u9633\u6d69\u4e00\u58f0\u5927 \u559d\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u6293\u7740\u4e86\u5389\u5546\u91cf\u7684\u80a9\u5934\uff01 \u3000\u3000\u516c\u5b59\u71d5\u7d27\u7d27\u8ddf\u7740\u5389\u5357\u661f\uff0c\u4e00\u89c1\u4e0d\u5999\uff0c\u53cc\u5251\u7acb\u523b\u4fbf\u523a\u8fc7\u53bb\u3002\u4e24\u65b9\u9762\u7684\u52a8\u4f5c\u90fd\u662f\u5feb\u5230\u4e86\u6781 \u70b9\uff0c\u9633\u6d69\u5143\u6687\u6293\u788e\u5389\u5357\u661f\u7684\u7435\u7436\u9aa8\uff0c\u638c\u5fc3\u52b2\u529b\u4e00\u5410\uff0c\u4fbf\u5373\u79fb\u5f62\u6362\u4f4d\uff0c\u5bf9\u4ed8\u516c\u5b59\u71d5\u7684\u5251\u62db\u3002 \u3000\u3000\u5e78\u4e8f\u9633\u6d69\u7684\u529b\u9053\u672a\u80fd\u4f7f\u8db3\uff0c\u5389\u5357\u661f\u50cf\u76ae\u7403\u4e00\u822c\u5730\u629b\u4e86\u8d77\u6765\uff0c\u5728\u534a\u7a7a\u4e2d\u7ffb\u4e86\u4e24\u4e2a\u7b4b\u6597\uff0c\u8dcc \u4e0b\u5730\u6765\uff0c\u5c45\u7136\u6ca1\u6709\u53d7\u4f24\uff0c\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\uff0c\u53c8\u8df3\u8d77\u6765\u4e86\u3002 \u3000\u3000\u5251\u5149\u4eba\u5f71\u4e4b\u4e2d\uff0c\u53ea\u542c\u5f97\u9633\u6d69\u559d\u58f0\uff1a\u201c\u6492\u5251\uff01\u201d\u516c\u5b59\u71d5\u7684\u4e24\u53e3\u9752\u94a2\u5251\u5316\u6210\u4e86\u4e24\u9053\u94f6\u8679\uff0c\u98de \u4e0a\u534a\u7a7a\u3002\u516c\u5b59\u71d5\u7684\u8eab\u5b50\u4e5f\u8ddf\u7740\u201c\u98de\u201d\u4e86\u8d77\u6765\uff0c\u5012\u7eb5\u51fa\u4e09\u4e08\u5f00\u5916\u3002\u539f\u6765\u5979\u662f\u7ed9\u9633\u6d69\u7528\u5927\u64d2\u62ff\u624b \u6cd5\u593a\u4e86\u53cc\u5251\u7684\uff0c\u597d\u5728\u5979\u7684\u8f7b\u529f\u7edd\u5353\uff0c\u5251\u4e00\u8131\u624b\uff0c\u4eba\u5373\u8dc3\u5f00\u3002 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u6c89\u58f0\u559d\u9053\uff1a\u201c\u8c01\u6562\u4f24\u5bb3\u6211\u5973\u513f\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u559d\u6709\u5982\u775b\u5929\u8d77\u4e86\u4e2a\u9739\u96f3\uff0c\u9633\u6d69\u5fc3\u5934\u5927\u9707\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u9000\u4e86\u51e0\u6b65\uff0c\u987e\u4e0d\u5f97\u518d\u5411\u53f2\u7ea2\u82f1 \u653b\u51fb\u3002\u5176\u5b9e\u90a3\u4eba\u53ea\u662f\u58f0\u5230\u4eba\u8fd8\u672a\u5230\uff0c\u9633\u6d69\u82e5\u662f\u6562\u4e8e\u4e58\u80dc\u653b\u654c\u7684\u8bdd\uff0c\u53f2\u7ea2\u82f1\u51b3\u6321\u4e0d\u4e86\u4ed6\u5168\u529b\u7684 \u4e00\u51fb\u3002\u4f46\u9633\u6d69\u4e00\u542c\uff0c\u5df2\u77e5\u6b64\u4eba\u662f\u8c01\uff0c\u53ea\u51ed\u6b64\u4eba\u7684\u58f0\u5a01\uff0c\u5df2\u662f\u8db3\u4ee5\u628a\u4ed6\u5413\u9000\u6709\u4f59\uff01 \u3000\u3000\u539f\u6765\u6b64\u4eba\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u7ea2\u7f28\u4f1a\u7684\u603b\u8235\u4e3b\u516c\u5b59\u5b8f\u3002 \u3000\u3000\u800c\u4e14\u8fd8\u4e0d\u4ec5\u662f\u516c\u5b59\u5b8f\u800c\u5df2\uff0c\u548c\u516c\u5b59\u5b8f\u4e00\u540c\u73b0\u8eab\u8fd8\u6709\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4e2a\u4eba\u7adf\u7136\u662f\u6b66\u6797\u516c\u8ba4\u7684\u5929 \u4e0b\u7b2c\u4e00\u9ad8\u624b\u6c5f\u6d77\u5929\uff01\u9633\u6d69\u62ac\u5934\u4e00\u770b\uff0c\u770b\u89c1\u4e86\u6c5f\u6d77\u5929\uff0c\u66f4\u662f\u5fc3\u60ca\u80c6\u4e27\u3002 \u3000\u3000\u516c\u5b59\u71d5\u559c\u51fa\u671b\u5916\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u5b69\u513f\u6ca1\u4e8b\uff01\u201d\u516c\u5b59\u5b8f\u6293\u7740\u5979\u7684\u53cc\u624b\uff0c\u597d\u751f\u8be7\u5f02\uff0c\u8bf4 \u9053\uff1a\u201c\u4f60\u4e00\u70b9\u90fd\u4e0d\u89c9\u5f97\u51b7\u4e48\u3002\u201d\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u9633\u6d69\u7684\u533a\u533a\u4fee\u7f57\u9634\u715e\u529f\u5c82\u80fd\u4f24\u5f97\u4e86\u6211\uff0c\u5b69\u513f \u6b63\u6253\u5f97\u53d1\u70ed\u5462\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u770b\u51fa\u5973\u513f\u679c\u7136\u6ca1\u6709\u4e2d\u6bd2\u7684\u8ff9\u8c61\uff0c\u4e0d\u7531\u5f97\u5927\u611f\u5947\u602a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4ee5\u71d5\u513f\u73b0\u5728\u7684\u5185 \u529f\u9020\u8be3\uff0c\u81f3\u5c11\u987b\u5f97\u518d\u7ec3\u5341\u5e74\uff0c\u65b9\u80fd\u62b5\u5fa1\u9633\u6d69\u7684\u4fee\u7f57\u9634\u715e\u529f\u3002\u96be\u9053\u5979\u6709\u4ec0\u4e48\u5947\u9047\u4e0d\u6210\uff1f\u8fd9\u6b64\u4e0d \u5fc5\u7ba1\u4e86\u3002\u4f46\u5979\u65e2\u6709\u62b5\u6297\u5bd2\u6bd2\u7684\u672c\u9886\uff0c\u6211\u5012\u662f\u53ef\u4ee5\u5047\u624b\u4e8e\u5979\uff0c\u53eb\u9633\u6d69\u8fd9\u53ae\u53d7\u4e2a\u5927\u5927\u7684\u6298\u8fb1 \u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u60f3\u4e0d\u51fa\u7f18\u6545\uff0c\u4fbf\u4e0d\u518d\u95ee\uff0c\u5f53\u4e0b\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u9633\u6d69\u8fd9\u70b9\u5fae\u672b\u4e4b\u6280\uff0c\u4e5f \u60f3\u62ff\u6765\u6b3a\u8d1f\u4eba\uff0c\u5f53\u771f\u662f\u592a\u4e0d\u81ea\u91cf\u4e86\u3002\u6253\u4e0b\u53bb\u4ed6\u5f53\u7136\u4e0d\u662f\u4f60\u7684\u5bf9\u624b\uff0c\u6211\u5176\u5b9e\u662f\u4e0d\u5fc5\u4e3a\u4f60\u62c5\u5fc3 \u7684\u3002\u201d \u3000\u3000\u516c\u5b59\u71d5\u63e1\u7740\u7236\u4eb2\u7684\u53cc\u624b\uff0c\u5ffd\u89c9\u638c\u5fc3\u6709\u80a1\u70ed\u529b\u4f20\u6765\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u6d41\u904d\u5168\u8eab\uff0c\u56db\u80a2\u767e\u9ab8\u65e0\u4e0d \u8212\u7545\u3002\u539f\u6765\u516c\u5b59\u5b8f\u662f\u4ee5\u672c\u8eab\u771f\u529b\uff0c\u4e3a\u5973\u513f\u6253\u901a\u5947\u7ecf\u516b\u8109\uff0c\u5e2e\u52a9\u5979\u5185\u606f\u8fd0\u884c\u3002\u8fd9\u662f\u4e00\u79cd\u6700\u4e0a\u4e58 \u7684\u5185\u529f\uff0c\u6240\u6ce8\u5165\u7684\u5185\u529b\u867d\u7136\u4e0d\u80fd\u4fdd\u6301\u957f\u4e45\uff0c\u4f46\u5728\u4e00\u4e24\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u516c\u5b59\u71d5\u7684\u5185\u529f\u5374\u662f\u8fdc\u80dc\u5e73 \u65f6\u3002 \u3000\u3000\u516c\u5b59\u71d5\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u53ea\u89c9\u6c14\u8fbe\u91cd\u5173\uff0c\u6d51\u8eab\u7cbe\u529b\u5f25\u6f2b\uff0c\u65e0\u5904\u53d1\u6cc4\u3002\u4e0d\u7981\u5927\u559c\u53eb\u9053\uff1a\u201c\u5357 \u54e5\uff0c\u5feb\u6765\uff01\u201d \u3000\u3000\u5389\u5357\u661f\u5728\u534a\u7a7a\u4e2d\u7ffb\u4e86\u4e24\u4e2a\u7b4b\u6597\uff0c\u8dcc\u4e0b\u5730\u6765\uff0c\u8bd5\u4e00\u8fd0\u6c14\uff0c\u77e5\u9053\u4e86\u81ea\u5df1\u5e76\u6ca1\u6709\u53d7\u4f24\u3002\u5598\u606f\u8fc7 \u540e\uff0c\u4fbf\u4e0a\u524d\u4e0e\u516c\u5b59\u5b8f\u76f8\u89c1\u3002 \u3000\u3000\u516c\u5b59\u5b9a\u5411\u4ed6\u4e0a\u4e0a\u4e0b\u4e0b\u6253\u91cf\u4e00\u756a\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u516c\u5b59\u71d5\u83ab\u540d\u5176\u5999\uff0c\u6485\u7740\u5c0f\u5634\u513f\u9053\uff1a\u201c\u7239\uff0c\u4f60 \u96be\u9053\u8fd8\u4e0d\u8ba4\u8bc6\u5357\u54e5\uff0c\u600e\u7684\u8fd9\u6837\u770b\u4ed6\uff1f\u201d\u516c\u5b59\u5b8f\u7b11\u9053\uff1a\u201c\u71d5\u513f\uff0c\u4f60\u4e0d\u77e5\u9053\uff0c\u7239\u7684\u786e\u662f\u4e0a\u4e86\u4e00\u6b21 \u5927\u5f53\uff0c\u628a\u4e00\u4e2a\u5192\u540d\u7684\u5929\u9b54\u6559\u6559\u4e3b\uff0c\u5f53\u4f5c\u4f60\u7684\u5357\u54e5\u4e86\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u542c\u5f97\u91d1\u9010\u6d41\u8c08\u8fc7\u6b64\u4e8b\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u9633\u6d69\u8fd9\u53ae\u6363\u7684\u9b3c\u3002\u90a3\u4e2a\u5192\u540d\u4e4b\u5f92\uff0c\u662f \u4ed6\u7684\u5e08\u4f84\uff0c\u5176\u5b9e\u4e5f\u662f\u4e2a\u53ef\u601c\u866b\uff0c\u4e00\u5207\u90fd\u5f97\u542c\u4ed6\u7684\u6446\u5e03\u7684\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u597d\uff0c\u9633\u6d69\u8fd9\u53ae\u5982\u6b64\u53ef\u6076\uff0c\u5f85\u4f1a\u513f\u4f60\u53bb\u6559\u8bad\u6559\u8bad\u4ed6\u5427\u3002\u4e0d\u5fc5\u751f\u6c14\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u4e0e\u5973\u513f\u6b22\u805a\u503e\u8c08\uff0c\u597d\u50cf\u538b\u6839\u513f\u5c31\u4e0d\u628a\u9633\u6d69\u653e\u5728\u773c\u5185\u3002\u9633\u6d69\u50f5\u5728\u4e00\u8fb9\uff0c\u65e2\u4e0d\u6562\u52a8 \u624b\uff0c\u53c8\u4e0d\u6562\u9003\u8d70\u3002\u4ed6\u6df1\u77e5\u516c\u5b59\u5b8f\u7684\u6b66\u529f\u80dc\u4ed6\u5341\u500d\uff0c\u9003\u8d70\u4e0d\u6210\uff0c\u53ea\u6015\u66f4\u53d7\u51cc\u8fb1\u3002 \u3000\u3000\u9633\u6d69\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u628a\u5fc3\u4e00\u6a2a\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u5b59\u8001\u513f\uff0c\u4f60\u6b66\u529f\u8fdc\u80dc\u4e8e\u6211\uff0c\u4f46\u4e5f\u65e0\u987b\u628a\u6211\u5982 \u6b64\u595a\u843d\u3002\u597d\uff0c\u6b7b\u5728\u4f60\u7684\u624b\u4e0a\u603b\u8fd8\u503c\u5f97\uff0c\u4f60\u8981\u5982\u4f55\uff0c\u53ea\u7ba1\u6765\u5427\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u6b64\u65f6\u5df2\u662f\u9677\u4e8e\u75af\u72c2\u72b6\u6001\uff0c\u516c\u5b59\u5b8f\u4e0e\u6c5f\u6d77\u5929\u4e8c\u4eba\u4ed6\u662f\u8ba4\u5f97\u7684\uff0c\u4f46\u5374\u4e0d\u77e5\u9053\u5bb3\u6015\u4e86\u3002 \u4ed6\u542c\u4e86\u9633\u6d69\u7684\u8bf4\u8bdd\uff0c\u5ffd\u5730\u4e5f\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff0c\u624b\u821e\u8db3\u8e48\u5730\u53eb\u9053\uff1a\u201c\u9633\u6d69\uff0c\u4f60\u8fd9\u8001\u6d51\u86cb\u4e0d\u53ef\u62a2\u6211 \u7684\u5bf9\u624b\u3002\u516c\u5b59\u5b8f\uff0c\u6c5f\u6d77\u5929\uff0c\u54c8\u54c8\u54c8\u54c8\uff0c\u4f60\u4eec\u6765\u5f97\u6b63\u597d\uff0c\u6211\u8981\u6740\u6389\u4f60\u4eec\u4e3a\u6211\u513f\u5b50\u62a5\u4ec7\uff01\u4f60\u4eec\u6240 \u6709\u5728\u573a\u7684\u4eba\uff0c\u901a\u901a\u90fd\u5f97\u65a9\u5c3d\u6740\u7edd\uff0c\u4e3a\u6211\u513f\u5b50\u62a5\u4ec7\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u624b\u821e\u8db3\u8e48\uff0c\u770b\u4f3c\u4e0d\u6210\u7ae0\u6cd5\uff0c\u4f46\u4e3e\u624b\u6295\u8db3\uff0c\u968f\u610f\u6240\u4e4b\uff0c\u5374\u53c8\u90fd\u662f\u6781\u5389\u5bb3\u7684\u6740\u624b\u3002\u6c5f \u6d77\u5929\u770b\u4e86\uff0c\u597d\u751f\u9a87\u5f02\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5e08\u5f1f\u7684\u62db\u6570\uff0c\u786e\u5b9e\u662f\u6bd4\u6211\u9ad8\u660e\u5f97\u591a\u3002\u5047\u5982\u6211\u4e0d\u51ed\u529f\u529b\u53d6 \u80dc\u7684\u8bdd\uff0c\u53ea\u6015\u8fd8\u5f53\u771f\u5bf9\u4ed8\u4e0d\u4e86\u8fd9\u6837\u75af\u72c2\u7684\u6253\u6cd5\u5462\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u770b\u89c1\u5e08\u5144\u6765\u5230\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u6587\u9053\u5e84\u4e0d\u4f9d\u7ae0\u6cd5\uff0c\u4ed6\u4e5f\u81ea\u521b\u65b0\u62db\uff0c\u6587\u9053\u5e84\u8bb8\u591a\u7a00\u5947\u53e4 \u602a\u7684\u653b\u6cd5\uff0c\u90fd\u7ed9\u4ed6\u968f\u610f\u5316\u89e3\u3002\u4e0d\u8fc7\uff0c\u7531\u4e8e\u6587\u9053\u5e84\u5728\u8d70\u706b\u5165\u9b54\u4e4b\u524d\uff0c\u6c14\u529b\u7279\u522b\u6765\u5f97\u5927\uff0c\u91d1\u9010\u6d41 \u4ecd\u662f\u4e0d\u514d\u8981\u5c48\u5c45\u4e0b\u98ce\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u63e1\u4e86\u5389\u5357\u661f\u53cc\u624b\uff0c\u4f9d\u6cd5\u65bd\u4e3a\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u5389\u5357\u661f\u672c\u6765\u662f\u82cd\u767d\u5982\u7eb8\u7684\u9762\u4e0a\uff0c\u73b0\u51fa\u4e00 \u7247\u7ea2\u5149\u3002\u6b64\u65f6\u521a\u597d\u662f\u9633\u6d69\u5f00\u58f0\uff0c\u5411\u4ed6\u6311\u6218\u7684\u65f6\u5019\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u653e\u4e0b\u53cc\u624b\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u4e5f\u914d\u548c\u6211\u4ea4\u624b\u3002\u71d5\u513f\uff0c\u4f60\u548c\u5389\u5927\u54e5\u66ff\u6211 \u5e9f\u6389\u4ed6\u7684\u6b66\u529f\u5427\uff01\u201d\u516c\u5b59\u71d5\u3001\u5389\u5357\u661f\u9f50\u58f0\u5e94\u4e86\u4e00\u4e2a\u201c\u662f\u201d\u5b57\uff0c\u53cc\u53cc\u8dc3\u51fa\u3002 \u3000\u3000\u9633\u6d69\u5fc3\u91cc\u6697\u559c\uff0c\u5374\u4f6f\u4f5c\u6012\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u5b59\u8001\u513f\uff0c\u4f60\u7adf\u7136\u81ea\u6b64\u5c0f\u89d1\u6211\uff01\u8981\u662f\u6211\u5931\u624b\u6253\u4f24 \u4e86\u4f60\u7684\u5973\u513f\uff0c\u5973\u5a7f\uff0c\u4f60\u53ef\u6028\u4e0d\u5f97\u6211\uff01\u201d \u3000\u3000\u516c\u5b59\u5b8f\u7b11\u9053\uff1a\u201c\u8c05\u4f60\u8fd9\u70b9\u672c\u9886\u4e5f\u4f24\u4e0d\u4e86\u4ed6\u4eec\uff0c\u6709\u4ec0\u4e48\u4fee\u4e3a\uff0c\u5c3d\u7ba1\u65bd\u5c55\u4fbf\u662f\u3002\u5018\u82e5\u4f60\u5728\u4ed6 \u4eec\u7684\u624b\u4e0b\u80fd\u591f\u9003\u751f\uff0c\u4eca\u540e\u6211\u4e5f\u4e0d\u4f1a\u518d\u627e\u4f60\u7684\u6666\u6c14\u4e86\u3002\u201d \u3000\u3000\u9633\u6d69\u628a\u5389\u5357\u661f\u79f0\u4f5c\u516c\u5b59\u5b8f\u7684\u5973\u5a7f\uff0c\u516c\u5b59\u5b8f\u5e76\u4e0d\u5426\u8ba4\uff0c\u7b49\u4e8e\u662f\u9ed8\u8bb8\u4e86\u4ed6\u4eec\u7684\u5a5a\u4e8b\u3002\u516c\u5b59\u71d5 \u7c89\u9762\u5a07\u7ea2\uff0c\u82b3\u5fc3\u6697\u559c\u3002 \u3000\u3000\u9633\u6d69\u542c\u5f97\u516c\u5b59\u5b8f\u7b54\u5e94\u7edd\u4e0d\u51fa\u624b\uff0c\u4e5f\u662f\u5fc3\u4e2d\u5927\u559c\uff0c\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00 \u97ad\uff01\u516c\u5b59\u59d1\u5a18\uff0c\u4f60\u5c31\u548c\u5389\u516c\u5b50\u4e0a\u5427\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u4e0d\u77e5\u4ed6\u4eec\u5df2\u5f97\u516c\u5b59\u5b8f\u4e4b\u52a9\uff0c\u4e1a\u5df2\u6062\u590d\u4e86\u529f\u529b\uff0c\u6b63\u81ea\u8e0c\u8e87\uff0c\u597d\u4e0d\u597d\u4e0a\u53bb\u548c\u4ed6\u4eec\u4e8c\u4eba \u8054\u624b\uff1f\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u53f2\u59d0\u59d0\uff0c\u4f60\u548c\u91d1\u5927\u54e5\u6253\u6587\u9053\u5e84\uff0c\u54b1\u4eec\u6bd4\u4e00\u6bd4\u770b\u8c01\u5148\u53d6\u80dc\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u89c1\u5979\u8bf4\u5f97\u751a\u6709\u628a\u63e1\uff0c\u5fc3\u60f3\uff1a\u201c\u4e00\u5b9a\u662f\u4ed6\u4eec\u5df2\u6709\u7a33\u64cd\u80dc\u5238\u7684\u65b9\u6cd5\uff0c\u5426\u5219\u516c\u5b59\u8235\u4e3b\u7edd \u4e0d\u4f1a\u5938\u4e0b\u6d77\u53e3\u3002\u5979\u548c\u5389\u5927\u54e5\u662f\u4e00\u5bf9\uff0c\u65e2\u7136\u516c\u5b59\u8235\u4e3b\u8fd9\u6837\u5b89\u6392\uff0c\u6211\u5012\u662f\u4e0d\u4fbf\u4e0e\u4ed6\u4eec\u8054\u624b\u4e86\u3002\u201d \u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u6211\u53ea\u80fd\u5c3d\u529b\u800c\u4e3a\uff0c\u8981\u6bd4\u662f\u4e00\u5b9a\u6bd4\u4e0d\u8fc7\u4f60\u4eec\u7684\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u602a\u7b11\u9053\uff1a\u201c\u5c0f\u5996\u7cbe\uff0c\u4f60\u4e5f\u6765\u4e86\uff01\u4f60\u6709\u80c6\u5bb3\u6b7b\u4f60\u7684\u54e5\u54e5\uff0c\u5c31\u5e94\u8be5\u6709\u80c6\u91cf\u966a\u6211\u7684\u513f \u5b50\u3002\u563f\u563f\uff0c\u53f2\u767d\u90fd\u5440\u53f2\u767d\u90fd\uff0c\u6211\u7ed9\u4f60\u62a5\u4ec7\uff0c\u8bf7\u4f60\u5728\u6cc9\u4e0b\u7ed9\u6211\u7684\u513f\u5b50\u4e3b\u5a5a\uff0c\u6211\u7684\u513f\u5b50\u4e0d\u8981\u5c01\u5999 \u5ae6\u4e86\uff0c\u8981\u4f60\u8fd9\u59b9\u59b9\u5c0f\u5996\u7cbe\uff01\u201d\u53f2\u7ea2\u82f1\u65a5\u9053\uff1a\u201c\u75af\u5b50\uff0c\u522b\u80e1\u8bf4\u516b\u9053\uff01\u770b\u5251\uff01\u201d\u4e00\u62db\u201c\u7389\u5973\u6295 \u68ad\u201d\uff0c\u5251\u5c16\u4e0a\u78a7\u83b9\u83b9\u7684\u5149\u8292\u6307\u5230\u4e86\u6587\u9053\u5e84\u7684\u540e\u5fc3\uff01 \u3000\u3000\u6587\u9053\u5e84\u53cd\u624b\u4e00\u638c\uff0c\u80cc\u540e\u5c31\u50cf\u957f\u7740\u773c\u775b\u4e00\u822c\uff0c\u8361\u5f00\u4e86\u53f2\u7ea2\u82f1\u7684\u5251\u5c16\uff0c\u4e09\u6307\u4fbf\u6263\u5979\u7684\u8109\u95e8\u3002 \u7adf\u662f\u4e00\u62db\u7a7a\u624b\u5165\u767d\u5203\u7684\u5927\u64d2\u62ff\u624b\u6cd5\u3002 \u3000\u3000\u91d1\u9010\u6d41\u559d\u9053\uff1a\u201c\u4f11\u5f97\u901e\u51f6\uff01\u201d\u7384\u94c1\u5b9d\u5251\u5f53\u5934\u5288\u4e0b\uff01\u6587\u9053\u5e84\u867d\u7136\u795e\u667a\u4e0d\u6e05\uff0c\u4f46\u5e94\u4ed8\u5f3a\u654c\u5374 \u662f\u6beb\u4e0d\u542b\u7cca\uff0c\u53cd\u5e94\u6781\u4e3a\u7075\u654f\uff0c\u4e00\u4e2a\u4fa7\u8eab\u9519\u6b65\uff0c\u9ed1\u7389\u8f6f\u5251\u53cd\u5f39\u524a\u51fa\uff0c\u67b6\u4f4f\u4e86\u91d1\u9010\u6d41\u7684\u7384\u94c1\u5b9d \u5251\uff0c\u5251\u950b\u4e00\u6296\uff0c\u5149\u8292\u7535\u5c04\uff0c\u5c45\u7136\u53c8\u662f\u4e00\u62db\u975e\u5e38\u51cc\u5389\u7684\u5251\u6cd5\uff0c\u523a\u5411\u91d1\u9010\u6d41\u80c1\u90e8\u7684\u6108\u6c14\u7a74\u3002 \u3000\u3000\u6b64\u65f6\u6587\u9053\u5e84\u7684\u8d70\u706b\u5165\u9b54\u5df2\u662f\u5feb\u5c06\u53d1\u4f5c\uff0c\u5728\u6b64\u6d88\u5f7c\u957f\u7684\u60c5\u5f62\u4e4b\u4e0b\uff0c\u53cc\u65b9\u529f\u529b\u5df2\u662f\u76f8\u5dee\u4e0d \u8fdc\u3002\u6587\u9053\u5e84\u67b6\u4f4f\u4e86\u7384\u94c1\u5b9d\u5251\u867d\u7136\u8fd8\u662f\u80fd\u591f\u53cd\u653b\uff0c\u4f46\u6253\u5411\u53f2\u7ea2\u82f1\u90a3\u4e00\u638c\u5df2\u662f\u6c14\u529b\u4e0d\u8db3\u4e86\u3002\u53f2\u7ea2 \u82f1\u624b\u80cc\u4e00\u6325\uff0c\u5316\u89e3\u4e86\u4ed6\u7684\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u559d\u58f0\uff1a\u201c\u7740\uff01\u201d\u7acb\u5373\u4fbf\u662f\u4e00\u62db\u201c\u91d1\u9488\u5ea6\u52ab\u201d\uff0c\u523a\u5230\u4ed6 \u7684\u4e39\u7530\u3002 \u3000\u3000\u6587\u9053\u5e84\u53eb\u9053\uff1a\u201c\u4e56\u4e56\u4e0d\u5f97\u4e86\uff0c\u4e2d\u51e0\uff0c\u4e2d\u513f\uff0c\u4f60\u7684\u5ab3\u5987\u513f\u5a36\u4e0d\u6210\u5566\uff01\u201d\u811a\u6b65\u8e09\u8dc4\uff0c\u5b9b\u5982\u9189 \u6c49\uff0c\u4f46\u5374\u6070\u597d\u907f\u5f00\u4e86\u53f2\u7ea2\u82f1\u7684\u8fd9\u62db\u6740\u624b\u3002\u53f2\u7ea2\u82f1\u6ee1\u4ee5\u4e3a\u8fd9\u4e00\u5251\u53ef\u4ee5\u81f4\u4ed6\u6b7b\u547d\u7684\uff0c\u4e0d\u6599\u7adf\u7ed9\u4ed6 \u53e4\u91cc\u53e4\u602a\u7684\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\u95ea\u5f00\uff0c\u5fc3\u91cc\u4e5f\u662f\u4e0d\u7981\u9a87\u7136\u3002\u4e0d\u8fc7\u6587\u9053\u5e84\u5728\u80cc\u8179\u53d7\u654c\u4e4b\u4e0b\uff0c\u523a\u5411\u91d1 \u9010\u6d41\u7684\u4e00\u5251\u5f53\u7136\u4e5f\u662f\u843d\u4e86\u7a7a\u4e86\u3002 \u3000\u3000\u53e6\u4e00\u8fb9\uff0c\u5389\u5357\u661f\u4e0e\u516c\u5b59\u71d5\u5e76\u80a9\u800c\u4e0a\uff0c\u4ea6\u5df2\u548c\u9633\u6d69\u5f00\u59cb\u4ea4\u950b\u3002 \u3000\u3000\u9633\u6d69\u4e0d\u77e5\u4ed6\u4eec\u7684\u529f\u529b\u975e\u4f46\u5df2\u7ecf\u6062\u590d\uff0c\u800c\u4e14\u91cd\u80dc\u4ece\u524d\uff0c\u6697\u81ea\u601d\u91cf\uff1a\u201c\u6211\u82e5\u91cd\u4f24\u4e86\u8fd9\u4e2a\u4e2b \u5934\uff0c\u516c\u5b59\u71d5\u8fd9\u8001\u513f\u867d\u662f\u6709\u8bdd\u5728\u5148\uff0c\u53ea\u6015\u4e5f\u662f\u4e0d\u4f1a\u653e\u8fc7\u6211\u7684\u3002\u5973\u5a7f\u603b\u662f\u9694\u4e00\u5c42\uff0c\u5bf9\uff0c\u6211\u53eb\u5389\u5357 \u661f\u8fd9\u5c0f\u5b50\u53d7\u70b9\u8f7b\u4f24\u4e5f\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u54ea\u77e5\u5389\u5357\u661f\u4e00\u5251\u523a\u6765\uff0c\u52b2\u529b\u7adf\u662f\u5927\u5f97\u51fa\u5947\u3002\u9633\u6d69\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u4e00\u89c9\u4e0d \u5bf9\uff0c\u4e0d\u89c9\u5927\u5403\u4e00\u60ca\uff01\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u516c\u5b59\u71d5\u5df2\u662f\u5530\u5530\u5530\u8fde\u73af\u4e09\u5251\uff0c\u523a\u54bd\u5589\u3001\u524a\u5de6\u80a9\u3001\u6302\u4e24 \u80c1\uff0c\u6740\u5f97\u9633\u6d69\u624b\u5fd9\u811a\u4e71\u3002\u5389\u5357\u661f\u6b3a\u8eab\u8fdb\u5251\uff0c\u9633\u6d69\u6a2a\u638c\u4e00\u5c01\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5de6\u80a9\u5df2\u662f \u7ed9\u516c\u5b59\u71d5\u524a\u53bb\u4e86\u4e00\u7247\u76ae\u8089\uff0c\u539f\u6765\u4ed6\u4ee5\u516b\u6210\u7684\u638c\u529b\u8361\u5f00\u5389\u5357\u661f\u7684\u957f\u5251\uff0c\u5df2\u662f\u65e0\u529b\u517c\u987e\u516c\u5b59\u71d5\u90a3 \u5947\u8be1\u8fc5\u6377\u7684\u5251\u62db\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fde\u6211\u7684\u5973\u513f\u4f60\u90fd\u6253\u4e0d\u8fc7\uff0c\u8fd8\u60f3\u4e0e\u6211\u52a8\u624b\u4e48\u3002\u201d\u9633\u6d69\u5fcd\u7740\u75bc\u75db\uff0c\u8bf4 \u9053\uff1a\u201c\u516c\u5b59\u8001\u513f\uff0c\u4f60\u77e5\u9053\u6211\u662f\u770b\u5728\u4f60\u7684\u4efd\u4e0a\uff0c\u2026\u2026\u201d\u516c\u5b59\u71d5\u51b7\u7b11\u9053\uff1a\u201c\u8c01\u8981\u4f60\u8ba8\u597d\u6211\u7684\u7239 \u7239\uff0c\u4f60\u6709\u591a\u5927\u672c\u9886\uff0c\u5c3d\u7ba1\u732e\u4e11\u5427\uff01\u53ea\u8981\u4f60\u5728\u6211\u7684\u5251\u4e0b\u80fd\u591f\u9003\u751f\uff0c\u7239\u8bf4\u8fc7\u7684\u8bdd\u5c82\u6709\u4e0d\u7b97\u6570 \u7684\uff1f\u201d \u3000\u3000\u9633\u6d69\u6b64\u65f6\u5df2\u662f\u77e5\u9053\u96be\u4ee5\u53d6\u80dc\uff0c\u4f46\u5fc3\u60f3\u8981\u9003\u751f\u603b\u8fd8\u80fd\u591f\uff0c\u5f53\u4e0b\u559d\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u4eec\u7236\u5973\u4eb2\u53e3 \u8bf4\u7684\uff0c\u53ef\u522b\u53cd\u6094\u4e86\uff01\u201d\u53cc\u638c\u9f50\u51fa\uff0c\u7a81\u7136\u4f7f\u51fa\u4e86\u7b2c\u516b\u91cd\u7684\u4fee\u7f57\u9634\u715e\u529f\uff0c\u5168\u529b\u5411\u516c\u5b59\u71d5\u653b\u53bb\uff01 \u3000\u3000\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u54c8\uff01\u72d7\u6025\u8df3\u5899\u4e86\uff01\u201d\u5389\u5357\u661f\u9053\uff1a\u201c\u5bf9\u4ed8\u75af\u62d8\uff0c\u53ea\u6709\u6253\u4e4b\uff01\u201d\u8fd0\u5251\u5982\u98ce\uff0c \u4e00\u62db\u4e4b\u5185\uff0c\u8fde\u523a\u9633\u6d69\u7684\u4e03\u5904\u5927\u7a74\u3002\u516c\u5b59\u71d5\u5530\u5730\u4e00\u5251\uff0c\u4ece\u4ed6\u610f\u60f3\u4e0d\u5230\u7684\u65b9\u4f4d\u523a\u6765\u3002\u4e09\u65b9\u9762\u52a8\u4f5c \u90fd\u5feb\uff0c\u9633\u6d69\u7684\u638c\u529b\u672a\u66fe\u5c3d\u53d1\uff0c\u5de6\u80a9\u53c8\u7740\u4e00\u5251\u3002\u4f46\u516c\u5b59\u71d5\u5374\u4e5f\u7ed9\u4ed6\u7684\u638c\u529b\u9707\u5f97\u8fde\u9000\u51e0\u6b65\uff0c\u65b9\u80fd \u7a33\u4f4f\u8eab\u5f62\u3002\u8981\u77e5\u516c\u5b59\u71d5\u867d\u7136\u662f\u4e0d\u60e7\u4ed6\u7684\u4fee\u7f57\u9634\u715e\u529f\uff0c\u4f46\u53c8\u5f97\u4e86\u7236\u4eb2\u7684\u771f\u529b\u4e4b\u52a9\uff0c\u4f46\u672c\u8eab\u529f\u529b \u7a76\u7adf\u8fd8\u662f\u7a0d\u900a\u4e00\u7b79\u3002 \u3000\u3000\u9633\u6d69\u63a5\u8fde\u4e2d\u4e86\u4e24\u5251\uff0c\u5e78\u4e8f\u90fd\u53ea\u662f\u76ae\u8089\u4e4b\u4f24\uff0c\u4f46\u4e5f\u75db\u5f97\u96be\u53d7\u3002\u5f53\u4e0b\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u62fc\u547d\u53cd \u6251\u3002 \u3000\u3000\u5389\u5357\u661f\u7b11\u9053\uff1a\u201c\u4e0d\u80fd\u8ba9\u75af\u72d7\u8dd1\u6389\uff0c\u53c8\u53bb\u54ac\u4eba\u3002\u201d\u516c\u5b59\u71d5\u9053\uff1a\u201c\u5b83\u8dd1\u4e0d\u6389\u7684\uff01\u201d\u5979\u548c\u5389\u5357 \u661f\u5728\u6843\u82b1\u8c37\u76f8\u5904\u90a3\u51e0\u5929\uff0c\u76f8\u4e92\u5207\u78cb\uff0c\u65e9\u5df2\u7ec3\u597d\u4e86\u4e00\u5957\u5e76\u80a9\u5bf9\u654c\u7684\u5251\u6cd5\uff0c\u521a\u624d\u56e0\u4e3a\u6c14\u529b\u4e0d\u6d4e\uff0c \u96be\u4ee5\u914d\u5408\uff0c\u6b64\u9645\u6b63\u597d\u62ff\u9633\u6d69\u6765\u4e00\u8bd5\u8eab\u624b\u3002 \u3000\u3000\u5389\u5357\u661f\u7684\u529f\u529b\u8f83\u9ad8\uff0c\u4ece\u6b63\u9762\u5fa1\u654c\uff0c\u5316\u89e3\u9633\u6d69\u7684\u653b\u52bf\uff0c\u516c\u5b59\u71d5\u8eab\u6cd5\u8f7b\u7075\uff0c\u5251\u5982\u98de\u51e4\uff0c\u4fa7\u88ad \u654c\u4eba\u3002\u4e24\u4eba\u867d\u662f\u7b2c\u4e00\u6b21\u5e76\u80a9\u5fa1\u654c\uff0c\u62ff\u9633\u6d69\u8bd5\u62db\uff0c\u4f46\u4e00\u653b\u4e00\u5b88\uff0c\u5982\u4e5f\u914d\u5408\u788d\u65e0\u61c8\u53ef\u51fb\u3002\u516c\u5b59\u5b8f \u770b\u5f97\u5fc3\u82b1\u6012\u653e\uff0c\u4e0d\u4f4f\u7684\u634b\u987b\u5fae\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u5f97\u6b64\u4f73\u5a7f\uff0c\u592b\u590d\u4f55\u6c42\uff01\u201d \u3000\u3000\u4e0d\u8fc7\u4e00\u4f1a\uff0c\u53ea\u89c1\u53cc\u5251\u76d8\u65cb\u98de\u821e\uff0c\u7ec7\u6210\u4e86\u4e00\u9053\u5149\u7f51\uff0c\u628a\u9633\u6d69\u7f69\u5728\u5149\u7f51\u4e4b\u4e0b\u3002\u9633\u6d69\u4e25\u4f3c\u53d7\u4f24 \u7684\u91ce\u517d\uff0c\u5de6\u51b2\u53f3\u7a81\uff0c\u603b\u662f\u51b2\u4e0d\u51fa\u53bb\u3002\u6b64\u65f6\u65b9\u59cb\u77e5\u9053\uff0c\u679c\u7136\u662f\u8fde\u9003\u751f\u4e5f\u4e0d\u80fd\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u770b\u5f97\u4e5f\u662f\u6709\u70b9\u8be7\u5f02\uff0c\u7b11\u9053\uff1a\u201c\u516c\u5b59\u524d\u8f88\uff0c\u606d\u559c\u4f60\u8c03\u6559\u51fa\u8fd9\u6837\u4e00\u4f4d\u672c\u9886\u9ad8\u5f3a\u7684\u5973 \u513f\u3002\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5c45\u7136\u80fd\u591f\u7834\u89e3\u7b2c\u516b\u91cd\u7684\u4fee\u7f57\u9634\u715e\u529f\uff01\u5f53\u771f\u662f\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\uff0c\u4e00\u4ee3\u80dc\u4e8e\u4e00 \u4ee3\u3002\u53ef\u559c\u53ef\u8d3a\uff01\u53ef\u559c\u53ef\u8d3a\uff01\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u8fc7\u5956\u4e86\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u5979\u8fd9\u62b5\u5fa1\u5bd2\u6bd2\u7684\u529f\u592b\uff0c\u5e76\u4e0d\u662f\u6211\u6240\u6559\u7684\u3002\u6211\u8fd8 \u4ee5\u4e3a\u662f\u5389\u5c11\u4fa0\u4ee3\u4ee4\u5e08\u4f20\u6388\u7684\u5462\u3002\u8c08\u5230\u7834\u89e3\u4fee\u7f57\u9634\u715e\u529f\uff0c\u5f53\u4eca\u4e4b\u4e16\u53ea\u6709\u4ee4\u5e08\u80fd\u591f\u3002\u60f3\u5f53\u5e74\u4ee4\u5e08 \u5728\u5d69\u5c71\u5343\u969c\u5c4f\u5927\u8d25\u5b5f\u795e\u901a\uff0c\u90a3\u624d\u662f\u771f\u6b63\u7834\u89e3\u4e86\u7b2c\u4e5d\u91cd\u7684\u4fee\u7f57\u9634\u715e\u529f\u5462\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6014\u4e86\u4e00\u6014\uff0c\u6709\u70b9\u89c9\u5f97\u5947\u602a\uff0c\u5fc3\u60f3\uff1a\u201c\u4f55\u4ee5\u4ed6\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u7adf\u6709\u5174\u81f4\u8c08\u8bba\u6b66\u6797\u65e7 \u4e8b\uff1f\u201d\u60f3\u4e86\u4e00\u60f3\uff0c\u968f\u5373\u604d\u7136\u5927\u609f\uff1a\u201c\u539f\u6765\u4ed6\u662f\u501f\u8fd9\u673a\u4f1a\uff0c\u5e0c\u671b\u6211\u63d0\u9192\u5389\u5357\u661f\u5982\u4f55\u7834\u89e3\u9633\u6d69\u7684 \u4fee\u7f57\u9634\u715e\u529f\u3002\u4ed6\u662f\u8bf4\u8fc7\u8981\u5e9f\u6389\u9633\u6d69\u7684\u529f\u592b\u7684\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u5bb6\u5e08\u66fe\u7ecf\u8a00\u9053\uff0c\u53ea\u8981\u5185\u529f\u7ec3\u5230 \u4e0d\u60e7\u5bd2\u6bd2\uff0c\u8981\u7834\u4fee\u7f57\u9634\u715e\u529f\uff0c\u90a3\u5c31\u662f\u6613\u4e8e\u53cd\u638c\u4e86\u3002\u4eca\u5a9b\u5982\u4eca\u5df2\u662f\u4e0d\u60e7\u5bd2\u6bd2\uff0c\u8c05\u9633\u6d69\u8fd9\u53ae\u9003\u4e0d \u51fa\u5979\u7684\u638c\u5fc3\uff01\u201d\u6c5f\u6d77\u5929\u8bf4\u8bdd\u4e4b\u4e2d\uff0c\u63a5\u8fde\u63d0\u4e86\u4e24\u6b21\u201c\u638c\u201d\u5b57\uff0c\u5389\u5357\u661f\u542c\u4e86\uff0c\u767b\u65f6\u5fc3\u9886\u795e\u4f1a\u3002 \u3000\u3000\u539f\u6765\u51e1\u662f\u4fee\u7f57\u9634\u715e\u529f\u7ec3\u5230\u7b2c\u516d\u8463\u4ee5\u4e0a\u7684\u4eba\uff0c\u9634\u5bd2\u4e4b\u6c14\u5fc5\u5b9a\u662f\u51dd\u805a\u638c\u5fc3\uff0c\u53ea\u8981\u5c06\u4ed6\u638c\u5fc3\u7684 \u201c\u52b3\u5bab\u7a74\u201d\u523a\u7a7f\uff0c\u5c31\u80fd\u5c06\u4ed6\u7684\u6b66\u529f\u5e9f\u6389\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e0d\u4fbf\u660e\u8a00\uff0c\u90a3\u662f\u56e0\u4e3a\u8981\u8ba9\u5c0f\u8f88\u6210\u540d\u7684\u7f18\u6545\u3002\u540c\u65f6\u4ed6\u4ee5\u6b66\u6797\u5b97\u4e3b\u7684\u8eab\u4efd\uff0c\u82e5\u662f\u516c\u7136 \u6307\u70b9\u5236\u654c\u7684\u8bc0\u7a8d\uff0c\u4e5f\u662f\u6709\u5931\u8eab\u4efd\u3002\u662f\u4ee5\u4ed6\u53ea\u662f\u53cd\u590d\u7684\u7528\u4e86\u4e24\u4e2a\u201c\u638c\u201d\u5b57\uff0c\u5e0c\u671b\u5389\u5357\u661f\u81ea\u5df1\u9886 \u609f\u3002\u8868\u9762\u4e0a\u4ed6\u662f\u5728\u5938\u8d5e\u516c\u5b59\u71d5\uff0c\u5176\u5b9e\u662f\u8bf4\u7ed9\u5389\u5357\u661f\u542c\u7684\u3002\u4e0d\u8fc7\uff0c\u4ed6\u8fd9\u6837\u63d0\u9192\u5389\u5357\u661f\uff0c\u5374\u4e5f\u7b49 \u4e8e\u662f\u6551\u4e86\u9633\u6d69\u4e00\u6761\u6027\u547d\uff0c\u56e0\u4e3a\u5389\u5357\u661f\u5e9f\u6389\u4ed6\u7684\u6b66\u529f\uff0c\u5c31\u7528\u4e0d\u7740\u6740\u4ed6\u4e86\uff0c\u9633\u6d69\u5018\u82e5\u77e5\u9053\u6c5f\u6d77\u5929 \u7684\u7528\u610f\uff0c\u90a3\u8fd8\u662f\u5e94\u8be5\u611f\u6fc0\u4ed6\u7684\u3002 \u3000\u3000\u5389\u5357\u661f\u5c0f\u65f6\u5019\u4e5f\u66fe\u542c\u8fc7\u91d1\u4e16\u9057\u6253\u8d25\u5b5f\u795e\u901a\u7684\u6545\u4e8b\uff0c\u4e0d\u8fc7\u5f53\u65f6\u6ca1\u6709\u600e\u6837\u7559\u5fc3\uff0c\u6b64\u65f6\u5f97\u6c5f\u6d77 \u5929\u63d0\u9192\uff0c\u767b\u65f6\u5fc3\u9886\u795e\u4f1a\u3002\u6697\u81ea\u60f3\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u4f20\u6211\u6b63\u90aa\u5408\u4e00\u7684\u5185\u529f\u5fc3\u6cd5\u4e4b\u65f6\uff0c\u66fe\u8bf4\u8fc7\u90aa\u6d3e\u9ad8 \u624b\u7684\u547d\u95e8\u8981\u7a74\uff0c\u4e0d\u5916\u4e09\u5904\uff1a\u4e00\u662f\u4e39\u7530\uff1b\u4e00\u662f\u4e0b\u9634\u7684\u5f52\u85cf\u7a74\uff1b\u4e00\u662f\u638c\u5fc3\u7684\u52b3\u5bab\u7a74\u3002\u91d1\u5927\u4fa0\u5f53\u5e74 \u5982\u4f55\u7834\u89e3\u5b5f\u795e\u901a\u7684\u4fee\u7f57\u9634\u715e\u529f\uff0c\u6211\u867d\u7136\u662f\u77e5\u800c\u4e0d\u8be6\uff0c\u4f46\u5982\u4eca\u6c5f\u5927\u4fa0\u4e00\u518d\u8bf4\u8d77\u4e00\u4e2a\u2018\u638c\u2019\u5b57\uff0c \u8bf4\u5230\u8fd9\u4e2a\u5b57\u65f6\uff0c\u58f0\u97f3\u4e5f\u7279\u522b\u5927\u4e9b\u3002\u60f3\u5fc5\u662f\u6559\u6211\u523a\u7a7f\u4ed6\u638c\u5fc3\u7684\u52b3\u5bab\u7a74\u4e86\u3002\u201d\u5f53\u4e0b\u6b3a\u8eab\u8fdb\u5251\uff0c\u5411 \u9633\u6d69\u638c\u5fc3\u523a\u53bb\uff0c\u9633\u6d69\u679c\u7136\u795e\u8272\u60ca\u614c\uff0c\u8fde\u5fd9\u7f29\u638c\u3002 \u3000\u3000\u9633\u6d69\u7684\u6b66\u529f\u975e\u540c\u6cdb\u6cdb\uff0c\u5389\u5357\u661f\u60f3\u4e00\u4e0b\u5b50\u5c31\u523a\u7740\u4ed6\u7684\u638c\u5fc3\u201c\u52b3\u5bab\u7a74\u201d\uff0c\u5f53\u7136\u4e5f\u662f\u5e76\u4e0d\u5bb9 \u6613\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u4e00\u770b\u5389\u5357\u661f\u5982\u6b64\u51fa\u5251\uff0c\u5c31\u77e5\u4ed6\u5df2\u9886\u609f\uff0c\u4e8e\u662f\u4e0d\u518d\u7406\u4f1a\u8fd9\u8fb9\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u770b\u91d1\u3001\u53f2 \u4e8c\u4eba\u548c\u6587\u9053\u5e84\u7684\u6076\u6597\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7684\u529f\u529b\u4e0e\u6587\u9053\u5e84\u672c\u6765\u76f8\u5dee\u4e0d\u8fdc\uff0c\u5f97\u4e86\u53f2\u7ea2\u82f1\u4e4b\u52a9\uff0c\u5927\u5360\u4e0a\u98ce\u3002 \u3000\u3000\u6587\u9053\u5e84\u201c\u5475\u5475\u201d\u602a\u53eb\uff0c\u5ffd\u7136\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u795e\u6001\u66f4\u662f\u75af\u72c2\uff01\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u53eb \u9053\uff1a\u201c\u5e08\u5f1f\u5c0f\u5fc3\u4e86\uff01\u201d \u3000\u3000\u516c\u5b59\u5b8f\u7b11\u9053\uff1a\u201c\u8fd9\u53ae\u867d\u662f\u75af\u72c2\uff0c\u4f46\u4ea6\u5df2\u662f\u5f3a\u5f29\u4e4b\u672a\uff0c\u5982\u4eca\u53c8\u53d7\u4e86\u5185\u4f24\uff0c\u4f9d\u6211\u770b\u6765\uff0c\u4ed6\u6050 \u6015\u662f\u547d\u4e0d\u4e45\u957f\u4e86\u3002\u6c5f\u5927\u4fa0\u96be\u9053\u8fd8\u6015\u4ed6\u53cd\u6251\u4e48\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8001\u524d\u8f88\u8bf4\u7684\u4e0d\u9519\uff0c\u770b\u8fd9\u8ff9\u8c61\uff0c\u4ed6\u4f3c\u4e4e\u5df2\u662f\u8d70\u706b\u5165\u9b54\u3001\u6027\u547d\u96be\u4fdd\u4e86\uff0c\u4e0d\u8fc7\u4ed6 \u73b0\u5728\u53e3\u5410\u9c9c\u8840\uff0c\u5374\u5e76\u975e\u662f\u56e0\u4e3a\u53d7\u4e86\u5185\u4f24\uff0c\u800c\u662f\u4ed6\u5728\u4f7f\u7528\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\uff01\u201d \u3000\u3000\u201c\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\u201d\u662f\u4e00\u79cd\u6781\u6b79\u6bd2\u7684\u90aa\u6d3e\u5185\u529f\uff0c\u4f7f\u7528\u7684\u4eba\u5728\u81ea\u6b8b\u672c\u8eab\u3001\u89c1\u8840\u4e4b\u540e\uff0c\u529f\u529b\u53ef \u4ee5\u9661\u589e\u4e00\u500d\u3002\u4f46\u4f7f\u7528\u8fd9\u79cd\u529f\u592b\uff0c\u6781\u4f24\u5143\u6c14\uff0c\u8fc7\u540e\u4e0d\u6b7b\u4e5f\u5c06\u6b8b\u5e9f\u3002\u662f\u4ee5\u90aa\u6d3e\u9ad8\u624b\uff0c\u53ea\u6709\u5728\u51c6\u5907 \u4e0e\u5bf9\u65b9\u4e24\u8d25\u4ff1\u4ea1\u7684\u65f6\u5019\uff0c\u65b9\u6562\u4f7f\u7528\u3002 \u3000\u3000\u6587\u9053\u5e84\u54ac\u7834\u820c\u5c16\uff0c\u4f7f\u51fa\u4e86\u5929\u9b54\u89e3\u4f53\u5927\u6cd5\uff0c\u679c\u7136\u653b\u52bf\u5927\u70bd\uff0c\u91d1\u9010\u6d41\u7684\u7384\u94c1\u5b9d\u5251\u90fd\u51e0\u4e4e\u906e\u62e6 \u4e0d\u4f4f\u3002\u53f2\u7ea2\u82f1\u66f4\u662f\u8fd1\u4e0d\u4e86\u4ed6\u7684\u8eab\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u5403\u60ca\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u54b1\u4eec\u4e5f\u4e0d\u7528\u548c\u4ed6\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u89c4\u77e9\u4e86\u3002\u6c5f\u5927\u4fa0\uff0c\u7531\u4f60\u51fa\u624b\uff0c \u8fd8\u662f\u7531\u6211\u51fa\u624b\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u52b3\u524d\u8f88\u8d39\u5fc3\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5f53\u7136\u662f\u8981\u81ea\u5df1\u51fa\u624b\u7684\u4e86\u3002\u4f46\u8bf4\u4e86\u4e4b\u540e\uff0c\u5374\u4ecd \u7136\u662f\u610f\u6001\u60a0\u95f2\u7684\u5728\u65c1\u89c2\u6218\u3002 \u3000\u3000\u6587\u9053\u5e84\u72c2\u7b11\u9053\uff1a\u201c\u91d1\u9010\u6d41\uff0c\u4f60\u6709\u7384\u94c1\u5b9d\u5251\uff0c\u4e5f\u4e0d\u80fd\u4f24\u6211\u6beb\u6bdb\uff0c\u6211\u7684\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\uff0c\u4f60\u670d \u4e0d\u670d\uff1f\u54c8\u54c8\u54c8\u54c8\uff0c\u6211\u7684\u6b66\u529f\u73b0\u5728\u662f\u5929\u4e0b\u7b2c\u4e00\u4e86\uff01\u4f60\u8fd9\u5c0f\u5b50\u82e5\u7136\u4e0d\u670d\uff0c\u8d76\u5feb\u628a\u4f60\u7684\u8001\u5b50\u53eb\u6765\uff0c \u5305\u7ba1\u4f60\u7684\u8001\u5b50\u4e5f\u4e0d\u662f\u6211\u7684\u5bf9\u624b\uff01\u54c8\u54c8\u54c8\u54c8\uff0c\u54c8\u54c8\u54c8\u54c8\uff01\u201d\u72c2\u7b11\u58f0\u4e2d\uff0c\u53cc\u638c\u8d77\u843d\u5982\u73af\uff0c\u5c06\u638c\u529b \u5411\u56db\u9762\u516b\u65b9\u53cd\u51fb\u51fa\u53bb\u3002\u91d1\u3001\u53f2\u4e8c\u4eba\uff0c\u5c55\u5f00\u4e86\u5929\u7f57\u6b65\u6cd5\uff0c\u5de6\u907f\u53f3\u95ea\uff0c\u7ed9\u4ed6\u8feb\u5230\u79bb\u8eab\u4e00\u4e08\u5f00\u5916\uff0c \u5140\u81ea\u611f\u5230\u80f8\u3001\u53e3\u5982\u53d7\u91cd\u538b\uff0c\u51e0\u4e4e\u900f\u4e0d\u8fc7\u6c14\u6765\uff0c\u54ea\u91cc\u80fd\u591f\u5206\u795e\u8bf4\u8bdd\u3002 \u3000\u3000\u6587\u9053\u5e84\u7b11\u58f0\u672a\u5df2\uff0c\u5ffd\u5730\u53c8\u4f3c\u54ed\u4e27\u4e00\u6837\u5730\u53eb\u9053\uff1a\u201c\u4e2d\u513f\uff0c\u4e2d\u513f\uff0c\u4f60\u5728\u4e5d\u6cc9\u4e4b\u4e0b\u5b64\u5b64\u5355\u5355\uff0c \u597d\u4e0d\u51c4\u51c9\uff01\u4e3a\u7236\u7ed9\u4f60\u627e\u4e2a\u6807\u81f4\u7684\u5c0f\u5a18\u5b50\u4f5c\u4f34\uff0c\u4f60\u6b22\u4e0d\u6b22\u559c\uff1f\u97ad\u70ae\u567c\u567c\u556a\u556a\u54cd\uff0c\u9501\u5450\u7684\u7684\u6253\u6253 \u5439\u3002\u567c\u567c\u556a\u556a\uff0c\u7684\u7684\u6253\u6253\u3002\u53f2\u7ea2\u82f1\u5440\u53f2\u7ea2\u82f1\uff0c\u4f60\u8fd9\u4e2b\u5934\u597d\u4e0a\u82b1\u8f7f\u5566\uff01\u201d \u3000\u3000\u9634\u607b\u607b\u5730\u568e\u53eb\u597d\u4f3c\u5229\u9488\u4e00\u6837\uff0c\u201c\u523a\u201d\u5f97\u53f2\u7ea2\u82f1\u5fc3\u91cc\u53d1\u614c\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6587\u9053\u5e84\u5df2\u662f \u4e58\u865a\u653b\u5165\uff0c\u5251\u524a\u638c\u5288\uff0c\u201c\u548b\u5693\u201d\u4e00\u58f0\uff0c\u628a\u53f2\u7ea2\u82f1\u7684\u8f6f\u97ad\u524a\u53bb\u4e86\u4e00\u622a\u3002\u91d1\u9010\u6d41\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9 \u62a2\u5728\u53f2\u7ea2\u82f1\u9762\u524d\uff0c\u6325\u5251\u906e\u62e6\u3002\u4f46\u5728\u6587\u9053\u5e84\u6392\u5c71\u5012\u6d77\u822c\u7684\u638c\u529b\u4e4b\u4e0b\uff0c\u7384\u94c1\u5b9d\u5251\u7adf\u662f\u65bd\u5c55\u4e0d\u5f00\u3002 \u91d1\u9010\u6d41\u4f7f\u51fa\u4e86\u201c\u5343\u65a4\u5760\u201d\u7684\u91cd\u8eab\u6cd5\uff0c\u4ecd\u7136\u6709\u5982\u4e00\u53f6\u8f7b\u821f\uff0c\u5728\u60ca\u6d9b\u9a87\u6d6a\u4e4b\u4e2d\u6447\u6447\u6643\u6643\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u6df1\u77e5\u6c5f\u6d77\u5929\u6b66\u5b66\u901a\u795e\uff0c\u4ed6\u65e2\u7136\u7b54\u5e94\u51fa\u624b\uff0c\u90a3\u81ea\u662f\u80f8\u6709\u6210\u7af9\uff0c\u4e0d\u6015\u91d1\u3001\u53f2\u4e8c\u4eba\u53d7\u5230 \u4f24\u5bb3\u7684\u4e86\uff0c\u4f46\u867d\u7136\u660e\u77e5\u5982\u6b64\uff0c\u516c\u5b59\u5b8f\u4ecd\u662f\u60c5\u4e0d\u81ea\u7981\u5730\u66ff\u4ed6\u4eec\u4e8c\u4eba\u7740\u6025\u3002\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u6c5f\u6d77 \u5929\u4ecd\u662f\u610f\u6001\u60a0\u95f2\u5730\u8896\u624b\u65c1\u89c2\u3002 \u3000\u3000\u731b\u542c\u5f97\u6587\u9053\u5e84\u4e00\u58f0\u5927\u559d\uff0c\u8eab\u5b50\u50cf\u65cb\u98ce\u7684\u6253\u4e86\u4e00\u4e2a\u5708\u5708\uff0c\u8f6f\u5251\u4e00\u5f39\uff0c\u91d1\u9010\u6d41\u7684\u7384\u94c1\u5b9d\u5251\u7ed9 \u4ed6\u5f39\u5f00\uff0c\u6587\u9053\u5e84\u6253\u4e86\u4e00\u4e2a\u5708\u5708\uff0c\u6070\u6070\u8f6c\u5230\u53f2\u7ea2\u82f1\u9762\u524d\uff0c\u4e94\u6307\u5982\u94a9\u671d\u7740\u5979\u7684\u5929\u7075\u76d6\u5c31\u6293\u4e0b\u6765\uff01 \u6b64\u65f6\u53f2\u7ea2\u82f1\u4e0e\u4ed6\u9762\u9762\u76f8\u5bf9\uff0c\u53ea\u89c1\u4ed6\u8138\u4e0a\u808c\u8089\u53d8\u5f62\uff0c\u663e\u5f97\u5341\u5206\u53ef\u6016\uff0c\u53f2\u7ea2\u82f1\u4e0d\u89c9\u201c\u554a\u5440\u201d\u4e00 \u58f0\uff0c\u53eb\u4e86\u8d77\u6765\uff01 \u3000\u3000\u516c\u5b59\u5b8f\u770b\u89c1\u6587\u9053\u5e84\u731b\u4e0b\u6740\u624b\uff0c\u53eb\u58f0\u201c\u4e0d\u5999\uff01\u201d\u65e0\u6687\u601d\u7d22\uff0c\u4e0d\u81ea\u89c9\u7684\u5c31\u8fc8\u6b65\u51fa\u53bb\uff0c\u4f46\u4ed6\u521a \u4e00\u8fc8\u6b65\uff0c\u53ea\u89c9\u5f97\u8863\u895f\u5e26\u98ce\u4e4b\u58f0\uff0c\u5728\u4ed6\u8eab\u65c1\u63a0\u8fc7\uff0c\u6c5f\u6d77\u5929\u7684\u8eab\u6cd5\u5feb\u5f97\u96be\u4ee5\u5f62\u5bb9\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u53ea \u89c1\u4ed6\u5df2\u7ad9\u5728\u6587\u9053\u5e84\u5bf9\u9762\u3002\u800c\u91d1\u3001\u53f2\u4e8c\u4eba\uff0c\u624b\u643a\u7740\u624b\uff0c\u6b63\u5728\u4f7f\u51fa\u4e00\u4e2a\u201c\u6bd4\u7ffc\u53cc\u98de\u201d\u7684\u8eab\u6cd5\uff0c\u8131 \u51fa\u4e86\u6587\u9053\u5e84\u638c\u529b\u7b3c\u7f69\u7684\u5708\u5b50\u3002\u6c5f\u6d77\u5929\u662f\u600e\u6837\u6551\u53f2\u7ea2\u82f1\u8131\u9669\u7684\uff0c\u7adf\u7136\u8fde\u516c\u5b59\u5b8f\u4e5f\u770b\u5f97\u4e0d\u5f88\u6e05 \u695a\uff0c\u516c\u5b59\u5b8f\u4e0d\u7981\u9762\u4e0a\u53d1\u70ed\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u8fd9\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\u7684\u540d\u5934\u786e\u662f\u540d\u4e0d\u865a\u4f20\uff01\u8fd9\u4e5f \u5f53\u771f\u662f\u4e00\u5c71\u8fd8\u6709\u4e00\u5c71\u9ad8\u4e86\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u7ffb\u8d77\u4e00\u53cc\u767d\u6e17\u6e17\u7684\u773c\u73e0\uff0c\u76ef\u7740\u6c5f\u6d77\u5929\uff0c\u602a\u91cc\u602a\u6c14\u5730\u8bf4\u9053\uff1a\u201c\u6211\u8ba4\u8bc6\u4f60\uff0c\u4f60\u662f\u6c5f\u6d77 \u5929\uff01\u4f60\u7684\u5e08\u7236\u4e0d\u5728\u4e2d\u539f\uff0c\u4eba\u5bb6\u90fd\u8bf4\u4f60\u7684\u6b66\u529f\u662f\u5929\u4e0b\u7b2c\u4e00\u3002\u4f60\u6562\u548c\u6211\u6253\u4e48\u3002\u201d\u6c5f\u6d77\u5929\u51b7\u51b7\u8bf4 \u9053\uff1a\u201c\u6b66\u529f\u662f\u6ca1\u6709\u7b2c\u4e00\u7684\u3002\u6211\u7684\u5e08\u7236\u867d\u7136\u6253\u904d\u5929\u4e0b\u65e0\u654c\u624b\uff0c\u4f46\u8fd9\u53e5\u8bdd\u5374\u6b63\u662f\u4ed6\u4e00\u518d\u548c\u6211\u8bf4 \u7684\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8c01\u8bf4\u6b66\u529f\u6ca1\u6709\u7b2c\u4e00\uff0c\u6211\u5c31\u662f\u5929\u4e0b\u7b2c\u4e00\u3002\u4f60\u6015\u4e86\u6211\u662f\u4e0d\u662f\uff1f\u54fc\uff0c\u4f60\u6015 \u4e86\u6211\uff0c\u6211\u4e5f\u8981\u6253\u4f60\uff0c\u8c01\u53eb\u4f60\u6740\u4e86\u6211\u7684\u513f\u5b50\uff0c\u6211\u8981\u66ff\u513f\u5b50\u62a5\u4ec7\uff01\u201d\u6b64\u65f6\u4ed6\u5df2\u662f\u795e\u667a\u5b8c\u5168\u9519\u4e71\uff0c \u89c1\u4e86\u4ec0\u4e48\u4eba\u90fd\u5f53\u4f5c\u662f\u6740\u5b50\u7684\u4ec7\u4eba\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u4e0d\u6015\u4f60\uff0c\u6211\u4e5f\u4e0d\u60f3\u6740\u4f60\u3002\u201d\u6587\u9053\u5e84\u9053\uff1a\u201c\u4f60\u4e0d\u6740\u6211\uff0c\u6211\u8981\u6740\u4f60\uff01\u201d\u5530\u7684 \u4e00\u5251\u5c31\u5411\u6c5f\u6d77\u5929\u523a\u6765\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u6587\u9053\u5e84\u90a3\u67c4\u9ed1\u7389\u8f6f\u5251\u5316\u4f5c\u4e86\u4e00\u6761\u9ed1\u9f99\uff0c\u8131\u624b\u98de\u4e0a\u534a\u7a7a\uff01\u516c\u5b59\u5b8f\u8d5e \u9053\uff1a\u201c\u597d\u4e2a\u5f39\u6307\u795e\u901a\u7684\u7edd\u9876\u795e\u529f\uff01\u201d \u3000\u3000\u6587\u9053\u5e84\u9053\uff1a\u201c\u4f60\u6709\u795e\u529f\u6211\u4e5f\u6709\u795e\u529f\uff01\u201d\u629b\u4e86\u8f6f\u5251\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u65b9\u5706\u6570\u4e08\u4e4b\u5185\uff0c\u767b\u65f6\u6c99\u98de \u77f3\u8d70\uff0c\u9690\u9690\u631f\u7740\u98ce\u96f7\u4e4b\u58f0\uff0c\u8fd9\u662f\u4ed6\u6bd5\u751f\u529f\u529b\u4e4b\u6240\u805a\u7684\u4e00\u51fb\uff01\u8981\u77e5\u4ed6\u867d\u7136\u795e\u667a\u9519\u4e71\uff0c\u4e5f\u77e5\u6c5f\u6d77 \u5929\u662f\u4e2a\u6700\u5927\u7684\u5f3a\u654c\uff0c\u4e09\u8c61\u795e\u529f\u4f7f\u51fa\uff0c\u5df2\u662f\u628a\u6700\u540e\u4e00\u70b9\u7684\u7cbe\u529b\u90fd\u6324\u51fa\u6765\u4e86\uff01\u516c\u5b59\u5b8f\u5fc3\u4e2d\u81ea\u5fd6\uff1a \u201c\u5047\u5982\u662f\u6211\u5e94\u4ed8\u4ed6\u8fd9\u6bd5\u751f\u529f\u529b\u4e4b\u6240\u805a\u7684\u4e00\u51fb\uff0c\u8d25\u7ed9\u4ed6\u90a3\u662f\u4e0d\u4f1a\u7684\uff1b\u4f46\u8981\u80dc\u4ed6\uff0c\u53ea\u6015\u4e5f\u662f\u5f88\u4e0d \u5bb9\u6613\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5140\u7acb\u5982\u5c71\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u53ea\u542c\u5f97\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u56db\u638c\u76f8\u4ea4\uff0c\u5c31\u50cf\u80f6\u7740\u4e86\u4e00\u822c\u3002 \u3000\u3000\u4e0d\u8fc7\u7247\u523b\uff0c\u6587\u9053\u5e84\u53ea\u89c9\u638c\u5fc3\u597d\u4f3c\u7ed9\u5229\u9488\u523a\u7a7f\u4e86\u4e00\u4e2a\u5c0f\u5b54\uff0c\u4f53\u4e2d\u771f\u6c14\u6e90\u6e90\u4e0d\u7edd\u5730\u4ece\u5c0f\u5b54\u55b7 \u51fa\u3002\u767b\u65f6\u50cf\u662f\u6cc4\u4e86\u6c14\u7684\u76ae\u7403\u4e00\u6837\uff0c\u56db\u80a2\u65e0\u529b\uff0c\u5927\u60ca\u5931\u8272\uff0c\u4e24\u773c\u65e0\u795e\u5446\u5446\u671b\u7740\u6c5f\u6d77\u5929\u3002 \u3000\u3000\u4f46\u8bf4\u4e5f\u5947\u602a\uff0c\u4ed6\u7684\u771f\u6c14\u6e90\u6e90\u4e0d\u7edd\u7684\u5916\u6cc4\uff0c\u4ed6\u7684\u795e\u667a\u5982\u6e10\u6e10\u7684\u6e05\u9192\u8d77\u6765\u3002\u5f85\u5230\u534a\u70b9\u6c14\u529b\u90fd \u4f7f\u4e0d\u51fa\u6765\u4e86\uff0c\u4ed6\u4e5f\u5b8c\u5168\u9192\u8fc7\u6765\u4e86\u3002\u6b64\u65f6\u4ed6\u867d\u7136\u56db\u80a2\u65e0\u529b\uff0c\u4f46\u5374\u6709\u8bf4\u4e0d\u51fa\u7684\u8212\u670d\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u6b7b\u4e86\u4e00\u4e2a\u513f\u5b50\uff0c\u5c31\u8981\u5230\u5904\u627e\u4eba\u62a5\u4ec7\u3002\u4f60\u4eec\u7236\u5b50\u6740\u4e86\u591a\u5c11\u4eba\uff0c\u522b\u4eba \u90fd\u6765\u5411\u4f60\u62a5\u4ec7\uff0c\u4f60\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u6587\u9053\u5e84\u77e5\u9053\u81ea\u5df1\u7684\u6b66\u529f\u5df2\u7ed9\u6c5f\u6d77\u5929\u5e9f\u6389\uff0c\u542c\u4e86\u6c5f\u6d77\u5929\u8fd9\u8bdd\uff0c\u5982\u53d7\u5f53\u5934\u4e00\u68d2\uff0c\u4e0d\u89c9\u70b9\u4e86\u70b9 \u5934\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u8981\u4eba\u5bb6\u7ed9\u6211\u513f\u5b50\u507f\u547d\uff0c\u6211\u4e5f\u5e94\u8be5\u7ed9\u4eba\u5bb6\u507f\u547d\u3002\u6c5f\u5927\u4fa0\uff0c\u6211\u6253\u4e0d\u8fc7 \u4f60\uff0c\u4f60\u6740\u4e86\u6211\u5427\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u653e\u4e0b\u5c60\u5200\uff0c\u7acb\u5730\u6210\u4f5b\u3002\u4f60\u82e5\u4ece\u4eca\u6539\u8fc7\u5411\u5584\uff0c\u8fd8\u53ef\u505a\u4e2a\u597d\u4eba\u3002\u201d\u6587\u9053\u5e84\u5fc3\u7070 \u610f\u51b7\uff0c\u8bf4\u9053\uff1a\u201c\u5982\u4eca\u6211\u5df2\u662f\u4e2a\u5e9f\u4eba\uff0c\u6d3b\u5728\u4e16\u4e0a\uff0c\u53c8\u6709\u4f55\u7528\uff1f\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u7136\uff0c\u4e0d\u7136\u3002\u4f60 \u7684\u4e09\u8c61\u795e\u529f\u867d\u7136\u662f\u6ca1\u6709\u4e86\uff0c\u81f3\u5c11\u8fd8\u53ef\u4ee5\u5c06\u8fd9\u95e8\u6b66\u5b66\u4f20\u4e4b\u540e\u4eba\u3002\u201d\u539f\u6765\u6c5f\u6d77\u5929\u5e9f\u4e86\u4ed6\u7684\u6b66\u529f\uff0c \u4f46\u5374\u7ed9\u4ed6\u6cbb\u597d\u4e86\u8d70\u706b\u5165\u9b54\u7684\u6b7b\u75c7\u3002 \u3000\u3000\u4fee\u70bc\u90aa\u6d3e\u5185\u529f\u7684\u4eba\uff0c\u6700\u6015\u7684\u5c31\u662f\u8d70\u706b\u5165\u9b54\uff0c\u56e0\u4e3a\u8d70\u706b\u5165\u9b54\u4e4b\u82e6\uff0c\u82e6\u4e0d\u582a\u8a00\uff0c\u6545\u6b64\u6587\u9053\u5e84 \u867d\u7136\u5931\u4e86\u5185\u529f\uff0c\u4f46\u5f97\u514d\u9664\u6b64\u96be\uff0c\u4e5f\u662f\u5fc3\u7518\u60c5\u613f\u7684\u4e86\u3002\u4ed6\u795e\u667a\u6e05\u9192\u4e4b\u540e\uff0c\u6c42\u751f\u4e4b\u5ff5\uff0c\u6cb9\u7136\u800c \u5174\uff0c\u53ea\u597d\u6ee1\u9762\u901a\u7ea2\uff0c\u5411\u6c5f\u6d77\u5929\u8868\u793a\u611f\u6fc0\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u5bb6\u5e08\u5f53\u5e74\u4e5f\u66fe\u5982\u6b64\u5bf9\u5f85\u4ee4\u53d4\uff0c\u4e3a\u7684\u5c31\u662f\u8981\u8ba9\u8d35\u6d3e\u7684\u4e09\u8c61\u795e\u529f\u4e0d\u81f4\u5931\u4f20\uff0c\u6211 \u5982\u4eca\u4e0d\u8fc7\u6548\u6cd5\u5bb6\u5e08\u6240\u4e3a\u800c\u5df2\u3002\u4f46\u613f\u6587\u5148\u751f\u5584\u4f53\u5bb6\u5e08\u7684\u82e6\u5fc3\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u6587\u9053\u5e84\uff0c\u6211\u4e0d\u548c\u4f60\u8bf4\u5ba2\u6c14\u7684\u8bdd\uff0c\u6211\u529d\u4f60\u4ee5\u540e\u597d\u597d\u9009\u62e9\u5f92\u5f1f\uff0c\u4f9d\u6211\u4e4b\u89c1\uff0c\u7b2c \u4e00\u662f\u6559\u4ed6\u505a\u4eba\uff0c\u7b2c\u4e8c\u624d\u662f\u6559\u4ed6\u6b66\u529f\u3002\u4f60\u8981\u62ff\u4f60\u81ea\u5df1\u505a\u9762\u955c\u5b50\uff0c\u514d\u5f97\u4f60\u7684\u5f92\u5f1f\u518d\u5760\u8986\u8f99\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u6ee1\u9762\u7f9e\u60ed\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u4e24\u4f4d\u5927\u4fa0\u7684\u91d1\u7389\u826f\u8a00\u3002\u201d\u516c\u5b59\u5b8f\u6325\u4e86\u6325\u624b\uff0c\u8bf4\u9053\uff1a \u201c\u597d\uff0c\u4f60\u8bb0\u5f97\u5c31\u597d\u3002\u90a3\u4f60\u8d70\u5427\u3002\u201d \u3000\u3000\u6587\u9053\u5e84\u6b66\u529f\u867d\u5931\uff0c\u4f53\u529b\u4ecd\u662f\u4e0d\u900a\u5e38\u4eba\uff0c\u5f53\u4e0b\u6298\u4e86\u4e00\u6839\u6811\u679d\u5f53\u4f5c\u62d0\u6756\uff0c\u4e00\u6b65\u4e00\u6b65\u5730\u8d70\u4e0b\u5c71 \u53bb\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u7b11\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\uff0c\u6211\u771f\u662f\u4f69\u670d\u4f60\u7684\u6c14\u91cf\uff0c\u4f60\u4e0d\u4f46\u533b\u597d\u4e86\u6587\u9053\u5e84\u8eab\u4f53\u7684\u75c5\uff0c\u8fde\u4ed6\u5fc3 \u4e0a\u7684\u75c5\u4e5f\u8981\u7ed9\u4ed6\u533b\u6cbb\uff0c\u4f46\u613f\u90fd\u80fd\u6cbb\u597d\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u3001\u53f2\u7ea2\u82f1\u4e0a\u524d\u4e0e\u516c\u5b59\u5b8f\u76f8\u89c1\uff0c\u8bf4\u9053\uff1a\u201c\u8d3a\u559c\u516c\u5b59\u524d\u8f88\uff0c\u4f60\u8001\u7684\u8eab\u4f53\u5df2\u7ecf\u5b8c\u5168\u597d \u5566\uff01\u201d\u516c\u5b59\u5b8f\u7b11\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u591a\u4e8f\u4e86\u4f60\u7684\u5e08\u5144\uff0c\u5426\u5219\u6211\u6050\u6015\u8fd8\u8981\u518d\u8fc7\u5341\u5929\u624d\u80fd\u8d77\u5e8a\u3002\u201d\u539f\u6765 \u51e0\u5929\u524d\u6c5f\u6d77\u5929\u5230\u4ed6\u5bb6\u91cc\uff0c\u77e5\u9053\u4ed6\u6b63\u4ee5\u4e0a\u4e58\u5185\u529f\u6cbb\u6bd2\u7597\u4f24\uff0c\u5f53\u4e0b\u4fbf\u4ee5\u672c\u8eab\u4fee\u4e60\u7684\u7eaf\u9633\u5185\u529f\u52a9\u4ed6 \u9a71\u9664\u5bd2\u6bd2\u3002\u6c5f\u6d77\u5929\u5c11\u5e74\u4e4b\u65f6\u66fe\u670d\u4e0b\u5947\u836f\u5929\u5fc3\u77f3\uff0c\u7ec3\u6210\u7684\u7eaf\u9633\u5185\u529f\u6b63\u662f\u5bd2\u6bd2\u7684\u514b\u661f\u3002\u4e0d\u8fc7\u516c\u5b59 \u5b8f\u4e4b\u6240\u4ee5\u597d\u5f97\u8fd9\u6837\u5feb\uff0c\u8fd9\u4e5f\u662f\u56e0\u4e3a\u4ed6\u7684\u672c\u8eab\u529f\u529b\u4ec5\u6b21\u4e8e\u6c5f\u6d77\u5929\u800c\u5df2\uff0c\u6362\u4e86\u522b\u4eba\uff0c\u5c31\u4e0d\u884c\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5411\u5389\u5357\u661f\u90a3\u8fb9\u671b\u53bb\uff0c\u89c1\u4ed6\u548c\u516c\u5b59\u71d5\u8054\u624b\uff0c\u5df2\u662f\u5360\u5c3d\u4e0a\u98ce\uff0c\u7a33\u64cd\u80dc\u5238\uff0c\u653e\u4e0b\u4e86\u5fc3\uff0c \u7b11\u9053\uff1a\u201c\u5feb\u4e86\uff0c\u5feb\u4e86\uff0c\u9633\u6d69\u8fd9\u8001\u8d3c\u5c31\u8981\u83b7\u5f97\u548c\u6587\u9053\u5e84\u540c\u6837\u7684\u7ed3\u679c\u4e86\u3002\u4e0d\u8fc7\uff0c\u6709\u4e00\u4e8b\u6211\u5374\u89c9\u5f97 \u6709\u70b9\u5947\u602a\uff0c\u9633\u6d69\u7684\u515a\u7fbd\u548c\u5929\u9b54\u6559\u7684\u5f92\u4f17\u4e3a\u4f55\u4e0d\u89c1\u6709\u4e00\u4eba\u6765\u5230\uff1f\u54b1\u4eec\u5728\u8fd9\u91cc\u5df2\u7ecf\u4e0d\u6b62\u534a\u4e2a\u65f6\u8fb0 \u5566\uff01\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u4f60\u5c31\u4f1a\u660e\u767d\u7684\u4e86\u3002\u201d\u6b63\u60f3\u8bf4\u4e0b\u53bb\uff0c\u5ffd\u542c\u5f97\u53f2\u7ea2\u82f1\u60ca\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u5403\u4e00\u60ca\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u9633\u6d69\u6b63\u5728\u98de\u8eab\u8dc3\u8d77\uff0c\u53cc\u638c\u5411\u516c\u5b59\u71d5\u7684\u5929\u7075\u76d6\u62cd\u4e0b\uff01\u8fd9 \u662f\u4e00\u62db\u62fc\u7740\u53cc\u65b9\u540c\u5f52\u4e8e\u5c3d\u7684\u7edd\u62db\uff01 \u3000\u3000\u539f\u6765\u9633\u6d69\u5e76\u4e0d\u76f8\u4fe1\u516c\u5b59\u71d5\u4e0e\u5389\u5357\u661f\u7701\u9976\u4ed6\u6027\u547d\uff0c\u66f4\u4e0d\u77e5\u9053\u5389\u5357\u661f\u5f97\u4e86\u6c5f\u6d77\u5929\u7684\u6307\u70b9\uff0c\u523a \u4ed6\u7684\u201c\u52b3\u5bab\u7a74\u201d\uff0c\u53ea\u4e0d\u8fc7\u60f3\u5e9f\u6389\u4ed6\u7684\u6b66\u529f\u3002\u4ed6\u4ee5\u4e3a\u5389\u5357\u661f\u4e00\u5b9a\u662f\u8981\u5728\u5e9f\u4ed6\u6b66\u529f\u4e4b\u540e\uff0c\u6162\u6162\u5c06 \u4ed6\u6298\u78e8\uff0c\u7ec8\u4e8e\u96be\u9003\u4e00\u6b7b\u7684\u3002\u6545\u6b64\u4ed6\u628a\u5fc3\u4e00\u6a2a\uff0c\u8d81\u7740\u5389\u5357\u661f\u672a\u66fe\u5f97\u624b\u4e4b\u524d\uff0c\u4fbf\u8981\u4e0e\u516c\u5b59\u71d5\u62fc\u4e2a \u540c\u5f52\u4e8e\u5c3d\u3002\u56e0\u4e3a\u5389\u5357\u661f\u529f\u529b\u8f83\u9ad8\uff0c\u4ed6\u8981\u6740\u5389\u5357\u661f\u4e0d\u53ca\u6740\u516c\u5b59\u71d5\u90a3\u4e48\u5bb9\u6613\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e00\u58f0\u9a87\u4eba\u5fc3\u9b44\u7684\u547c\u53eb\uff0c\u4e24\u9053\u94f6\u5149\u98de\u4e0a\u534a\u7a7a\uff0c\u516c\u5b59\u71d5\u659c\u63a0\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u9633\u6d69\u5374\u8dcc\u5728 \u5730\u6b62\uff0c\u56db\u811a\u671d\u5929\uff0c\u8840\u6d41\u6ee1\u9762\u3002 \u3000\u3000\u539f\u6765\u5728\u9633\u6d69\u53cc\u638c\u62cd\u4e0b\u7684\u5f53\u513f\uff0c\u5389\u5357\u661f\u5df2\u662f\u523a\u7740\u4e86\u4ed6\u638c\u5fc3\u7684\u201c\u52b3\u5bab\u7a74\u201d\u3002\u9633\u6d69\u7684\u638c\u529b\u9707\u5f97 \u516c\u5b59\u71d5\u7684\u53cc\u5251\u8131\u624b\u4e4b\u540e\uff0c\u771f\u6c14\u5df2\u662f\u4ece\u201c\u52b3\u5bab\u7a74\u201d\u6cc4\u51fa\uff0c\u540e\u52b2\u65e0\u529b\uff1b\u662f\u4ee5\u867d\u7136\u7ed9\u4ed6\u62cd\u7740\u80a9\u5934\uff0c \u4f46\u5982\u6beb\u65e0\u59a8\u788d\u3002\u516c\u5b59\u71d5\u8dc3\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u4e0d\u8fc7\u662f\u672c\u80fd\u7684\u53cd\u5e94\u800c\u5df2\u3002\u5176\u5b9e\u9633\u6d69\u6b66\u529f\u88ab\u5e9f\uff0c\u5df2\u662f\u65e0 \u80fd\u4e3a\u529b\u7684\u4e86\u3002 \u3000\u3000\u800c\u4e14\u516c\u5b59\u71d5\u5728\u53cc\u5251\u7ed9\u4ed6\u638c\u529b\u4e00\u9707\u7684\u90a3\u5239\u90a3\u95f4\uff0c\u5251\u5c16\u5212\u8fc7\u4ed6\u7684\u9762\u95e8\uff0c\u4e5f\u523a\u778e\u4e86\u4ed6\u4e00\u53ea\u773c \u775b\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u628a\u5973\u513f\u6276\u7a33\uff0c\u770b\u51fa\u5979\u5e76\u6ca1\u6709\u53d7\u4f24\u7684\u8ff9\u8c61\uff0c\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u4e3a\u7236\u7684\u4e5f\u662f\u5927 \u610f\u70b9\u513f\uff0c\u7d2f\u5f97\u5b69\u513f\u53d7\u60ca\u4e86\u3002\u4e0d\u8fc7\uff0c\u8fd9\u4e5f\u662f\u4e00\u4e2a\u6559\u8bad\uff0c\u6559\u8bad\u6211\u4eec\uff0c\u867d\u7136\u5728\u7a33\u64cd\u80dc\u5238\u7684\u60c5\u5f62\u4e4b \u4e0b\uff0c\u4e5f\u662f\u4e0d\u53ef\u8f7b\u654c\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u778e\u4e86\u773c\u7684\u72d7\u8d3c\uff0c\u6211\u672c\u6765\u662f\u8981\u9976\u4f60\u7684\u6027\u547d\u7684\uff0c\u4f60\u5374\u8fd9\u6837\u72e0\u6bd2\uff01\u201d \u3000\u3000\u9633\u6d69\u722c\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u5b59\u8235\u4e3b\uff0c\u4f60\u8bf4\u8fc7\u7684\u8bdd\u7b97\u4e0d\u7b97\u6570\uff1f\u201d \u3000\u3000\u4ea4\u624b\u4e4b\u524d\uff0c\u516c\u5b59\u5b8f\u662f\u5429\u5490\u8fc7\u5389\u6d77\u661f\u548c\u5973\u6b62\u53ea\u5e9f\u6389\u4ed6\u7684\u6b66\u529f\uff0c\u5e76\u6ca1\u6709\u8bf4\u8981\u53d6\u4ed6\u6027\u547d\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\u7684\u8bdd\uff0c\u5f53\u7136\u7b97\u6570\u3002\u201d\u5389\u5357\u661f\u6536\u5251\u5165\u9798\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u8c05\u4f60\u4eca\u540e\u4e5f\u4e0d \u80fd\u4f5c\u6076\u7684\u4e86\uff0c\u597d\uff0c\u5c31\u4fbf\u5b9c\u4f60\u8fd9\u8001\u8d3c\u5427\u3002\u201d \u3000\u3000\u9633\u6d69\u50cf\u6587\u9053\u5e84\u521a\u624d\u90a3\u6837\uff0c\u6298\u4e0b\u4e00\u679d\u6811\u679d\uff0c\u5f53\u4f5c\u62d0\u6756\uff0c\u5c31\u8981\u4e0b\u5c71\uff0c\u516c\u5b59\u71d5\u6068\u6068\u8bf4\u9053\uff1a\u201c\u8fd9 \u53ae\u6bd4\u6587\u9053\u5e84\u8fd8\u66f4\u53ef\u6076\uff0c\u540c\u6837\u7684\u7ed3\u679c\uff0c\u53ef\u771f\u662f\u4fbf\u5b9c\u4ed6\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u5ffd\u5730\u559d\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u9633\u6d69\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u5b59\u8001\u513f\uff0c\u4f60\u8981\u53cd\u6094\uff1f\u201d\u516c\u5b59\u5b8f \u9053\uff1a\u201c\u4f60\u6015\u4ec0\u4e48\uff0c\u6211\u8bf4\u8fc7\u4e0d\u53d6\u4f60\u7684\u6027\u547d\uff0c\u4f60\u5c31\u662f\u8dea\u5728\u6211\u7684\u9762\u524d\uff0c\u8ba9\u6211\u6740\u4f60\uff0c\u6211\u4e5f\u8d5a\u6c61\u4e86\u624b\u3002 \u4f46\u4f60\u53ef\u4e0d\u80fd\u4e00\u8d70\u4e86\u4e4b\uff0c\u5929\u9b54\u6559\u7684\u4e8b\u60c5\u8fd8\u672a\u4e86\u7ed3\u5462\uff0c\u4f60\u8bf7\u6765\u4e86\u8bb8\u591a\u52a9\u62f3\u7684\u670b\u53cb\uff0c\u96be\u9053\u4f60\u4e0d\u8981\u5411 \u4ed6\u4eec\u4ea4\u4ee3\u4ea4\u4ee3\u4e48\uff1f\u201d\u516c\u5b59\u71d5\u7b11\u9053\uff1a\u201c\u5bf9\uff0c\u53eb\u4ed6\u53bb\u4eae\u4e00\u4eae\u76f8\uff0c\u4e5f\u597d\u53eb\u90a3\u4e9b\u5996\u5b7d\u77e5\u9053\u4ed6\u662f\u600e\u4e48\u4e2a \u4e0b\u573a\uff01\u201d \u3000\u3000\u9633\u6d69\u6068\u4e0d\u5f97\u6709\u4e2a\u5730\u6d1e\u94bb\u51fa\u53bb\uff0c\u5fc3\u91cc\u81ea\u601d\uff1a\u201c\u5982\u6b64\u53d7\u8fb1\uff0c\u5012\u4e0d\u5982\u6b7b\u4e86\u8fd8\u597d\uff01\u201d\u4f46\u8f6c\u5ff5\u4e00 \u60f3\uff1a\u201c\u8fd9\u4ec7\u6211\u662f\u4e0d\u80fd\u4eb2\u81ea\u62a5\u590d\u7684\u4e86\uff0c\u4f46\u6211\u7684\u4fee\u7f57\u9634\u715e\u529f\uff0c\u4e5f\u8fd8\u53ef\u4ee5\u4f20\u7ed9\u6211\u7684\u5e08\u4f84\uff0c\u8fd9\u4e00\u4ee3\u62a5 \u4e0d\u4e86\uff0c\u4e0b\u4e00\u4ee3\u518d\u62a5\u3002\u53ea\u8981\u6211\u8fd8\u6709\u4e00\u53e3\u6c14\uff0c\u5c31\u4e0d\u80fd\u7b97\u5b8c\u3002\u201d\u4ed6\u6bd4\u6587\u9053\u5e84\u66f4\u4e3a\u8001\u5978\u767e\u733e\uff0c\u5f53\u4e0b\u51b3 \u5b9a\u4e86\u201c\u5fcd\u8fb1\u8d1f\u91cd\u201d\uff0c\u5c31\u4f6f\u4f5c\u611f\u6fc0\u6d95\u96f6\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u5b59\u8235\u4e3b\uff0c\u591a\u8499\u4f60\u4e0d\u6740\u4e4b\u6069\uff0c\u6211\u5f53\u7136\u5e94\u8be5\u542c \u4f60\u5429\u5490\u3002\u201d \u3000\u3000\u5f53\u4e0b\u6c5f\u6d77\u5929\u3001\u91d1\u9010\u6d41\u4e00\u884c\u4eba\u7b49\u9042\u4e0e\u9633\u6d69\u4e0a\u5c71\uff0c\u8f6c\u8fc7\u5c71\u5773\uff0c\u542c\u5f97\u4e00\u7247\u55a7\u95f9\u4e4b\u58f0\u3002\u6b63\u662f\uff1a \u3000\u3000\u4e00\u5ff5\u6148\u60b2\u7559\u7978\u60a3\uff0c\u4ece\u6765\u6076\u8349\u8981\u9664\u6839\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e94\u56de\u3000\u4e2d\u539f\u5e76\u9a7e\u82f1\u8c6a\u5728\u3000\u6d77\u5916\u8fde\u679d\u5251\u5ba2\u6765 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u56db\u5341\u4e94\u56de\u3000\u4e2d\u539f\u5e76\u9a7e\u82f1\u8c6a\u5728\u3000\u6d77\u5916\u8fde\u679d\u5251\u5ba2\u6765 \u3000\u3000\u91d1\u9010\u6d41\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5c71\u4e0a\u9ed1\u538b\u538b\u7684\u4e00\u7247\u4eba\u5934\uff0c\u4f3c\u662f\u4e24\u9635\u5bf9\u654c\u7684\u5f62\u52bf\u3002 \u3000\u3000\u516c\u5b59\u71d5\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u539f\u6765\u4f60\u4e0d\u662f\u5355\u8eab\u6765\u7684\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u5185\u5916\u4e09\u5802\u7684\u9999\u4e3b\u542c\u8bf4\u6211\u8981\u5230\u5f82\u5f95\u5c71\u627e\u9633\u6d69\u7b97\u5e10\uff0c\u5927\u5bb6\u90fd\u8981\u8ddf\u6765\uff0c\u4ed6\u4eec\u4e00\u7247 \u70ed\u5fc3\uff0c\u6211\u60f3\u538b\u4e5f\u538b\u4e0d\u4f4f\uff0c\u53ea\u597d\u8ba9\u4ed6\u4eec\u6765\u4e86\u3002\u201d\u8981\u77e5\u7ea2\u7f28\u4f1a\u4e43\u662f\u6c5f\u6e56\u4e0a\u7684\u7b2c\u4e00\u5927\u5e2e\u4f1a\uff0c\u5e2e\u4e3b\u7ed9 \u4eba\u6697\u7b97\uff0c\u53d7\u4e86\u91cd\u4f24\uff0c\u5e2e\u4e2d\u4e00\u4f17\u5f1f\u5144\uff0c\u81ea\u662f\u8ba4\u4e3a\u5947\u803b\u5927\u8fb1\uff0c\u662f\u4ee5\u4ed6\u4eec\u867d\u7136\u660e\u77e5\u516c\u5b59\u5b8f\u4e0e\u6c5f\u6d77\u5929 \u8054\u8882\u4e0a\u5c71\uff0c\u51b3\u4e0d\u4f1a\u5403\u4e8f\uff0c\u4e5f\u975e\u8ddf\u6765\u4e0d\u53ef\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u53c8\u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u8d35\u5e2e\u7684\u674e\u526f\u5e2e\u4e3b\uff0c\u4e5f\u5e26\u4e86\u8bb8\u591a\u4eba\u6765\u4e86\u3002\u4ed6\u4eec\u7684\u6d88\u606f\u5f88\u662f\u7075\u901a\uff0c \u6211\u8fd8\u672a\u66fe\u6d3e\u4eba\u5411\u4ed6\u4eec\u62a5\u4fe1\uff0c\u4ed6\u4eec\u5df2\u7ecf\u77e5\u9053\u4f60\u548c\u91d1\u5c11\u4fa0\u4e0a\u5f82\u5f95\u5c71\u4e86\u3002\u201d\u516c\u5b59\u71d5\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4e0d \u89c1\u9633\u6d69\u7684\u515a\u7fbd\u8dd1\u6765\u52a9\u9635\uff0c\u539f\u6765\u662f\u7ed9\u4ed6\u4eec\u5835\u4f4f\u4e86\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u5927\u559c\u9053\uff1a\u201c\u674e\u6566\u8fdb\u6765\u4e86\u5417\uff1f\u201d\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u4ed6\u4eec\u592b\u5987\u90fd\u6765\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u674e\u6566\u7cbe\u660e\u5e72\u7ec3\uff0c\u4e00\u5b9a\u65e9\u5df2\u8bc6\u7a7f\u4e86\u90a3\u4e2a\u5047\u5192\u5389\u5927\u54e5\u7684\u5929\u9b54\u6559\u6559\u4e3b\uff0c\u6599\u60f3\u54b1 \u4eec\u5b9a\u7136\u4f1a\u6765\u67e5\u63a2\u771f\u76f8\u7684\u3002\u674e\u6566\u80fd\u89e3\u5929\u9b54\u6559\u6240\u4e0b\u4e4b\u6bd2\uff0c\u4ed6\u8fd9\u4e00\u6765\uff0c\u6765\u5f97\u6b63\u597d\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u62c5\u5fe7\u9053\uff1a\u201c\u4e0d\u77e5\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u548c\u5929\u9b54\u6559\u7684\u4eba\u51b2\u7a81\u8d77\u6765\uff1f\u83ab\u8981\u4e3a\u6211\u4e00\u4eba\uff0c\u8fde\u7d2f\u4e86\u8bb8\u591a \u65e0\u8f9c\u7684\u4eba\u53d7\u4f24\u624d\u597d\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u544a\u8bc9\u4ed6\u4eec\uff0c\u8fd9\u6b21\u53ea\u627e\u9633\u6d69\u4e00\u4eba\u7b97\u5e10\uff0c\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u4e71\u6253\u4e00\u573a\u7684\uff0c\u5929\u9b54 \u6559\u7684\u4eba\uff0c\u4ea6\u5df2\u77e5\u9053\u6211\u548c\u6c5f\u5927\u4fa0\u540c\u6765\uff0c\u6599\u60f3\u4ed6\u4eec\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u80c6\u91cf\uff0c\u5148\u884c\u52a8\u624b\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4ed6\u4eec\u5df2\u7ecf\u5230\u4e86\u5c71\u4e0a\uff0c\u4e24\u65b9\u9762\u7684\u4eba\u4e5f\u90fd\u53d1\u73b0\u4ed6\u4eec\u3002 \u3000\u3000\u53ea\u89c1\u7ea2\u7f28\u4f1a\u7684\u4e09\u5927\u9999\u4e3b\u2014\u2014\u77f3\u7384\u3001\u79e6\u51b2\u3001\u5e84\u8fdc\uff0c\u5168\u90fd\u5728\u573a\uff0c\u674e\u6566\u5219\u6b63\u5728\u548c\u5bf9\u65b9\u4e00\u4e2a\u8001\u8005 \u8bf4\u8bdd\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5b9a\u775b\u4e00\u770b\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u4f46\u516d\u5408\u5e2e\u7684\u4eba\u6765\u4e86\uff0c\u8fd8\u6709\u5176\u4ed6\u5e2e\u4f1a\u7684\u4eba\u5462\uff0c\u54b1\u4eec\u5728\u534e\u5c71\u78b0 \u89c1\u7684\u90a3\u516d\u4e2a\u4eba\u4e5f\u6765\u4e86\u3002\u201d \u3000\u3000\u9633\u6d69\u90a3\u73ed\u515a\u7fbd\uff0c\u770b\u89c1\u9633\u6d69\u4e00\u526f\u4e27\u5bb6\u4e4b\u72d7\u7684\u795e\u6c14\uff0c\u7ed9\u4ed6\u4eec\u62bc\u6765\uff0c\u90fd\u662f\u5403\u60ca\u4e0d\u5df2\u3002\u5929\u9b54\u6559\u7684 \u5f92\u4f17\u770b\u89c1\u4e86\u5389\u5357\u661f\uff0c\u66f4\u662f\u60ca\u5947\u3002\u4ed6\u4eec\u660e\u660e\u542c\u5f97\u201c\u6559\u4e3b\u201d\u5728\u5730\u9053\u4e2d\u547c\u6551\u7684\u58f0\u97f3\uff0c\u90a3\u6247\u77f3\u95e8\u4e5f\u8fd8 \u672a\u66fe\u51ff\u5f00\uff0c\u4e0d\u89e3\u4f55\u4ee5\u201c\u6559\u4e3b\u201d\u5ffd\u7136\u4f1a\u4ece\u5916\u9762\u56de\u6765\u3002 \u3000\u3000\u674e\u6566\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u624d\u662f\u4f60\u4eec\u7684\u771f\u6559\u4e3b\uff0c\u5982\u4eca\u6c34\u843d\u77f3\u51fa\uff0c\u4f60\u4eec\u53ef\u76f8\u4fe1\u6211\u7684\u8bf4\u8bdd\u4e86\u5427\uff1f\u201d \u3000\u3000\u4e0e\u674e\u6566\u8bf4\u8bdd\u7684\u90a3\u4eba\uff0c\u662f\u5929\u9b54\u6559\u7684\u8001\u4eba\uff0c\u5bf9\u5389\u5357\u661f\u7236\u6bcd\u6700\u662f\u5fe0\u5fc3\uff0c\u4e0d\u8fc7\u4ed6\u5374\u662f\u672a\u66fe\u89c1\u8fc7\u5389 \u5357\u661f\u7684\u3002\u6b64\u65f6\u770b\u89c1\u5389\u5357\u661f\u548c\u90a3\u4e2a\u5047\u6559\u4e3b\u9762\u8c8c\u751a\u4e3a\u76f8\u4f3c\uff0c\u4f46\u4ed4\u7ec6\u770b\u65f6\uff0c\u53c8\u4f3c\u4e4e\u6709\u70b9\u4e0d\u50cf\uff0c\u800c\u4e14 \u88c5\u675f\u4e5f\u4e0d\u4e00\u6837\uff0c\u4e0d\u7981\u60ca\u7591\u4e0d\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u60c5\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u4f60\u662f\u5fe0\u5b57\u5802\u7684\u526f\u9999\u4e3b\u97e9\u6b63\u8fbe\u5427\uff1f\u5f53\u5e74\u5bb6\u7236\u5728\u5180\u5357\u8d1f\u4f24\uff0c\u591a\u5f97\u4f60\u62a4\u9001\u4ed6\u56de \u5c71\uff0c\u5bb6\u7236\u65f6\u5e38\u548c\u6211\u63d0\u8d77\u4f60\u7684\u3002\u201d \u3000\u3000\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u97e9\u6b63\u8fbe\u4ece\u672a\u5411\u522b\u4eba\u8bf4\u8fc7\uff0c\u4ed6\u5728\u5929\u9b54\u6559\u7684\u804c\u4f4d\uff0c\u4e5f\u662f\u53ea\u6709\u51e0\u4e2a\u65e7\u4eba\u77e5\u9053\uff0c\u90a3\u4e2a \u5047\u5192\u5389\u5357\u661f\u7684\u201c\u6559\u4e3b\u201d\uff0c\u5374\u53ea\u77e5\u9053\u4ed6\u662f\u8d44\u5386\u751a\u6df1\u7684\u65e7\u4eba\u800c\u5df2\u3002 \u3000\u3000\u97e9\u6b63\u8fbe\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u679c\u7136\u662f\u6211\u5c11\u4e3b\u4eba\uff01\u90a3\u4e48\uff0c\u90a3\u4e2a\u6559\u4e3b\u662f\u5047\u7684\u4e86\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u9633\u6d69\u5e03\u4e0b\u7684\u9a97\u5c40\uff0c\u6211\u73b0\u5728\u5c31\u548c\u4f60\u53bb\u628a\u90a3\u4e2a\u5047\u5192\u6211\u7684\u4eba \u63ea\u51fa\u6765\u3002\u8ba9\u4ed6\u4eb2\u53e3\u544a\u8bc9\u4f60\u4eec\u4ed6\u662f\u4ec0\u4e48\u4eba\uff01\u201d \u3000\u3000\u5929\u9b54\u6559\u7684\u4eba\u90fd\u4e0d\u7981\u54d7\u7136\u8d77\u6765\uff0c\u6709\u4e9b\u4eba\u8fd8\u5728\u534a\u4fe1\u534a\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u9633\u6d69\u5e03\u4e0b\u7684\u9a97\u5c40\uff0c\u4e3a \u4ec0\u4e48\u4ed6\u8981\u5982\u6b64\u4f5c\u5f04\u6211\u4eec\uff1f\u201d \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u4e8b\u60c5\u771f\u76f8\uff0c\u8ba9\u9633\u6d69\u81ea\u5df1\u8bf4\u7f62\uff01\u201d \u3000\u3000\u9633\u6d69\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u5f53\u7740\u4f17\u4eba\uff0c\u4e00\u4e94\u4e00\u5341\u5730\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5389\u5357\u661f\u5728\u4ed6\u8bf4\u8bdd\u7684\u65f6\u5019\u548c\u97e9\u6b63\u8fbe\u8fdb\u5165\u5929\u9b54\u6559\u7684\u5185\u9999\u5802\uff0c\u6253\u5f00\u5730\u9053\u7684\u77f3\u95e8\uff0c\u628a\u90a3\u4e2a\u5047\u6559\u4e3b \u653e\u51fa\u6765\u3002\u90a3\u4eba\u5728\u5730\u9053\u4e2d\u9971\u5438\u79fd\u6c14\uff0c\u82e6\u4e0d\u582a\u8a00\uff0c\u5e78\u800c\u8fd8\u672a\u6c14\u7edd\u3002 \u3000\u3000\u5389\u5357\u661f\u628a\u4ed6\u5e26\u5230\u5916\u9762\u7684\u5e7f\u573a\u3002\u8fd9\u65f6\u9633\u6d69\u521a\u521a\u8bf4\u5b8c\u4e86\u4ed6\u6240\u5e72\u7684\u574f\u4e8b\uff0c\u6b63\u597d\u53eb\u90a3\u4e2a\u5047\u6559\u4e3b\u63a5 \u4e0a\u53bb\u8bf4\u660e\u4ed6\u7ed9\u9633\u6d69\u6446\u5e03\u7684\u771f\u76f8\u3002 \u3000\u3000\u4e8b\u60c5\u6c34\u843d\u77f3\u51fa\uff0c\u5929\u9b54\u6559\u5f53\u7136\u662f\u4eba\u4eba\u75db\u9a82\u9633\u6d69\uff0c\u9633\u6d69\u9080\u6765\u7684\u4e00\u5e2e\u4eba\uff0c\u66f4\u662f\u6050\u60e7\u516c\u5b59\u5b8f\u548c\u6c5f \u6d77\u5929\u5c06\u4ed6\u4eec\u4e00\u540c\u6cbb\u7f6a\uff0c\u4e89\u7740\u4e5f\u90fd\u75db\u9a82\u9633\u6d69\uff0c\u628a\u7f6a\u8d23\u63a8\u5230\u9633\u6d69\u8eab\u4e0a\uff0c\u5e0c\u671b\u80fd\u591f\u83b7\u5f97\u8d66\u514d\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u7b11\u9053\uff1a\u201c\u9633\u6d69\u5df2\u7ecf\u53d7\u5230\u5e94\u5f97\u7684\u60e9\u5904\u4e86\uff0c\u7531\u4ed6\u53bb\u5427\u3002\u81f3\u4e8e\u4ed6\u8bf7\u6765\u7684\u670b\u53cb\u561b\u2026\u2026\u201d\u6545 \u610f\u987f\u4e86\u4e00\u987f\uff0c\u90a3\u4e9b\u4eba\u7eb7\u7eb7\u53eb\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u662f\u53d7\u9a97\u7684\uff01\u201d\u201c\u4ed6\u6043\u52bf\u6b3a\u4eba\uff0c\u6211\u4eec\u654c\u4e0d\u8fc7\u4ed6\uff0c\u4e0d\u6562 \u4e0d\u6765\uff01\u201d\u201c\u8fd9\u4ef6\u4e8b\u6211\u4eec\u6beb\u4e0d\u77e5\u60c5\uff0c\u516c\u5b59\u8235\u4e3b\uff0c\u4f60\u9ad8\u62ac\u8d35\u624b\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u60ca\u614c\uff0c\u6211\u4eec\u53ea\u662f\u60e9\u6cbb\u9996\u6076\uff0c\u4e0d\u95ee\u968f\u4ece\u3002\u81f3\u4e8e\u4f60\u4eec\u5e73\u65e5\u7684 \u4f5c\u4e3a\uff0c\u662f\u597d\u662f\u574f\uff0c\u4f60\u4eec\u81ea\u5df1\u53cd\u7701\u53cd\u7701\uff0c\u662f\u5426\u505a\u4e86\u574f\u4e8b\uff0c\u6709\u5219\u6539\u4e4b\uff0c\u65e0\u5219\u52a0\u52c9\u3002\u597d\u4e86\uff0c\u4f60\u4eec\u8981 \u8d70\u7684\u4e5f\u90fd\u53ef\u4ee5\u8d70\u4e86\uff01\u201d\u90a3\u73ed\u90aa\u6d3e\u4eba\u7269\u5982\u5949\u5927\u8d66\uff0c\u767b\u65f6\u56db\u6563\uff0c\u53ea\u6709\u5929\u9b54\u6559\u7684\u4eba\u7559\u4e0b\u6765\u3002\u516c\u5b59\u71d5 \u5bf9\u53f2\u7ea2\u82f1\u6084\u6084\u8bf4\u9053\uff1a\u201c\u4f9d\u6211\u7239\u7239\u5f80\u65e5\u7684\u813e\u6c14\uff0c\u51b3\u4e0d\u4f1a\u5982\u6b64\u5bbd\u5bb9\uff0c\u770b\u6765\u4ed6\u662f\u53d7\u4e86\u6c5f\u5927\u4fa0\u7684\u5f71\u54cd \u4e86\u3002\u201d\u53f2\u7ea2\u82f1\u7b11\u9053\uff1a\u201c\u9010\u6d41\uff0c\u4eba\u5bb6\u79f0\u8d5e\u4f60\u7684\u5e08\u5144\u5462\uff0c\u4f60\u51e1\u4e8b\u90fd\u4e0d\u6b63\u7ecf\uff0c\u5012\u662f\u5e94\u8be5\u5b66\u5b66\u4f60\u7684\u5e08 \u5144\u624d\u597d\u3002\u201d\u5ffd\u7136\u53d1\u73b0\u91d1\u9010\u6d41\u5df2\u7ecf\u4e0d\u5728\u5979\u7684\u8eab\u8fb9\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u5403\u4e86\u4e00\u60ca\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u9010\u6d41\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u516c\u5b59\u71d5\u4e5f\u662f\u597d\u751f\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a \u201c\u6211\u521a\u624d\u8fd8\u770b\u89c1\u4ed6\u5728\u4f60\u7684\u8eab\u65c1\u7684\uff0c\u600e\u7684\u4e00\u8f6c\u773c\u5c31\u4e0d\u89c1\u4e86\uff1f\u4e0d\u8fc7\uff0c\u4f60\u53ef\u4e0d\u7528\u62c5\u5fc3\uff0c\u6587\u9053\u5e84\u6b66\u529f \u5df2\u5e9f\uff0c\u90aa\u6d3e\u4e4b\u4e2d\u8fd8\u6709\u8c01\u7684\u6b66\u529f\u6bd4\u5f97\u4e0a\u91d1\u5c11\u4fa0\uff1f\u6216\u8bb8\u4ed6\u662f\u78b0\u4e0a\u719f\u4eba\uff0c\u4e0e\u670b\u53cb\u53d9\u65e7\u53bb\u4e86\uff0c\u51b3\u4e0d\u4f1a \u6709\u4ec0\u4e48\u610f\u5916\u7684\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u4ed6\u662f\u62f4\u4e0d\u4f4f\u7684\u91ce\u9a6c\u813e\u6c14\uff0c\u6211\u624d\u4e0d\u7ba1\u4ed6\u5462\u3002\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u5fc3\u91cc\u603b\u662f\u6709\u70b9\u7599 \u7629\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u5c31\u662f\u78b0\u4e0a\u4e86\u719f\u4eba\uff0c\u4e5f\u5e94\u8be5\u548c\u6211\u8bf4\u4e00\u58f0\u5440\u3002\u201d\u6b64\u65f6\u9633\u6d69\u548c\u4ed6\u7684\u90a3\u73ed\u515a\u7fbd\u5df2\u7ecf \u8d70\u4e86\uff0c\u4ff1\u7559\u5728\u5c71\u4e0a\u7684\u516d\u5408\u5e2e\u3001\u7ea2\u7f28\u4f1a\u548c\u5929\u9b54\u6559\u4e09\u65b9\u9762\u7684\u4eba\u8fd8\u6709\u4e00\u5343\u591a\u4eba\uff0c\u53f2\u7ea2\u82f1\u7528\u773c\u5149\u641c \u7d22\uff0c\u60f3\u5728\u4eba\u4e1b\u4e4b\u4e2d\u53d1\u73b0\u91d1\u9010\u6d41\uff0c\u8c08\u4f55\u5bb9\u6613\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u9053\u5c3d\u4e86\u9633\u6d69\u7684\u515a\u7fbd\u4e4b\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u5357\u661f\u8001\u5f1f\uff0c\u5929\u9b54\u6559\u7684\u4e8b\u60c5\u6211\u53ef\u4e0d\u4fbf\u8d8a\u4fce\u4ee3\u75b1 \u4e86\u3002\u201d \u3000\u3000\u97e9\u6b63\u8fbe\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5c11\u6559\u4e3b\uff0c\u6211\u4eec\u90fd\u662f\u51b2\u7740\u4f60\u624d\u56de\u5230\u5f82\u5f95\u5c71\u7684\uff0c\u60f3\u4e0d\u5230\u4e0a\u4e86\u9633\u6d69\u8fd9\u8001\u8d3c \u7684\u5f53\u3002\u5982\u4eca\u5047\u7684\u8d76\u8dd1\u4e86\uff0c\u771f\u7684\u81ea\u5f53\u5373\u4f4d\u3002\u5c11\u6559\u4e3b\uff0c\u91cd\u5f00\u9999\u5802\uff0c\u7ee7\u65e5\u6559\u4e3b\uff0c\u4f60\u53ef\u662f\u4e49\u4e0d\u5bb9\u8f9e \u5566\uff01\u201d\u4e00\u547c\u767e\u5e94\uff0c\u5929\u9b54\u6559\u7684\u65e7\u4eba\u90fd\u8868\u793a\u62e5\u62a4\u3002 \u3000\u3000\u5389\u5357\u661f\u9053\uff1a\u201c\u5404\u4f4d\u76db\u60c5\u53ef\u611f\uff0c\u4f46\u8bf7\u542c\u6211\u4e00\u8a00\u3002\u5bb6\u7236\u4e8c\u5341\u5e74\u524d\uff0c\u9075\u91d1\u5927\u4fa0\u4e4b\u5631\uff0c\u5173\u95ed\u9999 \u5802\uff0c\u5982\u4eca\u53c8\u4f55\u5fc5\u591a\u6b64\u4e00\u4e3e\uff1f\u518d\u8bf4\u6211\u5e74\u8f7b\u8bc6\u6d45\uff0c\u5fb7\u8584\u80fd\u9c9c\uff0c\u4e5f\u4e0d\u914d\u505a\u5404\u4f4d\u7684\u6559\u4e3b\u3002\u201d \u3000\u3000\u97e9\u6b63\u8fbe\u8bf4\u9053\uff1a\u201c\u6b64\u4e00\u65f6\uff0c\u5f7c\u4e00\u65f6\u3002\u5f53\u5e74\u91d1\u5927\u4fa0\u529d\u6559\u4e3b\u89e3\u6563\u672c\u6559\uff0c\u8fd9\u662f\u56e0\u4e3a\u672c\u6559\u9f99\u86c7\u6df7 \u6742\uff0c\u90aa\u6b63\u96be\u5206\uff0c\u6050\u6015\u4f1a\u53d7\u4eba\u5229\u7528\u7684\u7f18\u6545\u3002\u5982\u4eca\u90a3\u4e9b\u574f\u4eba\u6b7b\u7684\u6b7b\u4e86\uff0c\u6563\u7684\u6563\u4e86\uff0c\u672a\u6b7b\u7684\u4e5f\u65e9\u5df2 \u53e6\u8c0b\u2018\u51fa\u8def\u2019\u53bb\u4e86\u3002\u6211\u4eec\u8fd9\u73ed\u4eba\u90fd\u662f\u4e3a\u4e86\u6000\u5ff5\u6545\u4e3b\uff0c\u624d\u56de\u6765\u5411\u5c11\u4e3b\u6548\u5fe0\u7684\uff0c\u6211\u4e0d\u6562\u8bf4\u5728\u6211\u4eec \u91cc\u9762\u6ca1\u6709\u4e00\u4e2a\u574f\u4eba\uff0c\u4f46\u5982\u7edd\u5bf9\u662f\u6b63\u591a\u90aa\u5c11\u3002\u6211\u4eec\u4e4b\u4e2d\uff0c\u8fd8\u6709\u8bb8\u591a\u4eba\u662f\u5e26\u4e86\u5b50\u5f1f\u6765\u7684\uff0c\u5c11\u6559 \u4e3b\uff0c\u4f60\u53ef\u4e0d\u80fd\u8f9c\u8d1f\u4e86\u4ed6\u4eec\u7684\u597d\u610f\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u751a\u662f\u4e3a\u96be\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5012\u6709\u4e2a\u4e24\u5168\u4e4b\u7b56\uff0c\u5e0c\u671b\u5404\u4f4d\u8003\u8651\uff0c\u76ee\u524d\u5728\u6b64\u7684 \u7ea2\u7f28\u4f1a\u4e0e\u516d\u5408\u5e2e\uff0c\u6b63\u662f\u6c5f\u6e56\u4e0a\u6700\u5927\u7684\u4e24\u4e2a\u5e2e\u4f1a\uff0c\u7ea2\u7f28\u4f1a\u884c\u4fa0\u4ed7\u4e49\uff0c\u4eba\u6240\u5171\u77e5\uff0c\u4e0d\u5fc5\u591a\u8bf4\u3002\u516d \u5408\u5e2e\u5982\u4eca\u7531\u53f2\u5973\u4fa0\uff0c\u65b0\u4efb\u5e2e\u4e3b\uff0c\u4e5f\u662f\u4e00\u4e2a\u5149\u660e\u6b63\u5927\u7684\u5e2e\u4f1a\u4e86\u3002\u5404\u4f4d\u82e5\u662f\u6709\u5fc3\u91cc\u7aef\u2018\u6d77\u5e95\u201d\u5927 \u53ef\u5206\u522b\u6295\u5165\u8fd9\u4e24\u5927\u5e2e\u4f1a\u4e4b\u4e2d\uff0c\u4f55\u987b\u91cd\u8d77\u7089\u7076\uff1f\u201d \u3000\u3000\u5929\u9b54\u6559\u4f17\u4eba\u89c1\u5389\u5357\u661f\u575a\u8f9e\u6559\u4e3b\u4e4b\u4efb\uff0c\u800c\u4e14\u8bf4\u7684\u4e5f\u662f\u6b63\u7406\uff0c\u5546\u8ba8\u4e4b\u540e\uff0c\u4e5f\u90fd\u8868\u793a\u540c\u610f\u4e86\u3002 \u97e9\u6b63\u8fbe\u9053\uff1a\u201c\u4eca\u65e5\u559c\u4e8b\u91cd\u91cd\uff0c\u96be\u5f97\u5404\u4f4d\u5149\u4e34\uff0c\u5929\u9b54\u6559\u5f1f\u5144\u4e5f\u6709\u4e86\u5f52\u5bbf\uff0c\u8bf7\u8ba9\u6211\u4eec\u7a0d\u5c3d\u5730\u4e3b\u4e4b \u8c0a\u3002\u201d\u4e8e\u662f\u4f17\u4eba\u90fd\u8fdb\u5165\u5929\u9b54\u6559\u7684\u603b\u8235\uff0c\u53c2\u52a0\u97e9\u6b63\u8fbe\u6240\u8bbe\u7684\u5e86\u529f\u5bb4\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u6b63\u8981\u53bb\u627e\u5bfb\u91d1\u9010\u6d41\uff0c\u5ffd\u6709\u51e0\u4e2a\u6c49\u5b50\u8d70\u6765\u5411\u5979\u884c\u793c\uff0c\u539f\u6765\u5c31\u662f\u90a3\u5929\u5728\u534e\u5c71\u6e05\u98ce\u89c2\u6240 \u78b0\u89c1\u7684\u90a3\u4e94\u4e2a\u4eba\u3002\u90a3\u5929\u91d1\u3001\u53f2\u4e8c\u4eba\u6b63\u534e\u5c71\u63a2\u8bbf\u534e\u5c71\u533b\u9690\u7684\u5f1f\u5b50\u6f31\u77f3\u9053\u4eba\uff0c\u4e0d\u6599\u6f31\u77f3\u9053\u4eba\u5df2\u7ecf \u7ed9\u9633\u6d69\u6d3e\u4eba\u6bd2\u6b7b\uff0c\u8fd9\u4e94\u4e2a\u4eba\u5206\u5c5e\u4e94\u4e2a\u5e2e\u4f1a\uff0c\u4ed6\u4eec\u7684\u5e2e\u4e3b\u56e0\u4e3a\u62d2\u7edd\u5929\u9b54\u6559\u65b0\u6559\u4e3b\u5373\u4f4d\u7684\u89c2\u793c\u9080 \u8bf7\uff0c\u4e5f\u90fd\u7ed9\u9633\u6d69\u7684\u4eba\u6697\u4e2d\u4e0b\u6bd2\uff0c\u5f04\u5f97\u534a\u6b7b\u4e0d\u6d3b\uff0c\u8fd9\u4e94\u4e2a\u4eba\u8bf7\u4ed6\u4eec\u7684\u5e2e\u4e3b\u4e0a\u5c71\u6c42\u533b\uff0c\u6070\u5de7\u4e0e \u91d1\u3001\u53f2\u4e8c\u4eba\u76f8\u9047\u3002\u8fd9\u624d\u63ed\u53d1\u4e86\u6709\u4eba\u5047\u5192\u5389\u5357\u661f\u4e4b\u4e8b\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u606d\u606d\u656c\u656c\u7684\u5411\u53f2\u7ea2\u82f1\u65bd\u793c\uff0c\u53f2\u7ea2\u82f1\u53ea\u597d\u5411\u4ed6\u4eec\u53d9\u8bdd\u3002\u95ee\u660e\u6765\u610f\uff0c\u59cb\u77e5\u4ed6\u4eec\u662f\u542c\u5f97\u98ce \u58f0\uff0c\u8d77\u6765\u76f8\u52a9\uff0c\u5e76\u6765\u6c42\u533b\u7684\u3002 \u3000\u3000\u4e3a\u9996\u7684\u957f\u7ec3\u5e2e\u5e2e\u4e3b\u4e4b\u5f1f\u5b59\u767e\u5bff\u8bf4\u9053\uff1a\u201c\u90a3\u5929\u6211\u4eec\u542c\u5f97\u53f2\u59d1\u5a18\u548c\u91d1\u5c11\u4fa0\u8981\u6765\u5f82\u5f95\u5c71\u627e\u90a3\u4e2a \u5192\u540d\u7684\u5047\u6559\u4e3b\u7b97\u5e10\uff0c\u6211\u4eec\u4e00\u5411\u542c\u4ee4\u8d35\u5e2e\uff0c\u5982\u4eca\u53f2\u59d1\u5a18\u505a\u4e86\u5e2e\u4e3b\uff0c\u5c3d\u6539\u8fc7\u53bb\u7684\u82db\u89c4\uff0c\u6211\u4eec\u66f4\u662f \u611f\u6fc0\u4e0d\u5c3d\u3002\u56e0\u6b64\uff0c\u6211\u4eec\u867d\u7136\u660e\u77e5\u5e2e\u4e0d\u4e86\u5fd9\uff0c\u4e5f\u8be5\u6765\u6447\u65d7\u5450\u558a\uff0c\u53f2\u59d1\u5a18\u90a3\u5929\u53c8\u4f3c\u4e4e\u8bf4\u8fc7\uff0c\u8d35\u5e2e \u6709\u4e00\u4f4d\u526f\u5e2e\u4e3b\u80fd\u591f\u89e3\u5929\u9b54\u6559\u4e4b\u6bd2\uff0c\u6211\u4eec\u7684\u5e2e\u4e3b\u4e1a\u5df2\u6bd2\u53d1\uff0c\u53ea\u6015\u96be\u4ee5\u62d6\u5ef6\uff0c\u662f\u4ee5\u6211\u4eec\u53ea\u597d\u5c06\u5e2e \u4e3b\u62a4\u9001\u6765\u6b64\uff0c\u8bf7\u53f2\u59d1\u5a18\u5141\u51c6\u8d35\u5e2e\u7684\u674e\u526f\u5e2e\u4e3b\u4e3a\u6211\u4eec\u7684\u5e2e\u4e3b\u533b\u6cbb\uff0c\u52a9\u62f3\u4e3a\u540d\uff0c\u6c42\u533b\u662f\u5b9e\uff0c\u4f46\u8fd9 \u4efd\u4eba\u60c5\uff0c\u5374\u662f\u53f2\u7ea2\u82f1\u4e50\u610f\u505a\u7684\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u8d35\u5e2e\u7684\u5e2e\u4e3b\u5728\u54ea\u513f\uff1f\u201d\u5b59\u767e\u5bff\u9053\uff1a\u201c\u591a\u8499\u5929\u9b54\u6559\u7684\u97e9\u8001\u524d\u8f88\u7167\u6599\uff0c\u5982\u4eca\u6b63 \u5728\u9759\u5ba4\u6b47\u606f\uff0c\u53ea\u7b49\u53f2\u5e2e\u4e3b\u65bd\u6069\u4e86\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u5b59\u8235\u4e3b\u8a00\u91cd\u4e86\uff0c\u8fd9\u662f\u5e94\u8be5\u7684\u3002\u201d\u5f53\u4e0b\u53eb\u674e\u6566\u8fc7\u6765\uff0c\u4e0e\u4ed6\u4eec\u76f8\u89c1\u3002\u5389\u5357\u661f \u9053\uff1a\u201c\u674e\u5927\u54e5\uff0c\u6211\u7ed9\u4f60\u5e2e\u5fd9\u3002\u201d\u90a3\u4e9b\u4eba\u77e5\u9053\u4ed6\u662f\u5929\u9b54\u6559\u6559\u4e3b\u4e4b\u5b50\uff0c\u89e3\u6bd2\u7684\u672c\u9886\u6599\u60f3\u6bd4\u674e\u6566\u66f4 \u4e3a\u9ad8\u660e\uff0c\u5747\u5927\u559c\u8fc7\u671b\u3002 \u3000\u3000\u8bf4\u8d77\u90a3\u5929\u7684\u4e8b\u60c5\uff0c\u8fd9\u4e9b\u4eba\u4e0d\u514d\u8981\u95ee\u53ca\u91d1\u9010\u6d41\u3002\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u60f3\u627e\u4ed6\u5462\uff0c\u4e5f\u4e0d\u77e5\u4ed6 \u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d\u6b64\u65f6\u5df2\u662f\u65e5\u5f71\u897f\u659c\uff0c\u5c06\u8fd1\u9ec4\u660f\u7684\u65f6\u5206\uff0c\u6c5f\u6d77\u5929\u89c1\u5e08\u5f1f\u5c1a\u672a\u56de\u6765\uff0c\u4e5f\u662f\u4e0d\u7981\u60ca \u7591\u4e0d\u5b9a\uff0c\u4e8e\u662f\u5c31\u548c\u53f2\u7ea2\u82f1\u4e00\u540c\u51fa\u53bb\u627e\u5bfb\u3002\u90a3\u4e94\u4e2a\u4eba\u548c\u516d\u5408\u5e2e\u7684\u5934\u76ee\u4e5f\u90fd\u8ddf\u7740\u51fa\u53bb\uff0c\u5e2e\u5fd9\u4ed6 \u4eec\uff0c\u5206\u5934\u627e\u5bfb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5230\u54ea\u91cc\u53bb\u4e86\u5462\uff1f\u539f\u6765\u4ed6\u5728\u9633\u6d69\u90a3\u73ed\u515a\u7fbd\u4e4b\u4e2d\uff0c\u53d1\u73b0\u4e86\u4e00\u4e2a\u76f8\u8bc6\u7684\u4eba\uff0c\u8fd9\u4e2a\u4eba\u662f\u5c01 \u5999\u5ae6\u7684\u7236\u4eb2\u5c01\u5b50\u8d85\u3002 \u3000\u3000\u5728\u90a3\u4e9b\u4eba\u7eb7\u7eb7\u4e0b\u5c71\u7684\u65f6\u5019\uff0c\u91d1\u9010\u6d41\u770b\u89c1\u4e00\u4e2a\u4eba\u6df7\u5728\u4eba\u4e1b\u4e4b\u4e2d\u95ea\u95ea\u7f29\u7f29\u7684\u5411\u540e\u5c71\u9003\u53bb\uff0c\u8fd9 \u4eba\u62c9\u8d77\u62ab\u98ce\uff0c\u7f69\u8fc7\u5934\u90e8\uff0c\u4f46\u4ece\u4ed6\u7684\u80cc\u5f71\uff0c\u91d1\u9010\u6d41\u9690\u7ea6\u8fd8\u53ef\u4ee5\u770b\u5f97\u51fa\u662f\u8c01\u3002 \u3000\u3000\u91d1\u9010\u6d41\u60f3\u8d77\u66ff\u79e6\u5143\u6d69\u505a\u5a92\u4e4b\u4e8b\uff0c\u6b64\u65f6\u53d1\u73b0\u4e86\u5c01\u5b50\u8d85\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6b64 \u4e8b\u5c1a\u672a\u6709\u4e2a\u4ea4\u4ee3\uff0c\u5c01\u5b50\u8d85\u53ef\u662f\u6765\u5f97\u6b63\u597d\uff01\u4ed6\u53c8\u662f\u8428\u798f\u9f0e\u7684\u65e7\u5c5e\uff0c\u66fe\u7ecf\u505a\u8fc7\u5927\u5185\u4f8d\u536b\u7684\u3002\u8bf4\u4e0d \u5b9a\u6b64\u6765\u6216\u8bb8\u8fd8\u6709\u522b\u7684\u9634\u8c0b\uff0c\u6211\u5012\u662f\u4e0d\u80fd\u4e0d\u627e\u4ed6\u95ee\u4e2a\u660e\u767d\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u5c01\u5b50\u8d85\u5df2\u7ecf\u8d70\u5f97\u8fdc\u4e86\uff0c\u91d1\u9010\u6d41\u4e0d\u4fbf\u58f0\u5f20\uff0c\u7acb\u5373\u8ffd\u53bb\u3002\u4ed6\u8f7b\u529f\u8d85\u5999\u4e4b\u6781\uff0c\u662f\u4ee5\u8fde\u5728\u8eab \u8fb9\u7684\u53f2\u7ea2\u82f1\u4e5f\u6ca1\u53d1\u89c9\u3002 \u3000\u3000\u8f6c\u8fc7\u4e00\u4e2a\u5c71\u5773\uff0c\u53ea\u89c1\u5c01\u5b50\u8d85\u548c\u4e24\u4e2a\u4eba\u540c\u5728\u4e00\u8d77\uff0c\u4f4e\u4e0b\u5934\u6765\u5c0f\u58f0\u8bf4\u8bdd\uff0c\u5374\u542c\u4e0d\u5230\u4ed6\u8bf4\u4ec0 \u4e48\u3002\u9633\u6d69\u7684\u515a\u7fbd\u90fd\u662f\u5411\u5c71\u4e0b\u8dd1\u7684\uff0c\u53ea\u6709\u8fd9\u4e09\u4e2a\u4eba\u5411\u540e\u5c71\uff0c\u4f3c\u4e4e\u662f\u4e0d\u613f\u548c\u90a3\u4e9b\u4eba\u540c\u884c\u3002 \u3000\u3000\u91d1\u9010\u6d41\u770b\u6e05\u695a\u4e86\u5c01\u5b50\u8d85\uff0c\u7acb\u5373\u4f7f\u51fa\u201c\u71d5\u5b50\u4e09\u6284\u6c34\u201d\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u4e00\u4e2a\u8d77\u4f0f\uff0c\u5230\u4e86\u5c01\u5b50\u8d85 \u540e\u9762\uff0c\u4f38\u638c\u5411\u4ed6\u80a9\u5934\u62cd\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u8bb0\u5f97\u8d77\u6211\u8fd9\u4e2a\u5a92\u4eba\u4e48\uff1f\u4f60\u7684\u5973\u513f\u5c31\u8981\u51fa\u5ac1\u4e86\uff0c\u4f60\u8fd8 \u6ca1\u6709\u8c22\u5a92\u5462\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8fd9\u4e00\u638c\u62cd\u4e0b\uff0c\u638c\u52bf\u5df2\u662f\u628a\u5c01\u5b50\u8d85\u8eab\u5f62\u7f69\u4f4f\uff0c\u4e0d\u8bba\u4ed6\u5982\u4f55\u8eb2\u95ea\uff0c\u90fd\u662f\u96be\u4ee5\u907f\u8fc7\u7ed9\u91d1 \u9010\u6d41\u70b9\u4e2d\u7a74\u9053\u3002 \u3000\u3000\u91d1\u9010\u6d41\u827a\u9ad8\u80c6\u5927\uff0c\u6839\u672c\u6ca1\u6709\u628a\u90a3\u4e24\u4e2a\u4e0e\u5c01\u5b50\u8d85\u540c\u884c\u7684\u4eba\u653e\u5728\u773c\u5185\uff0c\u4ed6\u51c6\u5907\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9 \u8033\u7684\u624b\u6bb5\u70b9\u4e86\u5c01\u5b50\u8d85\u7684\u7a74\u9053\u4e4b\u540e\uff0c\u518d\u770b\u770b\u90a3\u4e24\u4e2a\u4eba\u5982\u4f55\uff0c\u90a3\u4e24\u4eba\u82e5\u6562\u5e72\u6d89\u7684\u8bdd\uff0c\u518d\u5bf9\u4ed8\u4ed6\u4eec \u4e5f\u8fd8\u4e0d\u8fdf\u3002 \u3000\u3000\u54ea\u77e5\u4ed6\u8fd9\u4e00\u638c\u62cd\u4e0b\uff0c\u5ffd\u547c\u5f97\u540e\u9762\u90a3\u4e2a\u6c49\u5b50\u4e00\u58f0\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u4e2a\u59d3\u91d1\u7684\u5c0f\u5b50\uff0c\u4f60\u4e5f\u672a \u514d\u592a\u7316\u72c2\u4e86\u3002\u516c\u5b59\u5b8f\u90fd\u8ba9\u6211\u4eec\u8d70\u4e86\uff0c\u4f60\u5374\u8981\u6765\u622a\u4eba\u3002\u597d\uff0c\u6211\u5012\u8981\u770b\u770b\u4f60\u8fd9\u5c0f\u5b50\u6709\u4f55\u672c\u9886\uff0c\u80c6 \u6562\u76ee\u4e2d\u65e0\u4eba\uff01\u201d \u3000\u3000\u53cc\u638c\u76f8\u4ea4\uff0c\u91d1\u9010\u6d41\u53ea\u89c9\u5f97\u5bf9\u65b9\u7684\u638c\u5fc3\u6709\u5982\u4e00\u5757\u70e7\u7ea2\u7684\u70d9\u94c1\u4e00\u822c\u3002\u767b\u65f6\u70ed\u5f97\u4ed6\u51e0\u4e4e\u900f\u4e0d\u8fc7 \u6c14\u6765\uff0c\u4e0d\u7531\u5f97\u5fc3\u4e2d\u5927\u9a87\uff01 \u3000\u3000\u90a3\u4eba\u53e3\u4e2d\u8bf4\u8bdd\uff0c\u638c\u529b\u5374\u662f\u4e00\u6d6a\u9ad8\u8fc7\u4e00\u6d6a\uff0c\u8fde\u53d1\u51fa\u7684\u638c\u98ce\u90fd\u50cf\u662f\u4ece\u9f13\u98ce\u7089\u4e2d\u55b7\u51fa\u6765\u4f3c\u7684\uff0c \u89e6\u4f53\u5982\u70eb\u3002\u91d1\u9010\u6d41\u63a5\u8fde\u7528\u4e86\u51e0\u62db\u521a\u67d4\u5e76\u6d4e\u7684\u5927\u987b\u5f25\u638c\u5f0f\uff0c\u7adf\u7136\u6446\u8131\u4e0d\u5f00\u3002 \u3000\u3000\u867d\u7136\u6446\u8131\u4e0d\u5f00\uff0c\u4f46\u4e5f\u9010\u6e10\u5316\u89e3\u4e86\u5bf9\u65b9\u7684\u638c\u529b\u3002\u90a3\u4eba\u521a\u521a\u8bf4\u5230\u201c\u6211\u5012\u8981\u770b\u4f60\u8fd9\u5c0f\u5b50\u6709\u4f55\u672c \u9886\u201d\u7684\u201c\u672c\u9886\u201d\u4e8c\u5b57\uff0c\u53ea\u542c\u5f97\u201c\u535c\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u91d1\u9010\u6d41\u4e2d\u6307\u4e00\u5f39\uff0c\u5f39\u4e2d\u4e86\u90a3\u4eba\u638c\u5fc3\u7684\u201c\u52b3\u5bab \u7a74\u201d\u3002 \u3000\u3000\u201c\u52b3\u5bab\u7a74\u201d\u5e76\u975e\u90a3\u4eba\u7684\u547d\u95e8\u8981\u7a74\uff0c\u4f46\u7ed9\u91d1\u9010\u6d41\u5f39\u4e2a\u6b63\u7740\uff0c\u4e5f\u662f\u4e0d\u7531\u5f97\u9661\u7136\u4e00\u9707\uff0c\u6025\u5fd9\u6536 \u638c\u3002\u91d1\u9010\u6d41\u51b7\u7b11\u9053\uff1a\u201c\u6211\u9053\u662f\u4ec0\u4e48\u4eba\uff0c\u539f\u6765\u662f\u4ef2\u5e2e\u4e3b\u7684\u624b\u4e0b\u8d25\u5c06\uff0c\u54fc\uff0c\u6211\u7684\u529f\u592b\u867d\u7136\u6bd4\u4e0d\u4e0a \u4ef2\u5e2e\u4e3b\uff0c\u4f46\u4f60\u7684\u96f7\u795e\u638c\u4e5f\u672a\u5fc5\u5c31\u80fd\u80dc\u5f97\u4e86\u6211\uff01\u201d \u3000\u3000\u539f\u6765\u6b64\u4eba\u540d\u53eb\u6b27\u9633\u575a\uff0c\u4e43\u662f\u6b66\u5b66\u4e16\u5bb6\u6b27\u9633\u4f2f\u548c\u4e4b\u5b50\u3002\u4ed6\u4eec\u7684\u5bb6\u4f20\u7edd\u5b66\u540d\u4e3a\u201c\u96f7\u795e\u62f3\u201d\uff0c \u4e0e\u5b5f\u795e\u901a\u7684\u201c\u4fee\u7f57\u9634\u715e\u529f\u201d\u4e00\u51b7\u4e00\u52bf\uff0c\u5f02\u66f2\u540c\u5de5\u3002\u5341\u5e74\u4e4b\u524d\uff0c\u6b27\u9633\u4f2f\u5728\u90aa\u6d3e\u4e2d\u4e5f\u662f\u58f0\u540d\u4ec5\u6b21 \u4e8e\u5b5f\u795e\u901a\u7684\u4e00\u4e2a\u5927\u9b54\u5934\u3002\u540e\u6765\u6b27\u9633\u4f2f\u4e0e\u5728\u534e\u5c71\u4e0e\u4e10\u5e2e\u7684\u5e2e\u4e3b\u4ef2\u957f\u7edf\u8f83\u6280\uff0c\u7ed9\u4ef2\u957f\u7edf\u5e9f\u4e86\u4ed6\u7684 \u6b66\u529f\u3002\uff08\u4e8c\u4e8b\u8be6\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\uff09\u4e09\u5e74\u524d\u6b27\u9633\u575a\u4e3a\u7236\u62a5\u4ec7\uff0c\u5728\u5f82\u5f95\u5c71\u4e0e\u4ef2\u957f\u7edf\u76f8\u9047\uff0c\u53cc\u65b9\u6076 \u6597\u4e00\u573a\uff0c\u7ed3\u679c\u4ecd\u662f\u6b27\u9633\u575a\u8d25\u4e0b\u9635\u6765\u3002\u90a3\u5929\u6070\u5de7\u91d1\u9010\u6d41\u548c\u79e6\u5143\u6d69\u5230\u5c01\u5b50\u8d85\u5bb6\u91cc\u627e\u4ed6\u5973\u513f\uff0c\u5c01\u5bb6 \u4e5f\u662f\u5728\u5f82\u5f95\u5c71\u4e0a\u79bb\u5929\u9b54\u6559\u65e7\u5740\u4e0d\u8fdc\u7684\uff0c\u662f\u4ee5\u6070\u9022\u5176\u4f1a\uff0c\u76ee\u7779\u4e86\u8fd9\u573a\u6076\u6597\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u662f\u6b66\u6797\u4e2d\u9876\u5c16\u513f\u7684\u4eba\u7269\uff0c\u6b27\u9633\u575a\u8d25\u5728\u4ed6\u7684\u624b\u4e0b\uff0c\u672c\u6765\u4e0d\u7b97\u5f97\u662f\u4ec0\u4e48\u803b\u8fb1\u3002\u4f46\u56e0\u4ed6 \u6781\u4e3a\u81ea\u8d1f\uff0c\u4ed6\u8d25\u7ed9\u4ef2\u957f\u7edf\u4e4b\u65f6\uff0c\u4ef2\u957f\u7edf\u5df2\u7ecf\u662f\u4e2a\u516d\u5341\u5f00\u5916\u7684\u8001\u5934\uff0c\u800c\u4ed6\u5219\u6b63\u5728\u58ee\u5e74\uff0c\u4ed6\u4e3a\u7236 \u62a5\u4ec7\uff0c\u5fd7\u5728\u5fc5\u80dc\uff0c\u662f\u4ee5\u60e8\u8d25\u4e4b\u540e\uff0c\u5f15\u4e3a\u5947\u803b\u5927\u8fb1\uff0c\u6700\u5fcc\u522b\u4eba\u63ed\u4ed6\u75ae\u75a4\u3002 \u3000\u3000\u6b64\u9645\uff0c\u4ed6\u7ed9\u91d1\u9010\u6d41\u70b9\u7740\u4e86\u201c\u52b3\u5bab\u7a74\u201d\uff0c\u867d\u65e0\u5927\u788d\uff0c\u6bd5\u7adf\u4e5f\u662f\u8f93\u4e86\u4e00\u62db\uff0c\u52a0\u4ee5\u53c8\u542c\u4e86\u91d1\u9010 \u6d41\u7684\u51b7\u5632\u70ed\u8bbd\uff0c\u4e0d\u7531\u5f97\u8001\u7f9e\u6210\u6012\uff0c\u201c\u54fc\u201d\u7684\u4e00\u58f0\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u60e7\u6211\u7684\u96f7\u795e\u638c\uff0c\u90a3 \u5c31\u8ba9\u4f60\u518d\u8bd5\u4e00\u8bd5\u5427\uff01\u201d \u3000\u3000\u4e24\u4eba\u518d\u6b21\u4ea4\u950b\uff0c\u6b27\u9633\u575a\u53cc\u638c\u5f00\u53d1\uff0c\u70ed\u6d6a\u56db\u6ea2\u3002\u91d1\u9010\u6d41\u77e5\u9053\u5389\u5bb3\uff0c\u5f53\u4e0b\u907f\u514d\u4e0e\u4ed6\u786c\u62fc\uff0c\u4f7f \u51fa\u201c\u5929\u7f57\u6b65\u6cd5\u201d\uff0c\u7ed5\u8eab\u6e38\u6597\u3002\u4e00\u89c1\u6709\u9699\u53ef\u4e58\uff0c\u4fbf\u4ee5\u8ffd\u98ce\u638c\u5f0f\u8fdb\u88ad\u3002 \u3000\u3000\u8bba\u771f\u5b9e\u7684\u672c\u9886\uff0c\u91d1\u9010\u6d41\u5e76\u4e0d\u5728\u6b27\u9633\u575a\u4e4b\u4e0b\uff0c\u4f46\u5374\u5403\u4e8f\u5728\u548c\u6587\u9053\u5e84\u5148\u6218\u4e86\u4e00\u573a\uff0c\u6b64\u65f6\u867d\u7136 \u8fc7\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u7cbe\u529b\u4ecd\u672a\u5b8c\u5168\u6e10\u590d\u3002\u800c\u6b27\u9633\u575a\u7ecf\u8fc7\u4e86\u4e09\u5e74\u82e6\u7ec3\uff0c\u529f\u529b\u53c8\u6bd4\u6597\u4ef2\u957f\u7edf\u4e4b\u65f6\u9ad8 \u4e86\u8bb8\u591a\uff0c\u6b64\u6d88\u5f7c\u957f\uff0c\u6597\u4e86\u4e00\u4f1a\uff0c\u91d1\u9010\u6d41\u597d\u50cf\u7f6e\u8eab\u5728\u70d8\u7089\u4e4b\u4e2d\uff0c\u4e0d\u7981\u547c\u547c\u5598\u6c14\uff0c\u5927\u6c57\u6dcb\u6f13\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u7ad9\u5728\u4e00\u65c1\u89c2\u6218\uff0c\u597d\u50cf\u6709\u70b9\u60f6\u6050\u4e0d\u5b89\u7684\u6a21\u6837\uff0c\u9891\u9891\u6413\u638c\uff0c\u91d1\u9010\u6d41\u89c1\u4ed6\u6ca1\u6709\u9003\u8d70\uff0c\u5012 \u662f\u89c9\u5f97\u6709\u70b9\u5947\u602a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u7eb5\u4f7f\u6253\u4e0d\u8fc7\u6b27\u9633\u575a\uff0c\u4f46\u6709\u6c5f\u5e08\u5144\u548c\u516c\u5b59\u5b8f\u8001\u524d\u8f88\u5728\u8fd9\u91cc\uff0c \u8fdf\u65e9\u4f1a\u8d76\u6765\u7684\u3002\u5c01\u5b50\u8d85\u65e2\u7136\u5e2e\u4e0d\u4e0a\u6b27\u9633\u575a\u7684\u5fd9\uff0c\u4e3a\u4f55\u4ed6\u4e0d\u8d81\u8fd9\u673a\u4f1a\u9003\u8d70\u5462\uff1f\u201d \u3000\u3000\u8896\u624b\u65c1\u89c2\u7684\u8fd8\u6709\u4e00\u4eba\uff0c\u662f\u4e2a\u4e66\u751f\u88c5\u675f\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u53ea\u89c1\u4ed6\u6298\u6247\u8f7b\u6447\uff0c\u610f\u6001\u6f47\u6d12\uff0c\u770b\u4e86\u4e00 \u4f1a\uff0c\u7b11\u9053\uff1a\u201c\u597d\u70ed\uff0c\u597d\u70ed\uff01\u606d\u559c\u6b27\u9633\u5144\uff0c\u4f60\u7684\u96f7\u795e\u638c\u5df2\u662f\u5927\u529f\u544a\u6210\uff0c\u5927\u80dc\u4ee4\u5c0a\u5f53\u5e74\u4e86\uff01\u201d \u3000\u3000\u6b27\u9633\u575a\u542c\u4ed6\u4e00\u8d5e\uff0c\u5927\u4e3a\u5f97\u610f\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6276\u6851\u5c9b\u6b66\u529f\u7edd\u4e16\uff01\u533a\u533a\u8fd9\u70b9\u5fae\u672b\u4e4b\u6280\uff0c\u600e\u5f53 \u5f97\u725f\u5144\u8c2c\u8d5e\uff0c\u4e0d\u8fc7\u7528\u6765\u5bf9\u4ed8\u8fd9\u5c0f\u5b50\u5927\u7ea6\u8fd8\u53ef\u4ee5\u53d6\u80dc\u7f62\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5403\u4e86\u4e00\u60ca\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u542c\u8bf4\u6276\u6851\u5c9b\u7684\u6b66\u529f\u4e45\u5df2\u5931\u4f20\uff0c\u600e\u7684\u53c8\u94bb\u51fa\u4e86\u8fd9\u4e2a\u59d3\u725f\u7684 \u6c49\u5b50\uff1f\u96be\u9053\u4ed6\u7adf\u662f\u725f\u6ca7\u6d6a\u7684\u540e\u4ee3\u5b50\u5b59\u4e48\uff1f\u201d \u3000\u3000\u539f\u6765\u6276\u6851\u5c9b\u8fd9\u4e00\u6d3e\u6b66\u529f\u6e90\u8fdc\u6d41\u957f\uff0c\u59cb\u7956\u662f\u5510\u4ee3\u7684\u866c\u9aef\u5ba2\u3002\u5176\u65f6\u5929\u4e0b\u5927\u4e71\uff0c\u866c\u9aef\u5ba2\u672c\u6709\u9010 \u9e7f\u4e2d\u539f\u4e4b\u5fc3\uff0c\u540e\u6765\u89c1\u4e86\u5510\u5927\u5b97\u674e\u4e16\u6c11\uff0c\u4e3a\u674e\u4e16\u6c11\u7684\u6c14\u5ea6\u6240\u6151\u670d\uff0c\u4e0d\u6562\u4e0e\u674e\u4e16\u6c11\u4e89\u9738\uff0c\u9042\u8fdc\u8d70 \u6276\u6851\uff0c\u81ea\u7acb\u4e3a\u738b\u3002\u866c\u9aef\u5ba2\u4f20\u7ed9\u725f\u6ca7\u6d6a\uff0c\u4e5f\u662f\u5510\u4ee3\u4e00\u4f4d\u9f0e\u9f0e\u6709\u540d\u7684\u6b66\u5b66\u5b97\u5e08\uff0c\u4e0e\u5f53\u65f6\u7684\u7a7a\u7a7a \u513f\u3001\u94c1\u6469\u52d2\u4e8c\u4eba\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u9f0e\u8db3\u79f0\u96c4\uff08\u4e8b\u8be6\u62d9\u8457\u300a\u5927\u5510\u6e38\u4fa0\u4f20\u300b\uff09\uff0c\u725f\u6ca7\u6d6a\u4e4b\u540e\uff0c\u7ecf\u8fc7\u6765\u3001 \u5143\u3001\u660e\u3001\u6e05\u56db\u4e2a\u671d\u4ee3\uff0c\u4e5f\u4e0d\u77e5\u5728\u4ec0\u4e48\u65f6\u5019\u65e9\u5df2\u5931\u4f20\u4e86\u3002\u8bb8\u591a\u5e74\u524d\uff0c\u91d1\u9010\u6d41\u4e4b\u7236\u91d1\u4e16\u9057\u5230\u8fc7\u6276 \u6851\u5c9b\uff0c\u60f3\u627e\u725f\u5bb6\u7684\u540e\u4eba\uff0c\u4f46\u4e5f\u6ca1\u6709\u627e\u7740\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6697\u81ea\u60f3\u9053\uff1a\u201c\u866c\u9aef\u5ba2\u3001\u725f\u6ca7\u6d6a\u4e0d\u4f46\u662f\u5f53\u65f6\u7684\u5927\u4fa0\uff0c\u4e5f\u662f\u540e\u4e16\u666f\u4ef0\u7684\u6b66\u5b66\u5b97\u5e08\uff0c\u8fd9 \u4eba\u82e5\u7136\u771f\u662f\u6276\u6851\u5c9b\u7684\u4e00\u8109\u6240\u4f20\uff0c\u4ed6\u4e0d\u4e0e\u4fa0\u4e49\u9053\u5f80\u6765\uff0c\u5374\u4e0e\u5996\u90aa\u7ed3\u7eb3\uff0c\u8fd9\u5c82\u4e0d\u662f\u81ea\u6bc1\u5bb6\u98ce\uff1f\u201d \u3000\u3000\u6b27\u9633\u575a\u5f97\u90a3\u4eba\u4e00\u8d5e\uff0c\u6b63\u81ea\u8be6\u6d0b\u5f97\u610f\uff0c\u4e0d\u6599\u90a3\u4eba\u8d5e\u4e86\u4ed6\u4e4b\u540e\uff0c\u8ddf\u7740\u5ffd\u7136\u53c8\u8d5e\u8d77\u4e86\u91d1\u9010\u6d41 \u6765\uff0c\u8bf4\u9053\uff1a\u201c\u513f\u5b50\u5982\u6b64\uff0c\u8001\u5b50\u53ef\u77e5\u3002\u4eba\u4eba\u90fd\u8bf4\u91d1\u4e16\u9057\u7684\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\u3002\u8fd9\u4f4d \u5c0f\u54e5\u4e5f\u5f53\u771f\u4e0d\u6127\u662f\u91d1\u5927\u4fa0\u7684\u513f\u5b50\uff01\u201d \u3000\u3000\u6b27\u9633\u575a\u542c\u4e86\uff0c\u6781\u4e0d\u8212\u670d\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5374\u4e0d\u8bf4\u8bdd\uff0c\u53cc\u638c\u52a0\u7d27\u8fdb\u653b\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u79f0\u8d5e \u8fd9\u5c0f\u5b50\u4e86\u5f97\uff0c\u6211\u5c31\u628a\u4ed6\u6253\u5f97\u72fc\u72c8\u4e0d\u582a\u7ed9\u4f60\u770b\u770b\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5403\u4e8f\u5728\u6076\u6597\u4e4b\u540e\u7cbe\u529b\u5c1a\u672a\u5b8c\u5168\u6062\u590d\uff0c\u5728\u6b27\u9633\u575a\u731b\u653b\u4e4b\u4e0b\uff0c\u867d\u7136\u8fd8\u53ef\u4ee5\u52c9\u5f3a\u5e94\u4ed8\uff0c \u4f46\u5927\u6c57\u6dcb\u6f13\uff0c\u597d\u50cf\u843d\u6c64\u9e21\u4f3c\u7684\uff0c\u4e5f\u7684\u786e\u662f\u6709\u70b9\u72fc\u72c8\u4e0d\u582a\u7684\u6837\u5b50\u3002 \u3000\u3000\u59d3\u725f\u7684\u90a3\u4e2a\u6c49\u5b50\u6447\u4e86\u4e00\u6447\u6247\u5b50\uff0c\u53c8\u9053\uff1a\u201c\u542c\u8bf4\u91d1\u4e16\u9057\u6240\u521b\u7684\u5251\u6cd5\u535a\u91c7\u4f17\u5bb6\u4e4b\u957f\uff0c\u7cbe\u6df1\u535a \u5927\uff0c\u800c\u7384\u94c1\u5b9d\u5251\u53c8\u662f\u5175\u5668\u4e2d\u4e4b\u738b\u3002\u8fd9\u4f4d\u5c0f\u54e5\u4f55\u4ee5\u4e0d\u7528\u5251\u5462\uff1f\u201d \u3000\u3000\u6b27\u9633\u575a\u4e00\u6012\u6536\u638c\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u9010\u6d41\uff0c\u4f60\u4eae\u5251\u5427\uff01\u514d\u5f97\u6709\u4eba\u8bf4\u6211\u662f\u6b3a\u8d1f\u4e86\u4f60\uff01\u201d \u3000\u3000\u672c\u6765\u91d1\u9010\u6d41\u82e5\u7136\u4f7f\u7528\u7384\u94c1\u5b9d\u5251\uff0c\u4fbf\u53ef\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\uff0c\u504f\u504f\u4ed6\u662f\u4e89\u5f3a\u8981\u80dc\u7684\u4eba\uff0c\u59d3\u725f\u7684\u63d0 \u9192\u4ed6\u7528\u5251\uff0c\u4ed6\u5374\u504f\u4e0d\u80af\u7528\u3002\u6b27\u9633\u575a\u638c\u5f0f\u4e00\u6536\uff0c\u4ed6\u7684\u53cc\u638c\u4fbf\u653b\u8fc7\u53bb\uff0c\u559d\u9053\uff1a\u201c\u63a5\u62db\uff01\u201d\u6b27\u9633\u575a \u6012\u9053\uff1a\u201c\u53eb\u4f60\u7528\u5251\uff0c\u4f60\u804b\u4e86\u5417\uff1f\u201d\u91d1\u9010\u6d41\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u7528\u7384\u94c1\u5b9d\u5251\uff0c\u4e5f\u6b63\u662f\u4e3a\u4e86\u907f\u514d\u7ed9\u4eba \u8bf4\u6211\u6b3a\u8d1f\u4f60\u5440\uff01\u4f60\u7528\u4ec0\u4e48\u6211\u5c31\u7528\u4ec0\u4e48\uff0c\u51b3\u4e0d\u5360\u4f60\u4fbf\u5b9c\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7684\u8ffd\u98ce\u638c\u5f0f\u98d8\u5ffd\u83ab\u6d4b\uff0c\u6b27\u9633\u575a\u7ed9\u4ed6\u62a2\u4e86\u5148\u624b\uff0c\u8fd8\u4e0d\u80fd\u4e0d\u8ba4\u771f\u5bf9\u4ed8\u3002\u4ed6\u6068\u4e0d\u5f97\u4e00\u638c \u6253\u6b7b\u91d1\u9010\u6d41\uff0c\u8e81\u6025\u4e4b\u4e0b\uff0c\u53cd\u800c\u7ed9\u91d1\u9010\u6d41\u4e00\u53e3\u6c14\u62a2\u653b\u4e86\u6570\u5341\u62db\u3002 \u3000\u3000\u59d3\u725f\u7684\u6c49\u5b50\u6447\u4e86\u6447\u5934\uff0c\u5fc3\u9053\uff1a\u201c\u597d\u4e2a\u5014\u5f3a\u7684\u5c0f\u5b50\u3002\u201d\u4ed6\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u60c5\u77e5\u91d1\u9010\u6d41 \u867d\u7136\u6682\u65f6\u62a2\u5230\u5148\u624b\uff0c\u4f46\u518d\u6218\u4e0b\u53bb\uff0c\u5fc5\u5b9a\u53c8\u8981\u5403\u4e8f\uff0c\u6b63\u662f\u7981\u4e0d\u4f4f\u53c8\u518d\u8bf4\u9053\uff1a\u201c\u9ad8\u624b\u6bd4\u62fc\uff0c\u5e94\u8be5 \u5404\u5c3d\u6240\u957f\u3002\u96f7\u795e\u638c\u662f\u6b27\u9633\u5144\u7684\u5bb6\u4f20\u7edd\u6280\uff0c\u5927\u987b\u5f25\u5251\u5f0f\u5219\u662f\u91d1\u5bb6\u7684\u5251\u672f\u7cbe\u534e\u3002\u6211\u8bf4\u5440\uff0c\u91d1\u9010\u6d41 \u4f60\u4e0d\u80af\u7528\u5251\uff0c\u4f60\u81ea\u8ba8\u82e6\u5403\u4e0d\u6253\u7d27\uff0c\u4f46\u8fd9\u573a\u6bd4\u6b66\u4e5f\u5c31\u4e0d\u80fd\u7b97\u662f\u516c\u5e73\u4e86\u3002\u4f60\u8fd9\u4e0d\u662f\u6545\u610f\u8981\u4ee4\u6b27\u9633 \u5144\u53d7\u4eba\u803b\u7b11\u5417\uff1f\u8fd9\u5982\u4f55\u4f7f\u5f97\uff01\u201d \u3000\u3000\u6b27\u9633\u575a\u662f\u6781\u8981\u9762\u5b50\u7684\u4eba\uff0c\u4ed6\u521a\u521a\u62a2\u56de\u653b\u52bf\uff0c\u53d7\u4e86\u8fd9\u4eba\u7684\u595a\u843d\uff0c\u4e0d\u7531\u5f97\u9762\u7ea2\u8033\u8d64\uff0c\u5927\u611f\u5c34 \u5c2c\uff0c\u6536\u638c\u4e5f\u4e0d\u662f\uff0c\u4e0d\u6536\u638c\u4e5f\u4e0d\u662f\u3002 \u3000\u3000\u5c31\u5728\u4ed6\u8fdb\u9000\u4e24\u96be\u4e4b\u9645\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6b27\u9633\u575a\u6211\u548c\u4f60\u6bd4\u638c\uff01\u6211\u8981\u770b\u770b\u4f60 \u8fd9\u4e09\u5e74\u6765\u7684\u529f\u592b\u9ad8\u4e86\u51e0\u591a\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4ef2\u5e2e\u4e3b\uff0c\u6211\u521a\u624d\u4e0d\u8fc7\u548c\u8fd9\u53ae\u620f\u800d\u620f\u8981\uff0c\u8fd8\u672a\u771f\u6b63\u51b3\u4e2a\u96cc\u96c4\u5462\uff01\u201d\u539f\u6765\u4ed6 \u662f\u56e0\u89c1\u4ef2\u957f\u7edf\u5e74\u7eaa\u8001\u8fc8\uff0c\u800c\u6b27\u9633\u575a\u7684\u6b66\u529f\u5374\u662f\u5927\u80dc\u5f53\u5e74\uff0c\u6050\u9632\u4ef2\u957f\u7edf\u4e07\u4e00\u4e0d\u654c\uff0c\u635f\u4e86\u82f1\u540d\uff0c \u6545\u6b64\u4e0d\u613f\u4ef2\u957f\u7edf\u66ff\u4ed6\u3002\u4ed6\u8bf4\u8fd9\u51e0\u53e5\u8bdd\u4e5f\u662f\u8bdd\u4e2d\u6709\u8bdd\u7684\uff0c\u65e2\u7136\u662f\u8981\u201c\u771f\u6b63\u51b3\u4e2a\u96cc\u96c4\u201d\uff0c\u5c31\u6709\u85c9 \u53e3\u53ef\u4ee5\u4f7f\u7528\u7384\u94c1\u5b9d\u5251\u4e86\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u662f\u6015\u6211\u8fd9\u51e0\u6839\u8001\u9aa8\u5934\u7ecf\u4e0d\u8d77\u6253\u4e48\uff1f\u4e0d\u59a8\u4e8b\u7684\uff0c\u6211\u6b63\u60f3\u6d3b\u52a8 \u7b4b\u9aa8\u5462\u3002\u8001\u5f1f\uff0c\u4f60\u5c31\u7ad9\u5728\u4e00\u65c1\u7b49\u7740\u77a7\u5427\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4ef2\u957f\u7edf\u5df2\u662f\u62a2\u5230\u4e86\u6b27\u9633\u575a\u9762\u524d\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4f60\u5148\u6253\u4e00\u573a\uff0c\u56e0\u6211\u5e74\u7eaa\u6bd4\u4f60\u5927 \u4e86\u4e00\u500d\uff0c\u4f60\u603b\u4e0d\u80fd\u8bf4\u6211\u5360\u4f60\u7684\u4fbf\u5b9c\u5427\uff01\u201d \u3000\u3000\u6b27\u9633\u575a\u60c5\u77e5\u91d1\u9010\u6d41\u82e5\u7528\u7384\u94c1\u5b9d\u5251\uff0c\u81ea\u5df1\u7edd\u8ba1\u6ca1\u6709\u53d6\u80dc\u7684\u628a\u63e1\uff0c\u4f46\u82e5\u91d1\u9010\u6d41\u4e0d\u7528\u5b9d\u5251\uff0c\u81ea \u5df1\u53c8\u662f\u80dc\u4e4b\u4e0d\u6b66\uff0c\u8fd8\u8981\u7ed9\u90a3\u59d3\u725f\u7684\u803b\u7b11\uff0c\u662f\u4ee5\u4ed6\u6b63\u4e50\u5f97\u8d81\u6b64\u843d\u53f0\u3002\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u8001\u53eb\u5316\u5b50 \u7684\u4e00\u53ea\u811a\u5df2\u662f\u8e0f\u5165\u68fa\u6750\u7684\u4e86\uff0c\u4e09\u5e74\u524d\u6211\u6253\u4ed6\u4e0d\u8fc7\uff0c\u96be\u9053\u73b0\u5728\u8fd8\u6253\u4e0d\u8fc7\u4ed6\uff1f\u201d \u3000\u3000\u6b27\u9633\u575a\u81ea\u5fd6\u53ef\u4ee5\u7a33\u64cd\u80dc\u5238\uff0c\u4fbf\u5373\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8001\u53eb\u5316\uff0c\u4f60\u65e2\u7136\u81ea\u5df1\u613f\u610f\u9001\u6b7b\uff0c\u6211\u53ea\u597d\u6210 \u5168\u4f60\u4e86\u3002\u4e0d\u8fc7\uff0c\u6211\u4e5f\u4e0d\u60f3\u53d6\u4f60\u6027\u547d\uff0c\u5f53\u5e74\u4f60\u6bc1\u4e86\u6211\u7239\u7239\u7684\u6b66\u529f\uff0c\u4eca\u65e5\u53ea\u662f\u4e00\u62a5\u8fd8\u4e00\u62a5\u7f62 \u4e86\uff01\u201d \u3000\u3000\u4ef2\u957f\u7edf\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u5df2\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\uff0c\u4f60\u53ef\u4e0d\u5fc5\u624b\u4e0b\u7559\u60c5\u3002\u4e0d\u8fc7\uff0c\u8c01\u5e9f\u6389\u8c01\u7684\u6b66 \u529f\uff0c\u8fd9\u53ef\u8fd8\u8981\u7b49\u7740\u77a7\u5462\uff01\u201d \u3000\u3000\u4ef2\u957f\u7edf\u6267\u610f\u8981\u4e0a\uff0c\u542c\u4ed6\u8bf4\u5f97\u53c8\u662f\u751a\u6709\u81ea\u4fe1\uff0c\u91d1\u9010\u6d41\u662f\u77e5\u9053\u8fd9\u4f4d\u8001\u524d\u8f88\u7684\u5014\u5f3a\u813e\u6c14\u7684\uff0c\u53ea \u597d\u8ba9\u4ed6\u3002 \u3000\u3000\u6b27\u9633\u575a\u7eb5\u58f0\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u53eb\u5316\uff0c\u4eca\u5929\u6050\u6015\u4e0d\u80fd\u5bb9\u4f60\u7316\u72c2\u4e86\uff01\u597d\uff0c\u90a3\u54b1\u4eec\u5c31\u9a91\u9a74\u8bfb\u5531 \u672c\uff0c\u8d70\u7740\u77a7\u5427\uff01\u201d \u3000\u3000\u6b27\u9633\u575a\u4e00\u638c\u5288\u4e0b\uff0c\u9690\u9690\u631f\u7740\u98ce\u96f7\u4e4b\u58f0\uff0c\u4ef2\u957f\u7edf\u5374\u4f3c\u6f2b\u4e0d\u7ecf\u610f\u7684\u8f7b\u8f7b\u4e00\u638c\u62cd\u51fa\uff0c\u53cc\u638c\u4e00 \u4ea4\uff0c\u5f7c\u6b64\u90fd\u4e0d\u7531\u5f97\u5fc3\u91cc\u6697\u6697\u5403\u60ca\u3002 \u3000\u3000\u6b27\u9633\u575a\u53ea\u9053\u4ef2\u957f\u7edf\u8001\u8fc8\u53ef\u6b3a\uff0c\u4e0d\u6599\u4e00\u7ecf\u63a5\u89e6\uff0c\u53ea\u89c9\u5bf9\u65b9\u7684\u638c\u529b\u67d4\u548c\u4e4b\u6781\uff0c\u4f46\u5374\u50cf\u78b0\u4e0a\u4e86 \u4e00\u56e2\u539a\u539a\u7684\u68c9\u82b1\uff0c\u81ea\u5df1\u7684\u52b2\u529b\u7adf\u662f\u65e0\u4ece\u53d1\u6325\u3002\u8fd9\u624d\u77e5\u9053\u4ef2\u957f\u7edf\u867d\u7136\u5e74\u7eaa\u8001\u8fc8\uff0c\u4f46\u5185\u529f\u5374\u662f\u6bd4 \u4e09\u5e74\u524d\u66f4\u7cbe\u7eaf\u4e86\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u4e5f\u662f\u6697\u6697\u5403\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u53e3\u51fa\u5927\u8a00\uff0c\u529f\u592b\u679c\u7136\u662f\u6bd4\u4e09\u5e74\u524d\u5f3a\u5f97\u591a\u4e86\u3002\u4e00\u767e \u62db\u4e4b\u5185\uff0c\u6211\u662f\u4e00\u5b9a\u53ef\u4ee5\u62b5\u6321\u5f97\u4f4f\u7684\uff0c\u767e\u62db\u4e4b\u5916\uff0c\u8fd9\u53ef\u5c31\u96be\u8bf4\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e0d\u77e5\u4ef2\u957f\u7edf\u5df2\u662f\u7528\u4e0a\u5168\u529b\uff0c\u89c1\u4ed6\u8f7b\u63cf\u6de1\u5199\u7684\u5c31\u5316\u89e3\u4e86\u201c\u96f7\u795e\u638c\u201d\u7684\u731b\u653b\uff0c\u5fc3\u91cc\u6697 \u6697\u4f69\u670d\uff0c\u60f3\u9053\uff1a\u201c\u6bd5\u7adf\u59dc\u662f\u8001\u7684\u8fa3\uff0c\u53ef\u7b11\u6211\u8fd8\u4e3a\u4ed6\u62c5\u5fc3\u5462\u3002\u201d\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684\u77f3\u5934\uff0c\u773c\u5149\u4e00 \u77a5\uff0c\u53ea\u89c1\u5c01\u5b50\u8d85\u4e5f\u6b63\u62ac\u773c\u671b\u4ed6\uff0c\u4f3c\u4e4e\u662f\u6709\u70b9\u8bdd\u8981\u548c\u4ed6\u8bf4\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6b63\u60f3\u8fc7\u53bb\u548c\u4ed6\u8bf4\u8bdd\uff0c\u5ffd\u89c1\u90a3\u59d3\u725f\u7684\u6c49\u5b50\u8f7b\u6447\u6298\u6247\uff0c\u5df2\u662f\u6765\u5230\u9762\u524d\uff0c\u91d1\u9010\u6d41\u6709\u5fc3\u4e0e \u4ed6\u7ed3\u7eb3\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u6b66\u5b66\u9ad8\u6df1\uff0c\u5c0f\u5f1f\u4f69\u670d\u5f97\u7d27\uff0c\u4e0d\u77e5\u6709\u4f55\u6307\u6559\uff1f\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5c01\u5b50 \u8d85\u8fd9\u8001\u5bb6\u4f19\u4f3c\u4e4e\u5e76\u65e0\u9003\u8d70\u4e4b\u610f\uff0c\u8c05\u8dd1\u8c05\u4e5f\u8dd1\u4e0d\u51fa\u6211\u7684\u624b\u5fc3\uff0c\u5f85\u4f1a\u513f\u5411\u4ed6\u95ee\u4e2a\u660e\u767d\uff0c\u4e5f\u8fd8\u4e0d \u8fdf\u3002\u201d \u3000\u3000\u90a3\u59d3\u725f\u7684\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u4fd7\u8bed\u8bf4\uff0c\u65c1\u89c2\u8005\u6e05\uff0c\u6211\u5728\u65c1\u8fb9\u8bf4\u8bf4\uff0c\u5012\u8fd8\u53ef\u4ee5\u3002\u8ba4\u771f\u8f83\u91cf\u8d77\u6765\uff0c \u53ea\u6015\u8fd8\u672a\u5fc5\u662f\u4f60\u8001\u5f1f\u7684\u5bf9\u624b\u5462\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6014\u4e86\u4e00\u6014\uff0c\u4e0d\u77e5\u4ed6\u8bf4\u8fd9\u8bdd\u662f\u4f55\u7528\u610f\uff0c\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u542c\u5f97\u90a3\u59d3\u725f\u7684\u6c49\u5b50\u8ddf\u7740\u8bf4\u9053\uff1a \u201c\u5c31\u4e0d\u77e5\u8001\u5f1f\u6709\u6ca1\u6709\u7cbe\u795e\u518d\u6253\u4e00\u573a\uff1f\u201d \u3000\u3000\u5168\u9010\u6d41\u4e0d\u7531\u5f97\u6c14\u5411\u4e0a\u51b2\uff0c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u4ed6\u662f\u8981\u4f30\u91cf\u6211\uff0c\u4ed6\u672c\u6765\u662f\u4e2a\u6709\u51e0\u5206\u72c2\u50b2\u6c14\u8d28\u7684 \u4eba\uff0c\u6b64\u65f6\u867d\u7136\u5598\u606f\u65b9\u5b9a\uff0c\u6c14\u529b\u4e0d\u52a0\uff0c\u4f46\u4e5f\u4e0d\u7518\u793a\u5f31\uff0c\u7acb\u5373\u8bf4\u9053\uff1a\u201c\u4e45\u4ef0\u6276\u6851\u5c9b\u7684\u6b66\u6797\u7edd\u5b66\uff0c \u6211\u53ea\u9053\u65e9\u5df2\u5931\u4f20\uff0c\u96be\u5f97\u8fd8\u6709\u773c\u798f\u89c1\u5230\uff0c\u6211\u6b63\u60f3\u5411\u9601\u4e0b\u8bf7\u6559\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5c11\u4fa0\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u4e0d\u9519\uff0c\u655d\u7956\u5e08\u866c\u9aef\u5ba2\u7684\u4fee\u4e3a\u7684\u786e\u5f53\u5f97\u2018\u6b66 \u6797\u7edd\u5b66\u2019\u56db\u5b57\uff0c\u4f46\u90a3\u662f\u4e00\u5343\u5e74\u4ee5\u524d\u7684\u4e8b\u60c5\uff0c\u6570\u5341\u4f20\u4e4b\u540e\uff0c\u5c0f\u53ef\u6240\u5f97\uff0c\u53ea\u6015\u5df2\u4e0d\u53ca\u524d\u4eba\u5341\u5206\u4e4b \u4e00\uff0c\u4ee4\u5c0a\u624d\u662f\u5f53\u4e16\u9996\u5c48\u4e00\u6307\u7684\u6b66\u5b66\u5927\u5e08\u5462\u3002\u5c0f\u53ef\u5192\u5473\uff0c\u60f3\u89c1\u8bc6\u89c1\u8bc6\u91d1\u5c11\u4fa0\u5bb6\u4f20\u7684\u5929\u4e0b\u65e0\u53cc\u7684 \u5251\u6cd5\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6599\u60f3\u6b64\u4eba\u7684\u6b66\u529f\u5fc5\u5b9a\u8fdc\u5728\u6b27\u9633\u575a\u4e4b\u4e0a\uff0c\u5f53\u4e0b\u5c31\u4e0d\u5ba2\u6c14\u5730\u62d4\u51fa\u7384\u94c1\u5b9d\u5251\uff0c\u8bf4\u9053\uff1a \u201c\u606d\u656c\u4e0d\u5982\u4ece\u547d\uff0c\u8bf7\u9601\u4e0b\u4eae\u5251\uff01\u201d \u3000\u3000\u90a3\u59d3\u725f\u7684\u6447\u4e86\u4e00\u6447\u6298\u6247\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5c11\u4fa0\u5df2\u7ecf\u6253\u4e86\u4e24\u573a\uff0c\u54b1\u4eec\u867d\u7136\u53ea\u662f\u5f7c\u6b64\u5207\u78cb\uff0c\u6211\u4e5f \u4e0d\u80fd\u5360\u5c11\u4fa0\u7684\u4fbf\u5b9c\u3002\u6211\u5c31\u7528\u8fd9\u67c4\u6247\u5b50\u63a5\u5c11\u4fa0\u51e0\u62db\uff0c\u5e0c\u671b\u5c11\u4fa0\u4e0d\u8981\u8bef\u4f1a\u6211\u662f\u5c0f\u89d1\u4f60\u7684\u672c\u9886\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u521d\u65f6\u7684\u786e\u662f\u6709\u51e0\u5206\u751f\u6c14\uff0c\u4ee5\u4e3a\u4ed6\u662f\u5b58\u5fc3\u8f7b\u89c6\u7684\u81ea\u5df1\uff0c\u4eca\u65e5\u4ed6\u628a\u8bdd\u62a1\u5728\u524d\u5934\u8bf4\u4e86\uff0c \u5012\u662f\u4e0d\u4fbf\u53d1\u4f5c\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u8d25\u7ed9\u4ed6\u4e0d\u6253\u7d27\uff0c\u53ea\u6015\u6298\u4e86\u7239\u7239\u7684\u5a01\u540d\u3002\u6211\u6c14\u529b\u4e0d\u6d4e\uff0c\u4ed7\u7740\u7384\u94c1\u5b9d \u5251\u4e4b\u5229\uff0c\u90a3\u4e5f\u53ea\u662f\u626f\u4e86\u4e2a\u76f4\u3002\u4e0d\u80fd\u8bf4\u662f\u80dc\u4e4b\u4e0d\u6b66\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u65e2\u7136\u53ea\u662f\u5f7c\u6b64\u5207\u78cb\uff0c \u90a3\u54b1\u4eec\u5c31\u70b9\u5230\u5373\u6b62\u5427\u3002\u8bf7\u9601\u4e0b\u8d50\u62db\uff01\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u5ba2\u4e0d\u50ed\u4e3b\uff0c\u8fd8\u662f\u8bf7\u91d1\u5c11\u4fa0\u5148\u884c\u8d50\u62db\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u6027\u60c5\u8c6a\u723d\uff0c\u4e0d\u8010\u70e6\u4e0e\u4ed6\u5a46\u5a46\u5988\u5988\uff0c\u5f53\u4e0b\u4fbf\u9053\uff1a\u201c\u5982\u6b64\u6709\u50ed\u4e86\uff01\u201d\u5530\u7684\u4e00\u5251\u523a\u5c06\u8fc7 \u53bb\u3002 \u3000\u3000\u90a3\u4eba\u6298\u6247\u4e00\u6307\uff0c\u6247\u5934\u8f7b\u8f7b\u4e00\u6309\u5251\u810a\uff0c\u7adf\u7136\u628a\u767e\u65a4\u91cd\u7684\u7384\u94c1\u5b9d\u5251\u7275\u8fc7\u4e00\u8fb9\u3002\u91d1\u9010\u6d41\u5403\u4e86\u4e00 \u60ca\uff0c\u7acb\u5373\u53d8\u62db\uff0c\u5b9d\u5251\u4e00\u4f38\uff0c\u5c06\u4ed6\u7684\u7c98\u9ecf\u4e4b\u52b2\u5316\u89e3\uff0c\u4e00\u62db\u201c\u591c\u53c9\u63a2\u6d77\u201d\uff0c\u6a2a\u524a\u90a3\u4eba\u624b\u8155\u3002\u90a3\u4eba \u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u6298\u6247\u5ffd\u5730\u6307\u5230\u4e86\u91d1\u9010\u6d41\u80f8\u524d\u7684\u201c\u6108\u6c14\u7a74\u201d\uff0c\u7adf\u7136\u4e5f\u662f\u4e00\u62db\u6781\u9ad8\u660e\u7684\u5251 \u6cd5\uff01 \u3000\u3000\u8fd9\u4e00\u62db\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u91d1\u9010\u6d41\u672c\u6765\u4e0d\u662f\u60f3\u548c\u4ed6\u62fc\u547d\u7684\uff0c\u65e2\u7136\u4e0d\u613f\u8f93\u62db\uff0c\u53ea\u597d\u56de\u5251\u906e \u62e6\u3002\u59d3\u725f\u8fd9\u6c49\u5b50\u89c1\u4ed6\u53d8\u62db\u795e\u901f\uff0c\u4e0d\u7981\u53c8\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff01 \u3000\u3000\u91d1\u9010\u6d41\u5374\u662f\u4e0d\u7981\u6697\u6697\u60ed\u6127\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6276\u6851\u5c9b\u7684\u6b66\u529f\u540d\u5782\u540e\u4e16\uff0c\u53d7\u4eba\u7d2f\u4ef0\uff0c\u679c \u7136\u662f\u4e0d\u540c\u51e1\u54cd\uff0c\u53ef\u7b11\u6211\u521a\u624d\u8fd8\u6050\u6015\u80dc\u4e4b\u4e0d\u6b66\u5462\uff0c\u8c01\u77e5\u6211\u7528\u4e86\u7384\u94c1\u5b9d\u5251\uff0c\u7adf\u6597\u4e0d\u8fc7\u4ed6\u4e00\u628a\u6298 \u6247\uff01\u201d \u3000\u3000\u59d3\u725f\u7684\u6c49\u5b50\u4e5f\u662f\u597d\u751f\u4f69\u670d\uff0c\u5fc3\u60f3\uff1a\u201c\u5018\u82e5\u4ed6\u771f\u4e2a\u548c\u6211\u62fc\u547d\u7684\u8bdd\uff0c\u6211\u5373\u4f7f\u4e0d\u81f4\u843d\u8d25\uff0c\u4e5f\u662f \u96be\u4ee5\u5bf9\u4ed8\u7684\u4e86\u3002\u7384\u94c1\u5b9d\u5251\u7684\u5a01\u529b\u56fa\u7136\u662f\u51fa\u4e4e\u6211\u7684\u610f\u6599\u4e4b\u5916\uff0c\u4f46\u4ed6\u5982\u662f\u5267\u6218\u8fc7\u4e24\u573a\u7684\uff0c\u4f9d\u6b64\u770b \u6765\uff0c\u91d1\u4e16\u9057\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\u7684\u540d\u5934\uff0c\u7684\u786e\u662f\u6b8a\u975e\u5e78\u81f3\u4e86\uff01\u201d\u539f\u6765\u8fd9\u4eba\u672c\u6765\u662f\u60f3\u53bb\u627e\u91d1\u4e16\u9057\u6bd4\u8bd5 \u7684\uff0c\u60f3\u4e0d\u5230\u672a\u627e\u7740\u91d1\u4e16\u9057\uff0c\u5374\u5148\u78b0\u4e0a\u4e86\u91d1\u4e16\u9057\u7684\u513f\u5b50\u3002\u5bf9\u65b9\u8fde\u6597\u4e24\u573a\u4e4b\u540e\uff0c\u81ea\u5df1\u4e5f\u4e0d\u8fc7\u7a0d\u7a0d \u5360\u4e86\u4e00\u70b9\u4fbf\u5b9c\uff0c\u4e0d\u89c9\u51b7\u4e86\u534a\u622a\u3002 \u3000\u3000\u4e24\u4eba\u60fa\u60fa\u76f8\u501f\uff0c\u4f46\u4e3a\u4e86\u672c\u95e8\u8363\u8a89\uff0c\u5374\u4e5f\u662f\u8c01\u90fd\u4e0d\u613f\u8f93\u62db\u3002\u91d1\u9010\u6d41\u81ea\u77e5\u6c14\u529b\u4e0d\u8db3\uff0c\u5f53\u4e0b\u4ed7 \u7740\u7384\u94c1\u5b9d\u5251\u4e4b\u5229\uff0c\u5c55\u5f00\u4e86\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u7d27\u7d27\u5c01\u95ed\u95e8\u6237\uff0c\u4e0d\u8ba9\u5bf9\u65b9\u6709\u53ef\u4e58\u4e4b\u673a\u3002\u59d3\u725f\u7684\u6c49\u5b50\u628a \u4e00\u67c4\u5c0f\u5c0f\u7684\u6298\u6247\u4f7f\u5f97\u51fa\u795e\u5165\u5316\uff0c\u65f6\u800c\u5f53\u4f5c\u4e94\u884c\u5251\u4f7f\uff0c\u65f6\u800c\u5f53\u4f5c\u5224\u5b98\u7b14\u7528\uff0c\u4e00\u67c4\u6247\u5b50\uff0c\u7adf\u7136\u53ef \u4ee5\u53d8\u4f5c\u8bb8\u591a\u79cd\u4e0d\u540c\u7684\u5175\u5668\uff0c\u4f46\u867d\u7136\u5982\u6b64\uff0c\u4e00\u65f6\u95f4\u5374\u4e5f\u653b\u4e0d\u7834\u91d1\u9010\u6d41\u4e25\u5bc6\u5f02\u5e38\u7684\u9632\u5fa1\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5c45\u7136\u8fd8\u6709\u4eba\u6562\u5728\u8fd9\u91cc\u6492\u91ce\uff0c\u6211\u5012\u8981\u770b\u770b\u662f\u8c01\uff1f\u539f\u6765\u662f\u4ef2 \u5e2e\u4e3b\u6765\u4e86\uff01\u8fd9\u5c0f\u5b50\u662f\u6b27\u9633\u575a\uff01\u201d\u8ddf\u7740\u4e00\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\uff0c\u4f60\u5feb\u672a\u884c\uff0c\u9010\u6d41\u6050\u6015 \u4e0d\u662f\u8fd9\u4e2a\u4eba\u7684\u5bf9\u624b\u3002\u539f\u672a\u662f\u516c\u5b59\u5b8f\u3001\u53f2\u7ea2\u82f1\u3001\u6c5f\u6d77\u5929\u7b49\u4eba\u8054\u8882\u6765\u5230\uff0c\u516c\u5b59\u5b8f\u9996\u5148\u6ce8\u610f\u7684\u662f\u4ef2 \u957f\u7edf\u4e0e\u6b27\u9633\u575a\u8fd9\u4e00\u5bf9\uff0c\u53f2\u7ea2\u82f1\u5219\u5f53\u7136\u662f\u5173\u5fc3\u91d1\u9010\u6d41\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u62ac\u773c\u5411\u91d1\u9010\u6d41\u8fd9\u8fb9\u671b\u53bb\uff0c\u4e00\u770b\u4e4b\u4e0b\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u5982\u6b64\u4e86\u5f97\uff1f \u770b\u6765\u4e5f\u4e0d\u8fc7\u4e09\u5341\u5de6\u53f3\u5e74\u7eaa\u5427\uff0c\u6211\u82e5\u662f\u5728\u4ed6\u8fd9\u4e2a\u5e74\u7eaa\uff0c\u6050\u6015\u8fd8\u5f53\u771f\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u5462\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u770b\u4e86\u4e5f\u4e0d\u7981\u5567\u5567\u79f0\u5f02\uff0c\u5bf9\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u6b66\u529f\u4e0d\u77e5\u662f\u4ec0\u4e48\u6b66\u6d3e\u7684\uff0c\u4f46\u770b\u6765\u5374 \u4f3c\u4e4e\u5e76\u65e0\u6076\u610f\u3002\u54b1\u4eec\u4e0d\u5fc5\u5fd9\u4e8e\u5e72\u9884\uff0c\u514d\u5f97\u9020\u6210\u65e0\u5fc3\u4e4b\u5931\uff0c\u5f97\u7f6a\u4e86\u670b\u53cb\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u70b9\u5934\u79f0\u662f\uff0c\u5374\u53c8\u8bf4\u9053\uff1a\u201c\u4f46\u6b27\u9633\u575a\u8fd9\u5c0f\u5b50\u53ef\u662f\u548c\u4ef2\u5e2e\u4e3b\u62fc\u547d\u554a\uff01\u54b1\u4eec\u4e0d\u80fd\u4e0d\u7ba1 \u5427\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u53eb\u5316\u548c\u8fd9\u5c0f\u5b50\u73a9\u73a9\uff0c\u516c\u5b59\u8001\u5f1f\uff0c\u4f60\u53ef\u4e0d\u8bb8\u591a\u4e8b\uff01\u201d \u3000\u3000\u6b64\u65f6\u6b27\u9633\u575a\u4e0e\u4ed6\u5df2\u7ecf\u6597\u5230\u767e\u62db\u5f00\u5916\uff0c\u521a\u521a\u6273\u6210\u5e73\u624b\u3002\u4ef2\u957f\u7edf\u56fa\u7136\u662f\u6c14\u529b\u4e0d\u52a0\uff0c\u6b27\u9633\u575a\u7684 \u96f7\u795e\u638c\u751a\u8017\u5143\u6c14\uff0c\u6597\u5230\u4e86\u767e\u62db\u5f00\u5916\uff0c\u4ea6\u5df2\u662f\u6e10\u6e10\u6210\u4e86\u5f3a\u5f29\u4e4b\u672b\u4e86\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u4e0e\u6c5f\u6d77\u5929\u867d\u7136\u53ea\u662f\u8896\u624b\u65c1\u89c2\uff0c\u4f46\u6b27\u9633\u575a\u770b\u89c1\u4ed6\u4eec\u6765\u4e86\u3002\u5374\u662f\u4e0d\u7531\u5f97\u8d8a\u53d1\u5fc3\u614c\u3002\u6fc0 \u6218\u4e2d\u731b\u542c\u5f97\u4ef2\u957f\u7edf\u559d\u58f0\uff1a\u201c\u53bb\uff01\u201d\u53cc\u638c\u76f8\u4ea4\uff0c\u58f0\u5982\u90c1\u96f7\uff0c\u6b27\u9633\u575a\u4e00\u4e2a\u5012\u5934\u7b4b\u6597\uff0c\u7ffb\u51fa\u6570\u4e08\u5f00 \u5916\uff01 \u3000\u3000\u5fe1\u957f\u7edf\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u56de\u53bb\u518d\u7ec3\u5341\u5e74\u5427\uff0c\u4f46\u4e00\u5e74\u4e4b\u540e\uff0c\u8001\u53eb\u5316\u53ea\u6015\u662f\u89c1\u4e0d\u7740\u4f60\u4e86\u3002\u4f46\u613f\u4f60 \u61c2\u5f97\u8001\u53eb\u5316\u9976\u4f60\u7684\u4e00\u756a\u5fc3\u610f\uff0c\u5341\u5e74\u540e\u91cd\u65b0\u505a\u4e2a\u597d\u4eba\u3002\u201d\u539f\u6765\u4ef2\u957f\u7edf\u5df2\u662f\u7528\u201c\u6df7\u5143\u4e00\u6c14\u529f\u201d\u7834 \u4e86\u6b27\u9633\u575a\u7684\u2018\u96f7\u795e\u638c\u201d\uff0c\u6b27\u9633\u575a\u7684\u6b66\u529f\u867d\u672a\u5168\u5e9f\uff0c\u4f46\u8fd9\u6700\u5389\u5bb3\u7684\u201c\u96f7\u795e\u638c\u201d\u82e5\u60f3\u518d\u7ec3\u6210\u529f\uff0c \u81f3\u5c11\u4e5f\u5f97\u5341\u5e74\u4e4b\u540e\u4e86\u3002 \u3000\u3000\u6b27\u9633\u575a\u54ea\u91cc\u8fd8\u6562\u7b54\u8bdd\uff0c\u4e00\u4e2a\u7b4b\u6597\u7ffb\u51fa\u6570\u4e08\u5f00\u5916\uff0c\u7acb\u5373\u4fbf\u4f3c\u4e00\u6e9c\u70df\u5730\u8dd1\u4e86\u3002\u5c01\u5b50\u8d85\u201c\u554a \u5440\u201d\u4e00\u58f0\uff0c\u4e00\u526f\u5931\u9b42\u848b\u9b44\u7684\u6a21\u6837\u671b\u7740\u6b27\u9633\u575a\u8dd1\u4e0b\u5c71\u53bb\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u6253\u7684\u662f\u4ec0\u4e48\u4e3b\u610f\uff0c\u5fc3\u91cc\u4f3c\u4e4e \u6b63\u5728\u72b9\u8c6b\u4e0d\u51b3\uff0c\u4f46\u5374\u6ca1\u6709\u8ddf\u7740\u9003\u8dd1\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u53eb\u4e86\u4e00\u58f0\u201c\u597d\u9669\uff01\u201d\u7b11\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\uff0c\u516c\u5b59\u8001\u5f1f\uff0c\u5e78\u4e8f\u4f60\u4eec\u4e24\u4f4d\u6765\u5230\uff0c\u7ed9\u6211\u63a0 \u9635\u3002\u4f60\u4eec\u867d\u6ca1\u51fa\u624b\uff0c\u5374\u4e5f\u5413\u574f\u4e86\u6b27\u9633\u575a\u8fd9\u5c0f\u5b50\u4e86\u3002\u8bf4\u8001\u5b9e\u8bdd\uff0c\u82e5\u4e0d\u662f\u4ed6\u5fc3\u91cc\u53d1\u614c\uff0c\u53ea\u6015\u6211\u8fd8 \u5f53\u771f\u80dc\u4e0d\u4e86\u4ed6\u5462\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u7b11\u9053\uff1a\u201c\u6bd5\u7adf\u59dc\u662f\u8001\u7684\u8fa3\uff0c\u60f3\u4e0d\u5230\u4f60\u975e\u4f46\u662f\u5b9d\u5200\u672a\u8001\uff0c\u800c\u4e14\u662f\u529f\u529b\u8d8a\u8001\u8d8a\u7eaf\uff0c\u8001 \u53eb\u5316\uff0c\u6211\u7b97\u662f\u670d\u4e86\u4f60\u4e86\u3002\u4f46\u4f60\u4f55\u4ee5\u4e0d\u5e9f\u4e86\u4ed6\u7684\u6b66\u529f\uff0c\u4ee5\u514d\u540e\u60a3\uff1f\u201d\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u516c\u5b59\u8001 \u5f1f\uff0c\u4f60\u522b\u7ed9\u6211\u8138\u4e0a\u8d34\u91d1\u3002\u6211\u7684\u4e00\u53ea\u811a\u5df2\u662f\u8e0f\u8fdb\u653b\u5893\u7684\u4e86\uff0c\u54ea\u91cc\u8fd8\u6709\u4e0e\u5c11\u5e74\u4eba\u4e89\u5f3a\u6597\u80dc\u4e4b\u5fc3\uff1f \u6b27\u9633\u575a\u8fd9\u5c0f\u5b50\u867d\u7136\u5c61\u6b21\u6211\u6211\u9ebb\u70e6\uff0c\u4f46\u4ed6\u53ea\u662f\u4ee3\u7236\u62a5\u4ec7\uff0c\u672a\u660e\u90aa\u6b63\u800c\u5df2\u3002\u672c\u8eab\u4f5c\u6076\u5012\u662f\u4e0d\u591a\u3002 \u8001\u53eb\u5316\u5df2\u7ecf\u5e9f\u4e86\u4ed6\u7236\u4eb2\u7684\u6b66\u529f\uff0c\u53c8\u4f55\u59a8\u9002\u53ef\u800c\u6b62\uff0c\u7ed9\u4ed6\u5341\u5e74\u529f\u592b\uff0c\u8ba9\u4ed6\u6709\u4e2a\u53cd\u7701\u7684\u673a\u4f1a\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u8d70\u8fd1\u4e86\u6765\uff0c\u671b\u4e86\u671b\u4ef2\u957f\u7edf\u3002\u53c8\u671b\u4e00\u671b\u6c5f\u6d77\u5929\uff0c\u8138\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u7ea2\uff0c\u4f3c\u4e4e\u662f\u6709\u4ec0\u4e48 \u8bdd\u8981\u8bf4\uff0c\u5374\u53c8\u541e\u541e\u5410\u5410\uff0c\u6b32\u8bf4\u8fd8\u4f11\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u5c01\u5b50\u8d85\uff0c\u4f60\u4e5f\u6765\u4e86\u3002\u4f60\u6709\u4ec0\u4e48\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u6211\u3001\u6211\u3001\u6211\u662f\u6709\u4e00\u4ef6\u4e8b\u60c5\uff0c\u60f3\u3001\u60f3\u3001\u60f3\u548c\u6c5f\u5927\u4fa0\u8bf4\uff01\u201d\u4ef2\u957f\u7edf\u559d\u9053\uff1a\u201c\u6709 \u8bdd\u5feb\u8bf4\uff0c\u6709\u5c41\u5feb\u653e\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u7ed9\u4ed6\u4e00\u559d\uff0c\u5e95\u4e0b\u7684\u8bdd\u66f4\u8bf4\u4e0d\u51fa\u6765\u4e86\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u53f2\u7ea2\u82f1\u201c\u5567\u201d\u4e86\u4e00\u58f0\uff0c\u6c5f \u6d77\u5929\u62ac\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u91d1\u9010\u6d41\u6b63\u5728\u4e00\u5251\u5288\u4e0b\uff0c\u59d3\u725f\u7684\u90a3\u6c49\u5b50\u6298\u6247\u8f7b\u8f7b\u4e00\u6309\uff0c\u8d34\u7740\u5251\u810a\uff0c\u628a\u7384\u94c1 \u5b9d\u5251\u5f15\u8fc7\u4e00\u8fb9\u3002\u91d1\u9010\u6d41\u4f3c\u4e4e\u60f3\u8981\u62bd\u5251\u53d8\u62db\uff0c\u4f46\u5982\u62bd\u4e0d\u56de\u6765\u3002\u5bf9\u65b9\u7684\u90a3\u628a\u6298\u6247\u8d34\u5728\u5251\u4e0a\uff0c\u4e5f\u62ff \u4e0d\u5f00\u3002\u4e24\u4eba\u767b\u65f6\u50f5\u5728\u5f53\u573a\uff0c\u597d\u50cf\u53d8\u6210\u4e86\u4e24\u5c0a\u77f3\u50cf\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u4f46\u5934\u4e0a\u5374\u90fd\u662f\u5192\u51fa\u4e86\u70ed\u817e\u817e\u7684 \u767d\u6c14\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u597d\uff0c\u5c01\u5b50\u8d85\u4f60\u60f3\u597d\u4e86\uff0c\u7b49\u4f1a\u513f\u5189\u8bf4\u4e0d\u8fdf\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5df2\u662f\u8d70\u5230\u91d1\u3001\u725f\u4e8c \u4eba\u4e4b\u524d\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u80dc\u8d1f\u672a\u5206\uff0c\u6b63\u597d\u9002\u53ef\u800c\u6b62\u4e86\uff01\u201d\u8bf4\u7f62\uff0c\u8f7b\u8f7b\u5728\u7384\u94c1\u5b9d\u5251\u4e0a\u4e00\u5f39\u3002 \u3000\u3000\u91d1\u3001\u725f\u4e8c\u4eba\uff0c\u540c\u65f6\u89c9\u5f97\u864e\u53e3\u4e00\u70ed\uff0c\u7384\u94c1\u5b9d\u5251\u79fb\u5f00\u6570\u5bf8\uff0c\u90a3\u628a\u6298\u6247\u4e5f\u624d\u80fd\u6536\u4e86\u56de\u53bb\u3002\u4e24\u4eba \u5404\u81ea\u5012\u8dc3\u4e09\u6b65\uff0c\u4e0d\u7531\u5f97\u90fd\u662f\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u60ed\u6127\u201d\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4eec\u4e24\u4eba\u90fd\u4e0d\u613f\u610f\u8f93\u62db\uff0c\u59d3\u725f\u90a3\u6c49\u5b50\u4f7f\u51fa\u4e86\u4ee5\u67d4\u514b\u521a\u7684\u7edd\u9876\u5185\u529f\uff0c\u60f3\u628a\u91d1\u9010\u6d41\u7684\u7384 \u94c1\u5b9d\u5251\u593a\u51fa\u624b\u53bb\uff0c\u54ea\u77e5\u91d1\u9010\u6d41\u4ea6\u662f\u65e9\u5df2\u7ec3\u6210\u4e86\u6b63\u90aa\u5408\u4e00\u7684\u5185\u529f\u7684\uff0c\u4ed6\u867d\u7136\u4e0d\u8bc6\u6276\u6851\u5c9b\u7684\u5185\u529f \u5fc3\u6cd5\uff0c\u4f46\u90a3\u59d3\u725f\u7684\u6c49\u5b50\u501f\u529b\u6253\u529b\uff0c\u5374\u662f\u4e0d\u80fd\u3002\u53cc\u65b9\u65e2\u7136\u90fd\u4e0d\u80fd\u591f\u5316\u89e3\u5bf9\u65b9\u7684\u529b\u9053\uff0c\u5251\u6247\u76f8 \u4ea4\uff0c\u8fd9\u5c31\u53d8\u6210\u4e86\u5185\u529b\u7684\u6bd4\u62fc\u4e86\u3002\u91d1\u9010\u6d41\u5403\u4e8f\u5728\u5267\u6218\u4e86\u4e24\u573a\uff0c\u5185\u529b\u81ea\u662f\u7a0d\u900a\u4e00\u7b79\uff0c\u4f46\u4ed6\u5374\u5360\u4e86 \u5175\u5668\u4e4b\u5229\uff0c\u59d3\u725f\u90a3\u6c49\u5b50\u7528\u4e00\u628a\u6298\u6247\u4e0e\u4ed6\u7684\u7384\u94c1\u5b9d\u5251\u76f8\u6297\uff0c\u4e07\u4e00\u652f\u6491\u4e0d\u4f4f\uff0c\u5c31\u6709\u6740\u8eab\u4e4b\u7978\u3002\u91d1 \u9010\u6d41\u4e5f\u662f\u4e00\u6837\u5371\u9669\uff0c\u4ed6\u7684\u5185\u529b\u4e0d\u53ca\u65f6\u65b9\uff0c\u5018\u82e5\u652f\u6491\u4e0d\u4f4f\uff0c\u8fc7\u540e\u4e0d\u6b7b\u4e5f\u5f97\u91cd\u4f24\u3002 \u3000\u3000\u4ed6\u4eec\u672c\u6765\u53ea\u662f\u76f8\u4e92\u5207\u78cb\uff0c\u53d8\u6210\u4e86\u5185\u529b\u6bd4\u62fc\uff0c\u5b9e\u975e\u59cb\u6599\u6240\u53ca\uff0c\u9ad8\u624b\u640f\u6597\uff0c\u6700\u5fcc\u7684\u5c31\u662f\u6bd4\u62fc \u5185\u529b\uff0c\u4e00\u5230\u4e86\u8fd9\u4e2a\u5730\u65b9\uff0c\u8c01\u4e5f\u4e0d\u80fd\u7f62\u624b\uff0c\u9664\u975e\u6709\u4e2a\u529f\u529b\u66f4\u9ad8\u7684\u4eba\u51fa\u6765\u5316\u89e3\uff0c\u5426\u5219\u5c31\u53ea\u80fd\u62fc\u4e2a \u4e24\u8d25\u4ff1\u4f24\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u51fa\u6765\u5316\u89e3\uff0c\u5176\u5b9e\u4e5f\u662f\u9887\u4e3a\u5192\u9669\u7684\u3002\u5047\u5982\u4ed6\u7684\u5185\u529b\u4e0d\u662f\u80dc\u8fc7\u91d1\u3001\u725f\u4e8c\u4eba\u7684\u603b\u548c\uff0c\u90a3 \u5c31\u975e\u4f46\u5316\u89e3\u4e0d\u4e86\uff0c\u800c\u4e14\u4e24\u4eba\u7684\u5185\u529b\u90fd\u5c06\u53cd\u9707\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u4ed6\u81ea\u5df1\u4e5f\u8981\u91cd\u4f24\u3002\u201d \u3000\u3000\u59d3\u725f\u90a3\u6c49\u5b50\u89c1\u6c5f\u6d77\u5929\u4e3e\u91cd\u82e5\u8f7b\u7684\u53ea\u662f\u4e00\u5f39\u6307\u5c31\u5c06\u4ed6\u4eec\u5206\u5f00\uff0c\u4e0d\u7531\u5f97\u53c8\u662f\u611f\u6fc0\u53c8\u662f\u60ed\u6127\uff0c \u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u91d1\u4e16\u9057\u7684\u5f92\u5f1f\u5c1a\u4e14\u5982\u6b64\uff0c\u6211\u51ed\u4ec0\u4e48\u4e0e\u4ed6\u4e89\u96c4\uff1f\u201d\u5f53\u4e0b\u8fde\u5fd9\u6536\u4e86\u6298\u6247\uff0c\u5411\u6c5f\u6d77\u5929 \u65bd\u4e86\u4e00\u793c\u8bf4\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u7edd\u4e16\u795e\u529f\uff0c\u4f69\u670d\u4f69\u670d\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4f4d\u725f\u5148\u751f\u662f\u6276\u6851\u5c9b\u7684\u4f20\u4eba\u3002\u201d\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u4e5f\u8fde\u5fd9\u62f1\u624b\u9053\uff1a\u201c\u8d35\u6d3e \u6b66\u529f\uff0c\u5343\u5e74\u4e4b\u524d\u5df2\u4eab\u76db\u540d\uff0c\u6211\u53ea\u9053\u4e45\u5df2\u5931\u4f20\uff0c\u4e0d\u6599\u4eca\u65e5\u6709\u6b64\u773c\u798f\uff0c\u5f97\u89c1\u8d35\u6d3e\u7684\u60ca\u4e16\u9a87\u4fd7\u7684\u6b66 \u6797\u7edd\u5b66\uff0c\u5f53\u771f\u662f\u540d\u4e0d\u865a\u4f20\uff0c\u5728\u4e0b\u66f4\u662f\u597d\u751f\u4f69\u670d\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8bf4\u7684\u7edd\u975e\u865a\u4f2a\u7684\u5ba2\u6c14\u8bf4\u8bdd\uff0c\u539f\u6765\u4ed6\u7684\u5f53\u4e16\u65e0\u654c\u7684\u5185\u529b\uff0c\u5927\u534a\u662f\u7531\u4e8e\u4ed6\u5728\u5c11\u5e74\u4e4b\u65f6 \u5e78\u5f97\u5947\u9047\uff0c\u670d\u98df\u4e86\u5bf9\u4e8e\u589e\u8fdb\u5185\u529b\u6700\u6709\u529f\u6548\u7684\u5947\u836f\u201c\u5929\u5fc3\u77f3\u201d\u6240\u81f3\u3002\u6c5f\u6d77\u5929\u81ea\u5fd6\uff1a\u201c\u672c\u95e8\u7684\u5185 \u529f\u5fc3\u6cd5\u56fa\u7136\u662f\u5947\u5999\u65e0\u6bd4\uff0c\u4f46\u6211\u82e5\u4e0d\u662f\u670d\u98df\u4e86\u5929\u5fc3\u77f3\uff0c\u5faa\u5e8f\u6e10\u8ff8\uff0c\u5728\u4ed6\u8fd9\u4e2a\u5e74\u7eaa\uff0c\u51b3\u4e0d\u80fd\u6709\u4ed6 \u7684\u529f\u529b\u3002\u8fd9\u4eba\u53ea\u7528\u4e00\u628a\u6298\u6247\uff0c\u80fd\u4f7f\u51fa\u5404\u79cd\u4e0d\u540c\u5175\u5668\u7684\u62db\u6570\uff0c\u867d\u8bf4\u4e5f\u672a\u5fc5\u5c31\u80fd\u591f\u80dc\u5f97\u8fc7\u5e08\u7236\u6240 \u4f20\uff0c\u4f46\u5982\u662f\u975e\u6211\u6240\u53ca\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u54c8\u54c8\u5927\u7b11\uff0c\u8d70\u4e0a\u524d\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6276\u6851\u5c9b\u6b66\u529f\u91cd\u73b0\u4e2d\u571f\uff0c\u8fd9\u771f\u662f\u6b66\u6797\u4e2d\u4e00\u5927\u559c\u4e8b\u3002 \u4f46\u8001\u673d\u5374\u6709\u4e00\u4e8b\u4e0d\u660e\uff0c\u4e0d\u89e3\u725f\u5148\u751f\u4f55\u4ee5\u4f1a\u4e0e\u6b27\u9633\u575a\u540c\u5728\u4e00\u8d77\uff0c\u83ab\u975e\u725f\u5148\u751f\u4e0d\u77e5\u4ed6\u7684\u6765\u5386 \u4e48\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u5c82\u6709\u660e\u73e0\u6295\u6697\u5ba4\uff0c\u4ece\u6765\u6cfe\u6e2d\u4e0d\u540c\u6d41\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u56db\u5341\u516d\u56de\u3000\u90c1\u90c1\u4f46\u6c42\u5fd8\u65e7\u6028\u3000\u60fa\u60fa\u76f8\u60dc\u7ed3\u65b0\u77e5 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u56db\u5341\u516d\u56de\u3000\u90c1\u90c1\u4f46\u6c42\u5fd8\u65e7\u6028\u3000\u60fa\u60fa\u76f8\u60dc\u7ed3\u65b0\u77e5 \u3000\u3000\u6b64\u65f6\u91d1\u9010\u6d41\u5598\u606f\u5df2\u5b9a\uff0c\u4e5f\u6765\u548c\u4ed6\u91cd\u65b0\u89c1\u8fc7\u4e86\u793c\uff0c\u5e76\u6559\u8bf7\u4ed6\u7684\u59d3\u540d\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u6211\u540d\u53eb\u725f\u5b97\u6d9b\u3002\u8bf4\u8d77\u6211\u4f55\u4ee5\u548c\u6b27\u9633\u575a\u7ed3\u8bc6\uff0c\u5012\u662f\u548c\u6c5f\u5927\u4fa0\u6709\u70b9\u5173\u7cfb\u3002\u201d\u6c5f \u6d77\u5929\u8be7\u9053\uff1a\u201c\u662f\u4e48\uff0c\u8fd9\u6211\u5012\u8981\u8bf7\u725f\u5148\u751f\u544a\u8bc9\u6211\u4e86\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u6b64\u4e8b\u8bf4\u6765\u8bdd\u957f\uff0c\u6c5f\u5927\u4fa0\u65e2\u7136\u95ee\u8d77\uff0c\u6211\u60f3\u5148\u8bf4\u4e00\u8bf4\u6211\u6765\u5230\u4e2d\u539f\u7684\u539f\u56e0\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u60f3\u77e5\u9053\u4ed6\u7684\u6765\u5386\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u66f4\u597d\u4e86\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u5148\u7956\u6ca7\u6d6a\u516c\u7684\u95e8\u4eba\u5f1f\u5b50\u4e0d\u591a\uff0c\u4f46\u7ecf\u8fc7\u4e86\u5343\u5e74\u4e4b\u4e45\uff0c\u4e5f\u5206\u6210\u4e86\u4e09\u652f\uff0c\u5e76\u672a\u5931 \u4f20\u3002\u6276\u6851\u5c9b\u65e9\u5df2\u7ed9\u502d\u4eba\u5360\u9886\uff0c\u975e\u590d\u6211\u6709\uff0c\u725f\u5bb6\u5b50\u5b59\u5927\u90fd\u9690\u59d3\u57cb\u540d\uff0c\u4e0d\u6562\u9732\u9762\uff0c\u6b66\u529f\u4ea6\u65e9\u8d8b\u5f0f \u5fae\uff0c\u5230\u4e86\u5982\u4eca\uff0c\u5c1a\u80fd\u4fdd\u5b58\u5148\u7956\u6b66\u5b66\u7684\u5341\u4e4b\u4e00\u8005\uff0c\u636e\u6211\u6240\u77e5\uff0c\u4e0d\u8fc7\u5be5\u5be5\u6570\u4eba\u800c\u5df2\u3002\u5176\u4ed6\u7684\u725f\u5bb6 \u5b50\u5b59\uff0c\u6216\u8005\u9690\u5c45\u6df1\u5c71\uff0c\u6216\u8005\u6539\u540d\u6362\u59d3\uff0c\u4ece\u4e8b\u5176\u4ed6\u804c\u4e1a\uff0c\u5c31\u662f\u8ba9\u6211\u78b0\u89c1\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u4e43\u662f \u540c\u95e8\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u602a\u4e0d\u5f97\u6211\u7239\u7239\u5230\u6276\u6851\u5c9b\u5bfb\u89c5\u725f\u5bb6\u540e\u4eba\uff0c\u6beb\u65e0\u7ed3\u679c\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5176\u4ed6\u4e24\u652f\uff0c\u5206\u6563\u6d77\u5916\uff0c\u7a76\u6709\u591a\u5c11\uff0c\u6211\u4e5f\u4e0d\u77e5\u3002\u4f46\u6211\u6709\u4e2a\u5fc3\u613f\uff0c\u60f3\u5728 \u6709\u751f\u4e4b\u5e74\uff0c\u904d\u8bc9\u5404\u5730\u540c\u95e8\uff0c\u5e0c\u671b\u80fd\u591f\u628a\u5931\u6563\u4e86\u7684\u5148\u7956\u6240\u4f20\u7684\u6b66\u5b66\uff0c\u91cd\u65b0\u6574\u7406\uff0c\u6062\u590d\u672c\u6765\u7684\u9762 \u76ee\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8d5e\u9053\uff1a\u201c\u725f\u5148\u751f\u8fd9\u53e3\u5b8f\u613f\u5018\u82e5\u6210\u529f\uff0c\u5b9a\u80fd\u4e3a\u6b66\u6797\u653e\u4e00\u653e\u5f69\uff01\u201d\u516c\u5b59\u5b8f\u5374\u8bf4\u9053\uff1a \u201c\u539f\u6765\u4ed6\u662f\u60f3\u5f00\u5b97\u7acb\u6d3e\uff0c\u7ee7\u627f\u7956\u5148\u9057\u4e1a\uff0c\u91ce\u5fc3\u5012\u662f\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u6211\u904d\u8bbf\u6d77\u5916\u5404\u5730\u540c\u95e8\uff0c\u4e0d\u80fd\u8bf4\u662f\u6beb\u65e0\u7ed3\u679c\uff0c\u4f46\u4ea6\u6536\u83b7\u751a\u5fae\u3002\u6211\u60f3\u65f6\u5386\u5e72 \u6597\uff0c\u53ef\u80fd\u4e5f\u6709\u82e5\u5e72\u540c\u95e8\uff0c\u56de\u5230\u4e2d\u539f\u4e86\u7684\uff0c\u56e0\u6b64\u6211\u53c8\u5174\u8d77\u4e86\u6765\u4e2d\u539f\u4e00\u6e38\uff0c\u5bfb\u89c5\u540c\u95e8\u4e4b\u5ff5\u3002 \u3000\u3000\u201c\u4ee4\u5e08\u91d1\u5927\u4fa0\u91d1\u4e16\u9057\u7684\u5927\u540d\uff0c\u6211\u5728\u6d77\u5916\u4e5f\u662f\u65e9\u5df2\u77e5\u9053\u7684\u4e86\u3002\u91d1\u5927\u4fa0\u76f8\u8bc6\u904d\u5929\u4e0b\uff0c\u4e2d\u539f\u6d77 \u5916\u7684\u6b66\u6797\u4eba\u7269\uff0c\u90fd\u6709\u4ed6\u7684\u670b\u53cb\uff0c\u662f\u4ee5\u6211\u5728\u8bbf\u67e5\u540c\u95e8\u7684\u671f\u95f4\uff0c\u4e5f\u66fe\u5230\u8fc7\u91d1\u5927\u4fa0\u7684\u5c0f\u5c9b\uff0c\u53ef\u6015\u4ed6 \u6070\u5de7\u5916\u51fa\u53bb\u4e86\uff0c\u65e0\u7f18\u76f8\u4f1a\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u65f6\u5019\u7684\u4e8b\u60c5\uff1f\u201d\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u8ddd\u4eca\u4e0d\u8fc7\u534a\u5e74\uff0c\u6211\u627e\u4e0d\u7740\u4ee4\u5c0a\uff0c\u624d \u6765\u4e2d\u539f\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6697\u81ea\u60f3\u9053\uff1a\u201c\u7239\u7239\u8bf4\u8fc7\u8981\u56de\u4e2d\u539f\u4e00\u884c\u7684\uff0c\u83ab\u975e\u4ed6\u5df2\u7ecf\u6765\u4e86\uff1f\u201d\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u53ef \u89c1\u7740\u6211\u7684\u59ec\u7237\u7237\u4e48\uff1f\u4ed6\u6709\u6ca1\u6709\u544a\u8bc9\u4f60\u6211\u7684\u7239\u7239\u53bb\u4e86\u4f55\u5904\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u795e\u5077\u59ec\u6653\u98ce\u524d\u8f88\u4e0d\u5e78\u5df2\u7ecf\u901d\u4e16\uff0c\u6211\u5728\u90a3\u513f\u4ec0\u4e48\u4eba\u4e5f\u6ca1\u89c1\u7740\uff0c\u53ea\u89c1\u5230\u4e86\u4eca\u5c0a \u7ed9\u59ec\u8001\u524d\u8f88\u6240\u5efa\u7684\u65b0\u575f\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5931\u58f0\u53eb\u9053\uff1a\u201c\u554a\uff0c\u59ec\u7237\u7237\u6b7b\u4e86\uff01\u201d\u59ec\u6653\u98ce\u662f\u4e2a\u6e38\u620f\u4eba\u95f4\u7684\u795e\u5077\uff0c\u665a\u5e74\u538c\u5026\u4e86\u6c5f\u6e56 \u751f\u6d3b\uff0c\u8dd1\u5230\u6d77\u5916\u548c\u91d1\u4e16\u9057\u4e00\u5bb6\u540c\u4f4f\u3002\u4ed6\u7684\u5e74\u7eaa\u867d\u7136\u6bd4\u91d1\u4e16\u9057\u8fd8\u5927\u5f97\u591a\uff0c\u4f46\u5982\u662f\u4e0d\u5931\u5176\u8d64\u5b50\u4e4b \u5fc3\uff0c\u91d1\u9010\u6d41\u548c\u4ed6\u6700\u5408\u5f97\u6765\uff0c\u4e00\u8001\u4e00\u5c0f\uff0c\u7ecf\u5e38\u5728\u4e00\u8d77\u620f\u800d\u7684\uff0c\u56e0\u6b64\u4e5f\u53ef\u4ee5\u8bf4\u5f97\u662f\u91d1\u9010\u6d41\u7ae5\u5e74\u65f6 \u4ee3\u552f\u4e00\u7684\u670b\u53cb\u3002\u91d1\u9010\u6d41\u60f3\u8d77\u8fd9\u4f4d\u671d\u5915\u4e0e\u5171\u7684\u8001\u7237\u7237\uff0c\u5fc3\u91cc\u5341\u5206\u96be\u8fc7\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u8fd9\u4f4d\u59ec\u8001\u524d\u8f88\u6709\u516b\u5341\u5c81\u4e86\u5427\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u79bb\u5bb6\u90a3\u5e74\uff0c\u4ed6\u5df2\u7ecf\u516b\u5341\u4e00 \u5c81\u4e86\u3002\u201d\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u4eba\u8c01\u65e0\u6b7b\uff0c\u59ec\u8001\u524d\u8f88\u5f97\u4eab\u9ad8\u5bff\uff0c\u65e0\u75be\u800c\u7ec8\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u4f24\u5fc3\u4e86\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u627e\u4e0d\u7740\u4ee4\u5c0a\uff0c\u5728\u56de\u7a0b\u4e2d\u7ecf\u8fc7\u4e00\u4e2a\u98ce\u666f\u7edd\u4f73\u7684\u5c0f\u5c9b\uff0c\u5374\u78b0\u5230\u4e86\u4e00\u4eff \u6b66\u529f\u9ad8\u660e\u7684\u4eba\u7269\uff0c\u867d\u7136\u672a\u5fc5\u6bd4\u5f97\u4e0a\u4ee4\u5c0a\u548c\u6c5f\u5927\u4fa0\uff0c\u4f46\u5728\u4e0b\u5f97\u4ee5\u548c\u4ed6\u7ed3\u4ea4\uff0c\u4e5f\u7b97\u5f97\u662f\u610f\u5916\u7684\u5947 \u9047\u4e86\u3002\u201d \u3000\u3000\u4e48\u5b59\u5b8f\u9887\u611f\u8be7\u5f02\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6276\u6851\u5c9b\u7684\u6b66\u529f\u5df2\u662f\u8db3\u4ee5\u60ca\u4e16\u9a87\u4fd7\uff0c\u9664\u4e86\u91d1\u5927\u4fa0\u4e4b\u5916\uff0c\u8fd8 \u6709\u4ec0\u4e48\u4eba\u503c\u5f97\u4ed6\u5982\u6b64\u4f69\u670d\uff1f\u96be\u9053\u6d77\u5916\u6d41\u4f20\u7684\u6b66\u5b66\uff0c\u7ade\u662f\u4e0d\u900a\u4e2d\u539f\uff1f\u8fd9\u53ef\u771f\u662f\u5929\u5916\u6709\u5929\uff0c\u4eba\u5916 \u6709\u4eba\u4e86\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u542c\u5230\u6b64\u5904\uff0c\u5df2\u662f\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u78b0\u89c1\u7684\u8fd9\u4f4d\u9ad8\u4eba\uff0c\u662f\u4e0d\u662f\u59d3\u53f6\u7684\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4f4d\u5c9b\u4e3b\u59d3\u53f6\uff0c\u5927\u540d\u51b2\u9704\u3002\u201d\u516c\u5b59\u5b8f\u5fc3\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u4ed6\u3002\u201d\u539f \u6765\u53f6\u51b2\u9704\u4e43\u662f\u897f\u57df\u4e00\u4e2a\u5c0f\u56fd\u7684\u738b\u5b50\uff0c\u4e3a\u4e86\u8981\u628a\u738b\u4f4d\u8ba9\u7ed9\u5f1f\u5f1f\uff0c\u907f\u5c45\u6d77\u5916\u7684\uff0c\u516c\u5b59\u5b8f\u53ea\u9053\u4ed6\u8bf4 \u7684\u662f\u4e00\u4f4d\u672c\u6765\u5c31\u5728\u6d77\u5916\u751f\u957f\u7684\u9ad8\u4eba\uff0c\u6545\u6b64\u4e00\u65f6\u6ca1\u6709\u60f3\u5230\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u53f6\u5c9b\u4e3b\u8c08\u8bba\u4e86\u4e09\u5929\u4e09\u591c\u7684\u6b66\u529f\uff0c\u627f\u4ed6\u9752\u773c\u6709\u52a0\uff0c\u8bb8\u6211\u4e3a\u5fd8\u5e74\u4e4b \u4ea4\uff0c\u4ed6\u77e5\u9053\u6211\u8981\u56de\u56fd\u4e00\u6e38\uff0c\u6258\u6211\u4e24\u4ef6\u4e8b\uff0c\u4ed6\u8bf4\u4ed6\u4e0e\u6c5f\u5927\u4fa0\u4e43\u662f\u90ce\u8205\u4e4b\u4eb2\uff0c\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u4fbf\u662f\u53eb \u6211\u6765\u62dc\u89c1\u6c5f\u5927\u4fa0\uff0c\u4ee3\u4ed6\u95ee\u5019\u3002\u201d\u6c5f\u6d77\u5929\u7684\u59bb\u5b50\u8c37\u4e2d\u83b2\u6b63\u662f\u53f6\u51b2\u9704\u7684\u59b9\u59b9\uff0c\u638c\u95e8\u5f1f\u5b50\u53f6\u6155\u534e\u53c8 \u6b63\u662f\u53f6\u51b2\u9704\u7684\u513f\u5b50\uff0c\u53ef\u4ee5\u8bf4\u5f97\u4e0a\u4eb2\u4e0a\u52a0\u4eb2\uff0c\u542c\u5230\u4ed6\u7684\u6d88\u606f\uff0c\u751a\u4e3a\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u542c\u8bf4\u4ed6\u4e09 \u5e74\u524d\u5c31\u60f3\u56de\u6765\u7684\uff0c\u4e0d\u77e5\u73b0\u5728\u4f55\u4ee5\u8fd8\u4e0d\u56de\u6765\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u4ed6\u73b0\u5728\u6b63\u5728\u6f5c\u5fc3\u7814\u7a76\u80a1\u82e5\u638c\u7684\u4e0a\u4e58\u6b64\u529f\uff0c\u4ed6\u8bf4\u4ed6\u8981\u5728\u7ec3\u6210\u4e4b\u540e\uff0c\u65b9\u80fd\u56de \u6765\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u53f6\u5927\u54e5\u7684\u597d\u80dc\u813e\u6c14\uff0c\u8fd8\u662f\u4e0d\u51cf\u5f53\u5e74\u3002\u201d\u539f\u6765\u53f6\u51b2\u9704\u5144\u59b9 \u4e43\u662f\u5e7c\u5e74\u5931\u6563\u7684\uff0c\u5f53\u5e74\u6c5f\u6d77\u5929\u521d\u6b21\u51fa\u9053\uff0c\u8fd8\u672a\u77e5\u9053\u53f6\u51b2\u9704\u662f\u8c37\u4e2d\u83b2\u7684\u54e5\u54e5\uff0c\u66fe\u7ecf\u548c\u4ed6\u8f83\u91cf\u8fc7 \u822c\u82e5\u638c\u7684\u529f\u592b\uff0c\u53f6\u51b2\u9704\u8f93\u4e86\u7ed9\u4ed6\uff0c\u751a\u4e0d\u670d\u6c14\uff0c\u53d1\u8a93\u8981\u628a\u822c\u82e5\u638c\u7ec3\u5f97\u8d85\u8fc7\u524d\u4eba\uff0c\u4e0d\u4ec5\u4ec5\u53ea\u8981\u80dc \u8fc7\u6c5f\u6d77\u5929\u800c\u5df2\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u7b2c\u4e8c\u4ef6\u4e8b\u5c31\u662f\u53f6\u5c9b\u4e3b\u4ee3\u4ed6\u592b\u4eba\u6258\u6211\u7684\u4e86\u3002\u5979\u8981\u77e5\u9053\u5979\u5bb6\u4eba\u7684\u6d88 \u606f\uff0c\u662f\u4ee5\u6211\u624d\u53bb\u627e\u5bfb\u6b27\u9633\u575a\u7684\u3002\u201d \u3000\u3000\u539f\u6765\u53f6\u51b2\u9704\u7684\u59bb\u5b50\u6b27\u9633\u5a49\u6b63\u662f\u6b27\u9633\u4f2f\u548c\u7684\u4f84\u5973\uff0c\u4e0e\u6b27\u9633\u575a\u4e43\u662f\u540c\u6c14\u8fde\u679d\u7684\u59d0\u5f1f\u6392\u884c\u3002\u4e0d \u8fc7\u5979\u5e74\u8f7b\u5f97\u591a\uff0c\u5f53\u5979\u5ac1\u7ed9\u53f6\u51b2\u9704\u7684\u65f6\u5019\uff0c\u6b27\u9633\u575a\u5c1a\u5728\u8941\u8913\u4e4b\u4e2d\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u5a5a\u4e8b\u5e76\u6ca1\u6709\u5f97\u5230\u4f5c\u4e3a\u4e00\u5bb6\u4e4b\u4e3b\u6b27\u9633\u4f2f\u548c\u7684\u540c\u610f\uff0c\uff08\u6b27\u9633\u4f2f\u548c\u672c\u6765\u662f\u8981\u5979\u5ac1\u7ed9\u6587\u9053 \u4e2d\u7684\uff0c\u4e8b\u8be6\u300a\u51b0\u6cb3\u6d17\u5251\u5f55\u300b\uff09\uff0c\u5f53\u65f6\u4ed6\u4eec\u4e43\u662f\u79c1\u5954\u7684\u3002\u5f85\u5230\u53f6\u51b2\u9704\u9690\u5c45\u6d77\u5916\u4e4b\u540e\uff0c\u4e0e\u5cb3\u5bb6\u66f4 \u662f\u65ad\u7edd\u5f80\u6765\u4e86\u3002 \u3000\u3000\u6b27\u9633\u5a49\u7684\u7236\u6bcd\u540e\u6765\u90c1\u90c1\u800c\u6b7b\uff0c\u6b27\u9633\u4f2f\u548c\u7ed9\u4ef2\u957f\u7edf\u5e9f\u4e86\u6b66\u529f\u4e4b\u540e\uff0c\u8fc7\u4e86\u51e0\u5e74\u4ea6\u5df2\u6b7b\u4e86\u3002\u5982 \u4eca\u6b27\u9633\u5a49\u7684\u5916\u5bb6\u5269\u4e0b\u7684\u5c31\u53ea\u6709\u6b27\u9633\u575a\u4e00\u4eba\u3002\u8fd9\u4e5f\u662f\u4ef2\u957f\u7edf\u4e3a\u4f55\u4e0d\u5fcd\u6740\u4ed6\u7684\u539f\u56e0\u4e4b\u4e00\u3002 \u3000\u3000\u6b27\u9633\u8fd9\u4e00\u5bb6\u4e43\u662f\u6b66\u6797\u4e00\u9738\uff0c\u4e00\u5411\u6076\u540d\u662d\u5f70\uff0c\u662f\u4ee5\u540e\u6765\u867d\u7136\u7531\u4e8e\u6b27\u9633\u5a49\u5ac1\u7ed9\u53f6\u51b2\u9704\uff0c\u6c5f\u6d77 \u5929\u548c\u4ed6\u4eec\u4e5f\u6709\u4e86\u4eb2\u621a\u5173\u7cfb\uff0c\u4f46\u4e24\u5bb6\u4ecd\u662f\u6ca1\u6709\u5f80\u6765\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u672c\u6765\u6211\u662f\u5e94\u8be5\u5148\u53bb\u62dc\u8bbf\u6c5f\u5927\u4fa0\u7684\uff0c\u4f46\u542c\u8bf4\u6c5f\u5927\u4fa0\u5df2\u5230\u5c0f\u91d1\u5ddd\u53bb\u4e86\uff0c \u6211\u53ea\u597d\u5148\u627e\u6b27\u9633\u575a\uff0c\u6211\u53ea\u77e5\u9053\u6b27\u9633\u575a\u4e0e\u53f6\u5c9b\u4e3b\u6709\u90ce\u8205\u4e4b\u4eb2\uff0c\u81f3\u4e8e\u4ed6\u7684\u4e3a\u4eba\u5982\u4f55\uff0c\u90a3\u5c31\u4e0d\u662f\u6211 \u6240\u80fd\u77e5\u6089\u7684\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u62b1\u6b49\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u8fd9\u662f\u521d\u5230\u4e2d\u539f\u7684\uff0c\u521a\u624d\u8bf4\u8bdd\u65e0\u793c\uff0c\u8fd8\u8bf7\u4e0d\u8981\u89c1\u602a\uff01 \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u7269\u4ee5\u7c7b\u805a\uff0c\u4eba\u4ee5\u7fa4\u5c45\u3002\u516c\u5b59\u524d\u8f88\u8d23\u5907\u6211\u4e0d\u8be5\u548c\u6b27\u9633\u575a\u540c\u5728\u4e00\u8d77\uff0c\u8fd9\u4e5f\u662f\u4e00 \u756a\u597d\u610f\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f60\u8981\u89c1\u6211\u5e08\u5144\uff0c\u90a3\u4e48\u4eca\u65e5\u6b63\u662f\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\u4e86\u3002\u4f46 \u4f55\u4ee5\u4f60\u4e0d\u65e9\u8bf4\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u6b27\u9633\u575a\u5e26\u6211\u6765\u627e\u9633\u6d69\uff0c\u8bf4\u662f\u9633\u6d69\u4e0e\u6b63\u90aa\u5404\u6d3e\u76f8\u5f53\u719f\u6089\uff0c\u5929\u9b54\u6559\u6559\u5f92\u4f17\u591a\uff0c \u4e5f\u53ef\u80fd\u5e2e\u6211\u5bfb\u89c5\u540c\u95e8\uff0c\u6211\u4e0d\u77e5\u5c31\u53bb\uff0c\u8ddf\u4ed6\u6765\u5230\u8fd9\u513f\u3002\u5230\u4e86\u8fd9\u513f\uff0c\u771f\u5047\u5929\u9b54\u6559\u4e3b\u7684\u771f\u76f8\u63ed\u9732\uff0c \u6c5f\u5927\u4fa0\u4e0e\u516c\u5b59\u524d\u8f88\u4ea6\u76f8\u7ee7\u800c\u6765\u5230\uff0c\u6211\u624d\u77e5\u9053\u9633\u6d69\u548c\u4ed6\u7684\u2018\u5929\u9b54\u6559\u2019\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u60c5\u3002 \u3000\u3000\u201c\u6211\u662f\u548c\u6b27\u9633\u575a\u4e00\u540c\u6765\u7684\uff0c\u5728\u672a\u66fe\u89e3\u91ca\u6e05\u695a\u4e4b\u524d\uff0c\u4f60\u4eec\u5f53\u7136\u628a\u6211\u7b97\u4f5c\u662f\u9633\u6d69\u7684\u515a\u7fbd\uff0c\u4f46 \u8981\u89e3\u91ca\u6e05\u695a\uff0c\u5374\u662f\u8bf4\u6765\u8bdd\u957f\u3002\u800c\u4e14\u6211\u56e0\u4e3a\u788d\u7740\u53f6\u5c9b\u4e3b\u592b\u5987\u7684\u60c5\u9762\uff0c\u6211\u4e00\u5230\u4e2d\u539f\uff0c\u5c31\u5f97\u5230\u6b27\u9633 \u575a\u7684\u6b3e\u5f85\uff0c\u4e0e\u4ed6\u4e5f\u6709\u7740\u4e3b\u5ba2\u7684\u60c5\u8c0a\uff0c\u6211\u4e5f\u4e0d\u613f\u4ee4\u4ed6\u592a\u96be\u582a\uff0c\u662f\u4ee5\u6211\u53ea\u597d\u8ddf\u7740\u4ed6\u79bb\u5f00\uff0c\u6253\u7b97\u7b49 \u5230\u4f60\u4eec\u5728\u8fd9\u91cc\u7684\u4e8b\u60c5\u544a\u4e00\u6bb5\u848b\uff0c\u6211\u518d\u72ec\u81ea\u6765\u4e0e\u6c5f\u5927\u4fa0\u89c1\u9762\u3002\u4e0d\u6599\u91d1\u5c11\u4fa0\u5df2\u7ecf\u53d1\u73b0\u6211\u4eec\u7684\u884c \u85cf\uff0c\u8ddf\u8e2a\u8ffd\u5230\uff0c\u5012\u662f\u6559\u6211\u4e0d\u80fd\u4e0d\u63d0\u524d\u9732\u9762\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u80fd\u591f\u65e9\u70b9\u89c1\u9762\u4e0d\u66f4\u597d\u5417\uff1f\u7ea2\u82b1\u7eff\u53f6\u540c\u662f\u4e00\u5bb6\uff0c\u6d77\u5916\u4e2d\u539f\uff0c\u4f55\u5206 \u5f7c\u6b64\u3002\u8d35\u6d3e\u6b66\u529f\u6211\u662f\u94a6\u4ef0\u5df2\u4e45\uff0c\u4eca\u65e5\u5e78\u5f97\u76f8\u8bc6\uff0c\u4fbf\u8bf7\u4e00\u540c\u56de\u53bb\uff0c\u8ba9\u6211\u501f\u82b1\u732e\u4f5b\uff0c\u656c\u725f\u5148\u751f\u4e00 \u676f\u3002\u6b64\u65f6\u5929\u8272\u8fd1\u665a\uff0c\u6c5f\u6d77\u5929\u6050\u9632\u603b\u8235\u4e2d\u4f17\u4eba\u7b49\u5f97\u5fc3\u7126\uff0c\u6545\u6b64\u4fbf\u5373\u9080\u8bf7\u725f\u5b97\u6d9b\u540c\u8d74\u5e86\u529f\u5bb4\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u6211\u548c\u4f60\u4eec\u7684\u670b\u53cb\u90fd\u4e0d\u76f8\u8bc6\uff0c\u4f60\u4eec\u4e5f\u6709\u6b63\u4e8b\u5546\u8bae\uff0c\u6211\u4e0d\u60f3\u6253\u6270\u4f60\u4eec\u4e86\u3002\u4ed6\u65e5 \u82e5\u6709\u673a\u7f18\uff0c\u6211\u518d\u5230\u4e24\u4f4d\u524d\u8f88\u8ddf\u524d\u8bf7\u6559\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8981\u67e5\u8bbf\u540c\u95e8\u5417\uff0c\u4eca\u65e5\u6709\u8bb8\u591a\u5e2e\u4f1a\u7684\u4eba\u6765\u5230\uff0c\u8bf4\u4e0d\u5b9a\u53ef\u4ee5\u5e2e\u4f60\u7684 \u5fd9\u3002\u201d\u91d1\u9010\u6d41\u5bf9\u4ed6\u9887\u6709\u597d\u611f\uff0c\u5f88\u60f3\u7559\u4ed6\u591a\u805a\u4e00\u4f1a\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u6b64\u4e8b\u6682\u65f6\u6211\u8fd8\u4e0d\u60f3\u5f20\u626c\u3002\u518d\u8bf4\u4e2d\u539f\u7684\u5e2e\u4f1a\u4e2d\u4eba\uff0c\u6050\u6015\u4e5f\u8fd8\u672a\u5fc5\u77e5\u9053\u6709\u4e2a\u6276 \u6851\u5c9b\u5462\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u89c1\u4ed6\u53bb\u610f\u575a\u51b3\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u725f\u5148\u751f\u65e2\u662f\u6709\u4e8b\u5728\u8eab\uff0c\u6211\u4e5f\u4e0d\u7559\u4f60\u4e86\u3002\u4f46\u613f\u4f60\u7684\u5b8f \u613f\u80fd\u591f\u65e9\u65e5\u5b8c\u6210\uff0c\u4e3a\u6b66\u6797\u653e\u4e00\u5f02\u5f69\u3002\u54b1\u4eec\u540e\u4f1a\u6709\u671f\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u7ad9\u5728\u4e00\u65c1\uff0c\u770b\u7740\u725f\u5b97\u7977\u4e0b\u5c71\uff0c\u597d\u50cf\u5fc3\u4e8b\u5f88\u91cd\u7684\u6837\u5b50\uff0c\u4e00\u53cc\u773c\u775b\u95ea\u70c1\u4e0d\u5b9a\uff0c\u4f46\u4ed6\u5374 \u4e5f\u6ca1\u8bf4\u8981\u8d70\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u597d\uff0c\u5c01\u5b50\u8d85\uff0c\u73b0\u5728\u8f6e\u5230\u4f60\u8bf4\u8bdd\u4e86\uff0c\u6709\u5c41\u5c31\u5feb\u653e\u5427\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4ed6\u521a\u624d\u6ca1\u6709\u4e58\u673a\u5077\u8d70\uff0c\u5012\u597d\u50cf\u6709\u70b9\u6094\u8fc7\u4e4b\u610f\uff0c\u54b1\u4eec\u4e14\u542c\u542c\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8a00\u5916\u4e4b\u610f\uff0c\u5373\u662f\u8bf7\u516c\u5b59\u5b8f\u4e0d\u8981\u4ee4\u4ed6\u592a\u96be\u582a\u3002\u6c5f\u6d77\u5929\u597d\u751f\u6b22\u559c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5e08\u5f1f\u5728\u6c5f\u6e56 \u4e0a\u5386\u7ec3\u4e86\u51e0\u5e74\uff0c\u8f7b\u6d6e\u5012\u662f\u51cf\u4e86\u51e0\u5206\uff0c\u5bbd\u539a\u5374\u52a0\u4e86\u51e0\u5206\u4e86\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\uff0c\u6211\u662f\u6709\u4e00\u4e9b\u8bdd\u60f3\u8981\u7980\u544a\u6c5f\u5927\u4fa0\u548c\u91d1\u5c11\u4fa0\uff0c\u5c31\u4e0d\u77e5\u4f60\u4eec\u80af \u4e0d\u80af\u539f\u8c05\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u671b\u4e86\u91d1\u9010\u6d41\u4e00\u773c\uff0c\u4e00\u526f\u60f6\u6050\u4e0d\u5b89\u7684\u795e\u6c14\uff0c\u8bdd\u5728\u820c\u5c16\u6253\u6eda\uff0c\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u5bf9\u554a\uff0c\u4f60\u8fd8\u6ca1\u6709\u5411\u6211\u8c22\u5a92\u5462\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u6211\u4e27\u5fc3\u75c5\u72c2\uff0c\u5f53\u65e5\u5984\u60f3\u501a\u9760\u5973\u513f\uff0c\u6c42\u53d6\u5bcc\u8d35\uff0c\u8f9c\u8d1f\u4e86\u91d1\u5c11\u4fa0\u4f60\u7684\u597d\u610f\u3002\u6211 \u4e0d\u4f46\u6ca1\u6709\u9762\u76ee\u89c1\u4f60\uff0c\u4e5f\u6ca1\u6709\u9762\u76ee\u89c1\u6211\u5973\u513f\u3002\u4e0d\u8fc7\uff0c\u6211\u5374\u5f88\u60f3\u77e5\u9053\u5979\u7684\u6d88\u606f\uff0c\u4f60\u53ef\u4ee5\u544a\u8bc9\u6211\u5979 \u7684\u4e0b\u843d\u5417\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u662f\u613f\u610f\u7b54\u5e94\u8fd9\u95e8\u4eb2\u4e8b\uff0c\u80af\u628a\u5973\u513f\u5ac1\u7ed9\u79e6\u5143\u6d69\u4e86\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u79e6\u5c11\u4fa0\u662f\u6b66\u5f53\u6d3e\u7684\u540d\u95e8\u5f1f\u5b50\uff0c\u5c31\u53ea\u6015\u4ed6\u4e0d\u80af\u8981\u6211\u8fd9\u4e2a\u5cb3\u7236\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u53ea\u8981\u4f60\u75db\u6094\u524d\u975e\uff0c\u6211\u8fd9\u4e2a\u505a\u5a92\u4eba\u7684\uff0c\u5f53\u7136\u4f1a\u53eb\u4f60\u7684\u5973\u5a7f\u5411\u4f60\u78d5\u5934\u8ba4\u4eb2\u3002 \u4ed6\u4eec\u73b0\u5728\u90fd\u5728\u5927\u51c9\u5c71\u7afa\u5c1a\u7236\u90a3\u513f\uff0c\u5e73\u5b89\u65e0\u4e8b\uff0c\u4f60\u4e0d\u5fc5\u6302\u5ff5\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u5927\u559c\u8fc7\u671b\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u5e74\u6211\u591a\u627f\u4ee4\u5c0a\u4e0d\u6740\u4e4b\u6069\uff0c\u5982\u4eca\u53c8\u591a\u5f97\u4f60\u7389\u6210\u6211\u5973\u513f\u7684\u5a5a \u4e8b\uff0c\u6211\u4e0d\u77e5\u5982\u4f55\u62a5\u7b54\u4f60\u624d\u597d\u3002\u597d\uff0c\u73b0\u5728\u6211\u53ef\u4ee5\u653e\u5fc3\u548c\u4f60\u4eec\u8bf4\u4e86\u3002\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u60f6\u6050\u4e0d\u5b89\u7684\u795e \u8272\u4ecd\u662f\u672a\u80fd\u6d88\u9664\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u4f60\u4e0d\u662f\u6709\u8bdd\u8981\u548c\u6211\u5e08\u5144\u8bf4\u7684\u5417\uff1f\u4e0d\u5fc5\u8001\u662f\u5411\u6211\u9053\u8c22\u4e86\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\uff0c\u6211\uff0c\u6211\u6709\u4e00\u4ef6\u4e8b\u60c5\u5bf9\u4e0d\u4f4f\u4f60\u3002\u201d\u6c5f\u6d77\u5929\u4e00\u65f6\u6ca1\u5f04\u6e05\u695a\u4ed6\u7684 \u610f\u601d\uff0c\u4ee5\u4e3a\u4ed6\u8bf4\u7684\u662f\u8fc7\u53bb\u4e4b\u4e8b\uff0c\u4fbf\u9053\uff1a\u201c\u6211\u65e9\u5df2\u8bf4\u8fc7\u539f\u8c05\u4f60\u4e86\u3002\u201d\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u4ed6\u8bf4\u7684\u597d\u50cf \u662f\u73b0\u5728\u7684\u4e8b\u3002\u201d\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b64\u4e8b\u6b63\u5728\u8fdb\u884c\u4e4b\u4e2d\uff0c\u6211\u5fc5\u987b\u8ba9\u4f60\u77e5\u9053\uff1a\u201c\u6c5f\u6d77\u5929\u9053\uff1a \u201c\u597d\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u4f60\u5c31\u8bf4\u5427\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u4ece\u4eac\u4e2d\u51fa\u6765\uff0c\u8428\u798f\u9f0e\u6709\u4e2a\u547d\u4ee4\u7ed9\u6211\uff0c\u8981\u6211\u5bb3\u4f60\u7684\u5bb6\u4eba\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8428\u798f\u9f0e\u5012\u662f\u5f88\u770b\u91cd\u4f60\u554a\uff01\u201d\u8981\u77e5\u6c5f\u6d77\u5929\u7684\u59bb\u5b50\u4e43\u662f\u90ca\u5c71\u6d3e\u7684 \u638c\u95e8\uff0c\u6b66\u529f\u4e4b\u5f3a\uff0c\u7eb5\u7136\u4e0d\u53ca\u4e08\u592b\uff0c\u4e5f\u8db3\u53ef\u5217\u5165\u5f53\u4e16\u5341\u5927\u9ad8\u624b\u4e4b\u5185\uff0c\u83ab\u8bf4\u4e00\u4e2a\u5c01\u5b50\u8d85\uff0c\u5c31\u662f\u5341 \u4e2a\u5c01\u5b50\u8d8a\u4e5f\u4e0d\u662f\u5979\u7684\u5bf9\u624b\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u8428\u798f\u9f0e\u5f53\u7136\u4e0d\u662f\u53eb\u6211\u72ec\u81ea\u53bb\u5e72\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4ed6\u662f\u8981\u6211\u505a\u6b27\u9633\u575a \u7684\u52a9\u624b\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u4f60\u505a\u6b27\u9633\u575a\u7684\u52a9\u624b\uff1f\u54e6\uff0c\u539f\u6765\u8fd9\u5c0f\u5b50\u4e5f\u5df2\u6295\u9760\u4e86\u6e05\u5ef7\u5566\u3002 \u4ef2\u5e2e\u4e3b\uff0c\u8fd9\u4e48\u8bf4\u521a\u624d\u4f60\u5012\u662f\u653e\u9519\u4ed6\u4e86\u3002\u201d\u5fc3\u60f3\uff1a\u201c\u602a\u4e0d\u5f97\u6b27\u9633\u575a\u521a\u624d\u8d70\u7684\u65f6\u5019\uff0c\u5c01\u5b50\u8d85\u597d\u50cf \u6709\u8bdd\u8981\u8bf4\u53c8\u4e0d\u6562\u8bf4\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u7b11\u9053\uff1a\u201c\u8bf4\u5230\u8981\u5bf9\u4ed8\u6c5f\u592b\u4eba\uff0c\u6b27\u9633\u575a\u8fd9\u5c0f\u5b50\u6050\u6015\u4e5f\u8fd8\u5dee\u5f97\u8fdc\u5427\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6c89\u541f\u534a\u664c\uff0c\u95ee\u9053\uff1a\u201c\u662f\u4e0d\u662f\u53e6\u5916\u8fd8\u6709\u9ad8\u624b\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u9664\u4e86\u6587\u9053\u5e84\u548c\u9633\u6d69\u4e8c\u4eba\uff0c\u8428\u798f\u9f0e\u54ea\u8fd8\u80fd\u627e\u5f97\u5230\u4ec0\u4e48\u9ad8\u624b\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u5929\u4e0b\u4e4b\u5927\uff0c\u4f55\u5904\u6ca1\u6709\u80fd\u4eba\uff1f\u6bd4\u5982\u521a\u624d\u7684\u725f\u5b97\u6d9b\u5c31\u662f\u4e00\u5927\u9ad8\u624b\uff01\u201d\u91d1 \u9010\u6d41\u9762\u4e0a\u4e00\u7ea2\uff0c\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u8bf4\u5f97\u5bf9\u4e86\uff0c\u7684\u786e\u662f\u53e6\u5916\u8fd8\u6709\u9ad8\u624b\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u9ad8\u624b\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u77e5\u9053\uff0c\u4f46\u4e5f\u5f88\u53ef\u80fd\u5c31\u662f\u725f\u5b97\u6d9b\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u90fd\u662f\u9a87\u7136\uff0c\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u4f1a\u5427\u3002\u4ed6\u521a\u624d\u5df2\u7ecf\u628a\u4ed6\u4e0e\u6b27\u9633\u575a\u4f5c\u4f34\u7684\u539f\u56e0 \u8bf4\u5f97\u5f88\u6e05\u695a\u4e86\uff0c\u6211\u770b\u4ed6\u4e5f\u4e0d\u50cf\u662f\u4e2a\u9634\u9669\u5c0f\u4eba\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u6c5f\u6e56\u9669\u6076\uff0c\u4eba\u5fc3\u96be\u6d4b\uff01\u201d\u4ef2\u957f\u7edf\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u6682\u65f6\u4e0d\u5fc5\u63e3\u6d4b\uff0c \u4e14\u542c\u5c01\u5b50\u8d85\u7ec6\u9053\u5176\u8be6\u3002\u201d\u6c5f\u6d77\u5929\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u542c\u4ef2\u5e2e\u4e3b\u7684\u8bed\u6c14\uff0c\u597d\u50cf\u4ed6\u4e5f\u77e5\u9053\u4e86\u4e00\u4e9b \u4ec0\u4e48\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u8bf4\u9053\uff1a\u201c\u4e8b\u60c5\u662f\u8fd9\u6837\u7684\uff0c\u6587\u9053\u5e84\u4ece\u897f\u660c\u9003\u56de\u4eac\u57ce\uff0c\u5e26\u56de\u6d88\u606f\uff0c\u8bf4\u662f\u6c5f\u5927\u4fa0\u4ee5\u53ca\u95e8 \u4eba\u5f1f\u5b50\u90fd\u5728\u5c0f\u91d1\u5ddd\u548c\u897f\u660c\u4e24\u5730\uff0c\u8428\u798f\u9f0e\u4e00\u542c\uff0c\u5c31\u8bf4\u8fd9\u662f\u4e00\u4e2a\u5927\u597d\u7684\u65f6\u673a\uff0c\u53ef\u4ee5\u4e3a\u671d\u5ef7\u4e00\u96ea\u767e \u5e74\u4e4b\u803b\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u83ab\u540d\u5176\u5999\uff0c\u95ee\u9053\uff1a\u201c\u8428\u798f\u9f0e\u8981\u6697\u7b97\u6c5f\u5927\u4fa0\u7684\u5bb6\u4eba\uff0c\u5374\u600e\u7684\u626f\u4e0a\u4e86\u8fd9\u4e48\u5927\u7684\u4e00\u4e2a\u9898 \u76ee\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u8fd9\u5012\u4e0d\u662f\u8428\u798f\u9f0e\u6545\u610f\u5938\u5927\u5176\u8f9e\uff0c\u6211\u66fe\u542c\u5f97\u7239\u7239\u8bf4\u8fc7\u8fd9\u4e2a\u6545\u4e8b\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u9099\u5c71\u6d3e\u7684\u5f00\u5c71\u7956\u5e08\u72ec\u81c2\u795e\u5c3c\u662f\u660e\u671d\u7684\u516c\u4e3b\uff0c\u6e05\u5ef7\u65e9\u5df2\u77e5\u9053\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u5374\u4e0d \u6562\u5ba3\u626c\u51fa\u53bb\u3002\u540e\u6765\u96cd\u6b63\u7687\u5e1d\u7ed9\u72ec\u81c2\u795e\u5c3c\u7684\u5f1f\u5b50\u5415\u56db\u5a18\u523a\u6b7b\uff0c\u6e05\u5ef7\u81ea\u662f\u66f4\u628a\u9099\u5c71\u6d3e\u89c6\u4f5c\u773c\u4e2d \u9489\uff0c\u8089\u4e2d\u523a\uff0c\u6068\u4e0d\u5f97\u62d4\u4e4b\u800c\u540e\u5feb\u4e86\uff01\u53ef\u662f\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5bf9\u7687\u5ba4\u4e43\u662f\u5947\u803b\u5927\u8fb1\uff0c\u7687\u5e1d\u5728\u6df1\u5bab\u7ed9\u4eba \u523a\u6b7b\uff0c\u8bf4\u51fa\u53bb\u989c\u9762\u4f55\u5b58\uff1f\u662f\u4ee5\u53ea\u80fd\u6697\u4e2d\u8bbe\u6cd5\u62a5\u4ec7\uff0c\u8868\u9762\u4e0a\u8fd8\u8981\u906e\u7792\u5462\u3002\u65e2\u662f\u8981\u6697\u4e2d\u62a5\u4ec7\uff0c\u90a3 \u5c31\u4e0d\u80fd\u5174\u5e08\u52a8\u4f17\u4e86\u3002\u767e\u591a\u5e74\u6765\uff0c\u6e05\u5ef7\u66fe\u5c61\u6b21\u6d3e\u9053\u9ad8\u624b\u6697\u7b97\u9099\u5c71\u6d3e\u7684\u9996\u8111\u4eba\u7269\uff0c\u5747\u672a\u5f97\u901e\uff0c\u5415 \u56db\u5a18\u662f\u9099\u5c71\u6d3e\u7684\u7b2c\u4e8c\u4ee3\u638c\u95e8\uff0c\u6211\u7684\u6bcd\u4eb2\u662f\u7b2c\u4e09\u4ee3\u638c\u95e8\uff0c\u5979\u4eec\u90fd\u662f\u6e05\u5ef7\u6240\u8981\u7f09\u6355\u7684\u94a6\u72af\uff0c\u4e00\u751f \u4e4b\u4e2d\uff0c\u4e0d\u77e5\u7ecf\u5386\u8fc7\u591a\u5c11\u98ce\u9669\u3002\u5982\u4eca\u6211\u7684\u5e08\u5ac2\u4e43\u662f\u7b2c\u56db\u4ee3\u638c\u95e8\uff0c\u65f6\u95f4\u867d\u7136\u8fc7\u4e86\u767e\u5e74\uff0c\u8fd9\u6869\u516c\u6848 \u5c1a\u672a\u4e86\u7ed3\uff0c\u6240\u4ee5\u8eab\u4e3a\u6e05\u5ef7\u5927\u5185\u603b\u7ba1\u7684\u8428\u798f\u9f0e\uff0c\u8981\u6697\u7b97\u5979\u4e00\u70b9\u4e5f\u4e0d\u7a00\u5947\uff0c\u4ed6\u6240\u8bf4\u7684\u66ff\u671d\u5ef7\u96ea\u767e \u5e74\u4e4b\u803b\u4e5f\u662f\u4e00\u70b9\u90fd\u4e0d\u5938\u5f20\u7684\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u8428\u798f\u9f0e\u8d77\u521d\u672c\u6765\u60f3\u8bf7\u6587\u9053\u5e84\u4e3b\u6301\u6b64\u4e8b\uff0c\u540e\u6765\u56e0\u4e3a\u6587\u9053\u5e84\u5f3a\u7ec3\u4e09\u8c61\u795e \u529f\uff0c\u8d70\u706b\u5165\u9b54\uff0c\u75af\u766b\u65e5\u751a\uff0c\u8428\u798f\u9f0e\u8ba4\u4e3a\u4ed6\u4e0d\u582a\u91cd\u4efb\uff0c\u53ea\u597d\u53e6\u8bf7\u80fd\u4eba\uff0c\u53ef\u60dc\u8fd9\u4e2a\u80fd\u4eba\u662f\u8c01\uff0c\u6211 \u73b0\u5728\u8fd8\u672a\u77e5\u9053\u3002\u4ed6\u4ea4\u7ed9\u6211\u7684\u547d\u4ee4\u662f\u8981\u6211\u5230\u5f82\u5f95\u5c71\u6765\u4e0e\u6b27\u9633\u575a\u4f1a\u5408\uff0c\u505a\u6b27\u9633\u575a\u7684\u52a9\u624b\u3002\u6b27\u9633\u575a \u5728\u8428\u798f\u9f0e\u8ddf\u524d\u5938\u4e0b\u6d77\u53e3\uff0c\u8bf4\u662f\u80fd\u591f\u8bf7\u5230\u8db3\u4e0e\u6c5f\u5927\u4fa0\u5339\u654c\u7684\u80fd\u4eba\uff0c\u8fd9\u624d\u5f97\u5230\u91cd\u7528\u7684\u3002\u81f3\u4e8e\u8981\u6211 \u53bb\u505a\u4ed6\u7684\u52a9\u624b\uff0c\u90a3\u662f\u56e0\u4e3a\u6211\u66fe\u5230\u6c5f\u5927\u4fa0\u5bb6\u91cc\uff0c\u53ef\u5145\u8bc6\u9014\u8001\u9a6c\u4e4b\u6545\u3002\u6b27\u9633\u575a\u53ef\u80fd\u662f\u5df2\u7ecf\u628a\u8fd9\u4e2a \u80fd\u4eba\u7684\u540d\u5b57\u544a\u8bc9\u4e86\u8428\u798f\u9f0e\u7684\uff0c\u4f46\u8428\u798f\u9f0e\u5374\u6ca1\u6709\u544a\u8bc9\u6211\u3002\u6216\u8bb8\u4e0d\u53ea\u4e00\u4e2a\u80fd\u4eba\uff0c\u4ea6\u672a\u53ef\u77e5\u3002 \u3000\u3000\u201c\u8d77\u521d\u6211\u4ee5\u4e3a\u8fd9\u4e2a\u80fd\u4eba\u662f\u9633\u6d69\uff0c\u5230\u4e86\u5f82\u5f95\u5c71\uff0c\u59cb\u77e5\u9633\u6d69\u6b63\u5728\u91cd\u7ec4\u5929\u9b54\u6559\uff0c\u867d\u7136\u7b54\u5e94\u4e86\u6b27 \u9633\u575a\u5e94\u4e3a\u81c2\u52a9\uff0c\u4f46\u4ed6\u81ea\u8eab\u5374\u662f\u4e0d\u80af\u9732\u9762\u7684\uff0c\u8fd9\u4e2a\u80fd\u4eba\u5f53\u7136\u4e0d\u662f\u4ed6\u4e86\u3002 \u3000\u3000\u201c\u6b64\u4e8b\u6b27\u9633\u575a\u672c\u6765\u6253\u7b97\u5728\u5929\u9b54\u6559\u5f00\u575b\u4e4b\u540e\u8fdb\u884c\u7684\uff0c\u4e0d\u6599\u91d1\u5c11\u4fa0\u7a81\u5982\u5176\u6765\uff0c\u843d\u5f97\u4eca\u65e5\u8fd9\u4e2a \u4e0b\u573a\uff0c\u5b9e\u975e\u4ed6\u4eec\u59cb\u6599\u6240\u53ca\u3002 \u3000\u3000\u201c\u9003\u8d70\u4e4b\u65f6\uff0c\u6211\u8ddf\u7740\u6b27\u9633\u575a\uff0c\u6211\u5e76\u4e0d\u77e5\u9053\u8fd9\u4e2a\u59d3\u725f\u7684\u662f\u8c01\uff0c\u4f46\u89c1\u4ed6\u4e5f\u8ddf\u7740\u6b27\u9633\u575a\u4e00\u9f50 \u8d70\uff0c\u662f\u4ee5\u6211\u5c31\u4e0d\u80fd\u4e0d\u6000\u7591\u6b27\u9633\u575a\u5938\u4e0b\u6d77\u53e3\u8bf4\u662f\u53ef\u4ee5\u8bf7\u5230\u7684\u80fd\u4eba\u5c31\u662f\u4ed6\u4e86\u3002\u201d \u3000\u3000\u4f17\u4eba\u4ed4\u7ec6\u4e00\u60f3\uff1a\u201c\u8db3\u4ee5\u4e0e\u6c5f\u5927\u4fa0\u5339\u654c\u7684\u80fd\u4eba\uff0c\u800c\u53c8\u4e0e\u6b27\u9633\u575a\u6709\u4ea4\u60c5\uff0c\u786e\u5b9e\u4e0e\u725f\u5b97\u6d9b\u7684\u8eab \u4efd\u543b\u5408\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6697\u81ea\u601d\u91cf\uff1a\u201c\u5047\u5982\u725f\u5b97\u6d9b\u521a\u624d\u7684\u8a00\u8bed\uff0c\u5f53\u771f\u53ea\u662f\u9a97\u6211\u5e08\u5144\uff0c\u5374\u62bd\u8eab\u8dd1\u53bb\u6697\u7b97\u6211\u5e08 \u5ac2\u7684\u8bdd\uff0c\u5012\u662f\u6709\u70b9\u53ef\u8651\u5462\u3002\u5e08\u5ac2\u4e0e\u4ed6\u5355\u6253\u72ec\u6597\u662f\u4e0d\u4f1a\u8f93\u7ed9\u4ed6\u7684\uff0c\u4f46\u8981\u80dc\u4ed6\u5374\u4e5f\u5f88\u96be\u3002\u5982\u679c\u4ed6 \u53e6\u5916\u6709\u4e2a\u6b66\u529f\u4e0e\u6b27\u9633\u575a\u76f8\u5f53\u7684\u52a9\u624b\uff0c\u5e08\u5ac2\u5c31\u51b3\u8ba1\u5e94\u4ed8\u4e0d\u4e86\uff01 \u3000\u3000\u516c\u5b59\u5b8f\u60f3\u8d77\u5c01\u5b50\u8d85\u90a3\u6b21\u8bf4\u5047\u8bdd\u9a97\u5389\u5357\u661f\u4e4b\u4e8b\uff0c\u5bf9\u4ed6\u7684\u8bdd\u534a\u4fe1\u534a\u7591\uff0c\u95ee\u9053\uff1a\u201c\u5c01\u5b50\u8d85\uff0c\u4f60 \u8bf4\u7684\u53ef\u90fd\u662f\u771f\u8bdd\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u6ee1\u8138\u59d4\u5c48\u7684\u795e\u6c14\uff0c\u6b63\u8981\u56de\u7b54\uff0c\u5ffd\u542c\u5f97\u4ef2\u957f\u7edf\u5df2\u5728\uff0c\u8bf4\u9053\uff1a\u201c\u5c01\u5b50\u8d85\u8bda\u5fc3\u5f03\u6697\u6295 \u660e\uff0c\u8001\u53eb\u5316\u5012\u662f\u53ef\u4ee5\u7ed9\u4ed6\u4f5c\u4e2a\u8bc1\u660e\uff1a\u4ed6\u8fd9\u6b21\u8bf4\u7684\u90fd\u662f\u771f\u8bdd\uff01\u201d \u3000\u3000\u516c\u5b59\u5b8f\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4e10\u5e2e\u6d88\u606f\u7d20\u6765\u7075\u901a\uff0c\u4ef2\u5e2e\u4e3b\u8fd9\u4e48\u8bf4\uff0c\u60f3\u5fc5\u4e5f\u662f\u542c\u5230\u4ec0\u4e48 \u98ce\u58f0\u7684\u4e86\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u8001\u53eb\u5316\u5c31\u662f\u56e0\u4e3a\u542c\u8bf4\u6c5f\u5927\u4fa0\u5728\u8fd9\u513f\uff0c\u7279\u5730\u8d76\u6765\u544a\u8bc9\u4ed6\u8fd9 \u4e2a\u6d88\u606f\u7684\u3002\u6c5f\u5927\u4fa0\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u56de\u5bb6\u4e00\u8d9f\u7684\u597d\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6211\u8fd8\u662f\u4e0d\u76f8\u4fe1\u725f\u5b97\u6d9b\u4f1a\u7ed9\u6e05\u5ef7\u5229\u7528\u3002\u800c\u4e14\u6b27\u9633\u575a\u521a\u624d\u5df2\u7ed9\u4f60\u7834\u4e86\u4ed6\u7684\u96f7\u795e \u638c\uff0c\u9633\u6d69\u4e5f\u7ed9\u5389\u5357\u661f\u5e9f\u4e86\u4ed6\u7684\u6b66\u529f\uff0c\u725f\u5b97\u6d9b\u5373\u4f7f\u771f\u7684\u8981\u53bb\u6697\u7b97\u6211\u7684\u5bb6\u4eba\uff0c\u4ed6\u5b64\u638c\u96be\u9e23\uff0c\u4e5f\u672a \u5fc5\u5948\u4f55\u5f97\u4e86\u5185\u5b50\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4ef2\u8001\u524d\u8f88\uff0c\u4f60\u542c\u522b\u7684\u6d88\u606f\uff0c\u6b27\u9633\u575a\u6240\u8bf7\u7684\u5341\u80fd\u4eba\u4e4b\u4e2d\uff0c\u53ef\u6709\u725f\u5b97\u6d9b\u5728 \u5185\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u6211\u542c\u5230\u7684\u6d88\u606f\u662f\u8428\u798f\u9f0e\u8fd9\u6b21\u5fd7\u5728\u5fc5\u5f97\uff0c\u636e\u8bf4\u5df2\u7ecf\u8bf7\u6765\u4e86\u5e73\u7d20\u4ece\u672a\u5728\u6c5f\u6e56\u4e0a \u9732\u9762\u7684\u597d\u51e0\u4e2a\u9ad8\u624b\uff0c\u4f46\u6211\u4e0d\u77e5\u6b27\u9633\u575a\u4e5f\u662f\u53c2\u4e0e\u6b64\u4e8b\u7684\u3002\u5426\u5219\u6211\u521a\u624d\u5c31\u4e0d\u4f1a\u653e\u8fc7\u4ed6\u4e86\u3002\u4e0d\u8fc7\u5c01 \u5b50\u8d85\u8bf4\u7684\u548c\u6211\u542c\u6765\u7684\u6d88\u606f\u76f8\u7b26\uff1b\u6240\u4ee5\u6211\u6562\u65ad\u5b9a\u4ed6\u8bf4\u7684\u4e43\u662f\u771f\u8bdd\u3002\u201d\u4ed6\u65e2\u7136\u4e0d\u77e5\u6b27\u9633\u575a\u53c2\u4e0e\u6b64 \u4e8b\uff0c\u90a3\u5c31\u4e0d\u7528\u518d\u8bf4\u4ed6\u4e5f\u662f\u4e0d\u77e5\u9053\u725f\u5b97\u6d9b\u662f\u5426\u4e0e\u6b64\u4e8b\u6709\u5173\u7684\u4e86\u3002\u91d1\u9010\u6d41\u5401\u4e86\u53e3\u6c14\uff0c\u5fc3\u60c5\u8f7b\u677e\u4e86 \u4e00\u4e9b\u3002 \u3000\u3000\u4f46\u4ef2\u957f\u7edf\u5374\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u6211\u867d\u7136\u4e0d\u77e5\u725f\u5b97\u6d9b\u662f\u5426\u53c2\u4e0e\u6b64\u4e8b\uff0c\u4f46\u5c01\u5b50\u8d85\u5e26\u6765\u7684\u6d88\u606f\u6211\u65e2 \u7136\u53ef\u4ee5\u8bc1\u660e\u662f\u786e\u5b9e\u7684\uff0c\u725f\u5b97\u6d9b\u4e5f\u5c31\u8131\u4e0d\u4e86\u5acc\u7591\uff0c\u8bf4\u4e0d\u5b9a\u90a3\u51e0\u4e2a\u4ece\u672a\u5728\u6c5f\u6e56\u4e0a\u9732\u51fa\u7684\u9ad8\u624b\uff0c\u5c31 \u662f\u4ed6\u7684\u540c\u95e8\u5144\u5f1f\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u8001\u53eb\u5316\uff0c\u8fd9\u5c31\u662f\u4f60\u7684\u4e0d\u662f\u4e86\uff0c\u4f60\u65e2\u7136\u77e5\u9053\u6b64\u4e8b\uff0c\u4f55\u4ee5\u4e0d\u66ff\u6c5f\u592b\u4eba\u9632\u5907\uff1f\u542c \u4f60\u8fd9\u4e48\u8bf4\uff0c\u8428\u798f\u9f0e\u6d3e\u6765\u7684\u4eba\u4e0d\u53ea\u4e00\u4f19\uff0c\u6b27\u9633\u575a\u3001\u725f\u5b97\u6d9b\u4e0d\u8fc7\u5176\u4e2d\u4e00\u4f19\u800c\u5df2\u3002\u4f60\u8001\u8fdc\u7684\u8d76\u6765\u8fd9 \u513f\u62a5\u4fe1\uff0c\u8fd9\u56fa\u7136\u4e5f\u662f\u8981\u505a\u7684\u4e8b\uff0c\u4f46\u4e07\u4e00\u8428\u798f\u9f0e\u6d3e\u6765\u7684\u90a3\u4e9b\u4eba\uff0c\u7b49\u4e0d\u53ca\u548c\u6b27\u9633\u575a\u4f1a\u5408\u3002\u5c31\u5230\u6c5f \u5927\u4fa0\u5bb6\u91cc\u7684\u8bdd\uff0c\u6c5f\u592b\u4eba\u5c82\u4e0d\u662f\u5f88\u5371\u9669\u4e48\uff1f\u800c\u4e14\u725f\u5b97\u6d9b\u5df2\u7ecf\u8dd1\u4e86\uff0c\u4ed6\u4e5f\u53ef\u4ee5\u8d74\u5728\u54b1\u4eec\u7684\u524d\u5934\u5230 \u6c5f\u5bb6\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u516c\u5b59\u8001\u5f1f\uff0c\u60f3\u4e0d\u5230\u4f60\u7684\u6027\u5b50\u6bd4\u8001\u53eb\u5316\u8fd8\u6025\uff0c\u8001\u53eb\u82b1\u5c1a\u672a\u8bf4\u5b8c\u5462\uff0c\u4f60\u600e\u4e48 \u77e5\u9053\u6211\u6ca1\u6709\u9632\u5907\uff1f\u6211\u5df2\u7ecf\u901a\u77e5\u4e86\u9099\u5c71\u6d3e\uff0c\u53eb\u9099\u5c71\u6d3e\u7684\u56db\u5927\u5f1f\u5b50\u706b\u901f\u8d76\u53bb\u8d74\u63f4\u4e86\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u9099\u5c71\u6d3e\u7684\u56db\u5927\u5f1f\u5b50\u6b66\u529f\u56fa\u7136\u5f88\u662f\u4e0d\u5f31\uff0c\u4f46\u6bd4\u4e4b\u725f\u5b97\u6d9b\u6050\u6015\u8fd8\u662f\u6709\u6240\u4e0d\u5982 \u5427\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u4e3a\u4ec0\u4e48\u8981\u50ac\u4fc3\u6c5f\u5927\u4fa0\u56de\u5bb6\u7684\u539f\u56e0\u4e86\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u63a5\u4e0b\u53bb\u8bf4\u9053\uff0c\u201c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6211\u542c\u5230\u8fd9\u4e2a\u6d88\u606f\u4e4b\u540e\uff0c\u8d77\u521d\u6211\u4ecd\u662f\u672a\u628a\u8428\u798f\u9f0e\u8bf7\u6765\u7684 \u4ec0\u4e48\u80fd\u4eba\u653e\u5728\u773c\u5185\u7684\uff0c\u6211\u60f3\u4e2d\u539f\u671d\u6b66\u6797\u4eba\u7269\uff0c\u54ea\u4e00\u4e2a\u6211\u4e0d\u77e5\u9053\u5373\u4f7f\u6709\u4ece\u672a\u9732\u8fc7\u9762\u7684\uff0c\u6599\u60f3\u672c \u9886\u4e5f\u51b3\u4e0d\u4f1a\u9ad8\u5f97\u8fc7\u6c5f\u5927\u4fa0\u592b\u59bb\uff0c\u6709\u9099\u5c71\u6d3e\u7684\u56db\u5927\u5f1f\u5b50\u7387\u4f17\u8d76\u53bb\u8d74\u63f4\u5df2\u662f\u8db3\u591f\u7684\u4e86\u3002\u4f46\u5982\u4eca\u6211 \u89c1\u4e86\u725f\u5b97\u6d9b\u7684\u6b66\u529f\uff0c\u59cb\u77e5\u6d77\u5916\u5c1a\u6709\u9ad8\u660e\u4e4b\u58eb\uff0c\u6b66\u529f\u7edd\u4e0d\u900a\u4e8e\u4e2d\u539f\uff0c\u6211\u5012\u662f\u6599\u654c\u592a\u8f7b\u4e86\u3002\u6c5f\u5927 \u4fa0\uff0c\u6211\u770b\u4f60\u8fd8\u662f\u56de\u53bb\u4e00\u8d9f\u5427\u3002\u8fd9\u91cc\u7684\u4e8b\uff0c\u53eb\u516c\u5b59\u8001\u513f\u7ed9\u4f60\u6599\u7406\uff0c\u4e5f\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4eca\u5929\u6765\u7684\u670b\u53cb\uff0c\u6709\u8bb8\u591a\u662f\u60f3\u8981\u548c\u6211\u89c1\u9762\u7684\uff0c\u6211\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u5931\u671b\uff0c\u629b\u4e0b\u4ed6\u4eec \u5c31\u8d70\u3002\u518d\u8bf4\u8d81\u7740\u7ea2\u7f28\u4f1a\u3001\u516d\u5408\u5e2e\u7684\u4eba\u90fd\u5728\u8fd9\u91cc\u3002\u54b1\u4eec\u6b63\u597d\u548c\u5176\u4ed6\u5404\u4e2a\u5e2e\u4f1a\u5546\u8bae\u7ed3\u76df\u4e4b\u4e8b\uff0c\u8fd9 \u6837\u53ef\u4ee5\u5927\u5927\u6709\u52a9\u4e8e\u53cd\u6e05\u7684\u4e49\u519b\uff0c\u6211\u53c8\u5c82\u53ef\u4e3a\u4e86\u5bb6\u4e8b\uff0c\u629b\u5f00\u5927\u4e8b\u4e0d\u7ba1\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u8428\u798f\u9f0e\u8981\u5bb3\u4f60\u7684\u5bb6\u4eba\uff0c\u8fd9\u4e5f\u4e0d\u80fd\u8bf4\u662f\u5c0f\u4e8b\u53ef\uff01\u201d\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u4e5f \u4e0d\u4ec5\u4ec5\u662f\u4f60\u7684\u5bb6\u4e8b\u5440\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u6bd4\u8d77\u4e49\u519b\u7684\u4e8b\u60c5\u6765\uff0c\u90a3\u5c31\u662f\u5c0f\u4e8b\u4e86\u3002\u4f55\u51b5\u4ef2\u5e2e\u4e3b\u6240\u62c5\u5fc3\u7684\u53ea\u662f\u4e00\u4e2a\u5047\u8bbe \u800c\u5df2\uff0c\u90a3\u4e9b\u4eba\u672a\u5fc5\u5c31\u4f1a\u6709\u8fd9\u6837\u5feb\u4e0b\u624b\uff0c\u725f\u5b97\u6d9b\u4e5f\u672a\u5fc5\u5c31\u662f\u8428\u798f\u9f0e\u6240\u9080\u8bf7\u7684\u2018\u80fd\u4eba\u2019\u3002\u53c8\u4f55\u51b5 \u6709\u9099\u5c71\u6d3e\u7684\u5f1f\u5b50\u5df2\u7ecf\u6765\u4e86\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u77e5\u9053\u6c5f\u6d77\u5929\u7684\u813e\u6c14\u7d20\u6765\u8bf4\u4e00\u4e0d\u4e8c\uff0c\u77e5\u9053\u529d\u4ed6\u4e0d\u52a8\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u597d\u5728\u5e9c\u4e0a\u79bb\u8fd9\u513f \u4e5f\u4e0d\u8fc7\u4e8c\u767e\u591a\u91cc\uff0c\u90a3\u5c31\u8fd9\u6837\u5427\uff0c\u660e\u5929\u4e00\u65e9\uff0c\u8001\u53eb\u5316\u966a\u4f60\u56de\u53bb\uff0c\u4f60\u53ef\u4e0d\u80fd\u518d\u803d\u6401\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u7b11\u9053\uff1a\u201c\u662f\u5440\uff0c\u5e86\u529f\u5bb4\u73b0\u5728\u60f3\u5fc5\u5df2\u7ecf\u6446\u8d77\u6765\u4e86\u3002\u54b1\u4eec\u5148\u56de\u53bb\u559d\u9152\u518d\u8bf4\u5427\uff0c\u660e\u5929\u6211 \u7b54\u5e94\u4f60\u56de\u53bb\u5c31\u662f\u3002\u5c01\u5148\u751f\uff0c\u4f60\u8fd9\u6b21\u5f03\u6697\u6295\u660e\uff0c\u8fd9\u5e86\u529f\u5bb4\u7684\u9152\uff0c\u4f60\u4e5f\u662f\u53ef\u4ee5\u559d\u5f97\u7684\uff0c\u54b1\u4eec\u4e00\u540c \u8d70\u5440\uff01\u201d\u201c\u5c01\u5b50\u8d85\u6ee1\u9762\u901a\u7ea2\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u8fd9\u4e2a\u2026\u2026\u201d\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u522b\u8fd9\u4e2a\u90a3\u4e2a\u4e86\uff0c \u6c5f\u5927\u4fa0\u65e2\u7136\u8bf7\u4f60\u53bb\uff0c\u4f60\u5c31\u53bb\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5c01\u5148\u751f\uff0c\u4f60\u662f\u4e0d\u662f\u8fd8\u6709\u4e00\u4e9b\u8bdd\u8981\u8bf4\uff1f\u201d \u3000\u3000\u5c01\u5b50\u8d85\u970d\u7136\u4e00\u7701\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u8fd8\u6709\u4e00\u4e2a\u6d88\u606f\u8981\u544a\u8bc9\u4f60\uff0c\u4eba\u591a\u7684\u5730\u65b9\uff0c\u4e0d\u4fbf\u8bf4 \u7684\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u7b11\u9053\uff1a\u201c\u4f60\u7684\u6d88\u606f\u5012\u662f\u5f88\u4e0d\u5c11\u5440\uff0c\u597d\uff0c\u90a3\u5c31\u8d76\u5feb\u8bf4\u5427\uff0c\u522b\u803d\u8bef\u65f6\u95f4\u4e86\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u521a\u8981\u8bf4\u51fa\u8fd9\u4e2a\u6d88\u606f\uff0c\u516c\u5b59\u5b8f\u5ffd\u5730\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u53c8\u662f\u8c01\u4eba\u6765\u4e86\uff1f\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u6797\u4e2d\u73b0\u51fa\u4e24\u4e2a\u4eba\u5f71\uff0c\u5c01\u5b50\u8d85\u62ac\u5934\u4e00\u671b\uff0c\u4e0d\u7531\u5f97\u60ca\u559c\u4ea4\u96c6\uff0c\u53eb\u9053\uff1a\u201c\u5ae6 \u513f\uff0c\u4f60\u4eec\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u539f\u6765\u6765\u7684\u8fd9\u65f6\u5c11\u5e74\u7537\u5973\uff0c\u6b63\u662f\u4ed6\u7684\u5973\u513f\u5c01\u5999\u5ae6\u548c\u79e6\u5143\u6d69\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u770b\u89c1\u7236\u4eb2\u548c\u6c5f\u6d77\u5929\u3001\u91d1\u9010\u6d41\u7b49\u4eba\u540c\u5728\u4e00\u8d77\uff0c\u4e5f\u662f\u5927\u51fa\u610f\u6599\u4e4b\u5916\uff0c\u4e00\u65f6\u95f4\u7adf\u4e0d\u77e5\u8bf4 \u4ec0\u4e48\u8bdd\u597d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u5ae6\u513f\uff01\u6765\u5f97\u6b63\u597d\uff0c\u6211\u7684\u5a92\u5df2\u7ecf\u8bf4\u6210\u529f\u4e86\uff0c\u79e6\u5144\uff0c\u4f60\u5feb\u6765\u5411\u5cb3\u7236\u53e9\u5934\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u53ea\u9053\u91d1\u9010\u6d41\u662f\u5f00\u4ed6\u73a9\u7b11\uff1f\u5fc3\u4e2d\u60f3\uff1a\u201c\u4ed6\u662f\u6e05\u5ef7\u7684\u5927\u5185\u4f8d\u536b\uff0c\u53eb\u6211\u5982\u4f55\u80fd\u591f\u5411\u4ed6\u53e9 \u5934\uff1f\u201d\u5c01\u5999\u5ae6\u4e0d\u77e5\u9053\u7236\u4eb2\u4e0e\u4ed6\u4eec\u4e00\u8d77\u7a76\u7adf\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u60c5\uff0c\u9762\u4e0a\u4e00\u9635\u9752\u4e00\u9635\u7ea2\u3002 \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u6559\u4f60\u4eec\u5f97\u77e5\uff0c\u5c01\u59d1\u5a18\uff0c\u4f60\u7684\u7239\u7239\u5982\u4eca\u5df2\u662f\u75db\u6094\u524d\u975e\uff0c\u4e0d\u4f46\u7b54\u5e94\u4e86 \u4f60\u4eec\u7684\u5a5a\u4e8b\uff0c\u800c\u4e14\u4e5f\u662f\u54b1\u4eec\u7684\u81ea\u5df1\u4eba\u5566\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u91d1\u8001\u5f1f\u867d\u7136\u5e73\u65e5\u559c\u6b22\u5f00\u4eba\u73a9\u7b11\uff0c\u8fd9\u6b21\u8bf4\u7684\u5374\u662f\u4e00\u70b9\u4e0d\u5047\uff0c\u5c01\u5148\u751f\u7684\u786e\u5b9a \u5f03\u6697\u6295\u660e\u3002\u5143\u6d69\uff0c\u4f60\u5c31\u8fc7\u6765\u53e9\u5934\u5427\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u4e0e\u5c01\u5999\u5ae6\u542c\u4e86\u4ef2\u957f\u7edf\u7684\u8bdd\uff0c\u624d\u76f8\u4fe1\u8fd9\u662f\u4e8b\u5b9e\uff0c\u4e24\u4eba\u7684\u559c\u6b22\u90a3\u5c31\u4e0d\u7528\u8bf4\u4e86\u3002\u5f53\u4e0b\u5c01 \u5999\u5ae6\u6b22\u5929\u559c\u5730\u5730\u53eb\u4e86\u58f0\u201c\u7239\u7239\uff01\u201d\u79e6\u5143\u6d69\u4e5f\u5fc3\u7518\u6e05\u613f\u7684\u884c\u4e86\u5927\u793c\uff0c\u7ea2\u4e86\u9762\u5b54\uff0c\u9ad8\u9ad8\u5174\u5174\u7684\u5bf9 \u5c01\u5b50\u8d85\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u5cb3\u7236\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u7709\u5f00\u773c\u7b11\u7684\u5c06\u79e6\u5143\u6d69\u6276\u8d77\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5e78\u4e8f\u6211\u56de\u6597\u672a\u665a\uff0c\u5426\u5219\u4e0d\u4f46\u5bcc\u8d35\u96be\u6c42\uff0c \u8fde\u5973\u513f\u5973\u5a7f\u4e5f\u8981\u5931\u6389\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u79e6\u5144\uff0c\u4f60\u4eec\u600e\u4e48\u4e5f\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u516c\u5b59\u59d1\u5a18\u51fa\u53bb\u627e\u5389\u5927\u54e5\uff0c\u4e0d\u89c1\u56de\u6765\uff1b\u4f60\u548c\u53f2\u59d1\u5a18\u8ddf\u7740\u51fa\u53bb\u5bfb\u627e\uff0c\u53c8\u4e0d\u89c1\u56de \u6765\u3002\u7afa\u8001\u524d\u8f88\u5f88\u662f\u62c5\u5fc3\uff0c\u662f\u4ee5\u6211\u4eec\u56de\u6765\u627e\u4f60\uff0c\u5018\u82e5\u627e\u4e0d\u89c1\u4f60\uff0c\u5c31\u5230\u7ea2\u7f28\u4f1a\u62a5\u4fe1\u3002\u6211\u5230\u4e86\u7ea2\u7f28 \u4f1a\u603b\u8235\uff0c\u77e5\u9053\u516c\u5b59\u8235\u4e3b\u5728\u8fd9\u513f\uff0c\u6240\u4ee5\u9a6c\u4e0a\u548c\u5999\u5ae6\u8d76\u6765\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u591a\u8c22\u4f60\u4eec\u4e3a\u670b\u53cb\u5954\u8d70\u7684\u4e00\u7247\u70ed\u5fc3\uff0c\u5c0f\u5973\u548c\u5389\u5c11\u4fa0\u5e73\u5b89\u65e0\u4e8b\uff0c\u5982\u4eca\u6b63\u5728\u5c71 \u4e0a\uff0c\u7b49\u4f1a\u513f\u4f60\u5c31\u53ef\u4ee5\u89c1\u5230\u4ed6\u4eec\u4e86\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u4eec\u5728\u4e49\u519b\u4e4b\u4e2d\uff0c\u6211\u5f88\u9ad8\u5174\u3002\u4f60\u4eec\u8fc7\u5f97\u597d\u4e48\uff1f\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u7afa\u8001\u524d\u8f88\u8fd9\u652f\u4e49\u519b\uff0c\u85cf\u5728\u5927\u51c9\u5c71\u4e2d\uff0c\u65e5\u5b50\u5f53\u7136\u8fc7\u5f97\u82e6\u4e86\u70b9\uff0c\u4f46\u5927\u5bb6\u90fd\u4f3c\u5bb6 \u4eba\u4e00\u822c\uff0c\u5341\u5206\u5feb\u6d3b\u3002\u201d\u79e6\u5143\u6d69\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u65e5\u5b50\u8fc7\u5f97\u82e6\uff0c\u8fd9\u4e5f\u662f\u2018\u62dc\u2019\u5b98\u519b\u4e4b\u2018\u8d50\u2019\uff0c\u8c01\u4eba \u4e5f\u4e0d\u57cb\u6028\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u53c8\u662f\u9ad8\u5174\uff0c\u53c8\u662f\u60ed\u6127\uff0c\u8bf4\u9053\uff1a\u201c\u8fc7\u53bb\u6211\u6295\u9760\u671d\u5ef7\u548c\u4e49\u519b\u4f5c\u5bf9\uff0c\u8bf4\u6765\u771f\u662f\u60ed\u6127\uff0c\u4f46 \u5982\u4eca\u90fd\u6709\u4e00\u4e2a\u673a\u4f1a\uff0c\u6216\u8005\u53ef\u4ee5\u4ee4\u6211\u7a0d\u8d4e\u7f6a\u884d\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u521a\u624d\u6240\u8981\u8bf4\u7684\u90a3\u4e2a\u6d88\u606f\uff0c\u6562\u60c5\u5c31\u662f\u548c\u4e49\u519b\u6709\u5173\u7684\u6d88\u606f\uff01\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b63\u662f\u8428\u798f\u9f0e\u900f\u9732\u51fa\u6765\u7684\uff0c\u671d\u5ef7\u51c6\u5907\u5982\u4f55\u5bf9\u4ed8\u4f60\u4eec\u8fd9\u652f\u4e49\u519b\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u201c\u8428\u798f\u9f0e\u8bf4\u4f60\u4eec\u8fd9\u652f\u4e49\u519b\u8eb2\u5728\u6df1\u5c71\u91cc\u9762\uff0c\u5b98\u519b\u2018\u8fdb\u88ad\u2019\u4e0d\u6613\uff0c\u4ed6\u51c6\u5907\u7528\u501f\u5200\u6740\u4eba\u4e4b\u8ba1\uff0c \u7b3c\u7edc\u9752\u6d77\u7684\u4e94\u4e2a\u76df\u65d7\u914b\u957f\uff0c\u53eb\u4ed6\u4eec\u4e0e\u4e49\u519b\u4e3a\u96be\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6761\u8ba1\u7b56\u679c\u7136\u6bd2\u8fa3\u65e0\u6bd4\uff0c\u82e5\u662f\u7ed9\u4ed6\u9634\u8c0b\u5f97\u901e\uff0c\u4e0d\u4f46\u7afa\u5c1a\u7236\u8fd9 \u652f\u4e49\u519b\u96be\u4ee5\u5728\u5927\u51c9\u5c71\u7acb\u8db3\uff0c\u5f04\u5f97\u4e0d\u597d\uff0c\u53ea\u6015\u8fd8\u4f1a\u6f14\u6210\u6c49\u56de\u4e4b\u4e89\u3002\u201d \u3000\u3000\u539f\u6765\u897f\u5eb7\u9752\u6d77\u4e00\u5e26\uff0c\u4e43\u662f\u6c11\u65cf\u590d\u6742\u7684\u5730\u533a\uff0c\u6700\u4e3b\u8981\u7684\u4e24\u4e2a\u6c11\u65cf\u4e43\u662f\u6c49\u65cf\u4e0e\u56de\u65cf\uff0c\u5728\u897f\u5eb7 \u6c49\u65cf\u7684\u4eba\u6570\u5dee\u4e0d\u591a\u7b49\u4e8e\u5176\u4ed6\u51e0\u4e2a\u5c11\u6570\u6c11\u65cf\u7684\u603b\u548c\uff0c\u4f46\u662f\u5728\u9752\u6d77\u5219\u662f\u4ee5\u56de\u65cf\u4e3a\u4e3b\uff0c\u6c49\u65cf\u53cd\u800c\u662f \u5c11\u6570\u6c11\u65cf\u4e86\u3002 \u3000\u3000\u5018\u82e5\u8428\u798f\u9f0e\u7b3c\u7edc\u9752\u6d77\u5404\u76df\u65d7\u914b\u957f\u7684\u8ba1\u5212\u6210\u529f\uff0c\u4e49\u519b\u662f\u8981\u4ece\u9752\u6d77\u53d6\u5f97\u8865\u7ed9\u7684\uff0c\u56e0\u6b64\u5373\u4f7f\u90a3 \u4e9b\u914b\u957f\u4e0d\u52a9\u6e05\u519b\u6765\u6253\u4e49\u519b\uff0c\u4e49\u519b\u7684\u7cae\u98df\u4e5f\u8981\u53d1\u751f\u95ee\u9898\u3002\u5982\u679c\u6253\u8d77\u4ed7\u6765\uff0c\u4e49\u519b\u5c31\u66f4\u8981\u9677\u4e8e\u6781\u4e3a \u96be\u7684\u5883\u5730\uff0c\u56e0\u4e3a\u4e49\u519b\u662f\u7edd\u4e0d\u80fd\u4f24\u5bb3\u5c11\u6570\u6c11\u65cf\u7684\u5229\u76ca\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\u5728\u54b1\u4eec\u77e5\u9053\u5f97\u65e9\uff0c\u54b1\u4eec\u53ef\u4ee5\u8d76\u5feb\u53bb\u901a\u77e5\u7afa\u8001\u524d\u8f88\uff0c\u8bf7\u4ed6\u8bbe\u6cd5\u963b\u6b62\u90a3\u4e9b\u914b \u957f\u4e0a\u6e05\u5ef7\u7684\u5f53\u3002\u201d \u3000\u3000\u5c01\u5b50\u8d85\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u6211\u51fa\u4eac\u4e4b\u65f6\uff0c\u8428\u798f\u9f0e\u5df2\u7ecf\u6d3e\u51fa\u4f7f\u8005\uff0c\u51c6\u5907\u53bb\u6e38\u8bf4\u90a3\u4e9b\u914b\u957f\uff0c\u4ed6 \u7684\u624b\u6bb5\u4e0d\u5916\u4e24\u79cd\uff1a\u8bb8\u4ee5\u91cd\u5229\uff0c\u5c01\u4ee5\u5b98\u7235\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u54b1\u4eec\u5c31\u6653\u4ee5\u5927\u4e49\uff0c\u8bf4\u4ee5\u5229\u5bb3\u3002\u6211\u60f3\u56de\u65cf\u4e4b\u4e2d\uff0c\u4e00\u5b9a\u4e0d\u4e4f\u89c1\u8bc6\u9ad8\u660e\u4e4b\u58eb\uff0c\u5373 \u4f7f\u90a3\u4e9b\u914b\u957f\u53d7\u773c\u524d\u7684\u5c0f\u5229\u6240\u8ff7\u60d1\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u4f1a\u8ddf\u7740\u8d70\u7684\uff0c\u4e0d\u8fc7\uff0c\u4e49\u519b\u6d3e\u51fa\u53bb\u7684\u4f7f\u8005\uff0c\u6700\u597d\u80fd \u591f\u8d76\u5728\u8428\u798f\u9f0e\u4f7f\u8005\u7684\u524d\u5934\uff0c\u5426\u5219\u53bb\u5f97\u592a\u8fdf\uff0c\u6240\u4e0b\u7684\u529f\u592b\u5c31\u8981\u52a0\u500d\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u613f\u610f\u62c5\u5f53\u8fd9\u4e2a\u5dee\u4f7f\uff0c\u660e\u5929\u4e00\u65e9\u4fbf\u8d70\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u672c\u6765\u60f3\u8981\u81ea\u5df1\u53bb\u7684\uff0c\u542c\u89c1\u91d1\u9010\u6d41\u81ea\u544a\u594b\u52c7\uff0c\u5fc3\u60f3\uff1a\u201c\u5e08\u5f1f\u7684\u529f\u592b\u5728\u6211\u4e4b\u4e0a\uff0c\u6709\u4ed6 \u8d76\u53bb\uff0c\u6211\u5012\u662f\u53ef\u4ee5\u653e\u5fc3\u3002\u201d\u5f53\u4e0b\u8c22\u8fc7\u4e86\u5c01\u5b50\u8d85\u62a5\u4fe1\u4e4b\u529f\uff0c\u4e00\u884c\u4eba\u7b49\uff0c\u56de\u8f6c\u5929\u9b54\u6559\u603b\u8235\u3002\u97e9\u6b63 \u8fbe\u6b63\u7b49\u7740\u5fc3\u7126\uff0c\u770b\u89c1\u4ed6\u4eec\u56de\u6765\uff0c\u5927\u559c\u8bf4\u9053\uff1a\u201c\u9152\u5e2d\u90fd\u5df2\u6446\u597d\u4e86\uff0c\u6211\u6b63\u8981\u6d3e\u4eba\u53bb\u627e\u4f60\u4eec\u5462\uff0c \u55ef\uff0c\u60f3\u4e0d\u5230\u4ef2\u5e2e\u4e3b\u4e5f\u6765\u4e86\uff0c\u8fd8\u6709\u8fd9\u4e24\u4f4d\u5c11\u5e74\u4fa0\u58eb\uff0c\u4eca\u513f\u53ef\u771f\u662f\u70ed\u95f9\u4e86\uff01\u201d\u4ed6\u6ca1\u6709\u95ee\u5c01\u5b50\u8d85\uff0c \u663e\u7136\u662f\u56e0\u4e3a\u4e0d\u77e5\u5e95\u7ec6\uff0c\u611f\u5230\u96be\u4ee5\u63aa\u8f9e\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u7ed9\u4ed6\u4ecb\u7ecd\u4e86\u79e6\u5143\u6d69\u3001\u5c01\u5999\u5ae6\u4e8c\u4eba\uff0c\u8ddf\u7740\u5411\u4ed6\u8bf4\u660e\u5c01\u5b50\u8d85\u5f03\u6697\u6295\u660e\u4e4b\u4e8b\uff0c\u97e9\u6b63\u8fbe\u559c \u4e0a\u52a0\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u4f60\u4eec\u8fdb\u53bb\uff0c\u6211\u4e5f\u6709\u4e2a\u597d\u6d88\u606f\u544a\u8bc9\u4f60\u4eec\u3002\u201d \u3000\u3000\u8fdb\u5165\u9999\u5802\uff0c\u53ea\u89c1\u957f\u9cb8\u5e2e\u7684\u5e2e\u4e3b\u5b59\u767e\u7984\u5e26\u9886\u5176\u4ed6\u51e0\u4e2a\u5e2e\u4f1a\u7684\u9996\u8111\u4eba\u7269\u51fa\u8fce\uff0c\u539f\u6765\u4ed6\u4eec\u7684\u6bd2 \u4f24\u7ecf\u5389\u5357\u661f\u3001\u674e\u6566\u4e8c\u4eba\u533b\u6cbb\uff0c\u867d\u7136\u5c1a\u672a\u75ca\u6108\uff0c\u4f46\u5df2\u662f\u53ef\u4ee5\u884c\u52a8\u5982\u5e38\u3002 \u3000\u3000\u5b59\u767e\u7984\u8c22\u8fc7\u4e86\u53f2\u7ea2\u82f1\u7684\u5927\u6069\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e00\u5411\u552f\u8d35\u5e2e\u9a6c\u9996\u662f\u77bb\uff0c\u4eca\u540e\u4e5f\u662f\u8fd9\u6837\uff0c\u8bf4\u8001 \u5b9e\u8bdd\uff0c\u6211\u4eec\u5bf9\u4ee4\u5144\u53ea\u662f\u2018\u754f\u5a01\u2019\u5982\u4eca\u5bf9\u53f2\u59d1\u5a18\u5219\u662f\u2018\u6000\u5fb7\u2019\u3002\u53f2\u59d1\u5a18\u5bf9\u6211\u4eec\u5982\u6b64\u5bbd\u539a\uff0c\u53c8\u6709 \u6551\u547d\u4e4b\u6069\uff0c\u6211\u4eec\u4eba\u4eba\u5fc3\u60a6\u8bda\u670d\uff0c\u4eca\u540e\u82e5\u6709\u5dee\u9063\uff0c\u6211\u4eec\u8d74\u6c64\u8e48\u706b\uff0c\u4ea6\u4e0d\u6562\u8f9e\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1b\u201c\u7ea2\u82b1\u7eff\u53f6\uff0c\u540c\u662f\u4e00\u5bb6\u3002\u60a3\u96be\u76f8\u52a9\uff0c\u4efd\u6240\u5e94\u4e3a\u3002\u4e9b\u987b\u5c0f\u4e8b\uff0c\u4f55\u8db3\u6302\u9f7f\uff0c\u5404\u95e8 \u6d3e\u5404\u81ea\u6240\u5c5e\u7684\u5e2e\u4f1a\u867d\u6216\u6709\u5927\u5c0f\u4e4b\u5206\uff0c\u5374\u65e0\u5c0a\u5351\u4e4b\u522b\u3002\u516d\u5408\u5e2e\u613f\u4e0e\u8bf8\u4f4d\u7ed3\u76df\uff0c\u96c6\u5927\u5c0f\u5e2e\u4f1a\u4e4b \u529b\uff0c\u540c\u52a9\u4e49\u519b\uff0c\u4e0d\u77e5\u8bf8\u4f4d\u610f\u4e0b\u5982\u4f55\uff1f\u201d \u3000\u3000\u5b59\u767e\u7984\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\u9ad8\u77bb\u8fdc\u77a9\uff0c\u7ed9\u6211\u4eec\u6307\u51fa\u4e86\u8fd9\u4e00\u6761\u660e\u8def\uff0c\u5b59\u67d0\u4e0d\u624d\uff0c\u7518\u613f\u6267\u97ad\u968f\u978d\uff0c \u8bf7\u53f2\u5e2e\u4e3b\u5c31\u4f5c\u6211\u4eec\u7684\u76df\u4e3b\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u5176\u4ed6\u51e0\u4e2a\u5c0f\u5e2e\u4f1a\u7684\u5e2e\u4e3b\u5f02\u53e3\u540c\u58f0\uff0c\u4e00\u81f4\u8d5e\u540c\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u6211\u5e74\u8f7b\u8bc6\u6d45\uff0c\u5982\u4ee5\u80fd\u591f\u62c5\u5f53\u6b64\u4e00\u91cd\u4efb\u3002\u4f9d\u6211\u4e4b\u89c1\uff0c\u8fd8\u662f\u8bf7\u7ea2\u7f28\u4f1a\u7684\u516c\u5b59\u8235 \u4e3b\u505a\u54b1\u4eec\u7684\u76df\u4e3b\u6700\u597d\uff01\u201d \u3000\u3000\u516c\u5b59\u5b8f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u5e74\u7eaa\u8001\u4e86\uff0c\u8fd9\u4e2a\u62c5\u5b50\u6050\u6015\u662f\u6311\u4e0d\u8d77\u6765\u7684\u4e86\u3002\u5018\u82e5\u6211\u5e74\u8f7b\u4e09\u5341\u5e74\uff0c \u6211\u4e00\u5b9a\u4e0d\u4f1a\u63a8\u8f9e\u7684\u3002\u53f2\u8d24\u4f84\uff0c\u4f60\u5c31\u4f53\u6064\u4f53\u6064\u6211\u5427\u3002\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5373\u662f\u529d\u53f2\u7ea2\u82f1\u4e0d\u5fc5\u63a8\u8f9e\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u5fc5\u63a8\u6765\u8ba9\u53bb\u4e86\u3002\u6211\u8001\u53eb\u5316\u5b50\u5012\u662f\u60f3\u505a\uff0c\u53ef\u60dc\u6211\u7684\u5e74\u7eaa\u6bd4\u516c\u5b59\u8001\u5f1f\u66f4 \u5927\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u7b11\u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u4f60\u542c\uff0c\u4ef2\u5e2e\u4e3b\u4e5f\u662f\u8ba4\u4e3a\u4f60\u505a\u66f4\u5408\u9002\u5417\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u552f\u8f9e\u4e0d\u5141\uff0c\u4ef2\u7edf\u9053\uff1a\u201c\u597d\u4e86\uff0c\u597d\u4e86\uff0c\u6211\u90fd\u542c\u5f97\u4e0d\u8010\u70e6\u4e86\u3002\u4f60\u4eec\u65e2\u7136\u63a8\u6765\u8ba9\u53bb\uff0c\u6211 \u5fc3\u76ee\u4e2d\u5012\u6709\u4e00\u4e2a\u4eba\uff0c\u6bd4\u4f60\u4eec\u66f4\u5408\u9002\u7684\u3002\u4f60\u4eec\u4e0d\u4f1a\u602a\u6211\u8fd9\u8bdd\u8bf4\u5f97\u592a\u8349\u7387\u5427\uff1f\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u5927\u559c\u9053\uff1a\u201c\u65e2\u7136\u6709\u8fd9\u6837\u4e00\u4e2a\u4eba\uff0c\u90a3\u5c31\u66f4\u597d\u4e86\uff01\u201d \u3000\u3000\u7ea2\u7f28\u4f1a\u7684\u51e0\u4e2a\u9999\u4e3b\u9887\u4e3a\u8be7\u5f02\uff0c\u5fc3\u91cc\u4e5f\u90fd\u6709\u70b9\u513f\u4e0d\u670d\u6c14\uff0c\u4f46\u662f\u8bf4\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u6700\u5927\u7684\u4e24\u4e2a \u5e2e\u4f1a\u5c31\u662f\u516d\u5408\u5e2e\u548c\u6211\u4eec\u7684\u7ea2\u7f28\u4f1a\uff0c\u54ea\u91cc\u8fd8\u6709\u7b2c\u4e09\u4e2a\u4eba\u914d\u505a\u6211\u4eec\u7684\u76df\u4e3b\uff1f\u201d\u4e8e\u662f\u7ea2\u7f28\u4f1a\u6392\u540d\u7b2c \u4e09\u7684\u9999\u4e3b\u77f3\u7384\u9996\u5148\u95ee\u9053\uff1a\u201c\u4e0d\u77e5\u4ef2\u8001\u524d\u8f88\u8bf4\u7684\u8fd9\u4f4d\u5927\u82f1\u96c4\u662f\u8c01\uff1f\u201d \u3000\u3000\u4ef2\u6c0f\u7edf\u9053\uff1a\u201c\u6211\u8bf7\u8fd9\u4f4d\u5927\u82f1\u96c4\u51fa\u6765\u4e4b\u524d\uff0c\u5148\u5f97\u8bf7\u4f60\u4eec\u628a\u540d\u76ee\u6539\u4e00\u6539\u3002\u4e0d\u662f\u8981\u4ed6\u505a\u5404\u4e2a\u5e2e \u4f1a\u7684\u76df\u4e3b\uff0c\u800c\u662f\u8981\u4ed6\u505a\u6b66\u6797\u76df\u4e3b\uff0c\u8fd9\u624d\u9002\u5408\u4ed6\u7684\u8eab\u5206\u3002\u201d \u3000\u3000\u77f3\u7384\u84e6\u5730\u7701\u609f\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u6211\u660e\u767d\u4e86\uff01\u4ef2\u8001\u524d\u8f88\uff0c\u4f60\u8bf4\u7684\u83ab\u975e\u662f\u2026\u2026\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u4e0d\u9519\u3002\u8fdc\u5728\u5929\u8fb9\uff0c\u8fd1\u5728\u773c\u524d\uff0c\u8fd9\u4f4d\u5176\u5b9e\u662f\u65e9\u5df2\u6709\u4e86\u6b66\u6797\u76df\u4e3b\u4e4b\u5b9e\u7684\u4eba\u5c31\u662f \u6c5f\u6d77\u5929\u5927\u4fa0\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u65e9\u5df2\u662f\u6b66\u6797\u516c\u8ba4\u7684\u7b2c\u4e00\u53f7\u4eba\u7269\uff0c\u4f46\u56e0\u4ed6\u4e0d\u662f\u5e2e\u4f1a\u4e2d\u4eba\uff0c\u6240\u4ee5\u4f17\u4eba\u4e00\u65f6\u6ca1\u60f3\u5230\u53ef\u4ee5 \u8bf7\u4ed6\u6765\u505a\u201c\u76df\u4e3b\u201d\u3002\u4ef2\u957f\u7edf\u4e00\u8bf4\u51fa\u4e86\u4ed6\u7684\u540d\u5b57\uff0c\u7ea2\u7f28\u4f1a\u3001\u516d\u5408\u5e2e\u7b49\u4e00\u4f17\u5e2e\u4f1a\u5934\u76ee\u90fd\u662f\u5fc3\u60a6\u8bda \u670d\u3001\u5f02\u53e3\u540c\u58f0\u5730\u8bf4\u9053\uff1a\u201c\u5c31\u53ea\u6015\u6c5f\u5927\u4fa0\u4e0d\u80af\u5c48\u5c31\u3002\u201d\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u672a\u542c\u6e05\u695a\u5417\uff1f\u6211 \u662f\u8bf7\u4ed6\u505a\u6b66\u6797\u76df\u4e3b\u554a\uff01\u82e5\u662f\u5acc\u5c48\u5c31\u7684\u8bdd\uff0c\u6211\u8001\u53eb\u5316\u53ef\u5c31\u8981\u751f\u6c14\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u53ef\u4ee5\uff0c\u8fd9\u4e0d\u662f\u53d8\u6210\u4e86\u79c1\u76f8\u6388\u53d7\u4e86\u5417\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u4ec0\u4e48\u79c1\u76f8\u63a5\u53d7\uff0c\u8fd9\u6b63\u662f\u5b9e\u81f3\u540d\u5f52\uff01\u4e0d\u9519\uff0c\u4eca\u65e5\u5728\u8fd9\u91cc\u7684\u670b\u53cb\u5c1a\u672a\u80fd\u5305\u62ec\u6b66 \u6797\u5404\u65b9\u9762\u7684\u4eba\u7269\uff0c\u4f46\u4eca\u65e5\u4e4b\u4f1a\uff0c\u7eb5\u7136\u4e0d\u80fd\u7b97\u662f\u6b66\u6797\u5927\u4f1a\uff0c\u4e5f\u53ef\u4ee5\u7b97\u5f97\u662f\u6b66\u6797\u5c0f\u4f1a\u4e86\uff0c\u76ee\u524d\u6b63 \u662f\u591a\u4e8b\u4e4b\u79cb\uff0c\u8981\u5f00\u6210\u6b66\u6797\u5927\u4f1a\u6050\u6015\u4e0d\u5f88\u5bb9\u6613\uff0c\u4f46\u6297\u6e05\u5175\u3001\u63f4\u4e49\u519b\u5374\u662f\u5f53\u52a1\u4e4b\u6025\uff01\u4fd7\u8bed\u8bf4\u86c7\u65e0 \u5934\u4e0d\u884c\uff0c\u6e05\u5175\u5728\u5404\u5730\u5927\u4e3e\u8fdb\u653b\u4e49\u519b\uff0c\u54b1\u4eec\u4e5f\u5fc5\u987b\u540c\u5fc3\u622e\u529b\u624d\u6210\uff01\u65e2\u7136\u7b49\u4e0d\u53ca\u5f00\u6b66\u6797\u5927\u4f1a\u63a8\u9009 \u76df\u4e3b\uff0c\u90a3\u5c31\u4e0d\u59a8\u7531\u54b1\u4eec\u8fd9\u4e2a\u6b66\u6797\u5c0f\u4f1a\u63a8\u4e3e\u95f4\u4f5c\u76df\u4e09\u7684\u5019\u9009\u4eba\uff0c\u7136\u540e\u518d\u5f81\u6c42\u5404\u5927\u95e8\u6d3e\u3001\u5404\u8def\u597d \u6c49\u7684\u540c\u610f\uff0c\u6599\u60f3\u63d0\u51fa\u4e86\u4f60\u6c5f\u5927\u4fa0\u7684\u540d\u5b57\uff0c\u7edd\u4e0d\u4f1a\u6709\u4e00\u4e2a\u4eba\u4e0d\u70b9\u5934\u7684\uff0c\u6c5f\u5927\u4fa0\uff0c\u4f60\u8fd9\u4e00\u751f\u4e0d\u662f \u4ee5\u9a71\u9664\u5308\u864f\u6062\u590d\u4e2d\u534e\u4e3a\u804c\u5fd7\u7684\u5417\uff1f\u4f60\u53c8\u6b63\u5728\u5e74\u5bcc\u529b\u5f3a\uff0c\u96be\u9053\u8fd8\u4f1a\u754f\u60e7\u8270\u96be\uff0c\u6311\u8fd9\u91cd\u62c5\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u8d23\u4ee5\u5927\u4e49\uff0c\u6c5f\u6d77\u5929\u65e0\u53ef\u63a8\u8f9e\uff0c\u53ea\u597d\u5e94\u627f\uff0c\u8bf4\u9053\uff1a\u201c\u627f\u8499\u5404\u4f4d\u770b\u5f97\u8d77\u6211\uff0c\u90a3\u5c31\u6682\u4e14 \u7531\u6211\u5145\u4e2a\u201c\u5934\u4eba\u201d\u8054\u7edc\u5404\u65b9\uff0c\u5171\u5546\u6297\u6e05\u7684\u5927\u4e1a\u5427\u3002\u81f3\u6b66\u6797\u76df\u4e3b\u7684\u5c0a\u79f0\uff0c\u6b66\u6797\u5927\u4f1a\u5728\u76ee\u524d\u65e2\u662f \u4e0d\u53ef\u80fd\u53ec\u5f00\uff0c\u90a3\u5c31\u7406\u8be5\u7559\u5f85\u8d24\u8005\uff0c\u8bf7\u6055\u6211\u4e0d\u4fbf\u63a5\u53d7\u4e86\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53ea\u8981\u4f60\u7b54\u5e94\u5c31\u884c\uff0c\u4f60\u613f\u610f\u53eb\u505a\u76df\u4e3b\u4e5f\u597d\uff0c\u603b\u4e4b\u4f60\u662f\u54b1\u4eec\u7684\u5934\u513f\uff0c\u5c06 \u6765\u4e5f\u7edd\u4e0d\u4f1a\u6709\u4eba\u548c\u4f60\u4e89\u7684\u3002\u201d \u3000\u3000\u5927\u4e8b\u8bae\u5b9a\uff0c\u867d\u7136\u6c5f\u6d77\u5929\u8c26\u8f9e\u201c\u6b66\u676f\u76df\u4e3b\u201d\u7684\u5c0a\u79f0\uff0c\u4f17\u4eba\u5df2\u65e0\u4e0d\u5c06\u4ed6\u5f53\u4f5c\u76df\u4e3b\u770b\u5f85\u4e86\uff0c\u5f53 \u4e0b\u7b75\u5e2d\u6446\u5f00\uff0c\u4eba\u4eba\u5f00\u6000\u7545\u996e\uff0c\u8f6e\u6d41\u5411\u6c5f\u6d77\u5929\u656c\u9152\u9053\u8d3a\u3002 \u3000\u3000\u4f17\u4eba\u559d\u5f97\u9152\u9163\u8033\u70ed\uff0c\u81ea\u7136\u5c11\u4e0d\u514d\u8981\u5174\u9ad8\u5f69\u70c8\u5730\u8c08\u8bba\u6b66\u529f\uff0c\u5927\u5bb6\u5bf9\u6c5f\u6d77\u5929\u7684\u672c\u4e8b\uff0c\u81ea\u7136\u4e5f \u5c11\u4e0d\u514d\u8981\u5938\u5927\u5176\u8f9e\uff0c\u8bf4\u5f97\u795e\u5947\u4e4b\u6781\u3002 \u3000\u3000\u5728\u6bbf\u89d2\u7684\u4e00\u5f20\u53f0\u4e0a\uff0c\u540c\u5e2d\u7684\u516b\u4e2a\u4eba\u6709\u4e03\u4e2a\u662f\u5c0f\u5e2e\u4f1a\u7684\u5c0f\u5934\u76ee\u3002\u53e6\u5916\u4e00\u4e2a\u9752\u888d\u6c49\u5b50\u5374\u4e0d\u77e5 \u662f\u4ec0\u4e48\u6765\u5386\uff0c\u4f46\u56e0\u4e3a\u5ea7\u4f4d\u5b89\u6392\u5728\u8fd9\u5f20\u684c\u5b50\u90fd\u662f\u6b21\u4e00\u7b49\u7684\u4eba\u7269\uff0c\u5927\u5bb6\u4e5f\u5c31\u4ee5\u4e3a\u4ed6\u662f\u4e2a\u4e0d\u8db3\u8f7b\u91cd \u7684\u67d0\u4e00\u4e2a\u5e2e\u4f1a\u4e2d\u4eba\uff0c\u800c\u4e14\u90a3\u4e03\u4e2a\u5c0f\u5934\u76ee\u4e5f\u662f\u5404\u4e0d\u76f8\u8bc6\u7684\uff0c\u662f\u4ee5\u5927\u5bb6\u4e5f\u5c31\u6ca1\u6709\u600e\u6837\u76d8\u95ee\u4ed6\u3002 \u3000\u3000\u5e2d\u4e2d\u6709\u4e2a\u957f\u9cb8\u5e2e\u7684\u5934\u76ee\uff0c\u66fe\u7ecf\u8ddf\u968f\u5e2e\u4e3b\uff0c\u5728\u4e09\u5e74\u524d\u5230\u8fc7\u6c5f\u6d77\u5929\u5bb6\u4e2d\u4f5c\u5ba2\uff0c\u559d\u8fc7\u6c5f\u6d77\u5929\u5ac1 \u5973\u7684\u559c\u9152\u7684\u3002\u8fd9\u4e2a\u4eba\u8981\u70ab\u8000\u81ea\u5df1\u7684\u89c1\u95fb\u5e7f\u535a\uff0c\u4e0e\u4f17\u4e0d\u540c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6c5f\u5927\u4fa0\u7684\u6b66\u529f\u73b0\u5728 \u5f53\u7136\u662f\u5929\u4e0b\u7b2c\u4e00\uff0c\u4f46\u5c06\u6765\u5c31\u6050\u6015\u4e0d\u4f1a\u662f\u4ed6\u4e86\u3002\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u5c0f\u5934\u76ee\u662f\u6c5f\u6d77\u5929\u7684\u5d07\u62dc\u8005\uff0c\u602b\u7136\u95ee\u9053\uff1a\u201c\u90a3\u53c8\u662f\u8c01\uff1f\u201d\u957f\u9cb8\u5e2e\u7684\u5c0f\u5934\u76ee\u9053\uff1a\u201c\u662f \u4ed6\u7684\u5e08\u5f1f\u91d1\u9010\u6d41\u3002\u90a3\u6b21\u6211\u4eb2\u773c\u89c1\u5230\u4ed6\u4e09\u62db\u4e24\u5f0f\u6253\u8d25\u4e86\u6587\u9053\u5e84\uff0c\u4eb2\u8033\u542c\u5230\u6c5f\u5927\u4fa0\u8bf4\u4ed6\u5e08\u5f1f\u7684\u6b66 \u5b66\u9020\u8be3\u5728\u4ed6\u4e4b\u4e0a\u7684\uff0c\u53ea\u662f\u76ee\u524d\u529f\u529b\u5c1a\u7a0d\u6709\u4e0d\u5982\u800c\u5df2\u3002\u201d \u3000\u3000\u53d1\u95ee\u7684\u90a3\u5c0f\u5934\u76ee\u8fd9\u624d\u5f00\u6000\u7b11\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff0c\u539f\u6765\u4f60\u8bf4\u7684\u662f\u91d1\u5c11\u4fa0\u3002\u5e08\u5144\u4e5f\u7f62\uff0c\u5e08\u5f1f\u4e5f \u7f62\uff0c\u603b\u4e4b\u662f\u4e00\u5bb6\u4eba\u3002\u6211\u5012\u4e0d\u5fc5\u4e3a\u6c5f\u5927\u4fa0\u548c\u4f60\u8fa9\u4e86\u3002\u201d \u3000\u3000\u5e2d\u4e0a\u6709\u4e24\u4e2a\u4eba\u8c08\u8d77\u4e86\u91d1\u9010\u6d41\uff0c\u5927\u5bb6\u7684\u8bdd\u9898\u4e5f\u5c31\u4e0d\u7ea6\u800c\u540c\u5730\u8f6c\u79fb\u5230\u91d1\u9010\u6d41\u8eab\u4e0a\u3002 \u3000\u3000\u7b2c\u4e09\u4e2a\u4eba\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u66f4\u7cbe\u5f69\u7684\u5462\uff0c\u91d1\u5c11\u4fa0\u4eca\u5929\u4e00\u5929\u4e4b\u5185\uff0c\u8fde\u8d25\u4e09\u5927\u9ad8\u624b\uff0c\u4f60\u4eec\u53ef\u77e5\u9053 \u4e48\u3002\u201d \u3000\u3000\u957f\u9cb8\u5e2e\u90a3\u5c0f\u5934\u76ee\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u91d1\u5c11\u4fa0\u5728\u5927\u7834\u5929\u9b54\u6559\u603b\u8235\u4e4b\u65f6\uff0c\u548c\u9633\u6d69\u6253\u4e86\u4e00\u573a\uff0c\u540e\u6765 \u542c\u8bf4\u4ed6\u5728\u540e\u5c71\uff0c\u4e5f\u6709\u4e00\u573a\u5267\u6218\uff0c\u4f46\u5374\u4e0d\u77e5\u90a3\u4e24\u4e2a\u9ad8\u624b\u662f\u8c01\u4e86\u3002\u201d \u3000\u3000\u201c\u5176\u4e2d\u4e00\u4e2a\u5c31\u662f\u6587\u9053\u5e84\u3002\u867d\u7136\u540c\u662f\u4e00\u4e2a\u6587\u9053\u5e84\uff0c\u4f46\u4eca\u65e5\u7684\u6587\u9053\u5e84\u7684\u672c\u9886\uff0c\u5df2\u662f\u8fdc\u975e\u4e09\u5e74 \u4e4b\u524d\u7684\u6587\u9053\u5e84\u6240\u80fd\u76f8\u6bd4\u3002\u542c\u8bf4\u4ed6\u7684\u4e09\u8c61\u795e\u529f\u5df2\u7ecf\u7ec3\u6210\uff0c\u5f53\u771f\u662f\u6709\u964d\u9f99\u4f0f\u864e\u4e4b\u80fd\uff0c\u5f00\u7891\u88c2\u77f3\u4e4b \u529b\u3002\u4f46\u7ed3\u679c\uff0c\u8fd8\u662f\u8d25\u5728\u91d1\u5c11\u4fa0\u7684\u624b\u4e0b\u3002\u201d \u3000\u3000\u201c\u554a\uff0c\u771f\u662f\u4e86\u4e0d\u8d77\uff01\u53ef\u60dc\u6211\u6ca1\u6709\u773c\u798f\u89c1\u5230\u3002\u90a3\u4e48\u8fd8\u6709\u4e00\u4e2a\u9ad8\u624b\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u201c\u54c8\uff0c\u8fd9\u4e2a\u9ad8\u624b\u561b\u53ef\u6bd4\u9633\u6d69\u548c\u6587\u9053\u5e84\u53c8\u66f4\u5389\u5bb3\u4e86\u3002\u542c\u8bf4\u4ed6\u662f\u866c\u9aef\u5ba2\u7684\u7b2c\u4e8c\u5341\u516d\u4ee3\u4f20\u4eba\uff0c \u6289\u6851\u5c9b\u8fd9\u4e00\u6d3e\u7684\u5b97\u4e3b\uff01\u201d \u3000\u3000\u201c\u866c\u9aef\u5ba2\u662f\u8c01\uff1f\u6276\u6851\u5c9b\u8fd9\u4e2a\u540d\u5b57\u6211\u4e5f\u6ca1\u6709\u542c\u8fc7\uff0c\u662f\u5728\u54ea\u91cc\u7684\uff1f\u201d\u5e2d\u4e0a\u8bf8\u4eba\u542c\u5f97\u6d25\u6d25\u6709 \u5473\uff0c\u542c\u5230\u8fd9\u91cc\uff0c\u597d\u51e0\u4e2a\u4eba\u540c\u65f6\u53d1\u95ee\u3002 \u3000\u3000\u4e8e\u662f\u90a3\u4eba\u53c8\u53e3\u6cab\u6a2a\u98de\u7684\u201c\u7ec6\u8bf4\u201d\u866c\u9aef\u5ba2\u4e0e\u6276\u6851\u5c9b\u8fd9\u6d3e\u6b66\u529f\u7684\u6e0a\u6e90\u548c\u5389\u5bb3\u4e4b\u5904\uff0c\u6240\u8c13\u201c\u7ec6 \u8bf4\u201d\uff0c\u65e0\u975e\u662f\u8033\u542c\u4e4b\u8a00\u52a0\u4e0a\u81ea\u5df1\u7684\u63e3\u6d4b\u4e4b\u8f9e\u800c\u5df2\u3002\u542c\u7684\u4eba\u4e0d\u77e5\u771f\u5047\uff0c\u4f46\u8868\u73b0\u51fa\u6765\u7684\u795e\u6c14\uff0c\u5374 \u597d\u4f3c\u90fd\u76f8\u4fe1\u4e86\u4ed6\u7684\u8bf4\u8bdd\uff0c\u4ed6\u8bf4\u4e00\u53e5\uff0c\u5927\u5bb6\u5c31\u6447\u5934\u6643\u8111\u7684\u8d5e\u53f9\u4e00\u58f0\u3002\u751a\u81f3\u8fd8\u6709\u90bb\u5e2d\u7684\u4eba\u653e\u4e0b\u676f \u7b77\uff0c\u8fc7\u6765\u505a\u4ed6\u7684\u542c\u4f17\u3002\u90a3\u4eba\u89c1\u8fd9\u4e48\u591a\u542c\u4f17\u7ed9\u4ed6\u6367\u573a\uff0c\u8d8a\u8bf4\u8d8a\u662f\u9ad8\u5174\uff0c\u6307\u624b\u5212\u811a\uff0c\u52a0\u679d\u6dfb\u53f6\uff0c \u8bb2\u5f97\u5386\u5386\u5982\u7ed8\uff0c\u5c31\u597d\u50cf\u4ed6\u4eb2\u773c\u89c1\u5230\u91d1\u9010\u6d41\u6253\u8d25\u725f\u5b97\u6d9b\u4e00\u822c\u3002 \u3000\u3000\u5176\u5b9e\u91d1\u9010\u6d41\u548c\u6587\u9053\u5e84\u3001\u725f\u5b97\u6d9b\u8fd9\u4e24\u573a\u6076\u6597\u90fd\u662f\u5904\u5728\u4e0b\u98ce\uff0c\u5c24\u5176\u548c\u725f\u5b97\u6d9b\u4ea4\u624b\u90a3\u573a\uff0c\u66f4\u662f \u9677\u4e8e\u82e6\u6218\u7684\u5883\u5730\uff0c\u82e5\u4e0d\u662f\u5f97\u5e08\u5144\u66ff\u4ed6\u89e3\u56f0\uff0c\u4ed6\u53ea\u6015\u65e9\u5df2\u53d7\u4e86\u91cd\u4f24\uff0c\u6b64\u9645\u8fde\u5e86\u529f\u9152\u4e5f\u559d\u4e0d\u6210 \u4e86\uff01 \u3000\u3000\u542c\u4f17\u4e4b\u4e2d\u53ea\u6709\u4e00\u4e2a\u4eba\u59cb\u7ec8\u4e0d\u53d1\u4e00\u8a00\uff0c\u4e5f\u6ca1\u6709\u8ddf\u7740\u4f17\u4eba\u540c\u58f0\u8d5e\u53f9\uff0c\u8fd9\u4e2a\u4eba\u5c31\u662f\u90a3\u4e2a\u8c01\u4e5f\u4e0d \u8ba4\u8bc6\u7684\u9752\u888d\u6c49\u5b50\u3002 \u3000\u3000\u90a3\u4eba\u8bb2\u5b8c\u4e86\u4e4b\u540e\uff0c\u8d5e\u53f9\u4e4b\u58f0\u7eb7\u8d77\uff0c\u6709\u7684\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u53ea\u6015\u91d1\u5c11\u4fa0\u7684\u6b66\u529f\u5982\u4eca\u5df2\u662f \u5929\u4e0b\u7b2c\u4e00\u4e86\u3002\u201d\u6709\u7684\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u73b0\u5728\u8fd8\u662f\u4ed6\u7684\u5e08\u5144\u6c5f\u5927\u4fa0\u5f3a\u4e9b\uff0c\u4e0d\u8fc7\uff0c\u518d\u8fc7\u51e0\u5e74\uff0c\u90a3\u5c31\u4e00 \u5b9a\u662f\u4ed6\u7684\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\u4e86\uff01\u201d \u3000\u3000\u5728\u4f17\u4eba\u5938\u8d5e\u91d1\u9010\u6d41\u7684\u8d5e\u626c\u58f0\u4e2d\uff0c\u90a3\u4e2a\u9752\u888d\u602a\u5ba2\u7a81\u7136\u201c\u563f\u563f\u563f\u201d\u7684\u51b7\u7b11\u4e09\u58f0\uff0c\u7b11\u58f0\u5341\u5206\u523a \u8033\uff0c\u5b9b\u5982\u91d1\u5c5e\u4ea4\u51fb\uff01 \u3000\u3000\u8fd9\u4e00\u7b11\u767b\u65f6\u4ee4\u5f97\u4f17\u4eba\u5c3d\u90fd\u60ca\u6115\uff0c\u957f\u9cb8\u5e2e\u90a3\u4e2a\u5934\u76ee\u6012\u9053\uff1a\u201c\u9601\u4e0b\u56e0\u4f55\u51b7\u7b11\uff1f\u201d \u3000\u3000\u201c\u6ca1\u6709\u4ec0\u4e48\uff0c\u6211\u53ea\u662f\u7b11\u4f60\u4eec\u4e43\u662f\u4e95\u5e95\u4e4b\u86d9\u800c\u5df2\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u4f0f\u864e\u85cf\u9f99\u4eba\u672a\u8bc6\uff0c\u5929\u5916\u6709\u5929\u541b\u53ef\u77e5\uff1f \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e03\u56de\u3000\u7384\u529f\u7edd\u6280\u60ca\u8c6a\u6770\u3000\u9ad8\u58eb\u795e\u62f3\u663e\u5f02\u80fd \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u56db\u5341\u4e03\u56de\u3000\u7384\u529f\u7edd\u6280\u60ca\u8c6a\u6770\u3000\u9ad8\u58eb\u795e\u62f3\u663e\u5f02\u80fd \u3000\u3000\u8fd9\u4eba\u6de1\u6de1\u9053\u6765\uff0c\u58f0\u97f3\u5e76\u4e0d\u54cd\u4eae\u3002\u4f46\u5374\u5b9b\u5982\u91d1\u5c5e\u6572\u51fb\uff0c\u9e64\u7f20\u94ff\u9535\uff0c\u542c\u8fdb\u8033\u6735\uff0c\u5c31\u597d\u50cf\u7ed9\u5229 \u9488\u624e\u4e86\u4e00\u4e0b\u4f3c\u7684\u3002\u5927\u5802\u4e0a\u7b75\u5f00\u767e\u5e2d\uff0c\u5c06\u8fd1\u5343\u4eba\uff0c\u7adf\u662f\u6bcf\u4e2a\u4eba\u90fd\u542c\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u8fd9\u51e0\u53e5\u8bdd\u672c\u6765\u5341\u5206\u201c\u523a\u8033\u201d\uff0c\u52a0\u4e0a\u4ed6\u8fd9\u6837\u602a\u5f02\u7684\u58f0\u97f3\uff0c\u66f4\u662f\u540d\u526f\u5176\u5b9e\u7684\u201c\u523a\u8033\u201d\u4e86\uff0c\u4f17 \u4eba\u7684\u76ee\u5149\uff0c\u4e0d\u7981\u90fd\u96c6\u4e2d\u5728\u4ed6\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u957f\u9cb8\u5e2e\u90a3\u4e2a\u5c0f\u5934\u76ee\u6c14\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u970d\u5730\u8df3\u8d77\u8eab\u6765\uff0c\u7d27\u63e1\u62f3\u5934\uff0c\u5c31\u60f3\u52a8\u6b66\u3002\u5e78\u4e8f\u65c1\u8fb9\u6709\u4e2a \u6b66\u5b66\u7684\u884c\u5bb6\uff0c\u5c06\u4ed6\u4e00\u628a\u62c9\u4f4f\uff0c\u8fd9\u4e2a\u5c0f\u5934\u76ee\u970d\u7136\u4e00\u7701\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u597d\u50cf\u6709\u70b9\u90aa\u95e8\uff0c\u53ea\u6015 \u6211\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\u3002\u4ed6\u5f97\u7f6a\u7684\u53c8\u4e0d\u53ea\u6211\u4e00\u4e2a\uff0c\u81ea\u4f1a\u6709\u4eba\u51fa\u5934\u201d\u3002\u4f46\u8fd9\u53e3\u6c14\u4ecd\u662f\u54bd\u4e0d\u4e0b\u53bb\uff0c\u5fcd\u4e0d \u4f4f\u95ee\u9053\uff1a\u201c\u4f55\u4ee5\u89c1\u5f97\u6211\u4eec\u662f\u4e95\u5e95\u4e4b\u5916\uff0c\u5012\u8981\u5411\u9601\u4e0b\u8bf7\u6559\u3002\u201d \u3000\u3000\u90a3\u4eba\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u4e4b\u5927\uff0c\u4f60\u4eec\u66fe\u7ecf\u89c1\u8fc7\u591a\u5c11\u4e2a\u9ad8\u4eba\uff0c\u52a8\u4e0d\u52a8\u5c31\u662f\u5929\u4e0b\u7b2c\u4e00\uff0c\u8fd9\u4e0d\u662f \u592a\u4ee4\u4eba\u597d\u7b11\u4e48\u3002\u201d \u3000\u3000\u4e10\u5e2e\u56db\u5927\u9999\u4e3b\u4e4b\u4e00\u7684\u79e6\u51b2\u662f\u6709\u540d\u7684\u201c\u9739\u96f3\u706b\u201d\u813e\u6c14\uff0c\u542c\u4e86\u8fd9\u8bdd\u4e0d\u7981\u6012\u706b\u4e0a\u51b2\uff0c\u8bf4\u9053\uff1a \u201c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u6562\u60c5\u4f60\u662f\u81ea\u8ba4\u9ad8\u4eba\uff0c\u628a\u6c5f\u5927\u4fa0\u548c\u91d1\u5927\u4fa0\u90fd\u4e0d\u653e\u5728\u773c\u5185\u4e86\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u540d\u9707\u6b66\u6797,\u81ea\u4ed6\u6210\u540d\u4e4b\u540e\uff0c\u4e8c\u5341\u5e74\u6765\uff0c\u4ece\u6ca1\u6709\u4eba\u6562\u5bf9\u4ed6\u8bf4\u8fc7\u4e00\u53e5\u65e0\u793c\u7684\u8bf4\u8bdd\uff0c\u4e0d \u6599\u8fd9\u4e2a\u4eba\u7adf\u662f\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u6211\u4e0d\u8fc7\u662f\u4e2a\u5c71\u91ce\u5339\u592b\uff0c\u600e\u5f53\u5f97\u9ad8\u4eba\u4e8c\u5b57\uff1f\u4e0d\u8fc7\u4f60\u8bf4\u7684\u90a3\u4e24 \u4f4d\u4ec0\u4e48\u6c5f\u5927\u4fa0\u548c\u91d1\u5c11\u4fa0\u561b\uff0c\u563f\uff0c\u563f\uff0c\u4f9d\u6211\u770b\u6765\uff0c\u672c\u9886\u867d\u7136\u4e0d\u9519\uff0c\u4f46\u6050\u6015\u4e5f\u672a\u89c1\u5f97\u5c31\u662f\u2014\u2014\u5929 \u4e0b\u7b2c\u4e00\u4e86\u5427\uff01\u201d \u3000\u3000\u79e6\u51b2\u6012\u9053\uff1a\u201c\u597d\uff0c\u6c5f\u5927\u4fa0\u4e0d\u7b97\u5929\u4e0b\u7b2c\u4e00\uff0c\u4f60\u662f\u5929\u4e0b\u7b2c\u4e00\uff0c\u6211\u79e6\u67d0\u4eba\u53ea\u4f1a\u51e0\u624b\u4e09\u811a\u63cf\u7684\u529f \u592b\uff0c\u5012\u8981\u5411\u9601\u4e0b\u9886\u6559\u9886\u6559\uff01\u201d \u3000\u3000\u90a3\u4eba\u5634\u89d2\u6302\u7740\u4e00\u4e1d\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u6211\u6ca1\u6709\u8bf4\u6211\u81ea\u5df1\u7684\u529f\u592b\u662f\u5929\u4e0b\u7b2c\u4e00\uff1b\u7b2c\u4e8c\uff0c\u6211 \u4e5f\u6ca1\u6709\u8bf4\u4f60\u8001\u54e5\u662f\u4e09\u811a\u732b\u529f\u592b\uff0c\u8fd9\u90fd\u662f\u4f60\u81ea\u5df1\u8bf4\u7684\uff0c\u6211\u53ea\u662f\u8bf4\u8fc7\u6c5f\u6d77\u5929\u548c\u91d1\u9010\u6d41\u4e0d\u89c1\u5f97\u662f\u5927 \u4e0b\u7b2c\u4e00\uff0c\u4f60\u4eec\u82e5\u662f\u4e0d\u76f8\u4fe1\u7684\u8bdd\uff0c\u6211\u613f\u610f\u5411\u4ed6\u4eec\u4e8c\u4f4d\u9886\u6559\u9886\u6559\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u56db\u5ea7\u7686\u60ca\uff01\u8fd1\u5343\u4e4b\u4f17\uff0c\u4eba\u4eba\u90fd\u609a\u7136\u52a8\u4f5c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u7aef\u7684\u662f\u597d\u5927\u80c6\uff0c \u7adf\u6562\u5411\u6c5f\u5927\u4fa0\u5e08\u5144\u5f1f\u516c\u7136\u6311\u6218\uff01\u201d \u3000\u3000\u79e6\u51b2\u6012\u6c14\u51b2\u51b2\u5730\u53eb\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\uff0c\u4f60\u4e00\u5b9a\u8981\u6559\u8bad\u6559\u8bad\u8fd9\u72c2\u5984\u4e4b\u5f92\uff0c\u4f60\u4e0d\u6559\u8bad\u4ed6\uff0c\u6211\u53ef\u5fcd \u4e0d\u4f4f\u4e86\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4ed4\u7ec6\u4e00\u770b\uff0c\u53ea\u89c1\u8fd9\u4eba\u51b7\u51b0\u51b0\u7684\uff0c\u9762\u90e8\u6beb\u65e0\u8868\u60c5\uff0c\u5fc3\u91cc\u597d\u751f\u7eb3\u7f55\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6b64 \u4eba\u6709\u5fc3\u6765\u8f83\u91cf\u6211\uff0c\u5982\u53c8\u5904\u5904\u6709\u5047\uff0c\u597d\u50cf\u662f\u5bb3\u6015\u6211\u8bc6\u7834\u4ed6\u7684\u672c\u6765\u9762\u76ee\uff0c\u4ed6\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u539f\u6765\u6c5f\u6d77\u5929\u4e00\u542c\u8fd9\u4eba\u8bf4\u8bdd\uff0c\u5c31\u77e5\u4ed6\u662f\u7528\u4e0a\u4e58\u5185\u529f\uff0c\u628a\u58f0\u97f3\u4ece\u5589\u5499\u4e2d\u903c\u51fa\u6765\u7684\uff0c\u5e76\u4e0d\u662f\u4ed6 \u539f\u6765\u7684\u58f0\u97f3\uff0c\u9762\u4e0a\u6beb\u65e0\u8840\u8272\uff0c\u663e\u7136\u4e5f\u662f\u6577\u4e86\u4eba\u9020\u9762\u5177\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u60ca\u7591\u4e0d\u5b9a\uff0c\u8d70\u8fc7\u53bb\u5411\u90a3\u4eba\u65bd\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u67d0\u8089\u773c\u4e0d\u8bc6\u771f\u4eba\uff0c\u6020\u6162\u4e86\u670b\u4eba\uff0c \u5b9e\u662f\u60ed\u6127\uff0c\u8bf7\u95ee\u9601\u4e0b\u9ad8\u59d3\u5927\u540d\uff1f\u201d \u3000\u3000\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u4f55\u5fc5\u7740\u5fd9\uff0c\u5f85\u6211\u5411\u6c5f\u5927\u4fa0\u8bf7\u6559\u8fc7\u4e86\uff0c\u518d\u901a\u540d\u9053\u59d3\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4ed6\u8981\u6bd4\u8bd5\u8fc7\u540e\u624d\u80af\u901a\u540d\u5462\uff1f\u96be\u9053\u4ed6\u662f\u6015\u6211\u77e5\u9053\u4e86\u4ed6\u7684\u6765\u5389\uff0c \u5c31\u4e0d\u80af\u548c\u4ed6\u6bd4\u8bd5\u4e48\uff1f\u8981\u77e5\u6c5f\u6e56\u4e0a\u6709\u987e\u5fcc\uff0c\u5982\u679c\u8bf4\u51fa\u4e86\u540d\u5b57\uff0c\u5f7c\u6b64\u662f\u6709\u6e0a\u6e90\u7684\u8bdd\uff0c\u90a3\u4e48\u52a8\u8d77\u624b \u6765\uff0c\u5c31\u4e0d\u80fd\u4e0d\u987e\u4f4f\u60c5\u9762\u4e86\u3002\u6b64\u4eba\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5927\u5bb6\u66f4\u8ba4\u5b9a\u4e86\u4ed6\u662f\u6709\u5fc3\u6765\u632b\u6298\u6c5f\u6d77\u5929\u7684\u4e86\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5374\u4e0d\u52a8\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u4eba\u4e0d\u9732\u76f8\uff0c\u9732\u76f8\u4e0d\u771f\u4eba\uff0c\u9601\u4e0b\u65e2\u662f\u4e0d\u613f\u8d50\u793a\u5927\u540d\uff0c\u6c5f\u67d0\u4e5f \u4e0d\u6562\u52c9\u5f3a\u3002\u4e0d\u8fc7\uff0c\u521a\u624d\u4f17\u4f4d\u670b\u53cb\u7ed9\u6211\u9762\u4e0a\u5e16\u91d1\uff0c\u6240\u8bf4\u7684\u90a3\u4e9b\u6367\u573a\u7684\u8bf4\u8bdd\uff0c\u9601\u4e0b\u53ef\u5343\u4e07\u4e0d\u8981\u5f53 \u771f\u3002\u6c5f\u67d0\u8fd9\u70b9\u5fae\u672b\u4e4b\u6280\uff0c\u6b63\u5982\u9601\u4e0b\u6240\u8bf4\uff0c\u5c82\u80fd\u5f53\u5f97\u5929\u4e0b\u7b2c\u4e00\u7684\u79f0\u53f7\uff1f\u8bf7\u9601\u4e0b\u5750\uff0c\u5bb9\u6c5f\u67d0\u8ba8 \u6559\u3002\u81f3\u4e8e\u6bd4\u8bd5\u4e48\uff0c\u6c5f\u67d0\u53ef\u5c31\u4e0d\u6562\u732e\u4e11\u4e86\uff01\u201d \u3000\u3000\u90a3\u4eba\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8bf4\u53e5\u516c\u9053\u8bdd\uff0c\u4f60\u7eb5\u7136\u7b97\u4e0d\u5f97\u5929\u4e0b\u7b2c\u4e00\uff0c\u4e5f\u7b97\u5f97\u662f\u4f4d\u9ad8\u624b\u3002\u5b9e\u4e0d \u76f8\u7792\uff0c\u6211\u662f\u6709\u5fc3\u6765\u5f00\u5f00\u773c\u754c\uff0c\u770b\u770b\u4f60\u7684\u672c\u9886\u7684\u3002\u4f60\u4e0d\u80af\u8d50\u6559\uff0c\u53ef\u771f\u662f\u4ee4\u6211\u592a\u5931\u671b\u4e86\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8d8a\u8c26\u865a\uff0c\u90a3\u4eba\u8d8a\u72c2\u5984\uff0c\u800c\u4f17\u4eba\u542c\u4e86\uff0c\u4e5f\u5c31\u8d8a\u53d1\u751f\u6c14\u3002\u79e6\u51b2\u6012\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u5c82\u80fd\u548c \u4f60\u4e00\u822c\u89c1\u8bc6\uff1f\u4f60\u4e00\u5b9a\u8981\u6bd4\u8bd5\u7684\u8bdd\uff0c\u6211\u548c\u4f60\u6bd4\u8bd5\u3002\u4f60\u6253\u8d62\u4e86\u6211\uff0c\u518d\u5411\u6c5f\u5927\u4fa0\u6311\u6218\u4e5f\u8fd8\u4e0d\u8fdf\uff01 \u3000\u3000\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u79e6\u51b2\uff0c\u4f60\u5c11\u8bf4\u4e24\u53e5\u5427\uff0c\u522b\u8ba9\u4eba\u5bb6\u7b11\u8bdd\uff01\u8fd9\u4f4d\u670b\u53cb\u9ad8\u660e\u5f97\u5f88\uff0c\u6211\u90fd\u4e0d\u6562\u73ed\u95e8 \u5f04\u65a7\uff0c\u4f60\u51ed\u4ec0\u4e48\u5411\u4eba\u5bb6\u9886\u6559\uff1f\u201d\u4ef2\u957f\u7edf\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u60f3\u6c5f\u5927\u4fa0\u81ea\u6709\u5206\u6570\uff0c\u54b1 \u4eec\u4e5f\u5c31\u4e0d\u7528\u591a\u4e8b\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e24\u4f4d\u6b66\u6797\u7684\u8f88\u8bf4\u51fa\u8bdd\u672a\uff0c\u4f17\u4eba\u65b9\u59cb\u77e5\u9053\u6b64\u4eba\u679c\u7136\u662f\u4e2a\u6b66\u529f\u83ab\u6d4b\u9ad8\u6df1\u7684\u4eba\u7269\uff0c\u65e0\u4e0d\u9a87 \u7136\uff01 \u3000\u3000\u516c\u5b59\u5b8f\u8ddf\u7740\u8bf4\u9053\uff1a\u201c\u6b66\u6797\u540c\u9053\uff0c\u5f7c\u6b64\u7422\u78e8\uff0c\u4e92\u76f8\u5370\u8bc1\uff0c\u4ea6\u5c5e\u5bfb\u5e38\u3002\u8fd9\u4f4d\u670b\u53cb\u76db\u610f\u62f3\u62f3\uff0c \u6c5f\u5927\u4fa0\u82e5\u4e0d\u4e0b\u573a\uff0c\u5c82\u4e0d\u8f9c\u8d1f\u4e86\u8fd9\u4f4d\u670b\u53cb\u7684\u4e00\u756a\u5fc3\u610f\uff1f\u201d\u4ef2\u957f\u7edf\u4e5f\u9053\uff1a\u201c\u662f\u5440\uff0c\u6c5f\u5927\u4fa0\u548c\u8fd9\u4f4d \u670b\u53cb\u5370\u8bc1\u4e00\u756a\uff0c\u6211\u4eec\u4e5f\u4e50\u5f97\u5f00\u5f00\u773c\u754c\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5728\u4e24\u4f4d\u8001\u80fd\u8f88\u6002\u607f\u4e4b\u4e0b\uff0c\u6b63\u81ea\u8df7\u8e87\uff0c\u91d1\u9010\u6d41\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u5e08\u5144\u4e0d\u613f\u4e0b\u573a\uff0c\u7531\u6211\u66ff \u4ee3\u5982\u4f55\uff1f\u53cd\u6b63\u8fd9\u4f4d\u670b\u53cb\u4e5f\u66fe\u8bf4\u8fc7\u8981\u6307\u6559\u6211\u7684\u3002\u201d \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u4e5f\u770b\u51fa\u4e86\u90a3\u4eba\u662f\u906e\u63a9\u4e86\u672c\u6765\u7684\u9762\u76ee\uff0c\u800c\u4e14\u662f\u6539\u53d8\u4e86\u539f\u6765\u7684\u53e3\u97f3\u7684\uff0c\u662f\u4ee5\u4ed6\u4e5f \u50cf\u5e08\u5144\u4e00\u6837\u8d77\u4e86\u7591\u5fc3\uff0c\u4e0d\u8fc7\u4ed6\u5374\u7591\u5fc3\u8fd9\u4eba\u662f\u6276\u6851\u5c9b\u7684\u4eba\u7269\uff0c\u751a\u6216\u53ef\u80fd\u5c31\u662f\u725f\u5b97\u6d9b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u6765\u662f\u5e74\u8f7b\u6c14\u76db\uff0c\u4e8c\u6765\u5fcd\u4e0d\u7740\u597d\u5947\u5fc3\uff0c\u8981\u60f3\u63ed\u5f00\u8fd9\u9752\u888d\u602a\u5ba2\u7684\u8eab\u4efd\u4e4b\u8c1c\uff0c\u662f\u4ee5\u81ea \u544a\u594b\u52c7\uff0c\u66ff\u4ed6\u5e08\u5144\u51fa\u573a\u3002 \u3000\u3000\u9752\u888d\u602a\u5ba2\u6253\u91cf\u4e86\u91d1\u9010\u6d41\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eca\u65e5\u8fde\u6597\u4e09\u5927\u9ad8\u624b\uff0c\u7cbe\u795e\u6050\u6015\u672a\u66fe\u5b8c\u5168\u6062\u590d \u5427\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u54b1\u4eec\u70b9\u5230\u5373\u6b62\uff0c\u80dc\u8d25\u4e0d\u8bba\uff0c\u4f60\u82e5\u80dc\u8fc7\u4e86\u6211\uff0c\u6211\u51b3\u4e0d\u7528\u4efb\u4f55\u85c9\u53e3\u63a9\u9970\u8d25\u7ee9\uff0c \u5411\u4f60\u4f4e\u5934\u8ba4\u8f93\u4fbf\u662f\u3002\u201d \u3000\u3000\u8981\u77e5\u91d1\u9010\u6d41\u5728\u65e5\u95f4\u66fe\u4e0e\u725f\u5b97\u6d9b\u89c1\u8fc7\u9ad8\u4f4e\uff0c\u90a3\u65f6\u4ed6\u521a\u5728\u6fc0\u6218\u4e4b\u540e\uff0c\u5c1a\u81ea\u53ef\u4ee5\u52c9\u5f3a\u6253\u6210\u5e73 \u624b\uff0c\u5982\u4eca\u4ed6\u7684\u6c14\u5200\u5df2\u6062\u590d\u4e86\u516b\u6210\uff0c\u5f53\u7136\u662f\u6709\u6043\u65e0\u6050\u4e86\u3002\u201c\u7eb5\u8bb8\u8fd9\u4eba\u771f\u7684\u662f\u725f\u5b97\u6d9b\uff0c\u627e\u4e0d\u7528\u7384 \u94c1\u5b9d\u5251\uff0c\u6700\u5c11\u4e5f\u53ef\u4ee5\u548c\u4ed6\u6597\u5230\u4e8c\u767e\u62db\u5f00\u5916\uff0c\u672a\u5fc5\u5c31\u4f1a\u8f93\u7ed9\u4e86\u4ed6\u3002\u201d\u5168\u9010\u6d41\u5fc3\u60f3\u3002 \u3000\u3000\u9752\u888d\u602a\u5ba2\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u52c7\u6c14\u53ef\u5609\uff0c\u4f46\u6211\u5374\u4e0d\u80fd\u5360\u4f60\u4fbf\u5b9c\u3002\u8fd9\u6837\u5427\uff0c\u6211\u672c\u6765\u60f3\u770b \u770b\u4f60\u4eec\u4e24\u4eba\u7684\u672c\u9886\uff0c\u4f60\u4eec\u5c31\u4e00\u9f50\u4e0a\u5427\uff0c\u4e5f\u7701\u5f97\u6211\u591a\u8d39\u529f\u592b\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4eba\u4eba\u90fd\u662f\u7ed9\u4ed6\u5413\u4e86\u4e00\u8df3\uff0c\u79e6\u51b2\u5fcd\u4e0d\u4f4f\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u542c\u542c\uff0c\u5927\u4e0b\u7adf\u6709\u8fd9\u6837\u72c2\u5984 \u4e4b\u4eba\uff01\u201d\u9752\u888d\u602a\u5ba2\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u8fd9\u53e5\u8bdd\u4f60\u5f85\u6211\u8f93\u4e86\u518d\u8bf4\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u6b64\u9645\u672a\u5206\u8f93\u8d62\uff0c\u600e\u89c1\u5f97\u6211 \u662f\u72c2\u5984\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e5f\u662f\u53c8\u60ca\u53c8\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5355\u72ec\u4e00\u4e2a\uff0c\u8981\u6597\u6211\u4eec\u4e24\u4eba\uff1f\u201d\u9752\u888d\u602a\u5ba2\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4 \u9053\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u6709\u4ec0\u4e48\u7a00\u5947\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u60f3\u5fc5\u662f\u4e2a\u75af\u5b50\uff01\u201d\u4e0d\u6599\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u6c5f\u6d77\u5929\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u606d \u656c\u4e0d\u5982\u4ece\u547d\u3002\u591a\u8499\u8fd9\u4f4d\u8001\u524d\u8f88\u770b\u5f97\u8d77\u4f60\u6211\uff0c\u54b1\u4eec\u7406\u8be5\u5949\u966a\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5ffd\u7136\u8bf4\u51fa\u8fd9\u4e2a\u8bdd\u6765\uff0c\u4f17\u4eba\u4e0d\u7981\u53c8\u662f\u5927\u4e3a\u60ca\u8be7\u3002\u8981\u77e5\u6c5f\u6d77\u5929\u4e43\u662f\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\u7684\u8eab \u4efd\uff0c\u8bb8\u591a\u5e74\u6765\uff0c\u90fd\u672a\u66fe\u6709\u8fc7\u4e0e\u4eba\u5355\u6253\u72ec\u6597\u7684\u4e8b\u4e86\uff0c\u5982\u4eca\u53cd\u8f6c\u8fc7\u6765\uff0c\u4ed6\u5374\u613f\u610f\u548c\u5e08\u5f1f\u8054\u624b\u6597\u8fd9 \u9752\u888d\u602a\u5ba2\uff0c\u5f53\u7136\u662f\u5927\u5927\u51fa\u4e4e\u4f17\u4eba\u610f\u6599\u4e4b\u5916\uff01 \u3000\u3000\u8fd8\u6709\u4e00\u5c42\uff0c\u6c5f\u6d77\u5929\u4e00\u76f4\u662f\u8c26\u4e0b\u81ea\u6301\uff0c\u4e0d\u613f\u548c\u8fd9\u4eba\u4ea4\u624b\u7684\uff0c\u4e3a\u4ec0\u4e48\u4ed6\u53c8\u7a81\u7136\u6539\u53d8\u4e86\u4e3b\u610f \u5462\uff1f \u3000\u3000\u5e08\u5144\u4f55\u4ee5\u7a81\u7136\u6539\u53d8\u4e3b\u610f\uff0c\u91d1\u9010\u6d41\u4e5f\u662f\u731c\u60f3\u4e0d\u900f\uff0c\u4f46\u4ed6\u77e5\u9053\u5e08\u5144\u7d20\u6765\u7a33\u91cd\uff0c\u5fc3\u60f3\uff1a\u201c\u5e08\u5144 \u65e2\u7136\u4e0d\u987e\u8eab\u4efd\uff0c\u83ab\u975e\u8fd9\u4eba\u771f\u7684\u662f\u6709\u60ca\u4e16\u7edd\u5b66\uff0c\u8fde\u6211\u4e5f\u8fd8\u672a\u66fe\u770b\u900f\u3002\u201d \u3000\u3000\u9752\u888d\u602a\u5ba2\u9053\uff1a\u201c\u5230\u5e95\u662f\u6c5f\u5927\u4fa0\u723d\u5feb\uff0c\u597d\uff0c\u90a3\u54b1\u4eec\u73b0\u5728\u5c31\u5f00\u59cb\u5427\u3002\u201d\u65e9\u5df2\u6709\u4eba\u642c\u5f00\u684c\u501a\uff0c \u817e\u51fa\u4e00\u5757\u7a7a\u5730\u3002\u9752\u888d\u602a\u5ba2\u8d70\u8fdb\u573a\u5fc3\uff0c\u5f53\u4e2d\u4e00\u7ad9\uff0c\u62b1\u62f3\u5fae\u7b11\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6c14\u5f80\u4e0a\u51b2\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u4eba\u4e5f\u672a\u514d\u592a\u81ea\u5927\u4e86\u3002\u201d\u5f53\u4e0b\u4fbf\u8981\u7acb\u5373\u8fc7\u53bb\u548c\u4ed6\u52a8\u624b\u3002\u6c5f\u6d77 \u5929\u5ffd\u5730\u5c06\u4ed6\u4e00\u62c9\uff0c\u4e0e\u4ed6\u5e76\u80a9\u7ad9\u5728\u4e0b\u9996\u3002\u8fd9\u662f\u628a\u5bf9\u65b9\u5f53\u4f5c\u524d\u8f88\uff0c\u4e0d\u6562\u7ad9\u5728\u5e73\u7b49\u5730\u4f4d\u548c\u4ed6\u4ea4\u624b\u7684 \u610f\u601d\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u628a\u5e08\u5f1f\u62c9\u5728\u4e0b\u9996\uff0c\u4e0d\u6562\u4ee5\u5e73\u8f88\u81ea\u5c45\uff0c\u5bf9\u90a3\u4eba\u7684\u5c0a\u5d07\u53ef\u8bf4\u662f\u5df2\u5230\u4e86\u6781\u70b9\u3002\u4f17\u4eba\u4e0d\u7981 \u53c8\u662f\u5927\u4e3a\u60ca\u8bb6\u3002\u8981\u77e5\u6c5f\u6d77\u5929\u7684\u5e74\u7eaa\u867d\u7136\u4e0d\u8fc7\u56db\u5341\u591a\u5c81\uff0c\u4f46\u4ee5\u8f88\u4efd\u800c\u8a00\uff0c\u4e2d\u539f\u5404\u5927\u95e8\u6d3e\uff0c\u4efb\u4f55 \u4e00\u4f4d\u540d\u5bbf\uff0c\u6700\u591a\u4e5f\u53ea\u80fd\u4e0e\u4ed6\u5e73\u8f88\u8bba\u4ea4\u3002\u4f17\u4eba\u90fd\u77e5\u9053\u6c5f\u6d77\u5929\u4e3a\u4eba\u8c26\u865a\uff0c\u4f46\u603b\u89c9\u5f97\u8fd9\u6837\u7684\u8c26\u865a\u4e5f \u672a\u514d\u592a\u8fc7\u4efd\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e0d\u6562\u8fdd\u80cc\u5e08\u5144\uff0c\u5fcd\u4f4f\u6c14\u5728\u4e0b\u9996\u7acb\u8db3\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u597d\u5566\uff0c\u6211\u4eec\u5e08\u5144\u5f1f\u9075\u547d\u5949\u966a\uff0c \u8fd9\u5c31\u8bf7\u8001\u524d\u8f88\u8d50\u62db\u5427\uff01\u201d\u53e3\u4e2d\u8bf4\u7684\u662f\u201c\u8001\u524d\u8f88\u201d\u4e09\u5b57\uff0c\u4f46\u8bed\u6c14\u5df2\u662f\u4e0d\u751a\u606d\u656c\u4e86\u3002 \u3000\u3000\u9752\u888d\u602a\u5ba2\u4fa7\u76ee\u659c\u89c6\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u7384\u94c1\u5b9d\u5251\u5462\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u4eae\u51fa\u6765\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8981\u7a7a\u624b\u548c\u6211\u7684\u7384\u94c1\u5b9d\u5251\u8f83\u91cf\uff1f\u201d \u3000\u3000\u9752\u888d\u602a\u5ba2\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u542c\u8bf4\u7384\u94c1\u5b9d\u5251\u662f\u5929\u4e0b\u5a01\u529b\u6700\u5f3a\u7684\u5175\u5668\uff0c\u6211\u60f3\u89c1\u8bc6\u89c1\u8bc6\uff01\u201d\u91d1\u9010 \u6d41\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u6211\u7684\u5251\u4e0a\u5374\u662f\u4e0d\u957f\u773c\u775b\u7684\uff01\u201d\u9752\u888d\u602a\u5ba2\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u5251\u4e0a\u4e0d \u957f\u773c\u775b\uff0c\u6211\u7684\u8138\u4e0a\u5374\u662f\u6709\u957f\u773c\u775b\u7684\u3002\u4f60\u653e\u5fc3\u5427\uff0c\u7384\u94c1\u5b9d\u5251\u867d\u7136\u5389\u5bb3\uff0c\u8981\u60f3\u4f24\u6211\uff0c\u53ea\u6015\u4e5f\u8fd8\u4e0d \u662f\u90a3\u4e48\u5bb9\u6613\uff01\u201d \u3000\u3000\u79e6\u51b2\u8eb2\u5728\u4eba\u4e1b\u91cc\u5fcd\u4e0d\u4f4f\u5600\u5495\u9053\uff1a\u201c\u8fd9\u4eba\u4e0d\u662f\u75af\u5b50\uff0c\u5c31\u662f\u60f3\u8981\u81ea\u5df1\u627e\u6b7b\u4e86\uff01\u201d\u8fd9\u8bdd\u6b63\u662f\u4eba \u4eba\u5fc3\u4e2d\u60f3\u8bf4\u7684\u8bdd\uff0c\u8fde\u516c\u5b59\u5b8f\u548c\u4ef2\u957f\u7edf\u8fd9\u4e24\u4f4d\u6b66\u6797\u524d\u8f88\uff0c\u867d\u7136\u770b\u51fa\u4e86\u9752\u888d\u602a\u5ba2\u8eab\u6000\u7edd\u6280\uff0c\u4e5f\u89c9 \u5f97\u4ed6\u672a\u514d\u592a\u8fc7\u72c2\u5984\u3002\u4f46\u89c1\u6c5f\u6d77\u5929\u7684\u9762\u8272\u5374\u662f\u8d8a\u53d1\u6c89\u91cd\uff0c\u800c\u4e14\u7709\u5934\u7d27\u76b1\uff0c\u82e5\u6709\u6240\u601d\u3002\u4f17\u4eba\u8d8a\u53d1 \u60ca\u7591\u4e0d\u5b9a\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u606d\u606d\u656c\u656c\u5730\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u65e2\u7136\u8fd9\u4f4d\u524d\u8f88\u8981\u4f60\u7528\u7384\u94c1\u5b9d\u5251\uff0c\u60f3\u5fc5\u662f\u8981\u6307\u6559\u4f60\u51e0\u8def \u5251\u6cd5\uff0c\u673a\u7f18\u4e0d\u53ef\u9519\u8fc7\uff0c\u4f60\u5c31\u5e94\u8be5\u8c26\u865a\u9886\u6559\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u60f3\u9053\uff1a\u201c\u4f60\u65e2\u7136\u8fd9\u6837\u72c2\u5984\uff0c\u6ca1\u529e\u6cd5\uff0c\u6211\u4e5f\u53ea\u597d\u7ed9\u4f60\u4e00\u70b9\u5389\u5bb3\u77a7\u77a7\u4e86\u3002\u201d\u5fc3\u4e2d\u751f \u6c14\uff0c\u8c8c\u4f5c\u606d\u656c\u5730\u5e94\u4e86\u4e00\u4e2a\u201c\u662f\u201d\u5b57\uff0c\u5f53\u4e0b\u5c31\u62d4\u51fa\u4e86\u7384\u94c1\u5b9d\u5251\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8bf7\u524d\u8f88\u8d50\u62db\u3002\u201d\u9752\u888d\u602a\u5ba2\u9053\uff1a\u201c\u4f60\u4eec\u8981\u6211\u6307\u6559\uff0c\u5148\u5f97\u6296\u9732\u4e24\u624b\u7ed9\u6211\u77a7\u77a7 \u5440\uff01\u201d\u4f17\u4eba\u542c\u4e86\uff0c\u65e0\u4e0d\u6447\u5934\uff0c\u60f3\u9053\uff1a\u201c\u771f\u662f\u4e09\u5206\u989c\u8272\u4e0a\u5929\u4e86\uff0c\u6c5f\u5927\u4fa0\u8d8a\u5ba2\u6c14\uff0c\u4ed6\u5c31\u8d8a\u4e0d\u5ba2\u6c14 \u4e86\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u662f\uff01\u201d\u4f7f\u4e86\u4e00\u62db\u5929\u5c71\u6d3e\u7684\u201c\u8bf7\u624b\u5f0f\u201d\uff0c\u53cc\u638c\u5408\u8ba1\uff0c\u5411\u90a3\u4eba\u51fb\u53bb\uff0c\u5b9a\u662f\u665a\u8f88 \u548c\u957f\u8f88\u8fc7\u62db\uff0c\u8868\u793a\u5c0a\u656c\u5bf9\u65b9\u7684\u5f00\u9996\u62db\u5f0f\uff0c\u4f46\u867d\u7136\u662f\u4e00\u62db\u201c\u8bf7\u624b\u5f0f\u201d\uff0c\u5728\u6c5f\u6d77\u5929\u624b\u4e2d\u4f7f\u51fa\uff0c\u5a01 \u529b\u4e4b\u5927\uff0c\u5374\u662f\u53ef\u4ee5\u88c2\u77f3\u5f00\u7891\uff0c\u6b66\u529f\u7a0d\u5dee\u4e00\u70b9\u7684\uff0c\u6050\u6015\u90fd\u4f1a\u7b4b\u65ad\u9aa8\u6298\u3002\u516c\u5b59\u5b8f\u770b\u51fa\u6c5f\u6d77\u5929\u8fd9\u4e00 \u51fa\u624b\u5df2\u662f\u7528\u4e86\u516b\u6210\u4ee5\u4e0a\u7684\u529f\u529b\uff0c\u7edd\u975e\u624b\u4e0b\u7559\u60c5\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u8fd9\u4e00\u62db\u8bf7\u624b\u5f0f\u53ea\u6015\u6211\u4e5f \u7981\u53d7\u4e0d\u8d77\uff0c\u4e14\u770b\u8fd9\u53ae\u5982\u4f55\u5e94\u4ed8\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u53ea\u89c1\u9752\u888d\u602a\u5ba2\u968f\u624b\u4e00\u62d4\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u4efb\u4f55\u62db\u5f0f\u53ef\u8a00\uff0c\u4f46\u5947\u602a\u7684\u662f\uff0c\u4ed6\u53ea\u662f\u8fd9 \u4e48\u968f\u624b\u4e00\u62e8\uff0c\u6c5f\u6d77\u5929\u7684\u62f3\u5934\u7adf\u7136\u7ed9\u4ed6\u62e8\u5f00\uff0c\u800c\u4e14\u8fd8\u4f3c\u6709\u70b9\u7981\u4e0d\u8d77\u7684\u6837\u5b50\uff0c\u8eab\u5f62\u6643\u4e86\u4e00\u6643\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u4e0e\u4ef2\u957f\u7edf\u9762\u9762\u76f8\u89d1\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u5947\u602a\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4f4d\u6b66\u6797\u524d\u8f88\u90fd\u89c9\u5f97\u5947\u602a\uff0c\u4f17\u4eba\u5f53\u7136\u66f4\u662f\u5927\u60ca\u5931\u8272\u4e86\uff0c\u4f46\u56e0\u4ed6\u4eec\u6ca1\u6709\u8fd9\u4e24\u4f4d\u6b66\u6797\u524d\u8f88 \u7684\u773c\u529b\uff0c\u770b\u4e0d\u51fa\u6c5f\u6d77\u5929\u7684\u786e\u662f\u8f93\u4e86\u4e00\u62db\uff0c\u8bb8\u591a\u4eba\u4ecd\u662f\u4e0d\u514d\u5982\u6b64\u60f3\u9053\uff1a\u201c\u6c5f\u5927\u4fa0\u4e43\u662f\u8c26\u8c26\u541b \u5b50\uff0c\u5018\u82e5\u89c1\u9762\u4e00\u62db\uff0c\u5c31\u628a\u5bf9\u65b9\u51fb\u5012\uff0c\u672a\u514d\u6709\u5931\u541b\u5b50\u4e4b\u9053\u3002\u5bf9\uff0c\u4e00\u5b9a\u662f\u56e0\u4e3a\u8fd9\u4e2a\u7f18\u6545\uff0c\u6240\u4ee5\u6c5f \u5927\u4fa0\u6709\u610f\u8ba9\u4ed6\u4e00\u62db\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5168\u795e\u6ce8\u89c6\u5bf9\u65b9\u8def\u6570\uff0c\u5012\u6ca1\u6709\u600e\u6837\u7559\u610f\u5e08\u5144\u3002\u4e0d\u6599\u5bf9\u65b9\u4f7f\u7684\u6839\u672c\u4e0d\u662f\u4ec0\u4e48\u62db\u6570\uff0c\u800c \u4ed6\u7684\u5e08\u5144\u5df2\u662f\u9000\u4e86\u4e0b\u6765\u3002\u91d1\u9010\u6d41\u770b\u4e0d\u6e05\u695a\u5e08\u5144\u56e0\u4f55\u843d\u8d25\uff0c\u4e0d\u89c9\u4e5f\u662f\u83ab\u540d\u5176\u5999\uff0c\u4e0d\u77e5\u5e08\u5144\u662f\u771f \u7684\u8f93\u62db\u8fd8\u662f\u6709\u610f\u8ba9\u62db\uff1f\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5f85\u6211\u8bd5\u4ed6\u4e00\u8bd5\u3002\u201d\u5f53\u4e0b\u4f7f\u51fa\u5929\u7f57\u6b65\u6cd5\uff0c\u500f\u5730\u6b3a\u8eab\u76f4\u8fdb\uff0c \u5de6\u638c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\uff0c\u5411\u5bf9\u65b9\u7684\u80f8\u819b\u51fb\u53bb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8fd9\u4e00\u638c\u5df2\u662f\u7528\u4e86\u4e5d\u6210\u6709\u591a\u7684\u529b\u9053\uff0c\u6ee1\u4ee5\u4e3a\u5373\u4f7f\u4e0d\u80fd\u51fb\u5012\u5bf9\u65b9\uff0c\u81f3\u5c11\u4e5f\u53ef\u4ee5\u8bd5\u51fa\u5bf9 \u65b9\u7684\u6df1\u6d45\uff0c\u54ea\u77e5\u5bf9\u65b9\u626c\u8d77\u624b\u638c\uff0c\u659c\u659c\u4e00\u6325\uff0c\u6307\u5c16\u8f7b\u8f7b\u7684\u5728\u91d1\u9010\u6d41\u7684\u638c\u7f18\u64e6\u8fc7\uff0c\u91d1\u9010\u6d41\u90a3\u80a1\u6781 \u4e3a\u521a\u731b\u7684\u529b\u9053\uff0c\u7adf\u7136\u7ed9\u4ed6\u62e8\u5f97\u8f6c\u4e86\u4e00\u4e2a\u65b9\u5411\uff0c\u767b\u65f6\u5316\u89e3\u4e8e\u65e0\u5f62\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u70b9\u4e5f\u6ca1\u6709\u611f\u5230\u5bf9\u65b9\u8fd0\u52b2\u53cd\u51fb\uff0c\u5bf9\u65b9\u7684\u6df1\u6d45\u5982\u4f55\uff0c\u5f53\u7136\u4ed6\u4e5f\u662f\u8bd5\u63a2\u4e0d\u51fa\u7684\u4e86\u3002 \u3000\u3000\u9752\u888d\u602a\u5ba2\u968f\u624b\u5316\u89e3\u4e86\u91d1\u9010\u6d41\u7684\u653b\u62db\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5927\u987b\u5f25\u638c\u5f0f\u8bb2\u7a76\u7684\u662f\u7eaf\u6b63\u548c\u5e73\uff0c\u4f60\u7528 \u7684\u8fd9\u80a1\u731b\u52b2\uff0c\u6050\u6015\u4e0d\u5927\u5bf9\u5427\uff1f\u201d \u3000\u3000\u5927\u987b\u5f25\u638c\u5f0f\u4e43\u662f\u5929\u5c71\u6d3e\u7956\u5e08\u51cc\u672a\u98ce\u6240\u521b\uff0c\u91d1\u9010\u6d41\u7684\u7236\u4eb2\u91d1\u4e16\u9057\u4e09\u5341\u5e74\u524d\u4ece\u5929\u5c71\u6d3e\u524d\u4efb\u638c \u95e8\u5510\u6653\u6f9c\u90a3\u91cc\u5b66\u6765\uff0c\u53c8\u518d\u52a0\u4ee5\u589e\u76ca\uff0c\u53d8\u5316\u7684\u5965\u5999\u7cbe\u5947\uff0c\u5728\u5929\u4e0b\u5404\u6d3e\u638c\u6cd5\u4e4b\u4e2d\u582a\u79f0\u7b2c\u4e00\u3002\u8bb2\u5f97 \u8fd9\u5957\u638c\u6cd5\u7684\uff0c\u53ea\u662f\u5be5\u5be5\u51e0\u4f4d\u6b66\u6797\u524d\u8f88\u800c\u5df2\u3002 \u3000\u3000\u5982\u4eca\u8fd9\u9752\u888d\u602a\u5ba2\u4e0d\u4f46\u8bc6\u5f97\u8fd9\u5957\u638c\u6cd5\uff0c\u800c\u4e14\u8fd8\u80fd\u6307\u51fa\u91d1\u9010\u6d41\u7684\u7f3a\u70b9\uff0c\u91d1\u9010\u6d41\u7eb5\u7136\u5c11\u5e74\u6c14 \u76db\uff0c\u4e5f\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u6697\u6697\u4f69\u670d\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u867d\u7136\u4f69\u670d\u5bf9\u65b9\u7684\u89c1\u8bc6\u9ad8\u660e\uff0c\u672a\u66fe\u8bd5\u51fa\u5bf9\u65b9\u6df1\u6d45\uff0c\u7a76\u7adf\u5c1a\u672a\u5b8c\u5168\u5fc3\u670d\u3002\u9752\u888d\u602a\u5ba2\u597d \u4f3c\u770b\u51fa\u4ed6\u7684\u5fc3\u601d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u7384\u94c1\u5b9d\u5251\u8fd8\u672a\u7528\u5462\uff0c\u653e\u5fc3\u523a\u8fc7\u6765\u5427\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u521a\u624d\u4e0d\u6562\u7528\u5251\uff0c\u4e43\u662f\u56e0\u4e3a\u8fd8\u6709\u51e0\u5206\u987e\u5fcc\uff0c\u6050\u6015\u8bef\u4f24\u5bf9\u65b9\u3002\u6b64\u9645\u5df2\u77e5\u9053\u8fd9\u9752\u888d\u602a\u5ba2 \u7684\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u5f53\u7136\u662f\u4e0d\u6562\u518d\u5ba2\u6c14\u4e86\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u6307\u6559\uff01\u201d\u7384\u94c1\u5b9d\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u5530 \u7684\u5c31\u662f\u4e00\u62db\u201c\u5927\u6f20\u5b64\u70df\u201d\uff0c\u7b14\u76f4\u7684\u5411\u5bf9\u65b9\u523a\u53bb\uff01 \u3000\u3000\u9752\u888d\u602a\u5ba2\u8d5e\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u8fd8\u7b97\u4f7f\u5f97\u4e0d\u9519\uff01\u201d\u91d1\u9010\u6d41\u8fd9\u62db\u201c\u5927\u6f20\u5b64\u70df\u201d\u4e43\u662f\u4e00\u62db\u51cc\u5389\u975e\u5e38 \u7684\u4e0a\u4e58\u5251\u6cd5\uff0c\u591a\u5c11\u5251\u672f\u540d\u5bb6\u68a6\u5bd0\u4ee5\u6c42\uff0c\u5c1a\u672a\u80fd\u8fbe\u5230\u4ed6\u7684\u9020\u8be3\uff0c\u4e0d\u6599\u53ea\u843d\u5f97\u201c\u8fd8\u7b97\u4e0d\u9519\u201d\u7684\u56db \u5b57\u8bc4\u8bed\uff01\u9752\u888d\u602a\u5ba2\u7684\u201c\u79f0\u8d5e\u201d\u5b8c\u5168\u662f\u4e00\u526f\u957f\u8f88\u5956\u52b1\u540e\u8f88\u7684\u8bed\u6c14\uff0c\u4f17\u4eba\u542c\u4e86\uff0c\u90fd\u4e0d\u670d\u6c14\u3002 \u3000\u3000\u53ef\u662f\u201c\u884c\u5bb6\u4e00\u51fa\u624b\uff0c\u5c31\u77e5\u6709\u6ca1\u6709\u3002\u201d\u53ea\u89c1\u91d1\u9010\u6d41\u4e00\u5251\u523a\u5230\u5bf9\u65b9\u9762\u524d\uff0c\u9752\u62b1\u602a\u5ba2\u201c\u4e0d\u9519\u201d \u4e8c\u5b57\u521a\u521a\u5410\u51fa\uff0c\u500f\u5730\u5c31\u662f\u4e00\u4e2a\u8f6c\u8eab\uff0c\u8863\u8896\u8f7b\u8f7b\u7684\u4e00\u62c2\u4e00\u5e26\uff0c\u91d1\u9010\u6d41\u7684\u7384\u94c1\u5b9d\u5251\u7adf\u7136\u6b6a\u8fc7\u4e00 \u8fb9\u3002\u9752\u888d\u602a\u5ba2\u5ba0\u624b\u8896\u4e2d\uff0c\u4e25\u683c\u6765\u8bf4\u6839\u672c\u8fd8\u6ca1\u201c\u51fa\u624b\u201d\uff0c\u5c31\u628a\u4ed6\u8fd9\u4e00\u62db\u51cc\u5389\u975e\u5e38\u7684\u4e0a\u4e58\u5251\u6cd5\u5316 \u89e3\u4e86\u3002\u800c\u4e14\u4ed6\u7684\u8863\u8896\u4e0a\u8fde\u4e00\u4e2a\u5c0f\u5b54\u90fd\u6ca1\u6709\u3002\u4f17\u4eba\u65b9\u59cb\u5927\u5403\u4e00\u60ca\uff0c\u77e5\u9053\u8fd9\u9752\u888d\u602a\u5ba2\u679c\u7136\u662f\u4e2a\u8eab \u6000\u7edd\u6280\u7684\u9ad8\u624b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7684\u5403\u60ca\u6bd4\u4f17\u4eba\u66f4\u751a\uff0c\u8981\u77e5\u4ed6\u7684\u7384\u94c1\u5b9d\u5251\u91cd\u8fbe\u4e00\u767e\u591a\u65a4\uff0c\u8863\u8896\u5374\u662f\u53c8\u8f7b\u53c8\u8f6f\u4e4b\u7269\uff0c \u53ea\u662f\u8fd9\u4e48\u8f7b\u8f7b\u4e00\u62c2\uff0c\u5c31\u80fd\u628a\u91d1\u9010\u6d41\u4ee5\u7384\u94c1\u5b9d\u5251\u653b\u51fa\u7684\u529b\u9053\u8f6c\u79fb\uff0c\u8fd9\u79cd\u529f\u592b\u6b63\u662f\u4e0a\u4e58\u6b66\u5b66\u4e2d \u201c\u56db\u4e24\u62e8\u5343\u65a4\u201d\u7684\u7edd\u6280\uff01 \u3000\u3000\u91d1\u9010\u6d41\u4e5f\u66fe\u5b66\u8fc7\u8fd9\u79cd\u529f\u592b\uff0c\u53ef\u662f\u50cf\u8fd9\u9752\u888d\u602a\u5ba2\u4f7f\u5f97\u5982\u6b64\u51fa\u795e\u5165\u5316\uff0c\u4e0d\u4ec5\u4ed6\u662f\u81ea\u6127\u4e0d\u5982\uff0c \u800c\u4e14\u662f\u4ed6\u6709\u751f\u4ee5\u6765\uff0c\u6839\u672c\u5c31\u672a\u66fe\u89c1\u8fc7\u7684\uff0c\u5305\u62ec\u4ed6\u7684\u7236\u4eb2\u548c\u5e08\u5144\u5728\u5185\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5251\u638c\u517c\u65bd\u90fd\u672a\u8bd5\u51fa\u5bf9\u65b9\u7684\u6df1\u6d45\uff0c\u867d\u7136\u5df2\u7ecf\u5fc3\u91cc\u4f69\u670d\uff0c\u4f46\u5374\u4e0d\u80af\u5c31\u6b64\u7f62\u4f11\uff0c\u5fc3\u91cc\u60f3 \u9053\uff1a\u201c\u6211\u8d25\u4e0b\u9635\u6765\uff0c\u8fde\u5bf9\u65b9\u662f\u4f55\u5bb6\u4f55\u6d3e\u90fd\u4e0d\u77e5\u9053\uff0c\u5c82\u975e\u7b11\u8bdd\uff1f\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u4e5f\u662f\u903c\u4ed6\u9732\u51fa\u4e09 \u62db\u4e24\u5f0f\u624d\u884c\u3002\u201d\u5f53\u4e0b\u518d\u653b\u4e0a\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4eba\u5bb6\u662f\u8981\u8f83\u91cf\u54b1\u4eec\u4e8c\u4eba\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u4e0a \u6765\uff1f\u201d\u6b64\u65f6\u4ed6\u5df2\u77e5\u9053\u4e0e\u5e08\u5144\u8054\u624b\u4e5f\u672a\u5fc5\u80fd\u591f\u53d6\u80dc\uff0c\u4e0d\u8fc7\uff0c\u6700\u5c11\u53ef\u4ee5\u903c\u5f97\u5bf9\u65b9\u201c\u51fa\u624b\u201d\u3002 \u3000\u3000\u9752\u888d\u602a\u5ba2\u54c8\u54c8\u4e00\u7b11\uff0c\u63a5\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6c5f\u5927\u4fa0\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u5e76\u80a9\u5b50\u4e0a\u5427\u3002\u4f60\u624d\u4e0d\u8fc7\u4f7f\u4e86 \u8bf7\u624b\u5f0f\uff0c\u54b1\u4eec\u4e5f\u8fd8\u6ca1\u89c1\u8f93\u8d62\u5462\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u91cc\u81ea\u77e5\uff0c\u5176\u5b9e\u4ed6\u5df2\u662f\u8f93\u4e86\u4e00\u62db\u3002\u4ee5\u4ed6\u7684\u8eab\u4efd\uff0c\u8f93\u4e86\u4e00\u62db\uff0c\u672c\u6765\u5c31\u5e94\u8be5\u5f53\u4f17\u8ba4\u8f93 \u7684\uff0c\u4f46\u56e0\u4ed6\u4e00\u6765\u4e5f\u662f\u5fcd\u4e0d\u4f4f\u597d\u5947\u4e4b\u5fc3\uff0c\u4e8c\u6765\u4e5f\u6015\u5e08\u5f1f\u5403\u4e8f\uff0c\u5fc3\u60f3\uff1a\u201c\u4e07\u4e00\u6211\u731c\u5f97\u4e0d\u5bf9\uff0c\u6211\u8ba4 \u8f93\u4e0d\u8981\u7d27\uff0c\u5e08\u5f1f\u53d7\u4e86\u4f24\u6211\u53ef\u5c31\u5bf9\u4e0d\u8d77\u5e08\u7236\u4e86\u3002\u201d\u539f\u6765\u4ed6\u5df2\u60f3\u5230\u4e86\u4e00\u4e2a\u4eba\uff0c\u6599\u60f3\u8fd9\u4e2a\u9752\u888d\u602a\u5ba2 \u5341\u4e5d\u5c31\u662f\u8fd9\u4eba\uff0c\u4f46\u5374\u8fd8\u4e0d\u6562\u5b8c\u5168\u65ad\u5b9a\u3002 \u3000\u3000\u9752\u888d\u602a\u5ba2\u65e2\u7136\u6709\u8bdd\u5728\u5148\uff0c\u662f\u8ba9\u4ed6\u4eec\u4e8c\u4eba\u8054\u624b\uff0c\u4ed6\u521a\u624d\u5355\u72ec\u8f93\u4e86\u4e00\u62db\uff0c\u8bba\u7406\u4e5f\u8fd8\u4e0d\u80fd\u5c31\u7b97 \u8f93\u4e86\u3002\u4e8e\u662f\u6c5f\u6d77\u5929\u53c8\u518d\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u8bf7\u6055\u665a\u8f88\u653e\u8086\uff0c\u665a\u8f88\u4e0d\u6562\u8bf4\u662f\u8f83\u91cf\uff0c\u53ea\u662f\u60f3\u6c42\u524d\u8f88\u6307 \u70b9\u3002\u201d\u9752\u888d\u602a\u5ba2\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u51fa\u624b\uff0c\u6211\u5982\u4f55\u6307\u70b9\u4f60\u5440\uff1f\u522b\u7f57\u55e6\u4e86\uff0c\u4f60\u6709\u4e9b\u4ec0\u4e48\u672c\u9886\uff0c\u5feb\u70b9\u4f7f \u51fa\u6765\u5427\uff01\u201d\u6c5f\u6d77\u5929\u606d\u606d\u656c\u656c\u5730\u5e94\u4e86\u4e00\u4e2a\u201c\u662f\u201d\u5b57\uff0c\u53cc\u638c\u5c31\u5411\u90a3\u9752\u888d\u602a\u5ba2\u6253\u53bb\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u53cc\u638c\u9f50\u51fa\uff0c\u91d1\u9010\u6d41\u4e5f\u662f\u5251\u638c\u517c\u65bd\uff0c\u5e08\u5144\u5f1f\u5de6\u53f3\u5939\u653b\uff0c\u90a3\u9752\u888d\u602a\u5ba2\u53ea\u6709\u4e00\u53cc\u624b\uff0c \u201c\u56db\u4e24\u62e8\u5343\u65a4\u201d\u7684\u529f\u592b\u65e0\u8bba\u5982\u4f55\u795e\u5999\uff0c\u4e5f\u51b3\u4e0d\u80fd\u540c\u65f6\u5316\u89e3\u4ed6\u4eec\u7684\u62db\u6570\u3002\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a \u201c\u597d\uff0c\u770b\u4f60\u8fd8\u80fd\u591f\u4e0d\u9732\u51fa\u672c\u95e8\u7684\u6b66\u529f\u4e48\u3002\u201d\u91d1\u9010\u6d41\u901a\u6653\u6b63\u90aa\u5404\u6d3e\u7684\u6b66\u529f\uff0c\u5fc3\u60f3\u6b64\u4eba\u9732\u51fa\u4e00\u62db \u534a\u5f0f\uff0c\u6211\u5c31\u4e0d\u96be\u77e5\u9053\u4ed6\u7684\u6765\u5386\u3002 \u3000\u3000\u9752\u888d\u602a\u5ba2\u8d5e\u9053\uff1a\u201c\u5230\u5e95\u662f\u5e08\u5144\u9ad8\u660e\u5f97\u591a\uff0c\u8fd9\u5927\u987b\u5f25\u638c\u5f0f\u5dee\u4e0d\u591a\u53ef\u4ee5\u8bf4\u662f\u7089\u706b\u7eaf\u9752\u4e86\uff01\u201d \u6c5f\u6d77\u5929\u7684\u6b66\u529f\u4e45\u5df2\u88ab\u6b66\u6797\u516c\u8ba4\u5929\u4e0b\u7b2c\u4e00\uff0c\u8fd9\u4e00\u5f0f\u5927\u987b\u5f25\u638c\u66f4\u662f\u4ed6\u6b66\u529f\u7684\u7cbe\u534e\u6240\u5728\uff0c\u4e0d\u6599\u5728\u9752 \u888d\u602a\u5ba2\u53e3\u4e2d\uff0c\u4e5f\u53ea\u4e0d\u8fc7\u843d\u5f97\u4e2a\u201c\u5dee\u4e0d\u591a\u201d\u7684\u4e09\u5b57\u8bc4\u8bed\u3002 \u3000\u3000\u638c\u98ce\u5251\u5f71\u4e4b\u4e2d\u53ea\u89c1\u9752\u888d\u602a\u5ba2\u4ecd\u662f\u4e0d\u614c\u4e0d\u5fd9\u5730\u8f7b\u8f7b\u4e00\u62e8\uff0c\u91d1\u9010\u6d41\u7684\u7384\u94c1\u5b9d\u5251\u9996\u5148\u653b\u5230\uff0c\u5b9d \u5251\u7ed9\u4ed6\u62e8\u5f97\u7a81\u7136\u8f6c\u4e86\u65b9\u5411\uff0c\u7adf\u662f\u4e0d\u7531\u81ea\u4e3b\u5730\u5411\u5e08\u5144\u523a\u53bb\u3002\u6c5f\u6d77\u5929\u53cc\u638c\u6539\u5288\u4e3a\u63a8\uff0c\u4e00\u80a1\u5288\u7a7a\u638c \u529b\u628a\u7384\u94c1\u5b9d\u5251\u8361\u5f00\u3002\u5e08\u5144\u5f1f\u4e0d\u7ea6\u800c\u540c\u5730\u5404\u81ea\u659c\u7a9c\u4e09\u6b65\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u9752\u888d\u602a\u5ba2\u7528\u7684\u624b\u6cd5\u66f4\u662f\u51fa\u4eba\u610f\u5916\u7684\u795e\u5999\uff0c\u4e0d\u4ec5\u662f\u201c\u56db\u4e24\u62e8\u5343\u65a4\u201d\uff0c\u800c\u4e14\u662f\u501f\u529b\u6253 \u529b\uff0c\u5229\u7528\u4e86\u91d1\u9010\u6d41\u7684\u7384\u94c1\u5b9d\u5251\u6765\u5bf9\u4ed8\u6c5f\u6d77\u5929\u3002\u4ed6\u672c\u8eab\u7684\u771f\u5b9e\u672c\u9886\u4ecd\u662f\u4e1d\u6beb\u672a\u9732\u3002 \u3000\u3000\u6c5f\u3001\u91d1\u4e8c\u4eba\u5de6\u53f3\u5206\u5f00\uff0c\u9752\u888d\u602a\u5ba2\u5e76\u6ca1\u4e58\u673a\u8fdb\u51fb\uff0c\u53cd\u800c\u5b9a\u4e0b\u8eab\u5f62\uff0c\u8bf4\u9053\uff1a\u201c\u518d\u6765\uff0c\u518d\u6765\uff01 \u6c5f\u5927\u4fa0\uff0c\u4f60\u8fd9\u4e00\u5f0f\u5927\u987b\u5f25\u638c\u7a0d\u5acc\u51fa\u624b\u5feb\u4e9b\uff0c\u6162\u4e00\u70b9\u66f4\u597d\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u591a\u627f\u524d\u8f88\u6307\u6559\uff01\u5f1f\u5b50\u53ef\u4e0d\u6562\u5f53\u5927\u4fa0\u4e4b\u79f0\u3002\u201d\u9752\u888d\u602a\u5ba2\u7b11\u9053\uff1a\u201c\u8fd9\u4f60\u5012\u4e0d\u5fc5 \u5ba2\u6c14\uff0c\u6211\u4e0d\u662f\u79f0\u8d5e\u4f60\u7684\u6b66\u529f\uff0c\u6211\u662f\u79f0\u8d5e\u4f60\u7684\u884c\u4e8b\uff0c\u4f60\u7684\u884c\u4e8b\u5e76\u4e0d\u6127\u4e8e\u2018\u5927\u4fa0\u2019\u4e8c\u5b57\uff01\u201d \u3000\u3000\u5e08\u5144\u5f1f\u9000\u800c\u590d\u4e0a\uff0c\u6c5f\u6d77\u5929\u5168\u795e\u8d2f\u6ce8\u5730\u4f7f\u51fa\u5927\u987b\u5f25\u638c\u5f0f\uff0c\u90a3\u4e00\u4e1d\u4e0d\u82df\u7684\u795e\u6c14\u5c31\u50cf\u5728\u5e08\u95e8\u4e60 \u6280\u4e4b\u65f6\u7ec3\u7ed9\u5e08\u7236\u770b\u4f3c\u7684\u3002\u9752\u888d\u602a\u5ba2\u968f\u624b\u5316\u89e3\uff0c\u4e00\u9762\u8fde\u8fde\u70b9\u5934\uff0c\u8868\u793a\u8d5e\u8bb8\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u4eec\u7684\u672c\u9886\u90fd\u5df2\u62ff\u51fa\u6765\u4e86\uff0c\u8bf7\u8001\u524d\u8f88\u4e5f\u8ba9\u6211\u4eec\u89c1\u8bc6\u89c1\u8bc6\u5427\uff01\u201d\u4ed6\u89c1\u5e08\u5144\u5bf9 \u6b64\u4eba\u5982\u6b64\u606d\u656c\uff0c\u4e0d\u89c9\u4e5f\u662f\u8d77\u4e86\u7591\u5fc3\uff0c\u8bf4\u51fa\u8bdd\u6765\uff0c\u4e5f\u5c31\u4e0d\u6562\u4e0d\u606d\u656c\u4e86\u3002 \u3000\u3000\u9752\u888d\u602a\u5ba2\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4f1a\u7684\u53ea\u662f\u6700\u5bfb\u5e38\u7684\u529f\u592b\uff0c\u5176\u5b9e\u4f60\u4e0d\u89c1\u4e5f\u4f1a\u8bc6\u7684\u3002\u4f60\u65e2\u7136 \u5b9a\u8981\u89c1\u8bc6\uff0c\u90a3\u5c31\u8ba9\u4f60\u89c1\u8bc6\u5427\u3002\u201d \u3000\u3000\u7b11\u58f0\u4e2d\u9752\u888d\u602a\u5ba2\u715e\u6709\u5176\u4e8b\u7684\u7acb\u4e86\u4e00\u4e2a\u95e8\u6237\uff0c\u6c89\u8170\u5750\u9a6c\uff0c\u4e00\u62f3\u6363\u51fa\uff0c\u903c\u9000\u4e86\u6c5f\u6d77\u5929\uff1b\u4e00\u638c \u659c\u98de\uff0c\u683c\u5f00\u4e86\u91d1\u9010\u6d41\u3002\u624d\u4f7f\u4e86\u4e24\u62db\uff0c\u4f17\u4eba\u8be7\u5f02\u7684\u7a83\u7a83\u79c1\u8bae\u4e4b\u58f0\u5df2\u662f\u6b64\u8d77\u5f7c\u843d\uff0c\u201c\u54a6\uff0c\u8fd9\u4e0d\u662f \u56db\u5e73\u62f3\u5417\uff1f\u201d\u201c\u5947\u602a\uff0c\u4ed6\u600e\u4f1a\u4f7f\u51fa\u8fd9\u79cd\u666e\u901a\u7684\u62f3\u6cd5\u5bf9\u4ed8\u6c5f\u5927\u4fa0\uff1f\u201d \u3000\u3000\u539f\u6765\u9752\u888d\u602a\u5ba2\u4f7f\u7684\u201c\u56db\u5e73\u62f3\u201d\u6b63\u662f\u6700\u5bfb\u5e38\u4e0d\u8fc7\u7684\u62f3\u6cd5\u3002 \u3000\u3000\u8fd9\u5957\u201c\u56db\u5e73\u62f3\u201d\u4e43\u662f\u6700\u666e\u901a\u7684\u5165\u95e8\u62f3\u811a\u529f\u592b\uff0c\u4e5f\u662f\u5f53\u65f6\u6700\u6d41\u884c\u7684\u4e00\u5957\u62f3\u672f\uff0c\u4f46\u5374\u4e3a\u6b66\u5b66 \u9ad8\u624b\u6240\u770b\u4e0d\u8d77\u7684\u3002\u4e00\u822c\u4e8c\u4e09\u6d41\u7684\u62f3\u5e08\uff0c\u7ed9\u5f1f\u5b50\u542f\u8499\uff0c\u6559\u7684\u5c31\u5927\u90fd\u662f\u8fd9\u4e00\u5957\u201c\u56db\u5e73\u62f3\u3002\u201d \u3000\u3000\u9752\u888d\u602a\u5ba2\u80c6\u6562\u5411\u6c5f\u6d77\u5929\u5e08\u5144\u5f1f\u6311\u6218\uff0c\u800c\u4e14\u672a\u66fe\u771f\u6b63\u201c\u51fa\u624b\u201d\uff0c\u5c31\u5360\u4e86\u4e0a\u98ce\uff0c\u8c01\u90fd\u4ee5\u4e3a\u4ed6 \u4e00\u5b9a\u6709\u60ca\u4eba\u7684\u6280\u4e1a\uff0c\u4e00\u51fa\u624b\u5c31\u4e0d\u77e5\u662f\u5982\u4f55\u795e\u5947\u5965\u5999\u7684\u62f3\u672f\u4e86\u3002\u54ea\u77e5\u4ed6\u4f7f\u51fa\u6765\u7adf\u7136\u662f\u4e00\u5957\u5e73\u5e73 \u65e0\u5947\u7684\u201c\u56db\u5e73\u62f3\u201d\uff0c\u4f17\u4eba\u90fd\u662f\u4e0d\u7981\u5567\u5567\u79f0\u5f02\u3002 \u3000\u3000\u4e0d\u6599\u8fd9\u4e00\u5957\u5927\u5bb6\u90fd\u77a7\u4e0d\u8d77\u7684\u201c\u56db\u5e73\u62f3\u201d\uff0c\u5728\u9752\u888d\u602a\u5ba2\u624b\u4e2d\u4f7f\u51fa\uff0c\u5374\u7adf\u7136\u4ee4\u5230\u6c5f\u6d77\u5929\u548c\u91d1 \u9010\u6d41\u90fd\u4f3c\u4e4e\u6709\u70b9\u96be\u4ee5\u5e94\u4ed8\u3002\u4f17\u4eba\u4e0d\u7981\u53c8\u662f\u5927\u4e3a\u60ca\u6115\u3002 \u3000\u3000\u201c\u56db\u5e73\u62f3\u201d\u5c31\u662f\u201c\u56db\u5e73\u62f3\u201d\uff0c\u9752\u888d\u602a\u5ba2\u5e76\u6ca1\u52a0\u4e0a\u4efb\u4f55\u53d8\u5316\uff0c\u6253\u51fa\u6765\u7684\u4e00\u62db\u4e00\u5f0f\u90fd\u662f\u4f17\u4eba \u89c1\u60ef\u7684\u8ba4\u4e3a\u7c97\u6d45\u4e0d\u582a\u7684\u201c\u56db\u5e73\u62f3\u201d\u3002\u53ef\u662f\u8bf4\u4e5f\u5947\u602a\uff0c\u6c5f\u6d77\u5929\u4f7f\u51fa\u4e86\u5965\u5999\u65e0\u7a77\u7684\u5927\u987b\u5f25\u638c\u5f0f\uff0c \u91d1\u9010\u6d41\u4ee5\u7384\u94c1\u5b9d\u5251\u4f7f\u51fa\u4e86\u51cc\u5389\u975e\u5e38\u7684\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u8ffd\u98ce\u5251\u5f0f\uff0c\u7adf\u7136\u4e00\u70b9\u4e5f\u5948\u4f55\u4ed6\u4e0d\u5f97\uff0c\u800c\u4e14 \u8fd8\u7ed9\u4ed6\u903c\u5f97\u53ea\u6709\u62db\u67b6\u7684\u4efd\u513f\u3002\u4f46\u89c1\u4ed6\u4fe1\u624b\u4e00\u62f3\u5730\u6253\u5411\u6c5f\u6d77\u5929\uff0c\u6c5f\u6d77\u5929\u5c31\u8981\u53cc\u638c\u9f50\u51fa\uff0c\u65b9\u80fd\u62b5 \u6321\u5f97\u4f4f\uff0c\u968f\u624b\u4e00\u638c\uff0c\u5411\u91d1\u9010\u6d41\u5288\u53bb\uff0c\u91d1\u9010\u6d41\u5c31\u8981\u8fde\u5fd9\u95ea\u907f\uff0c\u4f17\u4eba\u770b\u4e86\u90fd\u662f\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u770b\u4e86\u4e00\u4f1a\uff0c\u4e0d\u89c9\u5927\u5927\u5403\u60ca\uff0c\u6084\u6084\u5bf9\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u529f\u592b\u7aef\u7684\u5df2\u662f\u5230\u4e86\u51fa\u795e\u5165 \u5316\u4e4b\u5883\uff0c\u8001\u53eb\u5316\uff0c\u4f60\u53ef\u770b\u5f97\u51fa\u8fd9\u4eba\u7684\u6765\u5386\u4e48\u3002\u201d\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4eba\u4e0d\u8bba\u662f\u4efb\u4f55\u666e\u901a\u7684\u62f3 \u672f\uff0c\u4ed6\u53ea\u987b\u4fe1\u624b\u62c8\u6765\uff0c\u5c31\u53ef\u4ee5\u53d1\u6325\u65e0\u7a77\u5a01\u529b\u3002\u91d1\u4e16\u9057\u5f53\u5e74\u5728\u5d69\u5c71\u5c11\u6797\u5bfa\u5927\u8d25\u5b5f\u795e\u901a\u4e4b\u65f6\uff0c\u4e5f \u4f3c\u4e4e\u6ca1\u6709\u4ed6\u8fd9\u6837\u7684\u6b66\u5b66\u9020\u8be3\u3002\u201d \u3000\u3000\u9664\u4e86\u516c\u5b59\u5b8f\u4e0e\u4ef2\u957f\u7edf\u4e4b\u5916\uff0c\u4eba\u4eba\u90fd\u662f\u770b\u5f97\u83ab\u540d\u5176\u5999\u3002\u4ed6\u4eec\u6839\u636e\u6c5f\u3001\u91d1\u4e8c\u4eba\u7684\u6027\u683c\u731c\u6d4b\uff0c \u8fd8\u4ee5\u4e3a\u91d1\u9010\u6d41\u662f\u6709\u5fc3\u620f\u5f04\uff0c\u800c\u6c5f\u6d77\u5929\u5219\u662f\u6545\u610f\u8ba9\u62db\u3002\u54ea\u77e5\u6c5f\u3001\u91d1\u4e8c\u4eba\u7684\u786e\u662f\u201c\u68cb\u5dee\u4e00\u7740\uff0c\u675f \u624b\u675f\u811a\u201d\u3002\u6b64\u65f6\u5fc3\u4e2d\u90fd\u5728\u6697\u6697\u53eb\u82e6\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4eba\u4f7f\u7684\u867d\u7136\u662f\u4e00\u5957\u518d\u4e5f\u5bfb\u5e38\u4e0d\u8fc7\u7684\u201c\u56db\u5e73\u62f3\u201d\uff0c\u4f46\u6c5f\u3001\u91d1\u4e8c\u4eba\u7684\u6bcf\u62db\u6bcf\u5f0f\uff0c\u5374\u4f3c \u4e4e\u5168\u90e8\u5728\u4ed6\u610f\u6599\u4e4b\u4e2d\u3002\u6bd4\u5982\u8bf4\u91d1\u9010\u6d41\u4e00\u5251\u523a\u4ed6\u5de6\u80a9\uff0c\u4ed6\u968f\u4fbf\u8fc8\u4e0a\u4e00\u6b65\uff0c\u6253\u51fa\u6765\u7684\u4e00\u62f3\u5c31\u521a\u597d \u662f\u653b\u5411\u91d1\u9010\u6d41\u7684\u201c\u7a7a\u95e8\u201d\uff0c\u4ee4\u5f97\u91d1\u9010\u6d41\u975e\u8981\u95ea\u907f\u4e0d\u53ef\uff0c\u5bf9\u4ed8\u6c5f\u6d77\u5929\u4e5f\u662f\u4e00\u6837\uff0c\u6bcf\u4e00\u62db\u90fd\u662f\u5236 \u654c\u673a\u5148\uff0c\u653b\u654c\u4e4b\u6240\u5fc5\u6551\u3002\u53ef\u662f\u4ed6\u7684\u62f3\u6cd5\u6b65\u6cd5\uff0c\u5374\u53c8\u4e1d\u6beb\u6ca1\u6709\u7279\u5f02\u4e4b\u5904\uff0c\u7684\u7684\u786e\u786e\u662f\u7c97\u6d45\u4e0d\u582a \u7684\u201c\u56db\u5e73\u62f3\u201d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u672c\u4ee5\u4e3a\u9664\u975e\u4ed6\u4e0d\u51fa\u624b\uff0c\u4e00\u51fa\u624b\u5c31\u80fd\u770b\u51fa\u4ed6\u7684\u95e8\u6d3e\u7684\uff0c\u54ea\u77e5\u4ed6\u4f7f\u51fa\u4e86\u201c\u56db\u5e73\u62f3\u201d\uff0c \u201c\u56db\u5e73\u62f3\u201d\u65e2\u7136\u4eba\u4eba\u4f1a\u4f7f\uff0c\u91d1\u9010\u6d41\u53c8\u7109\u80fd\u770b\u51fa\u4ed6\u7684\u6765\u5386\uff1f \u3000\u3000\u91d1\u9010\u6d41\u4e0d\u7531\u5f97\u5fc3\u4e2d\u70e6\u8e81\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u6211\u4eec\u5e08\u5144\u5f1f\u8d25\u7ed9\u4eba\u5bb6\uff0c\u8fde\u4eba\u5bb6\u7684\u8fb9\u513f\u90fd\u672a\u6478\u7740\uff0c \u8fd9\u5c82\u4e0d\u662f\u5929\u5927\u7684\u7b11\u8bdd\uff01\u201d\u84e6\u5730\u4e00\u58f0\u957f\u5578\u3002\u4f7f\u51fa\u4e86\u4e00\u62db\u53e4\u602a\u4e4b\u6781\u7684\u5251\u6cd5\uff0c\u7384\u94c1\u5b9d\u5251\u6a2a\u7a7a\u4e00\u5212\uff0c \u5251\u5c16\u4f38\u7f29\u4e0d\u5b9a\uff0c\u5982\u5c01\u4f3c\u95ed\uff0c\u82e5\u5b88\u82e5\u653b\u3002 \u3000\u3000\u5728\u573a\u89c2\u6218\u7684\u5c06\u8fd1\u5343\u4eba\uff0c\u5404\u6d3e\u7684\u5251\u672f\u90fd\u6709\u4eba\u77e5\u6653\uff0c\u4f46\u5374\u65e0\u4eba\u8bc6\u5f97\u91d1\u9010\u6d41\u4f7f\u7684\u8fd9\u4e00\u62db\u662f\u4ec0\u4e48 \u5251\u6cd5\u3002 \u3000\u3000\u539f\u6765\u662f\u91d1\u9010\u6d41\u4e00\u534a\u5077\u6765\uff0c\u4e00\u534a\u662f\u81ea\u521b\u7684\u65b0\u62db\u3002\u662f\u4ece\u5e78\u5b97\u6d9b\u6240\u4f7f\u7684\u6276\u6851\u5c9b\u72ec\u95e8\u5251\u6cd5\u4e2d\u53d8\u5316 \u51fa\u6765\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u806a\u660e\u7edd\u9876\uff0c\u65e5\u95f4\u548c\u725f\u5b97\u6d9b\u6bd4\u6b66\u4e4b\u65f6\uff0c\u725f\u5b97\u6d9b\u6240\u4f7f\u7684\u90a3\u4e9b\u5947\u8be1\u7edd\u4f26\u7684\u62db\u6570\uff0c\u4ed6\u867d\u7136 \u672a\u80fd\u5168\u90e8\u9886\u609f\uff0c\u4f46\u6700\u7cbe\u5999\u7684\u5341\u51e0\u62db\u5251\u6cd5\uff0c\u4ed6\u5df2\u662f\u7262\u7262\u5730\u8bb0\u5728\u5fc3\u4e2d\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u662f\u7528\u4e00\u628a\u6298\u6247\u5f53\u4f5c\u5224\u5b98\u7b14\u548c\u4e94\u884c\u5251\u4f7f\u7684\uff0c\u6298\u6247\u662f\u4efd\u91cf\u6781\u8f7b\u7684\u4e1c\u897f\uff0c\u725f\u5b97\u6d9b\u4ee5\u6247\u4ee3 \u5251\u4e4b\u65f6\uff0c\u4f7f\u51fa\u7684\u5251\u62db\u8bb2\u7a76\u7684\u662f\u201c\u795e\u4f3c\u201d\u800c\u975e\u201c\u5f62\u4f3c\u201d\uff0c\u552f\u5176\u201c\u795e\u4f3c\u201d\uff0c\u56e0\u6b64\u5c31\u7279\u522b\u96be\u4ee5\u6349 \u6478\u3002\u597d\u5728\u91d1\u9010\u6d41\u609f\u6027\u6781\u9ad8\uff0c\u5251\u672f\u4e0a\u53c8\u6709\u6781\u6df1\u539a\u7684\u57fa\u7840\uff0c\u6bd4\u6b66\u8fc7\u540e\uff0c\u4ed4\u7ec6\u7422\u78e8\uff0c\u8fd9\u624d\u80fd\u591f\u5fc3\u9886 \u795e\u4f1a\u3002\u4f46\u5982\u4eca\u91d1\u9010\u6d41\u662f\u7528\u7384\u94c1\u91cd\u5251\u4f7f\u51fa\u5bf9\u65b9\u7684\u62db\u6570\uff0c\u5f53\u7136\u4e0d\u53ef\u80fd\u4e0e\u725f\u5b97\u6d9b\u7528\u6298\u6247\u4f7f\u51fa\u7684\u62db\u6570 \u4e00\u6a21\u4e00\u6837\uff0c\u5018\u82e5\u201c\u4f9d\u6837\u753b\u846b\u82a6\u201d\u7684\u8bdd\uff0c\u90a3\u5c31\u5fc5\u5b9a\u662f\u5f04\u5de7\u6210\u62d9\u4e86\uff0c\u6545\u6b64\u4ed6\u5fc5\u987b\u52a0\u4ee5\u53d8\u5316\uff0c\u4fdd\u5b58 \u5bf9\u65b9\u5251\u6cd5\u7684\u795e\u9ad3\u800c\u81ea\u521b\u65b0\u62db\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7528\u8fd9\u6837\u4e00\u62db\u53e4\u602a\u7684\u5251\u6cd5\u5bf9\u4ed8\u9752\u888d\u602a\u5ba2\uff0c\u4e5f\u662f\u6709\u4ed6\u7684\u7528\u610f\u7684\uff0c\u9752\u888d\u602a\u5ba2\u6b66\u529f\u9ad8\u660e\u4e4b \u6781\uff0c\u8fd9\u4e00\u62db\u5251\u6cd5\u867d\u7136\u5965\u5999\uff0c\u4f46\u8981\u80dc\u4ed6\uff0c\u91d1\u9010\u6d41\u81ea\u5df1\u4e5f\u77e5\u9053\u8fd9\u662f\u5984\u60f3\u3002\u4e0d\u8fc7\uff0c\u91d1\u9010\u6d41\u7684\u7528\u610f\u5012 \u4e0d\u662f\u5728\u4e8e\u80dc\u4ed6\uff0c\u800c\u662f\u5e0c\u671b\u8bd5\u63a2\u51fa\u5bf9\u65b9\u7684\u672c\u95e8\u5bb6\u6570\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8d77\u521d\u7591\u5fc3\u8fd9\u9752\u888d\u602a\u5ba2\u662f\u725f\u5b97\u6d9b\uff0c\u540e\u6765\u4e00\u770b\u4e0d\u50cf\uff0c\u4f46\u4ecd\u7136\u7591\u5fc3\u4ed6\u662f\u6276\u6851\u5c9b\u7684\u9ad8\u624b\u3002 \u56e0\u4e3a\u4e2d\u539f\u5404\u6d3e\u7684\u6b66\u676f\u4eba\u7269\uff0c\u59d4\u5b9e\u627e\u4e0d\u5230\u4e00\u4e2a\u6709\u9752\u888d\u602a\u5ba2\u8fd9\u822c\u672c\u9886\u7684\u4eba\uff0c\u800c\u6276\u6851\u5c9b\u866c\u9aef\u5ba2\u8fd9\u4e00 \u8109\u6240\u4f20\u7684\u6b66\u529f\uff0c\u636e\u725f\u5b97\u6d9b\u4e4b\u8a00\uff0c\u540e\u6765\u6f14\u53d8\u6210\u4e09\u4e2a\u652f\u6d3e\uff0c\u725f\u5b97\u6d9b\u6240\u5f97\u7684\u5148\u7956\u6240\u4f20\u5c1a\u672a\u5230\u5341\u5206\u4e4b \u4e00\uff0c\u7109\u77e5\u6ca1\u6709\u6bd4\u725f\u5b97\u6d9b\u66f4\u5f3a\u7684\u9ad8\u624b\u3002 \u3000\u3000\u4e0d\u8bba\u6b66\u5b66\u5982\u4f55\u9ad8\u660e\u4e4b\u58eb\uff0c\u7a81\u7136\u9047\u5230\u672c\u95e8\u7684\u7cbe\u5999\u62db\u6570\uff0c\u5341\u5c45\u516b\u4e5d\uff0c\u4e00\u5b9a\u4f1a\u7528\u672c\u95e8\u7684\u62db\u6570\u5316 \u89e3\u7684\uff0c\u56e0\u4e3a\u8fd9\u662f\u4e00\u79cd\u672c\u80fd\u7684\u53cd\u5e94\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8fd9\u4e00\u62db\u4f7f\u51fa\uff1a\u9752\u888d\u602a\u5ba2\u597d\u50cf\u6709\u70b9\u60ca\u5f02\u7684\u6837\u5b50\uff0c\u5fae\u5fae\u201c\u566b\u201d\u4e86\u4e00\u58f0\u3002\u91d1\u9010\u6d41\u6697\u6697\u6b22 \u559c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u4e00\u4e0b\u5b50\uff0c\u770b\u4f60\u8fd8\u80fd\u4e0d\u9732\u539f\u5f62\u4e48\u3002\u201d \u3000\u3000\u54ea\u77e5\u9752\u888d\u602a\u5ba2\u867d\u7136\u8be7\u5f02\u5f97\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u4f46\u5728\u91d1\u9010\u6d41\u7684\u5251\u62db\u653b\u5230\u4e4b\u65f6\uff0c\u4ed6\u4ecd\u7136\u662f\u7528\u4e00\u62db \u5e73\u5e73\u65e0\u5947\u7684\u201c\u56db\u5e73\u62f3\u201d\u5c31\u628a\u91d1\u9010\u6d41\u8fd9\u62db\u522b\u51fa\u5fc3\u88c1\u7684\u5251\u6cd5\u5316\u89e3\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5927\u4e3a\u5931\u671b\uff0c\u7a81\u7136\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5728\u5931\u671b\u4e4b\u4e2d\u53c8\u627e\u5230\u4e86\u5e0c\u671b\u3002 \u3000\u3000\u539f\u6765\u5f53\u91d1\u9010\u6d41\u4ee5\u5bb6\u4f20\u6b66\u529f\u4e0e\u8fd9\u9752\u888d\u602a\u5ba2\u5bf9\u654c\u4e4b\u65f6\uff0c\u9752\u888d\u602a\u5ba2\u4ee5\u201c\u56db\u5e73\u62f3\u201d\u968f\u624b\u5316\u89e3\uff0c\u6beb \u4e0d\u8d39\u5200\u3002\u5982\u4eca\u91d1\u9010\u6d41\u7528\u8fd9\u4e00\u62db\u65b0\u521b\u7684\u5251\u6cd5\uff0c\u867d\u7136\u4ed6\u4e5f\u4e00\u6837\u7684\u7528\u201c\u56db\u5e73\u62f3\u201d\u968f\u624b\u5316\u89e3\uff0c\u5e76\u4e0d\u8d39 \u529b\u3002\u4f46\u91d1\u9010\u6d41\u5374\u770b\u5f97\u51fa\u6765\uff0c\u4ed6\u5df2\u662f\u7a0d\u5fae\u591a\u7528\u4e86\u4e00\u70b9\u795e\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8fde\u5fd9\u5411\u5e08\u5144\u629b\u4e86\u4e00\u4e2a\u773c\u8272\u3002\u968f\u5373\u8fde\u7eed\u4f7f\u51fa\u4e00\u534a\u5077\u5b66\uff0c\u4e00\u534a\u81ea\u521b\u7684\u65b0\u62db\uff0c\u66b4\u98ce\u9aa4\u96e8 \u822c\u5411\u90a3\u9752\u888d\u602a\u5ba2\u653b\u53bb\u3002 \u3000\u3000\u6c5f\u6d77\u5929\u5fc3\u91cc\u6697\u6697\u597d\u7b11\uff1a\u201c\u5e08\u5f1f\u5fd2\u4e5f\u597d\u80dc\uff0c\u597d\u5728\u5bf9\u65b9\u5e76\u65e0\u6076\u610f\uff0c\u5426\u5219\u5982\u6b64\u5b8c\u5168\u4e0d\u987e\u9632\u5fa1\u7684 \u8fdb\u653b\uff0c\u78b0\u4e0a\u8fd9\u6837\u9ad8\u660e\u7684\u5bf9\u624b\uff0c\u4e0d\u7ed9\u5bf9\u65b9\u4f24\u4e86\u624d\u602a\uff01\u201d\u4f46\u4e3a\u4e86\u4e0d\u8ba9\u5e08\u5f1f\u5931\u671b\uff0c\u540c\u65f6\u4e5f\u662f\u4e3a\u4e86\u6050 \u9632\u81ea\u5df1\u6240\u6599\u4e0d\u4e2d\uff0c\u4e07\u4e00\u5e08\u5f1f\u53d7\u4f24\u7684\u8bdd\uff0c\u8fd9\u53ef\u4e0d\u662f\u5f53\u800d\u7684\u3002\u56e0\u6b64\u6c5f\u6d77\u5929\u867d\u7136\u5fc3\u91cc\u65e9\u5df2\u670d\u8f93\uff0c\u4ecd \u7136\u4e0d\u5f97\u4e0d\u4e0e\u91d1\u9010\u6d41\u7d27\u5bc6\u914d\u5408\uff0c\u50ac\u7d27\u638c\u529b\uff0c\u5c3d\u5176\u6240\u80fd\u7684\u4e0e\u91d1\u9010\u6d41\u8054\u624b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u4e00\u53e3\u6c14\u653b\u4e86\u5341\u591a\u62db\uff0c\u4f17\u4eba\u6b63\u5728\u770b\u5f97\u773c\u82b1\u7f2d\u4e71\uff0c\u5ffd\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u91d1\u9010\u6d41\u7684\u7384 \u94c1\u5b9d\u5251\u8131\u624b\u5760\u5730\uff0c\u4eba\u4e5f\u8dcc\u51fa\u4e86\u4e00\u4e08\u5f00\u5916\uff01\u539f\u6765\u5728\u4ed6\u653b\u5230\u7b2c\u5341\u4e09\u62db\u4e4b\u65f6\uff0c\u7adf\u7136\u4e0d\u987e\u5371\u9669\uff0c\u76f4\u6b3a \u5230\u9752\u888d\u602a\u5ba2\u7684\u8eab\u524d\uff0c\u7ed9\u9752\u888d\u602a\u5ba2\u5728\u4ed6\u864e\u53e3\u4e00\u5f39\uff0c\u7384\u94c1\u5b9d\u5251\u767b\u65f6\u8131\u624b\uff01 \u3000\u3000\u6c5f\u6d77\u5929\u5927\u5403\u4e00\u60ca\uff0c\u4e0d\u77e5\u5e08\u5f1f\u4f24\u5f97\u5982\u4f55\uff0c\u6b63\u8981\u8dd1\u8fc7\u53bb\u60f3\u8981\u6276\u4ed6\u8d77\u6765\uff0c\u4e0d\u6599\u91d1\u9010\u6d41\u5df2\u662f\u81ea\u5df1 \u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u539f\u6765\u662f\u4f60\u548c\u5b69\u513f\u5f00\u8fd9\u73a9\u7b11\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u653e\u4e0b\u4e86\u5fc3\u4e0a\u7684\u77f3\u5934\uff0c\u5927\u559c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u679c\u7136\u662f\u4f60\uff01\u201d\u8fde\u5fd9\u8dea\u4e0b\u78d5\u5934\u3002 \u3000\u3000\u9752\u888d\u602a\u5ba2\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6d77\u5929\uff0c\u4f60\u5f88\u4e0d\u9519\u5440\uff0c\u529f\u592b\u7684\u786e\u662f\u957f\u8fdb\u4e86\u8bb8\u591a\u4e86\u3002\u201d\u4e00\u62b9\u8138\u5b54\uff0c\u9664 \u4e0b\u4e86\u4eba\u76ae\u9762\u5177\uff0c\u9732\u51fa\u5e90\u5c71\u771f\u76f8\uff0c\u679c\u7136\u662f\u6c5f\u6d77\u5929\u7684\u5e08\u7236\u91d1\u4e16\u9057\u3002\u91d1\u4e16\u9057\u5e74\u7eaa\u5df2\u7ecf\u516d\u5341\u591a\u5c81\uff0c\u4f46 \u56e0\u5185\u529f\u6df1\u6e5b\uff0c\u9a7b\u989c\u6709\u672f\uff0c\u671b\u4e4b\u4ecd\u4f3c\u56db\u5341\u591a\u5c81\u7684\u5112\u751f\u3002\u8001\u4e00\u8f88\u89c1\u8fc7\u91d1\u4e16\u9057\u7684\u4eba\u5168\u90fd\u8ba4\u5f97\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u5927\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u662f\u8001\u7cca\u6d82\u4e86\uff0c\u65e9\u5e94\u8be5\u60f3\u5230\u662f\u4f60\u7684\u3002\u4f46\u60f3\u4e0d\u5230\u4f60\u8fd9\u559c\u6b22\u5f00\u73a9\u7b11\u7684\u813e \u6c14\u4ecd\u662f\u548c\u5f53\u5e74\u4e00\u6a21\u4e00\u6837\uff0c\u4e1d\u6beb\u672a\u6539\u3002\u600e\u4e48\u548c\u5f92\u5f1f\u3001\u513f\u5b50\u4e5f\u5f00\u8d77\u73a9\u7b11\u6765\u4e86\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8fd9\u6837\u8bd5\u4e00\u8bd5\u4ed6\u4eec\uff0c\u7109\u80fd\u77e5\u9053\u4ed6\u4eec\u80cc\u4e86\u6211\u6709\u6ca1\u6709\u5077\u61d2\u3002\u54fc\uff0c\u8bf4\u8d77\u6765 \u6211\u8fd8\u5f97\u602a\u4f60\u5462\uff01\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u81ea\u5df1\u6559\u8bad\u5f92\u5f1f\uff0c\u600e\u4e48\u602a\u8d77\u6211\u6765\u4e86\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u4eec\u505a\u957f\u8f88\u7684\u628a\u4ed6\u4eec\u6367\u6210\u4e86\u5929\u4e0b\u7b2c\u4e00\uff0c\u6211\u82e5\u4e0d\u632b\u6298\u632b\u6298\u4ed6\u4eec\uff0c\u5c82\u4e0d\u662f\u8981\u52a9 \u957f\u4ed6\u4eec\u7684\u9a84\u6c14\u4e86\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u6709\u8fd9\u6837\u7684\u597d\u5f92\u5f1f\uff0c\u96be\u9053\u8fd8\u4e0d\u6ee1\u610f\u4e48\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u5bf9\u6d77\u5929\u65e0\u8bdd\u53ef\u8bf4\uff0c\u4ed6\u7684\u529f\u592b\u7ec3\u5f97\u4e0d\u9519\u8fd8\u5728\u5176\u4e0a\uff0c\u96be\u5f97\u7684\u662f\u4ed6\u8fd9\u4e00\u4efd\u8c26 \u865a\u3002\u9010\u6d41\uff0c\u4f60\u6bd4\u8d77\u5e08\u5144\u6765\u53ef\u5c31\u5dee\u5f97\u8fdc\uff0c\u6b66\u529f\u56fa\u7136\u4e0d\u53ca\u5e08\u5144\u6c89\u7a33\uff0c\u6db5\u517b\u66f4\u662f\u4e0d\u53ca\u5e05\u5144\u3002\u4f60\u5e94\u8be5 \u597d\u597d\u7684\u5411\u5e08\u5144\u5b66\u5b66\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u8fd9\u53ef\u5c31\u6709\u70b9\u4e0d\u516c\u5e73\u4e86\u3002\u4ee4\u90ce\u7684\u529f\u529b\u867d\u7136\u4e0d\u53ca\u5e08\u5144\uff0c\u4f46\u4ed6\u81ea\u521b\u7684 \u65b0\u62db\uff0c\u5374\u662f\u7cbe\u5999\u7edd\u4f26\uff0c\u4eba\u6240\u96be\u80fd\uff01\u529f\u592b\u4e0d\u53ca\u5e08\u5144\uff0c\u8fd9\u4e5f\u662f\u5e74\u7eaa\u8fd8\u8f7b\u7684\u7f18\u6545\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5e08\u5f1f\u7684\u806a\u660e\u6211\u662f\u671b\u5c18\u83ab\u53ca\u3002\u82e5\u4e0d\u662f\u4ed6\u53eb\u51fa\u6765\uff0c\u6211\u8fd8\u4e0d\u77e5\u9053\u662f\u4f60\u8001\u4eba \u5bb6\u5462\u3002\u201d\u5176\u5b9e\u6c5f\u6d77\u5929\u4e5f\u65e9\u5df2\u6000\u7591\u9752\u888d\u602a\u5ba2\u4e43\u662f\u5e08\u7236\u7684\u4e86\u3002\u4e0d\u8fc7\u9996\u5148\u8bc6\u7834\u91d1\u4e16\u9057\u7684\u5374\u7684\u786e\u662f\u91d1 \u9010\u6d41\u3002 \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u53ef\u60dc\u4ed6\u7684\u806a\u660e\u5374\u4e0d\u7528\u5728\u6b63\u9053\u4e0a\uff0c\u6d77\u5929\uff0c\u4f60\u4e5f\u7ed9\u4ed6\u9a97\u8fc7\u4e86\u3002\u4f60\u4ee5\u4e3a\u4ed6\u662f\u4ece\u6211 \u7684\u6b66\u529f\u8bc6\u7834\u6211\u7684\u4e48\uff1f\u54fc\uff0c\u4ed6\u662f\u62ff\u59ec\u6653\u98ce\u6559\u4ed6\u7684\u90a3\u5957\u672c\u9886\uff0c\u5728\u6211\u7684\u8eab\u4e0a\u65bd\u5c55\u4e86\u3002\u6211\u7f5a\u4ed6\u8dcc\u4e00 \u8de4\u3002\u8fd8\u7b97\u4fbf\u5b9c\u4ed6\u5462\u3002\u201d \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u662f\u5728\u6b3a\u8eab\u8fdb\u6251\u4e4b\u9645\uff0c\u5728\u9752\u888d\u602a\u5ba2\u8eab\u4e0a\u5077\u4e86\u4e00\u6837\u4e1c\u897f\uff0c\u8fd9\u624d\u77e5\u9053\u662f\u4ed6\u7684\u7236\u4eb2 \u7684\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u539f\u6765\u4f60\u662f\u8f93\u4e86\u4e00\u62db\u7ed9\u513f\u5b50\uff0c\u5fc3\u91cc\u4e0d\u670d\u6c14\uff0c\u8fd9\u624d\u6559\u8bad \u4ed6\u7684\u3002\u54c8\u54c8\uff0c\u4f9d\u6211\u770b\u6765\uff0c\u5999\u624b\u7a7a\u7a7a\u7684\u672c\u4e8b\uff0c\u53ea\u8981\u7528\u5f97\u5176\u5f53\uff0c\u90a3\u4e5f\u662f\u597d\u5f97\u5f88\u5440\uff01\u201d \u3000\u3000\u516c\u5b59\u5b8f\u7b11\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u6709\u4f60\u56de\u6765\uff0c\u8fd9\u6b66\u529f\u5929\u4e0b\u7b2c\u4e00\u7684\u540d\u5934\u3002\u4ee4\u5f92\u4ee4\u90ce\u5e94\u5f53\u662f\u8981\u8ba9\u7ed9\u4f60 \u4e86\u3002\u201d\u4f17\u4eba\u542c\u4e86\u8fd9\u8bdd\u90fd\u7b11\u8d77\u6765\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5ffd\u5730\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u5929\u4e0b\u4e4b\u5927\uff0c\u4f55\u5904\u6ca1\u6709\u80fd\u4eba\uff1f\u6211\u521a\u624d\u8bf4\u7684\u8bdd\u53ef\u4e0d\u662f\u4e71\u8bf4\u7684\u3002\u4f60\u4eec \u4ee5\u4e3a\u6211\u5c31\u662f\u5929\u4e0b\u7b2c\u4e00\uff0c\u9519\u4e86\uff0c\u9519\u4e86\uff01\u201d \u3000\u3000\u4ef2\u957f\u7edf\u4ee5\u4e3a\u4ed6\u662f\u53c8\u5f00\u73a9\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u4f60\u7684\u813e\u6c14\u4e1d\u6beb\u672a\u6539\uff0c\u539f\u6765\u4e5f\u6709\u4e00\u70b9\u53d8\u4e86\u3002\u4ece \u6765\u4f60\u53ef\u6ca1\u6709\u8fd9\u6837\u8c26\u865a\u7684\u554a\uff0c\u8fd9\u662f\u8ddf\u4f60\u5f92\u5f1f\u5b66\u7684\u5417\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4ece\u524d\u6211\u662f\u4e0d\u8bc6\u5929\u4e0b\u4e4b\u5927\uff0c\u5982\u4eca\u624d\u77e5\u81ea\u5df1\u662f\u4e95\u5e95\u4e4b\u86d9\uff0c\u4e0d\u7792\u4f60\u8bf4\uff0c\u6628\u5929\u6211\u548c \u4eba\u5bb6\u6597\u5251\uff0c\u5c31\u683d\u4e86\u4e00\u4e2a\u8001\u5927\u7684\u7b4b\u6597\uff01\u201d \u3000\u3000\u4ef2\u957f\u7edf\u89c1\u4ed6\u795e\u60c5\u4e0d\u4f3c\u8bf4\u7b11\uff0c\u5927\u4e3a\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\u5929\u4e0b\u8fd8\u6709\u8c01\u80fd\u591f\u5728\u5251\u6cd5\u4e0a\u8d62\u5f97\u4f60 \u7684\u4e00\u62db\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\u4e48\uff1f\u9010\u6d41\uff0c\u628a\u4f60\u4ece\u6211\u8eab\u4e0a\u5077\u4e86\u53bb\u7684\u5bd2\u7389\u6212\u6307\u62ff\u51fa\u6765\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u6ee1\u9762\u901a\u7ea2\u5730\u62ff\u51fa\u4e86\u5bd2\u7389\u6212\u6307\uff0c\u91d1\u4e16\u9057\u63a5\u4e86\u8fc7\u6765\uff0c\u6307\u7ed9\u4ef2\u957f\u7edf\u770b\u9053\uff1a\u201c\u4f60\u4eec\u4ed4\u7ec6\u770b \u770b\uff0c\u6212\u6307\u4e0a\u662f\u4e0d\u662f\u6709\u4e00\u6761\u88c2\u75d5\uff1f\u201d\u516c\u5b59\u5b8f\u662f\u4e2a\u5251\u672f\u5927\u884c\u5bb6\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u53ef \u662f\u5251\u75d5\u4e48\u3002\u201d\u6b63\u662f\uff1a \u3000\u3000\u6d77\u5916\u5f02\u4eba\u5c65\u4e2d\u571f\uff0c\u5343\u5e74\u7edd\u5b66\u653e\u5149\u8292\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u56db\u5341\u516b\u56de\u3000\u8be7\u89c1\u5251\u75d5\u7559\u78a7\u7389\u3000\u4e3a\u5b8c\u5fc3\u613f\u8bbf\u540c\u95e8 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u56db\u5341\u516b\u56de\u3000\u8be7\u89c1\u5251\u75d5\u7559\u78a7\u7389\u3000\u4e3a\u5b8c\u5fc3\u613f\u8bbf\u540c\u95e8 \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4e0d\u9519\u3002\u5f53\u65f6\u6211\u7528\u5f39\u6307\u795e\u901a\u7684\u529f\u592b\u5f39\u5f00\u5bf9\u65b9\u7684\u5251\uff0c\u4ed6\u7684\u5251\u867d\u7136\u8131\u624b\uff0c\u53ef\u6211\u8fd9 \u679a\u5bd2\u7389\u6212\u6307\u4e5f\u7559\u4e0b\u4e86\u5251\u75d5\u4e86\u3002\u4e5f\u5e78\u4e8f\u6211\u662f\u6234\u7740\u8fd9\u679a\u6212\u6307\uff0c\u5426\u5219\u6027\u547d\u867d\u53ef\u65e0\u4f18\uff0c\u4e00\u6839\u6307\u5934\u5374\u6050 \u6015\u662f\u4fdd\u4e0d\u4f4f\u4e86\uff01\u201d \u3000\u3000\u4f17\u4eba\u542c\u4e86\u91d1\u4e16\u9057\u7684\u8bdd\uff0c\u65e0\u4e0d\u9a87\u7136\uff0c\u5c24\u5176\u662f\u77e5\u9053\u201c\u5bd2\u7389\u201d\u6765\u5386\u7684\u51e0\u4f4d\u8001\u524d\u8f88\uff0c\u66f4\u662f\u5927\u60ca\u5931 \u8272\uff01 \u3000\u3000\u201c\u5bd2\u7389\u201d\u4e43\u662f\u4e00\u79cd\u53ef\u4ee5\u9632\u8eab\u7684\u5b9d\u8d1d\uff0c\u91d1\u4e16\u9057\u6240\u5f97\u7684\u4e54\u5317\u6e9f\u201c\u4e09\u5b9d\u201d\u4e4b\u4e2d\uff0c\u6709\u4e00\u526f\u5f13\u7bad\u5c31 \u662f\u201c\u5bd2\u7389\u201d\u6240\u5236\uff0c\u540e\u6765\u91d1\u4e16\u9057\u628a\u90a3\u5f20\u7389\u5f13\u6253\u6210\u4e00\u4ef6\u7389\u7532\uff0c\u9001\u7ed9\u4e86\u6c5f\u6d77\u5929\u7684\u59bb\u5b50\u8c37\u4e2d\u83b2\u3002\u4e09\u679d \u5f13\u7bad\u5219\u6253\u6210\u4e86\u4e09\u679a\u5bd2\u7389\u6212\u6307\uff0c\u4e00\u679a\u7ed9\u5389\u5357\u661f\uff0c\u4e00\u679a\u7ed9\u91d1\u9010\u6d41\uff0c\u5269\u4e0b\u7684\u4e00\u679a\u7559\u7ed9\u81ea\u5df1\uff0c\u5bd2\u7389\u575a \u786c\u65e0\u6bd4\uff0c\u4efb\u4f55\u5229\u5668\u90fd\u4e0d\u80fd\u635f\u4f24\u3002\u5982\u4eca\u5c45\u7136\u4f1a\u7559\u4e0b\u5251\u75d5\uff0c\u53ef\u77e5\u90a3\u4eba\u4f7f\u7684\u4e0d\u4f46\u662f\u5b9d\u5251\uff0c\u800c\u4e14\u529f\u529b \u4e4b\u6df1\uff0c\u5373\u4f7f\u6bd4\u4e0d\u4e0a\u91d1\u4e16\u9057\uff0c\u4e5f\u662f\u5dee\u4e0d\u591a\u7684\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u7684\u201c\u5f39\u6307\u795e\u901a\u201d\u3002\u529f\u592b\u4e43\u662f\u72ec\u6b65\u5929\u4e0b\u7684\u7edd\u6280\uff0c\u4ed6\u5f39\u5f97\u5bf9\u65b9\u7684\u957f\u5251\u8131\u624b\uff0c\u5018\u82e5\u662f\u6b63 \u5f0f\u6bd4\u6b66\u7684\u8bdd\uff0c\u5f53\u7136\u662f\u4ed6\u8d62\u4e86\u3002\u4f46\u5047\u5982\u4ed6\u6ca1\u6709\u6234\u8fd9\u679a\u6212\u6307\uff0c\u771f\u7684\u7ed9\u524a\u4e86\u4e00\u6839\u6307\u5934\u7684\u8bdd\uff0c\u4e00\u4e2a\u5175 \u5668\u8131\u624b\uff0c\u4e00\u4e2a\u53d7\u4e86\u4f24\uff0c\u90a3\u5c31\u53ea\u80fd\u7b97\u662f\u5e73\u624b\u4e86\u3002\u4f46\u65e0\u8bba\u5982\u4f55\uff0c\u91d1\u4e16\u9057\u8bf4\u4ed6\u81ea\u5df1\u5728\u5251\u6cd5\u4e0a\u8f93\u4e86\u4e00 \u62db\uff0c\u8fd9\u8bdd\u662f\u5e76\u6ca1\u6709\u8bf4\u9519\u7684\u3002 \u3000\u3000\u4f17\u4eba\u5927\u60ca\u4e4b\u4e0b\uff0c\u5f53\u7136\u514d\u4e0d\u4e86\u7eb7\u7eb7\u95ee\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u73b0\u5728\u54ea\u91cc\uff1f\u201d\u8fde\u6c5f\u6d77\u5929\u4e5f\u4e0d\u7981\u60ca\u7591 \u4e0d\u5b9a\uff0c\u95ee\u9053\uff1a\u201c\u5e08\u7236\u56e0\u4f55\u548c\u6b64\u4eba\u52a8\u624b\uff0c\u4ed6\u662f\u54b1\u4eec\u7684\u654c\u4eba\u4e48\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u7684\u6765\u5386\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u6211\u77e5\u9053\u4ed6\u4eec\u662f\u51b2\u7740\u4f60\u6765\u7684\u3002\u6362\u8a00\u4e4b\u4e5f\u5c31\u662f\u4f60\u7684 \u654c\u4eba\u4e86\u3002\u4ed6\u4eec\u82e5\u77e5\u9053\u4f60\u5728\u8fd9\u91cc\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u627e\u5230\u8fd9\u91cc\u6765\u5462\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u8bf4\u7684\u662f\u201c\u8fd9\u4e9b\u4eba\u201d\uff0c\u663e\u7136\u78b0\u4e0a\u7684\u4e0d\u6b62\u4e00\u4e2a\u9ad8\u624b\uff0c\u4f17\u4eba\u542c\u4e86\uff0c\u66f4\u4e3a\u60ca\u8be7\uff01 \u3000\u3000\u91d1\u4e16\u9057\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u55a7\u95f9\u4e4b\u58f0\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d\u201c\u4f60\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\u3002\u201d\u201c\u6211\u4eec\u90fd \u4e0d\u8ba4\u8bc6\u9601\u4e0b\uff0c\u9601\u4e0b\u5c31\u662f\u8981\u627e\u6c5f\u5927\u4fa0\uff0c\u4e5f\u5f97\u7b49\u5f85\u6211\u4eec\u901a\u62a5\u3002\u201d\u770b\u6765\u5916\u9762\u662f\u6709\u964c\u751f\u4eba\u8981\u95ef\u8fdb\u6765\uff0c \u800c\u4e14\u6b63\u662f\u201c\u51b2\u7740\u201d\u6c5f\u6d77\u5929\u6765\u7684\u3002\u770b\u5b88\u4e0d\u8ba9\u4ed6\u8fdb\u6765\uff0c\u6545\u800c\u5435\u8d77\u6765\u4e86\u3002\u4f46\u56e0\u4e3a\u770b\u5b88\u4eba\u591a\uff0c\u53ea\u542c\u5f97 \u559d\u95ee\u4e4b\u58f0\uff0c\u6765\u4eba\u7684\u8bdd\u8bed\u5374\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u54fc\u4e86\u4e00\u58f0\uff0c\u6012\u9053\uff1a\u201c\u679c\u7136\u771f\u7684\u627e\u4e0a\u95e8\u6765\u4e86\uff0c\u54b1\u4eec\u90fd\u51fa\u53bb\u770b\u770b\uff0c\u770b\u8fd9\u5c0f\u5b50\u957f\u7684\u662f\u4e09 \u5934\u8fd8\u662f\u516d\u81c2\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u4e5f\u4ee5\u4e3a\u8fd9\u4eba\u5c31\u662f\u5e08\u7236\u78b0\u4e0a\u7684\u4eba\uff0c\u65e2\u662f\u51b2\u7740\u81ea\u5df1\u800c\u6765\uff0c\u5f53\u7136\u662f\u5e94\u8be5\u4eb2\u81ea\u53bb\u4f1a\u4f1a\u4ed6 \u4e86\u3002\u4e8e\u662f\u6c5f\u6d77\u5929\u62a2\u5728\u90a3\u4eba\u4e4b\u524d\uff0c\u5148\u8dd1\u51fa\u53bb\uff0c\u521a\u5230\u95e8\u53e3\uff0c\u548c\u6765\u4eba\u78b0\u4e2a\u6b63\u7740\uff0c\u53ea\u89c1\u90a3\u4eba\u4e00\u62db\u201c\u7ae5 \u5b50\u62dc\u89c2\u97f3\u201d\u5f0f\uff0c\u5411\u6c5f\u6d77\u5929\u4f5c\u4e86\u4e00\u4e2a\u957f\u63d6\u3002\u7ad9\u5728\u6c5f\u6d77\u5929\u65c1\u8fb9\u7684\u4eba\uff0c\u767b\u65f6\u7acb\u8db3\u4e0d\u7a33\uff0c\u8dcc\u8dcc\u649e\u649e\u5730 \u5411\u4e24\u8fb9\u5206\u5f00\uff01 \u3000\u3000\u516c\u5b59\u5b8f\u8ddf\u5728\u540e\u9762\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u53eb\u5316\uff0c\u8fd9\u662f\u4f5b\u95e8\u6b63\u5b97\u7684\u822c\u82e5\u638c\u529b\uff01\u201d \u3000\u3000\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff01\u516c\u5b59\u8001\u5f1f\uff0c\u4f60\u7684\u773c\u529b\u59d4\u5b9e\u4e0d\u5dee\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8fd8\u4e86\u4e00\u63d6\uff0c\u53ea\u89c1\u90a3\u4eba\u80a9\u5934\u5fae\u5fae\u4e00\u8038\uff0c\u6c5f\u6d77\u5929\u7a7f\u7684\u9752\u5e03\u957f\u886b\uff0c\u5374\u50cf\u88ab\u6625\u98ce\u5439\u7ec9\u4e86\u7684 \u6e56\u6c34\u4f3c\u7684\uff0c\u8361\u8d77\u4e86\u4e00\u5708\u5708\u7684\u6ce2\u7eb9\u3002\u770b\u6765\u8fd8\u662f\u6c5f\u6d77\u5929\u7684\u529f\u529b\u8f83\u9ad8\uff0c\u4f46\u5728\u8fd9\u822c\u82e5\u638c\u7684\u8f83\u91cf\u4e0a\uff0c\u5374 \u662f\u6280\u900a\u4e00\u7b79\u4e86\u3002 \u3000\u3000\u516c\u5b59\u5b8f\u4e0d\u8ba4\u5f97\u8fd9\u4eba\uff0c\u5fc3\u91cc\u6b63\u5728\u6709\u70b9\u5947\u602a\uff1a\u201c\u6c5f\u6d77\u5929\u7684\u654c\u4eba\u627e\u4e0a\u95e8\u6765\uff0c\u8fd9\u8001\u53eb\u5316\u4e3a\u4ec0\u4e48\u8fd8 \u6709\u5174\u8da3\u8bf4\u7b11\uff1f\u4e1d\u6beb\u4e5f\u4e0d\u62c5\u5fc3\uff1f\u822c\u82e5\u638c\u529b\u80fd\u4f24\u5947\u7ecf\u516b\u8109\uff0c\u6c5f\u5927\u4fa0\u82e5\u53d7\u4e86\u4f24\uff0c\u8fd9\u53ef\u4e0d\u662f\u5f53\u800d\u7684 \u554a\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u89c1\u6c5f\u6d77\u5929\u548c\u90a3\u4eba\u53cc\u624b\u7d27\u7d27\u76f8\u63e1\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53f6\u5927\u54e5\uff0c\u4f60\u7684\u5927\u4e58\u822c\u82e5\u638c\u679c \u7136\u662f\u7ec3\u5f97\u529f\u5fb7\u5b8c\u6ee1\u4e86\uff0c\u5c0f\u5f1f\u81ea\u6127\u4e0d\u5982\u3002\u4f69\u670d\uff0c\u4f69\u670d\uff01\u201d\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u4e0d\u89c1\uff0c\u4f60\u7684\u529f\u529b \u4e5f\u6bd4\u5f53\u5e74\u66f4\u9ad8\u4e86\u554a\u3002\u6211\u65e0\u8bba\u600e\u6837\u7ec3\uff0c\u603b\u662f\u80dc\u4e0d\u8fc7\u4f60\uff0c\u8fd9\u56de\u6211\u53ef\u771f\u662f\u8f93\u5f97\u5fc3\u670d\u53e3\u670d\u4e86\uff01\u6155\u534e \u5462\uff1f\u542c\u8bf4\u4f60\u7acb\u4e86\u4ed6\u505a\u638c\u95e8\u5f1f\u5b50\uff0c\u6211\u8fd8\u672a\u66fe\u66ff\u4ed6\u8c22\u5e08\u5462\uff1f\u54c8\u54c8\uff01\u201d \u3000\u3000\u516c\u5b59\u5b8f\u8fd9\u624d\u77e5\u9053\u6b64\u4eba\u662f\u53cb\u975e\u654c\uff0c\u4f17\u4eba\u4e5f\u90fd\u677e\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4eba\u4e43\u662f\u6c5f\u6d77\u5929\u7684\u59bb\u8205\u53f6\u51b2\u9704\uff0c\u6c5f\u6d77\u5929\u7684\u638c\u95e8\u5927\u5f1f\u5b50\u53f6\u6155\u534e\u5c31\u662f\u4ed6\u7684\u513f\u5b50\u3002\u53f6\u51b2\u9704 \u8db3\u8ff9\u7f55\u81f3\u4e2d\u539f\uff0c\u800c\u4e14\u5728\u4e8c\u5341\u591a\u5c81\u5c31\u5230\u6d77\u5916\u53bb\u4e86\uff0c\u6240\u4ee5\u4e2d\u539f\u7684\u6b66\u6797\u4eba\u7269\uff0c\u8ba4\u8bc6\u4ed6\u7684\u5be5\u5be5\u65e0\u51e0\u3002 \u4e0d\u8fc7\u5927\u5bb6\u867d\u4e0d\u8ba4\u8bc6\u4ed6\uff0c\u5982\u4e5f\u77e5\u9053\u6c5f\u6d77\u5929\u6709\u8fd9\u95e8\u4eb2\u621a\uff0c\u4e00\u7ecf\u4ecb\u7ecd\uff0c\u5927\u5bb6\u4e5f\u5c31\u4e00\u89c1\u5982\u6545\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u5982\u4f55\uff0c\u6211\u8bf4\u6b66\u529f\u6ca1\u6709\u5929\u4e0b\u7b2c\u4e00\uff0c\u8fd9\u4e0d\u53c8\u662f\u4e00\u4e2a\u8bc1\u660e\u4e86\u5417\uff1f\u5404\u6709\u5404\u7684\u4e13 \u957f\uff0c\u5c82\u80fd\u6bcf\u4e00\u6837\u529f\u592b\u90fd\u662f\u767b\u5cf0\u9020\u6781\uff1f\u6bd4\u5982\u822c\u82e5\u638c\u6d77\u5929\u5c31\u6bd4\u4e0d\u4e0a\u51b2\u9704\uff0c\u8bba\u5251\u6cd5\u6211\u4e5f\u672a\u5fc5\u5c31\u80dc\u5f97 \u8fc7\u6628\u65e5\u6240\u89c1\u7684\u90a3\u51e0\u4e2a\u540e\u751f\u5c0f\u5b50\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u4e16\u4f2f\u592a\u5938\u8d5e\u6211\u4e86\uff0c\u6211\u548c\u6c5f\u5144\u76f8\u6bd4\u8fd8\u5dee\u5f97\u8fdc\u5462\u3002\u4e0d\u8fc7\uff0c\u90a3\u51e0\u4e2a\u4eba\u7684\u5251\u638c\u548c\u6697 \u5668\u529f\u592b\uff0c\u5374\u7684\u786e\u662f\u4e16\u6240\u7f55\u89c1\uff0c\u6628\u65e5\u82e5\u4e0d\u662f\u4e16\u4f2f\u5728\u65c1\uff0c\u5c0f\u4f84\u8fd9\u4e2a\u4e8f\u53ea\u6015\u662f\u8981\u5403\u5b9a\u7684\u4e86\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u4f60\u4eec\u8bf4\u7684\u6628\u65e5\u4e4b\u4e8b\uff0c\u7a76\u7adf\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u91d1\u5927\u4fa0\u5df2\u7ecf\u544a\u8bc9\u4f60\u4eec\u4e86\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u521a\u521a\u8bf4\u5230\u8f93\u4e86\u4e00\u62db\u7684\u4e8b\u3002\u201d \u3000\u3000\u56de\u5230\u539f\u6765\u7684\u8bdd\u9898\uff0c\u6bcf\u4e00\u4e2a\u4eba\u90fd\u662f\u597d\u5947\u4e4b\u5fc3\u5927\u8d77\uff0c\u60f3\u8981\u77e5\u9053\u8d62\u4e86\u91d1\u4e16\u9057\u4e00\u62db\u7684\u4eba\u662f\u8c01\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u5979\u4eec\u6765\u4e86\uff0c\u6d77\u5929\uff0c\u8fd8\u662f\u8ba9\u4f60\u7684\u59bb\u5b50\u544a\u8bc9\u4f60\u5427\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u597d\u51e0\u4e2a\u4eba\u5174\u51b2\u51b2\u5730\u8dd1\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u8fd9\u53ef\u662f\u5927\u559c\u4e8b\u554a\uff0c\u9099\u5c71\u6d3e\u4e24\u4ee3\u638c\u95e8\u4eba \u90fd\u6765\u4e86\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5927\u559c\u9053\uff1a\u201c\u539f\u6765\u5e08\u6bcd\u4e5f\u4e00\u540c\u56de\u6765\u4e86\u3002\u201d\u91d1\u4e16\u9057\u7684\u59bb\u5b50\u8c37\u4e4b\u534e\u662f\u9099\u5c71\u6d3e\u7684\u524d\u4efb\u638c \u95e8\u3002\u56e0\u6b64\u6c5f\u6d77\u5929\u542c\u5f97\u201c\u9099\u5c71\u6d3e\u4e24\u4ee3\u638c\u95e8\u9a7e\u4e34\u201d\u7684\u7980\u62a5\uff0c\u4fbf\u77e5\u662f\u5e08\u6bcd\u548c\u59bb\u5b50\u4e00\u540c\u6765\u5230\u3002 \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u679c\u7136\u4fbf\u770b\u89c1\u8c37\u4e4b\u534e\u4e0e\u8c37\u4e2d\u83b2\u4e00\u540c\u8fdb\u6765\uff0c\u800c\u4e14\u548c\u5979\u4eec\u4e00\u8d77\u7684\u8fd8\u6709\u53f6\u51b2\u9704\u7684\u59bb\u5b50 \u6b27\u9633\u5a49\u548c\u9099\u5c71\u6d3e\u56db\u5927\u5f1f\u5b50\u4e4b\u9996\u7684\u7518\u6587\u9f99\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e0b\u5b50\u53ef\u771f\u70ed\u95f9\u4e86\uff0c\u4f60\u4eec\u51e0\u5bb6\u4eba\u90fd\u56e2\u805a\u5566\uff01\u201d \u3000\u3000\u6c5f\u6d77\u5929\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u906d\u9047\u7684\u90a3\u4e9b\u9ad8\u624b\uff0c\u6562\u60c5\u5c31\u662f\u5728\u6211\u7684\u5bb6\u4e2d\u78b0\u4e0a\u7684 \u5427\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u4e9b\u4eba\u6b63\u662f\u6e05\u5ef7\u6d3e\u9063\u7684\u9ad8\u624b\uff0c\u6765\u5bf9\u4ed8\u4f60\u4eec\u592b\u5987\u7684\u3002\u201d \u3000\u3000\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u6628\u5929\u65e9\u4e0a\uff0c\u7518\u5e08\u5144\u548c\u4e09\u4f4d\u540c\u95e8\u4ece\u9099\u5c71\u5306\u5306\u8d76\u6765\uff0c\u8bf4\u662f\u542c\u5230\u98ce\u58f0\uff0c\u6e05\u5ef7\u5c06\u6709 \u6240\u4e0d\u5229\u4e8e\u6211\u3002\u679c\u7136\u665a\u4e0a\u90a3\u4e9b\u4eba\u5c31\u6765\u4e86\u3002\u597d\u5728\u6211\u4eec\u65e9\u6709\u9632\u5907\uff0c\u5426\u5219\u6050\u6015\u66f4\u662f\u4e0d\u582a\u8bbe\u60f3\u3002 \u3000\u3000\u201c\u6628\u665a\u90a3\u4e9b\u4eba\u4e5f\u4e0d\u77e5\u662f\u4ece\u54ea\u91cc\u94bb\u51fa\u6765\u4e86\uff0c\u4e00\u5171\u6765\u4e86\u4e03\u4e2a\uff0c\u6709\u7537\u6709\u5973\uff0c\u6709\u8001\u6709\u5c11\uff0c\u7adf\u7136\u662f \u4e2a\u4e2a\u6b66\u827a\u9ad8\u5f3a\uff01\u60ed\u6127\u5f97\u5f88\uff0c\u5bf9\u65b9\u5171\u6709\u4e03\u4eba\u4e4b\u591a\uff0c\u4efb\u4f55\u4e00\u4e2a\u4eba\u7684\u6765\u5386\u6211\u4eec\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u201c\u4e00\u573a\u6076\u6218\uff0c\u767d\u5e08\u5144\u3001\u8def\u5e08\u5144\u3001\u674e\u5e08\u5144\u90fd\u53d7\u4e86\u91cd\u4f24\uff0c\u6211\u4e0e\u7518\u5e08\u5144\u4fa5\u5e78\u672a\u4f24\uff0c\u4f46\u4ea6\u5df2\u529b\u7aed \u7b4b\u75b2\uff0c\u4e0d\u582a\u518d\u6218\uff0c\u90a3\u4e9b\u4eba\u628a\u6211\u548c\u7518\u5e08\u5144\u56e2\u56e2\u56f4\u4f4f\uff0c\u8981\u903c\u6211\u4eec\u6295\u964d\u3002\u5f53\u65f6\u6211\u5df2\u6253\u7b97\u81ea\u65ad\u7ecf\u8109\uff0c \u5b81\u6b7b\u4e0d\u53d7\u654c\u4eba\u4e4b\u8fb1\u7684\u4e86\uff0c\u60f3\u4e0d\u5230\u5c31\u5728\u8fd9\u5371\u9669\u7684\u5173\u5934\uff0c\u5e08\u7236\u5e08\u6bcd\u548c\u5927\u54e5\u5927\u5ac2\u5e78\u4e8f\u540c\u65f6\u6765\u5230\uff0c\u6211 \u4eec\u8fd9\u624d\u80fd\u53cd\u8d25\u4e3a\u80dc\u7684\uff01\u201d \u3000\u3000\u4f17\u4eba\u542c\u4e86\uff0c\u65e0\u4e0d\u9a87\u7136\u3002\u8981\u77e5\u9099\u5c71\u6d3e\u7684\u7518\u3001\u8def\u3001\u767d\u3001\u674e\u56db\u5927\u5f1f\u5b50\uff0c\u4e43\u662f\u201c\u6c5f\u5357\u5927\u4fa0\u201d\u7518\u51e4 \u6c60\u3001\u8def\u6c11\u77bb\u3001\u767d\u6cf0\u5b98\u3001\u674e\u6e90\u7684\u540e\u4eba\uff0c\u6bcf\u4e00\u4e2a\u90fd\u6709\u72ec\u95e8\u6b66\u529f\uff0c\u56db\u4e2a\u4eba\u52a0\u4e0a\u4e86\u6c5f\u6d77\u5929\u7684\u59bb\u5b50\u8c37\u4e2d \u83b2\uff0c\u7adf\u7136\u6253\u4e0d\u8fc7\u5bf9\u65b9\uff0c\u4e09\u4e2a\u4eba\u8fd8\u53d7\u4e86\u91cd\u4f24\uff0c\u5bf9\u65b9\u7684\u5389\u5bb3\u4e5f\u5c31\u53ef\u60f3\u800c\u77e5\u4e86\u3002 \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u8fd9\u4e5f\u5e76\u975e\u5de7\u9047\uff0c\u6211\u4eec\u662f\u5148\u5230\u4e86\u9099\u5c71\uff0c\u5f97\u77e5\u6d88\u606f\u7684\u3002\u201d \u3000\u3000\u539f\u6765\u91d1\u4e16\u9057\u5728\u6d77\u5916\u4f4f\u4e86\u4e8c\u5341\u4e00\u5e74\uff0c\u4e8b\u8fc7\u5883\u8fc1\uff0c\u5fc3\u4e0a\u7684\u521b\u75d5\u65e9\u5df2\u5e73\u590d\uff0c\u60f3\u8d77\u4e2d\u539f\u7684\u4e00\u73ed\u670b \u53cd\uff0c\u9042\u7ea6\u4e86\u53f6\u51b2\u9704\u592b\u5987\u4e00\u540c\u56de\u56fd\u3002\u53f6\u51b2\u9704\u672c\u662f\u9a6c\u8428\u513f\u56ed\u7684\u5927\u738b\u5b50\uff0c\u56e0\u4e3a\u8981\u8ba9\u4f4d\u7ed9\u5f1f\u5f1f\u800c\u907f\u5c45 \u6d77\u5916\u7684\u3002\u6b64\u65f6\u5df2\u7ecf\u8fc7\u4e86\u4e8c\u5341\u5e74\uff0c\u4ed6\u4ece\u53f6\u6155\u534e\u6258\u6d77\u5ba2\u5e26\u6765\u7684\u5bb6\u4fe1\u5f97\u77e5\uff0c\u5f1f\u5f1f\u90fd\u65e9\u5df2\u4f20\u4f4d\u7ed9\u4f84\u513f \u4e86\uff0c\u56de\u53bb\u81ea\u662f\u65e0\u59a8\uff0c\u56e0\u6b64\u4e24\u5bb6\u4eba\u9042\u8054\u540c\u8fd4\u56fd\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5e08\u5f92\u4e24\u4ee3\u90fd\u66fe\u53d7\u8fc7\u5415\u56db\u5a18\uff08\u9099\u5c71\u6d3e\u7b2c\u4e8c\u4ee3\u638c\u95e8\uff09\u5927\u6069\uff0c\u4ed6\u7684\u59bb\u5b50\u8c37\u4e4b\u534e\u53c8\u662f\u5415\u56db \u5a18\u629a\u517b\u6210\u4eba\u7684\uff0c\u662f\u4ee5\u56de\u5230\u4e2d\u539f\uff0c\u7b2c\u4e00\u4ef6\u4e8b\u4fbf\u662f\u5230\u9099\u5c71\u796d\u626b\u5415\u56db\u5a18\u7684\u575f\u5893\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5148\u5230\u9099\u5c71\u7684\u53e6\u4e00\u4e2a\u539f\u56e0\u4e5f\u662f\u56e0\u4e3a\u8c37\u4e2d\u83b2\u4e43\u662f\u9099\u5c71\u6d3e\u638c\u95e8\u7684\u7f18\u6545\u3002\u4e00\u5e74\u4e4b\u4e2d\uff0c\u8c37\u4e2d \u83b2\u603b\u6709\u534a\u5348\u662f\u5728\u9099\u5c71\u7684\u3002\u6709\u65f6\u6c5f\u6d77\u5929\u966a\u7740\u5979\u6765\uff0c\u6709\u65f6\u662f\u5979\u81ea\u5df1\u6765\u3002\u4f46\u4e0d\u8bba\u662f\u5426\u89c1\u5f97\u7740\u6c5f\u6d77 \u5929\uff0c\u89c1\u7740\u4e86\u8c37\u4e2d\u83b2\uff0c\u4e5f\u5c31\u53ef\u4ee5\u77e5\u9053\u7231\u5f92\u7231\u5b50\u7684\u6d88\u606f\u4e86\u3002\u91d1\u4e16\u9057\u6700\u8bb0\u6302\u7684\u4e24\u4e2a\u4eba\uff0c\u5f53\u7136\u662f\u4ed6\u7684 \u5f92\u5f1f\u6c5f\u6d77\u5929\u4e0e\u513f\u5b50\u91d1\u9010\u6d41\u3002 \u3000\u3000\u5176\u65f6\u9099\u5c71\u6d3e\u4e0a\u4e00\u8f88\u7684\u4eba\u7269\uff0c\u5c1a\u6709\u767d\u82f1\u6770\u548c\u8def\u82f1\u8c6a\u4e8c\u4eba\u3002\u91d1\u4e16\u9057\u89c1\u4e86\u4ed6\u4eec\u4e8c\u4eba\uff0c\u65b9\u59cb\u5f97\u77e5 \u6e05\u5ef7\u5c06\u6709\u6240\u4e0d\u5229\u4e8e\u6c5f\u6d77\u5929\u7684\u6d88\u606f\uff0c\u9099\u5c71\u6d3e\u7684\u56db\u5927\u5f1f\u5b50\uff0c\u6628\u65e5\u521a\u521a\u8d74\u5f80\u6c5f\u5bb6\u8d74\u63f4\u3002 \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u5e78\u4e8f\u6211\u5148\u5230\u9099\u5c71\uff0c\u5f97\u5230\u4e86\u8fd9\u4e2a\u6d88\u606f\uff0c\u521a\u597d\u53ca\u65f6\u8d76\u4e0a\u4e86\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u6765\u5230\u4e4b\u65f6\uff0c\u542c\u5f97\u53ae\u6740\u4e4b\u58f0\uff0c\u6211\u5c1a\u4e0d\u4ee5\u4e3a\u610f\uff0c\u54ea\u77e5\u4e00\u4e0a\u53bb\u5c31\u5403 \u4e8f\u3002\u201d \u3000\u3000\u539f\u6765\u53f6\u51b2\u9704\u5728\u6d77\u5916\u4e8c\u5341\u5e74\uff0c\u5df2\u7ecf\u7ec3\u6210\u4e86\u5927\u4e58\u822c\u82e5\u638c\uff0c\u56de\u5230\u4e2d\u539f\uff0c\u6b63\u60f3\u627e\u4e2a\u673a\u4f1a\u8bd5\u8bd5\u3002\u4ed6 \u6765\u5230\u6c5f\u5bb6\u4e4b\u65f6\uff0c\u6b63\u662f\u4ed6\u7684\u59b9\u59b9\u8c37\u4e2d\u83b2\u9677\u4e8e\u82e6\u6597\u4e4b\u9645\u3002\u4ed6\u867d\u7136\u77e5\u9053\u6765\u4eba\u4e86\u5f97\uff0c\u4f46\u4ecd\u7136\u4e0d\u4ee5\u4e3a \u610f\u3002\u4ee5\u4e3a\u6e05\u5ef7\u5dee\u9063\u5f97\u52a8\u7684\u4eba\uff0c\u672c\u9886\u518d\u9ad8\uff0c\u4e5f\u662f\u6709\u9650\u3002\u91d1\u4e16\u9057\u65e9\u5df2\u662f\u6253\u904d\u5929\u4e0b\u65e0\u654c\u7684\u7b2c\u4e00\u4eba\uff0c \u53f6\u51b2\u9704\u4ee5\u4e3a\u201c\u5272\u9e21\u7109\u7528\u725b\u5200\u201d\uff0c\u56e0\u6b64\u5c31\u8bf7\u91d1\u4e16\u9057\u66ff\u4ed6\u63a0\u9635\uff0c\u72ec\u81ea\u4e0a\u524d\uff0c\u51c6\u5907\u628a\u90a3\u4e9b\u4eba\u6253\u4e2a\u843d \u82b1\u6d41\u6c34\u3002 \u3000\u3000\u91d1\u4e16\u9057\u521d\u65f6\u4e5f\u662f\u8fd9\u6837\u60f3\uff0c\u770b\u4e86\u51e0\u62db\uff0c\u65b9\u77e5\u4e0d\u5bf9\u3002\u8fde\u5fd9\u51fa\u624b\uff0c\u4e1a\u5df2\u8fdf\u4e86\u534a\u6b65\u3002 \u3000\u3000\u53f6\u51b2\u9704\u82e6\u7b11\u9053\uff1a\u201c\u5bf9\u65b9\u4e03\u4e2a\u4eba\u4f9d\u5317\u6597\u4e03\u661f\u4e4b\u52bf\uff0c\u5217\u6210\u9635\u5f62\uff0c\u6211\u53ea\u9053\u4e00\u4e0a\u53bb\u5c31\u53ef\u4ee5\u628a\u5bf9\u65b9 \u6253\u4e2a\u843d\u82b1\u6d41\u6c34\uff0c\u54ea\u77e5\u9053\u5bf9\u65b9\u7684\u9635\u811a\u4e1d\u6beb\u4e0d\u4e71\uff0c\u53ea\u5206\u51fa\u4e00\u4e2a\u4eba\u6765\u5bf9\u4ed8\u6211\uff0c\u90a3\u4e2a\u4eba\u662f\u4e2d\u5e74\u6c49\u5b50\uff0c \u5e74\u7eaa\u548c\u6211\u5dee\u4e0d\u591a\uff0c\u6211\u548c\u4ed6\u7167\u9762\u4e09\u4e2a\u8fde\u73af\u6025\u62db\uff0c\u4e0d\u4f46\u5360\u4e0d\u5230\u4fbf\u5b9c\uff0c\u53cd\u800c\u5403\u4e86\u70b9\u4e8f\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u53f6\u5927\u54e5\uff0c\u4f60\u7684\u822c\u82e5\u638c\u7528\u4e86\u6ca1\u6709\uff1f\u201d\u5fc3\u91cc\u597d\u751f\u7eb3\u7f55\uff1a\u201c\u53f6\u51b2\u9704\u7684\u822c\u82e5\u638c\u529b\uff0c \u5c1a\u5728\u5927\u5e08\u5144\u4e4b\u4e0a\uff0c\u5373\u4f7f\u662f\u725f\u5b97\u6d9b\u4e5f\u6321\u4e0d\u8d77\u4ed6\u7684\u4e00\u638c\uff0c\u4f55\u4ee5\u53cd\u800c\u4f1a\u5403\u4e86\u4e8f\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u5f53\u7136\u7528\u4e0a\u4e86\uff0c\u6211\u4e00\u51fa\u624b\u5c31\u662f\u822c\u82e5\u638c\u3002\u90a3\u4eba\u63a5\u4e86\u4e00\u638c\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u8eab\u5f62\u8fde \u6643\uff0c\u5374\u6ca1\u5012\u4e0b\u3002\u8ddf\u7740\u4e24\u62db\uff0c\u7adf\u662f\u5251\u638c\u517c\u65bd\uff0c\u8fc5\u5982\u66b4\u98ce\u9aa4\u96e8\u3002\u6211\u987e\u5f97\u5e94\u4ed8\u4ed6\u7684\u5251\uff0c\u987e\u4e0d\u4e86\u4ed6\u7684 \u638c\u6307\u529f\u592b\uff0c\u53ea\u89c9\u80c1\u4e0b\u4e00\u9ebb\uff0c\u5df2\u7ecf\u662f\u7740\u4e86\u4ed6\u7684\u9053\u513f\u3002\u5e78\u4e8f\u4f60\u7684\u7239\u7239\u8fc5\u901f\u628a\u6211\u63a8\u5f00\uff0c\u6211\u8fd9\u624d\u6ca1\u6709 \u53d7\u5230\u91cd\u4f24\u3002\u201d \u3000\u3000\u8bf4\u7f62\u63ed\u5f00\u8863\u88f3\uff0c\u53ea\u89c1\u80c1\u4e0b\u4e09\u4e2a\u7600\u9ed1\u7684\u4f24\u75d5\uff0c\u53f6\u51b2\u9704\u82e6\u7b11\u9053\uff1a\u201c\u5bf9\u65b9\u4ee5\u6307\u4ee3\u5251\uff0c\u6307\u6cd5\u4e4b\u7cbe \u5947\uff0c\u5b9e\u8db3\u6211\u5e73\u751f\u4ece\u6240\u672a\u89c1\uff01\u5e78\u4e8f\u7528\u7684\u4e0d\u662f\u771f\u5251\uff0c\u5426\u5219\u6211\u7684\u8eab\u4e0a\u5df2\u7ecf\u7a7f\u4e86\u4e09\u4e2a\u7a9f\u7abf\u4e86\uff01\u201d \u3000\u3000\u4f17\u4eba\u770b\u4e86\u53f6\u51b2\u9704\u8eab\u4e0a\u7684\u4f24\u75d5\uff0c\u90fd\u662f\u77a0\u76ee\u7ed3\u820c\uff0c\u76f8\u987e\u5931\u8272\u3002 \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u51b2\u9704\uff0c\u4f60\u4e5f\u4e0d\u5fc5\u8fc7\u8c26\uff0c\u548c\u4f60\u5bf9\u654c\u90a3\u4eba\uff0c\u4e43\u662f\u4ed6\u4eec\u4e4b\u4e2d\u7684\u7b2c\u4e09\u540d\u9ad8\u624b\uff0c\u4ed6 \u63a5\u4e86\u4f60\u7684\u4e00\u638c\uff0c\u5176\u5b9e\u5df2\u662f\u76f8\u5f53\u4e25\u91cd\u7684\u5185\u4f24\uff0c\u4e0d\u8fc7\u4f60\u770b\u4e0d\u51fa\u7f62\u4e86\u3002\u4e5f\u5e78\u4e8f\u4ed6\u4eec\u4e4b\u4e2d\u6709\u4e00\u4e2a\u5df2\u7ecf \u53d7\u4f24\uff0c\u5426\u5219\u6211\u4eec\u592b\u5987\u8981\u7834\u4ed6\u4eec\u8fd9\u4e2a\u4e03\u661f\u9635\uff0c\u53ea\u6015\u8fd8\u672a\u5fc5\u80fd\u591f\u5462\uff01\u201d \u3000\u3000\u6df7\u6218\u7684\u53cc\u65b9\u90fd\u662f\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u91d1\u4e16\u9057\u4e00\u773c\u5c31\u80fd\u591f\u770b\u5f97\u51fa\u654c\u65b9\u5404\u4e2a\u4eba\u6b66\u529f\u7684\u9ad8\u4e0b\uff0c\u5728\u573a \u7684\u6b66\u5b66\u884c\u5bb6\uff0c\u90fd\u662f\u4e0d\u7531\u5f97\u6697\u6697\u4f69\u670d\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u867d\u7136\u7a0d\u5fae\u5403\u4e86\u70b9\u4e8f\uff0c\u4f46\u6b66\u529f\u5929\u4e0b\u7b2c \u4e00\u7684\u540d\u5934\uff0c\u7a76\u7adf\u8fd8\u662f\u975e\u4ed6\u83ab\u5c5e\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u5ffd\u5730\u53eb\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u201d\u516c\u5b59\u5b8f\u9053\uff1a\u201c\u8001\u53eb\u5316\uff0c\u4f60\u53ef\u60dc\u4ec0\u4e48\u3002\u201d\u4ef2\u957f\u7edf\u9053\uff1a \u201c\u8fd9\u4e00\u6218\u5b9a\u7136\u7cbe\u5f69\u4e4b\u6781\uff0c\u53ef\u60dc\u6211\u6ca1\u6709\u773c\u798f\u89c1\u5230\u3002\u91d1\u5927\u4fa0\uff0c\u4f60\u662f\u600e\u4e48\u53d6\u80dc\u7684\uff0c\u5feb\u8bf4\u7ed9\u6211\u4eec\u542c\u542c \u5427\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u5374\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8bf4\u6765\u60ed\u6127\uff0c\u6211\u4eec\u592b\u5987\u867d\u7136\u4fa5\u5e78\u83b7\u80dc\uff0c\u53ef\u662f\u5bf9\u4ed6\u4eec\u7684\u6b66\u529f\u6765 \u5386\uff0c\u5374\u662f\u5168\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u8fd9\u4e03\u4e2a\u4eba\u4f7f\u7528\u4e0d\u540c\u7684\u5175\u5668\uff0c\u6bcf\u4e2a\u4eba\u6709\u4ed6\u7684\u72ec\u5230\u4e4b\u5904\uff0c\u4e0d\u8fc7\u4ece\u4ed6\u4eec\u7684\u62db\u6570\u770b\u6765\uff0c\u5374\u4f3c\u4e4e\u662f \u5c5e\u4e8e\u540c\u4e00\u95e8\u6d3e\u7684\u3002\u4ed6\u4eec\u7684\u62db\u6570\u5947\u8be1\u7e41\u590d\uff0c\u4f46\u5176\u4e2d\u4ea6\u6709\u8109\u7edc\u53ef\u5bfb\uff0c\u90fd\u662f\u4ece\u5251\u6cd5\u4e2d\u53d8\u5316\u51fa\u6765\u7684\u3002 \u6b66\u529f\u6700\u5f3a\u7684\u4e5f\u662f\u4e24\u4e2a\u4f7f\u5251\u7684\u597d\u624b\uff0c\u6211\u4ee5\u6307\u4ee3\u5251\uff0c\u65bd\u5c55\u4e86\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u7adf\u4e5f\u6ca1\u5360\u5230\u4ed6\u4eec\u7684\u4fbf \u5b9c\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u4e0d\u613f\u5938\u8000\u81ea\u5df1\u7684\u6218\u7ee9\uff0c\u53ea\u662f\u7ea6\u7565\u8bf4\u4e86\u4e00\u4e2a\u5927\u6982\u3002\u542c\u7684\u4eba\u90fd\u4e0d\u6ee1\u610f\uff0c\u90fd\u8981\u53f6\u51b2\u9704\u52a0 \u4ee5\u8865\u5145\u3002 \u3000\u3000\u53f6\u51b2\u9704\u9053\uff1a\u201c\u6211\u7684\u6b66\u5b66\u9020\u8be3\u548c\u91d1\u5927\u4fa0\u76f8\u6bd4\u5dee\u5f97\u592a\u8fdc\uff0c\u5bf9\u4ed6\u4eec\u53cc\u65b9\u6240\u4f7f\u7684\u4e0a\u4e58\u6b66\u529f\uff0c\u5f53\u7136 \u770b\u5f97\u773c\u82b1\u7f2d\u4e71\uff0c\u60ed\u6127\u5f97\u5f88\uff0c\u5176\u4e2d\u7684\u5965\u5999\uff0c\u6211\u4e5f\u662f\u770b\u4e0d\u51fa\u6765\u3002\u201d \u3000\u3000\u4e0d\u8fc7\u53f6\u51b2\u9704\u8fd8\u662f\u7709\u98de\u8272\u821e\u5730\u8bb2\u8ff0\u4e86\u90a3\u4e00\u573a\u767e\u5e74\u7f55\u8c03\u7684\u6076\u6597\uff0c\u4f17\u4eba\u65b9\u59cb\u77e5\u9053\u8d77\u521d\u91d1\u4e16\u9057\u4ee5 \u4e00\u654c\u4e03\uff0c\u7a0d\u5904\u4e0b\u98ce\uff0c\u540e\u6765\u8c37\u4e4b\u534e\u4e0e\u4ed6\u8054\u624b\uff0c\u4e0d\u8fc7\u534a\u4e2a\u65f6\u8fb0\uff0c\u5c31\u628a\u5bf9\u65b9\u7684\u4e03\u661f\u9635\u5b8c\u5168\u51fb\u6e83\u4e86\u3002 \u3000\u3000\u53f6\u51b2\u9704\u7b11\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u81ea\u8c26\u5403\u4e86\u70b9\u4e8f\uff0c\u5176\u5b9e\u5bf9\u65b9\u4eba\u591a\u4e0d\u77e5\u5927\u4e86\u591a\u5c11\u3002\u4e03\u4e2a\u4eba\u4e2d\uff0c\u9664\u4e86\u4e24 \u4e2a\u4f7f\u638c\u4e4b\u5916\uff0c\u4ed6\u6253\u4e94\u4e2a\u4eba\uff0c\u6709\u56db\u4e2a\u4eba\u7684\u5175\u5668\u7ed9\u4ed6\u593a\u51fa\u4e86\u624b\uff0c\u53ea\u6709\u4e00\u4e2a\u4f7f\u5251\u7684\uff0c\u53ea\u613f\u5403\u4ed6\u4e00 \u638c\uff0c\u4e0d\u613f\u5f03\u5251\uff0c\u7ec8\u4e8e\u7ed9\u4ed6\u4eec\u9003\u8dd1\u4e86\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u5730\u95ee\u91d1\u4e16\u9057\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u5f53\u65f6\u4f60\u5df2\u7ecf \u53ef\u4ee5\u53d6\u4ed6\u6027\u547d\uff0c\u5c31\u7b97\u4f60\u4e0d\u613f\u4f24\u4ed6\uff0c\u4e5f\u53ef\u4ee5\u5c06\u4ed6\u64d2\u83b7\uff0c\u76d8\u95ee\u4ed6\u7684\u53e3\u4f9b\u7684\u3002\u4e3a\u4f55\u4f60\u4e0d\u80af\u65bd\u5c55\u6740 \u624b\uff0c\u8f7b\u6613\u7684\u5c31\u8ba9\u4ed6\u9003\u4e86\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u8fd9\u4eba\u80fd\u591f\u5728\u77ac\u606f\u4e4b\u95f4\u63a5\u6211\u4e03\u62db\uff0c\u65b9\u59cb\u843d\u8d25\uff0c\u4e5f\u7b97\u5f97\u662f\u5f53\u4eca\u4e4b\u4e16\u7684\u4e00\u4e2a\u6b66\u5b66 \u9ad8\u624b\u4e86\u3002\u6211\u5982\u4f55\u8fd8\u80fd\u591f\u4f24\u4ed6\uff1f\u201d\u53f6\u51b2\u9704\u624d\u77e5\u9053\u8fd9\u662f\u56e0\u4e3a\u91d1\u4e16\u9057\u601c\u60dc\u5bf9\u65b9\u7684\u6b66\u529f\u4fee\u6765\u4e0d\u6613\u7684\u7f18 \u6545\u3002 \u3000\u3000\u5389\u5357\u661f\u5176\u6b64\u65f6\u65b9\u59cb\u6709\u7a7a\u4e0a\u62dc\u89c1\u91d1\u4e16\u9057\uff0c\u91d1\u4e16\u9057\u9053\uff1a\u201c\u539f\u6765\u4f60\u548c\u9010\u6d41\u65e9\u5df2\u76f8\u8bc6\u4e86\u3002\u201d\u91d1\u9010 \u6d41\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u662f\u7ed3\u62dc\u7684\u5144\u5f1f\u5462\u3002\u201d\u91d1\u4e16\u9057\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4f60\u4eec\u80fd\u591f\u76f8\u4eb2\u76f8\u7231\uff0c\u4e5f\u4e0d \u6789\u4e86\u6211\u4eec\u4e24\u4ee3\u7684\u4ea4\u60c5\u3002\u201d \u3000\u3000\u968f\u7740\u53f2\u7ea2\u82f1\u548c\u4e00\u73ed\u540e\u8f88\u4e0a\u524d\u7684\u62dc\u89c1\uff0c\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u53f2\u59d1\u5a18\uff0c\u4f60\u5e94\u8be5\u884c\u5927\u793c\u3002\u201d\u53f2\u7ea2\u82f1\u6ee1 \u8138\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u4ef2\u5e2e\u4e3b\u4e3a\u8001\u4e0d\u5c0a\uff0c\u600e\u7684\u62ff\u4f84\u5973\u5f00\u8d77\u73a9\u7b11\u6765\u4e86\u3002\u201d\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u7684\u53ef \u662f\u6b63\u7ecf\u8bdd\u554a\u3002\u4f60\u8fd9\u4e2a\u5934\u603b\u662f\u8981\u78d5\u7684\uff0c\u8001\u53eb\u5316\u7b49\u7740\u559d\u4f60\u7684\u559c\u9152\u5462\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u77e5\u9053\u53f2\u7ea2\u82f1\u662f\u4ed6\u7684\u5ab3\u5987\uff0c\u5341\u5206\u6b22\u559c\uff0c\u7b11\u9053\uff1a\u201c\u4e4b\u534e\uff0c\u4e00\u6643\u4e8c\u5341\u4f59\u5e74\uff0c\u5b69\u5b50\u4eec\u90fd\u5feb \u8981\u6210\u5bb6\u7acb\u5ba4\u4e86\uff0c\u5149\u9634\u53ef\u8fc7\u5f97\u771f\u5feb\u554a\uff01\u201d\u8c37\u4e4b\u534e\u628a\u53f2\u7ea2\u82f1\u62c9\u8fc7\u4e00\u65c1\uff0c\u5411\u957f\u95ee\u77ed\uff0c\u5979\u65e9\u5df2\u4ece\u767d\u82f1 \u6770\u53e3\u4e2d\u77e5\u9053\u53f2\u7ea2\u82f1\u7684\u5bb6\u4e16\uff0c\u77e5\u9053\u5979\u662f\u4e00\u4e2a\u51fa\u6c61\u6ce5\u800c\u4e0d\u67d3\u7684\u597d\u59d1\u5a18\u3002\u8c37\u4e4b\u534e\u7684\u7236\u4eb2\u662f\u5927\u9b54\u5934\u5b5f \u795e\u901a\uff0c\u53f2\u7ea2\u82f1\u7684\u54e5\u54e5\u662f\u516d\u5408\u5e2e\u7684\u5e2e\u4e3b\u53f2\u767d\u90fd\uff0c\u5a46\u5ab3\u4e8c\u4eba\u7684\u8eab\u4e16\u9887\u6709\u76f8\u540c\u4e4b\u5904\uff0c\u56e0\u6b64\u8c37\u4e4b\u534e\u5bf9 \u53f2\u7ea2\u82f1\u66f4\u662f\u7279\u522b\u601c\u60dc\uff0c\u8d8a\u770b\u8d8a\u7231\u3002 \u3000\u3000\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u662f\u8001\u5c11\u4e24\u8f88\u7684\u82f1\u96c4\u4f1a\uff0c\u54b1\u4eec\u53ef\u5f97\u91cd\u5f00\u7b75\u5e2d\uff0c\u597d\u597d\u7684\u5e86\u795d\u4e00\u756a\u3002\u201d\u91d1 \u9010\u6d41\u7b11\u9053\uff1a\u201c\u4ef2\u5e2e\u4e3b\uff0c\u4f60\u8fd8\u6ca1\u6709\u559d\u591f\u5417\uff1f\u201d\u4ef2\u957f\u7edf\u62cd\u4e00\u62cd\u809a\u76ae\uff0c\u8bf4\u9053\uff1a\u201c\u559d\u4f60\u7239\u7239\u7684\u63a5\u98ce \u9152\uff0c\u8001\u53eb\u5316\u8fd9\u5927\u809a\u76ae\u6700\u5c11\u8fd8\u53ef\u4ee5\u88c5\u4e0b\u9ec4\u9152\u5341\u65a4\u3002\u201d \u3000\u3000\u6ee1\u5802\u559c\u6c14\u6d0b\u6d0b\uff0c\u6b63\u5728\u6362\u8fc7\u676f\u7b77\uff0c\u91cd\u6446\u7b75\u5e2d\u3002\u91d1\u4e16\u9057\u60f3\u8d77\u4e00\u4e8b\uff0c\u5ffd\u9053\uff1a\u201c\u9010\u6d41\uff0c\u6709\u4e00\u4ef6\u4e8b \u6211\u5fd8\u8bb0\u95ee\u4f60\uff0c\u4f60\u521a\u624d\u4f7f\u7684\u90a3\u51e0\u62db\u5251\u6cd5\u662f\u4ece\u54ea\u91cc\u5b66\u6765\u7684\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5df2\u77e5\u5176\u4e2d\u7f18\u6545\uff0c\u8bf4\u9053\uff1a\u201c\u7239\u7239\uff0c\u6211\u4e5f\u6b63\u60f3\u95ee\u4f60\uff0c\u4f60\u8bf4\u5728\u5e08\u5144\u5bb6\u4e2d\u78b0\u5230 \u90a3\u4e03\u4e2a\u4eba\uff0c\u4e0d\u7ba1\u4f7f\u7684\u662f\u4ec0\u4e48\u5175\u5668\uff0c\u4ed6\u4eec\u7684\u62db\u6570\u90fd\u662f\u4ece\u4e00\u5957\u5251\u6cd5\u4e2d\u53d8\u5316\u51fa\u6765\u7684\uff0c\u4ed6\u4eec\u7684\u5251\u548c\u5b69 \u513f\u521a\u624d\u6240\u4f7f\u7684\u90a3\u51e0\u62db\uff0c\u5927\u7ea6\u662f\u9887\u4e3a\u76f8\u4f3c\u5427\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u662f\u5440\uff0c\u6240\u4ee5\u6211\u5c31\u8981\u95ee\u4f60\u4e86\uff0c\u83ab\u975e\u4f60\u4e5f\u66fe\u548c\u4ed6\u4eec\u8fd9\u4e00\u6d3e\u7684\u4eba\u4ea4\u8fc7\u7684\u4e48\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eca\u65e5\u7ed3\u8bc6\u4e86\u4e00\u4f4d\u65b0\u670b\u53cb\uff0c\u66fe\u7ecf\u548c\u4ed6\u5370\u8bc1\u6b66\u529f\u3002\u8fd9\u5251\u6cd5\u5c31\u662f\u4ece\u4ed6\u90a3 \u91cc\u5077\u5b66\u7684\u3002\u4e0d\u8fc7\uff0c\u8fd9\u670b\u53cb\u5374\u4f3c\u4e4e\u4e0d\u662f\u548c\u4f60\u6240\u78b0\u89c1\u7684\u90a3\u4e9b\u4eba\u4e00\u8def\u7684\uff0c\u7239\u7239\uff0c\u4ed6\u8fd8\u6b63\u60f3\u627e\u4f60 \u5462\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u8be7\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u4ec0\u4e48\u6765\u5386\uff1f\u201d \u3000\u3000\u521a\u521a\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u542c\u5f97\u95e8\u5916\u6709\u4eba\u7b11\u9053\uff1a\u201c\u4e0d\u901f\u4e4b\u5ba2\u53c8\u6765\u4e86\uff01\u201d \u3000\u3000\u8fd9\u7b11\u58f0\u94ff\u94ff\u9535\u9535\uff0c\u5b9b\u5982\u91d1\u5c5e\u4ea4\u51fb\u3002\u91d1\u4e16\u9057\u542c\u8fdb\u8033\u6735\uff0c\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4eba \u7684\u5185\u529f\u975e\u6b63\u975e\u90aa\uff0c\u867d\u7136\u5c1a\u672a\u767b\u5cf0\u9020\u6781\uff0c\u4e5f\u7b97\u5f97\u662f\u53e6\u8f9f\u8e4a\u8df7\uff0c\u9ad8\u660e\u5f97\u5f88\u4e86\u3002\u4f46\u4f55\u4f3c\u5374\u663e\u5f97\u4f3c\u4e4e \u6709\u70b9\u4e2d\u6c14\u4e0d\u8db3\u5462\uff1f\u83ab\u975e\u4ed6\u521a\u521a\u4e0e\u5f3a\u654c\u4ea4\u8fc7\u624b\u6765\uff0c\u4ee5\u81f4\u4e00\u65f6\u4e4b\u95f4\uff0c\u672a\u80fd\u8c03\u5300\u6c14\u606f\u4e48\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8bf4\u9053\uff1a\u201c\u521a\u8bf4\u66f9\u64cd\uff0c\u66f9\u64cd\u5c31\u5230\u3002\u7239\u7239\uff0c\u6765\u7684\u8fd9\u4eba\u6b63\u662f\u6211\u521a\u624d\u6240\u8bf4\u7684\u90a3\u4f4d\u670b\u53cb\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u679c\u7136\u662f\u725f\u5b97\u6d9b\u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8fce\u4e0a\u524d\u53bb\u8bf4\u9053\uff1a\u201c\u725f\u5144\uff0c\u4f60\u6765\u5f97\u6b63\u597d\u3002\u5bb6\u7236\u5df2\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u5927\u559c\u9053\uff1a\u201c\u6211\u8fd8\u6050\u6015\u6d88\u606f\u4e0d\u786e\u5b9e\u5462\uff0c\u539f\u6765\u4ee4\u5c0a\u679c\u7136\u662f\u56de\u6765\u4e86\u3002\u6211\u6b63\u662f\u7279\u5730\u6765\u62dc\u8c12 \u4ee4\u5c0a\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6709\u70b9\u8be7\u5f02\uff0c\u6b63\u60f3\u95ee\u4ed6\u662f\u4ece\u54ea\u91cc\u542c\u6765\u7684\u6d88\u606f\uff0c\u91d1\u4e16\u9057\u5df2\u7ecf\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d \u6562\u3002\u6211\u5c31\u662f\u91d1\u4e16\u9057\uff0c\u8bf7\u95ee\u9601\u4e0b\u9ad8\u59d3\u5927\u540d\uff0c\u5c0a\u5e08\u662f\u54ea\u4e00\u4f4d\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u606d\u606d\u656c\u656c\u5730\u884c\u4e86\u4e00\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u6276\u6851\u5c9b\u672b\u5b66\u540e\u8f88\u725f\u5b97\u6d9b\u62dc\u89c1\u91d1\u5927\u4fa0\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u65b9\u59cb\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u4ed6\u6240\u78b0\u4e0a\u7684\u90a3\u4e9b\u4eba\u4e43\u662f\u6276\u6851\u5c9b\u7684\u4eba\u7269\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u8d35\u6d3e\u7684 \u5f00\u5c71\u7956\u5e08\u60f3\u5fc5\u662f\u5510\u4ee3\u7684\u866c\u9aef\u5ba2\u5427\uff1f\u201d\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u91d1\u4e16\u9057\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u866c\u9aef \u5ba2\u4e43\u662f\u767e\u4e16\u7f55\u89c1\u7684\u6b66\u5b66\u5b97\u5e08\uff0c\u60f3\u4e0d\u5230\u4ed6\u4e00\u8109\u6240\u4f20\u7684\u6b66\u529f\uff0c\u5982\u4eca\u91cd\u89c1\u4e2d\u4e0a\uff0c\u5f53\u771f\u662f\u53ef\u559c\u53ef \u8d3a\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u65f6\u9694\u5343\u5e74\uff0c\u6ca7\u6851\u53d8\u6362\uff0c\u5148\u7956\u6240\u4f20\u7684\u6b66\u5b66\uff0c\u53ea\u4f59\u65ad\u7b80\u6b8b\u7bc7\uff0c\u665a\u8f88\u6240\u5f97\uff0c\u6050\u6015 \u8fd8\u4e0d\u5230\u5341\u5206\u4e4b\u4e00\u3002\u91d1\u5927\u4fa0\u7684\u8c2c\u8d5e\uff0c\u5b9e\u4e0d\u6562\u5f53\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff0c\u201c\u8fd9\u4f4d\u725f\u5144\u6b63\u662f\u8981\u6765\u4e2d\u539f\u5bfb\u8bbf\u95ee\u95e8\u7684\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u665a\u8f88\u6709\u4e2a\u5fc3\u613f\uff0c\u5e0c\u671b\u80fd\u591f\u5728\u4e2d\u571f\u627e\u5f97\u5230\u672c\u6d3e\u5931\u4f20\u7684\u6b66\u5b66\uff0c\u867d\u4e0d\u6562\u671b\u6062\u590d\u672c \u6765\u9762\u76ee\uff0c\u4f46\u53ea\u8981\u7a0d\u5f97\u4e00\u4e8c\uff0c\u4e5f\u53ef\u4ee5\u544a\u6170\u5148\u5e08\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u725f\u5144\u6709\u6b64\u5b8f\u613f\uff0c\u5b9a\u53ef\u4e3a\u6b66\u6797\u653e\u4e00\u5f02\u5f69\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u5c1a\u76fc\u91d1\u5927\u4fa0\u9f0e\u529b\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4f60\u8fd8\u6ca1\u6709\u78b0\u4e0a\u540c\u95e8\u4e48\u3002\u201d\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d\u91d1\u4e16\u9057\u5fae\u611f\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a \u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u521a\u624d\u78b0\u4e0a\u7684\u53c8\u662f\u54ea\u4e00\u4f4d\u9ad8\u624b\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u5927\u5403\u4e00\u60ca\uff0c\u8be7\u5f02\u66f4\u751a\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u5982\u4f55\u5f97\u77e5\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u542c\u725f\u5144\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u4f3c\u4e4e\u662f\u5c11\u9633\u7ecf\u8109\uff0c\u66fe\u53d7\u5bf9\u65b9\u7684\u5185\u529f\u6240\u9707\uff0c\u4ee5\u81f3\u4e2d\u6c14 \u5fae\u663e\u4e0d\u8db3\u3002\u4e0d\u77e5\u6211\u6709\u6ca1\u6709\u8bf4\u9519\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u5927\u60ca\u4e4b\u4e0b\uff0c\u51b7\u4e86\u534a\u622a\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u91d1\u4e16\u9057\u53ea\u662f\u542c\u97f3\u8fa8\u58f0\uff0c\u5bf9\u6211\u521a\u624d\u5982\u4f55\u53d7\u4f24\u7684 \u7ecf\u8fc7\u5c31\u597d\u50cf\u4eb2\u773c\u770b\u89c1\u4e00\u822c\u3002\u8fd9\u6837\u795e\u5947\u7684\u6b66\u5b66\u9020\u8be3\uff0c\u5f53\u771f\u662f\u8fdc\u8fdc\u975e\u6211\u6240\u53ca\uff01\u6211\u53ea\u9053\u631f\u4e86\u6276\u6851\u5c9b \u7684\u79d8\u4f20\u6b66\u5b66\uff0c\u5c31\u53ef\u4ee5\u79f0\u9738\u4e2d\u539f\uff0c\u5982\u4eca\u770b\u6765\uff0c\u80dc\u8fc7\u6211\u7684\u4eba\u8fd8\u591a\u7740\u5462\uff0c\u66f4\u4e0d\u7528\u8bf4\u91d1\u4e16\u9057\u4e86\uff01\u201d \u3000\u3000\u91d1\u6279\u9057\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5e78\u559c\u725f\u5144\u5185\u529f\u6df1\u539a\uff0c\u5c11\u9633\u7ecf\u8109\u867d\u53d7\u5bf9\u65b9\u5185\u529b\u6240\u9707\uff0c\u4e5f\u4e0d\u7d27 \u8981\uff0c\u53ea\u8981\u517b\u606f\u51e0\u5929\uff0c\u5c31\u53ef\u4ee5\u597d\u4e86\u3002\u4f46\u4e0d\u77e5\u725f\u5144\u78b0\u4e0a\u7684\u5f3a\u654c\uff0c\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u91d1\u4e16\u9057\u8d77\u521d\u4ee5\u4e3a \u4ed6\u662f\u78b0\u4e0a\u540c\u95e8\uff0c\u5f7c\u6b64\u5370\u8bc1\u6b66\u529f\uff0c\u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\uff0c\u5bf9\u65b9\u5728\u8ba4\u51fa\u662f\u540c\u95e8\u4e4b\u540e\uff0c\u6545\u800c\u624b\u4e0b\u7559\u60c5\uff0c\u6ca1 \u6709\u5c06\u4ed6\u91cd\u4f24\u3002\u73b0\u5728\u77e5\u9053\u731c\u5f97\u4e0d\u5bf9\uff0c\u5fc3\u91cc\u4e5f\u662f\u597d\u751f\u60ca\u5f02\uff0c\u60f3\u9053\uff1a\u201c\u80fd\u591f\u80dc\u5f97\u8fc7\u725f\u5b97\u6d9b\u7684\u4eba\uff0c\u672c \u9886\u81f3\u5c11\u4e0d\u4f1a\u5f31\u4e8e\u6211\u5728\u6d77\u5929\u5bb6\u91cc\u78b0\u4e0a\u7684\u90a3\u4e9b\u4eba\uff0c\u60f3\u4e0d\u5230\u6211\u5c0f\u9690\u4e8c\u5341\u5e74\uff0c\u6b66\u6797\u4e2d\u7adf\u7136\u51fa\u4e86\u8fd9\u8bb8\u591a \u9ad8\u624b\uff01\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u662f\u4e00\u5bf9\u4e0d\u77e5\u6765\u5386\u7684\u592b\u5987\uff0c\u60ed\u6127\u5f97\u5f88\uff0c\u6211\u770b\u4e0d\u51fa\u4ed6\u4eec\u7684\u5b97\u6d3e\u3002\u201d \u3000\u3000\u539f\u6765\u725f\u5b97\u6d9b\u5728\u4e0b\u5c71\u4e4b\u540e\uff0c\u56e0\u4e3a\u7b2c\u4e00\u6b21\u521d\u4f1a\u4e2d\u539f\u9ad8\u624b\uff0c\u4e0e\u91d1\u9010\u6d41\u6bd4\u6b66\uff0c\u867d\u7136\u7a0d\u5360\u4e0a\u98ce\uff0c\u5374 \u4e5f\u80dc\u4e0d\u4e86\u4ed6\u624b\u4e2d\u7684\u7384\u94c1\u5b9d\u5251\uff0c\u6bd4\u6c5f\u6d77\u5929\u7684\u5185\u529b\uff0c\u53c8\u66f4\u662f\u81ea\u6127\u4e0d\u5982\u3002\u662f\u4ee5\u5fc3\u60c5\u751a\u4e3a\u60c6\u6005\uff0c\u81ea\u5fd6 \u53ea\u6709\u65e9\u65e5\u627e\u5230\u540c\u95e8\uff0c\u628a\u672c\u6d3e\u7684\u6b66\u5b66\u79d8\u7b08\u641c\u96c6\u9f50\u5168\uff0c\u53d1\u626c\u5149\u5927\uff0c\u8fd9\u624d\u6709\u51fa\u4eba\u5934\u5730\u4e4b\u65e5\u3002 \u3000\u3000\u6b63\u5728\u80e1\u601d\u4e71\u60f3\uff0c\u5ffd\u542c\u5f97\u9a6c\u94c3\u58f0\u54cd\uff0c\u6709\u4e00\u5bf9\u4e2d\u5e74\u7537\u5973\uff0c\u9a91\u7740\u9a6c\u8d8a\u8fc7\u4ed6\u7684\u524d\u5934\u3002\u8fd9\u5bf9\u7537\u5973\u4e43 \u662f\u5e76\u8f94\u75be\u9a70\uff0c\u6b63\u5728\u8bf4\u7740\u8bdd\u7684\u3002\u5c31\u5728\u4ed6\u4eec\u4ece\u725f\u5b97\u6d9b\u8eab\u65c1\u9a70\u8fc7\u4e4b\u65f6\uff0c\u725f\u5b97\u6d9b\u521a\u597d\u542c\u5f97\u4ed6\u4eec\u63d0\u8d77\u91d1 \u4e16\u9057\u7684\u540d\u5b57\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8ddf\u4e0a\u51e0\u6b65\uff0c\u53ea\u542c\u5f97\u90a3\u7537\u7684\u8bf4\u9053\uff1a\u201c\u91d1\u4e16\u9057\u592b\u59bb\u548c\u6c5f\u6d77\u5929\u7684\u59bb\u5b50\u4ece\u8fd9\u6761 \u8def\u7ecf\u8fc7\uff0c\u770b\u6765\u6765\u8d70\u662f\u4ece\u6c5f\u5bb6\u51fa\u6765\uff0c\u524d\u5f80\u5f82\u5f95\u5c71\u7684\uff0c\u4e0d\u77e5\u6276\u6851\u4e03\u5b50\u53ef\u78b0\u4e0a\u4ed6\u6ca1\u6709\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u970d\u7136\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u6240\u8bf4\u7684\u6276\u6851\u4e03\u5b50\uff0c\u83ab\u975e\u5c31\u662f\u6211\u7684\u540c\u95e8\uff1f\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c \u60f3\u4e0d\u5230\u5728\u8fd9\u91cc\u7adf\u4f1a\u5f97\u77e5\u540c\u95e8\u7684\u6d88\u606f\uff0c\u800c\u7adf\u6709\u4e03\u4eba\u4e4b\u591a\uff01\u201d \u3000\u3000\u91d1\u4e16\u9057\u7684\u4e0b\u843d\u4e5f\u6b63\u662f\u725f\u5b97\u6d9b\u6240\u8981\u6253\u542c\u7684\uff0c\u5982\u4eca\u5728\u8fd9\u4eba\u53e3\u4e2d\uff0c\u4e00\u8fde\u900f\u9732\u51fa\u4e24\u4e2a\u91cd\u8981\u7684\u6d88 \u606f\uff0c\u4ed6\u5982\u4f55\u8fd8\u80fd\u653e\u8fc7\uff1f \u3000\u3000\u725f\u5b97\u6d9b\u7684\u8f7b\u529f\u751a\u4e3a\u4e86\u5f97\uff0c\u6570\u91cc\u4e4b\u5185\u7684\u9014\u7a0b\uff0c\u4e0d\u4e9a\u5954\u9a6c\u3002\u5f53\u4e0b\u8fde\u5fd9\u5c31\u8ffd\u4e0a\u53bb\u3002 \u3000\u3000\u90a3\u5973\u7684\u8bf4\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u4eec\u662f\u5426\u78b0\u4e0a\uff0c\u54b1\u4eec\u603b\u5f97\u628a\u91d1\u4e16\u9057\u4e1a\u5df2\u56de\u6765\u7684\u6d88\u606f\uff0c\u544a\u8bc9\u6276\u6851\u4e03 \u5b50\u3002\u201d \u3000\u3000\u90a3\u7537\u7684\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54a6\uff0c\u4ec0\u4e48\u4eba\u5728\u540e\u9762\u8ddf\u7740\uff1f\u201d\u6b64\u65f6\u4ed6\u5df2\u53d1\u73b0\u725f\u5b97\u6d9b\u8ffd\u6765\u4e86\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u6371\u4e86\u53e3\u6c14\uff0c\u53eb\u9053\uff1a\u201c\u8bf7\u4e24\u4f4d\u7a0d\u6b47\u4e00\u6b47\uff01\u201d\u55d6\u5730\u98de\u8eab\u63a0\u8fc7\uff0c\u62e6\u4f4f\u4e0b\u9a6c\u5934\u3002\u201d \u3000\u3000\u90a3\u4e24\u592b\u59bb\u89c1\u4e86\u725f\u5b97\u6d9b\u7684\u8eab\u624b\u4e5f\u662f\u597d\u751f\u8be7\u5f02\uff0c\u5f53\u4e0b\u53cc\u53cc\u4e0b\u9a6c\uff0c\u540c\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f \u6211\u4e0e\u4f60\u7d20\u6627\u5e73\u751f\u51fa\u4f55\u9014\u4e2d\u62e6\u963b\uff1f\u201d \u3000\u3000\u8fd9\u5bf9\u592b\u59bb\u9ad8\u9f3b\u6df1\u76ee\uff0c\u773c\u73e0\u5fae\u78a7\uff0c\u770b\u8d77\u6765\u4e0d\u5927\u50cf\u6c49\u4eba\uff0c\u4f46\u6c49\u8bed\u5374\u8bf4\u5f97\u5f88\u6d41\u5229\u3002\u725f\u5b97\u6d9b\u60ca\u7591 \u4e0d\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u59d3\u725f\uff0c\u8d31\u540d\u5b97\u6d9b\u3002\u4e0d\u77e5\u4e24\u4f4d\u53ef\u66fe\u542c\u8fc7\u5728\u4e0b\u7684\u540d\u5b57\uff1f\u201d \u3000\u3000\u90a3\u7537\u7684\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u9f0e\u9f0e\u5927\u540d\u7684\u4eba\u7269\u4e48\uff1f\u5bf9\u4e0d\u4f4f\uff0c\u6211\u5b64\u964b\u5be1\u95fb\uff0c\u53ef\u6ca1\u6709\u542c\u8fc7\u9601\u4e0b\u7684 \u5927\u540d\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u8d54\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u6211\u53ea\u662f\u65e0\u540d\u5c0f\u5352\u800c\u5df2\u3002\u4e0d\u8fc7\u6211\u5374\u662f\u4ece\u6276\u6851\u5c9b\u6765\u7684\uff0c \u56e0\u6b64\u6211\u4ee5\u4e3a\u4f60\u4eec\u7684\u670b\u53cb\u53ef\u80fd\u77e5\u9053\u6211\u7684\u540d\u5b57\u3002\u201d \u3000\u3000\u8fd9\u5bf9\u592b\u59bb\u542c\u5f97\u725f\u5b97\u6d9b\u662f\u4ece\u6276\u6851\u5c9b\u6765\u7684\uff0c\u90fd\u662f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\u3002\u4e08\u592b\u4ed4\u7ec6\u5730\u6253\u91cf\u4e86\u725f\u5b97\u6d9b\u4e00 \u756a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u6709\u4e9b\u4ec0\u4e48\u670b\u53cb\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u9601\u4e0b\u521a\u624d\u597d\u50cf\u8c08\u53ca\u6276\u6851\u4e03\u5b50\uff0c\u4e0d\u77e5\u6211\u6709\u6ca1\u6709\u542c\u9519\uff1f\u5982\u679c\u6ca1\u9519\u7684\u8bdd\uff0c\u6211\u60f3\u8bf7 \u95ee\u4f60\u8bf4\u7684\u6276\u6851\u4e03\u5b50\u662f\u4e0d\u662f\u4ece\u6276\u6851\u5c9b\u6765\u7684\u4e03\u4e2a\u4eba\uff1f\u201d \u3000\u3000\u90a3\u7537\u7684\u9053\uff1a\u201c\u4f60\u771f\u7684\u662f\u6276\u6851\u5c9b\u725f\u5bb6\u7684\u540e\u4eba\u4e48\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u5fae\u5fae\u9053\uff1a\u201c\u6211\u5e72\u561b\u8981\u9a97\u4f60\uff01\u201d \u3000\u3000\u90a3\u5973\u7684\u9053\uff1a\u201c\u6709\u70b9\u4e0d\u5927\u5bf9\u5427\uff1f\u5982\u679c\u4f60\u8bf4\u7684\u662f\u771f\u8bdd\uff0c\u4f55\u4ee5\u4ed6\u4eec\u5e76\u4e0d\u77e5\u9053\u6709\u4f60\u8fd9\u53f7\u4eba\u7269\uff1f\u201d \u3000\u3000\u6276\u6851\u5c9b\u8fd9\u4e00\u6d3e\u7684\u6b66\u529f\uff0c\u4ece\u6570\u767e\u5e74\u524d\u5c31\u5df2\u5206\u4e3a\u4e09\u652f\uff0c\u725f\u5b97\u6d9b\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6216\u8005\u662f\u522b\u7684\u652f\u6d3e \u7684\u540c\u95e8\uff0c\u53ea\u56e0\u4ed6\u4eec\u7684\u6b66\u529f\u6e90\u51fa\u6276\u6851\uff0c\u6545\u800c\u81ea\u79f0\u6276\u6851\u4e03\u5b50\u3002\u4f46\u53ea\u8981\u6211\u4e0e\u4ed6\u4eec\u5370\u8bc1\u6b66\u529f\uff0c\u4ed6\u4eec\u5c31 \u4f1a\u76f8\u4fe1\u6211\u4e86\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u662f\u771f\u662f\u5047\uff0c\u8bf7\u4e24\u4f4d\u5e26\u6211\u53bb\u89c1\u4e00\u89c1\u4ed6\u4eec\uff0c\u4fbf\u4f1a\u660e\u767d\u3002\u201d \u3000\u3000\u90a3\u5973\u7684\u534a\u4fe1\u534a\u7591\uff0c\u8bf4\u9053\uff1a\u201c\u5e26\u4f60\u53bb\u89c1\u4ed6\u4eec\u5012\u4e5f\u5bb9\u6613\u3002\u4f46\u6211\u4eec\u4e0d\u77e5\u4f60\u7684\u5e95\u7ec6\uff0c\u53c8\u5c82\u80fd\u8f7b\u6613 \u5730\u7b54\u5141\u4f60\u5462\uff1f\u201d\u5fc3\u60f3\uff1a\u201c\u5018\u82e5\u8fd9\u4eba\u662f\u5bf9\u65b9\u7684\u5978\u7ec6\u7684\u8bdd\uff0c\u5c06\u6765\u51fa\u4e86\u4ec0\u4e48\u5dee\u9519\uff0c\u6276\u6851\u4e03\u5b50\u5c82\u4e0d\u8981 \u602a\u8d23\u6211\u4eec\u592b\u59bb\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u8981\u89c1\u4e86\u90a3\u4e03\u4e2a\u4eba\u65b9\u80fd\u8bc1\u5b9e\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u4f46\u73b0\u5728\u8fd9\u5bf9\u592b\u59bb\u8981\u77e5\u9053\u4ed6\u7684\u5e95\u7ec6\uff0c\u5374\u53c8\u4e0d \u80af\u76f8\u4fe1\u4ed6\u7684\u8bf4\u8bdd\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u5c31\u53d8\u5f97\u7f20\u5939\u4e0d\u6e05\u4e86\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u65e0\u6cd5\u53ef\u60f3\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8981\u600e\u6837\u77e5\u9053\u6211\u7684\u5e95\u7ec6\uff0c\u597d\uff0c\u8bf7\u4f60\u4eec\u95ee\u5427\uff01\u201d \u3000\u3000\u90a3\u7537\u7684\u82e5\u6709\u6240\u601d\uff0c\u5ffd\u5730\u95ee\u9053\uff1a\u201c\u4f60\u521a\u624d\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u521a\u81ea\u5f82\u5f95\u5c71\u4e0b\u6765\u3002\u201d \u3000\u3000\u90a3\u7537\u7684\u9053\uff0c\u201c\u54e6\uff0c\u4f60\u5df2\u7ecf\u5230\u8fc7\u5f82\u5f95\u5c71\u4e86\uff0c\u4f60\u6709\u6ca1\u6709\u78b0\u4e0a\u91d1\u4e16\u9057\uff01\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u6ca1\u6709\u78b0\u4e0a\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u7684\u516c\u5b50\u6211\u5012\u662f\u89c1\u7740\u4e86\u3002\u201d\u90a3\u7537\u7684\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u91d1\u9010 \u6d41\u4e48\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u4e0d\u9519\u3002\u6211\u548c\u6b27\u9633\u575a\u540c\u5728\u4e00\u8d77\uff0c\u51e0\u4e4e\u7ed9\u4ed6\u8bef\u4f1a\uff0c\u540e\u6765\u6211\u548c\u4ed6\u8bf4\u660e\u6765\u610f\uff0c\u5e78\u4e8f \u4ed6\u8fd8\u80af\u76f8\u4fe1\u6211\u7684\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u90a3\u7537\u7684\u9053\uff1a\u201c\u4f60\u8bf4\u660e\u4e86\u4ec0\u4e48\u6765\u610f\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u6211\u60f3\u62dc\u6258\u7236\u4eb2\u4ee3\u4e3a\u6253\u542c\u540c\u95e8\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u90a3\u7537\u7684\u9053\uff1a\u201c\u4ed6\u8bf4\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u4ed6\u8bf4\u4ed6\u7236\u4eb2\u5c31\u8981\u56de\u6765\uff0c\u6240\u4ee5\u6211\u624d\u62dc\u6258\u4ed6\u7684\u3002\u5bf9\u4e86\uff0c\u6211\u6b63\u60f3\u8bf7\u95ee\u4e24\u4f4d\uff0c\u4f60\u4eec \u662f\u4e0d\u662f\u5df2\u7ecf\u89c1\u7740\u4e86\u91d1\u5927\u4fa0\u4e86\u3002\u201d \u3000\u3000\u90a3\u5973\u7684\u542c\u89c1\u725f\u5b97\u6d9b\u79f0\u91d1\u4e16\u9057\u4e3a\u201c\u91d1\u5927\u4fa0\u201d\uff0c\u67f3\u7709\u4e00\u626c\uff0c\u5c31\u60f3\u53d1\u4f5c\uff0c\u5374\u7ed9\u4ed6\u7684\u4e08\u592b\u7528\u773c\u8272 \u6b62\u4f4f\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u611f\u5230\u90a3\u5973\u7684\u795e\u8272\u4f3c\u4e4e\u6709\u70b9\u4e0d\u5bf9\uff0c\u6b63\u81ea\u8be7\u5f02\uff0c\u53ea\u542c\u5f97\u90a3\u7537\u7684\u5df2\u5728\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48 \u8bf4\u6765\uff0c\u4f60\u548c\u91d1\u9010\u6d41\u5012\u662f\u4e00\u89c1\u5982\u6545\u554a\uff01\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u4eec\u867d\u7136\u662f\u521d\u6b21\u76f8\u8bc6\uff0c\u4f46\u8bf4\u6765\u4e5f\u6709\u4e00\u6bb5\u6e90\u6e90\uff0c\u4ed6\u7684\u7236\u4eb2\u662f\u66fe\u7ecf\u5230\u8fc7 \u6276\u6851\u5c9b\uff0c\u8bbf\u67e5\u655d\u6d3e\u7684\u8fd1\u51b5\u7684\u3002\u662f\u4ee5\u6211\u548c\u4ed6\u8c08\u5f97\u5012\u5f88\u662f\u6295\u673a\u3002\u201d \u3000\u3000\u90a3\u7537\u7684\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\uff0c\u4ed6\u8d77\u521d\u5bf9\u4f60\u9887\u6709\u8bef\u4f1a\uff0c\u90a3\u53c8\u662f\u4e3a\u4e86\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u4ed6\u4eec\u542c\u5f97\u98ce\u58f0\uff0c\u636e\u8bf4\u6e05\u5ef7\u5c06\u6709\u6240\u4e0d\u5229\u4e8e\u4ed6\u7684\u5e08\u5144\uff0c\u800c\u6b27\u9633\u575a\u4e43\u662f\u6e05\u5ef7\u7684\u9e70 \u72ac\uff0c\u4f46\u6211\u5374\u4e0d\u77e5\u9053\u3002\u4e24\u4f4d\u521a\u624d\u8bf4\u8d77\u91d1\u5927\u4fa0\u4ece\u4e1c\u5e73\u53bf\u6765\uff0c\u4e0d\u77e5\u53ef\u66fe\u542c\u5230\u4ec0\u4e48\u5173\u4e8e\u6c5f\u5bb6\u7684\u6d88 \u606f\uff1f\u201d \u3000\u3000\u90a3\u5973\u7684\u5ffd\u5730\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8981\u6253\u542c\u7684\u4e5f\u672a\u514d\u592a\u591a\u4e86\uff01\u201d \u3000\u3000\u725f\u5b97\u6d9b\u6115\u7136\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u4e0d\u77e5\u9053\u662f\u4e0d\u8be5\u6253\u542c\u7684\u3002\u90a3\u4e48\u522b\u7684\u4e0d\u8bf4\uff0c\u8bf7\u4e24\u4f4d\u5e26\u6211\u53bb\u89c1\u4e00 \u89c1\u8d35\u53cb\uff0c\u603b\u53ef\u4ee5\u5427\uff1f\u201d\u6b64\u65f6\u4ed6\u5df2\u9690\u9690\u77e5\u9053\u6709\u70b9\u513f\u4e0d\u5bf9\u4e86\u3002 \u3000\u3000\u90a3\u7537\u7684\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5e26\u4f60\u53bb\u4e5f\u672a\u5c1d\u4e0d\u53ef\uff0c\u4e0d\u8fc7\u4f60\u5148\u8981\u4ee4\u6211\u76f8\u4fe1\u4f60\u7684\u786e\u662f\u6276\u6851\u5c9b\u7684\u4eba \u7269\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u5df2\u662f\u6709\u70b9\u751f\u6c14\uff0c\u5fcd\u4e0d\u4f4f\u5c31\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u8981\u600e\u6837\u624d\u80fd\u4ee4\u9601\u4e0b\u76f8\u4fe1\u3002\u201d \u3000\u3000\u90a3\u7537\u7684\u9053\uff1a\u201c\u5bb9\u6613\u5f97\u5f88\uff0c\u6211\u60f3\u5411\u9601\u4e0b\u9886\u6559\u51e0\u62db\u9ad8\u62db\uff01\u201d \u3000\u3000\u6276\u6851\u5c9b\u7684\u6b66\u529f\u81ea\u6210\u4e00\u5bb6\uff0c\u548c\u4efb\u4f55\u95e8\u6d3e\u90fd\u4e0d\u76f8\u540c\uff0c\u5f7c\u6b64\u5370\u8bc1\u6b66\u529f\uff0c\u4e5f\u7684\u786e\u662f\u53ef\u4ee5\u8bc1\u660e\u725f\u5b97 \u6d9b\u7684\u4e00\u4e2a\u529e\u6cd5\u3002\u725f\u5b97\u6d9b\u542c\u4e86\u6b64\u8a00\uff0c\u4e00\u65f6\u731c\u4e0d\u900f\u5bf9\u65b9\u662f\u597d\u610f\u8fd8\u662f\u6076\u610f\uff0c\u4fbf\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec \u70b9\u5230\u5373\u6b62\uff0c\u80dc\u8d25\u4e0d\u8bba\u3002\u201d \u3000\u3000\u90a3\u7537\u7684\u9053\uff1a\u201c\u5e9f\u8bdd\u5c11\u8bf4\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5df2\u662f\u5148\u884c\u53d1\u62db\u3002\u725f\u5b97\u6d9b\u662f\u4e2a\u6b66\u5b66\u7684\u5927\u884c\u5bb6\uff0c\u4e00\u770b \u6765\u52bf\uff0c\u4fbf\u77e5\u662f\u4e00\u62db\u6740\u624b\uff0c\u4e0d\u7531\u5f97\u6c14\u5f80\u4e0a\u51b2\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u628a\u4f60\u5f53\u4f5c\u670b\u53cb\uff0c\u4f60\u5012\u628a\u6211\u5f53\u4f5c\u654c \u4eba\u4e86\uff01\u201d \u3000\u3000\u725f\u5b97\u6d9b\u6c14\u5f80\u4e0a\u51b2\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u7ed9\u4f60\u4e00\u70b9\u5389\u5bb3\u77a7\u77a7\uff0c\u4f60\u53ea\u5f53\u6211\u662f\u597d\u6b3a\u8d1f\u7684\u4e86\u3002\u201d\u5f53\u4e0b \u4e00\u98d8\u4e00\u95ea\uff0c\u626c\u8d77\u6298\u6247\uff0c\u5212\u4e86\u4e00\u9053\u5706\u5f27\uff0c\u4f3c\u70b9\u4f3c\u6233\uff0c\u6247\u5934\u5bf9\u51c6\u4e86\u5bf9\u65b9\u638c\u5fc3\u7684\u201c\u52b3\u5bab\u7a74\u201d\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u98d8\u5ffd\u4e0d\u5b9a\uff0c\u53ef\u4ee5\u5f53\u4f5c\u5224\u5b98\u7b14\u7528\uff0c\u4e5f\u53ef\u4ee5\u5f53\u4f5c\u4e94\u884c\u5251\u4f7f\u3002\u5f53\u5224\u5b98\u7b14\u65f6\uff0c\u5728\u4e00\u62db\u4e4b \u5185\uff0c\u80fd\u70b9\u5bf9\u65b9\u7684\u4e03\u5904\u5927\u7a74\uff1b\u5f53\u4e94\u884c\u5251\u65f6\uff0c\u4e5f\u53ef\u4ee5\u5728\u4e00\u62db\u4e4b\u5185\uff0c\u523a\u5bf9\u65b9\u7684\u4e09\u5904\u8981\u5bb3\u3002\u6b63\u662f\u6276\u6851 \u5c9b\u4e00\u62db\u6700\u4e0a\u4e58\u7684\u5251\u6cd5\uff01 \u3000\u3000\u90a3\u7537\u7684\u5fae\u5fae\u4e00\u201c\u566b\u201d\uff0c\u5fc3\u91cc\u660e\u767d\u725f\u5b97\u6d9b\u7684\u786e\u662f\u6276\u6851\u5c9b\u866c\u9aef\u5ba2\u7684\u4e00\u8109\u6240\u4f20\uff0c\u4f46\u56e0\u4ed6\u4ea6\u5df2\u77e5 \u9053\u725f\u5b97\u6d9b\u5e76\u975e\u201c\u6276\u6851\u4e03\u5b50\u201d\u4e00\u8def\uff0c\u6545\u6b64\u8fd8\u662f\u4f6f\u4f5c\u4e0d\u77e5\uff0c\u53cc\u638c\u4f9d\u7136\u5411\u524d\u6253\u53bb\uff01 \u3000\u3000\u725f\u5b97\u6d9b\u5012\u662f\u5403\u4e86\u4e00\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u4ed6\u6709\u5c01\u95ed\u81ea\u8eab\u7a74\u9053\u4e4b\u80fd\uff0c\u4e0d\u6015\u6211\u7684\u91cd\u624b\u6cd5\u70b9\u7a74\uff1f\u201d \u3000\u3000\u4ed6\u56e0\u4e0d\u80fd\u65ad\u5b9a\u5bf9\u65b9\u662f\u53cb\u662f\u654c\uff0c\u53cd\u800c\u4e0d\u65e0\u987e\u5fcc\u3002 \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\u5ffd\u89c9\u5bf9\u65b9\u53cc\u638c\u53d1\u51fa\u7684\u529b\u9053\u4e92\u4e3a\u7275\u5f15\uff0c\u4fe8\u4f3c\u7f6e\u8eab\u6f29\u6da1\u4e4b\u4e2d\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u6253\u4e86\u4e00\u4e2a \u76d8\u65cb\uff0c\u6298\u6247\u70b9\u7a74\uff0c\u767b\u65f6\u5931\u4e86\u51c6\u5934\uff0c\u51e0\u4e4e\u7ed9\u90a3\u4eba\u5939\u624b\u62a2\u53bb\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4eba\u7ec3\u7684\u662f\u521a\u67d4\u76f8\u6d4e\u7684\u638c\u529b\uff0c\u4e5f\u662f\u4e00\u95e8\u6781\u5947\u7279\u7684\u6b66\u529f\u3002\u800c\u725f\u5b97\u6d9b\u56e0\u4e3a\u6709\u70b9\u987e\u5fcc\uff0c\u4e0d \u6562\u4f7f\u5230\u5341\u6210\u529f\u529b\uff0c\u6545\u800c\u4e00\u7167\u9762\u5c31\u5403\u4e8f\u4e86\u3002 \u3000\u3000\u90a3\u7537\u7684\u51b7\u7b11\u9053\uff0c\u201c\u6276\u6851\u5c9b\u7684\u6b66\u529f\u4ec5\u6b62\u4e8e\u6b64\u4e48\uff1f\u201d\u5f97\u7406\u4e0d\u9976\u4eba\uff0c\u7adf\u7136\u53c8\u662f\u6b3a\u8eab\u8fdb\u6251\uff0c\u53cc\u638c \u6253\u51fa\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u60f3\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u5370\u8bc1\u6b66\u529f\uff0c\u4f60\u7adf\u8981\u53d6\u6211\u7684\u6027\u547d\uff01\u201d\u4e8e\u662f\u4e5f\u51b7\u7b11 \u9053\uff1a\u201c\u4f60\u8981\u89c1\u8bc6\u6276\u6851\u5c9b\u7684\u6b66\u529f\uff0c\u90a3\u4e5f\u4e0d\u96be\uff01\u6697\u8fd0\u5343\u65a4\u5760\u7684\u91cd\u8eab\u6cd5\uff0c\u6247\u4e2d\u631f\u638c\uff0c\u7535\u5149\u77f3\u706b\u7684\u8fd8 \u4e86\u4e09\u62db\uff01 \u3000\u3000\u8fd9\u4e00\u6b21\u90a3\u4eba\u7684\u53cc\u638c\u4e4b\u529b\u672a\u80fd\u628a\u725f\u5b97\u6d9b\u63a8\u52a8\uff0c\u594b\u529b\u62c6\u4e86\u4e8c\u62db\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u8863 \u88f3\u7ed9\u725f\u5b97\u6d9b\u6495\u53bb\u4e86\u4e00\u5e45\u3002\u53ef\u662f\u725f\u5b97\u6d9b\u5728\u4ed6\u638c\u529b\u6fc0\u8361\u4e4b\u4e0b\uff0c\u4e5f\u81ea\u89c9\u5f97\u6709\u70b9\u6c14\u5598\u5fc3\u8df3\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u4e00\u638c\u51fb\u9000\u5bf9\u65b9\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6276\u6851\u5c9b\u6b66\u529f\u5982\u4f55\uff1f\u201d\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u600e\u4e48 \u6837\uff01\u201d\u9000\u800c\u590d\u4e0a\uff0c\u53cc\u638c\u865a\u62b1\uff0c\u8fd8\u4e86\u4e00\u62db\u3002\u725f\u5b97\u6d9b\u53ea\u9053\u4ed6\u4ecd\u7136\u662f\u5de6\u638c\u9633\u521a\uff0c\u53f3\u638c\u9634\u67d4\uff0c\u4e8e\u662f\u4f9d \u6837\u753b\u846b\u82a6\u7684\u7167\u521a\u624d\u7684\u65b9\u6cd5\u5728\u5e94\u4ed8\uff0c\u4e0d\u6599\u7a81\u7136\u95f4\u53ea\u89c9\u5bf9\u65b9\u7684\u638c\u529b\u5927\u5f97\u51fa\u5947\uff0c\u539f\u6765\u8fd9\u4eba\u867d\u7136\u662f\u4e00 \u521a\u4e00\u67d4\uff0c\u4f46\u4e5f\u53ef\u4ee5\u5de6\u53f3\u4e92\u6613\uff0c\u968f\u65f6\u53d8\u6362\uff0c\u751a\u6216\u53cc\u638c\u9f50\u521a\u3001\u53cc\u638c\u9f50\u67d4\u4ea6\u65e0\u4e0d\u53ef\u3002\u725f\u5b97\u6d9b\u731d\u4e0d\u53ca \u9632\uff0c\u51e0\u4e4e\u7740\u4e86\u9053\u513f\uff0c\u5e78\u4e8f\u5316\u89e3\u5f97\u5feb\uff0c\u63a5\u8fde\u9000\u51fa\u4e86\u4e09\u6b65\u4e4b\u540e\uff0c\u5df2\u5c06\u5bf9\u65b9\u7684\u529b\u9053\u5378\u53bb\u4e86\u4e00\u534a\uff0c\u4f46 \u80f8\u4e2d\u6c14\u8840\u7ffb\u6d8c\uff0c\u4ea6\u5df2\u4f3c\u53d7\u94c1\u9524\u6240\u51fb\u4e00\u822c\u3002 \u3000\u3000\u90a3\u5973\u7684\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5012\u6709\u51e0\u5206\u6328\u6253\u7684\u672c\u9886\uff0c\u90a3\u5c31\u518d\u8bd5\u4e00\u8bd5\u6211\u7684\u529f\u5927 \u5427\uff01\u201d\u6b63\u662f\uff1a \u3000\u3000\u904d\u8bbf\u540c\u95e8\u65e0\u4e00\u9047\uff0c\u5374\u4e8e\u65e0\u610f\u9047\u9ad8\u4eba\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u56db\u5341\u4e5d\u56de\u3000\u6d77\u5916\u5f52\u6765\u6c42\u79d8\u7b08\u3000\u534e\u5c71\u5de7\u9047\u8bd5\u5947\u62db \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u56db\u5341\u4e5d\u56de\u3000\u6d77\u5916\u5f52\u6765\u6c42\u79d8\u7b08\u3000\u534e\u5c71\u5de7\u9047\u8bd5\u5947\u62db \u3000\u3000\u8fd9\u5973\u5b50\u8bf4\u7684\u8bdd\u4f3c\u8d5e\u4f3c\u8bbd\uff0c\u725f\u5b97\u6d9b\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u53e3\u5b50\u5c31\u5e76\u80a9\u5b50\u4e0a\u5427\uff01\u201d\u8bdd\u72b9 \u672a\u4e86\uff0c\u5ffd\u89c1\u5f69\u8679\u8000\u76ee\uff0c\u730e\u730e\u751f\u98ce\uff0c\u539f\u6765\u8fd9\u5973\u5b50\u5df2\u7ecf\u89e3\u4e0b\u4e86\u675f\u8170\u7684\u7ea2\u7ef8\u5e26\u5f53\u4f5c\u6b66\u5668\u4f7f\u7528\uff0c\u500f\u5730 \u5c31\u5411\u725f\u5b97\u6d9b\u5377\u6765\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u6325\u6247\u4e00\u62e8\uff0c\u53ea\u542c\u5f97\u201c\u556a\u201d\u4e00\u58f0\u54cd\uff0c\u6298\u6247\u62e8\u5f00\u7ef8\u5e26\uff0c\u7adf\u4f3c\u89e6\u7740\u786c\u7269\u4e00\u822c\uff0c\u611f\u89c9\u5f97\u5230 \u5bf9\u65b9\u7684\u52b2\u529b\u3002\u725f\u5b97\u6d9b\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u7684\u5185\u529f\u975e\u6b63\u975e\u90aa\uff0c\u597d\u751f\u602a\u5f02\uff0c\u5c45\u7136\u80fd\u591f \u5c06\u4e00\u6761\u7ef8\u5e26\u8d2f\u4ee5\u771f\u529b\uff0c\u59d4\u5b9e\u662f\u4e0d\u53ef\u5c0f\u89d1\u4e86\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u521a\u624d\u548c\u90a3\u7537\u5b50\u5355\u6253\u72ec\u6597\uff0c\u7a0d\u5fae\u5360\u70b9\u4e0a\u98ce\uff0c\u5f85\u5230\u4ed6\u4eec\u592b\u5987\u8054\u624b\uff0c\u653b\u5b88\u767b\u65f6\u6613\u52bf\uff0c\u53ef \u5c31\u611f\u5230\u5e94\u4ed8\u4e3a\u96be\u4e86\u3002 \u3000\u3000\u5bf9\u65b9\u592b\u5987\u4e8c\u4eba\uff0c\u4e00\u4e2a\u4e0e\u725f\u5b97\u6d9b\u8fd1\u8eab\u7f20\u6597\uff0c\u53cc\u638c\u76d8\u65cb\u98de\u821e\uff0c\u6309\u62cd\u64d2\u62ff\uff0c\u638c\u6307\u5288\u6233\u4e4b\u5904\uff0c\u5904 \u5904\u4e0d\u79bb\u725f\u5b97\u6d9b\u7684\u8981\u5bb3\u7a74\u9053\u3002\u4e00\u4e2a\u5728\u4e8c\u4e08\u5f00\u5916\u6325\u821e\u7ef8\u5e26\u6253\u6765\uff0c\u5377\u523a\u8d70\u94bb\uff0c\u62db\u6570\u66f4\u662f\u865a\u5b9e\u83ab\u6d4b\uff0c \u53e4\u602a\u4e4b\u6781\u3002 \u3000\u3000\u90a3\u7537\u5b50\u6240\u53d1\u7684\u638c\u529b\u4e92\u4e3a\u7275\u5f15\uff0c\u725f\u5b97\u6d9b\u5018\u82e5\u4e0e\u4ed6\u5355\u6253\u72ec\u6597\uff0c\u53ef\u4ee5\u7528\u5343\u65a4\u5760\u7684\u91cd\u8eab\u6cd5\u5e94\u4ed8\uff0c \u4f46\u5982\u4eca\u6709\u90a3\u5973\u7684\u4ece\u65c1\u5939\u653b\uff0c\u90a3\u6761\u7ef8\u5e26\u8f7b\u7075\u7fd4\u52a8\uff0c\u77eb\u82e5\u6e38\u9f99\uff0c\u82e5\u7136\u5b9a\u7740\u8eab\u5f62\uff0c\u96be\u80fd\u5e94\u4ed8\uff0c\u725f\u5b97 \u6d9b\u53ea\u597d\u968f\u673a\u5e94\u53d8\uff0c\u500f\u8fdb\u500f\u9000\uff0c\u5ffd\u5b88\u5ffd\u653b\uff0c\u597d\u4e0d\u5bb9\u6613\u5e94\u4ed8\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u9976\u4ed6\u5185\u529f\u6df1\u539a\uff0c\u4e0d\u89c9\u4e5f \u662f\u6ee1\u5934\u5927\u6c57\u3002 \u3000\u3000\u90a3\u7537\u7684\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u770b\u9601\u4e0b\u8fd9\u6276\u6851\u5c9b\u7684\u6b66\u529f\u4e5f\u5e76\u975e\u600e\u4e48\u4e86\u5f97\u5440\u3002\u201d\u90a3\u5973\u7684\u9053\uff1a\u201c\u4ec0\u4e48 \u6276\u6851\u5c9b\u7684\u6b66\u529f\uff0c\u6211\u770b\u4ed6\u6839\u672c\u662f\u5047\u5192\u7684\u3002\u6276\u6851\u4e03\u5b50\u7684\u672c\u9886\u54ea\u4e00\u4e2a\u90fd\u6bd4\u4ed6\u5f3a\uff0c\u90a3\u624d\u662f\u771f\u7684\u3002\u201d\u90a3 \u7537\u7684\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u5c06\u4ed6\u64d2\u4e0b\uff0c\u53ef\u5f97\u597d\u597d\u5730\u62f7\u95ee\u4e00\u987f\u3002\u95ee\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5192\u540d\u884c\u9a97\u3002\u201d\u8fd9\u4e24\u592b \u5987\u5206\u660e\u5df2\u7ecf\u77e5\u9053\u725f\u5b97\u6d9b\u662f\u6276\u6851\u4e00\u6d3e\uff0c\u5374\u6545\u610f\u4e00\u5531\u4e00\u548c\uff0c\u60f3\u628a\u725f\u5b97\u6d9b\u6fc0\u6012\uff1b\u540c\u65f6\u4e5f\u662f\u60f3\u7ed9\u81ea\u5df1 \u627e\u4e2a\u85c9\u53e3\uff0c\u624d\u597d\u628a\u725f\u5b97\u6d9b\u5f53\u4f5c\u654c\u4eba\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u6c89\u4f4f\u4e86\u6c14\uff0c\u6697\u81ea\u601d\u91cf\uff1a\u201c\u8fd9\u4e24\u592b\u59bb\u663e\u7136\u662f\u5fc3\u6000\u6076\u610f\uff0c\u8981\u5411\u6211\u4e0b\u6bd2\u624b\u7684\u4e86\u3002\u6211\u8fd8\u548c \u4ed6\u4eec\u5ba2\u6c14\u4f5c\u751a\uff1f\u6253\u4ed6\u4eec\u4e0d\u8fc7\uff0c\u4e5f\u5f97\u62fc\u4e2a\u4e24\u8d25\u4ff1\u4f24\u3002\u201d \u3000\u3000\u90a3\u7537\u7684\u89c1\u725f\u5b97\u6d9b\u7a81\u7136\u594b\u4e0d\u987e\u8eab\u5730\u731b\u6251\uff0c\u5012\u4e5f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u4e0d\u60f3\u6d3b \u5566\uff01\u201d\u725f\u5b97\u6d9b\u5927\u559d\u9053\uff1a\u201c\u6559\u4f60\u89c1\u8bc6\u6276\u6851\u5c9b\u7684\u6b66\u529f\uff01\u201d\u6298\u6247\u500f\u5730\u4e00\u5f20\u4e00\u5f00\uff0c\u671d\u90a3\u91cc\u7684\u9762\u95e8\u4e00 \u62e8\uff0c\u7275\u5f15\u5bf9\u65b9\u7684\u89c6\u7ebf\uff0c\u6247\u5934\u4e00\u6307\uff0c\u7acb\u5373\u4fbf\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6cd5\uff0c\u70b9\u5bf9\u65b9\u7684\u4e09\u5904\u7a74\u9053\uff0c\u4e24\u5904 \u9ebb\u7a74\uff0c\u4e00\u5904\u6b7b\u7a74\u3002 \u3000\u3000\u90a3\u5973\u7684\u5982\u4f55\u5bb9\u5f97\u725f\u5b97\u6d9b\u5411\u5979\u4e08\u5927\u65bd\u5c55\u6740\u624b\uff1f\u7ef8\u5e26\u77eb\u82e5\u6e38\u9f99\uff0c\u7adf\u7136\u4f7f\u51fa\u4e86\u5c0f\u82b1\u67aa\u7684\u62db\u6570\uff0c \u5411\u725f\u5b97\u6d9b\u7684\u53cc\u773c\u523a\u6765\u3002\u725f\u5b97\u6d9b\u5f20\u53e3\u4e00\u54ac\uff0c\u6298\u6247\u4ecd\u7136\u5411\u90a3\u7537\u7684\u70b9\u53bb\u3002\u4e0d\u6599\u90a3\u5973\u7684\u7ef8\u5e26\u4f7f\u5f97\u5b9e\u5728 \u5947\u5e7b\u65e0\u6bd4\uff0c\u725f\u5b97\u6d9b\u4e00\u54ac\uff0c\u7adf\u662f\u672a\u80fd\u54ac\u7740\u3002 \u3000\u3000\u4e0d\u8fc7\u90a3\u5973\u5b50\u7684\u7ef8\u5e26\u4e5f\u672a\u80fd\u523a\u7740\u725f\u5b97\u6d9b\u7684\u773c\u775b\uff0c\u800c\u662f\u89e6\u53ca\u4ed6\u7684\u9f3b\u5c16\uff0c\u4eba\u4e2d\u662f\u8eab\u4f53\u6700\u654f\u611f\u7684 \u5730\u65b9\u4e4b\u4e00\uff0c\u725f\u5b97\u6d9b\u6253\u4e86\u4e00\u4e2a\u55b7\u568f\uff0c\u771f\u6c14\u6cc4\u4e86\u51e0\u5206\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u8fd9\u4e00\u62db\u662f\u653b\u5411\u90a3\u7537\u5b50\u7684\u4e09\u5904\u7a74\u9053\u7684\uff0c\u5176\u4e2d\u4e00\u5904\u4e43\u662f\u6b7b\u7a74\uff0c\u90a3\u7537\u5b50\u6a2a\u638c\u62a4\u7740\u6b7b\u7a74\uff0c \u53e6\u4e00\u5904\u9ebb\u7a74\u5374\u7ed9\u725f\u5b97\u6d9b\u7528\u91cd\u624b\u6cd5\u70b9\u4e2a\u6b63\u7740\u3002 \u3000\u3000\u53ef\u662f\u725f\u5b97\u6d9b\u7531\u4e8e\u771f\u6c14\u6cc4\u4e86\u51e0\u5206\uff0c\u7ed9\u4ed6\u638c\u529b\u4e00\u9707\uff0c\u5c11\u9633\u7ecf\u8109\u4ea6\u5df2\u53d7\u4e86\u5fae\u4f24\uff0c\u4e0d\u582a\u518d\u6218\u4e86\u3002 \u3000\u3000\u90a3\u5973\u7684\u89c1\u4e08\u592b\u75be\u9000\u4e24\u6b65\u4e4b\u540e\uff0c\u7a81\u7136\u5446\u82e5\u6728\u9e21\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u4e0a\u524d\u5c06\u4ed6\u6276\u7a33\uff0c\u95ee\u9053\uff1a \u201c\u4f60\u600e\u4e48\u5566\uff1f\u201d\u725f\u5b97\u6d9b\u8d81\u6b64\u65f6\u673a\uff0c\u65e9\u5df2\u9003\u4e4b\u592d\u592d\u3002 \u3000\u3000\u90a3\u7537\u7684\u81ea\u884c\u89e3\u4e86\u7a74\u9053\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u53ef\u60dc\u7ed9\u8fd9\u5c0f\u5b50\u8dd1\u4e86\u3002\u201d\u90a3\u5973\u7684\u9053\uff1a\u201c\u8c05\u4ed6\u8dd1\u5f97 \u672a\u8fdc\uff0c\u54b1\u4eec\u8ffd\u5427\uff01\u201d\u6b64\u65f6\u725f\u5b97\u6d9b\u5df2\u7ecf\u8dd1\u8fc7\u4e86\u5c71\u5773\uff0c\u8fd9\u51e0\u53e5\u8bdd\u968f\u98ce\u98d8\u6765\uff0c\u9690\u7ea6\u53ef\u95fb\u3002\u725f\u5b97\u6d9b\u4e5f \u662f\u4e0d\u7981\u6697\u6697\u5403\u60ca\uff1a\u201c\u6211\u7528\u91cd\u624b\u6cd5\u70b9\u4e86\u4ed6\u7684\u9ebb\u7a74\uff0c\u4ed6\u5c45\u7136\u80fd\u591f\u9a6c\u4e0a\u901a\u89e3\u3002\u5018\u82e5\u7ed9\u4ed6\u8ffd\u4e0a\uff0c\u6211\u53ef \u5c31\u8981\u5927\u5927\u7684\u7cdf\u7cd5\u4e86\uff01\u201d \u3000\u3000\u5176\u5b9e\u90a3\u7537\u7684\u867d\u7136\u80fd\u591f\u81ea\u884c\u89e3\u7a74\uff0c\u4f46\u8fd8\u662f\u4e0d\u80fd\u591f\u9a6c\u4e0a\u65bd\u5c55\u8f7b\u529f\u7684\u3002\u800c\u4e14\u6b64\u5904\u8ddd\u79bb\u5f82\u5f95\u5c71\u4e0d \u8fdc\uff0c\u4ed6\u8fd8\u5f97\u63d0\u9632\u7ed9\u9099\u5c71\u6d3e\u524d\u5f80\u5f82\u5f95\u5c71\u7684\u9ad8\u624b\u649e\u4e0a\uff0c\u662f\u4ee5\u4ed6\u7684\u59bb\u5b50\u867d\u7136\u8fd8\u60f3\u53bb\u8ffd\uff0c\u4ed6\u5374\u662f\u4e0d\u80fd \u4e0d\u6709\u6240\u987e\u5fcc\uff0c\u5fc5\u987b\u529d\u6b62\u59bb\u5b50\u4e86\u3002 \u3000\u3000\u4e14\u8bf4\u91d1\u4e16\u9057\u542c\u4e86\u725f\u5b97\u6d9b\u6240\u8bf4\u7684\u906d\u9047\uff0c\u8bf4\u9053\uff1a\u201c\u636e\u6211\u6240\u77e5\uff0c\u4e09\u5341\u5e74\u524d\uff0c\u6709\u4e2a\u963f\u523a\u4f2f\u7684\u6b66\u5b66 \u5927\u5e08\u540d\u5524\u63d0\u6469\u8fbe\u591a\uff0c\u66fe\u4e0e\u5929\u5c71\u5510\u8001\u638c\u95e8\u6bd4\u8d5b\u6500\u767b\u73e0\u7a46\u6717\u739b\u5cf0\uff0c\u7ed3\u679c\u9047\u5230\u96ea\u5d29\u800c\u4ea1\u3002\u6b64\u4eba\u7ec3\u6709 \u9634\u638c\u7684\u529f\u592b\uff0c\u542c\u4f60\u6240\u8bf4\uff0c\u4f60\u6240\u78b0\u5230\u7684\u8fd9\u5bf9\u592b\u5987\uff0c\u90a3\u7537\u7684\u80fd\u591f\u53d1\u51fa\u521a\u67d4\u517c\u6d4e\u7684\u638c\u529b\uff0c\u5f88\u53ef\u80fd\u5c31 \u662f\u63d0\u6469\u8fbe\u591a\u5728\u4e2d\u58eb\u7684\u4f20\u4eba\u3002\u201d \u3000\u3000\u8bf4\u81f3\u6b64\u5904\uff0c\u91d1\u4e16\u9057\u559f\u7136\u53f9\u9053\uff1a\u201c\u8fd9\u8bb8\u591a\u6b66\u5b66\u9ad8\u624b\uff0c\u805a\u96c6\u4e2d\u539f\uff0c\u5374\u7ed9\u6e05\u5ef7\u7f51\u7f57\u4e86\u53bb\uff0c\u53ea\u6015 \u6b66\u6797\u4ece\u6b64\u66f4\u591a\u4e8b\u4e86\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u5018\u82e5\u53ea\u662f\u90a3\u592b\u5987\u4e8c\u4eba\uff0c\u91d1\u5927\u4fa0\u4e0d\u4f1a\u7528\u4e0a\u2018\u8bb8\u591a\u2019\u3002\u83ab\u975e\u91d1\u5927 \u4fa0\u6240\u63e9\u7684\u90a3\u8bb8\u591a\u6b66\u5b66\u9ad8\u624b\uff0c\u4e5f\u5305\u62ec\u2018\u6276\u6851\u4e03\u5b50\u2019\u5728\u5185\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u6b63\u8981\u52a8\u95ee\uff0c\u53ea\u542c\u5f97\u91d1\u4e16\u9057\u5df2\u5148\u8bf4\u9053\uff1a\u201c\u725f\u5148\u751f\uff0c\u4f60\u53ef\u77e5\u9053\u90a3\u4e24\u592b\u5987\u4e3a\u4f55\u8981\u628a\u4f60\u5f53 \u4f5c\u654c\u4eba\u5417\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u665a\u8f88\u6b63\u662f\u60f3\u4e0d\u901a\u5176\u4e2d\u7684\u7f18\u6545\uff0c\u8bf7\u91d1\u5927\u4fa0\u6307\u6559\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u8bf4\u51fa\u6765\u4f60\u4e0d\u8981\u4f24\u5fc3\uff0c\u6211\u5df2\u78b0\u4e0a\u4f60\u7684\u540c\u95e8\u4e86\uff0c\u4ed6\u4eec\u5c31\u662f\u90a3\u4e24\u592b\u5987\u6240\u8bf4\u7684\u6276\u6851 \u4e03\u5b50\u4e86\uff01\u201d \u3000\u3000\u725f\u5b97\u6d9b\u5927\u5403\u4e00\u60ca\u3001\u5df2\u7ecf\u731c\u5230\u4e86\u51e0\u5206\uff0c\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u662f\u5728\u54ea\u91cc\u78b0\u4e0a\u7684\uff1f\u201d\u91d1\u4e16\u9057 \u9053\uff1a\u201c\u5c31\u662f\u5728\u5c0f\u5f92\u5bb6\u4e2d\uff01\u201d \u3000\u3000\u725f\u5b97\u6d9b\u867d\u7136\u65e9\u5df2\u6599\u5230\u51e0\u5206\uff0c\u4f46\u8ba4\u91d1\u4e16\u9057\u53e3\u4e2d\u5f97\u5230\u8bc1\u5b9e\uff0c\u4ecd\u662f\u4e0d\u7981\u9a87\u7136\u5931\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u539f \u6765\u4ed6\u4eec\u5c31\u662f\u6b27\u9633\u575a\u6240\u8bf4\u7684\u90a3\u4e2a\u4ec0\u4e48\u8428\u603b\u7ba1\u8bf7\u6765\u7684\u2018\u9ad8\u624b\u201d\u7adf\u7136\u8dd1\u5230\u6c5f\u5927\u4fa0\u5bb6\u91cc\u53bb\u6363\u4e71\u4e48\uff1f\u201d \u3000\u3000\u91d1\u4e16\u9057\u5c1a\u672a\u77e5\u9053\u6b27\u9633\u575a\u7684\u4e8b\u60c5\uff0c\u4ef2\u957f\u7edf\u548c\u4ed6\u8bf4\u4e86\uff0c\u91d1\u4e16\u9057\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6050\u6015\u6b63\u662f \u8fd9\u6837\u3002\u4e0d\u4ec5\u4ed6\u4eec\uff0c\u8fde\u4f60\u78b0\u89c1\u7684\u90a3\u5bf9\u592b\u5987\uff0c\u4e5f\u662f\u548c\u4ed6\u4eec\u4e00\u515a\u7684\u3002\u201d \u3000\u3000\u5f53\u4e0b\u91d1\u4e16\u9057\u628a\u6628\u665a\u4e0e\u90a3\u4e03\u4eba\u4ea4\u624b\u7684\u7ecf\u8fc7\uff0c\u7279\u5730\u4e3a\u725f\u5b97\u6d9b\u518d\u8bf4\u4e00\u904d\uff0c\u5e76\u4e14\u628a\u8fd9\u4ef6\u4e8b\u60c5\u7684\u6765 \u9f99\u53bb\u8109\uff0c\u4e5f\u90fd\u539f\u539f\u672c\u672c\u5730\u544a\u8bc9\u4e86\u4ed6\u3002\u725f\u5b97\u6d9b\u542c\u4e86\uff0c\u505a\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u8981\u77e5\u725f\u5b97\u6d9b\u4e43\u662f\u4e00\u5fc3\u4e00\u610f\u8dd1\u5230\u4e2d\u539f\u6765\u5bfb\u8bbf\u540c\u95e8\uff0c\u5e0c\u671b\u80fd\u591f\u590d\u5174\u672c\u6d3e\u3001\u91cd\u5149\u95e8\u6237\u7684\u3002\u5374\u4e0d \u6599\u4ed6\u7684\u540c\u95e8\u7adf\u7136\u6295\u9760\u4e86\u671d\u5ef7\uff0c\u8fd8\u4e0e\u4ed6\u6240\u666f\u4ef0\u7684\u91d1\u5927\u4fa0\u4e3a\u654c\uff0c\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u5b9e\u5728\u662f\u4ed6\u505a\u68a6\u4e5f\u60f3 \u4e0d\u5230\u7684\u3002 \u3000\u3000\u91d1\u4e16\u9057\u5b89\u6170\u4ed6\u9053\uff1a\u201c\u8bf4\u4e0d\u5b9a\u4ed6\u4e5f\u662f\u548c\u4f60\u4e00\u6837\uff0c\u4ece\u6d77\u5916\u521d\u5230\u4e2d\u539f\uff0c\u5bf9\u4e2d\u539f\u7684\u5c40\u52bf\u592a\u8fc7\u9694 \u819c\uff0c\u4e00\u65f6\u5c1a\u672a\u5206\u5f97\u6e05\u662f\u975e\u9ed1\u767d\uff0c\u4ee5\u81f4\u53d7\u4e86\u8428\u798f\u9f0e\u7684\u7b3c\u7edc\u3002\u5982\u679c\u4f60\u529d\u5f97\u4ed6\u4eec\u53ca\u65e9\u56de\u5230\u6b63\u8def\u6765\uff0c \u8fd9\u5012\u662f\u529f\u5fb7\u65e0\u91cf\u5462\uff01 \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\u3002\u6211\u4e5f\u662f\u60f3\u627e\u7740\u4ed6\u4eec\u518d\u8bf4\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u5982\u679c\u4ed6\u4eec\u6267\u8ff7\u4e0d\u609f\uff0c\u53cd\u989c\u76f8\u5411\uff0c\u90a3\u65f6\u4f60\u5c82\u4e0d\u662f\u81ea\u6295\u7f57\u7f51\uff1f\u6b64\u4e8b\u8fd8\u5f53\u4e09\u601d\u800c \u884c\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u6211\u4eec\u6276\u6851\u4e00\u6d3e\uff0c\u5148\u7956\u8ba2\u4e0b\u7684\u6212\u5f8b\uff0c\u662f\u51b3\u4e0d\u8bb8\u620f\u5bb3\u540c\u95e8\u7684\u3002\u6211\u60f3\u4ed6\u4eec\u7eb5\u7136\u8bef \u5165\u6b67\u9014\uff0c\u8fd9\u540c\u95e8\u4e4b\u60c5\uff0c\u603b\u8be5\u8fd8\u6709\u5427\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u53eb\u5316\u89c1\u4e8b\u89c1\u5f97\u591a\u4e86\uff0c\u4e00\u4e2a\u4eba\u5982\u679c\u5229\u7984\u718f\u5fc3\uff0c\u6050\u6015\u516d\u4eb2\u90fd\u4e0d \u8ba4\u5462\uff0c\u4e0d\u7ba1\u4f60\u662f\u4ec0\u4e48\u540c\u95e8\u4e0d\u540c\u95e8\u4e86\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u7b11\u9053\uff1a\u201c\u4ef2\u5e2e\u4e3b\uff0c\u4f60\u770b\u4eba\u504f\u91cd\u4e8e\u770b\u4ed6\u574f\u7684\u4e00\u9762\uff0c\u6211\u5c11\u5e74\u4e4b\u65f6\u4e5f\u662f\u5982\u6b64\u7684\u3002\u5176\u5b9e\u574f \u5230\u5e95\u7684\u4eba\u56fa\u7136\u4e0d\u662f\u6ca1\u6709\uff0c\u4f46\u66f4\u591a\u7684\u4f8b\u5b50\u5374\u662f\u4e5f\u6709\u597d\u7684\u4e00\u9762\u7684\u3002\u6bd4\u5982\u6211\u7684\u5e08\u7236\u6bd2\u9f99\u5c0a\u8005\u5c31\u662f\u5982 \u6b64\u3002\u6240\u4ee5\u4f5b\u5bb6\u8bf4\u8981\u666e\u6e21\u4f17\u751f\uff0c\u5112\u5bb6\u8bf4\u8981\u4e0e\u4eba\u4e3a\u5584\uff0c\u90fd\u662f\u540c\u4e00\u4e2a\u610f\u601d\u5728\u8fd9\u70b9\u4e0a\uff0c\u6211\u4eec\u662f\u540c\u610f \u2018\u4eba\u4e4b\u521d\uff0c\u6027\u672c\u5584\u2019\u7684\u8bf4\u6cd5\u7684\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u8fd8\u662f\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u53eb\u5316\u6ca1\u6709\u8bfb\u8fc7\u4ec0\u4e48\u4e66\uff0c\u4f46\u4f9d\u6211\u770b\u6765\u2018\u4eba\u4e4b\u521d\uff0c\u6027\u672c \u5584\u2019\u7684\u8bf4\u6cd5\u5b9e\u662f\u4e0d\u80fd\u6210\u7acb\u3002\u4eba\u6709\u5404\u79cd\u5404\u6837\u7684\u4eba\uff0c\u53eb\u5316\u5b50\u7684\u513f\u5b50\u548c\u505a\u5927\u5b98\u7684\u513f\u5b50\u5c31\u51b3\u4e0d\u4e00\u6837\uff0c \u4ec0\u4e48\u2018\u6027\u5584\uff0c\u6027\u6076\u2019\u6839\u672c\u5c31\u662f\u7cca\u6d82\u7684\u8bf4\u6cd5\u4e86\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u542c\u4e86\uff0c\u4e5f\u89c9\u6709\u7406\uff0c\u4f46\u5374\u7b11\u9053\uff1a\u201c\u90a3\u4eba\u6027\u96be\u9053\u5c31\u4e0d\u4f1a\u6539\u5417\uff1f\u4f8b\u5982\u5415\u56db\u5a18\u4e5f\u662f\u4e66\u9999\u4e16 \u5bb6\uff0c\u5979\u4e5f\u4e0d\u662f\u4f60\u4eec\u4e10\u5e2e\u7684\u597d\u670b\u53cb\u5417\uff1f\u8fd9\u53c8\u8be5\u600e\u4e48\u8bf4\u5462\uff1f\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u90a3\u662f\u56e0\u4e3a\u6e05\u5ef7\u6284\u4e86\u5979\u7684\u5bb6\uff0c\u5979\u4e0d\u80fd\u591f\u5b89\u5b89\u9759\u9759\u7684\u5728\u5bb6\u91cc\u8bfb\u4e66\u505a\u624d\u5973\u4e86\uff0c\u8fd9 \u624d\u903c\u4e0a\u6881\u5c71\uff0c\u548c\u6211\u4eec\u8fd9\u4e00\u7c7b\u4eba\u4ea4\u670b\u53cb\u7684\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u6700\u5c0a\u656c\u5415\u56db\u5a18\uff0c\u542c\u4e86\u5fc3\u4e2d\u8fd8\u662f\u4e0d\u670d\u3002\u4e0d\u8fc7\u4ef2\u957f\u7edf\u8bf4\u7684\u4e5f\u662f\u4e8b\u5b9e\uff0c\u91d1\u4e16\u9057\u4e00\u65f6\u65e0\u8a00 \u53ef\u5bf9\uff0c\u53ea\u53ef\u8bf4\u9053\uff1a\u201c\u5373\u4f7f\u6ca1\u6709\u5979\u7239\u7239\u8fd9\u5b97\u6848\u4ef6\uff0c\u6211\u76f8\u4fe1\u5415\u56db\u5a18\u4e5f\u51b3\u4e0d\u4f1a\u5728\u5bb6\u91cc\u5f53\u5c0f\u59d0\uff0c\u7ec8\u5fc5 \u8981\u6210\u4e3a\u4e00\u4ee3\u5973\u4fa0\u7684\u3002\u201d \u3000\u3000\u4ef2\u957f\u7edf\u9053\uff1a\u201c\u6216\u8bb8\u5982\u6b64\uff0c\u4f46\u603b\u6709\u5b83\u7684\u539f\u56e0\u3002\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u4e0d\u76f8\u4fe1\u4e00\u4e2a\u4eba\u4e00\u751f\u4e0b\u6765\uff0c\u5c31\u6ce8 \u5b9a\u4e86\u662f\u6709\u6761\u4e00\u79cd\u4eba\u6027\u3002\u201d \u3000\u3000\u516c\u5b59\u5b8f\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u4f3c\u4e4e\u8bf4\u7684\u90fd\u5f88\u6709\u7406\u7531\uff0c\u6211\u5374\u662f\u8d8a\u542c\u8d8a\u7cca\u6d82\u4e86\u3002\u8fd8\u662f\u56de\u5230\u6b63\u9898\u6765 \u5427\u3002\u201d\u5bf9\u8fd9\u4e2a\u95ee\u9898\uff0c\u5176\u5b9e\u5927\u5bb6\u90fd\u6ca1\u6709\u597d\u597d\u60f3\u8fc7\uff0c\u4e5f\u5f04\u4e0d\u6e05\u695a\u8c01\u662f\u8c01\u975e\uff0c\u56e0\u6b64\u53ea\u597d\u529d\u4ed6\u4eec\u6682\u505c \u4e89\u8bba\u3002\u4e0d\u8fc7\uff0c\u542c\u4e86\u4ed6\u4eec\u53cc\u65b9\u7684\u7406\u7531\uff0c\u5bf9\u5927\u5bb6\u4e5f\u90fd\u6709\u70b9\u542f\u53d1\u3002 \u3000\u3000\u91d1\u4e16\u9057\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4ece\u725f\u5148\u751f\u7684\u4e8b\u60c5\u4e00\u626f\u626f\u5230\u4e86\u4ec0\u4e48\u6027\u5584\u6027\u6076\uff0c\u53c8\u626f\u53c8\u626f\u5230\u4e86\u5415 \u56db\u5a18\u8eab\u4e0a\uff0c\u8fd9\u771f\u662f\u79bb\u9898\u4e07\u4e08\u4e86\u3002\u725f\u5148\u751f\uff0c\u54b1\u4eec\u521a\u624d\u8bf4\u5230\u54ea\u91cc\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u4ef2\u5e2e\u4e3b\u62c5\u5fc3\u4ed6\u4eec\u4e0d\u987e\u540c\u95e8\u4e4b\u4e49\uff0c\u52a0\u5bb3\u4e8e\u6211\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u90a3\u4f60\u6253\u7b97\u5982\u4f55\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u6211\u8fd8\u662f\u6253\u7b97\u53bb\u627e\u4ed6\u4eec\uff0c\u5c31\u53ea\u6015\u627e\u4ed6\u4eec\u4e0d\u7740\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u4ed6\u4eec\u82e5\u662f\u4e0a\u4e86\u8428\u798f\u9f0e\u7684\u5708\u5957\uff0c\u5f53\u4e86\u4ed6\u95e8\u5ba2\u7684\u8bdd\uff0c\u4f60\u5230\u4eac\u4e2d\uff0c\u4e00\u5b9a\u53ef\u4ee5\u6253\u542c \u5230\u4ed6\u4eec\u7684\u6d88\u606f\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u60f3\u4e86\u4e00\u60f3\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u80fd\u591f\u52a8\u5f97\u4ed6\u4eec\u56de\u5934\u56fa\u7136\u6700\u597d\uff0c\u4f46\u4ef2\u5e2e\u4e3b\u7684\u62c5\u5fc3\u4e5f\u4e0d\u662f\u8fc7 \u8651\uff0c\u5e94\u8be5\u6709\u4e2a\u9632\u5907\u624d\u5bf9\uff0c\u8fd9\u6837\u5427\uff0c\u6211\u548c\u4f60\u4e00\u540c\u4e0a\u4eac\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u559c\u51fa\u671b\u5916\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u52b3\u70e6\u91d1\u5927\u4fa0\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u662f\u5355\u5355\u4e3a\u4e86\u4f60\u7684\u4e8b\u60c5\uff0c\u6211\u4e8c\u5341\u5e74\u6ca1\u6709\u56de\u8fc7\u4e2d\u539f\uff0c\u6b63\u60f3\u85c9\u7740\u4e0a\u4eac\u4e4b \u4fbf\uff0c\u63a2\u8bbf\u6211\u7684\u51e0\u4f4d\u8001\u670b\u53cb\u3002\u5230\u4e86\u4eac\u57ce\uff0c\u4f60\u53ef\u4ee5\u7528\u6613\u5bb9\u4e39\u53d8\u5316\u9762\u8c8c\u53bb\u627e\u4ed6\u4eec\uff0c\u4ee5\u514d\u7ed9\u90a3\u4e24\u592b\u5987 \u8ba4\u51fa\uff0c\u4e00\u6709\u4ec0\u4e48\u6d88\u606f\u5c31\u544a\u8bc9\u6211\uff0c\u6211\u81ea\u4f1a\u89c1\u673a\u800c\u884c\u3002\u201d \u3000\u3000\u6709\u91d1\u4e16\u9057\u76f8\u4f34\uff0c\u7b49\u4e8e\u662f\u8eab\u8fb9\u591a\u4e86\u4e00\u4e2a\uff0c\u53ef\u4ee5\u4fdd\u5f97\u4e07\u65e0\u4e00\u5931\u7684\u4fdd\u9556\uff0c\u725f\u5b97\u6d9b\u81ea\u662f\u5927\u559c\u8fc7 \u671b\uff0c\u5f53\u4e0b\u4e0e\u91d1\u4e16\u9057\u7ea6\u597d\u4e86\u5728\u5317\u4eac\u76f8\u4f1a\u7684\u65e5\u671f\u5730\u70b9\uff0c\u4fbf\u5373\u544a\u8f9e\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u8d70\u540e\uff0c\u91d1\u4e16\u9057\u95ee\u8d77\u4e8c\u5341\u5e74\u6765\u6b66\u6797\u7684\u53d8\u5316\u548c\u6297\u6e05\u7684\u60c5\u51b5\u3002\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\u6559\u7239\u7239\u5f97 \u77e5\uff0c\u4f60\u7684\u8001\u670b\u53cb\u7afa\u5c1a\u7236\u5df2\u7ecf\u505a\u4e86\u897f\u5317\u4e49\u519b\u7684\u9996\u9886\u4e86\u3002\u201d\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u8fd8\u6709\u5c0f\u91d1\u5ddd\u65b9\u9762\u7684\u4e49\u519b \u9996\u9886\u8427\u5fd7\u8fdc\u4e5f\u662f\u4e00\u4f4d\u597d\u6c49\uff0c\u4e0d\u8fc7\uff0c\u4ed6\u662f\u5e08\u7236\u79bb\u5f00\u4e2d\u539f\u4e4b\u540e\u624d\u51fa\u9053\u7684\uff0c\u4f60\u8001\u4eba\u5bb6\u6050\u6015\u8fd8\u672a\u77e5\u9053 \u5427\uff1f\u201d \u3000\u3000\u5168\u4e16\u9057\u5927\u4e3a\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u95f4\uff0c\u51fa\u4e86\u8fd9\u8bb8\u591a\u6b66\u6797\u65b0\u79c0\uff0c\u5f53\u771f\u662f\u53ef\u559c\u53ef\u8d3a\uff0c\u66f4\u96be\u5f97 \u7684\u662f\u8001\u670b\u53cb\u4eec\u4e5f\u90fd\u662f\u5b9d\u5200\u672a\u8001\uff0c\u6b63\u5728\u4e8e\u7740\u8f70\u8f70\u70c8\u70c8\u7684\u4e8b\u4e1a\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u7afa\u8001\u524d\u8f88\u867d\u7136\u653e\u5f03\u4e86\u897f\u660c\uff0c\u4f46\u5df1\u5728\u5927\u51c9\u5c71\u4e2d\u5efa\u7acb\u4e86\u6297\u6e05\u7684\u57fa\u4e1a\uff0c\u6839\u57fa\u662f\u624e \u5f97\u66f4\u6df1\u4e86\u3002\u6155\u534e\u5e08\u4f84\u6b63\u5728\u90a3\u91cc\u505a\u4ed6\u7684\u519b\u5e05\u5462\u3002\u201d \u3000\u3000\u53f6\u51b2\u9704\u7b11\u9053\uff1a\u201c\u4ed6\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u61c2\u5f97\u505a\u4ec0\u4e48\u519b\u5e05\uff1f\u201d \u3000\u3000\u6c5f\u6d77\u5929\u9053\uff1a\u201c\u6155\u534e\u5f88\u662f\u4e0d\u9519\uff0c\u8bb2\u5230\u884c\u519b\u7528\u5175\u4e4b\u9053\uff0c\u6211\u8fd9\u4e2a\u505a\u5e08\u7236\u7684\u8fd8\u8fdc\u8fdc\u4e0d\u5982\u4ed6\u5462\uff01\u56db \u5e74\u524d\uff0c\u4ed6\u7387\u9886\u4e00\u652f\u4e49\u519b\uff0c\u89e3\u5c0f\u91d1\u5ddd\u4e4b\u56f4\uff0c\u5404\u8def\u82f1\u96c4\uff0c\u65e0\u4e0d\u4f69\u670d\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u9053\uff0c\u201c\u51b2\u9704\uff0c\u606d\u559c\u4f60\u6709\u5982\u6b64\u4f73\u513f\u3002\u9010\u6d41\uff0c\u4f60\u7684\u8f88\u4efd\u867d\u7136\u662f\u5e08\u53d4\uff0c\u53ef\u8fd8\u8981\u597d\u597d\u7684\u5411 \u4f60\u8fd9\u4f4d\u5e08\u4f84\u5b66\u5b66\u5462\uff01\u6211\u770b\u4f60\u7684\u529f\u592b\u867d\u7136\u6709\u4e9b\u589e\u8fdb\uff0c\u4f46\u5374\u8fd8\u50cf\u987d\u76ae\u7684\u5c0f\u513f\uff0c\u600e\u914d\u505a\u6155\u534e\u7684\u5e08 \u53d4\uff1f\u201d\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u91d1\u5927\u4fa0\uff0c\u4f60\u5c11\u5e74\u7684\u65f6\u5019\uff0c\u6050\u6015\u6bd4\u4f60\u7684\u513f\u5b50\u8fd8\u66f4\u987d\u76ae\u5427\u3002\u201d\u91d1\u9010\u6d41\u626e\u4e86 \u4e2a\u9b3c\u8138\uff0c\u5e94\u4e86\u4e00\u4e2a\u201c\u662f\u201d\u5b57\u3002 \u3000\u3000\u53f6\u51b2\u9704\u63a9\u4e0d\u4f4f\u5185\u5fc3\u7684\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u592a\u8fc7\u5938\u5956\u4ed6\u4e86\u3002\u8fd9\u90fd\u662f\u6c5f\u5e08\u5144\u6559\u5bfc\u4e4b\u529f\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u8c26\u865a\u4e86\u51e0\u53e5\uff0c\u8bf4\u9053\uff1a\u201c\u6e05\u5ef7\u628a\u5927\u51c9\u5c71\u548c\u5c0f\u91d1\u5ddd\u4e24\u5730\u7684\u4e49\u519b\u5f53\u4f5c\u773c\u4e2d\u9489\uff0c\u73b0\u5728\u6b63\u5728 \u9634\u8c0b\u5bf9\u4ed8\u7afa\u8001\u524d\u8f88\u3002\u201d\u5f53\u4e0b\u628a\u521a\u624d\u4ece\u5c01\u5b50\u8d85\u90a3\u91cc\u542c\u6765\u7684\u6d88\u606f\u544a\u8bc9\u5e08\u7236\u3002 \u3000\u3000\u91d1\u4e16\u9057\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u4eec\u5c31\u8be5\u65e9\u65e5\u52a8\u8eab\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5b69\u513f\u6b63\u60f3\u660e\u767d\u52a8\u8eab\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u8981\u56de\u53bb\u590d\u547d\uff0c\u6b63\u597d\u7ed9\u4f60\u5e26\u8def\u3002\u7afa\u8001\u524d\u8f88\u8fd9\u652f\u4e49\u519b\u5df2\u7ecf\u8f6c\u79fb\u5230\u5bc6\u6797\u6df1 \u5904\uff0c\u5916\u4eba\u662f\u5f88\u96be\u627e\u5230\u7684\u3002\u201d\u4ed6\u6240\u8bf4\u7684\u201c\u6211\u4eec\u201d\uff0c\u5f53\u7136\u662f\u5305\u62ec\u5c01\u5999\u5ae6\u5728\u5185\u3002\u4ed6\u4eec\u662f\u65e9\u5df2\u5728\u5927\u51c9 \u5c71\u6210\u4e86\u5a5a\u7684\u3002\u5c01\u5b50\u8d85\u5f88\u662f\u6b22\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u5ae6\u513f\uff0c\u4f46\u613f\u4f60\u4eec\u65e9\u65e5\u6210\u529f\u56de\u6765\u3002\u6211\u8fd9\u4e00\u751f\u8d70\u9519\u4e86 \u8def\uff0c\u53ea\u6709\u5e0c\u671b\u4f60\u4eec\u66ff\u6211\u7a0d\u8d4e\u7f6a\u8fc7\u4e86\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u65e9\u5df2\u548c\u91d1\u9010\u6d41\u8bf4\u597d\u4e86\u4e00\u540c\u53bb\u7684\uff0c\u4f46\u5728\u672a\u6765\u7684\u516c\u5a46\u9762\u524d\uff0c\u5374\u662f\u4e0d\u597d\u610f\u601d\u51fa\u53e3\u3002 \u3000\u3000\u674e\u6566\u77e5\u9053\u5979\u7684\u5fc3\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u53f2\u5e2e\u4e3b\uff0c\u4f60\u653e\u5fc3\u548c\u91d1\u5927\u4fa0\u540c\u53bb\uff0c\u5e2e\u4e2d\u4e4b\u4e8b\uff0c\u6709\u6211\u6599\u7406\uff0c\u8fd8 \u6709\u516c\u5b59\u8235\u4e3b\u7684\u7167\u987e\uff0c\u60f3\u4e0d\u81f3\u4e8e\u51fa\u4ec0\u4e48\u5c94\u5b50\u7684\u3002\u201d \u3000\u3000\u5389\u5357\u661f\u548c\u516c\u5b59\u71d5\u4e5f\u5f88\u60f3\u56de\u5230\u5927\u51c9\u5c71\u4e2d\u548c\u670b\u53cb\u4eec\u805a\u4f1a\uff0c\u4f46\u56e0\u5929\u9b54\u6559\u7684\u4f59\u4e8b\u672a\u4e86\uff0c\u53ea\u597d\u62dc\u6258 \u91d1\u9010\u6d41\u5148\u53bb\u77e5\u4f1a\u4e00\u58f0\u3002 \u3000\u3000\u91d1\u4e16\u9057\u60f3\u4e86\u4e00\u60f3\uff0c\u5ffd\u5730\u7b11\u9053\uff1a\u201c\u9010\u6d41\uff0c\u4f60\u548c\u53f2\u59d1\u5a18\u540d\u4efd\u672a\u5b9a\uff0c\u4e00\u8def\u540c\u884c\uff0c\u6050\u6709\u4e0d\u4fbf\u3002\u4e0d \u5982\u4f60\u4eec\u5c31\u5148\u884c\u8ba2\u4e86\u5a5a\u5427\uff0c\u5f85\u4f60\u4ece\u9752\u6d77\u56de\u6765\uff0c\u6211\u518d\u5230\u5c0f\u91d1\u5ddd\u4e3b\u6301\u4f60\u4eec\u7684\u5a5a\u793c\u3002\u201d \u3000\u3000\u4f17\u4eba\u8f70\u7136\u53eb\u597d\uff0c\u53f2\u7ea2\u82f1\u6ee1\u6000\u6b22\u60a6\uff0c\u988a\u6655\u8f7b\u7ea2\uff0c\u4f4e\u4e0b\u4e86\u5934\u3002 \u3000\u3000\u8c37\u4e4b\u534e\u89e3\u4e0b\u4e86\u201c\u971c\u534e\u5251\u201d\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u5e08\u7236\u7ed9\u6211\u7684\u4f20\u5bb6\u4e4b\u5b9d\uff0c\u73b0\u5728\u6211\u7ed9\u4f60\u5f53\u4f5c\u8058 \u7269\uff0c\u4e5f\u5f53\u4f5c\u6211\u7684\u89c1\u9762\u793c\u5427\u3002\u201d \u3000\u3000\u201c\u971c\u534e\u5251\u201d\u662f\u5415\u56db\u5a18\u5f53\u5e74\u6240\u7528\u7684\u5b9d\u5251\uff0c\u96cd\u6b63\u7687\u5e1d\u5c31\u662f\u7ed9\u8fd9\u67c4\u5b9d\u5251\u6740\u7684\uff0c\u662f\u4e00\u628a\u975e\u5e38\u51fa\u540d \u7684\u5b9d\u5251\u3002\u4ef2\u957f\u7edf\u7b11\u9053\uff1a\u201c\u5230\u5e95\u662f\u5a46\u5a46\u75bc\u5ab3\u5987\u591a\u4e9b\uff0c\u8fd9\u628a\u5b9d\u5251\uff0c\u5979\u8fde\u513f\u5b50\u90fd\u820d\u4e0d\u5f97\u7ed9\u7684\u3002\u201d \u3000\u3000\u6c5f\u6d77\u5929\u5374\u7b11\u9053\uff1a\u201c\u5e08\u5f1f\u6709\u4e86\u7384\u94c1\u5b9d\u5251\uff0c\u53f2\u5f1f\u59b9\u4e5f\u8be5\u6709\u4e00\u628a\u8db3\u4ee5\u5339\u654c\u7684\u5b9d\u5251\u624d\u5bf9\u3002\u8981\u4e0d\u7136 \u5c82\u4e0d\u662f\u8981\u7ed9\u4ed6\u6b3a\u8d1f\u3002\u201d\u53f6\u8c37\u4e2d\u83b2\u9053\uff1a\u201c\u5e08\u5f1f\u600e\u4e48\u4f1a\u6b3a\u8d1f\u53f2\u59d1\u5a18\uff0c\u4f60\u8fd9\u8bdd\u5c31\u5148\u8bf4\u5f97\u4e0d\u5bf9\u3002\u201d\u6c5f \u6d77\u5929\u4e00\u5411\u4e0d\u80af\u8a00\u7b11\u7684\uff0c\u6b64\u65f6\u4e3a\u4e86\u8ba8\u5e08\u7236\u5e08\u6bcd\u7684\u6b22\u5fc3\uff0c\u7834\u4f8b\u8bf4\u8d77\u7b11\u6765\uff0c\u4f17\u4eba\u65e0\u4e0d\u8ddf\u7740\u5927\u7b11\u3002 \u3000\u3000\u91d1\u4e16\u9057\u7ed9\u4ed6\u4eec\u4e3b\u6301\u4e86\u7b80\u5355\u800c\u53c8\u5e84\u91cd\u7684\u8ba2\u5a5a\u7684\u4eea\u5f0f\uff0c\u5f53\u665a\u91cd\u5f00\u7b75\u5e2d\uff0c\u7fa4\u96c4\u95f9\u9152\uff0c\u4e00\u76f4\u95f9\u5230 \u5929\u5149\u3002 \u3000\u3000\u91d1\u9010\u6d41\u3001\u53f2\u7ea2\u82f1\u4e0e\u79e6\u5143\u6d69\u3001\u5c01\u5999\u5ae6\u4e24\u5bf9\u5c0f\u592b\u59bb\uff0c\u5929\u4e00\u4eae\u4e5f\u5c31\u4e0e\u7fa4\u96c4\u544a\u522b\uff0c\u524d\u5f80\u5927\u51c9\u5c71 \u4e86\u3002 \u3000\u3000\u4e00\u5bf9\u662f\u5df2\u7ecf\u6210\u4e86\u4eb2\u7684\u592b\u5987\uff0c\u4e00\u5bf9\u662f\u521a\u521a\u8ba2\u4e86\u5a5a\u7684\u60c5\u4eba\uff0c\u8def\u4e0a\u81ea\u662f\u6709\u4e0d\u5c11\u7684\u775b\u5929\u98ce\u5149\uff0c\u4e0d \u53bb\u7ec6\u8868\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u7ecf\u8fc7\u534e\u5c71\u811a\u4e0b\uff0c\u6b63\u662f\u5c06\u8fd1\u9ec4\u660f\u7684\u65f6\u5206\u3002\u4ef0\u671b\u9ad8\u8038\u4e91\u9704\u7684\u534e\u5c71\uff0c\u53ea\u89c1\u90a3\u79c0\u4e3d\u591a\u5f69\u7684 \u7fa4\u5cf0\uff0c\u62e5\u7740\u832b\u832b\u6eda\u52a8\u7684\u4e91\u6d77\uff0c\u62ab\u7740\u707f\u70c2\u8df3\u8361\u7684\u6d41\u971e\uff0c\u5728\u91d1\u8272\u7684\u5915\u9633\u4e2d\uff0c\u8d6b\u5dcd\u5dcd\u64ce\u5929\u538b\u5730\uff0c\u8bf4 \u4e0d\u5c3d\u7684\u5e84\u4e25\u6c14\u8c61\uff0c\u5ced\u62d4\u96c4\u59ff\u3002\u79e6\u5143\u6d69\u4ece\u672a\u5230\u8fc7\u534e\u5c71\uff0c\u4e0d\u7531\u5f97\u8d5e\u9053\uff1a\u201c\u53e4\u4eba\u8bf4\u4e94\u5cb3\u540d\u74dc\u9996\u63a8\u897f \u5cb3\uff0c\u5f53\u771f\u662f\u540d\u4e0d\u865a\u4f20\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5728\u540d\u5c71\u811a\u4e0b\uff0c\u4e5f\u7981\u4e2a\u4f4f\u9038\u5174\u9065\u98de\u3002\u8bf4\u9053\uff1a\u201c\u6211\u5012\u662f\u5230\u8fc7\u4e24\u6b21\u534e\u5c71\uff0c\u53ef\u60dc\u90fd\u662f\u5306\u5306 \u6765\u53bb\uff0c\u65e0\u6687\u4e00\u6e38\u3002\u4eca\u665a\u54b1\u4eec\u53cd\u6b63\u662f\u8981\u627e\u5bbf\u5934\uff0c\u4e0d\u5982\u5c31\u5230\u6e05\u98ce\u89c2\u4f4f\u4e00\u665a\u5427\u3002\u6f31\u77f3\u9053\u4eba\u4e0d\u5e78\u8eab \u6b7b\uff0c\u54b1\u4eec\u4e5f\u8be5\u53bb\u4ed6\u7684\u7075\u524d\u4e0a\u4e00\u7076\u9999\u7684\u3002\u201d \u3000\u3000\u6f31\u77f3\u9053\u4eba\u662f\u7ed9\u9633\u6d69\u5047\u5192\u5929\u9b54\u6559\u6559\u4e3b\u5389\u5357\u661f\u4e4b\u540d\u5bb3\u6b7b\u7684\uff0c\u53f2\u7ea2\u7b11\u82f1\u60f3\u8d77\u4e86\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4e5f\u662f \u5f88\u89c9\u96be\u8fc7\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u90a3\u65e5\u65e0\u6687\u9001\u846c\uff0c\u4eca\u65e5\u7406\u8be5\u540a\u5501\uff0c\u4e0d\u8fc7\u6050\u6015\u8fd8\u662f\u6ca1\u6709\u65f6\u95f4\u53bb\u6e38 \u89c8\u534e\u5c71\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u2018\u53ec\u6653\u592a\u534e\u4fef\u54b8\u4eac\uff0c\u5929\u5916\u4e09\u5cf0\u524a\u4e0d\u6210\u3002\u592a\u534e\u5cf0\u5934\u7389\u4e95\u8fc8\uff0c\u82b1\u5f00\u5341\u4e08\u85d5\u5982 \u8239\u3002\u2019\u8fd9\u4e9b\u53e4\u4eba\u63cf\u5199\u534e\u5c71\u7684\u8bd7\u53e5\uff0c\u80fd\u4e0d\u4ee4\u4eba\u5fc3\u5411\u5f80\u4e4b\uff01\u4e09\u8fc7\u534e\u5c71\uff0c\u4e0d\u8bc6\u534e\u5c71\u771f\u9762\u76ee\uff0c\u90a3\u4e5f\u662f \u4e00\u4ef6\u61be\u4e8b\u554a\uff01\u54b1\u4eec\u4ee5\u540e\u6bcf\u5929\u591a\u8d70\u4e00\u7a0b\uff0c\u660e\u65e5\u75db\u75db\u5feb\u5feb\u6e38\u4e00\u5929\u5c71\u5427\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u7b11\u9053\uff1a\u201c\u4f60\u7528\u53e4\u4eba\u7684\u8bd7\u53e5\uff0c\u628a\u6211\u4e5f\u8bf4\u5f97\u5fc3\u52a8\u4e86\u3002\u4e0d\u8fc7\uff0c\u8fd8\u662f\u6e38\u534a\u5929\u5427\uff0c\u7559\u4e00\u4e9b\u672a \u5c3d\u7684\u6e38\u5174\uff0c\u4ee5\u5f85\u4ed6\u65e5\uff0c\u5c82\u4e0d\u66f4\u4f73\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u53f9\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u8bf4\u5f97\u597d\uff0c\u7559\u672a\u5c3d\u4e4b\u60c5\uff0c\u56de\u65e0\u7a77\u4e4b\u5473\uff0c\u8fd9\u6b63\u662f\u4eba\u751f\u6700\u7f8e\u7684\u610f\u5883\uff0c\u534a \u5929\u5c31\u534a\u5929\u5427\u3002\u201d \u3000\u3000\u4e8e\u662f\u4e00\u884c\u56db\u4f17\uff0c\u5728\u665a\u971e\u5915\u7167\u4e4b\u4e2d\u6500\u767b\u534e\u5c71\uff0c\u9053\u65c1\u602a\u77f3\u5947\u677e\uff0c\u6d41\u6cc9\u5c71\u7011\uff0c\u6ce8\u76ee\u90fd\u662f\u4f73\u666f\u3002 \u6cf0\u5143\u6d69\u8d5e\u9053\uff1a\u201c\u672a\u5230\u534e\u5c71\u9ad8\u5904\u5df2\u662f\u5982\u5165\u5c71\u9634\u9053\u4e0a\uff0c\u4ee4\u4eba\u76ee\u4e0d\u6687\u63a5\u4e86\u3002\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u4f3c\u4f60 \u8fd9\u6837\u6e38\u89c8\uff0c\u6e38\u4e2a\u5341\u5929\u534a\u6708\uff0c\u6050\u6015\u90fd\u8fd8\u4e0d\u591f\u3002\u5feb\u70b9\u8d70\u5427\uff0c\u8981\u4e0d\u7136\u534a\u591c\u6572\u95e8\uff0c\u53c8\u8981\u5bb3\u5f97\u5c0f\u9053\u58eb\u62c5 \u60ca\u4e86\u3002 \u3000\u3000\u5230\u4e86\u6e05\u98ce\u89c2\u6b63\u662f\u5929\u9ed1\u65f6\u5206\u3002\u6e05\u98ce\u89c2\u7684\u6240\u5728\u5730\u201c\u838e\u7f57\u576a\u201d\u4e5f\u662f\u534e\u5c71\u4e00\u5904\u540d\u80dc\uff0c\u636e\u8bf4\u662f\u56e0\u4e3a \u6709\u4e00\u682a\u897f\u57df\u79cd\u7684\u838e\u7f57\u6811\u800c\u5f97\u540d\u7684\uff0c\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u7239\u7239\u8bf4\uff0c\u8fd9\u682a\u838e\u7f57\u6811\u8fd8\u662f\u534e\u5c71\u533b\u9690\u534e\u5929\u98ce \u4eb2\u624b\u4ece\u9a6c\u8428\u513f\u56fd\u79fb\u690d\u6765\u7684\u5462\u3002\u838e\u7f57\u6811\u7684\u6811\u53f6\u6709\u80a1\u6e05\u9999\uff0c\u662f\u53ef\u4ee5\u6cbb\u75c5\u7684\u3002\u53ef\u60dc\u6f31\u77f3\u9053\u4eba\u6b7b\u540e\uff0c \u534e\u5c71\u533b\u9690\u90a3\u7740\u624b\u6210\u6625\u7684\u533b\u672f\u5728\u4e2d\u4e0a\u65e9\u5df2\u5931\u4f20\u4e86\u3002\u201d\u5929\u8272\u5df2\u665a\uff0c\u4f17\u4eba\u53ea\u597d\u7559\u5f85\u660e\u65e5\u89c2\u8d4f\uff0c\u4e0d\u4fbf \u53bb\u6572\u95e8\u3002 \u3000\u3000\u6e05\u98ce\u89c2\u7684\u9053\u58eb\u679c\u7136\u751a\u662f\u5403\u60ca\uff0c\u5728\u95e8\u7f1d\u7528\u5077\u77a7\uff0c\u5f85\u5230\u770b\u6e05\u695a\u662f\u91d1\u9010\u6d41\u4e4b\u540e\uff0c\u65b9\u6562\u5f00\u95e8\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u90a3\u65e5\u7684\u4e8b\u60c5\u8fc7\u540e\u8fd8\u6709\u6076\u5ba2\u6765\u9a9a\u6270\u4f60\u4eec\u5417\uff1f\u201d \u3000\u3000\u90a3\u9053\u58eb\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u5012\u6ca1\u6709\uff0c\u4e0d\u8fc7\uff0c\u4e00\u6b21\u7740\u86c7\u54ac\uff0c\u5341\u5e74\u89c1\u4e86\u8349\u7ef3\u90fd\u5bb3\u6015\u3002\u8bf4\u6765\u60ed\u6127\uff0c\u6211 \u4eec\u53ea\u662f\u5f97\u5230\u5bb6\u5e08\u533b\u672f\u7684\u4e00\u70b9\u76ae\u6bdb\u3002\u81f3\u4e8e\u6b66\u529f\uff0c\u5374\u662f\u8fde\u76ae\u6bdb\u90fd\u672a\u5b66\u5230\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6709\u4e2a\u597d\u6d88\u606f\u53ef\u4ee5\u544a\u6170\u5c0a\u5e08\uff0c\u5c0a\u5e08\u4e4b\u4ec7\uff0c\u5df2\u7ecf\u6709\u4eba\u7ed9\u4ed6\u62a5\u4e86\u3002\u201d \u3000\u3000\u9053\u58eb\u5927\u559c\u9053\uff1a\u201c\u591a\u8c22\u91d1\u5c11\u4fa0\u3002\u4e0d\u77e5\u5bb6\u5e08\u7684\u4ec7\u4eba\uff0c\u662f\u5426\u5c31\u662f\u90a3\u4e2a\u4ec0\u4e48\u5929\u9b54\u6559\u7684\u6559\u4e3b\u5389\u5357 \u661f\u3002\u201d\u4ed6\u53ea\u9053\u4ee3\u4ed6\u5e08\u7236\u62a5\u4ec7\u7684\u4eba\u662f\u91d1\u9010\u6d41\uff0c\u91d1\u9010\u6d41\u81ea\u8c26\uff0c\u6240\u4ee5\u4e0d\u80af\u81ea\u5df1\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6070\u6070\u76f8\u53cd\uff0c\u7ed9\u4ee4\u5e08\u62a5\u4ec7\u7684\u6b63\u662f\u5389\u5357\u661f\u3002\u4f46\u4ed6\u53ef\u5e76\u4e0d\u662f\u5927\u9b54\u6559\u7684\u6559\u4e3b\u3002\u201d \u3000\u3000\u9053\u58eb\u5927\u4e3a\u8be7\u5f02\uff0c\u95ee\u91d1\u9010\u6d41\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u3002\u91d1\u9010\u6d41\u628a\u9633\u6d69\u5047\u501f\u5389\u5357\u661f\u540d\u4e49\uff0c\u91cd\u5f00\u9999\u5802\uff0c\u7528 \u6bd2\u836f\u6bd2\u529f\u8981\u631f\u8bb8\u591a\u5c0f\u5e2e\u4f1a\u670d\u4ece\u4ed6\uff0c\u4e3a\u4e86\u5fcc\u60ee\u6f31\u77f3\u9053\u4eba\u80fd\u591f\u6cbb\u75c5\u6551\u4eba\uff0c\u6545\u800c\u5148\u4e0b\u624b\u628a\u6f31\u77f3\u9053\u4eba \u5bb3\u6b7b\u3002\u4ee5\u53ca\u540e\u6765\u5389\u5357\u661f\u600e\u6837\u63ed\u7834\u4ed6\u7684\u9634\u8c0b\uff0c\u5e9f\u4e86\u4ed6\u7684\u6b66\u529f\u7b49\u7b49\u4e8b\u60c5\uff0c\u539f\u539f\u672c\u672c\u8bf4\u4e86\u51fa\u6765\uff0c\u6e05 \u98ce\u89c2\u7684\u9053\u58eb\u8fd9\u624d\u660e\u767d\u3002 \u3000\u3000\u9053\u58eb\u53f9\u9053\uff1a\u201c\u5584\u6709\u5584\u62a5\uff0c\u6076\u6709\u6076\u62a5\uff0c\u82e5\u8fd8\u672a\u62a5\uff0c\u65f6\u8fb0\u672a\u5230\u3002\u8fd9\u8bdd\u5f53\u771f\u8bf4\u5f97\u4e0d\u9519\u3002\u9633\u6d69\u8fd9 \u8001\u8d3c\u867d\u7136\u672a\u6b7b\uff0c\u4f46\u6210\u4e86\u5e9f\u4eba\uff0c\u4ea6\u8db3\u4ee5\u4e00\u89e3\u6211\u4eec\u5fc3\u5934\u4e4b\u6068\u4e86\u3002\u201d \u3000\u3000\u9053\u58eb\u5f15\u9886\u91d1\u3001\u79e6\u7b49\u4eba\u5230\u7075\u5802\u4e0a\u9999\u4e4b\u540e\uff0c\u4fbf\u7ed9\u4ed6\u4eec\u5b89\u6392\u4f4f\u6240\uff0c\u4e24\u7537\u4e24\u5973\u6b63\u597d\u5206\u4f4f\u4e24\u95f4\u53a2 \u623f\u3002\u91d1\u9010\u6d41\u4e0e\u79e6\u5143\u6d69\u62b5\u8db3\u540c\u7720\uff0c\u5927\u5bb6\u90fd\u4e0d\u60f3\u7761\u89c9\u3002 \u3000\u3000\u4e24\u4eba\u8c08\u53e4\u8bba\u4eca\uff0c\u8bf4\u5f97\u5341\u5206\u9ad8\u5174\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u5df2\u662f\u5c06\u8fd1\u4e09\u66f4\u65f6\u5206\u3002\u91d1\u9010\u6d41\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1 \u6708\u660e\u5982\u955c\uff0c\u539f\u6765\u8fd9\u4e00\u5929\u6b63\u662f\u9634\u5386\u5341\u516d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5ffd\u53d1\u5947\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u53e4\u4eba\u79c9\u70db\u591c\u6e38\uff0c\u8ba4\u4e3a\u4eba\u751f\u4e50\u4e8b\u3002\u5176\u5b9e\u70db\u5149\u5982\u4f55\u80fd\u6bd4\u6708\u5149\uff1f\u4eca \u665a\u7684\u6708\u8272\u8fd9\u6837\u597d\uff0c\u82e5\u767b\u534e\u5c71\u4e4b\u5dc5\uff0c\u89c2\u8d4f\u5947\u666f\uff0c\u60f3\u5fc5\u53e6\u6709\u4e00\u756a\u4f73\u8da3\uff0c\u662f\u767d\u5929\u6240\u4e0d\u80fd\u9886\u7565\u7684 \u5462\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u7b11\u9053\uff1a\u201c\u6211\u5012\u662f\u60f3\u966a\u4f60\u7684\uff0c\u4f46\u54b1\u4eec\u6084\u6084\u5730\u6e9c\u51fa\u53bb\uff0c\u5374\u5982\u4f55\u5411\u5979\u4eec\u4ea4\u5f85\uff1f\u4e07\u4e00\u660e\u65e9 \u4e0d\u53ca\u56de\u6765\uff0c\u5979\u4eec\u66f4\u8981\u62c5\u5fc3\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5524\u9192\u5979\u4eec\u5982\u4f55\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u7761\u89c9\uff0c\u4eba\u5bb6\u4e0d\u8981\u7761\u5417\uff1f\u8d70\u4e86\u5927\u534a\u5929\u7684\u5c71\u8def\uff0c\u5979\u4eec\u4e5f\u8be5\u7d2f\u4e86\u3002\u5bf9\u4e0d \u4f4f\uff0c\u6211\u53ef\u4e0d\u80fd\u966a\u4f60\u53d1\u75af\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6b66\u5f53\u6d3e\u7684\u79e6\u5c11\u4fa0\uff0c\u4e00\u7ed3\u4e86\u5a5a\u5c31\u6015\u8001\u5a46\uff0c\u8fd9\u5012\u662f\u51fa\u4e4e\u6211\u7684\u610f\u5916\u3002\u660e\u5929\u6211\u5012 \u60f3\u95ee\u4e00\u95ee\u5c01\u59d1\u5a18\uff0c\u95ee\u95ee\u5979\u6709\u4ec0\u4e48\u5fa1\u592b\u7684\u672c\u9886\uff0c\u628a\u4f60\u7ba1\u5f97\u670d\u670d\u5e16\u5e16\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u7b11\u9053\uff1a\u201c\u5f85\u4f60\u7ed3\u4e86\u5a5a\u4f60\u5c31\u77e5\u9053\u4e86\uff0c\u8fd9\u4f4d\u53f2\u59d1\u5a18\u6bd4\u5999\u5ae6\u5389\u5bb3\u5f97\u591a\u5462\uff01\u770b\u4f60\u8fd8\u80fd\u4e0d\u80fd \u591f\u50cf\u4e0d\u7f81\u7684\u91ce\u9a6c\uff1f\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u91d1\u9010\u6d41\u5ffd\u5730\u5982\u6709\u6240\u89c9\uff0c\u4fa7\u8033\u7ec6\u542c\uff0c\u79e6\u5143\u6d69\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u662f\u864e\u5578\u5427\uff1f\u201d\u539f\u6765\u4ed6 \u4eec\u9690\u9690\u542c\u5f97\u4f3c\u6709\u4e00\u9635\u5578\u58f0\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u50cf\u662f\u864e\u5578\uff0c\u5012\u662f\u50cf\u4eba\u7684\u5578\u58f0\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u4e09\u66f4\u534a\u591c\uff0c\u6709\u8c01\u4e0a\u534e\u5c71\u53d1\u5578\uff1f\u9664\u975e\u4ed6\u4e5f\u662f\u50cf\u4f60\u8fd9\u6837\u7684\u75af\u5b50\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u521a\u624d\u4e0d\u4e5f\u662f\u8bf4\u60f3\u966a\u6211\u6e38\u5c71\u4e48\uff1f\u96be\u9053\u4f60\u4e5f\u662f\u75af\u5b50\uff1f\u82e5\u662f\u5f53\u771f\u6709\u4eba\u5728\u6b64\u6708\u660e \u4e4b\u591c\uff0c\u6765\u6b64\u540d\u5c71\uff0c\u547c\u5524\u5c71\u7075\uff0c\u6050\u6015\u8fd8\u662f\u4e16\u5916\u9ad8\u4eba\u5462\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u5ffd\u7136\u6447\u4e86\u6447\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u522b\u4f5c\u58f0\uff01\u201d\u4ed6\u7684\u5185\u529f\u4e0d\u53ca\u91d1\u9010\u6d41\uff0c\u8981\u9759\u542c\u4e00\u4f1a\uff0c\u65b9\u59cb\u542c \u5f97\u8f83\u4e3a\u6e05\u695a\u3002 \u3000\u3000\u91d1\u9010\u6d41\u89c1\u5730\u9762\u6709\u8be7\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u6837\uff0c\u542c\u6e05\u695a\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u54a6\uff0c\u7684\u786e\u50cf\u662f\u6b66\u529f\u9ad8\u660e\u4e4b\u58eb\u6240\u53d1\u7684\u5578\u58f0\u3002\u201d\u6b64\u65f6\u5578\u58f0\u5df2\u6b62\uff0c\u4f46\u5c71\u8c37\u95f4\u7684\u56de \u58f0\u4ecd\u662f\u9690\u9690\u542c\u95fb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u62fc\u7740\u53d7\u5979\u4eec\u660e\u5929\u8d23\u602a\uff0c\u6211\u53ef\u662f\u975e\u51fa\u53bb\u4f1a\u4e00\u4f1a\u8fd9\u4f4d\u9ad8\u4eba\u4e0d\u53ef\u4e86\u3002\u4f60\u4e0d\u6562\u966a \u6211\uff0c\u4f60\u5c31\u5f85\u5728\u8fd9\u91cc\u5427\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u597d\u5947\u5fc3\u8d77\uff0c\u4e00\u62cd\u80f8\u53e3\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u966a\u4f60\uff01\u4e5f\u4e0d\u7ba1\u89c1\u4e0d\u89c1\u5f97\u8fd9\u4e2a\u4eba\uff0c\u54b1\u4eec\u5728\u5929\u4eae \u4e4b\u524d\u4e00\u5b9a\u56de\u6765\uff0c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u4e3a\u9ad8\u5174\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u60f3\u7d2f\u4f60\u505a\u4e0d\u6210\u597d\u4e08\u592b\uff0c\u597d\uff0c\u4f9d\u4f60\u5c31\u662f\u3002\u201d \u3000\u3000\u4ed6\u4eec\u662f\u60f3\u7792\u7740\u53f2\u7ea2\u82f1\u548c\u5c01\u5999\u5ae6\u4e8c\u4eba\u5077\u5077\u51fa\u53bb\u7684\uff0c\u4e0d\u6599\u4e00\u6253\u5f00\u623f\u95e8\uff0c\u53ea\u89c1\u5979\u4eec\u4e8c\u4eba\u5df2\u7ecf\u662f \u5728\u9662\u5b50\u91cc\u6b63\u671d\u7740\u4ed6\u4eec\u8d70\u6765\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u53c8\u60ca\u53c8\u559c\uff0c\u4f4e\u58f0\u8bf4\u9053\uff0c\u201c\u4f60\u4eec\u4e5f\u542c\u89c1\u4e86\uff1f\u201d\u53f2\u7ea2\u82f1\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e0d \u662f\u51c6\u5907\u51fa\u53bb\u770b\u770b\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u8fd9\u4e2a\u5174\u8da3\uff1f\u201d\u53f2\u7ea2\u82f1\u7b11\u9053\uff1a\u201c\u5982\u679c\u6ca1\u6709\uff0c\u6211\u4e5f\u4e0d \u6765\u627e\u4f60\u4e86\u3002\u201d\u91d1\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u54b1\u4eec\u6084\u6084\u8fc7\u53bb\uff0c\u522b\u60ca\u9192\u4e86\u89c2\u4e2d\u7684\u9053\u58eb\u3002\u201d \u3000\u3000\u6708\u591c\u4e0b\u7684\u534e\u5c71\u679c\u7136\u662f\u663e\u5f97\u5f02\u6837\u7684\u6e05\u5e7d\uff0c\u522b\u6709\u4e00\u79cd\u6726\u80e7\u4e4b\u7f8e\u3002\u7fa4\u5cf0\u5b9b\u4f3c\u62ab\u4e0a\u4e86\u4e00\u5c42\u8584\u96fe\u8f7b \u7eb1\uff0c\u767d\u4e91\u7f2d\u7ed5\uff0c\u6d6e\u6c89\u5cf0\u5934\u5e7d\u58d1\u4e4b\u95f4\uff0c\u6784\u6210\u4e86\u4e00\u5e45\u7f8e\u5999\u7edd\u4f26\u7684\u56fe\u753b\uff0c\u4ece\u838e\u7f57\u576a\u51fa\u53bb\uff0c\u5f15\u9886\u4e1c \u671b\uff0c\u9690\u7ea6\u53ef\u89c1\u76f4\u63d2\u4e91\u9704\u7684\u201c\u7075\u829d\u77f3\u201d\u548c\u201c\u7389\u5973\u77f3\u201d\uff0c\u8fd9\u662f\u534e\u5c71\u897f\u5ea7\u6709\u540d\u7684\u77f3\u5cf0\uff0c\u201c\u7075\u829d\u77f3\u201d \u4e0a\u9762\u5e7f\u5927\uff0c\u4e0b\u9762\u524a\u5c0f\uff0c\u6570\u74e3\u5408\u62b1\uff0c\u597d\u50cf\u4e00\u6735\u7855\u5927\u65e0\u6bd4\u7684\u7075\u829d\u3002\u201c\u7389\u5973\u77f3\u201d\u633a\u62d4\u79c0\u78a7\uff0c\u8170\u95f4\u767d \u4e91\u56f4\u7ed5\uff0c\u66f4\u50cf\u4e00\u4e2a\u98ce\u59ff\u7ef0\u7ea6\uff0c\u7fe0\u5e26\u98d8\u6447\u7684\u7f8e\u4eba\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u8d5e\u53f9\u9053\uff1a\u201c\u8fd9\u7389\u5973\u77f3\u771f\u662f\u597d\u770b\uff0c\u771f\u50cf\u662f\u5de7\u624b\u96d5\u6210\u7684\u7f8e\u4eba\u3002\u201d\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u7075\u829d\u77f3 \u4e5f\u662f\u5947\u89c2\u3002\u55ef\uff0c\u4f60\u4eec\u6ce8\u610f\u5230\u4e86\u6ca1\u6709\uff0c\u534e\u5c71\u7fa4\u5cf0\uff0c\u8bb8\u591a\u90fd\u662f\u9177\u4f3c\u82b1\u7684\u5f62\u72b6\uff0c\u7eb5\u76ee\u4e00\u89c8\uff0c\u5c31\u50cf\u767e \u82b1\u76db\u5f00\u7684\u6837\u5b50\u3002\u2018\u82b1\u2019\u2018\u534e\u2019\u4e8c\u5b57\u662f\u76f8\u901a\u7684\uff0c\u534e\u5c71\u4e4b\u540d\u4e3a\u534e\u5c71\uff0c\u60f3\u5fc5\u662f\u4e0e\u5b83\u7684\u5f62\u72b6\u4f3c\u82b1\u6709 \u5173\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u5143\u6d69\u8fd8\u6015\u4f60\u4eec\u778c\u7761\uff0c\u53eb\u6211\u4e0d\u8981\u60ca\u9192\u4f60\u4eec\u5462\u3002\u201d\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u597d\u5728\u7ea2\u82f1\u59d0 \u59d0\u542c\u5230\u90a3\u4e2a\u5578\u58f0\uff0c\u53eb\u6211\u8d77\u6765\uff0c\u5426\u5219\u5c31\u8981\u9519\u8fc7\u8fd9\u4e00\u751f\u7f55\u89c1\u7684\u5947\u89c2\u3002\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec \u5373\u4f7f\u78b0\u4e0d\u4e0a\u90a3\u4e2a\u4eba\uff0c\u4ea6\u4e0d\u865a\u6b64\u884c\u4e86\u3002\u201d \u3000\u3000\u534e\u5c71\u5730\u5f62\u6781\u4e3a\u9669\u5cfb\uff0c\u53ea\u6709\u4e00\u6761\u6b63\u8def\u53ef\u4ee5\u767b\u5c71\uff0c\u8fd9\u662f\u5728\u77f3\u58c1\u95f4\u5f00\u51ff\u51fa\u7684\u5c71\u8def\u2014\u2014\u5929\u9669\u201c\u5343 \u5c3a\u5cf0\u201d\u3002\u8fd9\u6761\u8def\u957f\u4e8c\u91cc\u8bb8\uff0c\u5bbd\u4e0d\u8fc7\u4e8c\u5c3a\uff0c\u4ec5\u53ef\u5bb9\u4e00\u4eba\u901a\u8fc7\u3002\u6cbf\u9014\u540d\u80dc\u9887\u591a\u3002\u6709\u77f3\u9636\u4e8c\u767e\u4f59 \u7ea7\uff0c\u50cf\u4e00\u5ea7\u5927\u68af\uff0c\u91cd\u91cd\u53e0\u53e0\u6276\u6447\u76f4\u4e0a\uff0c\u4e00\u76f4\u4f38\u5411\u4e91\u96fe\u7f25\u7f08\u4e4b\u4e2d\u3002\u5411\u4e0a\u770b\u89c1\u4e00\u7ebf\u84dd\u5929\uff1b\u5de6\u53f3\u770b \u89c1\u5f25\u6f2b\u4e91\u6c14\uff1b\u5411\u4e0b\u770b\u662f\u5e7d\u6697\u4e0d\u89c1\u5e95\u7684\u6df1\u8c37\u3002\u5c01\u5999\u5ae6\u3001\u53f2\u7ea2\u82f1\u867d\u7136\u662f\u8eab\u6709\u6b66\u529f\u7684\u5dfe\u5e3c\u82f1\u96c4\uff0c\u8d70 \u5728\u201c\u5343\u5c3a\u5e62\u201d\u4e4b\u4e0a\uff0c\u4e5f\u4e0d\u7981\u6709\u70b9\u5fc3\u60ca\u8089\u8df3\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4eba\u79f0\u2018\u534e\u5c71\u81ea\u53e4\u4e00\u6761\u8def\u2019\u4eca\u65e5\u8eab\u5386\u5176\u666f\uff0c\u679c\u7136\u662f\u540d\u4e0b\u65e0\u865a\uff0c\u54b1\u4eec\u82e5\u4e0d\u662f\u4ece \u8fd9\u6761\u8def\u8d70\uff0c\u53ea\u6015\u518d\u597d\u7684\u8f7b\u529f\uff0c\u4e5f\u662f\u6500\u4e0d\u4e0a\u53bb\u3002\u201d \u3000\u3000\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u53f3\u58c1\u4e0a\u8fd8\u523b\u6709\u5b57\u5462\uff01\u201d\u91d1\u9010\u6d41\u4ed4\u7ec6\u4e00\u770b\uff0c\u89c1\u662f\u201c\u811a\u8e0f\u5b9e\u5730\uff0c\u6b65\u6b65\u7559 \u795e\u201d\u516b\u4e2a\u5927\u5b57\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6709\u540d\u7684\u2018\u56de\u5fc3\u77f3\u2019\u4e86\u3002\u6709\u4e9b\u4eba\u5230\u6b64\uff0c\u5c31\u56de\u5fc3\u8f6c\u610f\uff0c\u4e0d\u6562\u518d\u5f80 \u524d\u53bb\u4e86\u3002\u8fd9\u4e2a\u5b57\u60f3\u5fc5\u662f\u8b66\u544a\u6765\u6e38\u7684\u4eba\uff0c\u7ecf\u6b64\u9669\u5ce1\uff0c\u987b\u5f97\u7279\u522b\u5c0f\u5fc3\u7684\u3002\u201d\u53f2\u7ea2\u82f1\u7b11\u9053\uff1a\u201c\u8fd9\u516b \u4e2a\u5b57\u4e5f\u6b63\u53ef\u4f5c\u4e3a\u7acb\u8eab\u5904\u4e16\u7684\u5ea7\u53f3\u94ed\u5462\u3002\u201d \u3000\u3000\u8c08\u8bdd\u95f4\u4e00\u9635\u5c71\u98ce\u5439\u8fc7\uff0c\u91d1\u9010\u6d41\u9690\u9690\u542c\u5f97\u4f3c\u6709\u7b11\u58f0\u3002\u91d1\u9010\u6d41\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u53ea\u662f\u4e00 \u4e2a\u4eba\uff0c\u597d\u50cf\u6709\u51e0\u4e2a\u4eba\u5728\u4e0a\u9762\u8c08\u7b11\u3002\u54b1\u4eec\u8d76\u5feb\u4e0a\u53bb\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff0c\u201c\u4e14\u6162\u6b22\u559c\uff0c\u4e5f\u8fd8\u4e0d\u77e5\u662f\u4ec0\u4e48\u4eba\u5462\uff1f\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u61c2\u5f97\u5728\u6708\u660e\u4e4b\u591c\u6765\u6e38 \u534e\u5c71\u7684\u4eba\uff0c\u54ea\u6709\u662f\u4fd7\u5b50\u51e1\u592b\u7684\u9053\u7406\uff1f\u4e00\u5b9a\u662f\u4e16\u5916\u9ad8\u4eba\u65e0\u7591\uff01\u201d \u3000\u3000\u4f17\u4eba\u6000\u7740\u597d\u5947\u5fc3\u7406\uff0c\u52a0\u5feb\u811a\u6b65\uff0c\u8d70\u8fc7\u4e86\u201c\u5343\u5c3a\u5e62\u201d\uff0c\u53ea\u89c1\u5357\u5317\u4e24\u5cf0\uff0c\u5c79\u7acb\u5929\u8fb9\uff0c\u4e24\u65c1\u4e43 \u662f\u65ad\u5d16\u7edd\u8c37\uff0c\u811a\u4e0b\u4e91\u6c14\u5f25\u6f2b\uff0c\u597d\u50cf\u7f6e\u8eab\u5b50\u65e0\u6daf\u65e0\u9645\u7684\u592a\u7a7a\uff0c\u5947\u9669\u5df2\u8fbe\u6781\u81f4\u3002\u91d1\u9010\u6d41\u53f9\u9053\uff1a \u201c\u53e4\u4eba\u63cf\u5199\u534e\u5c71\u7684\u540d\u8bd7\uff0c\u6211\u8fd8\u8bb0\u5f97\u51e0\u53e5\u662f\u2018\u53ea\u6709\u5929\u5728\u4e0a\uff0c\u66f4\u65e0\u5c71\u4e0e\u9f50\u2019\uff1b\u2018\u4f38\u624b\u6458\u661f\u6597\uff0c\u5410 \u6c14\u63a5\u592a\u865a\u3002\u2019\u5982\u4eca\u8eab\u4e34\u5176\u5883\uff0c\u679c\u7136\u662f\u5e76\u4e0d\u6b3a\u6211\uff01\u201d\u53f2\u7ea2\u82f1\u7b11\u9053\uff1a\u201c\u4e5f\u4e0d\u514d\u6709\u70b9\u5938\u5927\u5427\uff1f\u4f60\u4f38 \u624b\u6458\u4e2a\u661f\u6597\u7ed9\u6211\u770b\u770b\u3002\u201d\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u8bfb\u53e4\u4eba\u7684\u8bd7\u8bcd\uff0c\u54ea\u6709\u8fd9\u6837\u6267\u7740\u7684\u9053\u7406\uff1f\u8981\u9886\u7565\u7684\u4e0d \u8fc7\u662f\u4ed6\u7b14\u4e0b\u7684\u5883\u754c\u7f62\u4e86\u3002\u201d \u3000\u3000\u79e6\u5143\u6d69\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u5fc5\u8c08\u8bd7\u8bba\u8bcd\u4e86\uff0c\u4f60\u770b\uff0c\u90a3\u8fb9\u771f\u7684\u662f\u6709\u4eba\u5462\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u671d\u7740\u4ed6\u6240\u6307\u7684\u65b9\u5411\u671b\u53bb\uff0c\u53ea\u89c1\u524d\u9762\u662f\u4e00\u6761\u77f3\u6881\uff0c\u7ea6\u6709\u516d\u4e03\u4e08\u957f\uff0c\u6a2a\u67b6\u4e8e\u4e24\u5cf0\u4e4b \u95f4\uff0c\u5b9b\u5982\u4e00\u5ea7\u4e91\u6d77\u98de\u6865\uff0c\u201c\u6865\u201d\u7684\u90a3\u8fb9\uff0c\u6709\u51e0\u68f5\u53c2\u5929\u7684\u53e4\u677e\uff0c\u677e\u836b\u4e4b\u4e0b\uff0c\u9690\u7ea6\u53ef\u770b\u5230\u6709\u4e24\u4e2a \u4eba\uff0c\u4f3c\u662f\u4e00\u7537\u4e00\u5973\u3002\u6708\u8272\u6726\u80e7\uff0c\u4e91\u6c14\u5f25\u6f2b\uff0c\u53e4\u677e\u82cd\u90c1\uff0c\u4eba\u5f71\u9690\u73b0\u3002\u7aef\u7684\u4f3c\u662f\u53e4\u4eba\u7b14\u4e0b\u7684\u201c\u7a7a \u5c71\u9ad8\u58eb\u56fe\u201d\u3002\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u542c\u521a\u624d\u8c08\u7b11\u7684\u58f0\u97f3\uff0c\u4f3c\u4e4e\u4e0d\u6b62\u662f\u4e24\u4e2a\u4eba\u3002\u4e14\u8fc7\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u89c1\u5730\u52bf\u5947\u9669\uff0c\u8bf4\u9053\uff1a\u201c\u9010\u6d41\uff0c\u5c0f\u5fc3\u4e8c\u4e9b\uff01\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u4eec\u5728\u8fd9\u91cc\u7b49\u6211\uff0c\u6211\u53bb \u770b\u770b\u662f\u4ec0\u4e48\u4eba\u3002\u201d\u4ed6\u53ea\u9053\u53f2\u7ea2\u82f1\u662f\u6015\u4ed6\u5931\u8db3\uff0c\u6043\u7740\u8f7b\u529f\u9ad8\u5999\uff0c\u5fc3\u91cc\u8fd8\u5728\u6697\u7b11\u53f2\u7ea2\u82f1\u80c6\u5c0f\u3002\u5374 \u4e0d\u77e5\u53f2\u7ea2\u82f1\u53eb\u4ed6\u5c0f\u5fc3\u63d0\u9632\u7684\u662f\u4eba\uff0c\u5728\u8fd9\u7edd\u9669\u4e4b\u5730\uff0c\u5982\u679c\u5bf9\u65b9\u4e0d\u6000\u597d\u610f\uff0c\u7a81\u7136\u88ad\u51fb\u7684\u8bdd\uff0c\u90a3\u53ef \u4e0d\u662f\u5f53\u800d\u7684\u3002\u4f46\u53f2\u7ea2\u82f1\u4e5f\u56e0\u4e3a\u5c1a\u672a\u77e5\u9053\u5bf9\u65b9\u662f\u53cb\u662f\u654c\uff0c\u5374\u662f\u4e0d\u4fbf\u8bf4\u51fa\u53e3\u6765\u3002\u4e07\u4e00\u5bf9\u65b9\u5f53\u771f\u4e43 \u662f\u524d\u8f88\u9ad8\u4eba\uff0c\u53ef\u5c31\u4e0d\u597d\u610f\u601d\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u98de\u8eab\u4e00\u8dc3\uff0c\u8df3\u4e0a\u77f3\u6881\uff0c\u53ea\u89c1\u767d\u4e91\u6735\u6735\uff0c\u4ece\u773c\u524d\u98de\u6765\uff0c\u53c8\u4ece\u811a\u4e0b\u6ed1\u53bb\uff0c\u98d8\u98d8\u6b32\u4ed9\uff0c \u4e0d\u7531\u5f97\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u5feb\u54c9\uff0c\u5feb\u54c9\uff01\u7ea2\u82f1\uff0c\u4e0d\u7528\u5bb3\u6015\uff0c\u4f60\u4e5f\u6765\u5427\uff01\u201d\u7b11\u58f0\u672a\u5df2\uff0c\u53ea\u89c1\u90a3\u4e24\u4e2a\u4eba \u5df2\u7ecf\u73b0\u51fa\u8eab\u5f62\uff0c\u4e5f\u5728\u5411\u7740\u77f3\u6881\u8d70\u8fc7\u6765\u4e86\u3002\u7537\u7684\u4e09\u7efa\u957f\u987b\uff0c\u5973\u7684\u9b13\u4e91\u9ad8\u8038\uff0c\u5927\u7ea6\u90fd\u662f\u56db\u5341\u5f00\u5916 \u7684\u5e74\u7eaa\uff0c\u88c5\u675f\u4e0d\u7c7b\u5c18\u4e16\u4e2d\u4eba\uff0c\u679c\u7136\u662f\u5c71\u6797\u9690\u9038\u3002 \u3000\u3000\u91d1\u9010\u6d41\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u91d1\u9010\u6d41\u4ef0\u6155\u9ad8\u8d24\uff0c\u7279\u6765\u62dc\u8bbf\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u89c1\u90a3\u4e2d\u5e74\u7537\u5b50\u4e5f\u662f\u98de\u8eab\u4e00\u8dc3\uff0c\u8df3\u4e0a\u4e86\u77f3\u6881\uff0c\u7b14\u76f4\u7684\u5411\u91d1\u9010\u6d41\u8d70\u6765\uff0c\u8bf4\u9053\uff1a \u201c\u4e0d\u6562\uff0c\u539f\u6765\u662f\u91d1\u5927\u4fa0\u7684\u516c\u5b50\u6765\u4e86\u3002\u5982\u6b64\u591a\u793c\uff0c\u53ef\u662f\u53eb\u6211\u62c5\u5f53\u4e0d\u8d77\u3002\u201d \u3000\u3000\u8fd9\u6761\u77f3\u6881\uff0c\u4ec5\u80fd\u5bb9\u5f97\u4e00\u4eba\u901a\u8fc7\u7684\uff0c\u90a3\u4e2a\u4eba\u76f4\u8d70\u8fc7\u6765\uff0c\u7adf\u65e0\u95ea\u907f\u4e4b\u610f\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u624d\u77e5\u9053\u6b64\u4eba\u4e0d\u6000\u597d\u610f\uff0c\u7adf\u7136\u662f\u8981\u628a\u81ea\u5df1\u7f6e\u4e8e\u6b7b\u5730\uff01 \u3000\u3000\u6b64\u65f6\u91d1\u9010\u6d41\u6b63\u8d70\u5230\u77f3\u6881\u7684\u6b63\u4e2d\uff0c\u4e24\u65c1\u4e0d\u80fd\u95ea\u907f\uff0c\u5018\u82e5\u56de\u5934\u8d70\u7684\u8bdd\uff0c\u8f6c\u8eab\u4e4b\u9645\uff0c\u90a3\u4eba\u53ea\u8981 \u8f7b\u8f7b\u4e00\u63a8\uff0c\u5c31\u53ef\u4ee5\u628a\u4ed6\u63a8\u4e0b\u77f3\u7cb1\u3002\u800c\u4e14\u90a3\u4eba\u662f\u7b14\u76f4\u5730\u649e\u8fc7\u6765\u7684\uff0c\u91d1\u9010\u6d41\u5373\u4f7f\u8111\u540e\u957f\u7740\u773c\u775b\uff0c \u4e0d\u7528\u8f6c\u8eab\uff0c\u4fbf\u5f80\u540e\u9000\uff0c\u4e5f\u662f\u96be\u9003\u8fd9\u4eba\u7684\u6bd2\u624b\uff01 \u3000\u3000\u91d1\u9010\u6d41\u6012\u4ece\u5fc3\u4e2d\u8d77\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u5927\u9a7e\u4eb2\u8fce\uff0c\u6211\u81ea\u5df1\u8fc7\u53bb\u5c31\u662f\u4e86\uff01\u201d\u4e0d\u9000\u53cd \u8fdb\uff0c\u4e5f\u662f\u7b14\u76f4\u5730\u649e\u8fc7\u53bb\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u5012\u8981\u770b\u770b\u4f60\u6709\u4ec0\u4e48\u672c\u9886\u80fd\u591f\u628a\u6211\u6324\u4e0b\u53bb\uff01\u201d \u3000\u3000\u773c\u770b\u4e24\u4eba\u5c31\u8981\u649e\u5728\u4e00\u8d77\uff0c\u4e00\u649e\u4e4b\u4e0b\uff0c\u52bf\u5fc5\u662f\u529b\u5f3a\u8005\u80dc\uff0c\u529b\u5f31\u8005\u8d25\u3002\u80dc\u8005\u56fa\u7136\u96be\u514d\u53d7\u4f24\uff0c \u8d25\u8005\u5219\u66f4\u5c06\u6027\u547d\u4e0d\u4fdd\uff01\u6216\u8005\u540c\u5f52\u4e8e\u5c3d\uff0c\u4ea6\u6709\u53ef\u80fd\uff01 \u3000\u3000\u5728\u8fd9\u6837\u60c5\u5f62\u4e4b\u4e0b\uff0c\u53f2\u7ea2\u82f1\u3001\u79e6\u5143\u6d69\u7b49\u4eba\u60f3\u8981\u63f4\u6551\u4ea6\u662f\u65e0\u8ba1\u53ef\u65bd\uff0c\u7981\u4e0d\u4f4f\u5931\u58f0\u60ca\u547c\u3002 \u3000\u3000\u8fd9\u4eba\u662f\u5403\u8fc7\u91d1\u4e16\u9057\u7684\u5927\u4e8f\u7684\uff0c\u770b\u89c1\u91d1\u9010\u6d41\u6beb\u65e0\u60e7\u8272\u7684\u5411\u4ed6\u649e\u6765\uff0c\u4e5f\u662f\u4e0d\u7981\u5fc3\u5934\u4e00\u51db\uff0c\u6697 \u81ea\u60f3\u9053\uff1a\u201c\u4ed6\u662f\u91d1\u4e16\u9057\u7684\u513f\u5b50\uff0c\u6b66\u529f\u60f3\u5fc5\u975e\u540c\u5c0f\u53ef\uff0c\u6211\u4e0e\u4ed6\u786c\u62fc\uff0c\u53ea\u6015\u672a\u5fc5\u5c31\u62fc\u5f97\u8fc7\u4ed6\u3002\u201d \u3000\u3000\u8fd9\u4eba\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u7acb\u5373\u4fbf\u715e\u4f4f\u8eab\u5f62\uff0c\u6de1\u6de1\u8bf4\u9053\uff0c\u201c\u4e0d\u6562\u5f53\u3002\u201d\u53cc\u638c\u5408\u4ec0\uff0c\u5411\u7740\u91d1\u9010\u6d41\u8fce\u5934 \u4e00\u63d6\u3002 \u3000\u3000\u8fd9\u4e00\u63d6\uff0c\u8868\u9762\u770b\u6765\u662f\u5411\u91d1\u9010\u6d41\u65bd\u793c\uff0c\u5176\u5b9e\u662f\u4e00\u62db\u6781\u5389\u5bb3\u7684\u6740\u624b\uff0c\u6697\u85cf\u7740\u201c\u7ae5\u5b50\u62dc\u89c2\u97f3\u201d \u7684\u62db\u5f0f\u3002\u201c\u7ae5\u4e8e\u62dc\u89c2\u97f3\u201d\u672c\u662f\u4e00\u62db\u5bfb\u5e38\u7684\u638c\u5f0f\uff0c\u4f46\u5728\u8fd9\u4eba\u624b\u4e2d\u4f7f\u51fa\uff0c\u5374\u53d8\u5316\u5f97\u975e\u5e38\u5de7\u5999\uff0c\u53d8 \u6210\u4e86\u4e00\u62db\u4f7f\u51fa\u610f\u60f3\u4e0d\u5230\u7684\u5251\u62db\uff0c\u6307\u5c16\u4ee3\u5251\uff0c\u6307\u5411\u4e86\u91d1\u9010\u6d41\u7684\u4e39\u7530\u8981\u5bb3\u3002\u800c\u4e14\u5728\u4ed6\u5408\u638c\u4e00\u63d6\u4e4b \u65f6\uff0c\u90a3\u80a1\u638c\u529b\u4e5f\u50cf\u6697\u6d41\u6c79\u6d8c\u4e00\u6837\uff0c\u5411\u91d1\u9010\u6d41\u63a8\u538b\u8fc7\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u51b7\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u5927\u591a\u793c\u4e86\uff01\u201d\u53cc\u624b\u865a\u62b1\u3002\u8c8c\u4f5c\u7b54\u793c\uff0c\u5316\u51fa\u4e86\u201c\u62c2\u4e91\u624b\u201d\u7684\u62db\u5f0f\uff0c \u4e00\u62c9\u4e00\u5faa\uff0c\u767b\u65f6\u5316\u89e3\u4e86\u90a3\u4eba\u7684\u638c\u529b\uff0c\u90a3\u4eba\u62ac\u5c16\u523a\u5230\uff0c\u91d1\u9010\u6d41\u7684\u67d4\u52b2\u7a81\u7136\u53d8\u6210\u4e86\u91d1\u521a\u638c\u7684\u521a\u731b \u638c\u529b\uff0c\u786c\u5288\u8fc7\u53bb\u3002\u90a3\u4eba\u8fde\u5fd9\u7f29\u62ac\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u4e0e\u91d1\u9010\u6d41\u5bf9\u4e86\u4e00\u638c\uff0c\u91d1\u9010\u6d41\u501f\u7740\u4ed6\u7684\u638c \u529b\uff0c\u4e00\u4e2a\u201c\u9e5e\u5b50\u7ffb\u8eab\u201d\uff0c\u4ece\u4ed6\u7684\u5934\u9876\u51cc\u7a7a\u98de\u8fc7\uff0c\u5230\u8fbe\u4e86\u77f3\u6881\u7684\u5f7c\u65b9\uff0c\u5fc3\u4e2d\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u4fa5 \u5e78\uff01\u201d \u3000\u3000\u672c\u6765\u8fd9\u4eba\u7684\u62db\u6570\u638c\u529b\u90fd\u662f\u53e4\u602a\u4e4b\u6781\uff0c\u4e2d\u571f\u6240\u65e0\u7684\uff0c\u91d1\u9010\u6d41\u5e78\u4e8f\u66fe\u89c1\u8fc7\u4ed6\u8fd9\u4e00\u95e8\u7684\u5bb6\u6570\uff0c \u6240\u4ee5\u624d\u80fd\u591f\u4ece\u5bb9\u5e94\u4ed8\uff0c\u5426\u5219\u9e7f\u6b7b\u8c01\u624b\uff0c\u6b8a\u96be\u9006\u6599\u3002 \u3000\u3000\u90a3\u4eba\u5f97\u4ee5\u4e0e\u91d1\u9010\u6d41\u540c\u8131\u9669\u5883\uff0c\u4e5f\u662f\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u4fa5\u5e78\u201d\uff0c\u5f53\u4e0b\u8ddf\u8e2a\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u91d1 \u5c11\u4fa0\uff0c\u597d\u529f\u592b\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u60f3\u5fc5\u662f\u6276\u6851\u4e03\u5b50\u4e4b\u4e00\u4e86\uff1f\u201d\u90a3\u4eba\u4e0d\u7981\u53c8\u662f\u4e00\u60ca\uff0c\u8d5e\u9053\uff1a\u201c\u91d1\u5c11\u4fa0\uff0c\u597d\u773c \u529b\uff01\u201d\u5fc3\u60f3\uff1a\u201c\u4ed6\u53ea\u63a5\u6211\u4e00\u62db\uff0c\u5c45\u7136\u5c31\u770b\u51fa\u4e86\u6211\u7684\u6765\u5386\uff0c\u59d4\u5b9e\u662f\u4e0d\u53ef\u5c0f\u89d1\u4e86\uff01\u201d \u3000\u3000\u90a3\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u60f3\u5fc5\u662f\u4ee4\u5c0a\u66fe\u5bf9\u516c\u5b50\u8a00\u53ca\u3002\u6211\u4eec\u9886\u6559\u8fc7\u4ee4\u5c0a\u7684\u7edd\u4e16\u6b66\u529f\uff0c\u4eca\u65e5 \u53c8\u5f97\u5de7\u9047\u516c\u5b50\uff0c\u771f\u662f\u4f55\u5e78\u5982\u4e4b\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u662f\u6709\u5fc3\u548c\u6211\u8f83\u91cf\u7684\u4e86\uff1f\u201d \u3000\u3000\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4ee4\u5c0a\u7684\u624b\u4e0b\u8d25\u5c06\uff0c\u672c\u6765\u4e0d\u6562\u5728\u516c\u5b50\u7684\u9762\u524d\u732e\u4e11\uff0c\u4f46\u516c\u5b50\u82e5\u80af\u8e22\u6559\uff0c\u6211 \u5012\u662f\u5f88\u60f3\u591a\u4e2a\u673a\u4f1a\u89c1\u8bc6\u89c1\u8bc6\u516c\u5b50\u5bb6\u4f20\u7684\u7edd\u4e16\u65e0\u53cc\u7684\u5251\u6cd5\u3002\u201d \u3000\u3000\u8fd9\u4eba\u8bf4\u8bdd\u8c26\u606d\uff0c\u53e3\u6c14\u5374\u751a\u72c2\u50b2\uff0c\u91d1\u9010\u6d41\u5c11\u5e74\u597d\u80dc\uff0c\u521a\u624d\u53c8\u51e0\u4e4e\u7ed9\u4ed6\u6324\u4e0b\u77f3\u6881\uff0c\u8fd9\u53e3\u6c14\u5982 \u4f55\u54bd\u5f97\u4e0b\u53bb\uff1f\u5373\u4f7f\u5bf9\u65b9\u4e0d\u5411\u4ed6\u6311\u6218\uff0c\u4ed6\u4e5f\u662f\u8981\u548c\u5bf9\u65b9\u8f83\u91cf\u7684\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u62d4\u51fa\u7384\u94c1\u5b9d\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u6276\u6851\u5c9b\u7684\u5251\u672f\uff0c\u6211\u4e5f\u6b63\u60f3\u591a\u70b9\u673a\u4f1a\u89c1\u8bc6\uff0c\u8bf7\u8fdb\u62db\u5427\uff01\u201d \u3000\u3000\u8fd9\u4eba\u770b\u89c1\u7384\u94c1\u5b9d\u5251\u9ed1\u9edd\u9edd\u7684\u6beb\u4e0d\u8d77\u773c\uff0c\u5012\u662f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6b64\u5251\u4e1d\u6beb\u4e0d\u9732 \u950b\u8292\uff0c\u5fc5\u5b9a\u6709\u4e9b\u53e4\u602a\u3002\u201d\u5f53\u4e0b\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u4f7f\u4e86\u4e00\u62db\u201c\u65e5\u51fa\u6276\u6851\u201d\uff0c\u5251\u5c16\u4e0a\u6307\uff0c\u8f7b\u8f7b\u6296\u52a8\u3002 \u3000\u3000\u8fd9\u662f\u6276\u6851\u6d3e\u5251\u672f\u7684\u201c\u8d77\u624b\u5f0f\u201d\uff0c\u4e5f\u662f\u4e00\u62db\u8bd5\u63a2\u5bf9\u65b9\u865a\u5b9e\u7684\u5251\u672f\uff0c\u6781\u5f97\u8f7b\u7075\u7fd4\u52a8\u4e4b\u81f4\u3002\u91d1 \u9010\u6d41\u8d5e\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u4e5f\u4e0d\u4f7f\u7528\u4ec0\u4e48\u62db\u5f0f\uff0c\u63d0\u8d77\u7384\u94c1\u5b9d\u5251\u4fbf\u5288\u4e0b\u6765\uff01 \u3000\u3000\u8fd9\u4eba\u770b\u51fa\u7384\u94c1\u5b9d\u5251\u975e\u540c\u51e1\u54c1\uff0c\u4f46\u5374\u60f3\u4e0d\u5230\u5b83\u7adf\u662f\u5982\u6b64\u6c89\u91cd\uff0c\u5251\u950b\u5c1a\u672a\u63a5\u89e6\uff0c\u5df2\u662f\u611f\u5230\u4e00 \u80a1\u5927\u529b\u76f4\u538b\u4e0b\u6765\u3002\u5e78\u4e8f\u4ed6\u7528\u7684\u662f\u8bd5\u63a2\u5bf9\u65b9\u865a\u5b9e\u7684\u5251\u62db\uff0c\u4e00\u89c9\u4e0d\u5999\uff0c\u5251\u5c16\u7acb\u5373\u8f7b\u8f7b\u4e00\u70b9\uff0c\u659c\u8dc3 \u4e09\u6b65\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8fd9\u80a1\u5927\u529b\u7ed9\u4ed6\u5378\u53bb\u4e86\u4e00\u534a\uff0c\u4e5f\u662f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff0c\u201c\u6b64\u4eba\u7684\u5185\u529f\u4e0e\u725f\u5b97 \u6d9b\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u5251\u672f\u7684\u7cbe\u5999\u53ea\u6015\u8fd8\u5728\u725f\u5b97\u6d9b\u4e4b\u4e0a\uff0c\u5e78\u4e8f\u6211\u66fe\u7ecf\u548c\u725f\u5b97\u6d9b\u4ea4\u8fc7\u624b\uff0c\u591a\u5c11\u77e5\u9053\u4e00\u70b9 \u4ed6\u8fd9\u4e00\u6d3e\u5251\u672f\u7684\u8bc0\u7a8d\uff0c\u5426\u5219\u6050\u6015\u8fd8\u5f53\u771f\u4e0d\u6613\u5e94\u4ed8\u5462\uff01\u201d \u3000\u3000\u8fd9\u4eba\u659c\u8dc3\u4e09\u6b65\uff0c\u6a2a\u5251\u53cd\u524a\uff0c\u53c8\u662f\u4e00\u62db\u653b\u5b88\u4ff1\u5999\u7684\u5251\u6cd5\uff0c\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6211\u65b0\u521b\u4e86\u51e0\u4e2a\u62db \u5f0f\uff0c\u548c\u8d35\u6d3e\u7684\u5251\u610f\uff0c\u5012\u4f3c\u9887\u6709\u4e0d\u8c0b\u800c\u5408\u4e4b\u5916\uff0c\u8bf7\u9601\u4e0b\u6307\u6559\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u91d1\u9010\u6d41\u5df2\u662f\u5530\u5530\u5530\u7684\u8fde\u73af\u4e09\u5251\uff0c\u90fd\u662f\u4ece\u6276\u6851\u6d3e\u6700\u7cbe\u5999\u7684\u90a3\u51e0\u62db\u4e4b\u4e2d\u53d8\u5316\u51fa\u6765 \u7684\u65b0\u62db\uff0c\u767b\u65f6\u628a\u90a3\u4e2a\u4eba\u6740\u4e2a\u63aa\u624b\u4e0d\u53ca\uff0c\u53ea\u597d\u8fde\u8fde\u540e\u9000\u3002 \u3000\u3000\u5341\u6570\u62db\u4e00\u8fc7\uff0c\u8fd9\u4eba\u8d8a\u53d1\u5403\u60ca\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u96be\u9053\u6211\u6d3e\u7684\u5251\u8c31\u4e1a\u5df2\u843d\u5728\u4ed6\u7684\u624b\u4e0a\u3002\u201d\u8981\u77e5 \u4ed6\u6240\u5f97\u7684\u672c\u95e8\u5251\u672f\u4e0d\u8fc7\u5341\u4e4b\u4e09\u56db\uff0c\u5982\u4eca\u53d1\u89c9\u91d1\u9010\u6d41\u4f7f\u51fa\u6765\u7684\u8fd8\u5728\u4ed6\u7cbe\u7814\u8fc7\u7684\u5251\u8c31\u4e4b\u4e0a\uff0c\u81ea\u662f \u4e0d\u514d\u6709\u6b64\u7591\u5fc3\u3002 \u3000\u3000\u8fd9\u4eba\u7684\u59bb\u5b50\u770b\u89c1\u4e08\u592b\u582a\u582a\u5c31\u8981\u843d\u8d25\uff0c\u8fd9\u4e00\u60ca\u4e5f\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u62d4\u51fa\u5251\u6765\uff0c\u4fbf\u5373\u559d\u9053\uff1a\u201c\u597d \u5440\uff0c\u4f60\u8fd9\u5c0f\u5b50\u5077\u4e86\u6211\u6d3e\u7684\u5251\u8c31\uff0c\u5c45\u7136\u8fd8\u6562\u5728\u539f\u4e3b\u7684\u9762\u524d\u5356\u5f04\uff0c\u5feb\u5feb\u62ff\u51fa\u6765\u5427\uff0c\u5426\u5219\u4f60\u53ef\u4f11\u60f3 \u6211\u4eec\u653e\u8fc7\u4f60\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53ef\u7b11\u5440\uff0c\u53ef\u7b11\uff01\u201d\u90a3\u5973\u4eba\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u597d\u7b11\uff1f\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6570\u767e \u5e74\u524d\uff0c\u8d35\u6d3e\u662f\u5df2\u5206\u4e3a\u4e09\u652f\uff0c\u5251\u8c31\u65e9\u5df2\u5931\u4f20\uff0c\u65e0\u4eba\u5f97\u7aa5\u5168\u8c8c\u7684\u4e86\u3002\u4f60\u4eec\u6839\u672c\u5c31\u6ca1\u6709\u4e00\u90e8\u5b8c\u6574\u7684 \u5251\u8c31\uff0c\u6211\u53c8\u4ece\u4f55\u5904\u5077\u6765\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u5f97\u8fd9\u6837\u6e05\u695a\uff1f\u201d\u90a3\u5973\u4eba\u77e5\u9053\uff1a\u201c\u672c\u6d3e\u5251\u672f\u7cbe\u5999\u7edd \u4f26\uff0c\u4f60\u5f97\u7684\u7eb5\u7136\u53ea\u662f\u65ad\u7b80\u6b8b\u7bc7\uff0c\u4e5f\u8db3\u4ee5\u50b2\u89c6\u6b66\u6797\uff0c\u6a2a\u884c\u4e2d\u571f\u7684\u4e86\u3002\u53ef\u60dc\u4f60\u4eca\u65e5\u78b0\u4e0a\u6211\u4eec\uff0c\u5077 \u6765\u7684\u4e1c\u897f\u603b\u662f\u8981\u5f52\u8fd8\u539f\u4e3b\u7684\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u4e0d\u8981\u82b1\u8a00\u5de7\u8bed\uff0c\u591a\u65b9\u8fa9\u89e3\u4e86\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u672c\u6765\u60f3\u628a\u725f\u5b97\u6d9b\u7684\u4e8b\u60c5\u544a\u8bc9\u8fd9\u4e24\u592b\u5987\u7684\uff0c\u4f46\u542c\u5f97\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u6709\u6c14\uff0c\u6545 \u610f\u6602\u9996\u5411\u5929\uff0c\u54c8\u54c8\u54c8\u53c8\u518d\u5927\u7b11\u4e09\u58f0\u3002 \u3000\u3000\u90a3\u5973\u4eba\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u53c8\u6709\u4ec0\u597d\u7b11\u7684\u4e86\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u7b11\u4f60\u4eec\u7eb5\u7136\u4e0d\u662f\u4e95\u5e95\u4e4b\u86d9\uff0c\u4e5f\u662f\u89c1\u8bc6\u6709\u9650\uff01\u201d \u3000\u3000\u505a\u4e08\u5927\u7684\u4f3c\u4e4e\u6bd4\u59bb\u5b50\u6709\u6db5\u517b\u5f97\u591a\uff0c\u542c\u4e86\u91d1\u9010\u6d41\u7684\u8bdd\uff0c\u5012\u662f\u5e76\u4e0d\u52a8\u6012\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u91d1\u516c \u5b50\u4f55\u6240\u89c1\u800c\u4e91\u7136\uff0c\u6211\u4eec\u662f\u4e95\u5e95\u86d9\uff0c\u96be\u4ee5\u81ea\u77e5\uff0c\u5012\u8981\u8bf7\u9ad8\u660e\u6307\u6559\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u9ad8\u660e\u4e8c\u5b57\uff0c\u6127\u4e0d\u6562\u5f53\uff0c\u4f46\u636e\u6211\u6240\u77e5\uff0c\u4e2d\u571f\u6b66\u672f\uff0c\u6e90\u8fdc\u6d41\u957f\uff0c\u5373\u5982\u8d35\u6d3e\u7684\u59cb \u7956\u866c\u9aef\u5ba2\u4e5f\u662f\u4ece\u4e2d\u571f\u53bb\u7684\u3002\u5343\u767e\u5e74\u6765\uff0c\u4e2d\u571f\u5404\u5bb6\u5404\u6d3e\u80fd\u4eba\u8f88\u51fa\uff0c\u7eb5\u672a\u5fc5\u80dc\u4e8e\u524d\u8d24\uff0c\u4f46\u63a8\u9648\u4e4b \u5904\u4ea6\u5c5e\u4e0d\u5c11\u3002\u5176\u535a\u5927\u7cbe\u6df1\u4e4b\u5904\uff0c\u5b9e\u975e\u6d45\u5b66\u8005\u6240\u80fd\u7aa5\u5176\u6daf\u5cb8\u3002\u4f60\u4eec\u5938\u79f0\u8d35\u6d3e\u7684\u5251\u672f\u7cbe\u5999\u7edd\u4f26\uff0c \u8a00\u4e0b\u5927\u6709\u8511\u89c6\u4e2d\u571f\u4e4b\u610f\u3002\u4f9d\u6211\u770b\u6765\uff0c\u53ea\u6015\u662f\u6240\u89c1\u672a\u5e7f\u5427\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u516c\u5b50\u7b11\u6211\u4eec\u89c1\u8bc6\u6709\u9650\uff0c\u90a3\u4e48\u8bf7\u516c\u5b50\u628a\u4e2d\u571f\u7684\u9ad8\u660e\u5251\u6cd5\uff0c\u8d50\u6559\u51e0\u62db\uff0c\u8ba9\u6211 \u4eec\u5f00\u5f00\u773c\u754c\u3002\u5f53\u771f\u80dc\u5f97\u8fc7\u5728\u4e0b\uff0c\u5728\u4e0b\u81ea\u7136\u5fc3\u60a6\u8bda\u670d\u3002\u201d\u90a3\u5973\u5b50\u4e5f\u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u662f\u5440\uff0c\u4f60\u65e2\u5938 \u9053\u4e2d\u571f\u6b66\u5b66\u9ad8\u660e\uff0c\u90a3\u53c8\u4f55\u5fc5\u7528\u6211\u4eec\u6276\u6851\u6d3e\u7684\u5251\u6cd5\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u521a\u624d\u4f7f\u7684\u90a3\u51e0\u62db\u5251\u6cd5\u867d\u7136\u662f\u4ece\u8d35\u6d3e\u5251\u6cd5\u4e2d\u53d8\u5316\u51fa\u6765\uff0c\u4f46\u4e5f \u662f\u6211\u81ea\u521b\u7684\u65b0\u62db\uff0c\u5e76\u975e\u8d35\u6d3e\u539f\u6765\u6240\u6709\u3002\u4f60\u4eec\u65e2\u4e0d\u76f8\u4fe1\uff0c\u6211\u5c31\u7528\u5bb6\u4f20\u7684\u51e0\u62db\u7c97\u6d45\u5251\u6cd5\uff0c\u548c\u4f60\u5370 \u8bc1\u5370\u8bc1\uff0c\u770b\u770b\u662f\u5426\u8f93\u4e8e\u8d35\u6d3e\uff1f\u201d \u3000\u3000\u90a3\u6c49\u5b50\u5c31\u662f\u66fe\u7ecf\u7528\u5251\u5212\u7834\u4e86\u91d1\u4e16\u9057\u7684\u5bd2\u7389\u6212\u6307\u7684\u90a3\u4e2a\u4eba\uff0c\u5f53\u65f6\u91d1\u4e16\u9057\u5e76\u4e0d\u4f7f\u7528\u5175\u5668\uff0c\u53ea \u51ed\u5f39\u6307\u795e\u901a\u7684\u529f\u592b\u5f39\u98de\u4e86\u4ed6\u624b\u4e2d\u7684\u957f\u5251\uff0c\u662f\u4ee5\u4ed6\u867d\u7136\u8d25\u5728\u91d1\u4e16\u9057\u624b\u4e0a\uff0c\u4f46\u5bf9\u81ea\u5df1\u7684\u5251\u6cd5\u5374\u8fd8 \u9887\u6709\u81ea\u4fe1\uff0c\u5e76\u4e0d\u5982\u4f55\u5fc3\u670d\uff0c\u5f53\u4e0b\u60f3\u9053\uff1a\u201c\u91d1\u4e16\u9057\u7684\u5251\u6cd5\u53f7\u79f0\u5929\u4e0b\u65e0\u53cc\uff0c\u4f46\u6211\u672a\u66fe\u89c1\u8fc7\uff0c\u4e0d\u77e5 \u662f\u771f\u662f\u5047\uff0c\u4ee5\u90a3\u65e5\u7684\u60c5\u5f62\u800c\u8bba\uff0c\u5728\u5251\u6cd5\u4e0a\u6211\u66fe\u8d62\u4e86\u4ed6\u534a\u62db\uff0c\u4e0d\u4fe1\u4ed6\u7684\u513f\u5b50\u5c31\u80fd\u80dc\u5f97\u8fc7\u6211\uff1f\u201d \u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u53ea\u8981\u4f60\u662f\u7528\u4f60\u672c\u95e8\u7684\u5251\u6cd5\u8d62\u5f97\u4e86\u6211\uff0c\u6211\u5c31\u62f1\u624b\u81e3\u670d\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u5fc5\u5982\u6b64\uff01\u201d\u5f53\u4e0b\u628a\u7384\u94c1\u5b9d\u5251\u633d\u4e86\u4e00\u6735\u5251\u82b1\uff0c\u7f13\u7f13\u7684\u5411\u90a3\u4eba\u523a \u53bb\u3002\u6b63\u662f\uff1a \u3000\u3000\u5251\u672f\u901a\u7384\u81fb\u5316\u5883\uff0c\u5c82\u77e5\u4e2d\u571f\u80dc\u6276\u6851\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e94\u5341\u56de\u3000\u67d4\u679d\u4ee3\u5251\u60ca\u795e\u6280\u82f1\u3000\u7f8e\u76ee\u6d41\u6ce2\u89c5\u6545\u4eba \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e94\u5341\u56de\u3000\u67d4\u679d\u4ee3\u5251\u60ca\u795e\u6280\u82f1\u3000\u7f8e\u76ee\u6d41\u6ce2\u89c5\u6545\u4eba \u3000\u3000\u4e0a\u4e58\u5251\u672f\u8bb2\u7a76\u8f7b\u7075\u7fd4\u52a8\uff0c\u90a3\u4eba\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u4f7f\u5251\u4f7f\u5f97\u8fd9\u4e48\u6162\u7684\uff0c\u5012\u662f\u4e0d\u89c9\u4e00\u6014\u3002\u90a3\u5973\u4eba \u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u5251\u6cd5\uff1f\u5927\u54e5\uff0c\u8fd9\u5c0f\u5b50\u770b\u4e0d\u8d77\u4f60\uff0c\u4f60\u8fd8\u548c\u4ed6\u5ba2\u6c14\u4f5c\u751a\uff1f\u201d\u539f\u6765\u4ed6\u8fd8\u4ee5\u4e3a\u91d1 \u9010\u6d41\u8fd9\u6837\u6162\u541e\u541e\u7684\u51fa\u5251\uff0c\u4e43\u662f\u6545\u610f\u5bf9\u4ed6\u4eec\u7684\u8f7b\u8511\u3002 \u3000\u3000\u90a3\u7537\u7684\u614e\u91cd\u5f97\u591a\uff0c\u4e00\u70b9\u4e5f\u4e0d\u6562\u8f7b\u654c\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u7528\u7684\u662f\u4ec0\u4e48\u5251\u6cd5\u3002\u603b\u662f\u5c0f\u5fc3\u5e94 \u4ed8\u4e3a\u5b9c\u3002\u201d\u5f53\u4e0b\u957f\u5251\u4e00\u6307\uff0c\u95ea\u7535\u822c\u7684\u4fbf\u653b\u8fc7\u53bb\uff01\u4e00\u5feb\u4e00\u6162\uff0c\u6070\u597d\u6210\u4e86\u9c9c\u660e\u7684\u5bf9\u6bd4\uff0c\u4f46\u8fd9\u4eba\u51fa \u5251\u867d\u5feb\uff0c\u4e5f\u5e76\u975e\u5b8c\u5168\u4e0d\u987e\u9632\u5fa1\u7684\u3002\u8fd9\u4e00\u62db\u6b63\u662f\u4ed6\u4eec\u6276\u6851\u6d3e\u5251\u6cd5\u7684\u7cbe\u534e\u6240\u5728\uff0c\u62db\u91cc\u85cf\u62db\uff0c\u5f0f\u4e2d \u5957\u5f0f\uff0c\u5176\u4e2d\u8574\u85cf\u7740\u5341\u5206\u590d\u6742\u7684\u53d8\u5316\u3002 \u3000\u3000\u8fd9\u4eba\u4f01\u56fe\u4ee5\u5feb\u5236\u6162\uff0c\u4e0d\u6599\u5251\u5c16\u521a\u4f38\u5165\u91d1\u9010\u6d41\u6240\u5212\u7684\u5251\u5708\uff0c\u8fd9\u624d\u53d1\u89c9\u56db\u9762\u516b\u65b9\u90fd\u5df2\u7ed9\u91d1\u9010 \u6d41\u7684\u5251\u52bf\u5c01\u4f4f\u3002 \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u4f7f\u7684\u6b63\u662f\u5927\u987b\u5f25\u5251\u5f0f\u4e2d\u7684\u4e00\u62db\u201c\u4ee5\u9759\u5236\u52a8\u201d\u7684\u7edd\u5999\u5251\u62db\uff01\u8981\u77e5\u4efb\u4f55\u9ad8\u660e\u7684\u5251 \u6cd5\uff0c\u4e5f\u603b\u662f\u96be\u4ee5\u505a\u5230\u767e\u5206\u4e4b\u767e\u7684\u653b\u5b88\u517c\u987e\u7684\uff0c\u65e2\u7136\u504f\u91cd\u4e8e\u653b\uff0c\u5c31\u96be\u514d\u4f1a\u6709\u4e00\u5b9a\u7684\u201c\u7a7a\u95e8\u201d\uff0c \u5927\u987b\u5f25\u5251\u5f0f\u7684\u51b3\u62db\u5c31\u662f\u5728\u4e8e\u4ee5\u9759\u5236\u52a8\uff0c\u627e\u5bf9\u65b9\u7684\u5f31\u70b9\u3002 \u3000\u3000\u5e78\u800c\u8fd9\u4eba\u7684\u62db\u6570\u8574\u85cf\u6709\u51e0\u4e2a\u540e\u7740\uff0c\u4e00\u89c9\u4e0d\u5999\uff0c\u7acb\u523b\u53d8\u62db\uff0c\u4fe8\u4f3c\u873b\u8712\u70b9\u6c34\uff0c\u7a0d\u6cbe\u5373\u901d\uff0c\u4f46 \u9976\u662f\u5982\u6b64\uff0c\u4ea6\u5df2\u7a0d\u7a0d\u5403\u4e86\u70b9\u4e8f\uff0c\u53ea\u542c\u5f97\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u4eba\u7684\u957f\u5251\u5df2\u662f\u635f\u4e86\u4e00\u4e2a\u7f3a\u53e3\u3002 \u3000\u3000\u91d1\u9010\u6d41\u62a2\u4e86\u4e0a\u98ce\uff0c\u5fc3\u91cc\u5374\u4e5f\u4e0d\u7981\u6697\u6697\u4f69\u670d\u5bf9\u65b9\u7684\u5251\u6cd5\u4e86\u5f97\u3002\u8981\u77e5\u521a\u624d\u8fd9\u4e00\u62db\uff0c\u4ed6\u5728\u5251\u6cd5 \u4e0a\u867d\u7136\u5236\u4e86\u5148\u673a\uff0c\u4f46\u4ed6\u4e4b\u6240\u4ee5\u80fd\u591f\u628a\u5bf9\u65b9\u7684\u957f\u5251\u635f\u4e86\u4e00\u4e2a\u7f3a\u53e3\uff0c\u90a3\u8fd8\u662f\u4ed7\u7740\u7384\u94c1\u5b9d\u5251\u7684\u5a01\u529b \u3002\u5426\u5219\uff0c\u5018\u82e5\u662f\u6362\u4e86\u4e00\u628a\u666e\u901a\u7684\u9752\u94a2\u5251\u7684\u8bdd\uff0c\u90a3\u5c31\u4ec5\u80fd\u593a\u5f97\u5148\u624b\uff0c\u7a0d\u5360\u4e0a\u98ce\u800c\u5df2\u3002 \u3000\u3000\u539f\u6765\u6276\u6851\u6d3e\u7684\u5251\u672f\u5176\u5b9e\u4e5f\u4e0d\u8f93\u4e8e\u91d1\u9010\u6d41\u6240\u4f7f\u7684\u201c\u5927\u987b\u5f25\u5251\u5f0f\u201d\u7684\uff0c\u4f46\u90a3\u4eba\u5403\u4e8f\u5728\u4ece\u6765\u6ca1 \u6709\u89c1\u8fc7\u201c\u5927\u987b\u5f25\u5251\u5f0f\u201d\uff0c\u800c\u91d1\u9010\u6d41\u53ea\u89c1\u8fc7\u4ed6\u8fd9\u4e00\u6d3e\u7684\u5251\u672f\u3002\u91d1\u9010\u6d41\u609f\u6027\u6781\u9ad8\uff0c\u9488\u5bf9\u5bf9\u65b9\u7684\u5f31 \u70b9\uff0c\u628a\u5927\u987b\u5f25\u5251\u5f0f\u7a0d\u5fae\u53d8\u5316\uff0c\u5c31\u53d8\u6210\u4e86\u5bf9\u65b9\u5251\u672f\u7684\u514b\u661f\u4e86\u3002 \u3000\u3000\u90a3\u5973\u5b50\u89c1\u4e08\u592b\u4e00\u4e2a\u7167\u9762\uff0c\u4fbf\u5373\u5403\u4e8f\uff0c\u5927\u60ca\u4e4b\u4e0b\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u4ed7\u7740\u6709\u4e00\u628a\u5b9d\u5251 \uff0c\u4e5f\u4e0d\u89c1\u5f97\u6709\u4ec0\u4e48\u771f\u5b9e\u7684\u672c\u9886\uff01\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\uff0c\u628a\u4f60\u7684\u5251\u6362\u7ed9\u6211\uff01\u201d\u53f2\u7ea2\u82f1\u5728\u77f3\u6881\u90a3\u9762 \u53eb\u9053\uff1a\u201c\u4e0d\u8981\u4e0a\u5979\u7684\u5f53\uff01\u201d \u3000\u3000\u7384\u94c1\u5b9d\u5251\u662f\u7a00\u4e16\u5947\u73cd\uff0c\u91d1\u9010\u6d41\u4e5f\u4e0d\u653e\u5fc3\u4e0e\u5979\u6362\u5251\uff0c\u53ef\u662f\u4ed6\u53c8\u751a\u4e3a\u597d\u80dc\uff0c\u5fcd\u53d7\u4e0d\u4e86\u90a3\u4e2a\u5973 \u5b50\u7684\u595a\u843d\u3002\u8bf4\u9053\uff1a\u201c\u7ea2\u82f1\uff0c\u4f60\u8fc7\u6765\uff0c\u6211\u548c\u4f60\u6362\u5251\u3002\u201d\u90a3\u5973\u5b50\u9053\uff1a\u201c\u4e0d\u5fc5\u5982\u6b64\u8d39\u4e8b\uff0c\u8ba9\u6211\u4e5f\u6765 \u9886\u6559\u9886\u6559\u4f60\u7684\u5251\u6cd5\u597d\u4e86\uff0c\u4f60\u6709\u5b9d\u5251\uff0c\u6211\u4eec\u591a\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4e5f\u8be5\u8bf4\u662f\u516c\u5e73\u7684\u5427\u3002\u201d\u539f\u6765\u8fd9\u5973\u5b50\u6545 \u610f\u595a\u843d\u91d1\u9010\u6d41\uff0c\u5176\u5b9e\u4e5f\u53ea\u662f\u60f3\u627e\u4e2a\u85c9\u53e3\u800c\u5df2\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u516c\u5e73\uff0c\u516c\u5e73\u5f97\u5f88\uff01\u4f60\u4eec\u4e24\u53e3\u5b50\u5e76\u80a9\u5b50\u4e0a\u5427\u3002\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u7239\u7239\u53ea\u51ed\u53cc \u638c\uff0c\u6253\u8d25\u4e86\u6276\u6851\u4e03\u5b50\uff0c\u6211\u6709\u7384\u94c1\u5b9d\u5251\uff0c\u6599\u60f3\u4e5f\u65e0\u59a8\u3002\u201d\u53c8\u60f3\u9053\uff1a\u201c\u81ea\u4ece\u6211\u5728\u6c5f\u6e56\u95ef\u9053\u4ee5\u6765\uff0c \u6b66\u529f\u80dc\u8fc7\u6211\u7684\u867d\u7136\u78b0\u5230\u4e0d\u5c11\uff0c\u4f46\u5728\u5251\u6cd5\u4e0a\u80dc\u8fc7\u6211\u7684\uff0c\u5374\u662f\u4ece\u672a\u89c1\u8fc7\u3002\u80fd\u591f\u4e0e\u6211\u6253\u6210\u5e73\u624b\u7684\u4e5f \u53ea\u6709\u4e00\u4e2a\u725f\u5b97\u6d9b\u800c\u5df2\uff0c\u96be\u5f97\u5982\u4eca\u78b0\u5230\u6276\u6851\u4e03\u5b50\u4e2d\u4e24\u4e2a\u4f7f\u5251\u7684\u9ad8\u624b\uff0c\u6211\u5012\u8981\u8bd5\u4e00\u8bd5\u80fd\u5426\u654c\u5f97\u4f4f \u4ed6\u4eec\u7684\u8054\u624b\u653b\u51fb\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u51fa\u4e8e\u597d\u80dc\u7684\u5fc3\u7406\uff0c\u60f3\u8981\u8bd5\u8bd5\u81ea\u5df1\u7684\u5b9e\u529b\uff0c\u90a3\u5973\u5b50\u5374\u53ea\u9053\u4ed6\u8bf4\u7684\u4e43\u662f\u53cd\u8bdd\uff0c\u4e0d\u7531\u5f97 \u6ee1\u9762\u901a\u7ea2\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u597d\uff0c\u73b0\u5728\u8ba9\u4f60\u7316\u72c2\uff0c\u7b49\u4e0b\u5c31\u53eb\u4f60\u77e5\u9053\u6211\u7684\u5389\u5bb3\uff01\u201d\u5f53\u4e0b\u7d27\u54ac\u94f6\u7259\uff0c \u5530\u7684\u4e00\u5251\u5c31\u523a\u8fc7\u53bb\u3002 \u3000\u3000\u4e24\u4eba\u8054\u624b\uff0c\u679c\u7136\u5927\u5927\u4e0d\u540c\u3002\u90a3\u5973\u5b50\u7684\u957f\u5251\u5212\u4e86\u4e00\u4e2a\u5f27\u5f62\uff0c\u4f3c\u5b88\u4f3c\u653b\uff0c\u98d8\u5ffd\u4e0d\u5b9a\u3002\u91d1\u9010\u6d41 \u6a2a\u5251\u4e00\u78d5\uff0c\u4ecd\u7528\u5927\u987b\u5f25\u5251\u5f0f\u4ee5\u9759\u5236\u52a8\u7684\u5251\u62db\u3002\u90a3\u7537\u7684\u957f\u5251\u4e00\u6311\uff0c\u7b14\u76f4\u5982\u77e2\u5c31\u653b\u8fdb\u6765\u3002\u53cc\u5251\u76f8 \u4ea4\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u7537\u5b50\u7684\u957f\u5251\u7ed9\u7384\u94c1\u5b9d\u5251\u8361\u5f00\uff0c\u5973\u5b50\u7684\u957f\u5251\u7acb\u5373\u4fbf\u4ece\u7f3a\u53e3\u653b\u8fdb\uff0c\u767b\u65f6\u7834\u4e86 \u91d1\u9010\u6d41\u7684\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u5e78\u800c\u91d1\u9010\u6d41\u5bb6\u4f20\u7684\u201c\u5929\u7f57\u6b65\u6cd5\u201d\u4e5f\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u4e00\u4e2a\u79fb\u5f62\u6362\u4f4d\uff0c\u5728\u95f4 \u4e0d\u5bb9\u53d1\u4e4b\u9645\uff0c\u95ea\u5f00\u4e86\u90a3\u5973\u5b50\u7684\u6740\u624b\u5251\u62db\u3002\u9976\u662f\u5982\u6b64\uff0c\u8863\u89d2\u5df2\u662f\u7ed9\u5251\u5c16\u5212\u7834\u4e86\u3002 \u3000\u3000\u90a3\u5973\u5b50\u7ed9\u7384\u94c1\u5b9d\u5251\u4e00\u51fb\uff0c\u867d\u672a\u78b0\u4e2a\u6b63\u7740\uff0c\u80f8\u53e3\u5df2\u662f\u5982\u53d7\u91cd\u538b\uff0c\u4ea6\u662f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\u3002\u62a2\u4e86 \u5148\u624b\uff0c\u4e0d\u6562\u8ba9\u91d1\u9010\u6d41\u6709\u53cd\u653b\u7684\u673a\u4f1a\uff0c\u7acb\u5373\u4ee5\u5feb\u5251\u8fdb\u653b\uff0c\u4e24\u592b\u59bb\u5de6\u53f3\u5939\u51fb\uff0c\u5c55\u5f00\u4e86\u66b4\u98ce\u9aa4\u96e8\u822c \u7684\u653b\u52bf\uff0c\u903c\u5f97\u91d1\u9010\u6d41\u900f\u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u5934\u706b\u8d77\uff0c\u60f3\u9053\uff1a\u201c\u597d\uff0c\u6211\u62fc\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u4e0d\u4fe1\u5c31\u4e0d\u80fd\u6740\u9000\u4f60\u4eec\u3002\u201d\u5251\u62db\u4e00\u53d8\uff0c \u4e5f\u53d8\u6210\u4e86\u4e00\u6d3e\u8fdb\u624b\u7684\u62db\u6570\u3002\u4f7f\u7684\u662f\u5929\u5c71\u5251\u6cd5\u4e2d\u7684\u8ffd\u98ce\u5251\u5f0f\uff0c\u8ffd\u98ce\u5251\u5f0f\u662f\u4ee5\u653b\u52bf\u51cc\u5389\u89c1\u957f\u7684\u5251 \u5f0f\uff0c\u4e0e\u5927\u987b\u5f25\u5251\u5f0f\u4e4b\u4ee5\u7ef5\u5bc6\u9632\u5fa1\u89c1\u957f\uff0c\u5b9e\u6709\u5f02\u66f2\u540c\u5de5\u4e4b\u5999\u3002 \u3000\u3000\u4e24\u592b\u59bb\u90fd\u662f\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u51db\uff1a\u201c\u4e2d\u539f\u7684\u5251\u6cd5\u679c\u7136\u662f\u4e0d\u4e0b\u4e8e\u672c\u6d3e\u6240\u4f20\uff01\u201d\u4e24\u592b\u59bb\u6253\u4e86\u4e00\u4e2a\u773c \u8272\uff0c\u5251\u6cd5\u4e5f\u5c31\u8ddf\u7740\u53d8\u5316\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u5973\u5b50\u6301\u5251\u6325\u821e\uff0c\u597d\u50cf\u81ea\u5df1\u7ec3\u4e60\u62db\u5f0f\u4f3c\u7684\uff0c\u4e0d\u4e0e\u91d1\u9010\u6d41\u8fd1\u8eab\u7f20\u6597\uff0c\u5374\u5728\u8ddd\u79bb\u4e00\u4e08\u4e4b \u5916\uff0c\u5de6\u5212\u4e00\u4e2a\u5708\u5708\uff0c\u53f3\u5212\u4e00\u4e2a\u5708\u5708\uff0c\u659c\u5212\u4e00\u4e2a\u5708\u5708\uff0c\u6b63\u5212\u4e00\u4e2a\u5706\u5708\uff0c\u53cd\u624b\u6325\u5251\uff0c\u53c8\u662f\u5212\u4e86\u4e00 \u4e2a\u5708\u5708\uff0c\u5708\u91cc\u5957\u5708\uff0c\u91cd\u91cd\u53e0\u53e0\uff0c\u597d\u4f3c\u4e00\u6ce2\u63a5\u7740\u4e00\u6ce2\u7684\u5954\u817e\u7ffb\u5377\uff0c\u5957\u7740\u91d1\u9010\u6d41\u7684\u8eab\u5f62\u3002\u867d\u7136\u662f \u5728\u4e00\u4e08\u5f00\u5916\uff0c\u4f46\u91d1\u9010\u6d41\u53ea\u8981\u7a0d\u4e00\u4e0d\u614e\uff0c\u8eab\u4e0a\u4efb\u4f55\u4e00\u5904\u7684\u8981\u5bb3\uff0c\u90fd\u6709\u4e2d\u5251\u7684\u53ef\u80fd\u3002 \u3000\u3000\u5018\u82e5\u662f\u5355\u6253\u72ec\u6597\uff0c\u8fd9\u5973\u5b50\u7684\u5251\u6cd5\u867d\u7136\u5947\u602a\uff0c\u91d1\u9010\u6d41\u4e5f\u6709\u529e\u6cd5\u7834\u5979\u3002\u4f46\u91d1\u9010\u6d41\u5982\u4eca\u662f\u4ee5\u4e00 \u654c\u4e8c\uff0c\u53ef\u5c31\u6709\u70b9\u96be\u4e8e\u517c\u987e\u4e86\u3002 \u3000\u3000\u5973\u7684\u5212\u51fa\u4e00\u9053\u9053\u5251\u5708\uff0c\u5957\u7740\u91d1\u9010\u6d41\u7684\u8eab\u5f62\uff0c\u7537\u7684\u4fbf\u5373\u8fd0\u5251\u5982\u98ce\uff0c\u7740\u7740\u8fdb\u903c\u3002\u6bcf\u4e00\u5251\u90fd\u662f \u7b14\u76f4\u7684\u523a\u5c06\u51fa\u53bb\uff0c\u548c\u90a3\u5973\u7684\u6bcf\u4e00\u5251\u5212\u6210\u5706\u5708\uff0c\u6070\u597d\u76f8\u53cd\uff0c\u4f46\u5374\u914d\u5408\u5f97\u5999\u5230\u6beb\u5dc5\uff0c\u6559\u91d1\u9010\u6d41\u653b \u4e5f\u4e0d\u5f97\uff0c\u5b88\u4e5f\u4e3a\u96be\u3002\u8fd9\u4e24\u4eba\u7684\u62db\u5f0f\u770b\u4e0a\u7b80\u5355\uff0c\u5176\u5b9e\u5185\u4e2d\u90fd\u662f\u85cf\u7740\u5341\u5206\u590d\u6742\u7684\u53d8\u5316\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5168\u795e\u5e94\u4ed8\uff0c\u7ec6\u5bdf\u4ed6\u4eec\u5251\u6cd5\u7684\u53d8\u5316\uff0c\u6709\u4e9b\u662f\u66fe\u7ecf\u5728\u725f\u5b97\u6d9b\u7684\u5251\u6cd5\u4e2d\u89c1\u8fc7\u7684\uff0c\u672a\u89c1\u8fc7 \u7684\u4e5f\u5927\u81f4\u53ef\u4ee5\u63e3\u6469\u5f97\u5230\u5251\u610f\uff0c\u4f46\u867d\u7136\u5982\u6b64\uff0c\u7531\u4e8e\u5bf9\u65b9\u662f\u53cc\u5251\u5408\u58c1\uff0c\u914d\u5408\u5f97\u5929\u8863\u65e0\u7f1d\uff0c\u91d1\u9010\u6d41 \u7eb5\u7136\u6599\u5f97\u4e2d\u5bf9\u65b9\u7684\u540e\u7740\uff0c\u4e5f\u662f\u65e0\u6cd5\u7834\u89e3\u3002\u4e0d\u8fc7\uff0c\u4e5f\u5e78\u4e8f\u91d1\u9010\u6d41\u609f\u6027\u6781\u9ad8\uff0c\u63e3\u6469\u5f97\u5230\u5bf9\u65b9\u7684 \u201c\u5251\u610f\u201d\uff0c\u5426\u5219\u53ea\u6015\u66f4\u96be\u5e94\u4ed8\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u6697\u81ea\u601d\u91cf\uff1a\u201c\u4e45\u6218\u4e0b\u53bb\uff0c\u6211\u5fc5\u5b9a\u5403\u4e8f\u65e0\u7591\u3002\u8981\u62fc\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u4e5f \u662f\u4e0d\u53ef\u80fd\u7684\u4e86\uff01\u201d\u82e5\u7136\u662f\u5728\u5e73\u5730\uff0c\u91d1\u9010\u6d41\u8fd8\u53ef\u4ee5\u65bd\u5c55\u8f7b\u529f\u9003\u8dd1\uff0c\u4f46\u5728\u8fd9\u534e\u5c71\u7edd\u9669\u4e4b\u5904\u3002\u540e\u9000 \u5c31\u662f\u767e\u4e08\u6df1\u8c37\uff0c\u8fd9\u4e24\u4eba\u5982\u4f55\u80fd\u591f\u5bb9\u5f97\u4ed6\u5b89\u7136\u4ece\u77f3\u6881\u8d70\u8fc7\uff1f\u65e2\u7136\u9000\u65e0\u53ef\u9000\uff0c\u4e5f\u5c31\u552f\u6709\u54ac\u7259\u82e6\u6218 \u4e86\u3002 \u3000\u3000\u77f3\u6881\u90a3\u8fb9\u7684\u53f2\u7ea2\u82f1\u3001\u79e6\u5143\u6d69\u3001\u5c01\u5999\u5ab3\u4e09\u4eba\uff0c\u6bd4\u91d1\u9010\u6d41\u8fd8\u8981\u7740\u6025\u3002\u79e6\u3001\u5c01\u4e8c\u4eba\u65e5\u5fd6\u672c\u9886\u76f8 \u5dee\u592a\u8fdc\uff0c\u8981\u63d2\u624b\u4e5f\u63d2\u4e0d\u8fdb\u53bb\uff0c\u5c01\u5999\u5ab3\u5fc3\u634f\u7740\u4e00\u628a\u51b7\u6c57\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5927\u54e5\u6050\u6015\u6709\u70b9\u4e0d\u5999\uff0c\u4ed6\u4eec \u501a\u591a\u4e3a\u80dc\uff0c\u6211\u4eec\u4e00\u9f50\u8fc7\u53bb\u548c\u4ed6\u62fc\u4e86\u5427\uff01\u201d\u79e6\u5143\u6d69\u7709\u5934\u7d27\u76b1\uff0c\u9ed8\u4e0d\u4f5c\u58f0\u3002\u4ed6\u4e0d\u662f\u5bb3\u6015\u5f3a\u654c\uff0c\u800c \u662f\u6015\u63d2\u4e0d\u8fdb\u624b\uff0c\u800c\u7d2f\u5f97\u91d1\u9010\u6d41\u8981\u7167\u987e\u81ea\u5df1\uff0c\u90a3\u5c31\u662f\u5e2e\u4e86\u5012\u5fd9\u4e86\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u8ba9\u6211\u8fc7\u53bb\uff0c\u82e5\u662f\u4e0d\u6210\u51fa\u4eec\u518d\u6765\u3002\u201d\u5c01\u5999\u5ae6\u9053\uff1a\u201c\u4e0d\uff0c\u6211\u548c\u4f60\u4e00\u540c\u8fc7\u53bb\u3002\u6211 \u4eec\u82e5\u90fd\u4e0d\u6210\uff0c\u5143\u6d69\uff0c\u4f60\u56de\u53bb\u7ed9\u91d1\u5927\u4fa0\u62a5\u4fe1\u3002\u201d\u5c01\u5999\u5ae6\u672a\u5c1d\u6ca1\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u4f46\u5979\u5374\u4e0d\u613f\u610f\u53f2\u7ea2 \u82f1\u72ec\u81ea\u5192\u9669\uff0c\u65e0\u53ef\u5948\u4f55\u4e4b\u4e2d\uff0c\u53ea\u597d\u60f3\u51fa\u8fd9\u4e2a\u529e\u6cd5\uff0c\u597d\u4fdd\u5168\u79e6\u5143\u6d69\u7684\u4e00\u6761\u6027\u547d\u3002\u79e6\u5143\u6d69\u5927\u4e3a\u611f \u52a8\uff0c\u53f2\u7ea2\u82f1\u4e5f\u662f\u6df1\u6df1\u611f\u6fc0\u5979\u7684\u610f\u6c14\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u662f\u548c\u5979\u4ea4\u4e86\u670b\u53cb\uff0c\u771f\u4e0d\u76f8\u4fe1\u5979\u4f1a\u662f \u5c01\u5b50\u8d85\u7684\u5973\u513f\uff01\u201d \u3000\u3000\u79e6\u5143\u6d69\u7259\u6839\u4e00\u54ac\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u4f60\u56de\u53bb\u5411\u91d1\u5927\u4fa0\u62a5\u4fe1\uff0c\u6211\u548c\u7ea2\u82f1\u59d0\u59d0\u8fc7\u53bb\u3002\u201d\u6253\u7b97\u4e00\u8fc7 \u53bb\u5c31\u65bd\u5c55\u6b66\u5f53\u6d3e\u7684\u8fde\u73af\u593a\u547d\u5251\u6cd5\uff0c\u4e0e\u5bf9\u65b9\u62fc\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u51b3\u4e0d\u8981\u91d1\u9010\u6d41\u7167\u987e\u81ea\u5df1\u3002 \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u5ffd\u542c\u5f97\u4e00\u4e2a\u4eba\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u81ea\u8eab\u96be\u4fdd\uff0c\u5435\u4e9b\u4ec0\u4e48\uff1f\u4f60\u4eec\u8981\u53bb\u81ea\u5df1\u9001 \u6b7b\uff0c\u4e0d\u5982\u8ba9\u6211\u6210\u5168\u4e86\u4f60\u4eec\u5427\uff01\u201d \u3000\u3000\u58f0\u5230\u4eba\u5230\uff0c\u8bf4\u5230\u201c\u6210\u5168\u201d\u4e8c\u5b57\uff0c\u90a3\u4e2a\u4eba\u5df2\u662f\u4e00\u6293\u5411\u5c01\u5999\u5ae6\u6293\u4e0b\u3002 \u3000\u3000\u5e78\u800c\u53f2\u7ea2\u82f1\u62d4\u5251\u5f97\u5feb\uff0c\u5c31\u5728\u90a3\u4eba\u4e00\u6293\u6293\u4e0b\u4e4b\u65f6\uff0c\u53f2\u7ea2\u82f1\u5df2\u662f\u5530\u7684\u4e00\u5251\u5411\u4ed6\u523a\u53bb\u3002\u5c01\u5999\u5ab3 \u8fd9\u624d\u8eb2\u907f\u5f97\u5f00\u3002\u5b9a\u775b\u4e00\u770b\uff0c\u5374\u539f\u6765\u662f\u6b27\u9633\u575a\u3002 \u3000\u3000\u539f\u6765\u6b27\u9633\u575a\u5bb6\u4f4f\u534e\u5c71\u5317\u5cf0\uff0c\u548c\u6e05\u98ce\u89c2\u8ddd\u79bb\u4e0d\u8fdc\u3002\u4e0e\u91d1\u9010\u6d41\u4ea4\u624b\u7684\u8fd9\u5bf9\u592b\u5987\uff0c\u4e43\u662f\u5728\u4ed6\u5bb6 \u4e2d\u4f5c\u5ba2\u7684\u3002 \u3000\u3000\u6b27\u9633\u575a\u81ea\u5f82\u5f95\u5c71\u94e9\u7fbd\u800c\u5f52\uff0c\u4e0d\u6562\u56de\u4eac\u590d\u547d\uff0c\u8eb2\u5728\u5bb6\u4e2d\uff0c\u518d\u7ec3\u6b66\u529f\u3002\u201c\u6276\u6851\u4e03\u5b50\u201d\u8d25\u5728\u91d1 \u4e16\u9057\u624b\u4e0b\uff0c\u4e5f\u5404\u81ea\u5206\u6563\u3002\u5176\u4e2d\u4e00\u5bf9\u592b\u5987\u6765\u5230\u534e\u5c71\uff0c\u4ed6\u4eec\u77e5\u9053\u6b27\u9633\u575a\u662f\u8428\u798f\u9f0e\u7684\u5f97\u529b\u624b\u4e0b\uff0c\u662f \u6545\u7279\u5730\u6765\u627e\u4ed6\u3002 \u3000\u3000\u65e0\u5de7\u4e0d\u5de7\uff0c\u6070\u5de7\u91d1\u9010\u6d41\u4e00\u884c\u56db\u4f17\uff0c\u4eca\u65e5\u4e5f\u4e0a\u534e\u5c71\u3002\u7ed9\u4ed6\u4eec\u77a7\u89c1\u3002\u8fd9\u5bf9\u592b\u5987\u5728\u542c\u5f97\u6b27\u9633\u575a \u8bf4\u51fa\u4e86\u91d1\u9010\u6d41\u7684\u8eab\u4efd\u4e4b\u540e\uff0c\u672c\u6765\u5c31\u60f3\u627e\u91d1\u9010\u6d41\u6bd4\u8bd5\uff0c\u62a5\u590d\u7ed9\u4ed6\u7236\u4eb2\u51fb\u8d25\u4e4b\u4ec7\u7684\uff0c\u6b27\u9633\u575a\u5c1a\u672a \u6df1\u77e5\u8fd9\u5bf9\u592b\u5987\u7684\u672c\u9886\uff0c\u5374\u6050\u6015\u4ed6\u4eec\u4e07\u4e00\u4e0d\u654c\uff0c\u56e0\u6b64\u7ed9\u4ed6\u4eec\u60f3\u51fa\u4e86\u4e00\u6761\u8bf1\u654c\u4e4b\u8ba1\u3002\u6df1\u591c\u53d1\u5578\uff0c \u628a\u91d1\u9010\u6d41\u5f15\u5230\u534e\u5c71\u7edd\u9669\u4e4b\u5904\uff0c\u518d\u65bd\u6697\u7b97\u3002\u6b27\u9633\u575a\u5148\u8eb2\u8d77\u6765\uff0c\u4e0d\u8ba9\u91d1\u9010\u6d41\u770b\u89c1\u3002 \u3000\u3000\u7ed3\u679c\u5728\u77f3\u6881\u4e0a\u7684\u6697\u7b97\u867d\u4e0d\u6210\u529f\uff0c\u4f46\u91d1\u9010\u6d41\u5728\u8fd9\u5bf9\u592b\u5987\u8054\u5251\u5939\u653b\u4e4b\u4e0b\uff0c\u4ea6\u662f\u53ea\u6709\u62db\u67b6\u4e4b\u529f \uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\u4e86\u3002 \u3000\u3000\u6b27\u9633\u575a\u89c1\u4ed6\u4eec\u592b\u5987\u5df2\u7ecf\u5927\u5360\u4e0a\u98ce\uff0c\u559c\u51fa\u671b\u5916\uff0c\u4e8e\u662f\u5c31\u6309\u7167\u539f\u6765\u7684\u8ba1\u5212\uff0c\u4ece\u5bc6\u6797\u6df1\u5904\u5077\u5077 \u5730\u94bb\u51fa\u6765\uff0c\u7ed5\u8fc7\u77f3\u6881\uff0c\u6765\u88ad\u51fb\u53f2\u7ea2\u82f1\u548c\u79e6\u65e0\u6d69\u592b\u59bb\u3002 \u3000\u3000\u5e78\u800c\u53f2\u7ea2\u82f1\u53ca\u65f6\u53d1\u89c9\uff0c\u62d4\u5251\u5f97\u5feb\uff0c\u8fd9\u624d\u6551\u4e86\u5c01\u5999\u5ae6\u7684\u6027\u547d\u3002 \u3000\u3000\u5c01\u5999\u5ae6\u770b\u6e05\u695a\u4e86\u662f\u6b27\u9633\u575a\uff0c\u5927\u6012\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u8d3c\u5b50\u5bb3\u4e86\u6211\u7236\u4eb2\u8fd8\u4e0d\u591f\u5417\uff0c\u53c8\u6765\u5bb3\u6211\uff01\u201d \u3000\u3000\u6b27\u9633\u575a\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u5e94\u8be5\u98a0\u5012\u8fc7\u6765\u8bf4\u624d\u662f\uff0c\u4f60\u7684\u7236\u4eb2\u672c\u6765\u548c\u6211\u95f9\u53d7\u8428\u5927\u4eba\u7684\u5dee\u9063\u7684 \uff0c\u4ed6\u5374\u4e2d\u9014\u53d8\u5357\uff0c\u53cd\u800c\u51fa\u5356\u4e86\u8428\u5927\u4eba\u7684\u673a\u5bc6\uff0c\u5bb3\u5f97\u6211\u4e5f\u53d7\u4e86\u4ed6\u7684\u62d6\u7d2f\uff0c\u65ad\u9001\u4e86\u524d\u7a0b\uff0c\u6211\u8fd8\u8981 \u627e\u4f60\u7684\u7236\u4eb2\u7b97\u5e10\u5462\uff01\u4eca\u665a\u4f60\u81ea\u6295\u7f57\u7f51\u3001\u4f60\u4eec\u592b\u59bb\u4e56\u4e56\u7684\u968f\u6211\u4e0a\u4eac\u5427\uff0c\u53ea\u8981\u4f60\u4eec\u8bf4\u51fa\u4e86\u7afa\u5c1a\u7236 \u8fd9\u652f\u53db\u519b\u7684\u8def\u85cf\u6240\u5728\uff0c\u6216\u8bb8\u6211\u8fd8\u5bf9\u4ee5\u9976\u4f60\u7236\u4eb2\u3002\u201d \u3000\u3000\u5c01\u5999\u5ab3\u9a82\u9053\uff1a\u201c\u653e\u5e87\uff01\u201d\u4e00\u5251\u523a\u5c06\u8fc7\u53bb\uff0c\u6b27\u9633\u575a\u54c8\u54c8\u5927\u7b11\uff1a\u201c\u4f60\u8fd9\u4e2b\u5934\u4e5f\u914d\u548c\u6211\u52a8\u624b\uff01 \u201d\u4f38\u624b\u4e00\u5f39\uff0c\u201c\u94ee\u201d\u7684\u4e00\u58f0\uff0c\u628a\u5c01\u5999\u5ae6\u7684\u9752\u94a2\u5251\u5f39\u5f00\uff0c\u79e6\u5143\u6d69\u3001\u53f2\u7ea2\u82f1\u53cc\u5251\u5f00\u51fa\uff0c\u5835\u4f4f\u4e86\u4ed6 \u7684\u8ffd\u51fb\u3002 \u3000\u3000\u6b27\u9633\u575a\u60f3\u62ff\u5c01\u5999\u5ae6\u6765\u5c06\u529f\u8d4e\u7f6a\uff0c\u662f\u6545\u5e76\u672a\u4f7f\u51fa\u770b\u5bb6\u672c\u9886\u3002\u4f46\u5bf9\u4ed8\u53f2\u7ea2\u82f1\u53ef\u4e0d\u540c\u4e86\uff0c\u4ed6\u77e5 \u9053\u53f2\u7ea2\u82f1\u6b66\u529f\u4e0d\u5f31\uff0c\u5979\u548c\u79e6\u5143\u6d69\u8054\u624b\uff0c\u81ea\u5df1\u5018\u82e5\u8f7b\u654c\uff0c\u53ea\u6015\u8fd8\u4f1a\u6298\u5728\u5979\u7684\u624b\u4e2d\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u7684\u5251\u6cd5\u672c\u6765\u5c31\u4e0d\u540c\u51e1\u4fd7\uff0c\u8fd9\u4e2a\u591a\u6708\u6765\uff0c\u548c\u91d1\u9010\u6d41\u65e5\u5915\u76f8\u5904\uff0c\u66f4\u662f\u8d8a\u53d1\u7cbe\u5999\uff0c\u4e00\u8fde \u51e0\u6b21\u6740\u624b\u62db\u6570\uff0c\u6740\u5f97\u6b27\u9633\u575a\u6b65\u6b65\u540e\u9000\u3002 \u3000\u3000\u4f46\u6b27\u9633\u575a\u9000\u51fa\u4e86\u51e0\u6b65\u4e4b\u540e\uff0c\u6697\u8fd0\u7384\u529f\uff0c\u4ea6\u5df2\u4f5c\u597d\u4e86\u51c6\u5907\uff0c\u4e00\u58f0\u51b7\u7b11\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e09\u4e2a \u4eba\u90fd\u8dd1\u4e0d\u4e86\uff01\u201d \u3000\u3000\u6b27\u9633\u575a\u4e00\u638c\u62cd\u51fa\uff0c\u70ed\u98ce\u547c\u547c\uff0c\u5c31\u50cf\u4ece\u6253\u94c1\u5320\u7684\u9f13\u98ce\u7089\u4e2d\u5439\u51fa\u6765\u4f3c\u7684\uff0c\u89e6\u4f53\u5982\u711a\uff01\u8fd9\u662f\u4ed6 \u7684\u5bb6\u4f20\u7edd\u5b66\u201c\u96f7\u795e\u638c\u201d\uff0c\u7528\u8d77\u6765\u751a\u4e3a\u8017\u635f\u771f\u529b\uff0c\u662f\u4ee5\u975e\u5230\u7d27\u5a36\u5173\u5934\uff0c\u51b3\u4e0d\u8f7b\u6613\u4f7f\u7528\u3002 \u3000\u3000\u4e0d\u8fc7\u7247\u523b\uff0c\u53f2\u7ea2\u82f1\u7b49\u4e09\u4eba\u5df2\u662f\u4eba\u5927\u6c57\u6dcb\u6f13\uff0c\u5934\u6655\u8111\u80c0\u3002\u53f2\u7ea2\u82f1\u529f\u529b\u8f83\u9ad8\uff0c\u8fd8\u597d\u4e00\u4e9b\uff0c\u5c01 \u5999\u5ab3\u529f\u529b\u6700\u5f31\uff0c\u66f4\u662f\u70ed\u5f97\u51e0\u4e4e\u900f\u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u5ae6\u59d0\uff0c\u4f60\u6b47\u4e00\u6b47\u3002\u201d\u62bd\u51fa\u957f\u97ad\uff0c\u5de6\u97ad\u53f3\u5251\uff0c\u633a\u8eab\u800c\u4e0a\uff0c\u6b63\u9762\u62b5\u6321\u6b27\u9633\u575a\u7684 \u653b\u51fb\u3002 \u3000\u3000\u6b27\u9633\u575a\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u8d31\u5a62\uff0c\u5e2e\u5916\u4eba\u903c\u6b7b\u4e86\u54e5\u54e5\uff0c\u5c45\u7136\u8fd8\u6562\u5728\u6211\u7684\u9762\u524d\u901e\u5f3a\uff0c\u4eca\u65e5\u6211\u6b63 \u597d\u66ff\u53f2\u767d\u90fd\u62a5\u4ec7\u4e86\u3002\u201d\u4ed6\u4ee5\u4e3a\u5c11\u4e86\u4e00\u4e2a\u5bf9\u624b\uff0c\u53d6\u80dc\u81ea\u5fc5\u66f4\u4e3a\u5bb9\u6613\uff0c\u53f2\u7ea2\u82f1\u62fc\u547d\u5411\u524d\uff0c\u4e0d\u8fc7\u662f \u56f0\u517d\u4e4b\u6597\u800c\u5df2\u3002 \u3000\u3000\u54ea\u77e5\u53f2\u7ea2\u82f1\u97ad\u5251\u53cc\u7edd\uff0c\u97ad\u6cd5\u4e0a\u9020\u8be3\u6bd4\u5251\u6cd5\u8fd8\u8981\u9ad8\u660e\u3002\u5f53\u65e5\u5979\u4e0e\u91d1\u9010\u6d41\u521d\u4f1a\uff0c\u5c31\u66fe\u7528\u4e00\u6839 \u957f\u97ad\u4e0e\u91d1\u9010\u6d41\u6597\u8fc7\u6570\u5341\u56de\u5408\uff0c\u91d1\u9010\u6d41\u4e5f\u4e0d\u8fc7\u53ea\u80fd\u80dc\u5979\u5c11\u8bb8\u800c\u5df2\u3002 \u3000\u3000\u957f\u97ad\u6325\u821e\u77eb\u82e5\u6e38\u9f99\uff0c\u6b27\u9633\u575a\u4e00\u4e2a\u758f\u795e\uff0c\u624b\u80cc\u7740\u4e86\u4e00\u97ad\uff0c\u867d\u975e\u8981\u5bb3\uff0c\u4e5f\u662f\u75db\u5f97\u5341\u5206\u96be\u53d7\u3002 \u6b27\u9633\u575a\u5927\u6012\uff0c\u659c\u8eab\u653b\u4e0a\uff0c\u53f2\u7ea2\u82f1\u77ed\u5251\u4e00\u7ffb\uff0c\u6296\u8d77\u4e09\u6735\u5251\u82b1\uff0c\u4e0a\u523a\u54bd\u5589\uff0c\u4e0b\u523a\u4e39\u7530\uff0c\u4e2d\u523a\u80f8\u53e3 \u7684\u7487\u7391\u8981\u7a74\u3002\u5251\u5c16\u6240\u843d\u4e4b\u5904\uff0c\u5168\u662f\u6307\u5411\u4ed6\u7684\u8981\u5bb3\u3002\u6b27\u9633\u575a\u89c1\u5979\u4f7f\u51fa\u4e24\u8d25\u4ff1\u4f24\u7684\u6740\u624b\uff0c\u4e5f\u662f\u4e0d \u7981\u4e00\u60ca\uff0c\u4e0d\u6562\u6b3a\u8eab\u8fdb\u903c\uff0c\u53ea\u597d\u53c8\u5411\u540e\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u79e6\u5143\u6d69\u4ea6\u5df2\u4ece\u4fa7\u9762\u4e0a\uff0c\u4ed6\u7684\u672c\u9886\u867d\u7136\u8f83\u5f31\uff0c\u4f46\u4ed6\u6240\u4f7f\u7684\u6b66\u5f53\u6d3e\u201c\u8fde\u73af \u593a\u547d\u5251\u6cd5\u201d\u5374\u662f\u5929\u4e0b\u7b2c\u4e00\u7b49\u72e0\u8fa3\u7684\u5251\u6cd5\uff0c\u6b64\u65f6\u62fc\u547d\u5411\u524d\uff0c\u6b27\u9633\u575a\u4e0d\u80fd\u4e0d\u6709\u70b9\u513f\u987e\u5fcc\uff0c\u5f53\u4e0b\u53ea \u5f97\u4e0d\u60dc\u8017\u635f\u771f\u529b\uff0c\u8fde\u7eed\u4f7f\u51fa\u201c\u96f7\u795e\u638c\u201d\u7684\u529f\u592b\uff0c\u53cc\u638c\u8fc2\u56de\u62cd\u51fa\u8fd9\u624d\u628a\u79e6\u5143\u6d69\u903c\u5f00\u3002\u79e6\u5143\u6d69\u70ed \u5f97\u901a\u4f53\u5982\u711a\uff0c\u54ac\u7259\u5fcd\u53d7\u3002 \u3000\u3000\u53f2\u7ea2\u5904\u4ee5\u957f\u97ad\u653b\u654c\uff0c\u77ed\u5251\u9632\u8eab\uff0c\u79e6\u5143\u6d69\u4ece\u65c1\u4fa7\u88ad\uff0c\u4e24\u4eba\u8054\u624b\u62d2\u654c\u3001\u53cd\u800c\u6bd4\u521a\u624d\u4e09\u4eba\u7684\u65f6 \u5019\u60c5\u51b5\u8fd8\u597d\u4e00\u4e9b\uff0c\u8fd9\u4e5f\u662f\u56e0\u4e3a\u79e6\u5143\u6d69\u4e0d\u5fc5\u5206\u5fc3\u6765\u4e3a\u5c01\u5999\u5ae6\u62c5\u5fe7\u7684\u7f18\u6545\u3002 \u3000\u3000\u6b27\u9633\u575a\u548c\u4ed6\u4eec\u6597\u4e86\u6570\u5341\u62db\uff0c\u5140\u662f\u4e0d\u80fd\u53d6\u80dc\uff0c\u4e0d\u540c\u5f97\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\uff1a\u201c\u8fd9\u4e00\u6218\u8fc7\u540e\uff0c\u7eb5\u7136 \u80fd\u591f\u53d6\u80dc\uff0c\u53ea\u6015\u6211\u53c8\u8981\u591a\u8017\u4e09\u5e74\u529f\u529b\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u6b27\u9633\u575a\u5728\u5f82\u5f95\u5c71\u4e0e\u4ef2\u957f\u7edf\u4e00\u573a\u6076\u6218\uff0c\u7ed9\u4ef2\u957f\u7edf\u4ee5\u201c\u6df7\u5143\u4e00\u6c14\u529f\u201d\u9707\u4f24\uff0c\u987b\u5f97\u82e6\u7ec3\u4e09 \u5e74\uff0c\u624d\u80fd\u6062\u53d1\u539f\u6765\u7684\u529f\u529b\u3002\u4e5f\u6b63\u662f\u56e0\u4e3a\u4ed6\u7684\u201c\u96f7\u795e\u638c\u201d\u7684\u5a01\u529b\u5df2\u7ecf\u6253\u4e86\u6298\u6263\uff0c\u6240\u4ee5\u53f2\u3001\u79e6\u4e8c \u4eba\u65b9\u80fd\u652f\u6301\u5230\u6b64\u523b\u3002 \u3000\u3000\u6b27\u9633\u575a\u5bb3\u6015\u53c8\u518d\u591a\u8017\u4e09\u5e74\u529f\u529b\uff0c\u6697\u6697\u53eb\u82e6\uff1b\u6b8a\u4e0d\u77e5\u53f2\u3001\u79e6\u4e8c\u4eba\u6bd4\u4ed6\u66f4\u4e3a\u7740\u6025\u3002\u4ed6\u4eec\u7684\u5251 \u6cd5\u97ad\u6cd5\u867d\u7136\u7cbe\u5999\uff0c\u4f46\u5728\u70ed\u98ce\u9f13\u8361\u4e4b\u4e0b\uff0c\u5403\u529b\u975e\u5e38\uff0c\u52c9\u5f3a\u652f\u6301\uff0c\u5df2\u662f\u5c06\u5230\u7b4b\u75b2\u529b\u7aed\u7684\u7530\u5730\u4e86\u3002 \u3000\u3000\u5728\u77f3\u7cb1\u7684\u90a3\u9762\uff0c\u91d1\u9010\u6d41\u4e5f\u662f\u9677\u4e8e\u82e6\u6597\u4e4b\u4e2d\uff0c\u5904\u5883\u6bd4\u53f2\u7ea2\u82f1\u8fd8\u8981\u6076\u52a3\u3002 \u3000\u3000\u90a3\u5bf9\u592b\u5987\u8d8a\u903c\u8d8a\u7d27\uff0c\u91d1\u9010\u6d41\u4ed7\u7740\u7384\u94c1\u5b9d\u5251\uff0c\u52c9\u5f3a\u62b5\u5fa1\uff0c\u5e78\u4e8f\u4ed6\u7684\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u4e43\u662f\u6700\u4e0a \u4e58\u7684\u9632\u5fa1\u5251\u6cd5\uff0c\u7ef5\u5bc6\u5f02\u5e38\uff0c\u65e0\u9699\u53ef\u51fb\u3002\u52a0\u4e0a\u4e86\u7384\u94c1\u5b9d\u5251\u7684\u5a01\u529b\uff0c\u90a3\u5bf9\u592b\u5987\u60f3\u5728\u6025\u5207\u4e4b\u95f4\u653b\u8fdb \u4ed6\u7684\u5251\u5708\uff0c\u5374\u4e5f\u4e0d\u80fd\u3002 \u3000\u3000\u53ef\u662f\u53f2\u7ea2\u82f1\u90a3\u9762\u7684\u9ad8\u547c\u9163\u6597\u4e4b\u58f0\uff0c\u58f0\u58f0\u5165\u8033\uff0c\u5374\u662f\u4e0d\u80fd\u4e0d\u4ee4\u4ed6\u5927\u5927\u5206\u5fc3\uff01\u5c24\u5176\u662f\u6b27\u9633\u575a \u7684\u201c\u96f7\u795e\u638c\u201d\uff0c\u6bcf\u53d1\u4e00\u638c\uff0c\u90fd\u9690\u9690\u631f\u7740\u98ce\u96f7\u4e4b\u58f0\uff0c\u542c\u8fdb\u4ed6\u7684\u8033\u6735\uff0c\u66f4\u662f\u4e0d\u7981\u4e3a\u53f2\u7ea2\u82f1\u62c5\u5fe7\u4e86 \u3002 \u3000\u3000\u9ad8\u624b\u6bd4\u6597\uff0c\u54ea\u5bb9\u5f97\u4e1d\u6beb\u5206\u5fc3\uff1f\u91d1\u9010\u6d41\u6068\u4e0d\u5f97\u63d2\u7ffc\u98de\u8fc7\u77f3\u6881\uff0c\u52a9\u53f2\u7ea2\u82f1\u4e00\u81c2\u4e4b\u529b\uff0c\u53ef\u662f\u4ed6 \u6b64\u9645\u81ea\u8eab\u96be\u4fdd\uff0c\u53c8\u7109\u80fd\u524d\u53bb\u52a9\u4eba\uff1f \u3000\u3000\u91d1\u9010\u6d41\u672c\u5c31\u5904\u4e8e\u4e0b\u98ce\uff0c\u5fc3\u795e\u4e00\u4e71\uff0c\u66f4\u96be\u62b5\u6562\u3002\u5251\u6cd5\u7684\u7ef5\u5bc6\u5927\u4e0d\u5982\u524d\uff0c\u751a\u81f3\u5728\u6325\u52a8\u7384\u94c1\u5b9d \u5251\u4f7f\u51fa\u590d\u6742\u7684\u5251\u62db\u4e4b\u65f6\uff0c\u4e5f\u6e10\u6e10\u6709\u4e86\u529b\u4e0d\u4ece\u5fc3\u4e4b\u611f\u4e86\u3002 \u3000\u3000\u90a3\u7537\u5b50\u4e1a\u5df2\u770b\u51fa\u91d1\u9010\u6d41\u7684\u7384\u94c1\u5b9d\u5251\u4e43\u662f\u5b9d\u7269\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u8ba4\u8f93\u4e86\u5427\uff0c\u4f60\u7ed9\u6211 \u78d5\u4e09\u4e2a\u54cd\u5934\uff0c\u628a\u8fd9\u67c4\u5251\u653e\u4e0b\u6765\uff0c\u6211\u5c31\u8ba9\u4f60\u8d70\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u559d\u9053\uff1a\u201c\u653e\u5c41\uff01\u201d\u62a1\u8d77\u5b9d\u5251\uff0c\u5f53\u4f5c\u5927\u5200\u6765\u4f7f\uff0c\u4e00\u62db\u201c\u529b\u5288\u534e\u5c71\u201d\uff0c\u4fbf\u65ab\u4e0b\u53bb\uff0c\u90a3 \u6c49\u5b50\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u53ea\u9053\u4ed6\u5df1\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u5374\u5c45\u7136\u8fd8\u80fd\u4f7f\u51fa\u8fd9\u6837\u521a\u731b\u7684\u62db\u6570\u3002\u201d \u5012\u4e5f\u4e0d\u6562\u592a\u8fc7\u7316\u72c2\uff0c\u5f53\u4e0b\u4ee5\u4e00\u62cd\u8f7b\u5fae\u7684\u5251\u6cd5\uff0c\u5316\u89e3\u4e86\u91d1\u9010\u6d41\u8fd9\u62db\uff0c\u4f46\u4ecd\u662f\u4e0d\u7981\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u4e43\u662f\u5728\u4e00\u6012\u4e4b\u4e0b\uff0c\u5f3a\u7528\u771f\u529b\u7684\uff0c\u8fd9\u51e0\u62db\u66b4\u98ce\u9aa4\u96e8\u822c\u7684\u653b\u51fb\uff0c\u4e0d\u8fc7\u662f\u7a0b\u54ac\u91d1\u7684 \u4e09\u677f\u65a7\u800c\u5df2\u3002 \u3000\u3000\u90a3\u5bf9\u592b\u5987\u53cc\u5251\u5408\u58c1\uff0c\u63a5\u8fde\u5316\u89e3\u4e86\u91d1\u9010\u6d41\u7684\u51e0\u62db\u653b\u52bf\uff0c\u8bd5\u51fa\u4ed6\u7684\u6c14\u529b\u4e0d\u52a0\uff0c\u90a3\u7537\u5b50\u8fd9\u624d\u653e \u4e0b\u4e86\u5fc3\u4e0a\u7684\u77f3\u5934\uff1a\u201c\u539f\u6765\u8fd9\u5c0f\u5b50\u679c\u7136\u5df2\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u6211\u5e76\u6ca1\u6709\u770b\u5dee\uff01\u201d\u767b\u65f6\u53c8\u5f97\u610f\u8d77\u6765\uff0c\u54c8 \u54c8\u7b11\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u4e5f\u771f\u591f\u987d\u5f3a\uff0c\u4f69\u670d\uff01\u4f69\u670d\uff01\u4e5f\u4f60\u603b\u662f\u9003\u4e0d\u8fc7\u6211\u4eec\u7684\u638c\u5fc3\u7684\u4e86\uff0c\u82e6\u6597\u65e0 \u76ca\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u7f34\u68b0\u4e86\u5427\u3002\u90a3\u4e09\u4e2a\u54cd\u5934\u4e48\uff0c\u770b\u5728\u4f60\u662f\u4e00\u6761\u6c49\u5b50\u7684\u4efd\u4e0a\uff0c\u4e0d\u78d5\u4e5f\u5c31\u7b97\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u60f3\u8d77\u4e86\u7236\u4eb2\u201c\u4e34\u654c\u6212\u8e81\u201d\u7684\u6559\u8bad\uff0c\u5f3a\u6291\u6012\u706b\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6709\u672c\u9886\uff0c\u628a\u6211\u8fd9\u6761\u547d \u62ff\u53bb\u5c31\u662f\u3002\u60f3\u8981\u8fd9\u628a\u5b9d\u5251\u5417\uff0c\u6709\u6ca1\u6709\u90a3\u4e48\u5bb9\u6613\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6b63\u60f3\u65bd\u5c55\u4e24\u8d25\u4ff1\u4f24\u7684\u6740\u624b\uff0c\u660e\u77e5\u5bf9\u65b9\u6b66\u529f\u9ad8\u5f3a\uff0c\u592b\u59bb\u8054\u5251\uff0c\u8fd9\u62db\u6740\u624b\u672a\u5fc5\u5c31\u80fd\u5982 \u613f\uff0c\u4f46\u603b\u80dc\u4e8e\u675f\u624b\u5f85\u6bd9\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u8f7b\u8f7b\u7684\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u4f3c\u4e4e\u662f\u4e2a\u5973\u5b50\u3002\u90a3\u5bf9 \u592b\u5987\u4e5f\u542c\u89c1\u4e86\uff0c\u8138\u4e0a\u9732\u51fa\u8be7\u5f02\u7684\u795e\u8272\u3002 \u3000\u3000\u91d1\u9010\u6d41\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4ece\u6811\u6797\u4e2d\u8d70\u51fa\u6765\u7684\u7adf\u7136\u662f\u4e2a\u5c11\u5973\uff0c\u4e4c\u9ed1\u7684\u5934\u53d1\uff0c\u660e\u4eae\u7684\u773c\u775b\uff0c\u770b \u6765\u6700\u591a\u4e0d\u8fc7\u4e8c\u5341\u5c81\u5de6\u53f3\u3002 \u3000\u3000\u4e00\u4e2a\u5c11\u5973\uff0c\u6df1\u591c\u5728\u534e\u5c71\u4e4b\u5dc5\u51fa\u73b0\uff0c\u5f53\u7136\u4e0d\u4f1a\u662f\u666e\u901a\u4eba\u5bb6\u7684\u5973\u513f\u4e86\u3002\u91d1\u9010\u6d41\u521a\u4e00\u62ac\u5934\uff0c\u53d1 \u73b0\u5979\u7684\u5f71\u5b50\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u4fbf\u89c1\u5979\u6765\u5230\u4e86\u524d\u9762\uff0c\u8eab\u6cd5\u7aef\u7684\u662f\u8f7b\u7075\u4e4b\u6781\uff01\u91d1\u9010\u6d41\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u91cc \u60f3\u9053\uff1a\u201c\u8fd9\u5c11\u5973\u522b\u7684\u529f\u592b\u4e0d\u77e5\uff0c\u53ea\u8fd9\u4efd\u8f7b\u529f\uff0c\u5df2\u662f\u4e0e\u6211\u4e0d\u76f8\u4e0a\u4e0b\u3002\u5979\u7684\u8eab\u6cd5\u548c\u4e2d\u539f\u5404\u5927\u95e8\u6d3e \u90fd\u4e0d\u76f8\u540c\uff0c\u6050\u6015\u591a\u534a\u662f\u6276\u6851\u4e03\u5b50\u4e00\u8def\u3002\u201d\u4e0d\u8fc7\u91d1\u9010\u6d41\u5df2\u662f\u628a\u751f\u6b7b\u7f6e\u4e4b\u4e8e\u5ea6\u5916\uff0c\u4e5f\u4e0d\u5728\u4e4e\u591a\u4e00 \u4e2a\u654c\u4eba\u4e86\u3002 \u3000\u3000\u8fd9\u5c11\u5973\u8f7b\u8f7b\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u5ffd\u5730\u4ece\u5730\u4e0a\u62fe\u8d77\u51e0\u9897\u77f3\u5b50\uff0c\u628a\u624b\u4e00\u626c\uff0c\u5c31\u5411\u91d1\u9010\u6d41\u4ed6\u4eec\u6253\u4e86 \u8fc7\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7684\u7384\u94c1\u5b9d\u5251\u821e\u5f97\u6cfc\u6c34\u4e0d\u5165\uff0c\u4e00\u9897\u77f3\u5b50\uff0c\u649e\u7740\u4e86\u4ed6\u7684\u5b9d\u5251\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c \u90a3\u9897\u77f3\u5b50\u5316\u6210\u7c89\u788e\uff01 \u3000\u3000\u4f46\u8fd9\u5c11\u5973\u98de\u51fa\u7684\u77f3\u5b50\uff0c\u4e0d\u4ec5\u662f\u6253\u91d1\u9010\u6d41\uff0c\u540c\u65f6\u8fd8\u6709\u4e24\u9897\u77f3\u5b50\uff0c\u6253\u5411\u90a3\u4e00\u5bf9\u592b\u5987\u3002\u90a3\u5bf9\u592b \u5987\u7528\u7684\u662f\u666e\u901a\u7684\u9752\u94a2\u5251\uff0c\u201c\u53ee\u53ee\u201d\u4e24\u58f0\uff0c\u77f3\u5b50\u5f39\u5f00\uff0c\u5374\u6ca1\u6709\u7c89\u788e\u3002 \u3000\u3000\u91d1\u9010\u6d41\u548c\u8fd9\u5bf9\u592b\u5987\u90fd\u611f\u5230\u864e\u53e3\u7a0d\u7a0d\u4e00\u9ebb\uff0c\u867d\u7136\u5e76\u65e0\u59a8\u788d\uff0c\u4ea6\u5df2\u5927\u4e3a\u60ca\u8be7\uff0c\u8981\u77e5\u4ed6\u4eec\u7684\u6b66 \u529f\u5728\u6b66\u6797\u4e2d\u90fd\u8db3\u4ee5\u6324\u8fdb\u4e00\u6d41\u9ad8\u624b\u4e4b\u5217\uff0c\u4e00\u4e2a\u5c11\u5973\u53d1\u51fa\u7684\u4e00\u679a\u5c0f\u5c0f\u7684\u77f3\u5b50\uff0c\u7adf\u7136\u80fd\u591f\u4ee4\u4ed6\u4eec\u7684 \u864e\u53e3\u9178\u9ebb\uff0c\u8fd9\u4efd\u529f\u592b\uff0c\u5f53\u7136\u662f\u51fa\u4e4e\u4ed6\u4eec\u610f\u6599\u4e4b\u5916\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5c24\u5176\u611f\u5230\u60f6\u60d1\uff0c\u4ed6\u672c\u6765\u4ee5\u4e3a\u8fd9\u5c11\u5973\u662f\u201c\u6276\u6851\u4e03\u5b50\u201d\u4e00\u8def\u7684\uff0c\u4f46\u5982\u4eca\u8fd9\u5c11\u5973\u7684\u77f3\u5b50 \u5374\u662f\u201c\u4e00\u89c6\u540c\u4ec1\u201d\uff0c\u6253\u4e86\u4ed6\u4e5f\u6253\u4e86\u90a3\u5bf9\u592b\u5987\uff0c\u5979\u7a76\u7adf\u662f\u53cb\u662f\u654c\uff1f\u91d1\u9010\u6d41\u53ef\u5c31\u715e\u8d39\u7591\u731c\u4e86\u3002 \u3000\u3000\u90a3\u5bf9\u592b\u5987\u5403\u4e86\u4e00\u60ca\uff0c\u9f50\u58f0\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff1f\u4e73\u81ed\u672a\u5e72\u7684\u9ec4\u6bdb\u4e2b\u5934\uff0c\u4e5f\u6562\u5728\u8fd9\u91cc\u591a\u7ba1 \u95f2\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u683c\u683c\u4e00\u7b11\uff0c\u4e0d\u7b54\u4ed6\u4eec\u7684\u8bdd\uff0c\u5374\u5148\u5411\u91d1\u9010\u6d41\u8bf4\u9053\uff1a\u201c\u4f60\u7528\u7684\u662f\u7384\u94c1\u5b9d\u5251\u5427\uff0c\u90a3\u4e48\uff0c \u4f60\u662f\u91d1\u9010\u6d41\u3001\u91d1\u5c11\u4fa0\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u89c1\u5979\u4f3c\u65e0\u6076\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u3002\u6211\u6b63\u662f\u91d1\u9010\u6d41\uff0c\u59d1\u5a18\u6709\u4f55\u6307\u6559\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u56de\u8fc7\u5934\u6765\uff0c\u8fd9\u624d\u5bf9\u90a3\u4e24\u592b\u5987\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8ba4\u8bc6\u6211\uff0c\u6211\u5374\u77e5\u9053\u4f60\u4eec\u3002\u4f60\u4eec\u662f\u6276\u6851\u6d3e \u7684\u4e0d\u662f\uff1f\u201d \u3000\u3000\u90a3\u5987\u4eba\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6211\u542c\u8bf4\u6276\u6851\u4e03\u5b50\u4e4b\u4e2d\uff0c\u53ea\u6709\u4e00\u5bf9\u592b\u5987\uff0c\u4e08\u592b\u540d\u53eb\u77f3\u536b\uff0c\u59bb\u5b50\u540d\u53eb\u6851\u9752\uff0c\u60f3 \u5fc5\u662f\u4f60\u4eec\u4e8c\u4eba\u4e86\u3002\u201d \u3000\u3000\u77f3\u536b\u60ca\u7591\u4e0d\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u600e\u7684\u77e5\u9053\u6211\u4eec\u7684\u6765\u5386\uff1f\u201d \u3000\u3000\u6851\u9752\u5374\u6ca1\u6709\u5979\u4e08\u592b\u90a3\u6837\u5ba2\u6c14\uff0c\u559d\u9053\uff1a\u201c\u4f60\u6765\u523a\u63a2\u6211\u4eec\uff0c\u6709\u4f55\u7528\u610f\uff1f\u5feb\u8bf4\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u725f\u5b97\u6d9b\u662f\u4e0d\u662f\u548c\u4f60\u4eec\u4e00\u8d77\uff1f\u201d \u3000\u3000\u90a3\u5987\u4eba\u9053\uff1a\u201c\u725f\u5b97\u6d9b\u662f\u4ec0\u4e48\u4eba\uff1f\u6ca1\u6709\u542c\u8fc7\uff01\u201d \u3000\u3000\u5979\u7684\u4e08\u592b\u5374\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u4e0d\u662f\u6276\u6851\u5c9b\u725f\u5bb6\u7684\u540e\u4eba\uff1f\u201d\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4e0d\u9519 \uff0c\u4ed6\u6b63\u662f\u6276\u6851\u6d3e\u7b2c\u4e8c\u4ee3\u5e08\u7956\u725f\u6ca7\u6d6a\u4e00\u8109\u76f8\u4f20\u7684\u5ae1\u7cfb\u5b50\u5b59\u3002\u201d\u90a3\u7537\u7684\u4f3c\u4e4e\u9887\u611f\u610f\u5916\uff0c\u8bf4\u9053\uff1a \u201c\u725f\u5bb6\u4e5f\u6709\u4eba\u6765\u4e86\u4e2d\u539f\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4e0d\u662f\u548c\u4f60\u4eec\u4e00\u8d77\uff0c\u8fd9\u6211\u5c31\u653e\u5fc3\u4e86\u3002\u5176\u5b9e\u6211\u4e5f\u65e9\u5c31\u60f3\u5230\uff0c\u4ed6\u662f\u4e0d\u4f1a\u548c \u4f60\u4eec\u4e00\u8def\u7684\u3002\u6211\u8fd9\u4e00\u95ee\u5012\u662f\u591a\u4f59\u4e86\u3002\u201d \u3000\u3000\u90a3\u5987\u4eba\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6276\u6851\u5c9b\u867d\u7136\u5b64\u8eab\u6d77\u5916\uff0c\u4f46\u6276\u6851\u6d3e\u7684\u59cb\u7956\u866c\u9aef\u5ba2\u5374\u662f\u4ece\u4e2d\u539f\u53bb\u7684\u4fa0\u58eb\uff0c\u6276\u6851 \u6d3e\u7684\u5f1f\u5b50\u5e94\u8be5\u5584\u4f53\u7956\u5e08\u7684\u9057\u6559\uff0c\u5c82\u80fd\u4e0e\u4e2d\u539f\u7684\u4fa0\u4e49\u9053\u4e3a\u654c\uff1f\u201d \u3000\u3000\u90a3\u5987\u4eba\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u6559\u8bad\u6211\u4eec\u4e48\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4e0d\u6562\u3002\u6211\u53ea\u662f\u8fd9\u6837\u60f3\uff1a\u725f\u5b97\u6d9b\u82e5\u7136\u6765\u5230\u4e2d\u539f\u7684\u8bdd\uff0c\u4ed6\u662f\u5e94\u8be5\u8bb0\u5f97\u7956\u5148\u7684\u9057 \u8bad\uff0c\u4e0d\u4f1a\u548c\u91d1\u5927\u4fa0\u7236\u5b50\u4f5c\u5bf9\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u559c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u731c\u5f97\u4e00\u70b9\u4e0d\u9519\u3002\u725f\u5b97\u6d9b\u7684\u786e\u4e0d\u662f\u548c\u4ed6\u4eec\u4e00\u8def\uff0c\u4f60\u8981\u77e5\u9053\u4ed6\u7684 \u6d88\u606f\uff0c\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u90a3\u5987\u4eba\u6c14\u5f80\u4e0a\u51b2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u90a3\u59d3\u725f\u7684\u662f\u4ec0\u4e48\u4eba\uff0c\u4f46\u4f60\u5206\u660e\u662f\u8bf4\u6211\u4eec\u4e0d\u5bf9\uff0c\u6211\u5012\u8981 \u8bf7\u95ee\u4f60\u51ed\u4ec0\u4e48\u6765\u6559\u8bad\u6211\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4f60\u4eec\u7684\u6240\u4f5c\u6240\u4e3a\uff0c\u82e5\u662f\u81ea\u95ee\u5408\u4e4e\u7956\u5e08\u7684\u9057\u6559\uff0c\u90a3\u53c8\u4f55\u5fc5\u6015\u522b\u4eba\u8bf4\u5462\uff1f\u201d \u3000\u3000\u90a3\u5987\u4eba\u6012\u9053\uff1a\u201c\u4f60\u5f00\u53e3\u7956\u5e08\uff0c\u95ed\u53e3\u7956\u5e08\uff0c\u4f60\u662f\u6276\u6851\u6d3e\u7684\u5f1f\u5b50\u5417\uff1f\u54fc\uff0c\u5c31\u7b97\u4f60\u662f\u672c\u95e8\u5f1f\u5b50 \uff0c\u51ed\u4f60\u8fd9\u9ec4\u6bdb\u4e2b\u5934\uff0c\u4e5f\u4e0d\u914d\u7528\u7956\u5e08\u7684\u9057\u8bad\u538b\u6211\u3002\u4f60\u6296\u9732\u51e0\u624b\u7ed9\u6211\u77a7\u77a7\u5427\uff0c\u771f\u80fd\u80dc\u5f97\u4e86\u6211\uff0c\u90a3 \u65f6\u4f60\u518d\u5f00\u53e3\u6559\u8bad\u6211\u4eec\uff0c\u4e5f\u8fd8\u4e0d\u8fdf\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u6211\u5c31\u9886\u6559\u4f60\u7684\u672c\u95e8\u5251\u6cd5\u3002\u201d\u5979\u8bf4\u51fa\u201c\u672c\u95e8\u5251\u6cd5\u201d\u56db\u5b57\uff0c\u5df2\u7ecf \u662f\u627f\u8ba4\u4e86\u662f\u6276\u6851\u6d3e\u7684\u5f1f\u5b50\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u8fd9\u5987\u4eba\u542c\u5979\u8bf4\u5f97\u51fa\u6276\u6851\u6d3e\u7684\u6765\u5386\uff0c\u4ea6\u5df2\u731c\u7591\u5979\u662f\u672c\u95e8\u4e2d\u4eba\u4e86\uff0c\u4e0d\u8fc7\u4e0d\u7518\u53d7\u5979\u595a\u843d\uff0c \u800c\u4e14\u4e5f\u8fd8\u60f3\u8bd5\u5979\u4e00\u8bd5\u800c\u5df2\u3002 \u3000\u3000\u90a3\u5c11\u5973\u968f\u624b\u6298\u4e86\u4e00\u6839\u6811\u679d\uff0c\u8bf4\u9053\uff1a\u201c\u6851\u5e08\u59d0\uff0c\u8fdb\u62db\u5427\uff01\u201d \u3000\u3000\u90a3\u5987\u4eba\u9053\uff1a\u201c\u4f60\u8981\u7528\u8fd9\u6839\u6811\u679d\u548c\u6211\u8f83\u91cf\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u8f83\u91cf\u4e8c\u5b57\u592a\u91cd\u4e86\uff0c\u5c0f\u59b9\u53ea\u662f\u60f3\u4e0e\u6851\u5e08\u59d0\u5370\u8bc1\u5370\u8bc1\uff01\u201d \u3000\u3000\u90a3\u5987\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5018\u82e5\u662f\u679c\u7136\u4f7f\u5f97\u51fa\u672c\u95e8\u5251\u6cd5\uff0c\u654c\u5f97\u4e86\u6211\u7684\u5341\u62db\uff0c\u90a3\u65f6\u4f60\u53eb\u6211\u5e08\u59d0\u4e5f \u8fd8\u4e0d\u8fdf\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u6447\u4e86\u6447\u5934\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u6211\u8fd8\u5f53\u771f\u4e0d\u5e0c\u7f55\u6709\u4f60\u8fd9\u6837\u7684\u540c\u95e8\u5462\uff01\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u597d \uff0c\u4f46\u4e5f\u4e0d\u5fc5\u9650\u5b9a\u5341\u62db\u3002\u201d\u4e3e\u8d77\u6811\u679d\uff0c\u8f7b\u8f7b\u4e00\u5212\uff0c\u679c\u7136\u4f7f\u7684\u662f\u6276\u6851\u6d3e\u7684\u8d77\u624b\u5f0f\u3002 \u3000\u3000\u6851\u9752\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u591a\u793c\uff01\u201d\u5530\u7684\u4e00\u5251\u523a\u53bb\uff0c\u90a3\u5c11\u5973\u628a\u6811\u679d\u8f7b\u8f7b\u4e00\u62c2\uff0c\u6851\u9752\u7684\u957f\u5251\u5206\u660e \u5df2\u7ecf\u78b0\u7740\u5979\u7684\u6811\u679d\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u660e\u6643\u7684\u5229\u5251\uff0c\u7adf\u7136\u4e0d\u80fd\u524a\u65ad\u4e00\u6839\u6811\u679d\uff0c\u53cd\u800c\u7ed9\u5979\u7684\u6811\u679d\u5f15\u8fc7 \u4e86\u4e00\u8fb9\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7981\u4e0d\u4f4f\u9ad8\u58f0\u559d\u5f69\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u7684\u6276\u6851\u5251\u6cd5\uff0c\u770b\u6765\u662f\u6bd4\u725f\u5b97\u6d9b\u8fd8\u66f4\u9ad8\u660e\u4e86 \uff01\u6211\u53ea\u9053\u5df2\u7ecf\u53c2\u900f\u4e86\u4ed6\u4eec\u8fd9\u6d3e\u7684\u5251\u610f\uff0c\u8c01\u77e5\u8fd8\u6709\u7cbe\u5999\u4e4b\u5904\uff0c\u6211\u5c1a\u672a\u80fd\u9886\u4f1a\uff01\u602a\u4e0d\u5f97\u7239\u7239\u5e38\u8bf4 \u5929\u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\u3002\u8fd9\u4e2a\u5c11\u5973\uff0c\u53ea\u6015\u6211\u5c31\u672a\u5fc5\u80fd\u6709\u628a\u63e1\u80dc\u5979\u3002\u201d \u3000\u3000\u6851\u9752\u8fd9\u4e00\u60ca\u66f4\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u5f53\u4e0b\u4e0d\u6562\u8f7b\u654c\uff0c\u5c06\u9752\u94a2\u5251\u821e\u5f97\u6cfc\u6c34\u4e0d\u8fdb\uff0c\u81ea\u5fd6\uff1a\u201c\u672c\u6d3e\u4ee5\u67d4 \u514b\u521a\u7684\u8fd9\u65b9\u9762\u5251\u6cd5\uff0c\u662f\u4f60\u80dc\u6211\u4e00\u7b79\uff0c\u4f46\u8c05\u4f60\u4e00\u6839\u6811\u679d\uff0c\u4e5f\u51b3\u4e0d\u80fd\u6253\u8d25\u6211\u624b\u4e2d\u7684\u957f\u5251\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5728\u65c1\u6570\u9053\uff1a\u201c\u7b2c\u4e00\u62db\uff0c\u7b2c\u4e8c\u62db\u2026\u2026\u7b2c\u4e94\u62db\u3001\u7b2c\u516d\u62db\u2026\u2026\u54c8\u54c8\u7b2c\u516b\u62db\u4e86\uff01\u201d\u4ea4\u624b\u4e4b \u524d\uff0c\u6851\u9752\u8bf4\u8fc7\u8981\u5728\u5341\u62db\u4e4b\u5185\u6253\u8d25\u8fd9\u4e2a\u5973\u5b50\uff0c\u91d1\u9010\u6d41\u6050\u5979\u53cd\u53e3\uff0c\u662f\u4ee5\u5728\u65c1\u66ff\u8fd9\u5c11\u5973\u6570\u5979\u7684\u62db\u6570 \u3002 \u3000\u3000\u521a\u8bf4\u5230\u7b2c\u516b\u62db\uff0c\u5ffd\u89c1\u8fd9\u5c11\u5973\u500f\u5730\u4e00\u4e2a\u7ffb\u8eab\uff0c\u8eab\u4f3c\u6c34\u86c7\u6e38\u8d70\uff0c\u6811\u5251\u7a81\u7136\u4f38\u8fdb\u4e86\u6851\u9752\u7684\u5251\u5149 \u5708\u5185\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u6851\u9752\u957f\u5251\u5df2\u7ecf\u8131\u624b\u3002\u539f\u6765\u662f\u7ed9\u5979\u7684\u6811\u679d\u6b63\u597d\u70b9\u7740\u4e86\u8109\u95e8\u3002 \u3000\u3000\u90a3\u5c11\u5973\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7684\u672c\u95e8\u5251\u6cd5\u4f3c\u4e4e\u8fd8\u672a\u5b66\u5f97\u5230\u5bb6\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u4e0d\u8981\u6043\u6280\u6b3a\u4eba\u5427\uff01\u201d \u3000\u3000\u201c\u90a3\u5c11\u5973\u7ffb\u8eab\u8fdb\u201c\u5251\u201d\u7684\u65f6\u5019\uff0c\u8863\u8882\u8f7b\u626c\uff0c\u8863\u89d2\u6709\u7ea2\u7ebf\u7ee3\u7740\u7684\u4e00\u6761\u98de\u9c7c\uff0c\u7ad9\u5728\u65c1\u8fb9\u7684\u91d1 \u9010\u6d41\u548c\u90a3\u7537\u5b50\u90fd\u770b\u89c1\u4e86\u3002 \u3000\u3000\u90a3\u7537\u5b50\u5927\u5403\u4e00\u60ca\uff0c\u5931\u58f0\u53eb\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u662f\u98de\u9c7c\u5c9b\u6797\u5c9b\u4e3b\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6b63\u662f\u5bb6\u7236\u3002\u4ed6\u65e9\u5df2\u4e0d\u662f\u98de\u9c7c\u5c9b\u7684\u5c9b\u4e3b\u4e86\u3002\u4f60\u60f3\u89c1\u89c1\u4ed6\u4e48\u3002\u201d \u3000\u3000\u90a3\u7537\u5b50\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u6797\u5e08\u4f2f\u7684\u5343\u91d1\uff0c\u6055\u6211\u4eec\u65e0\u793c\u4e86\u3002\u201d \u3000\u3000\u6851\u9752\u5782\u5934\u4e27\u6c14\uff0c\u62fe\u8d77\u5251\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8d70\u5427\uff0c\u96be\u9053\u4f60\u8fd8\u5f53\u771f\u8981\u548c\u5979\u53bb\u53c2\u89c1\u6797\u5e08\u4f2f\u4e48\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u91d1\u5c11\u4fa0\uff0c\u4f60\u521a\u624d\u8bf4\u6709\u725f\u5b97\u6d9b\u7684\u6d88\u606f\u544a\u8bc9\u6211\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u8bf7\u4f60\u7a0d\u5f85\u4e00\u4f1a\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6b63\u8981\u8fc7\u53bb\u52a9\u53f2\u7ea2\u82f1\u4e00\u81c2\u4e4b\u529b\uff0c\u5ffd\u89c1\u6b27\u9633\u575a\u8f6c\u8eab\u5c31\u8d70\uff0c\u91d1\u9010\u6d41\u5c1a\u672a\u8d70\u8fc7\u77f3\u7d2b\uff0c\u4ed6\u5df2 \u7ecf\u8dd1\u4e86\u4e0a\u5c71\uff0c\u80cc\u5f71\u4e5f\u770b\u4e0d\u89c1\u4e86\u3002\u539f\u6765\u6b27\u9633\u575a\u63a5\u8fde\u4f7f\u7528\u201c\u96f7\u795e\u638c\u201d\u7684\u529f\u592b\uff0c\u672c\u8eab\u5143\u6c14\u5927\u4e3a\u635f\u8017 \uff0c\u4ea6\u5df2\u5230\u4e86\u5f3a\u5f29\u4e4b\u672b\u7684\u7530\u5730\u3002\u5982\u4eca\u770b\u89c1\u8fd9\u5bf9\u592b\u5987\u5df2\u7ecf\u9003\u8dd1\uff0c\u4ed6\u5982\u4f55\u8fd8\u6562\u604b\u6218\u3002 \u3000\u3000\u90a3\u5c11\u5973\u8ddf\u7740\u8fc7\u6765\uff0c\u4e0e\u53f2\u7ea2\u82f1\u7b49\u4eba\u76f8\u89c1\uff0c\u91d1\u9010\u6d41\u8fd9\u624d\u5f97\u6709\u4f59\u6687\u5411\u5979\u9053\u8c22\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u591a\u8c22\u4ec0\u4e48\uff0c\u6211\u8fd8\u89c9\u5f97\u60ed\u6127\u5462\uff0c\u90fd\u662f\u6211\u7684\u540c\u95e8\u4e0d\u597d\u3002\u201d\u91d1\u9010\u6d41\u7531\u8877\u8d5e\u9053\uff1a \u201c\u59d1\u5a18\uff0c\u4f60\u7684\u5251\u6cd5\u771f\u597d\uff0c\u4eca\u65e5\u591a\u4e8f\u5f97\u4f60\u76f8\u52a9\uff0c\u8981\u4e0d\u7136\u53ea\u6015\u6211\u5df2\u4e0d\u80fd\u7ad9\u5728\u8fd9\u91cc\u8bf4\u8bdd\u4e86\uff0c\u6211\u8fd8\u4e0d \u8be5\u591a\u8c22\u4f60\u4e48\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5c11\u4fa0\uff0c\u53d6\u7b11\u4e86\u3002\u8bf4\u5230\u5251\u6cd5\uff0c\u6211\u624d\u771f\u662f\u4f69\u670d\u4f60\u5462\u3002\u6211\u4e0d\u8fc7\u662f \u56e0\u4e3a\u672c\u95e8\u7684\u5251\u6cd5\u6bd4\u4ed6\u4eec\u61c2\u5f97\u591a\u4e9b\uff0c\u624d\u5f97\u80dc\u4e86\u6851\u9752\u7684\u3002\u91d1\u5c11\u4fa0\uff0c\u4f60\u6ca1\u6709\u5b66\u8fc7\u6211\u4eec\u8fd9\u4e00\u6d3e\u7684\u5251\u6cd5 \uff0c\u6240\u521b\u7684\u65b0\u62db\uff0c\u5374\u975e\u4f46\u6697\u5408\u672c\u6d3e\u7684\u5251\u610f\uff0c\u751a\u81f3\u6709\u51e0\u62db\u6bd4\u6211\u4eec\u539f\u6709\u7684\u5251\u6cd5\u8fd8\u66f4\u9ad8\u660e\uff0c\u8fd9\u624d\u662f\u4e86 \u4e0d\u8d77\u5462\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u59d1\u5a18\u4f60\u771f\u4f1a\u5ba2\u6c14\uff0c\u4f46\u4e0d\u77e5\u4f60\u4f55\u4ee5\u4f1a\u6765\u5230\u6b64\u95ee\uff0c\u53c8\u662f\u600e\u5730\u77e5\u9053\u6211\u7684\u6765\u5386\u7684\uff1f \u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6211\u7239\u7239\u6700\u4f69\u670d\u7684\u4eba\u5c31\u662f\u4ee4\u5c0a\uff0c\u6276\u6851\u4e03\u5b50\u66fe\u4e0e\u4ee4\u5c0a\u4e3a\u96be\u4e4b\u4e8b\uff0c\u6211\u7239\u7239\u5df2\u7ecf\u77e5 \u9053\u4e86\uff0c\u4f46\u6211\u4eec\u5374\u4e0d\u77e5\u9053\u725f\u5b97\u6d9b\u662f\u5426\u5728\u6276\u6851\u4e03\u5b50\u4e4b\u5185\uff0c\u6211\u662f\u7279\u5730\u6765\u627e\u4ed6\u7684\uff0c\u81f3\u4e8e\u6211\u4e4b\u6240\u4ee5\u6765\u5230 \u534e\u5c71\u5bfb\u627e\uff0c\u4e43\u662f\u5f97\u81ea\u7239\u7239\u4e00\u4f4d\u670b\u53cb\u7684\u6307\u70b9\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u3001\u79e6\u5143\u6d69\u3001\u5c01\u5999\u5ae6\u7b49\u4eba\u4f9d\u6b21\u4e0a\u6765\u4e0e\u8fd9\u5c11\u5973\u89c1\u9762\uff0c\u5404\u81ea\u62a5\u4e86\u59d3\u540d\u3002\u91d1\u9010\u6d41\u9053\uff1a\u201c\u59d1 \u5a18\u4f60\u53ef\u4ee5\u628a\u4f60\u7684\u59d3\u540d\u6765\u5386\u544a\u8bc9\u6211\u4eec\u4e48\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4eca\u5c0a\u662f\u6211\u7239\u7239\u6700\u4f69\u670d\u7684\u4eba\uff0c\u53ea\u6068\u65e0\u7f18\u76f8\u89c1\u3002\u6211\u4eec\u7684\u6765\u5389\u8bf4\u7ed9\u4f60\u542c\u662f\u65e0\u59a8\u3002 \u6211\u7239\u7239\u5e74\u8fc8\uff0c\u4e0d\u613f\u60f9\u4e8b\uff0c\u4f46\u60f3\u9690\u5c45\u5ea6\u8fc7\u4f59\u5e74\u3002\u5e0c\u671b\u4f60\u4e0d\u8981\u8bf4\u7ed9\u522b\u4e2a\u77e5\u9053\uff1a\u201c \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4e2a\u5f53\u7136\u3002\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u539f\u6765\u5979\u7684\u7236\u4eb2\u4e43\u662f\u9690\u59d3\u57cb\u540d\u7684\u9ad8\u58eb\uff0c\u602a\u4e0d\u5f97\u6ca1 \u4eba\u77e5\u9053\uff1a\u201c \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6211\u59d3\u6797\uff0c\u540d\u53eb\u65e0\u53cc\u3002\u6211\u4eec\u672c\u6765\u662f\u5728\u6276\u6851\u5c9b\u4fa8\u5c45\u7684\uff0c\u5df2\u7ecf\u6709\u4e86\u597d\u51e0\u4ee3\u4e86\u3002\u725f \u5b97\u6d9b\u662f\u627e\u7684\u5f1f\u5144\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u602a\u4e0d\u5f97\u4f60\u8981\u627e\u4ed6\u3002\u4f60\u4eec\u662f\u51e0\u65f6\u6765\u5230\u4e2d\u539f\u7684\uff0c\u548c\u725f\u5b97\u6d9b\u4e00\u76f4\u6ca1\u901a \u97f3\u8baf\u5417\uff1f\u201d \u3000\u3000\u6797\u65e0\u53cc\u9053\uff1a\u201c\u6211\u4eec\u6765\u4e86\u5df2\u7ecf\u5dee\u4e0d\u591a\u6709\u5341\u4e00\u5e74\u4e86\u3002\u201d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5c0f\u65f6\u5019\uff0c\u6211\u4eec\u548c\u725f\u5bb6\u662f \u4f4f\u5728\u4e00\u8d77\u7684\uff0c\u6211\u7684\u6bcd\u4eb2\u5c31\u662f\u725f\u5b97\u6d9b\u7684\u59d1\u59d1\u3002\u6211\u548c\u4ed6\u7684\u5251\u6cd5\u90fd\u662f\u6211\u7684\u7236\u4eb2\u6559\u7684\u3002\u4f46\u4ed6\u7684\u5e74\u7eaa\u6bd4 \u6211\u5927\u4e86\u5341\u5c81\uff0c\u6211\u4eec\u4e3e\u5bb6\u8fc1\u56de\u4e2d\u539f\u7684\u65f6\u5019\uff0c\u6211\u624d\u4e0d\u8fc7\u5341\u4e00\u4e8c\u5c81\uff0c\u4ed6\u5df2\u957f\u5927\u6210\u4eba\u4e86\u3002\u6240\u4ee5\uff0c\u4ed6\u73b0 \u5728\u82e5\u662f\u89c1\u4e86\u6211\uff0c\u6050\u6015\u4ed6\u8fd8\u5728\u5fc5\u8ba4\u5f97\u6211\u5462\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u4eec\u4e3e\u5bb6\u8fc1\u56de\u4e2d\u539f\uff0c\u725f\u5b97\u6d9b\u77e5\u4e0d\u77e5\u9053\uff1f\u201d \u3000\u3000\u6797\u65e0\u53cc\u9053\uff1a\u201c\u662f\u8fd9\u6837\u7684\uff0c\u8d77\u521d\u6211\u4eec\u672c\u6765\u548c\u725f\u5bb6\u4f4f\u5728\u540c\u4e00\u4e2a\u5730\u65b9\uff0c\u540e\u6765\u6211\u7684\u7239\u7239\u770b\u4e0d\u60ef\u502d \u4eba\u7684\u6c14\u7130\uff0c\u548c\u4e00\u73ed\u6e14\u6c11\u907f\u5230\u6d77\u5916\u4e00\u4e2a\u8352\u5c9b\u3002\u5f00\u57a6\u8352\u5730\uff0c\u79cd\u7530\u6253\u730e\u3002\u65e5\u5b50\u5012\u662f\u8fc7\u5f97\u65e0\u62d8\u65e0\u675f\u3002 \u7239\u7239\u7ed9\u8fd9\u4e2a\u5c9b\u8d77\u4e86\u4e00\u4e2a\u540d\u53eb\u505a\u98de\u9c7c\u5c9b\u3002\u53ef\u60dc\u8fd9\u6837\u7684\u751f\u6d3b\u5374\u8fc7\u4e0d\u4e86\u51e0\u5e74\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u3002\u201d \u3000\u3000\u6797\u65e0\u53cc\u8bf4\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u98de\u9c7c\u5c9b\u5df2\u7ecf\u53d8\u4e86\u6837\u5566\u3002\u5f53\u5b83\u662f\u4e00\u7247\u8352\u829c\u4e4b\u65f6\uff0c\u6ca1\u4eba\u7406\u4f1a\uff1b\u4f46\u5728\u5b83 \u5f00\u53d1\u4e4b\u540e\uff0c\u53ef\u5c31\u6709\u4eba\u5782\u6d8e\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u662f\u6d77\u76d7\u4e48\u3002\u201d \u3000\u3000\u6797\u65e0\u53cc\u9053\uff1a\u201c\u4e0d\u662f\u6d77\u76d7\uff0c\u4f46\u4e5f\u53ef\u4ee5\u8bf4\u662f\u6bd4\u6d77\u76d7\u7a81\u7316\u7357\u7684\u6d77\u76d7\u3002\u662f\u502d\u4eba\u628a\u98de\u9c7c\u5c9b\u5360\u9886\u4e86\u3002 \u201d \u3000\u3000\u91d1\u9010\u6d41\u6124\u7136\u9053\uff1a\u201c\u4ed6\u4eec\u5012\u5f88\u4f1a\u6361\u73b0\u6210\uff01\u201d \u3000\u3000\u6797\u65e0\u53cc\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u5417\uff01\u7239\u7239\u66fe\u5728\u8fd9\u4e2a\u5c9b\u4e0a\u6d41\u8fc7\u8bb8\u591a\u8840\u6c57\uff0c\u5374\u7ec8\u4e8e\u7ed9\u4ed6\u4eec\u903c\u8d70\u4e86\u3002\u5f00\u53d1 \u98de\u9c7c\u5c9b\u7684\u8fd9\u5e2e\u6e14\u6c11\uff0c\u516c\u63a8\u7239\u7239\u505a\u5c9b\u4e3b\uff0c\u5728\u90a3\u51e0\u5e74\u4e2d\uff0c\u66fe\u4ee5\u51e0\u6b21\u51fb\u9000\u4e86\u502d\u56fd\u7684\u4fb5\u88ad\u3002\u540e\u6765\u502d\u56fd \u7684\u5175\u8239\u5f00\u5230\uff0c\u519b\u961f\u767b\u4e86\u9646\uff0c\u5360\u9886\u4e86\u98de\u9c7c\u5c9b\uff0c\u5f53\u7136\u662f\u6ca1\u6709\u6211\u7239\u7239\u7acb\u8db3\u4f59\u5730\u4e86\u3002\u201d \u3000\u3000\u6797\u65e0\u53cc\u53f9\u4e86\u53e3\u6c14\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5728\u98de\u9c7c\u5c9b\u4e0d\u80fd\u7acb\u8db3\uff0c\u5f53\u7136\u4e5f\u5c31\u4e0d\u80fd\u591f\u518d\u56de\u5230\u502d\u4eba\u5360 \u9886\u7684\u6276\u6851\u5c9b\u53bb\u5566\uff0c\u5f53\u65f6\u725f\u5b97\u6d9b\u6b63\u5728\u6d77\u5916\u5404\u5904\u5c0f\u5c9b\u627e\u5bfb\u4ed6\u7684\u540c\u95e8\uff0c\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u4ed6\u5230\u4e86\u4ec0\u4e48\u5730 \u65b9\u3002 \u3000\u3000\u201c\u7239\u7239\u672c\u6765\u5c31\u60f3\u91cd\u5f52\u6545\u56fd\u7684\uff0c\u65e2\u7136\u5927\u6d77\u5916\u65e0\u5730\u53ef\u4ee5\u5bb9\u8eab\uff0c\u6211\u4eec\u5c31\u56de\u5230\u4e2d\u539f\u6765\u4e86\u3002\u7b97\u6765\u5df2 \u7ecf\u5341\u5e74\u4e86\u3002\u5341\u5e74\u6765\u6211\u4eec\u4e00\u76f4\u5728\u4e00\u4e2a\u5c0f\u6e14\u6751\u9690\u5c45\uff0c\u6ca1\u4eba\u77e5\u9053\u6211\u4eec\u7684\u6765\u5389\uff0c\u9664\u4e86\u7239\u7239\u7684\u51e0\u4f4d\u670b\u53cb \u4e4b\u5916\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u90a3\u4eba\u662f\u2026\u2026\u201d \u3000\u3000\u6797\u65e0\u53cc\u9053\uff1a\u201c\u662f\u7239\u7239\u56de\u5230\u4e2d\u539f\u540e\uff0c\u6240\u7ed3\u4ea4\u7684\u552f\u4e00\u670b\u53cb\u3002\u4ed6\u7684\u540d\u5b50\u53eb\u5c09\u8fdf\u70af\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u5c09\u8fdf\u70af\uff01\u627e\u89c1\u8fc7\u4ed6\u7684\u3002\u4ed6\u672c\u6765\u662f\u5173\u5916\u7684\u9a6c\u8d3c\uff0c\u540e\u6765\u6295\u5165\u4e86\u4e49\u519b \uff0c\u73b0\u5728\u5df2\u662f\u5c0f\u91d1\u5ddd\u4e49\u519b\u7684\u4e00\u4f4d\u9886\u8896\u4eba\u7269\u4e86\u3002\u201d \u3000\u3000\u6797\u65e0\u53cc\u9053\uff1a\u201c\u5c0f\u5fc3\u4e86\uff0c\u6211\u4eec\u4ece\u6d77\u5916\u56de\u6765\uff0c\u662f\u7ecf\u8fc7\u671d\u9c9c\u5148\u5230\u5173\u5916\uff0c\u540e\u5230\u4e2d\u539f\u7684\uff0c\u5f53\u65f6\u5c09\u8fdf \u70af\u4e0d\u662f\u9a6c\u8d3c\uff0c\u5728\u5173\u5916\u4e00\u9762\u6297\u51fb\u6e05\u5175\uff0c\u4e00\u9762\u4e5f\u548c\u4ece\u671d\u9c9c\u6f5c\u5165\u5173\u5916\u7684\u502d\u56fd\u6d6a\u4eba\u4f5c\u6218\uff0c\u7239\u7239\u66fe\u5e2e\u8fc7 \u4ed6\u7684\u5fd9\uff0c\u6211\u4eec\u548c\u5c09\u8fdf\u70af\u5206\u624b\u4ea6\u5df2\u5c06\u8fd1\u5341\u5e74\uff0c\u6700\u8fd1\u624d\u91cd\u89c1\u4e86\u9762\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u604d\u7136\u5927\u609f\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u7684\u90a3\u4f4d\u6307\u70b9\u4f60\u5230\u534e\u5c71\u6765\u627e\u5bfb\u725f\u5b97\u6d9b\u7684\u4eba\uff0c\u6562\u60c5\u5c31\u8db3 \u8c22\u8fdf\u70af\u4e86\uff1f\u201d \u3000\u3000\u6797\u65e0\u53cc\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6b63\u662f\u3002\u4ed6\u867d\u7136\u4e0d\u8ba4\u8bc6\u725f\u5b97\u6d9b\uff0c\u4f46\u4ed6\u5374\u66fe\u548c\u6276\u6851\u4e03\u5b50\u4e2d\u4eba\u7269\u72e0 \u72e0\u6253\u8fc7\u4e00\u573a\uff0c\u6709\u5173\u6276\u6851\u4e03\u5b50\u7684\u6d88\u606f\uff0c\u5c31\u662f\u4ed6\u5e26\u6765\u7ed9\u627e\u7239\u7239\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u5c09\u8fdf\u70af\u4e5f\u662f\u6211\u6c5f\u5e08\u5144\u7684\u597d\u670b\u53cb\uff0c\u6211\u4e5f\u5341\u5206\u6000\u5ff5\u4ed6\u5462\u3002\u4ed6\u73b0\u5728 \u600e\u4e48\u6837\u4e86\uff1f\u6b63\u662f\uff1a \u3000\u3000\u8bf4\u8d77\u6e0a\u6e90\u90fd\u4e00\u8def\uff0c\u5173\u4e1c\u8c6a\u6770\u4e45\u77e5\u540d\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e94\u5341\u4e00\u56de\u3000\u795e\u97ad\u6697\u5668\u79f0\u53cc\u7edd\u3000\u5feb\u9a6c\u6325\u5200\u4f1a\u4e03\u96c4 \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e94\u5341\u4e00\u56de\u3000\u795e\u97ad\u6697\u5668\u79f0\u53cc\u7edd\u3000\u5feb\u9a6c\u6325\u5200\u4f1a\u4e03\u96c4 \u3000\u3000\u6797\u65e0\u53cc\u9053\uff1a\u201c\u4eca\u665a\u6708\u8272\u660e\u6717\uff0c\u90a3\u5929\u665a\u4e0a\uff0c\u5374\u662f\u65e0\u6708\u65e0\u661f\u3002\u5c09\u8fdf\u70af\u592b\u59bb\u7a81\u7136\u6765\u5230\u6211\u4eec\u5bb6\u91cc \uff0c\u4ed6\u4eec\u662f\u8df3\u5899\u8fdb\u6765\u7684\uff0c\u7ed9\u6211\u53d1\u89c9\u4e86\uff0c\u6211\u8fd8\u4ee5\u4e3a\u662f\u778e\u4e86\u773c\u775b\u7684\u5f3a\u76d7\uff0c\u5149\u987e\u5230\u6211\u4eec\u7a77\u4eba\u5bb6\u5462\u3002\u6211 \u521a\u521a\u7ec3\u4f1a\u4e86\u7239\u7239\u6240\u6559\u7684\u6697\u5668\u529f\u592b\uff0c\u8863\u888b\u91cc\u6709\u65e5\u95f4\u5403\u5269\u7684\u8695\u8c46\uff0c\u4e8e\u662f\u5c31\u60f3\u62ff\u4ed6\u4eec\u6765\u5f00\u5f00\u73a9\u7b11\uff0c \u6293\u8d77\u516d\u7c92\u8695\u8c46\uff0c\u6bcf\u4eba\u5949\u9001\u4e09\u7c92\uff0c\u6253\u5411\u4ed6\u4eec\u7684\u7a74\u9053\u3002 \u3000\u3000\u201c\u6211\u4ee5\u4e3a\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u5012\u4e0b\u53bb\u7684\uff0c\u4e0d\u6599\u8695\u8c46\u6253\u4e86\u51fa\u53bb\uff0c\u5ffd\u542c\u5f97\u90a3\u7537\u7684\u8bf4\u9053\uff0c\u2018\u6664\uff0c\u5473\u9053\u4e0d \u9519\uff01\u2019\u90a3\u5973\u7684\u5374\u8d5e\u9053\uff1a\u2018\u597d\u4fca\u7684\u6697\u5668\u529f\u592b\uff01\u2019\u6211\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u62d4\u51fa\u5251\u6765\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u542c \u5f97\u7239\u7239\u8bf4\u9053\uff0c\u2018\u53cc\u513f\uff0c\u4e0d\u8bb8\u52a8\u624b\uff01\u6765\u7684\u53ef\u662f\u5c09\u8fdf\u5144\u5ac2\u4e48\uff1f\u2019\u90a3\u7537\u7684\u7b11\u9053\uff1a\u2018\u4e0d\u9519\uff0c\u662f\u8001\u670b\u53cb \u6765\u770b\u4f60\u4e86\u3002\u6797\u5927\u54e5\u771f\u597d\u773c\u529b\uff0c\u4e00\u522b\u5341\u5e74\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u8ba4\u4e0d\u5f97\u6211\u4eec\u4e86\u5462\u3002\u2019\u7239\u7239\u4eae\u8d77\u4e86\u706f\uff0c\u6211 \u624d\u8ba4\u51fa\u662f\u4ed6\u4eec\u592b\u59bb\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u5c09\u8fdf\u70af\u7684\u59bb\u5b50\u7948\u5723\u56e0\uff0c\u5916\u53f7\u5343\u624b\u89c2\u97f3\uff0c\u4f60\u4ee5\u524d\u4e0d\u77e5\u9053\u4e48\uff1f\u201d \u3000\u3000\u6797\u65e0\u53cc\u9053\uff1a\u201c\u6211\u548c\u4ed6\u4eec\u5728\u5173\u5916\u7ed3\u8bc6\u7684\u65f6\u5019\uff0c\u6211\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u90a3\u665a\u624d\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\u7948\u5723\u56e0\u7684 \u6697\u5668\u529f\u592b\u3002\u201d \u3000\u3000\u63a5\u7740\u7b11\u9053\uff1a\u201c\u5343\u624b\u89c2\u97f3\u7684\u5916\u53f7\u786e\u5b9e\u540d\u4e0d\u865a\u4f20\uff0c\u706f\u5149\u4e00\u4eae\uff0c\u5979\u644a\u5f00\u4e86\u624b\u638c\uff0c\u6211\u6253\u5979\u7684\u4e09\u7c92 \u8695\u8c46\uff0c\u90fd\u5728\u5979\u7684\u624b\u638c\u5fc3\u4e4b\u4e2d\u3002\u5979\u8bf4\uff1a\u2018\u60f3\u4e0d\u5230\u5230\u65e0\u53cc\u4f84\u5973\u957f\u5f97\u90a3\u4e48\u9ad8\u4e86\uff0c\u6697\u5668\u529f\u592b\u53ef\u7740\u5b9e\u4e0d \u9519\u554a\u3002\u2019\u7239\u7239\u7b11\u6211\u73ed\u95e8\u5f04\u65a7\uff0c\u6211\u7f9e\u5f97\u8138\u90fd\u7ea2\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u5f97\u6709\u8da3\uff0c\u4e0d\u89c9\u7b11\u9053\uff1a\u2018\u7948\u5723\u56e0\u4e3a\u4eba\u9762\u51b7\u5fc3\u70ed\uff0c\u5979\u4e00\u5b9a\u4f20\u6388\u4e86\u4f60\u51e0\u624b\u6697\u5668\u7684\u529f\u592b \u4e86\u5427\uff1f\u5c09\u8fdf\u70af\u53ef\u8fd8\u662f\u90a3\u80a1\u7c97\u8c6a\u7684\u813e\u6c14\uff0c\u600e\u7684\u628a\u4f60\u7684\u8695\u8c46\u5168\u90e8\u5403\u4e86\u3002\u540e\u6765\u600e\u6837\uff1f\u201d \u3000\u3000\u6797\u65e0\u53cc\u5657\u55e4\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u987e\u8bf4\u95f2\u8bdd\uff0c\u53ef\u5fd8\u4e86\u6b63\u7ecf\u7684\u4e8b\u4e86\u3002\u201d\u5979\u5728\u6e14\u6751\u957f\u5927\uff0c\u751f\u6d3b \u5355\u7eaf\uff0c\u4e0d\u61c2\u4e16\u6545\uff0c\u8bf4\u5230\u9ad8\u5174\u4e4b\u5904\uff0c\u6d3b\u50cf\u4e00\u4e2a\u5929\u771f\u70c2\u6f2b\u7684\u5973\u5b69\u3002\u91d1\u9010\u6d41\u548c\u53f2\u7ea2\u82f1\u90fd\u662f\u4e0d\u7531\u5f97\u5fc3 \u5e95\u91cc\u6697\u6697\u6b22\u559c\u5979\uff0c\u60f3\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u4eb2\u773c\u89c1\u5230\uff0c\u771f\u60f3\u4e0d\u5230\u8fd9\u6837\u4e00\u4e2a\u5929\u771f\u6d3b\u6cfc\u7684\u5c0f\u59d1\u5a18\u4f1a\u6709\u8fd9\u6837 \u9ad8\u660e\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u6797\u65e0\u53cc\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u7239\u7239\u8bf4\uff0c\u4ec0\u4e48\u98ce\u628a\u4f60\u4eec\u5439\u6765\u7684\uff1f\u5c09\u8fdf\u70af\u7b11\u9053\uff1a\u201c\u6211\u627e\u4e86\u5341\u5e74\uff0c\u624d\u77e5 \u9053\u4f60\u4eec\u8eb2\u5728\u8fd9\u91cc\uff0c\u4f60\u4ee5\u4e3a\u662f\u51d1\u5de7\u7684\u5417\uff1f\u6797\u5927\u54e5\uff0c\u6211\u8fd9\u6b21\u56fa\u7136\u662f\u7279\u5730\u6765\u62dc\u8bbf\u4f60\u4eec\uff0c\u4f46\u4e5f\u662f\u987a\u4fbf \u8981\u5411\u4f60\u6253\u542c\u51e0\u4e2a\u4eba\u7684\u3002\u201d \u3000\u3000\u201c\u7239\u7239\u8d77\u521d\u83ab\u540d\u5176\u5999\uff0c\u8bf4\u9053\uff1a\u2018\u4f60\u77e5\u9053\u6211\u5728\u4e2d\u539f\u5e76\u65e0\u76f8\u8bc6\u7684\u670b\u53cb\uff0c\u5341\u5e74\u6765\u9690\u5c45\u6e14\u6751\uff0c\u66f4 \u662f\u5b64\u964b\u5be1\u95fb\uff0c\u4f60\u5374\u8981\u5411\u6211\u6253\u542c\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u201c\u5c09\u8fdf\u70af\u9053\uff1a\u2018\u8fd9\u51e0\u4e2a\u4eba\u4f60\u7eb5\u7136\u4e0d\u8ba4\u5f97\uff0c\u4e00\u5b9a\u4e5f\u4f1a\u77e5\u9053\u4ed6\u4eec\u7684\u6765\u5386\u7684\u3002\u6211\u5148\u544a\u8bc9\u4f60\u6211\u4eec \u4e24\u592b\u59bb\u7684\u4e00\u6869\u906d\u9047\u3002\u2019\u201d \u3000\u3000\u8ddf\u7740\u6797\u65e0\u53cc\u4e5f\u5c31\u628a\u5c09\u8fdf\u70af\u6240\u8bf4\u7684\u6545\u4e8b\u8f6c\u8ff0\u7ed9\u91d1\u9010\u6d41\u7b49\u4eba\u77e5\u9053\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u4e2a\u79cb\u9ad8\u6c14\u723d\u7684\u4f73\u65e5\uff0c\u5c09\u8fdf\u70af\u592b\u59bb\u5728\u5180\u5317\u9053\u4e0a\u5e76\u8f94\u5954\u9a70\uff0c\u4ed6\u4eec\u662f\u53d7\u4e86\u5c0f\u91d1\u5ddd\u4e49\u519b\u9996 \u9886\u8427\u5fd7\u8fdc\u7684\u59d4\u6258\uff0c\u5230\u4fdd\u5b9a\u53bb\u548c\u5929\u7406\u4f1a\u8054\u7edc\u7684\u3002 \u3000\u3000\u6b63\u884c\u8d70\u95f4\uff0c\u5ffd\u542c\u5f97\u8e44\u58f0\u5f97\u5f97\uff0c\u94c3\u58f0\u5f53\u5f53\uff0c\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5c18\u5934\u5927\u8d77\uff0c\u5374\u539f\u6765\u662f\u6765\u4e86\u4e00\u4e2a \u9a6c\u5e2e\u3002\u6709\u7537\u6709\u5973\uff0c\u6709\u8001\u6709\u5c11\uff0c\u603b\u5171\u4e03\u4eba\u4e4b\u591a\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u672c\u662f\u5173\u4e1c\u9a6c\u8d3c\u51fa\u8eab\uff0c\u5584\u4e8e\u76f8\u9a6c\uff0c\u4e00\u89c1\u9a6c\u5e2e\u7684\u6765\u52bf\uff0c\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e03 \u5339\u5750\u9a91\u90fd\u662f\u5343\u91d1\u96be\u4e70\u7684\u9a8f\u9a6c\uff0c\u8fd9\u4e9b\u4eba\u60f3\u5fc5\u4e5f\u4e00\u5b9a\u4e0d\u662f\u5bfb\u5e38\u7684\u9a6c\u5e2e\u4e86\u3002\u201d \u3000\u3000\u7948\u5723\u56e0\u5f00\u4ed6\u73a9\u7b11\u9053\uff1a\u201c\u8bf4\u4e0d\u5b9a\u662f\u4f60\u7684\u540c\u884c\u3002\u60f3\u6765\u5149\u987e\u6211\u4eec\u3002\u201d \u3000\u3000\u5c09\u8fdf\u70af\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u90a3\u6211\u5012\u662f\u6b22\u8fce\u4e4b\u81f3\uff0c\u6211\u8fd8\u672a\u66fe\u89c1\u8fc7\u8fd9\u6837\u9614\u6c14\u7684\u9a6c\u8d3c\u5462\u3002\u5018\u82e5\u4ed6\u4eec\u771f \u662f\u9a6c\u8d3c\uff0c\u6211\u5012\u60f3\u91cd\u65b0\u5165\u4f19\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u8fd9\u961f\u4eba\u9a6c\u548c\u4ed6\u4eec\u7684\u8ddd\u79bb\u5df2\u662f\u8d8a\u6765\u8d8a\u8fd1\uff0c\u5c09\u8fdf\u70af\u53ea\u9053\u4ed6\u4eec\u662f\u8981\u8d76\u8fc7\u524d\u5934\uff0c\u4e8e\u662f \u9042\u95ea\u8fc7\u4e00\u8fb9\uff0c\u7ed9\u4ed6\u4eec\u8ba9\u8def\u3002 \u3000\u3000\u4e0d\u6599\u90a3\u4e9b\u4eba\u5374\u4e0d\u7ea6\u800c\u540c\u7684\u653e\u6162\u4e86\u5750\u9a91\u4fdd\u6301\u5341\u6765\u4e08\u7684\u8ddd\u79bb\uff0c\u8ddf\u5728\u4ed6\u4eec\u592b\u59bb\u7684\u540e\u9762\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u4e0d\u89c9\u7591\u5fc3\u5927\u8d77\uff1a\u201c\u96be\u9053\u5f53\u771f\u662f\u51b2\u7740\u6211\u6765\u7684\uff1f\u201d\u548c\u59bb\u5b50\u6253\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u6545\u610f\u5ffd\u5feb\u5ffd \u6162\u7684\u884c\u8d70\uff0c\u90a3\u4e9b\u4eba\u679c\u7136\u4e5f\u662f\u4e0d\u75be\u4e0d\u5f90\u7684\u8ddf\u7740\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u5fc3\u91cc\u6697\u6697\u597d\u7b11\uff1a\u201c\u5982\u679c\u4ed6\u4eec\u771f\u662f\u9a6c\u8d3c\uff0c\u90a3\u5c31\u6d3b\u8be5\u662f\u4ed6\u4eec\u5012\u6963\u4e86\u3002\u6211\u6b63\u597d\u6362\u8fc7\u4e00\u5339 \u5750\u9a91\u3002\u201d \u3000\u3000\u5c09\u8fdf\u70af\u5df4\u4e0d\u5f97\u4ed6\u4eec\u52a8\u624b\uff0c\u5230\u4e86\u4e00\u5904\u50fb\u9759\u4e4b\u5904\uff0c\u4fbf\u4e0e\u59bb\u5b50\u7a81\u7136\u505c\u6b62\u4e0b\u6765\u3002\u770b\u4ed6\u4eec\u600e\u6837\u3002\u53ea \u89c1\u90a3\u4e9b\u4eba\u4e24\u5217\u6563\u5f00\uff0c\u5bf9\u4ed6\u4eec\u592b\u59bb\u91c7\u53d6\u4e86\u5305\u56f4\u7684\u6001\u52bf\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u7eb5\u58f0\u5927\u7b11\u9053\uff1a\u201c\u670b\u53cb\uff0c\u4f60\u4eec\u770b\u8d70\u4e86\u773c\u4e86\uff01\u201d\u610f\u601d\u662f\u5632\u7b11\u4ed6\u4eec\u773c\u529b\u4e0d\u591f\uff0c\u6765\u6253\u52ab\u4e00 \u4e2a\u4e0d\u503c\u4e00\u52ab\u7684\u4eba\uff1b\u4f46\u4e5f\u53ef\u4ee5\u89e3\u91ca\u4e3a\u4ed6\u4eec\u770b\u9519\u4e86\u4eba\uff0c\u5728\u592a\u5c81\u5934\u4e0a\u52a8\u571f\u3002 \u3000\u3000\u4e3a\u9996\u7684\u4e00\u4e2a\u957f\u987b\u6c49\u5b50\u6014\u4e86\u4e00\u6014\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5c09\u8fdf\u70af\u4e48\uff1f\u201d \u3000\u3000\u5c09\u8fdf\u70af\u5927\u5403\u4e00\u60ca\uff0c\u5b9a\u775b\u770b\u53bb\uff0c\u8fd9\u624d\u53d1\u73b0\u90a3\u4e03\u5339\u9a6c\u7684\u8eab\u4e0a\uff0c\u90fd\u6709\u4e00\u4e2a\u7279\u6b8a\u7684\u70d9\u5370\uff0c\u662f\u5927\u5185 \u9a6c\u7684\u70d9\u5370\uff0c\u5c09\u8fdf\u70af\u66fe\u52ab\u8fc7\u5929\u7262\uff0c\u95f9\u8fc7\u8428\u603b\u7763\u7684\u5bff\u5802\uff0c\u95ef\u8fc7\u5fa1\u6797\u519b\u7684\u519b\u8425\uff0c\u5e73\u751f\u4e0d\u77e5\u548c\u591a\u5c11\u5927 \u5185\u536b\u58eb\u53ca\u5fa1\u4f11\u519b\u7684\u519b\u5b98\u4ea4\u8fc7\u624b\uff0c\u662f\u4ee5\u8ba4\u5f97\u8fd9\u79cd\u5750\u9a91\u7684\u70d9\u5370\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u53c8\u518d\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u6211\u8d70\u4e86\u773c\u4e86\uff0c\u9519\u628a\u4f60\u4eec\u5f53\u4f5c\u4e86\u9ed1\u9053\u4e0a\u7684\u670b\u53cb\uff0c\u5374 \u539f\u6765\u4f60\u4eec\u662f\u9791\u5b50\u7684\u9e70\u722a\u5b59\uff01\u201d\u5c09\u8fdf\u70af\u7684\u5200\u4e0b\u6740\u8fc7\u65e0\u6570\u6e05\u5ef7\u9e70\u72ac\uff0c\u56e0\u6b64\u867d\u7136\u77e5\u9053\u4ed6\u4eec\u4e0d\u662f\u5bfb\u5e38 \u9a6c\u8d3c\uff0c\u5374\u4e5f\u8fd8\u672a\u66fe\u5c06\u8fd9\u4e03\u4e2a\u4eba\u653e\u5728\u773c\u5185\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4e2a\u5c11\u5987\u9053\uff1a\u201c\u536b\u54e5\uff0c\u4ed6\u8bf4\u4ec0\u4e48\u3002\u201d\u539f\u6765\u5979\u4e0d\u61c2\u5f97\u5c09\u8fdf\u70af\u8bf4\u7684\u201c\u9e70\u722a\u5b59\u201c\u662f\u4ec0\u4e48\u610f \u601d\u3002 \u3000\u3000\u90a3\u4e2a\u7834\u5979\u5524\u4f5c\u201c\u536b\u54e5\u201d\u7684\u7537\u5b50\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u76f8\u4fe1\u603b\u4e0d\u662f\u597d\u8bdd\u5427\uff01\u201d\u53e6 \u4e00\u4e2a\u7537\u7684\u7b54\u9053\uff1a\u201c\u54fc\uff0c\u4ed6\u8bf4\u6211\u4eec\u662f\u671d\u5ef7\u7684\u8d70\u72d7\u3002\u201d \u3000\u3000\u539f\u6765\u201c\u6276\u6851\u4e03\u5b50\u201d\u6765\u5230\u4e2d\u539f\uff0c\u548c\u4fa0\u4e49\u9053\u6ca1\u6709\u4ea4\u4e0a\uff0c\u5c31\u7ed9\u8428\u798f\u9f0e\u7684\u624b\u4e0b\u77e5\u9053\u3002\u4ed6\u4eec\u5c31\u8fd9\u4f6f \u7cca\u91cc\u7cca\u6d82\u7684\u53d7\u4e86\u8428\u798f\u9f0e\u7684\u7b3c\u7edc\u3002 \u3000\u3000\u5f53\u7136\u65e5\u5b50\u4e00\u4e45\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u4f1a\u5168\u65e0\u6240\u77e5\u7684\u3002\u4f46\u5176\u4e2d\u9886\u5934\u7684\u4e24\u4e2a\u4eba\u5229\u7984\u5fc3\u718f\uff0c\u6765\u5230\u4e2d\u539f\u4e5f\u60f3 \u6709\u4e2a\u597d\u7684\u9760\u5c71\uff0c\u4ee5\u5229\u4e8e\u5149\u5927\u95e8\u6237\uff0c\u56e0\u6b64\u5728\u77e5\u9053\u4e86\u8428\u798f\u9f0e\u8981\u5229\u7528\u4ed6\u4eec\u6765\u5bf9\u4ed8\u6297\u6e05\u7684\u4e49\u58eb\u4e4b\u540e\uff0c \u7adf\u4e5f\u7518\u5fc3\u53d7\u4ed6\u5229\u7528\u3002\u53e6\u5916\u51e0\u4e2a\u4eba\u5728\u6d77\u5916\u8fc7\u60ef\u4e86\u95f2\u4e91\u91ce\u9e64\u7684\u751f\u6daf\uff0c\u6765\u5230\u4e2d\u539f\uff0c\u4e5f\u5e76\u4e0d\u5173\u5fc3\u65f6\u5c40 \u3002\u9886\u5934\u7684\u5927\u54e5\u600e\u4e48\u8bf4\uff0c\u4ed6\u4eec\u5c31\u8ddf\u7740\u505a\u3002\u8fd9\u51e0\u4e2a\u4eba\u624d\u662f\u771f\u6b63\u7cca\u91cc\u7cca\u6d82\u7684\u53d7\u4e86\u8499\u9a97\u4e86\u3002 \u3000\u3000\u4e0d\u8fc7\u6709\u4e00\u6837\u5fc3\u601d\u5374\u662f\u4ed6\u4eec\u4e03\u4e2a\u4eba\u5171\u540c\u7684\uff0c\u4ed6\u4eec\u81ea\u4ee5\u4e3a\u662f\u4ee5\u201c\u9ad8\u4eba\u201d\u7684\u8eab\u4efd\u53d7\u8428\u798f\u9f0e\u7684 \u201c\u793c\u9047\u201d\uff0c\u5e76\u4e0d\u8ba4\u4e3a\u81ea\u5df1\u662f\u671d\u5ef7\u7684\u9e70\u72ac\u3002 \u3000\u3000\u90a3\u5c11\u5987\u5927\u6012\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff0c\u80e1\u8bf4\u516b\u9053\uff01\u5b97\u5e08\u53d4\uff0c\u6211\u4eec\u8fd8\u7b49\u4ec0\u4e48\uff0c\u6559\u8bad\u6559\u8bad\u4ed6\u5427\uff01\u201d \u3000\u3000\u5c09\u8fdf\u70af\u7eb5\u58f0\u5927\u7b11\uff1a\u201c\u4f60\u4eec\u4e0d\u662f\u8d70\u72d7\u662f\u4ec0\u4e48\uff01\u563f\uff0c\u563f\uff0c\u6211\u5c09\u8fdf\u70af\u8fd9\u4e00\u628a\u5200\u4e13\u6740\u8d70\u72d7\uff0c\u4e09\u4e94 \u4e03\u6761\uff0c\u5341\u6761\u516b\u6761\uff0c\u6765\u5f97\u8d8a\u591a\u8d8a\u597d\uff0c\u7701\u5f97\u6211\u591a\u8d39\u7cbe\u795e\uff01\u6765\u5440\uff0c\u6765\u5440\uff0c\u5feb\u6765\u5440\uff01\u201d \u3000\u3000\u5530\u5530\u8fde\u58f0\uff0c\u767d\u5203\u8000\u773c\uff0c\u6276\u6851\u4e03\u5b50\u4e4b\u4e2d\u5df2\u6709\u597d\u51e0\u4e2a\u4eba\u62d4\u51fa\u5251\u6765\uff0c\u90a3\u957f\u987b\u6c49\u5b50\u559d\u9053\uff1a\u201c\u6211\u4eec \u81ea\u6709\u6211\u4eec\u7684\u8eab\u4efd\uff0c\u7ba1\u4ed6\u80e1\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u627e\u4eec\u4e5f\u5f97\u6c89\u4f4f\u4e86\u6c14\uff0c\u4e0d\u53ef\u80e1\u6765\uff01\u201d \u3000\u3000\u65b9\u536b\u8bf4\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u533a\u533a\u4e00\u4e2a\u5c09\u8fdf\u70af\u4e5f\u503c\u5f97\u5927\u5bb6\u62a2\u7740\u548c\u4ed6\u52a8\u624b\u5417\uff1f\u5b97\u5e08\u53d4\u7f1a\u8d77\u4e00\u6761\u624b\u81c2 \u4e5f\u8db3\u591f\u5bf9\u4ed8\u4ed6\u4e86\u3002\u201d\u62a2\u7740\u4e0a\u53bb\u7684\u90a3\u51e0\u4e2a\u4eba\u4e0d\u7981\u90fd\u662f\u9762\u4e0a\u4e00\u7ea2\uff0c\u540c\u65f6\u9000\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u5012\u662f\u9887\u51fa\u5c09\u8fdf\u70af\u610f\u6599\u4e4b\u5916\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u73ed\u9e70\u722a\u5b59\u548c\u6211\u66fe\u7ecf\u78b0\u89c1\u8fc7\u7684\u5374\u662f\u6709\u70b9 \u4e0d\u540c\uff01\u201d \u3000\u3000\u90a3\u957f\u987b\u6c49\u5b50\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u5c09\u8fdf\u70af\uff0c\u4f60\u4e5f\u5fd2\u5c0f\u770b\u4eba\u4e86\u3002\u4f60\u6562\u548c\u6211\u6253\u8d4c\u4e48\u3002\u201d \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u6253\u4ec0\u4e48\u8d4c\uff1f\u201d \u3000\u3000\u957f\u987b\u6c49\u5b50\u9053\uff1a\u201c\u6211\u542c\u8bf4\u4f60\u81ea\u6043\u6b66\u827a\u9ad8\u5f3a\uff0c\u5230\u5904\u6a2a\u884c\u9738\u9053\uff0c\u4eca\u65e5\u7279\u5730\u6765\u4f1a\u4e00\u4f1a\u4f60\u3002\u54b1\u4eec\u5c31 \u7528\u5200\u5251\u6765\u4f5c\u8d4c\u5177\uff0c\u8d4c\u4e2a\u8f93\u8d62\u3002\u201d \u3000\u3000\u5c09\u8fdf\u70af\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u5f88\u597d\uff01\u4e0d\u8bba\u4f60\u4eec\u7fa4\u6bb4\u4e5f\u597d\uff0c\u5355\u6253\u72ec\u6597\u4e5f\u597d\uff0c\u6211\u5c09\u8fdf\u70af\u4e00\u51c6\u5949\u966a\uff01\u201d \u3000\u3000\u957f\u987b\u6c49\u5b50\u9053\uff1a\u201c\u6211\u8ba9\u4f60\u4eec\u592b\u59bb\u9f50\u4e0a\uff0c\u53ea\u8981\u4f60\u4eec\u8d62\u5f97\u4e86\u6211\u8fd9\u67c4\u624b\u4e2d\u957f\u5251\u2026\u2026\u201d \u3000\u3000\u5c09\u8fdf\u70af\u5927\u6012\u9053\uff1a\u201c\u653e\u5c41\uff0c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u503c\u5f97\u6211\u4eec\u592b\u59bb\u8054\u624b\u5bf9\u4ed8\uff1f\u201d \u3000\u3000\u957f\u987b\u6c49\u5012\u4e0d\u52a8\u6c14\uff0c\u53cd\u800c\u7b11\u9053\uff1a\u201c\u5982\u4f55\uff0c\u4f60\u4e5f\u5c1d\u5230\u7ed9\u4eba\u770b\u8f7b\u7684\u6ecb\u5473\u4e86\u5427\uff1f\u90a3\u4f60\u4e3a\u4ec0\u4e48\u521a\u624d \u8981\u8bf4\u6597\u6211\u4eec\u4e03\u4e2a\uff1f\u201d \u3000\u3000\u5c09\u8fdf\u70af\u5e73\u751f\u8d70\u5357\u95ef\u5317\uff0c\u51e0\u66fe\u53d7\u8fc7\u4eba\u5982\u6b64\u9876\u649e\uff1f\u4f46\u56e0\u4ed6\u8c6a\u6c14\u5343\u4e91\uff0c\u5018\u82e5\u78b0\u5230\u6c14\u8d28\u4e0e\u4ed6\u6709\u70b9 \u76f8\u4f3c\u7684\u786c\u6c49\uff0c\u4ed6\u4e5f\u662f\u4f1a\u53e6\u773c\u76f8\u770b\u7684\u3002\u662f\u4ee5\u4ed6\u53d7\u4e86\u8fd9\u957f\u987b\u6c49\u5b50\u7684\u595a\u843d\uff0c\u5012\u4e5f\u5e76\u4e0d\u53d1\u6012\uff0c\u53cd\u800c\u54c8 \u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u4f60\u6240\u8a00\uff0c\u4f60\u5012\u662f\u81ea\u8d1f\u5f97\u5f88\uff01\u597d\uff0c\u4f60\u81ea\u4fe1\u8d62\u5f97\u4e86\u6211\uff0c\u4f60\u5c31\u5212\u51fa\u9053\u513f\u6765\u5427\uff01 \u201d \u3000\u3000\u957f\u987b\u6c49\u5b50\u9053\uff1a\u201c\u4f60\u4eec\u592b\u59bb\u4e24\u4e2a\uff0c\u6211\u4eec\u8fd9\u8fb9\u4e03\u4e2a\u4eba\uff0c\u4e5f\u662f\u6709\u7537\u6709\u5973\u3002\u6211\u4eec\u7edd\u4e0d\u501a\u591a\u4e3a\u80dc\uff0c \u7537\u5bf9\u7537\u5973\u5bf9\u5973\uff0c\u6765\u4e2a\u5355\u6253\u72ec\u6597\uff0c\u770b\u770b\u662f\u8c01\u8f93\u8c01\u8d62\uff1f\u201d \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u597d\uff0c\u597d\u5f97\u5f88\uff01\u6211\u9886\u6559\u9601\u4e0b\u7684\u9ad8\u62db\u3002\u201d\u7948\u5723\u56e0\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u54ea\u4f4d\u51fa\u6765\u6307\u6559\u5c0f \u59b9\u4e86\u3002\u201d\u7948\u5723\u56e0\u5728\u6c5f\u6e56\u4e0a\u4ee5\u51fa\u624b\u72e0\u8fa3\u8457\u540d\uff0c\u4f46\u8bf4\u8bdd\u5374\u662f\u9634\u58f0\u7ec6\u6c14\uff0c\u57fa\u662f\u6e29\u67d4\u3002 \u3000\u3000\u4e24\u4e2a\u5973\u7684\u540c\u65f6\u8de8\u4e0a\u4e00\u6b65\uff0c\u90a3\u59d3\u5b97\u7684\u957f\u987b\u6c49\u5b50\u9053\uff1a\u201c\u971e\u513f\uff0c\u8ba9\u7ed9\u4f60\u7684\u6851\u5e08\u59d0\u4e0a\u3002\u201d\u5e74\u7eaa\u8f7b \u7684\u90a3\u4e2a\u5973\u5b50\u9053\uff1a\u201c\u662f\u3002\u201d\u8fd8\u5251\u5165\u9798\uff0c\u9000\u4e86\u4e0b\u6765\u3002\u5979\u7684\u5e08\u59d0\u5c31\u662f\u521a\u624d\u548c\u5c09\u8fdf\u70af\u5435\u5634\u7684\u90a3\u4e2a\u5c11\u5987 \uff0c\u540d\u5524\u6851\u9752\u3002 \u3000\u3000\u957f\u987b\u6c49\u5b50\u9053\uff1a\u201c\u597d\uff0c\u73b0\u5728\u6211\u4eec\u53ef\u4ee5\u4e00\u5bf9\u4e00\u6765\u4e2a\u8d4c\u8d5b\u4e86\uff0c\u4f60\u82e5\u8f93\u4e86\u7ed9\u6211\u2026\u2026\u201d\u5c09\u8fdf\u70af\u51b7\u54ed \u9053\uff1a\u201c\u6211\u7acb\u5373\u6a2a\u5200\u81ea\u520e\uff01\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5f53\u7136\u662f\u7edd\u4e0d\u4f1a\u8f93\u3002 \u3000\u3000\u957f\u987b\u6c49\u5b50\u7b11\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u5fc5\u3002\u4f60\u82e5\u8f93\u4e86\uff0c\u8ddf\u6211\u4e0a\u4eac\u9500\u6848\u4fbf\u884c\u3002\u6211\u53ef\u4ee5\u62c5\u4fdd\u4e0d\u8981\u4f60\u7684\u6027\u547d \u3002\u201d \u3000\u3000\u539f\u6765\u5c09\u8fdf\u70af\u66fe\u5728\u4eac\u57ce\u5e72\u8fc7\u51e0\u5b97\u5927\u52ab\u6848\uff0c\u53d7\u4ed6\u201c\u5149\u987e\u201d\u7684\u6709\u738b\u516c\u5927\u81e3\uff0c\u8c6a\u95e8\u5de8\u5ba4\u3002\u751a\u81f3\u5927 \u5185\u5b9d\u5e93\uff0c\u4e5f\u66fe\u7ed9\u4ed6\u6f5c\u5165\uff0c\u5077\u4e86\u51e0\u4ef6\u4ef7\u503c\u8fde\u57ce\u7684\u5b9d\u7269\u3002\u8428\u798f\u9f0e\u4e4b\u6240\u4ee5\u8981\u6349\u62ff\u4ed6\uff0c\u4e3b\u8981\u7684\u539f\u56e0\u8fd8 \u662f\u5728\u4e8e\u7834\u6848\uff0c\u201c\u8ffd\u8d43\u201d\uff0c\u5e76\u975e\u56e0\u4e3a\u4ed6\u662f\u4e49\u519b\u7684\u9886\u8896\u3002\u56e0\u4e3a\u8428\u798f\u9f0e\u53ea\u77e5\u9053\u4ed6\u548c\u4e49\u519b\u6709\u6765\u5f80\uff0c\u5374 \u5c1a\u672a\u77e5\u9053\u4ed6\u662f\u65e9\u5df2\u53c2\u52a0\u4e86\u4e49\u519b\u7684\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u5927\u7b11\u9053\uff1a\u201c\u9e70\u722a\u5b59\u6bd5\u7adf\u662f\u9e70\u722a\u5b59\uff0c\u5c3e\u5df4\u9732\u51fa\u6765\u624d\u597d\uff0c\u4f60\u6709\u672c\u9886\u8d62\u5f97\u4e86\u627e\uff0c\u6211\u8ddf\u4f60 \u6295\u6848\u53c8\u6709\u4f55\u96be\uff01\u4f60\u82e5\u8f93\u4e86\u5462\uff1f\u201d \u3000\u3000\u957f\u987b\u6c49\u5b50\u9053\uff1a\u201c\u6211\u4e5f\u4efb\u51ed\u4f60\u7684\u5904\u7f6e\uff01\u201d \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u8981\u4f60\u7684\u6027\u547d\uff0c\u53ea\u8981\u4f60\u8fd9\u5339\u5750\u9a91\uff01\u201d \u3000\u3000\u957f\u987b\u6c49\u5b50\u9053\uff1a\u201c\u597d\uff0c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\uff0c\u6211\u4eec\u8fd9\u4e2a\u8d4c\u8d5b\u5c31\u8fd9\u6837\u8bf4\u5b9a\u4e86\u3002\u8c01\u4e5f\u4e0d\u8bb8\u53cd\u6094 \uff01\u201d \u3000\u3000\u7948\u5723\u56e0\u4ee5\u5546\u91cf\u7684\u53e3\u543b\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4fd7\u8bdd\u8bf4\u592b\u5531\u5987\u968f\uff0c\u6211\u548c\u4f60\u7684\u8d4c\u8d5b\u5c31\u4f9d\u6837\u8bdd\u846b\u82a6\u5427\uff0c\u4f60 \u820d\u4e0d\u820d\u5f97\u4f60\u7684\u5750\u9a91\uff1f\u201d \u3000\u3000\u6851\u9752\u5927\u6012\u9053\uff1a\u201c\u968f\u4f60\u7684\u4fbf\uff0c\u53cd\u6b63\u6211\u4e0d\u4f1a\u8f93\u7ed9\u4f60\uff01\u6765\u5427\uff01\u201d \u3000\u3000\u7948\u5723\u56e0\u9053\uff1a\u201c\u90a3\u4e5f\u4e0d\u89c1\u5f97\uff01\u201d\u5530\u7684\u4e00\u97ad\u4fbf\u6253\u8fc7\u53bb\u3002\u6851\u9752\u75be\u524a\u5251\uff0c\u5251\u52bf\u5982\u8679\u3002\u4e00\u97ad\u6362\u4e09\u5251 \uff0c\u53cc\u65b9\u90fd\u6ca1\u6709\u5360\u5230\u4fbf\u5b9c\u3002\u4f46\u4ee5\u51fa\u624b\u7684\u8fc5\u6377\u800c\u8a00\uff0c\u5374\u662f\u7948\u5723\u56e0\u7a0d\u80dc\u4e00\u7b79\u4e86\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u5979\u4eec\u7684\u90a3\u8fb9\u662f\u6211\u7684\u6d51\u5bb6\u5148\u884c\u51fa\u624b\uff0c\u6211\u4eec\u8fd9\u8fb9\uff0c\u6211\u53ef\u8be5\u8ba9\u4f60\u5148\u4e0a\u3002\u201d\u957f\u987b\u6c49 \u5b50\u9053\uff1a\u201c\u597d\uff0c\u627f\u8ba9\u4e86\uff01\u201d\u4f46\u5374\u5e76\u4e0d\u62d4\u5251\u51fa\u9798\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u559d\u9053\uff1a\u201c\u8fd8\u4e0d\u4eae\u5251\uff0c\u66f4\u5f85\u4f55\u65f6\uff1f\u201d\u957f\u987b\u6c49\u5b50\u7b11\u9053\uff1a\u201c\u7740\u6025\u4ec0\u4e48\uff1f\u201d\u9661\u5730\u4e00\u634f\u5251\u9798 \uff0c\u8f7b\u8f7b\u4e00\u6296\uff0c\u8def\u4e2d\u7684\u957f\u5251\u4fbf\u7a81\u7136\u98de\u4e86\u51fa\u6765\uff01\u8fd9\u662f\u7eaf\u51ed\u5185\u529b\u7684\u51b2\u529b\uff0c\u5c06\u5251\u201c\u5c04\u201d\u51fa\u6765\u7684\uff0c\u548c\u4e00 \u822c\u7684\u201c\u62d4\u5251\u201d\u8fe5\u7136\u4e0d\u540c\uff01 \u3000\u3000\u8fd9\u4e00\u4e0b\u9887\u51fa\u5c09\u8fdf\u70af\u610f\u6599\u4e4b\u5916\uff0c\u9661\u7136\u95f4\u53ea\u89c1\u767d\u5203\u8000\u773c\uff0c\u5012\u4e5f\u4e0d\u89c9\u5927\u5403\u4e00\u60ca\u3002\u4f46\u4ed6\u60ef\u7ecf\u5927\u654c \uff0c\u867d\u60ca\u4e0d\u4e71\uff0c\u5f53\u4e0b\u6a2a\u5200\u4e00\u78d5\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u5012\u662f\u6709\u70b9\u9b3c\u95e8\u5934\uff0c\u4f46\u8fd9\u7b49\u82b1\u62db\uff0c\u53c8\u6709\u4f55\u7528 \uff1f\u201d \u3000\u3000\u957f\u987b\u6c49\u5b50\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u662f\u4e48\u3002\u201d\u90a3\u70b3\u5b9d\u5251\u7ed9\u5c09\u8fdf\u70af\u78d5\u5f97\u53ca\u65f6\u56de\u6765\uff0c\u957f\u987b\u6c49\u5b50\u4e00\u6293\u6293\u5230\u624b \u4e2d\uff0c\u8fc5\u5373\u4fbf\u662f\u4e00\u5251\u523a\u53bb\uff0c\u559d\u9053\uff1a\u201c\u5c31\u8ba9\u4f60\u89c1\u8bc6\u89c1\u8bc6\u6211\u7684\u82b1\u62db\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u957f\u987b\u6c49\u5b50\u4e5f\u77e5\u5c09\u8fdf\u70af\u5e76\u975e\u6613\u4e0e\u4e4b\u8f88\uff0c\u4e0d\u662f\u51fa\u5947\uff0c\u7edd\u96be\u5236\u80dc\uff0c\u6545\u6b64\u4e00\u51fa\u624b\u4fbf\u662f\u6572\u5c71 \u9707\u864e\u7684\u624b\u6cd5\uff0c\u6270\u4e71\u4ed6\u7684\u5fc3\u795e\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u60f3\u4e0d\u5230\u8fd9\u957f\u987b\u6c49\u5b50\u6765\u5f97\u5982\u6b64\u8fc5\u6377\uff0c\u679c\u7136\u56e0\u6b64\u5fc3\u795e\u5fae\u5206\uff0c\u7ed9\u4ed6\u653b\u4e86\u4e2a\u63aa\u624b\u4e0d\u53ca\u3002\u8fd9 \u957f\u987b\u6c49\u5b50\u662f\u5176\u4f59\u516d\u4eba\u7684\u5e08\u53d4\uff0c\u201c\u6276\u6851\u4e03\u5b50\u201d\u4e4b\u4e2d\u4ea6\u662f\u4ee5\u4ed6\u7684\u672c\u9886\u6700\u597d\u3002\u4e00\u62a2\u5230\u4e86\u5148\u624b\uff0c\u5251\u82e6 \u6e38\u9f99\uff0c\u5251\u8292\u6307\u5411\u5c09\u8fdf\u70af\u7684\u8981\u5bb3\uff01 \u3000\u3000\u5c09\u8fdf\u70af\u5140\u7acb\u5982\u5c71\uff0c\u5bf8\u6b65\u4e0d\u8ba9\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u957f\u987b\u6c49\u5b50\u5df2\u662f \u653b\u4e86\u4e09\u4e94\u5341\u62db\uff0c\u5140\u662f\u627e\u4e0d\u5230\u5c09\u8fdf\u70af\u534a\u70b9\u7834\u7efd\u3002\u5251\u52bf\u5982\u8679\uff0c\u5200\u5149\u80dc\u96ea\uff0c\u771f\u662f\u9488\u950b\u76f8\u5bf9\uff0c\u65d7\u9f13\u76f8 \u5f53\uff01\u957f\u987b\u6c49\u5b50\u7684\u5251\u950b\u6240\u53ca\u4e4b\u5904\uff0c\u90fd\u597d\u50cf\u6709\u4e00\u5835\u5200\u5899\u6321\u4f4f\uff0c\u4ed6\u7d20\u6765\u81ea\u8d1f\u5185\u529b\u6df1\u539a\uff0c\u81f3\u6b64\u4e5f\u4e0d\u7981 \u6697\u6697\u4f69\u670d\u5bf9\u65b9\uff01 \u3000\u3000\u6b8a\u4e0d\u77e5\u8fd9\u957f\u987b\u6c49\u5b50\u56fa\u7136\u662f\u6697\u6697\u5403\u60ca\uff0c\u5c09\u8fdf\u70af\u4ea6\u662f\u8be7\u5f02\u4e0d\u5df2\u3002\u4ed6\u5e73\u751f\u78b0\u8fc7\u7684\u52b2\u654c\u4e0d\u77e5\u591a\u5c11 \uff0c\u4ece\u6765\u6ca1\u6709\u4e00\u4e2a\u597d\u50cf\u957f\u987b\u6c49\u5b50\u8fd9\u6837\u7684\u68d8\u624b\u7684\uff0c\u9976\u662f\u4ed6\u5df2\u7ecf\u4f7f\u51fa\u4e86\u5168\u526f\u672c\u9886\uff0c\u5200\u6cd5\u4e25\u5bc6\u5f97\u6cfc\u6c34 \u4e0d\u5165\uff0c\u5140\u662f\u611f\u5230\u5251\u8292\u523a\u773c\uff0c\u51b7\u6c14\u4fb5\u808c\uff0c\u4f3c\u4e4e\u6709\u51e0\u5341\u53e3\u957f\u5251\uff0c\u56db\u9762\u516b\u65b9\u5411\u4ed6\u653b\u6765\u4e00\u6837\u3002\u5c09\u8fdf\u70af \u5012\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u7684\u5251\u6cd5\u4e0e\u4e2d\u539f\u5404\u5927\u95e8\u6d3e\u90fd\u4e0d\u76f8\u540c\uff0c\u4e5f\u4e0d\u77e5\u662f\u4ece\u54ea\u91cc\u94bb\u51fa\u6765 \u7684\u3002\u4ed6\u7684\u5251\u6cd5\u7cbe\u5999\u5982\u65af\uff0c\u6211\u82e5\u7136\u53ea\u987e\u9632\u5b88\uff0c\u53ea\u6015\u7ec8\u987b\u8d25\u5728\u4ed6\u7684\u5251\u4e0b\u3002\u201d \u3000\u3000\u4e94\u5341\u62db\u8fc7\u540e\uff0c\u5c09\u8fdf\u70af\u5df2\u628a\u5bf9\u65b9\u7684\u653b\u52bf\u9010\u6e10\u5316\u89e3\uff0c\u731b\u5730\u4e00\u58f0\u559d\u9053\uff1a\u201c\u6559\u4f60\u4e5f\u89c1\u8bc6\u89c1\u8bc6\u6211\u7684 \u5feb\u5200\uff01\u201d\u4e00\u5200\u5288\u51fa\uff0c\u5200\u950b\u8f6c\u4e86\u4e00\u4e2a\u5708\uff0c\u65c1\u8fb9\u7684\u4eba\u770b\u6765\uff0c\u4ed6\u53ea\u662f\u4f7f\u4e86\u4e00\u62db\uff0c\u5176\u5b9e\u8fd9\u4e00\u62db\u4e4b\u4e2d\uff0c \u5df2\u662f\u5305\u542b\u4e86\u5341\u516b\u4e2a\u590d\u6742\u7684\u62db\u5f0f\uff0c\u53ea\u56e0\u4ed6\u7684\u5200\u6cd5\u4f7f\u5f97\u592a\u5feb\uff0c\u65c1\u8fb9\u7684\u4eba\u5c31\u53ea\u770b\u89c1\u5200\u5149\u4e86\u3002 \u3000\u3000\u4e00\u7247\u65ad\u91d1\u621b\u7389\u4e4b\u58f0\uff0c\u9707\u5f97\u4f17\u4eba\u8033\u9f13\u55e1\u55e1\u4f5c\u54cd\uff0c\u957f\u987b\u6c49\u5b50\u5728\u4ed6\u731b\u653b\u4e4b\u4e0b\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u9000\u4e86 \u51e0\u6b65\u3002\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u6697\u81ea\u601d\u91cf\uff1a\u201c\u8fd9\u53ae\u7684\u5feb\u5200\u4e5f\u8fd8\u7f62\u4e86\uff0c\u4ed6\u7684\u5185\u529f\u7adf\u4f3c\u6e90\u6e90\u4e0d\u7aed\uff0c\u65e0\u7a77 \u65e0\u5c3d\uff0c\u4e45\u6218\u4e0b\u53bb\uff0c\u53ea\u6015\u6211\u662f\u5b9a\u8981\u5403\u4e8f\u7684\u4e86\u3002\u201d \u3000\u3000\u53e6\u4e00\u8fb9\uff0c\u7948\u5723\u56e0\u548c\u90a3\u5c11\u5987\u4e5f\u662f\u6597\u5f97\u96be\u89e3\u96be\u5206\u3002\u7948\u5723\u56e0\u53f7\u79f0\u201c\u5343\u624b\u89c2\u97f3\u201d\uff0c\u4e0d\u4f46\u6697\u5668\u529f\u592b \u4e43\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u97ad\u6cd5\u4e5f\u662f\u6781\u4e3a\u4e86\u5f97\u3002\u5979\u4e00\u4e0a\u6765\u4fbf\u91c7\u653b\u52bf\uff0c\u6025\u4e09\u97ad\u56de\u98ce\u626b\u67f3\uff0c\u8fde\u73af\u5251\u4e94\u5973\u6295\u68ad \uff0c\u957f\u97ad\u6325\u821e\uff0c\u5229\u5251\u7ffb\u98de\uff0c\u4e00\u53e3\u6c14\u653b\u4e86\u5341\u516d\u516b\u62db\uff0c\u653b\u5f97\u90a3\u5c11\u5987\u8fde\u8fde\u540e\u9000\u3002 \u3000\u3000\u90a3\u5c11\u5987\u5fc3\u9ad8\u6c14\u50b2\uff0c\u53ea\u9053\u4ee5\u81ea\u5df1\u7684\u672c\u9886\uff0c\u6765\u5230\u4e2d\u539f\uff0c\u7eb5\u7136\u4e0d\u80fd\u7eb5\u6a2a\u5929\u4e0b\uff0c\u6298\u670d\u987b\u7709\uff0c\u81f3\u5c11 \u5728\u4e2d\u539f\u5973\u5b50\u4e4b\u4e2d\uff0c\u662f\u65e0\u4eba\u53ef\u4ee5\u4e0e\u4ed6\u5339\u654c\u7684\u4e86\uff0c\u6599\u5979\u78b0\u4e0a\u4e86\u201c\u5343\u624b\u89c2\u97f3\u201d\u7948\u5723\u56e0\uff0c\u81ea\u5df1\u7adf\u7136\u8ba8 \u4e0d\u534a\u70b9\u4fbf\u5b9c\uff0c\u8fd8\u7ed9\u5979\u6740\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u4e0d\u7531\u5f97\u5012\u62bd\u4e00\u53e3\u51c9\u6c14\uff0c\u59cb\u77e5\u4ece\u524d\u4e43\u662f\u5750\u4e95\u89c2\u5929\uff0c\u4e0d\u77e5\u5929 \u5730\u4e4b\u5927\u3002 \u3000\u3000\u7948\u5723\u56e0\u5c61\u653b\u4e0d\u4e0b\uff0c\u4e5f\u662f\u6697\u6697\u5403\u60ca\uff0c\u5267\u6218\u4e2d\u90a3\u5c11\u5987\u84e6\u5730\u4e00\u58f0\u957f\u5578\uff0c\u9752\u94a2\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u4e00\u62db \u201c\u6d41\u661f\u8ffd\u6708\u201d\uff0c\u6296\u51fa\u4e86\u4e09\u6735\u5251\u82b1\uff0c\u5de6\u523a\u201c\u767d\u6d77\u7a74\u201d\u77f3\u523a\u201c\u4e73\u7a81\u7a74\u201d\uff0c\u4e2d\u523a\u201c\u7487\u7391\u7a74\u201d\uff0c\u8fd9\u4e00 \u62db\u6b63\u662f\u6276\u6851\u6d3e\u5251\u6cd5\u4e2d\u4e00\u62db\u6781\u5176\u7cbe\u5999\u7684\u6740\u624b\uff0c\u8bba\u5251\u6cd5\u8fd8\u4e0d\u53ca\u7948\u5723\u56e0\u7684\u5feb\u6377\uff0c\u4f46\u4e00\u5251\u523a\u51fa\uff0c\u98d8\u5ffd \u83ab\u6d4b\uff0c\u4f3c\u524d\u4f3c\u53f3\u4f3c\u4e2d\uff0c\u5982\u662f\u53eb\u4eba\u96be\u4ee5\u6349\u6478\u3002\u7948\u5723\u56e0\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u8fd9\u8def\u5251\u6cd5\uff0c\u4ed3\u4fc3\u95f4\u65e0\u6687\u7ec6\u601d \uff0c\u5012\u662f\u4e0d\u6562\u5192\u9669\u8d2a\u653b\uff0c\u4e0d\u6c42\u80dc\uff0c\u5148\u9632\u8d25\uff0c\u4fa7\u8eab\u4e00\u95ea\uff0c\u6539\u91c7\u5b88\u52bf\u3002 \u3000\u3000\u90a3\u5c11\u5987\u62a2\u5f97\u4e86\u5148\u624b\uff0c\u597d\u80dc\u4e4b\u5fc3\u53c8\u8d77\uff0c\u6697\u81ea\u601d\u91cf\uff1a\u201c\u8fd9\u5a46\u5a18\u672c\u9886\u9ad8\u5f3a\uff0c\u8981\u6253\u8d25\u5979\u6050\u6015\u662f\u5f88 \u96be\u7684\u4e86\uff0c\u4f46\u53ea\u8981\u5728\u5251\u6cd5\u4e0a\u80dc\u5f97\u51e0\u62db\uff0c\u903c\u5f97\u5979\u53ea\u6709\u62db\u67b6\u529f\uff0c\u6211\u4e5f\u53ef\u4ee5\u7b97\u662f\u80dc\u4e86\u3002\u201d\u5f53\u4e0b\u50ac\u7d27\u5251 \u62db\uff0c\u8fde\u8fde\u62a2\u653b\u3002 \u3000\u3000\u6276\u6851\u5251\u6cd5\u548c\u4e2d\u539f\u5404\u5927\u95e8\u6d3e\u90fd\u4e0d\u76f8\u540c\uff0c\u4f46\u56e0\u4e0a\u4e58\u6b66\u5b66\u5230\u5e95\u8fd8\u662f\u76f8\u901a\u7684\uff0c\u6545\u6b64\u4e5f\u6709\u8bb8\u591a\u62db\u6570 \uff0c\u548c\u4e2d\u539f\u7684\u5251\u6cd5\u76f8\u4f3c\uff0c\u4e0d\u8fc7\u76f8\u4f3c\u4e4b\u4e2d\uff0c\u4e5f\u6709\u5dee\u5f02\u3002\u7948\u5723\u56e0\u6478\u4e0d\u7740\u5e95\u7ec6\uff0c\u6709\u51e0\u62db\u5979\u4ee5\u4e3a\u662f\u81ea\u5df1 \u719f\u6089\u7684\u5251\u6cd5\u3002\u6309\u7167\u81ea\u5df1\u6240\u522b\u7684\u7834\u89e3\u3002\u4e0d\u6599\u4f3c\u662f\u800c\u975e\uff0c\u53c8\u7740\u4e86\u90a3\u5c11\u5987\u7684\u9053\u513f\u3002\u9519\u4e86\u51e0\u62db\u4e4b\u540e\uff0c \u679c\u7136\u4fbf\u7ed9\u90a3\u5c11\u5987\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u6740\u5f97\u5979\u53ea\u80fd\u62db\u67b6\u4e86\u3002 \u3000\u3000\u7948\u5723\u56e0\u62ac\u773c\u4e00\u671b\uff0c\u89c1\u4e08\u592b\u548c\u90a3\u957f\u987b\u6c49\u5b50\u6076\u6597\uff0c\u867d\u7136\u662f\u5360\u5f97\u4e0a\u6210\u653b\u52bf\uff0c\u4f46\u4e5f\u4f3c\u4e4e\u8ba8\u4e0d\u4e86\u4fbf \u5b9c\u3002\u7948\u5723\u56e0\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u5927\u54e5\u662f\u4e0d\u4f1a\u8f93\u7684\uff0c\u4f46\u6211\u82e5\u8f93\u4e86\uff0c\u8fd9\u4e2a\u8d4c\u8d5b\u4ed6\u4e5f\u5c31\u4e0d\u80fd\u8d62\u4e86\u3002\u81f3\u591a\u662f \u6273\u6210\u5e73\u5c40\uff0c\u4f55\u51b5\u4ed6\u4e5f\u672a\u5fc5\u5c31\u80fd\u8d62\u5462\u3002\u6211\u51b3\u4e0d\u80fd\u7d2f\u4ed6\u635f\u4e86\u4e00\u4e16\u82f1\u540d\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u4e0d\u80fd\u8f93\u7ed9\u8fd9 \u81ed\u5a46\u5a18\uff01\u201d \u3000\u3000\u90a3\u5c11\u5987\u5df2\u611f\u5230\u6709\u70b9\u6c14\u529b\u4e0d\u52a0\uff0c\u53eb\u9053\uff1a\u201c\u6211\u4eec\u53c8\u4e0d\u662f\u5e02\u4e95\u4e4b\u5f92\u6253\u67b6\uff0c\u4f60\u660e\u660e\u4e0d\u662f\u6211\u7684\u5bf9\u624b \uff0c\u8fd8\u4e0d\u670d\u8f93\u5417\uff1f\u201d \u3000\u3000\u7948\u5723\u56e0\u51b7\u7b11\u9053\uff1a\u201c\u600e\u89c1\u5f97\u6211\u4e0d\u662f\u4f60\u5bf9\u624b\uff0c\u4f60\u4f11\u5938\u53e3\uff0c\u6211\u53eb\u4f60\u4e09\u62db\u4e4b\u5185\uff0c\u4fbf\u8981\u64a4\u5251\uff01\u201d\u90a3 \u5c11\u5987\u6012\u9053\uff1a\u201c\u597d\uff0c\u4e14\u770b\u662f\u8c01\u64a4\u5251\uff1f\u201d\u5530\u7684\u4e00\u5251\u523a\u53bb\uff0c\u7948\u5723\u56e0\u7a81\u7136\u628a\u624b\u4e00\u626c\uff0c\u5c06\u5de6\u624b\u7684\u77ed\u5251\uff0c \u5411\u90a3\u5c11\u5987\u63b7\u53bb\uff01 \u3000\u3000\u8fd9\u5c11\u5987\u53ea\u9053\u7948\u5723\u56e0\u662f\u7ed9\u5979\u7684\u51cc\u5389\u5251\u6cd5\u903c\u5f97\u6254\u5251\u7684\uff0c\u5927\u559c\u53eb\u9053\uff1a\u201c\u6211\u53ea\u662f\u4e00\u62db\u5c31\u8981\u4f60\u64a4\u5251 \uff0c\u4f60\u8fd8\u4e0d\u8ba4\u8f93\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u89c1\u90a3\u67c4\u77ed\u5251\u5df2\u662f\u7b14\u76f4\u7684\u5411\u5979\u98de\u6765\uff01 \u3000\u3000\u8fd9\u5c11\u5987\u4e5f\u662f\u4e2a\u8bc6\u8d27\u7684\u884c\u5bb6\uff0c\u4e00\u89c1\u77ed\u5251\u7684\u6765\u52bf\uff0c\u5c31\u77e5\u662f\u6781\u5389\u5bb3\u7684\u6697\u5668\u624b\u6cd5\uff0c\u5bf9\u65b9\u662f\u7528\u77ed\u5251 \u5f53\u4f5c\u6697\u5668\u6765\u4f7f\u7528\u7684\uff0c\u867d\u7136\u662f\u7b14\u76f4\u98de\u6765\uff0c\u4f46\u5251\u5c16\u6296\u52a8\uff0c\u5df2\u662f\u7f69\u7740\u4e86\u5979\u7684\u8eab\u5f62\uff0c\u6b64\u65f6\u60f3\u8981\u95ea\u907f\u4ea6 \u5df1\u96be\u4e86\u3002 \u3000\u3000\u8fd9\u5c11\u5987\u548c\u7948\u5723\u56e0\u4ea4\u624b\u4e86\u4e00\u767e\u591a\u62db\uff0c\u77e5\u9053\u5bf9\u65b9\u7684\u5185\u529b\u548c\u81ea\u5df1\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u5984\u56fe\u8d25 \u4e2d\u53d6\u80dc\uff0c\u6211\u53ea\u8981\u78d5\u843d\u4f60\u7684\u5251\uff0c\u4f60\u4e0d\u8ba4\u8f93\u4e5f\u662f\u4e0d\u6210\uff01\u201d \u3000\u3000\u8fd9\u5c11\u5987\u4f7f\u51fa\u6d51\u8eab\u6c14\u529b\u4e00\u5251\u5288\u53bb\uff0c\u4e0d\u6599\u90a3\u77ed\u5251\u98de\u5230\u5979\u7684\u8eab\u524d\u5ffd\u5730\u8f6c\u4e86\u4e2a\u5f2f\uff0c\u5c11\u5987\u7684\u957f\u5251\u53ea \u662f\u5251\u5c16\u5fae\u5fae\u89e6\u53ca\u77ed\u5251\uff0c\u77ed\u5251\u8f6c\u4e86\u4e2a\u5f2f\uff0c\u53cd\u800c\u98de\u5f97\u66f4\u9ad8\u4e86\u3002 \u3000\u3000\u867d\u7136\u6ca1\u6709\u78d5\u843d\u77ed\u5251\uff0c\u4f46\u4e5f\u6ca1\u6709\u7ed9\u5b83\u4f24\u7740\uff0c\u5c11\u5987\u6b63\u81ea\u72c2\u559c\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u53ef\u8bf4\u7684\u4e48 \uff1f\u54ce\u54df\uff0c\u54df\u2026\u2026\u201d\u72c2\u559c\u7684\u53eb\u58f0\u7a81\u7136\u53d8\u4f5c\u4e86\u60ca\u9a87\u7684\u558a\u58f0\uff0c\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u7a81\u7136\u89c9\u5f97\u864e\u53e3\u4e00\u75db \uff0c\u539f\u6765\u7948\u5723\u56e0\u5728\u63b7\u51fa\u77ed\u5251\u7684\u540c\u65f6\uff0c\u8fd8\u5c04\u51fa\u4e86\u4e00\u6839\u6885\u82b1\u9488\u3002\u8f7b\u91cd\u60ac\u6b8a\u7684\u4e24\u79cd\u6697\u5668\uff0c\u540c\u65f6\u53d1\u51fa\uff0c \u7adf\u4e5f\u540c\u65f6\u98de\u5230\u3002\u8fd9\u5c11\u5987\u54ea\u60f3\u6cbb\u5230\u7948\u5723\u56e0\u7684\u6697\u5668\u624b\u6cd5\u5982\u6b64\u9ad8\u660e\uff0c\u5979\u5168\u795e\u5bf9\u4ed8\u63b7\u6765\u7684\u77ed\u5251\uff0c\u6839\u672c \u5c31\u6ca1\u6709\u53d1\u89c9\u90a3\u6839\u6885\u82b1\u9488\uff0c\u51b7\u4e0d\u9632\u4fbf\u7740\u4e86\u9053\u513f\u3002 \u3000\u3000\u6885\u82b1\u9488\u6b63\u597d\u523a\u7740\u5979\u7684\u864e\u53e3\uff0c\u5c11\u5987\u54ce\u54df\u4e00\u58f0\uff0c\u957f\u5251\u5f53\u5577\u5760\u5730\u3002 \u3000\u3000\u7948\u5723\u56e0\u51b7\u7b11\u9053\uff1a\u201c\u662f\u4f60\u64a4\u5251\u8fd8\u662f\u6211\u64a4\u5251\uff01\u201d\u98de\u8eab\u4e00\u63a0\uff0c\u628a\u624b\u4e00\u6284\uff0c\u521a\u597d\u5c06\u843d\u4e0b\u6765\u7684\u77ed\u5251 \u63a5\u5230\u624b\u4e2d\u3002 \u3000\u3000\u7948\u5723\u56e0\u7684\u77ed\u5251\u662f\u81ea\u5df1\u63b7\u51fa\u53bb\u7684\uff0c\u800c\u4e14\u5e76\u6ca1\u843d\u5730\u5c31\u56de\u5230\u5979\u7684\u624b\u4e2d\uff1b\u5c11\u5987\u7684\u5b9d\u5251\u5374\u662f\u7ed9\u5979\u7684 \u6697\u5668\u6253\u843d\u7684\uff0c\u6309\u7167\u6597\u5251\u7684\u672f\u8bed\u6765\u8bf4\uff0c\u201c\u6492\u5251\u201d\u7684\u5f53\u7136\u662f\u90a3\u5c11\u5987\u4e86\u3002 \u3000\u3000\u957f\u987b\u6c49\u5b50\u89c1\u8bc6\u6781\u9ad8\uff0c\u4ed6\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u5728\u7948\u5723\u56e0\u63b7\u5251\u4e4b\u65f6\uff0c\u4fbf\u77e5\u90a3\u5c11\u5987\u5b9a\u8981\u843d\u8d25 \uff0c\u7acb\u5373\u4ee5\u653b\u4e3a\u5b88\uff0c\u4e00\u62db\u201c\u957f\u6cb3\u843d\u65e5\u201d\u8ff3\u523a\u5c09\u8fdf\u70af\u7684\u54bd\u5589\u3002\u5e0c\u671b\u53ef\u4ee5\u51fb\u8d25\u5c09\u8fdf\u70af\uff0c\u90a3\u4e48\u53ef\u4ee5\u633d \u56de\u5e73\u5c40\u3002 \u3000\u3000\u8fd9\u4e00\u5251\u662f\u957f\u987b\u6c49\u5b50\u5e0c\u671b\u4e4b\u6240\u5bc4\uff0c\u4f7f\u51fa\u7684\u5f53\u7136\u662f\u4ed6\u7684\u6740\u624b\u7edd\u62db\u3002\u4e00\u5251\u523a\u6765\uff0c\u5c09\u8fdf\u70af\u7684\u5feb\u5200 \u7adf\u7136\u5c01\u95ed\u4e0d\u4f4f\u3002 \u3000\u3000\u957f\u987b\u6c49\u5b50\u4e1a\u5df2\u7b97\u51c6\u4e86\u597d\u51e0\u4e2a\u590d\u6742\u7684\u540e\u7740\uff0c\u4efb\u51ed\u5c09\u8fdf\u70af\u5982\u4f55\u5e94\u4ed8\uff0c\u4ed6\u90fd\u53ef\u4ee5\u5c06\u5c09\u8fdf\u70af\u523a\u4f24 \u3002\u4e0d\u6599\u5c09\u8fdf\u70af\u9661\u5730\u4e00\u58f0\u5927\u559d\u3001\u5728\u8fd9\u7535\u5149\u77f3\u706b\u4e4b\u95f4\uff0c\u731b\u7684\u4e00\u5200\u5c31\u5288\u4e0b\u6765\uff0c\u6839\u672c\u5c31\u4e0d\u662f\u4ec0\u4e48\u62db\u6570 \u3002\u800c\u662f\u8981\u548c\u5bf9\u65b9\u62fc\u4e2a\u540c\u5f52\u4e8e\u5c3d\u7684\u6253\u6cd5\uff01\u7531\u4e8e\u53cc\u65b9\u90fd\u662f\u5feb\u5230\u6781\u70b9\uff0c\u6ca1\u6709\u56de\u65cb\u7684\u4f59\u5730\uff0c\u8fd9\u6837\u4e00\u6765 \uff0c\u80dc\u8d25\u5c31\u53ea\u80fd\u53d6\u51b3\u4e8e\u672c\u8eab\u7684\u52c7\u6c14\u4e86\u3002 \u3000\u3000\u53cc\u65b9\u52a8\u4f5c\u90fd\u662f\u5feb\u5230\u6781\u70b9\uff0c\u5728\u8fd9\u6027\u547d\u4fc4\u9877\u4e4b\u95f4\uff0c\u5df2\u662f\u6ca1\u6709\u8003\u8651\u7684\u4f59\u5730\u3002\u957f\u987b\u6c49\u5b50\u8fd9\u4e00\u5251\u82e5 \u662f\u7528\u529b\u523a\u8fc7\u53bb\uff0c\u56fa\u7136\u53ef\u4ee5\u7a7f\u8fc7\u5c09\u8fdf\u70af\u7684\u54bd\u5589\uff0c\u4f46\u5c09\u8fdf\u70af\u8fd9\u4e00\u5200\u5288\u4e0b\u6765\uff0c\u4e5f\u53ef\u4ee5\u5c06\u4ed6\u7684\u5934\u9885\u5288 \u5f00\u4e24\u7247\uff01\u957f\u987b\u6c49\u5b50\u6bd5\u7adf\u662f\u8f83\u4e3a\u6015\u6b7b\uff0c\u9661\u7136\u95f4\u89c1\u5230\u5200\u5149\u5982\u96ea\u5288\u5c06\u4e0b\u6765\uff0c\u5fc3\u91cc\u4e00\u60ca\uff0c\u672c\u80fd\u7684\u5c31\u5f80 \u540e\u8eb2\u3002\u540c\u65f6\u56de\u5251\u906e\u62e6\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u5251\u672c\u6765\u662f\u5411\u524d\u523a\u53bb\u7684\uff0c\u73b0\u5728\u6539\u4e3a\u56de\u5251\u906e\u62e6\uff0c\u867d\u7136\u4ed6\u7684\u5251\u6cd5\u53ef\u4ee5\u6536\u53d1\u968f\u5fc3\uff0c\u4f46\u5728\u8fd9 \u4e00\u6536\u4e00\u53d1\u4e4b\u95f4\uff0c\u52b2\u9053\u81ea\u4e5f\u4e0d\u514d\u7a0d\u51cf\u51e0\u5206\u3002\u5c09\u8fdf\u70af\u53ea\u89c9\u80f8\u53e3\u4e00\u51c9\uff0c\u4e0d\u987e\u81ea\u5df1\u662f\u5426\u53d7\u4f24\uff0c\u8fd9\u4e00\u5200 \u4ecd\u662f\u7528\u5c3d\u5168\u529b\u7684\u5288\u4e0b\uff0c\u5c09\u8fdf\u70af\u7684\u6c14\u529b\u672c\u6765\u5c31\u80dc\u8fc7\u90a3\u957f\u987b\u6c49\u5b50\uff0c\u66f4\u52a0\u4ee5\u6b64\u6d88\u5f7c\u957f\uff0c\u4e00\u4e2a\u662f\u52c7\u6c14 \u500d\u589e\uff0c\u4e00\u4e2a\u662f\u533f\u7f29\u9000\u907f\uff0c\u7ed3\u679c\u5f53\u7136\u662f\u5c09\u8fdf\u70af\u83b7\u80dc\u4e86\u3002\u5200\u5251\u76f8\u4ea4\u4e4b\u4e0b\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c \u957f\u987b\u6c49\u5b50\u7684\u5251\u8131\u624b\u98de\u4e0a\u534a\u5929\uff0c\u5e78\u4e8f\u4ed6\u5012\u7eb5\u5f97\u5feb\uff0c\u4e00\u8dc3\u8dc3\u51fa\u4e09\u4e08\u5f00\u5916\uff0c\u8fd9\u624d\u6ca1\u6709\u53d7\u4f24\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\uff0c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u53ef\u8981\u62ff\u5f69\u7269\u5566\uff01\u201d\u98de\u8eab\u4e00\u63a0\uff0c\u8de8\u4e0a \u957f\u987b\u6c49\u5b50\u90a3\u5339\u5750\u9a91\u3002 \u3000\u3000\u957f\u987b\u6c49\u5b50\u53eb\u9053\uff1a\u201c\u6211\u7684\u5251\u6cd5\u53ef\u6ca1\u6709\u8f93\u7ed9\u4f60\uff0c\u4f60\u81ea\u5df1\u5e94\u8be5\u660e\u767d\uff01\u201d\u5c09\u8fdf\u70af\u559d\u9053\uff1a\u201c\u5251\u6cd5\u6ca1 \u8f93\uff0c\u4eba\u5374\u8f93\u4e86\uff01\u662f\u597d\u6c49\u5b50\u5c31\u522b\u8981\u80e1\u8d56\uff01\u201d\u7948\u5723\u56e0\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4e0d\u8981\u4e0e\u4ed6\u80e1\u7f20\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u6b64\u65f6\u5979\u4e5f\u65e9\u5df2\u62a2\u4e86\u90a3\u5c11\u5987\u7684\u5750\u9a91\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5987\u8f93\u5f97\u66f4\u4e0d\u670d\u6c14\uff0c\u9a82\u9053\uff1a\u201c\u6697\u5668\u4f24\u4eba\uff0c\u7b97\u5f97\u4ec0\u4e48\u597d\u6c49\uff01\u201d\u7948\u5723\u56e0\u7b11\u9053\uff1a\u201c\u8c01\u8bf4\u8fc7\u4e0d \u8bb8\u4f7f\u7528\u6697\u5668\u7684\uff1f\u7b97\u4e86\u5427\uff0c\u6211\u548c\u4f60\u90fd\u662f\u5973\u4eba\uff0c\u8c01\u4e5f\u4e0d\u5fc5\u5192\u5145\u2018\u597d\u6c49\u2019\uff01\u8f93\u4e86\u5c31\u662f\u8f93\u4e86\uff01\u201d\u7b11\u58f0 \u4e2d\u5530\u7684\u4e00\u97ad\uff0c\u50ac\u9a6c\u4fbf\u8dd1\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u672c\u8eab\u9a6c\u8d3c\u51fa\u8eab\uff0c\u9a91\u672f\u66f4\u4e3a\u8d85\u5353\u3002\u957f\u987b\u6c49\u5b50\u7684\u4e09\u4e2a\u5e08\u4f84\uff0c\u9a91\u7740\u9a6c\u4ece\u4e09\u65b9\u5411\u540c\u65f6\u5411\u4ed6 \u51b2\u6765\uff0c\u6709\u4e00\u5339\u9a6c\u8fd8\u662f\u4ece\u4ed6\u5bf9\u9762\u6765\u7684\uff0c\u773c\u770b\u5c31\u8981\u88ab\u56f4\u5728\u6838\u5fc3\uff0c\u5c09\u8fdf\u70af\u4e0d\u614c\u4e0d\u5fd9\u7684\u4e00\u63d0\u9a6c\u7ef3\uff0c\u731b \u5730\u4e00\u58f0\u5927\u559d\uff0c\u8dea\u4e0b\u7684\u5750\u9a91\u8dc3\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u7adf\u7136\u4ece\u5bf9\u9762\u90a3\u4eba\u7684\u5934\u9876\u8dc3\u4e86\u8fc7\u53bb\uff0c\u5413\u5f97\u90a3\u4e2a\u4eba\u6eda\u4e0b \u9a6c\u6765\u3002 \u3000\u3000\u957f\u987b\u6c49\u5b50\u548c\u90a3\u5c11\u5987\u9a91\u4e86\u5750\u9a91\uff0c\u5176\u4f59\u4e94\u4eba\u4e0d\u7518\u53d7\u632b\uff0c\u4ecd\u7136\u9a91\u9a6c\u6765\u8ffd\uff0c\u4e0d\u8fc7\uff0c\u5413\u5f97\u6eda\u4e0b\u9a6c\u7684 \u90a3\u4e2a\u4eba\u91cd\u65b0\u4e0a\u9a6c\u6765\u9053\uff0c\u5374\u662f\u843d\u540e\u751a\u591a\u4e86\u3002 \u3000\u3000\u7948\u5723\u56e0\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4e14\u53eb\u4f60\u4eec\u518d\u89c1\u8bc6\u89c1\u8bc6\u6211\u7684\u6697\u5668\u529f\u592b\uff01\u201d\u53cc\u624b\u5728\u6697\u5668\u56ca\u4e2d\u4e71\u6293\uff0c\u98de \u8757\u77f3\u3001\u94c1\u83e9\u63d0\uff0c\u8774\u8776\u9556\uff0c\u52a0\u4e0a\u4e86\u8896\u7bad\uff0c\u6885\u82b1\u9488\u7b49\u7b49\u5927\u5927\u5c0f\u5c0f\u7684\u6697\u5668\uff0c\u4fbf\u4f3c\u6d41\u6c34\u822c\u5730\u53d1\u51fa\u6765\u3002 \u5f53\u771f\u662f\u4e0d\u6127\u201c\u5343\u624b\u89c2\u97f3\u201d\u7684\u79f0\u53f7\uff01 \u3000\u3000\u8fd9\u56db\u4e2a\u4eba\u77e5\u9053\u5979\u7684\u6697\u5668\u5389\u5bb3\uff0c\u65e9\u6709\u51c6\u5907\uff0c\u5f53\u4e0b\u4e00\u9762\u8fd0\u529f\u4fdd\u62a4\u9a6c\u5339\uff0c\u4e00\u5411\u821e\u5251\u9632\u8eab\uff0c\u53ea\u542c \u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u90a3\u4e9b\u4efd\u91cf\u8f83\u91cd\u7684\u6697\u5668\u7ed9\u4ed6\u4eec\u7684\u957f\u5251\u8361\u5f00\uff0c\u6ee1\u7a7a\u98de\u821e\uff0c\u4efd\u91cf\u8f7b\u7684\u6697 \u5668\u6253\u7740\u4e86\u4ed6\u4eec\uff0c\u4e5f\u7ed9\u4ed6\u4eec\u7684\u62a4\u4f53\u795e\u529f\u9707\u843d\u3002 \u3000\u3000\u77f3\u536b\u559d\u9053\uff1a\u201c\u533a\u533a\u6697\u5668\uff0c\u80fd\u5948\u6211\u4f55\uff1f\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u4ed6\u80ef\u4e0b\u7684\u5750\u9a91\u5ffd\u5730\u4e00\u58f0\u5636\u9e23\uff0c\u56db\u819d\u5c48 \u5730\uff0c\u8fd9\u5339\u9a6c\u662f\u5728\u75be\u8dd1\u4e4b\u4e2d\u7a81\u7136\u5012\u4e0b\u7684\uff0c\u9669\u4e9b\u5c06\u4ed6\u6454\u4e0b\u9a6c\u6765\u3002\u4e0d\u5355\u662f\u4ed6\u7684\u5750\u9a91\u5982\u6b64\uff0c\u8f6c\u773c\u4e4b\u95f4 \uff0c\u53e6\u5916\u4e00\u4e2a\u4eba\u7684\u5750\u9a91\u4e5f\u90fd\u662f\u5982\u6b64\u7684\u7a81\u7136\u5012\u4e0b\u4e86\u3002 \u3000\u3000\u539f\u6765\u7948\u5723\u56e0\u7684\u6697\u5668\u5176\u5b9e\u4e43\u662f\u5c04\u9a6c\u800c\u975e\u5c04\u4eba\uff0c\u5979\u77e5\u9053\u8fd9\u4e9b\u4eba\u7684\u672c\u9886\u9ad8\u5f3a\uff0c\u6697\u5668\u5b9a\u7136\u4f24\u4ed6\u4eec \u4e0d\u5f97\uff0c\u5411\u4ed6\u4eec\u4e71\u53d1\u7684\u6697\u5668\uff0c\u7528\u610f\u4e0d\u8fc7\u662f\u8981\u4ed6\u4eec\u817e\u51fa\u624b\u6765\u5e94\u4ed8\u800c\u5df2\u3002\u4ed6\u4eec\u4fdd\u62a4\u5f97\u4e86\u81ea\u8eab\uff0c\u4fdd\u62a4 \u4e0d\u4e86\u5750\u9a91\u3002\u80ef\u4e0b\u7684\u5750\u9a91\uff0c\u90fd\u7ed9\u7948\u5723\u56e0\u7528\u6885\u82b1\u9488\u5c04\u778e\u4e86\u53cc\u76ee\uff0c\u800c\u4e14\u8fd9\u6885\u82b1\u9488\u8fd8\u662f\u89c1\u8840\u5c01\u5589\u7684\u6bd2 \u9488\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u6700\u7231\u540d\u9a6c\uff0c\u53f9\u9053\uff1a\u201c\u53ef\u60dc\uff0c\u53ef\u60dc\uff01\u5176\u5b9e\u4ed6\u4eec\u8981\u8ffd\u4e5f\u8ffd\u4e0d\u4e0a\u54b1\u4eec\uff0c\u4f55\u5fc5\u6740\u4e86\u8fd9\u51e0\u9a91 \u9a8f\u9a6c\uff1f\u201d\u7948\u5723\u56e0\u7b11\u9053\uff1a\u201c\u9a8f\u9a6c\u843d\u5728\u574f\u4eba\u624b\u91cc\uff0c\u90a3\u5c31\u662f\u5982\u864e\u6dfb\u7ffc\u4e86\u3002\u54b1\u4eec\u53c8\u8981\u4e0d\u4e86\u8fd9\u4e48\u591a\uff0c\u4e3a \u4f55\u4e0d\u6740\uff1f\u4e5f\u514d\u5f97\u4ed6\u4eec\u9634\u9b42\u4e0d\u6563\u7684\u80e1\u7f20\u554a\uff01\u201d\u8c08\u7b11\u4e4b\u95f4\uff0c\u592b\u59bb\u4e8c\u4eba\u5e76\u8f94\u75be\u9a70\uff0c\u5df2\u662f\u53bb\u5f97\u8fdc\u4e86\uff0c \u201c\u6276\u6851\u4e03\u624b\u201d\u53ea\u5269\u4e0b\u4e00\u5339\u5750\u9a91\uff0c\u5f53\u7136\u65e0\u6cd5\u8ffd\u8d76\u3002 \u3000\u3000\u4e14\u8bf4\u5c09\u8fdf\u70af\u592b\u59bb\u89c1\u4e86\u6797\u65e0\u53cc\u7684\u7236\u4eb2\u98de\u9c7c\u5c9b\u4e3b\uff0c\u8bb2\u4e86\u90a3\u65e5\u548c\u6276\u6851\u4e03\u5b50\u4ea4\u624b\u7684\u7ecf\u8fc7\u4e4b\u540e\uff0c\u98de \u9c7c\u5c9b\u4e3b\u53c8\u60ca\u53c8\u559c\uff0c\u60ca\u7684\u662f\u6276\u6851\u6d3e\u7684\u4e03\u540d\u9ad8\u624b\uff0c\u7adf\u7136\u8054\u8882\u6765\u5230\u4e2d\u539f\uff0c\u559c\u8005\u662f\u8001\u670b\u53cb\u5e78\u4e8f\u5f97\u4ee5\u5b89 \u7136\u65e0\u4e8b\uff0c\u5f53\u4e0b\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u670b\u53cb\u96c4\u98ce\u5982\u6614\uff0c\u53ef\u559c\u53ef\u8d3a\u3002\u4f46\u60f3\u4e0d\u5230\u4ed6\u4eec\u521d\u5230\u4e2d\u539f\uff0c\u7adf \u7136\u5c31\u4f1a\u6295\u9760\u671d\u5ef7\uff0c\u4f60\u7ed9\u4ed6\u4eec\u4e00\u70b9\u632b\u6298\uff0c\u4e5f\u662f\u597d\u7684\u3002\u201d \u3000\u3000\u5c09\u8fdf\u70af\u542c\u4e86\u8001\u53cb\u7684\u79f0\u8d5e\uff0c\u8138\u4e0a\u5374\u662f\u6beb\u65e0\u5f97\u8272\uff0c\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u8bf4\u8001\u5b9e\u8bdd\uff0c\u82e5\u8bba\u5251\u6cd5\uff0c\u6211\u8fd8 \u662f\u8f93\u7ed9\u90a3\u957f\u987b\u6c49\u5b50\u7684\u3002\u4f60\u770b\u2026\u2026\u201d\u8131\u4e0b\u5916\u8863\uff0c\u53ea\u89c1\u886c\u886b\u4e0a\u80f8\u53e3\u4e4b\u5904\uff0c\u7a7f\u4e86\u4e09\u4e2a\u5c0f\u7a9f\u7abf\uff0c\u6bcf\u4e2a \u90fd\u6709\u94dc\u94b1\u822c\u5927\u5c0f\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u90a3\u5929\u7a7f\u7684\u886c\u886b\u4e86\uff0c\u82e5\u4e0d\u662f\u4ed6\u6025\u4e8e\u56de\u5251\u62db\u67b6\uff0c\u5251\u5c16\u518d\u8fdb\u534a\u5206\uff0c\u6211 \u7684\u8eab\u4e0a\u5df2\u662f\u6dfb\u4e86\u4e09\u4e2a\u7a9f\u7abf\u4e86\u3002\u201d\u6b64\u4e8b\u7948\u5723\u56e0\u90fd\u672a\u77e5\u9053\uff0c\u4e00\u770b\u4e4b\u4e0b\uff0c\u4e0d\u7981\u9a87\u7136\u5931\u8272\u3002 \u3000\u3000\u6797\u65e0\u53cc\u5ffd\u9053\uff1a\u201c\u8fd9\u4e00\u62db\u662f\u4e09\u8f6c\u6cd5\u8f6e\uff0c\u725f\u8868\u54e5\u6700\u5f97\u610f\u7684\u5c31\u662f\u8fd9\u62db\uff0c\u4e0d\u77e5\u4ed6\u662f\u5426\u4e5f\u5728\u4e03\u4eba\u4e4b \u5185\u3002\u201d \u3000\u3000\u6797\u65e0\u53cc\u7684\u7236\u4eb2\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u8c22\u8fdf\u5927\u54e5\u6240\u8bf4\uff0c\u5176\u4e2d\u7684\u56db\u4e2a\u4eba\uff0c\u6211\u5df2\u77e5\u9053\u662f\u8c01\u4e86\u3002 \u201d\u6797\u65e0\u53cc\u9053\uff1a\u201c\u90a3\u957f\u987b\u6c49\u5b50\u60f3\u5fc5\u662f\u4e1c\u6d77\u56e2\u6c99\u5c9b\u7684\u5b97\u5e08\u53d4\uff1f\u201d \u3000\u3000\u98de\u9c7c\u5c9b\u4e3b\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6b64\u4eba\u66fe\u7ecf\u5230\u8fc7\u98de\u9c7c\u5c9b\uff0c\u53d9\u8d77\u5e08\u95e8\u8c31\u7cfb\uff0c\u548c\u6211\u4e43\u662f\u5e73\u8f88\u3002\u4ed6\u540d\u53eb\u5b97 \u795e\u9f99\uff0c\u90a3\u5bf9\u592b\u59bb\u4e43\u662f\u536b\u548c\u6851\u9752\uff0c\u5728\u6276\u6851\u6d3e\u4e2d\u53f7\u79f0\u592b\u59bb\u53cc\u4fa0\u3002\u201d \u3000\u3000\u5c09\u8fdf\u70af\u51b7\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u5982\u4eca\u5df1\u662f\u6e05\u5ef7\u7684\u9e70\u722a\u5b59\uff0c\u8fd8\u914d\u79f0\u4ec0\u4e48\u53cc\u4fa0\uff1f\u201d \u3000\u3000\u6797\u65e0\u53cc\u7684\u7236\u4eb2\u9053\uff1a\u201c\u4ed6\u4eec\u5728\u6d77\u5916\u4e4b\u65f6\uff0c\u90fd\u662f\u5230\u5904\u6253\u62b1\u4e0d\u5e73\uff0c\u4e5f\u66fe\u53c2\u52a0\u8fc7\u6297\u502d\u4e4b\u6218\u7684\u3002\u8bf4 \u4e0d\u5b9a\uff0c\u662f\u56e0\u4e3a\u4ed6\u4eec\u521d\u56de\u4e2d\u539f\uff0c\u672a\u660e\u5927\u5c40\uff0c\u53ea\u77e5\u9053\u8981\u62e5\u62a4\u2018\u81ea\u5df1\u7684\u2019\u671d\u5ef7\uff0c\u7cca\u91cc\u7cca\u6d82\u5c31\u4e0a\u4e86\u8428 \u798f\u9f0e\u7684\u5f53\u4e86\u3002\u201d \u3000\u3000\u7948\u5723\u56e0\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u770b\u4eba\u53ea\u770b\u4e00\u9762\u3002\u800c\u4e14\u4e5f\u5e38\u5e38\u72af\u4e86\u6025\u8e81\u7684\u6bdb\u75c5\u3002\u4fd7\u8bed\u8bf4\uff0c\u8def\u9065\u77e5 \u9a6c\u529b\uff0c\u65e5\u4e45\u89c1\u4eba\u5fc3\u2026\u2026\u201d \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u62ed\u65e5\u4ee5\u89c2\uff0c\u4f46\u613f\u4ed6\u4eec\u77e5\u9519\u80fd\u6539\u3002\u201d \u3000\u3000\u6797\u65e0\u53cc\u7684\u7236\u4eb2\u63a5\u7740\u8bf4\uff1a\u201c\u5b97\u795e\u9f99\u5728\u6d77\u5916\u7684\u58f0\u8a89\u5374\u4e0d\u5927\u597d\u3002\u4e5f\u8bf4\u4e0d\u5b9a\u662f\u7531\u4e8e\u4ed6\u7684\u8d2a\u56fe\u5229\u7984 \uff0c\u4ee5\u81f4\u7d2f\u4e86\u5176\u4ed6\u7684\u516d\u4e2a\u4eba\u8ddf\u4ed6\u8d70\u4e0a\u6b6a\u8def\u3002\u56e0\u4e3a\u8fd9\u516d\u4e2a\u4eba\u90fd\u662f\u4ed6\u7684\u665a\u8f88\u3002\u201d \u3000\u3000\u6797\u65e0\u53cc\u9053\uff1a\u201c\u7239\u7239\u4f60\u8bf4\u77e5\u9053\u5176\u4e2d\u56db\u4eba\u7684\u6765\u5386\uff0c\u90a3\u4e48\u8fd8\u6709\u4e00\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5979\u7684\u7236\u4eb2\u9053\uff1a\u201c\u5c31\u662f\u5c09\u8fdf\u5927\u5ac2\u8bf4\u7684\u90a3\u4e2a\u66fe\u548c\u6851\u9752\u4e89\u7740\u8981\u4e0e\u5979\u4ea4\u624b\u7684\u90a3\u4e2a\u5c11\u5973\u4e86\uff0c\u6211\u6000\u7591 \u5979\u5c31\u662f\u4f60\u513f\u65f6\u7684\u597d\u53cb\u7ec3\u5f69\u8679\u3002\u201d \u3000\u3000\u7ec3\u5f69\u8679\u662f\u6797\u65e0\u53cc\u521d\u5230\u98de\u9c7c\u5c9b\u4e4b\u65f6\u4ea4\u4e0a\u7684\u670b\u53cb\uff0c\u7ec3\u5f69\u8679\u662f\u4e2a\u6e14\u5bb6\u5973\uff0c\u5e74\u7eaa\u6bd4\u6797\u65e0\u53cc\u5927\u4e24\u5c81 \uff0c\u8ddf\u6797\u65e0\u53cc\u7684\u7236\u4eb2\u5b66\u4e86\u4e24\u5e74\u529f\u592b\uff0c\u540e\u6765\u98de\u9c7c\u5c9b\u88ab\u502d\u4eba\u5360\u9886\uff0c\u5979\u4eec\u8fd9\u4e00\u5bb6\u6ca1\u6709\u8ddf\u968f\u6797\u65e0\u53cc\u7236\u5973 \u9003\u8d70\u3002\u540e\u6765\u542c\u8bf4\u5979\u88ab\u5b97\u795e\u9f99\u7684\u59bb\u5b50\u6536\u4e3a\u5f1f\u5b50\uff0c\u4e5f\u4e0d\u5982\u662f\u771f\u662f\u5047\u3002 \u3000\u3000\u6797\u65e0\u53cc\u9053\uff1a\u201c\u5982\u679c\u5f53\u771f\u662f\u7ec3\u59d0\u59d0\u90a3\u5c31\u597d\u4e86\uff0c\u5979\u548c\u6211\u6700\u5408\u5f97\u6765\uff0c\u53ea\u8981\u6211\u89c1\u4e86\u5979\uff0c\u5979\u4e00\u5b9a\u4f1a \u542c\u6211\u7684\u8bdd\u7684\u3002\u5509\uff0c\u5982\u4eca\u5c31\u53ea\u4e0d\u77e5\u725f\u8868\u54e5\u662f\u5426\u5728\u90a3\u4e09\u4eba\u4e4b\u5185\u4e86\u3002\u5c09\u8fdf\u53d4\u53d4\uff0c\u90a3\u4e09\u4e2a\u4eba\u662f\u4ec0\u4e48\u6a21 \u6837\uff0c\u4f60\u8bb0\u5f97\u8d77\u6765\u5417\uff1f\u201d \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u9664\u4e86\u5b97\u795e\u9f99\u4e4b\u5916\uff0c\u5176\u4ed6\u56db\u4e2a\u4e2d\u5e74\u7537\u5b50\u90fd\u662f\u9ed1\u9ed1\u5b9e\u5b9e\u7684\u6c49\u5b50\uff0c\u53ea\u6709\u77f3\u536b\u4f3c\u4e4e \u6bd4\u8f83\u767d\u51c0\u3002\u8fd9\u56db\u4e2a\u4eba\u8eab\u6750\u4e5f\u597d\u50cf\u5dee\u4e0d\u591a\u3002\u4f60\u53eb\u6211\u8bf4\u51fa\u4ed6\u4eec\u8be6\u7ec6\u7684\u5f62\u8c8c\uff0c\u6211\u53ef\u662f\u8bf4\u4e0d\u51fa\u6765\u4e86\u3002 \u201d\u7948\u5723\u56e0\u7b11\u9053\uff1a\u201c\u4f60\u4e00\u5411\u5c31\u662f\u8fd9\u6837\u7c97\u5fc3\u5927\u610f\u3002\u4e0d\u8fc7\u6211\u5f53\u65f6\u4e5f\u662f\u548c\u4f60\u4e00\u6837\uff0c\u5168\u526f\u7cbe\u795e\u53ea\u662f\u7528\u6765 \u5bf9\u4ed8\u4ed6\u4eec\uff0c\u6fc0\u6597\u4e2d\u5bf9\u4ed6\u4eec\u7684\u76f8\u8c8c\u53ef\u4e5f\u6ca1\u6709\u8be6\u7ec6\u7559\u610f\u4e86\u3002\u201d \u3000\u3000\u6797\u65e0\u53cc\u7684\u7236\u4eb2\u7b11\u9053\uff1a\u201c\u4f60\u548c\u725f\u8868\u54e5\u522b\u79bb\u5df2\u6709\u5341\u5e74\uff0c\u53ea\u6015\u4f60\u4eec\u89c1\u4e86\u9762\uff0c\u4f60\u4e5f\u672a\u5fc5\u8ba4\u5f97\u4ed6\u5462 \u3002\u201d \u3000\u3000\u6797\u65e0\u53cc\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u54b1\u4eec\u603b\u5f97\u627e\u7740\u8868\u54e5\u624d\u597d\u3002\u201d\u539f\u6765\u6797\u65e0\u53cc\u7684\u5e74\u7eaa\u867d\u7136\u662f\u6bd4\u725f\u5b97\u6d9b \u5e74\u8f7b\u5341\u5e74\uff0c\u5206\u624b\u4e4b\u65f6\uff0c\u5979\u8fd8\u662f\u4e2a\u4e0d\u61c2\u4eba\u4e8b\u7684\u5c0f\u59d1\u5a18\uff0c\u4f46\u56e0\u8868\u54e5\u662f\u5979\u81ea\u5c0f\u5c31\u4eb2\u8fd1\u60ef\u7684\uff0c\u5728\u5979\u6240 \u76f8\u8bc6\u7684\u7537\u5b50\u4e2d\uff0c\u4e5f\u53ea\u6709\u8868\u54e5\u53ef\u4ee5\u7b97\u4f5c\u662f\u5979\u7684\u670b\u53cb\u3002\u6545\u6b64\u5728\u5979\u957f\u5927\u4e4b\u540e\uff0c\u4e00\u76f4\u5bf9\u8868\u54e5\u5ff5\u5ff5\u4e0d\u5fd8 \u3002 \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u4f60\u8981\u6253\u542c\u4ed6\u4eec\u7684\u6d88\u606f\uff0c\u6211\u5012\u6709\u4e2a\u4e3b\u610f\u3002\u201d \u3000\u3000\u98de\u9c7c\u5c9b\u4e3b\u9053\uff1a\u201c\u8bf7\u5c09\u8fdf\u5144\u6307\u70b9\u3002\u201d \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u524d\u4e24\u5929\u6211\u78b0\u5230\u4e10\u5e2e\u7684\u5f1f\u5b50\uff0c\u83b7\u77e5\u4e00\u4e2a\u6d88\u606f\u3002\u539f\u6765\u6276\u6851\u4e03\u5b50\u4e0d\u4f46\u5949\u4e86\u8428\u798f\u9f0e \u4e4b\u547d\u6765\u5bf9\u4ed8\u6211\uff0c\u800c\u4e14\u8fd8\u66fe\u7ecf\u5bf9\u4ed8\u6c5f\u5927\u4fa0\u3002\u201d \u3000\u3000\u98de\u9c7c\u5c9b\u4e3b\u9053\uff1a\u201c\u771f\u662f\u80c6\u5927\u5984\u4e3a\uff01\u201d \u3000\u3000\u5c09\u8fdf\u70af\u7b11\u9053\uff1a\u201c\u8fd8\u4e0d\u53ea\u6b64\u5462\u3002\u4ed6\u4eec\u5230\u4e86\u6c5f\u5927\u4fa0\u5bb6\u91cc\uff0c\u6c5f\u5927\u4fa0\u4e0d\u5728\u5bb6\uff0c\u4f46\u5374\u6070\u5de7\u78b0\u4e86\u4ece\u6d77 \u5916\u5f52\u6765\u7684\u6c5f\u5927\u4fa0\u7684\u5e08\u7236\u3002\u201d \u3000\u3000\u98de\u9c7c\u5c9b\u4e3b\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u5f97\u53ef\u662f\u91d1\u4e16\u9057\u91d1\u5927\u4fa0\uff1f\u201d \u3000\u3000\u5c09\u8fdf\u70af\u7b11\u9053\uff1a\u201c\u6b63\u662f\u3002\u4ed6\u4eec\u548c\u91d1\u5927\u4fa0\u4e5f\u52a8\u4e86\u624b\u4e86\u3002\u4e0d\u8fc7\u8fd9\u6b21\u53ef\u5c31\u4e0d\u662f\u5355\u6253\u72ec\u6597\u4e86\uff0c\u800c\u662f \u4ee5\u4e03\u654c\u4e00\u7684\u7fa4\u6bb4\u3002\u201d \u3000\u3000\u98de\u9c7c\u5c9b\u4e3b\u53f9\u9053\uff1a\u201c\u672c\u6d3e\u7684\u9762\u5b50\u90fd\u7ed9\u4ed6\u4eec\u4e22\u5c3d\u4e86\u3002\u60f3\u5fc5\u4ed6\u4eec\u4e0d\u4f1a\u5360\u5f97\u91d1\u5927\u4fa0\u7684\u4fbf\u5b9c\u5427\uff1f\u201d \u3000\u3000\u5c09\u8fdf\u70af\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u91d1\u5927\u4fa0\u600e\u4f1a\u6298\u5728\u4ed6\u4eec\u7684\u624b\u91cc\uff0c\u542c\u8bf4\u82e5\u4e0d\u662f\u91d1\u5927\u4fa0\u624b\u4e0b\u7559\u60c5\uff0c\u4ed6\u4eec\u4e00 \u4e2a\u4e5f\u8dd1\u4e0d\u6389\uff01\u201d\u8fd9\u5f53\u7136\u662f\u5c09\u8fdf\u70af\u5938\u5927\u5176\u8f9e\uff0c\u5176\u5b9e\u91d1\u4e16\u9057\u867d\u7136\u5927\u83b7\u5168\u80dc\uff0c\u5728\u5251\u6cd5\u4e0a\u4e5f\u66fe\u8f93\u4e86\u4e00 \u62db\u7684\u3002 \u3000\u3000\u98de\u9c7c\u5c9b\u4e3b\u672c\u6765\u6709\u70b9\u62c5\u5fc3\u91d1\u4e16\u9057\u548c\u201c\u6276\u6851\u4e03\u5b50\u201d\u6597\u4e2a\u4e24\u8d25\u4ff1\u4f24\u7684\uff0c\u542c\u5f97\u8fd9\u4e2a\u7ed3\u679c\uff0c\u677e\u4e86\u53e3 \u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8ba9\u4ed6\u4eec\u53d7\u4e2a\u6559\u8bad\u4e5f\u597d\uff0c\u597d\u53eb\u4ed6\u4eec\u77e5\u9053\u592d\u5916\u6709\u5929\uff0c\u4eba\u5916\u6709\u4eba\u3002\u4e5f\u4e0d\u77e5\u4ed6\u4eec\u73b0\u5728\u5df2 \u9003\u5f80\u4f55\u65b9\uff1f\u201d \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u786e\u5b9e\u7684\u6d88\u606f\u8fd8\u672a\u77e5\u9053\uff0c\u4e0d\u8fc7\u4ea6\u5df2\u6709\u4e86\u4e00\u4e2a\u7ebf\u7d22\u3002\u636e\u4e10\u5e2e\u6240\u77e5\uff0c\u7ed9\u2018\u6276\u6851\u4e03 \u5b50\u2019\u548c\u8428\u798f\u9f0e\u7a7f\u9488\u5f15\u94b1\u7684\u4e43\u662f\u6b27\u9633\u575a\uff0c\u6b27\u9633\u575a\u5982\u4eca\u5df2\u7ecf\u5230\u534e\u5c71\u4ed6\u7684\u8001\u5bb6\u53bb\u4e86\u2019\u3002\u4f60\u4eec\u60f3\u8981\u77e5 \u9053\u786e\u5b9e\u7684\u6d88\u606f\u4e0d\u59a8\u53bb\u6253\u63a2\u4e00\u4e0b\u3002\u201d \u3000\u3000\u6797\u65e0\u53cc\u8bb2\u5230\u8fd9\u91cc\uff0c\u8bf4\u9053\uff1a\u201c\u5bb6\u7236\u56e0\u4e3a\u548c\u5b97\u795e\u9f99\u8fc7\u53bb\u6709\u70b9\u6881\u5b50\uff0c\u6682\u65f6\u4e0d\u60f3\u9732\u9762\uff0c\u6240\u4ee5\u6211\u53ea \u597d\u72ec\u81ea\u6765\u4e86\u3002\u201d\u4f17\u4eba\u542c\u4e86\u5979\u6240\u8bf4\u7684\u524d\u56e0\u540e\u679c\uff0c\u65b9\u624d\u77e5\u9053\u5979\u662f\u8fd9\u6837\u6765\u5230\u534e\u5c71\u7684\u3002 \u3000\u3000\u91d1\u9010\u6d41\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u5c09\u8fdf\u70af\u592b\u59bb\u5462\uff0c\u4ed6\u4eec\u662f\u8fd8\u5728\u4f60\u4eec\u5bb6\u90a3\u513f\u8fd8\u662f\u5df2\u7ecf\u56de\u8f6c\u5c0f\u91d1\u5ddd\u4e86\uff1f\u201d \u3000\u3000\u6797\u65e0\u53cc\u9053\uff1a\u201c\u4ed6\u4eec\u672c\u6765\u60f3\u966a\u6211\u5230\u534e\u5c71\u7684\uff0c\u4f46\u56e0\u4e3a\u6709\u66f4\u7d27\u8981\u7684\u4e8b\u60c5\uff0c\u7b2c\u4e8c\u5929\u5c31\u52a8\u8eab\u5230\u5927\u51c9 \u5c71\u53bb\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u4e5f\u5230\u5927\u51c9\u5c71\u53bb\u4e86\uff1f\u662f\u4e0d\u662f\u53bb\u627e\u7afa\u5c1a\u7236\u8fd9\u652f\u4e49\u519b\u7684\u3002\u201d\u3002 \u3000\u3000\u6797\u5143\u53cc\u7b11\u9053\uff1a\u201c\u6b63\u662f\u3002\u91d1\u5c11\u4fa0\uff0c\u4f60\u53ef\u77e5\u9053\u4ed6\u4eec\u53bb\u5927\u51c9\u5c71\u4e3a\u4e86\u4f55\u4e8b\u5417\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5927\u51c9\u5c71\u4e0e\u5c0f\u91d1\u5ddd\u8fd9\u4e24\u652f\u4e49\u519b\u5507\u9f7f\u76f8\u4f9d\uff0c\u60f3\u5fc5\u4ed6\u4eec\u662f\u53bb\u8054\u7edc\u7684\u5427\uff1f\u201d \u3000\u3000\u6797\u65e0\u53cc\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u5012\u4e0d\u77e5\u3002\u4f46\u542c\u5c09\u8fdf\u70af\u8bf4\uff0c\u4e3b\u8981\u7684\u539f\u56e0\u662f\u53bb\u627e\u4f60\u7684\u4e24\u4e2a\u5e08\u4f84\u7684\u3002\u4ed6\u4eec \u4e00\u4e2a\u53eb\u6797\u9053\u8f69\uff0c\u4e00\u4e2a\u53eb\u674e\u5149\u590f\uff0c\u8fd9\u4e24\u4e2a\u540d\u5b57\u6211\u6ca1\u6709\u8bb0\u9519\u5427\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u6709\u70b9\u8be7\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u9519\uff0c\u4f46\u4e0d\u77e5\u5c09\u8fdf\u70af\u53c8\u662f\u4e3a\u4e86\u4f55\u4e8b\u53bb\u627e\u4ed6\u4eec\uff1f\u201d \u3000\u3000\u6797\u65e0\u53cc\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8bf4\u6765\u60ed\u6127\uff0c\u6211\u8fd9\u4f4d\u5b97\u5e08\u53d4\u7518\u5fc3\u53d7\u6e05\u5ef7\u5229\u7528\uff0c\u4e0d\u662f\u8dd1\u53bb\u4f60\u5e08\u5144 \u7684\u5bb6\u4e2d\u6363\u4e71\uff0c\u800c\u4e14\u7adf\u7136\u7387\u9886\u540c\u95e8\uff0c\u66ff\u6e05\u5ef7\u5356\u547d\uff0c\u5c06\u5929\u7406\u4f1a\u5728\u4fdd\u5b9a\u7684\u603b\u8235\u6311\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u7406\u4f1a\u7684\u603b\u8235\u7adf\u4e5f\u7ed9\u4ed6\u4eec\u6311\u4e86\uff0c\u8fd9\u662f\u4ec0\u4e48\u65f6\u5019\u7684\u4e8b\u60c5\uff1f\u201d\u5fc3 \u60f3\uff1a\u201c\u5929\u7406\u4f1a\u9ad8\u624b\u751a\u591a\uff0c\u82e5\u4e0d\u662f\u78b0\u4e0a\u6276\u6851\u4e03\u5b50\uff0c\u51b3\u4e0d\u4f1a\u906d\u53d7\u5982\u6b64\u60e8\u91cd\u7684\u635f\u5931\u3002\u201d \u3000\u3000\u6797\u65e0\u53cc\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4e00\u4e2a\u6708\u4ee5\u7684\u7684\u4e8b\u4e86\uff0c\u4ed6\u4eec\u662f\u6311\u4e86\u5929\u7406\u4f1a\u7684\u603b\u8235\u4e4b\u540e\uff0c\u624d\u51fa\u4eac\u57ce\u6597\u5267 \u8fdf\u70af\u3002\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u53f9\u9053\uff1a\u201c\u53ef\u60dc\u5c09\u8fdf\u70af\u8fdf\u4e86\u4e00\u6b65\uff0c\u82e5\u662f\u4ed6\u4eec\u592b\u59bb\u65e9\u5230\u4eac\u4e2d\uff0c\u6276\u6851\u4e03\u5b50\u5c31\u4e0d\u4f1a\u8fd9\u6837\u5bb9 \u6613\u5f97\u624b\u4e86\u3002\u5929\u7406\u4f1a\u7684\u603b\u8235\u4e3b\u5f53\u65f6\u53ef\u5728\u573a\u4e48\uff1f\u201d \u3000\u3000\u6797\u5143\u53cc\u9053\uff1a\u201c\u5e78\u4e8f\u6709\u5f20\u603b\u8235\u4e3b\u62fc\u547d\u62b5\u6321\uff0c\u635f\u5931\u624d\u4e0d\u81f3\u4e8e\u592a\u5927\u3002\u5185\u5802\u7684\u9999\u4e3b\u548c\u7559\u5b88\u7684\u5f1f\u5b50 \u5927\u90e8\u4efd\u9003\u4e86\u51fa\u6765\uff0c\u542c\u8bf4\u4f24\u4ea1\u7684\u4e0d\u8fc7\u5341\u4e4b\u4e00\u4e8c\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5f20\u603b\u8235\u4e3b\u5462\uff1f\u201d \u3000\u3000\u6797\u5143\u53cc\u9053\uff1a\u201c\u5c09\u8fdf\u70af\u5230\u4e86\u4fdd\u5b9a\uff0c\u521a\u597d\u8d74\u5f97\u4e0a\u4e0e\u4ed6\u8bdd\u522b\u3002\u4ed6\u5df2\u4e0d\u5e78\u727a\u7272\u4e86\u3002\u201d\u5929\u7406\u4f1a\u7684\u603b \u8235\u4e3b\u5f20\u58eb\u9f99\u4e43\u662f\u524d\u4efb\u8235\u4e3b\u6797\u6e05\u7684\u7ed3\u62dc\u5144\u5f1f\uff0c\u91d1\u9010\u6d41\u548c\u4ed6\u867d\u4e0d\u76f8\u8bc6\uff0c\u4f46\u4ed6\u5374\u662f\u6c5f\u6d77\u5929\u7684\u597d\u670b\u53cb \uff0c\u91d1\u9010\u6d41\u65e9\u5c31\u77e5\u9053\u4ed6\u7684\u5a01\u540d\u7684\u3002\u542c\u4e86\u8fd9\u4e2a\u6d88\u606f\uff0c\u5341\u5206\u96be\u8fc7\u3002 \u3000\u3000\u6797\u65e0\u53cc\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5f20\u58eb\u9f99\u4e34\u7ec8\u4e4b\u9645\uff0c\u62dc\u6258\u5c09\u8fdf\u70af\u5c06\u524d\u4efb\u4f53\u603b\u8235\u4e3b\u7684\u513f\u5b50\u627e\u56de\u6765\uff0c\u63a5\u4ed6 \u7684\u62c5\u5b50\uff0c\u4ee5\u514d\u7fa4\u9f99\u65e0\u9996\u3002\u8fd9\u5c31\u662f\u5267\u8fdf\u70af\u4e3a\u4ec0\u4e48\u8981\u6025\u5fd9\u8d76\u5f80\u897f\u660c\uff0c\u627e\u4f60\u90a3\u4e24\u4e2a\u5e08\u4f84\u7684\u539f\u56e0\u4e86\u3002 \u201d \u3000\u3000\u539f\u6765\u91d1\u9010\u6d41\u7684\u4e09\u5e08\u4f84\u6797\u9053\u8f69\u6b63\u662f\u524d\u4efb\u5927\u7406\u4f1a\u8235\u4e3b\u6797\u6e05\u7684\u513f\u5b50\uff0c\u56db\u5e08\u4f84\u674e\u5149\u590f\u7684\u7236\u4eb2\u674e\u6587 \u6210\u751f\u524d\u4e5f\u662f\u5929\u7406\u4f1a\u6700\u91cd\u8981\u7684\u4e00\u4f4d\u9999\u4e3b\uff0c\u5730\u4f4d\u4ec5\u6b21\u4e8e\u6797\u6e05\u7684\u3002\uff08\u4e8b\u8be6\u62d9\u8457\u300a\u98ce\u96f7\u9707\u4e5d\u5dde\u300b\u3002\uff09 \u3000\u3000\u6b64\u65f6\u5df2\u662f\u4e1c\u65b9\u5927\u767d\u7684\u65f6\u5019\uff0c\u6797\u65e0\u53cc\u5ffd\u5730\u9762\u4e0a\u4e00\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5c11\u4fa0\uff0c\u6211\u6240\u77e5\u9053\u7684\u4e8b\u60c5\u90fd \u5df2\u544a\u8bc9\u4f60\u4e86\u3002\u6211\u7684\u8868\u54e5\u2026\u2026\u201d \u3000\u3000\u91d1\u9010\u6d41\u970d\u7136\u4e00\u7701\uff0c\u7b11\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u6211\u4e5f\u5e94\u8be5\u628a\u725f\u5b97\u6d9b\u7684\u6d88\u606f\u544a\u8bc9\u4f60\u4e86\u3002\u201d \u3000\u3000\u6797\u65e0\u53cc\u542c\u8bf4\u725f\u5b97\u6d9b\u5df2\u7ecf\u8fdb\u4eac\uff0c\u5fc3\u91cc\u53c8\u60ca\u53c8\u559c\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u6211\u7684\u8868\u54e5\u679c\u7136\u662f\u4e0d\u5728\u4ed6\u4eec\u4e03 \u4eba\u4e4b\u5185\u3002\u53ea\u662f\u4ed6\u82e5\u7136\u4e0d\u80af\u8ddf\u5b97\u795e\u9f99\u8d70\u4e00\u6761\u8def\uff0c\u6050\u6015\u5b97\u795e\u9f99\u4e0d\u4f1a\u8f7b\u6613\u653e\u8fc7\u4ed6\u4e86\u3002\u91d1\u5c11\u4fa0\uff0c\u6211\u4e5f \u8be5\u8d70\u5566\uff0c\u54b1\u4eec\u518d\u89c1\u4e86\u3002\u201d \u3000\u3000\u6797\u65e0\u53cc\u8d70\u540e\uff0c\u53f2\u7ea2\u82f1\u7b11\u9053\uff1a\u201c\u770b\u6765\u8fd9\u5c0f\u59d1\u5a18\u662f\u7231\u4e0a\u4e86\u5979\u7684\u8868\u54e5\u4e86\u3002\u8bf4\u8001\u5b9e\u8bdd\uff0c\u6211\u5bf9\u725f\u5b97 \u6d9b\u6b8a\u65e0\u597d\u611f\uff0c\u603b\u89c9\u5f97\u8fd9\u4e2a\u4eba\u4f3c\u4e4e\u6709\u70b9\u865a\u4f2a\u3002\u4f46\u5bf9\u8fd9\u5c0f\u59d1\u5a18\u5374\u662f\u5341\u5206\u6b22\u559c\u3002\u4e3a\u4e86\u8fd9\u5c0f\u59d1\u5a18\u7684\u7f18 \u6545\uff0c\u6211\u4e5f\u4f46\u613f\u4ed6\u4eec\u6709\u60c5\u4eba\u80fd\u6210\u7737\u5c5e\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u53ea\u89c1\u6e05\u865a\u89c2\u7684\u9053\u58eb\u5df2\u7ecf\u51fa\u6765\u627e\u5bfb\u4ed6\u4eec\uff0c\u8fd9\u9053\u58eb\u53d1\u73b0\u4ed6\u4eec\u201c\u5931\u8e2a\u201d\uff0c\u53ea\u9053\u662f\u51fa \u4e86\u4ec0\u4e48\u610f\u5916\u3002 \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u6628\u665a\u6708\u8272\u5f88\u597d\uff0c\u6211\u4fa7\u4e0d\u60f3\u8f9c\u8d1f\u540d\u5c71\uff0c\u6545\u6b64\u7279\u5730\u51fa\u6765\u89c2\u8d4f\u534e\u5c71\u591c\u666f\uff0c\u5012\u53eb \u9053\u957f\u4e3a\u6211\u4eec\u62c5\u60ca\u4e86\u3002\u201d\u4ed6\u662f\u6050\u6015\u8bf4\u51fa\u6628\u665a\u4e4b\u4e8b\uff0c\u6e05\u865a\u89c2\u7684\u9053\u58eb\u66f4\u8981\u5403\u60ca\uff0c\u662f\u4ee5\u8f7b\u63cf\u6de1\u5199\u7684\u4e00 \u7b14\u5e26\u8fc7\u3002 \u3000\u3000\u9053\u58eb\u8bf4\u9053\uff1a\u201c\u91d1\u5c11\u4fa0\u96be\u5f97\u5230\u6b64\uff0c\u4f55\u4e0d\u591a\u4f4f\u4e24\u5929\uff0c\u8ba9\u5c0f\u9053\u7565\u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff0c\u966a\u5c11\u4fa0\u904d\u6e38\u534e\u5c71 \u540d\u80dc\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u6709\u70b9\u4e8b\u60c5\u8981\u8d76\u5f80\u897f\u660c\uff0c\u6050\u6015\u4e0d\u80fd\u803d\u6401\u4e86\uff0c\u5f85\u6211\u4eec\u56de\u6765\u4e4b\u65f6\uff0c\u4e00\u5b9a\u518d \u6765\u6253\u6270\u9053\u957f\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b49\u4eba\u56de\u89c2\u53d6\u4e86\u884c\u674e\uff0c\u4fbf\u5373\u4e0b\u5c71\u3002\u6b64\u65f6\u6b63\u662f\u671d\u9633\u521d\u51fa\u7684\u65f6\u5206\uff0c\u4ece\u5c71\u4e0a\u4e0b\u6765\uff0c\u53c8\u662f\u4e00 \u756a\u5947\u666f\u3002\u653e\u76ee\u8fdc\u773a\uff0c\u53ea\u89c1\u897f\u5357\u65b9\u7684\u79e6\u5cad\uff0c\u7fa4\u5cf0\u5217\u969c\uff1b\u4e1c\u5317\u65b9\u7684\u9ec4\u6cb3\u4fe8\u5982\u5929\u9645\u800c\u6765\uff0c\u9752\u6c34\u4e00\u7ebf \uff0c\u6a2a\u754f\u79e6\u5ddd\u5e73\u539f\u3002\u811a\u4e0b\u767d\u4e91\u7f2d\u7ed5\uff0c\u5343\u4ede\u4e07\u524a\u7684\u7fa4\u5c71\u6d6e\u6c89\u9690\u73b0\u5728\u7f25\u7f08\u7684\u4e91\u6c14\u4e4b\u4e2d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u53f9\u9053\uff1a\u201c\u5982\u4eca\u6211\u624d\u77e5\u9053\u4ec0\u4e48\u53eb\u505a\u58ee\u7f8e\u3002\u82e5\u628a\u6628\u665a\u6708\u591c\u4e0b\u7684\u534e\u5c71\u6bd4\u4f5c\u891b\u7740\u8f7b\u7eb1\u7684\u7f8e \u4eba\uff0c\u4eca\u65e5\u9633\u5149\u4e0b\u7684\u534e\u5c71\uff0c\u90a3\u5c31\u662f\u62ab\u895f\u8fce\u98ce\u7684\u8c6a\u58eb\u4e86\uff01\u201d \u3000\u3000\u53f2\u7ea2\u82f1\u7b11\u9053\uff1a\u201c\u4f60\u522b\u8981\u604b\u604b\u4e0d\u820d\u4e86\uff0c\u7afa\u8001\u524d\u8f88\u4ed6\u4eec\u5728\u5927\u51c9\u5c71\u6b63\u7b49\u5f97\u5fc3\u7126\u5462\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u54b1\u4eec\u8d76\u5feb\u4e00\u4e9b\u56de\u53bb\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u53ef\u4ee5\u78b0\u4e0a\u5c09\u8fdf\u70af\u592b\u59bb\u3002\u201d \u3000\u3000\u4e00\u8def\u65e0\u4e8b\uff0c\u56de\u5230\u4e86\u5927\u51c9\u5c71\u7684\u4e49\u519b\u57fa\u5730\uff0c\u679c\u7136\u89c1\u7740\u4e86\u5c09\u8fdf\u70af\u592b\u59bb\u3002\u4ed6\u4eec\u662f\u548c\u6797\u9053\u8f69\u3001\u674e\u5149 \u590f\u7b49\u4eba\u4e00\u540c\u51fa\u6765\u8fce\u63a5\u7684\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u89c1\u4e86\u91d1\u9010\u6d41\uff0c\u5341\u5206\u9ad8\u5174\uff0c\u63e1\u7740\u4ed6\u7684\u624b\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4eac\u534e\u4e00\u522b\uff0c\u4e0d\u8fc7\u4e24\u5e74\uff0c\u4f60\u5982\u4eca \u5df2\u662f\u540d\u6ee1\u6c5f\u6e56\uff0c\u5f53\u771f\u662f\u53ef\u559c\u53ef\u8d3a\u3002\u8001\u5f1f\uff0c\u597d\u5728\u4f60\u4eca\u65e5\u6765\u5230\uff0c\u82e5\u662f\u8fdf\u4e00\u5929\u7684\u8bdd\uff0c\u5c31\u89c1\u4e0d\u7740\u6211\u4eec \u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u592b\u59bb\u5927\u663e\u795e\u5a01\uff0c\u51fb\u8d25\u4e86\u6276\u6851\u4e03\u5b50\uff0c\u5b9d\u5200\u672a\u8001\uff0c\u96c4\u98ce\u72b9\u5728\uff0c\u8fd9\u624d\u662f\u53eb \u6211\u4eec\u505a\u5c0f\u8f88\u7684\u4f69\u670d\u5462\u3002\u201d \u3000\u3000\u5c09\u8fdf\u70af\u8be7\u9053\uff1a\u201c\u4f60\u7684\u6d88\u606f\u5012\u662f\u5f88\u7075\u901a\u5440\uff0c\u8c01\u544a\u8bc9\u4f60\u7684\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u5c31\u662f\u4f60\u7684\u8001\u670b\u53cb\u98de\u9c7c\u5c9b\u4e3b\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u7948\u5723\u56e0\u9053\uff1a\u201c\u54e6\uff0c\u6211\u4eec\u5728\u534e\u5c71\u78b0\u4e0a\u6797\u65e0\u53cc\u4e86\u3002\u5979\u53ef\u66fe\u627e\u7740\u4e86\u5979\u7684\u8868\u54e5\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u725f\u5b97\u6d9b\u5df2\u7ecf\u8fdb\u4eac\u53bb\u4e86\u3002\u6211\u5728\u5f82\u5f95\u5c71\u4e5f\u66fe\u548c\u4ed6\u4ea4\u8fc7\u624b\u5462\u3002\u201d\u5f53\u4e0b\u5c06\u524d\u540e\u78b0\u89c1 \u725f\u5b97\u6d9b\u548c\u6797\u65e0\u53cc\u7684\u4e8b\u60c5\uff0c\u544a\u8bc9\u5c09\u8fdf\u70af\u592b\u59bb\u3002 \u3000\u3000\u7948\u5723\u56e0\u542c\u5f97\u6797\u65e0\u53cc\u5728\u534e\u5c71\u7edd\u9876\u6bd4\u5251\u80dc\u4e86\u6851\u9752\uff0c\u751a\u4e3a\u6b22\u559c\uff0c\u7b11\u9053\uff1a\u201c\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\uff0c\u4e16 \u4e0a\u65b0\u4eba\u6362\u65e7\u4eba\u3002\u8fd9\u8bdd\u771f\u662f\u8bf4\u5f97\u4e00\u70b9\u4e0d\u9519\uff0c\u6c5f\u6e56\u4e0a\u6dfb\u4e86\u4f60\u4eec\u8fd9\u4e00\u73ed\u5c11\u5e74\u8c6a\u6770\uff0c\u4eca\u540e\u53c8\u6709\u4e00\u756a\u70ed \u95f9\u4e86\u3002\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u4f60\u7684\u5e08\u4f84\u6797\u9053\u8f69\u5c31\u8981\u505a\u5929\u7406\u4f1a\u7684\u603b\u8235\u4e3b\u4e86\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u66fe\u542c\u5f97\u6797\u59d1\u5a18\u8bf4\u8fc7\u3002\u201d\u5f53\u4e0b\u5411\u6797\u9053\u8f69\u9053\u8d3a\u3002\u6797\u9053\u8f69\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u6211 \u54ea\u6709\u80c6\u91cf\u505a\u5929\u7406\u4f1a\u7684\u603b\u8235\u4e3b\uff0c\u4f46\u5f20\u53d4\u53d4\u7684\u4e34\u7ec8\u9057\u547d\u53c8\u4e0d\u80fd\u8fdd\u80cc\uff0c\u6211\u73b0\u5728\u6b63\u5728\u4e3a\u96be\u5462\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u7b11\u9053\uff1a\u201c\u8def\u662f\u4eba\u8d70\u51fa\u672a\u7684\uff0c\u6454\u4e86\u8de4\u722c\u8d77\u6765\u518d\u8d70\u597d\u4e86\u3002\u6709\u4ec0\u4e48\u53ef\u4ee5\u5bb3\u6015\u7684\uff1f\u91cd\u62c5\u5b50 \u5018\u82e5\u5927\u5bb6\u90fd\u4e0d\u80af\u6311\uff0c\u90a3\u5c31\u4ec0\u4e48\u4e8b\u4e5f\u505a\u4e0d\u6210\u4e86\u3002\u4f60\u7239\u7239\u662f\u5929\u7406\u4f1a\u7684\u5f00\u5c71\u5802\u8235\u4e3b\uff0c\u7559\u4e0b\u7684\u62c5\u5b50\uff0c \u4f60\u4e0d\u6311\u8c01\u6311\uff1f\u201d \u3000\u3000\u5c09\u8fdf\u70af\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u8bdd\u8bf4\u5f97\u597d\uff01\u201d \u3000\u3000\u7948\u5723\u56e0\u4e5f\u7b11\u9053\uff1a\u201c\u4f60\u548c\u5149\u590f\u5df2\u7ecf\u662f\u6210\u5bb6\u7acb\u5ba4\u7684\u5927\u4eba\u4e86\uff0c\u4e5f\u8be5\u6311\u6311\u91cd\u62c5\u5b50\u5566\u3002\u8bf4\u4e2a\u7b11\u8bdd\uff0c \u9010\u6d41\uff0c\u4f60\u505a\u5e08\u53d4\u7684\u5c1a\u672a\u6210\u4eb2\uff0c\u4e0d\u89c9\u5f97\u60ed\u6127\u5417\uff1f\u4e5f\u8be5\u5feb\u70b9\u513f\u624d\u597d\u54e9\uff01\u201d \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u6211\u4eec\u51c6\u5907\u660e\u5929\u5c31\u966a\u4ed6\u4eec\u4e24\u5bf9\u5c0f\u592b\u59bb\u56de\u53bb\uff0c\u5e78\u4e8f\u4f60\u4eca\u5929\u8d76\u5230\uff0c\u6211\u4eec\u8fd8\u53ef\u4ee5\u6709 \u4e00\u5929\u76f8\u805a\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u8fdb\u4e86\u5185\u5802\uff0c\u7afa\u5c1a\u7236\u6b63\u5728\u90a3\u91cc\u7b49\u5019\u4ed6\u4eec\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9996\u5148\u62a5\u544a\u4e86\u5389\u5357\u661f\u548c\u516c\u5b59\u71d5\u4e1a\u5df2\u8131\u9669\u7684\u4e8b\u60c5\uff0c\u7afa\u5c1a\u7236\u542c\u5f97\u4ed6\u4eec\u5b89\u7136\u65e0\u4e8b\uff0c\u800c\u4e14\u5929 \u9b54\u6559\u4ea6\u5df2\u548c\u7ea2\u7f28\u4f1a\u5408\u5e76\uff0c\u7ed9\u6297\u6e05\u7684\u4e49\u519b\u53c8\u6dfb\u4e86\u4e00\u80a1\u529b\u91cf\uff0c\u5f53\u7136\u662f\u5927\u4e3a\u9ad8\u5174\u3002 \u3000\u3000\u7afa\u5c1a\u7236\u7b11\u9053\uff1a\u201c\u8f9b\u82e6\u4f60\u4e86\uff0c\u8fd9\u91cc\u51e0\u4e2a\u6708\u6765\u90fd\u662f\u98ce\u5e73\u6d6a\u9759\uff0c\u5176\u5b9e\u4f60\u4e5f\u4e0d\u7528\u8fd9\u6837\u7740\u6025\u56de\u6765\u7684 \uff0c\u4ee4\u5c0a\u521a\u4ece\u6d77\u5916\u56de\u6765\uff0c\u53c8\u8981\u4f60\u4eec\u7236\u5b50\u5206\u624b\uff0c\u6211\u5012\u662f\u6709\u70b9\u8fc7\u610f\u4e0d\u53bb\u5462\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5c01\u5b50\u8d85\u6709\u4e2a\u7d27\u6025\u7684\u6d88\u606f\u6258\u6211\u5e26\u56de\u6765\u7ed9\u4f60\u3002\u201d \u3000\u3000\u7afa\u5c1a\u7236\u8be7\u9053\uff1a\u201c\u5c01\u5b50\u8d85\uff1f\u4ed6\u4e0d\u662f\u66fe\u7ecf\u505a\u8fc7\u5927\u5185\u4f8d\u536b\u7684\u5417\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u4f46\u4ed6\u4e5f\u662f\u79e6\u5144\u7684\u6cf0\u5c71\uff0c\u73b0\u5728\u5df2\u7ecf\u80cc\u53db\u4e86\u6e05\u5ef7\uff0c\u662f\u54b1\u4eec\u7684\u81ea\u5df1\u4eba\u4e86\u3002 \u201d\u5f53\u4e0b\u5c06\u5c01\u5b50\u8d85\u544a\u8bc9\u4ed6\u7684\u90a3\u4e2a\u6d88\u606f\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u7afa\u5c1a\u7236\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u8428\u798f\u9f0e\u7ade\u6709\u8fd9\u6837\u7684\u9634\u8c0b\uff0c\u60f3\u5229\u7528\u9752\u6d77\u4e94\u4e2a\u76df\u65d7\u7684\u738b\u516c\u6765 \u63a3\u8098\u54b1\u4eec\uff0c\u8fd9\u5012\u662f\u4e0d\u53ef\u4e0d\u9632\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8428\u798f\u9f0e\u6d3e\u53bb\u8054\u7edc\u4ed6\u4eec\u7684\u4eba\u65e9\u5df2\u51fa\u4eac\uff0c\u54b1\u4eec\u987b\u5f97\u8d76\u5feb\u4e5f\u6d3e\u4eba\u53bb\u624d\u597d\u3002\u7afa\u4f2f\u4f2f \u82e5\u662f\u4e0d\u6015\u5c0f\u4f84\u8bef\u4f1a\uff0c\u6211\u60f3\u8bf7\u547d\u524d\u5f80\u3002\u201d \u3000\u3000\u7afa\u5c1a\u7236\u6c89\u541f\u534a\u664c\uff0c\u8bf4\u9053\uff1a\u201c\u9752\u6d77\u7684\u767d\u6559\u5587\u561b\uff0c\u4e0e\u4ee4\u5c0a\u9887\u6709\u4ea4\uff0c\u4f60\u8f7b\u529f\u53c8\u597d\uff0c\u7531\u4f60\u53bb\u7684\u786e \u9002\u5b9c\uff0c\u4e0d\u8fc7\u6700\u597d\u591a\u4e00\u4e2a\u4eba\u966a\u4f60\u3002\u201d\u8bf4\u81f3\u6b64\u5904\uff0c\u770b\u4e86\u53f2\u7ea2\u82f1\u4e00\u773c\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u672c\u6765\u6211\u5e94\u8be5\u8ba9 \u53f2\u59d1\u5a18\u548c\u4f60\u4e00\u540c\u53bb\u7684\uff0c\u4e0d\u8fc7\u90a3\u4e9b\u738b\u516c\u6709\u4e2a\u6210\u89c1\uff0c\u770b\u4e0d\u8d77\u4e09\u622a\u68b3\u5934\uff0c\u4e24\u622a\u7a7f\u8863\u7684\u5973\u5b50\u3002\u201d\u7948\u5723 \u56e0\u6124\u7136\u8bf4\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff01\u201d \u3000\u3000\u7afa\u5c1a\u7236\u9053\uff1a\u201c\u662f\u6ca1\u6709\u9053\u7406\u3002\u4e0d\u8fc7\u4ed6\u4eec\u7684\u4e60\u4fd7\u5982\u6b64\uff0c\u54b1\u4eec\u6709\u6c42\u4e8e\u4eba\uff0c\u4e5f\u53ea\u80fd\u8fc1\u5c31\u4ed6\u4eec\u4e86\u3002 \u201d\u6362\u53e5\u8bdd\u8bf4\uff0c\u4e49\u519b\u6d3e\u9053\u7684\u4f7f\u8005\uff0c\u5fc5\u987b\u662f\u7537\u5b50\uff0c\u4e0d\u80fd\u662f\u5973\u4eba\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u5ffd\u9053\uff1a\u201c\u9010\u6d41\uff0c\u6211\u548c\u4f60\u53bb\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8981\u966a\u9053\u8f69\u3001\u5149\u590f\u4ed6\u4eec\u56de\u53bb\u4e48\uff1f\u201d \u3000\u3000\u5c09\u8fdf\u70af\u7b11\u9053\uff1a\u201c\u6211\u4e00\u6765\u7231\u8d81\u70ed\u95f9\uff0c\u4e8c\u6765\u6ca1\u6709\u5230\u8fc7\u9752\u6d77\uff0c\u4e5f\u6b63\u60f3\u8d81\u6b64\u673a\u4f1a\u4e00\u6e38\uff0c\u6709\u4f60\u7684\u5ac2 \u5b50\u966a\u4ed6\u4eec\u4e24\u5bf9\u592b\u59bb\u56de\u53bb\uff0c\u6cbf\u9014\u53c8\u6709\u5929\u7406\u4f1a\u7684\u4eba\u63a5\u5e94\uff0c\u6599\u60f3\u4e0d\u4f1a\u51fa\u4e8b\u3002\u201d \u3000\u3000\u7948\u5723\u56e0\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u95f2\u4e0d\u4f4f\u7684\uff0c\u597d\uff0c\u4f60\u5c3d\u7ba1\u653e\u5fc3\u5427\u3002\u4ed6\u4eec\u4e24\u5bf9\u5c0f\u592b\u59bb\u7684\u672c\u9886\u65e9\u5df2 \u662f\u4eca\u975e\u6614\u6bd4\uff0c\u5c31\u662f\u78b0\u4e0a\u4e86\u6276\u6851\u4e03\u5b50\uff0c\u4e5f\u8db3\u53ef\u4ee5\u5bf9\u4ed8\u5f97\u4e86\u3002\u201d \u3000\u3000\u7afa\u5c1a\u7236\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8c01\u6562\u62db\u60f9\u5343\u624b\u89c2\u6614\uff1f\u9752\u6d77\u8fd9\u8fb9\uff0c\u6709\u5c09\u8fdf\u70af\u548c\u9010\u6d41\u540c\u884c\uff0c\u4e5f\u6b63\u662f\u6700\u597d \u4e0d\u8fc7\u3002\u53ea\u662f\u5982\u6b64\u4e00\u6765\uff0c\u5374\u662f\u8981\u62c6\u6563\u4f60\u4eec\u592b\u59bb\u4e86\u3002\u201d \u3000\u3000\u8ba1\u8bae\u5df2\u5b9a\uff0c\u7afa\u5c1a\u7236\u5f53\u4e0b\u4fbf\u5199\u4e86\u4e00\u5c01\u4e66\u4fe1\uff0c\u4ea4\u7ed9\u5c09\u8fdf\u70af\u3002\u8bf4\u9053\uff1a\u201c\u9752\u6d77\u4e94\u4e2a\u4e00\u76df\u65d7\uff0c\u4ee5\u4f0a \u514b\u662d\u76df\u4e3a\u9996\uff0c\u4f60\u4eec\u4ee5\u4e49\u519b\u4f7f\u8005\u7684\u8eab\u4efd\uff0c\u53bb\u89c1\u4f0a\u514b\u662d\u76df\u7684\u5927\u738b\uff0c\u4e0d\u5fc5\u7406\u4f1a\u4ed6\u662f\u5426\u5df2\u53d7\u6e05\u5ef7\u7b3c\u7edc \uff0c\u5148\u548c\u4ed6\u8bf4\u6e05\u695a\u5f7c\u6b64\u7684\u5229\u5bb3\u5173\u7cfb\uff0c\u770b\u4ed6\u5982\u4f55\u5e94\u5bf9\uff1f\u53ea\u987b\u4ed6\u80fd\u4e3a\u54b1\u4eec\u6240\u7528\uff0c\u5176\u4ed6\u56db\u4e2a\u76df\u65d7\uff0c\u81ea \u5fc5\u9a6c\u9996\u662f\u77bb\u3002\u201d \u3000\u3000\u5c09\u8fdf\u70af\u5374\u628a\u8fd9\u7ea3\u4fe1\u4ea4\u7ed9\u4e86\u91d1\u9010\u6d41\uff0c\u7b11\u9053\uff1a\u201c\u6211\u6700\u6015\u548c\u738b\u516c\u6253\u4ea4\u9053\uff0c\u8fd9\u4f7f\u8001\u4e00\u4efd\uff0c\u8fd8\u662f\u8bf7 \u9010\u6d41\u8001\u5f1f\u58ee\u80c6\u5427\u3002\u6211\u7b97\u4f5c\u4ed6\u7684\u968f\u4ece\u597d\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u53ef\u4ee5\uff1f\u201d\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u53c8\u4e0d\u662f\u4e89\u7740\u4f5c\u5b98\uff0c\u6709\u4ec0\u4e48\u4e0d\u53ef\u4ee5\uff1f\u201d\u91d1\u9010\u6d41 \u63a8\u8f9e\u4e0d\u6389\uff0c\u53ea\u597d\u628a\u4fe1\u6536\u4e0b\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8981\u662f\u571f\u738b\u4e0d\u80af\u548c\u54b1\u4eec\u8054\u76df\uff0c\u90a3\u53c8\u5982\u4f55\uff1f\u201d \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u8fd9\u4e9b\u571f\u738b\u591a\u534a\u662f\u8d2a\u8d22\u7684\uff0c\u4f3c\u4e4e\u5e94\u8be5\u9001\u4ed6\u4eec\u4e00\u70b9\u793c\u7269\u3002\u201d \u3000\u3000\u7afa\u5c1a\u7236\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5df2\u51c6\u5907\u597d\u4e86\u3002\u201d\u5f53\u4e0b\u62ff\u51fa\u4e86\u4e00\u4e2a\u78a7\u7389\u897f\u74dc\uff0c\u4e00\u652f\u5343\u5e74\u4eba\u53c2\uff0c\u8bf4\u9053\uff1a \u201c\u8fd9\u662f\u4f60\u4eec\u90a3\u5e74\u4ece\u8428\u798f\u9f0e\u90a3\u513f\u62a2\u6765\u7684\u5bff\u793c\uff0c\u5982\u4eca\u6b63\u597d\u501f\u82b1\u732e\u4f5b\u3002\u4f60\u4eec\u5230\u4e86\u90a3\u513f\uff0c\u770b\u571f\u738b\u6001\u5ea6 \u5982\u4f55\uff0c\u518d\u89c1\u673a\u800c\u4e3a\u5427\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u5c09\u8fdf\u70af\u592b\u59bb\u4fbf\u5404\u81ea\u5206\u9053\u626c\u9573\uff0c\u7948\u5723\u56e0\u4e0e\u6797\u3001\u674e\u4e24\u5bf9\u5c0f\u592b\u59bb\u56de\u4fdd\u5b9a\u3002 \u3000\u3000\u7afa\u5c1a\u7236\u7ed9\u5c09\u8fdf\u70af\u3001\u91d1\u9010\u6d41\u4e8c\u4eba\u9001\u884c\uff0c\u4e34\u884c\u524d\u7afa\u5c1a\u7236\u60f3\u8d77\u4e00\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u9010\u6d41\uff0c\u6211\u5fd8\u8bb0\u544a \u8bc9\u4f60\uff0c\u4f0a\u514b\u662d\u76df\u662f\u4fe1\u767d\u6559\u7684\uff0c\u90a3\u513f\u7684\u5927\u5587\u561b\u540d\u53eb\u5b97\u8fbe\u5b8c\u771f\uff0c\u662f\u767d\u6559\u6cd5\u738b\u7684\u5927\u5f1f\u5b50\uff0c\u767d\u6559\u6cd5\u738b \u548c\u4ee4\u5c0a\u662f\u8001\u670b\u53cb\uff0c\u8fd9\u5b97\u8fbe\u5b8c\u771f\u4e5f\u66fe\u89c1\u8fc7\u4ee4\u5c0a\u3002\u51ed\u7740\u8fd9\u70b9\u6e0a\u6e90\uff0c\u4f60\u5230\u4e86\u4f0a\u514b\u662d\u76df\uff0c\u4e0d\u59a8\u53bb\u6c42\u89c1 \u4ed6\uff0c\u8bf4\u4e0d\u5b9a\u53ef\u80fd\u5f97\u5230\u4ed6\u7684\u5e2e\u52a9\u3002\u201d \u3000\u3000\u4ece\u5927\u51c9\u5c71\u7a7f\u8fc7\u539f\u59cb\u68ee\u6797\u5230\u9752\u6d77\u8349\u539f\uff0c\u6cbf\u9014\u5386\u5c3d\u8270\u82e6\uff0c\u597d\u5728\u4e00\u8def\u65e0\u4e8b\uff0c\u8fd9\u4e00\u5929\u7ec8\u4e8e\u5230\u4e86\u4f0a \u514b\u662d\u76df\u3002\u6b63\u662f\uff1a \u3000\u3000\u6b32\u5316\u5e72\u6208\u4e3a\u7389\u5e1b\uff0c\u767b\u5c71\u6d89\u6c34\u4e0d\u8f9e\u52b3\u3002 \u3000\u3000\u6b32\u77e5\u540e\u4e8b\u5982\u4f55\uff1f\u8bf7\u542c\u4e0b\u56de\u5206\u89e3\u3002 \u3000\u3000------------- \u3000\u3000\u9ec4\u91d1\u4e66\u5c4b \u626b\u6821  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b\u2014\u2014\u7b2c\u4e94\u5341\u4e8c\u56de\u3000\u4f46\u613f\u6709\u60c5\u6210\u7737\u5c5e\u3000\u5374\u55df\u65e0\u5904\u89c5\u8427\u90ce \u6881\u7fbd\u751f\u300a\u4fa0\u9aa8\u4e39\u5fc3\u300b \u7b2c\u4e94\u5341\u4e8c\u56de\u3000\u4f46\u613f\u6709\u60c5\u6210\u7737\u5c5e\u3000\u5374\u55df\u65e0\u5904\u89c5\u8427\u90ce \u3000\u3000\u4f0a\u514b\u662d\u76df\u7684\u571f\u738b\u62db\u5f85\u4ed6\u4eec\u5728\u5ba2\u6808\u4f4f\u4e0b\uff0c\u5f53\u665a\u5c31\u63a5\u89c1\u4ed6\u4eec\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5448\u4e0a\u7afa\u5c1a\u7236\u7684\u4e66\u4fe1\u548c\u793c\u7269\uff0c\u571f\u738b\u89c1\u4e86\u8fd9\u4e24\u4ef6\u4ef7\u503c\u8fde\u57ce\u7684\u793c\u7269\uff0c\u679c\u7136\u4e50\u5f97\u53e3\u90fd\u5408\u4e0d \u62e2\u6765\u3002\u53ef\u662f\u770b\u4e86\u7afa\u5c1a\u7236\u7684\u4e66\u4fe1\u4e4b\u540e\uff0c\u5374\u53c8\u6c89\u541f\u4e0d\u8bed\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6211\u4eec\u6c49\u4eba\u6709\u53e5\u6210\u8bed\uff0c\u53eb\u505a\u2018\u5507\u4ea1\u9f7f\u5bd2\u2019\u3002\u6211\u4eec\u8fd9\u652f\u4e49\u519b\u5728\u5927\u51c9\u5c71\u7b49\u4e8e\u662f\u4f5c \u4f60\u4eec\u8d42\u5c4f\u969c\uff0c\u5982\u679c\u6211\u4eec\u5931\u8d25\u4e86\uff0c\u6e05\u5175\u5c31\u53ef\u4ee5\u957f\u9a71\u76f4\u5165\uff0c\u6765\u5230\u4f60\u4eec\u8fd9\u513f\u4e86\u3002\u5230\u4e86\u90a3\u65f6\uff0c\u6ee1\u6e05\u7684 \u7687\u5e1d\u4e0d\u4f1a\u5bb9\u8bb8\u4f60\u81ea\u7acb\u4e3a\u738b\u7684\u3002\u81f3\u5c11\u4e5f\u8981\u7528\u4ed6\u4eec\u7684\u6240\u8c13\u2018\u738b\u6cd5\u2019\u6765\u7ba1\u4f60\u4e86\u3002\u6240\u4ee5\u4e3a\u738b\u7237\u7740\u60f3\uff0c \u4e0a\u7b56\u662f\u548c\u4e49\u519b\u8054\u76df\u3002\u4e2d\u7b56\u662f\u4e24\u8fb9\u4e0d\u5e2e\uff0c\u548c\u4e49\u519b\u4e5f\u505a\u4e70\u5356\u3002\u4e0b\u7b56\u5219\u662f\u7ed9\u6e05\u5ef7\u5229\u7528\uff0c\u4e0e\u4e49\u519b\u4e3a\u96be \uff0c\u738b\u7237\u662f\u806a\u660e\u4eba\uff0c\u8fd9\u9053\u7406\u4e00\u5b9a\u662f\u65e9\u5df2\u660e\u7684\u4e86\u3002\u201d \u3000\u3000\u571f\u738b\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u5173\u7cfb\u4e94\u4e2a\u76df\u65d7\uff0c\u4e0d\u662f\u6211\u4e00\u4eba\u53ef\u4ee5\u51b3\u5b9a\u3002\u5bb9\u6211\u4ed4\u7ec6\u601d\u91cf\uff0c\u518d \u53ec\u96c6\u5404\u76df\u65d7\u7684\u738b\u516c\uff0c\u5927\u5bb6\u6765\u5546\u8bae\u5427\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u4e0d\u6562\u64cd\u4e4b\u8fc7\u6025\uff0c\u571f\u738b\u65e2\u7136\u4e0d\u80af\u8868\u660e\u6001\u5ea6\uff0c\u800c\u4e14\u5728\u8bf4\u4e86\u90a3\u756a\u8bf4\u8bdd\u4e4b\u540e\uff0c\u5c31\u987e\u5de6\u53f3\u800c \u8a00\u4ed6\uff0c\u91d1\u9010\u6d41\u548c\u5c09\u8fdf\u70af\u4e5f\u53ea\u597d\u544a\u9000\uff0c\u56de\u5ba2\u6808\u53bb\u7b49\u5019\u6d88\u606f\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u91d1\u9010\u6d41\u53bb\u62dc\u8bbf\u767d\u6559\u5927\u5587\u561b\u5b97\u8fbe\u5b8c\u771f\uff0c\u5b97\u8fbe\u5b8c\u771f\u77e5\u9053\u4ed6\u662f\u91d1\u4e16\u9057\u7684\u513f\u5b50\uff0c\u5bf9\u4ed6\u5012\u662f \u5341\u5206\u70ed\u60c5\uff0c\u4e00\u89c1\u5982\u6545\u3002 \u3000\u3000\u5b97\u8fbe\u5b8c\u771f\u8bf4\u9053\uff1a\u201c\u5f53\u5e74\u6211\u4eec\u6559\u4e2d\u5185\u4e71\uff0c\u5f97\u4ee4\u5c0a\u5e2e\u5fd9\u4e0d\u5c11\u3002\u4ee4\u5c0a\u4e5f\u662f\u6211\u6700\u4f69\u670d\u7684\u4eba\uff0c\u6211\u4e00 \u76f4\u6302\u5ff5\u7740\u4ed6\u7684\uff0c\u96be\u5f97\u4f60\u4eca\u65e5\u5230\u6765\uff0c\u89c1\u4e86\u4f60\u5c31\u4f3c\u89c1\u5230\u4eca\u5c0a\u4e00\u6837\u3002\u4f60\u6709\u4ec0\u4e48\u9700\u8981\u6211\u5e2e\u5fd9\u7684\uff0c\u6211\u4e00 \u5b9a\u5c3d\u529b\u4e3a\u4f60\u505a\u5230\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5c0f\u4f84\u6b63\u662f\u6709\u4e00\u4ef6\u4e3a\u96be\u4e8b\u3002\u201d\u5f53\u4e0b\u5c06\u4e49\u519b\u7684\u613f\u671b\u4ee5\u53ca\u81ea\u5df1\u548c\u571f\u738b\u4ea4\u6d89\u7684\u7ecf\u8fc7 \u544a\u8bc9\u5b97\u8fbe\u5b8c\u771f\u3002 \u3000\u3000\u5b97\u8fbe\u5b8c\u771f\u6c89\u541f\u534a\u54cd\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u6211\u4f1a\u627e\u673a\u4f1a\u5411\u738b\u7237\u8fdb\u8a00\u7684\u3002\u4e0d\u8fc7\u5176\u4e2d\u6709\u4e2a\u969c\u788d\uff0c\u4f60 \u60f3\u77e5\u9053\u738b\u7237\u4e3a\u4f55\u4e0d\u80af\u723d\u5feb\u7b54\u5e94\u4f60\u4eec\u7684\u539f\u56e0\u5417\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u6b63\u60f3\u8bf7\u5927\u5e08\u6307\u6559\u3002\u201d \u3000\u3000\u5b97\u8fbe\u5b8c\u771f\u8bf4\u9053\uff1a\u201c\u56e0\u4e3a\u6e05\u5ef7\u7684\u4f7f\u8005\u6bd4\u4f60\u4eec\u65e9\u6765\u4e86\u4e09\u5929\uff0c\u73b0\u5728\u6b63\u4f4f\u5728\u738b\u7237\u5bab\u4e2d\uff0c\u4f5c\u4ed6\u7684\u8d35 \u5bbe\u5462\uff0c\u4e0d\u8fc7\u738b\u7237\u4e0d\u8ba9\u4f60\u4eec\u77e5\u9053\u7f62\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u88ab\u62db\u5f85\u4f4f\u5728\u5ba2\u6808\uff0c\u6e05\u5ef7\u7684\u4f7f\u8005\u5219\u4f4f\u5728\u571f\u738b\u5bab\u4e2d\uff0c\u663e\u7136\u571f\u738b\u7684\u6001\u5ea6\u662f\u66f4\u4e3a\u4eb2\u8fd1\u6e05\u5ef7 \u7684\u4e86\u3002 \u3000\u3000\u5b97\u8fbe\u5b8c\u771f\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u738b\u7237\u5e76\u4e0d\u662f\u4e2a\u773c\u5149\u8fdc\u5927\u7684\u4eba\uff0c\u542c\u8bf4\u6e05\u5ef7\u7684\u4f7f\u8005\u8bb8\u4ed6\u6b63\u5f0f\u7b56\u7acb\u4e3a\u738b \uff0c\u53c8\u7b54\u5e94\u4e86\u7ed9\u4ed6\u8bb8\u591a\u5229\u76ca\uff0c\u81f3\u4e8e\u91d1\u94f6\u73e0\u5b9d\u4e4b\u7c7b\u7684\u793c\u7269\uff0c\u90a3\u662f\u66f4\u65e0\u987b\u8bf4\u4e86\u3002\u6211\u5f53\u7136\u662f\u4f1a\u5e2e\u4f60\u529d \u738b\u7237\u7684\uff0c\u4ed6\u80af\u4e0d\u80af\u542c\uff0c\u90a3\u5c31\u96be\u8bf4\u5f97\u5f88\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u5931\u6240\u671b\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u4f46\u6c42\u5927\u5e08\u4ee3\u4e3a\u8fdb\u8a00\uff0c\u6210\u4e0e\u4e0d\u6210\uff0c\u5c0f\u4f84\u90fd\u662f\u4e00\u6837\u611f\u6fc0\u3002\u201d \u3000\u3000\u5b97\u8fbe\u5b8c\u771f\u9053\uff1a\u201c\u6709\u4e00\u4ef6\u4e8b\uff0c\u6211\u8fd8\u8981\u63d0\u9192\u4f60\u4eec\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u591a\u8c22\u5927\u5e08\u5173\u7167\u3002\u201d \u3000\u3000\u5b97\u8fbe\u5b8c\u771f\u8bf4\u9053\uff1a\u201c\u6e05\u5ef7\u4f7f\u8005\u5fd7\u5728\u5fc5\u6210\uff0c\u4ed6\u4eec\u4f4f\u5728\u5bab\u4e2d\uff0c\u5bf9\u738b\u7237\u7684\u624b\u4e0b\u4eba\u7b49\u5ba0\u7edc\u5907\u81f3\uff0c\u4f60 \u987b\u5f97\u63d0\u9632\u4ed6\u4eec\u6697\u4e2d\u52a0\u5bb3\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u8c22\u8fc7\u4e86\u5b97\u8fbe\u5b8c\u771f\uff0c\u5367\u5230\u5ba2\u6808\uff0c\u5f53\u665a\u679c\u7136\u5c31\u6709\u4e00\u4e2a\u5bab\u4e2d\u7684\u5185\u4f8d\uff0c\u6367\u4e86\u4e00\u58f6\u9152\u56db\u76d2\u8089 \u812f\u997c\u98df\u524d\u6765\uff0c\u8bf4\u662f\u5949\u4e86\u738b\u7237\u4e4b\u547d\uff0c\u8d50\u4ed6\u4eec\u9152\u98df\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8d77\u4e86\u7591\u5fc3\uff0c\u6084\u6084\u5730\u628a\u4e00\u9897\u78a7\u7075\u4e39\u585e\u8fdb\u5c09\u8fdf\u70af\u624b\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u738b\u7237\u7f8e\u9152\uff0c\u53ea\u6015 \u6211\u4eec\u9152\u7687\u4e0d\u80dc\u3002\u201d\u5c09\u8fdf\u70af\u4e43\u662f\u6d77\u91cf\uff0c\u542c\u5f97\u91d1\u9010\u6d41\u8fd9\u4e48\u4e00\u8bf4\uff0c\u767b\u65f6\u4f1a\u610f\uff0c\u628a\u78a7\u7075\u4e39\u5077\u5077\u7eb3\u5165\u53e3 \u4e2d\u3002 \u3000\u3000\u90a3\u5185\u4f8d\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u4eec\u738b\u7237\u65e5\u5e38\u996e\u7528\u7684\u8461\u8404\u7f8e\u9152\uff0c\u9152\u5473\u9999\u9187\uff0c\u4f46\u591a\u996e\u4e5f\u4e0d\u4f1a\u9189\u3002\u738b\u7237 \u56e0\u4e3a\u6628\u665a\u6709\u4e8b\uff0c\u672a\u5f97\u4eb2\u81ea\u6b3e\u5f85\u8d35\u4f7f\u8005\uff0c\u662f\u4ee5\u53eb\u6211\u628a\u9152\u98df\u9001\u6765\uff0c\u7565\u8868\u656c\u610f\u3002\u8bf7\u8d35\u4f7f\u8005\u591a\u996e\u51e0\u676f \u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u597d\uff0c\u5c09\u8fdf\u5927\u54e5\uff0c\u591a\u8c22\u738b\u7237\u7684\u7f8e\u610f\uff0c\u90a3\u6211\u4eec\u5c31\u4e00\u540c\u996e\u5427\u3002\u201d \u3000\u3000\u4e24\u4eba\u5404\u81ea\u996e\u4e86\u4e09\u676f\uff0c\u90a3\u5185\u4f8d\u6697\u6697\u6b22\u559c\uff0c\u6b63\u60f3\u53eb\u9053\uff1a\u201c\u5012\u4e5f\uff0c\u5012\u4e5f\uff01\u201d\u5ffd\u542c\u5f97\u5c09\u8fdf\u70af\u54c8\u54c8 \u7b11\u9053\uff1a\u201c\u597d\u9152\uff0c\u597d\u9152\uff01\u201d\u7a81\u7136\u53cd\u624b\u4e00\u638c\uff0c\u201c\u4e53\u201d\u7684\u4e00\u58f0\uff0c\u628a\u4e00\u5f20\u64c5\u6728\u684c\u5b50\u5288\u4e0b\u4e00\u89d2\uff0c\u5413\u5f97\u90a3 \u5185\u4f8d\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5c09\u8fdf\u5927\u54e5\uff0c\u4f60\u559d\u9189\u5566\uff1f\u201d \u3000\u3000\u5c09\u8fdf\u70af\u624b\u821e\u8db3\u8e48\u5730\u53eb\u9053\uff1a\u201c\u6ca1\u9189\uff0c\u6ca1\u9189\u3002\u53ea\u662f\u8fd9\u9152\u5b9e\u5728\u592a\u597d\uff0c\u559d\u4e86\u4e4b\u540e\uff0c\u6211\u7684\u6c14\u529b\u500d\u589e \uff0c\u7981\u4e0d\u4f4f\u8981\u8bd5\u4e00\u8bd5\u589e\u4e86\u591a\u5c11\u4e86\u3002\u201d\u968f\u5373\u53c8\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5982\u6b64\u7f8e\u9152\uff0c\u4e0d\u5b9c\u72ec\u4eab\uff0c\u8bf7\u8d35\u5b98\u4e5f\u6765\u559d \u4e2a\u4e09\u676f\u5427\uff01\u201d \u3000\u3000\u539f\u6765\u78a7\u7075\u4e39\u4e43\u662f\u7528\u5929\u5c71\u96ea\u83b2\u70ae\u5236\u7684\uff0c\u80fd\u89e3\u767e\u6bd2\uff0c\u91d1\u9010\u6d41\u548c\u5c09\u8fdf\u70af\u5185\u529f\u6df1\u582a\uff0c\u5176\u5b9e\u6ca1\u6709\u78a7 \u7075\u4e39\uff0c\u4e5f\u65e0\u5927\u788d\u3002\u6709\u4e86\u78a7\u7075\u4e39\uff0c\u5f53\u7136\u662f\u66f4\u4e0d\u4f1a\u4e2d\u6bd2\u4e86\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u58f6\u53ef\u4ee5\u70c2\u809a\u65ad\u80a0\u7684\u6bd2\u9152\uff0c\u8fd9\u5185\u4f8d\u5982\u4f55\u6562\u559d\uff1f\u8fde\u8fde\u6447\u624b\u3002\u5c09\u8fdf\u70af\u6012\u9053\uff1a\u201c\u4f60\u8bf4\u8fd9\u9152 \u662f\u4e0d\u4f1a\u9189\u7684\uff0c\u4e3a\u4f55\u4e0d\u559d\uff1f\u201d \u3000\u3000\u5c09\u8fdf\u70af\u4f6f\u4f5c\u559d\u9189\u4e86\u9152\u7684\u6837\u5b50\uff0c\u5f3a\u8feb\u8fd9\u5185\u4f8d\u559d\u9152\uff0c\u5185\u4f8d\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u559d\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u8fd9 \u53ae\u5140\u662f\u65e0\u793c\uff01\u201d\u60f3\u8981\u53d1\u5a01\uff0c\u4f46\u58f0\u97f3\u5df2\u662f\u98a4\u6296\u4e0d\u582a\u3002 \u3000\u3000\u5c09\u8fdf\u70af\u53cc\u773c\u4e00\u7ffb\uff0c\u731b\u5730\u559d\u9053\uff1a\u201c\u660e\u4eba\u773c\u524d\u4e0d\u8bf4\u5047\u8bdd\uff0c\u4f60\u8fd9\u58f6\u9152\u662f\u4e0d\u662f\u6bd2\u9152\uff01\u201d \u3000\u3000\u5185\u4f8d\u5fc3\u602f\u80c6\u5bd2\uff0c\u8bb7\u8bb7\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\u662f\u6bd2\u9152\u3002\u201d\u91d1\u9010\u6d41\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u662f\u6bd2\u9152\uff0c\u8d35 \u5b98\u559d\u4e5f\u65e0\u59a8\u3002\u4e0d\u8fc7\u4f60\u4e00\u5b9a\u4e0d\u80af\u559d\uff0c\u6211\u4e5f\u4e0d\u6562\u52c9\u5f3a\u2026\u2026\u201d\u5185\u4f8d\u5fd9\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\u3002\u559d\u9152\u4e5f\u4e0d\u80fd\u52c9 \u5f3a\u7684\u3002\u201d\u91d1\u9010\u6d41\u4e0d\u7406\u4ed6\u7684\u63d2\u5634\uff0c\u63a5\u4e0b\u53bb\u8bf4\u9053\uff1a\u201c\u4e0d\u8fc7\u4e3a\u4e86\u67e5\u660e\u771f\u76f8\uff0c\u6211\u4eec\u53ea\u597d\u5e26\u8fd9\u58f6\u9152\u53bb\u89c1 \u738b\u7237\u4e86\u3002\u4f60\u4e0d\u559d\uff0c\u6211\u4eec\u8bf7\u4ed6\u559d\uff0c\u4f60\u4e0d\u662f\u8bf4\u8fc7\u8fd9\u662f\u738b\u7237\u65e5\u5e38\u996e\u7528\u7684\u7f8e\u9152\u5417\uff1f\u201d \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4ed6\u4e0d\u8bf4\u5b9e\u8bdd\uff0c\u975e\u8981\u4ed6\u5148\u559d\u4e0d\u53ef\uff01\u201d\u5288\u80f8\u63ea\u4f4f\u8fd9\u4e2a\u5185\u4f8d\uff0c\u4f5c\u52bf\u5c31\u8981\u704c \u4ed6\u3002 \u3000\u3000\u91d1\u9010\u6d41\u548c\u5c09\u8fdf\u70af\u4e8c\u4eba\uff0c\u4e00\u4e2a\u505a\u597d\uff0c\u4e00\u4e2a\u505a\u574f\uff0c\u5413\u5f97\u8fd9\u5185\u4f8d\u9b42\u4e0d\u9644\u4f53\u3002\u8981\u77e5\u9053\u8fd9\u6bd2\u9152\u5e76\u4e0d \u662f\u571f\u738b\u53eb\u4ed6\u9001\u6765\uff0c\u5373\u7167\u91d1\u9010\u6d41\u7684\u529e\u6cd5\uff0c\u4ed6\u867d\u7136\u53ef\u4ee5\u6682\u65f6\u4e0d\u559d\u6bd2\u9152\uff0c\u4f46\u79d8\u5bc6\u63ed\u7a7f\uff0c\u7ec8\u4e5f\u96be\u9003\u4e00 \u6b7b\u3002 \u3000\u3000\u8fd8\u6709\u4e00\u5c42\uff0c\u4ed6\u89c1\u91d1\u9010\u6d41\u548c\u5c09\u8fdf\u70af\u559d\u4e86\u6bd2\u9152\uff0c\u884c\u82e5\u65e0\u4e8b\uff0c\u5fc3\u91cc\u4e5f\u6709\u4e9b\u597d\u751f\u60ca\u5f02\u3002\u4ed6\u662f\u76f8\u4fe1 \u795e\u7684\uff0c\u6697\u81ea\u60f3\u9053\uff1a\u201c\u8d35\u4eba\u6709\u767e\u7075\u5475\u62a4\uff0c\u6bd2\u9152\u6bd2\u4ed6\u4eec\u4e0d\u6b7b\uff0c\u771f\u4e3b\u4e00\u5b9a\u5728\u4ed6\u4eec\u8fd9\u4e00\u8fb9\u7684\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5185\u4f8d\u53c8\u63a0\u53c8\u754f\uff0c\u7ec8\u4e8e\u628a\u5b9e\u8bdd\u8bf4\u4e86\u51fa\u6765\uff1a\u201c\u4e0d\u9519\uff0c\u8fd9\u662f\u6bd2\u9152\u3002\u4f46\u8fd9\u4e0d\u5173\u5c0f\u4eba\u7684\u4e8b\uff0c\u662f\u5927 \u6e05\u56fd\u7684\u4f7f\u81e3\u53eb\u6211\u4eec\u8fd9\u6837\u7684\u3002\u201d \u3000\u3000\u201c\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u597d\uff0c\u770b\u5728\u4f60\u8bf4\u5b9e\u8bdd\u7684\u4efd\u4e0a\uff0c\u9976\u4f60\u4e0d\u6b7b\u3002\u6e05\u5ef7\u7684\u4f7f\u8005\u4f4f\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u4f60\u628a \u5730\u56fe\u753b\u51fa\u6765\u3002\u201d \u3000\u3000\u8fd9\u5185\u4f8d\u4e0d\u6562\u4e0d\u4f9d\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u4f4f\u5728\u738b\u5bab\u7684\u82b1\u56ed\u91cc\u9762\u3002\u201d\u753b\u597d\u5730\u56fe\uff0c\u4ea4\u7ed9\u5c09\u8fdf\u70af\u3002 \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u5c09\u8fdf\u5927\u54e5\uff0c\u4f60\u4e3b\u610f\u5982\u4f55\uff1f\u201d \u3000\u3000\u5c09\u8fdf\u70af\u70b9\u4e86\u8fd9\u5185\u4f8d\u7684\u660f\u7761\u7a74\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u53bb\u628a\u6e05\u5ef7\u7684\u4f7f\u8005\u63ea\u51fa\u6765\uff0c\u5f53\u4f17\u5ba3\u5e03\u6b64\u4e8b\uff0c\u4e00 \u5200\u5c06\u5730\u6740\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4f7f\u4e0d\u5f97\u5427\u3002\u201d \u3000\u3000\u5c09\u8fdf\u70af\uff1a\u201c\u571f\u738b\u4e00\u5b9a\u662f\u8892\u62a4\u4ed6\u4eec\u7684\uff0c\u6211\u4eec\u53ea\u6709\u7528\u8fd9\u4e2a\u5feb\u5200\u65a9\u4e71\u9ebb\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u571f\u738b\u82e5\u662f\u5411\u6e05\u5ef7\uff0c\u6740\u4e86\u90a3\u4e2a\u4f7f\u8005\uff0c\u53ea\u6015\u4e5f\u65e0\u6d4e\u4e8e\u4e8b\u3002\u201d \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u6740\u4e86\u4f7f\u8005\uff0c\u4e5f\u662f\u65ad\u4e86\u571f\u738b\u6295\u9760\u6e05\u5ef7\u7684\u540e\u8def\u5440\u3002\u201d \u3000\u3000\u4e24\u4eba\u5404\u6267\u4e00\u89c1\uff0c\u91d1\u9010\u6d41\u60f3\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u4eec\u91c7\u53d6\u6298\u8877\u7684\u529e\u6cd5\uff0c\u4f60\u628a\u8fd9\u5185\u5f85\u9001\u53bb \u7ed9\u5b97\u8fbe\uff0c\u8ba9\u4ed6\u77e5\u9053\u4eca\u665a\u4e4b\u4e8b\uff0c\u8bf7\u4ed6\u6307\u70b9\u3002\u6211\u5077\u8fdb\u571f\u738b\u5b98\u4e2d\uff0c\u4fa6\u5bdf\u6e05\u5ef7\u4f7f\u8005\u7684\u884c\u52a8\uff0c\u5fc5\u8981\u65f6\u6211 \u4f1a\u628a\u4ed6\u4eec\u63ea\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u5c09\u8fdf\u70af\u9053\uff1a\u201c\u4e5f\u597d\u3002\u77e5\u5df1\u77e5\u5f7c\uff0c\u767e\u6218\u767e\u80dc\u3002\u201d\u5f53\u4e0b\u89e3\u5f00\u4e86\u90a3\u5185\u4f8d\u7684\u7a74\u9053\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u548c \u6211\u5230\u5587\u561b\u5e99\u8d70\u4e00\u8d9f\u3002\u4f46\u51fa\u53bb\u4e4b\u65f6\uff0c\u4f60\u53ea\u80fd\u8bf4\u662f\u5e26\u6211\u53bb\u8c12\u89c1\u738b\u7237\u7684\u3002\u5426\u5219\uff0c\u4f60\u5c31\u8981\u4ed4\u7ec6\u60f3\u60f3\uff0c \u4f60\u7684\u5934\u9885\u662f\u5426\u6bd4\u8fd9\u5f20\u6a80\u6728\u684c\u5b50\u66f4\u786c\u4e86\u3002\u201d \u3000\u3000\u8fd9\u5185\u4f8d\u8ff7\u8ff7\u7cca\u7cca\u7684\u4e5f\u4e0d\u77e5\u7761\u4e86\u591a\u4e45\uff0c\u7a81\u7136\u53c8\u9192\u8fc7\u6765\uff0c\u8d8a\u53d1\u7591\u5fc3\u4ed6\u4eec\u662f\u201c\u795e\u4eba\u201d\uff0c\u800c\u4e14\u4ed6 \u53c8\u89c1\u8fc7\u5c09\u8fdf\u70af\u7684\u5389\u5bb3\uff0c\u54ea\u6562\u4e0d\u4f9d\uff1f \u3000\u3000\u8fd9\u5185\u4f8d\u521a\u624d\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u662f\u5429\u5490\u8fc7\u5ba2\u6808\u7684\u4eba\u4e0d\u8bb8\u8fdb\u6765\u7684\uff0c\u662f\u4ee5\u521a\u624d\u53d1\u751f\u7684\u8fd9\u6869\u4e8b\u60c5\uff0c\u5ba2 \u6808\u4e2d\u7684\u6267\u5f79\u90fd\u4e0d\u77e5\u9053\uff0c\u5185\u4f8d\u5e26\u9886\u5c09\u8fdf\u70af\u8fdb\u5bab\u7b54\u8c22\uff0c\u8bf4\u6765\u4e5f\u662f\u987a\u7406\u6210\u7ae0\u4e4b\u4e8b\uff0c\u5f53\u7136\u6ca1\u4eba\u8d77\u7591\u4e86 \u3002 \u3000\u3000\u91d1\u9010\u6d41\u5f85\u5230\u5c06\u8fd1\u4e09\u66f4\u65f6\uff0c\u4f30\u8ba1\u5c09\u8fdf\u70af\u5df2\u7ecf\u89c1\u5230\u5b97\u8fbe\u5b8c\u771f\uff0c\u4fbf\u5373\u6362\u4e0a\u4e86\u591c\u884c\u8863\uff0c\u6084\u6084\u51fa\u53bb \uff0c\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u5230\u4e86\u571f\u738b\u5bab\u4e2d\u3002\u6309\u7167\u90a3\u5f20\u5730\u56fe\u6240\u793a\uff0c\u5f88\u5bb9\u6613\u7684\u5c31\u627e\u5230\u4e86\u6e05\u5ef7\u4f7f\u8005\u7684\u4f4f\u5904\uff0c \u53ea\u89c1\u90a3\u95f4\u623f\u95f4\uff0c\u706f\u706b\u5c1a\u672a\u7184\u706d\uff0c\u7eb1\u7a97\u73b0\u51fa\u4e24\u4e2a\u4eba\u5f71\u3002 \u3000\u3000\u91d1\u9010\u6d41\u8f7b\u8f7b\u63a0\u8fc7\u4e00\u5ea7\u5047\u5c71\uff0c\u6b63\u60f3\u8d70\u8fd1\u53bb\u5077\u542c\uff0c\u5ffd\u542c\u5f97\u90a3\u957f\u987b\u6c49\u5b50\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\u5728\u5916\u9762 \uff1f\u201d\u556a\u7684\u4e00\u58f0\u54cd\uff0c\u8fd9\u4eba\u5df2\u662f\u7ad9\u8d77\u8eab\u6765\uff0c\u63a8\u5f00\u4e86\u7a97\u5b50\u3002 \u3000\u3000\u5168\u9010\u6d41\u7684\u8f7b\u529f\u5dee\u4e0d\u591a\u5230\u4e86\u8e0f\u96ea\u65e0\u75d5\u7684\u5883\u754c\uff0c\u63a0\u8fc7\u5047\u5c71\uff0c\u7aef\u7684\u662f\u6709\u5982\u4e00\u53f6\u98d8\u843d\uff0c\u5760\u5904\u65e0\u58f0 \u3002\u91d1\u9010\u6d41\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u597d\u5389\u5bb3\uff01\u201d \u3000\u3000\u4f46\u91d1\u9010\u6d41\u4e5f\u662f\u5341\u5206\u673a\u8b66\uff0c\u4ed6\u65e9\u5df2\u770b\u89c1\u5047\u5c71\u65c1\u8fb9\u4e00\u68f5\u6811\u4e0a\u6709\u4e2a\u9e1f\u5de2\uff0c\u5f53\u90a3\u4eba\u51fa\u58f0\u7684\u65f6\u5019\uff0c \u91d1\u9010\u6d41\u634f\u4e86\u4e00\u9897\u6ce5\u4e38\uff0c\u4f7f\u51fa\u4e86\u5f39\u6307\u795e\u901a\u7684\u529f\u592b\uff0c\u628a\u6ce5\u4e38\u5411\u9e1f\u5de2\u5f39\u53bb\u3002\u8fc5\u5373\u4e00\u4e2a\u8d77\u4f0f\uff0c\u95ea\u8fc7\u5ba4 \u89d2\uff0c\u7ed5\u5230\u8fd9\u95f4\u623f\u5b50\u7684\u540e\u7a97\u3002 \u3000\u3000\u957f\u987b\u6c49\u5b50\u63a8\u5f00\u524d\u7a97\uff0c\u53ea\u542c\u5f97\u201d\u545c\u5440\u201d\u4e00\u58f0\uff0c\u4e00\u53ea\u5927\u9e1f\u6070\u6070\u4ece\u6811\u4e0a\u98de\u8d77\u6765\uff0c\u6811\u53f6\u6ce5\u5c51\u7c41\u7c41 \u843d\u4e0b\uff0c\u91d1\u9010\u6d41\u6240\u53d1\u7684\u90a3\u9897\u6ce5\u4e38\u8ddf\u7740\u843d\u4e0b\uff0c\u7ed9\u63a9\u76d6\u8fc7\u4e86\u3002\u90a3\u53ea\u5927\u9e1f\u53d7\u60ca\u98de\u8d77\uff0c\u7ed5\u6811\u4e00\u531d\uff0c\u53eb\u4e86 \u51e0\u58f0\uff0c\u597d\u50cf\u662f\u77e5\u9053\u6ca1\u6709\u5371\u9669\u4e86\uff0c\u53c8\u56de\u5230\u6811\u4e0a\u3002 \u3000\u3000\u957f\u987b\u6c49\u5b50\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u53ea\u9e1f\u513f\uff0c\u5012\u628a\u6211\u5413\u4e86\u4e00\u8df3\u3002\u201d\u90a3\u4e2a\u5b98\u5458\u8bf4\u9053\uff1a \u201c\u4f60\u5fd2\u4e5f\u591a\u7591\u4e86\uff0c\u600e\u4f1a\u6709\u4eba\uff0c\u6709\u4eba\u4e5f\u4e0d\u8fc7\u662f\u738b\u7237\u5bab\u4e2d\u7684\u4f8d\u536b\u7f62\u4e86\u3002\u201d \u3000\u3000\u957f\u987b\u6c49\u5b50\u9053\uff1a\u201c\u6211\u597d\u50cf\u542c\u5f97\u662f\u591c\u884c\u4eba\u7684\u58f0\u606f\u3002\u522b\u602a\u6211\u591a\u7591\uff0c\u56e0\u4e3a\u5bf9\u65b9\u5b9e\u5728\u662f\u975e\u540c\u5c0f\u53ef\u4e4b \u8f88\uff0c\u54b1\u4eec\u5018\u82e5\u5bb3\u4ed6\u4eec\u4e0d\u6210\uff0c\u53ea\u6084\u4ed6\u4eec\u4e5f\u4f1a\u6765\u6697\u7b97\u54b1\u4eec\u5462\uff0c\u5c82\u4e0d\u53ef\u9632\uff01\u201d \u3000\u3000\u90a3\u5b98\u5458\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u53ef\u662f\u90a3\u4e24\u4e2a\u5927\u51c9\u5c71\u7684\u4f7f\u8005\uff1f\u201d\u957f\u987b\u6c49\u5b50\u9053\uff1a\u201c\u5f53\u7136\u662f\u4e86\u3002\u9664\u4e86\u4ed6\u4eec \uff0c\u8fd8\u6709\u8c01\u662f\u54b1\u4eec\u7684\u5bf9\u5934\uff1f\u201d \u3000\u3000\u90a3\u5b98\u5458\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u6211\u6b63\u60f3\u95ee\u4f60\uff0c\u90a3\u4e24\u4e2a\u662f\u4ec0\u4e48\u4eba\uff1f\u5b97\u7237\uff0c\u4ee5\u4f60\u7684\u6b66\u529f\uff0c\u5f53\u4e16\u7f55\u6709\uff0c\u4f55 \u4ee5\u4f60\u4e0d\u53bb\u6084\u6084\u628a\u4ed6\u4eec\u6740\u6389\uff0c\u70b9\u4e86\u4ed6\u4eec\u7684\u6b7b\u7a74\uff0c\u522b\u4eba\u4e5f\u770b\u4e0d\u51fa\u75d5\u8ff9\u7684\u5440\u3002\u8fd9\u4e0d\u6bd4\u8f6c\u6258\u5185\u4f8d\u4e0b\u6bd2 \uff0c\u66f4\u4e3a\u5e72\u51c0\u5229\u843d\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u5f97\u8fd9\u5b98\u5458\u53eb\u8fd9\u957f\u987b\u6c49\u5b50\u505a\u201c\u5b97\u7237\u201d\uff0c\u624d\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u8fd9\u4e2a\u6c49\u5b50\u5c31\u662f\u6276\u6851\u4e03\u5b50 \u7684\u9886\u8896\uff0c\u66fe\u7ecf\u548c\u5c09\u8fdf\u70af\u4ea4\u8fc7\u624b\u7684\u90a3\u4e2a\u5b97\u795e\u9f99\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5c4f\u606f\u547c\u5438\u5077\u542c\uff0c\u53ea\u542c\u5f97\u5b97\u795e\u9f99\u8bf4\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u4e24\u4e2a\u4eba\u53ea\u6015\u6211\u4e5f\u4e0d\u662f\u4ed6\u4eec\u7684\u5bf9\u624b\uff01 \u201d\u90a3\u6ee1\u5dde\u5b98\u5458\u9053\uff1a\u201c\u7a76\u7adf\u662f\u8c01\uff0c\u5b97\u7237\uff0c\u8bf7\u4f60\u522b\u5356\u5173\u5b50\u4e86\uff0c\u597d\u5417\uff1f\u201d \u3000\u3000\u5b97\u795e\u9f99\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u6253\u542c\u6e05\u695a\uff0c\u4e00\u4e2a\u662f\u91d1\u4e16\u9057\u7684\u513f\u5b50\u4f59\u9010\u6d41\uff0c\u4e00\u4e2a\u662f\u4f60\u4eec\u7f09\u6355\u4e86\u591a \u5e74\uff0c\u8fd8\u672a\u80fd\u591f\u5c06\u4ed6\u7f09\u62ff\u5f52\u6848\u7684\u5173\u4e1c\u5927\u76d7\u5c09\u8fdf\u70af\uff01\u201d \u3000\u3000\u90a3\u6ee1\u5dde\u5b98\u5458\u554a\u5440\u4e00\u58f0\u53eb\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u4ed6\u4eec\uff0c\u8fd9\u5c31\u602a\u4e0d\u5f97\u5b97\u7237\u8981\u5206\u5916\u5c0f\u5fc3\u4e86\uff01 \u201d\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4e0d\u8fc7\u54b1\u4eec\u7684\u8ba1\u7b56\u4e07\u65e0\u4e00\u5931\uff0c\u5185\u4f8d\u662f\u738b\u7237\u81ea\u5df2\u7684\u5185\u4f8d\uff0c\u4ed6\u4eec\u518d\u806a\u660e\u4e5f\u60f3\u4e0d\u5230\u8fd9 \u5185\u4f8d\u662f\u66ff\u54b1\u4eec\u9001\u6bd2\u9152\u7684\u3002\u9152\u4e2d\u7684\u6bd2\u836f\u662f\u5927\u5185\u6240\u85cf\u7684\u9e64\u9876\u7ea2\uff01\u201d \u3000\u3000\u91d1\u9010\u6c49\u6697\u6697\u53eb\u4e86\u4e00\u58f0\u201c\u4fa5\u5e78\u201d\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u65e9\u5df2\u77e5\u9053\u662f\u6211\uff0c\u6211\u5374\u8fd8\u8499\u5728\u9f13\u91cc\u3002\u5e78\u4e8f \u6709\u5b97\u8fbe\u5b8c\u771f\u63d0\u9192\uff0c\u5426\u5219\u5c31\u8981\u7740\u4e86\u4ed6\u7684\u9053\u513f\u4e86\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u672a\u5df2\uff0c\u5ffd\u542c\u5f97\u5b97\u795e\u9f99\u53c8\u662f\u4e00\u58f0\u5927\u559d\uff1a\u201c\u4ec0\u4e48\u4eba\u80c6\u6562\u6765\u6b64\u7aa5\u63a2\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u9053\u53c8\u7ed9\u4ed6\u53d1\u73b0\uff0c\u5ffd\u542c\u5f97\u8863\u895f\u5e26\u98ce\u4e4b\u58f0\uff0c\u5c4b\u9876\u4e0a\u51fa\u73b0\u4e86\u51e0\u6761\u4eba\u5f71\u3002\u90a3 \u6ee1\u5dde\u5b98\u5458\u4e5f\u542c\u89c1\u4e86\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8fd9\u56de\u6050\u6015\u662f\u771f\u7684\u4e86\u3002\u201d \u3000\u3000\u8bdd\u72b9\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u4eba\u5df2\u5728\u559d\u9053\uff1a\u201c\u5b97\u795e\u9f99\u51fa\u6765\uff01\u201d \u3000\u3000\u5c4b\u9876\u4e0a\u8df3\u4e0b\u51e0\u4e2a\u4eba\u6765\uff0c\u4e3a\u9996\u7684\u7adf\u662f\u725f\u5b97\u6d9b\u3002 \u3000\u3000\u548c\u725f\u5b97\u6d9b\u4e00\u5411\u6765\u7684\u8fd8\u6709\u4e09\u4e2a\u4eba\uff0c\u91d1\u9010\u6d41\u4e00\u770b\uff0c\u4e09\u4e2a\u4eba\u4e2d\u4ed6\u8ba4\u5f97\u4e24\u4e2a\uff0c\u5c31\u662f\u90a3\u665a\u5728\u534e\u5c71\u7edd \u9669\u4e4b\u5904\u548c\u4ed6\u4ea4\u8fc7\u624b\u7684\u90a3\u5bf9\u592b\u59bb\u3002\u91d1\u9010\u6d41\u5df2\u7ecf\u77e5\u9053\u4ed6\u4eec\u7684\u540d\u5b57\u53eb\u505a\u77f3\u536b\u548c\u6851\u9752\u3002\u53e6\u5916\u4e00\u4e2a\u5219\u662f \u4e8c\u5341\u5c81\u5de6\u53f3\u7684\u5c11\u5973\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u5c11\u5973\u60f3\u5fc5\u5c31\u662f\u6797\u5143\u53cc\u8bf4\u7684\u5979\u90a3\u4e2a\u597d\u670b\u53cb\u7ec3\u5f69\u8679\u4e86\u3002\u201d \u3000\u3000\u5b97\u795e\u9f99\u770b\u89c1\u4ed6\u7684\u4e09\u4e2a\u5e08\u4f84\u548c\u4e00\u4e2a\u964c\u751f\u4eba\u540c\u6765\uff0c\u8fd9\u4e2a\u964c\u751f\u4eba\u5bf9\u4ed6\u751a\u662f\u65e0\u793c\uff0c\u5b97\u795e\u9f99\u4e0d\u89c9\u5f97 \u53c8\u662f\u5403\u60ca\u53c8\u662f\u8be7\u5f02\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u662f\u8c01\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u6211\u662f\u6276\u6851\u6d3e\u5ae1\u6d3e\u638c\u95e8\u5f1f\u5b50\u725f\u5b97\u6d9b\uff0c\u4f60\u7684\u8f88\u4efd\u867d\u9ad8\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u542c\u6211\u7684 \u547d\u4ee4\uff01\u201d \u3000\u3000\u5b97\u795e\u9f99\u6a2a\u773c\u5411\u77f3\u536b\u3001\u6851\u9752\u7b49\u4eba\u770b\u53bb\uff0c\u4ed6\u4eec\u592b\u5987\u548c\u90a3\u4e2a\u5c11\u5973\u90fd\u70b9\u4e86\u5934\uff0c\u8868\u793a\u725f\u5b97\u6d9b\u8bf4\u5f97\u4e0d \u9519\u3002 \u3000\u3000\u5b97\u795e\u9f99\u6012\u9053\uff1a\u201c\u6276\u6851\u6d3e\u65e9\u5df2\u5206\u4e3a\u4e09\u652f\uff0c\u5404\u81ea\u4e3a\u653f\u3002\u4f60\u8fd9\u638c\u95e8\u5f1f\u5b50\u662f\u81ea\u5c01\u7684\uff0c\u8981\u60f3\u7ba1\u6211\uff0c \u4e07\u4e07\u4e0d\u80fd\uff01\u201d \u3000\u3000\u77f3\u536b\u8bf4\u9053\uff1a\u201c\u5b97\u5e08\u53d4\uff0c\u53e4\u8bed\u6709\u4e91\uff1a\u5408\u4e45\u5fc5\u5206\uff0c\u5206\u4e45\u5fc5\u5408\uff0c\u6276\u6851\u6d3e\u6b63\u662f\u56e0\u4e3a\u5206\u5d29\u79bb\u6790\uff0c\u4ee5 \u81f4\u4e0d\u80fd\u91cd\u632f\u96c4\u98ce\u3002\u5982\u4eca\u662f\u8be5\u5230\u4e86\u7531\u5206\u800c\u5408\u7684\u65f6\u5019\u4e86\u3002\u201d \u3000\u3000\u5b97\u795e\u9f99\u201c\u54fc\u2019\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u613f\u610f\u6367\u4ed6\u505a\u638c\u95e8\u4e86\u5417\uff1f\u201d \u3000\u3000\u6851\u9752\u8bf4\u9053\uff1a\u201c\u4ed6\u662f\u725f\u5b97\u5e08\u7684\u5ae1\u7cfb\u5b50\u5b59\uff0c\u5f53\u8fd9\u638c\u95e8\uff0c\u539f\u662f\u540d\u6b63\u8a00\u987a\u3002\u201d \u3000\u3000\u5b97\u795e\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u65b0\u638c\u95e8\uff0c\u4f60\u6709\u4ec0\u4e48\u5429\u5490\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u7b2c\u4e00\uff0c\u4f60\u8d2a\u56fe\u5229\u7984\uff0c\u5b9e\u662f\u4e0d\u8be5\uff0c\u6211\u4e0d\u8bb8\u4f60\u5192\u5145\u6e05\u5ef7\u7684\u4f7f\u8005\uff0c\u5728\u6b64\u62db\u6447\u649e\u9a97 \u3002\u201d \u3000\u3000\u5b97\u795e\u9f99\u5927\u6012\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u8c01\u6562\u8bf4\u6211\u8fd9\u4f7f\u8005\u662f\u5192\u5145\u7684\uff01\u6211\u5f97\u671d\u5ef7\u91cd\u7528\uff0c\u4e5f\u6b63\u662f\u4e3a\u4e86\u91cd \u5149\u672c\u6d3e\u95e8\u6237\uff0c\u4f60\u8fd9\u5c0f\u5b50\u61c2\u5f97\u4ec0\u4e48\uff1f\u5c45\u7136\u6562\u6559\u8bad\u6211\uff01\u201d \u3000\u3000\u725f\u5b97\u6d9b\u4e0d\u7406\u776c\u4ed6\uff0c\u5f84\u81ea\u8bf4\u4e0b\u53bb\u9053\uff1a\u201c\u7b2c\u4e8c\uff0c\u672c\u6d3e\u7684\u62f3\u7ecf\u5251\u8c31\uff0c\u5404\u4eba\u90fd\u4e0d\u8bb8\u79c1\u85cf\u3002\u4f60\u5f97\u7684 \u90a3\u4e00\u4efd\uff0c\u5fc5\u987b\u4ea4\u51fa\u6765\u7ed9\u6211\u3002\u201d \u3000\u3000\u5b97\u795e\u9f99\u563f\u3001\u563f\u3001\u563f\u51b7\u7b11\u4e09\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u56fe\u7a77\u5315\u89c1\uff0c\u539f\u6765\u4f60\u662f\u60f3\u72ec\u9738\u672c\u95e8\u79d8\u7b08\uff01\u201d \u3000\u3000\u7ec3\u5f69\u8679\u9053\uff1a\u201c\u5e08\u516c\uff0c\u4f60\u53ef\u4e0d\u80fd\u8fd9\u6837\u8bf4\uff0c\u8fd9\u662f\u5bf9\u672c\u95e8\u5927\u6709\u597d\u5904\u7684\u4e8b\u5440\uff01\u201d \u3000\u3000\u5b97\u795e\u9f99\u77aa\u4e86\u5979\u4e00\u773c\uff0c\u4f3c\u4e4e\u60f3\u8981\u9a82\u5979\uff0c\u5374\u53c8\u5fcd\u4f4f\u3002 \u3000\u3000\u725f\u5b97\u7977\u9053\uff1a\u201c\u5b97\u795e\u9f99\uff0c\u4f60\u8fd9\u662f\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\u5ea6\u541b\u5b50\u4e4b\u8179\uff0c\u4f46\u6211\u4e5f\u4e0d\u7ba1\u4f60\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u53ea\u95ee\u4f60 \u4f9d\u662f\u4e0d\u4f9d\uff1f\u201d \u3000\u3000\u5b97\u795e\u9f99\u9053\uff1a\u201c\u4e0d\u4f9d\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u90a3\u6211\u53ea\u597d\u66ff\u7956\u5e08\u6e05\u7406\u95e8\u6237\u4e86\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u5927\u4e3a\u6b22\u559c\uff0c\u60f3\u9053\uff1a\u201c\u725f\u5b97\u6d9b\u4e3a\u6276\u6851\u6d3e\u6e05\u7406\u95e8\u6237\uff0c\u6211\u5012\u662f\u4e0d\u5fc5\u63d2\u624b\u4e86\u3002\u201d\u4e8e\u662f\u4ecd\u7136 \u85cf\u5728\u5047\u5c71\u80cc\u540e\uff0c\u6682\u4e0d\u9732\u9762\u3002 \u3000\u3000\u5b97\u795e\u9f99\u6c14\u5f97\u4e03\u7a8d\u751f\u70df\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u6b3a\u6211\u592a\u751a\uff0c\u597d\u5440\uff0c\u4f60\u8981\u6e05\u7406\u95e8\u6237\uff0c\u90a3\u5c31\u6765\u5427\uff01 \u77f3\u536b\u3001\u6851\u9752\uff0c\u4f60\u4eec\u592b\u5987\u600e\u6837\uff1f\u201d \u3000\u3000\u77f3\u3001\u6851\u4e8c\u4eba\u540c\u58f0\u7b54\u9053\uff1a\u201c\u6211\u4eec\u662f\u5e2e\u7406\u4e0d\u5e2e\u4eb2\uff0c\u725f\u5e08\u5144\u8bf4\u7684\u662f\u6b63\u7406\u3002\u201d \u3000\u3000\u539f\u6765\u5bf9\u4e8e\u4f9d\u9644\u6e05\u5ef7\u603b\u7ba1\u8428\u798f\u9f0e\u4e00\u4e8b\uff0c\u6276\u6851\u4e03\u5b50\u4e4b\u4e2d\uff0c\u672c\u6765\u5c31\u6709\u4e09\u6d3e\u4e0d\u540c\u4e4b\u610f\u89c1\u3002\u4e00\u6d3e\u662f \u548c\u4ed6\u5f80\u6765\uff0c\u5f97\u70b9\u4fbf\u5229\uff0c\u5e2e\u4ed6\u4e00\u70b9\u5c0f\u5fd9\uff0c\u4e5f\u65e0\u6240\u8c13\u3002\u4f46\u4e0d\u53ef\u8fc7\u4e8e\u4e3a\u4ed6\u5229\u7528\uff0c\u4ee5\u81f4\u5931\u4e86\u201c\u9ad8\u4eba\u201d \u8eab\u4efd\uff1b\u4e00\u6d3e\u662f\u521d\u8d77\u7cca\u91cc\u7cca\u6d82\uff0c\u8ddf\u7740\u5b97\u795e\u9f99\u8d70\u3002\u540e\u6765\u9010\u6e10\u660e\u767d\uff0c\u56e0\u800c\u5bf9\u4ed6\u4e0d\u6ee1\u7684\uff0c\u8fd8\u6709\u4e00\u6d3e\u5219 \u662f\u6b7b\u5fc3\u584c\u5730\u7684\u8ddf\u7740\u5b97\u795e\u9f99\u8d70\u7684\u3002\u7ec3\u5f69\u8679\u662f\u7b2c\u4e8c\u6d3e\u3002\u77f3\u536b\u3001\u6851\u9752\u72ac\u5f52\u662f\u7b2c\u4e00\u6d3e\u3002\u4f46\u4ed6\u4eec\u5728\u534e\u5c71 \u94ba\u7fbd\u800c\u5f52\u4e4b\u540e\uff0c\u4e5f\u6e10\u6e10\u6709\u4e86\u6094\u610f\uff0c\u89c9\u5f97\u4e0d\u5e94\u8be5\u8fd9\u6837\u4e0b\u53bb\uff0c\u81ea\u5760\u8eab\u4efd\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u8fdb\u4eac\uff0c\u5148\u627e\u7740\u7ec3\u5f69\u8679\uff0c\u53e6\u5916\u7684\u4e09\u4e2a\u4eba\u662f\u8981\u8ddf\u5b97\u795e\u9f99\u8d70\u7684\uff0c\u4e0d\u80af\u542c\u4ed6\u7684\u8bdd\u3002\u725f\u5b97\u6d9b \u5e26\u4e86\u7ec3\u5f69\u8679\u7acb\u5373\u79bb\u4eac\uff0c\u9014\u4e2d\u78b0\u4e0a\u6851\u9752\u3001\u77f3\u536b\u3002\u6851\u3001\u77f3\u4e8c\u4eba\u542c\u8bf4\u5b97\u795e\u9f99\u5df2\u6b63\u5f0f\u51fa\u4efb\u8428\u798f\u9f0e\u7684\u79c1 \u4eba\u4f7f\u8005\uff0c\u66f4\u4e3a\u4e0d\u6ee1\uff0c\u4e8e\u662f\u51b3\u610f\u5949\u725f\u5b97\u6d9b\u4e3a\u638c\u95e8\uff0c\u968f\u4ed6\u8d76\u6765\u9752\u6d77\u3002 \u3000\u3000\u5b97\u795e\u9f99\u89c1\u6851\u3001\u77f3\u4e8c\u4eba\u5df2\u7ecf\u7ed9\u725f\u5b97\u6d9b\u62c9\u4e86\u8fc7\u53bb\uff0c\u8d8a\u53d1\u5927\u6012\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u4e8c\u4eba\u548c \u4ed6\u5e76\u80a9\u4e0a\u5427\uff01\u4ed6\u8981\u6e05\u7406\u95e8\u6237\uff0c\u6211\u53ef\u4e5f\u8981\u6e05\u7406\u95e8\u6237\u4e86\u3002\u201d\u5b97\u795e\u9f99\u662f\u4ed6\u4eec\u7684\u5e08\u53d4\uff0c\u662f\u4ee5\u53e3\u51fa\u6b64\u8a00 \u3002 \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u77f3\u5e08\u5144\uff0c\u6851\u5e08\u59d0\uff0c\u8bf7\u4f60\u4eec\u66ff\u6211\u628a\u573a\uff0c\u4e0d\u8bb8\u5916\u4eba\u9a9a\u6270\u3002\u63a5\u7740\u51b7\u7b11\u9053\uff1a\u201c\u5b97\u795e \u9f99\uff0c\u6211\u4ee5\u638c\u95e8\u5f1f\u5b50\u7684\u8eab\u4efd\uff0c\u524d\u6765\u6e05\u7406\u95e8\u6237\uff0c\u5b9a\u8981\u4f60\u8f93\u5f97\u53e3\u670d\u5fc3\u670d\uff01\u201d \u3000\u3000\u5b97\u795e\u9f99\u8bf4\u4e86\u4e00\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u968f\u5373\u628a\u773c\u5411\u7ec3\u5f69\u8679\u770b\u53bb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5f69\u8679\uff0c\u4f60\u53c8\u5982\u4f55\uff0c\u4f60 \u4e5f\u662f\u5e2e\u7406\u4e0d\u5e2e\u4eb2\u5417\uff1f\u201d\u7ec3\u5f69\u8679\u662f\u4ed6\u59bb\u5b50\u7684\u5173\u95e8\u5f1f\u5b50\uff0c\u4ed6\u7684\u59bb\u5b50\u5df2\u6b7b\uff0c\u7ec3\u5f69\u8679\u4e5f\u5c31\u7b49\u4e8e\u662f\u4ed6\u7684 \u5f92\u5f1f\u4e00\u6837\u4e86\u3002 \u3000\u3000\u7ec3\u5f69\u8679\u9053\uff1a\u201c\u6211\u65e2\u662f\u5e2e\u7406\uff0c\u53c8\u662f\u5e2e\u4eb2\u3002\u201d \u3000\u3000\u5b97\u795e\u9f99\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bf4\uff1f\u201d \u3000\u3000\u7ec3\u5f69\u8679\u9053\uff1a\u201c\u5728\u5bb6\u4ece\u7236\uff0c\u51fa\u5ac1\u4ece\u592b\u3002\u4f60\u867d\u7136\u662f\u6211\u7684\u5e08\u516c\uff0c\u603b\u4e0d\u80fd\u80dc\u4e8e\u6211\u7684\u751f\u7236\u3002\u4e08\u592b\u6bd4 \u751f\u7236\u66f4\u4eb2\uff0c\u4f55\u51b5\u4e8e\u4f60\uff1f\u201d \u3000\u3000\u5b97\u795e\u9f99\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u4e48\uff0c\u4f60\u4eec\uff0c\u4f60\u4eec\u2026\u2026\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u6211\u4eec\u65e9\u5df2\u8ba2\u5a5a\u4e86\uff0c\u7ec3\u59d1\u5a18\u662f\u6211\u7684\u672a\u5a5a\u59bb\u5b50\u3002\u201d \u3000\u3000\u7ec3\u5f69\u8679\u9053\uff1a\u201c\u5e08\u516c\uff0c\u6211\u770b\u5728\u6545\u4e16\u5e08\u5a18\u7684\u4efd\u4e0a\uff0c\u53ea\u8981\u4f60\u4ea4\u51fa\u7956\u5e08\u7684\u5251\u8c31\uff0c\u6211\u4e00\u5b9a\u66ff\u4f60\u5411\u725f \u90ce\u6c42\u60c5\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u542c\u5230\u8fd9\u91cc\uff0c\u4e5f\u662f\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u771f\u662f\u59cb\u6599\u4e4b\u6240\u4e0d\u53ca\uff0c\u725f\u5b97\u6d9b\u7adf \u7136\u548c\u8fd9\u4f4d\u7ec3\u59d1\u5a18\u8ba2\u4e86\u5a5a\uff0c\u90a3\u4f4d\u6797\u59d1\u5a18\u53ef\u600e\u4e48\u529e\u5462\uff1f\u201d\u91d1\u9010\u6d41\u60f3\u8d77\u4e86\u6797\u65e0\u53cc\u90a3\u4e00\u665a\u548c\u4ed6\u8bf4\u8d77\u725f \u5b97\u6d9b\u7684\u65f6\u5019\uff0c\u90a3\u4e00\u526f\u4e00\u5f80\u60c5\u6df1\u7684\u795e\u6001\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6697\u6697\u4e3a\u5979\u53f9\u6c14\u3002 \u3000\u3000\u4e00\u58f0\u5927\u559d\u628a\u91d1\u9010\u6d41\u4ece\u8ff7\u832b\u4e4b\u4e2d\u60ca\u9192\uff0c\u53ea\u89c1\u5b97\u795e\u9f99\u5df2\u662f\u62d4\u5251\u51fa\u9798\uff0c\u5411\u725f\u5b97\u6d9b\u523a\u53bb\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u6298\u6247\u4e00\u6325\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\u8f7b\u54cd\uff0c\u5b97\u795e\u9f99\u7684\u957f\u5251\u5f39\u5f00\uff0c\u9000\u4e0b\u4e00\u6b65\u3002\u725f\u5b97\u6d9b\u7684 \u6298\u6247\u4e0a\u5374\u7ed9\u523a\u7a7f\u4e86\u4e00\u4e2a\u5c0f\u5b54\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u4e24\u4eba\u90fd\u662f\u5fc3\u4e2d\u4e00\u51db\uff0c\u77e5\u9053\u78b0\u4e0a\u4e86\u52b2\u654c\u3002\u8bba\u529f\u529b\u662f\u5b97\u795e\u9f99\u66f4\u6df1\uff0c\u8bba\u5251\u6cd5\u662f\u725f\u5b97\u6d9b\u66f4 \u5999\uff0c\u4ed6\u628a\u6298\u6247\u5f53\u4f5c\u77ed\u5251\u4f7f\u7528\uff0c\u5728\u90a3\u4e00\u62db\u4e4b\u95f4\uff0c\u5df2\u662f\u904d\u88ad\u4e86\u5bf9\u65b9\u7684\u4e03\u9053\u5927\u7a74\uff0c\u8fd9\u624d\u628a\u5b97\u795e\u9f99\u903c \u9000\u7684\u3002 \u3000\u3000\u5b97\u795e\u9f99\u957f\u5251\u4e00\u6311\uff0c\u6296\u8d77\u4e09\u6735\u5251\u82b1\uff0c\u653b\u5411\u5bf9\u65b9\u4e09\u5904\u8981\u5bb3\u3002\u725f\u5b97\u6d9b\u6298\u6247\u4e00\u5f20\u4e00\u5408\uff0c\u6247\u5b50\u6ef4\u6e9c \u6e9c\u4e00\u8f6c\uff0c\u7adf\u7136\u628a\u90a3\u67c4\u957f\u5251\u5f15\u5f97\u4e1c\u6447\u897f\u6643\u3002\u539f\u6765\u725f\u5b97\u6d9b\u4f7f\u7684\u662f\u4e00\u62db\u201c\u4e09\u8f6c\u6cd5\u8f6e\u201d\uff0c\u6b63\u662f\u514b\u5236\u5b97 \u795e\u9f99\u8fd9\u4e00\u62db\u7684\u3002\u53ef\u662f\u8868\u9762\u770b\u6765\uff0c\u725f\u5b97\u6d9b\u867d\u7136\u597d\u50cf\u8f7b\u63cf\u6de1\u5199\u7684\u4fbf\u5316\u89e3\u4e86\u5bf9\u65b9\u7684\u62db\u6570\uff0c\u4f46\u4ed4\u7ec6\u89c2 \u5bdf\u7684\u8bdd\uff0c\u5374\u53ef\u4ee5\u770b\u5230\u4ed6\u7684\u989d\u89d2\u5df2\u7ecf\u6c81\u51fa\u4e86\u51e0\u9897\u6c57\u73e0\u3002 \u3000\u3000\u91d1\u9010\u6d41\u770b\u5f97\u4e5f\u4e0d\u7981\u624b\u5fc3\u91cc\u634f\u4e86\u4e00\u628a\u51b7\u6c57\uff0c\u60f3\u9053\uff1a\u201c\u4e00\u4e2a\u529f\u529b\u6df1\u539a\uff0c\u4e00\u4e2a\u5251\u6cd5\u7cbe\u5947\uff0c\u9e7f\u6b7b \u8c01\u624b\uff0c\u5b9e\u662f\u96be\u6599\u3002\u201d \u3000\u3000\u571f\u738b\u5bab\u4e2d\u7684\u536b\u58eb\u6b64\u65f6\u5df1\u662f\u7ed9\u4ed6\u4eec\u7684\u5267\u6597\u60ca\u52a8\uff0c\u7eb7\u7eb7\u8d77\u6765\u3002 \u3000\u3000\u77f3\u536b\u559d\u9053\uff1a\u201c\u6211\u4eec\u6276\u6851\u6d3e\u5728\u6b64\u6e05\u7406\u95e8\u6237\uff0c\u4e0e\u65c1\u4eba\u65e0\u5173\uff01\u201d \u3000\u3000\u90a3\u4e2a\u6ee1\u5dde\u4f7f\u8005\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u4f11\u8981\u542c\u4ed6\u80e1\u8bf4\uff0c\u5feb\u5feb\u4e0a\u6765\uff0c\u6349\u62ff\u523a\u5ba2\uff01\u201d \u3000\u3000\u725f\u5b97\u6d9b\u53eb\u9053\uff1a\u201c\u8fd9\u4eba\u5e76\u975e\u6e05\u5ef7\u4f7f\u8005\uff0c\u4ed6\u4e0d\u8fc7\u662f\u8428\u798f\u9f0e\u79c1\u4eba\u6d3e\u6765\u7684\u3002\u8bf7\u4f60\u4eec\u6682\u65f6\u8896\u624b\u65c1\u89c2 \uff0c\u5f85\u4f1a\u513f\u6211\u518d\u5411\u4f60\u4eec\u738b\u7237\u5206\u8bf4\u3002\u201d \u3000\u3000\u90a3\u4e9b\u536b\u58eb\u4e0d\u61c2\u6c5f\u6e56\u89c4\u77e9\uff0c\u66f4\u4e0d\u6562\u76f8\u4fe1\u725f\u5b97\u6d9b\u7684\u8bf4\u8bdd\u3001\u56e0\u6b64\u4ecd\u662f\u62a1\u5200\u52a8\u67aa\uff0c\u56db\u9762\u56f4\u62e2\u3001\u773c \u770b\u4e00\u573a\u6df7\u6218\uff0c\u96be\u4ee5\u907f\u514d\uff0c\u91d1\u9010\u6d41\u5ffd\u5730\u5927\u559d\u4e00\u58f0\u4ece\u5047\u5c71\u77f3\u540e\u8df3\u51fa\u6765\u3002 \u3000\u3000\u91d1\u9010\u6d41\u559d\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u4ed6\u662f\u5426\u6e05\u5ef7\u4f7f\u8005\uff0c\u4ed6\u72af\u4e86\u8c0b\u6740\u6848\uff0c\u6211\u6b63\u8981\u62ff\u4ed6\u53bb\u89c1\u738b\u7237\uff01\u4f60\u4eec\u8c01 \u4e5f\u4e0d\u8bb8\u591a\u4e8b\uff01\u201d\u5927\u559d\u58f0\u4e2d\uff0c\u6325\u52a8\u7384\u94c1\u5b9d\u5251\u5411\u5047\u5c71\u5288\u4e0b\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5df2\u628a\u4e00\u5ea7\u5047\u5c71\u7684\u5c71\u5934\u524a\u5e73 \u3002\u5047\u5c71\u867d\u7136\u4e0d\u6bd4\u771f\u5c71\uff0c\u4f46\u5806\u53e0\u5728\u5c71\u9876\u7684\u4e94\u516d\u5757\u5de8\u77f3\u4e5f\u6709\u78e8\u76d8\u822c\u5927\uff0c\u7ed9\u7384\u94c1\u5b9d\u5251\u4e71\u780d\u4e71\u524a\uff0c\u53d8 \u6210\u4e86\u4e00\u5927\u5806\u788e\u77f3\u4e86\u3002 \u3000\u3000\u571f\u738b\u5bab\u4e2d\u7684\u536b\u58eb\u51e0\u66fe\u89c1\u8fc7\u8fd9\u6837\u5389\u5bb3\u7684\u672c\u9886\uff0c\u4eba\u4eba\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u767b\u65f6\u6f6e\u6c34\u822c\u7684\u9000\u4e0b\uff0c\u751f \u6015\u7ed9\u5b9d\u5251\u7684\u950b\u8292\u6b83\u53ca\u3002 \u3000\u3000\u90a3\u6ee1\u5dde\u4f7f\u8005\u867d\u7136\u61c2\u5f97\u6b66\u529f\uff0c\u4f46\u81ea\u5fd6\u51b3\u8ba1\u4e0d\u662f\u91d1\u9010\u6d41\u7684\u5bf9\u624b\uff0c\u6b64\u65f6\u4e5f\u662f\u5413\u5f97\u614c\u4e86\uff0c\u4e09\u5341\u516d \u8ba1\uff0c\u8d70\u4e3a\u4e0a\u8ba1\uff0c\u8fde\u5fd9\u6df7\u5728\u536b\u58eb\u5806\u4e2d\uff0c\u5411\u571f\u738b\u7684\u5185\u5bab\u62d4\u8db3\u98de\u5954\u3002 \u3000\u3000\u5ffd\u542c\u5f97\u6709\u4eba\u4e00\u58f0\u5927\u559d\uff1a\u201c\u76f4\u53ae\u9e1f\uff0c\u5f80\u54ea\u91cc\u8dd1\uff1f\u201d\u58f0\u5230\u4eba\u5230\uff0c\u53ea\u662f\u4e00\u4e2a\u7167\u9762\uff0c\u5c31\u628a\u8fd9\u6ee1\u5dde \u4f7f\u8005\u7684\u53f3\u81c2\u62d7\u6298\uff0c\u72e0\u72e0\u7684\u63ea\u7740\u4ed6\u3002\u8fd9\u4eba\u6b63\u662f\u5c09\u8fdf\u70af\u3002\u5728\u4ed6\u540e\u9762\u8ddf\u7740\u6709\u4e24\u4eba\u6765\u5230\uff0c\u8fd9\u4e24\u4e2a\u4eba\u662f \u5927\u5587\u561b\u5b97\u8fbe\u5b8c\u771f\u548c\u90a3\u4e2a\u66ff\u6ee1\u5dde\u4f7f\u8005\u9001\u6bd2\u9152\u7684\u5185\u4f8d\u3002 \u3000\u3000\u91d1\u9010\u6d41\u559c\u9053\uff1a\u201c\u5c09\u8fdf\u5927\u54e5\uff0c\u4f60\u6765\u5f97\u6b63\u5408\u65f6\u3002\u201d\u5c09\u8fdf\u70af\u7741\u5927\u773c\u775b\u770b\u725f\u5b97\u6d9b\u548c\u5b97\u795e\u9f99\u6bd4\u5251\uff0c \u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u201d \u3000\u3000\u91d1\u9010\u6d41\u9053\uff1a\u201c\u8fd9\u4eba\u5c31\u662f\u725f\u5b97\u6d9b\u4e86\u3002\u4ed6\u4e3a\u6276\u6851\u627c\u6e05\u7406\u95e8\u6237\uff0c\u54b1\u4eec\u6682\u4e14\u4e0d\u5fc5\u63d2\u624b\u3002\u5f85\u4f1a\u513f\u518d \u53bb\u89c1\u738b\u7237\u5427\u3002\u201d \u3000\u3000\u6b66\u6797\u4e2d\u4e00\u6d3e\u6e05\u7406\u95e8\u6237\uff0c\u6309\u89c4\u77e9\u5916\u6d3e\u662f\u4e0d\u80fd\u63d2\u624b\u7684\uff0c\u4f46\u91d1\u9010\u6d41\u7528\u201c\u6682\u4e14\u201d\u4e8c\u5b57\uff0c\u5374\u53e6\u6709\u4e00 \u5c42\u610f\u601d\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u77e5\u9e7f\u6b7b\u8c01\u624b\uff0c\u5982\u679c\u7ed3\u5c40\u662f\u725f\u5b97\u6d9b\u8d25\u4e86\u7684\u8bdd\uff0c\u4ed6\u5f53\u7136\u8fd8\u662f\u8981\u548c\u5b97\u795e\u9f99\u52a8\u624b\u7684 \u3002 \u3000\u3000\u5c09\u8fdf\u70af\u770b\u4e86\u4e00\u773c\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u4e0d\u5fc5\u54b1\u4eec\u63d2\u624b\u4e86\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u6014\u4e86\u4e00\u6014\uff0c\u60f3\u9053\uff1a\u201c\u600e\u7684\u5c09\u8fdf\u5927\u54e5\u597d\u50cf\u65ad\u5b9a\u4e86\u662f\u725f\u5b97\u6d9b\u4e1a\u5df2\u7a33\u64cd\u80dc\u5238\uff1f\u201d\u51dd\u795e\u770b \u53bb\uff0c\u53ea\u89c1\u5251\u82b1\u9519\u843d\uff0c\u4eba\u5f71\u7ffb\u98de\uff0c\u7a81\u7136\u95f4\u725f\u5b97\u6d9b\u4e00\u58f0\u5927\u559d\uff0c\u8fde\u8fdb\u6570\u62db\uff0c\u679c\u7136\u53c8\u62a2\u4e86\u4e0a\u98ce\uff0c\u91d1\u9010 \u6d41\u6697\u6697\u4f69\u670d\uff1a\u201c\u5c09\u8fdf\u5927\u54e5\u7684\u773c\u529b\u679c\u7136\u662f\u6bd4\u6211\u9ad8\u660e\u5f97\u591a\uff01\u201d \u3000\u3000\u91d1\u9010\u6d41\u518d\u770b\u4e00\u4f1a\uff0c\u4e0d\u89c9\u53c8\u662f\u597d\u751f\u8be7\u5f02\uff0c\u539f\u6765\u725f\u5b97\u6d9b\u4f3c\u6247\u4ee3\u5251\uff0c\u4f7f\u51fa\u7684\u5251\u62db\uff0c\u7adf\u6709\u51e0\u62db\u9177 \u4f3c\u201c\u5927\u987b\u5f25\u5251\u5f0f\u201d\u3002\u91d1\u9010\u6d41\u60f3\u4e86\u4e00\u60f3\uff0c\u604d\u7136\u5927\u609f\uff1a\u201c\u539f\u6765\u6211\u53c2\u609f\u4e86\u4ed6\u7684\u6276\u6851\u5251\u610f\uff0c\u53e6\u521b\u65b0\u62db \uff0c\u4ed6\u4e5f\u53c2\u609f\u4e86\u6211\u7684\u5929\u5c71\u5251\u6cd5\uff0c\u80fd\u591f\u53d8\u5316\u51fa\u5927\u987b\u5f25\u5251\u5f0f\u4e86\u3002\u5440\uff0c\u6b64\u4eba\u806a\u660e\uff0c\u5b9e\u662f\u4e0d\u5728\u6211\u4e4b\u4e0b\uff01 \u201d \u3000\u3000\u5927\u987b\u5f25\u5251\u5f0f\u662f\u4e0e\u6276\u6851\u5251\u6cd5\u5f02\u66f2\u540c\u5de5\u7684\u6700\u4e0a\u4e58\u5251\u6cd5\uff0c\u5b97\u795e\u9f99\u7684\u6276\u6851\u5251\u6cd5\u672c\u6765\u5c31\u7565\u900a\u725f\u5b97\u6d9b \u4e00\u7b79\uff0c\u66f4\u52a0\u4ee5\u4e0d\u8bc6\u5927\u987b\u5f25\u5251\u5f0f\uff0c\u53ea\u51ed\u529f\u529b\u8f83\u9ad8\uff0c\u5df2\u662f\u96be\u4e8e\u62b5\u654c\uff0c\u4e0d\u8fc7\u6570\u62db\uff0c\u53ea\u542c\u5f97\u725f\u5b97\u6d9b\u559d \u58f0\uff1a\u201c\u7740\uff01\u201d\u6247\u67c4\u4e00\u6572\uff0c\u6b63\u4e2d\u5b97\u795e\u9f99\u7684\u7435\u7436\u9aa8\uff0c\u628a\u4ed6\u7684\u7435\u7436\u9aa8\u6253\u788e\u4e86\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u641c\u4e86\u4ed6\u7684\u5251\u8c31\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u770b\u5728\u4f60\u662f\u5f69\u8679\u5e08\u516c\u7684\u4efd\u4e0a\uff0c\u9976\u4f60\u4e0d\u6b7b\uff0c\u53bb\u5427\uff01\u201d\u5b97 \u795e\u9f99\u6b66\u529f\u5df2\u5e9f\uff0c\u4e0d\u6562\u4f5c\u58f0\uff0c\u53ea\u597d\u8d70\u4e86\u3002 \u3000\u3000\u5b97\u8fbe\u5b8c\u771f\u5bf9\u4f17\u536b\u58eb\u9053\uff1a\u201c\u6b64\u4e8b\u7531\u6211\u548c\u738b\u7237\u53bb\u8bf4\uff0c\u4f60\u4eec\u90fd\u56de\u53bb\u5427\u3002\u201d\u4ed6\u662f\u5927\u5587\u561b\u8eab\u4efd\uff0c\u4f17 \u536b\u58eb\u90fd\u662f\u4fe1\u5949\u5587\u561b\u6559\u7684\uff0c\u81ea\u662f\u552f\u547d\u662f\u4ece\u3002\u4e8e\u662f\u5b97\u8fbe\u5b8c\u771f\u4e0e\u5c09\u8fdf\u70af\u7b49\u4eba\uff0c\u4fbf\u62bc\u4e86\u90a3\u4e2a\u5185\u4f8d\u548c\u6ee1 \u5dde\u4f7f\u8005\u53bb\u89c1\u571f\u738b\u3002 \u3000\u3000\u571f\u738b\u89c1\u5b97\u8fbe\u5b8c\u771f\u548c\u91d1\u9010\u6d41\u7b49\u4eba\u628a\u6ee1\u5dde\u4f7f\u8005\u62bc\u8fdb\u6765\uff0c\u53c8\u662f\u8be7\u5f02\uff0c\u53c8\u662f\u5403\u60ca\u3002\u539f\u6765\u4ed6\u521a\u624d\u5728 \u5bab\u5185\u7684\u9732\u53f0\u65e9\u5df2\u770b\u89c1\u91d1\u9010\u6d41\u5251\u5288\u5047\u5c71\u7684\u5389\u5bb3\uff0c\u6b64\u65f6\u65e0\u4e00\u536b\u58eb\u5728\u65c1\uff0c\u91d1\u9010\u6d41\u548c\u5c09\u8fdf\u70af\u5374\u63ea\u7740\u8fd9 \u6ee1\u5dde\u4f7f\u8005\u8fdb\u6765\uff0c\u4ed6\u5fc3\u4e2d\u81ea\u662f\u60c5\u77e5\u4e0d\u5999\u3002\u4f46\u8be7\u5f02\u7684\u5374\u662f\u4e0d\u77e5\u5587\u561b\u4f55\u4ee5\u4e5f\u4e0e\u6b64\u4e8b\u6709\u5173\u3002 \u3000\u3000\u5b97\u8fbe\u5b8c\u771f\u8bf4\u9053\uff1a\u201c\u771f\u4e3b\u7684\u5429\u5490\u5bf9\u5ba2\u4eba\u5fc5\u987b\u89c6\u540c\u81ea\u5df1\u7684\u5144\u5f1f\uff0c\u5ba2\u4eba\u5018\u82e5\u7ed9\u4eba\u52a0\u5bb3\uff0c\u505a\u4e3b\u4eba \u7684\u4e0d\u80fd\u4e0d\u7ba1\u3002\u738b\u7237\u4f60\u8bf4\u662f\u4e48\uff1f\u201d\u5b97\u8fbe\u5b8c\u771f\u62ac\u51fa\u6559\u89c4\u8d28\u95ee\uff0c\u571f\u738b\u53ea\u597d\u70b9\u5934\u79f0\u662f\u3002 \u3000\u3000\u5b97\u8fbe\u5b8c\u771f\u9053\uff1a\u201c\u4ed6\u4eec\u4e24\u4f4d\u90fd\u662f\u4f7f\u8005\u7684\u8eab\u4efd\u3002\u4f46\u5982\u4eca\u8fd9\u4f4d\u6e05\u5ef7\u4f7f\u8005\u5374\u52a0\u5bb3\u5927\u51c9\u5c71\u6765\u7684\u90a3\u4f4d \u4f7f\u8005\uff0c\u6211\u4e5f\u4e0d\u77e5\u8be5\u600e\u4e48\u529e\u4e86\uff0c\u8bf7\u738b\u7237\u5904\u7f6e\u3002\u201d \u3000\u3000\u571f\u738b\u8be7\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4e8b\uff1f\u201d\u5b97\u8fbe\u5b8c\u771f\u628a\u90a3\u5185\u4f8d\u63a8\u5230\u571f\u738b\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u628a\u5728\u771f\u4e3b\u9762 \u524d\u5fcf\u6094\u7684\u8bf4\u8bdd\u5bf9\u738b\u7237\u518d\u8bf4\u4e00\u904d\u3002\u201d\u8fd9\u5185\u4f8d\u4e0d\u6562\u4e0d\u4f9d\uff0c\u4e00\u4e94\u4e00\u5341\u5730\u4f9b\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u6ee1\u6d32\u4f7f\u8005\u5413\u5f97\u53d1\u6296\uff0c\u786c\u7740\u5934\u76ae\u53d1\u6012\u9053\uff1a\u201c\u6211\u662f\u671d\u5ef7\u7684\u4f7f\u8005\uff0c\u5c82\u80fd\u4e0e\u571f\u532a\u7684\u4f7f\u8005\u76f8\u6bd4\uff1f \u201d \u3000\u3000\u5b97\u8fbe\u5b8c\u771f\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5e76\u672a\u53d7\u6e05\u5ef7\u7b56\u5c01\uff0c\u53ea\u80fd\u628a\u4f60\u5f53\u4f5c\u5ba2\u4eba\u770b\u5f85\u3002\u6309\u7167\u6211\u4eec\u7684\u6559\u89c4\uff0c\u4e0d \u8bba\u662f\u8c01\u90fd\u4e0d\u80fd\u5728\u6211\u4eec\u8fd9\u513f\u5bb3\u4eba\u3002\u201d \u3000\u3000\u571f\u738b\u4e5f\u662f\u5413\u5f97\u6d51\u8eab\u53d1\u6296\uff0c\u6309\u4ed6\u7684\u5fc3\u610f\u662f\u8981\u8892\u62a4\u6ee1\u5dde\u4f7f\u8005\u7684\uff0c\u4f46\u91d1\u9010\u6d41\u548c\u5c09\u8fdf\u70af\u51f6\u795e\u6076\u715e \u5730\u7ad9\u5728\u4ed6\u8eab\u8fb9\uff0c\u5927\u5587\u561b\u660e\u663e\u53c8\u662f\u5e2e\u52a9\u4ed6\u4eec\u7684\uff0c\u571f\u738b\u7ed9\u5413\u5f97\u516d\u795e\u65e0\u4e3b\uff0c\u4e0d\u6562\u8bf4\u8bdd\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u5ffd\u5730\u8d70\u4e86\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u4eba\u4e0d\u662f\u6e05\u5ef7\u4f7f\u8005\uff0c\u4ed6\u53ea\u662f\u8428\u798f\u9f0e\u6d3e\u6765\u7684\u4eba\u3002\u201d \u3000\u3000\u6ee1\u6d32\u4f7f\u8005\u51b7\u7b11\u9053\uff1a\u201c\u8428\u5927\u4eba\u8eab\u4e3a\u5927\u5185\u603b\u7ba1\uff0c\u8fd8\u4e0d\u80fd\u4ee3\u8868\u671d\u5ef7\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u4e5f\u51b7\u7b11\u9053\uff1a\u201c\u8428\u798f\u9f0e\u65e9\u5df2\u7ed9\u6e05\u5ef7\u6cbb\u7f6a\uff0c\u4f60\u82e5\u56de\u53bb\uff0c\u53ea\u6015\u81ea\u8eab\u4e5f\u96be\u514d\u5462\uff1f\u201d \u3000\u3000\u571f\u738b\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u8fd9\u6d88\u606f\u4ece\u4f55\u5f97\u6765\uff1f\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u6211\u662f\u66ff\u738b\u7237\u9001\u4e00\u4efd\u2018\u90b8\u6284\u2019\uff08\u53e4\u4ee3\u7684\u5b98\u62a5\uff09\u6765\u7684\u3002\u201d \u3000\u3000\u539f\u6765\u8428\u798f\u9f0e\u56e0\u4e3a\u548c\u4e00\u4f4d\u4eb2\u738b\u52fe\u7ed3\uff0c\u628a\u6301\u6743\u67c4\uff0c\u8d2a\u6c61\u821e\u5f0a\uff0c\u7ed9\u4ed6\u4eec\u7684\u653f\u654c\u53c2\u52be\uff0c\u8fd9\u653f\u654c\u662f \u4eb2\u738b\u52a0\u4e0a\u4e24\u4f4d\u624b\u63e1\u91cd\u5175\u7684\u5c06\u519b\uff0c\u52bf\u529b\u6bd4\u4ed6\u4eec\u66f4\u5927\uff0c\u7687\u5e1d\u4e0d\u80fd\u4e0d\u51c6\u4ed6\u4eec\u594f\u3002\u90a3\u4efd\u2018\u90b8\u6284\u2019\u6240\u520a \u8f7d\u7684\u5c31\u662f\u628a\u8428\u798f\u9f0e\u514d\u804c\u4e0b\u72f1\u7684\u201c\u5723\u65e8\u201d\u3002 \u3000\u3000\u4e8b\u60c5\u81f3\u6b64\uff0c\u6025\u8f6c\u76f4\u4e0b\uff0c\u571f\u738b\u53bb\u4e86\u987e\u8651\uff0c\u540c\u65f6\u4e5f\u662f\u6743\u8861\u672c\u8eab\u5229\u5bb3\u7684\u7ed3\u679c\uff0c\u9042\u628a\u90a3\u6ee1\u6d32\u4f7f\u8005 \u8d76\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u91d1\u9010\u6d41\u867d\u6ca1\u83b7\u5f97\u4e0a\u738b\u7b54\u5e94\u548c\u4e49\u519b\u7b7e\u8ba2\u76df\u7ea6\uff0c\u4f46\u5f97\u571f\u738b\u7b54\u5e94\u4e24\u4e0d\u76f8\u52a9\uff0c\u4e5f\u7b97\u5f97\u662f\u5b8c\u6210\u4f7f\u547d \u4e86\u3002 \u3000\u3000\u5168\u9010\u6d41\u3001\u5c09\u8fdf\u70af\u3001\u725f\u5b97\u6d9b\u4e09\u4eba\u544a\u8f9e\u51fa\u5bab\uff0c\u7ec3\u5f69\u8679\u7b49\u4eba\u6b63\u5728\u5916\u9762\u7b49\u7740\u548c\u725f\u5b97\u6d9b\u540c\u8d70\u3002 \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u5f69\u8679\uff0c\u8fd9\u4f4d\u5c31\u662f\u6211\u548c\u4f60\u5e38\u5e38\u8bf4\u53ca\u7684\u91d1\u5c11\u4fa0\u4e86\uff0c\u4f60\u8fc7\u6765\u89c1\u89c1\u3002\u201d \u3000\u3000\u91d1\u9010\u6d41\u60f3\u8d77\u662f\u6797\u65e0\u53cc\uff0c\u4e0d\u7981\u6709\u70b9\u4e3a\u5979\u96be\u8fc7\uff0c\u8bf4\u9053\uff1a\u201c\u7ec3\u59d1\u5a18\uff0c\u4f60\u662f\u4e0d\u662f\u6709\u4e00\u4f4d\u597d\u670b\u53cb\u53eb \u505a\u6797\u65e0\u53cc\uff1f\u201d\u7ec3\u5f69\u8679\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u5979\u8fd8\u662f\u5b97\u6d9b\u7684\u8868\u59b9\u5462\u3002\u201d\u91d1\u9010\u6d41\u9053\uff1a\u201c\u4e00\u4e2a\u6708\u591a\u524d\uff0c\u6211\u5728 \u534e\u5c71\u66fe\u89c1\u8fc7\u5979\u3002\u201d\u7ec3\u5f69\u8679\u9053\uff1a\u201c\u662f\u3002\u6211\u542c\u5f97\u77f3\u5e08\u5144\u548c\u6851\u5e08\u59d0\u8bf4\u4e86\uff0c\u53ea\u662f\u672a\u77e5\u5979\u7684\u5730\u5740\u3002\u201d\u91d1 \u9010\u6d41\u9053\uff1a\u201c\u5979\u4eec\u7236\u5973\u4f4f\u5728\u95fd\u5357\u4e00\u4e2a\u5c0f\u6e14\u6751\u4e2d\uff0c\u4f46\u6797\u59d1\u5a18\u73b0\u5728\u5df2\u7ecf\u8fdb\u4eac\uff0c\u542c\u8bf4\u5979\u6b63\u662f\u53bb\u627e\u4f60\u4eec \u5462\u3002\u201d \u3000\u3000\u725f\u5b97\u6d9b\u9053\uff1a\u201c\u6211\u4eec\u4e5f\u60e6\u8bb0\u5979\uff0c\u597d\u5728\u73b0\u5728\u5df2\u77e5\u5979\u7684\u4f4f\u5740\uff0c\u5979\u627e\u4e0d\u7740\u6211\u4eec\uff0c\u6211\u4eec\u53bb\u627e\u5979\u597d\u4e86 \u3002\u201d\u725f\u5b97\u6d9b\u83b7\u77e5\u6797\u65e0\u53cc\u7684\u6d88\u606f\uff0c\u5f88\u662f\u6b22\u559c\uff0c\u4f46\u5374\u6ca1\u6709\u6fc0\u52a8\u7684\u795e\u60c5\uff0c\u539f\u6765\u4ed6\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u6797\u65e0 \u53cc\u6697\u604b\u7740\u4ed6\u3002 \u3000\u3000\u91d1\u9010\u6d41\u5fc3\u91cc\u53f9\u4e86\u53e3\u6c14\uff0c\u4f46\u60f3\u59fb\u7f18\u4e4b\u4e8b\uff0c\u4ea6\u662f\u96be\u5f97\u4eba\u4eba\u5982\u610f\uff0c\u4e0d\u518d\u8bf4\u4ec0\u4e48\uff0c\u4e5f\u5c31\u53ea\u597d\u544a\u8f9e \u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u548c\u5c09\u8fdf\u70af\u56de\u5230\u5927\u51c9\u5c71\uff0c\u8fdb\u5165\u5e10\u4e2d\uff0c\u53ea\u89c1\u4ed6\u7684\u7236\u4eb2\u91d1\u4e16\u9057\u548c\u5389\u5357\u661f\u3001\u516c\u5b59\u71d5\u4e8c\u4eba\u4e5f \u90fd\u5728\u5ea7\u3002\u91d1\u9010\u6d41\u559c\u51fa\u671b\u5916\uff0c\u53e9\u89c1\u7236\u4eb2\u4e4b\u540e\uff0c\u4fbf\u5411\u7afa\u5c1a\u7236\u62a5\u544a\u6b64\u884c\u7ecf\u8fc7\u3002 \u3000\u3000\u7afa\u5c1a\u7236\u5927\u4e3a\u9ad8\u5174\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u529e\u5f97\u975e\u5e38\u4e4b\u597d\u3002\u6211\u4e5f\u6709\u4e2a\u597d\u6d88\u606f\u8981\u544a\u8bc9\u4f60\uff0c\u4e0d\u8fc7\uff0c\u8fd9 \u5e94\u8be5\u7531\u4ee4\u5c0a\u8bf4\u4e86\u3002\u201d \u3000\u3000\u91d1\u4e16\u9057\u62c8\u987b\u7b11\u9053\uff1a\u201c\u9010\u6d41\uff0c\u8d81\u7740\u76ee\u524d\u6682\u65f6\u6ca1\u6709\u6218\u4e8b\uff0c\u6211\u60f3\u66ff\u4f60\u529e\u4e86\u8fd9\u4ef6\u559c\u4e8b\uff0c\u4ee5\u4e86\u5fc3\u613f \u3002\u4e0d\uff0c\u8fd8\u4e0d\u53ea\u4e00\u4ef6\u5462\uff0c\u5389\u8d24\u4f84\u548c\u516c\u5b59\u59d1\u5a18\u7684\u5a5a\u4e8b\uff0c\u4e5f\u5c06\u548c\u4f60\u540c\u65e5\u4e3e\u884c\u3002\u65e5\u671f\u5df2\u8d70\u5728\u4e0b\u6708\u5341\u4e94 \uff0c\u5230\u65f6\u516c\u5b59\u8235\u4e3b\u548c\u4f60\u7684\u5927\u5e08\u5144\u90fd\u4f1a\u8d76\u6765\u7684\u3002\u201d \u3000\u3000\u5927\u51c9\u5c71\u867d\u7136\u50fb\u5904\u897f\u9672\uff0c\u4f46\u56e0\u91d1\u4e16\u9057\u76f8\u8bc6\u6ee1\u5929\u4e0b\uff0c\u4ed6\u7684\u513f\u5b50\u6210\u5a5a\uff0c\u4ecd\u662f\u6709\u4e0d\u5c11\u5bbe\u5ba2\u5230\u6765\u9053 \u8d3a\u3002\u516c\u5b59\u5b8f\u3001\u6c5f\u6d77\u5929\u548c\u59bb\u5b50\u8c37\u4e2d\u83b2\u4ee5\u53ca\u7ea2\u7f28\u4f1a\u3001\u9099\u5c71\u6d3e\u3001\u4e10\u5e2e\u7b49\u7b49\u9886\u8896\u4eba\u7269\u662f\u65e9\u5c31\u6765\u4e86\u7684\uff0c \u5a5a\u793c\u4e3e\u884c\u90a3\u5929\uff0c\u5c09\u8fdf\u70af\u7684\u59bb\u5b50\u7948\u5723\u56e0\u4e5f\u4ece\u4fdd\u5b9a\u8d76\u56de\u6765\u4e86\u3002 \u3000\u3000\u6b63\u5728\u70ed\u95f9\u4e4b\u9645\uff0c\u5ffd\u5730\u6709\u77e5\u5ba2\u62a5\u9053\uff1a\u201c\u6709\u4e2a\u59d3\u6797\u7684\u59d1\u5a18\u4e5f\u6765\u9053\u8d3a\uff0c\u8bf4\u662f\u91d1\u5c11\u4fa0\u7684\u670b\u53cb\uff0c\u6211 \u4eec\u90fd\u4e0d\u8ba4\u8bc6\u5979\u3002\u201d\u91d1\u9010\u6d41\u53c8\u662f\u6b22\u559c\uff0c\u53c8\u662f\u96be\u8fc7\uff0c\u8fde\u5fd9\u53eb\u201c\u8bf7\u201d\u3002\u6797\u65e0\u53cc\u8fdb\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5230 \u4eac\u4e2d\uff0c\u627e\u4e0d\u7740\u8868\u54e5\u3002\u7279\u6765\u559d\u4f60\u7684\u559c\u9152\uff0c\u987a\u4fbf\u5411\u4f60\u6253\u542c\u6d88\u606f\u3002\u201d\u91d1\u9010\u6d41\u9053\u3002\u201c\u6211\u5df2\u7ecf\u89c1\u7740\u4ed6\u4e86 \uff0c\u4ed6\u8bf4\u4ed6\u4f1a\u53bb\u627e\u4f60\u7684\u3002\u201d\u4ed6\u4e0d\u613f\u5f15\u8d77\u6797\u65e0\u53cc\u7684\u4f24\u5fc3\uff0c\u53ea\u80fd\u5982\u6b64\u8bf4\u4e86\u3002 \u3000\u3000\u91d1\u9010\u6d41\u867d\u7136\u6709\u70b9\u96be\u8fc7\uff0c\u4f46\u6574\u4e2a\u6c14\u6c1b\u5374\u662f\u5341\u5206\u70ed\u95f9\u6b22\u817e\uff0c\u5c24\u5176\u5f53\u7948\u5723\u56e0\u8bf4\u5230\u5c0f\u91d1\u5ddd\u3001\u5929\u7406 \u4f1a\u5404\u65b9\u9762\u7684\u4e49\u519b\u90fd\u662f\u597d\u751f\u5174\u65fa\uff0c\u4eba\u4eba\u66f4\u662f\u9ad8\u5174\u3002 \u3000\u3000\u4e10\u5e2e\u5e2e\u4e3b\u4ef2\u957f\u7edf\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u4e00\u4ee3\u80dc\u8fc7\u4e00\u4ee3\uff0c\u66f4\u96be\u5f97\u7684\u662f\u8fd9\u8bb8\u591a\u6d8c\u73b0\u7684\u65b0\u4eba\uff0c\u4eba \u4eba\u90fd\u662f\u5bf9\u53cd\u6e05\u4e8b\u4e1a\u4e00\u7247\u4e39\u5fc3\uff0c\u4f55\u6101\u5927\u4e8b\u4e0d\u6210\uff01\u201d\u91d1\u9010\u6d41\u5fc3\u4e0a\u7684\u4e00\u70b9\u9634\u973e\uff0c\u5728\u8fd9\u6837\u9ad8\u5174\u7684\u6c14\u6c1b \u4e2d\uff0c\u4e5f\u5c31\u50cf\u6de1\u4e91\u906e\u4e0d\u4f4f\u71c3\u70e7\u7684\u592a\u9633\uff0c\u7ed9\u70e7\u5316\u4e86\u3002\u6b63\u662f\uff1a \u3000\u3000\u4fa0\u9aa8\u67d4\u8bf7\u8c10\u597d\u5408\uff0c\u6d1e\u623f\u7ea2\u70db\u6620\u4e39\u5fc3\u3002 \u3000\u3000\uff08\u5168\u4e66\u5b8c\uff09"}),showOnHomepage:!0}]}}]);