"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[62987],{52930:(e,n,s)=>{s.r(n),s.d(n,{default:()=>a});var t=s(74848);const a=[{name:"\u94c1\u77e2\u795e\u5f13\u3000\u5c11\u5e74\u6276\u5de8\u5ba6  ",content:(0,t.jsx)(t.Fragment,{children:"\u91d1\u978d\u5b9d\u9a6c\u3000\u5927\u76d7\u9707\u865a\u58f0 \u3000\u3000\u4e00\u5251\u897f\u6765\uff0c\u5343  \u62f1\u5217\uff0c\u9b54\u5f71\u7eb5\u6a2a\uff1a\u95ee\u660e\u955c\u975e\u53f0\uff0c\u83e9\u63d0\u975e\u6811\uff0c\u5883\u7531\u5fc3\u8d77\uff0c\u53ef\u5f97\u5206\u660e\uff1f\u662f\u9b54 \u975e\u9b54\uff1f \u3000\u3000\u975e\u9b54\u662f\u9b54\uff1f\u8981\u5f85\u6c5f\u6e56\u5f8c\u4e16\u8bc4\uff01\u4e14\u6536\u62fe\uff0c\u8bdd\u82f1\u5806\u513f\u5973\uff0c\u5148\u53d9\u95f2\u60c5\u3002 \u3000\u3000\u98ce\u96f7\u610f\u6c14\u5ce5\u5d58\uff0c\u8f7b\u62c2\u4e86\u5bd2\u971c\u59a9\u5a9a\u751f\u3002\u53f9\u4f73\u4eba\u7edd\u4ee3\uff0c\u767d\u5934\u672a\u8001\uff0c\u767e\u5e74\u4e00\u8bfa\uff0c\u4e0d\u8d1f\u5fc3\u76df\u3002\u77ed \u683d\u82b1\uff0c\u957f\u8bd7\u4f50\u9152\uff0c\u8bd7\u5251\u5e74\u5e74\u603b\u5fc6\u537f\u3002\u5929\u5c71\u4e0a\uff0c\u770b\u9f99\u86c7\u7b14\u8d70\uff0c\u58a8\u6cfc\u5357\u6e9f\u3002 \u3000\u3000  \u2014\u2014\u2014\u2014\u8bcd \u5bc4 \u6c81 \u56ed \u6625 \u3000\u3000\u51c9\u79cb\u4e5d\u6708\uff0c\u5317\u5730\u8349\u8870\uff0c\u6709\u4e00\u884c\u4eba\u9a6c\uff0c\u6b63\u6cbf\u7740\u7ef5\u4e98\u5ddd\u9655\u4e24\u7701\u8fb9\u754c\u7684\u5927\u5df4\u5c71\u8109\uff0c\u653e\u9a6c\u897f\u884c\u3002 \u884c\u5728\u524d\u5934\u7684\u662f\u51e0\u4e2a\u96c4\u8d73\u8d73\u7684\u6b66\u5e08\uff0c\u4e2d\u95f4\u4e00\u8f86\u655e\u7bf7\u9aa1\u8f66\uff0c\u5750\u7740\u4e00\u4e2a\u5e74\u8fd1\u516d\u65ec\u7684\u7ec5\u58eb\uff0c\u76ae\u8884\u62ab \u98ce\uff0c\u6001\u5ea6\u96cd\u5bb9\uff0c\u4e00\u9a91\u9ad8\u5934\u5927\u9a6c\uff0c\u508d\u7740\u9aa1\u8f66\uff0c\u9a6c\u4e0a\u5750\u7740\u4e00\u4e2a\u5251\u7709\u864e\u76ee\u7684\u5c11\u5e74\uff0c\u5251\u4f69\u7405\u7136\u4f5c\u54cd\u3002 \u3000\u3000\u8fd9\u4e2a\u7bf7\u8f66\u4e2d\u7684\u7ec5\u58eb\uff0c\u6b63\u662f\u5378\u4efb\u7684\u4e91\u8d35\u603b\u7763\uff0c\u540d\u53eb\u5353\u4ef2\u5ec9\uff0c\u4ed6\u4eba\u5982\u5176\u540d\uff0c\u867d\u7136\u5386\u4efb\u5927\u5b98\uff0c \u5c1a\u7b97\u6e05\u5ec9\u3002\u53ef\u662f\u4fd7\u8bed\u8bf4\u5f97\u597d\uff1a\u201c\u53c1\u5e74\u6e05\u77e5\u5e9c\uff0c\u5341\u4e07\u96ea\u82b1\u94f6\u3002\u201d\u4f55\u51b5\u4ed6\u662f\u603b\u7763\u3002\u4ed6\u4e0d\u5fc5\u5982\u4f55\u8d2a \u6c61\uff0c\u90a3\u94b1\u7cae\u4e0a\u7684\u6298\u5934\uff0c\u4e0b\u5c5e\u7684\u9001\u793c\uff0c\u4e5f\u81ea\u4e0d\u5c11\u3002\u6240\u4ee5\u5378\u4efb\u56de\u4e61\uff0c\u4e5f\u8bf7\u4e86\u51e0\u4e2a\u51fa\u540d\u9556\u5e08\uff0c\u968f\u884c \u62a4\u9001\u3002 \u3000\u3000\u90a3\u4e2a\u5251\u7709\u864e\u76ee\u7684\u5c11\u5e74\uff0c\u5374\u4e0d\u662f\u9556\u5e08\uff0c\u4ed6\u4e4b\u968f\u884c\uff0c\u53e6\u6709\u4e00\u756a\u6765\u5386\u3002\u539f\u6765\u5353\u4ef2\u5ec9\u539f\u7c4d  \u5317\uff0c \u9600\u9605\u95e8\u5ead\uff0c\u7c2a\u7f28\u4e16\u7b2c\uff0c\u53ea\u662f\u65fa\u8d22\u4e0d\u65fa\u4e01\uff0c\u6570\u4ee3\u5355\u4f20\uff0c\u4ed6\u53ea\u6709\u4e00\u5b50\u4e00\u5b59\uff0c\u513f\u5b50\u540d\u5524\u5353\u7ee7\u8d24\uff0c\u5728 \u4eac\u4e2d\u4e3a\u5b98\uff0c\u505a\u5230\u4e86\u6237\u90e8\u4f8d\u90ce\u4e4b\u804c\uff0c\u5b59\u513f\u540d\u5353\u4e00\u822a\uff0c\u5e7c\u65f6\u968f\u7236\u8d74\u4eac\uff0c\u7b97\u6765\u4eca\u5e74\u4e5f\u8be5\u6709\u5341\u516b\u4e5d\u5c81 \u4e86\u3002\u5353\u4e00\u822a\u81ea\u5c0f\u806a\u660e\u8fc7\u4eba\uff0c\u7956\u7236\u5bf9\u4ed6\u5341\u5206\u6000\u5ff5\uff0c\u8fd9\u56de\u8f9e\u5b98\u5f52\u91cc\uff0c\u4e5f\u66fe\u4fee\u4e66\u513f\u5b50\uff0c\u53eb\u4ed6\u9001\u5b59\u513f \u56de\u4e61\u3002\u4e0d\u6599\u5b59\u513f\u6ca1\u6765\uff0c\u8fd9\u803f\u7ecd\u5357\u5374\u62ff\u7740\u4ed6\u513f\u5b50\u7684\u4fe1\u6765\u4e86\uff0c\u4fe1\u4e0a\u8bf4\uff0c\u5b59\u513f\u6b63\u5728\u82e6\u8bfb\u5f85\u8003\uff0c\u4e0d\u80fd \u5373\u56de\u3002\u8fd9\u803f\u7ecd\u5357\u4e43\u662f\u5b59\u513f\u7684\u540c\u7a97\uff0c\u9887\u6653\u6b66\u827a\uff0c\u9002\u503c\u4e5f\u6709\u4e8b\u8981\u5230  \u897f\uff0c\u8bf7\u5927\u4eba\u5e26\u4ed6\u540c\u884c\uff0c\u4e24\u4ff1 \u65b9\u4fbf\u3002\u5353\u4ef2\u5ec9\u548c\u4ed6\u95f2\u8c08\uff0c\u53d1\u73b0\u4ed6\u5bf9\u4e66\u8bd7\u5e76\u4e0d\u751a\u89e3\uff0c\u5fc3\u91cc\u60f3\u9053\uff0c\u4e66\u751f\u5b66\u5251\uff0c\u6b66\u827a\u597d\u4e5f\u6709\u9650\uff0c\u8fd8 \u6697\u7b11\u4ed6\u662f\u4e2a\u8bfb\u4e66\u4e0d\u6210\u5b66\u5251\u53c8\u4e0d\u6210\u7684\u5e73\u51e1\u5c11\u5e74\uff0c\u4e0d\u6599\u8bf7\u6765\u7684\u51e0\u4e2a\u51fa\u540d\u9556\u5934\uff0c\u5bf9\u4ed6\u90fd\u5341\u5206\u606d\u656c\uff0c \u8fd9\u5374\u4e0d\u7531\u5f97\u5353\u4ef2\u5ec9\u4e0d\u7981\u5927\u4e3a\u8be7\u5f02\u3002 \u3000\u3000\u5176\u65f6\u662f\u660e\u4e07\u5386\u56db\u5341\u53c1\u5e74\uff0c\u6ee1\u6d32\u5d1b\u8d77\u4e1c\u5317\uff0c\u65f6\u65f6\u5185\u4fb5\uff0c\u795e\u5b97\u52a0\u6d3e\u201c\u8fbd\u9977\u201d\u8fbe\u7530\u8d4b\u603b\u989d\u4e8c\u5206 \u4e4b\u4e00\u4ee5\u4e0a\uff0c\u5168\u7531\u519c\u6c11\u8d1f\u62c5\uff0c\u52a0\u4ee5\u897f\u5317\u5730\u7620\u6c11\u8d2b\uff0c\u76d7\u532a\u7eb7\u8d77\uff0c\u6240\u4ee5\u5353\u4ef2\u5ec9\u867d\u8058\u6709\u9556\u5e08\uff0c\u5e76\u6709\u4eb2 \u5175\u62a4\u9001\uff0c\u4e5f\u4e0d\u5f97\u4e0d\u63d0\u5fc3\u540a\u80c6\u3002 \u3000\u3000\u8fd9\u65e5\u6b63\u884c\u8fc7\u5df4\u5cea\u5173\uff0c\u5c71\u8fb9\u9a7f\u9053\u4e0a\u5ffd\u9a70\u8fc7\u4e24\u9a91\u5feb\u9a6c\uff0c\u524d\u884c\u7684\u51e0\u540d\u9556\u5e08\uff0c\u9f50\u90fd\u53d8\u8272\uff01 \u3000\u3000\u803f\u7ecd\u5357\u6cfc\u5587\u5587\u4e00\u9a6c\u51b2\u4e0a\uff0c\u5c0f\u58f0\u95ee\u9053\uff1a\u201c\u600e\u9ebd\uff1f\u201d\u8001\u7f25\u5934\u9053\uff1a\u201c\u90a3\u662f\u897f\u5ddd\u53cc\u715e\u3002\u201d\u803f\u7ecd\u5357 \u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u5f6d\u5bb6\u5144\u5f1f\uff0c\u4ed6\u4eec\u7684\u94c1\u7802\u638c\u4e0b\u8fc7\u51e0\u5e74\u529f\u592b\uff0c\u8981\u7559\u5fc3\u4e00\u70b9\u3002\u201d\u53cc\u715e\u5feb\u9a6c\u8fc7\u5f8c\uff0c \u5e76\u4e0d\u56de\u5934\uff0c\u8001\u9556\u5e08\u9053\uff1a\u201c\u4e0d\u50cf\u4e0b\u624b\u505a\u6848\u7684\u6a21\u6837\u3002\u201d\u803f\u7ecd\u5357\u5fae\u5fae\u4e00\u7b11\uff0c\u52d2\u4f4f\u7ef3  \uff0c\u7b49\u9aa1\u8f66\u8d76 \u4e0a\uff0c\u6de1\u7136\u7684\u5bf9\u5353\u4ef2\u5ec9\u9053\uff1a\u201c\u8001\u5927\u4eba\u4e07\u5b89\uff0c\u6ca1\u6709\u4ec0\u9ebd\uff0c\u90a3\u53ea\u662f\u4e24\u4e2a\u5c0f\u8d3c\u3002\u201d\u53c8\u8fc7\u4e86\u4e00\u4f1a\uff0c\u80cc\u5f8c \u53c8\u662f\u53c1\u9a91\u5feb\u9a6c\uff0c\u7edd\u5c18\u63a0\u8fc7\uff0c\u5bf9\u5353\u5bb6\u7684\u7bb1\u7b3c\u8f66\u8f86\uff0c\u8fde\u6b63\u773c\u4e5f\u4e0d\u77a7\u4e00\u77a7\uff0c\u8001\u9556\u5934\u8be7\u9053\uff1a\u201c\u600e\u9ebd\u9f99 \u95e8\u5e2e\u7684\u53c1\u4f4d\u8235\u4e3b\uff0c\u90fd\u540c\u65f6\u51fa\u52a8\uff0c\u83ab\u975e\u662f\u7eff\u6797\u9053\u4e2d\uff0c\u51fa\u4e86\u4ec0\u9ebd\u7d27\u6025\u7684\u4e8b\u60c5\uff1f\u201d\u803f\u7ecd\u5357\u50b2\u7136\u8bf4 \u9053\uff1a\u201c\u7ba1\u4ed6\u4ec0\u9ebd\u5f55\u6797\u9053\u4e0d\u7eff\u6797\u9053\uff1f\u82e5\u6765\u72af\u65f6\uff0c\u6211\u4e0d\u7528\u624b\u4e0a\u7684\u5175\u5668\uff0c\u53ea\u51ed\u8fd9\u4e00\u5f20\u5f39\u5f13\uff0c\u4e5f\u8981\u6253 \u5f97\u4ed6\u4eec\u843d\u82b1\u6d41\u6c34\u3002 \u201d\u9556\u5e08\u4eec\u552f\u552f\u8bfa\u8bfa\uff0c\u4e00\u5473\u5949\u627f\uff0c\u5353\u4ef2\u5ec9\u89c1\u4ed6\u795e\u8272\u5028\u50b2\uff0c\u6697\u9053\uff0c\u8fd9\u5c11\u5e74\u597d \u5927\u53e3\u6c14\u3002\u5fc3\u4e2d\u9887\u4e3a\u4e0d\u60a6\u3002 \u3000\u3000\u8f66\u8f86\u9a6c\u56db\u7ee7\u7eed\u897f\u884c\uff0c\u9ec4\u660f\u65f6\u5206\uff0c\u5df2\u5c06\u8fd1\u5f3a\u5b81\u9547\u5916\u7684\u4e03\u76d8\u5173\uff0c\u5c71\u9053\u72ed\u7a84\uff0c\u8fd9\u4e03\u76d8\u5173\u4e43\u5ddd \u8fb9\u754c\u4e00\u4e2a\u9669\u8981\u6240\u5728\uff0c\u5b83\u501a\u5c71\u9762\u6cb3\uff0c\u4e24\u5cb8\u60ac\u7826\u9ad8\u8fbe\u767e\u4e08\uff0c\u4e0b\u9762\u7684\u6cb3\u6c34\u7ed9\u5ced\u58c1\u7ea6\u675f\u6210\u53ea\u6709\u4e94\u516d\u4e08 \u9614\u7684\u6025\u6d41\uff0c\u5728\u5c71\u8c37\u4e2d\u5954\u817e\u800c\u51fa\uff0c\u5b9b\u82e5\u4e07\u9a6c\u8131  \uff0c\u6c34\u82b1\u6e85\u6210\u6d53\u96fe\u3002\u4e00\u884c\u4eba\u8d70\u51fa\u5c71\u53e3\uff0c\u89c1\u524d\u5934\u534a \u91cc\u4e4b\u5730\uff0c\u6709\u4e00\u9a91\u767d\u9a6c\u7f13\u7f13\u800c\u884c\uff0c\u9a6c\u4e0a\u4eba\u4e00\u8eab\u767d\u8272\u8863\u88f3\uff0c\u914d\u7740\u767d\u9a6c\uff0c\u66f4\u663e\u5f97\u6f47  \u8131\u4fd7\u3002\u5353\u4ef2\u5ec9 \u9053\uff1a\u201c\u8fd9\u4eba\u597d\u4f3c\u4e00\u4e2a\u4e66\u751f\uff0c\u5b64\u8eab\u65e0\u4f34\uff0c\u597d\u4e0d\u5371\u9669\u3002\u6211\u4eec\u8d76\u4e0a\u524d\u53bb\u4e0e\u4ed6\u540c\u884c\u5982\u4f55\uff1f\u201d\u803f\u7ecd\u5357\u6447 \u4e86\u6447\u5934\uff0c\u731b\u542c\u5f97\u4e00\u9635\u6e05\u8106\u7684\u94c3\u58f0\uff0c\u516d\u4e03\u9a91\u5feb\u9a6c\u81ea\u5f8c\u98de\u6765\uff0c\u970e\u5ffd\u63a0\u8fc7\u8f66\u8f86\uff0c\u524d\u9762\u90a3\u767d\u9a6c\u5c11\u5e74\u6b63 \u662f\u5230\u72ed\u7a84\u7684\u5c71\u53e3\uff0c\u8001\u9556\u5934\u60ca\u9053\uff1a\u201c\u8fd8\u4e0d\u5feb\u8ba9\uff0c\u649e\u4e0a\u4e86\u90a3\u53ef\u8981\u7cdf\u3002\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u5c71\u5773\u90a3\u8fb9\u53c8\u662f \u5c18\u571f\u5927\u8d77\uff0c\u5341\u9980\u56db\u5065\u9a6c\u4e5f\u6b63\u5411\u8fd9\u8fb9\u51b2\u6765\uff0c\u4e24\u8fb9\u9a6c\u961f\uff0c\u628a\u5c11\u5e74\u5939\u5728\u4e2d\u95f4\uff0c\u773c\u770b\u5c31\u8981\u649e\u4e0a\uff0c\u5353\u4ef2 \u5ec9\u4e0d\u7981\u5931\u58f0\u60ca\u547c\uff0c\u5374\u731b\u542c\u5f97\u90a3\u5c11\u5e74\u5927\u53eb\u4e00\u58f0\uff0c\u767d\u9a6c\u5ffd\u7136\u817e\u7a7a\u800c\u8d77\uff0c\u75be\u4f3c\u6d41\u661f\uff0c\u7adf\u7136\u8dc3\u8fc7\u4e86\u4e94 \u516d\u4e08\u7684\u6025\u6d41\uff0c\u98de\u8d8a\u6cb3\u9762\uff0c\u5230\u4e86\u5bf9\u5cb8\u3002\u8fd9\u4e24\u5e2e\u9a6c\u961f\uff0c\u9a91\u672f\u7cbe\u7edd\uff0c\u6025\u9a70\u4e4b\u4e0b\uff0c\u7a81\u7136\u731b\u7684\u52d2\u9a6c\uff0c\u4e24 \u5925\u6c47\u6210\u4e00\u5925\uff0c\u62e8\u8fc7\u9a6c\u5934\uff0c\u62e6\u4f4f\u4e86\u524d\u9762\u7684\u5c71\u53e3\u3002 \u3000\u3000\u803f\u7ecd\u5357\u4e00\u9a6c\u98de\u524d\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u597d\u6c49\u4eec\u8bf7\u501f\u8def\uff01\u201d\u4e3a\u9996\u4e00\u4e2a\u866c\u9aef\u6c49\u5b50\u53eb\u9053\uff1a\u201c\u51ed\u4ec0\u9ebd\u8981 \u6211\u4eec\u501f\u8def\uff1f\u8d2a\u5b98\u4e4b\u8d22\u4eba\u4eba\u53ef\u5f97\u3002\u201d\u803f\u7ecd\u5357\u9053\uff1a\u201c\u987b\u77e5\u4ed6\u4e0d\u662f\u8d2a\u5b98\u3002\u201d\u53e6\u4e00\u4e2a\u532a\u9996\u53eb\u9053\uff1a\u201c\u8981 \u501f\u8def\u4e5f\u4e0d\u96be\uff0c\u628a\u7bb1\u7b3c\u884c\u674e\u7559\u4e0b\u4fbf\u53ef\uff01\u201d\u803f\u7ecd\u5357\u4e00\u8a00\u4e0d\u53d1\uff0c\u7a81\u7136\u53d6\u4e0b\u80cc\u4e0a\u7684\u94c1\u5f13\uff0c\u55d6\u55d6\u55d6\u4e00\u8fde \u6570\u5f39\uff0c\u628a\u62a2\u4e0a\u6765\u7684\u4eba\u4e00\u9f50\u6253\u5012\uff0c\u90a3\u866c\u9aef\u6c49\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u803f\u7ecd\u5357\u5f03\u5f39\u6362\u7bad\uff0c\u547c\u7684\u4e00\u7bad\uff0c\u628a\u76d7\u515a \u4e2d\u7684\u4e00\u9762\u9ed1\u65d7\u5c04\u65ad\uff0c\u90a3\u866c\u9aef\u6c49\u5b50\uff0c\u8fd9\u624d\u52c3\u7136\u53d8\u8272\uff0c\u75be\u51b2\u6570\u4e08\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u7eff\u6797 \u89c4\u77e9\uff1f\u201d\u803f\u7ecd\u5357\u66f4\u4e0d\u6253\u8bdd\uff0c\u5f39\u4f3c\u6d41\u661f\uff0c\u51b0\u96f9\u9ab0\u7684\u5411\u90a3\u6c49\u5b50\u6253\u53bb\uff01 \u3000\u3000\u90a3\u866c\u9aef\u6c49\u5b50\u75be\u82e5\u98d8\u98ce\uff0c\u4e00\u53e3\u539a\u80cc\u8d64\u94dc\u5200\u5de6\u6321\u6709\u78d5\uff0c\u628a\u51b0\u96f9\u822c\u5c04\u6765\u7684\u5f39\u5b50\uff0c\u78d5\u5f97\u56db\u9762\u7eb7 \u98de\uff0c\u5b9b\u5982\u843d\u4e0b\u6ee1\u5929\u5f39\u96e8\uff0c\u803f\u7ecd\u5357\u8d8a\u6253\u8d8a\u6025\uff0c\u90a3\u6c49\u5b50\u6e10\u60ed\u6709\u70b9\u624b\u5fd9\u811a\u4e71\uff0c\u76d7\u515a\u4e2d\u4e00\u4e2a\u6d53\u7709\u5927\u773c \u7684\u6c49\u5b50\u559d\u58f0\uff1a\u201c\u6765\u800c\u4e0d\u5f80\u975e\u793c\u4e5f\uff01\u201d\u4e5f\u53d6\u4e0b\u4e00\u5f20\u5f39\u5f13\uff0c\u55e4\u55e4\u6570\u58f0\uff0c\u5ffd\u7136\u53d1\u51fa\u51e0\u9053\u6df1\u84dd\u8272\u7684\u706b \u7130\uff0c\u4ea4\u53c9\u98de\u6765\uff0c\u803f\u7ecd\u5357\u4e00\u5f20\u5f39\u5f13\uff0c\u4e0d\u80fd\u4e24\u7528\uff0c\u6253\u843d\u4e86\u8fce\u9762\u800c\u6765\u7684\u201c\u86c7\u7130\u7bad\u201d\uff0c\u5374\u4e0d\u80fd\u6321\u4f4f\u5c04 \u5411\u5353\u5bb6\u7bb1\u7b3c\u7684\u706b\u7bad\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u5927\u8f66\u4e0a\u4e00\u53ea\u539a  \u5e03\u888b\u7adf\u7136\u7740\u706b\u71c3\u70e7\uff0c\u54d7\u5566\u5566\u5012\u4e0b\u4e86\u4e00\u5806 \u767d\u82b1\u82b1\u7684\u94f6\u5b50\u3002\u90a3\u866c\u9aef\u6c49\u5b50\u6447\u4e86\u6447\u5934\uff0c\u9762\u5c71\u663e\u7136\u9732\u51fa\u5931\u671b\u7684\u795e\u6c14\uff0c\u803f\u7ecd\u5357\u5f39\u4f3c\u8fde\u73e0\uff0c\u65bd\u5c55\u51fa \u201c\u516b\u65b9\u98ce\u96e8\u201d\u7684\u795e\u5f39\u7edd\u6280\uff0c\u866c\u9aef\u6c49\u5b50\u731d\u4e0d\u53ca\u9632\uff0c\u535c\u7684\u4e00\u58f0\uff0c\u5de6\u624b\u5173\u8282\u7adf\u7ed9\u5f39\u4e38\u6253\u4e2d\uff0c\u4e00\u4e2a\u7bad \u6b65\u8df3\u51fa\u5708\u5b50\uff0c\u5ffd\u7136\u62b1\u62f3\u53eb\u9053\uff1a\u201c\u6b66\u5f53\u5c71\u795e\u5f39\u5999\u6280\uff0c\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u54b1\u5f1f\u5144\u8d70\u4e86\u773c.\u591a\u591a\u5f97\u7f6a \u4e86\uff01\u201d\u90a3\u53d1\u86c7\u7130\u7bad\u7684\u6c49\u5b50\u4e5f\u7ffb\u8eab\u8de8\u4e0a\u9a6c\u80cc\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u7d2b\u9633\u9053\u957f\u4e4b\u524d\uff0c\u8bf7\u4ee3\u54b1\u5f1f\u5144\u95ee\u5019\uff0c \u5c31\u8bf4\u662f\u706b\u7075\u733f\u548c\u7ffb\u5c71\u864e\u8c22\u4ed6\u8001\u4eba\u5bb6\u5f53\u5e74\u4e0d\u6740\u4e4b\u6069\u5427\uff1f\u201d\u8bf4\u5b8c\u4e4b\u5f8c\uff0c\u4e00\u58f0\u80e1\u54e8\uff0c\u624b\u4e0b\u65e9\u6276\u8d77\u4e86 \u53d7\u4f24\u7684\u540c\u5925\uff0c\u9000\u51fa\u5c71\u8c37\u3002 \u3000\u3000\u803f\u7ecd\u5357\u653e\u4e0b\u5f39\u5f13\uff0c\u4ef0\u5929\u5927\u7b11\u3002\u5ffd\u7136\u80cc\u5f8c\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u771f\u597d\u5f39\u5f13\uff01\u201d\u803f\u7ecd\u5357\u6115\u7136\u56de \u987e\uff0c\u7adf\u7136\u662f\u90a3\u767d\u9a6c\u5c11\u5e74\uff0c\u4e0d\u77e5\u4ec0\u9ebd\u65f6\u5019\uff0c\u53c8\u4ece\u5bf9\u5cb8\u7eb5\u9a6c\u8fc7\u6765\uff0c\u4f17\u4eba\u521a\u624d\u7d27\u5f20\u5fd9\u4e71\uff0c\u7adf\u6ca1\u89c9 \u5bdf\u3002\u803f\u7ecd\u5357\u9053\uff1a\u201c\u96d5\u866b\u5c0f\u6280\uff0c\u8d3b\u7b11\u65b9\u5bb6\u3002\u201d\u767d\u9a6c\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u6211\u90a3\u91cc\u662f\u4ec0\u9ebd\u65b9\u5bb6\uff0c\u53ea\u9760\u7740\u8fd9 \u56db\u9a6c\u8fd8\u7b97\u4e0d\u9519\uff0c\u624d\u9003\u4e86\u5927\u96be\u3002\u201d\u5353\u4ef2\u5ec9\u4e0b\u8f66\u7aef\u8be6\u90a3\u767d\u9a6c\u5c11\u5e74\uff0c\u89c1\u4ed6\u9a6c\u80cc\u7a7a\u7a7a\uff0c\u6beb\u65e0\u884c\u674e\uff0c\u8bf4 \u8bdd\u6587\u8bcc\u8bcc\u7684\uff0c\u5b8c\u5168\u662f\u4e2a\u4e66\u751f\u6a21\u6837\u3002\u56e0\u95ee\u9053\uff1a\u201c\u8db3\u4e0b\u53ef\u662f\u51fa\u95e8\u6e38\u5b66\u5417\uff1f\u73b0\u4eca\u8def\u9014\u4e0d\u9756\uff0c\u8dcb\u6d89\u957f \u9014\uff0c\u5371\u9669\u5f97\u5f88\u5440\u3002\u201d\u767d\u9a6c\u5c11\u5e74\u8eac\u8eab\u7b54\u9053\uff1a\u201c\u665a\u751f\u5728\u5ef6\u5b89\u5e9c\u5165\u5b66\uff0c\u6025\u7740\u8981\u56de\u4e61\u8d76\u8003\u3002\u8001\u4f2f\u53f0 \u752b\uff0c\u4e0d\u6562\u8bf7\u95ee\u3002\u201d\u5353\u4ef2\u5ec9\u5fae\u7b11\u9053\u4e86\u59d3\u540d\u3002\u767d\u9a6c\u5c11\u5e74\u60f6\u6050\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u4e61\u5148\u8f88\u5353\u8001\u5927\u4eba\uff0c\u5931 \u656c\uff0c\u5931\u656c\uff01\u201d\u81ea\u62a5\u59d3\u540d\uff0c\u53eb\u505a\u738b\u7167\u5e0c\uff0c\u4e24\u4eba\u8c08\u5f97\u5f88\u662f\u6ca1\u7f18\uff0c\u738b\u7167\u5e0c\u9053\uff1a\u201c\u665a\u751f\u5b64\u8eab\u65e0\u4f34\uff0c\u613f \u968f\u9aa5\u5c3e\uff0c\u6258\u8001\u5927\u4eba\u5e87\u62a4\u3002\u201d\u803f\u7ecd\u5357\u7728\u4e86\u51e0\u7728\u773c\u775b\uff0c\u5353\u4ef2\u5ec9\u5e74\u8001\u5fc3\u6148\uff0c\u6168\u7136\u8bf4\u9053\uff1a\u201c\u5f7c\u6b64\u540c \u884c\uff0c\u90a3\u6709\u4ec0\u9ebd\u788d\u4e8b\uff1f\u8db3\u4e0b\u4f55\u5fc5\u8a00\u8c22\u3002\u201d\u7adf\u81ea\u5141\u4e86\u3002\u803f\u7ecd\u5357\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u4e00\u4ecb\u4e66\u751f\uff0c\u7adf\u9a91 \u5f97\u8fd9\u56db\u795e\u9a79\uff0c\u5b9e\u662f\u53ef\u4f69\u3002\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u8fd9\u56db\u9a6c\u4e43\u662f\u897f\u57df\u7684\u5927\u6c57\u9a6c\u79cd\uff0c\u540d\u4e3a\u7167\u591c\u72ee\u5b50\uff0c\u867d\u7136 \u795e\u9a8f\uff0c\u5374\u5f88\u9a6f\u826f\u3002\u201d\u897f\u5317\u591a\u540d\u9a6c\uff0c\u666e\u901a\u7684\u4eba\u90fd\u61c2\u9a91\u672f\uff0c\u5353\u4ef2\u5ec9\u867d\u89c9\u8fd9\u56db\u9a6c\u597d\u5f97\u51fa\u5947\uff0c\u4e5f\u6ca1\u7591 \u5fc3\u3002 \u3000\u3000\u5353\u5bb6\u8058\u6765\u7684\u90a3\u51e0\u540d\u9556\u5e08\u521a\u624d\u4e00\u76f4\u62a4\u7740\u8f66\u8f86\uff0c\u8fd9\u65f6\u90fd\u5df2\u56f4\u5728\u803f\u7ecd\u5357\u8eab\u8fb9\uff0c\u7b49\u5353\u4ef2\u5ec9\u7684\u8bdd\u544a \u4e00\u6bb5\u843d\uff0c\u5ffd\u7136\u9f50\u5411\u803f\u7ecd\u5357\u4e0b\u62dc\uff0c\u90a3\u8001\u9556\u5934\u6267\u793c\u66f4\u606d\uff0c\u534a\u5c48\u7740\u819d\uff0c\u6253\u4e2a\u5343\u513f\u8bdd\u9053\uff1a\u201c\u8001\u673d\u773c \u62d9\uff0c\u867d\u7136\u65e9\u5df2\u77e5\u9053\u803f\u82f1\u96c4\u662f\u4e2a\u5927\u884c\u5bb6\uff1b\u5374\u8fd8\u4e0d\u77e5\u803f\u82f1\u96c4\u7adf\u662f\u6b66\u5f53\u9ad8\u5f1f\uff0c\u8001\u673d\u8981\u8bf7\u803f\u82f1\u96c4\u8d4f\u53e3 \u996d\u5403\uff01\u201d\u5353\u4ef2\u5ec9\u542c\u4e86\uff0c\u695e\u7136\u4e0d\u89e3\u3002 \u3000\u3000\u803f\u7ecd\u5357\u5fae\u7b11\u4e00\u7b11\uff0c\u628a\u8001\u9556\u5934\u53cc\u624b\u6276\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u803f\u67d0\u4e0d\u624d\uff0c\u65e2\u7136\u6311\u8d77\u6881\u5b50\uff0c\u90a3\u5c31\u7edd\u4e0d\u4f1a \u4e2d\u9014\u6492\u803f\u7ecd\u5357\u5fae\u7b11\u624b\uff0c\u803f\u67d0\u6b64\u6765\uff0c\u4e0d\u662f\u4fdd\u9556\uff0c\u800c\u662f\u4e3a\u670b\u53cb\u4e0d\u60dc\u4e24\u808b\u63d2\u5200\uff0c\u8001\u9556\u5934\uff0c\u8bf7\u4f60\u653e \u5fc3\u3002\u201d\u5353\u4ef2\u5ec9\u542c\u5f97\u76ca\u53d1\u7eb3\u7f55\u3002 \u3000\u3000\u539f\u6765\u8fd9\u803f\u7ecd\u5357\u5e76\u975e\u8bfb\u4e66\u58eb\u5b50\uff0c\u800c\u662f\u5f53\u4eca\u5f0f\u5f53\u6d3e\u7684\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\u3002\u6b66\u5f53\u5c11\u6797\u4e43\u662f\u6b66\u6797\u4e2d\u7684\u6cf0 \u5c71\u5317\u6597\uff0c\u58f0\u5a01\u751a\u5927\u3002\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u4eba\u7d2b\u9633\u9053\u957f\uff0c\u6b66\u529f\u5353\u7edd\u3002\u4ed6\u548c\u56db\u4e2a\u5e08\u5f1f\uff1a\u9ec4\u53f6\u9053\u4eba\u3001\u767d\u77f3 \u9053\u4eba\u3001\u7ea2\u4e91\u9053\u4eba\u3001\u9752  \u9053\u4eba\uff0c\u5408\u79f0\u201c\u6b66\u5f53\u4e94\u8001\u201d\uff0c\u95e8\u4e0b\u5f1f\u5b50\uff0c\u6570\u4ee5\u767e\u8ba1\uff0c\u8fd9\u803f\u7ecd\u5357\u4e43\u767d\u77f3\u9053 \u4eba\u7684\u9996\u5f92\uff0c\u5728\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\u4e2d\uff0c\u662f\u4e2a\u51fa\u7c7b\u62d4\u8403\u7684\u4eba\u7269\u3002 \u3000\u3000\u521a\u624d\u62e6\u8def\u6253\u52ab\u7684\u90a3\u4e2a\u866c\u9aef\u6c49\u5b50\uff0c\u540d\u53eb\u7ffb\u5c71\u864e\u5468\u540c\uff0c\u90a3\u6d53\u7709\u5927\u773c\u7684\u6c49\u5b50\uff0c\u5219\u53eb\u706b\u7075\u733f\u6731\u5b9d \u693f\uff0c\u540c\u662f\u5ddd  \u8fb9\u5883\u7684\u608d\u532a\uff0c\u6b66\u529f\u8fd8\u5728\u897f\u5ddd\u53cc\u715e\u4e4b\u4e0a\u3002\u6b66\u5f53\u6d3e\u7d20\u4ee5\u6b66\u6797\u6b63\u5b97\u81ea\u5c45\uff0c\u6240\u4ee5\u5386\u4ee3\u76f8 \u4f20\uff0c\u5b9a\u4e0b\u4e24\u6761\u89c4\u77e9\uff1a\u4e00\u4e0d\u8bb8\u4f5c\u5f3a\u76d7\uff0c\u4e8c\u4e0d\u8bb8\u4f5c\u9556\u5e08\u3002\u803f\u7ecd\u5357\u4ee5\u6b66\u5f53\u95e8\u4eba\u7684\u8eab\u4efd\uff0c\u66ff\u5de8\u5b98\u62a4\u9001 \u884c\u674e\uff0c\u90a3\u662f\u6781\u5c11\u6709\u4e4b\u4e8b\u3002\u8001\u9556\u5934\u4e00\u6765\u6015\u706b\u7075\u733f\u7684\u540c\u515a\u62a5\u590d\uff0c\u4e8c\u6765\u5b9e\u5728\u731c\u4e0d\u900f\u803f\u7ecd\u5357\u7684\u6765\u610f\uff0c \u6240\u4ee5\u624d\u8bf4\u51fa\u90a3\u4e00\u9999\u8bdd\uff0c\u5c06\u803f\u7ecd\u5357\u5957\u4f4f\u3002 \u3000\u3000\u5353\u4ef2\u5ec9\u8fd9\u65f6\u624d\u6653\u5f97\u803f\u7ecd\u5357\u8eab\u6000\u7edd\u6280\uff0c\u4e0d\u660e\u81ea\u5df1\u7684\u5b59\u513f\u600e\u6837\u4f1a\u7ed3\u8bc6\u5982\u6b64\u5f02\u4eba\u3002\u53ea\u6709\u518d\u53c1\u9053 \u8c22\u3002\u803f\u7ecd\u5357\u795e\u91c7\u98de\u626c\uff0c\u5bf9\u5353\u4ef2\u5ec9\u4e5f\u663e\u5f97\u9887\u4e3a\u50b2\u5cb8\u3002\u5353\u4ef2\u5ec9\u60f3\u67e5\u95ee\u4ed6\u548c\u5b59\u513f\u7ed3\u8bc6\u7684\u7ecf\u8fc7\uff0c\u4ed6\u5f80 \u5f80\u76fc\u987e\u5de6\u53f3\u800c\u8a00\u4ed6\uff0c\u751a\u6216\u53ea\u662f\u7b11\u800c\u4e0d\u7b54\u3002 \u3000\u3000\u90a3\u767d\u9a6c\u5c11\u5e74\u738b\u7167\u5e0c\u5374\u663e\u5f97\u5341\u5206\u6587\u9759\uff0c\u4e00\u8def\u4e0a\u5bf9\u5353\u4ef2\u5ec9\u548c\u803f\u7ecd\u5357\u90fd\u6267\u793c\u751a\u606d\u3002\u8d70\u4e86\u4e24\u5929\uff0c \u5df2\u8fc7\u4e86\u5f3a\u5b81\uff0c\u5c06\u5230\u9633\u5e73\u5173\u4e86\uff0c\u6cbf\u8def\u4e0a\u4e0d\u7edd\u6709\u5f62\u8ff9\u53ef\u7591\u7684\u4eba\u7269\uff0c\u53c1\u53c1\u4e94\u4e94\uff0c\u6216\u4e58\u5feb\u9a6c\uff0c\u6216\u7b56\u9aa1 \u8f66\uff0c\u5728\u9a7f\u9053\u4e0a\u51fa\u6ca1\u3002\u8001\u9556\u5934\u4e00\u770b\u5c31\u77e5\u662f\u8e29\u5e95\u8ddf\u8e2a\u7684\u7eff\u6797\u4eba\u7269\uff0c\u6574\u6574\u4e24\u5929\uff0c\u63d0\u5fc3\u540a\u80c6\uff0c\u5e78\u5f97\u4e00 \u70b9\u4e8b\u60c5\u90fd\u6ca1\u53d1\u751f\u3002\u8fc7\u4e86\u9633\u5e72\u5173\u5f8c\uff0c\u90a3\u4e9b\u5f62\u8ff9\u53ef\u7591\u7684\u4eba\u7269\u5ffd\u7136\u90fd\u4e0d\u89c1\u4e86\u3002\u8fd9\u665a\uff0c\u6765\u5230\u4e86\u5927\u5b89 \u9a7f\uff0c\u5353\u4ef2\u5ec9\u9053\uff1a\u201c\u660e\u65e5\u8fc7\u4e86\u5b9a\u519b\u5c71\uff0c\u524d\u9762\u4fbf\u662f\u5766\u9014\u4e86\u3002\u201d\u9556\u5e08\u4eec\u4e5f\u677e\u4e86\u53e3\u6c14\uff0c\u53ea\u6709\u803f\u7ecd\u5357\u5374 \u663e\u5f97\u7279\u522b\u7d27\u5f20\uff0c\u548c\u5728\u8def\u4e0a\u7684\u95f2\u9002\u795e\u60c5\uff0c\u5b8c\u5168\u4e24\u6837\u3002 \u3000\u3000\u4e00\u884c\u4eba\u5728\u9547\u4e0a\u6700\u5927\u7684\u5ba2\u5e97\u5b89\u6b47\uff0c\u767d\u9a6c\u5c11\u5e74\u738b\u7167\u5e0c\u5ffd\u7136\u5bf9\u5353\u4ef2\u5ec9\u6df1\u6df1\u4e00\u63d6\uff0c\u6717\u58f0\u8bf4\u9053\uff1a \u201c\u665a\u751f\u4e00\u8def\u4e0a\u591a\u627f\u5e87\u62a4\uff0c\u4e0d\u6562\u6b3a\u7792\uff0c\u665a\u751f\u6709\u4e9b\u5389\u5bb3\u7684\u4ec7\u5bb6\uff0c\u4e00\u8def\u8ddf\u8e2a\uff0c\u82e5\u7136\u9003\u5f97\u4eca\u665a\uff0c\u4fbf\u53ef \u65e0\u4e8b\u3002\u4eca\u665a\u4e07\u4e00\u6709\u98ce\u5439\u8349\u524d\uff0c\u8001\u5927\u4eba\u4e0d\u5fc5\u60ca\u6050\u3002\u53ea\u8981\u6302\u8d77\u4e91\u8d35\u603b\u7763\u7684\u706f\u7b3c\uff0c\u5927\u534a\u4e0d\u4f1a\u6ce2 \u53ca\u3002\u201d\u5353\u4ef2\u5ec9\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\u8001\u9556\u5934\u66fe\u518d\u53c1\u53ee\u5631\uff0c\u5728\u8def\u4e0a\u53ea\u53ef\u626e\u4f5c\u5ba2\u5546\uff0c\u5343\u4e07\u4e0d\u80fd\u62ac\u51fa\u5b98 \u8854\u3002\u4e8b\u7f18\u7eff\u6797\u5927\u8c6a\uff0c\u6700\u559c\u6b22\u52ab\u63a0\u5378\u4efb\u5927\u5b98\u3002\u81ea\u5df2\u53ea\u9053\u8fd9\u5c11\u5e74\u4e43\u662f\u4e00\u4ecb\u4e66\u751f\uff0c\u90a3\u6599\u4ed6\u4e5f\u662f\u6c5f\u6e56 \u4eba\u7269\u3002\u81ea\u5df2\u548c\u4ed6\u975e\u4eb2\u975e\u6545\uff0c\u77e5\u4ed6\u5b89\u7684\u4ec0\u9ebd\u6765\u610f\uff01\u6b63\u5728\u8e0c\u8e87\uff0c\u803f\u7ecd\u5357\u53cc\u773c\u4e00\u7ffb\uff0c\u62a2\u7740\u7b54\u9053\uff1a \u201c\u4e8b\u5230\u5982\u4eca\uff0c\u5408\u5219\u4e24\u5229\uff0c\u5206\u5219\u4e24\u5371\uff01\u8db3\u4e0b\u610f\u601d\uff0c\u8001\u5927\u4eba\u4e00\u5b9a\u7167\u529e\uff01\u54b1\u4eec\u5f7c\u6b64\u8bb2\u660e\uff0c\u5927\u5bb6\u53ef\u8981 \u5408\u529b\u9f50\u5fc3\uff0c\u540c\u5fa1\u4eca\u665a\u52ab\u96be\uff01\u201d \u3000\u3000\u738b\u7167\u5e0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u4e2a\u81ea\u7136\u3002\u201d\u5728\u5ba2\u5e97\u91cc\u81ea\u5df1\u5360\u4e86\u4e00\u5ea7\u82b1\u5385\uff0c\u5f53\u4e2d\u6446\u4e86\u4e00\u5f20\u7d2b\u6a80\u9999 \u684c\uff0c\u53eb\u5e97\u5bb6\u70eb\u4e86\u4e24\u5927\u58f6\u9648\u5e74\u82b1\u96d5\uff0c\u684c\u4e0a\u63d2\u7740\u4e24\u679d\u660e\u6643\u6643\u7684\u5927\u725b\u6cb9\u70db\uff0c\u968f\u624b\u628a\u9a6c\u978d\u548c\u8e0f\u8e6c\u4e22\u5728 \u5899\u89d2\uff0c\u5bf9\u803f\u7ecd\u5357\u9053\uff1a\u201c\u4f60\u4eec\u8eb2\u5230\u4e24\u8fb9\u53a2\u623f\u91cc\u53bb\uff0c\u975e\u6211\u547c\u5524\uff0c\u5207\u52ff\u51fa\u6765\u3002\u201d\u8001\u9556\u5934\u4e0e\u803f\u7ecd\u5357\u89c1 \u4ed6\u884c\u5f84\u5947\u602a\uff0c\u7adf\u662f\u89c1\u591a\u8bc6\u5e7f\uff0c\u4e5f\u6478\u4e0d\u900f\u4ed6\u662f\u4f55\u8def\u9053\u3002 \u3000\u3000\u6714\u98ce\u9e23\u7b33\uff0c\u661f\u6a2a\u6597\u8f6c\uff0c\u591c\u5df2\u6e10\u6df1\uff0c\u4e07\u7c41\u4ff1\u5bc2\uff0c\u738b\u7167\u5e0c\u72ec\u5750\u5385\u4e2d\uff0c\u51dd\u795e\u5916\u671b\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c \u5353\u5bb6\u81ea\u5353\u4ef2\u5ec9\u4ee5\u4e0b\uff0c\u90fd\u4e0d\u6562\u7761\uff0c\u8001\u9556\u5934\u9053\uff1a\u201c\u96be\u9053\u4ed6\u5c31\u8fd9\u6837\u7684\u5750\u5230\u5929\u660e\uff1f\u201d\u803f\u7ecd\u5357\u5ffd\u7136\u5618\u58f0 \u8bf4\u9053\uff1a\u201c\u7981\u58f0\uff0c\u6709\u4eba\u6765\u4e86\uff01\u201d \u3000\u3000\u7aef\u5750\u7740\u7684\u738b\u7167\u5e0c\u7a81\u7136\u628a\u9152\u58f6\u4e00\u4e3e\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u8fdc\u6765\uff0c\u5931\u8fce\uff0c\u5931\u8fce\uff01\u201d\u95e8\u5916\u5927\u8e0f\u6b65 \u7684\u8d70\u8fdb\u4e86\u56db\u6761\u5927\u6c49\uff0c\u4e3a\u9996\u7684\u53cc\u76ee\u70af\u70af\uff0c\u65c1\u82e5\u65e0\u4eba\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u670b\u53cb\uff0c\u7701\u4e8b\u7684\u5feb\u8ddf\u6211\u53bb\uff01\u201d \u738b\u7167\u5e0c\u7b11\u9053\uff1a\u201c\u4ec0\u9ebd\u4e8b\u554a\uff01\u201d\u90a3\u5927\u6c49\u9762\u8272\u4e00\u6c89\uff0c\u6b63\u60f3\u53d1\u4f5c\uff0c\u5ffd\u89c1\u53a2\u623f\u5916\u60ac\u7740\u4e91\u8d35\u603b\u7763\u5b98\u8854\u7684 \u706f\u7b3c\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u505a\u4ec0\u9ebd\u6765\u7684\uff1f\u4f60\u4e0d\u662f\u2026\u2026\u201d\u738b\u7167\u5e0c\u622a\u7740\u8bf4\u9053\uff1a\u201c\u4fdd\u9556\u6765\u7684\uff0c \u5404\u4f4d\u770b\u5728\u5c0f\u5f1f\u521d\u521d\u51fa\u9053\uff0c\u4e0d\u8981\u7838\u574f\u6211\u7684\u996d\u7897\uff0c\u522b\u5904\u53d1\u8d22\u53bb\u5427\u3002\u201d\u90a3\u6c49\u5b50\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9a82 \u9053\uff1a\u201c\u4f60\u770b\u9519\u4e86\u4eba\uff01\u201d\u53cc\u81c2\u4e00\u632f\uff0c\u731b\u7684\u5411\u53a2\u623f\u6251\u53bb\u3002 \u3000\u3000\u623f\u4e2d\u7684\u5353\u4ef2\u5ec9\u5931\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4eac\u4e2d\u7684\u9526\u8863\u536b\u3002\u201d\u539f\u6765\u9526\u8863\u536b\u4e43\u662f\u671d\u5ef7\u7684\u7279\u52a1\u673a\u5173\uff0c\u8fd9 \u4e3a\u9996\u7684\u6c49\u5b50\u662f\u9526\u8863\u536b\u7684\u4e00\u4e2a\u6307\u6325\uff0c\u540d\u53eb\u77f3\u6d69\uff0c\u5353\u4ef2\u5ec9\u4ee5\u524d\u5728\u4e91\u9ec4\u603b\u7763\u4efb\u5185\u4e4b\u65f6\uff0c\u624b\u4e0b\u4e00\u4e2a\u5b98 \u5458\u72af\u4e86\u6848\u4ef6\uff0c\u4eac\u4e2d\u6d3e\u9526\u8863\u536b\u6765\u63d0\u89e3\u72af\u5b98\uff0c\u6b63\u5de7\u5c31\u662f\u8fd9\u77f3\u6d69\u7387\u9886\uff0c\u6240\u4ee5\u8ba4\u5f97\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u77f3\u6d69\u4e00\u4e2a\u7bad\u6b65\u8df3\u8fd1\u53a2\u623f\uff0c\u803f\u7ecd\u5357\u81ea\u5185\u7a9c\u51fa\uff0c\u53f3\u81c2\u4e00\u683c\uff0c\u559d\u9053\uff1a\u201c\u4ec0\u9ebd \u4eba\uff1f\u6562\u60ca\u8001\u5927\u4eba\u7684\u9a7e\uff01\u201d\u53cc\u81c2\u4e00\u4ea4\uff0c\u4e24\u4eba\u90fd\u7ed9\u9707\u9000\u51e0\u6b65\u3002\u5353\u4ef2\u5ec9\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u77f3\u6307\u6325\uff0c\u662f\u5351 \u804c\u5728\u6b64\uff0c\u53ef\u662f\u7687\u4e0a\u6709\u4ec0\u9ebd\u5723\u65e8\u8981\u5ba3\u53ec\u5351\u804c\u9ebd\uff1f\u201d\u5728\u660e\u4e00\u4ee3\uff0c\u7687\u5e1d\u5bf9\u4ed8\u5927\u81e3\u7d20\u6765\u6b8b\u9177\u5be1\u6069\uff0c\u5e38 \u5e38\u56e0\u4e00\u70b9\u5c0f\u4e8b\uff0c\u5c31\u7ed9\u9526\u8863\u536b\u63d0\u53bb\u51cc\u8fdf\u5904\u6b7b\uff0c\u5353\u4ef2\u5ec9\u521a\u521a\u5378\u4efb\uff0c\u8fd8\u62c5\u5fc3\u7687\u5e1d\u662f\u8981\u5c06\u4ed6\u89e3\u4eac\uff0c\u58f0 \u8c03\u90fd\u98a4\u6296\u4e86\uff0c\u77f3\u6d69\u51dd\u7738\u4e00\u770b\uff0c\u4f9d\u7a00\u8ba4\u5f97\uff0c\u53eb\u9053\uff1a\u201c\u679c\u7136\u662f\u5353\u8001\u5927\u4eba\u5728\u6b64\uff1f\u5c0f\u7684\u6349\u62ff\u94a6\u72af\uff0c\u65e0 \u610f\u5192\u72af\uff0c\u8bf7\u591a\u591a\u5305\u6db5\u6055\u7f6a\uff01\u201d\u53c8\u7b11\u9053\uff1a\u201c\u7687\u4e0a\u5bf9\u5353\u5927\u4eba\u751a\u662f\u5173\u6000\uff0c\u5e38\u5e38\u63d0\u8d77\uff0c\u8bf4\u5353\u5927\u4eba\u662f\u4e2a \u597d\u5b98\u3002\u201d\u5353\u4ef2\u5ec9\u60ca\u9b42\u7a0d\u5b9a\uff0c\u6025\u5fd9\u4f5c\u63d6\u8bf7\u4ed6\u559d\u9152\u3002\u77f3\u6d69\u9053\uff1a\u201c\u5353\u5927\u4eba\u8fd9\u6837\u5ba2\u6c14\uff0c\u6298\u6b7b\u5c0f\u7684\u4e86\u3002 \u5c0f\u7684\u5723\u65e8\u5728\u8eab\uff0c\u4e0d\u6562\u4e45\u7559\uff0c\u8001\u5927\u4eba\u5305\u6db5\u5219\u4e2a\u3002\u201d\u7387\u9886\u53c1\u4e2a\u9526\u8863\u536b\u9000\u51fa\uff0c\u4e34\u884c\u524d\u5bf9\u803f\u7ecd\u5357\u548c\u738b \u7167\u5e0c\u6df1\u6df1\u770b\u4e86\u4e24\u773c\uff0c\u5927\u58f0\u7b11\u9053\uff1a\u201c\u5353\u5927\u4eba\u8bf7\u7684\u8fd9\u4e24\u4e2a\u4fdd\u9556\uff0c\u771f\u662f\u786c\u5f97\u5f88\u554a\uff01\u201d \u3000\u3000\u77f3\u6d69\u8d70\u5f8c\uff0c\u803f\u7ecd\u5357\u4e00\u770b\uff0c\u53ea\u89c1\u5730\u4e0a\u5341\u6765\u4e2a\u8db3\u5370\uff0c\u6df1\u9677\u534a\u5bf8\u6709\u591a\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u5974 \u624d\uff0c\u5c31\u662f\u559c\u6b22\u70ab\u9732\u6b66\u529f\uff0c\u90a3\u6bd4\u5f97\u4e0a\u6211\u8fd9\u738b\u8d24\u5f1f\u6df1\u85cf\u82e5\u865a\u3002\u201d\u623f\u4e2d\u7684\u5353\u4ef2\u5ec9\u5ffd\u7136\u6025\u58f0\u53eb\u9053\uff1a \u201c\u803f\u8d24\u4f84\uff0c\u5feb\u6765\uff0c\u5feb\u6765\uff1f\u201d \u3000\u3000\u5353\u4ef2\u5ec9\u8001\u65bc  \u6d77\uff0c\u60ca\u9b42\u7a0d\u5b9a\uff0c\u84e6\u7136\u60f3\u8d77\uff1a\u4eac\u4e2d\u7684\u9526\u8863\u536b\uff0c\u8ffd\u8e2a\u81f3\u6b64\uff0c\u90a3\u767d\u9a6c\u5c11\u5e74\u5fc5\u5b9a\u662f \u4e2a\u91cd\u8981\u94a6\u72af\uff0c\u81ea\u5df2\u53d7\u4e86\u4ed6\u7684\u5229\u7528\uff0c\u505a\u4e86\u94a6\u72af\u7684\u6321\u7bad\u724c\uff0c\u65e5\u5f8c\u88ab\u7687\u4e0a\u67e5\u77e5\uff0c\u8fd9\u53ef\u662f\u6284\u5bb6\u4e4b\u7f6a\u3002 \u8fd9\u65f6\u4e5f\u987e\u4e0d\u5f97\u4ea4\u6d45\u8a00\u6df1\uff0c\u6025\u5fd9\u628a\u803f\u7ecd\u5357\u62db\u6765\uff0c\u6084\u6084\u8bf4\u4e86\u3002\u803f\u7ecd\u5357\u51b7\u51b7\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u6211 \u65e9\u5df2\u770b\u51fa\u3002\u201d\u5353\u4ef2\u5ec9\u5c1a\u5f85\u8bf4\u8bdd\uff0c\u4ed6\u5df2\u7fe9\u7136\u8d70\u51fa\u3002 \u3000\u3000\u5385\u5802\u4e0a\u70db\u5f71\u6447\u7ea2\uff0c\u738b\u7167\u5e0c\u5927\u676f\u5927\u676f\u7684\u559d\u9152\uff0c\u803f\u7ecd\u5357\u9762\u8272\u4e00\u6c89\uff0c\u563f\u563f\u7b11\u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u4f60\u771f \u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u611a\u5144\u4f69\u670d\u4e4b\u81f3\uff01\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u803f\u5144\u4e0d\u5fc5\u53d1\u6012\uff0c\u5c0f\u5f1f\u662f\u4e0d\u5f97\u5df2\u800c\u4e3a \u4e4b\u3002\u201d\u803f\u7ecd\u5357\u53cc\u773c\u4e00\u8f6c\uff0c\u500f\u5730\u4e00\u624b\u6293\u6765\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u4f60\u80c6\u6562\u628a\u6211\u6b66\u5f53\u95e8\u4eba\u620f\u5f04\uff1f\u201d\u738b\u7167\u5e0c \u80a9\u5934\u4e00\u4fa7\uff0c\u803f\u7ecd\u5357\u5de6\u638c\u547c\u7684\u4e00\u58f0\uff0c\u6253\u5728\u4ed6\u7684\u80f8\u4e0a\uff0c\u738b\u7167\u5e0c\u5fae\u5fae\u4e00\u7b11\uff0c\u808c\u8089\u9661\u7136\u4e00\u7f29\uff0c\u803f\u7ecd\u5357 \u7684\u624b\u638c\u7adf\u7136\u6ed1\u8fc7\u4e00\u65c1\uff0c\u738b\u7167\u5e0c\u4ecd\u7136\u7aef\u5750\u6905\u4e0a\uff0c\u82e5\u65e0\u5176\u4e8b\u3002\u803f\u7ecd\u5357\u4e0d\u7531\u5927\u5403\u4e00\u60ca\uff0c\u5de6\u624b\u64d2\u62ff\uff0c \u53f3\u624b\u70b9\u7a74\uff0c\u4e00\u62db\u4e24\u5f0f\uff0c\u731b\u7136\u53d1\u51fa\uff0c\u8fd9\u662f\u6b66\u5f53\u6d3e\u5927\u64d2\u62ff\u624b\u7684\u53c1\u5341\u516d\u5f0f\u4e4b\u4e00\uff0c\u738b\u7167\u5e0c\u5750\u5728\u6905\u4e0a\uff0c \u770b\u6765\u4e07\u96be\u9003\u907f\uff0c\u90a3\u6599\u803f\u7ecd\u5357\u5de6\u624b\u5148\u5230\uff0c\u4ed6\u6a2a\u8098\u4e00\u649e\uff0c\u95ea\u7535\u822c\u7684\u628a\u64d2\u62ff\u624b\u5316\u5f00\uff0c\u53f3\u624b\u4e00\u4e3e\uff0c\u53c8 \u628a\u803f\u7ecd\u5357\u7684\u53f3\u8098\u6258\u8d77\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u803f\u5144\uff0c\u4f60\u6211\u4e14\u6162\u52a8\u624b\uff0c\u5f3a\u654c\u5df2\u7ecf\u6765\u4e86\uff01\u4f60\u6211\u5408\u5219\u4e24\u5168\uff0c \u5206\u5219\u4e24\u4ea1\uff01\u201d\u803f\u7ecd\u5357\u51dd\u795e\u4e00\u542c\uff0c\u8fdc\u5904\u9690\u6709\u5578\u58f0\uff0c\u9762\u8272\u53d8\u9053\uff1a\u201c\u4f60\u6363\u4ec0\u9ebd\u9b3c\uff1f\u53bb\u4e86\u4e00\u6279\uff0c\u53c8\u6765 \u4e00\u6279\u3002\u201d\u738b\u7167\u5e0c\u7b11\u9053\uff1a\u201c\u8fd9\u56de\u6765\u7684\u662f\u771f\u6b63\u7684\u5f3a\u76d7\uff0c\u5b9e\u4e0d\u76f8\u7792\uff0c\u5ddd  \u8fb9\u754c\u6700\u5389\u5bb3\u7684\u4e94\u80a1\u5927\u76d7\uff0c \u4eca\u665a\u90fd\u4f1a\u5230\u6b64\uff01\u201d\u803f\u7ecd\u5357\u6012\u9053\uff1a\u201c\u5353\u5927\u4eba\u5e76\u6ca1\u6709\u591a\u5c11\u94f6\u5b50\uff0c\u4f60\u4eec\u4f55\u5fc5\u8fd9\u6837\u5c0f\u9898\u5927\u4f5c\uff0c\u91cc\u5e94\u5916 \u5408\uff1f\u201d\u738b\u7167\u5e0c\u7b11\u9053\uff1a\u201c\u4f60\u5f53\u6211\u662f\u5185\u5e94\u9ebd\uff1f\u4ed6\u4eec\u8981\u52ab\u7684\u662f\u6211\uff0c\u4e0d\u662f\u4f60\u7684\u4ec0\u9ebd\u5353\u5927\u4eba\uff0c\u4e0d\u8fc7\u4ed6\u4eec \u82e5\u987a\u624b\u7275\u7f8a\uff0c\u52ab\u4e86\u5c0f\u5f1f\uff0c\u518d\u52ab\u4f60\u4eec\uff0c\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u201d\u803f\u7ecd\u5357\u534a\u4fe1\u534a\u7591\uff0c\u5fc3\u91cc\u6697\u9053\uff1a\u4f60\u80a9\u65e0\u884c \u674e\uff0c\u4e24\u624b\u7a7a\u7a7a\uff0c\u52ab\u4f60\u4f5c\u751a\uff1f\u738b\u7167\u5e0c\u5ffd\u53c8\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u8d76\u5feb\u9000\u56de\u53a2\u623f\u53bb\uff0c\u628a\u6709\u5b98\u8854\u7684\u706f\u7b3c\u53d6 \u4e0b\uff0c\u4e5f\u8bb8\u4e0d\u4f1a\u6b83\u53ca\u9c7c\u6c60\u3002\u201d\u803f\u7ecd\u5357\u4e00\u9635\u8fdf\u7591\uff0c\u738b\u7167\u5e0c\u5ffd\u7136\u7ad9\u8d77\uff0c\u5728\u4ed6\u8033\u8fb9\u4f4e\u58f0\u8bf4\u4e86\u51e0\u53e5\uff0c\u803f \u7ecd\u5357\u4e0d\u7531\u5f97\u70b9\u4e86\u70b9\u5934\uff0c\u75be\u5fd9\u9000\u4e0b\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u5578\u58f0\u8d8a\u6765\u8d8a\u8fd1\uff0c\u738b\u7167\u5e0c\u628a\u5927\u95e8\u6253\u5f00\uff0c\u95e8\u5916\u6d8c\u8fdb\u4e86\u5341\u591a\u6761\u6c49\u5b50\uff0c\u9ad8\u9ad8\u77ee\u77ee\uff0c\u7ad9 \u6ee1\u4e00\u5c4b\uff0c\u803f\u7ecd\u5357\u4e00\u770b\uff0c\u9f99\u95e8\u5e2e\u7684\u53c1\u4e2a\u8235\u4e3b\u4e5f\u5728\u5176\u5185\u3002\u8001\u9556\u5934\u5728\u91cc\u9762\u5413\u5f97\u9762\u9752\u5507\u767d\uff0c\u6084\u6084\u8bf4 \u9053\uff1a\u201c\u8fd9\u56de\u7cdf\u4e86\uff0c\u6765\u4e86\u53c1\u6279\u6700\u5389\u5bb3\u7684\u5f3a\u4eba\uff0c\u9664\u4e86\u9f99\u95e8\u5e2e\u5916\uff0c\u8fd8\u6709\u5927\u5df4\u5c71\u9ed1\u864e\u5ca9\u7684\u65b9\u6c0f\u5144\u5f1f\uff0c \u548c\u5b9a\u519b\u5c71\u7684\u9ea6\u6c0f\u53c1\u96c4\u3002\u201d\u803f\u7ecd\u5357\u9053\uff1a\u201c\u8fd8\u6709\u4e24\u6279\u672a\u5230\u54e9\uff0c\u4f60\u7b49\u7740\u77a7\u5427\uff01\u201d \u3000\u3000\u5b9a\u519b\u5c71\u9ea6\u6c0f\u53c1\u96c4\u7684\u8001\u5927\u9ea6\u9022\u6625\u7ad9\u5728\u5f53\u4e2d\uff0c\u53cc\u76ee\u4e00\u626b\uff0c\u6840\u6840\u7b11\u9053\uff1a\u201c\u771f\u6709\u4f60\u7684\uff0c\u91d1\u73e0\u5b9d\u8d1d \u85cf\u5728\u90a3\u91cc\uff1f\u8fd8\u4e0d\u5feb\u62ff\u51fa\u6765\uff1f\u662f\u4e0d\u662f\u6df7\u5728\u90a3\u72d7\u5b98\u7684\u884c\u674e\u91cc\u4e86\uff1f\u201d\u738b\u7167\u5e0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u9ea6\u8001\u5927\uff0c \u4f60\u4e5f\u662f\u8001\u65bc\u6c5f\u6e56\u7684\u4e86\uff0c\u96be\u9053\u8fd9\u4e5f\u770b\u4e0d\u51fa\u6765\u5417\uff1f\u4e45\u95fb\u5927\u540d\uff0c\u4e0d\u8fc7\u5982\u662f\u3002\u4e0d\u5fc5\u52a8\u624b\uff0c\u4f60\u5df2\u8f93\u4e86\u4e00 \u62db\u4e86\uff01\u201d\u8bf4\u7f62\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u9f99\u95e8\u5e2e\u7684\u603b\u8235\u4e3b\u5c60\u666f\u96c4\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u7fd8\u8d77\u62c7\u6307\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u771f\u6709\u4f60\u7684\uff1f\u4f60\u62ff\u51fa\u6765\uff0c \u8ba9\u54b1\u4eec\u89c1\u8bc6\u89c1\u8bc6\uff0c\u54b1\u4eec\u597d\u597d\u4ea4\u4e2a\u670b\u53cb\u3002\u201d\u738b\u7167\u5e0c\u7ef6\u7f13\u8d77\u7acb\uff0c\u5c06\u653e\u5728\u5899\u6839\u7684\u9a6c\u978d\u4e00\u628a\u63d0\u8d77\uff0c\u653e \u5728\u7d2b\u6a80\u684c\u4e0a\uff0c\u53ea\u542c\u5f97\u6728\u684c\u5431\u5431\u4f5c\u54cd\uff0c\u62d4\u51fa\u4f69\u5251\uff0c\u8f7b\u8f7b\u4e00\u524a\u3002\u90a3\u9a6c\u978d\u539f\u662f\u9ed1\u9edd\u9edd\u7684\u6beb\u4e0d\u60ca\u4eba\uff0c \u4efb\u4f55\u4eba\u770b\u4e86\u90fd\u4ee5\u4e3a\u8fd9\u662f\u6f06\u6728\u9a6c\u978d\uff0c\u90a3\u6599\u4e00\u524a\u4e4b\u4e0b\uff0c\u987f\u65f6\u91d1\u5149\u900f\u9732\uff0c\u94c1\u76ae\u91cc\u9762\u5305\u7684\u7adf\u662f\u5341\u8db3\u7684 \u8d64\u91d1\uff0c\u4e0a\u9762\u8fd8\u9576\u5d4c\u6709\u5341\u9980\u7c92\u6eda\u5706\u7684\u732b\u513f\u7eff\u5b9d\u73e0\uff0c\u91d1\u5149\u5b9d\u6c14\uff0c\u5e7b\u6210\u5f02\u5f69\u3002\u9ea6\u6c0f\u53c1\u96c4\u9762\u9762\u76f8\u89d1\uff0c \u505a\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u539f\u6765\u6709\u7ecf\u9a8c\u7684\u7eff\u6797\u5927\u76d7\uff0c\u4e00\u770b\u884c\u674e\u5ba2\u5546\uff0c\u4fbf\u80fd\u6d4b\u77e5\u4ed6\u6709\u591a\u5c11\u91d1\u73e0\u8d22\u5b9d\uff0c\u767e\u4e0d\u5931\u4e00\uff0c\u5ddd \u8fb9\u5883\u7684\u4e94\u80a1\u5f3a\u76d7\uff0c\u8ddf\u8e2a\u738b\u7167\u5e0c\u5df2\u6709\u591a\u65e5\uff0c\u770b\u4ed6\u9a6c\u8e44\u8e0f\u5904\uff0c\u5c18\u571f\u98de\u626c\uff0c\u5206\u660e\u662f\u8d1f\u6709\u4f53\u79ef\u5c0f\u800c\u8d28 \u91cf\u91cd\u7684\u91d1\u73e0\u91cd\u5b9d\uff0c\u4f46\u5374\u770b\u4e0d\u51fa\u4ed6\u85cf\u5728\u4f55\u5904\uff0c\u8c01\u4e5f\u6599\u4e0d\u5230\u539f\u6765\u662f\u5305\u85cf\u5728\u9a6c\u978d\u4e4b\u4e2d\u3002 \u3000\u3000\u738b\u7167\u5e0c\u54c8\u54c8\u4e00\u7b11\uff0c\u63d0\u8d77\u4e86\u4e00\u4e2a\u8e0f\u8e6c\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u662f\u540c\u9053\u4e2d\u4eba\uff0c\u5c0f\u5f1f\u6ca1\u4ec0\u9ebd\u656c \u610f\uff0c\u8fd9\u4e2a\u8e0f\u8e6c\uff0c\u5c31\u9001\u4e0e\u5ddd\u9655\u8fb9\u754c\u7684\u9053\u4e0a\u540c\u6e90\uff0c\u7b97\u4e2a\u5c0f\u5c0f\u7684\u793c\u7269\u5427\uff01\u201d\u7eff\u6797\u7fa4\u96c4\u9762\u9762\u76f8\u89d1\uff0c\u9ea6 \u9022\u6625\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u884c\uff0c\u54b1\u4eec\u8ba4\u683d\u4e86\uff01\u201d\u4e0d\u63a5\u8e0f\u8e6c\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002 \u3000\u3000\u803f\u7ecd\u5357\u5728\u53a2\u623f\u91cc\u5077\u77a7\uff0c\u521a\u677e\u5f97\u53e3\u6c14\uff0c\u770b\u90a3\u9ea6\u9022\u6625\u65b9\u8d70\u5230\u95e8\u53e3\uff0c\u5ffd\u7136\u5916\u9762\u6840\u6840\u602a\u7b11\uff0c\u4eba\u5f71 \u4e00\u95ea\uff0c\u8d70\u8fdb\u4e86\u4e00\u4e2a\u77ee\u80d6\u8001\u5934\uff0c\u5438\u7740\u4e00\u6839\u5927\u65e9\u70df\u7ba1\uff0c\u5410\u51fa\u4e00\u7f15\u7f15\u9752\u70df\uff0c\u602a\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u54c7\uff0c\u4e0d \u5f85\u6211\u6765\uff0c\u4f60\u4eec\u4fbf\u5206\u8d43\u4e86\u5417\uff1f\u201d\u9ea6\u9022\u6625\u9053\uff1a\u201c\u90b5\u5927\u54e5\uff0c\u54b1\u4eec\u683d\u4e86\u3002\u201d\u77ee\u80d6\u8001\u5934\u70df\u888b\u4e00\u6307\uff0c\u9053\uff1a \u201c\u4ec0\u9ebd\u683d\u4e86\uff0c\u4ffa\u65e9\u77a7\u51fa\u4ed6\u9a6c\u978d\u88f9\u6709\u9b3c\uff0c\u4f60\u4eec\u7684\u8bdd\u6211\u5168\u542c\u5230\u5566\uff0c\u6211\u53ef\u4e0d\u662f\u53eb\u5316\uff0c\u60f3\u65bd\u820d\u6211\u4e00\u4e2a \u8e0f\u8e6c\u5417\uff1f\u90a3\u53ef\u4e0d\u884c\uff01\u201d \u3000\u3000\u803f\u7ecd\u5357\u5728\u91cc\u9762\u77a7\u5f97\u5206\u660e\uff0c\u4ed6\u867d\u548c\u77ee\u80d6\u8001\u5934\u672a\u4f1a\u4e2a\u9762\uff0c\u4f46\u770b\u4ed6\u795e\u6c14\u6253\u626e\uff0c\u5df2\u77e5\u4ed6\u662f\u9655\u5357\u7684 \u72ec\u811a\u5927\u76d7\u90b5\u5ba3\u626c\uff0c\u4ed6\u7684\u70df\u7ba1\u4e43\u662f\u4e00\u79cd\u7f55\u89c1\u7684\u5916\u95e8\u5175\u5668\uff0c\u53ef\u4f5c\u70b9\u7a74\u53a5\uff0c\u4e5f\u53ef\u4f5c\u4e94\u884c\u5251\uff0c\u662f\u6c5f\u6e56 \u4e0a\u7684\u6210\u540d\u4eba\u7269\uff0c\u4e0d\u60f3\u4ed6\u5374\u8fd9\u6837\u65e0\u8d56\u3002 \u3000\u3000\u738b\u7167\u5e0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u90b5\u8001\u7237\u5b50\uff0c\u4f60\u662f\u6211\u7684\u524d\u8f88\uff0c\u8fd9\u4e2a\u9a6c\u978d\uff0c\u5b5d\u656c\u4f60\u8001\uff0c\u672c\u4e5f\u7b97\u4e0d\u4e86\u4ec0 \u9ebd\uff0c\u65e0\u5948\u6211\u8fd8\u6709\u4e00\u4f4d\u670b\u53cb\uff0c\u4ed6\u8bf4\u4e0d\u80af\u3002\u201d\u90b5\u5ba3\u626c\u9053\uff1a\u201c\u90a3\u4f4d\u670b\u53cb\uff0c\u8bf7\u51fa\u4e00\u89c1\u3002\u201d\u8bdd\u58f0\u672a\u4e86\uff0c \u623f\u91cc\u500f\u7684\u51b2\u51fa\u4e00\u4eba\uff0c\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u6b66\u5f53\u95e8\u4eba\u803f\u7ecd\u5357\u62dc\u89c1\u5404\u4f4d\u524d\u8f88\u3002\u201d \u3000\u3000\u90b5\u5ba3\u626c\u773c\u73e0\u4e00\u6e9c\uff0c\u9053\uff1a\u201c\u4f60\u662f\u6b66\u5f53\u95e8\u4e0b\uff1f\u54b1\u4eec\u4eb2\u8fd1\u4eb2\u8fd1\u3002\u201d\u4f38\u624b\u4e00\u62c9\uff0c\u53c1\u6307\u4e00\u6263\uff0c\u6697\u85cf \u5206\u7b4b\u9500\u9aa8\u7684\u5389\u5bb3\u624b\u6cd5\uff0c\u803f\u7ecd\u5357\u638c\u5fc3\u5411\u4e0a\u4e00\u63a5\uff0c\u624b\u8155\u4e00\u8f6c\uff0c\u7528\u51fa\u6b66\u5f53\u6d3e\u4e8b\u6cd5\u4e2d\u7684\u201c\u53c1\u73af\u5957 \u6708\u201d\uff0c\u628a\u90b5\u5ba3\u626c\u7684\u624b\u6cd5\u89e3\u4e86\uff0c\u90b5\u5ba3\u626c\u5de6\u638c\u5ffd\u5730\u671d\u4ed6\u80a9\u5934\u4e00\u6309\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u554a\uff01\u201d\u803f\u7ecd\u5357\u5378\u4e86 \u4e00\u6b65\uff0c\u4e39\u7530\u4e00\u642d\uff0c\u6c14\u8fbe\u56db\u68a2\uff0c\u53cc\u81c2\u4e00\u62b1\uff0c\u5de6\u8098\u5fae\u62ac\uff0c\u7528\u51fa\u4e00\u62db\u201c\u6e14\u592b\u6652\u7f51\u201d\uff0c\u53c8\u628a\u90b5\u5ba3\u626c\u7684 \u64d2\u62ff\u624b\u62c6\u4e86\u3002\u90b5\u5ba3\u626c\u54c8\u54c8\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u679c\u7136\u662f\u6b66\u5f53\u95e8\u4e0b\uff01\u201d \u3000\u3000\u803f\u7ecd\u5357\u663e\u4e86\u4e24\u624b\u6b66\u5f53\u7edd\u6280\uff0c\u987f\u65f6\u628a\u90b5\u5ba3\u626c\u60ca\u7740\u3002\u672c\u6765\u8bba\u5230\u6b66\u529f\uff0c\u90b5\u5ba3\u626c\u8fd8\u5728\u803f\u7ecd\u5357\u4e4b \u4e0a\uff0c\u4f46\u6b66\u5f53\u6d3e\u4e43\u6b66\u6797\u6b63\u5b97\uff0c\u76db\u6781\u4e00\u65f6\uff0c\u7eff\u6797\u597d\u6c49\u65e0\u4e0d\u5fcc\u60ee\u3002\u90b5\u5ba3\u626c\u5411\u5f8c\u4e00\u8dc3\uff0c\u53d1\u8bdd\u9053\uff1a\u201c\u8db3 \u4e0b\u4f55\u82e6\u8d81\u8fd9\u6dcc\u6d51\u6c34\uff01\u201d\u803f\u7ecd\u5357\u9053\uff1a\u201c\u4ec0\u9ebd\u6d51\u6c34\uff1f\u6211\u4eec\u540c\u5c5e\u4e00\u5925\u3002\u91d1\u5b50\u662f\u5c0f\u4e8b\uff0c\u6b66\u5f53\u6d3e\u7684\u5a01\u540d \u53ef\u4e0d\u80fd\u5728\u8fd9\u513f\u6298\u5815\u3002\u201d\u90b5\u5ba3\u626c\u4e7e\u7b11\u96e8\u58f0\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6b66\u5f53\u95e8\u4eba\u4ece\u4e0d\u4fdd\u9556\uff0c\u4e5f\u4ece\u4e0d\u4e3a\u76d7\uff0c\u4f60 \u600e\u9ebd\u80fd\u4e0e\u4ed6\u540c\u5925\u3002\u201d\u803f\u7ecd\u5357\u9053\uff1a\u201c\u6c5f\u6e56\u4e4b\u4e8b\uff0c\u4eba\u4eba\u7ba1\u5f97\uff0c\u4f60\u6043\u4f17\u805a\u52ab\uff0c\u843d\u5728\u6211\u7684\u773c\u5185\uff0c\u6211\u4fbf \u4e0d\u5bb9\u3002\u201d\u90b5\u5ba3\u626c\u7b11\u9053\uff1a\u201c\u662f\u4f60\u5e08\u7236\u53eb\u4f60\u7ba1\u7684\u9ebd\uff1f\u4e3a\u4ec0\u9ebd\u53ea\u6d3e\u4f60\u4e00\u4e2a\u4eba\u6765\uff1f\u201d\u803f\u7ecd\u5357\u9053\uff1a\u201c\u8def \u89c1\u4e0d\u5e73\uff0c\u62d4\u5200\u76f8\u52a9\uff0c\u4f55\u5fc5\u5e08\u547d\uff1f\u201d\u738b\u7167\u5e0c\u6025\u5fd9\u4f7f\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u803f\u7ecd\u5357\u731b\u7684\u9192\u8d77\uff0c\u63a5\u7740\u8bf4\u9053\uff1a \u201c\u6b66\u5f53\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\u5728\u9655\u897f\u805a\u4f1a\uff0c\u6b63\u60f3\u4e0e\u4f60\u4eec\u6b66\u6797\u4e2d\u6709\u5934\u6709\u9762\u7684\u4eba\u7269\u4e00\u89c1\u3002\u201d\u90b5\u5ba3\u626c\u6014\u4e86\u4e00 \u6014\uff0c\u4ed6\u672c\u6253\u7b97\u82e5\u53ea\u662f\u803f\u7ecd\u5357\u4e00\u4eba\uff0c\u4fbf\u7d22\u6027\u628a\u4ed6\u5e72\u4e86\uff0c\u6bc1  \u706d\u8ff9\u518d\u8bf4\u3002\u5982\u4eca\u542c\u8bf4\u6b66\u5f53\u7b2c\u4e8c\u4ee3\u5f1f \u5b50\u5728  \u96c6\u4f1a\uff0c\u60f3\u5fc5\u6765\u7684\u751a\u591a\uff0c\u90b5\u5ba3\u626c\u5929\u5927\u7684\u80c6\u5b50\uff0c\u4e5f\u4e0d\u6562\u4e0e\u6b66\u5f53\u6d3e\u7684\u7fa4\u96c4\u76f8\u6597\uff0c\u5f53\u4e0b\u70df\u7ba1\u4e00 \u6536\uff0c\u7b11\u7b11\u53e3\u9053\uff1a\u201c\u8db3\u4e0b\u4f55\u5fc5\u751f\u8fd9\u9ebd\u5927\u6c14\uff0c\u65e2\u7136\u8fd9\u4f4d\u662f\u4f60\u7684\u670b\u53cb\uff0c\u54b1\u4eec\u90a3\u91cc\u4e0d\u5356\u4e2a\u4ea4\u60c5\u3002\u201d \u3000\u3000\u803f\u7ecd\u5357\u9762\u8272\u4e00\u677e\uff0c\u4e0d\u81ea\u89c9\u7684\u7528\u8863\u8896\u62b9\u4e86\u62b9\u989d\u4e0a\u7684\u51b7\u6c57\u3002\u539f\u6765\u4ed6\u8bd5\u4e86\u4e24\u62db\uff0c\u4e5f\u81ea\u77e5\u4e0d\u662f\u7fa4 \u76d7\u5bf9\u624b\uff0c\u5168\u51ed\u6b66\u5f53\u6d3e\u7684\u5a01\u98ce\uff0c\u624d\u628a\u654c\u4eba\u5413\u9000\u3002\u5176\u5b9e\u4ed6\u6240\u8bf4\u7684\u6b66\u5f53\u6d3e\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\u5728\u6b64\u805a\u4f1a\uff0c\u5012 \u4e5f\u5e76\u4e0d\u5168\u5047\uff0c\u7d2b\u9633\u9053\u4eba\u662f\u66fe\u6d3e\u6709\u56db\u4e2a\u5f1f\u5b50\u5728  \u529e\u4e8b\uff0c\u8fde\u4ed6\u5c31\u662f\u4e94\u4eba\u3002\u4f46\u90a3\u56db\u4eba\u548c\u4ed6\u53ef\u5e76\u6ca1\u6709 \u7ea6\u4f1a\u3002 \u3000\u3000\u90b5\u5ba3\u626c\u89c1\u4ed6\u4ee5\u8896\u62ed\u6c57\uff0c\u84e6\u7136\u7ad9\u7740\u4e0d\u52a8\uff0c\u53cc\u76ee\u71a0\u71a0\u53d1\u5149\uff0c\u738b\u7167\u5e0c\u6697\u53eb\u4e00\u58f0\u201c\u4e0d\u597d\u201d\uff0c\u90b5\u5ba3 \u626c\u5ffd\u7136\u4ef0\u5929\u5927\u7b11\u53c1\u58f0\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5f52\u5927\u54e5\uff0c\u4f60\u6765\u7684\u597d\uff0c\u4f60\u542c\u8fd9\u5c0f\u5b50\u662f\u4e0d\u662f\u6492\u8c0e\uff1f\u201d\u731b\u7136\u4e00 \u80a1\u5f3a\u98ce\uff0c\u5385\u4e2d\u70db\u5149\u6447\u6447\u6b32\u706d\uff0c\u4e00\u4e2a\u53c8\u9ad8\u53c8\u5927\u7684\u7ea2\u9762\u8001\u4eba\uff0c\u7a81\u7136\u4ece\u5916\u9762\u63a0\u7a7a\u800c\u964d\uff0c\u5927\u58f0\u7b11\u9053\uff1a \u201c\u6b66\u5f53\u6d3e\u662f\u6765\u4e86\u56db\u540d\uff0c\u53ef\u662f\u90fd\u7ed9\u522b\u4eba\u64d2\u4e86\u3002\u522b\u4eba\u6562\u78b0\u6b66\u5f53\u6d3e\uff0c\u4e3a\u4ec0\u9ebd\u54b1\u4eec\u4e0d\u6562\uff1f\u8fd9\u5c0f\u5b50\u4e00\u4eba \u5728\u6b64\uff0c\u54b1\u4eec\u628a\u4ed6\u6253\u6b7b\uff0c\u4e22\u5230\u8352\u5c71\u91cc  \u72fc\u4fbf\u662f\u3002\u5c31\u7b97\u6b66\u5f53\u4e94\u8001\u5bfb\u5230\uff0c\u8fd9\u7b14\u5e10\u4e5f\u7b97\u4e0d\u5230\u54b1\u4eec\u8eab \u4e0a\uff0c\u81ea\u6709\u4eba\u66ff\u54b1\u4eec\u9876\u7978\u3002\u201d\u803f\u7ecd\u5357\u4e0d\u7531\u5f97\u6697\u6697\u5403\u60ca\uff0c\u770b\u8fd9\u7ea2\u9762\u8001\u4eba\u7684\u58f0\u52bf\uff0c\u5fc5\u662f\u5ddd\u4e1c\u7684\u5927\u76d7 \u9e70\u722a\u738b\u5f52\u6709\u7ae0\u65e0\u7591\u3002\u4f46\u4ed6\u600e\u6653\u5f97\u6b66\u5f53\u6d3e\u6765\u4e86\u56db\u4eba\uff0c\u800c\u4e14\u7709\u56db\u4eba\u53c8\u7ed9\u4ec0\u9ebd\u4eba\u64d2\u4e86\uff1f \u3000\u3000\u90b5\u5ba3\u626c\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u5f52\u5927\u54e5\uff0c\u4e14\u6162\uff0c\u4f60\u662f\u8bf4\u90a3\u5973\u9b54\u5934\u51fa\u624b\u4e86\u9ebd\uff1f\u8fd9\u91cc\u53ef\u8fd8\u4e0d\u662f \u5979\u7ba1\u8f96\u7684\u5730\u65b9\u5440\uff1f\u201d\u5f52\u6709\u7ae0\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u8fd9\u6837\u80c6\u5c0f\u3002\u54b1\u4eec\u5ddd\u9655\u7684\u7eff\u6797\u9053\uff0c\u603b\u4e0d\u80fd\u53eb\u4e00\u4e2a\u5f8c\u8f88 \u5973\u5a03\u513f\u538b\u4e86\u3002\u201d\u4ed6\u53e3\u91cc\u8bf4\u8bdd\uff0c\u624b\u5e95\u53ef\u4e1d\u6beb\u4e0d\u7f13\uff0c\u80a9\u5934\u4e00\u6643\uff0c\u84b2\u6247\u822c\u7684\u5927\u624b\uff0c\u5df2\u8fce\u5934\u6293\u4e86\u4e0b \u6765\u3002\u803f\u7ecd\u5357\u89c1\u4ed6\u638c\u5fc3\u901a\u7ea2\uff0c\u90a3\u91cc\u6562\u63a5\uff0c\u5411\u5f8c\u4e00\u7f29\uff0c\u53f3\u8db3\u53d1\u8d77\uff0c  \u4ed6\u817f\u5f2f\u7684\u201c\u767d\u5e03\u7a74\u201d\uff0c\u5f52\u6709 \u7ae0\u6840\u6840\u602a\u7b11\uff0c\u6251\u8eab\u4e00\u95ea\uff0c\u6b3a\u8eab\u76f4\u8fdb\uff0c\u53f3\u624b\u4e94\u6307\u5982\u94a9\uff0c\u4e00\u628a\u6293\u5230\u803f\u7ecd\u5357\u8db3\u8ddf\u3002 \u3000\u3000\u803f\u7ecd\u5357\u8eab\u5b50\u4e00\u7f29\uff0c\u5f52\u6709\u7ae0\u53cc\u638c\u8fde\u73af\u6025\u53d1\uff0c\u803f\u7ecd\u5357\u8fde\u8fde\u5f8c\u9000\uff0c\u6697\u6068\u738b\u7167\u5e0c\u72b9\u81ea\u4e0d\u6765\u76f8\u63f4\uff0c \u5f52\u6709\u7ae0\u638c\u98ce\u547c\u547c\uff0c\u628a\u803f\u7ecd\u5357\u76f4\u903c\u81f3\u5899\u89d2\uff0c\u6b63\u60f3\u65bd\u5c55\u6740\u624b\uff0c\u5ffd\u95fb\u5f97\u738b\u7167\u5e0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8981 \u6211\u7684\u9a6c\u978d\uff0c\u8fd9\u4e5f\u4e0d\u96be\uff0c\u53ea\u662f\u4f60\u4eec\u53ef\u95ee\u8fc7\u7389\u7f57\u5239\u6ca1\u6709\uff1f\u201d\u90b5\u5ba3\u626c\u548c\u65b9\u6c0f\u5144\u5f1f\u3001\u9ea6\u6c0f\u53c1\u96c4\uff0c\u6b63\u5bf9 \u738b\u7167\u5e0c\u53d6\u5305\u56f4\u4e4b\u52bf\uff0c\u95fb\u8a00\u5927\u5403\u4e00\u60ca\uff0c\u90b5\u5ba3\u626c\u9661\u7684\u8df3\u51fa\u5708\u5b50\uff0c\u53eb\u9053\uff1a\u201c\u4ec0\u9ebd\u7389\u7f57\u5239\uff01\u201d\u738b\u7167\u5e0c \u9053\uff1a\u201c\u7eff\u6797\u9053\u5b81\u52ab\u5343\u5bb6\uff0c\u4e0d\u622a\u8584\u793c\uff0c\u8fd9\u662f\u522b\u4eba\u9001\u7ed9\u7389\u7f57\u5239\u7684\u8d22\u793c\uff0c\u4f60\u4eec\u60f3\u9ed1\u5403\u9ed1\u9ebd\uff01\u201d\u90b5\u5ba3 \u626c\u9762\u8272\u82cd\u767d\uff0c\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4e14\u6682\u4f4f\u624b\uff01\u201d\u5f52\u6709\u7ae0\u4e00\u4e2a\u5012\u7ffb\uff0c\u8dc3\u4e86\u56de\u6765\uff0c\u6012\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9 \u5c0f\u5b50\uff0c\u60f3\u62ff\u7389\u7f57\u5239\u6765\u606b\u5413\u6211\u4eec\u5417\uff1f\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u8c01\u4e2a\u5413\u4f60\uff1f\u201d\u628a\u9a6c\u978d\u4e00\u7ffb\uff0c\u53cd\u9762\u523b\u6709\u51e0\u4e2a \u5b57\u9053\uff1a\u201c\u656c\u5448\u7ec3\u9713\u88f3\u5c0f\u59d0\u54c2\u7eb3\u3002\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u8fd9\u53ef\u4e0d\u662f\u6211\u73b0\u5728\u523b\u7684\u3002\u201d\u90b5\u5ba3\u626c\u628a\u5f52\u6709\u7ae0\u62c9 \u8fc7\u4e00\u8fb9\uff0c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u5f52\u5927\u54e5\uff0c\u6b64\u4e8b\u5b81\u53ef\u4fe1\u5176\u6709\uff0c\u4e0d\u53ef\u4fe1\u5176\u65e0\uff0c\u4f9d\u5c0f\u5f1f\u611a\u89c1\uff0c\u8fd8\u662f\u628a\u4ed6\u653e\u8d70 \u4e86\u5427\u3002\u201d\u5f52\u6709\u7ae0\u54fc\u4e86\u4e00\u58f0\uff0c\u5782\u9996\u6c89\u601d\uff1a\u9ea6\u6c0f\u53c1\u96c4\uff0c\u9f99\u95e8\u53c1\u8235\uff0c\u90fd\u56f4\u4e86\u4e0a\u6765\uff0c\u53ea\u5269\u4e0b\u65b9\u5bb6\u5144 \u5f1f\uff0c\u5728\u5385\u4e2d\u76d1\u89c6\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u5927\u51fa\u610f\u5916\uff0c\u803f\u7ecd\u5357\u4e0d\u7531\u5f97\u6014\u5728\u5f53\u573a\uff0c\u6697\u60f3\uff1a\u8c01\u662f\u7389\u7f57\u5239\u554a\uff1f\u8fd9\u540d\u5b57\u53ef\u4ece\u672a\u542c\u8fc7\uff0c \u600e\u7684\u90a3\u4e9b\u5f3a\u76d7\u5c31\u5413\u5f97\u8fd9\u4e2a\u6837\u513f\uff1f \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u5f52\u6709\u7ae0\u731b\u7136\u62ac\u8d77\u5934\u6765\uff0c\u53cc\u773c\u4e00\u7ffb\uff0c\u542b\u55d4\u8bf4\u9053\uff1a\u201c\u662f\u7389\u7f57\u5239\u7684\u4e5f\u8981\u52ab\uff01\u201d\u90b5\u5ba3 \u626c\u5413\u4e86\u4e00\u8df3\uff0c\u6025\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5927\u54e5\uff01\u2026\u2026\u201d\u5f52\u6709\u7ae0\u547c\u7684\u4e00\u638c\uff0c\u51fb\u5728\u6a80\u6728\u684c\u4e0a\uff0c\u987f\u65f6\u628a\u684c \u5b50\u6253\u584c\u4e00\u89d2\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4e00\u5e74\u6765\u54b1\u4eec\u53d7\u90a3\u5973\u5a03\u5b50\u7684\u6c14\u4e5f\u53d7\u591f\u4e86\uff0c\u7d22\u6027\u8d81\u6b64\u65f6\u673a\uff0c\u8c41\u4e86\u51fa \u53bb\uff0c\u6597\u5979\u4e00\u6597\u3002\u201d\u90b5\u5ba3\u626c\u9000\u4e86\u51e0\u6b65\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\u2026\u2026\u201d\u5f52\u6709\u7ae0\u9053\uff1a\u201c\u4e8f\u4f60\u4e00\u4e16\u5a01 \u540d\uff0c\u5c31\u6015\u5f97\u8fd9\u4e2a\u6837\u513f\u3002\u5979\u7684\u5389\u5bb3\uff0c\u54b1\u4eec\u4e5f\u53ea\u662f\u8033\u95fb\uff0c\u672a\u66fe\u76ee\u51fb\uff0c\u5582\uff0c\u4f60\u4eec\u6709\u79cd\u7684\u5c31\u968f\u6211\u6765\uff0c \u8fd9\u5c0f\u5b50\u7684\u9a6c\u978d\u6211\u52ab\u5b9a\u4e86\u3002\u201d\u9ea6\u6c0f\u53c1\u96c4\uff0c\u9f99\u95e8\u53c1\u8235\uff0c\u7f29\u624b\u4e0d\u52a8\uff0c\u53ea\u6709\u65b9\u5bb6\u5144\u5f1f\u53eb\u9053\uff1a\u201c\u54b1\u5144\u5f1f \u613f\u542c\u5f52\u5927\u54e5\u8c03\u5ea6\u3002\u201d\u5f52\u6709\u7ae0\u6a2a\u4e86\u90b5\u5ba3\u626c\u4e00\u773c\uff0c\u53eb\u9053\uff1a\u201c\u597d\u554a\uff0c\u51e0\u5341\u5e74\u5144\u5f1f\u4e4b\u60c5\uff0c\u7b97\u662f\u767d\u4ea4\u7684 \u4e86\u3002\u201d\u90b5\u5ba3\u626c\u82e6\u7b11\u9053\uff1a\u201c\u5927\u54e5\u65e2\u7136\u8981\u5e72\uff0c\u5c0f\u5f1f\u53ea\u597d\u542c\u4ece\u3002\u201d\u5f52\u6709\u7ae0\u864e\u543c\u4e00\u58f0\uff0c\u9694\u7740\u684c\u5b50\uff0c\u4f38 \u624b\u5c31\u6293\uff0c\u738b\u7167\u5e0c\u8eab\u5f62\u4e00\u95ea\uff0c\u907f\u4e86\u5f00\u53bb\u3002\u65b9\u5bb6\u5144\u5f1f\uff0c\u5de6\u53f3\u6251\u4e0a\uff0c\u738b\u7167\u5e0c\u8eab\u5b50\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u84e6\u7136 \u4e00\u62db\u201c\u5de6\u53f3\u5f00\u5f13\u201d\u628a\u65b9\u5bb6\u5144\u5f1f\u683c\u5f00\u3002\u5f52\u6709\u7ae0\u624b\u8155\u4e00\u7ffb\uff0c\u9a88\u8d77\u4e2d\u98df\u4e8c\u6307\uff0c\u9aa4\u7136\u53d1\u51fa\uff0c\u76f4\u70b9\u738b\u7167 \u5e0c\u53cc\u76ee\uff0c\u738b\u7167\u5e0c\u970d\u5730\u4f7f\u4e2a\u201c\u51e4\u70b9\u5934\u201d\u8df3\u8fc7\u4e00\u8fb9\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u5f52\u8001\u5927\uff0c\u4f60\u4e2d\u4e86\u6211\u7684\u7f13\u5175\u8ba1 \u4e86\uff0c\u4f60\u8981\u52ab\u8be5\u65e9\u70b9\u52ab\uff0c\u73b0\u5728\u52ab\u9ebd\uff0c\u53ef\u6765\u4e0d\u53ca\u4e86\u3002\u4f60\u542c\uff0c\u5916\u9762\u4ec0\u9ebd\u58f0\u54cd\uff01\u201d\u5f52\u6709\u7ae0\u6115\u7136\u4e00\u542c\uff0c \u5916\u9762\u51fb\u6790\u58f0\u58f0\uff0c\u957f\u5bb5\u6613\u8fc7\uff0c\u7adf\u7136\u6253\u4e94\u66f4\u4e86\u3002\u738b\u7167\u5e0c\u5927\u7b11\u9053\uff1a\u201c\u4f60\u542c\u5230\u9ebd\u4e86\u6253\u4e94\u66f4\u4e86\uff01\u7389\u7f57\u5239 \u9a6c\u4e0a\u5c31\u6765\uff0c\u5f52\u8001\u5927\u4f60\u8fd8\u4e0d\u505c\u624b\uff0c\u8981\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff01\u201d\u5f52\u6709\u7ae0\u559d\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u60f3\u62d6\u5ef6\u65f6\u5019\uff0c \u5148\u9001\u4f60\u89c1\u960e\u738b\uff01\u201d\u547c\u7684\u4e00\u638c\uff0c\u53c8\u8fce\u5934\u5288\u4e0b\u3002 \u3000\u3000\u5927\u7b11\u58f0\u4e2d\uff0c\u738b\u7167\u5e0c\u51fa\u624b\u5982\u7535\uff0c\u626c\u4e86\u4e24\u626c\uff0c\u628a\u5385\u4e0a\u7684\u4e24\u679d\u5927\u725b\u6cb9\u70db\u6253\u706d\uff0c\u987f\u65f6\u4e00\u7247\u9ed1\u6f06\uff0c \u803f\u7ecd\u5357\u8d34\u5230\u5899\u6839\uff0c\u5c4f\u4e86\u547c\u5438\uff0c\u7fa4\u76d7\u867d\u7136\u4eba\u591a\u52bf\u5927\uff0c\u5728\u9ed1\u6697\u4e2d\u4e00\u65f6\u4e5f\u4e0d\u6562\u83bd\u52a8\uff0c\u5f52\u6709\u7ae0\u51dd\u795e\u9759 \u542c\uff0c\u8981\u60f3\u8fa8\u58f0\u8fdb\u51fb\uff0c\u5ffd\u7136\u5916\u9762\u4f20\u6765\u6e05\u8106\u7684\u7b11\u58f0\uff0c\u542c\u4f3c\u751a\u8fdc\uff0c\u970e\u5ffd\u4f7f\u5230\u4e86\u95e8\u5916\uff0c\u4f17\u4eba\u773c\u775b\u4e00 \u4eae\uff0c\u5385\u95e8\u5f00\u5904\uff0c\u8d70\u8fdb\u4e00\u961f\u5c11\u5973\uff0c\u524d\u9762\u56db\u4eba\uff0c\u63d0\u7740\u78a7\u7eb1\u706f\u7b3c\uff0c\u5f8c\u9762\u56db\u4eba\uff0c\u5de6\u53f3\u5206\u5217\uff0c\u62e5\u7740\u4e00\u4f4d \u7f8e\u82e5\u5929\u4ed9\u7684\u5c11\u5973\uff0c\u674f\u9ec4\u886b\u513f\uff0c\u767d\u7eeb\u675f\u8170\uff0c\u79cb\u6c34\u4e3a\u795e\uff0c\u957f\u7709\u4eba\u9b13\uff0c\u7b11\u76c8\u76c8\u7684\u4e00\u6b65\u6b65\u8d70\u6765\u3002\u5385\u4e2d \u7fa4\u76d7\u5446\u82e5\u6728\u9e21\uff0c\u6709\u51e0\u4e2a\u66f4\u662f\u9762\u5982\u6b7b\u7070\uff0c\u745f\u7f29\u4e00\u9685\uff0c\u52a8\u4e5f\u4e0d\u6562\u52a8\u3002 \u3000\u3000\u738b\u7167\u5e0c\u6b22\u58f0\u8bf4\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\uff0c\u5bb6\u7236\u95ee\u4f60\u8001\u4eba\u5bb6\u597d\u3002\u201d\u90a3\u5c11\u5973\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6 \u597d\u3002\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u5bb6\u7236\u6258\u6211\u5e26\u8fd9\u4e2a\u9a6c\u978d\u7ed9\u4f60\uff0c\u4ed6\u4eec\u2026\u2026\u201d\u5c11\u5973\u4f4e\u7709\u4e00\u7b11\uff0c\u622a\u7740\u9053\uff1a\u201c\u4f60\u7684 \u6765\u610f\u6211\u65e9\u5df2\u77e5\u9053\u3002\u662f\u4ed6\u4eec\u770b\u4e2d\u4e86\u8fd9\u4e2a\u9a6c\u978d\u9ebd\uff1f\u201d\u51e4\u773c\u4e00\u626b\uff0c\u90b5\u5ba3\u626c\u6025\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u662f\u4f60\u8001 \u4eba\u5bb6\u7684\u3002\u201d\u803f\u7ecd\u5357\u6697\u7b11\uff0c\u8fd9\u5973\u90ce\u770b\u6765\uff0c\u6700\u591a\u4e0d\u8fc7\u4e8c\u5341\u5c81\uff0c\u90b5\u5ba3\u626c\u504c\u5927\u4e00\u628a\u5e74\u7eaa\uff0c\u5374\u53e3\u53e3\u58f0\u58f0 \u53eb\u5979\u505a\u8001\u4eba\u5bb6\u3002 \u3000\u3000\u5c11\u5973\u7709\u6bdb\u4e00\u626c\uff0c\u53c8\u662f\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4e0d\u77e5\u4e0d\u7f6a\uff0c\u4f60\u4eec\u90fd\u968f\u6211\u56de\u5c71\u53bb\u5427\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u5ffd\u53c8 \u7b11\u9053\uff1a\u201c\u5f52\u8001\u5927\uff0c\u4f60\u4e5f\u6765\u4e86\u4e86\u4f60\u8fd9\u4e2a\u6708\u7684\u8d21\u7269\u8fd8\u672a\u4ea4\u6765\u5462\uff0c\u662f\u5fd8\u8bb0\u4e86\u9ebd\uff01\u201d\u5f52\u6709\u7ae0\u8c03\u5300\u547c \u5438\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u5ffd\u7136\u559d\u9053\uff1a\u201c\u7389\u7f57\u5239\uff0c\u522b\u4eba\u6015\u4f60\uff0c\u6211\u4e0d\u6015\u4f60\u3002\u8fd9\u91cc\u8fd8\u4e0d\u662f\u4f60\u7684\u5730\u754c\uff0c\u8fd9\u9a6c\u978d \u6211\u8981\u5b9a\u4e86\u3002\u201d\u4e00\u4e2a\u7bad\u6b65\uff0c\u51b2\u4e86\u4e0a\u6765\uff0c\u90a3\u88ab\u5524\u505a\u201c\u7389\u7f57\u5239\u201d\u7684\u5c11\u5973\u95ee\u9053\uff1a\u201c\u8fd8\u6709\u90a3\u4f4d\u63d2\u624b\u8981\u8fd9 \u9a6c\u978d\u7684\uff1f\u201d\u9ea6\u6c0f\u53c1\u96c4\u3001\u9f99\u95e8\u53c1\u8235\u75be\u5fd9\u9000\u8fc7\u4e00\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\uff01\u201d\u90b5\u5ba3\u626c\u9762\u8272\u60e8\u767d\uff0c\u5450\u5450\u4e0d \u80fd\u51fa\u8a00\uff0c\u65b9\u5bb6\u5144\u5f1f\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u5374\u968f\u5728\u5f52\u6709\u7ae0\u8eab\u5f8c\u3002\u7389\u7f57\u5239\u500f\u5730\u4e00\u58f0\u957f\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5f52\u8001\u5927\uff0c \u8c01\u8981\u4f60\u6015\u554a\uff01\u201d\u5f52\u6709\u7ae0\u6b63\u51b2\u5230\u9762\u524d\uff0c\u84b2\u6247\u822c\u7684\u5927\u624b\u5f80\u4e0b\u6293\u53bb\uff0c\u7389\u7f57\u5239\u4e0d\u52a8\u58f0\u8272\uff0c\u5f52\u6709\u7ae0\u4e00\u6293 \u4e4b\u4e0b\uff0c\u731b\u7684\u4e0d\u89c1\u4eba\u5f71\uff0c\u75be\u5fd9\u9000\u65f6\uff0c\u90a3\u91cc\u8fd8\u6765\u5f97\u53ca\uff0c\u5f8c\u5fc3\u4e00\u9635\u521b\u75db\uff0c\u987f\u65f6\u5012\u5728\u5730\u4e0a\uff0c\u65b9\u5bb6\u5144\u5f1f \u8fde\u770b\u4e5f\u672a\u770b\u5f97\u6e05\uff0c\u80c1\u4e0b\u4e5f\u540c\u53d7\u4e86\u7389\u7f57\u5239\u7684\u4e00\u638c\uff0c\u60e8\u53eb\u72c2\u55e5\uff0c\u5728\u5730\u4e0b\u6eda\u6765\u6eda\u53bb\uff01 \u3000\u3000\u7389\u7f57\u5239\u95ea\u7535\u4e4b\u95f4\uff0c\u8fde\u4e0b\u4e86\u53c1\u624b\u6bd2\u62db\uff0c\u628a\u53c1\u4e2a\u5267\u76d7\u6253\u5012\u5730\u4e0a\uff0c\u4ecd\u7136\u662f\u7b11\u541f\u541f\u7684\u7ad9\u7740\uff0c\u82e5\u65e0 \u5176\u4e8b\uff0c\u7eff\u6797\u7fa4\u8c6a\u5168\u90fd\u6151\u670d\uff0c\u7389\u7f57\u5239\u5bf9\u9ea6\u6c0f\u53c1\u96c4\uff0c\u9f99\u95e8\u53c1\u8235\u8bf4\u9053\uff1a\u201c\u4e0d\u5173\u4f60\u4eec\u7684\u4e8b\uff0c\u4f60\u4eec\u8d77 \u6765\uff01\u201d\u90b5\u5ba3\u626c\u8fde\u8fde\u8ba8\u9976\uff0c\u7389\u7f57\u5239  \u662f\u51b7\u7b11\u4e0d\u7b54\u3002 \u3000\u3000\u53c1\u4eba\u4e2d\u5f52\u6709\u7ae0\u6b66\u529f\u6700\u9ad8\uff0c\u88ab\u51fb\u5012\u5f8c\u8fd0\u5185\u529b\u62b5\u5fa1\uff0c\u5fcd\u4f4f\u521b\u75db\uff0c\u6240\u4ee5\u521d\u65f6\u4e0d\u4f3c\u65b9\u6c11\u5144\u5f1f\u7684\u75db \u53f7\u5931\u58f0\u3002\u90a3\u77e5\u4e0d\u8fd0\u6c14\u62b5\u5fa1\u8fd8\u597d\uff0c\u4e00\u8fd0\u6c14\u62b5\u5fa1\uff0c\u8eab\u4f53\u5185\u987f\u5982\u6709\u5343\u4e07\u6761\u6bd2\u86c7\u4e71\u7a9c\u4e71\u54ac\uff0c\u4e94\u810f\u7ffb \u817e\uff0c\u8fde\u53eb\u4e5f\u53eb\u4e0d\u51fa\u58f0\u6765\u4e86\u3002\u65c1\u8fb9\u7684\u4eba\u53ea\u89c1\u4ed6\u5934\u9876\u4e0a\u70ed\u6c14\u817e\u817e\uff0c\u8d21\u8c46\u5927\u7684\u6c57\u73e0\u4e00\u9897\u9897\u6ef4\u51fa\u6765\uff0c \u9762\u4e0a\u808c\u8089\u4e00\u9635\u9635\u75c9\u631b\uff0c\u75db\u82e6\u5f97\u8fde\u9762\u90e8\u90fd\u53d8\u4e86\u5f62\u3002\u8fd9\u7b80\u76f4\u662f\u5929\u4e0b\u6700\u6b8b\u5fcd\u7684\u9177\u5211\uff01 \u3000\u3000\u65b9\u6c0f\u5144\u5f1f\u53eb\u9053\uff1a\u201c\u6c42\u4f60\u8001\u4eba\u5bb6\u5f00\u6069\uff0c\u5feb\u70b9\u6740\u4e86\u6211\u5427\uff01\u201d\u5f52\u6709\u7ae0\u773c\u775b\u7a81\u51fa\uff0c\u5374\u558a\u4e0d\u51fa\u6765\u3002 \u7389\u7f57\u5239\u7b11\u76c8\u76c8\u7684\u8bf4\u9053\uff1a\u201c\u65b9\u5bb6\u5144\u5f1f\uff0c\u4f60\u4eec\u662f\u4ece\u72af\uff0c\u7f6a\u51cf\u4e00\u7b49\uff0c\u514d\u4e86\u4f60\u4eec\u7684\u5211\u7f5a\u5427\u3002\u201d\u7ea4\u8db3\u98de \u8d77\uff0c\u4e00\u4eba  \u4e86\u4e00\u811a\uff0c\u4e24\u5144\u5f1f\u60e8\u53eb\u4e00\u58f0\uff0c\u5bc2\u7136\u4e0d\u52a8\uff0c\u803f\u7ecd\u5357\u770b\u5f97\u60ca\u5fc3\u52a8\u9b44\uff0c\u60f3\u4e0d\u5230\u8fd9\u6837\u7f8e\u8273\u7684 \u5c11\u5973\uff0c\u7adf\u662f\u6740\u4eba\u4e0d\u7728\u773c\u7684\u9b54\u738b\u3002 \u3000\u3000\u7389\u7f57\u5239\u628a\u65b9\u5bb6\u5144\u5f1f\u7ed3\u679c\u4e4b\u5f8c\uff0c\u5411\u90b5\u5ba3\u626c\u62db\u62db\u624b\u9053\uff1a\u201c\u4f60\u8fc7\u6765\uff01\u201d\u90b5\u5ba3\u626c\u53cc\u624b\u6276\u7740\u5899\u58c1\uff0c \u8eab\u8eaf\u98a4\u6296\uff0c\u4e00\u6b65\u6b65\u8d70\u4e86\u8fc7\u6765\u3002\u7389\u7f57\u5239\u67d4\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u548c\u5f52\u8001\u5927\u662f\u51e0\u5341\u5e74\u5144\u5f1f\uff0c\u4ea4\u60c5\u5f88\u4e0d\u9519 \u554a\uff01\u201d\u90b5\u5ba3\u626c\u5fc3\u80c6\u6b32\u88c2\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u5973\u4fa0\u4f60\u660e\u9274\u79cb\u6beb\uff0c\u8fd9\u56de\u4e8b\u6ca1\u6709\u6211\u7684\u4efd\u3002\u201d\u7389\u7f57\u5239\u9762\u8272 \u4e00\u6c89\uff0c\u5389\u58f0\u65a5\u9053\uff1a\u201c\u6789\u4f60\u505a\u4e86\u8fd9\u9ebd\u591a\u5e74\u5f3a\u76d7\uff0c\u505a\u5f3a\u76d7\u7684\u7981\u5fcc\u4f60\u8fd8\u4e0d\u61c2\u9ebd\uff1f\u4f60\u7b80\u76f4\u4e00\u70b9\u773c\u5149\u90fd \u6ca1\u6709\uff0c\u8fd8\u5728\u7eff\u6797\u4e2d\u901e\u4ec0\u9ebd\u5f3a\uff0c\u79f0\u4ec0\u9ebd\u9738\uff1f\u4ed6\u4e00\u4e2a\u5c11\u5e74\uff0c\u5355\u8eab\u62bc\u8fd0\u91d1\u5b9d\uff0c\u6ca1\u6709\u6781\u5927\u7684\u6765\u5934\uff0c\u4ed6 \u6562\u8fd9\u6837\u505a\u9ebd\uff1f\u8001\u5b9e\u5bf9\u4f60\u8bf4\uff0c\u4ed6\u8fd9\u793c\u7269\u82e5\u4e0d\u662f\u9001\u7ed9\u6211\u7684\uff0c\u6211\u4e5f\u4e0d\u6562\u4f38\u624b\u52ab\u4ed6\u3002\u4f60\u5bf9\u4ed6\u7684\u6765\u5386\u77e5 \u9053\u591a\u5c11\uff1f\u4e0d\u95ee\u6e05\u695a\uff0c\u5c31\u80e1\u4e71\u542c\u4eba\u5506\u4f7f\uff0c\u5408\u5925\u884c\u52ab\uff0c\u4f60\u8fd9\u4e0d\u662f\u778e\u4e86\u773c\u775b\u9ebd\uff1f\u201d\u90b5\u5ba3\u626c\u542c\u5979\u8d8a\u9a82 \u8d8a\u51f6\uff0c\u5fc3\u91cc\u4e5f\u8d8a\u6765\u8d8a\u5bbd\uff0c\u542c\u5979\u9a82\u5b8c\uff0c\u5df2\u5b8c\u5168\u5b9a\u4e0b\u4e86\u5fc3\u3002\u4ed6\u77e5\u9053\u7389\u7f57\u5239\u7684\u813e\u6c14\uff0c\u6709\u91cd\u5927\u7684\u4e8b\u60c5 \u53d1\u751f\u4e4b\u65f6\uff0c\u82e5\u5979\u7b11\u5bb9\u6ee1\u9762\uff0c\u5bf9\u4f60\u6e29\u8a00\u7ec6\u8bed\uff0c\u90a3\u4e0b\u4e00\u6b65\u5c31\u4e00\u5b9a\u662f\u7528\u6781\u6bd2\u8fa3\u7684\u624b\u6cd5\u5bf9\u4ed8\uff0c\u82e5\u5f97\u5979 \u4e25\u5389\u65a5\u8d23\uff0c\u90a3\u5c31\u51c6\u4e0d\u4f1a\u6709\u4ec0\u9ebd\u4e8b\u513f\u3002\u542c\u5979\u9a82\u5b8c\u4e4b\u5f8c\uff0c\u90b5\u5ba3\u626c\u500f\u7684\u5de6\u53f3\u5f00\u5f13\uff0c\u81ea\u5df2\u6253\u4e86\u4e24\u4e2a\u8033 \u5149\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u5c0f\u7684\u778e\u4e86\u773c\u775b\uff0c\u662f\u5c0f\u7684\u8fd8\u6ca1\u8d44\u683c\u505a\u5f3a\u76d7\uff0c\u671b\u4f60\u8001\u4eba\u5bb6\u591a\u591a\u6559\u8bf2\u3002\u201d\u7389\u7f57 \u5239\u559d\u9053\uff1a\u201c\u4f60\u82e5\u7136\u81ea\u5df2\u77e5\u7f6a\uff0c\u6211\u5c31\u514d\u4f60\u7684\u7f6a\uff0c\u4f60\u8fc7\u6765\uff0c\u628a\u4f60\u7684\u628a\u5144\u6740\u6389\uff01\u201d\u90b5\u5ba3\u626c\u9762\u8272\u60e8 \u767d\uff0c\u5f52\u6709\u7ae0\u5230\u5e95\u662f\u4ed6\u591a\u5e74\u5144\u5f1f\uff0c\u5982\u4f55\u4e0b\u5f97\u6bd2\u624b\u3002\u5f52\u6709\u7ae0\u5374\u5728\u5730\u4e0b\u6eda\u6765\u6eda\u53bb\uff0c\u60ed\u60ed\u5411\u4ed6\u8fd9\u8fb9\u6eda \u6765\uff0c\u9732\u51fa\u54c0\u6073\u7684\u76ee\u5149\uff0c\u4f3c\u6c42\u4ed6\u8d76\u5feb\u4e0b\u624b\u3002 \u3000\u3000\u803f\u7ecd\u5357\u5fcd\u53d7\u4e0d\u4f4f\uff0c\u5ffd\u7136\u7eb5\u8eab\u51fa\u6765\uff0c\u4ea2\u58f0\u8bf4\u9053\uff1a\u201c\u5f52\u6709\u7ae0\u662f\u65e0\u6076\u4e0d\u4f5c\u7684\u72ec\u884c\u5927\u76d7\uff0c\u4f60\u628a\u4ed6 \u5904\u6b7b\uff0c\u4e5f\u7b97\u662f\u66ff\u7eff\u6797\u9053\u4e2d\u6e05\u9664\u4e00\u9738\uff0c\u6ca1\u4eba\u8bf4\u4f60\u4e0d\u5bf9\u3002\u4f46\u4f60\u53eb\u4ed6\u5144\u5f1f\u76f8\u6b8b\uff0c\u8fd9\u5374\u4e0d\u662f\u6b63\u6d3e\u6240 \u4e3a\u3002\u201d\u7389\u7f57\u5239\u9762\u8272\u4e00\u53d8\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u4f60\u662f\u90a3\u4e00\u6d3e\u7684\u95e8\u4eba\u3002\u201d\u803f\u7ecd\u5357\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u6b66\u5f53\u6d3e\u7684 \u7b2c\u4e8c\u4ee3\u5f1f\u5b50\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u54e6\uff0c\u6b66\u5f53\u6d3e\u7684\uff0c\u5931\u656c\uff0c\u5931\u656c\uff01\u201d\u79cb\u6ce2\u4e00\u8f6c\uff0c\u8bf4\u9053\uff1a\u201c\u90b5\u5ba3\u626c\uff0c \u6211\u8fd9\u662f\u8bd5\u4f60\u7684\u5fc3\u672f\u884c\u4e3a\uff0c\u4f60\u867d\u4e0e\u5f52\u6709\u7ae0\u4e00\u5925\uff0c\u8fd8\u4e0d\u4f3c\u4ed6\u90a3\u6837\u80e1\u4f5c\u975e\u4e3a\uff1a\u627e\u53eb\u4f60\u6740\u4ed6\uff0c\u4f60\u4e5f\u8fd8 \u4e0d\u662f\u4e00\u5473\u5c48\u670d\u5949\u627f\u3002\u4e0d\u613f\u6740\u53cb\u4ee5\u6c42\u81ea\u4fdd\u3002\u597d\uff0c\u51ed\u8fd9\u4e24\u70b9\uff0c\u6211\u5c31\u514d\u4e86\u4f60\u884c\u5211\u4e4b\u8d23\u3002\u201d\u8bf4\u8bdd\u4e4b \u95f4\uff0c\u7ea4\u8db3\u98de\u8d77\uff0c\u8f7b\u8f7b\u4e00  \uff0c\u53c8\u628a\u5f52\u6709\u7ae0\u7ed3\u675f\u4e86\u3002 \u3000\u3000\u7389\u7f57\u5239\u8c08\u7b11\u4e4b\u95f4\u6740\u4e86\u53c1\u4e2a\u5267\u76d7\uff0c\u6325\u6325\u624b\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u968f\u6211\u5230\u5b9a\u519b\u5c71\u53bb\uff01\u201d\u7b11\u4e86\u4e00\u7b11\uff0c\u6307 \u7740\u803f\u7ecd\u5357\u9053\uff1a\u201c\u4f60\u60f3\u8dd1\u5230\u90a3\u91cc\u53bb\uff1f\u60f3\u56de\u53bb\u4fdd\u62a4\u4f60\u7684\u5353\u5927\u4eba\u5417\uff1f\u4f60\u4e5f\u968f\u6211\u53bb\uff0c\u8fde\u540c\u4f60\u7684\u5353\u5927\u4eba \u548c\u6240\u6709\u884c\u674e\u94f6\u4e24\uff0c\u90fd\u7ed9\u6211\u642c\u4e0a\u5c71\u53bb\uff01\u201d \u3000\u3000\u803f\u7ecd\u5357\u51db\u7136\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u8fd9\u7389\u7f57\u5239\u597d\u5927\u7684\u80c6\u5b50\uff0c\u5c45\u7136\u7ba1\u5230\u6211\u6b66\u5f53\u6d3e\u7684\u5934\u4e0a\u3002\u8981\u77e5\u6b66\u5f53\u6d3e \u7d20\u4ee5\u6b66\u6797\u6b63\u5b97\u81ea\u5c45\uff0c\u95e8\u4e0b\u5f1f\u5b50\uff0c\u4e0d\u5c11\u4eba\u4fbf\u517b\u6210\u4e86\u50b2\u6162\u81ea\u5927\u7684\u4e60\u6c14\uff0c\u803f\u7ecd\u5357\u5c24\u5176\u5982\u6b64\uff0c\u4f46\u773c\u89c1 \u7389\u7f57\u5239\u72e0\u8fa3\u65e0\u6bd4\uff0c\u5982\u82e5\u4e0d\u4ece\uff0c\u53ea\u6050\u4e0d\u662f\u5979\u7684\u5bf9\u624b\uff0c\u4f46\u5982\u82e5\u76f8\u4ece\uff0c\u53c8\u6401\u4e0d\u4e0b\u8fd9\u4e2a\u9762\u5b50\u3002\u6b63\u5728\u8e0c \u8e87\uff0c\u5ffd\u89c1\u738b\u7167\u5e0c\u629b\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u5f00\u58f0\u8bf4\u9053\uff1a\u201c\u803f\u5144\u5bf9\u7ec3\u5973\u4fa0\u4e5f\u662f\u4ef0\u6155\u5f97\u5f88\uff0c\u4ed6\u5728\u8def\u4e0a\u8fd8\u66fe\u5bf9 \u6211\u8bf4\u8fc7\uff0c\u8bf4\u8981\u62dc\u8c12\u4f60\u8001\u4eba\u5bb6\u5462\uff01\u201d\u803f\u7ecd\u5357\u4e00\u542c\uff0c\u77e5\u662f\u738b\u7167\u5e0c\u6050\u6015\u81ea\u5df2\u9c81\u83bd\uff0c\u60f9\u51fa\u7978\u6765\uff0c\u6240\u4ee5 \u66ff\u81ea\u5df1\u5706\u573a\uff0c\u867d\u7136\u4e0d\u5feb\uff0c\u4e5f\u81ea\u611f\u6fc0\uff0c\u5f53\u4e0b\u60f3\u9053\uff1a\u597d\u6c49\u4e0d\u5403\u8ddf\u524d\u4e8f\uff0c\u4e14\u968f\u5979\u53bb\uff0c\u770b\u5979\u600e\u6837\uff1f\u82e5 \u5979\u4e0d\u7559\u9762\u5b50\uff0c\u5c06\u5353\u5bb6\u6d17\u52ab\u7684\u8bdd\uff0c\u81ea\u5df2\u4fbf\u9080\u96c6\u540c\u95e8\uff0c\u4e0e\u5979\u76f8\u6597\uff0c\u603b\u80fd\u62a5\u8fd9\u4e00\u7bad\u4e4b\u4ec7\u3002 \u3000\u3000\u5f53\u4e0b\u803f\u7ecd\u5357\u56de\u5230\u53a2\u623f\uff0c\u5bf9\u5353\u4ef2\u5ec9\u8bf4\u4e86\uff0c\u8001\u9556\u5934\u9002\u624d\u66fe\u5728\u95e8\u7f1d\u5077\u7aa5\uff0c\u5fc3\u60ca\u80c6\u6218\uff0c\u8fc4\u6709\u9980 \u60b8\uff0c\u6025\u5fd9\u529d\u5353\u4ef2\u5ec9\u4f9d\u4ece\u3002\u5353\u4ef2\u5ec9\u4e5f\u7b97\u8c41\u8fbe\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u53ea\u8981\u6027\u547d\u4fdd\u5f97\u4f4f\uff0c\u90a3\u4e9b\u8eab\u5916\u4e4b\u7269\u7531 \u4ed6\u53bb\u5427\u3002\u201d \u3000\u3000\u7ecf\u4e86\u4e00\u591c\u7684\u7eb7\u6270\uff0c\u5176\u65f6\u5df2\u662f\u5929\u8272\u5fae\u660e\uff0c\u6653\u971e\u9690\u73b0\uff0c\u7389\u7f57\u5239\u548c\u516b\u540d\u5c11\u5973\uff0c\u7763\u4fc3\u7fa4\u76d7\uff0c\u62bc\u89e3 \u5353\u5bb6\u7684\u8f66\u8f86\u884c\u674e\uff0c\u76f4\u4e0a\u5927\u5df4\u5c71\u7684\u652f\u8109\u5b9a\u519b\u5c71\u53bb\u3002\u5c71\u4e0a\u7889\u5821\u68ee\u4e25\uff0c\u6805\u57ce\u56f4\u7ed5\uff0c\u4ece\u5c71\u811a\u81f3\u5c71\u9876\uff0c \u4e00\u8def\u6709\u5973\u76d7\u8fce\u63a5\uff0c\u5317\u5730\u80ed\u8102\uff0c\u672c\u5c31\u6709\u7537\u513f\u6c14\u6982\uff0c\u7ecf\u8fc7\u7389\u7f57\u5239\u7684\u8bad\u7ec3\uff0c\u66f4\u662f\u521a\u5065\u5a40\u5a1c\u4e24\u6709\u4e4b\uff0c \u4fe8\u5982\u662f\u4e00\u652f\u96c4\u8d73\u8d73\u7684\u5a18\u5b50\u519b\uff0c\u738b\u7167\u5e0c\u4e5f\u4e0d\u7531\u5f97\u6697\u81ea\u4f69\u670d\uff0c\u5fc3\u60f3\uff1a\u8fd9\u4e9b\u5973\u5a18\uff0c\u6bd4\u6211\u7236\u4eb2\u7684\u90e8\u4e0b \u8fd8\u5f3a\u5f97\u591a\u3002 \u3000\u3000\u5230\u4e86\u5c71\u5be8\uff0c\u7389\u7f57\u5239\u53eb\u624b\u4e0b\u5c06\u5353\u5bb6\u8fd9\u4e00\u884c\u4eba\u90fd\u5b89\u7f6e\u5728\u5927\u5ba2\u623f\u4e2d\uff0c\u8f66\u8f86\u884c\u674e\u5219\u62bc\u5165\u5f8c\u5be8\uff0c\u738b \u7167\u5e0c\u88ab\u5b89\u7f6e\u5728\u53e6\u4e00\u5ea7\u5bbe\u7efe\u3002\u7389\u7f57\u5239\u53bb\u5f8c\uff0c\u803f\u7ecd\u5357\u6084\u6084\u95ee\u9053\uff1a\u201c\u8001\u9556\u5934\uff0c\u4f60\u4e45\u5728\u897f\u5317\u4fdd\u9556\uff0c\u8fd9 \u7389\u7f57\u5239\u5230\u5e95\u662f\u5eff\u9ebd\u4eba\u554a\uff1f\u201d\u8001\u9556\u5934\u9053\uff1a\u201c\u8fd9\u7389\u7f57\u5239\u662f\u6700\u8fd1\u4e24\u5e74\u624d\u5f00\u5c71\u7acb\u67dc\u7684\u5973\u5f3a\u76d7\uff0c\u771f\u540d\u53eb \u7ec3\u9713\u88f3\uff0c\u6b66\u6797\u4e2d\u8c01\u4e5f\u4e0d\u77e5\u5979\u7684\u6765\u5386\uff0c\u66f4\u4e0d\u77e5\u5979\u662f\u4ece\u90a3\u91cc\u7ec3\u6765\u7684\u8fd9\u4e00\u8eab\u60ca\u4eba\u7684\u6b66\u529f\uff01\u542c\u8bf4\u5979\u4e24 \u5e74\u524d\u521d\u521d\u51fa\u9053\uff0c\u5c31\u66fe\u4ee5\u53cc\u638c\u4e00\u5251\u8fde\u8d25\u5341\u516b\u540d\u5f3a\u76d7\u3002\u5979\u548c\u7fa4\u76d7\u76f8\u6597\u4e4b\u65f6\uff0c  \u897f\u7684\u6b66\u6797\u540d\u5bbf\u674e\u4e8c \u65a7\u66fe\u5728\u65c1\u89c2\u770b\uff0c\u770b\u5f8c\u5bf9\u4eba\u8bf4\uff0c\u7ec3\u9713\u88f3\u7684\u5251\u6cd5\u638c\u6cd5\u4e0e\u6b66\u6797\u5404\u6d3e\uff0c\u5168\u4e0d\u76f8\u540c\uff0c\u8f9b\u8fa3\u602a\u5f02\u4e4b\u5904\uff0c\u4e3a \u4ed6\u5e73\u751f\u6240\u4ec5\u89c1\u3002\u4ed6\u8fd8\u8bf4\uff0c\u4e0d\u7528\u5341\u5e74\uff0c\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\uff0c\u5c31\u5f97\u8ba9\u4f4d\u7ed9\u8fd9\u5973\u5a03\u513f\u4e86\u3002\u201d\u803f\u7ecd\u5357\u54fc\u4e86 \u4e00\u58f0\uff0c\u8001\u9556\u5934\u8bf4\u987a\u4e86\u5634\uff0c\u8fd9\u624d\u731b\u89c9\u81ea\u5df1\u5931\u8a00\u3002\u539f\u6765\u6570\u5341\u5e74\u6765\uff0c\u6b66\u6797\u4e2d\u4eba\uff0c\u90fd\u63a8\u8bb8\u6b66\u5f53\u6d3e\u7684\u7d2b \u9633\u9053\u957f\u662f\u5929\u4e0b\u7b2c\u4e00\u9ad8\u624b\uff0c\u82e5\u4f9d\u674e\u4e8c\u65a7\u7684\u8bf4\u6cd5\uff0c\u5c82\u4e0d\u662f\u8bf4\u6b66\u5f53\u6d3e\u7684\u9886\u8896\u5730\u4f4d\u5c31\u5c06\u4e0d\u7a33\uff1f\u5f53\u4e0b\u4e7e \u7b11\u4e24\u58f0\uff0c\u8f6c\u53e3\u8bf4\u9053\uff1a\u201c\u674e\u8001\u82f1\u96c4\u867d\u7136\u662f\u89c1\u591a\u8bc6\u5e7f\uff0c\u4f46\u4e5f\u672a\u514d\u628a\u7389\u7f57\u5239\u6367\u5f97\u592a\u8fc7\u5206\u4e86\u3002\u4f60\u4eec\u6b66 \u5f53\u6d3e\u7684\u4e5d\u5bab\u795e\u884c\u638c\u548c\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u5251\u5230\u5e95\u662f\u6b66\u6797\u6b63\u5b97\uff0c\u65c1\u95e8\u7684\u638c\u6cd5\u5251\u6cd5\u600e\u6bd4\u5f97\u4e0a\uff1f\u201d\u803f\u7ecd\u5357 \u8fd9\u624d\u50b2\u7136\u4e00\u7b11\uff0c\u8212\u670d\u4e0b\u6765\u3002 \u3000\u3000\u803f\u7ecd\u5357\u8fd9\u4e00\u884c\u4eba\u88ab\u5173\u5728\u5ba2\u623f\u91cc\u6574\u6574\u4e00\u5929\uff0c\u5bf8\u6b65\u4e0d\u80fd\u79fb\u52a8\uff0c\u508d\u665a\u65f6\u5206\uff0c\u5ffd\u7136\u6709\u4e24\u4e2a\u5973\u76d7\uff0c \u8fdb\u6765\u53eb\u9053\uff1a\u201c\u6211\u4eec\u5be8\u4e3b\u8bf7\u5353\u5927\u4eba\u548c\u803f\u82f1\u96c4\u524d\u53bb\u8d74\u5bb4\uff01\u201d \u3000\u3000\u5c71\u5be8\u4e2d\u706f\u706b\u901a\u660e\uff0c\u6446\u7740\u4e24\u684c\u9152\u5e2d\uff0c\u9664\u4e86\u7aef\u5750\u4e3b\u4f4d\u7684\u7389\u7f57\u5239\u7ec3\u9713\u88f3\u662f\u4e00\u4e2a\u7f8e\u82e5\u5929\u4ed9\u7684\u5c11\u5973 \u4e4b\u5916\uff0c\u5176\u9980\u7684\u90fd\u662f\u7eff\u6797\u4e2d\u7684\u7c97\u8c6a\u6c49\u5b50\uff0c\u5728\u8def\u4e0a\u78b0\u5230\u7684\u897f\u5ddd\u53cc\u715e\uff0c\u7ffb\u5c71\u864e\u5468\u540c\uff0c\u706b\u7075\u733f\u6731\u5b9d\u693f \u7b49\u4e5f\u90fd\u5728\u5e2d\u4e0a\u3002\u9152\u5e2d\u65c1\u6709\u5341\u4e8c\u540d\u5c11\u5973\u670d\u4f8d\uff0c\u656c\u9152\u7684\uff0c\u4e0a\u83dc\u7684\uff0c\u5b88\u536b\u7684\u90fd\u662f\u5be8\u4e2d\u5973\u76d7\uff0c\u7c97\u6c49 \u7ea2  \uff0c\u76f8\u6620\u6210\u8da3\u3002\u66f4\u6709\u8da3\u7684\u662f\uff0c\u90a3\u4e9b\u7eff\u6797\u8c6a\u6c49\uff0c\u4e00\u4e2a\u4e2a\u90fd\u5664\u82e5\u5bd2\u8749\uff0c\u602f\u751f\u751f\u7684\u50cf\u4e2a\u5973\u5a18\uff1a\u800c \u90a3\u4e9b\u6267\u5f79\u7684\u5c11\u5973\uff0c\u5374\u4e00\u4e2a\u4e2a\u626c\u7709\u5410\u6c14\uff0c\u8c6a\u8fc8\u5f02\u5e38\uff0c\u7765\u7768\u7fa4\u76d7\uff0c\u987e\u76fc\u751f\u59ff\u3002\u803f\u7ecd\u5357\u5fc3\u60f3\uff1a\u5973\u5b50 \u96c4\u98de\uff0c\u7537\u5b50\u96cc\u4f0f\uff0c\u8fd9\u771f\u662f\u5929\u4e0b\u6700\u5947\u602a\u7684\u7b75\u5e2d\uff0c\u5fc3\u867d\u4e0d\u5fff\uff0c\u5374\u4e5f\u4e0d\u7981\u5bf9\u7389\u7f57\u5239\u6697\u6697\u4f69\u670d\u3002 \u3000\u3000\u9152\u8fc7\u53c1\u5de1\uff0c\u7389\u7f57\u5239\u500f\u7684\u8d77\u7acb\uff0c\u628a\u624b\u4e00\u6325\uff0c\u53eb\u9053\uff1a\u201c\u628a\u9001\u7ed9\u738b\u516c\u5b50\u7684\u793c\u7269\u62ff\u4e0a\u6765\uff01\u201d\u968f\u5373 \u6709\u4f8d\u5973\u6367\u4e0a\u4e94\u4e2a\u91d1\u76d8\uff0c\u4e0a\u8986\u7ea2\u5dfe\uff0c\u7389\u7f57\u5239\u5c06\u5de6\u9996\u7684\u4e24\u4e2a\u91d1\u76d8\u63ed\u5f00\uff0c\u5353\u4ef2\u5ec9\u5413\u5f97\u60ca\u53eb\u4e00\u58f0\uff0c\u76d8 \u4e2d\u7adf\u662f\u4e24\u9897\u8840\u6dcb\u6dcb\u7684\u4eba\u5934\uff0c\u7389\u7f57\u5239\u5fae\u5fae\u4e00\u7b11\uff0c\u5bf9\u738b\u7167\u5e0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u5c0a\u5927\u4eba\u8981\u7684\u3002\u201d\u53c8\u628a\u53f3 \u9996\u53c1\u4e2a\u91d1\u76d8\u63ed\u5f00\uff0c\u91cc\u9762\u4e5f\u662f\u53c1\u9897\u8840\u6dcb\u6dcb\u7684\u4eba\u5934\uff0c\u7389\u7f57\u5239\u5c06\u4eba\u5934\u9010\u4e2a\u63d0\u8d77\uff0c\u6643\u4e86\u51e0\u6643\uff0c\u53c8\u5fae\u7b11 \u9053\uff1a\u201c\u8fd9\u53c1\u4eba\u5192\u72af\u516c\u5b50\uff0c\u56e0\u6b64\u6211\u628a\u4ed6\u4eec\u7684\u9996\u7ea7\u53d6\u6765\uff0c\u7b97\u52a0\u9001\u7ed9\u516c\u5b50\u7684\u8584\u793c\u3002\u4ed6\u4eec\u8fd8\u6709\u4e00\u4e2a\u540c \u5925\uff0c\u4e5f\u5403\u4e86\u5927\u4e8f\uff0c\u8c05\u4ed6\u4eca\u5f8c\u518d\u4e5f\u4e0d\u6562  \u70e6\u516c\u5b50\u4e86\u3002\u201d\u5353\u4ef2\u5ec9\u89c1\u4e86\uff0c\u66f4\u662f\u5403\u60ca\uff0c\u8fd9\u53c1\u9897\u4eba\u5934\uff0c \u6b63\u662f\u77f3\u6d69\u6628\u665a\u6240\u7387\u9886\u7684\u90a3\u53c1\u4e2a\u9526\u8863\u536b\uff0c\u60f3\u4e0d\u5230\u5728\u534a\u665a\u4e4b\u95f4\uff0c\u7adf\u5168\u7ed9\u7389\u7f57\u5239\u8ffd\u6740\u4e86\u3002 \u3000\u3000\u738b\u7167\u5e0c\u8083\u7136\u8d77\u7acb\uff0c\u606d\u8eab\u8bf4\u9053\uff1a\u201c\u5982\u6b64\u539a\u793c\uff0c\u5b9e\u4e0d\u6562\u5f53\uff0c\u53ea\u662f\u6211\u6682\u65f6\u8fd8\u672a\u60f3\u56de\u5bb6\u3002\u201d\u7389\u7f57 \u5239\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u5c06\u6709\u4e07\u91cc\u8fdc\u884c\uff0c\u8fd9\u4efd\u8584\u793c\uff0c\u6211\u81ea\u4f1a\u5dee\u4eba\u9001\u4e0e\u4ee4\u5c0a\uff0c\u8fde\u540c\u76df\u7ea6\u4e5f\u4e00\u5e76\u9001 \u53bb\u3002\u201d\u738b\u7167\u5e0c\u9053\u4e86\u58f0\u8c22\u3002\u7389\u7f57\u5239\u7b11\u541f\u541f\u7684\u5bf9\u7fa4\u76d7\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u6253\u4e0d\u6210\u76f8\u8bc6\uff0c\u6211\u7ed9\u4f60\u4eec\u63ed\u4e86 \u8fd9\u6bb5\u8fc7\u8282\u5427\u3002\u4ed6\u7684\u7236\u4eb2\u5c31\u662f  \u5317\u7684\u738b\u5609\u80e4\u3002\u201d\u7fa4\u76d7\u5f3a\u7b11\u8bf4\uff1a\u201c\u554a\uff0c\u771f\u662f\u5927\u6c34\u51b2\u5012\u9f99\u738b\u5e99\uff0c\u81ea \u5bb6\u4eba\u4e0d\u8ba4\u5f97\u81ea\u5bb6\u4eba\uff0c\u65e9\u77e5\u662f\u738b\u5927\u54e5\u7684\uff0c\u54b1\u4eec\u4e5f\u4e0d\u6562\u8ddf\u8e2a\u52a8\u624b\u3002\u201d \u3000\u3000\u539f\u6765\u738b\u5609\u80e4\u4e43\u662f  \u5317\u7eff\u6797\u7684\u9886\u8896\uff0c\u624b\u4e0b\u6709\u9ad8\u8fce\u6893\uff0c\u738b\u5de6\u6302\uff0c\u98de\u5c71\u864e\uff0e\u5927\u7ea2\u72fc\u7b49\u5267\u76d7\uff0c\u58f0 \u5a01\u751a\u76db\uff0c\u53ea\u662f\u52bf\u529b\u4f38\u4e0d\u8fc7  \u5357\u3002\u660e\u671d\u4e07\u5386\u5e74\u95f4\uff0c  \u897f\u6709\u5341\u53c1\u8def\u5927\u76d7\uff0c\u5404\u4e0d\u76f8\u670d\uff0c\u8fd9\u738b\u5609\u80e4\u5fd7 \u5411\u751a\u5927\uff0c\u5728  \u5317\u548c\u5267\u76d7\u9ad8\u8fce\u7965\u7ed3\u4e49\u4e4b\u5f8c\uff0c\u4e0d\u5230\u5341\u5e74\u4fbf\u505a\u4e86  \u5317\u7eff\u6797\u7684\u76df\u4e3b\uff0c\u4ed6\u7b56\u5212\u628a\u5168  \u7684 \u7eff\u6797\u9053\u90fd\u8054\u6210\u4e00\u6c14\uff0c\u7ffb\u5929\u8986\u5730\u7684\u5927\u5e72\u4e00\u573a\uff0c\u4f46  \u4e2d  \u5357\uff0c\u5374\u4e0d\u80af\u5949\u4ed6\u53f7\u4ee4\u3002\u5230\u8fd9\u4e24\u5e74\u7389\u7f57\u5239 \u5d1b\u8d77  \u5357\uff0c\u738b\u5609\u80e4\u53c8\u6709\u4e24\u4e2a\u5927\u4ec7\u5bb6\u6b63\u5728  \u5357\u6d3b\u52a8\u3002\u56e0\u6b64\u738b\u5609\u80e4\u5351\u8f9e\u539a\u5e01\uff0c\u6d3e\u4ed6\u7684\u513f\u5b50\u738b\u7167\u5e0c \u6765  \u5357\u8054\u7edc\u7389\u7f57\u5239\u3002\u7eff\u6797\u9053\u4e2d\u89c4\u77e9\uff0c\u5730\u76d8\u7586\u754c\u5206\u660e\uff0c\u6240\u4ee5\u738b\u7167\u5e0c\u7edd\u4e0d\u80fd\u591a\u5e26\u5165\u9a6c\uff0c\u53ea\u662f\u5b64\u8eab \u4e0a\u9053\u3002\u60f3\u4e0d\u5230\u5206\u5e03\u5404\u7701\u7684\u9526\u8863\u536b\u5b9e\u5728\u5389\u5bb3\uff0c\u738b\u7167\u5e0c\u4e00\u4e0a\u9053\uff0c\u4ed6\u4eec\u5c31\u8c03\u6765\u4e86\u77f3\u6d69\u7b49\u56db\u540d\u9ad8\u624b\uff0c \u6697\u6697\u8ddf\u8e2a\u3002\u800c\u5ddd  \u8fb9\u754c\u7684\u4e94\u80a1\u5267\u76d7\uff0c\u5782\u6d8e\u4ed6\u7684\u91d1\u5b9d\uff0c\u4e5f\u6697\u6697\u7f00\u4e0a\u3002 \u3000\u3000\u803f\u7ecd\u5357\u542c\u4e86\u738b\u7167\u5e0c\u7684\u6765\u5386\u5f8c\uff0c\u5fc3\u4e2d\u6697\u9a82\uff1a\u8fd9\u5c0f\u5b50\u539f\u6765\u65e9\u4e0e\u7389\u7f57\u5239\u6709\u7ea6\uff0c\u5374\u5229\u7528\u6211\u6b66\u5f53\u6d3e \u7684\u5a01\u540d\uff0c\u66ff\u4ed6\u6682\u6321\u8ffd\u5175\uff0c\u597d\u5f85\u7389\u7f57\u5239\u6765\u5230\u3002\u53ea\u7d2f\u4e86\u6211\u4e0e\u5353\u5bb6\u4eba\u4f17\uff0c\u90fd\u505a\u4e86\u8fd9\u8d3c\u5a46\u5a18\u7684\u4fd8\u864f\u3002 \u3000\u3000\u7389\u7f57\u5239\u987f\u4e86\u4e00\u987f\uff0c\u7aef\u9152\u8bf4\u9053\uff1a\u201c\u4ece\u4eca\u4ee5\u5f8c\uff0c\u54b1\u4eec\u5168  \u7684\u7eff\u6797\u9053\u90fd\u662f\u4e00\u5bb6\uff0c\u6211\u4e0e\u738b\u5609\u80e4\u5927 \u54e5\u5df3\u7ed3\u6210\u8054\u76df\uff0c\u613f\u5404\u8def\u5144\u5f1f\uff0c\u4e5f\u4e92\u76f8\u7167\u987e\u3002\u8bf8\u4f4d\u82e5\u65e0\u5f02\u89c1\uff0c\u8bf7\u5c3d\u6b64\u676f\u3002\u201d\u9aa8\u561f\u4e00\u58f0\uff0c\u628a\u9152\u996e \u5c3d\uff0c\u5e2d\u4e0a\u7fa4\u76d7\uff0c\u90a3\u6562\u4e0d\u4ece\uff0c\u7eb7\u7eb7\u8d77\u7acb\uff0c\u4e2a\u4e2a\u4e7e\u676f\u3002\u7389\u7f57\u5239\u63b7\u676f\u5927\u7b11\uff0c\u62db\u6765\u4e00\u540d\u5973\u76d7\uff0c\u5429\u5490\u4e86 \u51e0\u53e5\uff0c\u9063\u5979\u5165\u5185\uff0c\u8fc7\u4e01\u7247\u523b\uff0c\u8fd9\u540d\u5973\u76d7\u4ece\u91cc\u9762\u5e26\u51fa\u4e86\u56db\u4e2a\u4eba\u6765\uff0c\u803f\u7ecd\u5357\u89c1\u4e86\uff0c\u4e0d\u7981\u6115\u7136\uff0c\u8fd9 \u56db\u4eba\u90fd\u662f\u4ed6\u7684\u540c\u95e8\u5144\u5f1f\uff0c\u5949\u5e08\u957f\u4e4b\u547d\uff0c\u5728\u4ed6\u4e4b\u524d\uff0c\u6765\u9655\u529e\u4e8b\u7684\uff0c\u600e\u7684\u5374\u5ffd\u7136\u90fd\u5728\u5be8\u4e2d\u51fa\u73b0\uff0c \u96be\u9053\u771f\u5982\u5f52\u6709\u7ae0\u6240\u8bf4\uff0c\u662f\u88ab\u7389\u7f57\u5239\u4fd8\u63b3\u4e86\u7684\uff1f\u4f46\u770b\u60c5\u5f62\u5374\u53c8\u4e0d\u4f3c\uff0c\u7389\u7f57\u5239\u628a\u624b\u4e00\u6325\uff0c\u91cc\u9762\u5df2 \u7aef\u51fa\u4e00\u5e2d\u9152\u83dc\uff0c\u7389\u7f57\u5239\u8bf7\u90a3\u56db\u4eba\u5c31\u5750\uff0c\u62ff\u4e86\u9152\u676f\uff0c\u7b11\u76c8\u76c8\u7684\u62db\u547c\u803f\u7ecd\u5357\u9053\uff1a\u201c\u54b1\u4eec\u5230\u90a3\u8fb9\u5e2d \u4e0a\u53bb\u5750\uff0c\u8ba9\u6211\u4e5f\u6709\u673a\u4f1a\u4e0e\u6b66\u5f53\u6d3e\u7684\u9ad8\u4eba\u4eb2\u8fd1\u4eb2\u8fd1 \u3000\u3000\u803f\u7ecd\u5357\u5fc3\u4e2d\u4e00\u61d4\uff0c\u4f46\u770b\u5979\u7b11\u5bb9\u53ef\u63ac\uff0c\u5fc3\u60f3\uff0c\u6b66\u5f53\u6d3e\u5a01\u540d\uff0c\u7fa4\u6d41\u666f\u4ef0\uff0c\u8fd9\u5973\u5f3a\u76d7\u867d\u7136\u51f6 \u72e0\uff0c\u60f3\u6765\u4e5f\u8981\u6151\u60e7\u6211\u4eec\u6b63\u6d3e\u7684\u95e8\u5f92\uff0c\u6240\u4ee5\u66f2\u610f\u9022\u8fce\uff0c\u8868\u793a\u62c9\u62e2\u3002\u60f3\u5230\u6b64\u5904\uff0c\u89c1\u7389\u7f57\u5239\u6108\u7b11\u6108 \u751c\uff0c\u4e0d\u89c9\u5fc3\u9b42\u6c64\u6f3e\uff0c\u8d8a\u53d1\u4ee5\u4e3a\u81ea\u5df2\u60f3\u5f97\u4e0d\u9519\u3002 \u3000\u3000\u5750\u5b9a\u4e4b\u5f8c\uff0c\u803f\u7ecd\u5357\u4e0e\u540c\u95e8\u62db\u547c\uff0c\u53ea\u89c1\u4ed6\u4eec\u4e2a\u4e2a\u90fd\u4f3c\u610f\u5b58\u987e\u5fcc\uff0c\u4e0d\u6562\u7545\u8c08\uff0c\u5185\u4e2d\u4e00\u4e24\u4eba\uff0c \u4e14\u82e6\u7b11\u4f5c\u6001\u3002\u803f\u7ecd\u5357\u83ab\u540d\u5176\u5999\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u7389\u7f57\u5239\u53c8\u5524\u4e00\u540d\u5973\u76d7\u524d\u6765\uff0c\u5429\u5490\u4e86\u51e0\u53e5\uff0c\u803f\u7ecd\u5357 \u4e0d\u77e5\u5979\u53c8\u6709\u4f55\u82b1\u6837\uff0c\u5c4f\u606f\u4ee5\u5f85\u3002\u7389\u7f57\u5239\u548c\u5927\u5bb6\u53c8\u4e7e\u4e86\u51e0\u676f\uff0c\u674f\u8138\u98de\u971e\uff0c\u8d8a\u53d1\u5a07\u8273\u3002\u5ffd\u7136\u5be8\u5f8c \u4e00\u7247\u8f66\u58f0\uff0c\u51e0\u5341\u540d\u55bd\u7f57\uff0c\u628a\u5353\u5bb6\u7684\u8f66\u8f86\u90fd\u63a8\u4e86\u51fa\u6765\uff0c\u6ee1\u5217\u9636\u4e0b\u3002\u7389\u7f57\u5239\u500f\u7136\u8d77\u7acb\uff0c\u6717\u58f0\u8bf4 \u9053\uff1a\u201c\u5353\u5927\u4eba\uff0c\u6211\u548c\u4f60\u7b97\u4e00\u7b97\u5e10\uff01\u201d\u5353\u4ef2\u5ec9\u60f6\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u70b9\u94f6\u4e24\uff0c\u5be8\u4e3b\u4f60\u62ff\u53bb\u597d\u4e86\u3002\u5353\u67d0 \u5bb6\u4e2d\u8fd8\u6709\u8584\u4ea7\uff0c\u4e0d\u5fc5\u501a\u9760  \u56ca\u3002\u201d\u7389\u7f57\u5239\u9762\u8272\u4e00\u6c89\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u7ec3\u9713\u88f3\u867d\u7136\u4e3a\u76d7\uff0c\u76d7\u4ea6 \u6709\u9053\uff0c\u4f60\u53ef\u95ee\u5e2d\u4e0a\u7684\u4eba\uff0c\u6211\u7ec3\u9713\u88f3\u51e0\u66fe\u4e71\u53d6\u8fc7\u4eba\u7684\u94f6\u5b50\u3002\u82e5\u7136\u4ed6\u771f\u662f\u6e05\u5b98\uff0c\u6211\u4e00\u6587\u4e5f\u4e0d\u8981\uff0c \u82e5\u7136\u4ed6\u662f\u4e2a\u8d2a\u5b98\uff0c\u54fc\uff0c\u6211\u53ef\u5bf9\u4ed6\u4e0d\u4f4f\uff0c\u94f6\u5b50\u4e5f\u8981\uff0c\u8111\u888b\u4e5f\u8981\uff0c\u4f60\u542c\u6e05\u695a\u6ca1\u6709\uff1f\u201d\u5353\u4ef2\u5ec9\u5413\u5f97 \u6d51\u8eab\u5927\u6c57\uff0c\u8eab\u5b50\u6296\u4e2a\u4e0d\u505c\uff0c\u5fc3\u4e2d\u6697\u6697\u53eb\u9053\uff1a\u201c\u7cdf\u4e86\uff0c\u7cdf\u4e86\uff0c\u60f3\u4e0d\u5230\u8001\u547d\u4e27\u5728\u8fd9\u513f\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u9a82\u5b8c\u4e4b\u5f8c\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u5353\u4ef2\u5ec9\uff0c\u4f60\u4e14\u542c\u7740\uff0c\u4f60\u505a\u4e86\u5341\u591a\u5e74\u5b98\uff0c\u6536\u5230\u4e0b\u5c5e\u4e0e\u5730\u65b9 \u7ec5\u58eb\u6240\u9001\u7684\u94f6\u4e24\u5171\u662f\u4e03\u4e07\u516d\u5343\u4e03\u767e\u4e24\uff0c\u8fd9\u7b14\u94b1\u4e43\u662f\u4e0d\u4e49\u4e4b\u8d22\uff0c\u6211\u5168\u53d6\u4e86\u3002\u53e6\u5916\u94b1\u7cae\u7684\u6298\u5934\u662f \u53c1\u4e07\u4e8c\u5343\u4e94\u767e\u4e24\uff0c\u8fd9\u7b14\u94b1\u867d\u662f\u671d\u5ead\u5b9a\u4f8b\uff0c\u4f46\u5374\u662f\u51fa\u81ea\u767e\u59d3\uff0c\u6211\u4e5f\u8981\u53d6\u4e86\uff0c\u4ee3\u4f60\u8fd8\u4e4b\u65bc\u6c11\u3002\u53e6 \u5916\u4f60\u7684\u4ff8\u94f6\u662f\u4e00\u4e07\u516d\u5343\u516b\u767e\u4e24\uff0c\u8fd9\u662f\u4f60\u5e94\u5f97\u7684\uff0c\u6211\u53d1\u8fd8\u7ed9\u4f60\u3002\u4f60\u505a\u4e86\u5341\u591a\u5e74\u5b98\uff0c\u6cb9\u6c34\u4ec5\u6709\u5341 \u4e07\u591a\u4e24\uff0c\u4f60\u7b97\u4e0d\u5f97\u6e05\u5b98\uff0c\u4f46\u4e5f\u8fd8\u7b97\u4e0d\u5f97\u8d2a\u5b98\uff0c\u53ea\u7b97\u5f97\u4e00\u540d\u89c4\u89c4\u77e9\u77e9\u7684\u671d\u5ef7\u5927\u540f\u3002\u73b0\u5728\u5e10\u5df2\u7b97 \u6e05\uff0c\u4f60\u670d\u4e5f\u4e0d\u670d\uff01\u201d\u5353\u4ef2\u5ec9\u4e0d\u7981\u53c8\u60ca\u53c8\u559c\uff0c\u7389\u7f57\u5239\u5bf9\u4ed6\u7684  \u56ca\u6536\u5165\uff0c\u7adf\u7136\u5982\u6570\u5bb6\u73cd\uff0c\u8d26\u76ee\u5206 \u660e\uff0c\u4e1d\u6beb\u4e0d\u9519\uff0c\u4e5f\u4e0d\u77e5\u5979\u4ece\u90a3\u91cc\u4fa6\u5bdf\u5f97\u6765\uff1f\u4e3b\u7f57\u5239\u5904\u7f6e\u5b8c\u6bd5\uff0c\u53c8\u7b11\u76c8\u76c8\u7684\u5750\u4e0b\uff0c\u6328\u5728\u803f\u7ecd\u5357 \u8eab\u65c1\uff0c\u8bf4\u9053\uff1a\u201c\u6b66\u5f53\u6d3e\u7684\u9ad8\u8d24\uff0c\u5c0f\u59b9\u5e74\u8f7b\u8bc6\u6d45\uff0c\u4e8b\u60c5\u505a\u5f97\u4e0d\u5f53\uff0c\u8fd8\u8bf7\u6307\u6559\u3002\u201d\u803f\u7ecd\u5357\u5bf9\u5979\u521a \u624d\u8fd9\u624b\uff0c\u5012\u662f\u5341\u5206\u4f69\u670d\uff0c\u7fd8\u8d77\u62c7\u6307\u8bf4\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u7ec3\u5973\u4fa0\u5a01\u9707\u7eff\u6797\uff0c\u679c\u7136\u662f\u8d4f\u7f5a\u5206\u660e\uff0c\u4eca\u4eba \u8d77\u656c\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u6362\u8fc7\u70ed\u9152\uff0c\u548c\u803f\u7ecd\u5357\u6d45\u7684\u8f7b\u8c08\uff0c\u7b11\u9765\u542b\u8202\uff0c\u803f\u7ecd\u5357\u5927\u6709\u9152\u610f\uff0c\u53ea\u89c9\u7389\u7f57\u5239\u5439\u6c14\u5982 \u5170\uff0c\u4ee4\u4eba\u5fc3\u52a8\u3002\u4e0d\u7981\u60f3\u9053\uff1a\u8fd9\u7389\u7f57\u5239\u5012\u662f\u53ef\u4eba\uff0c\u53ea\u53ef\u60dc\u5979\u7edd\u4ee3\u4f73\u4eba\uff0c\u7518\u5fc3\u4f5c\u8d3c\uff0c\u82e5\u7136\u56de\u8f6c\u6b63 \u9014\uff0c\u4e0d\u77e5\u8981\u503e\u5012\u591a\u5c11\u82f1\u96c4\u4fa0\u5ba2\uff01\u634c\u9163\u8033\u70ed\uff0c\u7a81\u7136\u95ee\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\u6b66\u827a\u8d85\u7fa4\uff0c\u4e0d\u77e5\u5c0a\u5e08\u662f\u90a3\u4e00 \u4f4d\uff1f\u803f\u67d0\u82e5\u5f97\u673a\u4f1a\uff0c\u5f53\u5411\u5973\u4fa0\u8ba8\u6559\uff0c\u90a3\u771f\u662f\u5feb\u4f55\u5982\u4e4b\u3002\u53ea\u53ef\u60dc\u7ea2\u82b1\u7eff\u53f6\uff0c\u867d\u51fa\u4e00\u5bb6\uff0c\u67b3\u6a58\u6b8a \u9014\uff0c\u751c\u9178\u5374\u5f02\u3002\u53ea\u6015\u4ee5\u5f8c\u518d\u96be\u6709\u673a\u4f1a\u76f8\u805a\u4e86\uff01\u201d\u8fd9\u8bdd\u91cc\u4e00\u65b9\u9762\u8868\u9732\u4e86\u503e\u6155\u4e4b\u5fc3\uff0c\u53e6\u65b9\u9762\u5374\u53c8 \u8868\u9732\u4e86\u60cb\u60dc\u4e4b\u610f\uff0c\u6697\u6307\u7389\u7f57\u5239\u4e43\u662f\u201c\u903e\u6dee\u4e4b\u67b3\u201d\uff0c\u672c\u6765\u662f\u5927\u597d\u7684\u6a58\uff0c\u5374\u53d8\u574f\u4e86\u3002\u738b\u7167\u5e0c\u4e00\u542c \u4ed6\u53e3\u4e0d\u62e9\u8a00\uff0c\u614c\u5fd9\u8bf4\u9053\uff1a\u201c\u803f\u5144\u9189\u4e86\uff0c\u4e0d\u53ef\u518d\u996e\u4e86\u3002\u201d\u803f\u7ecd\u5357\u6447\u5934\u6446\u8111\u7684\u9053\uff1a\u201c\u6211\u6ca1\u9189\uff0c\u8c01 \u8bf4\u6211\u9189\uff01\u201d\u7389\u7f57\u5239\u5148\u662f\u9762\u8272\u4e00\u6c89\uff0c\u7ee7\u800c\u7b11\u5f97\u82b1\u679d\u4e71\u98a4\uff0c\u4e3e\u676f\u8bf4\u9053\uff1a\u201c\u8c22\u803f\u5927\u82f1\u96c4\u8fc7\u5956\uff0c\u6211\u662f \u4e00\u4e2a\u65e0\u7236\u65e0\u6bcd\u53c8\u65e0\u5e08\u5c0a\u7684\u91ce\u5973\u90ce\uff0c\u8fd9\u51e0\u624b\u53c1\u811a\u732b\u7684\u529f\u592b\uff0c\u90fd\u662f\u81ea\u5df2\u7ec3\u6765\u7684\u3002\u90a3\u6bd4\u5f97\u803f\u5927\u82f1\u96c4 \u662f\u540d\u95e8\u5f1f\u5b50\uff0c\u6b63\u6d3e\u6b66\u529f\u3002\u201d\u7ea4\u624b\u8f7b\u63a0\u4e91\u9b13\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u6211\u4e5f\u5f88\u60f3\u5411\u803f\u82f1\u96c4\u8ba8\u6559\uff0c\u673a\u4f1a\u6709\u7684 \u662f\uff0c\u803f\u82f1\u96c4\u4e0d\u7528\u5fc3\u6025\u3002\u201d\u5750\u4e86\u4e0b\u6765\uff0c\u5411\u803f\u7ecd\u5357\u98d8\u4e86\u4e00\u773c\uff0c\u7b11\u5f97\u66f4\u662f\u5a07\u5a9a\uff0c\u738b\u7167\u5e0c\u6c57\u6bdb\u5012\u7ad6\uff0c \u6697\u6028\u803f\u7ecd\u5357\u72b9\u662f\u6beb\u4e0d\u77e5\u89c9\uff0c\u6025\u5fd9\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u8c22\u5be8\u4e3b\u9152\u5e2d\uff0c\u803f\u5144\u5df2\u9189\uff0c\u5c0f\u5f1f\u4e5f\u4e0d\u80dc\u9152\u529b\uff0c\u6c42 \u5be8\u4e3b\u6055\u7f6a\uff0c\u6211\u4eec\u60f3\u544a\u9000\u4e86\u3002\u201d\u7389\u7f57\u5239\u9762\u6709\u4e0d\u8c6b\u4e4b\u8272\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u5012\u5f88\u5e2e\u7740\u4ed6\u3002\u201d\u738b\u7167\u5e0c \u9f13\u8d77\u52c7\u6c14\uff0c\u4f4e\u58f0\u56de\u9053\uff1a\u201c\u6211\u548c\u803f\u5144\u4e5f\u662f\u7d20\u4e0d\u68c9\u8bc6\uff0c\u8def\u4e0a\u4ed6\u66ff\u6211\u6321\u4e86\u4e00\u9635\u8ffd\u5175\uff0c\u4ed6\u65e2\u62ff\u6211\u5f53\u670b \u53cb\u770b\u5f85\uff0c\u6240\u4ee5\u6211\u4e5f\u62ff\u4ed6\u5f53\u670b\u53cb\u770b\u5f85\u3002\u201d\u7389\u7f57\u5239\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u6325\u6325\u624b\u9053\uff1a\u201c\u64a4\u5e2d\u3002\u201d\u5374\u53c8\u4f4e \u58f0\u5bf9\u803f\u7ecd\u5357\u9053\uff1a\u201c\u660e\u65e5\u6e05\u6668\uff0c\u8bf7\u5230\u5c71\u8170\u7684\u5ce1\u8c37\u76f8\u4f1a\u3002\u803f\u82f1\u96c4\u4e0d\u8981\u5fd8\u4e86\u3002\u201d\u803f\u7ecd\u5357\u559c\u4e0a\u7709\u68a2\uff0c \u8fde\u58f0\u8bf4\u9053\uff1a\u201c\u5be8\u4e3b\u5429\u5490\uff0c\u90a3\u91cc\u6562\u5fd8\u3002\u201d\u7389\u7f57\u5239\u53eb\u4eba\u64a4\u53bb\u9152\u5e2d\uff0c\u628a\u803f\u7ecd\u5357\u3001\u738b\u7167\u5e0c\u548c\u5176\u4ed6\u56db\u4e2a \u6b66\u5f53\u95e8\u4eba\u90fd\u5206\u5f00\u62db\u5f85\u3002\u738b\u7167\u5e0c\u60f3\u548c\u803f\u7ecd\u5357\u8bf4\u51e0\u65ec\u79c1\u8bdd\uff0c\u4e5f\u6ca1\u529e\u6cd5\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u6e05\u6668\uff0c\u803f\u7ecd\u5357\u5bbf\u9152\u672a\u6d88\uff0c\u4e00\u4e2a\u5973\u55bd\u5175\u8fdb\u6765\u53eb\u9053\uff1a\u201c\u803f\u82f1\u96c4\uff0c\u6211\u4eec\u5be8\u4e3b\u7ea6\u4f60\u3002\u201d\u803f \u7ecd\u5357\u614c\u5fd9\u6f31\u6d17\uff0c\u7ed3\u675f\u505c\u5f53\uff0c\u968f\u5973\u55bd\u5175\u8d70\u4e0b\u5c71\u8170\uff0c\u8fdb\u5165\u53cc\u5cf0\u73af\u62b1\u7684\u5ce1\u8c37\uff0c\u53ea\u89c1\u81ea\u5df1\u56db\u4e2a\u540c\u95e8\uff0c \u90fd\u5df2\u5019\u5728\u90a3\u513f\uff0c\u738b\u7167\u5e0c\u5219\u5750\u5728\u53e6\u4e00\u8fb9\u3002\u5353\u4ef2\u5ec9\u4e5f\u7531\u4e24\u4e2a\u5973\u55bd\u5175\u966a\u7740\uff0c\u5750\u5728\u4e00\u5757\u5927\u77f3\u5934\u4e0a\u3002\u7389 \u7f57\u5239\u4ece\u5c71\u5773\u4e71\u77f3\u5806\u4e2d\u7b11\u76c8\u76c8\u7684\u8d70\u4e86\u51fa\u6765\uff0c\u53d1\u675f\u91d1\u73af\uff0c\u8170\u60ac\u957f\u5251\uff0c\u66f4\u663e\u5f97\u98ce\u59ff\u7edd\u4fd7\u3002\u803f\u7ecd\u5357\u89c1 \u6b64\u60c5\u666f\uff0c\u4e0d\u7981\u5927\u5947\uff01 \u3000\u3000\u803f\u7ecd\u5357\u6ee1\u809a\u5bc6\u5708\uff0c\u672c\u4ee5\u4e3a\u7389\u7f57\u5239\u7ea6\u4ed6\u5355\u72ec\u7ea6\u4f1a\uff0c\u90a3\u6599\u5979\u5374\u9080\u4e86\u8bb8\u591a\u4eba\u6765\u3002\u7389\u7f57\u5239\u8f7b\u79fb\u83b2 \u6b65\uff0c\u8863\u8882\u98ce\u98d8\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u803f\u82f1\u96c4\uff0c\u4f60\u65e9\uff0c\u6628\u665a\u7761\u5f97\u597d\u5440\uff1f\u201d\u8bed\u8c03\u7adf\u4f3c\u751a\u4e3a\u5173\u6000\uff0c\u803f\u7ecd\u5357 \u9762\u4e0a\u4e00\u7ea2\uff0c\u5c34\u5c2c\u7b54\u9053\uff1a\u201c\u597d\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u6211\u5c31\u6015\u4f60\u6628\u665a\u7761\u5f97\u4e0d\u597d\uff0c\u82e5\u6628\u665a\u7761\u5f97\u4e0d\u597d\uff0c \u4eca\u665a\u4f60\u53c8\u4e0d\u80fd\u5b89\u7761\uff0c\u90a3\u591a\u53ef\u601c\u5462\uff01\u201d\u803f\u7ecd\u5357\u6115\u7136\u60f3\u9053\uff1a\u201c\u5979\u600e\u80fd\u65ad\u5b9a\u6211\u4eca\u665a\u5c31\u4e0d\u80fd\u5b89\u7761\uff1f\u90a3 \u4e0d\u662f\u75af\u8bdd\u5417\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u5982\u679c\u4f60\u53d7\u4e86\u91cd\u4f24\uff0c\u6216\u8005\u6b8b\u4e86\u80a2\u4f53\uff0c\u4f60\u4eca\u665a\u4e00\u5b9a\u4e0d\u80fd\u5b89\u7761\u4e86\u662f \u5417\uff1f\u201d\u803f\u7ecd\u5357\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5929\u6709\u4e0d\u6d4b\u4e4b\u98ce\u4e91\uff0c\u4eba\u6709\u65e6\u5915\u4e4b\u7978\u798f\uff0c\u82e5\u7136\u771f\u4e2a\u6a2a\u7978\u4e34\u5934\uff0c\u90a3\u53c8\u6709 \u4ec0\u9ebd\u529e\u6cd5\uff1f\u4f46\u9664\u975e\u662f\u5be8\u4e3b\u8981\u628a\u6211\u96be\u4e3a\uff0c\u5426\u5219\u6211\u53c8\u600e\u4f1a\u6709\u98de\u6765\u6a2a\u6da1\uff1f\u201d\u7389\u7f57\u5239\u5ffd\u9053\uff1a\u201c\u4f60\u5012\u8c41 \u8fbe\uff0c\u6211\u5c82\u6562\u628a\u4f60\u96be\u4e3a\uff0c\u6211\u53ea\u662f\u60f3\u5411\u4f60\u8ba8\u6559\uff0c\u6211\u542c\u8bf4\u6b66\u5f53\u6d3e\u5251\u6cd5\u5929\u4e0b\u65e0\u53cc\uff0c\u6211\u5012\u5f88\u60f3\u5f00\u5f00\u773c \u754c\u3002\u201d\u803f\u7ecd\u5357\u4e0d\u7531\u5f97\u6c14\u5f80\u4e0a\u51b2\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u5be8\u4e3b\u679c\u7136\u8981\u4f38\u91cf\u65bc\u6211\uff0c\u5927\u4e08\u592b\u5b81\u6b7b\u4e0d \u8fb1\uff0c\u6211\u62da\u53d7\u5be8\u4e3b\u53c1\u5200\u516d\u6d1e\uff0c\u65ad\u4f53\u6b8b\u80a2\uff0c\u4e5f\u4e0d\u80fd\u5815\u4e86\u6211\u6b66\u5f53\u5c71\u7684\u5a01\u671b\uff01\u201d\u7389\u7f57\u5239\u76c8\u76c8\u7b11\u9053\uff1a \u201c\u597d\uff0c\u90a3\u4f60\u53ef\u8981\u7559\u795e\u4e00\u70b9\uff0c\u6211\u8981\u8fdb\u62db\u4e86\u3002\u201d\u62d4\u5251\u5728\u624b\uff0c\u8f7b\u8f7b\u523a\u6765\uff0c\u803f\u7ecd\u5357\u89c1\u5979\u5251\u62db\u6781\u6162\uff0c\u72b6 \u7c7b\u513f\u620f\uff0c\u4e5f\u4e0d\u77e5\u5979\u662f\u771f\u662f\u5047\uff0c\u4e3e\u5251\u4e00\u6321\uff0c\u90a3\u77e5\u7389\u7f57\u5239\u624b\u8155\u4e00\u7ffb\uff0c\u5251\u5c16\u5df2\u523a\u5230\u5589\u5499\uff0c\u5a07\u7b11\u9053\uff1a \u201c\u4f60\u8fd9\u62db\u4e0d\u884c\uff0c\u53e6\u6765\u8fc7\uff01\u201d\u803f\u7ecd\u5357\u89c1\u5979\u6301\u5251\u4e0d\u523a\uff0c\u5374\u53d1\u8bed\u51b7\u5632\uff0c\u6bd4\u4e2d\u5251\u66f4\u4e3a\u96be\u8fc7\uff0c\u500f\u7684\u4e00\u4e2a \u95ea\u8eab\uff0c\u7528\u8fde\u73af\u5251\u4e2d\u7684\u53c1\u7edd\u62db\u731b\u7136\u51fa\u624b\uff0c\u5934\u4e00\u62db\u201c\u91d1\u9488\u5ea6\u7ebf\u201d\uff0c\u5251\u5c16\u659c\u70b9\uff0c\u4e00\u8f6c\u8eab\u4fbf\u53d8\u6210\u201c\u62bd \u6492\u8fde\u73af\u201d\uff0c\u70b9\u54bd\u5589\uff0c\u6302\u4e24\u81c2\uff0c\u5feb\u903e\u98d8\u98ce\uff0c\u90a3\u77e5\u5237\u5237\u4e24\u5251\uff0c\u5168\u843d\u4e86\u7a7a\uff0c\u7b2c\u53c1\u62db\u5c1a\u672a\u4f7f\u51fa\uff0c\u80cc\u810a \u5df2\u662f\u51b7\u6c14\u68ee\u68ee\uff0c\u7389\u7f57\u5239\u7684\u5251\u950b\u7adf\u8d34\u5230\u4e86\u5f8c\u5fc3\uff0c\u53c1\u7edd\u62db\u65e0\u6cd5\u8fde\u73af\u4f7f\u7528\uff0c\u6025\u5fd9\u65bd\u5c55\u201c\u65e9\u5730\u62d4\u8471\u201d \u8eab\u6cd5\uff0c\u5f80\u4e0a\u62d4\u8eab\uff0c\u5ffd\u7136\u5934\u9876\u53c8\u662f\u5fae\u98ce\u98d2\u7136\uff0c\u7389\u7f57\u5239\u5251\u950b\u8fc7\u5904\uff0c\u628a\u803f\u7ecd\u5357\u7684\u5934\u53d1\u5272\u4e86\u4e00\u7efa\uff0c\u803f \u7ecd\u5357\u843d\u4e0b\u5730\u65f6\uff0c\u7389\u7f57\u5239\u53c8\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u6211\u53eb\u4f60\u7559\u795e\uff0c\u4f60\u600e\u9ebd\u4e0d\u7559\u795e\u5440\uff01\u201d\u62b1\u5251\u4e00\u7acb\uff0c\u62db\u62db\u624b \u9053\uff1a\u201c\u6b66\u5f53\u6d3e\u7684\u5217\u4f4d\u9ad8\u4eba\uff0c\u5fcd\u5fc3\u770b\u4f60\u4eec\u7684\u540c\u95e8\u5728\u8fd9\u91cc\u800d\u7334\u620f\u5417\uff1f\u201d\u803f\u7ecd\u5357\u7684\u56db\u4e2a\u5e08\u5144\u5f1f\u90a3\u8fd8 \u5fcd\u53d7\u5f97\u4f4f\uff0c\u56db\u67c4\u5251\u8054\u6210\u4e00\u7ebf\uff0c\u500f\u7136\u8fdb\u653b\uff0c\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u8fd9\u624d\u75db\u5feb\u3002\u201d\u5251\u5149\u95ea\u95ea\uff0c\u5728\u6b66\u5f53\u4e94 \u5251\u56f4\u653b\u4e4b\u4e0b\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u738b\u7167\u5e0c\u89c1\u4e0d\u662f\u8def\uff0c\u6025\u5fd9\u8df3\u8d77\u6765\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\u624b\u4e0b\u7559 \u60c5\uff01\u201d\u8bed\u8fd8\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u65ad\u91d1\u621b\u7389\u4e4b\u58f0\uff0c\u63a5\u7740\u662f\u8fde\u58f0\u60e8\u53eb\uff0c\u6b66\u5f53\u4e94\u4e2a\u95e8\u4eba\uff0c\u624b\u4e2d\u957f\u5251\u5168 \u88ab\u622a\u65ad\uff0c\u803f\u7ecd\u5357\u65ad\u4e86\u5de6\u624b\u4e24\u6307\uff0c\u5176\u9980\u56db\u4eba\u4e5f\u5404\u65ad\u4e86\u4e00\u6307\u3002\u7389\u7f57\u5239\u9762\u631f\u5bd2\u971c\uff0c\u5389\u58f0\u53f1\u9053\uff1a\u201c\u53eb \u4f60\u4eec\u77e5\u9053\u5929\u5916\u6709\u5929\uff0c\u4e0d\u80fd\u5f92\u501a\u5e08\u95e8\u58f0\u671b\uff01\u803f\u7ecd\u5357\u4f60\u6628\u665a\u5341\u5206\u65e0\u793c\uff0c\u6211\u672c\u5f85\u65ad\u4f60\u624b\u81c2\uff0c\u525c\u4f60\u53cc \u76ee\uff0c\u4eca\u65e5\u89c1\u4f60\u4e5f\u8fd8\u6709\u70b9\u7537\u513f\u6c14\u6982\uff0c\u51cf\u5211\u53c1\u7b49\uff0c\u4f60\u5feb\u5feb\u6eda\u4e0b\u5c71\u53bb\uff01\u201d \u3000\u3000\u738b\u7167\u5e0c\u542c\u5f97\u7389\u7f57\u5239\u5389\u58f0\u53f1\u9a82\uff0c\u653e\u4e0b\u4e86\u5fc3\uff0c\u8dc3\u4e0a\u524d\u53bb\uff0c\u53ea\u89c1\u803f\u7ecd\u5357\u9762\u8272\u60e8\u767d\uff0c\u4e0d\u53d1\u4e00\u8a00\uff0c \u62e8\u5934\u4fbf\u8d70\u3002\u5176\u9980\u56db\u4f4d\u6b66\u5f53\u5f1f\u5b50\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u5be8\u4e3b\u7559\u60c5\uff0c\u6b64\u6069\u6b64\u5fb7\uff0c\u6c38\u4e0d\u6562\u5fd8\uff01\u201d\u7389\u7f57 \u5239\u51b7\u7b11\u9053\uff1a\u201c\u6211\u7b49\u7740\u4f60\u4eec\u6765\u62a5\u4ec7\u4fbf\u662f\u3002\u201d\u738b\u7167\u5e0c\u6025\u4f7f\u773c\u8272\uff0c\u793a\u610f\u53eb\u4ed6\u4eec\u4e0d\u8981\u591a\u8bf4\uff0c\u5176\u4e2d\u4e00\u4e2a \u4e2d\u5e74\u6c49\u5b50\uff0c\u4f3c\u662f\u4e94\u4e2a\u540c\u95e8\u4e4b\u9996\uff0c\u5ffd\u7136\u671d\u738b\u7167\u5e0c\u515c\u5934\u4e00\u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u738b\u516c\u5b50\uff0c\u655d\u5e08\u5f1f\u5728\u8def\u4e0a\u591a \u627f\u7167\u987e\uff0c\u53ef\u60dc\u6211\u6ca1\u65e9\u9047\u89c1\u4f60\uff0c\u5b5f\u6b66\u5e08\u7684\u4fe1\uff0c\u73b0\u5728\u8f6c\u4ea4\u7ed9\u4f60\u3002\u201d\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u5c01\u706b\u6f06\u5bc6\u5c01\u51fd \u4ef6\uff0c\u738b\u7167\u5e0c\u5fc3\u5934\u4e00\u9707\uff0c\u659c\u773c\u5077\u77a7\u7389\u7f57\u5239\u795e\u60c5\uff0c\u4e3b\u7f57\u5239\u671f\u7136\u8bf4\u9053\uff1a\u201c\u522b\u4eba\u4e07\u91cc\u8fe2\u8fe2\uff0c\u7ed9\u4f60\u9001 \u4fe1\uff0c\u4f60\u4e5f\u8be5\u591a\u8c22\u522b\u4eba\u4e00\u58f0\u3002\u201d\u738b\u7167\u5e0c\u770b\u5979\u5e76\u65e0\u6076\u610f\uff0c\u628a\u4fe1\u63a5\u8fc7\uff0c\u9053\u4e86\u53e5\u8c22\uff0c\u56db\u4e2a\u6b66\u5f53\u95e8\u4eba\u5634 \u89d2\u6302\u7740\u51b7\u7b11\uff0c\u4e5f\u4e0d\u8fd8\u793c\uff0c\u6025\u6b65\u4e0b\u51fa\u53bb\u4e86\u3002\u738b\u7167\u5e0c\u5fc3\u5934\u4e0d\u7531\u5f97\u4e00\u9635\u9635\u96be\u8fc7\uff0c\u6df1\u89c9\u81ea\u5df1\u5bf9\u6b66\u5f53\u6d3e \u4e0d\u4f4f\u3002 \u3000\u3000\u7389\u7f57\u5239\u770b\u803f\u7ecd\u5357\u7b49\u4eba\u80cc\u5f71\u6d88\u5931\u4e4b\u5f8c\uff0c\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u738b\u5144\uff0c\u4f60\u4e00\u5b9a\u9a82\u6211\u624b\u5e95\u592a\u8fa3\u4e86\uff1f\u201d\u738b \u7167\u5e0c\u9053\uff1a\u201c\u4e0d\u6562\u3002\u201d\u5176\u5b9e\u4ed6\u5fc3\u91cc\u786e\u5728\u6697\u9a82\u3002\u7389\u7f57\u5239\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u7684\u813e\u6c14\u6700\u62b5\u4e0d\u4f4f\u4eba\u6043\u52bf\u79f0 \u5f3a\uff0c\u6b66\u5f53\u6d3e\u95e8\u5f92\u4f17\u591a\uff0c\u8d24\u611a\u4e0d\u8096\uff0c\u5728\u6240\u591a\u6709\u3002\u5176\u4e2d\u4e0d\u5c11\u4eba\u6043\u7740\u5e08\u95e8\u5a01\u671b\uff0c\u76ee\u7a7a\u4e00\u5207\uff0c\u6b66\u5f53\u4e94 \u8001\uff0c\u9664\u7d2b\u9633\u9053\u957f\u4e4b\u5916\uff0c\u5176\u9980\u56db\u4eba\uff0c\u90fd\u6709\u62a4\u77ed\u7684\u6bdb\u75c5\uff0c\u4ee5\u81f4\u95e8\u5f92\u8d8a\u53d1\u56a3\u5f20\uff0c\u6b63\u662f\u867d\u65e0\u8fc7\u9519\uff0c\u9762 \u76ee\u53ef\u618e\uff0c\u6211\u4eca\u65e5\u7279\u5730\u8981\u6298\u632b\u4ed6\u4eec\u7684\u9a84\u6c14\uff0c\u6559\u8bad\u6559\u8bad\u4ed6\u4eec\u3002\u201d\u738b\u7167\u5e0c\u4e0d\u6562\u4f5c\u58f0\uff0c\u7389\u7f57\u5239\u505c\u4e86\u4e00 \u505c\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u542c\u8bf4\u4eac\u4e2d\u7684\u5b5f\u707f\u6b66\u5e08\u4e0e\u4ee4\u5c0a\u4e43\u662f\u516b\u62dc\u4e4b\u4ea4\uff1f\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u4e5f\u662f\u655d\u5cb3\u3002\u201d \u7389\u7f57\u5239\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u8fd8\u662f\u4eb2\u5bb6\uff0c\u90a3\u76ca\u53d1\u597d\u4e86\u3002\u5b5f\u5c0f\u59d0\u6211\u4e5f\u66fe\u6709\u8fc7\u4e00\u9762\u4e4b\u7f18\uff0c\u6b66\u529f\u4eba\u54c1\u90fd\u662f \u4e0a\u4e0a\u4e4b\u9009\u3002\u5b5f\u5c0f\u59d0\u672a\u8fc7\u95e8\u5427\uff1f\u201d\u738b\u7167\u5e0c\u9762\u4e0a\u4e00\u7ea2\uff0c\u7b54\u9053\uff1a\u201c\u672a\u3002\u5bb6\u7236\u53eb\u6211\u8c12\u89c1\u5973\u4fa0\u4e4b\u5f8c\uff0c\u5c31 \u8fdb\u4eac\u628a\u655d\u5cb3\u7236\u5973\u63a5\u6765\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4e5f\u8be5\u63a5\u4ed6\u4eec\u6765\u4e86\uff0c\u5728\u4eac\u4e2d\u505a\u7687\u5ba4\u7684\u6b66\u5e08\u6709\u4ec0\u9ebd\u51fa\u8272\uff01 \u54ce\uff0c\u6211\u4e00\u5411\u76f4\u7387\uff0c\u738b\u5144\u4f60\u522b\u89c1\u602a\u3002\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u5c82\u6562\uff0c\u5bb6\u7236\u4e5f\u662f\u8fd9\u6837\u8bf4\u6cd5\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a \u201c\u4e0d\u662f\u6211\u89c1\u5230\u5b5f\u6b66\u5e08\u7684\u4fe1\uff0c\u90a3\u56db\u4eba\u8fd8\u8981\u591a\u5403\u82e6\u5934\u5462\uff01\u4ed6\u4eec\u626e\u6210\u76ae\u8349\u5ba2\u5546\uff0c\u706b\u7075\u733f\u6731\u5b9d\u693f\u7684\u624b \u4e0b\u534a\u8def\u622a\u52ab\u4ed6\u4eec\uff0c\u6309\u8bf4\u4ed6\u4eec\u82e5\u628a\u6765\u5386\u8bf4\u660e\uff0c\u4fbf\u6ca1\u4e8b\u4e86\u3002\u4ed6\u4eec\u504f\u504f\u6043\u5f3a\u901e\u80fd\uff0c\u628a\u706b\u7075\u733f\u7684\u56db\u4e2a \u5934\u76ee\u4f24\u4e86\u3002\u662f\u6211\u770b\u4e0d\u8fc7\u773c\uff0c\u5355\u9a91\u8ffd\u8e2a\uff0c\u7528\u7ef5\u638c\u51fb\u77f3\u5982\u7c89\u7684\u529f\u592b\uff0c\u628a\u4ed6\u4eec\u9707\u4f4f\uff0c\u8bf7\u4ed6\u4eec\u4e0a\u5c71\u7814 \u7a76\u7814\u7a76\u5251\u6cd5\u3002\u201d\u738b\u7167\u5e0c\u5fc3\u91cc\u53eb\u82e6\uff0c\u6697\u9053\uff1a\u8fd9\u6837\u7684\u201c\u7814\u7a76\u201d\u6cd5\u53ea\u6015\u8981\u60f9\u8d77\u6b66\u6797\u7edd\u5927\u7684\u98ce\u6ce2\u3002 \u3000\u3000\u738b\u7167\u5e0c\u5c1a\u6b32\u8fdb\u8a00\uff0c\u7389\u7f57\u5239\u5ffd\u9053\uff1a\u201c\u54a6\uff0c\u90a3\u4e2a\u59d3\u5353\u7684\u5b98\u513f\u5462\uff1f\u201d\u53eb\u4e86\u4e24\u58f0\uff0c\u4e0d\u89c1\u56de\u7b54\uff0c\u8d70 \u53bb\u627e\u5bfb\uff0c\u539f\u6765\u5353\u4ef2\u5ec9\u88ab\u5979\u62c9\u6765\u89c2\u6218\uff0c\u770b\u5f97\u5fc3\u60ca\u80c6\u6218\uff0c\u7adf\u7136\u6655\u5012\u5728\u4e71\u77f3\u5806\u4e2d\u3002\u6b63\u662f\uff1a\u7b11\u8bed\u6cae\u8a00 \u65bd\u6bd2\u624b\uff0c\u73de\u65c1\u715e\u9526\u57ce\u4faf\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377\u2014\u2014\u7b2c\u4e8c\u56de\u3000\u9707\u52a8\u4eac\u534e\u3000\u60ca\u4f20\u6883\u51fb\u6848\u3000\u6ce2\u7ffb\u5927\u5185\u3000\u5de7\u9047\u591c\u884c\u4eba \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377 \u7b2c\u4e8c\u56de \u9707\u52a8\u4eac\u534e\u3000\u60ca\u4f20\u6883\u51fb\u6848 \u6ce2\u7ffb\u5927\u5185\u3000\u5de7\u9047\u591c\u884c\u4eba \u3000\u3000\u7389\u7f57\u5239\u9a82\u58f0 \u201c\u4e8f\u4ed6\u662f\u4e2a\u5c01\u7586\u5927\u540f\uff0c\u80c6\u5b50\u6bd4\u82a5\u5b50\u8fd8\u5c0f\u3002\u201d\u5728\u5353\u4ef2\u5ec9\u8eab\u4e0a\u62cd\u4e86\u4e24\u4e0b\uff0c\u5353\u4ef2 \u5ec9\u8fd9\u624d\u60a0\u60a0\u9192\u8f6c\u3002\u7389\u7f57\u5239\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u9762\u4ee4\u65d7\uff0c\u63b7\u7ed9\u4ed6\u9053\uff1a\u201c\u6211\u628a\u4f60\u7684\u4fdd\u9556\u6253\u53d1\u8d70\u4e86\uff0c\u73b0\u5728 \u8fd8\u4e00\u4e2a\u7ed9\u4f60\u3002\u201d\u5353\u4ef2\u5ec9\u6115\u7136\u4e0d\u89e3\uff0c\u7389\u7f57\u5239\u559d\u9053\uff1a\u201c\u4f60\u628a\u8fd9\u9762\u4eca\u65d7\u62ff\u53bb\uff0c\u63d2\u5728\u8f66\u4e0a\uff0c\u9655\u897f\u7701 \u5185\uff0c\u6ca1\u4eba\u6562\u52a8\u4f60\u5206\u6beb\uff0c\u6bd4\u4f60\u90a3\u4e2a\u4ec0\u9ebd\u6b66\u5f53\u6d3e\u7684\u4fdd\u9556\u8981\u5f3a\u5f97\u591a\uff01\u201d\u5353\u4ef2\u5ec9\u5927\u559c\u8fc7\u671b\uff0c\u614c\u5fd9\u6536\u4e86 \u4ee4\u65d7\uff0c\u6b63\u5f85\u53e9\u8c22\uff0c\u7389\u7f57\u5239\u5df2\u548c\u738b\u7167\u5e0c\u8d70\u4e86\u3002 \u3000\u3000\u738b\u7167\u5e0c\u62c6\u5f00\u5cb3\u7236\u7684\u4fe1\u4e00\u770b\uff0c\u4fe1\u7684\u524d\u534a\u6bb5\u662f\u50ac\u4ed6\u8d74\u4eac\u8fce\u4eb2\uff0c\u5f8c\u534a\u6bb5\u5374\u8bf4 \u201c\u4eac\u4e2d\u6b66\u5e08\uff0c\u6697 \u6597\u6781\u70c8\uff0c\u5c24\u4ee5\u5bab\u5ef7\u4e4b\u5185\uff0c\u9669\u8c61\u73af\u751f\uff0c\u671b\u8d24\u5a7f\u901f\u6765\uff0c\u611a\u6b63\u6709\u4e8b\u76f8\u5546\u4e5f\u3002\u201d\u539f\u6765\u738b\u7167\u5e0c\u7684\u7236\u4eb2\u738b \u5609\u80e4\u662f\u4e2a\u843d\u7b2c\u79c0\u624d\uff0c\u4e8c\u5341\u9980\u5e74\u4e4b\u524d\uff0c\u5728\u5317\u4eac\u4e0e\u540d\u6b66\u5e08\u5b5f\u707f\u7ed3\u4e3a\u516b\u62dc\u4e4b\u4ea4\uff0c\u6307\u8179\u4e3a\u5a92\uff0c\u7ed3\u6210\u4eb2 \u5bb6\u3002\u738b\u7167\u5e0c\u4e03\u5c81\u65f6\uff0c\u968f\u7236\u56de  \uff0c\u6b64\u5f8c\u4e24\u5bb6\u5c31\u6ca1\u89c1\u8fc7\u3002\u4e94\u516d\u5e74\u524d\u5b5f\u707f\u88ab\u671d\u5ef7\u8058\u4e3a\u201c\u6148\u5e86\u5bab\u201d \u201c\u592a\u5b50\u6240\u4f4f\u7684\u5bab\u6bbf\u201d\u7684\u503c\u6bbf\u6b66\u5e08\uff0c\u800c\u738b\u5609\u80e4\u4e5f\u5728  \u5317\uff0c\u6210\u4e86\u7eff\u6797\u9996\u9886\u3002\u738b\u5609\u80e4\u77e5\u9053\u4e86\u4eb2\u5bb6\u7684 \u6d88\u606f\uff0c\u751a\u4e3a\u60cb\u60dc\uff0c\u5b5f\u707f\u4e00\u5411\u8c6a\u4fa0\u4ed7\u4e49\uff0c\u540d\u91cd\u6c5f\u6e56\uff0c\u4e0d\u77e5\u4f55\u6545\uff0c\u5374\u4f1a\u63a5\u53d7\u4e86\u738b\u5ba4\u7684\u8058\u8bf7\u3002\u81ea\u5b5f \u707f\u505a\u4e86\u503c\u6bbf\u6b66\u5e08\u5f8c\uff0c\u6bcf\u5e74\u603b\u6709\u4e00\u4e24\u6b21\u6258\u6c5f\u6e56\u4eba\u7269\u634e\u4fe1\u7ed9\u4ed6\uff0c\u8fd9\u6b21\u5219\u662f\u6258\u6b66\u5f53\u6d3e\u7684\u4e00\u4e2a\u5f1f\u5b50\u3002 \u738b\u7167\u5e0c\u65e9\u5341\u591a\u5929\u5df2\u77e5\u5cb3\u7236\u6258\u6709\u6b66\u5f53\u6d3e\u7684\u4eba\u5e26\u4fe1\u7ed9\u4ed6\uff0c\u521d\u65f6\u8fd8\u4ee5\u4e3a\u5e26\u4fe1\u7684\u4eba\u662f\u803f\u7ecd\u5357\uff0c\u6240\u4ee5\u6545 \u610f\u8ddf\u4ed6\u7ed3\u7eb3\u3002\u90a3\u77e5\u5374\u662f\u803f\u7ecd\u5357\u7684\u5e08\u5144\u3002 \u3000\u3000\u4e14\u8bf4\u738b\u7167\u5e0c\u8bfb\u4fe1\u4e4b\u5f8c\uff0c\u4e0e\u7389\u7f57\u5239\u544a\u8f9e\uff0c\u5306\u5306\u8d74\u4eac\uff0c\u5728\u8def\u4e0a\u8d70\u4e86\u6570\u6708\uff0c\u5230\u4e86\u4eac\u5e08\uff0c\u5df2\u662f\u521d \u6625\u3002\u90a3\u65e5\u5927\u96ea\u4e0b\u5f97\u6b63\u7d27\uff0c\u738b\u7167\u5e0c\u81ea\u5ba3\u6b66\u95e8\u5165\u57ce\uff0c\u5ffd\u89c1\u4eba\u5934\u7c07\u62e5\uff0c\u8fdc\u5904\u6709\u4eba\u545c\u9523\u547c\u559d\uff0c\u738b\u7167\u5e0c \u597d\u5947\u4e00\u95ee\uff0c\u65c1\u8fb9\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u4f60\u4e0d\u77e5\u9ebd\uff1f\u8fd1\u65e5\u4eac\u57ce\uff0c\u95f9\u51fa\u4e00\u4ef6\u6781\u5927\u7684\u6848\u4ef6\uff0c\u8bb8\u591a\u5b98\u5458 \u90fd\u88ab\u7275\u8fde\u4eba\u5185\uff0c\u4eca\u5929\u8fde\u6237\u90e8\u4f8d\u90ce\u5353\u7ee7\u8d24\u4e5f\u88ab\u63a8\u51fa\u5348\u95e8\u65a9\u9996\u4e86\u3002\u4eba\u8bf4\uff1a\u201c\u4f34\u541b\u5982\u4f34\u864e\u201d\u679c\u7136\u4e0d \u9519\u3002\u5353\u4f8d\u90ce\u542c\u8bf4\u8fd8\u662f\u4e00\u4e2a\u597d\u5b98\u5462\uff01\u201d\u738b\u7167\u5e0c\u542c\u8bf4\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u5353\u4f8d\u90ce\u6b63\u662f\u5353\u4ef2\u5ec9\u7684\u513f\u5b50\uff0c \u803f\u7ecd\u5357\u66ff\u5353\u4ef2\u5ec9\u4fdd\u9556\u4e5f\u662f\u5353\u4f8d\u90ce\u8bf7\u4ed6\u6765\u7684\u3002\u600e\u7684\u597d\u7aef\u7aef\u5374\u88ab\u63a8\u51fa\u5348\u95e8\u65a9\u9996\uff01 \u3000\u3000\u738b\u7167\u5e0c\u4eba\u6781\u7cbe\u7075\uff0c\u5c31\u8fd1\u8d70\u4e0a\u4e00\u5bb6\u9152\u697c\uff0c\u542c\u4eba\u8c08\u8bba\uff0c\u4e0d\u6d88\u591a\u65f6\uff0c\u5df2\u77e5\u9053\u6848\u60c5\u539f\u59d4\u3002\u539f\u6765\u660e \u795e\u5b97\u201c\u5373\u4e07\u5386\u5e1d\u201d\u6731\u7fca\u94a7\u751f\u6709\u4e24\u4e2a\u513f\u5b50\uff0c\u957f\u5b50\u5e38\u6d1b\u662f\u7687\u540e\u6240\u751f\uff0c\u6b21\u5b50\u5e38\u6d35\u662f\u5ba0\u5983\u90d1\u8d35\u5983\u6240 \u751f\u3002\u90d1\u8d35\u5983\u9634\u8c0b\u593a\u5ae1\uff0c\u795e\u5b97\u8fdf\u8fdf\u4e0d\u7acb\u592a\u5b50\u3002\u5f8c\u6765\u671d\u81e3\u8bf7\u7acb\u5e38\u6d1b\u4e3a\u7687\u592a\u5b50\uff0c\u5c01\u5e38\u6d35\u4e3a\u798f\u738b\uff0c\u5c01 \u5730\u5728\u6d1b\u9633\uff0c\u5e38\u6d35\u4e0d\u80af\u51fa\u4eac\u53d7\u85e9\uff0c\u671d\u81e3\u53c8\u4e0a\u594f\u50ac\u4ed6\u51fa\u4eac\u3002\u5e38\u6d35\u51fa\u4eac\u5f8c\u53ea\u4e00\u5e74\u201c\u660e\u4e07\u5386\u56db\u5341\u53c1 \u5e74\u201d\uff0c\u5ffd\u7136\u6709\u4eba\u6267\u67a3\u6728\u68cd\u6253\u4f24\u6148\u5e86\u5bab\u7684\u5b88\u536b\uff0c\u76f4\u5165\u524d\u6bbf\uff0c\u59cb\u88ab\u6355\u83b7\u3002\u8fd9\u6848\u4ef6\u5c31\u662f\u5386\u53f2\u4e0a\u6709\u540d \u7684\uff0c\u660e\u671d\u53c1\u5927\u602a\u6848\u4e4b\u4e00\uff0c-\u201c\u6883\u51fb\u6848\u201d\uff0c\u4e00\u65f6\u95f9\u5f97\u6ee1\u57ce\u98ce\u96e8\uff0c\u9707\u52a8\u4eac\u534e\uff01 \u3000\u3000\u592a\u5b50\u867d\u7136\u6ca1\u6709\u53d7\u4f24\uff0c\u4f46\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\uff0c\u5c45\u7136\u6709\u4eba\u6562\u95ef\u8fdb\u5bab\u6bbf\uff0c\u6253\u4f24\u536b\u58eb\uff0c\u8fd9\u771f\u662f\u4ece\u6240\u672a \u6709\u4e4b\u4e8b\u3002\u5c24\u5176\u5947\u602a\u7684\u662f\uff0c\u90a3\u6267\u68cd\u95ef\u5bab\u7684\u4eba\uff0c\u81ea\u79f0\u90d1\u5927\u6df7\u5b50\uff0c\u8bf4\u8bdd\u4e3e\u6b62\uff0c\u75af\u75af\u766b\u766b\uff0c\u592a\u533b\u4f1a \u8bca\uff0c\u4e5f\u4e0d\u6562\u65ad\u5b9a\u4ed6\u6709\u75c5\u65e0\u75c5\u3002\u53c1\u53f8\u4f1a\u5ba1\uff0c\u8981\u4ed6\u4f9b\u51fa\u4e3b\u8c0b\uff0c\u4ed6\u80e1\u8bf4\u516b\u9053\uff0c\u62a5\u4e86\u4e00\u5927\u4e32\u5927\u81e3\u548c\u5bab \u4e2d\u592a\u76d1\u7684\u540d\u5b57\uff0c\u4e5f\u4e0d\u77e5\u90a3\u4e2a\u662f\u771f\uff0c\u90a3\u4e2a\u662f\u5047\uff0c\u7ed3\u679c\u671d\u81e3\u9609  \uff0c\u7687\u4eb2\u56fd\u5a01\uff0c\u7eb7\u7ed3\u515a\u7fbd\uff0c\u76f8\u4e92\u653b \u8ba6\uff0c\u795e\u5b97\u7687\u5e1d\uff0c\u53c8\u662f\u4e2a\u660f\u5eb8\u7684\u4eba\uff0c\u6beb\u65e0\u4e3b\u610f\uff0c\u4eca\u65e5\u542c\u8fd9\u4e2a\u671d\u81e3\u7684\u8bdd\uff0c\u660e\u65e5\u53c8\u542c\u90a3\u4e2a\u9609  \u7684 \u8bdd\uff0c\u5f04\u5f97\u7275\u8fde\u65e5\u5e7f\uff0c\u671d\u4e2d\u4eba\u4eba\u81ea\u5371\u3002\u8fde\u5353\u7ee7\u8d24\u90a3\u6837\u4e00\u4e2a\u4e0d\u597d\u7ba1\u9606\u4e8b\u7684\u5b98\u513f\uff0c\u4e5f\u88ab\u7275\u8fde\u5165\u5185\uff0c \u7adf\u7136\u4e0d\u52a0\u5ba1\u8baf\uff0c\u5c31\u628a\u4ed6\u63a8\u51fa\u5348\u95e8\u65a9\u9996\u53bb\u4e86\u3002 \u3000\u3000\u738b\u7167\u5e0c\u660e\u767d\u4e86\u6848\u60c5\u539f\u59d4\u4e4b\u5f8c\uff0c\u6697\u6697\u53f9\u606f\uff0c\u5fc3\u60f3\u6ee1\u6d32\u5d1b\u8d77\u4e1c\u5317\uff0c\u502d\u5bc7\u4e3a\u60a3\u4e1c\u5357\uff0c\u800c\u7687\u5e1d\u660f \u5eb8\uff0c\u671d\u4e2d\u53c8\u662f\u515a\u4e89\u672a\u5df2\uff0c\u8fd9\u5927\u660e\u6c5f\u5c71\uff0c\u6050\u6015\u4e5f\u4e0d\u4f1a\u957f\u4e45\u4e86\u3002\u8f6c\u800c\u53c8\u60f3\uff1a\u8fd9\u6837\u4e5f\u597d\uff0c\u6731\u5bb6\u65e0 \u80fd\uff0c\u5c31\u8ba9\u6211\u738b\u5bb6\u6765\u7ba1\u4e00\u7ba1\u3002\u6298\u4e0b\u9152\u697c\uff0c\u6839\u636e\u7236\u4eb2\u6240\u7ed9\u7684\u4eac\u57ce\u5730\u56fe\uff0c\u4e00\u76f4\u5bfb\u81f3\u62a5\u5b50\u80e1\u540c\uff0c\u5b5f\u5bb6 \u95e8\u5df7\u4f9d\u7a00\u8bb0\u5f97\uff0c\u4e0d\u6599\u8d70\u8fdb\u5df7\u5185\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u731b\u5403\u4e00\u60ca\uff0c\u5b5f\u5bb6\u6731\u95e8\u6df1\u9501\uff0c\u95e8\u5916\u4ea4\u53c9\u8d34\u4e86\u4e24\u9053\u5c01 \u6761\uff0c\u7adf\u7136\u662f\u9526\u8863\u536b\u5c01\u7684\uff0c\u95e8\u5916\u8fd8\u7ad9\u6709\u4e24\u540d\u9b41\u68a7\u6c49\u5b50\uff0c\u663e\u7136\u662f\u5bab\u4e2d\u536b\u58eb\u3002\u738b\u7167\u5e0c\u90a3\u6562\u505c\u7559\uff0c\u614c \u5fd9\u6e9c\u51fa\u80e1\u540c\u3002\u5fc3\u4e2d\u60ca\u7591\u4e0d\u5b9a\uff0c\u4e00\u8def\u8e31\u5230\u5929\u6865\u9644\u8fd1\uff0c\u518d\u5bfb\u8bbf\u4e00\u4f4d\u7236\u6267\uff0c\u4e5f\u662f\u4eac\u4e2d\u9887\u6709\u540d\u6c14\u7684\u6b66 \u5e08\u67f3\u897f\u94ed\uff0c\u5e78\u597d\u4e00\u627e\u4fbf\u7740\uff0c\u67f3\u897f\u94ed\u89c1\u662f\u4ed6\u6765\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u6025\u5fd9\u9501\u597d\u95e8\u6237\uff0c\u62c9\u4ed6\u8fdb\u5165\u5185\u5ba4\uff0c\u4f4e \u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u6837\u8fd9\u822c\u5927\u80c6\uff1f\u4f60\u7236\u4eb2\u662f\u671d\u5ef7\u94a6\u72af\uff0c\u4f60\u5cb3\u7236\u53c8\u88ab\u6355\u53bb\uff0c\u751f\u6b7b\u672a\u77e5\u3002\u82e5\u6709\u4eba\u77e5\u4f60 \u8eab\u5206\uff0c\u5982\u4f55\u662f\u597d\uff1f\u201d\u738b\u7167\u5e0c\u7b11\u4e86\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4eac\u4e2d\u6b63\u6ce8\u610f\u7740\u8fd9\u4ef6\u602a\u6848\uff0c\u9526\u8863\u536b\u672a\u5fc5\u4f1a\u5206\u5fc3 \u6765\u6599\u7406\u6211\u3002\u6211\u6b63\u60f3\u8bf7\u95ee\u53d4\u7236\uff0c\u655d\u5cb3\u662f\u592a\u5b50\u5b98\u4e2d\u7684\u503c\u6bbf\u6b66\u5e08\uff0c\u600e\u7684\u4e5f\u4f1a\u88ab\u6355\uff1f\u96be\u9053\u4ed6\u4e5f\u88ab\u7275\u8fde \u8fdb\u6883\u51fb\u6848\u4e86\u5417\uff01\u201d\u67f3\u897f\u94ed\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6211\u4e5f\u83ab\u540d\u5176\u5999\u5462\uff0c\u90a3\u90d1\u5927\u6df7\u5b50\uff0c\u8fd8\u662f\u4f60\u5cb3\u7236\u64d2\u7740 \u7684\uff0c\u5c31\u662f\u6ca1\u529f\u4e5f\u8be5\u65e0\u7f6a\uff0c\u5374\u98a0\u5012\u8d77\u6765\uff0c\u628a\u4ed6\u4e5f\u6355\u4e86\u53bb\u3002\u201d\u738b\u7167\u5e0c\u6697\u6697\u76d8\u7b97\uff0c\u5f53\u4e0b\u5374\u4e0d\u4f5c\u58f0\u3002 \u3000\u3000\u8fc7\u4e86\u4e24\u5929\uff0c\u5b5f\u5bb6\u95e8\u53e3\u7684\u8b66\u536b\u5df2\u7ecf\u64a4\u4e86\uff0c\u4e00\u665a\u738b\u7167\u5e0c\u98df\u8fc7\u665a\u996d\uff0c\u7a81\u7136\u6362\u4e86\u4e00\u8eab\u9ed1\u8272\u7684\u591c\u884c \u8863\uff0c\u5bf9\u67f3\u897f\u94ed\u9053\uff1a\u201c\u53d4\u7236\uff0c\u6211\u4eca\u665a\u60f3\u5230\u655d\u5cb3\u5bb6\u4e2d\uff0c\u63a2\u4ed6\u4e00\u63a2\u3002\u201d\u67f3\u897f\u94ed\u9053\uff1a\u201c\u8fd9\u5982\u4f55\u4f7f \u5f97\uff01\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u6211\u7edd\u4e0d\u8fde\u7d2f\u53d4\u7236\u5c31\u662f\u3002\u201d\u67f3\u897f\u94ed\u6447\u4e86\u6447\u5934\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u529d\u4ed6\u4e0d\u542c\uff0c\u4e5f\u53ea \u5f97\u7531\u4ed6\u53bb\u4e86\u3002 \u3000\u3000\u5317\u4eac\u7684\u6c11\u5c45\u4e00\u822c\u90fd\u5f88\u77ee\uff0c\u5c31\u662f\u5927\u5bb6\u81e3\u5ba4\uff0c\u4e5f\u53ea\u662f\u9662\u843d\u5e7f\u9614\uff0c\u5f88\u5c11\u6709\u53c1\u5c42\u697c\u5b87\u7684\u3002\u201c\u56e0\u4e3a \u5386\u4ee3\u7687\u5e1d\u9650\u5b9a\u6c11\u5c45\u4e0d\u80fd\u9ad8\u8fc7\u4e94\u51e4\u697c\u7684\u89d2\u697c\uff0c\u4ee5\u4fbf\u5728\u5bab\u4e2d\u53ef\u4ee5\u4fef\u77b0\u5168\u57ce\uff0c\u800c\u6c11\u5c45\u5219\u4e0d\u80fd\u7aa5\u63a2\u5bab \u5185\u3002\u201d\u738b\u7167\u5e0c\u8f7b\u529f\u751a\u597d\uff0c\u8f7b\u8f7b\u4e00\u8dc3\uff0c\u5df2\u4e0a\u4e86\u5c4b\u9876\uff0c\u4ece\u56ca\u4e2d\u53d6\u51fa\u4e24\u679a\u94dc\u94b1\uff0c\u7b9d\u5728\u4e2d\u98df\u4e8c\u6307\u4e4b \u95f4\uff0c\u5148\u628a\u7b2c\u4e00\u679a\u94dc\u94b1\u5411\u4e0a\u4e00\u629b\uff0c\u4e8c\u6307\u4e00\u7529\uff0c\u518d\u628a\u7b2c\u4e8c\u679a\u94dc\u94b1\u7167\u51c6\u7b2c\u4e00\u679a\u6253\u53bb\u3002\u4e24\u679a\u94dc\u94b1\u5728\u7a7a \u4e2d\u76f8\u649e\uff0c\u53d1\u51fa\u94ee\u7136\u58f0\u54cd\uff01 \u3000\u3000\u8fd9\u4e00\u62db\u6709\u4e2a\u540d\u5802\uff0c\u53eb\u505a\u201c\u9752\u86a8\u4f20\u4fe1\u201d\uff0c\u662f\u591c\u884c\u4eba\u8054\u7edc\u7684\u6697\u53f7\uff0c\u4e24\u679a\u94dc\u94b1\u5728\u7a7a\u4e2d\u4e00\u78b0\uff0c\u6eda \u843d\u9662\u4e2d\u3002\u738b\u7167\u5e0c\u8737\u4f0f\u5728\u5c4b\u6a90\u4e0a\u52a8\u4e5f\u4e0d\u52a8\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u679c\u7136\u6709\u4e24\u4e2a\u9ed1\u8863\u536b\u58eb\u8d70\u4e86\u51fa\u6765\uff0c\u671b\u4e86\u4e00 \u671b\uff0c\u54fa\u54fa\u81ea\u8bed\u9053\uff1a\u201c\u4ec0\u9ebd\u58f0\u54cd\uff0c\u8fde\u9b3c\u5f71\u4e5f\u4e0d\u89c1\u4e00\u4e2a\u3002\u201d\u53e6\u4e00\u4e2a\u4eba\u9053\uff1a\u201c\u4eac\u5e08\u91cd\u5730\uff0c\u90a3\u6709\u4eba\u8fd9 \u6837\u5927\u80c6\u3002\u674e\u6307\u6325\u4e5f\u592a\u5c0f\u5fc3\u4e86\u3002\u201d\u4e24\u4eba\u5446\u5934\u5446\u8111\u7684\u770b\u4e86\u4e00\u4f1a\uff0c\u53c8\u8fdb\u53bb\u4e86\u3002\u738b\u7167\u5e0c\u6697\u6263\u94b1\u9556\uff0c\u672c \u5f85\u4e8c\u4eba\u4e0a\u5c4b\uff0c\u5c31\u8981\u731b\u4e0b\u6740\u624b\u3002\u5fc3\u91cc\u7b11\u9053\uff1a\u201c\u771f\u662f\u7b28\u866b\uff0c\u6c5f\u6e56\u8def\u9053\u4e00\u70b9\u4e5f\u4e0d\u61c2\u3002\u201d\u8eab\u5f62\u4e00\u6643\uff0c \u75be\u7684\u98de\u8fc7\u4e00\u7247\u74e6\u9762\uff0c\u8d76\u5728\u4e24\u4e2a\u536b\u58eb\u7684\u524d\u5934\uff0c\u8fdb\u4e86\u5ead\u9662\uff0c\u518d\u7eb5\u8eab\u4e00\u8dc3\uff0c\u8df3\u4e0a\u4e66\u697c\uff0c\u8fd9\u662f\u4ed6\u5cb3\u7236 \u5e73\u65e5\u4f11\u61a9\u4e4b\u6240\uff0c\u738b\u7167\u5e0c\u89c1\u697c\u95e8\u534a\u63a9\uff0c\u5185\u91cc\u65e0\u4eba\uff0c\u8e51\u8db3\u5165\u5185\u3002\u4e0d\u6599\u524d\u811a\u521a\u521a\u8e0f\u5165\uff0c\u90a3\u6247\u95e8\u677f\u7a81 \u7136\u5012\u4e86\u4e0b\u6765\uff0c\u4e00\u53e3\u660e\u6643\u6643\u7684\u5229\u5203\uff0c\u4ece\u95e8\u5f8c\u4f38\u51fa\uff0c\u51b7\u6c14\u68ee\u68ee\uff0c\u5df2\u4ece\u4fa7\u9762\u523a\u5230\u3002\u597d\u4e2a\u738b\u7167\u5e0c\uff0c\u4e34 \u5371\u4e0d\u4e71\uff0c\u4f0f\u5730\u4e00\u6eda\uff0c\u5de6\u624b\u5c06\u95e8\u677f\u4e00\u62ac\uff0c\u90a3\u53e3\u5229\u5203\u63d2\u5728\u677f\u4e0a\uff0c\u738b\u7167\u5e0c\u4e00\u4e2a\u201c\u9ca4\u9c7c\u6253\u633a\u201d\u7ffb\u8d77\u8eab \u6765\uff0c\u957f\u5251\u62d4\u5728\u624b\u4e2d\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u563f\u563f\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u8d3c\u662f\u81ea\u6ca1\u7f57\u7f51\uff01\u201d\u738b\u7167\u5e0c\u957f\u5251\u4e00\u6643\uff0c\u6b63 \u5f85\u8fdb\u62db\uff0c\u84e6\u7136\u95f4\u4e66\u623f\u4e24\u9762\u4fa7\u95e8\u5927\u5f00\uff0c\u6697\u5668\u5636\u98ce\uff0c\u7eb7\u7eb7\u6253\u8fdb\uff0c\u738b\u7167\u5e0c\u8eab\u5b50\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u957f\u5251\u5212 \u51fa\u4e00\u5708\u94f6\u8679\uff0c\u5728\u6ee1\u5ba4\u6697\u5668\u98de\u821e\u6fc0\u649e\u4e4b\u4e2d\uff0c\u6325\u5251\u76f4\u53d6\u90a3\u4f0f\u5728\u95e8\u5f8c\u7684\u536b\u58eb\u3002 \u3000\u3000\u539f\u6765\u4eca\u665a\u8f6e\u503c\u7684\u53c1\u4e2a\u9526\u8863\u536b\uff0c\u90fd\u662f\u8001\u65bc\u6c5f\u6e56\u7684\u9ad8\u624b\uff0c\u4ed6\u4eec\u63a5\u7684\u547d\u4ee4\uff0c\u662f\u8981\u5c06\u6240\u6709\u6765\u63a2\u7684 \u4eba\u751f\u64d2\uff0c\u6240\u4ee5\u6545\u610f\u88c5\u51fa\u7956\u5fc3\u5927\u610f\u7684\u6837\u5b50\uff0c\u5f15\u4ed6\u8fdb\u6765\uff0c\u7136\u5f8c\u53c1\u9762\u4f0f\u51fb\u3002\u5e78\u597d\u738b\u7167\u5e0c\u6b66\u827a\u9ad8\u5f3a\uff0c \u8981\u4e0d\u7136\u51e0\u4e4e\u53d7\u4e86\u6697\u7b97\u3002 \u3000\u3000\u90a3\u4f0f\u5728\u95e8\u5f8c\u7684\u536b\u58eb\uff0c\u4f3c\u4e4e\u662f\u4e2a\u5934\u76ee\uff0c\u4e00\u53e3\u5200\u6a2a\u626b\u76f4\u5288\uff0c\u547c\u547c\u751f\u98ce\uff0c\u5c45\u7136\u662f\u201c\u4e94\u864e\u65ad\u95e8 \u5200\u201d\u7684\u4e0a\u4e58\u5200\u6cd5\uff0c\u53e6\u5916\u4e24\u540d\u536b\u58eb\uff0c\u4e00\u4e2a\u4f7f\u719f\u94dc\u68cd\uff0c\u4e00\u4e2a\u4f7f\u4e03\u8282\u97ad\uff0c\u4e5f\u90fd\u662f\u62db\u6c89\u529b\u731b\uff0c\u738b\u7167\u5e0c \u6325\u5251\u529b\u6218\uff0c\u5de6\u6c64\u53f3\u6325\uff0c\u8fde\u626b\u5e26\u624e\uff0c\u6253\u4e86\u7247\u523b\uff0c\u90a3\u4f7f\u719f\u94dc\u68cd\u7684\u536b\u58eb\u4e2d\u4e86\u4e00\u5251\uff0c\u8df3\u51fa\u5708\u5916\uff0c\u738b\u7167 \u5e0c\u631f\u5bd2\u98ce\uff0c\u4f0f\u8eab\u4e00\u8dc3\uff0c\u4e58\u7740\u4e00\u62db\u5f97\u624b\uff0c\u6025\u4e0b\u6740\u624b\uff0c\u60f3\u5148\u6bd9\u6389\u4e00\u4eba\u518d\u7b97\uff0c\u4e0d\u6599\u4f7f\u65ad\u95e8\u5200\u7684\u90a3\u4e2a \u5bb6\u4f19\uff0c\u62db\u6570\u7740\u5b9e\u6ed1\u6e9c\uff0c\u4e58\u7740\u738b\u7167\u5e0c\u4f0f\u8eab\u8fdb\u5251\uff0c\u84e6\u5730\u6a2a\u5200\u626b\u53bb\uff0c\u4e00\u62db\u201c\u51e4\u51f0\u5c55\u7fc5\u201d\uff0c\u8ff3\u65a9\u5bf9\u624b \u4e0a\u76d8\uff0c\u738b\u7167\u5e0c\u8feb\u5f97\u653e\u677e\u90a3\u540d\u4f7f\u719f\u94dc\u68cd\u7684\u536b\u58eb\uff0c\u62e7\u8eab\u7ffb\u5251\uff0c\u628a\u6765\u88ad\u7684\u65ad\u95e8\u5200\u683c\u51fa\u5916\u95e8\uff0c\u7f13\u5f97\u4e00 \u7f13\uff0c\u90a3\u4f7f\u4e03\u8282\u97ad\u7684\u536b\u58eb\u5df2\u6251\u4e86\u4e0a\u6765\uff0c\u4f7f\u719f\u94dc\u68cd\u7684\u4e5f\u8d1f\u4f24\u518d\u6218\u3002 \u3000\u3000\u738b\u7167\u5e0c\u4ee5\u4e00\u654c\u53c1\uff0c  \u7136\u4e0d\u60e7\uff01\u957f\u5251\u5bd2\u5149\u95ea\u95ea\uff0c\u5251\u52bf\u5982\u8679\u3002\u987b\u77e5\u4ed6\u7684\u7236\u4eb2\u738b\u5609\u80e4\u4e43\u662f\u5251\u6cd5 \u540d\u5bb6\uff0c\u5f97\u8fc7\u77f3\u5bb6\u8e51\u4e91\u5251\u7684\u771f\u4f20\uff0c\u738b\u7167\u5e0c\u6587\u6b66\u517c\u5b66\uff0c\u5185\u5916\u53cc\u4fee\uff0c\u6781\u4e3a\u4e86\u5f97\u3002\u518d\u6218\u4e86\u7247\u523b\uff0c\u4f7f\u4e03 \u8282\u97ad\u7684\u4e5f\u4e2d\u4e86\u4e00\u5251\uff0c\u75db\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u738b\u7167\u5e0c\u8fd0\u5251\u5982\u98ce\uff0c\u8282\u8282\u8fdb\u8feb\uff0c\u4f7f\u719f\u94dc\u68cd\u7684\u90a3\u4e2a\uff0c\u9000\u81f3\u5899 \u8fb9\uff0c\u72b9\u81ea\u4e0d\u5982\uff0c\u738b\u7167\u5e0c\u4e00\u5251\u523a\u53bb\uff0c\u4ed6\u5411\u5f8c\u4e00\u9000\uff0c\u78b0\u5f97\u90a3\u5835\u5899\u4e5f\u52a8\u4e86\u8d77\u6765\uff0c\u738b\u7167\u5e0c\u5251\u62db\u5982\u7535\uff0c \u4e00\u5251\u628a\u4ed6\u9489\u5728\u5899\u4e0a\uff0c\u5ffd\u542c\u5f97\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u5899\u4e0a\u7adf\u7136\u88c2\u4e86\u4e00\u4e2a\u5927\u6d1e\uff01\u90a3\u540d\u536b\u58eb\u7684  \u8eab\u8dcc\u5165\u6d1e \u5185\uff0c\u738b\u7167\u5e0c\u91cd\u5fc3\u9aa4\u5931\uff0c\u6643\u4e86\u4e00\u6643\uff0c\u51e0\u4e4e\u7ed9\u4e03\u8282\u97ad\u626b\u7740\uff0c\u6025\u5fd9\u62bd\u5251\u56de\u8eab\uff0c\u5c31\u5728\u6b64\u9645\uff0c\u731b\u542c\u5f97\u5899 \u5185\u4e00\u58f0\u602a\u53eb\uff0c\u7a9c\u51fa\u4e86\u4e00\u4e2a\u4eba\u6765\u3002\u738b\u7167\u5e0c\u695e\u4e86\u4e00\u695e\uff0c\u4e0d\u77e5\u662f\u53cb\u662f\u654c\u4e86\u5c1a\u672a\u770b\u6e05\uff0c\u773c\u775b\u53c8\u662f\u4e00 \u4eae\uff0c\u5899\u5185\u53c8\u8dc3\u51fa\u4e86\u4e00\u4e2a\u5c11\u5e74\u5973\u5b50\uff0c\u767d\u8863\u98d8\u98d8\uff0c\u7eb5\u8eab\u4e00\u8dc3\uff0c\u5728\u4f17\u4eba\u60ca\u6115\u4e4b\u4e2d\uff0c\u62a2\u5230\u4e86\u95e8\u53e3\uff0c\u6a2a \u5251\u4e00\u5c01\uff0c\u6025\u58f0\u53eb\u9053\uff1a\u201c\u654f\u54e5\uff0c\u653b\u90a3\u540d\u4f7f\u5200\u7684\u536b\u58eb\u3002\u201d \u3000\u3000\u5148\u8df3\u51fa\u6765\u7684\u662f\u4e2a\u5c11\u5e74\uff0c\u50bb\u864e\u864e\u7684\u62a1\u5200\u6025\u6251\uff0c\u4e24\u5200\u76f8\u683c\uff0c\u53cc\u65b9\u90fd\u611f\u624b\u8155  \u3002\u738b\u7167\u5e0c\u5b9a\u4e86 \u5b9a\u795e\uff0c\u51dd\u7738\u770b\u90a3\u5c11\u5973\uff0c\u5fc3\u60f3\uff1a\u83ab\u975e\u662f\u6211\u7684\u672a\u5a5a\u59bb\u5b50\u3002\u518d\u7ec6\u770b\u65f6\uff0c\u8f6e\u5ed3\u4f9d\u7a00\u8bb0\u5f97\uff0c\u5fc3\u91cc\u84e6\u7136\u4e00 \u9178\uff0c\u8bf4\u4e0d\u51fa\u662f\u4ec0\u9ebd\u5473\u513f\uff01\u5446\u5446\u7684\u770b\u90a3\u4e24\u4eba\u76f8\u6597\u3002\u53e6\u4e00\u540d\u536b\u58eb\uff0c\u89c1\u60c5\u4e0d\u5999\uff0c\u614c\u5fd9\u593a\u8def\u98de\u9003\uff0c\u501a \u5728\u95e8\u53e3\u7684\u5c11\u5973\u5a07\u53f1\u4e00\u58f0\uff0c\u4e00\u6296\u624b\uff0c\u53c1\u67c4\u98de\u5200\u8fde\u7fe9\u98de\u51fa\uff0c\u4e0a\u4e2d\u4e0b\u53c1\u8def\u4e00\u9f50\u6253\u5230\uff0c\u90a3\u540d\u536b\u58eb\u60e8\u53eb \u4e00\u58f0\uff0c\u8eab\u4e0a\u987f\u65f6\u6dfb\u4e86\u53c1\u4e2a\u7a9f\u7abf\u3002\u90a3\u767d\u8863\u5c11\u5973\u4e00\u8fb9\u653e\u6697\u5668\uff0c\u4e00\u8fb9\u5a07\u55d4\u53d1\u8bdd\u9053\uff1a\u201c\u5582\uff0c\u5c11\u5e74\u4eba\uff0c \u4f60\u4e3a\u4ec0\u9ebd\u5c3d\u77a7\u7740\u6211\u4e0d\u52a8\u624b\u5440\uff01\u201d\u738b\u7167\u5e0c\u9762\u8272\u4e00\u53d8\uff0c\u770b\u90a3\u4e2a\u5c11\u5e74\u548c\u654c\u624b\u76f8\u6301\u4e0d\u4e0b\uff0c\u4e00\u8dc3\u4e0a\u524d\uff0c \u5de6\u8098\u671d\u4ed6\u4e00\u649e\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u9000\u4e0b\uff01\u201d\u90a3\u5c11\u5e74\u6115\u9053\uff1a\u201c\u5e72\u5417\uff1f\u201d\u738b\u7167\u5e0c\u4e00\u8154\u6012\u6c14\uff0c\u65e0\u5904\u53d1  \uff0c \u957f\u5251\u4e00\u62a1\uff0c\u7528\u8db3\u4e86\u5341\u6210\u529b\u91cf\uff0c\u90a3\u540d\u4f7f\u5200\u7684\u536b\u58eb\u867d\u975e\u5eb8\u624b\uff0c\u5374\u90a3\u91cc\u654c\u5f97\u4f4f\u4ed6\u7684\u5185\u5bb6\u529f\u529b\uff0c\u53ea\u542c \u5f97\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u201c\u65ad\u95e8\u5200\u201d\u771f\u4e2a\u65ad\u4e86\uff0c\u738b\u7167\u5e0c\u5251\u950b\u4e00\u8f6c\uff0c\u628a\u4ed6\u65a9\u4e3a\u4e24\u622a\u3002\u6536\u5251\u8981\u8d70\uff0c\u5374\u542c \u5f97\u90a3\u5c11\u5973\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u4f60\u7684\u5251\u6cd5\u771f\u4e0d\u9519\u5440\uff01\u5df4\u662f\u9c81\u83bd\u4e00\u70b9\u3002\u201d\u738b\u7167\u5e0c\u5fc3\u5934\u4e00\u9707\uff0c\u6697\u7b11\u81ea\u5df2\u4fee \u517b\u4e0d\u591f\uff0c\u4e00\u4e2a\u4ee5\u5929\u4e0b\u4e3a\u5df1\u4efb\u7684\u4eba\uff0c\u600e\u80fd\u4e3a\u513f\u5973\u4e4b\u60c5\u52a8\u4e86\u95f2\u6c14\uff1f\u8fd9\u201c\u9c81\u83bd\u201d\u4e8c\u5b57\u4e4b\u8bc4\uff0c\u5f04\u5f97\u4ed6 \u9762\u90fd\u7ea2\u4e86\u3002\u90a3\u5c11\u5973\u4e0a\u524d\u4e00\u63d6\uff0c\u8bf4\u9053\uff0e\u201c\u4e49\u58eb\u4e3a\u5bb6\u7236\u5192\u6b64\u5927\u9669\uff0c\u5c0a\u59d3\u5927\u540d\uff0c\u53ef\u80af\u8d50\u544a\u9ebd\uff01\u201d \u3000\u3000\u738b\u7167\u5e0c\u4e0e\u672a\u5a5a\u59bb\u5206\u522b\u5df2\u6709\u4e00\u5341\u516d\u5e74\uff0c\u5b5f\u707f\u50ac\u4ed6\u8fce\u4eb2\u7684\u4e8b\uff0c\u5973\u513f\u5e76\u672a\u77e5\u9053\uff0c\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230 \u672a\u5a5a\u592b\u4ece\u4e07\u91cc\u4e4b\u5916\u6765\u5230\u4eac\u5e08\u3002\u6240\u4ee5\u867d\u89c9\u8fd9\u4eba\u4f3c\u66fe\u76f8\u8bc6\uff0c\u5374\u4e0d\u6562\u76f8\u8ba4\u3002\u738b\u7167\u5e0c\u9053\uff1a\u201c\u5c0f\u59d3\u738b\u540d \u65e5\u53ec\uff0c\u5c0f\u59d0\u53ef\u662f\u5b5f\u6b66\u5e08\u7684\u638c\u4e0a\u660e\u73e0\u95fa\u540d\u53eb\u505a\u79cb\u971e\uff1f\u201d\u5b5f\u79cb\u971e\u8be7\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u77e5\u9053\u6211\u7684\u540d \u5b57\uff1f\u201d\u738b\u7167\u5e0c\u53c8\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u54e5\u53ef\u662f\u2026\u2026\u201d\u90a3\u5c11\u5e74\u50bb\u7b11\u7b54\u9053\uff1a\u201c\u5c0f\u5f1f\u53eb\u505a\u767d\u654f\uff0c\u662f\u5b5f\u6b66\u5e08 \u7684\u5f1f\u5b50\uff0c\u738b\u5144\uff0c\u4f60\u7684\u6b66\u529f\u771f\u597d\uff0c\u53ea\u4e00\u62db\u5c31\u628a\u8fd9\u9e70\u722a\u5b59\u5e9f\u4e86\uff0c\u4f60\u649e\u4e86\u6211\u4e00\u4e0b\uff0c\u6211\u4e00\u70b9\u4e5f\u4e0d\u6028 \u4f60\u3002\u201d\u738b\u7167\u5e0c\u5fc3\u60f3\uff1a\u8fd9\u50bb\u5c0f\u5b50\u540d\u53eb\u201c\u767d\u654f\u201d\uff0c\u5374\u4e00\u70b9\u4e5f\u4e0d\u673a\u654f\u3002 \u3000\u3000\u738b\u7167\u5e0c\u5fc3\u91cc\u9178\u6e9c\u6e9c\u7684\uff0c\u6545\u610f\u4e0d\u62a5\u771f\u540d\uff0c\u80e1\u4e71\u634f\u4e86\u4e00\u6bb5\u6765\u5386\uff0c\u8bf4\u662f\u81ea\u5df1\u66fe\u53d7\u8fc7\u5b5f\u707f\u7684\u5927 \u6069\uff0c\u6240\u4ee5\u62da\u820d\u6027\u547d\uff0c\u4e5f\u8981\u6765\u63a2\u4ed6\u4e00\u63a2\u3002\u5b5f\u707f\u4ea4\u6e38\u751a\u5e7f\uff0c\u5b5f\u79cb\u971e\u7adf\u81ea\u4fe1\u4e86\u3002\u518d\u6b21\u9053\u8c22\u3002\u738b\u7167\u5e0c \u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u8eb2\u5728\u8fd9\u590d\u58c1\u91cc\u591a\u5c11\u5929\u4e86\uff1f\u201d\u767d\u654f\u9053\uff1a\u201c\u4ece\u8001\u5e08\u88ab\u6355\u7684\u90a3\u5929\u7b97\u8d77\uff0c\u5df2\u6709\u53c1\u5929 \u4e86\u3002\u201d\u738b\u7167\u5e0c\u8d8a\u53d1\u4e0d\u8212\u670d\uff0c\u4e0d\u81ea\u89c9\u7684\u9762\u8272\u94c1\u9752\uff01 \u3000\u3000\u5b5f\u79cb\u971e\u79cb\u6c34\u76c8\u76c8\uff0c\u6ce8\u89c6\u7740\u738b\u7167\u5e0c\u7684\u9762\u8272\uff0c\u5173\u5fc3\u8bf4\u9053\uff1a\u201c\u738b\u5144\uff0c\u4f60\u7d2f\u4e86\uff1f\u6b47\u4e00\u6b47\u5427\uff01\u201d\u767d \u654f\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u6253\u5f97\u4e4f\u4e86\uff0c\u6211\u53bb\u5bfb\u4e00\u74f6\u597d\u9152\u6765\uff0c\u7ed9\u4f60\u63d0\u63d0\u795e\u3002\u201d\u738b\u7167\u5e0c\u53c8\u597d\u6c14\u53c8\u597d \u7b11\uff0c\u90a3\u50bb\u5c0f\u5b50\u5df2\u7ecf\u8dd1\u4e0b\u4e86\u697c\uff0c\u5230\u9152\u7a96\u91cc\u5bfb\u9648\u5e74\u8001\u9152\u53bb\u4e86\u3002 \u3000\u3000\u738b\u7167\u5e0c\u4e0e\u672a\u5a5a\u59bb\u5728\u4e66\u623f\u91cc\u60a0\u60a0\u76f8\u5bf9\uff0c\u6de1\u6de1\u7684\u6708\u5149\u4ece\u7a97\u5916  \u8fdb\u6765\uff0c\u738b\u7167\u5e0c\u4e00\u9635\u9635\u5fc3\u8df3\uff0c\u5b5f \u79cb\u971e\u71c3\u8d77\u4e86\u4e24\u679d\u7ea2\u70db\uff0c\u5728\u70db\u5149\u6620\u7167\u4e0b\uff0c\u5979\u8d8a\u53d1\u663e\u5f97\u8273\u4e3d\u3002\u738b\u7167\u5e0c\u9053\uff1a\u201c\u5b5f\u5c0f\u59d0\u8bf7\u6055\u5192\u6627\uff0c\u6211 \u60f3\u77e5\u9053\u4ee4\u5c0a\u5927\u4eba\u662f\u600e\u6837\u88ab\u6355\u7684\uff01\u4e0b\u843d\u5982\u4f55\uff1f\u597d\u8bbe\u6cd5\u76f8\u6551\u3002\u201d \u3000\u3000\u5b5f\u79cb\u971e\u773c\u5149\u95ea\u4e86\u4e00\u95ea\uff0c\u773c\u775b\u4e2d\u5145\u6ee1\u8c22\u610f\uff0c\u738b\u7167\u5e0c\u4f4e\u4e0b\u4e86\u5934\u4e0d\u6562\u8feb\u89c6\uff0c\u5b5f\u79cb\u971e\u5012\u662f\u843d\u843d\u5927 \u65b9\uff0c  \u887d\u8bf4\u9053\uff1a\u201c\u5c31\u5728\u6883\u51fb\u6848\u53d1\u751f\u5f8c\u7684\u7b2c\u4e8c\u5929\u665a\u4e0a\uff0c\u6211\u4eec\u5bb6\u4e2d\u7a81\u7136\u6765\u4e86\u4e24\u4e2a\u5947\u5f02\u7684\u5ba2\u4eba\uff0c\u4e5f \u662f\u5728\u8fd9\u4e66\u623f\u91cc\u548c\u5bb6\u7236\u8bf4\u8bdd\u3002\u6211\u548c\u767d\u654f\u8eb2\u5728\u91cc\u623f\uff0c\u53ea\u542c\u5f97\u4ed6\u4eec\u8bf4\u8bdd\u7684\u58f0\u97f3\u8d8a\u6765\u8d8a\u5c0f\uff0c\u5f8c\u6765\u5c31\u7b80 \u76f4\u542c\u4e0d\u89c1\u4e86\u3002\u6211\u53ea\u65ad\u65ad\u7eed\u7eed\u542c\u5f97\u90a3\u5ba2\u4eba\u8bf4\u4e9b\u4ec0\u9ebd\u51f6\u624b\uff0c\u53e3\u4f9b\u3001\u9634\u8c0b\u4e4b\u7c7b\u7684\u8bdd\uff0c\u53c8\u542c\u5f97\u5bb6\u7236\u63a5 \u8fde\u8bf4\u4e86\u51e0\u6b21\u201c\u6211\u4e0d\u77e5\u9053\u201d\uff0c\u5f8c\u6765\u5ba2\u4eba\u53bb\u4e86\uff0c\u7236\u4eb2\u5c31\u53eb\u6211\u4eec\u8d76\u5feb\u9003\u8d70\uff0c\u4f46\u4ed6\u5230\u5916\u9762\u671b\u4e86\u4e00\u671b\uff0c \u5ffd\u7136\u53c8\u8d70\u56de\u4e66\u623f\u628a\u6211\u4eec\u63a8\u8fdb\u5899\u5185\u7684\u6697\u5ba4\uff0c\u8fd8\u628a\u4e24\u5927\u5305\u98df\u7269\u63b7\u4e86\u8fdb\u6765\u3002\u6211\u4eec\u521a\u521a\u8eb2\u597d\uff0c\u9526\u8863\u536b \u5c31\u8fdb\u6765\u4e86\u3002\u6211\u4eec\u8f6e\u6d41\u7761\u89c9\uff0c\u542c\u5916\u9762\u536b\u58eb\u7684\u6362\u73ed\u8c08\u8bdd\uff0c\u624d\u77e5\u9053\u5df2\u8fc7\u4e86\u53c1\u5929\u3002\u6211\u4eec\u5728\u91cc\u9762\u95f7\u5f97\u4e0d \u8010\u70e6\uff0c\u6b63\u60f3\u95ef\u51fa\u53bb\uff0c\u4f60\u5c31\u6765\u4e86\u3002\u201d\u738b\u7167\u5e0c\u542c\u5979\u8bf4\u5230\u4e0e\u767d\u654f\u5728\u91cc\u9762\u8eb2\u85cf\uff0c\u6beb\u65e0\u7f9e\u6da9\u9762\u7ea2\u4e4b\u6001\uff0c \u5fc3\u5ff5\u4e00\u52a8\uff0c\u6000\u7591\u4e0d\u5b9a\u3002\u5b5f\u79cb\u971e\u53c8\u9053\uff1a\u201c\u6211\u8bb0\u8d77\u4e86\uff0c\u4ed6\u4eec\u8fd8\u4f3c\u4e4e\u63d0\u5230\u90d1\u56fd\u8205\u548c\u9b4f\u516c\u516c\u7684\u540d \u5b57\u3002\u201d \u3000\u3000\u738b\u7167\u5e0c\u66fe\u4f50\u52a9\u7236\u4eb2\u5904\u7406\u8fc7\u8bb8\u591a\u4e8b\u60c5\uff0c\u89c1\u8bc6\u9605\u5386\u90fd\u8d85\u65bc\u4ed6\u7684\u5e74\u7eaa\u3002\u542c\u4e86\u5b5f\u79cb\u971e\u7684\u8bdd\u5f8c\uff0c\u4f4e \u5934\u9ed8\u60f3\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8fd9\u6883\u51fb\u6848\u4e00\u5b9a\u662f\u4e2a\u5927\u9634\u8c0b\uff0c\u6709\u4eba\u4e70\u901a\u51f6\u624b\uff0c\u60f3\u9677\u5bb3\u53e6\u4e00 \u6279\u4eba\u3002\u4f60\u7684\u7236\u4eb2\u662f\u7b2c\u4e00\u4e2a\u63a5\u89e6\u51f6\u624b\u7684\u4eba\uff0c\u6240\u4ee5\u88ab\u5377\u8fdb\u53bb\u4e86\u3002\u4e3b\u8c0b\u7684\u4eba\u53ea\u6050\u4f60\u7236\u4eb2\u77e5\u9053\u4ec0\u9ebd\u5185 \u60c5\uff0c\u6216\u8005\u662f\u60f3\u5957\u95ee\u51f6\u624b\u8bf4\u8fc7\u4e9b\u4ec0\u9ebd\u8bf4\u8bdd\uff0c\u6240\u4ee5\u628a\u4ed6\u67b6\u8d70\u3002\u7167\u60c5\u5f62\u770b\u6765\uff0c\u4e3b\u8c0b\u7684\u4eba\u5b9a\u662f\u671d\u5ef7\u4e0a \u6709\u5927\u52bf\u529b\u7684\u4eba\uff0c\u4e5f\u8bb8\u662f\u90a3\u4e2a\u90d1\u56fd\u8205\uff0c\u6216\u8005\u5c31\u662f\u90a3\u4e2a\u9b4f\u516c\u516c\u3002\u6211\u731c\u60f3\u4f60\u7684\u7236\u4eb2\u4e00\u5b9a\u6ca1\u6709\u6b7b\u3002\u201d \u5b5f\u79cb\u971e\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u738b\u7167\u5e0c\u7b11\u9053\uff1a\u201c\u9664\u975e\u4f60\u7236\u4eb2\u771f\u77e5\u9053\u4e9b\u4ec0\u9ebd\uff0c\u800c\u53c8\u628a\u6240\u77e5\u9053\u7684\u5168\u90fd\u8bf4 \u4e86.\u5426\u5219\u4ed6\u4eec\u7591\u795e\u7591\u9b3c\uff0c\u4e00\u5b9a\u4f1a\u6162\u6162\u5957\u95ee\u3002\u201d\u5b5f\u79cb\u971e\u773c\u775b\u660e\u4eae\uff0c\u8d5e\u53f9\u9053\uff1a\u201c\u4f60\u770b\u5f97\u771f\u900f \u5f7b\u3002\u201d\u5bf9\u9762\u524d\u7684\u8fd9\u4e2a\u5c11\u5e74\uff0c\u4e0d\u81ea\u89c9\u7684\u94a6\u4f69\u8d77\u6765\u3002\u5fc3\u60f3\uff1a\u81ea\u5df2\u672a\u5a5a\u592b\u4e0d\u77e5\u662f\u4ec0\u9ebd\u6837\u7684\u4eba\uff0c\u8981\u662f \u50cf\u8fd9\u4e2a\u59d3\u738b\u7684\u5c11\u5e74\u90a3\u5c31\u597d\u4e86\u3002\u53ef\u5de7\u4ed6\u4eec\u90fd\u662f\u59d3\u738b\u7684\u3002\u60f3\u5230\u8fd9\u91cc\uff0c\u9762\u4e0a\u4e00\u9635\u7ea2\u6655\uff0c\u7c89\u9888\u4f4e\u5782\uff0c \u738b\u7167\u5e0c\u6697\u6697\u8be7\u5f02\uff1a\u600e\u9ebd\u521a\u624d\u8fd8\u662f\u90a3\u6837\u843d\u843d\u5927\u65b9\uff0c\u73b0\u5728\u53c8\u663e\u51fa\u5973\u513f\u7f9e\u6001\u6765\u4e86\u3002 \u3000\u3000\u76c2\u79cb\u971e\u81ea\u89c9\u5931\u6001\uff0c\u6025\u5fd9\u5b9a\u4e86\u5b9a\u795e\uff0c\u62ac\u8d77\u5934\u6765\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u95e8\u5916\u4e00\u9635\u811a\u6b65\u58f0\uff0c\u767d\u654f\u5df2\u56de\u6765 \u4e86\u3002 \u3000\u3000\u767d\u654f\u63d0\u7740\u4e24\u74f6\u9648\u5e74\u8001\u9152\uff0c\u5174\u51b2\u51b2\u7684\u8dd1\u4e0a\u697c\u6765\uff0c\u63a8\u95e8\u8bf4\u9053\uff1a\u201c\u738b\u5144\uff0c\u559d\u4e24\u53e3\u9152\u63d0\u63d0\u795e\u5427\uff0c \u4f60\u6253\u5f97\u592a\u7d2f\u4e86\u3002\u201d\u4e00\u89c1\u738b\u7167\u5e0c\u795e\u91c7\u5955\u5955\uff0c\u53c8\u4e0d\u7981\u559c\u5b5c\u5b5c\u7684\u7b11\u9053\uff1a\u201c\u738b\u5144\uff0c\u4f60\u7cbe\u795e\u6062\u590d\u5f97\u771f \u5feb\uff0c\u521a\u624d\u770b\u4f60\u90a3\u6837\u574f\u7684\u9762\u8272\uff0c\u6211\u8fd8\u62c5\u5fc3\u4f60\u751f\u4e86\u75c5\u5462\uff01\u201d \u3000\u3000\u738b\u7167\u5e0c\u5fc3\u4e2d\u611f\u52a8\uff0c\u6697\u60f3\u8fd9\u5c0f\u5b50\u5012\u50bb\u5f97\u53ef\u7231\u3002\u60f3\u5230\u81ea\u5df2\u4e0e\u672a\u5a5a\u59bb\u5206\u522b\u4e86\u4e00\u5341\u516d\u5e74\uff0c\u82e5\u5979\u53e6 \u6709\u5fc3\u4e0a\u4e4b\u4eba\uff0c\u8fd9\u4e5f\u602a\u5979\u4e0d\u5f97\u3002\u8fd9\u6837\u4e00\u60f3\uff0c\u5fc3\u4e2d\u5bbd\u5766\u8bb8\u591a\uff0c\u53cd\u89c9\u5bf9\u767d\u654f\u6709\u4e9b\u6b49\u610f\u3002 \u3000\u3000\u5b5f\u79cb\u971e\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u50bb\u5c0f\u5b50\uff0c\u5012\u5f88\u4f1a\u732e\u6bb7\u52e4\u3002\u201d\u767d\u654f\u7b11\u563b\u563b\u7684\u659f\u4e86\u53c1\u676f\uff0c\u8bf4\u9053\uff1a\u201c\u5e08 \u59b9\uff0c\u4f60\u4e5f\u559d\u4e00\u676f\u3002\u201d\u5b5f\u79cb\u971e\u8d70\u51fa\u623f\u5916\uff0c\u5411\u5929\u7a7a\u77a7\u4e86\u4e00\u77a7\uff0c\u56de\u6765\u8bf4\u9053\uff1a\u201c\u522b\u5c3d\u987e\u996e\u9152\u4e86\uff0c\u5929\u8272 \u5df2\u5feb\u5c06\u4eae\u4e86\u3002\u536b\u58eb\u4eec\u5c31\u5c06\u6362\u73ed\uff0c\u6211\u4eec\u5f97\u60f3\u4e2a\u529e\u6cd5\u624d\u597d\u3002\u201d\u738b\u7167\u5e0c\u628a\u9152\u676f\u4e00\u63a8\uff0c\u8bf4\u9053\uff1a\u201c\u54b1\u4eec \u8d70\uff01\u201d \u3000\u3000\u738b\u7167\u5e0c\u5e26\u5b5f\u3001\u767d\u4e8c\u4eba\u5230\u67f3\u5bb6\uff0c\u67f3\u897f\u94ed\u4e00\u591c\u65e0\u7720\uff0c\u5c1a\u5728\u5fc3\u7126\u7b49\u5019\u3002\u738b\u7167\u5e0c\u53eb\u76c2\uff0c\u767d\u4e8c\u4eba\u5728 \u5ead\u4e2d\u7a0d\u5019\uff0c\u81ea\u5df2\u548c\u67f3\u897f\u94ed\u8fdb\u5165\u5185\u5ba4\u5bc6\u8c08\u3002\u738b\u7167\u5e0c\u5c06\u7ecf\u8fc7\u60c5\u5f62\u8bf4\u4e86\u4e00\u904d\uff0c\u53c8\u9053\uff1a\u8bf7\u67f3\u53d4\u53d4\u66ff\u6211 \u9690\u7792\u8eab\u5206\uff0c\u5b5f\u5c0f\u59d0\u5e76\u4e0d\u77e5\u9053\u6211\u5c31\u662f\u5979\u7684\u672a\u5a5a\u592b\u5a7f\uff0c\u8fd8\u662f\u4e0d\u8981\u544a\u8bc9\u5979\u597d\u3002\u67f3\u897f\u94ed\u62c8\u987b\u5fae\u7b11\uff0c\u62ac \u5934\u8bf4\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u738b\u7167\u5e0c\u9762\u4e0a\u4e00\u7ea2\uff0c\u5450\u5450\u8bf4\u9053\uff1a\u201c\u8fd8\u662f\u4e0d\u8981\u544a\u8bc9\u5979\u597d\uff01\u201d\u67f3\u897f\u94ed\u5fae\u5fae\u4e00 \u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u5c11\u5e74\u4eba\u7684\u5fc3\u4e8b\u771f\u4e0d\u6613\u731c\uff0c\u597d\uff0c\u6211\u4f9d\u4f60\u4fbf\u662f\u3002\u201d\u8d70\u51fa\u9662\u5b50\uff0c\u7ed9\u5b5f\u79cb\u971e\u548c\u767d\u654f\u5b89 \u6392\u4e86\u6b47\u606f\u7684\u5730\u65b9\u3002 \u3000\u3000\u8fc7\u4e86\u51e0\u5929\uff0c\u98ce\u6ce2\u6e10\u606f\u3002\u67f3\u897f\u94ed\u4ea4\u6e38\u9887\u5e7f\uff0c\u542c\u5728\u5bab\u4e2d\u5f53\u5dee\u7684\u4eba\u4f20\u6765\u7684\u6d88\u606f\uff0c\u795e\u5b97\u7687\u5e1d\u53c8\u628a \u5bab\u4e2d\u7684\u6267\u4e8b\u592a\u76d1\u5e9e\u4fdd\u3001\u5218\u6210\u6740\u4e86\u3002\u5374\u628a\u4e00\u4e2a\u53eb\u505a\u4ec0\u9ebd\u9b4f\u5fe0\u8d24\u7684\u592a\u76d1\uff0c\u5347\u505a\u592a\u76d1\u603b\u7ba1\u3002 \u738b\u7167 \u5e0c\u542c\u4e86\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u8fd9\u9b4f\u5fe0\u8d24\u60f3\u5fc5\u5c31\u662f\u90a3\u4e2a\u4ec0\u9ebd\u201c\u9b4f\u516c\u516c\u201d\u4e86\u3002 \u3000\u3000\u5b5f\u79cb\u971e\u5fc3\u60ac\u8001\u7236\uff0c\u5ea6\u65e5\u5982\u5e74\uff0c\u8fd9\u51e0\u5929\u6765\u5979\u548c\u738b\u7167\u5e0c\u5df2\u7ecf\u5f88\u719f\uff0c\u5c61\u6b21\u50ac\u4ed6\u60f3\u6cd5\u3002\u8fd9\u665a\uff0c\u738b \u7167\u5e0c\u62db\u5b5f\u79cb\u971e\u548c\u767d\u654f\u8fdb\u623f\uff0c\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u5b5f\u5c0f\u59d0\uff0c\u4f60\u6562\u4e0d\u6562\u518d\u5192\u4e00\u6b21\u7edd\u5927\u7684\u5371\u9669\u3002\u201d\u5b5f\u79cb\u971e \u55d4\u9053\uff1a\u201c\u738b\u5144\uff0c\u8fd9\u662f\u4ec0\u9ebd\u8bdd\u6765\uff1f\u6211\u65e0\u529b\u6551\u7236\uff0c\u5df2\u662f\u7f9e\u60ed\u65e0\u5730\uff0c\u6211\u5bb6\u7684\u4e8b\u60c5\u96be\u9053\u8fd8\u80fd\u8981\u738b\u5144\u72ec \u529b\u80a9\u62c5\uff1f\u201d\u738b\u7167\u5e0c\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u61c2\u8bf4\u8bdd\uff0c\u8be5\u6253\u8be5\u6253\u3002\u201d\u767d\u654f\u9053\uff1a\u201c\u4f60\u5feb\u4e9b\u8bf4\u51fa\u529e\u6cd5\u5427\uff0c\u8981\u5192 \u4ec0\u9ebd\u9669\uff0c\u8bf7\u7b97\u6211\u4e00\u4efd\u3002\u6211\u8fd9\u4e2a\u4eba\u6ca1\u6709\u4ec0\u9ebd\u7528\u5904\uff0c\u5c31\u662f\u4e0d\u6015\u6b7b\uff0c\u4e3a\u4e86\u6551\u51fa\u5e08\u7236\uff0c\u6211\u8d74\u6c64\u8e48\u706b\uff0c \u4e5f\u5728\u6240\u4e0d\u8f9e\u3002\u201d\u738b\u7167\u5e0c\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eca\u665a\u60f3\u8fdb\u7687\u5bab\u63a2\u4ed6\u4e00\u63a2\u3002\u6211\u5df2\u63a2\u6e05\u695a\u90a3\u4e2a\u90d1 \u8d35\u5983\u4f4f\u5728\u201c\u4e7e\u6e05\u5bab\u201d\uff0c\u8fde\u5bab\u4e2d\u7684\u5730\u56fe\u6211\u4e5f\u6258\u67f3\u53d4\u53d4\u5f04\u6765\u4e86\u3002\u201d\u767d\u654f\u62cd\u62cd\u624b\u9053\uff1a\u201c\u90a3\u6562\u60c5 \u597d\u3002\u201d\u738b\u7167\u5e0c\u5ffd\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u591c\u63a2\u7687\u5bab\uff0c\u90a3\u9ad8\u6765\u9ad8\u53bb\u7684\u672c\u4e8b\u4e00\u5b9a\u8981\u5341\u5206\u4e86\u5f97\uff0c\u5b5f\u5c0f\u59d0\u7684\u8f7b\u529f \u9020\u8be3\u6211\u53ef\u4ee5\u653e\u5fc3\u2026\u2026\u201d\u767d\u654f\u8fd9\u6b21\u5c45\u7136\u4e0d\u50bb\uff0c\u5fc3\u60f3\u81ea\u5df1\u7684\u8f7b\u529f\u672c\u4e8b\u679c\u7136\u8fdc\u6bd4\u4e0d\u4e0a\u5e08  \uff0c\u968f\u4ed6\u4eec \u53bb\uff0c\u83ab\u8bf4\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u53cd\u6210\u4e86\u7d2f\u8d58\u3002\u56e0\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4e0d\u53bb\u597d\u4e86\u3002\u201d\u5fc3\u65e0\u6742\u5ff5\uff0c\u8bf4\u5f97\u751a\u4e3a \u5766\u7136\u3002 \u3000\u3000\u8fd9\u665a\uff0c\u738b\u7167\u5e0c\u548c\u5b5f\u79cb\u971e\u542c\u5f97\u66f4\u697c\u6572\u4e86\u53c1\u66f4\uff0c\u6362\u4e0a\u9752\u8272\u7684\u591c\u884c\u8863\uff0c\u5230\u4e86\u7d2b\u7981\u57ce\u5916\uff0c\u6de1\u6708\u758f \u661f\uff0c\u4e00\u7247\u9759\u5bc2\u3002\u5b5f\u79cb\u971e\u8db3\u5c16\u70b9\u5730\uff0c\u6b63\u60f3\u8dc3\u4e0a\u5899\u5934\uff0c\u738b\u7167\u5e0c\u5ffd\u7136\u628a\u5979\u626f\u4f4f\uff0c\u6253\u4e86\u4e00\u4e2a\u624b\u52bf\uff0c\u4e00 \u8e72\u8eab\uff0c\u6361\u8d77\u4e24\u5757\u77f3\u5934\uff0c\u4e22\u4eba\u62a4\u57ce\u7684\u5fa1\u6cb3\uff0c\u201c\u535c\u901a\u201d\u4e24\u58f0\uff0c\u58f0\u54cd\u867d\u7136\u4e0d\u5927\uff0c\u5df2\u60ca\u52a8\u4e86\u6697\u4f0f\u5728\u57ce \u4e0a\u7684\u8f6e\u503c\u536b\u58eb\uff0c\u53ea\u89c1\u56db\u6761\u4eba\u5f71\uff0c\u98de\u4e0b\u57ce\u5899\uff0c\u76f4\u5954\u5fa1\u6cb3\u6865\u4e0a\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5c31\u5728\u8fd9\u4e00\u5239 \u90a3\uff0c\u738b\u7167\u5e0c\u548c\u5b5f\u79cb\u971e\u80aa\u8eab\u63a0\u8d77\uff0c\u98de\u4e0a\u57ce\u5899\uff0c\u5c31\u5982\u6362\u73ed\u4e00\u822c\u3002\u738b\u7167\u5e0c\u65e9\u628a\u5bab\u4e2d\u5730\u56fe\u7814\u7a76\u6e05\u695a\uff0c \u5e26\u7740\u5b5f\u79cb\u971e\uff0c\u7ed5\u8fc7\u4e86\u592a\u548c\u3001\u4e2d\u548c\u3001\u7eaa\u548c\u53c1\u5927\u6bbf\u8fdb\u5165\u5185\u5ef7\uff0c\u4e24\u4eba\u8f7b\u529f\u90fd\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u7b49\u5230\u90a3\u51e0 \u4e2a\u8f6e\u503c\u536b\u58eb\u6298\u56de\u5934\u65f6\uff0c\u4ed6\u4eec\u5df2\u5230\u4e86\u4e7e\u6e05\u5bab\u5916\u4fa7\u9762\u7684\u5c0f\u82b1\u56ed\u4e86\u3002 \u3000\u3000\u7687\u5bab\u9762\u79ef\u6781\u5927\uff0c\u771f\u8bf4\u5f97\u4e0a\u662f\u6bbf\u5b87\u8fde\u4e91\uff0c\u7ef5\u4e98\u4e0d\u7edd\uff0c\u5317\u6d77\u3001\u767d\u6d77\u3001\u4ec0\u5239\u6d77\u53c1\u4e2a\u4eba\u5de5\u6e56\u4e5f\u5305 \u62ec\u5728\u7687\u57ce\u4e4b\u5185\uff0c\u6e56\u6c34\u95ea\u95ea\u53d1\u5149\u3002\u738b\u7167\u5e0c\u548c\u5b5f\u79cb\u971e\u4f0f\u5728\u6697\u966c\u4e4b\u5904\uff0c\u5ffd\u89c1\u56ed\u811a\u4fa7\u95e8\u5f00\u5904\uff0c\u6709\u4e94\u516d \u4e2a\u536b\u58eb\u4f34\u7740\u4e00\u4e2a\u8eab\u62ab\u6597\u84ec\uff0c\u5934\u9762\u90fd\u85cf\u5728\u515c\u98ce\u4e4b\u5185\u7684\u4eba\uff0c\u95ea\u95ea\u7f29\u7f29\u7684\u8d70\u4e86\u8fdb\u6765\u3002\u738b\u7167\u5e0c\u76ee\u9001\u4ed6 \u4eec\u8d70\u5165\u5bab\u95e8\uff0c\u6b63\u60f3\u5192\u9669\u4e00\u63a2\uff0c\u8fdc\u5904\u7409\u7483\u74e6\u9762\uff0c\u4eba\u5f71\u5ffd\u7136\u4e00\u95ea\uff0c\u4e00\u6e9c\u70df\u822c\u76f4\u5165\u6bbf\u5b87\u4e4b\u4e2d\u3002\u738b\u7167 \u5e0c\u5927\u5403\u4e00\u60ca\uff0c\u8fd9\u4eba\u8f7b\u529f\u4e4b\u9ad8\uff0c\u7adf\u8fdc\u5728\u81ea\u5df1\u4e4b\u4e0a\u3002\u82e5\u7136\u4ed6\u662f\u5bab\u4e2d\u4f8d\u536b\uff0c\u90a3\u9ebd\u4eca\u665a\u5b9a\u7136\u8d70\u4e0d\u8131 \u4e86\u3002 \u3000\u3000\u5b5f\u79cb\u971e\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u5165\u864e\u7a74\uff0c\u7109\u5f97\u864e\u5b50\uff1f\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u4e14\u7b49\u4e00\u4f1a\u3002\u201d\u5c31\u5728\u8fd9\u4e00\u65f6 \u95f4\uff0c\u5ffd\u542c\u5f97\u4e7e\u6e05\u5bab\u5185\uff0c\u5927\u547c\u201c\u523a\u5ba2\uff01\u201d\u5b98\u5916\u7ea6\u6709\u4e94\u516d\u4e2a\u536b\u58eb\uff0c\u98de\u5954\u8dd1\u6765\u3002\u738b\u7167\u5e0c\u89d1\u51c6\u6700\u5f8c\u4e00 \u540d\uff0c\u7a81\u7136\u957f\u8eab\u800c\u8d77\uff0c\u51fa\u6307\u5982\u7535\uff0c\u4e00\u4e0b\u5b50\u5c31\u70b9\u4e86\u4ed6\u7684\u6655\u7729\u7a74\uff0c\u62d6\u56de\u6697\u5904\uff0c\u5728\u5047\u5c71\u77f3\u5f8c\uff0c\u5265\u4e86\u4ed6 \u7684\u8863\u88f3\uff0c\u5306\u5306\u6362\u4e0a\uff0c\u5bf9\u5b5f\u79cb\u971e\u9053\uff1a\u201c\u4f60\u4f0f\u5728\u8fd9\u91cc\u4e0d\u8981\u4e71\u52a8\uff0c\u6211\u8d70\u8fdb\u5bab\u5185\uff0c\u770b\u4ed6\u4e00\u770b\u3002\u201d\u8dc3\u4e86 \u51fa\u6765\uff0c\u62d4\u5251\u5728\u624b\uff0c\u4ed6\u4e5f\u5927\u53eb\u201c\u6349\u523a\u5ba2\u201d\uff0c\u8dd1\u4eba\u4e7e\u6e05\u5bab\u5185\u3002 \u3000\u3000\u5bab\u4e2d\u6df7\u6218\u6b63\u70c8\uff0c\u738b\u7167\u5e0c\u53ea\u89c1\u4e00\u4e2a\u957f\u8eab\u7389\u7acb\u7684\u5c11\u5e74\uff0c\u624b\u4f7f\u4e00\u628a\u5bd2\u5149\u95ea\u95ea\u7684\u957f\u5251\uff0c\u5927\u6218\u5341\u540d \u536b\u58eb\uff0c\u5251\u5149\u970d\u970d\uff0c\u864e\u864e\u751f\u98ce\uff0c\u6597\u5230\u6025\u5904\uff0c\u4f46\u89c1\u5251\u82b1\u95ea\u70c1\uff0c\u51b7\u7535\u7cbe\u8292\uff0c\u8000\u4eba\u773c\u76ee\u3002\u8fd9\u4eba\u4f7f\u7684\u662f \u6b66\u5f53\u6d3e\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u5251\u6cd5\uff0c\u4f46\u529f\u529b\u4e4b\u6df1\uff0c\u6bd4\u803f\u7ecd\u5357\u4e4b\u6d41\uff0c\u5374\u4e0d\u77e5\u8981\u9ad8\u591a\u5c11\u500d\uff01\u738b\u7167\u5e0c\u6697\u6697\u79f0 \u5947\uff0c\u770b\u4ed6\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5374\u4e0d\u6599\u8fd9\u822c\u4e86\u5f97\uff01 \u3000\u3000\u4f46\u5bab\u4e2d\u536b\u58eb\u4f17\u591a\uff0c\u5c11\u5e74\u867d\u7136\u5389\u5bb3\uff0c\u88ab\u5341\u9980\u4eba\u56f4\u653b\uff0c\u4e5f\u60ed\u60ed\u652f\u6301\u4e0d\u4f4f\u3002\u738b\u7167\u5e0c\u6b63\u770b\u5f97\u51fa \u795e\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u4e0a\u53bb\u5440\uff01\u201d\u8fd9\u4eba\u4e43\u662f\u9526\u8863\u536b\u7684\u4e00\u4e2a\u6307\u6325\uff0c\u738b\u7167\u5e0c\u8eb2 \u95ea\u4e0d\u53ca\uff0c\u548c\u4ed6\u6253\u4e86\u4e00\u4e2a\u7167\u9762\u3002\u8fd9\u4eba\u4e00\u89c1\u662f\u4e2a\u964c\u751f\u9762\u5b54\uff0c\u6bd4\u521a\u624d\u53d1\u73b0\u523a\u5ba2\u8fd8\u8981\u60ca\u614c\uff0c\u5927\u58f0\u53eb \u9053\uff1a\u201c\u6709\u4eba\u5192\u5145\u4f8d\u536b\u8fdb\u5bab\uff01\u201d\u624b\u4e2d\u94c1\u5c3a\u4e5f\u8fce\u5934\u5288\u4e0b\uff01\u738b\u7167\u5e0c\u5237\u5237\u4e24\u5251\uff0c\u628a\u4ed6\u523a\u4f24\uff0c\u4f46\u81ea\u5df2\u4e5f \u9677\u5165\u4e86\u5305\u56f4\u3002 \u3000\u3000\u8fd9\u8eab\u957f\u7389\u7acb\u7684\u5c11\u5e74\u6b63\u662f\u5353\u4ef2\u5ec9\u7684\u5b59\u5b50\u5353\u4e00\u822a\uff0c\u4ed6\u4e03\u5c81\u4e4b\u65f6\uff0c\u968f\u7236\u4eb2\u5353\u7ee7\u8d24\u6765\u4eac\uff0c\u9002\u9022\u6b66 \u5f53\u6d3e\u7684\u638c\u95e8\u7d2b\u9633\u9053\u957f\u4e5f\u6765\u4eac\u5316\u7f18\u3002\u7d2b\u9633\u9053\u957f\u5251\u6cd5\u5929\u4e0b\u65e0\u53cc\uff0c\u6b63\u60f3\u627e\u5bfb\u4e00\u4e2a\u6709\u6839\u57fa\u7684\u5c11\u5e74\u7ee7\u627f \u8863\u94b5\u3002\u4e00\u65e5\u6765\u5230\u5353\u5e9c\uff0c\u89c1\u5353\u4e00\u822a\u5934\u89d2\u5ce5\u5d58\uff0c\u6c14\u5b87\u4e0d\u51e1\uff0c\u52a8\u4e86\u6536\u5f92\u4e4b\u5ff5\u3002\u5353\u7ee7\u8d24\u4ee5\u524d\u5728\u6e56\u5317\u4e3a \u5b98\uff0c\u66fe\u548c\u7d2b\u9633\u9053\u957f\u6709\u4e00\u9762\u4e4b\u7f18\uff0c\u77e5\u4ed6\u6b66\u529f\u5999\u5965\uff0c\u6df1\u4e0d\u53ef\u6d4b\u3002\u4e5f\u613f\u513f\u5b50\u6210\u4e3a\u6587\u6b66\u5168\u6750\u7684\u5b8c\u4eba\uff0c \u65bc\u662f\u4e00\u53e3\u7b54\u5141\u3002\u7d2b\u9633\u9053\u957f\u628a\u4ed6\u5e26\u56de\u5c71\u4e2d\uff0c\u5168\u5fc3\u6559\u6388\uff0c\u53c8\u7528\u836f\u7269\u57f9\u517b\u4ed6\u7684\u5143\u6c14\uff0c\u78e8\u7ec3\u4ed6\u7684\u4f53 \u80a4\uff0c\u5982\u662f\u7ecf\u8fc7\u4e00\u5341\u4e8c\u5e74\uff0c\u5353\u4e00\u822a\u5df2\u5f97\u4e86\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u5251\u548c\u4e5d\u5bab\u795e\u884c\u638c\u7684\u5168\u90e8\u79d8\u5965\uff0c\u672c\u9886\u5728\u6b66 \u5f53\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\u4e2d\u9996\u5c48\u4e00\u6307\uff0c\u751a\u81f3\u6bd4\u82e5\u5e72\u5e08\u53d4\u8fd8\u5f3a\u3002\u5728\u8fd9\u5341\u4e8c\u5e74\u95f4\uff0c\u7d2b\u9633\u9053\u957f\u6bcf\u53c1\u5e74\u5e26\u4ed6\u56de\u4eac \u4e00\u6b21\uff0c\u8ba9\u4ed6\u7559\u5728\u5bb6\u4e2d\u4e00\u6708\uff0c\u653b\u8bfb\u8bd7\u4e66\uff0c\u5728\u8fd9\u4e00\u6708\u4e2d\uff0c\u5353\u7ee7\u8d24\u5c31\u8bf7\u540d\u5e08\u5bbf\u5112\u66ff\u4ed6\u8bb2\u89e3\u7ecf\u53f2\u5965 \u4e49\uff0c\u6ee1\u4e86\u4e00\u6708\u53c8\u8ba9\u4ed6\u628a\u4e66\u672c\u5e26\u56de\u5c71\u4e2d\u81ea\u4e60\u3002\u6240\u4ee5\u5353\u4e00\u822a\u662f\u6587\u6b66\u53cc\u4fee\uff0c\u5e08\u7236\uff0c\u7236\u4eb2\u90fd\u6781\u6ee1\u610f\u3002 \u3000\u3000\u5230\u4e86\u5353\u4e00\u822a\u5341\u4e5d\u5c81\u8fd9\u5e74\uff0c\u7d2b\u9633\u9053\u957f\u89c1\u4ed6\u6b66\u529f\u5df2\u6210\uff0c\u800c\u5353\u7ee7\u8d24\u53c8\u60f3\u4ed6\u56de\u4eac\u5e94\u4e3e\uff0c\u56e0\u6b64\u7d2b\u9633 \u9053\u957f\u9001\u4ed6\u56de\u6765\uff0c\u5e76\u8d50\u4e86\u4ed6\u4e00\u628a\u5bd2\u5149\u5251\u3002\u5206\u624b\u65f6\u7d2b\u9633\u9053\u957f\u9053\uff1a\u201c\u6211\u6df1\u613f\u4f60\u5728  \u6d77\u4e2d\u4e0d\u8981\u6c89\u8ff7\uff0c \u5c06\u6765\u6b66\u5f53\u6d3e\u638c\u95e8\u7684\u62c5\u5b50\uff0c\u8fd8\u8981\u4f60\u80a9\u62c5\u5462\u3002\u201d\u5353\u4e00\u822a\u9886\u4e86\u5e08\u7236\u7684\u5429\u5490\uff0c\u56de\u8f6c\u5bb6\u95e8\uff0c\u53c1\u5e74\u4e0d\u89c1\uff0c \u4ed6\u5df2\u957f\u5f97\u6bd4\u7236\u4eb2\u8fd8\u9ad8\u4e00\u4e2a\u5934\u4e86\u3002 \u3000\u3000\u7236\u5b50\u56e2\u5706\uff0c\u4e00\u5bb6\u9ad8\u5174\u3002\u5374\u4e0d\u6599\u98ce\u6ce2\u5ffd\u8d77\uff0c\u6a2a\u7978\u98de\u6765\uff0c\u7236\u5b50\u76f8\u805a\uff0c\u4e0d\u5230\u53c1\u6708\uff0c\u5353\u7ee7\u8d24\u5c31\u88ab \u5377\u5165\u4e86\u201c\u6883\u51fb\u6848\u201d\u7684\u6f29\u6da1\uff0c\u4e00\u65e5\u4e0a\u671d\uff0c\u9042\u6210\u6c38\u8bc0\u3002\u5353\u4e00\u822a\u54c0\u75db\u903e\u5e38\uff0c\u5728\u5c45\u5b98\u7684\u7236\u6267\u5904\u63a2\u542c\u5f97 \u77e5\uff0c\u7236\u4eb2\u4e43\u662f\u88ab\u90d1\u56fd\u8205\u6240\u8bec\u9677\uff0c\u800c\u90d1\u56fd\u8205\u53c8\u662f\u79c9\u627f\u4ed6\u59b9\u5b50\u90d1\u8d35\u5983\u7684\u610f\u65e8\u3002\u5353\u4e00\u822a\u4e00\u6012\u4e4b\u4e0b\uff0c \u4e0d\u7ba1\u5bab\u4e2d\u597d\u624b\u5982\u4e91\uff0c\u7adf\u81ea\u4e00\u5251\u5355\u8eab\uff0c\u6df1\u5bb5\u95ef\u5165\u3002 \u3000\u3000\u518d\u8bf4\u738b\u7167\u5e0c\u9677\u5165\u5305\u56f4\uff0c\u5c55\u5f00\u8e51\u4e91\u5251\u6cd5\uff0c\u98d8\u5ffd\u5982\u98ce\uff0c\u4e13\u62e3\u654c\u4eba\u7684\u7f45\u9699\u8fdb\u653b\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5c45 \u7136\u7ed9\u4ed6\u79fb\u8fd1\u4e86\u5353\u4e00\u822a\uff0c\u5353\u4e00\u822a\u4e5f\u8fde\u51b2\u6570\u5251\uff0c\u6740\u5f00\u4e00\u4e2a\u7f3a\u53e3\uff0c\u628a\u738b\u7167\u5e0c\u63a5\u7eb3\u8fdb\u6765\uff0c\u4e24\u4eba\u8054\u5251\u5e76 \u80a9\uff0c\u5a01\u529b\u5927\u589e\uff0c\u548c\u536b\u58eb\u4eec\u6df7\u6218\uff0c\u6709\u5b88\u6709\u653b\uff0c\u770b\u770b\u5c31\u53ef\u95ef\u51fa\u3002 \u3000\u3000\u8fd9\u65f6\u4e7e\u6e05\u5bab\u5185\u7684\u5bdd\u5bab\u623f\u95e8\u5ffd\u6563\uff0c\u90d1\u8d35\u5983\u5144\u59b9\u548c\u521a\u624d\u8fdb\u5bab\u90a3\u4e2a\u62ab\u7740\u6597\u7bf7\u7684\u7537\u5b50\uff0c\u5728\u4e94\u516d\u4e2a \u536b\u58eb\u56f4\u62e5\u4e4b\u4e0b\uff0c\u501a\u95e8\u89c2\u6218\uff0c\u90d1\u8d35\u5983\u7b11\u9053\uff1a\u201c\u5e38\u6d35\uff0c\u53eb\u4f60\u7684\u968f\u4ece\u663e\u663e\u529f\u592b\u3002\u8fd9\u4e9b\u536b\u58eb\u8113\u5305\uff0c\u8fde \u4e24\u540d\u5c0f\u8d3c\u90fd\u6349\u4e0d\u7740\u3002\u4e0d\u65e9\u70b9\u6536\u62ac\uff0c\u9a7e\u52a8\u6b63\u5bab\uff0c\u53cd\u800c\u4e0d\u5999\u3002\u201d\u90a3\u62ab\u7740\u6597\u7bf7\u7684\u7537\u5b50\u628a\u624b\u4e00\u6325\uff0c\u4e24 \u540d\u536b\u58eb\u75be\u51b2\u51fa\u53bb\uff0c\u4e00\u4e2a\u4f7f\u62a4\u624b\u94a9\uff0c\u76f4\u5954\u5353\u4e00\u822a\uff0c\u4e00\u4e2a\u53cc\u624b\u7a7a\u7a7a\uff0c\u7adf\u7136\u51ed\u7740\u4e00\u53cc\u8089\u638c\uff0c\u6765\u786c\u62a2 \u738b\u7167\u5e0c\u7684\u957f\u5251\u3002\u738b\u7167\u5e0c\u5237\u7684\u4e00\u5251\uff0c\u90a3\u4eba\u8eab\u5f62\u4e00\u77ee\uff0c\u7adf\u7136\u4ece\u4fa7\u9762\u62a2\u6765\uff0c\u738b\u7167\u5e0c\u7684\u8e51\u4e91\u5251\u4ee5\u5feb\u6377 \u89c1\u957f\uff0c\u4e00\u523a\u4e0d\u4e2d\uff0c\u7acb\u523b\u53d8\u62db\u6a2a\u622a\u654c\u4eba\u624b\u8155\uff0c\u5251\u5c16\u4e0b\u523a\u654c\u4eba\u819d\u76d6\uff0c\u90a3\u4eba\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u53cc\u638c\u62a4 \u8eab\uff0c\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u8fd9\u4eba\u7ec3\u5c31\u9e70\u722a\u529f\uff0c\u5728\u201c\u7a7a\u624b\u593a\u767d\u5203\u201d\u8fd9\u95e8\u529f\u592b\u4e0a\uff0c\u6709\u5f88\u6df1\u7684\u9020\u8be3\u3002\u4e0d\u6599\u738b\u7167\u5e0c\u5bb6\u4f20\u5251 \u6cd5\uff0c\u51cc\u5389\u5f02\u5e38\uff0c\u8fd9\u4eba\u8fde\u6251\u6570\u6b21\uff0c\u90fd\u672a\u5f97\u624b\u3002\u90a3\u8fb9\u4f7f\u62a4\u8eab\u53cc\u94a9\u7684\u536b\u58eb\uff0c\u4ee5\u4e3a\u51ed\u7740\u53cc\u94a9\u53ef\u4ee5\u514b\u5236 \u5200\u5251\uff0c\u6545\u4e00\u4e0a\u6765\u5c31\u7528\u6025\u62db\u201c\u5927\u9e4f  \u7fc5\u201d\uff0c\u53cc\u94a9\u4e00\u5408\u4e00\u62c9\uff0c\u8981\u9501\u62ff\u5353\u4e00\u822a\u624b\u4e2d\u7684\u957f\u5251\uff0c\u4e0d\u6599\u5353 \u4e00\u822a\u5251\u672f\u66f4\u5999\uff0c\u957f\u5251\u4e00\u7ffb\uff0c\u9752\u5149\u531d\u5730\uff0c\u5f8c\u53d1\u5148\u81f3\uff0c\u90a3\u4eba\u53cc\u94a9\u72b9\u672a\u9012\u5230\uff0c\u4ed6\u7684\u957f\u5251\u5df2\u4ee5\u201c\u65cb\u98ce \u626b\u53f6\u201d\u7684\u62db\u6570\u65a9\u5411\u654c\u624b\u4e0b\u76d8\uff0c\u4f7f\u62a4\u624b\u94a9\u7684\u4e5f\u7531\u4e0d\u5f97\u9000\u4e86\u51e0\u6b65\uff0c\u5e38\u6d35\u89c1\u81ea\u5df1\u501a\u91cd\u7684\u4e24\u540d\u9ad8\u624b\uff0c \u51fa\u624b\u4e0d\u5229\uff0c\u4e0d\u7981\u751a\u4e3a\u5931\u671b\u3002 \u3000\u3000\u4f46\u8fd9\u4e24\u4eba\u529f\u592b\u5230\u5e95\u6bd4\u5176\u4ed6\u536b\u58eb\u5f3a\u5f97\u591a\uff0c\u8fd9\u4e00\u52a0\u5165\uff0c\u914d\u5408\u4e86\u5176\u4ed6\u5341\u9980\u540d\u536b\u58eb\uff0c\u628a\u5353\u738b\u4e8c\u4eba \u7d27\u7d27\u56f4\u7740\uff0c\u53c8\u62d6\u5ef6\u4e86\u4e00\u4e9b\u65f6\u5019\uff0c\u738b\u7167\u5e0c\u4e0d\u89c9\u7126\u8e81\u8d77\u6765\uff0c\u5ffd\u542c\u5f97\u5b5f\u79cb\u971e\u5c16\u58f0\u6025\u53eb\uff0c\u63a5\u7740\u662f\u4e00\u7247 \u53eb\u558a\u6349\u5973\u523a\u5ba2\u4e4b\u58f0\uff0c\u738b\u7167\u5e0c\u66f4\u6025\uff0c\u5237\u5237\u6570\u5251\uff0c\u786c\u5f80\u524d\u51b2\uff0c\u4e0e\u5353\u4e00\u822a\u7a0d\u7a0d\u5206\u5f00\uff0c\u536b\u58eb\u7acb\u523b\u4e58\u865a \u800c\u4eba\uff0c\u628a\u4e24\u4eba\u9694\u5728\u4e24\u5904\uff0c\u738b\u7167\u5e0c\u4e00\u6025\u5219\u4e71\uff0c\u867d\u7136\u52c7\u731b\u524d\u6251\uff0c\u6740\u4f24\u4e86\u4e24\u540d\u536b\u58eb\uff0c\u800c\u81ea\u5df1\u80a9\u5934\u706b \u8fa3\u8fa3\u7684\uff0c\u4e5f\u4e2d\u4e86\u4e00\u5200\uff0c\u9669\u8c61\u73af\u751f\uff0c\u51e0\u906d\u4e0d\u6d4b\u3002\u6025\u5fd9\u51dd\u795e\u6b62\u8e81\uff0c\u628a\u4e00\u67c4\u5251\u821e\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u7f29\u77ed \u5708\u5b50\uff0c\u62a4\u8eab\u5f85\u63f4\u3002 \u3000\u3000\u6b63\u6df7\u6218\u95f2\uff0c\u4e7e\u6e05\u5bab\u5916\u4fa7\u9762\u7684\u82b1\u56ed\uff0c\u56ed\u95e8\u5927\u5f00\uff0c\u4e00\u961f\u536b\u58eb\u75be\u8dd1\u8fdb\u6765\uff0c\u90d1\u8d35\u5983\u9762\u4e0a\u53d8\u8272\uff0c\u6025 \u63a8\u90a3\u4e2a\u62ab\u7740\u6597\u7bf7\u7684\u7537\u5b50\u5165\u5185\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8fd9\u961f\u536b\u58eb\u5df2\u8dd1\u5230\u5bab\u524d\uff0c\u5374\u5e76\u4e0d\u52a0\u4eba\u8ffd\u62ff\u523a \u5ba2\uff0c\u5f53\u4e2d\u4e00\u4e2a\u7537\u5b50\uff0c\u5728\u536b\u58eb\u7c07\u62e5\u4e4b\u4e0b\uff0c\u5927\u53eb\u201c\u505c\u624b\uff0c\u641c\u5bab\uff01\u201d\u5305\u56f4\u738b\u5353\u4e8c\u4eba\u7684\u536b\u58eb\uff0c\u5413\u5f97\u4e2a \u4e2a\u4f4f\u624b\u8df3\u5f00\uff0c\u90d1\u8d35\u5983\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u6bbf\u4e0b\uff0c\u6211\u72af\u4e86\u4ec0\u9ebd\u7f6a\u4e86\uff1f\u201d\u539f\u6765\u8fd9\u4eba\u4e43\u662f\u592a\u5b50\uff0c\u53ea\u542c\u4ed6\u53c8 \u5927\u58f0\u559d\u9053\uff1a\u201c\u641c\u5bab\uff01\u201d\u4ed6\u5e26\u6765\u7684\u536b\u58eb\uff0c\u51b2\u4e0a\u53f0\u9636\u3002\u90d1\u8d35\u5983\u5934\u53d1\u4e00\u7529\uff0c\u5389\u58f0\u65a5\u9053\uff1a\u201c\u6ca1\u6709\u4e07\u5c81 \u7237\u7684\u5723\u65e8\uff0c\u8c01\u6562\u64c5\u8fdb\u6b64\u95e8\u3002\u201d\u536b\u58eb\u4e00\u7a92\uff0c\u592a\u5b50\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u65e9\u5df3\u6709\u4eba\u64c5\u8fdb\u6b64\u95e8\uff0c\u4e0d\u5fc5\u7236\u7687\u5723 \u65e8\uff0c\u4e07\u4e8b\u6709\u6211\u627f\u5f53\uff01\u201d\u536b\u58eb\u4eec\u53d1\u4e00\u58f0\u558a\uff0c\u62a2\u4eba\u5bab\u6bbf\uff0c\u90d1\u8d35\u5983\u4e5f\u5c16\u58f0\u53eb\u9053\uff1a\u201c\u66ff\u6211\u6321\u7740\u8fd9\u4e9b\u66b4 \u5f92\uff0c\u6211\u4e0e\u4ed6\u5230\u4e07\u5c81\u7237\u524d\u8bb2\u7406\u53bb\uff0c\u4e07\u4e8b\u6709\u6211\u627f\u5f53\uff01\u201d\u4e24\u8fb9\u9488\u950b\u76f8\u5bf9\uff0c\u536b\u58eb\u5404\u4e3a\u5176\u4e3b\uff0c\u987f\u65f6\u6df7\u6740 \u8d77\u6765\uff01 \u3000\u3000\u5353\u4e00\u822a\u8eab\u5f62\u6025\u8d77\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u53eb\u9053\uff1a\u201c\u592a\u5b50\uff0c\u6211\u66ff\u4f60\u6349\u62ff\u53db\u8d3c\uff01\u201d\u53ea\u89c1\u4ed6\u7ffb\u8eab\u8fdb\u5251\uff0c\u5728 \u4eba\u4e1b\u4e2d\u76f4\u7a7f\u8fc7\u53bb\uff0c\u4e7e\u6e05\u5b98\u7684\u536b\u58eb\u5728\u6df7\u6218\u4e2d\u90a3\u5206\u5f97\u51fa\u8eab\u6765\u62e6\u4ed6\uff0c\u5bab\u5185\u6709\u53c1\u51e0\u4e2a\u536b\u58eb\u51b2\u51fa\u62e6\u622a\uff0c \u4e5f\u7ed9\u4ed6\u4e00\u987f\u6cfc\u98ce\u5251\u6cd5\uff0c\u8fde\u73af\u53d1\u62db\uff0c\u6253\u5f97\u4e1c\u6b6a\u897f\u5012\u3002\u90a3\u62ab\u7740\u6597\u7bf7\u7684\u7537\u5b50\uff0c\u8dd1\u5728\u90d1\u8d35\u5983\u524d\u5934\uff0c\u770b \u770b\u5c31\u53ef\u8fdb\u5165\u5185\u5ba4\uff0c\u5353\u4e00\u822a\u8db3\u5c16\u4e00\u70b9\uff0c\u5e73\u5730\u8dc3\u8d77\uff0c\u75be\u5982\u98de\u7bad\uff0c\u5728\u534a\u7a7a\u4e2d\u75be\u51b2\u6251\u4e0b\uff0c\u4e00\u628a\u6293\u7740\u4ed6 \u7684\u6597\u7bf7\uff0c\u62ff\u4e86\u8d77\u6765\uff0c\u5c06\u4ed6\u7684\u8eab\u8eaf\u5f53\u6210\u5175\u5668\uff0c\u4e00\u4e2a\u65cb\u98ce\u6025\u821e\uff0c\u6325\u4e86\u4e00\u4e2a\u5706\u5708\uff0c\u5b98\u5185\u867d\u6709\u4e94\u4e03\u540d \u536b\u58eb\uff0c\u90a3\u4e2a\u6562\u4e0a\uff01\u5728\u8fd9\u65f6\u95f4\uff0c\u738b\u7167\u5e0c\u4e5f\u6325\u5251\u6740\u4e86\u5165\u6765\uff0c\u592a\u5b50\u548c\u4e24\u540d\u4f8d\u536b\u4e5f\u5df2\u95ef\u4eba\u6bbf\u4e2d\u3002 \u3000\u3000\u5353\u4e00\u822a\u4e00\u4e2a\u65cb\u98ce\u6025\u821e\uff0c\u5c06\u64d2\u83b7\u7684\u90a3\u4e2a\u7537\u5b50\u5411\u5916\u629b\u51fa\uff0c\u65e9\u6709\u592a\u5b50\u5e26\u6765\u7684\u536b\u58eb\u4e0a\u524d\u63a5\u8fc7\uff0c\u63ed \u5f00\u98ce\u515c\uff0c\u73b0\u51fa\u9762\u76ee\uff0c\u536b\u58eb\u60ca\u53eb\u9053\uff1a\u201c\u4e8c\u7687\u5b50\uff01\u201d\u592a\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u628a\u4ed6\u56f0\u4e86\uff01\u7ee7\u7eed\u641c\u5bab\uff01\u201d\u5353 \u4e00\u822a\u53cc\u81c2\u4e00\u632f\uff0c\u5288\u556a\u4e24\u638c\uff0c\u628a\u4e7e\u6e05\u5bab\u5185\u6bbf\u7684\u5bab\u95e8\u9707\u5f00\uff0c\u4e00\u9a6c\u95ef\u8fdb\u3002 \u3000\u3000\u539f\u6765\u4e8c\u7687\u5b50\u5e38\u6d35\uff0c\u4ed7\u7740\u6bcd\u4eb2\u90d1\u8d35\u5983\u5f97\u7236\u7687\u5ba0\u7231\uff0c\u65e9\u601d\u9634\u8c0b\u593a\u5ae1\uff0c\u4f46\u671d\u4e2d\u5927\u81e3\u591a\u662f\u592a\u5b50\u7684 \u7fbd\u7ffc\uff0c\u88ab\u8feb\u79bb\u5f00\u4eac\u5e08\uff0c\u53d7\u5c01\u5230\u6d1b\u9633\u53bb\u505a\u85e9\u738b\u3002\u90d1\u8d35\u5983\u5fc3\u4e2d\u4e0d\u5fff\uff0c\u52fe\u7ed3\u4e86\u592a\u76d1\u9b4f\u5fe0\u8d24\u3001\u54e5\u54e5\u90d1 \u56fd\u6cf0\u4e0e\u82e5\u5e72\u671d\u81e3\u7ed3\u6210\u515a\u7fbd\uff0c\u5b9a\u4e0b\u4e86\u4e00\u6761\u6076\u6bd2\u4e4b\u8ba1\uff0c\u5506\u4f7f\u4e00\u4e2a\u5fc3\u8179\u6b7b\u58eb\uff0c\u626e\u6210\u766b\u6c49\uff0c\u5728\u9752\u5929\u767d \u65e5\u4e4b\u4e0b\uff0c\u624b\u6267\u67a3\u6728\u68cd\uff0c\u786c\u95ef\u6148\u5e86\u5bab\uff0c\u88ab\u64d2\u4e4b\u5f8c\uff0c\u6545\u610f\u75af\u8a00\u75af\u8bed\uff0c\u4e71\u4f9b\u540c\u515a\uff0c\u5ac1\u7978\u63d2\u810f\uff0c\u5c06\u6276 \u52a9\u592a\u5b50\u7684\u5927\u81e3\u4e00\u4e2a\u4e2a\u7275\u8fde\u4eba\u5185\uff0c\u53c8\u628a\u5bab\u4e2d\u4e24\u4e2a\u6700\u6709\u52bf\u529b\u7684\u592a\u76d1\u5e9e\u4fdd\u5218\u6210\u9664\u4e86\uff0c\u8ba9\u9b4f\u5fe0\u8d24\u5f97\u4ee5 \u638c\u63e1\u4e1c\u5382\uff0c\u63a5\u4efb\u201c\u5b97\u4e3b\u201d\u3002 \u201c\u6309\uff1a\u660e\u671d\u7684\u7279\u52a1\u7ec4\u7ec7\uff0c\u5206\u201c\u4e1c\u5382\u201d\u201c\u897f\u5382\u201d\u548c\u201c\u9526\u8863\u536b\u201d\u53c1 \u4e2a\u673a\u5173\uff0c\u4e1c\u897f\u5382\u7531\u592a\u76d1\u638c\u63e1\uff0c\u201c\u9526\u8863\u536b\u201d\u5219\u7531\u6b66\u5b98\u4e3b\u7ba1\u3002\u4e1c\u5382\u7684\u603b\u7ba1\u79f0\u4e3a\u201c\u5b97\u4e3b\u201d\u3002\u201d\u5e38\u6d35 \u5728\u6d1b\u9633\u4e5f\u6536\u4e70\u6b7b\u58eb\uff0c\u5bc6\u8c0b\u9020\u53cd\u3002\u5f8c\u6765\u201c\u6883\u51fb\u6848\u201d\u9634\u8c0b\u6210\u529f\uff0c\u7275\u8fde\u65e5\u5e7f\uff0c\u90d1\u8d35\u5983\u4ee5\u4e3a\u5927\u4e8b\u53ef \u6210\uff0c\u9042\u5bc6\u53ec\u513f\u5b50\u8fdb\u4eac\u3002\u4e0d\u6599\u592a\u5b50\u5e38\u6d1b\uff0c\u9887\u4e3a\u7cbe\u660e\uff0c\u624b\u4e0b\u4e5f\u6709\u4e00\u73ed\u6b66\u58eb\u3002\u5e38\u6d35\u8fdb\u4eac\u7684\u4e8b\uff0c\u5c45\u7136 \u7ed9\u4ed6\u4fa6\u5bdf\u51fa\u6765\uff0c\u56e0\u6b64\u9042\u7206\u53d1\u4e86\u6df1\u5bab\u558b\u8840\u7684\u4e00\u5e55\u602a\u5267\u3002 \u3000\u3000\u5353\u4e00\u822a\u9707\u574d\u5bab\u95e8\uff0c\u76f4\u95ef\u8fdb\u53bb\u3002\u53ea\u89c1\u90d1\u8d35\u5983\u5144\u5f1f\u548c\u4e00\u4e2a\u767d\u51c0\u80a5\u80d6\u7684\u592a\u76d1\u90fd\u5728\u6bbf\u4e2d\u3002\u5353\u4e00\u822a \u8ba4\u5b9a\u90d1\u8d35\u5983\u5144\u5f1f\u662f\u9677\u5bb3\u4ed6\u7236\u4eb2\u7684\u4ec7\u4eba\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u62a1\u62f3\u76f4\u4e0a\u3002\u90a3\u592a\u76d1\u6b63\u662f\u9b4f\u5fe0\u8d24\uff0c\u65a5\u9053\uff1a \u201c\u4f60\u6562\u9020\u53cd\uff1f\u201d\u628a\u624b\u4e00\u6325\uff0c\u56db\u540d\u201c\u693f\u5934\u201d\u201c\u4e1c\u5382\u536b\u58eb\u7684\u5934\u76ee\u201d\u4e00\u9f50\u8fce\u51fb\uff0c\u5353\u4e00\u822a\u547c\u7684\u4e00\u638c\u626b \u53bb\uff0c\u7b2c\u4e00\u540d\u201c\u6869\u5934\u201d\u4f38\u81c2\u4e00\u683c\uff0c\u8eab\u5f62\u4e00\u6b6a\uff0c\u5c45\u7136\u5e76\u4e0d\u9000\u5f8c\uff0c\u7b2c\u4e8c\u540d\u201c\u6869\u5934\u201d\u53cd\u638c\u4e00\u6325\uff0c\u7adf\u662f \u94c1\u7435\u7436\u624b\u7684\u529f\u592b\uff0c\u631f\u7740\u52b2\u98ce\uff0c\u6251\u9762\u6253\u6765\uff0c\u7b2c\u53c1\u540d\u201c\u6869\u5934\u201d\u4e58\u7740\u4ed6\u65cb\u8eab\u4e4b\u9645\u5de6\u80a9\u5411\u524d\u4e00\u649e\uff0c\u548c \u5353\u4e00\u822a\u78b0\u4e2a\u6b63\u7740\uff0c\u4ed6\u7ed9\u5353\u4e00\u822a\u53cd\u9707\u4e4b\u529b\uff0c\u9707\u5012\u5730\u4e0a\uff0c\u5353\u4e00\u822a\u4e5f\u7ed9\u4ed6\u78b0\u5f97\u6b6a\u6b6a\u659c\u659c\uff0c\u6536\u4e0d\u7740 \u811a\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7b2c\u56db\u540d\u201c\u6869\u5934\u201d\u535c\u5730\u98de\u8d77\u4e00\u817f\uff0c\u4e00\u4e2a\u201c\u8e6c\u811a\u201d\u8e22\u5728\u5353\u4e00\u822a\u80ef\u4e0a\uff0c\u987f\u65f6 \u628a\u5353\u4e00\u822a\u8e22\u51fa\u4e00\u4e08\u4ee5\u5916\uff0c\u4f46\u5374\u5e76\u672a\u8dcc\u5012\u3002\u8fd9\u56db\u540d\u201c\u6869\u5934\u201d\u90fd\u662f\u4e1c\u5382\u9ad8\u624b\uff0c\u6b66\u529f\u8fdc\u5728\u5916\u9762\u6df7\u6218 \u7684\u536b\u58eb\u4e4b\u4e0a\u3002\u5353\u4e00\u822a\u867d\u7136\u6b66\u529f\u6781\u9ad8\uff0c\u4f46\u7ecf\u9a8c\u706b\u5019\u90fd\u5c1a\u4e0d\u8db3\uff0c\u4ee5\u4e00\u654c\u56db\uff0c\u7adf\u7136\u5403\u4e86\u5927\u4e8f\u3002\u5353\u4e00 \u822a\u52c3\u7136\u5927\u6012\uff0c\u4e00\u4e2a\u7ffb\u8eab\uff0c\u62d4\u51fa\u5bd2\u5149\u5b9d\u5251\uff0c\u738b\u7167\u5e0c\u548c\u592a\u5b50\u7684\u536b\u58eb\uff0c\u4e5f\u5df2\u7ecf\u4eba\u5230\u5185\u6bbf\u6765\u4e86\u3002\u592a\u5b50 \u559d\u9053\uff1a\u201c\u5e38\u6d35\u79c1\u79bb\u85e9\u5730\uff0c\u56fe\u8c0b\u53db\u9006\uff0c\u8c01\u6562\u5305\u5e87\uff0c\u4e00\u5e76\u62ff\u4e86\u3002\u201d\u559d\u58f0\u672a\u505c\uff0c\u5df2\u7ecf\u5165\u5230\u5185\u6bbf\u6765\u4e86 \u9b4f\u5fe0\u8d24\u5ffd\u7136\u628a\u624b\u4e00\u62db\uff0c\u53eb\u9053\uff1a\u201c\u9075\u547d\uff01\u201d\u7adf\u6307\u6325\u56db\u4e2a\u201c\u6869\u5934\u201d\uff0c\u4e00\u628a\u5c31\u5c06\u8d35\u5983\u5144\u5f1f\u62ff\u7740\u3002\u7b11 \u563b\u563b\u7684\u9053\uff1a\u201c\u90d1\u8d35\u5983\u5144\u5f1f\u4e3b\u8c0b\u53db\u9006\uff0c\u6211\u662f\u8bc1\u4eba\uff01\u201d\u592a\u5b50\u6115\u7136\uff0c\u738b\u7167\u5e0c\u5374\u5fc3\u4e0d\u5728\u7109\uff0c\u63d0\u5251\u56db \u987e\u3002\u6b63\u662f\uff1a\u6df1\u5bab\u558b\u8840\uff0c\u5927\u8d77\u6ce2\u6f9c\uff0c\u5200\u5149\u5251\u5f71\uff0c\u9aa8\u8089\u76f8\u6b8b\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377\u2014\u2014\u7b2c\u4e09\u56de\u3000\u624b\u8db3\u76f8\u6b8b\u3000\u6df1\u5bab\u817e\u5251\u6c14\u3000\u6069\u4ec7\u96be\u89e3\u3000\u53e4\u6d1e\u7ed3\u5947\u7f18 \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377 \u7b2c\u4e09\u56de \u624b\u8db3\u76f8\u6b8b\u3000\u6df1\u5bab\u817e\u5251\u6c14 \u6069\u4ec7\u96be\u89e3\u3000\u53e4\u6d1e\u7ed3\u5947\u7f18 \u3000\u3000\u90d1\u8d35\u5983\u56b7\u9053\uff1a\u201c\u9b4f\u516c\u516c\uff0c\u4f60\u8fd9\u662f\u4ec0\u9ebd\u610f\u601d\uff1f\u201d\u9b4f\u5fe0\u8d24\u9762\u5b54\u4e00\u6273\uff0c\u53cc\u773c\u4e00\u7ffb\uff0c\u6084\u58f0\u8bf4\u9053\uff1a \u201c\u4f60\u4eec\u6bcd\u5b50\u5144\u59b9\uff0c\u5bc6\u8c0b\u7be1\u4f4d\uff0c\u6211\u9b4f\u5fe0\u8d24\u5fe0\u5fc3\u8d64\u80c6\uff0c\u7ef4\u62a4\u592a\u5e99\u5b97\u7960\uff0c\u4e0e\u4f60\u4eec\u5468\u65cb\uff0c\u65e0\u975e\u662f\u60f3\u5957 \u53d6\u4f60\u4eec\u7684\u5978\u8c0b\uff0c\u4f60\u5f53\u6211\u771f\u4f1a\u53c2\u4e0e\u4f60\u4eec\u7684\u9020\u53cd\u9ebd\uff1f\u201d\u90d1\u8d35\u5983\u7834\u53e3\u5927\u9a82\u3002\u592a\u5b50\u5e38\u6d1b\u5c06\u4fe1\u5c06\u7591\uff0c\u8f6c \u5ff5\u4e00\u60f3\uff0c\u8fd9\u9b4f\u5fe0\u8d24\u65b0\u8fd1\u5f97\u52bf\uff0c\u638c\u6709\u4e1c\u5382\uff0c\u7ba1\u4ed6\u662f\u771f\u662f\u5047\uff0c\u53ea\u8981\u73b0\u5728\u5e2e\u6211\u4fbf\u884c\uff0c\u6211\u53c8\u4f55\u5fc5\u82e6\u82e6 \u8ffd\u7a76\u3002\u5f53\u4e0b\u559d\u4ee4\u5c06\u90d1\u8d35\u5983\u5144\u59b9\u4e0e\u4e8c\u7687\u5b50\u5e38\u6d35\u7ed1\u4e2a\u7ed3\u5b9e\uff0c\u6b63\u60f3\u9000\u51fa\uff0c\u738b\u7167\u5e0c\u5ffd\u7136\u5927\u58f0\u558a\u9053\uff1a \u201c\u5b5f\u4f2f\u4f2f\uff0c\u6211\u6765\u4e86\uff01\u201d\u592a\u5b50\u970d\u7136\u9192\u8d77\uff0c\u5411\u90d1\u8d35\u5983\u559d\u95ee\uff1a\u201c\u4f60\u4eec\u5c06\u6211\u7684\u503c\u6bbf\u6b66\u5e08\u7ed1\u67b6\uff0c\u85cf\u5728\u90a3 \u513f\uff1f\u201d \u3000\u3000\u9b4f\u5fe0\u8d24\u773c\u8272\u4e00\u629b\uff0c\u4e1c\u5382\u7684\u4e00\u4e2a\u201c\u6869\u5934\u201d\u628a\u5c4b\u4e2d\u7684\u516b\u4ed9\u62ac\u731b\u7684\u6380\u8d77\uff0c\u5730\u4e0a\u73b0\u51fa\u4e00\u4e2a\u9ed1\u9edd\u9edd \u7684\u6d1e\u7a74\uff0c\u738b\u7167\u5e0c\u5174\u56db\u4e2a\u6869\u5934\u7eb5\u8eab\u4eba\u5185\uff0c\u884c\u4e86\u51e0\u6b65\uff0c\u53ea\u542c\u5f97\u91cc\u9762\u5927\u58f0\u547c\u559d\uff0c\u91d1\u94c1\u4ea4\u9e23\uff0c\u738b\u7167\u5e0c \u4ece\u516b\u5b9d\u56ca\u4e2d\u53d6\u51fa\u706b\u77f3\uff0c\u70b9\u8d77\u706b\u7ed2\uff0c\u4e0e\u4e1c\u5382\u7684\u56db\u4e2a\u5934\u76ee\u6025\u6b65\u5954\u524d\uff0c\u805a\u62e2\u76ee\u5149\uff0c\u53ea\u89c1\u4e00\u4e2a\u9b41\u68a7\u6c49 \u5b50\uff0c\u62ab\u67b7\u5e26\u9501\uff0c\u5c45\u7136\u8eab\u4f3c\u65cb\u98ce\u75be\u8f6c\uff0c\u821e\u52a8\u957f\u67b7\uff0c\u4e0e\u4e24\u4e2a\u770b\u5b88\u536b\u58eb\u6076\u6218\u3002\u8fd9\u4eba\u6b63\u662f\u4ed6\u7684\u5cb3\u7236\u5b5f \u707f\uff0c\u4ed6\u542c\u5f97\u5916\u9762\u6740\u58f0\u64bc\u5730\uff0c\u60c5\u77e5\u6709\u53d8\uff0c\u56e0\u6b64\u5f3a\u8fd0\u5185\u529b\uff0c\u6323\u65ad\u624b\u9563\uff0c\u5c31\u4ee5\u957f\u67b7\u4f5c\u4e3a\u5175\u5668\uff0c\u4e0e\u4e7e \u6e05\u5bab\u7684\u4e24\u540d\u536b\u58eb\u62da\u6597\u3002 \u3000\u3000\u90a3\u4e24\u540d\u770b\u5b88\u90fd\u662f\u536b\u58eb\u4e2d\u4e00\u7b49\u4e00\u7684\u597d\u624b\uff0c\u5b5f\u707f\u5403\u4e8f\u5728\u811a\u4e0a\u5e26\u7740\u6c89\u91cd\u7684\u94c1  \uff0c\u672a\u80fd\u6323\u8131\uff0c\u7eb5 \u8df3\u4e0d\u7075\uff0c\u4e00\u573a\u6076\u6597\uff0c\u867d\u7136\u628a\u4e24\u4e2a\u770b\u5b88\u6253\u5f97\u5934\u7834\u8840\u6d41\uff0c\u4f46\u81ea\u5df1\u4e5f\u53d7\u4e86\u4e03\u516b\u5904\u5200\u5251\u4e4b\u4f24\u3002\u56db\u540d\u6869 \u5934\u75be\u8dd1\u4eba\u5185\uff0c\u90a3\u4e24\u4e2a\u770b\u5b88\u5927\u559c\u56b7\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4eec\u5feb\u6765\u670d\u4f8d\u8fd9\u4e2a\u86ee\u5b50\uff01\u201d\u5374\u4e0d\u6599\uff0c\u8bf4\u65f6\u8fdf\u3001\u90a3 \u65f6\u5feb\uff0c\u56db\u540d\u4e1c\u5382\u5934\u76ee\uff0c\u4e24\u4e2a\u670d\u4f8d\u4e00\u4e2a\uff0c\u53cd\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6bb5\uff0c\u628a\u4e24\u4e2a\u770b\u5b88\u6740\u4e86\u3002 \u3000\u3000\u738b\u7167\u5e0c\u63d0\u5251\u4e0a\u524d\uff0c\u53ea\u89c1\u5cb3\u7236\u5df2\u4f3c\u8840\u4eba\u4e00\u6837\uff0c\u6025\u5fd9\u5c06\u4ed6\u6276\u51fa\u5730\u7a9f\uff0c\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u5cb3 \u7236\uff0c\u662f\u5c0f\u5a7f\u6765\u4e86\u3002\u201d\u5b5f\u707f\u9053\uff1a\u201c\u971e\u513f\u5462\u4e86\u4f60\u89c1\u8fc7\u6ca1\u6709\uff1f\u201d\u8bed\u58f0\u5fae\u5f31\uff0c\u8bf4\u5f97\u5f88\u662f\u5403\u529b\u3002\u738b\u7167\u5e0c \u9053\uff1a\u201c\u971e\u59b9\u4e5f\u5728\u5916\u9762\u3002\u201d\u5b5f\u707f\u7cbe\u795e\u4e00\u632f\uff0c\u6276\u7740\u738b\u7167\u5e0c\u7684\u80a9\u5934\u8d70\u51fa\u5730\u7a9f\u3002 \u3000\u3000\u5bab\u6bbf\u5185\u592a\u5b50\u5e38\u6d1b\u6b63\u4e0e\u5353\u4e00\u822a\u8bf4\u8bdd\uff0c\u5353\u4e00\u822a\u7684\u7956\u7236\u662f\u603b\u7763\uff0c\u7236\u4eb2\u662f\u4f8d\u90ce\uff0c\u4e00\u8bf4\u8d77\u6765\uff0c\u592a\u5b50 \u81ea\u7136\u77e5\u9053\u3002\u592a\u5b50\u9053\uff1a\u201c\u4f60\u7236\u4eb2\u7684\u51a4\u6789\u6211\u5fc5\u5b9a\u66ff\u4f60\u662d\u96ea\u3002\u201d\u5b5f\u79cb\u971e\u4e5f\u5df2\u8fdb\u5165\u6bbf\u5185\uff0c\u7ad9\u5728\u5353\u4e00\u822a \u8eab\u8fb9\uff0c\u5ffd\u89c1\u738b\u7167\u5e0c\u6276\u7740\u4e00\u4e2a\u8840\u7ea2\u7684\u4eba\u51fa\u6765\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u5b9a\u775b\u4e00\u770b\uff0c\u5374\u662f\u7236\u4eb2\uff0c\u4e0d\u7531\u5f97\u9b42\u98de\u9b44 \u6563\uff0c\u773c\u6cea\u8ff8\u6d41\uff0c\u8df3\u4e0a\u524d\u53bb\u3002\u5b5f\u707f\u9053\uff1a\u201c\u592a\u5b50\uff0c\u6055\u6211\u4e0d\u80fd\u4f3a\u5019\u4f60\u4e86\uff01\u201d\u5de6\u624b\u62c9\u7740\u5973\u513f\uff0c\u53f3\u624b\u62c9 \u7740\u5973\u5a7f\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c\u5ffd\u7136\u6709\u4e24\u540d\u4ece\u5916\u6bbf\u8d76\u6765\u7684\u9526\u8863\u536b\uff0c\u53d1\u51fa\u602a\u58f0\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u53cc\u53cc\u8e2a\u4e0a\uff0c\u9f50 \u5411\u738b\u7167\u5e0c\u6251\u53bb\uff0c\u738b\u7167\u5e0c\u8eab\u5b50\u4e00\u4ef0\uff0c\u5de6\u8098\u4e00\u649e\uff0c\u628a\u4e00\u540d\u536b\u58eb\u649e\u7ffb\uff0c\u63a5\u7740\u4e00\u638c\u5288\u51fa\uff0c\u53c8\u5c06\u7b2c\u4e8c\u540d \u536b\u58eb\u683c\u9000\u3002\u5b9a\u775b\u4e00\u770b\uff0c\u8fd9\u540d\u536b\u58eb\u6b63\u662f\u5728  \u897f\u8ffd\u8e2a\u81ea\u5df1\uff0c\u7ed9\u7389\u7f57\u5239\u5413\u9000\u7684\u9526\u8863\u536b\u6307\u6325\u77f3\u6d69\uff01 \u3000\u3000\u77f3\u6d69\u7d20\u6765\u81ea\u8d1f\uff0c\u7ed9\u738b\u7167\u5e0c\u4e00\u638c\u683c\u9000\uff0c\u632f\u81c2\u518d\u6251\u3002\u592a\u5b50\u559d\u9053\uff1a\u201c\u77f3\u6d69\uff0c\u4f11\u5f97\u80e1\u6765\uff01\u201d\u77f3\u6d69 \u9053\uff1a\u201c\u8fd9\u4eba\u662f  \u897f\u7684\u53db\u9006\uff01\u201d\u592a\u5b50\u5947\u9053\uff1a\u201c\u4ec0\u9ebd\uff0c\u4ed6\u662f\u53db\u9006\uff1f\u201d\u77f3\u6d69\u9053\uff1a\u201c\u4ed6\u5728  \u897f\u8bf3\u79f0\u662f \u5353\u603b\u7763\u7684\u4fdd\u9556\uff0c\u6211\u4eec\u6709\u773c\u65e0\u73e0\uff0c\u628a\u4ed6\u8f7b\u8f7b\u653e\u8fc7\u4e86\u3002\u4e0d\u6599\u5f8c\u6765\u5267\u76d7\u7389\u7f57\u5239\u7adf\u66ff\u4ed6\u51fa\u5934\uff0c\u6740\u4e86\u6211 \u4eec\u53c1\u4e2a\u9526\u8863\u536b\u3002\u201d\u9526\u8863\u536b\u5bf9\u5916\uff0c\u4e1c\u897f\u4e24\u5382\u7684\u536b\u58eb\u5bf9\u5185\uff0c\u5404\u4e0d\u7edf\u5c5e\u3002\u77f3\u6d69\u8fd9\u73ed\u4eba\u662f\u4ece\u5916\u5ef7\u592a\u548c \u95e8\u90a3\u8fb9\u95fb\u8baf\u8d76\u6765\u7684\uff0c\u4ed6\u4eec\u76f4\u5c5e\u7687\u5e1d\u3002\u6240\u4ee5\u82e5\u7136\u771f\u662f\u641c\u6355\u53db\u9006\uff0c\u592a\u5b50\u4e5f\u5236\u4ed6\u4e0d\u4f4f\u3002\u592a\u5b50\u9053\uff1a \u201c\u4ec0\u9ebd\u7389\u7f57\u5239\uff0c\u662f\u7537\u5f3a\u76d7\u8fd8\u662f\u5973\u5f3a\u76d7\uff1f\u201d\u77f3\u6d69\u9053\uff1a\u201c\u662f\u5f53\u4eca\u5929\u4e0b\u6700\u5389\u5bb3\u7684\u5973\u5f3a\u76d7\u3002\u5979\u66ff\u4ed6\u51fa \u5934\uff0c\u663e\u89c1\u662f\u6709\u5173\u7cfb\u3002\u201d\u8bf4\u7f62\u4f5c\u52bf\u6b32\u6251\uff0c\u738b\u7167\u5e0c\u5ffd\u7136\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5353\u603b\u7763\u7684\u5b59\u513f\u4fbf\u5728\u6b64\u5730\uff0c\u4f60 \u95ee\u95ee\u4ed6\u6211\u662f\u5426\u4ed6\u5bb6\u7684\u4fdd\u9556\uff1f\u201d\u5353\u4e00\u822a\u770b\u4e86\u738b\u7167\u5e0c\u4e00\u773c\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u7968\u6bbf\u4e0b\uff0c\u8fd9\u4f4d\u738b\u5144\u6b63\u662f \u6211\u5bb6\u7684\u4fdd\u9556\uff0c\u6240\u4ee5\u6211\u548c\u4ed6\u4e00\u9053\u8fdb\u5bab\uff0c\u52a9\u6bbf\u4e0b\u64d2\u83b7\u53db\u9006\u3002\u201d\u77f3\u6d69\u9053\uff1a\u201c\u90a3\u9ebd\u7389\u7f57\u5239\u4e3a\u4f55\u5e2e \u4f60\uff01\u201d\u76c2\u707f\u867d\u53d7\u91cd\u4f24\uff0c\u795e\u667a\u5c1a\u6e05\uff0c\u6025\u5411\u592a\u5b50\u53e9\u5934\u7980\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u6211\u7684\u5973\u5a7f\uff0c\u4ed6\u548c\u5c0f\u5973\u524d\u6765\u6551 \u6211\uff0c\u8bf7\u77f3\u6307\u6325\u4e0d\u8981\u51a4\u6789\u597d\u4eba\u3002\u201d\u5b5f\u79cb\u971e\u7ad9\u5728\u65c1\u8fb9\uff0c\u7236\u4eb2\u7684\u8bdd\u867d\u7136\u5fae\u5f31\uff0c\u5374\u662f\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c \u8eab\u5b50\u9661\u7136\u53d1\u70ed\uff0c\u4e5f\u4e0d\u77e5\u662f\u7f9e\u662f\u559c\uff0c\u5fc3\u513f\u535c\u901a\u901a\u7684\u8df3\u4e2a\u4e0d\u505c\u3002 \u3000\u3000\u5b5f\u707f\u8fd9\u51e0\u5e74\u6765\u505a\u6148\u5e86\u5bab\u7684\u503c\u6bbf\u6b66\u5e08\uff0c\u548c\u592a\u5b50\u751a\u4e3a\u76f8\u5f97\uff0c\u65e5\u524d\u90a3\u4e2a\u201c\u6883\u51fb\u6848\u201d\u7684\u51f6\u624b\uff0c\u53c8 \u662f\u4ed6\u62da\u6b7b\u64d2\u7740\uff0c\u800c\u4eca\u4e3a\u4e86\u592a\u5b50\uff0c\u4ed6\u53c8\u88ab\u90d1\u8d35\u5983\u7684\u624b\u4e0b\u6349\u53bb\u79c1\u5211\u62f7\u6253\uff0c\u5f04\u5f97\u53d8\u6210\u8840\u4eba\uff0c\u592a\u5b50\u5bf9 \u4ed6\u751a\u89c9\u6b49\u759a\uff0c\u542c\u4ed6\u4e00\u8bf4\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u77f3\u6307\u6325\uff0c\u5b5f\u6b66\u5e08\u548c\u5353\u516c\u5b50\u603b\u4e0d\u4f1a\u8bf4\u8c0e\uff0c\u4f60\u653e\u4e86\u4ed6 \u5427\uff01\u201d\u5b5f\u707f\u9053\uff1a\u201c\u90a3\u7389\u7f57\u5239\u65e2\u662f\u6700\u6700\u5389\u5bb3\u7684\u5973\u5f3a\u76d7\uff0c\u5979\u548c\u5b98\u9762\u7684\u4eba\u81ea\u7136\u662f\u4f5c\u5bf9\u7684\u4e86\u3002\u53ea\u6015\u5979 \u6709\u610f\u79bb\u95f4\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u201d\u77f3\u6d69\u788d\u65bc\u592a\u5b50\u7684\u9762\u5b50\uff0c\u800c\u4e14\u5b5f\u707f\u53c8\u662f\u4ed6\u7684\u524d\u8f88\uff0c\u5fc3\u91cc\u867d\u7136\u8fd8\u6709\u6000\u7591\uff0c \u4e5f\u53ea\u597d\u60bb\u60bb\u9000\u4e0b\u3002 \u3000\u3000\u592a\u5b50\u9053\uff1a\u201c\u5b5f\u6b66\u5e08\u8eab\u53d7\u91cd\u4f24\uff0c\u968f\u6211\u56de\u5bab\u8c03\u517b\u53bb\u5427\u3002\u5353\u516c\u5b50\u548c\u8fd9\u4f4d\u738b\u5144\uff0c\u4e5f\u8bf7\u4e00\u5e76\u8fdb \u5bab\u3002\u201d\u76c2\u707f\u9053\uff1a\u201c\u8c22\u6bbf\u4e0b\uff0c\u5974\u5a62\u4eca\u751f\u6050\u518d\u4e0d\u80fd\u4f3a\u5019\u4f60\u4e86\u3002\u8fd8\u662f\u8ba9\u5974\u5a62\u56de\u5bb6\uff0c\u6599\u7406\u5f8c\u4e8b\u5427\u3002\u201d \u592a\u5b50\u770b\u4ed6\u4f24\u52bf\uff0c\u77e5\u662f\u65e0\u671b\uff0c\u800c\u81ea\u5df1\u53c8\u6709\u5927\u4e8b\u6599\u7406\uff0c\u4e5f\u5c31\u4e0d\u518d\u5f3a\u4ed6\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u4f60\u5750 \u6211\u7684\u8f66\u56de\u53bb\u3002\u201d\u53eb\u4eba\u53d6\u4e86\u5927\u5185\u7684\u91d1\u521b\u5723\u836f\uff0c\u9001\u4ed6\u4eec\u56de\u5bb6\u3002 \u3000\u3000\u4e00\u8def\u4e0a\uff0c\u5b5f\u79cb\u971e\u5728\u9a6c\u8f66\u91cc\u6276\u7740\u7236\u4eb2\uff0c\u4e0d\u65f6\u5077\u77a7\u738b\u7167\u5e0c\u3002\u738b\u7167\u5e0c\u5374\u662f\u7709\u5934\u6df1\u9501\u3002\u5230\u4e86\u5bb6 \u4e2d\uff0c\u5929\u8272\u5df2\u5c06\u53d1\u767d\u3002\u9001\u4ed6\u4eec\u56de\u5bb6\u7684\u592a\u5b50\u968f\u4ece\uff0c\u7ed9\u76c2\u5bb6\u63ed\u4e86\u5c01\u6761\uff0c\u7559\u4e0b\u91d1\u521b\u5723\u836f\uff0c\u544a\u8f9e\u56de\u5bab\u3002 \u738b\u7167\u5e0c\u4e0e\u5b5f\u79cb\u971e\u628a\u76c2\u707f\u6276\u4eba\u5e05\u623f\uff0c\u6577\u4f24\u88f9\u521b\uff0c\u5fd9\u4e86\u4e00\u9635\uff0c\u5353\u4e00\u822a\u4e5f\u5728\u65c1\u5e2e\u5fd9\u3002\u5b5f\u707f\u7cbe\u795e\u7a0d\u89c1 \u597d\u8f6c\uff0c\u7a81\u7136\u7741\u5927\u4e86\u773c\uff0c\u6c14\u5598\u5401\u5401\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u9760\u8fd1\u4e00\u4e9b\uff0c\u6211\u6709\u6700\u79d8\u5bc6\u7684\u4e8b\u8981\u544a\u8bc9\u4f60\u4eec\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u4ee5\u4e3a\u662f\u4ed6\u5bb6\u79c1\u4e8b\uff0c\u6084\u6084\u9000\u51fa\u3002\u76c2\u707f\u5ffd\u7136\u62db\u62db\u624b\u9053\uff1a\u201c\u8fd9\u4f4d\u5353\u5144\u53ef\u662f\u7d2b\u9633\u9053\u957f\u7684\u9ad8 \u5f92\uff01\u201d\u738b\u7167\u5e0c\u70b9\u4e86\u70b9\u5934\u3002\u5b5f\u707f\u9053\uff1a\u201c\u6211\u548c\u5353\u5144\u867d\u662f\u521d\u4ea4\uff0c\u4eca\u5f8c\u4e5f\u5c06\u6c38\u522b\u3002\u4f46\u9002\u624d\u89c1\u5353\u5144\u5e87\u62a4 \u5c0f\u5a7f\uff0c\u9ad8\u4e49\u96be\u5fd8\u3002\u8fd9\u4e8b\u60c5\u6211\u4e5f\u4e0d\u60f3\u7792\u7740\u5353\u5144\uff0c\u800c\u4e14\u65e5\u5f8c\u6050\u6015\u4e5f\u8981\u5353\u5144\u52a9\u4e00\u81c2\u4e4b\u529b\u3002\u201d\u5353\u4e00\u822a \u884c\u5230\u95e8\u53e3\uff0c\u518d\u6298\u56de\u6765\u3002\u738b\u7167\u5e0c\u5012\u4e86\u4e00\u676f\u70ed\u8336\uff0c\u7ed9\u5b5f\u707f\u559d\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u5b5f\u4f2f\u4f2f\u4f60\u517b\u517b\u795e\u518d\u8bf4 \u5427\u3002\u201d\u5b5f\u707f\u53cc\u7738\u70af\u70af\uff0c\u6025\u58f0\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u4e0d\u8bf4\uff0c\u90a3\u5c31\u8fdf\u4e86\u3002\u8d24\u5a7f\uff0c\u6211\u77e5\u9053\u4f60\u7236\u5b50\u8fd1\u5e74\u5bf9\u6211\u4e0d \u6ee1\u3002\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u90a3\u91cc\u7684\u8bdd\u3002\u201d\u5b5f\u707f\u9053\uff1a\u201c\u6211\u5feb\u6b7b\u4e86\uff0c\u54b1\u4eec\u90fd\u8bf4\u5b9e\u8bdd\u3002\u6211\u77e5\u9053\u4f60\u4eec\u7236\u5b50\u4e0d \u6ee1\u610f\u6211\u4f5c\u671d\u5ef7\u7684\u5974\u624d\uff0c\u53ef\u662f\u4f60\u4eec\u77e5\u9053\u6211\u4e3a\u4f55\u8981\u5230\u6148\u5e86\u5bab\u53bb\u505a\u503c\u6bbf\u6b66\u5e08\u5417\uff1f\u201d \u3000\u3000\u5b5f\u707f\u9762\u5bb9\u8083\u7a46\uff0c\u8eab\u5b50\u6296\u98a4\uff0c\u5927\u5bb6\u90fd\u4e0d\u6562\u8bf4\u8bdd\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u5b5f\u707f\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u548c\u5180\u5317\u7684 \u7f57\u5927\u4fa0\u7f57\u91d1\u5cf0\u662f\u631a\u4ea4\uff0c\u4f60\u4eec\u662f\u77e5\u9053\u7684\u4e86\uff0c\u7f57\u91d1\u5cf0\u5728\u4e94\u5e74\u524d\u7a81\u906d\u6a2a\u6b7b\uff0c\u4f60\u4eec\u53ef\u77e5\u9053\u9ebd\uff1f\u201d\u738b\u7167 \u5e0c\u9053\uff1a\u201c\u542c\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u8bf4\u8fc7\u3002\u201d\u5b5f\u707f\u9053\uff1a\u201c\u7f57\u91d1\u5cf0\u809d\u80c6\u7167\u4eba\uff0c\u5fe0\u5fc3\u7231\u56fd\uff0c\u5e74\u524d\u5230\u5173\u5916\u523a\u63a2 \u654c\u60c5\uff0c\u5f97\u4e86\u4e00\u4efd\u7edd\u5bc6\u7684\u60c5\u62a5\u3002\u539f\u6765\u6ee1\u6d32\u9791\u5b50\u84c4\u610f\u5185\u4fb5\uff0c\u8fde\u5e74\u6765\u6d3e\u4eba\u5230\u5173\u5185\u6d3b\u52a8\uff0c\u7adf\u7136\u6536\u4e70\u4e86 \u4e00\u6279\u4eba\u66ff\u4ed6\u4f5c\u5185\u5e94\u3002\u5176\u4e2d\u6709\u7763\u629a\u5927\u5458\uff0c\u6709\u671d\u5ef7\u91cd\u81e3\uff0c\u4e5f\u6709\u6b66\u6797\u9ad8\u624b\u3002\u7f57\u91d1\u5cf0\u53ea\u63a2\u51fa\u4e24\u4e2a\u4eba\uff0c \u5176\u4e2d\u4e00\u4e2a\u8fd8\u4e0d\u77e5\u9053\u540d\u5b57\u3002\u201d\u5353\u4e00\u822a\u548c\u738b\u7167\u5e0c\u4e49\u6124\u586b\u80f8\uff0c\u9f50\u58f0\u95ee\u9053\uff1a\u201c\u662f\u90a3\u4e24\u4e2a\uff1f\u201d\u76c2\u707f\u9053\uff1a \u201c\u4e00\u4e2a\u662f\u5ddd\u8fb9\u7684\u5e94\u4fee\u9633\u3002\u201d\u738b\u7167\u5e0c\u201c\u554a\u201d\u4e86\u4e00\u58f0\u3002\u5b5f\u707f\u9053\uff1a\u201c\u5e94\u4fee\u9633\u884c\u8e2a\u8be1\u79d8\uff0c\u5341\u5e74\u6765\u65e0\u4eba \u77e5\u9053\u4ed6\u7684\u4e0b\u843d\u3002\u53e6\u4e00\u4e2a\u5374\u662f\u5927\u5185\u9ad8\u624b\uff0c\u4f46\u5374\u4e0d\u77e5\u662f\u9526\u8863\u536b\u7684\u8fd8\u662f\u4e1c\u897f\u5382\u7684\uff1f\u636e\u8bf4\u82e5\u5e72\u91cd\u81e3\u7763 \u629a\u548c\u4ed6\u90fd\u6709\u8054\u7edc\u3002\u6240\u4ee5\u8fd9\u4eba\u6bd4\u5e94\u4fee\u9633\u8fd8\u66f4\u91cd\u8981\u3002\u7f57\u91d1\u5cf0\u77e5\u9053\u8fd9\u4e2a\u79d8\u5bc6\uff0c\u521a\u521a\u56de\u5230\u5173\u5185\uff0c\u5c31\u7ed9 \u4eba\u5bb3\u6b7b\u4e86\u3002\u4e34\u6b7b\u65f6\u4ed6\u5bf9\u6211\u8bf4\u51fa\u79d8\u5bc6\uff0c\u5230\u6148\u5e86\u5bab\u53bb\u505a\u503c\u6bbf\u6b66\u5e08\u4e5f\u662f\u4ed6\u7684\u4e3b\u610f\u3002\u201d\u738b\u7167\u5e0c\u8fd9\u624d\u604d \u7136\u5927\u609f\uff0c\u539f\u6765\u5cb3\u7236\u8fdb\u5bab\uff0c\u7528\u610f\u662f\u5c31\u8fd1\u4fa6\u67e5\u3002\u5b5f\u707f\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u53ef\u60dc\u6211\u5728\u5bab\u4e2d\u4e94\u5e74\uff0c\u4e00\u70b9\u7ebf\u7d22 \u90fd\u5f97\u4e0d\u5230\u3002\u201d\u6b47\u4e86\u4e00\u9635\uff0c\u53c8\u9053\uff1a\u201c\u5bab\u4e2d\u6697\u6597\u751a\u70c8\uff0c\u592a\u5b50\u8fd9\u4eba\uff0c\u867d\u7136\u6bd4\u4ed6\u7236\u4eb2\u7cbe\u660e\uff0c\u4e5f\u6709\u5fc3\u52b1 \u7cbe\u56fe\u6cbb\uff0c\u53ea\u6050\u4e5f\u672a\u5fc5\u80fd\u9003\u6697\u7b97\u5462\uff01\u6211\u4e0d\u60f3\u4f60\u4eec\u4e5f\u8fdb\u5bab\u5f53\u5dee\uff0c\u53ea\u613f\u4f60\u4eec\u8bb0\u7740\u5e94\u4fee\u9633\u8fd9\u4e2a\u540d \u5b87\u3002\u201d \u3000\u3000\u5b5f\u707f\u4e00\u53e3\u6c14\u8bf4\u5b8c\uff0c\u6c14\u5598\u66f4\u751a\uff0c\u5b5f\u79cb\u971e\u7ed9\u4ed6\u8f7b\u8f7b  \u80cc\u3002\u5b5f\u707f\u5ffd\u9053\uff1a\u201c\u767d\u654f\u5462\uff1f\u201d\u5b5f\u79cb\u971e \u9053\uff1a\u201c\u4ed6\u5728\u67f3\u53d4\u53d4\u5bb6\u91cc\u3002\u662f\u738b\u54e5\u54e5\u6551\u6211\u4eec\u51fa\u6765\uff0c\u5e26\u6211\u4eec\u53bb\u7684\u3002\u201d\u738b\u7167\u5e0c\u5fc3\u9053\uff1a\u201c\u8fd9\u767d\u654f\u539f\u6765 \u662f\u4ed6\u5fc3\u7231\u7684\u5f92\u513f\uff0c\u602a\u4e0d\u5f97\u79cb\u846d\u548c\u4ed6\u90a3\u9ebd\u4eb2\u70ed\u3002\u201d\u4e0d\u89c9\u53c8\u6709\u4e9b\u9178\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u5b5f\u4f2f\u4f2f\uff0c\u4f60\u60e6\u767d \u654f\uff0c\u6211\u7ed9\u4f60\u628a\u4ed6\u53eb\u56de\u6765\u3002\u201d\u5b5f\u707f\u60e8\u7b11\u9053\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u6765\u4e0d\u53ca\u4e86\uff01\u54a6\uff01\u7167\u5e0c\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u5c3d\u53eb\u6211 \u505a\u201c\u4f2f\u4f2f\u201d\uff1f\u6211\u53bb\u4e16\u5f8c\uff0c\u4f60\u548c\u79cb\u971e\u8981\u76f8\u4eb2\u76f8\u7231\uff0c\u6211\u89c1\u5f97\u7740\u4f60\u4eec\uff0c\u6211\u5fc3\u91cc\u5f88\u9ad8\u5174\uff0c\u5f88\u9ad8 \u5174\u2026\u2026\u201d\u8bdd\u58f0\u65ad\u65ad\u7ee7\u7ee7\uff0c\u8d8a\u8bf4\u8d9f\u5f31\uff0c\u8fd8\u672a\u8bf4\u5b8c\uff0c\u53cc\u817f\u4e00\u4f38\uff0c\u6c14\u606f\u5df2\u65ad\uff01 \u3000\u3000\u5b5f\u79cb\u971e\u53f7\u5555\u5927\u54ed\uff0c\u738b\u7167\u5e0c\u8dea\u4e0b\u53e9\u4e86\u51e0\u4e2a\u54cd\u5934\uff0c\u9053\uff1a\u201c\u6211\u8bf7\u67f3\u4f2f\u4f2f\u66ff\u4f60\u4e3b\u6301\u846c\u4e8b\uff0c\u8fd8\u6709\u4f60 \u7684\u767d\u654f\u54e5\u54e5\u3002\u201d\u5b5f\u79cb\u971e\u5e26\u6cea\u95ee\u9053\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u4e0d\u66ff\u6211\u4e3b\u6301\u5417\uff1f\u4f55\u5fc5\u52b3\u70e6\u5916\u4eba\uff1f\u201d\u738b\u7167\u5e0c\u9053\uff1a \u201c\u6211\uff0c\u6211\u2026\u2026\u201d\u6b32\u8a00\u53c8\u6b62\uff0c\u6b63\u5728\u6b64\u65f6\uff0c\u5916\u8fb9\u5ffd\u7136\u6709\u4eba\u53eb\u95e8\u3002\u5353\u4e00\u822a\u4e0b\u697c\u5f00\u4e86\u5927\u95e8\uff0c\u5374\u539f\u6765\u662f \u592a\u5b50\u5dee\u6765\u7684\u4eba\u3002 \u3000\u3000\u592a\u5b50\u5dee\u4eba\u6765\u63a2\u95ee\u5b5f\u707f\uff0c\u77e5\u9053\u5669\u8017\uff0c\u65e0\u9650\u60cb\u60dc\u3002\u53e6\u5916\u5dee\u4eba\u8fd8\u5e26\u6765\u4e86\u592a\u5b50\u7684\u9080\u8bf7\uff0c\u8bf7\u5353\u4e00\u822a \u5230\u6148\u5e86\u5bab\u4f5c\u5ba2\u3002\u5353\u4e00\u822a\u63a5\u4e86\u8bf7\u5e16\uff0c\u8bf7\u592a\u5b50\u7684\u968f\u4ece\u5728\u5ba2\u5385\u7a0d\u5019\uff0c\u81ea\u5df1\u8fdb\u5185\u66f4\u8863\uff0c\u5e76\u548c\u738b\u7167\u5e0c\u9053 \u522b\u3002 \u3000\u3000\u738b\u7167\u5e0c\u8bbe\u4e86\u5cb3\u7236\u7684\u7075\u4f4d\uff0c\u966a\u5353\u4e00\u822a\u8f9e\u7075\u4e4b\u5f8c\uff0c\u5ffd\u7136\u628a\u4ed6\u62c9\u4eba\u5185\u5ba4\uff0c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u5353\u5144\uff0c \u592a\u5b50\u53ec\u4f60\uff0c\u5c06\u6709\u91cd\u7528\uff0c\u4f46\u6211\u529d\u4f60\u8fd8\u662f\u4e0d\u8981\u505a\u5b98\u7684\u597d\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u4e27\u670d\u672a\u6ee1\uff0c\u90a3\u4f1a\u4e3a \u5b98\uff1f\u201d\u539f\u6765\u4ed6\u4eec\u8bb2\u7a76\u53e4\u793c\u7684\u5b98\u5bb6\u5b50\u5f1f\uff0c\u5b88\u5b5d\u8981\u5b88\u53c1\u5e74\uff0c\u5728\u8fd9\u53c1\u5e74\u5185\u975e\u4f46\u4e0d\u80fd\u51fa\u4ed5\uff0c\u8fde\u7ed3\u5a5a\u4f5c \u4e50\u4e5f\u4e0d\u53ef\u4ee5\u3002\u738b\u7167\u5e0c\u53c8\u9053\uff1a\u201c\u90a3\u9ebd\u5353\u5144\u662f\u5426\u8981\u643a\u4ee4\u5c0a\u91d1\u9aa8\uff0c\u56de  \u897f\u539f\u7c4d\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6b63 \u60f3\u5982\u6b64\uff0c\u4f46\u53ea\u6015\u4e07\u91cc\u8fe2\u8fe2\uff0c\u4e0d\u77e5\u80fd\u5426\u62a4\u5148\u7236\u9057\u9ab8\uff0c\u5f52\u846c\u6545\u56ed\u5462\u3002\u201d\u738b\u7167\u5e0c\u5ffd\u9053\uff1a\u201c\u51ed\u5353\u5144\u7684 \u672c\u9886\uff0c\u4f55\u5904\u4e0d\u53ef\u901a\u884c\u3002\u4f46\u8bf7\u4f60\u63d0\u9632\u4e00\u4e2a\u4eba\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u8c01\uff1f\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u7389\u7f57\u5239\uff01\u201d \u5353\u4e00\u822a\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u5979\u548c\u4f60\u4eec\u6b66\u5f53\u6d3e\u7ed3\u6709\u6881\u5b50\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u600e\u9ebd\u6211\u672a \u542c\u540c\u95e8\u8bf4\u8fc7\uff1f\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u8fd9\u662f\u6700\u8fd1\u7684\u4e8b\u60c5\u3002\u201d\u5f53\u4e0b\u5c06\u7389\u7f57\u5239\u52ab\u4ed6\u7956\u7236\uff0c\u8fb1\u4ed6\u5e08\u5144\u7684\u4e8b\u8bf4 \u4e86\u3002\u5353\u4e00\u822a\u6012\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u72e0\u5fc3\u8fa3\u624b\u7684\u8d3c\u5a46\u5a18\uff01\u201d\u738b\u7167\u5e0c\u7709\u5934\u4e00\u76b1\uff0c\u4ed6\u6599\u4e0d\u5230\u5353\u4e00\u822a\u5b98\u5bb6\u5b50 \u5f1f\u7684\u6c14\u5473\u7adf\u5982\u6b64\u6d53\uff0c\u53e3\u53e3\u58f0\u58f0\u9a82\u7389\u7f57\u5239\u505a\u201c\u8d3c\u5a46\u5a18\u201d\uff0c\u4ed6\u81ea\u5df2\u662f\u7eff\u6797\u5927\u8c6a\u4e4b\u5b50\uff0c\u5fc3\u4e2d\u672a\u514d\u4e0d \u5feb\u3002\u5f53\u4e0b\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7389\u7f57\u5239\u624b\u5e95\u4e4b\u8fa3\uff0c\u786e\u662f\u7f55\u89c1\u7f55\u95fb\u3002\u4f46\u5979\u5dfe\u5e3c\u987b\u7709\uff0c\u5374\u4e5f\u662f\u6b66\u6797\u4e2d\u767e\u4e16 \u96be\u9022\u7684\u5947\u5973\u3002\u201d\u5353\u4e00\u822a\u6de1\u7136\u8bf4\u9053\uff1a\u201c\u662f\u5417\uff1f\u82e5\u6709\u673a\u4f1a\u6211\u4e5f\u60f3\u89c1\u5979\u4e00\u89c1\u3002\u201d\u738b\u7167\u5e0c\u9661\u7136\u4e00\u9707\uff0c \u4ed6\u5230\u5e95\u53d7\u8fc7\u5353\u4e00\u822a\u5e87\u62a4\u4e4b\u6069\uff0c\u5982\u4f55\u80fd\u773c\u7741\u7741\u770b\u4ed6\u9001\u6b7b\u3002\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u5353\u5144\uff0c\u6211\u529d\u4f60\u8fd8\u662f\u4e0d\u8981 \u78b0\u5979\u4e3a\u5999\u3002\u4f60\u662f\u5343\u91d1\u4e4b\u4f53\uff0c\u82e5\u51fa\u4e86\u4ec0\u9ebd\u4e8b\u60c5\uff0c\u6211\u7684\u7f6a\u53ef\u66f4\u5927\u4e86\u3002\u201d\u5353\u4e00\u822a\u867d\u7136\u5fc3\u4e5f\u4e0d\u5feb\uff0c\u4f46 \u89c1\u4ed6\u8bf4\u5f97\u6781\u4e3a\u8bda\u631a\uff0c\u4fbf\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4e0d\u89c1\u5979\u4e5f\u7f62\u3002\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u662f\u554a\uff0c\u5353\u5144\u6b66\u827a\u867d \u9ad8\uff0c\u4e5f\u72af\u4e0d\u7740\u548c\u5979\u4f5c\u5bf9\u3002\u4f55\u51b5\u5353\u5144\u82e5\u56de\u539f\u7c4d\uff0c\u5f53\u7136\u662f\u53d6\u9053\u4eba\u540c\uff0c\u7ecf\u5c71\u897f\u56de\u9655\u5317\u7684\u4e86\u3002\u53ea\u8981\u4e0d \u5230\u9655\u5357\uff0c\u5c31\u53ef\u907f\u8fc7\u7389\u7f57\u5239\u4e86\u3002\u201d\u5353\u4e00\u822a\u9053\u8c22\u4e86\u4ed6\u5173\u6ce8\u4e4b\u60c5\uff0c\u62f1\u624b\u9053\u522b\u3002\u738b\u7167\u5e0c\u5ffd\u7136\u5728\u4ed6\u8033\u8fb9 \u8bf4\u9053\uff1a\u201c\u5353\u5144\u56de\u5bb6\u4e4b\u5f8c\uff0c\u82e5\u7136\u6709\u4e8b\uff0c\u8bf7\u5230\u5ef6\u5b89\u5e9c\u6765\u627e\u5c0f\u5f1f\u3002\u53ea\u8981\u8bf4\u51fa\u5c0f\u5f1f\u8d31\u540d\uff0c\u5b9a\u6709\u6c5f\u6e56\u540c \u9053\u7ed9\u4f60\u6307\u5f15\u3002\u201d\u5353\u4e00\u822a\u6027\u60c5\u78ca\u843d\u6566\u539a\uff0c\u53ea\u89c9\u6b64\u4eba\u9887\u4e3a\u8be1\u79d8\uff0c\u5374\u6599\u4e0d\u5230\u4ed6\u4fbf\u662f\u9655\u5317\u7eff\u6797\u9886\u8896\u7684 \u513f\u5b50\u3002 \u3000\u3000\u5f53\u4e0b\u5353\u4e00\u822a\u5e94\u4e86\u4e00\u58f0\uff0c\u4e5f\u4e0d\u95ee\u4ed6\u5728\u5ef6\u5b89\u5e9c\u7684\u4f4f\u5740\u3002\u4e24\u4eba\u6325\u624b\u9053\u522b\u3002\u5353\u4e00\u822a\u4e58\u4e86\u592a\u5b50\u6765\u63a5 \u7684\u9a6c\u8f66\uff0c\u76f4\u5165\u4e1c\u5bab\u3002\u968f\u4ece\u628a\u4ed6\u5b89\u7f6e\u5728\u5bbe\u9986\u7a0d\u5019\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u8fdb\u6765\u53eb\u9053\uff1a\u201c\u592a\u5b50\u8bf7\uff01\u201d\u5353\u4e00\u822a \u968f\u4f8d\u4ece\u8d70\u8fc7\u66f2\u66f2\u6298\u6298\u7684\u56de\u5eca\uff0c\u5230\u4e86\u4e00\u5904\u7528\u767d\u77f3\u680f\u6746\u56f4\u6210\u7684\u5ead\u9662\uff0c\u5ead\u9662\u4e2d\u6709\u51e0\u4e2a\u6b66\u58eb\u5728\u90a3\u91cc\u8868 \u6f14\u6b66\u529f\uff0c\u5ead\u9662\u5bf9\u7740\u4e00\u5ea7\u5f69\u697c\uff0c\u592a\u5b50\u5c31\u5728\u5f69\u697c\u4e2d\u996e\u9152\u770b\u6280\u3002\u4f8d\u4ece\u628a\u5353\u4e00\u822a\u5e26\u4e0a\u5f69\u697c\uff0c\u884c\u8fc7\u793c \u5f8c\uff0c\u592a\u5b50\u8d50\u4ed6\u5e73\u8eab\uff0c\u53eb\u4eba\u7aef\u4e00\u5f20\u51f3\u5b50\u7ed9\u4ed6\uff0c\u5c31\u53eb\u4ed6\u5750\u5230\u4fa7\u65c1\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u7ecf\u8fc7\u6628\u665a\u7684\u7eb7 \u6270\uff0c\u5927\u529f\u603b\u7b97\u544a\u6210\uff0c\u5916\u6709\u5ef7\u81e3\uff0c\u5185\u6709\u5b97\u5ba4\uff0c\u8fd8\u6709\u714c\u714c\u7956\u8bad\uff0c\u4e0d\u6015\u7236\u7687\u4e0d\u60e9\u6cbb\u4ed6\u4eec\u3002\u4f60\u4e5f\u8f9b\u82e6 \u4e86\uff0c\u54b1\u4eec\u4e14\u996e\u9152\u770b\u6280\u3002\u201d\u539f\u6765\u660e\u592a\u7956\u6731\u5143\u748b\u7acb\u56fd\u4e4b\u5f8c\uff0c\u5b9a\u4e0b\u5c01\u5efa\u5236\u5ea6\uff0c\u628a\u5b50\u5b59\u5c01\u4e3a\u85e9\u738b\uff0c\u5bf9 \u9632\u6b62\u85e9\u738b\u8c0b\u53db\uff0c\u5f02\u5e38\u4e25\u5bc6\uff0c\u4f8b\u5982\u82e5\u4e0d\u5949\u8bcf\uff0c\u85e9\u738b\u4e0d\u8bb8\u4eba\u4eac\uff0c\u5373\u5728\u85e9\u5730\uff0c\u51fa\u57ce\u626b\u5893\uff0c\u4e5f\u5fc5\u987b\u594f \u8bf7\uff0c\u85e9\u738b\u4e4b\u95f4\uff0c\u4e0d\u8bb8\u5f80\u6765\uff0c\u66f4\u4e0d\u5f97\u5e72\u9884\u671d\u653f\uff0c\u4e00\u72af\u7981\u4ee4\uff0c\u7acb\u5373\u524a\u7235\u8d2c\u4e3a\u5eb6\u4eba\uff0c\u9001\u51e4\u9633\u5e9c\u9ad8\u5899 \u201c\u7262\u72f1\u201d\u6c38\u8fdc\u7981\u9522\u3002\u8fd9\u4e9b\u4e25\u5bc6\u7684\u89c4\u5b9a\uff0c\u4fbf\u662f\u592a\u5b50\u6240\u8bf4\u7684\u7956\u8bad\u3002\u660e\u795e\u5b97\u6731\u7fca\u94a7\u867d\u7136\u5ba0\u7231\u90d1\u8d35\u5983 \u6bcd\u5b50\uff0c\u4f46\u8fd9\u6b21\u5e38\u6d35\u79c1\u81ea\u5165\u4eac\uff0c\u72af\u4e86\u7956\u8bad\uff0c\u5373\u4f7f\u67e5\u4e0d\u51fa\u53db\u9006\u5b9e\u636e\uff0c\u8fd9\u5927\u7f6a\u4e5f\u96be\u9003\u4e86\u3002\u52a0\u4ee5\u671d\u91ce \u7684\u5927\u81e3\u540d\u6d41\u5982\u987e\u5baa\u6210\uff0e\u7533\u65f6\u884c\uff0e\u738b\u9521\u7235\u3001\u738b\u5bb6\u5c4f\u7b49\u90fd\u662f\u62e5\u7acb\u592a\u5b50\u7684\u4eba\uff0c\u5c24\u5176\u662f\u987e\u5baa\u6210\uff0c\u5728\u4e07 \u5386\u5eff\u4e8c\u5e74\u65f6\uff0c\u5c31\u56e0\u7acb\u55e3\u4e4b\u4e89\uff0c\u8f9e\u5b98\u5f52\u91cc\uff0c\u5728\u65e0\u9521\u4e1c\u6797\u4e66\u9662\u8bb2\u5b66\uff0c\u4e00\u65f6\u5929\u4e0b\u666f\u4ece\uff0c\u540d\u58eb\u6e05\u6d41\uff0c \u7ec4\u6210\u4e86\u4e1c\u6797\u515a\u3002\u867d\u7136\u5728\u91ce\uff0c\u5f71\u54cd\u6781\u5927\u3002\u987e\u5baa\u6210\u662f\u62e5\u7acb\u592a\u5b50\u7684\u4eba\uff0c\u660e\u795e\u5b97\u867d\u504f\u7231\u5eb6\u5b50\uff0c\u4e5f\u6709\u987e \u5fcc\uff0c\u9b4f\u5fe0\u8d24\u8d77\u521d\u89c1\u90d1\u8d35\u5983\u6bcd\u5b50\u5f97\u5ba0\uff0c\u56e0\u6b64\u4e92\u76f8\u5229\u7528\uff0c\u85c9\u90d1\u8d35\u5983\u4e4b\u529b\u593a\u53d6\u4e1c\u5382\uff0c\u5f8c\u6765\u4e00\u770b\u5185\u5916 \u5f62\u52bf\uff0c\u5bf9\u90d1\u8d35\u5983\u4e0d\u5229\uff0c\u65bc\u662f\u53c8\u6ca1\u5f52\u592a\u5b50\uff0c\u66f4\u589e\u52a0\u4e86\u592a\u5b50\u7684\u4f18\u52bf\u3002\u56e0\u6b64\u592a\u5b50\u624d\u6d0b\u6d0b\u81ea\u5f97\u7684\u5bf9\u5353 \u4e00\u822a\u8bf4\u51fa\u90a3\u4e00\u756a\u8bdd\u3002 \u3000\u3000\u5353\u4e00\u822a\u542c\u4e86\u8fd9\u4e00\u756a\u8bdd\uff0c\u609a\u7136\u6709\u611f\uff0c\u5fc3\u60f3\uff1a\u4e8c\u7687\u5b50\u867d\u7136\u4e0d\u8096\uff0c\u4f46\u5144\u5f1f\u9aa8\u8089\u4e4b\u95f4\u603b\u4e0d\u5fc5\u5982\u6b64 \u731c\u7591\u5fcc\u514b\u3002\u592a\u5b50\u628a\u60f3\u8c0b\u53db\u7684\u5f1f\u5f1f\u6349\u4e86\uff0c\u672c\u662f\u5e94\u8be5\uff0c\u4f46\u8fd9\u6837\u5e78\u707e\u4e50\u6da1\uff0c\u5374\u975e\u4eba\u541b\u7684\u98ce\u5ea6\uff0c\u4e0d\u89c9 \u60f3\u8d77\u4e86\u201c\u5de6\u4f20\u201d\u91cc\u201c\u90d1\u4f2f\u514b\u6bb5\u4e8e\u9122\u201d\u90a3\u6bb5\u6587\u7ae0\u3002\u90a3\u91cc\u8bb0\u8f7d\u7684\u90d1\u56fd\u4e24\u4e2a\u7687\u5b50\uff0c\u4e5f\u50cf\u4eca\u65e5\u7684\u592a\u5b50 \u4e0e\u4e8c\u7687\u5b50\u4e00\u6837\uff0c\u4e3a\u4e86\u4e89\u4f4d\uff0c\u54e5\u54e5\u628a\u5f1f\u5f1f\u6349\u4e86\u3002\u90a3\u4e2a\u5f1f\u5f1f\u201c\u5171\u53d4\u6bb5\u201d\u6bd4\u4eca\u65e5\u7684\u4e8c\u7687\u5b50\u5e38\u6d35\u8fd8\u8981 \u80e1\u4f5c\u975e\u4e3a\uff0c\u800c\u90d1\u5e84\u516c\u5219\u8981\u6bd4\u592a\u5b50\u5e38\u6d1b\u5bbd\u539a\u3002\u4f46\u201c\u5de6\u4f20\u201d\u8fd8\u662f\u8ba5\u8bbd\u90d1\u4f2f\u4ee5\u673a\u8c0b\u65bd\u65bc\u9aa8\u8089\u3002\u5353\u4e00 \u822a\u6697\u6697\u5fc3\u5bd2\uff0c\u53c8\u60f3\u8d77\u76c2\u707f\u4e3a\u592a\u5b50\u800c\u6b7b\u201c\u800c\u592a\u5b50\u542c\u5230\u6b7b\u8baf\uff0c\u5374\u4e00\u70b9\u4e5f\u4e0d\u54c0\u60bc\uff0c\u4e0d\u89c9\u628a\u6ca1\u9760\u7684\u610f \u601d\u6d88\u53bb\u4e00\u534a\u3002 \u3000\u3000\u592a\u5b50\u89c1\u4ed6\u60a0\u7136\u82e5\u6709\u6240\u601d\uff0c\u4e3e\u676f\u7b11\u9053\uff1a\u201c\u4f60\u4e14\u770b\u6211\u95e8\u4e0b\u536b\u58eb\u7684\u8f7b\u529f\u5999\u6280\uff01\u201d\u5353\u4e00\u822a\u4e3e\u5934\u89c2 \u770b\uff0c\u53ea\u89c1\u5ead\u9662\u4e2d\u56db\u4e2a\u6c49\u5b50\uff0c\u80a9\u5934\u4e0a\u5404\u9876\u7740\u4e00\u679d\u957f\u957f\u7684\u7af9\u7aff\u3002 \u3000\u3000\u6bcf\u6839\u7af9\u7aff\u4e0a\u6500\u4e00\u4e2a\u5c11\u5e74\uff0c\u5de6\u624b\u63e1\u7aff\uff0c\u53f3\u624b\u6267\u5251\uff0c\u56db\u540d\u5927\u6c49\u80a9\u5934\u9876\u7af9\u7aff\u7ed5\u573a\u75be\u8d70\uff0c\u7af9\u7aff\u4e0a \u7684\u5c11\u5e74\u4f5c\u51fa\u79cd\u79cd\u59ff\u52bf\uff0c\u6216\u4f5c\u201c\u5012\u6302\u73e0  \u201d\uff0c\u6216\u4f5c\u201c\u5e73\u4f38\u96c1\u7fc5\u201d\uff0c\u6216\u4ee5\u8db3\u94a9\u7aff\uff0c\u6216\u4ee5\u6307\u5b9a\u7aff\uff0c \u59ff\u52bf\u5341\u5206\u7f8e\u5999\u3002\u5353\u4e00\u822a\u5e38\u5728\u5929\u6865\u770b\u800d\u6742\u6280\uff0c\u6742\u6280\u4e2d\u867d\u4e5f\u6709\u8fd9\u6837\u8282\u76ee\uff0c\u4f46\u6500\u9644\u7740\u7af9\u7aff\u6f14\u6280\u7684 \u4eba\uff0c\u5374\u8fdc\u6ca1\u6709\u8fd9\u9ebd\u7075\u6d3b\u3002\u56db\u540d\u5927\u6c49\u62b1\u7740\u53cc\u624b\uff0c\u5728\u573a\u4e2d\u7a7f\u82b1\u8774\u8776\u4f3c\u7684\u5de6\u7a7f\u53f3\u63d2\uff0c\u80a9\u9876\u7740\u7684\u7af9\u7aff \u98a4\u52a8\u4e0d\u4f11\uff0c\u5f2f\u4e0b\u4e86\u4e00\u5927\u622a\uff0c\u4f46\u7af9\u7aff\u4e0a\u7684\u5c11\u5e74\u5374\u662f\u563b\u7b11\u73a9\u800d\uff0c\u597d\u4f3c\u7a33\u5982\u6cf0\u5c71\u3002\u5353\u4e00\u822a\u9053\u58f0 \u201c\u597d\uff01\u201d\u592a\u5b50\u5fae\u7b11\u9053\uff1a\u201c\u8fd9\u7b97\u4e0d\u4e86\u4ec0\u9ebd\u3002\u201d\u4e00\u51fb\u638c\uff0c\u56db\u540d\u5927\u6c49\u5de6\u7a7f\u53f3\u63d2\uff0c\u4e0a\u9762\u56db\u4e2a\u5c11\u5e74\u4e5f\u662f \u4e1c\u4e00\u5251\u897f\u4e00\u5251\uff0c\u4ea4\u4e92\u6df7\u6218\uff0c\u771f\u662f\u6781\u5c3d\u9f99\u86c7\u884d\u66fc\u7684\u5947\u89c2\u3002\u5353\u4e00\u822a\u7ec6\u770b\u65f6\uff0c\u53ea\u89c1\u56db\u4e2a\u5c11\u5e74\uff0c\u867d\u662f \u6df7\u4e71\u523a\u51fb\uff0c\u5e76\u65e0\u56fa\u5b9a\u5bf9\u624b\uff0c\u4f46\u5374\u9887\u6709\u6cd5\u5ea6\u3002\u4e0d\u7981\u9f13\u638c\u79f0\u5999\u3002\u8fd9\u56db\u4e2a\u5c11\u5e74\u7684\u8f7b\u529f\u9020\u8be3\uff0c\u5df2\u975e\u5bfb \u5e38\u53ef\u6bd4\uff0c\u4e0d\u80fd\u4ee5\u7b49\u95f2\u800d\u6742\u6280\u7684\u4eba\u89c6\u4e4b\u4e86\u3002 \u3000\u3000\u592a\u5b50\u53c8\u51fb\u4e86\u51fb\u638c\uff0c\u536b\u58eb\u73ed\u4e2d\u84e6\u5730\u8d70\u51fa\u4e00\u4e2a\u4e94\u5341\u9980\u5c81\uff0c\u7d2b\u819b\u9762\uff0e\u5c71\u7f8a\u987b\u7684\u6c49\u5b50\uff0c\u624b\u4e0a\u4e5f\u62ff \u7740\u4e00\u6839\u7af9\u7aff\uff0c\u8d70\u5230\u573a\u5fc3\uff0c\u628a\u7af9\u7aff\u6298\u4e3a\u4e24\u6bb5\uff0c\u5728\u5ead\u4e2d\u4e00\u7ad6\uff0c\u8eab\u5b50\u817e\u8d77\uff0c\u53cc\u8db3\u70b9\u7740\u90a3\u4e24\u6839\u7af9\u7aff\uff0c \u8eab\u5f62\u6643\u4e86\u51e0\u6643\uff0c\u4fbf\u5b9a\u4e86\u4e0b\u6765\u3002\u8981\u77e5\u7af9\u7aff\u7ad6\u5728\u5730\u4e0a\u5df2\u96be\uff0c\u800c\u652f\u6301\u4e00\u4e2a\u4eba\u7684\u91cd\u91cf\u66f4\u96be\u3002\u8fd9\u4eba\u975e\u4f46 \u8f7b\u529f\u9ad8\u5999\uff0c\u529b\u5ea6\u4e5f\u7528\u5f97\u6070\u5230\u597d\u5904\uff0c\u624d\u80fd\u7a33\u4f4f\u91cd\u5fc3\u3002\u8fd9\u4eba\u7ad9\u7a33\u4e4b\u5f8c\uff0c\u53eb\u9053\uff1a\u201c\u6765\u5427\uff01\u201d\u90a3\u56db\u540d \u6c49\u5b50\uff0c\u80a9\u5934\u4e0a\u9876\u7740\u7af9\u7aff\uff0c\u7ed5\u7740\u4ed6\u6253\u8f6c\uff0c\u7af9\u7aff\u4e0a\u7684\u5c11\u5e74\u53d1\u4e00\u58f0\u558a\uff0c\u5ffd\u7136\u4e00\u4e2a\u4e2a\u7684\u8dc3\u4e0b\uff0c\u6301\u5251\u5411 \u4ed6\u75be\u51b2\uff01\u90a3\u4eba\u8eab\u624b\u77eb\u6377\u6781\u4e86\uff0c\u7ad9\u5728\u4e24\u6bb5\u7af9\u7aff\u4e0a\u7eb9\u98ce\u4e0d\u52a8\uff0c\u56db\u4e2a\u5c11\u5e74\u5148\u5f8c\u5411\u4ed6\u51b2\u6765\uff0c\u4ed6\u4f38\u51fa\u4e24 \u624b\uff0c\u4e00\u63a5\u4fbf\u629b\uff0c\u5c31\u50cf\u800d\u6742\u6280\u7684\u4eba\u629b\u98de\u5200\u4f3c\u7684\uff0c\u628a\u5de6\u9762\u51b2\u6765\u7684\u5c11\u5e74\u629b\u5411\u53f3\u8fb9\uff0c\u6709\u9762\u51b2\u6765\u7684\u5c11\u5e74 \u629b\u5411\u5de6\u8fb9\uff0c\u4e00\u629b\u53c8\u63a5\uff0c\u4e00\u63a5\u53c8\u629b\uff0c\u66f4\u5999\u7684\u662f\uff0c\u90a3\u4e9b\u51b2\u6765\u7684\u5c11\u5e74\u7ed9\u4ed6\u4e00\u629b\uff0c\u53c8\u6070\u6070\u629b\u5230\u90a3\u56db\u540d \u5927\u6c49\u7684\u7af9\u7aff\u4e0a\uff0c\u5c31\u50cf\u6f14\u51fa\u4e00\u573a\u95ed\u4e2d\u98de\u4eba\u7684\u5927\u6742\u6280\uff0c\u597d\u770b\u4e4b\u6781\uff01 \u3000\u3000\u592a\u5b50\u518d\u6b21\u51fb\u638c\uff0c\u573a\u4e2d\u7684\u4eba\u500f\u7136\u505c\u6b62\uff0c\u56db\u540d\u5927\u6c49\u53d6\u4e0b\u7af9\u7aff\uff0c\u7af9\u7aff\u4e0a\u7684\u5c11\u5e74\u4e5f\u5404\u4e2a\u8dc3\u4e0b\u3002\u90a3 \u4e2a\u7559\u7740\u5c71\u7f8a\u987b\u7684\u6c49\u5b50\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u8df3\u4e0b\u5730\u6765\uff0c\u90a3\u4e24\u6bb5\u7af9\u7aff\uff0c\u5374\u4ecd\u7136\u7ad6\u5728\u5730\u4e0a\u3002\u5353\u4e00\u822a\u773c \u5229\uff0c\u770b\u51fa\u90a3\u4e24\u6bb5\u7af9\u7aff\u4f3c\u4e4e\u77ed\u4e86\u4e00\u622a\uff0c\u65b9\u5728\u8be7\u5f02\u3002\u90a3\u6c49\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u628a\u4e24\u6bb5\u7af9\u7aff\u62d4\u8d77\uff0c\u5730\u4e0a\u7adf \u7136\u7559\u4e0b\u4e86\u4e24\u4e2a\u5c0f\u6d1e\uff0c\u987b\u77e5\u7af9\u7aff\u8d28\u67d4\uff0c\u6ce5\u5730\u751a\u786c\uff0c\u8fd9\u4eba\u4e3b\u89c1\u80fd\u8fd0\u7528\u8db3\u5c16\u7684\u5185\u529b\u628a\u8fd9\u7af9\u7aff\u63d2\u5165\u5730 \u5185\u3002\u8fd9\u4efd\u529f\u529b\uff0c\u786e\u662f\u975e\u540c\u5c0f\u9119\uff01\u592a\u5b50\u628a\u90a3\u6c49\u5b50\u62db\u6765\uff0c\u7ed9\u5353\u4e00\u822a\u4ecb\u7ecd\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u897f\u5382\u7b2c\u4e00\u9ad8 \u624b\uff0c\u73b0\u7236\u7687\u62e8\u7ed9\u6211\u4f7f\u7528\uff0c\u540d\u53eb\u90d1\u6d2a\u53f0\u3002\u5353\u5148\u751f\u6b66\u827a\u9ad8\u5f3a\uff0c\u4e24\u4f4d\u6b63\u597d\u4ea4\u4e2a\u670b\u53cb\u3002\u201d\u90d1\u6d2a\u53f0\u4f38\u624b \u76f8\u63e1\uff0c\u5353\u4e00\u822a\u5ffd\u89c9\u4ed6\u9661\u7136\u7528\u529b\uff0c\u4e94\u6307\u5c31\u5982\u94c1\u7b8d\u4e00\u822c\uff01 \u3000\u3000\u5353\u4e00\u822a\u5fc3\u60f3\uff1a\u4ed6\u662f\u5728\u8bd5\u6211\u7684\u529f\u529b\u3002\u624b\u677f\u653e\u8f7b\uff0c\u90d1\u6d2a\u53f0\u7a81\u89c9\u624b\u4e2d\u63e1\u7740\u4e00\u5806\u68c9\u82b1\uff0c\u5353\u4e00\u822a\u7684 \u624b\u638c\u5df2\u4f3c\u6e38\u9c7c\u4e00\u822c\u6ed1\u4e86\u51fa\u6765\u3002\u90d1\u6d2a\u53f0\u9053\uff1a\u201c\u597d\uff0c\u662f\u6b63\u5b97\u7684\u5185\u5bb6\u529f\u529b\uff0c\u9601\u4e0b\u4e0d\u662f\u6b66\u5f53\u6d3e\u4e5f\u662f\u5d69 \u9633\u6d3e\u7684\u4e86\u3002\u201d\u5353\u4e00\u822a\u5fae\u5fae\u5403\u60ca\uff1a\u53ea\u51ed\u8fd9\u4e00\u8bd5\u62db\uff0c\u4ed6\u7adf\u80fd\u77e5\u9053\u6211\u6b66\u5b66\u6e0a\u6e90\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u6b66\u5f53 \u6d3e\u7684\u7d2b\u9633\u9053\u957f\u6b63\u662f\u5bb6\u5e08\u3002\u201d\u90d1\u6d2a\u53f0\u201c\u554a\u5440\u201d\u4e00\u58f0\u9053\uff1a\u201c\u539f\u6765\u662f\u5929\u4e0b\u7b2c\u4e00\u540d\u624b\u7684\u9ad8\u5f92\uff0c\u96be\u602a\u8fd9 \u822c\u4e86\u5f97\u3002\u201d\u5404\u9053\u4ef0\u6155\u4e4b\u610f\u3002\u592a\u5b50\u5174\u5c3d\u9063\u6563\u4f17\u4eba\uff0c\u5e26\u5353\u4e00\u822a\u56de\u8f6c\u4e66\u623f\u3002 \u3000\u3000\u795e\u5b97\u5df2\u8001\uff0c\u592a\u5b50\u968f\u65f6\u53ef\u80fd\u5373\u4f4d\uff0c\u6240\u4ee5\u6025\u65bc\u62db\u63fd\u4eba\u624d\uff0c\u773c\u89c1\u8fd9\u5353\u4e00\u822a\u6587\u6b66\u5168\u624d\uff0c\u53c8\u662f\u4e16\u4ee3 \u5927\u5b98\u4e4b\u5f8c\uff0c\u5bf9\u4ed6\u5341\u5206\u8d4f\u8bc6\u3002\u65bc\u662f\u793c\u8d24\u4e0b\u58eb\uff0c\u8bf7\u4ed6\u5728\u592a\u5b50\u5bab\u4e2d\u62c5\u4efb\u5b98\u804c\u3002\u5353\u4e00\u822a\u4ee5\u5b5d\u670d\u672a\u6ee1\u63a8 \u8f9e\u3002\u592a\u5b50\u9053\uff1a\u201c\u53c8\u4e0d\u662f\u5728\u671d\u4e2d\u4e3a\u5b98\uff0c\u5728\u6211\u5e9c\u4e2d\u5f53\u4e2a\u5ba2\u537f\uff0c\u4e5f\u5e76\u4e0d\u8fdd\u80cc\u5b5d\u9053\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a \u201c\u5bb6\u7236  \u9aa8\uff0c\u8fd8\u8981\u8fd0\u56de\u5bb6\u4e61\u3002\u5fae\u81e3\u7956\u7236\uff0c\u5e74\u8001\u65e0\u4eba\u4f8d\u5949\u3002\u6614\u674e\u5bc6\u9648\u60c5\uff0c\u5723\u4e3b\u5c1a\u653e\u4ed6\u5f52\u91cc\u3002\u5fae \u81e3\u672a\u5165\u4ed5\u9014\uff0c\u5c82\u5fcd\u5924\u7f18\u6c42\u8fdb\u3002\u201d\u592a\u5b50\u53f9\u9053\uff1a\u201c\u5148\u751f\u7eaf\u5b5d\u53ef\u98ce\uff0c\u81ea\u53e4\u9053\u5fe0\u81e3\u51fa\u65bc\u5b5d\u5b50\u4e4b\u95e8\uff0c\u6211 \u4e5f\u4e0d\u52c9\u5f3a\u4e86\u3002\u4f46\u671b\u4f60\u5b89\u846c\u4ee4\u5c0a\u4e4b\u5f8c\uff0c\u518d\u5230\u4eac\u5e08\uff0c\u8ba9\u6211\u5f97\u4ee5\u4eb2\u8fd1\u8d24\u4eba\u3002\u4ee4\u5c0a\u7684\u51a4\u60c5\uff0c\u65e5\u5185\u5fc5\u53ef \u662d\u96ea\u3002\u4f60\u4e14\u5728\u6211\u5bab\u4e2d\u6682\u4f4f\u51e0\u5929\u3002\u201d\u592a\u5b50\u76db\u610f\u62f3\u62f3\uff0c\u5353\u4e00\u822a\u81ea\u7136\u4e0d\u597d\u63a8\u8f9e\u3002 \u3000\u3000\u8fc7\u4e86\u51e0\u5929\uff0c\u671d\u4e2d\u53c8\u662f\u4e00\u756a\u6c14\u8c61\u3002\u795e\u5b97\u683c\u65bc\u7956\u5b97\u9057\u8bad\u4e0e\u671d\u5ef7\u8bae\u8bba\uff0c\u8feb\u5f97\u628a\u90d1\u8d35\u5983\u8d2c\u4eba\u51b7 \u5bab\uff0c\u5c06\u4e8c\u7687\u5b50\u5e38\u6d35\u524a\u7235\u56da\u7981\uff0c\u90d1\u56fd\u8205\u5219\u88ab\u95ee\u4e86\u571c\u9996\u4e4f\u5211\uff0c\u4e00\u573a\u5927\u53d8\uff0c\u987f\u65f6\u5e73\u53cd\u8fc7\u6765\uff0c\u88ab\u7275\u8fde \u7684\u5927\u5b98\u4e5f\u4e00\u4e2a\u4e2a\u5f97\u5230\u662d\u96ea\u3002\u5353\u4e00\u822a\u7684\u7236\u4eb2\u5353\u7ee7\u8d24\u60e8\u906d\u6789\u6b7b\uff0c\u7687\u4e0a\u9881\u65e8\u7ed9\u4ed6\u6d17\u8131\u4e86\u53db\u9006\u4e4b\u540d\uff0c \u5e76\u8ffd\u8d60\u4e86\u592a\u5b50\u5c11\u4fdd\u3002\u5353\u4e00\u822a\u62dc\u8c22\u4e86\u592a\u5b50\u6069\u60c5\uff0c\u5fc3\u4e2d\u7a0d\u5f97\u5b89\u6170\uff0c\u6292\u53d1\u4e86\u6291\u90c1\u4e4b\u60c5\u3002\u201c\u6883\u51fb\u6848\u201d \u81f3\u6b64\u544a\u4e00\u6bb5\u843d\uff0c\u53ea\u662f\u90a3\u6301\u8713\u95ef\u5bab\u7684\u90d1\u5927\u6df7\u5b50\uff0c\u5374\u7a81\u7136\u4e0d\u660e\u4e0d\u767d\u7684\u6b7b\u5728\u72f1\u4e2d\uff0c\u795e\u5b97\u7cca\u91cc\u7cca\u6d82\uff0c \u4e5f\u4e0d\u8ffd\u95ee\u3002\u592a\u5b50\u4ee5\u5927\u654c\u5df2\u9664\uff0c\u4e0d\u613f\u7275\u8fde\u8fc7\u751a\uff0c\u4e5f\u4f5c\u7f62\u4e86\u3002\u81ea\u6b64\u9b4f\u5fe0\u8d24\u4e00\u9762\u5728\u5bab\u4e2d\u5f04\u6743\uff0c\u4e00\u9762 \u548c\u592a\u5b50\u7ed3\u7eb3\uff0c\u4f46\u5fcc\u60ee\u592a\u5b50\u7cbe\u660e\uff0c\u6697\u5730\u6000\u7740\u9b3c\u80ce\uff0c\u7ec8\u65bc\u5f8c\u6765\u53c8\u5f04\u51fa\u660e\u671d\u7684\u7b2c\u4e8c\u4e2a\u5927\u602a\u6848-\uff0c \u201c\u7ea2\u4e38\u6848\u201d\uff0c\u8fd9\u662f\u5f8c\u8bdd\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u4e14\u8bf4\u5353\u4e00\u822a\u8d56\u592a\u5b50\u4e4b\u529b\uff0c\u66ff\u7236\u4eb2\u662d\u96ea\u4e4b\u5f8c\uff0c\u6d69\u7136\u6709\u5f52\u5fd7\u3002\u4ed6\u5411\u592a\u5b50\u544a\u4e86\u4e2a\u5047\uff0c\u5230\u62a5\u5b50\u80e1 \u540c\u5b5f\u5bb6\u53bb\u63a2\u8bbf\u738b\u7167\u5e0c\u3002\u4e0d\u6599\u738b\u7167\u5e0c\u548c\u5b5f\u79cb\u971e\u90fd\u4e0d\u89c1\u4e86\u3002\u5353\u4e00\u822a\u6005\u7136\u56de\u5bab\uff0c\u4e0e\u592a\u5b50\u8bf4\u4e86\uff0c\u592a\u5b50 \u4e5f\u751a\u60cb\u60dc\u3002\u53eb\u4eba\u628a\u5b5f\u707f\u7684\u529f\u52b3\uff0c\u8bb0\u5728\u7c3f\u4e0a\uff0c\u628a\u76c2\u707f\u5973\u513f\u5973\u5a7f\u7684\u9762\u8c8c\u4e5f\u753b\u4e86\u51fa\u6765\uff0c\u4ee5\u4fbf\u65e5\u5f8c\u5bfb \u89c5\u916c\u62a5\u3002\u5353\u4e00\u822a\u5fc3\u91cc\u6697\u60f3\uff1a\u4ed6\u6b7b\u65f6\u4f60\u6beb\u4e0d\u5173\u5fc3\uff0c\u73b0\u5728\u5374\u60fa\u5fea\u4f5c\u6001\uff0c\u505a\u7ed9\u8c01\u770b\u3002 \u3000\u3000\u8fc7\u4e86\u51e0\u65e5\uff0c\u5353\u4e00\u822a\u5c06\u7236\u4eb2\u7684\u9aa8\u9ab8\u79fb\u4e86\u51fa\u6765\uff0c\u653e\u4eba\u91d1\u8c2d\uff0c\u5411\u592a\u5b50\u544a\u8f9e\u3002\u592a\u5b50\u5ffd\u9053\uff1a\u201c\u5353\u5148 \u751f\uff0c\u6709\u4e00\u4e2a\u4eba\u60f3\u548c\u4f60\u4e00\u540c\u56de\u53bb\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u9053\uff1a\u201c\u6bbf\u4e0b\u5e9c\u4e2d\u6709\u4eba\u8981\u5230\u72ed\u897f\u53bb\u9ebd\uff1f\u201d\u592a\u5b50\u9053\uff1a\u201c\u6b63\u662f\u3002\u4f60\u8fc1\u4e27\u4ee4\u5c0a\uff0c\u5343\u91cc\u8fe2 \u8fe2\uff0c\u6709\u4eba\u4f5c\u4f34\u4e5f\u597d\u3002\u201d\u53eb\u5353\u4e00\u822a\u7a0d\u5019\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u4f8d\u4ece\u5e26\u4e0a\u4e00\u4eba\uff0c\u5374\u539f\u6765\u5c31\u662f\u90a3\u65e5\u6f14\u6280\u7684\u90d1 \u6d2a\u53f0\u3002\u90d1\u6d2a\u53f0\u7b11\u9053\uff1a\u201c\u6211\u4eec\u4e24\u4eba\u4f5c\u4f34\uff0c\u591a\u5389\u5bb3\u7684\u5f3a\u76d7\uff0c\u5927\u7ea6\u4e5f\u80fd\u5e94\u4ed8\u4e86\u3002\u201d\u5353\u4e00\u822a\u5fc3\u5ff5\u4e00 \u52a8\uff0c\u51b2\u53e3\u95ee\u9053\uff1a\u201c\u82e5\u7136\u662f\u78b0\u5230\u7389\u7f57\u5239\u5462\uff1f\u201d\u90d1\u6d2a\u53f0\u9762\u8272\u500f\u53d8\uff0c\u968f\u5373\u63a9\u9970\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u4e0e\u7389\u7f57 \u5239\u6cb3\u6c34\u4e0d\u72af\u4e95\u6c34\u3002\u5353\u5144\u4e0d\u5fc5\u5bb3\u6015 \u3000\u3000\u4e24\u4eba\u79bb\u4e86\u4eac\u5e08\uff0c\u6653\u884c\u591c\u5bbf\uff0c\u8def\u4e0a\u5927\u5bb6\u8c08\u8bba\u6b66\u529f\uff0c\u5012\u4e5f\u4e0d\u89c9\u5bc2\u5bde\u3002\u8fc7\u4e86\u4e8c\u5341\u591a\u5929\uff0c\u7a7f\u8fc7\u5c71 \u897f\uff0c\u5230\u4e86\u72ed\u897f\u8fb9\u5883\u3002\u6cbf\u9014\u65f6\u4e0d\u65f6\u89c1\u6709\u4eba\u548c\u90d1\u6d2a\u53f0\u6253\u62db\u547c\uff0c\u8fd9\u65e5\u6765\u5230\u534e\u9634\uff0c\u897f\u5cb3\u534e\u5c71\uff0c\u5df2\u5728\u9762 \u524d\u3002\u5353\u4e00\u822a\u60f3\u8d77\u534e\u5c71\u843d\u96c1\u5cf0\u4e0a\uff0c\u6709\u4e00\u6240\u9053\u89c2\uff0c\u89c2\u4e2d\u7684\u9053\u58eb\u8d1e\u4e7e\u9053\u4eba\u662f\u5e08\u7236\u7684\u77e5\u4ea4\uff0c\u5e08\u7236\u66fe\u53eb \u81ea\u5df2\u56de\u5bb6\u65f6\u53bb\u62dc\u8bbf\u4ed6\uff0c\u56e0\u5bf9\u90d1\u6d2a\u53f0\u8bf4\u4e86\u3002\u90d1\u6d2a\u53f0\u9053\uff1a\u201c\u90a3\u6b63\u597d\u4e86\uff0c\u54b1\u4eec\u7d22\u6027\u5728\u8fd9\u91cc\u9017\u7559\u96e8 \u5929\uff0c\u6211\u4e5f\u8981\u7b49\u51e0\u4f4d\u670b\u53cb\u3002\u201d \u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u5353\u4e00\u822a\u9080\u90d1\u6d2a\u53f0\u4e0a\u534e\u5c71\uff0c\u90d1\u6d2a\u53f0\u63a8\u8bf4\u6709\u4e8b\uff0c\u4f46\u5631\u4ed6\u65e9\u53bb\u65e9\u56de\u3002\u5353\u4e00\u822a\u72ec\u81ea \u4e00\u4eba\uff0c\u6b65\u4e0a\u534e\u5c71\uff0c\u90a3\u534e\u5c71\u540d\u5217\u4e94\u5927\u540d\u5c71\uff0c\u671d\u9633\uff0e\u843d\u96c1\uff0e\u83b2\u82b1\u3001\u4e91\u53f0\u3001\u7389\u5973\uff0c\u4e94\u5cf0\u73af\u62f1\uff0c\u5cf0\u5ce6 \u91cd\u53e0\uff0c\u5f62\u4f3c\u4e00\u6735\u63d2\u5929\u82b1\u74e3\uff0c\u7aef\u7684\u58ee\u4e3d\u65e0\u4fe6\uff0c\u843d\u96c1\u5cf0\u662f\u534e\u5c71\u7b2c\u4e8c\u5cf0\uff0c\u5353\u4e00\u822a\u884c\u4e86\u8bb8\u4e45\uff0c\u5230\u4e86\u534a \u5c71\uff0c\u5df3\u8fd1\u662f\u4e2d\u5348\u65f6\u5206\uff0c\u5c71\u9876\u4e91\u70df\u5f25\u6f2b\uff0c\u5929\u8272\u6c89\u6697\uff0c\u5353\u4e00\u822a\u62c5\u5fc3\u4e0b\u96e8\uff0c\u5e78\u597d\u9053\u89c2\u5df2\u7ecf\u5728\u671b\uff0c\u5353 \u4e00\u822a\u6b65\u5165\u9053\u89c2\uff0c\u89c2\u5185\u758f\u758f\u843d\u843d\uff0c\u5c45\u7136\u4e5f\u6709\u51e0\u4e2a\u9999\u5ba2\u3002\u5353\u4e00\u822a\u8d70\u8fc7\u7ecf\u5802\uff0c\u62fe\u7ea7\u767b\u6bbf\uff0c\u5ffd\u89c1\u4e00\u4e2a \u5999\u9f84\u5c11\u5973\uff0c\u5306\u5306\u8d70\u51fa\uff0c\u989c\u5bb9\u8273\u4e3d\uff0c\u7f8e\u82e5\u5929\u4eba\uff0c\u867d\u662f\u60ca\u9e3f\u4e00\u77a5\uff0c\u4e5f\u89c9\u610f\u593a\u795e\u6447\u3002\u5353\u4e00\u822a\u5fc3\u60f3\uff0c \u82e5\u5979\u4e0b\u5230\u534a\u5c71\uff0c\u78b0\u7740\u5927\u96e8\uff0c\u90a3\u5c31\u7cdf\u4e86\u3002 \u3000\u3000\u5353\u4e00\u822a\u8fdb\u4e86\u5927\u6bbf\uff0c\u901a\u540d\u6c42\u89c1\uff0c\u8d1e\u4e7e\u9053\u4eba\u6781\u4e3a\u6b22\u559c\uff0c\u4eb2\u81ea\u628a\u4ed6\u63a5\u4eba\u4e39\u623f\uff0c\u53eb\u5c0f\u9053\u58eb\u7aef\u6765\u534e \u5c71\u7684\u540d\u8336\uff0c\u5353\u4e00\u822a\u66ff\u5e08\u7236\u95ee\u5019\uff0c\u8d1e\u4e7e\u9053\uff1a\u201c\u6211\u4e0e\u5c0a\u5e08\u5df2\u6709\u5341\u5e74\u4e0d\u89c1\u4e86\u3002\u60f3\u4e0d\u5230\u4ed6\u8c03\u6559\u51fa\u8fd9\u6837 \u4e00\u4f4d\u597d\u5f92\u5f1f\u3002\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u7684\u53c1\u5e08\u53d4\u7ea2\u4e91\u9053\u4eba\u4e00\u6708\u4e4b\u524d\uff0c\u5012\u66fe\u7ecf\u8fc7\u6b64\u5730\u3002\u201d\u5353\u4e00 \u822a\u9053\uff1a\u201c\u6211\u53c1\u5e08\u53d4\u6765\u505a\u4ec0\u9ebd\uff1f\u201d\u8d1e\u4e7e\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u6b66\u5f53\u95e8\u4e0b\uff0c\u6709\u4e94\u4e2a\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\uff0c\u5168\u7ed9\u7389\u7f57 \u5239\u5272\u4e86\u624b\u6307\uff0c\u8fb1\u9a82\u4e00\u987f\u3002\u7ea2\u4e91\u9053\u4eba\u8981\u627e\u7389\u7f57\u5239\u7b97\u8d26\u5462\u3002\u662f\u6211\u628a\u4ed6\u529d\u4e86\u53c8\u529d\uff0c\u529d\u4ed6\u4e0d\u8981\u548c\u5c0f\u8f88 \u6597\u6c14\uff0c\u5f8c\u6765\u4e5f\u4e0d\u77e5\u4ed6\u53bb\u4e86\u6ca1\u6709\uff1f\u201d\u5353\u4e00\u822a\u5fc3\u60f3\uff1a\u5230\u5904\u90fd\u542c\u4eba\u8bf4\u8d77\u7389\u7f57\u5239\uff0c\u8fd9\u5973\u9b54\u5934\u4e0d\u77e5\u662f\u600e \u6837\u51f6\u6076\u7684\u6837\u513f\uff1f \u3000\u3000\u4e24\u4eba\u8c08\u4e86\u4e00\u9635\uff0c\u5916\u9762\u4ecd\u662f\u95f7\u96f7\u4e0d\u96e8\u3002\u8d1e\u4e7e\u9053\uff1a\u201c\u770b\u6765\u6015\u6709\u4e00\u573a\u66b4\u96e8\uff0c\u4f60\u5728\u8fd9\u91cc\u6b47\u4e00\u665a \u5427\u3002\u201d\u5353\u4e00\u822a\u8bb0\u6302\u90d1\u6d2a\u53f0\u548c\u4ed6\u7236\u4eb2\u7684\u9aa8  \uff0c\u7acb\u523b\u544a\u8f9e\u9053\uff1a\u201c\u8fd8\u6709\u4e2a\u670b\u53cb\u5728\u7b49\u6211\uff0c\u4e0b\u5c71\u8f83\u5feb\uff0c \u6211\u8fd8\u662f\u8d76\u56de\u53bb\u5427\u3002\u201d\u8d1e\u4e7e\u6258\u4ed6\u95ee\u5019\u5e08\u7236\uff0c\u9001\u51fa\u5c71\u95e8\u3002 \u3000\u3000\u5353\u4e00\u822a\u4e0b\u5230\u534a\u5c71\uff0c\u5ffd\u7136\u96f7\u58f0\u8f70\u8f70\uff0c\u4e4c\u4e91\u853d\u5929\uff0c\u5927\u96e8\u6b32\u964d\u3002 \u3000\u3000\u5353\u4e00\u822a\u6e38\u76ee\u56db\u987e\uff0c\u5ffd\u89c1\u534a\u5c71\u8170\u5904\uff0c\u6709\u4e2a\u5927\u6d1e\uff0c\u6d1e\u53e3\u5d16\u77f3\uff0c\u523b\u6709\u201c\u9ec4\u9f99\u6d1e\u201d\u53c1\u4e2a\u5927\u5b57\uff0c\u6d1e \u5916\u4fee\u7af9\u6210\u4e1b\uff0c\u53e4\u677e\u51e0\u6811\uff0c\u8fd8\u6709\u77f3\u51e0\u77f3\u51f3\uff0c\u60f3\u662f\u89c2\u4e2d\u9053\u58eb\u89c1\u8fd9\u53e4\u6d1e\u98ce\u666f\u9887\u4f73\uff0c\u7279\u610f\u7ecf\u7ba1\u7684\u3002\u5353 \u4e00\u822a\u9053\u58f0\u201c\u4fa5\u5e78\u201d\uff0c\u8fd9\u5c71\u6d1e\u6b63\u597d\u907f\u96e8\uff0c\u65bc\u662f\u8fc8\u6b65\u4eba\u5185\uff0c\u4eba\u4e86\u6d1e\u5f8c\uff0c\u5916\u9762\u96f7\u58f0\u63a5\u8fde\u4e0d\u65ad\uff0c\u5927\u96e8 \u5df2\u662f\u503e\u76c6\u800c\u4e0b\u3002 \u3000\u3000\u6d1e\u9887\u6df1\u5e7d\uff0c\u5353\u4e00\u822a\u884c\u5230\u8179\u5730\uff0c\u5ffd\u7136\u773c\u775b\u4e00\u4eae\uff0c\u6d1e\u4e2d\u7684\u77f3\u677f\u51f3\u4e0a\uff0c\u7adf\u7136\u8eba\u7740\u4e00\u4e2a\u5999\u9f84\u5c11 \u5973\uff0c\u6b3a\u82b1\u80dc\u96ea\uff0c\u6b63\u662f\u5728\u9053\u89c2\u4e2d\u6240\u9047\u7684\u90a3\u4e2a\u5973\u5b50\uff0c\u770b\u4ed6\u6d77\u68e0\u6625\u7761\uff0c\u5a07\u6001\u66f4\u5a9a\uff0c\u5353\u4e00\u822a\u662f\u540d\u5bb6\u5b50 \u5f1f\uff0c\u4ee5\u793c\u81ea\u6301\uff0c\u51e0\u4e4e\u4e0d\u6562\u5e73\u89c6\u3002\u89c1\u5979\u7761\u5f97\u6b63\u9163\uff0c\u53c8\u4e0d\u6562\u5c06\u5979\u53eb\u9192\uff0c\u5fc3\u60f3\uff1a\u82e5\u5979\u9192\u6765\uff0c\u5c82\u4e0d\u8bef \u4f1a\u6211\u662f\u4e2a\u8f7b\u8584\u4e4b\u4eba\uff0c\u65bc\u51e0\u4e4e\u4e0d\u6562\u5e73\uff01\u662f\u653e\u8f7b\u811a\u6b65\uff0c\u8d70\u5230\u8fd1\u6d1e\u53e3\u4e4b\u5904\uff0c\u76d8\u819d\u9759\u5750\uff0c\u770b\u5916\u9762\u96e8\u8d8a \u4e0b\u8d8a\u5927\uff0c\u867d\u7136\u5fc3\u5934\u9e7f\u649e\uff0c\u60f3\u90a3\u5c11\u5973\u989c\u5bb9\u4e16\u95f4\u5c11\u89c1\uff0c\u4f46\u5374\u8fde\u770b\u4e5f\u4e0d\u6562\u56de\u5934\u53bb\u770b\u3002 \u3000\u3000\u5750\u4e86\u4e00\u9635\uff0c\u5353\u4e00\u822a\u5ffd\u89c9\u6d1e\u4e2d\u5bd2\u610f\u8feb\u4eba\uff0c\u5fc3\u60f3\uff1a\u6211\u662f\u4e00\u4e2a\u7ec3\u6b66\u7684\u4eba\uff0c\u72b9\u81ea\u611f\u5230\u5bd2\u610f\uff0c\u6d1e\u4e2d \u90a3\u4e2a\u5c11\u5973\u600e\u751f\u62b5\u53d7\uff0c\u53ea\u6015\u8981\u51b7\u51fa\u75c5\u6765\u3002\u53c8\u60f3\u9053\uff1a\u201c\u5b64\u7537\u5be1\u5973\uff0c\u867d\u7136\u907f\u5acc\uff0c\u4f46\u82e5\u773c\u89c1\u5979\u5c06\u56e0\u5bd2 \u81f4\u75c5\uff0c\u65bc\u5fc3\u4f55\u5fcd\u4e86\u907f\u5acc\u4e8b\u5c0f\uff0c\u5b81\u613f\u5979\u9192\u6765\u602a\u8d23\u6211\u5427\u3002\u65bc\u662f\u53c8\u653e\u8f7b\u811a\u6b65\uff0c\u6084\u6084\u8d70\u5165\u6d1e\u4e2d\uff0c\u8131\u4e0b \u8eab\u4e0a\u5927\u8863\uff0c\u8f7b\u8f7b\u76d6\u5728\u5979\u8eab\u4e0a\u3002\u53c8\u8e51\u624b\u8e51\u811a\uff0c\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8d70\u4e86\u51e0\u6b65\uff0c\u5ffd\u542c\u5f97\u80cc\u5f8c\u90a3\u5c11\u5973\u7ffb\u8eab\u7684\u58f0\u54cd\uff0c\u5353\u4e00\u822a\u4e0d\u6562\u56de\u5934\uff0c\u4f46\u542c\u5f97\u90a3\u5c11\u5973\u5389\u58f0\u65a5\u9053\uff1a \u201c\u5927\u80c6\u72c2\u5f92\uff0c\u6562\u6765\u6b3a\u6211\uff1f\u201d\u5353\u4e00\u822a\u5fd9\u9053\uff1a\u201c\u5c0f\u5a18\u5b50\u522b\u89c1\u602a\uff0c\u662f\u6211\u89c1\u8fd9\u6d1e\u4e2d\u5bd2\u610f\u8feb\u4eba\uff0c\u6015\u4f60\u53d7 \u51b7\uff0c\u6240\u4ee5\u5192\u6627\u7ed9\u4f60\u6dfb\u8863\u3002\u201d\u90a3\u5c11\u5973\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8bf7\u4f60\u56de\u8fc7\u5934\u6765\u3002\u201d\u5353\u4e00\u822a\u597d\u751f\u5947 \u602a\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u8fd8\u662f\u4e0d\u6562\u5e73\u89c6\uff0c\u90a3\u5c11\u5973\u5c06\u5927\u8863\u9012\u8fc7\uff0c\u8bf4\u9053\uff1a\u201c\u5148\u751f\u9002\u624d\u4e3e\u52a8\uff0c\u6211\u90fd\u89c1\u4e86\u3002\u5148 \u751f\u771f\u662f\u4e2a\u81f3\u8bda\u541b\u5b50\uff0c\u6211\u5e73\u751f\u8fd8\u6ca1\u6709\u89c1\u8fc7\u50cf\u4f60\u8fd9\u6837\u7684\u4eba\u3002\u6362\u662f\u65c1\u4eba\uff0c\u6015\u4e0d\u8981\u5927\u8086\u8f7b\u8584\u3002\u201d\u5353\u4e00 \u822a\u5fc3\u60f3\u8fd9\u5973\u5b50\u8bf4\u8bdd\u600e\u7684\u5982\u6b64\u5766\u7387\uff0c\u9762\u4e0a\u70ed\u8fa3\u8fa3\u7684\uff0c\u53c8\u542c\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6211\u521a\u624d\u9a82\u4f60\uff0c\u662f\u6545\u610f\u5413 \u4f60\u7684\uff0c\u4f60\u53ef\u4e0d\u8981\u89c1\u602a\u3002\u201d\u5353\u4e00\u822a\u76b1\u4e86\u76b1\u7709\uff0c\u5fc3\u60f3\u600e\u7684\u8fd9\u6837\u559c\u6012\u5012\u98a0\uff0c\u9a82\u4eba\u5f53\u73a9\u800d\u7684\u3002\u90a3\u5c11\u5973 \u9274\u8c8c\u8fa8\u8272\uff0c\u7b11\u9053\uff1a\u201c\u6211\u751f\u6027\u5982\u6b64\uff0c\u6240\u4ee5\u8bb8\u591a\u4eba\u90fd\u6015\u6211\u5462\u3002\u6211\u4ee5\u5f8c\u4e00\u5b9a\u6539\u4e86\u3002\u201d\u5353\u4e00\u822a\u542c\u5979\u8fd9 \u6ca1\u5934\u6ca1\u8111\u7684\u8bdd\uff0c\u66f4\u662f\u5947\u602a\uff0c\u5fc3\u60f3\uff0c\u4f60\u65e2\u7136\u6027\u60c5\u5982\u6b64\uff0c\u4f55\u5fc5\u7a81\u7136\u8981\u6539\uff0c\u4f60\u6539\u4e0d\u6539\u53c8\u4e0e\u6211\u4f55\u5e72\u3002 \u3000\u3000\u90a3\u5c11\u5973\u89c1\u4ed6\u5c3d\u4e0d\u8bf4\u8bdd\uff0c\u9762\u6709\u6120\u5bb9\uff0c\u53c8\u9053\uff1a\u201c\u5148\u751f\u8fd8\u607c\u6211\u5417\uff01\u201d\u5353\u4e00\u822a\u6025\u9053\uff1a\u201c\u5c0f\u5a18\u5b50\u90a3 \u91cc\u8bdd\u6765\uff0c\u6211\u600e\u4f1a\u607c\u4f60\u3002\u201d\u90a3\u5c11\u5973\u559c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e0d\u4f1a\u607c\u6211\u3002\u4f60\u5fc3\u5730\u771f\u597d\uff0c\u6211\u81ea\u51fa\u751f\u4ee5\u6765\uff0c \u8fd8\u672a\u6709\u4eba\u50cf\u4f60\u90a3\u6837\u7167\u987e\u8fc7\u6211\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u7684\u7238\u7238\u5988\u5988\u5462\uff1f\u201d\u5c11\u5973\u9053\uff1a\u201c\u6211\u8fd8\u672a\u61c2\u4eba \u4e8b\uff0c\u7238\u7238\u5988\u5988\u5c31\u5df2\u6b7b\u4e86\u3002\u201d\u5353\u4e00\u822a\u6b49\u7136\u8bf4\u9053\uff1a\u201c\u6055\u6211\u4e71\u95ee\uff0c\u6311\u8d77\u4f60\u7684\u6101\u7eea\u3002\u201d\u90a3\u5c11\u5973\u5ffd\u7136\u7389 \u624b\u4e00\u626c\uff0c\u5411\u4ed6\u80a9\u5934\u6309\u6765\u3002 \u3000\u3000\u5353\u4e00\u822a\u8eab\u5f62\u4e00\u95ea\uff0c\u90a3\u5c11\u5973\u8eab\u4f53\u6b6a\u659c\uff0c\u4f3c\u6b32\u503e\u8dcc\uff0c\u5353\u4e00\u822a\u7528\u624b\u6307\u4e00\u94a9\u8863\u5e26\uff0c\u98d8\u4e86\u8d77\u6765\uff0c\u7528 \u8863\u5e26\u62e6\u5979\u8170\u80a2\uff0c\u9632\u5979\u8dcc\u5012\u3002\u90a3\u5c11\u5973\u7ad9\u7a33\u811a\u6b65\uff0c\u5c34\u5c2c\u8bf4\u9053\uff1a\u201c\u5730\u4e0b\u6e7f\uff0c\u811a\u4e00\u6ed1\uff0c\u4e0d\u662f\u5148\u751f\u51fa\u624b \u76f8\u6276\uff0c\u6211\u51e0\u4e4e\u8dcc\u4e86\u4e00\u8de4\u3002\u201d\u5ffd\u800c\u53c8\u7b11\u9053\uff1a\u201c\u8bf4\u9519\u4e86\uff0c\u4e0d\u662f\u51fa\u624b\uff0c\u662f\u7528\u8863\u5e26\u6276\u6211\u3002\u201d\u5353\u4e00\u822a\u9762 \u7ea2\u8033\u70ed\uff0c\u4e0d\u6562\u51fa\u58f0\u3002\u90a3\u5c11\u5973\u5ffd\u9053\uff1a\u201c\u4f60\u4e5f\u6015\u6211\u5417\uff1f\u201d\u5353\u4e00\u822a\u5947\u602a\u8fd9\u5c11\u5973\u8bf4\u8bdd\uff0c\u600e\u9ebd\u7c7b\u4f3c\u75af \u75f4\uff0c\u7ee7\u800c\u4e00\u60f3\uff0c\u5979\u65e0\u7236\u65e0\u6bcd\uff0c\u6240\u4ee5\u5fc3\u91cc\u96be\u53d7\uff0c\u602a\u4e0d\u5f97\u5979\u8fd9\u6837\u3002\u56e0\u9053\uff1a\u201c\u6211  \u89c9\u5c0f\u59d0\u53ef\u601c\u3002\u201d \u5c11\u5973\u622a\u7740\u8bdd\u5934\uff0c\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u53ef\u601c\uff1f\u201d\u5353\u4e00\u822a\u7eed\u9053\uff1a\u201c\u4e5f\u5f88\u53ef\u4f69\u3002\u5c0f\u59d0\u5b64\u5355\u4e00\u4eba\uff0c\u6d3b\u5230\u73b0 \u5728\uff0c\u8fd8\u6562\u72ec\u4e0a\u534e\u5c71\u70e7\u9999\uff0c\u82e5\u975e\u6709\u7edd\u5927\u52c7\u6c14\uff0c\u4e5f\u4e0d\u80fd\u79fb\u3002\u201d\u90a3\u5c11\u5973\u4f4e\u5782\u7c89\u9888\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u5f97 \u771f\u5bf9\uff0c\u600e\u9ebd\u4f60\u5c31\u50cf\u6211\u7684\u8001\u670b\u53cb\u4e00\u822c\u3002\u5582\uff0c\u4f60\u53eb\u4ec0\u9ebd\u540d\u5b57\uff01\u6211\u8fd8\u672a\u8bf7\u6559\u4f60\u5462\u3002\u201d\u5353\u4e00\u822a\u628a\u59d3\u540d \u8bf4\u4e86\uff0c\u8f6c\u95ee\u5c11\u5973\uff0c\u5c11\u5973\u9053\uff1a\u201c\u6211\u59d3\u7ec3\uff0c\u6211\u6ca1\u6709\u540d\u5b57\uff0c\u4f60\u66ff\u6211\u8d77\u4e00\u4e2a\u597d\u5417\uff01\u201d\u5916\u9762\u96e8\u58f0\u6e10\u6b62\uff0c \u4e00\u9635\u98ce\u522e\u4e86\u8fdb\u6765\uff0c\u5c11\u5973\u8863\u8882\u98ce\u98d8\uff0c\u59ff\u6001\u7f8e\u5999\uff0c\u5353\u4e00\u822a\u7a81\u7136\u60f3\u8d77\u201c\u9713\u88f3\u7fbd\u8863\u201d\u7684\u8bf4\u8bdd\uff0c\u51b2\u53e3\u8bf4 \u9053\uff1a\u201c\u53eb\u505a\u9713\u88f3\uff0c\u5c82\u975e\u751a\u597d\uff1f\u201d\u90a3\u5c11\u5973\u5ffd\u7136\u9762\u8272\u5927\u53d8\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff0c\u4ece\u5b9e\u62db\u6765\uff01\u201d\u5353 \u4e00\u822a\u60ca\u9053\uff1a\u201c\u6211\u5c31\u662f\u5353\u4e00\u822a\u561b\uff0c\u7ec3\u5c0f\u59d0\u5acc\u8fd9\u4e2a\u540d\u5b57\u4e0d\u597d\uff0c\u4e0d\u8981\u4fbf\u662f\uff0c\u4f55\u5fc5\u53d1\u6012\u3002\u201d\u90a3\u5c11\u5973\u53cc \u7738\u95ea\u95ea\uff0c\u773c\u5149\u5982\u5229\u526a\u4e00\u822c\u76f4\u76ef\u7740\u4ed6\uff0c\u542c\u4ed6\u8bf4\u5f8c\uff0c\u9759\u4e86\u4e0b\u6765\u3002\u9053\uff1a\u201c\u6211\u53c8\u53d1\u602a\u813e\u6c14\u4e86\uff0c\u4f60\u7ed9\u6211 \u53d6\u7684\u540d\u5b57\u5f88\u597d\uff0c\u6211\u4ee5\u5f8c\u5c31\u53eb\u7ec3\u9713\u88f3\u5427\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u62b9\u4e86\u989d\u4e0a\u7684\u51b7\u6c57\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4f4d\u5c0f\u59d0\u771f\u5f97\u4eba\u60ca\u3002\u201d\u7ec3\u9713\u88f3\u5ffd\u9053\uff1a\u201c\u6211\u770b\u5148\u751f\u7cbe\u901a \u6b66\u529f\uff0c\u4e0d\u77e5\u5230\u534e\u5c71\u4f55\u4e8b\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u5728\u6b66\u5f53\u6d3e\u5b66\u8fc7\u51e0\u624b\u53c1\u811a\u732b\u7684\u529f\u592b\uff0c\u90a3\u8c08\u5f97\u4e0a\u7cbe\u901a \u4e8c\u5b57\uff0c\u6211\u8fd9\u6b21\u662f\u5c06\u7236\u4eb2\u9ab8\u9aa8\uff0c\u8fc1\u846c\u56de\u4e61\uff0c\u8def\u8fc7\u534e\u5c71\uff0c\u7279\u4e0a\u6765\u70e7\u4e00\u70b7\u9999\u3002\u201d\u770b\u5b98\u4eec\u5927\u7ea6\u90fd\u77e5\u9053 \u8fd9\u4f4d\u5c11\u5973\u5c31\u662f\u7389\u7f57\u5239\u7ec3\u9713\u88f3\u4e86\uff0c\u96be\u5f97\u5353\u4e00\u822a\u7ed9\u5979\u8d77\u7684\u540d\u5b57\uff0c\u6b63\u5de7\u5c31\u662f\u5979\u7684\u672c\u540d\u3002\u7389\u7f57\u5239\u5fc3\u91cc \u751f\u7591\uff0c\u521a\u624d\u8bd5\u4ed6\uff0c\u53c8\u770b\u51fa\u4ed6\u662f\u6b66\u5f53\u6d3e\u9ad8\u624b\uff0c\u6b66\u529f\u8fdc\u5728\u803f\u7ecd\u5357\u4e4b\u4e0a\uff0c\u8fde\u7ea2\u4e91\u9053\u4eba\uff0c\u4e5f\u8981\u900a\u4ed6\u4e00 \u7b79\u3002\u53ea\u9053\u4ed6\u662f\u6709\u610f\u5bfb\u4ec7\uff0c\u4e0d\u6599\u4ed6\u6beb\u4e0d\u9690\u7792\u5766\u7136\u8bf4\u4e86\uff0c\u770b\u795e\u6c14\u4ed6\u7edd\u5bf9\u4e0d\u77e5\u81ea\u5df1\u4fbf\u662f\u4e3b\u7f57\u5239\uff0c\u4e0d \u89c9\u54d1\u7136\u5931\u7b11\u3002\u987b\u77e5\u7389\u7f57\u5239\u624b\u5e95\u6781\u8fa3\uff0c\u82e5\u7136\u521a\u624d\u5353\u4e00\u822a\u6709\u70b9\u9690\u7792\uff0c\u90a3\u5c31\u7cdf\u4e86\u3002 \u3000\u3000\u7389\u7f57\u5239\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u6211\u95fb\u5f97\u6b66\u5f53\u6d3e\u5251\u6cd5\u5929\u4e0b\u65e0\u53cc\uff0c\u600e\u80fd\u8bf4\u662f\u53c1\u811a\u732b\u7684\u529f\u592b\uff1f\u201d\u5353\u4e00\u822a \u9053\uff1a\u201c\u5b66\u65e0\u6b62\u5883\uff0c\u5929\u5916\u6709\u5929\uff0c\u5404\u6d3e\u6b66\u529f\uff0c\u90fd\u6709\u7279\u957f\uff0c\u90a3\u6709\u5929\u4e0b\u65e0\u53cc\u7684\u9053\u7406\u3002\u4e0d\u8fc7\u6b66\u5f53\u5c11\u6797\uff0c \u5386\u53f2\u60a0\u4e45\uff0c\u4ee3\u51fa\u82f1\u8c6a\uff0c\u6240\u4ee5\u6b66\u6797\u4eba\u58eb\uff0c\u9042\u8c2c\u52a0\u8d5e\u8d4f\u7f62\u4e86\u3002\u81f3\u65bc\u6211\u8d44\u8d28\u9c81\u949d\uff0c\u867d\u6709\u540d\u5e08\uff0c\u4e66\u5251 \u65e0\u6210\uff0c\u66f4\u662f\u65e0\u8db3\u79f0\u9053\u3002\u201d\u5353\u4e00\u822a\u8fd9\u65f6\u5df2\u6000\u7591\u7389\u7f57\u5239\u61c2\u5f97\u6b66\u529f\uff0c\u8bdd\u8bf4\u5f97\u7279\u522b\u8c26\u865a\u3002\u7389\u7f57\u5239\u7559\u5fc3 \u8046\u542c\uff0c\u70b9\u4e86\u70b9\u5934\u3002\u5ffd\u7136\u5411\u5353\u4e00\u822a\u884c\u6765\uff0c\u8863\u8896\u4e00\u62c2\uff0c\u95ea\u7535\u822c\u7684\u6349\u7740\u4e86\u5353\u4e00\u822a\u7684\u624b\u8155\u3002 \u3000\u3000\u5353\u4e00\u822a\u5927\u5403\u4e00\u9a7e\uff1a\u81ea\u5df1\u600e\u4f1a\u95ea\u8eb2\u4e0d\u5f00\uff1f\u6da8\u7ea2\u4e86\u9762\u3002\u8bd5\u7528\u529b\u6323\u8131\uff0c\u7389\u7f57\u5239\u6545\u610f\u628a\u624b\u4e00\u677e\uff0c \u6d1e\u5916\u96e8\u58f0\u60ed\u6b62\uff0c\u5c71\u5934\u9690\u6709\u5578\u58f0\u3002\u7389\u7f57\u5239\u9053\uff1a\u201c\u54df\uff0c\u6211\u5bb3\u6015\u5f97\u7d27\uff0c\u6211\u4e00\u5bb3\u6015\u5c31\u60f3\u62c9\u4e2a\u4eba\u4f5c\u4f34\uff0c \u4f60\u53c8\u4e0d\u7406\u6211\u3002\u201d\u5353\u4e00\u822a\u4e5f\u4e0d\u77e5\u5979\u662f\u6709\u610f\u65e0\u610f\uff0c\u731c\u4e0d\u900f\u5979\u5230\u5e95\u4f1a\u4e0d\u4f1a\u6b66\u529f\uff0c\u4f46\u770b\u5979\u695a\u695a\u53ef\u601c\uff0c \u4e0d\u7981\u8bf4\u9053\uff1a\u201c\u5c0f\u59d0\u82e5\u662f\u5bb3\u6015\uff0c\u6211\u9001\u4f60\u56de\u5bb6\u5427\u3002\u201d\u7389\u7f57\u5239\u8d70\u8fd1\u6d1e\u53e3\uff0c\u770b\u770b\u5929\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u96e8\u5c31 \u8981\u505c\u6b62\uff0c\u6709\u4eba\u7b49\u7740\u6211\u5462\u3002\u4e0d\u7528\u9ebb\u70e6\u4f60\u4e86\u3002\u201d\u8fc7\u4e00\u4f1a\u513f\uff0c\u96e8\u6536\u4e91\u6563\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u597d\uff0c\u6211\u8981\u56de \u5bb6\u53bb\u4e86\u3002\u201d\u5353\u4e00\u822a\u672c\u60f3\u95ee\u5979\uff1a\u4f60\u65e2\u65e0\u7236\u65e0\u6bcd\uff0c\u5bb6\u91cc\u8fd8\u6709\u4f55\u4eba\u3002\u4f46\u89c1\u5979\u8a00\u884c\u8be1\u79d8\uff0c\u4e0d\u77e5\u600e\u7684\uff0c \u5fc3\u91cc\u6709\u70b9\u6015\u5979\uff0c\u4e0d\u6562\u5192\u6627\u95ee\u5979\u8eab\u4e16\u3002\u56e0\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4e5f\u8981\u4e0b\u5c71\u4e86\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u90a3 \u9ebd\u4f60\u5148\u8d70\u5427\u3002\u201d\u5353\u4e00\u822a\u8d70\u51fa\u6d1e\u53e3\uff0c\u7389\u7f57\u5239\u5ffd\u53c8\u5524\u4ed6\uff0c\u5353\u4e00\u822a\u6115\u7136\u56de\u987e\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u8981\u4f60 \u4f9d\u6211\u4e00\u4ef6\u4e8b\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u8bf4\u6765\u542c\uff0c\u6211\u4f9d\u5f97\u4fbf\u4f9d\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u9047\u89c1\u6211\u7684\u4e8b\uff0c\u4e0d\u8bb8 \u4f60\u5bf9\u4efb\u4f55\u4eba\u8bf4\u3002\u201d\u5353\u4e00\u822a\u7b11\u9053\uff1a\u201c\u8fd9\u4e8b\u597d\u4f9d\uff0c\u54b1\u4eec\u840d\u6c34\u76f8\u9022\uff0c\u8fc7\u4e86\u4fbf\u7b97\u4e86\u3002\u6211\u8bf4\u5b83\u5e72\u5417\uff1f\u201d \u7389\u7f57\u5239\u773c\u5708\u4e00\u7ea2\uff0c\u5ffd\u9053\uff1a\u201c\u539f\u6765\u4f60\u5b8c\u5168\u4e0d\u628a\u6211\u653e\u5fc3\u4e0a\u3002\u201d\u5353\u4e00\u822a\u4e0d\u77e5\u6240\u63aa\uff0c\u53ea\u597d\u9053\uff1a\u201c\u6211\u5c31 \u8981\u56de  \u5317\u8001\u5bb6\uff0c\u54b1\u4eec\u4ee5\u5f8c\u672a\u5fc5\u80fd\u591f\u518d\u89c1\u3002\u4e0d\u8fc7\u4ed6\u65e5\u5982\u80fd\u518d\u89c1\uff0c\u6211\u4e00\u5b9a\u5c06\u4f60\u5f53\u6210\u597d\u670b\u53cb\u6b3e \u5f85\u3002\u201d\u7389\u7f57\u5239\u6325\u6325\u624b\u9053\uff1a\u201c\u597d\uff0c\u4f60\u53bb\u5427\uff01\u201d\u5353\u4e00\u822a\u98de\u8dd1\u4e0b\u5c71\uff0c\u5230\u4e86\u5c71\u5773\uff0c\u8bd5\u4e00\u56de\u5934\uff0c\u7ec3\u9713\u88f3 \u8fd8\u501a\u5728\u5ca9\u524d\uff0c\u9690\u7ea6\u53ef\u89c1\u3002 \u3000\u3000\u5353\u4e00\u822a\u56de\u5230\u5ba2\u5e97\uff0c\u90d1\u6d2a\u53f0\u9053\uff1a\u201c\u4f60\u5230\u534e\u5c71\u8fdb\u8fc7\u9999\u4e86\uff1f\u53ef\u89c1\u5230\u8d1e\u4e7e\u9053\u957f\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a \u201c\u89c1\u8fc7\u4e86\u3002\u201d\u90d1\u6d2a\u53f0\u5ffd\u9053\uff1a\u201c\u53ef\u60dc\u8d1e\u4e7e\u9053\u4eba\u4ece\u6765\u4e0d\u7406\u95f2\u4e8b\u3002\u201d\u5353\u4e00\u822a\u542c\u4ed6\u8bdd\u4e2d\u6709\u8bdd\uff0c\u95ee\u9053\uff1a \u201c\u90d1\u524d\u8f88\u6709\u4ec0\u9ebd\u4e8b\uff1f\u201d\u90d1\u6d2a\u53f0\u6b32\u8bf4\u8fd8\u4f11\uff0c\u5ffd\u7136\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u4e0a\u534e\u5c71\uff0c\u9664\u4e86\u8d1e\u4e7e\u9053\u957f\u5916\uff0c\u8fd8\u89c1 \u5230\u4ec0\u9ebd\u6709\u672c\u9886\u7684\u4eba\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u5fc3\u7075\u9707\u52a8\uff0c\u60f3\u8d77\u7ec3\u9713\u88f3\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u5440\uff01\u201d\u90d1\u6d2a\u53f0\u4e5f\u4e0d \u518d\u95ee\uff0c\u5f53\u4e0b\u53c8\u548c\u4ed6\u8c08\u8bba\u4e86\u4e00\u4f1a\u6c5f\u6e56\u4e8b\u8ff9\uff0c\u5403\u8fc7\u665a\u996d\uff0c\u5404\u81ea\u5c31\u5bdd\u3002 \u3000\u3000\u5353\u4e00\u822a\u7761\u5230\u534a\u591c\uff0c\u6726\u80e7\u95f4\u5ffd\u542c\u5f97\u8fdc\u5904\u53c8\u6709\u5578\u58f0\uff0c\u77bf\u7136\u60ca\u8d77\u3002\u95e8\u5916\u6709\u4eba\u8f7b\u8f7b\u6572\u95e8\uff0c\u662f\u90d1\u6d2a \u53f0\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5353\u5144\uff0c\u5f00\u95e8\u3002\u201d\u5353\u4e00\u822a\u62d4\u4e86\u95e8\u95e9\uff0c\u90d1\u6d2a\u53f0\u8fdb\u6765\u5254\u4eae\u6cb9\u706f\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u5353 \u5144\uff0c\u4f60\u6015\u4e0d\u6015\u7389\u7f57\u5239\uff1f\u201d\u5353\u4e00\u822a\u8be7\u9053\uff1a\u201c\u4ec0\u9ebd\uff1f\u201d\u90d1\u6d2a\u53f0\u9053\uff1a\u201c\u6211\u53ea\u8981\u4f60\u5982\u5b9e\u7b54\u6211\u7684\u8bdd\uff0c\u4f60 \u6015\u4e0d\u6015\u5979\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u8fd8\u672a\u89c1\u8fc7\u5979\u600e\u4f1a\u6015\u5979\uff1f\u201d\u90d1\u6d2a\u53f0\u559c\u9053\uff1a\u201c\u4e0d\u6015\u4fbf\u597d\uff01\u90a3\u9ebd\u5979\u52ab \u4f60\u7956\u7236\uff0c\u8fb1\u4f60\u5e08\u5144\uff0c\u4f60\u4e5f\u60f3\u62a5\u4ec7\u9ebd\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u9664\u975e\u5e08\u7236\u6709\u547d\uff0c\u6211\u4e0d\u60f3\u7279\u5730\u53bb\u627e\u5979\u62a5 \u4ec7\u3002\u201d\u90d1\u6d2a\u53f0\u9053\uff1a\u201c\u90a3\u9ebd\u82e5\u5076\u7136\u78b0\u7740\u5462\uff1f\u201d\u5353\u4e00\u822a\u8d8a\u60f3\u8d8a\u5947\uff0c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u96be\u9053\u7389\u7f57\u5239\u5c31\u5728 \u8fd9\u91cc\uff1f\u201d\u6b63\u662f\uff1a\u5982\u5e7b\u5982\u68a6\uff0c\u7591\u96e8\u7591\u4e91\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377\u2014\u2014\u7b2c\u56db\u56de\u3000\u4e03\u7edd\u9635\u6210\u7a7a\u3000\u5927\u5978\u6388\u9996\u3000\u5345\u5e74\u60c5\u82e5\u68a6\u3000\u77f3\u58c1\u7559\u7ecf \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377 \u7b2c\u56db\u56de \u4e03\u7edd\u9635\u6210\u7a7a\u3000\u5927\u5978\u6388\u9996 \u5345\u5e74\u60c5\u82e5\u68a6\u3000\u77f3\u58c1\u7559\u7ecf \u3000\u3000\u90d1\u6d2a\u53f0\u5f39\u6307\u7b11\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u91cc\uff01\u201d\u5353\u4e00\u822a\u84e6\u5730\u4e00\u60ca\uff0c\u4e00\u4e2a\u5ff5\u5934\u95ea\u7535\u822c\u7684\u4ece\u8111\u6d77\u4e2d\u63a0\u8fc7\uff0c \u83ab\u975e\u78b0\u5230\u7684\u90a3\u4e2a\u7ec3\u9713\u88f3\uff0c\u5c31\u662f\u4ec0\u9ebd\u201c\u7389\u7f57\u5239\u201d-\u8f6c\u5ff5\u4e00\u60f3\uff1a\u4e0d\u4f1a\u5440\u4e0d\u4f1a\uff0c\u7389\u7f57\u5239\u4e00\u5b9a\u662f\u4e2a\u7a77 \u51f6\u6781\u6076\u7684\u5973\u4eba\uff0c\u7ec3\u9713\u88f3\u5374\u662f\u5343\u5a07\u767e\u5a9a\u7684\u5c0f\u59d0\uff0c\u600e\u9ebd\u4f1a\u626f\u5230\u4e00\u5757\u3002\u90d1\u6d2a\u53f0\u89c1\u4ed6\u4f4e\u9996\u6c89\u601d\uff0c\u53c8\u6fc0 \u4ed6\u4e00\u53e5\u9053\uff1a\u201c\u600e\u9ebd\u542c\u8bf4\u7389\u7f57\u5239\u5728\u6b64\uff0c\u5c31\u5bb3\u6015\u4e86\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u8c01\u5bb3\u6015\u4e86\uff1f\u4e0d\u8fc7\u548c\u5979\u4e4b\u95f4\u867d \u6709\u6881\u5b50\uff0c\u4f46\u5230\u5e95\u4e0d\u662f\u4ec0\u9ebd\u4e86\u4e0d\u5f97\u7684\u5927\u4e8b\uff0c\u6211\u53c8\u4f55\u5fc5\u5c0f\u9898\u5927\u4f5c\uff0c\u627e\u5979\u5bfb\u4ec7\uff1f\u201d\u90d1\u6d2a\u53f0\u55d4\u9053\uff1a \u201c\u90a3\u9ebd\u5979\u52ab\u4f60\u7956\u7236\u7684\u4e8b\u4f60\u5c31\u4e0d\u7406\u4e86\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u7237\u7237\u5df2\u5e73\u5b89\u5230\u5bb6\uff0c\u5931\u70b9\u94f6\u5b50\u4e5f\u5c31\u7b97 \u4e86\u3002\u201d\u90d1\u6d2a\u53f0\u9053\uff1a\u201c\u90a3\u9ebd\u5979\u4fae\u8fb1\u4f60\u7684\u5e08\u5144\uff0c\u8fd9\u4e8b\u5173\u7cfb\u4f60\u4eec\u6b66\u5f53\u6d3e\u7684\u58f0\u8a89\uff0c\u96be\u9053\u4e5f\u5c31\u7b97\u4e86\uff1f\u201d \u5353\u4e00\u822a\u9053\uff1a\u201c\u672c\u95e8\u7684\u4e8b\uff0c\u6211\u8981\u542c\u5e08\u7236\u5429\u5490\u3002\u201d\u90d1\u6d2a\u53f0\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u7389\u7f57\u5239\u627e\u4e0a\u95e8\u6765\uff0c\u4f60\u4e5f \u4e0d\u7406\u597d\u4e86\u3002\u6b66\u5f53\u6d3e\u7684\u5a01\u540d\uff0c\u5c82\u4e0d\u5728\u4f60\u624b\u91cc\u65ad\u9001\u4e86\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5979\u5e76\u6ca1\u6709\u627e\u4e0a\u95e8\u5440\u3002\u201d\u90d1 \u6d2a\u53f0\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u8001\u5b9e\u544a\u8bc9\u4f60\u5427\uff0c\u5979\u660e\u665a\u5c31\u8981\u548c\u6211\u51b3\u6597\uff0c\u4f60\u548c\u6211\u5728\u4e00\u8d77\uff0c\u96be\u9053\u4f60\u5c31\u80fd\u7f6e\u8eab\u4e8b \u5916\uff1f\u201d \u3000\u3000 \u5353\u4e00\u822a\u7709\u5934\u4e00\u76b1\uff0c\u5fc3\u60f3\u81ea\u5df1\u548c\u90d1\u6d2a\u53f0\u867d\u7136\u6ca1\u6709\u4ec0\u9ebd\u6df1\u539a\u7684\u4ea4\u60c5\uff0c\u4f46\u5230\u5e95\u662f\u540c\u884c\u7684\u5925 \u4f34\u3002\u800c\u7389\u7f57\u5239\u53c8\u786e\u5b9e\u662f\u672c\u95e8\u7684\u4ec7\u4eba\uff0c\u81ea\u5df1\u82e5\u4e0d\u51fa\u624b\u76f8\u52a9\uff0c\u90d1\u6d2a\u53f0\u602a\u8d23\u4e5f\u8fd8\u7f62\u4e86\uff0c\u53ea\u6015\u6b66\u6797\u4e2d \u7684\u670b\u53cb\uff0c\u771f\u4f1a\u4ee5\u4e3a\u81ea\u5df1\u80c6\u5c0f\u6015\u4e8b\uff0c\u4e0d\u6562\u60f9\u5979\u3002\u53c8\u60f3\u9053\uff1a\u201c\u53c1\u5e08\u53d4\u4e5f\u8981\u627e\u5979\u6666\u6c14\uff0c\u90a3\u9ebd\u6211\u5e2e\u8fd9 \u4e2a\u59d3\u90d1\u7684\u6597\u4e00\u6597\u5979\uff0c\u5e08\u7236\u4e00\u5b9a\u4e0d\u4f1a\u602a\u8d23\u3002\u201d\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u90d1\u8001\u524d\u8f88\uff0c\u7389\u7f57\u5239\u65e2\u7136\u8981\u548c\u4f60\u4e3a \u96be\uff0c\u90a3\u9ebd\u6211\u4e5f\u8981\u770b\u770b\u5979\u6709\u4ec0\u9ebd\u672c\u4e8b\u3002\u53ea\u662f\u6211\u5e74\u8f7b\u6280\u77ed\uff0c\u53ea\u6015\u5e2e\u4e0d\u4e86\u4ec0\u9ebd\u5fd9\u3002\u201d\u90d1\u6d2a\u53f0\u559c\u4e0a\u7709 \u68a2\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff0c\u8fd9\u624d\u662f\u4e2a\u6709\u79cd\u7684\u7537\u513f\u3002\u6211\u7ed9\u4f60\u5f15\u89c1\u51e0\u4f4d\u670b\u53cb\uff0c\u54b1\u4eec\u660e\u665a\u5408\u5925 \u513f\u53bb\u6597\u4e00\u6597\u90a3\u5973\u9b54\u5934\u3002\u201d\u62c9\u8d77\u5353\u4e00\u822a\uff0c\u8df3\u51fa\u7a97\u5916\uff0c\u5954\u5411\u8352\u91ce\u3002 \u3000\u3000\u6de1\u6708\u758f\u661f\uff0c\u8fdc\u5904\u6709\u70b9\u70b9  \u706b\u3002\u8dd1\u4e86\u4e00\u9635\uff0c\u5ffd\u542c\u5f97\u51e0\u58f0\u602a\u5578\uff0c\u90d1\u6d2a\u53f0\u500f\u7136\u505c\u6b65\uff0c\u62cd\u62cd\u624b \u638c\uff0c\u8352\u90ca\u91ce\u5893\u65c1\uff0c\u5ffd\u7136\u94bb\u51fa\u4e86\u51e0\u4e2a\u4eba\u6765\u3002\u5353\u4e00\u822a\u5b9a\u775b\u4e00\u770b\uff0c\u53ea\u89c1\u9ad8\u77ee\u8001\u5c11\uff0c\u5171\u662f\u56db\u4eba\u3002\u90d1\u6d2a \u53f0\u95ee\u9053\uff1a\u201c\u8303\u4e8c\u54e5\u6709\u6025\u4e8b\u4e0d\u80fd\u6765\u6211\u5df2\u77e5\u9053\u4e86\uff0c\u5e94\u5927\u54e5\u4e5f\u4e0d\u80fd\u6765\u5417\uff1f\u6ca1\u6709\u4ed6\u600e\u6210\uff01\u201d\u5176\u4e2d\u4e00\u4eba \u7b54\u9053\uff1a\u201c\u4ed6\u8981\u7b97\u51c6\u65f6\u523b\uff0c\u660e\u665a\u7a81\u5982\u5176\u6765\uff0c\u5413\u5413\u90a3\u4e2a\u5973\u9b54\u5934\u3002\u201d \u3000\u3000\u90d1\u6d2a\u53f0\u5c06\u56db\u4e2a\u602a\u5ba2\u4e00\u4e00\u4ecb\u7ecd\u3002\u5934\u4e00\u4e2a\u662f\u8d75\u633a\uff0c\u4e43\u5d69\u9633\u6d3e\u7684\u540d\u5bbf\uff0c\u7b2c\u4e8c\u4e2a\u662f\u8303  \uff0c\u4ee5\u5927\u529b \u91d1\u521a\u624b\u540d\u95fb\u6c5f\u6e56\uff0c\u7b2c\u53c1\u4e2a\u5374\u662f\u4e2a\u5eff\u516d\u4e03\u5c81\u7684\u5c11\u5e74\uff0c\u540d\u53eb\u7389\u9762\u5996\u72d0\u51cc\u9704\uff0c\u51fa\u9053\u672a\u6709\u51e0\u5e74\uff0c\u662f\u4e2a \u5267\u76d7\u3002\u7b2c\u56db\u4e2a\u662f\u9053\u58eb\uff0c\u540d\u53eb\u9752\u677e\u9053\u4eba\u3002\u8fd9\u56db\u4eba\u90fd\u662f\u6c5f\u6e56\u4e0a\u7684\u6210\u540d\u4eba\u7269\u3002\u5353\u4e00\u822a\u5fc3\u60f3\uff1a\u8fd9\u8d75\u633a \u8303  \u4e5f\u8fd8\u7f62\u4e86\uff0c\u9752\u677e\u9053\u4eba\u6211\u4e0d\u77e5\u4ed6\u7684\u6765\u5386\uff0c\u90a3\u7389\u9762\u5996\u72d0\u5374\u4e0d\u662f\u4e2a\u6b63\u6d3e\u7684\u4eba\u3002\u90d1\u6d2a\u53f0\u4e3a\u4f55\u628a\u8fd9 \u4e9b\u53c1\u5c71\u4e94\u5cb3\u7684\u4eba\u9a6c\u90fd\u7ea6\u5728\u4e00\u8d77\u3002 \u3000\u3000\u90d1\u6d2a\u53f0\u9053\uff1a\u201c\u5353\u5144\uff0c\u660e\u665a\u6211\u4eec\u5728\u534e\u5c71\u7edd\u9876\uff0c\u6597\u4e00\u6597\u90a3\u5973\u9b54\u5934\uff0c\u54b1\u4eec\u5148\u7ec3\u4e60\u4e00\u4e0b\u9635\u5f0f\u3002\u201d \u5353\u4e00\u822a\u9053\uff1a\u201c\u4ec0\u9ebd\u9635\u5f0f\uff1f\u201d\u90d1\u6d2a\u53f0\u9053\uff1a\u201c\u6211\u4eec\u672c\u7ea6\u597d\u4e03\u4eba\uff0c\u6bcf\u4eba\u90fd\u4e0d\u540c\u6d3e\u522b\uff0c\u5404\u6709\u7279\u6b8a\u6b66 \u529f\uff0c\u51c6\u5907\u5728\u5408\u6597\u7389\u7f57\u5239\u4e4b\u65f6\uff0c\u4e92\u76f8\u914d\u5408\uff0c\u76f8\u8f85\u76f8\u6210\uff0c\u56e0\u4e3a\u5927\u5bb6\u6b66\u529f\u4e0d\u540c\uff0c\u53c8\u8981\u914d\u5408\u5f97\u5999\u3002\u6240 \u4ee5\u8981\u9884\u5148\u64cd\u7ec3\u3002\u73b0\u5728\u6211\u4eec\u7ea6\u5b9a\u7684\u58eb\u4eba\uff0c\u6709\u4e00\u4e2a\u4e34\u65f6\u6709\u4e8b\uff0c\u4e0d\u80fd\u8d76\u6765\uff0c\u56e0\u6b64\u4e00\u5b9a\u8981\u5353\u5144\u52a0\u5165\uff0c \u624d\u80fd\u51d1\u6570\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f46\u73b0\u5728\u8fde\u6211\u5728\u5185\uff0c\u4e5f  \u6709\u516d\u4eba\u3002\u201d\u90d1\u6d2a\u53f0\u9053\uff1a\u201c\u6211\u4eec\u7684\u5927\u54e5\uff0c\u660e \u665a\u8981\u7b97\u51c6\u65f6\u523b\u624d\u6765\uff0c\u8fd9\u9635\u5f0f\u5c31\u662f\u4ed6\u7814\u7a76\u51fa\u6765\u7684\uff0c\u6240\u4ee5\u4e0d\u5fc5\u7b49\u4ed6\u3002\u201d\u5353\u4e00\u822a\u5fc3\u60f3\uff1a\u4e5f\u597d\uff0c\u770b\u4ed6 \u600e\u6837\u7ec3\u6cd5\u3002\u90d1\u6d2a\u53f0\u516d\u4eba\u6392\u6210\u4e00\u4e2a\u5706\u5708\uff0c\u9996\u5c3e\u76f8\u5e94\uff0c\u8bf4\u9053\uff1a\u201c\u6b66\u529f\u7684\u7384\u5999\uff0c\u5c31\u5168\u5728\u65f6\u95f4\u8981\u62ff\u634f \u5f97\u6070\u5230\u597d\u5904\uff0c\u6bd4\u5982\u4f60\u8fd9\u4e00\u62db\uff0c\u672c\u6765\u6781\u4e3a\u8f9b\u8fa3\uff0c\u4f46\u53d1\u5f97\u8fc7\u65e9\uff0c\u654c\u4eba\u4fbf\u80fd\u6709\u9980\u6687\u5e94\u4ed8\uff0c\u53d1\u5f97\u8fc7 \u8fdf\uff0c\u65b9\u4f4d\u5df2\u53d8\uff0c\u654c\u4eba\u53c8\u4fbf\u53ef\u4ee5\u8d81\u4f60\u62db\u8001\u53cd\u51fb\uff0c\u6240\u8c13\u5dee\u4e4b\u6beb  \uff0c\u8c2c\u4ee5\u5343\u91cc\uff0c\u5c31\u662f\u8fd9\u4e2a\u9053\u7406\u3002\u8fd9 \u9053\u7406\u867d\u7136\u7b80\u5355\uff0c\u4f46\u8981\u5b9e\u884c\u5374\u4e0d\u5bb9\u6613\u3002\u975e\u6709\u7089\u706b\u7eaf\u9752\u7684\u6b66\u529f\uff0c\u51fa\u795e\u5165\u5316\u7684\u672c\u9886\uff0c\u8c08\u4f55\u5bb9\u6613\u3002\u73b0 \u5728\u6211\u4eec\u4e03\u4eba\uff0c\u867d\u7136\u90fd\u662f\u4e00\u6d41\u9ad8\u624b\uff0c\u4f46\u90a3\u7389\u7f57\u5239\u51fa\u624b\u5982\u7535\uff0c\u6211\u4eec\u82e5\u4e0d\u9884\u5148\u7ec3\u597d\uff0c\u5408\u4e03\u4eba\u4e4b\u529b\uff0c \u8981\u80dc\u5979\u4e0d\u96be\uff0c\u8981\u5236\u5979\u6b7b\u547d\uff0c\u5374\u672a\u5fc5\u80fd\u591f\u3002\u6240\u4ee5\u6211\u4eec\u5927\u54e5\uff0c\u7814\u7a76\u51fa\u8fd9\u4e2a\u9635\u5f0f\uff0c\u540d\u4e3a\u4e03\u7edd\u8bdb\u9b54 \u9635\uff0c\u4ee5\u53c1\u4eba\u4f5c\u5148\u950b\uff0c\u53c1\u4eba\u4f5c\u5f8c\u536b\uff0c\u4e00\u4eba\u5f53\u4e2d\u4f5c\u4e3a\u4e3b\u5e05\uff0c\u7b56\u5e94\u56db\u65b9\u3002\u5148\u950b\u5f8c\u536b\uff0c\u4e92\u76f8\u8c03\u6362\uff0c\u9635 \u5bb9\u53d8\u5316\u5947\u8be1\uff0c\u8fd9\u6837\u53c1\u8fdb\u53c1\u9000\uff0c\u6b64\u53bb\u5f7c\u6765\uff0c\u4e2d\u95f4\u53c8\u6709\u4eba\u7b56\u5e94\uff0c\u5fc5\u5f04\u5230\u654c\u4eba\u6beb\u65e0\u5598\u606f\u7684\u53ef\u80fd\uff0c\u7b97 \u5979\u53c1\u5934\u516d\u81c2\uff0c\u4e5f\u96be\u9003\u8131\u3002\u73b0\u5728\u5927\u54e5\u672a\u6765\uff0c\u4e3b\u5e05\u6682\u7f3a\uff0c\u6211\u4eec\u516d\u4eba\u5148\u7ec3\u653b\u51fb\u7684\u914d\u5408\u4e4b\u9053\u3002\u201d\u5c06\u9635 \u52bf\u8bb2\u89e3\u4e4b\u5f8c\uff0c\u628a\u624b\u4e00\u6325\uff0c\u8f6c\u52a8\u8d77\u6765\uff0c\u5148\u950b\u53c1\u4eba\u5404\u53d1\u4e00\u62db\uff0c\u5f8c\u536b\u53c1\u4eba\u8fc5\u5373\u8865\u4e0a\uff0c\u9635\u5f62\u5ffd\u5706\u5ffd \u65b9\uff0c\u5ffd\u5982\u4e00\u5b57\u957f\u86c7\uff0c\u5ffd\u5982\u4e8c\u9f99\u6270\u6d77\uff0c\u5ffd\u800c\u56db\u9762\u5408\u56f4\uff0c\u5ffd\u800c\u5de6\u53f3\u5305\u6284\uff0c\u4f46\u6b65\u4f10\u5374\u662f\u4e1d\u6beb\u4e0d\u4e71\u3002 \u9635\u52bf\u50ac\u52a8\uff0c\u771f\u5982\u957f\u6c5f\u6d6a\u6d8c\uff0c\u5a01\u529b\u60ca\u4eba\u3002\u5353\u4e00\u822a\u672c\u5c31\u806a\u660e\uff0c\u4e0d\u987b\u591a\u65f6\uff0c\u5df2\u662f\u5fc3\u9886\u795e\u4f1a\uff0c\u6697\u60f3\uff1a \u73b0\u5728\u90a3\u4e2a\u4ec0\u9ebd\u201c\u5927\u54e5\u201d\u672a\u6765\uff0c\u5df2\u662f\u8fd9\u822c\u5389\u5bb3\uff0c\u82e5\u7136\u6765\u4e86\uff0c\u4e2d\u95f4\u518d\u6dfb\u4eba\u7b56\u5e94\uff0c\u90a3\u5c31\u771f\u662f\u5929\u7f57\u5730 \u7f51\uff0c\u63d2\u7fc5\u96be\u9003\u4e86\u3002\u4e0d\u77e5\u4ed6\u4eec\u4e0e\u7389\u7f57\u5239\u6709\u4ec0\u9ebd\u6df1\u4ec7\u5927\u6068\uff0c\u4e00\u5b9a\u8981\u5c06\u5979\u7f6e\u65bc\u6b7b\u5730\u3002 \u3000\u3000\u90d1\u6d2a\u53f0\u89c1\u5404\u4eba\u64cd\u7ec3\u5df2\u719f\uff0c\u5c06\u9635\u52bf\u4e00\u6536\uff0c\u7b11\u9053\uff1a\u201c\u5353\u5144\uff0c\u4f60\u8fd9\u624b\u6b66\u5f53\u8fde\u73af\u5251\u914d\u4e0a\u8d75\u5144\u90a3\u624b \u5d69\u9633\u62ab\u98ce\u5251\uff0c\u771f\u662f\u4e3a\u4e03\u7edd\u9635\u589e\u8272\u4e0d\u5c11\u3002\u201d\u968f\u5f8c\u53c8\u8bf4\u4e86\u597d\u4e9b\u7389\u7f57\u5239\u7684\u6076\u884c\uff0c\u65e0\u975e\u662f\u600e\u6837\u6b8b\u5bb3\u6b66 \u6797\u4eba\u7269\u7684\u4e8b\u3002\u5353\u4e00\u822a\u5fc3\u60f3\u7389\u7f57\u5239\u65e2\u7136\u5982\u6b64\u51f6\u66b4\uff0c\u9664\u4e86\u5979\u4e5f\u597d\u3002 \u3000\u3000\u6708\u4eae\u897f\u659c\uff0c\u758f\u661f\u6e10\u9690\uff0c\u90d1\u6d2a\u53f0\u9053\uff1a\u201c\u54b1\u4eec\u56de\u53bb\u5427\uff0c\u660e\u65e5\u5348\u591c\uff0c\u5230\u534e\u5c71\u7389\u5973\u5cf0\u4f1a\u9f50\u3002\u201d\u8bdd \u58f0\u672a\u5b8c\uff0c\u5ffd\u95fb\u5f97\u4e0d\u8fdc\u5904\u4f3c\u6709\u4e00\u58f0\u51b7\u7b11\uff0c\u90d1\u6d2a\u53f0\u5927\u559d\u4e00\u58f0\uff0c\u516d\u4eba\u7eb7\u7eb7\u5411\u7b11\u58f0\u6765\u5904\u6251\u53bb\u3002 \u3000\u3000\u4e00\u9635\u51b7\u98ce\uff0c  \u706b\u660e\u706d\uff0c\u758f\u6797\u53f6\u843d\uff0c\u5bbf\u9e1f\u60ca\u98de\uff0c\u90a3\u91cc\u6709\u4eba\u7684\u5f71\u5b50\u3002\u516d\u4eba\u7eb7\u6270\u4e00\u9635\uff0c\u6beb\u65e0\u6240 \u83b7\u3002\u91d1\u521a\u624b\u8303  \u60ca\u9053\uff1a\u201c\u83ab\u975e\u662f\u7389\u7f57\u5239\u6765\u4f5c\u5f04\u6211\u4eec\uff01\u201d\u9752\u677e\u9053\u4eba\u9053\uff1a\u201c\u4e0d\u50cf\u662f\u5973\u5b50\u7684\u7b11 \u58f0\u3002\u201d\u7389\u9762\u5996\u72d0\u51cc\u9704\u9053\uff1a\u201c\u96be\u9053\u662f\u9b3c\u9b45\uff0c\u9b3c\u9b45\u4e5f\u6ca1\u6709\u8fd9\u6837\u5feb\u7684\u8eab\u6cd5\u3002\u201d\u5d69\u9633\u5251\u5ba2\u8d75\u633a\u9053\uff1a \u201c\u83ab\u975e\u662f\u6211\u4eec\u542c\u9519\u4e86\uff1f\u201d\u90d1\u6d2a\u53f0\u5fc3\u5185\u6697\u60ca\uff0c\u53e3\u4e2d\u4e0d\u8bed\u3002\u5353\u4e00\u822a\u5fc3\u60f3\u4e0d\u77e5\u8fd9\u4eba\u6765\u610f\u5982\u4f55\uff0c\u82e5\u7136 \u662f\u7389\u7f57\u5239\u7684\u5e2e\u624b\uff0c\u90a3\u53ef\u7cdf\u4e86\u3002 \u3000\u3000\u90d1\u6d2a\u53f0\u89c1\u5404\u5165\u795e\u6cae\u6c14\u4e27\uff0c\u5f3a\u4f5c\u5927\u8a00\u9053\uff1a\u201c\u4e0d\u7ba1\u4ed6\u662f\u53cb\u662f\u654c\uff0c\u82e5\u95ef\u8fdb\u6211\u4eec\u7684\u4e03\u7edd\u9635\u4e2d\uff0c\u4e0d \u6b7b\u4e5f\u4f24\uff0c\u4f55\u5fc5\u5bb3\u6015\u3002\u201d\u5176\u5b9e\u4ed6\u81ea\u5df1\u6b63\u662f\u5bb3\u6015\u3002\u5f53\u4e0b\u516d\u4eba\u5206\u6563\uff0c\u90d1\u6d2a\u53f0\u548c\u5353\u4e00\u822a\u56de\u5230\u5ba2\u5bd3\uff0c\u90d1 \u6d2a\u53f0\u53f9\u9053\uff1a\u201c\u82e5\u7136\u662f\u4ee4\u5e08\u80af\u51fa\u5c71\uff0c\u90a3\u5c31\u597d\u529e\u4e86\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u6700\u4e0d\u53d8\u7406\u95f2\u4e8b\u3002\u201d \u90d1\u6d2a\u53f0\u9053\uff1a\u201c\u9002\u624d\u770b\u4f60\u7684\u5251\u6cd5\uff0c\u5df2\u7ecf\u7cbe\u5999\u7edd\u4f26\uff0c\u660e\u665a\u4f60\u4e0e\u5d69\u9633\u5251\u5ba2\u4e92\u4e3a\u950b\u536b\uff0c\u6211\u4eec\u90fd\u8981\u4ef0\u4ed7 \u4f60\u4e86\u3002\u201d\u5353\u4e00\u822a\u542c\u4ed6\u8bed\u6c14\uff0c\u7adf\u4f3c\u62c5\u5fc3\u81ea\u5df1\u4e0d\u80af\u7528\u529b\uff0c\u5f53\u4e0b\u6168\u7136\u8bf4\u9053\uff1a\u201c\u6211\u65e2\u7136\u7b54\u5e94\u5f97\u4f60\uff0c\u5c31 \u7b97\u662f\u7389\u7f57\u5239\u6709\u5929\u5927\u672c\u9886\uff0c\u6211\u4e5f\u7edd\u4e0d\u4e34\u9635\u9000\u7f29\uff01\u201d\u90d1\u6d2a\u53f0\u6025\u9053\uff1a\u201c\u8001\u5f1f\u4f11\u8981\u591a\u5fc3\uff0c\u611a\u5144\u53ea\u662f\u89c1 \u5927\u654c\u5f53\u524d\uff0c\u6240\u4ee5\u4e0d\u5f97\u4e0d\u63d0\u5fc3\u540a\u80c6\u3002\u201d \u3000\u3000\u4e24\u4eba\u6b47\u606f\u4e86\u4e00\u65e5\uff0c\u5403\u8fc7\u665a\u996d\uff0c\u8054\u6289\u6500\u767b\u534e\u5c71\uff0c\u591c\u9759\u6797\u6df1\uff0c\u5d07\u5c97\u6df1\u6da7\uff0c\u85e4\u841d\u906e\u9053\uff0c\u8305\u8349\u9f50 \u8170\uff0c\u6bd4\u767d\u65e5\u767b\u5c71\uff0c\u4f55\u6b62\u8270\u96be\u5341\u500d\u3002\u597d\u5728\u90d1\u6d2a\u53f0\u548c\u5353\u4e00\u822a\u90fd\u662f\u4e0a\u4e0a\u6b66\u529f\uff0c\u6500\u85e4\u9644\u845b\uff0c\u75be\u63a0\u8f7b \u9a70\uff0c\u5230\u4e86\u7389\u5973\u5cf0\u9876\uff0c\u6708\u4eae\u8fd8\u672a\u5230\u5929\u5fc3\u3002 \u3000\u3000\u9752\u677e\u9053\u4eba\u7b49\u56db\u4eba\u5df2\u7ecf\u5728\u5019\uff0c\u9762\u8272\u90fd\u6781\u7d27\u5f20\uff0c\u770b\u90a3\u6708\u4eae\u6162\u6162\u79fb\u52a8\uff0c\u90d1\u6d2a\u53f0\u624b\u5fc3\u6dcc\u6c57\uff0c\u5ffd\u7136 \u8df3\u8d77\u6765\u9053\uff1a\u201c\u770b\u90a3\u6708\u4eae\u3002\u201d\u6708\u4eae\u5f53\u5934\uff0c\u56db\u5468\u4ecd\u662f\u9759\u6084\u6084\u7684\u3002\u9752\u677e\u9053\u4eba\u9053\uff1a\u201c\u7389\u7f57\u5239\u8fd8\u6ca1\u5f71 \u513f\u3002\u201d\u8d75\u633a\u9053\uff1a\u201c\u7389\u7f57\u5239\u8a00\u51fa\u5fc5\u884c\uff0c\u6211\u53ea\u62c5\u5fc3\u5e94\u5927\u54e5\u4e0d\u80fd\u51c6\u65f6\u8d76\u6765\u3002\u201d\u90d1\u6d2a\u53f0\u9053\uff1a\u201c\u5e94\u5927\u54e5 \u7edd\u5bf9\u4e0d\u4f1a\u5931\u7ea6\u3002\u201d\u5353\u4e00\u822a\u542c\u4ed6\u4eec\u5c61\u5c61\u63d0\u8d77\u201c\u5e94\u5927\u54e5\u201d\u4e0d\u89c9\u5fc3\u5ff5\u4e00\u52a8\u3002\u6b63\u60f3\u5f00\u8a00\uff0c\u5ffd\u7136\u4e00\u58f0\u51b7 \u7b11\uff0c\u968f\u7740\u5c71\u98ce\u76f4\u98d8\u4e0b\u6765\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e00\u4e2a\u767d\u8863\u5c11\u5973\uff0c\u76f4\u4f3c\u51cc\u6ce2\u4ed9\u5b50\uff0c\u5189\u5189\u800c\u6765\uff0c\u500f\u5ffd \u4ece\u5bf9\u9762\u5c71\u5cf0\u98d8\u843d\u5230\u7389\u5973\u5cf0\u9876\u3002\u516d\u4eba\u4e00\u9f50\u7ad9\u8d77\uff0c\u5353\u4e00\u822a\u5413\u5f97\u5446\u4e86\u3002 \u3000\u3000\u5353\u4e00\u822a\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\uff1a\u8fd9\u7389\u7f57\u5239\u7adf\u7136\u5c31\u662f\u6628\u65e5\u5728\u534e\u5c71\u9ec4\u9f99\u6d1e\u4e2d\u6240\u89c1\u7684\u5c11\u5973\u2026\u2026\u7ec3\u9713\u88f3\u3002 \u4e00\u65f6\u95f4\u5947\u601d\u5f02\u60f3\u90fd\u4e0a\u5fc3\u5934\uff0c\u604d\u604d\u60da\u60da\uff0c\u673a\u4e4e\u7591\u662f\u4f5c\u68a6\u3002\u6628\u65e5\u8fd8\u662f\u90a3\u9ebd\u695a\u695a\u53ef\u601c\uff0c\u8981\u4eba\u5e87\u62a4\u7684 \u5973\u5b50\uff0c\u96be\u9053\u7adf\u7136\u5c31\u662f\u6c5f\u6e56\u4e0a\u95fb\u540d\u80c6\u843d\uff0c\u6740\u4eba\u4e0d\u7728\u773c\u7684\u7389\u7f57\u5239\uff1f\u81ea\u5df1\u53ef\u8fd8\u7b54\u5e94\u8fc7\u548c\u5979\u505a\u4e2a\u670b \u53cb\uff0c\u91cd\u9022\u65f6\u628a\u5979\u5f53\u6210\u59d0\u59b9\u6b3e\u5f85\u5462\uff01\u60f3\u4e0d\u5230\u4ec5\u9694\u4e00\u5929\uff0c\u5c31\u5728\u8fd9\u6837\u7684\u60c5\u666f\u4e0b\u518d\u89c1\uff01\u800c\u4e14\u4e24\u65b9\u5c45\u7136 \u6210\u4e86\u6b7b\u654c\uff01 \u3000\u3000\u7389\u7f57\u5239\u672c\u6765\u662f\u6c14\u5b9a\u795e\u95f2\uff0c\u4f4e\u9b1f\u6d45\u7b11\uff0c\u79cb\u6ce2\u4e00\u8f6c\uff0c\u5ffd\u7136\u9762\u8272\u60e8\u767d\uff0c\u5fc3\u91cc\u96be\u8fc7\u5230\u6781\uff0c\u4e24\u9897\u6cea \u73e0\u5fcd\u4e0d\u4f4f\u593a\u7736\u800c\u51fa\u3002\u90d1\u6d2a\u53f0\u7ad9\u5728\u524d\u9762\uff0c\u770b\u5f97\u5206\u660e\uff0c\u7389\u7f57\u5239\u7adf\u4f1a\u6d41\u6cea\uff0c\u8fd9\u771f\u662f\u6bd4\u6cf0\u5c71\u5d29\uff0e\u9ec4\u6cb3 \u6e05\u66f4\u4ee4\u4eba\u96be\u4ee5\u7f6e\u4fe1\u7684\u5947\u95fb\uff0c\u7136\u800c\u8fd9\u5374\u4e0d\u662f\u4f20\u95fb\uff0c\u800c\u662f\u81ea\u5df1\u773c\u89c1\u7684\u4e8b\u5b9e\u3002\u7389\u9762\u5996\u72d0\u9675\u9704\u751f\u6027\u8f7b \u8584\uff0c\u53c8\u672a\u66fe\u9886\u6559\u8fc7\u7389\u7f57\u5239\u7684\u5389\u5bb3\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u5230\u9ec4\u6cb3\u5fc3\u4e0d\u6b7b\uff0c\u4e0d\u89c1\u68fa\u6750\u6cea\u4e0d\u6d41\uff0c\u7389\u7f57\u5239\uff0c\u4f60 \u4e56\u4e56\u964d\u987a\uff0c\u54b1\u4eec\u4e5f\u8bb8\u8fd8\u53ef\u4ee5\u9976\u4f60\u3002\u201d\u7389\u7f57\u5239\u9762\u8272\u4e00\u53d8\uff0c\u5ffd\u800c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u76db \u60c5\uff01\u201d\u90d1\u6d2a\u53f0\u6025\u5fd9\u56b7\u9053\uff1a\u201c\u7389\u7f57\u5239\uff0c\u4f60\u4e0d\u80fd\u4e0d\u987e\u6c5f\u6e56\u4fe1\u4e49\uff0c\u65f6\u523b\u672a\u5230\uff0c\u4eba\u8fd8\u672a\u9f50\uff0c\u4f60\u4f11\u52a8 \u624b\u3002\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7389\u9762\u5996\u72d0\u9675\u9704\uff0c\u5ffd\u7136\u6367\u8179\u5927\u53eb\uff0c\u8dc3\u8d77\u4e00\u4e08\u6765\u9ad8\uff0c\u7389\u7f57\u5239\u7684\u72ec\u95e8\u6697\u5668\u5b9a\u5f62\u9488 \u9aa4\u7136\u51fa\u624b\uff0c\u865a\u6253\u9675\u9704\u8170\u9645\u7684\u53c1\u53f0\u7a74\uff0c\u51cc\u9704\u8f7b\u529f\u751a\u9ad8\uff0c\u89c1\u5979\u7ea4\u624b\u4e00\u98a4\uff0c\u6025\u5fd9\u8df3\u8dc3\uff0c\u90a3\u6599\u7389\u7f57\u5239 \u7684\u6697\u5668\u865a\u5b9e\u83ab\u6d4b\uff0c\u65e9\u7b97\u5b9a\u4ed6\u6709\u8fd9\u4e00\u8df3\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u5229\u9488\u5df2\u523a\u4e2d\u4ed6\u811a\u8ddf\u7684\u201c\u6d8c\u6cc9\u7a74\u201d\uff0c\u987f\u65f6\u53c8 \u9178\u53c8\u75db\uff0c\u773c\u6cea\u7adf\u4f3c\u6cc9\u6c34\u4e00\u6837\u7684\u6d41\u4e86\u51fa\u6765\u3002\u9752\u677e\u9053\u4eba\u6025\u5fd9\u66ff\u4ed6\u5c06\u9488\u62d4\u4e0b\uff0c\u63c9\u4e86\u4e24\u63c9\uff0c\u8fd9\u624d\u6ca1 \u4e8b\u3002\u7389\u7f57\u5239\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u4ed6\u662f\u4ece\u4e0d\u6d41\u6cea\u7684\u94c1\u94ee\u94ee\u6c49\u5b50\uff0c\u90a3\u6599\u5982\u6b64\u8113\u5305\u3002\u201d\u7389\u9762\u5996\u72d0\u9675\u9704 \u6ee1\u9762\u7f9e\u60ed\uff0c\u90a3\u6562\u8bf4\u8bdd\uff0c\u53ea\u542c\u5f97\u7389\u7f57\u5239\u53c8\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u9ebd\uff1f\u6211\u662f\u4e3a\u4f60\u4eec\u540a\u4e27\u3002\u53ef\u601c\u6211\u65b0\u4ea4\u7684 \u670b\u53cb\uff0c\u4eca\u65e5\u4e5f\u81ea\u5bfb\u6b7b\u8def\u3002\u201d\u5353\u4e00\u822a\u77e5\u9053\u7389\u7f57\u5239\u8bf4\u4ed6\uff0c\u4e5f\u662f\u611f\u559f\u4ea4\u96c6\uff0c\u5fc3\u60f3\u6211\u4e5f\u53ef\u601c\u4f60\u8fd9\u7edd\u4ee3 \u4f73\u4eba\uff0c\u7518\u5fc3\u4f5c\u8d3c\u3002\u4e03\u7edd\u9635\u5a01\u529b\u65e0\u7a77\uff0c\u4f60\u6b66\u529f\u518d\u9ad8\uff0c\u4eca\u65e5\u4e5f\u8981\u88ab\u8feb\u4e0a\u6b7b\u8def\u3002 \u3000\u3000\u7389\u7f57\u5239\u89c1\u5353\u4e00\u822a\u7709\u5934\u6df1\u9501\uff0c\u5b9a\u775b\u7684\u770b\u7740\u4ed6\uff0c\u4f3c\u6709\u60c5\u53c8\u4f3c\u65e0\u60c5\uff0c\u6068\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\uff0c \u4f60\u2026\u2026\u201d\u8bed\u58f0\u54fd\u54bd\uff0c\u8bf4\u4e0d\u4e0b\u53bb\u3002\u90d1\u6d2a\u53f0\uff0c\u9752\u677e\u9053\u4eba\u7b49\u77e5\u9053\u7389\u7f57\u5239\u559c\u6012\u65e0\u5e38\uff0c\u867d\u4e0d\u77e5\u5979\u610f\u4f55\u6240 \u6307\uff0c\u5c1a\u8fd8\u4e0d\u4ee5\u4e3a\u602a\uff0c\u5176\u4ed6\u53c1\u4eba\u5374\u662f\u83ab\u540d\u5176\u5999\u3002\u5d69\u9633\u5251\u5ba2\u8d75\u633a\u6025\u5fd9\u63a8\u4e86\u90d1\u6d2a\u53f0\u4e00\u4e0b\uff0c\u793a\u610f\u53eb\u4ed6 \u628a\u516d\u4eba\u7684\u5706\u9635\u5148\u6446\u8d77\u6765\uff0c\u9884\u9632\u7389\u7f57\u5239\u8fdb\u88ad\uff0c\u90d1\u6d2a\u53f0\u6b63\u60f3\u8bf4\u7389\u7f57\u5239\u4ece\u4e0d\u5077\u88ad\u3002\u90a3\u6599\u7389\u7f57\u5239\u8d8a\u60f3 \u8d8a\u6068\uff0c\u6068\u5353\u4e00\u822a\u660e\u660e\u4e0e\u5979\u4e3a\u4ec7\uff0c\u6628\u65e5\u5374\u53c8\u9a97\u5979\u3002\u89c1\u4f17\u4eba\u6446\u597d\u9635\u52bf\uff0c\u84e6\u7136\u4e00\u58f0\u957f\u7b11\uff0c\u4e00\u53e3\u5bd2\u5149 \u95ea\u95ea\u7684\u5251\u65e9\u62d4\u5728\u624b\u4e2d\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u73b0\u5728\u5df3\u662f\u5348\u591c\uff0c\u6211\u4e0d\u7b49\u4e86\uff01\u201d\u8eab\u5f62\u5fae\u52a8\uff0c\u75be\u5982\u7535\u95ea\uff0c\u5237 \u7684\u4e00\u5251\u5148\u5411\u90d1\u6d2a\u53f0\u523a\u6765\uff0c\u90d1\u6d2a\u53f0\u4f7f\u7684\u662f\u65e5\u6708\u53cc\u8f6e\uff0c\u65e5\u8f6e\u4e00\u9501\uff0c\u6708\u8f6e\u5e73\u63a8\uff0c\u9635\u5f62\u53d1\u52a8\uff0c\u9752\u677e\u9053 \u4eba\u7684\u6212\u5200\u4ece\u5de6\u9762\u5288\u81f3\uff0c\u9675\u9704\u7684\u5224\u5b98\u7b14\u53c8\u659c\u70b9\u5979\u7684\u201c\u5173\u5143\u7a74\u201d\uff0c\u7389\u7f57\u5239\u7fe9\u7136\u63a0\u51fa\uff0c\u5f8c\u536b\u53c1\u4eba\u4ea4 \u53c9\u66ff\u4e0a\uff0c\u7389\u7f57\u5239\u970d\u5730\u4e00\u4e2a\u6643\u8eab\uff0c\u5251\u950b\u81ea\u8d75\u633a\u80a9\u5934\u63a0\u8fc7\uff0c\u91d1\u521a\u624b\u8303  \u4e00\u4e2a\u5927\u64d2\u62ff\u624b\u62ff\u5979\u4e0d\u7740\uff0c \u5979\u5df2\u7fe9\u5982\u98de\u9e1f\u822c\u7684\u76f4\u5411\u5353\u4e00\u822a\u51b2\u6765\uff0c\u5353\u4e00\u822a\u6025\u4f7f\u8fde\u73af\u5251\u4e2d\u7684\u9632\u8eab\u7edd\u62db\u201c\u7389\u5e26\u56f4\u8170\u201d\uff0c\u5251\u5149\u4e00 \u7ed5\uff0c\u5e26\u5b88\u5e26\u653b\uff0c\u84e6\u89c9\u51b7\u6c14\u68ee\u68ee\uff0c\u4e00\u9053\u94f6\u8679\uff0c\u5288\u9762\u5c04\u81f3\uff01 \u3000\u3000\u5353\u4e00\u822a\u6025\u4f7f\u4e2a\u201c\u65e9\u5730\u62d4\u8471\u201d\uff0c\u7389\u7f57\u5239\u5251\u950b\u970d\u5730\u4ece\u811a\u4e0b\u63a0\u8fc7\uff0c\u8fd9\u8fd8\u662f\u5979\u6545\u610f\u7559\u60c5\uff0c\u8981\u4e0d\u7136 \u5353\u4e00\u822a\u5c31\u8981\u5f53\u573a\u6302\u5f69\u3002\u7389\u7f57\u5239\u970e\u5ffd\u4e4b\u95f4\uff0c\u8fde\u88ad\u516d\u540d\u9ad8\u624b\uff0c\u90d1\u6d2a\u53f0\u5927\u53eb\u201c\u7559\u5fc3\u201d\uff0c\u8f6c\u52a8\u9635\u52bf\uff0c \u628a\u7389\u7f57\u5239\u56f4\u5728\u6838\u5fc3\uff0c\u7389\u7f57\u5239\u5251\u62db\u8f9b\u8fa3\uff0c\u51cc\u5389\u65e0\u524d\uff0c\u8fde\u4e0b\u6740\u624b\uff0c\u5e78\u5728\u516d\u4eba\u9996\u5c3e\u547c\u5e94\uff0c\u4e92\u76f8\u6551 \u62a4\uff0c\u7389\u7f57\u5239\u867d\u7136\u8fde\u62a2\u653b\u52bf\uff0c\u5374\u4e5f\u51b2\u4e0d\u51fa\u91cd\u56f4\u3002\u5353\u4e00\u822a\u5939\u5728\u4f17\u4eba\u4e4b\u4e2d\u88ad\u51fb\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u603b\u8d77\u4e0d \u4e86\u6740\u673a\uff0c\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u5251\uff0c\u53ea\u6c42\u81ea\u4fdd\uff0c\u5e76\u4e0d\u8d2a\u529f\u3002\u800c\u7389\u7f57\u5239\u867d\u5bf9\u4ed6\u6068\u6781\uff0c\u51fa\u624b\u65f6\u4e5f\u4e0d\u77e5\u600e \u7684\uff0c\u603b\u907f\u514d\u523a\u4ed6\u8981\u5bb3\u3002\u516d\u4eba\u5982\u6f6e\u6c34\u822c\u7684\u500f\u8fdb\u500f\u9000\uff0c\u8d8a\u653b\u8d8a\u7d27\u3002\u7389\u7f57\u5239\u56e0\u4e3a\u5c61\u6b21\u5bf9\u5353\u4e00\u822a\u8f7b\u8f7b \u653e\u8fc7\uff0c\u4e0d\u51fa\u8fa3\u62db\uff0c\u53cd\u800c\u9669\u8c61\u73af\u751f\u3002\u6c14\u5f97\u94f6\u7259\u4e00\u54ac\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u65e2\u5982\u6b64\uff0c\u6211\u4e5f\u987e\u4e0d\u5f97\u4f60\u4e86\uff01\u201d \u5251\u6cd5\u4e00\u53d8\uff0c\u7edd\u4e0d\u7559\u60c5\u3002\u6b63\u5f53\u6b64\u9645\uff0c\u84e6\u542c\u5f97\u5c71\u5cf0\u4e0a\u4e00\u58f0\u602a\u5578\uff0c\u4e00\u4e2a\u4e7e\u7626\u8001\u5934\uff0c\u84e6\u7136\u4ece\u5ca9\u77f3\u4e0a\u8dc3 \u4e0b\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u7389\u7f57\u5239\uff0c\u4f60\u600e\u9ebd\u4e0d\u987e\u4fe1\u4e49\uff1f\u201d\u90d1\u6d2a\u53f0\u4e00\u6253\u624b\u52bf\uff0c\u516d\u4eba\u5982\u6f6e\u75be\u9000\uff0c\u7389\u7f57\u5239\u4e5f \u6536\u5251\u8df3\u51fa\u5708\u5b50\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u600e\u9ebd\u4e0d\u5b88\u4fe1\u4e49\uff0c\u4f60\u81ea\u5df1\u8bef\u4e86\u65f6\u523b\u3002\u201d\u90a3\u8001\u5934\u62ac\u5934\u4e00\u770b\uff0c\u6708\u4eae \u521a\u8fc7\u5929\u5fc3\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5c31\u5728\u8fd9\u91cc\u5019\u4f60\u4e86\uff0c\u4f60\u8fde\u6211\u8fd9\u516d\u4e2a\u5144\u5f1f\u7684\u5305\u56f4\u90fd\u51b2\u4e0d\u51fa\uff0c\u6211\u518d\u52a0 \u4eba\u4f60\u8fd8\u5982\u4f55\u5f97\u4e86\uff1f\u201d\u5353\u4e00\u822a\u5fc3\u60f3\uff1a\u8fd9\u4eba\u771f\u662f\u8001\u5978\u5de8\u733e\uff0c\u539f\u6765\u4ed6\u65e9\u4f0f\u5728\u8fd9\u91cc\u5148\u770b\u98ce\u8272\u3002\u770b\u51c6\u6709 \u5341\u6210\u628a\u6371\uff0c\u4ed6\u624d\u51fa\u6765\u3002\u7389\u7f57\u5239\u5ffd\u7136\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5e94\u8001\u8d3c\u4f60\u5bb3\u6b7b\u7f57\u91d1\u5cf0\u5927\u4fa0\uff0c\u81ea\u4ee5\u4e3a\u65e0\u4eba\u77e5\u6653 \u4e86\u9ebd\u4e86\u8fd9\u91cc\u7684\u51e0\u4e2a\u5c0f\u8d3c\uff0c\u90fd\u662f\u7518\u5fc3\u4ece\u4f60\u7684\uff0c\u8fd8\u662f\u4f60\u9a97\u6765\u7684\uff01\u201d\u9752\u677e\u9053\u4eba\u548c\u5d69\u9633\u5251\u5ba2\u8d75\u633a\u5fc3\u4e2d \u4e00\u9707\uff0c\u90a3\u4e7e\u7626\u8001\u5934\u6025\u5fd9\u9a82\u9053\uff1a\u201c\u522b\u542c\u8fd9\u8d3c\u5a46\u5a18\u6311\u62e8\uff01\u5979\u628a\u5ddd\u9655\u7684\u7eff\u6797\u9053\u6b3a\u538b\u5f97\u591f\u4e86\u3002\u53c8\u4f24\u4e86 \u5d69\u9633\u6d3e\u7684\u9556\u5934\uff0c\u6b66\u5f53\u6d3e\u7684\u95e8\u4e0b\u3002\u5979\u6b63\u662f\u6b66\u6797\u516c\u654c\u3002\u54b1\u4eec\u518d\u4e0d\u9664\u5979\uff0c\u5f8c\u5bb3\u65e0\u7a77\uff01\u201d\u62c2\u5c18\u4e00\u4e3e\uff0c \u90d1\u6d2a\u53f0\u6025\u5c06\u9635\u5f62\u518d\u5c55\uff0c\u91cd\u628a\u7389\u7f57\u5239\u56f4\u5728\u6838\u5fc3\uff0c\u8fd9\u756a\u201c\u4e03\u7edd\u9635\u201d\u4eba\u6570\u5df2\u9f50\uff0c\u90a3\u4e7e\u7626\u8001\u5934\u5c45\u4e2d\u7b56 \u5e94\uff0c\u4e00\u67c4\u62c2\u5c18\uff0c\u5ffd\u5f53\u4e94\u884c\u5251\u4f7f\uff0c\u5ffd\u5f53\u95ed\u7a74\u53a5\u7528\uff0c\u795e\u5999\u65e0\u65b9\u3002\u7389\u7f57\u5239\u51dd\u795e\u5e94\u654c\uff0c\u7adf\u4e0d\u80fd\u5206\u5fc3\u8bf4 \u8bdd\u3002 \u3000\u3000\u9752\u677e\u9053\u4eba\u3001\u8d75\u633a\u548c\u7f57\u91d1\u8702\u672c\u6709\u4ea4\u60c5\uff0c\u88ab\u7389\u7f57\u5239\u4e00\u559d\uff0c\u5fc3\u4e2d\u4e5f\u81ea\u8d77\u7591\uff0c\u4f46\u4e00\u60f3\u5230\u7389\u7f57\u5239\u5fc3 \u72e0\u624b\u8fa3\uff0c\u5374\u66f4\u5bd2\u5fc3\uff0c\u52bf\u6210\u9a91\u864e\uff0c\u4e0d\u5f97\u4e0d\u62da\uff0c\u9635\u5f62\u53d8\u5316\u65e0\u5e38\uff0c\u4e03\u540d\u9ad8\u624b\uff0c\u5404\u4f7f\u72ec\u95e8\u6b66\u529f\uff0c\u628a\u7389 \u7f57\u5239\u6740\u5f97\u9999\u6c57\u6dcb  \uff0c\u7389\u7f57\u5239\u5fc3\u9ad8\u6c14\u50b2\uff0c\u672c\u6765\u4ee5\u4e3a\u4ed6\u4eec\u96c6\u4e03\u4eba\u4e4b\u529b\uff0c\u81ea\u5df2\u4e5f\u4e0d\u81f4\u843d\u8d25\u3002\u90a3\u77e5\u4ed6 \u4eec\u5374\u60f3\u51fa\u8fd9\u6837\u53e4\u602a\u7684\u6253\u6cd5\u3002\u8d8a\u6218\u8d8a\u5371\uff0c\u8d8a\u6253\u8d8a\u9669\uff0c\u81ea\u77e5\u8fd9\u6b21\u4e07\u96be\u8131\u9003\uff0c\u4f46\u5979\u5374\u770b\u51fa\u8fd9\u4e03\u4eba \u4e2d\uff0c\u53ea\u6709\u5353\u4e00\u822a\u8fd8\u672a\u5c3d\u5168\u529b\uff0c\u4e0d\u662f\u62da\u547d\u7684\u6837\u513f\uff0c\u5237\u5237\u4e24\u5251\uff0c\u6c64\u5f00\u653b\u6765\u7684\u5175\u5203\uff0c\u5f85\u5353\u4e00\u822a\u4e00\u5251 \u523a\u6765\u65f6\uff0c\u5979\u628a\u5251\u4e00\u5f15\uff0c\u5f3a\u7528\u5185\u529b\u5c06\u5353\u4e00\u822a\u62c9\u5f97\u4e0e\u5979\u8d34\u8eab\u800c\u8fc7\uff0c\u5728\u4ed6\u8033\u8fb9\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u7518 \u5fc3\u4e3a\u864e\u4f5c\u4f25\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u5fc3\u4e2d\u4e00\u51db\uff0c\u90a3\u4e7e\u7626\u8001\u5934\u7684\u94c1\u62c2\u5c18\u5df2\u75be\u5fd9\u66ff\u4ed6\u89e3\u5f00\u4e86\u7389\u7f57\u5239\u7684\u5251\u62db\u3002 \u3000\u3000\u7389\u7f57\u5239\u4e5f\u4e0d\u77e5\u5353\u4e00\u822a\u662f\u5426\u542c\u5f97\u6e05\u695a\uff0c\u4f46\u89c1\u4ed6\u5251\u52bf\u4e00\u7f13\uff0c\u811a\u6b65\u8fdf\u6ede\uff0c\u7389\u7f57\u5239\u4f55\u7b49\u5389\u5bb3\uff0c\u8d81 \u9635\u52bf\u5c1a\u672a\u5408\u56f4\uff0c\u5237\u7684\u4e00\u5251\u5c06\u91d1\u521a\u624b\u8303  \u523a\u4f24\uff0c\u90a3\u4e7e\u7626\u8001\u5934\u6025\u628a\u62c2\u5c18\u4e00\u5377\uff0c\u5c01\u4f4f\u4e86\u7389\u7f57\u5239\u9000 \u8def\uff0c\u90d1\u6d2a\u53f0\u53cc\u8f6e\u4e00\u63a8\u4e00\u9501\uff0c\u8865\u4e0a\u7a7a\u7f3a\uff0c\u5708\u5b50\u8d8a\u6536\u8d8a\u7d27\u3002\u8303  \u867d\u7136\u4e2d\u5251\uff0c\u4f24\u52bf\u4e0d\u91cd\uff0c\u6012\u543c\u5982 \u96f7\uff0c\u4ecd\u7136\u731b\u6251\u3002\u90a3\u4e7e\u7626\u8001\u5934\u89c1\u5353\u4e00\u822a\u5251\u6cd5\u7cbe\u5999\uff01\u5374\u65e0\u6545\u8fdf\u7f13\uff0c\u8d77\u4e86\u7591\u5fc3\uff0c\u6b63\u60f3\u95f4\u4ed6\u3002\u5353\u4e00\u822a \u5237\u5237\u4e24\u5251\uff0c\u6321\u8fc7\u4e86\u7389\u7f57\u5239\u7684\u653b\u51fb\uff0c\u9000\u4e0b\u65f6\u5ffd\u7136\u5728\u4e7e\u7626\u8001\u5934\u8033\u8fb9\u53eb\u9053\uff1a\u201c\u5e94\u4fee\u9633\u5148\u8f88\uff01\u201d\u4e7e\u7626 \u8001\u5934\u7a81\u542c\u5f97\u4ed6\u53eb\u81ea\u5df1\u540d\u5b57\uff0c\u5fd9\u4e2d\u6709\u77e2\uff0c\u5e94\u4e86\u4e00\u58f0\uff0c\u53ea\u9053\u4ed6\u662f\u90d1\u6d2a\u5408\u7ea6\u6765\u7684\u4eba\uff0c\u672a\u89c1\u8fc7\u81ea\u5df1\uff0c \u6240\u4ee5\u60f3\u901a\u540d\u81f4\u610f\u3002\u6b63\u60f3\u5429\u5490\u4ed6\u5c0f\u5fc3\u5e94\u654c\uff0c\u90a3\u6599\u5353\u4e00\u822a\u5237\u7684\u4e00\u5251\u523a\u6765\uff01 \u3000\u3000\u5e94\u4fee\u9633\u5403\u60ca\u4e0d\u5c0f\uff0c\u8eab\u5b50\u9661\u7136\u4e00\u7f29\uff0c\u559d\u9053\uff1a\u201c\u4f60\u75af\u4e86\u5417\uff1f\u201d\u5353\u4e00\u822a\u8fd0\u5251\u5982\u98ce\uff0c\u5927\u58f0\u559d\u9053\uff1a \u201c\u6211\u5148\u6740\u4f60\u8fd9\u79c1\u901a\u6ee1\u6d32\u7684\u5978\u8d3c\uff01\u201d\u5e94\u4fee\u9633\u8eab\u8eaf\u4e00\u9707\uff0c\u94c1\u62c2\u5c18\u547c\u7684\u5377\u53bb\uff0c\u7389\u7f57\u5239\u5389\u58f0\u65a5\u9053\uff1a \u201c\u539f\u6765\u4f60\u8fd9  \u679c\u662f\u79c1\u901a\u6ee1\u6d32\uff01\u201d\u5251\u52bf\u5982\u8679\uff0c\u5411\u5e94\u4fee\u9633\u75be\u523a\uff0c\u90d1\u6d2a\u53f0\u548c\u8d75\u633a\u6025\u5fd9\u5de6\u53f3\u6551\u62a4\u3002\u7389 \u9762\u5996\u72d0\u51cc\u9704\u53cc\u7b14\u75be\u4f38\uff0c\u6025\u70b9\u5353\u4e00\u822a\u5f8c\u5fc3\u7684\u201c\u5fd7\u5802\u7a74\u201d\uff0c\u5353\u4e00\u822a\u53cd\u624b\u4e00\u5251\uff0c\u548c\u4ed6  \u6740\u8d77\u6765\uff01 \u3000\u3000\u8fd9\u4e00\u6765\u9635\u52bf\u5927\u4e71\uff0c\u53d8\u6210\u4e86\u7389\u7f57\u5239\u4e0e\u5353\u4e00\u822a\u5e76\u80a9\u8054\u5251\uff0c\u5408\u6218\u5e94\u4fee\u9633\u4e0e\u90d1\u6d2a\u53f0\u516d\u4e2a\u9ad8\u624b\u3002\u90d1 \u6d2a\u53f0\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5353\u4e00\u822a\u4f60\u662f\u5b98\u5bb6\u5b50\u5f1f\uff0c\u5982\u4f55\u53cd\u53bb\u5e2e\u90a3\u8d3c\u4eba\uff0c\u592a\u5b50\u9762\u524d\uff0c\u4f60\u5982\u4f55\u4ea4\u4ee3\uff01\u201d\u7389 \u7f57\u5239\u7b11\u9053\uff1a\u201c\u4f60\u5174\u5e94\u4fee\u9633\u7ed3\u4e3a\u5144\u5f1f\uff0c\u4e00\u4e2a\u5954\u8d70\u5173\u5916\uff0c\u4e00\u4e2a\u85cf\u5728\u5bab\u5185\u3002\u4ed6\u79c1\u901a\u6ee1\u6d32\uff0c\u4f60\u4e5f\u8131\u4e0d \u4e86\u5173\u7cfb\u3002\u201d\u5b9d\u5251\u4e00\u6296\uff0c\u5bd2\u5149\u7535\u8098\uff0c\u53ea\u89c1\u56db\u9762\u516b\u65b9\u90fd\u662f\u7389\u7f57\u5239\u7684\u5f71\u5b50\uff0c\u5353\u4e00\u822a\u5251\u8d70\u8fde\u73af\uff0c\u4e5f\u5728 \u7389\u7f57\u5239\u7684\u5251\u5149\u63a9\u62a4\u4e4b\u4e0b\uff0c\u7740\u7740\u62a2\u653b\u3002\u6218\u4e86\u7247\u523b\uff0c\u91d1\u521a\u624b\u8303  \u8d1f\u4f24\u6c14\u9981\uff0c\u7ed9\u7389\u7f57\u5239\u4e00\u5251\u524a\u53bb\u56db \u53ea\u6307\u5934\uff0c\u60e8\u53eb\u4e00\u58f0\uff0c\u614c\u5fd9\u9000\u65f6\uff0c\u7389\u7f57\u5239\u7a81\u7136\u51cc\u7a7a\u4e00\u8dc3\uff0c\u53f3\u624b\u957f\u5251\uff0c\u5728\u534a\u7a7a\u4e2d\u821e\u4e2a\u5706\u5708\uff0c\u628a\u90d1 \u6d2a\u53f0\u7b49\u51e0\u4eba\u7684\u5175\u5668\u6c64\u5f00\uff0c\u5de6\u624b\u4e00\u6293\uff0c\u6070\u4f3c\u82cd\u9e70\u6251\u5154\uff0c\u628a\u8303  \u4e00\u628a\u6293\u8d77\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u7684\u91d1\u521a\u624b \u4e0d\u5982\u6211\u7684\u3002\u201d\u5411\u5916\u4e00\u7529\uff0c\u7adf\u7136\u628a\u8303  \u7684\u8eab\u8eaf\u4ece\u534e\u5c71\u7edd\u9876\u76f4\u629b\u4e0b\u53bb\uff0c\u5c71\u98ce\u6012\u53f7\u4e2d\u9690\u9690\u542c\u89c1\u51cc\u5389 \u7684\u60e8\u53eb\uff0c\u90d1\u6d2a\u53f0\u7b49\u4e0d\u5bd2\u800c\u6817\u3002\u7389\u7f57\u5239\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u5de6\u4e00\u5251\uff0c\u53f3\u4e00\u5251\uff0c\u524d\u4e00\u5251\uff0c\u5f8c\u4e00 \u5251\uff0c\u5251\u5251\u8f9b\u8fa3\u3002\u66f4\u52a0\u4e0a\u5353\u4e00\u822a\u7684\u4e03\u5341\u4e8c\u624b\u6b66\u5f53\u5251\u6cd5\uff0c\u56de\u73af\u8fd0\u7528\uff0c\u5947\u6b63\u76f8\u751f\uff0c\u4e5f\u662f\u5389\u5bb3\u5f02\u5e38\u3002 \u5e94\u4fee\u9633\u7b49\u6b63\u4eba\u867d\u662f\u4e00\u6d41\u9ad8\u624b\uff0c\u7adf\u662f  \u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\u3002\u6218\u5230\u5206\u9645\uff0c\u7389\u7f57\u5239\u7a81\u7136\u559d \u9053\uff1a\u201c\u6211\u8981\u5927\u5f00\u6740\u6212\u4e86\uff0c\u9752\u677e\u9053\u4eba\u548c\u5d69\u9633\u5251\u5ba2\uff0c\u4f60\u4eec\u672c\u662f\u6b63\u6d3e\u4e4b\u4eba\uff0c\u82e5\u518d\u4e0d\u77e5\u8fdb\u9000\uff0c\u53ef\u8981\u7389 \u77f3\u4ff1\u788e\u4e86\u3002\u201d\u7389\u7f57\u5239\u8fd9\u4e00\u559d\uff0c\u4e0d\u557b\u7ed9\u4ed6\u4eec\u6307\u51fa\u4e00\u6761\u751f\u8def\uff0c\u9752\u677e\u9053\u4eba\u548c\u8d75\u633a\u500f\u7684\u6536\u5251\u8df3\u51fa\u5708 \u5b50\uff0c\u9053\u4e86\u58f0\u8c22\uff0c\u75be\u5fd9\u98de\u8dd1\u4e0b\u5c71\u3002\u5e94\u4fee\u9633\u9762\u8272\u60e8\u767d\uff0c\u90d1\u6d2a\u53f0\u80c6\u6218\u5fc3\u60ca\uff0c\u7389\u7f57\u5239\u4e00\u5251\u5feb\u4f3c\u4e00\u5251\uff0c \u5e94\u4fee\u9633\u7a81\u7136\u5411\u5f8c\u4e00\u7eb5\uff0c\u4e00\u6296\u624b\u53d1\u51fa\u4e94\u67c4\u98de\u5200\uff0c\u95ea\u7535\u822c\u7684\u5411\u7389\u7f57\u5239\u6253\u53bb\uff01 \u3000\u3000\u7389\u7f57\u5239\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u4e9b\u5e9f\u94dc\u70c2\u94c1\u8981\u6765\u4f55\u7528\uff1f\u201d\u957f\u5251\u4e00\u65cb\uff0c\u4e94\u67c4\u5200\u5168\u90fd\u6298\u65ad\uff0c\u53cd\u5c04\u56de\u53bb\u3002 \u90a3\u77e5\u5e94\u4fee\u9633\u660e\u662f\u8fdb\u653b\uff0c\u5b9e\u662f\u63a9\u62a4\uff0c\u98de\u5200\u4e00\u653e\u4e4b\u5f8c\uff0c\u8fc5\u5373\u548c\u8863\u4e00\u6eda\uff0c\u7adf\u7136\u4ece\u534e\u5c71\u7edd\u9876\uff0c\u76f4\u6eda\u4e0b \u53bb\u3002\u90d1\u6d2a\u53f0\u53cc\u8f6e\u4e00\u64a4\uff0c\u9aa4\u7684\u8dc3\u8d77\u4e00\u4e08\u6765\u9ad8\uff0c\u4e5f\u60f3\u6b65\u5e94\u4fee\u9633\u7684\u5f8c\u5c18\u9003\u8d70\uff0c\u7389\u7f57\u5239\u559d\u9053\uff1a\u201c\u90a3\u91cc \u9003\uff1f\u201d\u90a3\u8fb9\u53a2\u7389\u9762\u5996\u72d0\u9675\u9704\u4e5f\u865a\u6643\u4e00\u62db\uff0c\u8eab\u5f62\u75be\u8d77\uff0c\u5411\u53e6\u4e00\u8fb9\u9003\u8dd1\u3002\u7389\u9762\u5996\u72d0\u6b66\u529f\u5728\u90d1\u6d2a\u53f0 \u4e4b\u4e0b\uff0c\u8f7b\u529f\u5374\u5728\u90d1\u6d2a\u53f0\u4e4b\u4e0a\uff0c\u7389\u7f57\u5239\u662f\u4e2a\u5927\u884c\u5bb6\uff0c\u4e00\u770b\u4fbf\u77e5\uff0c\u4e5f\u6068\u7389\u9762\u5996\u72d0\u521a\u624d\u53e3\u820c\u8f7b\u8584\uff0c \u7eb5\u8eab\u8ffd\u53bb\uff0c\u7389\u624b\u4e00\u626c\uff0c\u53c1\u679a\u201c\u5b9a\u5f62\u9488\u201d\u5168\u90fd\u5c04\u5165\u51cc\u9704\u7684\u7a74\u9053\uff0c\u7389\u9762\u5996\u72d0\u60e8\u53eb\u4e00\u58f0\uff0c\u6447\u6447\u6b32 \u5815\uff0c\u7389\u7f57\u5239\u8d76\u4e0a\u8865\u4ed6\u4e00\u5251\uff0c\u4e00\u811a\u5c06\u4ed6\u7684  \u8eab\u8e22\u4e0b\u5c71\u5cf0\u3002\u5353\u4e00\u822a\u53eb\u9053\uff1a\u201c\u7ec3\u59d1\u5a18\uff0c\u6349\u8fd9\u4e2a\u59d3\u90d1 \u7684\u8981\u7d27\u3002\u201d\u7389\u7f57\u5239\u970d\u7136\u9192\u8d77\uff0c\u63d0\u5251\u8ffd\u65f6\uff0c\u90d1\u6d2a\u53f0\u5df2\u6eda\u4e0b\u5c71\u8170\uff0c\u8fdc\u671b\u53ea\u89c1\u4e00\u4e2a\u9ed1\u70b9\u3002\u7389\u7f57\u5239 \u9053\uff1a\u201c\u8ffd\uff01\u201d\u5ffd\u542c\u5f97\u534a\u5c71\u6709\u4eba\u56b7\u9053\uff1a\u201c\u4e0d\u8981\u5fd9\uff0c\u6211\u5df2\u66ff\u4f60\u628a\u4ed6\u6349\u7740\u4e86\uff01\u201d\u4eba\u8ff9\u4e0d\u89c1\uff0c\u58f0\u97f3\u5374 \u662f\u6781\u5176\u6e05\u695a\uff0c\u7389\u7f57\u5239\u5403\u4e86\u4e00\u60ca\uff1a\u8fd9\u624b\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u5185\u529f\uff0c\u5176\u5b9e\u975e\u540c\u5c0f\u9119\uff01\u8981\u77e5\u4ece\u9ad8\u5904\u53d1 \u58f0\uff0c\u4f4e\u5904\u6613\u65bc\u542c\u89c1\uff0c\u4ece\u4f4e\u5904\u53d1\u58f0\uff0c\u9ad8\u5904\u5374\u96be\u95fb\u6653\u3002\u542c\u8fd9\u4eba\u58f0\u97f3\uff0c\u5e76\u4e0d\u7279\u522b\u5b8f\u4eae\uff0c\u5c31\u50cf\u662f\u5728\u5c71 \u8170\u548c\u4eba\u968f\u4fbf\u8c08\u8bdd\u4e00\u822c\uff0c\u800c\u5374\u5b57\u5b57\u6e05\u6f88\u3002\u7389\u7f57\u5239\u4e5f\u4e0d\u7531\u5f97\u6697\u6697\u4f69\u670d\uff0c\u5b9a\u775b\u770b\u65f6\uff0c\u53ea\u89c1\u4e00\u4eba\u75be\u4f3c \u6d41\u661f\uff0c\u500f\u5ffd\u58f0\u5230\u4eba\u5230\uff0c\u5374\u662f\u4e00\u4e2a\u53c1\u5341\u5c81\u5de6\u53f3\uff0c\u65b9\u9762\u5927\u8033\u7684\u9752\u5e74\u3002\u80c1\u4e0b\u631f\u7740\u4e00\u4eba\uff0c\u4e00\u5230\u5cf0\u9876\uff0c \u7acb\u523b\u653e\u4e0b\uff0c\u88ab\u631f\u7740\u7684\u4eba\u6b63\u662f\u90d1\u6d2a\u53f0\u3002\u8fd9\u4eba\u770b\u4e86\u7389\u7f57\u5239\u4e00\u773c\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u5c31\u662f\u7389\u7f57\u5239\u5417\uff1f\u8fd9\u4f4d \u53c8\u662f\u8c01\uff1f\u201d\u7ec3\u9713\u88f3\u867d\u7136\u4ee5\u7389\u7f57\u5239\u7684\u540d\u5934\u9707\u6151\u6c5f\u6e56\uff0c\u4f46\u5374\u751a\u4e0d\u559c\u6b22\u522b\u4eba\u5f53\u9762\u53eb\u5979\u505a\u201c\u7389\u7f57 \u5239\u201d\u3002\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\uff1f\u201d\u5353\u4e00\u822a\u5374\u606d\u606d\u656c\u656c\u7b54\u9053\uff1a\u201c\u5c0f\u5f1f\u662f\u6b66\u5f53\u6d3e\u638c\u95e8\u7d2b\u9633\u9053\u957f\u95e8 \u4e0b\uff0c\u59d3\u5353\u540d\u4e00\u822a\uff0c\u6562\u95ee\u5144\u53f0\u9ad8\u59d3\u5927\u540d\uff0c\u5e08\u95e8\u5b97\u6d3e\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u5c0f\u5f1f\u540d\u53eb\u5cb3\u9e23\u73c2\uff0c\u54b1\u4eec\u5148\u8c08 \u5927\u4e8b\uff0c\u5f8c\u53d9\u5e08\u95e8\uff0c\u8fd9\u4eba\u4f60\u4eec\u51c6\u5907\u600e\u751f\u53d1\u4ed8\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4ed6\u65e2\u662f\u4f60\u6240\u64d2\u83b7\uff0c\u7531\u4f60\u4f5c\u4e3b\u3002\u201d \u5cb3\u9e23\u73c2\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u53ef\u4e0d\u5fc5\u7167\u9ed1\u9053\u4e0a\u7684\u89c4\u77e9\uff0c\u5bf9\u8fd9\u4eba\u6211\u6240\u77e5\u4e0d\u591a\uff0c\u4ed6\u662f\u5e94\u8001\u8d3c\u7684\u540c\u4f34\u5417\uff1f\u201d \u7389\u7f57\u5239\u8d8a\u53d1\u4e0d\u60a6\u3002\u539f\u6765\u5979\u867d\u662f\u5973\u8d3c\uff0c\u5374\u4e0d\u9ad8\u4e0e\u522b\u4eba\u8bf4\u5979\u662f\u5973\u8d3c\uff0c\u5cb3\u9e23\u73c2\u4e00\u4e0b\u5b50\u63ed\u7a7f\u5979\u6240\u8bf4\u7684 \u662f\u201c\u9ed1\u9053\u4e0a\u7684\u89c4\u77e9\u201d\uff0c\u4e0d\u89c9\u72af\u4e86\u5979\u7684\u5fc3\u75c5\u3002\u5353\u4e00\u822a\u9053\uff1a\u201c\u6b63\u662f\uff0c\u4ed6\u8fd8\u662f\u592a\u5b50\u7684\u4f8d\u536b\uff0c\u4ee5\u524d\u897f \u5382\u7684\u7b2c\u4e00\u9ad8\u624b\u5462\uff01\u201d\u5cb3\u545c\u73c2\u76ef\u4e86\u5353\u4e00\u822a\u4e00\u773c\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u5353\u5144\u539f\u6765\u5c31\u662f\u6628\u665a\u5728\u8352\u90ca\u548c\u4ed6\u4eec \u805a\u4f1a\u7684\u4eba\uff0c\u602a\u4e0d\u5f97\u8fd9\u6837\u719f\u6089\u4ed6\u4eec\u5e95\u8574\u3002\u201d\u5353\u4e00\u822a\u9762\u4e0a\u4e00\u7ea2\uff0c\u8fd9\u624d\u77e5\u9053\u4ed6\u539f\u6765\u5c31\u662f\u6628\u665a\u53d1\u58f0\u51b7 \u7b11\u7684\u602a\u5ba2\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u8bef\u4ea4\u532a\u5f92\uff0c\u60ed\u6127\u4e4b\u6781\uff0c\u90a3\u5e94\u4fee\u9633\u79c1\u901a\u6ee1\u6d32\uff0c\u4ed6\u4e5f\u4e00\u5b9a\u662f\u6ee1\u6d32\u7684 \u5185\u5e94\u3002\u201d\u90d1\u6d2a\u53f0\u5728\u5730\u4e0b\u7ffb\u8eab\u6eda\u52a8\uff0c\u7389\u7f57\u5239\u5ffd\u7136\u4e00\u811a\u5411\u4ed6  \u53bb\uff0c\u539f\u6765\u90d1\u6d2a\u53f0\u81ea\u77e5\u4e0d\u5154\uff0c\u6b63\u60f3\u54ac \u65ad\u820c\u5934\uff0c\u90a3\u77e5\u7389\u7f57\u5239\u719f\u6089\u6c5f\u6e56\u8def\u9053\uff0c\u978b\u5c16\u4e00\u52fe\uff0c\u987f\u65f6\u628a\u4ed6\u4e0b\u989a\u52fe\u88c2\uff0c\u5634\u5df4\u5f20\u5f00\uff0c\u4e0d\u80fd\u5408\u62e2\u3002 \u3000\u3000\u7389\u7f57\u5239\u5148\u4e0d\u7406\u4ed6\uff0c\u5374\u95ee\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u4f1a\u77e5\u9053\u5e94\u4fee\u9633\u79c1\u901a\u6ee1\u6d32\uff1f\u201d\u5353\u4e00\u822a\u4e00\u9635\u8fdf \u7591\uff0c\u4e0d\u6562\u5373\u7b54\u3002\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u5c31\u662f\u6000\u7591\u4ed6\u79c1\u901a\u6ee1\u6d32\uff0c\u6240\u4ee5\u5728\u8fd9\u4e24\u5e74\u4e2d\uff0c\u53c1\u6b21\u6363\u4ed6\u8001\u5de2\uff0c\u8feb \u5f97\u4ed6\u8981\u7ed3\u96c6\u515a\u7fbd\uff0c\u5728\u534e\u5c71\u4e4b\u5dc5\u548c\u6211\u51b3\u6597\u3002\u54fc\uff0c\u60f3\u4e0d\u5230\u4f60\u4e5f\u662f\u4ed6\u7ea6\u6765\u7684\u4eba\u3002\u201d\u5cb3\u9e23\u73c2\u53cc\u7738\u70af \u70af\uff0c\u4e5f\u5c3d\u76ef\u91cf\u7740\u4ed6\u3002\u5353\u4e00\u822a\u5fc3\u60f3\uff1a\u8fd9\u8bef\u4f1a\u53ef\u771f\u5927\u4e86\uff0c\u770b\u90a3\u7389\u7f57\u5239\u867d\u5fc3\u72e0\u624b\u8fa3\uff0c\u5012\u8fd8\u80fd\u8fa8\u9ed1 \u767d\uff0c\u77e5\u662f\u975e\uff0c\u6709\u4e9b\u8c6a\u6c14\u3002\u8fd9\u59d3\u5cb3\u7684\u5c11\u5e74\u4e30\u795e\u4fca\u6717\uff0c\u6b63\u6c14\u51db\u7136\uff0c\u5fc5\u662f\u975e\u5e38\u4e4b\u4eba\u3002\u4ed6\u4eec\u65e2\u7136\u4e5f\u7ea6 \u7565\u77e5\u9053\u6b64\u4e8b\u6839\u7531\uff0c\u800c\u53c8\u5bf9\u6211\u8d77\u7591\uff0c\u90a3\u5c31\u5e94\u5bf9\u4ed6\u4eec\u8bf4\u4e2a\u660e\u767d\u3002\u5f53\u4e0b\u5c06\u5b5f\u6b66\u5e08\u600e\u6837\u4e34\u7ec8\u544a\u5bc6\uff0c\u90d1 \u6d2a\u53f0\u600e\u6837\u7ed3\u4f34\u540c\u884c\u7b49\u7b49\u4e8b\u60c5\u8bf4\u4e86\u3002\u7389\u7f57\u5239\u8fd9\u624d\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e0d\u662f\u90a3\u6837\u7684\u4eba\uff0c\u8981\u4e0d\u7136 \u4f60\u7684\u5c0f\u547d\u65e9\u5c31\u5b8c\u4e86\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u95ee\u660e\u4e86\u5353\u4e00\u822a\u4e4b\u5f8c\uff0c\u7b11\u541f\u541f\u7684\u5bf9\u90d1\u6d2a\u53f0\u9053\uff1a\u201c\u600e\u9ebd\u6837\uff0c\u4e0d\u8212\u670d\u5417\u4e86\u8981\u4e0d\u8981\u6211\u66ff\u4f60 \u6cbb\u5b83\u4e00\u6cbb\uff1f\u201d\u8bed\u58f0\u6e29\u67d4\uff0c\u7adf\u4f3c\u751a\u4e3a\u5173\u5207\u3002\u90d1\u6d2a\u53f0\u4e24\u773c\u7ffb\u767d\uff0c\u5413\u5f97\u9b42\u98de\u5929\u5916\u3002\u7389\u7f57\u5239\u63d0\u8d77\u811a \u6765\uff0c\u53c8\u662f\u5411\u4ed6\u80cc\u5fc3\u8f7b\u8f7b\u4e00  \uff0c\u8fd9\u4e00\u4e0b\u90d1\u6d2a\u53f0\u66f4\u53d7\u4e0d\u4e86\uff0c\u53ea\u89c9\u8eab\u4f53\u5185\u5982\u6709\u5343\u4e07\u6839\u5229\u9488\uff0c\u5728\u4e94\u8155 \u516d\u8151\u91cc\u523a\u5c06\u51fa\u6765\uff0c\u60f3\u65ad\u820c\u81ea\u6740\uff0c\u5634\u5df4\u53c8\u5408\u4e0d\u62e2\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u600e\u9ebd\u6837\uff0c\u8fd8\u4e0d\u62db\u5417\uff1f\u4f60\u5634\u867d\u7136 \u4e0d\u80fd\u8bf4\u8bdd\uff0c\u624b\u6307\u8fd8\u80fd\u52a8\u5f39\uff0c\u5feb\u70b9\u5c06\u4f60\u540c\u515a\u7684\u540d\u5b57\u5728\u5730\u4e0a\u5212\u51fa\u6765\u3002\u8981\u4e0d\u7136\u8fd8\u6709\u597d\u53d7\u7684\u5728\u5f8c\u5934 \u5462\uff01\u201d\u90d1\u6d2a\u5408\u8eab\u4e3a\u897f\u5382\u5934\u76ee\uff0c\u5ba1\u8baf\u72af\u4eba\uff0c\u4ec0\u9ebd\u9177\u5211\u90fd\u66fe\u7528\u8fc7\u3002\u5374\u4e0d\u6599\u5929\u9053\u5faa\u73af\uff0c\u4eca\u65e5\u5374\u88ab\u7389 \u7f57\u5239\u5ba1\u95ee\uff0c\u8eab\u53d7\u6bd4\u4e00\u5207\u9177\u5211\u90fd\u5389\u5bb3\u7684\u75db\u695a\uff0c\u4e0d\u7531\u5f97\u62db\u4e86\u51fa\u6765\uff0c\u7528\u624b\u6307\u5934\u5728\u5730\u4e0a\u6b6a\u6b6a\u659c\u659c\u7684\u5212 \u4e86\u597d\u51e0\u4e2a\u540d\u5b57\uff0c\u7389\u7f57\u5239\u95ee\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u662f\u4ec0\u9ebd\u8eab\u4efd\uff1f\u201d\u90d1\u6d2a\u53f0\u5728\u524d\u9762\u53c1\u4e2a\u540d\u5b57\u4e0b\u6ce8\u4e86\u201c\u5bab\u4e2d \u536b\u58eb\u201d\u56db\u4e2a\u5b57\uff0c\u5728\u5f8c\u9762\u96e8\u4e2a\u540d\u5b57\u4e0b\u6ce8\u4e86\u201c\u7eff\u6797\u5f3a\u76d7\u201d\u56db\u4e2a\u5b57\u3002\u7389\u7f57\u5239\u559d\u95ee\u9053\uff1a\u201c\u8fd8\u6709\u5462\uff1f\u201d \u90d1\u6d2a\u53f0\u6ee1\u5934\u5927\u6c57\uff0c\u53c8\u5199\u51fa\u201c\u6ca1\u6709\u4e86\u201d\u53c1\u4e2a\u5b57\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\uff0c\u8fd8\u6709\u5730\u65b9\u4e0a\u7684\u7763\u629a\u548c\u671d \u4e2d\u7684\u5927\u81e3\u5462\uff1f\u201d\u90d1\u6d2a\u53f0\u6bd4\u5212\u5199\u9053\uff1a\u201c\u6211\u5b9e\u5728\u4e0d\u77e5\u9053\u4e86\u3002\u6ee1\u6d32\u738b\u7237\u6307\u5b9a\u8981\u6211\u8054\u7edc\u7684\u662f\u8fd9\u4e94\u4e2a \u4eba\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u60f3\u9690\u7792\uff1f\u201d\u53c8\u5728\u4ed6\u8170\u80c1\u5904\u8e22\u4e86\u4e00\u811a\uff0c\u90d1\u6d2a\u53f0\u75db\u5f97\u6b7b\u53bb\u6d3b\u6765\uff0c\u5728\u5730 \u4e0a\u7ffb\u817e\u4e86\u597d\u4e00\u9635\u5b50\uff0c\u4f38\u51fa\u6307\u5934\u5411\u5730\u4e0a\u5212\u5b57\uff0c\u4f46\u5374\u662f\u8bb8\u4e45\u8bb8\u4e45\uff0c\u90fd\u672a\u5212\u5f97\u4e00\u5212\uff0c\u597d\u50cf\u662f\u5728\u82e6\u82e6 \u601d\u7d22\u5012\u5e95\u8981\u4f9b\u51fa\u8c01\u4f3c\u7684\u3002\u5353\u4e00\u822a\u4e0d\u7981\u8bf4\u9053\uff1a\u201c\u7ec3\u59d1\u5a18\uff0c\u6211\u770b\u4ed6\u771f\u662f\u4e0d\u77e5\u9053\u4e86\u3002\u4f60\u7528\u9177\u5211\u8feb \u4f9b\uff0c\u53ea\u6050\u4ed6\u4f1a\u80e1\u4e71\u62db\u8ba4\uff0c\u8fde\u7d2f\u4e86\u597d\u4eba\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u77e5\u9053\u4ed6\u662f\u60f3\u80e1\u4e71\u5212\u4f9b\uff1f\u201d\u5353\u4e00 \u822a\u9053\uff1a\u201c\u4f60\u4e0d\u770b\u4ed6\u7684\u795e\u6c14\uff0c\u4ed6\u5206\u660e\u662f\u5728\u5fc3\u91cc\u6bd4\u8f83\uff0c\u770b\u90a3\u4e2a\u5925\u4f34\u548c\u4ed6\u4ea4\u60c5\u5dee\uff0c\u5c31\u62db\u4f9b\u8c01\uff0c\u7ec3\u59d1 \u5a18\uff0c\u6211\u6015\u770b\u4ed6\u8fd9\u4e2a\u6837\u5b50\uff0c\u4f60\u8fd8\u662f\u75db\u75db\u5feb\u5feb\u8d50\u4ed6\u4e00\u6b7b\u5427\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u5012\u6148\u5fc3\uff01\u201d\u4f46\u7ec8\u65bc \u98de\u8d77\u4e00\u811a\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u5411\u4ed6\u80cc\u5fc3\u6b7b\u7a74\u8e22\u53bb\u3002\u90d1\u6d2a\u53f0\u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u53cc\u773c\u4e00\u95ed\uff0c\u7ec8\u65bc\u6b7b \u4e86\u3002\u5353\u4e00\u822a\u5728\u7389\u7f57\u5239\u8033\u8fb9\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u559c\u6b22\u4f60\u8fd9\u6837\u6b8b\u66b4\uff0c\u66f4\u4e0d\u559c\u6b22\u4f60\u8fd9\u6837\u559c\u6012\u53cd\u5e38\uff01\u4f60 \u8fd9\u6837\u8c01\u6562\u4eb2\u8fd1\u4f60\u5462\uff1f\u201d \u3000\u3000\u7389\u7f57\u5239\u6014\u4e86\u4e00\u6014\uff0c\u82e6\u662f\u65c1\u4eba\u8bf4\u8fd9\u6837\u7684\u8bdd\uff0c\u5979\u4e00\u5b9a\u52a8\u6012\uff0c\u4f46\u73b0\u5728\u662f\u5353\u4e00\u822a\u8bf4\u7684\uff0c\u5979\u987f\u5982\u88ab \u6cfc\u4e86\u4e00\u74e2\u51b7\u6c34\uff0c\u5fc3\u60f3\uff1a\u201c\u602a\u4e0d\u5f97\u4eba\u4eec\u90fd\u6015\u6211\uff0c\u6211\u7684\u813e\u6c14\u679c\u7136\u4e0d\u597d\u3002\u53eb\u4eba\u5bb3\u6015\uff0c\u81ea\u5df2\u4e5f\u6ca1\u6709\u4ec0 \u9ebd\u5473\u513f\u3002\u201d\u4f4e\u58f0\u7b54\u9053\uff1a\u201c\u8c22\u8c22\u4f60\u7684\u826f\u8a00\u3002\u201d\u5353\u4e00\u822a\u77a7\u7740\u90d1\u6d2a\u53f0\u7684  \u4f53\uff0c\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u4e0d \u597d\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4ec0\u9ebd\u4e0d\u597d\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u4e0e\u4ed6\u7ed3\u4f34\u51fa\u4eac\uff0c\u540c\u8d74\u9655\u5317\uff0c\u4ed6\u4e0d\u660e\u4e0d\u767d\u7684 \u6b7b\u4e86\uff0c\u592a\u5b50\u5c82\u4e0d\u8981\u8ffd\u7a76\u65bc\u6211\uff01\u201d\u5cb3\u545c\u73c2\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u6613\u529e\u3002\u201d\u62d4\u51fa\u4f69\u5251\uff0c\u4e00\u5251\u628a\u90d1\u6d2a\u53f0\u7684\u5934 \u5272\u4e86\u4e0b\u6765\uff0c\u653e\u5165\u9769\u56ca\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u4e0e\u718a\u7ecf\u7565\u4e43\u662f\u4e16\u4ea4\uff0c\u718a\u7ecf\u7565\u5949\u65e8\u5de1\u8fb9\uff0c\u6709\u51fd\u62db\u5c0f\u5f1f\u53bb\u8944 \u8d5e\u519b\u52a1\u3002\u6211\u6b64\u6b21\u8981\u5230\u4eac\u5e08\u62a5\u5230\uff0c\u7136\u5f8c\u518d\u968f\u718a\u7ecf\u7565\u51fa\u5173\u3002\u5230\u4eac\u5e08\u65f6\uff0c\u6211\u81ea\u6709\u529e\u6cd5\u548c\u592a\u5b50\u8bf4\u660e\u4e00 \u5207\u3002\u201d\u5353\u4e00\u822a\u5927\u559c\u8c22\u4e86\u3002\u6b63\u60f3\u9053\u522b\uff0c\u7389\u7f57\u5239\u5ffd\u9053\uff1a\u201c\u5582\uff0c\u4f60\u5230\u5e95\u662f\u90a3\u4e00\u6d3e\u7684\u9ad8\u4eba\uff0c\u6211\u60f3\u89c1\u8bc6 \u89c1\u8bc6\u4f60\u7684\u6b66\u827a\u3002\u201d\u5cb3\u9e23\u73c2\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u6076\u6218\u4e4b\u5f8c\uff0c\u4f11\u606f\u597d\u4e86\u6ca1\u6709\uff1f\u201d\u7389\u7f57\u5239\u6120\u9053\uff1a\u201c\u968f\u4fbf \u53ef\u966a\u4f60\u6253\u53c1\u4e94\u5929\u3002\u201d\u5cb3\u545c\u73c2\u5f39\u5251\u7b11\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u60f3\u89c1\u8bc6\u4f60\u7684\u6b66\u529f\uff0c\u6211\u8fd8\u4e0d\u5230\u534e\u5c71\u6765\u5462\uff01\u5353 \u5144\uff0c\u9002\u624d\u4f60\u4eec\u95ee\u6211\u7684\u5e08\u95e8\u5b97\u6d3e\uff0c\u7b49\u4f1a\u4f60\u770b\u8fd9\u4f4d\u7389\u7f57\u5239\u4fbf\u77e5\u3002\u201d\u5353\u4e00\u822a\u60ca\u9053\uff1a\u201c\u597d\u7aef\u7aef\u7684\u6bd4\u4ec0 \u9ebd\u5251\uff1f\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u68cb\u9022\u654c\u624b\uff0c\u4e0d\u514d\u6280\u75d2\uff0c\u5353\u5144\uff0c\u4f60\u82e5\u6ca1\u6709\u8981\u4e8b\uff0c\u5c31\u77a7\u77a7\u6211\u4eec\u8fd9\u5c40\u68cb \u5427\u3002\u201d\u7389\u7f57\u5239\u5fc3\u91cc\u6697\u9a82\uff1a\u597d\u4e2a\u4e0d\u77e5\u5389\u5bb3\u7684\u5c0f\u5b50\uff0c\u600e\u89c1\u5f97\u4f60\u5c31\u662f\u6211\u7684\u654c\u624b\uff1f\u62a2\u5230\u4e0b\u9996\uff0c\u7acb\u4e86\u4e00 \u4e2a\u95e8\u6237\uff0c\u6545\u610f\u8ba9\u5cb3\u9e23\u73c2\u5360\u4e86\u6709\u5229\u7684\u4f4d\u7f6e\uff0c\u7b11\u76c8\u76c8\u7684\u4e3e\u5251\u5e73\u80f8\uff0c\u9053\uff1a\u201c\u8bf7\u8fdb\u62db\uff01\u201d \u3000\u3000\u5cb3\u545c\u73c2\u4e0e\u7389\u7f57\u5239\u76f8\u5bf9\u800c\u7acb\uff0c\u5168\u795e\u8d2f\u6ce8\u5bf9\u65b9\uff0c\u4e45\u4e45\u4e0d\u52a8\uff0c\u7a81\u7136\u95f4\u5cb3\u545c\u73c2\u5251\u950b\u4e00\u98a4\uff0c\u559d\u9053\uff1a \u201c\u7559\u795e\uff01\u201d\u5251\u5c16\u5410\u51fa\u83b9\u83b9\u5bd2\u5149\uff0c\u500f\u7684\u5411\u7389\u7f57\u5239\u80a9\u5934\u523a\u53bb\uff0c\u7389\u7f57\u5239\u957f\u5251\u4e00\u5f15\uff0c\u5251\u52bf\u5206\u660e\u5411\u5de6\uff0c \u5374\u7a81\u7136\u5728\u534a\u9014\u8f6c\u4e2a\u5706\u5708\uff0c\u5251\u950b\u53cd\u524a\u5411\u53f3\u3002\u5cb3\u545c\u73c2\u547c\u7684\u4e00\u4e2a\u8f6c\u8eab\uff0c\u5b9d\u5251\u201c\u76d8\u9f99\u75be\u8f6c\u201d\u3002\u7389\u7f57\u5239 \u4e00\u5251\u4ece\u4ed6\u5934\u9876\u524a\u8fc7\uff0c\u800c\u4ed6\u7684\u5251\u62db\u4e5f\u5230\u5f97\u6070\u662f\u65f6\u5019\uff0c\u4e00\u8f6c\u8fc7\u8eab\uff0c\u5251\u950b\u6070\u5bf9\u7740\u7389\u7f57\u5239\u7684\u80f8\u819b\uff0c\u5353 \u4e00\u822a\u9a87\u7136\u9707\u60ca\uff0c\u53ea\u89c1\u90a3\u7389\u7f57\u5239\u51fa\u624b\u5982\u7535\uff0c\u5b9d\u5251\u7a81\u7136\u5f80\u4e0b\u4e00\u62d6\uff0c\u5316\u89e3\u4e86\u5cb3\u545c\u73c2\u7684\u6765\u52bf\uff0c\u5251\u628a\u4e00 \u6296\uff0c\u5251\u8eab\u4e00\u98a4\uff0c\u53cd\u523a\u4e0a\u6765\uff0c\u5251\u5c16\u6296\u52a8\uff0c\u7adf\u7136\u4e0a\u523a\u5cb3\u545c\u73c2\u53cc\u76ee\u3002\u5353\u4e00\u822a\u53c8\u662f\u4e00\u60ca\u3002\u4e0d\u6599\u90a3\u5cb3\u9e23 \u73c2\u53d8\u62db\u5feb\u6377\uff0c\u771f\u662f\u96be\u4ee5\u5f62\u5bb9\uff0c\u6a2a\u5251\u4e00\u63a8\uff0c\u53c8\u628a\u7389\u7f57\u5239\u7684\u5251\u5c01\u4e86\u51fa\u53bb\u3002\u5353\u4e00\u822a\u53ea\u542c\u5f97\u4e24\u4eba\u90fd \u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u518d\u770b\u65f6\u53cc\u5251\u76f8\u4ea4\uff0c\u5df2\u662f\u4e89\u6301\u4e0d\u4e0b\u3002\u5353\u4e00\u822a\u770b\u5f97\u795e\u6447\u76ee\u593a\uff0c\u5ffd\u542c\u5f97\u5cb3\u545c\u73c2\u559d \u58f0\uff1a\u201c\u53bb\uff01\u201d\u7389\u7f57\u5239\u8eab\u5b50\u817e\u7a7a\u98de\u8d77\uff0c\u7136\u800c\u5251\u52bf\u4ecd\u662f\u4e1d\u6beb\u672a\u7f13\uff0c\u7adf\u7136\u4e00\u4e2a\u201c\u98de\u9e1f\u6ca1\u6797\u201d\uff0c\u8fde\u4eba \u5e26\u5251\uff0c\u51cc\u7a7a\u4e0b\u51fb\uff0c\u5cb3\u9e23\u73c2\u4e00\u62db\u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u4e24\u67c4\u5251\u4e92\u76f8\u6fc0\u6c64\uff0c\u7389\u7f57\u5239\u501f\u8fd9\u5251\u5c16\u4e00\u98a4\u4e4b\u529b\uff0c \u6574\u4e2a\u8eab\u5b50\u7ffb\u4e86\u8fc7\u6765\uff0c\u5b9d\u5251\u75be\u5982\u98ce\u53d1\uff0c\u5237\u5237\u51e0\u5251\uff0c\u76f4\u523a\u5cb3\u545c\u73c2\u5f8c\u5fc3\uff0c\u8fd9\u90a3\u91cc\u50cf\u662f\u6bd4\u5251\uff0c\u7b80\u76f4\u6bd4 \u521a\u624d\u5728\u4e03\u7edd\u9635\u4e2d\u7684\u6076\u6218\uff0c\u8fd8\u8981\u60ca\u4eba\uff01 \u3000\u3000\u5353\u4e00\u822a\u6b63\u60f3\u4e0a\u524d\u5316\u89e3\uff0c\u90a3\u5cb3\u545c\u73c2\u53cd\u624b\u4e00\u5251\uff0c\u6321\u4e2a\u6b63\u7740\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u5403\u7389\u7f57\u5239\u4e00\u8fde\u653b\u4e86\u51e0 \u62db\u3002\u5cb3\u545c\u73c2\u8e0f\u6b63\u4e2d\u5bab\uff0c\u6c89\u7a33\u5316\u89e3\uff0c\u5251\u5251\u523a\u5411\u7389\u7f57\u5239\u80f8\u819b\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u53c8\u626d\u6210\u4e86\u5e73\u624b\u5c40\u52bf\u3002\u7389 \u7f57\u5239\u5251\u62db\u602a\u7edd\uff0c\u771f\u662f\u77bb\u4e4b\u5728\u524d\uff0c\u5ffd\u7109\u5728\u5f8c\uff1a\u77bb\u4e4b\u5728\u5de6\uff0c\u5ffd\u7109\u5728\u53f3\u3002\u65f6\u5982\u9e70\u96bc\u51cc\u7a7a\uff0c\u65f6\u5982\u731b\u864e \u4f0f\u5730\uff0c\u65f6\u5982\u6c34\u86c7\u6e38\u8d70\uff0c\u65f6\u5982\u9f99\u8dc3\u6df1\u6e0a\u3002\u8eab\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u5251\u52bf\u8f7b\u7075\u7fd4\u52a8\u3002\u90a3\u5cb3\u9e23\u73c2  \u7136\u4e0d\u60e7\uff0c \u5251\u6cd5\u4e1d\u6beb\u4e0d\u4e71\uff0c\u9022\u62db\u62c6\u62db\uff0c\u653b\u5982\u96f7\u9706\u75be\u53d1\uff0c\u5b88\u5982\u6c5f\u6d77\u51dd\u5149\u3002\u534e\u5c71\u9876\u4e0a\uff0c\u5bd2\u98ce\u730e\u730e\uff0c\u661f\u6708\u65e0 \u8f89\uff0c\u53ea\u89c1\u5251\u6c14\u7eb5\u6a2a\uff0c\u5251\u5149\u8000\u76ee\uff0c\u4e24\u4eba\u8f97\u8f6c\u653b\u62d2\uff0c\u7adf\u7136\u6597\u4e86\u53c1\u767e\u6765\u62db\u3002\u5353\u4e00\u822a\u662f\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2 \u7684\u9ad8\u5f92\uff0c\u770b\u4e86\u4e5f\u4e0d\u7981\u7531\u8877\u4f69\u670d\u3002\u8fd9\u4e24\u4eba\u5251\u6cd5\u7684\u5965\u5999\u795e\u5947\uff0c\u770b\u6765\u7adf\u4f3c\u5728\u6b66\u5f53\u5251\u6cd5\u4e4b\u4e0a\uff0c\u770b\u4e86\u4e00 \u9635\uff0c\u5ffd\u7136\u770b\u51fa\u4e00\u4e2a\u9053\u7406\uff0c\u4e0d\u7981\u8fde\u58f0\u547c\u602a\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u5251\u6cd5\uff0c\u770b\u6765\u7edd\u5bf9\u4e0d\u540c\uff0c\u4f46\u770b\u5f97\u4e45\u4e86\uff0c\u5374\u53c8\u9887\u4f3c\u6709\u76f8\u540c\u4e4b\u5904\uff0c\u90a3\u5cb3\u545c\u73c2\u5251\u6cd5\u6781\u6742\uff0c \u770b\u6765\u6709\u5ce8\u5d4b\u6d3e\u3001\u6709\u5d69\u9633\u6d3e\uff0c\u6709\u5c11\u6797\u6d3e\u7684\uff0c\u8fd8\u6709\u81ea\u5df2\u6b66\u5f53\u6d3e\u7684\uff0c\u6240\u7528\u7684\u90fd\u662f\u5404\u6d3e\u5251\u6cd5\u4e2d\u6700\u7cbe\u5999 \u7684\u62db\u6570\uff0c\u4f46\u5374\u90fd\u7a0d\u52a0\u53d8\u5316\uff0c\u800c\u6240\u53d8\u5316\u7684\u53c8\u4f3c\u6bd4\u539f\u6765\u7684\u5251\u62db\u8fd8\u8981\u4f73\u5999\u3002\u5353\u4e00\u822a\u8fd9\u4e00\u770b\u5f97\u76ca\u4e0d \u5c11\uff0c\u8fd9\u662f\u5f8c\u8bdd\u3002\u800c\u90a3\u7389\u7f57\u5239\u7684\u5251\u6cd5\uff0c\u4e5f\u597d\u50cf\u662f\u535a\u63a2\u5404\u5bb6\uff0c\u4f46\u6bcf\u4e00\u62db\u90fd\u548c\u6b63\u5e38\u7684\u5251\u6cd5\u76f8\u53cd\uff0c\u4f8b \u5982\u534e\u5c71\u6d3e\u4e2d\u7684\u201c\u91d1\u96d5\u5c55\u7fc5\u201d\uff0c\u5251\u52bf\u5e94\u662f\u81ea\u5de6\u81f3\u53f3\uff0c\u5e73\u5c55\u5f00\u6765\uff0c\u800c\u5728\u5979\u624b\u4e2d\uff0c\u5374\u662f\u81ea\u53f3\u81f3\u5de6\u3002 \u53c8\u5982\u6b66\u5f53\u6d3e\u4e2d\u7684\u201c\u65e0\u5e38\u593a\u547d\u201d\u4e00\u62db\uff0c\u5251\u52bf\u5e94\u81ea\u4e0a\u800c\u4e0b\uff0c\u523a\u5411\u4e0b\u76d8\uff0c\u5728\u5979\u624b\u4e2d\uff0c\u5374\u662f\u81ea\u4e0b\u800c \u4e0a\uff0c\u523a\u5411\u4e2d\u76d8\u3002\u90a3\u5cb3\u9e23\u73c2\u5e94\u5979\u7684\u5251\u62db\uff0c\u8d77\u521d\u8fd8\u662f\u4ee5\u53e6\u5916\u7684\u62db\u6570\u5316\u89e3\uff0c\u4f8b\u5982\u7389\u7f57\u5239\u7528\u6b66\u5f53\u6d3e\u7684 \u201c\u65e0\u5e38\u593a\u547d\u201d\uff0c\u4ed6\u5c31\u7528\u96ea\u5c71\u6d3e\u7684\u201c\u660e\u9a7c\u5343\u91cc\u201d\uff0c\u907f\u62db\u8fdb\u62db\u3002\u5230\u5f8c\u6765\u7adf\u662f\u7528\u5979\u672c\u6765\u6a21\u62df\u7684\u62db\u6570 \u6765\u7834\u5979\u7684\u62db\u6570\uff0c\u4f8b\u5982\u5979\u628a\u201c\u91d1\u96d5\u5c55\u7fc5\u201d\u4e00\u62db\uff0c\u53cd\u8f6c\u65b9\u5411\u6765\u4f7f\u8fdb\u62db\uff0c\u4ed6\u4e5f\u5c31\u7528\u6b63\u5b97\u7684\u201c\u91d1\u96d5\u5c55 \u7fc5\u201d\u90a3\u62db\uff0c\u5374\u7565\u52a0\u53d8\u5316\uff0c\u6765\u6321\u5979\u7684\u5251\u62db\u3002\u800c\u4e14\u5c24\u5176\u5947\u602a\u7684\u662f\u7389\u7f57\u5239\u6bcf\u4f7f\u4e00\u62db\uff0c\u4ed6\u90fd\u597d\u50cf\u9884\u5148 \u77e5\u9053\u4f3c\u7684\uff0c\u5f85\u5979\u4e00\u5251\u523a\u6765\uff0c\u4ed6\u5c31\u6070\u6070\u7528\u5230\u5979\u6240\u6a21\u62df\u7684\u90a3\u539f\u6765\u62db\u6570\u5e94\u654c\u3002\u56e0\u6b64\u4e24\u4eba\u867d\u7136\u6597\u5f97\u6781 \u70c8\uff0c\u5374\u662f\u76f8\u6301\u4e0d\u4e0b\u3002\u6b63\u770b\u5f97\u51fa\u795e\uff0c\u5ffd\u53c8\u542c\u5f97\u5cb3\u545c\u73c2\u559d\u58f0\uff1a\u201c\u53bb\uff01\u201d\u7389\u7f57\u5239\u53c8\u98d8\u8eab\u9000\u51fa\u6570\u4e08\uff0c \u6b63\u60f3\u56de\u8eab\u518d\u6597\uff0c\u5cb3\u9e23\u73c2\u53eb\u9053\uff1a\u201c\u518d\u6597\u65e0\u76ca\uff0c\u4f60\u7684\u5e08\u7236\u73b0\u5728\u90a3\u91cc\uff1f\u5979\u6240\u85cf\u7684\u5251\u8c31\u662f\u4e0d\u662f\u90fd\u4f20\u7ed9 \u4f60\u4e86\uff1f\u4f60\u8d76\u5feb\u5bf9\u5979\u8bf4\uff0c\u5929\u90fd\u5c45\u58eb\u7b49\u5979\u76f8\u4f1a\u3002\u201d\u7389\u7f57\u5239\u500f\u7136\u6536\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u5e08\u5a18\u5728\u53c1\u5e74\u524d \u5df2\u53bb\u4e16\u4e86\uff01\u201d\u5cb3\u545c\u73c2\u5927\u5403\u4e00\u9a7e\uff0c\u5b9d\u5251\u626c\u7a7a\u4e00\u5288\uff0c\u53eb\u9053\uff1a\u201c\u662f\u8c01\u628a\u5979\u5bb3\u6b7b\u7684\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a \u201c\u5979\u81ea\u5df1\u8d70\u706b\u5165\u9b54\uff0c\u6492\u624b\u897f\u53bb\uff0c\u4e0e\u4eba\u65e0\u5c24\u3002\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u5979\u7684\u9057\u4f53\u548c\u5251\u8c31\u5462\uff1f\u201d\u7389\u7f57\u5239 \u9053\uff1a\u201c\u5728\u9ec4\u9f99\u6d1e\u5f8c\u6d1e\u7684\u77f3\u5ba4\u4e2d\uff0c\u4f60\u642c\u5f00\u5f8c\u6d1e\u90a3\u4e24\u5757\u5c4f\u98ce\u4f3c\u7684\u5ca9\u77f3\uff0c\u5c31\u627e\u5230\u4e86\u3002\u6211\u5949\u5979\u9057\u547d\uff0c \u5728\u5979\u6b7b\u5f8c\u53c1\u5e74\u7684\u5fcc\u65e5\uff0c\u5df2\u5c06\u5979\u7684\u6b7b\u8baf\uff0c\u544a\u77e5\u4e86\u8d1e\u4e7e\u9053\u957f\uff0c\u672c\u60f3\u6258\u8d1e\u4e7e\u9053\u957f\u8f6c\u544a\u4ee4\u5e08\uff0c\u4f60\u65e2\u6765 \u4e86\uff0c\u5c31\u81ea\u5df1\u53bb\u627e\u5427\uff01\u201d \u3000\u3000\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u8bf7\u4f60\u5e26\u5f15\u3002\u201d\u7389\u7f57\u5239\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u5e76\u80a9\u9ad8\u624b\uff0c\u4e0d\u80fd\u540c\u5728\u4e00\u5730\uff0c\u5341\u5e74\u5f8c\u6211 \u518d\u627e\u4f60\u6bd4\u5251\uff01\u201d\u5411\u5353\u4e00\u822a\u626c\u4e86\u626c\u624b\uff0c\u5c55\u5f00\u7edd\u9876\u8f7b\u529f\uff0c\u7adf\u81ea\u4e0b\u5c71\u53bb\u4e86\u3002\u5cb3\u545c\u73c2\u53f9\u9053\uff1a\u201c\u7389\u7f57\u5239 \u7684\u813e\u6c14\u4e0e\u6211\u5e08\u5a18\u771f\u4e2a\u76f8\u4f3c\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5979\u6b66\u529f\u771f\u9ad8\uff0c\u53ea\u662f\u592a\u9a84\u50b2\u4e86\uff01\u201d\u5cb3\u545c\u73c2\u5ffd\u9053\uff1a \u201c\u9ec4\u9f99\u6d1e\u4e0d\u77e5\u5750\u843d\u4f55\u65b9\uff0c\u534e\u5c71\u4e94\u5cf0\uff0c\u5374\u5230\u90a3\u91cc\u53bb\u627e\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d\u5e26\u5cb3\u545c\u73c2\u4ece \u7389\u5973\u5cf0\u8f6c\u5230\u4e91\u53f0\u5cf0\u90a3\u8fb9\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u8fb9\u884c\u8fb9\u8bf4\uff0c\u5c06\u5e08\u95e8\u7684\u4e00\u6bb5\u60c5\u5b7d\u5bf9\u5353\u4e00\u822a\u8bf4\u4e86\u51fa\u6765\u3002\u539f\u6765\u4ed6\u7684\u5e08\u7236\u970d\u5929\u90fd\u53c1\u5341\u5e74\u524d \u662f\u4e2a\u540d\u95fb\u6d77\u5185\u7684\u5251\u5ba2\uff0c\u59bb\u5b50\u51cc\u6155\u534e\u4e5f\u662f\u5251\u672f\u7684\u5927\u884c\u5bb6\uff0c\u4e24\u4eba\u5728\u5ce8\u5d4b\u5c71\u9876\u7ed3\u5e90\u53cc\u4fee\uff0c\u5ea6\u7684\u771f\u662f \u795e\u4ed9\u5c81\u6708\u3002\u5374\u4e0d\u6599\u51cc\u6155\u534e\u6781\u4e3a\u597d\u80dc\uff0c\u5e38\u5e38\u4e0d\u670d\u4e08\u592b\u3002\u970d\u5929\u90fd\u8d39\u5c3d\u534a\u4e16\u5fc3\u529b\uff0c\u641c\u7f57\u4e86\u5929\u4e0b\u5404\u6d3e \u7684\u5251\u8c31\uff0c\u6f5c\u5fc3\u7a77\u7814\uff0c\u4e00\u65e5\u8c41\u7136\u8d2f\u901a\uff0c\u5bf9\u59bb\u5b50\u9053\uff1a\u201c\u5eff\u5e74\u4e4b\u5f8c\uff0c\u6211\u5c31\u53ef\u4ee5\u628a\u767e\u5bb6\u5251\u6cd5\u6cbb\u65bc\u4e00 \u7089\uff0c\u72ec\u521b\u4e00\u6d3e\uff0c\u5929\u4e0b\u65e0\u654c\u4e86\u3002\u4f60\u5feb\u70b9\u62dc\u6211\u4e3a\u5e08\uff0c\u54b1\u4eec\u5408\u7ec3\u3002\u8981\u4e0d\u7136\u6211\u5c31\u4e0d\u628a\u5fc3\u5f97\u544a\u8bc9\u4f60\u3002\u201d \u8fd9\u672c\u6765\u662f\u592b\u59bb\u95f4\u5f00\u73a9\u7b11\u7684\u8bf4\u6cd5\uff0c\u4e0d\u6599\u51cc\u6155\u534e\u813e\u6c14\u5341\u5206\u5f3a\u9879\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u72ec\u521b\u4e00\u5bb6\uff0c\u6211 \u4e5f\u53ef\u4ee5\u3002\u504f\u4e0d\u62dc\u4f60\u4e3a\u5e08\u3002\u54b1\u4eec\u5eff\u5e74\u5f8c\u518d\u6bd4\u6bd4\u8fc7\uff0c\u770b\u662f\u4f60\u5f3a\uff0c\u8fd8\u662f\u6211\u5f3a\u3002\u201d\u970d\u5929\u90fd\u5f53\u662f\u620f\u8a00\uff0c \u4e00\u7b11\u4f5c\u7f62\u3002\u90a3\u6599\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u59bb\u5b50\u7adf\u7136\u643a\u4e86\u970d\u5929\u90fd\u641c\u7f57\u7684\u5251\u8c31\uff0c\u4e0d\u8f9e\u800c\u884c\u3002\u970d\u5929\u90fd\u5341\u5206\u4f24 \u5fc3\uff0c\u8d70\u5c3d\u5929\u4e0b\u540d\u5c71\u5927\u5ddd\uff0c\u90fd\u5bfb\u5979\u4e0d\u5230\u3002\u4f24\u5fc3\u4e4b\u9980\uff0c\u4e5f\u4e0d\u613f\u518d\u56de\u5ce8\u5d4b\u6545\u5c45\u4e86\u3002\u65bc\u662f\u631f\u5251\u8fdc\u6e38\uff0c \u5230\u4e86\u897f\u5317\uff0c\u7231\u4e0a\u4e86\u5929\u5c71\u96c4\u4f1f\u7684\u5947\u666f\uff0c\u7adf\u7136\u5728\u5929\u5c71\u7684\u5317\u9ad8\u5cf0\u4e0a\u9690\u5c45\u4e0b\u6765\u3002\u5fc3\u60f3\uff1a\u59bb\u5b50\u65e2\u7136\u8981\u72ec \u521b\u4e00\u5bb6\uff0c\u81ea\u5df2\u4e5f\u5e94\u8be5\u7ee7\u7eed\u7814\u7a76\uff0c\u5230\u65e5\u5f8c\u76f8\u89c1\uff0c\u4e5f\u597d\u4e92\u76f8\u5370\u8bc1\u3002\u5251\u8c31\u867d\u5931\uff0c\u4f46\u4ed6\u5df2\u8bb0\u5728\u5fc3\u4e2d\uff0c \u7a77\u5eff\u5e74\u4e4b\u529b\uff0c\u535a\u63a2\u5404\u5bb6\uff0c\u521b\u51fa\u4e00\u8def\u8d85\u51e1\u4eba\u5723\u7684\u5251\u6cd5\uff0c\u9042\u5b9a\u540d\u4e3a\u201c\u5929\u5c71\u5251\u6cd5\u201d\u3002\u5cb3\u545c\u73c2\u662f\u4ed6\u5230 \u5929\u5c71\u4e4b\u5f8c\u7b2c\u53c1\u5e74\u6240\u6536\u7684\u5f1f\u5b50\uff0c\u5cb3\u545c\u73c2\u4e00\u8def\u957f\u5927\uff0c\u4e00\u8def\u5b66\u5251\uff0c\u5e08\u5f92\u4e24\u4eba\u5e38\u5e38\u5c06\u65b0\u7814\u7a76\u7684\u5251\u6cd5\uff0c \u62c6\u62db\u5b9e\u4e60\u3002\u6240\u4ee5\u5929\u5c71\u5251\u6cd5\u7684\u5b8c\u6210\uff0c\u5cb3\u9e23\u73c2\u4e5f\u6709\u4e00\u4efd\u529f\u52b3\u3002\u4e24\u5e74\u524d\uff0c\u970d\u5929\u90fd\u5ffd\u542c\u5f97\u6b66\u6797\u670b\u53cb\u4f20 \u8a00\uff0c\u8bf4\u662f  \u5317\u7eff\u6797\u9053\u4e0a\uff0c\u51fa\u73b0\u4e86\u4e00\u4e2a\u5999\u9f84\u5c11\u5973\uff0c\u6b66\u529f\u7cbe\u5f3a\uff0c\u5251\u6cd5\u5947\u7edd\uff0c\u4e00\u7b97\u5eff\u5e74\u4e4b\u671f\u5df3\u6ee1\uff0c \u5176\u65f6\u5cb3\u545c\u73c2\u5df2\u7ecf\u4e0b\u5c71\uff0c\u970d\u5929\u90fd\u5c06\u4ed6\u62db\u56de\uff0c\u5c06\u5eff\u5e74\u524d\u7684\u4e00\u6bb5\u516c\u6848\u8bf4\u4e0e\u4ed6\u77e5\uff0c\u53eb\u4ed6\u8def\u8fc7  \u897f\u65f6\uff0c \u52a1\u5fc5\u8981\u8bbf\u90a3\u4f4d\u7389\u7f57\u5239\u3002 \u3000\u3000\u8bf4\u81f3\u6b64\u5904\uff0c\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u6240\u4ee5\u6211\u9002\u624d\u5174\u7389\u7f57\u5239\u6bd4\u5251\uff0c\u4e00\u89c1\u5979\u7684\u5251\u52bf\u6070\u6070\u4e0e\u5e08\u7236\u6240\u4f20\u76f8 \u53cd\uff0c\u56e0\u6b64\u6562\u65ad\u5b9a\u5979\u5c31\u662f\u6211\u5e08\u5a18\u7684\u5f92\u5f1f\u3002\u201d\u4e24\u4eba\u8fb9\u8bf4\u8fb9\u884c\uff0c\u4e0d\u89c9\u5df3\u5230\u4e86\u9ec4\u9f99\u6d1e\uff0c\u5353\u4e00\u822a\u9886\u5148\u8fdb \u4eba\u6d1e\u4e2d\uff0c\u4f3c\u89c9\u9057\u9999\u72b9\u5728\uff0c\u8111\u6d77\u4e2d\u4e0d\u89c9\u6cdb\u4e0a\u4e86\u7389\u7f57\u5239\u7684\u4ead\u4ead\u5029\u5f71\uff0c\u9887\u4e3a\u6005\u60d8\u3002\u4e24\u4eba\u4e00\u8def\u884c\u4eba\u5f8c \u6d1e\uff0c\u679c\u7136\u89c1\u6709\u4e24\u5757\u5ca9\u77f3\u5e76\u5217\uff0c\u72b6\u5982\u5c4f\u98ce\u3002\u5cb3\u9e23\u73c2\u594b\u8d77\u5185\u5bb6\u771f\u529b\uff0c\u547c\u547c\u4e24\u638c\uff0c\u5c06\u5ca9\u77f3\u6253\u5f97\u4e24\u8fb9 \u6447\u52a8\uff0c\u987a\u624b\u4e00\u6273\uff0c\u5c06\u5ca9\u77f3\u5411\u5de6\u53f3\u5404\u632a\u52a8\u5c11\u8bb8\uff0c\u4e24\u4eba\u4e3e\u6b65\u4eba\u5185\uff0c\u5ffd\u89c1\u4e00\u4e2a\u9ab7\u9ac5\uff0c\u7aef\u5750\u58c1\u4e0a\u9f9b \u4e2d\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u8dea\u4e0b\u53bb\u78d5\u4e86\u53c1\u4e2a\u54cd\u5934\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u77f3\u58c1\u4e0a\u523b\u6ee1\u4e86\u5404\u79cd\u5251\u6cd5\uff0c\u6253\u8d77\u706b\u77f3\uff0c\u56db\u5904 \u627e\u5bfb\uff0c\u5374\u4e0d\u89c1\u5251\u8c31\u3002\u60f3\u662f\u5e08\u5a18\u719f\u4e60\u4e4b\u5f8c\uff0c\u5df3\u628a\u5b83\u6bc1\u4e86\u3002\u5cb3\u545c\u73c2\u53e9\u5934\u7980\u9053\uff1a\u5e08\u5a18\u5728\u4e0a\uff0c\u4eca\u65e5\u5f1f \u5b50\u8bf7\u4f60\u79fb\u8f6c\u5929\u5c71\u4e0e\u5e08\u7236\u76f8\u89c1\uff0c\u613f\u4f60\u6697\u4e2d\u4fdd\u4f51\uff0c\u4e0d\u8981\u6bc1\u4e86\u6cd5\u4f53\u3002\u5c06\u9ab7\u9ac5\u53d6\u4e86\u4e0b\u6765\uff0c\u5ffd\u89c1\u9f9b\u4e0b\u88c5 \u7740\u4e00\u5377\u7f8a\u76ae\u4e66\uff0c\u4e66\u4e0a\u6ee1\u8f7d\u5404\u79cd\u5251\u5f0f\uff0c\u4e0e\u77f3\u58c1\u4e0a\u6240\u523b\u7684\u76f8\u540c\u3002\u7ffb\u5230\u6700\u5f8c\u51e0\u9875\uff0c\u5374\u662f\u7528\u8840\u5199\u6210\u7684 \u6587\u5b87\u3002\u5cb3\u9e23\u73c2\u7ec6\u8bfb\u4e0b\u53bb\uff0c\u539f\u6765\u662f\u5e08\u5a18\u65ad\u65ad\u7eed\u7eed\u7684\u65e5\u8bb0\u3002\u5934\u4e00\u4e24\u6bb5\u5199\u81ea\u5df1\u4e0e\u4e08\u592b\u522b\u5f8c\uff0c\u600e\u6837\u6df1 \u591c\u5fcf\u6094\uff0c\u6240\u4ee5\u65f6\u65f6\u5348\u591c\u68a6\u56de\uff0c\u5c31\u54ac\u7834\u6307\u5934\uff0c\u6ef4\u8840\u5199\u5b87\u3002\u5e0c\u671b\u5eff\u5e74\u5f8c\u76f8\u89c1\uff0c\u4ee5\u6b64\u65e5\u8bb0\uff0c\u8bc1\u660e\u76f8 \u7231\u4e4b\u6df1\u3002\u5f8c\u9762\u51e0\u6bb5\u5199\u7ec3\u5251\u7684\u8fdb\u5883\u3002\u6709\u4e00\u6bb5\u9053\uff1a \u3000\u3000\u201c\u5929\u90fd\u641c\u7f57\u4e16\u95f4\u5251\u8c31\uff0c\u5fc5\u63a2\u7eb3\u5404\u6d3e\u7cbe\u534e\uff0c\u521b\u4e16\u6b63\u5b97\u5251\u6cd5\uff0c\u4f59\u504f\u53cd\u5176\u9053\u800c\u884c\u4e4b\uff0c\u4ee5\u6c38\u4fdd\u5148 \u624b\uff0c\u96f7\u9706\u75be\u51fb\u4e3a\u4e3b\uff0c\u4eca\u5f8c\u4e16\u5251\u5ba2\uff0c\u77e5\u4e00\u6b63\u4e00\u53cd\uff0c\u4ff1\u8db3\u4ee5\u6c38\u5782\u4e0d\u673d\u4e5f\u3002\u201d \u3000\u3000\u5cb3\u9e23\u73c2\u53f9\u606f\u4e00\u58f0\uff0c\u8df3\u8fc7\u4e00\u9875\uff0c\u5ffd\u89c1\u4e00\u6bb5\u5199\u9053\uff1a \u3000\u3000\u201c\u6628\u591c\u7fa4\u72fc\u997f\u55e5\uff0c\u4f59\u4ed7\u5251\u51fa\u6d1e\uff0c\u5ffd\u95fb\u5973\u5b69\u54ed\u58f0\uff0c\u9a71\u6563\u7fa4\u72fc\uff0c\u5728\u72fc\u7a9f\u4e2d\uff0c\u53d1\u73b0\u5973\u5b69\uff0c\u8eab\u9a71 \u8d64\u88f8\uff0c\u7ea6\u53c1\u56db\u5c81\uff0c\u89c1\u4f59\u6765\u60ca\u6050\u4e07\u72b6\uff0c\u8df3\u8dc3\u5982\u98de\uff0c\u53d1\u97f3\u54bf\u5440\uff0c\u4e0d\u53ef\u8fa8\u8bc6\uff0c\u566b\uff0c\u6b64\u5973\u5b69\u4e43\u7fa4\u72fc\u6240 \u54fa\uff0c\u5c82\u975e\u5f02\u4e8b\u3002\u4f59\u7a77\u641c\u72fc\u7a9f\uff0c\u89c1\u6709\u8863\u5e26\uff0c\u5df2\u5c06\u8150\u70c2\uff0c\u7ec6\u8fa8\u4e4b\uff0c\u5b87\u8ff9\u6a21\u68da\u53ef\u8bfb\uff0c\u59cb\u77e5\u6b64\u5973\u59d3 \u7ec3\uff0c\u7236\u4e3a\u7a77\u5112\uff0c\u9003\u8352\u81f3\u6b64\uff0c\u6bcd\u96be\u4ea7\u6b7b\uff0c\u5176\u7236\u5f03\u65bc\u534e\u5c71\u811a\u4e0b\uff0c\u539f\u5180\u5c71\u4e2d\u5bfa\u50e7\uff0c\u53d1\u73b0\u629a\u517b\uff0c\u4e0d\u610f \u4e43\u4e3a\u6bcd\u72fc\u6308\u53bb\u3002\u610f\u5f97\u4e0d\u6b7b\uff0c\u800c\u53c8\u4e0e\u4f59\u9047\u5408\uff0c\u51a5\u51a5\u4e2d\u5c82\u975e\u6709\u5929\u610f\u4e4e\uff1f\u56e0\u643a\u6b64\u5973\u56de\u6d1e\uff0c\u51b3\u6536\u5176\u4e3a \u5f92\uff0c\u4ed7\u5176\u5148\u5929\u7968\u8d4b\uff0c\u57f9\u5176\u6839\u5143\uff0c\u6388\u5176\u6b66\u529f\uff0c\u4ed6\u65e5\u6216\u5c06\u4e3a\u6211\u6d3e\u653e\u4e00\u5f02\u5f69\u4e5f\u3002\u201d \u3000\u3000\u5cb3\u9e23\u73c2\u62db\u624b\u53eb\u5353\u4e00\u822a\u770b\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u8fd9\u7389\u7f57\u5239\u4e43\u662f\u6bcd\u72fc\u6240\u4e73\u5927\u7684\u3002\u201d\u518d\u770b\u4e0b\u53bb\uff0c\u53c8 \u6709\u4e00\u6bb5\u9053\uff1a\u201c\u7ec3\u5973\u4eca\u65e5\u6bdb\u81ea\u5c3d\u8131\uff0c\u4f59\u4e0b\u5c71\u5e02\u5e03\uff0c\u4e3a\u5176\u88c1\u8863\uff0c\u4f0a\u521d\u5b66\u4eba\u8a00\uff0c\u547c\u4f59\u201c\u5988\u5988\u201d\uff0c\u5fc3 \u4e2d\u6709\u611f\uff0c\u4e0d\u7981\u6cea\u4e0b\u3002\u6b64\u5973\u81ea\u8131\u79bb\u72fc\u7a9f\u4e4b\u5f8c\uff0c\u91ce\u6027\u60ed\u9664\uff0c\u4e0d\u518d\u54ac\u4eba\u556e\u7269\u77e3\u3002\u4f59\u4e3a\u4e4b\u53d6\u540d\u65e5\u9713 \u88f3\u3002\u8bb0\u4f59\u4e3a\u5f7c\u521d\u7f1d\u5f69\u8863\u4e5f\u3002\u201d \u3000\u3000\u4ee5\u5f8c\u53c8\u6709\u4e00\u4e24\u6bb5\u5199\u7ec3\u9713\u88f3\u7ec3\u5251\u7684\u8fdb\u5883\u3002\u6700\u5f8c\u4e00\u6bb5\uff0c\u5b57\u8ff9\u51cc\u4e71\uff0c\u5199\u9053\uff1a \u3000\u3000\u201c\u6628\u665a\u5750\u5173\u6f5c\u4fee\uff0c\u4e60\u7ec3\u5185\u529f\uff0c\u4e0d\u610f\u5669\u68a6\u7a81\u6765\uff0c\u604d\u60da\u6709\u65e0\u6570\u6076\u9b54\uff0c\u4e0e\u4f59\u76f8\u6597\uff0c\u4f59\u529b\u65a9\u7fa4 \u9b54\uff0c\u9192\u6765\u4e0b\u8eab\u762b\u75ea\uff0c\u4e0d\u53ef\u8f6c\u52a8\uff0c\u4e0a\u8eab\u4ea6\u6709  \u6728\u4e4b\u611f\u3002\u4f59\u6240\u4e60\u4e0d\u7eaf\uff0c\u7adf\u62db\u8d70\u706b\u5165\u9b54\u4e4b\u7978\uff0c\u55df \u4e4e\uff01\u4f59\u4e0e\u5929\u90fd\u5176\u4e0d\u53ef\u590d\u89c1\u77e3\u3002\u201d \u3000\u3000\u5cb3\u545c\u73c2\u53f9\u9053\uff1a\u201c\u6211\u5e08\u7236\u8bf4\u5185\u529f\u4e0d\u53ef\u5f3a\u4fee\uff0c\u5c24\u5176\u4e0d\u53ef\u730e\u6377\u901f\u8fdb\u3002\u4e0d\u60f3\u4ee5\u5e08\u5a18\u8fd9\u6837\u7684\u5927\u884c \u5bb6\uff0c\u7adf\u7136\u4e5f\u906d\u6b64\u7978\u3002\u201d\u5cb3\u545c\u73c2\u770b\u5b8c\u4e4b\u5f8c\uff0c\u628a\u7f8a\u76ae\u4e66\u5377\u5165\u56ca\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5377\u4e66\u662f\u6211\u5e08\u5a18\u5fc3 \u8840\uff0c\u6211\u60f3\u6258\u4eba\u5e26\u56de\u53bb\u7ed9\u6211\u5e08\u7236\u3002\u201d\u6b63\u8bf4\u8bdd\u95f2\uff0c\u6d1e\u5916\u5ffd\u7136\u706b\u5149\u4e00\u95ea\u3002 \u3000\u3000\u4e24\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u8df3\u8d77\u6765\u65f6\uff0c\u5374\u89c1\u8d1e\u4e7e\u9053\u957f\uff0c\u7f13\u7f13\u8d70\u8fdb\uff0c\u5cb3\u545c\u73c2\u677e\u4e86\u53e3\u6c14\u3002\u8d1e\u4e7e\u9053\u957f\u9053\uff1a \u201c\u6211\u4e0e\u5929\u90fd\u5c45\u58eb\uff0c\u7d2b\u9633\u9053\u957f\u90fd\u662f\u81f3\u4ea4\u3002\u524d\u65e5\u7389\u7f57\u5239\u6c42\u6211\u5c06\u5979\u5e08\u7236\u9057\u4f53\uff0c\u8fd0\u56de\u5ce8\u5d4b\u3002\u504f\u9047\u5e94\u4fee \u9633\u7b49\u4e00\u73ed\u8001\u8d3c\u6765\u6b64\u6597\u5251\uff0c\u76f4\u5ef6\u81f3\u5982\u4eca\uff0c\u59cb\u80fd\u529e\u7406\u3002\u78b0\u5de7\u9047\u89c1\u4f60\u4eec\uff0c\u8fd9\u771f\u597d\u6781\u4e86\u3002\u201d\u5cb3\u545c\u73c2 \u9053\uff1a\u201c\u4e0d\u5fc5\u8fd0\u53bb\u5ce8\u5d4b\uff0c\u6211\u7684\u5e08\u7236\u73b0\u5728\u5929\u5c71\u3002\u201d\u8d1e\u4e7e\u9053\u4eba\u9053\uff1a\u201c\u8fd9\u6211\u65e9\u5df2\u77e5\u9053\uff0c\u53ea\u662f\u4f60\u7684\u5e08\u5a18 \u4e0d\u77e5\u9053\u7f62\u4e86\u3002\u201d\u8d1e\u4e7e\u9053\u4eba\u5e26\u6765\u4e86\u4e00\u4e2a\u6728\u5323\uff0c\u653e\u5728\u5916\u6d1e\uff0c\u5cb3\u545c\u73c2\u5c06\u5e08\u5a18\u7684\u9057\u9ab8\u653e\u4eba\u5323\u4e2d\uff0c\u5ffd\u7136 \u8bf4\u9053\uff1a\u201c\u8d1e\u4e7e\u9053\u957f\uff0c\u6211\u6258\u4f60\u5c06\u4e00\u5377\u4e66\u5e26\u5230\u5929\u5c71\uff0c\u4ea4\u6211\u5e08\u7236\u3002\u5343\u4e07\u4e0d\u53ef\u5931\u4e86\u3002\u201d\u8d1e\u4e7e\u9053\u957f\u5fae\u9732 \u6120\u5bb9\uff0c\u5cb3\u545c\u73c2\u614c\u5fd9\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u505a\u5c0f\u8f88\u7684\u65e0\u793c\uff0c\u4e8b\u5173\u8fd9\u672c\u4e66\u82e5\u843d\u5728\u90aa\u6d3e\u624b\u4e0a\uff0c\u5f8c\u5bb3\u975e\u6d45\u3002\u201d \u8d1e\u4e7e\u9053\u957f\u5c06\u4e66\u63a5\u8fc7\uff0c\u7b11\u9053\uff1a\u201c\u6211\u5c3d\u5fc3\u4fdd\u62a4\u4fbf\u662f\uff0c\u4f60\u4e0d\u6015\u6211\u5077\u770b\u9ebd\uff1f\u201d\u5cb3\u545c\u73c2\u8fde\u547c\u201c\u7f6a\u8fc7\u201d\u3002 \u8d1e\u4e7e\u9053\u957f\u4e00\u7b11\u7eb3\u5165\u6000\u4e2d\u3002\u5cb3\u545c\u73c2\u518d\u5de1\u89c6\u4e00\u5468\uff0c\u5ffd\u7136\u62d4\u51fa\u4f69\u5251\uff0c\u5728\u77f3\u58c1\u4e0a\u55d6\u55d6\u4e71\u524a\uff0c\u4e0d\u8fc7\u4e00 \u4f1a\uff0c\u628a\u77f3\u58c1\u4e0a\u523b\u7740\u7684\u5251\u5f0f\u5168\u524a\u4e86\u53bb\u3002\u8d1e\u4e7e\u9053\u4eba\u8bf4\u9053\uff1a\u201c\u4f60\u5e08\u5a18\u6240\u521b\u7684\u51f6\u6b8b\u5251\u6cd5\uff0c\u5b9e\u5728\u4e0d\u5b9c\u7559 \u5728\u4e16\u95ee\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5251\u6cd5\u867d\u51f6\uff0c\u7528\u5f97\u5176\u6b63\uff0c\u4e5f\u53ef\u4ee5\u9664\u66b4\u5b89\u826f\u3002\u201d\u8d1e\u4e7e\u9053\u4eba\u7b11\u9053\uff1a\u201c\u770b\u6765 \u4f60\u548c\u7389\u7f57\u5239\u5012\u5f88\u6ca1\u7f18\u3002\u201d\u5353\u4e00\u822a\u6025\u9053\uff1a\u201c\u9053\u957f\u4f11\u5f97\u53d6\u7b11\u3002\u201d \u3000\u3000\u53c1\u4eba\u628a\u4e8b\u60c5\u529e\u597d\u4e4b\u5f8c\uff0c\u5404\u81ea\u5206\u624b\u3002\u5353\u4e00\u822a\u6653\u884c\u591c\u5bbf\uff0c\u6570\u5929\u4e4b\u5f8c\uff0c\u56de\u5230\u5bb6\u4e2d\uff0c\u8001\u5bb6\u4eba\u4e00 \u89c1\uff0c\u559c\u5f97\u6d41\u6cea\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5c11\u7237\uff0c\u5343\u76fc\u4e07\u76fc\uff0c\u597d\u4e0d\u5bb9\u6613\u76fc\u5f97\u4f60\u56de\u6765\u4e86\uff0c\u8001\u5927\u4eba\u601d\u5ff5\u6210\u75be\uff0c\u7b49 \u7740\u89c1\u4f60\u5462\uff01\u201d\u5353\u4e00\u822a\u6025\u5fd9\u8dd1\u8fdb\u5185\u5ba4\uff0c\u89c1\u4e86\u7237\u7237\uff0c\u5927\u54ed\u62dc\u5012\uff0c\u5353\u4ef2\u5ec9\u4e00\u89c1\u4e86\u4ed6\uff0c\u75c5\u5bb9\u5012\u51cf\u4e86\u4e0d \u5c11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u54ed\u4ec0\u9ebd\uff1f\u4f60\u7238\u600e\u9ebd\u4e0d\u56de\uff1f\u201d\u5353\u4e00\u822a\u89c1\u7956\u7236\u6709\u75c5\uff0c\u90a3\u91cc\u6562\u8bf4\uff0c\u53ea\u5f97\u9970\u8bcd\u56de\u8986\uff0c \u8bf4\u7238\u7238\u8eab\u4e3a\u4eac\u5b98\uff0c\u8fd8\u672a\u80fd\u8f9e\u804c\u3002\u5353\u4ef2\u5ec9\u9053\uff1a\u201c\u5b98\u573a\u9669\u6076\uff0c\u4e0d\u505a\u4e5f\u7f62\u3002\u201d \u3000\u3000\u8fc7\u4e86\u51e0\u65e5\uff0c\u5353\u4ef2\u5ec9\u75c5\u4f53\u60ed\u5065\uff0c\u8bf4\u8d77\u5f53\u65e5\u78b0\u89c1\u7389\u7f57\u5239\u4e4b\u4e8b\uff0c\u72b9\u6709\u9980\u60b8\u3002\u53c8\u95ee\u8d77\u803f\u7ecd\u5357\u7684\u6765 \u5386\uff0c\u5353\u4e00\u822a\u5982\u5b9e\u8bf4\u4e86\u3002\u5353\u4ef2\u5ec9\u8fd9\u624d\u77e5\u9053\u5b59\u513f\u6587\u6b66\u53cc\u4fee\uff0c\u7adf\u662f\u6b66\u5f53\u95e8\u4e0b\uff0c\u5f53\u4e0b\u53c8\u559c\u53c8\u60ca\uff0c\u8bf4 \u9053\uff1a\u201c\u4f60\u6587\u6b66\u53cc\u4fee\uff0c\u81ea\u7136\u597d\u6781\u3002\u53ea\u662f\u4f60\u662f\u6b66\u5f53\u95e8\u4e0b\uff0c\u53ef\u5343\u4e07\u4e0d\u8981\u5728\u6c5f\u6e56\u9053\u4e0a\uff0c\u80e1\u4e71\u884c\u8d70\u3002\u4e07 \u4e00\u78b0\u5230\u4e86\u7389\u7f57\u5239\uff0c\u90a3\u5c31\u7cdf\u4e86\u3002\u7389\u7f57\u5239\u597d\u50cf\u7279\u522b\u4ec7\u89c6\u4f60\u4eec\u6b66\u5f53\u95e8\u4eba\u3002\u201d\u5353\u4e00\u822a\u4e0d\u6562\u8bf4\u51fa\u9047\u89c1\u7389 \u7f57\u5239\u7684\u4e8b\uff0c\u53ea\u9053\uff1a\u201c\u5b59\u513f\u7b49\u65f6\u5c40\u7a0d\u597d\uff0c\u603b\u8981\u6c42\u4e2a\u6b63\u9014\u51fa\u8eab\uff0c\u7ee7\u627f\u7956\u4e1a\u3002\u201d\u5353\u4ef2\u5ec9\u9053\uff1a\u201c\u8fd9\u6837 \u4fbf\u597d\u3002\u201d\u53c8\u9053\uff1a\u201c\u5176\u5b9e\u7389\u7f57\u5239\u4e5f\u4e0d\u662f\u574f\u4eba\uff0c\u5979\u52ab\u4e86\u6211\u7684\u94f6\u4e24\uff0c\u6211\u4e00\u70b9\u4e5f\u4e0d\u6028\u6068\u3002\u201d\u5353\u4e00\u822a\u542c \u5f97\u7956\u7236\u5982\u6b64\u8bf4\u6cd5\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5fc3\u4e2d\u6697\u6697\u6b22\u559c\u3002 \u3000\u3000\u81ea\u6b64\uff0c\u5353\u4e00\u822a\u95ed\u6237\u8bfb\u4e66\uff0c\u8654\u5fc3\u7ec3\u5251\uff0c\u7ea6\u8fc7\u4e86\u4e24\u6708\uff0c\u5ffd\u7136\u4e00\u65e5\uff0c\u4eac\u4e2d\u6d3e\u4e86\u4e24\u4e2a\u94a6\u5dee\uff0c\u6765\u89c1 \u5353\u4ef2\u5ec9\uff0c\u5353\u4e00\u822a\u5728\u623f\u4e2d\u542c\u5f97\u7956\u7236\u54ed\u58f0\uff0c\u6025\u5fd9\u8d70\u51fa\uff0c\u53ea\u89c1\u7956\u7236\u5df2\u7ecf\u6655\u6b7b\u5730\u4e0a\u3002\u6b63\u662f\uff1a\u4f24\u5fc3\u5ba6\u6d77 \u98ce\u6ce2\u9669\uff0c\u4e00\u7eb8\u4e66\u6765\u6101\u65ad\u80a0\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377\u2014\u2014\u7b2c\u4e94\u56de\u3000\u5e73\u5730\u6ce2\u6f9c\u3000\u5978\u4eba\u65bd\u6bd2\u624b\u3000\u5c0f\u57ce\u70fd\u706b\u3000\u5bc6\u5ba4\u9732\u9634\u8c0b \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377 \u7b2c\u4e94\u56de \u5e73\u5730\u6ce2\u6f9c\u3000\u5978\u4eba\u65bd\u6bd2\u624b \u5c0f\u57ce\u70fd\u706b\u3000\u5bc6\u5ba4\u9732\u9634\u8c0b \u3000\u3000\u5353\u4e00\u822a\u8d70\u51fa\u623f\u6765\uff0c\u53ea\u89c1\u7956\u7236\u6c14\u82e5\u6e38\u4e1d\uff0c\u9762\u5982\u91d1\u7eb8\u3002\u6025\u5fd9\u53eb\u6765\u5bb6\u4eba\u5c06\u4ed6\u6276\u4eba\u5367\u623f\u3002\u8fd9\u65f6\u5353 \u4e00\u822a\u867d\u7136\u5fc3\u4e2d\u6025\u4e71\uff0c\u4f46\u94a6\u5dee\u5728\u5802\uff0c\u65e0\u4eba\u6b3e\u5f85\uff0c\u81ea\u5df1\u4e0d\u80fd\u4e0d\u966a\u3002\u6b63\u94a6\u5dee\u6b49\u7136\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\u5bf9\u5353 \u8001\u5927\u4eba\u975e\u5e38\u601d\u5ff5\uff0c\u60f3\u4e0d\u5230\u4e00\u7eb8\u8bcf\u4e66\uff0c\u7d2f\u4ed6\u4f24\u5fc3\u5982\u6b64\u3002\u201d\u5353\u4e00\u822a\u95ee\u9053\uff1a\u201c\u8bcf\u4e66\u4e0a\u8bf4\u4e9b\u4ec0\u9ebd\uff0c\u53ef \u80fd\u89c1\u544a\u9ebd\uff1f\u201d\u90a3\u4e24\u4e2a\u94a6\u5dee\u548c\u5353\u4ef2\u5ec9\u66fe\u662f\u4e00\u6bbf\u4e4b\u81e3\uff0c\u79c1\u4ea4\u9887\u597d\u3002\u5f53\u4e0b\u5c06\u7687\u5e1d\u4f55\u4ee5\u7a81\u7136\u5ba3\u53ec\u5353\u4ef2 \u5ec9\u7684\u4e8b\u60c5\u8bf4\u4e86\u3002\u539f\u6765\u795e\u5b97\u7687\u5e1d\u8bef\u4fe1\u5978\u4eba\u4e4b\u8a00\uff0c\u5c06\u5353\u4e00\u822a\u7684\u7236\u4eb2\u5353\u7ee7\u8d24\u6740\u4e86\uff0c\u5f8c\u6765\u6848\u60c5\u867d\u7136\u5e73 \u53cd\u8fc7\u6765\uff0c\u5e76\u8ffd\u8d60\u4e86\u5353\u7ee7\u8d24\u505a\u592a\u5b50\u5c11\u4fdd\uff0c\u4f46\u65bc\u5fc3\u603b\u89c9\u4e0d\u5b89\u3002\u4e00\u65e5\u548c\u5927\u5b66\u58eb\u65b9\u4ece\u54f2\u8c08\u8d77\uff0c\u795e\u5b97\u5ffd \u7136\u60f3\u8d77\u4e86\u5353\u7ee7\u8d24\u7684\u7236\u4eb2\u5353\u4ef2\u5ec9\uff0c\u559f\u7136\u53f9\u9053\uff1a\u201c\u4ed6\u4eec\u7236\u5b50\u90fd\u662f\u8d38\u826f\u6b63\u76f4\u4e4b\u81e3\uff0c\u5353\u4ef2\u5ec9\u82e5\u770b\u5230\u90b8 \u6284\uff0c\u4e0d\u77e5\u53ef\u4f1a\u57cb\u6028\u6715\u9ebd\uff1f\u201d\u65b9\u4ece\u54f2\u9053\uff1a\u201c\u5353\u4ef2\u5ec9\u4e16\u53d7\u56fd\u6069\uff0c\u90a3\u4f1a\u6028\u603c\uff1f\u965b\u4e0b\u601d\u5ff5\u65bc\u4ed6\uff0c\u76ee\u524d \u540f\u90e8\u5c1a\u4e66\u51fa\u7f3a\uff0c\u4f55\u4e0d\u53ec\u4ed6\u5165\u9601\u3002\u201d\u795e\u5b97\u9053\uff1a\u201c\u671d\u4e2d\u6b63\u4e4f\u8001\u6210\u8c0b\u56fd\u4e4b\u81e3\uff0c\u537f\u8a00\u751a\u5408\u6715\u610f\u3002\u201d\u5f53 \u5373\u5199\u4e86\u8bcf\u4e66\uff0c\u6d3e\u4e24\u540d\u94a6\u5dee\u4e13\u7a0b\u9001\u9655\uff0c\u8981\u4ed6\u56de\u671d\uff0c\u8bcf\u4e66\u4e2d\u63d0\u5230\u4e86\u5353\u7ee7\u8d24\u53d7\u8ffd\u8d60\u4e3a\u592a\u5b50\u5c11\u4fdd\u4e4b \u4e8b\uff0c\u5728\u795e\u5b97\u7684\u610f\u601d\uff0c\u672c\u662f\u5bf9\u81e3\u4e0b\u793a\u6069\uff0c\u60f3\u4e0d\u5230\u5353\u4ef2\u5ec9\u5c1a\u672a\u89c1\u5230\u90b8\u6284\uff0c\u7a81\u7136\u77e5\u9053\u513f\u5b50\u6b7b\u8baf\uff0c\u5fc3 \u4f24\u8fc7\u5ea6\uff0c\u75c5\u5f8c\u8eab\u8eaf\uff0c\u7adf\u81ea\u652f\u6301\u4e0d\u4f4f\u4e86\u3002 \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5185\u5802\u9690\u6709\u54ed\u58f0\uff0c\u94a6\u5dee\u6025\u9053\uff1a\u201c\u4e16\u5144\u4e0d\u5fc5\u62d8\u793c\uff0c\u8bf7\u66ff\u6211\u4eec\u95ee\u5019\u4ee4\u7956\u3002\u201d\u5353\u4e00\u822a \u544a\u4e86\u4e2a\u7f6a\uff0c\u8fdb\u5165\u5185\u5802\uff0c\u53ea\u89c1\u5bb6\u4eba\u4e71\u6210\u4e00\u7247\uff0c\u5353\u4ef2\u5ec9\u5944\u5944\u4e00\u606f\uff0c\u89c1\u5353\u4e00\u822a\u4eba\u6765\uff0c\u62db\u62db\u624b\u9053\uff1a \u201c\u4f60\u8fc7\u6765\u3002\u201d\u5353\u4e00\u822a\u8d70\u8fd1\u6cae\u7236\u8eab\u8fb9\uff0c\u542b\u6cea\u8bf4\u9053\uff1a\u201c\u7237\u7237\u8bf7\u6055\u5b59\u513f\u4e0d\u5b5d\u4e4b\u7f6a\u3002\u201d\u5353\u4ef2\u5ec9\u65ad\u65ad\u7eed \u7eed\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u5f8c\u4e0d\u5fc5\u5e94\u8003\u4e86\uff0c\u5c31\u5728\u5bb6\u4e2d\u8bfb\u4e66\u52a1\u519c\u5427\u3002\u201d\u8bf4\u5b8c\u4e4b\u5f8c\uff0c\u53cc\u817f\u4e00\u4f38\uff0c\u65ad\u4e86\u547c \u5438\u3002\u5353\u4e00\u822a\u653e\u58f0\u5927\u54ed\uff0c\u8001\u5bb6\u4eba\u529d\u9053\uff1a\u201c\u8001\u5927\u4eba\u5e74\u8fc7\u516d\u65ec\uff0c\u5bff\u7ec8\u6b63\u5bdd\uff0c\u5c11\u7237\u4e0d\u5fc5\u8fc7\u5ea6\u60b2\u4f24\u3002\u94a6 \u5dee\u5927\u4eba\u8fd8\u5728\u5916\u9762\uff0c\u5e94\u8be5\u8bf7\u4ed6\u4eec\u795f\u544a\u7687\u4e0a\uff0c\u7136\u5f8c\u5f00\u7075\u571f\u846c\u3002\u201d\u5353\u4e00\u822a\u63e9\u4e7e\u773c\u6cea\uff0c\u5230\u5ba2\u5385\u7980\u544a\u94a6 \u5dee\u3002\u94a6\u5dee\u55df\u53f9\u4e0d\u5df3\uff0c\u5f53\u665a\u5728\u5353\u5bb6\u8fc7\u5bbf\uff0c\u7b2c\u4e8c\u65e5\u5353\u5bb6\u5df2\u8bbe\u4e86\u7075\u4f4d\uff0c\u505c\u68fa\u897f\u5385\uff0c\u4e24\u4e2a\u94a6\u5dee\u90fd\u606d\u606d \u656c\u656c\u7684\u5728\u7075\u524d\u70b9\u4e86\u53c1\u70b7\u9999\u70df\uff0c\u4ee5\u540c\u50da\u4e4b\u8c0a\u81f4\u796d\uff0c\u5353\u4e00\u822a\u530d\u5310\u5730\u4e0a\uff0c\u53e9\u5934\u8c22\u793c\u3002\u6b63\u94a6\u5dee\u4f38\u624b\u6765 \u6276\uff0c\u529d\u9053\uff1a\u201c\u4e16\u5144\u8282\u54c0\uff0c\u6211\u4eec\u56de\u4eac\u7980\u544a\u7687\u4e0a\uff0c\u4e00\u5b9a\u66ff\u8001\u5927\u4eba\u8ba8\u4e2a\u5c01\u8d60\u3002\u201d\u7ba1\u5bb6\u7684\u5907\u597d\u7a0b\u4eea\uff0c \u51c6\u5907\u94a6\u5dee\u8f9e\u884c\uff0c\u5353\u4e00\u822a\u5ffd\u7136\u8df3\u4e86\u8d77\u6765\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u94a6\u5dee\u5927\u4eba\u6162\u8d70 \u3000\u3000\u94a6\u5dee\u548c\u7ba1\u5bb6\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\u5353\u4e00\u822a\u77e5\u4e66\u8bc6\u793c\uff0c\u4f55\u4ee5\u4f1a\u7a81\u7136\u5931\u6001\u3002\u8df3\u8d77\u6765\u5df2\u662f\u4e0d\u8be5\uff0c\u529d \u94a6\u5dee\u6162\u8d70\u66f4\u662f\u5931\u793c\u3002\u7ba1\u5bb6\u6025\u9053\uff1a\u201c\u5c11\u7237\uff0c\u8001\u5927\u4eba\u751f\u8363\u6b7b\u54c0\uff0c\u94a6\u5dee\u5927\u4eba\u4eb2\u6765\u796d\u5960\uff0c\u4f60\u8fd8\u4e0d\u53e9\u8c22 \u7687\u4e0a\u6d2a\u6069\uff01\u201d\u5353\u4e00\u822a\u5b9a\u4e86\u5b9a\u795e\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u94a6\u5dee\u5927\u4eba\uff0c\u8bf7\u8fdb\u5185\u623f\u4e00\u5750\u3002\u201d\u7ba1\u5bb6\u7684\u5fc3\u60ca\u8089 \u8df3\uff0c\u94a6\u5dee\u4e5f\u53d8\u4e86\u989c\u8272\u3002 \u3000\u3000\u5353\u4e00\u822a\u5c06\u4e24\u4f4d\u94a6\u5dee\u5e26\u8fdb\u4e66\u623f\uff0c\u7ba1\u5bb6\u7684\u8ddf\u5728\u5f8c\u9762\uff0c\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u51fa\u53bb\u770b\u5b88\u7075\u5802\u3002\u201d\u968f\u624b \u5c06\u623f\u95e8\u5173\u4e0a\u3002\u8001\u7ba1\u5bb6\u5fe7\u5fc3\u5fe1\u5fe1\uff0c\u5fc3\u60f3\u5c11\u4e3b\u884c\u4e3a\u98a0\u5012\uff0c\u83ab\u975e\u662f\u649e\u4e86\u201c\u90aa\u795e\u201d\uff0c\u4f46\u5728\u94a6\u5dee\u5927\u4eba\u9762 \u524d\uff0c\u5374\u53c8\u4e0d\u4fbf\u8bf4\u8bdd\uff0c\u53ea\u597d\u4e00\u8def\u5ff5\u7740\u201c\u8001\u5929\u83e9\u8428\u4fdd\u4f51\u201d\uff0c\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4e24\u4f4d\u94a6\u5dee\u4e5f\u662f\u60ca\u7591\u4e0d\u5b9a\uff0c\u53ea\u9053\u662f\u5353\u4e00\u822a\u6709\u4e8b\u8bf7\u6258\uff0c\u4f46\u7167\u7406\u6765\u8bf4\uff0c\u4ed6\u6b63\u5fd9\u65bc\u4e27\u4e8b\uff0c\u5c31\u662f\u60f3 \u5728\u5b98\u573a\u94bb\u8425\uff0c\u4e5f\u975e\u5176\u65f6\u3002\u5353\u4e00\u822a\u5c06\u623f\u95e8\u5173\u597d\uff0c\u5c0f\u58f0\u95ee\u9053\uff1a\u201c\u94a6\u5dee\u5927\u4eba\u53ef\u89c9\u5f97\u8eab\u4f53\u6709\u70b9\u4e0d\u8212\u670d \u9ebd\uff1f\u201d\u6b63\u94a6\u5dee\u53d8\u8272\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u5440\uff01\u201d\u526f\u94a6\u5dee\u9053\uff1a\u201c\u4e16\u5144\u771f\u662f\u7167\u6599\u5468\u5230\uff0c\u6211\u4eec\u5e74\u7eaa\u867d\u8001\uff0c\u8fd9 \u70b9\u98ce\u971c\u8fd8\u71ac\u5f97\u4f4f\uff0c\u5012\u662f\u4e16\u5144\u91cd\u5b5d\u5728\u8eab\uff0c\u8fd8\u671b\u8282\u54c0\u514d\u81f4\u4f24\u795e\u4e3a\u597d\u3002\u201d\u8fd9\u8bdd\u6697\u85cf\u8ba5\u8bbd\uff0c\u5353\u4e00\u822a \u9053\uff1a\u201c\u94a6\u5dee\u5927\u4eba\u8bf7\u6012\u65e0\u793c\uff0c\u9002\u624d\u6211\u89c1\u674e\u5927\u4eba\u53f3\u638c\u7684\u638c\u5fc3\u4f3c\u4e4e\u6709\u4e9b\u5f02\u6837\u3002\u201d\u6b63\u94a6\u5dee\u59d3\u674e\uff0c\u95fb\u8a00 \u4e0d\u89c9\u644a\u638c\u4e00\u770b\uff0c\u987f\u65f6\u9762\u4e0a\u9732\u51fa\u60ca\u5f02\u7684\u795e\u8272\u6765\u3002\u638c\u5fc3\u4e0a\u73b0\u51fa\u4e00\u70b9\u70b9\u7684\u7ea2\u7c92\uff0c\u5c31\u50cf\u51fa\u75b9\u5b50\u4e00\u822c\uff0c \u526f\u94a6\u5dee\u59d3\u5468\uff0c\u644a\u51fa\u53f3\u638c\u6765\u770b\uff0c\u4e5f\u662f\u4e00\u822c\u3002\u5353\u4e00\u822a\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u4eba\u8bf7\u7528\u6307\u7532\u4e00\u637b\uff0c\u770b\u662f\u75db\u4e5f\u4e0d \u75db\u3002\u201d\u4e24\u4f4d\u94a6\u5dee\u4f9d\u8a00\u8bd5\u4e86\uff0c\u4ee5\u524d\u7684\u8bfb\u4e66\u4eba\u90fd\u60ef\u7559\u957f\u957f\u7684\u6307\u7532\uff0c\u4ed6\u4eec\u7528\u5de6\u624b\u6307\u7532\uff0c\u731b\u523a\u53f3\u638c\u638c \u5fc3\uff0c\u5c45\u7136\u4e00\u70b9\u4e5f\u4e0d\u89c1\u75db\uff0c\u5012\u662f\u6709\u70b9  \u75d2\u7684\u611f\u89c9\u3002\u5353\u4e00\u822a\u53c8\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u4eba\u8bf7\u7528\u624b\u6307\u8f7b\u6309\u5934\u9888 \u5f8c\u810a\u9aa8\u4e0a\u90e8\u7684\u7b2c\u4e03\u8282\uff0c\u770b\u770b\u5982\u4f55\uff1f\u201d\u8fd9\u65f6\u4e24\u4e2a\u94a6\u5dee\u5c31\u5982\u540c\u5b69\u5b50\u4e00\u822c\u542c\u4ece\u5353\u4e00\u822a\u7684\u6446\u5e03\uff0c\u5404\u4ee5 \u624b\u6307\u8f7b\u6309\u5bf9\u65b9\u5934\u9888\u5f8c\u810a\u9aa8\u4e0a\u90e8\u7684\u7b2c\u4e03\u8282\uff0c\u53ea\u8fd9\u9ebd\u8f7b\u8f7b\u4e00\u6309\uff0c\u4e24\u4eba\u90fd\u75db\u5f97\u53eb\u51fa\u58f0\u6765\u3002\u6025\u5fd9\u95ee \u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u9ebd\u9053\u7406\uff1f\u4e16\u5144\u5982\u4f55\u77e5\u9053\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u4eba\u90fd\u53d7\u4e86\u6697\u7b97\u4e86\uff0c\u8fd9\u662f\u6c5f\u6e56\u4e0a\u6700\u9634\u6bd2\u7684\u9634\u98ce\u6bd2\u7802\u638c\u3002\u521a\u624d\u674e \u5927\u4eba\u4f38\u624b\u62c9\u6211\uff0c\u6211\u624d\u77a7\u51fa\uff0c\u60f3\u6765\u8fd9\u4e9b\u7ea2\u75b9\u662f\u521a\u521a\u53d1\u4f5c\u51fa\u6765\u7684\uff0c\u6240\u4ee5\u5927\u4eba\u8fd8\u672a\u77e5\u9053\u3002\u53d7\u4e86\u9634\u98ce \u6bd2\u7802\u638c\u7684\u6697\u7b97\uff0c\u53d1\u4f5c\u5f8c\u5341\u4e8c\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u82e5\u4e0d\u6551\u6cbb\uff0c\u6050\u6709\u6027\u547d\u4e4b\u5fe7\uff0c\u6240\u4ee5\u665a\u751f\u4e5f\u987e\u4e0d\u5f97\u5931 \u793c\uff0c\u8981\u5bf9\u5927\u4eba\u76f4\u8a00\u4e86\u3002\u201d\u987b\u77e5\u5728\u5c01\u5efa\u7687\u671d\uff0c\u94a6\u5dee\u4ee3\u8868\u7687\u5e1d\uff0c\u82e5\u7136\u6b7b\u5728\u5353\u5bb6\uff0c\u90a3\u9ebd\u83f2\u4f46\u5353\u5bb6\u6709 \u6284\u5bb6\u706d\u65cf\u4e4b\u7978\uff0c\u5730\u65b9\u5b98\u540f\u4e5f\u8981\u53d7\u7275\u8fde\u3002\u5173\u7cfb\u5982\u6b64\u91cd\u5927\uff0c\u5353\u4e00\u822a\u867d\u5728\u91cd\u5b5d\u4e4b\u4e2d\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u7ba1 \u4e86\u3002 \u3000\u3000\u4e24\u4e2a\u94a6\u5dee\u9762\u5982\u571f\u8272\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u90a3\u9ebd\u5c31\u8bf7\u4e16\u5144\u6551\u6cbb\u3002\u201d\u5353\u4e00\u822a\u628a\u7ba1\u5bb6\u53eb\u8fdb\uff0c\u53eb\u4ed6\u53e6\u8f9f \u9759\u5ba4\uff0c\u9664\u81f3\u4eb2\u597d\u53cb\u5916\uff0c\u6682\u4e0d\u62a5\u4e27\u3002\u5728\u9759\u5ba4\u4e2d\u53d6\u51fa\u91d1\u9488\uff0c\u5728\u4e24\u4f4d\u94a6\u5dee\u7684\u201c\u810a\u5fc3\u7a74\u201d\uff0e\u201c\u51e4\u5c3e \u7a74\u201d\u3001\u201c\u7cbe\u4fc3\u7a74\u201d\u4e0a\u5404\u523a\u4e86\u4e00\u9488\uff0c\u4e24\u4f4d\u94a6\u5dee\u987f\u89c9\u5fc3\u80c3\u9178\u80c0\uff0c\u5410\u4e86\u4e00\u644a\u9ec4\u6c34\uff0c\u4e0d\u4e45\u5468\u8eab\u53d1\u70ed\u3002 \u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u8fd9\u662f\u4fc3\u5b83\u7684\u6bd2\u6027\u65e9\u53d1\u3002\u4e24\u4f4d\u5927\u4eba\u5148\u8eba\u4e00\u9635\uff0c\u4eca\u665a\u8fd8\u8981\u7ee7\u7eed\u6cbb\u7597\u3002\u201d\u6536\u8d77\u91d1 \u9488\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4fdd\u62a4\u4e24\u4f4d\u4eba\u4eba\u7684\u536b\u58eb\u662f\u8c01\uff01\u4eba\u53ef\u9760\u5417\uff1f\u201d \u3000\u3000\u674e\u94a6\u5dee\u9053\uff1a\u201c\u6b64\u6b21\u51fa\u4eac\uff0c\u7687\u4e0a\u6d3e\u9526\u8863\u536b\u7684\u79e6\u6307\u6325\u968f\u884c\uff0c\u6b64\u4eba\u662f\u4e16\u88ad\u6307\u6325\uff0c\u7687\u4e0a\u4eb2\u4fe1\uff0c\u800c \u4e14\u4e3a\u4eba\u6b63\u76f4\uff0c\u65ad\u65e0\u6697\u7b97\u6211\u4eec\u4e4b\u7406\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u665a\u751f\u6597\u80c6\u60f3\u8bf7\u4ed6\u8fdb\u6765\u4e00\u8c08\u3002\u201d\u674e\u94a6\u5dee\u9053\uff1a \u201c\u4f46\u51ed\u5429\u5490\u3002\u201d\u5353\u4e00\u822a\u53eb\u7ba1\u5bb6\u7684\u8bf7\u79e6\u6307\u6325\u4eba\u6765\uff0c\u8fd9\u4eba\u4e2d\u7b49\u8eab\u6750\uff0c\u9762\u8c8c\u4e5f\u8fd8\u5584\u826f\uff0c\u4f46\u4e00\u770b\u5c31\u77e5 \u4e0d\u662f\u600e\u9ebd\u673a\u7075\u7684\u4eba\u3002\u5353\u4e00\u822a\u9053\uff1a\u201c\u4e45\u4ef0\u6307\u6325\u5927\u540d\uff0c\u54b1\u4eec\u4ea4\u4ea4\u3002\u201d\u4f38\u624b\u4e00\u63e1\uff0c\u79e6\u6307\u6325\u8df3\u4e86\u8d77 \u6765\uff0c\u624b\u8155  \u9ebb\uff0c\u53c8\u89c1\u4e24\u4e2a\u94a6\u5dee\u9762\u4f3c\u706b\u70ed\uff0c\u989d\u4e0a\u6dcc\u6c57\uff0c\u8eba\u5728\u5e8a\u4e0a\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u559d\u9053\uff1a\u201c\u4f60 \u6562\u6697\u7b97\u94a6\u5dee\uff01\u201d\u53cd\u624b\u4e00\u638c\uff0c\u76f4\u5288\u8fc7\u6765\uff0c\u5353\u4e00\u822a  \u5730\u8df3\u5f00\uff0c\u4e24\u4f4d\u94a6\u5dee\u9f50\u58f0\u559d\u6b62\u3002\u5353\u4e00\u822a\u9053\uff1a \u201c\u5f97\u7f6a\uff0c\u5f97\u7f6a\uff0c\u6211\u662f\u66ff\u6307\u6325\u6d17\u8131\u5acc\u7591\u3002\u94a6\u5dee\u5927\u4eba\u662f\u53d7\u4eba\u6697\u7b97\u4e86\uff0c\u4f46\u6697\u7b97\u7684\u4eba\u4e0d\u662f\u6211\u4e5f\u4e0d\u662f \u4f60\uff0c\u6211\u6b63\u60f3\u4e0e\u6307\u6325\u5927\u4eba\u8c08\u8c08\u3002\u201d\u79e6\u6307\u6325\u5446\u82e5\u6728\u9e21\uff0c\u5f85\u5353\u4e00\u822a\u8bf4\u5b8c\uff0c\u8fd9\u624d\u731b\u7136\u7701\u609f\uff0c\u8bf4\u9053\uff1a \u201c\u539f\u6765\u4f60\u521a\u624d\u662f\u6821\u8003\u6211\u4e86\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u6211\u53ea\u60f3\u77e5\u9053\u79e6\u6307\u6325\u4f1a\u4e0d\u4f1a\u9634\u98ce\u6bd2\u7802\u638c\u3002\u73b0 \u5728\u77e5\u9053\u79e6\u6307\u6325\u6b66\u529f\u9ad8\uff0c\u5374\u6ca1\u7ec3\u8fc7\u90a3\u79cd\u9634\u6bd2\u7684\u638c\u6cd5\u3002\u201d\u79e6\u6307\u6325\u60ca\u9053\uff1a\u201c\u4ec0\u9ebd\u9634\u98ce\u6bd2\u7802\u638c\uff01\u201d\u5353 \u4e00\u822a\u9053\uff1a\u201c\u4e24\u4f4d\u4eba\u4eba\u5c31\u662f\u53d7\u9634\u98ce\u6bd2\u7802\u638c\u7684\u6697\u7b97\u3002\u201d\u5e26\u79e6\u6307\u6325\u5230\u75c5\u69bb\u524d\u7ec6\u770b\uff0c\u5c06\u4e2d\u9634\u98ce\u6bd2\u7802\u638c \u7684\u5fb5\u8c61\u4e00\u4e00\u8bf4\u4e86\u3002\u79e6\u6307\u6325\u6b66\u529f\u867d\u975e\u6781\u9ad8\uff0c\u4f46\u4e5f\u89c1\u95fb\u9887\u5e7f\uff0c\u77e5\u9053\u5353\u4e00\u822a\u6240\u8bf4\u4e0d\u865a\uff0c\u5413\u51fa\u4e00\u8eab\u51b7 \u6c57\uff0c\u6025\u5fd9\u9053\u8c22\u3002 \u3000\u3000\u5353\u4e00\u822a\u9053\uff1a\u201c\u9634\u98ce\u6bd2\u7802\u638c\u7684\u5389\u5bb3\uff0c\u5728\u65bc\u5b83\u5e76\u4e0d\u662f\u4f24\u4eba\u7acb\u6b7b\uff0c\u800c\u662f\u6162\u6162\u53d1\u4f5c\u3002\u770b\u8fd9\u5fb5\u8c61\uff0c \u94a6\u5dee\u5927\u4eba\u662f\u5728\u53c1\u65e5\u4e4b\u524d\u6240\u53d7\u7684\u6697\u7b97\u3002\u8bf7\u6307\u6325\u5927\u4eba\u7ec6\u60f3\uff0c\u53c1\u65e5\u524d\u53ef\u78b0\u8fc7\u4ec0\u9ebd\u5f62\u8ff9\u53ef\u7591\u7684\u4eba\u3002\u201d \u79e6\u6307\u6325\u6697\u6697\u53eb\u58f0\u201c\u60ed\u5080\u201d\uff0c\u4f4e\u5934\u601d\u7d22\u3002\u674e\u94a6\u5dee\u5ffd\u9053\uff1a\u201c\u96be\u9053\u4e0e\u90a3\u9001\u8336\u7684\u8001\u6c49\u6709\u5173\uff1f\u201d\u79e6\u6307\u6325 \u4e5f\u60f3\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u65f6\u6211\u4e5f\u89c9\u5f97\u6709\u70b9\u53ef\u7591\uff0c\u4f46\u770b\u4ed6\u5e74\u7eaa\u8001\u8fc8\uff0c\u66f4\u4e0d\u50cf\u8eab\u6000\u7edd\u6280\u7684\u4eba\uff0c\u4e00 \u65f6\u5927\u610f\uff0c\u5c31\u653e\u8fc7\u4e86\u3002\u201d\u5353\u4e00\u822a\u5fd9\u95ee\u90a3\u9001\u837c\u7684\u8001\u6c49\u5982\u4f55\uff0c\u674e\u94a6\u5dee\u9053\uff1a\u201c\u53c1\u65e5\u524d\u6211\u4eec\u5728\u8def\u65c1\u6811\u836b \u4e58\u51c9\uff0c\u9887\u89c9\u53e3\u6e34\uff0c\u5ffd\u7136\u6709\u4e00\u4e2a\u8001\u6c49\uff0c\u6311\u7740\u4e00\u5927\u62c5\u51c9\u8336\uff0c\u4e5f\u5728\u6811\u836b\u4e0b\u6b47\u606f\uff0c\u95ee\u8d77\u6765\u4ed6\u8bf4\u662f\u7ed9\u7530 \u91cc\u7684\u5bb6\u4eba\u9001\u837c\u6c34\u53bb\u7684\uff0c\u4ed6\u8ddf\u6211\u4eec\u95f2\u804a\u8d77\u6765\uff0c\u542c\u8bf4\u6211\u4eec\u8981\u5230\u8d35\u5e9c\uff0c\u4ed6\u8bf4\u662f\u4f60\u4eec\u7684\u4f43\u6237\uff0c\u8fd8\u66ff\u6211 \u4eec\u6307\u70b9\u9053\u8def\u5462\u3002\u662f\u4ed6\u8bf7\u6211\u4eec\u559d\u4e86\u4e24\u7897\u837c\uff0c\u79e6\u6307\u6325\u6ca1\u6709\u559d\u3002\u4ed6\u628a\u8336\u7897\u9012\u8fc7\u6765\u65f6\uff0c\u624b\u6307\u66f9\u5728\u6211\u7684 \u638c\u5fc3\u8f7b\u8f7b\u78b0\u4e86\u4e00\u4e0b\uff0c\u5f53\u65f6\u6211\u4e5f\u4e0d\u7559\u610f\u3002\u201d\u5468\u94a6\u5dee\u9053\uff1a\u201c\u4ed6\u9012\u837c\u7ed9\u6211\u559d\u65f6\uff0c\u4e5f\u8f7b\u8f7b\u78b0\u4e86\u6211\u4e00 \u4e0b\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\u3002\u4ed6\u77e5\u4e0d\u77e5\u9053\u4f60\u4eec\u662f\u94a6\u5dee\uff1f\u201d\u79e6\u6307\u6325\u9053\uff1a\u201c\u5ddd\u9655\u9053\u4e0a\u76d7\u532a\u5982 \u6bdb\uff0c\u6211\u4eec\u5728\u8def\u4e0a\u884c\u8d70\u65f6\uff0c\u90a3\u91cc\u6562\u6302\u51fa\u5b98\u8854\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u6c89\u601d\u4e0d\u8bed\uff0c\u8d8a\u60f3\u8d8a\u60ca\uff0c\u8fd9\u8001\u6c49\u5206\u660e\u662f\u60f3\u79fb\u7978\u4e1c\u5434\uff0c\u8ba9\u94a6\u5dee\u5230\u4e86\u6211\u5bb6\u4e4b\u5f8c\uff0c\u6bd2\u53d1\u8eab \u4ea1\uff0c\u90a3\u65f6\u867d\u503e\u9ec4\u6cb3\u4e4b\u6c34\uff0c\u4e5f\u6d17\u4e0d\u6e05\u5173\u7cfb\u4e86\uff0c\u6b63\u5728\u601d\u91cf\uff0c\u5ffd\u7136\u5bb6\u4eba\u8dd1\u6765\u53eb\u9053\uff1a\u201c\u5c11\u7237\uff0c\u5c11 \u7237\uff01\u201d\u5353\u4e00\u822a\u63a8\u5f00\u623f\u95e8\uff0c\u559d\u9053\uff1a\u201c\u4ec0\u9ebd\u4e8b\uff1f\u201d\u5bb6\u4eba\u9053\uff1a\u201c\u5916\u9762\u6709\u4e00\u4e2a\u5e74\u8f7b\u6c49\u5b50\uff0c\u9762\u76ee\u9752\u80bf\uff0c \u597d\u50cf\u521a\u548c\u4eba\u6253\u8fc7\u4e00\u573a\u67b6\u4f3c\u7684\uff0c\u4ed6\u95ef\u8fdb\u6765\u8981\u627e\u5c11\u7237\uff0c\u6211\u4eec\u8bf4\u5bb6\u6709\u4e27\u4e8b\uff0c\u5c11\u7237\u4e0d\u89c1\u5ba2\uff0c\u4ed6\u7406\u4e5f\u4e0d \u7406\uff0c\u786c\u95ef\u8fdb\u6765\uff0c\u6211\u4eec\u4f38\u624b\u62e6\u963b\uff0c\u4ed6\u632f\u81c2\u4e00\u683c\uff0c\u62e6\u963b\u7684\u90fd\u8dcc\u5012\u4e86\u3002\u6211\u4eec\u6b63\u60f3\u628a\u4ed6\u8f70\u51fa\u53bb\uff0c\u4ed6\u5ffd \u7136\u53c8\u8d54\u8d77\u7f6a\u6765\uff0c\u8bf4\u662f\u6025\u7740\u8981\u89c1\u5c11\u7237\uff0c\u4e0d\u662f\u8bda\u5fc3\u6253\u6211\u4eec\u7684\u3002\u201d\u5353\u4e00\u822a\u8be7\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4e8b\uff01\u201d \u5411\u94a6\u5dee\u544a\u4e86\u4e2a\u7f6a\uff0c\u63a9\u4e0a\u623f\u95e8\uff0c\u8d70\u51fa\u4e2d\u5802\uff0c\u53ea\u89c1\u9636\u4e0b\u7acb\u7740\u4e00\u4eba\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5353\u5144\uff0c\u6025\u6b7b\u6211 \u4e86\u3002\u201d\u5353\u4e00\u822a\u4e00\u770b\uff0c\u539f\u6765\u5374\u662f\u5b5f\u707f\u7684\u5f1f\u5b50\u767d\u654f\u3002\u5353\u4e00\u822a\u5728\u5317\u4eac\u548c\u4ed6\u53ea\u89c1\u8fc7\u4e00\u9762\uff0c\u8bdd\u4e5f\u6ca1\u6709\u8c08 \u4e0a\u4e24\u65ec\uff0c\u6839\u672c\u8bf4\u4e0d\u4e0a\u6709\u4ec0\u9ebd\u4ea4\u60c5\uff0c\u4e0d\u77e5\u4ed6\u4f55\u6545\u5343\u91cc\u8fe2\u8fe2\uff0c\u524d\u6765\u5bfb\u8bbf\u3002 \u3000\u3000\u767d\u654f\u4e00\u63d6\u5230\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u5353\u5144\u6551\u6211\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u767d\u5144\u72af\u4e86\u4f55\u4e8b\uff1f\u201d\u767d\u654f\u9053\uff1a\u201c\u4e0d\u662f \u72af\u4e8b\uff0c\u662f\u53d7\u83ab\u540d\u5176\u5999\u7684\u4eba\u6253\u4e86\u4e00\u987f\uff0c\u4e34\u8d70\u65f6\u8fd8\u4e2d\u4e86\u9634\u98ce\u6bd2\u7802\u638c\u7684\u6697\u7b97\u3002\u201d\u5353\u4e00\u822a\u5403\u4e86\u4e00\u60ca\uff0c \u5fc3\u9053\uff1a\u53c8\u662f\u9634\u98ce\u6bd2\u7802\u638c\u3002\u6025\u5fd9\u5c06\u4ed6\u8bf7\u8fdb\u5185\u5ba4\uff0c\u7ec6\u95ee\u6839\u7531\u3002 \u3000\u3000\u539f\u6765\u5b5f\u707f\u91cd\u4f24\u6b7b\u5f8c\uff0c\u767d\u654f\u5f97\u8baf\u56de\u6765\uff0c\u77e5\u9053\u4e86\u738b\u7167\u5e0c\u5c31\u662f\u5e08\u59b9\u7684\u672a\u5a5a\u592b\u5a7f\uff0c\u867d\u7136\u5bf9\u5e08\u7236\u6b7b \u65bc\u975e\u547d\uff0c\u5341\u5206\u60b2\u60bc\uff0c\u4f46\u773c\u89c1\u738b\u7167\u5e0c\u5982\u6b64\u82f1\u96c4\uff0c\u6b23\u5e78\u5e08\u59b9\u7ec8\u8eab\u6709\u6258\uff0c\u60b2\u4f24\u4e2d\u4e5f\u89c9\u5feb\u6170\u3002\u4f46\u6599\u4e0d \u5230\u7b2c\u4e8c\u65e5\u738b\u7167\u5e0c\u5c31\u4e0d\u8f9e\u800c\u884c\uff0c\u5b5f\u79cb\u971e\u54ed\u5f97\u6cea\u4eba\u4f3c\u7684\uff0c\u767d\u654f\u518d\u53c1\u5b89\u6170\uff0c\u5e08\u59b9\u5374\u4e0d\u8a00\u4e0d\u8bed\uff0c\u4e0d\u7406 \u4e0d\u776c\uff0c\u767d\u654f\u8bf4\u5230\u8fd9\u91cc\uff0c\u50bb\u864e\u864e\u7684\u9053\uff1a\u201c\u5353\u5144\uff0c\u4f60\u548c\u738b\u7167\u5e0c\u4e5f\u662f\u670b\u53cb\uff0c\u4f60\u8bf4\u4ed6\u884c\u4e3a\u600e\u9ebd\u8fd9\u6837\u602a \u8bde\uff0c\u5343\u91cc\u8fe2\u8fe2\u7684\u6765\u8fce\u4eb2\uff0c\u53c8\u6070\u9022\u5cb3\u4e08\u8eab\u4ea1\uff1a\u600e\u9ebd\u8bf4\u4ed6\u4e5f\u8be5\u4ee5\u534a\u5b50\u4e4b\u793c\u4e3b\u6301\u4e27\u4e8b\uff0c\u4ed6\u5374\u4f38\u817f\u4e00 \u8dd1\u5c31\u5b8c\u4e86\uff0c\u8001\u5a46\u4e5f\u4e0d\u8981\u4e86\u3002\u8fd8\u6709\u6211\u7684\u5e08\u59b9\u4e5f\u602a\uff0c\u738b\u7167\u5e0c\u8dd1\u6389\u8ddf\u6211\u6709\u4ec0\u9ebd\u76f8\u5e72\uff0c\u5979\u5374\u4e0d\u776c\u6211\uff0c \u597d\u50cf\u662f\u6211\u628a\u4ed6\u6c14\u8d70\u4f3c\u7684\u3002\u201d\u5353\u4e00\u822a\u7ec6\u4e00\u7422\u78e8\uff0c\u5df2\u660e\u5c31\u91cc\uff0c\u6697\u91cc\u8bf4\u9053\uff1a\u201c\u53ef\u4e0d\u6b63\u662f\u4f60\u628a\u4ed6\u6c14\u8d70 \u4e86\u7684\u3002\u201d\u5f53\u4e0b\u5b89\u6170\u4ed6\u9053\uff1a\u201c\u8fd9\u4e9b\u5c0f\u4e8b\uff0c\u5c06\u6765\u6211\u66ff\u4f60\u5411\u738b\u5144\u8bf4\u53bb\u3002\u4e0d\u76f8\u5e72\u7684\u3002\u201d\u767d\u654f\u8be7\u9053\uff1a \u201c\u5411\u4ed6\u8bf4\u4ec0\u9ebd\u5440\u3002\u6211\u6ca1\u5f97\u7f6a\u4ed6\uff0c\u4ed6\u4e5f\u6ca1\u5f97\u7f6a\u6211\uff0c\u7528\u4e0d\u7740\u548c\u4ed6\u8bf4\u5440\u3002\u5bf9\u4ed6\u8bf4\u53cd\u53eb\u4ed6\u7b11\u8bdd\u6211\u4eec\u5e08 \u5144\u59b9\u5435\u67b6\uff0c\u5176\u5b9e\u6211\u4e5f\u6ca1\u6709\u548c\u5e08\u59b9\u5435\u67b6\u561b\u3002\u5e08\u59b9\u5f8c\u6765\u4e5f\u8bf4\uff0c\u4e0d\u5173\u4f60\u4e8b\uff0c\u4f60\u53bb\u7761\u5427\u3002\u6211\u542c\u5979\u7684\u8bdd \u56de\u53bb\u7761\u4e86\uff0c\u4e00\u89c9\u7761\u5230\u5929\u660e\uff0c\u4e0d\u60f3\u5979\u4e5f\u8dd1\u4e86\u3002\u201d\u5353\u4e00\u822a\u76b1\u7709\u9053\uff1a\u201c\u600e\u9ebd\uff0c\u5979\u4e5f\u8dd1\u4e86\uff1f\u201d\u767d\u654f \u9053\uff1a\u201c\u662f\u5440\uff0c\u5e08\u7236\u521a\u521a\u4e0b\u846c\uff0c\u5979\u4e5f\u4e0d\u5728\u5bb6\u5b88\u5b5d\uff0c\u5c31\u8dd1\u53bb\u627e\u4e08\u592b\u4e86\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u77e5 \u9053\u5979\u662f\u627e\u738b\u7167\u5e0c\uff1f\u201d\u767d\u654f\u9053\uff1a\u201c\u5979\u7559\u6709\u4fe1\u7ed9\u6211\u561b\uff0c\u5979\u8fd8\u53eb\u6211\u7559\u5728\u5bb6\u4e2d\u66ff\u5979\u5b88\u7075\uff0c\u4e0d\u8981\u5230\u5904\u4e71 \u8dd1\u60f9\u4e8b\u3002\u201d\u5353\u4e00\u822a\u82e5\u975e\u5c45\u4e27\u5b88\u5b5d\uff0c\u51e0\u4e4e\u7ed9\u4ed6\u60f9\u5f97\u7b11\u4e86\u51fa\u6765\u3002\u60f3\u4e0d\u5230\u8fd9\u4eba\u5982\u6b64\u50bb\u91cc\u50bb\u6c14\uff0c\u7ed9\u4eba \u8bef\u4f1a\u4e86\uff0c\u81ea\u5df2\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u767d\u654f\u6b47\u4e86\u4e00\u6b47\u53c8\u9053\uff1a\u201c\u6211\u62c5\u5fc3\u5e08\u59b9\u5b64\u8eab\u72ec\u884c\uff0c\u5979\u53eb\u6211\u4e0d\u8981\u4e71\u8dd1\uff0c\u6211\u4e5f\u8981\u8dd1\u51fa\u6765\u4e86\u3002\u201d\u8bf4 \u7f62\u5ffd\u7136\u4e3e\u8d77\u53cc\u624b\uff01 \u3000\u3000\u638c\u5fc3\u4e0a\u7ea2\u75b9\u89e6\u76ee\uff0c\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u53c1\u65e5\u4e4b\u524d\u53d7\u4eba\u6697\u7b97\u7684\uff1f\u201d\u767d\u654f\u9053\uff1a\u201c\u662f\u5440\u3002\u6211\u5230 \u4e86  \u897f\uff0c\u4e5f\u4e0d\u77e5\u738b\u7167\u5e0c\u662f\u90a3\u91cc\u4eba\u6c0f\u3002\u5012\u662f\u4f60\u8001\u54e5\u7684\u5730\u5740\u5bb9\u6613\u6253\u542c\uff0c\u6211\u4e00\u8bf4\u8d77\u505a\u8fc7\u603b\u7763\u7684\u90a3\u4e2a \u5353\u5bb6\uff0c\u8bb8\u591a\u4eba\u90fd\u77e5\u9053\u3002\u6211\u5fc3\u60f3\u627e\u5230\u4e86\u4f60\u5c31\u6613\u529e\u4e86\uff0c\u4f60\u603b\u8be5\u77e5\u9053\u4ed6\u7684\u5730\u5740\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211 \u4e5f\u4e0d\u77e5\u9053\u3002\u201d\u767d\u654f\u9053\uff1a\u201c\u65e9\u77e5\u5982\u6b64\uff0c\u6211\u4e0d\u627e\u4f60\u8fd8\u597d\u3002\u6211\u5230\u4e86\u5ef6\u5b89\u5e9c\u5f8c\uff0c\u5c31\u53d1\u73b0\u6709\u4eba\u7f00\u5728\u6211\u7684 \u5f8c\u9762\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u5012\u8fd8\u7ec6\u5fc3\u3002\u201d\u767d\u654f\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u6c5f\u6e56\u4e0a\u7684\u4f0e\u4fe9\u6211\u8fd8\u77e5\u9053\u3002\u5927\u524d\u5929\u6211 \u7ecf\u8fc7\u87e0\u9f99\u5c71\uff0c\u5728\u8def\u4e0a\u8d70\u5f97\u597d\u597d\u7684\uff0c\u5ffd\u7136\u4e24\u9a91\u9a6c\u5728\u5f8c\u9762\u8ffd\u6765\uff0c\u95ee\u6211\u662f\u4e0d\u662f\u8981\u5230\u9ad8\u6865\u9547\u7684\u5353\u5bb6\uff0c \u6211\u8bf4\u662f\uff0c\u90a3\u4e24\u4e2a\u5bb6\u4f19\u7a81\u7136\u8df3\u4e0b\u9a6c\u6765\uff0c\u4e0d\u5206\u9752\u7ea2\u7682\u767d\uff0c\u628a\u6211\u4e71\u6253\u4e00\u987f\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u55ef\uff0c\u4f60 \u6253\u8f93\u4e86\uff1f\u201d\u767d\u654f\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u5bb6\u4f19\u662f\u786c\u70b9\u5b50\uff0c\u6211\u8d77\u521d\u8fd8\u80fd\u548c\u4ed6\u4eec\u6253\u4e2a\u5e73\u624b\uff0c\u5f8c\u6765\u8d8a\u6253\u8d8a\u4e0d\u884c \u4e86\u3002\u90a3\u4e24\u4e2a\u5bb6\u4f19\u7684\u5f8c\u9762\u8fd8\u6709\u4e00\u4e2a\u8001\u6c49\uff0c\u4ed6\u4e5f\u4e0d\u52a8\u624b\uff0c\u5c3d\u5728\u5f8c\u9762\u53eb\uff1a\u8981\u6d3b\u7684\u4e0d\u8981\u6b7b\u7684\u3002\u628a\u6211\u6c14 \u5f97\u8981\u6b7b\uff0c\u62f3\u6cd5\u66f4\u4e71\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u90a3\u4f60\u5f8c\u6765\u600e\u9ebd\u9003\u5f97\u51fa\u6765\uff1f\u201d\u767d\u654f\u9053\uff1a\u201c\u4eca\u5e74\u521d\u6211\u66fe\u5230\u5929 \u6865\u770b\u76f8\uff0c\u770b\u76f8\u7684\u8bf4\u6211\u4eca\u5e74\u867d\u7136\u6d41\u5e74\u4e0d\u5229\uff0c\u4f46\u5374\u80fd\u9022\u51f6\u5316\u5409\uff0c\u9047\u96be\u6210\u7965\u3002\u201d\u5353\u4e00\u822a\u5fcd\u4e0d\u4f4f\u9053\uff1a \u201c\u6211\u95ee\u4f60\u600e\u9ebd\u8131\u9669\uff0c\u4f60\u5374\u8bf4\u53bb\u5929\u6865\u770b\u76f8\uff0c\u8fd9\u548c\u770b\u76f8\u6709\u4ec0\u9ebd\u76f8\u5e72\uff1f\u201d\u767d\u654f\u9053\uff1a\u201c\u90a3\u770b\u76f8\u7684\u8fd8\u771f \u6709\u70b9\u9053\u7406\u5462\uff01\u8fd9\u56de\u6211\u4e0d\u662f\u5371\u9669\u4e4b\u6781\u4e86\u9ebd\u3002\u770b\u770b\u5c31\u8981\u7ed9\u4ed6\u4eec\u6253\u5012\u4e86\uff0c\u5ffd\u7136\u87e0\u9f99\u5c71\u4e0a\u6709\u4eba\u51b7\u7b11\uff0c \u7b11\u5f97\u975e\u5e38\u523a\u8033\uff0c\u90a3\u62bc\u9635\u7684\u8001\u6c49\u53eb\u9053\uff1a\u201c\u5feb\u9000\uff01\u201d\u7b11\u58f0\u53eb\u58f0\uff0c\u9980\u97f3\u72b9\u5728\uff0c\u5c71\u9876\u4e0a\u5df2\u75be\u5982\u6d41\u661f\u98de \u7bad\u822c\u7684\u51b2\u4e0b\u4e00\u4eba\uff0c\u4e00\u7167\u9762\u5c31\u628a\u548c\u6211\u52a8\u624b\u7684\u90a3\u4e24\u4e2a\u5bb6\u4f19\u6254\u4e86\u51fa\u53bb\uff01\u90a3\u62bc\u9635\u7684\u8001\u4eba\u8dc3\u4e86\u4e0a\u6765\uff0c\u95ea \u7535\u822c\u7684\u75be\u53d1\u4e24\u638c\uff0c\u6211\u521a\u521a\u51fa\u638c\u76f8\u62b5\uff0c\u8033\u8fb9\u6709\u4eba\u53eb\u9053\uff1a\u201c\u8d70\u5f00\uff01\u201d\u968f\u5373\u542c\u5f97\u90a3\u8001\u8005\u5927\u53eb\u4e00\u58f0\u5012 \u7eb5\u51fa\u53bb\uff0c\u631f\u8d77\u4e24\u4e2a\u540c\u5925\u4fbf\u8dd1\uff0c\u6211\u8fd9\u65f6\u624d\u770b\u6e05\u695a\u6551\u6211\u7684\u4eba\u7adf\u7136\u662f\u4e2a\u7f8e\u8c8c\u5973\u5b50\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u5fc3\u7075\u4e00\u9707\uff0c\u53eb\u51fa\u58f0\u9053\uff1a\u201c\u7389\u7f57\u5239\uff01\u201d\u767d\u654f\u9053\uff1a\u201c\u4ec0\u9ebd\u7389\u7f57\u5239\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u8fd9 \u5973\u7684\u53eb\u7389\u7f57\u5239\uff0c\u662f  \u5357\u5267\u76d7\uff0c\u4f60\u4e0d\u77e5\u9053\u9ebd\uff1f\u201d\u767d\u654f\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u8ba4\u5f97\u5979\u7684\uff0c\u602a\u4e0d\u5f97\u5979\u53eb\u6211 \u627e\u4f60\u4e86\u3002\u518d\u8bf4\u90a3\u65e5\u7684\u60c5\u5f62\uff0c\u90a3\u8001\u6c49\u8dd1\u4e86\uff0c\u5979\u4e5f\u4e0d\u8ffd\uff0c\u53ea\u662f\u5728\u5f8c\u9762\u7b11\u9053\uff0c\u4f60\u7684\u9634\u98ce\u6bd2\u7802\u638c\u4e0d\u574f \u554a\uff0c\u51e0\u65f6\u54b1\u4eec\u518d\u6597\u4e00\u6597\u3002\u90a3\u8001\u8005\u5df2\u53bb\u8fdc\u4e86\u3002\u5979\u7a81\u7136\u634f\u7740\u6211\u7684\u624b\u638c\u7ffb\u6765\u8986\u53bb\u7684\u770b\uff0c\u6211\u8bf4\uff1a \u201c\u5582\uff0c\u4f60\u4e5f\u8981\u7ed9\u6211\u770b\u76f8\u9ebd\uff01\u201d\u5979\u8bf4\uff1a\u201c\u50bb\u5c0f\u5b50\uff0c\u8c01\u7ed9\u4f60\u770b\u76f8\uff0c\u4f60\u4e2d\u4e86\u90a3\u8001\u8d3c\u7684\u6bd2\u638c\u5566\uff01\u201d\u968f \u5373\u6478\u51fa\u4e00\u7c92\u836f\u4e38\uff0c\u53eb\u6211\u541e\u4e0b\uff0c\u53c8\u9053\uff1a\u201c\u6211\u53ea\u80fd\u7ed9\u4f60\u4fdd\u7740\u5143\u6c14\uff0c\u4f7f\u4f60\u7684\u6b66\u529f\u4e0d\u81f4\u56e0\u6b64\u51cf\u635f\uff0c\u9634 \u98ce\u6bd2\u7802\u638c\u7684\u4f24\u6211\u53ef\u4e0d\u4f1a\u533b\u3002\u4f60\u8d76\u5feb\u627e\u5353\u4e00\u822a\u53bb\uff0c\u4ed6\u662f\u6b66\u5f53\u6d3e\u7d2b\u9633\u9053\u4eba\u7684\u5ae1\u4f20\uff0c\u7d2b\u9633\u8fd9\u8001\u9053\u6700 \u62ff\u624b\u533b\u6cbb\u90aa\u6bd2\uff0c\u53bb\uff0c\u5feb\u53bb\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4f60\u7684\u4f24\u52bf\u4e0d\u91cd\uff0c\u539f\u6765\u662f\u7389\u7f57\u5239\u7528\u836f\u7ed9\u4f60\u4fdd\u4f4f\u5143\u6c14\u3002\u201d\u533b\u6cbb\u90aa\u6bd2\u6697 \u4f24\uff0c\u662f\u6b66 \u5f53\u6d3e\u7d2b\u9633\u9053\u4eba\u7684\u4e13\u957f\uff0c\u5353\u4e00\u822a\u5728\u5e08\u95e8\u4e00\u5341\u4e8c\u5e74\uff0c\u4e5f\u66fe\u5f97\u4f20\u79d8\u6280\u3002\u5f53\u4e0b\u53d6\u4e86\u91d1\u9488\uff0c \u7ed9\u4ed6\u523a\u7a74\u89e3\u6bd2\uff0c\u7136 \u5f8c\u66ff\u4ed6\u63a8\u8840\u8fc7\u5bab\u3002\u5fd9\u4e86\u4e00\u9635\uff0c\u624b\u672f\u505a\u5b8c\uff0c\u767d\u654f\u5df2\u547c\u547c\u719f\u7761\u3002 \u3000\u3000\u5353\u4e00\u822a\u518d\u53bb\u63a2\u671b\u94a6\u5dee\uff0c\u94a6\u5dee\u4e5f\u5728\u719f\u7761\u4e4b\u4e2d\u3002\u5353\u4e00\u822a\u9080\u966a\u4f34\u94a6\u5dee\u6765\u7684\u79e6\u6307\u6325\u5230\u5c4b\u5f8c\u82b1\u56ed\u884c \u8d70\uff0c\u8bf4\u9053\uff1a\u201c\u82e5\u6709\u4ec0\u9ebd\u4e8b\u60c5\u53d1\u751f\uff0c\u4f60\u53ef\u4ee5\u5e26\u94a6\u5dee\u5927\u4eba\u4ece\u897f\u89d2\u4fa7\u95e8\u8d70\u51fa\uff0c\u5916\u9762\u6709\u50fb\u5f84\u76f4\u901a\u5c71 \u4e0a\u3002\u201d\u53c8\u5e26\u4ed6\u5728\u5c4b\u524d\u5c4b\u5f8c\uff0c\u8d70\u4e86\u4e00\u904d\uff0c\u8ba9\u4ed6\u719f\u6089\u9053\u8def\uff0c\u7136\u5f8c\u56de\u8f6c\u5bb6\u4e2d\uff0c\u5429\u5490\u5bb6\u4eba\u5728\u706b\u623f\u70e7\u8d77 \u5341\u5927\u9505\u70ed\u6c34\uff0c\u5c06\u767d\u654f\u548c\u4e24\u4f4d\u94a6\u5dee\u62ac\u4eba\u706b\u623f\uff0c\u53eb\u79e6\u6307\u6325\u548c\u4e00\u4e2a\u8001\u5bb6\u4eba\u98df\u4e86\u6781\u51c9\u7684\u836f\u5242\u4e4b\u5f8c\uff0c\u5165 \u5185\u670d\u4f8d\u4ed6\u4eec\uff0c\u628a\u4ed6\u4eec\u8863\u670d\u8131\u5149\uff0c\u5229\u7528\u6c34\u84b8\u6c7d\u7684\u70ed\u529b\u5c06\u4ed6\u4eec\u4f53\u5185\u7684\u6bd2\u8feb\u53d1\u51fa\u6765\u3002\u8fc7\u4e86\u4e24\u4e2a\u65f6 \u8fb0\uff0c\u6253\u5f00\u623f\u95e8\uff0c\u8001\u5bb6\u4eba\u5df2\u70ed\u5f97\u51e0\u4e4e\u6655\u5012\uff0c\u5353\u4e00\u822a\u548c\u79e6\u6307\u6325\u66ff\u53c1\u4eba\u7a7f\u597d\u8863\u670d\uff0c\u62ac\u4e86\u51fa\u6765\uff0c\u53c8\u628a \u71ac\u597d\u4e86\u7684\u4e0a\u597d\u4eba\u53c2\u6c41\u704c\u7ed9\u4ed6\u4eec\u670d\u4e0b\uff0c\u7136\u5f8c\u518d\u66ff\u4ed6\u4eec\u6309\u6469\u4e86\u4e00\u4f1a\uff0c\u770b\u7740\u4ed6\u4eec\u719f\u7761\u4e4b\u5f8c\uff0c\u7136\u5f8c\u79bb \u5f00\u3002\uff0e \u5353\u4e00\u822a\u5fd9\u4e86\u4e00\u5929\uff0c\u8fd9\u65f6\u5df2\u4ea4\u5348\u591c\uff0c\u8001\u7ba1\u5bb6\u62a5\u9053\uff1a\u201c\u5ef6\u5b89\u77e5\u5e9c\u66fe\u6d3e\u8fc7\u4eba\u6765\u95ee\u8baf\uff0c\u5f53\u65f6 \u4ee5\u5c11\u7237\u4e8b\u5fd9\uff0c\u6240\u4ee5\u6ca1\u6709\u7980\u77e5\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u660e\u5929\u62ff\u4e00\u5f20\u8c22\u5e16\u53bb\u5427\u3002\u5230\u5f00\u4e27\u65f6\u518d\u5bc4\u5367\u95fb\u3002\u201d \u5bf9\u8fd9\u4e9b\u5c0f\u4e8b\uff0c\u5353\u4e00\u822a\u4e5f\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u81ea\u53bb\u7761\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e24\u4f4d\u94a6\u5dee\u548c\u767d\u654f\u90fd\u5df2\u7cbe\u795e\u6e05\u723d\uff0c\u53ef\u8fdb\u8584\u7ca5\uff0c\u5230\u4e86\u9ec4\u660f\uff0c\u767d\u654f\u9664\u4e86\u4f53\u529b\u5c1a\u672a\u5b8c\u5168\u6062 \u590d\u4e4b\u5916\uff0c\u4e00\u5207\u5df2\u5982\u5e38\u4eba\u3002\u5353\u4e00\u822a\u548c\u4ed6\u5728\u4e66\u623f\u95f2\u8bdd\uff0c\u89c1\u4ed6\u5fc3\u5730\u7eaf\u539a\uff0c\u8bf4\u5f97\u9887\u4e3a\u6ca1\u673a\u3002\u6b63\u8bf4\u8bdd \u95f2\uff0c\u5ffd\u7136\u95e8\u5916\u4eba\u9a6c\u55a7\u817e\uff0c\u8001\u5bb6\u4eba\u8fdb\u6765\u7980\u9053\uff1a\u201c\u5e9c\u91cc\u7684\u738b\u5175\u5907\u5e26\u9886\u4eba\u9a6c\u6765\u5230\uff0c\u8bf4\u8981\u62dc\u89c1\u5c11 \u7237\u3002\u201d\u5353\u4e00\u822a\u76b4\u4e86\u7709\u5934\uff0c\u5fc3\u9053\uff1a\u7237\u7237\u53c8\u4e0d\u662f\u73b0\u804c\u5b98\u5458\uff0c\u4ed6\u4f55\u5fc5\u8fd9\u6837\u5df4\u7ed3\uff01\u8bf4\u58f0\uff1a\u201c\u8bf7\u201d\uff0c\u6b65 \u51fa\u5927\u5385\uff0c\u738b\u5175\u5907\u5df2\u5e26\u4e86\u4e8c\u53c1\u5341\u540d\u5175\u52c7\uff0c\u5927\u8e0f\u6b65\u8d70\u4e0a\u5385\u6765\u3002\u5353\u4e00\u822a\u988a\u4e3a\u5947\u602a\uff0c\u5fc3\u60f3\u8fd9\u5b98\u513f\u4f55\u4ee5 \u5982\u6b64\u65e0\u793c\u3002\u4ed6\u8fd8\u4ee5\u4e3a\u738b\u5175\u5907\u662f\u5e26\u5175\u6765\u66ff\u4ed6\u5b88\u95e8\u6267\u5f79\uff0c\u90a3\u6599\u738b\u5175\u5907\u5ffd\u7136\u559d\u9053\uff1a\u201c\u5353\u4e00\u822a\u4f60\u77e5\u7f6a \u9ebd\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u6709\u4f55\u7f6a\uff1f\u201d\u738b\u5175\u5907\u9053\uff1a\u201c\u4f60\u7a9d\u85cf\u53db\u5f92\uff0c\u72af\u4e86\u5927\u7f6a\u3002\u201d\u5353\u4e00\u822a\u6012\u9053\uff1a \u201c\u6211\u5bb6\u4e16\u4ee3\u4e3a\u5b98\uff0c\u4f60\u6562\u80e1\u8bf4\u516b\u9053\u3002\u201d\u738b\u5175\u5907\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u6562\u4ed7\u52bf\u6b3a\u4eba\uff0c\u641c\uff01\u201d\u5175\u4e01\u5411\u5185 \u5802\u6d8c\u5165\uff0c\u5353\u4e00\u822a\u559d\u9053\uff1a\u201c\u4f60\u6562\u60ca\u52a8\u94a6\u5dee\uff01\u201d\u738b\u5175\u5907\u9053\uff1a\u201c\u6211\u5949\u6709\u671d\u5ef7\u4e4b\u547d\uff0c\u6b63\u60f3\u6765\u89c1\u94a6 \u5dee\u3002\u201d\u4e66\u623f\u91cc\u4e52\u4e52\u4e53\u4e53\u6253\u4e86\u8d77\u6765\uff0c\u5353\u4e00\u822a\u53eb\u9053\uff1a\u201c\u767d\u8d24\u5f1f\uff0c\u4e0d\u8981\u52a8\u6b66\uff0c\u54b1\u4eec\u548c\u4ed6\u5230\u5ef6\u5b89\u5e9c\u8bb2 \u7406\u53bb\uff01\u201d\u738b\u5175\u5907\u53c8\u53eb\u4eba\u7ed1\u4ed6\uff0c\u5353\u4e00\u822a\u6012\u6781\u51b7\u7b11\uff0c\u53cc\u624b\u5728\u7d2b\u6a80\u6728\u9020\u7684\u516b\u4ed9\u53f0\u4e0a\u4e00\u6309\uff0c\u684c\u5b50\u987f\u65f6 \u5012\u584c\u3002\u5353\u4e00\u822a\u559d\u9053\uff1a\u201c\u4f60\u597d\u8bf4\u4fbf\u7f62\uff0c\u4f60\u82e5\u52a8\u7c97\uff0c\u6211\u5c31\u628a\u4f60\u6253\u4e86\uff0c\u518d\u5230\u4eac\u57ce\u8bf7\u7f6a\u3002\u201d\u738b\u5175\u5907\u8eab \u8fb9\u7684\u4e24\u540d\u519b\u5b98\u631f\u4e86\u631f\u773c\u3002\u738b\u5175\u5907\u4f1a\u610f\u9053\uff1a\u201c\u597d\uff0c\u59d1\u5ff5\u4f60\u662f\u5927\u81e3\u4e4b\u5f8c\uff0c\u7ed9\u4f60\u7559\u4e00\u70b9\u9762\u5b50\u3002\u201d\u5353 \u4e00\u822a\u62a2\u5728\u738b\u5175\u5907\u4e4b\u524d\uff0c\u76f4\u5165\u5185\u95f4\u9759\u5ba4\uff0c\u63a8\u95e8\u4e00\u770b\uff0c\u4e24\u4e2a\u94a6\u5dee\u90fd\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5353\u4e00\u822a\u5403\u4e86\u4e00\u9a7e\uff0c\u5fc3\u60f3\uff1a\u83ab\u975e\u4ed6\u4eec\u7591\u5fc3\u662f\u5f3a\u76d7\u6765\u52ab\uff0c\u6240\u4ee5\u8dd1\u4e86\u3002\u738b\u5175\u5907\u8ddf\u4e86\u8fdb\u6765\uff0c\u51b7\u7b11 \u95ee\u9053\uff1a\u201c\u94a6\u5dee\u5462\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u8ba9\u6211\u53bb\u627e\u4ed6\u3002\u201d\u738b\u5175\u5907\u9053\uff1a\u201c\u94a6\u5dee\u90fd\u7ed9\u4f60\u5bb3\u6b7b\u4e86\uff0c\u4f60\u8fd8 \u5230\u90a3\u91cc\u53bb\u627e\uff01\u201d\u5353\u4e00\u822a\u5fc3\u5ff5\u4e00\u52a8\uff0c\u84e6\u7136\u56de\u8fc7\u5934\u6765\uff0c\u53cd\u624b\u4e00\u6293\uff0c\u559d\u9053\uff1a\u201c\u5b9a\u662f\u4f60\u8fd9  \u4e0b\u7684\u6bd2 \u624b\uff01\u201d\u738b\u5175\u5907\u80cc\u5f8c\u4e00\u540d\u519b\u5b98\uff0c\u500f\u7684\u51b2\u4e0a\uff0c\u4f38\u81c2\u76f8\u683c\uff0c\u53d8\u638c\u64d2\u62ff\uff0c\u5353\u4e00\u822a\u548c\u4ed6\u63a5\u4e86\u4e00\u62db\uff0c\u7adf\u662f \u672a\u5206\u9ad8\u4e0b\u3002\u90a3\u540d\u519b\u5b98\u559d\u9053\uff1a\u201c\u4f60\u5bb3\u6b7b\u94a6\u5dee\uff0c\u8fd8\u6562\u62d2\u6355\uff01\u201d\u5353\u4e00\u822a\u5b9a\u4e86\u5b9a\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u8fd9 \u5b98\u53f8\u6211\u548c\u4f60\u6253\u5230\u5317\u4eac\u3002\u201d\u90a3\u540d\u519b\u5b98\u53d6\u51fa\u9563\u94d0\uff0c\u559d\u9053\uff1a\u201c\u9002\u624d\u672a\u6709\u5b9e\u636e\uff0c\u8fd8\u53ef\u7531\u4f60\u62b5\u8d56\uff0c\u73b0\u5728 \u94a6\u5dee\u4e0d\u89c1\uff0c\u4f60\u8fd8\u6709\u4f55\u53ef\u8bf4\uff1f\u56fd\u6cd5\u4ff1\u5728\uff0c\u53ef\u7531\u4e0d\u5f97\u4f60\u9a84\u6a2a\u653e\u8086\u4e86\uff0c\u5feb\u628a\u5211\u5177\u5e26\u4e0a\u3002\u201d\u5353\u4e00\u822a\u9762 \u8272\u500f\u53d8\uff0c\u5f85\u8981\u62d2\u6355\uff0c\u4f46\u8f6c\u5ff5\u81ea\u5df1\u7956\u7236\u7236\u4eb2\u90fd\u662f\u671d\u5ef7\u5927\u81e3\uff0c\u82e5\u7136\u62d2\u6355\uff0c\u90a3\u5c31\u5750\u5b9e\u4e86\u53db\u9006\u4e4b\u540d\uff0c \u5c82\u4e0d\u6709\u8fb1\u95e8\u6963\uff0c\u5982\u6b64\u4e00\u60f3\uff0c\u4e0d\u89c9\u628a\u624b\u5782\u4e86\u4e0b\u6765\uff0c\u8ba9\u90a3\u540d\u519b\u5b98\u628a\u4ed6\u7684\u53cc\u624b\u5957\u5728\u94d0\u4e2d\u3002 \u3000\u3000\u8fd9\u4e00\u95f9\u628a\u5353\u5bb6\u5413\u5f97\u72d7\u8d70\u9e21\u98de\uff0c\u8001\u5bb6\u4eba\u557c\u557c\u54ed\u54ed\uff0c\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u5723\u4e0a\u660e\u9274 \u4e07\u91cc\uff0c\u8fd9\u51a4\u5c48\u5fc5\u7136\u80fd\u7533\u3002\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u60f3\u5230\u7236\u4eb2\u7684\u6789\u6b7b\uff0c\u5374\u4e5f\u5bd2\u5fc3\u3002\u5353\u4e00\u822a\u53c8\u5429\u5490\u7ba1\u5bb6 \u9053\uff1a\u201c\u4f60\u597d\u597d\u770b\u5b88\u8001\u5927\u4eba\u7684\u7075\u5802\u3002\u201d\u738b\u5175\u5907\u50ac\u9053\uff1a\u201c\u5feb\u8d70\uff01\u201d\u628a\u5353\u4e00\u822a\u63a8\u51fa\u5927\u95e8\uff0c\u767d\u654f\u65e9\u5df2 \u88ab\u4e94\u82b1\u5927\u7ed1\uff0c\u62bc\u5728\u95e8\u5916\u7b49\u5019\u4e86\u3002 \u3000\u3000\u5b98\u519b\u8fde\u591c\u5c06\u4e8c\u4eba\u62bc\u8d70\uff0c\u5230\u4e86\u5ef6\u5b89\u5e9c\u5929\u5df2\u5927\u660e\u3002\u5019\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5f00\u5802\u5ba1\u95ee\uff0c\u95ee\u5b98\u5374\u4e0d\u662f\u5ef6 \u5b89\u77e5\u5e9c\uff0c\u800c\u662f\u53e6\u4e00\u4e2a\u4e8c\u54c1\u9876\u6234\u7684\u5b98\u513f\uff0c\u5148\u95ee\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u5bb6\u4e16\u53d7\u56fd\u6069\uff0c\u4e3a\u4f55\u5374\u56fe\u8c0b\u53db\u9006\uff0c \u6697\u5bb3\u94a6\u5dee\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6697\u5bb3\u94a6\u5dee\u7684\uff0c\u786e\u6709\u5176\u4eba\uff0c\u4f46\u5374\u4e0d\u662f\u6211\u3002\u201d\u95ee\u5b98\u9053\uff1a\u201c\u90a3\u5374\u662f \u8c01\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5927\u4eba\u82e5\u7ed9\u6211\u4e00\u6708\u4e4b\u671f\uff0c\u6211\u5c06\u6697\u5bb3\u94a6\u5dee\u7684\u4eba\u6349\u7ed9\u4f60\u770b\u3002\u201d\u95ee\u5b98\u5c06\u60ca\u5802\u6728\u4e00 \u62cd\uff0c\u559d\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u672c\u5b98\u53ef\u4e0d\u662f\u53c1\u5c3a\u5c0f\u7ae5\uff0c\u8ba9\u4f60\u82b1\u8a00\u5de7\u8bed\u8499\u8fc7\uff0c\u653e\u4f60\u9003\u8dd1\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a \u201c\u6211\u82e5\u60f3\u9003\u8dd1\uff0c\u4e5f\u4e0d\u5230\u8fd9\u91cc\u6765\u4e86\u3002\u201d\u95ee\u5b98\u53c8\u5c06\u60ca\u5802\u6728\u4e00\u62cd\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4f60\u5c31\u4ece\u5b9e\u62db\u6765\uff01\u201d\u5353 \u4e00\u822a\u9053\uff1a\u201c\u65e0\u8bdd\u53ef\u62db\uff01\u201d\u95ee\u5b98\u9053\uff1a\u201c\u4f60\u8bf4\u4f60\u6ca1\u6709\u6697\u5bb3\u94a6\u5dee\uff0c\u90a3\u4f60\u53c8\u600e\u77e5\u6697\u5bb3\u94a6\u5dee\u7684\u53e6\u6709\u5176 \u4eba\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u8fd9\u8bdd\u6211\u8981\u89c1\u4e07\u5c81\u7237\u624d\u8bf4\u3002\u201d\u95ee\u5b98\u6309\u6848\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u96be\u9053\u6211\u5c31\u4e0d\u914d\u95ee \u4f60\uff01\u201d\u5353\u4e00\u822a\u95ed\u53e3\u4e0d\u7b54\uff0c\u95ee\u5b98\u624b\u6293\u7b7e\u7b52\uff0c\u60f3\u662f\u8981\u559d\u4ee4\u7528\u5211\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5374\u53c8\u5fcd\u4f4f\uff0c\u559d\u9053\uff1a \u201c\u5c06\u90a3\u540d\u53db\u8d3c\u62bc\u4e0a\u6765\uff01\u201d\u5175\u4e01\u5c06\u767d\u654f\u63a8\u4e0a\uff0c\u95ee\u5b98\u9053\uff1a\u201c\u4f60\u59d3\u751a\u540d\u8c01\uff0c\u90a3\u91cc\u4eba\u6c0f\uff1f\u201d\u767d\u654f\u9053\uff1a \u201c\u6211\u53eb\u767d\u654f\uff0c\u5317\u4eac\u4eba\u6c0f\u3002\u201d\u95ee\u5b98\u9053\uff1a\u201c\u4f60\u662f\u592a\u5b50\u503c\u6bbf\u6b66\u5e08\u5b5f\u707f\u7684\u5f92\u5f1f\uff0c\u662f\u5417\uff1f\u201d\u767d\u654f\u9053\uff1a \u201c\u662f\u5440\uff0c\u4f60\u4e5f\u77e5\u9053\u5417\uff01\u201d\u95ee\u5b98\u5c06\u60ca\u5802\u6728\u4e00\u62cd\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4e07\u91cc\u8fe2\u8fe2\uff0c\u6765\u5230\u5ef6\u5b89\uff0c\u6240\u4e3a\u4f55\u4e8b\uff0c \u4ece\u5b9e\u62db\u6765\uff0c\u4e0d\u5f97\u9690\u7792\uff01\u201d\u767d\u654f\u633a\u80f8\u8bf4\u9053\uff1a\u201c\u5927\u4e08\u592b\u4f5c\u4e8b\uff0c\u4f55\u5fc5\u9690\u7792\u3002\u6211\u5230\u5ef6\u5b89\u6765\u627e\u670b\u53cb\uff0c\u53f9 \u9053\u4e5f\u4e0d\u8bb8\u9ebd\uff1f\u201d\u95ee\u5b98\u9053\uff1a\u201c\u4f60\u8981\u627e\u7684\u662f\u8c01\uff1f\u201d\u767d\u654f\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u738b\u7167\u5e0c\uff01\u201d\u95ee\u5b98\u5c06\u60ca\u5802\u6728\u62cd \u5f97\u9707\u5929\u4ef7\u54cd\uff0c\u5802\u4e0b\u5927\u58f0\u5406\u559d\uff0c\u966a\u5ba1\u7684\u5ef6\u5b89\u77e5\u5e9c\u53d8\u4e86\u989c\u8272\u3002 \u3000\u3000\u95ee\u5b98\u53eb\u5f55\u4e8b\u5c06\u4f9b\u8bcd\u5f55\u4e86\uff0c\u4ea4\u7ed9\u767d\u654f\u770b\u8fc7\uff0c\u53eb\u4ed6\u5212\u62bc\uff0c\u767d\u654f\u770b\u89c1\u6240\u5f55\u4e0d\u8bef\uff0c\u60f3\u4e5f\u4e0d\u60f3\uff0c\u63d0 \u8d77\u7b14\u6765\u4fbf\u5212\u4e86\u62bc\u3002\u95ee\u5b98\u5c06\u4f9b\u8bcd\u9012\u7ed9\u5ef6\u5b89\u77e5\u5e9c\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4fbf\u5b8c\u4e86\uff01\u201d\u53c8\u5c06\u60ca\u5802\u6728\u4e00\u62cd\uff0c\u5bf9\u5353 \u4e00\u822a\u559d\u9053\uff1a\u201c\u4f60\u7684\u540c\u4f34\u5df2\u7ecf\u62db\u4e86\uff0c\u4f60\u8fd8\u4e0d\u62db\uff1f\u201d\u5353\u4e00\u822a\u832b\u7136\u4e0d\u89e3\uff0c\u8bf4\u9053\uff1a\u201c\u62db\u4e86\u4ec0\u9ebd\uff01\u201d\u5ef6 \u5b89\u77e5\u5e9c\u559d\u9053\uff1a\u201c\u738b\u7167\u5e0c\u7236\u5b50\u662f\u672c\u5e9c\u5267\u76d7\uff0c\u8c01\u4e2a\u4e0d\u77e5\uff0c\u90a3\u4e2a\u4e0d\u6653\uff1f\u201d\u5353\u4e00\u822a\u5403\u4e86\u4e00\u60ca\uff0c\u987f\u65f6\u5446 \u4f4f\u3002\u95ee\u5b98\u9053\uff1a\u201c\u4f60\u79c1\u901a\u5267\u76d7\uff0c\u4fbf\u662f\u4e2a\u5927\u5927\u7684\u7f6a\u540d\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u968f\u4f60\u8bf4\u53bb\uff0c\u6211\u4e0e\u4f60\u5230\u4eac\u5e08 \u5927\u7406\u5e9c\u53bb\u8bb2\u3002\u201d\u95ee\u5b98\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u60f3\u5230\u4eac\u5e08\uff01\u201d\u53eb\u72f1\u5352\u5c06\u4ed6\u62bc\u5165\u76d1\u7262\uff0c\u5353\u4e00\u822a\u53c8\u60ca\u53c8\u6012\uff0c \u767d\u654f\u5728\u4ed6\u8eab\u8fb9\u95ee\u9053\uff1a\u201c\u90a3\u738b\u7167\u5e0c\u771f\u662f\u5f3a\u76d7\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u95ed\u53e3\u4e0d\u7b54\uff0c\u9762\u8272\u94c1\u9752\u3002\u767d\u654f\u96be\u8fc7\u81f3 \u6781\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u662f\u6211\u8fde\u7d2f\u4f60\u4e86\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4e0d\u5173\u4f60\u4e8b\u3002\u201d\u7262\u5934\u559d\u9053\uff1a\u201c\u72af\u4eba\u4e0d\u8bb8\u79c1\u81ea \u4ea4\u8c08\u3002\u201d\u5c06\u4e24\u4eba\u5206\u5f00\u62bc\u5165\u76d1\u623f\u3002 \u3000\u3000\u5353\u4e00\u822a\u4e00\u4eba\u4f4f\u4e00\u4e2a\u76d1\u623f\uff0c\u623f\u95f4\u5c45\u7136\u9887\u4e3a\u6574\u6d01\uff0c\u4e0d\u50cf\u662f\u666e\u901a\u76d1\u623f\u3002\u4f4f\u4e86\u53c1\u5929\uff0c\u4e5f\u4e0d\u89c1\u6709\u4eba \u63d0\u95ee\u3002\u5fc3\u4e2d  \u76fc\u5bb6\u4eba\u80fd\u6765\u63a2\u76d1\uff0c\u597d\u8bf7\u7956\u7236\u7684\u95e8\u751f\u6545\u65e7\u8425\u6551\u3002\u4f46\u53c1\u5929\u8fc7\u53bb\uff0c\u5374\u65e0\u4eba\u6765\uff0c\u4e0d\u77e5\u662f \u7ba1\u5bb6\u7684\u6015\u4e8b\uff0c\u8fd8\u662f\u5e9c\u91cc\u4e0d\u51c6\u3002\u5230\u4e86\u7b2c\u56db\u5929\u665a\uff0c\u5ffd\u7136\u738b\u5175\u5907\u548c\u90a3\u65e5\u4e0e\u81ea\u5df2\u4ea4\u8fc7\u624b\u7684\u90a3\u4e2a\u519b\u5b98\uff0c \u5f00\u4e86\u76d1\u623f\uff0c\u5c06\u5353\u4e00\u822a\u63d0\u4e86\u51fa\u6765\uff0c\u7a7f\u623f\u7ed5\u5ba4\uff0c\u8d70\u4e86\u597d\u4e45\uff0c\u628a\u4ed6\u63a8\u5165\u4e00\u95f4\u5c0f\u623f\uff0c\u623f\u95e8\u8fc5\u901f\u5173\u4e0a\uff0c \u5353\u4e00\u822a\u62ac\u5934\u4e00\u770b\uff0c\u623f\u4e2d\u7aef\u5750\u7740\u4e00\u4e2a\u7ea2\u9762\u8001\u4eba\uff0c\u773c\u5149\u9634\u68ee\u53ef\u6015\u3002\u62db\u624b\u53eb\u5353\u4e00\u822a\u5750\u4e0b\uff0c\u542b\u7b11\u8bf4 \u9053\uff1a\u201c\u592a\u5b50\u5f88\u8d4f\u8bc6\u4f60\u3002\u201d\u5353\u4e00\u822a\u6478\u4e0d\u7740\u5934\u8111\uff0c\u90a3\u8001\u4eba\u53c8\u9053\uff1a\u201c\u4e07\u5c81\u7237\u5e74\u7eaa\u8001\u8fc8\u591a\u75c5\uff0c\u592a\u5b50\u4e0d \u4e45\u5f53\u53ef\u767b\u57fa\uff0c\u4f46\u6709\u8bb8\u591a\u4e8b\u60c5\uff0c\u4e5f\u8bb8\u8fd8\u8981\u4ef0\u4ed7\u9b4f\u516c\u516c\u3002\u201d\u5353\u4e00\u822a\u53d8\u8272\u8bf4\u9053\uff1a\u201c\u6211\u662f\u72af\u4eba\uff0c\u4f60\u8981 \u5ba1\u4fbf\u5ba1\uff0c\u8bf4\u8fd9\u4e9b\u8bdd\u5e72\u5417\uff1f\u201d\u90a3\u8001\u4eba\u9053\uff1a\u201c\u9b4f\u516c\u516c\u4e5f\u5f88\u8d4f\u8bc6\u4f60\u3002\u201d\u5353\u4e00\u822a\u6012\u9053\uff1a\u201c\u8c01\u8981\u4ed6\u8d4f \u8bc6\uff1f\u201d\u7ea2\u9762\u8001\u4eba\u9053\uff1a\u201c\u4f60\u5012\u662f\u4e00\u6761\u786c\u6c49\uff0c\u4f46\u4f60\u53ef\u77e5\u9053\u4f60\u7684\u6027\u547d\u5374\u634f\u5728\u6d12\u5bb6\u624b\u4e2d\u3002\u201d\u5353\u4e00\u822a\u51b7 \u7b11\u9053\uff1a\u201c\u4f60\u60f3\u600e\u6837\uff01\u201d\u7ea2\u9762\u8001\u4eba\u5ffd\u9053\uff1a\u201c\u90d1\u6d2a\u53f0\u662f\u4f60\u7684\u8001\u76f8\u8bc6\u4e86\uff01\u201d\u5353\u4e00\u822a\u5fc3\u5934\u4e00\u9707\uff0c\u9053\uff1a \u201c\u600e\u9ebd\u6837\uff1f\u201d\u7ea2\u9762\u8001\u4eba\u9053\uff1a\u201c\u4ed6\u4e34\u6b7b\u524d\u5bf9\u4f60\u8bf4\u4e9b\u4ec0\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u9ebd\uff01\u6211\u4e0d\u77e5 \u9053\uff01\u201d\u7ea2\u9762\u8001\u4eba\u7b11\u9053\uff1a\u201c\u771f\u4eba\u9762\u524d\u4e0d\u8bf4\u5047\u8bdd\uff0c\u6211\u53eb\u4e91\u71d5\u5e73\u3002\u4f60\u542c\u8fc7\u6211\u7684\u540d\u5b57\u9ebd\uff01\u201d\u5353\u4e00\u822a\u84e6 \u5730\u4e00\u58f0\u5927\u543c\uff0c\u53cc\u81c2\u5f80\u5916\u4e00\u5206\uff0c\u624b\u94d0\u987f\u65f6\u65ad\u88c2\uff0c\u5353\u4e00\u822a\u4e00\u638c\u626b\u53bb\uff0c\u559d\u9053\uff1a\u201c\u597d\uff0c\u539f\u6765\u4f60\u5c31\u662f\u5978 \u4eba\uff01\u201d\u7ea2\u9762\u8001\u4eba\u5411\u5f8c\u4e00\u5012\uff0c\u811a\u5c16\u4e00\u8e22\uff0c\u5c06\u5750\u51f3\u8e22\u5f97\u98de\u4e86\u8d77\u6765\uff0c\u53ea\u542c\u5f97\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u51f3\u5b50\u7ed9 \u5353\u4e00\u822a\u638c\u98ce\u5288\u88c2\u3002\u4e91\u71d5\u5e73\u89e3\u4e0b\u8170\u5e26\uff0c\u5411\u524d\u4e00\u6325\uff0c\u7b11\u9053\uff1a\u201c\u679c\u7136\u8bd5\u51fa\u6765\u4e86\uff0c\u5353\u4e00\u822a\u4f60\u5230\u5982\u4eca\u8fd8 \u6562\u8bf4\u5047\u8bdd\u5417\uff1f\u201d \u3000\u3000\u4f60\u9053\u5353\u4e00\u822a\u4f55\u4ee5\u5982\u6b64\u52a8\u6012\u3002\u539f\u6765\u90d1\u6d2a\u53f0\u4e34\u6b7b\u65f6\u66fe\u4f9b\u51fa\u4e94\u4e2a\u540c\u515a\uff0c\u90fd\u662f\u79c1\u901a\u6ee1\u6d32\u4e4b\u4eba\uff0c\u5176 \u4e2d\u53c1\u4e2a\u662f\u5927\u5185\u536b\u58eb\uff0c\u4e24\u4e2a\u662f\u7eff\u6797\u5f3a\u76d7\uff0c\u53c1\u4e2a\u536b\u58eb\u4e2d\uff0c\u6709\u4e00\u4e2a\u6b63\u662f\u53eb\u505a\u4e91\u71d5\u5e73\uff01 \u3000\u3000\u5353\u4e00\u822a\u63c9\u8eab\u8fdb\u638c\uff0c\u4e91\u71d5\u5e73\u5c06\u8170\u5e26\u4e00\u6325\uff0c\u9aa4\u7136\u547c\u547c\u4f5c\u54cd\uff0c\u5353\u4e00\u822a\u8fde\u62a2\u51e0\u62db\uff0c\u6a2a\u626b\u76f4\u5288\uff0c\u4e91 \u71d5\u5e73\u8eab\u6cd5\u8f7b\u7075\uff0c\u6597\u5ba4\u4e4b\u4e2d\uff0c\u56de\u65cb\u81ea\u5982\uff0c\u624b\u4e2d\u8170\u5e26\u4f7f\u5f97\u4f3c\u8f6f\u97ad\u4e00\u6837\uff0c\u6597\u4e86\u4e8c\u53c1\u5341\u62db\uff0c\u5353\u4e00\u822a\u5360 \u4e0d\u5230\u4e1d\u6beb\u4fbf\u5b9c\uff0c\u84e6\u7136\u60f3\u9053\uff1a\u201c\u4e8b\u5df2\u81f3\u6b64\uff0c\u6211\u4e0d\u5982\u9003\u4e86\u51fa\u53bb\uff0c\u7980\u544a\u592a\u5b50\u3002\u201d\u638c\u6cd5\u4e00\u7d27\uff0c\u53c8\u62a2\u4e86 \u51e0\u62db\uff0c\u5ffd\u7136\u4e00\u4e2a\u8f6c\u8eab\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\u5c06\u623f\u95e8\u8e22\u5f00\uff0c\u4e91\u71d5\u5e73\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u9003\u8d70\uff0c\u90a3  \u662f\u505a \u68a6\uff01\u201d\u5353\u4e00\u822a\u98de\u6b65\u7a9c\u51fa\uff0c\u84e6\u5730\u91cc\u638c\u98ce\u98d2\u7136\uff0c\u8fce\u9762\u5288\u81f3\uff0c\u5353\u4e00\u822a\u659c\u8eab\u6ed1\u6b65\uff0c\u6b63\u60f3\u51fa\u638c\u76f8\u6297\uff0c\u5ffd \u89c1\u7a9c\u8fdb\u4e00\u4eba\u638c\u5fc3\u4f3c\u6731\u7802\u822c\u7ea2\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u90a3\u4eba\u547c\u547c\u4e24\u638c\uff0c\u638c\u98ce\u52b2\u75be\uff0c\u5353\u4e00\u822a\u6012\u9053\uff1a\u201c\u96be\u9053\u6211 \u5c31\u6015\u4f60\u7684\u9634\u98ce\u6bd2\u7802\u638c\uff1f\u201d\u4f7f\u51fa\u4e94\u4e01\u8c79\u5c71\u638c\u6cd5\uff0c\u638c\u638c\u96c4\u52b2\uff0c\u62da\u4e0e\u90a3\u4eba\u4e24\u8d25\u4ff1\u4f24\uff0c\u90a3\u4eba\u4e0d\u6562\u8ff3 \u63a5\uff0c\u53cc\u638c  \u5f80\u5353\u4e00\u822a\u7a74\u9053\u62cd\u53bb\uff0c\u5353\u4e00\u822a\u4e0d\u6562\u7ed9\u4ed6\u78b0\u7740\u8eab\u8eaf\uff0c\u4e5f\u95ef\u4e0d\u51fa\u53bb\uff0c\u53cd\u7ed9\u4ed6\u8feb\u5f97\u53c8\u9000\u5230 \u623f\u95e8\uff0c\u4e91\u71d5\u5e73\u8170\u5e26\u4e00\u6296\uff0c\u5353\u4e00\u822a\u7ed9\u4ed6\u4e00\u5377\u4e00\u62c9\uff0c\u84e6\u7136\u4ec6\u5012\u3002\u7528\u9634\u98ce\u6bd2\u7802\u638c\u7684\u90a3\u8001\u5934\u8ddf\u8eab\u62a2 \u8fdb\uff0c\u5173\u4e86\u623f\u95e8\uff0c\u5728\u95e8\u53e3\u4e00\u7ad9\uff0c\u95ee\u9053\uff1a\u201c\u4e91\u5144\uff0c\u8bd5\u51fa\u6765\u4e86\u9ebd\uff1f\u201d\u4e91\u71d5\u5e73\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e0d\u80af\u5410 \u5b9e\uff0c\u91d1\u5144\u4f60\u8d4f\u4ed6\u4e00\u638c\u3002\u201d\u90a3\u59d3\u91d1\u7684\u8001\u5934\u62ac\u8d77\u624b\u638c\uff0c\u4f5c\u52bf\u5411\u5353\u4e00\u822a\u8111\u95e8\u62cd\u4e0b\uff0c\u5353\u4e00\u822a  \u7136\u4e0d \u60e7\u3002\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u6253\u6b7b\u6211\u4e5f\u6ca1\u6709\u7528\u3002\u6211\u6b7b\u5f8c\u6211\u7684\u670b\u53cb\u4f1a\u4e0a\u4eac\u544a\u5fa1\u72b6\uff0c\u5c06\u4f60\u4eec\u90fd\u6296\u9732\u51fa \u6765\u3002\u201d\u4e91\u71d5\u5e73\u8eab\u8eaf\u4e00\u9707\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u7389\u7f57\u5239\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u6602\u9996\u77aa\u76ee\uff0c\u50b2\u7136\u4e0d\u7406\uff0c\u90a3\u59d3\u91d1 \u7684\u8001\u5934\u9053\uff1a\u201c\u597d\uff0c\u77a7\u4e0d\u51fa\u4f60\u8fd9\u5c0f\u5b50\uff0c\u5c45\u7136\u6562\u548c\u7389\u7f57\u5239\u5f80\u6765\u3002\u201d\u4e91\u71d5\u5e73\u7a81\u53d1\u5978\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9 \u5c0f\u5b50\u5012\u53ef\u4ee5\u5927\u6d3e\u7528\u573a\u3002\u201d\u59d3\u91d1\u7684\u8001\u5934\u84e6\u7136\u98de\u8d77\u4e00\u811a\uff0c\u8e22\u4e2d\u5353\u4e00\u822a\u5f8c\u817f\u5f2f\u7684\u201c\u59d4\u4e2d\u7a74\u201d\uff0c\u8fd9\u7a74 \u9053\u6b63\u5f53\u5927\u817f\u9aa8\u4e0e\u80eb\u9aa8\u8054\u63a5\u7684\u9aa8\u7f1d\u95f4\uff0c\u662f\u4eba\u8eab\u4e5d\u4e2a  \u7a74\u4e4b\u4e00\uff0c\u5353\u4e00\u822a\u987f\u65f6\u6655\u5012\u3002\u4e91\u71d5\u5e73\u53eb\u738b\u5b88 \u5907\u8fdb\u6765\uff0c\u518d\u5c06\u4ed6\u9001\u5165\u76d1\u7262\u3002 \u3000\u3000\u5353\u4e00\u822a\u53bb\u5f8c\uff0c\u4e91\u71d5\u5e73\u4e0e\u90a3\u59d3\u91d1\u7684\u8001\u5934\u76f8\u89c6\u800c\u7b11\u3002\u539f\u6765\u4e0d\u72ec\u4ed6\u4eec\u4e8c\u4eba\u79c1\u901a\u6ee1\u6d32\uff0c\u8fde\u9b4f\u5fe0\u8d24 \u548c\u6ee1\u6d32\u4e5f\u6709\u5f80\u6765\u3002\u90d1\u6d2a\u53f0\u6b7b\u5f8c\uff0c\u5cb3\u545c\u73c2\u5230\u4e86\u5317\u4eac\uff0c\u628a\u90d1\u6d2a\u53f0\u4e34\u6b7b\u65f6\u8bf4\u51fa\u7684\u79d8\u5bc6\u544a\u8bc9\u4e86\u718a\u7ecf\u7565 \u201c\u5ef7\u5f3c\u201d\uff0c\u718a\u7ecf\u7565\u8fdb\u5bab\u9762\u5723\uff0c\u63ed\u53d1\u5185\u5978\uff0c\u660e\u795e\u5b97\u7b11\u4e3a\u201c\u4e0d\u7ecf\u4e4b\u8c08\u201d\uff0c\u6401\u4e0b\u4e0d\u7406\u3002\u90a3\u53c1\u4e2a\u5bab\u4e2d \u536b\u58eb\u6d88\u606f\u4e5f\u771f\u7075\u901a\uff0c\u4e00\u6709\u98ce\u95fb\uff0c\u7acb\u523b\u9003\u8d70\u3002\u795e\u5b97\u542c\u5f97\u90a3\u53c1\u540d\u536b\u58eb\u9003\u8d70\u7684\u6d88\u606f\uff0c\u5f8c\u6094\u5df3\u6765\u4e0d \u53ca\u3002 \u3000\u3000\u4f46\u90a3\u53c1\u540d\u536b\u58eb  \u662f\u9003\u51fa\u5bab\u5916\uff0c\u5374\u5e76\u672a\u9003\u51fa\u5317\u4eac\uff0c\u4ed6\u4eec\u4e0e\u9b4f\u5fe0\u8d24\u4ecd\u6709\u5f80\u6765\u3002\u90d1\u6d2a\u53f0\u4e0e\u9b4f\u5fe0 \u8d24\u5173\u7cfb\u8f83\u758f\uff0c\u4ed6\u4e0e\u6ee1\u6d32\u5bc6\u4f7f\u8054\u7edc\u65f6\uff0c  \u77e5\u90a3\u53c1\u540d\u536b\u58eb\u662f\u540c\u5925\uff0c\u5374\u4e0d\u77e5\u9b4f\u5fe0\u8d24\u4e5f\u662f\u3002\u800c\u9b4f\u5fe0\u8d24 \u5374\u77e5\u4ed6\u662f\u540c\u515a\uff0c\u4f46\u4e24\u4eba\u4ece\u4e0d\u8c08\u53ca\uff0c\u9b4f\u5fe0\u8d24\u4e5f\u6349\u6478\u4e0d\u900f\u90d1\u6d2a\u53f0\u662f\u5426\u4e5f\u77e5\u9053\u4ed6\u7684\u8eab\u5206\uff0c\u6240\u4ee5\u5927\u4e3a \u60f6\u6050\uff0c\u6697\u4e2d\u6d3e\u9063\u53c1\u540d\u536b\u58eb\u6765  \uff0c\u5e76\u6d3e\u51fa\u4e00\u540d\u5fc3\u8179\u5fa1\u540f\uff0c\u5047\u5145\u94a6\u4f7f\uff0c\u5230\u5ef6\u5b89\u5e9c\u6765\uff0c\u60f3\u4ece\u5353\u4e00\u822a \u5904\u6253\u63a2\u79d8\u5bc6\u3002\u9002\u503c\u7687\u5e1d\u6d3e\u4e86\u4e24\u540d\u94a6\u5dee\u5230\u5353\u5bb6\u5ba3\u53ec\uff0c\u9b4f\u5fe0\u8d24\u9042\u5b9a\u4e0b\u6bd2\u8ba1\uff0c\u53eb\u90a3\u4e24\u540d\u536b\u58eb\u6697\u5bb3\u94a6 \u5dee\uff0c\u79fb\u7978\u5353\u5bb6\uff0c\u597d\u85c9\u6b64\u7f6a\u540d\uff0c\u5c06\u5353\u4e00\u822a\u62ff\u6765\u5ba1\u95ee\u3002 \u3000\u3000\u8fd9\u4e24\u540d\u5927\u5185\u536b\u58eb\u4e00\u4e2a\u64c5\u957f\u65bc\u897f\u85cf\u5bc6\u5b97\u79d8\u4f20\u7684\u201c\u67d4\u529f\u201d\uff0c\u5373\u521a\u624d\u7528\u8170\u5e26\u6765\u548c\u5353\u4e00\u822a\u5bf9\u654c\u7684 \u4e91\u71d5\u5e73\u3002\u8fd9\u79cd\u201c\u67d4\u529f\u201d\u82e5\u7ec3\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u80fd\u4ee5\u81f3\u67d4\u800c\u514b\u81f3\u521a\uff0c\u4e91\u71d5\u5e73\u867d\u5c1a\u672a\u81fb\u7089\u706b\u7eaf\u9752 \u4e4b\u5883\uff0c\u4f46\u4e5f\u5df2\u6709\u4e86\u4e03\u516b\u6210\u706b\u5019\uff1a\u53e6\u4e00\u540d\u5219\u662f\u90a3\u4e2a\u4f7f\u9634\u98ce\u6bd2\u7802\u638c\u7684\u8001\u5934\u5b50\uff0c\u540d\u53eb\u91d1\u5343  \uff0c\u4ed6\u7684 \u6bd2\u7802\u638c\u80fd\u4ee4\u4eba\u53c1\u65e5\u4e4b\u5f8c\u6bd2\u53d1\uff0c\u4e03\u65e5\u4e4b\u5f8c\u8eab\u4ea1\uff0c\u80fd\u6740\u4eba\u65bc\u95f9\u5e02\u4e4b\u4e2d\u800c\u4e0d\u88ab\u53d1\u89c9\u3002\u8fd9\u6b21\u4ed6\u4eec\u5949\u4e86 \u9b4f\u5fe0\u8d24\u4e4b\u547d\uff0c\u5728\u9014\u4e2d\u6697\u7b97\u4e86\u94a6\u5dee\uff0c\u672c\u4ee5\u4e3a\u53ef\u79fb\u7978\u5353\u5bb6\uff0c\u4e0d\u6599\u5374\u7ed9\u5353\u4e00\u822a\u770b\u7834\uff0c\u5c06\u94a6\u5dee\u6551\u4e86\u3002 \u8fd9\u4e8b\u5f8c\u6765\u5f15\u8d77\u5bab\u5ef7\u4e2d\u7684\u6697\u6597\u660e\u4e89\uff0c\u6b64\u662f\u5f8c\u8bdd\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u518d\u8bf4\u5353\u4e00\u822a\u88ab\u70b9\u4e86\u201c\u59d4\u4e2d\u7a74\u201d\u5f8c\uff0c\u62bc\u56de\u76d1\u72f1\uff0c\u8d8a\u60f3\u8d8a\u6068\uff0c\u6012\u706b\u4e0a\u5347\uff0c\u66f4\u89c9  \u65e0\u529b\uff0c\u6697 \u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d\u5fc3\u60f3\uff1a\u6ee1\u6d32\u6697\u4e2d\u6536\u4e70\u5bab\u4e2d\u536b\u58eb\u3001\u7eff\u6797\u5927\u76d7\u3001\u5ef7\u81e3\u7763\u629a\uff0c\u8fd9\u4e8b\u975e\u540c\u5c0f\u9119\u3002\u6211\u6240 \u77e5\u8005  \u6709\u4e94\u4eba\uff0c\u5176\u4ed6\u88ab\u6536\u4e70\u7684\u5c1a\u4e0d\u77e5\u591a\u5c11\uff0c\u8fd9\u4e8b\u987b\u5373\u8bbe\u6cd5\u544a\u8bc9\u592a\u5b50\u3002\u4f46\u6211\u88ab\u5173\u7981\u5728\u6b64\uff0c\u65e0\u4eba \u76f8\u6551\uff0c\u5fc5\u987b\u9760\u672c\u8eab\u80fd\u8010\u8d8a\u72f1\uff0c\u6211\u8fd9\u4e00\u52a8\u6012\uff0c\u6c14\u8840\u66f4\u4e0d\u80fd\u7545\u884c\uff0c\u5982\u4f55\u80fd\u79fb\u89e3\u7a74\u3002\u60f3\u597d\u4e4b\u5f8c\uff0c\u6012 \u706b\u60ed\u5e73\uff0c\u7d22\u6027\u76d8\u819d\u9759\u5750\uff0c\u8fd0\u6c14\u51dd\u795e\u3002\u5353\u4e00\u822a\u5185\u529f\u672c\u6765\u751a\u6709\u6839\u57fa\uff0c\u5750\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u60ed\u89c9\u6c14\u900f\u91cd \u5173\uff0c\u5168\u8eab\u8212\u7545\uff0c\u7a74\u9053\u5df2\u89e3\uff0c\u6b63\u60f3\u9707\u65ad\u624b\u94d0\uff0c\u7834\u95e8\u800c\u51fa\uff0c\u5ffd\u542c\u5f97\u8fdc\u5904\u9690\u9690\u4f3c\u6709  \u6740\u4e4b\u58f0\u3002 \u3000\u3000\u5353\u4e00\u822a\u628a\u8033\u8d34\u5728\u5730\u4e0a\u9759\u542c\uff0c\u6740\u58f0\u8d8a\u6765\u8d8a\u8fd1\uff0c\u6b63\u81ea\u60ca\u5947\u3002\u76d1\u623f\u94c1\u95e8\u5ffd\u7136\u6253\u5f00\uff0c\u5353\u4e00\u822a\u7ad9\u4e86 \u8d77\u6765\uff0c\u53ea\u89c1\u4e91\u71d5\u5e73\u6ee1\u9762\u5978\u7b11\uff0c\u7f13\u7f13\u884c\u8fdb\uff0c\u5353\u4e00\u822a\u559d\u9053\uff1a\u201c\u4f60\u6765\u4f5c\u751a\uff1f\u201d\u4e91\u71d5\u5e73\u9053\uff1a\u201c\u4f60\u7684\u597d \u670b\u53cb\u6765\u4e86\uff0c\u6211\u5e26\u4f60\u53bb\u89c1\u5979\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u53ea\u542c\u5f97\u8f70\u7136\u5de8\u54cd\uff0c\u77e5\u5e9c\u7684\u8859\u95e8\u5df2\u7ed9\u4eba\u7528\u571f\u70ae\u8f70\u5f00\uff0c \u4e00\u65f6\u706b\u5149\u89e6\u5929\uff0c\u4e91\u71d5\u5e73\u9762\u4e0a\u53d8\u8272\uff0c\u624b\u638c\u4e00\u7ffb\uff0c\u75be\u7684\u5411\u5353\u4e00\u822a\u624b\u8155\u6293\u6765\u3002 \u3000\u3000\u201c\u59d4\u4e2d\u7a74\u201d\u88ab\u70b9\uff0c\u6700\u5c11\u8981\u8fc7\u516d\u4e2a\u65f6\u8fb0\uff0c\u624d\u80fd\u81ea\u89e3\u3002\u6240\u4ee5\u4e91\u71d5\u5e73\u6ee1\u5fc3\u4ee5\u4e3a\u662f\u624b\u5230\u64d2\u6765\uff0c\u81ea \u5df1\u6beb\u65e0\u9632\u5907\u3002\u4e0d\u6599\u5353\u4e00\u822a\u820c\u7efd\u6625\u96f7\uff0c\u4e00\u58f0\u864e\u543c\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u624b\u94d0\u98de\u8d77\uff0c\u53cc\u811a\u8fde\u73af\u75be\u8e22\uff0c\u4e91\u71d5 \u5e73\u731d\u4e0d\u53ca\u9632\uff0c\u819d\u76d6\u4e2d\u4e86\u4e00\u811a\uff0c\u8dcc\u5012\u5730\u4e0a\u3002\u4f46\u4ed6\u6b66\u529f\u975e\u540c\u5c0f\u9119\uff0c\u5728\u5730\u4e0a\u4e00\u6eda\uff0c\u907f\u5f00\u4e86\u5353\u4e00\u822a\u7684 \u653b\u51fb\uff0c\u7ad9\u8d77\u6765\u65f6\uff0c\u8170\u5e26\u5df2\u62ff\u5728\u624b\u4e2d\uff0c\u7528\u529b\u4e00\u6296\uff0c\u8170\u5e26\u7ed9\u4ed6\u4f7f\u5f97\u5982\u540c\u8f6f\u97ad\u4e00\u822c\uff0c\u547c\u7684\u5411\u5353\u4e00\u822a \u8170\u9645\u76f4\u5377\u8fc7\u6765\u3002\u5353\u4e00\u822a\u77e5\u9053\u5916\u6709\u6551\u5175\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u8eab\u5f62\u95ea\u5904\uff0c\u4e00\u8bb0\u201c\u624b\u6325\u7435\u7436\u201d\uff0c\u7ffb\u8eab\u62a2 \u8fdb\uff0c\u4e91\u71d5\u5e73\u8170\u5e26\u4e00\u6325\uff0c\u5f85\u5377\u654c\u4eba\u53cc\u81c2\uff0c\u5353\u4e00\u822a\u5ffd\u5730\u8170\u5411\u5f8c\u501a\uff0c\u4e00\u4e2a\u65cb\u8eab\uff0c\u6539\u638c\u4e3a\u62f3\uff0c\u62f3\u98ce\u98d5 \u98d5\uff0c\u4ecd\u662f\u62a2\u653b\u62db\u6570\uff0c\u4e91\u71d5\u5e73\u628a\u8170\u5e26\u4e00\u6536\uff0c\u9000\u4e86\u4e24\u6b65\uff0c\u5353\u4e00\u822a\u6325\u62f3\u731b\u6251\uff0c\u4ed6\u7a81\u4f38\u51fa\u5de6\u638c\u4e00\u683c\uff0c \u8170\u5e26\u5ffd\u5730\u4e58\u9699\u98de\u51fa\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u51fb\u5230\u4e86\u5353\u4e00\u822a\u80c1\u4e0b\uff0c\u5353\u4e00\u822a\u624b\u81c2\u4e00\u631f\uff0c\u5c06\u4ed6\u8170\u5e26\u631f\u7740\uff0c\u5750\u8eab \u5411\u5f8c\u4e00\u626f\uff0c\u7adf\u7136\u6ca1\u6709\u626f\u52a8\u3002\u4e91\u71d5\u5e73\u51b7\u7b11\u4e00\u58f0\uff0c\u5de6\u638c\u53c8\u547c\u7684\u4e00\u58f0\u5288\u6765\uff0c\u5353\u4e00\u822a\u4e0d\u80fd\u4e0d\u817e\u51fa\u624b\u638c \u5bf9\u654c\uff0c\u4e91\u71d5\u5e73\u7684\u8170\u5e26\uff0c\u6d3b\u4f3c\u7075\u86c7\uff0c\u7adf\u7136\u81ea\u4e0b\u800c\u4e0a\uff0c\u5c06\u4ed6\u81c2\u818a\u7f20\u4f4f\u3002 \u3000\u3000\u5353\u4e00\u822a\u53f3\u81c2\u88ab\u56f0\uff0c\u5de6\u638c\u7528\u529b\u76f8\u6297\uff0c\u4e91\u71d5\u5e73\u628a\u8170\u5e26\u4e00\u6536\uff0c\u5353\u4e00\u822a\u867d\u7528\u4e86\u201c\u529b\u5815\u5343\u65a4\u201d\u7684\u8eab \u6cd5\uff0c\u4ecd\u7136\u7ad9\u7acb\u4e0d\u7a33\uff0c\u9669\u88ab\u62c9\u5012\uff01\u6b63\u5728\u5371\u6025\uff0c\u5916\u9762\u7684\u811a\u6b65\u58f0\u5df2\u6e10\u6e10\u6765\u8fd1\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a \u201c\u4e91\u5927\u54e5\uff0c\u98ce\u7d27\uff0c\u626f\u547c\uff01\u201d\u4e91\u71d5\u5e73\u9762\u8272\u5927\u53d8\uff0c\u4f46\u8155\u5e95\u4ecd\u5728\u4f7f\u52b2\uff0c\u60f3\u628a\u5353\u4e00\u822a\u64d2\u8fc7\u6765\u4f5c\u4e3a\u4eba \u8d28\u3002\u5c31\u5728\u6b64\u9645\uff0c\u53ea\u542c\u5f97\u4e00\u4e32\u94f6\u94c3\u4f3c\u7684\u7b11\u58f0\u5df2\u98d8\u4e86\u8fdb\u6765\uff0c\u5353\u4e00\u822a\u53c8\u60ca\u53c8\u559c\uff0c\u53eb\u9053\uff1a\u201c\u7389\u7f57 \u5239\uff01\u201d\u4e91\u71d5\u5e73\u6025\u5fd9\u677e\u52b2\uff0c\u5c06\u8170\u5e26\u6536\u56de\uff0c\u7ffb\u8eab\u62a2\u51fa\u76d1\u623f\u3002 \u3000\u3000\u5353\u4e00\u822a\u6599\u5f97\u4e0d\u9519\uff0c\u5e26\u5175\u653b\u57ce\u7684\u679c\u7136\u662f\u7389\u7f57\u5239\u3002\u5979\u4e0e\u738b\u7167\u5e0c\u7684\u7236\u4eb2\u738b\u5609\u80e4\u8ba2\u76df\u4e4b\u5f8c\uff0c\u672c\u6765 \u65e9\u5c31\u60f3\u5230\u9655\u5317\u76f8\u4f1a\uff0c  \u56e0\u4e0e\u5e94\u4fee\u9633\u6709\u534e\u5c71\u4e4b\u7ea6\uff0c\u6240\u4ee5\u624d\u803d\u6401\u4e86\u5927\u534a\u5e74\u3002\u8fd9\u6b21\u5979\u5e26\u4e86\u51e0\u5341\u5973 \u5175\uff0c\u672c\u6765\u662f\u8981\u5230\u74e6\u7a91\u5821\u548c\u738b\u5609\u80e4\u76f8\u4f1a\u7684\uff0c\u4f46\u5728\u9014\u4e2d\u6551\u4e86\u767d\u654f\u4e4b\u5f8c\uff0c\u6108\u60f3\u6108\u7591\uff0c\u731b\u7684\u60f3\u8d77\u4e86\u5353 \u4e00\u822a\uff0c\u9063\u4eba\u5165\u57ce\u6697\u63a2\uff0c\u77e5\u9053\u4e86\u5353\u4e00\u822a\u88ab\u6349\u7684\u6d88\u606f\uff0c\u8fd9\u65f6\u738b\u7167\u5e0c\u4e5f\u5df2\u5f97\u5230\u4e86\u6d88\u606f\uff0c\u5e26\u5175\u8d76\u6765\uff0c \u7edf\u7531\u7389\u7f57\u5239\u6307\u6325\uff0c\u6df1\u591c\u653b\u57ce\uff0c\u4e0d\u6d88\u4e00\u4e2a\u66f4\u6b21\uff0c\u5c31\u628a\u57ce\u95e8\u653b\u7834\uff0c\u6740\u5165\u5e9c\u8859\u3002 \u3000\u3000\u518d\u8bf4\u4e91\u71d5\u5e73\u62a2\u51fa\u76d1\u623f\uff0c\u53ea\u89c1\u91d1\u5343  \u6b63\u5728\u524d\u9762\u53c1\u4e08\u4e4b\u5730\uff0c\u4e0e\u4e00\u4e2a\u5c11\u5973\u6fc0\u6597\u3002\u91d1\u5343  \u5df2\u88ab\u7b3c \u7f69\u5728\u5251\u5149\u4e4b\u4e0b\uff0c\u5341\u5206\u5371\u9669\u3002 \u3000\u3000\u4e91\u71d5\u5e73\u6025\u5fd9\u5c06\u8170\u5e26\u4e00\u6325\uff0c\u4e00\u4e2a\u201c\u91d1\u86df\u9501\u67f1\u201d\uff0c\u5411\u7740\u7389\u7f57\u5239\u7684\u5251\u8eab\u4fbf\u7f20\uff0c\u8981\u65bd\u5c55\u4ee5\u67d4\u514b\u521a \u7684\u529f\u592b\uff0c\u5377\u62ff\u7389\u7f57\u5239\u7684\u5b9d\u5251\u3002\u7389\u7f57\u5239\u76c8\u76c8\u4e00\u7b11\uff0c\u5251\u950b\u5f80\u5916\u4e00\u5c55\uff0c\u4e91\u71d5\u5e73\u864e\u53e3\u4e00\u75db\uff0c\u6025\u677e\u624b \u65f6\uff0c\u8170\u5e26\u5df2\u88ab\u7389\u7f57\u5239\u5272\u4e3a\u4e24\u6bb5\u3002\u8981\u77e5\u4ee5\u67d4\u514b\u521a\u7684\u529f\u592b\uff0c\u5168\u51ed\u5185\u529f\u52b2\u529b\uff0c\u4e91\u71d5\u5e73\u7684\u529f\u529b\u867d\u5728\u5353 \u4e00\u822a\u4e4b\u4e0a\uff0c\u4f46\u5374\u5728\u7389\u7f57\u5239\u4e4b\u4e0b\uff0c\u4ee5\u8fd9\u624b\u201c\u67d4\u529f\u201d\u5bf9\u4ed8\u5353\u4e00\u822a\u72b9\u53ef\uff0c\u5bf9\u4ed8\u7389\u7f57\u5239\u5374\u662f\u4e0d\u884c\u3002 \u3000\u3000\u91d1\u5343  \u8d81\u7389\u7f57\u5239\u5206\u5fc3\u4e4b\u9645\uff0c\u53cc\u638c\u4e00\u5206\uff0c\u53cd\u51fb\u7389\u7f57\u5239\u4e24\u80c1\uff0c\u7389\u7f57\u5239\u5251\u62db\u5947\u5feb.\u4e00\u5251\u524a\u65ad\u4e91 \u71d5\u5e73\u8170\u5e26\uff0c\u811a\u8ddf\u4e00\u65cb\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u5251\u6c14\u68ee\u68ee\uff0c\u5251\u950b\u53c8\u6307\u5230\u91d1\u5343  \u5589\u5499\u3002\u91d1\u5343  \u5413\u5f97\u4ea1\u9b42\u76f4 \u5192\uff0c\u6025\u5fd9\u6492\u62db\u9632\u5fa1\u3002\u91d1\u5343  \u7684\u638c\u6cd5\u867d\u7136\u9634\u6bd2\uff0c\u4f46\u7389\u7f57\u5239\u5251\u6cd5\u8f9b\u8fa3\uff0c\u91d1\u5343  \u6839\u672c\u8fd1\u4e0d\u4e86\u8eab\u3002\u82e5 \u975e\u7389\u7f57\u5239\u4e5f\u7a0d\u5b58\u987e\u5fcc\uff0c\u4ed6\u65e9\u5df2\u4e27\u751f\u3002\u4e91\u71d5\u5e73\u5012\u5438\u4e00\u53e3\u51b7\u6c14\uff0c\u4e8b\u5230\u5176\u95f4\uff0c\u4e0d\u80fd\u4e0d\u62da\uff0c\u53ea\u597d\u4ece\u504f \u950b\u62a2\u4e0a\uff0c\u4ee5\u64d2\u62ff\u5341\u516b\u638c\u7684\u62db\u6570\uff0c\u6270\u654c\u6551\u53cb\u3002\u5408\u4e24\u4eba\u4e4b\u529b\uff0c\u62da\u6b7b\u529b\u6597\uff0c\u72b9\u81ea\u5904\u5728\u4e0b\u98ce\u3002 \u3000\u3000\u518d\u8bf4\u5353\u4e00\u822a\u8d70\u4e86\u51fa\u6765\uff0c\u89c1\u7389\u7f57\u5239\u4e0e\u4e24\u540d\u9ad8\u624b\u62da\u6597\uff0c\u6b63\u60f3\u6325\u62f3\u76f8\u52a9\uff0c\u7389\u7f57\u5239\u53eb\u9053\uff1a\u201c\u4f60\u5230 \u5f8c\u9762\u53bb\u5e2e\u738b\u7167\u5e0c\u5427\uff0c\u8fd9\u4e24\u4e2a\u5154\u5d3d\u5b50\u4e0d\u662f\u6211\u7684\u5bf9\u624b\u3002\u201d\u5353\u4e00\u822a\u81ea\u662f\u884c\u5bb6\uff0c\u53ea\u77a7\u4e86\u4e00\u773c\uff0c\u4fbf\u77e5\u7389 \u7f57\u5239\u6240\u8a00\u975e\u5047\uff0c\u8df3\u8fc7\u8d70\u5eca\uff0c\u679c\u7136\u542c\u5f97\u6740\u58f0\u9707\u5929\uff0c\u6709\u4e00\u5bf9\u6c49\u5b50\uff0c\u5728\u8d70\u5eca\u8fb9\u6253\u8fb9\u8d70\uff0c\u524d\u9762\u7684\u90a3\u4eba \u6b63\u662f\u738b\u7167\u5e0c\u3002\u4ed6\u8fd0\u5251\u5982\u98ce\uff0c\u4f46\u654c\u4eba\u5374\u4e5f\u4e0d\u5f31\uff01\u4e00\u67c4\u5251\u5de6\u906e\u53f3\u6321\uff0c\u5e26\u5b88\u5e26\u653b\uff0c\uff0e\u7adf\u662f\u6253\u5f97\u96be\u5206 \u96be\u89e3\u3002 \u3000\u3000\u548c\u738b\u7167\u5e0c\u6597\u5251\u8fd9\u4eba\uff0c\u6b63\u662f\u90a3\u65e5\u540c\u738b\u5175\u5907\u4e00\u8d77\u6765\u6349\u62ff\u5353\u4e00\u822a\u7684\u519b\u5b98\u3002\u5353\u4e00\u822a\u4e00\u89c1\uff0c\u5fc3\u5934\u706b \u8d77\uff0c\u970d\u5730\u8df3\u5c06\u4e0a\u53bb\uff0c\u62f3\u80cc\u5411\u5916\uff0c\u5de6\u53f3\u4e00\u5206\uff0c\u4e00\u8bb0\u201c\u5206\u91d1\u624b\u53cc\u6302\u62f3\u201d\u7167\u51c6\u654c\u4eba\u4e24\u8fb9\u592a\u9633\u7a74\u6253 \u53bb\uff0c\u90a3\u540d\u519b\u5b98\u672c\u662f\u9655\u7518\u603b\u7763\u5e10\u4e0b\u7b2c\u4e00\u540d\u6b66\u5c06\uff0c\u529f\u529b\u867d\u7136\u4e0d\u5f31\uff0c\u53ef\u662f\u90a3\u80fd\u8fde\u654c\u4e24\u540d\u9ad8\u624b\uff0c\u4ed6\u8eb2 \u5f97\u5f00\u5353\u4e00\u822a\u7684\u62f3\uff0c\u5374\u8eb2\u4e0d\u5f00\u738b\u7167\u5e0c\u7684\u5251\uff0c\u53cc\u80a9\u6643\u5904\uff0c\u672a\u8f6c\u8eab\u5f62\uff0c\u80a9\u80db\u9aa8\u7684\u5929\u67f1\u7a74\u5df2\u7ed9\u738b\u7167\u5e0c \u4e00\u5251\u7a7f\u5165\uff0c\u5f53\u573a\u4e27\u547d\u3002 \u3000\u3000\u738b\u7167\u5e0c\u9053\uff1a\u201c\u5353\u5144\uff0c\u5c0f\u5f1f\u6765\u8fdf\uff0c\u7d2f\u6211\u5144\u53d7\u82e6\u4e86\uff01\u201d\u5353\u4e00\u822a\u70b9\u4e86\u70b9\u5934\uff0c\u6728\u7136\u4e0d\u8bed\u3002\u4ed6\u89c1\u6b64 \u60c5\u5f62\uff0c\u59cb\u77e5\u738b\u7167\u5e0c\u771f\u662f  \u5317\u7684\u5de8\u76d7\u3002\u738b\u7167\u5e0c\u53c8\u9053\uff1a\u201c\u54b1\u4eec\u770b\u7ec3\u5973\u4fa0\u53bb\uff0c\u770b\u5979\u5982\u4f55\u6536\u62fe\u90a3\u4e24\u540d \u5978\u8d3c\u3002\u201d\u5353\u4e00\u822a\u6069\u6028\u5206\u660e\uff0c\u867d\u7136\u4e0d\u613f\u4e0e\u5f3a\u76d7\u7ed3\u4ea4\uff0c\u4f46\u522b\u4eba\u820d\u8eab\u6765\u6551\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u4e0d\u80fd\u62c2\u8896 \u800c\u8d70\u3002\u53ea\u597d\u968f\u7740\u738b\u7167\u5e0c\u7a7f\u8fc7\u8d70\u5eca\u3002\u8fd9\u65f6\u7389\u7f57\u5239\u5728\u8d70\u5eca\u90a3\u8fb9\u5927\u5c55\u795e\u5a01\uff0c\u5251\u5149\u95ea\u70c1\uff0c\u8fdc\u671b\u8fc7\u53bb\uff0c \u51e0\u4e4e\u5206\u4e0d\u6e05\u4eba\u5f71\u3002\u738b\u7167\u5e0c\u8d5e\u9053\uff1a\u201c\u7389\u7f57\u5239\u771f\u884c\uff0c\u6211\u770b\u90a3\u4e24\u540d\u5978\u8d3c\u8981\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u3002\u201d\u8bdd\u521a\u8bf4 \u5b8c\uff0c\u5ffd\u542c\u5f97\u6709\u4e00\u4e2a\u6e05\u8106\u7684\u58f0\u97f3\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4e0d\u89c1\u5f97\uff01\u201d\u738b\u7167\u5e0c\u9762\u8272\u500f\u53d8\uff0c\u8d70\u5eca\u6a90\u4e0a\u7a81\u7136\u8dc3\u4e0b \u4e00\u4eba\uff0c\u5374\u662f\u4e00\u4e2a\u8499\u9762\u5c11\u5973\uff0c\u542c\u58f0\u97f3\uff0c\u770b\u4f53\u6001\uff0c\u4f3c\u4e4e\u6bd4\u7389\u7f57\u5239\u8fd8\u8981\u5e74\u8f7b\u3002 \u3000\u3000\u738b\u7167\u5e0c\u53eb\u9053\uff1a\u201c\u4f60\u6765\u505a\u4ec0\u9ebd\uff1f\u201d\u8499\u9762\u5c11\u5973\u9053\uff1a\u201c\u4f60\u6765\u5f97\u96be\u9053\u6211\u6765\u4e0d\u5f97\uff1f\u5582\uff0c\u6709\u4eba\u7b49\u7740\u4f60 \u5462\uff01\u5f85\u6211\u4f1a\u8fc7\u4e86\u7389\u7f57\u5239\u518d\u548c\u4f60\u8bf4\u3002\u201d\u5353\u4e00\u822a\u95ee\u9053\uff1a\u201c\u8fd9\u4eba\u662f\u8c01\uff1f\u662f\u738b\u5144\u76f8\u8bc6\u7684\u9ebd\uff1f\u201d\u738b\u7167\u5e0c \u9762\u8272\u5c34\u5c2c\uff0c\u9053\uff1a\u201c\u4e5f\u8bf4\u5f97\u4e0a\u662f\u76f8\u8bc6\u3002\u201d\u62d4\u6b65\u4fbf\u8ffd\u3002 \u3000\u3000\u518d\u8bf4\u7389\u7f57\u5239\u4e0e\u4e91\u71d5\u5e73\uff0c\u91d1\u5343  \u4e8c\u4eba\u6076\u6597\uff0c\u5251\u52bf\u5982\u8679\uff0c\u5947\u5e7b\u65e0\u6bd4\uff0c\u91d1\u5343  \u7a7a\u6709\u9634\u98ce\u6bd2\u7802\u638c \u7684\u529f\u592b\uff0c\u5374\u8fde\u5979\u8863\u88f3\u90fd\u6cbe\u4e0d\u7740\uff0c\u53ea\u597d\u7f29\u5c0f\u5708\u5b50\uff0c\u529b\u56fe\u81ea\u4fdd\uff0c\u7389\u7f57\u5239\u5251\u62db\u50ac\u7d27\uff0c\u500f\u5982\u5de8\u6d6a\u60ca \u6d9b\uff0c\u518d\u6597\u7247\u523b\uff0c\u4e24\u4eba\u8fde\u81ea\u4fdd\u4e5f\u96be\uff0c\u7389\u7f57\u5239\u6b63\u60f3\u75db\u4e0b\u6740\u624b\uff0c\u5ffd\u89c9\u80cc\u5f8c\u6709\u91d1\u5203\u631f\u98ce\u4e4b\u58f0\uff0c\u53cd\u624b\u4e00 \u5251\uff0c\u53ee\u5f53\u4e00\u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u90a3\u4eba\u7684\u5251\u7adf\u672a\u51fa\u624b\u3002\u7389\u7f57\u5239\u5fae\u5fae\u5403\u60ca\uff0c\u8f6c\u8eab\u4e00\u671b\uff0c\u5374\u539f\u6765\u662f\u4e2a\u8499 \u9762\u5c11\u5973\u3002\u7389\u7f57\u5239\u559d\u9053\uff1a\u201c\u4f60\u627e\u6b7b\u9ebd\uff1f\u201d\u5c11\u5973\u9053\uff1a\u201c\u4eba\u4eba\u90fd\u5938\u8d5e\u4f60\u7684\u5251\u6cd5\uff0c\u6211\u60f3\u89c1\u8bc6\u3002\u201d\u7389\u7f57 \u5239\u9053\uff1a\u201c\u597d\uff0c\u4f60\u89c1\u8bc6\u5427\uff01\u201d\u5251\u67c4\u4e00\u65cb\uff0c\u8f6c\u4e86\u534a\u4e2a\u5f27\u5f62\uff0c\u5237\u7684\u5206\u5fc3\u523a\u5230\uff0c\u90a3\u5c11\u5973\u6a2a\u5251\u4e00\u5c01\uff0c\u594b \u529b\u4e00\u51b2\uff0c\u5c45\u7136\u628a\u7389\u7f57\u5239\u7684\u5251\u62db\u62c6\u5f00\u3002 \u3000\u3000\u4e91\u71d5\u5e73\u548c\u91d1\u5343  \u5401\u4e86\u53e3\u6c14\uff0c\u98de\u8eab\u4e0a\u5c4b\uff0c\u7389\u7f57\u5239\u53eb\u9053\uff1a\u201c\u738b\u7167\u5e0c\u622a\u7740\u4ed6\uff0c\u6211\u7247\u523b\u4fbf\u6765\uff01\u201d \u738b\u7167\u5e0c\u811a\u5c16\u4e00\u70b9\uff0c\u4e0a\u5c4b\u8ffd\u654c\uff0c\u53e3\u4e2d\u53eb\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\u4f60\u624b\u4e0b\u7559\u60c5\u3002\u201d\u5353\u4e00\u822a\u77e5\u9053\u4e91\u3001\u91d1\u4e8c\u4eba\u7684 \u529f\u592b\u90fd\u5728\u738b\u7167\u5e0c\u4e4b\u4e0a\uff0c\u773c\u73e0\u4e00\u8f6c\uff0c\u7a0d\u4e00\u8fdf\u7591\uff0c\u4e5f\u8ddf\u7740\u8ffd\u4e0a\u53bb\u3002 \u3000\u3000\u7389\u7f57\u5239\u672c\u4ee5\u4e3a\u4e0d\u8fc7\u53c1\u62db\uff0c\u5c31\u53ef\u5c06\u90a3\u8499\u9762\u5c11\u5973\u523a\u4f24\uff0c\u4e0d\u6599\u53c1\u62db\u90fd\u7ed9\u5c11\u5973\u89e3\u5f00\uff0c\u542c\u90a3\u5c4b\u9876\u4e0a \u6740\u4e4b\u58f0\uff0c\u5df2\u60ed\u60ed\u53bb\u8fdc\uff0c\u4e0d\u7981\u5927\u6012\u3002 \u3000\u3000\u90a3\u8499\u9762\u5c11\u5973\u51fa\u5c3d\u5403\u4e73\u4e4b\u529b\uff0c\u624d\u89e3\u5f97\u5f00\u7389\u7f57\u5239\u7684\u53c1\u8bb0\u8fa3\u62db\uff0c\u77e5\u9053\u7389\u7f57\u5239\u5251\u6cd5\u8fdc\u5728\u5df1\u4e0a\uff0c\u4f6f \u653b\u4e00\u5251\uff0c\u62bd\u8eab\u4fbf\u9003\uff0c\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5973\u5a03\u513f\u8fd8\u6562\u8fd8\u624b\uff01\u201d\u8138\u4e0a\u5806\u7740\u7b11\u5bb9\uff0c\u5fc3\u4e2d\u5374\u662f\u6124 \u6068\uff0c\u5237\u5237\u51e0\u5251\uff0c\u628a\u90a3\u5c11\u5973\u8feb\u5f97\u56e2\u56e2\u4e71\u8f6c\uff0c\u5374\u9003\u4e0d\u5f00\uff0c\u90a3\u5c11\u5973\u9053\uff1a\u201c\u6253\u4e0d\u8fc7\u4f60\uff0c\u6211\u8ba4\u8f93\u4fbf\u4e86\uff0c \u4f60\u8feb\u5f97\u8fd9\u6837\u7d27\u505a\u4ec0\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8ba4\u8f93\u4e5f\u4e0d\u884c\uff01\u201d\u8499\u9762\u5c11\u5973\u9053\uff1a\u201c\u6709\u672c\u4e8b\u7684\u4f60\u548c\u6211\u53bb\u89c1 \u7239\u7239\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u5148\u89c1\u4f60\u3002\u201d\u5251\u950b\u4e00\u5212\uff0c\u8499\u9762\u5c11\u5973\u5ffd\u89c9\u5f97\u51b7\u6c14\u68ee\u68ee\uff0c\u7389\u7f57\u5239\u7684\u5b9d\u5251\u5c31 \u4f3c\u5728\u9762\u524d\u5212\u6765\u5212\u53bb\uff0c\u60ca\u53eb\u4e00\u58f0\uff0c\u9762\u7eb1\u5df2\u7ed9\u6311\u5f00\u3002\u7389\u7f57\u5239\u4e00\u89c1\u662f\u4e2a\u7f8e\u8c8c\u5c11\u5973\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e0d \u6740\u4f60\uff0c\u7ed9\u4f60\u7559\u4e2a\u8bb0\u53f7\u3002\u201d\u5251\u5c16\u4e00\u70b9\uff0c\u8981\u5728\u5979\u9762\u4e0a\u7559\u4e2a\u75a4\u75d5\u3002 \u3000\u3000\u8499\u9762\u5c11\u5973\u5413\u5f97\u6025\u4e86\uff0c\u9752\u94a2\u5251\u4e00\u6296\uff0c\u5251\u950b\u53cd\u5f39\u800c\u4e0a\uff0c\u548c\u7389\u7f57\u5239\u7684\u5251\u4e00\u4ea4\uff0c\u5ffd\u7136\u5251\u950b\u4e00\u6ed1\uff0c \u5206\u660e\u5411\u5de6\uff0c\u5230\u4e86\u4e2d\u9014\uff0c\u5374\u500f\u5730\u5411\u53f3\uff0c\u53cd\u523a\u7389\u7f57\u5239\u5de6\u4e73\u4e0a\u7684\u201c\u5c06\u53f0\u7a74\u201d\uff0c\u7389\u7f57\u5239\u5446\u4e86\u4e00\u5446\uff0c\u90a3 \u5c11\u5973\u98de\u8eab\u4e0a\u5c4b\u3002\u7389\u7f57\u5239\u5927\u53eb\u9053\uff1a\u201c\u4f60\u90a3\u91cc\u5b66\u6765\u7684\u5251\u6cd5\uff1f\u201d\u63d0\u5251\u8ffd\u53bb\u3002 \u3000\u3000\u518d\u8bf4\u738b\u7167\u5e0c\u548c\u5353\u4e00\u822a\u4e8c\u4eba\uff0c\u542c\u7389\u7f57\u5239\u4e4b\u4ee4\uff0c\u8ffd\u622a\u5978\u8d3c\u3002\u91d1\u5343  \u548c\u4e91\u71d5\u5e73\u4e8c\u4eba\u6b66\u529f\u5728 \u738b\uff0e\u5353\u4e4b\u4e0a\uff0c\u7389\u7f57\u5239\u53c8\u8fdf\u8fdf\u4e0d\u51fa\uff0c\u56db\u4eba\u4ea4\u624b\uff0c\u6597\u4e86\u5341\u6765\u62db\uff0c\u738b\u7167\u5e0c\u4e0e\u5353\u4e00\u822a\u5df2\u88ab\u8feb\u91c7\u53d6\u5b88 \u52bf\u3002\u91d1\u5343  \u548c\u4e91\u71d5\u5e73\u5fd7\u5728\u9003\u547d\uff0c\u65e0\u5fc3\u604b\u6218\uff0c\u62a2\u4e86\u653b\u52bf\uff0c\u865a\u6643\u4e00\u62db\uff0c\u8f6c\u8eab\u4fbf\u9003\u3002\u738b\u7167\u5e0c\u9053\uff1a \u201c\u8ffd\u4e0d\u8ffd\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u8ffd\uff01\u8fd9\u4e24\u4eba\u662f\u79c1\u901a\u6ee1\u6d32\u7684\u5978\u8d3c\u3002\u201d\u8fd9\u65f6\u5e9c\u8859\u88ab\u738b\u7167\u5e0c\u7684\u624b\u4e0b\u653e\u706b \u711a\u70e7\uff0c\u70c8\u7130\u51b2\u5929\uff0c\u70df\u96fe\u8ff7\u6f2b\uff0c\u738b\u7167\u5e0c\u4e0e\u5353\u4e00\u822a\u8ffd\u51fa\u5e9c\u8859\uff0c\u5df2\u4e0d\u89c1\u90a3\u4e24\u4eba\u80cc\u5f71\u3002\u5353\u4e00\u822a\u63d0\u5251\u56db \u987e\uff0c\u5ffd\u89c1\u4e00\u56e2\u767d\u5f71\uff0c\u547c\u7684\u4e00\u58f0\u4ece\u8eab\u65c1\u63a0\u8fc7\uff0c\u539f\u6765\u5c31\u662f\u9002\u624d\u90a3\u4e2a\u8499\u9762\u5c11\u5973\uff0c\u8fd9\u65f6\u9762\u7eb1\u5df2\u8131\uff0c\u5728 \u70df\u96fe\u4e2d\u76f4\u7a9c\u51fa\u53bb\u3002\u63a5\u7740\u53c8\u662f\u547c\u7684\u4e00\u58f0\uff0c\u53c8\u662f\u4e00\u56e2\u767d\u5f71\uff0c\u5728\u70df\u96fe\u4e2d\u98de\u4e86\u51fa\u6765\u3002\u738b\u7167\u5e0c\u53eb\u9053\uff1a \u201c\u90a3\u4e24\u540d\u5978\u8d3c\u8dd1\u4e86\u3002\u7ec3\u5973\u4fa0\uff0c\u54b1\u4eec\u53c1\u4eba\u5206\u4e24\u8def\u641c\u5427\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8ffd\u90a3\u4e2a\u5973\u5a03\u513f\u8981\u7d27\uff01\u201d \u5353\u4e00\u822a\u9053\uff1a\u201c\u90a3\u4e24\u4eba\u79c1\u901a\u6ee1\u6d32\uff0c\u8fd8\u662f\u8ffd\u90a3\u4e24\u4eba\u8981\u7d27\u3002\u201d\u7389\u7f57\u5239\u75be\u63a0\u98de\u524d\uff0c\u51b3\u7136\u8bf4\u9053\uff1a\u201c\u6211\u8bf4 \u8ffd\u90a3\u4e2a\u5973\u5a03\u513f\u8981\u7d27\uff01\u201d\u738b\u7167\u5e0c\u65e0\u5948\uff0c  \u597d\u548c\u5353\u4e00\u822a\u8ddf\u5728\u5f8c\u9762\u3002\u5353\u4e00\u822a\u5927\u60d1\u4e0d\u89e3\uff0c\u9887\u4e3a\u53cd\u611f\uff0c \u5fc3\u60f3\u4f55\u4ee5\u7389\u7f57\u5239\u8f7b\u91cd\u5012\u7f6e\uff0c\u653e\u4e86\u5927\u5978\u8d3c\uff0c\u5374\u53bb\u8ffd\u4e00\u4e2a\u5c0f\u59d1\u5a18\u3002 \u3000\u3000\u4f60\u9053\u7389\u7f57\u5239\u4f55\u4ee5\u5982\u6b64\uff0c\u539f\u6765\u8499\u9762\u5c11\u5973\u6700\u5f8c\u90a3\u62db\uff0c\u6b63\u662f\u7389\u7f57\u5239\u5e08\u7236\u6240\u4f20\u7684\u72ec\u95e8\u5251\u6cd5\uff0c\u7389\u7f57 \u5239\u81ea\u5c0f\u4e0e\u5e08\u7236\u5728\u53e4\u6d1e\u6f5c\u4fee\uff0c\u76f8\u4f9d\u4e3a\u547d\uff0c\u6df1\u77e5\u5e08\u7236\u522b\u65e0\u5f92\u5f1f\u3002\u89c1\u8499\u9762\u5c11\u5973\u4f7f\u51fa\u8fd9\u62db\uff0c\u60ca\u7591\u4e0d \u5b9a\u3002\u5fc3\u60f3\u96be\u9053\u662f\u5cb3\u545c\u73c2\u548c\u5353\u4e00\u822a\u53d6\u4e86\u5251\u8c31\u4e4b\u5f8c\uff0c\u79c1\u81ea\u4f20\u7ed9\u5916\u4eba\u3002\u7389\u7f57\u5239\u5f53\u65e5\u4e0e\u5cb3\u545c\u73c2\u6597\u5251\uff0c \u6253\u6210\u5e73\u624b\uff0c\u8d1f\u6c14\u8d70\u5f00\uff0c\u8fc7\u5f8c\u601d\u91cf\uff0c\u6df1\u4e3a\u5f8c\u6094\uff0c\u518d\u56de\u6d1e\u4e2d\uff0c\u975e\u552f\u5251\u8c31\u4e0d\u89c1\uff0c\u8fde\u58c1\u4e0a\u6240\u523b\u7684\u5251\u5f0f \u4e5f\u88ab\u524a\u5e73\u4e86\u3002\u7389\u7f57\u5239\u7acb\u4e0b\u5fc3\u613f\uff0c\u4e00\u5b9a\u8981\u5c06\u5251\u8c31\u53d6\u56de\uff0c\u5982\u4eca\u8fd9\u8499\u9762\u5c11\u5973\u5c45\u7136\u4f7f\u51fa\u81ea\u5df2\u72ec\u95e8\u5251 \u62db\uff0c\u90a3\u80fd\u4e0d\u53d1\u6025\u8ffd\u8d76\uff01 \u3000\u3000\u90a3\u5c11\u5973\u8dd1\u5728\u524d\u5934\uff0c\u7389\u7f57\u5239\u548c\u5353\uff0e\u738b\u4e8c\u4eba\u8854\u5c3e\u75be\u8ffd\uff0c\u9010\u7535\u8ffd\u98ce\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u7389\u7f57\u5239\u5df2\u8ffd\u5230 \u5c11\u5973\u8eab\u5f8c\uff0c\u738b\u7167\u5e0c\u4e0e\u5353\u4e00\u822a\u5374\u88ab\u629b\u5728\u5f8c\u9762\u3002\u90a3\u5c11\u5973\u60f3\u662f\u88ab\u8ffd\u5f97\u6025\u4e86\uff0c\u9ad8\u58f0\u558a\u53eb\u201c\u7239\u7239\uff01\u201d\u7389 \u7f57\u5239\u653e\u7f13\u811a\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u7b49\u4f60\u7239\u7239\u51fa\u9762\u518d\u6765\u95ee\u4f60\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5df2\u8ffd\u81f3\u57ce\u5916\u7684\u6e05\u98ce\u5c71\u811a\uff0c\u90a3\u5c11\u5973\u8fb9\u53eb\u8fb9\u8dd1\u4e0a\u5c71\uff0c\u7389\u7f57\u5239\u5982\u5f71\u968f\u5f62\uff0c\u7d27\u8e51\u5c11\u5973\u8eab\u5f8c\uff0c \u957f\u5251\u6643\u52a8\uff0c\u5251\u5c16\u65f6\u4e0d\u65f6\u70b9\u7740\u5c11\u5973\u5f8c\u5fc3\uff0c\u770b\u90a3\u5c11\u5973\u60ca\u60f6\u4e07\u72b6\uff0c\u5de6\u7eb5\u53f3\u8dc3\uff0c\u603b\u6446\u8131\u4e0d\u4e86\u3002\u7389\u7f57\u5239 \u6709\u5982\u7075\u732b\u620f\u9f20\uff0c\u201c\u73a9\u201d\u5f97\u5341\u5206\u9ad8\u5174\u3002\u683c\u683c\u7684\u7b11\u4e2a\u4e0d\u4f11\u3002\u90a3\u5c11\u5973\u5413\u5f97\u9510\u58f0\u5c16\u53eb\u3002\u7b11\u58f0\u53eb\u58f0\u6742\u6210 \u4e00\u7247\uff0c\u84e6\u7136\u95f4\uff0c\u5c11\u5973\u8eab\u5b50\u5411\u524d\u4e00\u4ec6\uff0c\u9ad8\u53eb\u201c\u7239\u7239\u201d-\uff0c\u5c71\u8170\u5904\u4f20\u51fa\u4e00\u58f0\u602a\u5578\uff0c\u7389\u7f57\u5239\u6536\u5251\u770b \u65f6\uff0c\u53ea\u89c1\u4e00\u56e2\u7070\u5f71\uff0c\u4f3c\u6d41\u661f\u6b92\u77f3\u822c\u76f4\u51b2\u4e0b\u6765\uff0c\u771f\u7684\u662f\u58f0\u5230\u4eba\u5230\uff0c\u7389\u7f57\u5239\u6a2a\u8dc3\u4e24\u6b65\uff0c\u53ea\u89c1\u4e00\u4e2a \u9ad8\u5927\u8001\u4eba\uff0c\u9e70\u9f3b\u72ee\u53e3\uff0c\u6ee1\u5634\u7edc\u816e\u77ed\u987b\uff0c\u76f8\u8c8c\u4e11\u964b\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u8c01\u6562\u6b3a\u4fae\u6211\u513f\uff1f\u201d\u90a3\u5c11\u5973\u6ee1 \u9762\u6cea\u75d5\uff0c\u8eb2\u5728\u8001\u4eba\u8eab\u5f8c\u3002\u6492\u5a07\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u66ff\u6211\u628a\u8fd9\u8d3c\u5a46\u5a18\u7684\u773c\u73e0\u6316\u4e86\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u4e00\u58f0\u51b7\u7b11\uff0c\u957f\u5251\u4e00\u6307\uff0c\u559d\u9053\uff1a\u201c\u8001\u8d3c\uff0c\u5feb\u628a\u6211\u7684\u5251\u8c31\u8fd8\u6765\uff01\u201d\u8001\u4eba\u4e00\u6014\uff0c\u6c89\u58f0\u559d \u9053\uff1a\u201c\u4ec0\u9ebd\u5251\u8c31\uff01\u201d\u90a3\u5c11\u5973\u54ed\u9053\uff1a\u201c\u7239\u7239\uff0c\u8fd9\u8d3c\u5a46\u5a18\u8bec\u8d56\u5973\u513f\u4f5c\u8d3c\uff0c\u5973\u513f\u4f55\u66fe\u89c1\u8fc7\u5979\u4ec0\u9ebd\u5251 \u8c31\uff1f\u5979\u628a\u5251\u8d34\u7740\u5973\u513f\u80cc\u5fc3\uff0c\u5c3d\u60c5\u620f\u4fae\uff1f\u7239\u7239\uff0c\u4f60\u4e00\u5b9a\u5f97\u66ff\u6211\u628a\u5979\u7684\u773c\u73e0\u6316\u51fa\u6765\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u7ed9\u5979\u4e00\u8fde\u51e0\u53e5\u201c\u8d3c\u5a46\u5a18\u201d\u9a82\u5f97\u5fc3\u5934\u706b\u8d77\uff0c\u8138\u4e0a\u7b11\u5bb9\u672a\u6536\uff0c\u624b\u4e2d\u5251\u5df3\u523a\u51fa\u3002\u90a3\u8001\u4eba \u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u5012\u9000\u53c1\u6b65\u3002\u624b\u638c\u4e00\u63a8\u5c11\u5973\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7ad9\u5230\u90a3\u5757\u5ca9\u77f3\u4e0a\u53bb\uff0c\u4e0d\u51c6\u5e2e\u624b\u3002\u521a\u624d \u7684\u4e8b\uff0c\u6211\u5168\u90fd\u770b\u5230\u4e86\u3002\u201d\u7389\u7f57\u5239\u4e00\u5251\u4e0d\u4e2d\uff0c\u7b2c\u4e8c\u5251\u7b2c\u53c1\u5251\u8fde\u73af\u523a\u6765\uff0c\u8001\u4eba\u84e6\u5730\u4e00\u58f0\u6012\u543c\uff0c\u8eab \u5f62\u9aa4\u8d77\uff0c\u5de6\u638c\u9a88\u6307\u5982\u621f\uff0c\u76f4\u70b9\u7389\u7f57\u5239\u9762\u4e0a\u53cc\u775b\uff0c\u53f3\u638c\u6a2a\u638c\u5982\u5200\uff0c\u6eda\u65ab\u7389\u7f57\u5239\u4e0b\u76d8\u53cc\u8db3\uff0c\u4e24\u53cc \u624b\u4e00\u4e0a\u4e00\u4e0b\uff0c\u5f62\u4f3c\u5cb3\u5bb6\u7684\u201c\u6491\u693d\u624b\u201d\uff0c\u4f46\u529b\u96c4\u52bf\u6377\uff0c\u6bd4\u6b63\u5b97\u7684\u5cb3\u5bb6\u201c\u6491\u693d\u624b\u201d\u8fd8\u8981\u5389\u5bb3\u5f97 \u591a\uff01\u7389\u7f57\u5239\u5251\u5df2\u9012\u51fa\uff0c\u64a4\u62db\u4e0d\u53ca\uff0c\u8eab\u5f62\u4e00\u6c89\u4e00\u7eb5\uff0c\u731b\u7684\u65bd\u5c55\u201c\u71d5\u5b50\u94bb\u4e91\u201d\u7684\u7edd\u9876\u8f7b\u529f\uff0c\u51ed\u7a7a \u7a9c\u8d77\u53c1\u4e08\u591a\u9ad8\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u5012\u7ffb\uff0c\u843d\u5728\u5c71\u8170\u5904\u7684\u4e00\u5757\u5927\u5ca9\u77f3\u4e0a\u3002\u90a3\u8001\u4eba\u8ddf\u8e2a\u76f4\u4e0a\uff0c\u6012\u6781\u559d \u9053\uff1a\u201c\u6211\u751f\u5e73\u8fd8\u672a\u78b0\u5230\u8fc7\u6562\u5728\u6211\u9762\u524d\u53eb\u9635\u7684\u4eba\uff0c\u4f60\u80c6\u6562\u5982\u6b64\u65e0\u793c\uff01\u4f60\u7684\u5e08\u7236\u53eb\u4ec0\u9ebd\u540d\u5b57\uff1f\u201d \u7389\u7f57\u5239\u9762\u8272\u5fae\u53d8\uff0c\u65cb\u5373\u626c\u58f0\u7b11\u9053\uff1a\u201c\u6211\u751f\u5e73\u4e5f\u672a\u78b0\u8fc7\u6562\u5728\u6211\u9762\u524d\u5927\u58f0\u547c\u559d\u7684\u4eba\uff0c\u4f60\u7684\u5e08\u7236\u53eb \u4ec0\u9ebd\u540d\u5b87\uff1f\u201d\u8fd9\u8001\u4eba\u4e43\u98ce\u5c18\u5f02\u58eb\uff0c\u751f\u5e73\u7684\u786e\u672a\u9022\u654c\u624b\uff0c\u4ed6\u559d\u95ee\u7389\u7f57\u5239\u7684\u5e08\u627f\uff0c\u4e43\u662f\u81ea\u5c45\u524d\u8f88 \u8eab\u4efd\uff0c\u60f3\u4e0d\u5230\u7389\u7f57\u5239\u8fd9\u6837\u4e00\u4e2a\u5e74\u8f7b\u5973\u5b50\uff0c\u5c45\u7136\u4e5f\u559d\u95ee\u4ed6\u7684\u5e08\u627f\u201c\u4ed6\u7684\u5e08\u7236\u65e9\u6b7b\u4e86\u53c1\u5341\u591a \u5e74\u201d\uff0c\u628a\u4ed6\u4e5f\u5f53\u6210\u5f8c\u751f\u5c0f\u8f88\uff01\u8fd9\u8001\u4eba\u987b\u7709\u6380\u52a8\uff0c\u6012\u6781\u6c14\u6781\uff0c\u66b4\u559d\u4e00\u58f0\uff1a\u201c\u72c2\u5984\u5c0f\u8f88\uff0c\u5403\u6211\u4e00 \u638c\uff01\u201d\u7389\u7f57\u5239\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u5728\u5ca9\u77f3\u4e0a\u7a81\u7136\u63a0\u4e0b\u3002\u6b63\u662f\uff1a\u5973\u9b54\u9022\u8001\u602a\uff0c\u5251\u638c\u5224\u96cc\u96c4\u3002\u6b32\u77e5\u5f8c\u4e8b \u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377\u2014\u2014\u7b2c\u516d\u56de\u3000\u6708\u591c\u8bc9\u60c5\u6000\u3000\u5b7d\u7f18\u7ea0\u7ed3\u3000\u8352\u5c71\u6597\u5947\u58eb\u3000\u5251\u638c\u4e89\u96c4 \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377 \u7b2c\u516d\u56de \u6708\u591c\u8bc9\u60c5\u6000\u3000\u5b7d\u7f18\u7ea0\u7ed3 \u8352\u5c71\u6597\u5947\u58eb\u3000\u5251\u638c\u4e89\u96c4 \u3000\u3000\u8001\u4eba\u8fd9\u4e00\u638c\u8fd0\u8db3\u5185\u5bb6\u529f\u529b\uff0c\u4e00\u638c\u5288\u53bb\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u7389\u7f57\u5239\u4e00\u63a0\u907f\u8fc7\uff0c\u8863\u8882\u98ce\u98d8\uff0c\u957f\u5251\u7a81 \u81ea\u534a\u7a7a\u523a\u4e0b\uff0c\u8001\u4eba\u970d\u5730\u4e00\u4e2a\u8f6c\u8eab\uff0c\u53cc\u638c\u9f50\u51fa\uff0c\u731d\u51fb\u7389\u7f57\u5239\u547d\u95e8\u8981\u7a74\uff0c\u7389\u7f57\u5239\u8eab\u5f62\u5fae\u52a8\uff0c\u957f\u5251 \u4e00\u62db\u201c\u91d1\u9488\u5ea6\u7ebf\u201d\u53cd\u6311\u4e0a\u6765\uff0c\u90a3\u8001\u4eba\u4f3c\u65e9\u5df2\u6599\u5230\u5979\u8981\u4f7f\u8fd9\u4e00\u62db\uff0c\u62a2\u524d\u4e00\u6b65\u3002\u7389\u7f57\u5239\u5251\u5c16\u5728\u4ed6 \u808b\u65c1\u500f\u7136\u7a7f\u8fc7\uff0c\u4ed6\u53cc\u638c\u5408\u62e2\uff0c\u5de6\u53f3\u4e00\u5206\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u5df2\u4ece\u201c\u7ae5\u5b50\u62dc\u89c2\u97f3\u201d\u7684\u62db\u5f0f\u53d8\u6210\u201c\u9634\u9633 \u53cc\u649e\u638c\u201d\uff0c\u5411\u7389\u7f57\u5239\u75db\u4e0b\u6740\u624b\u3002\u90a3\u77e5\u7389\u7f57\u5239\u4e5f\u4f3c\u65e9\u5df2\u6599\u4ed6\u6709\u6b64\u4e00\u62db\uff0c\u5251\u628a\u4e00\u6c89\uff0c\u5251\u950b\u53cd\u5f39\uff0c \u8f6c\u5411\u8001\u4eba\u814b\u4e0b\u7684\u201c\u671f\u95e8\u7a74\u201d\u523a\u53bb\uff0c\u8001\u4eba\u811a\u6b65\u4e0d\u52a8\uff0c\u8eab\u5f62\u9661\u7136\u4e00\u7f29\uff0c\u907f\u5f00\u8fd9\u62db\uff0c\u7a81\u7136\u5316\u638c\u4e3a \u62f3\uff0c\u4e00\u62db\u201c\u6a2a\u8eab\u6253\u864e\u201d\u731b\u6363\u51fa\u53bb\u3002\u7389\u7f57\u5239\u62d4\u8eab\u4e00\u7eb5\uff0c\u53c8\u98de\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u659c\u659c\u5411\u4e0b\u4e00\u843d\uff0c\u8001\u4eba \u559d\u9053\uff1a  \u201c\u5c0f\u8f88\u63a5\u62db\uff01\u201d\u8ddf\u8e2a\u731b\u6251\uff0c\u7389\u7f57\u5239\u76c8\u76c8\u7b11\u9053\uff1a \u201c\u8001\u8d3c\u63a5\u62db\uff1f\u201d\u5251\u8eab\u4e00\u6a2a\uff0c\u5e73\u524a\u51fa \u53bb\uff0c\u8001\u4eba\u53ea\u9053\u5979\u4f7f\u7684\u662f\u8fbe\u6469\u5251\u4e2d\u7684\u201c\u6a2a\u6c5f\u98de\u6e21\u201d\uff0c\u811a\u8e0f\u201c\u574e\u201d\u4f4d\uff0c\u8f6c\u8fdb\u201c\u79bb\u201d\u65b9\uff0c\u53cd\u624b\u4e00 \u638c\uff0c\u5c31\u8981\u64d2\u5979\u6301\u5251\u7684\u624b\u8155\uff0c\u90a3\u77e5\u7389\u7f57\u5239\u4e00\u5251\u524a\u53bb\uff0c\u65b9\u5230\u4e2d\u9014\uff0c\u5251\u52bf\u5ffd\u53d8\uff0c\u6b63\u6b63\u5411\u7740\u5bf9\u65b9\u6240\u907f \u7684\u65b9\u4f4d\u524a\u6765\uff0c\u90a3\u8001\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u5e78\u4ed6\u6b66\u529f\u7cbe\u6e5b\uff0c\u53d8\u62db\u8fc5\u901f\uff0c\u4ece\u201c\u79bb\u201d\u4f4d\u4e00\u65cb\uff0c\u5de6\u638c\u9a88\u4e86\u4e2d\u98df \u4e8c\u6307\uff0c\u53cd\u70b9\u7389\u7f57\u5239\u80a9\u5f8c\u7684\u201c\u51e4\u773c\u7a74\u201d\uff0c\u7389\u7f57\u5239\u5251\u52bf\u75be\u8f6c\uff0c\u4ee5\u653b\u5bf9\u653b\uff0c\u8feb\u5f97\u8001\u4eba\u53c8\u4ece\u201c\u79bb\u201d\u4f4d \u907f\u5f00\uff0c\u4e24\u4eba\u7684\u653b\u52bf\u90fd\u843d\u4e86\u7a7a\u3002 \u3000\u3000\u7389\u7f57\u5239\u4e0e\u90a3\u8001\u4eba\u6597\u62a2\u653b\u52bf\uff0c\u4e00\u62db\u4e00\u5f0f\uff0c\u6beb\u4e0d\u653e\u677e\uff0c\u5206\u5bf8\u4e4b\u95f2\uff0c\u4e92\u4e89\u5148\u624b\u3002\u7389\u7f57\u5239\u5251\u6cd5\u5947 \u7edd\uff0c\u4f3c\u524d\u5ffd\u5f8c\uff0c\u4f3c\u5de6\u5ffd\u53f3\uff0c\u6742\u6709\u5404\u5bb6\u5251\u6cd5\uff0c\u5374\u53c8\u65e0\u4e00\u62db\u96f7\u540c\u3002\u90a3\u8001\u4eba\u7684\u638c\u6cd5\u4e5f\u6781\u602a\u5f02\u3002\u5c3d\u7ba1 \u4ed6\u51fa\u624b\u8fc5\u82e5\u96f7\u9706\uff0c\u75be\u5982\u98ce\u96e8\uff0c\u8eab\u6cd5\u6b65\u6cd5\u5374\u662f\u6309\u7740\u201c\u516b\u95e8\u201d\u201c\u4e94\u6b65\u201d\u4e1d\u6beb\u4e0d\u4e71\u3002\u6309\uff1a\u5728\u6b66\u5b66 \u4e2d\uff0c\u201c\u516b\u95e8\u201d\u5373\u662f\u6307\u516b\u4e2a\u65b9\u5411\uff0c\u6839\u636e\u201c\u516b\u5366\u201d\u7684\u574e\u3001\u79bb\uff0e\u514b\uff0e\u9707\uff0e\u5dfd\u3001\u4e7e\uff0e\u5764\u3001\u826e\u516b\u4e2a\u65b9\u4f4d \u800c\u6765\uff0c\u5373\u56db\u4e2a\u201c\u6b63\u65b9\u5411\u201d\u548c\u56db\u4e2a\u201c\u659c\u65b9\u5411\u201d\uff1a\u201c\u4e94\u6b65\u201d\u662f\u6307\u4e94\u4e2a\u7acb\u8db3\u7684\u4f4d\u7f6e\uff0c\u6839\u636e\u201c\u4e94\u884c\u201d \u7684\u91d1\uff0c\u6728\uff0e\u6c34\uff0e\u706b\uff0e\u571f\u4e94\u4e2a\u65b9\u5411\u800c\u6765\uff0c\u5373\uff1a\u524d\u8fdb\uff0e\u5f8c\u9000\uff0c\u5de6\u987e\uff0c\u201c\u542b\u5411\u5de6\u8f6c\u52a8\u610f\u201d\u53f3\u76fc\u201c\u542b \u5411\u53f3\u8f6c\u52a8\u610f\u201d\uff0c\u4e2d\u5b9a\u3002 \u201d\u8fd9\u201c\u516b\u95e8\u201d\u201c\u4e94\u6b65\u201d\u7684\u8fdb\u9000\u53d8\u5316\uff0c\u672c\u662f\u592a\u6781\u6d3e\u9f3b\u7956\u5f20\u53c1\u4e30\u6240\u521b\uff0c \u79f0\u4e3a\u201c\u592a\u6781\u5341\u53c1\u52bf\u201d\uff0c\u592a\u6781\u62f3\u8bb2\u7a76\u7684\u662f\u4ee5\u67d4\u514b\u521a\u3002\u8fd9\u8001\u4eba\u7684\u638c\u6cd5\u521a\u52b2\u4e4b\u6781\uff0c\u7528\u7684\u5374\u662f\u201c\u592a\u6781 \u5341\u53c1\u52bf\u201d\u7684\u8eab\u6cd5\u6b65\u6cd5\uff0c\u521a\u67d4\u5408\u7528\uff0c\u82e5\u975e\u529f\u592b\u5df2\u5230\u5316\u5883\uff0c\u4e07\u4e07\u4e0d\u80fd\u3002\u7389\u7f57\u5239\u548c\u4ed6\u4ee5\u653b\u5bf9\u653b\uff0c\u6597 \u4e86\u4e00\u767e\u6765\u62db\uff0c\u5360\u4e0d\u5230\u534a\u70b9\u4fbf\u5b9c\uff0c\u6697\u6697\u5403\u60ca\uff0c\u4e0d\u6562\u518d\u5b09\u7b11\u513f\u620f\uff0c\u9762\u8272\u51dd\u91cd\uff0c\u4e13\u5fc3\u6ce8\u654c\uff0c\u628a\u5e08\u5085 \u6240\u521b\u7684\u72ec\u95e8\u5251\u6cd5\uff0c\u8d8a\u53d1\u4f7f\u5f97\u51cc\u5389\u65e0\u524d\uff01 \u3000\u3000\u90a3\u8001\u4eba\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u4e5f\u662f\u5360\u4e0d\u5230\u4e1d\u6beb\u4fbf\u5b9c\u3002\u7389\u7f57\u5239\u5251\u6cd5\u4e4b\u5947\uff0c\u5904\u5904\u4ee4\u4ed6\u4e0d\u5f97\u4e0d\u5c0f\u5fc3\u9632 \u5907\u3002\u6597\u5230\u75be\u5904\uff0c\u638c\u98ce\u5251\u5149\u4e0b\uff0c\u4e24\u6761\u4eba\u5f71\u7a7f\u63d2\u6765\u5f80\uff0c\u7adf\u5206\u4e0d\u51fa\u8c01\u662f\u8001\u5934\uff0c\u8c01\u662f\u5c11\u5973\uff01 \u3000\u3000\u8fd9\u8001\u4eba\u6697\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u771f\u6599\u4e0d\u5230\u50cf\u7389\u7f57\u5239\u8fd9\u6837\u7f8e\u82e5\u5929\u4ed9\u7684\u5c11\u5973\uff0c\u5251\u6cd5\u7adf\u7136\u51f6\u72e0\u65e0\u6bd4\uff0c\u7684 \u786e\u662f\u524d\u6240\u672a\u9022\uff0c\u5e73\u751f\u4ec5\u89c1\u7684\u52b2\u654c\u3002\u7389\u7f57\u5239\u4e5f\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u6599\u4e0d\u5230\u8fd9\u8001\u4eba\u638c\u6cd5\u5982\u6b64\u96c4\u52b2\uff0c\u82e5 \u7136  \u8bba\u529f\u529b\uff0c\u53ea\u6015\u8fd9\u8001\u4eba\u8fd8\u5728\u81ea\u5df1\u4e4b\u4e0a\u3002 \u3000\u3000\u4e24\u4eba\u6597\u5f97\u9e21\u89e3\u96be\u5206\uff0c\u53cc\u65b9\u90fd\u662f\u9669\u62db\u8fed\u89c1\uff01\u9163\u6597\u4e2d\u7389\u7f57\u5239\u5ffd\u95fb\u5f97\u5c71\u5f8c\u98d8\u6765\u4e00\u58f0\u60ca\u53eb\uff0c\u7adf\u4f3c \u662f\u5353\u4e00\u822a\u7684\u58f0\u97f3\uff0c\u5fc3\u795e\u4e00\u6c64\uff0c\u5251\u62db\u7a0d\u7f13\uff0c\u90a3\u8001\u4eba\u4ece\u201c\u826e\u201d\u4f4d\u547c\u7684\u4e00\u638c\u5288\u6765\uff0c\u7389\u7f57\u5239\u523a\u4e00\u62db \u201c\u661f\u6a2a\u6597\u8f6c\u201d\uff0c\u90a3\u8001\u4eba\u638c\u950b\u5c06\u6b32\u6cbe\u8863\uff0c\u773c\u770b\u5c31\u8981\u4e24\u8d25\u4ff1\u4f24\uff0c\u5ffd\u7136\u8df3\u5f8c\u4e24\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u4e0a \u6765\uff01\u201d\u7389\u7f57\u5239\u659c\u773c\u4e00\u671b\uff0c\u5728\u90a3\u5c11\u5973\u6240\u7ad9\u7684\u5ca9\u77f3\u4e0a\uff0c\u53c8\u591a\u4e86\u4e00\u4e2a\u4e2d\u5e74\u7f8e\u5987\u3002\u90a3\u8001\u4eba\u7684\u8bdd\uff0c\u539f\u6765 \u662f\u5bf9\u8fd9\u7f8e\u5987\u4eba\u8bf4\u7684\u3002\u4ee5\u7389\u7f57\u5239\u6b66\u529f\u4e4b\u9ad8\uff0c\u8033\u76ee\u4e4b\u7075\uff0c\u7adf\u89c9\u5bdf\u4e0d\u51fa\u5979\u662f\u4f55\u65f6\u6765\u7684\uff0c\u53ef\u89c1\u9002\u624d\u7684 \u5267\u6597\uff0c\u662f\u4f55\u7b49\u731b\u70c8\uff0c\u4ee4\u7389\u7f57\u5239\u4e5f\u5206\u4e0d\u51fa\u534a\u70b9\u5fc3\u795e\u3002 \u3000\u3000\u8fd9\u65f6\u7389\u7f57\u5239\u5bf9\u90a3\u8001\u4eba\uff0c\u4e5f\u5df2\u5fae\u5fae\u6709\u70b9\u4f69\u670d\u3002\u5fc3\u60f3\uff1a\u9ad8\u624b\u5bf9\u9635\uff0c\u5fc5\u987b\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b \u65b9\uff0c\u81ea\u5df1\u4e00\u78b0\u5230\u65d7\u9f13\u76f8\u5f53\u7684\u654c\u624b\uff0c\u5c31\u5206\u4e0d\u51fa\u5fc3\u795e\uff0c\u706b\u5019\u7a76\u662f\u8f83\u900a\u3002\u90a3\u8001\u4eba\u559d\u4e86\u4e00\u58f0\uff0c\u7ffb\u8eab\u518d \u6251\uff0c\u559d\u9053\uff1a\u201c\u54b1\u4eec\u518d\u6597\uff01\u201d\u7389\u7f57\u5239\u6012\u9053\uff1a\u201c\u96be\u9053\u6015\u4f60\u4e0d\u6210\u3002\u6789\u4f60\u6b66\u529f\u5982\u6b64\u4e4b\u9ad8\uff0c\u5374\u505a\u4e0b\u53c1\u6d41 \u5c0f\u8d3c\uff0c\u4eca\u65e5\u4e0d\u5c06\u5251\u8c31\u8fd8\u6211\uff0c\u8a93\u4e0d\u4e0e\u4f60\u5e72\u4f11\uff01\u201d\u5237\u5237\u4e24\u5251\uff0c\u8fde\u73af\u75be\u523a\uff0c\u8001\u4eba\u5927\u6012\uff0c\u4e00\u62db\u201c\u6392\u5c71 \u5012\u6d77\u201d\u8fce\u51fb\uff0c\u4e24\u4eba\u53c8\u6597\u5728\u4e00\u8d77\u3002 \u3000\u3000\u5ca9\u77f3\u4e0a\uff0c\u5148\u524d\u4e0e\u7389\u7f57\u5239\u5bf9\u654c\u7684\u5c11\u5973\u5bf9\u5f8c\u6765\u7684\u7f8e\u5987\u8bf4\u9053\uff1a\u201c\u73c2\u59e8\uff0c\u4f60\u6253\u90a3\u8d3c\u5a46\u5a18\u4e00\u4e0b\u3002\u201d \u7f8e\u5987\u9053\uff1a\u201c\u963f\u745a\uff0c\u4f60\u7684\u8774\u8776\u9556\u6253\u5f97\u6bd4\u6211\u8fd8\u597d\uff0c\u4e3a\u4f55\u8981\u6211\u732e\u4e11\uff1f\u201d\u5c11\u5973\u9053\uff1a\u201c\u7239\u7239\u8bf4\u8fc7\u4e0d\u51c6\u6211 \u5e2e\u624b\u3002\u201d\u7f8e\u5987\u6084\u6084\u95ee\u9053\uff1a\u201c\u5979\u8bf4\u4ec0\u9ebd\u5251\u8c31\uff0c\u96be\u9053\u90a3\u5251\u8c31\u662f\u5979\u7684\u5417\uff1f\u201d\u5c11\u5973\u53d8\u4e86\u989c\u8272\uff0c\u51d1\u5728\u5979 \u7684\u8033\u6839\u8bf4\u9053\uff1a\u201c\u5feb\u70b9\u522b\u8bf4\uff0c\u7ed9\u7239\u7239\u542c\u89c1\uff0c\u90a3\u53ef\u8981\u7cdf\uff01\u201d\u90a3\u7f8e\u5987\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u91cc\u8bf4\u9053\uff1a\u201c\u8fd9 \u8001\u4e0d\u6b7b\u6b63\u5728\u4e0e\u522b\u4eba\u62da\u547d\uff0c\u58f0\u97f3\u8bf4\u5f97\u518d\u5927\u4e00\u70b9\u4ed6\u90fd\u542c\u4e0d\u89c1\u3002\u201d\u89c1\u5c11\u5973\u60c5\u6025\uff0c\u4ece\u6000\u4e2d\u638f\u51fa\u53c1\u53ea\u8774 \u8776\u9556\u6765\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u8bf4\u4fbf\u662f\uff0c\u4f60\u770b\u6211\u6253\u5979\uff01\u201d\u53f3\u624b\u626c\u7a7a\u4e00\u6296\uff0c\u53c1\u53ea\u8774\u8776\u9556\u53d1\u51fa\u545c\u545c\u602a\u53eb\uff0c\u95ea \u7535\u822c\u7684\u5411\u7389\u7f57\u5239\u98de\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u7389\u7f57\u5239\u4e0e\u90a3\u8001\u4eba\u6597\u5f97\u6b63\u9163\uff0c\u7389\u7f57\u5239\u7684\u5251\u62db\u8d8a\u5c55\u8d8a\u5feb\uff0c\u90a3\u8001\u4eba\u7684\u638c\u529b\u4e5f\u8d8a\u53d1\u8d8a\u52b2\u3002\u4e24 \u4eba\u6b63\u5728\u5168\u795e\u62da\u6597\uff0c\u6697\u5668\u5ffd\u7136\u4fa7\u9762\u88ad\u6765\u3002\u7389\u7f57\u5239\u542c\u58f0\u8fa8\u5668\uff0c\u65e9\u77e5\u6653\u8fd9\u53c1\u679a\u8774\u8776\u9556\u662f\u4e0a\u4e2d\u4e0b\u53c1 \u8def\uff0c\u5206\u6253\u81ea\u5df1\u7684\u201c\u6c14\u95e8\u7a74\u201d\uff0c\u201c\u5f53\u95e8\u7a74\u201d\u548c\u201c\u767d\u6d77\u7a74\u201d\u3002\u82e5\u6309\u7389\u7f57\u5239\u5e73\u5e38\u7684\u529f\u529b\uff0c\u8fd9\u53c1\u679a\u5c0f \u5c0f\u7684\u8774\u8776\u9556\u771f\u7b97\u4e0d\u4e86\u4ec0\u9ebd\uff0c\u53ea\u8981\u5979\u4e00\u4e3e\u624b\u4e00\u6ca1\u8db3\uff0c\u5c31\u53ef\u628a\u6765\u88ad\u7684\u6697\u5668\u5168\u90e8\u6253\u843d\u3002\u53ef\u662f\u73b0\u5728\u4e24 \u4eba\u62da\u6597\uff0c\u65d7\u9f13\u76f8\u5f53\uff0c\u4e00\u4eba\u529f\u529b\u9ad8\u5f3a\uff0c\u4e00\u4eba\u5251\u6cd5\u5389\u5bb3\uff0c\u521a\u521a\u62c9\u6210\u5e73\u624b\u3002\u6b63\u597d\u50cf\u5929\u5e73\u4e0a\u7684\u4e24\u8fb9\u781d \u7801\u521a\u521a\u76f8\u7b49\u4e00\u822c\uff0c\u53ea\u8981\u90a3\u4e00\u8fb9\u52a0\u4e0a\u4e00\u9488\u4e00\u7ebf\u4e4b\u5fae\uff0c\u7acb\u523b\u5c31\u8981\u5931\u53bb\u5e73\u8861\u72b6\u6001\uff01 \u3000\u3000\u7389\u7f57\u5239\u542c\u5f97\u6697\u5668\u98de\u6765\uff0c\u545c\u545c\u4f5c\u54cd\uff0c\u9762\u8272\u500f\u53d8\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u65e0\u803b\u5339\u592b\uff0c\u5984\u65bd\u6697\u7b97\uff01\u201d\u7adf \u7136\u4e0d\u907f\u6697\u5668\uff0c\u624b\u4e2d\u5251\u4e00\u62db\u201c\u6781\u76ee\u6ca7\u6ce2\u201d\u65cb\u5316\u201c\u53c1\u73af\u5957\u6708\u201d\uff0c\u6b63\u9762\u523a\u654c\u4eba\u7684\u201c\u5c06\u53f0\u7a74\u201d\uff0c\u4fa7\u9762 \u523a\u201c\u5de8\u9aa8\u7a74\u201d\u3002\u4f60\u9053\u7389\u7f57\u5239\u4f55\u4ee5\u4e0d\u907f\u6697\u5668\u3002\u539f\u6765\u7389\u7f57\u5239\u5fc3\u60f3\uff0c\u8981\u907f\u6697\u5668\u4e0d\u96be\uff0c\u53ef\u662f\u82e5\u7136\u5206\u795e \u62b5\u5fa1\uff0c\u4ee5\u654c\u624b\u529f\u529b\u4e4b\u9ad8\uff0c\u4e58\u865a\u8fdb\u51fb\uff0c\u81ea\u5df1\u5fc5\u65e0\u5e78\u514d\u3002\u4e0d\u5982\u62da\u4e2a\u4e24\u8d25\u4ff1\u4f24\uff0c\u6b7b\u4e5f\u6b7b\u5f97\u5149\u5f69\u3002\u8fd9 \u4e24\u5251\u51f6\u72e0\u5f02\u5e38\uff0c\u6dae\u6dae\u4e24\u5251\uff0c\u679c\u7136\u8feb\u5f97\u8001\u4eba\u4ece\u201c\u826e\u201d\u4f4d\u76f4\u8ffd\u5230\u201c\u4e7e\u5bab\u201d\uff0c\u7389\u7f57\u5239\u624b\u5e95\u4e1d\u6beb\u4e0d \u7f13\uff0c\u633a\u8eab\u8fdb\u5251\uff0c\u4ece\u201c\u53c1\u73af\u5957\u6708\u201d\u4e00\u53d8\u53c8\u6210\u201c\u767d\u8679\u5c04\u65e5\u201d\uff0c\u5251\u5c16\u76f4\u6307\u8001\u4eba\u80f8\u53e3\u7684\u201c\u7384\u673a\u7a74\u201d\uff0c \u8fd9\u65f6\u53c1\u679a\u8774\u8776\u9556\u5df3\u8fde\u7fe9\u98de\u6765\uff0c\u7b2c\u4e00\u679a\u8ff3\u5411\u7740\u7389\u7f57\u5239\u54bd\u5589\uff0c\u773c\u770b\u7740\u5c31\u8981\u78b0\u4e0a\uff01 \u3000\u3000\u6697\u5668\u98de\u6765\uff0c\u4e0d\u552f\u7389\u7f57\u5239\u53d8\u4e86\u9762\u8272\uff0c\u90a3\u8001\u4eba\u4e5f\u6da8\u7ea2\u4e86\u9762\uff0c\u542c\u5f97\u7389\u7f57\u5239\u4e00\u9a82\uff0c\u66f4\u662f\u96be\u582a\uff0c\u80a9 \u5934\u4e00\u95ea\uff0c\u53f3\u638c\u7a81\u7136\u626c\u7a7a\u4e00\u5288\uff0c\u628a\u7b2c\u4e00\u679a\u8774\u8776\u9556\u9707\u5f97\u98de\u843d\u5c71\u811a\uff0c\u8fd9\u4e00\u4e0b\u5927\u51fa\u7389\u7f57\u5239\u610f\u5916\uff0c\u5979\u7684 \u5251\u6536\u52bf\u4e0d\u53ca\uff0c\u4e58\u9699\u5373\u5165\uff0c\u8001\u4eba\u80a9\u5934\u4e00\u95ea\uff0c\u53ea\u907f\u5f00\u4e86\u6b63\u9762\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u8863\u8896\u4ecd\u88ab\u523a\u7a7f\uff0c\u624b\u81c2\u88ab \u5251\u5c16\u5212\u4e86\u9053\u53e3\u5b50\uff0c\u9c9c\u8840\u6ef4\u51fa\u3002\u8001\u4eba\u95f7\u95f7\u4e0d\u54cd\uff0c\u5012\u8dc3\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u8fd9\u65f6\u7b2c\u4e8c\u679a\u7b2c\u53c1\u679a\u8774\u8776\u9556\u4e5f \u5df2\u5230\u4e86\u7389\u7f57\u5239\u8ddf\u524d\u3002 \u3000\u3000\u5f3a\u654c\u4e00\u9000\uff0c\u738b\u7f57\u5239\u957f\u5251\u4e00\u626b\uff0c\u4e24\u679a\u8774\u8776\u9556\u5168\u7ed9\u626b\u843d\u3002\u90a3\u8001\u5934\u8dd1\u4e0a\u5c71\u8170\uff0c\u6307\u7740\u7f8e\u5987\u5389\u58f0\u65a5 \u9053\uff1a\u201c\u8c01\u53eb\u4f60\u4e71\u653e\u6697\u5668\uff1f\u201d\u7f8e\u5987\u4eba\u773c\u6ce2\u4e00\u8f6c\uff0c\u72b6\u751a\u98ce\u9a9a\uff0c\u53ef\u662f\u5374\u88c5\u6210\u59d4\u59d4\u5c48\u5c48\u7684\u6837\u5b50\u8bf4\u9053\uff1a \u201c\u8001\u7237\u5b50\uff0c\u4f60\u53c8\u6ca1\u6709\u5429\u5490\u6211\u6765\uff0c\u963f\u745a\u53d7\u4e86\u5979\u7684\u6b3a\u8d1f\uff0c\u6211\u4eec\u53c8\u4f55\u5fc5\u5bf9\u5979\u5ba2\u6c14\uff1f\u8001\u7237\u5b50\uff0c\u6211\u8fd8\u4e0d \u662f\u4e3a\u4e86\u4f60\u4eec\u7236\u5973\uff01\u201d\u773c\u5708\u4e00\u7ea2\uff0c\u6cea\u73e0\u6b32\u6ef4\u3002\u7389\u7f57\u5239\u8eab\u5f62\u4e00\u8d77\uff0c\u7a81\u5982\u5927\u9e64\u63a0\u7a7a\uff0c\u84e6\u7136\u98de\u81f3\u3002\u559d \u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u8fd9\u8d3c\u5a46\u5a18\u653e\u7684\u6697\u5668\uff01\u201d\u53f3\u624b\u4e00\u626c\uff0c\u53c1\u679a\u94f6\u9488\u5728\u9633\u5149\u4e0b\u4e00\u95ea\uff0c\u8001\u5934\u4e3e\u8896\u4e00\u62c2\uff0c \u62c2\u843d\u4e24\u679a\uff0c\u7b2c\u53c1\u53e3\u94f6\u9488\u5374\u523a\u8fdb\u4e86\u90a3\u7f8e\u5987\u4eba\u7684\u80a9\u5934\uff0c\u75db\u5f97\u5979\u201c\u54df\u54df\u201d\u53eb\u558a\uff01 \u3000\u3000\u90a3\u8001\u5934\u559d\u9053\uff1a\u201c\u9002\u624d\u4f60\u5df2\u89c1\u5230\uff0c\u5979\u653e\u7684\u6697\u5668\u4e0e\u6211\u65e0\u5173\u3002\u4f60\u8fd9\u5973\u8d3c\u5341\u5206\u65e0\u793c\uff0c\u6b3a\u6211\u5973\u513f\uff0c \u4f24\u6211\u7231\u59be\uff0c\u6211\u4e0e\u4f60\u7edd\u4e0d\u5e72\u4f11\uff01\u54b1\u4eec\u5355\u6253\u72ec\u6597\uff0c\u8c01\u4e5f\u4e0d\u8bb8\u9080\u8bf7\u5e2e\u624b\uff0c\u4f60\u6562\u4e5f\u4e0d\u6562\uff1f\u201d\u7389\u7f57\u5239\u5ffd \u7136\u4e00\u7b11\uff0c\u8001\u4eba\u9762\u8272\u500f\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u73b0\u5728\u8981\u6597\u4e5f\u884c\uff01\u201d\u4ed6\u4ee5\u4e3a\u7389\u7f57\u5239\u662f\u7b11\u4ed6\u53d7\u4e86\u5251\u4f24\uff0c\u6240\u4ee5 \u624d\u8981\u7ea6\u671f\u518d\u6597\u3002\u5176\u5b9e\u7389\u7f57\u5239\u662f\u7b11\u4ed6\u4f5c\u4f2a\uff0c\u521a\u624d\u81ea\u5df1\u6240\u53d1\u7684\u53c1\u679d\u94f6\u9488\uff0c\u4ee5\u90a3\u8001\u5934\u7684\u529f\u529b\uff0c\u8981\u5168 \u90e8\u6253\u843d\u5e76\u4e0d\u96be\uff0c\u4ed6\u5374\u7559\u4e0b\u4e00\u679d\uff0c\u8ba9\u90a3\u7f8e\u5987\u4eba\u53d7\u4f24\uff0c\u60f3\u662f\u542b\u6709\u60e9\u7f5a\u4e4b\u610f\u3002\u5fc3\u9053\uff1a\u201c\u539f\u6765\u90a3\u5973\u4eba \u662f\u4ed6\u7684\u59be\u4f8d\uff0c\u602a\u4e0d\u5f97\u4ed6\u8981\u9690\u85cf\u521a\u624d\u7684\u4f5c\u4f2a\uff0c  \u662f\u602a\u6211\u4f24\u5979\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u5077\u6211\u7684\u5251\u8c31\uff0c \u6211\u4e5f\u51b3\u4e0d\u4e0e\u4f60\u5e72\u4f11\uff0c\u4f46\u4eca\u65e5\u5f7c\u6b64\u90fd\u75b2\uff0c\u518d\u6597\u4e5f\u6597\u4e0d\u51fa\u4ec0\u9ebd\u9053\u7406\uff0c\u4f60\u4f4f\u5728\u4f55\u65b9\uff0c\u82e5\u80af\u8d50\u77e5\uff0c\u6211 \u5fc5\u767b\u95e8\u8bf7\u6559\uff01\u201d\u7389\u7f57\u5239\u8bf4\u8bdd\u7f13\u548c\u4e86\u8bb8\u591a\uff0c\u800c\u4e14\u5e76\u6ca1\u63d0\u90a3\u8001\u5934\u53d7\u4f24\u4e4b\u4e8b\u3002 \u3000\u3000\u90a3\u8001\u5934\u662f\u4e2a\u6210\u540d\u4eba\u7269\uff0c\u521a\u624d\u4ed6\u7684\u7231\u59be\u98de\u9556\u76f8\u52a9\uff0c\u51e0\u4e4e\u4ee4\u4ed6\u4e0b\u4e0d\u4e86\u53f0\u3002\u6240\u4ee5\u867d\u53d7\u5251\u4f24\uff0c\u4e5f \u4e0d\u52a8\u6012\u3002\u89c1\u7389\u7f57\u5239\u4e00\u95ee\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u4e00\u6708\u4e4b\u5185\uff0c\u6211\u5728\uff0e\u9f99\u95e8\u94c1\u5bb6\u5e84\u7b49\u4f60\uff01\u201d\u7389 \u7f57\u5239\u51db\u7136\u4e00\u60ca\uff0c\u90a3\u8001\u5934\u4e00\u624b\u643a\u59be\uff0c\u4e00\u624b\u643a\u5973\uff0c\u75be\u5fd9\u4e0b\u5c71\uff0c\u7389\u7f57\u5239\u6b63\u60f3\u8ffd\u4e0b\u53bb\u518d\u95ee\uff0c\u5ffd\u542c\u5f97\u5c71 \u8170\u5904\u5353\u4e00\u822a\u548c\u738b\u7167\u5e0c\u540c\u58f0\u558a\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\uff0c\u7ec3\u59d0\u59d0\uff0c\u5feb\u6765\uff0c\u5feb\u6765\uff01\u201d\u53eb\u201c\u7ec3\u59d0\u59d0\u201d\u7684\u662f\u5353\u4e00 \u822a\uff0c\u7389\u7f57\u5239\u5fc3\u91cc\u751c\u4e1d\u4e1d\u7684\uff0c\u4f46\u53c8\u6015\u4ed6\u4eec\u906d\u9022\u51f6\u9669\uff0c\u6025\u5fd9\u8f6c\u8fc7\u5c71\u5f8c\u3002 \u3000\u3000\u5c71\u5f8c\u4e71\u77f3  \uff0c\u738b\u7167\u5e0c\u4e0e\u5353\u4e00\u822a\u8eab\u5b50\u534a\u8e72\uff0c\u6324\u5728\u4e00\u4e2a\u77f3\u7a9f\u4e4b\u5185\uff0c\u7389\u7f57\u5239\u5947\u9053\uff1a\u201c\u5582\uff0c\u4f60 \u4eec\u505a\u4ec0\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u53cd\u8eab\u8df3\u51fa\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u8d1e\u4e7e\u9053\u4eba\u7ed9\u5bb3\u6b7b\u4e86\uff01\u201d\u7389\u7f57\u5239\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4ec0 \u9ebd\uff1f\u8d1e\u4e7e\u9053\u4eba\u7ed9\u5bb3\u6b7b\u4e86\uff01\u201d\u4e0a\u524d\u53bb\u770b\uff0c\u53ea\u89c1\u77f3\u7a9f\u5185\u8d1e\u4e7e\u9053\u4eba\u76d8\u819d\u800c\u5750\uff0c\u4e03\u7a8d\u6d41\u8840\uff0c\u72b6\u751a\u75db \u695a\uff0c\u7389\u7f57\u5239\u4f38\u624b\u53bb\u6478\uff0c\u8109\u606f\u867d\u65ad\uff0c\u4f53\u5c1a\u9980\u6e29\uff0c\u77e5\u4ed6\u65ad\u6c14\u672a\u4e45\u3002\u5353\u4e00\u822a\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u6709\u4eba\u89ca\u89ce \u4ed6\u6240\u5e26\u7684\u5251\u8c31\uff0c\u6240\u4ee5\u628a\u4ed6\u5bb3\u6b7b\u4e86\uff01\u201d\u7389\u7f57\u5239\u6c14\u5598\u5fc3\u8df3\uff0c\u6025\u5fd9\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u4ec0\u9ebd\u5251\u8c31\uff1f\u201d \u5353\u4e00\u822a\u9053\uff1a\u201c\u5c31\u662f\u4f60\u5e08\u7236\u6240\u7740\u7684\u5251\u8c31\uff0c\u545c\u73c2\u5927\u54e5\u6258\u8d1e\u4e7e\u9053\u957f\u5e26\u7ed9\u5929\u90fd\u8001\u4eba\u3002\u60f3\u4e0d\u5230\u4ed6\u8eab\u6b7b\u6b64 \u5730\uff0c\u5251\u8c31\u4e5f\u4e0d\u89c1\u4e86\uff01\u201d\u7389\u7f57\u5239\u6012\u53eb\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u94c1\u8001\u8d3c\u5e72\u7684\u52fe\u5f53\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u524d\u8f88\u82f1\u96c4\uff0c \u6709\u51e0\u5206\u4fa0\u4e49\u672c\u8272\uff0c\u90a3\u77e5\u4ed6\u5077\u4e86\u6211\u7684\u5251\u8c31\uff0c\u8fd8\u5bb3\u4e86\u8d1e\u4e7e\u9053\u4eba\u3002\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u600e\u89c1\u5f97\u662f\u4ed6\uff1f\u201d \u7389\u7f57\u5239\u9053\uff1a\u201c\u8d1e\u4e7e\u9053\u4eba\u6b66\u529f\u8d85\u5353\uff0c\u4e0d\u662f\u8fd9\u4e2a\u8001\u8d3c\u51fa\u624b\uff0c\u8fd8\u6709\u8c01\u4f24\u5f97\u4e86\u4ed6\uff1f\u5582\uff0c\u738b\u7167\u5e0c\uff0c\u4f60\u548c \u8fd9\u8001\u8d3c\u662f\u4e0d\u662f\u8001\u76f8\u8bc6\uff0c\u5feb\u8bf4\uff01\u201d\u5353\u4e00\u822a\u95ee\u9053\uff1a\u201c\u8bf4\u4e86\u8fd9\u9ebd\u534a\u5929\uff0c\u5230\u5e95\u8c01\u662f\u201c\u94c1\u8001\u8d3c\u201d\uff1f\u201d \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u867d\u7136\u51fa\u9053\u672a\u6ee1\u53c1\u5e74\uff0c\u4f46\u9ed1\u767d\u4e24\u9053\u7684\u82f1\u96c4.\u4e5f\u77e5\u4e2a\u5927\u6982\u3002\u5c71\u897f\u9f99\u95e8\u53bf\u7684\u94c1 \u98de\u9f99\u5c31\u662f\u897f\u5317\u7684\u4e00\u4e2a\u602a\u7269\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u4ed6\u8fd9\u4eba\u4ecb\u4e4e\u6b63\u90aa\u4e24\u8005\u4e4b\u95ee\uff0c\u597d\u4e8b\u4e5f \u505a\uff0c\u574f\u4e8b\u4e5f\u505a\uff0c\u8c01\u8981\u5192\u72af\u4e86\u4ed6\uff0c\u4e00\u5b9a\u4f1a\u7ed9\u4ed6\u51cc\u8fb1\u81f3\u6b7b\u3002\u4f46\u4ed6\u4e00\u751f\u81ea\u8d1f\uff0c\u672a\u5fc5\u80af\u5077\u522b\u6d3e\u5251 \u8c31\u3002\u201d\u7389\u7f57\u5239\u77aa\u773c\u8bf4\u9053\uff1a\u201c\u96c9\u9053\u6211\u8fd8\u770b\u9519\uff0c\u5728\u5e9c\u8859\u4e2d\u7684\u90a3\u4e2a\u662f\u4e0d\u662f\u4ed6\u7684\u5973\u513f\uff1f\u201d\u738b\u7167\u5e0c\u795e\u8272 \u5c34\u5c2c\uff0c\u70b9\u5934\u9053\uff1a\u201c\u662f\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4ed6\u5973\u513f\u4f7f\u7684\u5c31\u662f\u6211\u7684\u672c\u95e8\u5251\u6cd5\u3002\u201d\u738b\u7167\u5e0c\u7741\u5927\u773c\u775b\uff0c \u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684\u4e8b\uff01\u201d\u7389\u7f57\u5239\u51b7\u7b11\u9053\uff1a\u201c\u60f3\u662f\u4f60\u89c1\u5979\u7f8e\u8c8c\uff0c\u6240\u4ee5\u56de\u62a4\u5979\u4e86\uff01\u201d\u738b\u7167\u5e0c\u5413\u5f97\u9000 \u4e86\u4e24\u6b65\uff0c\u606d\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u8001\u5934\u548c\u5bb6\u7236\u76f8\u8bc6\uff0c\u6211\u5bf9\u4ed6\u7684\u4e3a\u4eba\uff0c\u4e5f\u662f  \u5f97\u4e4b\u4f20\u95fb\uff0c\u5e76\u4e0d\u77e5\u9053\u5e95 \u8574\u3002\u201d\u5176\u5b9e\u738b\u7167\u5e0c\u4e0e\u94c1\u5bb6\u7236\u5973\u6709\u4e00\u6bb5\u8fc7\u8282\uff0c\u672c\u60f3\u8bf4\u51fa\uff0c\u4f46\u89c1\u7389\u7f57\u5239\u5982\u6b64\u52a8\u6012\uff0c  \u597d\u628a\u8981\u8bf4\u7684 \u8bdd\uff0c\u541e\u56de\u8179\u4e2d\u3002 \u3000\u3000\u7389\u7f57\u5239\u53c8\u9053\uff1a\u201c\u9002\u624d\u6211\u8fd8\u548c\u94c1\u8001\u8d3c\u6253\u4e86\u534a\u5929\uff0c\u6211\u672c\u6765\u4e0d\u77e5\u4ed6\u662f\u8c01\u4eba\uff0c\u4ed6\u4e34\u8d70\u53eb\u6211\u5230\u9f99\u95e8 \u94c1\u5bb6\u5e84\u627e\u4ed6\uff0c\u4ed6\u771f\u80c6\u5927\uff0c\u52ab\u4e66\u5bb3\u547d\uff0c\u8fd8\u6562\u7559\u4e0b\u59d3\u540d\uff0c\u6211\u975e\u627e\u4ed6\u7b97\u5e10\u4e0d\u53ef\uff01\u201d\u5353\u4e00\u822a\u5ffd\u7136\u201c\u554a \u5440\u201d\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u60f3\u8d77\u6765\u4e86\uff0c\u8fd9\u8001\u5934\u662f\u9e70\u9f3b\u72ee\u53e3\uff0c\u6ee1\u5634\u7edc\u816e\u77ed\u987b\uff0c\u76f8\u8c8c\u4e11\u964b\u7684\uff0c\u662f\u4e5f\u4e0d \u662f\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u4e5f\u8ba4\u5f97\u4ed6\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5927\u7ea6\u4e03\u516b\u5e74\u524d\uff0c\u4ed6\u66fe\u627e\u8fc7\u6211\u7684\u5e08\u7236\u6bd4\u638c\uff0c \u6211\u7684\u5e08\u7236\u4e0d\u80af\uff0c\u53eb\u56db\u5e08\u53d4\u548c\u4ed6\u6bd4\u8bd5\uff0c\u7ed3\u679c\u8f93\u4e86\u4e00\u62db\u3002\u4e8b\u5f8c\u51e0\u4e2a\u5e08\u53d4\u57cb\u6028\u6211\u5e08\u7236\u4e0d\u80af\u51fa\u624b\uff0c\u635f \u4e86\u6b66\u5f53\u58f0\u8a89\u3002\u6211\u5e08\u7236\u9053\uff1a\u5bf9\u597d\u80dc\u7684\u4eba\uff0c\u5e94\u8be5\u8ba9\u4ed6\uff0c\u6211\u4eec\u6b66\u5f53\u6d3e\u6811\u5927\u62db\u98ce\uff0c\u4f55\u5fc5\u8981\u4e3a\u4e89\u53e3\u6c14\u800c \u62db\u60f9  \u70e6\u3002\u800c\u4e14\uff0c\u6211\u6562\u65ad\u5b9a\u4ed6\u867d\u5b34\u4e86\u56db\u5e08\u5f1f\u4e00\u62db\uff0c\u5bf9\u6211\u4eec\u6b66\u5f53\u6d3e\u5374\u53cd\u800c\u5fc3\u60a6\u8bda\u670d\u3002\u56db\u4e2a\u5e08\u53d4 \u90fd\u95ee\u662f\u4f55\u9053\u7406\uff0c\u6211\u5e08\u7236\u7b11\u800c\u4e0d\u7b54\u3002\u5f8c\u6765\u4ed6\u624d\u5bf9\u6211\u8bf4\uff1a\u4f60\u7684\u56db\u4e2a\u5e08\u53d4\u4e5f\u90fd\u662f\u597d\u80dc\u4e4b\u4eba\uff0c\u6240\u4ee5\u6211 \u4e0d\u613f\u5bf9\u4ed6\u4eec\u8bf4\u3002\u4ed6\u8d62\u4f60\u56db\u5e08\u53d4\u90a3\u62db\uff0c\u7528\u7684\u662f\u964d\u9f99\u624b\uff0c\u8fd9\u662f\u4ed6\u96f7\u9706\u516b\u5366\u638c\u4e2d\u7684\u7edd\u62db\u3002\u4ed6\u5b34\u4e86\u4e4b \u5f8c\uff0c\u5f97\u610f\u6d0b\u6d0b\uff0c\u548c\u6211\u8c08\u8bba\u4ed6\u8fd9\u624b\u7edd\u62db\uff0c\u81ea\u4ee5\u4e3a\u5929\u4e0b\u65e0\u4eba\u80fd\u7834\u3002\u6211\u4e0d\u4f5c\u58f0\uff0c\u9001\u4ed6\u51fa\u95e8\u65f6\uff0c\u6545\u610f \u8e0f\u516b\u5366\u65b9\u4f4d\uff0c\u4ece\u5f02\u4f4d\u76f4\u8d70\u4e7e\u4f4d\u518d\u8f6c\u79bb\u65b9\uff0c\u53cc\u624b\u62b1\u62f3\u4e00\u63d6\uff0c\u624b\u5fc3\u7565\u5411\u4e0b\u659c\uff0c\u5de6\u53f3\u4e00\u5206\uff0c\u660e\u662f\u9001 \u5ba2\u51fa\u95e8\uff0c\u5b9e\u662f\u6f14\u7834\u964d\u9f99\u624b\u7684\u62db\u5f0f\uff0c\u4ed6\u662f\u4e2a\u884c\u5bb6\uff0c\u81ea\u7136\u77e5\u9053\u3002\u6240\u4ee5\u51fa\u95e8\u4e4b\u5f8c\uff0c\u8fd8\u56de\u5934\u62f1\u624b\uff0c\u53eb \u6211\u5305\u6db5\u3002\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u4f60\u5e08\u7236\u7684\u5ea6\u91cf\u771f\u597d\u3002\u201d\u7389\u7f57\u5239\u51b7\u7b11\u9053\uff1a\u201c\u5bf9\u8fd9\u6837\u7684\u574f\u4eba\uff0c\u6211\u53ef\u4e0d\u80af \u7559\u60c5\u3002\u201d \u3000\u3000\u738b\u7167\u5e0c\u4e0d\u6562\u4f5c\u58f0\uff0c\u5fc3\u91cc\u6697\u6697\u53eb\u82e6\u3002\u539f\u6765\u8fd9\u94c1\u98de\u9f99\u819d\u4e0b\u65e0\u513f\uff0c  \u6709\u4e00\u5973\uff0c\u540d\u53eb\u94c1\u73ca\u745a\uff0c\u5341 \u5206\u5b9d\u8d1d\u3002\u94c1\u98de\u9f99\u597d\u80dc\u4efb\u6027\uff0c\u4eba\u53c8\u602a\u50fb\uff0c\u548c\u6b66\u6797\u670b\u53cb\uff0c\u7d20\u5c11\u6765\u5f80\uff0c\u4eba\u5bb6\u4e5f\u4e0d\u6562\u60f9\u4ed6\u3002\u6240\u4ee5\u94c1\u73ca \u745a\u867d\u957f\u5f97\u751a\u4e3a\u7f8e\u4e3d\uff0c\u5374\u5341\u516b\u5c81\u4e86\u8fdc\u6ca1\u5a46\u5bb6\u3002\u94c1\u98de\u9f99\u5e26\u5979\u5728\u6c5f\u6e56\u95ef\u6c64\uff0c\u4e5f\u627e\u4e0d\u5230\u5408\u9002\u4e4b\u4eba\u3002\u738b \u7167\u5e0c\u8f85\u52a9\u7236\u4eb2\uff0c\u5728  \u5317\u7eff\u6797\u9053\u4e2d\uff0c\u751a\u6709\u58f0\u540d\u3002\u94c1\u98de\u9f99\u548c\u738b\u7167\u5e0c\u7684\u7236\u4eb2\u738b\u5609\u80e4\u672c\u5c5e\u76f8\u8bc6\uff0c\u542c\u5f97 \u738b\u7167\u5e0c\u7684\u58f0\u540d\uff0c\u6697\u7b11\u81ea\u5df1\u73b0\u949f\u4e0d\u6253\u5374\u53bb  \u94dc\uff0c\u5c31\u5e26\u4e86\u5973\u513f\uff0c\u5230\u5ef6\u5b89\u6765\u627e\u738b\u5609\u80e4\uff0c\u738b\u5609\u80e4\u5bf9\u8fd9 \u6837\u7684\u98ce\u5c18\u5f02\u571f\uff0c\u5f53\u7136\u6bb7\u52e4\u6b3e\u5f85\u3002\u7236\u5973\u4fe9\u89c1\u4e86\u738b\u7167\u5e0c\u90fd\u89c9\u5f97\u5341\u5206\u5408\u610f\u3002\u5e2d\u6563\u4e4b\u5f8c\uff0c\u94c1\u98de\u9f99\u8ff3\u76f4 \u7684\u5c31\u63d0\u51fa\u4e86\u5a5a\u4e8b\u6765\uff0c\u738b\u5609\u80e4\u5341\u5206\u4e0d\u597d\u610f\u601d\uff0c\u59d4\u5a49\u5bf9\u4ed6\u8bf4\u660e\uff0c\u81ea\u5df1\u7684\u513f\u5b50\u548c\u5317\u4eac\u6b66\u5e08\u5b5f\u707f\u7684\u5973 \u513f\uff0c\u81ea\u5e7c\u6307\u8179\u4e3a\u5a92\uff0c\u8bf7\u4ed6\u53e6\u9009\u8d24\u5a7f\u3002\u90a3\u77e5\u94c1\u98de\u9f99\u751a\u662f\u4e0d\u901a\u4eba\u60c5\uff0c\u7adf\u7136\u62cd\u6848\u8bf4\u9053\uff1a\u201c\u6789\u4f60\u662f\u7eff \u6797\u9053\u7684\u5934\u513f\uff0c\u600e\u9ebd\u548c\u671d\u5ef7\u7684\u9e70\u72ac\u7ed3\u4e3a\u4eb2\u5bb6\u3002\u6211\u7684\u5973\u513f\u6709\u90a3\u70b9\u4e0d\u597d\uff1f\u5feb\u628a\u90a3\u5934\u4eb2\u4e8b\u9000\u4e86\u3002\u201d\u738b \u5609\u80e4\u77e5\u4ed6\u4e0d\u53ef\u7406\u903e\uff0c\u800c\u4e14\u6b63\u5f53\u56fe\u8c0b\u5927\u4e8b\uff0c\u53c8\u4e0d\u613f\u5f97\u7f6a\u8fd9\u6837\u7684\u4eba\u3002  \u597d\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u8981\u9000\uff0c\u4e5f \u5f97\u548c\u5b5f\u6b66\u5e08\u8bf4\u4e2a\u6e05\u695a\uff0c\u8def\u9014\u9065\u8fdc\uff0c\u4e0d\u662f\u4e00\u671d\u4e00\u5915\u6240\u80fd\u529e\u5230\u3002\u201d\u94c1\u98de\u9f99\u60bb\u60bb\u7136\u5e26\u5973\u513f\u8d70\u5f00\u3002\u4e8b \u60c5\u8fc7\u5f8c\uff0c\u738b\u5609\u80e4\u95f4\u513f\u5b50\u5fc3\u610f\uff0c\u738b\u7167\u5e0c\u5bf9\u94c1\u73ca\u745a\u5e76\u65e0\u597d\u611f\uff0c\u4e0d\u613f\u9000\u4eb2\u53e6\u8ba2\uff0c\u4f46\u4e5f\u4e0d\u613f\u5f97\u7f6a\u94c1\u8001 \u5934\u5b50\u3002\u6240\u4ee5\u7236\u5b50\u5546\u8bae\uff0c\u9042\u7531\u738b\u7167\u5e0c\u6025\u6025\u4e0a\u4eac\u8fce\u4eb2\u3002\u60f3\u4e0d\u5230\u5230\u4e86\u4eac\u5e08\uff0c\u53c8\u53d1\u751f\u4e86\u76c2\u6b66\u5e08\u4f24\u6b7b\uff0c \u548c\u8bef\u4f1a\u767d\u654f\u4e4b\u4e8b\u3002 \u3000\u3000\u738b\u7167\u5e0c\u5fc3\u60f3\uff1a\u7389\u7f57\u5239\u6b63\u4e0e\u6211\u5bb6\u8ba2\u76df\uff0c\u82e5\u7136\u8dd1\u53bb\u548c\u90a3\u94c1\u8001\u602a\u5927\u52a8\u5e72\u6208\uff0c\u8fd9\u7b14\u5e10\u5c82\u4e0d\u4e00\u53d1\u7b97 \u5728\u6211\u5bb6\u5934\u4e0a\uff1f \u3000\u3000\u738b\u7167\u5e0c\u53c8\u60f3\uff1a\u7b97\u5728\u6211\u5bb6\u5e10\u4e0a\u4e5f\u4e0d\u6253\u7d27\uff0c\u4f46\u76ee\u524d\u6b63\u8981\u805a\u96c6\u5404\u8def\u82f1\u96c4\uff0c\u5408\u529b\u540c\u5fc3\uff0c\u5171\u56fe\u4e49 \u4e3e\uff0c\u4f55\u5fc5\u4e3a\u8fd9\u4e9b\u5c0f\u4e8b\u5f97\u7f6a\u4e00\u4f4d\u6b66\u6797\u602a\u5ba2\uff0c\u51b5\u4e14\u94c1\u8001\u5934\u5b50\u4e5f\u7edd\u4e0d\u4f1a\u662f\u52ab\u4e66\u5bb3\u547d\u4e4b\u4eba\u3002\u4ed6\u5bf9\u7389\u7f57 \u5239\u7684\u611f\u60c5\u7528\u4e8b\uff0c\u9887\u4e3a\u4e0d\u6ee1\uff0c\u4f46\u7389\u7f57\u5239\u8981\u6bd4\u94c1\u8001\u5934\u5b50\u66f4\u96be\u5bf9\u4ed8\u3002\u738b\u7167\u5e0c  \u597d\u9ed8\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u5fd9\u4e86\u4e00\u591c\uff0c\u6253\u4e86\u534a\u5929\uff0c\u8fd9\u65f6\u5df2\u5c06\u8fd1\u6b63\u5348\u65f6\u5206\uff0c\u7389\u7f57\u5239\u7b49\u4eba\u90fd\u662f\u53c8\u9965\u53c8\u6e34\uff0c\u9633\u5149\u7167\u8fdb\u77f3 \u7a9f\uff0c\u8840\u8165\u5473\u751a\u662f\u96be\u95fb\u3002\u7389\u7f57\u5239\u6495\u4e0b\u534a\u622a\u8863\u8896\uff0c\u8d70\u8fdb\u7a9f\u4e2d\uff0c\u66ff\u8d1e\u4e7e\u9053\u4eba\u6162\u6162\u63e9\u4e7e\u8840\u8ff9\uff0c\u8840\u8ff9\u6de4 \u9ed1\uff0c\u4f3c\u662f\u4e2d\u6bd2\u3002\u7389\u7f57\u5239\u60f3\u9053\uff1a\u94c1\u98de\u9f99\u7684\u6b66\u529f\u5728\u8d1e\u4e7e\u4e4b\u4e0a\uff0c\u8981\u62a2\u5251\u8c31\uff0c\u4f3c\u4e4e\u4e0d\u5fc5\u653e\u6bd2\uff0c\u7ec6\u4e00\u5bdf \u770b\uff0c\u89c1\u4ed6\u989a\u9aa8\u788e\u88c2\uff0c\u5206\u660e\u662f\u53d7\u638c\u529b\u6240\u4f24\uff0c\u518d\u7814\u7a76\u53d7\u4f24\u4e4b\u5904\uff0c\u9aa8\u5934\u5fae\u73b0\u6307\u5370\uff0c\u53c8\u5206\u660e\u662f\u4e00\u638c\u6253 \u4e0b\u4e4b\u5f8c\uff0c\u518d\u4e94\u6307\u5408\u62e2\uff0c\u7528\u5185\u5bb6\u624b\u6cd5\uff0c\u4f24\u635f\u4ed6\u7684\u5589\u5499\u3002\u8fd9\u624b\u6cd5\u53ef\u6b63\u662f\u94c1\u98de\u9f99\u7684\u624b\u6cd5\uff01\u5fc3\u4e2d\u5927\u60d1 \u4e0d\u89e3\uff01 \u3000\u3000\u8d1e\u4e7e\u9053\u4eba\u548c\u5353\u4e00\u822a\u3001\u7389\u7f57\u5239\u7684\u5e08\u7236\u90fd\u662f\u77e5\u4ea4\uff0c\u4e24\u4eba\u6325\u6cea\u6398\u7a74\uff0c\u5c06\u4ed6\u57cb\u846c\u3002\u5f04\u597d\u4e4b\u5f8c\uff0c\u7389 \u7f57\u5239\u64ae\u571f\u4e3a\u9999\uff0c\u5411\u5929\u62dc\u544a\uff0c\u8a93\u4e3a\u8d1e\u4e7e\u9053\u4eba\u62a5\u4ec7\u3002 \u3000\u3000\u53c1\u4eba\u6d17\u4e7e\u8840\u624b\uff0c\u638f\u6cc9\u6c34\uff0c\u9001\u4e7e\u7cae\uff0c\u4e0b\u5f97\u5c71\u6765\uff0c\u5df2\u6709\u738b\u7167\u5e0c\u7684\u55bd\u5175\u6765\u63a5\u3002\u767d\u654f\u4e5f\u5df2\u88ab\u6551\u4e86 \u51fa\u6765\uff0c\u89c1\u4e86\u7389\u7f57\u5239\u5927\u559c\u62dc\u8c22\u3002\u5353\u4e00\u822a\u6101\u7709\u6df1\u9501\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u5353\u5144\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u4ee4\u7956\u7684\u7075\u886c\uff0c \u6211\u5df2\u4ee4\u4eba\u642c\u5230\u4e86\u74e6\u7a91\u5821\uff0c\u5f85\u5353\u5144\u5230\u8fbe\uff0c\u5c31\u53ef\u5b89\u6392\u3002\u5353\u5144\u7684\u5bb6\u4eba\uff0c\u4e5f\u5df2\u7531\u6211\u4f5c\u4e3b\uff0c\u66ff\u5353\u5144\u5206\u6d3e \u94f6\u4e24\uff0c\u5c06\u4ed6\u4eec\u9063\u6563\u4e86\u3002\u201d\u5353\u4e00\u822a\u9ed8\u7136\u4e0d\u8bed\uff0c\u5fc3\u60f3\u4e8b\u5df2\u81f3\u6b64\uff0c\u81ea\u5df1\u56de\u5230\u5bb6\u5fc5\u88ab\u7f09\u6355\uff0c\u4e5f\u53ea\u597d\u7531 \u5979\u5982\u6b64\u529e\u7406\u4e86\u3002 \u3000\u3000\u5353\u4e00\u822a\u672c\u4e0d\u613f\u968f\u738b\u7167\u5e0c\u5230\u74e6\u7a91\u5821\uff0c\u4f46\u7956\u7236\u7684\u9057\u4f53\u5f85\u4ed6\u4eba\u571f\uff0c\u53ea\u597d\u8ddf\u53bb\u3002\u74e6\u7a91\u5821\u79bb\u5ef6\u5b89\u57ce \u4e00\u767e\u4e94\u5341\u9980\u91cc\uff0c\u4ed6\u4eec\u7387\u9886\u9a6c\u961f\u5148\u884c\uff0c\u5348\u591c\u4fbf\u5df2\u8d76\u5230\u3002\u738b\u5609\u80e4\u4eb2\u6765\u8fce\u63a5\uff0c\u89c1\u4e86\u7389\u7f57\u5239\u975e\u5e38\u559c \u6b22\uff0c\u4e92\u9053\u4ef0\u6155\u4e4b\u610f\u3002\u738b\u7167\u5e0c\u5c06\u5353\u4e00\u822a\u8eab\u4efd\u544a\u77e5\uff0c\u738b\u5609\u80e4\u53c8\u662f\u4e00\u559c\uff0c\u7b11\u9053\uff1a\u201c\u5353\u5144\u6587\u6b66\u53cc\u4fee\uff0c \u8fd9\u597d\u6781\u4e86\u3002\u6211\u4eec\u8fd9\u4e9b\u4e4c\u5408\u4e4b\u4f17\uff0c\u6b63\u7f3a\u5c11\u8fd0\u7b79\u5e37\u5e44\u3001\u7b56\u5212\u5b9a\u8ba1\u7684\u4eba\u624d\u3002\u201d\u5353\u4e00\u822a\u62f1\u4e86\u62f1\u624b\uff0c\u51b7 \u51b7\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u7f13\u63d0\u3002\u201d\u738b\u5609\u80e4\u6115\u4e86\u4e00\u6115\uff0c\u738b\u7167\u5e0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5353\u5144\u6b63\u5728\u91cd\u5b5d\u4e4b\u4e2d\u3002\u201d\u738b\u5609 \u80e4\u8fde\u5fd9\u8d54\u7f6a\u3002\u53eb\u4eba\u53d6\u8fc7\u5b5d\u670d\uff0c\u7ed9\u5353\u4e00\u822a\u6362\u4e86\u3002 \u3000\u3000\u5353\u4e00\u822a\u53bb\u610f\u5306\u5306\uff0c\u7b2c\u4e8c\u65e5\u5c31\u5c06\u7956\u7236\u5b89\u846c\uff0c\u62dc\u6258\u738b\u7167\u5e0c\u7167\u987e\u575f\u5893\u3002\u7389\u7f57\u5239\u767d\u5929\u4e0e\u5404\u5bb6\u5be8\u4e3b \u4f1a\u9762\uff0c\u5fd9\u4e86\u4e00\u65e5\uff0c\u4f46\u9ec4\u660f\u65f6\u5206\uff0c\u4ecd\u7136\u62bd\u7a7a\u5230\u5353\u4ef2\u5ec9\u65b0\u575f\u81f4\u796d\u3002\u5979\u867d\u7136\u711a\u9999\u70b9\u70db\uff0c\u966a\u5353\u4e00\u822a\u53e9 \u5934\uff0c\u4f46\u5fc3\u4e2d\u5374\u5728\u6697\u7b11\uff0c\u60f3\u4e0d\u5230\u4ee5\u524d\u88ab\u81ea\u5df1\u6240\u52ab\u7684\u5927\u5b98\uff0c\u73b0\u5728\u81ea\u5df1\u5374\u5411\u4ed6\u53e9\u5934\u3002\u5353\u4e00\u822a\u770b\u5979\u9762 \u4e0a\u5e76\u65e0\u60b2\u621a\u4e4b\u5bb9\uff0c\u5fc3\u4e2d\u9887\u4e3a\u4e0d\u6ee1\uff0c\uff0e\u602a\u5979\u60fa\u5fea\u4f5c\u6001\u3002\u5176\u5b9e\u4ed6\u5374\u4e0d\u77e5\u7389\u7f57\u5239\u5fc3\u610f\uff0c\u5982\u679c\u7389\u7f57\u5239 \u4e0d\u662f\u4e3a\u4ed6\uff0c\u5c31\u662f\u628a\u5251\u67b6\u5728\u5979\u7684\u9888\u4e0a\uff0c\u5979\u4e5f\u4e0d\u4f1a\u5230\u6765\u8dea\u62dc\u3002 \u3000\u3000\u665a\u971e\u6e10\u6536\uff0c\u65b0\u6708\u521d\u4e0a\uff0c\u5353\u4e00\u822a\u548c\u7389\u7f57\u5239\u5e76\u80a9\u7f13\u6b65\uff0c\u4ece\u5893\u5730\u6162\u6162\u8d70\u56de\u3002\u7389\u7f57\u5239\u9760\u7740\u5353\u4e00 \u822a\uff0c\u773c\u6ce2\u6d41\u8f6c\uff0c\u5ffd\u7136\u4f4e\u63a0\u4e91\u9b13\uff0c\u6b32\u8a00\u53c8\u6b62\u3002\u5353\u4e00\u822a\u89c9\u5979\u5439\u6c14\u5982\u5170\uff0c\u5fc3\u9b42\u4e00\u6c64\uff0c\u6025\u5fd9\u907f\u5f00\u3002\u7389 \u7f57\u5239\u7b11\u9053\uff1a\u201c\u4f60\u73b0\u5728\u8fd8\u6015\u6211\u5417\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u4f60\u4e3a\u4ec0\u9ebd\u8981\u4ee4\u522b\u4eba\u6015\u4f60\uff1f\u201d\u7389\u7f57\u5239 \u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u6211\u662f\u6bcd\u72fc\u6240\u4e73\u5927\u7684\u9ebd\uff1f\u6211\u5e76\u6ca1\u6709\u7acb\u5fc3\u53eb\u4eba\u6015\u6211\uff0c\u5927\u7ea6\u662f\u6211\u91ce\u6027\u672a\u9664\uff0c\u6240\u4ee5\u522b\u4eba \u5c31\u6015\u6211\u4e86\u3002\u201d\u5353\u4e00\u822a\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u5fc3\u60f3\u7389\u7f57\u5239\u79c0\u5916\u6167\u4e2d\uff0c\u6709\u5982\u5929\u751f\u7f8e\u7389\uff0c\u53ef\u60dc\u6ca1\u4eba\u5e26\u5979\u8d70 \u5165\u201c\u6b63\u9014\u201d\u3002\u7389\u7f57\u5239\u95ee\u9053\uff1a\u201c\u597d\u7aef\u7aef\u7684\u4f60\u4e3a\u4ec0\u9ebd\u53f9\u6c14\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4ee5\u4f60\u7684\u7edd\u4e16\u6b66\u529f\uff0c\u4f55 \u5fc5\u5728\u7eff\u6797\u4e2d  \u6df7\uff1f\u201d\u7389\u7f57\u5239\u9762\u8272\u4e00\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u7eff\u6797\u6709\u4ec0\u9ebd\u4e0d\u597d\uff0c\u603b\u6bd4\u5b98\u573a\u4e7e\u51c0\u5f97\u591a\uff01\u201d\u5353 \u4e00\u822a\u4f4e\u5934\u4e0d\u8bed\uff0c\u7389\u7f57\u5239\u53c8\u9053\uff1a\u201c\u4f60\u4eca\u5f8c\u6253\u7b97\u600e\u6837\uff1f\u96be\u9053\u8fd8\u60f3\u5f53\u5b98\u4f5c  \uff0c\u50cf\u4f60\u7956\u7236\u3001\u7236\u4eb2\u4e00 \u6837\uff0c\u66ff\u7687\u5e1d\u8001\u513f\u5356\u547d\u5417\uff1f\u201d\u5353\u4e00\u822a\u51b3\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4eca\u751f\u7edd\u4e0d\u4f5c\u5b98\uff0c\u4f46\u4e5f\u4e0d\u4f5c\u5f3a\u76d7\uff01\u201d\u7389\u7f57\u5239 \u5fc3\u4e2d\u6c14\u6781\uff0c\u82e5\u8bf4\u8fd9\u8bdd\u7684\u4eba\u4e0d\u662f\u5353\u4e00\u822a\uff0c\u5979\u65e9\u5df2\u4e00\u638c\u626b\u53bb\u3002\u5353\u4e00\u822a\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u6211\u662f\u6b66\u5f53\u95e8 \u5f92\uff0c\u6211\u4eec\u7684\u95e8\u89c4\u662f\u4e00\u4e0d\u8bb8\u4f5c\u5f3a\u76d7\uff0c\u4e8c\u4e0d\u8bb8\u4f5c\u9556\u5e08\uff0c\u4f60\u96be\u9053\u8fd8\u4e0d\u77e5\u9053\uff1f\u201d\u7389\u7f57\u5239\u51b7\u7b11\u9053\uff1a\u201c\u4f60 \u7684\u7956\u7236\u3001\u7236\u4eb2\u96be\u9053\u4e0d\u662f\u5f3a\u76d7\uff1f\u201d\u5353\u4e00\u822a\u6012\u9053\uff1a\u201c\u4ed6\u4eec\u600e\u9ebd\u4f1a\u662f\u5f3a\u76d7\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u5f53\u5b98\u7684 \u662f\u52ab\u8d2b\u6d4e\u5bcc\uff0c\u6211\u4eec\u662f\u52ab\u5bcc\u6d4e\u8d2b\uff0c\u90fd\u662f\u5f3a\u76d7\uff01\u4f46\u6211\u4eec\u8fd9\u79cd\u5f3a\u76d7\uff0c\u6bd4\u4f60\u4eec\u90a3\u79cd\u5f3a\u76d7\u597d\u5f97\u591a\uff01\u201d\u5353 \u4e00\u822a\u9053\uff1a\u201c\u597d\uff0c\u968f\u4f60\u8bf4\u53bb\uff01\u4f46\u4eba\u5404\u6709\u5fd7\uff0c\u4ea6\u4e0d\u5fc5\u76f8\u5f3a\uff01\u201d\u7389\u7f57\u5239\u8eab\u8eaf\u5fae\u98a4\uff0c\u4f24\u5fc3\u5df2\u6781\u3002\u5353\u4e00 \u822a\u770b\u5979\u773c\u5708\u5fae\u7ea2\uff0c\u6cea\u73e0\u6b32\u6ef4\uff0c\u601c\u60dc\u4e4b\u5fc3\uff0c\u6cb9\u7136\u800c\u751f\uff0c\u4e0d\u89c9\u8f7b\u8f7b\u63e1\u5979\u624b\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5fd7\u5411 \u867d\u6216\u4e0d\u540c\uff0c\u4f46\u4ea4\u60c5\u6c38\u8fdc\u90fd\u5728\u3002\u201d\u7389\u7f57\u5239\u51c4\u7136\u95ee\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u8d70\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u660e\u5929\uff01\u201d\u7389 \u7f57\u5239\u53f9\u4e86\u53e3\u6c14\uff0c\u518d\u4e0d\u8bf4\u8bdd\u3002\u8fc7\u4e86\u597d\u4e45\uff0c\u5353\u4e00\u822a\u624d\u5f52\u8f6c\u8bdd\u9898\uff0c\u53eb\u7389\u7f57\u5239\u8c08\u6c5f\u6e56\u7684\u5947\u95fb\u8f76\u4e8b\uff0c\u800c \u4ed6\u4e5f\u8c08\u4eac\u534e\u98ce\u7269\uff0c\u4e24\u4eba\u50cf\u8001\u670b\u53cb\u4e00\u6837\uff0c\u5728\u6708\u4eae\u4e0b\u6f2b\u6b65\u95f2\u8c08\uff0c\u867d\u7136\u5927\u5bb6\u90fd\u4e0d\u6562\u63ed\u9732\u5fc3\u7075\u6df1\u5904\uff0c \u4f46\u76f8\u4e92\u4e4b\u95f4\uff0c\u4e5f\u6bd4\u4ee5\u524d\u4e86\u89e3\u8bb8\u591a\u3002\u8fd9\u4e00\u665a\u4ed6\u4eec\u76f4\u8c08\u5230\u6df1\u591c\u624d\u6563\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u5353\u4e00\u822a\u5411\u738b\u7167\u5e0c\u8f9e\u884c\uff0c\u738b\u7167\u5e0c\u77e5\u4ed6\u53bb\u5fd7\u751a\u575a\uff0c\u4e5f\u4e0d\u62e6\u963b\uff0c\u5f53\u4e0b\u5404\u9053\u73cd\u91cd\uff0c \u6325\u6cea\u800c\u522b\u3002 \u3000\u3000\u5353\u4e00\u822a\u906d\u9022\u5927\u53d8\uff0c\u6ee1\u6000\u51c4\u6006\u3002\u4f46\u5bb6\u56fd\u4e4b\u4e8b\uff0c\u53c8\u4e0d\u80fd\u4e0d\u7406\u3002\u4ed6\u60f3\u4e86\u597d\u4e45\uff0c\u51b3\u610f\u5192\u9669\u4e0a\u4eac\uff0c \u5c06\u5185\u5978\u52fe\u7ed3\u6ee1\u6d32\u4e4b\u4e8b\uff0c\u544a\u8bc9\u592a\u5b50\uff0c\u987a\u4fbf\u4e5f\u66ff\u81ea\u5df2\u4f38\u51a4\u3002\u4ed6\u6b64\u53bb\u4eac\u5e08\u662f\u53d6\u9053\u5c71\u897f\uff0c\u8f6c\u5165\u6cb3\u5317\u3002 \u884c\u4e86\u4e03\u516b\u5929\uff0c\u5df2\u8fdb\u4eba\u5c71\u897f\uff0c\u8fd9\u65e5\u5230\u4e86\u9f99\u95e8\u53bf\uff0c\u4e00\u8def\u884c\u6765\uff0c\u53ea\u89c1\u9ec4\u6c34\u6ed4\u6ed4\uff0c\u4e24\u8fb9\u77f3\u58c1\u5ced\u7acb\uff0c\u5f62 \u52bf\u9669\u5cfb\u3002\u5353\u4e00\u822a\u5ffd\u7136\u60f3\u8d77\u94c1\u98de\u9f99\u7236\u5973\u5c31\u5728\u6b64\u5730\u3002\u5fc3\u4e2d\u4e0d\u89c9\u4e00\u52a8\uff0c\u6e38\u76ee\u56db\u987e\uff0c\u8def\u4e0a\u4e0d\u89c1\u884c\u4eba\u3002 \u53ea\u5728\u6cb3\u4e2d\u8fdc\u5904\uff0c\u6709\u51e0\u652f\u5e06\u5f71\u3002\u5353\u4e00\u822a\u8e3d\u8e3d\u72ec\u884c\uff0c\u9887\u611f\u5bc2\u5bde\uff0c\u884c\u4e86\u4e00\u4f1a\uff0c\u8f6c\u8fc7\u4e00\u4e2a\u5c71\u5773\uff0c\u5ffd\u89c1 \u524d\u9762\u6709\u4e00\u6751\u5e84\u3002 \u3000\u3000\u5353\u4e00\u822a\u5fc3\u9053\uff1a\u83ab\u975e\u8fd9\u5c31\u662f\u201c\u94c1\u5bb6\u5e84\u201d\u3002\u6b63\u5728\u5600\u5495\uff0c\u5ffd\u95fb\u5f97\u6709\u563b\u563b\u51b7\u7b11\u4e4b\u58f0\uff0c\u4ece\u8eab\u5f8c\u4f20 \u6765\uff0c\u56de\u5934\u4e00\u671b\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u539f\u6765\u5374\u662f\u4e91\u71d5\u5e73\u548c\u91d1\u5343  \u4e8c\u4eba\u3002\u4e91\u71d5\u5e73\u51b7\u7b11\u9053\uff1a\u201c\u5582\uff0c\u4f60\u7684\u4fdd\u9556 \u7389\u7f57\u5239\u5462\uff1f\u4f60\u8fd9\u5c0f\u5b50\u82e5\u8ddf\u5b9a\u4e86\u5979\uff0c\u6211\u4eec\u5948\u4f55\u4e0d\u4e86\u4f60\u3002\u539f\u6765\u4f60\u4e5f\u6709\u5355\u9a91\u72ec\u884c\u7684\u65f6\u5019\u3002\u201d\u5353\u4e00\u822a \u62d4\u5251\u51fa\u9798\uff0c\u6012\u9053\uff1a\u201c\u6211\u5355\u4eba\u4e5f\u4e0d\u6015\u4f60\u3002\u201d\u91d1\u5343  \u7b11\u9053\uff1a\u201c\u597d\u4e2a\u82f1\u96c4\uff0c\u4f60\u6709\u591a\u5c11\u65a4\u4e24\uff0c\u96be\u9053\u6211 \u4eec\u4e0d\u77e5\uff1f\u522b\u518d\u5439\u5927\u6c14\u5566\uff01\u201d\u8fb9\u8bf4\u8fb9\u7b11\uff0c\u7a81\u7136\u547c\u7684\u4e00\u638c\u5288\u6765\u201c\u5353\u4e00\u822a\u626d\u8170\u4e00\u95ea\uff0c\u8fd8\u4e86\u4e00\u5251\uff0c\u91d1 \u5343  \u8eab\u5f62\u4e00\u8d77\uff0c\u5de6\u62f3\u53f3\u638c\uff0c  \u80f8\u5207\u8155\uff0c\u4e00\u62db\u4e24\u5f0f\uff0c\u540c\u65f6\u53d1\u51fa\uff0c\u5353\u4e00\u822a\u970d\u5730\u4e00\u4e2a\u8f6c\u8eab\uff0c\u5b9d\u5251\u4e00 \u5c01\uff0c\u4ece\u4fa7\u7ffc\u8fdb\u88ad\uff0c\u91d1\u5343  \u54c8\u54c8\u5927\u7b11\uff0c\u53f3\u624b\u4e8c\u6307\u7a81\u7136\u4e00\u70b9\u5251\u8eab\uff0c\u5c06\u5353\u4e00\u822a\u5b9d\u5251\u6c64\u5f00\uff0c\u5de6\u62f3\u4e00 \u626b\uff0c\u53c8\u62a2\u8fdb\u6765\u3002\u5353\u4e00\u822a\u6025\u5fd9\u4f7f\u4e2a\u201c\u5012\u8e29\u4e03\u661f\u6b65\u201d\uff0c\u5251\u968f\u8eab\u8f6c\uff0c\u5bd2\u5149\u95ea\u5904\uff0c\u4e00\u62db\u201c\u5012  \u91d1 \u94b1\u201d\uff0c\u622a\u638c\u523a\u8155\u3002\u8fd9\u4e00\u62db\u6765\u5f97\u751a\u6025\uff0c\u91d1\u5343  \u4e0d\u6562\u51fa\u6307\u76f8\u62b5\uff0c\u4e00\u4e2a\u201c\u56de\u8eab\u62d7\u6b65\u201d\uff0c\u53cc\u81c2\u7b95\u5f20\uff0c \u7ea2\u4f3c\u6731\u7802\u7684\u638c\u5fc3\uff0c\u84e6\u5730\u5411\u5353\u4e00\u822a\u6402\u5934\u7f69\u4e0b\u3002\u5353\u4e00\u822a\u77e5\u4ed6\u7ec3\u7684\u662f\u6bd2\u7802\u638c\uff0c\u90a3\u6562\u7ed9\u4ed6\u78b0\u7740\uff0c\u4e00\u9886 \u5251\u950b\uff0c\u5237\u7684\u4ece\u654c\u4eba\u638c\u98ce\u4e4b\u4e0b\u63a0\u51fa\uff0c\u6025\u5c55\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u5251\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u53eb\u654c\u4eba\u4e0d\u6562\u8feb\u8fd1\u3002 \u3000\u3000\u91d1\u5343\u5d4c\u638c\u529b\u96c4\u52b2\uff0c\u8eab\u6cd5\u867d\u4e0d\u53ca\u5353\u4e00\u822a\u8f7b\u7075\uff0c\u529f\u529b\u53ef\u8981\u6bd4\u4ed6\u9ad8\u5f97\u591a\u3002\u800c\u4e14\u9634\u98ce\u6bd2\u7802\u638c\u53c8\u9669 \u72e0\u9634\u6bd2\uff0c\u82e5\u975e\u5353\u4e00\u822a\u7ec3\u8fc7\u5185\u529f\uff0c\u7ed9\u4ed6\u638c\u98ce\u626b\u7740\uff0c\u4e5f\u5df2\u96be\u5f53\u3002\u4e24\u4eba\u6597\u4e86\u4e94\u4e03\u5341\u62db\uff0c\u5353\u4e00\u822a\u60ed\u843d \u4e0b\u98ce\uff0c\u800c\u4e91\u71d5\u5e73\u53c8\u864e\u89c4\u7708\u7708\uff0c\u62c8\u7740\u8170\u5e26\u5728\u65c1\u89c2\u6218\u3002 \u3000\u3000\u5353\u4e00\u822a\u60c5\u77e5\u4e0d\u662f\u4ed6\u4eec\u5bf9\u624b\uff0c\u8fb9\u6253\u8fb9\u60f3\u8131\u8eab\u4e4b\u8ba1\uff0c\u6597\u5230\u6025\u5904\uff0c\u84e6\u7136\u865a\u6643\u4e00\u62db.\u5411\u6751\u5e84\u75be \u8dd1\uff0c\u4e91\u71d5\u5e73\u8f7b\u529f\u751a\u9ad8\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u5f80\u90a3\u91cc\u9003\uff1f\u201d\u8db3\u5c16\u70b9\u5730\uff0c\u53c1\u8d77\u53c1\u4f0f\uff0c\u5df2\u8ffd\u5230\u5353\u4e00\u822a\u8eab \u5f8c\uff0c\u8170\u5e26\u4e00\u6325\uff0c\u5c31\u5f80\u5353\u4e00\u822a\u8eab\u4e0a\u7f20\u6765\u3002\u5353\u4e00\u822a\u95ea\u4e86\u4e24\u95ea\uff0c\u8fd9\u65f6\u5df2\u8fdb\u4e86\u5e84\u5185\uff0c\u4e91\u71d5\u5e73\u7684\u8170\u5e26\u50cf \u87d2\u86c7\u4e00\u6837\uff0c\u4e0d\u79bb\u5353\u4e00\u822a\u80cc\u5fc3\u53c1\u5bf8\u4e4b\u5730\uff0c\u6b63\u5728\u5371\u6025\uff0c\u9053\u65c1\u7684\u82b1\u6811\u4e1b\u4e2d\uff0c\u5ffd\u7136\u4f20\u51fa\u5973\u5b50\u5403\u5403\u7684\u7b11 \u58f0\uff0c\u4e00\u628a\u957f\u526a\u84e6\u7136\u4f38\u4e86\u51fa\u6765\uff0c  \u4e00\u5939\u5c31\u628a\u4e91\u71d5\u5e73\u7684\u8170\u5e26\u5939\u65ad\u3002 \u3000\u3000\u82b1\u6811\u4e1b\u4e2d\u4e24\u4e2a\u5973\u5b50\u5148\u5f8c\u8d70\u51fa\uff0c\u8d70\u5728\u524d\u9762\u7684\u5c31\u662f\u90a3\u5df2\u7ed9\u7389\u7f57\u5239\u7528\u6697\u5668\u6253\u4f24\u7684\u4e2d\u5e74\u7f8e\u5987\uff0c\u8ddf \u5728\u5f8c\u9762\u7684\u5219\u662f\u94c1\u98de\u9f99\u7684\u5973\u513f\u94c1\u73ca\u745a\u3002\u4e91\u71d5\u5e73\u6025\u5fd9\u62b1\u62f3\u4f5c\u793c\uff0c\u53eb\u9053\uff1a\u201c\u4e5d\u5a18\uff0c\u8fd9\u5c0f\u5b50\u4e0d\u662f\u597d \u4eba\u3002\u201d\u53c8\u9053\uff1a\u201c\u73ca\u745a\u5c0f\u59d0\uff0c\u4f60\u597d\u4eba\u505a\u5230\u5e95\uff0c\u90a3\u65e5\u4f60\u65e2\u7ed9\u6211\u4eec\u52a9\u62f3\uff0c\u5c31\u8bf7\u4f60\u66ff\u6211\u4eec\u628a\u4ed6\u64d2\u4e0b\u6765 \u5427\u3002\u201d\u94c1\u73ca\u745a\u9119\u8584\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5e72\u6211\u81ea\u5df1\u7684\u4e8b\uff0c\u8c01\u7ed9\u4f60\u52a9\u62f3\uff1f\u201d\u90a3\u4e2d\u5e74\u5987\u4eba\u5374\u677f\u8d77\u9762\u5b54 \u65a5\u9053\uff1a\u201c\u6211\u4eec\u7684\u8001\u7237\u5b50\u8bf4\u8fc7\u4e0d\u89c1\u4f60\u4eec\uff0c\u4f60\u4eec\u53c8\u95ef\u8fdb\u6765\u4f5c\u751a\uff1f\u201d\u4e91\u71d5\u5e73\u9053\uff1a\u201c\u6211\u4eec\u662f\u8ffd\u8fd9\u4e2a\u5c0f \u5b50\u6765\u7684\uff0c\u4f60\u8001\u4eba\u5bb6\u4e0d\u770b\u89c1\u9ebd\uff1f\u201d\u4e2d\u5e74\u5987\u4eba\u65a5\u9053\uff1a\u201c\u8c01\u83c5\u4f60\u8fd9\u4e9b\u95f2\u4e8b\uff0c\u6211\u4eec\u94c1\u5bb6\u5e84\u5c82\u662f\u53ef\u4ee5\u968f \u4fbf\u95ef\u8fdb\u7684\u3002\u6eda\uff0c\u5feb\u6eda\uff01\u201d\u4e91\u71d5\u5e73\u4e0e\u91d1\u5343  \u9762\u9762\u76f8\u89d1\uff0c\u505a\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u8fd9\u4e2d\u5e74\u5987\u4eba\u540d\u53eb\u7a46\u4e5d\u5a18\uff0c\u4e43\u662f\u94c1\u73ca\u745a\u7684\u5eb6\u6bcd\u3002\u94c1\u98de\u9f99\u4e2d\u5e74\u4e27\u5076\u4e4b\u5f8c\uff0c\u8ba8\u4e86\u4e00\u4e2a\u5356\u89e3\u5973 \u4eba\uff0c\u4e3a\u4e86\u5c0a\u91cd\u524d\u59bb\uff0c\u4e0d\u80af\u7acb\u5979\u505a\u6b63\u5ba4\u3002\u4f46\u867d\u7136\u5982\u6b64\uff0c\u4e5d\u5a18\u4ecd\u662f\u751a\u4e3a\u5f97\u5ba0\u3002\u8fd9\u65f6\u91d1\u5343  \u548c\u4e91\u71d5 \u5e73\u9762\u9762\u76f8\u89d1\uff0c\u8bba\u6b66\u529f\uff0c\u4ed6\u4eec\u867d\u7136\u6bd4\u7a46\u4e5d\u5a18\u8981\u9ad8\u8bb8\u591a\uff0c\u4f46\u6ca1\u9f20\u5fcc\u5668\uff0c\u4ed6\u4eec\u7eb5\u6709\u5929\u5927\u7684\u80c6\u5b50\uff0c\u4e5f \u4e0d\u6562\u548c\u94c1\u98de\u9f99\u7684\u5ba0\u59be\u4f5c\u5bf9\u3002\u7a46\u4e5d\u5a18\u53c8\u559d\u9053\uff1a\u201c\u600e\u9ebd\u656c\u9152\u4e0d\u5403\u4f60\u8981\u5403\u7f5a\u9152\uff0c\u6211\u53eb\u4f60\u4eec\u6eda\u4f60\u4eec\u4e0d \u6eda\uff0c\u96be\u9053\u8981\u60ca\u52a8\u8001\u7237\u5b50\u628a\u4f60\u4eec\u8bf7\u8fdb\u53bb\u5417\uff01\u201d\u4e91\u71d5\u5e73\u5fd9\u9053\uff1a\u201c\u4e5d\u5a18\u4e0d\u8981\u89c1\u602a\uff0c\u6211\u4eec\u9000\u51fa\u5b9d\u5e84\u4fbf \u662f\u3002\u201d\u6068\u6068\u7684\u76ef\u4e86\u5353\u4e00\u822a\u4e00\u773c\uff0c\u548c\u91d1\u5343  \u8dd1\u51fa\u6751\u5e84\u3002 \u3000\u3000\u5353\u4e00\u822a\u4e5f\u60f3\u9000\u51fa\uff0c\u7a46\u4e5d\u5a18\u5ae3\u7136\u4e00\u7b11\uff0c\u62db\u62db\u624b\u9053\uff1a\u201c\u4f60\u8981\u53bb\u90a3\u91cc\uff0c\u4f60\u6765\uff01\u201d\u5353\u4e00\u822a\u62e2\u8896\u4e00 \u63d6\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u53e8\u6270\u5b9d\u5e84\u3002\u201d\u7a46\u4e5d\u5a18\u9053\uff1a\u201c\u4f60\u8fd9\u50bb\u5c0f\u5b50\uff0c\u8fd9\u4e2a\u65f6\u5019\u51fa\u53bb\uff0c\u4ed6\u4eec\u4e24\u4e2a\u8fd8\u6ca1\u8d70 \u8fdc\u5462\u58eb\u4f60\u53c8\u4e0d\u662f\u4ed6\u4eec\u7684\u5bf9\u624b\uff0c\u60f3\u767d\u9001\u6b7b\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u9762\u4e0a\u4e00\u7ea2\uff0c\u60f3\u60f3\u4e5f\u662f\u9053\u7406\uff0c\u53ea\u597d\u968f\u5979\u4eec \u8fdb\u5165\u5c4b\u5185\u3002 \u3000\u3000\u7a46\u4e5d\u5a18\u8bf7\u5353\u4e00\u822a\u5728\u897f\u9762\u82b1\u5385\u5750\u4e0b\uff0c\u94c1\u73ca\u745a\u9001\u4e0a\u9999\u837c\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u738b\u7167\u5e0c\u4e0d\u662f\u548c\u4f60\u4e00\u9053 \u5417\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d\u94c1\u73ca\u745a\u597d\u4f3c\u751a\u4e3a\u5931\u671b\uff0c\u626d\u8170\u8d70\u51fa\u82b1\u5385\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u94c1\u98de\u9f99\u643a\u7740 \u5973\u513f\uff0c\u8d70\u4e86\u8fdb\u6765\u3002\u5353\u4e00\u822a\u8fde\u5fd9\u606d\u8eab\u65bd\u793c\u3002\u94c1\u98de\u9f99\u95ee\u4e86\u59d3\u540d\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u662f\u5353\u4ef2\u5ec9\u7684\u5f8c\u4eba \u5417\uff1f\u201d\u5353\u4e00\u822a\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u662f\u6211\u5148\u7956\u3002\u201d\u94c1\u98de\u9f99\u9762\u8272\u4e0d\u8c6b\uff0c\u53c8\u9053\uff1a\u201c\u738b\u7167\u5e0c\u662f\u4f60\u7684\u597d\u670b \u53cb\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4e5f\u7b97\u5f97\u662f\u9053\u4e49\u4e4b\u4ea4\u3002\u201d\u94c1\u98de\u9f99\u5ffd\u7136\u51b7\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u738b\u5609\u80e4\u4e5f\u7b97\u7eff\u6797 \u5927\u8c6a\uff0c\u600e\u9ebd\u8001\u662f\u559c\u6b22\u6cbe\u5b98\u8fd1\u5e9c\u3002\u201d\u5353\u4e00\u822a\u5341\u5206\u4e0d\u5feb\uff0c\u94c1\u98de\u9f99\u9053\uff1a\u201c\u90a3\u65e5\u548c\u6211\u5bf9\u654c\u7684\u90a3\u4e2a\u8d3c\u5a46 \u5a18\uff0c\u4e5f\u662f\u548c\u4f60\u4e00\u9053\u7684\u5427\uff1f\u201d\u5353\u4e00\u822a\u867d\u7136\u81ea\u5df2\u4e0d\u6ee1\u7389\u7f57\u5239\u4e3a\u76d7\uff0c\u4f46\u542c\u4eba\u79f0\u5979\u4e3a\u201c\u8d3c\u5a46\u5a18\u201d\uff0c\u5fc3 \u4e2d\u5374\u751a\u751f\u6c14\u3002\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u94c1\u8001\u82f1\u96c4\u65e2\u7136\u618e\u538c\u5b98\u5bb6\uff0c\u53c8\u75db\u9a82\u5f3a\u76d7\uff0c\u662f\u4f55\u9053\u7406\uff0c\u665a\u751f\u613f\u95fb\u5176 \u8be6\u3002\u201d\u94c1\u98de\u9f99\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u5c0f\u5b50\u65e0\u793c\uff01\u201d\u4f38\u624b\u5411\u5353\u4e00\u822a\u80a9\u5934\u6293\u6765\u3002\u5353\u4e00\u822a\u6c89\u80a9\u5782\u8098\uff0c\u5f80\u5916 \u4e00\u6323\uff0c\u53ea\u89c9\u80a9\u5934\u5982\u7ed9\u706b\u7ef3\u70d9\u8fc7\u4e00\u6837\uff0c\u8fa3\u8fa3\u4f5c\u75db\u3002\u4f46\u7ec8\u65bc\u89e3\u4e86\u90a3\u62db\u3002\u94c1\u98de\u9f99\u9762\u8272\u4e00\u53d8\uff0c\u559d\u9053\uff1a \u201c\u4f60\u662f\u7d2b\u9633\u9053\u957f\u7684\u5f1f\u5b50\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6b63\u662f\u5bb6\u5e08\u3002\u201d\u94c1\u98de\u9f99\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u5353\u4e00\u822a\u53c8\u9053\uff1a \u201c\u4e03\u516b\u5e74\u524d\uff0c\u6211\u5728\u6b66\u5f53\u968f\u4f8d\u5bb6\u5e08\uff0c\u66fe\u89c1\u8fc7\u94c1\u8001\u524d\u8f88\u3002\u201d\u94c1\u98de\u9f99\u53c8\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u9762\u8272\u66f4\u89c1\u7f13 \u548c\uff0c\u6325\u6325\u624b\u9053\uff1a\u201c\u4f60\u5750\u4e0b\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u4f9d\u8a00\u5750\u4e0b\uff0c\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6211\u548c\u4ee4\u5e08\u66fe\u6709\u4e00\u9762\u4e4b\u7f18\uff0c\u6211\u4e5f\u4e0d\u613f\u96be\u4e3a\u65bc\u4f60\u3002\u4f46\u4f60\u53ef\u5f97 \u4ece\u5b9e\u8bf4\u6765\uff0c\u90a3\u65e5\u548c\u6211\u5bf9\u654c\u7684\u5973\u5b50\u5230\u5e95\u662f\u8c01\uff1f\u201d\u5353\u4e00\u822a\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u5979\u5c31\u662f\u7eff\u6797\u9053\u4e2d\u540d\u95fb\u80c6\u843d \u7684\u7389\u7f57\u5239\uff01\u201d\u94c1\u98de\u9f99\u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u5979\u5c31\u662f\u7389\u7f57\u5239\uff01\u6211\u53ea\u9053\u7eff\u6797\u4e2d\u4eba\u8a00\u8fc7\u5176 \u5b9e\uff0c\u5374\u771f\u6709\u4e24\u624b\u529f\u592b\u3002\u201d\u5373\u95ee\uff1a\u201c\u4f60\u662f\u5979\u7684\u4ec0\u9ebd\u4eba\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4e5f\u7b97\u5f97\u662f\u9053\u4e49\u4e4b\u4ea4\u3002\u201d \u94c1\u98de\u9f99\u5ffd\u53c8\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u5353\u4e00\u822a\u83ab\u660e\u6240\u4ee5\uff0c\u94c1\u98de\u9f99\u7b11\u4e86\u4e00\u9635\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6b63\u60f3\u8bf7\u7389\u7f57\u5239\u548c\u738b\u7167\u5e0c\u524d\u6765\uff0c\u65e2\u7136\u4f60\u548c \u4ed6\u4eec\u90fd\u662f\u9053\u4e49\u4e4b\u4ea4\uff0c\u90a3\u597d\u6781\u4e86\uff0c\u5c31\u5c48\u9a7e\u5728\u5bd2\u820d\u591a\u4f4f\u51e0\u5929\uff0c\u8ba9\u4ed6\u4eec\u6765\u4e86\u518d\u653e\u4f60\u8d70\u3002\u201d\u5353\u4e00\u822a\u6012 \u9053\uff1a\u201c\u8001\u524d\u8f88\u662f\u8981\u7ed1\u7968\u5417\uff01\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6b63\u662f\uff01\u4f46\u770b\u4f60\u5e08\u7236\u9762\u4e0a\uff0c\u6211\u4e0d\u7ed1\u4f60\uff0c\u4f60\u53ef\u522b\u5984\u60f3 \u9003\u8d70\uff01\u201d\u628a\u5353\u4e00\u822a\u7275\u51fa\u82b1\u5385\uff0c\u5c06\u4ed6\u63a8\u8fdb\u4e00\u95f4\u67f4\u623f\u3002\u987a\u624b\u628a\u95e8\u63a9\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u623f\u95f4\u4e0d\u7b97\u597d\uff0c\u4f60 \u5c31\u59d4\u5c48\u70b9\u4f4f\u51e0\u5929\u5427\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u77e5\u9053\u8fd9\u4eba\u813e\u6c14\u53e4\u602a\uff0c\u88ab\u5173\u8fdb\u67f4\u623f\uff0c\u4ed6\u53ea\u597d\u9006\u6765\u987a\u53d7\u3002\u5c31\u76d8\u5750\u5728\u5730\u4e0b\uff0c\u505a\u8d77\u5410\u7eb3\u529f \u592b\u3002\u5230\u4e86\u665a\u9ed1\uff0c\u7a46\u4e5d\u5a18\u7ed9\u4ed6\u9001\u996d\uff0c\u7b11\u9053\uff1a\u201c\u597d\u7528\u529f\u554a\uff01\u201d\u5353\u4e00\u822a\u4e5f\u4e0d\u7406\u5979\uff0c\u628a\u996d\u53c1\u6252\u4e24\u62e8\u5403 \u4e86\u3002\u7a46\u4e5d\u5a18\u5728\u65c1\u770b\u4ed6\uff0c\u5ffd\u7136\u674f\u9762\u98de\u971e\uff0c\u770b\u4e86\u4e00\u4f1a\uff0c\u53c8\u4f4e\u4e0b\u5934\u3002\u81ea\u6b64\u4e00\u8fde\u51e0\u5929\uff0c\u90fd\u662f\u7a46\u4e5d\u5a18\u9001 \u996d\uff0c\u996d\u83dc\u8d8a\u6765\u8d8a\u597d\uff0c\u4e0d\u4f46\u6709\u5c71\u9e21\u91ce\u5473\uff0c\u8fd8\u6709\u9ec4\u6cb3\u9ca4\u9c7c\u3002\u7a46\u4e5d\u5a18\u6bcf\u6765\uff0c\u90fd\u7f20\u4e03\u5939\u516b\u7684\u548c\u5353\u4e00\u822a \u778e\u804a\uff0c\u5353\u4e00\u822a\u603b\u662f\u7231\u7406\u4e0d\u7406\u3002\u8ba9\u5979\u81ea\u5df2\u6ca1\u8da3\u3002\u4e00\u665a\u7a46\u4e5d\u5a18\u53c8\u6765\u778e\u804a\uff0c\u95ee\u5353\u4e00\u822a\u9053\uff1a\u201c\u4eba\u5bb6\u90fd \u8bf4\u4f60\u7684\u5e08\u7236\u662f\u5929\u4e0b\u7b2c\u4e00\u5251\u5ba2\uff0c\u90a3\u9ebd\u4f60\u7684\u5251\u4e5f\u4e00\u5b9a\u4f7f\u5f97\u5f88\u597d\u4e86\u3002\u4f60\u7ed9\u6211\u5f00\u5f00\u773c\u754c\u5427\u3002\u201d\u5353\u4e00\u822a \u7eb9\u4e1d\u4e0d\u52a8\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u662f\u4f60\u4eec\u7684\u8089\u7968\uff0c\u600e\u6562\u821e\u5200\u5f04\u5251\uff1f\u201d\u7a46\u4e5d\u5a18\u9053\uff1a\u201c\u54ce\u54df\uff0c\u4f60\u602a\u6211\u4eec \u5e84\u4e3b\u4e86\uff01\u8bf4\u8d77\u6765\u4e5f\u771f\u662f\u7684\uff0c\u4f60\u662f\u4e2a\u5b98\u5bb6\u5b50\u5f1f\uff0c\u600e\u53d7\u5f97\u4e86\u8fd9\u7b49\u59d4\u5c48\u3002\u4f60\u60f3\u8d70\u5417\uff1f\u201d\u5353\u4e00\u822a\u95ed\u53e3 \u4e0d\u7b54\u3002\u7a46\u4e5d\u5a18\u53c8\u9053\uff1a\u201c\u4f60\u9053\u6211\u4eec\u5e84\u4e3b\u4e3a\u4ec0\u9ebd\u8981\u628a\u4f60\u5173\u5728\u8fd9\u91cc\uff1f\u539f\u6765\u662f\u4e3a\u4ed6\u5b9d\u8d1d\u7684\u5973\u513f\u3002\u201d\u5353 \u4e00\u822a\u9887\u611f\u610f\u5916\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u9ebd\uff1f\u201d\u5fc3\u60f3\uff1a\u4e00\u4e2a\u5df2\u96be\u5bf9\u4ed8\uff0c\u82e5\u518d\u7f20\u4e0a\u4e00\u4e2a\uff0c\u5982\u4f55\u5f97\u4e86\uff1f\u7a46\u4e5d\u5a18 \u7b11\u9053\uff1a\u201c\u73ca\u745a\u4e00\u5fc3\u60f3\u5ac1\u738b\u7167\u5e0c\uff0c\u738b\u7167\u5e0c\u5374\u6709\u4e2a\u672a\u5a5a\u59bb\u5b50\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u505c\u4f4f\uff0c\u5353\u4e00\u822a\u6697 \u9053\u201c\u4e0d\u597d\u201d\uff0c\u7a46\u4e5d\u5a18\u7eed\u8bf4\uff1a\u201c\u56e0\u6b64\u628a\u4f60\u5173\u5728\u8fd9\u91cc\u3002\u201d\u5353\u4e00\u822a\u6025\u9053\uff1a\u201c\u8fd9\u4e2a\u4e0e\u6211\u4f55\u5e72\uff1f\u5929\u4e0b\u5c3d \u591a\u7537\u5b50\u2026\u2026\u201d\u7a46\u4e5d\u5a18\u7b11\u5f97\u4f3c\u82b1\u679d\u4e71\u98a4\uff0c\u5353\u4e00\u822a\u8be7\u7136\u505c\u8bed\uff0c\u7a46\u4e5d\u5a18\u7b11\u4e86\u4e00\u9635\uff0c\u4f38\u51fa\u4e2d\u98df\u4e8c\u6307\uff0c \u5728\u9762\u76ae\u4e0a\u4e00\u522e\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u8bc6\u7f9e\uff0c\u4f60\u5f53\u662f\u4eba\u5bb6\u770b\u4e0a\u4f60\u5417\uff1f\u73ca\u745a\u8981\u628a\u4f60\u5173\u5728\u8fd9\u91cc\uff0c\u5f15\u738b\u7167\u5e0c \u6765\uff0c\u7136\u5f8c\u561b\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u53c8\u505c\u6b62\u3002\u5353\u4e00\u822a\u677e\u4e86\u53e3\u6c14\uff0c\u6697\u7b11\u81ea\u5df1\u591a\u7591\uff0c\u7a46\u4e5d\u5a18\u5ffd\u7136\u53f9\u4e86 \u53e3\u6c14\uff0c\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u4e5f\u8bb8\u6709\u4eba\u770b\u4e0a\u4f60\u5462\uff01\u201d\u5353\u4e00\u822a\u76d8\u819d\u4e00\u5750\uff0c\u4e0d\u7406\u4f1a\u5979\u3002\u7a46\u4e5d\u5a18\u751a\u662f\u65e0\u8da3\uff0c \u6328\u4e0a\u524d\u6765\uff0c\u642d\u8baa\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u628a\u5251\u662f\u5e08\u7236\u7ed9\u4f60\u7684\u5427\uff1f\u201d\u5353\u4e00\u822a\u4ecd\u7136\u4e0d\u7406\uff0c\u7a46\u4e5d\u5a18\u5ffd\u7136\u4f38\u624b\u5728 \u4ed6\u8170\u95f4\u4e00\u62bd\uff0c\u628a\u4ed6\u7684\u7aa6\u5251\u62bd\u4e86\u51fa\u6765\u3002\u5353\u4e00\u822a\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4f60\u505a\u4ec0\u9ebd\uff1f\u201d\u7a46\u4e5d\u5a18\u9053\uff1a\u201c\u501f\u7ed9\u6211 \u770b\u770b\u90fd\u4e0d\u6210\u5417\uff1f\u201d\u5353\u4e00\u822a\u5f85\u8981\u53bb\u62a2\uff0c\u7a46\u4e5d\u5a18\u628a\u5251\u85cf\u5728\u8eab\u5f8c\uff0c\u5374\u628a\u80f8\u812f\u633a\u4e86\u4e0a\u6765\uff0c\u5353\u4e00\u822a\u6025\u5fd9 \u5f8c\u9000\uff0c\u6b63\u5f53\u6b64\u9645\uff0c\u5ffd\u7136\u95e8\u5916\u6709\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u597d\u4e2a\u65e0\u803b\u8d31\u4eba\uff01\u201d\u7830\u7684\u4e00\u58f0\uff0c\u628a\u95e8\u8e22\u5f00\uff0c\u7a46\u4e5d\u5a18 \u5413\u4e86\u4e00\u8df3\uff0c\u53ea\u89c1\u4e00\u4e2a\u5c11\u5973\u8df3\u4e86\u8fdb\u6765\uff0c\u7adf\u7136\u662f\u7389\u7f57\u5239\uff01 \u3000\u3000\u5353\u4e00\u822a\u53eb\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff01\u201d\u7389\u7f57\u5239\u77aa\u76ee\u4e0d\u7406\uff0c\u9762\u631f\u5bd2\u971c\uff0c\u5bf9\u7a46\u4e5d\u5a18\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u505a\u4ec0 \u9ebd\uff1f\u54fc\uff0c\u771f\u662f\u65e0\u803b\uff01\u201d \u3000\u3000\u7a46\u4e5d\u5a18\u51e0\u66fe\u53d7\u8fc7\u8fd9\u6837\u8d23\u9a82\uff0c\u53c8\u7f9e\u53c8\u607c\uff0c\u867d\u7136\u660e\u77e5\u4e0d\u662f\u7389\u7f57\u5239\u5bf9\u624b\uff0c\u4f46\u706b\u4e0a\u5fc3\u5934\uff0c\u5df2\u96be\u6309 \u637a\uff0c\u5237\u7684\u4e00\u5251\u4fbf\u5411\u7389\u7f57\u5239\u523a\u6765\u3002\u7389\u7f57\u5239\u51b7\u7b11\u4e00\u58f0\uff0c\u8fd8\u4e86\u4e00\u5251\uff0c\u987f\u65f6\u628a\u7a46\u4e5d\u5a18\u7684\u5251\u5c01\u51fa\u5916\u95e8\u3002 \u7a46\u4e5d\u5a18\u628a\u5251\u4e00\u65cb\u4e00\u5377\uff0c\u62bd\u4e86\u51fa\u6765\uff0c\u4ece\u7a97\u53e3\u4e00\u8df3\u800c\u51fa\u3002 \u3000\u3000\u7389\u7f57\u5239\u6014\u4e86\u4e00\u6014\uff0c\u7a46\u4e5d\u5a18\u8fd9\u4e00\u62db\u53c8\u662f\u5979\u5e08\u7236\u6240\u521b\u72ec\u95e8\u5251\u6cd5\u3002\u6025\u5fd9\u8ddf\u8e2a\u8df3\u51fa\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c \u547c\u7684\u4ece\u7a46\u4e5d\u5a18\u5934\u9876\u98de\u63a0\u800c\u8fc7\uff0c\u62e6\u5728\u5979\u7684\u524d\u9762\uff0c\u628a\u5251\u5f80\u524d\u4e00\u523a\uff0c\u518d\u5728\u53f3\u4e00\u6311\uff0c\u9980\u52bf\u672a\u5c3d\uff0c\u5251\u950b \u500f\u53c8\u5708\u4e86\u56de\u6765\uff0c\u8fd9\u662f\u7389\u7f57\u5239\u72ec\u95e8\u5251\u6cd5\u4e2d\u7684\u7edd\u62db\uff0c\u5bf9\u624b\u7684\u529f\u529b\u9664\u975e\u6bd4\u81ea\u5df2\u9ad8\u8bb8\u591a\uff0c\u5426\u5219\u975e\u7528\u672c \u95e8\u5251\u6cd5\uff0c\u65e0\u80fd\u89e3\u62c6\u3002\u7a46\u4e5d\u5a18\u679c\u7136\u628a\u5251\u4e00\u5c01\uff0c\u81ea\u5de6\u81f3\u53f3\u7684\u53cd\u65cb\u56de\u6765\uff0c\u518d\u6c89\u5251\u4e00\u538b\uff0c\u89e3\u4e86\u8fd9\u62db\uff0c \u624b\u6cd5\u867d\u7136\u5e76\u4e0d\u7eaf\u719f\uff0c\u4f46\u770b\u8fc7\u90a3\u90e8\u5251\u8c31\uff0c\u5374\u662f\u65e0\u7591\u3002\u7389\u7f57\u5239\u7eb5\u58f0\u72c2\u7b11\uff0c\u624b\u4e0b\u66f4\u4e0d\u7559\u60c5\uff0c\u5251\u62db\u50ac \u5feb\uff0c\u5237\u5237\u4e24\u5251\uff0c\u5206\u523a\u7a46\u4e5d\u5a18\u4e24\u80c1\u7a74\u9053\u3002\u7a46\u4e5d\u5a18\u867d\u7136\u5077\u7ec3\u8fc7\u7389\u7f57\u5239\u7684\u5251\u6cd5\uff0c\u4f46\u65f6\u65e5\u751a\u77ed\uff0c\u62db\u5f0f \u90fd\u8fd8\u672a\u719f\uff0c\u5982\u4f55\u6321\u5f97\uff1f\u987f\u65f6\u7ed9\u7389\u7f57\u5239\u5251\u900f\u8863\u88f3\uff0c\u4e24\u80c1\u7a74\u9053\uff0c\u5168\u88ab\u523a\u4e2d\uff0c\u7ffb\u8eab\u4ec6\u5012\u3002 \u3000\u3000\u7389\u7f57\u5239\u6536\u5251\u72c2\u7b11\uff0c\u6b63\u60f3\u8feb\u4f9b\u3002\u94c1\u98de\u9f99\u5df2\u662f\u95fb\u58f0\u800c\u51fa\u3002\u53cc\u773c\u4e00\u626b\uff0c\u66b4\u6012\u5982\u96f7\uff0c\u94c1\u638c\u4e00\u626c\uff0c \u5927\u58f0\u559d\u9053\uff1a\u201c\u7389\u7f57\u5239\uff0c\u4f60\u6b3a\u6211\u592a\u751a\uff1f\u4f60\u767b\u95e8\u8f83\u6280\uff0c\u4e3a\u4f55\u5168\u4e0d\u4f9d\u6c5f\u6e56\u793c\u8282\uff0c\u5979\u4e0e\u4f60\u6709\u4ec0\u9ebd\u5927\u4e0d \u4e86\u7684\u51a4\u4ec7\uff0c\u4f60\u8981\u4e0b\u8fd9\u7b49\u6bd2\u624b\uff01\u201d\u7389\u7f57\u5239\u51b7\u7b11\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u4eec\u4e00\u5bb6\u90fd\u662f\u4e0b\u53c1\u6d41\u7684\u5c0f\u8d3c\uff01\u201d\u94c1\u98de \u9f99\u864e\u543c\u4e00\u58f0\uff0c\u626c\u7a7a\u4e00\u638c\uff0c\u500f\u7684\u6253\u51fa\uff1f\u4e3b\u7f57\u5239\u7ffb\u8eab\u8fdb\u5251\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u628a\u5251\u8c31\u8fd8\u6211\uff0c\u8a93\u4e0d \u5e72\u4f11\uff01\u201d\u94c1\u98de\u9f99\u594b\u529b\u62c6\u4e86\u51e0\u62db\uff0c\u731b\u7684\u4e00\u638c\uff0c\u5c06\u7389\u7f57\u5239\u8feb\u9000\u4e24\u6b65\uff0c\u559d\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u4ec0\u9ebd\u5251 \u8c31\uff1f\u201d\u7389\u7f57\u5239\u4e00\u5251\u523a\u53bb\uff0c\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u73b0\u5728\u8fd8\u88c5\u751a\u9ebd\u849c\uff1f\u8981\u4e0d\u662f\u4f60\u5077\u4e86\u6211\u7684\u5251\u8c31\uff0c\u4f60\u90a3\u5b9d \u8d1d\u5973\u513f\u548c\u8fd9\u4e2a\u9a9a\u72d0\u72f8\uff0c\u600e\u9ebd\u4f1a\u4f7f\u6211\u5e08\u7236\u7684\u72ec\u95e8\u5251\u6cd5\uff1f\u201d\u94c1\u98de\u9f99\u5927\u543c\u4e00\u58f0\uff0c\u53cc\u62f3\u4e00\u683c\uff0c\u628a\u7389\u7f57 \u5239\u53c8\u8feb\u9000\u4e24\u6b65\uff0c\u8df3\u51fa\u5708\u5b50\uff0c\u559d\u9053\uff1a\u201c\u4e14\u6162\uff01\u5f85\u6211\u95ee\u4e2a\u660e\u767d\u3002\u201d\u8df3\u5230\u7a46\u4e5d\u5a18\u8eab\u8fb9\uff0c\u5c06\u5979\u6276\u8d77\uff0c \u89c1\u5979\u80c1\u4e0b\u6d41\u8840\uff0c\u53c8\u601c\u53c8\u7231\u3002\u5ffd\u89c1\u5979\u8eab\u8fb9\u4e00\u67c4\u957f\u5251\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u94c1\u98de\u9f99\u8ba4\u5f97\u662f\u7d2b\u9633\u9053\u4eba\u7684\u5bd2\u5149 \u5251\uff0c\u4e0d\u7528\u731c\u5ea6\uff0c\u5df2\u77e5\u5979\u662f\u81ea\u5353\u4e00\u822a\u8eab\u4e0a\u53d6\u6765\u3002\u84e6\u7136\u60f3\u8d77\u201c\u9a9a\u72d0\u72f8\u201d\u53c1\u5b87\uff0c\u4e0d\u89c9\u53d8\u8272\u3002\u6c89\u58f0\u559d \u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd\u5077\u522b\u4eba\u7684\u5b9d\u5251\uff1f\u201d\u7389\u7f57\u5239\u5659\u7740\u51b7\u7b11\uff0c\u6b63\u60f3\u5f00\u53e3\uff0c\u5ffd\u89c1\u7a46\u4e5d\u5a18\u5168\u8eab\u98a4\u6296\uff0c\u76ee\u5149 \u4e2d\u542b\u7740\u65e0\u9650\u60e7\u6015\uff0c\u6d3b\u50cf\u5e73\u65f6\u7ed9\u81ea\u5df2\u5904\u6b7b\u7684\u90a3\u73ed\u5f3a\u76d7\u5934\u5b50\u4e00\u6837\uff0c\u84e6\u7136\u60f3\u8d77\u5353\u4e00\u822a\u5728\u5c71\u6d1e\u6240\u8bf4\u7684 \u8bdd\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5ffd\u7136\u8d77\u4e86\u4e00\u70b9\u6148\u5fc3\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u5374\u53c8\u7559\u4f4f\u3002\u7a46\u4e5d\u5a18\u89c1\u7389\u7f57\u5239\u5e76\u4e0d\u7b54\u8bdd\uff0c\u677e\u4e86 \u53e3\u6c14\uff0c\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u6211\u89c1\u5979\u6301\u5251\u7834\u95e8\u800c\u5165\uff0c\u6211\u624b\u4e2d\u6ca1\u6709\u5175\u5668\uff0c  \u597d\u501f\u5353\u4e00\u822a\u7684\u5b9d\u5251\u4e00\u7528\u3002\u201d \u8fd9\u8bdd\u8bf4\u5f97\u9887\u6709\u9053\u7406\u3002\u94c1\u98de\u9f99\u53c8\u559d\u9053\uff1a\u201c\u90a3\u9ebd\u5251\u8c31\u662f\u4e0d\u662f\u4f60\u5077\u7684\uff1f\u201d\u7a46\u4e5d\u5a18\u786c\u7740\u5934\u76ae\u8bf4\uff1a \u201c\u4e0d\uff0c\u4e0d\uff0c\u4e0d\u662f\u6211\u5077\u7684\uff01\u201d\u94c1\u98de\u9f99\u5927\u559d\u9053\uff1a\u201c\u53eb\u73ca\u745a\u6765\uff01\u201d\u7a46\u4e5d\u5a18\u500f\u7136\u53d8\u8272\u3002\u6b63\u662f\uff1a\u5947\u4e66\u60f9 \u5947\u7978\uff0c\u7389\u9aa8\u59d4\u5c18\u7802\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377\u2014\u2014\u7b2c\u4e03\u56de\u3000\u5251\u8c31\u60f9\u5947\u707e\u3000\u98ce\u6ce2\u53e0\u8d77\u3000\u638c\u95e8\u627f\u91cd\u6258\u3000\u8bef\u4f1a\u6a2a\u751f \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377 \u7b2c\u4e03\u56de \u5251\u8c31\u60f9\u5947\u707e\u3000\u98ce\u6ce2\u53e0\u8d77 \u638c\u95e8\u627f\u91cd\u6258\u3000\u8bef\u4f1a\u6a2a\u751f \u3000\u3000\u94c1\u98de\u9f99\u66f4\u662f\u8d77\u7591\uff0c\u8df3\u4e0a\u5047\u77f3\u5c71\u4e0a\uff0c\u5927\u53eb\u53c1\u58f0 \u201c\u73ca\u745a\uff0c\u73ca\u745a\uff0c\u73ca\u745a\uff01\u201d\u4e0d\u89c1\u56de\u7b54\uff0c\u84e6\u7136 \u95f4\uff0c\u5ffd\u89c1\u4e24\u6761\u4eba\u5f71\uff0c\u4ece\u5f8c\u9662\u5899\u5934\u98de\u51fa\uff0c\u63a5\u7740\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u6e9c\u706b\u5149\uff0c\u51b2\u5929\u800c\u8d77\u3002\u94c1\u98de\u9f99\u6307 \u7740\u7a46\u4e5d\u5a18\u559d\u9053 \u201c\u8d31\u4eba\uff0c\u4e0d\u8bb8\u4e71\u52a8\uff01\u201d\u7389\u7f57\u5239\u6301\u5251\u51b7\u7b11\uff0c\u7ad9\u5728\u7a46\u4e5d\u5a18\u8eab\u8fb9\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u4f60 \u5c3d\u7ba1\u53bb\uff0c\u6709\u6211\u5728\u8fd9\u513f\u5462\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u77ed\u987b\u5982\u621f\uff0c\u6012\u6781\u6c14\u6781\uff0c\u51e0\u5341\u5e74\u6765\uff0c\u4ece\u672a\u6709\u4eba\u6562\u634b\u4ed6\u7684\u864e\u987b\uff0c\u60f3\u4e0d\u5230\u5c45\u7136\u6709\u4eba\u6562\u5230 \u4ed6\u5bb6\u653e\u706b\u3002\u770b\u90a3\u4e24\u6761\u4eba\u5f71\uff0c\u8eab\u6cd5\u5947\u592e\uff0c\u6b66\u529f\u60f3\u5fc5\u6781\u9ad8\uff0c\u53ea\u6cca\u5973\u513f\u906d\u4e86\u6bd2\u624b\uff0c\u65e2\u6025\u4e14\u60ca\uff0c\u65e0\u6687 \u8ffd\u654c\uff0c\u5148\u5411\u706b\u5149\u5904\u5954\u53bb\u3002 \u3000\u3000\u521a\u521a\u98de\u8d8a\u4e86\u4e24\u5ea7\u697c\u623f\uff0c\u706b\u5149\u4e2d\u7a81\u7136\u7a9c\u51fa\u53c1\u4eba\uff0c\u4e24\u5973\u4e00\u7537\uff0c\u90a3\u7537\u7684\u6b63\u662f\u738b\u662d\u5e0c\uff0c\u4e24\u4e2a\u5973 \u7684\uff0c\u4e00\u4e2a\u662f\u76c2\u79cb\u971e\uff0c\u4e00\u4e2a\u662f\u94c1\u73ca\u745a\u3002\u94c1\u73ca\u745a\u9762\u8272\u60e8\u767d\uff0c\u88ab\u5b5f\u79cb\u971e\u6276\u7740\u8d70\u51fa\u3002 \u3000\u3000\u94c1\u98de\u9f99\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u4e00\u8dc3\u800c\u524d\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u738b\u7167\u5e0c\u4f60\u597d\u5927\u80c6\uff0c\u4f60\u6765\u6551\u672a\u5a5a\u59bb\u5b50\u4e5f\u8fd8 \u7f62\u4e86\uff0c\u4e3a\u4f55\u5374\u5728\u6211\u5bb6\u4e2d\u653e\u706b\uff0c\u53c8\u6253\u4f24\u6211\u7684\u5973\u513f\uff1f\u201d\u4f38\u624b\u4e00\u6293\uff0c\u94c1\u73ca\u745a\u5ffd\u7136\u7741\u773c\u8bf4\u9053\uff1a\u201c\u7238 \u7238\uff0c\u4e0d\u662f\u4ed6\uff01\u201d\u738b\u7167\u5e0c\u65c1\u7a9c\u53c1\u6b65\uff0c\u94c1\u98de\u9f99\u624b\u638c\u64a4\u56de\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u662f\u4ec0\u9ebd\u4eba\uff1f\u201d\u94c1\u73ca\u745a\u9053\uff1a \u201c\u662f\u91d1\u5343  \u7684\u53d4\u53d4\uff01\u201d\u94c1\u98de\u9f99\u9762\u8272\u5927\u53d8\uff0c\u738b\u7167\u5e0c\u9053\uff1a\u201c\u6551\u706b\u8981\u7d27\uff0c\u65e5\u5f8c\u6211\u4eec\u518d\u627e\u4ed6\u7b97\u5e10\u3002\u201d \u3000\u3000\u94c1\u98de\u9f99\u60f3\u60f3\u4e5f\u662f\u9053\u7406\u3002\u539f\u6765\u90a3\u91d1\u5343  \u7684\u53d4\u53d4\u540d\u53eb\u91d1\u72ec\u5f02\uff0c\u8fdc\u5904\u897f\u9672\uff0c\u53c1\u5341\u5e74\u6765\uff0c\u8db3\u8ff9\u4e0d \u51fa\u5929\u5c71\u5357\u5317\uff0c\u4ed6\u6240\u7ec3\u7684\u9634\u98ce\u6bd2\u7802\u638c\uff0c\u706b\u5019\u6781\u7eaf\uff0c\u91d1\u5343  \u6240\u5f97\u4e0d\u8fc7\u662f\u4ed6\u7684\u516d\u4e03\u6210\u800c\u5df2\u3002\u94c1\u98de\u9f99 \u53c1\u5341\u591a\u5e74\u4e4b\u524d\u66fe\u89c1\u8fc7\u4ed6\u4e00\u9762\uff0c\u90a3\u65f6\u4ed6\u7684\u9634\u98ce\u6bd2\u7802\u638c\u8fd8\u672a\u7ec3\u6210\uff0c\u4e24\u4eba\u8bba\u6b66\u8f83\u6280\uff0c\u5df2\u662f\u96be\u5206\u9ad8 \u4e0b\u3002\u5f8c\u6765\u95fb\u5f97\u4ed6\u7ec3\u6210\u6bd2\u7802\u638c\u5f8c\uff0c\u5728\u897f\u57df\u5e7f\u6536\u95e8\u5f92\uff0c\u884c\u4e3a\u751a\u662f\u4e56\u8c2c\uff0c\u94c1\u98de\u9f99\u5176\u65f6\u5df2\u5728\u9f99\u95e8\u9690 \u5c45\uff0c\u4e0d\u5927\u7406\u4f1a\u95f2\u4e8b\uff0c\u4e24\u4eba\u5404\u884c\u5176\u662f\uff0c\u4e92\u4e0d\u5f80\u6765\u3002\u76f4\u5230\u53c1\u65e5\u4e4b\u524d\uff0c\u91d1\u5343  \u5ffd\u7136\u5055\u540c\u4e91\u71d5\u5e73\u6765 \u8bbf\uff0c\u94c1\u98de\u9f99\u56e0\u4e3a\u8ba8\u538c\u4ed6\u7684\u53d4\u53d4\uff0c\u4e0d\u4e88\u63a5\u7eb3\uff0c\u91d1\u5343  \u65b9\u8e0f\u8fdb\u5e84\u95e8\uff0c\u4ed6\u5c31\u53eb\u7a46\u4e5d\u5a18\u5c06\u4ed6\u4eec\u8f70\u4e86\u51fa \u53bb\u3002\u94c1\u98de\u9f99\u5fc3\u60f3\uff1a\u96be\u9053\u8fd9\u8001\u602a\u7269\u662f\u56e0\u4e3a\u6211\u8f70\u8d70\u4e86\u4ed6\u7684\u4f84\u513f\uff0c\u6240\u4ee5\u7279\u5730\u524d\u6765\u62a5\u590d\uff0c\u82e5\u7136\u8fd9\u6837\uff0c \u5fc3\u5730\u4e5f\u672a\u514d\u592a\u72ed\u7a84\u4e86\u3002\u53ea\u662f\u4ed6\u6b66\u529f\u6781\u9ad8\uff0c\u8981\u8ffd\u8c05\u4e5f\u8ffd\u4e4b\u4e0d\u53ca\uff0c\u53ea\u597d\u4f9d\u4ece\u738b\u7167\u5e0c\u4e4b\u8a00\uff0c\u5148\u884c\u6551 \u706b\u3002 \u3000\u3000\u518d\u8bf4\u5b5f\u79cb\u971e\u4e07\u91cc\u5bfb\u592b\uff0c\u800c\u4eca\u59cb\u89c1\u3002\u5728\u706b\u5149\u4e2d\u770b\u770b\u738b\u7167\u5e0c\u53c8\u770b\u770b\u94c1\u73ca\u745a\uff0c\u4e0d\u89c9\u767e\u611f\u4ea4\u96c6\u3002 \u539f\u6765\u5b5f\u79cb\u971e\u79bb\u5f00\u4eac\u5e08\uff0c\u8fdc\u8d70\u897f\u5317\uff0c\u4eba\u65e2\u7cbe\u7075\uff0c\u53c8\u4ed7\u7740\u4e00\u8eab\u6b66\u827a\uff0c\u4e07\u91cc\u72ec\u884c\uff0c\u5c45\u7136\u6ca1\u51fa\u5c94\u5b50\u3002 \u4e00\u65e5\u6765\u5230  \u897f\uff0c\u9014\u4e2d\u7a81\u7136\u78b0\u5230\u94c1\u73ca\u745a\u548c\u7a46\u4e5d\u5a18\uff0c\u5f7c\u6b64\u90fd\u662f\u6c5f\u6e56\u5973\u5b50\uff0c\u4ea4\u8c08\u751a\u6b22\u3002\u5728\u8a00\u8c08\u4e2d\u5b5f \u79cb\u971e\u9732\u51fa\u53e3\u98ce\uff0c\u8bf4\u662f\u8981\u5230  \u5317\u5bfb\u592b\uff0c\u94c1\u73ca\u745a\u5fc3\u4e2d\u6709\u4e8b\uff0c\u7acb\u523b\u7559\u610f\uff0c\u51fa\u8a00\u8bd5\u63a2\uff0c\u76c2\u79cb\u971e\u867d\u7136\u7cbe \u7075\uff0c\u7ec8\u662f\u4e16\u6545\u672a\u6df1\uff0c\u7adf\u7136\u628a\u738b\u7167\u5e0c\u7684\u540d\u5b57\u8bf4\u4e86\u51fa\u6765\u3002\u94c1\u73ca\u745a\u4e00\u58f0\u51b7\u7b11\uff0c\u7a81\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684 \u624b\u6cd5\uff0c\u70b9\u4e86\u5979\u7684  \u7a74\u3002 \u3000\u3000\u5f85\u76c2\u79cb\u971e\u9192\u8f6c\u6765\u65f6\uff0c\u4eba\u5df2\u5728\u94c1\u5bb6\u5e84\u5185\u3002\u94c1\u73ca\u745a\u5c0f\u5b69\u5fc3\u6027\uff0c\u542c\u5979\u8bf4\u662f\u738b\u7167\u5e0c\u7684\u672a\u5a5a\u59bb\u5b50\uff0c \u4e0d\u987e\u5229\u5bb3\uff0c\u4e00\u4e0b\u5b50\u5c06\u5979\u70b9\u5012\uff0c\u56de\u5bb6\u7980\u544a\u7236\u4eb2\uff0c\u521d\u65f6\u8fd8\u60f4\u60f4\u4e0d\u5b89\uff0c\u751f\u6015\u7236\u4eb2\u8d23\u5907\uff1a\u94c1\u98de\u9f99\u5374\u6380 \u987b\u7b11\u9053\uff1a\u201c\u738b\u5609\u80e4\u8eab\u4e3a\u7eff\u6797\u5927\u8c6a\uff0c\u5374\u548c\u4ec0\u9ebd\u592a\u5b50\u7684\u503c\u6bbf\u6b66\u5e08\u7ed3\u4e3a\u4eb2\u5bb6\uff0c\u4f60\u4f5c\u5f04\u4e00\u4e0b\u5979\u4e5f \u597d\u3002\u201d\u94c1\u98de\u9f99\u751f\u6027\u602a\u50fb\uff0c\u4e0d\u8bb8\u522b\u4eba\u62c2\u9006\u4ed6\u7684\u610f\u601d\uff0c\u738b\u5609\u80e4\u90a3\u6b21\u5a49\u8f6c\u62d2\u5a5a\uff0c\u4ed6\u751a\u4e3a\u4e0d\u60a6\uff0c\u4f46\u8f6c \u5ff5\u4e00\u60f3\uff0c\u4ee5\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u96be\u4e3a\u4e00\u4e2a\u5355\u8eab\u5973\u5b50\uff0c\u4f20\u51fa\u53bb\u4e5f\u4e0d\u597d\u542c\uff0c\u56e0\u6b64\u4fbf\u53eb\u94c1\u73ca\u745a\u5c06\u5b5f\u79cb\u971e\u597d \u597d\u6b3e\u5f85\uff0c\u4e00\u9762\u6d3e\u4eba\u53bb\u901a\u77e5\u738b\u5609\u80e4\u3002 \u3000\u3000\u7389\u7f57\u5239\u548c\u94c1\u98de\u9f99\u4e00\u6708\u4e4b\u7ea6\u672c\u672a\u5230\u671f\uff0c\u4f46\u542c\u5230\u6b64\u4e8b\uff0c\u4e5f\u4fbf\u548c\u738b\u7167\u5e0c\u7ed3\u4f34\u540c\u884c\u3002\u5230\u4e86\u94c1\u5bb6\uff0c \u7389\u7f57\u5239\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u867d\u7136\u7ed3\u4f34\u540c\u6765\uff0c\u4f46\u6240\u56f0\u5404\u5f02\u3002\u6211\u548c\u94c1\u8001\u5934\u8f83\u6280\uff0c\u7ea6\u660e\u5355\u6253\u72ec\u6597\uff0c\u4f60 \u4e14\u5f85\u6211\u4eec\u89c1\u4e86\u771f\u7ae0\u4e4b\u5f8c\uff0c\u624d\u597d\u8fdb\u6765\u3002\u201d\u738b\u7167\u5e0c\u867d\u7136\u5fc3\u6025\u5982\u711a\uff0c\u4e5f\u53ea\u597d\u5f98\u5f8a\u5e84\u5916\u3002 \u3000\u3000\u8fc7\u4e86\u597d\u4e45\uff0c\u8fd8\u672a\u89c1\u7389\u7f57\u5239\u51fa\u6765\uff0c\u738b\u7167\u5e0c\u5fc3\u60f3\u4e0d\u597d\uff0c\u4ed6\u4eec\u4e24\u4eba\u90fd\u6781\u597d\u80dc\uff0c\u82e5\u81f3\u76f8\u6301\u4e0d\u4e0b\uff0c \u53ea\u6050\u4e24\u8d25\u4ff1\u4f24\uff0c\u6211\u65e2\u5230\u6b64\uff0c\u4e0d\u80fd\u5750\u89c6\u3002\u4e3b\u610f\u62ff\u5b9a\uff0c\u62da\u53d7\u7389\u7f57\u5239\u8d23\u602a\uff0c\u6084\u6084\u7684\u4ece\u5f8c\u5e84\u8df3\u5165\uff0c\u60f3 \u5148\u770b\u770b\u4ed6\u4eec\u4e24\u4e2a\uff0c\u6253\u5f97\u5982\u4f55\u3002 \u3000\u3000\u4e0d\u6599\u5c31\u5728\u6b64\u65f6\uff0c\u91d1\u72ec\u5f02\u548c\u53e6\u5916\u4e00\u4e2a\u9ad8\u624b\uff0c\u591c\u641c\u94c1\u5bb6\uff0c\u94c1\u73ca\u745a\u5927\u58f0\u53eb\u56b7\uff0c\u5403\u4ed6\u63d2\u4e86\u4e00\u638c\uff0c \u5b5f\u79cb\u971e\u5367\u5ba4\u548c\u94c1\u73ca\u745a\u76f8\u90bb\uff0c\u95fb\u58f0\u8df3\u51fa\uff0c\u6070\u6070\u78b0\u7740\u4e86\u738b\u7167\u5e0c\uff0c\u5b5f\u79cb\u971e\u5c06\u94c1\u73ca\u745a\u6276\u8d77\uff0c\u800c\u91d1\u72ec\u5f02 \u53d1\u4e86\u4e00\u679a\u786b\u78fa\u5f39\u5f8c\uff0c\u4e5f\u4fbf\u8d8a\u5899\u9003\u8d70\u3002 \u3000\u3000\u786b\u78fa\u5f39\u5f15\u8d77\u7684\u706b\u52bf\u4e0d\u5927\u3002\u94c1\u98de\u9f99\u968f\u624b\u6293\u8d77\u4e86\u4e24\u5f20\u68c9\u88ab\uff0c\u98de\u8eab\u5728\u706b\u82d7\u4e4b\u4e0a\u6251\u538b\uff0c\u8fc7\u4e86\u4e00 \u9635\uff0c\u706b  \u7184\u706d\u3002\u94c1\u98de\u9f99\u8df3\u4e0b\u697c\u6765\uff0c\u53ea\u89c1\u738b\u7167\u5e0c\u548c\u5b5f\u79cb\u971e\u8e72\u5728\u5730\u4e0a\uff0c\u66ff\u94c1\u73ca\u745a\u63a8\u8840\u8fc7\u5b98\u3002\u94c1\u98de \u9f99\u770b\u5728\u773c\u5185\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8fd9\u51e0\u5929\u6765\u4ed6\u4e5f\u66fe\u548c\u5b5f\u79cb\u971e\u4ea4\u8c08\uff0c\u5b5f\u79cb\u971e\u4e0d\u5351\u4e0d\u4ea2\uff0c\u9887\u51fa\u4ed6\u610f\u6599\u4e4b \u5916\uff0c\u5982\u4eca\u89c1\u4ed6\u4eec\u4e24\u4eba\u5e76\u5934\u8054\u624b\uff0c\u66ff\u81ea\u5df1\u5973\u513f\u6cbb\u4f24\uff0c\u795e\u60c5\u751a\u662f\u4eb2\u5bc6\uff0c\u773c\u6ce2\u4e4b\u95f4\uff0c\u6d41\u9732\u65e0\u9650\u7231 \u610f\uff0c\u4f46\u66ff\u81ea\u5df2\u5973\u513f\u6cbb\u4f24\uff0c\u5374\u53c8\u751a\u4e3a\u8ba4\u771f\u3002\u94c1\u98de\u9f99\u5fc3\u60f3\uff1a\u8fd9\u5b5f\u79cb\u971e\u4e07\u91cc\u5bfb\u592b\uff0c\u751a\u662f\u4e0d\u6613\uff0c\u4f46\u5979 \u5374\u80fd\u5728\u60a3\u96be\u76f8\u9022\u4e4b\u9645\uff0c\u4e0d\u5148\u7545\u53d9\u79bb\u60c5\uff0c\u53cd\u66ff\u201c\u4ec7\u654c\u201d\u6cbb\u4f24\uff0c\u8fd9\u6837\u7684\u5973\u5b50\uff0c\u4e5f\u771f\u96be\u5f97\u3002 \u3000\u3000\u738b\u7167\u5e0c\u53eb\u4e86\u4e00\u58f0\u201c\u94c1\u8001\u82f1\u96c4\u201d\uff0c\u6b63\u60f3\u5411\u4ed6\u62a5\u544a\u73ca\u745a\u7684\u4f24\u52bf\u4e0d\u91cd\uff0c\u514d\u4ed6\u6302\u5ff5\u3002\u94c1\u98de\u9f99\u65e9\u5df2 \u7b11\u9053\uff1a\u201c\u91d1\u8001\u8d3c\u867d\u7136\u80c6\u5927\u5984\u4e3a\uff0c\u5bf9\u6211\u5012\u4e5f\u8fd8\u6709\u4e9b\u987e\u5fcc\uff0c\u5982\u679c\u4ed6\u771f\u4e0b\u6bd2\u624b\u7684\u8bdd\uff0c\u73ca\u745a\u5341\u6761\u547d\u4e5f \u6ca1\u6709\u4e86\u3002\u201d\u738b\u7167\u5e0c\u8fd9\u624d\u77e5\u9053\uff0c\u4ed6\u662f\u77e5\u9053\u4e86\u5973\u513f\u4f24\u52bf\u4e0d\u91cd\u4e4b\u5f8c\uff0c\u8fd9\u624d\u653e\u5fc3\u6551\u706b\u7684\u3002 \u3000\u3000\u8fd9\u65f6\u94c1\u73ca\u745a\u9762\u8272\u5df2\u8f6c\u7ea2\u6da6\uff0c\u94c1\u98de\u9f99\u7a81\u7136\u5389\u58f0\u65a5\u9053\uff1a\u201c\u4f60\u8d77\u6765\uff01\u201d\u94c1\u73ca\u745a\u5e94\u58f0\u800c\u8d77\uff0c\u8bf4 \u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u53c8\u751f\u4ec0\u9ebd\u6c14\u4e86\uff01\u201d\u738b\u7167\u5e0c\u4e5f\u5728\u5947\u602a\uff1a\u94c1\u73ca\u745a\u5403\u4e86\u5927\u4e8f\uff0c\u5979\u7236\u4eb2\u4e0d\u5b89\u6170\u5979\u4e5f\u8fd8 \u5ba0\u4e86\uff0c\u4f55\u4ee5\u8fd8\u4e25\u8f9e\u5389\u8272\u5bf9\u5979\uff1f\u94c1\u98de\u9f99\u559d\u9053\uff1a\u201c\u6211\u6709\u8bdd\u95ee\u4f60\uff0c\u4f60\u968f\u6211\u51fa\u53bb\uff01\u201d\u7275\u7740\u5973\u513f\u7684\u624b\uff0c \u8d70\u51fa\u5916\u9762\u5ead\u9662\uff0c\u738b\u7167\u5e0c\u5b5f\u79cb\u971e\u8ddf\u5728\u5f8c\u9762\u3002\u53ea\u89c1\u7389\u7f57\u5239\u7ad9\u5728\u4e00\u5757\u77f3\u4e0a\uff0c\u6301\u5251\u51b7\u7b11\u3002\u7a46\u4e5d\u5a18\u5750\u5728 \u5730\u4e0b\uff0c\u9762\u8272\u60e8\u767d\uff01 \u3000\u3000\u94c1\u98de\u9f99\u9053\uff1a\u201c\u597d\uff0c\u7389\u7f57\u5239\uff0c\u4f60\u542c\u7740\uff01\u6211\u7edd\u4e0d\u5faa\u79c1\uff01\u201d\u8f6c\u8fc7\u5934\u6765\u95ee\u94c1\u73ca\u745a\u9053\uff1a\u201c\u4f60\u6709\u6ca1\u6709 \u5077\u4e86\u5979\u7684\u5251\u8c31\uff1f\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u6ca1\u6709\u5440\uff01\u201d\u7389\u7f57\u5239\u8fde\u8fde\u51b7\u7b11\u3002\u94c1\u98de\u9f99\u6273\u8d77\u9762\u5b54\uff0c\u5389\u58f0\u65a5\u9053\uff1a \u201c\u73ca\u745a\uff0c\u4f60\u8bf4\u5b9e\u8bdd\uff0c\u6211\u518d\u95ee\u4f60\u4e00\u6b21\uff1a\u4f60\u5230\u5e95\u6709\u6ca1\u6709\u62ff\u4e86\u5979\u7684\u5251\u8c31\uff1f\u201d\u94c1\u73ca\u745a\u54ed\u9053\uff1a\u201c\u5251\u8c31\u6211 \u662f\u89c1\u8fc7\u4e00\u672c\uff0c\u4f46\u4e0d\u662f\u5077\u6765\u7684\u3002\u201d\u94c1\u98de\u9f99\u9762\u8272\u500f\u53d8\uff0c\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u662f\u600e\u9ebd\u5f97\u89c1\u7684\uff1f\u201d\u94c1 \u73ca\u745a\u9053\uff1a\u201c\u662f\u59e8\u5a18\u8981\u6765\u7684\uff01\u201d\u8fd9\u5239\u90a3\u95f4\uff0c\u7a46\u4e5d\u5a18\u9762\u5982\u6b7b\u7070\uff0c\u7389\u7f57\u5239\u5f97\u610f\u72c2\u7b11\uff0c\u94c1\u98de\u9f99\u53cc\u77b3\u55b7 \u706b\uff0c\u9762\u8272\u9752\u91cc\u6cdb\u7ea2\u3002\u7389\u7f57\u5239\u7b11\u58f0\u5ffd\u6536\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u94c1\u8001\u5934\uff0c\u6211\u53ef\u6ca1\u6709\u602a\u9519\u4f60\u4eec\u5427\uff1f\u201d \u3000\u3000\u94c1\u98de\u9f99\u9762\u631f\u5bd2\u971c\uff0c\u4e0d\u7406\u7389\u7f57\u5239\u7684\u8bdd\uff0c\u5411\u94c1\u73ca\u745a\u9053\uff1a\u201c\u4f60\u4ece\u5b9e\u8bf4\u6765\uff0c\u4e0d\u8bb8\u6709\u4e00\u53e5\u9690\u7792\uff01\u201d \u94c1\u73ca\u745a\u4e3e\u8896\u63e9\u6cea\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u524d\u4e24\u4e2a\u6708\u6211\u4ece\u9655\u897f\u56de\u5bb6\uff0c\u4e00\u65e5\u5728\u96c6\u8d24\u9547\u7684\u4e00\u5bb6\u5c0f\u65c5\u9986\u6b47\u811a\uff0c \u5ffd\u89c1\u4e00\u4e2a\u9053\u4eba\uff0c\u9762\u8272\u7600\u9ed1\uff0c\u5750\u5728\u5730\u4e0a\uff0c\u4e0d\u80fd\u884c\u52a8\u3002\u5e97\u5bb6\u8bf4\u4ed6\u60a3\u4e86\u6025\u75c7\uff0c\u6050\u6015\u6b7b\u5728\u5e97\u4e2d\uff0c\u8981\u62ac \u4ed6\u51fa\u53bb\u3002\u6211\u89c1\u4ed6\u597d\u751f\u53ef\u601c\uff0c\u4e00\u65f6\u597d\u5947\uff0c\u4e0a\u524d\u53bb\u770b\uff0c\u90a3\u9053\u4eba\u4e5f\u771f\u5389\u5bb3\uff0c\u5f20\u773c\u4e00\u77a7\uff0c\u5c31\u77e5\u6211\u61c2\u5f97 \u6b66\u529f\u3002\u4ed6\u8bf4\uff1a\u5c0f\u59d1\u5a18\uff0c\u4f60\u5e26\u6709\u5251\u5427\uff1f\u8bf7\u4f60\u8d76\u5feb\u6495\u5f00\u6211\u7684\u80f8\u8863\uff0c\u5728\u80a9\u80db\u7a74\u4e0b\u4e00\u5bf8\u4e4b\u5730\uff0c\u7528\u5251\u5c16 \u5c06\u70c2\u8089\u525c\u6389\uff0c\u7ed9\u6211\u628a\u4e00\u53e3\u6bd2\u9489\u53d6\u51fa\u6765\u3002\u201d\u5353\u4e00\u822a\u5931\u58f0\u53eb\u9053\uff1a\u201c\u90a3\u4e00\u5b9a\u662f\u8d1e\u4e7e\u9053\u4eba\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u9053\uff1a\u201c\u8d1e\u4e7e\u9053\u4eba\u77e5\u4e0d\u77e5\u9053\u4f60\u662f\u6211\u7684\u5973\u513f\uff1f\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u5f53\u65f6\u4e0d\u77e5\u9053\uff0c\u5f8c\u6765\u6211\u544a \u8bc9\u4e86\u4ed6\u3002\u4ed6\u8bf4\uff1a\u6211\u5bf9\u4ee4\u5c0a\u95fb\u540d\u5df2\u4e45\uff0c\u6df1\u77e5\u4ed6\u662f\u6709\u8840\u6c14\u7684\u82f1\u96c4\uff0c\u73b0\u5728\u6211\u6258\u4f60\u8f6c\u544a\u4ed6\uff0c\u6211\u6709\u4e00\u672c \u5251\u8c31\uff0c\u662f\u522b\u4eba\u6258\u6211\u5e26\u7ed9\u5929\u5c71\u970d\u5929\u90fd\u7684\uff0c\u73b0\u5728\u7ed9\u4eba\u52ab\u4e86\uff0c\u82e5\u662f\u6211\u4e0d\u6cbb\u8eab\u6b7b\u7684\u8bdd\uff0c\u8bf7\u4ed6\u8bbe\u6cd5\u7ed9\u6211 \u5c06\u8fd9\u4e2a\u53e3\u4fe1\u9001\u5230\u5929\u5c71\u3002\u8981\u970d\u5929\u90fd\u7ed9\u6211\u62a5\u4ec7\u3002\u201d\u94c1\u98de\u9f99\u4ece\u672a\u542c\u8fc7\u4eba\u79f0\u8d5e\u4ed6\u662f\u201c\u6709\u8840\u6027\u7684\u82f1 \u96c4\u201d\uff0c\u95fb\u8a00\u9762\u8272\u7a0d\u9701\uff0c\u634b\u987b\u8bf4\u9053\uff1a\u201c\u8d1e\u4e7e\u9053\u4eba\u662f\u4e2a\u4eba\u7269\u3002\u201d\u94c1\u73ca\u745a\u7eed\u9053\uff1a\u201c\u5f8c\u6765\u4ed6\u53c8\u5f00\u4e86\u4e00 \u5f20\u836f\u65b9\uff0c\u8981\u6211\u7ed9\u4ed6\u914d\u836f\u3002\u6211\u62ff\u4e86\u836f\u65b9\uff0c\u5230\u9547\u4e0a\u7684\u836f\u94fa\u53bb\u914d\uff0c\u90a3\u4e9b\u836f\u94fa\u836f\u6750\u4e0d\u9f50\uff0c\u4e0d\u662f\u7f3a\u8fd9\u6837 \u5c31\u662f\u7f3a\u90a3\u6837\uff0c\u6211\u8d70\u4e86\u51e0\u5bb6\uff0c\u597d\u5bb9\u6613\u628a\u836f\u65b9\u914d\u9f50\uff0c\u5ffd\u7136\u78b0\u5230\u59e8\u5a18\u524d\u6765\u627e\u6211\u3002\u201d\u94c1\u98de\u9f99\u201c\u5514\u201d\u4e86 \u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e45\u53bb\u4e0d\u56de\uff0c\u662f\u6211\u53eb\u5979\u8ffd\u4f60\u56de\u6765\u7684\u3002\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u6211\u5c06\u4e8b\u60c5\u5bf9\u59e8\u5a18\u8bf4\u4e86\uff0c \u548c\u59e8\u5a18\u540c\u53bb\u770b\u90a3\u8001\u9053\uff0c\u4e0d\u6599\u8001\u9053\u5df2\u4e0d\u89c1\u4e86\uff0c\u5374\u89c1\u4e24\u4e2a\u6c49\u5b50\u5728\u90a3\u91cc\u6253\u63a2\u8001\u9053\u7684\u8e2a\u8ff9\u3002\u4e00\u4e2a\u5e74 \u8001\uff0c\u4e00\u4e2a\u5e74\u8f7b\u3002\u4ed6\u4eec\u89c1\u4e86\u59e8\u5a18\uff0c\u6025\u5feb\u884c\u793c\uff0c\u8fd8\u95ee\u4f60\u8001\u5b89\u597d\u3002\u59e8\u5a18\u5ffd\u9053\uff1a\u201c\u91d1\u8001\u53c1\uff0c\u4f60\u548c\u6211\u51fa \u53bb\uff01\u201d\u94c1\u98de\u9f99\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5411\u7a46\u4e5d\u5a18\u65a5\u9053\uff1a\u201c\u4f60\u548c\u91d1\u5343  \u5e72\u7684\u597d\u4e8b\uff1f\u201d\u7a46\u4e5d\u5a18\u54ed\u9053\uff1a\u201c\u6211 \u53ea\u662f\u60f3\u8feb\u4ed6\u5410\u51fa\u810f\u7269\u800c\u5df2\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u597d\uff0c\u73ca\u745a\uff0c\u4f60\u518d\u8bf4\u3002\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u90a3\u4e24\u4eba\u8ddf\u6211 \u4eec\u8d70\u5230\u50fb\u9759\u4e4b\u5904\uff0c\u59e8\u5a18\u5411\u90a3\u8001\u5934\u8bf4\u9053\uff1a\u201c\u8001\u53c1\uff0c\u628a\u90a3\u9053\u58eb\u7684\u5251\u8c31\u4ea4\u51fa\u6765\uff1f\u201d\u90a3\u8001\u5934\u8d77\u521d\u63a8\u8bf4 \u6ca1\u6709\uff0c\u5f8c\u6765\u7ed9\u8feb\u5f97\u7d27\u4e86\uff0c\u8fd9\u624d\u627f\u8ba4\u3002\u201d\u7389\u7f57\u5239\u542c\u5230\u8fd9\u91cc\uff0c\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\uff0c\u51b7\u68ee\u68ee\u7684\u76ee\u5149\u5c04\u5728 \u94c1\u98de\u9f99\u9762\u4e0a\u3002 \u3000\u3000\u94c1\u98de\u9f99\u6012\u9053\uff1a\u201c\u7389\u7f57\u5239\u4f60\u6025\u4ec0\u9ebd\uff0c\u5251\u8c31\u662f\u4f60\u7684\u603b\u662f\u4f60\u7684\uff01\u201d\u7eed\u95ee\u94c1\u73ca\u745a\u9053\uff1a\u201c\u5f8c\u6765\u90a3\u4e2a \u91d1\u5343  \u628a\u5251\u8c31\u4ea4\u51fa\u6765\u6ca1\u6709\uff1f\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u8d77\u521d\u4ed6\u4e0d\u80af\uff0c\u59e8\u5a18\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u9053\u8d1e\u4e7e\u9053\u4eba\u662f\u4f55 \u7b49\u4eba\u7269\uff0c\u4ed6\u4ea4\u6e38\u5e7f\u9614\uff0c\u4f60\u628a\u4ed6\u5bb3\u6b7b\uff0c\u5c31\u60f3\u628a\u4ed6\u7684\u5251\u8c31\u5e26\u56de\u53bb\u5417\uff1f\u4f60\u4e0d\u6015\u4ed6\u7684\u670b\u53cb\u641c\u67e5\u5417\uff1f\u4f60 \u628a\u5251\u8c31\u7ed9\u6211\uff0c\u6211\u7ed9\u4f60\u4fdd\u7ba1\uff0c\u770b\u5b8c\u4e86\u518d\u4ea4\u56de\u7ed9\u4f60\uff0c\u8981\u4e0d\u7136\uff0c\u54fc\uff0c\u54fc\uff0c\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\u6211\u7a46\u4e5d\u5a18\u4e5f \u4e0d\u662f\u597d\u76f8\u4e0e\u7684\uff01\u201d\u90a3\u91d1\u8001\u5934\u7b11\u9053\uff1a\u201c\u4e5d\u5a18\uff0c\u90a3\u9ebd\u54b1\u4eec\u5c31\u6309\u7eff\u6797\u9053\u7684\u89c4\u77e9\uff0c\u4e00\u74e2\u6c34\u5927\u5bb6\u559d\u5566\uff01 \u8fd9\u5251\u8c31\u5148\u4ea4\u7ed9\u4f60\uff0c\u4e24\u4e2a\u6708\u5f8c\uff0c\u6211\u6765\u53d6\u56de\u3002\u201c\u59e8\u5a18\u62ff\u5230\u4e86\u5251\u8c31\uff0c\u5c31\u5fd9\u7740\u548c\u6211\u5230\u9644\u8fd1\u7684\u5c71\u5934\u53bb \u7ec3\u3002\u201d \u3000\u3000\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u628a\u8fd9\u4e8b\u60c5\u544a\u8bc9\u6211\uff1f\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u59e8\u5a18\u53eb\u6211\u4e0d\u8981\u8bf4\u7684\u3002\u5979\u7ec3\u4e86 \u51e0\u62db\uff0c\u50cf\u53d1\u73b0\u4e86\u7aa6\u7269\u4f3c\u7684\uff0c\u5bf9\u6211\u8bf4\uff1a\u8fd9\u662f\u5929\u4e0b\u7b2c\u4e00\u672c\u5947\u4e66\uff0c\u628a\u4e66\u4e0a\u7684\u5251\u672f\u7ec3\u4e86\uff0c\u53ef\u4ee5\u5929\u4e0b\u65e0 \u654c\u3002\u5979\u8bf4\uff1a\u73ca\u745a\uff0c\u54b1\u4eec\u5077\u5077\u7ec3\u4e86\u5427\uff0c\u53ef\u4e0d\u8981\u5bf9\u4f60\u7238\u7238\u8bf4\u3002\u6211\u60f3\uff1a\u672c\u4e8b\u591a\u5b66\u4e00\u70b9\u603b\u4e0d\u662f\u574f\u4e8b\uff0c \u4e00\u65f6\u80e1\u6d82\uff0c\u4e5f\u5c31\u7b54\u5e94\u5566\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u63d2\u53e3\u95ee\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u4eec\u4ee5\u5f8c\u6709\u6ca1\u6709\u89c1\u8fc7\u8d1e\u4e7e\u9053\u4eba\uff1f\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u8d1e\u4e7e\u9053\u4eba\u5728\u6e05 \u98ce\u5c71\u89c1\u5230\u5566\uff0c\u90a3\u5929\u4f60\u4eec\u4e0d\u662f\u4e5f\u5728\u5c71\u4e0a\u5417\uff1f\u201d\u94c1\u98de\u9f99\u53c8\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8d1e\u4e7e\u9053\u4eba\u7ea6\u6211\u5230\u5c71 \u4e0a\u76f8\u4f1a\uff0c\u53bb\u4e86\u53c8\u4e0d\u89c1\u4eba\uff0c\u60f3\u6765\u4e5f\u662f\u548c\u8fd9\u4e8b\u6709\u5173\u5566\u3002\u4f60\u8fd9\u8d31\u4eba\u4e3a\u4f55\u4e8b\u5230\u4e34\u5934\u90fd\u4e0d\u544a\u8bc9\u6211\u3002\u201d\u7a46 \u4e5d\u5a18\u4e0d\u6562\u56de\u7b54\u3002\u539f\u6765\u7a46\u4e5d\u5a18\u53d6\u4e86\u5251\u8c31\u4e4b\u5f8c\uff0c\u751a\u60f3\u636e\u4e3a\u5df2\u6709\uff0c\u4e0a\u6708\u94c1\u98de\u9f99\u518d\u8d74  \u5317\u8981\u53bb\u627e\u738b\u5609 \u80e4\uff0c\u91d1\u5343  \u6697\u4e2d\u6d3e\u9063\u515a\u7fbd\u5c06\u5bc6\u4fe1\u9001\u7ed9\u5979\uff0c\u8bf4\u63a2\u51fa\u8d1e\u4e7e\u9053\u4eba\u85cf\u533f\u5728\u6e05\u98ce\u5c71\u4e0a\uff0c\u6070\u597d\u94c1\u98de\u9f99\u4e5f\u6536 \u5230\u533f\u540d\u4fe1\uff0c\u7ea6\u4ed6\u5230\u6e05\u98ce\u5c71\u76f8\u4f1a\uff0c\u94c1\u98de\u9f99\u5c31\u5e26\u7a46\u4e5d\u5a18\u53bb\u4e86\u3002\u5f8c\u6765\u94c1\u73ca\u745a\u5c06\u7389\u7f57\u5239\u5f15\u6765\uff0c\u94c1\u98de\u9f99 \u5728\u5c71\u524d\u548c\u5979\u76f8\u6597\uff0c\u7a46\u4e5d\u5a18\u5374\u5728\u5c71\u5f8c\u53d1\u73b0\u4e86\u8d1e\u4e7e\u9053\u4eba\u533f\u85cf\u7684\u6d1e\u7a74\u3002 \u3000\u3000\u7389\u7f57\u5239\u542c\u5230\u8fd9\u91cc\uff0c\u771f\u76f8\u5df2\u7ecf\u5927\u767d\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u60f3\u8981\u6211\u7684\u5251\u8c31\u4e5f\u8fd8\u7f62\u4e86\uff0c\u4e3a\u4f55\u5374\u53c8\u628a \u8d1e\u4e7e\u5bb3\u6b7b\uff1f\u201d\u94c1\u98de\u9f99\u5706\u7741\u4e86\u773c\uff0c\u7a46\u4e5d\u5a18\u6025\u5fd9\u8fa9\u9053\uff1a\u201c\u6211\u5728\u77f3\u7a9f\u53d1\u73b0\u8d1e\u4e7e\u9053\u4eba\uff0c\u90a3\u65f6\u4ed6\u5df3\u5c06\u65ad \u6c14\uff0c\u4ed6\u8eab\u65c1\u8fd8\u7559\u6709\u98df\u7269\uff0c\u60f3\u662f\u6709\u4ec0\u9ebd\u4eba\u5728\u670d\u4f8d\u4ed6\uff0c\u53ef\u662f\u90a3\u65f6\u5374\u53ea\u6709\u4ed6\u4e00\u4eba\uff0c\u4ed6\u795e\u60c5\u6781\u4e3a\u75db \u82e6\uff0c\u793a\u610f\u53eb\u6211\u52a9\u4ed6\uff0c\u8ba9\u4ed6\u901f\u6b7b\u3002\u6211\u662f\u4e0d\u5f97\u5df2\u624d\u542c\u4ed6\u4e4b\u547d\u7684\u3002\u201d\u7a46\u4e5d\u5a18\u6240\u8bf4\u662f\u771f\uff0c\u53ef\u662f\u90a3\u65f6\u5979 \u5df2\u53e6\u6709\u6253\u7b97\u3002\u5979\u6015\u8d1e\u4e7e\u77e5\u9053\u5251\u8c10\u5728\u5979\u624b\u4e0a\uff0c\u53c8\u6015\u94c1\u98de\u9f99\u56de\u6765\u4e8b\u60c5  \u6f0f\uff0c\u6240\u4ee5\u624d\u6025\u5fd9\u5c06\u8d1e\u4e7e\u5f04 \u6b7b\u3002 \u3000\u3000\u94c1\u98de\u9f99\u76d8\u95ee\u5b8c\u5f8c\uff0c\u5fc3\u4e2d\u6012\u6781\uff0c\u4f46\u770b\u7740\u7231\u59be\u548c\u5973\u513f\u745f\u7f29\u7684\u6a21\u6837\uff0c\u53c8\u89c9\u6781\u5176\u96be\u8fc7\uff0c\u4e00\u9635\u9635\u5bd2 \u610f\u76f4\u900f\u5fc3\u5934\uff0c\u58f0\u8c03\u5ffd\u7136\u98a4\u6296\uff0c\u5148\u5411\u5973\u513f\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4f60\u628a\u5251\u8c31\u62ff\u51fa\u6765\u8fd8\u7ed9\u4eba\u5bb6\u3002\u201d\u94c1\u73ca\u745a \u9053\uff1a\u201c\u521a\u521a\u7ed9\u4eba\u52ab\u53bb\u4e86\uff01\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u5c31\u662f\u90a3\u4e2a\u91d1\u8001\u602a\u6765\u52ab\u7684\u5417\uff1f\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u662f\uff01\u201d \u94c1\u98de\u9f99\u604d\u7136\u609f\u9053\uff1a\u201c\u524d\u4e24\u5929\u91d1\u5343  \u6765\u627e\u6211\uff0c\u60f3\u6765\u4e5f\u4e0e\u6b64\u4e66\u6709\u5173\u4e86\u3002\u201d\u7389\u7f57\u5239\u542c\u5f97\u5251\u8c31\u53c8\u518d\u88ab \u52ab\uff0c\u9762\u8272\u4e00\u53d8\uff0c\u5c31\u8981\u53d1\u4f5c\u3002 \u3000\u3000\u94c1\u98de\u9f99\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u7389\u7f57\u5239\uff0c\u4f60\u7684\u5251\u8c31\u5305\u5728\u6211\u8eab\u4e0a\u4fbf\u662f\u3002\u8d70\u904d\u5929\u6daf\u6d77\u89d2\uff0c\u6211\u4e5f\u8981\u66ff\u4f60\u627e \u56de\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u597d\uff0c\u9a91\u7740\u9a74\u513f\u770b\u5531\u672c\uff0c\u8d70\u7740\u77a7\u5427\u3002\u201d\u610f\u4f3c\u72b9\u4e0d\u76f8\u4fe1\uff0c\u94c1\u98de\u9f99\u5374\u4e0d\u7406\u5979\uff0c \u4f38\u51fa\u624b\u638c\u8f7b\u629a\u5973\u513f\u7684\u5934\u53d1\uff0c\u5c31\u50cf\u5979\u7ae5\u5e74\u65f6\u5019\u4e00\u6837\uff0c\u94c1\u73ca\u745a\u63a5\u89e6\u4e86\u5979\u7236\u4eb2\u7684\u76ee\u5149\uff0c\u4e5f\u4e0d\u7981\u5bd2\u610f \u76f4\u900f\u5fc3\u5934\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u600e\u9ebd\u5566\uff1f\u201d \u3000\u3000\u94c1\u98de\u9f99\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u8dda\u513f\uff0c\u4f60\u4eca\u5e74\u5341\u4e5d\u5c81\u4e86\uff0c\u662f\u9ebd\uff01\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u5514\uff0c\u4f60\u8bf4\u8fd9\u5e72 \u5417\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u5df2\u7ecf\u4e0d\u662f\u5c0f\u4e4c\u513f\u5566\uff0c\u4f60\u73b0\u5728\u662f\u5df2\u7ecf\u957f\u4e86\u7fc5\u8180\uff0c\u53ef\u4ee5\u8fdc\u8d70\u9ad8\u98de\u5566\u3002\u201d\u94c1 \u73ca\u745a\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u6211\u6c38\u8fdc\u90fd\u60f3\u5728\u4f60\u8eab\u8fb9\u505a\u4f60\u7684\u5c0f\u9e1f\u513f\u3002\u201d\u94c1\u98de\u9f99\u9762\u8272\u4e00\u7aef\uff0c\u7a81\u7136\u628a\u5979\u63a8 \u5f00\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4ece\u4eca\u65e5\u8d77\uff0c\u4f60\u518d\u4e0d\u662f\u6211\u7684\u5973\u513f\uff0c\u4f60\u7ed9\u6211\u6eda\u51fa\u53bb\uff01\u4f60\u5728\u5916\u9762\uff0c\u4e5f\u4e0d\u51c6\u7528\u6211\u7684 \u540d\u5934\u62db\u6447\u3002\u201d\u94c1\u73ca\u745a\u8eab\u8eaf\u98a4\u6296\uff0c\u6b32\u54ed\u65e0\u6cea\uff0c\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u89ca\u89ce\u522b\u6d3e\u5251\u8c31\uff0c\u6b3a\u7792\u81ea\u5bb6\u8001\u7236\uff0c \u4e0d\u662f\u770b\u5728\u4f60\u5a18\u4efd\u4e0a\uff0c\u6211\u65e9\u628a\u4f60\u7684\u5c0f\u547d\u8981\u4e86\uff01\u201d\u94c1\u73ca\u745a\u6709\u751f\u4ee5\u6765\uff0c\u4ece\u672a\u53d7\u8fc7\u7236\u4eb2\u8fd9\u6837\u82db\u8d23\uff0c\u5979 \u77e5\u9053\u7236\u4eb2\u813e\u6c14\uff0c\u8bf4\u51fa\u7684\u8bdd\u7edd\u4e0d\u66f4\u6539\uff0c\u53c8\u89c1\u7389\u7f57\u5239\u6b6a\u7740\u773c\u775b\u770b\u5979\uff0c\u53c8\u662f\u7f9e\u6127\uff0c\u53c8\u662f\u6c14\u6124\uff0c\u8dea\u5728 \u5730\u4e0a\uff0c\u78d5\u4e86\u53c1\u4e2a\u54cd\u5934\uff0c\u51c4\u7136\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u4fdd\u91cd\uff01\u201d\u5934\u4e5f\u4e0d\u56de\uff0c\u53cd\u8eab\u8dd1\u51fa\u5927\u95e8\u53bb\u4e86\uff01 \u3000\u3000\u7389\u7f57\u5239\u5e73\u65e5\u867d\u7136\u6740\u4eba\u5982\u8349\uff0c\u89c1\u6b64\u60c5\u666f\uff0c\u4e5f\u4e0d\u89c9\u5fc3\u9178\uff0c\u5979\u521a\u624d\u770b\u94c1\u73ca\u745a\u745f\u7f29\u53ef\u601c\uff0c\u672c\u60f3\u51fa \u8a00\u76f8\u529d\uff0c\u53ef\u662f\u4e00\u65f6\u95f4\u5374\u8f6c\u4e0d\u8fc7\u53e3\u6765\uff0c\u5230\u4e86\u4ed6\u4eec\u7236\u5973\u51b3\u7edd\u4e4b\u5f8c\uff0c\u8981\u529d\u4e5f\u5df2\u7ecf\u8fdf\u4e86\u3002 \u3000\u3000\u94c1\u98de\u9f99\u628a\u5973\u513f\u9010\u8d70\u4e4b\u5f8c\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u53c8\u5411\u7a46\u4e5d\u5a18\u559d\u9053\uff1a\u201c\u8d31\u4eba\uff0c\u4f60\u8fc7\u6765\uff01\u201d\u7a46\u4e5d\u5a18\u5ffd\u7136 \u62ab\u53d1\u72c2\u7b11\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u8001\u5339\u592b\uff0c\u8fd9\u6761\u547d\u6211\u65e9\u60f3\u4e0d\u8981\u4e86\uff0c\u4f60\u6253\u6b7b\u6211\u5427\uff01\u201d\u94c1\u98de\u9f99\u559d\u9053\uff1a\u201c\u4f60 \u7a83\u53d6\u522b\u4eba\u5251\u8c31\uff0c\u8d25\u574f\u6211\u7684\u58f0\u540d\uff0c\u7f6a\u6709\u5e94\u5f97\uff0c\u6b7b\u6709\u9980\u8f9c\u3002\u4f60\u8fd8\u6709\u4ec0\u9ebd\u53ef\u57cb\u6028\u7684\uff1f\u201d\u7a46\u4e5d\u5a18\u72c2\u7b11 \u9053\uff1a\u201c\u5f53\u5e74\u6211\u7236\u4eb2\u5ba2\u6b7b\u5f02\u4e61\uff0c\u6211\u65e0\u94b1\u846c\u7236\uff0c\u624d\u8feb\u5f97\u5ac1\u4f60\u3002\u5ac1\u4e86\u4f60\u5f8c\uff0c\u4f60\u53c8\u5e76\u4e0d\u5c06\u6211\u5f53\u6b63\u5ba4\u770b \u5f85\u3002\u6211\u5728\u4f60\u9762\u524d\u88c5\u51fa\u7b11\u8138\uff0c\u4f60\u5f53\u6211\u662f\u6b22\u559c\u4f60\u9ebd\uff1f\u4f60\u6253\u6b7b\u6211\u6b63\u597d\uff0c\u8fd9\u6837\u7684\u65e5\u5b50\u6211\u4e5f\u4e0d\u613f\u8fc7 \u4e86\uff01\u201d\u539f\u6765\u7a46\u4e5d\u5a18\u5e7c\u968f\u7236\u4eb2\u5728\u6c5f\u6e56\u5356\u89e3\uff0c\u4e0d\u60ef\u62d8\u675f\u3002\u5ac1\u4e86\u94c1\u98de\u9f99\u5f8c\uff0c\u8001\u592b\u5c11\u59bb\uff0c\u767d\u53d1\u7ea2\u989c\u5df1 \u81ea\u4e0d\u886c\uff0c\u52a0\u4ee5\u94c1\u98de\u9f99\u6027\u60c5\u4e25\u5389\u602a\u50fb\uff0c\u5979\u66f4\u662f\u6291\u90c1\u5c11\u6b22\uff0c\u4e0d\u662f\u4e3a\u4e86\u754f\u60e7\u94c1\u98de\u9f99\u7684\u5389\u5bb3\uff0c\u5979\u65e9\u5df2 \u9003\u8dd1\u4e86\u3002\u8fd9\u6b21\u5979\u7a83\u53d6\u5251\u8c31\uff0c\u5c31\u662f\u60f3\u6697\u4e2d\u628a\u5251\u6cd5\u5b66\u6210\uff0c\u4ee4\u94c1\u98de\u9f99\u5236\u5979\u4e0d\u4f4f\u3002 \u3000\u3000\u94c1\u98de\u9f99\u7edd\u6599\u4e0d\u5230\u7a46\u4e5d\u5a18\u4f1a\u8bf4\u51fa\u8fd9\u4e00\u756a\u8bdd\u6765\uff0c\u4e00\u65f6\u95f4\u4e0d\u7981\u5446\u7740\uff0c\u770b\u5979\u989c\u5bb9\u7f8e\u8273\uff0c\u800c\u81ea\u5df1\u4e24 \u987b\u5982\u971c\uff0c\u4e5f\u771f\u602a\u4e0d\u5f97\u5979\u6709\u90a3\u6837\u7684\u5fc3\u4e8b\uff0c\u4ed6\u4e3e\u8d77\u7684\u624b\u638c\uff0c\u505c\u5728\u534a\u7a7a\uff0c\u7adf\u81ea\u5288\u4e0d\u4e0b\u53bb\u3002\u7389\u7f57\u5239\u7a81 \u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u628a\u94c1\u98de\u9f99\u7684\u624b\u62c9\u5f00\u3002\u94c1\u98de\u9f99\u957f\u53f9\u4e00\u58f0\uff0c\u6325\u624b\u8bf4\u9053\uff1a\u201c\u4f60\u8d70\u5427\uff01\u6c38\u4e0d\u8981\u518d\u89c1 \u6211\uff01\u201d\u7a46\u4e5d\u5a18\u7b11\u58f0\u500f\u505c\uff0c\u4e5f\u8dea\u5728\u5730\u4e0a\u78d5\u4e86\u53c1\u4e2a\u54cd\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u7237\uff0c\u4f60\u4fdd\u91cd\uff01\u201d\u4e5f\u5b66\u94c1\u73ca\u745a \u4e00\u6837\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u8dd1\u51fa\u5927\u95e8\u53bb\u4e86\u3002 \u3000\u3000\u94c1\u98de\u9f99\u6006\u7136\u4f24\u6000\uff0c\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u662f\u771f\u6b63\u7684\u8001\u4e86\uff0c\u4ed6\u501a\u5728\u5047\u5c71\u77f3\u4e0a\uff0c\u597d\u50cf\u5927\u75c5\u521d\u6108\u4e00\u822c\uff0c \u53f9\u53e3\u6c14\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u4e5f\u8be5\u8d70\u4e86\u3002\u201d \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u5353\u4e00\u822a\u5148\u884c\u544a\u8f9e\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f46\u613f\u5e73\u5b89\u5230\u4eac\u3002\u201d\u5353\u4e00\u822a\u4e5f\u9053\uff1a\u201c\u4f46\u613f\u4f60 \u80fd\u6536\u56de\u5251\u8c31\u3002\u201d\u738b\u7167\u5e0c\u548c\u5b5f\u79cb\u971e\u4e5f\u4e00\u540c\u8fc7\u6765\u5411\u94c1\u98de\u9f99\u9053\u522b\u3002\u94c1\u98de\u9f99\u9053\uff1a\u201c\u8d24\u4f84\uff0c\u4f60\u56de\u53bb\u4ee3\u6211 \u5411\u4ee4\u5c0a\u8bf7\u7f6a\uff0c\u6211\u4ee5\u524d\u505a\u4e8b\u592a\u9c81\u83bd\u4e86\u3002\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u4e0d\u6562\u3002\u201d\u94c1\u98de\u9f99\u987f\u4e86\u4e00\u987f\uff0c\u51c4\u51c9\u7b11\u9053\uff1a \u201c\u8fd9\u4f4d\u5b5f\u5c0f\u59d0\u6bd4\u73ca\u745a\u597d\u5f97\u591a\uff0c\u4f60\u4eec\u7ecf\u8fc7\u8fd9\u573a\u98ce\u6ce2\uff0c\u5b9a\u80fd\u767d\u5934\u5055\u8001\u3002\u201d\u738b\u7167\u5e0c\u5fc3\u4e2d\u4e00\u677e\uff0c\u77e5\u9053 \u8fd9\u8001\u4eba\u4ee5\u5f8c\u518d\u4e0d\u4f1a\u5411\u81ea\u5df2\u7ea0\u7f20\u4e86\uff0c\u8fd9\u5239\u90a3\u95f4\uff0c\u4ed6\u65e2\u6709\u559c\u60a6\u4e4b\u60c5\uff0c\u53c8\u6709\u601c\u60af\u4e4b\u5ff5\uff0c\u559c\u60a6\u7684\u662f\uff1a \u5b5f\u79cb\u971e\u679c\u7136\u662f\u5bf9\u81ea\u5df1\u771f\u60c5\uff1a\u601c\u60af\u7684\u662f\uff1a\u8fd9\u8001\u4eba\u672a\u514d\u592a\u5b64\u72ec\u4e86\u3002 \u3000\u3000\u738b\u7167\u5e0c\u9053\uff1a\u201c\u6211\u987a\u4fbf\u9001\u5353\u5144\u4e00\u7a0b\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u7389\u7f57\u5239\uff0c\u4f60\u5462\uff0c\u4f60\u4e0d\u8d70\u9ebd\uff1f\u201d\u7389\u7f57\u5239 \u7b11\u9053\uff1a\u201c\u6211\u603b\u4e0d\u80fd\u53eb\u4f60\u4e00\u4e2a\u4eba\u53bb\u66ff\u6211\u53d6\u56de\u5251\u8c31\u5440\uff01\u201d\u94c1\u98de\u9f99\u602b\u7136\u8bf4\u9053\uff1a\u201c\u6211\u65e2\u7136\u7b54\u5e94\u4e86\u4f60\uff0c \u8fd9\u5c31\u662f\u6211\u7684\u4e8b\u60c5\uff0c\u4f60\u4ee5\u4e3a\u6211\u4e00\u4e2a\u4eba\u53d6\u4e0d\u56de\u6765\u9ebd\uff01\u201d\u7389\u7f57\u5239\u6697\u7b11\u8fd9\u8001\u4eba\u597d\u80dc\u5f97\u7d27\uff0c\u8bf4\u9053\uff1a\u201c\u94c1 \u8001\u82f1\u96c4\u51fa\u9a6c\uff0c\u6211\u662f\u7edd\u5bf9\u653e\u5fc3\u201c\u4f46\u4f60\u4e00\u4e2a\u4eba\u51fa\u8fdc\u95e8\uff0c\u603b\u4e0d\u514d\u5bc2\u5bde\uff0c\u6211\u4f34\u5728\u4f60\u8eab\u8fb9\uff0c\u66ff\u4f60\u89e3\u89e3\u95f7 \u4e0d\u597d\u9ebd\uff01\u201d\u94c1\u98de\u9f99\u7a81\u7136\u542c\u5230\u7389\u7f57\u5239\u79f0\u8d5e\u81ea\u5df2\uff0c\u751a\u4e3a\u9ad8\u4e0e\uff0c\u542c\u4e86\u5f8c\u534a\u6bb5\u8bdd\uff0c\u6709\u5982\u5973\u513f\u5bf9\u7236\u4eb2\u8bf4 \u8bdd\u4e00\u822c\uff0c\u66f4\u89c9\u53d7\u7528\u3002\u94c1\u98de\u9f99\u867d\u7136\u597d\u80dc\uff0c\u4f46\u5374\u559c\u6b22\u771f\u6709\u672c\u4e8b\u3001\u813e\u6c14\u76f4\u7387\u7684\u4eba\uff0c\u4ed6\u548c\u7389\u7f57\u5239\u7ecf\u8fc7 \u4e24\u573a\u6076\u6597\uff0c\u53cd\u800c\u5316\u654c\u4e3a\u53cb\uff0c\u5f7c\u6b64\u656c\u91cd\u3002\u5f53\u4e0b\u94c1\u98de\u9f99\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u4e0d\u662f\u6211\u7684\u5973\u513f\u3002\u201d\u7389 \u7f57\u5239\u9053\uff1a\u201c\u6211\u5c31\u505a\u4f60\u7684\u5973\u513f\u597d\u4e86\u3002\u201d\u76c8\u76c8\u4e0b\u62dc\uff0c\u53eb\u58f0\u201c\u4e49\u7236\u201d\u3002\u94c1\u98de\u9f99\u8fde\u5fd9\u628a\u5979\u6276\u8d77\uff0c\u8bf4 \u9053\uff1a\u201c\u8fd9\u600e\u9ebd\u6562\u5f53\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u4e0d\u80af\u6536\u6211\u505a\u4e49\u5973\uff0c\u4e00\u5b9a\u662f\u602a\u6211\u9a82\u8fc7\u4f60\u53c8\u6253\u8fc7\u4f60\u4e86\u3002\u6211 \u8bf4\u5440\uff0c\u4f60\u82e5\u60f3\u51fa\u6c14\uff0c\u8fd8\u662f\u505a\u6211\u7684\u4e49\u7236\u597d\uff0c\u4f60\u505a\u4e86\u6211\u7684\u4e49\u7236\uff0c\u4fbf\u53ea\u6709\u4f60\u9a82\u6211\u6ca1\u6709\u6211\u9a82\u4f60\u7684 \u4e86\u3002\u201d\u94c1\u98de\u9f99\u88ab\u5979\u5f15\u5f97\u5927\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u8fd9\u6837\uff0c\u6211\u4e0d\u6536\u4f60\u505a\u4e49\u5973\u53cd\u800c\u663e\u5f97\u6211\u5c0f\u5668\u4e86\u3002\u53ef\u60dc \u6211\u6ca1\u6709\u4ec0\u9ebd\u89c1\u9762\u793c\u7ed9\u4f60\uff0c\u4f60\u7684\u6b66\u827a\u6bd4\u6211\u8fd8\u9ad8\uff0c\u6211\u662f\u6ca1\u6709\u4ec0\u9ebd\u53ef\u4ee5\u7ed9\u4f60\u7684\u4e86\u3002\u53ea\u662f\u6211\u5728\u5185\u529f\u4fee \u4e0a\u8fd8\u6709\u4e00\u4e9b\u5fc3\u5f97\uff0c\u5c06\u6765\u53ef\u4ee5\u548c\u4f60\u7814\u8ba8\u3002\u201d\u7389\u7f57\u5239\u4e4b\u80af\u62dc\u94c1\u98de\u9f99\u505a\u4e49\u7236\uff0c\u4e00\u534a\u662f\u7531\u65bc\u559c\u6b22\u4ed6\u7684 \u6027\u683c\uff0c\u548c\u81ea\u5df2\u4e00\u6a21\u4e00\u6837\uff1a\u4e00\u534a\u662f\u53ef\u601c\u4ed6\u7684\u5b64\u72ec\uff0c\u672c\u4e0d\u60f3\u5b66\u4ed6\u7684\u72ec\u95e8\u6b66\u529f\uff0c\u4e0d\u60f3\u4ed6\u7adf\u6168\u7136\u4ee5\u6570 \u5341\u5e74\u4fee\u4e60\u7684\u5185\u529f\u5fc3\u5f97\u76f8\u4f20\uff0c\u5374\u4e4b\u4e0d\u606d\uff0c\u4e5f\u53ea\u597d\u62dc\u8c22\u4e86\u3002 \u3000\u3000\u94c1\u98de\u9f99\u548c\u7389\u7f57\u5239\u9001\u738b\uff0e\u5353\u7b49\u4eba\u51fa\u5e84\uff0c\u7389\u7f57\u5239\u628a\u5c71\u5be8\u7684\u4e8b\u60c5\u6258\u738b\u7167\u5e0c\u6599\u7406\uff0c\u5e76\u7279\u522b\u6073\u8bf7\u5b5f \u79cb\u971e\u66ff\u5979\u5e26\u9886\u5973\u5175\uff0c\u76c2\u79cb\u971e\u4e5f\u7b54\u5e94\u4e86\u3002\u7389\u7f57\u5239\u53c8\u4e00\u6b21\u548c\u5353\u4e00\u822a\u9053\u522b\uff0c\u5fc3\u4e2d\u66f4\u89c9\u4e0d\u820d\u3002 \u3000\u3000\u9001\u8d70\u4f17\u4eba\u4e4b\u5f8c\uff0c\u5df2\u5c06\u4e2d\u5348\u3002\u94c1\u98de\u9f99\u548c\u7389\u7f57\u5239\u56de\u5bb6\u6b47\u606f\uff0c\u94c1\u98de\u9f99\u5ffd\u7136\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u90a3\u5353\u4e00 \u822a\u4e00\u526f\u516c\u5b50\u54e5\u513f\u813e\u6c14\uff0c\u6211\u771f\u5947\u602a\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u548c\u4ed6\u90a3\u9ebd\u76f8\u597d\uff1f\u201d\u7389\u7f57\u5239\u4e00\u7b11\u4e0d\u7b54\uff0c\u5916\u9762\u5e84\u4e01\u5ffd \u7136\u9001\u8fdb\u4e86\u4e00\u4e2a\u9ed1\u8272\u7684\u62dc\u5323\u6765\uff01 \u3000\u3000\u94c1\u98de\u9f99\u89c1\u4e86\u9ed1\u8272\u62dc\u5323\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u8fd9\u4eba\u600e\u7684\u5982\u6b64\u65e0\u793c\u3002\u201d\u4e00\u822c\u76db\u62dc\u5e16\u7684\u5323 \u5b50\uff0c\u4e0d\u662f\u63cf\u91d1\uff0c\u4fbf\u662f\u7ea2\u6728\uff0c\u53d6\u5176\u559c\u5e86\u4e4b\u610f\uff0c\u7edd\u5c11\u7528\u9ed1\u6f06\u7684\u3002\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4e14\u770b\u4e86\u518d\u8bf4\u3002\u201d\u5c06 \u62dc\u5323\u6253\u5f00\uff0c\u628a\u5e16\u5b50\u62ff\u51fa\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u7684\u4e43\u662f\uff1a\u6b66\u5f53\u5c71\u9ec4\u53f6\u9053\u4eba\u3001\u7ea2\u4e91\u9053\u4eba\u7387\u95e8\u5f92\u62dc\u8c12\u3002\u94c1\u98de \u9f99\u5947\u9053\uff1a\u201c\u6b66\u5f53\u4e94\u8001\uff0c\u4e07\u91cc\u8fdc\u6765\uff0c\u627e\u6211\u4f5c\u751a\uff1f\u4ed6\u4eec\u81ea\u6043\u662f\u6b66\u6797\u6b63\u5b97\uff0c\u4e00\u5411\u628a\u6211\u5f53\u4f5c\u90aa\u9b54\u5916 \u9053\uff0c\u4f55\u4ee5\u4eca\u65e5\u5982\u6b64\u606d\u656c\u6765\u4e86\uff01\u201d\u5f53\u4e0b\u4f20\u8bdd\u8bf7\u8fdb\u3002 \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u5728\u6b66\u5f53\u4e94\u8001\u4e2d\u6392\u884c\u7b2c\u4e8c\uff0c\u7ea2\u4e91\u9053\u4eba\u6392\u884c\u7b2c\u53c1\uff0c\u8f88\u4efd\u4e4b\u5c0a\uff0c\u5728\u6b66\u5f53\u6d3e\u4e2d\u4ec5\u5439\u65bc\u7d2b \u9633\u9053\u957f\u3002\u94c1\u98de\u9f99\u6614\u5e74\u66fe\u4e0e\u6b66\u5f53\u6d3e\u4e2d\u6392\u884c\u7b2c\u56db\u7684\u767d\u77f3\u9053\u4eba\u6bd4\u638c\uff0c\u80dc\u4e86\u4e00\u62db\uff0c\u4ed6\u4eec\u4e8c\u4eba\u90fd\u4e0d\u5fc3 \u670d\u3002\u94c1\u98de\u9f99\u89c1\u4e86\u4ed6\u4eec\u7684\u62dc\u5e16\uff0c\u7591\u5fc3\u5927\u8d77\uff0c\u4e0d\u77e5\u4ed6\u4eec\u6765\u610f\u662f\u597d\u662f\u574f\uff0c\u795e\u60c5\u9887\u663e\u7d27\u5f20\uff0c\u7389\u7f57\u5239\u7ad9 \u5728\u4e00\u65c1\uff0c\u5fae\u5fae\u53d1\u7b11\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u5927\u95e8\u5f00\u5904\uff0c\u9ec4\u53f6\u9053\u4eba\u4e0e\u7ea2\u4e91\u9053\u4eba\u5e76\u80a9\u8d70\u4e0a\u53f0\u9636\uff0c\u94c1\u98de\u9f99\u8d77\u7acb\u62f1\u624b\u9053\uff1a\u201c\u5341\u5e74 \u4e0d\u89c1\uff0c\u4e24\u4f4d\u9053\u7237\u8fd8\u662f\u5065\u94c4\u5982\u6614\uff0c\u7d2b\u9633\u9053\u957f\u53ef\u597d\u9ebd\uff1f\u201d\u9ec4\u53f6\u9053\u4eba\u51c4\u7136\u8bf4\u9053\uff1a\u201c\u655d\u5e08\u5144\u6708\u524d\u5df2\u7fbd \u5316\u767b\u4ed9\u53bb\u4e86\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u4e0e\u9ec4\u53f6\u9053\u4eba\u7b49\u56db\u4e2a\u5e08\u5f1f\u867d\u7136\u9887\u6709\u5acc\u9699\uff0c\u5bf9\u7d2b\u9633\u9053\u4eba\u5374\u662f\u5fc3\u60a6\u8bda\u670d\u3002 \u8fd9\u65f6\u4ed6\u624d\u77e5\u9053\u9ec4\u53f6\uff0c\u7ea2\u4e91\u4e8c\u4eba\u9001\u9ed1\u8272\u62dc\u5323\u7684\u9053\u7406\u3002\u4e0d\u7981\u8001\u6cea\u6f78\u7136\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u771f\u662f\u610f\u60f3\u4e0d \u5230\uff0c\u4ece\u6b64\u6b66\u6797\u4e2d\u518d\u4e5f\u6ca1\u6709\u5a01\u5fb7\u8db3\u4ee5\u670d\u4eba\u7684\u957f\u8005\u4e86\u3002\u201d\u8fd9\u8bdd\u660e\u8d5e\u7d2b\u9633\u9053\u4eba\uff0c\u9ec4\u53f6\uff0e\u7ea2\u4e91\u542c\u4e86\uff0c \u5374\u6709\u70b9\u4e0d\u5927\u8212\u670d\u3002 \u3000\u3000\u94c1\u98de\u9f99\u671d\u5357\u8fb9\u62dc\u4e86\u53c1\u62dc\uff0c\u731b\u7136\u60f3\u8d77\uff1a\u6b66\u5f53\u6d3e\u4e43\u662f\u5f53\u4eca\u6b66\u6797\u4e2d\u7684\u6cf0\u5c71\u5317\u6597\uff0c\u638c\u95e8\u7684\u957f\u8001\u6b7b \u4e86\uff0c\u5fc5\u987b\u63a8\u5b9a\u7ee7\u627f\u4e4b\u4eba\uff0c\u800c\u4e14\u4e5f\u5fc5\u5b9a\u6709\u8bb8\u591a\u5f8c\u4e8b\u987b\u8981\u6599\u7406\uff0c\u8fd9\u9ec4\u53f6\u7ea2\u4e91\u4e8c\u4eba\uff0c\u5982\u4f55\u80fd\u62bd\u7a7a\u5230 \u6b64\u3002\u96be\u9053\u4ed6\u4eec\u4e3a\u4e86\u6e05\u7406\u672c\u95e8\u7684\u6069\u6028\u7ea0\u7eb7\uff0c\u5148\u627e\u81ea\u5df1\u7b97\u5e10\u9ebd\uff1f\u201d\u4f46\u7ec6\u4e00\u60f3\u6765\uff0c\u5374\u53c8\u65e0\u6b64\u9053\u7406\uff0c \u4e0d\u7981\u95ee\u9053\uff1a\u201c\u4e24\u4f4d\u9053\u957f\u5230\u6b64\uff0c\u6709\u4f55\u89c1\u6559\uff1f\u201d\u9ec4\u53f6\u9053\u4eba\u6e38\u76ee\u56db\u987e\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6b63\u6709\u4e24\u4ef6\u4e8b\u60c5 \u8bf7\u95ee\uff0c\u7b2c\u4e00\u4ef6\u662f\uff1a\u655d\u6d3e\u7684\u5f1f\u5b50\u5353\u4e00\u822a\u53ef\u5728\u5e9c\u4e0a\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u63d2\u53e3\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u627e\u5353\u4e00\u822a\u505a\u4ec0 \u9ebd\uff1f\u8981\u7b49\u4ed6\u5954\u4e27\u5417\uff1f\u201d \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u6a2a\u4e86\u7389\u7f57\u5239\u4e00\u773c\uff0c\u4ed6\u77e5\u9053\u94c1\u98de\u9f99\u6709\u4e00\u4e2a\u5973\u513f\u540d\u53eb\u94c1\u73ca\u745a\uff0c\u751a\u4e3a\u9a84\u7eb5\uff0c\u53ea\u9053\u7389\u7f57 \u5239\u4fbf\u662f\u5979\uff0c\u6697\u7b11\u5979\u6ca1\u6709\u5bb6\u6559\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u655d\u6d3e\u5949\u7d2b\u9633\u957f\u8001\u7684\u9057\u547d\uff0c\u7acb\u5353\u4e00\u822a\u4e3a\u638c\u95e8\u5f1f\u5b50\uff0c \u6211\u4eec\u7279\u5730\u6765\u63a5\u4ed6\u56de\u5c71\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u542c\u4e86\u53c8\u559c\u53c8\u60ca\uff0c\u559c\u7684\u662f\uff1a\u5353\u4e00\u822a\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5c45\u7136\u4f1a\u88ab\u7acb\u4e3a\u638c\u95e8\uff0c\u4e00\u8dc3\u4fbf\u6210\u4e86\u6b66\u6797 \u4e2d\u7684\u9886\u8896\uff1a\u60ca\u7684\u662f\uff1a\u81ea\u5df2\u4e0e\u6b66\u5f53\u6d3e\u7ed3\u6709\u6881\u5b50\uff0c\u82e5\u4ed6\u6210\u4e86\u638c\u95e8\uff0c\u53ea\u6050\u4ee5\u5f8c\u66f4\u96be\u63a5\u8fd1\u3002 \u3000\u3000\u94c1\u98de\u9f99\u89c1\u9ec4\u53f6\u9053\u4eba\u795e\u60c5\u5028\u50b2\uff0c\u4e5f\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u6765\u5f97\u771f\u4e0d\u51d1\u5de7\uff0c\u5353\u4e00\u822a\u521a\u521a\u4ece\u8fd9\u91cc\u51fa \u53bb\u3002\u201d\u4ed6\u4ee5\u4e3a\u9ec4\u53f6\u9053\u4eba\u5fc5\u5b9a\u7acb\u5373\u544a\u8f9e\uff0c\u51fa\u95e8\u53bb\u8ffd\uff0c\u4e0d\u6599\u9ec4\u53f6\u7ea2\u4e91\u4e8c\u4eba\u5374\u751a\u4e3a\u9547\u5b9a\uff0c\u8bf4\u9053\uff1a \u201c\u662f\u9ebd\u4e86\u90a3\u9ebd\u6211\u4eec\u5728\u8fd9\u91cc\u7b49\u4ed6\u4e00\u4f1a\u3002\u201d\u5750\u4e86\u4e0b\u6765\uff0c\u94c1\u98de\u9f99\u8d77\u521d\u5927\u60d1\u4e0d\u89e3\uff0c\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5ffd\u7136\u660e \u767d\u3002 \u3000\u3000\u90a3\u62dc\u5e16\u4e0a\u5199\u7684\u662f\u201c\u9ec4\u53f6\u9053\u4eba\u7ea2\u4e91\u9053\u4eba\u7387\u5f92\u62dc\u8c12\u3002\u201d\u73b0\u5728\u6765\u7684\u5374\u4ec5\u662f\u9ec4\u53f6\u7ea2\u4e91\u4e8c\u4eba\uff0c\u90a3\u9ebd \u60f3\u5fc5\u8fd8\u6709\u6b66\u5f53\u6d3e\u7684\u95e8\u4eba\u5728\u5f8c\u9762\u4e86\u3002\u8fce\u63a5\u4e00\u6d3e\u638c\u95e8\uff0c\u4e43\u662f\u6781\u4e3a\u9686\u91cd\u4e4b\u4e8b\uff0c\u8fd9\u4e24\u4eba\u662f\u5353\u4e00\u822a\u7684\u5e08 \u53d4\uff0c\u5c06\u6765\u662f\u6276\u52a9\u4ed6\u7684\uff0c\u6765\u6b64\u4e43\u662f\u4f20\u4e0b\u9057\u547d\uff0c\u4e0d\u662f\u540c\u638c\u95e8\u53c2\u89c1\uff1a\u53e6\u5916\u5fc5\u5b9a\u8981\u6709\u540c\u8f88\u7684\u5e08\u5144\u5f1f\u524d \u6765\u606d\u8fce\u3002\u94c1\u98de\u9f99\u60f3\u8d77\u4e86\u6b66\u6797\u89c4\u77e9\uff0c\u4e0d\u89c9\u6697\u7b11\u81ea\u5df2\u7cca\u6d82\uff0c\u5f8c\u9762\u65e2\u6709\u6b66\u5f53\u95e8\u4eba\uff0c\u90a3\u9ebd\u5353\u4e00\u822a\u51fa \u53bb\uff0c\u5fc5\u5b9a\u4f1a\u7ed9\u4ed6\u4eec\u622a\u7740\uff0c\u602a\u4e0d\u5f97\u8fd9\u4e24\u4e2a\u8001\u9053\u8981\u5750\u5728\u8fd9\u91cc\u7b49\u5019\u4e86\u3002 \u3000\u3000\u4f46\u94c1\u98de\u9f99\u5fc3\u4e2d\u5c1a\u6709\u7591\u56e2\uff0c\u5f53\u4e0b\u53c8\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u8bf7\u95ee\u4e24\u4f4d\u9053\u957f\uff0c\u6d88\u606f\u4f55\u4ee5\u5982\u6b64\u7075\u901a\uff0c\u77e5\u9053 \u5353\u4e00\u822a\u66fe\u5230\u5bd2\u820d\uff1f\u201d\u9ec4\u53f6\u9053\u4eba\u6273\u8138\u4e0d\u7b54\uff0c\u5374\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u6709\u7b2c\u4e8c\u4ef6\u4e8b\u8bf7\u6559\u3002\u201d \u3000\u3000\u94c1\u98de\u9f99\u751a\u4e3a\u751f\u6c14\uff0c\u5927\u58f0\u9053\uff1a\u201c\u8bf7\u8bf4\uff01\u201d\u9ec4\u53f6\u9053\u4eba\u9053\uff1a\u201c\u8d1e\u4e7e\u9053\u4eba\u662f\u600e\u9ebd\u6b7b\u7684\uff1f\u201d\u94c1\u98de\u9f99 \u8df3\u4e86\u8d77\u6765\uff0c\u56b7\u9053\uff1a\u201c\u54fc\uff0c\u90a3\u65e5\u7684\u533f\u540d\u4fe1\u662f\u4f60\u5199\u7684\u4e86\uff1f\u201d\u9ec4\u53f6\u9053\u4eba\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d\u94c1\u98de\u9f99\u51b7\u7b11 \u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u4e43\u662f\u5931\u7ea6\u4e86\uff01\u201d\u9ec4\u53f6\u9053\u4eba\u9053\uff1a\u201c\u73b0\u5728\u6765\u4e5f\u8fd8\u672a\u665a\uff01\u201d \u3000\u3000\u539f\u6765\u9ec4\u53f6\u548c\u7ea2\u4e91\u4e8c\u4eba\u7387\u7b2c\u4e8c\u4ee3\u516d\u540d\u5f1f\u5b50\u6765\u63a5\u5353\u4e00\u822a\uff0c\u5f53\u7136\u662f\u8981\u5148\u5230  \u5317\u5353\u5bb6\uff0c\u4e0d\u6599\u4e00\u5230 \u9655\u5317\uff0c\u5ffd\u65bc\u65e0\u610f\u4e4b\u4e2d\u5728\u5ba2\u5bd3\u89c1\u4e86\u8d1e\u4e7e\u9053\u4eba\u6240\u7559\u4e0b\u6765\u7684\u6697\u8bb0\uff0c\u77e5\u4ed6\u53d7\u4e86\u6697\u7b97\uff0c\u73b0\u5728\u6e05\u98ce\u5c71\u4e0a\u517b \u4f24\u3002\u6b66\u5f53\u95e8\u4eba\u904d\u5e03\u5404\u5730\uff0c\u53e6\u5916\u53c8\u6709\u5f53\u5730\u5f1f\u5b50\u8d76\u6765\u5411\u9ec4\u53f6\u9053\u4eba\u62a5\u544a\uff0c\u8bf4\u662f\u53d1\u73b0\u4e86\u94c1\u98de\u9f99\u7684\u8e2a \u8ff9\uff0c\u4f4f\u5728\u5c0f\u9547\u7684\u4e00\u5bb6\u5ba2\u5e97\u4e2d\u3002\u8d1e\u4e7e\u9053\u4eba\u548c\u6b66\u5f53\u4e94\u8001\u4e43\u662f\u81f3\u4ea4\uff0c\u9ec4\u53f6\u9053\u4eba\u7acb\u5373\u8d76\u5230\u5c71\u4e0a\uff0c\u5176\u65f6 \u8d1e\u4e7e\u9053\u4eba\u5df2\u4e0d\u80fd\u8a00\u8bed\uff0c\u9ec4\u53f6\u9053\u4eba\u95ee\u4ed6\u8be6\u60c5\uff0c\u4ed6\u53ea\u80fd\u7528\u624b\u6307\u5728\u5730\u4e0a\u5212\u9053\uff1a\u95ee\u94c1\u98de\u9f99\u3002\u8d1e\u4e7e\u9053\u4eba \u66fe\u628a\u8be6\u60c5\u544a\u8bc9\u4e86\u94c1\u73ca\u745a\uff0c\u4ee5\u4e3a\u94c1\u73ca\u745a\u5fc5\u5b9a\u544a\u8bc9\u7236\u4eb2\uff0c\u6240\u4ee5\u624d\u53eb\u9ec4\u53f6\u53bb\u95ee\u94c1\u98de\u9f99\u3002\u5c82\u77e5\u9ec4\u53f6\u8bef \u4f1a\u4e86\u610f\u601d\uff0c\u7adf\u4ee5\u4e3a\u8d1e\u4e7e\u9053\u4eba\u4e43\u662f\u94c1\u98de\u9f99\u5bb3\u6b7b\u7684\uff0c\u5f53\u65f6\u770b\u8d1e\u4e7e\u4f24\u52bf\uff0c\u77e5\u9053\u5df2\u662f\u65e0\u6cd5\u6551\u6cbb\uff0c\u53ea\u597d \u6c14\u5fe1\u51b2\u7684\u8d76\u4e86\u56de\u6765\uff0c\u628a\u7ea6\u4f1a\u7684\u533f\u540d\u4fe1\u9001\u5230\u94c1\u98de\u9f99\u6240\u5c45\u7684\u5ba2\u5bd3\u3002\u7ea6\u4ed6\u5230\u6e05\u98ce\u5c71\u4e0a\uff0c\u597d\u5728\u8d1e\u4e7e\u9053 \u4eba\u9057\u4f53\u4e4b\u524d\uff0c\u95ee\u7f6a\u590d\u4ec7\u3002\u9ec4\u53f6\u9053\u4eba\u6240\u4ee5\u8981\u533f\u540d\u7684\u539f\u56e0\uff0c\u4e43\u662f\u6050\u9632\u94c1\u98de\u9f99\u5bb3\u6015\u6b66\u5f53\u4e94\u8001\uff0c\u4e0d\u6562 \u524d\u6765\u3002\u9ec4\u53f6\u9053\u4eba\u9001\u51fa\u533f\u540d\u4fe1\u5f8c\uff0c\u672c\u8be5\u8d74\u7ea6\uff0c\u4e0d\u6599\u4fe1\u529b\u9001\u51fa\uff0c\u53c8\u5f97\u5230\u5f53\u5730\u5f1f\u5b50\u7684\u62a5\u544a\uff0c\u8bf4\u662f\u5353 \u5bb6\u4e0d\u77e5\u600e\u7684\uff0c\u7a81\u7136\u5c01\u4e86\u5927\u95e8\uff0c\u5353\u5e9c\u7684\u5bb6\u4eba\u7eb7\u7eb7\u5916\u51fa\uff0c\u800c\u4e14\u90fd\u662f\u643a\u6709\u884c\u674e\uff0c\u770b\u6765\u5b9a\u6709\u975e\u5e38\u53d8\u6545 \u53d1\u751f\u3002\u9ec4\u53f6\u9053\u4eba\u4e00\u60f3\uff1a\u8d1e\u4e7e\u9053\u4eba\u4e4b\u4e8b\uff0c\u4ee5\u5f8c\u8fd8\u53ef\u5904\u7406\uff1a\u63a5\u5353\u4e00\u822a\u7684\u4e8b\uff0c\u5374\u662f\u6700\u4e3a\u7d27\u8981\uff0c\u8f7b\u91cd \u6743\u8861\uff0c\u4e5f\u987e\u4e0d\u5f97\u5931\u7ea6\u4e86\u3002 \u3000\u3000\u9ec4\u53f6\u5230\u4e86\u5353\u5bb6\uff0c\u5176\u65f6\u5353\u4e00\u822a\u5df2\u88ab\u6349\u5230\u5ef6\u5b89\u5e9c\u53bb\u4e86\u3002\u5230\u9ec4\u53f6\u8d76\u5230\u5ef6\u5b89\u5e9c\u65f6\uff0c\u5353\u4e00\u822a\u53c8\u5df2\u88ab \u6551\u51fa\uff0c\u8fd9\u6837\u8f97\u8f6c\u5bfb\u8bbf\uff0c\u5230\u5f8c\u6765\u8bbf\u51fa\u4e86\u5353\u4e00\u822a\u4e4b\u88ab\u6355\u4e0e\u738b\u7167\u5e0c\u6709\u5173\uff0c\u65bc\u662f\u6b66\u5f53\u4e00\u4f17\uff0c\u53c8\u5230\u74e6\u7a91 \u5821\u53bb\u627e\u738b\u5609\u80e4\uff0c\u738b\u5609\u80e4\u4e5f\u5f04\u4e0d\u6e05\u695a\u513f\u5b50\u4e0e\u5353\u4e00\u822a\u7684\u4e8b\uff0c\u53ea\u80fd\u544a\u8bc9\u4ed6\u513f\u5b50\u6b63\u53bb\u5c71\u897f\u9f99\u95e8\u63a2\u8bbf\u94c1 \u98de\u9f99\u3002 \u3000\u3000\u738b\u5609\u80e4\u548c\u6b66\u5f53\u4e94\u8001\u5e76\u975e\u6df1\u4ea4\uff0c\u6b66\u5f53\u4e00\u6d3e\u53c8\u7d20\u6765\u770b\u4e0d\u8d77\u7eff\u6797\u4e2d\u4eba\uff0c\u6240\u4ee5\u738b\u5609\u80e4\u4e5f\u6ca1\u6709\u600e\u9ebd \u7ec6\u8bf4\uff0c\u66f4\u4e0d\u4f1a\u63d0\u8d77\u7389\u7f57\u5239\u4e0e\u94c1\u98de\u9f99\u7ea6\u4f1a\u6bd4\u6b66\uff0c\u4ee5\u53ca\u738b\u7167\u5e0c\u53bb\u6551\u672a\u5a5a\u59bb\u7b49\u4e8b\u4e86\u3002\u9ec4\u53f6\u4e00\u60f3\uff0c\u6839 \u636e\u76ee\u524d\u7ebf\u7d22\uff0c\u8981\u627e\u5353\u4e00\u822a\u5c31\u8981\u5148\u89c1\u5f97\u7740\u738b\u7167\u5e0c\uff0c\u738b\u7167\u5e0c\u65e2\u53bb\u94c1\u5bb6\uff0c\u90a3\u9ebd\u6b63\u597d\u4e24\u4ef6\u4e8b\u5e76\u505a\u4e00\u4ef6 \u529e\u7406\u3002 \u3000\u3000\u5c31\u662f\u8fd9\u6837\uff0c\u9ec4\u53f6\u7ea2\u4e91\u4e8c\u4eba\uff0c\u4e00\u76f4\u8ffd\u5230\u94c1\u5bb6\u3002\u5f53\u9762\u8d28\u95ee\u94c1\u98de\u9f99\u8d1e\u4e7e\u9053\u4eba\u662f\u600e\u6837\u6b7b\u7684\u3002\u94c1\u98de \u9f99\u542c\u4e86\uff0c\u6012\u4e0d\u53ef\u904f\uff0c\u5f53\u4e0b\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u90a3\u9ebd\u4e8c\u4f4d\u9053\u957f\u60f3\u662f\u8ba4\u5b9a\u8d1e\u4e7e\u4e4b\u6b7b\u4e43\u94c1\u67d0\u6240\u4e3a\u4e86\uff1f\u201d\u9ec4 \u53f6\u9053\u4eba\u6beb\u4e0d\u9690\u853d\u8bcd\u950b\uff0c\u53c8\u662f\u76f4\u7387\u5e94\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u6709\u5982\u706b\u4e0a\u52a0\u6cb9\uff01\u94c1\u98de\u9f99\u731b\u7136\u8dc3\u8d77\uff0c\u4e00\u638c\u5411\u9ec4\u53f6\u9053\u4eba\u5288\u4e0b\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u9ec4\u53f6 \u9053\u4eba\uff0c\u4f60\u628a\u6211\u94c1\u98de\u9f99\u770b\u6210\u4f55\u7b49\u6837\u4eba\uff01\u201d\u9ec4\u53f6\u9053\u4eba\u4e00\u638c\u683c\u5f00\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u81ea\u5bb6\u505a\u4e8b\u81ea\u5bb6\u77e5\uff0c \u4f55\u5fc5\u95ee\u6211\uff1f\u201d\u94c1\u98de\u9f99\u864e\u543c\u4e00\u58f0\uff0c\u4e00\u62db\u201c\u767d\u733f\u63a2\u8def\u201d\uff0c\u5408\u7740\u53cc\u638c\uff0c\u500f\u7136\u5de6\u53f3\u4e00\u5206\uff0c\u53cc\u201c\u526a\u201d\u9ec4 \u53f6\u9053\u4eba\u4e24\u80a9\uff0c\u9ec4\u53f6\u9053\u4eba\u8eab\u8eaf\u970d\u5730\u4e00\u7ffb\uff0c\u8fde\u7528\u201c\u53c1\u73af\u5957\u6708\u201d\u201c\u98ce\u62c2\u5782\u6768\u201d\u4e24\u62db\uff0c\u624d\u582a\u582a\u628a\u94c1\u98de \u9f99\u7684\u62db\u6570\u7834\u53bb\u3002\u94c1\u98de\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u7d2b\u9633\u9053\u957f\u6b7b\u5f8c\uff0c\u4f60\u4eec\u8fd9\u51e0\u4e2a\u6c14\u91cf\u72ed\u7a84\u7684\u9053\u58eb\u5fc5\u7136\u653e \u4e0d\u8fc7\u6211\uff0c\u54fc\uff0c\u54fc\uff0c\u4f60\u4e0d\u670d\u6c14\uff0c\u54b1\u4eec\u518d\u6bd4\u4e00\u6bd4\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u8fd9\u8bdd\u6697\u85cf\u8ba5\u8bbd\uff0c\u65e0\u5f02\u662f\u8bf4\uff1a\u4f60\u4eec\u6b66\u5f53\u4e94\u8001\u4e2d\u4eba\uff0c\u66fe\u6709\u4e00\u4eba\u88ab\u6211\u6240\u632b\uff0c\u7d2b\u9633\u9053\u957f\u91cf \u5927\uff0c\u5e76\u4e0d\u8bb0\u5728\u5fc3\u5934\uff0c\u4f60\u4eec\u6c14\u91cf\u592a\u5c0f\uff0c\u53ef\u5c31\u8981\u775a  \u5fc5\u62a5\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u9ec4\u53f6\u9053\u4eba\u5f53\u5e74\u867d\u4e0d\u670d\u6c14\uff0c\u5374\u7edd\u4e0d\u4f1a\u56e0\u767d\u77f3\u4e4b\u4e8b\u8bb0\u4ec7\uff0c\u4f46\u542c\u4ed6\u5982\u6b64\u8bf4\u6cd5\uff0c\u5fc3\u5934\u4e5f\u81ea\u706b \u8d77\uff0c\u62a2\u5230\u4e0b\u9996\u7acb\u4e86\u4e00\u4e2a\u95e8\u6237\uff0c\u559d\u9053\uff1a\u201c\u8001\u8d3c\uff0c\u6bd4\u5c31\u6bd4\uff0c\u96be\u9053\u6211\u6015\u4f60\u4e0d\u6210\uff01\u8d1e\u4e7e\u9053\u957f\u5728\u9634\u53f8\u7b49 \u7740\u4f60\uff01\u4f60\u6709\u4ec0\u9ebd\u5f8c\u4e8b\uff0c\u8d81\u65e9\u5bf9\u5bb6\u4eba\u4ea4\u4ee3\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u52c3\u7136\u5927\u6012\uff0c\u9a82\u9053\uff1a\u201c\u4e71\u56bc\u820c\u5934\uff0c\u5403\u6211\u4e00\u638c\u3002\u201d\u4ece\u201c\u826e\u201d\u4f4d\u62a2\u5230\u201c\u79bb\u201d\u65b9\uff0c\u4e00\u8bb0\uff01 \u94c1\u7435\u7436\u624b\u201d\uff0c\u624b\u80cc\u5411\u5916\u4e00\u6325\uff0c\u8fc5\u5982\u9a87\u7535\u7684\u5411\u9ec4\u53f6\u9053\u4eba\u9762\u95e8\u63b4\u6765\uff0c\u9ec4\u53f6\u9053\u4eba\u8eab\u5f62\u4e00\u95ea\uff0c\u63a2\u638c\u6765 \u5207\u94c1\u98de\u9f99\u53f3\u81c2\uff0c\u53cc\u6307\u6697\u6307\u7a74\u9053\uff0c\u94c1\u98de\u9f99\u7a81\u7136\u7f29\u638c\uff0c\u9ec4\u53f6\u9053\u4eba\u8eab\u5f62\u51b2\u4e0a\uff0c\u4ed6\u5de6\u62f3\u7a81\u51fa\uff0c\u53d8\u6210 \u201c\u8098\u5e95\u770b  \u201d\uff0c\u62f3\u5934\u4e00\u62b5\u638c\u5fc3\uff0c\u53cc\u65b9\u5404\u81ea\u9000\u5f8c\u53c1\u6b65\u3002 \u3000\u3000\u94c1\u98de\u9f99\u4e00\u9000\u590d\u4e0a\uff0c\u559d\u9053\uff1a\u201c\u8d1e\u4e7e\u9053\u4eba\u7ed9\u5978\u4eba\u5bb3\u6b7b\uff0c\u4e0e\u6211\u4f55\u5e72\uff1f\u4f60\u4e71\u628a\u8fd9\u7b14\u5e10\u7b97\u5728\u6211\u7684\u8eab \u4e0a\uff0c\u82e5\u4e0d\u8d54\u7f6a\uff0c\u8981\u4f60\u4e0d\u80fd\u6d3b\u51fa\u6b64\u95e8\uff01\u201d\u94c1\u98de\u9f99\u6027\u60c5\u66b4\u8e81\uff0c\u521a\u624d\u4e00\u8a00\u4e0d\u5408\uff0c\u7acb\u5373\u6325\u62f3\uff0c\u62c6\u4e86\u4e24 \u62db\uff0c\u731b\u7136\u9192\u8d77\uff1a\u6bd4\u638c\u662f\u4e00\u56de\u4e8b\uff1a\u8d1e\u4e7e\u9053\u4eba\u4e4b\u6b7b\u5374\u53c8\u662f\u53e6\u4e00\u56de\u4e8b\uff1a\u975e\u5f97\u8bf4\u660e\u4e0d\u53ef\u3002\u9ec4\u53f6\u9053\u4eba\u6014 \u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8bdd\u53ef\u771f\uff1f\u201d\u94c1\u98de\u9f99\u6012\u9053\uff1a\u201c\u4f60\u6562\u4e0d\u4fe1\u6211\u7684\u8bf4\u8bdd\u4e86\u8d3c\u8001\u9053\uff0c\u6211\u53ef\u4ee5\u66ff\u8d1e\u4e7e\u62a5 \u4ec7\uff0c\u4f46\u4ecd\u7136\u8981\u548c\u4f60\u6bd4\u638c\uff01\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u4ece\u201c\u79bb\u201d\u4f4d\u5954\u201c\u574e\u201d\u65b9\uff0c\u638c\u631f\u98ce  \uff0c\u547c\u7684\u4e00\u58f0\uff0c\u53cc\u638c \u53c8\u5411\u9ec4\u53f6\u9053\u4eba\u5939\u51fb\uff01 \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u89c1\u4ed6\u6765\u52bf\u51f6\u731b\uff0c\u5de6\u62f3\u53d8\u638c\u5411\u5185\u4e00\u5708\uff0c\u53f3\u81c2\u4e00\u6eda\u4e00\u62e7\uff0c\u7528\u201c\u9e64\u818a\u624b\u201d\u7684\u62db\u6570\u6d88\u6389 \u4ed6\u7684\u6765\u52bf\uff0c\u90a3\u77e5\u94c1\u98de\u9f99\u638c\u6cd5\u53ef\u67d4\u53ef\u521a\uff0c\u53f3\u81c2\u5df2\u88ab\u5708\u4f4f\uff0c\u4ed6\u5374\u8d81\u52bf\u4e00\u5e26\uff0c\u5de6\u62f3\u75be\u53d1\u5982\u98ce\uff0c\u4e00\u4e2a \u201c\u6512\u62f3\u201d\uff0c\u81ea\u53f3\u81c2\u7684\u52fe\u624b\u5708\u4e2d\u76f4\u6512\u4e0a\u6765\uff0c\u51b2\u6253\u9ec4\u53f6\u9053\u4eba\u7684\u592a\u9633\u8981\u7a74\u3002\u9ec4\u53f6\u9053\u4eba\u5728\u6b66\u5f53\u4e94\u8001\u4e2d \u529f\u529b\u4ec5\u6b21\u65bc\u638c\u95e8\u5e08\u5144\uff0c\u80a9\u5934\u4e00\u8f6c\uff0c\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u786c\u63a5\u4e86\u94c1\u98de\u9f99\u8fd9\u62f3\uff0c\u5de6\u638c\u4e00\u52fe\uff0c\u95ea\u7535\u822c\u7684 \u628a\u94c1\u98de\u9f99\u624b\u8155\u52fe\u4f4f\uff0c\u5f80\u4e0b\u4e00\u62d7\u3002\u94c1\u98de\u9f99\u8fd9\u62f3\uff0c\u628a\u9ec4\u53f6\u9053\u4eba\u6253\u5f97\u91d1\u661f\u4e71\u5192\uff1a\u4f46\u94c1\u98de\u9f99\u7ed9\u4ed6\u8fd9\u4e00 \u62d7\uff0c\u4e5f\u662f\u5947\u75db\u96be\u5f53\uff0c\u6025\u5fd9\u8fd0\u529b\u5de6\u638c\uff0c\u5e73\u63a8\u51fa\u53bb\uff0c\u9ec4\u53f6\u9053\u4eba\u817e\u51fa\u53f3\u638c\u786c\u63a5\uff0c\u7ed9\u4ed6\u63a8\u5f97\u8eab\u5f62\u6447 \u6643\uff0c\u4f46\u5de6\u624b\u5374  \u662f\u4e0d\u80af\u653e\u677e\uff01 \u3000\u3000\u4e24\u4eba\u6b66\u529f\u90fd\u5df2\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u8fd9\u4e00\u76f8\u6301\u4e0d\u4e0b\uff0c\u4e24\u4eba\u989d\u4e0a\u90fd\u6ef4\u4e0b\u6c57\u6765\u201c\u9ec4\u53f6\u9053\u4eba\u9762\u8272 \u7070\u8d25\uff0c\u6c14\u5598\u5982\u725b\uff1a\u94c1\u98de\u9f99\u8fd0\u8db3\u5185\u52b2\u652f\u6301\uff0c\u8155\u9aa8\u4e5f\u7ed9\u62d7\u5f97\u5947\u75db\u6b32\u88c2\u3002\u4e24\u4eba\u90fd\u6697\u6697\u5f8c\u6094\uff0c\u8fd9\u65f6\u6536 \u624b\u5df2\u96be\u3002\u7ea2\u4e91\u9053\u4eba\u89c1\u72b6\u5947\u9669\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u6b63\u60f3\u51fa\u624b\uff1a\u5ffd\u7136\u773c\u775b\u4e00\u4eae\uff0c\u7389\u7f57\u5239\u767d\u8863\u98d8\u98d8\uff0c\u4e5f\u4e0d \u89c1\u600e\u6837\u4f5c\u52bf\uff0c\u8eab\u6cd5\u5374\u662f\u5feb\u5230\u6781\u70b9\uff0c\u4e00\u4e0b\u5b50\u5c31\u62a2\u5728\u7ea2\u4e91\u4e4b\u524d\uff0c\u53cc\u81c2\u6a2a\u5c55\uff0c\u5728\u94c1\u98de\u9f99\u548c\u9ec4\u53f6\u9053\u4eba \u7684\u814b\u7a9d\u5404\u6293\u4e86\u4e00\u628a\uff0c\u4e24\u4eba\u5ffd\u89c9\u5947\u75d2\uff0c\u4e0d\u89c9\u540c\u65f6\u677e\u4e86\u5185\u52b2\uff0c\u7389\u7f57\u5239\u8f7b\u8f7b\u4e00\u62c9\uff0c\u5c06\u4e24\u4eba\u90fd\u62c9\u5f00 \u4e86\u3002 \u3000\u3000\u9ec4\u53f6\u7ea2\u4e91\u4e8c\u4eba\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u7389\u7f57\u5239\u62bf\u5634\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u9053\u7237\u4e00\u628a\u5e74\u7eaa\uff0c\u5374\u4e0e\u6211\u4e00\u6837\u89c1 \u8bc6\uff1f\u201d\u9ec4\u53f6\u8fd0\u6c14\u8c03\u5143\uff0c\u6c14\u5598\u6e10\u6b62\uff0c\u95fb\u58f0\u8be7\u5f02\uff1a\u201c\u4f60\u8bf4\u4ec0\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8d77\u521d\u6211\u4e5f\u5f53\u8d1e\u4e7e \u9053\u4eba\u662f\u94c1\u8001\u82f1\u96c4\u5bb3\u6b7b\u7684\uff0c\u4e5f\u50cf\u4f60\u4e00\u6837\uff0c\u4e0d\u95ee\u9752\u7ea2\u7682\u767d\u5c31\u548c\u4ed6\u4ea4\u624b\uff0c\u73b0\u5728\u60f3\u6765\uff0c\u771f\u662f\u53ef\u7b11\uff01\u201d \u9ec4\u53f6\u9053\u4eba\u5947\u9053\uff1a\u201c\u600e\u9ebd\uff0c\u4f60\u4e0d\u662f\u4ed6\u7684\u5973\u513f\u5417\uff1f\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u8c01\u8bf4\u4e0d\u662f\u5440\uff1f\u201d\u9ec4\u53f6\u9053\u4eba\u6c14 \u9053\uff1a\u201c\u54fc\uff0c\u4f60\u548c\u6211\u5f00\u4ec0\u9ebd\u73a9\u7b11\uff1f\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f2\uff0c\u5916\u9762\u4e00\u9635\u811a\u6b65\u58f0\u54cd\uff0c\u7ea2\u4e91\u8dc3\u51fa\u53f0\u9636\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5353\u4e00\u822a\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u5374\u8bf4\u5353\u4e00\u822a\u8f9e\u522b\u4e86\u7389\u7f57\u5239\u4e4b\u5f8c\uff0c\u5fc3\u60c5\u751a\u4e3a\u6005\u60d8\uff0c\u7b56\u9a6c\u8ddf\u5728\u738b\u7167\u5e0c\u4e0e\u5b5f\u79cb\u971e\u4e4b\u5f8c\uff0c\u89c1\u4ed6\u548c \u5b5f\u79cb\u971e\u5e76\u8f94\u5954\u9a70\uff0c\u9887\u6709\u611f\u89e6\uff0c\u4e0d\u7981\u60f3\u8d77\u4e86\u7389\u7f57\u5239\u6765\u3002\u8d8a\u60f3\u8d8a\u4e71\uff0c\u731b\u7136\u95f4\u8fce\u9762\u6765\u4e86\u51e0\u9a91\u5feb\u9a6c\uff0c \u6709\u4eba\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5353\u5e08\u5f1f\u3002\u201d\u738b\u7167\u5e0c\u52d2\u4e86\u9a6c  \uff0c\u90a3\u4e9b\u4eba\u4e5f\u7eb7\u7eb7\u4e0b\u9a6c\uff0c\u4e3a\u9996\u7684\u662f\u6b66\u5f53\u6d3e\u7b2c\u4e8c\u4ee3 \u7684\u5927\u5f1f\u5b50\u865e\u65b0\u57ce\uff0c\u80cc\u5f8c\u8fd8\u6709\u4e94\u4eba\uff0c\u5176\u4e2d\u4e00\u4eba\u662f\u803f\u7ecd\u5357\uff01 \u3000\u3000\u5353\u4e00\u822a\u628a\u540c\u95e8\u7ed9\u738b\u7167\u5e0c\u5f15\u89c1\uff0c\u5176\u4e2d\u803f\u7ecd\u5357\u548c\u4ed6\u65e9\u5df2\u76f8\u8bc6\uff0c\u56de\u601d\u524d\u4e8b\uff0c\u751a\u89c9\u5c34\u5c2c\u3002\u5353\u4e00\u822a \u95ee\u9053\uff1a\u201c\u5404\u4f4d\u5e08\u5144\u8fdc\u6765\u4f55\u4e8b\uff1f\u201d\u865e\u65b0\u57ce\u9053\uff1a\u201c\u4f60\u8fd8\u672a\u89c1\u4e8c\u5e08\u53d4\u548c\u53c1\u5e08\u53d4\u5427\uff1f\u201d\u5353\u4e00\u822a\u5947\u9053\uff1a \u201c\u600e\u9ebd\u4ed6\u4eec\u4e24\u4f4d\u8001\u4eba\u5bb6\u4e5f\u6765\uff1f\u201d\u865e\u65b0\u57ce\u6f78\u7136\u6cea\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u7236\u524d\u6708\u521d\u4e5d\u65e5\u5b50\u65f6\u4ed9\u6e38\u53bb \u4e86\uff01\u201d\u5353\u4e00\u822a\u9aa4\u95fb\u5669\u8017\uff0c\u201c\u54c7\u201d\u7684\u4e00\u58f0\u54ed\u4e86\u8d77\u6765\uff0c\u6447\u6447\u6b32\u5012\uff01\u7d2b\u9633\u9053\u4eba\u4e0e\u4ed6\u60c5\u903e\u7236\u6bcd\uff0c\u5341\u4e8c \u5e74\u6765\u82e6\u5fc3\u57f9\u80b2\uff0c\u6b63\u662f\u6df1\u6069\u672a\u62a5\uff0c\u4e0d\u6599\u4ece\u6b64\u76f8\u89c1\u65e0\u671f\uff01 \u3000\u3000\u865e\u65b0\u57ce\u6025\u5fd9\u5c06\u4ed6\u6276\u7740\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u5f1f\u8282\u54c0\uff0c\u5e08\u7236\u4e00\u6b7b\uff0c\u6211\u4eec\u6b66\u5f53\u6d3e\u7684\u62c5\u5b50\u53ef\u8981\u4f60\u62c5 \u4e86\uff01\u201d\u5353\u4e00\u822a\u62ed\u6cea\u95ee\u9053\uff1a\u201c\u4ec0\u9ebd\uff1f\u201d\u865e\u65b0\u57ce\u9053\uff1a\u201c\u5e08\u7236\u9057\u547d\uff0c\u8981\u4f60\u505a\u638c\u95e8\u5f1f\u5b50\uff01\u201d\u5353\u4e00\u822a\u5403 \u4e86\u4e00\u60ca\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4e0a\u6709\u56db\u4f4d\u5e08\u53d4\uff0c\u4e0b\u6709\u5217\u4f4d\u5e08\u5144\uff0c\u600e\u9ebd\u8981\u6211\u505a\u638c\u95e8\uff1f\u201d\u865e\u65b0\u57ce\u9053\uff1a\u201c\u5e08 \u5f1f\u4f60\u6587\u6b66\u5168\u6750\uff0c\u6709\u89c1\u8bc6\uff0c\u6709\u9b44\u529b\uff0c\u5149\u5927\u6211\u4eec\u6b66\u5f53\u4e00\u6d3e\uff0c\u5c31\u5168\u6307\u671b\u4f60\u4e86\u3002\u540c\u95e8\u62dc\u9886\u5e08\u7236\u9057\u547d\uff0c \u65e0\u4e0d\u6df1\u5e86\u5f97\u4eba\uff01\u201d\u8bf4\u5b8c\u4e4b\u5f8c\uff0c\u7adf\u4ee5\u638c\u95e8\u4e4b\u793c\u53c2\u89c1\uff0c\u803f\u7ecd\u5357\u7b49\u4e94\u4eba\u4e5f\u7eb7\u7eb7\u8fc7\u6765\u53c2\u89c1\u3002\u5353\u4e00\u822a\u614c \u5fd9\u8fd8\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u5217\u4f4d\u5e08\u5144\u5982\u6b64\u76f8\u5f85\uff0c\u5c82\u4e0d\u6298\u6740\u5c0f\u5f1f\u3002\u638c\u95e8\u4e4b\u4e8b\u7f13\u63d0\uff0c\u5f85\u6211\u56de\u5c71\u4e4b\u5f8c\uff0c\u518d\u4ece \u957f\u8ba1\u8bae\u3002\u201d\u865e\u65b0\u57ce\u9053\uff1a\u201c\u5e08\u5f1f\u4e0d\u5fc5\u53c1\u5fc3\u4e24\u610f\u3002\u201d\u803f\u7ecd\u5357\u9053\uff1a\u201c\u5e08\u5144\u5148\u548c\u6211\u4eec\u53bb\u89c1\u4e8c\u5e08\u53d4\u548c\u53c1 \u5e08\u53d4\u5427\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4e24\u4f4d\u5e08\u53d4\u5728\u90a3\u91cc\uff01\u201d\u865e\u65b0\u57ce\u9053\uff1a\u201c\u5c31\u5728\u524d\u9762\u94c1\u5bb6\uff01\u201d\u803f\u7ecd\u5357\u9053\uff1a \u201c\u6211\u4eec\u8d39\u4e86\u597d\u5927\u6c14\u529b\uff0c\u624d\u63a2\u51fa\u4f60\u5728\u8fd9\u91cc\u3002\u201d\u5353\u4e00\u822a\u6325\u6cea\u9053\uff1a\u201c\u4e3a\u6211\u4e00\u4eba\u8981\u5404\u4f4d\u5e08\u53d4\u5e08\u5144\u957f\u9014 \u8dcb\u6d89\uff0c\u771f\u662f\u65bc\u5fc3\u4e0d\u5b89\uff0c\u53ea\u6050\u6211\u8981\u8d1f\u5e08\u7236\u548c\u5404\u4f4d\u540c\u95e8\u7684\u539a\u671b\u4e86\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u6325\u6cea\u4e0e\u738b\u7167\u5e0c\u9053\u522b\uff0c\u7b56\u9a6c\u518d\u8d70\u56de\u7a0b\u3002\u803f\u7ecd\u5357\u9053\uff1a\u201c\u5353\u5e08\u5144\u4e3a\u4f55\u548c\u8fd9\u5c0f\u5b50\u4e00\u9053\uff1f\u201d \u5353\u4e00\u822a\u9053\uff1a\u201c\u600e\u9ebd\uff1f\u201d\u803f\u7ecd\u5357\u9053\uff1a\u201c\u4ed6\u662f  \u5317\u5927\u76d7\u738b\u5609\u80e4\u7684\u513f\u5b50\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u65e9 \u5df2\u77e5\u9053\u3002\u201d\u865e\u65b0\u57ce\u662f\u7b2c\u4e8c\u4ee3\u5927\u5f1f\u5b50\uff0c\u4eba\u751a\u5e73\u5eb8\uff0c\u5bf9\u5353\u4e00\u822a\u88ab\u7acb\u4e3a\u638c\u95e8\u4e5f\u5fc3\u60a6\u8bda\u670d\u3002\u53ef\u662f\u4ed6\u5bf9 \u6b66\u5f53\u95e8\u89c4\u751a\u4e3a\u91cd\u89c6\uff0c\u95fb\u8a00\u5413\u4e86\u4e00\u8df3\uff0c\u95ee\u803f\u7ecd\u5357\u9053\uff1a\u201c\u9002\u624d\u90a3\u4eba\u5c31\u662f\u53bb\u5e74\u548c\u4f60\u4f5c\u4f34\u90a3\u4e2a\u767d\u9a6c\u5c11 \u5e74\u9ebd\uff1f\u201d\u803f\u7ecd\u5357\u88ab\u8fb1\u4e4b\u5f8c\uff0c\u66fe\u56de\u5c71\u54ed\u8bc9\uff0c\u6240\u4ee5\u6b66\u5f53\u95e8\u4eba\u5168\u90fd\u77e5\u9053\u3002\u803f\u7ecd\u5357\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u865e \u65b0\u57ce\u4e0d\u89c9\u53d8\u4e86\u9762\u8272\uff0c\u6b63\u8a00\u5bf9\u5353\u4e00\u822a\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u73b0\u5728\u5df2\u662f\u6211\u6d3e\u638c\u95e8\uff0c\u4ee5\u5f8c\u884c\u4e8b\uff0c\u53ef\u5f97\u66f4\u4e3a \u5c0f\u5fc3\uff0c\u4ee5\u4e3a\u540c\u95e8\u8868\u7387\u3002\u201d\u5353\u4e00\u822a\u62ed\u6cea\u7b54\u9053\uff1a\u201c\u5e08\u5144\u826f\u8a00\uff0c\u81ea\u5f53\u62dc\u9886\u3002\u53ea\u662f\u7eff\u6797\u4e2d\u4eba\u4e5f\u9887\u591a\u4fa0 \u4e49\u4e4b\u58eb\uff0c\u6211\u4eec\u4e0d\u4f5c\u5f3a\u76d7\uff0c\u4e0e\u4ed6\u4eec\u5f80\u6765\u4e5f\u4e0d\u7b97\u8fdd\u4e86\u95e8\u89c4\u3002\u201d\u865e\u65b0\u57ce\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u4e5f\u5bf9\uff0c\u4f46\u542c\u8bf4 \u8fd9\u4e2a\u738b\u7167\u5e0c\u4e0e\u5973\u76d7\u7389\u7f57\u5239\u9887\u6709\u52fe\u7ed3\u3002\u7389\u7f57\u5239\u52ab\u4ee4\u7956\u4e4b\u4e8b\uff0c\u5e08\u5f1f\u4e00\u5b9a\u662f\u77e5\u9053\u7684\u4e86\u3002\u201d\u5353\u4e00\u822a\u9762 \u4e0a\u4e00\u7ea2\uff0c\u5450\u5450\u8bf4\u9053\uff1a\u201c\u6211\u7237\u7237\u5012\u5e76\u4e0d\u602a\u5979\u3002\u201d\u803f\u7ecd\u5357\u95fb\u8a00\u9887\u4e3a\u4e0d\u6ee1\uff0c\u95ee\u9053\uff1a\u201c\u5353\u5e08\u5144\u89c1\u8fc7\u7389 \u7f57\u5239\u4e86\u5417\uff1f\u201d\u5353\u4e00\u822a\u70b9\u4e86\u70b9\u5934\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u6211\u73b0\u5728\u5fc3\u91cc\u5f88\u70e6\uff0c\u6709\u8bb8\u591a\u4e8b\u60c5\u5c06\u6765\u8fd8\u8981\u548c\u51e0\u4f4d \u5e08\u5144\u8be6\u8c08\u3002\u803f\u5144\uff0c\u90a3\u5e74\u4f60\u4ee3\u6211\u62a4\u9001\u5148\u7956\uff0c\u6211\u662f\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d\u8bf4\u7f62\u6df1\u6df1\u4f5c\u4e86\u4e00\u63d6\uff0c\u803f\u7ecd\u5357\u614c\u5fd9 \u8fd8\u793c\uff0c\u9762\u4e5f\u7ea2\u4e86\u3002\u5450\u5450\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u672c\u4e8b\u4f4e\u5fae\uff0c\u62a4\u9001\u4e0d\u529b\uff0c\u5e08\u5144\u7eb5\u4e0d\u602a\u8d23\uff0c\u5c0f\u5f1f\u4e5f\u89c9\u7f9e \u989c\u3002\u201d\u865e\u65b0\u57ce\u9053\uff1a\u201c\u8fd9\u4e9b\u8bdd\u90fd\u4e0d\u5fc5\u63d0\u4e86\u3002\u5353\u5e08\u5f1f\u662f\u672c\u95e8\u4fca\u6770\uff0c\u73b0\u5728\u53c8\u662f\u638c\u95e8\uff0c\u4f60\u8fd8\u62c5\u5fc3\u4ed6\u4e0d \u66ff\u4f60\u51fa\u4e00\u53e3\u6c14\u5417\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u7b56\u9a6c\u7f13\u884c\uff0c\u5fc3\u4e8b\u771f\u662f\u70e6\u5982\u4e71  \uff0c\u540c\u95e8\u5144\u5f1f\u5bf9\u7389\u7f57\u5239\u4ec7\u89c6\uff0c\u65e9\u5df2\u5728\u4ed6\u610f\u6599\u4e4b\u4e2d\uff0c\u4f46 \u5374\u8fd8\u60f3\u4e0d\u5230\u5982\u6b64\u4e4b\u751a\uff1f\u800c\u4eca\u65e5\u7389\u7f57\u5239\u6b63\u5728\u94c1\u5bb6\uff0c\u7247\u523b\u4e4b\u5f8c\uff0c\u5c31\u8981\u76f8\u9047\uff01 \u3000\u3000\u5353\u4e00\u822a\u5fc3\u5934\u9e7f\u649e\uff0c\u865e\u65b0\u57ce\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u653e\u9a6c\u5feb\u8d70\u5440\uff01\u201d\u5353\u4e00\u822a\u832b\u7136\u653e\u677e\u9a6c  \uff0c\u4e0d\u4e00\u523b\u5230 \u4e86\u94c1\u5bb6\uff0c\u65b9\u8e0f\u5165\u5e84\u95e8\uff0c\u4fbf\u542c\u5f97\u9ec4\u53f6\u9053\u4eba\u547c\u559d\u4e4b\u58f0\uff0c\u865e\u65b0\u57ce\u5927\u5403\u4e00\u60ca\uff0c\u4e0d\u5f85\u5e84\u4e01\u901a\u62a5\uff0c\u4fbf\u548c\u4f17 \u540c\u95e8\u4e00\u51b2\u800c\u5165\u3002 \u3000\u3000\u518d\u8bf4\u9ec4\u53f6\u9053\u4eba\u6b63\u5728\u8d23\u95ee\u7389\u7f57\u5239\uff0c\u5ffd\u89c1\u865e\u65b0\u57ce\u7b49\u4eba\u62e5\u7740\u5353\u4e00\u822a\u8d70\u8fdb\uff0c\u6025\u5fd9\u4e0a\u524d\u8fce\u63a5\uff0c\u5353\u4e00 \u822a\u5927\u54ed\u62dc\u5012\uff0c\u9ec4\u53f6\u9053\u4eba\u5c06\u4ed6\u6276\u8d77\uff0c\u628a\u7d2b\u9633\u9053\u957f\u7684\u9057\u547d\u5411\u4ed6\u518d\u8bf4\u4e00\u904d\u3002\u5353\u4e00\u822a\u9053\uff1a\u201c\u5f1f\u5b50\u65e0\u5fb7 \u65e0\u80fd\uff0c\u4f55\u80fd\u81ba\u6b64\u91cd\u4efb\u3002\u5e08\u53d4\u8bf7\u9886\u5f1f\u5b50\u56de\u5c71\uff0c\u518d\u53ec\u96c6\u540c\u95e8\uff0c\u53e6\u63a8\u8d24\u5fb7\u3002\u201d\u9ec4\u53f6\u9053\u4eba\u4e0d\u4fbf\u5728\u94c1\u5bb6 \u5546\u8ba8\uff0c\u9053\uff1a\u201c\u90a3\u4e5f\u597d\u3002\u5f85\u6211\u4e0e\u94c1\u8001\u5934\u63ed\u4e86\u8fd9\u6bb5\u8fc7\u8282\uff0c\u5c31\u548c\u4f60\u56de\u5c71\u3002\u201d \u3000\u3000\u94c1\u98de\u9f99\u89c1\u6b66\u5f53\u6d3e\u7684\u4eba\u53cd\u5bbe\u4e3a\u4e3b\uff0c\u5728\u4ed6\u5bb6\u91cc\u95f9\u5f97\u4e71\u54c4\u54c4\u7684\uff0c\u5fc3\u4e2d\u9887\u4e3a\u4e0d\u5feb\u3002\u597d\u5728\u7d2b\u9633\u9053\u957f \u662f\u4ed6\u6700\u4f69\u670d\u7684\u4eba\uff0c\u8981\u4e0d\u7136\u65e9\u5df2\u53d1\u4f5c\u3002\u8fd9\u65f6\u89c1\u9ec4\u53f6\u9053\u4eba\u548c\u5353\u4e00\u822a\u8c08\u8bdd\u544a\u4e00\u6bb5\u843d\uff0c\u84e6\u7136\u7ad9\u4e86\u8d77 \u6765\uff0c\u53d1\u58f0\u95ee\u9053\uff1a\u201c\u9ec4\u53f6\u9053\u4eba\uff0c\u4f60\u4eec\u7684\u638c\u95e8\u5f1f\u5b50\u73b0\u5728\u8fd9\u91cc\uff0c\u4f60\u53ef\u95ee\u4ed6\uff0c\u8d1e\u4e7e\u9053\u4eba\u662f\u8c01\u5bb3\u6b7b \u7684\uff1f\u201d\u5353\u4e00\u822a\u95fb\u8a00\u9274\u8c8c\uff0c\u6599\u5f97\u94c1\u98de\u9f99\u548c\u81ea\u5df1\u7684\u5e08\u53d4\u5fc5\u662f\u56e0\u8d1e\u4e7e\u4e4b\u6b7b\u4ea7\u751f\u4e86\u8bef\u4f1a\u3002\u5f53\u4e0b\u5411\u5e08\u53d4 \u7968\u9053\uff1a\u201c\u8d1e\u4e7e\u9053\u4eba\u7ed9\u9634\u98ce\u6bd2\u7802\u638c\u91d1\u72ec\u5f02\u7684\u95e8\u4e0b\u6240\u5bb3\uff0c\u94c1\u8001\u82f1\u96c4\u6b63\u8981\u8d76\u8d74\u897f\u57df\u4e3a\u4ed6\u62a5\u4ec7\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u4e4b\u8a00\uff0c\u9ec4\u53f6\u9053\u4eba\u4e0d\u7531\u4e0d\u4fe1\uff0c\u5f53\u4e0b\u8001\u9762\u6cdb\u7ea2\uff0c\u6025\u5fd9\u62b1\u62f3\u8d77\u7acb\uff0c\u5411\u94c1\u98de\u9f99\u65bd\u793c\u9053\uff1a \u201c\u9002\u624d\u5192\u6627\uff0c\u8d2b\u9053\u8fd9\u53a2\u966a\u7f6a\uff01\u94c1\u8001\u4f55\u65e5\u52a8\u8eab\uff0c\u8d2b\u9053\u5f53\u547d\u95e8\u4e0b\u5f1f\u5b50\u76f8\u52a9\u3002\u201d\u94c1\u98de\u9f99\u51b7\u7b11\u9053\uff1a \u201c\u4e0d\u5fc5\u4e86\uff01\u4ffa\u53ea\u6709\u4e00\u4e8b\u76f8\u6c42\uff0c\u8bf7\u4f60\u4eec\u5728\u7d2b\u9633\u9053\u957f\u7075\u524d\u4ee3\u4e3a\u7980\u544a\uff0c\u5c31\u8bf4\u94c1\u67d0\u4e00\u6765\u56e0\u6709\u522b\u4e8b\u5728 \u8eab\uff0c\u4e8c\u6765\u95e8\u6237\u4e0d\u540c\uff0c\u53ea\u6562\u9065\u796d\uff0c\u4e0d\u6562\u4eb2\u4e34\uff0c\u4e5e\u4ed6\u6055\u7f6a\uff01\u201d\u9ec4\u53f6\u9053\u4eba\u77e5\u4ed6\u5fc3\u4e2d\u5c1a\u81ea\u6709\u6c27\uff0c\u53ea\u662f \u65e0\u53ef\u5982\u4f55\uff0c\u53ea\u5f97\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u94c1\u8001\u8a00\u91cd\u4e86\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u4f8d\u7acb\u4e00\u8fb9\uff0c\u201c\u5e08\u7236\u867d\u6709\u547d\u7acb\u4ed6\u505a\u638c\u95e8\u5f1f\u5b50\uff0c\u4ed6\u53ef\u4e0d\u6562\u4ee5\u638c\u95e8\u4eba\u81ea\u5c45\u3002\u201d\u4fa7\u76ee\u659c \u7aa5\uff0c\u5ffd\u89c1\u803f\u7ecd\u5357\u7ad9\u5728\u7ea2\u4e91\u5e08\u53d4\u8eab\u65c1\uff0c\u5527\u5527\u55b3\u55b3\u5982\u5728\u4f4e\u58f0\u7980\u544a\uff0c\u5353\u4e00\u822a\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6697\u53eb\u4e0d\u597d\uff0c \u803f\u7ecd\u5357\u6b63\u662f\u7ea2\u4e91\u9053\u4eba\u7684\u5f97\u610f\u5f1f\u5b50\uff0c\u4ed6\u5fc5\u7136\u662f\u6c42\u5e08\u7236\u66ff\u4ed6\u62a5\u4ec7\u3002\u5353\u4e00\u822a\u518d\u770b\u7389\u7f57\u5239\uff0c\u7389\u7f57\u5239\u5750 \u5728\u94c1\u98de\u9f99\u8eab\u5f8c\uff0c\u82e5\u65e0\u5176\u4e8b\u7684\u5de6\u987e\u53f3\u76fc\uff0c\u5353\u4e00\u822a\u6b63\u5de7\u78b0\u5230\u5979\u5c04\u6765\u7684\u76ee\u5149\uff0c\u614c\u5fd9\u4f4e\u4e0b\u4e86\u5934\uff0c\u4e00\u9897 \u5fc3\u66f4\u8df3\u5f97\u535c\u535c\u4f5c\u54cd\u3002 \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u5411\u94c1\u98de\u9f99\u966a\u7f6a\u4e4b\u5f8c\uff0c\u5df2\u662f\u65e0\u8bdd\u53ef\u8bf4\u3002\u865e\u65b0\u57ce\u7b49\u5f1f\u5b50\u7ad9\u4e86\u8d77\u6765\uff0c\u51c6\u5907\u52a8\u8eab\u3002\u9ec4\u53f6 \u9053\u4eba\u5f3a\u7b11\u9053\uff1a\u201c\u94c1\u8001\u6055\u7f6a\uff0c\u6211\u4eec\u544a\u8f9e\u4e86\uff01\u201d\u8bdd\u58f0\u65b9\u505c\uff0c\u7ea2\u4e91\u9053\u4eba\u5ffd\u7136\u4e00\u8dc3\u800c\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u5e08 \u5144\u4e14\u6162\uff01\u201d \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u6115\u7136\u56de\u987e\uff0c\u53ea\u513f\u7ea2\u4e91\u9053\u4eba\u6307\u7740\u94c1\u98de\u9f99\u8eab\u5f8c\u7684\u90a3\u4e2a\u5c11\u5973\uff0c\u6717\u58f0\u8bb8\u9053\uff1a\u201c\u8fd9\u4f4d\u5973\u82f1 \u96c4\u6211\u4eec\u4f69\u670d\u5f97\u7d27\uff0c\u8d2b\u9053\u65e9\u60f3\u9886\u6559\uff0c\u4e0d\u60f3\u4eca\u65e5\u6709\u7f18\u76f8\u4f1a\u3002\u201d\u9ec4\u53f6\u9053\u4eba\u5927\u4e3a\u60ca\u8be7\uff0c\u5fc3\u60f3\uff1a\u5e08\u5f1f\u96be \u9053\u75af\u4e86\u4e0d\u6210\uff0c\u600e\u9ebd\u4ee5\u6b66\u5f53\u4e94\u8001\u7684\u8eab\u4efd\uff0c\u7adf\u5411\u4e00\u4e2a\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5973\u5b50\u53d1\u51fa\u6311\u6218\u7684\u53e3\u543b\u3002 \u3000\u3000\u94c1\u98de\u9f99\u51b7\u51b7\u4e00\u7b11\uff0c\u95ea\u8fc7\u4e00\u65c1\uff0c\u7389\u7f57\u5239\u4ecd\u662f\u795e\u8272\u81ea\u82e5\uff0c\u6162\u6761\u65ad\u7406\u7684\u6574\u597d\u8863\u88f3\uff0c\u8fd9\u624d\u7f13\u7f13\u8d77 \u7acb\u3002 \u3000\u3000\u7ea2\u4e91\u9053\u4eba\u8fc8\u524d\u4e00\u6b65\uff0c\u7389\u7f57\u5239\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u6b66\u5f53\u5251\u6cd5\u72ec\u6b65\u5929\u4e0b\uff0c\u6211\u600e\u9ebd\u6562\u5411\u9053\u957f\u9886\u6559\u3002\u201d \u7ea2\u4e91\u9053\u4eba\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e0d\u63a5\u62db\u4e5f\u884c\uff0c\u4f46\u59d1\u5a18\u6b20\u6b66\u5f53\u6d3e\u7684\u503a\uff0c\u8d2b\u9053\u53ef\u8981\u6597\u80c6\u8ba8\u56de\u3002\u201d\u7389\u7f57 \u5239\u7709\u6bdb\u4e00\u626c\uff0c\u8bf4\uff1a\u201c\u8ba8\u8fd8\u4ec0\u9ebd\uff1f\u201d\u7ea2\u4e91\u9053\u4eba\u9053\uff1a\u201c\u6562\u8bf7\u59d1\u5a18\u5c06\u516d\u6839\u6307\u5934\u5272\u4e0b\uff0c\u4ea4\u8d2b\u9053\u5e26 \u56de\u3002\u201d\u7389\u7f57\u5239\u5f53\u5e74\u5728\u5b9a\u519b\u5c71\u4e0a\u6298\u8fb1\u6b66\u5f53\u4e94\u4e2a\u95e8\u5f92\uff0c\u5c06\u803f\u7ecd\u5357\u4e24\u6839\u624b\u6307\u524a\u65ad\uff0c\u5176\u9980\u56db\u4eba\u5219\u5404\u524a \u65ad\u4e00\u6839\uff0c\u5408\u8d77\u6765\u6b63\u662f\u516d\u6839\u3002\u9ec4\u53f6\u9053\u4eba\u4e00\u542c\uff0c\u604d\u7136\u5927\u609f\uff1a\u539f\u6765\u8fd9\u4e2a\u5c11\u5973\u4e0d\u662f\u94c1\u98de\u9f99\u7684\u5973\u513f\uff0c\u800c \u662f\u6c5f\u6e56\u4e0a\u95fb\u540d\u843d\u80c6\u7684\u7389\u7f57\u5239\uff01\u600e\u9ebd\u5374\u662f\u8fd9\u6837\u5e74\u8f7b\uff01 \u3000\u3000\u7389\u7f57\u5239\u683c\u683c\u7684\u7b11\u4e2a\u4e0d\u4f11\uff0c\u5e76\u4e0d\u7b54\u8bdd\u3002\u7ea2\u4e91\u9053\u4eba\u60ca\u6115\u5f53\u573a\uff0c\u53c8\u4e0d\u4fbf\u7acb\u5373\u62d4\u5251\u76f8\u903c\u3002\u5353\u4e00\u822a \u8eab\u8eaf\u98a4\u6296\uff0c\u803f\u7ecd\u5357\u770b\u4ed6\u9762\u8272\u6709\u5f02\uff0c\u8f7b\u8f7b\u7684\u8d70\u8fd1\u4ed6\u7684\u8eab\u8fb9\uff0c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4f60\u600e\u9ebd\u5566\uff1f\u201d \u5353\u4e00\u822a\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u9ebd\u3002\u201d\u803f\u7ecd\u5357\u9053\uff1a\u201c\u8fd9\u5973\u5f3a\u76d7\u5251\u6cd5\u975e\u5e38\u5389\u5bb3\uff0c\u6211\u53ea\u6015\u5e08\u7236\u514b\u5979\u4e0d\u4f4f\u3002\u5e08 \u5144\uff0c\u4f60\u53ef\u8981\u65e9\u505a\u51c6\u5907\uff0c\u4e0d\u80fd\u8ba9\u5979\u9003\u8dd1\uff01\u201d\u5353\u4e00\u822a\u832b\u7136\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u4e2d\u4f46\u671b\u8fd9\u573a\u5251\u6bd4\u4e0d\u6210\u3002 \u3000\u3000\u94c1\u98de\u9f99\u5728\u7b11\u58f0\u4e2d\u8d70\u5230\u573a\u5fc3\uff0c\u6717\u58f0\u95ee\u9053\uff1a\u201c\u7ec3\u513f\uff0c\u4f60\u771f\u7684\u6b20\u4e86\u6b66\u5f53\u6d3e\u7684\u503a\u5417\uff1f\u201d\u7389\u7f57\u5239\u7b11 \u9053\uff1a\u201c\u4e0d\u662f\u6b20\u503a\uff0c\u90a3\u662f\u5f69\u7269\u3002\u6b66\u5f53\u6d3e\u7684\u4e94\u4f4d\u95e8\u5f92\u548c\u6211\u6bd4\u5251\uff0c\u6211\u603b\u4e0d\u80fd\u7a7a\u624b\u800c\u5f52\u5440\uff0c\u8fd9\u662f\u9ed1\u9053 \u4e0a\u7684\u89c4\u77e9\uff0c\u7239\uff0c\u96be\u9053\u4f60\u8fd8\u4e0d\u77e5\u9053\uff1f\u201d\u9ec4\u53f6\u9053\u4eba\u542c\u4ed6\u4eec\u7236\u5973\u76f8\u79f0\uff0c\u53c8\u662f\u4e00\u6115\u3002\u94c1\u98de\u9f99\u6380\u987b\u7b11 \u9053\uff1a\u201c\u7ec3\u513f\uff0c\u4f60\u4e00\u5b9a\u770b\u9519\u4eba\u4e86\uff0c\u90a3\u4e9b\u4eba\u4e00\u5b9a\u662f\u5192\u6b66\u5f53\u6d3e\u4e4b\u540d\uff0c\u4f60\u8bd5\u60f3\u6b66\u5f53\u5251\u6cd5\u65e2\u7136\u72ec\u6b65\u5929 \u4e0b\uff0c\u90a3\u6709\u4ee5\u4e94\u654c\u4e00\u8fd8\u8d25\u5728\u4f60\u624b\u4e0a\u4e4b\u7406\uff1f\u201d\u4e24\u7236\u5973\u4e00\u5439\u4e00\u5531\uff0c\u7ea2\u4e91\u9053\u4eba\u66f4\u662f\u96be\u582a\uff0c\u55d6\u7684\u4e00\u58f0\uff0c \u62d4\u5251\u5728\u624b\uff0c\u559d\u9053\uff1a\u201c\u7389\u7f57\u5239\uff0c\u8fd9\u7b14\u5e10\u4f60\u8fd8\u4e5f\u4e0d\u8fd8\uff1f\u201d\u53c8\u671b\u7740\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6211\u4eec\u50fb\u5904\u6df1\u5c71\uff0c\u5b64 \u964b\u5be1\u95fb\uff0c\u7adf\u4e0d\u77e5\u4f60\u6709\u8fd9\u6837\u4e00\u4f4d\u6709\u5927\u672c\u4e8b\u7684\u5973\u513f\uff0c\u6211\u4eec\u5728\u4f60\u7684\u9762\u524d\u5411\u4f60\u7684\u5973\u513f\u8ba8\u503a\uff0c\u5b9e\u5728\u592a\u4e0d \u606d\u656c\uff0c\u4f46\u6740\u4eba\u507f\u547d\uff0c\u6b20\u503a\u8fd8\u94b1\uff0c\u6211\u4e5f\u6ca1\u6709\u529e\u6cd5\u3002\u201d\u94c1\u98de\u9f99\u5927\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u4e2a\u5973\u513f\u53ef\u662f\u4e0e\u4f17\u4e0d \u540c\uff0c\u4ed6\u505a\u7684\u4e8b\u60c5\uff0c\u53ef\u4ece\u6765\u4e0d\u8981\u6211\u7ba1\uff0c\u5979\u6709\u4ec0\u9ebd\u503a\u52a1\u7ea0\u7eb7\uff0c\u5979\u81ea\u4f1a\u6599\u7406\u3002\u4f60\u4eec\u53ef\u522b\u8981\u8feb\u6211\u66ff\u5979 \u8fd8\u503a\u3002\u201d\u9ec4\u53f6\u7ea2\u4e91\u751a\u89c9\u5947\u5f02\uff0c\u542c\u94c1\u98de\u9f99\u7684\u8bdd\uff0c\u53c8\u7edd\u4e0d\u4f3c\u662f\u7236\u5973\u5173\u7cfb\u3002\u94c1\u98de\u9f99\u987f\u4e86\u4e00\u987f\uff0c\u53c8 \u9053\uff1a\u201c\u53ef\u662f\u6211\u505a\u7236\u4eb2\u7684\u4e5f\u5f97\u4e3b\u6301\u516c\u9053\uff0c\u662f\u4f60\u4e00\u4e2a\u4eba\u5411\u5979\u8ba8\u503a\u5462\uff0c\u8fd8\u662f\u4f60\u4eec\u4eca\u65e5\u6765\u7684\u6b66\u5f53\u6d3e\u4e24 \u4ee3\u9ad8\u4eba\u90fd\u8981\u5411\u5979\u8ba8\u503a\u5462\uff1f\u201d\u7ea2\u4e91\u6012\u9053\uff1a\u201c\u53ea\u8981\u4f60\u4e0d\u51fa\u624b\uff0c\u6211\u4eec\u6b66\u5f53\u6d3e\u4eba\u7edd\u4e0d\u4ee5\u591a\u4e3a\u80dc\u3002\u201d\u94c1 \u98de\u9f99\u7b11\u9053\uff1a\u201c\u662f\u9ebd\u4e86\u5176\u5b9e\u4f60\u4eec\u591a\u4e0a\u51e0\u4e2a\u4e5f\u4e0d\u8981\u7d27\uff0c  \u671b\u9ec4\u53f6\u9053\u5144\u6c89\u5f97\u4e0b\u6c14\uff0c\u6211\u8001\u5934\u513f\u5012\u4e0d\u5acc \u70e6\uff0c\u613f\u966a\u4ed6\u9759\u5750\u770b\u5251\u3002\u201d\u8fd9\u8bdd\u5373\u662f\u8bf4\uff1a  \u8981\u9ec4\u53f6\u9053\u4eba\u4e0d\u52a8\u624b\uff0c\u4f60\u4eec\u5168\u90e8\u4e0a\u6765\uff0c\u90fd\u4e0d\u662f\u7389\u7f57\u5239 \u5bf9\u624b\u3002\u7ea2\u4e91\u8d8a\u53d1\u5927\u6012\u3002 \u3000\u3000\u94c1\u98de\u9f99\u548c\u9ec4\u53f6\u9053\u4eba\u6253\u4e86\u4e00\u4e2a\u62db\u547c\uff0c\u5404\u81ea\u9000\u4e0b\u3002\u7ea2\u4e91\u9053\u4eba\u9053\uff1a\u201c\u7389\u7f57\u5239\uff0c\u4f60\u8fd8\u4e0d\u4eae\u5251\uff0c\u66f4 \u5f85\u4f55\u65f6\uff1f\u201d\u7389\u7f57\u5239\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u957f\u8005\u6709\u547d\uff0c\u5c0f\u8f88\u4e0d\u6562\u4e0d\u5c0a\uff01\u6211\u4e0d\u6562\u50ed\u4e0a\uff0c\u8bf7\u4f60\u5148\u8fdb\u62db \u5440\uff01\u201d \u3000\u3000\u7ea2\u4e91\u5484\u5484\u903c\u4eba\uff0c\u7389\u7f57\u5239\u7adf\u662f\u82e5\u65e0\u5176\u4e8b\uff0c\u53e3\u8bf4\u9075\u547d\uff0c\u5374\u5e76\u4e0d\u62d4\u5251\u3002\u7ea2\u4e91\u9053\u4eba\u6c14\u6781\uff0c\u628a\u5251\u5728 \u9798\u4e2d\u4e00\u63d2\uff0c\u5de6\u638c\u7a81\u53d1\uff0c\u888d\u8896\u5e26\u98ce\uff0c\u9a88\u4f38\u4e8c\u6307\uff0c\u4e00\u4e2a\u201c\u753b\u9f99\u70b9\u775b\u201d\uff0c\u8ff3\u5411\u7389\u7f57\u5239\u9762\u95e8\u70b9\u53bb\uff0c\u90a3 \u77e5\u7389\u7f57\u5239\u8eab\u5f62\u5fae\u6643\uff0c\u7ea2\u4e91\u9053\u4eba\u6251\u4e86\u4e2a\u7a7a\uff0c\u5ffd\u89c9\u80cc\u5f8c\u91d1\u5203\u631f\u98ce\u4e4b\u58f0\uff0c\u4e00\u56e2\u51b7\u6c14\u500f\u5ffd\u8feb\u6765\uff0c\u7ea2\u4e91 \u9053\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u5e78\u4ed6\u6b66\u529f\u6781\u9ad8\uff0c\u811a\u5c16\u70b9\u5730\uff0c\u4e00\u4e2a\u201c\u5f2f\u8170\u63d2\u67f3\u201d\uff0c\u8fd0\u7528\u65cb\u8eab\u4e4b\u529b\uff0c\u98de\u7a9c\u51fa\u53bb\uff0c \u5728\u65cb\u8eab\u4e4b\u9645\uff0c\u8fd8\u5356\u5f04\u4e86\u4e00\u624b\u6b66\u5f53\u6d3e\u201c\u9e33\u9e2f\u8fde\u73af\u817f\u201d\u7684\u7edd\u9876\u529f\u592b\uff0c\u542c\u98ce\u8fa8\u5668\uff0c\u5de6\u811a\u5411\u5f8c\u4e00\u8e6c\uff0c \u5411\u7389\u7f57\u5239\u6301\u5251\u7684\u624b\u8155\u75be\u8e22\uff0c\u7389\u7f57\u5239\u4e00\u4e2a\u6ed1\u6b65\u79fb\u8eab\uff0c\u7ea2\u4e91\u5df2\u7eb5\u51fa\u4e08\u8bb8\u4e4b\u5730\u53c8\u8f6c\u8fc7\u8eab\u6765\u3002\u7389\u7f57\u5239 \u957f\u5251\u5728\u624b\uff0c\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u9053\u957f\u600e\u9ebd\u4e0d\u62d4\u5251\u5440\uff1f\u201d \u3000\u3000\u7ea2\u4e91\u9053\u4eba\u6697\u6697\u5438\u4e86\u4e00\u5de8\u4f60\u6c14\uff0c\u8fd9\u7389\u7f57\u5239\u8eab\u624b\u4e4b\u5feb\uff0c\u771f\u662f\u751f\u5e73\u4ec5\u89c1\uff01\u5979\u7adf\u80fd\u5728\u907f\u62db\u4e4b\u9645\uff0c \u4e00\u4e2a\u6643\u8eab\uff0c\u5c31\u7acb\u523b\u62d4\u5251\u8fdb\u62db\uff0c\u81ea\u5df1\u4e00\u5ff5\u8f7b\u654c\uff0c\u9c81\u83bd\u75be\u8fdb\uff0c\u5c31\u51e0\u4e4e\u5403\u4e86\u5927\u4e8f\u3002 \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u5728\u65c1\u89c2\u6218\uff0c\u4e5f\u662f\u5927\u4e3a\u60ca\u5947\uff0c\u8fd9\u7389\u7f57\u5239\u529f\u529b\u5982\u4f55\u8fd8\u672a\u77e5\u9053\uff0c\u4f46\u8fd9\u4efd\u8f7b\u8eab\u529f\u592b\uff0c\u5374 \u786e\u5df2\u5728\u94c1\u98de\u9f99\u4e4b\u4e0a\uff0c\u770b\u6765\u5979\u7684\u6b66\u529f\u7edd\u975e\u94c1\u98de\u9f99\u6240\u4f20\u4e86\u3002 \u3000\u3000\u7ea2\u4e91\u9053\u4eba\u8fd9\u65f6\u90a3\u91cc\u8fd8\u6562\u6020\u6162\uff0c\u6025\u5fd9\u628a\u5251\u62d4\u51fa\uff0c\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u6b21\u8981\u8bf7\u59d1\u5a18\u5148\u8d50\u62db\u3002\u201d\u8fde\u8bdd \u58f0\u4e5f\u5df2\u8c26\u548c\u8bb8\u591a\u3002\u7389\u7f57\u5239\u53c8\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\u58f0\uff1a\u201c\u6709\u50ed\uff01\u201d\u5de6\u624b\u634f\u7740\u5251\u8bc0\u4e00\u6307\uff0c\u53f3\u81c2\u5411\u524d\u4e00 \u9012\uff0c\u5251\u5c16\u9752\u5149\u95ea\u52a8\uff0c\u7adf\u7136\u8e0f\u6b63\u4e2d\u5bab\u5411\u7ea2\u4e91\u9053\u4eba\u80f8\u574e\u523a\u6765\u3002\u6b66\u5b66\u6709\u4e91\uff1a\u201c\u5251\u8d70\u4e00\u504f\uff0c\u67aa\u7b31\u4e00 \u7ebf\u3002\u201d\u53c8\u9053\uff1a\u201c\u5200\u8d70\u767d\uff0c\u5251\u8d70\u9ed1\u3002\u201d\u610f\u601d\u662f\u8bf4\uff0c\u5251\u672f\u5e94\u4ee5\u8f7b\u7075\u7fd4\u52a8\u4e3a\u4e3b\uff0c\u51e1\u4f7f\u5251\u7684\u591a\u7531\u5de6\u53f3 \u504f\u950b\u8d70\u8fdb\uff0c\u5f88\u5c11\u8e0f\u6b63\u4e2d\u5bab\u3002\u800c\u4eca\u7389\u7f57\u5239\u8d77\u624b\u7b2c\u4e00\u62db\u5c31\u5954\u6b63\u9762\u4e2d\u950b\u523a\u6765\uff0c\u8fd9\u7b80\u76f4\u662f\u4e00\u79cd\u85d0\u89c4\u3002 \u7ea2\u4e91\u9053\u4eba\u867d\u7136\u5bf9\u7389\u7f57\u5239\u5df2\u8f6c\u4e86\u89c2\u611f\uff0c\u628a\u5979\u5f53\u6210\u4e86\u5e73\u7b49\u7684\u5bf9\u624b\uff0c\u4f46\u89c1\u5979\u5982\u6b64\u85d0\u89c6\uff0c\u4e5f\u4e0d\u7981\u52a8\u4e86 \u771f\u6c14\uff0c\u5b9d\u5251\u4e00\u5708\uff0c\u8fce\u7740\u7389\u7f57\u5239\u5251\u950b\uff0c\u4e00\u62db\u201c\u5c71\u821e\u94f6\u86c7\u201d\u75be\u5708\u51fa\u53bb\uff0c\u8fd9\u62db\u662f\u6b66\u5f53\u6d3e\u4e03\u5341\u4e8c\u624b\u8fde \u73af\u593a\u547d\u5251\u4e2d\u7684\u4e00\u7740\u7edd\u62db\uff0c\u4e13\u7834\u654c\u4eba\u4ece\u6b63\u9762\u523a\u6765\u7684\u62db\u6570\u3002\u9ec4\u53f6\u9053\u4eba\u5728\u65c1\u770b\u5f97\u6697\u6697\u53eb\u597d\uff0c\u5fc3\u60f3\uff1a \u5e08\u5f1f\u7684\u5251\u672f\u786e\u662f\u5927\u6709\u8fdb\u5883\uff0c\u8fd9\u62db\u62ff\u634f\u65f6\u5019\uff0c\u6070\u5230\u597d\u5904\uff0c\u8fd9\u4e00\u5708\u4e00\u5e26\uff0c\u7eb5\u654c\u4eba\u591a\u5f3a\uff0c\u5175\u5203\u4e5f\u8981 \u88ab\u593a\u51fa\u624b\uff01 \u3000\u3000\u7ea2\u4e91\u9053\u4eba\u4e5f\u662f\u5982\u6b64\u5fc3\u60f3\uff0c\u6ee1\u4ee5\u4e3a\u5341\u62ff\u4e5d\u7a33\uff0c\u90a3\u6599\u7389\u7f57\u5239\u7684\u5251\u672f\u5b8c\u5168\u4e0d\u4f9d\u5e38\u8f68\uff0c\u770b\u5979\u4e2d\u950b \u8fdb\u5251\uff0c\u660e\u662f\u201c\u6bd2\u86c7\u5410\u4fe1\u201d\u7684\u62db\u6570\uff0c\u4e0d\u77e5\u600e\u7684\u5251\u950b\u4e00\u98a4\uff0c\u5374\u5ffd\u7136\u6ed1\u8fc7\u4e00\u8fb9\uff0c\u5de6\u523a\u80a9\u80db\uff0c\u517c\u6302\u81c2 \u80c1\uff0c\u7ea2\u4e91\u9053\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u4eba\u5e26\u5251\u8f6c\u4e86\u534a\u5708\uff0c\u624d\u907f\u5f00\u8fd9\u62db\uff0c\u7389\u7f57\u5239\u8ddf\u8e2a\u6025\u8fdb\uff0c\u8eac\u8170\u9012\u81c2\uff0c\u957f \u5251\u7a81\u5982\u98ce\u53d1\u3002 \u3000\u3000\u7ea2\u4e91\u9053\u4eba\u660e\u660e\u770b\u51fa\u5979\u8fd9\u4e00\u62db\u662f\u201c\u9f99\u95e8\u9f13\u6d6a\u201d\u7684\u62db\u6570\uff0c\u6025\u4e3e\u5251\u4e0a\u64a9\uff0c\u90a3\u77e5\u7389\u7f57\u5239\u5251\u5230\u4e2d \u9014\uff0c\u5ffd\u7136\u53d8\u4e86\u65b9\u5411\uff0c\u4f3c\u4e0a\u53cd\u4e0b\uff0c\u4f3c\u5de6\u53cd\u53f3\uff0c\u7ea2\u4e91\u9053\u4eba\u624b\u5fd9\u811a\u4e71\uff0c\u7ed9\u8feb\u5f97\u8fde\u8fde\u5f8c\u9000\u3002\u4f46\u6b66\u5f53\u5251 \u6cd5\uff0c\u5230\u5e95\u4e0d\u662f\u5f92\u6709\u865a\u540d\u53ef\u6bd4\uff0c\u4ed6\u6321\u4e86\u51e0\u62db\u4e4b\u5f8c\uff0c\u867d\u7136\u6df1\u89c9\u7389\u7f57\u5239\u7684\u5251\u6cd5\u5947\u8be1\u65e0\u6bd4\uff0c\u4f46\u4e5f\u60ed\u6e10 \u770b\u51fa\u4e00\u4e9b\u9053\u7406\uff0c\u4e0d\u4f3c\u521d\u65f6\u5fd9\u4e71\u3002\u4ed6\u62b1\u5b9a\u4e3b\u610f\uff0c\u628a\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u5251\u6cd5\u9010\u4e00\u5c55\u5f00\uff0c\u4f7f\u5f97\u4e2a\u98ce\u96e8\u4e0d \u900f\uff0c  \u5b88\u4e0d\u653b\u3002\u8981\u77e5\u6b66\u5f53\u6d3e\u4e43\u5185\u5bb6\u6b63\u5b97\uff0c\u5251\u672f\u7ecf\u8fc7\u5386\u4ee3\u9ad8\u624b\u589e\u76ca\uff0c\u786e\u662f\u4e25\u5bc6\u7cbe\u6df1\uff0c\u8981\u4e0d\u7136\u600e \u80fd\u6709\u201c\u5929\u4e0b\u7b2c\u4e00\u201d\u7684\u58f0\u8a89\uff1f\u7389\u7f57\u5239\u5728\u4ed6\u4e25\u9632\u8c28\u5b88\u4e4b\u4e0b\uff0c\u4e00\u65f6\u95f4\u5012\u653b\u4e0d\u8fdb\u53bb\u3002 \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u624b\u5fc3\u6dcc\u6c57\uff0c\u8fd9\u65f6\u624d\u6697\u6697\u677e\u4e86\u53e3\u6c14\u3002\u4f46\u7ea2\u4e91\u9053\u4eba\u8fd8\u662f\u6478\u4e0d\u900f\u7389\u7f57\u5239\u7684\u65b0\u5947\u5251\u6cd5\uff0c \u8f97\u8f6c\u653b\u62d2\uff0c\u53c8\u6597\u4e86\u4e94\u4e03\u5341\u62db\uff0c\u7389\u7f57\u5239\u603b\u662f\u7a33\u5360\u4e0a\u98ce\uff0c\u5904\u5904\u4e3b\u52a8\u3002\u9ec4\u53f6\u9053\u4eba\u5fc3\u60c5\u53c8\u590d\u7d27\u5f20\uff0c\u5fc3 \u77e5\u9ad8\u624b\u6bd4\u5251\uff0c\u82e5\u7136\u7eb8\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u5219\u5fc5\u5904\u5904\u53d7\u654c\u6240\u5236\uff0c\u65f6\u95f4\u4e00\u4e45\uff0c\u5fc5\u6709\u7834\u7efd\u4e3a\u654c\u6240\u4e58\u3002\u4ed6\u81ea \u5df2\u8f88\u4efd\u6781\u5c0a\uff0c\u53c8\u4e0e\u94c1\u98de\u9f99\u6709\u7ea6\uff0c\u5f53\u7136\u4e0d\u80fd\u51fa\u624b\u76f8\u6551\u3002\u8fd9\u65f6\u5353\u4e00\u822a\u6b63\u5de7\u5728\u4ed6\u8eab\u8fb9\uff0c\u4ed6\u8f7b\u8f7b\u7684\u5c06 \u4ed6\u7684\u624b\u62c9\u4e86\u4e00\u4e0b\uff0c\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u518d\u7b49\u4e00\u4f1a\uff0c\u4f60\u53bb\u628a\u5e08\u53d4\u66ff\u4e0b\u6765\u5427\u3002\u201d\u5353\u4e00\u822a\u6b66\u529f\u5728\u7b2c\u4e8c\u4ee3\u5f1f \u5b50\u4e4b\u4e2d\u9996\u5c48\u4e00\u6307\uff0c\u867d\u7136\u6bd4\u8d77\u7ea2\u4e91\u8fd8\u8981\u7a0d\u5dee\u4e00\u7b79\uff0c\u4f46\u5e74\u8f7b\u529b\u58ee\uff0c\u5374\u8981\u80dc\u8fc7\u5e08\u53d4\u3002\u6240\u4ee5\u9ec4\u53f6\u9053\u4eba \u5fc3\u60f3\uff1a\u53eb\u4ed6\u51fa\u53bb\u6700\u5c11\u53ef\u4ee5\u62b5\u6321\u53c1\u4e94\u5341\u62db\uff0c\u800c\u4e14\u5353\u4e00\u822a\u662f\u5c0f\u8f88\uff0c\u867d\u8d25\u4e0d\u8fb1\uff0c\u6321\u5f97\u4e00\u9635\uff0c\u518d\u4f5c\u6253 \u7b97\u3002 \u3000\u3000\u5353\u4e00\u822a\u8fd9\u65f6\u5982\u75f4\u5982\u5446\uff0c\u76ee\u6ce8\u6597\u573a\uff0c\u624b\u8db3\u51b0\u51b7\u3002\u9ec4\u53f6\u9053\u4eba\u62c9\u4ed6\u7684\u624b\uff0c\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u770b\u4ed6 \u4e00\u773c\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u6709\u75c5\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u6447\u4e86\u64ad\u5934\uff0c\u9ec4\u53f6\u9053\u4eba\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u542c\u6e05\u695a\u4e86\u6211\u7684\u8bdd \u9ebd\uff1f\u201d\u5353\u4e00\u822a\u832b\u7136\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u662f\u771f\u662f\u5047\uff0c\u9ec4\u53f6\u9053\u4eba\u89c1\u4ed6\u9b42\u4e0d\u5b88\u820d\u7684\u6a21\u6837\uff0c\u5341\u5206\u5fe7 \u8651\u3002 \u3000\u3000\u8fd9\u65f6\u573a\u4e2d\u6597\u5f97\u8d8a\u53d1\u6fc0\u70c8\uff0c\u7ea2\u4e91\u9053\u4eba\u5df2\u662f\u989d\u5934\u89c1\u6c57\u3002\u7389\u7f57\u5239\u5ffd\u7136\u4e00\u58f0\u957f\u7b11\uff0c\u633d\u4e86\u4e00\u4e2a\u5251 \u82b1\uff0c\u76f4\u523a\u7ea2\u4e91\u5de6\u624b\u624b\u8155\uff0c\u7ea2\u4e91\u4e3e\u5251\u4e00\u6321\uff0c\u5979\u624b\u8155\u4e00\u7f29\uff0c\u5251\u950b\u500f\u7684\u81ea\u4e0a\u800c\u4e0b\uff0c\u6765\u52bf\u5206\u660e\u662f\u523a\u5411 \u819d\u76d6\u7684\u5173\u8282\uff0c\u8fd9\u4e00\u62db\u7adf\u662f\u6b66\u5f53\u6d3e\u7684\u5251\u6cd5\uff0c\u540d\u4e3a\u201c\u91d1\u9488\u5ea6\u4e16\u201d\uff0c\u7ea2\u4e91\u5927\u51fa\u610f\u5916\uff01 \u3000\u3000\u672c\u6765\u7ea2\u4e91\u548c\u5979\u6597\u4e86\u4e00\u767e\u591a\u62db\uff0c\u5df2\u6e10\u60ed\u770b\u51fa\u5979\u7684\u5251\u5f0f\u4e0e\u666e\u901a\u5251\u6cd5\u76f8\u53cd\uff0c\u6478\u4e0d\u7740\u7834\u6cd5\uff0c  \u597d \u575a\u5fcd\u81ea\u6301\uff0c\u4e0d\u4e3a\u654c\u8bf1\uff0c\u4e25\u5bc6\u9632\u5b88\uff0c\u5148\u6c42\u65e0\u8fc7\u3002\u4f46\u9aa4\u7136\u4e4b\u95f4\uff0c\u5ffd\u89c1\u654c\u4eba\u653b\u6765\u7684\u62db\u6570\u4e43\u662f\u672c\u95e8\u5251 \u6cd5\uff0c\u4e00\u65f6\u5fd8\u4e86\u5979\u7684\u5251\u5f0f\u603b\u662f\u76f8\u53cd\u4e4b\u7406\uff0c\u7adf\u7136\u62a2\u5230\u5916\u95e8\uff0c\u5251\u628a\u4e00\u65cb\uff0c\u75be\u8f6c\u4e24\u5708\uff0c\u8fd9\u4e00\u62db\u540d\u4e3a \u201c\u53c1\u8f6c\u6cd5\u8f6e\u201d\uff0c\u672c\u6765\u662f\u6321\u201c\u91d1\u9488\u5ea6\u4e16\u201d\u7684\u5999\u62db\uff0c\u4e0d\u6599\u7389\u7f57\u5239\u660e\u662f\u4e0b\u523a\uff0c\u5ffd\u7136\u5251\u950b\u53cd\u5f39\uff0c\u5411\u4e0a \u4e00\u7ede\uff0c\u7ea2\u4e91\u7684\u5251\u8ddf\u5979\u7684\u5251\u65cb\u4e86\u4e24\u65cb\uff0c\u51e0\u4e4e\u8131\u624b\u98de\u53bb\u3002\u6b63\u662f\uff1a\u773c\u82b1\u64a9\u4e71\u5904\uff0c\u5251\u6cd5\u89c1\u795e\u5947\u3002\u6b32\u77e5 \u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377\u2014\u2014\u7b2c\u516b\u56de\u3000\u8c26\u8c22\u638c\u95e8\u3000\u60c5\u7f18\u96be\u65a9\u65ad\u3000\u96be\u6536\u8986\u6c34\u3000\u6068\u610f\u6731\u5168\u6d88 \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377 \u7b2c\u516b\u56de \u8c26\u8c22\u638c\u95e8\u3000\u60c5\u7f18\u96be\u65a9\u65ad \u96be\u6536\u8986\u6c34\u3000\u6068\u610f\u6731\u5168\u6d88 \u3000\u3000\u803f\u7ecd\u5357\u770b\u5e08\u7236\u5371\u6025\uff0c\u60ca\u53eb\u4e00\u58f0\uff0c\u6b63\u60f3\u62c9\u865e\u65b0\u57ce\u62a2\u51fa\uff0c\u53ea\u89c1\u7ea2\u4e91\u9053\u4eba\u9000\u5f8c\u4e24\u6b65\uff0c\u5df3\u8131\u4e86 \u9669\u3002\u539f\u6765\u7ea2\u4e91\u5251\u6cd5\u867d\u975e\u7389\u7f57\u5239\u4e4b\u654c\uff0c\u4f46\u529f\u529b\u9887\u9ad8\uff0c\u5371\u6025\u4e4b\u9645\uff0c\u6025\u8fd0\u5185\u529b\u5c06\u7389\u7f57\u5239\u7684\u5251\u4e00\u9ecf\uff0c \u7a0d\u5fae\u6d88\u4e86\u6765\u52bf\uff0c\u5c31\u7acb\u523b\u62cd\u5251\u9000\u8eab\uff0c\u5401\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u7389\u7f57\u5239\u5fae\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u672a\u89c1\u80dc\u8d1f\u3002\u6211\u770b\u8fd9\u7b14\u503a\u4e00\u7b14\u52fe\u6d88\u4e86\u5427\uff0c\u54b1\u4eec\u4e0d\u5fc5 \u6597\u4e86\u3002\u201d\u7389\u7f57\u5239\u8fd9\u662f\u770b\u5728\u5353\u4e00\u822a\u9762\u4e0a\uff0c\u624d\u5982\u6b64\u8bf4\u6cd5\uff0c\u4e3a\u7ea2\u4e91\u9053\u4eba\u7559\u70b9\u9762\u5b50.\u90a3\u77e5\u7ea2\u4e91\u9053\u4eba\u5df2 \u6597\u5f97\u660f\u5934\u660f\u8111\uff0c\u5728\u5f92\u5f1f\u9762\u524d\uff0c\u6218\u4e00\u4e2a\u5c0f\u8f88\u4e0d\u4e0b\uff0c\u90a3\u80af\u5e72\u4f11\uff1f\u542c\u4e86\u8fd9\u8bdd\uff0c\u66f4\u662f\u5982\u706b\u6dfb\u6cb9\uff0c\u94c1\u9752 \u7740\u9762\uff0c\u54ac\u5b9e\u7259\u6839\uff0c\u5237\u7684\u4e00\u5251\uff0c\u53c8\u5411\u7389\u7f57\u5239\u523a\u53bb\uff01 \u3000\u3000\u7389\u7f57\u5239\u79c0\u7709\u4e00\u6311\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u8fd8\u8981\u6597\uff1f\u201d\u5251\u950b\u4e00\u504f\uff0c\u6233\u4ed6\u53f3\u4fa7\uff0c\u8fd9\u4e00\u62db\u53c8\u662f\u6b66\u5f53 \u6d3e\u7684\u5251\u6cd5\uff0c\u540d\u4e3a\u201c\u767d\u9e64\u5544\u9c7c\u201d\uff0c\u6309\u8bf4\u7ea2\u4e91\u521a\u624d\u5403\u4e86\u5927\u4e8f\uff0c\u5e94\u8be5\u8b66\u9192\uff0c\u6025\u5fd9\u9000\u5b88\u4e3a\u662f\u3002\u4e0d\u6599\u7ea2 \u4e91\u5728\u672c\u95e8\u5251\u6cd5\u4e0a\u6c89\u6d78\u4e86\u51e0\u5341\u5bd2\u6691\uff0c\u5fc3\u5251\u5408\u4e00\uff0c\u5df2\u6210\u4e60\u60ef\uff0c\u4e00\u89c1\u7389\u7f57\u5239\u4f7f\u7684\u662f\u672c\u95e8\u5251\u6cd5\uff0c\u4e0d\u77e5 \u4e0d\u89c9\u53c8\u62a2\u5230\u5916\u95e8\uff0c\u6a2a\u5251\u4e00\u5c01\uff0c\u4f7f\u4e86\u4e00\u62db\u201c\u6a2a\u6c5f\u622a\u6597\u201d\uff0c\u7389\u7f57\u5239\u53cd\u624b\u4e00\u5251\uff0c\u5251\u52bf\u4e00\u8f6c\uff0c\u53ea\u542c\u5f97 \u201c\u53ee\u5f53\u201d\u4e00\u58f0\uff0c\u7ea2\u4e91\u9053\u4eba\u7684\u5251\uff0c\u987f\u65f6\u8131\u624b\u98de\u51fa\u3002 \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u6025\u6781\uff0c\u63a8\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u51fa\u53bb\uff01\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u865e\u65b0\u57ce\u548c\u51e0\u4e2a\u540c\u95e8\u5df2 \u7eb7\u7eb7\u62a2\u51fa\u3002\u5353\u4e00\u822a\u4ea1\u9b42\u5931\u9b44\uff0c\u614c\u5fd9\u62d4\u5251\u4e0a\u524d\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u91d1\u94c1\u4ea4\u9e23\u4e4b\u58f0\uff0c\u7389\u7f57\u5239\u767d\u8863\u98d8\u98d8\uff0c \u5de6\u7a7f\u6709\u63d2\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u4e94\u4e2a\u6b66\u5f53\u5f1f\u5b50\uff0c\u624b\u4e2d\u957f\u5251\u5168\u90fd\u8131\u624b\u98de\u53bb\u201c\u8fd8\u6709\u4e00\u4e2a\u803f\u7ecd\u5357\u521a\u624d\u4e3a\u4e86\u6551 \u5e08\uff0c\u4e0d\u987e\u751f\u6b7b\uff0c\u90a3\u77e5\u51fa\u53bb\u4e4b\u5f8c\uff0c\u7ed9\u7389\u7f57\u5239\u53cc\u773c\u4e00\u77aa\uff0c\u731b\u7136\u4e00\u9707\uff0c\u52c7\u6c14\u5168\u6d88\uff0c\u7adf\u7136\u4e0d\u6562\u4ea4\u950b\uff0c \u4f0f\u5730\u4e00\u6eda\uff0c\u76f4\u6eda\u5230\u5899\u89d2\u65b9\u624d\u505c\u6b62\u3002 \u3000\u3000\u7ea2\u4e91\u9053\u4eba\u89c1\u4e00\u4f17\u5f1f\u5b50\u5982\u6b64\u72fc\u72c8\uff0c\u706b\u7ea2\u4e86\u773c\uff0c\u5728\u5730\u4e0b\u6361\u8d77\u4e00\u628a\u957f\u5251\uff0c\u5411\u7389\u7f57\u5239\u53c8\u662f\u4e00\u5251\uff0c \u7389\u7f57\u5239\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u5f85\u4f60\u7684\u5f92\u5f1f\u518d\u6361\u8d77\u5251\u6765\u4e5f\u8fd8\u4e0d\u8fdf\uff01\u201d\u7ea2\u4e91\u9053\u4eba\u7728\u773c\u4e4b\u95f4\u75be\u653b\u53c1\u5251\uff0c\u7389\u7f57 \u5239\u6a2a\u5251\u4e00\u5c01\uff0c\u7a81\u7136\u8f6c\u950b\u4e0b\u6233\uff0c\u75be\u5982\u95ea\u7535\u3002\u5353\u4e00\u822a\u8fd9\u65f6\u6070\u597d\u8d76\u5230\uff0c\u624b\u8f6f\u811a\u8f6f\uff0c\u89c1\u5e08\u53d4\u5371\u6025\uff0c\u6ca1 \u5948\u4f55\u4e00\u5251\u523a\u51fa\uff0c\u7389\u7f57\u5239\u53eb\u9053\uff1a\u201c\u4f60\u597d\uff01\u201d\u5ffd\u7136\u5c16\u53eb\u4e00\u58f0\uff0c\u628a\u5251\u4e00\u6492\uff0c\u6389\u5728\u5730\u4e0a\uff0c\u5411\u5f8c\u5012\u7eb5\u4e08 \u8bb8\uff0c\u624b\u81c2\u4e0a\u767d\u8863\u5df2\u73b0\u8840\u8ff9\uff01 \u3000\u3000\u7389\u7f57\u5239\u539f\u662f\u4e2a\u597d\u5f3a\u4e89\u80dc\u7684\u4eba\uff0c\u6240\u4ee5\u521d\u6597\u7ea2\u4e91\u4e4b\u65f6\uff0c\u867d\u7136\u788d\u65bc\u5353\u4e00\u822a\u60c5\u4efd\uff0c\u60f3\u8ba9\u7ea2\u4e91\u9053\u4eba \u4e00\u62db\u534a\u62db\uff0c\u4f46\u89c1\u7ea2\u4e91\u5484\u5484\u8feb\u4eba\uff0c\u4e00\u65f6\u52a8\u4e86\u813e\u6c14\uff0c\u6597\u5230\u9163\u65f6\uff0c\u90a3\u8fd8\u80af\u8ba9\uff1f\u5230\u80dc\u4e86\u7ea2\u4e91\uff0c\u53c8\u593a\u4e86 \u6b66\u5f53\u4f17\u5f1f\u5b50\u7684\u5175\u5203\u4e4b\u5f8c\uff0c\u8fd9\u624d\u731b\u7136\u5f8c\u6094\uff0c\u4e0d\u77e5\u8fd9\u5c40\u6b8b\u68cb\u5982\u4f55\u6536\u62ac\uff1f\u6240\u4ee5\u5230\u4e86\u5353\u4e00\u822a\u6325\u5251\u6765 \u65f6\uff0c\u5979\u6545\u610f\u8ba9\u7ea2\u4e91\u7684\u5251\u950b\uff0c\u8f7b\u8f7b\u64e6\u8fc7\u624b\u81c2\uff0c\u88c5\u51fa\u8d1f\u4f24\u8d25\u9003\uff01 \u3000\u3000\u7ea2\u4e91\u9053\u4eba\u5012\u53cd\u5403\u4e86\u4e00\u9a7e\uff0c\u89c1\u7389\u7f57\u5239\u5f03\u5251\u8d25\u9003\uff0c\u51e0\u7591\u662f\u68a6\uff01\u633a\u7740\u957f\u5251\uff0c\u7adf\u7136\u4e0d\u6562\u8ffd\u53bb\uff0c\u5c31 \u5728\u8fd9\u65f6\uff0c\u5ffd\u542c\u5f97\u94c1\u98de\u9f99\u4e00\u58f0\u5927\u543c\uff0c\u9ec4\u53f6\u9053\u4eba\u5636\u58f0\u53eb\u5524\uff01 \u3000\u3000\u539f\u6765\u5728\u5353\u4e00\u822a\u5954\u51fa\u4e4b\u5f8c\uff0c\u9ec4\u53f6\u9053\u4eba\u8033\u542c\u65ad\u91d1\u621b\u7389\u4e4b\u58f0\uff0c\u773c\u89c1\u95e8\u4eba\u72fc\u72c8\u4e4b\u72b6\uff0c\u53c8\u89c1\u5353\u4e00\u822a \u811a\u6b65\u8e09\u8dc4\uff0c\u663e\u7136\u8fdc\u975e\u7389\u7f57\u5239\u4e4b\u654c\uff1a\u8fd9\u65f6\u518d\u7531\u4e0d\u5f97\u9ec4\u53f6\u9053\u4eba\u77dc\u6301\uff0c\u53cc\u81c2\u4e00\u632f\uff0c\u6025\u5fd9\u98de\u63a0\u4e0a\u53bb\u3002 \u8fd9\u8fb9\u53a2\u9ec4\u53f6\u9053\u4eba\u8eab\u5f62\u4e00\u8d77\uff0c\u90a3\u8fb9\u53a2\u94c1\u98de\u9f99\u888d\u8896\u4e00\u62c2\uff0c\u4e5f\u5982\u5927\u96c1\u98de\u6765\uff0c\u4e24\u4eba\u51fa\u638c\u76f8\u62b5\uff0c\u201c\u84ec\u201d \u7684\u4e00\u58f0\uff0c\u5404\u7ed9\u9707\u9000\uff0c\u94c1\u98de\u9f99\u5927\u543c\u9053\uff1a\u201c\u9ec4\u53f6\u9053\u4eba\uff0c\u4f60\u8981\u4e0d\u8981\u8138\uff1f\u201d\u8fd9\u65f6\u7389\u7f57\u5239\u5df2\u6545\u610f\u53d7\u4f24\uff0c \u5c16\u53eb\u5f8c\u9000\u3002\u9ec4\u53f6\u9053\u4eba\u5fc3\u60ca\u52a8\u9b44\uff0c\u987e\u4e0d\u5f97\u7b54\u94c1\u98de\u9f99\u7684\u8bdd\uff0c\u54d1\u58f0\u5636\u5524\u9053\uff1a\u201c\u4e00\u822a\uff0c\u4f60\u6302\u5f69\u4e86\uff1f\u201d \u4ed6\u8fd8\u4ee5\u4e3a\u662f\u5353\u4e00\u822a\u906d\u4e86\u6bd2\u624b\u3002\u7ea2\u4e91\u9053\u4eba\u53eb\u9053\uff1a\u201c\u5e08\u5144\uff0c\u54b1\u4eec\u8d70\u5427\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u5f15\u62f3\u6b32\u51fb\uff0c\u7389\u7f57\u5239\u501a\u7740\u7d2b\u6a80\u9999\u684c\uff0c\u53eb\u9053\uff1a\u201c\u7239\uff0c\u5973\u513f\u548c\u4ed6\u4eec\u6253\u4e2a\u5e73\u624b\uff0c\u4e0d\u5fc5\u6bd4 \u4e86\uff01\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u8fd9\u662f\u600e\u9ebd\u4e2a\u8bf4\u6cd5\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u627f\u7ea2\u4e91\u9053\u957f\u8ba9\u4e86\u4e00\u573a\uff0c\u4f46\u63a5\u6218\u4ed6\u4eec \u7b2c\u4e8c\u4ee3\u5f1f\u5b50\u4e4b\u65f6\uff0c\u6211\u5374\u8f93\u4e86\u4e00\u62db\uff0c\u6240\u4ee5\u53ea\u80fd\u7b97\u662f\u626f\u5e73\uff0c\u4e24\u65e0\u4e8f\u8f93\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u65e2\u7136\u5982 \u6b64\uff0c\u90a3\u9ebd\u8fd9\u7b14\u5e10\u4e0d\u5fc5\u7b97\u4e86\uff01\u9ec4\u53f6\u9053\u5144\uff0c\u4f60\u4eec\u6709\u5927\u4e8b\u5728\u8eab\uff0c\u6211\u4e0d\u7559\u4e86\uff01\u201d\u6536\u62f3\u5f52\u5ea7\uff0c\u907d\u7136\u7aef\u837c \u9001\u5ba2\u3002\u7ea2\u4e91\u9053\u4eba\u54ed\u7b11\u4e0d\u5f97\uff0c\u9ec4\u53f6\u9053\u4eba\u77e5\u9053\u518d\u6597\u4e0b\u53bb\uff0c\u7edd\u65e0\u597d\u5904\uff0c\u53ea\u597d\u5f3a\u6291\u6012\u6c14\uff0c\u88c5\u51fa\u7b11\u5bb9\uff0c \u5411\u94c1\u98de\u9f99\u62f1\u624b\u9053\u522b\u3002\u94c1\u98de\u9f99\u9053\uff1a\u201c\u7d2b\u9633\u9053\u957f\u7075\u524d\uff0c\u4ee3\u6211\u591a\u591a\u544a\u7f6a\uff01\u201d\u9ec4\u53f6\u9053\u4eba\u9053\uff1a\u201c\u90a3\u7edd\u5fd8 \u4e0d\u4e86\uff01\u201d\u5353\u4e00\u822a\u4e5f\u968f\u7740\u9ec4\u53f6\u9053\u4eba\u62f1\u624b\u9053\u522b\uff0c\u5ffd\u89c1\u7389\u7f57\u5239\u501a\u5728\u95e8\u8fb9\uff0c\u4f3c\u7b11\u975e\u7b11\u3002\u5353\u4e00\u822a\u75be\u5fd9\u8f6c \u8eab\uff0c\u4e0d\u6562\u518d\u671b\u3002 \u3000\u3000\u4e00\u884c\u4eba\u79bb\u5f00\u94c1\u5bb6\uff0c\u7ea2\u4e91\u9053\u4eba\u9762\u8272\u7d27\u7ef7\uff0c\u4e45\u4e45\u4e0d\u8bdd\u3002\u9ec4\u53f6\u9053\u4eba\u548c\u5353\u4e00\u822a\u5e76\u8f94\u800c\u884c\uff0c\u6545\u610f\u843d \u5f8c\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u7389\u7f57\u5239\u5251\u6cd5\u5947\u8be1\u7cbe\u5999\uff0c\u679c\u7136\u4e0d\u662f\u5f92\u5177\u865a\u540d\uff0c\u600e\u9ebd\u5979\u5012\u7ed9\u4f60\u523a\u4e86\u4e00\u5251\uff1f\u201d \u5353\u4e00\u822a\u9053\uff1a\u201c\u90a3\u662f\u53c1\u5e08\u53d4\u4e4b\u529f\u3002\u201d\u9ec4\u53f6\u9053\u4eba\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u672a\u5fc5\u80fd\u591f\u80dc\u5979\u3002\u201d\u5353\u4e00\u822a \u77e5\u4ed6\u4e0d\u4fe1\uff0c\u9762\u4e0a\u4e00\u7ea2\u3002\u9ec4\u53f6\u9053\u4eba\u53c8\u9053\uff1a\u201c\u6211\u770b\u5979\u5bf9\u4f60\u5012\u662f\u624b\u4e0b\u7559\u60c5\u3002\u201d\u5353\u4e00\u822a\u77e5\u9053\u5e08\u53d4\u5df2\u7ecf \u8d77\u7591\uff0c\u53ea\u5f97\u628a\u548c\u7389\u7f57\u5239\u7ed3\u8bc6\u7684\u7ecf\u8fc7\uff0c\u7ec6\u7ec6\u8bf4\u4e86\u3002\u9ec4\u53f6\u9053\u4eba\u542c\u5353\u4e00\u822a\u8bf4\u5230\u7389\u7f57\u5239\u5728\u534e\u5c71\u7edd\u9876\u6076 \u6597\u516d\u9b54\u7b49\u4e8b\uff0c\u6697\u81ea\u60ca\u53f9\uff0c\u542c\u4e86\u7389\u7f57\u5239\u6765\u5386\u4e4b\u5f8c\uff0c\u66f4\u662f\u9a87\u7136\u3002\u6c89\u541f\u826f\u4e45\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u60f3\uff1a\u8fd9 \u5973\u5f3a\u76d7\u884c\u4e8b\u5012\u4e0d\u5bfb\u5e38\uff0c\u867d\u662f\u201c\u5996\u90aa\u201d\uff0c\u4e5f\u8fd8\u6709\u70b9\u6b63\u6c14\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u539f\u6765\u5979\u662f\u6bcd\u72fc\u6240\u4e73\u5927\uff0c \u602a\u4e0d\u5f97\u6027\u5b50\u5982\u6b64\u4e4b\u91ce\u3002\u53ea\u662f\u4f60\u662f\u4e66\u9999\u5b50\u5f1f\uff0c\u4e0d\u5b9c\u4e0e\u5979  \u6df7\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5e08\u53d4\u660e\u9274\uff0c\u5f1f\u5b50 \u5176\u5b9e\u4e0e\u5979\u5e76\u65e0\u79c1\u60c5\u3002\u201d\u9ec4\u53f6\u9053\u4eba\u7b11\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\u3002\u8981\u4e0d\u7136\u4f60\u8fd9\u638c\u95e8\u5f1f\u5b50\uff0c\u53ef\u8981\u88ab\u540c\u95e8\u7b11 \u8bdd\u3002\u201d\u5353\u4e00\u822a\u5fc3\u9053\uff1a\u8fd9\u638c\u95e8\u5f1f\u5b50\uff0c\u6211\u4e0d\u505a\u4e5f\u7f62\u3002 \u3000\u3000\u4ed6\u4eec\u6cbf\u7740\u9ec4\u6cb3\uff0c\u7ecf\u6f7c\u5173\u800c\u4eba\u6cb3\u5357\uff0c\u518d\u81ea\u5357\u9633\u6298\u4e0b\uff0c\u8fdb\u5165\u6e56\u5317\uff0c\u4e00\u8def\u4e0a\u8c08\u8c08\u8bb2\u8bb2\uff0c\u5012\u4e0d\u5bc2 \u5bde\u3002\u53ea\u662f\u7ea2\u4e91\u9053\u4eba\u548c\u865e\u65b0\u57ce\u803f\u7ecd\u5357\u7b49\uff0c\u8a00\u8c08\u4e4b\u95f4\uff0c\u5bf9\u7389\u7f57\u5239\u603b\u662f\u5145\u6ee1\u654c\u610f\u3002\u9ec4\u53f6\u9053\u4eba\u867d\u7136\u8f83 \u597d\uff0c\u4f46\u4e5f\u662f\u628a\u7389\u7f57\u5239\u89c6\u4e3a\u5f02\u7aef\u90aa\u6d3e\uff0c\u5353\u4e00\u822a\u6697\u81ea\u6168\u53f9\uff0c\u53f9\u4eba\u4e0e\u4eba\u95f4\u7684\u8bef\u4f1a\uff0c\u771f\u96be\u6d88\u9664\u3002 \u3000\u3000\u884c\u4e86\u4e8c\u5341\u591a\u5929\uff0c\u8fc7\u4e86\u8001\u6cb3\u53e3\uff0c\u6b66\u5f53\u5c71\u5df2\u7ecf\u5728\u671b\uff0c\u6b66\u5f53\u6d3e\u9053\u5bb6\u4fd7\u5bb6\u7684\u5404\u652f\u5f1f\u5b50\uff0c\u5df2\u4e91\u96c6\u5c71 \u4e0a\uff0c\u95fb\u5f97\u9ec4\u53f6\u7ea2\u4e91\u63a5\u5f97\u5353\u4e00\u822a\u5f52\u6765\uff0c\u7eb7\u7eb7\u51fa\u6765\u8fce\u63a5\uff0c\u4e0a\u5230\u5c71\u4e0a\uff0c\u767d\u77f3\u9053\u4eba\u548c\u9752  \u9053\u4eba\u4e5f\u51fa\u4e86 \u9053\u89c2\u76f8\u8fce\u3002\u5353\u4e00\u822a\u884c\u793c\u4e4b\u5f8c\uff0c\u767d\u77f3\u9053\u4eba\u5e26\u4ed6\u4eba\u5185\uff0c\u77bb\u4ef0\u7d2b\u9633\u9053\u957f\u7684\u9057\u5bb9\u3002 \u3000\u3000\u7d2b\u9633\u9053\u4eba\u901d\u4e16\u5df2\u6709\u4e24\u6708\uff0c\u6b66\u5f53\u95e8\u4e0b\u4e3a\u7b49\u5353\u4e00\u822a\u5f52\u6765\uff0c\u72b9\u81ea\u505c\u68fa\u672a\u846c\uff0c\u7d2b\u9633\u7684  \u4f53\u7528\u836f\u7269 \u9632\u8150\uff0c\u867d\u7136\u8fc7\u4e86\u4e24\u6708\uff0c\u72b9\u5982\u751f\u524d\u3002\u5353\u4e00\u822a\u63ed\u68fa\u77bb\u89c6\uff0c\u4e0d\u7981\u5927\u54ed\u6655\u5012\u3002 \u3000\u3000\u8fc7\u4e86\u8bb8\u4e45\uff0c\u5353\u4e00\u822a\u60a0\u60a0\u9192\u8f6c\uff0c\u53ea\u89c1\u56db\u4e2a\u5e08\u53d4\u548c\u7b2c\u4e8c\u4ee3\u5357\u5317\u5404\u652f\u7684\u5341\u4e8c\u4e2a\u5927\u5f1f\u5b50\u5206\u5217\u4e24 \u65c1\uff0c\u9762\u5bb9\u8083\u7a46\u3002\u9ec4\u53f6\u9053\u4eba\u5f00\u53e3\u8bf4\u9053\uff1a\u201c\u4e00\u822a\uff0c\u4f60\u5e08\u7236\u751f\u524d\u5bf9\u4f60\u7231\u62a4\u5907\u81f3\uff0c\u628a\u5e73\u751f\u6280\u827a\uff0c\u5168\u90fd \u4f20\u7ed9\u4e86\u4f60\u3002\u4e3a\u7684\u5c31\u662f\u671b\u4f60\u80fd\u7ee7\u627f\u4ed6\u7684\u9057\u4e1a\uff0c\u628a\u672c\u6d3e\u66f4\u53d1\u626c\u5149\u5927\uff0c\u4f60\u77e5\u9053\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u53e9\u9996 \u9053\uff1a\u201c\u5f1f\u5b50\u7c89\u8eab\u788e\u9aa8\uff0c\u4ea6\u4e0d\u8db3\u62a5\u7b54\u5148\u5e08\u65bc\u4e07\u4e00\u3002\u201d\u9ec4\u53f6\u9053\u4eba\u5c06\u4ed6\u6276\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u4eca\u665a \u6c90\u6d74\u658b\u6212\uff0c\u660e\u65e5\u4e3e\u884c\u5927\u5178\uff0c\u7531\u4f60\u63a5\u4efb\u638c\u95e8\u3002\u5bf9\u672c\u6d3e\u5404\u652f\u60c5\u5f62\uff0c\u4f60\u6709\u4e0d\u660e\u4e4b\u5904\uff0c\u73b0\u5728\u5c31\u53ef\u95ee \u660e\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u638c\u95e8\u5927\u4efb\uff0c\u5f1f\u5b50\u4e07\u4e07\u4e0d\u6562\u62c5\u627f\u3002\u201d\u9ec4\u53f6\u9053\u4eba\u9053\uff1a\u201c\u8fd9\u662f\u4e3a\u4f55\uff1f\u201d\u5353\u4e00\u822a \u9053\uff1a\u201c\u5f1f\u5b50\u5e74\u8f7b\u8bc6\u6d45\uff0c\u600e\u80fd\u8868\u7387\u540c\u95e8\u3002\u201d\u9ec4\u53f6\u9053\u4eba\u9053\uff1a\u201c\u8981\u5149\u5927\u672c\u95e8\uff0c\u6b63\u8981\u4f60\u8fd9\u6837\u5e74\u8f7b\u529b \u58ee\uff0c\u6709\u624d\u80fd\u6709\u9b44\u529b\u7684\u4eba\u62c5\u4efb\u3002\u96be\u9053\u4f60\u8fd8\u8981\u63a8\u5728\u6211\u4eec\u51e0\u4e2a\u8001\u5934\u8eab\u4e0a\u5417\uff1f\u201d\u5353\u4e00\u822a\u770b\u4e86\u865e\u65b0\u57ce\u4e00 \u773c\uff0c\u865e\u65b0\u57ce\u4e0d\u5f85\u4ed6\u8bf4\u8bdd\uff0c\u5df2\u5148\u7387\u672c\u652f\u7684\u56db\u5927\u5f1f\u5b50\u8fc7\u6765\u53c2\u89c1\uff0c\u5f00\u58f0\u8bf4\u9053\uff1a\u201c\u5353\u8d24\u5f1f\u4f60\u4e0d\u5fc5\u63a8 \u8f9e\uff0c\u524d\u4efb\u638c\u95e8\u7684\u9057\u547d\uff0c\u8c01\u6562\u8fdd\u6297\u3002\u4f55\u51b5\u6709\u56db\u4f4d\u5e08\u53d4\u6276\u4f60\u3002\u201d\u865e\u65b0\u57ce\u4ee5\u4e3a\u5353\u4e00\u822a\u6050\u6015\u540c\u95e8\u4e0d \u670d\uff0c\u6240\u4ee5\u5982\u6b64\u8bf4\u6cd5\u3002\u5176\u76f4\u5353\u4e00\u822a\u5374\u4e0d\u662f\u4e3a\u6b64\u3002\u767d\u77f3\u9053\u4eba\u4e5f\u63d2\u53e3\u9053\uff1a\u201c\u4e00\u822a\uff0c\u4f60\u5e94\u8be5\u60f3\u60f3\u4f60\u5e08 \u7236\u751f\u524d\u5bf9\u4f60\u7684\u671f\u671b\u3002\u201d\u5353\u4e00\u822a\u73af\u5ba4\u56db\u987e\uff0c\u89c1\u540c\u8f88\u7684\u5341\u4e8c\u4e2a\u5e08\u5144\u5f1f\u4e2d\uff0c\u786e\u5b9e\u6ca1\u6709\u4e00\u4e2a\u8db3\u4ee5\u62c5\u627f \u5927\u4efb\u7684\u4eba\uff0c\u77e5\u9053\u53e6\u63d0\u4eba\u9009\uff0c\u4e5f\u5fc5\u7136\u4e0d\u88ab\u63a5\u53d7\u3002\u9ec4\u53f6\u9053\u4eba\u53c8\u8feb\u7d27\u4e00\u53e5\u9053\uff1a\u201c\u4f60\u5e08\u7236\u4e0d\u80fd\u957f\u4e45\u505c \u68fa\uff0c\u4f60\u82e5\u4e0d\u63a5\u638c\u95e8\u4e4b\u547d\uff0c\u4ee4\u4ed6\u4e0d\u80fd\u4eba\u571f\uff0c\u4f60\u65bc\u5fc3\u4f55\u5b89\u3002\u201d\u5353\u4e00\u822a\u54ed\u9053\uff1a\u201c\u5404\u4f4d\u5e08\u53d4\u5e08\u5144\u542c \u7980\uff0c\u5f1f\u5b50\u8eab\u53d7\u672c\u95e8\u91cd\u6069\uff0c\u65e2\u6709\u5148\u5e08\u4e4b\u547d\uff0c\u81ea\u5f53\u9075\u4ece\uff0c\u65e0\u5948\u5f1f\u5b50\u5c1a\u53e6\u6709\u522b\u60c5\uff0c\u5c31\u662f\u8981\u63a5\u638c\u95e8\uff0c \u4e5f\u987b\u5f85\u53c1\u5e74\u4e4b\u5f8c\u3002\u201d\u9ec4\u53f6\u9053\u4eba\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4e3a\u4f55\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5f1f\u5b50\u53d7\u4eba\u9677\u5bb3\uff0c\u73b0\u4e3a\u671d\u5ef7 \u94a6\u72af\uff0c\u82e5\u4e0d\u8fa9\u767d\uff0c\u5982\u4f55\u53ef\u63a5\u638c\u95e8\uff01\u201d\u9ec4\u53f6\u9053\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u5353\u4e00\u822a\u4eba\u5185\uff0c\u7ec6\u95ee\u6839\u7531\u3002 \u3000\u3000\u5353\u4e00\u822a\u56e0\u4e3a\u4e8b\u7247\u91cd\u5927\uff0c\u5728\u65c5\u9014\u4e0a\u540c\u95e8\u4f17\u591a\uff0c\u6050\u6015  \u6f0f\uff0c\u6240\u4ee5\u672a\u66fe\u5411\u9ec4\u53f6\u7968\u544a\uff0c\u73b0\u5728\u8feb\u65bc \u65e0\u5948\uff0c\u53ea\u5f97\u8bf4\u51fa\u3002\u9ec4\u53f6\u9053\u4eba\u542c\u5f97\u6ee1\u6d32\u6536\u4e70\u5978\u4eba\u56fe\u8c0b\u503e\u8986\u671d\u5ef7\u7b49\u4e8b\uff0c\u4e0d\u7981\u9a87\u7136\u3002\u8fc7\u4e86\u8bb8\u4e45\uff0c\u5ffd \u7136\u95ee\u9053\uff1a\u201c\u90a3\u9ebd\u8fd9\u4e8b\u7389\u7f57\u5239\u77e5\u9053\u5417\uff01\u201d \u3000\u3000\u6dd6\u4e00\u822a\u9053\uff1a\u201c\u7389\u7f57\u5239\u5f53\u7136\u77e5\u9053\uff0c\u5728\u534e\u5c71\u4e0a\u548c\u5979\u6076\u6597\u7684\u516d\u9b54\u4e4b\u4e2d\uff0c\u6709\u4e24\u4e2a\u5c31\u662f\u6ee1\u6d32\u5978 \u7ec6\u3002\u201d\u9ec4\u53f6\u9053\u4eba\u9053\uff1a\u201c\u5979\u65e2\u662f\u7eff\u6797\u5de8\u76d7\uff0c\u6709\u4eba\u8981\u503e\u8986\u671d\u5ef7\uff0c\u90a3\u5c82\u4e0d\u662f\u548c\u5979\u5fd7\u540c\u9053\u5408\uff1f\u201d\u5353\u4e00 \u822a\u9053\uff1a\u201c\u5979\u628a\u90a3\u4e9b\u4eba\u6068\u540c\u523a\u9aa8\u3002\u4e0d\u4f46\u662f\u5979\uff0c\u738b\u7167\u5e0c\u4e5f\u662f\u5982\u6b64\u3002\u5728\u7eff\u6797\u8c6a\u6770\u5fc3\u4e2d\uff0c\u5929\u5b50\u53ef\u53d6\u800c \u4ee3\u4e4b\uff0c\u4f46\u5374\u7edd\u4e0d\u80fd\u4ea1\u65bc\u5f02\u65cf\u3002\u201d\u9ec4\u53f6\u9053\u4eba\u6c89\u541f\u826f\u4e45\uff0c\u8bf4\u9053\uff1a\u201c\u672c\u6765\u6211\u4eec\u6b66\u5f53\u4e00\u6d3e\uff0c\u7d20\u4e0d\u4e3b\u5f20 \u8fc7\u95ee\u671d\u653f\u3002\u4f46\u4e8b\u60c5\u65e2\u6709\u5173\u56fd\u8fd0\uff0c\u800c\u4f60\u53c8\u8eab\u53d7\u5947\u51a4\uff0c\u90a3\u9ebd\u5012\u4e0d\u80fd\u4e0d\u7ba1\u4e86\u3002\u4f60\u662f\u60f3\u5f85\u5e08\u7236\u4e0b\u571f\u4e4b \u5f8c\uff0c\u5c31\u8d74\u4eac\u5e08\u9ebd\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6211\u8981\u9762\u89c1\u592a\u5b50\uff0c\u628a\u90a3\u4e9b\u5978\u4eba\u9677\u5bb3\u94a6\u5dee\uff0c\u79fb\u7978\u65bc\u6211\u7684 \u4e8b\u60c5\u8bf4\u51fa\u6765\u3002\u201d\u9ec4\u53f6\u9053\u4eba\u9053\uff1a\u201c\u5176\u4ed6\u540c\u95e8\uff0c\u53ef\u4e0d\u5fc5\u8bf4\u77e5\uff0c\u56db\u4e2a\u5e08\u53d4\uff0c\u4f60\u5374\u8be5\u7980\u544a\u3002\u201d\u5353\u4e00\u822a \u9053\uff1a\u201c\u6211\u4e5f\u6b63\u662f\u5982\u6b64\u60f3\u6cd5\u3002\u6211\u4e0d\u662f\u4e0d\u4fe1\u540c\u95e8\u5144\u5f1f\uff0c\u4f46\u53ea\u6050\u4eba\u591a\u77e5\u6653\uff0c\u4f1a  \u6f0f\u51fa\u53bb\u3002\u201d\u9ec4\u53f6\u9053 \u4eba\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u5f88\u660e\u767d\uff0c\u4f60\u4e0d\u5fc5\u518d\u89e3\u91ca\u4e86\u3002\u201d \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u5429\u5490\u5353\u4e00\u822a\u5728\u9759\u5ba4\u7a0d\u5019\uff0c\u5230\u5916\u9762\u53bb\u5c06\u7ea2\u4e91\u3001\u767d\u77f3\u3001\u9752  \u53c1\u4eba\u5524\u4e86\u8fdb\u6765\uff0c\u5546\u8bae\u597d \u4e45\uff0c\u767d\u77f3\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u9ebd\u638c\u95e8\u4e00\u804c\uff0c\u5c31\u7531\u9ec4\u53f6\u5e08\u5144\u6682\u4ee3\u53c1\u5e74\u3002\u201d\u9ec4\u53f6\u9053\uff1a\u201c\u6211\u5e74\u5c06\u5782 \u66ae\uff0c\u7cbe\u795e\u65e5\u8870\uff0c\u600e\u80fd\u5e94\u4ed8\uff01\u201d\u767d\u77f3\u9053\u4eba\u9053\uff1a\u201c\u53cd\u6b63\u4e0d\u8fc7\u53c1\u5e74\uff0c\u5e08\u5144\u4f60\u4e0d\u63a5\u4efb\u8fd8\u6709\u8c01\u53ef\u63a5 \u4efb\u3002\u201d\u9ec4\u53f6\u9053\u4eba  \u597d\u7b54\u5e94\u3002\u56db\u8001\u548c\u5353\u4e00\u822a\u540c\u51fa\uff0c\u5bf9\u5341\u4e8c\u5f1f\u5b50\u8bf4\u660e\uff0c\u4e00\u4f17\u540c\u95e8\u77e5\u9053\u5353\u4e00\u822a\u53d7\u4eba \u9677\u5bb3\uff0c\u65e0\u4e0d\u5173\u6000\uff0c\u4f46\u4ed6\u4eec\u77e5\u9053\u4e8b\u5173\u79d8\u5bc6\uff0c\u4e5f\u4e0d\u6562\u63a2\u95ee\u3002 \u3000\u3000\u5f53\u4e0b\u5fd9\u4e86\u51e0\u5929\uff0c\u7d2b\u9633\u9053\u957f\u4e0b\u846c\u4e4b\u5f8c\uff0c\u5404\u4fd7\u5bb6\u5f1f\u5b50\u4e5f\u7eb7\u7eb7\u79bb\u5c71\u5f52\u53bb\u3002\u5353\u4e00\u822a\u4ecd\u7559\u5c71\u5b88\u5b5d\uff0c \u4e00\u665a\uff0c\u9ec4\u53f6\u9053\u4eba\u5c06\u4ed6\u5524\u8fdb\u4e91\u623f\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u7236\u4eb2\u5728\u4eac\u65f6\u53ef\u66fe\u66ff\u4f60\u5b9a\u4e0b\u5a5a\u7ea6\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a \u201c\u6ca1\u6709\u3002\u201d\u9ec4\u53f6\u9053\u4eba\u9053\uff1a\u201c\u90a3\u4f60\u53ef\u6709\u610f\u4e2d\u4e4b\u4eba\uff1f\u201d\u5353\u4e00\u822a\u800c\u4e0a\u98de\u7ea2\uff0c\u8fdf\u7591\u534a\u664c\uff0c\u7b54\u9053\uff1a\u201c\u4e5f \u6ca1\u6709\u3002\u201d\u5fc3\u4e2d\u5947\u602a\u4f55\u4ee5\u5e08\u53d4\u4f1a\u5982\u6b64\u95ee\u4ed6\uff1f\u9ec4\u53f6\u9053\u4eba\u9053\uff1a\u201c\u4f60\u5e74\u7eaa\u4e0d\u5c0f\uff0c\u4e5f\u8be5\u5b9a\u4e00\u95e8\u4eb2\u4e8b \u4e86\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5f1f\u5b50\u91cd\u5b5d\u5728\u8eab\uff0c\u90a3\u80fd\u8bae\u5a5a\u3002\u201d\u9ec4\u53f6\u7b11\u9053\uff1a\u201c\u6211\u867d\u975e\u5b98\u5ba6\u4eba\u5bb6\uff0c\u53e4\u793c\u5c1a\u77e5 \u4e00\u4e8c\uff0c\u91cd\u5b5d\u5728\u8eab\uff0c\u5a5a\u59fb\u81ea\u5f53\u5f85\u53c1\u5e74\u670d\u6ee1\u4e4b\u5f8c\uff0c\u4f46\u8bae\u5a5a\u5374\u662f\u4e0d\u59a8\u3002\u201d\u5353\u4e00\u822a\u5fc3\u4e2d\u4e00\u9707\uff0c\u6025\u5fd9\u8bf4 \u9053\uff1a\u201c\u6211\u5b9e\u5728\u65e0\u610f\u53ca\u6b64\u3002\u201d\u9ec4\u53f6\u60f3\u4e86\u4e00\u60f3\uff0c\u7b11\u9053\uff1a\u201c\u4ee5\u4f60\u7684\u4eba\u624d\uff0c\u5f53\u914d\u624d\u8c8c\u53cc\u5168\u7684\u6dd1\u5973\u3002\u90a3 \u7389\u7f57\u5239\u6b66\u529f\u867d\u9ad8\uff0c\u53ef\u662f\u91ce\u6027\u96be\u9664\u7684\u5f3a\u76d7\uff0c\u6211\u529d\u4f60\u4e0d\u5fc5\u7559\u610f\u5979\u4e86\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5f1f\u5b50\u5e76\u65e0\u6b64 \u5fc3\uff0c\u5e08\u53d4\u4e00\u518d\u9053\u53ca\uff0c\u83ab\u975e\u4e0d\u76f8\u4fe1\u5f1f\u5b50\u9ebd\uff1f\u201d\u9ec4\u53f6\u9053\uff1a\u201c\u4f60\u662f\u672c\u95e8\u6700\u6770\u51fa\u4e4b\u4eba\uff0c\u8eab\u81ba\u91cd\u547d\uff0c\u6211 \u6015\u4f60\u8bef\u5165\u6b67\u9014\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5e08\u53d4\u653e\u5fc3\uff0c\u5f1f\u5b50\u8fd8\u77e5\u81ea\u7231\u3002\u201d\u9ec4\u53f6\u9053\uff1a\u201c\u8fd9\u6837\u5c31\u597d\u3002\u4f46\u82e5\u6709 \u5408\u9002\u7684\u6dd1\u5973\uff0c\u6211\u5012\u8981\u529d\u4f60\u5148\u5b9a\u4e0b\u6765\uff0c\u4e5f\u5154\u5fc3\u751f\u5916  \u3002\u201d\u5353\u4e00\u822a\u8d8a\u542c\u8d8a\u60ca\uff0c\u5728\u4ed6\u5fc3\u4e2d\uff0c\u867d\u7136\u4e5f \u786e\u5b9e\u672a\u60f3\u5230\u8981\u4e0e\u7389\u7f57\u5239\u6210\u5a5a\uff0c\u4f46\u4e0d\u5982\u600e\u7684\uff0c\u81ea\u4ece\u89c1\u5979\u4e4b\u5f8c\uff0c\u4fbf\u89c9\u5f97\u5929\u4e0b\u5973\u513f\uff0c\u90fd\u5982\u5c18\u571f\u3002 \u3000\u3000\u7389\u7f57\u5239\u90a3\u5f3a\u70c8\u7684\u4e2a\u6027\uff0c\u867d\u7136\u6709\u65f6\u4e5f\u4ee4\u4ed6\u6050\u60e7\uff0c\u751a\u81f3\u4eca\u4ed6\u618e\u538c\uff0c\u4f46\u5374\u5df2\u6df1\u70d9\u4ed6\u7684\u5fc3\u5934\u3002\u73b0 \u5728\u542c\u5f97\u5e08\u53d4\u53e3\u6c14\uff0c\u597d\u50cf\u8981\u4e3a\u4ed6\u505a\u5a92\uff0c\u5413\u5f97\u8fde\u5fd9\u6447\u624b\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u5b9e\u5728\u4e0d\u60f3\u8fc7\u65e9\u8bba\u5a5a\u3002\u201d\u9ec4\u53f6 \u9053\u4eba\u770b\u4ed6\u795e\u60c5\uff0c\u4e0d\u89c9\u6697\u7b11\uff0c\u4f46\u4e5f\u4e0d\u7981\u6697\u6697\u5fe7\u8651\u3002\u77e5\u4ed6\u6240\u8bf4\u5bf9\u7389\u7f57\u5239\u65e0\u60c5\u4e4b\u8bdd\uff0c\u672a\u5fc5\u662f\u771f\u3002\u5fc3 \u60f3\uff1a\u4ed6\u65e2\u5982\u6b64\uff0c\u4e5f\u4e0d\u597d\u8feb\u4ed6\u3002\u5f85\u4ed6\u89c1\u5230\u53e6\u4e00\u4e2a\u66f4\u597d\u7684\u4eba\u65f6\uff0c\u518d\u8ba9\u4ed6\u4eec\u591a\u5728\u4e00\u5904\uff0c\u4e0d\u6101\u4ed6\u4e0d\u6162 \u6162\u79fb\u60c5\u3002 \u3000\u3000\u5353\u4e00\u822a\u89c1\u5e08\u53d4\u5fae\u5fae\u4e00\u7b11\uff0c\u4e0d\u518d\u7eed\u8bf4\u4e0b\u53bb\uff0c\u677e\u4e86\u53e3\u6c14\uff0c\u7ad9\u8d77\u6765\u9053\uff1a\u201c\u5e08\u53d4\u8fd8\u6709\u522b\u7684\u5429\u5490 \u9ebd\uff0c\u5f1f\u5b50\u60f3\u660e\u65e5\u79bb\u5c71\u4e86\u3002\u201d\u672c\u6765\u4ed6\u60f3\u5b88\u6ee1\u201c\u53c1\u4e03\u201d\u4e4b\u5f8c\u624d\u8d70\uff0c\u4f46\u542c\u4e86\u9ec4\u53f6\u4eca\u665a\u4e4b\u8a00\uff0c\u53ea\u60f3\u65e9 \u65e9\u79bb\u53bb\u3002\u9ec4\u53f6\u53c8\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u4e14\u5750\u4e0b\u3002\u201d \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u63f4\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u672c\u95e8\u5f85\u4efb\u7684\u638c\u95e8\u5f1f\u5b50\uff0c\u6211\u4e0d\u653e\u5fc3\u4f60\u72ec\u81ea\u8d74\u4eac\u3002\u201d\u5353\u4e00\u822a\u60f3\u8d77 \u4e91\u71d5\u5e73\u548c\u91d1\u5343  \u76f8\u8feb\u4e4b\u4e8b\uff0c\u4e5f\u89c9\u5e08\u53d4\u5e76\u975e\u8fc7\u8651\uff0c\u9ec4\u53f6\u7eed\u9053\uff1a\u201c\u56e0\u6b64\u6211\u60f3\u53eb\u4f60\u7684\u56db\u5e08\u53d4\u966a\u4f60\u4e00 \u906d\u3002\u201d\u56db\u5e08\u53d4\u4e43\u662f\u767d\u77f3\u9053\u4eba\u3002\u767d\u77f3\u9053\u4eba\u5728\u6b66\u5f53\u4e94\u8001\u4e2d\u867d\u662f\u6392\u884c\u7b2c\u56db\uff0c\u5e74\u7eaa\u5374\u662f\u6700\u8f7b\uff0c\u4eca\u5e74\u521a \u521a\u4e94\u5341\u51fa\u5934\uff0c\u800c\u4e14\u4ed6\u505a\u9053\u58eb\uff0c\u4e5f\u4e0d\u8fc7\u662f\u6700\u8fd1\u5341\u5e74\u7684\u4e8b\u3002\u5353\u4e00\u822a\u7ea6\u7565\u77e5\u9053\u4ed6\u4fd7\u5bb6\u59d3\u4f55\uff0c\u662f\u59bb\u5b50 \u6b7b\u4e86\u4e4b\u5f8c\u624d\u62ab\u4e0a\u9ec4\u51a0\uff0c\u4e0a\u6b66\u5f53\u5c71\u505a\u9053\u58eb\u7684\u3002 \u3000\u3000\u9ec4\u53f6\u7eed\u9053\uff1a\u201c\u4f60\u56db\u5e08\u53d4\u81ea\u90a3\u5e74\u4e0e\u94c1\u98de\u9f99\u6bd4\u638c\u53d7\u632b\u4e4b\u5f8c\uff0c\u52e4\u4fee\u5185\u529f\uff0c\u73b0\u5728\u5df2\u5927\u975e\u6614\u6bd4\uff0c\u4f60 \u591a\u4e0e\u4ed6\u4eb2\u8fd1\uff0c\u4e5f\u6709\u597d\u5904\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6709\u56db\u5e08\u53d4\u540c\u884c\uff0c\u90a3\u597d\u6781\u4e86\uff0c\u53ea\u662f\u592a  \u70e6\u4ed6\u4e86\u3002\u201d\u9ec4 \u53f6\u7b11\u9053\uff1a\u201c\u600e\u9ebd\u4f60\u4e0e\u5e08\u53d4\u4e5f\u8bb2\u8d77\u5ba2\u5957\u8bdd\u6765\uff1f\u201d\u5f53\u4e0b\u542b\u7b11\u7acb\u8d77\uff0c\u53eb\u4ed6\u65e9\u65e9\u4f11\u606f\u3002 \u3000\u3000\u5728\u56db\u4e2a\u5e08\u53d4\u4e4b\u4e2d\uff0c\u5353\u4e00\u822a\u5e73\u65e5\u4e0e\u767d\u77f3\u9053\u4eba\u8f83\u4e3a\u63a5\u8fd1\uff0c\u5f97\u4ed6\u540c\u884c\uff0c\u9887\u4e3a\u6b22\u559c\u3002\u7b2c\u4e8c\u65e5\u5353\u4e00 \u822a\u62dc\u522b\u4e86\u53c1\u4f4d\u5e08\u53d4\uff0c\u53c8\u5230\u5e08\u7236\u7684\u5893\u796d\u626b\u4e00\u756a\uff0c\u8fd9\u624d\u548c\u767d\u77f3\u9053\u4eba\u4e0b\u5c71\uff0c\u4e00\u8def\u6653\u884c\u591c\u5bbf\uff0c\u8d70\u4e86\u5341 \u591a\u5929\u5f8c\uff0c\u8fdb\u5165\u6cb3\u5357\u4e1c\u90e8\uff0c\u767d\u77f3\u9053\u4eba\u5ffd\u9053\uff1a\u201c\u4e00\u822a\uff0c\u6211\u548c\u4f60\u5230\u5d69\u5c71\u4e00\u6e38\u5982\u4f55\uff01\u201d\u5353\u4e00\u822a\u4e00\u5fc3\u60f3 \u5230\u5317\u4eac\uff0c\u9887\u5947\u5e08\u53d4\u6709\u6b64\u96c5\u5174\uff0c\u56e0\u9053\uff1a\u201c\u5e08\u53d4\u4f55\u4ee5\u8981\u6e38\u5d69\u5c71\uff1f\u201d\u767d\u77f3\u9053\u4eba\u7b11\u9053\uff1a\u201c\u5d69\u5c71\u4e3a\u4e94\u5cb3 \u4e4b\u4e00\uff0c\u5927\u597d\u540d\u5c71\u5c82\u80fd\u9519\u8fc7\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5f85\u4e8b\u5b8c\u4e4b\u5f8c\uff0c\u56de\u6765\u65f6\u518d\u6e38\u4e5f\u8fd8\u672a\u8fdf\uff1f\u201d\u767d\u77f3\u9053\uff1a \u201c\u8fdf\u4e5f\u4e0d\u8fdf\u5728\u8fd9\u51e0\u5929\uff0c\u800c\u4e14\u6211\u4e0d\u5355\u662f\u53bb\u6e38\uff0c\u9001\u60f3\u53bb\u8bbf\u4e00\u4e2a\u4eba\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3 \u5f1f\u5b50\u81ea\u5f53\u5949\u966a\u3002\u201d\u5fc3\u4e2d\u6697\u602a\u5e08\u53d4\u4f55\u4e0d\u65e9\u8bf4\u3002 \u3000\u3000\u5d69\u5c71\u662f\u592a\u5ba4\uff0e\u5c11\u5ba4\u4e24\u5c71\u7684\u603b\u79f0\uff0c\u4e24\u5c71\u5bf9\u5cd9\uff0c\u4e2d\u95f4\u76f8\u8ddd\u7ea6\u5341\u9980\u91cc\u3002\u5728\u5c11\u5ba4\u5317\u9e93\u7684\u4e94\u4e73\u5cf0 \u4e0b\uff0c\u5c31\u662f\u95fb\u540d\u5168\u56fd\u7684\u5c11\u6797\u6d3e\u62f3\u672f\u53d1\u6e90\u5730\u5c11\u6797\u5bfa\u3002\u5353\u4e00\u822a\u95ee\u9053\uff1a\u201c\u5e08\u53d4\u662f\u5230\u5c11\u6797\u5bfa\u53c2\u8c12\u9ebd\uff1f\u201d \u767d\u77f3\u7b11\u9053\uff1a\u201c\u50e7\u9053\u4e0d\u540c\uff0c\u6211\u53bb\u53c2\u8c12\u4f5c\u751a\uff1f\u6211\u548c\u5c11\u6797\u5bfa\u7684\u4e3b\u6301\u4e5f\u6ca1\u6709\u4ec0\u9ebd\u4ea4\u60c5\u3002\u6211\u548c\u4f60\u5148\u6e38\u592a \u5ba4\uff0c\u82e5\u6709\u9980\u6687\uff0c\u518d\u5230\u5c11\u5ba4\u5c71\u53bb\u3002\u201d\u5353\u4e00\u822a\u66f4\u89c9\u5947\u602a\uff0c\u6b66\u6797\u4eba\u58eb\u5230\u5d69\u5c71\u5374\u4e0d\u5148\u6e38\u5c11\u6797\uff0c\u90a3\u9ebd\u4ed6 \u6240\u8bbf\u7684\u5927\u7ea6\u4e0d\u662f\u6b66\u6797\u4e2d\u4eba\u4e86\u3002\u4f46\u5e08\u53d4\u65e2\u8981\u5148\u6e38\u592a\u5ba4\uff0c\u5353\u4e00\u822a\u4e5f  \u597d\u968f\u4ed6\u3002 \u3000\u3000\u4e24\u4eba\u7edd\u65e9\u8d77\u6765\uff0c\u722c\u767b\u5d69\u5c71\uff0c\u4e1c\u65b9\u521d\u767d\uff0c\u671d\u9633\u672a\u51fa\uff0c\u5d69\u5c71\u4e0a\u8ff7\u8499\u8499\u4e00\u7247\u4e91\u6d77\uff0c\u4e0a\u5230\u534a\u5c71\uff0c \u90a3\u8ff7\u6f2b\u7684\u4e91\u6d77\u624d\u60ed\u6e10\u7531\u539a\u800c\u8584\uff0c\u4e00\u8f6e\u65ed\u65e5\u5728\u4e91\u6d77\u4e2d\u6d6e\u73b0\u51fa\u6765\uff0c\u5c71\u4e2d\u666f\u7269\uff0c\u50cf\u5ffd\u7136\u95f4\u88ab\u63ed\u53bb\u4e00 \u5c42\u5e54\u5e10\uff0c\u8c41\u7136\u663e\u9732\u3002\u4f46\u89c1\u5cf0\u5ce6\u96c4\u79c0\uff0c\u6cc9\u77f3\u6e05\u598d\uff0c  \u6d1e\u5e7d\u6df1\uff0c\u4e91\u971e\u660e\u5a9a\uff0c\u9e1f\u8bed\u5541\u557e\uff0c\u82b1\u9999\u6251 \u9f3b\u3002\u5353\u4e00\u822a\u53f9\u9053\uff1a\u201c\u540d\u5c71\u666f\u7269\uff0c\u679c\u7136\u5999\u7edd\u4eba\u5bf0\u3002\u201d\u4e24\u4eba\u5c0f\u61a9\u4e00\u4f1a\uff0c\u7528\u5c71\u6c34\u9001\u54bd\u4e7e\u7cae\uff0c\u56bc\u4e86\u534a \u9971\uff0c\u7ee7\u7eed\u767b\u5c71\u3002\u5d69\u5c71\u4e0a\u53e4\u67cf\u6781\u591a\uff0c\u4e24\u4eba\u5192\u7740\u98d2\u98d2\u5c71\u98ce\uff0c\u5728\u67cf\u6811\u4e1b\u4e2d\u7a7f\u8fdb\u3002\u8d70\u4e86\u4e00\u9635\uff0c\u8d8a\u6500\u8d8a \u9ad8\uff0c\u5ffd\u89c1\u4e00\u682a\u8001\u67cf\uff0c\u82cd\u7fe0\u592d\u77eb\uff0c\u6811\u8eab\u4e24\u4eba\u5408\u56f4\u90fd\u56f4\u4e0d\u8fc7\uff0c\u5353\u4e00\u822a\u6d41\u8fde\u8d5e\u53f9\uff0c\u767d\u77f3\u9053\u4eba\u9053\uff1a \u201c\u51e1\u4e0a\u592a\u5ba4\u7684\u6e38\u5ba2\uff0c\u65e0\u4e0d\u559c\u5728\u8fd9\u682a\u6811\u4e0b\u6d41\u8fde\uff0c\u76f8\u4f20\u6c49\u6b66\u5e1d\u5230\u5d69\u5c71\u201c\u5c01  \u201d\u4e4b\u65f6\uff0c\u66fe\u628a\u5b83\u5c01\u4e3a \u201c\u5927\u5c06\u519b\u201d\uff0c\u6240\u4ee5\u4e00\u822c\u6e38\u5ba2\uff0c\u90fd\u53eb\u5b83\u505a\u201c\u5c06\u519b\u67cf\u201d\u3002\u82e5\u7136\u8fd9\u4e2a\u4f20\u8bf4\u662f\u771f\uff0c\u90a3\u9ebd\u8fd9\u682a\u67cf\u6811\u5927\u7ea6 \u6709\u4e24\u5343\u5c81\u7684\u9ad8\u9f84\u4e86\uff01\u201d\u5353\u4e00\u822a\u4ef0\u89c2\u67cf\u6811\uff0c\u53ea\u89c1\u5b83\u7684\u5927\u90e8\u679d\u5e72\u4ecd\u7136\u679d\u7e41\u53f6\u8302\uff0c\u751f\u610f\u76ce\u7136\uff0c\u4e0d\u7981 \u7b11\u9053\uff1a\u201c\u4eba\u751f\u4e0d\u8fc7\u767e\u5e74\uff0c\u6bd4\u8d77\u8fd9\u682a\u6811\u6765\uff0c\u4e0d\u8fc7\u662f\u5a74\u513f\u7f62\u4e86\uff0c\u4f55\u82e6\u593a\u5229\u4e89\u540d\uff0c\u7eb7\u7eb7\u6270\u6270\u3002\u201d\u6b63 \u8bf4\u8bdd\u95f4\uff0c\u767d\u77f3\u9053\u4eba\u5ffd\u7136\u62c9\u4ed6\u4e00\u4e0b\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u542c\uff0c\u597d\u50cf\u6709\u4eba\u4e0a\u6765\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u85cf\u5728\u53e4\u67cf\u4e4b\u5f8c\uff0c\u53ea\u89c1\u90a3\u8fb9\u5c71\u5f84\uff0c\u8d70\u6765\u4e86\u53c1\u4e2a\u519b\u5b98\uff0c\u5176\u4e2d\u4e00\u4eba\uff0c\u5353\u4e00\u822a\u8ba4\u5f97\u662f\u9526\u8863 \u536b\u7684\u6307\u6325\u77f3\u6d69\uff0c\u5fc3\u60f3\uff1a\u600e\u9ebd\u4ed6\u4e5f\u6709\u6b64\u96c5\u5174\uff0c\u5230\u5d69\u5c71\u6765\u6e38\u3002\u5ffd\u89c9\u767d\u77f3\u9053\u4eba\u62c9\u7740\u81ea\u5df1\u7684\u624b\u5fae\u5fae\u98a4 \u6296\u3002 \u3000\u3000\u5c71\u98ce\u9001\u58f0\uff0c\u6e05\u6670\u53ef\u95fb\u3002\u77f3\u6d69\u9053\uff1a\u201c\u674e\u5927\u4eba\uff0c\u94a6\u5dee\u5df2\u9001\u5230\u629a\u8859\uff0c\u6211\u4eec\u7684\u62c5\u5b50\u53ef\u8f7b\u4e86\u4e0d\u5c11 \u4e86\u3002\u201d\u90a3\u88ab\u4ed6\u5524\u4f5c\u201c\u674e\u5927\u4eba\u201d\u7684\u9053\uff1a\u201c\u592a\u5b50\u5c31\u8981\u767b\u57fa\uff0c\u8c05\u4e91\u71d5\u5e73\u4ed6\u4eec\u4e5f\u4e0d\u6562\u518d\u5bf9\u94a6\u5dee\u52a0 \u5bb3\u3002\u201d\u5353\u4e00\u822a\u542c\u4e86\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4ed6\u4eec\u8bf4\u7684\uff0c\u660e\u660e\u662f\u5468\u674e\u4e24\u94a6\u5dee\u4e4b\u4e8b\uff0c\u542c\u4ed6\u4eec\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u94a6\u5dee \u5df3\u7ed9\u4ed6\u4eec\u5bfb\u7740\uff0c\u5b89\u7136\u8131\u9669\u4e86\u3002\u5176\u4e2d\u4e00\u4eba\u53c8\u9053\uff1a\u201c\u674e\u5927\u4eba\u6545\u5251\u60c5\u6df1\uff0c\u4eca\u665a\u6211\u4eec\u53ef\u8981\u53e8\u6270\u4e00\u676f\u56e2 \u5706\u9152\u4e86\u3002\u201d\u90a3\u4e2a\u201c\u674e\u5927\u4eba\u201d\u5fae\u7b11\u4e0d\u7b54\uff0c\u5353\u4e00\u822a\u773c\u5149\u89e6\u5904\uff0c\u89c9\u767d\u77f3\u9053\u4eba\u9762\u8272\u6709\u5f02\uff0c\u6b63\u60f3\u8bf4\u8bdd\uff0c \u767d\u77f3\u5374\u4ee5\u624b\u793a\u610f\uff0c\u53eb\u4ed6\u4e0d\u8981\u4f5c\u58f0\u3002 \u3000\u3000\u53c1\u4eba\u4e0a\u5230\u5c71\u4e0a\uff0c\u77f3\u6d69\u9053\uff1a\u201c\u8fd9\u682a\u8001\u67cf\u5c45\u7136\u8fd8\u5982\u6b64\u82cd\u7fe0\uff0c\u771f\u662f\u96be\u5f97\u3002\u54b1\u4eec\u5230\u6811\u4e0b\u6b47\u6b47\u3002\u201d \u90a3\u4e2a\u201c\u674e\u5927\u4eba\u201d\u53f9\u9053\uff1a\u201c\u7f8e\u4eba\u81ea\u53e4\u5982\u540d\u5c06\uff0c\u4e0d\u8bb8\u4eba\u95f4\u89c1\u767d\u5934\u3002\u8fd9\u682a\u6811\u53f7\u4e3a\u201c\u5927\u5c06\u519b\u201d\uff0c\u4e8c\u5343 \u5c81\u9ad8\u9f84\u72b9\u672a\u767d\u5934\uff0c\u771f\u4ee4\u6211\u8f88\u94a6  \u3002\u201d\u5353\u4e00\u822a\u5fc3\u60f3\uff1a\u8fd9\u4eba\u809a\u4e2d\u5012\u6709\u70b9\u58a8\u6c34\u3002\u90a3\u53c1\u4eba\u8d8a\u884c\u8d8a\u8fd1\uff0c \u767d\u77f3\u9053\u4eba\u6b63\u60f3\u8dc3\u51fa\uff0c\u5ffd\u7136\u5c71\u98ce\u4e2d\u53c8\u9001\u6765\u4e86\u5973\u5b69\u5b50\u7b11\u8bed\u4e4b\u58f0\uff0c\u90a3\u53c1\u4eba\u4e00\u9f50\u505c\u4f4f\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u5c71\u9876\u8d70\u4e0b\u4e00\u4e2a\u5c11\u5973\uff0c\u5e74\u7ea6\u5341\u4e03\u516b\u5c81\uff0c\u62d6\u7740\u4e00\u4e2a\u5973\u5b69\uff0c\u5973\u5b69\u4e0d\u8fc7\u5341\u5c81\u5149\u666f\uff0c\u7b11 \u7b11\u8df3\u8df3\uff0c\u89c1\u4e86\u751f\u4eba\uff0c\u53eb\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4f60\u770b\u6709\u4eba\u5728\u8fd9\u91cc\u5462\uff0c\u53eb\u4ed6\u4eec\u8ba9\u5f00\uff0c\u6211\u4eec\u8981\u5728\u8fd9\u91cc\u6349\u8ff7 \u85cf\u3002\u201d\u8fd9\u5239\u90a3\u95f4\uff0c\u767d\u77f3\u9053\u4eba\u7684\u624b\uff0c\u53c8\u5fae\u5fae\u98a4\u6296\u3002 \u3000\u3000\u90a3\u4e2a\u88ab\u5524\u4f5c\u201c\u674e\u5927\u4eba\u201d\u7684\u7ea6\u83ab\u56db\u5341\u591a\u5c81\u5e74\u7eaa\uff0c\u76f8\u8c8c\u9887\u4e3a\u5a01\u6b66\uff0c\u8fce\u8fc7\u53bb\u5524\u9053\uff1a\u201c\u5582\uff0c\u5c0f\u59d1 \u5a18\uff0c\u4f60\u53eb\u4ec0\u9ebd\u540d\u5b57\uff1f\u4f60\u7684\u5988\u5988\u5462\uff1f\u201d\u90a3\u4e2a\u5973\u5b69\u9053\uff1a\u201c\u4f60\u7ba1\u4e0d\u4f4f\uff01\u201d\u4f46\u8fd8\u662f\u7b54\u4e86\u4e00\u53e5\u9053\uff1a\u201c\u6211 \u6ca1\u6709\u5988\u5988\uff0c  \u6709\u59d1\u59d1\u3002\u201d\u90a3\u4e2a\u5c11\u5973\u77aa\u4e86\u201c\u674e\u5927\u4eba\u201d\u4e00\u773c\uff0c\u9053\uff1a\u201c\u534e\u59b9\uff0c\u4e0d\u8981\u7406\u4ed6\u4eec\uff0c\u54b1\u4eec\u56de \u53bb\u3002\u201d\u90a3\u4e2a\u5973\u5b69\u95ee\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4ed6\u4eec\u662f\u505a\u5b98\u7684\u9ebd\uff1f\u59d1\u59d1\u8bf4\uff0c\u505a\u5b98\u7684\u90fd\u4e0d\u662f\u597d\u4eba\u3002\uff0e\u597d\uff0c\u6211\u542c \u4f60\u8bdd\uff0c\u4e0d\u7406\u4ed6\u4e86\uff01\u201d \u3000\u3000\u5c11\u5973\u62d6\u7740  \u59b9\uff0c\u626d\u8f6c\u4e86\u8eab\uff0c\u90a3\u4e2a\u201c\u674e\u5927\u4eba\u201d\u6025\u5fd9\u5524\u9053\uff1a\u201c\u5582\uff0c\u6211\u4eec\u4e0d\u662f\u574f\u4eba\uff0c\u4f60\u5e26\u6211\u4eec \u89c1\u4f60\u7684\u59d1\u59d1\u53bb\uff01\u201d\u5c11\u5973\u9053\uff1a\u201c\u6211\u7684\u59d1\u59d1\u4e0d\u89c1\u4f60\u4eec\uff01\u201d\u201c\u674e\u5927\u4eba\u201d\u8eab\u8fb9\u90a3\u4e2a\u519b\u5b98\uff0c\u4f3c\u4e4e\u662f\u4e3a\u4e86 \u8981\u5df4\u7ed3\u4e0a\u53f8\uff0c\u98de\u8eab\u4e00\u63a0\uff0c\u626a\u5728\u90a3\u5c11\u5973\u7684\u9762\u524d\uff0c\u563b\u563b\u7b11\u9053\uff1a\u201c\u771f\u6f02\u4eae\u7684\u5c0f\u59d1\u5a18\uff0c\u4e3a\u4ec0\u9ebd\u4e0d\u7406\u6211 \u4eec\uff1f\u6211\u4eec\u5e26\u4f60\u5230\u57ce\u91cc\u53bb\u73a9\uff0c\u90a3\u624d\u597d\u73a9\u5462\uff01\u201d\u4f38\u624b\u8981\u6478\u5c11\u5973\u7684\u8138\u86cb\uff0c\u201c\u674e\u5927\u4eba\u201d\u53eb\u9053\uff1a\u201c\u8001 \u80e1\uff0c\u522b\u80e1\u95f9\uff01\u201d\u8bdd\u58f0\u672a\u4e86\uff0c\u90a3\u5c11\u5973\u7ea4\u624b\u4e00\u626c\uff0c\u53ea\u542c\u5f97\u201c\u62cd\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u540d\u519b\u5b98\u5df2\u6371\u4e86\u4e00\u8bb0\u8033 \u5149\uff01 \u3000\u3000\u5353\u4e00\u822a\u770b\u5f97\u51e0\u4e4e\u8981\u7b11\u51fa\u58f0\uff0c\u5fc3\u60f3\uff1a\u8fd9\u4e9b\u519b\u5b98\u5e73\u65e5\u4ed7\u52bf\u6b3a\u4eba\uff0c\u8c03\u620f\u5987\u5973\u4e0d\u5f53\u4e00\u56de\u4e8b\u60c5\uff0c\u6371 \u4e86\u8fd9\u5c11\u5973\u8033\u5149\uff0c\u771f\u662f\u6d3b\u8be5\u3002\u770b\u8fd9\u5c11\u5973\u51fa\u624b\u4e0d\u51e1\uff0c\u4e00\u5b9a\u662f\u7ec3\u8fc7\u6b66\u529f\u7684\u4eba\u3002 \u3000\u3000\u90a3\u540d\u519b\u5b98\u53eb\u80e1\u56fd\u67f1\uff0c\u804c\u4f4d\u6bd4\u90a3\u201c\u674e\u5927\u4eba\u201d\u548c\u77f3\u6d69\u8981\u4f4e\u4e00\u7ea7\uff0c\u4f46\u8fd9\u53c1\u4eba\u540c\u5728\u9526\u8863\u536b\u4e2d\u4f9b \u804c\uff0c\u5e73\u65f6\u996e\u82b1\u9152\u3001\u73a9\u5973\u4eba\u5e38\u5728\u4e00\u5904\u3002\u5148\u524d\u542c\u5f97\u4e0a\u53f8\u559d\u4ed6\u201c\u522b\u80e1\u95f9\u201d\uff0c\u5fc3\u91cc\u5df2\u81ea\u4e0d\u6ee1\uff0c\u6697\u9053\uff1a \u54fc\uff0c\u4f60\u88c5\u4ec0\u9ebd\u6b63\u7ecf\uff01\u6371\u4e86\u4e00\u638c\uff0c\u5341\u5206\u75bc\u75db\uff0c\u8fd9\u4e2a\u6c14\u53ef\u5c31\u5927\u4e86\uff0c\u8eab\u5b50\u4e00\u6251\uff0c\u53cc\u624b\u6293\u53bb\uff0c\u90a3\u5c11\u5973 \u628a\u59b9\u59b9\u63a8\u5f00\uff0c\u4e00\u62db\u201c\u5982\u5c01\u4f3c\u95ed\u201d\uff0c\u53ea\u638c\u4e00\u9634\u4e00\u9633\uff0c\u8f7b\u8f7b\u4e00\u683c\uff0c\u628a\u80e1\u56fd\u67f1\u7684\u6765\u52bf\u6d88\u6389\uff0c\u53cc\u638c\u5411 \u524d\u4e00\u6309\uff0c\u80e1\u56fd\u67f1\u4e0d\u7531\u5f97\u4e0d\u9000\u5f8c\u53c1\u6b65\u3002\u5c11\u5973\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4f60\u662f\u4e0d\u662f\u60f3\u6253\u67b6\uff01\u201d \u3000\u3000\u80e1\u56fd\u67f1\u8eab\u4e3a\u9526\u8863\u536b\u7684\u526f\u6307\u6325\uff0c\u53c8\u662f\u6606\u4ed1\u6d3e\u7684\u597d\u624b\uff0c\u5728\u6b66\u6797\u4e2d\u4e5f\u6709\u70b9\u540d\u58f0\uff0c\u7adf\u7136\u731d\u4e0d\u53ca \u9632\uff0c\u88ab\u5c11\u5973\u51fa\u62db\u8feb\u9000\uff0c\u5728\u540c\u50da\u9762\u524d\uff0c\u9762\u5b50\u66f4\u6302\u4e0d\u4e0b\u53bb\uff0c\u5f53\u4e0b\u559d\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u8981\u548c\u6211\u6253\u67b6\uff1f\u201d \u5c11\u5973\u9053\uff1a\u201c\u4e0d\u662f\u6211\u8981\u548c\u4f60\u6253\u67b6\uff0c\u662f\u4f60\u8981\u548c\u6211\u6253\u67b6\uff01\u201d\u80e1\u56fd\u67f1\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u7ba1\u8c01\u8981\u6253\u67b6\uff0c\u8fd9\u573a \u67b6\u662f\u6253\u5b9a\u4e86\uff01\u201d \u3000\u3000\u90a3\u201c\u674e\u5927\u4eba\u201d\u672c\u60f3\u559d\u4f4f\uff0c\u8f6c\u5ff5\u4e00\u60f3\uff1a\u4e14\u770b\u770b\u8fd9\u5c11\u5973\u6b66\u529f\u5982\u4f55\uff1f\u770b\u5979\u662f\u5426\u90a3\u4eba\u6240\u6559\uff1f\u5f53\u4e0b \u53eb\u9053\uff1a\u201c\u5582\uff0c\u8981\u6253\u67b6\u5230\u8fd9\u91cc\u6765\u6253\uff0c\u8fd9\u91cc\u5730\u65b9\u5bbd\u9614\uff0c\u5728\u5c71\u5f84\u4e0a\u6253\u4ec0\u9ebd\u5440\uff1f\u201d\u5c11\u5973\u79c0\u7709\u4e00\u6311\uff0c\u8bf4 \u9053\uff1a\u201c\u4f60\u4eec\u53c1\u4e2a\u4eba\u4e0a\u6765\u6211\u4e5f\u4e0d\u6015\u3002\u201d\u628a\u59b9\u59b9\u5b89\u987f\u5728\u5c71\u77f3\u4e0a\u5750\u4e0b\uff0c\u5429\u5490\u5979\u9053\uff1a\u201c\u4f60\u770b\u6253\u67b6\uff0c\u53ef \u522b\u4e71\u8dd1\uff01\u201d\u90a3\u5973\u5b69\u62cd\u638c\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u770b\u6253\u67b6\uff0c\u770b\u6253\u67b6\uff01\u59d0\u59d0\uff0c\u4f60\u53ef\u4e00\u5b9a\u8981\u6253\u8d62\u5440\uff01\u201d\u5c11\u5973 \u8eab\u5f62\u98de\u8d77\uff0c\u8dc3\u5230\u53e4\u67cf\u524d\u7684\u7a7a\u5730\u4e0a\uff0c\u56de\u5934\u62db\u624b\u9053\uff1a\u201c\u5582\uff0c\u6765\u5440\uff01\u201d\u80e1\u56fd\u67f1\u6c14\u7ea2\u4e86\u9762\uff0c\u8ddf\u8e2a\u8dc3 \u81f3\uff0c\u5728\u8f7b\u529f\u4e0a\u4ed6\u5df2\u5148\u8f93\u4e86\u4e00\u62db\u4e86\uff01 \u3000\u3000\u5c11\u5973\u6c14\u5b9a\u795e\u95f2\uff0c\u51dd\u8eab\u5f85\u654c\u3002\u77f3\u6d69\u9053\uff1a\u201c\u8001\u80e1\uff0c\u4e0d\u8981\u6258\u5927\uff0c\u8fd9\u4e2a\u59d1\u5a18\u662f\u4e2a\u4f1a\u5bb6\uff01\u201d\u80e1\u56fd\u67f1 \u811a\u5c16\u4e00\u70b9\uff0c\u98de\u8eab\u7a9c\u8d77\uff0c\u53f3\u62f3\u5288\u9762\u6363\u51fa\uff0c\u559d\u58f0\uff1a\u201c\u63a5\u62db\uff01\u201d\u5c11\u5973\u4e00\u58f0\u51b7\u7b11\uff0c\u8eab\u5f62\u5fae\u6643\uff0c\u53cd\u624b\u4e00 \u638c\uff0c\u95ea\u7535\u822c\u7684\u622a\u51fb\u654c\u4eba\u53f3\u81c2\u3002\u80e1\u56fd\u67f1\u559d\u58f0\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u5de6\u638c\u5f80\u4e0a\u4e00\u642d\uff0c\u53f3\u624b\u5f80\u4e0a\u4e00\u4f38\uff0c\u5237 \u7684\u5411\u5c11\u5973\u9762\u95e8\u6293\u53bb\uff0c\u8fd9\u4e00\u62db\u540d\u53eb\u201c\u91d1\u9f99\u63a2\u722a\u201d\uff0c\u662f\u6606\u4ed1\u6d3e\u201c\u9f99\u5f62\u5341\u516b\u5f0f\u201d\u4e2d\u7684\u5389\u5bb3\u62db\u6570\u3002 \u3000\u3000\u90a3\u77e5\u4e00\u6293\u6293\u53bb\uff0c\u7adf\u81ea\u6251\u7a7a\u3002\u5c11\u5973\u8eab\u8eaf\u75be\u7684\u62e7\u8c79\uff0c\u53f3\u638c\u500f\u7136\u5288\u51fa\uff0c\u53cd\u5288\u654c\u4eba\u5de6\u808b\uff0c\u80e1\u56fd\u67f1 \u4e00\u4e2a\u5f2f\u8170\u8f6c\u6b65\uff0c\u597d\u5bb9\u6613\u624d\u907f\u5f00\u8fd9\u62db\uff0c\u5c11\u5973\u5de6\u638c\u53c8\u53d1\uff0c\u53d8\u4e86\u201c\u5370\u638c\u201d\uff0c\u201c\u5370\u201d\u5411\u654c\u80f8\uff0c\u80e1\u56fd\u67f1 \u5927\u5403\u4e00\u60ca\uff0c\u731b\u7684\u957f\u8eab\uff0c\u201c\u62cd\u201d\u7684\u4e00\u58f0\uff0c\u80a9\u5934\u4e2d\u4e86\u4e00\u638c\uff0c\u88ab\u6253\u5f97\u5012\u9000\u6570\u6b65\uff0c\u6697\u53eb\u201c\u597d\u9669\uff01\u201d\u82e5 \u4e0d\u662f\u7528\u80a9\u5934\u786c\u63a5\uff0c\u80f8\u819b\u8981\u7a74\uff0c\u88ab\u5979\u5370\u638c\u6240\u51fb\uff0c\u53ea\u6050\u6709\u6027\u547d\u4e4b\u5fe7\u3002 \u3000\u3000\u80e1\u56fd\u67f1\u9886\u4e86\u4e24\u62db\uff0c\u90a3\u6562\u8f7b\u654c\uff0c\u62a1\u62f3\u590d\u4e0a\uff0c\u547c\u547c\u751f\u98ce\uff0c\u4ece\u201c\u9f99\u5f62\u5341\u516b\u5f0f\u201d\u7684\u638c\u6cd5\u6539\u6210\u4e86 \u201c\u9ed1\u864e\u62f3\u201d\uff0c\u8fd9\u5957\u62f3\u5b9c\u653b\uff0c\u5a01\u529b\u751a\u731b\uff0c\u5c11\u5973\u8f7b\u529f\u867d\u597d\uff0c\u6c14\u529b\u5374\u5dee\uff0c\u4e00\u65f6\u95f4\u5012\u6253\u6210\u4e86\u5e73\u624b\u3002 \u3000\u3000\u6253\u4e86\u4e00\u9635\uff0c\u5c11\u5973\u62f3\u6cd5\u5ffd\u53d8\uff0c\u5728\u80e1\u56fd\u67f1\u5468\u56f4\u7ed5\u6765\u7ed5\u53bb\uff0c\u4e13\u62e3\u4ed6\u7684\u7a7a\u95e8\u8fdb\u88ad\uff0c\u80e1\u56fd\u67f1\u8eab\u6cd5\u8fdc \u4e0d\u5982\u5c11\u5973\u8f7b\u7075\uff0c\u653b\u5979\u4e0d\u7740\uff0c\u5b88\u4e5f\u4e0d\u79fb\u4e25\u5bc6\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u53c8\u63a5\u8fde\u6371\u4e86\u4e24\u638c\uff0c\u5e78\u559c\u51fb\u4e2d\u7684\u4e0d\u662f\u8981 \u5bb3\uff0c\u8fd8\u53ef\u652f\u6301\uff0c\u4f46\u4e5f\u5df2\u7d2f\u5230\u6ee1\u5934\u5927\u6c57\u3002 \u3000\u3000\u90a3\u4e2a\u201c\u674e\u5927\u4eba\u201d\u770b\u5f97\u8fde\u8fde\u6447\u5934\uff0c\u53eb\u9053\uff1a\u201c\u8001\u77f3\uff0c\u4f60\u53bb\u628a\u8001\u80e1\u62c9\u4e0b\u6765\uff0c\u4e0d\u8981\u4f24\u90a3\u5973\u5b50\u3002\u201d \u77f3\u6d69\u4e00\u4e2a\u7bad\u6b65\u51b2\u4e0a\uff0c\u63d2\u5728\u4e24\u4eba\u4e2d\u95f4\uff0c\u53f3\u638c\u4e00\u63a8\uff0c\u5de6\u638c\u4e00\u5e26\uff0c\u8fd9\u4e00\u62db\u5c31\u79f0\u4e3a\u201c\u5e26\u9a6c\u5f52\u69fd\u201d\uff0c\u80e1 \u56fd\u67f1\u7ed9\u4ed6\u5de6\u638c\u5e26\u5230\u65c1\u8fb9\uff0c\u90a3\u5c11\u5973\u4e5f\u7ed9\u4ed6\u63a8\u5f00\u51e0\u6b65\u3002\u672c\u6765\u8bba\u638c\u6cd5\u77f3\u6d69\u672a\u5fc5\u80dc\u5f97\u8fc7\u90a3\u4f4d\u59d1\u5a18\uff0c\u53ef \u662f\u4ed6\u5185\u529b\u751a\u5f3a\uff0c\u638c\u542b\u9634\u52b2\uff0c\u5f53\u5e74\u4ed6\u7f09\u6355\u738b\u7167\u5e0c\u4e4b\u65f6\uff0c\u5c31\u66fe\u663e\u8fc7\u201c\u811a\u788e\u9636\u77f3\u201d\u7684\u6b66\u529f\uff0c\u738b\u7167\u5e0c \u4e5f\u8981\u907f\u4ed6\u3002\u8fd9\u5c11\u5973\u6b66\u529f\u5728\u738b\u7167\u5e0c\u4e4b\u4e0b\uff0c\u5f53\u7136\u63a5\u4e0d\u4f4f\u4ed6\u7684\u638c\u529b\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u5c11\u5973\u4f3c\u4e4e\u4e5f\u9887\u597d\u80dc\uff0c\u8eab\u5f62\u4e00\u9000\u590d\u4e0a\uff0c\u53eb\u9053\uff1a\u201c\u597d\u54c7\uff0c\u4f60\u4eec\u90fd\u4e0a\u6765\u5427\uff01\u201d\u90a3\u4e2a\u201c\u674e \u5927\u4eba\u201d\u53eb\u9053\uff1a\u201c\u5c0f\u59d1\u5a18\uff0c\u4e0d\u5fc5\u6253\u4e86\uff01\u54b1\u4eec\u90fd\u662f\u4e00\u5bb6\u4eba\uff0c\u4f60\u7684\u5e08\u7236\u662f\u4e0d\u662f\u59d3\u4f55\u7684\uff01\u201d\u5c11\u5973\u6115\u7136 \u6ce8\u89c6\uff0c\u4e45\u4e45\u90fd\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u201c\u674e\u5927\u4eba\u201d\u53c8\u5fae\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u53ef\u4ee5\u5e26\u6211\u53bb\u89c1\u4f60\u7684\u59d1\u59d1\u4e86\u5427\uff1f\u201d\u8bdd\u58f0\u4e00\u505c\uff0c\u5ffd\u7136\u4ece\u4e0a\u9762 \u5c71\u5773\u5904\u5954\u4e0b\u4e00\u4eba\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u6765\u89c1\u6211\u505a\u4ec0\u9ebd\uff1f\u201d\u8fd9\u4eba\u662f\u4e2a\u4e2d\u5e74\u5c3c\u59d1\uff0c\u7ea6\u83ab\u56db\u5341\u5c81\u5149 \u666f\u3002\u201c\u674e\u5927\u4eba\u201d\u4e00\u89c1\uff0c\u8dd1\u4e0a\u524d\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u600e\u9ebd\u524a\u53d1\u505a\u4e86\u5c3c\u59d1\u4e86\uff1f\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u5c3c\u59d1\u4e0d\u7406\u4e0d\u776c\uff0c\u5de6\u624b\u643a\u90a3\u5c11\u5973\uff0c\u53f3\u624b\u643a\u90a3\u5973\u5b69\uff0c\u9053\uff1a\u201c\u8fd9\u4e16\u754c\u574f\u4eba\u592a\u591a\uff0c\u54b1\u4eec\u56de \u53bb\u3002\u201d\u201c\u674e\u5927\u4eba\u201d\u53c8\u5954\u524d\u51e0\u6b65\uff0c\u56b7\u9053\uff1a\u201c\u5582\uff0c\u4f60\u542c\u6211\u4e00\u53e5\u8bdd\u6210\u4e0d\u6210\uff1f\u201d \u3000\u3000\u90a3\u5c3c\u59d1\u6b32\u884c\u53c8\u6b62\uff0c\u56de\u8fc7\u5934\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\u3002\u201d\u201c\u674e\u4eba\u4eba\u201d\u7b11\u563b\u563b\u7684\u9053\uff1a\u201c\u8bf4\u591a\u4e24\u53e5\u6210\u4e0d \u6210\uff01\u201d\u90a3\u5c3c\u59d1\u9762\u8272\u4e00\u6c89\uff0c\u201c\u674e\u5927\u4eba\u201d\u9053\uff1a\u201c\u971e\u59b9\uff0c\u5f53\u5e74\u662f\u6211\u9519\u4e86\uff0c\u73b0\u5728\u6211\u7279\u6765\u63a5\u4f60\u56de\u53bb\uff01\u201d \u90a3\u5c3c\u59d1\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u6709\u4ec0\u9ebd\u76f8\u5e72\u4e86\u4f60\u505a\u4f60\u7684\u5b98\uff0c\u6211\u505a\u6211\u7684\u5c3c\u59d1\uff0c\u4f60\u522b\u6765\u8fd9\u91cc \u80e1\u7f20\u3002\u201d\u201c\u674e\u5927\u4eba\u201d\u9053\uff1a\u201c\u592a\u5b50\u5c31\u8981\u767b\u57fa\u4e86\u3002\u201d\u5c3c\u59d1\u9053\uff1a\u201c\u8fd9\u66f4\u4e0e\u6211\u65e0\u5173\uff01\u201d\u201c\u674e\u5927\u4eba\u201d \u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u662f\u592a\u5b50\u7684\u4eb2\u4fe1\uff0c\u592a\u5b50\u767b\u57fa\uff0c\u6211\u6c42\u4ed6\u5916\u653e\uff0c\u8d77\u7801\u5c31\u662f\u4e00\u4e2a\u603b\u5175\uff0c\u4e5f\u8bb8\u662f\u5c06\u519b\u4e5f \u8bf4\u4e0d\u5b9a\uff0c\u90a3\u65f6\u4f60\u5c31\u662f\u8bf0\u547d\u592b\u4eba\u3002\u201d\u90a3\u5c3c\u59d1\u6c14\u5f97\u9762\u8272\u7ea2\u91cc\u6cdb\u9752\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u81ea\u6709\u4f60\u7684\u8bf0\u547d\u592b \u4eba\uff0c\u4f60\u518d\u80e1\u7f20\uff0c\u4f11\u602a\u6211\u4e0d\u5ba2\u6c14\uff01\u201d\u90a3\u201c\u674e\u5927\u4eba\u201d\u7b11\u4e86\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u96be\u602a\u4f60\u53d1\u813e\u6c14\uff0c\u4f60\u8fd8\u4e0d \u77e5\u9053\u54e9\uff01\u80e1\u6c0f\u5df2\u7ecf\u6b7b\u4e86\uff0c\u5979\u53c8\u6ca1\u7559\u4e0b\u513f\u5973\uff0c\u6211\u8fd9\u4e2a\u5bb6\u8fd8\u662f\u4f60\u7684\uff01\u201d\u90a3\u5c3c\u59d1\u51b7\u7b11\u4e00\u58f0\uff0c\u677f\u8d77\u8138 \u5b54\u65a5\u9053\uff1a\u201c\u6eda\u5f00\uff0c\u5341\u56db\u5e74\u524d\u4f60\u8d2a\u56fe\u5bcc\u8d35\u628a\u6211\u4f11\u6389\u2026\u2026\u201d\u90a3\u201c\u674e\u5927\u4eba\u201d\u6025\u63d2\u53e3\u8bf4\u9053\uff1a\u201c\u90a3\u662f\u6211 \u6bcd\u4eb2\u7684\u4e3b\u610f\uff0c\u4e0e\u6211\u65e0\u5173\uff01\u201d\u90a3\u5c3c\u59d1\u7eed\u9053\uff1a\u201c\u6211\u53ef\u6ca1\u90a3\u9ebd\u4e0b\u8d31\uff0c\u4f11\u4e86\u7684\u59bb\u5df2\u6cfc\u51fa\u53bb\u7684\u6c34\uff0c\u4f60\u628a \u6cfc\u51fa\u53bb\u7684\u6c34\u6536\u56de\u7ed9\u6211\u770b\u770b\uff01\u201d\u90a3\u201c\u674e\u5927\u4eba\u201d\u53c8\u9053\uff1a\u201c\u4f60\u7eb5\u4e0d\u5ff5\u592b\u59bb\u4e4b\u60c5\uff0c\u4e5f\u5f53\u770b\u5728\u7533\u513f\u9762 \u4e0a\u3002\u201d\u90a3\u5c3c\u59d1\u8eab\u8eaf\u98a4\u6296\uff0c\u672c\u5df2\u8f6c\u8eab\uff0c\u53c8\u56de\u8fc7\u6765\u95ee\u9053\uff1a\u201c\u7533\u513f\u600e\u6837\uff1f\u201d\u201c\u674e\u5927\u4eba\u201d\u9053\uff1a\u201c\u4ed6\u7b49 \u7740\u5988\u5988\u56de\u5bb6\u54e9\uff01\u201d\u90a3\u5c3c\u59d1\u7a81\u53d1\u51b7\u7b11\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u5f53\u6211\u4ec0\u9ebd\u4e5f\u4e0d\u77e5\u9053\u9ebd\uff1f\u7533\u513f\u4e0d\u582a\u5f8c\u6bcd\u8650\u5f85\uff0c \u65e9\u5c31\u8dd1\u5566\u201c\u4f60\u8981\u4e0d\u8981\u6211\u544a\u8bc9\u4f60\u4ed6\u5728\u90a3\u91cc\uff1f\u201d\u90a3\u201c\u674e\u5927\u4eba\u201d\u9762\u8272\u7070\u8d25\uff0c\u5ffd\u7136\u8dc3\u8d77\u6765\u9053\uff1a\u201c\u597d \u5440\uff0c\u679c\u7136\u662f\u4f60\u628a\u4ed6\u6536\u8d77\u6765\uff01\u201d\u90a3\u5c3c\u59d1\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u770b\uff0c\u6211\u4e00\u8bd5\u4fbf\u8bd5\u51fa\u6765\u4e86\uff0c\u4f60\u662f\u6765\u8981\u4f60\u7684\u513f \u5b50\uff0c\u4ec0\u9ebd\u8bf0\u547d\u592b\u4eba\uff0c\u5478\uff01\u8198\u6eda\uff01\u201d\u90a3\u201c\u674e\u5927\u4eba\u201d\u98de\u6b65\u51b2\u524d\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6211\u8981\u4f60\u4eec\u6bcd\u5b50\u4e24\u4eba \u90fd\u56de\u6765\uff01\u201d\u90a3\u5c3c\u59d1\u51b7\u51b0\u51b0\u7684\u5b9b\u5982\u77f3\u4eba\uff0c\u5f85\u5f97\u90a3\u201c\u674e\u5927\u4eba\u201d\u51b2\u5230\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u7533\u513f\u4e0d\u5728\u8fd9 \u513f\uff01\u201d\u201c\u674e\u5927\u4eba\u201d\u8bf4\u9053\uff1a\u201c\u90a3\u9ebd\u4ed6\u5728\u90a3\u91cc\uff1f\u201d\u90a3\u5c3c\u59d1\u677f\u8138\u4e0d\u7406\u3002\u201c\u674e\u5927\u4eba\u201d\u56b7\u9053\uff1a\u201c\u90a3\u4f60\u968f \u6211\u56de\u53bb\uff01\u201d\u90a3\u5c3c\u59d1\u4ecd\u662f\u677f\u8138\u4e0d\u7406\uff0c\u201c\u674e\u5927\u4eba\u201d\u5ffd\u9053\uff1a\u201c\u597d\uff0c\u6211\u77e5\u4f60\u662f\u604b\u7740\u90a3\u59d3\u9f99\u7684\u5c0f\u5b50\uff0c\u53ef \u662f\u4eba\u5bb6\u4e5f\u4e0d\u8981\u4f60\uff01\u201d\u90a3\u5c3c\u59d1\u6012\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff01\u201d\u75be\u7684\u4e00\u638c\u6253\u53bb\uff0c\u201c\u62cd\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u201c\u674e\u5927 \u4eba\u201d\u4e5f\u50cf\u80e1\u56fd\u67f1\u4e00\u6837\uff0c\u6371\u4e86\u4e00\u8bb0\u8033\u5149\uff01 \u3000\u3000\u201c\u674e\u5927\u4eba\u201d\u6367\u8d77\u9762\u5b54\u53eb\u9053\uff1a\u201c\u597d\u6cfc\u7684\u5a46\u5a18\uff01\u201d\u4e00\u6293\u6293\u53bb\uff0c\u5c3c\u59d1\u8eab\u5f62\u4e00\u8f6c\uff0c\u4e00\u62db\u201c\u4e03\u661f \u624b\u201d\u8fde\u73af\u63a8\u51fa\uff0c\u90a3\u201c\u674e\u5927\u4eba\u201d\u5438\u80f8\u51f9\u8179\uff0c\u500f\u5730\u63c9\u8eab\u8fdb\u638c\uff0c\u9053\uff1a\u201c\u6211\u5df2\u8ba9\u4e86\u4f60\uff0c\u4f60\u8fd8\u4e0d\u77e5\u8fdb \u9000\uff01\u201d\u547c\u547c\u4e24\u62f3\uff0c\u5de6\u638c\u6a2a\u5288\uff0c\u53f3\u638c\u76f4\u626b\uff0c\u7aef\u7684\u662f\u5185\u5bb6\u9ad8\u624b\uff0c\u90a3\u4f4d\u5c3c\u59d1\u4e5f\u559d\u9053\uff1a\u201c\u4f60\u6eda\u4e0d \u6eda\uff01\u201d\u5728\u638c\u98ce\u4e2d\u7a81\u7136\u8fdb\u62db\uff0c\u4e00\u624b\u5201\u7740\u4ed6\u7684\u624b\u8155\uff0c\u5f80\u5916\u4fbf\u7529\uff0c\u90a3\u201c\u674e\u5927\u4eba\u201d\u6b66\u529f\u786e\u9ad8\uff0c\u624b\u8155\u4e00 \u6c89\uff0c\u5c45\u7136\u6323\u8131\uff0c\u53eb\u9053\uff1a\u201c\u5582\uff0c\u592b\u59bb\u6253\u67b6\uff0c\u4e0d\u53eb\u65c1\u4eba\u7b11\u8bdd\uff01\u201d\u90a3\u5c3c\u59d1\u6c14\u6781\u6012\u6781\uff0c\u8fde\u73af\u53d1\u638c\uff0c\u51cc \u5389\u4e4b\u6781\uff0c\u201c\u674e\u5927\u4eba\u201d\u7ed9\u8feb\u5f97\u8fde\u8fde\u5f8c\u9000\u3002\u77f3\u6d69\u7ad9\u5728\u65c1\u8fb9\uff0c\u4e0d\u6562\u5e2e\u624b\uff0c\u90a3\u201c\u674e\u5927\u4eba\u201d\u76f4\u9000\u5230\u4e86\u8001 \u67cf\u6811\u524d\uff01 \u3000\u3000\u90a3\u5c3c\u59d1\u4e00\u638c\u51fb\u53bb\uff0c\u201c\u674e\u5927\u4eba\u201d\u9000\u5230\u6811\u5f8c\uff0c\u767d\u77f3\u9053\u4eba\u5ffd\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u5de6\u624b\u671d\u4ed6\u80a9\u5934\u4e00\u6309\uff0c \u5c06\u4ed6\u63a8\u5f00\uff0c\u90a3\u5c3c\u59d1\u4e00\u89c1\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u51e0\u65f6\u6765\u7684\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u5c3c\u59d1\u4e43\u662f\u767d\u77f3\u9053\u4eba\u7684  \u59b9\uff0c\u540d\u53eb\u4f55\u7eee\u971e\uff0c\u4e8c\u5341\u9980\u5e74\u524d\uff0c\u6709\u4e24\u5bb6\u5411\u5979\u6c42\u5a5a\uff0c\u8fd9\u4e24\u5bb6 \u5728\u6b66\u6797\u4e2d\u90fd\u9887\u6709\u540d\u671b\uff0c\u4e00\u4e2a\u662f\u5ce8\u5d4b\u6d3e\u7684\u9f99\u5578\u4e91\uff0c\u4e00\u4e2a\u4fbf\u662f\u73b0\u5728\u8fd9\u4e2a\u201c\u674e\u5927\u4eba\u201d\uff0c\u540d\u53eb\u674e\u5929\u626c \u7684\u3002\u9f99\u5578\u4e91\u3001\u674e\u5929\u626c\u548c\u4f55\u5bb6\u90fd\u662f\u4e16\u4ea4\uff0c\u4f55\u7eee\u971e\u7236\u5144\u51b3\u65ad\u4e0d\u4e0b\uff0c\u5c31\u7531\u5979\u81ea\u9009\u3002\u90a3\u65f6\u4f55\u7eee\u971e\u8fd8\u53ea \u662f\u5341\u516d\u4e03\u5c81\u5c0f\u59d1\u5a18\uff0c\u89c1\u674e\u5929\u626c\u751f\u5f97\u8f83\u4e3a\u82f1\u4fca\uff0c\u4fbf\u9009\u4e0a\u4ed6\u4e86\u3002 \u3000\u3000\u90a3\u77e5\u674e\u5929\u626c\u540d\u5229\u4e4b\u5fc3\u751a\u91cd\uff0c\u7ed3\u5a5a\u4e4b\u5f8c\uff0c\u6e38\u5b66\u4eac\u5e08\uff0c\u4ed6\u6b66\u529f\u65e2\u9ad8\uff0c\u53c8\u901a\u6587\u58a8\uff0c\u7ed9\u4e00\u4e2a\u4e16\u88ad \u7684\u201c\u8f66\u9a91\u5c06\u519b\u201d\u770b\u4e0a\uff0c\u8981\u628a\u5973\u513f\u914d\u4ed6\u3002\u674e\u5929\u626c\u8fd8\u7b97\u7a0d\u5fae\u6709\u70b9\u826f\u5fc3\uff0c\u4e0d\u6562\u7acb\u5373\u5728\u4eac\u522b\u5a36\uff0c\u51c6\u8bf4 \u8981\u56de\u5bb6\u7980\u544a\u7236\u6bcd\uff0c\u56de\u5bb6\u4e4b\u5f8c\uff0c\u5c31\u6697\u4e2d\u53eb\u6bcd\u4eb2\u51fa\u5934\uff0c\u628a\u59bb\u5b50\u4f11\u4e86\u3002\u4ed6\u4eec\u5df2\u6709\u4e86\u4e00\u4e2a\u53c1\u5c81\u5927\u7684\u5b69 \u5b50\uff0c\u767d\u77f3\u9053\u4eba\u90a3\u65f6\u8fd8\u672a\u51fa\u5bb6\uff0c\u4e5f\u66fe\u53bb\u674e\u5bb6\u529d\u89e3\uff0c\u8bf4\u662f\uff1a\u592b\u59bb\u5df2\u505a\u4e86\u51e0\u5e74\uff0c\u53c8\u6709\u4e86\u5b69\u5b50\uff0c\u4f55\u5fc5 \u79bb\u5f02\uff1f\u53ef\u662f\u674e\u5bb6\u6267\u610f\u4e0d\u7406\uff0c\u767d\u77f3\u751a\u4e3a\u6c14\u6124\uff0c\u4ece\u6b64\u5c31\u548c\u674e\u5bb6\u65ad\u4e86\u8fd9\u95e8\u4eb2\u621a\u3002 \u3000\u3000\u5982\u662f\u8005\u8fc7\u4e86\u5341\u56db\u5e74\uff0c\u674e\u5929\u626c\u5728\u9526\u8863\u536b\u4e2d\u505a\u5230\u4e86\u6307\u6325\u4e4b\u804c\uff0c\u9f99\u5578\u4e91\u4e0d\u77e5\u4e0b\u843d\uff0c\u4f55\u7eee\u971e\u5219\u5728 \u88ab\u4f11\u4e4b\u5f8c\uff0c\u5c31\u5230\u592a\u5ba4\u5c71\u8ddf\u5979\u7684\u5e08\u5085\uff0c\u5e08\u5085\u4e03\u5e74\u524d\u6b7b\u4e86\uff0c\u5979\u8fd9\u65f6\u5df2\u60ef\u5c71\u5c45\u751f\u6d3b\uff0c\u4e5f\u4fbf\u505a\u4e86\u5c3c \u59d1\u3002 \u3000\u3000\u4e14\u8bf4\u674e\u5929\u626c\u9aa4\u89c1\u767d\u77f3\u9053\u4eba\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u5450\u5450\u8bf4\u9053\uff1a\u201c\u5927\u8205\uff0c\u4f60\u6765\u5f97\u6b63\u597d\uff0c\u7ed9 \u6211\u529d\u529d\u7eee\u971e\u3002\u201d\u767d\u77f3\u9053\u4eba\u542b\u55d4\u8bf4\u9053\uff1a\u201c\u90a3\u662f\u4f60\u4e24\u4eba\u4e4b\u4e8b\uff0c\u6211\u529d\u6709\u4f55\u7528\u5904\u3002\u5341\u56db\u5e74\u524d\u6211\u5df2\u7ecf\u529d \u8fc7\u4f60\u4e86\uff01\u201d\u674e\u5929\u626c\u751a\u662f\u5c34\u5c2c\uff0c\u4e00\u65f6\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u518d\u8bf4\u5353\u4e00\u822a\u4e5f\u8ddf\u7740\u8dc3\u4e86\u51fa\u6765\uff0c\u77f3\u6d69\u4e00\u89c1\uff0c\u62f1\u624b\u53eb\u9053\uff1a\u201c\u5353\u516c\u5b50\uff01\u201d\u4ed6\u4e0d\u597d\u610f\u601d\u542c\u674e\u5929\u626c \u7684\u5bb6\u4e8b\u7ea0\u7eb7\uff0c\u5c31\u62c9\u5353\u4e00\u822a\u8fc7\u4e00\u8fb9\u8bf4\u8bdd\u3002\u5353\u4e00\u822a\u9053\uff1a\u201c\u77f3\u6307\u6325\uff0c\u6211\u73b0\u5728\u4ecd\u662f\u94a6\u72af\uff0c\u4f60\u53ef\u8981\u7f09\u6211 \u56de\u4eac\uff1f\u201d\u77f3\u6d69\u5927\u7b11\u9053\uff1a\u201c\u592a\u5b50\u6b63\u601d\u5ff5\u4f60\u5462\uff0c\u4f60\u65e9\u5df2\u4e0d\u662f\u94a6\u72af\u4e86\u4e8c\u7687\u4e0a\u73b0\u5728\u91cd\u75c5\uff0c\u4e24\u4e2a\u6708\u524d\u671d \u653f\u5df3\u7531\u592a\u5b50\u6444\u7406\u3002\u674e\u94a6\u5dee\u548c\u5468\u94a6\u5dee\u90a3\u65e5\u5728\u4f60\u5bb6\u9003\u51fa\u6765\u5f8c\uff0c\u5954\u5230\u6cb3\u5357\uff0c\u5728\u6cb3\u5357\u7684\u6cb3\u9632\u7763\u529e\u5bb6\u4e2d \u4f4f\u4e0b\uff0c\u9063\u4eba\u5bc6\u62a5\u592a\u5b50\uff0c\u8fd9\u65f6\u592a\u5b50\u5df2\u638c\u671d\u653f\uff0c\u4e0b\u4ee4\u5f7b\u67e5\uff0c\u90a3\u5192\u5145\u94a6\u4f7f\u7684\u5fa1\u53f2\u5df2\u88ab\u9769\u804c\u67e5\u529e\uff0c\u5927 \u5185\u7684\u536b\u58eb\u4e91\u71d5\u5e73\u4e5f\u88ab\u901a\u7f09\uff0c\u7ebf\u7d22\u4e00\u76f4\u67e5\u5230\u9b4f\u5fe0\u8d24\u8eab\u4e0a\uff0c\u4f46\u9b4f\u5fe0\u8d24\u638c\u7ba1\u4e1c\u5382\uff0c\u7fbd\u7ffc\u5df2\u6210\uff0c\u592a\u5b50 \u4e0d\u613f\u5728\u767b\u57fa\u4e4b\u524d\uff0c\u548c\u4ed6\u786c\u62da\u3002\u73b0\u5728\u6b63\u62db\u8d24\u7eb3\u58eb\uff0c\u5bf9\u4f60\u5c24\u5176\u601d\u5ff5\u3002\u4ed6\u5dee\u9063\u6211\u548c\u674e\u6307\u6325\u51fa\u4eac\uff0c\u4fdd \u62a4\u94a6\u5dee\u56de\u6765\uff0c\u987a\u4fbf\u4e5f\u53eb\u6211\u63a2\u95ee\u4f60\u7684\u6d88\u606f\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u6b63\u6709\u4e8b\u8981\u5230\u4eac\u5e08\u53bb\u89c1\u592a\u5b50\uff0c\u53ef\u662f \u4f60\u4eec\u4fdd\u62a4\u94a6\u5dee\uff0c\u6211\u53ef\u4e0d\u80fd\u548c\u4f60\u540c\u884c\u3002\u201d\u77f3\u6d69\u9053\uff1a\u201c\u5728\u4eac\u76f8\u89c1\u4e5f\u662f\u4e00\u6837\u3002\u201d \u3000\u3000\u4e24\u4eba\u8bf4\u4e86\u4e00\u4f1a\uff0c\u5ffd\u542c\u5f97\u90a3\u5c3c\u59d1\u5389\u58f0\u65a5\u9053\uff1a\u201c\u6eda\u4e0b\u53bb\uff01\u201d\u60f3\u662f\u548c\u89e3\u4e0d\u6210\uff0c\u674e\u5929\u626c\u53c8\u60f9\u5f97\u5979 \u751f\u6c14\u4e86\uff01 \u3000\u3000\u5353\u4e00\u822a\u4e3e\u5934\u4e00\u671b\uff0c\u53ea\u89c1\u90a3\u674e\u5929\u626c\u54ed\u4e27\u7740\u8138\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u9ebd\u54b1\u4eec\u518d\u89c1\uff01\u201d\u5c3c\u59d1\u9053\uff1a \u201c\u6211\u4e0e\u4f60\u6069\u65ad\u4e49\u7edd\uff0c\u6c38\u4e0d\u518d\u89c1\uff01\u201d\u674e\u5929\u626c\u53f9\u4e86\u53e3\u6c14\uff0c\u62db\u624b\u53eb\u77f3\u6d69\u4e0b\u5c71\u3002 \u3000\u3000\u674e\u5929\u626c\u7b49\u53c1\u4eba\u4e0b\u4e86\u5c71\u5f8c\uff0c\u5353\u4e00\u822a\u8fc7\u6765\u4e0e\u90a3\u5c3c\u59d1\u76f8\u89c1\u3002\u8fd9\u65f6\u90a3\u4e2a\u5c11\u5973\u5df2\u5728\u5c3c\u59d1\u8eab\u8fb9\uff0c\u5c0f\u7684 \u90a3\u4e2a\u5219\u5750\u5728\u767d\u77f3\u9053\u4eba\u819d\u4e0a\uff0c\u767d\u77f3\u9053\u4eba\u7b11\u9053\uff1a\u201c\u53eb\u5353\u54e5\u54e5\uff01\u201d\u5411\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u672a\u89c1\u8fc7\u6211\u7684\u5973 \u513f\u5427\uff1f\u201d\u6307\u7740\u5927\u7684\u90a3\u4e00\u4e2a\u9053\uff1a\u201c\u5979\u53eb\u4f55\u843c\u534e\u3002\u201d\u53c8\u62b1\u8d77\u90a3\u5c0f\u7684\u4e00\u4e2a\u9053\uff1a\u201c\u5979\u53eb\u4f55\u7eff\u534e\u3002\u201d\u4f55 \u7eff\u534e\u9ad8\u9ad8\u5174\u5174\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u5353\u54e5\u54e5\u3002\u201d\u4f55\u843c\u534e\u5374\u5fae\u73b0\u7f9e\u6001\uff0c  \u662f\u4f4e\u4f4e\u53eb\u4e86\u4e00\u58f0\u3002\u767d\u77f3\u9053\u4eba\u54c8 \u54c8\u5927\u7b11\u3002\u6b63\u662f\uff1a\u6700\u601c\u5c0f\u513f\u5973\uff0c\u88ab\u5377\u5165\u60c5\u6f6e\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377\u2014\u2014\u7b2c\u4e5d\u56de\u3000\u6c5f\u6e56\u672f\u58eb\u3000\u65bd\u8bc8\u9a97\u7ea2\u4e38\u3000\u9896\u5f02\u5c11\u5e74\u3000\u6709\u5fc3\u6c42\u5251\u8bc0 \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377 \u7b2c\u4e5d\u56de \u6c5f\u6e56\u672f\u58eb\u3000\u65bd\u8bc8\u9a97\u7ea2\u4e38 \u9896\u5f02\u5c11\u5e74\u3000\u6709\u5fc3\u6c42\u5251\u8bc0 \u3000\u3000\u539f\u6765\u767d\u77f3\u9053\u4eba\u4fd7\u5bb6\u59d3\u4f55\uff0c\u751f\u6709\u4e8c\u5973\uff0c\u957f\u5973\u4f55\u843c\u534e\u4eca\u5e74\u5341\u516b\u5c81\uff0c\u6b21\u5973\u4f55\u7eff\u534e\u4eca\u5e74\u521a\u521a\u5341 \u5c81\u3002\u4f55\u7eff\u534e\u51fa\u751f\u672a\u4e45\uff0c\u767d\u77f3\u9053\u4eba\u6b7b\u4e86\u59bb\u5b50\uff0c\u9042\u628a\u4e24\u4e2a\u5973\u513f\u90fd\u4ea4\u4e0e\u59b9\u59b9\u629a\u517b\uff0c\u5341\u5e74\u6765\uff0c\u767d\u77f3\u9053 \u4eba\u6bcf\u9694\u4e00\u4e24\u5e74\u5fc5\u5230\u592a\u5ba4\u5c71\u4e00\u6b21\u63a2\u671b\u5973\u513f\uff0c\u4e0d\u8fc7\u5353\u4e00\u822a\u4e0d\u77e5\u9053\u7f62\u4e86\u3002 \u3000\u3000\u90a3\u77e5\u767d\u77f3\u9053\u4eba\u5fc3\u4e2d\u53e6\u6709\u6253\u7b97\uff0c\u5353\u4e00\u822a\u662f\u6b66\u5f53\u6d3e\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\u4e2d\u6700\u6770\u51fa\u7684\u4eba\u7269\uff0c\u767d\u77f3\u9053\u4eba\u65e9 \u5df2\u5c5e\u610f\u65bc\u4ed6\uff0c\u60f3\u628a\u4f55\u843c\u534e\u914d\u4ed6\u4e3a\u59bb\u3002\u9ec4\u53f6\u9053\u4eba\u77e5\u9053\u5e08\u5f1f\u7684\u5fc3\u610f\uff0c\u6240\u4ee5\u65e5\u524d\u4e00\u518d\u5411\u5353\u4e00\u822a\u8bd5 \u63a2\uff0c\u76ee\u7684\u4fbf\u662f\u60f3\u64ae\u5408\u8fd9\u6bb5\u59fb\u7f18\u3002 \u3000\u3000\u518d\u8bf4\u767d\u77f3\u9053\u4eba\u5c06\u5973\u513f\u4ecb\u7ecd\u4e0e\u5353\u4e00\u822a\u76f8\u8bc6\u4e4b\u5f8c\uff0c\u7b11\u9053\uff1a\u201c\u843c\u534e\uff0c\u5e08\u5144\u4e0d\u662f\u5916\u4eba\uff0c\u4f60\u4eec\u53ef\u4e0d \u5fc5\u62d8\u607a\u5ba2\u5957\u3002\u4f60\u8fd9\u4f4d\u5e08\u5144\u6587\u6b66\u53cc\u4fee\uff0c\u4f60\u6709\u4ec0\u9ebd\u4e0d\u61c2\u7684\u5730\u65b9\u53ef\u4ee5\u95ee\u4ed6\u3002\u201d \u3000\u3000\u4e00\u884c\u4eba\u8d70\u4e0a\u592a\u5ba4\u5c71\u9876\uff0c\u4f55\u7eee\u971e\u524a\u53d1\u4e3a\u5c3c\u5f8c\u6539\u79f0\u6148\u6167\uff0c\u5c31\u5728\u592a\u5ba4\u5c71\u9876\u5efa\u5bfa\u9759\u4fee\u3002\u6148\u6167\u5e26\u9886 \u4ed6\u4eec\u8fdb\u4e86\u5bfa\u9662\uff0c\u62db\u547c\u4e00\u822a\u5750\u4e0b\u3002\u767d\u77f3\u9053\u4eba\u7b11\u9053\uff1a\u201c\u8ba9\u4ed6\u4eec\u5c0f\u4e00\u8f88\u7684\u53bb\u73a9\u5427\u3002\u201d \u3000\u3000\u4f55\u843c\u534e\u5e26\u5353\u4e00\u822a\u5f80\u5bfa\u5185\u5404\u5904\u53c2\u89c2\uff0c\u8d70\u5230\u5026\u65f6\uff0c\u4fbf\u5728\u53e4\u67cf\u4e0b\u6b47\u606f\uff0c\u4e24\u4eba\u76f8\u5bf9\u95f2\u8c08\uff0c\u8bf4\u8d77\u6148 \u6167\u5e08\u592a\u7684\u906d\u9047\uff0c\u4f55\u843c\u534e\u4e00\u9635\u5401\u55df\u53f9\u606f\u8bf4\u9053\uff1a\u201c\u5973\u4eba\u7684\u547d\u771f\u82e6\uff01\u201d\u5353\u4e00\u822a\u7b11\u9053\uff1a\u201c\u4f55\u4ee5\u89c1\u5f97\u4e86 \u8fd9\u4e0d\u8fc7\u662f\u6148\u6167\u5e08\u592a\u9047\u4eba\u4e0d\u6dd1\u7f62\u4e86\u3002\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u8fd9\u4e0d\u5c31\u662f\u4e86\uff1f\u5343\u53e4\u4ee5\u6765\uff0c\u5973\u4eba\u603b\u5f97\u4f9d\u9760\u7537 \u4eba\uff0c\u5ac1\u5f97\u597d\u7684\u8fd8\u53ef\uff0c\u5ac1\u5f97\u4e0d\u597d\uff0c\u4e00\u751f\u53ef\u5c31\u5b8c\u4e86\u3002\u50cf\u6211\u59d1\u59d1\u90a3\u6837\u7684\u4eba\u54c1\u6b66\u529f\uff0c\u4e5f\u53ea\u5f97\u72ec\u4f34\u9752\u706f \u53e4\u4f5b\uff0c\u7ec8\u8001\u8352\u5c71\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5176\u5b9e\u5979\u5927\u53ef\u4e0d\u5fc5\u4e3a\u90a3\u8d1f\u5fc3\u7684\u6c49\u5b50\u53bb\u4f24\u5fc3\u3002\u201d\u4f55\u843c\u534e\u7f24\u9053\uff1a \u201c\u5c31\u662f\u5f7c\u6b64\u60c5\u6ca1\u610f\u5408\u7684\u4e5f\u96be\u514d\u4e0d\u751f\u53d8\u5366\u3002\u50cf\u53f8\u9a6c\u76f8\u5982\u548c\u5353\u6587\u541b\uff0c\u624d\u5b50\u4f73\u4eba\uff0c\u4e24\u60c5\u6b22\u60a6\uff0c\u5e94\u7b97 \u5f97\u662f\u5343\u79cb\u4f73\u8bdd\u4e86\u5427\uff1f\u53ef\u662f\u5230\u5353\u6587\u541b\u5e74\u7eaa\u5927\u4e86\uff0c\u53f8\u9a6c\u76f8\u5982\u4fbf\u751f\u4e8c\u5fc3\uff0c\u8981\u4e0d\u662f\u5353\u6587\u541b\u8d4b\u4e86\u90a3\u9996 \u201c\u767d\u5934\u541f\u201d\uff0c\u4f7f\u53f8\u9a6c\u76f8\u5982\u56de\u5fc3\u8f6c\u610f\uff0c\u4f73\u5076\u5c82\u4e0d\u53cd\u6210\u6028\u5076\u4e86\uff0c\u4e8f\u90a3\u53f8\u9a6c\u76f8\u5982\u8fd8\u7ed9\u9648\u7687\u540e\u201c\u6309\uff1a \u5373\u6c49\u5b5d\u6b66\u7687\u5e1d\u4e4b\u540e\u201d\u5199\u8fc7\u957f\u95e8\u8d4b\u5462\uff1f\u8f6e\u5230\u4ed6\u81ea\u5df1\u4e4b\u65f6\uff0c\u5374\u5c31\u4e0d\u77e5\u90a3\u6028\u5987\u4e4b\u82e6\u4e86\u3002\u4f60\u8bf4\u5973\u4eba\u7684 \u547d\u8fd0\u662f\u4e0d\u662f\u53ef\u60b2\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u542c\u4e86\uff0c\u7a81\u7136\u8d77\u4e86\u4e00\u79cd\u5947\u5f02\u7684\u611f\u89c9\uff0c\u4e0d\u671f\u7136\u7684\u60f3\u8d77\u4e86\u7389\u7f57\u5239\u6765\uff0c\u4ed6\u60f3\u5728\u7389\u7f57\u5239\u53e3 \u4e2d\uff0c\u7edd\u4e0d\u4f1a\u8bf4\u51fa\u201c\u5973\u4eba\u547d\u82e6\u201d\u4e4b\u7c7b\u7684\u8bdd\uff01 \u3000\u3000\u8fd9\u4f55\u843c\u534e\u8c08\u5410\u6587\u96c5\uff0c\u6001\u5ea6\u5927\u65b9\uff0c\u8bba\u672c\u4e8b\u6587\u624d\u6b66\u827a\u4ff1\u90fd\u6765\u5f97\u3002\u7136\u800c\u4e0d\u77e5\u600e\u7684\uff0c\u5353\u4e00\u822a\u603b\u89c9 \u5f97\u5979\u7f3a\u5c11\u4e86\u4e9b\u4ec0\u9ebd\u4e1c\u897f\u4f3c\u7684\u3002\u662f\u4ec0\u9ebd\u4e1c\u897f\u5462\uff1f\u5353\u4e00\u822a\u8bf4\u4e0d\u51fa\u6765\uff0c\u4e5f\u8bb8\u5c31\u662f\u96be\u4ee5\u63cf\u7ed8\u7684\u3001\u8574\u85cf \u5728\u751f\u547d\u4e2d\u7684\u4e00\u79cd\u5947\u5f02\u7684\u5149\u5f69\u5427\uff1f\u8fd9\u79cd\u201c\u5149\u5f69\u201d\uff0c\u5353\u4e00\u822a\u5728\u7389\u7f57\u5239\u7684\u8eab\u4e0a\u53ef\u4ee5\u4eb2\u5207\u7684\u611f\u77e5\uff0c\u4e5f \u56e0\u800c\u5f15\u8d77\u6fc0\u52a8\u751a\u81f3\u201c\u618e\u6076\u201d\uff0c\u4f46\u5c31\u7b97\u662f\u618e\u6076\u5427\uff0c\u90a3\u201c\u618e\u6076\u201d\u4e5f\u662f\u5f3a\u70c8\u7684\u5438\u5f15\u4eba\u7684\u3002 \u3000\u3000\u7136\u800c\u767d\u77f3\u9053\u4eba\u5374\u4e0d\u77e5\u5353\u4e00\u822a\u5fc3\u4e2d\u6240\u60f3\uff0c\u4ed6\u548c\u59b9\u59b9\u7545\u53d9\u79bb\u60c5\u4e4b\u5f8c\uff0c\u8d70\u51fa\u5916\u5802\uff0c\u89c1\u4e8c\u4eba\u8c08\u5f97 \u751a\u6b22\uff0c\u5fc3\u4e2d\u5f88\u662f\u9ad8\u5174\u3002 \u3000\u3000\u767d\u77f3\u9053\u4eba\u672c\u6765\u6ca1\u6709\u6253\u7b97\u5230\u5c11\u6797\u5bfa\u53c2\u8c12\uff0c\u4f46\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u6148\u6167\u5e08\u592a\u5374\u5ffd\u7136\u63a5\u5230\u5c11\u6797\u76d1\u5bfa\u5c0a \u80dc  \u5e08\u7684\u4e24\u4efd\u8bf7\u5e16\uff0c\u4e00\u4efd\u5199\u5979\u7684\u540d\u5b57\uff0c\u53e6\u4e00\u4efd\u5199\u767d\u77f3\u9053\u4eba\u7684\u540d\u5b57\u3002\u6148\u6167\u7b11\u9053\uff1a\u201c\u5c11\u6797\u76d1\u5bfa\u7684 \u6d88\u606f\u5012\u771f\u7075\uff0c\u4f60\u624d\u6765\u4e86\u4e00\u5929\u4ed6\u4eec\u5c31\u77e5\u9053\u4e86\u3002\u201d\u6148\u6167\u5728\u592a\u5ba4\u5c71\u9876\u9690\u5c45\uff0c\u548c\u5c11\u6797\u5bfa\u76f8\u90bb\uff0c\u6240\u4ee5\u4e5f \u6709\u6765\u5f80\u3002\u767d\u77f3\u9053\uff1a\u201c\u54b1\u4eec\u638c\u95e8\u5e08\u5144\u7fbd\u5316\u4e4b\u5f8c\uff0c\u4ed6\u4eec\u4e5f\u66fe\u6d3e\u4eba\u540a\u5501\uff0c\u793c\u5c1a\u5f80\u6765\uff0c\u65e2\u7136\u4ed6\u4eec\u53c8\u6709 \u8bf7\u5e16\u9012\u5230\uff0c\u6211\u5c31\u548c\u4f60\u53bb\u7b54\u62dc\u5427\u3002\u201d\u53c8\u5bf9\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u662f\u672c\u6d3e\u672a\u6765\u7684\u638c\u95e8\uff0c\u8d81\u8fd9\u673a\u4f1a\u89c1\u89c1\u5c11 \u6797\u7684\u957f\u8f88\u4e5f\u597d\u3002\u201d \u3000\u3000\u592a\u5ba4\u5c11\u5ba4\u4e24\u5c71\u5bf9\u5cd9\uff0c\u4e2d\u95f4\u76f8\u8ddd\u7ea6\u5341\u9980\u91cc\uff0c\u53c1\u4eba\u884c\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5df2\u5230\u5c11\u5ba4\u5c71\u5317\u9e93\u7684\u4e94\u4e73\u5cf0 \u4e0b\uff0c\u4f46\u89c1\u767e\u5854\u5982\u6797\uff0c\u5c11\u6797\u5bfa\u5c31  \u7acb\u5728\u5854\u6797\u4e4b\u4e2d\u3002\u767d\u77f3\u9053\uff1a\u201c\u6211\u4eec\u5148\u53bb\u627e\u77e5\u5ba2\u901a\u62a5\uff0c\u4f60\u5728\u5f8c\u9762 \u7a0d\u5f85\u3002\u201d\u5353\u4e00\u822a\u70b9\u5934\u5e94\u8bfa\u3002\u6b63\u8bf4\u8bdd\u95f4\u5ffd\u95fb\u5f97\u55a7\u56a3\u4e4b\u58f0\uff0c\u53c1\u4eba\u8d70\u5230\u5c11\u6797\u5bfa\u524d\uff0c\u53ea\u89c1\u5bfa\u95e8\u7d27\u95ed\uff0c \u6709\u4e24\u4e2a\u8001\u5934\u7ad9\u7acb\u5728\u95e8\u524d\u7684\u5927\u77f3\u4e0a\u7834\u53e3\u5927\u9a82\u3002\u4e00\u4e2a\u53eb\u9053\uff1a\u201c\u955c\u660e\u8001\u79c3\uff0c\u4f60\u6446\u4ec0\u9ebd\u67b6\u5b50\uff1f\u4f60\u867d\u662f \u4e00\u6d3e\u5b97\u7956\uff0c\u6211\u4eec\u4e5f\u4e0d\u662f\u6ca1\u6709\u6765\u5934\u7684\u4eba\uff01\u201d\u53e6\u4e00\u4e2a\u9053\uff1a\u201c\u6211\u770b\u4f60\u4eec\u5c11\u6797\u4e5f\u662f\u6d6a\u5f97\u865a\u540d\uff0c\u82e5\u7136\u662f \u786e\u6709\u771f\u624d\u5b9e\u5b66\uff0c\u4e3a\u4f55\u4e0d\u6562\u4e0e\u6211\u4eec\u89c2\u6469\u8f83\u6280\uff1f\u201d\u5353\u4e00\u822a\u542c\u8fd9\u4e24\u4eba\u7834\u53e3\u5927\u9a82\uff0c\u5341\u5206\u60ca\u8bb6\uff0c\u8981\u77e5\u5c11 \u6797\u6b66\u5f53\u4e24\u6d3e\u4e43\u662f\u6b66\u6797\u4e2d\u7684\u6cf0\u5c71\u5317\u6597\uff0c\u5728\u5f53\u65f6\u800c\u8bba\uff0c\u6b66\u5f53\u6d3e\u867d\u8f83\u4e3a\u4eba\u591a\u58f0\u76db\uff0c\u4f46\u8bf4\u5230\u5386\u53f2\u60a0 \u957f\uff0c\u4eba\u624d\u8f88\u51fa\uff0c\u5374\u8fd8\u8981\u63a8\u5c11\u6797\u7b2c\u4e00 \u3002\u8fd9\u4e24\u4e2a\u662f\u4f55\u7b49\u4eba\u7269\uff01\u575d\u7136\u6562\u5728\u5c11\u6797\u5bfa\u7684\u5c71\u95e8\u524d\u6311\u6218\uff01 \u3000\u3000\u8fd9\u4e24\u4e2a\u8001\u5934\u89c1\u767d\u77f3\u9053\u4eba\u548c\u6148\u6167\u5e08\u592a\u8d70\u6765\uff0c\u5728\u77f3\u4e0a\u8df3\u4e0b\uff0c\u8fce\u4e0a\u524d\u6765\uff0c\u9762\u4e0a\u5806\u7b11\uff0c\u4f5c\u51fa\u62db\u547c \u4e4b\u72b6\u3002\u6148\u6167\u5e08\u592a\u51b7\u7740\u9762\u5b54\uff0c\u671b\u4e5f\u4e0d\u671b\u4ed6\u4eec\u3002\u767d\u77f3\u9053\u4eba\u89c1\u72b6\uff0c\u4e5f\u6602\u5934\u9614\u6b65\uff0c\u50b2\u7136\u4e0d\u7406\u3002\u4e24\u4e2a\u8001 \u5934\u751a\u4e3a\u6ca1\u8da3\uff0c\u8d70\u4e86\u8fc7\u6765\uff0c\u8fce\u7740\u5353\u4e00\u822a\u642d\u8baa\u8bf4\u9053\uff1a\u201c\u5c0f\u54e5\uff0c\u4f60\u662f\u6765\u5c11\u6797\u53c2\u8c12\u7684\u5417\uff1f\u201d\u5353\u4e00\u822a\u70b9 \u4e86\u70b9\u5934\u3002\u4e00\u4e2a\u8001\u5934\u9f3b\u5b50\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u5176\u5b9e\u4e0d\u53c2\u8c12\u4e5f\u7f62\uff0c\u5c11\u6797\u5bfa\u9664\u4e86\u955c\u660e\u957f\u8001\u5927\u7ea6\u8fd8\u53ef \u548c\u6211\u8f83\u91cf\u51e0\u56de\u5408\u4e4b\u5916\uff0c\u5176\u9980\u7684\u90fd\u65e0\u8db3\u89c2\u3002\u4f60\u53c8\u4f55\u82e6\u52b3\u795e\u8fdc\u6765\uff01\u201d\u5353\u4e00\u822a\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u95ee \u9053\uff1a\u201c\u6562\u95ee\u8001\u524d\u8f88\u59d3\u6c0f\u3002\u201d\u90a3\u8001\u5934\u53c8\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6211\u7684\u540d\u5b57\u8bf4\u4f60\u4e5f\u4e0d\u77e5\u9053\u3002\u5f53\u4eca\u4e4b \u4e16\uff0c\u5f8c\u5b66\u8005\u4f46\u6155\u865a\u540d\uff0c\u8a00\u5fc5\u5c11\u6797\u6b66\u5f53\uff0c\u50cf\u6211\u4eec\u8fd9\u6837\u7684\u8001\u5934\u5b50\u53ea\u56e0\u65e0\u7455\u5f00\u5b97\u7acb\u6d3e\uff0c\u5c0f\u8f88\u90a3\u8fd8\u77e5 \u9053\u6211\u4eec\u4e86\u4e0d\u8fc7\u82e5\u662f\u6b66\u5f53\u4e94\u8001\u5728\u6b64\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u4ee5\u665a\u8f88\u81ea\u5c45\u3002\u201d\u90a3\u8001\u5934\u5520\u5520\u53e8\u53e8\u8bf4\u4e86\u4e00\u5927\u7bc7\u3002 \u5353\u4e00\u822a\u7b80\u76f4\u6478\u4e0d\u7740\u5934\u8111\u3002 \u3000\u3000\u90a3\u8001\u4eba\u53c8\u95ee\u9053\uff1a\u201c\u524d\u9762\u90a3\u4f4d\u9053\u58eb\u662f\u4f60\u7684\u5e08\u7236\u5417\uff1f\u201d\u5353\u4e00\u822a\u6253\u4e86\u4e2a\u7a81  \uff0c\u6697\u60f3\u4ed6\u8bf4\u6b66\u5f53\u4e94 \u8001\u90fd\u8981\u5949\u4ed6\u4e3a\u957f\u8f88\uff0c\u5982\u4f55\u5374\u4e0d\u8bc6\u767d\u77f3\u5e08\u53d4\u3002\u5f53\u4e0b\u7b54\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u5e08\u53d4\u3002\u201d\u53c8\u95ee\u4e24\u4eba\u540d\u5b57\uff0c \u90a3\u8001\u5934\u5f97\u610f\u6d0b\u6d0b\u7684\u9053\uff1a\u201c\u4f60\u662f\u54ea\u4e00\u6d3e\u7684\uff1f\u4f60\u4eec\u6d3e\u4e2d\u7684\u957f\u8001\u6ca1\u6709\u5bf9\u4f60\u8bf4\u8fc7\u201c\u9646\u4e0a\u4ed9\u201d\u80e1\u8fc8\u548c \u201c\u795e\u624b\u201d\u5b5f\u98de\u7684\u540d\u5b57\u5417\uff1f\u6211\u5c31\u662f\u9646\u4e0a\u4ed9\u80e1\u8fc8\u3002\u4e8c\u5341\u5e74\u524d\u6211\u4e0e\u7d2b\u9633\u9053\u957f\u5728\u6b66\u5f53\u5c71\u8f83\u6280\u8bba\u5251\uff0c\u5728 \u62f3\u6cd5\u4e0a\u627f\u4ed6\u8ba9\u4e86\u6211\u4e00\u62db\uff1a\u5728\u5251\u6cd5\u4e0a\u5462\uff0c\u6211\u672c\u6765\u53ef\u5174\u4ed6\u6253\u6210\u5e73\u624b\uff0c\u4f46\u65e2\u7136\u5728\u62f3\u6cd5\u4e0a\u80dc\u4e86\u4ed6\uff0c\u5c31 \u4e0d\u80fd\u4e0d\u7ed9\u4ed6\u7559\u70b9\u9762\u5b50\uff0c\u6240\u4ee5\u5728\u5251\u6cd5\u4e0a\u6211\u8ba9\u4e86\u4ed6\u534a\u62db\u3002\u201d\u5353\u4e00\u822a\u771f\u662f\u95fb\u6240\u672a\u95fb\uff0c\u5fc3\u60f3\u81ea\u5df1\u5e08\u7236 \u6700\u4e3a\u8c26\u6339\u670d\u5584\uff0c\u82e5\u7136\u771f\u6709\u8fd9\u4e00\u56de\u4e8b\uff0c\u4ed6\u4e3a\u4f55\u4ece\u4e0d\u63d0\u53ca\u3002 \u3000\u3000\u90a3\u201c\u795e\u624b\u201d\u5b5f\u98de\u63d2\u53e3\u9053\uff1a\u201c\u90a3\u662f\u4e8c\u5341\u5e74\u524d\u4e4b\u4e8b\uff0c\u90a3\u65f6\u7d2b\u9633\u9053\u4eba\u7684\u5251\u672f\u8fd8\u53ef\u4ee5\u4e0e\u6211\u8fd9\u4f4d\u80e1 \u8001\u54e5\u5339\u654c\uff0c\u82e5\u73b0\u5728\u6765\u6bd4\uff0c\u6211\u6562\u8bf4\u4e0d\u6ee1\u4e94\u5341\u62db\u4ed6\u5c31\u8981\u8d25\u4e0b\u9635\u6765\u3002\u81f3\u65bc\u5c11\u6797\u5bfa\u867d\u4ee5\u795e\u62f3\u7740\u540d\uff0c\u4f46 \u5176\u5b9e\u5f31\u70b9\u751a\u591a\uff0c\u770b\u6765\u90a3\u955c\u660e  \u5e08\u8fd8\u4e0d\u662f\u6211\u7684\u5bf9\u624b\uff0c\u66f4\u4e0d\u8981\u8bf4\u5bf9\u6211\u4eec\u7684\u80e1\u8001\u54e5\u4e86\u3002\u201d\u8bf4\u7f62\u4ece\u888b \u5b50\u91cc\u6478\u51fa\u4e00\u672c\u4e66\u6765\uff0c\u5c01\u9762\u4e0a\u5199\u7740\u201c\u5c11\u6797\u62f3\u6cd5\u5341\u5f0a\u201d\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e3a\u4e86\u7834\u9664\u4e16\u4eba\u6210\u89c1\uff0c\u6240\u4ee5\u7740 \u4e86\u8fd9\u4e00\u90e8\u4e66\uff0c\u8be6\u8bba\u5c11\u6797\u62f3\u6cd5\u7684\u758f\u6f0f\u4e4b\u5904\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u54e6\uff0c\u90a3\u4f60\u662f\u8981\u628a\u6b64\u4e66\u732e\u4e0e\u955c\u660e\u957f\u8001 \u7684\u4e86\uff01\u201d\u5b5f\u98de\u9053\uff1a\u201c\u53ef\u60dc\u90a3\u955c\u660e\u8001\u79c3\u7a7a\u8d1f\u91cd\u540d\uff0c\u6c14\u5ea6\u751a\u5dee\uff0c\u6211\u4eec\u6765\u4e86\uff0c\u4ed6\u7adf\u7136\u7ed9\u6211\u4eec\u6765\u4e2a\u95ed \u95e8\u4e0d\u89c1\u3002\u201d\u5353\u4e00\u822a\u6b63\u60f3\u63a5\u8fc7\u6b64\u4e66\u7ffb\u9605\uff0c\u5ffd\u89c1\u5c11\u6797\u5bfa\u5927\u95e8\u6253\u5f00\uff0c\u4e24\u4e2a\u8001\u548c\u5c1a\u5e76\u80a9\u8d70\u51fa\u3002\u90a3\u80e1\u8fc8 \u5927\u53eb\u4e00\u58f0\uff1a\u201c\u597d\u5440\uff01\u603b\u7b97\u89c1\u7740\u4f60\u4e86\uff01\u6697\u660e\uff0c\u4f60\u6562\u4e0d\u6562\u63a5\u6211\u5341\u62db\u3002\u201d\u5de6\u9996\u90a3\u4e2a\u6148\u7709\u5584\u76ee\u7684\u8001\u548c \u5c1a\u5ff5\u4e86\u4e00\u58f0\u201c\u963f\u5f25\u9640\u4f5b\u201d\uff0c\u9053\uff1a\u201c\u8d2b\u50e7\u5e74\u8001\u4f53\u8870\uff0c\u4e45\u5df2\u65e0\u6b64\u96c5\u5174\u4e86\u3002\u201d\u53f3\u9996\u90a3\u4e2a\u548c\u5c1a\u5374\u51b7\u7b11 \u9053\uff1a\u201c\u542c\u8bf4\u4f60\u4eec\u8fd9\u51e0\u5929\u5929\u5929\u8981\u6765\u627e\u6211\u4eec\u7684\u4e3b\u6301\u6bd4\u6b66\uff0c\u6211\u4eec\u7684\u77e5\u5ba2\u50e7\u4eba\u5df2\u7ecf\u5bf9\u4f60\u8bf4\u8fc7\u5c11\u6797\u7684\u89c4 \u77e9\uff0c\u8981\u6765\u6b64\u6b66\u7684\u5148\u548c\u6211\u4eec\u7b2c\u4e94\u7ea7\u7684\u95e8\u4eba\u6bd4\u8d77\uff0c\u4f60\u4e00\u7ea7\u7ea7\u7684\u6253\u53bb\uff0c\u82e5\u90fd\u6253\u80dc\u4e86\uff0c\u6211\u81ea\u7136\u6765\u63a5\u4f60 \u7684\u9ad8\u62db\uff0c\u4f60\u4e0d\u6309\u6211\u4eec\u7684\u89c4\u77e9\uff0c\u6765\u8fd9\u91cc\u5435\u5435\u56b7\u56b7\u4f5c\u751a\uff1f\u201d\u628a\u624b\u4e00\u62db\uff0c\u53eb\u9053\uff1a\u201c\u609f\u51c0\uff0c\u4f60\u548c\u8fd9\u4e24 \u4f4d\u5ba2\u4eba\u6bd4\u5212\u6bd4\u5212\u3002\u201d\u4e00\u4e2a\u5341\u56db\u4e94\u5c81\u7684\u5c0f\u6c99\u5f25\u5e94\u58f0\u8df3\u51fa\uff0c\u80e1\u8fc8\u6012\u6c14\u51b2\u51b2\uff0c\u5927\u58f0\u9a82\u9053\uff1a\u201c\u5c0a\u80dc\u8001 \u79c3\uff0c\u4f60\u6562\u8fd9\u6837\u5c0f\u89d1\u6211\u4eec\uff0c\u4f60\u662f\u76d1\u5bfa\uff0c\u6211\u4eec\u4e5f\u662f\u6709\u8eab\u4efd\u7684\u4eba\uff0c\u96be\u9053\u6211\u4eec\u5c31\u4e0d\u914d\u548c\u4f60\u89c2\u6469\u5370 \u8bc1\u3002\u201d\u90a3\u5c0f\u6c99\u5f25\u7acb\u4e86\u4e00\u4e2a\u95e8\u6237\uff0c\u53eb\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4eec\u8fdc\u6765\u662f\u5ba2\uff0c\u8ba9\u4f60\u5148\u8fdb\u53c1\u62db\uff01\u201d\u80e1\u8fc8\u6012 \u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u79c3\u9a74\uff0c\u4f60\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d\u5c0f\u6c99\u5f25\u505a\u4e2a\u9b3c\u8138\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u53eb\u65e0\u8d56\uff01\u201d\u5353\u4e00\u822a \u542c\u4e86\uff0c\u4e0d\u89c9\u7b11\u51fa\u58f0\u6765\uff0c\u8fd9\u201c\u65e0\u8d56\u201d\u4e8c\u5b57\u7528\u6cb3\u5357\u4e61\u97f3\u5ff5\u51fa\uff0c\u6b63\u597d\u548c\u201c\u80e1\u8fc8\u201d\u76f8\u540c\u3002 \u3000\u3000\u80e1\u8fc8\u53c8\u9a82\u9053\uff1a\u201c\u6b66\u5f53\u5c11\u6797\uff0c\u5e76\u79f0\u6b66\u6797\u9886\u8896\uff1a\u955c\u660e\u4f60\u4e3a\u4f55\u4e0d\u5b66\u5b66\u7d2b\u9633\u9053\u957f\u7684\u6c14\u5ea6\uff0c\u7d2b\u9633\u5f53 \u65e5\u4eb2\u81ea\u8fce\u63a5\u6211\u4e0a\u6b66\u5f53\uff0c\u6bd4\u62f3\u8f93\u4e86\u7ed9\u6211\uff0c\u53c8\u4eb2\u7387\u56db\u4e2a\u5e08\u5f1f\u9001\u6211\u4e0b\u5c71\u3002\u90a3\u624d\u662f\u6b66\u6797\u9886\u8896\u7684\u80f8 \u895f\uff01\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5ffd\u7136\u62cd\u7684\u4e00\u58f0\u4e2d\u4e86\u4e00\u8bb0\u8033\u5149\uff0c\u767d\u77f3\u9053\u4eba\u628a\u624b\u4e00\u6325\uff0c\u5c06\u4ed6\u6454\u51fa\u53c1\u4e08\u5f00\u5916\uff0c\u6740 \u732a\u822c\u7684\u6eda\u5730\u5927\u53eb\uff01 \u3000\u3000\u5b5f\u98de\u5728\u65c1\u5927\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5c11\u6797\u5bfa\u76ee\u4e2d\u8fd8\u6709\u738b\u6cd5\u9ebd\uff1f\u767d\u65e5\u9752\u5929\u4f24\u4eba\u5bb3\u547d\uff01\u201d\u80e1\u8fc8\u4e5f\u8fb9\u6eda\u8fb9 \u53eb\uff0c\u6e10\u6e10\u58f0\u97f3\u5636\u54d1\uff0c\u5c31\u50cf\u771f\u7684\u8981\u6b7b\u4e00\u822c\u3002\u955c\u660e\u8001\u60ee\u5e08\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u5bf9\u76d1\u5bfa\u5c0a\u80dc\u9053\uff1a\u201c\u7ed9\u4e00\u7c92 \u5c0f\u8fd8\u4e39\u4e0e\u4ed6\u670d\u7528\u3002\u201d\u5c0a\u80dc  \u5e08\u4ece\u6000\u4e2d\u6478\u51fa\u4e00\u53ea\u94f6\u74f6\uff0c\u5012\u4e86\u4e00\u7c92\u5c0f\u5c0f\u7684\u7ea2\u4e38\uff0c\u53eb\u5c0f\u6c99\u5f25\u9012\u7ed9\u5b5f \u98de\u9053\uff1a\u201c\u4e3b\u6301\u6148\u60b2\uff0c\u8d50\u4f60\u7075\u4e39\u3002\u201d\u5b5f\u98de\u4e00\u628a\u63a5\u8fc7\uff0c\u9001\u5165\u80e1\u8fc8\u53e3\u4e2d\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u80e1\u8fc8\u4ecd\u7136\u5636 \u53eb\u3002\u5b5f\u98de\u9053\uff1a\u201c\u6211\u7684\u5927\u54e5\u7ed9\u4f60\u4eec\u7528\u6bd2\u624b\u6697\u4f24\uff0c\u4e00\u7c92\u7ea2\u4e38\u9876\u4e0d\u5f97\u4e8b\uff0c\u518d\u7ed9\u4e24\u9897\u4e0e\u6211\u3002\u201d\u5c0a\u80dc \u5e08\u6012\u9053\uff1a\u201c\u4f60\u60f3\u8bb9\u8bc8\u9ebd\uff1f\u201d\u955c\u660e\u8001  \u5e08\u6148\u60b2\u4e3a\u6000\uff0c\u53ea\u6050\u80e1\u8fc8\u771f\u7684\u4f24\u91cd\uff0c\u4fbf\u9053\uff1a\u201c\u518d\u7ed9\u4e00\u9897\u4ed6 \u5427\u3002\u201d\u5c0a\u80dc\u65e0\u5948\uff0c\u53ea\u5f97\u518d\u6311\u51fa\u4e00\u9897\u7ea2\u4e38\u4e0e\u4ed6\uff0c\u5b5f\u98de\u5927\u559c\u63a5\u8fc7\uff0c\u7eb3\u5165\u6000\u4e2d\uff0c\u628a\u80e1\u8fc8\u80cc\u5728\u80cc\u4e0a\uff0c \u62d4\u811a\u4e0b\u5c71\u3002 \u3000\u3000\u767d\u77f3\u9053\u4eba\u6012\u6c14\u672a\u6d88\uff0c\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8ba4\u5f97\u6211\u9ebd\uff01\u201d\u5b5f\u98de\u56de\u5934\u8bf4\u9053\uff1a\u201c\u6b63\u60f3\u8bf7\u6559\u3002\u201d\u767d\u77f3\u9053 \u4eba\u51b7\u7b11\u9053\uff1a\u201c\u6211\u662f\u7d2b\u9633\u9053\u957f\u7684\u56db\u5e08\u5f1f\uff0c\u4eba\u79f0\u5c60\u9f99\u5251\u5ba2\u767d\u77f3\u9053\u4eba\u7684\u4fbf\u662f\uff01\u90a3\u8001\u65e0\u8d56\u4e0d\u662f\u8bf4\u6211\u66fe \u4eb2\u81ea\u9001\u4ed6\u4e0b\u8fc7\u6b66\u5f53\u5c71\u5417\uff1f\u600e\u9ebd\u5f53\u9762\u53c8\u4e0d\u8ba4\u8bc6\u4e86\uff1f\u201d\u4e00\u7fa4\u5c0f\u6c99\u5f25\u54d7\u7136\u5927\u7b11\u3002 \u3000\u3000\u90a3\u80e1\u8fc8\u5ffd\u7136\u5728\u5b5f\u98de\u80cc\u4e0a\u62ac\u8d77\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u6211\u9053\u662f\u8c01\uff1f\u539f\u6765\u662f\u6b66\u5f53\u4e94\u8001\u4e2d\u4eba\uff0c\u602a\u4e0d \u5f97\u6709\u70b9\u529f\u529b\uff0c\u6211\u8001\u4e86\uff0c\u7cbe\u795e\u4e0d\u6d4e\u4e86\uff0c\u8fc7\u53c1\u5e74\u6211\u53eb\u5f92\u5f1f\u627e\u4f60\u7b97\u5e10\u3002\u201d\u58f0\u97f3\u867d\u7136\u5e76\u4e0d\u54cd\u4eae\uff0c\u4f46\u5374 \u4e00\u70b9\u4e5f\u4e0d\u5636\u54d1\u3002\u767d\u77f3\u9053\u4eba\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u559d\u9053\uff1a\u201c\u9f20\u8f88\u5feb\u6eda\uff01\u201d\u5b5f\u98de\u6025\u5fd9\u98de\u6b65\u4e0b\u5c71\u3002 \u3000\u3000\u5c0a\u80dc\u7b11\u9053\uff1a\u201c\u767d\u77f3\u9053\u5144\uff0c\u4f60\u771f\u4e0d\u8be5\u901a\u540d\u3002\u201d\u767d\u77f3\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u5c0a\u80dc\u9053\uff1a\u201c\u4f60\u4e00\u901a \u540d\uff0c\u53c8\u6709\u4ed6\u4eec\u8bf4\u5634\u7684\u4e86\u3002\u4ed6\u4eec\u5c06\u6765\u6b7b\u4e86\uff0c\u4e5f\u53ef\u4ee5\u5728\u5893\u7891\u4e0a\u523b\u4e0a\u4e00\u884c\u5927\u5b57\uff1a\u66fe\u4e0e\u6b66\u5f53\u4e94\u8001\u4ea4 \u624b\uff1f\u201d\u767d\u77f3\u5931\u7b11\u9053\uff1a\u201c\u5c82\u6709\u6b64\u7406\uff01\u201d\u5c0a\u80dc\u9053\uff1a\u201c\u767d\u77f3\u9053\u5144\uff0c\u8fd9\u5012\u4e0d\u662f\u6211\u6545\u610f\u8bf4\u7b11\u3002\u6b66\u6797\u4e2d\u5f88 \u6709\u8fd9\u9ebd\u4e00\u4e9b\u65e0\u804a\u4eba\u7269\u3002\u50cf\u8fd9\u4e24\u4e2a\u8001\u65e0\u8d56\uff0c\u4ed6\u660e\u77e5\u6211\u4eec\u7684\u4e3b\u6301\u4e0d\u80af\u4e0e\u4ed6\u4eec\u52a8\u624b\uff0c\u53c8\u660e\u77e5\u5c11\u6797\u5bfa \u7684\u4eba\u7edd\u4e0d\u4f1a\u4f24\u4ed6\u4eec\u6027\u547d\uff0c\u6240\u4ee5\u624d\u6562\u5728\u5c71\u95e8\u80e1\u9a82\uff0c\u5e0c\u671b\u4e00\u9a82\u6210\u540d\u3002\u201d\u767d\u77f3\u9053\uff1a\u201c\u53ea\u6709\u4f60\u4eec\u5c11\u6797 \u5bfa\u624d\u8fd9\u9ebd\u5bbd\u5b8f\u5927\u91cf\uff0c\u82e5\u7136\u662f\u5728\u6b66\u5f53\u5c71\u4e0a\uff0c\u4ed6\u4eec\u4e0d\u65ad\u4e86\u4e24\u6761\u817f\u624d\u602a\u3002\u201d\u5c0a\u80dc\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u4eec \u4e0d\u6562\u60f9\u4f60\u4eec\u6b66\u5f53\u6d3e\uff0c\u4f46\u4ed6\u4eec\u5374\u6599\u4e0d\u5230\u5728\u5d69\u5c71\u4e0a\u8c08\u8bba\u6b66\u5f53\u6d3e\uff0c\u4e5f\u4f1a\u9047\u4e0a\u4f60\u8fd9\u4f4d\u715e\u661f\u3002\u201d\u767d\u77f3\u629a \u638c\u5927\u7b11\u3002\u5c0a\u80dc\u5ffd\u9053\uff1a\u201c\u767d\u77f3\u9053\u5144\uff0c\u6211\u770b\u4f60\u521a\u624d\u6240\u53d1\u90a3\u638c\uff0c\u521d\u53d1\u65f6\u4f3c\u7528\u4e86\u5341\u6210\u529b\u91cf\uff0c\u5230\u6cbe\u8863\u65f6 \u6700\u591a\u53ea\u6709\u53c1\u6210\u529b\u91cf\uff0c\u4e0d\u77e5\u6211\u770b\u5f97\u5bf9\u5426\uff1f\u201d\u767d\u77f3\u5341\u5206\u4f69\u670d\uff0c\u9053\uff1a\u201c\u5927\u5e08\u771f\u662f\u89c2\u5bdf\u5165\u5fae\u3002\u6211\u89c1\u90a3 \u8001\u65e0\u8d56\u8fd9\u6837\u8bf4\u5634\uff0c\u6240\u4ee5\u51fa\u624b\u65f6\u7528\u529b\u6253\u53bb\uff0c\u90a3\u77e5\u4e00\u770b\u4ed6\u7684\u8eab\u6cd5\uff0c\u624d\u77e5\u4ed6\u5b9e\u662f\u4e0d\u582a\u4e00\u51fb\uff0c\u6240\u4ee5\u53ea \u7528\u4e86\u53c1\u6210\u529b\u91cf\u3002\u201d\u5c0a\u80dc  \u5e08\u53f9\u606f\u8bf4\u9053\uff1a\u201c\u5012\u5e95\u4e0a\u4e86\u4ed6\u4eec\u7684\u5f53\u4e86\uff01\u201d\u767d\u77f3\u9053\uff1a\u201c\u600e\u9ebd\uff1f\u201d\u5c0a\u80dc \u9053\uff1a\u201c\u7ed9\u4ed6\u4eec\u591a\u9a97\u53bb\u4e86\u4e00\u7c92\u7075\u4e39\u3002\u201d\u955c\u660e\u8001  \u5e08\u9053\uff1a\u201c\u5e08\u5f1f\u4e0d\u53ef\u5982\u6b64\u523b\u8584\uff0c\u5c31\u7b97\u7ed9\u4ed6\u591a\u8981\u4e86 \u4e00\u7c92\uff0c\u6b64\u4e39\u53ea\u80fd\u6551\u4eba\uff0c\u4e5f\u4e0d\u6101\u4ed6\u4eec\u4f1a\u62ff\u53bb\u505a\u4ec0\u9ebd\u574f\u4e8b\u3002\u201d\u5c0a\u80dc\u6447\u4e86\u6447\u5934\uff0c\u9ed8\u7136\u4e0d\u8bed\u3002\u8c01\u77e5\u4e8b \u6709\u51fa\u4e4e\u610f\u6599\uff0c\u5f8c\u6765\u7adf\u7136\u56e0\u4e3a\u6b64\u7c92\u7ea2\u4e38\uff0c\u5f15\u51fa\u660e\u53f2\u4e0a\u7684\u7b2c\u4e8c\u4e2a\u5927\u5947\u6848\uff0c\uff0c\u201c\u7ea2\u4e38\u6848\u201d\uff0c\u767d\u767d\u9001 \u4e86\u4e00\u4f4d\u7687\u5e1d\u7684\u6027\u547d\uff0c\u8fd9\u662f\u5f8c\u8bdd\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u518d\u8bf4\u767d\u77f3\u9053\u4eba\u4e0e\u955c\u660e\u957f\u8001\u76f8\u89c1\u4e4b\u5f8c\uff0c\u62db\u624b\u53eb\u5353\u4e00\u822a\u8fc7\u6765\u53c2\u8c12\uff0c\u955c\u660e\u957f\u8001\u89c1\u5353\u4e00\u822a\u6c14\u5b87\u4e0d \u51e1\uff0c\u751a\u4e3a\u79f0\u8d5e\u3002 \u3000\u3000\u5f53\u665a\u955c\u660e\u957f\u8001\u5728\u201c\u89e3\u884c\u7cbe\u820d\u201d\u8bbe\u4e0b\u658b\u5bb4\uff0c\u7ed9\u767d\u77f3\u9053\u4eba\u63a5\u98ce\uff0c\u5e2d\u95f2\u8c08\u8d77\u7d2b\u9633\u9053\u957f\u901d\u4e16\u4e4b \u4e8b\uff0c\u5401\u55df\u518d\u56db\u3002\u5353\u4e00\u822a\u4e5f\u6697\u6697\u611f\u6168\uff0c\u5fc3\u60f3\uff1a\u81ea\u5df2\u7684\u5e08\u7236\u6b7b\u5f8c\uff0c\u6b66\u5f53\u6d3e\u5df2\u662f\u7fa4\u9f99\u65e0\u9996\uff0c\u56db\u4e2a\u5e08 \u53d4\uff0c\u867d\u7136\u6b66\u529f\u4e0d\u9519\uff0c\u5374\u90fd\u4e0d\u662f\u9886\u8896\u4e4b\u624d\uff0c\u770b\u6765\u6b66\u6797\u5b97\u4e3b\u4e4b\u4f4d\uff0c\u8be5\u8ba9\u5c11\u6797\u6d3e\u4e86\u3002 \u3000\u3000\u665a\u971e\u6e10\u6536\uff0c\u5c71\u95f4\u660e\u6708\u5347\u8d77\uff0c\u53c1\u5341\u516d\u6bbf\u4e0e\u4e94\u5341\u56db\u5854\u90fd\u6d78\u5728\u6eb6\u6eb6\u6708\u8272\u4e4b\u4e2d\uff0c\u955c\u660e\u957f\u8001\u555c\u4e86\u4e00 \u53e3\u6e05\u837c\uff0c\u4ef0\u89c2\u6708\u8272\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u6837\u7684\u591c\u8272\uff0c\u591c\u884c\u4eba\u65b9\u4e0d\u65b9\u4fbf\uff01\u201d\u767d\u77f3\u9053\u4eba\u8be7\u9053\uff1a \u201c\u8001  \u5e08\u8bf4\u8fd9\u8bdd\u662f\u4ec0\u9ebd\u610f\u601d\uff0c\u96be\u9053\u6709\u4ec0\u9ebd\u591c\u884c\u4eba\u6562\u5230\u5c11\u6797\u5bfa\u6765\u9ebd\u4e86\u90a3\u4e24\u4e2a\u8001\u65e0\u8d56\u5c31\u662f\u60f3\u4e0e\u5c11 \u6797\u7ea0\u7f20\uff0c\u4e5f\u6ca1\u6709\u8fd9\u6837\u5927\u7684\u672c\u4e8b\u3002\u201d\u955c\u660e\u957f\u8001\u7b11\u9053\uff1a\u201c\u4eca\u591c\u6765\u7684\u53ef\u4e0d\u662f\u4ec0\u9ebd\u65e0\u8d56\u4e86\uff0c\u4ed6\u662f\u718a\u7ecf \u7565\u6d3e\u6765\u7684\u4eba\u3002\u800c\u4e14\u662f\u6211\u7279\u522b\u9080\u8bf7\u4ed6\u6765\u7684\u3002\u201d \u3000\u3000\u767d\u77f3\u9053\u4eba\u76ca\u53d1\u83ab\u540d\u5176\u5999\u3002\u95ee\u9053\uff1a\u201c\u90a3\u4e2a\u718a\u7ecf\u7565\uff1f\u662f\u4e0d\u662f\u8fbd\u4e1c\u7ecf\u7565\u4f7f\u718a\u5ef7\u5f3c\u5927\u5c06\u519b\uff1f\u201d\u955c \u660e\u9053\uff1a\u201c\u5929\u4e0b\u90a3\u8fd8\u6709\u4e24\u4f4d\u718a\u7ecf\u7565\uff01\u201d\u767d\u77f3\u8be7\u9053\uff1a\u201c\u718a\u7ecf\u7565\u662f\u5f53\u4e16\u540d\u5c06\uff0c\u9053\u5fb7\u5175\u6cd5\uff0c\u4e3e\u4e16\u63a8 \u91cd\uff0c\u96be\u9053\u4ed6\u4f1a\u4e0e\u5c11\u6797\u4e3a\u96be\uff1f\u201d\u955c\u660e\u7b11\u9053\uff1a\u201c\u90a3\u5f53\u7136\u4e0d\u4f1a\uff01\u201d\u6b47\u4e86\u4e00\u6b47\uff0c\u5ffd\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u4eba\u53eb \u5cb3\u9e23\u73c2\u7684\uff0c\u4f60\u4eec\u53ef\u542c\u8fc7\u4ed6\u7684\u540d\u5b57\u9ebd\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4eba\u6211\u77e5\u9053\u3002\u201d\u955c\u660e\u9053\uff1a\u201c\u4eca\u591c\u5c31\u662f\u4ed6\u6765\u3002\u201d\u5353\u4e00\u822a\u9a87\u7136\u95ee \u9053\uff1a\u201c\u4ed6\u4e3a\u4ec0\u9ebd\u4f1a\u6765\uff01\u201d\u955c\u660e\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u718a\u7ecf\u7565\u5dee\u9063\u6765\u7684\u3002\u201d \u3000\u3000\u539f\u6765\u718a\u5ef7\u5f3c\u5949\u65e8\u6302\u4e86\u8fbd\u4e1c\u7ecf\u7565\u4f7f\u7684\u5e05\u5370\u4e4b\u5f8c\uff0c\u660e\u671d\u7687\u5e1d\u53c8\u8d50\u4ed6\u5c1a\u65b9\u5b9d\u5251\uff0c\u51c6\u4ed6\u5148\u65a9\u5f8c \u594f\u3002\u8981\u77e5\u660e\u671d\u8fb9\u9632\u4e4b\u574f\uff0c\u90a3\u5c6f\u8fb9\u7684\u5c06\u519b\u4e4b\u8150\u8d25\uff0c\u4e5f\u662f\u4e00\u5927\u56e0\u7d20\u3002\u718a\u5ef7\u5f3c\u5f97\u4e86\u5c1a\u65b9\u5b9d\u5251\u4e4b\u5f8c\uff0c \u51b3\u610f\u6574\u987f\u519b\u52a1\uff0c\u7387\u4e86\u4eb2\u5175\uff0c\u663c\u591c\u517c\u7a0b\uff0c\u8d76\u51fa\u5173\u5916\u3002\u4e00\u5230\u8fbd\u9633\uff0c\u5c31\u628a\u53c1\u4e2a\u8d2a\u6c61\u6789\u6cd5\u3001\u7eb5\u5175\u6270\u6c11 \u7684\u5c06\u519b\u5218\u9047\u8282\uff0c\u738b\u6377\u3001\u738b\u6587\u9f0e\u6740\u4e86\uff0c\u65ab\u4e0b\u8111\u888b\uff0c\u9001\u5230\u5404\u8425\u793a\u4f17\uff0c\u519b\u58eb\u4eec\u770b\u4e86\uff0c\u4e2a\u4e2a\u5bb3\u6015\uff0c\u4eba \u4eba\u542c\u4ee4\u3002\u718a\u5ef7\u5f3c\u65bc\u662f\u5927\u52a0\u6574\u987f\uff0c\u4e00\u9762\u6559\u7ec3\u5175\u58eb\uff0c\u4e00\u9762\u7763\u9020\u6218\u8f66\u706b\u70ae\uff0c\u6398\u58d5\u4fee\u57ce\uff0c\u628a\u5341\u516b\u4e07\u539f \u6765\u8150\u8d25\u4e0d\u582a\u7684\u8fb9\u9632\u519b\u961f\uff0c\u7adf\u7136\u8bad\u7ec3\u6210\u4e86\u96c4\u8d73\u8d73\u7684\u7cbe\u5175\uff0c\u8fdb\u5b88\u629a\u987a\u548c\u6ee1\u6d32\u5175\u5bf9\u5792\uff0c\u90a3\u6ee1\u6d32\u7684\u7687 \u5e1d\u542c\u8bf4\u662f\u718a\u5ef7\u5f3c\u7763\u5e08\uff0c\u4e0d\u6562\u8fdb\u5175\uff0c\u9000\u5b88\u5174\u4eac\u3002\u4e24\u519b\u5bf9\u5cd9\uff0c\u5012\u4e5f\u65e0\u4e8b\u3002\u8fd9\u65f6\u5cb3\u545c\u73c2\u5728\u519b\u4e2d\u6302\u4e0a \u53c2\u8d5e\u7684\u5dee\u4e8b\uff0c\u804c\u4f4d\u867d\u7136\u4e0d\u9ad8\uff0c\u5374\u662f\u718a\u5ef7\u5f3c\u7684\u4e00\u6761\u81c2\u818a\u3002 \u3000\u3000\u4e1c\u5317\u51fa\u4ea7\u6709\u4e0a\u597d\u7684\u767d\u91d1\u548c\u7cbe\u94c1\uff0c\u718a\u5ef7\u5f3c\u7a81\u7136\u60f3\u8d77\u8981\u94f8\u4e00\u628a\u5b9d\u5251\uff0c\u53eb\u5cb3\u545c\u73c2\u8d1f\u8d23\u94f8\u9020\u3002\u8fd9 \u65f6\u4eac\u4e2d\u6070\u53c8\u4f20\u51fa\u6d88\u606f\uff0c\u8bf4\u662f\u9996\u8f85\u65b9\u4ece\u54f2\u548c\u5175\u90e8\u4e3b\u4e8b\u5218\u56fd\u7f19\u7b49\u4eba\uff0c\u5a18\u5fcc\u718a\u5ef7\u5f3c\u5f97\u7687\u5e1d\u4fe1\u4efb\uff0c\u4e13 \u638c\u5175\u6743\uff0c\u51c6\u5907\u5bf9\u4ed6\u4e0d\u5229\uff0c\u8981\u793a\u610f\u5378\u53f2\u5f39\u52be\u4ed6\u3002\u56e0\u6b64\u5cb3\u545c\u73c2\u8bf7\u4ee4\u56de\u6765\uff0c\u4e00\u9762\u5230\u4eac\u4e2d\u6253\u63a2\u6d88\u606f\uff0c \u5e76\u66ff\u718a\u5ef7\u5f3c\u758f\u901a\uff0c\u4e00\u9762\u7269\u8272\u5251\u5e08\u5230\u5173\u5916\u66ff\u718a\u5ef7\u5f3c\u94f8\u5251\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u5148\u5230\u5317\u4eac\uff0c\u6253\u542c\u5f97\u9634\u8c0b\u867d\u7136\u6b63\u5728\u915d\u917f\uff0c\u4f46\u6709\u4e00\u73ed\u6b63\u76f4\u7684\u5927\u81e3\uff0c\u5982\u6768\u6d9f\uff0e\u5218\u4e00\u61ac\u7b49 \u90fd\u529b\u4fdd\u718a\u5ef7\u5f3c\uff0c\u6682\u65f6\u53ef\u4ee5\u65e0\u4e8b\u3002\u65bc\u662f\u53c8\u60f3\u8d77\u94f8\u5251\u4e4b\u4e8b\uff0c\u4f46\u7740\u540d\u7684\u5251\u5e08\uff0c\u4e0d\u662f\u6b7b\u4e86\uff0c\u4fbf\u662f\u5e74\u8001 \u5230\u4e0d\u613f\u8d70\u52a8\u4e86\u3002\u5cb3\u545c\u73c2\u867d\u662f\u5251\u6cd5\u7684\u5927\u884c\u5bb6\uff0c\u5374\u4e0d\u4f1a\u94f8\u5251\u3002\u60f3\u4e86\u53c8\u60f3\uff0c\u5ffd\u7136\u60f3\u8d77\u6b66\u6797\u5404\u6d3e\u4e4b \u4e2d\uff0c\u53ea\u6709\u5c11\u6797\u6d3e\u6709\u4e00\u672c\u4e13\u7814\u94f8\u5251\u7684\u4e66\uff0c\u540d\u4e3a\u201c\u9f99\u6cc9\u767e\u70bc\u8bc0\u201d\uff0c\u5cb3\u545c\u73c2\u60f3\uff0c\u4e0d\u5982\u8bf7\u5c11\u6797\u5bfa\u7684\u4e3b \u6301\u51c6\u4ed6\u6284\u4e00\u672c\u526f\u672c\u51fa\u6765\uff0c\u90a3\u5c31\u4e0d\u4f46\u53ef\u4ee5\u4e3a\u718a\u7ecf\u7565\u94f8\u5251\uff0c\u800c\u4e14\u53ef\u4ee5\u5229\u7528\u4e1c\u5317\u7684\u7cbe\u94c1\uff0c\u7ed9\u5175\u58eb\u4eec \u94f8\u9020\u8bb8\u591a\u5200\u5251\u4e86\uff0c\u56e0\u6b64\u4ed6\u8d81\u7740\u8fb9\u9632\u65e0\u4e8b\uff0c\u4e0a\u5c11\u5ba4\u5c71\u8c12\u5c11\u6797\u5bfa\uff0c\u9053\u8fbe\u6765\u610f\u3002 \u3000\u3000\u518d\u8bf4\u955c\u660e\u957f\u8001\u5c06\u5cb3\u9e23\u73c2\u7684\u6765\u610f\u5bf9\u767d\u77f3\u9053\u4eba\u8bf4\u5f8c\uff0c\u8bf4\u9053\uff1a\u201c\u672c\u6765\u8fd9\u662f\u4e00\u4ef6\u597d\u4e8b\uff0c\u4f55\u51b5\u53c8\u662f \u718a\u7ecf\u7565\u7684\u9762\u5b50\u3002\u4f46\u5c11\u6797\u5bb6\u6cd5\uff0c\u5178\u7c4d\u4e0d\u8bb8\u5916\u4f20\uff0c\u6211\u601d\u7ef4\u518d\u53c1\uff0c\u53ea\u597d\u53eb\u4ed6\u6765\u5077\u3002\u201d\u8bf4\u7f62\u54c8\u54c8\u5927 \u7b11\u3002 \u3000\u3000\u5c0a\u80dc  \u5e08\u5ffd\u7136\u95ee\u5353\u4e00\u822a\u9053\uff1a\u201c\u8fd9\u5cb3\u545c\u73c2\u6b66\u827a\u5982\u4f55\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6bd4\u5f1f\u5b50\u4f55\u6b62\u9ad8\u660e\u5341 \u500d\uff01\u201d\u767d\u77f3\u9053\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u9762\u8272\u4e0d\u60a6\u3002\u5c0a\u80dc  \u5e08\u7b11\u9053\uff1a\u201c\u8001\u5f1f\u592a\u8fc7\u8c26\u4e86\u3002\u6211\u6253\u63a2\u4ed6\u7684\u6b66\u529f\u9020 \u8be3\uff0c\u53e6\u6709\u539f\u56e0\u3002\u6211\u548c\u4e3b\u6301\u5e08\u5144\u867d\u7136\u613f\u4ed6\u987a\u5229\u5f97\u624b\uff0c\u4f46\u96be\u4fdd\u5176\u4ed6\u50e7\u4f17\u4e0d\u4e0e\u4ed6\u4e3a\u96be\u3002\u56e0\u6b64\uff0c\u82e5\u7136 \u4ed6\u662f\u6b66\u827a\u4f4e\u5fae\u7684\u8bdd\uff0c\u6211\u4eec\u5c31\u4e0d\u6d3e\u9ad8\u624b\u628a\u5173\u4e86\u3002\u201d\u767d\u77f3\u9053\u4eba\u5ffd\u9053\uff1a\u201c\u4ee5\u5c11\u6797\u5bfa\u7684\u76db\u540d\uff0c\u5c31\u662f\u6709 \u610f\u8ba9\u4ed6\uff0c\u4e5f\u8be5\u53eb\u4ed6\u4e0d\u8981\u592a\u6613\u5f97\u624b\u3002\u201d\u5c0a\u80dc\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u9053\u5144\u6709\u6b64\u96c5\u5174\uff0c\u4e0d\u59a8\u770b\u770b\u3002\u201d \u3000\u3000\u518d\u8bf4\u5cb3\u545c\u73c2\u5f97\u4e86\u955c\u660e  \u5e08\u6697\u793a\uff0c\u5341\u5206\u6b22\u559c\u3002\u8fd9\u665a\u6362\u4e86\u9752\u8272\u7684\u591c\u884c\u8863\u670d\uff0c\u5230\u5c11\u6797\u5bfa\u6765\uff0c\u5728 \u5bfa\u95e8\u5916\u606d\u606d\u656c\u656c\u62dc\u4e86\u53c1\u62dc\uff0c\u98de\u8eab\u4eba\u5185\u3002\u6b63\u5728\u6b64\u65f6\uff0c\u5ffd\u7136\u4e00\u80a1\u5fae\u98ce\u63a0\u8fc7\u8eab\u7554\uff0c\u4f3c\u6709\u4e00\u6761\u9ed1\u5f71\uff0c \u75be\u82e5\u6d41\u661f\uff0c\u5411\u4e1c\u5317\u89d2\u98de\u53bb\u3002\u8fd9\u4eba\u7684\u8f7b\u529f\u9020\u8be3\u5df2\u662f\u4e0a\u4e0a\u529f\u592b\uff0c\u7b49\u95f2\u7684\u4eba\uff0c\u6839\u672c\u4e0d\u80fd\u53d1\u73b0\u3002\u5cb3\u545c \u73c2\u5fae\u5403\u4e00\u60ca\uff0c\u5fc3\u60f3\u96be\u9053\u955c\u660e\u957f\u8001\u6539\u4e86\u4e3b\u610f\uff0c\u6d3e\u9ad8\u624b\u6697\u4e2d\u9489\u7740\u6211\u4e86\uff1f \u3000\u3000\u6b63\u5728\u601d\u91cf\uff0c\u7f57\u6c49\u5802\u5185\u500f\u7684\u8df3\u51fa\u4e00\u4e2a\u6c99\u5f25\uff0c\u53ea\u6709\u5341\u4e94\u516d\u5c81\u5149\u666f\uff0c\u8eab\u6cd5\u5374\u6781\u4e3a\u654f\u6377\uff0c\u4e00\u7167\u9762 \u5c31\u662f\u4e00\u62db\u201c\u9634\u9633\u53cc\u649e\u638c\u201d\u8fce\u9762\u626b\u6765\uff0c\u559d\u9053\uff1a\u201c\u5927\u80c6\u72c2\u5f92\uff0c\u6562\u6765\u95ef\u5bfa\uff01\u201d\u5cb3\u9e23\u73c2\u5df2\u5f97\u955c\u660e\u6307 \u793a\uff0c\u77e5\u4ed6\u6545\u610f\u88c5\u6a21\u4f5c\u6837\uff0c\u5047\u620f\u771f\u505a\uff0c\u6697\u6697\u597d\u7b11\u3002\u95ea\u5f97\u51e0\u95ea\uff0c\u6b63\u81ea\u6253\u4e0d\u5b9a\u4e3b\u610f\u5982\u4f55\u95ef\u5173\uff0c\u4ee4\u4ed6 \u597d\u597d\u4e0b\u53f0\u3002\u4e0d\u6599\u8fd9\u5c0f\u6c99\u5f25\u5374\u4f3c\u5341\u5206\u597d\u80dc\uff0c\u7adf\u7136\u65bd\u5c55\u51fa\u5c11\u6797\u201c\u7ef5\u638c\u201d\u7684\u529f\u592b\uff0c\u5ffd\u638c\u5ffd\u6307\uff0c\u4f3c\u70b9 \u4f3c\u6233\u3002\u5353\u4e00\u822a\u548c\u5e08\u53d4\u7531\u8fbe\u6469\u9662\u7684\u4e00\u4e2a\u9ad8\u50e7\u966a\u7740\uff0c\u5728\u77f3\u5854\u4e0a\u89c2\u770b\uff0c\u89c1\u8fd9\u5c0f\u6c99\u5f25\u6b63\u662f\u65e5\u95f4\u5411\u80e1\u9065 \u53eb\u9635\u7684\u90a3\u4e00\u4e2a\uff0c\u4e0d\u89c9\u597d\u7b11\u3002\u5353\u4e00\u822a\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f  \u5e08\u8eab\u6cd5\u597d\u7075\uff0c\u8981\u662f\u65e5\u95f4\u7531\u4ed6\u51fa\u624b\uff0c\u53ea\u6015\u90a3 \u8001\u65e0\u8d56\u4f24\u5f97\u66f4\u91cd\u3002\u201d \u3000\u3000\u5cb3\u545c\u73c2\u968f\u7740\u90a3\u5c0f\u6c99\u5f25\u8f6c\u4e86\u51e0\u8f6c\uff0c\u5ffd\u7136\u5356\u4e2a\u7834\u7efd\uff0c\u5c0f\u6c99\u5f25\u6536\u638c\u4e0d\u53ca\uff0c\u556a\u7684\u4e00\u638c\u6309\u5230\u4ed6\u5de6\u4e73 \u4e0b\u7684\u671f\u95e8\u7a74\u4e0a\uff0c\u5cb3\u545c\u73c2\u8eab\u5b50\u500f\u7684\u98d8\u8d77\uff0c\u98de\u4e0a\u5899\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5e08\u7236\u638c\u98ce\u5389\u5bb3\uff0c\u6211\u7518\u62dc\u4e0b \u98ce\uff01\u201d\u90a3\u5c0f\u6c99\u5f25\u638c\u65b9\u6cbe\u8863\uff0c\u9661\u89c9\u654c\u4eba\u808c\u8089\u5185\u9677\uff0c\u6839\u672c\u6ca1\u6709\u6309\u5b9e\uff0c\u60f3\u4e0d\u5230\u4ed6\u5df2\u7ed9\u201c\u6309\u201d\u5f97\u8fde\u8eab \u98de\u8d77\uff0c\u4e0d\u89c9\u6123\u5728\u5f53\u573a\u3002 \u3000\u3000\u5c0f\u6c99\u5f25\u8fd8\u9053\u662f\u81ea\u5df1\u7684\u7ef5\u638c\u529f\u529b\u5389\u5bb3\uff0c\u624b\u638c\u8fd8\u6ca1\u6709\u6309\u5b9e\uff0c\u654c\u4eba\u5c31\u5df2\u7ad9\u7acb\u4e0d\u4f4f\uff0c\u8981\u98d8\u8eab\u8eb2\u95ea \u4e86\u3002\u6b63\u60f3\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u7136\u7518\u62dc\u4e0b\u98ce\uff0c\u4e3a\u4f55\u8fd8\u5411\u5185\u95ef\uff1f\u518d\u4e0b\u6765\u6597\u51e0\u4e2a\u56de\u5408\u5427\uff01\u201d\u6b63\u5728\u4ed6\u53d1\u695e\u7684 \u5f53\u513f\uff0c\u5ffd\u95fb\u5f97\u534a\u7a7a\u4e2d\u6709\u58f0\u98d8\u4e0b\uff0c\u539f\u6765\u662f\u5c0a\u80dc  \u5e08\u5728\u201c\u521d\u7956\u5eb5\u201d\u7684\u9ad8\u5904\u559d\u9053\uff1a\u201c\u8822\u624d\uff0c\u522b\u4eba\u8ba9 \u4e86\u4f60\u8fd8\u4e0d\u591a\u8c22\uff1f\u4f60\u7684\u7ef5\u638c\u529f\u592b\u8fd8\u5dee\u5f97\u8fdc\u5462\uff1f\u201d \u3000\u3000\u5c0f\u6c99\u5f25\u9762\u7ea2\u8033\u70ed\uff0c\u62b1\u638c\u8bf4\u9053\uff1a\u201c\u8c22\u8d35\u5ba2\u624b\u4e0b\u7559\u60c5\u3002\u201d\u5cb3\u9e23\u73c2\u4e5f\u89c9\u9a87\u7136\uff0c\u5fc3\u60f3\u8fd9\u5c0a\u80dc  \u5e08 \u4eba\u5728\u8fdc\u5904\uff0c\u5374\u770b\u5f97\u5982\u6b64\u6e05\u695a\uff0c\u5c11\u6797\u5bfa\u679c\u7136\u540d\u4e0d\u865a\u4f20\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u8df3\u8fc7\u4e86\u7f57\u6c49\u5802\uff0c\u8fdb\u5165\u201c\u89e3\u884c\u7cbe\u820d\u201d\uff0c\u5c31\u662f\u9002\u624d\u955c\u660e\u957f\u8001\u6b3e\u5f85\u767d\u77f3\u9053\u4eba\u7684\u5730\u65b9\u3002\u5cb3 \u9e23\u73c2\u521a\u521a\u8dc3\u5165\uff0c\u5ffd\u95fb\u5f97\u547c\u547c\u58f0\u54cd\uff0c\u8fce\u9762\u98de\u6765\uff0c\u5cb3\u9e23\u73c2\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u4e00\u98d8\u8eab\u6500\u4e0a\u5927\u6881\uff0c\u53ea\u542c \u5f97\u4e00\u4e2a\u548c\u5c1a\u7b11\u9053\uff1a\u201c\u5ba2\u4eba\u52ff\u60ca\uff0c\u8bf7\u4e0b\u6765\u6bd4\u8bd5\u6697\u5668\u3002\u201d\u5cb3\u9e23\u73c2\u773c\u89c1\u90a3\u957f\u65b9\u5f62\u7684\u4e1c\u897f\uff0c\u53c8\u56de\u5230\u548c \u5c1a\u624b\u4e2d\uff0c\u4e5f\u9887\u4e3a\u60ca\u5f02\u3002 \u3000\u3000\u8fd9\u548c\u5c1a\u4e43\u76d1\u5bfa\u5c0a\u80dc  \u5e08\u7684\u5f1f\u5b50\uff0c\u540d\u53eb\u7384\u901a\uff0c\u521a\u624d\u4f7f\u8fd9\u72ec\u95e8\u6697\u5668\uff0c\u672c\u662f\u60f3\u5413\u5413\u6765\u4eba\uff0c\u90a3\u6599 \u5cb3\u9e23\u73c2\u8f7b\u529f\u4e4b\u9ad8\uff0c\u51fa\u4e4e\u4ed6\u610f\u60f3\u4e4b\u5916\uff0c\u4ed6\u672c\u60f3\u7528\u201c\u9e33\u9e2f\u6795\u201d\u5939\u7740\u654c\u4eba\u53cc\u8033\u98de\u8fc7\uff0c\u90a3\u77e5\u521a\u5230\u654c\u4eba \u8eab\u524d\uff0c\u4ed6\u7684\u8eab\u5f71\u5c31\u4e0d\u89c1\u4e86\u3002\u6536\u56de\u6697\u5668\uff0c\u624d\u770b\u51fa\u4ed6\u5df2\u8eb2\u5230\u6881\u4e0a\u3002\u8fd9\u4e00\u6765\u5374\u6fc0\u8d77\u7384\u901a\u597d\u80dc\u4e4b\u5ff5\uff0c \u771f\u7684\u8981\u548c\u4ed6\u8f83\u91cf\u6697\u5668\u4e86\uff01 \u3000\u3000\u5cb3\u545c\u73c2\u4e00\u7b11\u98d8\u8eab\uff0c\u8dc3\u4e86\u4e0b\u6765\uff0c\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u8bf7\u5927\u5e08\u624b\u4e0b\u7559\u60c5\uff01\u201d\u7384\u901a\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d \u8bf4\uff0c\u4f60\u7528\u4ec0\u9ebd\u6697\u5668\uff1f\u201d\u5cb3\u9e23\u73c2\u4ece\u6765\u4e0d\u7528\u6697\u5668\uff0c\u60f3\u4e86\u4e00\u60f3\uff0c\u4e3e\u5934\u5916\u671b\uff0c\u5ffd\u89c1\u7cbe\u820d\u5916\u4e00\u68f5\u9f99\u773c\u6811 \u7ed3\u5b9e  \uff0c\u7b11\u9053\uff1a\u201c\u6211\u53e3\u6e34\u5f97\u7d27\uff0c\u8ba9\u6211\u5148\u6458\u51e0\u9897\u9f99\u773c\u89e3\u6e34\u5982\u4f55\uff1f\u201d\u7384\u901a\u4e00\u695e\uff0c\u9053\uff1a\u201c\u8bf7 \u4fbf\u3002\u201d\u5cb3\u545c\u73c2\u4e00\u53e3\u6c14\u5403\u4e86\u4e8c\u53c1\u5341\u7c92\uff0c\u5c06\u9f99\u773c\u6838\u96c6\u5728\u624b\u4e2d\uff0c\u7b11\u9053\uff1a\u201c\u597d\u4e86\uff0c\u6211\u6697\u5668\u5df2\u7ecf\u6709\u4e86\uff0c \u8bf7\u5927\u5e08\u6307\u6559\uff01\u201d \u3000\u3000\u7384\u901a\u89c1\u4ed6\u7adf\u4ee5\u9f99\u773c\u6838\u4f5c\u4e3a\u6697\u5668\uff0c\u4e0d\u89c9\u6120\u6012\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u5148\u6253\u51fa\u4e94\u7c92\u94c1\u83e9\u63d0\uff0c\u4f46\u542c\u5f97\u94ee\u94ee \u4e71\u54cd\uff0c\u5cb3\u9e23\u73c2\u624b\u6307\u8fde\u5f39\uff0c\u4e00\u7c92\u7c92\u7684\u9f99\u773c\u6838\u8fde\u73e0\u98de\u53bb\u3002\u628a\u7384\u901a\u7684\u94c1\u83e9\u63d0\u5168\u90e8\u6253\u843d\u3002 \u3000\u3000\u7384\u901a\u5927\u5403\u4e00\u60ca\uff0c\u53cc\u624b\u4e00\u626c\uff0c\u72ec\u95e8\u6697\u5668\u201c\u9e33\u9e2f\u6795\u201d\u4e24\u8def\u6253\u51fa\uff0c\u8fd9\u6697\u5668\u72b6\u4f3c\u6795\u5934\uff0c\u4e2d\u85cf\u5229 \u5200\uff0c\u80fd\u653e\u80fd\u6536\uff0c\u7aef\u7684\u5389\u5bb3\u3002\u5cb3\u9e23\u73c2\u53cc\u6307\u8fde\u5f39\uff0c\u63a5\u8fde\u6253\u51fa\u56db\u679a\u9f99\u773c\u6838\uff0c\u90a3\u4e24\u4e2a\u94c1\u9e33\u9e2f\u6795\u7ed9\u5c0f\u5c0f \u7684\u9f99\u773c\u6838\u4e00\u649e\uff0c\u7adf\u7136\u6b6a\u6b6a\u659c\u659c\u5931\u4e86\u51c6\u5934\uff0c\u7384\u901a\u628a\u624b\u4e00\u62db\uff0c\u6536\u4e86\u56de\u6765\u3002\u5cb3\u545c\u73c2\u773c\u5229\uff0c\u770b\u51fa\u201c\u9e33 \u9e2f\u6795\u201d\u4e0a\u7cfb\u6709\u4e00\u6761\u6781\u7ec6\u7684\u94c1\u4e1d\uff0c\u53e6\u4e00\u7aef\u7f20\u5728\u7384\u901a\u6307\u4e0a\uff0c\u5f85\u4ed6\u518d\u53d1\u51fa\u65f6\uff0c\u7a81\u7136\u98d8\u8eab\u800c\u8d77\uff0c\u53cc\u6307 \u5728\u94c1\u7ebf\u4e0a\u4e00\u526a\uff0c\u628a\u94c1\u7ebf\u526a\u65ad\uff0c\u9e33\u9e2f\u6795\u9aa4\u7136\u659c\u98de\u51fa\u53bb\uff0c\u5185\u4e2d\u7684\u98de\u5200\u6fc0\u5c04\u51fa\u6765\uff0c\u7adf\u7136\u5c04\u51fa\u201c\u89e3\u884c \u7cbe\u820d\u201d\uff0c\u9489\u5728\u9f99\u773c\u6811\u4e0a\u3002\u5cb3\u9e23\u73c2\u8bf4\u58f0\uff1a\u201c\u627f\u8ba9\uff01\u201d\u95ef\u8fc7\u4e86\u7b2c\u4e8c\u5173\uff0c\u76f4\u5411\u85cf\u7ecf\u9601\u884c\u53bb\u3002 \u3000\u3000\u884c\u5f97\u51e0\u6b65\uff0c\u8fbe\u6469\u9662\u4e2d\u53c8\u8df3\u51fa\u4e00\u540d\u548c\u5c1a\uff0c\u624b\u63d0\u4e00\u67c4\u65b9\u4fbf\u94f2\uff0c\u5bd2\u5149\u95ea\u95ea\uff0c\u62e6\u5728\u9762\u524d\uff0c\u8bf4\u9053\uff1a \u201c\u65bd\u4e3b\u7559\u6b65\uff01\u201d \u3000\u3000\u5cb3\u9e23\u73c2\u77e5\u9053\u5c11\u6797\u5bfa\u5bf9\u6b66\u529f\u7684\u8003\u6838\u6700\u4e25\uff0c\u5bfa\u4e2d\u50e7\u4f17\u6216\u4ee5\u62f3\u6280\u89c1\u957f\uff0c\u6216\u4ee5\u6697\u5668\u89c1\u80dc\uff0c\u6216\u4ee5\u5175 \u5203\u79f0\u96c4\uff1a\u800c\u5bf9\u62f3\u6280\u3001\u6697\u5668\uff0c\u5175\u5203\u5168\u90fd\u6709\u4e86\u9020\u8be3\u4e4b\u5f8c\uff0c\u518d\u7cbe\u7814\u5185\u529f\uff0c\u5230\u5185\u529f\u4e5f\u6709\u4e86\u6df1\u6e5b\u7684\u9020\u8be3 \u4e4b\u5f8c\uff0c\u65b9\u624d\u9001\u5165\u8fbe\u6469\u9662\u3002\u6240\u4ee5\u5c11\u6797\u5bfa\u8fbe\u6469\u9662\u4e2d\u7684\u9ad8\u50e7\uff0c\u65e0\u4e00\u4e0d\u662f\u5185\u5916\u517c\u4fee\uff0c\u8eab\u6000\u7edd\u6280\u7684\u597d \u624b\u3002\u8fd9\u4e2a\u548c\u5c1a\u4ece\u8fbe\u6469\u9662\u4e2d\u8df3\u51fa\uff0c\u5fc5\u7136\u662f\u5c11\u6797\u5bfa\u4e2d\u6709\u6570\u7684\u4eba\u7269\u4e86\uff0c\u5f53\u4e0b\u62b1\u62f3\u8bf7\u95ee\uff0c\u8fd9\u548c\u5c1a\u540d\u53eb \u5929\u5143\uff0c\u4e43\u662f\u955c\u660e  \u5e08\u7684\u5934\u5f92\uff0c\u6a2a\u94f2\u628a\u5173\uff0c\u7a3d\u9996\u7b11\u9053\uff1a\u201c\u5cb3\u65bd\u4e3b\u8bf7\u4eae\u5175\u5203\u3002\u201d \u3000\u3000\u5cb3\u545c\u73c2\u9053\u58f0\u201c\u5f97\u7f6a\u201d\uff0c\u62d4\u5251\u5728\u624b\uff0c\u53ea\u89c1\u4e00\u6cd3\u79cb\u6c34\uff0c\u6ee1\u5ba4\u751f\u8f89\uff0c\u539f\u6765\u5cb3\u545c\u73c2\u7684\u5e08\u7236\u5929\u90fd\u5c45 \u58eb\u5728\u5929\u5c71\u4e0a\u63a2\u53d6\u4e94\u91d1\u4e4b\u7cbe\uff0c\u6258\u524d\u8f88\u70bc\u5251\u5e08\u6b27\u9633\u6cbb\u5b50\u70bc\u4e86\u4e24\u628a\u5b9d\u5251\uff0c\u4e00\u957f\u4e00\u77ed\uff0c\u957f\u7684\u540d\u4e3a\u201c\u6e38 \u9f99\u201d\uff0c\u77ed\u7684\u540d\u4e3a\u201c\u65ad\u7389\u201d\uff0c\u5cb3\u545c\u73c2\u8fd9\u628a\uff0c\u6b63\u662f\u5929\u5c71\u6d3e\u9547\u5c71\u4e4b\u5b9d\u5251\u6e38\u9f99\u5251\u3002 \u3000\u3000\u5929\u5143\u548c\u5c1a\u89c1\u4ed6\u4eae\u51fa\u5b9d\u5251\uff0c\u5fae\u5fae\u4e00\u51db\uff0c\u4f46\u60f3\u8d77\u65b9\u4fbf\u94f2\u4e43\u662f\u91cd\u5175\u5668\uff0c\u5b9d\u5251\u96be\u524a\uff0c\u4ea6\u81ea\u4e0d\u60e7\u3002 \u5cb3\u9e23\u73c2\u65bd\u793c\u4e4b\u5f8c\uff0c\u5e73\u5251\u5f53\u80f8\uff0c\u5929\u5143\u548c\u5c1a\u4e00\u94f2\u62cd\u4e0b\uff0c\u5cb3\u545c\u73c2\u4e24\u80a9\u4e00\u6446\uff0c\u8eab\u8eaf\u534a\u8f6c\uff0c\u53cd\u624b\u4e00\u5251\uff0c \u6025\u5982\u7535\u5149\u77f3\u706b\uff0c\u76f4\u523a\u5929\u5143\u624b\u8155\uff0c\u5929\u5143\u548c\u5c1a\u559d\u58f0\u201c\u597d\u5feb\uff01\u201d\u624b\u8155\u4e00\u7ffb\uff0c\u65b9\u4fbf\u94f2\u53cd\u94f2\u4e0a\u6765\uff0c\u5cb3\u9e23 \u73c2\u628a\u5251\u4e00\u6536\uff0c\u8f6c\u950b\u523a\u51fa\uff0c\u5929\u5143\u548c\u5c1a\u7684\u94f2\u5411\u524d\u4e00\u9001\uff0c\u53ea\u542c\u5f97\u201c\u53ee\u5f53\u201d\u4e00\u58f0\uff0c\u706b\u82b1\u56db\u6e85\uff0c\u65b9\u4fbf\u94f2 \u7f3a\u4e86\u4e00\u53e3\uff0c\u5cb3\u545c\u73c2\u4e5f\u89c9\u81c2\u818a \u3000\u3000  \uff0c\u4e0d\u6562\u6020\u6162\uff0c\u5c31\u5728\u817e\u632a\u95ea\u5c55\u4e4b\u65f6\uff0c\u624b\u4e2d\u5251\u5df2\u5237\uff0c\u5237\uff0c\u5237\u7684\u8fde\u8fdb\u53c1\u62db\uff01 \u3000\u3000\u5929\u5143\u548c\u5c1a\u80dc\u5728\u81c2\u529b\u6c89\u96c4\uff0c\u89c1\u5cb3\u9e23\u73c2\u5251\u62db\u6765\u5f97\u5389\u5bb3\uff0c\u628a\u4e00\u67c4\u94f2\u76d8\u65cb\u6025\u821e\uff0c\u79bb\u8eab\u4e24\u4e08\u4ee5\u5185\uff0c \u98ce\u96e8\u4e0d\u900f\uff0c\u5168\u8eab\u4e0a\u4e0b\uff0c\u4fe8\u5982\u7b3c\u7f69\u5728\u4e00\u7247\u9752\u8272\u7684\u5149\u5e62\u4e4b\u4e2d\u3002\u5cb3\u9e23\u73c2\u8d5e\u9053\uff1a\u201c\u597d\uff01\u201d\u51ed\u7740\u4e00\u8eab\u6240 \u5b66\uff0c\u6e38\u9f99\u5251\u75be\u82e5\u60ca\u98d9\uff0c\u541e\u5410\u6492\u653e\uff0c\u5728\u9752\u8272\u7684\u5149\u5e62\u4e2d\u6325\u970d\u81ea\u5982\uff01 \u3000\u3000\u5929\u5143\u548c\u5c1a\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u662f\u8fbe\u6469\u9662\u4e2d\u7684\u9ad8\u50e7\uff0c\u8bba\u672c\u9886\u5728\u5c11\u6797\u5bfa\u53ef\u5750\u7b2c\u53c1\u628a\u4ea4\u6905\uff1a\u8bba\u9605\u5386\u5357 \u5317\u5404\u6d3e\u7684\u6b66\u529f\u65e0\u4e0d\u89c1\u8fc7\u3002\u4f46\u5cb3\u545c\u73c2\u7684\u5251\u672f\uff0c\u4e43\u662f\u63a2\u7eb3\u5404\u5bb6\u5251\u672f\u800c\u6210\uff0c\u6c89\u7a33\u96c4\u5065\u517c\u800c\u6709\u4e4b\uff0c\u5929 \u5143\u548c\u5c1a\u6253\u4e86\u4e94\u5341\u9980\u56de\u5408\uff0c\u7adf\u7136\u6478\u4e0d\u900f\u4ed6\u7684\u5bb6\u6570\u3002 \u3000\u3000\u4e24\u4eba\u8f97\u8f6c\u653b\u62d2\uff0c\u53c8\u6597\u4e86\u53c1\u4e94\u5341\u62db\uff0c\u5cb3\u545c\u73c2\u5251\u62db\u50ac\u7d27\uff0c\u76f4\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u6eda\u6eda\u800c\u4e0b\uff0c\u5728\u9752\u8272 \u5149\u61a7\u4e2d\u76d8\u65cb\u8fdb\u9000\uff0c\u53ea\u542c\u5f97\u4e00\u7247\u65ad\u91d1\u621b\u7389\u4e4b\u58f0\uff0c\u8fde\u7ef5\u4e0d\u65ad\uff0c\u6fc0\u6597\u6b63\u9163\uff0c\u5ffd\u542c\u5f97\u53c8\u6709\u58f0\u97f3\uff0c\u7a7a\u4e2d \u98d8\u4e0b\uff0c\u539f\u6765\u662f\u955c\u660e\u8001  \u5e08\u5728\u5854\u9876\u4f20\u58f0\uff0c\u5fae\u54c2\u8bf4\u9053\uff1a\u201c\u5929\u5143\u4f60\u5df2\u7ecf\u8f93\u4e86\uff0c\u8fd8\u4e0d\u9000\u4e0b\uff01\u201d\u58f0\u97f3\u5e76 \u4e0d\u5f88\u5927\uff0c\u4f46\u5374\u5165\u8033\u60ca\u5fc3\u3002\u5929\u5143\u4e00\u695e\u6536\u62db\u3002\u53ea\u89c1\u65b9\u4fbf\u94f2\u7684\u4e24\u8fb9\u950b\u5203\uff0c\u5df2\u5168\u7ed9\u524a\u5e73\uff0c\u867d\u662f\u60ca\u5fc3\uff0c \u4f46\u5fc3\u60f3\uff1a\u8fd9\u4e43\u662f\u5bf9\u624b\u5b9d\u5251\u4e4b\u529b\uff0c\u8bba\u672c\u9886\u81ea\u5df2\u5e76\u672a\u8f93\u62db\uff0c\u6240\u4ee5\u867d\u7136\u88ab\u5e08\u7236\u559d\u9000\uff0c\u5fc3\u4e2d\u5374\u5e76\u4e0d\u5f88 \u670d\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u671b\u7a7a\u9065\u62dc\uff0c\u7ed5\u8fc7\u8fbe\u6469\u9662\uff0c\u518d\u5411\u85cf\u7ecf\u9601\u884c\u53bb\u3002\u8fd9\u65f6\u5929\u5143\u548c\u5c1a\u5df2\u4e0a\u4e86\u77f3\u5854\uff0c\u95ee\u5e08\u7236 \u9053\uff1a\u201c\u5f1f\u5b50\u5e76\u672a\u8f93\u62db\uff0c\u5e08\u5c0a\u4f55\u4ee5\u559d\u9000\uff1f\u5c31\u662f\u6709\u610f\u653e\u4ed6\uff0c\u4e5f\u8be5\u8ba9\u4ed6\u77e5\u9053\u3002\u8fd9\u6837\u8ba9\u4ed6\uff0c\u5c82\u4e0d\u53eb\u4ed6 \u5c0f\u89d1\u4e86\u5c11\u6797\u5bfa\u7684\u94f2\u6cd5\uff1f\u201d \u3000\u3000\u8981\u77e5\u5c11\u6797\u5bfa\u7684\u4f0f\u9b54\u94f2\u6cd5\uff0c\u4e43\u662f\u6b66\u6797\u7edd\u5b66\u3002\u5f53\u65f6\u8bba\u5251\u6cd5\u9996\u63a8\u6b66\u5f53\uff1a\u8bba\u62f3\u638c\u6697\u5668\u548c\u5176\u4ed6\u5668\u68b0 \u5374\u8fd8\u7b97\u5c11\u6797\uff0c\u6240\u4ee5\u5929\u5143\u548c\u5c1a\u6709\u6b64\u8bf4\u6cd5\u3002\u955c\u660e\u957f\u8001\u53c8\u662f\u5fae\u5fae\u4e00\u54c2\u3002\u8bf4\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u8fd9\u9ebd\u591a\u5e74\uff0c \u5728\u8fbe\u6469\u9662\u4e2d\u4e5f\u5750\u4e0a\u4e86\u4e0a\u5ea7\u4e86\uff0c\u600e\u9ebd\u8f93\u4e86\u62db\u90fd\u8fd8\u4e0d\u77e5\uff1f\u4f60\u770b\u4f60\u7684\u80f8\u524d\u8863\u670d\u3002\u201d\u5929\u5143\u548c\u5c1a\u4fef\u9996\u4e00 \u770b\uff0c\u53ea\u89c1\u8888\u88df\u4e0a\u5f53\u80f8\u4e4b\u5904\uff0c\u7a7f\u4e86\u53c1\u4e2a\u5c0f\u6d1e\uff0c\u8fd9\u4e00\u4e0b\u51b7\u6c57\u6c81\u808c\uff0c\u624d\u77e5\u5cb3\u545c\u73c2\u786e\u662f\u624b\u4e0b\u7559\u60c5\u3002 \u3000\u3000\u955c\u660e\u8001  \u5e08\u5408\u4ec0\u8d5e\u9053\uff1a\u201c\u771f\u7684\uff1a\u6c5f\u5c71\u4ee3\u6709\u624d\u5165\u51fa\uff0c\u5404\u9886\u98ce\u9a9a\u6570\u5341\u5e74\u3002\u60f3\u4e0d\u5230\u8001\u8872\u665a\u5e74\u8fd8 \u5f97\u89c1\u6b66\u6797\u4e2d\u653e\u6b64\u5f02\u5f69\u3002\u201d\u5929\u5143\u548c\u5c1a\u9a87\u7136\u95ee\u9053\uff1a\u201c\u8fd9\u5cb3\u9e23\u73c2\u7684\u5251\u6cd5\u7a76\u662f\u4f55\u5bb6\u4f55\u6d3e\uff0c\u5e08\u7236\u5bf9\u4ed6\u5982 \u6b64\u63a8\u5d07\uff1f\u201d\u955c\u660e\u8001  \u5e08\u9053\uff1a\u201c\u4ed6\u7684\u5251\u6cd5\u4e43\u63a2\u7eb3\u5404\u5bb6\u5404\u6d3e\u7cbe\u534e\uff0c\u72ec\u521b\u51fa\u6765\u7684\u3002\u6211\u4e45\u95fb\u5929\u90fd\u5c45\u58eb \u5728\u5929\u5c71\u6f5c\u4fee\u5251\u6cd5\uff0c\u8fd9\u4eba\u60f3\u5fc5\u662f\u4ed6\u7684\u5f97\u610f\u9ad8\u8db3\u3002\u201d\u5929\u5c71\u5d69\u5c71\u76f8\u9694\u4f55\u6b62\u4e07\u91cc\uff0c\u970d\u5929\u90fd\u6f5c\u7814\u5251\u6cd5\u4e4b \u4e8b\uff0c\u53ea\u6709\u6781\u5c11\u6570\u6b66\u6797\u957f\u8001\u77e5\u9053\uff0c\u5929\u5143\u548c\u5c1a\u867d\u662f\u8fbe\u6469\u9662\u4e2d\u7684\u9ad8\u50e7\uff0c\u5374\u8fde\u970d\u5929\u90fd\u7684\u540d\u5b57\u90fd\u672a\u542c \u8fc7\u3002\u5f53\u4e0b\u66f4\u662f\u60ca\u5f02\u3002\u955c\u660e\u8001  \u5e08\u53c8\u9053\uff1a\u201c\u8fd9\u4eba\u9664\u4e86\u529f\u529b\u8fd8\u7a0d\u5acc\u6d45\u8584\u4e4b\u5916\uff0c\u8bba\u5251\u6cd5\u5373\u7d2b\u9633\u9053\u957f \u590d\u751f\uff0c\u4e5f\u672a\u5fc5\u80fd\u591f\u80dc\u4ed6\u3002\u770b\u6765\u4ed6\u4e0d\u5fc5\u8981\u6211\u4eec\u8ba9\uff0c\u4e5f\u53ef\u4ee5\u95ef\u8fc7\u56db\u5173\u7684\u4e86\u3002\u6b66\u5b66\u4e4b\u9053\u65e5\u65b0\u6708\u5f02\uff0c \u4e0d\u8fdb\u5219\u9000\uff0c\u6c5d\u5176\u614e\u4e4b\uff01\u201d\u5929\u5143\u548c\u5c1a\u5f97\u5e08\u7236\u6240\u4f20\u6700\u591a\uff0c\u5728\u8bf8\u5f1f\u5b50\u4e2d\u6b66\u529f\u7b2c\u4e00\uff0c\u672c\u6765\u6709\u70b9\u81ea\u8d1f\uff0c \u7ecf\u4e86\u6b64\u756a\u6559\u8bf2\u4e4b\u5f8c\uff0c\u4fee\u517b\u66f4\u7eaf\uff0c\u4e60\u7ec3\u66f4\u8654\uff0c\u7ec8\u65bc\u7ee7\u955c\u660e  \u5e08\u4e4b\u5f8c\uff0c\u6210\u4e3a\u5c11\u6797\u4e0b\u4e00\u4ee3\u7684\u4e3b\u6301\uff0c \u8fd9\u662f\u5f8c\u8bdd\u3002 \u3000\u3000\u518d\u8bf4\u5cb3\u545c\u73c2\u7ed5\u8fc7\u4e86\u8fbe\u6469\u9662\uff0c\u884c\u5230\u521d\u7956\u5eb5\u524d\uff0c\u85cf\u7ecf\u9601\u5df2\u7136\u5728\u671b\u3002\u8fd9\u521d\u7956\u5eb5\u4e43\u5c11\u6797\u5bfa\u50e7\u7eaa\u5ff5 \u8fbe\u6469\u7956\u5e08\u6240\u5efa\uff0c\u975e\u540c\u5c0f\u9119\uff0c\u5cb3\u9e23\u73c2\u6025\u5fd9\u8dea\u4e0b\u793c\u62dc\u3002\u91cc\u9762\u5c0a\u80dc  \u5e08\u7b11\u9053\uff1a\u201c\u5cb3\u65bd\u4e3b\u8bf7\u8fdb\u6765 \u5750\u3002\u201d\u5cb3\u545c\u73c2\u8fdb\u4e86\u5eb5\u5802\uff0c\u606d\u606d\u656c\u656c\u7684\u884c\u793c\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u53c2\u89c1\uff0c\u4e0d\u6562\u8f83\u91cf\u3002\u201d\u8fd9\u5c0a\u80dc  \u5e08\u548c\u955c \u660e\u957f\u8001\u4e43\u662f\u540c\u8f88\uff0c\u672c\u6765\u4ed6\u4e0d\u60f3\u81ea\u5df2\u628a\u5173\uff0c\u5f8c\u6765\u89c1\u5230\u5cb3\u545c\u73c2\u6b66\u529f\u786e\u5b9e\u5389\u5bb3\uff0c\u4e00\u65f6\u5174\u8d77\uff0c\u8fd9\u624d\u4ece \u77f3\u5854\u4e0b\u6765\uff0c\u8981\u4eb2\u81ea\u8bd5\u8bd5\u4ed6\u7684\u529f\u592b\u3002 \u3000\u3000\u5c0a\u80dc  \u5e08\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u8fc7\u4efd\u8c26\u865a\uff0c\u5750\u4e0b\u6765\u5427\u3002\u5b66\u65e0\u5148\u5f8c\uff0c\u8fbe\u8005\u4e3a\u5e08\u3002\u76f8\u4e92\u89c2\u6469\uff0c\u5f7c\u6b64 \u6709\u76ca\u3002\u201d\u5cb3\u9e23\u73c2\u9053\u58f0\u201c\u6055\u7f6a\u201d\uff0c\u5750\u5728\u897f\u9996\u84b2\u56e2\u4e4b\u4e0a\u3002\u5c0a\u80dc  \u5e08\u5750\u5728\u4e1c\u9996\u84b2\u56e2\u4e4b\u4e0a\uff0c\u4e24\u4eba\u76f8\u8ddd \u53c1\u4e08\u3002\u5c0a\u80dc\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u5fc5\u52a8\u624b\u8f83\u91cf\uff0c\u6211\u5c31\u5750\u5728\u8fd9\u84b2\u56e2\u4e4b\u4e0a\u4e0e\u4f60\u6bd4\u6bd4\u62f3\u6cd5\u5427\u3002\u201d\u5cb3\u545c\u73c2\u5fc3 \u60f3\uff1a\u5750\u5728\u84b2\u56e2\u4e0a\u600e\u9ebd\u6bd4\u62f3\uff1f\u53ea\u542c\u5f97\u5c0a\u80dc\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u76f8\u8ddd\u53c1\u4e08\uff0c\u62f3\u98ce\u53ef\u53ca\uff0c\u4f60\u6211\u5c31\u5750\u5728\u84b2\u56e2 \u4e4b\u4e0a\u53d1\u62f3\uff0c\u82e5\u8c01\u7ed9\u6253\u4e0b\u84b2\u56e2\uff0c\u90a3\u5c31\u7b97\u8f93\u4e86\u3002\u82e5\u4e24\u4eba\u90fd\u80fd\u7a33\u5750\uff0c\u90a3\u9ebd\u5c31\u7528\u94c3\u58f0\u8ba1\u70b9\u3002\u201d\u5cb3\u545c\u73c2 \u8be7\u9053\uff1a\u201c\u4ec0\u9ebd\u53eb\u505a\u94c3\u58f0\u8ba1\u70b9\uff1f\u201d \u3000\u3000\u5c0a\u80dc  \u5e08\u5fae\u5fae\u4e00\u7b11\uff0c\u628a\u4e00\u4e2a\u94dc\u94c3\u629b\u4e86\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u628a\u5b83\u653e\u5728\u6000\u4e2d\u3002\u201d\u5cb3\u545c\u73c2\u4f9d\u8a00\u653e \u597d\u3002\u5c0a\u80dc \u3000\u3000\u5e08\u76d8\u819d\u800c\u5750\uff0c\u4e5f\u628a\u4e00\u4e2a\u94dc\u94c3\u653e\u5728\u6000\u4e2d\uff0c\u7136\u5f8c\u8bf4\u9053\uff1a\u201c\u4f60\u6211\u968f\u610f\u53d1\u62f3\uff0c\u4ee5\u4e00\u70b7\u9999\u4e3a\u9650\uff0c\u4e24 \u4eba\u82e5\u90fd\u4e0d\u8dcc\u4e0b\u84b2\u56e2\uff0c\u5c31\u770b\u8c01\u4eba\u7684\u94c3\u58f0\u54cd\u5f97\u6700\u591a\u3002\u201d\u8fd9\u6bd4\u6cd5\u5012\u5f88\u65b0\u5947\uff0c\u5cb3\u545c\u73c2\u70b9\u5934\u9075\u547d\u3002 \u3000\u3000\u5c0a\u80dc\u7aef\u5750\u84b2\u56e2\uff0c\u9053\uff1a\u201c\u8bf7\u53d1\u62f3\u3002\u201d\u5cb3\u545c\u73c2\u4e00\u62f3\u5288\u7a7a\u6253\u51fa\uff0c\u5c0a\u80dc\u559d\u9053\uff1a\u201c\u597d\uff01\u201d\u8fc1\u51fb\u4e00 \u62f3\uff0c\u62f3\u98ce\u76f8\u649e\uff0c\u5cb3\u9e23\u73c2\u62f3\u529b\u7a0d\u900a\uff0c\u53ea\u89c9\u5fae\u98ce\u62c2\u9762\uff0c\u5e78\u597d\u94dc\u94c3\u672a\u54cd\u3002\u5c0a\u80dc\u8fde\u53d1\u6570\u62f3\uff0c\u5cb3\u9e23\u73c2\u62da \u529b\u62b5\u6321\uff0c\u62f3\u98ce\u76f8\u649e\uff0c\u6bcf\u6b21\u90fd\u6709\u5fae\u98ce\u5439\u6765\uff0c\u800c\u4e14\u98ce\u529b\u6709\u9010\u6e10\u52a0\u5f3a\u4e4b\u52bf\u3002\u5cb3\u9e23\u73c2\u4e00\u60f3\u4e0d\u597d\uff0c\u8fd9\u5c11 \u6797\u795e\u62f3\u65e0\u654c\uff0c\u548c\u4ed6\u786c\u62da\uff0c\u5fc5\u7136\u62b5\u6321\u4e0d\u4f4f\u3002\u5c0a\u80dc\u4e00\u62f3\u6253\u6765\uff0c\u4ed6\u6697\u8fd0\u5343\u65a4\u5760\u529f\u592b\uff0c\u5750\u7a33\u8eab\u5b50\uff0c\u5374 \u5e76\u4e0d\u53d1\u62f3\uff0c\u53ea\u542c\u5f97\u94c3\u54cd\u53ee\u5f53\uff0c\u5c0a\u80dc\u6570\u9053\uff1a\u201c\u4e00\uff0c\u4e8c\u2026\u2026\u201d\u5cb3\u545c\u73c2\u8d81\u8fd9\u7a7a\u9699\uff0c\u9aa4\u53d1\u4e00\u62f3\uff0c\u5c0a\u80dc \u4e00\u62f3\u65b9\u51fa\uff0c\u672a\u53ca\u53d1\u62f3\u62b5\u5fa1\uff0c\u6000\u4e2d\u94dc\u94c3\u4e5f\u53ee\u5f53\u54cd\u4e86\uff0c\u5cb3\u545c\u73c2\u4e5f\u6570\u201c\u4e00\u4e8c\u2026\u2026\u201d\u4e24\u4eba\u94dc\u94c3\u90fd\u5404\u54cd \u53c1\u4e0b\u3002\u5c0a\u80dc\u7b11\u9053\uff1a\u201c\u4f60\u5012\u806a\u660e\u3002\u201d\u9065\u51fb\u4e00\u62f3\uff0c\u5cb3\u545c\u73c2\u53c8\u4f7f\u7528\u524d\u6cd5\uff0c\u5f85\u4ed6\u51fa\u62f3\u4e4b\u5f8c\uff0c\u624d\u518d\u53d1 \u62f3\uff0c\u90a3\u77e5\u5c0a\u80dc\u8fd9\u62f3\u5374\u662f\u865a\u53d1\uff0c\u5cb3\u545c\u73c2\u4e00\u62f3\u51fb\u51fa\uff0c\u4ed6\u624d\u6309\u5b9e\uff0c\u62f3\u98ce\u53c8\u649e\u8fc7\u6765\u3002\u5cb3\u545c\u73c2\u6025\u5fd9\u7f29 \u624b\uff0c\u5c0a\u80dc\u51fa\u62f3\u5feb\u6781\uff0c\u8ddf\u624b\u53c8\u662f\u4e00\u62f3\uff0c\u5cb3\u9e23\u73c2\u6000\u4e2d\u94dc\u94c3\u53c8\u53ee\u5f53\u54cd\u4e86\u8d77\u6765\uff0c\u8fd9\u4e00\u6b21\u5cb3\u545c\u73c2\u8f93\u4e86\u4e24 \u70b9\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u9886\u4e86\u4e2a\u4e56\uff0c\u7559\u5fc3\u770b\u5c0a\u80dc\u7684\u62f3\u52bf\u865a\u5b9e\uff0c\u5bfb\u7455\u62b5\u9699\uff0c\u6b64\u6765\u5f7c\u5f80\uff0c\u94c3\u58f0\u53ee\u5f53\u4e0d\u7edd\uff0c\u8fc7\u4e86 \u5927\u534a\u70b7\u9999\uff0c\u5cb3\u545c\u73c2\u6bd4\u5bf9\u4e4b\u5f8c\uff0c\u8f93\u4e86\u4e94\u70b9\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u5c0a\u80dc\u4e00\u60f3\uff0c\u8be5\u8ba9\u8ba9\u4ed6\u4e86\uff0c\u5cb3\u545c\u73c2\u8fde\u53d1\u4e24 \u62f3\uff0c\u5c0a\u80dc\u5e76\u4e0d\u62b5\u5fa1\uff0c\u6000\u4e2d\u94dc\u94c3\u54cd\u4e86\u56db\u4e0b\uff0c\u5cb3\u545c\u73c2\u6bd4\u5bf9\u53ea\u8f93\u4e00\u70b9\uff0c\u4e0d\u89c9\u9732\u51fa\u7b11\u5bb9\u3002\u5c0a\u80dc\u6697\u9053\uff1a \u201c\u518d\u8ba9\u4f60\u7740\u6025\u4e00\u4e0b\u3002\u201d\u4e0d\u518d\u8ba9\u62f3\uff0c\u62f3\u98ce\u731b\u6251\uff0c\u5cb3\u545c\u73c2\u6253\u8d77\u7cbe\u795e\uff0c\u5e26\u653b\u5e26\u5b88\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u6bd4\u5bf9 \u53c8\u8f93\u4e86\u53c1\u70b9\uff0c\u9999\u5df2\u5c31\u8981\u70e7\u5b8c\u3002\u5cb3\u545c\u73c2\u4e0d\u77e5\u5c0a\u80dc\u5fc3\u610f\uff0c\u53ea\u9053\u4ed6\u6709\u610f\u4e3a\u96be\uff0c\u731b\u7136\u5f97\u4e01\u4e00\u8ba1\uff0c\u5c0a\u80dc \u53c8\u53d1\u4e00\u62f3\uff0c\u5cb3\u545c\u73c2\u8fd0\u5185\u529b\u4e00\u8feb\uff0c\u6000\u4e2d\u94dc\u94c3\u9aa4\u7136\u98de\u4e86\u8d77\u6765\uff0c\u5cb3\u545c\u73c2\u52a0\u4e0a\u4e00\u62f3\uff0c\u4e24\u4eba\u62f3\u98ce\u51b2\u51fb\uff0c \u90a3\u94dc\u94c3\u5728\u534a\u7a7a\u4e2d\u70b8\u88c2\uff0c\u94dc\u7247\u7eb7\u98de\uff0c\u5cb3\u9e23\u73c2\u5927\u53eb\u9053\uff1a\u201c\u54ce\uff0c\u6211\u7684\u94dc\u94c3\u6bc1\u4e86\uff01\u8fd9\u5982\u4f55\u7b97\u6cd5\u3002\u201d\u5c0a \u80dc\u4e00\u695e\uff0c\u8eab\u5f62\u6b32\u8d77\uff0c\u5cb3\u545c\u73c2\u8d81\u8fd9\u6863\u53e3\u731b\u53d1\u4e00\u62f3\uff0c\u5c0a\u80dc\u6000\u4e2d\u7684\u94dc\u94c3\u63a5\u8fde\u54cd\u4e86\u53c1\u4e0b\uff0c\u6eda\u843d\u84b2\u56e2\uff0c \u90a3\u70b7\u9999\u521a\u521a\u70e7\u5b8c\uff01 \u3000\u3000\u5c0a\u80dc\u5927\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u771f\u6709\u4f60\u7684\uff01\u54b1\u4eec\u521a\u597d\u626f\u5e73\uff0c\u8fd9\u5173\u7b97\u4f60\u53c8\u95ef\u8fc7\u4e86\uff01\u201d\u5cb3\u9e23\u73c2\u9053\u58f0 \u201c\u5f97\u7f6a\u201d\uff0c\u8df3\u4e0b\u84b2\u56e2\uff0c\u4f5c\u4e86\u4e00\u63d6\uff0c\u53ea\u89c9\u4e24\u81c2  \u75db\u3002\u5c0a\u80dc\u7b11\u9053\uff1a\u201c\u4ee5\u4f60\u7684\u5e74\u7eaa\uff0c\u6709\u5982\u6b64\u529f\u529b\uff0c \u8fd9\u5173\u4e5f\u8be5\u8ba9\u4f60\u8fc7\u4e86\u3002\u201d \u3000\u3000\u5cb3\u545c\u73c2\u8d70\u51fa\u521d\u7956\u5eb5\uff0c\u4f46\u89c9\u6de1\u6708\u5fae\u660e\uff0c\u661f\u6cb3\u803f\u803f\u3002\u5ffd\u7136\u60f3\u8d77\u521d\u4eba\u5bfa\u65f6\u7684\u90a3\u6761\u9ed1\u5f71\u6765\u3002\u5fc3\u60f3 \u8fde\u95ef\u56db\u5173\uff0c\u591c\u5df2\u53c1\u66f4\u4e86\uff0c\u90a3\u6761\u9ed1\u5f71\u82e5\u662f\u5c11\u6797\u5bfa\u4e2d\u6240\u6d3e\u6697\u4e2d\u76ef\u7740\u81ea\u5df1\uff0c\u4e3a\u4f55\u73b0\u5728\u8fd8\u4e0d\u51fa\u73b0\u3002\u4e0d \u77e5\u4e0d\u89c9\u8d70\u5230\u4e86\u85cf\u7ecf\u9601\uff0c\u5cb3\u545c\u73c2\u53c8\u8dea\u4e0b\u53bb\u78d5\u4e86\u53c1\u4e2a\u54cd\u5934\uff0c\u53ea\u542c\u5f97\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u9053\uff1a\u201c\u597d\u5b69 \u5b50\uff0c\u8fdb\u6765\u5427\uff01\u201d \u3000\u3000\u5cb3\u9e23\u73c2\u63a8\u95e8\u8fdb\u53bb\uff0c\u53ea\u89c1\u955c\u660e\u8001  \u5e08\u7aef\u5750\u84b2\u56e2\u4e4b\u4e0a\uff0c\u5cb3\u545c\u73c2\u6025\u6574\u8083\u8863\u51a0\uff0c\u4e0b\u8dea\u53c2\u8c12\u3002\u955c\u660e \u9053\uff1a\u201c\u4f60\u662f\u5929\u90fd\u5c45\u58eb\u7684\u5f1f\u5b50\u9ebd\uff1f\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u662f\u3002\u201d\u955c\u660e  \u5e08\u9053\uff1a\u201c\u53c1\u5341\u5e74\u524d\u8d2b\u50e7\u6e38\u81f3\u5ce8 \u5d4b\uff0c\u4e0e\u4ee4\u5e08\u66fe\u6709\u4e00\u9762\u4e4b\u7f18\u3002\u90a3\u65f6\u4ed6\u6b63\u6536\u96c6\u5929\u4e0b\u5251\u8c31\uff0c\u51a5\u601d\u9ed8\u7d22\uff0c\u6b32\u7a77\u5176\u7406\u3002\u5f8c\u6765\u4ed6\u9690\u5c45\u5929 \u5c71\uff0c\u97f3\u8baf\u4e43\u7edd\u3002\u4eca\u665a\u770b\u4e86\u4f60\u7684\u51fa\u624b\uff0c\u60f3\u6765\u4ed6\u5929\u5c71\u5251\u6cd5\u5df2\u6210\uff0c\u8d2b\u50e7\u771f\u8981\u4e3a\u6545\u4eba\u9053\u8d3a\u4e86\u3002\u201d\u5cb3\u9e23 \u73c2\u5782\u624b\u8bf4\u9053\uff1a\u201c\u5929\u5c71\u5251\u6cd5\u521d\u5177\u89c4\u6a21\uff0c\u8fd8\u671b\u5927\u5e08\u6307\u70b9\u3002\u201d\u955c\u660e\u957f\u8001\u7b11\u9053\uff1a\u201c\u5251\u51fb\u4e4b\u5b66\uff0c\u8001\u8872\u8fdc \u8fdc\u4e0d\u53ca\u5c0a\u5e08\u3002\u4f60\u4eca\u665a\u5230\u6765\uff0c\u6211\u8bd5\u8bd5\u4f60\u7684\u5185\u529f\u5427\u3002\u201d\u5cb3\u9e23\u73c2\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\u5185\u529f\u8f83\u91cf\uff0c\u8d62\u8f93\u7acb \u5224\uff0c\u60f3\u53d6\u5de7\u85cf\u62d9\uff0c\u5747\u65e0\u53ef\u80fd\uff0c\u8fd9\u5374\u5982\u4f55\u662f\u597d\u3002\u955c\u660e\u9053\uff1a\u201c\u4f60\u5230\u90a3\u8fb9\u7684\u84b2\u56e2\u4e0a\u5750\u4e0b\u3002\u201d\u5cb3\u9e23\u73c2 \u53ea\u9053\u4ed6\u53c8\u4e0e\u5c0a\u80dc\u4e00\u6837\uff0c\u8981\u8bd5\u81ea\u5df1\u7684\u62f3\u529b\uff0c\u6025\u5fd9\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u4e07\u4e07\u4e0d\u6562\u63a5\u8001\u7985\u5e08\u7684\u795e\u62f3\u3002\u201d\u955c \u660e\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4e0e\u4f60\u6bd4\u62f3\uff0c\u4f60\u4e14\u5750\u4e0b\u3002\u201d\u5cb3\u545c\u73c2\u81ea\u77e5\u5931\u8a00\uff0c\u955c\u660e  \u5e08\u4e00\u6d3e\u5b97\u4e3b\uff0c \u65ad\u65e0\u4e0e\u81ea\u5df1\u6bd4\u62f3\u4e4b\u7406\uff0c\u9762\u4e0a\u4e00\u7ea2\uff0c\u4f9d\u8a00\u5230\u84b2\u56e2\u4e0a\u5750\u4e0b\u3002\u955c\u660e\u7aef\u6765\u4e00\u4e2a\u84b2\u56e2\uff0c\u5750\u5728\u5cb3\u545c\u73c2\u5bf9 \u9762\uff0c\u53d6\u51fa\u4e00\u6761\u7ef3\u5b50\uff0c\u9012\u7ed9\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u4f60\u6211\u5404\u6267\u4e00\u7aef\uff0c\u4f60\u7167\u5e73\u65f6\u505a\u529f\u592b\u7684\u6837\u5b50\uff0c\u9759\u5750\u8c03\u5143\uff0c \u8ba9\u6211\u770b\u4f60\u5185\u529f\u7684\u6df1\u6d45\u3002\u201d \u3000\u3000\u5cb3\u545c\u73c2\u5c06\u4fe1\u5c06\u7591\uff0c\u5fc3\u60f3\uff1a\u600e\u9ebd\u8fd9\u6837\u5c31\u53ef\u4ee5\u8bd5\u51fa\u6211\u5185\u529f\u7684\u6df1\u6d45\uff0c\u65bc\u662f\u76d8\u819d\u5750\u4e0b\uff0c\u505a\u8d77\u5410\u7eb3 \u529f\u592b\u3002\u5750\u4e86\u4e00\u4f1a\uff0c\u53ea\u89c9\u80f8\u8179\u4e4b\u95f4\uff0c\u4f3c\u5df2\u7ed3\u6210\u4e00\u80a1\u52b2\u529b\uff0c\u968f\u7740\u547c\u5438\u52a8\u4f5c\uff0c\u4e0a\u4e0b\u5347\u6c89\u3002\u8fd9\u6b63\u662f\u5185 \u529f\u5230\u4e86\u4e00\u5b9a\u706b\u5019\u65f6\uff0c\u4f53\u5185\u6240\u517b\u6210\u7684\u6c14\u52b2\u3002\u5cb3\u9e23\u73c2\u81ea\u5e7c\u968f\u5e08\u7236\u5728\u5929\u5c71\u9759\u4fee\uff0c\u5185\u529f\u5df2\u5f97\u771f\u4f20\uff0c\u6240 \u4ee5\u5750\u4e86\u4e00\u4f1a\uff0c\u5df2\u662f\u6c14\u900f\u56db\u68a2\uff0c\u8eab\u5b50\u5fae\u5fae\u53d1\u70ed\u3002\u5cb3\u545c\u73c2\u81ea\u77e5\u9887\u6709\u8fdb\u5883\uff0c\u5fc3\u4e2d\u6b22\u559c\uff0c\u773c\u775b\u5fae\u5f00\uff0c \u53ea\u89c1\u955c\u660e  \u5e08\u7aef\u5750\u84b2\u56e2\u4e4b\u4e0a\uff0c\u95ed\u76ee\u5782\u9996\uff0c\u9762\u6709\u7b11\u5bb9\u3002\u5cb3\u545c\u73c2\u5fc3\u60f3\u96be\u9053\u955c\u660e  \u5e08\u5df2\u6d4b\u77e5\u4e86\u6211\u7684 \u5185\u529b\uff0c\u53ea\u6b64\u4e00\u5ff5\uff0c\u5fc3\u4e2d\u5df2\u6709\u5fae\u6ce2\u3002\u955c\u660e\u60ee\u5e08\u4ecd\u662f\u95ed\u76ee\u9759\u5750\uff0c\u5cb3\u9e23\u73c2\u5750\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u6742\u5ff5\u60ed \u751f\uff0c\u4ece\u731c\u6d4b\u955c\u660e\u7528\u610f\u60f3\u5230\u201c\u9f99\u6cc9\u767e\u70bc\u8bc0\u201d\u4e0d\u77e5\u80fd\u5426\u53d6\u5230\uff0c\u4e00\u4f1a\u513f\u53c8\u60f3\u5230\u81ea\u5df1\u7684\u6b66\u529f\u4e0d\u77e5\u662f\u5426 \u80fd\u5165\u8001  \u5e08\u6cd5\u773c\uff0c\u4e00\u4f1a\u513f\u53c8\u60f3\u5230\u718a\u7ecf\u7565\u9501\u5b88\u8fb9\u5173\uff0c\u519b\u60c5\u4e0d\u77e5\u6709\u5426\u53d8\u5316\uff1a\u6742\u5ff5\u4e00\u751f\uff0c\u4ee5\u610f\u884c \u6c14\uff0c\u5df2\u6ca1\u6709\u6700\u521d\u90a3\u6837\u81ea\u7136\u3002\u955c\u660e  \u5e08\u5ffd\u9053\uff1a\u201c\u5584\u54c9\uff0c\u5584\u54c9\uff01\u201d\u5cb3\u545c\u73c2\u5403\u4e86\u4e00\u60ca\uff0c\u53c8\u542c\u5f97\u955c\u660e \u5e08\u9053\uff1a\u201c\u65a9\u65e0\u660e\uff0c\u65ad\u6267\u7740\uff0c\u8d77\u667a\u6167\uff0c\u8bc1\u771f\u5982\u3002\u8fd9\u5341\u4e8c\u5b57\u8bc0\uff0c\u53e4\u4eca\u4fee\u58eb\u51e0\u4eba\u9886\u7565\uff1f\u201d\u5cb3\u545c\u73c2\u51db \u7136\u6212\u60e7\uff0c\u5480\u56bc\u8fd9\u5341\u4e8c\u5b57\uff0c\u9886\u609f\u955c\u660e\u957f\u8001\u662f\u501f\u4e0a\u4e58\u4f5b\u7406\uff0c\u6307\u70b9\u81ea\u5df1\u5185\u529f\u3002\u6240\u8c13\u201c\u65e0\u660e\u201d\uff0c\u6307\u7684 \u4e43\u662f\u201c\u8d2a\u55d4\u75f4\u201d\u4e4b\u5ff5\uff1a\u6240\u8c13\u201c\u6267\u7740\u201d\u6307\u7684\u4e43\u662f\u5fc3\u4e2d\u6709\u4e8b\u4e0d\u80fd\u5316\u5f00\uff0c\u4ee5\u81f4\u95ed\u585e\u6027\u7075\u3002\u6240\u8c13\u201c\u771f \u5982\u201d\u4e43\u662f\u6307\u65e0\u4eba\u65e0\u6211\u4e4b\u5883\u3002\u4f5b\u5bb6  \u7406\uff0c\u5fc5\u987b\u65a9\u65e0\u660e\uff0c\u65ad\u6267\u7740\uff0c\u7136\u5f8c\u624d\u80fd\u8d77\u667a\u6167\uff0c\u800c\u5230\u8fbe\u771f\u5982 \u7684\u5883\u754c\u3002\u5cb3\u545c\u73c2\u4ece  \u7406\u53c2\u900f\u5185\u529f\u4fee\u6301\u4e4b\u9053\uff0c\u8c41\u7136\u8d2f\u901a\uff0c\u5fc3\u4e2d\u5f00\u6717\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u4e00\u901a\u6b64\u610f\uff0c\u6742\u5ff5\u5373\u6cef\uff0c\u8fd0\u6c14\u53c1\u8f6c\uff0c\u5fc3\u5883\u7a7a\u660e\u3002\u955c\u660e  \u5e08\u628a\u7ef3\u4e00\u7275\uff0c\u9053\uff1a\u201c\u884c\u4e86\uff0c \u4f60\u4f9d\u6b64\u4fee\u6301\uff0c\u5185\u529f\u81ea\u6709\u5927\u6210\u4e4b\u65e5\u3002\u201d\u5cb3\u545c\u73c2\u8d77\u7acb\u81f4\u8c22\uff0c\u4e0d\u77e5\u955c\u660e\u4f55\u4ee5\u4f1a\u77e5\u81ea\u5df2\u5fc3\u4e2d\u610f\u5ff5\uff0c\u6b63 \u60f3\u8bf7\u95ee\u3002\u955c\u660e\u5df2\u9053\uff1a\u201c\u4fee\u7ec3\u5185\u529f\uff0c\u5fc5\u987b\u5fc3\u4e2d\u4e00\u5c18\u4e0d\u67d3\u3002\u5fc3\u82e5\u4e0d\u9759\uff0c\u56db\u80a2\u4ea6\u4e0d\u80fd\u9759\uff0c\u6240\u4ee5\u82e5\u6709 \u6742\u5ff5\uff0c\u5fc5\u5f62\u4e4b\u65bc\u5916\uff0c\u4f60\u521d\u5750\u65f6\uff0c\u7ef3\u5b50\u5fae\u52a8\uff0c\u5176\u5f8c\u5373\u5f52\u9759\u6b62\uff0c\u53ef\u89c1\u4f60\u5185\u529f\u5df2\u6709\u706b\u5019\u3002\u53ef\u60dc\u5c1a\u672a \u7eaf\u9759\uff0c\u5176\u5f8c\u7ef3\u5b50\u53c8\u5fae\u5fae\u98a4\u52a8\uff0c\u6709\u5982\u6b7b\u6c34\u5fae\u6f9c\uff0c\u6211\u5c31\u77e5\u9053\u4f60\u5fc5\u7136\u80f8\u6709\u6742\u5ff5\u4e86\u3002\u201d\u5cb3\u545c\u73c2\u5fc3\u60a6\u8bda \u670d\uff0c\u6b63\u60f3\u5bdf\u544a\u53d6\u4e66\uff0c\u955c\u660e\u957f\u8001\u9762\u5bb9\u4e00\u7aef\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u662f\u5426\u8fd8\u6709\u540c\u4f34\u968f\u6765\uff1f\u201d \u3000\u3000\u5cb3\u545c\u73c2\u5403\u4e86\u4e00\u60ca\uff0c\u6025\u9053\uff1a\u201c\u6ca1\u6709\u5440\uff01\u201d\u955c\u660e  \u5e08\u9053\uff1a\u201c\u6709\u4eba\u5df2\u5230\u85cf\u7ecf\u9601\u4e0a\uff0c\u4f60\u66ff\u6211\u628a\u4ed6 \u6349\u6765\u3002\u201d\u8bdd\u58f0\u65b9\u505c\uff0c\u5df2\u542c\u5f97\u5c0a\u80dc  \u5e08\u5728\u9ad8\u5904\u4f20\u58f0\u53eb\u9053\uff1a\u201c\u8fbe\u6469\u9662\u50e7\u4eba\u5feb\u5230\u85cf\u7ecf\u9601\u6765\uff01\u201d \u3000\u3000\u5cb3\u9e23\u73c2\u62d4\u5251\u5728\u624b\uff0c\u98de\u8dc3\u4e0a\u9601\uff0c\u9ed1\u9edd\u4e2d\u5ffd\u542c\u5f97\u4e00\u58f0\u602a\u5578\uff0c\u638c\u98ce\u5288\u9762\u626b\u6765\uff0c\u5cb3\u545c\u73c2\u4e00\u8fb9\u6321 \u638c\uff0c\u53ea\u89c9\u654c\u4eba\u638c\u98ce\u5947\u52b2\uff0c\u6025\u5411\u638c\u98ce\u6765\u5904\uff0c\u8eab\u5f62\u75be\u8fdb\uff0c\u5237\u7684\u4e00\u5251\u523a\u53bb\u3002\u5cb3\u545c\u73c2\u5185\u529f\u5df2\u6709\u6839  \uff0c \u81ea\u7136\u4ea6\u901a\u542c\u98ce\u8fa8\u5668\u4e4b\u672f\uff0c\u4e0d\u6599\u4e00\u5251\u523a\u51fa\uff0c\u53ea\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u4e00\u56e2\u9ed1\u5f71\u5411\u524d\u6251\u5230\u81ea\u5df1\u53f3\u4fa7\uff0c\u5cb3\u545c \u73c2\u5927\u559d\u4e00\u58f0\uff0c\u6e38\u9f99\u5251\u4e00\u4e2a\u65cb\u98ce\u75be\u821e\uff0c\u987f\u65f6\u94f6\u5149\u56db\u5c04\uff0c\u4e00\u5ba4\u751f\u8f89\uff0c\u7167\u89c1\u4e00\u4e2a\u7ea2\u9762\u8001\u4eba\uff0c\u8d1f\u9685\u72de \u7b11\uff01 \u3000\u3000\u5cb3\u9e23\u73c2\u5b9d\u5251\u4e00\u7ffb\uff0c\u5bd2\u5149\u95ea\u5904\uff0c\u4e00\u62db\u201c\u767d\u8679\u8d2f\u65e5\u201d\uff0c\u5251\u950b\u76f4\u5954\u654c\u4eba\u201c\u534e\u76d6\u7a74\u201d\u624e\u53bb\uff0c\u90a3\u7ea2 \u9762\u8001\u4eba\u500f\u5730\u4e00\u9000\uff0c\u5cb3\u545c\u73c2\u6050\u6bc1\u574f\u67b6\u4e0a\u85cf\u7ecf\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u622a\u4ed6\u53bb\u8def\uff0c\u90a3\u77e5\u8fd9\u8001\u4eba\u8eab\u624b\uff0c\u7adf\u662f\u8fc5 \u75be\u5f02\u5e38\uff0c\u4ed6\u8d81\u7740\u5cb3\u545c\u73c2\u6362\u62db\u4e4b\u9645\uff0c\u7a81\u7136\u6251\u5230\uff0c\u624b\u638c\u4e00\u62c2\uff0c\u4fbf\u7167\u5cb3\u9e23\u73c2\u6301\u5251\u7684\u624b\u8155\u76f4\u622a\u8fc7\u6765\u3002 \u5cb3\u545c\u73c2\u8eab\u8eaf\u4e00\u77ee\uff0c\u4e3e\u5251\u64a9\u65a9\u654c\u624b\u8109\u95e8\uff0c\u90a3\u8001\u4eba\u8eab\u8eaf\u534a\u8f6c\uff0c\u7a81\u98de\u4e00\u638c\uff0c\u5cb3\u9e23\u73c2\u6025\u64a4\u62db\u65f6\uff0c\u624b\u8155 \u5df2\u7ed9\u654c\u4eba\u624b\u6307\u62c2\u4e86\u4e00\u4e0b\uff0c\u706b\u8fa3\u8fa3\u7684\u4f5c\u75db\u3002\u5cb3\u9e23\u73c2\u5927\u6012\uff0c\u6e38\u9f99\u5251\u5411\u524d\u4e00\u9886\uff0c\u5251\u950b\u4e00\u98a4\uff0c\u4f38\u7f29\u4e0d \u5b9a\uff0c\u8fd9\u4e00\u62db\u6697\u85cf\u51e0\u4e2a\u53d8\u5316\uff0c\u662f\u5929\u5c71\u5251\u6cd5\u4e2d\u6740\u7740\u4e4b\u4e00\uff0c\u7ea2\u9762\u8001\u4eba\u80a9\u5934\u4e00\u6643\uff0c\u5cb3\u545c\u73c2\u7684\u5251\u5237\u7684\u5411 \u4ed6\u9000\u5904\u523a\u53bb\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u8001\u4eba\u7684\u957f\u886b\u7ed9\u6495\u4e86\u4e00\u5757\uff0c\u5cb3\u545c\u73c2\u633a\u5251\u518d\u523a\uff0c\u7ea2\u9762\u8001\u4eba\u731b\u559d\u4e00 \u58f0\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u638c\u98ce\u52b2\u75be\uff0c\u5cb3\u545c\u73c2\u7684\u5251\u70b9\u7adf\u7ed9\u9707\u6b6a\uff01\u90a3\u8001\u4eba\u75be\u5982\u9e70\u96bc\uff0c\u98d2\u58f0\u7a9c\u4e0a\u5c4b\u9876\uff01 \u3000\u3000\u5cb3\u9e23\u73c2\u6b63\u60f3\u8ffd\u4e0a\uff0c\u5ffd\u542c\u5f97\u5c4b\u9876\u4e0a\u5c0a\u80dc\u60ee\u5e08\u5927\u559d\u4e00\u58f0\uff1a\u201c\u6eda\u4e0b\uff01\u201d\u63a5\u7740\u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u5982 \u5de8\u6728\u76f8\u649e\uff0c\u7ea2\u9762\u8001\u4eba\u76f4\u8dcc\u4e0b\u6765\u201c\u5c0a\u80dc  \u5e08\u8ddf\u7740\u8dc3\u4e0b\uff0c\u628a\u706b\u647a\u5b50\u4e00\u4eae\uff0c\u53ea\u89c1\u90a3\u4e2a\u8001\u4eba\u8eb2\u5728\u4e24\u4e2a \u4e66\u67b6\u4e4b\u4e2d\uff0c\u9762\u8272\u7070\u8d25\uff0c\u5374\u4ecd\u662f\u72de\u7b11\u4e0d\u5df2\u3002 \u3000\u3000\u5c0a\u80dc  \u5e08\u559d\u9053\uff1a\u201c\u4ec0\u9ebd\u4eba\uff0c\u8fd8\u4e0d\u675f\u624b\u5c31\u7f1a\uff1f\u201d\u90a3\u7ea2\u9762\u8001\u4eba\u72de\u7b11\u9053\uff1a\u201c\u4f60\u6562\u518d\u8fdb\u4e00\u6b65\uff0c\u6211 \u4fbf\u628a\u4f60\u4eec\u5c11\u6797\u5bfa\u7684\u85cf\u7ecf\u7edf\u7edf\u6bc1\u4e86\uff0c\u4f60\u63a5\u8fc7\u6211\u4e00\u638c\uff0c\u96be\u9053\u8fd8\u4e0d\u76f8\u4fe1\u6211\u6709\u6b64\u529b\u91cf\uff1f\u201d \u3000\u3000\u5c0a\u80dc  \u5e08\u9762\u8272\u94c1\u9752\uff0c\u4ed6\u521a\u624d\u548c\u90a3\u8001\u4eba\u786c\u63a5\u786c\u67b6\uff0c\u90a3\u4e00\u638c\u4e5f\u53d7\u5f97\u4e0d\u8f7b\uff0c\u77e5\u4ed6\u6240\u8a00\u4e0d\u5047\u3002\u6295 \u9f20\u5fcc\u5668\uff0c\u695e\u5728\u5f53\u573a\u3002\u6b63\u5728\u6b64\u9645\uff0c\u955c\u660e  \u5e08\u53e3\u5ba3\u4f5b\u53f7\uff0c\u8d70\u4e0a\u9601\u6765\uff0c\u7ea2\u9762\u8001\u4eba\u9053\uff1a\u201c\u955c\u660e  \u5e08\uff0c \u4f60\u4eec\u5c11\u6797\u5bfa\u82e5\u4ee5\u591a\u4e3a\u80dc\uff0c\u6211\u4e5f\u4e0d\u6253\u7b97\u751f\u51fa\u6b64\u95e8\u4e86\uff01\u201d\u955c\u660e  \u5e08\u5ff5\u4e86\u53e5\u201c\u963f\u5f25\u9640\u4f5b\u201d\uff0c\u5408\u4ec0\u95ee \u9053\uff1a\u201c\u65bd\u4e3b\u5230\u6b64\uff0c\u610f\u6b32\u4f55\u4e3a\uff0c\u53ef\u80af\u89c1\u544a\u9ebd\uff1f\u201d \u3000\u3000\u7ea2\u9762\u8001\u4eba\u9053\uff1a\u201c\u60f3\u501f\u9f99\u6cc9\u767e\u70bc\u8bc0\u548c\u6613\u7b4b\u7ecf\u4e00\u89c2\u3002\u201d\u955c\u660e  \u5e08\u9053\uff1a\u201c\u9f99\u6cc9\u767e\u70bc\u8bc0\u6211\u5df2\u7b54\u5e94 \u501f\u4e0e\u522b\u4eba\uff0c\u81f3\u65bc\u6613\u7b4b\u7ecf\u4e43\u662f\u6211\u4eec\u7956\u5e08\u7684\u9057\u5b9d\uff0c\u8bf7\u6055\u4e0d\u80fd\u5949\u9605\u3002\u201d\u5c0a\u80dc\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e2d\u4e86\u6211\u7684 \u795e\u62f3\uff0c\u4e0d\u8d76\u5feb\u9759\u517b\u6cbb\u7597\uff0c\u8fd8\u6562\u5728\u8fd9\u91cc\u8bb9\u8bc8\u9ebd\uff1f\u201d\u955c\u660e  \u5e08\u7ed5\u4e66\u67b6\u8d70\u4e86\u4e00\u5468\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u51fa\u53bb \u5427\uff0c\u6211\u4e0d\u602a\u4f60\u4fbf\u662f\u3002\u5178\u7c4d\u7ecf\u4e66\u4f60\u8981\u5e26\u4e5f\u5e26\u4e0d\u51fa\u53bb\u3002\u201d\u90a3\u7ea2\u9762\u8001\u4eba\u4e00\u60f3\uff0c\u786e\u662f\u9053\u7406\uff0c\u5c31\u7b97\u955c\u660e \u957f\u8001\u4e0d\u7ba1\u3002\u5c11\u6797\u50e7\u4f17\u4e5f\u4e0d\u4f1a\u4e0d\u7406\uff0c\u4fbf\u9053\uff1a\u201c\u4f60\u8bf4\u653e\u6211\u51fa\u53bb\uff0c\u90a3\u5916\u9762\u7684\u50e7\u4eba\u5462\uff1f\u201d\u955c\u660e\u9053\uff1a \u201c\u6211\u53eb\u76d1\u5bfa\u966a\u4f60\u51fa\u53bb\uff0c\u6653\u8c15\u4ed6\u4eec\uff0c\u4e0d\u8981\u52a8\u624b\u3002\u201d\u7ea2\u9762\u8001\u4eba\u770b\u4e86\u5c0a\u80dc\u4e00\u773c\uff0c\u53cc\u624b\u4ecd\u7136\u6276\u7740\u4e66 \u67b6\u3002\u955c\u660e\u957f\u8001\u9053\uff1a\u201c\u4f5b\u5bb6\u4e0d\u6253\u8bf3\u8bed\u3002\u4f60\u8fd8\u60ca\u60e7\u4ec0\u9ebd\uff1f\u201d\u7ea2\u9762\u8001\u4eba\u9053\uff1a\u201c\u597d\uff0c\u90a3\u8bf7\u628a\u5c0f\u8fd8\u4e39\u62ff \u4e00\u7c92\u6765\uff01\u201d\u5c0a\u80dc\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u955c\u660e\u60ee\u5e08\u9053\uff1a\u201c\u7ed9\u4ed6\u3002\u201d\u5c0a\u80dc\u65e0\u5948\uff0c\u4ece\u94f6\u74f6\u4e2d\u6311\u51fa\u4e00\u7c92\u7ea2 \u4e38\uff0c\u7ea2\u9762\u8001\u4eba\u63a5\u8fc7\uff0c\u7acb\u523b\u653e\u4eba\u53e3\u4e2d\u3002\u5c0a\u80dc\u559d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u968f\u6211\u51fa\u53bb\uff01\u201d\u98de\u8eab\u4e00\u8dc3\u800c\u51fa\uff0c\u7ea2\u9762 \u8001\u4eba\u8f6c\u8eab\u5411\u955c\u660e  \u5e08\u4e00\u63d6\uff0c\u968f\u7740\u8dc3\u51fa\u3002\u5cb3\u545c\u73c2\u89c1\u4ed6\u773c\u5149\u6d41\u52a8\uff0c\u6015\u6709\u4e0d\u6d4b\uff0c\u4e5f\u63d0\u7740\u6e38\u9f99\u5b9d\u5251\uff0c \u8ddf\u5728\u5f8c\u9762\u3002 \u3000\u3000\u5c4b\u9876\u74e6\u80cc\u4e0a\u5df2\u7ad9\u6ee1\u4e86\u4eba\uff0c\u8fbe\u6469\u9662\u4e2d\u7684\u516b\u540d\u9ad8\u50e7\uff0c\u8fde\u540c\u767d\u77f3\u9053\u4eba\u4e0e\u5353\u4e00\u822a\u5168\u90fd\u6765\u4e86\u3002\u5cb3\u545c \u73c2\u89c1\u5353\u4e00\u822a\u5728\u6b64\uff0c\u6014\u4e86\u4e00\u6014\u3002\u5c0a\u80dc  \u5e08\u626c\u624b\u56b7\u9053\uff1a\u201c\u65b9\u4e08\u6709\u547d\uff0c\u653e\u4ed6\u51fa\u53bb\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u6b63\u5728\u5c0a\u80dc\u60ee\u5e08\u8eab\u65c1\uff0c\u5728\u6708\u5149\u4e0b\u770b\u5f97\u660e\u767d\uff0c\u5c0a\u80dc  \u5e08\u7684\u624b\u638c\u904d\u5e03\u7ea2\u6591\uff0c\u6025\u5fd9\u95ee\u9053\uff1a \u201c  \u5e08\u9002\u624d\u548c\u8fd9\u8001\u8d3c\u5bf9\u638c\u6765\u4e86\uff1f\u201d\u5c0a\u80dc\u9053\uff1a\u201c\u600e\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4ed6\u662f\u9634\u98ce\u6bd2\u7802\u638c\u91d1\u8001 \u602a\uff01\u201d\u5c0a\u80dc\u60ee\u5e08\u5403\u4e86\u4e00\u60ca\uff0c\u9002\u624d\u63a5\u4e86\u4e00\u638c\uff0c\u5df2\u89c9\u5947\u5f02\uff0c\u4f46\u8fd8\u6599\u4e0d\u5230\u5c31\u662f\u9634\u98ce\u6bd2\u7802\u638c\u3002\u5927\u559d\u4e00 \u58f0\uff0c\u8981\u60f3\u8ffd\u65f6\uff0c\u53cc\u817f\u5ffd\u8f6f\u3002\u91d1\u72ec\u5f02\u5df2\u8d8a\u4e86\u4e24\u91cd\u5927\u6bbf\uff0c\u56de\u5934\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5c11\u6797\u5bfa\u8bf4\u8bdd\u4e0d\u7b97\u8bdd \u5417\uff01\u201d\u955c\u660e\u957f\u8001\u5728\u4e0b\u9762\u4e5f\u9053\uff1a\u201c\u4e0d\u8981\u8ffd\u4ed6\uff01\u201d \u3000\u3000\u5cb3\u545c\u73c2\u5ffd\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5c11\u6797\u5bfa\u7684\u4eba\uff01\u201d\u5353\u4e00\u822a\u731b\u7136\u9192\u8d77\uff0c\u6025\u9053\uff1a\u201c\u5cb3\u5927\u54e5\uff0c\u6211\u4eec\u8ffd\u4ed6\uff0c \u4ed6\u5077\u4e86\u4f60\u5e08\u5a18\u7684\u5251\u8c31\uff01\u201d\u5cb3\u545c\u73c2\u5927\u559d\u4e00\u58f0\uff0c\u8eab\u5f62\u75be\u8d77\uff0c\u4ece\u85cf\u7ecf\u9601\u4e00\u63a0\u6570\u4e08\uff0c\u4e24\u4e2a\u8d77\u4f0f\uff0c\u5df3\u8df3 \u5230\u4e86\u521d\u7956\u5eb5\u6bbf\u80cc\u3002\u5353\u4e00\u822a\u4e0e\u5cb3\u9e23\u73c2\u540c\u65f6\u8d77\u6b65\uff0c\u7d27\u8ddf\u7740\u4ed6\u8ffd\u51fa\u4e86\u51e0\u91cd\u5c4b\u9762\u3002 \u3000\u3000\u767d\u77f3\u9053\u4eba\u5927\u611f\u610f\u5916\uff0c\u5fc3\u4e2d\u9887\u602a\u5353\u4e00\u822a\u597d\u7ba1\u95f2\u4e8b\u3002\u4ed6\u5374\u4e0d\u77e5\u5353\u4e00\u822a\u5ff5\u7740\u7389\u7f57\u5239\uff0c\u4e00\u89c1\u4e86\u5077 \u7389\u7f57\u5239\u5251\u8c31\u4e4b\u4eba\uff0c\u7adf\u7136\u4e0d\u987e\u672c\u9886\u60ac\u6b8a\uff0c\u8ff3\u81ea\u8ffd\u4e0b\u53bb\u4e86\uff01 \u3000\u3000\u4e14\u8bf4\u5353\u4e00\u822a\u98de\u8d76\u4e0b\u53bb\uff0c\u8d77\u521d\u8fd8\u53ef\u89c1\u7740\u5cb3\u9e23\u73c2\u7684\u80cc\u5f71\uff0c\u60ed\u6e10\u80cc\u5f71\u53d8\u6210\u4e86\u4e00\u4e2a\u9ed1\u70b9\uff0c\u5728\u591c\u8272 \u6726\u80e7\u4e2d\u9690\u53bb\u3002\u5353\u4e00\u822a\u8f7b\u529f\u867d\u662f\u4e0d\u51e1\uff0c\u4f46\u6bd4\u8d77\u5cb3\u545c\u73c2\u548c\u91d1\u72ec\u5f02\u5374\u8fd8\u76f8\u5dee\u9887\u8fdc\u3002\u6240\u4ee5\u8d8a\u8ffd\u8d8a\u8fdc\uff0c \u7ec8\u65bc\u671b\u4e0d\u89c1\u4ed6\u7684\u5f71\u5b50\u3002  \uff0c \u3000\u3000\u5353\u4e00\u822a\u6b63\u5728\u8e0c\u8e87\uff0c\u767d\u77f3\u9053\u4eba\u5df2\u7ecf\u8d76\u5230\uff0c\u5353\u4e00\u822a\u9053\uff1a\u201c\u4ed6\u4eec\u5728\u897f\u5317\u89d2\uff0c\u6211\u4eec\u53bb\u4e5f\u4e0d\u53bb\uff1f\u201d \u767d\u77f3\u9053\uff1a\u201c\u4f60\u662f\u6211\u6d3e\u672a\u6765\u7684\u638c\u95e8\uff0c\u5bf9\u6c5f\u6e56\u4e0a\u7684\u4eba\u60c5\u4e16\u6545\uff0c\u5e94\u8be5\u901a\u8fbe\u3002\u6211\u4eec\u5230\u5c11\u6797\u5bfa\u4f5c\u5ba2\uff0c\u5c11 \u6797\u7684\u76d1\u5bfa\u4e2d\u4e86\u6bd2\u7802\u638c\u7684\u4f24\uff0c\u6211\u4eec\u8be5\u5148\u6551\u4e3b\u4eba\uff0c\u7136\u5f8c\u8ffd\u654c\u3002\u4f55\u51b5\u90a3\u91d1\u8001\u602a\u5df2\u4e2d\u4e86\u5c11\u6797\u795e\u62f3\uff0c\u5b9a \u975e\u90a3\u59d3\u5cb3\u7684\u5bf9\u624b\uff0c\u4f55\u5fc5\u4f60\u53bb\u76f8\u5e2e\u3002\u201d\u5353\u4e00\u822a\u4e00\u60f3\uff0c\u4e5f\u662f\u9053\u7406\uff0c\u5f53\u4e0b\u968f\u767d\u77f3\u9053\u4eba\u56de\u8f6c\u5c11\u6797\u5bfa\u3002 \u3000\u3000\u518d\u8bf4\u5cb3\u9e23\u73c2\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u7d27\u8e51\u9634\u98ce\u6bd2\u7802\u638c\u91d1\u72ec\u5f02\u8eab\u5f8c\uff0c\u8ffd\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5df3\u4ece\u5c11\u5ba4\u5c71\u8ffd \u5230\u592a\u5ba4\u5c71\u9e93\u3002\u5cb3\u545c\u73c2\u5ffd\u89c9\u5fc3\u5934\u70e6\u8e81\uff0c\u53e3\u4e2d\u7126\u6e34\uff0c\u811a\u6b65\u4e00\u6162\uff0c\u91d1\u72ec\u5f02\u53d1\u8db3\u72c2\u5954\uff0c\u500f\u5ffd\u4e0d\u89c1\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u7f13\u4e86\u53e3\u6c14\uff0c\u53ea\u89c9\u81c2\u818a  \u75d2\uff0c\u5377\u8896\u4e00\u770b\uff0c\u81ea\u81c2\u5f2f\u4ee5\u4e0b\uff0c\u7600\u9ed1\u80c0\u80bf\uff0c\u4e00\u6761\u7ea2\u7ebf\uff0c\u6162\u6162 \u4e0a\u5347\uff0c\u5c31\u5982\u53d7\u4e86\u6bd2\u86c7\u6240\u54ac\u4e00\u822c\u3002\u8981\u77e5\u8fd9\u91d1\u72ec\u5f02\u4ee5\u9634\u98ce\u6bd2\u7802\u638c\u6210\u540d\uff0c\u529f\u529b\u6bd4\u4ed6\u7684\u4f84\u513f\u91d1\u5343  \u4f55 \u6b62\u6df1\u539a\u5341\u500d\u3002\u5cb3\u545c\u73c2\u624b\u8155\u88ab\u4ed6\u62c2\u7740\uff0c\u5267\u6597\u4e4b\u5f8c\u7ee7\u4ee5\u72c2\u8ffd\uff0c\u6bd2\u4f24\u53d1\u4f5c\uff0c\u6bd2\u6c14\u4e0a\u5347\uff0c\u5cb3\u9e23\u73c2\u89c1\u4e86 \u4e0d\u89c9\u9a87\u7136\uff0c\u6025\u5fd9\u62e9\u5730\u5750\u4e0b\uff0c\u5fd9\u8fd0\u5410\u7eb3\u529f\u592b\uff0c\u4ee5\u4e0a\u4e58\u5185\u529f\uff0c\u5c06\u6bd2\u6c14\u5f3a\u538b\u4e0b\u53bb\u3002 \u3000\u3000\u5927\u7ea6\u8fc7\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u90a3\u6761\u7ea2\u7ebf\u5df2\u9000\u81f3\u5bf8\u5173\u5c3a\u8109\u4ee5\u4e0b\u3002\u5cb3\u9e23\u73c2\u60f3\uff1a\u7b49\u5230\u5929\u4eae\uff0c\u5927\u7ea6\u53ef\u4ee5\u56de \u5c11\u6797\u5bfa\u4e86\u3002\u6b63\u81ea\u6b23\u6170\uff0c\u5ffd\u95fb\u5f97\u6e05\u8106\u7b1b\u58f0\uff0c\u8d85\u81ea\u85cf\u8eab\u4e0d\u8fdc\u4e4b\u5904\u3002\u5cb3\u545c\u73c2\u63a2\u5934\u5916\u671b\uff0c\u53ea\u89c1\u4e00\u4e2a\u5c11 \u5e74\uff0c\u5c31\u7aef\u5750\u5728\u5916\u9762\u7684\u4e00\u5757\u5ca9\u77f3\u4e0a\u3002\u5cb3\u545c\u73c2\u5927\u5947\uff0c\u770b\u6597\u8f6c\u661f\u6a2a\uff0c\u6708\u659c\u4e91\u6de1\uff0c\u60f3\u6765\u5df2\u662f\u56db\u66f4\u65f6\u5206 \u4e86\uff0c\u4e3a\u4f55\u8fd9\u4e2a\u5c11\u5e74\uff0c\u8fd8\u72ec\u81ea\u5728\u6b64\u5439\u7b1b\uff1f \u3000\u3000\u53c8\u8fc7\u4e86\u4e00\u9635\uff0c\u8fdc\u5904\u9ed1\u5f71\u5e62\u5e62\uff0c\u5386\u4e71\u5954\u6765\uff0c\u5c11\u5e74\u628a\u7b1b\u5b50\u4e00\u6536\uff0c\u500f\u7136\u7ad9\u8d77\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u4f60 \u4eec\u6765\u8fdf\u4e86\u3002\u201d \u3000\u3000\u6765\u7684\u7ea6\u6709\u5341\u9980\u4eba\uff0c\u4e3a\u9996\u7684\u662f\u4e2a\u4e94\u5341\u5c81\u5de6\u53f3\u7684\u4e7e\u7626\u8001\u5934\uff0c\u4ef0\u5929\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u8c05\u4f60 \u4e5f\u4e0d\u6562\u64c5\u81ea\u79bb\u53bb\u3002\u5582\uff0c\u4f60\u8fd9\u4e2a\u5a03\u5a03\uff0c\u53eb\u4ec0\u9ebd\u540d\u5b57\uff1f\u201d \u3000\u3000\u5c11\u5e74\u7709\u6bdb\u4e00\u626c\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u9ebd\u8981\u544a\u8bc9\u4f60\uff1f\u201d \u3000\u3000\u8001\u5934\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u521d\u51fa\u5934\u7684\u96cf\u513f\uff0c\u4f60\u61c2\u4e0d\u61c2\u7eff\u6797\u89c4\u77e9\uff1f\u4f60\u4f38\u624b\u505a\u6848\uff0c\u4e3a\u4f55\u4e0d\u62dc\u89c1\u8fd9\u91cc\u7684 \u9f99\u5934\uff1f\u201d\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u662f\u8fd9\u91cc\u7684\u9f99\u5934\u3002\u201d\u8001\u5934\u7b11\u9053\uff1a\u201c\u4f60\u5012\u67e5\u5f97\u6e05\u695a\uff0c\u90a3\u9ebd\u770b\u6765\u4f60\u5df2\u77e5 \u9053\u8fd9\u91cc\u7684\u9f99\u5934\u5927\u54e5\u662f\u8c01\u4e86\u3002\u90a3\u4f60\u662f\u77e5\u60c5\u6545\u72af\uff0c\u7f6a\u52a0\u4e00\u7b49\u3002\u201d\u5c11\u5e74\u9053\uff1a\u201c\u4ec0\u9ebd\u5927\u54e5\u4e0d\u5927\u54e5\uff0c\u4f60 \u4eec\u5077\u5f97\uff0c\u6211\u4e5f\u5077\u5f97\u3002\u201d \u3000\u3000\u8001\u5934\u65c1\u8fb9\u95ea\u51fa\u4e00\u4e2a\u9b41\u68a7\u6c49\u5b50\uff0c\u6012\u6c14\u51b2\u51b2\uff0c\u621f\u6307\u9a82\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u8d3c\uff0c\u5c45\u7136\u6562\u5e72\u9ed1\u5403\u9ed1\u7684\u52fe \u5f53\uff0c\u5feb\u628a\u90a3\u679d\u7389\u73ca\u745a\u7f34\u56de\u6765\u3002\u201d \u3000\u3000\u5cb3\u545c\u73c2\u5fc3\u60f3\u539f\u6765\u8fd9\u662f\u5f3a\u76d7\u5185\u8ba7\uff0c\u4f46\u770b\u8fd9\u5c11\u5e74\uff0c\u4e00\u8868\u65ad\u6587\uff0c\u4e3a\u4f55\u4e5f\u5e72\u9ed1\u9053\u7684\u52fe\u5f53\uff1f\u6b63\u662f\uff1a \u6c5f\u6e56\u9ed1\u5403\u9ed1\uff0c\u4fa0\u58eb\u8d77\u7591\u5fc3\u3002\u4ed7\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377\u2014\u2014\u7b2c\u5341\u56de\u3000\u5251\u672f\u901a\u7384\u3000\u5929\u5c71\u4f20\u4fa0\u5ba2\u3000\u4eac\u534e\u8bf4\u602a\u3000\u5185\u82d1\u51fa\u6deb\u90aa \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377 \u7b2c\u5341\u56de \u5251\u672f\u901a\u7384\u3000\u5929\u5c71\u4f20\u4fa0\u5ba2 \u4eac\u534e\u8bf4\u602a\u3000\u5185\u82d1\u51fa\u6deb\u90aa \u3000\u3000\u90a3\u5c11\u5e74\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u662f\u8fd9\u91cc\u7684\u9f99\u5934\u5927\u54e5\u4e86\uff1f\u201d\u90a3\u6c49\u5b50\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u53eb\u4f60\u77e5\u9053 \u9ed1\u5b50\u7684\u5389\u5bb3\uff0c\u7389\u73ca\u745a\u62ff\u4e0d\u62ff\u6765\uff1f\u201d\u5c11\u5e74\u7b11\u9053\uff1a\u201c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u5df2\u628a\u5b83\u6362\u4e86\u94f6\u5b50\u4e86\u3002\u201d  \u9ed1\u5b50\u5927 \u6012\uff0c\u53cc\u624b\u4e00\u4f38\uff0c\u4eae\u51fa\u4e00\u5bf9\u98de\u722a\uff0c\u6402\u5934\u6293\u4e0b\uff0c\u90a3\u7626\u8001\u5934\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u4f24\u4ed6\u3002\u201d\u5c11\u5e74\u7b1b\u5b50\u4e00\u6a2a\uff0c \u4e00\u5bf9\u98de\u722a\u6c64\u4e86\u5f00\u53bb\u3002\u4fe1\u624b\u4e00\u70b9\uff0c  \u9ed1\u5b50\u5495\u549a\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u90a3\u4e7e\u7626\u8001\u5934\u9762\u8272\u4e00\u53d8\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u662f\u94c1\u98de\u9f99\u7684\u4ec0\u9ebd\u4eba\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u6b63\u662f\u94c1\u98de\u9f99\u7684\u5973\u513f\u94c1\u73ca\u745a\uff0c\u5979\u7ed9\u7236\u4eb2\u9010\u51fa\u5bb6\u95e8\u4e4b\u5f8c\uff0c\u5973\u626e\u7537\u88c5\uff0c\u968f\u5904\u98d8\u6e38\uff0c\u5012\u4e5f \u81ea\u5728\u3002\u6ca1\u51e0\u65f6\u4f7f\u5230\u5bcc\u6237\u91cc\u5077\uff0c\u524d\u51e0\u5929\u5979\u5230\u4e86\u767b\u5c01\uff0c\u5ffd\u7136\u5728\u8857\u4e0a\u78b0\u5230\u91d1\u72ec\u5f02\u53d4\u4f84\u4e00\u5927\u73ed\u4eba\uff0c\u6025 \u5fd9\u8eb2\u907f\u3002\u672c\u6765\u5979\u5e94\u8be5\u65e9\u65e9\u79bb\u5f00\uff0c\u4f46\u4e00\u60f3\u8d77\u91d1\u8001\u602a\u65e2\u7136\u5728\u6b64\u51fa\u73b0\uff0c\u5979\u7684\u7236\u4eb2\u548c\u7389\u7f57\u5239\u4e5f\u53ef\u80fd\u8ffd \u6765\u3002\u94c1\u73ca\u745a\u867d\u7136\u88ab\u9010\u51fa\u5bb6\uff0c\u5bf9\u7236\u4eb2\u4ecd\u662f\u601d\u5ff5\u3002\u5979\u77e5\u9053\u7236\u4eb2\u548c\u7389\u7f57\u5239\u53bb\u627e\u91d1\u72ec\u5f02\u7d22\u56de\u5251\u8c31\uff0c\u5979 \u65e2\u7136\u5728\u6b64\u9047\u5230\u91d1\u72ec\u5f02\u53d4\u4f84\uff0c\u867d\u7136\u81ea\u77e5\u672c\u9886\u76f8\u5dee\u6781\u8fdc\uff0c\u4e5f\u8981\u6697\u91cc\u8ddf\u8e2a\u3002 \u3000\u3000\u5979\u5230\u4e86\u767b\u5c01\u4e4b\u5f8c\uff0c\u6cbf\u9014\u6240\u5077\u7684\u94b1\u5df2\u82b1\u5149\u4e86\uff0c\u4e00\u665a\u5979\u5230\u57ce\u91cc\u4e00\u5bb6\u5927\u6237\u53bb\u5077\uff0c\u51d1\u5de7\u78b0\u5230  \u9ed1 \u5b50\u7684\u624b\u4e0b\u5148\u5230\u90a3\u91cc\u505a\u6848\u3002\u5979\u5728\u5f3a\u76d7\u624b\u4e2d\u8f6c\u5077\u4e86\u4e00\u5927\u5305\u94f6\u5b50\uff0c\u53c8\u89c1\u4e00\u679d\u7389\u73ca\u745a\u751a\u4e3a\u53ef\u7231\uff0c\u4e5f\u987a \u624b\u7275\u7f8a\u7684\u62ff\u4e86\u3002\u5979\u672c\u6765\u4e0d\u5c06\u8fd9\u73ed\u5f3a\u76d7\u653e\u5728\u773c\u5185\uff0c\u4e0d\u6599\u7b2c\u4e8c\u5929\u7adf\u7136\u63a5\u5230\u7eff\u6797\u201c\u8bf7\u5e16\u201d\uff0c\u6307\u5b9a\u8981 \u5979\u5728\u53c1\u66f4\u65f6\u5206\uff0c\u5728\u592a\u5ba4\u5c71\u9e93\u4e94\u67cf\u6811\u5761\u76f8\u5019\uff0c\u540c\u65f6\u4e5f\u5df2\u53d1\u73b0\u4e86\u76d1\u89c6\u7684\u4eba\u3002\u94c1\u73ca\u745a\u4e00\u60f3\u4e0d\u5999\uff0c\u82e5 \u7136\u5728\u5bd3\u6240\u548c\u8fd9\u73ed\u5f3a\u76d7\u4e89\u6597\u8d77\u6765\uff0c\u53ea\u6050\u88ab\u91d1\u5bb6\u53d4\u4f84\u770b\u7834\u81ea\u5df1\u884c\u85cf\uff0c\u5012\u4e0d\u5982\u6084\u6084\u7684\u53bb\u8d74\u4ed6\u4eec\u4e4b \u7ea6\uff0c\u6599\u90a3\u73ed\u5f3a\u76d7\u4e0d\u662f\u81ea\u5df1\u5bf9\u624b\u3002\u8c01\u77e5\u90a3  \u9ed1\u5b50\u548c\u91d1\u5bb6\u53d4\u4f84\u76f8\u8bc6\uff0c\u7adf\u7136\u8bf7\u6765\u4e86\u91d1\u5343  \u52a9\u62f3\u3002 \u3000\u3000\u91d1\u5343\u5d4c\u548c\u94c1\u73ca\u745a\u672c\u6765\u76f8\u8bc6\uff0c\u4f46\u5979\u6362\u4e86\u7537\u88c5\uff0c\u6de1\u6708\u758f\u661f\u4e0b\u4e00\u65f6\u770b\u4e0d\u6e05\u695a\uff0c\u76f4\u5230\u5979\u51fa\u624b\u4e4b \u5f8c\uff0c\u8fd9\u624d\u770b\u6e05\u4e86\u662f\u94c1\u5bb6\u8eab\u6cd5\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u5728\u5ca9\u77f3\u5f8c\u4e00\u542c\uff0c\u6697\u6697\u9a87\u5f02\u3002\u8fd9\u94c1\u98de\u9f99\u548c\u91d1\u72ec\u5f02\u5728\u897f\u5317\u9f50\u540d\uff0c\u600e\u9ebd\u5ffd\u7136\u95f4\u90fd\u4f1a\u6765\u5230 \u6b64\u5904\uff1f \u3000\u3000\u94c1\u73ca\u745a\u5fae\u5fae\u4e00\u7b11\uff0c\u94c1\u7b1b\u4e00\u6a2a\uff0c\u9053\uff1a\u201c\u91d1\u8001\u513f\uff0c\u7389\u7f57\u5239\u8981\u53d6\u4f60\u7684\u547d\u5462\uff0c\u4f60\u8fd8\u6562\u5728\u8fd9\u91cc\u7316 \u72c2\u3002\u201d\u91d1\u5343  \u5413\u4e86\u4e00\u8df3\uff0c\u5f20\u773c\u56db\u671b\u3002\u53eb\u9053\uff1a\u201c\u4f60\u662f\u73ca\u745a\uff0c\u4f60\u7239\u7239\u548c\u7389\u7f57\u5239\u4e5f\u6765\u4e86\uff1f\u201d\u94c1\u73ca\u745a \u628a\u7b1b\u51d1\u5728\u53e3\u8fb9\u4e00\u5439\uff0c\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u4e00\u5b9a\u542c\u5230\u6211\u7684\u7b1b\u58f0\u4e86\u3002\u201d \u3000\u3000\u94c1\u73ca\u745a\u6545\u5e03\u7591\u9635\uff0c\u91d1\u5343  \u9762\u9752\u5507\u767d\uff0c\u5fc3\u60f3\u53d4\u53d4\u5230\u5c11\u6797\u5bfa\u76d7\u4e66\uff0c\u600e\u9ebd\u8fd8\u4e0d\u89c1\u56de\uff1f\u82e5\u7136\u7389\u7f57 \u5239\u548c\u94c1\u98de\u9f99\u4e00\u9f50\u51fa\u73b0\uff0c\u8fd9\u53ef\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u3002\u94c1\u73ca\u745a\u53c8\u662f\u4e00\u9635\u51b7\u7b11\u3002\u91d1\u5343  \u614c\u5fd9\u65bd\u793c\u9053\uff1a\u201c\u59d1 \u5a18\uff0c\u6211\u4e0d\u77e5\u662f\u4f60\uff0c\u4f11\u602a\u4f11\u602a\uff01\u201c\u628a\u624b\u4e00\u6325\uff0c\u8f6c\u8eab\u6b32\u9003\uff0c  \u9ed1\u5b50\u8fd9\u65f6\u5df2\u81ea\u5730\u4e0a\u722c\u8d77\uff0c\u5ffd\u7136\u51b7\u7b11 \u8bf4\u9053\uff1a\u201c\u91d1\u5927\u54e5\u4f11\u8981\u542c\u4ed6\u80e1\u8a00\u4e71\u8bed\u201c\u8fd9\u51e0\u5929\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u5f00\u5c01\u5883\u5185\uff0c\u5e76\u6ca1\u6709\u6c5f\u6e56\u4eba\u7269\uff01\u201d \u3000\u3000\u8fd9  \u9ed1\u5b50\u4e43\u662f\u6cb3\u5357\u5e2e\u4f1a\u9996\u9886\uff0c\u53c8\u662f\u5f00\u5c01\u4e00\u9738\uff0c\u672c\u4e8b\u867d\u7136\u4e0d\u9ad8\u5f3a\uff0c\u624b\u4e0b\u515a\u7fbd\u751a\u591a\uff0c\u6d88\u606f\u5012 \u662f\u7075\u901a\u4e4b\u6781\u3002\u91d1\u5343  \u542c\u4ed6\u4e00\u8bf4\uff0c\u60ca\u9b42\u7a0d\u5b9a\u3002\u53eb\u9053\uff1a\u201c\u597d\u54c7\uff0c\u4f60\u8fd9\u5c0f\u4e2b\u5934\u4e5f\u6562\u9a97\u6211\uff01\u201d \u3000\u3000  \u9ed1\u5b50\u559c\u9053\uff1a\u201c\u5979\u662f\u5973\u7684\uff1f\u62ff\u6765\u7ed9\u6211\u3002\u201d\u94c1\u73ca\u745a\u5927\u6012\uff0c\u7b1b\u5b50\u4e00\u70b9\uff0c  \u9ed1\u5b50\u5495\u549a\u4e00\u58f0\uff0c\u53c8 \u5012\u5730\u4e0a\u3002\u8fd9\u56de\u4f24\u5f97\u66f4\u91cd\uff0c\u7adf\u7136\u722c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u91d1\u5343  \u563b\u563b\u7b11\u9053\uff1a\u201c\u5c0f\u4e2b\u5934\uff0c\u4f11\u5f97\u901e\u51f6\u3002\u201d\u53f3\u624b\u4e00\u4f38\uff0c\u5288\u9762\u6293\u5230\uff0c\u94c1\u73ca\u745a\u6643\u8eab\u6025\u95ea\uff0c\u9ad8 \u58f0\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u5feb\u6765\u5440\uff01\u201d\u91d1\u5343  \u4e00\u7a92\uff0c\u94c1\u73ca\u745a\u55d6\u7684\u7a9c\u51fa\u4e24\u4e08\u5f00\u5916\uff0c\u91d1\u5343  \u5927\u6012\uff0c\u98de\u8eab\u4e00 \u63a0\uff0c\u62e6\u5728\u94c1\u73ca\u745a\u9762\u524d\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u54fc\uff0c\u62ff\u7389\u7f57\u5239\u6765\u5413\u6211\uff01\u201d\u5f20\u624b\u5c31\u6293\uff0c\u94c1\u73ca\u745a\u7ed9\u8feb\u5f97\u6b65\u6b65 \u9000\u5f8c\u3002 \u3000\u3000\u91d1\u5343  \u4e00\u638c\u62cd\u5230\uff0c\u94c1\u73ca\u745a\u94c1\u7b1b\u4e00\u70b9\uff0c\u7ed9\u4ed6\u631f\u624b\u62a2\u53bb\uff0c\u4e22\u5728\u5730\u4e0a\uff0c\u5de6\u638c\u53c8\u5230\uff0c\u94c1\u73ca\u745a\u9000\u5df2 \u4e0d\u53ca\uff0c\u91d1\u5343  \u5ffd\u7136\u628a\u638c\u4e00\u6536\uff0c\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u820d\u4e0d\u5f97\u7528\u9634\u98ce\u6bd2\u7802\u638c\u4f24\u4f60\uff0c\u5c0f\u4e2b\u5934\uff0c\u4f60\u597d\u597d\u7b54\u6211 \u7684\u8bdd\uff0c\u82e5\u6709\u4e00\u5b57\u9690\u7792\uff0c\u53eb\u4f60\u6b7b\u4e0d\u4e86\u6d3b\u7740\u53d7\u82e6\u3002\u4f60\u7239\u7239\u5462\uff1f\u4ed6\u548c\u7389\u7f57\u5239\u5230\u90a3\u91cc\u53bb\u4e86\uff1f\u201d \u3000\u3000\u94c1\u73ca\u745a\u9053\uff1a\u201c\u4f60\u771f\u7684\u8981\u89c1\u4ed6\u4eec\uff1f\u201d\u91d1\u5343  \u6012\u9053\uff1a\u201c\u8c01\u548c\u4f60\u8bf4\u7b11\uff01\u201d\u53cd\u624b\u4e00\u62ff\uff0c\u94c1\u73ca\u745a\u4e00 \u95ea\u8eab\u53c8\u53eb\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff01\u201d\u91d1\u5343  \u4e0d\u518d\u53d7\u9a97\uff0c\u624b\u6307\u4e00\u4f38\uff0c\u6307\u5c16\u5df2\u662f\u6cbe\u8863\uff0c\u5ffd\u7136\u201c\u54ce\u54df\u201d\u4e00 \u58f0\uff0c\u6025\u6025\u64a4\u624b\uff0c\u94c1\u73ca\u745a\u4e5f\u5f04\u5f97\u83ab\u540d\u5176\u5999\u3002 \u3000\u3000\u539f\u6765\u5cb3\u9e23\u73c2\u8eb2\u5728\u77f3\u5f8c\uff0c\u542c\u5f97\u5206\u660e\uff0c\u521d\u65f6\u4ee5\u4e3a\u662f\u5f3a\u76d7\u5185\u8ba7\uff0c\u672c\u4e0d\u60f3\u51fa\u624b\u52a9\u8c01\u3002\u5f8c\u6765\u4e00\u542c\u94c1 \u73ca\u745a\u9053\u51fa\u90a3\u8001\u5934\u59d3\u91d1\uff0c\u53c8\u542c\u90a3\u8001\u5934\u81ea\u62a5\u201c\u9634\u98ce\u6bd2\u7802\u638c\u201d\u7684\u5b57\u53f7\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u54c8\uff0c\u60f3 \u4e0d\u5230\u5728\u8fd9\u91cc\u4e5f\u649e\u5230\u4ed6\u4eec\u3002\u91d1\u8001\u602a\u8ffd\u4e0d\u7740\uff0c\u4e14\u628a\u4ed6\u7684\u4f84\u513f\u62ff\u4e86\u3002\u6697\u4e2d\u634f\u4e86\u4e00\u7c92\u6ce5\u4e38\uff0c\u624b\u6307\u4e00 \u5f39\uff0c\u6b63\u6b63\u6253\u4e2d\u91d1\u5343  \u7684\u8109\u95e8\u3002\u8fd9\u4e00\u6765\u91d1\u5343  \u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u4ee5\u4e3a\u771f\u662f\u7389\u7f57\u5239\u5230\u6765\uff0c\u8f6c\u8eab\u4fbf \u9003\u3002  \u9ed1\u5b50\u5df2\u7531\u5925\u4f34\u6276\u8d77\uff0c\u89c1\u72b6\u83ab\u540d\u5176\u5999\uff0c\u56b7\u9053\uff1a\u201c\u8fd9\u91cc\u9664\u4e86\u8fd9\u5c0f\u8d3c\u4e4b\u5916\uff0c\u5e76\u6ca1\u65c1\u7684\u4eba \u5440\uff01\u201d\u91d1\u5343  \u56de\u8fc7\u5934\u6765\uff0c\u89c1\u94c1\u73ca\u745a\u563b\u563b\u51b7\u7b11\uff0c\u90a3\u6709\u7389\u7f57\u5239\u5f71\u5b50\u3002\u91d1\u5343  \u5fc3\u6000\u6050\u60e7\uff0c\u4e0d\u6562\u8d70 \u56de\uff0c\u770b\u4e86\u4e00\u9635\uff0c\u4ecd\u65e0\u5f02\u72b6\uff0c  \u9ed1\u5b50\u7684\u624b\u4e0b\u56e2\u56e2\u5c06\u94c1\u73ca\u745a\u5708\u7740\uff0c\u53ef\u662f\u4ed6\u4eec\u89c1\u8fc7\u94c1\u73ca\u745a\u6b66\u529f\uff0c\u91d1 \u5343  \u4e0d\u6765\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u6562\u8d38\u7136\u52a8\u624b\u3002 \u3000\u3000\u91d1\u5343  \u5b9a\u4e86\u5b9a\u795e\uff0c\u4e00\u60f3\u82e5\u7136\u662f\u7389\u7f57\u5239\u7684\u8bdd\uff0c\u5979\u51fa\u624b\u4e4b\u5f8c\uff0c\u7edd\u4e0d\u5bb9\u60c5\uff0c\u4e00\u5b9a\u73b0\u8eab\u6765\u8ffd\uff1a\u53c8 \u60f3\uff1a\u82e5\u7136\u771f\u662f\u7389\u7f57\u5239\u5728\u6b64\uff0c\u5979\u6765\u53bb\u5982\u7535\uff0c\u8981\u9003\u4e5f\u9003\u4e0d\u6389\uff0c\u53cd\u6b63\u662f\u6b7b\uff0c\u4e0d\u5982\u56de\u53bb\u770b\u770b\u3002\u83ab\u53eb\u4e0d \u662f\u7389\u7f57\u5239\u65f6\uff0c\u7ed9  \u9ed1\u5b50\u7b11\u81ea\u5df1\u80c6\u602f\u3002 \u3000\u3000\u94c1\u73ca\u745a\u89c1\u91d1\u5343  \u4e00\u6b65\u53c8\u8d70\u56de\u6765\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u53c8\u53eb\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff01\u201d\u91d1\u5343  \u867d\u7136\u6253\u5b9a\u4e3b \u610f\uff0c\u60ca\u5f13\u4e4b\u9e1f\uff0c\u95fb\u58f0\u4ecd\u662f\u4e00\u7a92\uff0c\u4e3e\u5934\u56db\u671b\uff0c\u5ffd\u7136\u5fae\u98ce\u98d2\u7136\uff0c\u6025\u5fd9\u628a\u638c\u4e00\u626c\uff0c\u53eb\u9053\uff1a\u201c\u9f20\u8f88\u4f11 \u653e\u6697\u5668\uff01\u201d\u4e00\u638c\u51fb\u51fa\uff0c\u5ffd\u7136\u60e8\u53eb\u4e00\u58f0\u6eda\u5728\u5730\u4e0a\uff01\u5cb3\u9e23\u73c2\u500f\u7684\u4ece\u5ca9\u77f3\u5f8c\u73b0\u51fa\u8eab\u6765\u3002 \u3000\u3000\u539f\u6765\u5cb3\u545c\u73c2\u7b2c\u4e00\u7c92\u6ce5\u4e38\uff0c\u672c\u60f3\u4e00\u4e0b\u5c06\u91d1\u5343  \u51fb\u5012\uff0c\u90a3\u77e5\u91d1\u5343  \u6b66\u529f\u9887\u6709\u6839\u67e2\uff0c\u867d\u88ab\u51fb\u4e2d \u8109\u95e8\u8fd8\u80fd\u5fcd\u53d7\u3002\u5cb3\u545c\u73c2\u6bd2\u4f24\u521a\u521a\u597d\u8f6c\uff0c\u4e0d\u6562\u65bd\u5c55\u8f7b\u529f\u53bb\u8ffd\uff0c\u770b\u770b\u5c31\u8981\u88ab\u4ed6\u9003\u53bb\u3002\u53ef\u7b11\u91d1\u5343 \u7591\u795e\u7591\u9b3c\uff0c\u5fc3\u4e2d\u53ea\u6015\u4e00\u4e2a\u7389\u7f57\u5239\uff0c\u5374\u4e0d\u77e5\u5cb3\u545c\u73c2\u6b66\u529f\u6bd4\u7389\u7f57\u5239\u8fd8\u8981\u5389\u5bb3\u3002\u4ed6\u518d\u8d70\u56de\u6765\u65f6\uff0c\u5cb3 \u545c\u73c2\u5df2\u634f\u4e86\u53c1\u7c92\u6ce5\u4e38\uff0c\u53c8\u62fe\u4e86\u4e24\u6bb5\u67af\u679d\uff0c\u540c\u65f6\u53d1\u51fa\u3002\u91d1\u5343  \u53f3\u773c\u7ed9\u67af\u679d\u5c04\u5165\uff0c\u5982\u4e2d\u5229\u7bad\uff0c\u987f \u65f6\u8840\u6d41\u6ee1\u9762\uff0c\u6eda\u5730\u72c2\u55e5\uff01 \u3000\u3000  \u9ed1\u5b50\u90a3\u73ed\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u5175\u5203\u7eb7\u4e3e\uff0c\u5cb3\u9e23\u73c2\u4e00\u58f0\u957f\u7b11\uff0c\u6e38\u9f99\u5251\u500f\u7136\u51fa\u9798\uff0c\u56db\u4e0b\u4e00\u6c64\uff0c\u53ea \u542c\u5f97\u4e00\u7247\u94ff\u9535\u4e4b\u58f0\uff0c\u6240\u6709\u5175\u5203\uff0c\u5168\u7ed9\u524a\u65ad\uff01  \u9ed1\u5b50\u987e\u4e0d\u5f97\u75bc\u75db\uff0c\u6eda\u4e0b\u5c71\u5761\u3002\u91d1\u5343  \u5fcd\u75db\u8df3 \u8d77\uff0c\u5cb3\u9e23\u73c2\u5251\u950b\u5df2\u6307\u5411\u4ed6\u7684\u54bd\u5589\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u4f60\u662f\u91d1\u72ec\u5f02\u7684\u4ec0\u9ebd\u4eba\uff1f\u201d\u91d1\u5343  \u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u53d4\u53d4\u3002\u201d\u4ed6\u4eec\u4e24\u53d4\u4f84\u76f8\u5dee \u4e0d\u5230\u5341\u5c81\u3002\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u597d\u54c7\uff0c\u53eb\u4f60\u53d4\u53d4\u628a\u5251\u8c31\u62ff\u6765\u5c06\u4f60\u8d4e\u56de\u3002\u201d\u91d1\u5343  \u9053\uff1a\u201c\u4ec0\u9ebd\u5251 \u8c31\uff1f\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u4f60\u8fd8\u88c5\u4ec0\u9ebd\u849c\uff1f\u7389\u7f57\u5239\u7684\u5251\u8c31\u5462\uff1f\u201d\u91d1\u5343  \u9053\uff1a\u201c\u54a6\uff0c\u7389\u7f57\u5239\u7684\u5251\u8c31\u4e0e \u4f60\u6709\u4ec0\u9ebd\u76f8\u5e72\uff1f\u201d\u5cb3\u9e23\u73c2\u5251\u950b\u4e00\u70b9\uff0c\u8f6c\u89d2\u5c71\u5773\u5904\u5ffd\u7136\u5954\u51fa\u4e00\u4eba\uff0c\u53eb\u9053\uff1a\u201c\u628a\u4eba\u653e\u5f00\uff0c\u7ed9\u4f60\u5251 \u8c31\uff01\u201d \u3000\u3000\u5cb3\u545c\u73c2\u5de6\u638c\u4e00\u63a8\uff0c\u5c06\u91d1\u5343  \u63a8\u5012\u5730\u4e0a\uff0c\u6863\u5251\u5f85\u654c\uff0c\u53ea\u89c1\u91d1\u72ec\u5f02\u8dd1\u4e86\u51fa\u6765\uff0c\u72de\u7b11\u8bf4\u9053\uff1a \u201c\u54fc\uff0c\u4f60\u771f\u662f\u5730\u72f1\u65e0\u95e8\u504f\u8fdb\u6765\uff01\u6765\uff0c\u6765\uff0c\u6765\uff01\u5251\u8c31\u5c31\u5728\u8fd9\u91cc\uff0c\u6709\u672c\u4e8b\u7684\u6765\u62ff\uff01\u201d \u3000\u3000\u4f60\u9053\u91d1\u72ec\u5f02\u4f55\u4ee5\u9002\u624d\u88ab\u5cb3\u545c\u73c2\u8ffd\u8d76\u65f6\u4e0d\u6562\u52a8\u624b\uff0c\u73b0\u5728\u5374\u53eb\u9635\u6765\u4e86\uff1f\u539f\u6765\u4ed6\u4e2d\u4e86\u5c0a\u80dc\u4e00 \u62f3\uff0c\u53d7\u4e86\u5185\u4f24\uff0c\u6240\u4ee5\u4e0d\u6562\u63a5\u62db\uff0c\u5230\u6446\u8131\u4e86\u5cb3\u9e23\u73c2\u4e4b\u5f8c\uff0c\u4e5f\u50cf\u5cb3\u545c\u73c2\u4e00\u6837\uff0c\u62e9\u5730\u9759\u5750\uff0c\u8fd0\u6c14\u8c03 \u5143\uff0c\u76f4\u8fc7\u4e86\u4e00\u4e2a\u66f4\u6b21\u624d\u80fd\u6c14\u8fbe\u56db\u80a2\uff0c\u8840\u8109\u8212\u7545\u3002\u4ed6\u672c\u6765\u548c\u4f84\u513f\u7ea6\u597d\u5728\u6b64\u76f8\u89c1\uff0c\u6240\u4ee5\u5185\u4f24\u5e73\u670d \u4e4b\u5f8c\uff0c\u4fbf\u6025\u6025\u8d76\u6765\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u597d\uff0c\u6211\u6b63\u8981\u4e0e\u4f60\u518d\u51b3\u4e00\u6218\uff0c\u6709\u79cd\u7684\u4e0d\u8981\u9003\u4e86\uff01\u201d\u624b\u8155\u4e00\u7ffb\uff0c\u6e38\u9f99\u5251\u500f\u7684\u523a \u51fa\uff0c\u91d1\u72ec\u5f02\u8eab\u5f62\u4e00\u8f6c\uff0c\u8fd8\u4e86\u4e00\u638c\uff0c\u4e24\u4eba\u5c31\u5728\u5c71\u5761\u4e0a\u6076\u6597\u8d77\u6765\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u6015\u4ed6\u7684\u6bd2\u7802\u638c\u5389\u5bb3\uff0c\u5251\u5f0f\u5c55\u5f00\uff0c\u4fe8\u5982\u66b4\u98ce\u9aa4\u96e8\uff0c\u53eb\u4ed6\u4e0d\u6562\u6b3a\u8fd1\u8eab\u524d\u3002\u91d1\u72ec\u5f02\u4e5f\u6015 \u4ed6\u7684\u5b9d\u5251\u5389\u5bb3\uff0c\u53ea\u662f\u5728\u5251\u5149\u7f1d\u4e2d\uff0c\u94bb\u6765\u94bb\u53bb\uff0c\u4f3a\u9699\u53d1\u638c\u3002 \u3000\u3000\u6218\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5cb3\u9e23\u73c2\u4e00\u5251\u5feb\u4f3c\u4e00\u5251\uff0c\u94c1\u73ca\u745a\u5728\u5ca9\u77f3\u4e0a\u671b\u4e0b\uff0c\u53ea\u89c1\u91d1\u72ec\u5f02\u5c31\u4f3c\u88ab\u88f9\u5728\u5251 \u5149\u4e4b\u4e2d\uff0c\u94c1\u73ca\u745a\u6697\u6697\u60ca\u5947\uff0c\u5bf9\u5cb3\u545c\u73c2\u5341\u5206\u4f69\u670d\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u8fd9\u8def\u5251\u6cd5\u4e43\u5929\u5c71\u5251\u4e2d\u7684\u8ffd\u98ce\u5251\u6cd5\uff0c\u8fc5\u6377\u65e0\u4f26\u3002\u8fd9\u8fd8\u662f\u4ed6\u7b2c\u4e00\u6b21\u4f7f\u7528\uff0c\u65bd\u5c55\u5f00\u6765\uff0c \u679c\u7136\u628a\u91d1\u72ec\u5f02\u8feb\u5f97\u8fde\u8fde\u5f8c\u9000\u3002\u5cb3\u545c\u73c2\u5927\u559c\uff0c\u5fc3\u60f3\u5e08\u7236\u4e8c\u5341\u5e74\u6765\u7684\u5fc3\u8840\u679c\u7136\u6ca1\u6709\u767d\u82b1\uff0c\u6240\u521b\u7684 \u5929\u5c71\u5251\u6cd5\u53ea\u6b64\u4e00\u8def\u4f7f\u53ef\u65e0\u654c\u65bc\u5929\u4e0b\u3002\u91d1\u72ec\u5f02\u95ea\u5c55\u817e\u632a\uff0c\u5f62\u52bf\u8d8a\u6765\u8d8a\u9669\u3002\u5cb3\u9e23\u73c2\u5927\u58f0\u559d\u9053\uff1a \u201c\u5feb\u628a\u5251\u8c31\u8fd8\u6765\uff01\u201d \u3000\u3000\u91d1\u72ec\u5f02\u84e6\u7136\u4e00\u58f0\u602a\u5578\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u53eb\u4f60  \u70b9\u5389\u5bb3\uff0c\u4f60\u8fd8\u4ee5\u4e3a\u8001\u592b\u771f\u7684\u6015\u4f60\uff01\u201d\u638c\u6cd5 \u9aa4\u53d8\uff0c\u51f6\u608d\u4e4b\u6781\uff0c\u6bcf\u4e00\u638c\u90fd\u631f\u7740\u52b2\u98ce\uff0c\u547c\u547c\u4f5c\u54cd\u3002\u5cb3\u9e23\u73c2\u7684\u5251\u70b9\u7adf\u7ed9\u9707\u6b6a\uff0c\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\u3002 \u518d\u6218\u7247\u523b\uff0c\u5ffd\u7136\u53c8\u89c9\u53e3\u4e2d\u7126\u6e34\uff0c\u5fc3\u8eab\u70e6\u8e81\u3002\u539f\u6765\u8fd9\u8ffd\u98ce\u5251\u6cd5\u5168\u662f\u653b\u7740\uff0c\u6700\u8017\u6c14\u529b\uff0c\u5cb3\u9e23\u73c2\u6bd2 \u4f24\u521a\u521a\u597d\u8f6c\uff0c\u7ecf\u4e86\u8fd9\u573a\u6fc0\u6597\uff0c\u987f\u65f6\u53c8\u53d1\u4f5c\u8d77\u6765\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u6697\u81ea\u53eb\u82e6\uff0c\u4f46\u4ed6\u5374\u4e0d\u77e5\uff0c\u91d1\u72ec\u5f02\u6bd4\u4ed6\u8fd8\u8981\u96be\u53d7\u3002\u91d1\u72ec\u5f02\u4e2d\u4e86\u5c0a\u80dc  \u5e08\u7684\u5c11\u6797\u62f3\uff0c \u867d\u4ed7\u7740\u529f\u529b\u6df1\u539a\uff0c\u8fd0\u6c14\u8c03\u5143\uff0c\u6682\u65f6\u6b62\u4f4f\uff0c\u4f46\u5185\u4f24\u5230\u5e95\u8fd8\u672a\u75ca\u6108\u3002\u8fd9\u4e00\u6765\uff0c\u4e3a\u4e86\u8981\u62b5\u5fa1\u5cb3\u545c\u73c2 \u8fc5\u6377\u65e0\u4f26\u7684\u8ffd\u98ce\u5251\u6cd5\uff0c\u5f3a\u7528\u5185\u5bb6\u771f\u529b\uff0c\u867d\u7136\u6682\u65f6\u62a2\u4e86\u4e0a\u98ce\uff0c\u4e94\u810f\u516d\u8151\u90fd\u53d7\u9707\u52a8\uff0c\u8fc7\u4e86\u7247\u523b\uff0c \u8ddf\u524d\u5df3\u89c9\u6a21\u7cca\u3002\u9163\u6597\u58f0\u4e2d\uff0c\u5cb3\u545c\u73c2\u731b\u53d1\u4e00\u5251\uff0c\u91d1\u72ec\u5f02\u542c\u98ce\u8fa8\u5668\uff0c\u4e00\u638c\u5288\u53bb\uff0c\u5c06\u4ed6\u5251\u70b9\u9707\u5f00\uff0c \u5de6\u624b\u4e00\u52fe\uff0c\u53d8\u5927\u64d2\u62ff\u624b\u6cd5\uff0c\u4e00\u628a\u6293\u7740\u4e86\u5cb3\u9e23\u73c2\u624b\u8155\uff01\u5cb3\u545c\u73c2\u987f\u65f6\u5168\u8eab  \u8f6f\uff0c\u672c\u80fd\u7684\u5c06\u5251\u8f6c\u950b \u4e0b\u6233\u3002\u4e0d\u60f3\u8fd9\u4e00\u5251\u5374\u594f\u4e86\u5947\u529f\u3002\u539f\u6765\u91d1\u72ec\u5f02\u5185\u4f24\u53d1\u4f5c\uff0c\u773c\u775b\u5df2\u4e0d\u80fd\u89c6\u7269\uff0c\u5cb3\u9e23\u73c2\u56e0\u6c14\u529b\u6d88 \u5931\uff0c\u8fd9\u4e00\u5251\u53c8\u6162\u53c8\u8f7b\uff0c\u91d1\u72ec\u5f02\u542c\u4e0d\u51fa\u6765\u8e2a\u53bb\u8ff9\uff0c\u7adf\u7136\u7ed9\u4e00\u5251\u523a\u5728\u80ef\u9aa8\u4e4b\u4e0a\uff0c\u6e38\u9f99\u5251\u950b\u5229\u5f02 \u5e38\uff0c\u867d\u7136\u529b\u5ea6\u751a\u8f7b\uff0c\u4e5f\u5df2\u624e\u5230\u9aa8\u5934\u91cc\u53bb\uff01\u91d1\u72ec\u5f02\u4e00\u58f0\u5927\u543c\uff0c\u547c\u547c\u4e24\u638c\uff0c\u72ec\u529b\u53d1\u51fa\uff0c\u5cb3\u545c\u73c2\u624b \u8155\u88ab\u4eba\u62ff\u7740\uff0c\u65e0\u6cd5\u95ea\u8eb2\uff0c\u4e24\u638c\u5168\u88ab\u6253\u4e2d\uff0c\u987f\u65f6\u50cf\u629b\u7ee3\u7403\u4e00\u6837\uff0c\u8eab\u5b50\u817e\u7a7a\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u76f4\u8dcc\u4e0b \u6765\uff01 \u3000\u3000\u94c1\u73ca\u745a\u89c1\u72b6\u5927\u60ca\uff0c\u6025\u5fd9\u4e00\u8dc3\u800c\u524d\uff0c\u5f20\u624b\u4e00\u63a5\uff0c\u6070\u6070\u628a\u5cb3\u545c\u73c2\u63a5\u5728\u6000\u4e2d\u3002\u5cb3\u9e23\u73c2\u201c\u54c7\u201d\u7684 \u4e00\u53e3\u9c9c\u8840\u55b7\u4e86\u51fa\u6765\uff0c\u5636\u58f0\u53eb\u9053\uff1a\u201c\u5feb\u53bb\u62fe\u90a3\u628a\u5b9d\u5251\uff01\u201d\u94c1\u73ca\u745a\u9762\u8272\u72b9\u8c6b\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u9ebd \u6837\uff1f\u201d\u5cb3\u9e23\u73c2\u6012\u9053\uff1a\u201c\u5feb\u53bb\uff0c\u5feb\u53bb\uff01\u201d \u3000\u3000\u91d1\u72ec\u5f02\u4e24\u638c\u6253\u51fa\uff0c\u4eba\u4e5f\u6655\u6b7b\u8fc7\u53bb\u3002\u91d1\u5343  \u778e\u4e86\u4e00\u773c\uff0c\u53c8\u53d7\u4e86\u5cb3\u9e23\u73c2\u4e00\u638c\uff0c\u4e5f\u662f\u529b\u7aed\u7b4b \u75b2\uff0c\u4f46\u8fd8\u80fd\u79fb\u8d70\u52a8\u3002\u8fd9\u65f6\u89c1\u53d4\u7236\u6655\u5728\u5730\u4e0a\uff0c\u62da\u547d\u8fc7\u6765\u62a2\u6551\u3002\u94c1\u73ca\u745a\u62ac\u8d77\u5b9d\u5251\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u821e \u8d77\u4e00\u9053\u94f6\u8679\uff0c\u4fe1\u624b\u4e00\u5251\uff0c\u628a\u9644\u8fd1\u7684\u5ca9\u77f3\u65a9\u5f97\u706b\u82b1\u56db\u6e85\uff0c\u77f3\u5c51\u7eb7\u98de\u3002\u5979\u662f\u6015\u91d1\u5343  \u5411\u5979\u8fdb\u51fb\uff0c \u6240\u4ee5\u4ee5\u5251\u793a\u5a01\u3002\u4e0d\u77e5\u91d1\u5343\u6309\u5df2\u662f\u529b\u7aed\u7b4b\u75b2\uff0c\u751f\u6015\u94c1\u73ca\u745a\u5bfb\u4ed6\u6666\u6c14\uff0c\u4ed6\u628a\u53d4\u7236\u4e00\u62b1\uff0c\u7acb\u523b\u6eda\u4e0b \u5c71\u5761\u3002 \u3000\u3000\u9002\u624d\u5cb3\u3001\u91d1\u4e8c\u4eba\u9163\u6597\u4e4b\u65f6\uff0c  \u9ed1\u5b50\u7684\u4eba\u5168\u5df2\u9003\u8d70\uff0c\u8fd9\u65f6\u592a\u5ba4\u5c71\u9e93\uff0c\u53ea\u5269\u4e0b\u5cb3\u9e23\u73c2\u548c\u94c1\u73ca \u745a\u4e8c\u4eba\uff0c\u94c1\u73ca\u745a\u8d70\u4e86\u56de\u6765\uff0c\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u628a\u6211\u6276\u8d77\u3002\u201d\u968f\u5373\u76d8\u819d\u9759\u5750\uff0c\u5636\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u5148\u8d70 \u5427\uff01\u201d\u94c1\u73ca\u745a\u4e0d\u7406\uff0c\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u63d0\u9632\u654c\u4eba\u518d\u6765\u3002\u4f60\u5148\u8d70\uff01\u5230\u5c11\u6797\u5bfa\u53bb\u62a5\u8baf\uff01\u201d\u94c1\u73ca\u745a\u5927\u4e3a \u611f\u52a8\uff0c\u5fc3\u60f3\u4ed6\u8eab\u53d7\u91cd\u4f24\uff0c\u5374\u8fd8\u5148\u5ff5\u7740\u6211\u3002\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u4e0d\u542c\u6211\u8bdd\uff01\u201d\u94c1\u73ca\u745a\u4e00\u5411\u5c0f\u5b69 \u5fc3\u6027\uff0c\u82e5\u5728\u5e73\u65f6\u6709\u4eba\u7528\u8fd9\u6837\u53e3\u543b\u5411\u5979\u8bf4\u8bdd\uff0c\u5979\u4e00\u5b9a\u8981\u53d1\u813e\u6c14\u3002\u73b0\u5728\u5374\u6cea\u627f\u53cc\u776b\uff0c\u67d4\u58f0\u7b54\u9053\uff1a \u201c\u6211\u542c\u7740\u5462\uff0c\u6211\u73b0\u5728\u5c31\u53bb\uff01\u201d \u3000\u3000\u5cb3\u545c\u73c2\u9759\u5750\u8fd0\u6c14\uff0c\u4f46\u56e0\u4f24\u5f97\u592a\u91cd\uff0c\u90a3\u80a1\u6c14\u52b2\u65e0\u6cd5\u8fd0\u8f6c\u81ea\u5982\uff0c\u5750\u4e86\u4e00\u4f1a\uff0c\u5929\u8272\u5df2\u4eae\uff0c\u7741\u773c \u4e00\u770b\uff0c\u53ea\u89c1\u94c1\u73ca\u745a\u62ff\u7740\u5b9d\u5251\uff0c\u5728\u67cf\u6811\u4e0b\u7ad9\u7740\uff0c\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u4e0d\u53bb\uff1f\u201d\u94c1\u73ca\u745a\u8df3\u8dc3\u8d77 \u6765\uff0c\u561f\u7740\u5c0f\u5634\u513f\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u4eba\u600e\u9ebd\u4e0d\u8bb2\u7406\u7684\uff01\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u6211\u600e\u9ebd\u4e0d\u8bb2\u7406\uff1f\u201d\u94c1\u73ca\u745a \u9053\uff1a\u201c\u4f60\u6551\u4e86\u6211\u7684\u6027\u547d\uff0c\u4e3a\u4ec0\u9ebd\u4e0d\u8bb8\u6211\u5c3d\u70b9\u5fc3\u4e8b\uff0c\u7ed9\u4f60\u5b88\u62a4\u3002\u96be\u9053  \u8bb8\u4f60\u4e00\u4e2a\u4eba\u505a\u4fa0\u58eb \u9ebd\uff1f\u201d\u5cb3\u9e23\u73c2\u65e0\u8bdd\u53ef\u7b54\uff0c\u8bd5\u7740\u8fd0\u52a8\u56db\u80a2\uff0c\u53ea\u89c9\u75bc\u75db\u96be\u5f53\uff0c\u5168\u8eab\u9aa8\u5934\u90fd\u50cf\u677e\u6563\u4e86\u4e00\u822c\u3002\u94c1\u73ca\u745a \u9053\uff1a\u201c\u6211\u80cc\u4f60\u5230\u5c11\u6797\u5bfa\u53bb\u5427\u3002\u201d\u5cb3\u545c\u73c2\u770b\u5979\u4e00\u773c\uff0c\u60f3\u8d77\u5979\u662f\u5973\u626e\u7537\u88c5\uff0c\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d \u5fc5\uff01\u201d\u53c8\u9759\u5750\u8fd0\u6c14\u3002\u94c1\u73ca\u745a\u5fc3\u60f3\u600e\u9ebd\u8fd9\u4eba\u8fd9\u6837\u7231\u95f9  \u626d.\u5979\u4e00\u7247\u7eaf\u771f\uff0c\u5374\u4e0d\u77e5\u5cb3\u9e23\u73c2\u662f\u4e3a\u4e86 \u907f\u7537\u5973\u4e4b\u5acc\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u5750\u4e86\u597d\u4e45\uff0c\u4e0d\u4f46\u65e0\u6cd5\u8fd0\u6c14\u8c03\u5143\uff0c\u800c\u4e14\u547c\u5438\u4e5f\u60ed\u60ed\u56f0\u96be\u3002\u539f\u6765\u4ed6\u4e00\u665a\u6ca1\u5403\u4e1c\u897f\uff0c\u52a0 \u4e4b\u4f24\u52bf\u8fc7\u91cd\uff0c\u60f3\u7528\u5410\u7eb3\u7684\u6c14\u529f\u7597\u6cd5\u5df2\u4e0d\u80fd\u591f\u3002\u4ed6\u7741\u5f00\u773c\u775b\uff0c\u94c1\u73ca\u745a\u4ecd\u7136\u9759\u9759\u7684\u5b88\u5728\u8eab\u65c1\u3002\u5cb3 \u545c\u73c2\u53f9\u4e86\u53e3\u6c14\uff0c\u94c1\u73ca\u745a\u9053\uff1a\u201c\u8fd8\u662f\u6211\u80cc\u4f60\u53bb\u5427\uff1f\u201d\u5cb3\u9e23\u73c2\u4e0d\u4f5c\u58f0\u3002\u94c1\u73ca\u745a\u4e00\u7b11\u5c06\u4ed6\u80cc\u5728\u80cc \u4e0a\uff0c\u7f13\u7f13\u7684\u5411\u5c11\u6797\u5bfa\u884c\u53bb\u3002 \u3000\u3000\u4e14\u8bf4\u5c11\u6797\u5bfa\u7684\u76d1\u5bfa\u5c0a\u80dc  \u5e08\u867d\u7136\u4e5f\u4e2d\u4e86\u4e00\u638c\uff0c\u4f46\u4ed6\u529f\u529b\u6df1\u6e5b\uff0c\u72b9\u5728\u91d1\u72ec\u5f02\u4e4b\u4e0a\uff0c\u66f4\u517c\u6709 \u5c0f\u8fd8\u4e39\u5316\u6bd2\u8865\u6c14\uff0c\u8fc7\u4e86\u4e00\u665a\uff0c\u5df2\u662f\u65e0\u4e8b\u3002\u767d\u77f3\u9053\u4eba\u5144\u59b9\u89c1\u4ed6\u65e0\u4e8b\uff0c\u4e00\u65e9\u544a\u8f9e\u3002\u5353\u4e00\u822a\u9053\uff1a \u201c\u5cb3\u5927\u54e5\u4e0d\u77e5\u600e\u6837\uff0c\u600e\u9ebd\u8fd8\u672a\u56de\u6765\uff1f\u201d\u767d\u77f3\u9053\u4eba\u9053\uff1a\u201c\u6050\u6015\u4ed6\u8981\u8ffd\u51fa\u51e0\u5341\u91cc\u5916\uff0c\u624d\u80fd\u5c06\u90a3\u8001 \u602a\u8ffd\u83b7\u3002\u201d\u5c0a\u80dc\u4e5f\u9053\uff1a\u201c\u90a3\u8001\u602a\u4e2d\u4e86\u6211\u7684\u795e\u62f3\uff0c\u8c05\u975e\u5cb3\u65bd\u4e3b\u5bf9\u624b\u3002\u201d\u5353\u4e00\u822a\u653e\u4e0b\u4e86\u5fc3\uff0c\u4f46\u4ecd \u60f3\u7b49\u5cb3\u545c\u73c2\u56de\u6765\u3002\u53ef\u662f\u767d\u77f3\u9053\u4eba\u5df2\u7ecf\u544a\u8f9e\uff0c\u5353\u4e00\u822a\u81ea\u4e0d\u5f97\u4e0d\u968f\u4ed6\u53bb\u3002\u539f\u6765\u767d\u77f3\u9053\u4eba\u53e6\u6709\u6253 \u7b97\uff0c\u4ed6\u60f3\u5e26\u5973\u513f\u548c\u5353\u4e00\u822a\u4e00\u9053\u4e0a\u4eac\uff0c\u8ba9\u4ed6\u4e24\u4eba\u591a\u4e9b\u63a5\u89e6\u3002\u82e5\u6dfb\u591a\u4e86\u4e00\u4e2a\u5cb3\u9e23\u73c2\uff0c\u90a3\u5c31\u6ca1\u6709\u8fd9 \u9ebd\u7406\u60f3\u4e86\u3002 \u3000\u3000\u518d\u8bf4\u94c1\u73ca\u745a\u80cc\u7740\u5cb3\u545c\u73c2\uff0c\u884c\u5230\u5c11\u6797\u5bfa\u65f6\uff0c\u5df2\u662f\u4e2d\u5348\u65f6\u5206\u3002\u77e5\u5ba2\u50e7\u62a5\u4e86\u8fdb\u53bb\uff0c\u5c0a\u80dc\u60ee\u5e08\u4eb2 \u81ea\u6765\u63a5\uff0c\u89c1\u72b6\u5927\u60ca\uff0c\u6025\u95ee\u94c1\u73ca\u745a\u7ecf\u8fc7\u3002\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u65b9\u4e08\u5fc3\u6148\uff0c\u5012\u7ed9\u5cb3\u65bd\u4e3b\u6dfb\u4e86\u8bb8\u591a\u75db \u695a\u3002\u201d\u6025\u5c06\u5cb3\u9e23\u73c2\u5e26\u4eba\u9759\u5ba4\uff0c\u7528\u4e0a\u597d\u53c2\u6c64  \u4ed6\uff0c\u7136\u5f8c\u5c06\u53c1\u7c92\u5c0f\u8fd8\u4e39\u7ed9\u4ed6\u670d\u4e0b\u3002\u955c\u660e\u957f\u8001\u8fc7\u6765 \u63a2\u89c6\uff0c\u89c1\u94c1\u73ca\u745a\u5728\u65c1\u670d\u4f8d\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u4e0d\u5fc5\u4f60\u5728\u8fd9\u513f\u4e86\u3002\u201d\u94c1\u73ca\u745a\u6014\u4e86\u4e00\u6014\uff0c\u955c\u660e  \u5e08 \u9053\uff1a\u201c\u4ed6\u9759\u517b\u4e24\u5929\u4f7f\u597d\uff0c\u4f60\u5e26\u6211\u7684\u4e66\u672d\u5230\u592a\u5ba4\u5c71\u9876\u6148\u6167\u5e08\u592a\u90a3\u91cc\u6295\u5bbf\u5427\u3002\u4e24\u5929\u4e4b\u5f8c\u4f60\u518d\u5230\u5bfa \u95e8\u63a5\u4ed6\u3002\u201d\u94c1\u73ca\u745a\u77e5\u9053\u8fd9\u8001\u548c\u5c1a\u5df2\u770b\u7834\u81ea\u5df1\u884c\u85cf\uff0c\u674f\u9762\u98de\u7ea2\uff0c\u53d6\u4e86\u4e66\u672d\uff0c\u6025\u5fd9\u544a\u9000\u3002 \u3000\u3000\u94c1\u73ca\u745a\u53bb\u5f8c\uff0c\u5c0a\u80dc  \u5e08\u548c\u5e08\u5144\u8d70\u51fa\u9759\u5ba4\uff0c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u8fd9\u5cb3\u9e23\u73c2\u6b66\u529f\u7cbe\u5f3a\uff0c\u82f1\u534e\u5185\u8574\uff0c \u548c\u5353\u4e00\u822a\u7ad9\u5728\u4e00\u8d77\uff0c\u771f\u5982\u5e76\u751f\u7389\u6811\uff0c\u90fd\u662f\u5343\u767e\u5e74\u96be\u5f97\u4e00\u89c1\u7684\u4eba\u624d\u3002\u4f46\u60f3\u4e0d\u5230\u4ed6\u884c\u4e3a\u8fd9\u6837\u4e0d \u68c0\u3002\u51e0\u4e4e\u574f\u4e86\u6211\u5c11\u6797\u5bfa\u6e05\u89c4\u3002\u8981\u4e0d\u662f\u5e08\u5144\u770b\u51fa\u5979\u662f\u4e2a\u5973\u5b50\uff0c\u82e5\u7136\u7ed9\u5979\u5728\u6b64\u4e0e\u5cb3\u545c\u73c2\u540c\u5bbf\u4e00 \u5ba4\uff0c\u4f20\u51fa\u53bb\u5c82\u4e0d\u662f\u4e2a\u5929\u5927\u7b11\u8bdd\uff01\u201d\u955c\u660e\u957f\u8001\u5fae\u5fae\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u5012\u4e0d\u5bb3\u6015\u8fd9\u4e9b\uff01\u201d \u3000\u3000\u955c\u660e  \u5e08\u9053\uff1a\u201c\u4e8b\u6709\u7f13\u6025\u8f7b\u91cd\uff0c\u4ed6\u53d7\u4e86\u91cd\u4f24\uff0c\u7537\u7684\u5973\u7684\uff0c\u8c01\u9001\u4ed6\u6765\u90fd\u662f\u4e00\u6837\u3002\u5230\u4e86\u8fd9\u4e2a \u65f6\u5019\uff0c\u5c31\u4e0d\u5fc5\u987e\u4ec0\u9ebd\u7537\u5973\u4e4b\u5acc\u4e86\u3002\u82e5\u7136\u771f\u4e2a\u65e0\u4eba\u770b\u62a4\u65f6\u5c31\u540c\u5bbf\u4e00\u5ba4\u4e5f\u662f\u884c\u7684\u3002\u201d\u5c0a\u80dc\u9053\uff1a \u201c\u90a3\u9ebd\uff0c\u5e08\u5144\u4e3a\u4f55\u53f9\u6c14\uff1f\u201d\u955c\u660e\u9053\uff1a\u201c\u5cb3\u545c\u73c2\u9887\u6709\u6167\u6839\uff0c\u4e0d\u4f46\u53ef\u6210\u5251\u5ba2\uff0c\u800c\u4e14\u53ef\u4e3a\u9ad8\u50e7\u3002\u6211 \u53ea\u6015\u4ed6\u5815\u4eba\u60c5\u7f51\u5462\u3002\u201d \u3000\u3000\u4e0d\u8bf4\u955c\u660e\u957f\u8001\u5e08\u5144\u5f1f\u6697\u5730\u8c08\u8bba\uff0c\u4e14\u8bf4\u5cb3\u9e23\u73c2\u7ecf\u8fc7\u4e24\u5929\u8c03\u6cbb\uff0c\u679c\u7136\u4f24\u6bd2\u53bb\u51c0\uff0c\u9664\u4e86\u6c14\u529b\u8fd8 \u672a\u6062\u590d\u4e4b\u5916\uff0c\u7cbe\u795e\u5df2\u662f\u5982\u5e38\u3002\u7b2c\u53c1\u65e5\u6e05\u6668\uff0c\u955c\u660e\u957f\u8001\u5c06\u201c\u9f99\u6cc9\u767e\u70bc\u8bc0\u201d\u7684\u6284\u672c\u4ea4\u4e86\u7ed9\u4ed6\uff0c\u5631 \u5490\u4ed6\u9053\uff1a\u201c\u767e\u5343\u6cd5\u95e8\uff0c\u540c\u5f52\u65b9\u5bf8\uff0c\u6cb3\u6c99\u5999\u5fb7\uff0c\u603b\u5728\u5fc3\u6e90\u3002\u80fd\u65a9\u65e0\u660e\uff0c\u83e9\u63d0\u53ef\u8bc1\u3002\u201d\u5cb3\u9e23\u73c2\u62dc \u8f9e\u51fa\u5bfa\uff0c\u53ea\u89c1\u94c1\u73ca\u745a\u5df2\u5728\u5bfa\u95e8\u5916\u542b\u7b11\u5019\u4ed6\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u60f3\u8d77\u7ed9\u5979\u80cc\u6765\u4e4b\u4e8b\uff0c\u9887\u89c9\u5c34\u5c2c\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u6765\u4f5c\u751a\uff1f\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u4e00\u6765\u63a5\u4f60\uff0c \u4e8c\u6765\u5411\u4f60\u9053\u8c22\u3002\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u6211\u4e5f\u8981\u5411\u4f60\u9053\u8c22\u3002\u4f60\u53bb\u90a3\u91cc\uff1f\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u4f60\u53bb\u90a3\u91cc\uff1f\u201d \u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u6211\u53bb\u5317\u4eac\u3002\u201d\u94c1\u73ca\u745a\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u53bb\u5317\u4eac\u3002\u201d\u5cb3\u545c\u73c2\u695e\u4e86\u4e00\u695e\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u53bb \u5317\u4eac\uff1f\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u662f\u5440\uff0c\u54b1\u4eec\u6b63\u597d\u540c\u884c\u3002\u201d\u5cb3\u545c\u73c2\u65e0\u6cd5\u62d2\u7edd\uff0c\u53ea\u597d\u7b54\u5e94\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u8def\u5317\u884c\uff0c\u94c1\u73ca\u745a\u5929\u771f\u70c2\u6f2b\uff0c\u5cb3\u9e23\u73c2\u770b\u5979\u5bf9\u5f85\u81ea\u5df1\u6709\u5982\u5144\u957f\uff0c\u4f97\u4fc3\u4e0d\u5b89\u7684\u5fc3\u60c5\u4e5f\u4fbf \u60ed\u6e10\u6d88\u5931\u3002\u94c1\u73ca\u745a\u4ec0\u9ebd\u90fd\u8c08\uff0c\u53ea\u662f\u4e0d\u613f\u8c08\u53ca\u5979\u7684\u7236\u4eb2\uff0c\u5cb3\u9e23\u73c2\u597d\u751f\u5947\u602a\u3002 \u3000\u3000\u94c1\u73ca\u745a\u867d\u4f3c\u7ae5\u771f\u672a\u8131\uff0c\u53ef\u662f\u81ea\u5e7c\u968f\u7236\u4eb2\u8d70\u5357\u95ef\u5317\uff0c\u6c5f\u6e56\u8def\u9053\u5012\u8fd8\u5f88\u719f\u3002\u4ed6\u4eec\u4e00\u8def\u884c\u6765\uff0c \u65f6\u4e0d\u65f6\u89c1\u6709\u6c5f\u6e56\u4eba\u7269\u7b56\u9a6c\u5317\u4e0a\uff0c\u4e00\u65e5\u5230\u4e86\u6cb3\u5317\u7684\u90af\u90f8\uff0c\u8fd9\u662f\u4e00\u4e2a\u5927\u57e0\uff0c\u4e24\u4eba\u8d70\u4eba\u5e02\u533a\uff0c\u94c1\u73ca \u745a\u5ffd\u7136\u6084\u6084\u8bf4\u9053\uff1a\u201c\u524d\u9762\u90a3\u95f4\u9152\u697c\uff0c\u6709\u4e00\u4e2a\u9ed1\u5e2e\u7684\u5934\u5b50\u5728\u5185\u3002\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u4e0d\u8981\u591a\u7406\u95f2 \u4e8b\u3002\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u4f60\u966a\u6211\u8fdb\u53bb\u770b\u770b\u5427\uff0c\u8fd9\u4eba\u8f88\u4efd\u751a\u9ad8\uff0c\u6211\u4eec\u8fd9\u4e24\u5929\u78b0\u5230\u7684\u6c5f\u6e56\u4eba\u7269\uff0c\u6050\u6015 \u90fd\u8981\u5c0a\u4ed6\u4e3a\u957f\u5462\u3002\u201d\u5cb3\u545c\u73c2\u5947\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u77e5\u9053\uff1f\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u4f60\u770b\uff0c\u9152\u5bb6\u5899\u89d2\u663c\u6709\u4e00\u6735 \u6885\u82b1\uff0c\u4f60\u6570\u4e00\u6570\u6709\u51e0\u74e3\u82b1\u74e3\uff1f\u201d\u5cb3\u545c\u73c2\u884c\u8fd1\u4e00\u770b\uff0c\u9053\uff1a\u201c\u5341\u4e8c\u74e3\u3002\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u8fd9\u5c31\u662f \u4e86\u3002\u8fd9\u6735\u6885\u82b1\u4e43\u662f\u6697\u8bb0\uff0c\u4ee5\u82b1\u74e3\u7684\u591a\u5c11\u5b9a\u8f88\u4efd\u7684\u5c0a\u5351\uff0c\u6700\u591a\u7684\u662f\u5341\u53c1\u74e3\uff0c\u73b0\u5728\u8fd9\u6735\u6885\u82b1\u6709\u5341 \u4e8c\u74e3\uff0c\u5728\u6c5f\u6e56\u9053\u4e0a\u5df2\u7ecf\u662f\u975e\u5e38\u7f55\u89c1\u7684\u4e86\u3002\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u597d\u5427\uff0c\u90a3\u6211\u4eec\u5148\u8fdb\u53bb\u770b\u770b\uff0c\u4f46\u4f60\u53ef \u4e0d\u8bb8\u80e1\u4e71\u95f9\u4e8b\u3002\u201d \u3000\u3000\u4e24\u4eba\u4e0a\u4e86\u9152\u697c\uff0c\u62e3\u4e00\u526f\u5ea7\u4f4d\u5750\u4e0b\u3002\u5cb3\u545c\u73c2\u6e38\u76ee\u56db\u987e\uff0c\u5ffd\u89c1\u4e1c\u9762\u4e34\u7a97\u4e4b\u5904\uff0c\u6709\u4e24\u4e2a\u4eba\u5e3d\u5b50 \u6234\u5f97\u5f88\u4f4e\uff0c\u5176\u4e2d\u4e00\u4eba\uff0c\u7adf\u4f3c\u5728\u90a3\u513f\u89c1\u8fc7\u4f3c\u7684\u3002\u5cb3\u545c\u73c2\u5fc3\u5ff5\u4e00\u52a8\uff0c\u84e6\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u94c1\u73ca\u745a\u9053\uff1a \u201c\u5927\u54e5\uff0c\u4f60\u5e72\u4ec0\u9ebd\uff1f\u201d\u5cb3\u545c\u73c2\u62db\u624b\u53eb\u9053\uff1a\u201c\u5802\u500c\uff0c\u7ed9\u6211\u5148\u6ce1\u4e00\u58f6\u9f99\u4e95\u3002\u201d\u8d81\u52bf\u9065\u53d1\u4e00\u638c\uff0c\u90a3 \u4eba\u7684\u5e3d\u5b50\u98de\u4e86\u8d77\u6765\uff0c\u5cb3\u545c\u73c2\u7a81\u7136\u98de\u8fc7\u4e24\u4e2a\u5ea7\u4f4d\uff0c\u4e00\u624b\u6293\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u5e94\u4fee\u9633\u8001\u8d3c\u8ba4\u5f97\u6211 \u9ebd\uff1f\u201d\u90a3\u4eba\u500f\u7684\u53d6\u51fa\u4e00\u67c4\u62c2\u5c18\uff0c\u8fce\u7740\u5cb3\u545c\u73c2\u624b\u8155\u4e00\u7ed5\u3002\u94c1\u73ca\u745a\u5fc3\u4e2d\u5947\u9053\uff1a\u201c\u600e\u9ebd\u4ed6\u53eb\u6211\u4e0d\u95f9 \u4e8b\uff0c\u4ed6\u81ea\u5df1\u53cd\u95f9\u4e8b\u4e86\uff1f\u201d \u3000\u3000\u94c1\u73ca\u745a\u90a3\u91cc\u77e5\u9053\u8fd9\u4eba\u4e43\u79c1\u901a\u6ee1\u6d32\u7684\u5927\u5978\uff0c\u5f53\u5e74\u5728\u534e\u5c71\u7edd\u9876\u6446\u4e0b\u4e03\u7edd\u9635\u56f4\u653b\u7389\u7f57\u5239\u7684\u5934 \u5b50\u3002\u5cb3\u9e23\u73c2\u6697\u52a9\u7389\u7f57\u5239\u65f6\u66fe\u548c\u4ed6\u671d\u8fc7\u76f8\u3002 \u3000\u3000\u5e94\u4fee\u9633\u6b66\u529f\u867d\u7136\u6781\u9ad8\uff0c\u4f46\u89c1\u4e86\u5cb3\u9e23\u73c2\u5374\u6709\u602f\u610f\u3002\u5c18\u626b\u4e00\u4f5b\u4e0d\u4e2d\uff0c\u5cb3\u9e23\u73c2\u5de6\u638c\u5df2\u662f\u5288\u6765\uff0c \u5e94\u4fee\u9633\u5927\u543c\u4e00\u58f0\uff0c\u4e3e\u8d77\u684c\u5b50\u4e00\u6321\uff0c\u676f\u76d8\u9152\u83dc\uff0c\u9f50\u5411\u5cb3\u545c\u73c2\u98de\u6765\uff0c\u5cb3\u9e23\u73c2\u4e00\u8df3\u95ea\u8fc7\uff0c\u5e94\u4fee\u9633\u5df2 \u4ece\u7a97\u53e3\u8df3\u4e0b\u5927\u8857\u3002\u4ed6\u7684\u540c\u4f34\u4e0d\u77e5\u5389\u5bb3\uff0c\u4e0a\u6765\u62e6\u963b\uff0c\u7ed9\u5cb3\u545c\u73c2\u4e00\u628a\u6293\u7740\u5934\u76ae\uff0c\u63b7\u4e0b\u8857\u5fc3\u3002 \u3000\u3000\u5e94\u4fee\u9633\u521a\u521a\u8df3\u4e0b\uff0c\u5cb3\u9e23\u73c2\u5df2\u81ea\u5f8c\u8ffd\u6765\uff0c\u6e38\u9f99\u5251\u5bd2\u5149\u95ea\u95ea\uff0c\u8fde\u8fde\u8fdb\u51fb\u3002\u5e94\u4fee\u9633\u786c\u7740\u5934\u76ae\uff0c \u6325\u52a8\u62c2\u5c18\uff0c\u53cd\u8eab\u548c\u4ed6\u76f8\u6597\u3002 \u3000\u3000\u5e94\u4fee\u9633\u7684\u90a3\u67c4\u62c2\u5c18\u53ef\u4f5c\u4e94\u884c\u5251\u7528\uff0c\u53ef\u5f53\u95ed\u7a74\u53a5\u4f7f\uff0c\u53c8\u53ef\u7f20\u593a\u5200\u5251\uff0c\u62db\u6570\u672c\u6765\u795e\u5999\u3002\u4f46\u5cb3 \u9e23\u73c2\u7684\u5929\u5c71\u5251\u6cd5\u5251\u5251\u7cbe\u7edd\uff0c\u66f4\u517c\u6e38\u9f99\u5251\u6709\u65ad\u91d1\u5207\u7389\u4e4b\u80fd\uff0c\u76f8\u5f62\u4e4b\u4e0b\uff0c\u5e94\u4fee\u9633\u7684\u94c1\u62c2\u5c18\u9eef\u7136\u5931 \u8272\uff01 \u3000\u3000\u4e24\u4eba\u5728\u5927\u8857\u4e0a\u8fd9\u4e00\u6fc0\u6597\uff0c\u53ea\u5413\u5f97\u884c\u4eba\u8fdc\u907f\uff0c\u5546\u5e97\u5173\u95e8\uff0c\u5cb3\u9e23\u73c2\u4e00\u5251\u7d27\u4f3c\u4e00\u5251\uff0c\u6740\u5f97\u5e94\u4fee \u9633\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b\u529b\u3002\u6b63\u9163\u6218\u95f4\uff0c\u5ffd\u7136\u8857\u4e0a\u545c\u9523\u5f00\u9053\uff0c\u516b\u9a91\u5065\u9a6c\u524d\u5bfc\uff0c\u516b\u540d\u592a\u76d1 \u5728\u5f8c\u547c\u62e5\uff0c\u4e2d\u95f4\u4e00\u8f86\u5bab\u8f66\u3002\u5e94\u4fee\u9633\u5927\u53eb\u9053\uff1a\u201c\u5feb\u6765\u6349\u8fd9\u51f6\u5f92\uff01\u201d\u516b\u540d\u5bab\u5ef7\u4f8d\u536b\u9f50\u8df3\u4e0b\u9a6c\uff0c\u5411 \u5cb3\u9e23\u73c2\u56f4\u653b\u3002\u8fd9\u4e9b\u4eba\u4f3c\u548c\u5e94\u4fee\u9633\u5f88\u719f\uff0c\u7eb7\u7eb7\u548c\u4ed6\u62db\u547c\u3002\u5cb3\u9e23\u73c2\u4e00\u60f3\u4e0d\u597d\uff0c\u5bf9\u8fd9\u51e0\u540d\u4f8d\u536b\uff0c\u81ea \u5df1\u867d\u7136\u4e0d\u60e7\uff0c\u4f46\u81ea\u5df1\u662f\u718a\u7ecf\u7565\u6d3e\u9063\u56de\u4eac\u7684\u4f7f\u8005\uff0c\u82e5\u7136\u4e8b\u60c5\u95f9\u5927\u53ef\u6709\u4e0d\u4fbf\u3002\u865a\u6643\u4e00\u5251\u8f6c\u8eab\u4fbf \u9003\u3002\u90a3\u4e9b\u4eba\u8981\u8ffd\u4e5f\u8ffd\u4e0d\u53ca\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u8dd1\u8fc7\u4e24\u6761\u957f\u8857\uff0c\u94c1\u73ca\u745a\u5ffd\u7136\u5728\u89d2\u843d\u94bb\u51fa\uff0c\u7b11\u9053\uff1a\u201c\u600e\u9ebd\u4f60\u95f9\u4e8b\u4e86\uff1f\u201d\u5cb3\u545c\u73c2\u7b11 \u9053\uff1a\u201c\u4f60\u5012\u7cbe\u7075\uff0c\u5148\u5230\u8fd9\u91cc\u7b49\u6211\u3002\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u6253\u4e0d\u8fc7\u4ed6\u4eec\u561b\uff0c\u6211\u5f53\u7136\u5413\u5f97\u5148\u8dd1 \u4e86\u3002\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u4e0d\u662f\u6253\u4e0d\u8fc7\u2026\u2026\u201d\u94c1\u73ca\u745a\u7b11\u9053\uff1a\u201c\u6211\u548c\u4f60\u8bf4\u7b11\u5462\uff0c\u4f60\u7740\u6025\u4ec0\u9ebd\u3002\u6211\u77e5\u9053 \u4f60\u4e0d\u662f\u6253\u4e0d\u8fc7\uff0c\u662f\u6015\u90a3\u4e9b\u4f8d\u536b\u6765\u4e86\u3002\u4f60\u53ef\u77e5\u9053\u5bab\u8f66\u4e2d\u5750\u7684\u662f\u8c01\uff1f\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u94c1 \u73ca\u745a\u9053\uff1a\u201c\u662f\u4e2a\u5927\u4e2b\u5934\u3002\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u80e1\u8bf4\u3002\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u8c01\u4e2a\u9a97\u4f60\u3002\u5bab\u8f66\u4e2d\u5750\u7684\u662f\u7687 \u592a\u5b59\u4e73\u6bcd\u7684\u5973\u513f\uff0c\u6211\u521a\u521a\u6253\u542c\u6765\u7684\u3002\u7687\u592a\u5b59\u7684\u4e73\u6bcd\u53eb\u5ba2\u6c0f\u592b\u4eba\uff0c\u975e\u5e38\u5f97\u65b0\u4e3b\u7231\u5ba0\uff0c\u6240\u4ee5\u767b\u4f4d \u4e4b\u5f8c\uff0c\u7279\u522b\u6d3e\u4eba\u5230\u5979\u7684\u4e61\u4e0b\u63a5\u5979\u7684\u5973\u513f\u6765\u5462\u3002\u201d\u5cb3\u9e23\u73c2\u8bf4\u9053\uff1a\u201c\u4ec0\u9ebd\uff0c\u4f60\u8bf4\u4ec0\u9ebd\u65b0\u4e3b\uff1f\u201d\u94c1 \u73ca\u745a\u9053\uff1a\u201c\u8001\u7687\u5e1d\u5df2\u6b7b\u5566\uff0c\u73b0\u5728\u592a\u5b50\u5df2\u767b\u4e86\u4f4d\u3002\u201d\u5cb3\u545c\u73c2\u51fa\u4eac\u65f6\u8001\u7687\u5e1d\u5df2\u7ecf\u75c5\u91cd\uff0c\u4f46\u60f3\u4e0d\u5230 \u8fd9\u6837\u5feb\u4fbf\u6b7b\u3002\u5cb3\u545c\u73c2\u53f9\u4e86\u53e3\u6c14\uff0c\u94c1\u73ca\u745a\u9053\uff1a\u201c\u600e\u9ebd\uff0c\u8001\u7687\u5e1d\u5bf9\u4f60\u6709\u4ec0\u9ebd\u597d\u5904\uff0c\u4f60\u4e3a\u4ed6\u4f24\u5fc3\u8d77 \u6765\u4e86\uff1f\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u4e0d\u662f\u4e3a\u8001\u7687\u5e1d\u4f24\u5fc3\uff0c\u54ce\uff0c\u56fd\u5bb6\u5927\u4e8b\u4e0d\u8bf4\u4e5f\u7f62\u3002\u201d\u94c1\u73ca\u745a\u201c\u54fc\u201d\u4e86\u4e00\u58f0 \u9053\uff1a\u201c\u54e6\uff0c\u4f60\u5f53\u6211\u662f\u5c0f\u5b69\u5b50\uff0c\u8bf4\u6211\u4e0d\u914d\u542c\u56fd\u5bb6\u5927\u4e8b\u662f\u4e0d\u662f\uff1f\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u6837\u3002\u201d\u6b63 \u60f3\u8bf4\u65f6\uff0c\u5ffd\u89c1\u4e00\u961f\u5b98\u5175\u5728\u6a2a\u8857\u8d70\u51fa\u3002\u5cb3\u9e23\u73c2\u6025\u5fd9\u62c9\u4e86\u94c1\u73ca\u745a\u4fbf\u8dd1\u3002 \u3000\u3000\u4e24\u4eba\u76f4\u8dd1\u5230\u90ca\u5916\u624d\u6b62\u3002\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u54b1\u4eec\u95f9\u4e86\u8fd9\u4e00\u8d9f\u4e8b\uff0c\u53ef\u5f97\u8eb2\u7740\u70b9\u3002\u201d\u63a5\u7740\u8bf4\u9053\uff1a \u201c\u6211\u672c\u4ee5\u4e3a\u592a\u5b50\u8d24\u660e\uff0c\u4ed6\u767b\u4f4d\u5f8c\u4f1a\u52a0\u4ee5\u632f\u4f5c\u3002\u8c01\u77e5\u4ed6\u5374\u5982\u6b64\u884c\u4e8b\uff0c\u5ba0\u4fe1\u4e73\u6bcd\u4e00\u81f3\u5982\u65af\uff01\u4e71\u4e86 \u7956\u5b97\u6cd5\u5236\u4e5f\u8fd8\u7f62\u4e86\uff0c\u8fde\u90a3\u4e9b\u5978\u4eba\u4e5f\u7ed9\u6df7\u5230\u5bab\u4e2d\u4e86\u3002\u53ef\u60dc\u718a\u7ecf\u7565\u548c\u5353\u5144\u7684\u4e00\u7247\u82e6\u5fc3\u3002\u201d\u539f\u6765\u5353 \u4e00\u822a\u5728\u53d1\u73b0\u5bab\u4e2d\u4f8d\u536b\u6709\u5185\u5978\u4e4b\u5f8c\uff0c\u66fe\u6258\u5cb3\u545c\u73c2\u8f6c\u544a\u718a\u5ef7\u5f3c\u7980\u544a\u7687\u4e0a\uff0c\u4e91\u71d5\u5e73\u548c\u91d1\u5343  \u5c31\u662f\u60e7 \u6015\u4e1c\u7a97\u7f6a\u53d1\u9003\u51fa\u6765\u7684\u3002\u5e94\u4fee\u9633\u867d\u4e0d\u662f\u5bab\u4e2d\u536b\u58eb\uff0c\u4f46\u540d\u5b57\u4e5f\u66fe\u4e0a\u8fbe\u5929\u542c\u3002\u60f3\u4e0d\u5230\u8001\u7687\u5e1d\u6b7b\u5f8c\uff0c \u8fde\u5e94\u4fee\u9633\u4e5f\u6562\u516c\u7136\u51fa\u73b0\uff0c\u800c\u4e14\u4e0e\u5bab\u4e2d\u4f8d\u536b\u6709\u52fe\u7ed3\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u7ecf\u4e86\u8fd9\u6b21\u4e8b\u5f8c\uff0c\u4e00\u8def\u8c28\u614e\uff0c\u7ed5\u8fc7\u77f3\u5bb6\u5e84\u4fdd\u5b9a\u7b49\u5927\u57ce\uff0c\u6084\u6084\u8fdb\u4eba\u5317\u4eac\u3002\u5cb3\u545c\u73c2\u5e26\u4e86\u94c1 \u73ca\u745a\u5230\u718a\u5ef7\u5f3c\u597d\u53cb\u5175\u79d1\u7ed9\u4e8b\u4e2d\u201c\u5b98\u540d\u201d\u6768\u6d9f\u5bb6\u91cc\u53bb\u4f4f\u3002\u6253\u542c\u4e4b\u4e0b\uff0c\u624d\u77e5\u795e\u5b97\u7687\u5e1d\u6b7b\u4e86\u5df2\u4e00\u4e2a \u591a\u6708\uff0c\u592a\u5b50\u5e38\u6d1b\u5373\u4f4d\uff0c\u53f7\u4e3a\u5149\u5b97\u3002\u6768\u6d9f\u9053\uff1a\u201c\u8fd1\u6765\u4eac\u4e2d\u6709\u4e24\u4e2a\u5927\u65b0\u95fb\uff0c\u4e00\u4e2a\u662f\u592a\u5b50\u5373\u4f4d\u4e4b \u5f8c\uff0c\u5c31\u5f97\u4e86\u602a\u75c5\uff0c\u592a\u533b\u8bca\u65ad\u8bf4\u662f\u75e2\u75be\uff0c\u53ef\u662f\u6309\u75e2\u75be\u5f00\u65b9\uff0c\u5374\u4e0d\u89c1\u6548\u3002\u73b0\u5728\u4e00\u4e2a\u591a\u6708\u4e86\uff0c\u7687\u5e1d \u8fd8\u4e0d\u80fd\u5750\u671d\u3002\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u592a\u5b50\u672c\u66f9\u4e60\u6b66\uff0c\u8eab\u4f53\u7d20\u5065\uff0c\u600e\u9ebd\u5f97\u6b64\u602a\u75c5\u3002\u7b2c\u4e8c\u4ef6\u5462\uff1f\u201d\u6768\u6d9f \u9053\uff1a\u201c\u8fd1\u6765\u4eac\u57ce\u5e38\u62a5\u5c11\u5e74\u5931\u8e2a\uff0c\u5176\u4e2d\u8fd8\u6709\u5bcc\u5bb6\u5b50\u5f1f\u3002\u4e5d\u95e8\u63d0\u7763\u4e0b\u65e8\u4e25\u67e5\uff0c\u4e5f\u65e0\u7ed3\u679c\u3002\u4f60\u8bf4\u602a \u4e5f\u4e0d\u602a\u3002\u201d\u5cb3\u545c\u73c2\u5947\u9053\uff1a\u201c\u82e5\u662f\u5c11\u5e74\u5973\u5b50\u5931\u8e2a\uff0c\u8fd8\u53ef\u8bf4\u662f\u63a2\u82b1\u5927\u76d7\u6240\u4e3a\uff0c\u7537\u5b50\u5931\u8e2a\uff0c\u8fd9\u53ef\u771f \u662f\u602a\u4e86\u3002\u201d \u3000\u3000\u8c08\u4e86\u4e00\u9635\uff0c\u5cb3\u545c\u73c2\u95ee\u9053\uff1a\u201c\u718a\u7ecf\u7565\u7684\u6848\u5b50\u5462\uff1f\u201d\u6768\u6d9f\u9053\uff1a\u201c\u4f60\u4e0a\u6b21\u79bb\u4eac\u4e4b\u5f8c\uff0c\u4fbf\u6709\u51e0\u4e2a \u5fa1\u53f2\u4e0a\u672c\u7ae0\u5f39\u4ed6\u3002\u4e3b\u5176\u4e8b\u7684\u662f\u5175\u90e8\u4e3b\u4e8b\u5218\u56fd\u7f19\u548c\u5fa1\u53f2\u59da\u5b97\u6587\u3002\u5199\u594f\u647a\u7684\u662f\u5fa1\u53f2\u51af\u53c1\u5143\u3002\u201d\u5cb3 \u545c\u73c2\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u5218\u56fd\u7f19\u662f\u56e0\u4e3a\u6614\u5e74\u5728\u8fbd\u4e1c\u53c2\u8d5e\u519b\u52a1\uff0c\u8d2a\u6c61\u821e\u5f0a\uff0c\u7ed9\u718a\u7ecf\u7565\u594f\u660e\u7687\u4e0a\uff0c\u5c06\u4ed6 \u64a4\u56de\uff0c\u4ee5\u6b64\u6000\u6068\u5728\u5fc3\u3002\u90a3\u59da\u5b97\u6587\u66f4\u4e3a\u5351\u9119\uff0c\u4ed6\u5411\u6211\u4eec\u7ecf\u7565\u5927\u4eba\u6572\u8bc8\uff0c\u8981\u53c1\u4ef6\u6700\u597d\u7684\u7d2b\u8c82\uff0c\u4f60 \u77e5\u9053\u718a\u7ecf\u7565\u5b98\u6e05\u5982\u6c34\uff0c\u90a3\u4e70\u5f97\u8d77\u4e0a\u597d\u7d2b\u8c82\uff0c  \u5f97\u628a\u522b\u4eba\u9001\u6765\u8fd8\u672a\u7a7f\u8fc7\u7684\u4e00\u4ef6\u7d2b\u8c82\u8f6c\u9001\u7ed9\u4ed6\u3002 \u90a3\u59da\u5b97\u6587\u6697\u5730\u91cc\u8bf4\u6211\u4eec\u5927\u4eba\u770b\u4e0d\u8d77\u4ed6\u3002\u90a3\u51af\u53c1\u5143\u7684\u5e95\u7ec6\u6211\u5374\u4e0d\u77e5\uff0c\u4f46\u542c\u8bf4\u4ed6\u4e13\u4e0e\u6b63\u6d3e\u7684\u4e1c\u6797 \u515a\u4f5c\u5bf9\uff0c\u60f3\u6765\u4e5f\u4e0d\u662f\u597d\u4eba\u3002\u201d\u6768\u6d9f\u9053\uff1a\u201c\u8fd9\u4eba\u7684\u7b14\u5012\u771f\u5389\u5bb3\uff0c\u4ed6\u7684\u594f\u672c\u7adf\u7136\u5217\u4e3e\u4e86\u718a\u5ef7\u5f3c\u5341 \u4e00\u6761\u7f6a\u72b6\uff0c\u516b\u6761\u662f\u8bf4\u718a\u7ecf\u7565\u65e0\u8c0b\u8bef\u56fd\uff0c\u53c1\u6761\u8bf4\u4ed6\u6b3a\u541b\u7f54\u4e0a\uff1f\u201d\u5cb3\u545c\u73c2\u5927\u7b11\u9053\uff1a\u201c\u8fd9\u771f\u5947\u4e86\u3002 \u5c45\u7136\u8bf4\u718a\u7ecf\u7565\u65e0\u8c0b\u8bef\u56fd\uff0c\u90a3\u9ebd\u6ee1\u6d32\u5175\u88ab\u62d2\u5728\u5174\u4eac\u5916\uff0c\u8fd9\u662f\u8c01\u7684\u529f\u52b3\u3002\u718a\u7ecf\u7565\u6bcf\u6709\u5174\u9769\u5927\u4e8b\uff0c \u90fd\u6709\u594f\u647a\u5230\u4eac\u3002\u4ed6\u624b\u6371\u5175\u7b26\uff0c\u638c\u6709\u5c1a\u65b9\u5b9d\u5251\uff0c\u90fd\u4e0d\u6562\u81ea\u4e13\uff0c\u8fd9\u53c8\u600e\u80fd\u8bf4\u662f\u6b3a\u541b\u7f54\u4e0a\uff1f\u201d\u6768\u6d9f \u9053\uff1a\u201c\u6240\u4ee5\u8bf4\u90a3\u51af\u5fa1\u53f2\u7684\u7b14\u5389\u5bb3\uff0c\u98a0\u5012\u662f\u975e\uff0c\u6df7\u6dc6\u9ed1\u767d\uff0c\u8fd9\u6837\u7684\u6587\u7ae0\u53eb\u6211\u4eec\u5199\u7edd\u5bf9\u5199\u4e0d\u51fa \u6765\u3002\u201d\u505c\u4e86\u4e00\u505c\uff0c\u53c8\u9053\uff1a\u201c\u4e0d\u8fc7\u4f60\u4e5f\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u7687\u4e0a\u75c5\u4e86\u4e00\u4e2a\u591a\u6708\uff0c\u90a3\u594f\u7ae0\u4e5f\u6401\u5728\u90a3\u513f\u3002\u518d \u8bf4\u671d\u4e2d\u90aa\u6d3e\u867d\u591a\uff0c\u6b63\u4eba\u541b\u5b50\u4e5f\u8fd8\u4e0d\u5c11\u3002\u201d \u3000\u3000\u8fd9\u665a\u5cb3\u545c\u73c2\u6ee1\u6000\u6124\u6012\uff0c\u4e0d\u89c9\u501f\u9152\u6d47\u6101\uff0c\u996e\u5f97\u9169\u914a\u5927\u9189\u3002\u5230\u5929\u4eae\u65f6\u5ffd\u89c9\u6709\u4eba\u8eba\u5728\u8eab\u4fa7\uff0c\u5411 \u81ea\u5df2\u7684\u9888\u4e0a\u76f4\u5439\u51b7\u98ce\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u7ffb\u8eab\u4e00\u770b\uff0c\u539f\u6765\u5374\u662f\u94c1\u73ca\u745a\u3002\u5cb3\u545c\u73c2\u7b11\u9053\uff1a\u201c\u4e0d\u8981\u987d\u76ae\u3002\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u4e60\u6b66\u7684 \u4eba\u559d\u5f97\u5982\u6b64\u5927\u9189\uff0c\u719f\u7761\u5982\u6ce5\uff0c\u7ed9\u4eba\u884c\u5230\u8eab\u8fb9\u4e5f\u4e0d\u77e5\u9053\uff0c\u4f60\u7f9e\u4e5f\u4e0d\u7f9e\uff1f\u597d\u5728\u662f\u6211\uff0c\u82e5\u7136\u662f\u7ed9\u4ec0 \u9ebd\u5973\u63a2\u82b1\u8d3c\u628a\u4f60\u7ed1\u53bb\uff0c\u90a3\u624d\u7cdf\u5462\uff01\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u4ec0\u9ebd\u80e1\u8bf4\uff1f\u4f60\u4e0d\u542c \u6768\u5927\u4eba\u8bf4\u4eac\u57ce\u8fd1\u65e5\u5e38\u6709\u5c11\u5e74\u5931\u8e2a\u5417\uff1f\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u5973\u5b69\u513f\u5bb6\u53e3\u6ca1\u906e\u62e6\uff0c\u4f60\u518d\u4e71\u8bf4\uff0c\u6211\u53ef\u8981 \u6253\u4f60\u4e86\u3002\u201d\u94c1\u73ca\u745a\u4f38\u4f38\u820c\u55d4\u9053\uff1a\u201c\u597d\u5566\uff0c\u5c31\u662f\u6ca1\u6709\u5973\u63a2\u82b1\u8d3c\u4f60\u4e5f\u8be5\u8d77\u6765\u5566\u3002\u201d\u5cb3\u545c\u73c2\u4e00\u7b11\u8d77 \u5e8a\uff0c\u9053\uff1a\u201c\u6211\u4eca\u65e5\u53bb\u8bbf\u5353\u5144\uff0c\u6211\u770b\u4ed6\u4e5f\u5e94\u8be5\u5230\u4eac\u4e86\uff0c\u4f60\u7559\u5728\u5c4b\u91cc\u5427\u3002\u767d\u77f3\u9053\u4eba\u5bf9\u4f60\u4eec\u7236\u5973\u53ef \u80fd\u6000\u6709\u6210\u89c1\u3002\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u4f60\u53eb\u6211\u53bb\u6211\u4e5f\u4e0d\u53bb\uff0c\u6211\u770b\u5440\uff0c\u90a3\u5353\u4e00\u822a\u4e5f\u4e0d\u591f\u670b\u53cb\u3002\u201d\u5cb3\u545c\u73c2 \u62c9\u957f\u4e86\u9762\uff0c\u9053\uff1a\u201c\u600e\u9ebd\uff1f\u201d\u94c1\u73ca\u745a\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u4e86\u4f60\u7684\u597d\u670b\u53cb\u4f60\u751f\u6c14\u4e86\u4e86\u6211\u95ee\u4f60\uff0c\u4ed6\u82e5\u591f\u670b \u53cb\u7684\u8bdd\uff0c\u90a3\u665a\u5728\u5c11\u6797\u5bfa\u4e3a\u4ec0\u9ebd\u4e0d\u6765\u5e2e\u624b\u3002\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u4ed6\u8ffd\u4e0b\u6765\u5566\uff0c\u6ca1\u6709\u8ffd\u7740\u3002\u201d\u94c1\u73ca\u745a \u9053\uff1a\u201c\u5c31\u7b97\u6ca1\u6709\u8ffd\u7740\uff0c\u4e5f\u8be5\u7ee7\u7eed\u8ffd\u4e0b\u6765\u5462\u3002\u6211\u770b\u4ed6\u5bf9\u4f60\u5e76\u4e0d\u5173\u5fc3\u3002\u201d\u5cb3\u545c\u73c2\u607c\u9053\uff1a\u201c\u6211\u4e0d\u51c6 \u4f60\u8fd9\u6837\u4e71\u8bf4\u95f2\u8bdd\u3002\u201d\u94c1\u73ca\u745a\u89c1\u4ed6\u771f\u4e2a\u607c\u4e86\uff0e\uff0c\u6241\u7740\u5634\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e0d\u8bf4\u4fbf\u662f\u3002\u201d \u3000\u3000\u5cb3\u9e23\u73c2\u5403\u4e86\u65e9\u70b9\uff0c\u72ec\u81ea\u5230\u5927\u65b9\u5bb6\u80e1\u540c  \u897f\u4f1a\u9986\u53bb\u63a2\u671b\u5353\u4e00\u822a\u7684\u6d88\u606f\u3002\u8d70\u5230\u4e1c\u957f\u5b89\u8857\u65f6\uff0c \u5ffd\u6709\u4e00\u8f86\u9a6c\u8f66\u8fce\u9762\u9a70\u6765\uff0c\u9a6c\u8f66\u5468\u56f4\u9970\u6709\u9526\u7ee3\uff0c\u5341\u5206\u534e\u4e3d\u3002\u8f66\u4e0a\u5750\u6709\u4e24\u4e2a\u7a7f\u9ec4\u8863\u670d\u7684\u4eba\u3002\u9a6c\u8f66 \u6328\u8eab\u800c\u8fc7\uff0c\u5cb3\u9e23\u73c2\u4f9d\u7a00\u4f3c\u542c\u5f97\u8f66\u4e0a\u7684\u4eba\u8bf4\u9053\uff1a\u201c\u597d\u4e2a\u4fca\u7f8e\u5c11\u5e74\u3002\u201d\u5cb3\u5cd9\u73c2\u4e5f\u4e0d\u5728\u610f\uff0c\u8d70\u5230 \u897f\u4f1a\u9986\u4e00\u95ee\uff1a\u5353\u4e00\u822a\u679c\u7136\u524d\u4e24\u5929\u5c31\u5230\u4e86\u4eac\u57ce\uff0c\u4f4f\u5728\u4ed6\u7236\u6267\u540f\u90e8\u5c1a\u4e66\u6768  \u5bb6\u91cc\u3002\u5cb3\u545c\u73c2\u95ee\u4e86\u6768 \u7684\u5730\u5740\uff0c\u518d\u8dd1\u53bb\u95ee\uff0c\u6768  \u7684\u7ba1\u5bb6\u56de\u9053\uff1a\u201c\u5353\u5c11\u7237\u8fd9\u4e24\u5929\u5f88\u5fd9\uff0c\u6628\u5929\u8fdb\u5bab\u671d\u89c1\uff0c\u6ca1\u6709\u89c1\u7740\u7687 \u4e0a\u3002\u4eca\u5929\u53c8\u51fa\u53bb\u5566\u3002\u201d\u5cb3\u9e23\u73c2\u95ee\u9053\uff1a\u201c\u4ec0\u9ebd\u65f6\u5019\u56de\u6765\uff1f\u201d\u7ba1\u5bb6\u9053\uff1a\u201c\u90a3\u53ef\u4e0d\u77e5\u9053\u5566\uff01\u4f60\u665a\u4e0a \u8f6c\u6765\u770b\u770b\u5427\u3002\u201d \u3000\u3000\u5cb3\u545c\u73c2\u5fc3\u5934\u70e6\u95f7\uff0c\u8f9e\u4e86\u51fa\u6765\u3002\u6768  \u5e9c\u7b2c\u5c31\u5728\u7409\u7483\u5382\u4fa7\uff0c\u8fd9\u7409\u7483\u5382\u201c\u5730\u540d\u201d\u4e43\u5317\u4eac\u7740\u540d\u7684 \u5b57\u753b\u5e02\u573a\uff0c\u96c5\u58eb\u6587\u4eba\u4ee5\u53ca\u90a3\u5404\u65b9\u8d76\u8003\u7684\u58eb\u5b50\u548c\u4eac\u4e2d\u5b98\u5bb6\u5b50\u5f1f\u90fd\u559c\u5230\u90a3\u91cc\u6e9c\u8fbe\u3002\u5cb3\u545c\u73c2\u4fe1\u6b65\u8d70 \u53bb\uff0c\u5ffd\u89c1\u521a\u624d\u6240\u78b0\u5230\u7684\u90a3\u8f86\u534e\u4e3d\u9a6c\u8f66\u4e5f\u505c\u5728\u5e02\u573a\u4e4b\u5916\u3002\u8fd9\u65e5\u5929\u8272\u751a\u597d\uff0c\u4f46\u6765\u901b\u7684\u4eba\u5374\u5e76\u4e0d \u591a\u3002\u5cb3\u9e23\u73c2\u8d70\u8fdb\u6f31\u77f3\u658b\u6d4f\u89c8\u4e66\u753b\uff0c\u5de1\u89c6\u4e00\u904d\uff0c\u89c1\u73cd\u54c1\u4e5f\u5e76\u4e0d\u591a\uff0c\u968f\u624b\u62ff\u8d77\u4e00\u5e45\u6587\u5fb5\u660e\u7684\u82b1\u9e1f \u6765\u770b\uff0c\u65c1\u8fb9\u5ffd\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u5e45\u753b\u6709\u4ec0\u9ebd\u770b\u5934\uff1f\u201d\u5cb3\u545c\u73c2\u4e00\u770b\uff0c\u539f\u6765\u5c31\u662f\u9a6c\u8f66\u4e0a\u90a3\u4e24\u4e2a\u9ec4\u8863 \u6c49\u5b50\uff0c\u56e0\u9053\uff1a\u201c\u6587\u5fb5\u660e\u7684\u753b\u4e5f\u4e0d\u9519\u4e86\u3002\u201d\u4e00\u4e2a\u9ec4\u8863\u6c49\u5b50\u9053\uff1a\u201c\u6587\u5fb5\u660e\u662f\u56fd\u521d\u56db\u624d\u4e86\u4e4b\u4e00\uff0c\u4ed6 \u7684\u753b\u5f53\u7136\u4e0d\u80fd\u7b97\u574f\u3002\u4e0d\u8fc7\u8fd9\u4e00\u5e45\u663c\u5374\u7edd\u4e0d\u662f\u4ed6\u7684\u7cbe\u54c1\u3002\u5144\u53f0\u82e5\u559c\u597d\u4ed6\u7684\u753b\uff0c\u5c0f\u5f1f\u85cf\u6709\u4ed6\u548c\u8c22 \u65f6\u81e3\u5408\u4f5c\u7684\u201c\u8d64\u58c1\u80dc\u6e38\u5377\u201d\uff0c\u613f\u7ed9\u5144\u53f0\u9274\u8d4f\u3002\u201d\u8fd9\u5e45\u753b\u4e43\u6587\u5fb5\u660e\u665a\u5e74\u5f97\u610f\u4e4b\u4f5c\uff0c\u4e43\u662f\u753b\u4e2d\u7470 \u5b9d\u3002\u5cb3\u9e23\u73c2\u542c\u4e86\u4e00\u6014\uff0c\u5fc3\u60f3\u600e\u9ebd\u4ed6\u80af\u9080\u4e00\u4e2a\u964c\u751f\u4eba\u5230\u5bb6\u4e2d\u9274\u8d4f\u540d\u753b\u3002 \u3000\u3000\u90a3\u4e2a\u9ec4\u8863\u6c49\u5b50\u53c8\u9053\uff1a\u201c\u6709\u4e9b\u4eba\u5bb6\u4e2d\u85cf\u6709\u540d\u8d35\u5b57\u753b\uff0c\u4fbf\u89c6\u540c\u62f1\u58c1\uff0c\u4e0d\u80af\u793a\u4eba\u3002\u5c0f\u5f1f\u5374\u4e0d\u662f \u8fd9\u6837\u3002\u9aa8\u8463\u540d\u753b\u82e5\u65e0\u540c\u597d\u5171\u8d4f\uff0c\u90a3\u53c8\u6709\u4ec0\u9ebd\u610f\u601d\uff1f\u201d\u5cb3\u545c\u73c2\u5fc3\u60f3\u8fd9\u4eba\u5012\u96c5\u5f97\u53ef\u7231\uff0c\u53c8\u60f3\uff1a\u81ea \u5df2\u4e00\u8eab\u6b66\u529f\uff0c\u5c31\u7b97\u6709\u4ec0\u9ebd\u610f\u5916\uff0c\u4e5f\u4e0d\u60e7\u6015\u3002\u4e0d\u59a8\u5077\u534a\u65e5\u95f2\u5230\u4ed6\u5bb6\u91cc\u770b\u770b\u3002\u56e0\u9053\uff1a\u201c\u627f\u5144\u53f0\u5ba0 \u62db\uff0c\u5c0f\u5f1f\u4e5f\u5c31\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d\u4e92\u76f8\u901a\u540d\uff0c\u90a3\u4e24\u4e2a\u6c49\u5b50\u4e00\u4e2a\u59d3\u738b\u4e00\u4e2a\u59d3\u6797\uff0c\u4e0a\u4e86\u9a6c\u8f66\uff0c\u59d3\u6797\u7684\u53d6 \u51fa\u4e00\u4e2a\u7fe1\u7fe0\u9f3b\u70df\u58f6\uff0c\u9012\u7ed9\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u8fd9\u9f3b\u70df\u58f6\u6765\u81ea\u897f\u6d0b\uff0c\u5473\u9053\u4e0d\u9519\u3002\u201d\u5cb3\u545c\u73c2\u8c22\u9053\uff1a\u201c\u5c0f \u5f1f\u4fd7\u4eba\u65e0\u6b64\u55dc\u597d\u3002\u201d\u90a3\u59d3\u738b\u7684\u5374\u53d6\u51fa\u4e00  \u65e9\u70df\u888b\u6765\uff0c\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u5c0f\u5f1f\u4e0e\u70df\u9152\u65e0\u7f18\u3002\u201d\u5176\u5b9e \u9152\u4ed6\u662f\u559d\u7684\uff0c\u4e0d\u8fc7\u4ed6\u5728\u964c\u751f\u4eba\u524d\uff0c\u5c0f\u5fc3\u8c28\u614e\uff0c\u6240\u4ee5\u5982\u6b64\u8bf4\u6cd5\u3002\u59d3\u738b\u7684\u6c49\u5b50\u5927\u53e3\u5927\u53e3\u7684\u5438\u8d77\u70df \u6765\u3002\u5cb3\u545c\u73c2\u89c9\u70df\u5473\u96be\u95fb\uff0c\u751a\u662f\u8ba8\u538c\u3002\u90a3\u59d3\u738b\u7684\u5ffd\u7136\u8fce\u9762\u4e00\u53e3\u70df\u55b7\u6765\uff0c\u5cb3\u9e23\u73c2\u987f\u89c9\u8111\u80c0\u5934\u660f\uff0c \u559d\u9053\uff1a\u201c\u5e72\u9ebd\uff01\u201d\u59d3\u738b\u7684\u53c8\u662f\u4e00\u53e3\u6d53\u70df\u5288\u9762\u55b7\u6765\uff0c\u5cb3\u9e23\u73c2\u987f\u89c9\u5929\u65cb\u5730\u8f6c\uff0c\u4e00\u638c\u5288\u51fa\uff0c\u6012\u9053\uff1a \u201c\u9f20\u8f88\u6562\u65bd\u6697\u7b97\u3002\u201d\u90a3\u4e24\u4e2a\u6c49\u5b50\u65e9\u5df2\u8df3\u4e0b\u9a6c\u8f66\uff0c\u5cb3\u545c\u73c2\u4e00\u638c\u6253\u51fa\uff0c\u4eba\u4e5f\u6655\u5012\u8f66\u4e0a\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5cb3\u545c\u73c2\u60a0\u60a0\u9192\u8f6c\uff0c\u53ea\u89c9\u6697\u9999\u7f15\u7f15\uff0c\u9189\u9b42\u9165\u9aa8\uff0c\u5f20\u773c\u4e00\u770b\uff0c\u81ea\u5df1\u7adf\u7136\u662f \u8eba\u5728\u9526\u8925\u4e4b\u4e0a\uff0c\u8336\u51e0\u4e0a\u7089\u9999\u8885\u8885\uff0c\u8fd9\u623f\u95f4\u5e03\u7f6e\u5f97\u534e\u4e3d\u65e0\u4f26\uff0c\u6302\u7684\u7329\u7329\u6be1  \uff0c\u60ac\u7684\u662f\u5efa\u660c\u5b9d \u955c\u3002\u58c1\u4e0a\u9489\u6709\u4e00\u5e45\u753b\u5377\uff0c\u5c71\u6c34\u4eba\u7269\uff0c  \u7136\u6d6e\u52a8\uff0c\u5cb3\u9e23\u73c2\u773c\u5229\uff0c\u7ec6\u770b\u9898\u7b7e\uff0c\u7adf\u7136\u771f\u7684\u662f\u6587\u5fb5\u660e \u548c\u8c22\u65f6\u81e3\u5408\u4f5c\u7684\u201c\u8d64\u58c1\u80dc\u6e38\u201d\u3002\u5cb3\u9e23\u73c2\u7591\u5e7b\u7591\u68a6\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5ffd\u7136\u60f3\u8d77\u94c1\u73ca\u745a\u6240\u8bf4\u7684\u201c\u5973\u63a2 \u82b1\u8d3c\u201d\u3002\u5fc3\u60f3\uff1a\u96be\u9053\u771f\u7684\u5e94\u4e86\u5979\u7684\u8bdd\u4e86\uff1f\u4e00\u60f3\u4e4b\u5f8c\uff0c\u53c8\u6697\u7b11\u81ea\u5df1\u8352\u5510\uff1a\u201c\u63a2\u82b1\u5973\u8d3c\u201d\u90a3\u4f1a\u6709 \u8fd9\u6837\u534e\u4e3d\u65e0\u4f26\u7684\u623f\u95f4\u3002\u5cb3\u545c\u73c2\u8bd5\u4e00\u8f6c\u8eab\uff0c\u4f46\u89c9\u56db\u80a2  \u8f6f\u65e0\u529b\uff0c\u5fc3\u60f3\uff1a\u600e\u9ebd\u90a3\u51e0\u53e3\u70df\u8fd9\u6837\u5389 \u5bb3\uff0c\u4ee5\u81ea\u5df1\u7684\u529f\u592b\uff0c\u5c45\u7136\u7981\u53d7\u4e0d\u4f4f\uff1f\u6323\u624e\u5750\u8d77\uff0c\u76d8\u819d\u7528\u529f\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u60ed\u60ed\u8840\u8109\u6d41\u901a\uff0c\u767e\u9ab8 \u8212\u7545\u3002 \u3000\u3000\u518d\u8bf4\u5353\u4e00\u822a\u548c\u767d\u77f3\u9053\u4eba\u7236\u5973\u5230\u4e86\u4eac\u5e08\u4e4b\u5f8c\uff0c\u5353\u4e00\u822a\u4e3a\u4e86\u671d\u89c1\u65b9\u4fbf\uff0c\u4f4f\u5230\u5175\u90e8\u5c1a\u4e66\u6768  \u5bb6 \u91cc\u3002\u767d\u77f3\u9053\u4eba\u7236\u5973\u5219\u4f4f\u5728\u6b66\u5e08\u67f3\u897f\u94ed\u5bb6\u4e2d\u3002\u767d\u77f3\u9053\u4eba\u6bb7\u6bb7\u5631\u5490\u9053\uff1a\u201c\u4f60\u5927\u4e8b\u529e\u4e86\uff0c\u5c31\u8d76\u5feb\u56de \u5c71\uff0c\u53ef\u4e0d\u8981\u505a\u4ec0\u9ebd\u635e\u4ec0\u5b50\u5bab\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u201d \u3000\u3000\u4e0d\u6599\u5149\u5b97\u75c5\u5728\u6df1\u5bab\uff0c\u5353\u4e00\u822a\u7b2c\u4e8c\u65e5\u4e00\u65e9\u548c\u6768  \u5230\u592a\u548c\u95e8\u5916\uff0c\u606d\u95ee\u5723\u5b89\uff0c\u6295\u540d\u542c\u53ec\uff0c\u7b49\u4e86 \u534a\u5929\uff0c\u53ea\u89c1\u6765\u95ee\u5019\u7684\u767e\u5b98\uff0c\u6392\u6ee1\u592a\u548c\u6bbf\u5916\uff0c\u7687\u5e1d  \u53ec\u89c1\u4e86\u4e00\u4e2a\u9e3f\u80ea\u5bfa\u4e1e\u201c\u5b98\u540d\u201d\u674e\u53ef\u707c\u3002\u767e \u5b98\u65e0\u4e0d\u9a87\u5f02\u3002\u9e3f\u80ea\u5bfa\u4e1e\u4e0d\u8fc7\u4e8c\u54c1\uff0c\u4e0d\u77e5\u4f55\u6545\u201c\u5723\u7737\u201d\u5982\u6b64\u4e4b\u9686\u3002\u5353\u4e00\u822a\u56de\u5230\u6768\u5bb6\u95f7\u95f7\u4e0d\u4e50\u3002 \u5fc3\u60f3\uff1a\u7687\u5e1d\u8fd9\u6837\u96be\u89c1\uff0c\u770b\u6765\u4f1a\u865a\u6b64\u4e00\u884c\u3002\u4e0d\u6599\u5230\u4e86\u508d\u665a\u65f6\u5206\uff0c\u5bab\u4e2d\u5ffd\u7136\u6d3e\u6765\u4e00\u540d\u5185\u76d1\uff0c\u5230\u6768 \u5bb6\u4e2d\u8bf4\u9053\uff1a\u201c\u5723\u4e0a\u9f99\u4f53\u4eca\u65e5\u5927\u6709\u8d77\u8272\uff0c\u95fb\u8bf4\u5353\u603b\u7763\u7684\u5b59\u513f\u8fdb\u4eac\uff0c\u5429\u5490\u4ed6\u660e\u65e5\u5230\u517b\u5fc3\u6bbf\u671d \u89c1\u3002\u201d\u5353\u4e00\u822a\u5927\u559c\u3002\u6768  \u95ee\u9053\uff1a\u201c\u662f\u90a3\u4f4d\u592a\u533b\u7684\u7075\u836f\uff1f\u201d\u5185\u76d1\u9053\uff1a\u201c\u4f60\u518d\u4e5f\u731c\u60f3\u4e0d\u5230\uff0c\u8fd9\u75c5 \u4e0d\u662f\u533b\u751f\u533b\u7684\u3002\u201d\u6768  \u5927\u4e3a\u5947\u602a\u3002 \u3000\u3000\u7687\u5e1d\u6709\u75c5\uff0c\u60ef\u4f8b\u5fc5\u662f\u592a\u533b\u4f1a\u8bca\uff0c\u533b\u4e0d\u597d\u65f6\u518d\u5ba3\u53ec\u5404\u5730\u540d\u81c0\u3002\u5149\u5b97\u75c5\u4e86\u6708\u9980\uff0c\u592a\u533b\u675f\u624b\u65e0 \u7b56\uff0c\u5404\u5730\u540d\u533b\u9646\u7eed\u5230\u6765\uff0c\u836f\u77f3\u7eb7\u6295\uff0c\u4ea6\u65e0\u8d77\u8272\u3002\u5982\u4eca\u5185\u76d1\u8bf4\u4e0d\u662f\u533b\u751f\u4e0b\u836f\uff0c\u6768  \u81ea\u7136\u5947\u602a\u3002 \u5185\u76d1\u7eed\u9053\uff1a\u201c\u674e\u53ef\u707c\u4e0d\u77e5\u4ea4\u4e86\u4ec0\u9ebd\u597d\u8fd0\uff0c\u5c45\u7136\u7acb\u4e86\u5927\u529f\u3002\u201d\u6768  \u9053\uff1a\u201c\u600e\u9ebd\uff1f\u4ed6\u7acb\u4e86\u4ec0\u9ebd\u529f \u4e86\uff1f\u201d\u5185\u76d1\u9053\uff1a\u201c\u5723\u4e0a\u7684\u75c5\u5df4\u662f\u4ed6\u533b\u7684\u3002\u201d\u6768  \u5947\u9053\uff1a\u201c\u674e\u53ef\u707c\u61c2\u5f97\u533b\u9053\uff1f\u7687\u5e1d\u6562\u5403\u4ed6\u7684 \u836f\uff01\u201d\u5185\u76d1\u9053\uff1a\u201c\u90a3\u674e\u53ef\u707c\u662f\u5bb0\u76f8\u65b9\u4ece\u54f2\u6240\u7aed\u529b\u4fdd\u8350\u7684\uff0c\u8bf4\u4ed6\u6709\u80fd\u6cbb\u767e\u75c5\u7684\u7ea2\u4e38\uff0c\u674e\u9009\u4f8d\u4e5f \u529d\u5723\u4e0a\u8bd5\u670d\u3002\u201d\u674e\u9009\u4f8d\u4e43\u662f\u7687\u5e1d\u7684\u5ba0\u5983\u3002\u6768  \u7709\u5934\u4e00\u76b4\uff0c\u9053\uff1a\u201c\u7687\u5e1d\u600e\u9ebd\u542c\u4fe1\u5987\u4eba\u4e4b\u8a00\uff0c\u4ee5 \u4e07\u91d1\u4e4b\u4f53\u53bb\u8bd5\u4ec0\u9ebd\u7ea2\u4e38\u3002\u201d\u5185\u76d1\u7b11\u9053\uff1a\u201c\u5012\u771f\u4e8f\u674e\u53ef\u707c\u90a3\u7c92\u7ea2\u4e38\u5462\uff0c\u4e07\u5c81\u7237\u670d\u5f8c\uff0c\u8fc7\u4e86\u4e00\u4e2a \u65f6\u8fb0\uff0c\u5c45\u7136\u8212\u670d\u8bb8\u591a\uff0c\u80c3\u53e3\u4e5f\u5f00\u4e86\u3002\u4e07\u5c81\u7237\u8fde\u91cd\u79f0\u8d5e\uff0c\u53eb\u4ed6\u505a\u5fe0\u81e3\u3002\u201d\u6768  \u89c1\u5185\u76d1\u5982\u6b64\u8bf4 \u6cd5\uff0c\u4e5f\u4fbf\u4e0d\u518d\u8a00\u8bed\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\u5353\u4e00\u822a\u548c\u6768  \u53c8\u5230\u592a\u548c\u6bbf\u5916\u542c\u5ba3\uff0c\u5728\u5348\u95e8\u5916\u78b0\u89c1\u674e\u53ef\u707c\u6d0b\u6d0b\u5f97\u610f\u800c\u6765\uff0c\u4e24\u4e2a \u4f8d\u4ece\u4fbf\u5728\u5348\u95e8\u7b49\u5019\u3002\u5353\u4e00\u822a\u4e00\u89c1\uff0c\u4e0d\u89c9\u6115\u7136\u3002\u4f60\u9053\u8fd9\u4e24\u4e2a\u4f8d\u4ece\u662f\u8c01\u4e86\u539f\u6765\u6b63\u662f\u5728\u5c11\u6797\u5bfa\u5c71\u95e8 \u9a82\u6218\u7684\u90a3\u4e24\u4e2a\u8001\u5bb6\u4f19  \u80e1\u8fc8\u548c\u5b5f\u98de\u3002\u80e1\u8fc8\u5782\u624b\u8bf4\u9053\uff1a\u201c\u5927\u4eba\u8fd9\u6b21\u533b\u597d\u5723\u4e0a\uff0c\u5347\u5b98\u90a3\u662f\u6307\u65e5\u53ef \u5f85\u3002\u201d\u674e\u53ef\u707c\u9053\uff1a\u201c\u6211\u6709\u597d\u5904\uff0c\u4e5f\u5c31\u6709\u4f60\u4e24\u4eba\u7684\u4efd\u3002\u201d\u76c2\u98de\u9053\uff1a\u201c\u8c22\u5927\u4eba\u683d\u57f9\u3002\u201d\u674e\u53ef\u707c\u4f4e \u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u53ef\u4e0d\u8981\u8d70\u201c\u5f00\u3002\u5723\u4e0a\u670d\u836f\u4e4b\u5f8c\uff0c\u82e5\u6709\u4ec0\u9ebd\u53d8\u5316\uff0c\u6211\u4f1a\u53eb\u5185\u76d1\u51fa\u6765\u8bf7\u95ee\u4f60 \u4eec\u3002\u201d\u5b5f\u98de\u9053\uff1a\u201c\u5c0f\u8fd8\u4e39\u836f\u5230\u75c5\u9664\uff0c\u5927\u4eba\u4e0d\u5fc5\u62c5\u5fc3\u3002\u201d\u674e\u53ef\u707c\u76f4\u8fdb\u5348\u95e8\uff0c\u5353\u4e00\u822a\u8ddf\u7740\u8fdb\u53bb\uff0c \u80e1\u8fc8\u5b5f\u98de\u4e00\u89c1\uff0c\u9762\u7ea2\u8fc7\u8033\uff0c\u6025\u6025\u628a\u5934\u626d\u8fc7\u4e00\u8fb9\uff0c\u4f6f\u4f5c\u770b\u4e0d\u89c1\u4ed6\u3002 \u3000\u3000\u8fd9\u6b21\u5728\u592a\u548c\u95e8\u5916\u95ee\u5723\u5b89\u7684\u5b98\u513f\u66f4\u591a\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u5185\u5ef7\u4f20\u4ee4\u51fa\u6765\uff0c\u53eb\u9e3f\u80ea\u5bfa\u4e1e\u674e\u53ef\u707c\uff0c\u5175 \u90e8\u5c1a\u4e66\u6768  \uff0c\u793c\u90e8\u5c1a\u4e66\u5b59\u614e\u884c\uff0c\u5fa1\u53f2\u738b\u5b89\u821c\u7b49\u5341\u591a\u4e2a\u5b98\u513f\u5230\u4f53\u4ec1\u9601\u5019\u5ba3\uff0c\u6700\u5f8c\u53eb\u5230\u5353\u4e00\u822a\uff0c \u767e\u5b98\u89c1\u5353\u4e00\u822a\u5e76\u65e0\u529f\u540d\u7adf\u5f97\u5ba3\u53ec\uff0c\u5341\u5206  \u6155\u3002\u6709\u4eba\u77e5\u9053\u4ed6\u662f\u524d\u4e91\u8d35\u603b\u7763\u5353\u4ef2\u5ec9\u7684\u5b59\u513f\uff0c\u7eb7\u7eb7 \u8bae\u8bba\uff0c\u8bf4\u8fd9\u771f\u662f\u96be\u5f97\u7684\u6b8a\u6069\u3002 \u3000\u3000\u5149\u5b97\u7687\u5e1d\u5728\u517b\u5fc3\u6bbf\u517b\u75c5\uff0c\u4f53\u4ec1\u9601\u5c31\u5728\u4fa7\u8fb9\u3002\u5353\u4e00\u822a\u968f\u4f17\u5b98\u4e4b\u5f8c\uff0c\u5728\u672a\u5ea7\u5750\u4e0b\u3002\u5019\u5ba3\u4f17\u5b98 \u7eb7\u7eb7\u5411\u674e\u53ef\u707c\u9053\u8d3a\u3002\u674e\u53ef\u707c\u559c\u6d0b\u6d0b\u7684\u9053\uff1a\u201c\u8fd9\u53ef\u771f\u662f\u5723\u4e0a\u7684\u9e3f\u798f\u9f50\u5929\u3002\u6211\u7684\u7ea2\u4e38\u6070\u6070\u5728\u4e0a\u6708 \u914d\u6210\u3002\u201d\u793c\u90e8\u5c1a\u4e66\u5b59\u614e\u884c\u9053\uff1a\u201c\u4f60\u7684\u7ea2\u4e38\u771f\u662f\u4ed9\u4e39\u5999\u836f\uff0c\u4e0d\u77e5\u5982\u4f55\u914d\u6cd5\uff0c\u82e5\u80af\u516c\u8bf8\u5929\u4e0b\uff0c\u90a3 \u771f\u662f\u9020\u798f\u65e0\u91cf\u3002\u201d\u674e\u53ef\u707c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u5f53\u662f\u5bb9\u6613\u914d\u7684\u5417\uff1f\u90a3\u8981\u5343\u5e74\u7684\u4f55\u9996\u4e4c\uff0c\u5929\u5c71\u7684\u96ea\u83b2\uff0c \u957f\u767d\u5c71\u4e0a\u597d\u7684\u4eba\u53c2\uff0c\u8fd8\u8981\u7aef\u5348\u65e5\u5348\u65f6\u6b63\u5728\u4ea4\u914d\u7684\u4e00\u5bf9\u87cb\u87c0\u4f5c\u4e3a\u836f\u5f15\uff0c\u6211\u82b1\u4e86\u51e0\u5341\u5e74\u529f\u592b\u624d\u4fa5 \u5e78\u628a\u5404\u7269\u914d\u9f50\u3002\u201d\u4f17\u5b98\u542c\u4e86\uff0c\u4e2a\u4e2a\u548b\u820c\u3002\u5353\u4e00\u822a\u542c\u4ed6\u80e1\u5439\uff0c\u6697\u6697\u597d\u7b11\u3002\u5fc3\u77e5\u8fd9\u7ea2\u4e38\u4e00\u5b9a\u662f\u5c11 \u6797\u5bfa\u7684\u5c0f\u8fd8\u4e39\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5185\u76d1\u51fa\u6765\u5ba3\u53ec\u674e\u53ef\u707c\u8fdb\u53bb\u3002\u5353\u4e00\u822a\u5ffd\u7136\u60f3\u8d77\uff0c\u80e1\u8fc8\u548c\u5b5f\u98de\u9a97\u5230\u7684 \u5c0f\u8fd8\u4e39\u867d\u6709\u4e24\u7c92\uff0c\u4f46\u4e00\u7c92\u5df2\u5f53\u573a\u54bd\u4e0b\uff0c  \u5269\u4e0b\u4e00\u7c92\u3002\u5c31\u7b97\u7687\u5e1d\u6628\u65e5\u6240\u670d\u90a3\u7c92\u662f\u771f\uff0c\u4eca\u65e5\u6240\u8fdb \u7684\u7ea2\u4e38\u5b9a\u662f\u5047\u4e86\uff0c\u62ff\u7687\u5e1d\u6027\u547d\u5f53\u4f5c\u513f\u620f\uff0c\u771f\u771f\u5c82\u6709\u6b64\u7406\u3002 \u3000\u3000\u6768  \u89c1\u5353\u4e00\u822a\u7126\u6025\u4e4b\u60c5\u73b0\u65bc\u989c\u8272\uff0c\u95ee\u9053\uff1a\u201c\u600e\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u6015\u8fd9\u674e\u53ef\u707c\u4e71\u8fdb\u5047 \u836f\u3002\u201d\u65c1\u8fb9\u76ef\u5b98\u513f\u6a2a\u4e86\u5353\u4e00\u822a\u4e00\u773c\uff0c\u6768  \u8ba4\u5f97\u8fd9\u662f\u5bb0\u76f8\u65b9\u4ece  \u7684\u4eb2\u4fe1\uff0c\u6025\u9053\uff1a\u201c\u65b9\u5927\u4eba\u4fdd\u8350 \u7684\u5b9a\u4e0d\u4f1a\u9519\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u674e\u53ef\u707c\u6625\u98ce\u6ee1\u9762\u56de\u6765\u3002\u4f17\u5b98\u7eb7\u7eb7\u95ee\u8baf\uff0c\u674e\u53ef\u707c\u9053\uff1a\u201c\u6211\u8fd9\u7ea2\u4e38\u975e\u540c\u5c0f\u9119\uff0c\u672c \u6765\u4e00\u7c92\u4fbf\u591f\uff0c\u4f55\u51b5\u8fde\u670d\u4e24\u7c92\u3002\u5723\u4e0a\u670d\u4e0b\u4e4b\u5f8c\uff0c\u7cbe\u795e\u5927\u4f73\uff0c\u660e\u5929\u4fbf\u53ef\u4e0a\u671d\u4e0e\u8bf8\u541b\u76f8\u89c1\u4e86\u3002\u201d\u4f17 \u5b98\u53c8\u662f\u7eb7\u7eb7\u9053\u8d3a\u3002 \u3000\u3000\u5353\u4e00\u822a\u5c06\u4fe1\u5c06\u7591\uff0c\u5fc3\u60f3\u5c31\u662f\u771f\u4f60\u5c0f\u8fd8\u4e39\u4e5f\u4e0d\u4f1a\u597d\u5f97\u8fd9\u9ebd\u5feb\u3002\u5185\u76d1\u53c8\u51fa\u6765\u53eb\u9053\uff1a\u201c\u5723\u4e0a\u53eb \u5353\u4e00\u822a\u8fdb\u8c12\u201d\u3002\u6b63\u662f\uff1a\u6c5f\u6e56\u672f\u58eb\uff0c\u6545\u5f04\u7384\u865a\uff0c\u4e07\u4e58\u4e4b\u5c0a\uff0c\u6027\u547d\u513f\u620f\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b \u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377\u2014\u2014\u7b2c\u5341\u4e00\u56de\u3000\u7cdc\u70c2\u53f9\u5bab\u95f1\u3000\u82f1\u96c4\u6c14\u77ed\u3000\u8729\u5858\u60b2\u56fd\u4e8b\u3000\u4fa0\u58eb\u5fc3\u4f24 \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377 \u7b2c\u5341\u4e00\u56de \u7cdc\u70c2\u53f9\u5bab\u95f1\u3000\u82f1\u96c4\u6c14\u77ed \u8729\u5858\u60b2\u56fd\u4e8b\u3000\u4fa0\u58eb\u5fc3\u4f24 \u3000\u3000\u6768  \u9053\uff1a\u201c\u4e16\u5144\u7559\u795e\u5e94\u5bf9\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u8c22\u5927\u4eba\u5173\u7167\u3002\u201d\u968f\u5185\u76d1\u8d70\u8fc7\u957f\u5eca\uff0c\u8fdb\u4eba\u517b\u5fc3 \u6bbf\u5185\uff0c\u53ea\u89c1\u7687\u5e1d\u659c\u501a\u5e8a\u4e0a\uff0c\u9762\u6709\u7b11\u5bb9\uff0c\u5353\u4e00\u822a\u530d\u4f0f\u671d\u62dc\uff0c\u5e38\u6d1b\u9053\uff1a\u201c\u514d\u793c\u3002\u8d50\u5750\u3002\u201d\u5185\u76d1\u7aef \u8fc7\u4e00\u5f20\u6905\u5b50\uff0c\u5353\u4e00\u822a\u4fa7\u8eab\u5750\u4e86\u671d\u7687\u5e1d\u4e00\u671b\uff0c\u53ea\u89c1\u4ed6\u9762\u53d1\u7ea2\u5149\uff0c\u6beb\u65e0\u75c5\u5bb9\uff0c\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u8981 \u77e5\u6cf0\u660c\u7687\u5e1d\u201c\u5149\u5b97\u5e74\u53f7\u201d\u5f97\u75c5\u5df2\u4e45\uff0c\u5373\u7b97\u771f\u7684\u662f\u4ed9\u4e39\u5999\u836f\uff0c\u4e5f\u96be\u836f\u5230\u75c5\u9664\u3002\u800c\u4eca\u5403\u4e86\u4e00\u7c92\u7ea2 \u4e38\uff0c\u5c31\u5c45\u7136\u7ea2\u5149\u6ee1\u9762\uff0c\u82e5\u975e\u56de\u5149\u53cd\u662d\uff0c\u5c31\u662f\u90a3\u7ea2\u4e38\u662f\u7528\u6781\u9738\u9053\u7684\u836f\u6240\u70bc\uff0c\u80fd\u6682\u6536\u523a\u6fc0\u4e4b\u529f\uff0c \u7136\u7ec8\u5c5e\u5927\u5bb3\u3002\u5353\u4e00\u822a\u9690\u5fe7\u5728\u5fc3\uff0c\u5374\u4e0d\u6562\u8bf4\u51fa\u3002 \u3000\u3000\u5e38\u6d1b\u9053\uff1a\u201c\u6211\u6628\u65e5\u5df2\u77e5\u4f60\u6765\uff0c\u4f46\u75c5\u9b54\u672a\u53bb\uff0c\u4e0d\u4fbf\u53ec\u4f60\u3002\u5e78\u5f97\u674e\u53ef\u707c\u8fdb\u4e86\u4e24\u7c92\u7ea2\u4e38\uff0c\u771f\u771f \u662f\u836f\u5230\u75c5\u9664\uff0c\u8981\u4e0d\u7136\u4eca\u65e5\u4e5f\u8fd8\u672a\u80fd\u89c1\u4f60\u3002\u4f60\u770b\u6211\u7684\u6c14\u8272\u5982\u4f55\uff1f\u201d\u8a00\u4e0b\u751a\u4e3a\u5f97\u610f\uff0c\u5353\u4e00\u822a\u4e0d\u6562 \u76f4\u9648\uff0c\u53ea\u597d\u8bf4\u9053\uff1a\u201c\u7687\u4e0a\u9e3f\u798f\u9f50\u5929\uff0c\u6c14\u8272\u597d\u6781\u4e86\u3002\u4f46\u4e45\u75c5\u4e4b\u5f8c\uff0c\u8fd8\u987b\u73cd\u6444\u3002\u201d \u3000\u3000\u5e38\u6d1b\u559d\u4e86\u4e00\u76cf\u9e7f\u8840\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u7684\u4e8b\u6e05\uff0c\u77f3\u6d69\u5df2\u7ecf\u544a\u8bc9\u6211\u4e86\u3002\u674e\u5468\u4e8c\u4f4d\u94a6\u5dee\u4e5f\u5df2\u7ecf\u5b89\u5168 \u56de\u4eac\u3002\u4ed6\u4eec\u90fd\u5f88\u611f\u6fc0\u4f60\u5462\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6697\u7b97\u4e8c\u4f4d\u94a6\u5dee\u7684\u4eba\u53ea\u6050\u80cc\u5f8c\u6709\u6743\u52bf\u8005\u6491\u8170\u3002\u201d\u670d \u4f8d\u7687\u5e1d\u7684\u592a\u76d1\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u5353\u4e00\u822a\u9053\uff1a\u201c\u4e07\u5c81\u521d  \uff0c\u6211\u672c\u4e0d\u8be5\u8bf4\u8fd9\u4e9b\u8bdd\u4ee4\u7687\u4e0a\u62c5\u5fc3\u2026\u2026\u201d\u5e38 \u6d1b\u9762\u8272\u4e00\u6c89\uff0c\u5bf9\u5185\u76d1\u9053\uff1a\u201c\u4f60\u5230\u7fe0\u534e\u5bab\u53eb\u674e\u9009\u4f8d\u6765\u3002\u201d\u5185\u76d1\u5782\u624b\u9000\u4e0b\u3002\u5e38\u6d1b\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u5353 \u5148\u751f\u6df1\u8c0b\u8fdc\u8651\uff0c\u6d1e\u5bdf\u673a\u5fae\uff0c\u6715\u6b63\u60f3\u4ef0\u4ed7\u5148\u751f\u81c2\u52a9\u3002\u201d\u5353\u4e00\u822a\u5fc3\u4e2d\u4e00\u52a8\uff0c\u53ea\u542c\u5f97\u7687\u5e1d\u7eed\u9053\uff1a \u201c\u4f60\u83ab\u4e0d\u662f\u7591\u5fc3\u9b4f\u5fe0\u8d24\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u81e3\u4e00\u4ecb\u5e03\u8863\uff0c\u4e0d\u6562\u5984\u8bba\u671d\u653f\uff0c\u4f46\u5382\u536b\u4ed8\u4e4b\u9609\u4eba\uff0c \u53ea\u6015\u592a\u963f\u5012\u6301\uff0c  \u5b98\u4e4b\u7978\u4e0d\u53ef\u4e0d\u9632\u3002\u201d\u5e38\u6d1b\u9053\uff1a\u201c\u672c\u6765\u4f60\u88ab\u9677\u5bb3\u7684\u4e8b\uff0c\u6211\u65e9\u60f3\u5f7b\u67e5\uff0c\u4f46\u53ea\u6068 \u767b\u6781\u4e4b\u5f8c\uff0c\u4fbf\u7f20\u7ef5\u75c5\u69bb\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4e2a\u4eba\u7684\u51a4\u6789\u7b97\u4e0d\u4e86\u4ec0\u9ebd\uff0c\u56fd\u5bb6\u5927\u4e8b\u8981\u7d27\u3002\u201d\u5e38\u6d1b \u9053\uff1a\u201c\u6240\u4ee5\u6211\u8bf7\u4f60\u6765\u3002\u9b4f\u5fe0\u8d24\u5176\u5b9e\u4e0d\u5fe0\u4e0d\u8d24\uff0c\u6211\u90a3\u6709\u4e0d\u77e5\u9053\u4e4b\u7406\u3002\u53ea\u662f\u4ed6\u638c\u63e1\u4e1c\u5382\uff0c\u5bab\u4e2d\u4f8d \u536b\u5168\u542c\u4ed6\u8c03\u5ea6\uff0c\u4e5f\u4e0d\u80fd\u884c\u4e8b\u8349\u7387\u3002\u5f85\u6715\u75c5\u597d\u4e34\u671d\u4e4b\u5f8c\uff0c\u5f53\u518d\u56fe\u4e4b\u3002\u201d\u5353\u4e00\u822a\u9ed8\u7136\u65e0\u8bed\u3002\u7687\u5e1d \u5ffd\u9053\uff1a\u201c\u5353\u5148\u751f\u53ef\u80af\u7559\u5728\u5bab\u4e2d\u9ebd\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u9053\uff1a\u201c\u5fae\u81e3\u5b5d\u670d\u672a\u6ee1\uff0c\u4e0d\u6562\u4f3a\u5019\u660e\u541b\u3002\u201d\u5e38\u6d1b\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u8981\u4f60\u505a\u5b98\uff0c\u4f60\u66ff\u6211 \u5728\u5bab\u4e2d\u6559\u6559\u592a\u5b50\u5982\u4f55\uff1f\u7531\u6821\u4eca\u5e74\u5341\u4e03\u5c81\u4e86\uff0c\u8fd8\u662f\u987d\u52a3\u4e0d\u61c2\u4eba\u4e8b\u3002\u201d\u5353\u4e00\u822a\u60f3\u8d77\u7956\u7236\u9057\u8a00\uff0c\u6b63 \u5c06\u63a8\u8f9e\u3002\u5e38\u6d1b\u5df2\u6293\u8d77\u7b14\u6765\uff0c\u5728\u5e8a\u524d\u7684\u5c0f\u8336\u51e0\u4e0a\u5199\u4e86\u5723\u65e8\uff0c\u7528\u4e86\u7389\u73ba\uff0c\u5353\u4e00\u822a\u4e0d\u4fbf\u62e6\u963b\uff0c\u6b63\u81ea \u5fc3\u6025\uff0c\u5e38\u6d1b\u5c06\u8bcf\u4e66\u9012\u8fc7\uff0c\u9053\uff1a\u201c\u4f60\u660e\u65e5\u53ef\u5230\u5185\u52a1\u5e9c\u53bb\u62a5\u5230\uff0c\u53eb\u4ed6\u4eec\u66ff\u4f60\u5b89\u6392\u4f4f\u6240\u3002\u201d\u5353\u4e00\u822a \u63a5\u8fc7\u8bcf\u4e66\uff0c\u5148\u8dea\u4e0b\u8c22\u6069\uff0c\u7136\u5f8c\u8bf4\u9053\uff1a\u201c\u5fae\u81e3\u8fd8\u662f\u4e0d\u6562\u63a5\u65e8\u3002\u201d\u5e38\u6d1b\u8bb6\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u4ec0\u9ebd\u4e3a\u96be \u4e4b\u5904\uff1f\u201d\u6b63\u8bf4\u8bdd\u95f4\u5ffd\u7136\u201c\u54ce\u5537\u201d\u4e00\u58f0\uff0c\u95e8\u5916\u7684\u4f8d\u536b\u7eb7\u7eb7\u62a2\u8fdb\uff0c\u5e38\u6d1b\u547b\u541f\u9053\uff1a\u201c\u4e0d\u5173\u4ed6\u7684\u4e8b\uff0c \u53eb\u674e\u53ef\u707c\u6765\uff01\u201d\u9762\u4e0a\u7ea2\u7b4b\u9690\u73b0\uff0c\u9893\u7136\u5012\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u5353\u4e00\u822a\u6599\u5f97\u4e0d\u9519\uff0c\u5e38\u6d1b\u7b2c\u4e00\u6b21\u670d\u7684\u7ea2\u4e38\u679c\u662f\u5c11\u6797\u5bfa\u7684\u5c0f\u8fd8\u4e39\uff0c\u7b2c\u4e8c\u6b21\u670d\u7684\u5374\u662f\u5047\u836f\u3002\u539f \u6765\u80e1\u8fc8\u548c\u5b5f\u98de\u90fd\u662f\u674e\u53ef\u707c\u7684\u95e8\u5ba2\uff0c\u80e1\u8fc8\u7c97\u6653\u6b66\u529f\uff0c\u76c2\u98de\u5219\u662f\u4e2a\u4e13\u9020\u5047\u836f\u7684\u6c5f\u6e56\u9a97\u5b50\uff0c\u4e8c\u4eba\u5728 \u5c11\u6797\u5bfa\u8bb9\u8bc8\uff0c\u9a97\u4e86\u4e24\u7c92\u5c0f\u8fd8\u4e39\uff0c\u5176\u4e2d\u4e00\u7c92\u80e1\u8fc8\u5f53\u573a\u653e\u4eba\u53e3\u4e2d\uff0c\u5374\u5e76\u672a\u54bd\u4e0b\uff0c\u4e8b\u5f8c\u5410\u4e86\u51fa\u6765\uff0c \u4ea4\u7ed9\u5b5f\u98de\u5316\u9a8c\uff0c\u5b5f\u98de\u81ea\u4f5c\u806a\u660e\uff0c\u80e1\u731c\u5c0f\u8fd8\u4e39\u7684\u914d\u836f\u6210\u4efd\uff0c\u5236\u4e86\u51e0\u7c92\u3002\u674e\u53ef\u707c\u636e\u4ee5\u4e3a\u5b9d\uff0c\u732e\u7ed9 \u7687\u5e1d\uff0c\u7ec8\u65bc\u917f\u6210\u4e86\u660e\u53f2\u4e0a\u201c\u7ea2\u4e38\u201d\u4e00\u6848\u3002 \u3000\u3000\u5353\u4e00\u822a\u89c1\u5e38\u6d1b\u751a\u4e3a\u75db\u82e6\uff0c\u9ec4\u8c46\u822c\u7684\u6c57\u73e0\u9897\u9897\u6ef4\u4e0b\uff0c\u6b63\u81ea\u5fc3\u6025\uff0c\u5ffd\u95fb\u5f97\u517b\u5fc3\u6bbf\u5916\u6709\u53f1\u5412\u8ffd \u9010\u4e4b\u58f0\uff0c\u4f8d\u536b\u957f\u4e00\u8dc3\u800c\u51fa\uff0c\u559d\u9053\uff1a\u201c\u8c01\u6562\u60ca\u52a8\u5723\u9a7e\uff01\u201d \u3000\u3000\u518d\u8bf4\u5cb3\u9e23\u73c2\u60a0\u60a0\u9192\u8f6c\uff0c\u53d1\u73b0\u81ea\u5df1\u7adf\u662f\u5904\u5728\u534e\u4e3d\u7edd\u4f26\u7684\u623f\u95f4\u4e4b\u4e2d\uff0c\u9759\u5750\u4e00\u9635\uff0c\u795e\u667a\u6682\u590d\uff0c \u7591\u5e7b\u7591\u68a6\u3002\u5ffd\u7136\u5728\u5bf9\u9762\u5899\u4e0a\u60ac\u7740\u7684\u5efa\u660c\u5b9d\u955c\u91cc\uff0c\u7167\u89c1\u81ea\u5df1\u5df2\u6362\u4e86\u4e00\u5957\u7761\u8863\uff0c\u731b\u7136\u60f3\u8d77\u81ea\u5df1\u51fa \u6765\u65f6\u539f\u5e26\u6709\u4f69\u5251\uff0c\u6e38\u76ee\u56db\u987e\uff0c\u4e0d\u4f46\u81ea\u5df1\u539f\u6765\u7684\u8863\u88f3\u4e0d\u89c1\uff0c\u8fde\u4f69\u5251\u4e5f\u4e0d\u89c1\u4e86\u3002\u987b\u77e5\u5cb3\u545c\u73c2\u8fd9\u628a \u4f69\u5251\uff0c\u4e43\u4ed6\u5e08\u7236\u5728\u5929\u5c71\u6240\u70bc\u7684\u4e24\u628a\u5b9d\u5251\u4e4b\u4e00\uff0c\u795e\u7269\u5229\u5668\uff0c\u7a81\u7136\u4e0d\u89c1\uff0c\u5982\u4f55\u4e0d\u60ca\u3002\u6025\u5fd9\u8d77\u6765\u5bfb \u89c5\uff0c\u521a\u521a\u4e0b\u5f97\u5e8a\u6765\uff0c\u5bf9\u9762\u5899\u4e0a\u7684\u5927\u955c\u5ffd\u7136\u6162\u6162\u79fb\u5f00\uff0c\u7f15\u7f15\u6697\u9999\uff0c\u5f25\u6f2b\u5ba4\u5185\uff0c\u955c\u5f8c\u7adf\u662f\u4e00\u9053\u6697 \u95e8\uff0c\u4e00\u4e2a\u7f8e\u5987\u4eba\u8f7b\u8f7b\u7684\u8d70\u4e86\u51fa\u6765\uff0c\u683c\u683c\u7b11\u9053\uff1a\u201c\u4f60\u9192\u6765\u4e86\uff1f\u201d \u3000\u3000\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4e3a\u4ec0\u9ebd\u628a\u6211\u7684\u5b9d\u5251\u5077\u4e86\uff1f\u201d\u90a3\u7f8e\u5987\u5927\u7b11\u9053\uff1a\u201c\u5b9d\u5251\uff1f\u4ec0\u9ebd\u5b9d\u5251\u503c \u5f97\u5927\u60ca\u5c0f\u602a\uff1f\u6211\u8fd9\u91cc\u7684\u5b9d\u7269\u591a\u7740\u5462\uff0c\u4f60\u8981\u591a\u5c11\uff1f\u201d\u968f\u624b\u6253\u5f00\u4e00\u4e2a\u62bd\u5c49\uff0c\u53ea\u89c1\u5b9d\u6c14\u73e0\u5149\uff0c\u8000\u773c \u751f\u7f2c\u3002\u91cc\u9762\u5806\u6ee1\u4e86\u73ca\u745a\u5b9d\u77f3\uff0c\u7fe1\u7fe0\u73cd\u73e0\u3002\u7f8e\u5987\u4eba\u4ee5\u4e3a\u5cb3\u545c\u73c2\u5fc5\u5b9a\u60ca\u8bb6\uff0c\u90a3\u77e5\u5cb3\u545c\u73c2\u8bf4\u9053\uff1a \u201c\u8fd9\u4e9b\u4e1c\u897f\u518d\u591a\u5341\u500d\u4e5f\u6bd4\u4e0d\u5f97\u6211\u7684\u5b9d\u5251\uff01\u201d\u7f8e\u5987\u4eba\u8f7b\u8511\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5b9d\u5251\u7b97\u5f97\u4ec0\u9ebd\uff1f\u4f60\u559c\u6b22 \u5b9d\u5251\uff0c\u6211\u8fd9\u91cc\u6709\u7684\u662f\uff01\u4f60\u53ea\u8981\u4e56\u4e56\u542c\u6211\u7684\u8bdd\uff0c\u4f60\u8981\u4ec0\u9ebd\u4fbf\u6709\u4ec0\u9ebd\uff1f\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f \u8c01\uff1f\u201d\u7f8e\u5987\u4eba\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u77a7\u8fd9\u91cc\u53ef\u50cf\u4eba\u95f4\u6240\u5728\uff1f\u201d\u5cb3\u545c\u73c2\u8f7b\u54ac\u820c\u5934\uff0c\u9690\u9690\u751f\u75db\uff0c\u60c5\u77e5\u4e0d\u662f \u4f5c\u68a6\uff0c\u4fbf\u9053\uff1a\u201c\u96be\u9053\u4f60\u8fd9\u91cc\u662f\u5e7f\u5bd2\u4ed9\u5e9c\u4e0d\u6210\uff1f\u201d\u7f8e\u5987\u5927\u7b11\u9053\uff1a\u201c\u4e5f\u5dee\u4e0d\u591a\uff01\u201d\u8bf4\u7740\u6328\u8fd1\u8eab \u6765\uff0c\u9999\u6c14\u8d8a\u53d1\u6d53\u90c1\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u5fc3\u795e\u4e00\u8361\uff0c\u53ea\u89c9\u8fd9\u9999\u5473\u5341\u5206\u5947\u602a\uff0c\u5438\u4eba\u9f3b\u7aef\uff0c\u9189\u9b42\u9165\u9aa8\uff0c\u6e10\u60ed\u9762\u7ea2\u8033\u70ed\u8840\u8109\u6124 \u5f20\u3002\u5cb3\u545c\u73c2\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u662f\u9047\u4e86\u90aa\u9b54\uff0c\u6765\u8bd5\u6211\u7684\u5b9a\u529b\uff1f\u201d\u76d8\u819d\u4e00\u5750\uff0c\u53c8\u7528\u8d77\u529f\u6765\u3002\u90a3\u7f8e\u5987\u4eba \u6328\u7740\u5cb3\u545c\u73c2\u8eab\u5b50\uff0c\u7528\u624b\u6307\u62e8\u4ed6\u773c\u76ae\uff0c\u5cb3\u545c\u73c2\u53ea\u662f\u4e0d\u7406\u3002\u7f8e\u5987\u5927\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u4e0d\u662f\u548c\u5c1a\uff0c\u6253\u5750 \u4f5c\u751a\uff1f\u201d\u5cb3\u545c\u73c2\u4ecd\u7136\u4e0d\u7406\u3002\u7f8e\u5987\u4eba\u53c8\u7b11\u9053\uff1a\u201c\u6211\u95fb\u6709\u9053\u9ad8\u50e7\uff0c\u76ee\u4e0d\u8ff7\u65bc\u4e94\u8272\uff0c\u8033\u4e0d\u60d1\u65bc\u4e94 \u58f0\uff0c\u4f60\u4e0d\u6562\u5f20\u5f00\u773c\u775b\uff0c\u600e\u9ebd\u80fd\u505a\u9ad8\u50e7\uff01\u201d\u5cb3\u9e23\u73c2\u5fc3\u5934\u4e00\u9707\uff0c\u76ca\u53d1\u6000\u7591\u5979\u662f\u5996\u90aa\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a \u201c\u6211\u867d\u672a\u95fb\u5927\u4e58\u4f5b\u7406\uff0c\u4f46\u955c\u660e\u957f\u8001\u8bf4\u6211\u9887\u6709\u6167\u6839\uff0c\u4e5f\u66fe\u4f20\u8fc7\u6211\u660e\u5fc3\u89c1\u6027\u7684\u771f\u8a00\u3002\u6211\u5012\u8981\u8bd5\u8bd5 \u81ea\u5df1\u7684\u5b9a\u529b\u3002\u201d\u500f\u7684\u5f20\u5f00\u773c\u775b\uff0c\u773c\u89c2\u9f3b\uff0c\u9f3b\u89c2\u5fc3\uff0c\u6c14\u805a\u4e39\u7530\uff0c\u884c\u8d77\u5410\u7eb3\u4e4b\u9053\u3002\u90a3\u7f8e\u5987\u4eba\u89c1\u4ed6 \u82e5\u65e0\u5176\u4e8b\uff0c\u4e5f\u662f\u9887\u4e3a\u5947\u602a\uff0c\u7d22\u6027\u628a\u8eab\u5b50\u51d1\u4e86\u4e0a\u6765\uff0c\u5411\u4ed6\u5618\u6c14\uff0c\u5cb3\u545c\u73c2\u8bd5\u8fd0\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684 \u529f\u592b\uff0c\u9f13\u6c14\u4e00\u5f39\uff0c\u90a3\u7f8e\u5987\u4eba\u201c\u54ce\u5537\u201d\u4e00\u58f0\uff0c\u8dcc\u843d\u5e8a\u4e0b\uff0c\u5a07\u55d4\u9a82\u9053\uff1a\u201c\u4f60\u7528\u4ec0\u9ebd\u5996\u672f\uff1f\u201d \u3000\u3000\u5cb3\u545c\u73c2\u8bd5\u7528\u4e86\u201c\u6cbe\u8863\u5341\u516b\u8dcc\u201d\u7684\u529f\u592b\uff0c\u8bd5\u51fa\u90a3\u7f8e\u5987\u4eba\u4e1d\u6beb\u4e0d\u61c2\u6b66\u529f\uff0c\u4e0d\u89c9\u8bf4\u9053\uff1a\u201c\u554a\uff0c \u539f\u6765\u4f60\u4e0d\u662f\u5996\u90aa\uff01\u201d\u7f8e\u5987\u4eba\u6012\u9053\uff1a\u201c\u4f60\u624d\u662f\u5996\u90aa\uff01\u201d\u5ffd\u53c8\u56de\u55d4\u4f5c\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8fdb\u4eac\u8003\u6b66\u7684 \u4e3e\u5b50\u9ebd\uff1f\u201d\u5cb3\u545c\u73c2\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u8bf4\u4f60\u6709\u8bb8\u591a\u5b9d\u5251\uff0c\u8bf7\u501f\u4e00\u628a\u6765\u77a7\u3002\u201d\u7f8e\u5987\u4eba\u7a0d\u73b0\u72b9 \u7591\uff0c\u968f\u5373\u7b11\u9053\uff1a\u201c\u8c05\u4f60\u4e5f\u4e0d\u6562\u6740\u6211\u3002\u6211\u5c31\u8ba9\u4f60\u5f00\u5f00\u773c\u754c\u3002\u201d\u968f\u624b\u5728\u5899\u4e0a\u4e00\u6309\uff0c\u6253\u5f00\u4e00\u9053\u6697 \u95e8\uff0c\u4e43\u662f\u4e00\u4e2a\u58c1\u6a71\uff0c\u91cc\u9762\u60ac\u6709\u5341\u6765\u53e3\u5251\uff0c\u5cb3\u9e23\u73c2\u4e00\u773c\u77a7\u53bb\uff0c\u5e76\u65e0\u81ea\u5df1\u7684\u6e38\u9f99\u5251\u5728\u5185\u3002\u53ea\u542c\u5f97 \u90a3\u7f8e\u5987\u4eba\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u5251\uff0c\u968f\u4fbf\u90a3\u628a\u90fd\u8981\u6bd4\u4f60\u7684\u597d\uff0c\u4f60\u670d\u4e86\u5427\uff1f\u201d\u5cb3\u545c\u73c2\u7a81\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u5728 \u58c1\u6a71\u91cc\u62bd\u51fa\u4e00\u628a\u5251\u6765\uff0c\u53ea\u89c1\u5bd2\u5149\u95ea\u95ea\uff0c\u51b7\u6c14\u68ee\u68ee\uff0c\u7f8e\u5987\u4eba\u9053\uff1a\u201c\u5982\u4f55\uff1f\u662f\u4e0d\u662f\u6bd4\u4f60\u7684\u5251\u597d\uff1f \u5feb\u4e9b\u6302\u56de\u53bb\u5427\uff01\u201d \u3000\u3000\u5cb3\u545c\u73c2\u5403\u4e86\u4e00\u9a7e\uff0c\u8fd9\u628a\u5251\u5f62\u72b6\u5947\u53e4\uff0c\u5251\u67c4\u94dc\u8272\u6591\u6593\uff0c\u6015\u4e0d\u662f\u5343\u5e74\u4ee5\u4e0a\u7684\u5b9d\u5251\uff1f\u7ec6\u7ec6\u4e00\u770b \u5251\u67c4\u4e0a\u954c\u6709\u201c\u9f99\u6cc9\u201d\u4e8c\u5b87\uff0c\u731b\u7136\u60f3\u8d77\u5e08\u7236\u66fe\u8bba\u53e4\u4eca\u5b9d\u5251\uff0c\u4ed6\u8bf4\uff1a\u201c\u6e38\u9f99\u65ad\u7389\u867d\u662f\u4e94\u91d1\u4e4b\u7cbe\u6240 \u70bc\uff0c\u4f46\u6bd4\u8d77\u53e4\u4ee3\u7684\u5e72\u5c06\u3001\u83ab\u90aa\u3001\u9c7c\u80a0\u3001\u9f99\u6cc9\u3001\u5929\u8679\u3001\u5de8\u9619\uff0c\u7eaf\u94a9\uff0c\u6e5b\u5362\u7b49\u5251\uff0c\u90a3\u8fd8\u662f\u8fdc\u8fdc\u4e0d \u53ca\u3002\u201d\u5cb3\u545c\u73c2\u5f53\u65f6\u66fe\u95ee\u53ca\u8fd9\u516b\u628a\u53e4\u4ee3\u5b9d\u5251\u7684\u4e0b\u843d\uff0c\u5e08\u7236\u9053\uff1a\u201c\u542c\u8bf4\u9f99\u6cc9\u3001\u5de8\u9619\u3001\u6e5b\u5362\u53c1\u5251\u81ea \u5510\u4ee3\u8d77\u5c31\u6d41\u5165\u5bab\u4e2d\uff0c\u5176\u4ed6\u4e94\u628a\u5374\u662f\u4e0d\u77e5\u4e0b\u843d\u3002\u201d\u8fd9\u6837\u8bf4\u6765\uff0c\u96be\u9053\u8fd9\u91cc\u7adf\u662f\u5bab\u4e2d\u7981\u5730\uff1f\u7a17\u5b98\u91ce \u53f2\u4e0a\u8bf4\u5510\u4ee3\u7684\u516c\u4e3b\u559c\u6b22\u63b3\u7f8e\u7537\u5b50\u5165\u5bab\u4eab\u53d7\uff0c\u96be\u9053\u8fd9\u79cd\u5bab\u95f1\u79fd\u53f2\u91cd\u73b0\u65bc\u4eca\u65e5\uff1f\u6b63\u601d\u91cf\u95f4\u5ffd\u542c\u5f97 \u5899\u58c1\u6709\u4eba\u6572\u4e86\u51e0\u4e0b\uff0c\u5176\u58f0\u6025\u4fc3\u3002\u7f8e\u5987\u4eba\u9053\uff1a\u201c\u5feb\u628a\u5251\u6302\u4e0a\uff01\u201d\u5cb3\u545c\u73c2\u628a\u5251\u4e00\u6307\uff0c\u731b\u7136\u559d\u9053\uff1a \u201c\u4f60\u662f\u4f55\u4eba\uff1f\u4ece\u5b9e\u9053\u6765\uff01\u201d\u7f8e\u5987\u4eba\u7389\u989c\u53d8\u8272\uff0c\u628a\u624b\u4e00\u6309\uff0c\u58c1\u6a71\u9690\u6ca1\uff0c\u5cb3\u545c\u73c2\u4e00\u6b65\u6b65\u8feb\u8fd1\uff0c\u7f8e \u5987\u4eba\u5728\u5899\u4e0a\u4e00\u9760\uff0c\u6697\u95e8\u500f\u5f00\uff0c\u91cc\u9762\u8df3\u51fa\u4e24\u4e2a\u4eba\u6765\uff0c\u7f8e\u5987\u4eba\u4e5f\u4ece\u6697\u95e8\u9003\u51fa\u53bb\u4e86\uff01 \u3000\u3000\u4ece\u590d\u58c1\u4e2d\u8df3\u51fa\u7684\u4e24\u4eba\uff0c\u624b\u4e2d\u90fd\u63d0\u7740\u5175\u5668\uff0c\u5176\u4e2d\u4e00\u4eba\u6b63\u662f\u7528\u8ff7\u70df\u55b7\u7ffb\u81ea\u5df1\u7684\u9ec4\u8863\u6c49\u5b50\u3002\u5cb3 \u545c\u73c2\u5927\u6012\uff0c\u4e00\u5251\u523a\u53bb\uff0c\u90a3\u4eba\u628a\u624b\u4e00\u626c\uff0c\u5c04\u51fa\u53c1\u679a\u5f39\u5b50\uff0c\u4e00\u51fa\u4f7f\u81ea\u884c\u70b8\u88c2\uff0c\u55b7\u51fa\u6d53\u70df\u3002\u5cb3\u9e23\u73c2 \u65e9\u6709\u9632\u5907\uff0c\u5fcd\u7740\u6c14\u7edd\u4e0d\u547c\u5438\uff0c\u624b\u4e2d\u5251\u8fc5\u82e5\u60ca\u98d5\uff0c\u4e00\u5251\u523a\u5230\u90a3\u4eba\u54bd\u5589\uff0c\u731b\u7136\u60f3\u8d77\uff0c\u6b64\u5730\u82e5\u662f\u7981 \u82d1\uff0c\u6b64\u4eba\u4fbf\u662f\u5bab\u4e2d\u4f8d\u536b\uff0c\u5251\u628a\u4e00\u7f29\uff0c\u53f3\u8fb9\u90a3\u4eba\u4e00\u94db\u6253\u6765\uff0c\u5cb3\u545c\u73c2\u53cd\u624b\u4e00\u635e\uff0c\u5c06\u4ed6\u7684\u5175\u5668\u5939\u624b \u62a2\u8fc7\uff0c\u201c\u7830\u201d\u7684\u4e00\u811a\u8e22\u5f00\u623f\u95e8\uff0c\u5f80\u5916\u4fbf\u95ef\u3002 \u3000\u3000\u90a3\u4e24\u4eba\u7edd\u6599\u4e0d\u5230\u4ed6\u521a\u521a\u9192\u8f6c\uff0c\u6b66\u529f\u8fd8\u6709\u5982\u6b64\u5389\u5bb3\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u6025\u5fd9\u51fb\u638c\u547c\u63f4\u3002\u5cb3\u545c\u73c2\u4e00 \u51fa\u623f\u95e8\uff0c\u4e03\u516b\u540d\u536b\u58eb\u56db\u8fb9\u56f4\u4e0a\uff0c\u5cb3\u545c\u73c2\u4e0d\u613f\u4f24\u4eba\uff0c\u6a2a\u5251\u56db\u9762\u4e00\u626b\uff0c\u4f46\u542c\u5f97\u4e00\u9635\u65ad\u91d1\u621b\u7389\u4e4b \u58f0\uff0c\u4e03\u516b\u6761\u5175\u5203\u90fd\u7ed9\u622a\u65ad\uff0c\u9f99\u6cc9\u5b9d\u5251\u7684\u5a01\u529b\u679c\u7136\u5927\u5f97\u60ca\u4eba\uff01\u6709\u4eba\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u5077\u4e86\u5bab\u4e2d \u7684\u5b9d\u5251\uff0c\u95ef\u5f97\u51fa\u53bb\u4e5f\u662f\u6b7b\u7f6a\uff0c\u4e0d\u5982\u8d76\u5feb\u5f03\u5251\u6ca1\u964d\uff0c\u6211\u4eec\u53ef\u4ee5\u5077\u5077\u653e\u4f60\u51fa\u53bb\u3002\u201d\u5cb3\u9e23\u73c2\u5fc3\u60f3\uff1a \u4e8b\u5df2\u81f3\u6b64\uff0c\u4e0d\u5982\u6211\u5c31\u643a\u5251\u53bb\u89c1\u7687\u4e0a\uff0c\u62da\u7740\u4e00\u6b7b\uff0c\u4e5f\u8981\u628a\u6b64\u4e8b\u67e5\u660e\uff0c\u4e3b\u610f\u6253\u5b9a\uff0c\u624b\u4e2d\u5251\u53c8\u4e00\u4e2a \u65cb\u98ce\u75be\u821e\uff0c\u628a\u536b\u58eb\u4eec\u8feb\u51fa\u4e8c\u4e08\u5f00\u5916\uff0c\u7eb5\u8eab\u8df3\u4e0a\u5c4b\u9876\u3002 \u3000\u3000\u7687\u5bab\u6bbf\u5b87\u5168\u662f\u7528\u9ec4\u8272\u7684\u7409\u7483\u74e6\u6240\u76d6\uff0c\u5cb3\u545c\u73c2\u98de\u8eab\u76f4\u4e0a\uff0c\u53ea\u89c9\u6ed1\u4e0d\u7559\u8db3\uff0c\u56db\u9762\u4e00\u671b\uff0c\u4f46\u89c1 \u6bbf\u5b87\u8fde\u4e91\uff0c\u9c7c\u9cde\u6809\u6bd4\uff0c\u5cb3\u545c\u73c2\u5148\u524d\u5c1a\u6709\u4e9b\u7591\u60d1\uff0c\u6b64\u65f6\u77e5\u9053\u786e\u662f\u7687\u5bab\u65e0\u7591\uff0c\u4e00\u65f6\u767e\u611f\u4ea4\u96c6\uff0c\u60f3 \u4e0d\u5230\u5bab\u4e2d\u8150\u8d25\u7adf\u81f3\u5982\u65af\uff0c\u81ea\u5df1\u4e0e\u718a\u7ecf\u7565\u5728\u8fb9\u5173\u82e6\u6218\uff0c\u53ea\u6050\u4e5f\u662f\u65e0\u8865\u65bc\u4e8b\u4e86\u3002 \u3000\u3000\u90a3\u51e0\u540d\u88ab\u524a\u65ad\u4e86\u5175\u5203\u7684\u536b\u58eb\uff0c\u89c1\u5cb3\u545c\u73c2\u5341\u5206\u5389\u5bb3\uff0c\u4e0d\u6562\u6765\u8ffd\uff0c\u53ea\u662f\u5728\u4e0b\u9762\u5927\u58f0\u5406\u559d\uff0c\u5cb3 \u545c\u73c2\u8ba4\u5b9a\u524d\u95e8\u7684\u534e\u8868\uff0c\u53d1\u8db3\u72c2\u5954\uff0c\u7409\u7483\u74e6\u9762\uff0c\u867d\u7136\u6ed1\u4e0d\u7559\u8db3\uff0c\u4f46\u4ed6\u8f7b\u529f\u5353\u7edd\uff0c\u811a\u5c16\u5fae\u70b9\uff0c\u4fbf \u5373\u98de\u8d77\uff0c\u5c45\u7136\u5982\u7d2b\u71d5\u63a0\u6ce2\uff0c\u6beb\u65e0\u6cbe\u6ede\uff01 \u3000\u3000\u4f46\u7687\u5bab\u6781\u5927\uff0c\u6bbf\u5b87\u4f55\u6b62\u5343\u95f4\uff0c\u4ed6\u521a\u63a0\u8fc7\u51e0\u5ea7\u74e6\u9762\uff0c\u4e0b\u9762\u4e00\u58f0\u5406\u559d\uff0c\u4e00\u4eba\u8df3\u4e86\u4e0a\u6765\uff0c\u7adf\u7136 \u662f\u5e94\u4fee\u9633\uff01\u5cb3\u9e23\u73c2\u5fc3\u9053\uff1a\u7f62\u4e86\uff0c\u7f62\u4e86\uff01\u8fd9\u6837\u7684  \u4eba\u5c45\u7136\u4e5f\u6df7\u8fdb\u5bab\u4e2d\uff0c\u56fd\u4e8b\u8fd8\u6709\u53ef\u4e3a\u5417\uff1f\u5e94\u4fee \u9633\u5927\u53eb\u9053\uff1a\u201c\u6709\u523a\u5ba2\uff01\u201d\u5cb3\u545c\u73c2\u6012\u9053\uff1a\u201c\u597d\u54c7\uff0c\u4f60\u8fd9\u5978\u8d3c\uff0c\u6211\u5148\u6349\u4f60\u53bb\u89c1\u7687\u4e0a\uff01\u201d\u4e00\u62db\u201c\u9f99 \u5377\u66b4\u4f38\u201d\uff0c\u9752\u5149\u500f\u7684\u957f\u51fa\u4e08\u8bb8\uff0c\u5e94\u4fee\u9633\u62c2\u5c18\u4e00\u5377\uff0c\u5251\u5149\u8fc7\u5904\uff0c\u5c18\u5c3e\u5df2\u88ab\u524a\u65ad\u4e00\u7efa\uff0c\u8fd9\u8fd8\u662f\u4ed6 \u907f\u62db\u5f97\u5feb\uff0c\u8981\u4e0d\u7136\u8fde\u624b\u8155\u4e5f\u8981\u622a\u65ad\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u5251\u5982\u9f99\u95e8\u9f13\u6d6a\uff0c\u4e00\u62db\u672a\u6536\uff0c\u4e8c\u62db\u7eed\u81f3\uff0c\u5251\u6cd5\u4e4b\u5feb\uff0c\u96be\u65bc\u5f62\u5bb9\uff0c\u5e94\u4fee\u9633\u672c\u5c31\u4e0d\u662f\u4ed6 \u7684\u5bf9\u624b\uff0c\u66f4\u52a0\u4e0a\u4ed6\u6012\u6781\u6c14\u6781\uff0c\u8fde\u4f7f\u7edd\u62db\uff0c\u5e94\u4fee\u9633\u6321\u4e86\u5341\u62db\uff0c\u5df3\u6709\u51e0\u6b21\u9669\u9669\u88ab\u4ed6\u523a\u4e2d\u3002\u8fd9\u65f6\u5bab \u4e2d\u5404\u5904\u536b\u58eb\uff0c\u95fb\u8baf\u8d76\u6765\uff0c\u4eba\u58f0\u6b65\u58f0\uff0c\u54cd\u6210\u4e00\u7247\u3002\u5cb3\u9e23\u73c2\u6012\u9053\uff1a\u201c\u628a\u4f60\u6bd9\u4e86\u518d\u8bf4\uff01\u201d\u5b9d\u5251\u4e00 \u65cb\uff0c\u9752\u5149\u75be\u9a76\uff0c\u628a\u5e94\u4fee\u9633\u5377\u5728\u5f53\u4e2d\uff0c\u5237\u5237\u51e0\u5251\uff0c\u8fde\u4e0b\u6740\u624b\uff01 \u3000\u3000\u5e94\u4fee\u9633\u5de6\u907f\u53f3\u95ea\uff0c\u5ffd\u89c9\u5934\u9876\u5fc3\u4e00\u51c9\uff0c\u5934\u53d1\u5df2\u88ab\u524a\u4e22\u4e00\u7247\uff0c\u5413\u5f97\u4ea1\u9b42\u4ff1\u5192\uff0c\u62c2\u5c18\u865a\u67b6\uff0c\u62da \u547d\u5411\u4e0a\u8dc3\u8d77\uff0c\u5cb3\u9e23\u73c2\u559d\u9053\uff1a\u201c\u4f60\u8fd8\u60f3\u9003\uff01\u201d\u811a\u5c16\u4e00\u70b9\uff0c\u4ece\u5c4b\u74e6\u51ed\u7a7a\u63a0\u8d77\u53c1\u4e08\uff0c\u4ed6\u7684\u8f7b\u529f\u6bd4\u5e94 \u4fee\u9633\u9ad8\u660e\u5f97\u591a\uff0c\u8fd9\u4e00\u8dc3\uff0c\u7adf\u7136\u63a0\u8fc7\u5e94\u4fee\u9633\u5934\u9876\uff0c\u500f\u7136\u4e00\u7ffb\uff0c\u957f\u5251\u4e0b\u523a\uff0c\u5e94\u4fee\u9633\u8eab\u5b50\u60ac\u7a7a\uff0c\u7edd \u96be\u9003\u907f\uff0c\u53ea\u89c9\u51b7\u6c14\u68ee\u68ee\uff0c\u5251\u950b\u5df2\u5230\u5934\u9876\uff01 \u3000\u3000\u5cb3\u545c\u73c2\u7ffb\u8155\u4e0b\u523a\uff0c\u5c31\u5728\u5e94\u4fee\u9633\u6027\u547d\u4fc4\u9877\u4e4b\u9645\uff0c\u84e6\u5730\u4e00\u56e2\u767d\u5f71\uff0c\u6a2a\u91cc\u98de\u6765\uff0c\u8eab\u5f62\u672a\u5230\uff0c\u638c \u529b\u5148\u6765\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u53c8\u52b2\u53c8\u75be\uff0c\u5cb3\u545c\u73c2\u7684\u5251\u5c16\u7ed9\u9707\u5f97\u6b6a\u8fc7\u4e00\u8fb9\uff0c\u987a\u52bf\u4e00\u5272\uff0c\u5e94\u4fee\u9633\u624b\u81c2\u7f29\u5728 \u8896\u4e2d\uff0c\u8896\u53e3\u7ed9\u5251\u5272\u4e86\u4e00\u6bb5\uff0c\u7ec8\u65bc\u9003\u4e86\u6027\u547d\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u633d\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u91cd\u843d\u74e6\u9762\uff0c\u6551\u5e94\u4fee\u9633\u7684\u4eba\u4e5f\u5df2\u8d76\u5230\uff0c\u8fd0\u638c\u6210\u98ce\uff0c\u547c\u547c\u51e0\u58f0\uff0c\u628a\u5cb3 \u9e23\u73c2\u8feb\u5f97\u8fde\u9000\u53c1\u6b65\u3002\u5cb3\u9e23\u73c2\u5927\u5403\u4e00\u60ca\uff0c\u60f3\u4e0d\u5230\u7687\u5bab\u4e2d\u7684\u536b\u58eb\uff0c\u7adf\u7136\u6709\u5982\u6b64\u529f\u529b\uff01\u5b9a\u775b\u770b\u65f6\uff0c \u90a3\u4eba\u5e26\u7740\u4e00\u5f20\u9762\u5177\uff0c\u72f0\u72de\u53ef\u6015\u3002\u5728\u5251\u5149\u4e2d\u7adf\u7136\u4f38\u624b\u6293\u4ed6\u624b\u8155\u3002\u5cb3\u9e23\u73c2\u6025\u5fd9\u4e00\u6296\u5251\u950b\uff0c\u8d70\u659c\u8fb9 \u653b\u4ed6\u7a7a\u95e8\uff0c\u90a3\u4eba\u5de6\u638c\u659c\u5207\uff0c\u53f3\u638c\u6a2a\u5288\uff0c\u7adf\u7136\u4ee5\u653b\u5bf9\u653b\uff0c\u4e1d\u6beb\u4e0d\u8ba9\u3002\u4e24\u4eba\u6362\u4e86\u51e0\u62db\uff0c\u90fd\u662f\u7edd\u9669 \u4e4b\u7740\uff0c\u5cb3\u9e23\u73c2\u5ffd\u89c9\u8fd9\u4eba\u638c\u6cd5\uff0c\u4f3c\u4e4e\u5728\u90a3\u91cc\u89c1\u8fc7\u4e00\u822c\uff0c\u5c31\u662f\u8fd9\u9ebd\u7565\u4e00\u5206\u5fc3\uff0c\u51e0\u4e4e\u7ed9\u90a3\u4eba\u6a2a\u638c\u5288 \u4e2d\u3002 \u3000\u3000\u8fd9\u65f6\u5b98\u4e2d\u9ad8\u624b\u56db\u9762\u8d76\u6765\uff0c\u5e94\u4fee\u9633\u53eb\u9053\uff1a\u201c\u523a\u5ba2\u5728\u8fd9\u513f\uff01\u201d\u90a3\u8499\u9762\u602a\u4eba\u7a81\u7136\u865a\u53d1\u4e00\u638c\uff0c\u8df3 \u843d\u5730\u9762\uff0c\u9690\u5165\u82b1\u6811\u4e1b\u4e2d\u3002\u7247\u523b\u4e4b\u5f8c\uff0c\u4ece\u5bab\u4e2d\u5404\u5904\u8d76\u6765\u7684\u536b\u58eb\u7eb7\u7eb7\u8df3\u4e0a\u74e6\u9762\u3002\uff0e \u3000\u3000\u5cb3\u545c\u73c2\u5927\u4e3a\u5947\u602a\uff0c\u8fd9\u8499\u9762\u5ba2\u6b66\u529f\u4e4b\u9ad8\uff0c\u4e0d\u5728\u201c\u9634\u98ce\u6bd2\u7802\u638c\u201d\u91d1\u72ec\u5f02\u4e4b\u4e0b\uff0c\u4ee5\u4e00\u5bf9\u4e00\uff0c\u81ea \u5df1\u7eb5\u7136\u672a\u5fc5\u843d\u8d25\uff0c\u4e5f\u7edd\u96be\u5360\u5f97\u4e0a\u98ce\uff0c\u82e5\u7136\u4ed6\u662f\u5bab\u4e2d\u536b\u58eb\uff0c\u4f55\u4ee5\u540c\u4f34\u6765\u65f6\uff0c\u4ed6\u53cd\u800c\u6084\u6084\u6e9c\u8d70\u3002 \u3000\u3000\u8499\u9762\u4eba\u4e00\u53bb\uff0c\u5bab\u4e2d\u536b\u58eb\u867d\u591a\uff0c\u5374\u6ca1\u6709\u6b66\u529f\u7279\u5f3a\u7684\u4eba\uff0c\u5cb3\u545c\u73c2\u8f7b\u529f\u65e2\u9ad8\uff0c\u53c8\u6709\u5b9d\u5251\uff0c\u4e14\u6218 \u4e14\u9000\uff0c\u4e0d\u8fc7\u7247\u523b\uff0c\u5c31\u9003\u81f3\u4e7e\u6e05\u5b98\u5916\uff0c\u4f17\u536b\u58eb\u8854\u5c3e\u6025\u8ffd\uff0c\u5927\u58f0\u5450\u558a\u3002\u5728\u6df7\u6218\u4e2d\uff0c\u5e94\u4fee\u9633\u4e5f\u6084\u6084 \u7684\u6e9c\u8d70\u4e86\u3002 \u3000\u3000\u518d\u8bf4\u5353\u4e00\u822a\u5728\u517b\u5fc3\u6bbf\u4e2d\u542c\u5f97\u5916\u9762\u547c\u559d  \u6740\u4e4b\u58f0\uff0c\u9760\u7a97\u4e00\u5f20\uff0c\u5ffd\u89c1\u7ed9\u536b\u58eb\u8ffd\u8d76\u7684\u7adf\u662f\u5cb3\u545c \u73c2\uff01\u5927\u5403\u4e00\u60ca\uff0c\u65e0\u6687\u601d\u7d22\uff0c\u4e5f\u6025\u5fd9\u4e00\u8dc3\u800c\u51fa\uff0c\u670d\u4f8d\u7687\u5e1d\u7684\u4f8d\u536b\u957f\u6b63\u62d4\u5200\u62e6\u5835\uff0c\u9aa4\u89c1\u5353\u4e00\u822a\u51b2 \u51fa\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5353\u4e00\u822a\u5df2\u4e00\u628a\u5c06\u5cb3\u545c\u73c2\u626f\u4eba\u517b\u5fc3\u6bbf\u5185\uff0c\u5728\u7687\u5e1d\u9762\u524d\u53cc\u53cc\u8dea\u4e0b\u3002 \u3000\u3000\u5e38\u6d1b\u7a81\u5403\u4e00\u60ca\uff0c\u51b7\u6c57\u8ff8\u6d41\uff0c\u6307\u7740\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u5e26\u5251\u6765\u4f5c\u751a\uff1f\u201d\u5353\u4e00\u822a\u6025\u7980 \u9053\uff1a\u201c\u4ed6\u662f\u718a\u7ecf\u7565\u7684\u4f7f\u8005\uff0c\u5fae\u81e3\u613f\u4ee5\u6027\u547d\u4fdd\u4ed6\uff01\u201d\u5cb3\u545c\u73c2\u63d2\u5251\u5f52\u9798\uff0c\u9053\uff1a\u201c\u5723\u4e0a\uff0c\u5bab\u4e2d\u51fa\u4e86 \u6deb\u90aa\u5996\u5b7d\uff0c\u8bf7\u5bb9\u5fae\u81e3\u7ec6\u7980\u3002\u201d\u5e38\u6d1b\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u795e\u667a\u53cd\u800c\u7565\u89c1\u6e05\u9192\uff0c\u718a\u5ef7\u5f3c\u8d64\u80c6\u5fe0\u5fc3\uff0c\u4ed6 \u7d20\u6765\u77e5\u9053\uff0c\u6325\u624b\u53eb\u9053\uff1a\u201c\u6210\u5764\uff0c\u4f60\u5429\u5490\u90a3\u4e9b\u5974\u624d\uff0c\u90fd\u9000\u56de\u53bb\uff01\u201d \u3000\u3000\u6210\u5764\u662f\u90a3\u4f8d\u536b\u957f\u7684\u540d\u5b57\uff0c\u4e3a\u4eba\u5012\u8fd8\u6b63\u76f4\u5fe0\u5fc3\uff0c\u4e5f\u77e5\u5bab\u4e2d\u6d3e\u522b\u5206\u6b67\uff0c\u4e1c\u5382\u81ea\u6210\u4e00\u7cfb\u7b49\u4e8b \u60c5\u3002\u542c\u5f97\u8fd9\u201c\u523a\u5ba2\u201d\u662f\u718a\u7ecf\u7565\u7684\u4eba\uff0c\u5df2\u653e\u4e0b\u4e86\u4e00\u534a\u5fc3\uff0c\u518d\u542c\u5f97\u7687\u4e0a\u5429\u5490\uff0c\u7b54\u9053\uff1a\u201c\u5974\u624d\u9075 \u547d\u3002\u201d\u6a2a\u5200\u7acb\u5728\u95e8\u53e3\uff0c\u8ffd\u6765\u7684\u536b\u58eb\uff0c\u90fd\u7ed9\u4ed6\u65a5\u4e86\u56de\u53bb\uff01 \u3000\u3000\u518d\u8bf4\u5cb3\u9e23\u73c2\u88ab\u7687\u5e1d\u4e00\u559d\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u628a\u9f99\u6cc9\u5b9d\u5251\u6367\u4e0a\u53bb\u9053\uff1a\u201c\u5723\u4e0a\uff0c\u8bf7\u770b\u8fd9\u662f\u4e0d\u662f\u5bab\u4e2d \u4e4b\u7269\uff1f\u201d\u5e38\u6d1b\u63a5\u6765\u4e00\u770b\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u5f97\u6765\u7684\uff1f\u201d\u5cb3\u545c\u73c2\u8dea\u5728\u69bb\u524d\uff0c\u5c06\u201c\u5947\u9047\u201d\u7980\u544a\uff0c\u521a \u8bf4\u5230\u9047\u89c1\u7f8e\u5987\u4e4b\u4e8b\uff0c\u5e38\u6d1b\u9053\uff1a\u201c\u662f\u4e0d\u662f\u68b3\u7740\u76d8\u9f99\u53cc\u9afb\uff0c\u8138\u513f\u5706\u5706\u7684\uff1f\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u6b63 \u662f\u3002\u201d\u5e38\u6d1b\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u6c14\u6b7b\u6211\u4e5f\uff01\u201d\u6655\u4e86\u8fc7\u53bb\uff0c\u5353\u4e00\u822a\u6025\u5fd9\u4e0a\u524d\u66ff\u4ed6\u63c9\u6413\uff0c\u6210\u5764\u4e5f\u56de\u8f6c\u8eab \u6765\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u5e38\u6d1b\u60a0\u60a0\u9192\u8f6c\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u4e14\u9000\u4e0b\u53bb\uff0c\u8fd9\u4e8b\u4e0d\u8981\u4e71\u8bf4\u3002\u6210\u5764\uff0c\u5feb\u628a\u65b9\u4ece  \u548c \u674e\u9009\u4f8d\u53eb\u6765\u3002\u201d\u5353\u4e00\u822a\u634f\u4e86\u628a\u6c57\uff0c\u548c\u5cb3\u545c\u73c2\u8d70\u51fa\uff0c\u9065\u89c1\u4e7e\u6e05\u5bab\u4e2d\uff0c\u4e00\u961f\u5bab\u5a25\u8d70\u51fa\uff0c\u4e8c\u4eba\u4e0d\u6562 \u505c\u7559\uff0c\u6025\u6025\u56de\u5230\u4f53\u4ec1\u9601\u5185\u3002\u5019\u5ba3\u7684\u5b98\u513f\u89c1\u7a81\u7136\u591a\u51fa\u4e00\u4eba\uff0c\u51e0\u5341\u53cc\u773c\u775b\uff0c\u90fd\u770b\u7740\u5cb3\u545c\u73c2\u3002\u6768 \u6084\u6084\u95ee\u9053\uff1a\u201c\u7687\u4e0a\u600e\u9ebd\u4e86\uff01\u201d\u5353\u4e00\u822a\u4e0d\u6562\u56de\u7b54\uff0c  \u6447\u4e86\u6447\u5934\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u5185\u91cc\u9690\u9690\u4f20\u51fa\u54ed \u58f0\uff0c\u5185\u76d1\u8d70\u51fa\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u6563\u4e86\u5427\uff0c\u7687\u4e0a\u4eca\u5929\u4e0d\u89c1\u4f60\u4eec\u4e86\u3002\u201d \u3000\u3000\u51fa\u4e86\u5348\u95e8\uff0c\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u770b\u6765\u7687\u4e0a\u53ea\u6015\u96be\u4fdd\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5927\u660e\u7684\u56fd\u8fd0\uff0c\u53ea\u597d\u4ed8\u4e4b\u5929 \u610f\u4e86\u3002\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u7687\u4e0a\u867d\u975e\u5723\u660e\uff0c\u4f46\u4e5f\u8fd8\u8bc6\u5927\u4f53\uff0c\u82e5\u592a\u5b50\u7ee7\u4f4d\uff0c\u4ed6\u53ea\u662f\u4e2a\u65e0\u77e5\u5c0f\u513f\uff0c\u5916 \u6709\u6743\u81e3\uff0c\u5185\u6709\u5978\u9609\uff0c\u5bab\u4e2d\u53c8\u6deb\u4e71\u8352\u9761\uff0c\u53ea\u6015\u4e0d\u5fc5\u7b49\u6ee1\u4eba\u4eba\u5173\uff0c\u5929\u4e0b\u5148\u81ea\u4ea1\u4e86\u3002\u201d\u6768  \u89c1\u4ed6\u4eec \u7adf\u7136\u8bae\u8bba\u7687\u4e0a\uff0c\u8086\u8a00\u65e0\u5fcc\uff0c\u6025\u5fd9\u5f15\u5f00\u8bdd\u5934\u3002\u5cb3\u545c\u73c2\u95ee\u4e86\u5353\u4e00\u822a\u4f4f\u5740\uff0c\u9053\uff1a\u201c\u660e\u65e5\u6211\u6765\u89c1 \u4f60\u3002\u201d\u4e24\u4eba\u62f1\u624b\u76f8\u522b\u3002 \u3000\u3000\u90a3\u77e5\u7b2c\u4e8c\u65e5\u5bab\u4e2d\u4fbf\u4f20\u51fa\u7687\u4e0a\u9a7e\u5d29\u7684\u6d88\u606f\uff0c\u767e\u5b98\u4e3e\u54c0\uff0c\u81ea\u4e0d\u6d88\u8bf4\u3002\u592a\u5b50\u7531\u6821\u5373\u4f4d\uff0c\u6539\u5143\u5929 \u542f\uff0c\u5bab\u4e2d\u4e71\u7eb7\u7eb7\u7684\uff0c\u90a3\u674e\u53ef\u707c\u8fdb\u4e86\u7ea2\u4e38\uff0c\u836f\u6b7b\u7687\u5e1d\uff0c\u975e\u4f46\u6ca1\u6709\u7f6a\u540d\uff0c\u5bb0\u76f8\u65b9\u4ece  \u53cd\u8bf4\u662f\u7687\u5e1d \u4f20\u6709\u9057\u65e8\uff0c\u8bf4\u674e\u53ef\u707c\u4e43\u662f\u5fe0\u81e3\uff0c\u8d4f\u4ed6\u94f6\u4e24\u3002\u7fa4\u81e3\u95fb\u8baf\u54d7\u7136\uff0c\u6709\u4e00\u73ed\u4e0d\u6015\u6b7b\u7684\u5b98\u513f\u5982\u793c\u90e8\u5c1a\u4e66 \u5b59\u614e\u884c\uff0c\u5fa1\u53f2\u738b\u5b89\u821c\uff0c\u7ed9\u4e8b\u4e2d\u60e0\u4e16\u626c\u7b49\u4fbf\u5546\u8bae\u4e0a\u594f\u7ae0\u53c2\u4ed6\uff0c\u8bf4\u65b9\u4ece\u54f2\u6709\u5f11\u541b\u7684\u7f6a\u540d\u3002\u8fd9\u4e8b\u95f9 \u4e86\u5f88\u4e45\uff0c\u5f8c\u6765\u65b9\u4ece\u54f2\u7ec8\u65bc\u9760\u9b4f\u5fe0\u8d24\u4e4b\u529b\uff0c\u5c06\u8fd9\u4e2a\u60ca\u52a8\u5929\u4e0b\u7684\u7ea2\u4e38\u6848\u5b50\u538b\u4e86\u4e0b\u53bb\uff0c\u8fd9\u662f\u5f8c\u8bdd\uff0c \u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u4e14\u8bf4\u5cb3\u545c\u73c2\u5f53\u65e5\u56de\u5230\u6768\u6d9f\u5bb6\u4e2d\uff0c\u628a\u4e8b\u60c5\u4e0e\u94c1\u73ca\u745a\u8bf4\uff0c\u6168\u53f9\u4e0d\u5df2\u3002\u94c1\u73ca\u745a\u7b11\u9053\uff1a\u201c\u53ea\u6709\u4f60 \u4eec\u8fd9\u73ed\u50bb\u74dc\uff0c\u4ee5\u5929\u4e0b\u4e3a\u5df2\u4efb\uff0c\u6276\u52a9\u7684\u5374\u662f\u8fd9\u6837\u7cdc\u70c2\u7684\u7687\u671d\uff0c\u5012\u4e0d\u5982\u91ce\u9e64\u95f2\u4e91\uff0c\u5728\u6c5f\u6e56\u4e0a\u884c\u4fa0 \u4ed7\u4e49\u8fd8\u6765\u5f97\u75db\u5feb\u3002\u201d\u5cb3\u545c\u73c2\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u4f60\u5f53\u6211\u53ea\u662f\u4e3a\u6276\u52a9\u59d3\u6731\u7684\u4e00\u5bb6\u9ebd\uff1f\u201d\u94c1\u73ca\u745a\u7b11 \u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u8fd8\u6709\u62b5\u5fa1\u5916\u65cf\u4eba\u4fb5\u6240\u4ee5\u5fc5\u987b\u6276\u52a9\u7687\u5e1d\u7684\u4e00\u756a\u9053\u7406\uff0c\u662f\u9ebd\u4e86\u5176\u5b9e\u8981\u62b5\u6297\u9791\u5b50\uff0c \u4f55\u5fc5\u4e00\u5b9a\u8981\u4e2a\u7687\u5e1d\uff01\u201d \u3000\u3000\u5cb3\u545c\u73c2\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u6211\u4ee5\u4e3a\u8fd9\u59ae\u5b50\u5168\u4e0d\u61c2\u4e8b\uff0c\u90a3\u77e5\u5979\u4e5f\u6709\u4e00\u756a\u9053\u7406\u3002\u5f53\u4e0b\u4e0d\u518d\u8a00 \u8bed\u3002\u94c1\u73ca\u745a\u9053\uff1a\u201c\u6211\u4e0d  \u89c1\u90a3\u5353\u4e00\u822a\uff0c\u4f60\u4e0d\u8981\u8bf4\u6211\u5728\u8fd9\u91cc\u3002\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u94c1\u73ca \u745a\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4e0d\u4e3a\u4ec0\u9ebd\uff0c\u5c31\u662f\u4e0d\u559c\u6b22\u89c1\u4ed6\u3002\u201d\u539f\u6765\u94c1\u73ca\u745a\u4ee5\u524d\u4e0e\u738b\u7167\u5e0c\u6709\u8fc7\u8bba\u5a5a\u4e0d\u6210 \u4e4b\u4e8b\uff0c\u94c1\u73ca\u745a\u77e5\u9053\u5353\u4e00\u822a\u4e0e\u738b\u7167\u5e0c\u4ea4\u60c5\u751a\u539a\uff0c\u6599\u4ed6\u5fc5\u77e5\u6b64\u4e8b\uff0c\u6240\u4ee5\u4e0d\u60f3\u89c1\u4ed6\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u5cb3\u545c\u73c2\u4f9d\u7ea6\u5230\u6768  \u5bb6\u4e2d\uff0c\u6768  \u5df2\u548c\u540c\u50da\u5546\u8bae\u53c2\u65b9\u4ece\u54f2\u7684\u4e8b\u53bb\u4e86\u3002\u5353\u4e00\u822a\u5355\u72ec\u548c\u5cb3 \u545c\u73c2\u4f1a\u9762\u3002\uff0e\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6cf0\u660c\u7687\u5e1d\u8fd9\u6837\u5feb\u4fbf\u6b7b\uff0c\u5bab\u4e2d\u7684\u4e11\u4e8b\u65e0\u4eba\u518d\u7ba1\u4e86\u3002\u201d\u5353\u4e00\u822a \u53f9\u4e86\u53e3\u6c14\uff0c\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u8fd9\u8d9f\u56de\u4eac\uff0c\u770b\u4e86\u8bb8\u591a\u4e8b\u60c5\uff0c\u6211\u4e5f\u6709\u70b9\u5fc3\u7070\u610f\u51b7\u3002\u53ea\u662f\u65b0\u541b\u5373\u4f4d\u4e4b \u5f8c\uff0c\u638c\u6743\u7684\u4e00\u5b9a\u662f\u9b4f\u5fe0\u8d24\u65b9\u4ece\u54f2\u8fd9\u4e00\u73ed\u4eba\uff0c\u4ed6\u4eec\u548c\u718a\u7ecf\u7565\u4e00\u5411\u4f5c\u5bf9\uff0c\u6211\u82e5\u4e0d\u662f\u4e3a\u4e86\u8001\u5e08\uff0c\u771f \u7684\u60f3\u51fa\u5bb6\u53bb\u4e86\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u4eec\u4e14\u505c\u7559\u51e0\u65e5\uff0c\u770b\u770b\u5982\u4f55\uff1f\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u671d\u653f\u4e0d\u582a\u95fb \u95ee\uff0c\u6211\u4e5f\u4e0d\u613f\u518d\u7406\u4e86\u3002\u53ea\u662f\u6211\u4eca\u665a\u8fd8\u8981\u8fdb\u5bab\u4e00\u8d9f\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4e3a\u4f55\u8981\u5192\u6b64\u4eba\u9669\uff1f\u201d\u5cb3\u545c \u73c2\u9053\uff1a\u201c\u6211\u7684\u6e38\u9f99\u5251\u5931\u5728\u5bab\u4e2d\uff0c\u6211\u4e00\u5b9a\u8981\u63a2\u5b83\u4e00\u63a2\u3002\u201d\u5353\u4e00\u822a\u5fc3\u5ff5\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u6211\u966a\u4f60\u540c\u53bb \u5982\u4f55\uff1f\u201d\u5cb3\u9e23\u73c2\u5fc3\u60f3\u5353\u4e00\u822a\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u8fd8\u672a\u5230\u767b\u5cf0\u9020\u6781\u7684\u5730\u6b65\uff0c\u82e5\u7136\u9047\u9669\uff0c\u53ea\u6015\u9003\u4e0d\u51fa \u6765\u3002\u4fbf\u9053\uff1a\u201c\u591c\u63a2\u6df1\u5bab\uff0c\u4eba\u591a\u53cd\u800c\u4e0d\u4fbf\uff0c\u6211\u5144\u76db\u60c5\uff0c\u5c0f\u5f1f\u5fc3\u9886\u4e86\u3002\u201d\u5353\u4e00\u822a\u82e5\u6709\u6240\u601d\uff0c\u4e45\u4e45 \u4e0d\u8bed\u3002\u5ffd\u9053\uff1a\u201c\u6211\u548c\u4f60\u540c\u53bb\u89c1\u6211\u7684\u5e08\u53d4\u5982\u4f55\uff1f\u201d\u5cb3\u9e23\u73c2\u95ee\u9053\uff1a\u201c\u90a3\u4f4d\u9053\u957f\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a \u201c\u56db\u5e08\u53d4\u767d\u77f3\u9053\u4eba\u3002\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u4e45\u95fb\u6b66\u5f53\u4e94\u8001\u4e4b\u540d\uff0c\u4f55\u51b5\u53c8\u662f\u4f60\u7684\u5e08\u53d4\uff0c\u65e2\u7136\u5728\u6b64\uff0c\u81ea \u5f53\u62dc\u89c1\u3002\u201d \u3000\u3000\u767d\u77f3\u9053\u4eba\u7236\u5973\u5bc4\u5c45\u5728\u6b66\u5e08\u67f3\u897f\u94ed\u5bb6\u4e2d\uff0c\u79bb\u6768  \u5bb6\u6709\u5341\u9980\u91cc\u8def\u3002\u5353\u4e00\u822a\u548c\u5cb3\u545c\u73c2\u5230\u4e86\u67f3 \u5bb6\uff0c\u6572\u95e8\u597d\u4e45\uff0c\u624d\u6709\u4eba\u5f00\u3002\u5f00\u95e8\u7684\u7adf\u7136\u4e0d\u662f\u67f3\u5bb6\u7684\u4eba\uff0c\u800c\u662f\u4f55\u843c\u534e\uff0c\u5353\u4e00\u822a\u5fae\u5fae\u4e00\u6115\u3002\u5fc3 \u60f3\uff1a\u67f3\u5bb6\u7684\u4eba\u90a3\u91cc\u53bb\u4e86\uff0c\u600e\u9ebd\u8981\u5ba2\u4eba\u6765\u5f00\u95e8\uff1f \u3000\u3000\u4f55\u843c\u534e\u9762\u4e0a\u4e5f\u6709\u60ca\u6115\u4e4b\u5bb9\uff0c\u6c34\u6c6a\u6c6a\u7684\u4e00\u5bf9\u773c\u775b\u76ef\u7740\u5353\u4e00\u822a\u4f3c\u4e4e\u6709\u4ec0\u9ebd\u8bdd\u8981\u8bf4\u53c8\u8bf4\u4e0d\u51fa \u6765\uff0c\u5353\u4e00\u822a\u4f4e\u4e0b\u4e86\u5934\uff0c\u5cb3\u9e23\u73c2\u77a7\u5728\u773c\u91cc\uff0c\u6697\u6697\u5077\u7b11\u3002 \u3000\u3000\u4f55\u843c\u534e\u628a\u4e24\u4eba\u5e26\u5230\u897f\u9762\u5ba2\u623f\uff0c\u6572\u95e8\u53eb\u9053\uff1a\u201c\u7238\uff0c\u5353\u5e08\u54e5\u548c\u4ed6\u7684\u670b\u53cb\u6765\u89c1\u4f60\u3002\u201d\u767d\u77f3\u9053\u4eba \u6253\u5f00\u623f\u95e8\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u6211\u9053\u662f\u90a3\u4e00\u4f4d\uff0c\u539f\u6765\u662f\u5cb3\u82f1\u96c4\uff01\u201d\u5cb3\u9e23\u73c2\u5927\u60d1\u4e0d\u89e3\uff0c\u4e0d\u77e5\u767d\u77f3 \u9053\u4eba\u4f55\u4ee5\u8ba4\u8bc6\u81ea\u5df1\u3002\u5353\u4e00\u822a\u5728\u65c1\u7b11\u9053\uff1a\u201c\u5cb3\u5144\u5c11\u6797\u53d6\u4e66\uff0c\u8fde\u95ef\u4e94\u5173\u4e4b\u591c\uff0c\u655d\u5e08\u53d4\u4e5f\u6b63\u5728\u5c11\u6797 \u5bfa\u4e2d\u3002\u201d\u767d\u77f3\u9053\uff1a\u201c \u4f60\u7684\u5251\u4f7f\u5f97\u5f88\u597d\uff01\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u6b66\u5f53\u5251\u6cd5\u5929\u4e0b\u72ec\u6b65\uff0c\u8fd8\u8981\u8bf7\u9053\u957f\u6307 \u70b9\u3002\u201d\u767d\u77f3\u9053\u4eba\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5cb3\u82f1\u96c4\u8fc7\u8c26\u4e86\uff0c\u957f\u6c5f\u5f8c\u6d6a\u63a8\u524d\u6d6a\uff0c\u6b66\u5f53\u7684\u5251\u6cd5\u5df2\u8fdc\u8fdc\u843d\u5728\u5f8c\u9762 \u4e86\u3002\u201d\u767d\u77f3\u5fc3\u80f8\u8f83\u7a84\uff0c\u5728\u5c11\u6797\u5bfa\u65f6\u5c31\u66fe\u56e0\u955c\u660e\u957f\u8001\u8fc7\u65bc\u63a8\u5d07\u5cb3\u545c\u73c2\u7684\u5929\u5c71\u5251\u6cd5\uff0c\u5fc3\u4e2d\u4e0d\u5feb\u3002 \u5353\u4e00\u822a\u7edd\u6599\u4e0d\u5230\u5e08\u53d4\u6709\u5982\u6b64\u59a8\u5fcc\u4e4b\u5fc3\uff0c\u9887\u89c9\u5e08\u53d4\u6001\u5ea6\u5f02\u5e38\uff0c\u5cb3\u9e23\u73c2\u66f4\u662f\u5c34\u5c2c\u4e0d\u5b89\u3002 \u3000\u3000\u767d\u77f3\u9053\uff1a\u201c\u5cb3\u82f1\u96c4\u8bf7\u7a0d\u5750\uff0c\u8d2b\u9053\u6709\u4e9b\u5c0f\u4e8b\uff0c\u8981\u4e0e\u655d\u5e08\u4f84\u4e00\u8c08\u3002\u201d\u7275\u5353\u4e00\u822a\u7684\u624b\u8d70\u4eba\u5185 \u5ba4\u3002\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u8bf7\u4fbf\u3002\u201d\u67af\u5750\u5ba2\u5385\uff0c\u5341\u5206\u65e0\u8da3\u3002\u731c\u4e0d\u900f\u767d\u77f3\u9053\u4eba\uff0c\u4e3a\u4f55\u5bf9\u81ea\u5df1\u5982\u6b64\u795e\u60c5\u51b7 \u6f20\u3002 \u3000\u3000\u5353\u4e00\u822a\u66f4\u662f\u5927\u60d1\u4e0d\u89e3\uff0c\u968f\u767d\u77f3\u9053\u4eba\u8fdb\u5165\u5185\u5ba4\uff0c\u5fae\u6120\u95ee\u9053\uff1a\u201c\u90a3\u5cb3\u545c\u73c2\u662f\u5f53\u4eca\u4fa0\u58eb\uff0c\u53c8\u4e0e \u5f1f\u5b50\u751a\u662f\u6295\u7f18\uff0c\u4e0d\u77e5\u5e08\u53d4\u4f55\u4ee5\u5bf9\u4ed6\u51b7\u6de1\uff1f\u201d\u767d\u77f3\u9053\u4eba\u9053\uff1a\u201c\u4ed6\u65e2\u662f\u5f53\u4eca\u4fa0\u58eb\uff0c\u90a3\u5b9a\u4e0d\u4f1a\u62d8\u6ce5 \u5ba2\u5957\u4fd7\u793c\u3002\u6211\u6709\u4e8b\u8981\u548c\u4f60\u8bf4\uff0c\u8ba9\u4ed6\u5750\u4e00\u4f1a\u6709\u4ec0\u9ebd\u8981\u7d27\uff01\u201d\u767d\u77f3\u9053\u4eba\u7684\u8bdd\u867d\u9887\u4e3a\u5f3a\u8f9e\u593a\u7406\uff0c\u4f46 \u5353\u4e00\u822a\u8eab\u5c45\u5f8c\u8f88\uff0c\u5374\u4e0d\u4fbf\u53cd\u9a73\uff0c\u53ea\u5f97\u606d\u656c\u95ee\u9053\uff1a\u201c\u5e08\u53d4\u6709\u4ec0\u9ebd\u5429\u5490\uff1f\u201d \u3000\u3000\u767d\u77f3\u9053\u4eba\u6b47\u4e86\u534a\u664c\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u6cf0\u660c\u7687\u5e1d\u65e2\u6b7b\uff0c\u4f60\u7684\u4e8b\u4e5f\u5f04\u6e05\u695a\u4e86\uff0c\u4f60\u8be5\u968f\u6211\u56de \u5c71\u4e86\u5427\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u4e2a\uff0c\u5f1f\u5b50\u8fd8\u60f3\u9017\u7559\u51e0\u65e5\u3002\u201d\u767d\u77f3\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u5353\u4e00\u822a \u55eb\u5685\u8bf4\u9053\uff1a\u201c\u5f1f\u5b50\u4e0e\u5cb3\u5927\u54e5\u6709\u4e2a\u7ea6\u4f1a\u3002\u4ed6\u7684\u5b9d\u5251\u5931\u843d\u5728\u7687\u5bab\u4e4b\u5185\uff0c\u5185\u60c5\u53e4\u602a\u975e\u5e38\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u5c06\u5cb3\u545c\u73c2\u5bab\u4e2d\u5386\u9669\u7684\u4e8b\u8bf4\u4e86\uff0c\u767d\u77f3\u9053\u4eba\u76b1\u7709\u9053\uff1a\u201c\u5c45\u7136\u6709\u8fd9\u6837\u7684\u4e8b\uff01\u201d\u5353\u4e00\u822a \u9053\uff1a\u201c\u56fd\u4e4b\u5c06\u4ea1\uff0c\u5fc5\u6709\u5996\u5b7d\u3002\u4f46\u5f1f\u5b50\u4e16\u53d7\u56fd\u6069\uff0c\u89c1\u4e86\u8fd9\u6837\u7684\u4e8b\uff0c\u603b\u89c9\u5f97\u96be\u8fc7\u3002\u201d\u767d\u77f3\u9053\uff1a \u201c\u90a3\u9ebd\u4f60\u662f\u60f3\u52a9\u5cb3\u545c\u73c2\u4e00\u81c2\u4e4b\u529b\uff0c\u548c\u4ed6\u591c\u63a2\u7687\u5bab\uff0c\u67e5\u660e\u6b64\u4e8b\u4e86\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d\u767d \u77f3\u9053\u4eba\u5ffd\u9053\uff1a\u201c\u81ea\u5df2\u7684\u4e8b\u60c5\u90fd\u7406\u4e0d\u4e86\uff0c\u8fd8\u7406\u522b\u4eba\u7684\u5462\uff01\u201d\u7a81\u7136\u89e3\u5f00\u8863\u88f3\uff0c\u9053\uff1a\u201c\u4f60\u770b\uff01\u201d \u3000\u3000\u767d\u77f3\u9053\u4eba\u8892\u5f00\u80f8\u819b\uff0c\u80f8\u819b\u4e0a\u6709\u4e00\u4e2a\u6de1\u7ea2\u7684\u624b\u5370\uff01\u5353\u4e00\u80ae\u9a87\u7136\u95ee\u9053\uff1a\u201c\u5e08\u53d4\u4f60\u53d7\u4e86\u6697\u7b97 \u4e86\uff1f\u201d\u767d\u77f3\u9053\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8981\u548c\u4f60\u5546\u91cf\uff0c\u54b1\u4eec\u662f\u56de\u5c71\u5462\uff1f\u8fd8\u662f\u7559\u5728\u8fd9\u91cc\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u9053\uff1a\u201c\u8fd9\u662f\u9634\u98ce\u6bd2\u7802\u638c\u91d1\u8001\u602a\u7684\u624b\u6cd5\uff0c\u4f60\u78b0\u5230\u4ed6\u4e86\uff01\u201d\u767d\u77f3\u9053\uff1a\u201c\u82e5\u662f\u91d1\u8001\u602a\uff0c \u6211\u53ea\u6015\u7559\u4e0d\u7740\u6027\u547d\u89c1\u4f60\u4e86\u3002\u8fd9\u4eba\u529f\u529b\u8981\u6bd4\u91d1\u8001\u602a\u7a0d\u900a\u4e00\u7b79\u3002\u201d \u3000\u3000\u767d\u77f3\u9053\u4eba\u4ee5\u624b\u51fb\u638c\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6628\u65e5\u9ec4\u660f\u65f6\u5206\uff0c\u6211\u72ec\u81ea\u5230\u5929\u6865\u6e9c\u8fbe\uff0c\u6709\u4e00\u6863\u5356\u6b66\u7684\uff0c \u8d70\u94a2\u7ebf\uff0c\u800d\u9a6c\u6280\uff0c\u5012\u8fd8\u6709\u70b9\u771f\u5b9e\u529f\u592b\u3002\u6211\u6b63\u770b\u5f97\u51fa\u795e\uff0c\u5ffd\u7136\u6709\u4e00\u4e2a\u6076\u9738\u6a21\u6837\u7684\u6d53\u7709\u5927\u773c\u7684\u6c49 \u5b50\u8fdb\u573a\u6536\u53d6\u89c4\u94b1\u3002\u5356\u6280\u7684\u8001\u513f\u6253\u62f1\u4f5c\u63d6\uff0c\u5341\u5206\u53ef\u601c\uff0c\u4e5e\u6c42\u4ed6\u9053\uff1a\u201c\u4eca\u65e5\u6574\u65e5\u6ca1\u53d1\u5e02\uff0c\u4f60\u8001\u9ad8 \u62ac\u8d35\u624b\uff0c\u5bbd\u9650\u4e9b\u513f\u5427\u3002\u201d\u90a3\u6076\u9738\u5927\u547c\u5c0f\u559d\uff0c  \u662f\u4e0d\u5141\u3002\u662f\u6211\u8def\u89c1\u4e0d\u5e73\uff0c\u8fdb\u573a\u53bb\u6b62\u7740\u90a3\u4e2a\u6076 \u9738\uff0c\u7565\u4e00\u52a8\u624b\uff0c\u628a\u4ed6\u8dcc\u4e86\u4e2a\u56db\u811a\u671d\u5929\uff0c\u50cf\u6761\u72d7\u4f3c\u7684\u5939\u7740\u5c3e\u5df4\u8d70\u4e86\u3002\u90a3\u5356\u6280\u8001\u513f\u5bf9\u6211\u5343\u591a\u8c22\u4e07 \u591a\u8c22\uff0c\u8fd9\u65f6\u5929\u5df2\u9ec4\u660f\uff0c\u53c8\u95f9\u4e86\u8fd9\u4e00\u573a\u4e8b\uff0c\u770b\u5ba2\u90fd\u5df2\u6563\u4e86\u3002\u90a3\u8001\u513f\u4fbf\u9080\u6211\u5230\u4ed6\u7684\u5e10\u5e55\u4e2d\u559d\u676f\u6de1 \u9152\u3002\u6211\u4e0d\u6599\u6709\u4ed6\uff0c\u4fbf\u968f\u4ed6\u53bb\u4e86\u3002\u90a3\u77e5\u8fd9\u8001\u513f\u5374\u662f\u7ec3\u5c31\u9634\u98ce\u6bd2\u7802\u638c\u7684\u9ad8\u624b\uff01\u5728\u4ed6\u628a\u9152\u9012\u8fc7\u6765 \u65f6\uff0c\u7a81\u7136\u4e00\u638c\u6253\u5728\u6211\u7684\u80f8\u4e0a\uff01\u201d\u5353\u4e00\u822a\u201c\u54ce\u5537\u201d\u4e00\u58f0\uff0c\u767d\u77f3\u7b11\u9053\uff1a\u201c\u4f46\u4ed6\u5360\u4e0d\u4e86\u4fbf\u5b9c\uff0c\u6211\u5403 \u4e86\u4e00\u638c\uff0c\u8fd8\u4ed6\u4e8c\u6307\uff0c\u628a\u4ed6\u7684\u6108\u6c14\u7a74\u70b9\u4e86\uff0c\u9976\u4ed6\u6b66\u529f\u591a\u9ad8\uff0c\u4e5f\u5f97\u843d\u4e2a\u6b8b\u5e9f\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u8fd9 \u6837\u8bf4\u6765\uff0c\u91d1\u8001\u602a\u4e5f\u4e00\u5b9a\u5230\u4e86\u4eac\u57ce\u6765\u4e86\uff01\u201d \u3000\u3000\u767d\u77f3\u9053\u4eba\u7eed\u9053\uff1a\u201c\u90a3\u5356\u6280\u7684\u8001\u5934\u513f\u9003\u51fa\u5e10\u7bf7\uff0c\u4e34\u884c\u559d\u9053\uff1a\u201c\u767d\u77f3\u8d3c\u9053\uff0c\u4f60\u53c1\u65e5\u5185\u82e5\u4e0d\u56de \u5c71\uff0c\u8fd8\u6709\u4eba\u8981\u656c\u4f60\u4e00\u638c\uff01\u201d\u6211\u6015\u4ed6\u8fd8\u6709\u540c\u515a\uff0c\u6025\u56de\u67f3\u5bb6\u3002\u90a3\u6599\u67f3\u5bb6\u4e5f\u95f9\u5f97\u5929\u7ffb\u5730\u8986\u3002\u201d\u5353\u4e00 \u822a\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6211\u4eca\u65e5\u6765\u65f6\uff0c\u4e0d\u89c1\u67f3\u5bb6\u7684\u4eba\u5f00\u95e8\u3002\u201d\u767d\u77f3\u9053\uff1a\u201c\u67f3\u6b66\u5e08\u9080\u8bf7\u5e2e\u624b\u53bb\u4e86\u3002\u201d\u5353 \u4e00\u822a\u9053\uff1a\u201c\u600e\u9ebd\u4e86\u67f3\u6b66\u5e08\u5728\u4eac\u4e2d\u5fb7\u9ad8\u671b\u91cd\uff0c\u6781\u5f97\u4eba\u548c\uff0c\u96be\u9053\u4e5f\u6709\u4eba\u5411\u4ed6\u5bfb\u4ec7\u5417\uff1f\u201d\u767d\u77f3\u9053\uff1a \u201c\u5c31\u5728\u6211\u9047\u4e8b\u7684\u65f6\u5019\uff0c\u67f3\u5bb6\u4e5f\u6765\u4e86\u51e0\u4e2a\u4e0d\u901f\u4e4b\u5ba2\uff0c\u58f0\u52bf\u6c79\u6c79\uff0c\u4e0d\u51c6\u4ed6\u7559\u6211\u5728\u4ed6\u5bb6\u5c45\u4f4f\u3002\u539f\u6765 \u8fd9\u4e9b\u4eba\u548c\u4ed6\u5e76\u65e0\u4ec7\u51a4\uff0c\u800c\u662f\u51b2\u7740\u6211\u6765\u7684\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u8fd9\u5012\u5947\u4e86\uff0c\u6211\u4eec\u548c\u91d1\u8001\u602a\u4e95\u6c34\u4e0d\u72af \u6cb3\u6c34\uff0c\u6b66\u5f53\u4e94\u8001\u7684\u5a01\u540d\u66f4\u662f\u5929\u4e0b\u77e5\u95fb\uff0c\u4e3a\u4f55\u4ed6\u4eec\u504f\u8981\u4e0e\u5e08\u53d4\u4f5c\u5bf9\uff01\u201d\u767d\u77f3\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053 \u4ed6\u4eec\u7684\u7528\u610f\u3002\u6240\u4ee5\u6211\u548c\u4f60\u5546\u91cf\uff0c\u54b1\u4eec\u662f\u56de\u5c71\u7684\u597d\uff0c\u8fd8\u662f\u7559\u5728\u8fd9\u91cc\u63a5\u4ed6\u4eec\u8fd9\u4e2a\u78b4\u5b50\uff1f\u201d\u5353\u4e00\u822a \u9053\uff1a\u201c\u6309\u8bf4\uff0c\u82e5\u662f\u4e3a\u4e86\u4e0d\u60f3\u7275\u7d2f\u67f3\u8001\u524d\u8f88\uff0c\u90a3\u5f53\u7136\u662f\u56de\u5c71\u7684\u597d\u3002\u4f46\u73b0\u5728\u67f3\u6b66\u5e08\u5df2\u51fa\u53bb\u9080\u4eba\u52a9 \u62f3\uff0c\u90a3\u54b1\u4eec\u5012\u4e0d\u80fd\u4e00\u8d70\u4e86\u4e4b\u4e86\u3002\u201d\u767d\u77f3\u9053\uff1a\u201c\u7740\u5440\uff01\u4f60\u7684\u610f\u601d\u4e0e\u6211\u6b63\u597d\u4e00\u6837\u3002\u90a3\u9ebd\u5728\u8fd9\u53c1\u65e5 \u4e4b\u4e2d\uff0c\u4f60\u4e0d\u5fc5\u56de\u6768\u5bb6\u53bb\u4e86\u3002\u5c31\u7559\u5728\u8fd9\u513f\uff0c\u770b\u90a3\u4e9b\u4eba\u6562\u600e\u9ebd\u6837\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5cb3\u5927\u54e5\u5251\u672f\u7cbe \u5999\uff0c\u6b66\u827a\u9ad8\u5f3a\uff0c\u54b1\u4eec\u4f55\u4e0d\u4e0e\u4ed6\u8054\u624b\u5408\u6597\uff1f\u5148\u52a9\u4ed6\u4e00\u81c2\u4e4b\u529b\uff0c\u7136\u5f8c\u9080\u4ed6\u52a9\u62f3\uff1f\u201d\u767d\u77f3\u9053\u4eba\u9762\u8272 \u500f\u53d8\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4e00\u822a\uff0c\u4f60\u662f\u6211\u6d3e\u672a\u6765\u638c\u95e8\uff0c\u672c\u95e8\u7684\u89c4\u77e9\u4f60\u4e0d\u77e5\u9053\u5417\uff1f\u201d\u5353\u4e00\u822a\u60f6\u6050\u8bf4 \u9053\uff1a\u201c\u4e0d\u77e5\u5f1f\u5b50\u72af\u4e86\u90a3\u4e00\u6761\u89c4\u77e9\uff1f\u201d\u767d\u77f3\u9053\u4eba\u60f3\u4e86\u4e00\u9635\uff0c\u5ffd\u53c8\u54d1\u7136\u5931\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8bf4\u6765\u4e5f\u602a \u4e0d\u5f97\u4f60\u3002\u4f60\u51fa\u5e08\u4e0d\u8fc7\u4e24\u5e74\uff0c\u4f60\u5e08\u7236\u4e5f\u4e0d\u5927\u575a\u6301\u8fd9\u6761\u89c4\u77e9\uff0c\u60f3\u6765\u4ed6\u6ca1\u6709\u544a\u8bc9\u4f60\u4e86\u3002\u201d\u5353\u4e00\u822a\u8bb6 \u9053\uff1a\u201c\u5230\u5e95\u662f\u4ec0\u9ebd\u89c4\u77e9\uff1f\u201d\u767d\u77f3\u9053\uff1a\u201c\u8fd9\u89c4\u77e9\u5e76\u4e0d\u662f\u672c\u95e8\u7956\u8bad\uff0c\u4f46\u8fd1\u4e8c\u5341\u5e74\u6765\uff0c\u5927\u5bb6\u90fd\u662f\u8fd9 \u6837\u3002\u4f60\u77e5\u9053\u8fd9\u4e8c\u5341\u53c1\u5e74\uff0c\u6211\u6d3e\u76db\u6781\u4e00\u65f6\uff0c\u540c\u95e8\u904d\u5e03\u5404\u5730\uff0c\u6240\u4ee5\u4e00\u5411\u4e0e\u522b\u6d3e\u4e89\u6597\uff0c\u4ece\u4e0d\u9700\u4eba\u52a9 \u62f3\uff01\u61ca\u800c\u4e45\u4e4b\uff0c\u4e60\u4e3a\u98ce\u6c14\u3002\u51e1\u662f\u6b66\u5f53\u6d3e\u4eba\uff0c\u90fd\u4ee5\u7ea6\u4eba\u52a9\u62f3\u4e3a\u803b\uff0c\u60ed\u60ed\u4e5f\u5c31\u6210\u4e3a\u4e0d\u6210\u6587\u7684\u89c4\u77e9 \u4e86\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u90a3\u9ebd\u67f3\u6b66\u5e08\u7ea6\u4eba\u52a9\u62f3\uff0c\u5e08\u53d4\u96be\u9053\u4e5f\u4e0d\u8981\u4ed6\u4eec\u5e2e\u5fd9\u9ebd\uff1f\u201d\u767d\u77f3\u7b11\u9053\uff1a\u201c\u8fd9 \u4e2a\u4e0d\u540c\u3002\u4ed6\u4e0d\u662f\u6b66\u5f53\u6d3e\u4eba\uff0c\u4ed6\u7ea6\u4eba\u52a9\u62f3\uff0c\u867d\u7136\u4e0e\u6211\u6709\u5173\uff0c\u4f46\u90a3\u4e9b\u4eba\u662f\u51b2\u7740\u4ed6\u7684\u9762\u5b50\u800c\u6765\uff0c\u6211 \u4e0d\u5fc5\u9886\u4ed6\u4eec\u7684\u60c5\u3002\u201d\u5353\u4e00\u822a\u5fc3\u9053\uff1a\u8fd9\u771f\u662f\u4e2a\u602a\u89c4\u77e9\uff0c\u6211\u82e5\u505a\u4e86\u638c\u95e8\uff0c\u9996\u5148\u5c31\u8981\u5e9f\u9664\u8fd9\u6761\u3002\u6b66 \u6797\u4e2d\u5e94\u4ee5\u4fa0\u4e49\u4e3a\u5148\uff0c\u4e00\u5473\u7279\u5f3a\u81ea\u50b2\uff0c\u5230\u5e95\u4e0d\u662f\u6b66\u6797\u9886\u8896\u7684\u98ce\u8303\u3002\u4fa0\u4e49\u4e2d\u4eba\uff0c\u539f\u5e94\u5f7c\u6b64\u76f8\u52a9\u624d \u662f\u9053\u7406\u3002 \u3000\u3000\u767d\u77f3\u7eed\u9053\uff1a\u201c\u6211\u6d3e\u5f1f\u5b50\u4e0e\u522b\u6d3e\u4e89\u6597\u65f6\u4ece\u4e0d\u7ea6\u4eba\u52a9\u62f3\uff0c\u4e0d\u8fc7\uff0c\u82e5\u6709\u4eb2\u53cb\u77e5\u9053\u5176\u4e8b\uff0c\u81ea\u52a8\u51fa \u6765\u52a9\u62f3\uff0c\u90a3\u5012\u6ca1\u6709\u5173\u7cfb\u3002\u53ea\u662f\u6211\u4eec\u7edd\u4e0d\u80fd\u81ea\u5df1\u53bb\u9080\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u6211\u5012\u4e0d\u597d \u548c\u5cb3\u5927\u54e5\u8bf4\u4e86\u3002\u201d\u767d\u77f3\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\uff0c\u6240\u4ee5\u6211\u9002\u624d\u4e0d\u613f\u5f53\u7740\u4ed6\u7684\u9762\u548c\u4f60\u8c08\u8bb2\u3002\u6211\u6d3e\u5728\u4eac\u7684 \u5f1f\u5b50\u4e5f\u6709\u5341\u9980\u4eba\uff0c\u4eca\u65e5\u4f1a\u9646\u7eed\u5230\u67f3\u5bb6\u5468\u56f4\u57cb\u4f0f\uff01\u201d \u3000\u3000\u518d\u8bf4\u5cb3\u9e23\u73c2\u5728\u5ba2\u5385\u67af\u5750\u8bb8\u4e45\uff0c\u767d\u77f3\u9053\u4eba\u624d\u548c\u5353\u4e00\u822a\u51fa\u6765\uff0c\u5cb3\u9e23\u73c2\u5fc3\u4e2d\u4e0d\u5feb\uff0c\u6b20\u8eab\u8bf4\u9053\uff1a \u201c\u6253\u6270\u4e45\u4e86\u3002\u201d\u767d\u77f3\u9053\uff1a\u201c\u4e00\u822a\uff0c\u4f60\u966a\u5cb3\u5144\u518d\u5750\u4e00\u4f1a\u3002\u201d\u8fd9\u660e\u660e\u662f\u9001\u5ba2\u7684\u6697\u793a\u3002\u5cb3\u545c\u73c2\u602b\u7136 \u800c\u8d77\uff0c\u767d\u77f3\u9053\uff1a\u201c\u542c\u4e00\u822a\u8bf4\u5cb3\u5144\u4f4f\u5728\u6768\u5bb6\uff0c\u8d2b\u9053\u6539\u65e5\u548c\u4e00\u822a\u767b\u95e8\u62dc\u5019\u3002\u201d\u5cb3\u545c\u73c2\u4e00\u63d6\u8bf4\u9053\uff1a \u201c\u665a\u8f88\u4e0d\u6562\u6709\u52b3\u5927\u9a7e\u3002\u201d\u53cd\u8eab\u8d70\u51fa\u67f3\u5bb6\u3002\u5353\u4e00\u822a\u9001\u51fa\u95e8\u5916\uff0c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u53c1\u65e5\u5f8c\u6211\u5144\u5982\u5c1a\u672a \u79bb\u4eac\uff0c\u5343\u4e07\u5230\u6b64\u4e00\u53d9\u3002\u201d\u5cb3\u545c\u73c2\u695e\u4e86\u4e00\u695e\uff0c\u5fc3\u60f3\uff1a\u7ea6\u671f\u4f1a\u9762\uff0c\u4e8b\u6781\u5bfb\u5e38\uff0c\u4f55\u4ee5\u8981\u5982\u6b64\u6084\u6084\u7684 \u8bf4\u3002\u6b63\u60f3\u53d1\u95ee\uff0c\u5353\u4e00\u822a\u4e00\u63d6\u5230\u5730\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u6055\u4e0d\u8fdc\u9001\u4e86\u3002\u201d\u5cb3\u545c\u73c2\u8bdd\u672a\u51fa\u53e3\uff0c\u5353\u4e00\u822a\u5df2 \u628a\u95e8\u63a9\u4e0a\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u95f7\u9f13\u9f13\u7684\u56de\u5230\u6768\u5bb6\uff0c\u7761\u4e86\u4e00\u4e2a\u4e0b\u5348\uff0c\u517b\u8db3\u7cbe\u795e\uff0c\u665a\u4e0a\u8d77\u6765\uff0c\u5403\u4e86\u996d\u5f8c\uff0c\u542c\u5f97\u66f4\u697c \u9f13\u54cd\uff0c\u6253\u4e86\u4e8c\u66f4\uff0c\u6362\u4e86\u591c\u884c\u8863\u670d\uff0c\u5bf9\u94c1\u73ca\u745a\u9053\uff1a\u201c\u4f60\u5728\u5bb6\u4e2d\uff0c\u8981\u7559\u5fc3\u5728\u610f\uff0c\u8b66\u9192\u4e00\u4e9b\uff0c\u6211\u6b64 \u53bb\u4e5f\u8bb8\u5230\u5929\u660e\u4e4b\u5f8c\u624d\u80fd\u56de\u6765\u3002\u82e6\u5929\u660e\u5f8c\u8fd8\u4e0d\u89c1\u6211\u56de\u6765\uff0c\u4f60\u5c31\u5230\u57ce\u5317\u67f3\u6b66\u5e08\u5bb6\u4e2d\u544a\u8bc9\u5353\u4e00\u822a\u77e5 \u9053\u3002\u201d\u94c1\u73ca\u745a\u5657\u55e4\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8d8a\u6765\u8d8a\u5a18\u513f\u6c14\u5566\uff0c\u6211\u53c8\u4e0d\u662f\u5c0f\u5b69\uff0c\u8981\u4f60\u7f57\u54e9\u7f57\u5506\u7684\u5429 \u5490\uff1f\u6211\u624d\u4e0d\u50cf\u4f60\u90a3\u6837\u50bb\u5934\u50bb\u8111\uff0c\u8fd9\u9ebd\u5927\u7684\u4eba\u4f1a\u88ab\u63a2\u82b1\u8d3c\u52ab\u53bb\u3002\u201d\u5cb3\u545c\u73c2\u7b11\u9a82\u4e00\u58f0\uff1a\u201c\u80e1 \u8bf4\u201d\uff0c\u548c\u5979\u626c\u624b\u9053\u522b\uff0c\u51fa\u4e86\u6768\u5bb6\uff0c\u76f4\u5954\u7d2b\u7981\u57ce\u4e2d\u3002 \u3000\u3000\u79cb\u591c\u98ce\u5bd2\uff0c\u5929\u9ad8\u6708\u9ed1\uff0c\u6b63\u662f\u591c\u884c\u4eba\u51fa\u6ca1\u7684\u826f\u597d\u65f6\u673a\u3002\u7d2b\u7981\u57ce\u4e0a\u867d\u7136\u6709\u536b\u58eb\u5de1\u903b\uff0c\u4f46\u5cb3\u9e23 \u73c2\u8f7b\u529f\u5353\u7edd\uff0c\u771f\u6709\u767b\u840d\u6e21\u6c34\u4e4b\u80fd\uff0c\u98de\u7d6e\u65e0\u58f0\u4e4b\u5999\uff0c\u7adf\u7136\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u8fdb\u4eba\u7687\u5bab\uff0c\u76f4\u6e9c\u8fdb\u4e86 \u5185\u82d1\u7684\u5fa1\u82b1\u56ed\u5185\u3002 \u3000\u3000\u7687\u5bab\u9762\u79ef\u6781\u5927\uff0c\u6bbf\u5b87\u8fde\u4e91\uff0c\u5cb3\u545c\u73c2\u4f0f\u5728\u6697\u77b0\u4e4b\u5904\uff0c\u6b63\u81ea\u601d\u7d22\u524d\u65e5\u767d\u5929\u6240\u7ecf\u4e4b\u5904\uff0c\u5ffd\u542c\u5f97 \u6709\u811a\u6b65\u58f0\u4ece\u8eab\u65c1\u7ecf\u8fc7\uff0c\u539f\u6765\u662f\u4e24\u540d\u9ed1\u8863\u536b\u58eb\u3002\u5176\u4e2d\u4e00\u4eba\u9053\uff1a\u201c\u9b4f\u5b97\u4e3b\u6df1\u591c\u76f8\u62db\uff0c\u4e0d\u77e5\u4f55 \u4e8b\uff01\u201d\u53e6\u4e00\u4e2a\u9053\uff1a\u201c\u4f60\u662f\u6210\u5764\u7684\u597d\u670b\u53cb\uff0c\u542c\u8bf4\u6210\u5764\u5df2\u88ab\u9b4f\u5b97\u4e3b\u6293\u8d77\u6765\u4e86\uff0c\u9b4f\u5b97\u4e3b\u53eb\u4f60\uff0c\u60f3\u6765 \u4e0e\u6b64\u6709\u5173\u3002\u201d\u524d\u5934\u90a3\u4eba\u201c\u54fc\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6210\u5764\u90a3\u5c0f\u5b50\u592a\u4e0d\u8bc6\u76f8\uff0c\u6211\u53ef\u6551\u4ed6\u4e0d\u5f97\u3002\u201d \u3000\u3000\u5cb3\u545c\u73c2\u5fc3\u5934\u4e00\u52a8\uff0c\u77e5\u9053\u8fd9\u4e24\u4eba\u53e3\u4e2d\u6240\u8bf4\u7684\u201c\u9b4f\u5b97\u4e3b\u201d\u4e43\u662f\u9b4f\u5fe0\u8d24\uff0c\u800c\u6210\u5764\u5219\u662f\u5148\u5e1d\u5e38\u6d1b \u7684\u4f8d\u536b\u73ed\u957f\u3002\u5fc3\u60f3\uff1a\u6210\u5764\u867d\u662f\u5bab\u4e2d\u4f8d\u536b\uff0c\u8fd8\u4e0d\u5931\u4e3a\u4e00\u4e2a\u5fe0\u5fc3\u6b63\u76f4\u7684\u4eba\uff0c\u600e\u9ebd\u5148\u5e1d\u4e00\u6b7b\uff0c\u9b4f\u5fe0 \u8d24\u591a\u5c11\u5927\u4e8b\u4e0d\u7ba1\uff0c\u5c31\u5148\u8981\u6293\u4ed6\uff1f\u53c8\u60f3\uff1a\u6211\u6b63\u8981\u53bb\u627e\u90a3\u9b4f\u5fe0\u8d24\uff0c\u4f55\u4e0d\u968f\u8fd9\u4e24\u4eba\u8fdb\u5bab\u4e00\u770b\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u4ed7\u7740\u7edd\u9876\u8f7b\u529f\uff0c\u6697\u6697\u7f00\u5728\u4e8c\u4eba\u8eab\u5f8c\u3002\u542c\u4ed6\u4eec\u8c08\u8c08\u8bb2\u8bb2\uff0c\u77e5\u9053\u8fd9\u4e8c\u4eba\u4e43\u662f\u9b4f\u5fe0\u8d24\u5fc3 \u8179\uff0c\u53c8\u77e5\u9053\u81ea\u6628\u65e5\u8d77\uff0c\u897f\u5382\u4e5f\u5f52\u9b4f\u5fe0\u8d24\u7ba1\u4e86\u3002\u53ea\u6709\u9526\u8863\u536b\u8fd8\u81ea\u6210\u7cfb\u7edf\uff0c\u638c\u5728\u5185\u5ef7\u6821\u5c09\u9f99\u6210\u4e1a \u624b\u4e2d\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u968f\u7740\u90a3\u4e24\u540d\u536b\u58eb  \u5f2f\u66f2\u66f2\u7684\u8d70\u4e86\u4e00\u5927\u6bb5\u8def\uff0c\u8d70\u5230\u4e86\u4e00\u6240\u5706\u4f1e\u5f62\u5c4b\u9876\u7684\u6bbf\u5b87\u4e4b\u524d\uff0c \u4e24\u540d\u536b\u58eb\u53e9\u95e8\u4eba\u5185\uff0c\u5cb3\u9e23\u73c2\u98d8\u8eab\u4f0f\u5728\u6a90\u7aef\uff0c\u5077\u5077\u7aa5\u63a2\uff0c\u53ea\u89c1\u91cc\u9762\u4e00\u4e2a\u80a5\u80a5\u767d\u767d\u7684\u592a\u76d1\uff0c\u7aef\u5750 \u5f53\u4e2d\uff0c\u56db\u540d\u536b\u58eb\u5206\u5217\u5de6\u53f3\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u731c\u60f3\u8fd9\u5f53\u4e2d\u7684\u592a\u76d1\u5fc5\u662f\u9b4f\u5fe0\u8d24\u65e0\u7591\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u624b\u6307\u63d2\u5165\u6697\u5668\u56ca\u4e2d\uff0c\u4f46\u4e00\u60f3\u671d\u5ef7 \u81ea\u6709\u738b\u6cd5\uff0c\u6211\u82e5\u6697\u4e2d\u628a\u4ed6\u6740\u6389\uff0c\u718a\u7ecf\u7565\u5fc5\u7136\u602a\u8d23\u3002\u8feb\u5f97\u5fcd\u4f4f\u3002\u90a3\u4e24\u540d\u536b\u58eb\u53e9\u95e8\u4eba\u5185\uff0c\u5411\u9b4f\u5fe0 \u8d24\u89c1\u8fc7\u4e86\u793c\u3002\u53ea\u542c\u5f97\u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u7389\u6210\u8463\u65b9\uff0c\u4f60\u4eec\u6765\u4e86\uff1f\u4f60\u4eec\u53ef\u77e5\u9053\u6210\u5764\u5728\u8fd9\u91cc\u9ebd\uff1f\u201d\u4e24\u540d \u536b\u58eb\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u7389\u6210\uff0c\u4f60\u4e00\u5411\u662f\u6210\u5764\u7684\u526f\u624b\uff0c\u5fa1\u524d\u4f8d\u536b\u7684\u526f\u4f8d\u536b\u957f\uff1f\u662f \u9ebd\uff1f\u201d\u7389\u6210\u5e94\u9053\uff1a\u201c\u5974\u5a62\u867d\u662f\u6210\u5764\u7684\u526f\u624b\uff0c\u4f46\u548c\u4ed6\u4e00\u5411\u4e0d\u548c\u3002\u201d\u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u6ca1\u6709\u4e89\u5435\u8fc7 \u5427\uff1f\u201d\u7389\u6210\u8fdf\u7591\u4e00\u9635\uff0c\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4f46\u5fc3\u91cc\u4e0d\u548c\u3002\u201d\u9b4f\u5fe0\u8d24\u201c\u5514\u201d\u4e86\u4e00\u58f0\uff0c\u53c8\u9053\uff1a\u201c\u8463\u65b9\uff0c \u4f60\u662f\u548c\u6210\u5764\u540c\u65f6\u8fdb\u5bab\u7684\uff0c\u5728\u5fa1\u524d\u4f8d\u536b\u4e2d\uff0c\u4f60\u548c\u4ed6\u4ea4\u60c5\u6700\u597d\uff0c\u662f\u5417\uff1f\u201d\u8463\u65b9\u6025\u5fd9\u8dea\u4e0b\u53e9\u5934\uff0c\u56de \u9053\uff1a\u201c\u5974\u624d\u53ea\u77e5\u6709\u9b4f\u5b97\u4e3b\u3002\u201d\u9b4f\u5fe0\u8d24\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff01\u201d\u4f4e\u58f0\u5429\u5490\u4e86\u51e0\u53e5\uff0c\u968f\u5373\u5e26\u4f8d\u536b\u4ece\u4fa7\u95e8 \u8d70\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u4fa7\u95e8\u518d\u5f00\uff0c\u51fa\u6765\u7684\u5374\u4e0d\u662f\u9b4f\u5fe0\u8d24\u90a3\u73ed\u4eba\u4e86\uff0c\u800c\u662f\u53e6\u4e24\u540d\u536b\u58eb\uff0c\u62bc\u7740\u6210\u5764\u8d70 \u51fa\u3002\u5cb3\u545c\u73c2\u4e00\u77a7\uff0c\u4ec5\u4ec5\u76f8\u9694\u4e24\u65e5\uff0c\u6210\u5764\u5df2\u662f\u5f62\u5bb9\u6194\u60b4\uff0c\u624b\u811a\u90fd\u5e26\u6709\u9563\u94d0\u3002\u90a3\u62bc\u89e3\u4ed6\u7684\u536b\u58eb\u5c06 \u4ed6\u5e26\u5230\u5c4b\u5185\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u7684\u597d\u670b\u53cb\u4fdd\u91ca\u4f60\u4e86\uff0c\u53bb\u5427\u3002\u201d\u4f46\u5374\u5e76\u4e0d\u7ed9\u4ed6\u89e3\u5f00\u9563\u94d0\uff0c\u4fbf\u81ea\u8d70\u4e86\u3002 \u3000\u3000\u738b\u6210\u6ee1\u8138\u7b11\u5bb9\uff0c\u6276\u6210\u5764\u5750\u4e0b\uff0c\u6bb7\u52e4\u95ee\u9053\uff1a\u201c\u6ca1\u6709\u53d7\u82e6\u5427\uff1f\u201d\u6210\u5764\u51b7\u7b11\u4e00\u58f0\uff0c\u5374\u4e0d\u8a00\u8bed\u3002 \u8463\u65b9\u9053\uff1a\u201c\u5927\u54e5\uff0c\u81ea\u53e4\u9053\u8bc6\u65f6\u52a1\u8005\u4e3a\u4fca\u6770\uff0c\u4f60\u53c8\u4f55\u5fc5\u548c\u9b4f\u5fe0\u8d24\u76f8\u6297\uff1f\u201d\u6210\u5764\u6012\u9053\uff1a\u201c\u8c01\u548c\u4ed6 \u76f8\u6297\uff0c\u6211\u5c31\u4e0d\u660e\u767d\u4ed6\u4e3a\u4f55\u653e\u4e0d\u8fc7\u6211\uff1f\u201d\u738b\u6210\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u4eec\u62c5\u7740\u8eab\u5bb6\u6027\u547d\u5173\u7cfb\uff0c\u4fdd\u4f60\u51fa \u6765\uff0c\u53ea\u6c42\u4f60\u8bf4\u4e00\u53e5\u5b9e\u8bdd\u3002\u201d\u6210\u5764\u9053\uff1a\u201c\u5c0f\u5f1f\u611f\u6fc0\u4e0d\u5c3d\u3002\u4f60\u8981\u6211\u8bf4\u4ec0\u9ebd\u5b9e\u8bdd\uff1f\u201d\u738b\u6210\u9053\uff1a\u201c\u5148 \u5e1d\u53bb\u4e16\u4e4b\u65e5\uff0c\u4f60\u5728\u517b\u5fc3\u6bbf\u4f3a\u4fdf\u3002\u90a3\u65f6\u4ed6\u6b63\u53ec\u89c1\u5353\u7ee7\u5ec9\u7684\u5b59\u513f\uff0c\u4f60\u53ef\u77e5\u9053\u4ed6\u4eec\u8bf4\u4e9b\u4ec0\u9ebd\u8bdd\uff01\u201d \u6210\u5764\u9053\uff1a\u201c\u542c\u4e0d\u6e05\u695a\u3002\u201d\u8463\u65b9\u9053\uff1a\u201c\u6709\u6ca1\u6709\u8bf4\u53ca\u9b4f\u5b97\u4e3b\uff1f\u201d\u6210\u5764\u9053\uff1a\u201c\u6211\u5728\u95e8\u5916\u3002\u201d\u738b\u6210 \u9053\uff1a\u201c\u5f8c\u6765\u90a3\u4e2a\u523a\u5ba2\u9003\u6765\uff0c\u7687\u5e1d\u4e3a\u4ec0\u9ebd\u628a\u4ed6\u653e\u4e86\uff1f\u201d\u6210\u5764\u9053\uff1a\u201c\u8fd9\u6211\u66f4\u4e0d\u77e5\u9053\u3002\u201d\u8463\u65b9\u9053\uff1a \u201c\u5148\u5e1d\u662f\u4e0d\u662f\u98df\u4e86\u7ea2\u4e38\u4e4b\u5f8c\u4e0d\u4e45\u5c31\u75c5\u60c5\u6076\u5316\uff1f\u8fd9\u4e2a\u4f60\u603b\u8be5\u77e5\u9053\u4e86\u5427\uff1f\u201d\u6210\u5764\u9053\uff1a\u201c\u5148\u5e1d\u7b2c\u4e00 \u65e5\u98df\u4e86\u7ea2\u4e38\uff0c\u7cbe\u795e\u8f6c\u597d\uff0c\u7b2c\u4e8c\u65e5\u98df\u4e86\u7ea2\u4e38\uff0c\u4e0d\u4e45\u4fbf\u7a81\u53d1\u9ad8\u70ed\uff0c\u5c31\u5728\u517b\u5fc3\u6bbf\u5185\u6b7b\u53bb\u3002\u8fd9\u4e2a\u6211\u5df2 \u5bf9\u9b4f\u5fe0\u8d24\u8bf4\u4e86\u3002\u201d \u3000\u3000\u738b\u6210\u9762\u8272\u500f\u53d8\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u4e0e\u4f60\u540c\u65f6\u8fdb\u5bab\uff0c\u4e8c\u5341\u5e74\u77e5\u4ea4\uff0c\u800c\u4eca\u6211\u4ee5\u8eab\u5bb6\u6027\u547d\u4fdd\u4f60\uff0c \u4f60\u82e5\u4e0d\u8bf4\u5b9e\u8bdd\uff0c\u4e0d\u4f46\u4f60\u4f11\u60f3\u751f\u7740\u51fa\u5bab\uff0c\u6211\u4eec\u4e8c\u4eba\u4e5f\u5408\u5bb6\u6027\u547d\u4e0d\u4fdd\u3002\u201d\u6210\u5764\u9053\uff1a\u201c\u77e5\u9053\u7684\u6211\u4fbf \u8bf4\uff0c\u4e0d\u77e5\u9053\u7684\u4f60\u53eb\u6211\u8bf4\u4e9b\u4ec0\u9ebd\uff1f\u201d\u8463\u65b9\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4e0d\u662f\u9b4f\u5b97\u4e3b\u591a\u7591\uff0c\u4ed6\u6276\u52a9\u5e7c\u4e3b\uff0c\u65b0\u638c\u5927 \u6743\uff0c\u671d\u4e2d\u6587\u6b66\uff0c\u603b\u6709\u4e00\u4e9b\u4e0e\u4ed6\u4e0d\u548c\uff0c\u5148\u5e1d\u5728\u65e5\uff0c\u4e5f\u5f88\u5fcc\u4ed6\u3002\u8fd9\u5353\u4e00\u822a\u548c\u5175\u90e8\u5c1a\u4e66\u6768  \u662f\u4e16 \u4ea4\uff0c\u5148\u5e1d\u505a\u592a\u5b50\u4e4b\u65f6\uff0c\u5df2\u66fe\u548c\u4ed6\u76f8\u8bc6\uff0c\u96be\u4fdd\u5148\u5e1d\u6ca1\u6709\u4ec0\u9ebd\u9057\u8bcf\u7ed9\u4ed6\uff1f\u201d \u3000\u3000\u6210\u5764\u9053\uff1a\u201c\u6768\u5175\u90e8\u4e43\u662f\u597d\u5b98\uff0c\u82e5\u9b4f\u5b97\u4e3b\u4e00\u5fc3\u4fdd\u536b\u5e7c\u4e3b\uff0c\u6768\u5175\u90e8\u5fc5\u4e0d\u4f1a\u4e0e\u9b4f\u5b97\u4e3b\u4f5c\u5bf9\u3002\u201d \u738b\u6210\u6025\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u662f\u8bf4\u5148\u5e1d\u6709\u4ec0\u9ebd\u9057\u8bcf\u7ed9\u5353\u4e00\u822a\u4e86\uff1f\u201d\u6210\u5764\u9053\uff1a\u201c\u6211\u6ca1\u6709\u8fd9\u9ebd\u8bf4\u3002\u201d\u738b\u6210 \u53c8\u9053\uff1a\u201c\u90a3\u8fd9\u4e8b\u6211\u4eec\u4ee5\u5f8c\u518d\u67e5\u3002\u90a3\u523a\u5ba2\u5173\u7cfb\u6781\u5176\u91cd\u5927\uff0c\u4f60\u771f\u7684\u6ca1\u6709\u542c\u5230\u4ed6\u5bf9\u5148\u5e1d\u8bf4\u4ec0\u9ebd \u5417\uff1f\u201d\u6210\u5764\u9053\uff1a\u201c\u771f\u7684\u6ca1\u6709\uff01\u201d\u8463\u65b9\u9053\uff1a\u201c\u90a3\u9ebd\u4ed6\u7684\u59d3\u540d\u6765\u5386\u4f60\u4e5f\u4e0d\u77e5\u9053\u5417\uff1f\u201d\u6210\u5764\u9053\uff1a \u201c\u5144\u5f1f\u4f60\u4e3a\u4ec0\u9ebd\u8fd9\u6837\u903c\u6211\uff1f\u201d\u6210\u5764\u77e5\u9053\u5cb3\u545c\u73c2\u662f\u718a\u7ecf\u7565\u7684\u4f7f\u8005\uff0c\u53ea\u6050\u8bf4\u4e86\u51fa\u6765\uff0c\u9b4f\u5fe0\u8d24\u4f1a\u5bf9 \u718a\u5ef7\u5f3c\u4e0d\u5229\u3002\u201d\u738b\u6210\u9053\uff1a\u201c\u4e0d\u662f\u903c\u4f60\uff0c\u8fd9\u523a\u5ba2\u9b4f\u5b97\u4e3b\u5fc5\u6b32\u5f97\u800c\u7518\uff0c\u4f60\u77e5\u9053\u4e86\u4e0d\u8bf4\uff0c\u771f\u7684\u8981\u5144 \u5f1f\u4e00\u5bb6\u6027\u547d\u90fd\u548c\u4f60\u540c\u5f52\u65bc\u5c3d\u5417\uff1f\u201d \u3000\u3000\u5cb3\u545c\u73c2\u5fc3\u60f3\uff1a\u90a3\u5bab\u4e2d\u7684\u7f8e\u5987\u4e0d\u77e5\u662f\u516c\u4e3b\u8fd8\u662f\u540e\u5983\uff0c\u4f46\u542c\u8fd9\u53e3\u6c14\uff0c\u5fc5\u7136\u662f\u548c\u9b4f\u5fe0\u8d24\u7ed3\u6210\u4e00 \u6c14\u7684\u4e86\u3002\u6240\u4ee5\u9b4f\u5fe0\u8d24\u624d\u4e3a\u5979\u8fd9\u9ebd\u7740\u6025\uff0c\u4e00\u5b9a\u8981\u5f97\u81ea\u5df1\u800c\u7518\u5fc3\u3002 \u3000\u3000\u6210\u5764\u89c1\u738b\u6210\u4e00\u518d\u63d0\u53ca\u4ed6\u4ee5\u8eab\u5bb6\u6027\u547d\u62c5\u4fdd\u81ea\u5df2\uff0c\u72b6\u4f3c\u631f\u6069\u6765\u80c1\u8feb\u81ea\u5df1\uff0c\u4e0d\u7981\u8d77\u4e86\u5fc3\uff1a\u53cd\u95ee \u9053\uff1a\u201c\u4f60\u4eec\u600e\u9ebd\u77e5\u9053\u4ed6\u662f\u523a\u5ba2\uff1f\u82e5\u4ed6\u662f\u523a\u5ba2\uff1f\u4e3a\u4ec0\u9ebd\u89c1\u4e86\u7687\u4e0a\u53c8\u4e0d\u52a8\u624b\uff1f\u201d\u738b\u6210\u9053\uff1a\u201c\u4f60\u522b \u7ba1\u8fd9\u4e2a\uff0c\u4f60\u53ea\u8bf4\u4ed6\u59d3\u751a\u540d\u8c01\uff0c\u4ec0\u9ebd\u6765\u5386\uff1f\u53ea\u8981\u4f60\u8bf4\uff0c\u9b4f\u5b97\u4e3b\u4f7f\u7acb\u523b\u628a\u4f60\u5f00\u91ca\u3002\u8bf4\u4e0d\u5b9a\u5c06\u6765\u8fd8 \u8981\u628a\u9526\u8863\u536b\u4ea4\u4f60\u7edf\u7387\u3002\u201d\u6210\u5764\u6012\u9053\uff1a\u201c\u6211\u4e0d\u5e0c\u7f55\u3002\u518d\u8bf4\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u90a3\u4eba\u8fdb\u4e86\u517b\u5fc3\u6bbf\u5f8c\uff0c\u5148 \u5e1d\u5c31\u53eb\u6211\u51fa\u53bb\u65a5\u9000\u90a3\u4e9b\u8ffd\u4ed6\u7684\u4f8d\u536b\u3002\u201d \u3000\u3000\u6210\u5764\u4e0e\u8463\u65b9\u9762\u9762\u76f8\u89d1\u3002\u8463\u65b9\u9053\uff1a\u201c\u4ec0\u9ebd\u4f60\u4e5f\u8bf4\u4e0d\u77e5\u9053\u3002\u90a3\u9ebd\u6709\u4e00\u4ef6\u4e8b\u53ea\u987b\u4f60\u4e3e\u624b\u4e4b\u52b3 \u7684\uff0c\u4f60\u613f\u505a\u9ebd\uff1f\u201d\u6210\u5764\u9053\uff1a\u201c\u8981\u770b\u662f\u4ec0\u9ebd\u4e8b\uff1f\u201d\u738b\u6210\u9053\uff1a\u201c\u73b0\u5728\u5916\u5ef7\u6709\u4e9b\u5b98\u513f\u786c\u8bf4\u5148\u5e1d\u662f\u7ed9 \u674e\u53ef\u707c\u7684\u7ea2\u4e38\u5bb3\u6b7b\u7684\uff0c\u8fde\u5bb0\u76f8\u90fd\u53d7\u682a\u8fde\uff0c\u9b4f\u5b97\u4e3b\u8981\u4f60\u505a\u8bc1\u4eba\uff0c\u8bf4\u5148\u5e1d\u662f\u524d\u5929\u665a\u4e0a\u6b7b\u7684\uff0c\u4e0d\u662f \u5728\u517b\u5fc3\u6bbf\u5185\u5403\u4e86\u7ea2\u4e38\u4e0d\u4e45\u5c31\u6b7b\u7684\u3002\u201d\u6210\u5764\u9762\u8272\u5927\u53d8\uff0c\u5ffd\u7136\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u672c\u6765\u6ca1\u6709\u6000\u7591\uff0c\u542c \u4f60\u4eec\u8fd9\u9ebd\u4e00\u8bf4\uff0c\u83ab\u975e\u5148\u5e1d\u771f\u662f\u65b9\u4ece  \u548c\u674e\u53ef\u707c\u5bb3\u6b7b\u7684\u9ebd\uff1f\u201d \u3000\u3000\u738b\u6210\u6025\u9053\uff1a\u201c\u4f60\u4e3e\u624b\u4e4b\u52b3\uff0c\u5c31\u53ef\u83b7\u91ca\u653e\u3002\u201d\u6210\u5764\u9053\uff1a\u201c\u6211\u5e73\u751f\u4e0d\u6253\u5047\u8bdd\u3002\u201d\u738b\u6210\u9053\uff1a \u201c\u6211\u4eec\u7684\u5bb6\u5c0f\u8001\u5e7c\u90fd\u62c5\u7740\u5173\u7cfb\uff0c\u4f60\u82e5\u4e0d\u80af\uff0c\u4ed6\u4eec\u4e5f\u90fd\u4e0d\u80fd\u6d3b\u4e86\uff01\u201d\u6210\u5764\u5ffd\u5927\u58f0\u559d\u9053\uff1a\u201c\u738b \u6210\uff0c\u5982\u4eca\u624d\u770b\u51fa\u4f60\u662f\u5c0f\u4eba\uff01\u4ec0\u9ebd\u8eab\u5bb6\u6027\u547d\u62c5\u4fdd\uff0c\u9b3c\u624d\u76f8\u4fe1\u4f60\u7684\u5047\u8bdd\uff01\u201d\u738b\u6210\u9762\u8272\u9752\u767d\uff0c\u8463\u65b9 \u559d\u9053\uff1a\u201c\u72d7\u54ac\u5415\u6d1e\u5bbe\uff0c\u4e0d\u8bc6\u597d\u4eba\u5fc3\uff01\u201d\u7a81\u7136\u4f38\u624b\u4e00\u622e\uff0c\u95ed\u4e86\u4ed6\u7684\u7a74\u9053\u3002\u738b\u6210\u53d6\u51fa\u4e00\u4e2a\u5e03\u888b\uff0c \u5c06\u6210\u5764\u5e26\u7740\u9563\u94d0\u585e\u5165\u888b\u5185\uff0c\u7b11\u9053 \uff1a\u201c\u9b4f\u5b97\u4e3b\u6015\u660e\u5e72\u6389\u4ed6\uff0c\u4f1a\u5f15\u8d77\u65e7\u4f8d\u536b\u7684\u4e0d\u5b89\uff0c\u4f60\u770b\u600e\u6837 \u624d\u80fd\u628a\u4ed6\u9759\u6084\u6084\u7684\u5e72\u6389\uff0c\u8ba9\u522b\u4eba\u4e0d\u8d77\u7591\u5fc3 \u3002\u201d\u8463\u65b9\u9053\uff1a\u201c\u8fd9\u5012\u662f\u4e2a\u96be\u5dee\u4f7f\uff0c\u8ba9\u6211\u60f3\u60f3\u3002\u201d \u60f3\u4e86\u4e00\u9635\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u5148\u628a\u4ed6\u7684\u9563\u94d0\u53bb\u4e86\u3002\u201d \u738b\u6210\u5947\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u8463\u65b9\u9053\uff1a\u201c\u53cd\u6b63\u4f60\u5df2\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u8131\u4e86\u4ed6\u7684\u9563\u94d0\uff0c\u4e5f\u9003\u4e0d\u6389\u3002\u6211\u4eec\u5c06\u4ed6\u5077\u5077\u5e26\u5230\u7164 \u5c71\uff0c\u628a\u4ed6\u7f22\u6b7b\u6811\u4e0a\uff0c\u5c31\u8bf4\u4ed6\u662f\u81ea\u6740\u6b7b\u7684\uff0c\u5c82\u4e0d\u751a\u5999\uff0c\u8ba9\u4ed6\u6b7b\u4e86\u4e5f\u53ef\u5f97\u4e2a\u5fe0\u70c8\u4e4b\u540d\u3002\u201d\u738b\u6210\u9f13 \u638c\u9053\uff1a\u201c\u5999\u54c9\uff01\u201d\u89e3\u5f00\u5e03\u888b\uff0c\u5c06\u6210\u5764\u63d0\u4e86\u51fa\u6765\uff0c\u628a\u4ed6\u7684\u9563\u94d0\u89e3\u4e86\uff0c\u56de\u5934\u5bf9\u8463\u65b9\u9053\uff1a\u201c\u884c\u4e86 \u5427\uff1f\u201d\u8463\u65b9\u7a81\u7136\u4e00\u638c\u5288\u4e0b\u3002\u738b\u6210\u9aa4\u51fa\u4e0d\u610f\uff0c\u7f29\u80a9\u4e0d\u53ca\uff0c\u7ed9\u4ed6\u4e00\u638c\u6253\u6655\uff0c\u8463\u65b9\u53cc\u6307\u4e00\u4f38\uff0c\u6b63\u8981 \u66ff\u6210\u5764\u89e3\u5f00\u7a74\u9053\uff0c\u5ffd\u7136\u5495\u549a\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\u3002\u4fa7\u95e8\u91cc\u7a9c\u51fa\u4e00\u540d\u536b\u58eb\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u9b4f\u5b97\u4e3b\u771f \u6709\u5148\u89c1\u4e4b\u660e\uff01\u201d \u3000\u3000\u539f\u6765\u8463\u65b9\u867d\u4e00\u5411\u4e0e\u6210\u5764\u4e0d\u548c\uff0c\u5fc3\u5730\u5374\u6bd4\u738b\u6210\u7a0d\u597d\uff0c\u4ed6\u4e00\u89c1\u738b\u6210\u975e\u628a\u6210\u5764\u7f6e\u65bc\u6b7b\u5730\u4e0d\u53ef\uff0c \u5ffd\u7136\u8d77\u4e86\u4e0d\u5fcd\u4e4b\u5fc3\uff0c\u4ea6\u6015\u81ea\u5df2\u5c06\u6765\u4e5f\u4f1a\u548c\u4ed6\u4e00\u6837\uff0c\u56e0\u6b64\u9661\u7136\u8f6c\u5ff5\uff0c\u60f3\u628a\u6210\u5764\u653e\u8d70\uff0c\u53cc\u53cc\u9003\u51fa \u5bab\u5916\u3002\u90a3\u77e5\u9b4f\u5fe0\u8d24\u4f0f\u6709\u9ad8\u624b\u5728\u65c1\uff0c\u8463\u65b9\u521a\u521a\u52a8\u624b\uff0c\u5c31\u7ed9\u4ed6\u7528\u6697\u5668\u6253\u4e86\u7a74\u9053\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u5728\u5c4b\u6a90\u4e0a\u770b\u5f97\u9a87\u7136\u3002\u57cb\u4f0f\u7684\u536b\u58eb\u8d70\u4e86\u51fa\u6765\uff0c\u5148\u628a\u738b\u6210\u6551\u9192\uff0c\u7b11\u9053\uff1a\u201c\u5012\u5e95\u662f\u4f60\u5fe0 \u5fc3\u3002\u201d\u4ecd\u628a\u6210\u5764\u585e\u5165\u5e03\u888b\uff0c\u9053\uff1a\u201c\u8463\u65b9\u867d\u7136\u53ef\u6740\uff0c\u4f46\u4ed6\u7684\u8ba1\u7b56\u5012\u771f\u4e0d\u9519\u3002\u6211\u4eec\u5c31\u8ba9\u6210\u5764\u201c\u81ea \u9552\u201d\u4e86\u5427\u3002\u201d\u63d0\u8d77\u5e03\u888b\uff0c\u548c\u738b\u6210\u4e00\u540c\u8d70\u51fa\u3002 \u3000\u3000\u4e24\u4eba\u5728\u5fa1\u82b1\u56ed\u91cc\u8d70\u4e86\u4e00\u5927\u6bb5\u8def\uff0c\u591c\u5df2\u53c1\u66f4\uff0c\u98ce\u5bd2\u9732\u91cd\uff0c\u5fa1\u82b1\u56ed\u91cc\u5df3\u662f\u4e00\u7247\u5bc2\u9759\uff0c\u4e24\u4eba\u8d70 \u5230\u5047\u5c71\u8f6c\u89d2\uff0c\u9661\u7136\u4e00\u9635\u51b7\u98ce\u5439\u6765\uff0c\u738b\u6210\u6253\u4e86\u4e00\u4e2a\u51b7\u98a4\uff0c\u9053\uff1a\u201c\u54a6\uff0c\u5927\u54e5\uff0c\u6211\u6709\u70b9\u5bb3\u6015\u3002\u201d\u90a3 \u540d\u536b\u58eb\u9053\uff1a\u201c\u6015\u4ec0\u9ebd\uff1f\u4eba\u8fd8\u672a\u5bb3\u6b7b\u5462\uff0c\u5c31\u662f\u6709\u51a4\u9b3c\u4e5f\u4e0d\u4f1a\u73b0\u5728\u6765\u627e\u4f60\u3002\u201d\u8bdd\u521a\u8bf4\u5b8c\uff0c\u7a81\u7136\u4e00 \u9635\u51b7\u98ce\u4ece\u80cc\u5f8c\u5439\u6765\uff0c\u8033\u8fb9\u542c\u5f97\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u627e\u4f60\uff01\u201d\u90a3\u540d\u536b\u58eb\u672a\u5f85\u56de\u5934\uff0c\u624b\u8155\u5df2\u7ed9\u4eba\u6293\u7740\uff0c \u80c1\u4e0b\u7684\u5c06\u53f0\u7a74\u4e5f\u7ed9\u6765\u4eba\u7528\u624b\u8098\u4e00\u649e\uff0c\u75db\u4eba\u5fc3\u813e\uff0c\u5374\u53eb\u4e0d\u51fa\u58f0\uff0c\u738b\u6210\u4e5f\u540c\u6837\u7ed9\u6765\u4eba\u4f9d\u6cd5\u70ae\u5236\uff0c \u90a3\u5927\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u8981\u5bb3\u4eba\uff0c\u960e\u7f57\u738b\u5374\u8981\u4f60\u4eec\u5148\u53bb\u62a5\u5230\u3002\u201d\u624b\u8155\u7528\u529b\uff0c\u628a\u4e24\u4eba\u6454\u4eba\u5047\u5c71\u6d1e\u5185\u3002 \u3000\u3000\u518d\u8bf4\u6210\u5764\u5728\u5e03\u888b\u4e2d\u5ffd\u7136\u88ab\u4eba\u63d0\u4e86\u51fa\u6765\uff0c\u7741\u773c\u4e00\u770b\uff0c\u539f\u6765\u5c31\u662f\u524d\u65e5\u7684\u201c\u523a\u5ba2\u201d\uff0c\u90a3\u5927\u7b11 \u9053\uff1a\u201c\u4f60\u7684\u7a74\u9053\u5df2\u7ecf\u89e3\u4e86\uff0c\u51fa\u5bab\u53bb\u5427\uff0c\u4e0d\u8981\u518d\u5f53\u8fd9\u635e\u4ec0\u5b50\u7684\u5fa1\u524d\u4f8d\u536b\u4e86\uff01\u201d\u6210\u5764\u9053\uff1a\u201c\u4f60\u600e \u9ebd\u8fd9\u6837\u5927\u80c6\uff01\u201d\u8fdc\u5904\u5ffd\u73b0\u706f\u5149\u3002\u6210\u5764\u9053\uff1a\u201c\u5cb3\u5927\u54e5\uff0c\u4f60\u628a\u90a3\u738b\u6210\u7684\u8863\u88f3\u6362\u4e86\uff0c\u6211\u5e26\u4f60\u6df7\u51fa\u5bab \u53bb\u3002\u201d\u4e0e\u5cb3\u545c\u73c2\u8dc3\u5165\u6d1e\u5185\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u5cb3\u9e23\u73c2\u6362\u4e86\u8863\u88f3\uff0c\u524d\u9762\u7684\u706f\u7b3c\u4e5f\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u6210\u5764\u9053\uff1a\u201c\u6211\u4eec\u4ece\u897f\u534e\u95e8\u51fa\u53bb\uff0c\u90a3\u8fb9\u662f\u9526\u8863\u536b\u628a\u5b88\u3002\u6211\u6709\u719f\u4eba\u3002\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u6211\u4e0d\u51fa \u53bb\u3002\u201d\u6210\u5764\u5947\u9053\uff1a\u201c\u4f60\u4e00\u518d\u8fdb\u5bab\u6765\u505a\u4ec0\u9ebd\uff1f\u201d\u5cb3\u9e23\u73c2\u5fc3\u5934\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u6211\u6b63\u6709\u4e8b\u8bf7\u6559\u3002\u201d\u5c06 \u524d\u4e8b\u518d\u8bf4\u4e00\u904d\uff0c\u95ee\u9053\uff1a\u201c\u6210\u5144\u53ef\u77e5\u9053\u90a3\u7f8e\u5987\u7a76\u662f\u4ec0\u9ebd\u4eba\u9ebd\uff1f\u201d\u6210\u5764\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u56fd\u4e4b\u5c06 \u4ea1\uff0c\u5fc5\u6709\u5996\u5b7d\uff0c\u60f3\u4e0d\u5230\u8fd9\u5a46\u5a18\u5c45\u7136\u5982\u6b64\u65e0\u6cd5\u65e0\u5929\u3002\u201d\u5cb3\u9e23\u73c2\u542c\u4ed6\u53e3\u6c14\u5341\u5206\u4e0d\u656c\uff0c\u9053\uff1a\u201c\u8fd9\u4eba \u4e0d\u662f\u516c\u4e3b\u6216\u5983\u5b50\u5417\uff1f\u201d\u6210\u5764\u9053\uff1a\u201c\u5979\u73b0\u5728\u6bd4\u7687\u592a\u540e\u8fd8\u6709\u52bf\u529b\uff01\u5979\u662f\u5f53\u4eca\u5723\u4e0a\u7684\u4e73\u5a18\u5ba2\u6c0f\u592b \u4eba\uff01\u201d \u3000\u3000\u5cb3\u545c\u73c2\u5947\u9053\uff1a\u201c\u4e73\u5a18\uff0c\u600e\u9ebd\u4e73\u5a18\u6709\u8fd9\u6837\u5927\u7684\u6743\u52bf\uff1f\u201d\u6210\u5764\u9053\uff1a\u201c\u5f53\u4eca\u5723\u4e0a\u662f\u5979\u629a\u517b\u5927 \u7684\uff0c\u8bf4\u4e5f\u5947\u602a\uff0c\u5723\u4e0a\u81ea\u5c0f\u5df4\u79bb\u4e0d\u5f00\u5979\uff0c\u5979\u53c8\u751f\u5f97\u5e74\u8f7b\u7f8e\u8c8c\uff0c\u73b0\u5728\u5df2\u662f\u56db\u5341\u591a\u5c81\u7684\u5987\u4eba\uff0c\u770b\u8d77 \u6765\u8fd8\u50cf\u4e0d\u5230\u53c1\u5341\u5c81\u4f3c\u7684\uff0c\u6240\u4ee5\u5148\u5e1d\u4e5f\u5f88\u5ba0\u7231\u5979\u3002\u201d\u5cb3\u9e23\u73c2\u7ec6\u5473\u53e3\u6c14\uff0c\u4f3c\u4e4e\u5bab\u95f1\u4e2d\u8fd8\u6709\u66f4\u4e0d\u582a \u95fb\u95ee\u7684\u4e8b\u60c5\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u5979\u5982\u6b64\u7316\u7357\u3002\u201d\u6210\u5764\u9053\uff1a\u201c\u9b4f\u5fe0\u8d24\u4e5f\u662f\u9760\u4e86\u5df4\u7ed3\u5979\uff0c \u624d\u6e10\u6e10\u5728\u5bab\u4e2d\u5f97\u52bf\u7684\u3002\u9b4f\u5fe0\u8d24\u81ea\u524d\u5e74\u638c\u7ba1\u4e86\u4e1c\u5382\u4e4b\u5f8c\uff0c\u62e8\u4e86\u51e0\u540d\u4eb2\u4fe1\u536b\u58eb\u5230\u4e73\u5a18\u5e9c\u542c\u5979\u8c03 \u9063\uff0c\u6e10\u6e10\u5979\u4e5f\u6709\u8d77\u79c1\u4eba\u7684\u536b\u58eb\u6765\u4e86\u3002\u201d\u5cb3\u545c\u73c2\u604d\u7136\u5927\u609f\uff1a\u90a3\u4e24\u540d\u7528\u8ff7\u70df\u8ff7\u7ffb\u81ea\u5df1\u7684\u9ec4\u8863\u6c49 \u5b50\uff0c\u4e00\u5b9a\u662f\u5979\u7684\u536b\u58eb\u66ff\u5979\u5077\u63b3\u7537\u5b50\u8fdb\u5bab\u7684\u4e86\u3002\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u4e5f\u77e5\u9053\u5979\u5077\u63b3\u7537\u5b50\u7684\u4e8b\u5417\uff1f\u201d \u6210\u5764\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u6599\u4e0d\u5230\u5979\u6562\u5982\u6b64\uff0c\u4e73\u5a18\u5e9c\u7684\u4f8d\u536b\u81ea\u6210\u4e00\u7cfb\uff0c\u6211\u4eec\u4e5f\u4e0d\u4fbf\u53bb\u63a2\u95ee\u3002\u201d\u5cb3\u9e23\u73c2 \u95ee\u6e05\u695a\u4e86\u53bb\u4e73\u5a18\u5e9c\u7684\u8def\uff0c\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u7b49\u6211\u4e00\u4f1a\uff0c\u6211\u53bb\u53bb\u5c31\u6765\uff01\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5cb3\u545c\u73c2\u5faa\u7740\u6210\u5764\u6307\u70b9\u7684\u8def\u7ebf\uff0c\u6478\u5230\u4e86\u4e73\u5a18\u5e9c\u5916\uff0c\u89c1\u5916\u9762\u6709\u51e0\u6761\u9ed1\u5f71\u7a7f\u68ad\u5de1 \u903b\uff0c\u4fbf\u6084\u6084\u7684\u5728\u5730\u4e0a\u62ac\u8d77\u4e24\u679a\u5c0f\u77f3\uff0c\u5411\u7a7a\u4e00\u5f39\uff0c\u8d81\u7740\u90a3\u4e9b\u536b\u58eb\u5206\u5fc3\u4e4b\u9645\uff0c\u7a81\u7136\u4ece\u6697\u89d2\u98de\u63a0\u5165 \u5e9c\u3002\u5cb3\u9e23\u73c2\u524d\u65e5\u66fe\u4ece\u8fd9\u91cc\u9003\u51fa\u3002\u95e8\u6237\u4f9d\u7a00\u8bb0\u5f97\uff0c\u4e00\u8def\u501f\u7269\u969c\u5f62\uff0c\u8f7b\u767b\u5de7\u7eb5\uff0c\u6478\u7d22\u5230\u4e2d\u95f4\u90a3\u5ea7 \u623f\u5b50\uff0c\u521a\u4ece\u6697\u9edd\u5904\u957f\u51fa\u8eab\u6765\uff0c\u84e6\u7136\u542c\u5f97\u6709\u4eba\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u662f\u5c0f\u53c1\u5417\uff1f\u5723\u4e0a\u5728\u91cc\u9762\uff0c\u4f60\u5230\u5916\u9762 \u503c\u73ed\u53bb\u3002\u201d\u5cb3\u545c\u73c2\u5df2\u6362\u4e86\u4e1c\u5382\u536b\u58eb\u670d\u9970\uff0c\u60c5\u77e5\u8bef\u4f1a\uff0c\u5374\u4e0d\u8bf4\u8bdd\uff0c\u5f85\u90a3\u4eba\u8d70\u8fc7\u6765\u65f6\uff0c\u84e6\u7136\u4f38\u6307 \u4e00\u70b9\uff0c\u70b9\u4e86\u4ed6\u7684\u6b7b\u7a74\uff0c\u538b\u5728\u5bab\u524d\u7684\u77f3\u9f13\u5e95\u4e0b\uff0c\u98de\u8eab\u6500\u4e0a\u5c4b\u6a90\u3002 \u3000\u3000\u5c4b\u5b50\u91cc\u7089\u9999\u8885\u8885\uff0c\u7ea2\u70db\u9ad8\u70e7\uff0c\u5cb3\u545c\u73c2\u5fc3\u60f3\uff1a\u8fd9\u5012\u50cf\u4e2a\u65b0\u623f\u3002\u7ec6\u770b\u65f6\u623f\u4e2d\u5df3\u6362\u4e86\u5e03\u7f6e\uff0c\u9760 \u7a97\u5904\u6709\u4e00\u5f20\u5927\u7406\u77f3\u7684\u957f\u5f62\u4e66\u684c\uff0c\u684c\u4e0a\u5806\u6ee1\u594f\u7ae0\uff0c\u4e00\u4e2a\u5341\u516d\u4e03\u5c81\u7684\u5c11\u5e74\u5728\u90a3\u91cc\u62ab\u9605\u594f\u7ae0\uff0c\u4e1c\u7ffb \u4e00\u672c\uff0c\u897f\u7ffb\u4e00\u672c\uff0c\u6837\u5b50\u663e\u5f97\u5341\u5206\u6dd8\u6c14\u3002\u5cb3\u545c\u73c2\u6697\u9053\uff1a\u201c\u771f\u662f\u8352\u5510\uff0c\u8fd9\u7687\u5e1d\u8bf4\u5c0f\u4e0d\u5c0f\uff0c\u8bf4\u5927\u4e0d \u5927\uff0c\u600e\u9ebd\u8fd8\u79bb\u4e0d\u5f00\u4e73\u5988\uff0c\u8fd9\u6837\u80e1\u95f9\uff0c\u628a\u594f\u7ae0\u90fd\u642c\u5230\u4e73\u5988\u623f\u4e2d\u6765\u4e86\uff01\u201d \u3000\u3000\u5c0f\u7687\u5e1d\u7ffb\u4e86\u51e0\u672c\u594f\u7ae0\uff0c\u4f38\u4e86\u4e2a\u61d2\u8170\u9053\uff1a\u201c\u771f  \u70e6\uff01\u201d\u4ed6\u7684\u4e73\u5988\u5ba2\u6c0f\u5750\u5728\u4e00\u65c1\uff0c\u659f\u4e86\u4e00\u76cf \u53c2\u6c64\uff0c\u9012\u7ed9\u4ed6\u9053\uff1a\u201c\u505a\u7687\u5e1d\u561b\uff0c\u600e\u80fd\u4e0d\u770b\u594f\u7ae0\uff01\u201d\u5c0f\u7687\u5e1d\u9053\uff1a\u201c\u6709\u597d\u4e9b\u5b87\u6211\u90fd\u8ba4\u4e0d\u5f97\uff0c\u660e\u5929 \u95ee\u592a\u5085\u53bb\u3002\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u54ce\u5537\uff0c\u7531\u54e5\u513f\uff0c\u201c\u6ce8\uff0e\u71b9\u5b97\u540d\u6731\u7531\u6821\u201d\u8fd9\u4f1a\u7ed9\u4eba\u7b11\u8bdd\u7684\uff0c\u4f60\u62ff\u7ed9\u6211 \u770b\u5427\uff0c\u4e5f\u8bb8\u6211\u4f1a\u8ba4\u5f97\u3002\u201d\u5c0f\u7687\u5e1d\u968f\u624b\u9012\u8fc7\u4e00\u672c\u594f\u7ae0\uff0c\u90a3\u662f  \u897f\u5de1\u629a\u62a5\u544a\u201c\u532a\u4e71\u201d\uff0c\u8bf7\u6c42\u589e\u5175 \u7684\u594f\u647a\uff0c\u5ba2\u6c0f\u770b\u4e86\u9053\uff1a\u201c\u738b\u5de1\u629a\u8bf4\uff0c  \u897f\u8fde\u5e74\u5927\u9965\uff0c\u73b0\u5728\u5df2\u6709\u53c1\u5341\u516d\u80a1\u76d7\u532a\uff0c\u8981\u4f60\u6d3e\u5175 \u53bb\u3002\u201d\u7531\u6821\u614c\u9053\uff1a\u201c  \u897f\u79bb\u8fd9\u91cc\u591a\u8fdc\uff1f\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u8fdc\u7740\u5462\uff0c\u54e5\u513f\uff0c\u4f60\u4e0d\u7528\u62c5\u5fc3\u3002\u201d\u7531\u6821 \u9053\uff1a\u201c\u90a3\u4e9b\u5b98\u513f\u7684\u540d\u5b87\u597d\u591a\uff0c\u6211\u90fd\u8bb0\u4e0d\u5f97\uff0c\u660e\u5929\u95f4\u6768\u5175\u90e8\u53bb\uff0c\u53eb\u4ed6\u4fdd\u4e00\u4e2a\u4eba\u53bb\u5427\u3002\u201d\u5ba2\u6c0f\u53c8 \u7b11\u9053\uff1a\u201c\u4e0d\u884c\u54df\u54e5\u513f\uff0c\u8c03\u5175\u9063\u5c06\u4e4b\u4e8b\uff0c\u5e94\u8be5\u7687\u5e1d\u505a\u4e3b\uff0c\u4f60\u8981\u5916\u9762\u7684\u5927\u81e3\u51fa\u4e3b\u610f\uff0c\u5c06\u6765\u592a\u963f\u5012 \u6301\uff0c\u90a3\u5c31\u4e0d\u597d\u5566\uff01\u201d\u6b63\u662f\uff1a\u72d0\u5a9a\u6b3a\u5e7c\u4e3b\uff0c\u690d\u515a\u4e71\u671d\u7eb2\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377\u2014\u2014\u7b2c\u5341\u4e8c\u56de\u3000\u5757\u5792\u96be\u6d88\u3000\u4f24\u5fc3\u60b2\u56fd\u4e8b\u3000\u6743\u5978\u5f04\u67c4\u3000\u8bbe\u8ba1\u5bb3\u5c06\u519b \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377 \u7b2c\u5341\u4e8c\u56de \u5757\u5792\u96be\u6d88\u3000\u4f24\u5fc3\u60b2\u56fd\u4e8b \u6743\u5978\u5f04\u67c4\u3000\u8bbe\u8ba1\u5bb3\u5c06\u519b \u3000\u3000\u7531\u6821\u53c8\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u9053\uff1a\u201c\u6211\u5b9e\u5728\u4e0d\u60f3\u770b\u4e86\uff0c\u505a\u7687\u5e1d\u8fd9\u6837\u8f9b\u82e6\uff0c\u771f\u662f\u4e0d\u505a\u4e5f\u7f62\u3002\u4e73\u5a18\uff0c \u4f9d\u4f60\u8bf4\u600e\u9ebd\u6837\uff1f\u201d\u5ba2\u6c0f\u5df4\u4e0d\u5f97\u4ed6\u6709\u6b64\u4e00\u95ee\uff0c\u56de\u9053\uff1a\u201c\u542c\u8bf4\u5175\u79d1\u7ed9\u4e8b\u4e2d\u5218\u5ef7\u5143\u5f88\u884c\uff0c\u4f55\u4e0d\u53eb\u4ed6 \u5e26\u5175\uff1f\u201d\u7531\u6821\u9053\uff1a\u201c\u597d\uff0c\u5218\u5ef7\u5143\u5c31\u5218\u5ef7\u5143\u5427\uff01\u201d\u63d0\u8d77\u6731\u7b14\u5728\u594f\u7ae0\u4e0a\u6279\u4e86\uff0c\u7b11\u9053\uff1a\u201c\u4e73\u5a18\uff0c\u4ee5 \u5f8c\u4f60\u66ff\u6211\u770b\uff0c\u4f60\u8bf4\u4ec0\u9ebd\uff0c\u6211\u5c31\u6279\u4ec0\u9ebd\u3002\u201d\u5ba2\u6c0f\u8feb\u4ed6\u770b\u594f\u7ae0\uff0c\u672c\u5fc3\u5c31\u662f\u6545\u610f\u4ee4\u4ed6  \u70e6\uff0c\u597d\u4e58\u673a \u6293\u6743\uff0c\u542c\u4ed6\u4e00\u8bf4\uff0c\u5fc3\u4e2d\u72c2\u559c\uff0c\u9762\u4e0a\u5374\u4e0d\u8868\u9732\u51fa\u6765\u3002\u8e59\u7709\u8bf4\u9053\uff1a\u201c\u7531\u54e5\u513f\uff0c\u8fd9\u62c5\u5b50\u6211\u53ef\u62c5\u4e0d \u8d77\uff0c\u5982\u6709\u5dee\u9519\uff0c\u90a3\u4e9b\u4e1c\u6797\u515a\u4eba\u4e00\u5b9a\u653e\u4e0d\u8fc7\u6211\u3002 \u201c\u7531\u6821\u9053\uff1a\u201c\u6211\u4e0d\u8bf4\u51fa\u53bb\u4fbf\u662f\u3002\u201d\u5ba2\u6c0f\u8fd9\u624d \u76c8\u76c8\u7b11\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u53bb\u7761\u5427\u3002\u594f\u7ae0\u8ba9\u6211\u770b\u597d\u4e86\u3002\u201d\u7531\u6821\u5ffd\u9053\uff1a\u201c\u718a\u5ef7\u5f3c\u53ef\u662f\u4e2a\u5927\u5fe0\u81e3\uff01\u201d\u8fb9 \u8bf4\u8fb9\u63d0\u7b14\u5728\u7eb8\u4e0a\u80e1\u4e71\u6d82\u5199\uff0c\u5b57\u4f53\u6b6a\u659c\uff0c\u4f46\u5374\u5199\u5f97\u5f88\u5927\uff0c\u8fde\u5cb3\u9e23\u73c2\u5728\u5c4b\u6a90\u4e0a\u4e5f\u770b\u5f97\u6e05\u695a\uff0c\u53ea\u89c1 \u4ed6\u6ee1\u7eb8\u5199\u7740\u201c\u718a\u5ef7\u5f3c\u662f\u4e2a\u5927\u5fe0\u81e3\u201d\uff0c\u603b\u6709\u4e03\u516b\u884c\u4e4b\u591a\u3002\u5ba2\u6c0f\u4e00\u6115\uff0c\u7b11\u95ee\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u77e5\u9053\u718a \u5ef7\u5f3c\u662f\u4e2a\u5927\u5fe0\u81e3\uff1f\u201d\u7531\u6821\u9053\uff1a\u201c\u7236\u7687\u751f\u524d\u5e38\u5bf9\u6211\u8bf4\uff0c\u8bf4\u8981\u4e0d\u662f\u718a\u5ef7\u5f3c\u66ff\u54b1\u4eec\u6491\u7740\u8fb9\u5173\uff0c\u6ee1\u5dde \u9791\u5b50\u65e9\u5df2\u6253\u8fdb\u6765\u4e86\u3002\u7236\u7687\u75c5\u91cd\u65f6\u66fe\u8bcf\u4ed6\u56de\u4eac\uff0c\u521a\u624d\u6211\u770b\u5230\u718a\u5ef7\u5f3c\u534a\u6708\u524d\u53d1\u7684\u594f\u7ae0\uff0c\u8bf4\u662f\u5df2\u7ecf \u52a8\u8eab\uff0c\u9884\u8ba1\u5728\u5eff\u516b\u53ef\u5230\uff0c\u5eff\u516b\u5c31\u662f\u5927\u5f8c\u5929\uff0c\u4f60\u770b\u6211\u8981\u4e0d\u8981\u51fa\u5bab\u53bb\u8fce\u63a5\u4ed6\uff01\u201d\u5cb3\u545c\u73c2\u53c8\u60ca\u53c8 \u559c\uff0c\u60ca\u7684\u662f\u718a\u7ecf\u7565\u6b64\u65f6\u56de\u4eac\uff0c\u671d\u4e2d\u6b63\u6df7\u4e71\u4e0d\u582a\uff0c\u5bb0\u76f8\u65b9\u4ece\u54f2\u548c\u9b4f\u5fe0\u8d24\u5185\u5916\u52fe\u7ed3\uff0c\u670b\u6bd4\u4e3a\u5978\uff0c \u7687\u5e1d\u53c8\u88ab\u5ba2\u6c0f\u631f\u6301\uff0c\u53ea\u6050\u5bf9\u718a\u7ecf\u7565\u4e0d\u5229\uff0c\u559c\u7684\u662f\u53c1\u5929\u4e4b\u5f8c\u4fbf\u53ef\u89c1\u5230\u5927\u5e05\u3002\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5ffd\u7136\u60f3 \u8d77\u5353\u4e00\u822a\u53c1\u5929\u4e4b\u5f8c\u7684\u7ea6\u671f\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u9ebd\u8fd9\u6837\u51d1\u5de7\uff0c\u718a\u7ecf\u7565\u5b9a\u53c1\u5929\u4e4b\u5f8c\u5230\u4eac\uff0c\u800c\u4ed6\u7684\u7ea6\u4f1a\u4e5f \u7279\u522b\u63d0\u51fa\u201c\u53c1\u5929\u201d\u8fd9\u4e2a\u671f\u9650\uff01\u201d \u3000\u3000\u5ba2\u6c0f\u555c\u4e86\u4e00\u53e3\u53c2\u6c64\uff0c\u6b6a\u7740\u773c\u775b\u7b11\u9053\uff1a\u201c\u77a7\u4f60\uff0c\u4f60\u8bf4\u4e0d\u4e3a\u8fd9\u4e9b\u4e8b\u64cd\u5fc3\uff0c\u73b0\u5728\u53c8\u64cd\u8d77\u5fc3\u6765 \u4e86\u3002\u5148\u5e1d\u9a7e\u5d29\uff0c\u5230\u5eff\u516b\u8fd8\u672a\u8fc7\u4e03\u65e5\u4e4b\u671f\uff0c\u4f60\u4e0d\u80fd\u51fa\u5bab\u3002\u8ba9\u4ed6\u6765\u671d\u89c1\u4f60\u597d\u4e86\u3002\u597d\u5b69\u5b50\u4f60\u4e5f\u7d2f \u5566\uff0c\u5feb\u53bb\u7761\u5427\uff01\u201d \u3000\u3000\u7531\u6821\u672c\u6765\u60f3\u7761\uff0c\u60f3\u8d77\u718a\u5ef7\u5f3c\u5374\u60f3\u8d77\u4e00\u6869\u4e8b\u60c5\uff0c\u53c8\u9053\uff1a\u201c\u521a\u624d\u6211\u4e71\u7ffb\u90a3\u4e9b\u594f\u7ae0\uff0c\u89c1\u5341\u6709\u516b \u4e5d\u90fd\u662f\u53c2\u52be\u718a\u5ef7\u5f3c\u7684\uff0c\u718a\u5ef7\u5f3c\u65e2\u662f\u4e2a\u5927\u5fe0\u81e3\uff0c\u90a3\u9ebd\u90a3\u4e9b\u53c2\u52be\u4ed6\u7684\u5b98\u513f\u4e00\u5b9a\u662f\u5978\u81e3\u4e86\u3002\u6211\u660e\u65e5 \u5750\u671d\uff0c\u4e00\u4e2a\u4e2a\u5c06\u4ed6\u95ee\u7f6a\u3002\u4f60\u66ff\u6211\u628a\u4ed6\u4eec\u7684\u540d\u5b87\u6284\u5728\u7eb8\u4e0a\uff0c\u597d\u5417\uff01\u201d\u5cb3\u9e23\u73c2\u6697\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4e2a \u5c0f\u7687\u5e1d\u5728\u8fd9\u4ef6\u4e8b\u60c5\u4e0a\u5c45\u7136\u5f88\u61c2\u4e8b\u3002\u201d\u5ba2\u6c0f\u5413\u4e86\u4e00\u8df3\uff0c\u5fd9\u9053\uff1a\u201c\u6211\u4eec\u5750\u5728\u6df1\u5bab\uff0c\u4e0d\u77e5\u9053\u5916\u9762\u7684 \u4e8b\u60c5\uff0c\u5148\u5e1d\u867d\u8bf4\u718a\u5ef7\u5f3c\u662f\u4e2a\u5fe0\u81e3\uff0c\u4f46\u96be\u4fdd\u4ed6\u5728\u5176\u4ed6\u65b9\u9762\u4e0d\u4e13\u6743\u64c5\u65ad\uff0c\u65e2\u7136\u6709\u90a3\u9ebd\u591a\u4eba\u52be\u4ed6\uff0c \u90a3\u4ed6\u4e5f\u4e00\u5b9a\u6709\u505a\u9519\u7684\u5730\u65b9\u3002\u201d\u7531\u6821\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u662f\u8bf4\u8981\u60e9\u529e\u718a\u5ef7\u5f3c\u5417\uff1f\u7236\u7687\u5728\u5730\u4e0b\u77e5\u9053\uff0c\u4e00 \u5b9a\u4e0d\u7b54\u5e94\u7684\u3002\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u4e24\u8fb9\u90fd\u4e0d\u7406\u597d\u5566\u3002\u4f60\u82e5\u5c06\u90a3\u4e9b\u52be\u718a\u5ef7\u5f3c\u7684\u4eba\u95ee\u7f6a\uff0c\u4e00\u65f6\u95f4\u90a3\u80fd\u627e \u8fd9\u9ebd\u591a\u5b98\u513f\u6276\u52a9\u4f60\u5904\u7406\u653f\u4e8b\u3002\u201d\u7531\u6821\u4fa7\u5934\u60f3\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u628a\u90a3\u4e9b\u594f\u7ae0\uff0c\u88c5\u4e00\u5927\u7ba9\uff0c \u90fd\u7ed9\u718a\u5ef7\u5f3c\u9001\u53bb\uff01\u201d \u3000\u3000\u5ba2\u6c0f\u9053\uff1a\u201c\u597d\u4e86\uff0c\u597d\u4e86\uff0c\u5feb\u53bb\u7761\u5427\uff01\u201d\u7531\u6821\u628a\u6240\u5199\u7684\u5b57\u56e2\u63c9\u6210\u4e00\u56e2\uff0c\u63b7\u843d\u684c\u5e95\u3002\u5ba2\u6c0f\u66ff \u4ed6\u628a\u594f\u7ae0\u6536\u62ac\u597d\u4e86\uff0c\u7275\u4ed6\u53bb\u7761\u3002\u7531\u6821\u5ffd\u7136\u505a\u4e86\u4e2a\u602a\u8138\uff0c\u9053\uff1a\u201c\u674e\u9009\u4f8d\u8981\u66ff\u6211\u7acb\u7687\u540e\u5462\uff01\u201d\u674e \u9009\u4f8d\u662f\u5149\u5b97\u5e38\u6d1b\u6700\u5ba0\u7684\u5983\u5b50\uff0c\u7531\u6821\u6bcd\u4eb2\u65e9\u6b7b\uff0c\u4e8b\u4e4b\u5982\u6bcd\u3002\uff0e\u5ba2\u6c0f\u7b11\u9053\uff1a\u201c\u7687\u4e0a\u5927\u559c\u5440\uff0c\u6211\u7684 \u7531\u54e5\u513f\u6210\u4e86\u5927\u4eba\u4e86\u3002\u201d\u7531\u6821\u9053\uff1a\u201c\u6211\u4e0d\u8981\u7687\u540e\uff0c\u6211\u8981\u4e73\u5a18\u505a\u7687\u540e\u3002\u4e73\u5a18\uff0c\u4f60\u771f\u7f8e\uff0c\u4f60\u7684\u5973\u513f \u5c31\u50cf\u4f60\u7684\u59b9\u59b9\u4e00\u6837\uff0c\u548c\u4f60\u7ad9\u5728\u4e00\u8d77\uff0c\u8fd8\u6ca1\u6709\u4f60\u597d\u770b\u5462\uff01\u201d\u5ba2\u6c0f\u5550\u4e86\u4e00\u53e3\u9053\uff1a\u201c\u75af\u8bdd\u513f\uff01\u201d\u5f00 \u4e86\u7761\u623f\u7684\u95e8\uff0c\u548c\u7531\u6821\u8fdb\u53bb\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u98d8\u8eab\u4e0b\u5730\uff0c\u4ece\u684c\u5b50\u5e95\u4e0b\u6361\u8d77\u90a3\u56e2\u7eb8\u56e2\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u63a8\u95e8\u4e4b\u58f0\uff0c\u6025\u53c8\u8df3\u4e0a\u6881\u4e0a\uff0c\u623f \u95e8\u5f00\u5904\uff0c\u4e00\u4e2a\u5a40\u5a1c\u5c11\u5973\u95ea\u8eab\u8d70\u8fdb\u3002\u5cb3\u545c\u73c2\u5fc3\u9053\uff1a\u600e\u9ebd\u8fd9\u4e2a\u5c11\u5973\u5982\u6b64\u5927\u80c6\uff1f\u4e5f\u4e0d\u53eb\u95e8\u5c31\u8fdb\u6765 \u4e86\u3002 \u3000\u3000\u5ba2\u6c0f\u5728\u91cc\u623f\u95ee\u9053\uff1a\u201c\u662f\u5a77\u513f\u5417\uff1f\u201d\u5c11\u5973\u53eb\u4e86\u58f0\u201c\u5988\u3002\u201d\u8fc7\u4e86\u4e00\u9635\uff0c\u5ba2\u6c0f\u4ece\u91cc\u9762\u8d70\u51fa\uff0c\u628a \u95e8\u8f7b\u8f7b\u63a9\u4e0a\uff0c\u9053\uff1a\u201c\u5c0f\u58f0\u4e00\u70b9\uff0c\u7687\u5e1d\u521a\u521a\u7761\u5462\u3002\u201d\u5c11\u5973\u9053\uff1a\u201c\u9b4f\u516c\u516c\u8bf4\u7687\u5e1d\u5728\u4f60\u8fd9\u91cc\uff0c\u6240\u4ee5 \u6211\u624d\u8d76\u6765\u3002\u201d \u3000\u3000\u8fd9\u5c11\u5973\u4e43\u662f\u5ba2\u6c0f\u7684\u5973\u513f\uff0c\u540d\u53eb\u5ba2\u5a09\u5a77\u3002\u5ba2\u6c0f\u672a\u8fdb\u5bab\u524d\uff0c\u9b4f\u5fe0\u8d24\u4e5f\u8fd8\u672a\u505a\u592a\u76d1\uff0c\u4e24\u4eba\u672c\u662f \u8001\u76f8\u597d\uff0c\u5ba2\u6c0f\u548c\u4ed6\u79c1\u901a\uff0c\u751f\u4e0b\u4e00\u5973\uff0c\u5c31\u662f\u8fd9\u4e2a\u5ba2\u5a09\u5a77\u3002\u6240\u4ee5\u795e\u5b97\u6b7b\u5f8c\u4e0d\u4e45\uff0c\u9b4f\u5fe0\u8d24\u4e00\u638c\u4e86 \u6743\uff0c\u5c31\u66ff\u5ba2\u6c0f\u628a\u5979\u5973\u513f\u63a5\u6765\u3002\u4f46\u5ba2\u5a09\u5a77\u5374\u4e0d\u77e5\u9053\u9b4f\u5fe0\u8d24\u662f\u5979\u751f\u8eab\u4e4b\u7236\u3002 \u3000\u3000\u5ba2\u6c0f\u628a\u5973\u513f\u62c9\u5728\u8eab\u65c1\u5750\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u50bb\u4e2b\u5934\uff0c\u4f60\u6765\u505a\u4ec0\u9ebd\u4e86\u4f60\u60f3\u505a\u7687\u540e\u5417\uff1f\u53ef\u60dc\u4f60\u6ca1\u6709 \u8fd9\u6837\u798f\u6c14\u3002\u7687\u5e1d\u867d\u7136\u542c\u6211\u7684\u8bdd\uff0c\u53ef\u662f\u7687\u540e\u5fc5\u987b\u662f\u540d\u95e8\u671b\u65cf\uff0c\u8c01\u53eb\u54b1\u4eec\u7956\u5b97\u6ca1\u505a\u8fc7\u5927\u5b98\u5462\u3002\u8981 \u4f60\u505a\u5983\u5b50\u627e\u53c8\u4e0d\u613f\u610f\u3002\u4e56\u5973\u513f\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u4e00\u5b9a\u7ed9\u4f60\u6311\u4e2a\u597d\u5973\u5a7f\u3002\u201d\u5ba2\u5a09\u5a77\u9762\u7ea2\u7ea2\u7684\u4f6f\u55d4 \u9053\uff1a\u201c\u5988\u3002\u597d\u6ca1\u6b63\u7ecf\u3002\u6211\u95ee\u4f60\u6b63\u7ecf\u7684\u4e8b\uff0c\u4f60\u7ed9\u7687\u4e0a\u8bf4\u4e86\u6ca1\u6709\uff1f\u5e08\u516c\u8bf4\u4ed6\u5077\u5077\u8eb2\u5728\u5b98\u5185\u603b\u662f\u4e0d \u59a5\u3002\u4ed6\u60f3\u5f04\u4e00\u4e2a\u9526\u8863\u536b\u7684\u90fd\u6307\u6325\u505a\u505a\u3002\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u8fd8\u6ca1\u7a7a\u8bf4\u5462\u3002\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u5e08\u516c\u5df2\u4f20 \u4e86\u6211\u7684\u5251\u8c31\uff0c\u4f60\u518d\u4e0d\u66ff\u4ed6\u53bb\u8bf4\uff0c\u6211\u53ef\u96be\u4e3a\u60c5\u3002\u201d\u5ba2\u6c0f\u7b11\u9053\uff1a\u201c\u8fd9\u53c8\u4e0d\u662f\u4ec0\u9ebd\u5927\u4e8b\u60c5\uff0c\u4e56\u5973 \u513f\uff0c\u4f60\u8fd9\u6837\u5fc3\u6025\u5e72\u5417\u4e86\u6211\u660e\u5929\u66ff\u4f60\u4e00\u8bf4\u4fbf\u6210\u3002\u201d \u3000\u3000\u5cb3\u9e23\u73c2\u597d\u751f\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u8fd9\u4e2a\u5973\u5a03\u513f\u4e5f\u6709\u5e08\u516c\uff0c\u8fd8\u7ec3\u5251\u5462\uff01\u84d6\u5a09\u5a77\u5ffd\u9053\uff1a\u201c\u5988\uff0c\u4f60\u501f\u90a3 \u628a\u9f99\u6cc9\u5251\u7ed9\u6211\u77a7\u77a7\u3002\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u522b\u63d0\u8fd9\u628a\u5251\u5566\uff0c\u8fd9\u628a\u5251\u51e0\u4e4e\u5f04\u51fa\u5927\u4e8b\u3002\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u77a7 \u4e00\u77a7\u6709\u4ec0\u9ebd\u5173\u7cfb\u3002\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u8fd9\u5251\u4f60\u53ef\u4e0d\u80fd\u62ff\u53bb\u7528\u3002\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u6211\u542c\u5e08\u516c\u548c\u6155\u5bb9\u603b\u7ba1 \u9053\uff1a\u5bab\u4e2d\u5b9d\u5251\u867d\u591a\uff0c \u3000\u3000\u6709\u8fd9\u628a\u6700\u597d\uff0c\u5176\u4ed6\u7684\u8fd8\u6bd4\u4e0d\u4e0a\u9b4f\u516c\u516c\u65b0\u5f97\u7684\u90a3\u628a\u6e38\u9f99\u5251\u5462\uff01\u201d\u5ba2\u6c0f\u5fae\u9732\u60ca\u8bb6\u4e4b\u5bb9\uff0c\u81ea\u8a00 \u81ea\u8bed\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u90a3\u5c0f\u5b50\u8fd9\u6837\u5b9d\u8d1d\uff1f\u201d\u5cb3\u9e23\u73c2\u542c\u5f97\u5979\u4eec\u8bae\u8bba\u81ea\u5df1\u7684\u5b9d\u5251\uff0c\u5341\u5206\u7559\u610f\u3002\u5ba2\u6c0f\u8fb9 \u8bf4\u8fb9\u62c9\u5f00\u58c1\u6a71\uff0c\u5cb3\u9e23\u73c2\u51dd\u795e\u6ce8\u610f\uff0c\u5ffd\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u4e00\u84ec\u94f6\u5149\u5411\u81ea\u5df1\u5c04\u6765\uff01 \u3000\u3000\u5cb3\u9e23\u73c2\u8863\u8896\u4e00\u62c2\uff0c\u5c06\u90a3\u4e9b\u6885\u82b1\u9488\u7eb7\u7eb7\u62c2\u843d\uff0c\u4e00\u8dc3\u4e0b\u5730\uff0c\u5ba2\u5a09\u5a77\u53eb\u9053\uff1a\u201c\u6709\u523a\u5ba2\uff01\u201d\u5ba2\u6c0f \u89c1\u662f\u5cb3\u545c\u73c2\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u5ba2\u5a09\u5a77\u53eb\u9053\uff1a\u201c\u5988\u522b\u614c\uff0c\u5973\u513f\u62ff\u4ed6\uff01\u201d\u5ba2\u6c0f\u4e00\u6309\u673a\u5173\uff0c\u9690\u4eba\u590d\u58c1\u6697 \u5ba4\u3002\u5ba2\u5a09\u5a77\u968f\u624b\u62d4\u4e86\u4e00\u628a\u957f\u5251\uff0c\u5237\u7684\u4e00\u5251\u5411\u5cb3\u9e23\u73c2\u523a\u6765\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u5927\u5403\u4e00\u60ca\u3002\u5403\u60ca\u7684\u4e0d\u662f\u4e3a\u4e86\u8fd9\u5c11\u5973\u5251\u6cd5\u9ad8\u660e\uff0c\u800c\u662f\u5979\u4f7f\u7684\u7adf\u662f\u7389\u7f57\u5239\u72ec\u95e8\u5251\u6cd5\u7684 \u62db\u6570\uff01\u5f53\u4e0b\u8fde\u907f\u53c1\u62db\uff0c\u95e8\u5916\u4eba\u58f0\u7eb7\u6270\uff0c\u5cb3\u545c\u73c2\u4e00\u4e2a\u201c\u79cb\u6c34\u6a2a\u821f\u201d\uff0c\u5f80\u5979\u624b\u8155\u4e00\u5207\uff0c\u5de6\u624b\u53cc\u6307 \u70b9\u5979\u9762\u4e0a\u53cc\u775b\uff0c\u5ba2\u5a09\u5a77\u6b66\u529f\u867d\u7136\u4e0d\u5f31\uff0c\u7a76\u662f\u521d\u4e34\u5927\u654c\uff0c\u5fc3\u4e00\u614c\uff0c\u88ab\u5cb3\u9e23\u73c2\u5288\u624b\u5c06\u957f\u5251\u593a\u8fc7\uff0c \u7eb5\u8eab\u4e00\u8dc3\uff0c\u4e00\u672c\u5251\u8c31\u5ffd\u7136\u8dcc\u4e0b\u5730\u6765\u201c\u5cb3\u545c\u73c2\u6025\u5fd9\u6361\u8d77\uff0c\u95e8\u5916\u536b\u58eb\u5df2\u7136\u62a2\u8fdb\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u593a\u83b7\u7684\u90a3\u628a\u957f\u5251\u867d\u7136\u4e0d\u662f\u9f99\u6cc9\u5b9d\u5251\uff0c\u5374\u4e5f\u5341\u5206\u950b\u5229\uff0c\u968f\u624b\u4e00\u524a\uff0c\u628a\u4e00\u540d\u536b\u58eb\u7684\u5355 \u5200\u524a\u65ad\uff0c\u53f3\u811a\u4e00\u8d77\uff0c\u53c8\u5c06\u4e00\u540d\u536b\u58eb\u8e22\u51fa\u95e8\u5916\uff0c\u98d8\u8eab\u98de\u4e0a\u5c4b\u6a90\uff0c\u518d\u4e00\u7ffb\u8eab\u4e0a\u4e86\u5c4b\u810a\uff0c\u75be\u5fd9\u9003 \u8dd1\uff0c\u8d8a\u8fc7\u51e0\u91cd\u697c\u53f0\u6bbf\u5b87\uff0c\u5ffd\u542c\u5f97\u56db\u9762\u5927\u558a\u201c\u6349\u523a\u5ba2\u201d\u4e4b\u58f0\uff01\u5cb3\u9e23\u73c2\u8eb2\u5165\u82b1\u6811\u4e1b\u4e2d\uff0c\u53ea\u89c1\u6570\u5341 \u540d\u536b\u58eb\uff0c\u56db\u5904\u6d8c\u6765\uff0c\u8ffd\u8d76\u7684\u65b9\u5411\u5374\u4e0d\u662f\u5411\u81ea\u5df1\u95f9\u4e8b\u7684\u4e73\u5a18\u5e9c\uff0c\u5cb3\u545c\u73c2\u597d\u751f\u5947\u602a\uff0c\u8df3\u4e0a\u6811\u9876\u4e86 \u671b\uff0c\u53ea\u89c1\u8fdc\u5904\u4e00\u6761\u9ed1\u5f71\uff0c\u75be\u82e5\u6d41\u661f\uff0c\u4ece\u5185\u82d1\u4e00\u76f4\u98de\u51fa\u5916\u9762\u7684\u4fdd\u548c\u4e2d\u548c\u592a\u548c\u53c1\u5927\u6bbf\uff0c\u500f\u5ffd\u4e0d \u89c1\uff0c\u8eab\u5f62\u4e4b\u5feb\uff0c\u524d\u6240\u672a\u89c1\uff01\u90a3\u4efd\u8f7b\u529f\u7edd\u4e0d\u5728\u81ea\u5df1\u4e4b\u4e0b\uff01\u5cb3\u9e23\u73c2\u5927\u4e3a\u5947\u602a\uff0c\u60f3\u4e0d\u5230\u6709\u4eba\u548c\u81ea\u5df1 \u5728\u540c\u4e00\u5929\u665a\u4e0a\u591c\u95ef\u6df1\u5bab\u3002 \u3000\u3000\u536b\u58eb\u4eec\u5230\u5904\u641c\u7d22\uff0c\u8fc7\u4e86\u534a\u4e2a\u66f4\u6b21\uff0c\u6e10\u6e10\u6563\u53bb\uff0c\u5cb3\u545c\u73c2\u89c1\u9644\u8fd1  \u6709\u4e24\u540d\u536b\u58eb\u5de1\u9009\uff0c\u8d70\u6765\u8d70 \u53bb\uff0c\u84e6\u7136\u60f3\u9053\uff1a\u6211\u4f55\u4e0d\u6349\u7740\u4ed6\u4eec\u4e00\u95ee\uff0c\u5373\u4ece\u82b1\u6728\u5f8c\u7a81\u7136\u6251\u51fa\uff0c\u53cc\u81c2\u659c\u4f38\uff0c\u4ee5\u95ea\u7535\u822c\u7684\u624b\u6cd5\uff0c \u5206\u70b9\u4e24\u540d\u654c\u4eba\u7a74\u9053\uff0c\u5de6\u8fb9\u90a3\u540d\u536b\u58eb\u5495\u549a\u4e00\u58f0\uff0c\u5e94\u6307\u5373\u5012\uff01\u53f3\u8fb9\u90a3\u540d\u536b\u58eb\u7a81\u7136\u5411\u5f8c\u4e00\u4ef0\uff0c\u53cd\u624b \u4e00\u52fe\uff0c\u7adf\u7136\u52fe\u7740\u4e86\u5cb3\u545c\u73c2\u624b\u8155\uff0c\u5cb3\u545c\u73c2\u5750\u8170\u4e00\u5e26\uff0c\u6ca1\u6709\u5e26\u52a8\uff0c\u81ea\u5df1\u5374\u53cd\u529b\u800c\u7ed9\u4ed6\u53cd\u63a8\u4e86\u51fa \u53bb\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u62d4\u51fa\u957f\u5251\uff0c\u4e00\u5251\u523a\u51fa\uff0c\u90a3\u4eba\u95f7\u58f0\u4e0d\u54cd\uff0c\u8eab\u5f62\u4e00\u7ffb\uff0c\u53cc\u638c\u5207\u843d\uff0c\u7adf\u7136\u62a2 \u653b\u81ea\u5df1\u5de6\u9762\u7a7a\u95e8\uff0c\u5cb3\u545c\u73c2\u5251\u950b\u4e00\u98a4\uff0c\u75be\u523a\u654c\u4eba\u5c0f\u8179\uff0c\u8fd9\u4e00\u62db\u8fc5\u6377\u65e0\u4f26\uff0c\u90a3\u4eba\u201c\u5413\u201d\u7684\u4e00\u58f0\uff0c \u4e00\u4f4e\u5934\uff0c\u7adf\u7136\u4ece\u5251\u5e95\u94bb\u8fc7\uff0c\u53cc\u638c\u8fc5\u6536\u5373\u53d1\uff0c\u638c\u98ce\u5939\u8033\u63a0\u8fc7\uff01\u529f\u529b\u4e4b\u7eaf\uff0c\u53d8\u62db\u4e4b\u901f\uff0c\u4e3a\u5cb3\u545c\u73c2 \u5bf9\u654c\u4ee5\u6765\u6240\u4ec5\u89c1\u3002 \u3000\u3000\u6b8a\u4e0d\u77e5\u5cb3\u545c\u73c2\u5403\u60ca\uff0c\u90a3\u4eba\u5374\u5403\u60ca\u66f4\u751a\u3002\u4ed6\u662f\u4e1c\u5382\u536b\u58eb\u7684\u603b\u6559\u5934\uff0c\u5b98\u4e2d\u7684\u7b2c\u4e00\u628a\u597d\u624b\uff0c\u540d \u53eb\u6155\u5bb9\u51b2\uff0c\u8eab\u517c\u5185\u5916\u4e24\u5bb6\u4e4b\u957f\uff0c\u51e0\u5341\u5e74\u6765\uff0c\u4ece\u65e0\u5bf9\u624b\u3002\u90a3\u6599\u4eca\u665a\u5bab\u4e2d\uff0c\u63a5\u8fde\u4e24\u5904\u62a5\u6709\u523a\u5ba2\uff0c \u795e\u6b66\u5b98\u524d\u53d1\u73b0\u7684\u523a\u5ba2\uff0c\u8f7b\u529f\u5728\u4ed6\u4e4b\u4e0a\uff0c\u8ffd\u4e4b\u4e0d\u53ca\uff0c\u8fd9\u72b9\u8bf4\u662f\u672a\u66fe\u4ea4\u624b\uff0c\u4e0d\u7b97\u6298\u635f\u5a01\u98ce\uff1a\u800c\u8fd9 \u540d\u523a\u5ba2\uff0c\u89c1\u9762\u53c1\u62db\uff0c\u5251\u5251\u8f9b\u8fa3\uff0c\u81ea\u5df1\u51e0\u4e4e\u7ed9\u4ed6\u523a\u4e2d\uff0c\u800c\u4e14\u4ed6\u8eab\u4e0a\u7a7f\u7684\u8fd8\u662f\u4e1c\u5382\u536b\u58eb\u7684\u5236\u670d\uff0c \u770b\u6765\u5fc5\u5b9a\u6709\u4eba\u5df2\u906d\u6bd2\u624b\u3002\u82e5\u7136\u64d2\u4ed6\u4e0d\u5f97\uff0c\u81ea\u5df1\u8fd8\u6709\u4f55\u9762\u76ee\u4ee5\u89c1\u540c\u50da\u3002 \u3000\u3000\u4e24\u4eba\u5404\u6000\u6212\u60e7\uff0c\u624b\u5e95\u4e1d\u6beb\u4e0d\u7f13\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u5404\u81ea\u62a2\u653b\u4e86\u4e00\u4e8c\u5341\u62db\uff01 \u3000\u3000\u5cb3\u545c\u73c2\u89c1\u4ed6\u54d1\u6597\u95f7\u6218\uff0c\u8d77\u4e86\u7591\u5fc3\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u5582\uff0c\u4f60\u662f\u90a3\u6761\u7ebf\u4e0a\u7684\u670b\u53cb\uff01\u6211\u4e0d\u662f\u5bab\u4e2d \u536b\u58eb\uff0c\u4f60\u522b\u8ba4\u9519\u4e86\u4eba\uff01\u201d\u5728\u5cb3\u9e23\u73c2\u5fc3\u4e2d\uff0c\u4ee5\u4e3a\u4ed6\u65e2\u4e0d\u62db\u5524\u540c\u4f34\uff0c\u53ef\u80fd\u50cf\u81ea\u5df1\u4e00\u6837\uff0c\u4e5f\u662f\u5077\u5077 \u6e9c\u8fdb\u7687\u5bab\u3002\u6b8a\u4e0d\u77e5\u6155\u5bb9\u51b2\u8eab\u4e3a\u4e1c\u5382\u536b\u58eb\u7684\u603b\u6559\u5934\u3002\u6b66\u529f\u81ea\u5938\u65e0\u654c\uff0c\u521d\u65f6\u53d1\u73b0\u201c\u523a\u5ba2\u201d\uff0c\u53c8\u60f3 \u72ec\u81ea\u64d2\u83b7\u9886\u529f\uff0c\u751f\u6015\u5176\u4ed6\u536b\u58eb\u8d76\u6765\u5206\u529f\uff0c\u6240\u4ee5\u672a\u66fe\u547c\u5524\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u8fd9\u4e00\u8d77\u7591\uff0c\u51fa\u58f0\u62db\u547c\uff0c\u7565\u4e00\u5206\u5fc3\uff0c\u5251\u6cd5\u7a0d\u7f13\uff0c\u6155\u5bb9\u51b2\u89c1\u9699\u5373\u5165\uff0c\u201c\u84ec\u201d\u7684\u4e00\u62f3\uff0c \u51fb\u5728\u5cb3\u545c\u73c2\u80a9\u4e0a\uff0c\u7adf\u662f\u5cb3\u9e23\u73c2\u5185\u529f\u6df1\u6e5b\uff0c\u4e5f\u6643\u4e86\u51e0\u6643\uff0c\u5fcd\u75db\u8fd8\u4e86\u4e00\u5251\u3002\u6155\u5bb9\u51b2\u4e00\u62db\u5f97\u624b\uff0c\u6251 \u51fb\u8d8a\u52a0\u51cc\u5389\uff01\u5cb3\u9e23\u73c2\u4e2d\u4e86\u4e00\u62f3\uff0c\u60ed\u89c9\u4e0d\u652f\uff0c\u53c8\u6597\u4e86\u4e8c\u53c1\u5341\u62db\uff0c\u4e7e\u6e05\u5bab\u7684\u536b\u58eb\u5df2\u542c\u5230\u58f0\u606f\uff0c\u8fdc \u8fdc\u8d76\u6765\u3002\u6155\u5bb9\u51b2\u6025\u65bc\u9886\u529f\uff0c\u5de6\u624b\u52fe\u62f3\uff0c\u53f3\u624b\u7ef5\u638c\u540c\u65f6\u53d1\u51fa\uff0c\u5cb3\u9e23\u73c2\u5411\u5f8c\u4e00\u4ef0\uff0c\u957f\u5251\u8fc5\u6233\u4e0b \u76d8\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u638c\u98ce\u4ece\u9f3b\u5c16\u63a0\u8fc7\uff0c\u6155\u5bb9\u51b2\u5411\u4e0a\u4e00\u8dc3\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u88e4\u7ba1\u4e5f\u88ab\u523a\u7a7f\uff0c\u5cb3\u545c\u73c2\u4fa7\u8eab \u4e00\u5251\uff0c\u6155\u5bb9\u51b2\u5ffd\u7136\u5927\u53eb\u4e00\u58f0\uff0c\u817e\u8eab\u4fbf\u8d70\u3002\u9ed1\u9edd\u91cc\u4e00\u4e2a\u4eba\u7a9c\u4e86\u51fa\u6765\uff0c\u628a\u5cb3\u545c\u73c2\u4e00\u62c9\uff0c\u8f6c\u5230\u5047\u5c71 \u77f3\u5f8c\u3002 \u3000\u3000\u8fd9\u4eba\u6b63\u662f\u6210\u5764\uff0c\u4ed6\u8eab\u4e3a\u5fa1\u524d\u4f8d\u536b\u7684\u73ed\u957f\uff0c\u5f53\u7136\u4e5f\u662f\u4e00\u6d41\u9ad8\u624b\uff0c\u4ed6\u8eb2\u5728\u5c71\u6d1e\u91cc\u95f7\u5f97\u53d1\u614c\uff0c \u542c\u5f97\u5916\u9762\u58f0\u54cd\u5c91\u5bc2\uff0c\u5077\u5077\u6e9c\u51fa\uff0c\u5ffd\u7136\u53d1\u73b0\u6155\u5bb9\u51b2\u6765\u56de\u641c\u7d22\uff0c\u82e5\u5728\u5e73\u65f6\uff0c\u6210\u5764\u6b66\u529f\u867d\u7136\u7565\u900a\u65bc \u6155\u5bb9\u51b2\uff0c\u8fd8\u4e0d\u81f3\u6015\u4ed6\uff0c\u4f46\u5728\u6b64\u9645\uff0c\u5374\u5413\u5f97\u53c8\u8eb2\u5230\u5047\u5c71\u77f3\u5f8c\u3002\u8eb2\u85cf\u7684\u5730\u65b9\uff0c\u6070\u6070\u548c\u5cb3\u545c\u73c2\u9690\u8eab \u4e4b\u5904\u76f8\u8ddd\u4e0d\u8fdc\u3002 \u3000\u3000\u4e0d\u4e45\uff0c\u5cb3\u9e23\u73c2\u7a9c\u51fa\u548c\u6155\u5bb9\u51b2\u4ea4\u8d77\u624b\u6765\uff0c\u6210\u5764\u65e5\u95f4\u66fe\u53d7\u82e6\u5211\uff0c\u521b\u4f24\u6731\u590d\uff0c\u6025\u5fd9\u8fd0\u6c14\u8c03\u5143\uff0c \u8fc7\u4e86\u4e00\u9635\uff0c\u89c1\u5cb3\u545c\u73c2\u4e2d\u4e86\u4e00\u62f3\u4e4b\u5f8c\uff0c\u6e10\u5904\u4e0b\u98ce\uff0c\u5077\u5077\u6298\u4e86\u51e0\u679d\u7af9\u679d\uff0c\u7528\u6700\u4e0a\u4e58\u7684\u201c\u6458\u53f6\u98de \u82b1\uff0c\u4f24\u4eba\u7acb\u6b7b\u201d\u7684\u6697\u5668\u529f\u592b\uff0c\u53d1\u4e86\u51fa\u53bb\u3002\u6155\u5bb9\u51b2\u6218\u5cb3\u545c\u73c2\u4e0d\u8fc7\u662f\u6253\u4e2a\u5e73\u624b\uff0c\u9aa4\u7136\u53d1\u73b0\u6709\u9ad8\u624b \u6697\u4f3a\u5728\u65c1\uff0c\u53ea\u6015\u6298\u635f\u5f53\u573a\uff0c\u7eb5\u540c\u4f34\u8d76\u6765\uff0c\u4ed6\u5df2\u6709\u4f24\u989c\u9762\uff0c\u6240\u4ee5\u817e\u8eab\u4fbf\u8d70\u3002 \u3000\u3000\u6210\u5764\u628a\u5cb3\u9e23\u73c2\u62d6\u5230\u5047\u5c71\u77f3\u5f8c\uff0c\u9053\uff1a\u201c\u968f\u6211\u6765\u3002\u201d\u8f6c\u8fc7\u51e0\u5904\u5047\u5c71\uff0c\u7a81\u628a\u4e00\u5757\u5927\u77f3\u4e00\u6380\uff0c\u9732 \u51fa\u4e00\u4e2a\u9ed1\u9edd\u9edd\u7684\u5730\u6d1e\u3002\u6210\u5764\u548c\u5cb3\u545c\u73c2\u7f13\u4e86\u53e3\u6c14\uff0c\u53ea\u542c\u5f97\u5916\u9762\u53c8\u95f9\u6210\u4e00\u7247\u3002 \u3000\u3000\u6210\u5764\u9053\uff1a\u201c\u4ece\u8fd9\u91cc\u53ef\u4e00\u76f4\u901a\u5230\u5bab\u5916\u5fa1\u6cb3\uff0c\u4e0d\u5fc5\u5192\u9669\u4ece\u897f\u534e\u95e8\u51fa\u53bb\u4e86\u3002\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u8fd9 \u6761\u795e\u79d8\u5730\u9053\u6ca1\u4eba\u77e5\u9053\u9ebd\uff1f\u201d\u6210\u5764\u9053\uff1a\u201c\u8fd9\u6761\u79d8\u9053\u662f\u5148\u5e1d\u8fd8\u5728\u4e1c\u5bab\u4e4b\u65f6\u6240\u9020\u3002\u53ea\u6709\u4e94\u540d\u536b\u58eb\u77e5 \u9053\u3002\u5148\u5e1d\u4e00\u6b7b\uff0c\u6211\u4eec\u8fd9\u73ed\u5fa1\u524d\u4f8d\u536b\u90fd\u5df2\u5931\u52bf\u3002\u4ed6\u4eec\u672a\u5fc5\u80af\u4e3a\u9b4f\u5fe0\u8d24\u5356\u529b\uff0c\u6211\u6599\u4ed6\u4eec\u672a\u5fc5\u6562\u5192 \u9669\u5230\u5730\u9053\u6765\u641c\u3002\u201d\u4e24\u4eba\u4e00\u8def\u51fa\u53bb\uff0c\u679c\u7136\u6beb\u65e0\u963b\u6ede\uff0c\u80cc\u5f8c\u4e5f\u6ca1\u4eba\u8ffd\u3002\u4e0d\u4e45\u542c\u89c1\u6c34\u58f0\u6dd9\u6dd9\uff0c\u6210\u5764 \u6253\u5f00\u6697\u95e8\uff0c\u6cb3\u6c34\u6df9\u6f2b\u8fdb\u6765\uff0c\u5cb3\u9e23\u73c2\u5c31\u60f3\u7a9c\u51fa\uff0c\u6210\u5764\u53eb\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u4f38\u624b\u5728\u77f3\u58c1\u4e0a\u4e00\u6309\uff0c\u5cb3 \u545c\u73c2\u8fd9\u624d\u770b\u51fa\uff0c\u6d1e\u5916\u6709\u4e00\u9762\u94c1\u8f6e\u75be\u8f6c\uff0c\u8f6e\u53f6\u90fd\u662f\u5c16\u5200\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u8f6c\u52bf\u6e10\u7f13\uff0c\u53c8\u8fc7\u4e86\u4e00\u9635\uff0c \u624d\u5b8c\u5168\u505c\u6b62\u3002 \u3000\u3000\u6210\u5764\u63a9\u4e0a\u6697\u95e8\uff0c\u548c\u5cb3\u545c\u73c2\u4ece\u5200\u8f6e\u4e4b\u4e0b\u94bb\u51fa\uff0c\u4e0a\u5cb8\u4e4b\u5f8c\uff0c\u6210\u5764\u4ef0\u671b\u5929\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u5929\u5feb\u4eae \u4e86\uff0c\u6211\u4eec\u8fd9\u8eab\u6e7f\u6f09\u6f09\u7684\u4e0d\u597d\u884c\u8d70\u3002\u8463\u65b9\u7684\u5bb6\u5c31\u5728\u9644\u8fd1\uff0c\u6211\u4eec\u4e14\u5230\u4ed6\u90a3\u91cc\u6362\u8fc7\u4e00\u8eab\u8863\u88f3\uff0c\u6211\u4e5f \u6709\u8bdd\u8981\u5bf9\u8463\u5ac2\u5b50\u8bf4\u3002\u201d \u3000\u3000\u8463\u65b9\u662f\u6210\u5764\u7684\u526f\u624b\uff0c\u8463\u65b9\u7684\u59bb\u5b50\u4e5f\u662f\u6b66\u6797\u4eba\u7269\uff0c\u5e76\u4e14\u77e5\u9053\u4e08\u592b\u4e00\u5411\u548c\u6210\u5764\u4e0d\u5927\u548c\u597d\uff0c\u5f00 \u95e8\u4e00\u89c1\u6210\u5764\u5e26\u4e86\u53e6\u4e00\u4e2a\u536b\u58eb\u50cf\u4e24\u53ea\u843d\u6c64\u9e66\u4f3c\u7684\u8d70\u4e86\u8fdb\u6765\uff0c\u4e0d\u7981\u5413\u4e86\u4e00\u8df3\uff0c\u6210\u5764\u9053\uff1a\u201c\u5927\u5ac2\u628a \u95e8\u5173\u7d27\uff0c\u6211\u6709\u8bdd\u8981\u548c\u4f60\u8bf4\u3002\u201d \u3000\u3000\u6210\u5764\u628a\u8463\u65b9\u4e34\u5371\u6551\u4ed6\uff0c\u53d7\u4e86\u6697\u7b97\u4e4b\u4e8b\u8bf4\u4e86\uff0c\u8463\u65b9\u59bb\u5b50\u7d20\u77e5\u6210\u5764\u4ece\u4e0d\u8bf4\u8c0e\uff0c\u201c\u54c7\u201d\u7684\u4e00\u58f0 \u54ed\u8d77\u6765\u9053\uff1a\u201c\u6211\u65e9\u53eb\u4ed6\u4e0d\u8981\u5f53\u8fd9\u635e\u4ec0\u5b50\u7684\u5fa1\u524d\u4f8d\u536b\u4e86\uff0c\u8ddf\u6211\u7236\u4eb2\u5e72\u9556\u884c\u8fd8\u81ea\u5728\u5f97\u591a\uff0c\u4ed6\u5374\u4e0d \u542c\uff0c\u5982\u4eca\u679c\u7136\u51fa\u4e86\u4e8b\u4e86\u3002\u201d\u6210\u5764\u9053\uff1a\u201c\u5ac2\u5b50\uff0c\u4f60\u5148\u522b\u54ed\uff0c\u6211\u4eec\u4e8c\u4eba\u867d\u7136\u4e00\u5411\u4e0d\u5927\u548c\u597d\uff0c\u4f46\u4ed6 \u8fd9\u6b21\u820d\u8eab\u6551\u6211\uff0c\u6211\u5374\u611f\u6fc0\u5f97\u5f88\uff0c\u5305\u5728\u6211\u7684\u8eab\u4e0a\uff0c\u628a\u4f60\u4e08\u592b\u6551\u51fa\u6765\u4fbf\u662f\u3002\u201d\u8463\u5927\u5ac2\u6536\u4e86\u773c\u6cea\uff0c \u7741\u5927\u773c\u775b\uff0c\u9732\u51fa\u7591\u60d1\u4e4b\u5bb9\uff0c\u4f3c\u4e4e\u662f\u5728\u8bf4\uff1a\u201c\u4f60\u81ea\u8eab\u96be\u4fdd\uff0c\u5982\u4f55\u80fd\u6551\u6211\u7684\u4e08\u592b\uff1f\u201d \u3000\u3000\u6210\u5764\u9053\uff1a\u201c\u4f60\u62ff\u7eb8\u7b14\u6765\uff0c\u6211\u66ff\u4f60\u5199\u4e00\u5c01\u4fe1\uff0c\u5929\u660e\u5f8c\u4f60\u53bb\u627e\u9526\u8863\u536b\u6307\u6325\u77f3\u6d69\uff0c\u53eb\u4ed6\u66ff\u4f60\u628a \u4fe1\u9001\u7ed9\u9b4f\u5fe0\u8d24\uff0c\u9b4f\u5fe0\u8d24\u518d\u5927\u80c6\u5b50\u4e5f\u4e0d\u6562\u6740\u4f60\u4e08\u592b\uff01\u201d\u5cb3\u9e23\u73c2\u604d\u7136\u609f\u9053\uff1a\u201c\u662f\u554a\uff0c\u6210\u5927\u54e5\u6ca1 \u6b7b\uff0c\u9b4f\u5fe0\u8d24\u81ea\u7136\u4e0d\u6562\u6740\u8463\u5927\u54e5\u3002\u201d \u3000\u3000\u8463\u5927\u5ac2\u8fd9\u65f6\u4e5f\u5df2\u9192\u609f\uff0c\u6210\u5764\u77e5\u9053\u5bab\u4e2d\u7684\u79d8\u5bc6\u592a\u591a\uff0c\u9b4f\u5fe0\u8d24\u4e0e\u5ba2\u6c0f\u79fd\u4e71\u5bab\u5ef7\u8bdb\u9504\u5f02\u5df1\u7b49\u7b49 \u4e8b\u60c5\uff0c\u906e\u7792\u4e0d\u4e86\u6210\u5764\u8033\u76ee\u3002\u800c\u4e14\u5b98\u4e2d\u8fd8\u6709\u8bb8\u591a\u536b\u58eb\u662f\u6210\u5764\u7684\u670b\u53cb\uff0c\u6210\u5764\u4ee5\u6b64\u8981\u631f\uff0c\u9b4f\u5fe0\u8d24\u603b \u4e0d\u80fd\u4e0d\u6709\u6240\u987e\u5fcc\u3002 \u3000\u3000\u6210\u5764\u5199\u4e86\u4e66\u4fe1\uff0c  \u5927\u5ac2\u9053\uff1a\u201c\u6211\u5df2\u66ff\u4f60\u4eec\u51c6\u5907\u4e86\u4e24\u5957\u8863\u670d\uff0c\u4f60\u4eec\u5c06\u5c31\u4e00\u70b9\u7a7f\u5427\u3002\u201d\u6210\u5764 \u548c\u5cb3\u545c\u73c2\u8fdb\u4e86\u5ba2\u623f\uff0c\u63a9\u4e0a\u623f\u95e8\u628a\u6e7f\u8863\u8131\u4e0b\uff0c\u6210\u5764\u7684\u6e7f\u8863\u4e2d\u85cf\u7740\u4e00\u5bf9\u624b\u5957\uff0c\u6210\u5764\u53cd\u8986\u770b\u4e86\u4e00 \u904d\uff0c\u73cd\u800c\u91cd\u4e4b\u7684\u628a\u5b83\u653e\u5728\u684c\u4e0a\u3002\u5cb3\u545c\u73c2\u62ac\u8d77\u7684\u7687\u5e1d\u6240\u5199\u7684\u90a3\u56e2\u7eb8\u56e2\uff0c\u85cf\u5728\u8d34\u8089\u4e4b\u5916\uff0c\u5e78\u559c\u6ca1 \u6709\u6e7f\u900f\uff0c\u6025\u5fd9\u70b9\u8d77\u6cb9\u706f\uff0c\u8d34\u7740\u706f\u7f69\uff0c\u628a\u5b83\u70d8\u4e7e\u3002\u6362\u4e86\u8863\u88f3\uff0c\u6210\u5764\u5ffd\u9053\uff1a\u201c\u5cb3\u5927\u54e5\uff0c\u4f60\u7684\u6b66\u529f \u662f\u9ad8\u660e\u6781\u4e86\uff0c\u5c0f\u5f1f\u8fdc\u8fdc\u4e0d\u5982\u3002\u4f60\u6551\u4e86\u6211\u7684\u6027\u547d\uff0c\u4eca\u751f\u6211\u662f\u65e0\u53ef\u62a5\u7b54\u7684\u4e86\uff0c\u8fd9\u4e00\u5bf9\u624b\u5957\u4e07\u671b\u4f60 \u8d4f\u9762\u6536\u4e0b\u3002\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u6210\u5927\u54e5\uff0c\u8fd9\u662f\u90a3\u91cc\u8bdd\u6765\uff1f\u2026\u2026\u201d\u672c\u60f3\u63a8\u8f9e\uff0c\u89c1\u4ed6\u8f9e\u8bda\u610f\u6073\uff0c\u800c\u4e14 \u4e00\u5bf9\u624b\u5957\u4e5f\u4e0d\u662f\u4ec0\u9ebd\u8d35\u91cd\u4e1c\u897f\uff0c\u4e5f\u4fbf\u6536\u4e86\u3002 \u3000\u3000\u6210\u5764\u89c1\u4ed6\u6536\u597d\u624b\u5957\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u5cb3\u5927\u54e5\uff0c\u8fd9\u5bf9\u624b\u5957\u4e43\u5148\u5e1d\u6240\u8d50\uff0c\u542c\u8bf4\u662f\u7528\u91d1\u4e1d\u733f\u7684\u6bdb \u548c\u9ed1\u9f99\u6c5f\u7684\u767d\u76ae\u7ebf\u7ec7\u6210\uff0c\u5200\u67aa\u4e0d\u4eba\uff0c\u6bd2\u90aa\u4e0d\u4fb5\uff0c\u6234\u4e0a\u4e86\u7528\u6765\u7a7a\u624b\u593a\u4eba\u5175\u5203\uff0c\u90a3\u662f\u6700\u597d\u4e0d \u8fc7\uff01\u201d\u5cb3\u545c\u73c2\u53eb\u9053\uff1a\u201c\u4f60\u4e3a\u4f55\u4e0d\u65e9\u8bf4\uff0c\u8fd9\u6837\u8d35\u91cd\u7684\u793c\u7269\uff0c\u6211\u53ef\u4e0d\u6562\u63a5\u53d7\uff01\u201d\u628a\u624b\u5957\u62ff\u4e86\u51fa \u6765\uff0c\u6210\u5764\u7b11\u9053\uff1a\u201c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c\u4e00\u97ad\uff01\u4f60\u65e2\u7b54\u5141\u6536\u4e86\u6211\u7684\u5fae\u793c\uff0c\u5982\u4f55\u53c8\u8981\u53cd\u6094\uff01\u201d\u5cb3\u9e23\u73c2 \u6ca1\u6cd5\uff0c\u53ea\u597d\u518d\u591a\u8c22\u4e00\u904d\uff0c\u628a\u624b\u5957\u73cd\u91cd\u5730\u85cf\u4eba\u6000\u4e2d\u3002 \u3000\u3000\u8fd9\u65f6\u4e1c\u65b9\u5df2\u9732\u9c7c\u809a\u767d\u8272\uff0c\u8463\u5927\u5ac2\u51fa\u95e8\u96c7\u4e86\u4e00\u8f86\u9a6c\u8f66\uff0c\u6084\u6084\u628a\u6210\u5764\u5cb3\u545c\u73c2\u9001\u8d70\uff0c\u5979\u4e5f\u5165\u7687 \u57ce\u53bb\u4e86\u5df3 \u3000\u3000\u5cb3\u9e23\u73c2\u5429\u5490\u8d76\u9a6c\u8f66\u7684\u9a7e\u5230\u5175\u79d1\u7ed9\u4e8b\u4e2d\u6768\u6d9f\u5bb6\u4e2d\uff0c\u6210\u5764\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u4f60\u662f\u4f4f\u5728\u90a3\u91cc\uff0c\u6768 \u6d9f\u662f\u4e00\u4e2a\u597d\u5b98\u3002\u8c05\u6765\u4ed6\u4eec\u4e0d\u6562\u592a\u8fc7\u653e\u8086\u3002\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u600e\u9ebd\uff1f\u201d\u6210\u5764\u9053\uff1a\u201c\u4f60\u4f4f\u5728\u6768\u5bb6\u6709 \u4eba\u77e5\u9053\u5417\uff1f\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u77e5\u9053\u7684\u4e0d\u591a\uff0c\u6211\u4eba\u4eac\u65f6\u4e5f\u6599\u4e0d\u5230\u53d1\u751f\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u6240\u4ee5\u4e5f\u5c31\u6ca1\u6709\u628a \u5c45\u5904\u4fdd\u5bc6\u3002\u201d\u6210\u5764\u53f9\u4e86\u53e3\u6c14\uff0c\u8d34\u7740\u5cb3\u545c\u73c2\u8033\u6839\u6084\u6084\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u4f4f\u5904\u53ea\u6015\u4ed6\u4eec\u5df2\u77e5\u9053\u4e86\u3002\u201d \u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u77e5\u9053\uff1f\u201d\u6210\u5764\u9053\uff1a\u201c\u524d\u5929\u6211\u88ab\u9b4f\u5fe0\u8d24\u56da\u7981\u4e4b\u524d\uff0c\u542c\u5f97\u6709\u4e9b\u4e1c\u5382\u536b\u58eb\u5546 \u8bae\uff0c\u8bf4\u662f\u8981\u76d1\u89c6\u6768\u5bb6\u3002\u6211\u6b63\u4e0d\u660e\u767d\u4e3a\u4f55\u4ed6\u4eec\u5982\u6b64\uff0c\u539f\u6765\u662f\u4f60\u4f4f\u5728\u90a3\u91cc\u3002\u201d \u3000\u3000\u5cb3\u9e23\u73c2\u5927\u6025\uff0c\u8d76\u5230\u6768\u5bb6\uff0c\u5929\u5df2\u5927\u660e\uff0c\u6210\u5764\u5077\u77a7\u5916\u9762\uff0c\u89c1\u6ca1\u719f\u4eba\uff0c\u548c\u5cb3\u545c\u73c2\u4e0b\u8f66\uff0c\u5ffd\u89c1\u6768 \u5bb6\u5927\u95e8\u6253\u5f00\uff0c\u5bb6\u4eba\u53eb\u9053\uff1a\u201c\u5cb3\u7237\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u5cb3\u545c\u73c2\u548c\u6210\u5764\u8d70\u4e0a\u4e2d\u5802\uff0c\u53ea\u513f\u6768\u6d9f\u7aef\u5750\u5f53\u4e2d\uff0c\u5927\u53eb\u201c\u53cd\u4e86\uff01\u201d\u5cb3\u545c\u73c2\u6025\u95ee\u4f55\u4e8b\uff1f\u6768\u6d9f \u9053\uff1a\u201c\u6211\u8eab\u4e3a\u5175\u90e8\u5927\u5458\uff0c\u6599\u4e0d\u5230\u7adf\u7136\u6709\u5f3a\u76d7\u6253\u6211\u7684\u4e3b\u610f\u3002\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u5931\u4e86\u4ec0\u9ebd\u4e1c\u897f\uff1f\u201d \u6768\u6d9f\u9053\uff1a\u201c\u4e1c\u897f\u5012\u6ca1\u6709\u5931\u4ec0\u9ebd\u3002\u5f3a\u76d7\u53ea\u62ff\u4e86\u4e00\u4e9b\u53e4\u8463\uff0c\u4e0d\u8fc7\u4f60\u90a3\u4f4d\u540c\u4f34\u5374\u7ed9\u8d3c\u4eba\u52ab\u8d70\u4e86\u3002\u201d \u5cb3\u9e23\u73c2\u4e00\u542c\uff0c\u9b42\u98de\u9b44\u6563\uff0c\u4ed6\u548c\u94c1\u73ca\u745a\u610f\u6c14\u867d\u7136\u672a\u5c3d\u76f8\u6ca1\uff0c\u53ef\u662f\u4e00\u8def\u540c\u884c\uff0c\u60c5\u4efd\u5374\u5982\u5144\u59b9\u3002\u5b9a \u4e86\u5b9a\u795e\uff0c\u95ee\u9053\uff1a\u201c\u5f3a\u76d7\u6765\u4e86\u591a\u5c11\uff1f\u201d\u6768\u6d9f\u9053\uff1a\u201c\u5927\u7ea6\u6709\u4e03\u516b\u4e2a\u5427\uff1f\u90fd\u662f\u8499\u9762\u7684\uff01\u4f60\u90a3\u4f4d\u540c\u4f34 \u51fa\u6765\u548c\u4ed6\u4eec\u6253\uff0c\u5be1\u4e0d\u654c\u4f17\uff0c\u7ed9\u6349\u53bb\u4e86\u3002\u201d\u5cb3\u545c\u73c2\u4e00\u60f3\uff1a\u8fd9\u73ed\u5f3a\u76d7\u4e00\u5b9a\u662f\u9b4f\u5fe0\u8d24\u7684\u624b\u4e0b\uff0c\u4f46\u4e0d \u6562\u8bf4\u51fa\uff0c\u514d\u6768\u6d9f\u5fe7\u60e7\uff0c\u53ea\u9053\uff1a\u201c\u5f85\u5c0f\u4f84\u9080\u8bf7\u6b66\u6797\u670b\u53cb\uff0c\u66ff\u8001\u4f2f\u4fa6\u67e5\u3002\u201d\u6768\u6d9f\u9053\uff1a\u201c\u4eac\u4e2d\u4ece\u6765 \u6ca1\u6709\u51fa\u8fc7\u8fd9\u6837\u7316\u7357\u7684\u532a\u5f92\uff0c\u6211\u8981\u5230\u5175\u90e8\u8859\u95e8\u53bb\uff0c\u53eb\u4ed6\u4eec\u901a\u77e5\u4e5d\u95e8\u63d0\u7763\uff0c\u95ee\u4ed6\u662f\u5e72\u4ec0\u9ebd\u7684\uff1f\u4f60 \u56de\u6765\u4e86\u90a3\u597d\u6781\u5566\uff0c\u66ff\u6211\u770b\u7740\u8fd9\u4e2a\u5bb6\u5427\u3002\u201d\u53c8\u5429\u5490\u5bb6\u4e01\u4e25\u5bc6\u770b\u7ba1\u95e8\u6237\uff0c\u6012\u6c14\u51b2\u51b2\uff0c\u4eb2\u81ea\u5230\u5175\u90e8 \u53bb\u4e86\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u548c\u6210\u5764\u8fdb\u5165\u5ba2\u623f\uff0c\u6210\u5764\u9053\uff1a\u201c\u5fc5\u7136\u662f\u4e1c\u5382\u536b\u58eb\u5e72\u7684\u65e0\u7591\u3002\u4ee4\u53cb\u662f\u8c01\uff0c\u53eb\u4ec0\u9ebd\u540d \u5b57\uff0c\u6211\u66ff\u4f60\u6253\u542c\u6253\u542c\u3002\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u6211\u8fdb\u5bab\u53bb\u548c\u4ed6\u4eec\u5927\u95f9\u4e00\u573a\u3002\u201d\u6210\u5764\u6447\u64ad\u5934\u9053\uff1a\u201c\u4e0d \u884c\uff0c\u4f60\u95f9\u4e86\u4e24\u6b21\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4e25\u5bc6\u6212\u5907\u3002\u5bab\u4e2d\u9664\u4e86\u6155\u5bb9\u51b2\u5916\uff0c\u542c\u8bf4\u8fd8\u65b0\u6765\u4e86\u4e24\u540d\u9ad8\u624b\uff0c\u8fde\u6211\u4e5f \u53ea\u662f\u9690\u9690\u7ea6\u7ea6\u7684\u542c\u4ed6\u4eec\u8bf4\uff0c\u4e0d\u77e5\u9053\u4ed6\u4eec\u540d\u5b87\u3002\u50cf\u6b64\u60c5\u5f62\u4e00\u5b9a\u662f\u5728\u6c5f\u6e56\u4e0a\u8f88\u4efd\u6781\u9ad8\u7684\u4eba\uff0c\u5cb3\u5144 \u82e5\u518d\u5192\u9669\u95ef\u5bab\uff0c\u53ea\u6050\u81ea\u6295\u7f57\u7f51\u3002\u6211\u5728\u5bab\u4e2d\u8fd8\u6709\u597d\u53cb\uff0c\u5f85\u8fc7\u4e86\u4e00\u4e24\u5929\uff0c\u98ce\u58f0\u7a0d\u7f13\u4e4b\u5f8c\uff0c\u6211\u5c31\u79d8 \u5bc6\u66ff\u4f60\u6253\u542c\u3002\u201d\u5cb3\u545c\u73c2\u4e00\u60f3\uff0c\u4e5f\u53ea\u597d\u5982\u6b64\uff0c\u9053\uff1a\u201c\u90a3\u9ebd\uff0c\u4f60\u770b\u4ed6\u4eec\u8fd8\u4f1a\u4e0d\u4f1a\u518d\u6765\uff1f\u53ea\u6015\u6211\u4eec \u4e0d\u53bb\u627e\u4ed6\uff0c\u4ed6\u5374\u6765\u627e\u6211\u4eec\u3002\u201d\u6210\u5764\u9053\uff1a\u201c\u5175\u6cd5\u4e91\u865a\u8005\u5b9e\u4e4b\uff0c\u5b9e\u8005\u865a\u4e4b\u3002\u51fa\u4e86\u8fd9\u6869\u4e8b\u60c5\uff0c\u4ed6\u4eec \u6599\u4f60\u4e0d\u6562\u4f4f\u5728\u6768\u5bb6\uff0c\u6211\u4eec\u5374\u504f\u5728\u8fd9\u91cc\u3002\u4ed6\u4eec\u548c\u6768\u6d9f\u6ca1\u6709\u4ec0\u9ebd\u4ec7\u6068\uff0c\u770b\u6765\u4e0d\u4f1a\u518d\u6765\u3002\u518d\u8bf4\uff0c\u4ed6 \u4eec\u82e5\u6765\uff0c\u4ee5\u4f60\u6211\u7684\u6b66\u529f\uff0c\u5f53\u573a\u6349\u4ed6\u4e00\u4e24\u4e2a\uff0c\u7136\u5f8c\u62da\u6b7b\u6253\u51fa\u53bb\uff0c\u628a\u8fd9\u4ef6\u4e8b\u63ed\u7a7f\uff0c\u7d22\u6027\u548c\u4ed6\u5e72\u4e00 \u573a\u3002\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u597d\uff0c\u5c31\u662f\u8fd9\u6837\uff01\u201d \u3000\u3000\u665a\u4e0a\u6768\u6d9f\u56de\u6765\uff0c\u9053\uff1a\u201c\u4e5d\u95e8\u63d0\u7763\u5df2\u4e0b\u65e8\u7f09\u62ff\uff0c\u6211\u9650\u4ed6\u4eec\u5341\u5929\u7834\u6848\u3002\u201d\u5cb3\u9e23\u73c2\u6697\u7b11\u9053\uff1a \u201c\u8fd9\u4e2a\u6848\u53eb\u4e5d\u95e8\u63d0\u7763\u53bb\u529e\uff0c\u5341\u5e74\u4e5f\u4e0d\u4f1a\u7834\uff01\u201d\u6768\u6d9f\u7f13\u4e86\u53e3\u6c14\u5ffd\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u6c14\u6b7b\u6211\u4e86\uff0c\u597d\u5728 \u8fd8\u6709\u4e00\u4ef6\u597d\u6d88\u606f\u53ef\u544a\u8bc9\u4f60\uff0c\u201d \u3000\u3000\u5cb3\u545c\u73c2\u95ee\u9053\uff1a\u201c\u4ec0\u9ebd\u597d\u6d88\u606f\uff1f\u201d\u6768\u6d9f\u9053\uff1a\u201c\u4eca\u65e5\u6211\u5230\u5175\u90e8\u8859\u95e8\uff0c\u63a5\u5230\u4e86\u718a\u7ecf\u7565\u516b\u767e\u91cc\u5feb \u9a6c\u52a0\u6025\u9001\u6765\u7684\u4fe1\uff0c\u8bf4\u662f\u5f8c\u5929\u4fbf\u53ef\u5230\u4eac\uff0c\u544a\u8bc9\u5175\u90e8\u540c\u4ec1\u77e5\u9053\u3002\u4fe1\u4e2d\u5e76\u8bf4\u8981\u5728\u5bd2\u820d\u4e0b\u69bb\u3002\u8fd9\u771f\u662f \u5927\u559c\u4e4b\u4e8b\uff0c\u671d\u4e2d\u4e71\u7cdf\u7cdf\u7684\uff0c\u4e5f\u5f97\u4ed6\u56de\u6765\u7ba1\u4e00\u4e0b\u4e86\u3002\u201d\u718a\u7ecf\u7565\u8981\u56de\u6765\u4e4b\u4e8b\uff0c\u5cb3\u545c\u73c2\u6628\u665a\u5df2\u77e5\uff0c \u4e0d\u8fc7\u73b0\u5728\u6d88\u606f\u66f4\u52a0\u8bc1\u5b9e\uff0c\u5fc3\u4e2d\u4ea6\u662f\u9ad8\u5174\u3002\u4fbf\u9053\uff1a\u201c\u718a\u7ecf\u7565\u867d\u7136\u624b\u63e1\u5175\u6743\uff0c\u4f46\u4ed6\u662f\u5916\u81e3\uff0c\u53ea\u6050 \u7ba1\u4e0d\u4e86\u671d\u5ef7\u4e4b\u4e8b\u3002\u201d\u6768\u6d9f\u9053\uff1a\u201c\u8bba\u804c\u4f4d\u4ed6\u867d\u7136\u9ad8\u4e0d\u8fc7\u53f0\u9601\u4e4b\u81e3\uff0c\u4f46\u4ed6\u6b63\u6c14\u51db\u7136\uff0c\u53c8\u6709\u5c1a\u65b9\u5b9d \u5251\uff0c\u5c31\u662f\u65b9\u4ece  \u9b4f\u5fe0\u8d24\u4e5f\u8981\u6015\u4ed6\u3002\u201d \u3000\u3000\u5230\u4e86\u718a\u5ef7\u5f3c\u56de\u6765\u7684\u65e5\u671f\uff0c\u718a\u5ef7\u5f3c\u7684\u51e0\u4f4d\u597d\u53cb\u5982\u540f\u90e8\u5c1a\u4e66\u5468\u5609\u8c1f\uff0c\u793c\u90e8\u5c1a\u4e66\u5b59\u614e\u884c\uff0c\u90fd\u5fa1 \u53f2\u90b9\u5143\u6807\u7b49\u4eba\u90fd\u5230\u6768\u6d9f\u5bb6\u4e2d\u7b49\u5019\u3002\u5175\u90e8\u5c1a\u4e66\u6768  \u672c\u4e5f\u8981\u6765\uff0c\u4f46\u5374\u56e0\u8c03\u5175  \u897f\u4e4b\u4e8b\uff0c\u4e0d\u80fd\u53c2 \u52a0\u3002\u51e0\u4e2a\u4eba\u4e00\u65e9\u4fbf\u7b49\uff0c\u7b49\u5230\u8fc7\u4e86\u5348\u724c\u65f6\u5206\uff0c\u90fd\u672a\u95fb\u6709\u9e23\u9523\u5f00\u9053\u4e4b\u58f0\uff0c\u6b63\u81ea\u5947\u602a\u3002\u5b59\u614e\u884c\u9053\uff1a \u201c\u83ab\u975e\u6539\u671f\u4e86\uff1f\u201d\u6768\u6d9f\u9053\uff1a\u201c\u718a\u7ecf\u7565\u7edd\u4e0d\u4f1a\u5931\u4fe1\u65bc\u4eba\u3002\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u7ba1\u5bb6\u7684\u6765\u62a5\u9053\uff1a\u201c\u5916\u9762 \u6709\u4e24\u6761\u5927\u6c49\u8981\u89c1\u8001\u7237\u3002\u6211\u95f4\u4ed6\u59d3\u540d\uff0c\u4ed6\u8bf4\u662f\u59d3\u718a\u7684\uff0c\u53ea\u6050\u662f\u718a\u7ecf\u7565\u7684\u5bb6\u4eba\uff0c\u8001\u7237\u89c1\u4e0d\u89c1 \u4ed6\u3002\u201d\u6768\u6d9f\u201c\u554a\u5440\uff01\u201d\u4e00\u58f0\u7ad9\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5feb\u8bf7\u4ed6\u8fdb\u6765\uff01\u8fd9\u4e00\u5b9a\u662f\u8001\u718a\u4e86\uff0c\u6211\u77e5\u9053\u4ed6\u7684\u813e \u6c14\uff01\u201d\u8fc7\u4e86\u7247\u523b\uff0c\u4e00\u4e2a\u864e\u5934\u9e70\u76ee\u7684\u5927\u6c49\u8e0f\u6b65\u8d70\u4e0a\u53f0\u9636\uff0c\u6ee1\u8138\u98ce\u5c18\u4e4b\u8272\uff0c\u5f8c\u9762\u4e00\u4e2a\u968f\u4ece\uff0c\u80cc\u7740 \u4e00\u4e2a\u5305\u88b1\uff0c\u4f17\u5b98\u7eb7\u7eb7\u8d77\u7acb\uff0c\u53eb\u9053\uff1a\u201c\u718a\u7ecf\u7565\uff0c\u4f60\u600e\u9ebd\u4e0d\u9884\u5148\u901a\u62a5\u4e00\u58f0\uff01\u201d\u60f3\u4e0d\u5230\u8fd9\u4e2a\u624b\u63e1\u5175 \u7b26\uff0c\u58f0\u5a01\u8d6b\u8d6b\u7684\u540d\u5c06\uff0c\u7adf\u7136\u53ea\u5e26\u4e86\u4e00\u4e2a\u968f\u4ece\uff0c\u5c31\u4ece\u8fb9\u5173\u6765\u5230\u4eac\u57ce\u3002 \u3000\u3000\u718a\u5ef7\u5f3c\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u524d\u5929\u5c31\u6d3e\u4eba\u9001\u4e86\u4fe1\u5417\uff1f\u600e\u9ebd\u8bf4\u6211\u6ca1\u6709\u901a\u62a5\u3002\u201d\u4f17\u5b98\u6240\u6307\u7684\u201c\u901a \u62a5\u201d\u5176\u5b9e\u4e0d\u662f\u5982\u6b64\uff0c\u53ea\u597d\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u6765\uff0c\u771f\u50cf\u4e00\u4e2a\u521a\u521a\u4ece\u9635\u4e0a\u9000\u4e0b\u6765\u7684\u5175\u5927\u7237\u3002\u201d\u718a\u5ef7 \u5f3c\u5927\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u662f\u5927\u5175\u561b\u3002\u201d\u5cb3\u9e23\u73c2\u4e5f\u6025\u51fa\u6765\u53c2\u89c1\uff0c\u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u4f60\u4e5f\u4f4f\u5728\u8fd9\u91cc\uff0c\u90a3 \u597d\u6781\u5566\uff01\u54b1\u4eec\u665a\u4e0a\u518d\u8c08\u3002\u201d\u63a5\u7740\u628a\u4ed6\u7684\u968f\u4ece\u7ed9\u5404\u4eba\u5f15\u89c1\u3002\u8fd9\u968f\u4ece\u540d\u53eb\u738b\u8d5e\uff0c\u662f\u6b66\u6797\u540d\u5bb6\uff0c\u65e5 \u6708\u8f6e\u90b1\u592a\u865a\u7684\u5165\u5ba4\u5f1f\u5b50\uff0c\u548c\u5cb3\u545c\u73c2\u65e9\u5df2\u76f8\u8bc6\u3002\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u8def\u4e0a\u6ca1\u9047\u5230\u4e8b\uff01\u201d\u738b\u8d5e\u7b11\u9053\uff1a \u201c\u9014\u4e2d\u9047\u8fc7\u4e24\u53c1\u5904\u526a\u5f84\u5f3a\u4eba\uff0c\u89c1\u6211\u4eec\u53ea\u6709\u8fd9\u70b9\u884c\u674e\uff0c\u770b\u90fd\u4e0d\u770b\u5c31\u8d70\u4e86\u3002\u201d\u5cb3\u545c\u73c2\u7b11\u9053\uff1a\u201c\u90a3 \u9ebd\u7b97\u662f\u4ed6\u4eec\u7684\u9020\u5316\u3002\u201d \u3000\u3000\u4f17\u5b98\u56f4\u7740\u718a\u5ef7\u5f3c\u8feb\u4e0d\u53ca\u5f85\u7684\u628a\u671d\u4e2d\u4e71\u7cdf\u7cdf\u7684\u4e8b\u8bf4\u4e86\u51fa\u6765\u3002\u718a\u5ef7\u5f3c\u9ed8\u7136\u503e\u542c\uff0c\u4e0d\u65f6\u6447\u5934\u3002 \u4f17\u5b98\u6b63\u81ea\u8bf4\u5f97\u9ad8\u5174\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u5927\u58f0\u5406\u559d\uff0c\u7ba1\u5bb6\u7684\u62a5\u9053\uff1a\u201c\u94a6\u5dee\u5927\u4eba\u5230\uff01\u201d\u4f17\u5b98\u56de\u907f\uff0c\u718a\u5ef7 \u5f3c\u548c\u5cb3\u9e23\u73c2\u4e5f\u9000\u4eba\u53a2\u623f\uff0c\u6768\u6d9f\u5728\u4e2d\u5802\u7ad9\u7acb\u3002\u8fc7\u4e86\u7247\u523b\uff0c\u5927\u95e8\u5f00\u5904\uff0c\u53ea\u513f\u4e00\u4e2a\u87d2\u888d\u7389\u5e26\u7684\u5b98 \u513f\uff0c\u5e26\u4e86\u51e0\u5341\u540d\u6821\u5c09\uff0c\u8d70\u4e0a\u5802\u6765\u3002\u6768\u6d9f\u6025\u5fd9\u8dea\u4e0b\u9886\u65e8\uff0c\u94a6\u5dee\u9053\uff1a\u201c\u4e0d\u5173\u4f60\u7684\u4e8b\uff0c\u53eb\u718a\u5ef7\u5f3c\u51fa \u6765\uff01\u201d\u718a\u5ef7\u5f3c\u5bf9\u5cb3\u9e23\u73c2\u7b11\u9053\uff1a\u201c\u54a6\uff0c\u6211\u524d\u811a\u521a\u5230\uff0c\u4ed6\u4eec\u5f8c\u811a\u5c31\u6765\u4e86\u3002\u5723\u4e3b\u5e74\u7eaa\u867d\u5e7c\uff0c\u5012\u5f88\u7cbe \u660e\u5462\uff01\u65f6\u95f4\u7b97\u5f97\u8fd9\u6837\u7684\u51c6\uff01\u201d\u8bf4\u7740\uff0c\u968f\u4fbf\u6574\u6574\u8863\u51a0\uff0c\u8d70\u51fa\u5802\u5916\uff0c\u5ffd\u542c\u5f97\u94a6\u5dee\u559d\u9053\uff1a\u201c\u718a\u5ef7\u5f3c \u8dea\u4e0b\u9886\u65e8\uff01\u201d \u3000\u3000\u718a\u5ef7\u5f3c\u8dea\u4e0b\u9886\u65e8\uff0c\u53ea\u542c\u5f97\u94a6\u5dee\u5ba3\u8bfb\u9053\uff1a\u201c\u7f6a\u81e3\u718a\u5ef7\u5f3c\u4e13\u6743\u64c5\u65ad\uff0c\u7eb5\u5175\u6405\u6c11\uff0c\u5de1\u8fb9\u7ecf\u5e74\uff0c \u5e76\u65e0\u5bf8\u8fdb\u3002\u800c\u4eca\u53c8\u64c5\u79bb\u804c\u5b88\uff0c\u79c1\u81ea\u56de\u4eac\uff0c\u85d0\u89c6\u671d\u7eb2\uff0c\u56fe\u8c0b\u4e0d\u8f68\u3002\u7740\u4ee4\u7f34\u56de\u5c1a\u65b9\u5b9d\u5251\uff0c\u4e0b\u5927\u7406 \u5e9c\u5ba1\u95ee\u3002\u201d\u94a6\u5dee\u8bfb\u4e86\u4e4b\u5f8c\uff0c\u559d\u9053\uff1a\u201c\u7ed1\u4e86\uff01\u201d\u718a\u5ef7\u5f3c\u6c14\u5f97\u987b\u7709\u5982\u621f\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6211\u662f\u5148\u5e1d \u53ec\u56de\u6765\u7684\uff0c\u6709\u4ec0\u9ebd\u7f6a\uff1f\u201d\u94a6\u5dee\u559d\u9053\uff1a\u201c\u4f60\u5c82\u4e0d\u95fb\u96f7\u9706\u96e8\u9732\uff0c\u4ff1\u662f\u5929\u6069\uff0c\u4eca\u4e0a\u7684\u5723\u65e8\uff0c\u4f60\u6562\u5486 \u54ee\uff1f\u67e2\u6b64\u4e00\u7aef\u4fbf\u662f\u5927\u7f6a\uff01\u201d\u718a\u5ef7\u5f3c\u6012\u9053\uff1a\u201c\u5723\u4e0a\u5e74\u5e7c\uff0c\u671d\u653f\u88ab\u5978\u81e3\u8d3c\u5b50\u628a\u6301\uff0c\u7f62\u4e86\uff0c\u7f62 \u4e86\uff01\u201d\u675f\u624b\u5c31\u7f1a\u3002\u718a\u5ef7\u5f3c\u8fd8\u4ee5\u4e3a\u8fd9\u771f\u662f\u5723\u65e8\uff0c\u6240\u4ee5\u867d\u7136\u6c14\u6124\u586b\u80f8\uff0c\u5374\u662f\u4e0d\u6562\u8fdd\u80cc\u3002 \u3000\u3000\u6768\u6d9f\u6728\u7acb\u4e00\u65c1\uff0c\u5413\u5f97\u5446\u4e86\u3002\u6821\u5c09\u6b63\u81ea\u6d8c\u4e0a\u6765\u56f0\u7f1a\uff0c\u5cb3\u9e23\u73c2\u5ffd\u7136\u5728\u53a2\u623f\u4e00\u8dc3\u800c\u51fa\uff0c\u820c\u7efd\u6625 \u96f7\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4e14\u6162\uff01\u201d\u94a6\u5dee\u65a5\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff01\u201d\u5cb3\u9e23\u73c2\u53cc\u81c2\u4e00\u632f\uff0c\u628a\u56db\u540d\u51b2\u4e0a\u6765\u7684\u6821 \u5c09\uff0c\u5f39\u51fa\u53c1\u4e08\u5f00\u5916\uff0c\u8dcc\u843d\u53f0\u9636\u3002\u94a6\u5dee\u5927\u53eb\uff1a\u201c\u767d\u65e5\u9752\u5929\uff0c\u4f60\u6562\u9020\u53cd\uff01\u201d\u718a\u5ef7\u5f3c\u6c14\u4e0a\u52a0\u6c14\uff0c\u5389 \u58f0\u65a5\u9053\uff1a\u201c\u5cb3\u545c\u73c2\uff0c\u4f60\u60f3\u9677\u5bb3\u6211\u5417\uff1f\u201d \u3000\u3000\u5cb3\u9e23\u73c2\u864e\u76ee\u542b\u6cea\uff0c\u6025\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u5e05\uff0c\u8fd9\u5723\u65e8\u662f\u5047\u7684\uff01\u201d\u718a\u5ef7\u5f3c\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5047 \u7684\uff1f\u201d\u94a6\u5dee\u65a5\u9053\uff1a\u201c\u80e1\u8bf4\uff01\u201d\u6307\u6325\u6821\u5c09\u6355\u4eba\u3002\u718a\u5ef7\u5f3c\u500f\u7684\u62d4\u51fa\u5c1a\u65b9\u5b9d\u5251\uff0c\u559d\u9053\uff1a\u201c\u4e14\u6162\uff0c\u5f85 \u6211\u5f04\u6e05\u695a\u4e86\uff0c\u518d\u8ddf\u4f60\u53bb\uff01\u201d\u4f17\u6821\u5c09\u7d20\u77e5\u718a\u5ef7\u5f3c\u6709\u4e07\u592b\u4e0d\u5f53\u4e4b\u52c7\uff0c\u66f4\u517c\u4ed6\u8fd9\u4e00\u559d\uff0c\u795e\u5a01\u51db\u51db\uff0c \u4e00\u65f6\u95f4\u4e0d\u6562\u52a8\u624b\u3002\u5cb3\u9e23\u73c2\u4ece\u6000\u4e2d\u638f\u51fa\u4e00\u56e2\u7eb8\u56e2\uff0c\u5c55\u4e86\u5f00\u6765\uff0c\u94fa\u5728\u624b\u5fc3\uff0c\u53eb\u6768\u6d9f\u9053\uff1a\u201c\u6768\u5927 \u4eba\uff0c\u4f60\u6765\u770b\uff0c\u8fd9\u662f\u4e0d\u662f\u5f53\u4eca\u5723\u4e0a\u7684\u4eb2\u7b14\u7b14\u8ff9\uff1f\u201d \u3000\u3000\u6bcf\u9022\u7687\u5e1d\u767b\u4f4d\uff0c\u603b\u6709\u8bcf\u4e66\u5206\u53d1\u5404\u90e8\uff0c\u6170\u52c9\u5927\u5458\u3002\u6768\u6d9f\u4e00\u770b\uff0c\u53ea\u89c1\u7eb8\u4e0a\u5199\u6ee1\u201c\u718a\u5ef7\u5f3c\u662f\u5927 \u5fe0\u81e3\u201d\u51e0\u4e2a\u5927\u5b87\uff0c\u6b6a\u6b6a\u659c\u659c\u7684\u6709\u4e03\u516b\u884c\u4e4b\u591a\uff0c\u679c\u662f\u7531\u6821\u7b14\u8ff9\u3002\u5fc3\u6c14\u987f\u58ee\uff0c\u4e5f\u4e0d\u6687\u95ee\u5cb3\u545c\u73c2\u4ece \u4f55\u5f97\u6765\uff0c\u5927\u559c\u8bf4\u9053\uff1a\u201c\u718a\u5927\u4eba\uff0c\u8fd9\u662f\u5f53\u4eca\u7687\u4e0a\u7b14\u8ff9\uff01\u201d\u53eb\u9053\uff1a\u201c\u5404\u4f4d\u5927\u4eba\u51fa\u6765\uff0c\u54b1\u4eec\u5927\u5bb6\u770b \u770b\uff01\u201d \u3000\u3000\u8fd9\u94a6\u5dee\u662f\u9b4f\u5fe0\u8d24\u7684\u5978\u515a\u5d14\u5448\u79c0\uff0c\u8fd9\u65f6\u614c\u4e86\u624b\u811a\uff0c\u5f3a\u81ea\u9547\u5b9a\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5723\u65e8\u90a3\u6709\u5047 \u7684\uff1f\u201d\u628a\u8bcf\u4e66\u4e00\u5c55\uff0c\u9732\u51fa\u7687\u5e1d\u7389\u73ba\uff0c\u718a\u5ef7\u5f3c\u4e00\u773c\u770b\u53bb\uff0c\u5b57\u8ff9\u867d\u7136\u4e0d\u50cf\uff0c\u7389\u73ba\u5374\u662f\u771f\u7684\u3002\u5cb3\u545c \u73c2\u6025\u9053\uff1a\u201c\u5978\u9609\u5f53\u6743\uff0c\u76d7\u7528\u56fd\u73ba\u3002\u5927\u5e05\u4e0a\u671d\u548c\u4ed6\u8fa9\u53bb\u3002\u201d \u3000\u3000\u718a\u5ef7\u5f3c\u51b7\u7b11\u9053\uff1a\u201c\u5d14\u5448\u79c0\uff0c\u6211\u548c\u4f60\u4eb2\u81ea\u4e0a\u671d\uff01\u201d\u4f17\u5b98\u9053\uff1a\u201c\u6211\u4eec\u966a\u53bb\uff01\u201d\u5d14\u5448\u79c0\u9053\uff1a \u201c\u718a\u5ef7\u5f3c\uff0c\u4f60\u5982\u6b64\u4fae\u8511\u671d\u5ef7\uff0c\u6297\u65e8\u8fdd\u547d\uff0c\u90a3\u662f\u6284\u5bb6\u706d\u65cf\u4e4b\u7978\uff01\u201d\u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u4e0d\u7528\u591a\u8bf4\uff0c\u6211 \u62fc\u6740\u62da\u5250\uff0c\u548c\u4f60\u4e0a\u671d\uff1f\u201d\u5d14\u5448\u79c0\u7075\u673a\u4e00\u52a8\uff0c\u9053\uff1a\u201c\u5723\u4e0a\u5728\u5bab\u5b88\u5b5d\uff0c\u4f60\u8981\u4e0a\u671d\uff0c\u660e\u65e9\u53bb\u5427\u3002\u201d \u53c8\u5047\u610f\u547c\u559d\u9053\uff1a\u201c\u6768\u6d9f\uff0c\u718a\u5ef7\u5f3c\u4ea4\u4f60\u770b\u5b88\u4e86\uff0c\u82e5\u7136\u660e\u65e5\u4e0d\u89c1\uff0c\u552f\u4f60\u662f\u95ee\uff01\u201d\u7387\u9886\u6821\u5c09\u64a4\u9000\uff0c \u718a\u5ef7\u5f3c\u6697\u9053\u5d14\u5448\u79c0\u90a3  \u603b\u9003\u4e0d\u6389\uff0c\u81ea\u5df1\u662f\u5916\u81e3\uff0c\u4e0d\u4fbf\u5728\u6b64\u6263\u7559\u4ed6\u4eec\u3002\u65bc\u662f\u559d\u6b62\u5cb3\u545c\u73c2\uff0c\u8ba9\u4ed6 \u4eec\u9000\u51fa\uff0c\u51e0\u4e2a\u5927\u5b98\u6c14\u5f97\u8bf4\u4e0d\u51fa\u8bdd\uff01 \u3000\u3000\u718a\u5ef7\u5f3c\u9893\u7136\u5750\u4e0b\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u6447\u5934\u8bf4\u9053\uff1a\u201c\u7b97\u8fd9\u5723\u65e8\u662f\u5047\uff0c\u671d\u4e2d\u5978\u515a\u5982\u6b64\u7316\u7357\uff0c\u56fd\u4e8b\u5df2 \u4e0d\u53ef\u4e3a\u4e86\uff01\u201d\u4f17\u5b98\u7eb7\u7eb7\u6170\u529d\u3002\u6768\u6d9f\u9053\uff1a\u201c\u718a\u5927\u54e5\u8fdc\u9053\u56de\u4eac\uff0c\u522b\u7ed9\u8fd9\u4e9b\u5978\u8d3c\u8d25\u4e86\u8c6a\u5174\uff0c\u54b1\u4eec\u559d \u9152\uff01\u201d\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u95fb\u5f97\u5916\u9762\u53c8\u6709\u5927\u58f0\u5406\u559d\uff0c\u628a\u95e8\u6572\u5f97\u9707\u5929\u4ef7\u54cd\uff0c\u6768\u6d9f\u6012\u9053\uff1a\u201c\u5d14\u5448\u79c0\u8fd9 \u8fd8\u6562\u56de\u6765\uff01\u201d\u8bdd\u72b9\u672a\u4e86\uff0c\u5927\u95e8\u7830\u7684\u9707\u5f00\uff0c\u4e00\u7fa4\u4eba\u6d8c\u4e86\u8fdb\u6765\uff0c\u4e2a\u4e2a\u4ee5\u9ed1\u5e03\u8499\u9762\uff0c  \u7559\u9762\u4e0a\u53cc \u775b\u3002\u4e3a\u9996\u7684\u5927\u58f0\u559d\u9053\uff1a\u201c\u542c\u8bf4\u718a\u5927\u5e05\u56de\u6765\uff0c\u54b1\u4eec\u8981\u501f\u70b9\u94f6\u4e24\uff01\u201d\u718a\u5ef7\u5f3c\u72c2\u7b11\u9053\uff1a\u201c\u6211\u4e24\u8896\u6e05 \u98ce\uff0c\u4f55\u6765\u94f6\u4e24\uff01\u201d\u6768\u6d9f\u5927\u53eb\u9053\uff1a\u201c\u767d\u65e5\u9752\u5929\uff0c\u660e\u706b\u6253\u52ab\uff0c\u53cd\u4e86\uff0c\u53cd\u4e86\uff01\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u8fd9\u4e9b \u4eba\u4e0d\u662f\u666e\u901a\u5f3a\u76d7\uff01\u201d\u51e0\u5341\u540d\u5f3a\u76d7\u7eb7\u7eb7\u56f4\u4e0a\uff0c\u718a\u5ef7\u5f3c\u628a\u6768\u6d9f\u63a8\u4eba\u623f\u4e2d\uff0c\u4e3a\u9996\u7684\u201c\u5f3a\u76d7\u201d\u4e00\u624b\u6293 \u4e0b\uff0c\u718a\u5ef7\u5f3c\u4e00\u58f0\u5927\u559d\uff0c\u5b9d\u5251\u6a2a\u5288\uff0c\u90a3\u540d\u201c\u5f3a\u76d7\u201d\u8eab\u5f62\u4e00\u659c\uff0c\u547c\u7684\u4e00\u638c\u626b\u53bb\uff0c\u718a\u5ef7\u5f3c\u53eb\u9053\uff1a \u201c\u4f60\u8fd9\u6837\u8eab\u624b\u505a\u5f3a\u76d7\u5c82\u4e0d\u53ef\u60dc?\u201d\u5cb3\u9e23\u73c2\u4fa7\u8eab\u4e00\u5251\uff0c\u63a5  \u53eb\u9053\uff1a\u201c\u6155\u5bb9\u51b2\u4f60\u8981\u4e0d\u8981\u547d?\u201d\u90a3\u540d \u5f3a\u76d7\u9aa4\u5403\u4e00\u9e6b\uff0c\u7f13\u4e86\u4e00\u7f13\uff0c\u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u9e23\u73c2\uff0c\u4f60\u8ba4\u5f97\u4ed6?\u201d\u6155\u5bb9\u51b2\u89c1\u88ab\u8bc6\u7834\uff0c\u5927\u559d\u4e00\u58f0 \u201c\u628a\u4ed6\u4eec\u5e72\u4e86\uff01\u201d\u51e0\u5341\u540d\u4e1c\u5382\u9ad8\u624b\uff0c\u4e00\u6d8c\u800c\u6765\uff0c\u628a\u718a\u5cb3\u4e8c\u4eba\u8feb\u5230\u5899\u6839\uff01 \u3000\u3000\u539f\u6765\u77eb\u5723\u65e8\uff0c\u626e\u5f3a\u76d7\u90fd\u662f\u9b4f\u5fe0\u8d24\u548c\u5ba2\u6c0f\u7684\u7b56\u5212\uff0c\u60f3\u7792\u4f4f\u7687\u5e1d\uff0c\u628a\u718a\u5ef7\u5f3c\u9664\u53bb\u3002\u738b\u8d5e\u4e00\u6446 \u4e94\u884c\u8f6e\u4ece\u623f\u4e2d\u51b2\u51fa\uff0c\u4e00\u540d\u536b\u58eb\u63d0\u97ad\u5288\u4e0b\uff0c\u7ed9\u4ed6\u4e94\u884c\u8f6e\u4e00\u7ede\uff0c\u987f\u65f6\u8131\u624b\uff0c\u65ad\u4e3a\u4e24\u622a\u3002\u536b\u58eb\u4e2d\u7a81 \u7136\u51b2\u51fa\u4e00\u4e2a\u8001\u5934\uff0c\u53cc\u638c\u75be\u53d1\uff0c\u638c\u98ce\u96c4\u52b2\uff0c\u628a\u4e94\u884c\u8f6e\u7adf\u7136\u9707\u6b6a\uff0c\u5cb3\u545c\u73c2\u8d34\u7740\u5899\u6839\uff0c\u4e00\u5251\u523a\u51fa\uff0c \u90a3\u8001\u8005\u8db3\u6839\u534a\u65cb\uff0c\u5de6\u638c\u4e00\u62db\u201c\u8fc5\u96f7\u51fb\u9876\u201d\uff0c\u6402\u5934\u5288\u4e0b\uff0c\u5927\u767d\u5929\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u624b\u638c\u7ea2\u4f3c\u6731 \u7802\uff0c\u5cb3\u9e23\u73c2\u5927\u53eb\u9053\uff1a\u201c\u91d1\u8001\u602a\uff0c\u4f60\u4e5f\u6765\u4e86\uff01\u201d\u90a3\u8001\u5934\u54c8\u54c8\u5927\u7b11\uff0c\u7d22\u6027\u628a\u9762\u5dfe\u9664\u4e0b\u53eb\u9053\uff1a\u201c\u5cb3 \u545c\u73c2\uff0c\u4eca\u671d\u987b\u62a5\u4f60\u4e00\u5251\u4e4b\u4ec7\uff01\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u5927\u5e05\uff0c\u8fd9\u8001\u8d3c\u7ec3\u7684\u662f\u6bd2\u7802\u638c\uff0c\u4e0d\u8981\u7ed9\u4ed6\u78b0 \u7740\uff01\u201d\u8fd0\u5251\u5982\u98ce\uff0c\u6321\u5728\u718a\u5ef7\u5f3c\u9762\u524d\uff0c\u6155\u5bb9\u51b2\u548c\u91d1\u72ec\u5f02\u5de6\u53f3\u5939\u653b\uff01\u5cb3\u545c\u73c2\u5341\u5206\u5371\u9669\u3002 \u3000\u3000\u718a\u5ef7\u5f3c\u7741\u76ee\u5927\u559d\uff1a\u201c\u9f20\u5b50\u6562\u5c14\uff01\u201d\u7a81\u7136\u53d1\u8d77\u795e\u5a01\uff0c\u628a\u8feb\u8fd1\u8eab\u524d\u7684\u4e00\u540d\u536b\u58eb\u4e00\u624b\u6293\u8d77\uff0c\u6454 \u51fa\u95e8\u53bb\uff01\u4f17\u536b\u58eb\u5403\u4e86\u4e00\u60ca\uff0c\u6155\u5bb9\u51b2\u53eb\u9053\uff1a\u201c\u4e0d\u5fc5\u6015\u4ed6\uff01\u201d\u65bd\u5c55\u5927\u64d2\u62ff\u624b\u6765\u62a2\u718a\u5ef7\u5f3c\u5b9d\u5251\uff01 \u9f9b\uff01\u5ef7\u5f3c\u867d\u7136\u529b\u654c\u4e07\u592b\uff0c\u64d2\u62ff\u6251\u51fb\u5374\u975e\u6240\u957f\uff0c\u51e0\u4e4e\u906d\u4e86\u6155\u5bb9\u51b2\u6bd2\u624b\uff0c\u738b\u8d5e\u62da\u6b7b\u529b\u6218\uff0c  \u662f\u62b5 \u6321\u4e0d\u4f4f\uff01 \u3000\u3000\u6b63\u5728\u7d27\u5f20\uff0c\u623f\u4e2d\u4e00\u4eba\u53c8\u7a9c\u4e86\u51fa\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f17\u5144\u5f1f\u542c\u6211\u4e00\u8a00\uff01\u201d\u6b64\u4eba\u6b63\u662f\u6210\u5764\uff0c\u4e1c \u5382\u536b\u58eb\uff0c\u5168\u90fd\u8ba4\u5f97\uff0c\u7ed9\u4ed6\u4e00\u53eb\uff0c\u4e00\u534a\u4eba\u505c\u4e0b\u4e86\u624b\u3002\u6210\u5764\u53eb\u9053\uff1a\u201c\u718a\u7ecf\u7565\u671d\u5ef7\u67f1\u77f3\uff0c\u53cc\u624b\u64ce \u5929\uff0c\u4f60\u4eec\u600e\u80fd\u5982\u6b64\u4e27\u5fc3\u75c5\u762a\uff0c\u628a\u4ed6\u8c0b\u5bb3\uff01\u9b4f\u95f1\u73b0\u5728\u867d\u7136\u5f97\u52bf\uff0c\u5c06\u6765\u5fc5\u65e0\u597d\u4e0b\u573a\uff0c\u5144\u5f1f\u4eec\uff0c\u5927 \u5bb6\u6563\u4e86\u5427\uff01\u201d\u6709\u51e0\u540d\u536b\u58eb\u7a81\u7136\u5927\u54ed\u8d77\u6765\uff0c\u64a4\u4e0b\u5175\u5668\u4fbf\u9003\uff01\u6155\u5bb9\u51b2\u6025\u5fd9\u559d\u9053\uff1a\u201c\u6210\u5764\u5df2\u662f\u53db \u8d3c\uff0c\u8c01\u6562\u542c\u4ed6\u8bf4\u8bdd\uff0c\u6b7b\u7f6a\u96be\u9003\uff01\u201d\u8fd9\u6279\u7279\u522b\u6311\u9009\u6765\u7684\u536b\u58eb\uff0c\u5341\u4e5d\u90fd\u662f\u9b4f\u5fe0\u8d24\u5fc3\u8179\uff0c\u542c\u4e86\u8fd9 \u8bdd\uff0c\u9664\u4e86\u5c11\u6570\u51e0\u4eba\u5f03\u68b0\u6f5c\u9003\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u53c8\u518d\u56f4\u653b\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u6321\u5728\u718a\u5ef7\u5f3c\u9762\u524d\uff0c\u6210\u5764\u738b\u8d5e\u4e8c\u4eba\uff0c\u4e00\u4eba\u5728\u53f3\uff0c\u4e00\u4eba\u5728\u5de6\uff0c\u8d34\u7740\u5899\u6839\uff0c\u62da\u6b7b\u529b\u6218\u3002 \u5e78\u4e8f\u90a3\u51e0\u5341\u540d\u536b\u58eb\uff0c\u867d\u7136\u8feb\u65bc\u9b4f\u5fe0\u8d24\u4e0e\u6155\u5bb9\u51b2\u4e4b\u52bf\uff0c\u4e0d\u6562\u6f5c\u9003\uff0c\u4f46\u5df2\u6709\u4e00\u534a\u53ea\u662f\u4f5c\u52bf\u4f6f\u653b\uff0c \u4e0d\u80af\u51fa\u529b\u3002\u4f46\uff0c\u867d\u7136\u5982\u6b64\uff0c\u6155\u5bb9\u51b2\u4e0e\u91d1\u72ec\u5f02\u6b66\u529f\u5b9e\u5728\u9ad8\u5f3a\uff0c\u53ea\u6b64\u4e8c\u4eba\u5df2\u4f7f\u5cb3\u9e23\u73c2\u7b49\u53c1\u4eba\u96be\u65bc \u5e94\u4ed8\uff0c\u4f55\u51b5\u8fd8\u6709\u5176\u4ed6\u536b\u58eb\u56f4\u653b\uff0c\u53c8\u6218\u4e86\u7247\u523b\uff0c\u6210\u5764\u80a9\u5934\u4e2d\u4e86\u4e00\u638c\uff0c\u718a\u5ef7\u5f3c\u5de6\u81c2\u4e5f\u4e2d\u4e86\u4e00\u5200\u3002 \u5cb3\u9e23\u73c2\u53cc\u77b3\u55b7\u706b\uff0c\u6325\u5251\u6b7b\u6218\u3002\u5ffd\u7136\u5916\u5c42\u7684\u536b\u58eb\u7eb7\u7eb7\u60e8\u53eb\uff0c\u4e00\u4e2a\u8001\u5934\u5927\u58f0\u559d\u9053\uff1a\u201c\u91d1\u8001\u602a\uff0c\u8fd9 \u56de\u53ef\u627e\u7740\u4f60\u4e86\uff01\u201d\u91d1\u72ec\u5f02\u53eb\u9053\uff1a\u201c\u90dd\u8d24\u5f1f\uff0c\u4f60\u63a5\u4ed6\u5341\u62db\uff01\u201d \u3000\u3000\u53eb\u558a\u58f0\u4e2d\uff0c\u5ffd\u53c8\u542c\u5f97\u54af\u54af\u7b11\u58f0\uff0c\u5341\u5206\u6e05\u8106\uff0c\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u6211\u5462\uff01\u91d1\u8001\u602a\u54b1\u4eec\u7b2c\u4e00\u6b21\u89c1 \u9762\uff0c\u4f60\u4e0d\u8d4f\u9762\u8d50\u62db\u5417\u4e86\uff1f\u201d\u7b11\u58f0\u7ed5\u6881\uff0c\u5bd2\u5149\u95ed\u76ee\uff0c\u53ea\u89c1\u7389\u7f57\u5239\u624b\u63d0\u957f\u5251\uff0c\u53d1\u51fa\u5f02\u6837\u5149\u8292\uff0c\u4ece \u4eba\u4e1b\u4e2d\u6740\u4e86\u8fdb\u6765\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c\u523a\u4f24\u4e86\u4e03\u516b\u540d\u536b\u58eb\uff0c\u76f4\u6740\u5230\u6838\u5fc3\uff01\u6155\u5bb9\u51b2\u5927\u6012\uff0c\u53cd\u624b\u4e00\u52fe\uff0c\u7389 \u7f57\u5239\u4e00\u5251\u6251\u7a7a\uff0c\u51e0\u4e4e\u7ed9\u4ed6\u51fb\u4e2d\uff01\u5251\u950b\u4e00\u98a4\uff0c\u4f3c\u5de6\u53cd\u53f3\uff0c\u6155\u5bb9\u51b2\u4e5f\u51e0\u4e4e\u7ed9\u5979\u523a\u7740\uff0c\u4e24\u4eba\u6362\u4e86\u4e00 \u62db\uff0c\u5404\u81ea\u5403\u60ca\uff01\u7389\u7f57\u5239\u4e3a\u4e86\u80cc\u8179\u53d7\u654c\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u6253\u4e0d\u597d\uff01\u201d\u53cd\u624b\u4e00\u5251\uff0c\u5c06\u4e00\u540d\u536b\u58eb\u523a \u4f24\uff0c\u4f4e\u5934\u53c8\u907f\u8fc7\u6155\u5bb9\u51b2\u4e00\u638c\uff0c\u4e00\u4e2a\u65cb\u8eab\uff0c\u8f6c\u5230\u5cb3\u9e23\u73c2\u8eab\u8fb9\uff0c\u4e5f\u5b66\u7740\u4ed6\u8d34\u5899\u4f5c\u6218\u3002\u5cb3\u545c\u73c2\u5927\u559c \u9053\uff1a\u201c\u7ec3\u5973\u4fa0\uff0c\u5feb\u6765\u4fdd\u536b\u5927\u5e05\uff01\u201d\u7389\u7f57\u5239\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u4f60\u4ec0\u9ebd\u5927\u5e05\uff0c\u6211  \u8981\u5251\u8c31\uff01\u201d \u84e6\u7136\u4e00\u8dc3\u800c\u51fa\uff0c\u4e00\u5251\u5411\u91d1\u72ec\u5f02\u523a\u53bb\uff01\u91d1\u72ec\u5f02\u731b\u53d1\u4e00\u638c\uff0c\u638c\u98ce\u9875\u80f8\uff0c\u7389\u7f57\u5239\u88ab\u9707\u8feb\u9000\u4e86\u4e00\u6b65\uff0c \u5251\u950b\u4e00\u8f6c\uff0c\u5e26\u5b88\u5e26\u653b\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u5514\uff0c\u679c\u7136\u4e0d\u9519\uff01\u53ea\u662f\u4e5f\u8fd8\u4e0d\u914d\u8981\u6211\u7684\u5251\u8c31\uff01\u201d\u4fa7\u8eab\u4e24\u8bb0\u602a \u62db\uff0c\u91d1\u72ec\u5f02\u4e5f\u7ed9\u8feb\u5f97\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u53eb\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\uff0c\u4f60\u7684\u5251\u8c31\u5305\u5728\u6211\u7684\u8eab\u4e0a\uff0c\u4f60\u4eca\u65e5\u5982\u6b64\u51fa\u529b\uff0c\u6211\u5148\u8c22\u4f60\uff01\u201d\u7389\u7f57 \u5239\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u9886\u4f60\u7684\u60c5\uff0c\u6211\u4e5f\u4e0d\u662f\u66ff\u4f60\u51fa\u529b\u3002\u201d\u8bdd\u867d\u7136\u5982\u6b64\uff0c\u4f46\u5979\u624b\u4e2d\u5251\u62db\uff0c\u53ef\u662f\u62db\u62db\u6bd2 \u8fa3\uff0c\u4e1d\u6beb\u4e0d\u7f13\u3002\u5cb3\u545c\u73c2\u767e\u5fd9\u4e2d\u659c\u773c\u4e00\u77a5\uff0c\u5ffd\u89c1\u7389\u7f57\u5239\u624b\u4e0a\u90a3\u628a\u5b9d\u5251\uff0c\u751a\u4f3c\u81ea\u5df1\u7684\u6e38\u9f99\u5251\uff0c\u975e \u5e38\u5947\u602a\uff0c\u4f46\u5728\u56f4\u653b\u4e4b\u4e2d\uff0c\u5df2\u65e0\u6687\u7ec6\u5fc3\u8fa8\u8ba4\uff01 \u3000\u3000\u9163\u6597\u4e2d\u5ffd\u53c8\u542c\u5f97\u5916\u5c42\u536b\u58eb\u51fa\u58f0\u547c\u559d\uff0c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u91d1\u5927\u54e5\uff0c\u662f\u786c\u628a\u5b50\uff01\u201d\u91d1\u72ec\u5f02\u5e94\u9053\uff1a \u201c\u6211\u77e5\u9053\uff0c\u5206\u4e00\u534a\u53bb\u56f4\u4ed6\uff01\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u6740\u8fdb\u6765\uff01\u91d1\u8001\u602a\u5728\u8fd9\u91cc\uff01\u201d\u5916\u9762\u4e00\u4e2a \u82cd\u8001\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u884c\u5440\uff01\u7389\u5a03\u513f\uff01\u201d\u84e6\u7136\u53ea\u89c1\u51e0\u540d\u536b\u58eb\u98de\u5728\u534a\u7a7a\uff0c\u539f\u6765\u662f\u7ed9\u90a3\u8001\u5934\u7528\u5927\u6454 \u7891\u624b\u6293\u4e86\u8d77\u6765\uff0c\u6454\u51fa\u95e8\u53bb\uff01\u7247\u523b\u4e4b\u5f8c\uff0c\u90a3\u8001\u5934\u8fb9\u6253\u8fb9\u6251\u4eba\u6765\uff0c\u5cb3\u545c\u73c2\u4e0d\u77e5\u6b64\u4eba\u4fbf\u662f\u5a01\u9707\u897f\u5317 \u7684\u94c1\u98de\u9f99\uff0c\u89c1\u5982\u6b64\u58f0\u52bf\uff0c\u751a\u4e3a\u60ca\u5f02\uff0c\u5fc3\u60f3\uff1a\u8fd9\u4e2a\u5973\u9b54\u5934\u679c\u7136\u795e\u901a\u5e7f\u5927\uff0c\u5c45\u7136\u8ba4\u4e86\u8fd9\u6837\u4e00\u4e2a\u7239 \u7239\u3002 \u3000\u3000\u94c1\u98de\u9f99\u4e0e\u7389\u7f57\u5239\u4e00\u6765\uff0c\u5cb3\u9e23\u73c2\u8fd9\u8fb9\u5b9e\u529b\u5927\u589e\uff0c\u53ef\u662f\u654c\u4eba\u90a3\u8fb9\u529b\u91cf\u66f4\u589e\uff01\u539f\u6765\u90a3\u4e9b\u536b\u58eb\u8d77 \u521d\u4e0d\u60f3\u9677\u5bb3\u718a\u5ef7\u5f3c\uff0c\u6709\u4e00\u534a\u4e0d\u80af\u51fa\u529b\uff0c\u53ef\u662f\u5230\u94c1\u98de\u9f99\u4e0e\u7389\u7f57\u5239\u4e00\u6765\uff0c\u4e0b\u624b\u6bd2\u8fa3\u65e0\u6bd4\uff0c\u4e0d\u89c9\u6fc0 \u8d77\u516c\u6124\uff01 \u3000\u3000\u90a3\u4e9b\u539f\u5148\u4e0d\u80af\u51fa\u529b\u7684\u536b\u58eb\uff0c\u89c1\u540c\u4f34\u7ed9\u7389\u7f57\u5239\u523a\u4f24\u904d\u5730\uff0c\u800c\u4e14\u6bcf\u4e00\u5251\u4e2d\u7684\u4e0d\u662f\u5173\u8282\u8981\u5bb3\uff0c \u5c31\u662f\u7a74\u9053\u6240\u5728\uff0c\u75db\u5f97\u6eda\u5730\u5927\u53eb\uff0c\u60e8\u4e0d\u5fcd\u95fb\u3002\u90a3\u4e9b\u88ab\u94c1\u98de\u9f99\u6454\u6b7b\u6253\u4f24\u7684\uff0c\u66f4\u662f\u8111\u6d46\u8ff8\u6d41\u3002\u536b\u58eb \u4eec\u5927\u6012\uff0c\u7eb7\u7eb7\u56f4\u653b\u7389\u7f57\u5239\u94c1\u98de\u9f99\u4e8c\u4eba\uff0c\u718a\u5ef7\u5f3c\u8fd9\u8fb9\uff0c\u53cd\u800c\u51cf\u4e86\u538b\u529b\u3002\u56f4\u653bP \u3000\u3000\u7389\u7f57\u5239\u5251\u6cd5\u867d\u9ad8\uff0c\u53ef\u662f\u987b\u914d\u4ee5\u8f7b\u529f\uff0c\u624d\u76f8\u5f97\u76ca\u5f70\u3002\u5728\u56f4\u653b\u4e2d\u8f7b\u529f\u4f7f\u4e0d\u51fa\u6765\uff0c\u5a01\u529b\u51cf\u4e86\u4e00 \u534a\uff0c\u5e78\u597d\u94c1\u98de\u9f99\u4e0b\u76d8\u529f\u592b\u6781\u7a33\uff0c\u638c\u529b\u96c4\u52b2\u5f02\u5e38\uff0c\u5251\u638c\u76f8\u8fde\uff0c\u8fd9\u624d\u62b5\u6321\u5f97\u4f4f\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u89c1\u5f62\u52bf\u7565\u7a33\uff0c\u4f46\u5371\u673a\u4ecd\u672a\u6d88\u901d\uff0c\u800c\u4e14\u53c8\u6015\u4e1c\u5382\u589e\u63f4\uff0c\u5fc3\u4e2d\u4ecd\u7136\u7740\u6025\u3002\u7389\u7f57\u5239\u5237\u5237 \u4e24\u5251\uff0c\u628a\u903c\u8fd1\u8eab\u524d\u7684\u4e00\u540d\u536b\u571f\u523a\u4f24\uff0c\u53c8\u5a07\u7b11\u9053\uff1a\u201c\u5cb3\u545c\u73c2\uff0c\u4f60\u7684\u597d\u670b\u53cb\u5462\uff1f\u201d\u5cb3\u9e23\u73c2\u5fc3\u5ff5\u4e00 \u52a8\uff0c\u5e94\u9053\uff1a\u201c\u5c31\u6765\uff01\u201d\u817e\u51fa\u5de6\u624b\uff0c\u53d6\u4e86\u6210\u5764\u6240\u8d60\u7684\u624b\u5957\u5e26\u4e0a\uff0c\u7a81\u7136\u51b2\u4e86\u51fa\u53bb\uff01\u91d1\u72ec\u5f02\u5927\u559d\u4e00 \u58f0\uff1a\u201c\u90a3\u91cc\u8d70\uff01\u201d\u547c\u7684\u4e00\u638c\u6a2a\u626b\u8fc7\u53bb\uff0c\u5cb3\u545c\u73c2\u7a81\u7136\u4f38\u51fa\u5de6\u638c\u4e00\u63a5\uff0c\u53f3\u624b\u5251\u95ea\u7535\u60ca\u98d9\uff0c\u201c\u5580\u201d \u7684\u4e00\u5251\u5c06\u4ed6\u80eb\u9aa8\u523a\u7a7f\u3002\u5de6\u638c\u501f\u4ed6\u7684\u638c\u529b\uff0c\u817e\u8eab\u98de\u8d77\uff0c\u7adf\u7136\u4ece\u4f17\u536b\u58eb\u5934\u4e0a\uff0c\u98de\u8d8a\u8fc7\u53bb\uff01 \u3000\u3000\u6309\u8bf4\u91d1\u72ec\u5f02\u6b66\u529f\u7edd\u4e0d\u5728\u5cb3\u545c\u73c2\u4e4b\u4e0b\uff0c\u5982\u4f55\u4f1a\u5403\u6b64\u5927\u4e8f\uff1f\u539f\u6765\u91d1\u72ec\u5f02\u81ea\u6043\u638c\u6709\u5267\u6bd2\uff0c\u5cb3\u9e23 \u73c2\u4ece\u4e0d\u6562\u786c\u63a5\uff0c\u6240\u4ee5\u677e\u4e86\u6212\u5907\u3002\u90a3\u77e5\u5cb3\u545c\u73c2\u5e26\u4e86\u91d1\u4e1d\u624b\u5957\uff0c\u4e0d\u6015\u6bd2\u4f24\uff0c\u7adf\u7136\u7528\u4e86\u4e00\u8bb0\u7edd\u5feb\u7684 \u62db\u6570\u548c\u4ed6\u62a2\u653b\uff0c\u4e00\u62db\u5f97\u624b\u4fbf\u5373\u9003\u51fa\uff01 \u3000\u3000\u718a\u5ef7\u5f3c\u89c1\u5cb3\u545c\u73c2\u4e34\u5371\u9003\u8d70\uff0c\u4e0d\u89c9\u5927\u5947\u3002\u738b\u8d5e\u6c14\u9053\uff1a\u201c\u60a3\u96be\u89c1\u4eba\u5fc3\uff0c\u679c\u7136\u4e0d\u9519\uff01\u201d\u718a\u5ef7\u5f3c \u9053\uff1a\u201c\u5cb3\u9e23\u73c2\u60f3\u662f\u53e6\u6709\u4f5c\u4e3a\uff0c\u4f60\u4e0d\u8981\u80e1\u4e71\u731c\u7591\uff01\u201d\u5b9d\u5251\u5c55\u5f00\uff0c\u5bd2\u5149\u6325\u970d\uff01\u91d1\u72ec\u5f02\u53d7\u4e86\u5251\u4f24\uff0c \u529f\u529b\u5927\u51cf\uff0c\u6155\u5bb9\u51b2\u867d\u7136\u6b66\u827a\u9ad8\u5f3a\uff0c\u4f46\u718a\u5ef7\u5f3c\u795e\u52c7\u8fc7\u4eba\uff0c\u53c8\u6709\u738b\u8d5e\u6210\u5764\u4e24\u540d\u9ad8\u624b\u63a9\u62a4\uff0c\u800c\u4e14\u5176 \u4ed6\u7684\u536b\u58eb\u53c8\u4e0d\u80af\u653b\u4ed6\uff0c\u6240\u4ee5\u867d\u7136\u4e0d\u80fd\u7a81\u56f4\uff0c\u5012\u4e5f\u80fd\u6682\u5b89\u65e0\u4e8b\u3002 \u3000\u3000\u518d\u8bf4\u767d\u77f3\u9053\u4eba\u88ab\u4eba\u5a01\u5413\uff0c\u5927\u4e3a\u6124\u6012\uff0c\u53ec\u96c6\u4e86\u4eac\u4e2d\u7684\u6b66\u5f53\u6d3e\u5f1f\u5b50\u5341\u591a\u4eba\uff0c\u66f4\u52a0\u4e0a\u67f3\u897f\u94ed\u7ea6 \u6765\u7684\u9ad8\u624b\u5341\u591a\u4eba\uff0c\u6d4e\u6d4e\u4e00\u5802\uff0c\u51c6\u5907\u4e0e\u654c\u4eba\u51b3\u4e00\u96cc\u96c4\u3002\u5019\u4e86\u4e24\u5929\uff0c\u654c\u4eba\u8e2a\u5f71\u4e0d\u89c1\u3002\u8fd9\u65e5\u5df2\u662f\u6700 \u5f8c\u65e5\u671f\uff0c\u5fc3\u60c5\u4efd\u5916\u7d27\u5f20\uff0c\u4f17\u4eba\u96c6\u5728\u67f3\u5bb6\uff0c\u4ece\u65e9\u4e0a\u5b88\u5230\u4e0b\u5348\uff0c\u4ecd\u7136\u4e0d\u89c1\u654c\u8e2a\u3002\u67f3\u897f\u94ed\u7b11\u9053\uff1a \u201c\u6b66\u5f53\u6d3e\u58f0\u5a01\u76d6\u4e16\uff0c\u6709\u4ec0\u9ebd\u4eba\u6562\u8f7b\u634b\u864e\u987b\u3002\u201d\u767d\u77f3\u9053\u4eba\u751a\u4e3a\u5f97\u610f\uff0c\u7b11\u9053\uff1a\u201c\u8fc7\u4e86\u4eca\u65e5\uff0c\u6211\u53ef \u4e0d\u7b49\u4ed6\u4e86\u3002\u201d\u6b63\u8c08\u7b11\u95f4\uff0c\u5ffd\u6709\u6b66\u5f53\u5f1f\u5b50\u62a5\u9053\uff1a\u201c\u6709\u4eba\u6765\uff01\u201d\u67f3\u897f\u94ed\u95ee\u9053\uff1a\u201c\u6709\u591a\u5c11\uff1f\u201d\u628a\u98ce \u7684\u5f1f\u5b50\u62a5\u9053\uff1a\u201c\u53ea\u662f\u4e00\u4eba\uff01\u201d\u67f3\u897f\u94ed\u5947\u9053\uff1a\u201c\u8fd9\u6837\u5927\u80c6\uff0c\u628a\u95e8\u6253\u5f00\uff0c\u8ba9\u4ed6\u8fdb\u6765\uff01\u201d\u7247\u523b\u4e4b \u5f8c\uff0c\u4e00\u4eba\u6ee1\u5934\u5927\u6c57\u51b2\u8fdb\uff0c\u4f17\u4eba\u7eb7\u7eb7\u8d77\u7acb\uff0c\u51c6\u5907\u8fce\u654c\u3002\u5353\u4e00\u822a\u53eb\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u662f\u5cb3\u5927\u54e5\uff01\u201d \u767d\u77f3\u9053\u4eba\u677e\u4e86\u53e3\u6c14\uff0c\u4ee5\u4e3a\u4ed6\u662f\u5f97\u4e86\u8baf\u606f\uff0c\u8d76\u6765\u52a9\u62f3\u7684\u3002\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5cb3\u82f1\u96c4\uff0c\u4e0d\u5fc5\u6709\u52b3\u5927\u9a7e \u4e86\uff01\u201d\u5cb3\u545c\u73c2\u7b11\u4e86\u4e00\u7b11\uff0c\u8d70\u4e0a\u524d\u53bb\u4e0e\u5353\u4e00\u822a\u62c9\u624b\uff0c\u7a81\u7136\u62da\u6307\u5728\u5353\u4e00\u822a\u8170\u95f4\u4e00\u6233\uff0c\u70b9\u4e86\u4ed6\u7684\u8f6f \u54d1\u7a74\uff0c\u4e00\u8f6c\u8eab\u5c06\u4ed6\u80cc\u8d77\uff0c\u98de\u4e00\u822c\u7684\u51b2\u51fa\u95e8\u53bb\u3002\u6ee1\u5802\u9ad8\u624b\uff0c\u9aa4\u51fa\u4e0d\u610f\uff0c\u5168\u90fd\u6115\u7136\u3002\u6b63\u662f\uff1a\u7a81\u51fa \u5947\u5175\u65bd\u5999\u8ba1\uff0c\u6ee1\u5802\u9ad8\u624b\u5c3d\u853c\u5947\u3002\u6b32\u77e5\u5cb3\u545c\u5777\u4f55\u6545\u5c06\u5353\u4e00\u822a\u63b3\u53bb\uff0c\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377\u2014\u2014\u7b2c\u5341\u4e09\u56de\u3000\u98ce\u96e8\u591a\u7ecf\u3000\u65ad\u80a0\u9057\u65e7\u6068\u3000\u5e02\u671d\u6613\u6539\u3000\u5386\u52ab\u817e\u65b0\u6101 \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377 \u7b2c\u5341\u4e09\u56de \u98ce\u96e8\u591a\u7ecf\u3000\u65ad\u80a0\u9057\u65e7\u6068 \u5e02\u671d\u6613\u6539\u3000\u5386\u52ab\u817e\u65b0\u6101 \u3000\u3000\u4e14\u8bf4\u5cb3\u545c\u73c2\u7a81\u5982\u5176\u6765\uff0c\u628a\u5353\u4e00\u822a\u7684\u8f6f  \u54d1\u7a74\u70b9\u4e86\uff0c\u4e00\u8f6c\u8eab\u5c06\u4ed6\u80cc\u8d77\uff0c\u98de\u4e00\u822c\u7684\u51b2\u51fa\u95e8 \u53bb\uff0c\u6ee1\u5802\u9ad8\u624b\u65e0\u4e0d\u6115\u7136\u3002\u767d\u77f3\u9053\u4eba\u6012\u559d\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u8fd9\u5c0f\u5b50\u4e0e\u6211\u4e3a\u96be\uff0c\u8ffd\uff1f\u201d\u7387\u5148\u4ed7\u5251\u8ffd \u51fa\uff01\u67f3\u897f\u94ed\u77e5\u9053\u5cb3\u545c\u73c2\u8eab\u4efd\uff0c\u9053\uff1a\u201c\u9053\u5144\u4e0d\u53ef\u9c81\u83bd\uff1f\u201d\u767d\u77f3\u9053\u4eba\u5df2\u7387\u6b66\u5f53\u5f1f\u5b50\u8ffd\u51fa\u5927\u95e8\u3002\u67f3 \u897f\u94ed\u548c\u4e00\u4f17\u6b66\u5e08\u53ea\u597d\u8ddf\u7740\u8ffd\u51fa\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u8f7b\u529f\u5353\u7edd\uff0c\u80cc\u4e86\u4e00\u4eba\uff0c\u8fd8\u662f\u6bd4\u767d\u77f3\u9053\u4eba\u9ad8\u51fa\u5c11\u8bb8\uff0c\u767d\u77f3\u9053\u4eba\u4f7f\u51fa\u201c\u516b\u6b65\u8d76\u8749\u201d\u7684 \u7edd\u6280\uff0c\u8fd8\u662f\u843d\u5f8c\u4e24\u53c1\u4e08\u5730\u4e4b\u8fdc\uff0c\u6068\u5f97\u7259\u6839\u75d2\u75d2\u7684\uff0c\u4f46\u6295\u9f20\u5fcc\u5668\uff0c\u53c8\u4e0d\u6562\u65bd\u653e\u6697\u5668\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u4e00\u53e3\u6c14\u8dd1\u5230\u6768\u5bb6\uff0c\u8fd9\u624d\u628a\u5353\u4e00\u822a\u7a74\u9053\u89e3\u5f00\u3002\u5353\u4e00\u822a\u521a\u521a\u8f6c\u9192\uff0c\u4fbf\u542c\u5f97\u91cc\u9762\u91d1\u94c1\u4ea4 \u9e23\uff0c\u53f1\u5412\u8ffd\u9010\u7684  \u6740\u58f0\uff0c\u51e0\u4e4e\u7591\u662f\u53d1\u4e86\u4e00\u573a\u6076\u68a6\uff0c\u672a\u53ca\u5f00\u58f0\uff0c\u5cb3\u545c\u73c2\u5df2\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u5353 \u5144\uff0c\u52a9\u6211\u4e00\u81c2\u4e4b\u529b\uff0c\u6551\u718a\u7ecf\u7565\uff1f\u201d \u3000\u3000\u518d\u8bf4\u7389\u7f57\u5239\u4e0e\u94c1\u98de\u9f99\u6b63\u5728\u5403\u7d27\uff0c\u5ffd\u89c1\u5353\u4e00\u822a\u4e0e\u5cb3\u545c\u73c2\u8fde\u8882\u800c\u6765\uff0c\u7cbe\u795e\u9661\u632f\uff0c\u957f\u5251\u4e00\u6296\uff0c \u6362\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u4e00\u62db\u201c\u674e\u5e7f\u5c04\u77f3\u201d\uff0c\u76f4\u53d6\u91d1\u72ec\u5f02\u54bd\u5589\u8981\u5bb3\uff1a\u91d1\u72ec\u5f02\u80a9\u5934\u4e00\u504f\uff0c\u53cd\u624b\u52fe\u5979\u7684\u624b \u8155\uff0c\u94c1\u98de\u9f99\u4e00\u62f3\u6363\u51fa\uff0c\u91d1\u72ec\u5f02\u6c89\u8155\u4e00\u683c\uff0c\u7adf\u7ed9\u9707\u9000\u4e24\u6b65\uff1a\u7389\u7f57\u5239\u5df2\u500f\u7684\u51b2\u51fa\uff0c\u5b9d\u5251\u4e0a\u4e0b\u7ffb \u98de\uff0c\u987f\u65f6\u95f4\u8fde\u4f24\u56db\u540d\u4e1c\u5382\u536b\u58eb\u51b2\u51fa\u53bb\u63a5\u5e94\u5353\u4e00\u822a\u4e86\u3002 \u3000\u3000\u5353\u4e00\u822a\u89c1\u94c1\u98de\u9f99\u4e0e\u7389\u7f57\u5239\u90fd\u5728\u6b64\u5730\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u9ebd\u56de\u4e8b\uff1f\u201d\u5cb3\u9e23\u73c2\u9053\uff1a \u201c\u4f60\u4e0e\u7ec3\u5973\u4fa0\u654c\u7740\u8fd9\u73ed\u5f3a\u76d7\uff0c\u6211\u53bb\u6551\u5927\u5e05\u3002\u201d\u8fd0\u5251\u5982\u98ce\uff0c\u659c\u523a\u6740\u5f00\u8840\u8def\u3002\u5353\u4e00\u822a\u8ddf\u8e2a\u671b\u53bb\uff0c \u53ea\u89c1\u5899\u89d2\u4e00\u4e2a\u9b41\u68a7\u6c49\u5b50\uff0c\u718a\u8170\u864e\u80cc\uff0c\u51db\u82e5\u5929\u795e\uff0c\u60f3\u5fc5\u662f\u718a\u5ef7\u5f3c\u65e0\u7591\u3002\u5353\u4e00\u822a\u5bf9\u718a\u5ef7\u5f3c\u4e45\u5df2\u94a6 \u4ef0\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u9a6c\u4e0a\u660e\u767d\u4e86\u5cb3\u545c\u73c2\u7528\u610f\uff0c\u5bf9\u7389\u7f57\u5239\u4e5f\u987f\u7136\u597d\u611f\u8d77\u6765\uff0c\u6025\u8fd0\u6b66\u5f53\u4e03\u5341\u4e8c\u624b\u8fde\u73af \u593a\u547d\u5251\u6740\u51fa\u91cd\u56f4\uff0c\u5251\u5251\u8f9b\u8fa3\uff0c\u970e\u65f6\u95f4\u4e5f\u4f24\u4e86\u51e0\u540d\u4e1c\u5382\u536b\u58eb\uff0c\u7389\u7f57\u5239\u5df2\u7136\u6740\u6765\u4f1a\u5408\u3002\u5353\u4e00\u822a\u559c \u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u539f\u6765\u4f60\u4e5f\u662f\u4e00\u7247\u5fe0\u5fc3\uff0c\u6765\u6551\u718a\u7ecf\u7565\u4e86\uff01\u201d\u7389\u7f57\u5239\u672c\u610f\u53ea\u662f\u6765\u8ffd\u7d22\u5251\u8c31\uff0c\u89c1\u5353 \u4e00\u822a\u5982\u6b64\u8a00\u8bed\uff0c\u4e5f\u4e0d\u4fbf\u7ec6\u8bf4\uff0c\u76c8\u76c8\u4e00\u7b11\uff0c\u5c06\u5f53\u524d\u4e24\u540d\u536b\u58eb\u7684\u624b\u81c2\u524a\u65ad\uff0c\u7b11\u9053\uff1a\u201c\u50bb\u5c0f\u5b50\uff0c\u5148 \u628a\u8fd9\u73ed\u4eba\u4e86\u7ed3\u518d\u8bf4\u3002\u4f60\u7684\u718a\u7ecf\u7565\u635f\u4f24\u4e0d\u4e86\uff0c\u6709\u4f60\u7684\u597d\u670b\u53cb\u4fdd\u7740\u5462\uff0c\u4f60\u62c5\u4ec0\u9ebd\u5fc3\uff1f\u201d\u8a00\u7b11\u4e4b \u95f4\uff0c\u624b\u5e95\u4e1d\u6beb\u4e0d\u7f13\uff0c\u5251\u5c16\u4e1c\u523a\u897f\u6233\uff0c\u53c8\u4f24\u4e86\u51e0\u540d\u536b\u58eb\u7684\u5173\u8282\u8981\u5bb3\uff0c\u75db\u5f97\u4ed6\u4eec\u6ee1\u5730\u6253\u6eda\uff01 \u3000\u3000\u518d\u8bf4\u767d\u77f3\u9053\u4eba\u4e00\u8154\u6012\u6c14\uff0c\u4ed7\u5251\u6025\u8ffd\uff0c\u5ffd\u89c1\u5cb3\u545c\u73c2\u5c06\u5353\u4e00\u822a\u653e\u4e0b\uff0c\u5e76\u80a9\u8fdb\u5165\u6768\u6d9f\u5b98\u90b8\uff0c\u800c \u91cc\u9762\u53c8\u4f20\u51fa\u9635\u9635  \u6740\u4e4b\u58f0\uff0c\u4e0d\u7981\u5927\u5947\uff0c\u4e0d\u77e5\u4ed6\u4eec\u6363\u4ec0\u9ebd\u9b3c\uff0c\u7565\u4e3a\u8fdf\u7591\uff0c\u4e5f\u95ef\u4e86\u5165\u53bb\u3002\u53ea\u89c1\u5353 \u4e00\u822a\u548c\u4e00\u4e2a\u5c11\u5973\uff0c\u5e76\u80a9\u8054\u5251\uff0c\u6b63\u81ea\u6740\u5f97\u70ed\u95f9\uff0c\u90a3\u5c11\u5973\u957f\u7709\u4eba\u9b13\uff0c\u79cb\u6c34\u6a2a\u6ce2\uff0c\u91d1\u73af\u675f\u53d1\uff0c\u7ea2\u7eeb \u7f20\u8155\uff0c\u7f8e  \u4e4b\u4e2d\uff0c\u900f\u7740\u4e00\u80a1\u4ee4\u4eba\u5fc3\u98a4\u7684\u6740\u6c14\uff01\u767d\u77f3\u5fc3\u5934\u4e00\u9707\uff0c\u6697\u60f3\uff1a\u8fd9\u201c\u5996\u5973\u201d\u5fc5\u662f\u7389\u7f57\u5239 \u65e0\u7591\uff01\u767d\u77f3\u9053\u4eba\u4e00\u5fc3\u60f3\u628a\u5973\u513f\u8bb8\u914d\u5e08\u4f84\uff0c\u51e0\u4e4e\u5df2\u628a\u7389\u7f57\u5239\u89c4\u4e3a\u654c\u4eba\uff0c\u9aa4\u7136\u89c1\u5230\uff0c\u53c8\u5fcc\u53c8\u6068\uff01 \u3000\u3000\u5353\u4e00\u822a\u53eb\u9053\uff1a\u201c\u5e08\u53d4\u5feb\u6765\u5440\uff0c\u718a\u7ecf\u7565\u5728\u8fd9\u91cc\u5462\uff01\u201d\u767d\u77f3\u9053\u4eba\u4e00\u53e3\u5251\u906e\u62e6\u62b9\u523a\uff0c\u62a4\u7740\u5168 \u8eab\uff0c\u5374\u5e76\u672a\u6740\u8fdb\u3002\u9163\u6218\u95f4\uff0c\u6709\u4e00\u4e2a\u8499\u9762\u6c49\u5b50\u88ab\u7389\u7f57\u5239\u5251\u5c16\u5212\u7834\u9762\u5177\uff0c\u5206\u6210\u4e24\u534a\uff0c\u843d\u5728\u5730\u4e0a\uff0c \u767d\u77f3\u9053\u4eba\u4e00\u773c\u671b\u53bb\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u559d\u9053\uff1a\u201c\u54fc\uff0c\u539f\u6765\u4f60\u5728\u8fd9\u91cc\uff0c\u53c1\u65e5\u4e4b\u671f\u6b63\u5c4a\uff0c\u6211\u5012\u8981\u770b\u4f60\u6709 \u4ec0\u9ebd\u672c\u4e8b\u8d76\u6211\u51fa\u4eac\uff1f\u201d\u5251\u5149\u970d\u970d\u5c55\u5f00\uff0c\u5411\u90a3\u4eba\u76f4\u6740\u8fc7\u53bb\u3002 \u3000\u3000\u4f60\u9053\u767d\u77f3\u9053\u4eba\u56e0\u4f55\u52a8\u6012\uff0c\u539f\u6765\u8fd9\u4eba\u6b63\u662f\u90a3\u65e5\u5728\u5929\u6865\u6697\u7b97\u4ed6\u7684\u5356\u6b66\u6c49\u5b50\u3002\u540d\u53eb\u90dd\u5efa\u660c\uff0c\u4e43 \u662f\u9634\u98ce\u6bd2\u7802\u638c\u91d1\u72ec\u5f02\u7684\u9996\u5f92\u3002\u539f\u6765\u6697\u7b97\u767d\u77f3\u9053\u4eba\uff0c\u548c\u6050\u5413\u67f3\u897f\u94ed\u9650\u4ed6\u53c1\u65e5\u4e4b\u5185\u8d76\u767d\u77f3\u51fa\u4eac\u7b49 \u4e8b\uff0c\u90fd\u662f\u5e94\u4fee\u9633\u5728\u6697\u4e2d\u6307\u4f7f\u3002 \u3000\u3000\u5e94\u4fee\u9633\u672c\u662f\u9b4f\u5fe0\u8d24\u5fc3\u8179\uff0c\u5149\u5b97\u4e00\u6b7b\uff0c\u4ed6\u4fbf\u79d8\u5bc6\u5165\u4eac\uff0c\u53c8\u7531\u4ed6\u5f15\u8fdb\u4e86\u91d1\u72ec\u5f02\u3002\u53ea\u56e0\u91d1\u72ec\u5f02 \u58f0\u540d\u592a\u574f\uff0c\u6240\u4ee5\u5728\u5bab\u4e2d\u4e5f\u662f\u9690\u7792\u8eab\u4efd\u3002\u81ea\u5cb3\u9e23\u73c2\u7b2c\u4e00\u6b21\u5927\u95f9\u7687\u5bab\u548c\u5353\u4e00\u822a\u88ab\u5149\u5b97\u4e34\u6b7b\u4e4b\u524d\u53ec \u89c1\uff0c\u8fd9\u4e24\u4ef6\u4e8b\u540c\u65e5\u53d1\u751f\u4e4b\u5f8c\uff0c\u4e1c\u5382\u4fa6\u9a91\u56db\u51fa\uff0c\u65e9\u628a\u4e24\u4eba\u7684\u8eab\u4efd\u548c\u4e0b\u843d\u63a2\u660e\u3002\u5e94\u4fee\u9633\u542c\u8bf4\u5cb3\u545c \u73c2\u662f\u718a\u7ecf\u7565\u7684\u4f7f\u8005\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u5bf9\u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u718a\u5ef7\u5f3c\u5728\u4e8c\u5341\u516b\u56de\u6765\uff0c\u5b97\u4e3b\u8981\u9664\u6389\u4ed6\uff0c\u5fc5\u5148 \u8981\u628a\u4ed6\u7684\u7fbd\u7ffc\u526a\u6389\u3002\u201d\u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u6211\u65b0\u638c\u5927\u6743\uff0c\u671d\u4e2d\u6587\u6b66\uff0c\u6700\u5c11\u6709\u4e00\u534a\u4eba\u548c\u718a\u86ee\u5b50\u540c\u4e00\u9f3b \u5b54\u51fa\u6c14\uff0c\u5982\u4f55\u53ef\u4ee5\u4e00\u9f50\u9664\u6389\uff01\u201d\u5e94\u4fee\u9633\u7b11\u9053\uff1a\u201c\u6211\u8bf4\u7684\u4e0d\u662f\u6307\u718a\u5ef7\u5f3c\u671d\u4e2d\u7684\u540c\u515a\uff0c\u800c\u662f\u6307\u53ef \u80fd\u5e2e\u52a9\u4ed6\u7684\u6c5f\u6e56\u597d\u624b\u3002\u987b\u77e5\u5b97\u4e3b\u539f\u8ba2\u7684\u8ba1\u753b\uff0c\u4e5f\u4e0d\u662f\u5728\u671d\u5ef7\u4e4b\u4e0a\u5c06\u718a\u5ef7\u5f3c\u6273\u5012\uff0c\u8010\u662f\u6697\u4e2d\u6d3e \u4eba\u5e72\u6389\u4ed6\u3002\u5982\u679c\u4ed6\u6709\u8bb8\u591a\u9ad8\u624b\u76f8\u52a9\uff0c\u4e8b\u60c5\u5c31\u4f1a\u5f04\u574f\u4e86\u3002\u201d\u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u6211\u77e5\u9053\u718a\u86ee\u5b50\u7684\u813e \u6c14\uff0c\u4ed6\u4e0d\u4f1a\u4ece\u8fbd\u4e1c\u5e26\u8bb8\u591a\u4eba\u56de\u6765\u7684\u3002  \u5cb3\u9e23\u73c2\u4e00\u4eba\uff0c\u7b97\u4ed6\u6709\u5929\u5927\u672c\u4e8b\uff0c\u4e5f\u62a4\u4e0d\u4e86\u718a\u86ee\u5b50\u3002\u201d \u5e94\u4fee\u9633\u9053\uff1a\u201c\u5cb3\u545c\u73c2\u4e00\u4eba\u56fa\u662f\u5b64\u638c\u96be\u545c\uff0c\u53ef\u662f\u90a3\u5353\u4e00\u822a\u6b63\u662f\u5cb3\u545c\u73c2\u7684\u597d\u53cb\u3002\u201d\u9b4f\u5fe0\u8d24\u9053\uff1a \u201c\u90a3\u5353\u4e00\u822a\u6b66\u529f\u5982\u4f55\uff01\u201d\u5e94\u4fee\u9633\u9053\uff1a\u201c\u90a3\u5353\u4e00\u822a\u7684\u6b66\u529f\u867d\u7136\u6bd4\u4e0d\u4e0a\u5cb3\u9e23\u73c2\uff0c\u53ef\u662f\u4ed6\u662f\u6b66\u5f53\u6d3e \u7684\u638c\u95e8\u5f1f\u5b50\uff0c\u6211\u4eec\u63a2\u5f97\u4ed6\u8fd9\u6b21\u6765\u4eac\uff0c\u4e5f\u662f\u548c\u4e00\u4e2a\u5e08\u53d4\u540c\u6765\u7684\u3002\u5728\u5317\u4eac\u7684\u6b66\u5f53\u6d3e\u9ad8\u624b\u5c31\u6709\u5341\u591a \u4e2a\u4eba\u3002\u201d\u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u90a3\u9ebd\u5c31\u628a\u4ed6\u4eec\u4e00\u9f50\u5e72\u6389\u5427\uff01\u201d\u5e94\u4fee\u9633\u9053\uff1a\u201c\u4e0d\u884c\u54df\uff0c\u5b97\u4e3b\u3002\u5f53\u4eca\u6c5f\u6e56 \u4e4b\u4e0a\uff0c\u6b66\u5f53\u6d3e\u58f0\u5a01\u6700\u76db\uff0c\u53c8\u559c\u4ed6\u4eec\u4e00\u5411\u4e0d\u7406\u671d\u653f\uff0c\u6211\u4eec\u548c\u4ed6\u4eec\u4e95\u6c34\u4e0d\u72af\u6cb3\u6c34\uff0c\u90a3\u5012\u53ef\u76f8\u5b89\u65e0 \u4e8b\uff0c\u82e5\u7136\u628a\u4ed6\u4eec\u6d3e\u4e2d\u7684\u957f\u8001\u548c\u638c\u95e8\u5e72\u4e86\uff0c\u5c82\u4e0d\u662f\u51ed\u7a7a\u6811\u4e86\u4e00\u4e2a\u52b2\u654c\u3002\u201d\u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u6c5f\u6e56\u4e4b \u4e8b\u6211\u4e0d\u5982\u4f60\u719f\u6089\uff0c\u4f9d\u4f60\u8bf4\u8be5\u600e\u9ebd\u529e\uff1f\u201d\u5e94\u4fee\u9633\u9053\uff1a\u201c\u4e0d\u5982\u6d3e\u4eba\u6697\u7b97\u90a3\u4e2a\u9053\u58eb\u53eb\u4ed6\u5403\u70b9\u5c0f\u7f16 \u5934\uff0c\u7136\u5f8c\u6050\u5413\u4ed6\u548c\u6536\u7559\u4ed6\u7684\u90a3\u4e2a\u5c45\u505c\u4e3b\u4eba\uff0c\u9650\u4ed6\u53c1\u5929\u4e4b\u5185\u79bb\u4eac\u3002\u793a\u610f\u6211\u4eec\u53c1\u5929\u4e4b\u5185\uff0c\u5fc5\u5230\u4ed6 \u7684\u4f4f\u5740\u5bfb\u4e8b\u3002\u6211\u77e5\u9053\u90a3\u9053\u58eb\u7d20\u6765\u5f3a\u9879\uff0c\u4e00\u5b9a\u4e0d\u80af\u79bb\u4eac\u3002\u5728\u53c1\u65e5\u7684\u671f\u9650\u5185\uff0c\u5fc5\u5b9a\u9080\u9f50\u4ed6\u7684\u672c\u6d3e \u5f1f\u5b50\uff0c\u5728\u5bb6\u4e2d\u7b49\u5019\u6211\u4eec\u3002\u5176\u5b9e\u6211\u4eec\u5e76\u4e0d\u662f\u5411\u4ed6\u4eec\u5bfb\u4e8b\uff0c\u53ea\u662f\u9632\u5907\u4ed6\u4eec\u53bb\u548c\u5cb3\u545c\u73c2\u4f1a\u5408\uff0c\u53eb\u6211 \u4eec\u96be\u65bc\u5411\u718a\u5ef7\u5f3c\u4e0b\u624b\u7f62\u4e86\u3002\u201d\u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u8fd9\u6b63\u662f\u58f0\u4e1c\u51fb\u897f\u4e4b\u8ba1\uff0c\u5c31\u8fd9\u6837\u529e\u5427\uff01\u201d \u3000\u3000\u53ef\u7b11\u767d\u77f3\u9053\u4eba\u61f5\u7136\u4e0d\u77e5\uff0c\u505a\u68a6\u4e5f\u6599\u4e0d\u5230\u5176\u4e2d\u85cf\u7740\u8fd9\u6837\u5927\u7684\u9634\u8c0b\uff1f \u3000\u3000\u5176\u5b9e\u767d\u77f3\u9053\u4eba\u4e5f\u4e0d\u662f\u6709\u5fc3\u76f8\u52a9\u5cb3\u545c\u73c2\uff0c\u90a3\u201c\u58f0\u4e1c\u51fb\u897f\u201d\u4e4b\u8ba1\u53ea\u662f\u5e94\u4fee\u9633\u9632\u60a3\u672a\u7136\uff0c\u62c5\u5fc3 \u4ed6\u4eec\u4f1a\u5408\u6210\u4e00\u8def\uff0c\u6240\u4ee5\u8bbe\u8ba1\u5c06\u4ed6\u4eec\u9694\u5f00\u800c\u5df2\u3002 \u3000\u3000\u5c82\u77e5\u8fd9\u6837\u4e00\u6765\uff0c\u53cd\u5f15\u8d77\u4e86\u5cb3\u545c\u73c2\u7684\u7591\u5fc3\uff0c\u5728\u7d27\u6025\u4e4b\u9645\uff0c\u9661\u7136\u60f3\u8d77\u90a3\u53c1\u65e5\u7684\u671f\u9650\uff0c\u731c\u7834\u4e86 \u654c\u4eba\u7684\u7528\u610f\u3002\u56e0\u6b64\u4e5f\u4fbf\u5c06\u8ba1\u5c31\u8ba1\uff0c\u7528\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6bb5\uff0c\u5c06\u5353\u4e00\u822a\u52ab\u8d70\uff0c\u5f15\u5f97\u767d\u77f3\u9053\u4eba\u548c \u6b66\u5f53\u6d3e\u5f1f\u5b50\u5927\u4e3e\u8ffd\u6765\uff01 \u3000\u3000\u8fd9\u65f6\u718a\u5ef7\u5f3c\u4e4b\u56f4\u60ed\u89e3\uff0c\u91d1\u72ec\u5f02\u89c1\u767d\u77f3\u9053\u4eba\u4e00\u6765\uff0c\u60c5\u77e5\u6b66\u5f53\u6d3e\u5fc5\u5927\u4e3e\u800c\u81f3\uff0c\u614c\u4e86\u624b\u811a\uff0c\u53eb \u9053\uff1a\u201c\u98ce\u7d27\uff0c\u626f\u547c\uff01\u201d\u94c1\u98de\u9f99\u4e00\u638c\u6363\u51fa\uff0c\u62e6\u7740\u53bb\u8def\uff0c\u6155\u5bb9\u51b2\u6a2a\u51fb\u4e00\u638c\uff0c\u5c06\u94c1\u98de\u9f99\u7684\u62db\u6570\u7834 \u5f00\uff0c\u628a\u624b\u4e00\u6325\uff0c\u6b63\u60f3\u62db\u547c\u540c\u4f34\u64a4\u8d70\uff0c\u5916\u9762\u67f3\u897f\u94ed\u6b66\u5e08\u548c\u6b66\u5f53\u5f1f\u5b50\u5df2\u7136\u8d76\u81f3\uff0c\u767d\u77f3\u9053\u4eba\u4e0d\u77e5\u654c \u4eba\u4e43\u662f\u4e1c\u5382\u536b\u58eb\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u628a\u4ed6\u4eec\u622a\u4f4f\uff01\u201d \u3000\u3000\u8fd9\u4e00\u6765\u4f18\u52a3\u52bf\u6613\uff0c\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\u52a0\u4e0a\u67f3\u897f\u94ed\u8bf7\u6765\u52a9\u62f3\u7684\u597d\u624b\uff0c\u4e0d\u4e0b\u4e8c\u53c1\u5341\u4eba\uff0c\u987f\u65f6\u53cd\u5ba2 \u4e3a\u4e3b\uff0c\u628a\u4e1c\u5382\u536b\u58eb\u56f4\u4e86\u8d77\u6765\uff0c\u5251\u5f71\u5200\u5149\uff0c\u6ee1\u5ead\u98d8\u77a5\uff0c\u91d1\u72ec\u5f02\u548c\u6155\u5bb9\u51b2\u5e76\u80a9\u51b2\u51fa\uff0c\u88ab\u767d\u77f3\u9053\u4eba \u548c\u67f3\u897f\u94ed\u4e00\u622a\uff0c\u9694\u4e86\u5f00\u6765\u3002\u7389\u7f57\u5239\u4e00\u58f0\u957f\u7b11\uff0c\u957f\u5251\u5bd2\u5149\u95ea\u95ea\uff0c\u970d\u5730\u5377\u6765\uff0c\u91d1\u72ec\u5f02\u8fd0\u638c\u6210\u98ce\uff0c \u6321\u4e86\u51e0\u62db\u3002\u5cb3\u9e23\u73c2\u5237\u7684\u4e00\u5251\u523a\u5230\uff0c\u91d1\u72ec\u5f02\u53cd\u624b\u4e00\u638c\uff0c\u5cb3\u9e23\u73c2\u5de6\u638c\u4e00\u6321\uff0c\u53f3\u624b\u957f\u5251\u5212\u4e86\u534a\u4e2a\u5706 \u5f27\uff0c\u55e4\u7684\u4e00\u58f0\uff0c\u5c06\u91d1\u72ec\u5f02\u4e0a\u8863\u523a\u7834\uff0c\u7389\u7f57\u5239\u51fa\u624b\u5982\u98ce\uff0c\u4e00\u62db\u201c\u6d41\u661f\u75be\u9a76\u201d\uff0c\u70b9\u5411\u91d1\u72ec\u5f02\u5fc3 \u7a9d\uff0c\u91d1\u72ec\u5f02\u4fa7\u8eab\u4e00\u95ea\uff0c\u53ea\u542c\u5f97\u7389\u7f57\u5239\u559d\u58f0\u201c\u7740\uff01\u201d\u5251\u5c16\u4e00\u98a4\uff0c\u9c9c\u8840\u98de\u6e85\uff0c\u5728\u91d1\u72ec\u5f02\u80f8\u4e0a\u5212\u4e86 \u4e00\u9053\u65e5\u5b50\u3002\u672c\u6765\u82e5\u8bba\u6b66\u529f\uff0c\u91d1\u72ec\u5f02\u7edd\u4e0d\u5728\u7389\u7f57\u5239\u4e0e\u5cb3\u9e23\u73c2\u4e4b\u4e0b\uff0c\u4f46\u5cb3\u9e23\u73c2\u6234\u4e86\u91d1\u4e1d\u624b\u5957\uff0c\u4e0d \u6015\u6bd2\u4f24\uff0c\u5a01\u529b\u65e0\u5f62\u589e\u4e86\u51e0\u5206\uff0c\u66f4\u52a0\u4e0a\u7389\u7f57\u5239\u5251\u6cd5\u51f6\u6b8b\u65e0\u6bd4\uff0c\u91d1\u72ec\u5f02\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f\u6321\u4e0d\u4f4f\u4e24\u4eba \u5408\u51fb\uff0c\u8fd8\u5e78\u4ed6\u95ea\u907f\u5f97\u5feb\uff0c\u8981\u4e0d\u7136\u8fd9\u4e00\u5251\u4fbf\u662f\u5f00\u819b\u7834\u8179\u4e4b\u707e\uff01 \u3000\u3000\u7389\u7f57\u5239\u4e00\u62db\u5f97\u624b\uff0c\u5251\u5149\u6eda\u6eda\u800c\u4e0a\uff0c\u6155\u5bb9\u51b2\u89c1\u52bf\u5371\u6025\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u75be\u53d1\u51e0\u62db\uff0c\u970e\u773c\u4e4b\u95f4\u628a \u53c1\u540d\u6b66\u5f53\u6d3e\u5f1f\u5b50\u6253\u7ffb\u5730\u4e0a\uff0c\u5cb3\u545c\u73c2\u89c1\u91d1\u72ec\u5f02\u5df2\u53d7\u4e86\u4f24\uff0c\u6599\u4ed6\u4e0d\u662f\u7389\u7f57\u5239\u5bf9\u624b\uff0c\u5206\u51fa\u8eab\u6765\uff0c\u957f \u5251\u4e00\u7ffb\uff0c\u6321\u7740\u4e86\u6155\u5bb9\u51b2\u53bb\u8def\uff1f \u3000\u3000\u7389\u7f57\u5239\u8fde\u73af\u51e0\u5251\uff0c\u628a\u91d1\u72ec\u5f02\u8feb\u5f97\u8fde\u8fde\u5f8c\u9000\uff0c\u7b11\u9053\uff1a\u201c\u91d1\u8001\u602a\uff0c\u4f60\u8fd8\u4e0d\u628a\u6211\u7684\u5251\u8c31\u8fd8 \u6765\uff01\u201d\u91d1\u72ec\u5f02\u8fd0\u6c14\u5fa1\u4f24\uff0c\u54ac\u7259\u6b7b\u6218\uff0c\u7389\u7f57\u5239\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u518d\u4e0d\u62ff\u51fa\u6765\uff0c\u6211\u53ef\u8981\u4e0b\u624b\u6740\u4e86\uff01\u201d \u5c31\u5728\u76c8\u76c8\u7b11\u8bed\u4e4b\u4e2d\uff0c\u5251\u62db\u6025\u5982\u66b4\u98ce\u9aa4\u96e8\uff0c\u628a\u91d1\u72ec\u5f02\u88f9\u5728\u5251\u5149\u4e4b\u4e2d\uff01 \u3000\u3000\u6b63\u6df7\u6218\u95f4\uff0c\u95e8\u5916\u4eba\u9a6c\u58f0\u55a7\uff0c\u5ffd\u7136\u6d8c\u8fdb\u4e86\u4e00\u961f\u5b98\u5175\uff0c\u4e3a\u9996\u7684\u5c06\u9886\u5927\u53eb\u9053\uff1a\u201c\u718a\u7ecf\u7565\uff0c\u5351\u804c \u6765\u8fdf\u4e86\uff01\u201d\u53c8\u559d\u9053\uff1a\u201c\u597d\u5927\u80c6\u7684\u8d3c\u4eba\uff0c\u767d\u65e5\u9752\u5929\uff0c\u6253\u52ab\u5b98\u5bb6\uff0c\u8fd8\u4e0d\u7ed9\u6211\u7f34\u68b0\u6ca1\u964d\uff01\u201d\u6765\u7684\u6b63 \u662f\u4e5d\u95e8\u63d0\u7763\u7530\u5c14\u8015\uff0c\u5175\u4e01\u4e00\u6446\u4e0a\u524d\uff0c\u5200\u67aa\u4e71\u65ab\uff0c\u718a\u5ef7\u5f3c\u53eb\u9053\uff1a\u201c\u6211\u4eec\u7684\u4eba\u9000\u4e0b\uff01\u201d\u7389\u7f57\u5239\u6b63 \u5c06\u5f97\u624b\uff0c\u88ab\u5b98\u5175\u4e00\u51b2\uff0c\u91d1\u72ec\u5f02\u4e58\u673a\u5728\u4eba\u4e1b\u4e2d\u9003\u51fa\uff0c\u7389\u7f57\u5239\u5927\u6012\uff0c\u624b\u4e2d\u5b9d\u5251\u56db\u4e0b\u4e00\u6c64\uff0c\u628a\u5b98\u519b \u7684\u5200\u77db\u67aa\u6222\uff0c\u6216\u9707\u98de\u534a\u7a7a\uff0c\u6216\u622a\u65ad\u5730\u4e0a\u3002\u5b98\u519b\u5927\u53eb\u9053\uff1a\u201c\u597d\u5389\u5bb3\u7684\u5973\u8d3c\u554a\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u5927\u6012\uff0c\u800c\u4e0a\u73b0\u51fa\u51b7\u51b7\u7684\u7b11\u5bb9\uff0c\u94c1\u98de\u9f99\u6025\u5fd9\u53eb\u9053\uff1a\u201c\u4f7f\u4e0d\u5f97\uff1f\u201d\u62c9\u5979\u9000\u4e0b\u3002\u5cb3\u9e23\u73c2 \u4e5f\u62db\u547c\u5b98\u519b\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u4fdd\u62a4\u7ecf\u7565\u5927\u4eba\u7684\u4fa0\u5973\uff0c\u4e0d\u53ef\u52a8\u624b\u3002\u201d \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u90a3\u4e9b\u53d7\u4f24\u5012\u5730\u7684\u4e1c\u5382\u536b\u58eb\u5168\u88ab\u5b98\u5175\u7ed1\u8d77\uff0c\u53ef\u662f\u6155\u5bb9\u51b2\u8fd9\u4e00\u73ed\u4eba\u5374\u90fd\u8d81\u6df7\u4e71\u4e2d \u9003\u4e86\u3002\u4e5d\u95e8\u63d0\u7763\u7530\u5c14\u8015\u4e0a\u524d\u53c2\u89c1\u718a\u5ef7\u5f3c\uff0c\u8eac\u8170\u8bf4\u9053\uff1a\u201c\u8bf7\u6055\u5351\u804c\u6765\u8fdf\uff0c\u7d2f\u5927\u4eba\u53d7\u4e86\u865a\u60ca\u3002\u201d \u5175\u79d1\u7ed9\u4e8b\u4e2d\u6768\u6d9f\u5df2\u4ece\u5185\u5802\u8d70\u51fa\uff0c\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u7530\u5927\u4eba\u8fd9\u6b21\u7684\u6d88\u606f\u5012\u7075\u901a\u5f97\u5f88 \u5440\uff01\u201d\u7530\u5c14\u8015\u800c\u4e0a\u4e00\u7ea2\uff0c\u5450\u5450\u8bf4\u9053\uff1a\u201c\u5927\u4eba\u5bb6\u4e2d\u8fde\u53d7\u4e24\u6b21\u8d3c\u52ab\uff0c\u5351\u804c\u7f6a\u5f53\u4e07\u6b7b\uff01\u201d\u6768\u6d9f\u9053\uff1a \u201c\u4eac\u57ce\u4e4b\u5185\uff0c\u5c45\u7136\u6709\u8fd9\u6837\u7316\u7357\u7684\u532a\u5f92\uff0c\u6211\u770b\u53ea\u6015\u4e0d\u662f\u5bfb\u5e38\u7684\u76d7\u8d3c\u5427\uff01\u201d\u7530\u5c14\u8015\u9053\uff1a\u201c\u5351\u804c\u5e26 \u4ed6\u4eec\u56de\u53bb\uff0c\u9a6c\u4e0a\u4e25\u5211\u8baf\u95ee\u3002\u201d\u5cb3\u9e23\u73c2\u53cc\u773c\u4e00\u7ffb\uff0c\u9053\uff1a\u201c\u8fd9\u73ed\u5f3a\u76d7\u6765\u5934\u5f88\u5927\uff0c\u53ea\u6015\u5927\u4eba\u4e0d\u4fbf\u5ba1 \u95ee\u3002\u201d\u8f6c\u8eab\u5bf9\u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u545c\u73c2\u6597\u80c6\u8bf7\u7ecf\u7565\u5927\u4eba\u4eb2\u81ea\u5ba1\u95ee\u3002\u201d\u7530\u5c14\u8015\u6025\u9053\uff1a\u201c\u5351\u804c\u804c\u8d23\u6538 \u5173\uff0c\u4e0d\u6562\u52b3\u70e6\u7ecf\u7565\u5927\u4eba\u3002\u201d\u718a\u5ef7\u5f3c\u53cc\u7738\u70af\u70af\uff0c\u626b\u4e86\u7530\u752d\u8015\u4e00\u773c\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5ffd\u6325\u624b\u9053\uff1a \u201c\u597d\uff0c\u4f60\u5e26\u53bb\u5427\uff01\u201d \u3000\u3000\u7530\u5c14\u8015\u6536\u961f\u8d70\u5f8c\uff0c\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u5927\u4eba\uff0c\u4f60\u8fd9\u5c82\u4e0d\u662f\u7eb5\u864e\u5f52\u5c71\uff1f\u201d\u6768\u6d9f\u4e5f\u9053\uff1a\u201c\u7530\u5c14\u8015\u8fd9 \u5c0f\u5b50\uff0c\u6211\u5c31\u4fe1\u4ed6\u4e0d\u8fc7\uff01\u201d\u718a\u5ef7\u5f3c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u5c82\u4e0d\u77e5\u8fd9\u73ed\u5f3a\u76d7\u5fc5\u975e\u5bfb\u5e38\uff0c\u4f46\u6211\u662f\u5728\u5916\u7edf\u5175 \u7684\u5c06\u9886\uff0c\u4ed6\u662f\u8d1f\u8d23\u4eac\u5e08\u6cbb\u5b89\u7684\u63d0\u7763\uff0c\u5404\u6709\u804c\u6743\u3002\u671d\u4e2d\u5df2\u6709\u4eba\u8bf4\u6211\u4e13\u6743\u64c5\u65ad\uff0c\u6211\u53c8\u600e\u597d\u8d8a\u4fce\u4ee3 \u5e96\uff01\u201d\u6768\u6d9f\u9eef\u7136\u65e0\u8bed\u3002\u718a\u5ef7\u5f3c\u5927\u58f0\u9053\uff1a\u201c\u545c\u73c2\uff0c\u4f60\u8bf7\u4f17\u4f4d\u4e49\u58eb\u4e0a\u5750\uff0c\u5f85\u6211\u4e00\u4e00\u62dc\u8c22\u3002\u201d\u7389\u7f57 \u5239\u4e0e\u94c1\u98de\u9f99\u8d8a\u4f17\u800c\u51fa\uff0c\u5bf9\u718a\u5ef7\u5f3c\u4f5c\u4e86\u4e00\u63d6\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u662f\u8bef\u6253\u8bef\u649e\u800c\u6765\uff0c\u4e0d\u6562\u9886 \u8c22\uff01\u201d\u718a\u5ef7\u5f3c\u4e00\u6014\uff0c\u94c1\u98de\u9f99\u9053\uff1a\u201c\u718a\u5927\u4eba\u8d64\u5fc3\u4e3a\u56fd\uff0c\u5c0f\u4eba\u4f69\u670d\u5f97\u7d27\uff0c\u4f46\u4ffa\u7236\u5973\u4e43\u662f\u5c71\u91ce\u8349 \u6c11\uff0c\u7d20\u4e0d\u6562\u6cbd\u5b98\u8fd1\u5e9c\uff0c\u4eca\u65e5\u4e5f\u4e0d\u8fc7\u662f\u65e0\u5fc3\u76f8\u9047\uff0c\u8c08\u4e0d\u4e0a\u6709\u4ec0\u9ebd\u529f\u52b3\u3002\u7ecf\u7565\u6055\u7f6a\uff0c\u6211\u4eec\u544a\u8f9e \u4e86\uff01\u201d\u718a\u5ef7\u5f3c\u4ecd\u7136\u65bd\u4e86\u4e00\u793c\uff0c\u9053\uff1a\u201c\u9e23\u73c2\uff0c\u66ff\u6211\u9001\u5ba2\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u624b\u4e2d\u7684\u5b9d\u5251\u5c1a\u672a\u5f52\u9798\uff0c\u5cb3\u9e23\u73c2\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u53ef\u4e0d\u6b63\u662f\u81ea\u5df1\u5931\u5728\u5bab\u4e2d\u7684\u90a3\u628a\u6e38\u9f99 \u5b9d\u5251\uff01\u8fd9\u4e00\u6765\u731b\u7136\u9192\u8d77\uff0c\u90a3\u4e00\u665a\u548c\u81ea\u5df1\u540c\u65f6\u95ef\u8fdb\u6df1\u5bab\u7684\u9ed1\u5f71\uff0c\u5fc5\u7136\u662f\u7389\u7f57\u5239\u65e0\u7591\u3002\u7389\u7f57\u5239\u7f13 \u7f13\u7684\u628a\u9ec4\u5251\u63d2\u5165\u9798\u4e2d\uff0c\u5f97\u610f\u5fae\u7b11\u3002\u5cb3\u545c\u73c2\u9001\u81f3\u9636\u4e0b\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\uff0c\u6211\u6709\u4e00\u6837\u4e1c\u897f\u8981 \u9001\u56de\u7ed9\u4f60\u3002\u201d\u4ece\u6000\u4e2d\u53d6\u51fa\u5251\u8c31\uff0c\u9053\uff1a\u201c\u8bf7\u7ec3\u5973\u4fa0\u68c0\u89c4\uff0c\u8fd9\u662f\u4e0d\u662f\u539f\u7269\uff1f\u201d \u3000\u3000\u7389\u7f57\u5239\u6de1\u6de1\u4e00\u7b11\uff0c\u5c06\u5251\u8c31\u63a5\u8fc7\uff0c\u94c1\u98de\u9f99\u5927\u4e3a\u60ca\u5947\uff0c\u9053\uff1a\u201c\u6211\u7236\u5973\u4e3a\u4e86\u8fd9\u4e2a\u5251\u8c31\uff0c\u4e07\u91cc\u5954 \u6ce2\uff0c\u4f60\u4ece\u90a3\u91cc\u5f97\u6765\u7684\uff1f\u201d\u5cb3\u9e23\u73c2\u6b63\u60f3\u56de\u7b54\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u4e5f\u6709\u4e00\u6837\u4e1c\u897f\u8fd8\u4f60\uff01\u201d\u628a\u6e38\u9f99\u5251 \u89e3\u4e86\u4e0b\u6765\uff0c\u4ea4\u56de\u7ed9\u5cb3\u9e23\u73c2\uff0c\u5927\u7b11\u8bf4\u9053\uff1a\u201c\u4e00\u7269\u6362\u4e00\u7269\uff0c\u54b1\u4eec\u8c01\u4e5f\u4e0d\u5fc5\u9886\u60c5\uff01\u201d\u94c1\u98de\u9f99\u6014\u4e86\u4e00 \u6014\uff0c\u5fc3\u9053\uff1a\u8fd9\u5b69\u5b50\u771f\u662f\u597d\u5f3a\u3002 \u3000\u3000\u7389\u7f57\u5239\u6b65\u4e0b\u53f0\u9636\uff0c\u5ffd\u56de\u5934\u62db\u624b\uff0c\u53eb\u9053\uff1a\u201c\u5353\u4e00\u822a\uff0c\u4f60\u8fc7\u6765\uff01\u201d\u5353\u4e00\u822a\u5446\u5446\u7684\u6df7\u5728\u4eba\u4e1b\u4e4b \u4e2d\uff0c\u95fb\u8a00\u5982\u53d7\u547d\u4ee4\uff0c\u4e0d\u7531\u81ea\u5df2\u7684\u8d70\u4e86\u51fa\u53bb\uff0c\u767d\u77f3\u9053\u4eba\u5411\u4ed6\u77aa\u773c\uff0c\u4ed6\u4e5f\u6d51\u5982\u672a\u89c9\u3002 \u3000\u3000\u5353\u4e00\u822a\u6b65\u4e0b\u53f0\u9636\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u597d\u554a\uff1f\u201d\u5353\u4e00\u822a\u5c1a\u672a\u5f00\u58f0\uff0c\u767d\u77f3\u9053\u4eba\u8ddf\u5728\u5f8c\u9762\uff0c\u5ffd\u63d2 \u53e3\u9053\uff1a\u201c\u6709\u4ec0\u9ebd\u4e0d\u597d\uff01\u201d\u7389\u7f57\u5239\u4fcf\u773c\u4e00\u7ffb\uff0c\u5353\u4e00\u822a\u5fd9\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u56db\u5e08\u53d4\u3002\u201d\u7389\u7f57\u5239\u51b7\u7b11 \u9053\uff1a\u201c\u6211\u751f\u5e73\u6700\u4e0d\u559c\u6b22\u522b\u4eba\u591a\u5634\u3002\u5582\uff0c\u5353\u4e00\u822a\uff0c\u6211\u662f\u95ee\u4f60\u7684\u8bdd\u3002\u201d\u767d\u77f3\u9053\u4eba\u8fd9\u4e00\u6c14\u975e\u540c\u5c0f \u9119\uff0c\u624b\u6478\u5251\u628a\uff0c\u5353\u4e00\u822a\u5fd9\u9053\uff1a\u201c\u6211\u5f88\u597d\uff0c\u4f60\u548c\u94c1\u8001\u524d\u8f88\u4f4f\u5728\u90a3\u513f\uff0c\u6539\u65e5\u6211\u53bb\u62dc\u5019\u3002\u201d\u767d\u77f3 \u9053\uff1a\u201c\u4e00\u822a\uff0c\u8fd9\u91cc\u4e8b\u60c5\u5df2\u4e86\uff0c\u4f60\u660e\u65e5\u5c31\u548c\u6211\u56de\u5c71\u3002\u201d\u7389\u7f57\u5239\u51b7\u51b7\u5730\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4eba\u771f\u662f\u4f60 \u7684\u5e08\u53d4\uff1f\u201d\u767d\u77f3\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u8bdd\u662f\u4ec0\u9ebd\u610f\u601d\uff1f\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u6211\u770b\u4f60\u5012\u50cf\u4ed6\u7684\u7236\u4eb2\uff0c\u7236\u4eb2 \u7ba1\u513f\u5b50\u90fd\u6ca1\u6709\u8fd9\u9ebd\u4e25\uff01\u201d\u767d\u77f3\u9053\u4eba\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u677f\u9762\u5bf9\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u4eec\u6b66\u5f53\u6d3e\u7684\u95e8\u89c4\uff0c \u53ef\u4e0d\u8bb8\u548c\u532a\u4eba\u6765\u5f80\u3002\u201d\u7389\u7f57\u5239\u641c\u7684\u4e00\u58f0\u62d4\u51fa\u4f69\u5251\uff0c\u9053\uff1a\u201c\u767d\u77f3\u9053\u4eba\uff0c\u4f60\u4eec\u6b66\u5f53\u6d3e\u7684\u4eba\uff0c\u6211\u4e5f \u7ed3\u8bc6\u4e86\u4e0d\u5c11\uff0c\u9664\u4e86\u7d2b\u9633\u9053\u957f\u4e4b\u5916\uff0c\u4e5f\u5e76\u672a\u542c\u8bf4\u8fc7\u90a3\u4f4d\u771f\u591f\u5f97\u4e0a\u4fa0\u4e49\u4e4b\u540d\u3002\u6211\u95ee\u4f60\uff0c\u4f60\u505a\u8fc7\u4ec0 \u9ebd\u4ee4\u4eba\u94a6\u670d\u4e4b\u4e8b\uff1f\u4f60\u6562\u770b\u4e0d\u8d77\u7eff\u6797\u9053\u7684\u597d\u6c49\uff1f\u54fc\uff0c\u6211\u5c31\u662f\u4f60\u4eec\u6b63\u6d3e\u76ee\u4e3a\u532a\u4eba\u7684\u4eba\uff0c\u54b1\u4eec\u6bd4\u5212 \u6bd4\u5212\uff01\u201d\u767d\u77f3\u9053\u4eba\u6599\u4e0d\u5230\u5979\u7684\u8bdd\u950b\u5982\u6b64\u5c16\u9510\uff0c\u6da8\u7ea2\u4e86\u8138\uff0c\u641c\u7684\u4e00\u58f0\uff0c\u4e5f\u62d4\u51fa\u5251\u6765\u3002\u5353\u4e00\u822a\u614c \u4e86\u624b\u811a\uff0c\u5fd9\u9053\uff1a\u201c\u5728\u718a\u7ecf\u7565\u9762\u524d\uff0c\u4e0d\u53ef\u5931\u4eea\uff01\u201d\u767d\u77f3\u9053\uff1a\u201c\u660e\u65e5\u5348\u65f6\uff0c\u6211\u5728\u79d8\u9b54\u5d16\u5019\u6559\uff01\u201d \u5353\u4e00\u822a\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u4f60\u4e0d\u662f\u8bf4\u660e\u65e5\u56de\u5c71\u9ebd\uff1f\u201d\u767d\u77f3\u6c14\u547c\u547c\u7684\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u7ba1\u3002\u201d\u7389\u7f57\u5239\u4e00\u7b11 \u9053\uff1a\u201c\u6211\u51c6\u9075\u547d\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u4e0e\u767d\u77f3\u9053\u4eba\u6597\u53e3\u4e4b\u65f6\uff0c\u94c1\u98de\u9f99\u5374\u628a\u5cb3\u545c\u73c2\u62c9\u8fc7\u4e00\u8fb9\uff0c\u95ee\u957f\u95ee\u77ed\uff0c\u5148\u95ee\u4ed6\u7684\u59d3\u540d\uff0c \u5f8c\u95ee\u4ed6\u7684\u5bb6\u4e16\u5e08\u627f\u3002\u5cb3\u545c\u73c2\u4e0d\u77e5\u4ed6\u4fbf\u662f\u94c1\u73ca\u745a\u7684\u7236\u4eb2\uff0c\u5fc3\u4e2d\u9887\u4e3a\u8be7\u5f02\u3002\u6697\u9053\uff1a\u770b\u4ed6\u521a\u624d\u95ef\u95e8 \u6253\u6597\uff0c\u96c4\u98ce\u4e07\u4e08\uff0c\u5e94\u8be5\u662f\u4e2a\u8c6a\u8fc8\u7684\u8001\u82f1\u96c4\uff0c\u4e3a\u4f55\u5374\u8fd9\u6837\u5a46\u5a46\u5988\u5988\u3002\u597d\u51e0\u6b21\u60f3\u8bf7\u6559\u4ed6\u7684\u59d3\u540d\uff0c \u4f46\u94c1\u98de\u9f99\u95ee\u4e2a\u4e0d\u4f11\uff0c\u5cb3\u9e23\u73c2\u7adf\u6ca1\u673a\u4f1a\u63d2\u53e3\u3002\u597d\u5bb9\u6613\u7b49\u5230\u7389\u7f57\u5239\u4e0e\u767d\u77f3\u9053\u4eba\u95f9\u5b8c\u4e4b\u5f8c\uff0c\u7389\u7f57\u5239 \u9053\uff1a\u201c\u7239\uff0c\u54b1\u4eec\u8d70\uff01\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u5cb3\u5144\uff0c\u4eca\u665a\u65e0\u8bba\u5982\u4f55\uff0c\u8bf7\u5230\u897f\u5c71\u7075\u5b89\u5bfa\u4e00\u53d9\u3002\u201d\u5353\u4e00\u822a \u8fc7\u6765\uff0c\u884c\u4e86\u4e00\u793c\uff0c\u606d\u606d\u656c\u656c\u95ee\u9053\uff1a\u201c\u94c1\u8001\u524d\u8f88\uff0c\u4f60\u597d\uff1f\u201d\u5cb3\u9e23\u73c2\u500f\u7136\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u8001\u524d\u8f88\u662f \u5a01\u9707\u897f\u5317\u7684\u2026\u2026\u201d\u94c1\u98de\u9f99\u622a\u7740\u8bf4\u9053\uff1a\u201c\u8001\u673d\u6b63\u662f\u94c1\u98de\u9f99\u3002\u201d\u5cb3\u545c\u73c2\u5450\u5450\u8bf4\u9053\uff1a\u201c\u73ca\u2026\u2026\u73ca \u745a\u2026\u2026\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u73ca\u745a\u6b63\u662f\u5c0f\u5973\u3002\u201d\u5cb3\u9e23\u73c2\u6b63\u5f85\u628a\u73ca\u745a\u5931\u8e2a\u4e4b\u4e8b\u544a\u4ed6\uff0c\u7389\u7f57\u5239\u5df2\u62c9\u7740\u94c1 \u98de\u9f99\u8d70\u51fa\u5927\u95e8\u3002 \u3000\u3000\u5353\u4e00\u822a\u5401\u4e86\u53e3\u6c14\uff0c\u767d\u77f3\u9053\u4eba\u72b9\u81ea\u6c14\u6124\u96be\u5e73\uff0c\u8d70\u56de\u5927\u5802\uff0c\u5411\u718a\u5ef7\u5f3c\u544a\u8f9e\u3002\u718a\u5ef7\u5f3c\u77e5\u9053\u4ed6\u662f \u6b66\u5f53\u4e94\u8001\u4e4b\u4e00\uff0c\u597d\u751f\u656c\u91cd\uff0c\u4eb2\u81ea\u9001\u4ed6\u8d70\u4e0b\u53f0\u9636\u3002\u767d\u77f3\u9053\u4eba\u4e00\u8d70\uff0c\u6b66\u5f53\u4f17\u5f1f\u5b50\u4e5f\u968f\u7740\u8d70\u4e86\u3002\u63a5 \u7740\u662f\u67f3\u897f\u94ed\u548c\u4e00\u4f17\u6b66\u5e08\u544a\u8f9e\uff0c\u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u4e45\u95fb\u4eac\u4e2d\u67f3\u4e49\u58eb\u5927\u540d\uff0c\u4eca\u65e5\u5e78\u4f1a\uff0c\u4f55\u4e0d\u591a\u5750\u4e00 \u4f1a\u3002\u201d\u67f3\u897f\u94ed\u9053\uff1a\u201c\u4eca\u65e5\u8fd9\u73ed\u8d3c\u4eba\uff0c\u663e\u7136\u4e0d\u662f\u4e3a\u4e86\u94b1\u8d22\u800c\u6765\uff0c\u5927\u5e05\u4e0d\u53ef\u4e0d\u9632\u3002\u201d\u718a\u5ef7\u5f3c\u9053\uff1a \u201c\u6211\u8eab\u7ecf\u767e\u6218\uff0c\u9669\u6b7b\u8005\u6570\u5341\u56de\uff0c\u6b7b\u751f\u6709\u547d\uff0c\u6211\u4e5f\u53ea\u6709\u542c\u5176\u81ea\u7136\u4e86\u3002\u201d\u67f3\u897f\u94ed\u9053\uff1a\u201c\u6211\u5bb6\u4e16\u4ee3 \u5728\u4eac\u6388\u6b66\uff0c\u95e8\u751f\u6545\u65e7\uff0c\u9887\u4e0d\u4e4f\u4eba\uff0c\u613f\u4e3a\u5927\u5e05\u7a0d\u5c3d\u68c9\u8584\uff0c\u5fc5\u4e0d\u4ee4\u5978\u4eba\u5f97\u901e\u3002\u4f46\u53ec\u96c6\u9700\u65f6\uff0c\u6211\u73b0 \u5728\u5c31\u8981\u56de\u53bb\u4e86\u3002\u201d\u5cb3\u9e23\u73c2\u5927\u559c\u62dc\u8c22\u3002 \u3000\u3000\u67f3\u897f\u94ed\u53bb\u5f8c\uff0c\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u6b64\u4eba\u5728\u4eac\u4e2d\u4ea4\u6e38\u6781\u5e7f\uff0c\u9ed1\u767d\u4e24\u9053\uff0c\u5168\u6709\u4ea4\u60c5\u3002\u6709\u4ed6\u6697\u4e2d\u5e2e \u5fd9\uff0c\u6211\u4eec\u4e5f\u53ef\u7a0d\u7a0d\u653e\u5fc3\u3002\u201d\u718a\u5ef7\u5f3c\u53f9\u6c14\u9053\uff1a\u201c\u4ed7\u4e49\u6bcf\u591a\u5c60\u72d7\u8f88\uff0c\u770b\u4eca\u65e5\u671d\u5ef7\u4e4b\u4e8b\uff0c\u6211\u5b9e\u5df2\u7070 \u5fc3\u3002\u201d\u4f17\u5b98\u7eb7\u7eb7\u529d\u52c9\u3002\u6768\u6d9f\u9053\uff1a\u201c\u660e\u65e5\u4e0a\u671d\uff0c\u5148\u95ee\u5047\u94a6\u5dee\u5d14\u5448\u79c0\u4e4b\u4e8b\uff0c\u7136\u5f8c\u5411\u4e5d\u95e8\u63d0\u7763\u8981 \u4eba\u3002\u201d\u90fd\u5fa1\u53f2\u90b9\u5143\u6807\u9053\uff1a\u201c\u5d14\u5448\u79c0\u4e43\u662f\u9b4f\u5fe0\u8d24\u7684\u4eba\uff0c\u6211\u4eec\u4e00\u4e0d\u505a\u4e8c\u4e0d\u4f11\uff0c\u8d81\u8fd9\u4ef6\u4e8b\u5c06\u9b4f\u5fe0\u8d24 \u53c2\u4e86\u3002\u201d\u9080\u4f17\u5b98\u5171\u8bae\u594f\u647a\uff0c\u793c\u90e8\u5c1a\u4e66\u5b59\u614e\u884c\u9053\uff1a\u201c\u4f55\u4e0d\u9080\u96c6\u671d\u4e2d\u6240\u6709\u7684\u6b63\u6d3e\u5927\u81e3\uff0c\u8054\u540d\u4e0a \u594f\uff0c\u8981\u5723\u4e0a\u52a1\u5fc5\u5f7b\u67e5\u6b64\u4e8b\u3002\u201d\u540f\u90e8\u5c1a\u4e66\u5468\u5609\u8c1f\u9053\uff1a\u201c\u5bf9\u554a\uff0c\u8054\u540d\u4e0a\u594f\uff0c\u4eba\u591a\u52bf\u5927\uff0c\u53eb\u5978\u515a\u4e5f \u4e0d\u6562\u5c0f\u89d1\u6211\u4eec\u3002\u201d\u5f53\u4e0b\u5404\u81ea\u5206\u5934\u529e\u4e8b\u3002 \u3000\u3000\u4f17\u5b98\u6563\u5f8c\uff0c\u5cb3\u545c\u73c2\u5fc3\u4e2d\u6709\u4e8b\uff0c\u9887\u4e3a\u4e0d\u5b89\uff0c\u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u4eca\u65e5\u4e8f\u4f60\u89c1\u673a\uff0c\u53ca\u65f6\u95ef\u51fa\u53bb\u8bf7\u4e86 \u8fd9\u9ebd\u591a\u597d\u624b\u6765\u6551\u3002\u201d\u738b\u8d5e\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u5cb3\u5144\uff0c\u4f60\u600e\u9ebd\u8fd9\u6837\u795e\u901a\u5e7f\u5927\uff0c\u4e00\u4e0b\u5b50\u8bf7 \u5f97\u8fd9\u9ebd\u591a\u9ad8\u624b\u524d\u6765\u3002\u201d\u5cb3\u9e23\u73c2\u628a\u8fc7\u53bb\u7684\u4e8b\u60c5\u8bf4\u4e86\uff0c\u53c8\u8bf4\u5230\u94c1\u98de\u9f99\u7ea6\u4ed6\u4eca\u665a\u76f8\u4f1a\u7684\u4e8b\u3002\u718a\u5ef7\u5f3c \u9053\uff1a\u201c\u65e2\u7136\u6709\u7ea6\uff0c\u4e0d\u53ef\u5931\u4fe1\u3002\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u79bb\u5f00\u5927\u5e05\u3002\u800c\u4e14\u6211\u4e5f\u8fd8\u6ca1\u6709\u7b54\u5e94\u4ed6\u3002\u201d \u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u90a3\u4f60\u62d2\u7edd\u4e86\u4ed6\u6ca1\u6709\uff01\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u6765\u4e0d\u53ca\u62d2\u7edd\uff0c\u4ed6\u5df2\u8d70\u51fa\u5927\u95e8\u3002\u201d\u718a\u5ef7\u5f3c \u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u8fd8\u662f\u5e94\u8be5\u524d\u53bb\u8d74\u7ea6\u3002\u6211\u62b5\u6321\u767e\u4e07\u5927\u519b\u5c1a\u4e14\u4e0d\u60e7\uff0c\u4f55\u60e7\u5c0f\u8d3c\u3002\u800c\u4e14\u6709\u67f3\u4e49 \u58eb\u6697\u4e2d\u76f8\u52a9\uff0c\u4f60\u53bb\u597d\u4e86\u3002\u90a3\u4e2a\u8001\u5934\uff0c\u867d\u7136\u8c8c\u4f3c\u72c2\u5984\uff0c\u6211\u770b\u4ed6\u5374\u662f\u6027\u60c5\u4e2d\u4eba\uff0c\u5e94\u8be5\u53bb\u7ed3\u7eb3\u7ed3 \u7eb3\u3002\u201d \u3000\u3000\u665a\u996d\u8fc7\u5f8c\uff0c\u5cb3\u545c\u73c2\u5411\u718a\u5ef7\u5f3c\u544a\u8f9e\uff0c\u53c8\u4ea4\u4ee3\u4e86\u738b\u8d5e\u597d\u4e9b\u8bf4\u8bdd\uff0c\u8d70\u51fa\u5927\u95e8\uff0c\u679c\u7136\u89c1\u6709\u67f3\u897f\u94ed \u7684\u4eba\uff0c\u5206\u5e03\u5728\u6768\u6d9f\u5e9c\u90b8\u7684\u5468\u56f4\uff0c\u6697\u4e2d\u4fdd\u62a4\uff0c\u653e\u4e0b\u4e86\u5fc3\uff0c\u76f4\u5954\u90ca\u5916\u3002 \u3000\u3000\u7075\u5149\u5bfa\u5728\u897f\u5c71\u5c71\u9e93\uff0c\u5cb3\u545c\u73c2\u4e0a\u5f97\u5c71\u6765\uff0c\u5df2\u662f\u6708\u8fd1\u4e2d\u5929\uff0c\u5c06\u5230\u53c1\u66f4\u65f6\u5206\u3002\u5cb3\u9e23\u73c2\u5fc3\u60f3\uff0c\u8fd9 \u94c1\u98de\u9f99\u4e5f\u771f\u662f\u602a\u4eba\uff0c\u4f4f\u5f97\u79bb\u57ce\u5982\u6b64\u4e4b\u8fdc\uff0c\u5374\u8981\u4eba\u534a\u591c\u627e\u4ed6\uff0c\u4e0d\u77e5\u6709\u4ec0\u9ebd\u7d27\u6025\u4e8b\u60c5\u3002\u6b63\u601d\u91cf \u95f4\uff0c\u5ffd\u95fb\u5f97\u4e00\u9635\u7b11\u58f0\uff0c\u53d1\u81ea\u6797\u9645\uff0c\u7b11\u58f0\u672a\u505c\uff0c\u4eba\u5f71\u51fa\u73b0\uff0c\u7389\u7f57\u5239\u9ec4\u8863\u767d\u88d9\uff0c\u98d8\u7136\u6b65\u51fa\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u4e00\u6014\uff0c\u95ee\u9053\uff1a\u201c\u94c1\u8001\u524d\u8f88\u5462\uff1f\u201d\u7389\u7f57\u5239\u9762\u8272\u4e00\u7aef\uff0c\u5ffd\u9053\uff1a\u201c\u4eca\u65e5\u4f60\u662f\u6211\u7239\u7239\u7684\u8d35 \u5bbe\uff0c\u6211\u4eec\u867d\u6709\u70b9\u5c0f\u5c0f\u8fc7\u8282\uff0c\u4e5f\u5c31\u7b97\u4e86\u3002\u201d\u5cb3\u545c\u73c2\u5fc3\u9053\uff1a\u8c01\u548c\u4f60\u6709\u8fc7\u8282\uff1f\u4ee5\u524d\u5728\u534e\u5c71\u7edd\u9876\uff0c\u662f \u4f60\u65e0\u7aef\u7aef\u627e\u6211\u6bd4\u5251\uff0c\u5173\u6211\u751a\u4e8b\uff1f\u4f46\u7389\u7f57\u5239\u813e\u6c14\u4e4b\u602a\uff0c\u4ed6\u5df2\u5c61\u6b21\u9886\u6559\uff0c\u4e5f\u5c31\u4e0d\u53bb\u9a73\u5979\uff0c\u53c8\u95ee \u9053\uff1a\u201c\u94c1\u8001\u524d\u8f88\u53eb\u4f60\u6765\u63a5\u6211\u9ebd\uff1f\u201d \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u5c82\u6b62\u8981\u6211\u63a5\u4f60\uff0c\u8fd8\u8981\u6211\u5ba1\u95ee\u4f60\u5462\uff01\u201d\u5cb3\u9e23\u73c2\u6120\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\u522b\u5f00\u73a9\u7b11\u3002\u7389 \u7f57\u5239\u9053\uff1a\u201c\u8c01\u548c\u4f60\u5f00\u73a9\u7b11\u3002\u6211\u95ee\u4f60\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u94c1\u73ca\u745a\u662f\u4ed6\u7684\u5973\u513f\u3002\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u77e5 \u9053\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u7684\u5973\u513f\u662f\u8d1f\u6c14\u51fa\u8d70\u7684\uff1f\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u8fd9\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u201d \u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u548c\u5979\u4e00\u9053\u6765\u4eac\uff0c\u540c\u4f4f\u5728\u6768\u6d9f\u5bb6\u4e2d\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u4e0d\u9519\uff01\u4f46\u5979\u5728\u524d \u51e0\u5929\u5df2\u7ed9\u8d3c\u4eba\u52ab\u53bb\uff0c\u6211\u6b63\u60f3\u524d\u6765\u8bf7\u7f6a\u3002\u201d\u7389\u7f57\u5239\u5ffd\u7136\u683c\u683c\u5730\u7b11\u4e2a\u4e0d\u4f11\uff01 \u3000\u3000\u5cb3\u545c\u73c2\u53c8\u662f\u4e00\u6014\uff0c\u5fc3\u60f3\uff1a\u522b\u4eba\u906d\u4e86\u98de\u6765\u7684\u6a2a\u7978\uff0c\u4f60\u8fd8\u597d\u7b11\uff0c\u7389\u7f57\u5239\u7b11\u4e86\u4e00\u9635\uff0c\u53c8\u9053\uff1a \u201c\u6211\u7239\u7239\u4e0d\u662f\u95ee\u4f60\u8981\u4eba\uff0c\u4f60\u522b\u62c5\u5fc3\u3002\u4ed6\u662f\u8981\u628a\u5973\u513f\u9001\u7ed9\u4f60\uff01\u201d\u5cb3\u9e23\u73c2\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u8fd9 \u8bdd\u662f\u4ec0\u9ebd\u610f\u601d\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4ec0\u9ebd\u610f\u601d\uff0c\u4f60\u8fd8\u88c5\u50bb\u5417\uff1f\u6211\u66ff\u4f60\u505a\u5a92\uff0c\u4f60\u61c2\u4e0d\u61c2\uff1f\u201d\u5cb3\u545c\u73c2 \u9053\uff1a\u201c\u90a3\u6709\u8fd9\u6837\u505a\u5a92\u7684\u9053\u7406\uff1f\u201d\u7389\u7f57\u5239\u9762\u8272\u4e00\u7aef\uff0c\u9053\uff1a\u201c\u770b\u4f60\u4e0d\u662f\u8d1f\u4e49\u4e4b\u4eba\uff0c\u4e3a\u4f55\u8d56\u5e10\uff1f\u201d \u5cb3\u545c\u73c2\u53c8\u6c14\u53c8\u6025\uff0c\u9053\uff1a\u201c\u6211\u600e\u9ebd\u8d1f\u4e49\u4e86\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u4eec\u5b64\u7537\u5be1\u5973\uff0c\u4e07\u91cc\u540c\u884c\uff0c\u5230\u4e86\u4eac \u5e08\uff0c\u94c1\u73ca\u745a\u53c8\u662f\u5973\u626e\u7537\u88c5\uff0c\u548c\u4f60\u540c\u4f4f\u6768\u5bb6\uff0c\u96be\u9053\u4f60\u4eec\u5c31\u6ca1\u6709\u534a\u70b9\u79c1\u60c5\uff1f\u201d\u7389\u7f57\u5239\u5fc3\u76f4\u53e3\u5feb\uff0c \u8bf4\u8bdd\u6ca1\u534a\u70b9\u906e\u62e6\uff0c\u5cb3\u545c\u73c2\u7f9e\u5f97\u9762\u7ea2\u900f\u8033\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5cb3\u67d0\u4eba\u5149\u660e\u78ca\u843d\u2026\u2026\u201d\u5e95\u4e0b\u90a3\u53e5 \u201c\u5c82\u6709\u82df\u4e14\u4e4b\u884c\u3002\u201d\u5374\u5450\u5450\u4e0d\u4f7f\u51fa\u53e3\u3002\u7389\u7f57\u5239\u5df2\u7b11\u7740\u62a2\u9053\uff1a\u201c\u7537\u5973\u7231\u6155\uff0c\u4e8b\u6781\u5bfb\u5e38\uff0c\u6211\u82e5\u6709 \u559c\u6b22\u7684\u4eba\uff0c\u5c31\u5bf9\u8c01\u90fd\u4e0d\u6015\u8bf4\u3002\u906e\u906e\u63a9\u63a9\uff0c\u5c82\u662f\u4fa0\u58eb\u884c\u5f84\uff01\u201d\u5cb3\u9e23\u73c2\u6025\u6781\uff0c\u6325\u8896\u8bf4\u9053\uff1a\u201c\u6211\u548c \u73ca\u745a\u5144\u59b9\u76f8\u5904\uff0c\u7ec3\u5973\u4fa0\uff0c\u4f60\u5343\u4e07\u4e0d\u53ef\u8bef\u4f1a\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u7709\u5934\u4e00\u76b1\uff0c\u4f3c\u7b11\u975e\u7b11\uff0c\u9053\uff1a\u201c\u6709\u5426\u79c1\u60c5\u7684\u4e8b\u4e0d\u5fc5\u8bf4\u4e86\uff0c\u6211\u53ea\u95ee\u4f60\uff0c\u4f60\u559c\u4e0d\u559c\u6b22 \u5979\uff1f\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u6211\u5df2\u548c\u4f60\u8bf4\u8fc7\u2026\u2026\u201d\u7389\u7f57\u5239\u622a\u9053\uff1a\u201c\u4f60\u76f4\u622a\u4e86\u5f53\u56de\u6211\u7684\u8bdd\uff0c\u6211\u6700\u8ba8\u538c\u8bf4 \u8bdd\u515c\u5708\u5b50\uff0c\u4f60\u53ea\u8bf4\u559c\u6b22\u4e0d\u559c\u6b22\uff1f\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u559c\u6b22\uff01\u201d\u7389\u7f57\u5239\u677f\u8d77\u8138\u5b54\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u613f\u4e0d \u9a90\u5a36\u5979\uff01\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u559c\u6b22\u662f\u4e00\u56de\u4e8b\uff0c\u5ac1\u5a36\u53c8\u662f\u4e00\u56de\u4e8b\uff0c\u600e\u53ef\u6df7\u4e3a\u4e00\u8c08\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a \u201c\u4f60\u522b\u7f57\u54e9\u7f57\u5506\uff0c\u4f60\u7b54\u6211\uff1a\u4f60\u613f\u4e0d\u613f\u5a36\u5979\uff1f\u201d\u5cb3\u545c\u73c2\u89c1\u7389\u7f57\u5239\u4e0d\u53ef\u7406\u55bb\uff0c\u62c2\u8896\u8bf4\u9053\uff1a\u201c\u82e5\u65e0 \u4ed6\u4e8b\uff0c\u8bf7\u4f60\u4ee3\u7980\u94c1\u8001\u524d\u8f88\uff0c\u8bf4\u6211\u6765\u8fc7\u4e86\u3002\u201d\u8f6c\u8eab\u4fbf\u8d70\uff01\u7389\u7f57\u5239\u4e00\u58f0\u957f\u7b11\uff0c\u8eab\u5f62\u98de\u8d77\uff0c\u62a2\u5728\u4ed6 \u7684\u9762\u524d\uff0c\u5b9d\u5251\u65e9\u5df2\u62d4\u5728\u624b\u4e2d\uff0c\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u505a\u4ec0\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4e0d\u8bb8\u8d70\uff01\u4f60\u5230\u5e95\u5a36\u4e0d\u5a36 \u5979\uff1f\u201d\u5cb3\u545c\u73c2\u6c14\u5f80\u4e0a\u51b2\uff0c\u9053\uff1a\u201c\u4e0d\u5a36\uff01\u201d\u7389\u7f57\u5239\u51b7\u7b11\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u679c\u7136\u4e0d\u662f\u4e1c\u897f\uff01\u201d\u5237\u7684\u4e00 \u5251\uff0c\u7adf\u7136\u5411\u5cb3\u545c\u73c2\u523a\u6765\uff0c\u5cb3\u545c\u73c2\u817e\u632a\u95ea\u907f\uff0c\u7389\u7f57\u5239\u51fa\u624b\u4e4b\u5f8c\uff0c\u4e0d\u80fd\u81ea\u4f11\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u8fde\u523a\u6570 \u5251\u3002\u7389\u7f57\u5239\u5251\u6cd5\u51f6\u6b8b\u65e0\u6bd4\uff0c\u968f\u624b\u523a\u6765\uff0c\u90fd\u662f\u6307\u5411\u5173\u8282\u8981\u5bb3\uff01 \u3000\u3000\u5cb3\u545c\u73c2\u5fcd\u65e0\u53ef\u5fcd\uff0c\u95ea\u5f97\u51e0\u95ea\uff0c\u55d6\u7684\u4e00\u58f0\uff0c\u4e5f\u628a\u6e38\u9f99\u5251\u62d4\u4e86\u51fa\u6765\u3002\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u6709\u672c \u4e8b\uff0c\u5c31\u628a\u6211\u8fd9\u5a92\u4eba\u6740\u4e86\uff01\u201d\u5251\u52bf\u50ac\u7d27\uff0c\u6025\u5982\u9aa4\u96e8\u66b4\u98ce\u201c\u5cb3\u9e23\u73c2\u8fde\u89e3\u6570\u5251\uff0c\u6012\u9053\uff1a\u201c\u5929\u5e95\u4e0b\u5c31 \u6ca1\u89c1\u8fc7\u4f60\u8fd9\u6837\u4e0d\u8bb2\u7406\u7684\u4eba\uff0c\u90a3\u6709\u8feb\u4eba\u6210\u4eb2\u4e4b\u7406\uff01\u201d\u5c82\u77e5\u7389\u7f57\u5239\u60f3\u6cd5\u4e0e\u4ed6\u4e0d\u540c\uff0c\u5979\u8ba4\u4e3a\u5cb3\u545c\u73c2 \u65e2\u4e0e\u94c1\u73ca\u745a\u4e07\u91cc\u540c\u884c\uff0c\u53c8\u540c\u4f4f\u4e00\u5bb6\uff0c\u800c\u4e14\u94c1\u73ca\u745a\u4e5f\u613f\u5ac1\u4ed6\uff0c\u90a3\u9ebd\u4ed6\u5c31\u975e\u5a36\u4e0d\u53ef\uff01 \u3000\u3000\u5cb3\u545c\u73c2\u7ed9\u5979\u82e6\u8feb\uff0c\u4e5f\u81ea\u52a8\u4e86\u771f\u6c14\uff0c\u628a\u5929\u5c71\u5251\u6cd5\u7684\u7cbe\u5999\u62db\u6570\u5c55\u4e86\u5f00\u6765\uff0c\u6740\u5f97\u7389\u7f57\u5239\u4e0d\u6562\u6b3a \u8eab\u8feb\u8fd1\u3002\u7389\u7f57\u5239\u53eb\u9053\uff1a\u201c\u73ca\u745a\u59b9\u59b9\uff0c\u8fd9\u6837\u65e0\u4e49\u4e4b\u4eba\uff0c\u4e0d\u5ac1\u4e5f\u7f62\uff0c\u6211\u66ff\u4f60\u628a\u4ed6\u6740\u4e86\uff01\u201d\u5cb3\u545c\u73c2 \u4e00\u6014\uff0c\u6e38\u76ee\u56db\u987e\uff0c\u7565\u7565\u5206\u795e\uff0c\u7389\u7f57\u5239\u5de6\u4e00\u5251\uff0c\u53f3\u4e00\u5251\uff0c\u7a81\u7136\u4e58\u9699\u76f4\u8fdb\uff0c\u5f53\u4e2d\u4e00\u5251\uff0c\u76f4\u523a\u5230\u5cb3 \u9e23\u73c2\u54bd\u5589\u8981\u5bb3\uff01 \u3000\u3000\u5cb3\u545c\u73c2\u80a9\u5934\u4e00\u7f29\uff0c\u5934\u4e0a\u51b7\u6c14\u68ee\u68ee\uff0c\u7389\u7f57\u5239\u5237\u7684\u4e00\u5251\u524a\u8fc7\uff01\u5cb3\u545c\u73c2\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u52c3\u7136\u5927 \u6012\uff0c\u5251\u628a\u4e00\u7ffb\uff0c\u4e00\u62db\u201c\u4e3e\u706b\u71ce\u5929\u201d\uff0c\u628a\u7389\u7f57\u5239\u7684\u5251\u6c64\u4e86\u5f00\u53bb\uff0c\u6012\u9053\uff1a\u201c\u51ed\u4ec0\u9ebd\u6211\u90fd\u4e0d\u5a36 \u5979\uff01\u201d\u7389\u7f57\u5239\u53c8\u53eb\u4e00\u58f0\uff1a\u201c\u73ca\u745a\u59b9\u59b9\uff01\u201d\u5cb3\u9e23\u73c2\u5728\u6c14\u5934\u4e0a\u53e3\u4e0d\u62e9\u8a00\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u662f\u53eb\u5979\u6765\u4e5f \u6ca1\u7528\uff0c\u6211\u600e\u9ebd\u4e5f\u4e0d\u4f1a\u5a36\u5979\uff01\u201d\u8bdd\u521a\u51fa\u53e3\uff0c\u6811\u6797\u4e2d\u7a81\u7136\u54cd\u8d77\u4e00\u58f0\u7126\u96f7\u822c\u7684\u5927\u559d\uff0c\u4e00\u56e2\u9ed1\u5f71\u7a81\u7136 \u5f53\u7a7a\u7f69\u4e0b\uff0c\u5cb3\u9e23\u73c2\u4f0f\u5730\u4e00\u6eda\uff0c\u53ea\u542c\u5f97\u90a3\u4eba\u9a82\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u6562\u6c61\u8fb1\u6211\u7684\u5973\u513f\uff0c\u5403\u6211\u4e00 \u62f3\uff01\u201d\u58f0\u5230\u4eba\u5230\uff0c\u5cb3\u9e23\u73c2\u865a\u6321\u4e00\u5251\uff0c\u8fa9\u9053\uff1a\u201c\u94c1\u8001\u524d\u8f88\u6055\u7f6a\uff0c\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u94c1\u98de\u9f99\u5288\u9762 \u4e00\u62f3\uff0c\u53c8\u9a82\u9053\uff1a\u201c\u9713\u88f3\u548c\u4f60\u63d0\u4eb2\uff0c\u4f60\u4e0d\u613f\u610f\u4e5f\u5c31\u7b97\u4e86\uff0c\u4e3a\u4f55\u51fa\u8a00\u6c61\u8fb1\uff01\u201d\u5cb3\u545c\u73c2\u4e00\u5251\u523a\u4ed6\u5de6 \u80a9\uff0c\u4ee5\u653b\u4e3a\u5b88\uff0c\u89e3\u4e86\u94c1\u98de\u9f99\u7684\u6076\u62db\uff0c\u6025\u9053\uff1a\u201c\u94c1\u8001\u524d\u8f88\uff0c\u4f60\u522b\u591a\u5fc3\u2026\u2026\u201d\u94c1\u98de\u9f99\u80a9\u5934\u4e00\u62e7\uff0c \u5de6\u62f3\u53f3\u638c\uff0c\u540c\u65f6\u53d1\u51fa\uff0c\u9a82\u9053\uff1a\u201c\u6211\u90fd\u542c\u5230\u4e86\uff0c\u4f60\u518d\u72e1\u8fa9\u4e5f\u6ca1\u6709\u7528\u3002\u201d\u94c1\u98de\u9f99\u529f\u529b\u6781\u9ad8\uff0c\u62f3\u96c4 \u52bf\u52b2\uff1a\u5cb3\u9e23\u73c2\u5fc3\u4e2d\u53c8\u614c\uff0c\u56de\u8eab\u6321\u65f6\uff0c\u94c1\u98de\u9f99\u62f3\u80cc\u5411\u5916\uff0c\u6643\u4e86\u4e00\u6643\uff0c\u628a\u5cb3\u545c\u73c2\u773c\u795e\u5f15\u5411\u5de6\u8fb9\uff0c \u53f3\u638c\u4e00\u6c89\uff0c\u547c\u7684\u4e00\u638c\u63a8\u51fa\uff0c\u5cb3\u545c\u73c2\u80a9\u5934\u5267\u75db\uff0c\u7b4b\u9aa8\u6b32\u88c2\uff0c\u7ed9\u638c\u65b9\u9707\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u7389\u7f57\u5239\u4e00\u5251 \u98de\u524d\uff0c\u9752\u5149\u4e00\u95ea\uff0c\u5237\u7684\u4e00\u5251\u5206\u5fc3\u523a\u5230\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u73b0\u5728\u8fd8\u60f3\u9003\u5417\uff1f\u201d\u5cb3\u545c\u73c2\u5b9d\u5251\u4e00\u65cb\uff0c\u5c06 \u7389\u7f57\u5239\u5251\u62db\u7834\u53bb\uff0c\u53cd\u8eab\u4e00\u8dc3\uff0c\u94c1\u98de\u9f99\u8eab\u5f62\u4e00\u8d77\uff0c\u76f4\u5982\u5de8\u9e70\u63a0\u7a7a\uff0c\u62a2\u5728\u4ed6\u7684\u9762\u524d\uff0c\u4e94\u6307\u5982\u94a9\uff0c \u500f\u5730\u6293\u4e0b\u3002\u5cb3\u545c\u73c2\u80cc\u8179\u53d7\u654c\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u628a\u5251\u4e00\u629b\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4f60\u628a\u6211\u6740\u4e86\u5427\uff01\u201d \u3000\u3000\u8fd9\u4e00\u62db\u662f\u94c1\u98de\u9f99\u7684\u6740\u624b\u7edd\u62db\uff0c\u4e0d\u610f\u5cb3\u545c\u73c2\u7a81\u7136\u5f03\u5251\uff0c\u4e0d\u89c9\u4e00\u6014\uff0c\u624b\u638c\u5212\u4e86\u4e00\u4e2a\u5706\u5f27\uff0c\u505c \u5728\u534a\u7a7a\u3002\u6b63\u5728\u5c06\u843d\u672a\u843d\u4e4b\u9645\uff0c\u6797\u4e2d\u4e00\u58f0\u5c16\u53eb\uff0c\u4e00\u4e2a\u5c11\u5973\u98de\u4e00\u822c\u7684\u8dd1\u4e86\u51fa\u6765\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c \u4e0d\u8981\u52a8\u624b\uff0c\u5973\u513f\u6709\u8bdd\u8981\u8bf4\uff01\u201d\u5cb3\u545c\u73c2\u53c8\u60ca\u53c8\u559c\uff0c\u53eb\u4e86\u4e00\u58f0\u201c\u73ca\u745a\uff01\u201d\u518d\u4e5f\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u539f\u6765\u94c1\u98de\u9f99\u548c\u7389\u7f57\u5239\u4e3a\u4e86\u8ffd\u56de\u5251\u8c31\uff0c\u66fe\u8fdc\u5230\u585e\u5916\uff0c\u76f4\u6363\u91d1\u72ec\u5f02\u7684\u8001\u5de2\uff0c\u67e5\u5f97\u91d1\u72ec\u5f02\u5df2\u79d8 \u5bc6\u6765\u4eac\uff0c\u65bc\u662f\u4e24\u4eba\u53c8\u4ec6\u4ec6\u98ce\u5c18\uff0c\u4e00\u76f4\u8ffd\u5230\u4eac\u57ce\u3002\u5230\u4e86\u4eac\u57ce\u4e4b\u5f8c\uff0c\u65e0\u610f\u4e2d\u53d1\u73b0\u94c1\u73ca\u745a\u5973\u626e\u7537\u88c5 \u548c\u5cb3\u9e23\u73c2\u540c\u4f4f\u6768\u5bb6\u3002\u94c1\u98de\u9f99\u5f53\u65e5\u628a\u5973\u513f\u8d76\u51fa\u5bb6\u95e8\uff0c\u539f\u662f\u4e00\u65f6\u4e4b\u6c14\uff0c\u8fc7\u5f8c\u5341\u5206\u5f8c\u6094\u3002\u7389\u7f57\u5239\u77e5 \u4ed6\u5fc3\u610f\uff0c\u4fbf\u9053\uff1a\u201c\u4f60\u4f55\u4e0d\u53bb\u770b\u770b\u4ed6\u4eec\uff0c\u90a3\u4e2a\u59d3\u5cb3\u7684\u5c0f\u5b50\u662f\u6211\u8ba4\u8bc6\u7684\uff0c\u5982\u679c\u4f60\u6709\u610f\u601d\uff0c\u6211\u4fbf\u66ff \u4f60\u505a\u5a92\u3002\u201d\u5176\u65f6\u94c1\u98de\u9f99\u548c\u7389\u7f57\u5239\u5df2\u63a2\u51fa\u91d1\u72ec\u5f02\u8eb2\u5728\u5bab\u4e2d\uff0c\u7389\u7f57\u5239\u4e14\u5df2\u9884\u5b9a\u5f53\u665a\u5c31\u8981\u4eba\u5bab\u641c \u4ed6\u3002\u94c1\u98de\u9f99\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u548c\u6211\u5148\u53bb\u6768\u5bb6\uff0c\u7136\u5f8c\u518d\u95ef\u5bab\u641c\u90a3\u8001\u602a\u7269\u5427\u3002\u201d\u4e0d\u610f\u7389\u7f57\u5239\u5374\u9053\uff1a \u201c\u6211\u4e0d\u60f3\u89c1\u90a3\u59d3\u5cb3\u7684\u5c0f\u5b50\uff0c\u54b1\u4eec\u5206\u5934\u529e\u4e8b\uff0c\u4f60\u53bb\u63a2\u5973\u513f\uff0c\u6211\u4eba\u5bab\u53bb\u641c\u90a3\u4e2a\u8001\u602a\u7269\u3002\u201d\u94c1\u98de\u9f99 \u9053\uff1a\u201c\u600e\u9ebd\uff0c\u90a3\u5c0f\u5b50\u4e0d\u662f\u597d\u4eba\u5417\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8c01\u8bf4\u4ed6\u4e0d\u662f\u597d\u4eba\uff0c\u4e0d\u8fc7\u6211\u548c\u4ed6\u6709\u4e00\u6bb5\u8fc7 \u8282\uff0c\u9664\u975e\u4ed6\u548c\u73ca\u745a\u59b9\u59b9\u6210\u4eb2\uff0c\u5426\u5219\u6211\u548c\u4ed6\u4e0d\u80fd\u548c\u89e3\u3002\u201d\u94c1\u98de\u9f99\u548c\u7389\u7f57\u5239\u4e24\u4eba\u813e\u6c14\u90fd\u602a\uff0c\u4e00\u8bf4 \u4e4b\u5f8c\uff0c\u7adf\u7136\u5404\u81ea\u5206\u5934\u529e\u4e8b\uff0c\u5c31\u5728\u90a3\u4e00\u665a\u4e0a\uff0c\u4e24\u4eba\u90fd\u6709\u5947\u9047\uff01 \u3000\u3000\u90a3\u4e00\u665a\u9002\u503c\u5cb3\u545c\u73c2\u4e8c\u6b21\u5165\u5bab\uff0c\u7389\u7f57\u5239\u5728\u5bab\u4e2d\u4e71\u95ef\uff0c\u6070\u6070\u95ef\u5230\u9b4f\u5fe0\u8d24\u7684\u5c45\u5904\uff0c\u9b4f\u5fe0\u8d24\u6b63\u5728 \u548c\u624b\u4e0b\u6b66\u58eb\u8d4f\u73a9\u5cb3\u545c\u73c2\u7684\u6e38\u9f99\u5b9d\u5251\u3002\u7389\u7f57\u5239\u4e0d\u8ba4\u5f97\u9b4f\u5fe0\u8d24\uff0c\u5374\u8ba4\u5f97\u90a3\u628a\u6e38\u9f99\u5b9d\u5251\uff0c\u4e00\u4f38\u624b\u5c31 \u628a\u90a3\u67c4\u5251\u62a2\u4e86\uff0c\u5f15\u8d77\u4e00\u9635\u5927\u4e71\u3002\u5cb3\u545c\u73c2\u4e8f\u5f97\u6709\u5979\u5206\u6563\u5bab\u4e2d\u536b\u58eb\u7684\u6ce8\u610f\uff0c\u8fd9\u624d\u5f97\u4ece\u5bb9\u6551\u51fa\u6210 \u5764\uff0c\u4f46\u5cb3\u9e23\u73c2\u5f53\u65f6\u5374\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u53e6\u4e00\u65b9\u9762\uff0c\u94c1\u98de\u9f99\u6765\u770b\u5973\u513f\uff0c\u672a\u5230\u6768\u5bb6\uff0c\u5c31\u78b0\u5230\u4e1c\u5382\u7684\u536b\u58eb\u5c06\u5979\u52ab\u8d70\uff0c\u94c1\u98de\u9f99\u5927\u6012\uff0c\u4e00 \u8fde\u51fb\u6bd9\u4e03\u540d\u536b\u58eb\uff0c\u5c06\u5973\u513f\u6551\u4e86\u51fa\u6765\u3002\u4e5f\u6b63\u56e0\u6b64\uff0c\u94c1\u98de\u9f99\u77e5\u9053\u91d1\u8001\u602a\u7b49\u8fd9\u4e00\u73ed\u4eba\u5fc5\u5b9a\u4f1a\u518d\u5230\u6768 \u5bb6\uff0c\u6240\u4ee5\u624d\u6709\u5f8c\u6765\u94c1\u98de\u9f99\u548c\u7389\u7f57\u5239\u53cc\u53cc\u95ef\u6765\uff0c\u6070\u597d\u66ff\u718a\u5ef7\u5f3c\u89e3\u4e86\u56f4\u653b\u7684\u4e00\u5e55\u3002 \u3000\u3000\u94c1\u98de\u9f99\u5c06\u5973\u513f\u6551\u51fa\u4e4b\u5f8c\uff0c\u7ec6\u7ec6\u76d8\u95ee\uff0c\u63a2\u51fa\u5973\u513f\u7684\u53e3\u98ce\uff0c\u77e5\u5979\u5bf9\u5cb3\u545c\u73c2\u751a\u4e3a\u7231\u6155\u3002\u94c1\u98de\u9f99 \u4e5f\u4ee5\u4e3a\u5973\u513f\u548c\u4ed6\u5df2\u6709\u79c1\u60c5\uff0c\u6240\u4ee5\u624d\u5f15\u8d77\u90a3\u9ebd\u6df1\u7684\u8bef\u4f1a\u3002\u94c1\u98de\u9f99\u63a2\u51fa\u5973\u513f\u7684\u5fc3\u4e8b\u4e4b\u5f8c\uff0c\u5c31\u548c\u7389 \u7f57\u5239\u5546\u91cf\uff0c\u7389\u7f57\u5239\u81ea\u544a\u594b\u52c7\uff0c\u613f\u4f5c\u5927\u5a92\uff0c\u94c1\u98de\u9f99\u548c\u5973\u513f\u8eb2\u5728\u6797\u4e2d\u7684\u5927\u6811\u4e0a\u542c\u4ed6\u4eec\u8c08\u8bdd\uff0c\u542c\u5230 \u5f8c\u6765\uff0c\u4ed6\u4eec\u8d9f\u8bf4\u8d8a\u50f5\uff0c\u7adf\u7136\u62d4\u5251\u52a8\u624b\uff0c\u94c1\u98de\u9f99\u6c89\u4e0d\u7740\u6c14\uff0c\u6325\u62f3\u52a0\u4eba\u6218\u5708\uff0c\u4e8b\u60c5\u8d8a\u95f9\u8d8a\u5927\u3002 \u3000\u3000\u518d\u8bf4\u94c1\u73ca\u745a\u5728\u6797\u4e2d\u542c\u5f97\u5cb3\u545c\u73c2\u548c\u7389\u7f57\u5239\u7684\u5bf9\u8bdd\uff0c\u5fc3\u4e2d\u751a\u4e3a\u60b2\u75db\u3002\u867d\u7136\u4ed6\u548c\u5cb3\u9e23\u73c2\u4e07\u91cc\u540c \u884c\uff0c\u4ece\u672a\u6d89\u53ca\u201c\u7231\u201d\u5b57\uff0c\u4f46\u5979\u4e00\u7247\u82b3\u5fc3\uff0c\u5df2\u7cfb\u5728\u5cb3\u9e23\u73c2\u8eab\u4e0a\uff0c\u5979\u7edd\u672a\u60f3\u5230\u5cb3\u545c\u73c2\u4f1a\u62d2\u7edd\u8981 \u5979\uff0c\u542c\u4e86\u90a3\u756a\u5bf9\u8bdd\u4e4b\u5f8c\uff0c\u53c8\u662f\u6c14\u6124\u53c8\u662f\u81ea\u5351\uff0c\u9519\u7efc\u590d\u6742\u7684\u5fc3\u60c5\uff0c\u4ee4\u5979\u7231\u6068\u4ea4\u8ff8\uff0c\u6b32\u54ed\u65e0\u6cea\u3002 \u7136\u800c\u773c\u89c1\u5cb3\u545c\u73c2\u53d7\u7236\u4eb2\u548c\u7389\u7f57\u5239\u7684\u56f4\u653b\uff0c\u6b7b\u751f\u4fc4\u9877\uff0c\u5979\u7981\u4e0d\u4f4f\u51b2\u4e86\u51fa\u6765\uff0c\u6500\u7740\u4e86\u7236\u4eb2\u7684\u624b \u8155\u3002 \u3000\u3000\u4e66\u63a5\u524d\u6587\uff0c\u4e14\u8bf4\u5cb3\u545c\u73c2\u7a81\u89c1\u94c1\u73ca\u745a\u73b0\u8eab\uff0c\u521a\u53eb\u5f97\u4e00\u58f0\u201c\u73ca\u745a  \u59b9\uff01\u201d\u53ea\u542c\u5f97\u73ca\u745a\u5c16\u58f0\u53eb \u9053\uff1a\u201c\u7239\u7239\uff0c\u4e0d\u5173\u4ed6\u7684\u4e8b\uff01\u201d\u968f\u5373\u8f6c\u8fc7\u8eab\u6765\uff0c\u54d1\u58f0\u5bf9\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u5cb3\u5927\u54e5\uff0c\u591a\u8c22\u4f60\u4e00\u8def\u7167 \u987e\uff0c\u4f60\u8fd9\u4e0d\u6210\u6750\u60f9\u4eba\u618e\u538c\u7684\u59b9  \uff0c\u4eca\u5f8c\u4e0d\u6562\u53eb\u4f60\u518d\u64cd\u5fc3\u4e86\u3002\u6211\u627f\u4f60\u7167\u987e\uff0c\u7d2f\u4f60\u751f\u6c14\uff0c\u65e0\u53ef\u62a5 \u7b54\uff0c\u65e0\u53ef\u8d4e\u7f6a\uff0c\u5927\u54e5\u5728\u4e0a\uff0c\u8bf7\u4f60\u53d7\u6211\u4e00\u62dc\uff01\u201d\u67f3\u8170\u4e00\u5f2f\uff0c\u62dc\u4e86\u4e0b\u53bb\uff0c\u5cb3\u545c\u73c2\u695e\u5728\u5f53\u573a\uff0c\u60f3\u5230 \u81ea\u5df1\u65e0\u610f\u4e4b\u4e2d\uff0c\u4f24\u4e86\u8fd9\u6837\u4e00\u4e2a\u5929\u771f\u65e0\u90aa\u7684\u5c11\u5973\u82b3\u5fc3\uff0c\u771f\u662f\u83ab\u5927\u7684\u7f6a\u5b7d\uff0c\u53ea\u89c9\u5168\u8eab\u6218\u6817\uff0c\u4e00\u53e5 \u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\uff0c\u53c8\u4e0d\u6562\u4f38\u624b\u6276\u5979\uff0c\u6014\u6014\u7684\u770b\u5979\u62dc\u4e86\u4e0b\u53bb\uff0c\u53c8\u7ad9\u4e86\u8d77\u6765\uff0c\u8138\u8272\u60e8\u767d\uff0c\u9762\u988a\u6709\u4e24 \u9897\u9ec4\u8c46\u822c\u7684\u6cea\u73e0\uff0c\u5fc3\u4e2d\u96be\u8fc7\u5f02\u5e38\uff0c\u521a\u60f3\u8bf4\u8bdd\uff0c\u53ea\u542c\u5f97\u94c1\u73ca\u745a\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u6562\u9ad8\u6500\uff0c\u4ece\u4eca \u5f8c\u4f60\u6211\u4e0d\u5fc5\u518d\u4ee5\u5144\u59b9\u76f8\u79f0\uff0c\u6211\u2026\u2026\u6211\u4eec\u4e5f\u4e0d\u5fc5\u518d\u76f8\u89c1\u4e86\uff01\u201d\u4e00\u8f6c\u8eab\u98de\u5954\u56de\u5bfa\u3002\u5cb3\u545c\u73c2\u50f5\u4e86\u4e00 \u4f1a\uff0c\u7a81\u7136\u53eb\u9053\uff1a\u201c\u662f\u6211\u7684\u9519\uff01\u201d\u811a\u6b65\u4e00\u8d77\uff0c\u6b63\u8981\u8ffd\u53bb\uff0c\u7389\u7f57\u5239\u5728\u65c1\u6c14\u5f97\u9762\u8272\u94c1\u9752\uff0c\u559d\u9053\uff1a \u201c\u4f60\u8fd8\u60fa\u60fa\u4f5c\u6001\uff1f\u201d\u5237\u7684\u4e00\u5251\u523a\u6765\uff0c\u94c1\u98de\u9f99\u53f3\u624b\u4e00\u4f38\uff0c\u628a\u7389\u7f57\u5239\u7684\u624b\u8155\u4e00\u6258\uff0c\u559d\u9053\uff1a\u201c\u59d3\u5cb3 \u7684\u5c0f\u5b50\uff0c\u4f60\u8d70\uff01\u518d\u8fdf\u6211\u4e5f\u4e0d\u9976\u4f60\u4e86\uff01\u201d\u5cb3\u545c\u73c2\u62ac\u8d77\u5b9d\u5251\uff0c\u9ed8\u7136\u4e0b\u5c71\uff0c\u8033\u8fb9\u72b9\u81ea\u542c\u5f97\u7389\u7f57\u5239 \u201c\u563f\u563f\u201d\u7684\u51b7\u7b11\uff0c\u5728\u5c71\u98ce\u4e2d\u56de\u6c64\uff0c\u72b9\u5982\u4e07\u7bad\u98de\u6765\uff0c\u63d2\u5728\u4ed6\u7684\u5fc3\u4e0a\uff01 \u3000\u3000\u94c1\u98de\u9f99\u76ee\u9001\u5cb3\u545c\u73c2\u7684\u80cc\u5f71\u5728\u591c\u8272\u4e2d\u6d88\u5931\uff0c\u5446\u7acb\u4e00\u4f1a\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u7239\uff0c\u56de\u53bb\u5427\uff1f\u201d\u94c1\u98de \u9f99\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u73ca\u745a\u59b9\u59b9\u6b64\u523b\u4e0d\u77e5\u591a\u96be\u8fc7\u5462\uff0c\u54b1\u4eec\u56de\u53bb\u770b\u5979\uff01\u201d\u94c1\u98de\u9f99\u4e00\u7529\u80e1 \u987b\uff0c\u6124\u7136\u8bf4\u9053\uff1a\u201c\u6211\u7684\u5973\u513f\u6709\u90a3\u70b9\u4e0d\u597d\uff0c\u59d3\u5cb3\u90a3\u5c0f\u5b50\u6562\u8fd9\u6837\u65e0\u793c\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u90a3\u662f\u4ed6\u6ca1 \u798f\u6c14\uff0c\u4ee5\u5f8c\u4ed6\u5c31\u662f\u4e00\u6b65\u4e00\u62dc\u6765\u6c42\u5a5a\uff0c\u54b1\u4eec\u4e5f\u4e0d\u7406\u4ed6\u3002\u201d\u7389\u7f57\u5239\u4e0d\u77e5\u6b63\u662f\u5979\u8fd9\u6837\u505a\u5a92\u505a\u574f\u4e86\u3002 \u94c1\u98de\u9f99\u7ed9\u5979\u7684\u8bdd\u5f15\u5f97\u5657\u55e4\u4e00\u7b11\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u597d\u4e86\uff0c\u54b1\u4eec\u8be5\u56de\u53bb\u770b\u73ca\u745a\u4e86\uff0c\u8981\u4e0d\u7136\u5979\u54ed\u5012\u4e86 \u4e5f\u6ca1\u4eba\u7406\uff0c\u4f1a\u66f4\u4f24\u5fc3\u5462\uff01\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u5979\u54ed\u5c31\u4e0d\u662f\u6211\u7684\u5973\u513f\uff01\u201d\u94c1\u98de\u9f99\u6df1\u77e5\u5973\u513f\u813e \u6c14\uff0c\u4e0d\u8bba\u53d7\u591a\u5927\u59d4\u5c48\uff0c\u90fd\u4e0d\u4f1a\u5f53\u4eba\u793a\u5f31\uff0c\u66f4\u4e0d\u4f1a\u5411\u4eba\u6c42\u60c5\u3002\u4f46\uff0c\u867d\u7136\u5982\u6b64\uff0c\u94c1\u98de\u9f99\u8fd8\u662f\u653e\u5fc3 \u4e0d\u4e0b\uff0c\u53c1\u6b65\u79fb\u4f5c\u4e24\u6b65\uff0c\u8d76\u56de\u5bfa\u5185\u3002 \u3000\u3000\u7075\u5149\u5bfa\u539f\u662f\u4e00\u4e2a\u8352\u829c\u53e4\u5bfa\uff0c\u94c1\u98de\u9f99\u501f\u6b64\u6682\u5c45\u624d\u7a0d\u7a0d\u6253\u626b\uff0c\u4f46\u4ecd\u662f\u7070\u5c18\u6ee1\u5730\u3002\u94c1\u98de\u9f99\u8e0f\u4eba \u5b88\u95e8\uff0c\u5ffd\u89c1\u53f0\u9636\u4e0a\u6709\u51cc\u4e71\u7684\u811a\u5370\uff0c\u6025\u53eb\u9053\uff1a\u201c\u73ca\u745a\uff0c\u73ca\u745a\uff01\u201d\u53e4\u5bfa\u9759\u5bc2\u5bc2\u7684\u6c93\u65e0\u4eba\u58f0\uff0c\u7389\u7f57 \u5239\u4e5f\u770b\u51fa\u4e86\u8ff9\u8c61\uff0c\u9053\uff1a\u201c\u600e\u9ebd\uff1f\u96be\u9053\u6709\u751f\u4eba\u8eb2\u5728\u5bfa\u91cc\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u5230\u524d\u9762\u5c71\u5934\u773a\u671b\uff0c \u82e5\u7136\u6709\u8b66\uff0c\u53d1\u5578\u4e3a\u53f7\u3002\u201d\u94c1\u98de\u9f99\u662f\u4e2a\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u4ed6\u53eb\u7389\u7f57\u5239\u5728\u5916\u773a\u671b\uff0c\u4e00\u6765\u662f\u63d0\u9632\u6765 \u4eba\u6709\u515a\u7fbd\u5728\u5916\uff0c\u4e8c\u6765\u662f\u63d0\u9632\u82e5\u6709\u6697\u7b97\uff0c\u4e24\u4eba\u5206\u5f00\u4e24\u5904\uff0c\u4e5f\u597d\u4e92\u76f8\u6551\u63f4\uff0c\u4e0d\u81f3\u65bc\u7ed9\u4e00\u7f51\u6253\u5c3d\u3002 \u3000\u3000\u94c1\u98de\u9f99\u5728\u5e99\u5185\u5de1\u89c6\u4e00\u5468\uff0c\u542c\u5f97\u73ca\u745a\u6240\u4f4f\u7684\u897f\u9762\u53a2\u623f\u4f3c\u6709\u62bd\u564e\u58f0\u606f\uff0c\u5fc3\u9053\uff1a\u201c\u96be\u9053\u8fd9\u50bb\u4e2b \u5934\u771f\u7684\u54ed\u4e86\uff1f\u201d\u6084\u6084\u7684\u63a8\u5f00\u623f\u95e8\uff0c\u53eb\u9053\uff1a\u201c\u73ca\u745a\uff01\u201d\u5ffd\u89c1\u5e8a\u4e0a\u5750\u7740\u4e00\u4e2a\u5973\u4eba\uff0c\u62ab\u5934\u6563\u53d1\uff0c\u7f13 \u7f13\u8bf4\u9053\uff1a\u201c\u73ca\u745a\u5df2\u7ecf\u8d70\u4e86\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u77aa\u773c\u4e00\u770b\uff0c\u5e8a\u4e0a\u5750\u7684\u7adf\u7136\u662f\u81ea\u5df1\u4ee5\u524d\u7684\u7231\u59be\u7a46\u4e5d\u5a18\uff0c\u4e0d\u7981\u5927\u51fa\u610f\u5916\u3002\u6012\u9053\uff1a\u201c\u4f60 \u8fd9\u8d31\u4eba\u6765\u505a\u4ec0\u9ebd\uff1f\u662f\u4f60\u628a\u73ca\u745a\u52fe\u5f15\u8d70\u4e86\uff1f\u201d\u7a46\u4e5d\u5a18\u4e00\u58f0\u4e0d\u54cd\uff0c\u628a\u624b\u5fc3\u4e00\u644a\uff0c\u91cc\u9762\u6709\u53c1\u9897\u6bb7\u7ea2 \u5982\u8840\u7684\u73cd\u73e0\uff0c\u94c1\u98de\u9f99\u5927\u60ca\u5931\u8272\uff0c\u9053\uff1a\u201c\u4f60\u548c\u90a3\u4e2a\u5973\u9b54\u5934\u505a\u4e00\u8def\u4e86\u3002\u201d\u7a46\u4e5d\u5a18\u51c4\u7136\u4e00\u7b11\u9053\uff1a \u201c\u8001\u7237\uff0c\u4f60\u8fd8\u662f\u4ee5\u524d\u7684\u813e\u6c14\uff0c\u5f00\u53e3\u4fbf\u4e71\u9a82\u4eba\uff01\u201d\u94c1\u98de\u9f99\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u662f\u60f3\u501f\u90a3\u5973 \u9b54\u5934\u4e4b\u529b\u5411\u6211\u5bfb\u4ec7\u4e86\uff1f\u201d\u7a46\u4e5d\u5a18\u4ee5\u524d\u56e0\u4e3a\u5077\u4e86\u7389\u7f57\u5239\u7684\u5251\u8c31\uff0c\u7ed9\u94c1\u98de\u9f99\u8d76\u51fa\u5bb6\u95e8\uff0c\u6240\u4ee5\u94c1\u98de \u9f99\u7591\u5979\u5fc3\u6000\u4e0d\u8f68\uff0c\u7ed3\u4eba\u5bfb\u4ec7\u3002 \u3000\u3000\u7a46\u4e5d\u5a18\u8138\u4e0a\u73b0\u51fa\u4e00\u79cd\u5947\u5f02\u7684\u795e\u60c5\uff0c\u5ffd\u7136\u53f9\u9053\uff1a\u201c\u8001\u7237\uff0c\u4f60\u8001\u4e86\u8bb8\u591a\u4e86\uff01\u201d\u94c1\u98de\u9f99\u5fc3\u4e2d\u4e00 \u52a8\uff0c\u9053\uff1a\u201c\u5973\u9b54\u5934\u662f\u4e0d\u662f\u548c\u4f60\u540c\u6765\uff0c\u6211\u4e14\u4e0d\u7ba1\uff0c\u73ca\u745a\u5462\uff01\u201d\u7a46\u4e5d\u5a18\u9053\uff1a\u201c\u6211\u6765\u7684\u65f6\u5019\uff0c\u89c1\u73ca \u745a\u4ece\u8fd9\u5e99\u7684\u80cc\u9762\u4e0b\u5c71\uff0c\u6211\u8fd8\u4ee5\u4e3a\u662f\u4f60\u5f97\u4e86\u8baf\u606f\uff0c\u8fde\u591c\u53eb\u73ca\u745a\u51fa\u53bb\u8bf7\u6551\u5175\u5462\u3002\u5230\u4e86\u8fd9\u91cc\uff0c\u624d\u77e5 \u4e0d\u662f\uff0c\u4f60\u770b\u684c\u4e0a\u4e0d\u662f\u73ca\u745a\u7559\u7ed9\u4f60\u7684\u5b57\uff1f\u201d\u94c1\u98de\u9f99\u4e00\u770b\uff0c\u679c\u7136\u6709\u4e00\u5f20\u5b57\u6761\uff0c\u4e0a\u9762\u7528\u6728\u70ad\u5199\u9053\uff1a \u201c\u6211\u5148\u56de\u5bb6\uff0c\u7239\u7239\u4f60\u4e0d\u5fc5\u627e\u6211\u4e86\u3002\u201d\u94c1\u98de\u9f99\u77e5\u9053\u5973\u513f\u813e\u6c14\uff0c\u6599\u60f3\u5979\u5df2\u53bb\u8fdc\uff0c\u8ffd\u4e5f\u65e0\u53ca\u3002\u770b\u7a46 \u4e5d\u5a18\u65f6\uff0c\u4ecd\u662f\u5148\u524d\u90a3\u4e2a\u59ff\u52bf\uff0c\u624b\u5fc3\u644a\u5f00\uff0c\u624b\u5fc3\u4e0a\u53c1\u9897\u6bb7\u7ea2\u5982\u8840\u7684\u73cd\u73e0\uff0c\u5728\u5fae\u5f31\u7684\u83dc\u6cb9\u706f\u4e0b\uff0c \u653e\u51fa\u8d64\u8272\u5149\u534e\uff01 \u3000\u3000\u7adf\u662f\u94c1\u98de\u9f99\u90a3\u6837\u5929\u4e0d\u6015\u5730\u4e0d\u6015\u7684\u4eba\uff0c\u770b\u4e86\u8fd9\u53c1\u9897\u602a\u5f02\u7684\u73cd\u73e0\uff0c\u4e5f\u4e0d\u7981\u6709\u70b9\u5fc3\u60b8\u3002\u7a46\u4e5d\u5a18 \u9053\uff1a\u201c\u8001\u7237\uff0c\u4f60\u8d81\u65e9\u9003\u8d70\u5427\uff01\u201d\u94c1\u98de\u9f99\u5927\u6012\u65a5\u9053\uff1a\u201c\u4f60\u8ddf\u4e86\u6211\u8fd9\u9ebd\u591a\u5e74\uff0c\u51e0\u66fe\u89c1\u6211\u907f\u8fc7\u5f3a \u654c\uff1f\u201d\u6b47\u4e86\u4e00\u9635\uff0c\u9762\u8272\u7a0d\u9701\uff0c\u5ffd\u9053\uff1a\u201c\u90a3\u4f60\u662f\u901a\u98ce\u62a5\u8baf\u6765\u4e86\uff01\u201d\u7a46\u4e5d\u5a18\u9053\uff1a\u201c\u4f60\u4ee5\u524d\u7684\u8bdd\u8fd8 \u7b97\u4e0d\u7b97\u6570\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6211\u8bf4\u51fa\u7684\u8bdd\u51b3\u4e0d\u66f4\u6539\uff0c\u4f60\u8ddf\u4ec0\u9ebd\u4eba\u6211\u90fd\u4e0d\u7406\u4f60\uff01\u201d\u7a46\u4e5d\u5a18\u9053\uff1a \u201c\u8c22\u8c22\u8001\u7237\u3002\u201d\u94c1\u98de\u9f99\u53cc\u773c\u671b\u51fa\u7a97\u5916\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u8ddf\u4ec0\u9ebd\u4eba\u6211\u90fd\u4e0d\u7ba1\u3002\u9664\u975e\u4f60\u81ea\u5df2\u8981\u56de\u6765\uff0c \u5426\u5219\u6211\u4e5f\u4e0d\u4f1a\u95ee\u4f60\u3002\u201d\u94c1\u98de\u9f99\u665a\u5e74\u5bc2\u5bde\uff0c\u8fd9\u8bdd\u5176\u5b9e\u662f\u6697\u793a\u8981\u5979\u56de\u6765\u3002\u7a46\u4e5d\u5a18\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u6211\u8ddf\u8001\u7237\u5341\u591a\u5e74\uff0c\u522b\u7684\u6ca1\u5b66\u5230\uff0c\u8001\u7237\u7684\u813e\u6c14\u6211\u8fd8\u5b66\u5f97\u51e0\u6210\u3002\u6211\u5c31\u7b97\u9519\u4e5f\u5f97\u9519\u5230\u5e95\u3002\u201d\u94c1\u98de \u9f99\u9762\u4e0a\u4e00\u70ed\uff0c\u9053\uff1a\u201c\u90a3\u4f60\u6765\u7ed9\u6211\u62a5\u8baf\u505a\u4ec0\u9ebd\uff01\u201d\u7a46\u4e5d\u5a18\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u8001\u7237\u80af\u653e\u6211\u51fa\u53bb\uff0c\u4e0d\u8981 \u6211\u518d\u5f53\u5974\u5a62\uff0c\u6211\u5ff5\u8001\u7237\u7684\u6069\u5fb7\uff0c\u4e0d\u613f\u89c1\u8001\u7237\u6b7b\u65bc\u975e\u547d\uff01\u201d\u94c1\u98de\u9f99\u76b4\u8d77\u7709\u5934\uff0c\u65a5\u9053\uff1a\u201c\u80e1\u8bf4\uff0c \u4f60\u5f53\u6211\u771f\u662f\u8001\u8fc8\u65e0\u80fd\u4e86\u9ebd\uff1f\u201d\u7a46\u4e5d\u5a18\u9053\uff1a\u201c\u8001\u7237\uff0c\u4f60\u7684\u6b66\u529f\u9ad8\u5f3a\uff0c\u6211\u5c82\u4e0d\u77e5\uff0c\u4f46\u6211\u7684\u5a46\u5a46\u5df2 \u7ec3\u6210\u4e86\u7ef5\u638c\u51fb\u77f3\u5982\u7c89\u7684\u529f\u592b\uff0c\u66f4\u517c\u6d78\u8fc7\u6bd2\u836f\uff0c\u8001\u7237\u8fd8\u662f\u907f\u5f00\u7684\u597d\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u53cc\u773c\u4e00\u7ffb\uff0c\u9053\uff1a\u201c\u4ec0\u9ebd\uff0c\u4f60\u7684\u5a46\u5a46\uff1f\u201d\u7a46\u4e5d\u5a18\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6211\u73b0\u5728\u662f\u7ea2\u82b1\u9b3c\u6bcd\u516c \u5b59\u5927\u5a18\u7684\u513f\u5ab3\uff1f\u201d\u94c1\u98de\u9f99\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u7f62\u4e86\uff01\u7f62\u4e86\uff01\u4f60\u5feb\u8d70\uff01\u201d\u7a46\u4e5d\u5a18\u9053\uff1a\u201c\u5979\u5df3\u77e5\u9053 \u4f60\u5728\u8fd9\u513f\uff0c\u660e\u5929\u665a\u4e0a\u5c31\u8981\u627e\u4f60\u7b97\u8d26\u3002\u5979\u548c\u91d1\u8001\u602a\u4e5f\u5df2\u7ecf\u548c\u597d\u4e86\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u597d\u5440\uff0c\u90a3\u4f60 \u4e5f\u8981\u6765\u548c\u6211\u4f5c\u5bf9\u4e86:\uff0e\u201d\u7a46\u4e5d\u5a18\u9053\uff1a\u201c\u6211\u4e0d\u6562\u4e0e\u8001\u7237\u4f5c\u5bf9\u3002\u4ed6\u4eec\u4e5f\u4e0d\u8981\u6211\u51fa\u573a\u3002\u8fd8\u6709\u6211\u90a3\u5a46 \u5a46\u813e\u6c14\u867d\u7136\u521a\u66b4\uff0c\u4f46\u4e5f\u50cf\u8001\u7237\u4f60\u4e00\u4e2a\u6837\u5b50\uff0c\u8fd8\u4e0d\u7b97\u662f\u5f88\u574f\u7684\u4eba\u3002\u6211\u4e0d\u613f\u5979\u6253\u6b7b\u4f60\uff0c\u4e5f\u4e0d\u613f\u4f60 \u6253\u6b7b\u5979\uff0c\u8001\u7237\u4f60\u8fd8\u662f\u907f\u5f00\u4e86\u5427\uff01\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5916\u9762\u4e00\u58f0\u6e05\u5578\uff0c\u94c1\u98de\u9f99\u9053\uff1a\u201c\u7389\u7f57\u5239\u5c31\u8981\u56de\u6765 \u4e86\uff0c\u4f60\u5feb\u8d70\uff01\u201d\u7a46\u4e5d\u5a18\u5403\u4e86\u4e00\u60ca\uff0c\u56de\u8eab\u4e00\u62dc\uff0c\u53eb\u9053\uff1a\u201c\u8001\u7237\uff0c\u4f60\u4fdd\u91cd\uff01\u201d\u7acb\u5373\u7a7f\u7a97\u98de\u51fa\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u7389\u7f57\u5239\u56de\u5230\u5bfa\u4e2d\u3002\u94c1\u98de\u9f99\u9053\uff1a\u201c\u89c1\u6709\u4ec0\u9ebd\u53ef\u7591\u7684\u8ff9\u8c61\u5417\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6ca1 \u6709\u3002\u53ea\u662f\u79d8\u9b54\u5d16\u90a3\u8fb9\uff0c\u4f3c\u6709\u661f\u661f\u677e\u706b\u3002\u8981\u4e0d\u8981\u53bb\u770b\u4e00\u770b\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4e0d\u5fc5\u4e86\uff0c\u6211\u5df2\u7ecf\u77e5 \u9053\u4e86\u3002\u201d\u7389\u7f57\u5239\u770b\u4e86\u5730\u4e0a\u4e00\u4e0b\u9053\uff1a\u201c\u662f\u4ec0\u9ebd\u4eba\u6765\u8fc7\u4e86\uff1f\u73ca\u745a  \u59b9\u5462\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u73ca\u745a\u5df2 \u7ecf\u8d70\u4e86\u3002\u521a\u624d\u662f\u7a46\u4e5d\u5a18\u6765\u627e\u6211\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u7a46\u4e5d\u5a18\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6b63\u662f\u3002\u4f60\u542c\u8fc7\u7ea2\u82b1 \u9b3c\u6bcd\u516c\u5b59\u5927\u5a18\u7684\u540d\u5b57\u5417\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6ca1\u6709\u542c\u8fc7\u3002\u8fd9\u4e2a\u540d\u5b87\u597d\u602a\uff0c\u6211\u7684\u6d51\u540d\u53eb\u505a\u7f57\u5239\u5df2\u7ecf \u591f\u5413\u4eba\u7684\u4e86\uff0c\u5c45\u7136\u8fd8\u6709\u4eba\u53eb\u505a\u9b3c\u6bcd\u3002\u6211\u8fd9\u4e2a\u7f57\u5239\u5012\u8981\u4f1a\u4f1a\u5979\u8fd9\u4e2a\u9b3c\u6bcd\u3002\u201d\u94c1\u98de\u9f99\u7ed9\u5979\u5f15\u5f97\u7b11 \u4e86\u4e00\u7b11\uff0c\u5ffd\u53c8\u6b63\u5bb9\u8bf4\u9053\uff1a\u201c\u5979\u8fd9\u4e2a\u9b3c\u6bcd\u6bd4\u4f60\u8fd9\u4e2a\u7f57\u5239\u6210\u540d\u65e9\u5f97\u591a\u4e86\u3002\u5979\u5728\u56db\u5341\u5e74\u524d\u5df2\u7ecf\u88ab\u4eba \u53eb\u505a\u7ea2\u82b1\u9b3c\u6bcd\u4e86\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u5979\u5230\u5e95\u662f\u4ec0\u9ebd\u6765\u5386\uff1f\u6211\u5e74\u7eaa\u867d\u8f7b\uff0c\u6c5f\u6e56\u4e0a\u7684\u9ad8\u4eba\u5012\u4f1a\u4e86\u4e0d \u5c11\uff0c\u4e3a\u4f55\u603b\u672a\u542c\u8fc7\u7ea2\u82b1\u9b3c\u6bcd\u7684\u540d\u5b87\uff1f\u201d\uff0e \u94c1\u98de\u9f99\u634b\u4e86\u634b\u987b\uff0c\u62ac\u8d77\u773c\u6765\uff0c\u773c\u5149\u4e2d\u542b\u7740\u5fe7\u60e7\uff0c \u7389\u7f57\u5239\u5403\u4e86\u4e00\u60ca\uff0c\u5947\u9053\uff1a\u201c\u7239\u7239\uff0c\u96be\u9053\u4f60\u6015\u8fd9\u4e2a\u4ec0\u9ebd\u9b3c\u6bcd\u4e0d\u6210\uff1f\u201d \u3000\u3000\u94c1\u98de\u9f99\u76b1\u8d77\u7709\u5934\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ec0\u9ebd\u4eba\u6211\u90fd\u4e0d\u6015\u3002\u4f46\u8fd9\u4e2a\u7ea2\u82b1\u9b3c\u6bcd\u5374\u771f\u662f\u4e00\u4e2a\u52b2\u654c\u3002\u7ec3 \u5973\u4fa0\uff0c\u4f60\u5750\u4e0b\u6765\uff0c\u6211\u7ed9\u4f60\u8bf4\u4e00\u4e2a\u6545\u4e8b\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u5750\u5728\u5e8a\u6cbf\uff0c\u6014\u6014\u7684\u671b\u7740\u94c1\u98de\u9f99\u3002\u94c1\u98de\u9f99\u559d\u4e86\u4e00\u53e3\u6d53\u837c\uff0c\u54b3\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u77e5\u9053\u8fd9 \u51e0\u5341\u5e74\u6765\uff0c\u6211\u548c\u91d1\u8001\u602a\u5728\u897f\u5317\u9f50\u540d\u3002\u4f46\u4f60\u53ef\u77e5\u9053\u91d1\u8001\u602a\u7684\u6b66\u529f\u662f\u8c01\u6559\u7684\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60 \u4eec\u90fd\u662f\u516d\u5341\u5f00\u5916\u4e4b\u4eba\uff0c\u6211\u600e\u80fd\u77e5\u9053\u524d\u4e24\u4ee3\u7684\u4e8b\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u91d1\u8001\u602a\u7684\u6b66\u529f\u662f\u4ed6\u7684\u59bb\u5b50\u6559 \u7684\u3002\u4ed6\u7684\u59bb\u5b50\u5c31\u662f\u8fd9\u4e2a\u7ea2\u82b1\u9b3c\u6bcd\u516c\u5b59\u5927\u5a18\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u59bb\u5b50\u505a\u4e08\u592b\u7684\u5e08\u7236\uff0c\u6b64\u4e8b\u771f \u5999\u3002\u201d\u5fc3\u4e2d\u6697\u60f3\uff1a\u81ea\u5df1\u82e5\u80fd\u548c\u5353\u4e00\u822a\u7ed3\u5408\uff0c\u53ea\u6015\u5353\u4e00\u822a\u4e5f\u5f97\u8981\u81ea\u5df2\u6559\u4ed6\u4e00\u6559\u3002\u60f3\u8d77\u4e00\u4e8b\uff0c\u53c8 \u95ee\u9053\uff1a\u201c\u5973\u4eba\u5ac1\u5f8c\uff0c\u591a\u662f\u7528\u4e08\u592b\u4e4b\u59d3\uff0c\u4e3a\u4ec0\u9ebd\u5979\u4e0d\u53eb\u91d1\u5927\u5a18\u5374\u53eb\u516c\u5b59\u5927\u5a18\uff1f\u201d \u3000\u3000\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6545\u4e8b\u5c31\u662f\u8fd9\u6837\u6765\u7684\u3002\u56db\u5341\u5e74\u524d\uff0c\u897f\u5317\u6709\u4e2a\u602a\u4eba\u53eb\u505a\u516c\u5b59\u4e00\u9633\uff0c\u6b66\u529f\u6df1\u4e0d\u53ef \u6d4b\uff0c\u53c8\u559c\u9972\u517b\u6bd2\u7269\uff0c\u6240\u4ee5\u4eba\u4eba\u6015\u4ed6\u3002\u4ed6\u6709\u8bb8\u591a\u5f92\u5f1f\uff0c\u5374\u6ca1\u4e00\u4e2a\u5f97\u4ed6\u771f\u4f20\u3002\u6211\u7684\u5e08\u7236\u662f\u4ed6\u7684\u8001 \u53cb\uff0c\u636e\u4ed6\u8bf4\u516c\u5b59\u4e00\u9633\u66fe\u5bf9\u4ed6\u8bf4\uff1a\u4ed6\u7684\u6b66\u529f\u751a\u4e3a\u6b79\u6bd2\uff0c\u82e5\u7136\u6240\u4f20\u975e\u4eba\uff0c\u4e3a\u5bb3\u4e0d\u6d45\u3002\u6240\u4ee5\u6559\u5f92\u5f1f \u53ea\u6559\u4ed6\u4eec\u7ec3\u4e9b\u7c97\u6d45\u5bb9\u6613\u89c1\u6548\u7684\u529f\u592b\uff0c\u4ece\u4e0d\u6388\u4ee5\u672c\u95e8\u5fc3\u6cd5\u3002\u4e0d\u60f3\u5f8c\u6765\u6765\u4e86\u4e00\u4e2a\u9752\u5e74\uff0c\u62dc\u5728\u4ed6\u7684 \u95e8\u4e0b\uff0c\u7adf\u7136\u628a\u4ed6\u7684\u5973\u513f\u52fe\u5f15\u5230\u624b\uff0c\u4e24\u4eba\u5c06\u516c\u5b59\u4e00\u9633\u7684\u7ec3\u529f\u79d8\u672c\u5077\u6389\u3002\u516c\u5b59\u4e00\u9633\u53ea\u6709\u6b64\u5973\uff0c\u5341 \u5206\u5b9d\u8d1d\uff0c\u5c31\u50cf\u6211\u5bf9\u73ca\u745a\u4e00\u6837\u3002\u77e5\u9053\u4e4b\u5f8c\uff0c\u867d\u7136\u6781\u4e3a\u751f\u6c14\uff0c\u4f46\u4e5f\u4e0d\u613f\u8ffd\u7a76\uff0c\u5c31\u8fd9\u6837\u6d3b\u6d3b\u6c14\u6b7b \u4e86\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8fd9\u4e2a\u9752\u5e74\u4e00\u5b9a\u5c31\u662f\u5f8c\u6765\u7684\u91d1\u8001\u602a\u4e86\u3002\u539f\u6765\u4ed6\u662f\u60ef\u7a83\u3002\u602a\u4e0d\u5f97\u4ed6\u5077\u6211\u5e08\u7236 \u7684\u5251\u8c31\uff0c\u53c8\u60f3\u53bb\u5077\u5c11\u6797\u5bfa\u7684\u62f3\u7ecf\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u53c1\u5c81\u5c0f\u513f\u770b\u516b\u5341\uff0c\u91d1\u8001\u602a\u5c11\u5e74\u4e4b\u65f6\u5fc3\u672f\u5df2 \u5982\u6b64\u4e4b\u574f\uff0c\u8d8a\u8001\u5c31\u5f53\u7136\u8d8a\u574f\u4e86\u3002\u4ed6\u5506\u4f7f\u59bb\u5b50\u5077\u4e86\u4e08\u4eba\u7684\u7ec3\u529f\u79d8\u672c\u4e4b\u5f8c\uff0c\u5c31\u8eb2\u5230\u5929\u5c71\u5317\u8def\uff0c\u9690 \u5c45\u4fee  \u3002\u90a3\u65f6\u4ed6\u7684\u6b66\u529f\u521a\u521a\u5165\u95e8\uff0c\u800c\u4ed6\u59bb\u5b50\u7684\u6b66\u529f\u5df2\u6709\u6839\u67e2\uff0c\u6240\u4ee5\u4ed6\u7684\u529f\u592b\u53ef\u4ee5\u8bf4\u662f\u5168\u7531\u59bb \u5b50\u6240\u6388\u3002\u8fc7\u4e86\u5341\u9980\u5e74\u5f8c\uff0c\u592b\u59bb\u6b66\u529f\u90fd\u5df2\u7ec3\u6210\u3002\u91d1\u72ec\u5f02\u60ed\u60ed\u4e3a\u975e\u4f5c\u6b79\uff0c\u7ec8\u65bc\u6fc0\u8d77\u6b66\u6797\u516c\u6124\uff0c\u897f \u5317\u5341\u53c1\u540d\u597d\u624b\u8054\u624b\u6597\u4ed6\uff0c\u90a3\u65f6\u672c\u9080\u6709\u6211\uff0c\u6211\u5374\u56e0\u4e8b\u672a\u53bb\u3002\u90a3\u5341\u53c1\u540d\u597d\u624b\u628a\u4ed6\u56f4\u4f4f\uff0e\uff0c\u672c\u6765\u4ed6 \u4e07\u96be\u9003\u8131\uff0c\u4e0d\u6599\u5230\u4e86\u5371\u6025\u4e4b\u65f6\uff0c\u4ed6\u7684\u59bb\u5b50\u7a81\u7136\u73b0\u8eab\uff0c\u4e00\u573a\u6fc0\u6597\uff0c\u5c06\u5341\u53c1\u5404\u597d\u624b\u5168\u6570\u6253\u8d25\uff0c\u91d1 \u72ec\u5f02\u867d\u7136\u53d7\u4e86\u91cd\u4f24\uff0c\u5230\u5e95\u88ab\u4ed6\u7684\u59bb\u5b50\u6551\u51fa\u6765\u4e86\u3002\u516c\u5b59\u5927\u5a18\u9b13\u8fb9\u559c\u63d2\u7ea2\u82b1\uff0c\u7ecf\u6b64\u4e00\u4ed7\uff0c\u5c31\u5f97\u4e86 \u4e2a\u7ea2\u82b1\u9b3c\u6bcd\u7684\u7ef0\u53f7\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u7ea2\u82b1\u9b3c\u6bcd\u6b66\u529f\u867d\u9ad8\uff0c\u5305\u5e87\u4e08\u592b\uff0c\u5374\u662f\u4ee4\u4eba\u53f9\u606f\u3002\u201d\u94c1\u98de \u9f99\u9053\uff1a\u201c\u7ea2\u82b1\u9b3c\u6bcd\u7684\u7ef0\u53f7\u867d\u7136\u53ef\u6015\uff0c\u8bf4\u53e5\u516c\u9053\u7684\u8bdd\uff0c\u5fc3\u672f\u5374\u4e0d\u5982\u5979\u4e08\u592b\u4e4b\u574f\u3002\u5979\u66fe\u5c61\u6b21\u89c4\u529d \u4e08\u592b\uff0c\u4e08\u592b\u90fd\u4e0d\u542c\u5979\u3002\u6240\u4ee5\u90a3\u6b21\u91d1\u8001\u602a\u53d7\u5341\u53c1\u540d\u597d\u624b\u56f4\u653b.\u5979\u6545\u610f\u8ba9\u4ed6\u5230\u4e86\u6781\u5371\u6025\u4e4b\u65f6\u624d\u73b0 \u8eab\u76f8\u6551\uff0c\u672c\u610f\u4ee5\u4e3a\u4ed6\u53d7\u4e86\u8fd9\u6837\u4e00\u573a\u6559\u8bad\uff0c\u4f1a\u6709\u6240\u8b66\u60d5\uff0c\u5e61\u7136\u6539\u609f\u3002\u4e0d\u6599\u91d1\u8001\u602a\u7279\u6709\u59bb\u5b50\u505a\u9760 \u5c71\uff0c\u4f24\u597d\u4e4b\u5f8c\uff0c\u53c8\u51fa\u53bb\u80e1\u4f5c\u975e\u4e3a\uff0c\u56e0\u6b64\u4ed6\u7684\u59bb\u5b50\u4e00\u6c14\u4e4b\u4e0b\uff0c\u4fbf\u548c\u4ed6\u76f8\u7edd\u3002\u4e00\u76f4\u53c1\u5341\u591a\u5e74\uff0c\u6ca1 \u4eba\u77e5\u9053\u5979\u7684\u8e2a\u8ff9\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u5401\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5514\uff0c\u90a3\u8fd9\u7ea2\u82b1\u9b3c\u6bcd\uff0c\u8fd8\u4e0d\u80fd\u7b97\u662f\u5f88\u574f\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u7ea2\u82b1\u9b3c \u6bcd\u79bb\u5f00\u4e08\u592b\u4e4b\u5f8c\uff0c\u4e0d\u613f\u4ee5\u592b\u59d3\u4e3a\u59d3\uff0c\u6240\u4ee5\u624d\u6539\u540d\u53eb\u516c\u5b59\u5927\u5a18\u3002\u9690\u5c45\u7684\u5934\u5341\u5e74\uff0c\u8fd8\u51fa\u73b0\u8fc7\u4e24\u53c1 \u6b21\uff0c\u5f8c\u6765\u5c31\u4e00\u76f4\u6ca1\u6709\u51fa\u73b0\u3002\u8bb8\u591a\u4eba\u4ee5\u4e3a\u5979\u5df2\u6b7b\u6389\u4e86\u3002\u8c01\u77e5\u5979\u8fd8\u5728\u4eba\u95ee\uff0c\u800c\u4e14\u5c45\u7136\u8981\u6765\u548c\u6211\u4f5c \u5bf9\uff0c\u53c8\u6599\u4e0d\u5230\u5979\u8fd8\u6709\u4e86\u4e00\u4e2a\u513f\u5b50\uff0c\u5c45\u7136\u4f1a\u5a36\u7a46\u4e5d\u5a18\u505a\u59bb\u5b50\u3002\u771f\u662f\u4e16\u60c5\u5982\u620f\uff0c\u4ee4\u4eba\u4e0d\u80dc\u611f\u6168 \u4e86\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u4e0d\u77e5\uff0c\u539f\u6765\u7a46\u4e5d\u5a18\u79bb\u5f00\u4e86\u4ed6\u4e4b\u5f8c\uff0c\u7ed9\u91d1\u5343  \u4e00\u8def\u8ffd\u8e2a\uff0c\u8ffd\u5230\u6e56\u5317\u8944\u9633\uff0c\u78b0\u89c1\u4e86\u7ea2 \u82b1\u9b3c\u6bcd\uff0c\u91d1\u5343  \u6700\u6015\u4ed6\u7684\u5a76\u5a76\uff0c\u7ed9\u5979\u6559\u8bad\u4e00\u987f\uff0c\u62b1\u5934\u800c\u7a9c\u3002\u4f46\u7ea2\u82b1\u9b3c\u6bcd\u4e5f\u7531\u91d1\u5343  \u53e3\u4e2d\u77e5\u9053 \u4e86\u4e08\u592b\u7684\u6d88\u606f\uff0c\u5f15\u8d77\u4e86\u65e7\u60c5\uff0c\u77e5\u4ed6\u5c06\u8981\u4eba\u4eac\uff0c\u4fbf\u8d76\u5148\u5165\u4eac\u5019\u4ed6\u3002\u8fd9\u91cc\u9762\u53c8\u7275\u6d89\u6709\u4e00\u6bb5\u4e8b\u60c5\u3002 \u539f\u6765\u7ea2\u82b1\u9b3c\u6bcd\u79bb\u5f00\u4e08\u592b\u4e4b\u65f6\uff0c\u5df2\u6709\u8eab\u5b55\uff0c\u5f8c\u6765\u751f\u4e0b\u4e00\u5b50\uff0c\u53d6\u540d\u516c\u5b59\u96f7\uff0c\u6545\u610f\u4e0d\u8ba9\u4ed6\u8ddf\u4e08\u592b\u7684 \u59d3\u3002\u4e0d\u6599\u8fd9\u4e2a\u513f\u5b50\u597d\u50cf\u627f\u53d7\u4e86\u7236\u4eb2\u7684\u9057\u4f20\u4e00\u6837\uff0c\u81ea\u5c0f\u987d\u52a3\uff0c\u95ef\u4e86\u597d\u51e0\u6b21\u7978\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u5f8c\u6765\u7acb \u4e0b\u7981\u5f8b\uff0c\u4e0d\u51c6\u4ed6\u79bb\u5bb6\u534a\u6b65\uff0c\u8fd9\u624d\u7ba1\u675f\u4e86\u4ed6\u7684\u91ce\u6027\u3002\u7f38\u82b1\u9b3c\u6bcd\u56e0\u4e3a\u513f\u5b50\u987d\u52a3\uff0c\u5230\u4e86\u665a\u5e74\uff0c\u53c8\u6536 \u4e86\u4e00\u4e2a\u5973\u5f92\uff0c\u8fd9\u4e2a\u5973\u5f92\u5f1f\u5927\u6709\u6765\u5934\uff0c\u5c31\u662f\u5f53\u4eca\u7687\u4e0a\u7684\u4e73\u5a18\u5ba2\u6c0f\u592b\u4eba\u7684\u5973\u513f\u3002\u7ea2\u82b1\u9b3c\u6bcd\u6536\u5979\u505a \u5f92\u5f1f\u65f6\uff0c\u5ba2\u6c0f\u5728\u5bab\u4e2d\u8fd8\u672a\u5f97\u5ba0\u5462\u3002 \u3000\u3000\u7a46\u4e5d\u5a18\u7ed9\u516c\u5b59\u5927\u5a18\u6536\u5bb9\u4e4b\u5f8c\uff0c\u516c\u5b59\u96f7\u56e0\u4e3a\u7ed9\u4e25\u6bcd\u7ba1\u675f\u5df2\u4e45\uff0c\u672a\u66fe\u89c1\u8fc7\u8fd9\u6837\u7f8e\u8c8c\u7684\u5973\u5b50\uff0c \u66f4\u517c\u7a46\u4e5d\u5a18\u4eba\u53c8\u98ce\u9a9a\uff0c\u4e0d\u5230\u53c1\u5929\uff0c\u4e24\u4eba\u7adf\u52fe\u642d\u4e0a\u4e86\u3002\u516c\u5b59\u5927\u5a18\u867d\u7136\u67e5\u77e5\u7a46\u4e5d\u5a18\u4e43\u662f\u94c1\u98de\u9f99\u7684 \u7231\u59be\uff0c\u672c\u6765\u4e0d\u76f8\u5339\u914d\uff0c\u4f46\u65e0\u5948\u7c73\u5df2\u6210\u708a\uff0c\u4e5f  \u597d\u7531\u4ed6\u4eec\u7ed3\u6b64\u5b7d\u7f18\u3002 \u3000\u3000\u516c\u5b59\u96f7\u548c\u7a46\u4e5d\u5a18\u5a5a\u5f8c\u4e0d\u4e45\uff0c\u795e\u5b97\u9a7e\u5d29\uff0c\u5149\u5b97\u7ee7\u4f4d\uff0c\u5ba2\u6c0f\u5728\u5bab\u4e2d\u5f97\u52bf\uff0c\u4fbf\u63a5\u81ea\u5df1\u7684\u5973\u513f\u4eba \u4eac\u3002\u516c\u5b59\u5927\u5a18\u4e5f\u4fbf\u8d81\u6b64\u673a\u4f1a\uff0c\u4eba\u4e86\u5bab\u5ef7\u3002\u5f8c\u6765\u5149\u5b97\u53c8\u6b7b\uff0c\u7531\u6821\u7ee7\u4f4d\uff0c\u5ba2\u6c0f\u66f4\u662f\u5f97\u52bf\u3002\u516c\u5b59\u5927 \u5a18\u770b\u51fa\u9b4f\u5fe0\u8d24\u548c\u5ba2\u6c0f\u52fe\u642d\uff0c\u98a0\u5012\u671d\u7eb2\uff0c\u5f53\u65f6\u4fbf\u60f3\u79bb\u5bab\u3002\u53ef\u662f\u9002\u5728\u8fd9\u65f6\u91d1\u72ec\u5f02\u6765\u4e86\uff0c\u516c\u5b59\u5927\u5a18 \u5077\u5077\u548c\u4ed6\u4f1a\u9762\uff0c\u529d\u4ed6\u5f52\u53bb\u3002\u91d1\u72ec\u5f02\u8bf4\u51fa\u94c1\u98de\u9f99\u548c\u7389\u7f57\u5239\u4e07\u91cc\u8ffd\u8e2a\uff0c\u8feb\u4ed6\u4e4b\u4e8b\u3002\u516c\u5b59\u5927\u5a18\u521d\u65f6 \u672c\u4e0d\u60f3\u7ba1\uff0c\u5f8c\u6765\u5728\u6768\u5bb6\u4e00\u6218\uff0c\u91d1\u72ec\u5f02\u5403\u4e86\u5927\u4e8f\uff0c\u53d7\u4e86\u91cd\u4f24\uff0c\u56de\u6765\u65f6\u5bf9\u59bb\u5b50\u54ed\u8bc9\uff0c\u8bf4\u662f\u9664\u975e\u59bb \u5b50\u7ed9\u4ed6\u62a5\u4e86\u6b64\u4ec7\uff0c\u5426\u5219\u4ed6\u4e0d\u56de\u5bb6\u3002\u53c8\u8bf4\u94c1\u98de\u9f99\u4e0e\u7389\u7f57\u5239\u5728\u6c5f\u6e56\u4e0a\u90fd\u4ee5\u5fc3\u72e0\u624b\u8fa3\u51fa\u540d\uff0c\u82e5\u4e0d\u65a9 \u8349\u9664\u6839\uff0c\u4ee5\u5f8c\u4e5f\u96be\u4ee5\u5b89\u6795\u3002\u516c\u5b59\u5927\u5a18\u5fc3\u80a0\u4e00\u8f6f\uff0c\u9053\uff1a\u201c\u6211\u5e2e\u4f60\u7684\u5fd9\uff0c\u8fd9\u662f\u6700\u5f8c\u4e00\u6b21\u4e86\u3002\u90a3\u94c1 \u98de\u9f99\u4e5f\u662f\u4e2a\u52b2\u654c\uff0c\u6211\u4e5f\u62ff\u4e0d\u51c6\u6597\u5f97\u8d62\u4ed6\u5462\u3002\u201d\u91d1\u72ec\u5f02\u9053\uff1a\u201c\u4f60\u82e5\u80af\u51fa\u5934\uff0c\u6211\u518d\u8bf7\u597d\u624b\u52a9 \u4f60\u3002\u201d\u516c\u5b59\u5927\u5a18\u9762\u8272\u4e00\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4ece\u4e0d\u501a\u591a\u4e3a\u80dc\uff0c\u4f60\u82e5\u627e\u597d\u624b\u6765\uff0c\u6211\u5c31\u4e0d\u53bb\uff01\u201d\u91d1\u72ec\u5f02 \u8bfa\u8bfa\u8fde\u58f0\uff0c\u6ee1\u53e3\u542c\u4ece\u59bb\u5b50\u7684\u5429\u5490\uff0c\u6697\u4e2d\u5374\u53e6\u6709\u5e03\u7f6e\u4e0d\u63d0\u3002 \u3000\u3000\u4e14\u8bf4\u94c1\u98de\u9f99\u628a\u7ea2\u82b1\u9b3c\u6bcd\u516c\u5b59\u5927\u5a18\u7684\u6765\u5386\u8bf4\u5b8c\u4e4b\u5f8c\uff0c\u53c8\u53f9\u9053\uff1a\u201c\u7ea2\u82b1\u9b3c\u6bcd\u7684\u672c\u6027\u539f\u4e0d\u7b97\u5f88 \u574f\uff0c\u4f46\u6700\u6015\u5979\u53d7\u4e08\u592b\u5506\u6446\uff0c\u90a3\u5c31\u96be\u8bf4\u4e86\u3002\u5979\u4e0d\u52a8\u624b\u5219\u5df2\uff0c\u4e00\u52a8\u4e86\u624b\uff0c\u5c31\u662f\u51f6\u72e0\u65e0\u6bd4\uff0c\u8981\u4e0d\u7136 \u4e5f\u4e0d\u4f1a\u5f97\u8fd9\u4e2a\u9b3c\u6bcd\u7684\u79f0\u547c\u4e86\uff1f\u201d\u7389\u7f57\u5239\u542c\u4e86\uff0c\u54c8\u54c8\u5927\u7b11\uff01 \u3000\u3000\u94c1\u98de\u9f99\u8be7\u9053\uff1a\u201c\u7ec3\u5973\uff0c\u4f60\u7b11\u4ec0\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u7f57\u5239\u78b0\u5230\u9b3c\u6bcd\uff0c\u4e14\u770b\u8c01\u5f3a\u8c01\u5f31\u3002\u7239\uff0c \u6211\u6068\u4e0d\u5f97\u73b0\u5728\u5c31\u6597\u5979\u4e00\u6597\uff01\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u660e\u65e5\u5348\u65f6\u4f60\u4e0d\u662f\u548c\u767d\u77f3\u9053\u4eba\u6709\u7ea6\u5417\uff1f\u4f60\u6597\u4e86\u767d\u77f3 \u9053\u4eba\u4e4b\u5f8c\uff0c\u665a\u4e0a\u600e\u80fd\u518d\u6597\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u5979\u4eec\u4f4f\u5728\u79d8\u9b54\u5d16\u76d1\u89c6\u6211\u4eec\u5417\uff1f\u6211\u4eec\u660e\u5929 \u53bb\uff0c\u65e2\u6597\u767d\u77f3\u9053\u4eba\uff0c\u53c8\u6597\u7ea2\u82b1\u9b3c\u6bcd\uff0c\u4e24\u6869\u4e8b\u4f5c\u4e00\u6869\u529e\uff0c\u5c82\u4e0d\u5feb\u54c9\uff1f\u7239\uff0c\u6211\u81ea\u4ece\u548c\u4f60\u6253\u4e86\u90a3\u573a \u4e4b\u5f8c\uff0c\u5f88\u4e45\u4ee5\u6765\uff0c\u6ca1\u6709\u75db\u75db\u5feb\u5feb\u7684\u5927\u6253\u4e00\u573a\u4e86\uff01\u6211\u6b63\u624b\u75d2\u5f97\u7d27\u5462\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u76b4\u4e86\u76b1\u7709\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u5b69\u5b50\uff0c\u5c31\u77e5\u6253\u67b6\uff01\u201d\u53e3\u867d\u8d23\u5907\uff0c\u5fc3\u5b9e\u7231\u5979\u3002\u7389\u7f57\u5239\u9053\uff1a \u201c\u7239\uff0c\u660e\u5929\u8ba9\u6211\u5148\u6253\uff01\u201d\u94c1\u98de\u9f99\u7a81\u7136\u8d70\u8fd1\u7a97\u524d\uff0c\u5411\u5916\u4e00\u671b\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u5feb\u8fd1\u56db\u66f4\u4e86\uff0c\u8fd8\u6765 \u5f97\u53ca\uff01\u201d\u7389\u7f57\u5239\u95ee\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8bf4\u4ec0\u9ebd\uff1f  \u8981\u542c\u8bf4\u6709\u5bf9\u624b\u53ef\u4ee5\u5927\u6253\u4e00\u573a\uff0c\u6211\u7684\u7cbe\u795e\u5c31\u6765\u4e86\uff0c \u5c31\u662f\u53c1\u5929\u53c1\u591c\u4e0d\u7761\uff0c\u6211\u4e5f\u53ef\u4ee5\u5949\u966a\uff01\u201d\u94c1\u98de\u9f99\u5657\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u5c31\u6d3b\u50cf\u6211\u5c11\u5e74\u4e4b\u65f6\uff01\u201d\u5ffd \u53c8\u9762\u8272\u4e00\u7aef\uff0c\u90d1\u91cd\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6015\u4f60\u6ca1\u7cbe\u795e\uff0c\u6211\u662f\u8981\u53eb\u4f60\u53bb\u6267\u836f\u3002\u201d\u7389\u7f57\u5239\u5947\u9053\uff1a\u201c\u6267 \u836f\uff0c\u6267\u4ec0\u9ebd\u836f\uff1f\u67b6\u8fd8\u6ca1\u6253\uff0c\u5c31\u51c6\u5907\u53d7\u4f24\u4e86\u9ebd\uff01\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u513f\u5440\uff0c\u4f60\u90a3\u91cc\u77e5\u9053\u7ea2\u82b1\u9b3c\u6bcd\u7684 \u5389\u5bb3\uff01\u5979\u7684\u6bd2\u7802\u638c\u6bd4\u91d1\u8001\u602a\u8981\u9ad8\u660e\u5f97\u591a\uff0c\u66f4\u517c\u7ec3\u6709\u7ef5\u638c\u51fb\u77f3\u5982\u7c89\u7684\u529f\u592b\uff0c\u82e5\u975e\u65e9\u6709\u9884\u9632\uff0c\u5b9e \u5728\u4e0d\u6613\u62b5\u6321\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u600e\u9ebd\u9884\u9632\u5462\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u8d76\u5230\u57ce\u91cc\u53bb\uff0c\u5148\u5230\u957f\u5b89\u9556\u5c40\u5411 \u9f99\u8fbe\u53c1\u9556\u5e08\u501f\u4e24\u526f\u62a4\u5fc3\u94dc\u955c\uff0c\u9f99\u9556\u5e08\u662f\u6211\u7684\u597d\u53cb\uff0c\u4f60\u62ff\u6211\u7684\u4eb2\u7b14\u4fe1\u53bb\uff0c\u4ed6\u51c6\u4f1a\u7ed9\u4f60\u3002\u7136\u5f8c\u7b49 \u5929\u4e00\u4eae\uff0c\u4f60\u5c31\u53bb\u914d\u836f\u3002\u201d\u8bf4\u7f62\u6495\u4e0b\u4e24\u5e45\u767d\u886c\u8863\uff0c\u627e\u4e86\u4e00\u6839\u6728\u70ad\uff0c\u5148\u5199\u4e86\u4fe1\uff0c\u7136\u5f8c\u5f00\u836f\u65b9\u3002\u5199 \u7684\u662f\uff1a\u4e73\u9999\u201c\u94b1\u534a\u53bb\u6cb9\u201d\u3001\u672b\u836f\u201c\u94b1\u534a\u53bb\u6cb9\u201d\uff0c\u5ddd\u8fde\u201c\u94b1\u534a\u201d\u3001\u571f\u5fc5\u201c\u94b1\u534a\u9152\u7092\u201d\u3001\u8c61\u80c6\uff01 \u94b1\u201d\u3001\u7ea2\u82b1\u201c\u94b1\u534a\u9152\u7092\u201d\u3001\u7530\u4e03\u201c\u94b1\u534a\u201d\u3001\u6c89\u9999\u201c\u94b1\u534a\u201d\u3001\u6728\u9999\u201c\u94b1\u534a\u201d\uff0e\u964d\u9999\u201c\u94b1\u534a\u201d\u3001 \u8840\u73c0\u201c\u4e8c\u94b1\u534a\uff0c\u7eff\u8c46\u6c34\u7172\u201d\u3001\u5f52\u5c3e\u201c\u94b1\u534a\u9152\u7092\u201d\u3001\u5730\u9f99\u201c\u4e00\u94b1\u53bb\u6ce5\u201d\uff0c\u5bc4\u5974\u201c\u4e8c\u94b1\u9152 \u7092\u201d\uff0e\u718a\u80c6\u201c\u94b1\u534a\u201d\u3001\u9e9d\u9999\u201c\u53c1\u5206\u201d\uff0c\u4eba\u53c2\u201c\u56db\u5206\u201d\u3001\u679a\u7247\u201c\u4e94\u5206\u201d\u2026\u2026\u7389\u7f57\u5239\u53eb\u8d77\u6765\u9053\uff1a \u201c\u8fd9\u9ebd\u591a\u836f\uff0c\u82e5\u914d\u4e0d\u9f50\u53c8\u600e\u9ebd\u529e\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u8fd9\u836f\u65b9\u9664\u4e86\u4e00\u4e24\u5473\u5916\uff0c\u5176\u4ed6\u90fd\u662f\u666e\u901a\u7684 \u836f\uff0c\u82e5\u914d\u4e0d\u9f50\uff0c\u4f60\u5c31\u8bf7\u9f99\u9556\u5e08\u5e2e\u5fd9\u3002\u836f\u65b9\u8fd8\u672a\u5f00\u5b8c\u5462\u3002\u201d\u53c8\u6dfb\u4e0a\uff1a\u7f8c\u6d3b\u201c\u94b1\u534a\u201d\uff0c\u72ec\u6d3b\u201c\u94b1 \u534a\u201d\u3001\u4f5b\u624b\u201c\u4e00\u94b1\u201d\u3001\u7389\u6842\u201c\u94b1\u534a\u201d\uff0c\u539a  \u201c\u4e00\u94b1\u9152\u7092\u201d\u3001\u9e7f\u8338\u201c\u4e00\u94b1\u201d\uff0c\u8299\u84c9\u818f\u201c\u56db \u5206\u201d\u3002\u7389\u7f57\u5239\u76b1\u7709\u9053\uff1a\u201c\u6ca1\u6709\u4e86\u5427\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u836f\u65b9\u914d\u5b8c\u4e86\uff0c\u4f46\u8fd8\u8981\u4e70\u4e24\u5757\u96c4\u9ec4\u3002\u836f\u65b9 \u914d\u9f50\u4e4b\u5f8c\uff0c\u5c31\u5728\u9556\u5c40\u91cc\u7814\u4e3a\u7ec6\u672a\uff0c\u70bc\u871c\u4e3a\u4e38\u597d\u4e86\u3002\u660e\u5929\u8fd9\u573a\u6fc0\u6597\uff0c\u6211\u4eec\u5b9a\u4f1a\u53d7\u4f24\uff0c\u8fd9\u836f\u65b9\u662f \u8212\u7b4b\u6d3b\u7edc\uff0c\u6b62\u75db\u6563\u7600\uff0c\u6cbb\u4f24\u9632\u75e8\u7684\u5999\u65b9\u3002\u4f60\u8d76\u7d27\u53bb\u5427\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u8fd9\u8fb9\u7d27\u5f20\u5fd9\u788c\uff0c\u767d\u77f3\u9053\u4eba\u90a3\u8fb9\u4e5f\u662f\u63d0\u5fc3\u540a\u80c6\uff0c\u5c24\u5176\u662f\u767d\u77f3\u9053\u4eba\u7684\u5973\u513f\u4f55\u843c\u534e\uff0c\u542c \u8bf4\u7236\u4eb2\u548c\u6c5f\u6e56\u4e0a\u95fb\u540d\u80c6\u843d\u7684\u5973\u9b54\u5934\u7389\u7f57\u5239\u7ea6\u6597\uff0c\u975e\u5e38\u4e0d\u5b89\u3002\u767d\u77f3\u9053\u4eba\u6545\u4f5c\u9547\u5b9a\uff0c\u5176\u5b9e\u5fc3\u91cc\u4e5f \u6709\u70b9\u5bb3\u6015\u3002\u6b63\u662f\uff1a\u95fb\u540d\u80c6\u843d\u60ca\u9b54\u5973\uff0c\u5a01\u9707\u6c5f\u6e56\u8fdc\u8fd1\u77e5\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377\u2014\u2014\u7b2c\u5341\u56db\u56de\u3000\u540d\u5c06\u80f8\u895f\u3000\u5973\u9b54\u7518\u6298\u670d\u3000\u79d8\u9b54\u5d16\u4e0b\u3000\u9b3c\u6bcd\u901e\u8c6a\u5f3a"}),showOnHomepage:!0},{name:"\u540d\u5c06\u80f8\u895f\u3000\u5973\u9b54\u7518\u6298\u670d ",content:(0,t.jsx)(t.Fragment,{children:"\u79d8\u9b54\u5d16\u4e0b\u3000\u9b3c\u6bcd\u901e\u8c6a\u5f3a \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u767d\u77f3\u9053\u4eba\u8d77\u6765\uff0c\u6b66\u5f53\u4f17\u5f1f\u5b50\u5df2\u9f50\u96c6\u4e86\u6765\u95ee\u5019\u3002\u4f17\u4eba\u53fd\u53fd\u55b3\u55b3\u7684\u8bae\u8bba\uff0c\u6709\u4eba \u77e5\u9053\u672c\u6d3e\u957f\u8001\u4e2d\u7684\u7ea2\u4e91\u9053\u4eba\u66fe\u8d25\u5728\u7389\u7f57\u5239\u4e4b\u624b\uff0c\u66f4\u662f\u62c5\u5fc3\u3002\u4eac\u4e2d\u7684\u5927\u5f1f\u5b50\u674e\u5c01\u9996\u5148\u8bf4\u9053\uff1a \u201c\u5e08\u53d4\uff0c\u6211\u4eec\u90fd\u966a\u4f60\u53bb\u5427\uff1f\u201d\u767d\u77f3\u9053\uff1a\u201c\u6211  \u7ea6\u7389\u7f57\u5239\u5355\u6253\u72ec\u6597\uff0c\u4f60\u4eec\u53bb\u505a\u4ec0\u9ebd\uff1f\u201d\u674e\u5c01 \u9053\uff1a\u201c\u6211\u4eec\u53bb\u89c2\u6218\uff0c\u7ed9\u5e08\u53d4\u52a9\u5a01\u3002\u201d\u767d\u77f3\u77e5\u9053\u4ed6\u4eec\u7684\u610f\u601d\uff0c\u5fc3\u60f3\uff1a\u7389\u7f57\u5239\u867d\u662f\u52b2\u654c\uff0c\u4f46\u542c\u7ea2 \u4e91\u5e08\u5144\u8bf4\u8fc7\uff0c\u5979\u7684\u957f\u5904\u5728\u65bc\u5251\u6cd5\uff0c\u82e5\u8bba\u5230\u529f\u592b\uff0c\u5219\u4f3c\u8fd8\u5728\u4e8c\u5e08\u5144\u9ec4\u53f6\u4e4b\u4e0b\uff0c\u548c\u4ed6\u5dee\u4e0d\u591a\u3002\u81ea \u5df1\u7684\u5251\u6cd5\u5728\u540c\u95e8\u4e4b\u4e2d\u6700\u9ad8\uff0c\u4e5f\u8bb8\u514b\u5979\u5f97\u4f4f\u3002\u82e5\u51c6\u8fd9\u73ed\u5c0f\u8f88\u540c\u53bb\uff0c\u53ea\u6050\u4ed6\u4eec\u7231\u5e08\u5fc3\u5207\uff0c\u5230\u65f6\u4e00 \u6d8c\u800c\u4e0a\uff0c\u90a3\u5c31\u8981\u574f\u4e86\u6b66\u5f53\u7684\u540d\u6c14\u4e86\u3002\u65bc\u662f\u6447\u6447\u5934\u9053\uff1a\u201c\u4e0d\u884c\uff0c\u4f60\u4eec\u4e00\u4e2a\u90fd\u4e0d\u8bb8\u53bb\uff1f\u201d\u674e\u5c01 \u9053\uff1a\u201c  \u770b\u770b\u90fd\u4e0d\u8bb8\u5417\uff1f\u201d\u767d\u77f3\u9053\u4eba\u6120\u9053\uff1a\u201c\u8c01\u82e5\u64c5\u81ea\u53bb\u770b\uff0c\u5bb6\u6cd5\u4ece\u4e8b\u3002\u201d\u4f55\u843c\u534e\u9053\uff1a \u201c\u7239\uff0c\u6211\u966a\u4f60\u53bb\u5427\u3002\u201d\u767d\u77f3\u9053\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u4e0d\u8981\u53bb\uff01\u7389\u7f57\u5239\u5fc3\u72e0\u624b\u8fa3\uff0c\u4f60\u53bb \u53cd\u800c\u6210\u4e86\u7d2f\u8d58\u3002\u201d\u4f55\u843c\u534e\u8ddf\u59d1\u59d1\u7ec3\u4e86\u5341\u5e74\u6b66\u529f\uff0c\u867d\u7136\u660e\u77e5\u7389\u7f57\u5239\u5389\u5bb3\u975e\u5e38\uff0c\u4e5f\u60f3\u968f\u7236\u4eb2\u53bb\u4e00 \u8bd5\u8eab\u624b\uff0c\u88ab\u7236\u4eb2\u4e00\u8bf4\uff0c\u5fc3\u4e2d\u5f88\u4e0d\u670d\u6c14\u3002 \u3000\u3000\u767d\u77f3\u9053\u4eba\u7ed3\u4e1c\u505c\u5f53\uff0c\u4f17\u5f1f\u5b50\u9001\u51fa\u95e8\u5916\u3002\u767d\u77f3\u9053\u4eba\u5ffd\u7136\u8e0c\u8e87\u4e00\u9635\uff0c\u62db\u624b\u8bf4\u9053\uff1a\u201c\u4e00\u822a\uff0c\u4f60 \u53ef\u4ee5\u53bb\u3002\u4f60\u548c\u7389\u7f57\u5239\u76f8\u8bc6\uff0c\u53c8\u662f\u6211\u6d3e\u672a\u6765\u7684\u638c\u95e8\uff0c\u5e94\u8be5\u5728\u573a\u3002\u201d\u5353\u4e00\u822a\u5fc3\u4e2d\u5b9e\u4e0d\u613f\u89c1\u81ea\u5df2\u7684 \u5e08\u53d4\u548c\u7389\u7f57\u5239\u62da\u6597\uff0c\u6b63\u5728\u82e6\u82e6\u601d\u7d22\u5316\u89e3\u4e4b\u65b9\uff0c\u5e08\u53d4\u9080\u4ed6\u540c\u884c\uff0c\u6b63\u5408\u5fc3\u610f\u3002 \u3000\u3000\u518d\u8bf4\u7389\u7f57\u5239\u8fde\u591c\u8fdb\u57ce\uff0c\u5979\u8f7b\u529f\u6781\u9ad8\uff0c\u751a\u81f3\u8fd8\u5728\u94c1\u98de\u9f99\u4e4b\u4e0a\uff0c\u4e5f\u6b63\u56e0\u5982\u6b64\uff0c\u94c1\u98de\u9f99\u624d\u53eb\u5979 \u5165\u57ce\u914d\u836f\u3002\u5979\u8fc7\u4e86\u56db\u66f4\uff0c\u624d\u4ece\u897f\u5c71\u7684\u7075\u5149\u5bfa\u52a8\u8eab\uff0c\u5230\u4e86\u57ce\u4e2d\u7684\u957f\u5b89\u9556\u5c40\u4e4b\u65f6\uff0c\u5929\u8fd8\u672a\u4eae\u3002 \u3000\u3000\u957f\u5b89\u9556\u5c40\u7684\u603b\u9556\u5934\u9f99\u8fbe\u53c1\u548c\u94c1\u98de\u9f99\u6709\u8fc7\u4e00\u6bb5\u8fc7\u547d\u7684\u4ea4\u60c5\u3002\u5728\u4e8c\u5341\u5e74\u524d\uff0c\u4ed6\u4fdd\u9556\u897f\u5317\uff0c\u6709 \u4e00\u6b21\u88ab\u5f3a\u76d7\u6240\u52ab\uff0c\u4eba\u4e5f\u9677\u5728\u91cd\u56f4\uff0c\u51e0\u4e4e\u8131\u4e0d\u4e86\u8eab\u3002\u5e78\u4e8f\u662f\u94c1\u98de\u9f99\u95fb\u8baf\u8d76\u6765\uff0c\u51ed\u7740\u201c\u5a01\u9707\u897f \u5317\u201d\u7684\u5a01\u540d\uff0c\u5c06\u90a3\u73ed\u5f3a\u76d7\u559d\u4f4f\uff0c\u4e0d\u4f46\u9556\u94f6\u5b8c\u6574\u65e0\u7f3a\uff0c\u800c\u4e14\u9762\u5b50\u4e5f\u5f97\u4ee5\u4fdd\u5168\uff0c\u6240\u4ee5\u9f99\u8fbe\u53c1\u5bf9\u94c1 \u98de\u9f99\u5341\u5206\u611f\u6fc0\uff0c\u4e8c\u5341\u5e74\u6765\u6c38\u94ed\u5fc3\u7248\uff0c\u53ea\u6068\u62a5\u7b54\u65e0\u7531\u3002 \u3000\u3000\u9f99\u8fbe\u53c1\u4e5f\u662f\u67f3\u897f\u94ed\u7684\u597d\u53cb\uff0c\u6628\u65e5\u67f3\u897f\u94ed\u5728\u6768\u5bb6\u56de\u6765\uff0c\u9080\u4ed6\u6697\u52a9\u718a\u5ef7\u5f3c\u9632\u5907\u5978\u515a\u9677\u5bb3\uff0c\u5e76 \u8bf4\u8d77\u65e0\u610f\u4e4b\u4e2d\u7ed9\u718a\u5ef7\u5f3c\u89e3\u56f4\u4e4b\u4e8b\u3002\u9f99\u8fbe\u53c1\u542c\u8bf4\u94c1\u98de\u9f99\u548c\u4e00\u4e2a\u6f02\u4eae\u7684\u5c11\u5973\u5f53\u65f6\u4e5f\u5728\u573a\u4e2d\uff0c\u6025\u5fd9 \u6253\u542c\u94c1\u98de\u9f99\u7684\u4f4f\u5740\u3002\u67f3\u897f\u94ed\u9053\uff1a\u201c\u90a3\u4e2a\u8001\u5934\u771f\u602a\uff0c\u4ed6\u548c\u90a3\u5c11\u5973\u51fa\u529b\u6700\u591a\uff0c\u5374\u6beb\u4e0d\u5c45\u529f\uff0c\u4e8b\u60c5 \u4e00\u5b8c\uff0c\u4fbf\u98d8\u7136\u8d70\u4e86\u3002\u4e5f\u4e0d\u548c\u6211\u4eec\u8bf4\u8bdd\uff0c\u6211\u662f\u5f8c\u6765\u95ee\u767d\u77f3\u9053\u4eba\u624d\u77e5\u9053\u4ed6\u662f\u94c1\u98de\u9f99\u7684\u3002\u8fd8\u542c\u8bf4\u90a3 \u5929\u4ed9\u822c\u7684\u5c11\u5973\u4fbf\u662f\u65b0\u8fd1\u5728\u897f\u5317\u7a9c\u8d77\u7684\u5973\u5f3a\u76d7\u7389\u7f57\u5239\u5462\u3002\u201d\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u54e6\uff0c\u7389\u7f57\u5239\uff01\u4e0d\u9519\uff0c \u8fd9\u540d\u5b57\u6700\u8fd1\u6211\u8fd8\u542c\u4eba\u63d0\u8fc7\u3002\u542c\u8bf4\u7389\u7f57\u5239\u5fc3\u72e0\u624b\u8fa3\uff0c\u662f\u4e2a\u6740\u4eba\u4e0d\u7728\u773c\u7684\u5973\u9b54\u5934\u3002\u94c1\u8001\u813e\u6c14\u867d\u7136 \u602a\u7656\uff0c\u4f46\u5374\u662f\u6b63\u6d3e\u4e4b\u4eba\uff0c\u4e0d\u5982\u4f55\u4ee5\u548c\u5979\u4e00\u8def\uff1f\u201d\u7389\u7f57\u5239\u6740\u4eba\u4e0d\u7728\u773c\u90a3\u662f\u4e8b\u5b9e\uff0c\u4f46\u5374\u4e5f\u4e0d\u662f\u4e71 \u6740\uff0c\u53ea\u56e0\u6811\u654c\u592a\u591a\uff0c\u6c5f\u6e56\u4e0a\u53c8\u5938\u5927\u5176\u8f9e\uff0c\u6240\u4ee5\u51fa\u9053\u4e0d\u8fc7\u53c1\u56db\u5e74\uff0c\u5c31\u51e0\u4e4e\u7ed9\u4eba\u8bf4\u6210\u4e86\u4e07\u6076\u4e0d\u8d66 \u7684\u5973\u9b54\u3002 \u3000\u3000\u9f99\u8fbe\u53c1\u548c\u67f3\u897f\u94ed\u8c08\u8bba\u7389\u7f57\u5239\u3002\u9f99\u8fbe\u53c1\u8bf4\u5979\u662f\u6740\u4eba\u4e0d\u7728\u773c\u7684\u5973\u9b54\uff0c\u5bf9\u94c1\u98de\u9f99\u548c\u5979\u4e00\u8def\uff0c\u5fc3 \u4e2d\u4e0d\u4ee5\u4e3a\u7136\u3002\u67f3\u897f\u94ed\u7b11\u9053\uff1a\u201c\u8bf4\u8d77\u6765\u4e5f\u771f\u7b11\u8bdd\uff0c\u767d\u77f3\u9053\u4eba\u90a3\u9ebd\u4e00\u628a\u5e74\u7eaa\u4e86\uff0c\u5374\u8fd8\u8fd9\u6837\u597d\u80dc\uff0c \u4e00\u5b9a\u8981\u548c\u7389\u7f57\u5239\u6bd4\u5251\u3002\u201d\u67f3\u897f\u94ed\u5bf9\u7389\u7f57\u5239\u4e0e\u6b66\u5f53\u6d3e\u7684\u6069\u6069\u6028\u6028\u6beb\u4e0d\u77e5\u60c5\uff0c\u6240\u4ee5\u53ea\u4ee5\u4e3a\u4ed6\u4eec\u662f \u597d\u80dc\u4e89\u5f3a\u7684\u6b66\u6797\u5e38\u4e8b\u3002\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u767d\u77f3\u9053\u4eba\u662f\u6b66\u5f53\u4e94\u8001\u4e4b\u4e00\uff0c\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u593a\u547d\u5251\u56db\u6d77\u95fb \u540d\u3002\u90a3\u5973\u9b54\u5934\u627e\u4ed6\u6bd4\u5251\uff0c\u90a3\u662f\u81ea\u5bfb\u6b7b\u8def\u4e86\uff01\u201d\u67f3\u897f\u94ed\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4e5f\u61d2\u5f97\u7406\u4f1a\u3002\u767d\u77f3\u5012\u5f88\u7d27 \u5f20\uff0c\u597d\u50cf\u5168\u526f\u5fc3\u795e\u90fd\u653e\u5728\u8fd9\u4ef6\u4e8b\u4e0a\uff0c\u8fde\u6697\u9632  \u515a\uff0c\u4fdd\u62a4\u7ecf\u7565\u5927\u4eba\u7684\u4e8b\uff0c\u90fd\u4e0d\u8d77\u52b2\u4e86\u3002\u6240\u4ee5\u6211 \u624d\u6765\u6c42\u4f60\u52a9\u4e00\u81c2\u4e4b\u529b\u3002\u201d\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u53bb\u5e74\u6709\u4e00\u6279\u519b\u9977\u89e3\u51fa\u8fb9\u5173\uff0c\u627f\u718a\u7ecf\u7565\u770b\u5f97\u8d77\u6211\uff0c\u8fd8\u53eb \u6211\u5e2e\u5fd9\u62bc\u8fd0\u3002\u6211\u751f\u5e73\u4fdd\u9556\uff0c\u90a3\u6b21\u4fdd\u5f97\u6700\u6709\u610f\u601d\u3002\u867d\u7136\u6211\u53ea\u662f\u52a9\u624b\uff0c\u4f46\u5374\u6bd4\u81ea\u5df1\u505a\u603b\u9556\u5934\u72ec\u6311 \u5927\u6881\u65f6\u66f4\u6709\u7cbe\u795e\u3002\u718a\u7ecf\u7565\u5f85\u4eba\u771f\u597d\uff01\u201d\u67f3\u897f\u94ed\u597d\u751f\u7fa1\u6155\uff0c\u9053\uff1a\u201c\u8fd9\u6837\u8bf4\u6765\uff0c\u4f60\u5012\u662f\u718a\u7ecf\u7565\u7684 \u8001\u670b\u53cb\u4e86\u3002\u201d\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u4e0d\u6562\u3002\u6211\u751f\u5e73  \u5bf9\u4e24\u4e2a\u4eba\u5fc3\u670d\u53e3\u670d\uff0c\u82e5\u662f\u8fd9\u4e24\u4e2a\u4eba\u6709\u4e8b\u8981\u5dee\u9063\u5230 \u6211\uff0c\u6211\u8d74\u6c64\u8e48\u706b\uff0c\u90fd\u5728\u6240\u4e0d\u8f9e\u3002\u201d\u67f3\u897f\u94ed\u7b11\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\u4e00\u4e2a\u662f\u94c1\u8001\u5934\u5b50\uff0c\u4e00\u4e2a\u662f\u7ecf\u7565\u5927 \u4eba\uff0c\u5bf9\u9ebd\uff1f\u53ef\u7b11\u6211\u4eec\u76f8\u4ea4\u591a\u5e74\uff0c\u8fd8\u4e0d\u77e5\u9053\u4f60\u5bf9\u718a\u7ecf\u7565\u8fd9\u9ebd\u4f69\u670d\uff0c\u521a\u624d\u6211\u6765\u627e\u4f60\uff0c\u5fc3\u4e2d\u8fd8\u8e0c\u8e87 \u4e0d\u51b3\uff0c\u6050\u6015\u4f1a\u59a8\u4e86\u4f60\u9556\u5c40\u7684\u751f\u610f\u5462\u3002\u201d\u9f99\u8fbe\u53c1\u4e5f\u7b11\u9053\uff1a\u201c\u90a3\u5f97\u602a\u6211\u4e0d\u597d\u3002\u53bb\u5e74\u6211\u5e94\u718a\u7ecf\u7565\u4e4b \u8058\uff0c\u52a9\u4ed6\u62bc\u89e3\u519b\u9977\u7684\u4e8b\uff0c\u6ca1\u6709\u5bf9\u8001\u670b\u53cb\u8bf4\u77e5\u3002\u201d\u67f3\u897f\u94ed\u9053\uff1a\u201c\u90a3\u662f\u5e94\u8be5\u7684\u3002\u62bc\u89e3\u519b\u9977\u7684\u4e8b \u60c5\uff0c\u90a3\u53ef\u968f\u4fbf\u4e71\u63d0\u3002\u201d\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u73b0\u5728\u6765\u9080\u6211\uff0c\u6211\u624d\u5bf9\u4f60\u8bf4\u3002\u5927\u54e5\uff0c\u4f60\u653e\u5fc3\uff0c\u5c31\u7b97 \u9b4f\u5fe0\u8d24\u8981\u5c01\u6211\u7684\u9556\u5c40\uff0c\u62c9\u6211\u53bb\u788e\u5250\uff0c\u6211\u4e5f\u5f97\u5e2e\u7ecf\u7565\u5927\u4eba\u3002\u201d \u3000\u3000 \u8fd9\u4e00\u665a\u9f99\u8fbe\u53c1\u679c\u7136\u4ee5\u603b\u9556\u5934\u4e4b\u5c0a\uff0c\u6697\u4e2d\u5728\u6768\u6d9f\u4f4f\u5bb6\u9644\u8fd1\uff0c\u5de1\u98ce\u628a\u591c\uff0c\u5230\u4e86\u56db\u66f4\uff0c\u624d\u6362 \u73ed\u56de\u6765\u3002\u9556\u5c40\u65e5\u591c\u6709\u4eba\u628a\u5b88\uff0c\u9f99\u8fbe\u53c1\u624d\u6b47\u4e86\u4e00\u9635\uff0c\u5ffd\u62a5\u6709\u4e00\u4e2a\u5c11\u5973\u62cd\u95e8\u6765\u627e\uff0c\u9f99\u8fbe\u53c1\u5947\u9053\uff1a \u201c\u600e\u9ebd\u4f1a\u6709\u5c11\u5973\u627e\u6211\uff0c\u600e\u9ebd\u4e0d\u7b49\u5929\u4eae\u624d\u6765\uff1f\u201d\u62ab\u8863\u5ef6\u89c1\uff0c\u53ea\u89c1\u4e00\u4e2a\u4e8c\u5341\u5c81\u5de6\u53f3\u7684\u5c11\u5973\uff0c\u957f\u7709 \u5165\u9b13\uff0c\u4e00\u53cc\u4fcf\u76ee\uff0c\u9690\u9690\u542b\u6709\u6740\u6c14\uff0c\u4ee4\u4eba\u4e0d\u5bd2\u800c\u6817\uff01\u9f99\u8fbe\u53c1\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\uff0c\u4f60\uff0c\u4f60\u662f\u7389 \u7f57\u5239\uff1f\u2026\u2026\u201d\u8bf4\u5b8c\u4e4b\u5f8c\uff0c\u5ffd\u89c9\u4e0d\u59a5\uff0c\u7389\u7f57\u5239\u4e43\u662f\u5979\u7684\u6d51\u53f7\uff0c\u600e\u597d\u4e71\u53eb\uff1f\u90a3\u5c11\u5973\u5374\u6beb\u4e0d\u5728\u610f\uff0c \u4e00\u58f0\u7b11\u9053\uff1a\u201c\u4f60\u731c\u5f97\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u7389\u7f57\u5239\uff01\u201d\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u2026\u2026\u5973\u4fa0\uff0c\u6df1\u591c\u964d\u4e34\uff0c\u6709 \u4f55\u89c1\u6559\uff1f\u201d\u9f99\u8fbe\u53c1\u8fd8\u6015\u662f\u4ec7\u5bb6\u628a\u8fd9\u5973\u9b54\u8bf7\u6765\uff0c\u548c\u81ea\u5df1\u4f5c\u5bf9\u3002\u4f46\u60f3\u8d77\u65e2\u7136\u5979\u548c\u94c1\u98de\u9f99\u540c\u884c\uff0c\u4f3c \u4e4e\u4e5f\u4e0d\u5e94\u548c\u81ea\u5df2\u4f5c\u5bf9\u3002\u679c\u7136\u7389\u7f57\u5239\u53c8\u7b11\u4e86\u4e00\u7b11\uff0c\u628a\u4e00\u5e45\u767d\u5e03\u638f\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7239\u7ed9\u4f60 \u7684\u4fe1\uff01\u201d\u9f99\u8fbe\u53c1\u63a5\u8fc7\u4e00\u770b\uff0c\u767d\u5e03\u7684\u89d2\u843d\u5904\u753b\u4e86\u4e00\u6761\u5f20\u7259\u821e\u722a\u7684\u98de\u9f99\uff0c\u5fc3\u4e2d\u4e00\u559c\uff0c\u770b\u4e86\u4e0b\u53bb\uff0c \u624d\u77e5\u8fd9\u7389\u7f57\u5239\u7adf\u662f\u4ed6\u6069\u516c\u94c1\u98de\u9f99\u7684\u4e49\u5973\uff0c\u4fe1\u4e0a\u5199\u660e\u8981\u8bf7\u4ed6\u76f8\u52a9\u3002\u90a3\u767d\u5e03\u4e43\u662f\u6495\u788e\u7684\u8863\u886b\uff0c\u5b57 \u8ff9\u4e43\u662f\u6728\u70ad\u6240\u4e66\uff0c\u60f3\u89c1\u4e8b\u60c5\u751a\u6025\u3002 \u3000\u3000\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u94c1\u8001\u4e4b\u547d\uff0c\u5c82\u6562\u4e0d\u9075\u3002\u4e0d\u77e5\u5973\u4fa0\u6709\u4f55\u4e8b\u5dee\u9063\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u6211\u8981\u548c\u4eba \u6253\u67b6\uff01\u201d\u9f99\u8fbe\u53c1\u6014\u4e86\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u8fd9\u5374\u5982\u4f55\u662f\u597d\uff1f\u94c1\u98de\u9f99\u662f\u81ea\u5df1\u7684\u6069\u4eba\uff0c\u767d\u77f3\u9053\u4eba\u4e5f\u662f\u81ea\u5df1 \u7684\u670b\u53cb\u3002\u800c\u4e14\u8fd8\u4f4f\u5728\u67f3\u897f\u94ed\u5bb6\u91cc\u3002\u73b0\u5728\u7389\u7f57\u5239\u8981\u548c\u767d\u77f3\u9053\u4eba\u6bd4\u5251\uff0c\u60f3\u662f\u94c1\u98de\u9f99\u6015\u4ed6\u7684\u4e49\u5973\u5403 \u4e8f\uff0c\u53c8\u77e5\u9053\u6211\u548c\u767d\u77f3\u76f8\u8bc6\uff0c\u6240\u4ee5\u53eb\u7389\u7f57\u5239\u4eb2\u81ea\u767b\u95e8\uff0c\u8bf7\u6211\u51fa\u5934\u4e86\u3002\u4e0d\u77e5\u94c1\u98de\u9f99\u7684\u610f\u601d\u662f\u8981\u6211 \u53bb\u8c03\u89e3\u8fd8\u662f\u8981\u6211\u53bb\u52a9\u62f3\uff0c\u82e5\u662f\u8981\u8c03\u89e3\u8fd8\u597d\uff0c\u82e5\u8981\u52a9\u62f3\uff0c\u90a3\u8fd9\u4e2a\u9762\u5b50\u600e\u9ebd\u653e\u5f97\u4e0b\u6765\uff01\u7389\u7f57\u5239\u89c1 \u4ed6\u5446\u82e5\u6728\u9e66\uff0c\u5fc3\u9053\uff1a\u600e\u9ebd\u8fd9\u4e2a\u4eba\u5982\u6b64\u8113\u5305\uff0c\u542c\u5230\u6253\u67b6\u5c31\u614c\u5f97\u8fd9\u4e2a\u6a21\u6837\uff0c\u8fd8\u505a\u603b\u9556\u5934\u5462\uff01\u9f99\u8fbe \u53c1\u5b9a\u4e86\u5b9a\u795e\uff0c\u5450\u5450\u8bf4\u9053\uff1a\u201c\u5973\u4fa0\u4f55\u82e6\u548c\u6b66\u5f53\u8109\u7ed3\u4ec7\uff01\u201d\u7389\u7f57\u5239\u7709\u6bdb\u4e00\u626c\uff0c\u9053\uff1a\u201c\u522b\u4eba\u6015\u6b66\u5f53 \u6d3e\u4eba\u591a\u52bf\u5927\uff0c\u6211\u504f\u4e0d\u6015\uff01\u201d\u9f99\u8fbe\u53c1\u55eb\u5685\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u5973\u4fa0\u4e0d\u6015\uff0c\u4f46\u51a4\u5bb6\u5b9c\u89e3\u4e0d\u5b9c\u7ed3\uff0c\u7531\u6211 \u6765\u6446\u548c\u5934\u9152\uff0c\u8bf7\u5973\u4fa0\u548c\u767d\u77f3\u9053\u4eba\u8d4f\u9762\uff0c\u5f7c\u6b64\u6765\u559d\u4e00\u676f\uff0c\u548c\u89e3\u4e86\u5427\uff1f\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u6211\u548c\u767d \u77f3\u9053\u4eba\u6bd4\u5251\u662f\u6bd4\u5b9a\u7684\u4e86\uff0c\u767d\u77f3\u9053\u4eba\u6b66\u529f\u867d\u975e\u767b\u5cf0\u9020\u6781\uff0c\u4f46\u4e5f\u8fd8\u53ef\u4ee5\u505a\u505a\u5bf9\u624b\u3002\u4f60\u53eb\u6211\u4e0d\u8981\u548c \u4ed6\u6bd4\u5251\uff0c\u9664\u975e\u4f60\u53e6\u5916\u627e\u4e00\u4e2a\u53ef\u4ee5\u505a\u6211\u5bf9\u624b\u7684\u6765\u6bd4\u3002\u5929\u4e0b\u4e8b\u6700\u75db\u5feb\u7684\u83ab\u5982\u627e\u5230\u5bf9\u624b\u6bd4\u6b66\uff0c\u4f60\u53eb \u6211\u4e0d\u6bd4\uff0c\u90a3\u600e\u9ebd\u6210\uff01\u201d\u9f99\u8fbe\u53c1\u9053\u58f0\u82e6\u4e5f\uff0c\u7ef7\u7d27\u4e86\u9762\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u3002\u7389\u7f57\u5239\u9053\uff1a\u201c\u600e\u9ebd\uff0c\u4f60\u5e2e\u4e0d \u5e2e\u5fd9\uff1f\u5929\u5c31\u5feb\u4eae\uff0c\u6211\u8fd8\u8981\u8d76\u56de\u53bb\u5462\uff01\u201d\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u6211\u8fd9\u6761\u547d\u4e5f\u662f\u4f60\u7239\u7239\u6551\u7684\uff0c\u4ed6\u6709\u547d\u4ee4\uff0c \u6211\u600e\u6562\u4e0d\u9075\uff1f\u4e0d\u8fc7\u6211\u60f3\u5148\u89c1\u4ed6\u4e00\u9762\u3002\u767d\u77f3\u9053\u4eba\u5251\u6cd5\u5929\u4e0b\u72ec\u6b65\uff0c\u6211\u548c\u4ed6\u4e00\u6597\uff0c\u51c6\u6b7b\u65e0\u7591\u3002\u6211\u8981 \u8bf7\u4f60\u7239\u7239\u4ee3\u6211\u7167\u987e\u9057\u5b64\u3002\u201d\u5728\u9f99\u8fbe\u53c1\u5fc3\u4e2d\uff0c\u4ee5\u4e3a\u7389\u7f57\u5239\u5b9a\u662f\u8981\u81ea\u5df1\u53bb\u52a9\u62f3\u5e05\u4e86\uff0c\u6240\u4ee5\u60f3\u5148\u89c1 \u94c1\u98de\u9f99\uff0c\u8868\u8fbe\u82e6\u8877\u3002\u7389\u7f57\u5239\u54c8\u54c8\u5927\u7b11\uff0c\u7b11\u5230\u773c\u6cea\u90fd\u6389\u4e0b\u6765\u3002\u9f99\u8fbe\u53c1\u6115\u7136\u4e0d\u89e3\u3002\u5fc3\u4e2d\u70e6\u607c\u4e4b \u6781\u3002\u7389\u7f57\u5239\u5927\u7b11\u4e00\u9635\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u8bf4\u4e86\u534a\u5929\uff0c\u539f\u6765\u4f60\u662f\u4ee5\u4e3a\u6211\u8981\u627e\u4f60\u52a9\u62f3\u3002\u767d\u77f3\u9053\u4eba\u7b97\u5f97 \u4e86\u4ec0\u9ebd\uff0c\u4f55\u5fc5\u4f60\u6765\u76f8\u52a9\u3002\u518d\u5389\u5bb3\u7684\u5bf9\u5934\u6211\u4eec\u7236\u5973\u4e5f\u4e0d\u602f\u60e7\uff0c\u4f55\u51b5\u65bc\u4ed6\uff01\u201d \u3000\u3000\u9f99\u8fbe\u53c1\u677e\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u90a3\u9ebd\u5973\u4fa0\u6709\u4f55\u4e8b\u5429\u5490\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u4eec\u627e\u4f60\u4e3a\u7684\u4e0d\u662f\u8981\u5bf9 \u4ed8\u767d\u77f3\u9053\u4eba\uff0c\u800c\u662f\u8981\u5bf9\u4ed8\u7ea2\u82b1\u9b3c\u6bcd\u3002\u201d\u9f99\u8fbe\u53c1\u53c8\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u7ea2\u82b1\u9b3c\u6bcd\u516c\u5b59\u5927\u5a18\u8fd8\u5728\u4e16 \u4e0a\u9ebd\uff1f\u201d\u5fc3\u867d\u60e7\u6015\uff0c\u4f46\u5374\u4e0d\u50cf\u521a\u624d\u90a3\u6837\u60f6\u6050\u3002\u7389\u7f57\u5239\u6545\u610f\u7b11\u9053\uff1a\u201c\u600e\u9ebd\uff0c\u4f60\u4e0d\u6562\u8ddf\u5979\u52a8\u624b \u5417\uff01\u201d\u8fd9\u56de\u8f6e\u5230\u9f99\u8fbe\u53c1\u5927\u7b11\u4e86\uff0c\u9f99\u8fbe\u53c1\u5927\u7b11\u8bf4\u9053\uff1a\u201c\u6211\u82e5\u6015\u6b7b\uff0c\u4e5f\u4e0d\u6562\u5e72\u4fdd\u9556\u8fd9\u4e00\u884c\u4e86\u3002\u4f60 \u8981\u6597\u7ea2\u82b1\u9b3c\u6bcd\uff0c\u6211\u4e07\u6b7b\u4e0d\u8f9e\uff01\u201d\u7389\u7f57\u5239\u597d\u751f\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u7ea2\u82b1\u9b3c\u6bcd\u6bd4\u767d\u77f3\u8fd9\u4eba\u5389\u5bb3\u5f97\u591a\u3002\u4f60 \u4e0d\u6562\u6597\u767d\u77f3\u9053\u4eba\uff0c\u53cd\u800c\u6562\u6597\u7ea2\u82b1\u9b3c\u6bcd\uff0c\u771f\u4e0d\u77e5\u662f\u4ec0\u9ebd\u7406\u7531\u3002\u4f46\u5979\u89c1\u9f99\u8fbe\u53c1\u613f\u610f\u6177\u6168\u8d74\u96be\uff0c\u628a \u5148\u524d\u8f7b\u89c6\u4ed6\u7684\u5fc3\u51cf\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u662f\u4e0d\u662f\u73b0\u5728\u5c31\u53bb\uff01\u201d\u7389\u7f57\u5239\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u8981\u4f60\u52a9\u62f3\u3002\u201d\u628a\u6240\u6c42\u7684\u4e8b\u8bf4 \u4e86\u51fa\u6765\u3002\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u62a4\u5fc3\u94dc\u955c\uff0c\u9556\u5c40\u91cc\u6709\u7684\u662f\uff0c\u53ea\u662f\u90a3\u836f\u65b9\u5f00\u4e86\u8fd9\u9ebd\u591a\u836f\uff0c\u80fd\u5426\u914d\u9f50\uff0c\u5374 \u662f\u96be\u4fdd\u3002\u597d\uff0c\u4f60\u5728\u8fd9\u91cc\u7a0d\u5750\uff0c\u6211\u9a6c\u4e0a\u53eb\u4eba\u7ed9\u4f60\u53bb\u914d\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u5728\u9556\u5c40\u4e2d\u5750\u5019\uff0c\u770b\u770b\u5929\u8272\u5927\u767d\uff0c\u7ea2\u65e5\u4e1c\u5347\uff0c\u53c8\u8fc7\u4e86\u4e00\u4f1a\uff0c\u592a\u9633\u5df2\u7167\u8fdb\u7a97\u6765\u3002\u7389\u7f57 \u5239\u9053\uff1a\u201c\u600e\u9ebd\u8fd8\u4e0d\u56de\u6765\uff1f\u201d\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u51e0\u5341\u5473\u836f\uff0c\u4e00\u65f6\u672a\u5fc5\u914d\u5f97\u9f50\u5168\u3002\u201d\u518d\u8fc7\u4e86\u4e00\u987f\u996d\u65f6 \u95f4\uff0c\u914d\u836f\u7684\u4eba\u56de\u5230\u9556\u5c40\u3002\u4e94\u7f57\u5239\u770b\u770b\u5929\u8272\uff0c\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u6ca1\u6709\u803d\u6401\u65f6\u5019\u3002\u201d\u914d\u836f\u7684\u4f19\u8ba1\u9053\uff1a \u201c\u5eff\u4e94\u5473\u836f\uff0c\u9664\u4e86\u718a\u80c6\u7f3a\u8d27\uff0c\u5176\u4ed6\u90fd\u914d\u9f50\u4e86\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u7f3a\u4e00\u5473\u4e0d\u7d27\u8981\u5427\uff01\u201d\u9f99\u8fbe\u53c1\u4e00 \u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u718a\u80c6\u4e43\u662f\u4e3b\u836f\uff0c\u4e0d\u80fd\u7f3a\u5c11\u3002\u718a\u80c6\u867d\u7136\u540d\u8d35\uff0c\u5374\u4e5f\u4e0d\u662f\u7a00\u7f55\u4e4b\u7269\u5e02\u4e0a\u600e\u9ebd\u4f1a\u7f3a \u8d27\uff1f\u201d\u4f19\u8ba1\u9053\uff1a\u201c\u542c\u8bf4\u8fd9\u4e24\u5929\u5bab\u4e2d\u5185\u76d1\u5927\u4e8b\u641c\u8d2d\uff0c\u836f\u5e97\u91cc\u7684\u718a\u80c6\u5168\u53eb\u4ed6\u4eec\u4e70\u53bb\u4e86\u3002\u201d\u7389\u7f57\u5239 \u6068\u6068\u8bf4\u9053\uff1a\u201c\u82e5\u975e\u6211\u8981\u8d76\u7740\u7b49\u7528\uff0c\u6211\u4fbf\u5230\u5bab\u4e2d\u5077\u5b83\u51fa\u6765\u3002\u201d\u9f99\u8fbe\u53c1\u6c89\u541f\u826f\u4e45\uff0c\u5ffd\u9053\uff1a\u201c\u6709\u4e00 \u4e2a\u5730\u65b9\u4e5f\u8bb8\u4f1a\u6709\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4ec0\u9ebd\u5730\u65b9\uff0c\u6211\u4eec\u9a6c\u4e0a\u5c31\u53bb\u3002\u201d\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u718a\u80c6\u4ee5\u5173\u5916\u51fa \u4ea7\u7684\u6700\u597d\uff0c\u8fb9\u5173\u5c06\u5e05\u5fc5\u5b9a\u5907\u6709\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u90a3\u9ebd\u718a\u7ecf\u7565\u4e00\u5b9a\u6709\u4e86\uff1f\u201d\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u6b63 \u662f\u3002\u718a\u7ecf\u7565\u4e24\u8896\u6e05\u98ce\uff0c\u9001\u4e0d\u8d77\u8c82\u88d8\u7b49\u540d\u8d35\u793c\u7269\uff0c\u718a\u80c6\u5728\u8fd9\u91cc\u867d\u7136\u503c\u94b1\uff0c\u4ed6\u5173\u5916\u5374\u5e76\u4e0d\u8d35\uff0c\u718a \u7ecf\u7565\u5b9a\u4f1a\u5e26\u4e9b\u56de\u6765\uff0c\u9001\u7ed9\u4eb2\u53cb\u3002\u6211\u548c\u4f60\u53bb\u4e00\u8d9f\u5427\u3002\u201d\u7389\u7f57\u5239\u60f3\u8d77\u6628\u5929\u548c\u5cb3\u545c\u73c2\u52a8\u624b\u4e4b\u4e8b\uff0c\u597d \u751f\u59d4\u51b3\u4e0d\u4e0b\uff0c\u60f3\u4e86\u4e00\u4f1a\uff0c\u5ffd\u9053\uff1a\u201c\u4ed6\u82e5\u53eb\u718a\u7ecf\u7565\u4e0d\u7ed9\uff0c\u90a3\u9ebd\u4ed6\u7684\u4eba\u54c1\u5c31\u66f4\u4e0d\u8db3\u53d6\u4e86\u3002\u201d\u9f99\u8fbe \u53c1\u83ab\u660e\u5176\u5999\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u4e00\u7b11\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u9ebd\uff0c\u6211\u548c\u718a\u7ecf\u7565\u624b\u4e0b\u4e00\u4e2a\u6b66 \u5b98\uff0c\u6709\u70b9\u5c0f\u5c0f\u7684\u8fc7\u8282\u3002\u201d \u3000\u3000\u4e14\u8bf4\u718a\u5ef7\u5f3c\u6628\u65e5\u8ffd\u9047\u4e24\u573a\u6a2a\u7978\uff0c\u5fc3\u60c5\u6fc0\u6124\uff0c\u53cd\u663e\u5f97\u610f\u5174\u9611\u73ca\u3002\u8fd9\u65e5\u4f17\u5b98\u594f\u647a\u5df2\u4e0a\uff0c\u7687\u5e1d \u5374\u6ca1\u5750\u671d\uff0c\u594f\u647a\u662f\u6309\u671d\u5ef7\u4f53\u5236\u7531\u5bab\u4e2d\u7684\u594f\u4e8b\u592a\u76d1\u8f6c\u5448\u4e0a\u53bb\u7684\u3002\u6309\u8bf4\u8fd9\u6837\u5927\u4e8b\uff0c\u7687\u5e1d\u5e94\u8be5\u9a6c\u4e0a \u5904\u7406\uff0c\u4f46\u7b49\u5230\u65e5\u4e0a\u53c1\u7aff\uff0c\u8fd8\u4e0d\u89c1\u52a8\u9759\uff0c\u4e5f\u4e0d\u89c1\u6709\u94a6\u5dee\u6765\u5ba3\u53ec\u3002\u718a\u5ef7\u5f3c\u5728\u623f\u4e2d\u8e31\u7740\u65b9\u6b65\uff0c\u8d70\u6765 \u8d70\u53bb\u3002\u5cb3\u545c\u73c2\u77e5\u9053\u8fd9\u662f\u4ed6\u7684\u8001\u4e60\u60ef\uff0c\u6bcf\u5f53\u6709\u5927\u4e8b\u5f85\u51b3\u4e4b\u65f6\uff0c\u603b\u662f\u8fd9\u6837\u3002\u5230\u4e86\u8fd1\u5348\u65f6\u5206\uff0c\u7687\u5e1d \u624d\u7a81\u7136\u6d3e\u4e86\u4e24\u540d\u592a\u76d1\uff0c\u62ac\u4e86\u4e00\u7ba9\u4e1c\u897f\uff0c\u4f20\u65e8\u8d4f\u7ed9\u718a\u5ef7\u5f3c\u770b\u3002\u5185\u76d1\u53bb\u5f8c\uff0c\u718a\u5ef7\u5f3c\u6253\u5f00\u4e00\u770b\uff0c\u53ea \u89c1\u6ee1\u7ba9\u594f\u647a\uff0c\u90fd\u662f\u5978\u515a\u53c2\u52be\u81ea\u5df1\u7684\u594f\u647a\u3002\u718a\u5ef7\u5f3c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u7f62\u4e86\uff01\u7f62\u4e86\uff01\u201d\u6768\u6d9f\u9053\uff1a\u201c\u7ecf \u7565\u5927\u4eba\u5bbd\u5fc3\uff0c\u5723\u4e0a\u628a\u594f\u647a\u539f\u5c01\u4e0d\u52a8\u9001\u7ed9\u4f60\u770b\uff0c\u6b63\u8db3\u89c1\u4fe1\u8d56\u4e4b\u6df1\u3002\u201d\u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u82e5\u7136\u6211\u4eec\u7684 \u594f\u647a\u672a\u4e0a\uff0c\u5982\u6b64\u8bf4\u6cd5\uff0c\u4e5f\u8fd8\u4e0d\u65e0\u9053\u7406\uff0c\u4f46\u5728\u6211\u4eec\u594f\u647a\u9012\u4e0a\u4e4b\u5f8c\uff0c\u624d\u8d4f\u7ed9\u6211\u770b\uff0c\u8fd9\u5206\u660e\u662f\u8bf4\uff1a \u4f60\u53c2\u52be\u522b\u4eba\uff0c\u522b\u4eba\u4e5f\u53c2\u52be\u4f60\u3002\u7687\u5e1d\u662f\u5fe0  \u4e0d\u5206\uff0c\u4e00\u5f8b\u770b\u5f85\u7684\u4e86\u3002\u201d\u6768\u6d9f\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u81f3\u5982 \u6b64\u3002\u201d\u718a\u5ef7\u5f3c\u80cc\u8d1f\u53cc\u624b\uff0c\u53c8\u5728\u623f\u5185\u8e31\u8d77\u65b9\u6b65\uff0c\u8d70\u6765\u8d70\u53bb\u3002\u6768\u6d9f\u7b49\u90fd\u4e0d\u6562\u51fa\u58f0\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u718a \u5ef7\u5f3c\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u62ff\u7eb8\u7b14\u6765\u3002\u201d\u6768\u6d9f\u9053\uff1a\u201c\u7ecf\u7565\u8981\u518d\u4e0a\u594f\u647a\u5417\uff1f\u201d\u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u6211\u8981\u4e0a\u8f9e \u5448\uff01\u201d\u6768\u6d9f\u9053\uff1a\u201c\u4e0d\u53ef\u5440\u4e0d\u53ef\uff01\u788d\u7565\u4e0d\u53ef\u56e0\u4e00\u65f6\u4e4b\u6c14\uff0c\u628a\u56fd\u4e8b\u629b\u5f00\u4e0d\u7406\u3002\u201d\u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u6768 \u5144\uff0c\u4f60\u6709\u6240\u4e0d\u5982\uff0c\u671d\u4e2d\u65e2\u7136\u5168\u7ed9  \u515a\u628a\u6301\uff0c\u6211\u7eb5\u80fd\u518d\u56de\u8fb9\u5173\uff0c\u4e5f\u5fc5\u53d7\u8bf8\u591a\u63a3\u8098\uff0c\u4e0d\u80fd\u7edf\u5175\u6297 \u654c\u7684\u4e86\u3002\u6211\u4e0d\u5982\u8ff3\u4e0a\u8f9e\u5448\uff0c\u8bd5\u8bd5\u7687\u5e1d\u7684\u5fc3\u610f\u3002\u8fd9\u5728\u5175\u6cd5\u4e0a\u53eb\u505a\u7f6e\u4e4b\u6b7b\u5730\u800c\u5f8c\u751f\u3002\u82e5\u7136\u7687\u5e1d\u8fd8 \u4e0d\u7b97\u592a\u7cca\u6d82\u7684\u8bdd\uff0c\u4ed6\u5b9a\u4f1a\u53ec\u6211\u5165\u5b98\uff0c\u7ec6\u95ee\u60c5\u7531\u7684\u3002\u201d \u3000\u3000\u5176\u5b9e\u7531\u6821\u867d\u7136\u5e74\u5e7c\uff0c\u4e5f\u8fd8\u4e0d\u7b97\u592a\u8fc7\u80e1\u6d82\uff0c\u4ed6\u8fd8\u61c2\u5f97\u718a\u5ef7\u5f3c\u662f\u4e2a\u5927\u5fe0\u81e3\u7684\u3002\u53ef\u662f\u4ed6\u7684\u4e73\u6bcd \u5ba2\u6c0f\u548c\u9b4f\u5fe0\u8d24\u72fc\u72c8\u4e3a\u5978\uff0c\u6839\u672c\u4e0d\u8ba9\u4ed6\u77e5\u9053\u5916\u9762\u7684\u4e8b\u60c5\uff0c\u5374\u628a\u4ed6\u4e00\u6b65\u6b65\u522b\u5230\u58f0\u8272\u73a9\u4e50\u7684\u4eab\u53d7\u4e0a \u53bb\uff0c\u628a\u4ed6\u90a3\u4e00\u70b9\u70b9\u7075\u6027\uff0c\u4e5f\u5168\u95ed\u585e\u4e86\u3002\u53ef\u601c\u671d\u4e2d\u90a3\u9ebd\u591a\u6b63\u6d3e\u5927\u81e3\uff0c\u5455\u5fc3\u6ca5\u8840\u5199\u51fa\u6765\u7684\u594f\u647a\uff0c \u7531\u6821\u6839\u672c\u5c31\u6ca1\u6709\u770b\u5230\uff0c\u88ab\u4ed6\u7684\u4e73\u6bcd\u6ca1\u6536\u53bb\u4e86\u3002\u7531\u6821\u4ee5\u524d\u8bf4\u8fc7\u8981\u628a\u594f\u647a\u88c5\u6ee1\u4e00\u7ba9\uff0c\u9001\u7ed9\u718a\u5ef7\u5f3c \u770b\u7684\u8bdd\u3002\u5ba2\u6c0f\u770b\u4e86\u6768\u6d9f\u7b49\u4eba\u7684\u594f\u647a\u4e4b\u5f8c\uff0c\u4fbf\u548c\u9b4f\u5fe0\u8d24\u5546\u8bae\uff0c\u4e58\u673a\u6002\u607f\u7531\u6821\uff0c\u8bf4\u9053\uff1a\u201c\u718a\u5ef7\u5f3c \u5df2\u7ecf\u56de\u6765\uff0c\u5723\u4e0a\u53ef\u4ee5\u628a\u90a3\u4e9b\u594f\u647a\u9001\u7ed9\u4ed6\u770b\u4e86\u3002\u201d\u7531\u6821\u9053\uff1a\u201c\u4ed6\u65e2\u7136\u56de\u6765\uff0c\u628a\u4ed6\u53ec\u8fdb\u5bab\u6765\uff0c\u5f53 \u9762\u7ed9\u4ed6\uff0c\u4e0d\u5f88\u597d\u5417\uff1f\u201d\u9b4f\u5fe0\u8d24\u4f5c\u4e86\u4e2a\u5978\u7b11\uff0c\u7531\u6821\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u9ebd\uff1f\u201d\u9b4f\u5fe0\u8d24\u6084\u6084\u8bf4\u9053\uff1a\u201c\u7980 \u7687\u4e0a\uff0c\u8fd9\u718a\u5ef7\u5f3c\u6837\u6837\u90fd\u597d\uff0c\u5c31\u662f\u4e00\u6837\u4e0d\u597d\u3002\u201d\u7531\u6821\u9053\uff1a\u201c\u90a3\u6837\u4e0d\u597d\uff1f\u201d\u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u8fd9\u4eba\u53e4 \u677f\u5f97\u5f88\uff0c\u770b\u89c1\u7687\u4e0a\u90a3\u9ebd\u597d\u73a9\uff0c\u4e00\u5b9a\u4f1a\u5520\u5520\u53e8\u53e8\u8bf4\u4e2a\u4e0d\u4f11\u3002\u201d\u7531\u6821\u5728\u7236\u4eb2\u6b7b\u5f8c\uff0c\u6ca1\u4e86\u7ba1\u5934\uff0c\u73a9 \u5f97\u5341\u5206\u653e\u8086\uff0c\u5728\u5bab\u4e2d\u8f9f\u4e86\u6597\u9e21\u8dd1\u72d7\u8e22\u6bfd\u9a6c\u620f\u4e4b\u573a\uff0c\u5929\u5929\u73a9\u4e50\uff0c\u95fb\u8bf4\u718a\u5ef7\u5f3c\u53e4\u677f\uff0c\u679c\u7136\u5bb3\u6015\uff0c \u9053\uff1a\u201c\u90a3\u9ebd\u5916\u9762\u7684\u53c1\u5927\u6bbf\u53ec\u89c1\uff0c\u4e0d\u8ba9\u4ed6\u770b\u5230\uff0c\u884c\u5417\uff1f\u201d\u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u4ed6\u6765\u5f8c\u4e00\u5b9a\u6709\u4eba\u8bf4\u7ed9\u4ed6 \u542c\uff0c\u4f60\u89c1\u4e86\u4ed6\uff0c\u4e00\u5b9a\u7ed9\u4ed6\u6570\u8bf4\u7684\u3002\u201d\u53c8\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u6885\u83ca\u4e89\u598d\uff0c\u54b1\u4eec\u6b63\u8981\u5f00\u8bbe\u6885\u83ca\u4e4b\u5bb4\uff0c\u53eb \u5bab\u5973\u4eec\u626e\u6210\u6885\u82b1\u4ed9\u5b50\uff0c\u83ca\u82b1\u795e\u5973\uff0c\u8ba9\u5979\u4eec\u4e5f\u4e89\u598d\u6597\u4e3d\u4e00\u9999\uff0c\u82e5\u7136\u7687\u4e0a\u53ec\u89c1\u90a3\u4e2a\u8001\u718a\uff0c\u5c82\u4e0d\u7ed9 \u4ed6\u8d25\u4e86\u6e05\u5174\uff1f\u201d\u7531\u6821\u60f3\u60f3\uff0c\u4e5f\u662f\u9053\u7406\uff0c\u4fbf\u9053\uff1a\u201c\u4f46\u662f\u5230\u5e95\u603b\u5f97\u8981\u89c1\u4ed6\u5440\uff01\u201d\u5ba2\u6c0f\u5728\u65c1\u7b11\u9053\uff1a \u201c\u50bb\u54e5\u513f\uff0c\u5230\u4ed6\u8981\u56de\u8fb9\u5173\u7684\u65f6\u5019\uff0c\u624d\u7ed9\u4ed6\u9001\u884c\u4e5f\u4e0d\u8fdf\u5440\uff01\u201d\u7531\u6821\u5230\u5e95\u53ea\u662f\u4e2a\u5341\u516d\u4e03\u5c81\u7684\u5b69 \u5b50\uff0c\u4e73\u6bcd\u548c\u9b4f\u5fe0\u8d24\u65e2\u7136\u90fd\u662f\u8fd9\u6837\u8bf4\u6cd5\uff0c\u4ed6\u4e5f\u4e50\u5f97\u4f5c\u4e50\u53bb\u4e86\u3002 \u3000\u3000\u53ef\u601c\u718a\u5ef7\u5f3c\u867d\u7136\u77e5\u9053\u5bab\u4e2d\u7ed9\u5ba2\u9b4f\u628a\u6301\uff0c\u8fd8\u6599\u4e0d\u5230\u7531\u6821\u7ed9\u8499\u853d\u5230\u8fd9\u4e2a\u7530\u5730\u3002\u4ed6\u770b\u4e86\u90a3\u7ba9\u594f \u647a\uff0c\u8fd8\u5c3d\u81ea\u731c\u6d4b\u7687\u5e1d\u7528\u610f\uff0c\u5728\u623f\u95f4\u5185\u8e31\u6765\u8e31\u53bb\uff0c\u60f3\u5199\u8f9e\u5448\u3002\u6768\u6d9f\u9053\uff1a\u201c\u718a\u5144\uff0c\u4f60\u82e5  \u662f\u60f3\u8bd5 \u7687\u5e1d\u5fc3\u610f\uff0c\u5199\u5199\u54a9\u5448\uff0c\u6211\u4e5f\u4e0d\u52a0\u53cd\u5bf9\u3002\u4f46\u4e0d\u5fc5\u73b0\u5728\u5c31\u5199\u3002\u5175\u90e8\u5c1a\u4e66\u6768  \u73b0\u5728\u6b63\u53bb\u8ffd\u95ee\u4e5d\u95e8\u63d0 \u7763\uff0c\u95ee\u6628\u65e5\u6349\u5230\u7684\uff0c\u90a3\u4e9b\u5047\u88c5\u5f3a\u76d7\u52ab\u4f60\u7684\u4eba\uff0c\u4ed6\u5ba1\u95ee\u5f97\u5982\u4f55\u4e86\uff1f\u7b49\u4ed6\u56de\u6765\uff0c\u6211\u4eec\u518d\u4ece\u957f\u5546 \u8bae\uff0c\u4f60\u9053\u5982\u4f55\uff1f\u201d\u718a\u5ef7\u5f3c\u53ea\u8bf4\u4e86\u4e24\u4e2a\u5b57\u201c\u4e5f\u597d\u201d\u3002\u4ecd\u8e31\u7740\u65b9\u6b65\uff0c\u7ed5\u5ba4\u800c\u884c\uff0c\u6768\u6d9f\u6015\u4ed6\u95f7\u51fa\u75c5 \u6765\uff0c\u9053\uff1a\u201c\u8001\u718a\uff0c\u6211\u548c\u4f60\u4e0b\u76d8\u68cb\u597d\u5417\uff1f\u201d\u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u4e5f\u597d\u3002\u201d\u8d70\u4e86\u51e0\u7740\uff0c\u968f\u4ece\u6b66\u5b98\u738b\u8d5e\u8fdb \u6765\u62a5\u9053\uff1a\u201c\u7980\u7ecf\u7565\uff0c\u4ee5\u524d\u7ed9\u6211\u4eec\u62bc\u8fd0\u8fc7\u519b\u9977\u7684\u90a3\u4f4d\u9f99\u9556\u5934\uff0c\u548c\u6628\u5929\u90a3\u4e2a\u5973\u5b50\uff0c\u6c42\u89c1\u7ecf\u7565\u3002\u201d \u718a\u5ef7\u5f3c\u628a\u68cb\u5b50\u4e00\u62e8\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u5c40\u68cb\u7b97\u6211\u8f93\u4e86\u3002\u201d\u5429\u5490\u738b\u8d5e\u9053\uff1a\u201c\u8bf7\u4ed6\u4eec\u8fdb\u6765\uff01\u201d \u3000\u3000\u5cb3\u9e23\u73c2\u5728\u65c1\u7eb3\u7f55\uff0c\u4ee5\u4e3a\u7389\u7f57\u5239\u53c8\u6765\u627e\u4ed6\u6666\u6c14\uff0c\u8fd9\u4e9b\u513f\u5973\u4e4b\u4e8b\uff0c\u5bf9\u718a\u7ecf\u7565\u53ef\u96be\u8bf4\u5f97\u6e05\u695a\u3002 \u718a\u5ef7\u5f3c\u89c1\u5cb3\u545c\u73c2\u9762\u8272\u4e0d\u8c6b\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u60f3\u4ec0\u9ebd\uff1f\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u90a3\u5973\u5b50\u91ce\u6027\u96be\u9a6f\uff0c\u6211\u6015\u5979\u4f1a \u51b2\u95ef\u7ecf\u7565\uff01\u201d\u718a\u5ef7\u5f3c\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u4e00\u6014\uff0c\u718a\u5ef7\u5f3c\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u4e24\u5929\uff0c\u89c1\u4e86\u8bb8\u591a\u8863\u51a0\u79bd\u517d\uff0c\u6b63\u60f3\u89c1\u4e00\u89c1\u5c71\u91ce\u4e4b\u4eba\u3002\u201d \u6768\u6d9f\u89c1\u4ed6\u9ad8\u5174\uff0e\uff0c\u4e5f\u51d1\u8da3\u8bf4\u9053\uff1a\u201c\u90a3\u5973\u5b50\u5251\u6cd5\u9ad8\u5f3a\uff0c\u6628\u5929\u6211\u5728\u95e8\u7f1d\u91cc\u5f20\u671b\uff0c\u89c1\u5979\u628a\u7fa4\u8d3c\u6740\u5f97 \u9b3c\u54ed\u795e\u568e\uff0c\u771f\u662f\u75db\u5feb\u6dcb\u6f13\u4e4b\u81f3\uff0c\u6211\u4e5f\u60f3\u89c1\u5979\u4e00\u89c1\u3002\u201d\u5cb3\u545c\u73c2\u4e0d\u4fbf\u963b\u6320\uff0c\u53ea\u597d\u4f8d\u7acb\u5728\u718a\u5ef7\u5f3c\u8eab \u8fb9\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u738b\u8d5e\u5e26\u4e86\u9f99\u8fbe\u53c1\u548c\u7389\u7f57\u5239\u8d70\u4e0a\uff0c\u9f99\u8fbe\u53c1\u5c48\u819d\u884c\u793c\uff0c\u7389\u7f57\u5239\u5374\u5b66\u7537\u5b50\u6a21\u6837\uff0c\u53ea \u662f\u4f5c\u4e86\u4e2a\u63d6\uff0c\u5bf9\u5cb3\u545c\u73c2\u77a7\u4e5f\u4e0d\u77a7\u3002\u718a\u5ef7\u5f3c\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u610f\uff0c\u5bf9\u7389\u7f57\u5239\u9053\uff1a\u201c\u6628\u65e5\u591a\u8499\u4f60\u4ed7\u5251\u6765 \u6551\uff0c\u6211\u8fd8\u672a\u66fe\u8bf7\u6559\u4f60\u7684\u82b3\u540d\u5462\uff1f\u201d\u7389\u7f57\u5239\u5657\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ec0\u9ebd\u82b3\u540d\u4e0d\u82b3\u540d\u7684\uff0c\u6211\u7684\u540d\u5b57\u53eb \u505a\u7ec3\u9713\u88f3\uff0c\u4f46\u6c5f\u6e56\u4e0a\u7684\u4eba\u90fd\u53eb\u6211\u505a\u7389\u7f57\u5239\uff0c\u771f\u540d\u53cd\u800c\u6ca1\u4eba\u53eb\u4e86\u3002\u4f60\u9ad8\u5174\u53eb\u6211\u9713\u88f3\u4e5f\u884c\uff0c\u9ad8\u5174 \u53eb\u6211\u505a\u7389\u7f57\u5239\u4e5f\u884c\uff01\u201d\u718a\u5ef7\u5f3c\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7ec3\u59d1\u5a18\uff0c\u4f60\u771f\u662f\u5feb\u4eba\u5feb\u8bed\uff01\u201d \u3000\u3000\u738b\u8d5e\u5012\u4e86\u4e24\u676f\u8309\u8389\u9999\u8336\uff0c\u7389\u7f57\u5239\u4e00\u53e3\u559d\u5b8c\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u676f\u5b50\u592a\u5c0f\u3002\u201d\u718a\u5ef7\u5f3c\u5fd9\u9053\uff1a \u201c\u597d\uff0c\u6362\u8fc7\u5927\u7897\u6765\u3002\u7ec3\u59d1\u5a18\uff0c\u4f60\u559d\u9152\u5417\uff1f\u6211\u559d\u9152\u65f6\uff0c\u4e5f\u603b\u662f\u7528\u5927\u7897\u7684\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u600e\u9ebd \u4e0d\u559d\uff0c\u559d\u9152\u6211\u4e5f\u7528\u5927\u676f\u7684\u3002\u4e0d\u8fc7\uff0c\u4eca\u5929\u6211\u4e0d\u80fd\u559d\uff0c\u4f60\u4e0d\u5fc5\u5ba2\u6c14\u3002\u4f60\u8fd9\u8336\u5f88\u9999\uff0c\u6211\u5012\u53ef\u4ee5\u591a\u559d \u4e00\u7897\u3002\u201d\u718a\u5ef7\u5f3c\u6ee1\u6000\u6101\u90c1\uff0c\u7ed9\u5979\u51e0\u53e5\u5999\u8a00\u5999\u8bed\uff0c\u9a71\u5f97\u4e91\u6d88\u59fb\u6563\uff0c\u7b11\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u5750\u4e0b\u6765\u597d \u597d\u4e00\u8c08\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u7528\u624b\u8098\u78b0\u4e86\u4e00\u78b0\u9f99\u8fbe\u53c1\uff0c\u9053\uff1a\u201c\u6211\u4eec\u53ef\u4e0d\u80fd\u597d\u597d\u7684\u8c08\u3002\u201d\u718a\u5ef7\u5f3c\u4e00\u6115\uff0c\u968f\u5373\u7b11 \u9053\uff1a\u201c\u4f60\u4eec\u60f3\u662f\u6709\u4ec0\u9ebd\u4e8b\u60c5\u8981\u89c1\u6211\u4e86\u3002\u8fbe\u53c1\uff0c\u4f60\u8bf4\u3002\u201d\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u7ecf\u7565\u5927\u4eba\u4e3a\u56fd\u5ba3\u52b3\uff0c\u4e07 \u91cc\u56de\u6765\uff0c\u5c0f\u4eba\u4e00\u65e0\u793c\u7269\u8868\u8fbe\u5bf8\u5fc3\uff0c\u53cd\u800c\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7389\u7f57\u5239\u5ffd\u76b1\u7709\u5934\uff1a\u201c\u4f60\u8fd9\u4eba\u600e\u9ebd \u7684\uff0c\u8bf4\u8bdd\u8fd9\u9ebd\u6587\u7ec9\u7ec9\u7684\uff0c\u8bdd\u4e0d\u5230\u9898\uff01\u201d\u718a\u5ef7\u5f3c\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u59d1\u5a18\u8bf4\u5f97\u5bf9\uff01\u9f99\u8fbe\u53c1\uff0c\u4f60 \u8be5\u7f5a\u4e00\u676f\u3002\u4f60\u5feb\u8bf4\uff0c\u4f60\u53ef\u6709\u4ec0\u9ebd\u4e8b\u60c5\u8981\u6211\u5e2e\u5fd9\u5417\uff1f\u201d\u9f99\u8fbe\u53c1\u6da8\u7ea2\u4e86\u9762\uff0c\u5450\u5450\u8bf4\u9053\uff1a\u201c\u5927\u4eba\u6709 \u6ca1\u6709\u718a\u80c6\u5e26\u56de\uff0c\u6211\u60f3\u6c42\u4eba\u4eba\u8d4f\u8d50\u3002\u201d\u718a\u5ef7\u5f3c\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5c0f\u4e8b\u4e5f\u503c\u5f97\u6302\u9f7f\u4e86\u5bf9\u4e86\uff0c\u718a\u80c6\u662f\u6b62 \u75db\u6563\u7600\u7684\u826f\u836f\uff0c\u6b63\u5408\u4f60\u4eec\u7f25\u5c40\u4f7f\u7528\u3002\u738b\u8d5e\uff0c\u628a\u6211\u5e26\u56de\u7684\u5206\u4e00\u534a\u7ed9\u4ed6\u3002\u201d\u53c8\u9053\uff1a\u201c\u6211\u672c\u6765\u51c6\u5907 \u53eb\u4eba\u9001\u53bb\u7ed9\u4f60\u7684\u3002\u8fd9\u4e24\u5929\u4e8b\u60c5\u592a\u591a\u4e86\uff0c\u4e00\u4e0b\u5b50\u5c31\u5fd8\u4e86 \u3000\u3000\u7389\u7f57\u5239\u4e00\u53cc\u773c\u73e0\u5706\u6e9c\u6e9c\u7684\u8f6c\u4e86\u51e0\u8f6c\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u5b98\u513f\u5012\u4e0d\u9519\uff0c\u548c\u6211\u4eec\u7eff\u6797\u8c6a\u6770 \u7684\u813e\u6c14\u76f8\u5dee\u4e0d\u591a\uff01\u201d\u6768\u6d9f\u53d8\u4e86\u9762\u8272\u3002\u718a\u5ef7\u5f3c\u53ea\u662f\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u7eff\u6797\u4e2d\u7684\u5973\u8c6a\u6770 \u5417\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u6211\u81ea\u5df2\u4e5f\u4e0d\u77e5\u9053\u662f\u4e0d\u662f\u8c6a\u6770\uff1f\u201d\u718a\u5ef7\u5f3c\u7b11\u4e86\u4e00\u7b11\uff0c\u5374\u6b63\u8272\u9053\uff1a \u201c\u505a\u66ff\u5929\u884c\u9053\u7684\u7eff\u6797\u8c6a\u6770\u4e5f\u65e0\u6240\u8c13\u3002\u4e0d\u8fc7\u6ee1\u6d32\u9791\u5b50\u90fd\u5feb\u8981\u6253\u6765\u5566\uff0c\u7eff\u6797\u4e2d\u7684\u8c6a\u6770\u8fd8\u662f\u8be5\u542c\u671d \u5ef7\u62db\u5b89\uff0c\u540c\u5fa1\u5916\u4fae\u7684\u597d\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u82e5\u662f\u4f60\u8fd9\u6837\u7684\u5b98\u513f\u53bb\u62db\u5b89\uff0c\u5927\u7ea6\u8fd8\u6709\u4eba\u542c\u4f60\u7684\u8bdd\uff0c \u5176\u4ed6\u7684\u5b98\u513f\u8c01\u4e2a\u7406\u4ed6\uff01\u4f9d\u6211\u8bf4\uff0c\u4e5f\u4e0d\u5fc5\u8bf4\u8c01\u62db\u5b89\u8c01\uff0c\u6ee1\u6d32\u9791\u5b50\u6253\u6765\uff0c\u54b1\u4eec\u5927\u5bb6\u63cd\u4ed6\uff01\u201d\u718a\u5ef7 \u5f3c\u9ed8\u7136\u4e0d\u8bed\uff0c\u6014\u6014\u7684\u770b\u7740\u7389\u7f57\u5239\uff01 \u3000\u3000\u718a\u5ef7\u5f3c\u6df1\u77e5\u671d\u653f\u8150\u8d25\uff0c\u5bf9\u7eff\u6797\u5f3a\u76d7\uff0c\u53ea\u662f\u7528\u201c  \u201d\uff0c\u5076\u5c14\u62db\u5b89\uff0c\u4e5f\u53ea\u662f\u51fa\u65bc\u5c06\u5e05\u7684\u79c1 \u5fc3\uff0c\u60f3\u6536\u4e3a\u5df2\u7528\uff0c\u6269\u5145\u52bf\u529b\u7f62\u4e86\u3002\u602a\u4e0d\u5f97\u7389\u7f57\u5239\u8bf4\u522b\u7684\u5b98\u513f\u4e0d\u6210\uff0c\u4ed6\u4eec\u4e5f\u7684\u786e\u662f\u96be\u4ee5\u4ee4\u4eba\u5fc3 \u670d\u3002\u7389\u7f57\u5239\u89c1\u4ed6\u770b\u7740\u81ea\u5df1\u51fa\u795e\uff0c\u9053\uff1a\u201c\u600e\u9ebd\uff1f\u6211\u8bf4\u9519\u8bdd\u4e86\uff1f\u201d\u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u8bf4\u9519\u3002\u201d \u6768\u6d9f\u662f\u5175\u90e8\u5927\u5458\uff0c\u4e24\u5929\u524d\u8fd8\u7980\u627f\u7687\u5e1d\u4e4b\u547d\u201c\u5176\u5b9e\u662f\u5ba2\u6c0f\u7684\u4e3b\u610f\u201d\uff0c\u6d3e\u5218\u5ef7\u5143\u53bb\u9655\u897f\u201c\u88ad \u532a\u201d\uff0c\u542c\u7389\u7f57\u5239\u81ea\u8868\u8eab\u5206\uff0c\u60f3\u8d77\u9655\u897f\u544a\u6025\u7684\u6587\u4e66\u4e2d\uff0c\u679c\u7136\u6709\u4e00\u80a1\u76d7\u532a\uff0c\u532a\u9996\u53eb\u505a\u7389\u7f57\u5239\u7684\u3002 \u5f53\u65f6\u81ea\u5df1\u56e0\u4e3a\u8fd9\u4e2a\u532a\u9996\u662f\u4e2a\u5973\u7684\uff0c\u8fd8\u7279\u522b\u7559\u5fc3\uff0c\u60f3\u4e0d\u5230\u5c31\u662f\u8fd9\u4e2a\u7f8e\u82e5\u5929\u4ed9\u7684\u5973\u5b50\uff0c\u4e00\u65f6\u4e0d\u77e5 \u6240\u63aa\uff0c\u5750\u7acb\u4e0d\u5b89\u3002\u718a\u5ef7\u5f3c\u77e5\u4ed6\u5fc3\u610f\uff0c\u7b11\u9053\uff1a\u201c\u6768\u5144\uff0c\u8fd9\u4f4d\u59d1\u5a18\u73b0\u5728\u6765\u63a2\u671b\u6211\uff0c\u5979\u53ef\u662f\u6211\u7684\u670b \u53cb\u3002\u201d\u6768\u6d9f\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u201d\u5fc3\u60f3\u718a\u5ef7\u5f3c\u771f\u662f\u4e2a\u602a\u4eba\uff0c\u548c\u8fd9\u4e2a\u5973\u5f3a\u76d7\u8c08\u5f97\u8fd9\u9ebd\u6b22\u6d3d\uff0c\u5012\u771f \u50cf\u591a\u5e74\u8001\u53cb\u4f3c\u7684\u3002\u4e0d\u8fc7\u718a\u5ef7\u5f3c\u65e2\u7136\u5982\u6b64\u8868\u793a\uff0c\u6768\u6d9f\u4e5f\u5c31\u653e\u4e0b\u4e86\u5fc3\uff0c\u4e0d\u518d\u7d27\u5f20\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u738b\u8d5e\u5df2\u628a\u718a\u80c6\u6562\u4e86\u51fa\u6765\uff0c\u5305\u4e86\u597d\u5927\u4e00\u5305\uff0c\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u54df\uff0c\u592a\u591a\u4e86\uff01\u201d\u718a\u5ef7 \u5f3c\u900d\uff1a\u201c\u4f60\u4eec\u9556\u5c40\u53cd\u6b63\u6709\u7528\uff0c\u62ff\u53bb\u5427\uff01\u201d\u9f99\u8fbe\u53c1\u63a5\u8fc7\u718a\u80c6\uff0c\u6b63\u60f3\u544a\u8f9e\uff0c\u718a\u5ef7\u5f3c\u5bf9\u7389\u7f57\u5239\u751a\u4e3a \u8d4f\u8bc6\uff0c\u771f\u6068\u4e0d\u5f97\u6709\u4e2a\u5973\u513f\u4f3c\u5979\u4e00\u6837\uff0c\u770b\u7740\u5979\u7684\u4f69\u5251\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u7ec3\u59d1\u5a18\uff0c\u4f60\u7684\u5251\u6cd5\u662f\u8c01\u6559 \u7684\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u95ee\u8fd9\u4e2a\u5e72\u5417\uff1f\u201d\u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u4f60\u7684\u5251\u6cd5\u9ad8\u660e\u6781\u4e86\uff0c\u6211\u867d\u7136\u4e0d\u7cbe\u5251\u672f\uff0c \u4f46\u5374\u6700\u559c\u6b22\u770b\u4eba\u6bd4\u5251\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u662f\u5927\u5b98\uff0c\u8981\u4e0d\u7136\u4eca\u5929\u6211\u5c31\u8bf7\u4f60\u53bb\u770b\u6bd4\u5251\u3002\u201d\u718a \u5ef7\u5f3c\u5ffd\u9053\uff1a\u201c\u7ec3\u59d1\u5a18\uff0c\u8fd9\u4f4d\u662f\u6211\u7684\u53c2\u8d5e\u540d\u53eb\u5cb3\u545c\u73c2\u2026\u2026\u201d\u7389\u7f57\u5239\u622a\u7740\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d\u718a\u5ef7 \u5f3c\u9053\uff1a\u201c\u4ed6\u7684\u5251\u6cd5\u5728\u6211\u519b\u4e2d\u53f7\u79f0\u7b2c\u4e00\uff0c\u4f60\u613f\u4e0d\u613f\u610f\u548c\u4ed6\u6bd4\u4e00\u6bd4\uff0c\u70b9\u5230\u4e3a\u6b62\uff0c\u4e0d\u51c6\u4f24\u4eba\u3002\u201d\u7389 \u7f57\u5239\u5ffd\u51b7\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u5cb3\u9e23\u73c2\uff0c\u539f\u6765\u4f60\u8fd8\u4e0d\u670d\u6c14\uff0c\u597d\uff0c\u54b1\u4eec\u518d\u6bd4\u4e00\u6bd4\u3002\u201d\u55d6\u7684\u4e00\u58f0\uff0c\u62d4\u51fa\u5251 \u6765\u3002\u6768\u6d9f\u5413\u5f97\u8eb2\u5230\u6905\u5f8c\uff0c\u718a\u5ef7\u5f3c\u542c\u5f97\u8bdd\u91cc\u6709\u56e0\uff0c\u5fd9\u9053\uff1a\u201c\u6162\u6765\uff0c\u545c\u73c2\uff0c\u4f60\u4ee5\u524d\u548c\u5979\u6bd4\u8fc7\u5251 \u7684\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4e0d\u6b62\u4e00\u6b21\u4e86\uff0c\u54ce\u5440\uff0c\u5929\u8272\u4e0d\u65e9\uff0c\u4f60\u82e5\u672a\u56de\u8fb9\u5173\uff0c\u4ee5\u5f8c\u6211\u518d\u544a\u8bc9\u4f60\u3002\u5cb3\u9e23 \u73c2\uff0c\u54b1\u4eec\u8fd9\u573a\u6bd4\u5251\uff0c\u8bb0\u4e0b\u6765\u5427\u3002\u201d\u718a\u5ef7\u5f3c\u820d\u4e0d\u5f97\u5979\u7acb\u5373\u79bb\u5f00\uff0c\u770b\u770b\u65e5\u5f71\u9053\uff1a\u201c\u8fd8\u5dee\u4e00\u70b9\u624d\u5230 \u6b63\u5348\uff0c\u600e\u9ebd\u8bf4\u5929\u8272\u4e0d\u65e9\u3002\u201d\u7389\u7f57\u5239\u6df1\u6015\u718a\u5ef7\u5f3c\u4e00\u5b9a\u8981\u7559\u5979\u548c\u5cb3\u9e23\u73c2\u6bd4\u5251\uff0c\u51b2\u53e3\u8bf4\u9053\uff1a\u201c\u6211\u8981 \u548c\u7ea2\u82b1\u9b3c\u6bcd\u6bd4\u5251\uff0c\u4f60\u77e5\u9053\u4ec0\u9ebd\uff01\u201d\u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u4ec0\u9ebd\u7ea2\u82b1\u9b3c\u6bcd\uff01\u8fd9\u540d\u5b57\u597d\u602a\uff01\u201d \u3000\u3000\u5cb3\u545c\u73c2\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u7684\u5e08\u7236\u970d\u5929\u90fd\u662f\u6b66\u6797\u524d\u8f88\uff0c\u89c1\u591a\u8bc6\u5e7f\u3002\u5cb3\u545c\u73c2\u5728\u5929\u5c71\u4e4b\u65f6\uff0c\u5df2\u542c\u4ed6 \u8bf4\u8fc7\u7ea2\u82b1\u9b3c\u6bcd\u7684\u6545\u4e8b\u3002\u5fd9\u62c9\u4e86\u62c9\u718a\u5ef7\u5f3c\uff0c\u9053\uff1a\u201c\u5927\u5e05\uff0c\u6211\u6709\u8bdd\u8981\u548c\u4f60\u8bf4\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60 \u4e0d\u80fd\u5f3a\u7559\u6211\u5728\u6b64\u5730\u6bd4\u5251\uff01\u201d\u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u653e\u5fc3\uff0c\u4f60\u6709\u4e8b\u60c5\uff0c\u6bd4\u5251\u4ee5\u5f8c\u518d\u8bf4\uff0c\u4f60\u7a0d\u5f85 \u4e00\u4f1a\u3002\u597d\uff0c\u545c\u73c2\uff0c\u6709\u4ec0\u9ebd\u8bdd\u5feb\u8bf4\u3002\u201d\u5cb3\u545c\u73c2\u628a\u718a\u5ef7\u5f3c\u626f\u5230\u5c4f\u98ce\u80cc\u5f8c\uff0c\u7ea6\u8fc7\u4e86\u4e00\u76cf\u837c\u7684\u65f6\u523b\uff0c \u8fd8\u672a\u51fa\u6765\u3002\u9f99\u8fbe\u53c1\u7684\u5fc3\u535c\u535c\u7684\u8df3\u3002 \u3000\u3000\u9f99\u8fbe\u53c1\u53ea\u9053\u5cb3\u545c\u73c2\u4e0d\u80af\u653e\u8fc7\u7389\u7f57\u5239\uff0c\u5fc3\u60f3\uff1a\u8fd9\u5973\u9b54\u5934\u771f\u662f\u5929\u5927\u80c6\u5b50\uff0c\u7adf\u7136\u5728\u718a\u5ef7\u5f3c\u9762 \u524d\uff0c\u81ea\u8868\u8eab\u5206\u3002\u6211\u82e5\u77e5\u5979\u5982\u6b64\uff0c\u600e\u9ebd\u4e5f\u4e0d\u5e26\u5979\u6765\u3002\u718a\u5ef7\u5f3c\u8eab\u4e3a\u5927\u5c06\uff0c\u5c82\u6709\u89c1\u4e86\u5f3a\u76d7\uff0c\u4e5f\u4e0d\u6355 \u62ff\u7684\u9053\u7406\u3002\u8fd9\u56de\u5b9a\u9003\u4e0d\u4e86\u3002\u7389\u7f57\u5239\u5012\u662f\u795e\u8272\u81ea\u5982\uff0c\u718a\u5ef7\u5f3c\u8c08\u5410\u4e4b\u4e2d\uff0c\u81ea\u7136\u6709\u4e00\u79cd\u4ee4\u5979\u4fe1\u670d\u7684 \u529b\u91cf\u3002\u5979\u60f3\u718a\u5ef7\u5f3c\u8bf4\u8fc7\u5f53\u5979\u670b\u53cb\uff0c\u5f53\u7136\u5c31\u662f\u670b\u53cb\uff0c\u534a\u70b9\u4e5f\u6ca1\u7591\u5fc3\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u718a\u5ef7\u5f3c\u548c\u5cb3\u545c \u73c2\u51fa\u6765\uff0c\u7b11\u9053\uff1a\u201c\u7ec3\u59d1\u5a18\uff0c\u4f60\u8fc7\u6765\uff01\u201d\u7389\u7f57\u5239\u6beb\u4e0d\u5728\u610f\u7684\u8d70\u4e86\u8fc7\u53bb\u3002\u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u6211\u672c\u60f3\u9001 \u4f60\u4e00\u4ef6\u793c\u7269\uff0c\u4f46\u5728\u5ba2\u9014\u4e4b\u4e2d\uff0c\u5374\u62ff\u4e0d\u51fa\u597d\u4e1c\u897f\u6765\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u54c8\uff0c\u6211\u4ee5\u4e3a\u4f60\u6709\u4ec0\u9ebd\u8bdd\u8981 \u548c\u6211\u8bf4\uff0c\u4f60\u5374\u8981\u548c\u6211\u8bb2\u5ba2\u5957\u3002\u4ea4\u670b\u53cb\u4e0d\u5fc5\u9001\u793c\u7684\u3002\u6211\u751f\u5e73  \u6536\u5f3a\u76d7\u5934\u5b50\u7684\u793c\u7269\uff0c\u5bf9\u670b\u53cb\u7684\u4e1c \u897f\uff0c\u6211\u53ef\u4e0d\u8981\u3002\u201d\u718a\u5ef7\u5f3c\u7eed\u9053\uff1a\u201c\u6211\u867d\u7136\u6ca1\u6709\u793c\u7269\u9001\u4f60\uff0c\u4f46\u6211\u5374\u8981\u501f\u4e00\u4ef6\u7ed9\u4f60\uff0c\u4f60\u7528\u4e86\u4e4b \u5f8c\uff0c\u4e00\u5b9a\u8981\u4ea4\u8fd8\u7684\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u54c8\uff01\u501f\u4e00\u4ef6\u7ed9\u6211\uff01\u8fd9\u5012\u65b0\u5947\u3002\u6211\u5012\u8981\u770b\u770b\u662f\u4ec0\u9ebd\u4e1c \u897f\uff1f\u201d\u718a\u5ef7\u5f3c\u62ff\u51fa\u4e00\u5bf9\u624b\u5957\uff0c\u7b11\u9053\uff1a\u201c\u7ec3\u59d1\u5a18\uff0c\u4f60\u5f53\u4e0d\u5f53\u6211\u662f\u670b\u53cb\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u82e5\u4e0d \u628a\u4f60\u5f53\u670b\u53cb\u770b\u5f85\uff0c\u600e\u4f1a\u548c\u4f60\u5f53\u5927\u5b98\u7684\u8c08\u8fd9\u9ebd\u4e45\uff1f\u201d\u718a\u5ef7\u5f3c\u6e29\u8a00\u8bf4\u9053\uff1a\u201c\u90a3\u9ebd\u6211\u6c42\u4f60\u4e00\u4ef6\u4e8b\u4f60 \u7b54\u4e0d\u7b54\u5e94\uff1f\u201d\u7389\u7f57\u5239\u559c\u9053\uff1a\u201c\u4f60\u6709\u4e8b\u8981\u6c42\u6211\uff1f\u54c8\uff0c\u6c64\u91cc\u706b\u91cc\uff0c\u4e07\u6b7b\u4e0d\u8f9e\uff01\u201d\u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u7b49 \u4f1a\u4f60\u53bb\u6597\u90a3\u4e2a\u4ec0\u9ebd\u7ea2\u82b1\u9b3c\u6bcd\u4e4b\u65f6\uff0c\u4e00\u5b9a\u8981\u628a\u8fd9\u5bf9\u624b\u5957\u5e26\u4e0a\uff0c\u7528\u5b8c\u4e4b\u5f8c\uff0c\u518d\u9001\u56de\u6765\u3002\u201d\u7389\u7f57\u5239 \u89c1\u8fd9\u5bf9\u624b\u5957\u91d1\u5149\u5fae\u95ea\uff0c\u597d\u50cf\u4e0d\u662f\u7528\u666e\u901a\u4e1d\u7ebf\u7ec7\u6210\uff0c\u751a\u4e3a\u559c\u7231\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u542c\u4f60\u7684\u8bdd\u3002\u201d\u718a \u5ef7\u5f3c\u76f4\u9001\u5979\u51fa\u5230\u95e8\u53e3\uff0c\u8fd9\u624d\u9053\u522b\u3002 \u3000\u3000\u7389\u7f57\u5239\u98de\u5feb\u8d76\u56de\u9556\u5c40\uff0c\u9556\u5c40\u91cc\u7684\u5925\u8ba1\u65e9\u628a\u836f\u4e38\u914d\u597d\uff0c\u53ea\u7b49\u718a\u80c6\u4e00\u6765\uff0c\u9a6c\u4e0a\u7814\u6210\u788e\u672a\uff0c\u6df7 \u5165\u4e38\u4e2d\u3002\u9f99\u8fbe\u53c1\u53d6\u51fa\u4e24\u526f\u4e0a\u597d\u7684\u62a4\u5fc3\u94dc\u955c\uff0c\u53c8\u628a\u7409\u749c\u5305\u4e86\u4e24\u5305\uff0c\u4e00\u4e00\u4ea4\u7ed9\u7389\u7f57\u5239\u6536\u597d\uff0c\u9053\uff1a \u201c\u767d\u5929\u4e0d\u4fbf\u65bd\u5c55\u8f7b\u529f\uff0c\u4f60\u4e58\u6211\u7684\u5feb\u9a6c\u53bb\u5427\uff01\u5230\u4e86\u5c71\u811a\uff0c\u4f60\u518d\u5f03\u9a6c\u767b\u5c71\u3002\u201d\u7389\u7f57\u5239\u4e00\u58f0\uff1a\u201c\u591a \u8c22\uff01\u201d\u8de8\u4e0a\u9a6c\u80cc\uff0c\u98de\u9a70\u800c\u53bb\u3002\u51fa\u4e86\u57ce\u95e8\uff0c\u7ea2\u65e5\u5df2\u8fc7\u4e2d\u5929\uff0c\u7389\u7f57\u5239\u9053\uff1a\u7cdf\uff0c\u8fd9\u56de\u662f\u81ea\u5df1\u7b2c\u4e00\u6b21 \u7684\u5931\u7ea6\u4e86\uff01 \u3000\u3000\u518d\u8bf4\u767d\u77f3\u9053\u4eba\u548c\u5353\u4e00\u822a\u79bb\u5f00\u67f3\u5bb6\uff0c\u8d76\u5f80\u897f\u90ca\u3002\u8def\u4e0a\u5353\u4e00\u822a\u95ee\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u4e3a\u4ec0\u9ebd\u7ea6\u5979\u5728 \u79d8\u9b54\u5d16\u6bd4\u5251\uff1f\u201d\u767d\u77f3\u9053\uff1a\u201c\u79d8\u9b54\u5d16\u5ca9\u77f3\u5e95\u4e0b\u6709\u4e2a\u77f3\u5ba4\uff0c\u636e\u4f20\u5510\u671d\u7684\u65f6\u5019\u6709\u4e00\u4e2a\u540d\u53eb\u201c\u5362\u5e08\u201d \u7684\u548c\u5c1a\u66fe\u5728\u90a3\u91cc\u4f4f\u8fc7\u3002\u5362\u5e08\u662f\u6606\u5362\u5251\u6d3e\u7684\u7956\u5e08\uff0c\u4ed6\u7684\u5251\u6cd5\u7cbe\u4e49\u65e9\u5df2\u5931\u4f20\uff0c\u73b0\u5728\u7684\u6606\u5362\u5251\u6d3e \u5f97\u4ed6\u7684\u76ae\u6bdb\u800c\u5df2\u3002\u542c\u8bf4\u77f3\u5ba4\u4e2d\u8fd8\u6709\u5362\u5e08\u9057\u8ff9\uff0c\u5b66\u6b66\u4e4b\u4eba\uff0c\u6bcf\u5230\u90a3\u91cc\uff0c\u90fd\u662f\u6d41\u8fde\u5fd8\u8fd4\uff0c\u4f60\u662f\u6211 \u6d3e\u672a\u6765\u7684\u638c\u95e8\uff0c\u5e94\u8be5\u5230\u90a3\u91cc\u89c1\u8bc6\u89c1\u8bc6\u3002\u800c\u4e14\u79d8\u9b54\u5d16\u662f\u6709\u540d\u7684\u9669\u5cfb\u8352\u50fb\u4e4b\u5730\uff0c\u5728\u5317\u4eac\u8fd1\u90ca\uff0c\u53ef \u96be\u627e\u5230\u8fd9\u6837\u4e00\u5904\u826f\u597d\u7684\u6bd4\u5251\u573a\u6240\u3002\u201d\u5353\u4e00\u822a\u5fc3\u60f3\uff1a\u4f60\u548c\u7389\u7f57\u5239\u6bd4\u5251\uff0c\u53eb\u6211\u90a3\u6709\u4ec0\u9ebd\u5fc3\u7eea\u73a9 \u8d4f\u3002\u5fc3\u4e2d\u4e00\u8def\u76d8\u7b97\uff0c\u5982\u4f55\u66ff\u4ed6\u4eec\u5316\u89e3\uff0c\u4e0d\u77e5\u4e0d\u89c9\uff0c\u5df2\u5230\u897f\u5c71\u3002 \u3000\u3000\u767d\u77f3\u9053\u4eba\u62ac\u5934\u4e00\u770b\uff0c\u9053\uff1a\u201c\u6211\u4eec\u6765\u5f97\u65e9\u4e86\uff0c\u8fd8\u672a\u5230\u4e2d\u5348\u5462\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u4eec\u5148\u5230\u79d8 \u9b54\u5d16\u5019\u5979\u3002\u201d\u767d\u77f3\u9053\uff1a\u201c\u5019\u5979\uff1f\u5979\u597d\u5927\u7684\u67b6\u5b50\uff1f\u201d\u5353\u4e00\u822a\u4e0d\u6562\u56de\u7b54\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u9ebd\u56db\u5e08\u53d4\u8fd1 \u6765\u597d\u50cf\u5fc3\u80f8\u8d8a\u6765\u8d8a\u72ed\u7a84\u4e86\uff0c\u4ee5\u524d\u5374\u4e0d\u662f\u8fd9\u4e2a\u6837\u5b50\u3002\u201d\u53c8\u60f3\u8d77\u548c\u4ed6\u4e00\u8def\u540c\u884c\u4e4b\u65f6\uff0c\u4ed6\u603b\u662f\u6545\u610f \u8ba9\u81ea\u5df2\u548c\u4ed6\u7684\u5973\u513f\u63a5\u8fd1\uff0c\u4ed6\u5bf9\u7389\u7f57\u5239\u7684\u4ec7\u6068\uff0c\u83ab\u975e\u4e5f\u4e0e\u6b64\u6709\u5173\u3002\u601d\u524d\u60f3\u5f8c\uff0c\u8d8a\u53d1\u95f7\u95f7\u4e0d\u4e50\u3002 \u3000\u3000\u767d\u77f3\u9053\uff1a\u201c\u4f60\u60f3\u4ec0\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6ca1\u4ec0\u9ebd\u3002\u5e08\u53d4\u3001\u6211\u770b\u8fd9\u573a\u6bd4\u5251\u8fd8\u662f\u514d\u4e86\u5427\uff01\u201d\u767d \u77f3\u9053\uff1a\u201c\u80e1\u8bf4\u3002\u6b66\u5f53\u6d3e\u7684\u4eba\u4ece\u4e0d\u602f\u573a\uff01\u201d\u5fc3\u60f3\uff1a\u5148\u5230\u79d8\u9b54\u5d16\u770b\u6e05\u695a\u5730\u5f62\u4e5f\u6709\u597d\u5904\u3002\u98de\u6b65\u767b \u5c71\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u53ea\u89c1\u4e00\u5757\u7855\u5927\u65e0\u6bd4\u7684\u5ca9\u77f3\uff0c\u4ece\u5c71\u9876\u4e0a\u51ed\u7a7a\u4f38\u51fa\uff0c\u4e0b\u9762\u6709\u4e00\u7247\u5e73\u5730\uff0c\u5c31\u597d\u50cf\u5f20 \u5f00\u4e86\u7684\u72ee\u5b50\u5634\u4e00\u822c\u3002\u767d\u77f3\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u79d8\u9b54\u5d16\u4e86\uff0c\u54b1\u4eec\u4e0a\u53bb\uff01\u201d\u4e24\u4eba\u65bd\u5c55\u8f7b\u529f\uff0c\u5230\u4e86\u4e0a\u9762\uff0c \u767d\u77f3\u9053\u4eba\u5ffd\u7136\u54a6\u7684\u4e00\u58f0\uff0c\u53eb\u4e86\u51fa\u6765\uff01 \u3000\u3000\u90a3\u7247\u5e73\u5730\u5806\u7740\u4e00\u5806\u5806\u77f3\u5934\uff0c\u597d\u50cf\u4ec0\u9ebd\u9635\u56fe\u4e00\u6837\uff0c\u767d\u77f3\u9053\uff1a\u201c\u7389\u7f57\u5239\u6363\u4ec0\u9ebd\u9b3c\uff1f\u201d\u548c\u5353\u4e00 \u822a\u8fdb\u5165\u77f3\u5934\u9635\uff0c\u8d70\u4e86\u4e00\u9635\uff0c\u53ea\u89c9\u5176\u4e2d\u5343\u95e8\u4e07\u6237\uff0c\u590d\u6742\u5f02\u5e38\uff0c\u597d\u50cf\u662f\u6309\u4e94\u884c\u516b\u5366\u6240\u5e03\u7684\u9635\u56fe\u3002 \u5bf9\u4e94\u884c\u516b\u5366\u4e4b\u9635\uff0c\u6b66\u5f53\u79d8\u7b08\u4e5f\u8f7d\u6709\uff0c\u4f46\u767d\u77f3\u9053\u4eba\u5374\u4e0d\u751a\u7cbe\uff0c\u7ed5\u6765\u7ed5\u53bb\uff0c\u597d\u4e00\u4f1a\u4e86\uff0c\u627e\u4e0d\u5230\u51fa \u8def\u3002\u767d\u77f3\u6012\u9053\uff1a\u201c\u4e0d\u83c5\u8fd9\u9b54\u5934\u6363\u4ec0\u9ebd\u9b3c\u3002\u6211\u628a\u5979\u7684\u77f3\u5934\u626b\u8361\u4e86\u518d\u8bf4\u3002\u201d\u4f38\u817f\u4e00\u626b\uff0c\u628a\u4e00\u5806\u77f3 \u5934\u8e22\u5f97\u5230\u5904\u4e71\u98de\uff0c\u649e\u5728\u5176\u4ed6\u7684\u77f3\u5934\u4e0a\uff0c\u628a\u597d\u51e0\u5806\u77f3\u5934\u649e\u6563\uff0c\u767d\u77f3\u9053\u4eba\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u7b11\u58f0\u672a\u505c\uff0c\u5ffd\u7136\u6709\u4eba\u9634\u607b\u607b\u7684\u51b7\u7b11\u9053\uff1a\u201c\u4f55\u7269\u5c0f\u5b50\uff0c\u80c6\u6562\u6363\u4e71\u6211\u7ec3\u529f\u7684\u77f3\u9635\u3002\u201d\u8bdd\u58f0\u5c16 \u9510\u523a\u8033\uff0c\u5c31\u597d\u50cf\u6709\u4eba\u5bf9\u7740\u8033\u6735\u53eb\u558a\u4e00\u822c\uff0c\u767d\u77f3\u9053\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u6e38\u76ee\u56db\u987e\uff0c\u4e0d\u89c1\u4eba\u5f71\uff0c\u767d\u77f3 \u9053\uff1a\u201c\u4f60\u662f\u4ec0\u9ebd\u9b3c\u602a\uff1f\u201d\u5ffd\u5730\u773c\u775b\u4e00\u4eae\uff0c\u5ca9\u77f3\u4e0b\u5ffd\u7136\u73b0\u51fa\u4e00\u4e2a\u9e21\u76ae\u9e64\u53d1\uff0c\u7126\u9ec4\u67af\u7626\u7684\u8001\u5987 \u4eba\uff0c\u62ff\u7740\u4e00\u6839\u62d0\u6756\uff0c\u9b13\u8fb9\u63d2\u4e86\u4e00\u6735\u7ea2\u82b1\uff0c\u6253\u626e\u5f97\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u771f\u50cf\u9b3c\u9b45\u73b0\u5f62\uff0c\u5c71\u9b41\u51fa\u4e16\u3002\u9762\u4e0a \u4f3c\u6012\u4f3c\u7b11\uff0c\u9976\u662f\u767d\u77f3\u9053\u4eba\u827a\u9ad8\u80c6\u5927\uff0c\u4e5f\u611f\u5230\u4e00\u9635\u5bd2\u610f\uff0c\u76f4\u900f\u5fc3\u5934\uff01 \u3000\u3000\u90a3\u8001\u5987\u5dc5\u5dcd\u5dcd\u7684\u8d70\u5165\u77f3\u9635\u4e4b\u4e2d\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u4e2a\u5c0f\u8f88\u53eb\u505a\u4ec0\u9ebd\u540d\u5b57\uff0c\u5e08\u7236\u4f55\u4eba\uff1f\u6765\u6b64 \u4f55\u4e3a\uff0c\u8d76\u5feb\u4ece\u5b9e\u62db\u6765\uff01\u201d\u767d\u77f3\u9053\u4eba\u8eab\u4e3a\u6b66\u5f53\u4e94\u8001\u4e4b\u4e00\uff0c\u5e74\u7eaa\u4e5f\u5df2\u6709\u4e94\u5341\u6709\u4e00\uff0c\u51e0\u66fe\u7ed9\u4eba\u8fd9\u6837 \u5c0f\u89c6\uff0c\u547c\u4ed6\u201c\u5c0f\u8f88\u201d\uff0c\u5927\u6012\u8bf4\u9053\uff1a\u201c\u6b66\u5f53\u4e94\u8001\u7684\u540d\u5b57\uff0c\u4f60\u542c\u4eba\u8bf4\u8fc7\u6ca1\u6709\uff01\u201d\u8001\u5987\u4eba\u773c\u76ae\u4e00 \u7ffb\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ec0\u9ebd\u6b66\u5f53\u4e94\u8001\uff0c\u6ca1\u542c\u8bf4\u8fc7\uff1f\u201d\u6b66\u5f53\u4e94\u8001\u7684\u5f97\u540d\uff0c\u662f\u8fd1\u5341\u5e74\u4e4b\u4e8b\uff0c\u8fd9\u8001\u5987\u4eba \u9690\u5c45\u5df2\u53c1\u5341\u5e74\uff0c\u53c1\u5341\u5e74\u524d\uff0c\u767d\u77f3\u9053\u4eba\u8fd8\u662f\u521a\u521a\u4e8c\u5341\u51fa\u5934\u7684\u5c0f\u4f19\u5b50\uff0c\u4f55\u6765\u201c\u4e94\u8001\u201d\u4e4b\u540d\uff0c\u6240\u4ee5 \u8fd9\u8001\u5987\u4eba\u8bf4\u4e0d\u77e5\u9053\uff0c\u786e\u662f\u5b9e\u60c5\u3002\u767d\u77f3\u9053\u4eba\u5374\u4ee5\u4e3a\u201c\u6b66\u5f53\u4e94\u8001\u201d\u4e4b\u540d\uff0c\u5929\u4e0b\u65e0\u4eba\u4e0d\u8bc6\uff0c\u542c\u4e86\u8fd9 \u8001\u5987\u4eba\u7684\u8bdd\uff0c\u4ee5\u4e3a\u5979\u6545\u610f\u8f7b\u89c6\uff0c\u8d8a\u53d1\u5927\u6012\u3002 \u3000\u3000\u5353\u4e00\u822a\u5374\u8eac\u4e86\u8eac\u8170\uff0c\u606d\u656c\u95ee\u9053\uff1a\u201c\u4e0d\u6562\u8bf7\u6559\u8001\u524d\u8f88\u5927\u540d\u3002\u201d\u90a3\u8001\u5987\u4eba\u88c2\u5634\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u5514\uff0c\u4f60\u8fd9\u5b69\u5b50\u8fd8\u61c2\u5f97\u4e00\u70b9\u793c\u8c8c\u3002\u201d\u6307\u7740\u9b13\u8fb9\u7684\u7ea2\u82b1\u9053\uff1a\u201c\u4f60\u80fd\u4e0a\u5230\u79d8\u9b54\u5d16\uff0c\u4e5f\u7b97\u6709\u70b9\u672c \u9886\uff0c\u5e94\u662f\u51fa\u65bc\u9ad8\u4eba\u6240\u6388\u3002\u4f60\u7684\u524d\u8f88\u6ca1\u5bf9\u4f60\u8bf4\u8fc7\u5417\uff1f\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u6735\u7ea2\u82b1\u7684\u6765\u5386\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u5341\u5206\u60f6\u60d1\uff0c\u6447\u4e86\u6447\u5934\u3002\u767d\u77f3\u9053\u4eba\u5ffd\u7136\u60f3\u8d77\u7ea2\u82b1\u9b3c\u6bcd\u7684\u540d\u5b57\uff0c\u9aa4\u5403\u4e00\u60ca\uff0c\u51b2\u53e3\u53eb \u9053\uff1a\u201c\u4f60\u8fd9\u5996\u5987\uff0c\u5c45\u7136\u8fd8\u5728\u4e16\u95f4\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u5927\u6012\uff0c\u6756\u5934\u4e00\u6307\uff0c\u53eb\u9053\uff1a\u201c\u8d3c\u9053\uff0c\u5403\u6211\u4e00 \u62d0\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u4eca\u5e74\u5df2\u516d\u5341\u5f00\u5916\uff0c\u6bd4\u524d\u4efb\u7684\u6b66\u5f53\u638c\u95e8\u7d2b\u9633\u9053\u957f\u5c0f\u51e0\u5e74\uff0c\u767d\u77f3\u9053\u4eba\u66fe\u542c\u5927\u5e08\u5144 \u8bf4\u8fc7\u7ea2\u82b1\u9b3c\u6bcd\u7684\u6545\u4e8b\uff0c\u867d\u7136\u77e5\u5979\u662f\u4e2a\u5f3a\u654c\uff0c\u4f46\u603b\u4ee5\u4e3a\u5f53\u5e74\u90a3\u897f\u5317\u5341\u53c1\u540d\u597d\u624b\uff0c\u4e0d\u662f\u4e00\u6d41\u4eba \u7269\uff0c\u6240\u4ee5\u8d25\u4e5f\u4e0d\u8db3\u4e3a\u5947\u3002\u5bf9\u7ea2\u82b1\u9b3c\u6bcd\u7684\u795e\u5947\u6b66\u529f\uff0c\u4e5f\u603b\u8ba4\u4e3a\u662f\u5938\u5927\u4e4b\u8f9e\uff0c\u867d\u7136\u4e25\u9635\u4ee5\u5f85\uff0c\u5374 \u4e5f\u5e76\u4e0d\u6050\u60e7\u3002 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u5c0f\u8f88\uff0c\u4f60\u8fd8\u4e0d\u8fdb\u62db\uff1f\u201d\u767d\u77f3\u4e5f\u9053\uff1a\u201c\u5996\u5987\u4f60\u8fd8\u4e0d\u8fdb\u62db\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u628a\u62d0 \u6756\u5411\u77f3\u5806\u4e00\u62e8\uff0c\u90a3\u4e9b\u77f3\u5934\u7eb7\u7eb7\u98de\u4e86\u8d77\u6765\uff0c\u4ece\u767d\u77f3\u9053\u4eba\u8eab\u8fb9\u98de\u8fc7\uff0c\u5374\u5e76\u4e0d\u6253\u4e2d\u4ed6\uff0c\u77f3\u5f39\u7eb7\u98de\uff0c \u6e85\u4e86\u767d\u77f3\u9053\u4eba\u4e00\u8eab\u5c18\u571f\uff0c\u767d\u77f3\u5927\u6012\uff0c\u9752\u94a2\u5251\u626c\u7a7a\u4e00\u95ea\uff0c\u84e6\u5730\u4e00\u62db\u201c\u91d1\u9488\u5ea6\u7ebf\u201d\uff0c\u76f4\u53d6\u7ea2\u82b1\u9b3c \u6bcd\u7684\u54bd\u5589\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u968f\u624b\u4e00\u6296\uff0c\u62d0\u6756\u731b\u7136\u538b\u4e0b\uff0c\u767d\u77f3\u9053\u4eba\u659c\u8eab\u6ed1\u6b65\uff0c\u4e00\u7529\u5251\u950b\uff0c\u8ddf\u8ddf\u8dc4\u8dc4\u5411 \u65c1\u51b2\u51fa\u51e0\u6b65\uff0c\u864e\u53e3\u53d1\u70ed\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u5237\u5237\u56de\u8eab\u4e24\u5251\uff0c\u4f7f\u51fa\u4e86\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u593a\u547d\u5251\u7684\u7edd\u62db\uff0c\u524d \u53d1\u5f8c\u81f3\uff0c\u5feb\u901f\u4e4b\u6781\uff01\u7ea2\u82b1\u9b3c\u6bcd\u62d0\u6756\u4e00\u4e3e\uff0c\u5c06\u4e24\u62db\u540c\u65f6\u7834\u53bb\uff0c\u9053\uff1a\u201c\u4f60\u80fd\u5728\u6211\u62d0\u5e95\u9003\u751f\u4e5f\u7b97\u4e0d \u9519\u3002\u201d\u767d\u77f3\u6124\u7136\u8fdb\u5251\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u8fde\u8fdb\u4e03\u62db\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u4e00\u4e00\u7834\u5f00\uff0c\u9053\uff1a\u201c\u5514\uff0c\u4f60\u8fd9\u5251\u6cd5\u6211\u597d \u4f3c\u5728\u90a3\u91cc\u89c1\u8fc7\uff0c\u5f53\u4eca\u4e4b\u4e16\uff0c\u6709\u8fd9\u6837\u7684\u5251\u6cd5\u4e5f\u7b97\u662f\u4e00\u628a\u597d\u624b\u4e86\u3002\u201d\u8c08\u7b11\u4e4b\u95f4\uff0c\u8fde\u8fde\u53cd\u51fb\uff0c\u767d\u77f3 \u9053\u4eba\u7ed9\u8feb\u5f97\u8fde\u8fde\u5f8c\u9000\uff0c\u8dc3\u8fc7\u4e86\u597d\u51e0\u4e2a\u77f3\u5806\uff0c\u60ed\u60ed\u88ab\u7ea2\u82b1\u9b3c\u6bcd\u56f0\u5728\u77f3\u9635\u4e4b\u4e2d\uff0c\u767d\u77f3\u9053\u4eba\u77e5\u9053\u96be \u4ee5\u9003\u8131\uff0c\u811a\u8e0f\u516b\u5366\u65b9\u4f4d\uff0c\u628a\u5251\u4f7f\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u653b\u4e86\u4e94\u5341\u591a\u62db\uff0c\u628a\u767d\u77f3\u9053\u4eba\u6740\u5f97\u6c57\u6c34 \u6dcb\u6f13\uff0c\u4f46\u767d\u77f3\u9053\u4eba\u5b88\u5f97\u5f88\u7a33\uff0c\u62da\u529b\u652f\u6491\uff0c\u7adf\u7136\u4e5f\u65e0\u7834\u7efd\u3002\u7ea2\u82b1\u9b3c\u6bcd\u653b\u52bf\u5ffd\u7f13\uff0c\u559d\u9053\uff1a\u201c\u7d2b\u9633 \u9053\u957f\u662f\u4f60\u4f55\u4eba\uff1f\u201d\u767d\u77f3\u9053\u4eba\u8fd9\u65f6\u7f9e\u6124\u4ea4\u8ff8\uff0c\u4e0d\u613f\u518d\u63d0\u201c\u6b66\u5f53\u4e94\u8001\u201d\u7684\u540d\u5934\uff0c\u4e58\u5979\u653b\u52bf\u6682\u7f13\u4e4b \u9645\uff0c\u7a81\u7136\u4e24\u8bb0\u7edd\u62db\u201c\u9e70\u51fb\u957f\u7a7a\u201d\u201c\u9c7c\u7fd4\u6d45\u5e95\u201d\uff0c\u4e0a\u4e0b\u4e24\u5251\uff0c\u76f4\u53d6\u7ea2\u82b1\u9b3c\u6bcd\u7a74\u9053\u8981\u5bb3\u3002\u7ea2\u82b1\u9b3c \u6bcd\u6012\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b50\u4e0d\u53d7\u62ac\u4e3e\u3002\u201d\u62d0\u6756\u4e00\u6a2a\uff0c\u628a\u4e24\u8bb0\u7edd\u62db\u90fd\u5316\u4e86\u5f00\u53bb\u3002\u5de6\u638c\u4e00\u4f38\uff0c\u547c\u547c\u98ce \u54cd\uff0c\u7802\u77f3\u98de\u626c\uff0c\u5a01\u52bf\u60ca\u4eba\u3002\u767d\u77f3\u9053\u4eba\u62b5\u6321\u5979\u9f99\u5934\u62d0\u6756\uff0c\u5df2\u81ea\u5904\u5728\u4e0b\u98ce\uff0c\u5979\u53d1\u638c\u52a9\u5a01\uff0c\u66f4\u662f\u96be \u654c\uff0c\u5251\u6cd5\u60ed\u60ed\u6563\u4e71\u3002\u5353\u4e00\u822a\u4e00\u770b\u4e0d\u5999\uff0c\u5192\u7740\u7802\u77f3\uff0c\u62d4\u5251\u649e\u6765\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u4e5f\u6765 \u4e86\uff01\u201d\u62d0\u638c\u9f50\u65bd\uff0c\u628a\u4e24\u4eba\u90fd\u56f0\u5728\u77f3\u9635\u4e4b\u4e2d\u3002\u5353\u4e00\u822a\u6bcf\u6321\u4e00\u62d0\uff0c\u8eab\u8eaf\u4fbf\u9707\u4e00\u4e0b\uff0c\u77e5\u5979\u529f\u529b\u592a \u9ad8\uff0c\u65e0\u6cd5\u62b5\u6321\uff0c\u53ea\u597d\u8fde\u8d70\u5de7\u62db\uff0c\u52a9\u5e08\u53d4\u9632\u5b88\u3002\u7ea2\u82b1\u9b3c\u6bcd\u4e5f\u597d\u50cf\u5bf9\u4ed6\u7279\u522b\u7559\u60c5\uff0c\u53ea\u628a\u4ed6\u7684\u5251\u62db \u6321\u5f00\u4fbf\u7b97\uff0c\u5e76\u4e0d\u4f7f\u51fa\u6740\u624b\u3002 \u3000\u3000\u5353\u4e00\u822a\u5251\u6cd5\u6b66\u529f\uff0c\u5728\u6b66\u5f53\u7b2c\u4e8c\u8f88\u4e2d\u9996\u5c48\u4e00\u6307\uff0c\u6bd4\u767d\u77f3\u9053\u4eba\u4e5f\u4e0d\u8fc7\u4ec5\u900a\u4e00\u7b79\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u5bf9 \u4ed6\u624b\u4e0b\u7559\u60c5\uff0c\u4fbf\u5b9c\u4e86\u767d\u77f3\u9053\u4eba\uff0c\u7adf\u81ea\u8f6c\u5371\u4e3a\u5b89\uff0c\u8fd8\u80fd\u51fa\u624b\u53cd\u51fb\u3002\u6253\u4e86\u4e00\u9635\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u53eb\u9053\uff1a \u201c\u5f53\u5e74\u5341\u53c1\u540d\u597d\u624b\u8054\u624b\u6597\u6211\uff0c\u4e5f\u4e0d\u8fc7\u8d70\u4e86\u4e94\u767e\u591a\u62db\uff0c\u73b0\u5728\u5df2\u8d70\u5230\u53c1\u767e\u591a\u62db\uff0c\u4e0d\u80fd\u518d\u8ba9\u4f60\u4eec \u4e86\uff01\u201d\u62d0\u6756\u6a2a\u6311\u76f4\u626b\uff0c\u638c\u529b\u8fdc\u9707\u8fd1\u653b\uff0c\u7802\u77f3\u98de\u626c\u4e2d\u5353\u4e00\u822a\u5192\u6b7b\u6297\u62d2\uff0c\u773c\u770b\u7ea2\u82b1\u9b3c\u6bcd\u4e00\u62d0\u622e\u5230 \u5e08\u53d4\u80f8\u819b\uff0c\u6025\u5fd9\u62a2\u8fdb\u4e00\u5251\uff0c\u523a\u5979\u5de6\u80c1\uff0c\u660e\u77e5\u523a\u5979\u4e0d\u4e2d\uff0c\u4e5f\u8981\u8fdb\u653b\uff0c\u76ee\u7684\u4e0d\u8fc7\u662f\u89e3\u5e08\u53d4\u4e4b\u5371\uff0c \u7ea2\u82b1\u9b3c\u6bcd\u5de6\u638c\u4e00\u5e26\uff0c\u559d\u58f0\uff1a\u201c\u53bb\u201d\uff0c\u5353\u4e00\u822a\u53ea\u89c9\u5982\u817e\u4e91\u9a7e\u96fe\u4e00\u822c\uff0c\u7ed9\u63b7\u51fa\u4e86\u77f3\u9635\u4e4b\u5916\u722c\u4e86\u8d77 \u6765\uff0c\u5c45\u7136\u5e76\u672a\u53d7\u4f24\uff0c\u597d\u751f\u5947\u602a\u3002\u5c31\u5728\u6b64\u65f6\uff0c\u731b\u542c\u5f97\u5e08\u53d4\u4e00\u58f0\u60e8\u53eb\uff0c\u4e5f\u7ed9\u63b7\u51fa\u4e86\u77f3\u9635\u4e4b\u5916\u3002\u5353 \u4e00\u822a\u6025\u5fd9\u5954\u53bb\uff0c\u53ea\u89c1\u5e08\u53d4\u80f8\u8863\u788e\u88c2\uff0c\u80f8\u819b\u4e0a\u6709\u4e24\u9053\u7d2b\u8272\u7684\u4f24\u75d5\uff0c\u9762\u5982\u91d1\u7eb8\uff0c\u6c14\u82e5\u6e38\u4e1d\uff0c\u5353\u4e00 \u822a\u5927\u54ed\u8d77\u6765\uff0c\u633a\u5251\u5411\u7ea2\u82b1\u9b3c\u6bcd\u51b2\u53bb\uff0c\u54ed\u53eb\u9053\uff1a\u201c\u5996\u5987\uff0c\u4f60\u5bb3\u4e86\u6211\u7684\u5e08\u53d4\uff0c\u6211\u4e5f\u548c\u4f60\u62da\u4e86\uff01\u201d \u7f38\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u4e5f\u53eb\u6211\u5996\u5987\uff01\u201d\u6162\u6162\u7684\u4e3e\u8d77\u62d0\u6756\uff0c\u5353\u4e00\u822a\u6b63\u51b2\u5165\u77f3\u9635\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u53eb \u9053\uff1a\u201c\u4e00\u822a\uff0c\u4e00\u822a\uff01\u201d\u5353\u4e00\u822a\u811a\u6b65\u500f\u505c\uff0c\u53eb\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\u5feb\u6765\uff0c\u5e2e\u6211\u6740\u8fd9\u5996\u5987\uff01\u201d\u8f6c\u77ac\u4e4b \u95f2\uff0c\u94c1\u98de\u9f99\u4e0e\u7389\u7f57\u5239\u53cc\u53cc\u5954\u5230\u3002\u6b63\u662f\uff1a\u9b3c\u6bcd\u5de7\u9022\u7389\u7f57\u5239\uff0c\u79c1\u9b54\u5d16\u4e0b\u51b3\u96cc\u96c4\uff01\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f \u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377\u2014\u2014\u7b2c\u5341\u4e94\u56de\u3000\u795e\u5251\u65bd\u5a01\u3000\u80c6\u5bd2\u60ca\u7edd\u6280\u3000\u6bd2\u73e0\u7a7a\u63b7\u3000\u5fc3\u51b7\u655b\u950b\u8292 \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377 \u7b2c\u5341\u4e94\u56de \u795e\u5251\u65bd\u5a01\u3000\u80c6\u5bd2\u60ca\u7edd\u6280 \u6bd2\u73e0\u7a7a\u63b7\u3000\u5fc3\u51b7\u655b\u950b\u8292 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u6840\u6840\u51b7\u7b11\uff0c\u94c1\u98de\u9f99\u9053\uff1a\u201c\u516c\u5b59\u5927\u5a18\uff0c\u4f60\u8fd9\u56de\u884c\u4e8b\u5dee\u4e86\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u602a\u773c\u4e00\u7ffb\uff0c \u9053\uff1a\u201c\u600e\u9ebd\u5dee\u4e86\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u91d1\u72ec\u5f02\u5c61\u884c\u4e0d\u4e49\uff0c\u800c\u4eca\u53c8\u542c\u5978  \u9063\u4f7f\uff0c\u8c0b\u5bb3\u5fe0\u81e3\uff0c\u4f60\u4e3a\u4f55 \u66ff\u4ed6\u51fa\u5934\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u51b7\u7b11\u9053\uff1a\u201c\u6211\u90a3\u8001\u9b3c\u7eb5\u505a\u9519\u4e86\u4e8b\uff0c\u4e5f\u8f6e\u4e0d\u5230\u4f60\u6765\u7ba1\u6559\uff1f\u201d\u94c1\u98de\u9f99\u813e\u6c14 \u4e5f\u786c\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5012\u662f\u6211\u79bb\u95f4\u4f60\u4eec\u592b\u59bb\u4e86\uff1f\u516c\u5b59\u5927\u5a18\u5440\uff0c\u516c\u5b59\u5927\u5a18\uff01\u9119\u7b11\u4f60\u662f\u4e00 \u4ee3\u540d\u5bb6\u4e4b\u5973\uff0c\u5374\u8fd9\u6837\u7cca\u6d82\uff0c\u4e0d\u660e\u5927\u4e49\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u62d0\u6756\u4e00\u987f\uff0c\u53eb\u9053\uff1a\u201c\u94c1\u98de\u9f99\u4f11\u5f97\u591a\u8a00\uff0c\u6211 \u4eca\u65e5\u5230\u6765\uff0c\u4e13\u8bda\u8981\u9886\u6559\u4f60\u7684\u96f7\u9706\u516b\u5366\u638c\uff1f\u201d\u94c1\u98de\u9f99\u54c8\u54c8\u5927\u7b11\uff0c\u98de\u8eab\u8dc3\u5165\u77f3\u9635\u3002\u9053\uff1a\u201c\u597d\u54c7\uff0c \u539f\u6765\u4f60\u7acb\u5fc3\u4f38\u91cf\u6211\u8001\u94c1\u6765\u4e86\uff1f\u201d\u8eab\u5f62\u4e00\u6643\uff0c\u8df3\u5728\u4e00\u5806\u77f3\u5934\u5f8c\u9762\u3002\u7ea2\u82b1\u9b3c\u6bcd\u629b\u4e86\u62d0\u6756\uff0c\u9053\uff1a \u201c\u4f60\u60f3\u501f\u6211\u7684\u77f3\u9635\u6bd4\u8bd5\u638c\u529b\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6b63\u662f\uff1f\u201d\u53cc\u638c\u4e00\u626c\uff0c\u77f3\u5757\u7eb7\u7eb7\u98de\u8d77\uff0c\u7ea2\u82b1\u9b3c\u6bcd \u5355\u638c\u4e00\u5288\uff0c\u4e5f\u628a\u4e00\u5806\u77f3\u5934\u6253\u5f97\u7eb7\u98de\uff0c\u77f3\u5934\u5bf9\u7a7a\u4e71\u649e\uff0c\u4e24\u4eba\u4e00\u9762\u8fd0\u638c\u529b\u6fc0\u6c64\u77f3\u5934\uff0c\u4e00\u9762\u8df3\u8dc3\u8eb2 \u907f\u77f3\u5f39\u3002 \u3000\u3000\u94c1\u98de\u9f99\u811a\u8e0f\u516b\u5366\u65b9\u4f4d\uff0c\u6bcf\u53d1\u4e00\u638c\uff0c\u4fbf\u8df3\u8fc7\u4e00\u5806\u77f3\u5806\uff0c\u8eb2\u907f\u4e4b\u5904\uff0c\u6070\u662f\u77f3\u5f39\u98de\u5c04\u4e0d\u5230\u7684\u6b7b \u89d2\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u94c1\u8001\u8d3c\u4f60\u5012\u6e9c\u6ed1\uff01\u201d\u53cc\u638c\u9f50\u626c\uff0c\u628a\u4e24\u5806\u77f3\u5934\u6253\u98de\uff0c\u5de6\u53f3\u5939\u51fb\uff0c\u94c1\u98de\u9f99\u53cd \u8eab\u4e00\u8dc3\uff0c\u4ece\u201c\u574e\u95e8\u201d\u8df3\u5230\u201c\u5151\u95e8\u201d\uff0c\u8fd8\u51fb\u4e86\u4e00\u638c\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u4e5f\u6025\u4ece\u201c\u4e7e\u95e8\u201d\u8df3\u5230\u201c\u826e\u95e8\u201d\uff0c \u4e24\u4eba\u4e00\u8fdb\u4e00\u9000\uff0c\u5728\u77f3\u9635\u4e2d\u7a7f\u6765\u63d2\u53bb\uff0c\u5404\u8fd0\u638c\u529b\u98de\u77f3\u51fb\u654c\uff0c\u5728\u79d8\u9b54\u5d16\u4e0b\u6253\u5f97\u6c99\u5c18\u6eda\u6eda\uff0c\u77f3\u5757\u4e71 \u98de\uff0c\u800c\u4e24\u4eba\u8fdb\u9000\u653b\u5b88\uff0c\u90fd\u6709\u6cd5\u5ea6\uff0c\u90a3\u6ee1\u7a7a\u98de\u821e\u7684\u77f3\u5757\uff0c\u5374\u6ca1\u6709\u4e00\u5757\u51fb\u4e2d\u4e86\u4eba\u3002\u7389\u7f57\u5239\u5728\u65c1\u8fb9 \u770b\u5f97\u5341\u5206\u9ad8\u5174\uff0c\u8dc3\u8dc3\u6b32\u8bd5\u3002 \u3000\u3000\u94c1\u98de\u9f99\u7684\u96f7\u9706\u516b\u5366\u638c\u6cd5\u539f\u662f\u6309\u7167\u516b\u95e8\u4e94\u6b65\u7684\u8eab\u6cd5\u6b65\u6cd5\uff0c\u4ee5\u521a\u67d4\u5408\u7528\u6765\u5236\u80dc\u514b\u654c\u7684\u3002\u539f\u6765 \u94c1\u98de\u9f99\u7ecf\u9a8c\u8001\u5230\uff0c\u800c\u4e14\u6709\u77e5\u5df2\u77e5\u5f7c\u4e4b\u660e\uff0c\u4ed6\u77e5\u9053\u7ea2\u82b1\u9b3c\u6bcd\u7684\u6b66\u529f\u5728\u81ea\u5df2\u4e4b\u4e0a\uff0c\u6240\u4ee5\u624d\u5c06\u8ba1\u5c31 \u8ba1\uff0c\u501f\u5979\u5e03\u597d\u7684\u77f3\u9635\u548c\u5979\u6bd4\u8bd5\u638c\u529b\u3002 \u3000\u3000\u800c\u8fd9\u79cd\u9635\u5f0f\u6b63\u662f\u94c1\u98de\u9f99\u6700\u719f\u4e60\u7684\u9635\u5f0f\u3002\u5728\u8fd9\u6837\u7684\u77f3\u9635\u4e2d\u6bd4\u638c\u4e0d\u5355\u5355\u662f\u9760\u638c\u529b\u53d6\u80dc\uff0c\u7ea2\u82b1 \u9b3c\u6bcd\u7684\u77f3\u9635\u6309\u4e94\u884c\u516b\u5366\u7684\u65b9\u4f4d\u5e03\u7f6e\uff0c\u8fd8\u8981\u9760\u8d8b\u907f\u5f97\u5b9c\uff0c\u6240\u95ea\u4e4b\u5904\uff0c\u8981\u6070\u6070\u662f\u77f3\u5f39\u6253\u4e0d\u5230\u7684 \u201c\u6b7b\u89d2\u201d\uff0c\u6240\u4ee5\u6bcf\u53d1\u4e00\u638c\u6bcf\u8df3\u4e00\u6b65\uff0c\u90fd\u8981\u9884\u8ba1\u5230\u5f8c\u8def\u3002\u94c1\u98de\u9f99\u7684\u638c\u6cd5\u672c\u6765\u5c31\u662f\u6309\u7167\u516b\u95e8\u4e94\u6b65 \u7684\u65b9\u4f4d\uff0c\u6bd4\u7ea2\u82b1\u9b3c\u6bcd\u8fd8\u8981\u719f\u4e60\u5f97\u591a\uff0c\u817e\u632a\u95ea\u907f\uff0c\u5999\u5230\u6beb\u5dc5\uff0c\u56e0\u6b64\u94c1\u98de\u9f99\u7684\u638c\u529b\u867d\u7136\u8981\u6bd4\u7ea2\u82b1 \u9b3c\u6bcd\u7a0d\u900a\u4e00\u7b79\uff0c\u53ef\u662f\u4ee5\u5de7\u8865\u62d9\uff0c\u6253\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u8fd8\u662f\u6070\u6070\u6253\u4e2a\u5e73\u624b\u3002 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u52c3\u7136\u5927\u6012\uff0c  \u62da\u534a\u4e2a\u65f6\u8fb0\u8fd8\u672a\u5c06\u654c\u4eba\u6253\u5012\uff0c\u8fd9\u5728\u5979\u6765\u8bf4\uff0c\u662f\u4ece\u6240\u672a\u6709\u4e4b\u4e8b\u3002\u5c24 \u5176\u6c14\u6124\u7684\u662f\uff1a\u8fd9\u65f6\u5979\u5df2\u770b\u51fa\u94c1\u98de\u9f99\u638c\u529b\u4e0d\u5982\u81ea\u5df1\uff0c\u53ef\u662f\u5728\u77f3\u9635\u4e2d\u6bd4\u8bd5\uff0c\u53c8\u504f\u504f\u80dc\u4e0d\u4e86\u4ed6\u3002\u94c1 \u98de\u9f99\u770b\u5979\u706b\u8d77\uff0c\u6545\u610f\u518d\u53d1\u4e00\u638c\uff0c\u4fbf\u5927\u7b11\u53c1\u58f0\uff0c\u628a\u7ea2\u82b1\u9b3c\u6bcd\u66f4\u662f\u6fc0\u5f97\u66b4\u8df3\u5982\u96f7\uff0c\u53cc\u638c\u8fde\u626c\uff0c\u8fd0 \u7528\u4e86\u5185\u5bb6\u771f\u529b\uff0c\u970e\u65f6\u95f4\u5c18\u571f\u98de\u626c\uff0c\u77f3\u5f39\u5982\u96e8\uff0c\u638c\u98ce\u547c\u547c\uff0c\u4eba\u5f71\u51cc\u4e71\uff0c\u5728\u94c1\u98de\u9f99\u5927\u7b11\u58f0\u4e2d\uff0c\u7389 \u7f57\u5239\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u505c\uff01\u201d\u94c1\u98de\u9f99\u53cd\u8eab\u4e00\u8dc3\uff0c\u8df3\u51fa\u5708\u5b50\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u559d\u9053\uff1a\u201c\u505a\u4ec0\u9ebd\uff1f\u201d\u7389\u7f57\u5239 \u51b7\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u7684\u77f3\u9635\u5df2\u5168\u7ed9\u6467\u6bc1\u4e86\uff0c\u8fd9\u573a\u6bd4\u8bd5\u4e5f\u8be5\u5b8c\u4e86\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u8eab\u5f62\u4e00\u505c\uff0c\u51dd\u6b65\u7acb\u5728 \u4e71\u77f3\u4e4b\u4e0a\uff0c\u8fd9\u624d\u53d1\u73b0  \u62da\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u52a0\u4ee5\u81ea\u5df1\u53c8\u7528\u529b\u8fc7\u731b\uff0c\u767e\u591a\u5806\u77f3\u5934\u5df2\u5168\u6253\u5f97\u5012\u584c\uff0c\u8bb8 \u591a\u77f3\u5757\u6b63\u5728\u7ffb\u7ffb\u6eda\u6eda\uff0c\u6eda\u4e0b\u5c71\u5761\u3002 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u6c14\u72b9\u672a\u6d88\uff0c\u5728\u4e71\u77f3\u4e2d\u6361\u8d77\u9f99\u5934\u62d0\u6756\uff0c\u5411\u77f3\u5934\u4e0a\u4e00\u987f\uff0c\u94ff\u7136\u6709\u58f0\uff0c\u9053\uff1a\u201c\u94c1\u8001 \u8d3c\uff0c\u8fd9\u573a\u7b97\u662f\u62c9\u5e73\uff0c\u6211\u518d\u548c\u4f60\u89c1\u4e2a\u771f\u7ae0\u3002\u201d\u7389\u7f57\u5239\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u7ea2\u82b1\u9b3c\u6bcd\uff0c\u4f60\u8fd9\u5c31\u4e0d\u516c\u5e73 \u4e86\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u6012\u9053\uff1a\u201c\u600e\u9ebd\u4e0d\u516c\u5e73\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u624b\u4e0a\u6709\u5175\u5668\uff0c\u6211\u7239\u7239\u53ef\u6ca1\u5e26\u5175 \u5668\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u6012\u9053\uff1a\u201c\u518d\u6bd4\u638c\u529b\u4e5f\u884c\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u4eec\u521a\u624d\u6bd4\u638c\u5df2\u662f\u6bd4\u6210\u5e73\u624b\uff0c\u8fd8\u6bd4 \u4ec0\u9ebd\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u4e00\u6014\uff0c\u867d\u7136\u9002\u624d\u94c1\u98de\u9f99\u5229\u7528\u77f3\u9635\u53d6\u5de7\uff0c\u53ef\u662f\u603b\u4e0d\u80fd\u8bf4\u4e0d\u662f\u6bd4\u8bd5\u638c\u529b\uff0c\u800c\u4e14 \u77f3\u9635\u53c8\u662f\u81ea\u5df1\u5e03\u7684\uff0c\u66f4\u4e0d\u597d\u610f\u601d\u8bf4\u4ed6\u5229\u7528\u77f3\u9635\u5360\u4e86\u4fbf\u5b9c\u3002\u672c\u6765\u6b66\u6797\u540d\u624b\uff0c\u5404\u6709\u64c5\u957f\uff0c\u6709\u7684\u4eba \u4ee5\u638c\u529b\u79f0\u96c4\uff0c\u6709\u7684\u4eba\u4ee5\u5175\u5668\u89c1\u80dc\u3002\u7ea2\u82b1\u9b3c\u6bcd\u662f\u62f3\u638c\u5175\u5668\uff0c\u5168\u90fd\u51fa\u8272\u5f53\u884c\uff0c\u94c1\u98de\u9f99\u5219\u53ea\u662f\u4ee5\u638c \u529b\u79f0\u96c4\uff0c\u5e73\u751f\u4ece\u4e0d\u4f7f\u7528\u5175\u5668\u3002\u6240\u4ee5\u7ea2\u82b1\u9b3c\u6bcd\u82e5\u7136\u8981\u548c\u94c1\u98de\u9f99\u89c1\u4e2a\u771f\u7ae0\uff0c\u5219\u7528\u9f99\u5934\u62d0\u6756\u5bf9\u4ed6\u53cc \u638c\uff0c\u4e5f\u4e0d\u80fd\u7b97\u662f\u4e0d\u516c\uff0c\u65e0\u5948\u7389\u7f57\u5239\u4e00\u53e3\u54ac\u5b9a\uff0c\u6bd4\u638c\u5df2\u6210\u5e73\u624b\uff0c\u8981\u6bd4\u5175\u5668\u94c1\u98de\u9f99\u53ef\u4e0d\u80fd\u5949\u966a\uff0c \u6b6a\u6709\u6b6a\u7406\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u62ff\u5979\u6ca1\u6709\u529e\u6cd5\uff0c\u91cd\u91cd\u7684\u628a\u62d0\u6756\u4e00\u987f\uff0c\u6068\u6068\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u6211\u4e0d\u80fd\u5c31 \u6b64\u5e72\u4f11\uff01\u201d\u53ef\u662f\u8981\u600e\u6837\u518d\u6bd4\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u5374\u4e5f\u8bf4\u4e0d\u51fa\u529e\u6cd5\u6765\uff01 \u3000\u3000\u7389\u7f57\u5239\u770b\u5979\u6012\u6c14\u51b2\u5929\uff0c\u8fd9\u624d\u597d\u6574\u4ee5\u6687\uff0c\u53d6\u4e0b\u51e0\u6839\u5934\u4e0a\u7ea2\u7ef3\uff0c\u7f1a\u4e86\u8896\u53e3\uff0c\u6162\u6162\u8bf4\u9053\uff1a\u201c\u7ea2 \u82b1\u9b3c\u6bcd\uff0c\u4f60\u4e0d\u5fc5\u6c14\u607c\uff0c\u4f60\u8981\u6253\u67b6\uff0c\u90a3\u8fd8\u4e0d\u5bb9\u6613\uff1f\u6709\u4eba\u5949\u966a\u4f60\u4fbf\u662f\uff01\u201d \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u5973\u5a03\u513f\u8981\u548c\u6211\u6bd4\u8bd5\uff1f\u201d\u7389\u7f57\u5239\u5c55\u7709\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u731c\u5f97 \u5bf9\u4e86\uff01\u201d\u7389\u7f57\u5239\u8fd1\u51e0\u5e74\u867d\u662f\u540d\u9707\u6c5f\u6e56\uff0c\u53ef\u662f\u7ea2\u82b1\u9b3c\u6bcd\u9690\u5c45\u5df2\u4e45\uff0c\u5e76\u6ca1\u542c\u8fc7\u5979\u7684\u540d\u5934\u3002\u867d\u7136\u6700 \u8fd1\u4eba\u4eac\uff0c\u4e08\u592b\u5bf9\u5979\u7ea6\u7565\u63d0\u8fc7\u7389\u7f57\u5239\u7684\u6b66\u529f\uff0c\u53ef\u662f\u73b0\u5728\u89c1\u5979\u624d\u662f\u4e2a\u4e8c\u5341\u5c81\u5de6\u53f3\u7684\u5c11\u5973\uff0c\u672a\u514d\u610f \u5b58\u8f7b\u89c6\u3002\u8981\u77e5\u7ea2\u82b1\u9b3c\u6bcd\u5728\u53c1\u5341\u591a\u5e74\u4e4b\u524d\u5df2\u4eab\u76db\u540d\uff1a\u81ea\u7136\u4e0d\u613f\u548c\u201c\u5c0f\u8f88\u201d\u52a8\u624b\u3002\u62d0\u6756\u4e00\u6307\uff0c\u6840 \u6840\u7b11\u9053\uff1a\u201c\u4f60\u518d\u7ec3\u5341\u5e74\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u55d6\u7684\u4e00\u58f0\u62d4\u51fa\u5b9d\u5251\uff0c\u7b11\u9053\uff1a\u201c\u7ea2\u82b1\u9b3c\u6bcd\uff0c\u4f60\u662f\u8bf4\u4f60\u8981\u6bd4\u6211\u5f3a\u5f97\u591a\u9ebd\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd \u7765\u7768\u659c\u89c6\uff0c\u4e0d\u63a5\u8bdd\u950b\uff0c\u7389\u7f57\u5239\u53c8\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u662f\u4e2a\u5927\u8349\u5305\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u5927\u6012\uff0c\u65a5\u9053\uff1a\u201c\u80e1 \u8bf4\u516b\u9053\uff1f\u201d\u7389\u7f57\u5239\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u662f\u5927\u8349\u5305\uff0c\u4e3a\u4f55\u8fde\u201c\u5b66\u65e0\u524d\u5f8c\uff0c\u8fbe\u8005\u4e3a\u5e08\u201d\u7684\u8bdd\u90fd\u4e0d\u6653 \u5f97\uff01\u201d\u5176\u5b9e\u7389\u7f57\u5239\u4e5f\u53ea\u662f\u7c97\u89e3\u6587\u5b87\uff0c\u8fd9\u4e24\u53e5\u8bdd\u8fd8\u662f\u5979\u4ece\u5353\u4e00\u822a\u5904\u542c\u6765\u7684\uff0c\u5979\u6545\u610f\u7528\u6765\u6fc0\u6012\u7ea2 \u82b1\u9b3c\u6bcd\uff0c\u4e43\u662f\u4e00\u79cd\u6218\u7565\u3002 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u7ed9\u5979\u4e00\u6fc0\uff0c\u679c\u7136\u6c14\u5f97\u975e\u540c\u5c0f\u9119\uff0c\u62d0\u6756\u4e00\u6307\uff0c\u6012\u9053\uff1a\u201c\u4f60\u82e5\u771f\u80fd\u80dc\u6211\uff0c\u6211\u62dc\u4f60\u4e3a \u5e08\uff01\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u4e0d\u6562\uff01\u8fd9\u6837\u5427\uff0c\u4f60\u82e5\u80fd\u80dc\u6211\uff0c\u6211\u4eec\u7236\u5973\u4e8c\u4eba\u4efb\u4f60\u5904\u7f6e\u3002\u8981\u662f\u6211\u80dc \u4e86\u5462\uff0c\u4f60\u90a3\u81ed\u8001\u9b3c\u4e08\u592b\u53ef\u5f97\u7531\u6211\u5904\u7f6e\uff0c\u6211\u8981\u6740\u4ed6\u5254\u4ed6\uff0c\u4f60\u90fd\u4e0d\u80fd\u5e2e\u4ed6\u7684\u624b\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u6c14\u5f80 \u4e0a\u51b2\uff0c\u9053\uff1a\u201c\u53ea\u8981\u4f60\u80fd\u548c\u6211\u6253\u4e2a\u5e73\u624b\uff0c\u6211\u5c31\u518d\u9690\u5c45\u53c1\u5341\u5e74\uff01\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4e00\u8a00\u4e3a \u5b9a\uff0c\u8fdb\u62db\u5427\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u6211\u751f\u5e73\u548c\u4eba\u5355\u6253\u72ec\u6597\uff0c\u4ece\u4e0d\u5148\u884c\u52a8\u624b\uff01\u201d\u7389\u7f57\u5239\u4f4e\u7709\u4e00\u7b11\uff0c \u628a\u5251\u7f13\u7f13\u7684\u5728\u7ea2\u82b1\u9b3c\u6bcd\u9762\u524d\u5212\u4e86\u4e00\u9053\u5706\u5f27\u3002 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u559d\u9053\uff1a\u201c\u4f60\u6363\u4ec0\u9ebd\u9b3c\uff1f\u4f60\u5230\u5e95\u60f3\u4e0d\u60f3\u6bd4\u8bd5\uff1f\u201d\u8bdd\u58f0\u672a\u505c\uff0c\u7389\u7f57\u5239\u624b\u638c\u4e00\u7ffb\uff0c\u672c \u6765\u6781\u5176\u7f13\u6162\u7684\u5251\u62db\u7a81\u7136\u53d8\u5f97\u5feb\u5982\u63a3\u7535\uff0c\u9752\u5149\u4e00\u95ea\uff0c\u5251\u950b\u5df2\u5212\u5230\u9762\u95e8\uff01\u539f\u6765\u7389\u7f57\u5239\u7cbe\u7075\u6bd2\u8fa3\uff0c \u5979\u770b\u4e86\u521a\u624d\u7ea2\u82b1\u9b3c\u6bcd\u7684\u638c\u6cd5\uff0c\u77e5\u5979\u6b66\u529f\u975e\u6bd4\u5bfb\u5e38\uff0c\u6240\u4ee5\u6545\u610f\u5148\u4ee4\u5979\u52a8\u6012\uff0c\u6270\u4e71\u5979\u7684\u5fc3\u795e\uff0c\u518d \u7528\u72b6\u7c7b\u513f\u620f\u4f3c\u7684\u7f13\u6162\u5251\u62db\uff0c\u4ee4\u5979\u758f\u65bc\u9632\u5907\uff0c\u7136\u5f8c\u624d\u7a81\u7136\u4f7f\u51fa\u72ec\u95e8\u5251\u6cd5\uff0c\u500f\u7684\u53d8\u62db\uff0c\u7ea2\u82b1\u9b3c\u6bcd \u5927\u5403\u4e00\u60ca\uff0c\u6756\u5934\u5f80\u4e0a\u4e00\u70b9\uff0c\u7389\u7f57\u5239\u5251\u950b\u4e00\u8f6c\uff0c\u523a\u5979\u54bd\u5589\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u80a9\u5934\u4e00\u7f29\uff0c\u5de6\u638c\u4e00\u62ff\uff0c\u60f3 \u786c\u62a2\u5979\u7684\u5b9d\u5251\uff0c\u90a3\u6599\u7389\u7f57\u5239\u7684\u5251\u52bf\uff0c\u770b\u6765\u662f\u523a\u5979\u54bd\u5589\uff0c\u5f85\u5979\u95ea\u65f6\uff0c\u5251\u5c16\u4e00\u9001\uff0c\u5374\u7a81\u7136\u81ea\u504f\u65c1 \u523a\u51fa\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u4e00\u8dc3\uff0c\u53ea\u89c9\u5bd2\u98ce\u98d2\u7136\uff0c\u81ea\u9b13\u8fb9\u63a0\u8fc7\uff0c\u90a3\u6735\u5927\u7ea2\u82b1\u5df2\u7ed9\u524a\u53bb\uff0c\u7389\u7f57\u5239\u54c8\u54c8\u5927 \u7b11\u3002\u5979\u65e9\u6599\u5230\u523a\u7ea2\u82b1\u9b3c\u6bcd\u4e0d\u4e2d\uff0c\u6240\u4ee5\u7528\u5947\u8be1\u5feb\u6377\u7684\u5251\u6cd5\uff0c\u660e\u523a\u8981\u5bb3\uff0c\u5b9e\u65bd\u6697\u88ad\uff0c\u524a\u4e86\u5979\u9b13\u4e0a \u7684\u7ea2\u82b1\uff0c\u632b\u5979\u9510\u6c14\u3002 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5251\u6cd5\u867d\u4f73\uff0c\u8fd8\u4e0d\u662f\u771f\u5b9e\u672c\u9886\uff01\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u9a84\u77dc\u4e4b\u6c14 \u5df2\u51cf\u4e86\u8bb8\u591a\uff0c\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u597d\uff0c\u53eb\u4f60\u770b\u771f\u5b9e\u672c\u9886\uff01\u201d\u5237\u5237\u51e0\u5251\uff0c\u5251\u52bf\u5982\u8679\uff0c\u4f3c\u5b9e\u4f3c\u865a\uff0c\u5728 \u6bcf\u4e00\u62db\u4e4b\u4e2d\uff0c\u90fd\u6697\u85cf\u597d\u51e0\u4e2a\u53d8\u5316\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u7adf\u672a\u66fe\u89c1\u8fc7\u8fd9\u79cd\u5251\u6cd5\uff0c\u7ed9\u8feb\u5f97\u8fde\u8fde\u5f8c\u9000\uff0c\u5353\u4e00\u822a \u5728\u65c1\u89c1\u4e86\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u8fde\u5e08\u53d4\u8eab\u53d7\u91cd\u4f24\uff0c\u4e5f\u6682\u5fd8\u4e86\u3002 \u3000\u3000\u94c1\u98de\u9f99\u5728\u65c1\u5168\u795e\u8d2f\u6ce8\uff0c\u5fc3\u4e2d\u5374\u662f\u5fe7\u8651\u3002\u5353\u4e00\u822a\u559c\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\u80dc\u5238\u5728\u64cd\uff0c\u8fd9\u4e2a\u8001\u5996\u5987\u4e0d \u662f\u5979\u7684\u5bf9\u624b\u3002\u201d\u94c1\u98de\u9f99\u5fae\u54c2\u8bf4\u9053\uff1a\u201c\u8fd8\u65e9\u5462\uff01\u201d\u5353\u4e00\u822a\u518d\u770b\u573a\u4e2d\uff0c\u5f62\u52bf\u5ffd\u53d8\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u94c1\u62d0 \u7ffb\u98de\uff0c\u8f6c\u5b88\u4e3a\u653b\uff0c\u5de6\u638c\u75be\u53d1\uff0c\u547c\u547c\u98ce\u54cd\u3002\u7389\u7f57\u5239\u66b4\u98ce\u9aa4\u96e8\u822c\u7684\u5251\u70b9\u6bcf\u7ed9\u9707\u6b6a\uff0c\u518d\u8fc7\u7247\u523b\uff0c\u53ea \u89c1\u573a\u4e2d\u4e00\u56e2\u767d\u5149\u76d8\u7a7a\u98de\u821e\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u7684\u4e00\u6839\u94c1\u62d0\u5c31\u50cf\u5316\u4e86\u51e0\u5341\u6839\u4f3c\u7684\uff0c\u62d0\u5f71\u5982\u5c71\u88f9\u7740\u90a3\u56e2\u767d \u5149\uff0c\u5b9b\u5982\u6bd2\u9f99\u62a2\u73e0\uff0c\u6eda\u6765\u6eda\u53bb\uff1a\u518d\u8fc7\u7247\u523b\uff0c\u62d0\u5f71\u5251\u5149\uff0c\u878d\u6210\u4e00\u7247\uff0c\u518d\u4e5f\u5206\u8fa8\u4e0d\u51fa\u8c01\u662f\u7389\u7f57\u5239 \u8c01\u662f\u7ea2\u82b1\u9b3c\u6bcd\u4e86\uff01\u5353\u4e00\u822a\u770b\u5f97\u76ee\u7729\u795e\u6447\uff0c\u5012\u5438\u4e00\u53e3\u51b7\u6c14\uff01\u94c1\u98de\u9f99\u8fd9\u65f6\uff0c\u5374\u662f\u5fe7\u60e7\u4e4b\u5bb9\u60ed\u89e3\uff0c \u6307\u70b9\u8bf4\u9053\uff1a\u201c\u90a3\u8001\u5996\u5987\u529f\u529b\u867d\u9ad8\uff0c\u5374\u5948\u4f55\u4e0d\u5f97\u5979\uff01\u201d \u3000\u3000\u539f\u6765\u7389\u7f57\u5239\u867d\u4ee5\u72ec\u95e8\u5251\u672f\uff0c\u4e00\u5f00\u9996\u5c31\u7528\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u7684\u624b\u6bb5\u62a2\u4e86\u4e0a\u98ce\uff0c\u4f46\u7ea2\u82b1\u9b3c\u6bcd\u7684\u529f \u529b\u6bd4\u94c1\u98de\u9f99\u8fd8\u8981\u9ad8\u51fa\u4e00\u7b79\uff0c\u6bd4\u8d77\u7389\u7f57\u5239\u6765\uff0c\u81ea\u7136\u66f4\u8981\u9ad8\u4e86\uff0c\u800c\u4e14\u5979\u7ecf\u9a8c\u53c8\u4e30\uff0c\u4e00\u7701\u609f\u4e0a\u4e86\u5f53 \u65f6\uff0c\u7acb\u523b\u6b62\u6012\u51dd\u795e\uff0c\u6f5c\u5fc3\u5316\u89e3\uff0c\u53c1\u5341\u62db\u4e4b\u5f8c\uff0c\u4fbf\u8f6c\u5b88\u4e3a\u653b\uff0c\u4ee5\u638c\u52a9\u62d0\uff0c\u7389\u7f57\u5239\u7684\u8eab\u5f62\u5728\u5979\u7684 \u638c\u529b\u7b3c\u7f69\u4e4b\u4e0b\uff0c\u5947\u8be1\u7684\u5251\u62db\u7adf\u7136\u53d7\u4e86\u7275\u5236\uff0c\u88ab\u5979\u90a3\u795e\u51fa\u9b3c\u6ca1\u7684\u9f99\u5934\u62d0\u6756\uff0c\u8feb\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\uff01 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u6b63\u4ee5\u4e3a\u53ef\u4ee5\u5f97\u624b\uff0c\u5c82\u77e5\u7389\u7f57\u5239\u80f8\u6709\u6210\u7af9\uff0c\u867d\u5904\u4e0b\u98ce\uff0c\u5374\u662f\u50b2\u7136\u4e0d\u60e7\u3002\u6bcf\u5230\u7edd\u9669 \u4e4b\u65f6\uff0c\u5979\u90fd\u80fd\u4e3e\u91cd\u82e5\u8f7b\uff0c\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\u5ffd\u7136\u907f\u8fc7\uff01\u7ea2\u82b1\u9b3c\u6bcd\u4e5f\u6697\u6697\u4f69\u670d\uff0c\u94c1\u62d0\u8d8a\u88f9\u8d8a\u7d27\uff0c \u770b\u770b\u7389\u7f57\u5239\u5df2\u662f\u4e07\u96be\u8eb2\u907f\uff0c\u7389\u7f57\u5239\u5ffd\u7136\u957f\u5251\u4e00\u4f38\uff0c\u5728\u5979\u9f99\u5934\u62d0\u6756\u4e0a\u4e00\u70b9\uff0c\u4fbf\u501f\u7740\u8fd9\u4e00\u70b9\u4e4b \u529b\uff0c\u8eab\u5b50\u817e\u7a7a\u98de\u8d77\uff0c\u5728\u534a\u7a7a\u633d\u4e86\u4e2a\u5251\u82b1\uff0c\u5c45\u7136\u8fd8\u80fd\u53cd\u51fb\uff01\u4e24\u4eba\u5728\u4e71\u77f3\u5806\u4e2d\u5954\u9a70\u8ffd\u9010\uff0c\u7ea2\u82b1\u9b3c \u6bcd\u867d\u5360\u4e86\u4e03\u6210\u653b\u52bf\uff0c\u5374\u662f\u65e0\u5948\u5979\u4f55\uff01\u539f\u6765\u7389\u7f57\u5239\u662f\u6bcd\u72fc\u4e73\u5927\uff0c\u81ea\u5e7c\u5728\u534e\u5c71\u7edd\u9876\u6e38\u620f\uff0c\u8f7b\u529f\u4e4b \u9ad8\uff0c\u5e76\u4e16\u65e0\u4e24\u3002\u5373\u7b97\u94c1\u98de\u9f99\u7ea2\u82b1\u9b3c\u6bcd\u5cb3\u545c\u73c2\u7b49\u5728\u8f7b\u529f\u4e0a\u4e5f\u90fd\u8981\u7a0d\u7a0d\u900a\u5979\u4e00\u7b79\u3002\u5979\u77e5\u7ea2\u82b1\u9b3c\u6bcd \u5185\u529f\u5389\u5bb3\uff0c\u4fbf\u5c3d\u91cf\u53d1\u6325\u81ea\u5df1\u6240\u957f\uff0c\u653b\u654c\u6240\u77ed\uff0c\u5e76\u4e0d\u548c\u7ea2\u82b1\u9b3c\u6bcd\u771f\u6b63\u8f83\u52b2\uff0c\u5374\u5728\u817e\u632a\u95ea\u5c55\u4e4b \u9645\uff0c\u4f3a\u9699\u53cd\u51fb\uff0c\u6597\u4e86\u53c1\u767e\u6765\u62db\uff0c  \u662f\u4e0d\u5206\u80dc\u8d1f\u3002 \u3000\u3000\u94c1\u98de\u9f99\u677e\u4e86\u53e3\u6c14\uff0c\u8fd9\u65f6\u624d\u60f3\u8d77\u767d\u77f3\u9053\u4eba\u8eab\u53d7\u91cd\u4f24\uff0c\u5411\u5353\u4e00\u822a\u9053\uff1a\u201c\u77a7\u4f60\u7684\u5e08\u53d4\u53bb\uff01\u201d\u5353 \u4e00\u822a\u4e5f\u970d\u7136\u9192\u8d77\uff0c\u8d70\u8fd1\u767d\u77f3\u9053\u4eba\u8eab\u65c1\uff0c\u53ea\u89c1\u4ed6\u76d8\u819d\u5750\u5728\u5730\u4e0a\uff0c\u6b63\u5728\u95ed\u76ee\u7528\u529f\u3002\u94c1\u98de\u9f99\u5524\u4e86\u4e00 \u58f0\uff0c\u767d\u77f3\u9053\u4eba\u5fae\u7741\u5f00\u773c\uff0c\u9762\u8272\u6120\u6012\u3002\u94c1\u98de\u9f99\u6478\u51fa\u4e24\u9897\u836f\u4e38\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u6cbb\u4f24\u89e3\u6bd2\u7684\u5723\u836f\u3002\u201d \u767d\u77f3\u9053\u4eba\u6447\u4e86\u6447\u5934\u5e76\u4e0d\u7b54\u8bdd\u3002\u4ed6\u5df2\u670d\u4e86\u6b66\u5f53\u672c\u95e8\u7684\u89e3\u836f\uff0c\u4e0d\u613f\u63a5\u53d7\u654c\u65b9\u201c\u4ed6\u628a\u94c1\u98de\u9f99\u4e0e\u7389\u7f57 \u5239\u90fd\u5212\u4eba\u201c\u654c\u65b9\u201d\u4e86\u3002\u201d\u7684\u8d60\u4e0e\u3002\u94c1\u98de\u9f99\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u5728\u4ed6\u8033\u8fb9\u65a5\u9053\uff1a\u201c\u6211\u4e0d\u613f\u89c1\u6210\u540d\u4eba \u7269\u5982\u6b64\u6b7b\u53bb\uff0c\u4f60\u7684\u672c\u95e8\u89e3\u836f\u53ea\u53ef\u6682\u4fdd\u4e00\u65f6\uff0c\u6211\u7684\u89e3\u836f\u624d\u662f\u6b63\u836f\uff0c\u4f60\u4e0d\u670d\u6c14\uff0c\u5c31\u8bf7\u4f60\u5148\u5403\u6211\u7684 \u89e3\u836f\uff0c\u5f85\u4f60\u4f24\u597d\u4e4b\u5f8c\uff0c\u54b1\u4eec\u518d\u6765\u8f83\u91cf\u3002\u201d\u767d\u77f3\u9053\u4eba\u95ed\u76ee\u4e0d\u7406\u3002\u94c1\u98de\u9f99\u4e00\u607c\uff0c\u7a81\u7136\u4f38\u624b\u5728\u4ed6\u5634 \u5df4\u4e0a\u4e00\u634f\uff0c\u767d\u77f3\u9053\u4eba\u201c\u5440\u201d\u7684\u4e00\u58f0\u558a\u4e86\u51fa\u6765\uff0c\u94c1\u98de\u9f99\u5df2\u628a\u4e24\u9897\u4e38\u836f\uff0c\u9001\u4eba\u4ed6\u7684\u53e3\u4e2d\u3002 \u3000\u3000\u767d\u77f3\u9053\u4eba\u6d51\u8eab\u65e0\u529b\uff0c\u8981\u60f3\u5410\u4e5f\u5410\u4e0d\u6389\uff0c\u4e24\u9897\u836f\u4e38\u6ed1\u5165\u4ed6\u7684\u5589\u5499\uff0c\u7247\u523b\u4e4b\u5f8c\uff0c\u4e39\u7530\u5347\u8d77\u4e00 \u80a1\u70ed\u6c14\uff0c\u4eba\u4e5f\u8212\u8bb8\u591a\u4e86\uff0c\u4fbf\u4e0d\u518d\u8a00\u8bed\u3002\u94c1\u98de\u9f99\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5e08\u53d4\uff0c\u5014\u5f3a\u5f97\u597d\u6ca1\u9053\u7406\u3002\u201d\u628a\u5353 \u4e00\u822a\u626f\u5230\u8eab\u8fb9\uff0c\u89e3\u5f00\u4e00\u89d2\u80f8\u8863\uff0c\u6084\u6084\u8bf4\u9053\uff1a\u201c\u4f60\u770b\u3002\u201d\u5353\u4e00\u822a\u89c1\u4ed6\u80f8\u524d\u7684\u62a4\u5fc3\u94dc\u955c\u5df2\u5236\u6210\u51e0 \u5757\uff0c\u82e5\u65e0\u94a2\u7ebf\u52d2\u4f4f\uff0c\u65e9\u5df2\u6389\u4e86\u4e0b\u6765\u3002\u94c1\u98de\u9f99\u4e00\u7b11\u6263\u8863\uff0c\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u662f\u6709\u8fd9\u5757\u62a4\u5fc3\u94dc\u955c\uff0c\u4e5f \u53d7\u4f24\u4e86\u3002\u4f60\u7684\u5e08\u53d4\u53d7\u4e86\u7ea2\u82b1\u9b3c\u6bcd\u5185\u529b\u6240\u4f24\uff0c\u73b0\u5728\u6551\u6cbb\u4e4b\u5f8c\u6027\u547d\u867d\u53ef\u65e0\u59a8\uff0c\u4f46\u8981\u590d\u5143\uff0c\u6050\u6015\u8fd8 \u5f97\u5f85\u4e00\u6708\u4e4b\u5f8c\u3002\u201d\u5353\u4e00\u822a\u4e0d\u7981\u9a87\u7136\u3002\u60f3\u8d77\u7ea2\u82b1\u9b3c\u6bcd\u9002\u624d\u5206\u6563\u81ea\u5df2\u548c\u5e08\u53d4\u4e8c\u4eba\uff0c\u5e08\u53d4\u53d7\u4e86\u5185 \u4f24\uff0c\u800c\u81ea\u5df1\u5374\u4e1d\u6beb\u65e0\u635f\uff0c\u8fd9\u5206\u660e\u662f\u7ea2\u82b1\u9b3c\u6bcd\u624b\u4e0b\u7559\u60c5\u7684\u4e86\u3002\u601d\u5ff5\u53ca\u6b64\uff0c\u4e0d\u89c9\u53c8\u4e3a\u7389\u7f57\u5239\u62c5\u5fc3 \u8d77\u6765\uff0c\u751f\u6015\u5979\u53d7\u4e0d\u4f4f\u7ea2\u82b1\u9b3c\u6bcd\u7684\u638c\u529b\uff0c\u4e5f\u50cf\u81ea\u5df2\u7684\u5e08\u53d4\u4e00\u6837\u53d7\u4e86\u91cd\u4f24\u3002 \u3000\u3000\u5353\u4e00\u822a\u5fe7\u5fc3\u5fe1\u5fe1\uff0c\u518d\u770b\u6597\u573a\uff0c\u53ea\u89c1\u6597\u573a\u5f62\u52bf\u53c8\u53d8\u3002\u7ea2\u82b1\u9b3c\u6bcd\u7684\u94c1\u62d0\u4e1c\u6307\u897f\u5212\uff0c\u624b\u4e0a\u50cf\u633d \u7740\u5343\u65a4\u91cd\u7269\u4e00\u6837\uff0c\u6bd4\u524d\u7f13\u6162\u8bb8\u591a\uff0c\u4f46\u7389\u7f57\u5239\u7684\u5251\u62db\u5374\u975e\u4f46\u653b\u4e0d\u8fdb\u53bb\uff0c\u800c\u4e14\u597d\u50cf\u8981\u8131\u8eab\u4e5f\u4e0d\u53ef \u80fd\uff0c\u4e24\u4eba\u5728\u4e71\u77f3\u5806\u4e2d\uff0c\u5404\u81ea\u5c01\u95ed\u95e8\u6237\uff0c\u4e00\u62db\u4e00\u5f0f\uff0c\u5e26\u5b88\u5e26\u653b\uff0c\u770b\u5f97\u975e\u5e38\u6e05\u695a\uff0c\u5c31\u50cf\u4e24\u4e2a\u597d\u53cb \u62c6\u62db\u7ec3\u4e60\u4e00\u822c\u3002\u53ef\u662f\u4e24\u4eba\u9762\u8272\u90fd\u6781\u6c89\u91cd\uff0c\u8fde\u4e00\u5411\u559c\u6b22\u5b09\u7b11\u7684\u7389\u7f57\u5239\u4e5f\u7d27\u7ef8\u7740\u8138\uff0c\u76ee\u4e0d\u659c\u89c6\uff0c \u968f\u7740\u7ea2\u82b1\u9b3c\u6bcd\u7684\u94c1\u62d0\u6240\u6307\uff0c\u4e00\u5251\u4e00\u5251\uff0c\u594b\u529b\u89e3\u62c6\u3002 \u3000\u3000\u539f\u6765\u7ea2\u82b1\u9b3c\u6bcd\u89c1\u7389\u7f57\u5239\u8f7b\u529f\u4e86\u5f97\uff0c\u62da\u4e86\u53c1\u767e\u591a\u62db\uff0c  \u81ea\u4e0d\u80fd\u53d6\u80dc\uff0c\u5fc3\u4e2d\u4e00\u8e81\uff0c\u7adf\u628a\u5e73\u751f \u7edd\u5b66\uff0c\u8f7b\u6613\u4e0d\u80af\u4e00\u7528\u7684\u201c\u592a\u4e59\u7384\u529f\u201d\u65bd\u5c55\u51fa\u6765\uff0c\u8fd9\u79cd\u529f\u592b\u53ef\u628a\u5168\u8eab\u529f\u529b\u79fb\u5230\u7269\u4f53\u4e4b\u4e0a\uff0c\u4e0a\u4e58 \u8005\u53ef\u4ee5\u6458\u53f6\u98de\u82b1\uff0c\u4f24\u4eba\u7acb\u6b7b\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u628a\u529f\u529b\u8fd0\u5230\u94c1\u62d0\u4e4b\u4e0a\uff0c\u7389\u7f57\u5239\u5251\u950b\u7a0d\u8fd1\u62d0\u8eab\uff0c\u5ffd\u89c9\u5982 \u6709\u4e00\u80a1\u7c98\u529b\u628a\u81ea\u5df1\u7684\u5251\u5438\u7740\u4f3c\u7684\uff0c\u81ea\u5df1\u7528\u529b\u6108\u5927\uff0c\u5979\u7684\u7c98\u529b\u4e5f\u6108\u5927\uff0c\u8fd9\u4e00\u6765\u7389\u7f57\u5239\u5947\u8be1\u7edd\u4f26 \u7684\u5251\u62db\u65e0\u6cd5\u65bd\u5c55\uff0c\u800c\u4e14\u7ea2\u82b1\u9b3c\u6bcd\u7684\u62d0\u52bf\u867d\u4f3c\u7f13\u6162\u5f02\u5e38\uff0c\u5b9e\u9645\u6bcf\u4e00\u62d0\u90fd\u662f\u6307\u7740\u81ea\u5df1\u7684\u7a74\u9053\u8981 \u5bb3\uff0c\u53ea\u8981\u81ea\u5df2\u7a0d\u5fae\u758f\u5ffd\uff0c\u5bf9\u65b9\u5c31\u7acb\u523b\u53ef\u4ee5\u4e58\u9699\u800c\u4eba\uff0c\u6240\u4ee5\u7389\u7f57\u5239\u53ea\u80fd\u594b\u529b\u62c6\u62db\uff0c\u540c\u65f6\u907f\u514d\u548c \u5979\u8f83\u91cf\u771f\u529b\uff01\u8fde\u9003\u8d70\u4e5f\u4e0d\u53ef\u80fd\u3002\u56e0\u4e3a\u53ea\u8981\u81ea\u5df1\u7684\u5251\u62db\u4e00\u6492\uff0c\u8eab\u5f62\u4e00\u9000\uff0c\u9632\u5b88\u5c31\u8981\u9732\u51fa\u5f31\u70b9\uff0c \u8981\u5bb3\u7a74\u9053\uff0c\u5c31\u5168\u5728\u654c\u4eba\u653b\u51fb\u4e4b\u4e0b\u4e86\u3002 \u3000\u3000\u5353\u4e00\u822a\u770b\u51fa\u60c5\u5f62\u4e0d\u5bf9\uff0c\u5bf9\u94c1\u98de\u9f99\u9053\uff1a\u201c\u53eb\u5979\u8d70\u5427\uff01\u201d\u5353\u4e00\u822a\u4ee5\u4e3a\u51ed\u7740\u94c1\u98de\u9f99\u7684\u6b66\u529f\uff0c\u7eb5 \u4e0d\u80fd\u80dc\u7ea2\u82b1\u9b3c\u6bcd\uff0c\u4f46\u63a9\u62a4\u7389\u7f57\u5239\u9003\u8d70\u4e1d\u6709\u53ef\u80fd\u3002\u94c1\u98de\u9f99\u53f9\u4e86\u53e3\u6c14\uff0c\u6447\u6447\u5934\u6084\u58f0\u9053\uff1a\u201c\u521a\u624d\u8fd8 \u53ef\u4ee5\u8d70\uff0c\u73b0\u5728\u53ef\u4e0d\u80fd\u4e86\uff01\u800c\u4e14\u9664\u975e\u662f\u7d2b\u9633\u9053\u957f\u590d\u751f\uff0c\u6216\u8005\u5929\u90fd\u5c45\u58eb\u6765\u5230\uff0c\u5929\u4e0b\u6ca1\u7b2c\u53c1\u4e2a\u4eba\u53ef \u4ee5\u62c6\u5f00\u5979\u4eec\uff01\u201d\u5353\u4e00\u822a\u66f4\u662f\u5403\u60ca\uff0c\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u89c1\u7ea2\u82b1\u9b3c\u6bcd\u624b\u8d77\u4e00\u62d0\uff0c\u5f53\u5934\u5288\u4e0b\uff0c\u7389\u7f57\u5239\u7684 \u5251\u5c16\u65c1\u6307\uff0c\u95e8\u6237\u5927\u5f00\uff0c\u60ca\u6781\u6b32\u547c\uff0c\u94c1\u98de\u9f99\u5ffd\u7136\u4f38\u624b\u628a\u4ed6\u5634\u5df4\u5c01\u4f4f\uff0c\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u4e0d\u53ef\u60ca \u53eb\uff0c\u6270\u4e71\u5979\u7684\u5fc3\u795e\uff01\u201d\u5353\u4e00\u822a\u518d\u770b\u65f6\uff0c\u53ea\u89c1\u7ea2\u82b1\u9b3c\u6bcd\u90a3\u62d0\u660e\u660e\u53ef\u4ee5\u5288\u788e\u7389\u7f57\u5239\u7684\u5934\u9885\uff0c\u5374\u7a81 \u7136\u4e00\u6b6a\uff0c\u6ed1\u8fc7\u4e00\u65c1\uff0c\u4e0d\u77e5\u662f\u4f55\u9053\u7406\uff0c\u5fc3\u4e2d\u5927\u60d1\u4e0d\u89e3\u3002 \u3000\u3000\u94c1\u98de\u9f99\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u9713\u88f3\u7684\u5251\u6cd5\u771f\u662f\u5999\u7edd\u5929\u4e0b\uff0c\u521a\u624d\u90a3\u4e00\u62db\u89e3\u5f97\u597d\u6781\u4e86\uff01\u8fde\u6211\u4e5f\u610f\u60f3\u4e0d \u5230\u3002\u201d\u8bf4\u7f62\u4e3e\u8896\u62b9\u989d\uff0c\u5353\u4e00\u822a\u89c1\u4ed6\u989d\u4e0a\u6c57\u6c34\u76f4\u6d41\uff0c\u8fd9\u624d\u77e5\u9053\u94c1\u98de\u9f99\u7684\u7740\u6025\u4e4b\u60c5\uff0c\u5e76\u4e0d\u5728\u81ea\u5df1 \u4e4b\u4e0b\u3002 \u3000\u3000\u539f\u6765\u7ea2\u82b1\u9b3c\u6bcd\u521a\u624d\u90a3\u62d0\u867d\u7136\u53ef\u4ee5\u5288\u788e\u7389\u7f57\u5239\u5934\u9876\uff0c\u4f46\u7389\u7f57\u5239\u4e5f\u5192\u9669\u8fdb\u62db\uff0c\u5251\u52bf\u6307\u5411\u5979\u80c1 \u4e0b\u7684\u7ae0\u95e8\u8981\u7a74\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u82e5\u4e0d\u9632\u6551\uff0c\u52bf\u5fc5\u4e24\u8d25\u4ff1\u4ea1\uff1a\u6240\u4ee5\u94c1\u62d0\u867d\u7136\u8ddd\u79bb\u7389\u7f57\u5239\u5934\u9876\u4e0d\u5230\u4e94 \u5bf8\uff0c\u8fd8\u662f\u4e0d\u5f97\u4e0d\u7a0d\u7a0d\u79fb\u5f00\uff0c\u9707\u6b6a\u7389\u7f57\u5239\u7684\u5251\u950b\u3002 \u3000\u3000\u4ea4\u6362\u4e86\u8fd9\u4e00\u9669\u62db\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u60f3\u9053\uff1a\u8fd9\u5973\u5a03\u5b50\u529f\u529b\u4e0d\u5982\u6211\u9ad8\uff0c\u6211\u4f55\u5fc5\u548c\u5979\u5192\u9669\u5bf9\u653b\u3002\u6162\u6162 \u628a\u5979\u56f0\u6b7b\u4fbf\u6210\u3002\u4ecd\u7136\u65bd\u7528\u201c\u592a\u4e59\u7384\u529f\u201d\uff0c\u628a\u5185\u529b\u8fd0\u5230\u62d0\u6756\u4e4b\u4e0a\uff0c\u5c06\u7389\u7f57\u5239\u56f0\u5728\u4e08\u8bb8\u65b9\u5706\u4e4b \u5730\uff0c\u653b\u65e2\u4e0d\u80fd\u9000\u4ea6\u4e0d\u5f97\uff01 \u3000\u3000\u94c1\u98de\u9f99\u81ea\u662f\u884c\u5bb6\uff0c\u8d8a\u770b\u5fc3\u5934\u8d8a\u6025\uff0c\u5fc3\u9053\uff1a\u7ea2\u82b1\u9b3c\u6bcd\u4e00\u7a33\u4e0b\u6765\uff0c\u7528\u8fd9\u6837\u7684\u6253\u6cd5\uff0c\u88f3\u513f\u5251\u6cd5 \u518d\u5999\uff0c\u4e5f\u9e21\u4e45\u654c\u3002\u53ef\u662f\u51ed\u7740\u81ea\u5df1\u529f\u529b\uff0c\u53c8\u4e0d\u80fd\u4e0a\u524d\u89e3\u62c6\uff0c\u53ea\u597d\u5728\u65c1\u8fb9\u4e7e\u7740\u6025\u3002\u5353\u4e00\u822a\u867d\u7136\u4e0d \u61c2\u5176\u4e2d\u5965\u5999\uff0c\u4f46\u89c1\u94c1\u98de\u9f99\u6c57\u6c34\u76f4\u6d41\uff0c\u573a\u4e2d\u7389\u7f57\u5239\u795e\u8272\u8d8a\u52a0\u9634\u6c89\uff0c\u4e5f\u77e5\u9053\u60c5\u5f62\u4e0d\u5999\u3002\u53ef\u662f\u8fde\u94c1 \u98de\u9f99\u90fd\u65e0\u80fd\u4e3a\u529b\uff0c\u4ed6\u66f4\u662f\u6beb\u65e0\u529e\u6cd5\uff0c\u4e5f  \u6709\u7126\u6025\u7684\u4efd\u513f\u3002\u94c1\u98de\u9f99\u60f3\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u60f3\u8d77\u4e00\u7b56\uff0c \u53cc\u638c\u731b\u529b\u76f8\u649e\uff0c\u5353\u4e00\u822a\u83ab\u540d\u5176\u5999\uff0c\u5fc3\u60f3\uff1a\u8fd9\u8001\u513f\u53d1\u4e86\u75af\u4e0d\u6210\uff1f\u66f4\u662f\u7740\u6025\u3002 \u3000\u3000\u4e0d\u4f46\u65c1\u89c2\u7684\u4e8c\u4eba\u8549\u6025\uff0c\u573a\u4e2d\u5267\u6218\u7684\u4e8c\u4eba\u4e5f\u90fd\u6697\u6697\u5fc3\u6025\u3002\u7ea2\u82b1\u9b3c\u6bcd\u7528\u51fa\u201c\u592a\u4e59\u7384\u529f\u201d\uff0c\u672c \u4ee5\u4e3a\u5728\u4e94\u5341\u62db\u4e4b\u5185\u4fbf\u53ef\u5f97\u624b\uff0c\u90a3\u77e5\u62da\u4e86\u4e00\u767e\u591a\u62db\uff0c\u867d\u7136\u5360\u5f97\u4e0a\u98ce\uff0c\u4f46\u7389\u7f57\u5239\u5374\u8fd8\u662f\u53ef\u4ee5\u62b5 \u6321\u3002\u800c\u7528\u8fd9\u79cd\u5185\u529b\u62da\u6597\uff0c\u6700\u4e3a\u4f24\u795e\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u4e0d\u7531\u5f97\u6697\u6697\u5fc3\u614c\uff0c\u8fd9\u573a\u5927\u6218\u4e4b\u5f8c\uff0c\u5c31\u7b97\u83b7\u5f97\u5168 \u80dc\uff0c\u4e5f\u6050\u6015\u8981\u751f\u4e00\u573a\u5927\u75c5\u3002\u7389\u7f57\u5239\u6597\u4e86\u534a\u65e5\uff0c\u66f4\u662f\u7126\u6025\u5f02\u5e38\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u7528\u8fd9\u79cd\u6253\u6cd5\uff0c\u4ee4\u5979\u653b \u65e2\u4e0d\u80fd\uff0c\u9000\u4ea6\u4e0d\u5f97\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u96be\u9053\u5c31\u8fd9\u6837\u675f\u624b\u5f85\u6bd9\u4e0d\u6210\u4e86\u5ffd\u89c1\u94c1\u98de\u9f99\u53cc\u638c\u76f8\u649e\uff0c\u5fc3\u5ff5\u4e00 \u52a8\uff0c\u7389\u7f57\u5239\u672c\u77e5\u9053\u7ea2\u82b1\u9b3c\u6bcd\u5185\u529f\u6df1\u539a\uff0c\u4e0d\u6562\u548c\u5979\u8f83\u91cf\u52b2\u529b\uff0c\u8fd9\u65f6\u4e3a\u4e86\u8981\u5728\u6b7b\u91cc\u9003\u751f\uff0c\u54ac\u4e86\u54ac \u7259\uff0c\u6697\u8fd0\u5185\u529b\uff0c\u6218\u5230\u6025\u5904\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u970d\u5730\u4e00\u62d0\u6253\u6765\uff0c\u7389\u7f57\u5239\u7a81\u7136\u6a2a\u5251\u4e00\u5c01\uff0c\u5251\u62d0\u76f8\u4ea4\uff0c\u706b\u661f \u56db\u6e85\uff0c\u7389\u7f57\u5239\u7ed9\u9707\u5f97\u5012\u9000\u53c1\u6b65\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u4e5f\u7acb\u8db3\u4e0d\u7a33\uff0c\u6643\u4e86\u4e24\u6643\uff0c\u4e0d\u7531\u5f97\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u7389\u7f57\u5239\u8bd5\u4e86\u4e00\u62db\uff0c\u7cbe\u795e\u9661\u632f\uff01\u7ea2\u82b1\u9b3c\u6bcd\u7684\u5185\u529f\u4e5f\u5e76\u4e0d\u5982\u60f3\u50cf\u4e4b\u751a\uff0c\u987f\u65f6\u5251\u5149\u98de\u821e\uff0c\u518d\u4e5f \u4e0d\u6015\u548c\u5979\u7684\u94c1\u62d0\u76f8\u4ea4\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u5927\u4e3a\u9a7e\u5947\uff0c\u60f3\u4e0d\u5230\u7389\u7f57\u5239\u7684\u5185\u529f\u4e5f\u5982\u6b64\u6df1\u539a\uff01 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u8fd9\u56de\u5403\u4e86\u5927\u4e8f\u3002\u539f\u6765\u7ea2\u82b1\u9b3c\u6bcd\u7684\u529f\u529b\uff0c\u7684\u786e\u8981\u6bd4\u7389\u7f57\u5239\u9ad8\u51fa\u8bb8\u591a\uff0c\u53ef\u662f\u5979\u5148\u548c \u767d\u77f3\u9053\u4eba\u6253\u4e86\u53c1\u767e\u591a\u62db\uff0c\u8ddf\u7740\u53c8\u548c\u94c1\u98de\u9f99\u6bd4\u8bd5\u638c\u529b\uff0c\u52a8\u4e86\u6012\u6c14\uff0c\u7528\u529b\u8fc7\u5ea6\uff0c\u5185\u529f\u5df2\u51cf\u524a\u8bb8 \u591a\uff0c\u8981\u4e0d\u7136\u83ab\u8bf4\u8fd0\u7528\u4e86\u201c\u592a\u4e59\u7384\u529f\u201d\uff0c\u4e0d\u987b\u7528\u5230\u4e00\u767e\u591a\u62db\uff0c\u5c31\u662f\u8fd9\u4e00\u62d0\u6700\u5c11\u4e5f\u53ef\u4ee5\u628a\u7389\u7f57\u5239 \u7684\u5b9d\u5251\u6253\u98de\u3002\u7389\u7f57\u5239\u65e0\u5f62\u4e2d\u5360\u4e86\u4fbf\u5b9c\uff0c\u81ea\u5df1\u8fd8\u4e0d\u77e5\u9053\uff01 \u3000\u3000\u94c1\u98de\u9f99\u8fd9\u65f6\u624d\u677e\u4e86\u53e3\u6c14\uff0c\u6697\u6697\u53d1\u7b11\u3002\u539f\u6765\u4ed6\u5148\u51fa\u573a\uff0c\u628a\u7ea2\u82b1\u9b3c\u6bcd\u6fc0\u6012\uff0c\u5c06\u77f3\u9635\u6467\u6bc1\u4e4b \u5f8c\uff0c\u624d\u8ba9\u7389\u7f57\u5239\u51fa\u95e8\uff0c\u6b63\u662f\u4ed6\u9884\u5148\u5b89\u6392\u597d\u7684\u6218\u7565\u3002\u7389\u7f57\u5239\u4e0d\u61c2\u6b63\u95e8\u516b\u5366\u4e4b\u9635\uff0c\u4f46\u8f7b\u529f\u6781\u9ad8\uff0c \u6240\u4ee5\u5728\u77f3\u9635\u6467\u6bc1\u4e4b\u5f8c\uff0c\u80fd\u79fb\u4e0e\u7ea2\u82b1\u9b3c\u6bcd\u6253\u6210\u5e73\u624b\u3002\u94c1\u98de\u9f99\u53c8\u56e0\u8fd9\u4e00\u6218\u5173\u7cfb\u91cd\u5927\uff0c\u5e76\u4e14\u77e5\u9053\u7389 \u7f57\u5239\u4e5f\u5341\u5206\u597d\u80dc\uff0c\u6240\u4ee5\u5e76\u6ca1\u5c06\u4e8b\u5148\u7684\u8ba1\u753b\u8bf4\u7ed9\u5979\u542c\uff0c\u4ee5\u514d\u5f71\u54cd\u5979\u7684\u5fc3\u60c5\uff0c\u8ba9\u5979\u597d\u4e13\u5fc3\u5bf9\u654c\u3002 \u53ef\u662f\u94c1\u98de\u9f99\u4e8b\u5148\u867d\u7136\u5e03\u7f6e\u5468\u5bc6\uff0c\u5230\u76ee\u7779\u7389\u7f57\u5239\u4e0e\u7ea2\u82b1\u9b3c\u6bcd\u6fc0\u6218\u4e4b\u65f6\uff0c\u8fd8\u514d\u4e0d\u4e86\u5fe7\u5fc3\u5fe1\u5fe1\uff0c\u751f \u6015\u7389\u7f57\u5239\u7684\u5185\u529f\u4e0e\u7ea2\u82b1\u9b3c\u6bcd\u76f8\u5dee\u592a\u8fdc\uff0c\u76f4\u81f3\u770b\u5230\u7389\u7f57\u5239\u5192\u9669\u53cd\u51fb\uff0c\u5251\u62d0\u76f8\u4ea4\uff0c\u5404\u7ed9\u9707\u9000\u7684\u60c5 \u5f62\uff0c\u94c1\u98de\u9f99\u624d\u5bbd\u4e86\u5fc3\u3002 \u3000\u3000\u518d\u8bf4\u7389\u7f57\u5239\u7a81\u7834\u4e86\u7ea2\u82b1\u9b3c\u6bcd\u7684\u80f6\u7740\u6218\u672f\uff0c\u5251\u5251\u53cd\u51fb\uff0c\u8f9b\u8fa3\u5f02\u5e38\uff1a\u7ea2\u82b1\u9b3c\u6bcd\u9980\u52bf\u672a\u8870\uff0c\u638c \u98ce\u547c\u547c\uff0c\u94c1\u62d0\u4e71\u626b\uff0c\u4e5f\u5c3d\u81ea\u906e\u6321\u5f97\u4f4f\u3002\u4e24\u4eba\u5404\u4ee5\u5185\u529b\u76f8\u62da\uff0c\u53ea\u89c1\u6756\u5f71\u5251\u5149\uff0c\u6b64\u6765\u5f7c\u5f80\uff0c\u53ee\u53ee \u5f53\u5f53\uff0c\u6218\u4e86\u4e00\u4e2a\u52bf\u5747\u529b\u654c\u3002 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u60f3\u4e0d\u5230\u4e00\u4e16\u5a01\u540d\uff0c\u7adf\u7ed9\u8fd9\u4e2a\u5973\u5a03\u5b50\u8feb\u6210\u5e73\u624b\uff0c\u6218\u5230\u5206\u9645\uff0c\u7a81\u7136\u5de6\u638c\u62a4\u80f8\uff0c\u94c1\u62d0 \u5012\u62d6\uff0c\u5356\u4e86\u4e00\u4e2a\u7834\u7efd\uff0c\u8df3\u51fa\u5708\u5b50\uff0c\u7389\u7f57\u5239\u4e00\u58f0\u5a07\u7b11\uff0c\u811a\u6b65\u4e00\u70b9\uff0c\u8eab\u5f62\u98de\u8d77\uff0c\u51cc\u7a7a\u4e0b\u51fb\u3002\u94c1\u98de \u9f99\u53eb\u9053\uff1a\u201c\u88f3\u513f\uff0c\u5c0f\u5fc3\u4e86\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u628a\u624b\u4e00\u626c\uff0c\u53c1\u56e2\u8d64\u8272\u5149\u534e\uff0c\u7535\u5c04\u98de\u6765\uff0c\u7389\u7f57\u5239\u5df2\u6709\u9632 \u5907\uff0c\u5728\u7a7a\u4e2d\u4e00\u4e2a\u8f6c\u8eab\uff0c\u907f\u4e86\u5f00\u53bb\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u6363\u4ec0\u9ebd\u9b3c\u628a\u620f\uff1f\u201d\u90a3\u6599\u53e3\u65b9\u5f20\u5f00\uff0c\u7b11\u58f0\u672a\u6b47\uff0c \u8ddf\u524d\u7ea2\u5149\u4e00\u95ea\uff0c\u4e00\u9897\u5706\u6e9c\u6e9c\u7684\u4e1c\u897f\uff0c\u7a81\u7136\u98de\u8fdb\u53e3\u4e2d\uff0c\u7389\u7f57\u5239\u5934\u4e0b\u811a\u4e0a\uff0c\u75be\u51b2\u4e0b\u6765\uff0c\u7ea2\u82b1\u9b3c\u6bcd \u53cd\u624b\u4e00\u62d0\uff0c\u7389\u7f57\u5239\u4e00\u4e2a\u201c\u7ec6\u80f8\u5de7\u7ffb\u4e91\u201d\uff0c\u8eab\u7ffb\u4e86\u8fc7\u6765\uff0c\u5b9d\u5251\u5728\u62d0\u4e0a\u4e00\u70b9\uff0c\u5012\u8dc3\u51fa\u53c1\u4e08\u5f00\u5916\uff0c \u7ad9\u5728\u5730\u4e0a\uff0c\u6447\u6447\u6643\u6643\u3002\u5353\u4e00\u822a\u5927\u5403\u4e00\u60ca\uff0c\u94c1\u98de\u9f99\u5374\u4ecd\u662f\u795e\u8272\u5982\u5e38\uff0c\u5fae\u5fae\u53d1\u7b11\u3002 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u5f97\u610f\u4e4b\u6781\uff0c\u8fde\u8fde\u602a\u5578\uff0c\u8fc8\u6b65\u4e0a\u524d\uff0c\u5c06\u9f99\u5934\u62d0\u6756\u5411\u7389\u7f57\u5239\u80f8\u524d\u4e00\u70b9\uff0c\u53eb\u9053\uff1a\u201c\u4f60 \u8fd9\u5973\u5a03\u5b50\u8fd8\u4e0d\u53f6\u5251\u8ba4\u8f93\uff0c\u8981\u7b49\u6b7b\u9ebd\uff01\u201d\u7389\u7f57\u5239\u8eab\u5f62\u4e00\u6643\uff0c\u907f\u4e86\u5f00\u53bb\u3002\u7ea2\u82b1\u9b3c\u6bcd\u53c8\u559d\u9053\uff1a\u201c\u4f60 \u4e2d\u4e86\u6211\u7684\u6bd2\u73e0\uff0c\u6027\u547d\u4e0d\u8fc7\u4e00\u65f6\u53c1\u523b\uff0c\u8d76\u5feb\u6ca1\u964d\uff0c\u8fd8\u53ef\u4ee5\u6551\u4f60\u4e00\u547d\u3002\u201d\u7389\u7f57\u5239\u53c8\u6643\u4e86\u4e00\u6643\uff0c\u4ecd \u7136\u4e0d\u7406\u3002\u7ea2\u82b1\u9b3c\u6bcd\u5fc3\u9053\uff1a\u8fd9\u5973\u5a03\u89c1\u597d\u5014\u5f3a\uff01\u4e00\u628a\u6293\u53bb\uff0c\u7389\u7f57\u5239\u7a81\u7136\u5f20\u53e3\u4e00\u5410\uff0c\u4e00\u9897\u8d64\u7ea2\u5982\u8840 \u7684\u73cd\u73e0\u98de\u4e86\u51fa\u6765\uff0c\u5237\u7684\u4e00\u5251\u524a\u53bb\u3002\u7ea2\u82b1\u9b3c\u6bcd\u4ee5\u4e3a\u5979\u53d7\u4e86\u4f24\uff0c\u6599\u4e0d\u5230\u5979\u8eab\u624b\u8fd8\u662f\u5982\u6b64\u77eb\u6377\uff0c\u55e4 \u7684\u4e00\u58f0\uff0c\u6025\u95ea\u5f00\u65f6\uff0c\u8863\u8896\u5df2\u88ab\u524a\u53bb\u4e00\u622a\u3002\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u5996\u5987\u8fd8\u4e0d\u8ba4\u8f93\uff0c\u8981\u7b49\u6b7b \u9ebd\uff01\u201d \u3000\u3000\u539f\u6765\u8fd9\u8d64\u7ea2\u5982\u8840\u7684\u73cd\u73e0\uff0c\u4e43\u7ea2\u82b1\u9b3c\u6bcd\u7684\u72ec\u95e8\u6697\u5668\uff0c\u540d\u4e3a\u201c\u8d64\u6bd2\u73e0\u201d\u3002\u6b64\u662f\u5c06\u73cd\u73e0\u5728\u6bd2\u86c7 \u8840\u4e2d\u6d78\u70bc\uff0c\u76f4\u5230\u628a\u767d\u8272\u7684\u73cd\u73e0\u70bc\u5230\u8d64\u7ea2\u5982\u8840\u65b9\u6b62\uff0c\u5267\u6bd2\u65e0\u6bd4\uff0c\u8f7b\u6613\u4e0d\u80af\u4f7f\u7528\u3002\u5e78\u800c\u7a46\u4e5d\u5a18\u6628 \u665a\u5c06\u53c1\u9897\u8d64\u6bd2\u73e0\u5e26\u6765\u793a\u8b66\uff0c\u94c1\u98de\u9f99\u6709\u4e86\u9632\u5907\uff0c\u6559\u7389\u7f57\u5239\u5c06\u96c4\u5bc5\u7b49\u836f\u7269\u70bc\u6210\u7684\u836f\u4e38\u542b\u5728\u53e3\u4e2d\uff0c \u6545\u610f\u63a5\u5979\u4e00\u9897\uff0c\u7136\u5f8c\u51fa\u5176\u4e0d\u610f\u5410\u4e86\u51fa\u53bb\uff0c\u5206\u6563\u5979\u7684\u5fc3\u795e\uff0c\u523a\u5979\u4e00\u5251\u3002 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u5927\u6012\uff0c\u94c1\u62d0\u4e00\u9707\uff0c\u628a\u7389\u7f57\u5239\u7684\u5b9d\u5251\u6c64\u5f00\u3002\u94c1\u98de\u9f99\u53eb\u9053\uff1a\u201c\u7ea2\u82b1\u9b3c\u6bcd\uff0c\u4f60\u8981\u4e0d\u8981 \u8138\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u4e00\u58f0\u4e0d\u54cd\uff0c\u94c1\u62d0\u75be\u626b\u3002\u7389\u7f57\u5239\u51b7\u7b11\u9053\uff1a\u201c\u8001\u5996\u5987\uff0c\u4f60\u8fd8\u6709\u4ec0\u9ebd\u4f0e\u4fe9\uff01\u201d\u8fd0\u5251 \u5982\u98ce\uff0c\u864e\u8dc3\u9e70\u7fd4\uff0c\u98d2\u98d2\u8fde\u58f0\uff0c\u6d51\u8eab\u4e0a\u4e0b\uff0c\u5377\u8d77\u7cbe\u8292\u51b7\u7535\u3002\u7ea2\u82b1\u9b3c\u6bcd\u9000\u4e86\u51e0\u6b65\uff0c\u7a81\u7136\u4e00\u8dc3\u800c \u4e0a\uff0c\u7528\u529b\u5c06\u9f99\u5934\u62d0\u6756\u4e00\u6296\uff0c\u7389\u7f57\u5239\u5de6\u624b\u634f\u7740\u5251\u8bc0\uff0c\u53f3\u624b\u6a2a\u5251\u4e00\u5c01\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u7ea2 \u82b1\u9b3c\u6bcd\u7684\u9f99\u5934\u62d0\u6756\u4e00\u6b6a\uff0c\u6756\u5934\u4e0a\u7a81\u7136\u4f38\u51fa\u4e00\u679d\u660e\u6643\u6643\u7684\u5229\u5203\uff0c\u51ed\u7a7a\u957f\u4e86\u4e00\u5c3a\u3002\u8981\u77e5\u9ad8\u624b\u8f83 \u91cf\uff0c\u5206\u5bf8\u4e4b\u95f4\u90fd\u8981\u8ba1\u7b97\u5f97\u5341\u5206\u51c6\u786e\uff0c\u7389\u7f57\u5239\u6240\u5360\u65b9\u4f4d\uff0c\u672c\u662f\u62d0\u6756\u4e0d\u53ca\u4e4b\u5904\uff0c\u90a3\u6599\u654c\u4eba\u7684\u62d0\u6756 \u5934\u4e0a\u5ffd\u7136\u4f38\u51fa\u4e00\u679d\u5229\u5203\uff0c\u7389\u7f57\u5239\u5251\u5df2\u5c01\u4e86\u51fa\u53bb\uff0c\u4e0d\u53ca\u56de\u9632\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u8eab\u624b\u4f55\u7b49\u8fc5\u75be\uff0c\u62d0\u6756\u5411 \u524d\u4e00\u9001\uff0c\u5229\u5203\u51b7\u68ee\u68ee\uff0c\u6307\u5230\u4e86\u7389\u7f57\u5239\u7684\u5fc3\u7a9d\uff01 \u3000\u3000\u94c1\u98de\u9f99\u5728\u65c1\u770b\u5f97\u771f\u5207\uff0c\u7a81\u7136\u60f3\u8d77\u767d\u77f3\u9053\u4eba\u5fc3\u53e3\u7684\u5200\u75d5\uff0c\u51b7\u6c57\u8ff8\u6d41\uff0c\u98de\u8eab\u8dc3\u5165\u5708\u5b50\uff0c\u5927\u58f0 \u559d\u9053\uff1a\u201c\u7528\u6bd2\u624b\u5bf9\u4ed8\u5c0f\u8f88\u4e0d\u5bb3\u81ca\u9ebd\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u5fc3\u5934\u4e00\u9707\uff0c\u4f46\u5979\u8fd9\u62db\u5feb\u5982\u7535\u5149\u706b\u77f3\uff0c\u8981\u6536\u624b \u4e5f\u4e0d\u53ef\u80fd\uff0c\u94c1\u98de\u9f99\u8eab\u5f62\u65b9\u8d77\uff0c\u573a\u4e2d\u5df2\u6709\u4eba\u60e8\u53eb\u4e00\u58f0\uff0c\u94c1\u98de\u9f99\u7acb\u7a33\u8db3\u65f6\uff0c\u53ea\u89c1\u7389\u7f57\u5239\u4e0e\u7ea2\u82b1\u9b3c \u6bcd\u5df2\u7ecf\u5206\u5f00\uff0c\u7389\u7f57\u5239\u795e\u8272\u81ea\u5982\uff0c\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u6765\uff0c\u6765\uff0c\u6765\uff01\u6211\u4e0e\u4f60\u518d\u6597\u53c1\u767e\u62db\uff01\u201d\u94c1\u98de\u9f99\u5927 \u4e3a\u60ca\u5f02\uff0c\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u7389\u7f57\u5239\u4f1a\u6709\u8fd9\u6837\u9ad8\u5f3a\u7684\u672c\u9886\uff0c\u5c45\u7136\u80fd\u591f\u6b7b\u91cc\u9003\u751f\uff01 \u3000\u3000\u5176\u5b9e\u5e76\u4e0d\u662f\u7389\u7f57\u5239\u51ed\u7740\u672c\u8eab\u7684\u529f\u592b\u9003\u4e86\u8fd9\u62db\uff0c\u800c\u662f\u5cb3\u545c\u73c2\u90a3\u5bf9\u624b\u5957\u7684\u529b\u91cf\u3002\u7ea2\u82b1\u9b3c\u6bcd\u7684 \u6bd2\u5203\u582a\u582a\u63d2\u5230\u5fc3\u7a9d\uff0c\u7389\u7f57\u5239\u5de6\u624b\u672c\u6765\u662f\u634f\u7740\u5251\u8bc0\uff0c\u6a2a\u5728\u80f8\u524d\uff0c\u8fd9\u65f6\u8feb\u65bc\u65e0\u5948\uff0c\u767e\u5fd9\u4e2d\u65e0\u6687\u8003 \u8651\uff0c\u6c89\u638c\u4e00\u683c\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u4e00\u5200\u63d2\u4e2d\u5979\u7684\u638c\u5fc3\uff0c\u5200\u5c16\u4e00\u5f2f\uff0c\u5374\u63d2\u4e0d\u8fdb\u53bb\uff01\u7389\u7f57\u5239\u5251\u62db\u4f55\u7b49\u5feb \u6377\uff0c\u5c31\u5728\u7ea2\u82b1\u9b3c\u6bcd\u7a81\u5403\u4e00\u60ca\u4e4b\u9645\uff0c\u624b\u81c2\u4e00\u5708\uff0c\u56de\u624b\u4e00\u5251\uff0c\u628a\u7ea2\u82b1\u9b3c\u6bcd\u80a9\u4e0a\u7684\u7435\u7436\u9aa8\u523a\u7a7f\uff01 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u60e8\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\uff0c\u957f\u6c5f\u5f8c\u6d6a\u63a8\u524d\u6d6a\uff0c\u4ece\u4eca\u4e4b\u5f8c.\u6c5f\u6e56\u4e0a\u518d\u4e5f\u6ca1\u6709\u7ea2\u82b1\u9b3c\u6bcd \u8fd9\u53f7\u4eba\u7269\uff01\u201d\u62d0\u6756\u4e00\u987f\uff0c\u970e\u5ffd\u4e4b\u95f4\u9003\u5f97\u65e0\u5f71\u65e0\u8e2a\uff01\u7389\u7f57\u5239\u683c\u683c\u7b11\u9053\uff1a\u201c\u8fd9\u5bf9\u624b\u5957\u771f\u662f\u5b9d \u8d1d\uff01\u201d\u628a\u80f8\u8863\u89e3\u5f00\uff0c\u91cc\u9762\u7684\u62a4\u5fc3\u94dc\u955c\u54d7\u5566\u5566\u4e00\u9635\u54cd\uff0c\u788e\u6210\u65e0\u6570\u5c0f\u7247\uff0c\u8dcc\u4e86\u4e0b\u6765\u3002\u7389\u7f57\u5239\u5403\u4e86 \u4e24\u9897\u836f\u4e38\uff0c\u8fd0\u6c14\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u5e78\u597d\u6ca1\u6709\u53d7\u7740\u5185\u4f24\u3002\u201d\u5353\u4e00\u822a\u6035\u76ee\u60ca\u5fc3\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u7ec3\u59d0 \u59d0\uff01\u201d\u7389\u7f57\u5239\u70b9\u4e00\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u4eec\u6b66\u5f53\u6d3e\u8fd8\u6709\u4ea4\u4ee3\u3002\u201d\u8d70\u5230\u767d\u77f3\u9053\u4eba\u8eab\u65c1\uff0c\u767d\u77f3\u9053\u4eba \u670d\u4e86\u89e3\u836f\uff0c\u6bd4\u524d\u8212\u670d\u5f97\u591a\uff0c\u5dc5\u5dcd\u5dcd\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u7389\u7f57\u5239\u628a\u5251\u4e00\u626c\uff0c\u5353\u4e00\u822a\u5927\u53eb\u9053\uff1a\u201c\u4f60\u505a\u4ec0 \u9ebd\uff1f\u201d\u767d\u77f3\u9053\u4eba\u5706\u7741\u53cc\u76ee\uff0c\u624b\u6478\u5251\u67c4\u3002\u7389\u7f57\u5239\u9053\uff1a\u201c\u767d\u77f3\u9053\u4eba\uff0c\u4f60\u5df2\u53d7\u4e86\u91cd\u4f24\uff0c\u54b1\u4eec\u8fd9\u573a\u6bd4 \u5251\u8bb0\u4e0b\u6765\u5427\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f55\u5fc5\u8fd8\u8981\u6bd4\u5251\uff1f\u201d\u767d\u77f3\u9053\u4eba\u9053\uff1a\u201c\u597d\uff0c\u53c1\u5e74\u4e4b\u5185\uff0c\u6211\u5728\u6b66\u5f53\u5c71 \u7b49\u4f60\uff01\u201d\u7389\u7f57\u5239\u51b7\u7b11\u9053\uff1a\u201c\u6211\u51c6\u4e0d\u4f1a\u53eb\u4f60\u5931\u671b\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u5ffd\u542c\u5f97\u79d8\u9b54\u5d16\u4e0b\u4e00\u7247\u4eba\u58f0\uff0c\u94c1\u98de\u9f99\u8df3\u4e0a\u5ca9\u77f3\uff0c\u53ea\u89c1\u4e0b\u9762\u6709\u4eba  \u6740\uff0c\u4e00\u7fa4\u4e1c\u5382 \u536b\u58eb\u56f4\u7740\u4e00\u6761\u5927\u6c49\uff0c\u53e6\u6709\u4e00\u540d\u5c11\u5973\u5df2\u88ab\u7f1a\u5728\u9a6c\u80cc\uff0c\u5931\u58f0\u53eb\u5524\u3002 \u3000\u3000\u767d\u77f3\u9053\u4eba\u500f\u7136\u53d8\u8272\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4e00\u822a\u4f60\u542c\uff0c\u8fd9\u4e0d\u662f\u843c\u534e\u5728\u53eb\u6211\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u542c \u4e0d\u6e05\u695a\u3002\u201d\u5c71\u98ce\u9001\u58f0\uff0c\u6108\u6765\u6108\u8fd1\u3002\u767d\u77f3\u53eb\u9053\uff1a\u201c\u662f\u843c\u534e\u3002\u843c\u534e\uff01\u201d\u632f\u81c2\u4e00\u8dc3\uff0c\u8df3\u4e0a\u5ca9\u77f3\u3002\u94c1 \u98de\u9f99\u9053\uff1a\u201c\u4f60\u627e\u6b7b\u9ebd\uff1f\u201d\u767d\u77f3\u91cd\u4f24\u4e4b\u5f8c\uff0c\u6c14\u529b\u4e0d\u5982\uff0c\u7eb5\u8eab\u4e00\u8dc3\uff0c\u7a81\u7136\u817f\u8f6f\uff0c\u51e0\u4e4e\u8dcc\u4e0b\u5ca9\u53bb\u3002 \u94c1\u98de\u9f99\u4e00\u624b\u628a\u4ed6\u62c9\u7740\uff0c\u9053\uff1a\u201c\u4e00\u822a\uff0c\u80cc\u4f60\u7684\u5e08\u53d4\u56de\u53bb\u3002\u201d\u5ca9\u4e0b\u6709\u5341\u591a\u540d\u536b\u58eb\u6500\u85e4\u9644\u845b\uff0c\u8dc3\u4e0a \u5ca9\u6765\u3002\u94c1\u98de\u9f99\u4e00\u58f0\u957f\u5578\uff0c\u62ac\u8d77\u77f3\u5934\uff0c\u96e8\u70b9\u822c\u629b\u63b7\u4e0b\u53bb\uff0c\u722c\u4e0a\u6765\u7684\u536b\u58eb\u53d1\u4e00\u58f0\u558a\uff0c\u7eb7\u7eb7\u8eb2\u907f\u3002 \u94c1\u98de\u9f99\u6325\u624b\u9053\uff1a\u201c\u5feb\u8d70\uff01\u201d\u5353\u4e00\u822a\u80cc\u8d77\u5e08\u53d4\uff0c\u968f\u7389\u7f57\u5239\u4ece\u80cc\u9762\u4e0b\u5c71\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u94c1\u98de\u9f99\u4e5f\u8d76 \u4e86\u6765\uff0c\u9053\uff1a\u201c\u91d1\u8001\u602a\u771f\u4e0d\u662f\u4e1c\u897f\uff0c\u4ed6\u5506\u4f7f\u4ed6\u7684\u81ed\u5a46\u5a18\u7ea6\u6211\u4eec\u5355\u6253\u72ec\u6597\uff0c\u6697\u4e2d\u5374\u53c8\u5e26\u4e1c\u5382\u7684\u536b \u58eb\u6765\u6349\u4eba\u3002\u201d\u7389\u7f57\u5239\u6068\u6068\u8bf4\u9053\uff1a\u201c\u4ed6\u7684\u81ed\u5a46\u5a18\u5df2\u4e0d\u5e2e\u4ed6\u4e86\uff0c\u4ed6\u82e5\u518d\u649e\u5728\u6211\u7684\u624b\u91cc\uff0c\u7ba1\u6559\u4ed6\u4e0d \u80fd\u9003\u547d\u3002\u201d \u3000\u3000\u53c1\u4eba\u811a\u7a0b\u8fc5\u75be\uff0c\u8438\u660f\u65f6\u5206\u56de\u5230\u57ce\u4e2d\uff0c\u5353\u4e00\u822a\u9053\uff1a\u201c\u94c1\u8001\u524d\u8f88\uff0c\u8bf7\u540c\u5230\u67f3\u6b66\u5e08\u5bb6\u4e2d\u4e00 \u5750\u3002\u201d\u767d\u77f3\u9053\u4eba\u4f4f\u5728\u67f3\u897f\u94ed\u5bb6\u4e2d\u3002\u7389\u7f57\u5239\u4e00\u7b11\u9053\uff1a\u201c\u597d\u4eba\u505a\u5230\u5e95\uff0c\u4f60\u7684\u5e08\u53d4\u53d7\u4e86\u91cd\u4f24\uff0c\u6211\u4eec \u81ea\u5f53\u62a4\u9001\u4ed6\u5e73\u5b89\u5230\u5bb6\u3002\u201d\u767d\u77f3\u9053\u4eba\u7ffb\u4e86\u4e00\u7ffb\u767d\u773c\uff0c\u6c14\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u3002 \u3000\u3000\u67f3\u897f\u94ed\u89c1\u767d\u77f3\u9053\u4eba\u53d7\u4e86\u91cd\u4f24\uff0c\u94c1\u98de\u9f99\u548c\u7389\u7f57\u5239\u966a\u4ed6\u56de\u6765\uff0c\u5403\u4e86\u4e00\u60ca\u3002\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\u6469\u62f3 \u64e6\u638c\uff0c\u7eb7\u7eb7\u8d77\u7acb\uff0c\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u4e0d\u5173\u6211\u4e8b\u3002\u201d\u94c1\u98de\u9f99\u5c06\u767d\u77f3\u9053\u4eba\u88ab\u7ea2\u82b1\u9b3c\u6bcd\u6253\u4f24\u7684\u4e8b \u8bf4\u4e86\uff0c\u5e76\u9053\uff1a\u201c\u5e78\u559c\u6211\u65e9\u51c6\u5907\u597d\u4e86\u89e3\u836f\uff0c\u5f3a\u4ed6\u5403\u4e86\u3002\u4ed6\u5185\u529f\u9887\u6709\u6839\u5e95\uff0c\u9759\u517b\u53c1\u5929\uff0c\u4fbf\u53ef\u8d70 \u52a8\uff0c\u518d\u8fc7\u4e00\u6708\uff0c\u53ef\u4ee5\u5b8c\u5168\u590d\u5143\u3002\u201d\u6b66\u5f53\u6d3e\u7684\u4eba\u89c1\u94c1\u98de\u9f99\u8bf4\u51fa\u60c5\u7531\uff0c\u6709\u7684\u4fbf\u4e0a\u6765\u62dc\u8c22\u3002\u767d\u77f3\u9053 \u4eba\u5c34\u5c2c\u4e4b\u6781\uff0c\u9053\uff1a\u201c\u4e00\u822a\uff0c\u4f60\u966a\u6211\u8fdb\u53bb\u3002\u201d\u6709\u4e24\u540d\u5f1f\u5b50\u7980\u9053\uff1a\u201c\u5e08\u59b9\u548c\u674e\u5e08\u5144\u8d70\u53bb\u89c2\u6218\uff0c\u6ca1 \u6709\u89c1\u7740\u5e08\u53d4\u9ebd\uff1f\u201d\u767d\u77f3\u9053\u4eba\u6325\u624b\u9053\uff1a\u201c\u90fd\u8fdb\u91cc\u9762\u53bb\u8bf4\u3002\u201d\u5411\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u7684\u89e3\u836f\u53ef\u4e0d\u662f\u6211 \u8981\u5403\u7684\u3002\u201d\u94c1\u98de\u9f99\u5fae\u5fae\u4e00\u7b11\uff0c\u767d\u77f3\u7eed\u9053\uff1a\u201c\u4f46\u6211\u4e00\u6837\u9886\u4f60\u7684\u60c5\u3002\u6211\u4eec\u6b66\u5f53\u6d3e\u6069\u6028\u5206\u660e\uff0c\u4f60\u7684 \u5927\u6069\u5b9a\u5f53\u62a5\u7b54\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u6211\u5bf9\u4f60\u53ef\u6ca1\u6709\u6069\uff0c\u4f60\u4f24\u597d\u4e4b\u5f8c\uff0c\u968f\u65f6\u53ef\u4ee5\u7ea6\u6211\u6bd4\u5251\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u548c\u4f17\u540c\u95e8\u6276\u5e08\u53d4\u4eba\u5185\u4f11\u606f\uff0c\u67f3\u897f\u94ed\u7b11\u9053\uff1a\u201c\u8fd9\u9053\u58eb\u771f\u9a84\uff0c\u65e0\u8bba\u5982\u4f55\u4e0d\u80af\u8f93\u53e3\u3002\u4ed6 \u7684\u5378\u5144\u7d2b\u9633\u9053\u957f\u8c26\u51b2\u548c\u6613\uff0c\u548c\u4ed6\u53ef\u5927\u4e0d\u76f8\u540c\u3002\u201d\u94c1\u98de\u9f99\u5fae\u7b11\u4e0d\u8bed\u3002\u67f3\u897f\u94ed\u7eed\u9053\uff1a\u201c\u7ea2\u82b1\u9b3c\u6bcd \u8fdb\u4eac\uff0c\u6211\u4eec\u524d\u4e24\u5929\u4e5f\u542c\u4eba\u8bf4\u8d77\uff0c\u53ef\u4e0d\u77e5\u5979\u4e3a\u4e86\u4f55\u4e8b\u3002\u539f\u6765\u5374\u662f\u627e\u4f60\u4eec\u7684\u5c94\u5b50\u3002\u201d\u94c1\u98de\u9f99\u5fc3\u5ff5 \u4e00\u52a8\uff0c\u5634\u5df4\u4e00\u5f20\uff0c\u5374\u53c8\u628a\u8bdd\u541e\u4f4f\u3002\u67f3\u897f\u94ed\u548c\u94c1\u98de\u9f99\u867d\u6709\u4e00\u9762\u4e4b\u7f18\uff0c\u5374\u975e\u77e5\u4ea4\u53cb\u597d\uff0c\u5f53\u4e0b\u4e5f\u4e0d \u4fbf\u95ee\u4ed6\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u5353\u4e00\u822a\u51fa\u6765\u9053\uff1a\u201c\u5e08\u53d4\u884c\u52a8\u4e0d\u4fbf\uff0c\u53eb\u6211\u66ff\u4ed6\u9001\u5ba2\u3002\u201d\u94c1\u98de\u9f99\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a \u201c\u4f60\u4e0d\u9001\u6211\u4e5f\u8981\u8d70\u4e86\u3002\u201d\u67f3\u897f\u94ed\u9887\u4e3a\u4e0d\u60a6\uff0c\u4ed6\u6b63\u60f3\u8d81\u6b64\u673a\u4f1a\uff0c\u4e0e\u94c1\u98de\u9f99\u7ed3\u7eb3\uff0c\u751a\u4e0d\u6ee1\u610f\u767d\u77f3 \u9053\u4eba\u55a7\u5bbe\u593a\u4e3b\u3002\u4f46\u4ed6\u788d\u65bc\u6b66\u5f53\u6d3e\u60c5\u9762\uff0c\u800c\u4e14\u548c\u767d\u77f3\u9053\u4eba\u53c8\u662f\u8001\u670b\u53cb\u4e86\uff0c\u6240\u4ee5\u4e5f\u4e0d\u4fbf\u53d1\u4f5c\u3002\u5f53 \u4e0b\u62f1\u4e86\u62f1\u624b\uff0c\u548c\u94c1\u98de\u9f99\u7389\u7f57\u5239\u9053\u522b\u3002 \u3000\u3000\u5353\u4e00\u822a\u9001\u51fa\u95e8\u5916\uff0c\u9053\uff1a\u201c\u655d\u5e08\u53d4\u4e0d\u8fd1\u4eba\u60c5\uff0c\u671b\u94c1\u8001\u524d\u8f88\u6055\u7f6a\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d \u8bf4\u3002\u4f60\u5e08\u53d4\u6709\u4ec0\u9ebd\u8bdd\u4ea4\u4ee3\u4f60\u8bf4\u3002\u201d\u5353\u4e00\u822a\u9762\u4e0a\u4e00\u7ea2\uff0c\u539f\u6765\u4ed6\u5e08\u53d4\u5bf9\u4e00\u4f17\u540c\u95e8\u5429\u5490\uff0c\u8bf4\u94c1\u98de\u9f99 \u867d\u5bf9\u4ed6\u6709\u6069\uff0c\u7389\u7f57\u5239\u5374\u662f\u672c\u95e8\u516c\u654c\uff0c\u51e1\u662f\u6b66\u5f53\u6d3e\u4eba\u90fd\u4e0d\u51c6\u4e0e\u7389\u7f57\u5239\u6765\u5f80\u3002\u8fd9\u8bdd\u660e\u662f\u544a\u8beb\u4e00\u4f17 \u540c\u95e8\uff0c\u5b9e\u662f\u8bf4\u7ed9\u5353\u4e00\u822a\u4e00\u4e2a\u4eba\u542c\u3002\u53eb\u5353\u4e00\u822a\u66ff\u4ed6\u9001\u5ba2\uff0c\u4e5f\u662f\u542b\u6709\u53eb\u4ed6\u548c\u7389\u7f57\u5239\u8bc0\u522b\u7684\u610f\u601d\u3002 \u3000\u3000\u7389\u7f57\u5239\u8f7b\u8f7b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u8bf4\u6211\u4e5f\u77e5\u9053\uff0c\u603b\u4e4b\u662f\u4e0d\u51c6\u4f60\u548c\u6211\u4eb2\u8fd1\u5c31\u662f\u4e86\u3002\u6211\u504f\u4e0d\u6015 \u4ed6\uff0c\u4f60\u5bb3\u6015\u6211\u4eb2\u8fd1\u4f60\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u9762\u7ea2\u76f4\u900f\u8033\u80cc\u3002\u94c1\u98de\u9f99\u7b11\u9053\uff1a\u201c\u88f3\u513f\uff0c\u4f60\u7684\u53e3\u597d\u6ca1\u906e\u62e6\uff0c \u628a\u4eba\u7a98\u5f97\u8fd9\u4e2a\u6837\u5b50\u3002\u201d\u5353\u4e00\u822a\u8fdf\u7591\u4e86\u4e00\u9635\uff0c\u5ffd\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u6211\u6709\u8bdd\u548c\u4f60\u8bf4\u3002\u201d\u94c1\u98de\u9f99\u884c\u5f00 \u51e0\u6b65\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u5e08\u53d4\u6709\u4e2a\u5973\u513f\uff0c\u7ed9\u4e1c\u5382\u7684\u536b\u58eb\u63b3\u53bb\u4e86\u3002\u6211\u5e08\u53d4 \u53d7\u4e86\u91cd\u4f24\uff0c\u4eac\u4e2d\u53c8\u627e\u4e0d\u5230\u80fd\u8010\u7279\u522b\u9ad8\u5f3a\u7684\u4eba\uff0c\u2026\u2026\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u8981\u627e\u6211\u4eec\u66ff\u4f60\u60f3 \u6cd5\u5b50\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6b63\u662f\u3002\u4f60\u4eec\u82e5\u80fd\u628a\u4ed6\u7684\u5973\u513f\u6551\u51fa\u6765\uff0c\u8fd9\u4e00\u6881\u5b50\u5c31\u4e0d\u89e3\u81ea\u89e3\u4e86\u3002\u201d\u7389\u7f57 \u5239\u9053\uff1a\u201c\u4f60\u4eec\u6b66\u5f53\u6d3e\u90a3\u51e0\u4e2a\u957f\u8001\uff0c\u867d\u65e0\u8fc7\u9519\uff0c\u9762\u76ee\u53ef\u618e\uff0c\u4ed6\u4eec\u4e0d\u9ad8\u5174\u6211\uff0c\u6211\u5c31\u504f\u8981\u548c\u4ed6\u4eec\u4f5c \u5bf9\u3002\u201d\u5353\u4e00\u822a\u9ed8\u7136\u4e0d\u8bed\u3002\u7389\u7f57\u5239\u5ffd\u9053\uff1a\u201c\u4f60\u5e08\u53d4\u90a3\u4e2a\u5973\u513f\u957f\u5f97\u7f8e\u4e0d\u7f8e\u5440\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u90a3 \u5f53\u7136\u6bd4\u4e0d\u4e0a\u7ec3\u59d0\u59d0\u4e86\u3002\u201d\u7389\u7f57\u5239\u4e00\u7b11\u9053\uff1a\u201c\u957f\u5f97\u4e5f\u4e0d\u96be\u770b\u5427\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5728\u4e00\u822c\u5973\u5b50 \u4e2d\uff0c\u4e5f\u7b97\u5f97\u662f\u7f8e\u8c8c\u7684\u4e86\u3002\u201d\u7389\u7f57\u5239\u82e5\u6709\u6240\u601d\uff0c\u9762\u8272\u5ffd\u5730\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u5b9e\u8bdd\uff0c\u4f60\u5e08\u53d4\u662f\u4e0d \u662f\u60f3\u628a\u4ed6\u7684\u5973\u513f\u8bb8\u914d\u7ed9\u4f60\uff1f\u201d\u5353\u4e00\u822a\u55eb\u5685\u8bf4\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u8bf4\u8fc7\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u53c8\u4e0d\u662f\u6728 \u5934\uff0c\u96be\u9053\u4ed6\u7684\u610f\u601d\u4f60\u4e5f\u770b\u4e0d\u51fa\u6765\u5417\uff1f\u201d\u5353\u4e00\u822a\u53ea\u5f97\u8bf4\u9053\uff1a\u201c\u6211\u770b\u2026\u2026\u4e5f\u8bb8\u4f1a\u6709\u8fd9\u4e2a\u610f\u601d\u3002\u201d \u7389\u7f57\u5239\u51b7\u51b7\u4e00\u7b11\uff0c\u5353\u4e00\u822a\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u603b\u4e0d\u4f1a\u5fd8\u4e86\u59d0\u59d0\u3002\u201d\u7389\u7f57\u5239\u82b3\u5fc3\u4e00\u8df3\uff0c\u8fd9\u8fd8\u662f\u5353\u4e00 \u822a\u7b2c\u4e00\u6b21\u5bf9\u5979\u660e\u767d\u8868\u793a\u3002\u5353\u4e00\u822a\u7eed\u9053\uff1a\u201c\u4f46\u6211\u6b66\u5f53\u6d3e\u95e8\u89c4\u7d20\u4e25\u2026\u2026\u201d\u7389\u7f57\u5239\u79c0\u7709\u4e00\u7ad6\uff0c\u9053\uff1a \u201c\u600e\u9ebd\uff0c\u4f60\u6015\u4e86\uff1f\u201d\u5353\u4e00\u822a\u7eed\u9053\uff1a\u201c\u82e5\u7136\u6211\u4eec\u4e0d\u80fd\u76f8\u5904\uff0c\u5c31\u7b97\u6d77\u89d2\u5929\u6daf\u6211\u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\u4e86\u4f60\u3002 \u6211\uff0c\u6211\u7ec8\u8eab\u4e0d\u5a36\u3002\u201d\u8bf4\u5230\u5f8c\u6765\uff0c\u8bdd\u58f0\u4f4e\u6c89\uff0c\u51e0\u4e4e\u4e0d\u53ef\u5206\u8fa8\u3002\u7389\u7f57\u5239\u597d\u751f\u5931\u671b\uff0c\u5fc3\u9053\uff1a\u201c\u771f\u662f \u8113\u5305\u3002\u505a\u4e8b\u754f\u9996\u754f\u5c3e\uff0c\u4e00\u70b9\u513f\u4e5f\u4e0d\u723d\u8106\u3002\u201d\u5353\u4e00\u822a\u89c1\u7389\u7f57\u5239\u53d8\u4e86\u989c\u8272\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u4e5f\u77e5 \u9053\u6240\u6c42\u975e\u4efd\uff0c\u6211\u5e08\u53d4\u5f97\u7f6a\u4e86\u4f60\uff0c\u6211\u5374\u8981\u4f60\u53bb\u6551\u4ed6\u7684\u5973\u513f\u3002\u201d\u7389\u7f57\u5239\u51dd\u671b\u665a\u971e\uff0c\u601d\u6f6e\u6d6a\u6d8c\uff0c\u5979 \u4e00\u9762\u6068\u5353\u4e00\u822a\u7684\u8f6f\u5f31\uff0c\u4f46\u8f6c\u5fc3\u4e00\u60f3\uff1a\u4ed6\u5230\u5e95\u662f\u6b22\u559c\u6211\u7684\u3002\u4e5f\u81ea\u6709\u70b9\u6b23\u6170\u3002\u5353\u4e00\u822a\u8bf4\u8bdd\u4e4b\u5f8c\uff0c \u5077\u770b\u5979\u7684\u8138\u8272\uff0c\u7389\u7f57\u5239\u7709\u6bdb\u4e00\u626c\uff0c\u5ffd\u9053\uff1a\u201c\u6789\u6211\u4eec\u76f8\u4ea4\u4e00\u573a\u2026\u2026\u201d\u5353\u4e00\u822a\u4e00\u9635\u98a4\u6817\uff0c\u5fc3\u9053\uff1a \u201c\u7cdf\u4e86\uff0c\u7cdf\u4e86\uff01\u201d\u7389\u7f57\u5239\u7eed\u9053\uff1a\u201c\u4f60\u7b80\u76f4\u4e00\u70b9\u4e5f\u4e0d\u61c2\u5f97\u6211\u7684\u771f\u4eba\u3002\u201d\u5353\u4e00\u822a\u731c\u4e0d\u900f\u5979\u559c\u6012\u5982 \u4f55\uff01\u8bf4\u4e0d\u51fa\u8bdd\u3002\u7389\u7f57\u5239\u5ffd\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4e3a\u4e86\u8981\u8ba8\u597d\u767d\u77f3\u9053\u4eba\uff0c\u4f46\u6211\u7b54\u5e94\u4f60\uff0c\u6211\u4e00\u5b9a\u4e3a\u4f60\u6551\u4e86 \u5e08\u59b9\u3002\u201d\u5353\u4e00\u822a\u5927\u559c\u62dc\u8c22\uff0c\u5ffd\u53c8\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u6551\u5979\u51fa\u6765\uff0c\u4e0d\u8981\u8bf4\u662f\u6211\u6258\u4f60\u505a\u7684\u3002\u6211\u5e08 \u53d4\u2026\u2026\u201d\u7389\u7f57\u5239\u6012\u9053\uff1a\u201c\u6211\u77e5\u9053\u5566\uff0c\u4f60\u4eec\u6b66\u5f53\u6d3e\u4ece\u4e0d\u6c42\u4eba\uff0c\u4f60\u53c8\u6015\u72af\u4e86\u95e8\u89c4\u5566\uff01\u597d\uff0c\u4f60\u56de\u53bb \u5427\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u4e00\u6012\u628a\u5353\u4e00\u822a\u65a5\u8d70\uff0c\u770b\u4ed6\u80cc\u5f71\u6ca1\u5165\u6731\u95e8\uff0c\u53c8\u6697\u6697\u5f8c\u6094\u3002\u94c1\u98de\u9f99\u8d70\u8fc7\u6765\u9053\uff1a\u201c\u4ed6\u8bf4 \u4ec0\u9ebd\uff01\u201d\u7389\u7f57\u5239\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u6ca1\u4ec0\u9ebd\u3002\u201d\u4e24\u4eba\u8d76\u56de\u897f\u5c71\u4f4f\u5904\u3002\u7389\u7f57\u5239\u4e00\u8def\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u5230\u4e86\u7075 \u5149\u5bfa\u5f8c\uff0c\u7389\u7f57\u5239\u624d\u9053\uff1a\u201c\u7239\uff0c\u6211\u6c42\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u54b1\u4eec\u7237 \u513f\u4fe9\u53bb\u6551\u767d\u77f3\u9053\u4eba\u7684\u5973\u513f\u3002\u201d\u94c1\u98de\u9f99\u76b4\u7709\u8bf4\u9053\uff1a\u201c\u4f60\u548c\u5cb3\u545c\u73c2\u628a\u5bab\u4e2d\u95f9\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u8fd8\u60f3\u518d \u53bb\u81ea\u6295\u7f57\u7f51\u5417\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u5df2\u7b54\u5e94\u4eba\u5bb6\u4e86\u3002\u201d\u94c1\u98de\u9f99\u9ed8\u5750\u51dd\u601d\uff0c\u8fc7\u4e86\u597d\u4e45\uff0c\u77bf\u7136\u9192 \u8d77\uff0c\u9053\uff1a\u201c\u6709\u4e86\uff0c\u6211\u4eec\u4e0d\u5fc5\u8fdb\u5bab\u6551\u5979\u3002\u201d\u7389\u7f57\u5239\u559c\u9053\uff1a\u201c\u7239\u771f\u6709\u529e\u6cd5\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6211\u4e5f \u62ff\u4e0d\u7a33\u51c6\u6210\uff0c\u54b1\u4eec\u59d1\u8bd5\u4e00\u8bd5\u3002\u660e\u65e5\u6211\u548c\u4f60\u53bb\u627e\u9f99\u8fbe\u53c1\u5427\u3002\u201d \u3000\u3000\u518d\u8bf4\u4f55\u843c\u534e\u90a3\u65e5\uff0c\u60f3\u966a\u7236\u4eb2\u524d\u5f80\uff0c\u88ab\u7236\u4eb2\u8bad\u65a5\u4e00\u987f\uff0c\u5fc3\u4e2d\u4e0d\u5fff\u3002\u767d\u77f3\u9053\u4eba\u53bb\u5f8c\uff0c\u4f55\u843c\u534e \u6084\u6084\u53bb\u627e\u674e\u5c01\uff0c\u9080\u4ed6\u540c\u5230\u79d8\u9b54\u5d16\u53bb\u3002\u674e\u5c01\u662f\u6b66\u5f53\u6d3e\u5728\u5317\u4eac\u7684\u638c\u95e8\uff0c\u5fc3\u4e2d\u672c\u6765\u60f3\u53bb\uff0c\u53ea\u662f\u788d\u65bc \u767d\u77f3\u9053\u4eba\u7684\u547d\u4ee4\uff0c\u6240\u4ee5\u4e0d\u6562\u3002\u89c1\u4f55\u843c\u534e\u9080\u4ed6\uff0c\u6b63\u5408\u5fc3\u610f\u3002 \u3000\u3000\u4e24\u4eba\u5077\u5077\u51fa\u57ce\uff0c\u884c\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u5c06\u8fd1\u897f\u5c71\u3002\u674e\u5c01\u5ffd\u9053\uff1a\u201c\u5f8c\u9762\u6709\u4e24\u4e2a\u4eba\u597d\u50cf\u8ddf\u8e2a\u54b1 \u4eec\u3002\u201d\u4f55\u843c\u534e\u56de\u5934\u4e00\u770b\uff0c\u80cc\u5f8c\u679c\u7136\u6709\u4e24\u4e2a\u4eba\uff0c\u4e00\u4e2a\u662f\u56db\u5341\u6765\u5c81\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u4e00\u4e2a\u662f\u4e8c\u5341\u5c81\u5de6 \u53f3\u7684\u5c11\u5e74\uff0c\u76f8\u8c8c\u9887\u4e3a\u82f1\u4fca\uff0c\u4f3c\u4e4e\u5728\u90a3\u513f\u89c1\u8fc7\u3002\u4e24\u4eba\u6307\u70b9\u8c08\u7b11\uff0c\u597d\u50cf\u662f\u5728\u8bae\u8bba\u81ea\u5df1\u548c\u674e\u5c01\u4e00 \u6837\u3002\u4f55\u843c\u534e\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5bf9\u674e\u5c01\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u8def\uff0c\u4f60\u5f88\u719f\u5417\uff1f\u201d\u674e\u5c01\u7b11\u9053\uff1a\u201c\u6211\u662f\u8001\u5317\u4eac\u4e86\uff0c \u8fd8\u80fd\u4e0d\u719f\u3002\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u90a3\u9ebd\u54b1\u4eec\u7ed5\u8def\u907f\u5f00\u4ed6\u4eec\u3002\u201d\u8fc7\u4e86\u7247\u523b\u5df3\u5230\u897f\u5c71\u3002\u897f\u5c71\u6709\u53c1\u4e2a\u79c0\u4e3d \u7684\u5c71\u5cf0\uff1a\u7fe0\u5fae\u5c71\uff0e\u5362\u5e08\u5c71\u548c\u5e73\u5761\u5c71\u3002\u5230\u79d8\u9b54\u5d16\u7684\u8def\uff0c\u672c\u5e94\u4ece\u5e73\u5761\u5c71\u5b9d\u73e0\u6d1e\u6298\u5411\u5317\u884c\uff0c\u674e\u5c01\u5374 \u7ed5\u9053\u4ece\u7fe0\u5fae\u5c71\u7684\u5c71\u811a\u8d70\u53bb\u3002\u4e24\u4eba\u5c55\u5f00\u8f7b\u8eab\u529f\u592b\uff0c\u7ed5\u6797\u8d8a\u6da7\uff0c\u8d70\u4e86\u4e00\u9635\uff0c\u80cc\u5f8c\u90a3\u4e24\u4eba\u5df2\u7ecf\u4e0d \u89c1\u3002\u674e\u5c01\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u6211\u591a\u7591\u4e86\uff0c\u90a3\u4e24\u4eba\u6ca1\u6709\u8ddf\u6765\u3002\u201d\u4e24\u4eba\u7f13\u4e86\u811a\u6b65\uff0c\u5ffd\u542c\u5f97\u80cc\u5f8c\u53c8\u6709\u8c08\u7b11 \u4e4b\u58f0\u3002\u4f55\u843c\u534e\u518d\u56de\u5934\u770b\uff0c\u9661\u89c1\u90a3\u80cc\u5f8c\u4e24\u4eba\u722c\u4e0a\u5c71\u5761\u3002\u674e\u5c01\u9053\uff1a\u201c\u5e08 \u3000\u3000\uff0c\u8fd9\u4e24\u4e2a\u5bb6\u4f19\u662f\u5b58\u5fc3\u8ddf\u8e2a\u54b1\u4eec\u6765\u4e86\u3002\u201d\u624b\u6478\u5251\u67c4\u3002\u4f55\u843c\u534e\u9053\uff1a\u201c\u4e14\u6162\u52a8\u624b\u3002\u518d\u770b\u4e00 \u4f1a\u3002\u201d\u4e24\u4eba\u5728\u5c71\u5cf0\u95f4\u4e13\u7ed5\u5c0f\u8def\uff0c\u80cc\u5f8c\u8ddf\u8e2a\u7684\u4eba\u5ffd\u5feb\u5ffd\u6162\uff0c\u500f\u75be\u500f\u5f90\uff0c\u8f6c\u773c\u95f4\u53c8\u8d70\u4e86\u53c1\u56db\u91cc \u5730\uff0c\u90a3\u4e24\u4eba\u4ecd\u662f\u7d27\u7d27\u8ddf\u5728\u5f8c\u9762\u3002\u674e\u5c01\u6012\u9053\uff1a\u201c\u7ed9\u4ed6\u4eec\u4e00\u70b9\u989c\u8272\u77a7\u77a7\uff01\u201d\u500f\u7136\u6b62\u6b65\u3002 \u3000\u3000\u90a3\u4e24\u4eba\u8eab\u5f62\u597d\u5feb\uff0c\u674e\u5c01\u521a\u4e00\u505c\u6b65\uff0c\u53ea\u89c9\u8eab\u65c1\u98d5\u7684\u4e00\u80a1\u75be\u98ce\u8fc7\u53bb\uff0c\u5fd9\u7f29\u8eab\u65f6\uff0c\u90a3\u4e24\u4e2a\u4eba\u5df2 \u8d8a\u8fc7\u4e86\u5934\u3002\u90a3\u4e2d\u5e74\u6c49\u5b50\u56de\u8eab\u95ee\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4eec\u53bb\u4ec0\u9ebd\u5730\u65b9\uff1f\u201d\u674e\u5c01\u6012\u9053\uff1a\u201c\u4f60\u8ddf\u7740\u6211\u4eec\uff0c\u610f \u6b32\u4f55\u4e3a\uff01\u201d\u90a3\u6c49\u5b50\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u8def\uff0c\u4f60\u8d70\u5f97\u96be\u9053\u6211\u8d70\u4e0d\u5f97\uff1f\u5e74\u8f7b\u4f19\u5b50\uff0c\u706b\u6c14\u600e\u9ebd\u8fd9\u6837 \u5927\uff1f\u201d\u8fc8\u524d\u4e00\u6b65\uff0c\u4f38\u624b\u6765\u62cd\u674e\u5c01\u7684\u80a9\u818a\uff0c\u674e\u5c01\u53cc\u81c2\u4e00\u632f\uff0c\u559d\u9053\uff1a\u201c\u53bb\uff01\u201d\u4e0d\u6599\u521a\u521a\u89e6\u7740\u5bf9\u65b9 \u7684\u8eab\u4f53\uff0c\u5c31\u7ed9\u4e00\u80a1\u5927\u529b\u53cd\u5f39\u56de\u6765\u3002\u674e\u5c01\u5927\u6012\uff0c\u62d4\u51fa\u4f69\u5251\u3002\u4f55\u843c\u534e\u6025\u9053\uff1a\u201c\u4e0d\u8981\u52a8\u624b\u3002\u201d\u95ee \u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u53bb\u4ec0\u9ebd\u5730\u65b9\uff1f\u201d\u90a3\u6c49\u5b50\u9053\uff1a\u201c\u6211\u4eec\u6b63\u8981\u95ee\u4f60\uff01\u201d  \u6b63\u662f\uff1a\u897f\u5c71\u602a\u5ba2\u7a81\u5982\u6765\uff0c \u4f3c\u66fe\u76f8\u8bc6\u8d39\u7591\u731c\u3002 \u3000\u3000\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377\u2014\u2014\u7b2c\u5341\u516d\u56de\u3000\u7236\u5b50\u559c\u76f8\u9022\u3000\u6307\u6325\u89e3\u7532\u3000\u5fe0\u5978\u96be\u5e76\u7acb\u3000\u7ecf\u7565\u5f52\u519c \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0a\u5377 \u7b2c\u5341\u516d\u56de \u7236\u5b50\u559c\u76f8\u9022\u3000\u6307\u6325\u89e3\u7532 \u5fe0\u5978\u96be\u5e76\u7acb\u3000\u7ecf\u7565\u5f52\u519c \u3000\u3000\u674e\u5c01\u6a2a\u5251\u6012\u89c6\u3002\u4f55\u843c\u534e\u5927\u5927\u65b9\u65b9\u7b54\u9053\uff1a\u201c\u6211\u4eec\u4e0a\u79d8\u9b54\u5d16\uff0c\u4f60\u4eec\u5462\uff1f\u201d\u5f53\u4f55\u843c\u534e\u4e0e\u90a3\u4e2d\u5e74 \u6c49\u5b50\u8bf4\u8bdd\u65f6\uff0c\u90a3\u5c11\u5e74\u4eba\u4e00\u76f4\u51dd\u89c4\u7740\u5979\uff0c\u8fd9\u65f6\u7a81\u7136\u53eb\u8d77\u6765\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u843c\u534e\u59b9\u59b9\u5417\uff1f\u201d\u4f55\u843c\u534e \u60f3\u4e86\u8d77\u6765\uff0c\u6b22\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u7533\u65f6\u54e5\u54e5\uff1f\u201d\u90a3\u5c11\u5e74\u9ad8\u5174\u5f97\u8df3\u4e86\u8d77\u6765\uff0c\u5fd8\u5f62\u5730\u62c9\u7740\u4e86\u843c\u534e\u7684 \u624b\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u957f\u5f97\u8fd9\u9ebd\u9ad8\u4e86\uff1f\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u4f60\u8fd8\u8bf4\u5462\uff1f\u4ee5\u524d\u4f60\u548c\u6211\u4e00\u6837\u9ad8\uff0c\u73b0\u5728\u4f60 \u957f\u5f97\u6bd4\u6211\u9ad8\u534a\u4e2a\u5934\u4e86\u3002\u201d\u4e2d\u5e74\u6c49\u5b50\u54c8\u54c8\u5927\u7b11\uff0c\u90a3\u5c11\u5e74\u731b\u7136\u9192\u8d85\u73b0\u5728\u5df2\u662f\u201c\u5927\u4eba\u201d\uff0c\u6025\u201c\u677e \u624b\u3002\u674e\u5c01\u63d2\u5251\u5f52\u9798\uff0c\u9053\uff1a\u201c\u54c8\uff0c\u539f\u6765\u4f60\u4eec\u662f\u8ba4\u8bc6\u7684\uff1f\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u5c82\u6b62\u8ba4\u8bc6\uff0c\u6211\u4eec\u662f\u81ea\u5c0f \u73a9\u5927\u7684\uff0c\u4ed6\u662f\u6211\u7684\u8868\u54e5\u5462\uff1f\u201d \u3000\u3000\u8fd9\u5c11\u5e74\u540d\u53eb\u674e\u7533\u65f6\uff0c\u4e43\u662f\u767d\u77f3\u9053\u4eba\u7684\u59b9\u59b9\u4f55\u7eee\u971e\u5728\u672a\u524a\u53d1\u4e3a\u5c3c\u4e4b\u524d\uff0c\u548c\u674e\u5929\u626c\u751f\u4e0b\u7684\u513f \u5b50\u3002\u674e\u5929\u626c\u8d2a\u56fe\u5bcc\u8d35\uff0c\u4f11\u59bb\u518d\u5a36\u4e4b\u5f8c\uff0c\u4f55\u7eee\u971e\u5230\u592a\u5ba4\u5c71\u505a\u4e86\u5c3c\u59d1\uff0c\u767d\u77f3\u9053\u4eba\u5c06\u4e24\u4e2a\u5973\u513f\u4ea4\u5979 \u629a\u517b\uff0c\u6539\u79f0\u6148\u6167\uff0c\u674e\u7533\u65f6\u548c\u4f55\u843c\u534e\u540c\u5e74\uff0c\u771f\u7b97\u5f97\u662f\u9752\u6885\u7af9\u9a6c\u4e4b\u4ea4\u3002 \u3000\u3000\u6148\u6167\u5e08\u592a\u56e0\u4e3a\u66fe\u906d\u5a5a\u53d8\uff0c\u5bf9\u8fd9\u552f\u4e00\u7684\u513f\u5b50\uff0c\u81ea\u4e0d\u514d\u6709\u70b9\u5ba0\u7231\u903e\u4efd\uff0c\u6240\u4ee5\u5728\u7ae5\u5e74\u65f6\u5019\uff0c\u674e \u7533\u65f6\u548c\u4f55\u843c\u534e\u4e00\u540c\u4e60\u6b66\uff0c\u674e\u7533\u65f6\u7684\u8fdb\u5883\u603b\u843d\u5728\u4f55\u843c\u534e\u4e4b\u5f8c\uff0c\u6148\u6167\u5e08\u592a\u609f\u51fa\u4e86\u53e4\u4eba\u6613\u5b50\u800c\u6559\u7684 \u9053\u7406\uff0c\u5f53\u674e\u7533\u65f6\u5341\u4e8c\u5c81\u90a3\u5e74\uff0c\u4fbf\u628a\u4ed6\u9001\u4e0e\u81ea\u5df1\u7684\u597d\u53cb\u9f99\u5578\u4e91\u4e3a\u5f92\u3002\u8fd9\u9f99\u5578\u4e91\u662f\u5ce8\u5d4b\u6d3e\u7684\u4eba\u5ba4 \u5f1f\u5b50\uff0c\u5eff\u9980\u5e74\u524d\u66fe\u548c\u674e\u5929\u626c\u4e00\u540c\u5411\u4f55\u7eee\u971e\u6c42\u5a5a\u7684\uff0c\u843d\u9009\u4e4b\u5f8c\uff0c\u8fdc\u8d70\u4ed6\u65b9\uff0c\u76f4\u5230\u4f55\u7eee\u971e\u505a\u4e86\u5c3c \u59d1\uff0c\u624d\u53c8\u5230\u592a\u5ba4\u5c71\u6765\u627e\u5979\u3002\u6240\u4ee5\u6148\u6167\u5e08\u592a\u628a\u513f\u5b50\u6258\u4ed8\u7ed9\u4ed6\uff0c\u5176\u4e2d\u8fd8\u6709\u6df1\u610f\u3002\u5f53\u65f6\u6148\u6167\u5e08\u592a\u5bf9 \u4ed6\u8bf4\u9053\uff1a\u201c\u5f85\u6211\u7684\u513f\u5b50\u5b66\u6210\u4e4b\u5f8c\uff0c\u4f60\u518d\u5e26\u4ed6\u56de\u6765\u89c1\u6211\u5427\u3002\u201d\u9f99\u5578\u4e91\u4e00\u53e3\u7b54\u5e94\uff0c\u628a\u674e\u7533\u65f6\u5e26\u4e0a \u5ce8\u5d4b\uff0c\u82e6\u5fc3\u6559\u4e86\u4e03\u8f7d\uff0c\u8fd9\u4e03\u5e74\u95f4\u867d\u7136\u6258\u4eba\u62a5\u8fc7\u6d88\u606f\uff0c\u53ef\u662f\u4ed6\u548c\u6148\u6167\u5e08\u592a\u5374\u6ca1\u6709\u518d\u89c1\u8fc7\u9762\u3002 \u3000\u3000\u4f55\u843c\u534e\u548c\u674e\u7533\u65f6\u8fd9\u5bf9\u5b69\u5b50\uff0c\u9752\u6885\u7af9\u9a6c\uff0c\u4e24\u5c0f\u65e0\u731c\uff0c\u672c\u6765\u751a\u4e3a\u767b\u5bf9\u3002\u6148\u6167\u5e08\u592a\u4e5f\u6709\u610f\u5f85\u513f \u5b50\u5b66\u6210\u4e4b\u5f8c\uff0c\u5c31\u548c\u54e5\u54e5\u63d0\u51fa\u5a5a\u4e8b\u3002\u65e0\u5948\u767d\u77f3\u9053\u4eba\u53e6\u6709\u60f3\u5934\uff0c\u674e\u7533\u65f6\u5e7c\u5e74\u4e60\u6b66\u65f6\u8fdb\u5ea6\u8fdf\u6162\uff0c\u770b \u6765\u4e0d\u662f\u806a\u660e\u7684\u5b69\u5b50\u3002\u800c\u5353\u4e00\u822a\u5219\u5728\u6b66\u5f53\u7b2c\u4e8c\u8f88\u4e2d\u9996\u5c48\u4e00\u6307\u3002\u800c\u4e14\u5353\u4e00\u822a\u662f\u4e16\u5bb6\u516c\u5b50\uff0c\u4eba\u54c1\u6c14 \u5ea6\uff0c\u5747\u5c5e\u4e0d\u51e1\uff0c\u6587\u6b66\u5168\u624d\uff0c\u66f4\u4e3a\u96be\u5f97\u3002\u9664\u4e86\u8fd9\u4e9b\u672c\u8eab\u7684\u4f18\u8d8a\u6761\u4ef6\u4e4b\u5916\uff0c\u7d2b\u9633\u9053\u957f\u53c8\u6307\u5b9a\u4ed6\u505a \u7ee7\u627f\u4eba\uff0c\u662f\u6b66\u5f53\u6d3e\u672a\u6765\u7684\u638c\u95e8\uff0c\u8981\u77e5\u6b66\u5f53\u6d3e\u5728\u5f53\u65f6\u58f0\u5a01\u6700\u76db\uff0c\u82e5\u505a\u4e86\u6b66\u5f53\u7684\u638c\u95e8\uff0c\u5c31\u7b49\u65bc\u662f \u6b66\u6797\u4e2d\u516c\u8ba4\u7684\u9886\u8896\u3002\u767d\u77f3\u9053\u4eba\u8981\u66ff\u7231\u5973\u9009\u62e9\u4f73\u5a7f\uff0c\u81ea\u81ea\u7136\u7136\u7684\u5c31\u60f3\u8d77\u4e86\u5353\u4e00\u822a\uff0c\u4e5f\u4e0d\u7ba1\u4e24\u4eba \u662f\u5426\u6027\u60c5\u76f8\u6ca1\uff0c\u4fbf\u786c\u62c9\u4e24\u4eba\u63a5\u8fd1\u3002\u4ee5\u81f4\u751f\u51fa\u4e86\u8bb8\u591a\u4e8b\u7aef\u3002 \u3000\u3000\u518d\u8bf4\u4f55\u843c\u534e\u4e0e\u674e\u7533\u65f6\u76f8\u89c1\u4e4b\u65f6\uff0c\u5341\u5206\u9ad8\u5174\uff0c\u8c08\u4e86\u4e00\u9635\uff0c\u624d\u8bb0\u8d77\u90a3\u4e2d\u5e74\u6c49\u5b50\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d \u524d\u8f88\uff0c\u8fd8\u672a\u8bf7\u6559\u3002\u201d\u9f99\u5578\u4e91\u54c8\u54c8\u5927\u7b11\uff0c\u674e\u7533\u65f6\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u5e08\u7236\u3002\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u539f\u6765\u662f \u9f99\u4f2f\u4f2f\u3002\u8bf7\u6055\u4f84\u5973\u8bb0\u6027\u592a\u5dee\u3002\u201d\u9f99\u5578\u4e91\u9053\uff1a\u201c\u4e03\u5e74\u524d\u6211\u89c1\u4f60\u59d1\u59d1\u4e4b\u65f6\uff0c\u4f60\u8fd8\u662f\u4e2a\u5b69\u5b50\u5462\u3002\u96be \u602a\u4f60\u8bb0\u4e0d\u8d77\u4e86\u3002\u201d\u8bf4\u8d77\u4f55\u843c\u534e\u7684\u59d1\u59d1\uff0c\u9f99\u5578\u4e91\u4e0d\u89c9\u9eef\u7136\uff01 \u3000\u3000\u4f55\u843c\u534e\u9053\uff1a\u201c\u59d1\u59d1\u5e38\u5e38\u8bf4\u8d77\u4f60\u4eec\u3002\u201d\u9f99\u5578\u4e91\u9053\uff1a\u201c\u4f60\u59d1\u59d1\u597d\uff1f\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u597d\u3002\u201d\u89c1 \u4ed6\u6006\u7136\u795e\u4f24\uff0c\u5373\u628a\u8bdd\u5934\u62c9\u5f00\u3002\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u8981\u53bb\u90a3\u91cc\uff1f\u201d\u674e\u7533\u65f6\u9053\uff1a\u201c\u548c\u4f60\u4eec\u4e00\u6837\uff0c\u4e5f\u662f\u79d8 \u9b54\u5d16\u3002\u201d\u9f99\u5578\u4e91\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u7239\u7239\u8981\u548c\u7389\u7f57\u5239\u6bd4\u5251\uff0c\u6240\u4ee5\u6211\u4eec\u5c31\u8d76\u6765\u4e86\u3002\u201d\u674e\u7533\u65f6\u9053\uff1a\u201c\u6211 \u4eec\u662f\u524d\u4e24\u5929\u6765\u7684\uff0c\u51c6\u5907\u6e38\u89c8\u51e0\u5929\uff0c\u5c31\u5230\u592a\u5ba4\u5c71\u53bb\u627e\u4f60\u4eec\u3002\u6628\u5929\u9f99\u4f2f\u4f2f\u78b0\u5230\u4e00\u4f4d\u6b66\u6797\u670b\u53cb\uff0c\u662f \u957f\u5b89\u9556\u5c40\u7684\u4e00\u4e2a\u9556\u5934\uff0c\u8bf4\u8d77\u8205\u8205\u548c\u4f60\u8fd8\u6709\u4e00\u4e2a\u53eb\u505a\u4ec0\u9ebd\u5353\u4e00\u822a\u7684\uff0c\u90fd\u4ece\u4eac\u4e2d\u6765\u4e86\u3002\u8fd8\u8bf4\u8205\u8205 \u7ea6\u597d\u4e86\u4e00\u4e2a\u5973\u9b54\u5934\u53eb\u7389\u7f57\u5239\u7684\u4eca\u5929\u7684\u4e2d\u5348\u5728\u79d8\u9b54\u5d16\u6bd4\u5251\uff0c\u6211\u731c\u60f3\u4f60\u4e00\u5b9a\u4f1a\u6765\uff0c\u679c\u7136\u78b0\u5230\u4e86 \u4f60\u3002\u8fd9\u4f4d\u662f\u5353\u5144\u5417\uff1f\u201d\u674e\u7533\u65f6\u8bf4\u8d77\u201c\u5353\u4e00\u822a\u201d\u65f6\uff0c\u5fc3\u91cc\u9178\u6e9c\u6e9c\u7684\uff0c\u4e00\u65f6\u8bf4\u6f0f\u4e86\u5634\uff0c\u79f0\u4e4b\u4e3a \u201c\u90a3\u4e2a\u4ec0\u9ebd\u5353\u4e00\u822a\u201d\uff0c\u8bf4\u4e86\u4e4b\u5f8c\uff0c\u624d\u89c9\u5927\u4e3a\u4e0d\u656c\uff0c\u4ed6\u8bef\u4f1a\u674e\u5c01\u5c31\u662f\u201c\u90a3\u4e2a\u4ec0\u9ebd\u5353\u4e00\u822a\u201d\uff0c\u8138 \u4e0a\u53d1\u70e7\uff0c\u751a\u4e3a\u5c34\u5c2c\uff0c\u6025\u5fd9\u8bf7\u6559\u3002\u4f55\u843c\u534e\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6211\u7684\u5e08\u5144\u674e\u5c01\uff0c\u5317\u4eac\u6b66\u5f53\u6d3e\u7684\u638c \u95e8\u5927\u5f1f\u5b50\u3002\u201d\u674e\u7533\u65f6\u8fd9\u624d\u653e\u4e0b\u4e86\u5fc3\u3002 \u3000\u3000\u4e00\u884c\u56db\u4f17\uff0c\u8c08\u8c08\u7b11\u7b11\uff0c\u4ece\u7fe0\u5fae\u5c71\u6298\u4e0b\uff0c\u674e\u5c01\u9053\uff1a\u201c\u518d\u8fc7\u53bb\u5c31\u662f\u5362\u5e08\u5c71\u4e86\u3002\u79d8\u9b54\u5d16\u5c31\u5728\u5362 \u5e08\u5c71\u4e0a\u3002\u201d\u9f99\u5578\u4e91\u62ac\u5934\u4e00\u671b\uff0c\u65e5\u5df2\u5f53\u4e2d\uff0c\u609a\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u65f6\u5019\uff0c\u4ed6\u4eec\u60f3\u6765\u5df2\u5f00\u59cb\u6bd4\u5251 \u4e86\u3002\u201d\u674e\u7533\u65f6\u9053\uff1a\u201c\u90a3\u7389\u7f57\u5239\u662f\u4f55\u7b49\u4eba\u7269\uff01\u96be\u9053\u5979\u7684\u5251\u6cd5\u8fd8\u80fd\u80dc\u8fc7\u6211\u7684\u8205\u8205\u4e0d\u6210\uff01\u201d\u9f99\u5578\u4e91 \u9053\uff1a\u201c\u542c\u8bf4\u53ea\u662f\u5eff\u5c81\u5de6\u53f3\u7684\u5c11\u5973\uff0c\u5251\u6cd5\u51f6\u72e0\u7edd\u4f26\uff0c\u6211\u5374\u6ca1\u6709\u89c1\u8fc7\u3002\u201d\u4f55\u843c\u534e\u7b11\u9053\uff1a\u201c\u5353\u5e08\u5144 \u5012\u548c\u5979\u5f88\u719f\u3002\u6240\u4ee5\u6211\u7684\u7236\u4eb2\u4e0d\u8bb8\u6211\u53bb\uff0c\u5374\u8981\u62c9\u4ed6\u540c\u53bb\u3002\u201d \u3000\u3000\u518d\u8d70\u4e00\u9635\uff0c\u524d\u9762\u5947\u5cf0\u7a81\u8d77\uff0c\u5982\u864e\u5982\u72ee\uff0c\u56db\u4eba\u8d70\u5165\u5c71\u8c37\uff0c\u674e\u5c01\u6307\u7740\u524d\u9762\u4e00\u4e2a\u5f62\u5982\u72ee\u5b50\u7684\u5c71 \u5cf0\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u79d8\u9b54\u5d16\u4e86\u3002\u4f60\u770b\u8fd9\u5c71\u8702\u4e0b\u9762\u6709\u4e00\u5757\u5e73\u5730\uff0c\u5c31\u50cf\u5f20\u5f00\u4e86\u53e3\u7684\u72ee\u5634\u4e00\u6837\uff0c\u4ed6\u4eec \u5fc5\u7136\u662f\u5728\u90a3\u91cc\u6bd4\u5251\u3002\u201d\u8bdd\u58f0\u65b9\u505c\uff0c\u5c71\u5408\u7684\u4e71\u77f3\u5806\u4e2d\uff0c\u7a81\u7136\u8df3\u51fa\u56db\u4eba\uff0c\u559d\u9053\uff1a\u201c\u8c01\u8981\u5230\u79d8\u9b54\u5d16 \u53bb\uff01\u201d\u4f55\u843c\u534e\u5ffd\u7136\u201c\u54d7\u201d\u7684\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e3a\u9996\u90a3\u4eba\u7ea6\u83ab\u56db\u5341\u591a\u5c81\u5e74\u7eaa\uff0c\u76f8\u8c8c\u9887\u4e3a\u5a01\u6b66\uff0c\u7adf\u7136\u5c31\u662f\u90a3\u5e74\u4e0a\u592a\u5ba4\u5c71\u627e\u5979\u59d1\u59d1\u7684\u4eba\u3002\u4f55 \u843c\u534e\u5f8c\u6765\u624d\u77e5\u9053\u8fd9\u4eba\u4fbf\u662f\u59d1\u59d1\u7684\u524d\u592b\uff0c\u4eac\u4e2d\u9526\u8863\u536b\u7684\u6307\u6325\u674e\u5929\u626c\u3002 \u3000\u3000\u674e\u5929\u626c\u6014\u4e86\u4e00\u6014\uff0c\u9f99\u5578\u4e91\u5df2\u51b7\u7136\u53d1\u8bdd\uff1a\u201c\u674e\u5927\u4eba\uff0c\u4f60\u8d35\u4eba\u4e8b\u5fd9\uff0c\u8fde\u6211\u4eec\u5230\u79d8\u9b54\u5d16\u4f60\u4e5f\u8981 \u7ba1\u9ebd\uff1f\u201d\u674e\u5929\u626c\u9053\uff1a\u201c\u9f99\u5144\uff0c\u54b1\u4eec\u4e00\u522b\u5eff\u5e74\uff0c\u6211\u5c61\u6b21\u6253\u542c\u4f60\u7684\u6d88\u606f\u90fd\u6253\u542c\u4e0d\u5230\uff0c\u5b9e\u5728\u6302\u5ff5\u5f97 \u5f88\u3002\u201d\u9f99\u5578\u4e91\u4ef0\u5929\u6253\u4e86\u4e00\u4e2a\u54c8\u54c8\uff0c\u9053\uff1a\u201c\u5c71\u91ce\u4e4b\u4eba\uff0c\u7adf\u52b3\u674e\u5927\u4eba\u6302\u5ff5\uff0c\u771f\u662f\u7f6a\u8be5\u4e07\u6b7b\uff01\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u4e24\u8fb9\u5c71\u5761\u4e0a\u57cb\u4f0f\u7684\u4e1c\u897f\u5382\u536b\u58eb\uff0c\u7eb7\u7eb7\u6d8c\u51fa\u3002\u539f\u6765\u91d1\u72ec\u5f02\u5506\u4f7f\u4ed6\u7684\u5a46\u5a18\u5728\u79d8\u9b54 \u5d16\u7ea6\u6597\u94c1\u98de\u9f99\u4e0e\u7389\u7f57\u5239\u4e8c\u4eba\uff0c\u672c\u60f3\u7ea6\u4eba\u5230\u73b0\u573a\u52a9\u6218\uff0c\u53ef\u662f\u7ea2\u82b1\u9b3c\u6bcd\u7684\u813e\u6c14\u602a\u50fb\uff0c\u58f0\u660e\u82e5\u6709\u4eba \u52a9\u6218\uff0c\u5979\u5c31\u9000\u51fa\u4e0d\u7ba1\u3002\u6240\u4ee5\u91d1\u72ec\u5f02\u4e0d\u6562\u5230\u79d8\u9b54\u5d16\u53bb\u3002\u53ef\u662f\u4ed6\u60a3\u5f97\u60a3\u5931\uff0c\u4e00\u65b9\u9762\u76f8\u4fe1\u4ed6\u59bb\u5b50\u7684 \u6b66\u529f\u8fdc\u5728\u94c1\u98de\u9f99\u4e0e\u7389\u7f57\u5239\u4e4b\u4e0a\uff1a\u4f46\u53c8\u6015\u5979\u72ec\u529b\u514b\u5236\u4e0d\u4f4f\uff0c\u4f1a\u8ba9\u654c\u4eba\u9003\u8131\uff0c\u65bc\u662f\u4fbf\u548c\u6155\u5bb9\u51b2\u5546 \u91cf\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u662f\u4e1c\u5382\u536b\u58eb\u603b\u7ba1\uff0c\u6b63\u662f\u9b4f\u5fe0\u8d24\u7684\u6b7b\u515a\u3002\u4ed6\u542c\u4e86\u91d1\u72ec\u5f02\u7684\u8bdd\u4e4b\u5f8c\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u8bf4 \u9053\uff1a\u201c\u4f60\u7684\u8d24\u5185\u52a9\u80af\u51fa\u5c71\u5e2e\u5fd9\uff0c\u90a3\u81ea\u7136\u662f\u6700\u597d\u4e0d\u8fc7\u3002\u53ef\u662f\u90a3\u7389\u7f57\u5239\u548c\u94c1\u98de\u9f99\u660e\u660e\u662f\u718a\u5ef7\u5f3c\u7684 \u4e00\u515a\u3002\u90a3\u65e5\u6211\u4eec\u5728\u6768\u6d9f\u5bb6\u4e2d\u5403\u4e86\u5927\u4e8f\uff0c\u8001\u5144\u96be\u9053\u5fd8\u8bb0\u4e86\u5417\uff1f\u201d\u91d1\u72ec\u5f02\u9053\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u6b66\u6797\u4e2d \u7684\u6210\u540d\u4eba\u7269\uff0c\u53cc\u65b9\u7ea6\u6597\uff0c\u4e0d\u8bb8\u7b2c\u53c1\u8005\u63d2\u8db3\uff0c\u96be\u9053\u718a\u86ee\u5b50\u4ee5\u8fb9\u5173\u7edf\u5e05\u7684\u8eab\u4efd\uff0c\u8fd8\u4f1a\u51fa\u573a\u52a9\u6218\u4e0d \u6210\u3002\u201d\u6155\u5bb9\u51b2\u51b7\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u8fd9\u6837\u5fe0\u539a\uff1f\u718a\u86ee\u5b50\u5f53\u7136\u4e0d\u4f1a\u6765\uff0c\u4f46\u94c1\u98de\u9f99\u7389\u7f57\u5239\u65e2\u7136 \u662f\u718a\u5ef7\u5f3c\u7684\u515a\u7fbd\uff0c\u4ed6\u4eec\u7684\u540c\u515a\u591a\u7740\u5462\u3002\u8c01\u6562\u62c5\u4fdd\u94c1\u98de\u9f99\u4e0d\u6697\u4e2d\u7ea6\u4eba\u52a9\u62f3\uff1f\u201d\u91d1\u72ec\u5f02\u9053\uff1a\u201c\u4f9d \u4f60\u8bf4\u600e\u9ebd\u6837\uff0c\u6211\u90a3\u81ed\u5a46\u5a18\u813e\u6c14\u53e4\u602a\uff0c\u6211\u4eec\u82e5\u53bb\u52a9\u62f3\uff0c\u5979\u771f\u4f1a\u6492\u624b\u4e0d\u7ba1\u3002\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u718a\u5ef7 \u5f3c\u7684\u515a\u7fbd\u4e2d\u4ee5\u94c1\u98de\u9f99\u7389\u7f57\u5239\u6700\u4e3a\u51f6\u72e0\u5389\u5bb3\uff0c\u6709\u4f60\u7684\u5a46\u5a18\u5bf9\u4ed8\u4ed6\u4eec\uff0c\u5176\u9980\u7684\u5c31\u597d\u529e\u4e86\u3002\u6211\u4eec\u591a \u7ea6\u597d\u624b\uff0c\u5728\u79d8\u9b54\u5d16\u9644\u8fd1\u57cb\u4f0f\u3002\u6211\u6599\u90a3\u94c1\u98de\u9f99\u548c\u7389\u7f57\u5239\u4e0d\u662f\u4f60\u5a46\u5a18\u7684\u5bf9\u624b\uff0c\u53ef\u662f\u4ed6\u4eec\u4ee5\u4e8c\u654c \u4e00\uff0c\u867d\u4e0d\u80fd\u80dc\uff0c\u8981\u9003\u8d70\u6599\u8fd8\u53ef\u4ee5\u3002\u54b1\u4eec\u5728\u5916\u9762\u57cb\u4f0f\uff0c\u5f85\u4ed6\u4eec\u9003\u51fa\u6765\u65f6\uff0c\u5c31\u5c06\u4ed6\u4eec\u6d3b\u6349\u3002\u90a3\u65f6 \u4ed6\u4eec\u5df2\u6253\u5f97\u7b4b\u75b2\u529b\u7aed\uff0c\u4f60\u7684\u5a46\u5a18\u6492\u624b\u4e0d\u7406\uff0c\u54b1\u4eec\u4e5f\u80fd\u5bf9\u4ed8\u5f97\u4e86\u3002\u6b64\u5176\u4e00\u3002\u201d\u91d1\u72ec\u5f02\u7b11\u7740\u63a5 \u9053\uff1a\u201c\u82e5\u4ed6\u4eec\u6709\u515a\u7fbd\u6765\u52a9\u6218\uff0c\u54b1\u4eec\u6697\u4e2d\u57cb\u4f0f\uff0c\u4e5f\u53ef\u4e00\u7f51\u6210\u64d2\u3002\u6b64\u5176\u4e8c\u3002\u662f\u4e0d\u662f\uff1f\u201d\u5176\u5b9e\u91d1\u72ec \u5f02\u6df1\u77e5\u94c1\u98de\u9f99\u813e\u6c14\uff0c\u6599\u4ed6\u4e0d\u4f1a\u7ea6\u4eba\u52a9\u62f3\uff0c\u6240\u4ee5\u8fd9\u6837\u8bf4\u6cd5\uff0c\u4e00\u65b9\u9762\u662f\u987a\u7740\u6155\u5bb9\u51b2\u7684\u53e3\u6c14\uff0c\u53e6\u65b9 \u9762\u91d1\u72ec\u5f02\u5f88\u6068\u94c1\u98de\u9f99\u7389\u7f57\u5239\uff0c\u7167\u6155\u5bb9\u51b2\u7684\u8ba1\u753b\uff0c\u5bf9\u4ed6\u4e5f\u6781\u6709\u5229\u3002\u6155\u5bb9\u51b2\u6b63\u5728\u5f53\u6743\u5f97\u4ee4\uff0c\u4ee5\u5c0f \u4eba\u4e4b\u5fc3\u5ea6\u541b\u5b50\u4e4b\u8179\uff0c\u53c8\u56e0\u5728\u6768\u6d9f\u5bb6\u4e2d\u5403\u4e86\u5927\u4e8f\uff0c\u8bef\u4f1a\u94c1\u98de\u9f99\u548c\u738b\u7f57\u5239\u662f\u718a\u5ef7\u5f3c\u515a\u7fbd\uff0c\u6240\u4ee5\u4e00 \u5fc3\u8981\u66ff\u9b4f\u5b97\u4e3b\u201c\u5fe0\u8d24\u201d\u9664\u6b64\u5fc3\u8179\u5927\u60a3\u3002 \u3000\u3000\u91d1\u72ec\u5f02\u53c8\u9053\uff1a\u201c\u82e5\u6709\u6b66\u5f53\u6d3e\u7684\u4eba\u7275\u8fde\u8fdb\u6765\uff0c\u90a3\u53c8\u5982\u4f55\uff1f\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u4e0a\u6b21\u6211\u4eec\u529f\u8d25\u5782 \u6210\uff0c\u9664\u4e86\u94c1\u98de\u9f99\u7389\u7f57\u5239\u4e0e\u6211\u4eec\u4f5c\u5bf9\u4e4b\u5916\uff0c\u767d\u77f3\u90a3\u8d3c\u9053\u7387\u9886\u4e00\u5927\u7fa4\u6b66\u5f53\u5f1f\u5b50\u524d\u6765\u52a9\u6218\uff0c\u66f4\u662f\u6211 \u4eec\u81f4\u8d25\u4e4b\u7531\u3002\u6b66\u5f53\u6d3e\u867d\u662f\u6b66\u6797\u6b63\u5b97\uff0c\u4ea4\u6e38\u5e7f\u9614\uff0c\u4f46\u4ed6\u4eec\u82e5\u4e0d\u77e5\u597d\u574f\uff0c\u6211\u4eec\u4e5f\u5c31\u7ba1\u4e0d\u5f97\u8fd9\u9ebd\u591a \u4e86\u3002\u603b\u4e4b\u662f\u6765\u4e00\u4e2a\u6349\u4e00\u4e2a\u3002\u201d\u505c\u4e86\u4e00\u505c\u53c8\u9053\uff1a\u201c\u8fd9\u6b21\u6211\u4eec\u518d\u7ea6\u51e0\u4e2a\u597d\u624b\u53bb\u3002\u9526\u8863\u536b\u7684\u6307\u6325\u674e \u5929\u626c\u3001\u77f3\u6d69\uff0c\u897f\u5382\u7684\u603b\u7ba1\u8fde\u57ce\u864e\u7b49\u90fd\u53ef\u4ee5\u8bf7\u53bb\u3002\u201d\u660e\u4ee3\u7684\u7279\u52a1\u673a\u6784\u5206\u4e1c\u5382\u3001\u897f\u5382\u548c\u9526\u8863\u536b\u53c1 \u4e2a\u673a\u6784\uff0c\u5404\u6210\u7cfb\u7edf\u3002\u795e\u5b97\u665a\u5e74\uff0c\u56e0\u4e3a\u9b4f\u5fe0\u8d24\u638c\u7ba1\u4e1c\u5382\uff0c\u6240\u4ee5\u4e1c\u5382\u52bf\u529b\u6700\u5927\u3002\u6155\u5bb9\u51b2\u51fa\u9762\u53bb\u9080 \u674e\u5929\u626c\u7b49\u4eba\uff0c\u4ed6\u4eec\u4e3a\u4e86\u8981\u5df4\u7ed3\u9b4f\u5fe0\u8d24\uff0c\u81ea\u7136\u4e00\u4e00\u7b54\u5e94\u3002 \u3000\u3000\u4e66\u63a5\u524d\u6587\u3002\u4e14\u8bf4\u674e\u5929\u626c\u6b63\u4e0e\u9f99\u5578\u4e91\u6253\u8bdd\u4e4b\u9645\uff0c\u6155\u5bb9\u51b2\u4e0e\u91d1\u72ec\u5f02\u7387\u4f17\u6740\u6765\u3002\u6155\u5bb9\u51b2\u5927\u53eb \u9053\uff1a\u201c\u4e0d\u7ba1\u4f55\u4eba\uff0c\u51e1\u662f\u8981\u5230\u79d8\u9b54\u5d16\u7684\u90fd\u6349\u4e86\u518d\u8bf4\uff01\u201d\u674e\u5929\u626c\u5229\u7984\u5fc3\u91cd\uff0c\u76ee\u524d\u65b0\u541b\u5373\u4f4d\uff0c\u4ed6\u6b63 \u8981\u5df4\u7ed3\u9b4f\u5fe0\u8d24\u4ee5\u4fdd\u5b98\u804c\uff0c\u5f53\u4e0b\u9762\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u59d4\u5c48\u9f99\u5144\uff0c\u8bf7\u968f\u5c0f\u5f1f\u5230\u9526\u8863\u536b\u53bb\uff01\u201d\u9f99\u5578\u4e91 \u5927\u6012\uff0c\u65a5\u9053\uff1a\u201c\u597d\u4e2a\u4e0d\u77e5\u7f9e\u803b\u7684\u5974\u624d\uff0c\u7eee\u971e\u771f\u662f\u5ac1\u9519\u4e86\u4f60\u3002\u201d\u674e\u5929\u626c\u548c\u9f99\u5578\u4e91\u672c\u6709\u5acc\u9699\uff0c\u8fd9 \u65f6\u653e\u4e0b\u9762\u5b50\uff0c\u4e00\u58f0\u51b7\u7b11\uff0c\u6325\u5251\u5411\u9f99\u5578\u4e91\u523a\u53bb\uff0c\u4e24\u5251\u4e00\u4ea4\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u9707\u5f97\u864e\u53e3\u53d1\u70ed\u3002 \u3000\u3000\u9f99\u5578\u4e91\u8fd9\u9ebd\u591a\u5e74\u5728\u5ce8\u7709\u5c71\u52e4\u4fee\u82e6\u7ec3\uff0c\u6b66\u529f\u975e\u540c\u5c0f\u9119\u3002\u5eff\u5e74\u4e4b\u524d\uff0c\u674e\u5929\u626c\u6b66\u529f\u6bd4\u4ed6\u9ad8\uff0c\u800c \u4eca\u5374\u5df2\u662f\u76f8\u5f62\u89c1\u7ecc\uff0c\u77f3\u6d69\u51b2\u4e0a\u52a9\u6218\uff0c\u674e\u7533\u65f6\u62d4\u5251\u6321\u7740\u3002\u674e\u5929\u626c\u89c1\u8fd9\u5c11\u5e74\u9762\u8c8c\uff0c\u4f3c\u662f\u5728\u90a3\u513f\u89c1 \u8fc7\u4e00\u822c\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u4e00\u9635\u5bd2\u610f\u76f4\u900f\u5fc3\u5934\uff0c\u6b63\u60f3\u559d\u95ee\u662f\u8c01\uff0c\u6155\u5bb9\u51b2\u4e0e\u91d1\u72ec\u5f02\u8eab\u5f62\u8fc5\u75be\uff0c\u500f\u5ffd\u4e4b \u95f4\uff0c\u5df2\u4ece\u5c71\u5761\u4e0a\u51b2\u5230\uff01 \u3000\u3000\u674e\u5929\u626c\u4fa7\u8eab\u4e00\u5251\uff0c\u95ea\u4e86\u5f00\u53bb\uff0c\u8ba9\u6155\u5bb9\u51b2\u6765\u62ff\u654c\u4eba\u3002\u674e\u7533\u65f6\u4f55\u843c\u534e\u4e8c\u4eba\u4e5f\u5df2\u548c\u536b\u58eb\u4ea4\u4e0a\u4e86 \u624b\u3002\u674e\u5929\u626c\u5fc3\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u5b50\u662f\u767d\u77f3\u9053\u4eba\u7684\u5973\u513f\uff0c\u53ef\u4e0d\u80fd\u770b\u5979\u9001\u4e86\u6027\u547d\u3002\u201d\u53c8\u60f3\u9053\uff1a\u201c\u6211\u548c \u767d\u77f3\u9053\u4eba\u4e43\u662f\u90ce\u8205\u81f3\u4eb2\uff0c\u8fd9\u4e8b\u4e5f\u4e0d\u4fbf\u8ba9\u6155\u5bb9\u51b2\u77e5\u9053\u3002\u201d\u4f55\u843c\u534e\u5251\u6cd5\u51cc\u5389\uff0c\u5237\u5237\u4e24\u5251\uff0c\u523a\u4f24\u4e86 \u4e00\u540d\u536b\u58eb\u3002\u674e\u5929\u626c\u5927\u53eb\u9053\uff1a\u201c\u8ba9\u6211\u62ff\u5979\u3002\u201d\u6325\u5251\u76f4\u53d6\u843c\u534e\u3002\u4f55\u843c\u534e\u4e0d\u77e5\u4ed6\u7684\u7528\u610f\uff0c\u53c8\u6068\u4ed6\u4ee4 \u59d1\u59d1\u53d7\u82e6\uff0c\u4e5f\u5c31\u4e0d\u987e\u4ec0\u9ebd\u60c5\u9762\uff0c\u5251\u8bc0\u4e00\u9886\uff0c\u4e00\u62db\u201c\u7389\u5973\u6295\u68ad\u201d\uff0c\u523a\u80a9\u524a\u8155\uff0c\u53c8\u72e0\u53c8\u75be\u3002\u674e\u5929 \u626c\u731d\u51fa\u4e0d\u610f\uff0c\u51e0\u4e4e\u5403\u4e8f\u3002\u53ef\u662f\u4ed6\u7684\u6b66\u529f\u5230\u5e95\u6bd4\u4f55\u843c\u534e\u9ad8\u51fa\u8bb8\u591a\uff0c\u6a2a\u5251\u4e00\u649e\uff0c\u628a\u4f55\u843c\u534e\u5251\u52bf\u963b \u6b62\uff0c\u987a\u624b\u5c06\u5251\u4e00\u63a8\uff0c\u628a\u4f55\u843c\u534e\u9000\u51fa\u51e0\u6b65\uff0c\u8d81\u5979\u8eab\u5f62\u672a\u7a33\uff0c\u4e00\u8dc3\u800c\u524d\uff0c\u5c06\u5979\u4e00\u628a\u6293\u4e86\u8fc7\u6765\uff0c\u8fc5 \u5373\u70b9\u4e86\u5979\u7684  \u7a74\u3002\u674e\u7533\u65f6\u89c1\u72b6\u5927\u60ca\uff0c\u594b\u529b\u6740\u9000\u8eab\u524d\u536b\u58eb\uff0c\u8d76\u6765\u62a2\u6551\u3002 \u3000\u3000\u674e\u5929\u626c\u4f11\u59bb\u518d\u5a36\u4e4b\u65f6\uff0c\u674e\u7533\u65f6\u4e0d\u8fc7\u53c1\u5c81\u3002\u4f55\u7eee\u971e\u4e0d\u613f\u53d7\u4ed6\u5f8c\u6bcd\u8650\u5f85\uff0c\u79bb\u5f02\u4e4b\u5f8c\u4e24\u5e74\uff0c\u5c31 \u53eb\u54e5\u54e5\u5c06\u7525\u513f\u5e26\u51fa\uff0c\u62b1\u4e0a\u5d69\u5c71\uff0c\u4e00\u522b\u5341\u4e94\u5e74\uff0c\u7236\u5b50\u76f8\u9022\uff0c\u5404\u4e0d\u76f8\u8bc6\u3002\u53ef\u662f\u521a\u624d\u674e\u5929\u626c\u548c\u9f99\u5578 \u4e91\u9a82\u6218\u4e4b\u65f6\uff0c\u5608\u6742\u58f0\u4e2d\uff0c\u674e\u7533\u65f6\u5374\u9690\u9690\u542c\u5f97\u5e08\u7236\u8bf4\u51fa\u201c\u7eee\u971e\u201d\u4e8c\u5b57\uff0c\u5fc3\u60f3\uff1a\u600e\u9ebd\u5e08\u7236\u5bf9\u8fd9\u964c \u751f\u4eba\u9053\u6211\u6bcd\u4eb2\uff0c\u6325\u5251\u6740\u6765\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u654c\u4eba\u7adf\u548c\u81ea\u5df2\u9762\u8c8c\u76f8\u4f3c\uff0c\u5fc3\u4e2d\u4e00\u9635\u5bd2\u98a4\uff0c\u624b\u7adf\u8f6f\u4e86\u3002 \u65c1\u8fb9\u4e00\u540d\u536b\u58eb\uff0c\u7ffb\u8f6c\u5200\u80cc\uff0c\u5728\u4ed6\u5251\u4e0a\u4e00\u62cd\uff0c\u6309\u8bf4\u674e\u7533\u65f6\u6b66\u529f\u672c\u6765\u4e0d\u5f31\uff0c\u4f46\u7ed9\u8fd9\u536b\u58eb\u4e00\u62cd\uff0c\u957f \u5251\u7adf\u7136\u545b  \u5815\u5730\u3002\u674e\u5929\u626c\u5012\u8f6c\u5251\u67c4\uff0c\u5728\u4ed6\u80cc\u5fc3\u4e00\u70b9\uff0c\u53c8\u5c06\u4ed6\u64d2\u4e86\u3002\u674e\u5929\u626c\u867d\u7136\u4e0d\u77e5\u9053\u4ed6\u5c31\u662f \u81ea\u5df1\u7684\u4eb2\u751f\u513f\u5b50\uff0c\u53ef\u662f\u89c1\u4ed6\u4e0e\u9f99\u5578\u4e91\u4f55\u843c\u534e\u540c\u6765\uff0c\u4e0d\u65e0\u7591\u60d1\uff0c\u800c\u4e14\u52a8\u624b\u4e4b\u65f6\uff0c\u5fc3\u4e2d\u7a81\u7136\u8d77\u4e86 \u4e00\u79cd\u5947\u602a\u7684\u60c5\u7eea\uff0c\u6781\u4e4b\u4e0d\u613f\u4f24\u5bb3\u8fd9\u4e2a\u5c11\u5e74\uff0c\u81ea\u5df1\u4e5f\u4e0d\u660e\u4f55\u4ee5\u6709\u8fd9\u6837\u7684\u5fc3\u60c5\u3002\u6240\u4ee5\u674e\u5929\u626c\u5c06\u4ed6 \u70b9\u5012\u4e4b\u5f8c\uff0c\u7acb\u5373\u4ea4\u7ed9\u77f3\u6d69\uff0c\u53eb\u4ed6\u5e26\u56de\u9526\u8863\u536b\u6240\uff0c\u7531\u81ea\u5df1\u5904\u7406\u3002 \u3000\u3000\u518d\u8bf4\u9f99\u5578\u4e91\u4e0e\u6155\u5bb9\u51b2\u76f8\u9047\uff0c\u8fde\u523a\u53c1\u5251\uff0c\u90fd\u7ed9\u6155\u5bb9\u51b2\u907f\u5f00\uff0c\u975e\u4f46\u523a\u4e0d\u4e2d\u654c\u4eba\uff0c\u53cd\u89c9\u654c\u4eba\u62f3 \u98ce\u5288\u9762\uff0c\u5927\u5403\u4e00\u60ca\uff01\u5fc3\u9053\uff1a\u5bab\u5ef7\u4e2d\u7adf\u6709\u8fd9\u9ebd\u5389\u5bb3\u7684\u9ad8\u624b\uff01\u6155\u5bb9\u51b2\u89c1\u654c\u4eba\u5251\u62db\u8fc5\u75be\uff0c\u529f\u529b\u6df1 \u539a\uff0c\u4e5f\u7559\u4e86\u5fc3\u3002\u53cc\u62f3\u5316\u638c\uff0c\u5c55\u5f00\u4e86\u64d2\u62ff\u624b\u6cd5\uff0c\u62e6\u963b\u52fe\u62ff\uff0c\u9f99\u5578\u4e91\u89c1\u52bf\u4e0d\u4f73\uff0c\u65e0\u5fc3\u604b\u6218\uff0c\u865a\u6643 \u4e00\u5251\uff0c\u659c\u523a\u63a0\u51fa\uff0c\u4e00\u540d\u4e1c\u5382\u536b\u58eb\uff0c\u624b\u4f7f\u864e\u5934\u53cc\u5934\u53cc\u94a9\uff0c\u8fce\u9762\u75be\u7ede\uff0c\u60f3\u628a\u9f99\u5578\u4e91\u5b9d\u5251\u7ede\u4f4f\uff0c\u593a \u51fa\u624b\u53bb\uff0c\u90a3\u77e5\u9f99\u5578\u4e91\u7684\u5ce8\u7709\u5251\u6cd5\uff0c\u5df2\u5230\u4f7f\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u5728\u536b\u58eb\u5305\u56f4\u4e4b\u4e2d\uff0c\u6beb\u4e0d\u614c\u4e71\uff0c\u770b\u89c1\u53cc \u94a9\u7ede\u5230\uff0c\u5b9d\u5251\u4e00\u7ffb\u4e00\u5377\uff0c\u987f\u65f6\u628a\u90a3\u536b\u58eb\u7684\u4e94\u4e2a\u6307\u5934\uff0c\u9f50\u6839\u524a\u65ad\uff01\u5927\u559d\u4e00\u58f0\uff0c\u76f4\u51b2\u51fa\u53bb\uff01\u6155\u5bb9 \u51b2\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u4eba\u591a\u963b\u52bf\uff0c\u53cd\u800c\u4e0d\u4fbf\u65bd\u5c55\u3002\u9f99\u5578\u4e91\u8eab\u5f62\u98d8\u5ffd\u4e0d\u5b9a\uff0c\u5728\u4e71\u77f3\u5806\u4e2d\uff0c\u62da\u547d\u9003\u7a9c\u3002 \u3000\u3000\u91d1\u72ec\u5f02\u672c\u6765\u662f\u62bc\u5f8c\u7763\u6218\uff0c\u62c5\u5f53\u515c\u622a\u654c\u4eba\u7684\u4efb\u52a1\u3002\u89c1\u9f99\u5578\u4e91\u8eab\u6cd5\u8fc5\u75be\uff0c\u5728\u5c71\u8c37\u4e2d\u7a7f\u63d2\u5954 \u9003\uff0c\u5927\u4e3a\u751f\u6c14\uff0c\u8eab\u5f62\u98de\u63a0\uff0c\u62a2\u51fa\u62e6\u622a\u3002\u9f99\u5578\u4e91\u89c1\u4ed6\u52bf\u51f6\uff0c\u6389\u5934\u897f\u8d70\uff0c\u91d1\u72ec\u5f02\u53cc\u81c2\u4e00\u632f\uff0c\u628a\u4e24 \u540d\u536b\u58eb\u63a8\u5f00\uff0c\u4e00\u624b\u7167\u9f99\u5578\u4e91\u5f8c\u5fc3\u6293\u6765\uff0c\u9f99\u5578\u4e91\u53cd\u624b\u4e00\u5251\uff0c\u6ca1\u6709\u523a\u7740\uff0c\u6155\u5bb9\u51b2\u5df2\u7ecf\u8ffd\u4e0a\uff0c\u9f99\u5578 \u4e91\u4e14\u6218\u4e14\u8d70\uff0c\u8d70\u5230\u79d8\u9b54\u5d16\u4e0b\uff0c\u5230\u5e95\u654c\u4e0d\u4f4f\u4e24\u540d\u9ad8\u624b\u8ffd\u51fb\uff0c\u88ab\u6155\u5bb9\u51b2\u4e00\u638c\u6253\u7ffb\uff0c\u4e5f\u88ab\u64d2\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u94c1\u98de\u9f99\u548c\u7389\u7f57\u5239\u5df2\u5728\u5ca9\u4e0a\u73b0\u51fa\u8eab\u5f62\uff0c\u6709\u5341\u591a\u540d\u51b2\u4e0a\u53bb\u7684\u536b\u58eb\u7ed9\u94c1\u98de\u9f99\u98de\u77f3\u6253\u4f24\u3002\u6155 \u5bb9\u51b2\u559d\u4ee4\u5c06\u9f99\u5578\u4e91\u7f1a\u4e86\uff0c\u5bf9\u674e\u5929\u626c\u9053\uff1a\u201c\u4f60\u770b\u7ba1\u4fd8\u864f\uff0c\u9632\u5907\u4ed6\u4eec\u7684\u515a\u7fbd\u6765\u52ab\u3002\u6211\u4eec\u4e0a\u5ca9\u53bb \u770b\u3002\u201d\u548c\u91d1\u72ec\u5f02\u51b2\u4e0a\u5c71\u5ca9\uff0c\u5230\u4e86\u79d8\u9b54\u5d16\u4e0a\uff0c\u4f46\u89c1\u4e71\u77f3\u6ee1\u5730\uff0c\u5730\u4e0b\u6709\u70b9\u70b9\u9c9c\u8840\uff0c\u4e0d\u4f46\u94c1\u98de\u9f99\u4e0e \u7389\u7f57\u5239\u5df2\u7ecf\u4e0d\u89c1\uff0c\u8fde\u7ea2\u82b1\u9b3c\u6bcd\u4e5f\u4e0d\u89c1\u4e86\u3002\u91d1\u72ec\u5f02\u4e0d\u89c9\u5fc3\u5bd2\uff0c\u9ad8\u53eb\u51e0\u58f0\uff0c\u4e0d\u89c1\u59bb\u5b50\u56de\u5e94\u3002\u6155\u5bb9 \u51b2\u9053\uff1a\u201c\u96be\u9053\u7ed9\u4ed6\u4eec\u5bb3\u4e86\u4e0d\u6210\uff01\u201d\u91d1\u72ec\u5f02\u9053\uff1a\u201c\u7edd\u65e0\u6b64\u7406\uff01\u201d\u767b\u9ad8\u4e00\u671b\uff0c\u53ea\u89c1\u7389\u7f57\u5239\u7b49\u4eba\u5df2 \u4ece\u80cc\u9762\u4e0b\u5c71\uff0c\u53bb\u5f97\u8fdc\u4e86\u3002\u7ea2\u82b1\u9b3c\u6bcd\u7684\u8e2a\u8ff9\u4ecd\u7136\u4e0d\u89c1\u3002\u8fd9\u65f6\u91d1\u72ec\u5f02\u548c\u6155\u5bb9\u51b2\u5df3\u987e\u4e0d\u5f97\u8ffd\u8d76\u654c \u4eba\uff0c\u800c\u4e14\u5373\u7b97\u8ffd\u53ca\uff0c\u4e5f\u672a\u5fc5\u662f\u654c\u4eba\u5bf9\u624b\u3002\u4ed6\u4eec\u672c\u662f\u501a\u9760\u7ea2\u82b1\u9b3c\u6bcd\u5236\u654c\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u4e0d\u89c1\uff0c\u4ed6\u4eec \u9510\u6c14\u5df2\u632b\u3002\u5f53\u4e0b\u7ffb\u904d\u4e86\u79d8\u9b54\u5d16\uff0c\u8fd8\u662f\u4ec0\u9ebd\u4eba\u4e5f\u627e\u4e0d\u5230\u3002 \u3000\u3000\u9002\u624d\u5728\u6df7\u6218\u4e2d\uff0c\u674e\u5c01\u65e9\u5df2\u88ab\u4f17\u536b\u58eb\u64d2\u7740\u3002\u674e\u5929\u626c\u5728\u5ca9\u4e0b\u770b\u5b88\u56db\u540d\u4fd8\u864f\uff0c\u8fc7\u4e86\u8bb8\u4e45\uff0c\u624d\u89c1 \u6155\u5bb9\u51b2\u4e0e\u91d1\u72ec\u5f02\u4e0b\u5ca9\uff0c\u674e\u5929\u626c\u89c1\u4ed6\u4eec\u6ca1\u7cbe\u6253\u91c7\uff0c\u5df2\u77e5\u4e0d\u5999\u3002\u4e00\u95ee\u4e4b\u4e0b\uff0c\u679c\u7136\u654c\u4eba\u5df2\u7ecf\u9003\u8131\u3002 \u6155\u5bb9\u51b2\u9053\uff1a\u201c\u8fd9\u56db\u4eba\u662f\u5426\u94c1\u8d3c\u4e0e\u7389\u7f57\u5239\u7ea6\u6765\u7684\u4eba\uff0c\u674e\u5927\u4eba\u53ef\u6709\u8baf\u95ee\u6e05\u695a\u9ebd\uff1f\u201d\u4f55\u843c\u534e\u5728\u65c1\u56b7 \u9053\uff1a\u201c\u4ec0\u9ebd\u7389\u7f57\u5239\u7ea6\u6765\u7684\uff1f\u6211\u7684\u7239\u7239\u548c\u7389\u7f57\u5239\u5728\u5ca9\u4e0a\u6bd4\u5251\uff0c\u6211\u4eec\u662f\u6765\u5e2e\u4ed6\u7684\u3002\u4f60\u4eec\u8fd9\u4e9b\u5b98\u5dee \u600e\u9ebd\u6beb\u4e0d\u8bb2\u7406\uff0c\u80e1\u4e71\u6349\u4eba\uff01\u201d\u8bf4\u65f6\u6a2a\u4e86\u674e\u5929\u626c\u4e00\u773c\u3002\u9f99\u5578\u4e91\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u548c\u4ed6\u4eec\u7f57\u5506\u4f5c \u751a\uff1f\u662f\u8bb2\u7406\u7684\u5c31\u4e0d\u5f53\u5b98\u5dee\u4e86\u3002\u201d\u6155\u5bb9\u51b2\u773c\u73e0\u4e00\u7ffb\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u7684\u7239\u7239\u662f\u8c01\uff1f\u201d\u4f55\u843c\u534e\u50b2\u7136\u8bf4 \u9053\uff1a\u201c\u6b66\u5f53\u738b\u8001\u4e2d\u7684\u767d\u77f3\u9053\u4eba\uff0c\u4f60\u672a\u89c1\u8fc7\u4e5f\u5e94\u542c\u8fc7\u3002\u201d\u6155\u5bb9\u51b2\u7b11\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u767d\u77f3\u9053\u4eba\u7684 \u5973\u513f\uff0c\u90a3\u9ebd\u6211\u4eec\u6349\u4f60\u5e76\u65e0\u6349\u9519\u3002\u8c01\u53eb\u4f60\u7684\u7236\u4eb2\u548c\u6211\u4eec\u4f5c\u5bf9\u3002\u201d\u91d1\u72ec\u5f02\u5374\u51b7\u7b11\u9053\uff1a\u201c\u9b3c\u8bdd\uff0c\u9b3c \u8bdd\uff0c\u767d\u77f3\u9053\u4eba\u600e\u9ebd\u4f1a\u4e0e\u7389\u7f57\u5239\u6bd4\u5251\uff1f\u4f60\u80e1\u8bf4\u516b\u9053\uff0c\u4e00\u5b9a\u662f\u5192\u8ba4\u7684\u3002\u201d\u4f55\u843c\u534e\u6012\u9053\uff1a\u201c\u5929\u4e0b\u5c82 \u6709\u5192\u8ba4\u7236\u4eb2\u4e4b\u7406\uff1f\u201d\u674e\u7533\u65f6\u95fb\u8a00\u611f\u89e6\uff0c\u77aa\u5927\u773c\u775b\uff0c\u76ef\u7740\u4e86\u674e\u5929\u626c\u671b\u5f97\u51fa\u795e\u3002\u674e\u5929\u626c\u6253\u4e86\u4e00\u4e2a \u5bd2\u5664\uff0c\u51fa\u6765\u8bf4\u9053\uff1a\u201c\u4e0d\u7ba1\u5979\u662f\u4e0d\u662f\u767d\u77f3\u9053\u4eba\u7684\u5973\u513f\uff0c\u5148\u5e26\u56de\u53bb\u518d\u5ba1\u95ee\u5427\u3002\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u662f \u8be5\u8fd9\u6837\u3002\u201d\u674e\u5929\u626c\u9053\uff1a\u201c\u5e26\u5979\u4eec\u56de\u5bab\u5ba1\u95ee\uff0c\u4e0d\u5927\u65b9\u4fbf\uff0c\u8fd8\u662f\u8ba9\u6211\u5e26\u5230\u9526\u8863\u536b\u6240\u53bb\u5427\u3002\u201d\u4e1c\u897f \u4e24\u5382\u8bbe\u5728\u5bab\u4e2d\uff0c\u7531\u592a\u76d1\u638c\u63e1\uff0c\u4e24\u5382\u201c\u6869\u5934\u201d\u76f8\u5f53\u65bc\u5bab\u4e2d\u536b\u58eb\uff1a\u9526\u8863\u536b\u5219\u7ba1\u5916\u5ef7\u4e4b\u4e8b\uff0c\u7531\u6b66\u5b98 \u4e3b\u7ba1\uff0c\u641c\u6355\u6d41\u72af\uff0c\u8baf\u95ee\u72af\u4eba\uff0c\u591a\u5c5e\u9526\u8863\u536b\u7ba1\u7406\uff0c\u6155\u5bb9\u51b2\u89c1\u8fd9\u56db\u4eba\u5e76\u975e\u7d27\u8981\u72af\u4eba\uff0c\u4fbf\u5356\u674e\u5929\u626c \u9762\u5b50\uff0c\u968f\u53e3\u5e94\u5141\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u51fa\u52a8\u4e86\u5927\u6279\u5382\u536b\uff0c\u4ecd\u7136\u88ab\u94c1\u98de\u9f99\u7b49\u8131\u9003\uff0c\u5927\u4e3a\u4e27\u6c14\uff1a\u91d1\u72ec\u5f02\u5931\u4e86\u59bb\u5b50\uff0c\u66f4\u662f\u65e0 \u795e\u3002\u56de\u5230\u57ce\u4e2d\uff0c\u674e\u5929\u626c\u548c\u4ed6\u4eec\u9053\u522b\uff0c\u81ea\u628a\u56db\u540d\u4fd8\u864f\uff0c\u62bc\u56de\u536b\u6240\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u4e14\u8bf4\u7ea2\u82b1\u9b3c\u6bcd\u88ab\u7389\u7f57\u5239\u6253\u8d25\u4e4b\u5f8c\uff0c\u56de\u5230\u5bb6\u4e2d\uff0c\u5429\u5490\u513f\u5b50\u5ab3\u5987\uff0c\u7b2c\u4e8c\u65e5\u4e00\u65e9\u4fbf\u56de\u8f6c\u6e56\u5317\u8001 \u5bb6\u3002\u516c\u5b59\u96f7\u9053\uff1a\u201c\u5988\uff0c\u4f60\u548c\u90a3\u7389\u7f57\u5239\u89c1\u4e86\u6ca1\u6709\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u65a5\u9053\uff1a\u201c\u4f60\u5c11\u7ba1\u95f2\u4e8b\uff0c\u8fd9\u6b21\u56de\u8f6c \u8001\u5bb6\u4e4b\u5f8c\uff0c\u6211\u518d\u4e0d\u51c6\u4f60\u5728\u6c5f\u6e56\u8d70\u52a8\uff0c\u4e5f\u4e0d\u51c6\u4f60\u95ee\u53ca\u6b66\u6797\u4e4b\u4e8b\u3002\u4f60\u5b89\u5b89\u4efd\u4efd\u7ed9\u6211\u8e72\u5728\u5bb6\u91cc\uff0c\u82e5 \u6562\u6709\u8fdd\uff0c\u6211\u5c31\u6253\u65ad\u4f60\u7684\u53cc\u817f\u3002\u201d\u516c\u5b59\u96f7\u561f\u7740\u5634\u5600\u5600\u5495\u5495\u8bf4\u9053\uff1a\u201c\u5988\uff0c\u7687\u5bab\u8fd9\u9ebd\u534e\u4e3d\u4f60\u90fd\u4e0d \u4f4f\uff0c\u518d\u8bf4\u6211\u4eec\u4e00\u5bb6\u56e2\u5706\u591a\u597d\uff0c\u6211\u4eec\u548c\u7239\u7239\u76f8\u89c1\u4e5f\u4e0d\u8fc7\u4e00\u6708\u3002\u201d\u539f\u6765\u7ea2\u82b1\u9b3c\u6bcd\u9001\u5ba2\u5a09\u5a77\u4eba\u5bab\uff0c \u4ea4\u7ed9\u4e86\u5979\u7684\u751f\u6bcd\u5ba2\u6c0f\u592b\u4eba\u4e4b\u5f8c\uff0c\u5728\u5bab\u4e2d\u4e5f\u9017\u7559\u4e86\u51e0\u5929\uff0c\u8fc7\u4e0d\u60ef\u5bab\u4e2d\u751f\u6d3b\uff0c\u52a0\u4ee5\u5ba2\u9b4f\u6deb\u79fd\u4e4b \u4e8b\uff0c\u5979\u4e5f\u5fae\u6709\u6240\u95fb\uff0c\u5979\u4eba\u672c\u4e0d\u574f\uff0c\u4e0d\u80af\u5728\u5bab\u4e2d\u518d\u4f4f\uff0c\u5728\u5916\u9762\u79df\u4e86\u4e00\u680b\u623f\u5c4b\uff0c\u516c\u5b59\u96f7\u548c\u7a46\u4e5d\u5a18 \u4e5f\u88ab\u5b89\u987f\u5728\u8fd9\u95f4\u5c4b\u5185\uff0c\u4e0d\u51c6\u4ed6\u4eec\u5165\u5b98\u3002 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u89c1\u513f\u5b50\u8d2a\u604b\u7e41\u534e\uff0c\u5927\u4e3a\u751f\u6c14\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u6709\u672c\u4e8b\u5566\uff0c\u4f60\u8981\u8ddf\u4f60\u7236\u4eb2\uff0c\u5c31\u522b\u56de \u6211\u8fd9\u91cc\u3002\u201d\u516c\u5b59\u96f7\u4e0d\u6562\u4f5c\u58f0\uff0c\u548c\u7a46\u4e5d\u5a18\u6536\u62ac\u7ec6\u8f6f\u3002\u7ea2\u82b1\u9b3c\u6bcd\u62ff\u8d77\u62d0\u6756\uff0c\u5728\u5ead\u9662\u4e2d\u8d70\u6765\u8d70\u53bb\uff0c \u65f6\u4e0d\u65f6\u4ee5\u62d0\u6756\u51fb\u77f3\uff0c\u9535\u9535\u6709\u58f0\u3002\u516c\u5b59\u96f7\u6700\u6015\u4ed6\u6bcd\u4eb2\uff0c\u5728\u623f\u5b50\u91cc\u8eb2\u7740\u4e0d\u6562\u51fa\u6765\u3002\u6b8a\u4e0d\u77e5\u7ea2\u82b1\u9b3c \u6bcd\u5fc3\u60c5\u66b4\u8e81\uff0c\u56fa\u7136\u548c\u513f\u5b50\u4e0d\u8096\u6709\u5173\uff0c\u4f46\u88ab\u7389\u7f57\u5239\u6253\u8d25\uff0c\u5374\u66f4\u662f\u4ee4\u5979\u96be\u8fc7\u3002 \u3000\u3000\u770b\u770b\u5df2\u5230\u5348\u591c\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u8fd8\u662f\u5728\u5ead\u9662\u4e2d\u8d70\u6765\u8d70\u53bb\uff0c\u4e00\u5ffd\u513f\u60f3\u66f4\u628a\u6b66\u529f\u7cbe\u7814\uff0c\u518d\u627e\u7389\u7f57\u5239 \u51b3\u4e2a\u80dc\u8d1f\uff1a\u4e00\u5ffd\u513f\u60f3\u4ece\u6b64\u95ed\u95e8\u5c01\u62d0\uff0c\u4ec0\u9ebd\u4e8b\u4e5f\u4e0d\u7406\u5b83\u3002\u60f3\u5230\u5348\u591c\uff0c\u5ffd\u5730\u54d1\u7136\u5931\u7b11\uff0c\u81ea\u5df1\u5e74\u5df2 \u8001\u8fc8\uff0c\u4f55\u5fc5\u8fd8\u4e0e\u4eba\u6597\u6c14\u4e89\u5f3a\uff1a\u800c\u4e14\u4e3a\u4e86\u8fd9\u9ebd\u4e00\u4e2a\u574f\u4e08\u592b\uff0c\u60f9\u51fa\u8bb8\u591a\u662f\u975e\uff0c\u4e5f\u5b9e\u5728\u65e0\u804a\u3002\u8fd9\u9ebd \u4e00\u60f3\uff0c\u66b4\u8e81\u7684\u5fc3\u60c5\u60ed\u60ed\u5e73\u9759\u3002\u5ffd\u542c\u5f97\u5916\u9762\u6709\u4eba\u62cd\u95e8\uff0c\u516c\u5b59\u5927\u5a18\u95ee\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u5916\u9762\u91d1\u72ec\u5f02 \u7684\u58f0\u97f3\u7b54\u9053\uff1a\u201c\u5a18\u5b50\uff0c\u662f\u6211\u6765\u5566\uff01\u201d \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u5f00\u4e86\u5927\u95e8\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u8fd8\u6765\u4f5c\u751a\uff1f\u201d\u91d1\u72ec\u5f02\u9053\uff1a\u201c\u4f60\u6ca1\u4e8b\u5417\uff1f\u771f\u628a\u6211\u6025\u6b7b \u5566\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u677f\u8138\u9053\uff1a\u201c\u4f60\u5230\u79d8\u9b54\u5d16\u4e86\uff1f\u201d\u91d1\u72ec\u5f02\u9053\uff1a\u201c\u6211\u5c82\u6562\u4e0d\u542c\u4f60\u7684\u5429\u5490\uff0c\u6211\u662f\u4e45\u4e0d \u89c1\u4f60\u56de\u6765\uff0c\u8fd9\u624d\u53bb\u770b\u4e2a\u52a8\u9759\u7684\u3002\u201d\u5176\u5b9e\u4ed6\u5728\u6492\u8c0e\u3002\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u6765\u6253\u542c\u4e86\uff0c\u6211\u4e0d\u80fd \u518d\u5e2e\u4f60\u4e86\u3002\u201d\u91d1\u72ec\u5f02\u9053\uff1a\u201c\u5a18\u5b50\uff0c\u6211\u4eec\u5230\u5e95\u662f\u591a\u5e74\u592b\u5987\uff0c\u4f60\u5c31\u4e0d\u7406\u6211\u7684\u6b7b\u6d3b\u4e86\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd \u5173\u4e0a\u5927\u95e8\uff0c\u548c\u91d1\u72ec\u5f02\u8d70\u8fdb\u5c4b\u5185\uff0c\u8fb9\u8d70\u8fb9\u9053\uff1a\u201c\u8fde\u6211\u4e5f\u4e0d\u662f\u4eba\u5bb6\u5bf9\u624b\uff0c\u53eb\u6211\u5982\u4f55\u5e2e\u4f60\uff1f\u201d\u91d1\u72ec \u5f02\u5927\u5403\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u7ed9\u4ed6\u4eec\u4e8c\u4eba\u6253\u8d25\u4e86\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u55ef\uff0c\u662f\u7ed9\u7389\u7f57\u5239\u8fd9\u5973\u5a03\u513f\u6253\u8d25 \u4e86\u3002\u201d\u91d1\u72ec\u5f02\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u4fe1\uff01\u201d\u5fc3\u60f3\uff1a\u7389\u7f57\u5239\u5251\u6cd5\u867d\u7136\u7cbe\u5999\u7edd\u4f26\uff0c\u4f46\u82e5\u5355\u6253\u72ec\u6597\uff0c\u548c \u81ea\u5df1\u4e5f\u4e0d\u8fc7\u6253\u4e2a\u5e73\u624b\uff0c\u8fd9\u81ed\u5a46\u5a18\u6b66\u529f\u6bd4\u6211\u5f3a\u5f97\u591a\uff0c\u600e\u4f1a\u6253\u4e0d\u8fc7\u5979\uff1f\u7ea2\u82b1\u9b3c\u6bcd\u628a\u80a9\u4e0a\u8863\u670d\u6293 \u88c2\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u4fe1\u5c31\u6765\u770b\u770b\uff01\u201d \u3000\u3000\u91d1\u72ec\u5f02\u4e0a\u524d\uff0c\u53ea\u89c1\u59bb\u5b50\u80a9\u5934\u4e0a\u6709\u4e00\u9053\u5251\u4f24\uff0c\u6df1\u53ef\u89c1\u9aa8\uff0c\u4e0d\u7981\u5927\u60ca\u3002\u9053\uff1a\u201c\u6211\u7ed9\u4f60\u627e\u4f24 \u836f\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u4e0d\u5fc5\u5047\u60fa\u60fa\u5566\uff0c\u8fd9\u70b9\u4f24\u96be\u9053\u6211\u8fd8\u62b5\u53d7\u4e0d\u4e86\uff1f\u201d\u91d1\u72ec\u5f02\u9053\uff1a\u201c\u54b1\u4eec\u592b\u59bb \u8054\u624b\uff0c\u518d\u4e0e\u4ed6\u4eec\u6253\u8fc7\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u51b7\u7b11\u9053\uff1a\u201c\u6211\u529d\u4f60\u4e5f\u5c11\u5728\u5916\u9762\u80e1\u95f9\u5427\u3002\u201d\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c \u7b11\u5f97\u751a\u662f\u51c4\u51c9\uff0c\u91d1\u72ec\u5f02\u4e0d\u6562\u4f5c\u58f0\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u7eed\u9053\uff1a\u201c\u4f60\u628a\u6211\u7239\u6c14\u6b7b\uff0c\u8fd9\u9ebd\u591a\u5e74\u6765\u5728\u5916\u9762\u80e1\u4f5c \u975e\u4e3a\uff0c\u800c\u4eca\u5df2\u662f\u8fd9\u9ebd\u4e00\u628a\u5e74\u7eaa\uff0c\u8fd8\u4e0d\u56de\u8fc7\u5934\u9ebd\uff1f\u201d\u91d1\u72ec\u5f02\u4ecd\u4e0d\u4f5c\u58f0\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u6309\u8bf4\u6211 \u4eec\u592b\u59bb\u4e4b\u60c5\u5df2\u7edd\uff0c\u6211\u8fd9\u6b21\u672c\u60f3\u6700\u5f8c\u5e2e\u4f60\u4e00\u6b21\uff0c\u73b0\u5728\u4e5f\u5e2e\u4e0d\u4e0a\u624b\u3002\u6211\u660e\u5929\u5c31\u8981\u56de\u53bb\u4e86\u3002\u201d\u91d1\u72ec \u5f02\u8df3\u8d77\u6765\u9053\uff1a\u201c\u4f60\u8981\u56de\u53bb\uff1f\u4f60\u518d\u4e5f\u4e0d\u7406\u6211\u4e86\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u6b63\u662f\u8fd9\u6837\u3002\u201d\u91d1\u72ec\u5f02\u6b63\u60f3\u53d1 \u4f5c\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u5ffd\u7136\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u4fdd\u5b58\u6027\u547d\uff0c\u4e56\u4e56\u7684\u8ddf\u6211\u56de\u53bb\u5427\uff0c\u4e0d\u8981\u518d\u5728\u8fd9 \u513f\u80e1\u6df7\u4e86\u3002\u201d\u91d1\u72ec\u5f02\u9053\uff1a\u201c\u4ec0\u9ebd\u80e1\u6df7\uff01\u6211\u4eec\u5728\u5bab\u4e2d\u4eab\u798f\uff0c\u5c82\u4e0d\u6bd4\u5728\u6df1\u5c71\u91ce\u5cad\u8fc7\u82e6\u65e5\u5b50\u5f3a\u5f97 \u591a\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u62d0\u6756\u4e00\u987f\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4e0d\u56de\u53bb\uff1f\u201d\u91d1\u72ec\u5f02\u9053\uff1a\u201c\u8bf4\u4ec0\u9ebd\u6211\u4e5f\u4e0d\u56de\u53bb\uff01\u201d \u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u597d\uff0c\u4ee5\u5f8c\u4f60\u662f\u6b7b\u662f\u6d3b\uff0c\u6211\u90fd\u4e0d\u7ba1\uff01\u201d\u8bdd\u58f0\u4e00\u505c\uff1a\u5ffd\u89c1\u5ead\u9662\u4e2d\u7684\u74dc\u68da\u4e0a\u4f3c\u6709\u4eba \u5f71\uff0c\u91d1\u72ec\u5f02\u8fd8\u672a\u53d1\u73b0\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u5389\u58f0\u559d\u9053\uff1a\u201c\u7ed9\u6211\u6eda\u4e0b\u6765\uff01\u201d\u74dc\u68da\u4e0a\u4e00\u58f0\u957f\u7b11\uff0c\u5148\u5f8c\u98de\u4e0b\u4e24 \u4eba\uff0c\u7389\u7f57\u5239\u8d70\u5728\u524d\u5934\uff0c\u62b1\u62f3\u4e00\u63d6\uff0c\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u6211\u770b\u4f60\u6765\u5566\uff01\u6211\u4eec\u6bd4\u5251\u65f6\u6240\u8d4c\u7684\u8bdd\uff0c\u4f60\u8001\u4eba \u5bb6\u5f53\u7136\u4e0d\u4f1a\u5fd8\u8bb0\uff01\u201d\u94c1\u98de\u9f99\u5927\u6b65\u8d70\u4e0a\u53f0\u9636\uff0c\u9053\uff1a\u201c\u516c\u5b59\u5927\u5a18\u8a00\u51fa\u5fc5\u884c\uff0c\u4f60\u521a\u624d\u6ca1\u6709\u542c\u5230\u5417\uff1f \u4f55 \u5fc5\u591a\u8bf4\uff01\u201d \u3000\u3000\u539f\u6765\u7389\u7f57\u5239\u575a\u6301\u8981\u6551\u767d\u77f3\u9053\u4eba\u7684\u5973\u513f\uff0c\u94c1\u98de\u9f99\u60f3\u6765\u60f3\u53bb\uff0c\u60f3\u51fa\u4e86\u4e00\u4e2a\u529e\u6cd5\u3002\u4ed6\u627e\u9f99\u8fbe\u53c1 \u5e2e\u5fd9\uff0c\u6253\u542c\u5230\u7ea2\u82b1\u9b3c\u6bcd\u7684\u4f4f\u5904\u3002\u9884\u6599\u91d1\u72ec\u5f02\u5fc5\u6765\u627e\u5979\uff0c\u4fbf\u548c\u7389\u7f57\u5239\u660f\u591c\u8d70\u6765\uff0c\u5077\u5077\u5728\u74dc\u68da\u4e0a \u542c\u4ed6\u4eec\u8c08\u8bdd\u3002 \u3000\u3000\u91d1\u72ec\u5f02\u4e5f\u4e0d\u77e5\u59bb\u5b50\u4e0e\u4ed6\u4eec\u8d4c\u8d5b\u4ec0\u9ebd\uff0c\u6043\u7740\u6709\u5979\u5728\u65c1\uff0c\u6012\u9053\uff1a\u201c\u4f60\u4eec\u4e0a\u95e8\u6b3a\u8d1f\u6765\u4e86\uff1f\u201d\u7ea2 \u82b1\u9b3c\u6bcd\u9893\u7136\u5750\u5728\u5385\u4e2d\u7684\u592a\u5e08\u6905\u4e0a\uff0c\u4e0d\u53d1\u4e00\u8a00\u3002\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u5c82\u6562\uff0c\u5c82\u6562\uff01\u4f60\u4eec\u4eca\u65e5\u4e00\u5927\u7fa4 \u4eba\u5230\u79d8\u9b54\u5d16\u627e\u6211\uff0c\u627e\u4e0d\u7740\u603b\u672a\u514d\u6709\u70b9\u5931\u671b\u5427\uff1f\u6211\u73b0\u5728\u662f\u4e13\u8bda\u8bf7\u6559\u6765\u4e86\u3002\u201d\u91d1\u72ec\u5f02\u9053\uff1a\u201c\u4f60\u60f3 \u600e\u6837\uff0c\u5212\u51fa\u9053\u6765\uff01\u201d\u94c1\u98de\u9f99\u5728\u65c1\u7b11\u9053\uff1a\u201c\u60f3\u501f\u5c0a\u9a7e\u8fd9\u4e03\u5c3a\u4e4b\u8eaf\u4e00\u7528\uff01\u201d\u91d1\u72ec\u5f02\u5927\u6012\uff0c\u624b\u638c\u4e00 \u7ffb\uff0c\u671d\u7389\u7f57\u5239\u4e00\u638c\u6253\u6765\uff0c\u7389\u7f57\u5239\u4e00\u8df3\u8df3\u5f00\uff0c\u5b9d\u5251\u62d4\u5728\u624b\u4e2d\uff0c\u5c31\u5728\u7ea2\u82b1\u9b3c\u6bcd\u9762\u524d\uff0c\u4e0e\u91d1\u72ec\u5f02\u6076 \u6218\uff01 \u3000\u3000\u516c\u5b59\u96f7\u4e0e\u7a46\u4e5d\u5a18\u95fb\u58f0\u8dd1\u51fa\u201c\u516c\u5b59\u96f7\u62d4\u51fa\u4f69\u5200\uff0c\u94c1\u98de\u9f99\u5706\u7741\u53cc\u773c\uff0c\u9053\uff1a\u201c\u4f60\u6562\u8fc7\u6765\uff01\u201d\u7a46 \u4e5d\u5a18\u751a\u662f\u5c34\u5c2c\uff0c\u5c06\u516c\u5b59\u96f7\u4e00\u628a\u62c9\u7740\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u6012\u9053\uff1a\u201c\u4f60\u6562\u6b3a\u8d1f\u6211\u7684\u513f\u5b50\uff1f\u201d\u94c1\u98de\u9f99\u51b7\u7b11 \u9053\uff1a\u201c\u6211\u7684\u5973\u513f\u4e0e\u4f60\u7684\u6c49\u5b50\u5355\u6253\u72ec\u6597\uff0c\u82e5\u6709\u522b\u4eba\u52a9\u62f3\uff0c\u6211\u5f53\u7136\u4e0d\u80fd\u5750\u89c6\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u5927\u53eb\u4e00 \u58f0\uff0c\u6c14\u5728\u5fc3\u5934\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u3002\u62d0\u6756\u4e00\u987f\uff0c\u9053\uff1a\u201c\u96f7\u513f\uff0c\u54b1\u4eec\u73b0\u5728\u5c31\u8d70\uff01\u8fde\u591c\u56de\u5bb6\uff01\u201d\u5979\u4e0e\u7389\u7f57 \u5239\u6709\u7ea6\u5728\u5148\uff0c\u65e2\u7136\u4e0d\u80fd\u5e2e\u624b\uff0c\u4e0d\u5fcd\u89c1\u4e08\u592b\u6b7b\u5728\u654c\u4eba\u5251\u4e0b\uff0c\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u60f3\u4e00\u8d70\u4e86\u4e4b\uff01 \u3000\u3000\u516c\u5b59\u96f7\u65e0\u8bba\u5982\u4f55\u4e0d\u80af\u968f\u6bcd\u4eb2\u51fa\u8d70\uff0c\u6b63\u5728\u62c9\u62c9\u626f\u626f\u4e4b\u65f6\uff01\u5ffd\u542c\u5f97\u91d1\u72ec\u5f02\u4e00\u58f0\u60e8\u53eb\uff0c\u516c\u5b59\u96f7 \u6012\u53eb\u9053 \uff1a\u201c\u5988\uff01\u54b1\u4eec\u5c82\u80fd\u89c1\u6b7b\u4e0d\u6551\uff01\u4e0d\u5fe0\u4e0d\u5b5d\u4f55\u4ee5\u4e3a\u4eba\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u5230\u5e95\u8fd8\u6709\u592b\u5987\u4e4b\u60c5\uff0c \u542c\u4e86\u513f\u5b50\u7684\u8bdd\uff0c \u5fc3\u5934\u5982\u4e2d\u5de8  \uff0c\u9661\u7136\u56de\u8fc7\u4e86\u5934\uff0c\u4e3e\u8d77\u62d0\u6756\u3002\u94c1\u98de\u9f99\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u8bf4\u8bdd\u7b97\u4e0d \u7b97\u6570\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u6012\u9053\uff1a\u201c\u4f60\u4eec\u8981\u5728\u6211\u5c4b\u5185\u884c\u51f6\uff0c\u6211\u4e0d\u8bb8\u53ef\uff01\u201d\u4e00\u6756\u5954\u94c1\u98de\u9f99\u5934\u4e0a\u6253\u6765\uff0c\u53f0 \u9636\u4e0b\u91d1\u72ec\u5f02\u5df2\u88ab\u7389\u7f57\u5239\u6253\u5012\u5730\u4e0a\u3002 \u3000\u3000\u672c\u6765\u91d1\u72ec\u5f02\u7684\u6b66\u529f\uff0c\u4e0d\u5728\u7389\u7f57\u5239\u4e4b\u4e0b\uff1a\u4f46\u4e00\u6765\u4ed6\u524d\u51e0\u5929\u53d7\u4e86\u5251\u4f24\uff0c\u521a\u521a\u6cbb\u597d\uff0c\u6c14\u529b\u8fd8\u672a \u590d\u539f\uff1a\u4e8c\u6765\u4ed6\u9760\u7684\u662f\u6bd2\u7802\u638c\u5a01\u529b\uff0c\u7389\u7f57\u5239\u624b\u4e0a\u5e26\u6709\u5cb3\u545c\u73c2\u7684\u91d1\u4e1d\u624b\u5957\uff0c\u4e0d\u6015\u6bd2\u4f24\uff0c\u5251\u62db\u5168\u53d6 \u653b\u52bf\uff0c\u5a01\u529b\u5927\u589e\uff1a\u53c1\u6765\u91d1\u72ec\u5f02\u89c1\u59bb\u5b50\u5c45\u7136\u8fd9\u6837\u5fcd\u5fc3\uff0c\u7adf\u4e0d\u5e2e\u4ed6\uff0c\u8fd8\u8981\u548c\u513f\u5b50\u5ab3\u5987\u8fde\u591c\u51fa\u8d70\uff0c \u4e0d\u7981\u53c8\u6c14\u53c8\u60ca\u53c8\u6012\uff0c\u8fde\u8d70\u8d25\u62db\uff0c\u7ed9\u7389\u7f57\u5239\u4e00\u5251\u523a\u4f24\uff0c\u518d\u60f3\u9003\u65f6\uff0c\u90a3\u8fd8\u9003\u5f97\u3002\u7389\u7f57\u5239\u8eab\u5f62\u75be \u8d77\uff0c\u4e00\u811a\u628a\u4ed6\u8e22\u5012\uff0c\u5f13\u978b\u4e00  \uff0c\u5c06\u4ed6\u808b\u9aa8  \u65ad\u4e24\u6839\uff0c\u987a\u52bf\u53c8\u70b9\u4e86\u4ed6\u7684\u8f6f  \u54d1\u7a74\u3002 \u3000\u3000\u94c1\u98de\u9f99\u529b\u62c6\u6570\u62db\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u62d0\u52bf\u7a0d\u7f13\uff0c\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6211\u4eec\u53c8\u4e0d\u6740\u5bb3\u4f60\u7684\u6c49\u5b50\uff0c\u4f60\u6025\u4ec0 \u9ebd\uff1f\u201d\u516c\u5b59\u96f7\u5954\u53bb\u6551\u7236\uff0c\u7ed9\u7389\u7f57\u5239\u4e00\u5251\u524a\u65ad\u4ed6\u7684\u4f69\u5200\uff0c\u53cd\u624b\u4e00\u6325\uff0c\u5c06\u4ed6\u8dcc\u51fa\u4e00\u4e08\u5f00\u5916\u3002\u7ea2\u82b1 \u9b3c\u6bcd\u62d0\u6756\u4e00\u505c\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u60f3\u600e\u9ebd\u6837\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6211\u4eec\u53ea\u662f\u60f3\u501f\u5c0a\u592b\u4e00\u7528\u3002\u201d\u7389\u7f57\u5239\u6162 \u6761\u65af\u7406\u7684\u63d2\u5251\u5f52\u9798\uff0c\u8d70\u4e86\u8fc7\u6765\uff0c\u76c8\u76c8\u4e00\u63d6\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u8981\u8bf7\u4f60\u5e2e\u5fd9\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u6c14\u9053\uff1a \u201c\u4f60\u8fd9\u5973\u5a03\u513f\u5a01\u98ce\u4e0d\u53ef\u4f7f\u5c3d\uff0c\u4f60\u65e2\u4e0d\u7559\u60c5\u9762\uff0c\u5c31\u4f11\u602a\u6211\u4e0d\u5b88\u8bfa\u8a00\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u662f \u8bf4\u98ce\u51c9\u8bdd\u513f\uff0c\u771f\u7684\u8981\u8bf7\u4f60\u8001\u5e2e\u5fd9\u3002\u800c\u4e14\u4f60\u65e2\u628a\u8fd9\u81ed\u6c49\u5b50\u5f53\u6210\u5b9d\u8d1d\uff0c\u6211\u4eec\u4e5f\u53ef\u9001\u8fd8\u7ed9\u4f60\u3002\u4f46\u4f60 \u53ef\u5f97\u628a\u4ed6\u597d\u597d\u7ba1\u675f\u4e86\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u62d0\u6756\u672c\u5df2\u4e3e\u8d77\uff0c\u53c8\u518d\u653e\u4e0b\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8bf4\uff01\u201d\u7389\u7f57\u5239 \u9053\uff1a\u201c\u767d\u77f3\u9053\u4eba\u7684\u5973\u513f\u88ab\u6155\u5bb9\u51b2\u6349\u53bb\u4e86\uff0c\u4f60\u5bf9\u4ed6\u8bf4\uff0c\u8bf7\u4ed6\u653e\u4eba\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765 \u4f60\u4eec\u662f\u60f3\u501f\u6b64\u8981\u631f\uff0c\u8feb\u6211\u8981\u4ed6\u6362\u4eba\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u8fd9\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u9ebd\u8981\u631f\u3002\u5c0a\u592b\u662f\u6210\u540d\u7684\u4eba \u7269\uff0c\u767d\u77f3\u9053\u4eba\u7684\u5973\u513f\u4e0d\u8fc7\u662f\u4e2a\u6bdb\u4e2b\u5934\u3002\u8fd9\u4ea4\u6362\u5bf9\u4f60\u4eec\u7edd\u4e0d\u5403\u4e8f\u3002\u6155\u5bb9\u51b2\u7eb5\u4e0d\u770b\u5728\u4f60\u7684\u60c5\u9762\uff0c \u95fb\u77e5\u6b64\u4e8b\uff0c\u4e5f\u8981\u8d76\u6765\u4ea4\u6362\u3002\u4e0d\u8fc7\u6155\u5bb9\u51b2\u8fd9  \uff0c\u6211\u4eec\u89c1\u4ed6\u4e0d\u6613\uff0c\u6240\u4ee5\u53ea\u597d\u8bf7\u4f60\u5e2e\u5fd9\u5954\u8d70\u7f62 \u4e86\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u7709\u6bdb\u4e00\u626c\uff0c\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u4e00\u8a00\u4e3a\u5b9a\uff0c\u660e\u65e5\u665a\u4e0a\uff0c\u53c1\u66f4\u65f6\u5206\uff0c\u4ecd\u5728\u79d8\u9b54\u5d16\u4ea4 \u6362\u3002\u4f60\u4eec\u53ef\u4e0d\u8bb8\u5c06\u4ed6\u4e3a\u96be\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8fd9\u6b21\u4f60\u4eec\u53ef\u4e0d\u8bb8\u5077\u5077 \u57cb\u4f0f\uff0c\u8981\u4e0d\u7136\u6211\u7684\u5b9d\u5251\u53ef\u4e0d\u8bb2\u60c5\u9762\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u516c\u5b59\u5927\u5a18\u662f\u6b66\u6797\u524d\u8f88\uff0c\u8fd9\u70b9\u9ed1\u9053\u7684\u89c4\u77e9 \u90a3\u4f1a\u4e0d\u61c2\u4e86\u660e\u665a\u54b1\u4eec\u7239\u513f\u4fe9\u53bb\uff0c\u4ed6\u4eec\u90a3\u8fb9\uff0c\u9664\u4e86\u516c\u5b59\u5927\u5a18\u524d\u8f88\u4e4b\u5916\uff0c\u81ea\u7136  \u6709\u6155\u5bb9\u51b2\u4e00 \u4eba\uff01\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u4e24\u4f4d\u8981\u4ea4\u6362\u7684\u4fd8\u864f\u5462\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u6012\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u5fc5\u7f57\u5506\uff0c\u5c31 \u8fd9\u6837\u529e\u201c\u6155\u5bb9\u51b2\u82e5\u8981\u591a\u5e26\u4eba\u53bb\uff0c\u6211\u5c31\u5148\u4e0e\u4ed6\u62da\u4e86\u3002\u201d\u94c1\u98de\u9f99\u4e00\u7b11\uff0c\u62b1\u62f3\u4f5c\u63d6\uff0c\u8f6c\u8eab\u5c06\u91d1\u72ec\u5f02 \u6293\u8d77\uff0c\u548c\u7389\u7f57\u5239\u4e0a\u5c4b\u8d70\u4e86\u3002 \u3000\u3000\u518d\u8bf4\u674e\u5929\u626c\u5c06\u9f99\u5578\u4e91\u7b49\u56db\u4eba\u62bc\u56de\u8859\u6240\uff0c\u8fd9\u4e00\u665a\u601d\u524d\u60f3\u5f8c\uff0c\u5750\u5367\u4e0d\u5b89\u3002\u5230\u4e86\u5348\u591c\uff0c\u53eb\u4eba\u5c06 \u9f99\u5578\u4e91\u63d0\u4e86\u4e0a\u6765\uff0c\u5173\u4e86\u623f\u95e8\uff0c\u4eb2\u81ea\u66ff\u9f99\u5578\u4e91\u89e3\u4e86\u9563\u94d0\uff0c\u8bf7\u4ed6\u5750\u4e0b\u3002\u9f99\u5578\u4e91\u51b7\u51b7\u7b11\u9053\uff1a\u201c\u674e\u5927 \u4eba\u5bbd\u5f85\u72af\u4eba\uff0c\u4e0d\u6015\u8bef\u4e01\u529f\u540d\u5bcc\u8d35\u9ebd\uff1f\u201d\u674e\u5929\u626c\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u5f53\u5e74\u4e4b\u4e8b\uff0c\u662f\u6211\u9519\u4e86\u3002\u6211\u5b9e \u5728\u5f85\u8584\u7eee\u971e\uff0c\u73b0\u5728\u60f3\u6765\uff0c\u6094\u6068\u5df2\u7ecf\u665a\u4e86\u3002 \u3002\u201d\u9f99\u5578\u4e91\u9053\uff1a\u201c\u4f60\u548c\u6211\u8bf4\u6709\u4ec0\u9ebd\u7528\uff1f\u201d\u674e\u5929\u626c \u9053\uff1a\u201c\u60f3\u5f53\u5e74\u6211\u4eec\u53c1\u4eba\u90fd\u662f\u597d\u53cb\u2026\u2026\u201d\u9f99\u5578\u4e91\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u674e\u5929\u626c\u9053\uff1a\u201c\u4f60\u7eb5\u4e0d\u628a\u6211\u5f53\u670b \u53cb\uff0c\u4e5f\u5f53\u770b\u5728\u7eee\u971e\u9762\u4e0a\u3002\u201d\u9f99\u5578\u4e91\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u5012\u5947\u4e86\uff01\u4f60\u4eec\u4eca\u65e5\u51f6\u5982\u864e\u72fc\uff0c\u628a\u6211\u6349\u6765\uff0c\u73b0 \u5728\u6211\u662f\u4f60\u7684\u9636\u4e0b\u4e4b\u56da\uff0c\u6027\u547d\u90fd\u634f\u5728\u4f60\u7684\u624b\u91cc\uff0c\u600e\u9ebd\u98a0\u5012\u8fc7\u6765\u8bf4\uff0c\u8981\u5411\u6211\u6c42\u4ec0\u9ebd\u60c5\uff1f\u201d\u674e\u5929\u626c \u82e6\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u9f99\u5144\uff0c\u4f60\u4e5f\u77e5\u9053\u6211\u5e74\u5c06\u534a\u767e\uff0c\u53ea\u6709\u4e00\u4e2a\u513f\u5b50\uff0c\u5b9e\u5728\u60f3\u5ff5\u5f97\u7d27\u3002\u201d\u9f99\u5578\u4e91\u53c8 \u54fc\u4e86\u4e00\u58f0\u3002\u674e\u5929\u626c\u9053\uff1a\u201c\u9f99\u5144\u8fd9\u9ebd\u591a\u5e74\u6765\uff0c\u53ef\u6709\u89c1\u8fc7\u62d9\u8346\u9ebd\uff1f\u201d\u9f99\u5578\u4e91\u9053\uff1a\u201c\u6211\u89c1\u8fc7\u4e00\u6b21\u7eee \u971e\uff0c\u53ef\u6ca1\u6709\u89c1\u8fc7\u4f60\u7684\u592b\u4eba\uff0c\u600e\u9ebd\u6837\uff1f\u201d\u674e\u5929\u626c\u5f3a\u6291\u6012\u6c14\uff0c\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u548c\u7eee\u971e\u4ea4\u60c5\u5f88\u662f\u4e0d \u9519\uff0c\u6240\u4ee5\u4f60\u81f3\u4eca\u672a\u5a36\u3002\u201d\u9f99\u5578\u4e91\u6012\u9053\uff1a\u201c\u6211\u5a36\u4e0d\u5a36\u4e0e\u4f60\u4f55\u5e72\uff1f\u4f60\u5c11\u4e71\u56bc\u820c\u5934\u3002\u201d\u674e\u5929\u626c\u5f3a\u7b11 \u9053\uff1a\u201c\u9f99\u5144\u60f3\u5230\u90a3\u513f\u53bb\u4e86\uff1f\u8bf7\u6055\u5144\u5f1f\u4e0d\u4f1a\u8bf4\u8bdd\u3002\u6211\u53ea\u662f\u4e3a\u4e86\u601d\u5ff5\u513f\u5b50\uff0c\u6240\u4ee5\u60f3\u95ee\u9f99\u5144\u4e00\u58f0\uff0c \u77e5\u4e0d\u77e5\u9053\u7533\u513f\u7684\u6d88\u606f\u3002\u201d\u9f99\u5578\u4e91\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u4f60\u7684\u513f\u5b50\u77e5\u9053\u6709\u4f60\u8fd9\u9ebd\u4e00\u4e2a\u7236\u4eb2\u3002\u201d\u674e\u5929\u626c\u5fcd \u53d7\u4e0d\u4f4f\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u7533\u513f\u7684\u4ec0\u9ebd\u4eba\uff0c\u4f60\u51ed\u4ec0\u9ebd\u6559\u4ed6\u4e0d\u8ba4\u7236\u4eb2\uff1f\u4f60\u6562\u79bb\u95f4\u6211\u7684\u5bb6\u4eba\u9aa8 \u8089\u3002\u201d\u9f99\u5578\u4e91\u53ea\u51b7\u7b11\u8bf4\u4e86\u4e00\u58f0\uff1a\u201c\u4f55\u5fc5\u6211\u6765\u79bb\u95f4\u3002\u201d\u4e4b\u5f8c\u5c31\u95ed\u53e3\u4e0d\u7b54\uff0c\u4efb\u7531\u4ed6\u9a82\u3002\u674e\u5929\u626c\u5486 \u54ee\u4e00\u9635\uff0c\u91cd\u628a\u9f99\u5578\u4e91\u4e0a\u4e86\u9563\u94d0\uff0c\u53c8\u53eb\u4eba\u5c06\u4ed6\u9501\u56de\u76d1\u623f\u3002 \u3000\u3000\u674e\u5929\u626c\u628a\u9f99\u5578\u4e91\u62bc\u56de\u76d1\u623f\u4e4b\u5f8c\uff0c\u60f3\u4e86\u4e00\u4f1a\uff0c\u53c8\u53eb\u4eba\u5c06\u4f55\u843c\u534e\u63d0\u4e86\u4e0a\u6765\u3002\u5173\u4e0a\u623f\u95e8\uff0c\u7ec6\u58f0 \u8bf4\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u662f\u4f60\u7684\u59d1\u4e08\u9ebd\uff1f\u201d\u4f55\u843c\u534e\u62bf\u5634\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u59d1\u59d1\u6709\u8fc7\u4f60\u8fd9\u9ebd\u4e00\u4e2a\u4e08\u592b\u3002\u201d \u674e\u5929\u626c\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u548c\u7533\u65f6\u8ba4\u8bc6\u5417\uff1f\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u6211\u4eec\u81ea\u5c0f\u4e00\u540c\u73a9\u800d\uff0c\u6709\u4f55\u4e0d \u8bc6\uff1f\u201d\u674e\u5929\u626c\u559c\u9053\uff1a\u201c\u7533\u513f\u53ef\u6709\u95ee\u8d77\u8fc7\u4ed6\u7684\u7236\u4eb2\u9ebd\uff1f\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u6211\u59d1\u59d1\u5bf9\u4ed6\u8bf4\uff0c\u4ed6\u7236\u4eb2 \u662f\u4e2a\u574f\u4eba\uff0c\u81ea\u5e7c\u628a\u4ed6\u629b\u5f03\uff0c\u6240\u4ee5\u4ed6\u4ece\u6765\u6ca1\u6709\u95ee\u8fc7\u4ed6\u7684\u7236\u4eb2\u3002\u201d\u674e\u5929\u626c\u9ed8\u7136\u4e0d\u8bed\uff0c\u8fc7\u4e86\u8bb8\u4e45\uff0c \u624d\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8fdb\u6211\u7684\u4e66\u623f\u5750\u4e00\u4f1a\u513f\u3002\u201d\u8131\u4e86\u5979\u7684\u9563\u94d0\uff0c\u5e26\u5979\u8fdb\u5185\u4e66\u623f\uff0c\u7ed9\u5979\u6ce1\u4e86\u4e00\u676f\u9f99 \u4e95\uff0c\u53c8\u9012\u7ed9\u5979\u4e00\u5305\u871c\u67a3\uff0c\u9053\uff1a\u201c\u4f60\u5750\u4e00\u4f1a\uff0c\u6211\u5c31\u56de\u6765\u3002\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u8fd9\u91cc\u6bd4\u76d1\u623f\u8212\u670d\u591a \u4e86\u3002\u201d\u674e\u5929\u626c\u82e6\u7b11\u4e00\u58f0\uff0c\u53cd\u624b\u5173\u4e0a\u623f\u95e8\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u674e\u5929\u626c\u53c8\u628a\u674e\u7533\u65f6\u63d0\u4e86\u4e0a\u6765\uff0c\u53eb\u4ed6\u5750\u4e0b\u3002\u770b\u4e86\u4e00\u9635\uff0c\u8d8a\u770b\u8d8a\u89c9\u5f97\u4ed6\u548c\u81ea\u5df1\u76f8 \u50cf\uff0c\u6094\u6068\u4ea4\u8ff8\uff0c\u5c06\u4ed6\u9563\u94d0\u89e3\u4e0b\uff0c\u629a\u6478\u4ed6\u7684\u80a9\u5934\uff0c\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u53d7\u4f24\u4e86\uff1f\u201d\u674e\u7533\u65f6\u5728\u6df7\u6218\u4e2d\u66fe \u88ab\u5200\u950b\u522e\u7834\u80a9\u5934\u76ae\u8089\uff0c\u53d7\u4e86\u4e00\u70b9\u8f7b\u4f24\uff0c\u674e\u5929\u626c\u770b\u5728\u773c\u5185\uff0c\u75db\u5728\u5fc3\u5934\uff0c\u5fc3\u9053\uff1a\u82e5\u7136\u4ed6\u771f\u662f\u7533 \u513f\uff0c\u53ea\u6015\u66f4\u6068\u6211\u4e86\u3002\u674e\u7533\u65f6\u8fd9\u65f6\u5341\u5206\u60f6\u60d1\uff0c\u773c\u73e0\u8f6c\u6765\u8f6c\u53bb\uff0c\u4f3c\u5728\u601d\u7d22\u4ec0\u9ebd\u96be\u89e3\u7684\u95ee\u9898\u3002\u8fc7\u4e86 \u8bb8\u4e45\uff0c\u5ffd\u9053\uff1a\u201c\u6211\u72af\u4e86\u4ec0\u9ebd\u7f6a\u540d\uff1f\u4f60\u4eec\u8981\u5c06\u6211\u5173\u8fdb\u7262\u72f1\uff1f\u201d \u3000\u3000\u674e\u5929\u626c\u9053\uff1a\u201c\u56e0\u4e3a\u6709\u4eba\u7591\u5fc3\u4f60\u4eec\u662f\u718a\u5ef7\u5f3c\u7684\u515a\u7fbd\u3002\u201d\u674e\u7533\u65f6\u9053\uff1a\u201c\u718a\u5ef7\u5f3c\u662f\u4e2a\u6297\u654c\u82f1 \u96c4\uff0c\u6211\u867d\u7136\u5e74\u5c0f\uff0c\u4e5f\u5230\u5904\u542c\u5f97\u6709\u4eba\u8d5e\u4ed6\u3002\u83ab\u8bf4\u6211\u4eec\u591f\u4e0d\u4e0a\u662f\u4ed6\u515a\u7fbd\uff0c\u5c31\u7b97\u662f\u4ed6\u515a\u7fbd\uff0c\u4e5f\u7edd\u4e0d \u662f\u4ec0\u9ebd\u7f6a\uff01\u201d\u674e\u5929\u626c\u53c8\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u4f60\u4eec\u5e74\u8f7b\u4eba\u5f04\u4e0d\u660e\u767d\u3002\u201d\u674e\u7533\u65f6\u6602\u5934\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u4f60 \u8fd9\u4f4d\u5927\u4eba\u624d\u4e0d\u660e\u767d\uff01\u201d\u674e\u5929\u626c\u5fc3\u5934\u4e00\u9707\uff0c\u5782\u9996\u4e0d\u8bed\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u62ac\u8d77\u4e86\u5934\uff0c\u76ef\u7740\u674e\u7533\u65f6\u7684\u773c \u775b\u95ee\u9053\uff1a\u201c\u4f55\u843c\u534e\u8fd9\u5c0f\u59d1\u5a18\u662f\u4f60\u7684\u4ec0\u9ebd\u4eba\uff1f\u201d\u674e\u7533\u65f6\u9053\uff1a\u201c\u662f\u6211\u7684\u8868\u59b9\uff0c\u4f60\u7ba1\u8fd9\u4e2a\u5e72\u5417\uff1f\u201d \u3000\u3000\u674e\u5929\u626c\u53c8\u60ed\u53c8\u559c\uff0c\u500f\u7684\u8d77\u6765\uff0c\u53d6\u4e86\u4e00\u9762\u94dc\u955c\uff0c\u9012\u7ed9\u674e\u7533\u65f6\u9053\uff1a\u201c\u4f60\u7167\u7167\u955c\u5b50\uff01\u201d\u674e\u7533\u65f6 \u4e00\u9635\u98a4\u6817\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u4ec0\u9ebd\u610f\u601d\uff1f\u201d\u674e\u5929\u626c\u9053\uff1a\u201c\u4f60\u7167\u7167\u955c\u5b50\uff0c\u770b\u4f60\u7684\u50cf\u8c8c\u662f\u4e0d\u662f\u4e0e\u6211\u76f8 \u4f3c\uff1f\u201d\u674e\u7533\u65f6\u4f7f\u52b2\u4e00\u6454\uff0c\u5c06\u94dc\u955c\u6454\u5728\u5730\u4e0a\uff0c\u88c2\u6210\u51e0\u5757\uff0c\u201c\u54c7\u201d\u7684\u4e00\u58f0\uff0c\u54ed\u4e86\u51fa\u6765\u201c\u674e\u5929\u626c\u624b \u8db3\u65e0\u63aa\uff0c\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u8fd9\u662f\u600e\u9ebd\u5566\uff1f\u201d\u4e0a\u524d\u4e00\u628a\u5c06\u4ed6\u62b1\u4f4f\uff0c\u5728\u4ed6\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u7533\u513f\uff0c\u6211\u662f\u4f60 \u7684\u7236\u4eb2\u54ea\uff01\u201d\u674e\u7533\u65f6\u5728\u6000\u4e2d\u6323\u8131\u51fa\u6765\uff0c\u674e\u5929\u626c\u9053\uff1a\u201c\u600e\u9ebd\u4f60\u4e0d\u8ba4\u7238\u7238\uff1f\u201d\u674e\u7533\u65f6\u9053\uff1a\u201c\u5988 \u8bf4\uff0c\u6211\u7684\u7239\u65e9\u5df2\u6b7b\u4e86\uff01\u201d\u674e\u5929\u626c\u9053\uff1a\u201c\u7236\u5b50\u5c82\u6709\u5192\u8ba4\u4e4b\u7406\u4e86\u4f60\u4e0d\u4fe1\u6211\u662f\u4f60\u7684\u7239\u9ebd\uff01\u201d\u674e\u7533\u65f6 \u9053\uff1a\u201c\u6211\u7684\u7239\u7edd\u4e0d\u4f1a\u5fe0\u5978\u4e0d\u5206\uff0c\u5584\u6076\u4e0d\u660e\uff0c\u66f4\u7edd\u4e0d\u4f1a\u53eb\u4eba\u6349\u4ed6\u7684\u513f\u5b50\uff0c\u4f24\u4ed6\u7684\u513f\u5b50\uff01\u201d\u674e\u5929 \u626c\u5fc3\u4e2d\u5927\u75bc\uff0c\u9aa4\u7136\u9192\u609f\uff0c\u62c9\u7740\u513f\u5b50\u7684\u624b\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u7533\u513f\uff0c\u4f60\u7684\u7236\u4eb2\u679c\u7136\u662f\u5df2\u7ecf\u6b7b\u4e86\uff01\u201d \u674e\u7533\u65f6\u6115\u7136\u770b\u4ed6\uff0c\u674e\u5929\u626c\u9053\uff1a\u201c\u4f60\u542c\u8fc7\u4e24\u53e5\u53e4\u8bdd\u9ebd\uff1a\u8fc7\u53bb\u79cd\u79cd\uff0c\u6bd4\u5982\u6628\u65e5\u6b7b\uff1a\u73b0\u5728\u79cd\u79cd\uff0c\u6bd4 \u5982\u4eca\u65e5\u751f\u3002\u201d\u674e\u7533\u65f6\u70b9\u4e86\u70b9\u5934\uff0c\u674e\u5929\u626c\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u7684\u7236\u4eb2\u6b7b\u8fc7\u53bb\u53c8\u91cd\u751f\u4e86\u3002\u4ed6\u660e\u65e5\u4e00\u65e9\uff0c \u5c31\u5c06\u4f60\u9001\u56de\u5d69\u5c71\uff0c\u89c1\u4f60\u6bcd\u4eb2\u3002\u4ece\u6b64\u518d\u4e5f\u4e0d\u505a\u635e\u4ec0\u5b50\u7684\u5b98\u4e86\u3002\u201d\u674e\u7533\u65f6\u4e00\u559c\uff0c\u62b9\u4e86\u773c\u6cea\uff0c\u9053\uff1a \u201c\u771f\u7684\uff01\u201d\u674e\u5929\u626c\u6d41\u4e0b\u773c\u6cea\uff0c\u9053\uff1a\u201c\u7533\u513f\uff0c\u4f60\u8fd8\u4e0d\u4fe1\u6211\u9ebd\uff1f\u201d\u674e\u7533\u65f6\u4f4e\u4f4e\u53eb\u4e86\u4e00\u58f0\u201c\u7238 \u7238\uff01\u201d\u674e\u5929\u626c\u9732\u51fa\u7b11\u5bb9\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8fd9\u9ebd\u591a\u5e74\u6765\u5728\u4ec0\u9ebd\u5730\u65b9\uff1f\u201d\u674e\u7533\u65f6\u9053\uff1a\u201c\u5728\u5ce8\u7709\u5c71\u548c\u6211 \u7684\u5e08\u7236\u5728\u4e00\u8d77\u3002\u201d\u674e\u5929\u626c\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u5e08\u7236\uff1f\u201d\u674e\u7533\u65f6\u9053\uff1a\u201c\u5c31\u662f\u4eca\u5929\u5728\u79d8\u9b54\u5d16\u4e0b\u88ab\u4f60\u4eec \u6349\u7740\u7684\u90a3\u4f4d\u9f99\u4f2f\u4f2f\u3002\u201d\u674e\u5929\u626c\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u4ed6\uff01\u201d\u674e\u7533\u65f6\u9053\uff1a\u201c\u4f60\u4eec\u662f\u8ba4\u8bc6\u7684\uff1f\u201d\u674e\u5929 \u626c\u9053\uff1a\u201c\u55ef\uff0c\u662f\u8001\u670b\u53cb\u5566\uff01\u201d\u5728\u623f\u95f4\u91cc\u8e31\u6765\u8e31\u53bb\u3002\u674e\u7533\u65f6\u9053\uff1a\u201c\u90a3\u597d\u6781\u5566\uff01\u9f99\u4f2f\u4f2f\u5bf9\u6211\u975e\u5e38 \u4e4b\u597d\u3002\u8fd8\u6709\u534e  \u548c\u90a3\u4f4d\u674e\u5c01\uff0c\u8bf7\u4f60\u5c06\u4ed6\u4eec\u4e5f\u4e00\u5e76\u653e\u4e86\u3002\u201d\u674e\u5929\u626c\u9053\uff1a\u201c\u597d\uff0c\u4e00\u5207\u542c\u4f60\u7684 \u8bdd\u3002\u201d\u5f00\u95e8\u53eb\u4eba\u8fdb\u6765\uff0c\u53eb\u4ed6\u4eec\u5c06\u9f99\u5578\u4e91\u548c\u674e\u5c01\u4e00\u5e76\u63d0\u4e0a\u3002\u674e\u7533\u65f6\u5f85\u4ed6\u7236\u4eb2\u518d\u5173\u4e0a\u623f\u95e8\u56de\u8fc7\u5934 \u65f6\uff0c\u4e00\u628a\u5c06\u4ed6\u62b1\u7740\u3002\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u8d9f\u56de\u53bb\uff0c\u89c1\u7740\u5988\u5988\uff0c\u4e00\u5bb6\u4eba\u518d\u4e5f\u4e0d\u8981\u5206\u5f00\u4e86\u3002\u201d\u7236\u5b50\u4fe9\u76f8 \u89c6\u800c\u7b11\uff0c\u773c\u775b\u91cc\u6709\u4eae\u6676\u6676\u7684\u6cea\u5149\u3002 \u3000\u3000\u518d\u8bf4\u94c1\u98de\u9f99\u548c\u7389\u7f57\u5239\u7b2c\u4e8c\u5929\u665a\u4e0a\uff0c\u5e26\u4e86\u91d1\u72ec\u5f02\u5728\u79d8\u9b54\u5d16\u4e0b\u7b49\u5019\u7ea2\u82b1\u9b3c\u6bcd\uff0c\u7389\u7f57\u5239\u9053\uff1a \u201c\u767d\u77f3\u8fd9\u8d3c\u9053\u6211\u5b9e\u5728\u6c14\u4ed6\u4e0d\u8fc7\uff0c\u7b49\u4f1a\u6551\u4e86\u4ed6\u7684\u5973\u513f\uff0c\u4f60\u5c06\u5979\u9001\u56de\u53bb\u5427\u3002\u201d\u94c1\u98de\u9f99\u8bf4\u9053\uff1a\u201c\u8fd8 \u662f\u4f60\u9001\u53bb\u7684\u597d\u3002\u201d\u8fc7\u4e86\u4e00\u9635\uff0c\u6708\u4eae\u5df2\u5230\u4e2d\u5929\uff0c\u8fdc\u8fd1\u5c71\u5934\u8fd8\u662f\u9759\u6084\u6084\u7684\u4e0d\u89c1\u4eba\u8ff9\u3002\u7389\u7f57\u5239\u7b11 \u9053\uff1a\u201c\u7ea2\u82b1\u9b3c\u6bcd\u8fd8\u672a\u6765\u5462\uff0c\u4e5f\u8bb8\u6155\u5bb9\u51b2\u4e0d\u613f\u4ea4\u6362\u4e86\u3002\u201d \u3000\u3000\u94c1\u98de\u9f99\u9053\uff1a\u201c\u7ea2\u82b1\u9b3c\u6bcd\u7edd\u4e0d\u4f1a\u723d\u7ea6\u3002\u6155\u5bb9\u51b2\u4e5f\u4e0d\u81f3\u65bc\u541d\u60dc\u4e00\u4e2a\u4e2b\u5934\uff0c\u727a\u7272\u6389\u4ed6\u4e00\u6761\u81c2 \u818a\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u4ed6\u4eec\u82e5\u4e0d\u80af\u4ea4\u6362\uff0c\u54b1\u4eec\u5c31\u628a\u8089\u7968\u6495\u4e86\u3002\u201d\u91d1\u72ec\u5f02\u4e00\u751f\u6b8b\u66b4\uff0c\u4f46 \u542c\u4e86\u7389\u7f57\u5239\u8fd9\u79cd\u8bed\u6c14\uff0c\u4e5f\u4e0d\u7981\u5fc3\u614c\u3002\u4f38\u957f\u9888\u8116\uff0c\u4f46\u671b\u59bb\u5b50\u5230\u6765\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5bf9\u9762\u5c71\u5934\u73b0\u51fa\u4eba \u5f71\uff0c\u7389\u7f57\u5239\u8df3\u4e0a\u9ad8\u5ca9\uff0c\u8fdc\u8fdc\u773a\u671b\u3002\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6765\u4e86\u51e0\u4eba\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4e24\u4eba\uff01\u201d\u8fc7\u4e86\u4e00 \u9635\uff0c\u7389\u7f57\u5239\u5ffd\u7136\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u7f38\u82b1\u9b3c\u6bcd\u80cc\u4e0a\u6ca1\u6709\u80cc\u4eba\u3002\u201d\u8df3\u4e0b\u77f3\u5ca9\uff0c\u4e00\u624b\u6293\u7740\u91d1\u72ec \u5f02\u80cc\u5fc3\uff0c\u91d1\u72ec\u5f02\u7a74\u9053\u672a\u89e3\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002\u7389\u7f57\u5239\u4e00\u624b\u62d4\u5251\uff0c\u633a\u7740\u4ed6\u7684\u5f8c\u5fc3\uff0c\u7b11\u9053\uff1a\u201c\u7239\uff0c\u6211\u8981 \u6495\u7968\u5566\uff01\u201d\u91d1\u72ec\u5f02\u5413\u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u94c1\u98de\u9f99\u9053\uff1a\u201c\u88f3\u513f\uff0c\u4e0d\u8981\u80e1\u95f9\uff0c\u7b49\u7ea2\u82b1\u9b3c\u6bcd\u6765\u4e86\u518d\u8bf4\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u548c\u6155\u5bb9\u51b2\u5982\u98de\u5954\u81f3\uff0c\u5e76\u672a\u5e26\u6709\u65c1\u4eba\u3002\u6708\u5149\u4e0b\u7ea2\u82b1\u9b3c\u6bcd\u9762\u8272\u60e8\u767d\uff0c\u66f4 \u662f\u72f0\u72de\u53ef\u6015\u3002\u7389\u7f57\u5239\u51b7\u7b11\u9053\uff1a\u201c\u4eba\u5462\uff1f\u201d\u6155\u5bb9\u51b2\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u52fe\u7ed3\u674e\u5929\u626c\uff0c\u5c06 \u4ed6\u4eec\u90fd\u653e\u8d70\u4e86\uff0c\u8fd8\u6765\u95ee\u6211\u8981\u4eba\uff01\u201d\u7389\u7f57\u5239\u8fd9\u4e00\u6012\u975e\u540c\u5c0f\u9119\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u8c01\u662f\u674e\u5929\u626c\uff1f\u54b1\u4eec\u53ef \u4ece\u4e0d\u8ba4\u8bc6\uff01\u4f60\u8981\u60f3\u62b5\u8d56\uff0c\u90a3\u53ef\u4e0d\u6210\uff01\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u8ba4\u4e0d\u8ba4\u8bc6\uff0c\u4f60\u4eec\u7684\u4eba\u5168\u90fd\u8d70\u4e86\uff0c \u4f60\u4eec\u4e5f\u8be5\u628a\u6211\u7684\u4eba\u653e\u56de\u4e86\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8c01\u4fe1\u4f60\u7684\u9b3c\u8bdd\uff1f\u201d\u5251\u5c16\u5728\u91d1\u72ec\u5f02\u80cc\u5fc3\u8f7b\u8f7b\u4e00\u70b9\uff0c \u91d1\u72ec\u5f02\u6740\u732a\u822c\u53eb\u5c06\u8d77\u6765-\u7ea2\u82b1\u9b3c\u6bcd\u6012\u9053\uff1a\u201c\u6155\u5bb9\u51b2\u8fd9\u6b21\u4e0d\u662f\u780c\u8bcd\u54c4\u9a97\uff0c\u6211\u4eb2\u81ea\u5230\u9526\u8863\u536b\u770b \u8fc7\u3002\u4f60\u4eec\u4e0d\u4fe1\uff0c\u660e\u5929\u53ef\u770b\u7f09\u6355\u674e\u5929\u626c\u548c\u90a3\u56db\u4e2a\u72af\u4eba\u5f52\u6848\u7684\u544a\u793a\u3002\u201d\u7389\u7f57\u5239\u4ecd\u7136\u662f\u51b7\u7b11\u9053\uff1a \u201c\u6709\u4eba\u6362\u4eba\uff0c\u6ca1\u4eba\u6495\u7968\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u6012\u4e0d\u53ef\u6291\uff0c\u62d0\u6756\u4e00\u4e3e\uff0c\u5c31\u60f3\u548c\u7389\u7f57\u5239\u62da\u547d\u3002\u94c1\u98de\u9f99\u9053\uff1a \u201c\u88f3\u513f\uff0c\u628a\u91d1\u8001\u602a\u4ea4\u56de\u7ed9\u5979\uff01\u201d\u7389\u7f57\u5239\u957f\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f46\u4e5f\u8be5\u7559\u70b9\u8bb0\u53f7\uff01\u201d\u5251\u5c16\u4e00 \u5212\uff0c\u5728\u91d1\u72ec\u5f02\u7684\u80a9\u4e0a\u4e00\u6311\uff0c\u628a\u4ed6\u7684\u7435\u7436\u9aa8\u6311\u65ad\u3002\u7ec3\u6b66\u4e4b\u4eba\uff0c\u8fd9\u7435\u7436\u9aa8\u751a\u4e3a\u91cd\u8981\uff0c\u82e5\u7136\u88ab\u6311\u65ad \u4e86\uff0c\u529b\u6c14\u5c31\u4f7f\u4e0d\u51fa\u6765\uff0c\u867d\u6709\u6781\u597d\u6b66\u529f\u4e5f\u662f\u65e0\u7528\u3002\u800c\u4e14\u8fd9\u7435\u7436\u9aa8\u4e0d\u6bd4\u5176\u4ed6\u9aa8\u9abc\uff0c\u6311\u65ad\u4e4b\u5f8c\uff0c\u7eb5 \u6709\u6700\u597d\u7684\u7eed\u7b4b\u9a73\u9aa8\u4e4b\u672f\uff0c\u4e5f\u4e0d\u80fd\u5373\u65f6\u533b\u597d\uff0c\u975e\u5f97\u7528\u836f\u57f9\u8865\uff0c\u8ba9\u5b83\u6162\u6162\u751f\u957f\uff0c\u975e\u53c1\u5e74\u4e94\u6211\u4e0d\u80fd \u5b8c\u597d\u5982\u521d\u3002\u8fd9\u5c31\u662f\u8bf4\u91d1\u72ec\u5f02\u5728\u53c1\u4e94\u5e74\u5185\uff0c\u90a3\u662f\u4e0d\u80fd\u4f5c\u6076\u7684\u4e86\u3002 \u3000\u3000\u7389\u7f57\u5239\u4e00\u5251\u6311\u65ad\u91d1\u72ec\u5f02\u7684\u7435\u7436\u9aa8\uff0c\u628a\u4ed6\u671d\u7ea2\u82b1\u9b3c\u6bcd\u6000\u4e2d\u4e00\u63b7\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u6c14\u7f38\u53cc\u773c\uff0c\u63a5\u4e86 \u8fc7\u6765\uff0c\u4e00\u9a8c\u4ed6\u7684\u4f24\u5904\uff0c\u89c1\u9664\u4e86\u7435\u7436\u9aa8\u88ab\u6311\u4e4b\u5916\uff0c\u5e76\u6ca1\u5176\u4ed6\u6697\u4f24\u3002\u6012\u706b\u6536\u655b\uff0c\u5fc3\u60f3\uff1a\u8ba9\u8fd9\u8d3c\u6c49 \u5b50\u53d7\u53d7\u6559\u8bad\u4e5f\u597d\u3002\u628a\u4e08\u592b\u80cc\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u7389\u7f57\u5239\uff0c\u6211\u9886\u4f60\u7684\u60c5\uff0c\u54b1\u4eec\u4e4b\u95f4\u7684\u6069\u6028\uff0c\u4e00\u7b14\u52fe \u9500\uff01\u201d\u8eab\u5f62\u4e00\u8d77\uff0c\u98de\u63a0\u4e0b\u5c71\uff0c\u500f\u5ffd\u4e0d\u89c1\u3002 \u6155\u5bb9\u51b2\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u89c1\u7389\u7f57\u5239\u7b11\u563b\u7684\u7acb\u5728\u4ed6\u7684\u9762\u524d\uff0c\u9053\uff1a\u201c\u6155\u5bb9\u51b2\uff0c\u8fd9\u56de\u662f\u7b2c\u4e8c\u6b21\u89c1\u9762 \u4e86\u3002\u201d\u6155\u5bb9\u51b2\u5fc3\u9053\uff1a\u201c\u65e9\u77e5\u5982\u6b64\uff0c\u771f\u4e0d\u8be5\u542c\u90a3\u8001\u5996\u5987\u7684\u8bdd\uff0c\u5355\u8eab\u524d\u6765\u3002\u201d\u539f\u6765\u6155\u5bb9\u51b2\u6765\u65f6\u5fc3 \u60f3\uff1a\u51ed\u4ed6\u7684\u6b66\u529f\uff0c\u52a0\u4e0a\u7ea2\u82b1\u9b3c\u6bcd\uff0c\u5bf9\u4ed8\u94c1\u98de\u9f99\u548c\u7389\u7f57\u5239\uff0c\u90a3\u662f\u7a33\u64cd\u80dc\u5238\u3002\u60f3\u4e0d\u5230\u7ea2\u82b1\u9b3c\u6bcd\u5f97 \u4e86\u4e08\u592b\uff0c\u5374\u5148\u9003\u4e86\uff01 \u6155\u5bb9\u51b2\u6697\u6697\u53eb\u58f0\u82e6\u4e5f\uff0c\u53ea\u542c\u5f97\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u7b2c\u4e00\u56de\u89c1\u9762\u662f\u5728\u6768\u6d9f\u5bb6\u91cc\uff0c\u4f60\u4eec\u8981\u6697\u5bb3\u718a \u7ecf\u7565\uff0c\u6211\u4eec\u8981\u6765\u6349\u91d1\u8001\u602a\uff0c\u867d\u7136\u5927\u6253\u4e00\u987f\uff0c\u8fd8\u662f\u5f7c\u6b64\u65e0\u6d89\u3002\u8fd9\u56de\u53ef\u4e0d\u540c\u5566\uff01\u201d\u6155\u5bb9\u51b2\u9053\uff1a \u201c\u600e\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u718a\u7ecf\u7565\u662f\u6211\u7684\u597d\u670b\u53cb\u5566\uff0c\u4f60\u8981\u4f24\u5bb3\u4ed6\u6211\u53ef\u653e\u4f60\u4e0d\u8fc7\u3002\u201d\u6155\u5bb9\u51b2\u662f\u5bab \u4e2d\u7b2c\u4e00\u628a\u597d\u624b\uff0c\u867d\u7136\u5728\u94c1\u98de\u9f99\u4e0e\u7389\u7f57\u5239\u5a01\u80c1\u4e4b\u4e0b\uff0c\u867d\u7136\u5904\u65bc\u4e0b\u98ce\uff0c\u4ecd\u662f\u4e0d\u80af\u793a\u5f31\uff0c\u51b7\u51b7\u8bf4 \u9053\uff1a\u201c\u671d\u5ef7\u4e4b\u4e8b\u4e0d\u7528\u4f60\u7ba1\uff01\u201d\u7389\u7f57\u5239\u79c0\u7709\u4e00\u626c\uff0c\u9053\uff1a\u201c\u6211\u504f\u8981\u7ba1\uff01\u201d\u5587\u7684\u4e00\u5251\u523a\u53bb\uff0c\u6155\u5bb9\u51b2 \u4fa7\u8eab\u4e00\u5377\uff0c\u7389\u7f57\u5239\u8fde\u523a\u6570\u5251\uff0c\u6155\u5bb9\u51b2\u4e5f\u8fde\u8fdb\u6570\u62db\uff0c\u4e24\u4eba\u5404\u4e0d\u76f8\u8ba9\u3002\u94c1\u98de\u9f99\u9053\uff1a\u201c\u88f3\u513f\uff0c\u4f55\u5fc5 \u4e0e\u4ed6\u5455\u6c14\u3002\u201d\u7389\u7f57\u5239\u5251\u62db\u7a0d\u7f13\uff0c\u6155\u5bb9\u51b2\u6d8c\u8eab\u4e00\u8dc3\uff0c\u8df3\u4e0b\u5c71\u5761\u3002\u7389\u7f57\u5239\u9053\uff1a\u201c\u7239\u7239\u4f55\u6545\u653e \u4ed6\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u8fd9\u4e24\u65e5\u6765\u5df2\u7ecf\u4e86\u51e0\u573a\u6076\u6597\uff0c\u518d\u6253\u534a\u591c\uff0c\u7eb5\u5f97\u80dc\u4e5f\u8981\u53d7\u5185\u4f24\u3002\u201d\u7389\u7f57\u5239 \u4e00\u60f3\uff1a\u6155\u5bb9\u51b2\u6b66\u529f\u4e0d\u5728\u81ea\u5df2\u4e4b\u4e0b\uff0c\u82e5\u8981\u7239\u7239\u5e2e\u624b\uff0c\u80dc\u4e86\u4e5f\u4e0d\u5149\u5f69\uff0c\u4e5f\u4fbf\u7f62\u4e86\u3002 \u4e5f\u6b63\u60f3\u53bb\u89c1\u4ed6\u9053\u8c22\u3002\u201d\u4e24\u4eba\u4e00\u9053\u8fdb\u57ce\uff0c\u5230\u4e86\u6768\u6d9f\u5bb6\u4e2d\uff0c\u901a\u62a5\u8fdb\u53bb\uff0c\u6768\u6d9f\u7acb\u523b\u5ef6\u89c1\u3002\u7389\u7f57\u5239\u8d70 \u4e0a\u5385\u5802\uff0c\u5374\u4e0d\u89c1\u718a\u5ef7\u5f3c\uff0c\u6768\u6d9f\u9053\uff1a\u201c\u718a\u5927\u4eba\u5df2\u8f9e\u5b98\u5f52\u91cc\u4e86\u3002\u4ed6\u7b49\u4f60\u4e0d\u6765\uff0c\u53eb\u6211\u544a\u8bc9\u4f60\u4eec\uff0c\u4f60 \u4eec\u5c06\u6765\u82e5\u8def\u8fc7\u6e56\u5317\u6c5f\u590f\uff0c\u53ef\u4ee5\u987a\u4fbf\u628a\u90a3\u5bf9\u624b\u5957\u9001\u56de\u3002\u4f46\u4e5f\u4e0d\u5fc5\u4e13\u4e3a\u6b64\u4e8b\u800c\u53bb\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a \u201c\u718a\u7ecf\u7565\u5bb6\u5728\u6c5f\u590f\uff1f\u201d\u6768\u6d9f\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u7389\u7f57\u5239\u53eb\u8d77\u6765\u9053\uff1a\u201c\u8fd9\u4e2a\u5c0f\u7687\u5e1d\u771f\u4e0d\u61c2\u4e8b\uff0c\u600e\u80fd \u8ba9\u4ed6\u8f9e\u5b98\uff1f\u201d\u6768\u6d9f\u82e6\u7b11\u9053\uff1a\u201c\u671d\u5ef7\u4e4b\u4e8b\uff0c\u4f60\u4eec\u5c31\u5f04\u4e0d\u660e\u767d\u4e86\uff01\u201d\u8fd9\u8bdd\u548c\u6155\u5bb9\u51b2\u6240\u8bf4\u7684\u8bdd\u5927\u540c \u5c0f\u5f02\uff0c\u7389\u7f57\u5239\u6697\u6697\u751f\u6c14\uff0c\u53ef\u662f\u60f3\u5230\u6768\u6d9f\u548c\u6155\u5bb9\u51b2\u5230\u5e95\u5927\u4e0d\u76f8\u540c\uff0c\u4e5f\u4fbf\u5fcd\u7740\u4e0d\u53d1\u4f5c\u4e86\u3002 \u3000\u3000\u539f\u6765\u718a\u5ef7\u5f3c\u9012\u4e0a\u8f9e\u5448\uff0c\u4e0d\u8fc7\u662f\u60f3\u8bd5\u63a2\u7687\u5e1d\u7684\u5fc3\u610f\uff0c\u594f\u7ae0\u4e00\u4e0a\uff0c\u5148\u5230\u5ba2\u6c0f\u624b\u91cc\uff0c\u770b\u4e86\u4e4b \u5f8c\uff0c\u6b63\u4e2d\u4e0b\u6000\u3002\u5bf9\u7531\u6821\u9053\uff1a\u201c\u718a\u5ef7\u5f3c\u8fd9  \u7f57\u54e9\u7f57\u5506\uff0c\u8ba9\u4ed6\u8d70\u5427\u3002\u201d\u7531\u6821\u9053\uff1a\u201c\u7236\u7687\u8bf4\u8fc7\uff0c\u718a \u5ef7\u5f3c\u662f\u671d\u5ef7\u680b\u6881\uff0c\u600e\u53ef\u8ba9\u4ed6\u8f9e\u804c\u3002\u201d\u5ba2\u6c0f\u7b11\u9053\uff1a\u201c\u7531\u54e5\u513f\uff0c\u4f60\u5c31\u53ea\u77e5\u9053\u7236\u7687\u7684\u8bdd\uff0c\u6b8a\u4e0d\u77e5\u6b64 \u4e00\u65f6\u5f7c\u4e00\u65f6\uff0c\u5982\u4eca\u53ef\u4ee5\u8eab\u5f53\u7edf\u5e05\u4e4b\u4efb\u8005\uff0c\u5927\u6709\u4eba\u5728\u3002\u800c\u4e14\u4ee4\u4e00\u4eba\u4e13\u6743\u8fc7\u4e45\uff0c\u592a\u963f\u5012\u6301\uff0c\u4e5f\u975e \u671d\u5ef7\u4e4b\u798f\u3002\u201d\u7531\u6821\u9053\uff1a\u201c\u5148\u671d\u91cd\u81e3\uff0c\u4e0d\u4fbf\u514d\u4ed6\u519b\u804c\u3002\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u662f\u4ed6\u81ea\u5df1\u8981\u8d70\uff0c\u4e0e\u4f60\u4f55 \u5173\uff1f\u201d\u53c8\u9053\uff1a\u201c\u718a\u5ef7\u5f3c\u5728\u5916\u9762\u8bf4\uff0c\u660e\u671d\u7684\u6c5f\u5c71\u5168\u662f\u9760\u4ed6\uff0c\u4f60\u53d7\u5f97\u7740\u8fd9\u53e3\u6c14\u9ebd\uff1f\u800c\u4e14\u4ed6\u8fd9\u4eba\u52a8 \u8f84\u4ee5\u5fe0\u81e3\u81ea\u547d\uff0c\u77e5\u9053\u4f60\u7684\u80e1\u95f9\uff0c\u52bf\u5fc5\u53c8\u6765\u7f57\u5506\uff0c\u4f60\u505a\u7687\u5e1d\u4e5f\u505a\u5f97\u4e0d\u5feb\u6d3b\u3002\u201d\u7531\u6821\u53d7\u4e86\u5ba2\u6c0f\u86ca \u60d1\uff0c\u95ee\u9053\uff1a\u201c\u8fd8\u6709\u8c01\u53ef\u4ee5\u7ecf\u7565\u8fbd\u4e1c\uff1f\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u636e\u9b4f\u5fe0\u8d24\u8bf4\uff0c\u8881\u5e94\u6cf0\u5c31\u662f\u4e2a\u5927\u5c06\u4e4b\u624d\u3002\u201d \u7531\u6821\u8bb0\u8d77\u8fd9\u4e2a\u8881\u5e94\u6cf0\u66fe\u9001\u8fc7\u4ed6\u5341\u7b3c\u753b\u7709\u9e1f\uff0c\u5370\u8c61\u751a\u597d\uff0c\u4fbf\u5728\u718a\u5ef7\u5f3c\u7684\u548b\u5448\u4e0a\u6279\u4e86\u4e2a\u201c\u51c6\u201d \u5b57\uff0c\u53ef\u601c\u718a\u5ef7\u5f3c\u8fd9\u6b21\u56de\u6765\uff0c\u8fde\u7687\u5e1d\u7684\u9762\u4e5f\u6ca1\u89c1\u7740\uff0c\u4fbf\u6389\u4e86\u8fbd\u4e1c\u7ecf\u7565\u7684\u5b98\u54e6\uff0c\u4e00\u6c14\u4e4b\u4e0b\uff0c\u5728\u8f9e \u5448\u53d1\u4e0b\u7684\u7b2c\u4e8c\u5929\uff0c\u4fbf\u5e26\u5cb3\u9e23\u73c2\u548c\u738b\u8d5e\u56de\u5bb6\u79cd\u5730\u53bb\u4e86\u3002 \u3000\u3000\u7389\u7f57\u5239\u542c\u8bf4\u718a\u5ef7\u5f3c\u5df2\u8d70\uff0c\u5927\u4e3a\u5931\u671b\u3002\u94c1\u98de\u9f99\u9053\uff1a\u201c\u5cb3\u9e23\u73c2\u4e5f\u8ddf\u4ed6\u8d70\u4e86\u5417\uff1f\u201d\u94c1\u98de\u9f99\u5bf9\u5cb3 \u545c\u73c2\u62d2\u5a5a\u4e4b\u4e8b\uff0c\u59cb\u7ec8\u803f\u803f\u65bc\u6000\u3002\u6768\u6d9f\u9053\uff1a\u201c\u90fd\u8d70\u4e86\u3002\u4e0d\u6b62\u662f\u5cb3\u53c2\u8d5e\uff0c\u5353\u516c\u5b50\u548c\u4ed6\u7684\u6b66\u5f53\u6d3e\u540c \u95e8\uff0c\u90fd\u968f\u7740\u8d70\u4e86\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u90a3\u9ebd\uff0c\u767d\u77f3\u9053\u4eba\u5462\uff1f\u201d\u6768\u6d9f\u9053\uff1a\u201c\u90a3\u4e2a\u767d\u77f3\u9053\u4eba\u4e86\u554a\uff0c\u4f60 \u662f\u8bf4\u90a3\u65e5\u6765\u7684\u90a3\u4e2a\u9053\u58eb\u5427\uff1f\u4ed6\u4e5f\u968f\u7740\u8d70\u4e86\uff0c\u8fd8\u6709\u4ed6\u7684\u5973\u513f\u5462\u3002\u201d\u7389\u7f57\u5239\u4e00\u542c\uff0c\u77e5\u9053\u7ea2\u82b1\u9b3c\u6bcd \u6240\u8a00\u975e\u5047\uff0c\u5f53\u4e0b\u4fbf\u4e0e\u6768\u6d9f\u9053\u522b\u3002\u6768\u6d9f\u5ffd\u9053\uff1a\u201c\u5973\u82f1\u96c4\u662f\u56de\u9655\u5317\u5427\uff1f\u4e0b\u5b98\u6709\u4e00\u8a00\u76f8\u529d\uff0c\u73b0\u4e0b\u671d \u5ef7\u6b63\u8c03\u52a8\u5927\u519b\uff0c\u8981\u5230  \u5317\u527f\u532a\uff0c\u5973\u82f1\u96c4\u82e5\u662f\u548c\u90a3\u4e9b\u7eff\u6797\u82f1\u96c4\u76f8\u719f\uff0c\u8fd8\u662f\u529d\u4ed6\u4eec\u65e9\u53d7\u62db\u5b89\u7684 \u597d\u3002\u201d\u7389\u7f57\u5239\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u94c1\u98de\u9f99\u6025\u5fd9\u628a\u5979\u626f\u8d70\u3002 \u3000\u3000\u518d\u8bf4\u767d\u77f3\u9053\u4eba\u5931\u4e86\u5973\u513f\uff0c\u6781\u4e3a\u7126\u6025\uff0c\u53ef\u662f\u81ea\u5df1\u4f24\u8fd8\u672a\u6108\uff0c\u6beb\u65e0\u529e\u6cd5\u3002\u4e0d\u60f3\u7b2c\u4e8c\u65e5\u665a\u95f4\uff0c \u674e\u5929\u626c\u7236\u5b50\u3001\u9f99\u5578\u4e91\u548c\u4ed6\u7684\u5973\u513f\u4ee5\u53ca\u674e\u5c01\uff0c\u90fd\u56de\u6765\u4e86\u3002\u767d\u77f3\u9053\u4eba\u559c\u4e4b\u4e0d\u80dc\u3002\u674e\u5929\u626c\u8bf4\u51fa\u60c5 \u7531\uff0c\u767d\u77f3\u9053\u4eba\u6168\u7136\u8bf4\u9053\uff1a\u201c\u59b9\u5a7f\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u8fd9\u56de\u6211\u5728\u820d\u59b9\u9762\u524d\uff0c\u5b9a\u5f53\u4e3a\u4f60\u8bf4\u9879\u3002\u201d\u674e\u5929\u626c\u53c8 \u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4e00\u9003\u8d70\uff0c\u671d\u5ef7\u5fc5\u7136\u7f09\u62ff\u3002\u800c\u4e14\u542c\u6155\u5bb9\u51b2\u53e3\u6c14\uff0c\u8fde\u4f60\u4e5f\u602a\u5728\u91cc\u5934\uff0c\u54b1\u4eec\u8fd8\u662f\u660e\u65e5 \u4e00\u65e9\uff0c\u5c31\u79bb\u4eac\u56de\u53bb\u5427\u3002\u201d\u767d\u77f3\u9053\uff1a\u201c\u8fd9\u91cc\u5927\u4e8b\u5df2\u4e86\uff0c\u81ea\u7136\u5e94\u5f53\u56de\u53bb\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u4e0e\u5cb3\u9e23\u73c2\u4ea4\u60c5\u751a\u597d\uff0c\u8fde\u591c\u8dd1\u53bb\u8f9e\u884c\uff0c\u77e5\u9053\u718a\u5ef7\u5f3c\u4e5f\u8981\u56de\u6e56\u5317\u8001\u5bb6\u3002\u5353\u4e00\u822a\u9053\uff1a \u201c\u671d\u4e2d\u5978\u515a\uff0c\u5bf9\u7ecf\u7565\u751a\u4e3a\u5992\u6068\uff0c\u867d\u7136\u8f9e\u4e86\u5b98\u804c\uff0c\u53ea\u6050\u4ed6\u4eec\u8fd8\u8981\u52a0\u5bb3\uff0c\u54b1\u4eec\u4e00\u9053\u8d70\u5427\u3002\u201d\u5cb3\u9e23 \u73c2\u4e5f\u6050\u8def\u4e0a\u6709\u4e8b\uff0c\u72ec\u529b\u96be\u6491\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u518d\u597d\u4e0d\u8fc7\uff0c\u4f60\u4eec\u56de\u6b66\u5f53\u5c71\u6b63\u597d\u548c\u6211\u4eec\u4e00\u8def\uff0c\u5c31\u662f \u4f60\u90a3\u4f4d\u5e08\u53d4\u5927\u4eba\u4e0d\u597d\u76f8 \u4e0e\u3002 \u201d \u3000\u3000\u4e24\u4eba\u8bf4\u597d\u4e4b\u5f8c\uff0c\u718a\u5ef7\u5f3c\u548c\u767d\u77f3\u9053\u4eba\u90fd\u540c\u610f\u4e86\u3002\u4e24\u5925\u5408\u6210\u4e00\u5925\uff0c\u4e00\u8def\u540c\u884c\u3002\u53ea\u662f\u5cb3\u545c\u73c2\u548c \u767d\u77f3\u9053\u4eba\u76f8\u5904\u4e0d\u597d\uff0c\u56e0\u6b64\u5206\u4e3a\u4e24\u62e8\uff0c\u718a\u5ef7\u5f3c\uff0c\u5cb3\u545c\u73c2\uff0c\u738b\u8d5e\u3001\u674e\u5929\u626c\uff0c\u674e\u7533\u65f6\u3001\u9f99\u5578\u4e91\u7b49 \u4eba\uff0c\u8d70\u5728\u524d\u5934\uff0c\u4f46\u4e24\u62e8\u5165\u76f8\u8ddd\u4e5f\u4e0d\u8fc7\u6b63\u4e03\u91cc\u8def\u3002\u53ef\u4ee5\u4e92\u76f8\u7167\u5e94\u3002\u665a\u4e0a\u4ecd\u662f\u4e00\u540c\u4f4f\u5e97\u3002\u8d70\u5c71\u6cb3 \u5317\u7701\u4ffa\uff0c\u6b66\u5f53\u5c71\u9ec4\u53f6\u9053\u4eba\u5df2\u6d3e\u4e86\u7ea2\u4e91\u9752\u84d1\u4e24\u4f4d\u5e08\u5f1f\u524d\u6765\u8fce\u63a5\u3002\u539f\u6765\u6b66\u5f53\u6d3e\u6d88\u606f\u751a\u662f\u7075\u901a\uff0c\u5df2 \u77e5\u767d\u77f3\u9053\u4eba\u548c\u5353\u4e00\u822a\u5728\u4eac\u95f9\u51fa\u4e8b\u60c5\uff0c\u9ec4\u53f6\u9053\u4eba\u751f\u6015\u4ed6\u4eec\u6709\u5931\uff0c\u6240\u4ee5\u628a\u6b66\u5f53\u4e94\u8001\u4e2d\u7684\u4e8c\u8001\u90fd\u6d3e \u51fa\u6765\u4e86\u3002 \u3000\u3000\u4e00\u8def\u4e0a\u767d\u77f3\u9053\u4eba\u8bf4\u8d77\u7389\u7f57\u5239\u7ea6\u4ed6\u6bd4\u5251\u4ee5\u53ca\u201c\u770b\u4e0d\u8d77\u201d\u6b66\u5f53\u6d3e\u7684\u4e8b\uff0c\u5353\u4e00\u822a\u90fd\u4e0d\u8a00\u8bed\u3002\u7ea2 \u4e91\u9053\u4eba\u5403\u8fc7\u7389\u7f57\u5239\u7684\u5927\u4e8f\uff0c\u66ff\u5e08\u5f1f\u6124\u6124\u4e0d\u5e73\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u5973\u9b54\u5934\u975e\u632b\u5979\u7684\u9510\u6c14\u4e0d\u53ef\u3002\u201d\u5353\u4e00 \u822a\u4ecd\u4e0d\u4f5c\u58f0\u3002\u767d\u77f3\u9053\u4eba\u6a2a\u4ed6\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u4eec\u6b66\u5f53\u6d3e\u4eba\uff0c\u82e5\u540c\u5fc3\u5408\u529b\uff0c\u5929\u4e0b\u4f55\u4eba\u6562\u5c0f\u89d1\u6211 \u4eec\u3002\u201d\u8bf4\u7f62\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u4e00\u884c\u4eba\u4f17\uff0c\u7eed\u5411\u5357\u884c\u3002\u8fd9\u4e00\u7fa4\u4eba\u4e2a\u4e2a\u90fd\u662f\u6b66\u6797\u9ad8\u624b\uff0c\u5c31\u7b97\u9b4f\u5fe0\u8d24\u60f3\u6d3e\u4eba\u6697\u5bb3\uff0c\u4e5f\u4e0d\u6562\u52a8 \u624b\u3002\u4e00\u8def\u4e0a\u98ce\u5e73\u6d6a\u9759\uff0c\u8fc7\u4e86\u51e0\u65e5\uff0c\u7ecf\u8fc7\u5d69\u5c71\uff0c\u674e\u5929\u626c\u8981\u4e0a\u5c71\u5bfb\u8bbf\u524d\u59bb\uff0c\u767d\u77f3\u9053\u4eba\u7b49\u5f53\u7136\u968f\u7740 \u4e0a\u53bb\u3002\u5cb3\u545c\u73c2\u8d81\u6b64\u673a\u4f1a\uff0c\u4e5f\u8981\u4e0a\u5c71\u89c1\u89c1\u5c11\u6797\u5bfa\u7684\u955c\u660e\u957f\u8001\uff0c\u65bc\u662f\u5927\u5bb6\u4e00\u540c\u4e0a\u5c71\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u662f\u51ac\u5c3d\u6625\u6765\uff0c\u4e00\u8def\u4e0a\u4f46\u89c1\u5c0f\u9e1f\u8fce\u4eba\uff0c\u5c71\u82b1\u542b\u7b11\uff0c\u674e\u5929\u626c\u8fd9\u65f6\u548c\u767d\u77f3\u9053\u4eba\u4e00\u62e8\uff0c\u5fc3 \u5883\u548c\u4e0a\u6b21\u4e0a\u5c71\u4e4b\u65f6\u5927\u4e0d\u76f8\u540c\u3002\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u65b9\u77e5\u5c71\u5c45\u91ce\u5904\uff0c\u5c24\u80dc\u65bc\u5bab\u6bbf\u743c\u697c\u3002\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c \u7ea2\u4e91\u9053\u4eba\u5ffd\u7136\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u53eb\u8d77\u6765\u9053\uff1a\u201c\u4ec0\u9ebd\u4eba\u8eab\u6cd5\u5982\u6b64\u5feb\u75be\uff01\u201d\u4f17\u4eba\u767b\u9ad8\u4e00\u671b\uff0c\u4f46\u89c1\u5c71 \u4e0b\u4e00\u6761\u4eba\u5f71\uff0c\u98de\u5954\u800c\u6765\uff0c\u5feb\u75be\u4e4b\u6781\uff0c\u5b9b\u5982\u4e00\u9053\u767d\u70df\uff0c\u6eda\u6eda\u800c\u81f3\uff01\u674e\u5929\u626c\u7236\u5b50\u548c\u5353\u4e00\u822a\u4fdd\u62a4\u767d \u77f3\u9053\u4eba\u8d70\u5728\u524d\u5934\uff0c\u7ea2\u4e91\u9752  \u4e8c\u4eba\u62d4\u5251\u6bbf\u5f8c\uff0c\u4e0d\u591a\u4e00\u523b\uff0c\u90a3\u9053\u201c\u767d\u70df\u201d\u5df2\u5347\u81f3\u5c71\u4e0a\uff0c\u7ea2\u4e91\u9752 \u4e8c\u4eba\u5f20\u773c\u4e00\u770b\uff0c\u6765\u7684\u7adf\u7136\u662f\u7389\u7f57\u5239\u8fd9\u4e2a\u51a4\u5bb6\u3002 \u3000\u3000\u7ea2\u4e91\u9053\u4eba\u5927\u6012\uff0c\u4e0d\u95ee\u60c5\u7531\uff0c\u5237\u7684\u4e00\u5251\uff0c\u5411\u524d\u523a\u53bb\uff0c\u559d\u9053\uff1a\u201c\u7389\u7f57\u5239\uff0c\u4f60\u6b3a\u8d1f\u6211\u4eec\u6b66\u5f53\u6d3e \u592a\u751a\uff0c\u767d\u77f3\u5e08\u5144\u672a\u80fd\u4e0e\u4f60\u6bd4\u5251\uff0c\u7531\u6211\u4ee3\u5427\uff01\u201d\u7ea2\u4e91\u9053\u4eba\u8fd8\u4ee5\u4e3a\u7389\u7f57\u5239\u662f\u6765\u8ffd\u8d76\u767d\u77f3\u9053\u4eba\uff0c\u5176 \u5b9e\u7389\u7f57\u5239\u548c\u94c1\u98de\u9f99\u5374\u662f\u6765\u8ffd\u718a\u5ef7\u5f3c\u548c\u5cb3\u9e23\u73c2\uff0c\u7389\u7f57\u5239\u6027\u5b50\u65e2\u6025\uff0c\u8f7b\u529f\u53c8\u9ad8\uff0c\u6240\u4ee5\u5148\u8ffd\u4e86\u6765\u3002 \u3000\u3000\u7389\u7f57\u5239\u89c1\u7ea2\u4e91\u9053\u4eba\u4e0d\u95ee\u60c5\u7531\uff0c\u4e71\u523a\u4e71\u6233\uff0c\u52c3\u7136\u5927\u6012\uff0c\u4e5f\u5c31\u4e0d\u628a\u6765\u610f\u8bf4\u660e\uff0c\u51b7\u7b11\u8bf4\u9053\uff1a \u201c\u7ea2\u4e91\u9053\u4eba\uff0c\u4f60\u662f\u6211\u624b\u4e0b\u8d25\u5c06\uff0c\u8fd8\u6bd4\u4ec0\u9ebd\uff1f\u201d\u7ea2\u4e91\u8d8a\u53d1\u706b\u8d77\uff0c\u628a\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u593a\u547d\u5251\u4f7f\u5f97\u51cc \u5389\u65e0\u524d\uff01\u5353\u4e00\u822a\u6276\u7740\u5e08\u53d4\u4e0d\u6562\u4e0a\u524d\u529d\u67b6\uff0c\u7a7a\u81ea\u7740\u6025\u3002 \u3000\u3000\u7389\u7f57\u5239\u89c1\u7ea2\u4e91\u9053\u4eba\u4e0d\u77e5\u8fdb\u9000\uff0c\u5a07\u7b11\u4e00\u58f0\uff0c\u6545\u610f\u4e0e\u4ed6\u76f8\u620f\uff0c\u5251\u6cd5\u4e00\u5c55\uff0c\u5b9b\u5982\u7389\u9f99\u592d\u77eb\uff0c\u76d8 \u65cb\u98de\u821e\uff0c\u628a\u7ea2\u4e91\u9053\u4eba\u7684\u5251\u5149\u88f9\u5728\u5f53\u4e2d\u3002\u7ea2\u4e91\u9053\u4eba\u7684\u5b9d\u5251\u51e0\u6b21\u8981\u7ed9\u5979\u51fb\u5f97\u8131\u624b\u98de\u53bb\uff0c\u9752\u5bf0\u9053\u4eba \u89c1\u4e0d\u662f\u8def\uff0c\u4e5f\u987e\u4e0d\u5f97\u6b66\u5f53\u4e94\u8001\u7684\u8eab\u4efd\uff0c\u62d4\u51fa\u5251\u6765\uff0c\u7adf\u7136\u4ee5\u4e8c\u654c\u4e00\uff0c\u4e0a\u524d\u5939\u653b\u3002 \u3000\u3000\u7389\u7f57\u5239\u529b\u654c\u6b66\u5f53\u4e8c\u8001\uff0c\u50b2\u7136\u4e0d\u60e7\uff0c\u4e00\u67c4\u5251\u4f7f\u5f97\u795e\u51fa\u9b3c\u6ca1\uff0c\u4f3c\u5b9e\u8fd8\u865a\uff0c\u4f3c\u865a\u5374\u5b9e\uff0c\u6bcf\u4e00\u62db \u90fd\u662f\u62db\u91cc\u85cf\u62db\uff0c\u5f0f\u4e2d\u5957\u5f0f\uff0c\u5251\u52bf\u5982\u8679\uff0c\u5947\u8be1\u83ab\u6d4b\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u7ea2\u4e91\u9752  \u4e8c\u4eba\u8054\u5251 \u5408\u6597\uff0c\u62da\u529b\u62b5\u6321\uff0c\u4e5f\u4e0d\u8fc7\u662f\u521a\u521a\u6253\u4e2a\u5e73\u624b\u3002 \u3000\u3000\u674e\u5929\u626c\u548c\u9f99\u5578\u4e91\u770b\u5f97\u51fa\u5947\uff0c\u9f99\u5578\u4e91\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4e2a\u5973\u5a03\u5b50\u7684\u5251\u6cd5\u600e\u9ebd\u8fd9\u6837\u5389\u5bb3\uff01\u201d\u767d\u77f3 \u9053\u4eba\u89c1\u4ed6\u4eec\u4e8c\u4eba\u5728\u65c1\u8bc4\u8bba\u5251\u6cd5\uff0c\u8d8a\u89c9\u989c\u9762\u65e0\u5149\uff0c\u6012\u9053\uff1a\u201c\u4e00\u822a\uff0c\u6211\u4e0d\u8981\u4f60\u6276\u3002\u4f60\u8fd8\u4e0d\u4e0a\u53bb\u52a9 \u4f60\u5e08\u53d4\u3002\u4eca\u65e5\u82e5\u53eb\u8fd9\u5996\u5973\u9003\u4e0b\u5c71\u53bb\uff0c\u54b1\u4eec\u6b66\u5f53\u6d3e\u8fd8\u89c1\u5f97\u4eba\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u4e5f\u89c9\u5f97\u7389\u7f57\u5239\u8ffd\u6765\u6311 \u6218\uff0c\u672a\u514d\u592a\u8fc7\u9a84\u7eb5\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u7389\u7f57\u5239\u83ab\u975e\u662f\u6765\u8ffd\u81ea\u5df2\u3002\u867d\u7136\u5fc3\u4e2d\u60f6\u6025\uff0c\u4f46\u4e5f\u9887\u4e3a\u5feb\u6170\u3002 \u767d\u77f3\u9053\u4eba\u53c8\u559d\u9053\uff1a\u201c\u4e00\u822a\uff0c\u4f60\u8fd8\u4e0d\u8868\uff0c\u8fd9\u5996\u5973\u662f\u672c\u95e8\u516c\u654c\uff0c\u4e0d\u5fc5\u548c\u5979\u8bb2\u4ec0\u9ebd\u6c5f\u6e56\u89c4\u77e9\u3002\u201d\u9f99 \u5578\u4e91\u5fc3\u4e2d\u4e0d\u503c\u767d\u77f3\u6240\u4e3a\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u8fd9\u5973\u5a03\u5b50\u80fd\u529b\u654c\u6b66\u5f53\u4e8c\u8001\uff0c\u5251\u6cd5\u53ef\u7b97\u5f53\u4eca\u7b2c\u4e00\u9ad8\u624b\uff0c \u6bc1\u4e86\u5979\u5c82\u4e0d\u53ef\u60dc\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u542c\u4e86\u8fd9\u8bdd\uff0c\u672c\u6765\u4e0d\u60f3\u4e0a\u524d\uff0c\u8fd9\u65f6\u66f4\u6545\u610f\u51dd\u8eab\u4e0d\u52a8\uff0c\u767d\u77f3\u6012\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u53bb\uff01\u201d\u5353 \u4e00\u822a\u65e0\u5948\uff0c\u53ea\u597d\u62d4\u5251\u4e0a\u524d\u3002\u8fd9\u65f6\u7389\u7f57\u5239\u8d8a\u6218\u8d8a\u52c7\uff0c\u5947\u62db\u5999\u7740\uff0c\u5c42\u51fa\u4e0d\u7a77\uff01\u628a\u7ea2\u4e91\u9752  \u4e8c\u4eba\u4ece \u5e73\u624b\u8feb\u5230\u4e0b\u98ce\uff0c\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u5353\u4e00\u822a\uff0c\u4f60\u4e5f\u8981\u6765\u9ebd\uff1f\u54c8\u54c8\uff0c\u6211\u4eca\u65e5\u8981\u4f1a\u5c3d\u6b66\u5f53\u9ad8\u624b\u4e86\uff01\u201d\u6b63 \u662f\uff1a\u4e00\u5251\u7eb5\u6a2a\u5357\u5317\uff0c\u4eca\u671d\u53c8\u663e\u795e\u901a\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002\u201c\u4e0a\u5377\u5b8c\u201d  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377\u2014\u2014\u7b2c\u5341\u4e03\u56de\u3000\u73e0\u5b9d\u62db\u5f3a\u3000\u4eba\u8352\u6797\u6076\u3000\u6597\u795e\u5a01\u6298\u3000\u9b54\u5973\u7fa4\u76d7\u503e\u5fc3 \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377 \u7b2c\u5341\u4e03\u56de \u73e0\u5b9d\u62db\u5f3a\u3000\u4eba\u8352\u6797\u6076 \u6597\u795e\u5a01\u6298\u3000\u9b54\u5973\u7fa4\u76d7\u503e\u5fc3 \u3000\u3000\u5353\u4e00\u822a\u8fdb\u9000\u4e24\u96be\uff0c\u6447\u6447\u6643\u6643\uff0c\u8d70\u4e24\u6b65\uff0c\u6b47\u4e00\u6b47\u3002\u7389\u7f57\u5239\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6765\u5440\uff0c\u6765\u5440\uff01\u201d\u5ffd \u542c\u5f97\u5cb3\u9e23\u73c2\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\uff0c\u4f4f\u624b\uff0c\u4f4f\u624b\uff01\u201d\u5353\u4e00\u822a\u4e58\u673a\u6b62\u6b65\u3002\u7389\u7f57\u5239\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1 \u5cb3\u545c\u73c2\u548c\u4e00\u4e2a\u8001\u548c\u5c1a\u5982\u98de\u8dd1\u6765\u3002 \u3000\u3000\u7389\u7f57\u5239\u6c14\u5f80\u4e0a\u51b2\uff0c\u4e00\u62db\u201c\u96ea\u5377\u82cd\u5c71\u201d\uff0c\u628a\u7ea2\u4e91\u9752\u4e8c\u4eba\u8feb\u9000\u53c1\u6b65\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u5cb3\u9e23\u73c2\uff0c\u4f60 \u9080\u4e86\u5e2e\u624b\u6765\u4e86\uff0c\u597d\u5440\uff0c\u54b1\u4eec\u518d\u75db\u75db\u5feb\u5feb\u6bd4\u4e00\u573a\u3002\u201d\u5251\u8bc0\u4e00\u634f\uff0c\u5237\u5237\u4e24\u5251\uff0c\u201c\u5206\u82b1\u62c2\u67f3\u201d\uff0c\u5de6 \u523a\u5cb3\u9e23\u73c2\uff0c\u53f3\u523a\u8001\u548c\u5c1a\u3002\u7389\u7f57\u5239\u6b63\u6253\u5230\u5174\u5934\uff0c\u5251\u52bf\u5c55\u5f00\uff0c\u4e0d\u53ef\u6536\u62fe\uff0c\u98d5\u98d5\u4e24\u5251\uff0c\u4fe8\u5982\u9a87\u7535\u5954 \u96f7\u3002\u4e0d\u6599\u9aa4\u7136\u4e4b\u95f4\u5ffd\u4f3c\u78b0\u7740\u4e00\u80a1\u5927\u529b\u53cd\u63a8\u8fc7\u6765\uff0c\u8033\u8fb9\u4f46\u542c\u5f97\u4e00\u58f0\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff01\u201d\u81ea\u5df1\u7684\u624b \u7adf\u4f3c\u7ed9\u4eba\u6267\u7740\u63a8\u4e86\u56de\u6765\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u6a2a\u5251\u5f53\u80f8\uff0c\u5c31\u4f3c\u4e13\u7a0b\u5411\u6765\u4eba\u62b1\u5251\u7b54\u793c\u4e00\u822c\u3002\u7389\u7f57\u5239\u5927\u5403 \u4e00\u60ca\uff0c\u53ea\u89c1\u90a3\u8001\u548c\u5c1a\u5408\u4ec0\u7b11\u9053\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff0c\u8fd9\u91cc\u7075\u5c71\u80dc\u5730\uff0c\u538c\u95fb\u6740\u4f10\u4e4b\u58f0\u3002\u5973\u83e9\u8428\u628a\u5251\u6536 \u4e0b\u6765\u5427\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u662f\u8c01\u4eba\uff1f\u201d\u6697\u4e2d\u8fd0\u6c14\uff0c\u6d3b\u52a8\u7b4b\u9aa8\uff0c\u6b63\u60f3\u518d\u8bd5\u8bd5\u90a3\u8001\u548c\u5c1a\u7684\u80fd \u4e3a\u3002\u5ffd\u53c8\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u94c1\u98de\u9f99\u5df2\u4e0a\u5230\u5c71\u4e0a\uff0c\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u7ec3\u513f\uff0c\u4e0d\u53ef\u65e0\u793c\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u6115\u7136\u6536\u5251\u3002\u90a3\u8001\u548c\u5c1a\u7a3d\u9996\u8bf4\u9053\uff1a\u201c\u94c1\u5c45\u58eb\u522b\u6765\u65e0\u6059\uff01\u201d\u94c1\u98de\u9f99\u62b1\u62f3\u4f5c\u63d6\u9053\uff1a\u201c\u955c \u660e\u5e08\uff0c\u8bf7\u6055\u5c0f\u5973\u83bd\u649e\u3002\u201d\u7389\u7f57\u5239\u542c\u4e86\u4e49\u7236\u4e4b\u8a00\uff0c\u624d\u77e5\u9762\u524d\u8fd9\u4e2a\u548c\u5c1a\uff0c\u7adf\u662f\u5c11\u6797\u5bfa\u7684\u4e3b\u6301\uff0c\u4e0e \u5f53\u5e74\u7684\u7d2b\u9633\u9053\u957f\u5e76\u79f0\u7684\u955c\u660e\u957f\u8001\u3002\u5fc3\u9053\uff1a\u201c\u5514\uff0c\u8fd9\u4e2a\u8001\u548c\u5c1a\u5012\u4e0d\u662f\u6d6a\u5f97\u865a\u540d\uff0c\u6bd4\u6b66\u5f53\u4e94\u8001\u5f3a \u5f97\u591a\u4e86\u3002\u201d \u3000\u3000\u955c\u660e\u9053\uff1a\u201c\u8d2b\u5055\u5728\u7d2b\u9633\u9053\u957f\u4e0e\u5929\u90fd\u5c45\u58eb\u4e4b\u5f8c\uff0c\u53c8\u5f97\u89c1\u6b66\u6797\u5251\u672f\u5927\u653e\u5f02\u5f69\uff0c\u5b9e\u5c5e\u6709\u7f18\u3002\u8bf7 \u94c1\u5c45\u58eb\u4e0e\u4ee4\u5ad2\u5230\u5c0f\u5bfa\u4e00\u53d9\u5982\u4f55\u3002\u201d\u7389\u7f57\u5239\u542c\u4ed6\u79f0\u8d5e\u81ea\u5df1\u7684\u5251\u672f\uff0c\u5fc3\u4e2d\u9887\u4e3a\u9ad8\u5174\u3002\u94c1\u98de\u9f99\u89c1\u5cb3 \u9e23\u73c2\u5728\u65c1\uff0c\u5374\u60f3\u8d77\u4ed6\u6c14\u8d70\u81ea\u5df2\u5973\u513f\u4e4b\u4e8b\uff0c\u4e0d\u7981\u201c\u54fc\u201d\u4e86\u4e00\u58f0\uff0c\u5cb3\u9e23\u73c2\u53eb\u4e86\u58f0\u201c\u94c1\u8001\u524d\u8f88\u201d\u3002 \u94c1\u98de\u9f99\u677f\u9762\u4e0d\u7406\uff0c\u5cb3\u545c\u73c2\u751a\u662f\u5c34\u5c2c\uff0c\u955c\u660e\u957f\u8001\u83ab\u540d\u6240\u4ee5\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u718a\u7ecf\u7565\u7684\u53c2\u8d5e\uff0c\u53c8\u662f \u5929\u90fd\u5c45\u58eb\u7684\u552f\u4e00\u4f20\u4eba\uff0c\u5251\u672f\u7cbe\u5999\uff0c\u4e0e\u4eca\u5ad2\u582a\u79f0\u6b66\u6797\u53cc\u74a7\u3002\u201d\u7389\u7f57\u5239\u51b7\u7b11\u9053\uff1a\u201c\u5251\u672f\u867d\u7136\u4e0d \u9519\uff0c\u4eba\u54c1\u5374\u662f\u7a0d\u5dee\u3002\u201d\u955c\u660e\u957f\u8001\u4e00\u6014\uff0c\u4f46\u89c1\u5cb3\u545c\u73c2\u9762\u7ea2\u8fc7\u8033\uff0c\u6599\u77e5\u5176\u4e2d\u5fc5\u6709\u522b\u60c5\uff0c\u7b11\u4e86\u4e00 \u7b11\uff0c\u9053\uff1a\u201c\u718a\u7ecf\u7565\u5c31\u5728\u5bfa\u4e2d\uff0c\u4ed6\u521a\u624d\u8fd8\u63d0\u8d77\u4f60\u4eec\u7236\u5973\u4e24\u4eba\u5462\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u597d\uff0c\u6211\u6b63\u60f3\u8fd8 \u4ed6\u624b\u5957\u3002\u201d\u62c9\u7740\u94c1\u98de\u9f99\u968f\u955c\u660e\u4fbf\u8d70\u3002 \u3000\u3000\u539f\u6765\u718a\u5ef7\u5f3c\u548c\u5cb3\u545c\u73c2\u7b49\u5148\u5230\u5c11\u6797\uff0c\u5750\u4e0b\u4e0d\u4e45\uff0c\u4fbf\u95fb\u5f97\u5916\u9762\u6740\u4e4b\u58f0\uff0c\u5cb3\u545c\u73c2\u6599\u662f\u7389\u7f57\u5239\u8ffd \u6765\uff0c\u6240\u4ee5\u62c9\u955c\u660e\u957f\u8001\u51fa\u5916\u529d\u67b6\u3002 \u3000\u3000\u955c\u660e\u957f\u8001\u53c8\u548c\u767d\u77f3\u3001\u7ea2\u4e91\uff0c\u9752\u53c1\u4eba\u6253\u4e86\u62db\u547c\uff0c\u8bf7\u4ed6\u4eec\u540c\u4e0a\u5c11\u6797\uff0c\u767d\u77f3\u9053\u4eba\u90a3\u91cc\u80af\u53bb\uff0c\u72e0 \u72e0\u7684\u76ef\u4e86\u7389\u7f57\u5239\u4e00\u773c\uff0c\u8f6c\u8fc7\u9762\u6765\uff0c\u5a49\u8f9e\u62d2\u7edd\u4e86\u955c\u660e\u957f\u8001\u7684\u9080\u8bf7\uff0c\u8bf4\u9053\uff1a\u201c\u8d2b\u9053\u6709\u4e8b\u8981\u5148\u89c1\u820d \u59b9\u3002\u201d\u955c\u660e\u957f\u8001\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u7b49\u4e0b\u8bf7\u548c\u6148\u6167\u5e08\u592a\u4e00\u540c\u6765\u5427\u3002\u201d\u65bc\u662f\u5206\u6210\u4e24\u8def\uff0c\u767d\u77f3\u9053\u4eba \u548c\u674e\u5929\u626c\u9f99\u5578\u4e91\u7b49\u4e0a\u592a\u5ba4\u5c71\uff0c\u955c\u660e\u957f\u8001\u5219\u5e26\u7389\u7f57\u5239\u7b49\u56de\u5c11\u6797\u3002 \u3000\u3000\u7389\u7f57\u5239\u968f\u955c\u660e\u957f\u8001\u8fdb\u5165\u5c11\u6797\u5bfa\u4e2d\uff0c\u5230\u4e86\u89e3\u884c\u7cbe\u820d\uff0c\u53ea\u89c1\u5c0a\u80dc\u5e08\u6b63\u5728\u966a\u718a\u7ecf\u7565\u95f2\u8bdd\u3002\u7389\u7f57 \u5239\u6301\u624b\u5957\u9012\u4e0a\uff0c\u718a\u5ef7\u5f3c\u7b11\u9053\uff1a\u201c\u7ec3\u59d1\u5a18\uff0c\u4f60\u5343\u91cc\u8ffd\u6765\uff0c\u8fd8\u6b64\u5fae\u7269\uff0c\u771f\u6709\u53e4\u4eba\u4e4b\u98ce\u3002\u201d\u7389\u7f57\u5239 \u9053\uff1a\u201c\u4ec0\u9ebd\u5fae\u7269\uff1f\u662f\u5b9d\u7269\u624d\u771f\uff0c\u6211\u5168\u9760\u5b83\u624d\u6253\u8d25\u4e86\u7ea2\u82b1\u9b3c\u6bcd\u3002\u82e5\u8bba\u672c\u8eab\u529f\u592b\uff0c\u6211\u8fd8\u771f\u4e0d\u662f\u90a3 \u8001\u5996\u5987\u7684\u5bf9\u624b\u5462\uff01\u201d\u7389\u7f57\u5239\u8bf4\u5f97\u6781\u4e3a\u76f4\u723d\uff0c\u718a\u5ef7\u5f3c\u7ed9\u5979\u5f15\u5f97\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u82e5\u5b9a\u8981\u9053 \u8c22\uff0c\u90a3\u4e5f\u4e0d\u5fc5\u8c22\u6211\uff0c\u5e94\u8be5\u8c22\u4ed6\u3002\u201d\u8fb9\u8bf4\u8fb9\u5c06\u624b\u5957\u9012\u56de\u7ed9\u5cb3\u545c\u73c2\u3002\u7389\u7f57\u5239\u5927\u51fa\u610f\u5916\uff0c\u6014\u4e86\u4e00 \u6014\uff0c\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u8fd9\u70b9\u5c0f\u4e8b\uff0c\u90a3\u503c\u5f97\u63d0\u3002\u201d\u94c1\u98de\u9f99\u6380\u987b\u8bf4\u9053\uff1a\u201c\u5927\u5fb7\u4e0d\u8a00\u62a5\uff0c\u6c5f\u6e56\u4e0a\u8bb2\u7a76\u7684 \u662f\u6069\u6028\u5206\u660e\uff0c\u7ec3\u513f\uff0c\u4e8b\u60c5\u5df2\u4e86\uff0c\u54b1\u4eec\u8d70\u5427\u3002\u201d\u5c0a\u80dc\u5e08\u8be7\u9053\uff1a\u201c\u94c1\u5c45\u58eb\uff0c\u4f60\u521a\u521a\u6765\u5230\uff0c\u53c8\u8981\u8d70 \u4e86\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u76f8\u77e5\u5728\u5fc3\uff0c\u4f55\u5fc5\u957f\u8c08\u77ed\u8bba\uff1f\u201d\u62b1\u62f3\u4e00\u63d6\uff0c\u548c\u7389\u7f57\u5239\u8f6c\u8eab\u4fbf\u8d70\u3002\u718a\u5ef7\u5f3c\u8ffd \u51fa\u53bb\u9053\uff1a\u201c\u7ec3\u59d1\u5a18\uff0c\u6211\u6709\u51e0\u53e5\u8bdd\u8981\u548c\u4f60\u8bf4\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d\u718a\u5ef7\u5f3c\u9053\uff1a\u201c\u671d\u5ef7\u5927\u519b \u4e0d\u65e5\u5f00\u5230\u9655\u897f\uff0c\u59d1\u5a18\uff0c\u4f60\u82e5\u4e0d\u613f\u53d7\u671d\u5ef7\u62db\u5b89\uff0c\u90a3\u5c31\u4e0d\u5fc5\u56de\u53bb\u4e86\u3002\u201d\u7389\u7f57\u5239\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a \u201c\u7ecf\u7565\u5927\u4eba\uff0c\u4f60\u662f\u600e\u6837\u5e26\u5175\u7684\uff1f\u201d\u718a\u5ef7\u5f3c\u77e5\u5979\u8bdd\u610f\uff0c\u7b11\u9053\uff1a\u201c\u5904\u5883\u4e0d\u540c\uff0c\u4e0d\u80fd\u6267\u4e00\u800c\u8bba\u3002\u201d \u7389\u7f57\u5239\u9053\uff1a\uff0c\u201c\u4e00\u519b\u4e3b\u5e05\uff0c\u65ad\u65e0\u89c1\u96be\u5148\u9003\uff0c\u4e0d\u4e0e\u58eb\u5352\u540c\u7518\u5171\u82e6\u7684\u9053\u7406\u3002\u4f60\u5e26\u7684\u662f\u767e\u4e07\u5927\u519b\uff0c \u6211\u5e26\u7684\u662f\u51e0\u767e\u4e2a\u4f60\u4eec\u77a7\u4e0d\u8d77\u7684\u201c\u5973\u5f3a\u76d7\u201d\uff0c\u5904\u5883\u867d\u6709\u4e0d\u540c\uff0c\u4f46\u5728\u6211\u770b\u6765\uff0c\u5374\u662f\u4e00\u6837\u3002\u201d\u718a\u5ef7 \u5f3c\u5fae\u5fae\u53f9\u4e86\u53e3\u6c14\uff0c\u77e5\u9053\u4e0d\u80fd\u529d\u5979\u79bb\u5f00\u7eff\u6797\uff0c\u53ea\u5f97\u7f62\u4e86\u3002 \u3000\u3000\u7389\u7f57\u5239\u4e0e\u94c1\u98de\u9f99\u53bb\u5f8c\uff0c\u955c\u660e\u957f\u8001\u95ee\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u542c\u90a3\u94c1\u8001\u5934\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u5bf9\u4f60\u9887\u4e3a\u4e0d \u6ee1\uff0c\u8fd9\u7a76\u7adf\u662f\u600e\u9ebd\u56de\u4e8b\uff1f\u201d\u5cb3\u545c\u73c2\u65e0\u5948\u8bf4\u4e86\u3002\u955c\u660e\u957f\u8001\u9053\uff1a\u201c\u4f60\u65e0\u610f\u4e2d\u9020\u4e86\u6b64\u5b7d\uff0c\u5fc5\u987b\u81ea \u89e3\u3002\u201d\u718a\u5ef7\u5f3c\u7b11\u9053\uff1a\u201c\u4f60\u4f55\u4e0d\u65e9\u8bf4\uff0c\u4f60\u82e5\u65e9\u8bf4\uff0c\u6211\u5c31\u66ff\u4f60\u5411\u90a3\u4e2a\u94c1\u8001\u5934\u966a\u7f6a\uff0c\u7531\u6211\u51fa\u9762\uff0c\u518d \u66ff\u4f60\u4f5c\u5a92\u3002\u201d\u5cb3\u9e23\u73c2\u9ed8\u7136\u4e0d\u8bed\uff0c\u5fc3\u4e2d\u5341\u5206\u96be\u8fc7\u3002 \u3000\u3000\u518d\u8bf4\u767d\u77f3\u9053\u4eba\u548c\u674e\u5929\u626c\u9f99\u5578\u4e91\u7b49\u76ee\u9001\u7389\u7f57\u5239\u4e0a\u5c71\u4e4b\u5f8c\uff0c\u7ed5\u8fc7\u5c71\u5357\uff0c\u76f4\u4e0a\u592a\u5ba4\u5cf0\u9876\u3002\u767d\u77f3 \u9053\u4eba\u7684\u5c0f\u5973\u513f\u4f55\u5f55\u534e\u6b63\u5728\u5c71\u9876\u6e38\u620f\uff0c\u89c1\u7236\u4eb2\u548c\u59d0\u59d0\u56de\u6765\uff0c\u53c8\u7b11\u53c8\u56b7\u3002\u767d\u77f3\u9053\uff1a\u201c\u5feb\u8bf7\u59d1\u59d1\u51fa \u6765\u3002\u201d\u674e\u5929\u626c\u5fc3\u4e2d\u5341\u4e94\u4e2a\u540a\u6876\uff0c\u4e03\u4e0a\u516b\u843d\uff0c\u8ddf\u5728\u4f17\u4eba\u5f8c\u8fb9\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\uff0c\u6148\u6167\u5e08\u592a\u8d70\u4e86\u51fa\u6765\uff0c\u674e\u7533\u65f6\u8dd1\u4e0a\u524d\u53bb\uff0c\u53eb\u4e86\u58f0\uff1a\u201c\u5988\u5988\u201d\uff0c\u6148\u6167\u559c\u6781\u800c\u6ce3\uff0c\u628a \u4ed6\u4e00\u628a\u62b1\u8fdb\u6000\u91cc\uff0c\u53eb\u4e86\u58f0\uff1a\u201c\u7533\u513f\u3002\u201d\u5fd9\u7740\u53c8\u5411\u9f99\u5578\u4e91\u9053\u8c22\u3002\u674e\u5929\u626c\u89c1\u6b64\u60c5\u666f\uff0c\u9635\u9635\u8f9b\u9178\uff0c \u60f3\u5f00\u53e3\u8bf4\u8bdd\u5374\u8bf4\u4e0d\u51fa\u6765\u3002\u6148\u6167\u6b63\u773c\u4e5f\u4e0d\u77a7\u4ed6\u4e00\u4e0b\uff0c\u62c9\u7740\u513f\u5b50\u5fd9\u7740\u62db\u547c\u7ea2\u4e91\u9752\u7b49\u5ba2\u4eba\u5165\u5bfa\u3002 \u3000\u3000\u5230\u4e86\u5bfa\u4e2d\u77f3\u5ba4\uff0c\u674e\u7533\u65f6\u5f20\u773c\u56db\u671b\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u7238\u7238\u5462\uff1f\u201d\u9f99\u5578\u4e91\u8fd9\u624d\u53d1\u73b0\u674e\u5929 \u626c\u5df2\u6084\u6084\u8d70\u4e86\u3002\u6148\u6167\u9053\uff1a\u201c\u8fd9\u6837\u7684\u7238\u7238\u4e0d\u8981\u4e5f\u7f62\u3002\u4f60\u4eec\u600e\u9ebd\u78b0\u4e0a\u4ed6\u7684\uff1f\u201d\u674e\u7533\u65f6\u6d41\u6cea\u8bf4\u9053\uff1a \u201c\u4e0d\uff0c\u7238\u7238\u662f\u597d\u7238\u7238\u3002\u5988\u4e0d\u80fd\u4e0d\u8981\u4ed6\u3002\u201d\u628a\u4e8b\u60c5\u8be6\u7ec6\u8bf4\u4e86\u3002\u8fd8\u672a\u8bf4\u5b8c\uff0c\u6148\u6167\u773c\u4e2d\u5df2\u6709\u6676\u83b9\u7684 \u6cea\u5149\u3002 \u3000\u3000\u518d\u8bf4\u674e\u5929\u626c\u8e3d\u8e3d\u72ec\u884c\uff0c\u8d70\u5230\u534a\u5c71\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u5c16\u58f0\u5524\u9053\uff1a\u201c\u5929\u626c\uff01\u201d\u674e\u5929\u626c\u4e00\u542c\uff0c\u987f\u5982 \u89e6\u7535\u4e00\u822c\uff0c\u7f13\u7f13\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u81ea\u5df1\u7684\u59bb\u5b50\u6cea\u6d41\u6ee1\u9762\uff0c\u98de\u6b65\u8d76\u6765\u3002\u674e\u5929\u626c\u9053\uff1a\u201c\u6148\u6167\u5e08\u592a\uff0c \u8d3a\u4f60\u4eec\u6bcd\u5b50\u76f8\u9022\uff0c\u6211\u65e0\u989c\u7559\u5728\u8fd9\u91cc\uff0c\u613f\u4f60\u597d\u597d\u4fdd\u91cd\uff0c\u6559\u517b\u7533\u513f\u3002\u201d\u6148\u6167\u4ee5\u8896\u63e9\u6cea\uff0c\u5ae3\u7136\u4e00 \u7b11\uff0c\u9053\uff1a\u201c\u5eff\u5e74\u524d\u4f60\u5fcd\u5fc3\u79bb\u5f00\u6211\u4eec\uff0c\u73b0\u5728\u53c8\u8981\u629b\u5f03\u7533\u513f\u5417\uff1f\u201d\u674e\u5929\u626c\u9053\uff1a\u201c\u8fc7\u53bb\u7684\u4e8b\uff0c\u6211\u5f88 \u60ed\u69d0\u3002\u4f60\u5f53\u6211\u6b7b\u53bb\u4e86\u5427\u3002\u201d\u6148\u6167\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u8fc7\u53bb\u79cd\u79cd\u6bd4\u5982\u6628\u65e5\u6b7b\uff0c\u4ee5\u5f8c\u79cd\u79cd\u6bd4\u5982\u4eca\u65e5 \u751f\u3002\u201d\u8fd9\u4e24\u53e5\u8bdd\u6b63\u662f\u674e\u5929\u626c\u8ba4\u513f\u5b50\u65f6\u6240\u8bf4\u7684\u8bdd\uff0c\u95fb\u8a00\u4e00\u6014\uff0c\u77e5\u9053\u674e\u7533\u65f6\u5df2\u5bf9\u6bcd\u4eb2\u8bf4\u660e\u4e00\u5207\u3002 \u53ea\u89c1\u6148\u6167\u5fae\u5fae\u4e00\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u800c\u4e14\u4ece\u4eca\u65e5\u8d77\uff0c\u6211\u4e5f\u4e0d\u53eb\u505a\u6148\u6167\u4e86\u3002\u201d\u674e\u5929\u626c\u53eb\u9053\uff1a\u201c\u7eee\u971e\uff0c \u4f60\u8981\u84c4\u53d1\u8fd8\u4fd7\u4e86\u9ebd\uff1f\u201d\u4f55\u7eee\u971e\u9053\uff1a\u201c\u4f60\u4e0d\u505a\u5b98\u6211\u4e5f\u4e0d\u505a\u5c3c\u59d1\uff0c\u8fd9\u4e0d\u5f88\u597d\u9ebd\uff1f\u201d\u8138\u4e0a\u6cea\u75d5\u5df2 \u6de1\uff0c\u9690\u9690\u6cdb\u51fa\u7ea2\u6f6e\u3002\u674e\u5929\u626c\u5927\u559c\uff0c\u60f3\u4e0d\u5230\u5979\u4e00\u65e6\u56de\u5fc3\u8f6c\u610f\uff0c\u7834\u955c\u91cd\u5706\u3002 \u3000\u3000\u4e24\u4eba\u643a\u624b\u91cd\u56de\u5c71\u4e0a\u5c3c\u5eb5\uff0c\u767d\u77f3\u9053\u4eba\u7b49\u6b63\u7b49\u5f97\u5fc3\u7126\uff0c\u89c1\u4ed6\u4eec\u592b\u59bb\u548c\u597d\uff0c\u53cc\u53cc\u56de\u6765\uff0c\u7686\u5927\u6b22 \u559c\uff0c\u7eb7\u7eb7\u9053\u8d3a\u3002\u6b22\u7b11\u58f0\u4e2d\uff0c\u767d\u77f3\u9053\u4eba\u5ffd\u89c1\u4f55\u843c\u534e\u548c\u674e\u7533\u65f6\u5e76\u80a9\u501a\u504e\uff0c\u72b6\u751a\u4eb2\u5bc6\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c \u4f55\u7eee\u971e\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u6211\u4e5f\u8981\u5411\u4f60\u9053\u8d3a\u5440\uff01\u201d\u767d\u77f3\u9053\uff1a\u201c\u4ec0\u9ebd\uff1f\u201d\u4f55\u7eee\u971e\u9053\uff1a\u201c\u8bf7\u4f60\u5165\u5185\uff0c\u6211 \u8981\u548c\u4f60\u4e00\u8c08\u3002\u201d \u3000\u3000\u767d\u77f3\u9053\u4eba\u9ed8\u7136\u65e0\u8bed\uff0c\u968f\u59b9\u59b9\u8d70\u4eba\u5185\u5ba4\u3002\u4f55\u7eee\u971e\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u770b\u7533\u65f6\u600e\u6837\uff1f\u201d\u767d\u77f3\u9053\uff1a \u201c\u4eba\u54c1\u6b66\u529f\u90fd\u8fd8\u4e0d\u9519\u3002\u201d\u4f55\u7eee\u971e\u9053\uff1a\u201c\u6211\u7ecf\u6b64\u5927\u53d8\uff0c\u76ca\u77e5\u5a5a\u59fb\u4e4b\u4e8b\uff0c\u52c9\u5f3a\u4e0d\u5f97\u3002\u843c\u534e\u548c\u7533\u65f6 \u9752\u6885\u7af9\u9a6c\uff0c\u81ea\u5c0f\u76f8\u6295\u3002\u54e5\u54e5\uff0c\u54b1\u4eec\u4eb2\u4e0a\u52a0\u4eb2\uff0c\u4f60\u610f\u601d\u600e\u6837\uff1f\u201d\u767d\u77f3\u9053\u4eba\u548c\u5353\u4e00\u822a\u6765\u56de\u4e07\u91cc\uff0c \u7ecf\u4e86\u8fd9\u9ebd\u591a\u65f6\u65e5\uff0c\u5df2\u77e5\u5353\u4e00\u822a\u5e76\u4e0d\u5c5e\u610f\u4ed6\u7684\u5973\u513f\uff0c\u53c8\u76ee\u7779\u4e86\u59b9\u59b9\u8fd9\u573a\u5a5a\u53d8\uff0c\u542c\u4e86\u201c\u5a5a\u59fb\u4e4b \u4e8b\uff0c\u52c9\u5f3a\u4e0d\u5f97\u201d\u7684\u8bdd\uff0c\u9762\u4e0a\u70ed\u8fa3\u8fa3\u7684\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u4f55\u7eee\u971e\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u8bf4\u5440\uff01\u662f\u4e0d\u662f\u7533\u513f \u914d\u4e0d\u4e0a\u4f60\u7684\u843c\u534e\uff1f\u201d\u767d\u77f3\u5f3a\u7b11\u9053\uff1a\u201c\u59b9\u59b9\u90a3\u91cc\u8bdd\u6765\uff0c\u53ea\u8981\u4ed6\u4eec\u60c5\u6295\u610f\u5408\uff0c\u6211\u4eec\u505a\u7236\u6bcd\u7684\u4e5f\u514d \u5f97\u64cd\u5fc3\u3002\u201d\u4f55\u7eee\u971e\u5fae\u5fae\u4e00\u7b11\uff0c\u53eb\u6765\u674e\u7533\u65f6\u548c\u4f55\u843c\u534e\uff0c\u628a\u5a5a\u4e8b\u5f53\u9762\u8bf4\u4e86\u3002\u674e\u7533\u65f6\u50bb\u4e4e\u4e4e\u7684\u53eb\u4e86 \u58f0\u201c\u8205\u8205\u201d\uff0c\u4f55\u7eee\u971e\u9053\uff1a\u201c\u50bb\u5b69\u5b50\uff0c\u8fde\u79f0\u547c\u90fd\u4e0d\u61c2\u3002\u201d\u674e\u7533\u65f6\u6539\u53eb\u201c\u5cb3\u4e08\u5927\u4eba\u3002\u201d\u53e9\u5934\u884c \u793c\uff0c\u4f55\u843c\u534e\u62bf\u7740\u5634\u7b11\uff0c\u663e\u89c1\u5341\u5206\u9ad8\u5174\uff0c\u767d\u77f3\u9053\u4eba\u89c1\u6b64\u60c5\u666f\uff0c\u5fc3\u4e2d\u867d\u7136\u4e0d\u5f88\u613f\u610f\uff0c\u4e5f\u53ea\u5f97\u7b54 \u5e94\u3002\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u7533\u513f\uff0c\u4f60\u7684\u6b66\u529f\u6839\u5e95\u8fd8\u5dee\uff0c\u4ee5\u5f8c\u66f4\u8981\u7528\u529f\u3002\u4f60\u968f\u6211\u5230\u6b66\u5f53\u5c71\u53bb\uff0c\u6211\u8bf7\u5e08\u5144 \u9ec4\u53f6\u9053\u957f\u6536\u4f60\u4e3a\u5f92\u3002\u4f60\u8fd9\u5341\u591a\u5e74\u6765\uff0c\u5c31\u53ea\u662f\u5b66\u4e86\u4e00\u5957\u5ce8\u7709\u5251\u6cd5\u9ebd\uff01\u9f99\u5578\u4e91\u7684\u5251\u672f\uff0c\u597d\u867d\u7136 \u597d\uff0c\u5230\u5e95\u2026\u2026\u201d\u6447\u4e86\u6447\u5934\uff0c\u4f55\u7eee\u971e\u9887\u611f\u4e0d\u5feb\uff0c\u622a\u7740\u8bf4\u9053\uff1a\u201c\u5230\u5e95\u53ca\u4e0d\u4e0a\u4f60\u4eec\u6b66\u5f53\u6d3e\u7684\u7cbe\u5999\uff0c \u662f\u9ebd\uff1f\u201d\u767d\u77f3\u9053\uff1a\u201c\u6211\u662f\u60f3\u7533\u513f\u767e\u5c3a\u7aff\u5934\uff0c\u66f4\u8fdb\u4e00\u6b65\u3002\u201d\u4f55\u7eee\u971e\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u9f99\u5578\u4e91\u80af\u82e6\u5fc3 \u6559\u4ed6\uff0c\u4ed6\u8fd8\u66f4\u4e0d\u6210\u5668\u5462\uff01\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u9f99\u5578\u4e91\u5728\u5916\u9762\u5524\u9053\uff1a\u201c\u7533\u513f\uff01\u201d\u674e\u7533\u65f6\u9053\uff1a\u201c\u8c22\u5cb3\u4e08 \u5927\u4eba\u597d\u610f\uff0c\u4f46\u6539\u6295\u95e8\u6237\uff0c\u7406\u5e94\u5148\u7980\u544a\u6069\u5e08\u3002\u201d \u3000\u3000\u9f99\u5578\u4e91\u5012\u5f88\u723d\u5feb\uff0c\u542c\u5f97\u767d\u77f3\u9053\u4eba\u8981\u674e\u7533\u65f6\u6539\u4eba\u6b66\u5f53\u95e8\u4e0b\uff0c\u4e00\u53e3\u4fbf\u7b54\u5e94\u4e86\u3002\u4f17\u4eba\u542c\u5f97\u4e24\u5c0f \u8ba2\u5a5a\uff0c\u559c\u4e0a\u52a0\u559c\uff0c\u53c8\u662f\u7eb7\u7eb7\u9053\u8d3a\uff0c\u5353\u4e00\u822a\u5c24\u5176\u9ad8\u5174\uff0c\u62c9\u7740\u674e\u7533\u65f6\u95ee\u957f\u95ee\u77ed\uff0c\u5e73\u65f6\u4ed6\u5bf9\u4f55\u843c\u534e \u603b\u89c9\u62d8\u675f\uff0c\u542c\u4e86\u767d\u77f3\u9053\u4eba\u5ba3\u5e03\u5a5a\u7ea6\u4e4b\u5f8c\uff0c\u6001\u5ea6\u7acb\u523b\u81ea\u7136\uff0c\u548c\u4f55\u843c\u534e\u8c08\u7b11\u4e4b\u65f6\uff0c\u8bf4\u8bdd\u4e5f\u6d41\u7545 \u4e86\u3002\u674e\u7533\u65f6\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u8fd9\u5353\u4e00\u822a\u4e3a\u4eba\u751a\u597d\uff0c\u4ee5\u524d\u9519\u602a\u4ed6\u4e86\u3002\u201d\u767d\u77f3\u9053\u4eba\u770b\u5728\u773c\u5185\uff0c\u867d\u7136\u5a5a \u7ea6\u5df2\u6210\u5b9a\u5c40\uff0c\u4f46\u5fc3\u4e2d\u53c8\u6dfb\u4e86\u4e00\u5c42\u4e0d\u5feb\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u767d\u77f3\u9053\u4eba\u7b49\u4f1a\u540c\u4e86\u718a\u5ef7\u5f3c\u7eed\u5411\u5357\u884c\uff0c\u534a\u6708\u4e4b\u5f8c\uff0c\u5230\u4e86\u6e56\u5317\uff0c\u5206\u9053\u626c\u9573\uff0c\u718a\u5ef7\u5f3c\u5e26 \u5cb3\u545c\u73c2\u738b\u8d5e\u56de\u6c5f\u590f\u6545\u91cc\uff0c\u9f99\u5578\u4e91\u897f\u4e0a\u5ce8\u7709\u3002\u6b66\u5f53\u53c1\u8001\u5e26\u5353\u4e00\u822a\u548c\u674e\u7533\u65f6\u4e0a\u6b66\u5f53\u5c71\u3002 \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u89c1\u5353\u4e00\u822a\u56de\u6765\uff0c\u53c8\u63d0\u8d77\u8981\u4ed6\u63a5\u638c\u638c\u95e8\u4e4b\u4e8b\u3002\u5353\u4e00\u822a\u9053\uff1a\u201c\u5f1f\u5b50\u5b5d\u670d\u672a\u6ee1\uff0c\u60f3\u56de \u6545\u91cc\u8fc1\u846c\u7956\u7236\u9057\u9aa8\u3002\u53c1\u5e74\u4e4b\u5f8c\uff0c\u5f1f\u5b50\u613f\u62ab\u4e0a\u9ec4\u51a0\uff0c\u56de\u5c71\u542c\u5e08\u53d4\u5dee\u9063\u3002\u201d\u9ec4\u53f6\u7b11\u9053\uff1a\u201c\u4f60\u505a\u638c \u95e8\uff0c\u5374\u4e0d\u5fc5\u505a\u9053\u58eb\u3002\u4f60\u5bb6\u53c1\u4ee3\u5355\u4f20\uff0c\u4f60\u600e\u53ef\u5b66\u6211\u4eec\u4e00\u6837\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5f1f\u5b50\u53c2\u900f\u4e16\u60c5\uff0c\u5bf9 \u5c18\u4fd7\u4e4b\u4e8b\u5df2\u7ecf\u770b\u5f97\u5f88\u6de1\u3002\u201d\u9ec4\u53f6\u9053\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u628a\u773c\u770b\u767d\u77f3\u9053\u4eba\u3002\u767d\u77f3\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4f60 \u7ed3\u5a5a\u751f\u5b50\u4e4b\u5f8c\uff0c\u518d\u505a\u9053\u58eb\u4e5f\u8fd8\u672a\u8fdf\u3002\u6211\u4eec\u89c6\u4f60\u5982\u5b50\uff0c\u4e00\u5b9a\u8981\u66ff\u4f60\u9009\u4e2a\u597d\u5973\u5b50\u3002\u90a3\u7389\u7f57\u5239\u91ce\u6027 \u96be\u9a6f\uff0c\u662f\u6211\u4eec\u6b66\u5f53\u6d3e\u7684\u516c\u654c\uff0c\u4f60\u53ef\u4e0d\u8981\u548c\u5979\u6765\u5f80\u3002\u201d\u9ec4\u53f6\u9053\u4eba\u5c1a\u672a\u77e5\u5e08\u5f1f\u5df2\u628a\u5973\u513f\u6539\u914d\u4ed6 \u4eba\uff0c\u95fb\u8a00\u5fae\u5fae\u4e00\u695e\u3002\u76f4\u5230\u665a\u4e0a\uff0c\u767d\u77f3\u9053\u4eba\u8bf7\u4ed6\u6536\u674e\u7533\u65f6\u4e3a\u5f92\uff0c\u4ed6\u624d\u77e5\u9053\u539f\u59d4\u3002 \u3000\u3000\u5353\u4e00\u822a\u5728\u5c71\u4e0a\u4f4f\u4e86\u534a\u6708\uff0c\u796d\u626b\u4e86\u5e08\u7236\u7684\u5893\u5f8c\uff0c\u4e0b\u5c71\u56de\u91cc\u3002\u9ec4\u53f6\u9053\u4eba\u672c\u60f3\u8bf7\u767d\u77f3\u9053\u4eba\u9001 \u4ed6\uff0c\u5353\u4e00\u822a\u575a\u6301\u4e0d\u8981\u3002\u767d\u77f3\u9053\u4eba\u5bf9\u4ed6\u5df2\u4e0d\u4f3c\u5148\u524d\u5ba0\u7231\uff0c\u5353\u4e00\u822a\u5ba2\u6c14\u63a8\u8f9e\uff0c\u4ed6\u4e5f\u4fbf\u7f62\u4e86\u3002 \u3000\u3000\u5176\u65f6\u660e\u519b\u5728\u5175\u79d1\u7ed9\u4e8b\u4e2d\u5218\u5ef7\u5143\u7387\u9886\u4e0b\uff0c\u6b63\u5728\u9655\u897f\u5927\u4e3e\u201c\u527f\u532a\u201d\uff0c\u5353\u4e00\u822a\u6cbf\u8def\u53d7\u5230\u76d8\u67e5\uff0c \u5e78\u4ed6\u7956\u7236\u7236\u4eb2\u90fd\u66fe\u505a\u8fc7\u5927\u5b98\uff0c\u5218\u5ef7\u5143\u8fd8\u662f\u4ed6\u7956\u7236\u7684\u665a\u8f88\uff0c\u4e00\u8bf4\u8d77\u6765\uff0c\u4eba\u4eba\u77e5\u9053\u3002\u5f8c\u6765\u5353\u4e00\u822a \u4e3a\u4e86\u514d\u53d7\u70e6\uff0c\u7d22\u6027\u548c\u519b\u961f\u540c\u884c\u3002\u8d70\u4e86\u51e0\u5929\uff0c\u7ecf\u8fc7\u5ddd\u4e1c\u7684\u5b9a\u519b\u5c71\uff0c\u6b63\u662f\u65e7\u65f6\u7389\u7f57\u5239\u5b89\u8425\u7acb\u5be8\u4e4b \u5730\uff0c\u5353\u4e00\u822a\u7ecf\u8fc7\u5c71\u4e0b\uff0c\u53ea\u89c1\u5c71\u4e0a\u9980\u70ec\u672a\u706d\uff0c\u5c71\u5be8\u65e9\u5df2\u5316\u6210\u74e6\u783e\u3002\u5353\u4e00\u822a\u5927\u9a87\uff0c\u95ee\u540c\u884c\u7684\u519b \u5b98\u3002\u519b\u5b98\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u4ed7\u4e0d\u662f\u6211\u4eec\u6253\u7684\uff0c\u4f46\u542c\u8bf4\u8fd9\u4e00\u4ed7\u6781\u4e3a\u6fc0\u70c8\uff0c\u800c\u4e14\u9999\u4e4b\u81f3\u3002\u201d\u5353\u4e00\u822a\u95ee \u9053\uff1a\u201c\u600e\u9ebd\uff1f\u201d\u519b\u5b98\u9053\uff1a\u201c\u76d8\u636e\u8fd9\u5ea7\u5c71\u7684\u5168\u662f\u5973\u5f3a\u76d7\uff0c\u542c\u8bf4\u4e2a\u4e2a\u90fd\u662f\u7f8e\u8c8c\u5982\u82b1\uff0c\u6253\u8d77\u4ed7\u6765\u5374 \u51f6\u6076\u4e4b\u6781\u3002\u5979\u4eec\u6709\u51e0\u767e\u4eba\uff0c\u6211\u4eec\u8c03\u4e86\u53c1\u5343\u94c1\u9a91\u519b\u53bb\u56f4\u653b\uff0c\u56f4\u4e86\u534a\u6708\uff0c\u624d\u628a\u5c71\u5be8\u653b\u7834\uff0c\u53c1\u5343\u94c1 \u9a91\u519b\u6b7b\u4f24\u8fc7\u534a\uff0c\u4f46\u8fd8\u662f\u7ed9\u90a3\u80a1\u5973\u5f3a\u76d7\u7a81\u56f4\u51b2\u51fa\u3002\u6211\u4eec\u4fd8\u864f\u4e86\u5341\u591a\u4e2a\u5973\u532a\uff0c\u5168\u7ed9\u90a3\u4e9b\u9ad8\u7ea7\u519b\u5b98 \u62a2\u53bb\u3002\u90a3\u4e9b\u519b\u5b98\u6b63\u4ee5\u4e3a\u798f\u4e0d\u6d45\uff0c\u8c01\u77e5\u6709\u53c1\u540d\u519b\u5b98\uff0c\u6025\u65bc\u6210\u4eb2\uff0c\u5f53\u665a\u5c31\u7ed9\u5973\u532a\u523a\u6b7b\uff0c\u5176\u9980\u519b\u5b98 \u5168\u614c\u4e86\uff0c\u4e0d\u7ba1\u90a3\u4e9b\u5973\u532a\u591a\u9ebd\u7f8e\u8c8c\uff0c\u90fd\u63a8\u51fa\u53bb\u65a9\u6389\u3002\u54c8\u54c8\uff01\u5e78\u800c\u90a3\u4e00\u4ed7\u6ca1\u6211\u7684\u4efd\uff0c\u8981\u4e0d\u7136\u6211\u4e5f \u8bb8\u505a\u4e86\u98ce\u6d41\u9b3c\u4e86\u3002\u201d\u5353\u4e00\u822a\u9762\u8272\u500f\u53d8\uff0c\u51b2\u53e3\u95ee\u9053\uff1a\u201c\u90a3\u9ebd\u7389\u7f57\u5239\u5462\uff1f\u201d\uff0e\u519b\u5b98\u8be7\u9053\uff1a\u201c\u7389\u7f57 \u5239\uff1f\u4f60\u4e5f\u77e5\u9053\u7389\u7f57\u5239\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u542c\u6b66\u6797\u7684\u670b\u53cb\u8c08\u8fc7\u3002\u201d\u90a3\u519b\u5b98\u5b9a\u4e86\u5b9a\u795e\uff0c\u7b11\u9053\uff1a \u201c\u6211\u5fd8\u8bb0\u4e86\uff0c\u4f60\u662f\u6b66\u5f53\u6d3e\u7684\u9ad8\u5f92\uff0c\u96be\u602a\u6b66\u6797\u7684\u670b\u53cb\u5bf9\u4f60\u63d0\u8fc7\u7389\u7f57\u5239\u7684\u540d\u5b57\u3002\u8fd9\u7389\u7f57\u5239\u540d\u5934\u6781 \u5927\uff0c\u542c\u8bf4\u51f6\u72e0\u65e0\u6bd4\uff0c\u662f\u6740\u4eba\u4e0d\u7728\u773c\u7684\u5973\u76ae\u738b\u3002\u5e78\u597d\u8fd9\u6b21\u56f4\u653b\u5c71\u5be8\uff0c\u7389\u7f57\u5239\u5374\u4e0d\u5728\u5185\uff0c\u8981\u4e0d\u7136 \u8fd9\u4e00\u4ed7\u66f4\u96be\u6253\u5462\uff01\u201d\u5353\u4e00\u822a\u542c\u4e86\u5185\u5fc3\u7a0d\u5b89\u3002\u8fd9\u652f\u519b\u961f\u5f00\u8d74\u5ef6\u5b89\uff0c\u5353\u4e00\u822a\u5bb6\u5728\u5ef6\u5b89\u5e9c\u5916\uff0c\u519b\u961f \u76f4\u628a\u4ed6\u62a4\u9001\u5230\u5bb6\u3002\u5353\u4e00\u822a\u7684\u6848\u60c5\u65e9\u5df2\u662d\u96ea\uff0c\u5bb6\u95e8\u4ea6\u5df2\u6563\u5c01\uff0c\u5bb6\u4eba\u7b49\u4e5f\u90fd\u5df2\u56de\u6765\uff0c\u89c1\u516c\u5b50\u5f52 \u5bb6\uff0c\u4eba\u4eba\u6b22\u559c\u3002\u81ea\u6b64\u5353\u4e00\u822a\u6682\u5728\u5bb6\u4e2d\u7ec3\u6b66\u8bfb\u4e66\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u518d\u8bf4\u7389\u7f57\u5239\u542c\u5f97\u671d\u5ef7\u6d3e\u5927\u519b\u8d74\uff0c\u517c\u7a0b\u8d76\u56de\u3002\u94c1\u98de\u9f99\u5219\u6d6a\u8ff9\u6c5f\u6e56\uff0c\u627e\u5bfb\u5973\u513f\u3002\u7389\u7f57\u5239\u56de\u5230 \u5b9a\u519b\u5c71\u65f6\uff0c\u6b63\u662f\u5c71\u5be8\u88ab\u653b\u7834\u5f8c\u7684\u7b2c\u53c1\u65e5\uff0c\u5927\u519b\u5df2\u7ecf\u5f00\u8d70\u3002\u8fd9\u51e0\u767e\u540d\u5a18\u5b50\u519b\uff0c\u662f\u7389\u7f57\u5239\u4e00\u624b\u8bad \u7ec3\u51fa\u6765\u7684\uff0c\u7389\u7f57\u5239\u53ea\u9053\u5979\u4eec\u5168\u5df2\u6218\u6b7b\uff0c\u5fc3\u4e2d\u5927\u75db\uff0c\u62d4\u5251\u65ab\u77f3\uff0c\u8a93\u4e3a\u540c\u4f34\u62a5\u4ec7\u3002\u5f53\u4e0b\u6362\u4e86\u7537 \u88c5\uff0c\u8d76\u5f80\u9655\u5317\uff0c\u60f3\u548c\u738b\u5609\u80e4\u8054\u5408\uff0c\u5174\u5b98\u519b\u75db\u75db\u5feb\u5feb\u6253\u4ed6\u4e00\u573a\u3002 \u3000\u3000\u6cbf\u9014\u5175\u52c7\u7edc\u7ece\u4e0d\u7edd\u3002\u7389\u7f57\u5239\u4e3a\u4e86\u514d\u60f9\u9ebb\u70e6\uff0c\u663c\u4f0f\u591c\u884c\uff0c\u5979\u8f7b\u529f\u8d85\u5353\uff0c\u5730\u65b9\u53c8\u719f\uff0c\u4e00\u9047\u5b98 \u519b\u4fbf\u5148\u8eb2\u907f\uff0c\u4e0d\u8fc7\u56db\u5929\uff0c\u5df2\u5230\u53bf\uff0c\u79bb\u5ef6\u5b89\u6709\u4e00\u65e5\u8def\u7a0b\u3002\u8fc7\u4e86\u5ef6\u5b89\uff0c\u4ee5\u7389\u7f57\u5239\u7684\u811a\u7a0b\uff0c\u4e0d\u6d88\u53c1 \u5929\uff0c\u4fbf\u53ef\u5230\u738b\u5609\u80e4\u9655\u5317\u7fa4\u76d7\u805a\u96c6\u4e4b\u5730\u7684\u7c73\u8102\u3002\u7389\u7f57\u5239\u6025\u65bc\u8d76\u8def\uff0c\u9ec4\u660f\u52a8\u8eab\u884c\u4e86\u4e00\u7a0b\uff0c\u5ffd\u89c1\u524d \u9762\u51e0\u9a91\uff0c\u4e5f\u5728\u8d76\u8def\u3002\u5176\u4e2d\u4e00\u4eba\uff0c\u80cc\u5f71\u4f3c\u4e4e\u751a\u719f\uff0c\u7389\u7f57\u5239\u52a0\u5feb\u811a\u6b65\uff0c\u62a2\u4e0a\u524d\u53bb\uff0c\u90a3\u4e9b\u9a91\u58eb\uff0c\u89c1 \u4e00\u6761\u4eba\u5f71\u65cb\u98ce\u4f3c\u7684\u63a0\u8fc7\u8eab\u8fb9\uff0c\u9f50\u90fd\u60ca\u53eb\uff0c\u5176\u4e2d\u4e00\u4eba\uff0c\u9a6c\u97ad\u5237\u5730\u4e00\u626b\uff0c\u51fa\u624b\u672c\u6765\u4e5f\u7b97\u5f97\u751a\u4e3a\u5feb \u6377\uff0c\u83ab\u5948\u7389\u7f57\u5239\u7684\u8f7b\u529f\u7edd\u6280\uff0c\u6b66\u6797\u7b2c\u4e00\uff0c\u6c5f\u6e56\u65e0\u53cc\uff0c\u9a6c\u97ad\u63a0\u9762\u9762\u8fc7\uff0c\u7adf\u81ea\u626b\u5979\u4e0d\u4f4f\u3002\u90a3\u4eba \u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u662f\u4eba\u662f\u9b3c\uff1f\u201d\u6709\u4e00\u4eba\u5403\u5403\u51b7\u7b11\uff0c\u53c8\u6709\u4e00\u4eba\u9053\uff1a\u201c\u9655\u5317\u591a\u5f02\u4eba\uff0c\u9ad8\u58eb\u5f53\u524d\uff0c\u7adf\u7136 \u9519\u8fc7\uff0c\u771f\u771f\u53ef\u60dc\uff01\u201d \u3000\u3000\u5c31\u5728\u7535\u5149\u77f3\u706b\u7684\u5239\u90a3\uff0c\u7389\u7f57\u5239\u5df2\u628a\u90a3\u51e0\u4e2a\u4eba\u7684\u9762\u8c8c\u770b\u4e2a\u6e05\u695a\u3002\u90a3\u4e00\u884c\u5171\u6709\u516d\u4eba\uff0c\u5176\u4e2d\u53c1 \u4eba\uff0c\u4f53\u683c\u7855\u4f1f\uff0c\u9e70\u9f3b\u72ee\u5634\uff0c\u597d\u50cf\u4e0d\u662f\u6c49\u4eba\u3002\u5c24\u4ee5\u5f53\u4e2d\u90a3\u9a91\uff0c\u5c11\u5e74\u82f1\u4fca\uff0c\u76f8\u8c8c\u751a\u4e3a\u5a01\u6b66\u3002\u53e6\u4e24 \u4eba\u5219\u662f\u519b\u5b98\u88c5\u675f\uff0c\u7528\u9a6c\u97ad\u626b\u5979\u7684\u5c31\u662f\u519b\u5b98\u4e4b\u4e00\uff0c\u770b\u4ed6\u51fa\u624b\uff0c\u6b66\u529f\u751a\u6709\u6839\u5e95\uff0c\u60f3\u6765\u4e0d\u662f\u666e\u901a\u4eba \u7269\u3002 \u3000\u3000\u4f46\u6700\u4ee4\u7389\u7f57\u5239\u60ca\u5947\u7684\u5374\u662f\u5f8c\u9762\u90a3\u9a91\u7684\u5c11\u5e74\uff0c\u770b\u201c\u4ed6\u201d\u9762\u8c8c\uff0c\u542c\u201c\u4ed6\u201d\u58f0\u97f3\uff0c\u7adf\u4f3c\u662f\u94c1\u73ca \u745a\u4e54\u88c5\u6253\u626e\u7684\uff01\u94c1\u73ca\u745a\u4f55\u4ee5\u4f1a\u548c\u8fd9\u4e9b\u4eba\u540c\u5728\u4e00\u8d77\uff0c\u7389\u7f57\u5239\u518d\u4e5f\u731c\u60f3\u4e0d\u900f\u3002\u5fc3\u60f3\uff1a\u6211\u4e49\u7236\u5230\u5904 \u627e\u5979\uff0c\u4e0d\u77e5\u591a\u6302\u5fc3\u5462\uff1f\u60f3\u4e0d\u5230\u201c\u8e0f\u7834\u94c1\u978b\u65e0\u89c5\u5904\uff0c\u5f97\u6765\u5168\u4e0d\u8d39\u529f\u592b\uff01\u201d\u6211\u5374\u4f1a\u5728\u6b64\u5730\u548c\u5979\u76f8 \u9047\u3002\u4eca\u665a\u5b81\u53ef\u4e0d\u8d76\u8def\uff0c\u4e14\u770b\u770b\u8fd9\u59ae\u5b50\u846b\u82a6\u91cc\u5356\u4ec0\u9ebd\u836f\u3002 \u3000\u3000\u7389\u7f57\u5239\u6253\u5b9a\u4e3b\u610f\uff0c\u6084\u6084\u7684\u6e9c\u8fdb\u53bf\u53bf\u57ce\uff0c\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u98d8\u8eab\u5230\u57ce\u697c\u4e4b\u4e0a\uff0c\u7b49\u4e86\u4e00\u4f1a\uff0c\u90a3 \u51e0\u9a91\u9a6c\u8fdb\u4e86\u57ce\u95e8\uff0c\u7389\u7f57\u5239\u6697\u6697\u7f00\u5728\u4ed6\u4eec\u5f8c\u9762\uff0c\u89c1\u4ed6\u4eec\u7adf\u7136\u8fdb\u4e86\u77e5\u53bf\u7684\u8859\u95e8\u3002 \u3000\u3000\u7389\u7f57\u5239\u66f4\u662f\u60ca\u5947\u3002\u627e\u4e86\u4e00\u95f4\u5ba2\u6808\uff0c\u6b47\u4e86\u4e00\u4f1a\uff0c\u542c\u5f97\u53c1\u66f4\u9f13\u54cd\uff0c\u65bd\u5c55\u8f7b\u529f\u7edd\u6280\uff0c\u6084\u6084\u6e9c\u4eba \u77e5\u53bf\u8859\u95e8\u3002\u5148\u628a\u4e00\u4e2a\u66f4\u592b\u70b9\u5012\uff0c\u95ee\u4ed6\u5ba2\u4eba\u4f4f\u5728\u4f55\u5904\uff1f\u90a3\u66f4\u592b\u662f\u53bf\u8859\u4e2d\u7684\u5dee\u5f79\uff0c\u5982\u4f55\u77e5\u9053\uff1f\u7389 \u7f57\u5239\u60f3\u4e86\u4e00\u9635\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u4eec\u7684\u77e5\u53bf\u8001\u7237\u4f4f\u5728\u90a3\u91cc\uff1f\u201d\u8fd9\u4e2a\uff0c\u66f4\u592b\u81ea\u7136\u77e5\u9053\uff0c\u5f53\u4e0b\u5982\u5b9e \u8bf4\u4e86\u3002\u7389\u7f57\u5239\u9053\uff1a\u201c\u59d4\u5c48\u4f60\u4e00\u9635\u3002\u201d\u628a\u66f4\u592b\u7684\u53f7\u8863\u6495\u4e0b\u4e00\u5757\uff0c\u585e\u8fdb\u4ed6\u7684\u53e3\u5185\uff0c\u628a\u4ed6\u7f1a\u5728\u89d2\u843d \u7684\u77f3\u72ee\u5b50\u4e0a\u3002\u62ff\u8fc7\u4e86\u66f4\u592b\u624b\u4e2d\u7684\u6728\u67dd\uff0c\u6572\u4e86\u51e0\u4e0b\uff0c\u4fbf\u7167\u66f4\u592b\u6240\u6307\u70b9\u7684\u65b9\u5411\u5bfb\u53bb\u3002 \u3000\u3000\u4e0a\u623f\u900f\u51fa\u706f\u706b\uff0c\u53bf\u5b98\u5c45\u7136\u672a\u7761\u3002\u7389\u7f57\u5239\u6f5c\u4f0f\u7a97\u5916\uff0c\u542c\u5f97\u53bf\u5b98\u95ee\u592b\u4eba\u9053\uff1a\u201c\u8fd9\u51e0\u4e2a\u5ba2\u4eba\u8981 \u597d\u597d\u670d\u4f8d\uff0c\u90a3\u51e0\u7897\u51b0\u7cd6\u71d5\u7a9d\uff0c\u4f60\u53eb\u4e2b\u9b1f\u7aef\u53bb\u4e86\u6ca1\u6709\uff1f\u201d\u592b\u4eba\u9053\uff1a\u201c\u71d5\u7a9d\u90fd\u5f04\u597d\u4e86\uff0c\u53ef\u662f\u90a3\u4e24 \u4f4d\u5b98\u957f\u8bf4\u8981\u65e9\u6b47\uff0c\u5429\u5490\u8859\u5f79\uff0c\u4e0d\u51c6\u6253\u6405\u4ed6\u4eec\u3002\u201d\u53bf\u5b98\u201c\u5514\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4e5f\u597d\uff0c\u90a3\u9ebd\u660e\u65e9 \u518d\u7aef\u53bb\u5427\u3002\u201d\u592b\u4eba\u95ee\u9053\uff1a\u201c\u90a3\u77ee\u4e2a\u756a\u5b50\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u4e3a\u4f55\u671d\u5ef7\u6d3e\u4e86\u4e24\u4e2a\u5fa1\u6797\u519b\u7edf\u9886\u62a4\u9001\u4ed6 \u4eec\uff1f\u201d\u53bf\u5b98\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u90a3\u5e74\u5c11\u7684\u756a\u5b50\uff0c\u542c\u8bf4\u662f\u897f\u57df\u4e00\u4e2a\u5c0f\u56fd\u7684\u738b\u5b50\u5462\uff01\u201d\u592b\u4eba\u9053\uff1a\u201c\u602a\u4e0d \u5f97\u90a3\u4e24\u4e2a\u5fa1\u6797\u519b\u7edf\u9886\u5bf9\u4ed6\u5fc5\u606d\u5fc5\u656c\u3002\u201d\u53bf\u5b98\u9053\uff1a\u201c\u90a3\u8fd8\u987b\u8bf4\uff0c\u90a3\u738b\u5b50\u7684\u8eab\u4efd\u662f\u5916\u56fd\u4f7f\u8282\uff0c\u82e5 \u6709\u610f\u5916\u53d1\u751f\uff0c\u4e0d\u4f46\u62a4\u9001\u7684\u5fa1\u6797\u519b\u7edf\u9886\u8981\u7ed9\u6cbb\u7f6a\uff0c\u5c31\u662f\u7ecf\u8fc7\u7684\u5dde\u53bf\u957f\u5b98\uff0c\u4e5f\u8981\u53d7\u7275\u8fde\u3002\u201d\u592b\u4eba \u9053\uff1a\u201c\u54ce\uff0c\u73b0\u5728\u5175\u8352\u9a6c\u4e71\uff0c\u76d7\u532a\u5982\u6bdb\uff0c\u82e5\u4ed6\u5728\u6211\u4eec\u53bf\u5883\u5185\u51fa\u4e8b\u90a3\u600e\u9ebd\u597d\uff1f\u201d\u53bf\u5b98\u9053\uff1a\u201c\u592b\u4eba \u653e\u5fc3\uff0c\u90a3\u4e24\u4e2a\u7edf\u9886\u90fd\u662f\u597d\u624b\uff0c\u800c\u4e14\u6211\u4eec\u53bf\u5883\u5185\u53c8\u6709\u6570\u5343\u94c1\u9a91\u519b\u9a7b\u624e\uff0c\u8c05\u76d7\u532a\u4e0d\u6562\u4e71\u52a8\u3002\u201d\u8bdd \u867d\u5982\u6b64\uff0c\u5230\u5e95\u62c5\u5fc3\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u90a3\u53bf\u5b98\u81ea\u8a00\u81ea\u8bed\u7684\u9053\uff1a\u201c\u521a\u624d\u5df2\u542c\u5f97\u6572\u4e86\u53c1\u66f4\uff0c\u53ea\u8981\u8fc7\u4e86\u4eca \u65e5\uff0c\u660e\u65e5\u9001\u4ed6\u4eec\u767b\u7a0b\uff0c\u4e0d\u6d88\u534a\u5929\uff0c\u4fbf\u53ef\u8d70\u51fa\u6211\u4eec\u53bf\u5883\u3002\u767d\u65e5\u9752\u5929\uff0c\u6cbf\u8def\u53c8\u6709\u519b\u961f\uff0c\u5b9a\u4fdd\u592a\u5e73 \u65e0\u4e8b\u3002\u201d\u90a3\u77e5\u53bf\u662f\u6b66\u5b98\u51fa\u8eab\uff0c\u6709\u70b9\u80c6\u91cf\uff0c\u5bf9\u592b\u4eba\u9053\uff1a\u201c\u6211\u51fa\u53bb\u5de1\u4e00\u904d\uff0c\u4e5f\u597d\u53eb\u4f60\u5b89\u5fc3\u3002\u201d\u63d0 \u4e86\u4f69\u5200\u51fa\u623f\uff0c\u7389\u7f57\u5239\u6084\u6084\u8ddf\u5728\u4ed6\u7684\u8eab\u5f8c\u3002\u53bf\u5b98\u884c\u5230\u897f\u8fb9\u89d2\u697c\uff0c\u697c\u4e0b\u6709\u51e0\u540d\u5b88\u591c\u7684\u8859\u5f79\uff0c\u89c1\u53bf \u5b98\u6765\u67e5\uff0c\u8fc7\u6765\u884c\u793c\uff0c\u7980\u9053\uff1a\u201c\u5b98\u957f\u4eec\u90fd\u7761\u4e86\uff0c\u5927\u4eba\u653e\u5fc3\uff0c\u6ca1\u4e8b\u513f\uff01\u201d\u53bf\u5b98\u6e38\u76ee\u56db\u987e\uff0c\u9053\uff1a \u201c\u597d\uff0c\u4f60\u4eec\u5c0f\u5fc3\u70b9\u513f\u3002\u201d\u7389\u7f57\u5239\u8eb2\u5728\u4e00\u68f5\u6811\u4e0a\u6697\u7b11\u3002\u770b\u90a3\u53bf\u5b98\u53bb\u5f8c\uff0c\u6b63\u597d\u6709\u4e00\u7247\u9ed1\u4e91\u906e\u8fc7\u6708 \u4eae\uff0c\u7389\u7f57\u5239\u8f7b\u8f7b\u4e00\u63a0\uff0c\u75be\u5982\u98de\u9e1f\u822c\u7684\u4e0a\u4e86\u89d2\u697c\u3002 \u3000\u3000\u89d2\u697c\u91cc\u9ed1\u9edd\u9edd\u7684\uff0c\u7389\u7f57\u5239\u4f0f\u4e86\u4e00\u9635\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u4e0a\u697c\uff0c\u811a\u6b65\u6781\u8f7b\uff0c\u7389\u7f57\u5239\u98d8\u8eab\u8eb2\u4e0a\u6a2a \u6881\uff0c\u90a3\u4eba\u4e0a\u697c\u4e4b\u5f8c\uff0c\u5230\u4e1c\u9762\u4e00\u95f4\u53a2\u623f\u7684\u95e8\u4e0a\u8f7b\u6572\u53c1\u4e0b\uff0c\u623f\u91cc\u7684\u4eba\u71c3\u4e86\u706f\u706b\uff0c\u5728\u5fae\u5f31\u7684\u706b\u5149\u4e2d \u7389\u7f57\u5239\u770b\u51fa\u8fd9\u4eba\u6b63\u662f\u4ee5\u524d\u5728\u5ef6\u5b89\u5e9c\u548c\u81ea\u5df1\u4ea4\u8fc7\u624b\u7684\u4e91\u71d5\u5e73\u3002\u5fc3\u60f3\uff1a\u6b64\u4eba\u4e43\u662f\u5927\u5185\u536b\u58eb\uff0c\u4ed6\u660f \u591c\u5230\u6765\uff0c\u5374\u662f\u4e3a\u4f55\uff1f\u96be\u9053\u671d\u5ef7\u6015\u90a3\u4e24\u4e2a\u5fa1\u6797\u519b\u7edf\u9886\u9876\u4e0d\u4e86\u4e8b\uff0c\u8fd8\u8981\u52a0\u6d3e\u62a4\u536b\u4e0d\u6210\uff1f \u3000\u3000\u4e91\u71d5\u5e73\u8fdb\u4e86\u623f\u4e2d\uff0c\u7389\u7f57\u5239\u53ea\u542c\u5f97\u623f\u4e2d\u7684\u519b\u5b98\u7b11\u9053\uff1a\u201c\u606d\u559c\u4e91\u5927\u4eba\uff0c\u5916\u653e\u505a\u5e26\u5175\u5b98\u6bd4\u5728\u5bab \u5ef7\u4e2d\u597d\u5f97\u591a\u4e86\u3002\u201d\u4e91\u71d5\u5e73\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u4e00\u6837\uff1f\u201d\u623f\u4e2d\u7684\u519b\u5b98\u9053\uff1a\u201c\u5916\u5feb\u603b\u8981\u591a\u4e9b\uff01\u201d\u8fd9\u5f53 \u513f\uff0c\u7389\u7f57\u5239\u53ea\u542c\u5f97\u4e91\u71d5\u5e73\u53d1\u51fa\u8be1\u79d8\u7684\u7b11\u58f0\u3002 \u3000\u3000\u7389\u7f57\u5239\u5fc3\u9053\uff1a\u8fd9\u65e2\u505a\u4e86\u5e26\u5175\u5c06\u5b98\uff0c\u4e3a\u4f55\u5374\u50cf\u5c0f\u5077\u4e00\u822c\uff0c\u5077\u5077\u6478\u6765\u3002\u4e91\u71d5\u5e73\u7b11\u4e86\u4e00\u9635\uff0c \u9053\uff1a\u201c\u76ee\u4e0b\u5c31\u6709\u4e00\u5b97\u6781\u5927\u7684\u6cb9\u6c34\u53ef\u635e\uff0c\u5144\u5f1f\u6b63\u8981\u4e0e\u4e24\u4f4d\u5144\u53f0\u5546\u91cf\u3002\u201d\u623f\u4e2d\u7684\u4e24\u4e2a\u519b\u5b98\u9f50\u9053\uff1a \u201c\u8bf7\u8bf4\u3002\u201d\u4e91\u71d5\u5e73\u9053\uff1a\u201c\u6211\u65e5\u524d\u63a5\u5230\u5218\u5927\u5e05\u8f6c\u4e0b\u7684\u6587\u4e66\uff0c\u8bf4\u662f\u6709\u5916\u56fd\u7684\u4f7f\u8282\u8fc7\u5883\uff0c\u8981\u6211\u534f\u540c \u4fdd\u62a4\u3002\u60f3\u4e0d\u5230\u5c31\u662f\u7531\u4f60\u4eec\u62a4\u9001\uff0c\u8fd9\u597d\u6781\u4e86\uff01\u201d\u623f\u4e2d\u7684\u4e24\u4e2a\u5fa1\u6797\u519b\u7edf\u9886\u4e43\u662f\u540c\u80de\u5144\u5f1f\uff0c\u4e00\u540d\u738b \u5ef7\u798f\uff0c\u4e00\u540d\u738b\u5ef7\u7984\uff0c\u539f\u5148\u4e5f\u662f\u5927\u5185\u7684\u536b\u58eb\uff0c\u548c\u4e91\u71d5\u5e73\u751a\u4e3a\u7a14\u719f\u3002\u738b\u5ef7\u798f\u9053\uff1a\u201c\u4e91\u5144\uff0c\u6211\u4eec\u4e5f \u6599\u4e0d\u5230\u4f60\u5c31\u5728\u6b64\u5730\u9a7b\u519b\u3002\u53ea\u662f\u6211\u4eec\u5306\u5306\u8fc7\u5883\uff0c\u7eb5\u6709\u4ec0\u9ebd\u5916\u5feb\u53ef\u635e\uff0c\u4e5f\u8f6e\u4e0d\u5230\u6211\u4eec\u7684\u4efd\u3002\u201d\u4e91 \u71d5\u5e73\u9053\uff1a\u201c\u6211\u6240\u8bf4\u7684\u5916\u5feb\uff0c\u5c31\u5168\u8981\u9760\u4e24\u4f4d\u5144\u53f0\u5e2e\u5fd9\u3002\u201d\u738b\u5ef7\u7984\u9053\uff1a\u201c\u4e91\u5927\u4eba\u6562\u662f\u8bf4\u7b11\u9ebd\uff1f\u201d \u738b\u5ef7\u798f\u5df2\u77e5\u5176\u610f\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5916\u5feb\u53ef\u635e\u4e0d\u5f97\u3002\u201d\u4e91\u71d5\u5e73\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u738b\u5ef7\u798f\u6084\u58f0\u8bf4 \u9053\uff1a\u201c\u6211\u4eec\u662f\u62a4\u9001\u7684\u4eba\uff0c\u82e5\u7136\u52ab\u4e86\u5916\u90e8\u4f7f\u8282\uff0c\u7f6a\u52a0\u53c1\u7b49\u3002\u4f60\u4e0d\u6015\u6ee1\u95e8\u6284\u65a9\u9ebd\uff1f\u201d\u8bf4\u7740\u8fd8\u7528\u624b \u505a\u4e86\u4e00\u4e2a\u65a9\u5934\u7684\u59ff\u52bf\u3002\u8bdd\u58f0\u5f88\u4f4e\uff0c\u7389\u7f57\u5239\u5728\u5916\u9762\u53ea\u65ad\u65ad\u7eed\u7eed\u542c\u5230\u51e0\u4e2a\u5355\u5b57\uff0c\u53ef\u662f\u7389\u7f57\u5239\u4e43\u662f \u7eff\u6797\u5927\u76d7\uff0c\u95fb\u58f0\u4f1a\u610f\u3002\u5fc3\u60f3\uff1a\u5916\u90a6\u7684\u738b\u5b50\u6765\u671d\uff0c\u7687\u5e1d\u514d\u4e0d\u4e86\u8981\u8d50\u91d1\u94f6\u73cd\u5b9d\uff0c\u8fd9\u679c\u7136\u662f\u5b97\u6781\u5927 \u7684\u9ed1\u9053\u751f\u610f\u3002\u53ef\u662f\u8fd9\u6837\u7684\u201c\u751f\u610f\u201d\uff0c\u9ed1\u9053\u4e0a\u7684\u4eba\u4e5f\u5927\u90fd\u4e0d\u6562\u4e0b\u624b\uff0c\u60f3\u4e0d\u5230\u4e91\u71d5\u5e73\u662f\u671d\u5ef7\u7684\u5c06 \u519b\uff0c\u4e5f\u6562\u52a8\u8fd9\u5ff5\u5934\uff01 \u3000\u3000\u7389\u7f57\u5239\u9759\u5fc3\u8046\u542c\uff0c\u53ea\u542c\u5f97\u4e91\u71d5\u5e73\u9053\uff1a\u201c\u4e3b\u610f\u662f\u60f3\u51fa\u6765\u7684\uff0c\u4f60\u4eec\u5144\u5f1f\u653e\u5fc3\uff0c\u6211\u62c5\u4fdd\u4f60\u4eec\u4ec0 \u9ebd\u7f6a\u90fd\u6ca1\u6709\u3002\u201d\u738b\u5ef7\u798f\u88c5\u6a21\u4f5c\u6837\u8bf4\u9053\uff1a\u201c\u613f\u542c\u6559\u8a00\uff0c\u4ee5\u5f00\u8305\u585e\u3002\u201d\u4e91\u71d5\u5e73\u9053\uff1a\u201c\u73b0\u4e0b\u65f6\u52bf\u6df7 \u4e71\uff0c\u76d7\u532a\u5982\u6bdb\uff0c\u54b1\u4eec\u5077\u5077\u628a\u8fd9\u51e0\u4e2a\u756a\u72d7\u5e72\u4e86\uff0c\u7136\u5f8c\u6211\u518d\u523a\u4f60\u4eec\u4e24\u5200\uff1f\u201d\u738b\u5ef7\u7984\u9a87\u9053\uff1a\u201c\u505a\u4ec0 \u9ebd\uff1f\u201d\u738b\u5ef7\u798f\u7b11\u9053\uff1a\u201c\u50bb\u5144\u5f1f\uff0c\u8fd9\u4e2a\u4e5f\u60f3\u4e0d\u51fa\u6765\u3002\u6211\u4eec\u8ba9\u4e91\u5927\u4eba\u62e3\u4e0d\u662f\u8981\u5bb3\u4e4b\u5904\u523a\u4e0a\u4e24\u5200\uff0c \u5c31\u8bf4\u662f\u4e2d\u9014\u9047\u76d7\uff0c\u529b\u6297\u53d7\u4f24\uff0c\u867d\u7136\u72af\u6709\u4fdd\u62a4\u4e0d\u5468\u4e4b\u7f6a\uff0c\u4f46\u529b\u6297\u53d7\u4f24\uff0c\u7f6a\u540d\u51cf\u7b49\uff0c\u90a3\u6700\u591a\u662f\u524a \u804c\u7f62\u4e86\u3002\u201d\u4e91\u71d5\u5e73\u9053\uff1a\u201c\u4f55\u51b5\u54b1\u4eec\u8fd8\u6709\u9b4f\u516c\u516c\u6491\u8170\uff0c\u8fde\u524a\u804c\u4e5f\u672a\u5fc5\u4f1a\u3002\u5582\uff0c\u5c0f\u7687\u5e1d\u8d50\u4e86\u4ed6\u4eec \u4ec0\u9ebd\u73cd\u5b9d\u3002\u201d\u738b\u5ef7\u798f\u9053\uff1a\u201c\u8be6\u7ec6\u7684\u6211\u4e0d\u77e5\u9053\uff0c\u542c\u9b4f\u516c\u516c\u8bf4\uff0c\u5c0f\u7687\u5e1d\u767b\u57fa\u672a\u4e45\uff0c\u5c31\u6709\u8fdc\u90a6\u7687\u5b50 \u6765\u671d\uff0c\u975e\u5e38\u9ad8\u5174\uff0c\u4ed6\u662f\u5b69\u5b50\u813e\u6c14\uff0c\u4e00\u9ad8\u5174\u5c31\u80e1\u4e71\u628a\u5185\u5e93\u7684\u5b9d\u7269\u9001\u4eba\uff0c\u542c\u8bf4\u53ea\u662f\u4e00\u679d\u78a7\u7389\u73ca \u745a\uff0c\u4ef7\u503c\u4fbf\u8fc7\u767e\u4e07\u3002\u9b4f\u516c\u516c\u8bf4\u65f6\uff0c\u7fa1\u6155\u5230\u6781\u3002\u201d\u4e91\u71d5\u5e73\u9053\uff1a\u201c\u90a3\u51e0\u4e2a\u756a\u72d7\u61c2\u4e0d\u61c2\u6b66\u529f\uff1f\u201d\u738b \u5ef7\u798f\u9053\uff1a\u201c\u770b\u6837\u5b50\u61a7\u5f97\u591a\u5c11\uff0c\u4f46\u4e0d\u662f\u9ad8\u624b\u3002\u201d\u738b\u5ef7\u7984\u9053\uff1a\u201c\u53ea\u662f\u90a3\u4e2a\u5c0f\u5b50\u60f9\u538c\uff1f\u201d\u4e91\u71d5\u5e73 \u9053\uff1a\u201c\u4ec0\u9ebd\u5c0f\u5b50\uff1f\u201d\u738b\u5ef7\u7984\u9053\uff1a\u201c\u90a3\u4e2a\u756a\u90a6\u7687\u5b50\u4e5f\u662f\u602a\u4eba\uff0c\u4ed6\u4e2d\u9014\u9047\u89c1\u4e00\u4e2a\u6f02\u4eae\u7684\u5c0f\u4f19\u5b50\uff0c \u8c08\u5f97\u6295\u673a\uff0c\u90a3\u5c0f\u4f19\u5b50\u8bf4\u897f\u76d7\u532a\u6781\u591a\uff0c\u8def\u9014\u4e0d\u9756\uff0c\u4ed6\u7adf\u4e00\u53e3\u7b54\u5e94\u51c6\u90a3\u5c0f\u5b50\u540c\u884c\uff0c\u628a\u4ed6\u5f53\u6210\u968f \u4ece\u3002\u4e0a\u738b\u5ef7\u798f\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u5e74\u7eaa\u867d\u8f7b\uff0c\u542c\u4ed6\u8a00\u8c08\u4e3e\u6b62\uff0c\u5374\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\u3002\u201d\u7389\u7f57\u5239\u5fc3\u4e2d \u6697\u7b11\uff0c\u94c1\u73ca\u745a\u81ea\u5e7c\u8ddf\u7236\u4eb2\u8d70\u5357\u95ef\u5317\uff0c\u6bd4\u4f60\u8fd9\u4e24\u4e2a\u5446\u9e1f\u5f53\u7136\u8981\u5f3a\u5f97\u591a\u3002\u623f\u95f4\u5185\u4e91\u71d5\u5e73\u9634\u6c89\u6c89\u7684 \u8bf4\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u53eb\u4ec0\u9ebd\u540d\u5b57\uff0c\u73b0\u5728\u5728\u8fd9\u91cc\u9ebd\uff1f\u201d \u3000\u3000\u738b\u5ef7\u798f\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u81ea\u79f0\u59d3\u91d1\u540d\u6208\uff0c\u4ed6\u4eec\u90fd\u4f4f\u5728\u697c\u4e0a\uff0c\u90a3\u756a\u90a6\u738b\u5b50\u548c\u4e24\u4e2a\u968f\u4ece\u4f4f\u5728\u4e1c\u9762 \u53a2\u623f\uff0c\u90a3\u5c0f\u5b50\u4f4f\u5728\u897f\u4fa7\u7684\u5c0f\u623f\u3002\u201d\u4e91\u71d5\u5e73\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e0a\u53bb\u77a7\u4ed6\u4e00\u77a7\uff0c\u4ec0\u9ebd\u8def\u9053\uff0c\u53ef\u7792\u4e0d\u8fc7 \u6211\u7684\u773c\u775b\uff01\u201d\u738b\u5ef7\u7984\u9053\uff1a\u201c\u53ef\u4e0d\u8981\u6253\u8349\u60ca\u86c7\uff01\u201d\u4e91\u71d5\u5e73\u50b2\u7136\u8bf4\u9053\uff1a\u201c\u6599\u4e0d\u4f1a\u9634\u6c9f\u91cc\u7ffb\u8239\uff01\u201d \u738b\u5ef7\u798f\u8c04\u5a9a\u7b11\u9053\uff1a\u201c\u4e91\u5927\u4eba\u4e45\u5386\u6c5f\u6e56\uff0c\u8f7b\u529f\u8d85\u5353\uff0c\u90a3\u5c0f\u5b50\u80fd\u6709\u591a\u5927\u672c\u9886\u3002\u8d24\u5f1f\uff0c\u4f60\u8fd9\u662f\u592a\u8fc7 \u8651\u4e86\u3002\u201d\u4e91\u71d5\u5e73\u5fae\u5fae\u4e00\u7b11\uff0c\u7389\u7f57\u5239\u5728\u5fc3\u91cc\u4e5f\u51e0\u4e4e\u7b11\u51fa\u58f0\u6765\u3002 \u3000\u3000\u4e91\u71d5\u5e73\u8d70\u51fa\u623f\u95e8\uff0c\u65bd\u5c55\u8f7b\u529f\u672c\u9886\uff0c\u4e00\u6309\u6a90\u89d2\uff0c\u98de\u4e0a\u9876\u697c\uff0c\u5374\u4e0d\u77e5\u7389\u7f57\u5239\u5df2\u5982\u5f71\u9644\u5f62\uff0c\u8ddf \u5728\u8eab\u5f8c\u3002\u4e91\u71d5\u5e73\u5bfb\u5230\u897f\u4fa7\u7684\u5c0f\u623f\uff0c\u53d6\u51fa\u4e00\u4e2a\u5f62\u5982\u9e64\u5634\u7684\u4e1c\u897f\uff0c\u5728\u9ed1\u6697\u4e2d\u53d1\u51fa\u4e00\u70b9\u7ea2\u4eae\u4eae\u7684\uff0c \u597d\u4f3c\u9999\u706b\u4e00\u822c\u3002\u7389\u7f57\u5239\u662f\u5927\u884c\u5bb6\uff0c\u4e00\u770b\u4fbf\u77e5\u4e91\u71d5\u5e73\u6253\u7684\u662f\u4e0b\u6d41\u4e3b\u610f\uff0c\u60f3\u7528\u201c\u9e21\u9e23\u4e94\u9f13\u8fd4\u9b42 \u9999\u201d\u5f04\u6655\u91cc\u9762\u7684\u4eba\uff0c\u7136\u5f8c\u8fdb\u53bb\u641c\u7d22\u3002\u7389\u7f57\u5239\u5fc3\u4e2d\u9a82\u9053\uff1a\u201c\u8fd9\u6789\u662f\u671d\u5ef7\u7684\u5e26\u5175\u5b98\uff0c\u5374\u5e72\u9ed1\u9053\u4e0a \u4e0b\u53c1\u6d41\u7684\u52fe\u5f53\u3002\u201d\u672c\u5f85\u62d4\u5251\u628a\u4ed6\u6740\u6389\uff0c\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5728\u8fd9\u91cc\u95f9\u51fa\u4e8b\u6765\uff0c\u5374\u662f\u4e0d\u59a5\u3002\u770b\u4e91\u71d5\u5e73\u6b63 \u60f3\u628a\u9999\u706b\u63d2\u8fdb\u7a97\u9699\uff0c\u7389\u7f57\u5239\u624b\u6307\u4e00\u5f39\uff0c\u628a\u72ec\u95e8\u6697\u5668\u201c\u4e5d\u661f\u5b9a\u5f62\u9488\u201d\u5c04\u51fa\uff0c\u4e91\u71d5\u5e73\u5ffd\u89c9\u5fae\u98ce\u98d2 \u7136\uff0c\u9999\u706b\u5df2\u706d\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u6e38\u76ee\u56db\u987e\uff0c\u7ad6\u8033\u7ec6\u542c\uff0c\u7389\u7f57\u5239\u65e9\u85cf\u5728\u697c\u89d2\u98de\u6a90\u4e4b\u5f8c\uff0c\u4e91\u71d5\u5e73\u542c\u4e0d \u5230\u534a\u70b9\u58f0\u8272\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u91cd\u628a\u8ff7\u9999\u71c3\u70b9\uff0c\u6b63\u60f3\u518d\u63d2\u8fdb\u7a97\u9699\uff0c\u7389\u7f57\u5239\u624b\u6307\u4e00\u5f39\uff0c\u98de\u9488\u518d\u5c04\uff0c\u4e91 \u71d5\u5e73\u53c8\u89c9\u5fae\u98ce\u98d2\u7136\uff0c\u9999\u706b\u518d\u706d\u3002\u98de\u9488\u6781\u5c0f\uff0c\u7389\u7f57\u5239\u51fa\u624b\u53c8\u5feb\uff0c\u4e91\u71d5\u5e73\u7adf\u4e0d\u77e5\u9053\u9999\u706b\u4e3a\u4f55\u7184 \u706d\u3002\u5982\u662f\u4e00\u8fde\u8bd5\u4e86\u53c1\u6b21\uff0c\u53c1\u6b21\u90fd\u7ed9\u7389\u7f57\u5239\u6253\u706d\uff0c\u4e91\u71d5\u5e73\u6bdb\u9aa8\u609a\u7136\uff0c\u6025\u5fd9\u4e0b\u697c\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u5fa1\u6797\u519b\u7edf\u9886\u89c1\u4e91\u71d5\u5e73\u8fd9\u6837\u5feb\u4fbf\u56de\uff0c\u9887\u611f\u610f\u5916\u3002\u738b\u5ef7\u798f\u95ee\u9053\uff1a\u201c\u4e91\u5927\u4eba\u53ef\u67e5\u51fa\u90a3\u5c0f \u5b50\u662f\u4ec0\u9ebd\u8def\u9053\u9ebd\uff1f\u201d\u4e91\u71d5\u5e73\u9762\u4e0a\u4e00\u7ea2\uff0c\u542b\u7cca\u7b54\u9053\uff1a\u201c\u662f\u897f\u5317\u9ed1\u9053\u4e0a\u7684\u9ad8\u624b\u3002\u201d\u738b\u5ef7\u798f\u9053\uff1a \u201c\u6211\u4eec\u5144\u5f1f\u4e5f\u6599\u4ed6\u662f\u9ed1\u9053\u4e0a\u7684\u670b\u53cb\uff0c\u89ca\u89ce\u8fd9\u5e2e\u73cd\u5b9d\u7684\u3002\u201d\u4e91\u71d5\u5e73\u9053\uff1a\u201c\u8def\u4e0a\u53ef\u78b0\u8fc7\u4ec0\u9ebd\u602a\u4e8b \u9ebd\uff1f\u201d\u738b\u5ef7\u7984\u9053\uff1a\u201c\u4e00\u8def\u4e0a\u90fd\u6ca1\u4e8b\u60c5\uff0c\u53ea\u662f\u6628\u665a\u5c06\u5230\u53bf\u57ce\u4e4b\u65f6\uff0c\u5374\u78b0\u5230\u5982\u6b64\u4e00\u6869\u5f02\u4e8b\u3002\u201d\u5f53 \u4e0b\u628a\u78b0\u5230\u7389\u7f57\u5239\u7684\u4e8b\u60c5\u8bf4\u4e86\uff0c\u8fd8\u9053\uff1a\u201c\u90a3\u4eba\u5feb\u4f3c\u75be\u98ce\uff0c\u6211\u4eec\u8fde\u4ed6\u7684\u9762\u8c8c\u90fd\u77a7\u4e0d\u6e05\u695a\uff0c\u771f\u662f\u90aa \u95e8\uff01\u201d\u4e91\u71d5\u5e73\u6c89\u541f\u534a\u664c\uff0c\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u660e\u65e5\u52a8\u624b\u4e4b\u65f6\uff0c\u5206\u90a3\u5c0f\u5b50\u4e00\u4efd\u3002\u82e5\u4ed6\u4e0d\u80af\u5c31\u8303\uff0c \u6211\u81ea\u6709\u6cd5\u5bf9\u4ed8\u3002\u5bb9\u4e8c\u54e5\u6b63\u5728\u6211\u7684\u8425\u4e2d\uff0c\u6211\u9080\u4ed6\u4e00\u540c\u6765\u597d\u4e86\u3002\u201d \u3000\u3000\u8fd9\u756a\u8bdd\u7389\u7f57\u5239\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u597d\u6781\u597d\u6781\uff01\u660e\u65e5\u6211\u6b63\u597d\u4e00\u7bad\u53cc\u96d5\u3002\u5148\u628a\u8fd9\u4e9b \u72d7\u8d3c\u6740\u4e86\uff0c\u7136\u5f8c\u628a\u90a3\u5e2e\u73e0\u5b9d\u72ec\u5360\u3002\u54c8\uff0c\u771f\u662f\u5929\u8d50\u826f\u673a\uff0c\u6211\u8981\u91cd\u805a\u4e49\u6c11\uff0c\u5360\u5c71\u4e3a\u738b\u3002\u548c\u5b98\u519b\u5bf9 \u6297\uff0c\u90a3\u662f\u975e\u94b1\u4e0d\u884c\u3002\u8fd9\u5e2e\u73e0\u5b9d\uff0c\u542c\u4ed6\u4eec\u8bf4\u6765\uff0c\u4ef7\u503c\u4e0d\u4e0b\u5343\u4e07\uff0c\u6709\u4e86\u8fd9\u7b14\u94b1\uff0c\u6211\u53ef\u4e0d\u5fc5\u518d\u53e6\u52a8 \u8111\u7b4b\u4e86\u3002\u201d\u518d\u542c\u4e00\u9635\uff0c\u542c\u5f97\u4e91\u71d5\u5e73\u548c\u738b\u5ef7\u798f\u7ea6\u597d\u52a8\u624b\u7684\u5730\u70b9\uff0c\u662f\u79bb\u57ce\u4e94\u5341\u591a\u91cc\u7684\u201c\u91ce\u732a \u6797\u201d\u3002\u7389\u7f57\u5239\u6697\u6697\u53d1\u7b11\u3002 \u3000\u3000\u8fd9\u201c\u91ce\u732a\u6797\u201d\u662f\u6709\u540d\u8352\u9669\u4e4b\u5730\uff0c\u7389\u7f57\u5239\u5fc3\u9053\uff1a\u4ed6\u4eec\u9009\u8fd9\u5730\u65b9\u4e0b\u624b\uff0c\u771f\u662f\u6df1\u5408\u543e\u5fc3\u3002\u6599\u4e91 \u71d5\u5e73\u4e0d\u6562\u518d\u4e0a\u697c\u7aa5\u63a2\uff0c\u4fbf\u6084\u6084\u8d70\u4e86\u3002 \u3000\u3000\u5176\u5b9e\u90a3\u4e2a\u80e1\u670d\u5c11\u5e74\u5e76\u975e\u201c\u756a\u90a6\u738b\u5b50\u201d\uff0c\u4ed6\u662f\u5357\u7586\u7f57\u5e03\u65cf\u5927\u914b\u957f\u5510\u739b\u7684\u513f\u5b50\uff0c\u540d\u53eb\u5510\u52aa\u3002 \u5357\u7586\u79cd\u65cf\u751a\u591a\uff0c\u5404\u4e0d\u7edf\u5c5e\uff0c\u5230\u4e86\u5510\u739b\u7ee7\u627f\u7f57\u5e03\u65cf\u914b\u957f\u4e4b\u5f8c\uff0c\u8054\u5408\u5404\u65cf\uff0c\u7ed3\u6210\u540c\u76df\uff0c\u81ea\u4e3a\u76df \u4e3b\u3002\u5510\u739b\u52b1\u7cbe\u56fe\u6cbb\uff0c\u60f3\u628a\u5357\u7586\u5efa\u6210\u4e00\u56fd\uff0c\u56e0\u6b64\u6d3e\u9063\u513f\u5b50\u6765\u671d\uff0c\u85c9\u6b64\u89c2\u6469\u201c\u4e2d\u539f\u4e0a\u56fd\u201d\u7684\u5178\u7ae0 \u6587\u7269\u3002\u660e\u671d\u65b0\u7687\u5e1d\u7531\u6821\u4e43\u662f\u4e00\u4e2a\u5c0f\u5b69\uff0c\u6839\u672c\u4e0d\u6e05\u695a\u5357\u7586\u5404\u65cf\u7684\u5236\u5ea6\uff0c\u628a\u5927\u914b\u957f\u5f53\u4f5c\u201c\u756a \u738b\u201d\uff0c\u56e0\u4e4b\u4e5f\u5c31\u628a\u5510\u52aa\u5f53\u6210\u201c\u7687\u5b50\u201d\u3002\u5176\u65f6\u660e\u671d\u56fd\u52bf\u5df2\u5f31\uff0c\u85e9\u5c5e\u4e45\u5df2\u4e0d\u6765\u671d\u8d21\u3002\u7531\u6821\u767b\u57fa\u672a \u4e45\uff0c\u4fbf\u6709\u5357\u7586\u7f57\u5e03\u65cf\u7684\u4f7f\u8005\u6765\u8fdb\u8d21\u6c57\u8840\u5b9d\u9a6c\u5174\u548c\u9617\u7f8e\u7389\uff0c\u56e0\u6b64\u751a\u4e3a\u9ad8\u5174\uff0c\u5927\u81e3\u4eec\u4e3a\u4e86\u8ba8\u7531\u6821 \u6b22\u5fc3\uff0c\u4e5f\u5c31\u628a\u7f57\u5e03\u65cf\u8bf4\u6210\u897f\u57df\u4e00\u4e2a\u201c\u5c0f\u56fd\u201d\u3002\u7531\u6821\u4e00\u65f6\u5174\u8d77\uff0c\u4fbf\u628a\u5927\u6279\u5b9d\u7269\u8d4f\u8d50\u7ed9\u4ed6\u3002\u6240\u4ee5 \u5510\u52aa\u867d\u975e\u7687\u5b50\uff0c\u6000\u6709\u91cd\u5b9d\uff0c\u5374\u662f\u771f\u60c5\u3002 \u3000\u3000\u94c1\u73ca\u745a\u4e3a\u5cb3\u545c\u73c2\u62d2\u5a5a\uff0c\u8d1f\u6c14\u518d\u5ea6\u79bb\u5f00\u7236\u4eb2\u4e4b\u5f8c\uff0c\u56de\u5230\u897f\uff0c\u5728\u9014\u4e2d\u9047\u5230\u5510\u52aa\u8fd9\u4e00\u73ed\u4eba\u3002\u94c1 \u73ca\u745a\u5e74\u7eaa\u867d\u5c0f\uff0c\u9605\u5386\u5374\u4e30\u3002\u4e00\u770b\u4fbf\u77e5\u5510\u52aa\u6000\u6709\u91d1\u73e0\u91cd\u5b9d\uff0c\u94c1\u73ca\u745a\u662f\u4e2a\u5014\u5f3a\u7684\u5c11\u5973\uff0c\u56de\u5230\u897f\uff0c \u7acb\u5b9a\u4e3b\u610f\uff0c\u60f3\u5b66\u7389\u7f57\u5239\u4e00\u6837\uff0c\u5360\u5c71\u4e3a\u738b\u3002\u6240\u4ee5\u5979\u4e5f\u60f3\u52ab\u8fd9\u5e2e\u73e0\u5b9d\u3002 \u3000\u3000\u4e14\u8bf4\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u738b\u5ef7\u798f\u5144\u5f1f\u7ee7\u7eed\u62a4\u9001\u5510\u52aa\u767b\u7a0b\uff0c\u8d70\u4e86\u4e00\u9635\uff0c\u5374\u820d\u4e86\u5b98\u9053\uff0c\u6284\u5c71\u8fb9\u5c0f\u8def \u884c\u8d70\u3002\u5510\u52aa\u9887\u4e3a\u5947\u602a\uff0c\u738b\u5ef7\u798f\u9053\uff1a\u201c\u82e5\u8d70\u5b98\u9053\uff0c\u4eca\u65e5\u96be\u5230\u7518\u6cc9\u201c\u5730\u540d\u201d\u3002\u53cd\u6b63\u53bf\u9a7b\u6709\u5927\u519b\uff0c \u76d7\u532a\u6f5c\u8ff9\uff0c\u4e0d\u5982\u6284\u5c0f\u5cb8\u884c\u8d70\uff0c\u8def\u7a0b\u53ef\u7f29\u77ed\u8bb8\u591a\u3002\u201d\u5510\u52aa\u4e0d\u719f\u9053\u8def\uff0c\u542c\u5f97\u4e5f\u662f\u9053\u7406\uff0c\u4fbf\u7531\u5f97\u4ed6 \u4eec\u5e26\u8def\u3002\u94c1\u73ca\u745a\u77e5\u9053\u4eca\u65e5\u5fc5\u7136\u6709\u4e8b\uff0c\u6697\u52a0\u6212\u5907\u3002 \u3000\u3000\u9053\u8def\u8d8a\u884c\u8d8a\u9669\uff0c\u4e2d\u5348\u65f6\u5206\uff0c\u7a7f\u4eba\u4e00\u5904\u4e1b\u6797\uff0c\u6797\u4e2d\u5c71\u8def\uff0c\u7ea6\u6709\u4e94\u5c3a\u591a\u5bbd\uff0c\u4ec5\u53ef\u5bb9\u5355\u9a91\u901a \u8fc7\uff0c\u5939\u9053\u662f\u8346\u68d8\u8513\u8349\uff0c\u8352\u51c9\u4e4b\u6781\u3002\u738b\u5ef7\u798f\u9053\uff1a\u201c\u54b1\u4eec\u4e14\u5728\u8fd9\u91cc\u7a0d\u6b47\u4e00\u56de\u3002\u201d\u4e0d\u5f85\u5510\u52aa\u5141\u8bb8\uff0c \u4fbf\u4e0b\u4e86\u9a6c\u3002\u5510\u52aa\u4e0d\u6599\u6709\u4ed6\uff0c\u548c\u968f\u4ece\u4e5f\u4e0b\u4e86\u9a6c\u3002\u94c1\u73ca\u745a\u563b\u563b\u51b7\u7b11\uff0c\u738b\u5ef7\u798f\u9053\uff1a\u201c\u91d1\u5144\u5f1f\uff0c\u54b1\u4eec \u4e00\u7897\u6c34\u5927\u5bb6\u559d\u5566\uff01\u201d\u5510\u52aa\u6115\u7136\u95ee\u9053\uff1a\u201c\u90a3\u513f\u6709\u6c34\u554a\uff01\u201d\u738b\u5ef7\u798f\u5144\u5f1f\u653e\u58f0\u5927\u7b11\uff0c\u5bf9\u9762\u5c71\u8def\u4e0a\u4e24 \u9a91\u98de\u5954\u800c\u6765\uff0c\u5176\u4e2d\u4e00\u4eba\u6b63\u662f\u4e91\u71d5\u5e73\uff0c\u4ed6\u5df2\u6362\u4e86\u5e73\u6c11\u670d\u9970\uff0c\u4e0d\u518d\u662f\u519b\u5b98\u88c5\u675f\u4e86\u3002 \u3000\u3000\u94c1\u73ca\u745a\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u73ed\u4eba\u662f\u8c0b\u8d22\u5bb3\u547d\u7684\u72d7\u5f3a\u76d7\uff01\u201d\u62d4\u51fa\u7eff\u7389\u7bab\uff0c\u5411\u738b\u5ef7\u798f\u8170\u95f4\u4e00\u70b9\uff0c \u738b\u5ef7\u798f\u8f6c\u8eab\u4e00\u638c\uff0c\u9a82\u9053\uff1a\u201c\u4e0d\u53d7\u62ac\u4e3e\u7684\u8d31\u4e1c\u897f\uff0c\u597d\u5fc3\u5206\u4f60\u4e00\u4efd\uff0c\u4f60\u5374\u4e0d\u9886\u60c5\uff0c\u60f3\u72ec\u5360\u9ebd\uff1f\u201d \u94c1\u73ca\u745a\u7389\u7bab\u8fde\u6325\uff0c\u5168\u662f\u5224\u5b98\u7b14\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u628a\u738b\u5ef7\u798f\u8feb\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\u3002\u5510\u52aa\u5927\u60ca\uff0c\u731b\u9192\u8fc7 \u6765\uff0c\u4e00\u58f0\u5927\u543c\uff0c\u5411\u738b\u5ef7\u7984\u8fce\u9762\u6293\u53bb\uff0c\u738b\u5ef7\u7984\u62d4\u51fa\u4f69\u5200\u4e00\u65ab\uff0c\u90a3\u6599\u5510\u52aa\u7cbe\u65bc\u6454\u89d2\u4e4b\u672f\uff0c\u624b\u81c2\u4e00 \u4f38\uff0c\u500f\u7136\u628a\u738b\u5ef7\u7984\u7684\u624b\u8155\u5201\u4f4f\uff0c\u4ed6\u7684\u4e24\u4e2a\u968f\u4ece\uff0c\u90fd\u662f\u5357\u7586\u7740\u540d\u7684\u529b\u58eb\uff0c\u5404\u53d6\u51fa\u62a4\u8eab\u94c1\uff0c\u53cc\u5939 \u51fb\uff0c\u8fc5\u82e5\u5954\u96f7\u3002 \u3000\u3000\u738b\u5ef7\u7984\u6b66\u529f\u8f83\u5f31\uff0c\u624b\u8155\u53c8\u7ed9\u5510\u52aa\u5201\u4f4f\uff0c\u731d\u4e0d\u53ca\u9632\uff0c\u5357\u7586\u4e24\u4e2a\u529b\u58eb\u53cc\u9f50\u4e0b\uff0c\u987f\u65f6\u8111\u6d46\u8ff8 \u88c2\uff0c\u6b7b\u65bc\u975e\u547d\u3002 \u3000\u3000\u4e91\u71d5\u5e73\u5feb\u9a6c\u9a70\u5230\uff0c\u4e00\u8dc3\u800c\u4e0b\uff0c\u5357\u7586\u4e24\u4e2a\u529b\u58eb\u821e\u8fce\u654c\uff0c\u4e91\u71d5\u5e73\u7cbe\u65bc\u897f\u85cf\u5bc6\u5b97\u79d8\u4f20\u7684\u201c\u67d4 \u529f\u201d\uff0c\u89e3\u4e0b\u8170\u5e26\uff0c\u821e\u5f97\u547c\u547c\u98ce\u54cd\uff0c\u94c1\u4e00\u5230\uff0c\u7ed9\u4ed6\u8170\u5e26\u4e00\u5377\uff0c\u8f7b\u8f7b\u4e00\u626f\uff0c\u201c\u67d4\u529f\u201d\u7684\u9053\u7406\u548c\u592a \u6781\u62f3\u76f8\u540c\uff0c\u90fd\u662f\u501f\u529b\u6253\u529b\uff0c\u4ee5\u56db\u4e24\u800c\u62e8\u5343\u65a4\uff0c\u8fd9\u4e24\u4e2a\u5357\u7586\u529b\u58eb\uff0c\u4e0d\u61c2\u4e2d\u571f\u6b66\u529f\u7684\u5965\u79d8\uff0c\u94c1\u821e \u5f97\u52b2\u9053\u5341\u8db3\uff0c\u7ed9\u4ed6\u501f\u65b9\u4e00\u593a\uff0c\u4e24\u67c4\u94c1\u5148\u5f8c\u88ab\u593a\u51fa\u624b\u3002\u72c2\u7b11\u58f0\u4e2d\uff0c\u4e91\u71d5\u5e73\u63c9\u8eab\u76f4\u8fdb\uff0c\u628a\u8fd9\u4e24\u540d \u529b\u58eb\u5148\u5f8c\u5377\u8d77\uff0c\u63b7\u5411\u5d16\u77f3\u4e4b\u4e0a\uff0c\u7a7a\u6709\u4e00\u8eab\u795e\u529b\uff0c\u7adf\u81ea\u8840\u6d12\u8352\u6797\u3002 \u3000\u3000\u8fd9\u65f6\u94c1\u73ca\u745a\u548c\u738b\u5ef7\u798f\u6b63\u6253\u5f97\u96be\u5206\u96be\u89e3\uff0c\u738b\u5ef7\u798f\u6b66\u529f\u6bd4\u4e43\u5f1f\u5f3a\u5f97\u591a\uff0c\u4e00\u679d\u7ec3\u5b50\u4f7f\u5f97\u98ce\u96e8\u4e0d \u900f\uff0c\u4f46\u94c1\u73ca\u745a\u7684\u7389\u7bab\u70b9\u7a74\u4e4b\u672f\uff0c\u51fa\u81ea\u5bb6\u4f20\uff0c\u81ea\u6210\u4e00\u8def\uff0c\u53ef\u4f5c\u5224\u5b98\u7b14\u7528\uff0c\u53c8\u53ef\u5f53\u4e94\u884c\u5251\u4f7f\uff0c\u867d \u7136\u6c14\u529b\u8f83\u5f31\uff0c\u5374\u662f\u62db\u6570\u795e\u5947\u3002 \u3000\u3000\u4e91\u71d5\u5e73\u53eb\u9053\uff1a\u201c\u4f60\u6765\u6536\u62ac\u8fd9\u4e2a\u756a\u72d7\uff0c\u6211\u6765\u4f1a\u8fd9\u5c0f\u5b50\u3002\u201d\u8170\u5e26\u547c\u7684\u4e00\u58f0\uff0c\u5411\u94c1\u73ca\u745a\u5934\u4e0a\u5377 \u53bb\u3002\u4e91\u71d5\u5e73\u770b\u4e86\u94c1\u73ca\u745a\u7684\u62db\u6570\uff0c\u89c9\u5979\u70b9\u7a74\u4e4b\u672f\u867d\u7136\u795e\u5999\uff0c\u6b66\u529f\u8fd8\u4e0d\u662f\u4e0a\u4e58\u3002\u60f3\u8d77\u6628\u665a\u4e4b\u4e8b\uff0c \u6df1\u89c9\u5947\u602a\u3002\u5fc3\u9053\uff1a\u65e9\u77e5\u8fd9\u5c0f\u5b50\u6b66\u529f\u4e0d\u8fc7\u5982\u6b64\uff0c\u771f\u4e0d\u5fc5\u9080\u5bb9\u4e8c\u54e5\u6765\u3002 \u3000\u3000\u94c1\u73ca\u745a\u6325\u7bab\u8fce\u6218\uff0c\u6218\u4e86\u5341\u9980\u5eff\u62db\uff0c\u5ffd\u89c1\u6797\u83bd\u5bc6\u83c1\u4e4b\u4e2d\uff0c\u54e8\u58f0\u5927\u8d77\uff0c\u68ee\u6797\u4e24\u8fb9\uff0c\u6d8c\u51fa\u5341\u9980 \u5065\u6c49\uff0c\u5fc3\u4e2d\u4e00\u614c\uff0c\u4e91\u71d5\u5e73\u8170\u5e26\u592d\u77eb\u5982\u9f99\uff0c\u4e00\u626b\u4e00\u5377\uff0c\u628a\u94c1\u73ca\u745a\u76ae\u5e3d\u626b\u843d\uff0c\u73b0\u51fa\u4e00\u5934\u79c0\u53d1\u3002\u4e91 \u71d5\u5e73\u5446\u4e86\u4e00\u5446\uff0c\u9053\uff1a\u201c\u54c8\uff0c\u94c1\u73ca\u745a\uff0c\u539f\u6765\u662f\u4f60\uff01\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u65e2\u77e5\u662f\u6211\uff0c\u5c31\u8be5\u5feb\u6eda\u3002\u201d\u4e91 \u71d5\u5e73\u6e38\u76ee\u56db\u987e\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u90a3\u8d3c\u8001\u5b50\u4e0d\u548c\u4f60\u6765\uff1f\u54c8\uff0c\u4f60\u8fd8\u5439\u4ec0\u9ebd\u5927\u6c14\uff1f\u201d\u8170\u5e26\u4e00\u626b\uff0c\u53c8\u5411\u94c1 \u73ca\u745a\u7389\u7bab\u5377\u5230\u3002 \u3000\u3000\u518d\u8bf4\u6797\u4e2d\u6d8c\u51fa\u7684\u90a3\u4e24\u80a1\u5f3a\u4eba\uff0c\u90fd\u662f\u5357\u5317\u7740\u540d\u7684\u5f3a\u76d7\u5934\u5b50\uff0c\u4e3a\u4e86\u52ab\u593a\u91d1\u94f6\u91cd\u5b9d\uff0c\u4e0d\u60dc\u5192\u5b98 \u519b\u5305\u56f4\u4e4b\u9669\uff0c\u8ddf\u8e2a\u6765\u5230\u68ee\u6797\u3002\u5317\u7684\u608d\u76d7\u8fc7\u5929\u661f\u548c\u4e5d\u8282\u72f8\u9996\u5148\u51b2\u4e0a\uff0c\u53ea\u89c1\u4e00\u4e2a\u8001\u5934\uff0c\u957f\u987b\u98d8 \u4f5b\uff0c\u624b\u91cc\u62ff\u7740\u4e00\u6839\u957f\u8fbe\u53c1\u5c3a\u7684\u94c1\u83f8\uff0c\u5927\u53e3\u5927\u53e3\u7684\u55b7\u70df\u3002\u8fc7\u5929\u661f\u559d\u9053\uff1a\u201c\u662f\u9053\u4e0a\u7684\u670b\u53cb\u5417\uff1f\u201d \u90a3\u8001\u5934\u95f7\u58f0\u4e0d\u54cd\uff0c\u5f85\u5f97\u4e24\u4eba\u51b2\u5230\u773c\u524d\uff0c\u94c1\u83f8\u7a81\u7136\u6a2a\u7a7a\u4e00\u626b\uff0c\u4e00\u62db\u201c\u4e91\u9ebe\u53c1\u821e\u201d\uff0c\u628a\u8fc7\u5929\u661f\u7684 \u6d41\u661f\u548c\u4e5d\u8282\u72f8\u7684\u201c\u4e5d\u8282\u97ad\u201d\u4e00\u9f50\u6c64\u5f00\uff0c\u4fe1\u624b\u4e00\u70b9\uff0c\u8fc7\u5929\u661f\u201c\u5495\u549a\u201d\u5012\u5730\uff0c\u4e5d\u8282\u72f8\u8eab\u6cd5\u8f7b\u7075\uff0c \u4e00\u7ed5\u7ed5\u5230\u8001\u5934\u8eab\u5f8c\uff0c\u8f6c\u97ad\u75be\u626b\uff0c\u4e0d\u6599\u90a3\u8001\u5934\u5374\u50cf\u80cc\u5f8c\u957f\u6709\u773c\u775b\u4e00\u822c\uff0c\u53cd\u624b\u4e00\u51fb\uff0c\u6b63\u6b63\u6253\u5728\u4e5d \u8282\u72f8\u80ef\u9aa8\u4e4b\u4e0a\uff0c\u4e5d\u8282\u72f8\u60e8\u53eb\u4e00\u58f0\uff0c\u80eb\u9aa8\u788e\u88c2\uff0c\u5012\u5730\u72c2\u55e5\u3002 \u3000\u3000\u8fd9\u8001\u5934\u6b63\u662f\u4e91\u71d5\u5e73\u9080\u6765\u7684\u5e2e\u624b\uff0c\u540d\u53eb\u5bb9\u4e00\u4e1c\u3002\u4ed6\u548c\u5e94\u4fee\u9633\u6700\u597d\uff0c\u5f53\u5e74\u5e94\u4fee\u9633\u4e3a\u4e86\u5bf9\u4ed8\u7389 \u7f57\u5239\uff0c\u5728\u534e\u5c71\u7edd\u9876\u6446\u4e0b\u201c\u4e03\u7edd\u9635\u201d\uff0c\u539f\u9080\u7684\u6709\u4ed6\uff0c\u5f8c\u6765\u4ed6\u56e0\u4e8b\u4e0d\u6765\uff0c\u6240\u4ee5\u624d\u7531\u90d1\u6d2a\u53f0\u4e34\u65f6\u62c9 \u4e86\u5353\u4e00\u822a\u5145\u6570\u3002\u201c\u4e8b\u8be6\u7b2c\u4e00\u56de\u3002\u201d\u5e94\u4fee\u9633\u4e3a\u6b64\u5341\u5206\u53ef\u60dc\uff0c\u5e38\u8bf4\u5f53\u5e74\u82e5\u662f\u5bb9\u4e00\u4e1c\u80fd\u6765\uff0c\u7389\u7f57\u5239 \u65e9\u5df2\u88ab\u4ed6\u4eec\u5408\u529b\u6740\u4e86\u3002\u7531\u6b64\u4e5f\u53ef\u89c1\u5bb9\u4e00\u4e1c\u7684\u6b66\u529f\u975e\u540c\u5c0f\u9119\uff01 \u3000\u3000\u7fa4\u76d7\u89c1\u5bb9\u4e00\u4e1c\u51fa\u624b\u5389\u5bb3\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u6b63\u60f3\u9f50\u4e0a\uff0c\u5ffd\u542c\u5f97\u5bb9\u4e00\u4e1c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u81ed\u5f3a\u76d7\uff0c\u4f60 \u4eec\u4e2d\u4f0f\u5566\uff01\u201d\u5f15\u542d\u957f\u5578\uff0c\u6797\u4e2d\u558a\u58f0\u56db\u8d77\uff0c\u6d8c\u51fa\u767e\u9980\u5065\u5352\uff0c\u4e2a\u4e2a\u8eab\u62ab\u94c1\u7532\uff0c\u6309\u5f26\u5f85\u5c04\u3002\u539f\u6765\u4e91 \u71d5\u5e73\u4e5f\u6599\u5230\u4f1a\u6709\u5f3a\u4eba\u5192\u9669\u62a2\u52ab\uff0c\u6240\u4ee5\u6697\u4e2d\u8c03\u4e86\u5fc3\u8179\u7cbe\u5175\u5728\u6b64\u57cb\u4f0f\u3002\u8fd9\u4e00\u4e0b\uff0c\u987f\u65f6\u628a\u7fa4\u76d7\u56f4\u5728 \u6838\u5fc3\uff0c\u770b\u770b\u5c31\u8981\u52a8\u624b\u3002 \u3000\u3000\u518d\u8bf4\u94c1\u73ca\u745a\u529b\u6297\u5f3a\u654c\uff0c\u9669\u8c61\u9891\u751f\u3002\u5510\u52aa\u4e5f\u7ed9\u738b\u5ef7\u798f\u8feb\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u73af\u624b\u4e4b\u529b\u3002 \u4e91\u71d5\u5e73\u7684\u8170\u5e26\u8d8a\u5c55\u8d8a\u5feb\uff0c\u4fe8\u5982\u4e00\u6761\u7389\u9f99\u76d8\u7a7a\u98de\u821e\u3002\u94c1\u73ca\u745a\u6b63\u5728\u5403\u7d27\uff0c\u5ffd\u542c\u4e00\u58f0\u957f\u7b11\uff0c\u63a0\u8fc7\u6797 \u9645\u4e0a\u7a7a\u3002\u94c1\u73ca\u745a\u5927\u559c\u53eb\u9053\uff1a\u201c\u7389\u7f57\u5239\u6765\u5566\uff01\u201d\u4e91\u71d5\u5e73\u542c\u5f97\u201c\u7389\u7f57\u5239\u201d\u53c1\u5b87\uff0c\u5c31\u4f3c\u9f20\u513f\u95fb\u4e86\u732b \u53eb\u4e00\u822c\uff0c\u624b\u90fd\u8f6f\u4e86\u3002\u7389\u7f57\u5239\u58f0\u5230\u4eba\u5230\uff0c\u4e0d\u8fc7\u4e24\u53c1\u7167\u9762\uff0c\u5c31\u4e00\u5251\u70b9\u4e2d\u4e91\u71d5\u5e73\u80a9\u9aa8\u7a74\uff0c\u628a\u4ed6\u8e22\u8fc7 \u4e00\u8fb9\u3002\u94c1\u73ca\u745a\u5ffd\u7136\u51d1\u4e86\u4e0a\u6765\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u4e0d\u8981\u4f24\u90a3\u4e2a\u756a\u4eba\u3002\u201d\u7389\u7f57\u5239\u5fae\u5fae\u4e00\u695e\uff0c \u9053\uff1a\u201c\u5514\uff0c\u4e5f\u597d\uff01\u201d\u8eab\u5f62\u4e00\u8d77\uff0c\u4e00\u62db\u201c\u94f6\u6cb3\u5012\u6302\u201d\uff0c\u5251\u5149\u7ed5\u5904\uff0c\u738b\u5ef7\u798f\u5934\u9885\u98de\u4e0a\u534a\u7a7a\u3002\u5510\u52aa \u89c1\u6b64\u5a01\u52bf\uff0c\u5413\u5f97\u5446\u4e86\u3002 \u3000\u3000\u4f60\u9053\u7389\u7f57\u5239\u4f55\u4ee5\u6765\u8fdf\u4e86\u539f\u6765\u5979\u5728\u5230\u91ce\u732a\u6797\u7684\u9014\u4e2d\uff0c\u4e5f\u53d1\u73b0\u4e86\u7fa4\u76d7\u7684\u8e2a\u8ff9\uff0c\u5176\u4e2d\u6709\u51e0\u4eba\u8fd8 \u662f\u4ece\u9655\u5357\u6765\u7684\u3002\u8981\u77e5\u7389\u7f57\u5239\u4e43\u662f\u5f3a\u76d7\u7684\u201c\u963f\u7238\u201d\uff0c\u4ee5\u524d\u5979\u5750\u9547\u5b9a\u519b\u5c71\u4e4b\u65f6\uff0c\u5357\u7fa4\u76d7\u62a2\u6765\u7684\u8d22 \u7269\uff0c\u90fd\u8981\u732e\u5979\u4e00\u4efd\u3002\u5979\u89c1\u6709\u81ea\u5df2\u8f96\u4e0b\u7684\u532a\u9996\u5230\u6b64\uff0c\u4e0d\u89c9\u6545\u6001\u590d\u840c\uff0c\u6697\u7f00\u4ed6\u4eec\uff0c\u5bdf\u770b\u4ed6\u4eec\u662f\u5404 \u81ea\u4e3a\u653f\uff0c\u72ec\u884c\u62a2\u52ab\uff0c\u8fd8\u662f\u5df2\u88ab\u5317\u7684\u5927\u5be8\u9996\u9886\u6536\u7f16\u4e86\u3002 \u3000\u3000\u5bb9\u4e00\u4e1c\u89c1\u7389\u7f57\u5239\u7a81\u5982\u5176\u6765\uff0c\u5403\u4e86\u4e00\u60ca\u3002\u8fd9\u65f6\u5b98\u519b\u7684\u5305\u56f4\u5708\u6b63\u5728\u7d27\u7f29\u3002\u7389\u7f57\u5239\u4e00\u624b\u628a\u4e91\u71d5 \u5e73\u6293\u8d77\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u4eec\u8c01\u6562\u4e0a\u6765\uff0c\u6211\u5c31\u628a\u4f60\u4eec\u7684\u4e3b\u5c06\u65ab\u4e86\uff01\u201d\u90a3\u767e\u9980\u5065\u5352\u90fd\u662f\u4e91\u71d5\u5e73\u7684 \u5fc3\u8179\u4eb2\u5175\uff0c\u89c1\u4e3b\u5c06\u88ab\u64d2\uff0c\u679c\u7136\u4e0d\u6562\u52a8\u624b\u3002 \u3000\u3000\u5bb9\u4e00\u4e1c\u5c1a\u4e0d\u77e5\u6765\u7684\u4fbf\u662f\u5a01\u9707\u6c5f\u6e56\u7684\u201c\u5973\u9b54\u5934\u201d\u7389\u7f57\u5239\uff0c\u89c1\u64d2\u4e86\u4e91\u71d5\u5e73\u7684\u7adf\u662f\u4e2a\u5eff\u9980\u5c81\u7684 \u5c11\u5e74\uff0c\u867d\u611f\u60ca\u5947\uff0c\u5374\u8fd8\u4e0d\u614c\u3002\u626c\u58f0\u559d\u9053\uff1a\u201c\u63b3\u4eba\u8981\u631f\uff0c\u7b97\u662f\u90a3\u95e8\u5b50\u7684\u82f1\u96c4\uff1f\u201d\u7389\u7f57\u5239\u76c8\u76c8\u4e00 \u7b11\uff0c\u628a\u4e91\u71d5\u5e73\u631f\u5728\u80c1\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u6211\u5c31\u7528\u4e00\u53ea\u624b\u6765\u4f1a\u4f1a\u4f60\u8fd9\u4e2a\u5927\u82f1\u96c4\uff01\u4f60\u82e5\u5b34\u5f97\u4e86 \u6211\uff0c\u6211\u7acb\u523b\u628a\u4f60\u4eec\u7684\u4e3b\u5c06\u653e\u8fd8\uff01\u201d\u5bb9\u4e00\u4e1c\u542c\u8fd9\u5c11\u5e74\u58f0\u97f3\u5a07\u5a9a\uff0c\u9887\u89c9\u51fa\u5947\uff0c\u5f53\u4e0b\u8bf4\u9053\uff1a\u201c\u4f60\u62ff \u4fd8\u864f\u5f53\u4f5c\u5175\u5668\uff0c\u90a3\u5f53\u7136\u662f\u4f60\u5b34\u4e86\uff01\u201d\u7389\u7f57\u5239\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u8bef\u4f24\u4e86\u6211\u80c1\u4e0b\u7684\u4fd8\u864f\uff0c\u4e5f\u7b97\u4f60 \u5b34\u3002\u5982\u4f55\uff1f\u201d\u8fd9\u79cd\u6253\u6cd5\uff0c\u786e\u662f\u5f00\u6b66\u6797\u672a\u6709\u4e4b\u5947\u3002\u672c\u6765\u631f\u7740\u4fd8\u63b3\u5e94\u6218\uff0c\u4ee4\u5bf9\u65b9\u6295\u9f20\u5fcc\u5668\uff0c\u90a3\u786e \u662f\u5927\u5360\u4f18\u52bf\uff1a\u53ef\u662f\u5982\u4eca\u7389\u7f57\u5239\u53cd\u5176\u9053\u800c\u884c\u4e4b\uff0c\u975e\u4f46\u4e0d\u7528\u4fd8\u864f\u4f5c\u76fe\u724c\uff0c\u800c\u4e14\u53ea\u8981\u4fd8\u864f\u53d7\u4e86\u8bef \u4f24\uff0c\u5c31\u5f97\u7b97\u8f93\uff0c\u90a3\u5c31\u7b49\u65bc\u88ab\u7f1a\u4e86\u4e00\u53ea\u624b\u4e4b\u5916\uff0c\u8fd8\u5f97\u5c0f\u5fc3\u9632\u62a4\u4fd8\u864f\u53d7\u4f24\uff0c\u672c\u662f\u5927\u4f18\u52bf\u7684\u4e5f\u8981\u53d8 \u6210\u5927\u52a3\u52bf\u4e86\u3002 \u3000\u3000\u5bb9\u4e00\u4e1c\u542c\u5f97\u7389\u7f57\u5239\u63d0\u51fa\u8fd9\u6837\u6253\u6cd5\uff0c\u53c8\u6c14\u53c8\u60ca\uff0c\u4ed6\u51fa\u9053\u4ee5\u6765\u51e0\u66fe\u53d7\u8fc7\u8fd9\u6837\u7684\u8511\u89c6\uff1f \u3000\u3000\u7389\u7f57\u5239\u53c8\u7b11\u9053\uff1a\u201c\u5982\u4f55\uff1f\u201d\u5bb9\u4e00\u4e1c\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u54b1\u4eec\u5c31\u4e00\u8a00\u4e3a\u5b9a\u3002\u4f60\u5b34 \u4e86\u6211\uff0c\u73cd\u73e0\u91cd\u5b9d\u90fd\u662f\u4f60\u7684\u3002\u6211\u5b34\u4e86\u4f60\u5462\uff1f\u201d\u7389\u7f57\u5239\u542c\u4ed6\u7684\u8bdd\uff0c\u624d\u77e5\u4ed6\u770b\u91cd\u7684\u7adf\u662f\u90a3\u6279\u73cd\u5b9d\u800c \u975e\u53cb\u4eba\u3002\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u7eff\u6797\u4e2d\u4ee5\u4e49\u6c14\u4e3a\u5148\uff0c\u6bd4\u4ed6\u4eec\u8fd9\u4e9b\u72d7\u5b98\u5f3a\u5f97\u591a\u4e86\u3002\u201d\u5bb9\u4e00\u4e1c\u9053\uff1a\u201c\u5982 \u4f55\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u82e5\u5b34\u5f97\u4e86\u6211\uff0c\u9664\u5c06\u4f60\u4eec\u7684\u4e3b\u5c06\u653e\u8fd8\u4e4b\u5916\uff0c\u73cd\u73e0\u91cd\u5b9d\u4e5f\u5168\u5f52\u4f60\u6240\u6709\u3002\u201d \u5bb9\u4e00\u4e1c\u53cc\u76ee\u987e\u76fc\uff0c\u73af\u626b\u573a\u4e2d\u7fa4\u76d7\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u505a\u5f97\u4e86\u4e3b\u5417\uff1f\u201d \u3000\u3000\u7389\u7f57\u5239\u54c8\u54c8\u5927\u7b11\uff0c\u628a\u5916\u8863\u4e00\u6495\uff0c\u9732\u51fa\u91cc\u9762\u7684\u7ee3\u82b1\u5973\u670d\uff0c\u53c8\u628a\u5934\u4e0a\u9752\u5dfe\u9664\u4e0b\uff0c\u9732\u51fa\u675f\u53d1\u91d1 \u73af\u3002\u5357\u7fa4\u76d7\u5df2\u6599\u77e5\u5979\u5c31\u662f\u7389\u7f57\u5239\uff0c\u89c1\u5979\u9732\u51fa\u5973\u513f\u672c\u76f8\uff0c\u9f50\u58f0\u6b22\u547c\u3002\u9655\u5317\u7fa4\u76d7\u4e5f\u6df1\u77e5\u7389\u7f57\u5239\u5389 \u5bb3\uff0c\u867d\u7136\u4e0d\u662f\u5f52\u5979\u6240\u7ba1\uff0c\u4f46\u542c\u5f97\u5979\u613f\u4e0e\u654c\u4eba\u8d4c\u8d5b\uff0c\u90a3\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u5f53\u4e0b\u4e00\u9f50\u8bf4\u9053\uff1a\u201c\u4f60\u82e5\u5b34 \u5f97\u4e86\u7ec3\u5973\u4fa0\uff0c\u4e0d\u7ba1\u4ec0\u9ebd\u91d1\u94f6\u73e0\u5b9d\uff0c\u6211\u4eec\u51b3\u4e0d\u67d3\u6307\uff01\u201d\u8fd9\u65f6\u7fa4\u76d7\u73af\u7ed5\u573a\u8fb9\uff0c\u5b98\u519b\u5305\u56f4\u5728\u5916\u3002\u7389 \u7f57\u5239\u631f\u7740\u4e91\u71d5\u5e73\u548c\u5bb9\u4e00\u4e1c\u5bf9\u7acb\u573a\u5fc3\u3002\u5510\u52aa\u4e0e\u94c1\u73ca\u745a\u5750\u5728\u8def\u65c1\u3002\u5510\u52aa\u8fd8\u4e0d\u77e5\u7389\u7f57\u5239\u4e5f\u662f\u5b58\u5fc3\u52ab \u4ed6\u73e0\u5b9d\u7684\u4eba\uff0c\u5bf9\u5979\u5341\u5206\u611f\u6fc0\uff0c\u5fc3\u4e2d\u4f46\u613f\u7389\u7f57\u5239\u6253\u5b34\uff0c\u5bf9\u94c1\u73ca\u745a\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u6599\u4e0d\u5230\u4f60\u8eab\u6000\u7edd \u6280\uff0c\u66f4\u6599\u4e0d\u5230\u4f60\u8fd9\u4f4d\u670b\u53cb\u5c31\u5982\u4ed9\u5973\u4e00\u822c\uff0c\u53c8\u7f8e\u4e3d\u53c8\u795e\u901a\uff0c\u6211\u4eca\u65e5\u5f97\u4f60\u4eec\u6551\u547d\uff0c\u6ca1\u9f7f\u4e0d\u5fd8\u3002\u201d \u94c1\u73ca\u745a\u672c\u6765\u4e5f\u60f3\u52ab\u4ed6\u7684\u73e0\u5b9d\uff0c\u4f46\u4e00\u8def\u540c\u884c\uff0c\u77e5\u4ed6\u5fc3\u80a0\u6781\u597d\uff0c\u800c\u4e14\u8c41\u8fbe\u5927\u5ea6\uff0c\u7b97\u5f97\u662f\u4e2a\u585e\u5916\u82f1 \u96c4\uff0c\u8fd9\u65f6\u5df2\u628a\u52ab\u4ed6\u73e0\u5b9d\u4e4b\u610f\u6253\u6d88\uff0c\u542c\u4ed6\u5982\u6b64\u8bf4\u6cd5\uff0c\u5fc3\u4e2d\u6697\u53eb\u201c\u60ed\u6127\u201d\uff0c\u6df1\u6015\u7389\u7f57\u5239\u6218\u80dc\u4e4b \u5f8c\uff0c\u7acb\u65f6\u7ffb\u8138\uff0c\u90a3\u5c31\u66f4\u96be\u4e3a\u60c5\u3002 \u3000\u3000\u5bb9\u4e00\u4e1c\u542c\u5f97\u7fa4\u76d7\u6b22\u547c\u4e4b\u5f8c\uff0c\u624d\u77e5\u9762\u524d\u8fd9\u4e2a\u5c11\u5973\uff0c\u5c31\u662f\u6c5f\u6e56\u4e0a\u95fb\u540d\u80c6\u843d\u7684\u5973\u9b54\u5934\u4e0a\u60f3\u8d77\u5f53 \u5e74\u5e94\u4fee\u9633\u5728\u534e\u5c71\u6446\u9635\uff0c\u7389\u9762\u5996\u72d0\u51cc\u9704\u4e0e\u91d1\u521a\u624b\u8303\u90fd\u4e27\u5728\u7389\u7f57\u5239\u624b\u4e0b\uff0c\u601d\u4e4b\u4e0d\u7981\u80c6\u5bd2\u3002\u81ea\u5df1\u5f53 \u5e74\u56e0\u4e8b\u9003\u8fc7\uff0c\u60f3\u4e0d\u5230\u4eca\u65e5\u4ecd\u4e0e\u5979\u964c\u8def\u76f8\u9022\u3002\u5bb9\u4e00\u4e1c\u8fd9\u65f6\u9a84\u6c14\u5168\u6d88\uff0c\u5fc3\u4e2d\u53ea\u662f\u76d8\u7b97\uff0c\u600e\u6837\u624d\u80fd \u5728\u7389\u7f57\u5239\u5251\u5e95\u9003\u751f\u3002 \u3000\u3000\u7389\u7f57\u5239\u626c\u5251\u4f5c\u52bf\uff0c\u7b11\u9053\uff1a\u201c\u6765\u5440\uff0c\u6765\u5440\uff01\u201d\u5bb9\u4e00\u4e1c\u94c1\u83f8\u4e00\u7ffb\uff0c\u4e00\u62db\u201c\u674e\u5e7f\u5c04\u77f3\u201d\uff0c\u9aa4\u4ee5 \u5927\u67aa\u62db\u6570\u5411\u7389\u7f57\u5239\u5e73\u80de\u75be\u523a\uff0c\u7389\u7f57\u5239\u54c8\u54c8\u4e00\u7b11\uff0c\u6a2a\u5251\u4e00\u5c01\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u94c1\u83f8\u7ed9\u9707\u5f97\u6b6a\u8fc7 \u4e00\u8fb9\uff0c\u706b\u82b1\u98de\u6e85\uff01\u7389\u7f57\u5239\u5251\u62db\u5feb\u6377\u5f02\u5e38\uff0c\u8eab\u5f62\u4e00\u4fa7\uff0c\u5b9d\u5251\u76f4\u523a\u54bd\u5589\u3002\u5bb9\u4e00\u4e1c\u94c1\u83f8\u888b\u4e00\u78d5\uff0c\u4e0d \u5f85\u7389\u7f57\u5239\u7684\u5251\u660e\u662f\u523a\u5589\uff0c\u5251\u5230\u4e2d\u9014\uff0c\u624b\u8155\u4e00\u6c89\uff0c\u4f4e\u4e86\u53c1\u5bf8\uff0c\u5251\u5c16\u6307\u7684\u7adf\u662f\u5589\u4e0b\u201c\u7487\u673a\u201d\u8981 \u7a74\u3002\u5bb9\u4e00\u4e1c\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u6ed1\u6b65\u95ea\u8eab\uff0c\u9976\u4ed6\u8eb2\u95ea\u5f97\u5feb\uff0c\u80a9\u5934\u8fd8\u662f\u7ed9\u5251\u5c16\u5212\u8fc7\uff0c\u201c\u55e4\u201d\u7684\u4e00 \u58f0\uff0c\u8863\u88f3\u7834\u88c2\uff0c\u9c9c\u8840\u6c81\u51fa\u3002\u8fd9\u8fd8\u5e78\u662f\u7389\u7f57\u5239\u80c1\u4e0b\u631f\u4eba\uff0c\u8eab\u6cd5\u4e0d\u82e5\u5e73\u65f6\u8f7b\u7075\uff0c\u8981\u4e0d\u7136\u8fd9\u4e00\u5251\u5bb9 \u4e00\u4e1c\u51b3\u9003\u4e0d\u4e86\uff01 \u3000\u3000\u7389\u7f57\u5239\u4e00\u62db\u5f97\u624b\uff0c\u5251\u52bf\u672a\u6536\uff0c\u5251\u62db\u53c8\u51fa\u3002\u5bb9\u4e00\u4e1c\u594b\u529b\u62c6\u4e86\u4e24\u62db\uff0c\u83f8\u4e00\u659c\uff0c\u7a81\u7136\u7167\u88ab\u7389\u7f57 \u5239\u631f\u7740\u7684\u4e91\u71d5\u5e73\u6253\u6765\uff01 \u3000\u3000\u7389\u7f57\u5239\u53eb\u9053\uff1a\u201c\u597d\u72e0\u7684\u72d7\u8d3c\uff01\u201d\u8eab\u8eaf\u4e00\u8f6c\uff0c\u4e00\u5251\u628a\u5bb9\u4e00\u4e1c\u83f8\u683c\u5f00\uff0c\u631f\u7740\u7684\u4e91\u71d5\u5e73\u51e0\u4e4e\u7ed9 \u4ed6\u6253\u4e2d\u3002\u5bb9\u4e00\u4e1c\u6218\u6cd5\u4e00\u53d8\uff0c\u4e0d\u67b6\u7389\u7f57\u5239\u5b9d\u5251\uff0c\u94c1\u83f8\u888b\u78d5\uff0e\u6253\uff0c\u5288\u3001\u538b\uff0c\u5168\u671d\u4e91\u71d5\u5e73\u6253\u6765\uff01\u7389 \u7f57\u5239\u6599\u4e0d\u5230\u5bb9\u4e00\u4e1c\u5fc3\u80a0\u5982\u6b64\u4e4b\u6bd2\uff0c\u7adf\u628a\u597d\u53cb\u5f53\u6210\u6d3b\u9776\uff01\u4f46\u4ee5\u81ea\u5df1\u6709\u8a00\u5728\u5148\uff0c\u4fd8\u864f\u82e5\u7ed9\u4ed6\u4f24 \u4e86\uff0c\u7eb5\u7136\u4e0d\u662f\u201c\u8bef\u4f24\u201d\uff0c\u4e5f\u96be\u5206\u8fa8\u3002\u56e0\u6b64\u8feb\u5f97\u6539\u653b\u4e3a\u5b88\uff0c\u4e00\u67c4\u5251\u4f7f\u5f97\u98ce\u96e8\u4e0d\u900f\uff0c\u4fe8\u4f3c\u4e00\u5708\u94f6 \u8679\uff0c\u628a\u81ea\u5df1\u4e0e\u4e91\u71d5\u5e73\u5168\u8eab\u62a4\u4f4f\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u5f62\u52bf\u7a81\u53d8\uff0c\u5bb9\u4e00\u4e1c\u6b66\u529f\u4e0d\u5728\u5e94\u4fee\u9633\u4e4b\u4e0b\uff0c\u94c1\u83f8\u517c\u6709\u67aa\u68d2\u4e0e\u70b9\u7a74\u4e4b\u957f\uff0c\u5c45\u7136\u6562\u4ee5\u653b \u4e3a\u5b88\uff0c\u4e0e\u7389\u7f57\u5239\u82e6\u82e6\u7f20\u6597\u3002 \u3000\u3000\u94c1\u73ca\u745a\u53eb\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u8fd9\u72d7\u8d3c\u662f\u6709\u5fc3\u4f24\u4ed6\u670b\u53cb\uff0c\u4f55\u5fc5\u7406\u4ed6\uff1f\u201d\u5510\u52aa\u5374\u9053\uff1a\u201c\u8fd9\u4f4d\u5973\u82f1 \u96c4\u8bf4\u4e00\u4e0d\u4e8c\uff0c\u771f\u771f\u53ef\u656c\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u4e0e\u5bb9\u4e00\u4e1c\u6fc0\u6218\u4e2d\uff0c\u5b98\u519b\u6162\u6162\u79fb\u8fd1\u3002\u5ffd\u7136\u95f2\u53f7\u89d2\u957f\u9e23\uff0c\u6797\u95f4\u53c8\u6740\u51fa\u4e00\u5f6a\u5b98\u519b\uff01\u7389\u7f57 \u5239\u53eb\u9053\uff1a\u201c\u5b98\u519b\u542c\u7740\uff0c\u6211\u4eec\u5728\u6b64\u5355\u6253\u72ec\u6597\uff0c\u4f60\u4eec\u82e5\u6562\u52a8\u5200\u52a8\u67aa\uff0c\u5c31\u4f11\u602a\u6211\u4e0d\u5b88\u8bfa\u8a00\uff01\u201d\u4e91\u71d5 \u5e73\u7684\u4eb2\u5175\u679c\u7136\u90fd\u8f6c\u8fc7\u4e86\u8eab\uff0c\u60f3\u529d\u6b62\u90a3\u5f6a\u4eba\u9a6c\uff0c\u4f11\u8981\u518d\u8fdb\u3002 \u3000\u3000\u5bb9\u4e00\u4e1c\u5374\u6697\u6697\u7eb3\u7f55\uff0c\u4ed6\u77e5\u9053\u4e91\u71d5\u5e73\u4e0d\u60f3\u8ba9\u5168\u519b\u77e5\u9053\uff0c\u53ea\u662f\u6311\u9009\u4e86\u6700\u53ef\u9760\u7684\u767e\u9980\u4eb2\u5175\uff0c\u5230 \u68ee\u6797\u57cb\u4f0f\u3002\u8fd9\u5f6a\u519b\u9a6c\u600e\u7684\u5374\u4f1a\u6740\u6765\uff1f \u3000\u3000\u5b98\u519b\u4e2d\u4e00\u4e2a\u5c11\u5e74\u5c06\u519b\u9a91\u7740\u9ad8\u5934\u5927\u9a6c\uff0c\u795e\u5a01\u51db\u51db\uff0c\u4e91\u71d5\u5e73\u7684\u62a4\u5175\u526f\u5c06\u89c1\u8fd9\u5c11\u5e74\u5c06\u519b\u6781\u4e3a\u964c \u751f\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u6765\u5c06\u4f4f\u9a6c\uff0c\u4f60\u4eec\u662f\u90a3\u4e00\u8425\u7684\uff1f\u201d\u90a3\u5c11\u5e74\u5c06\u519b\u5927\u559d\u4e00\u58f0\uff1a\u201c\u53db\u5175\u5728\u6b64\u4f55\u4e3a\uff1f \u8d76\u5feb\u968f\u6211\u56de\u57ce\uff01\u201d\u5580\u7684\u4e00\u7bad\uff0c\u5c06\u4e91\u71d5\u5e73\u526f\u5c06\u5c04\u6bd9\uff01\u4e91\u71d5\u5e73\u7684\u4eb2\u5175\u5927\u4e71\uff0c\u7ed9\u5f8c\u6765\u8fd9\u80a1\u5b98\u519b\u5305\u56f4 \u8d77\u6765\uff0c\u5168\u9a71\u5165\u4e86\u68ee\u6797\u4e4b\u4e2d\uff01\u7389\u7f57\u5239\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\uff0c\u90a3\u5c11\u5e74\u5c06\u519b\u7684\u4e00\u4e3e\u4e00\u52a8\uff0c\u5168\u5728\u5979\u7684 \u773c\u4e2d\uff0c\u5fc3\u4e2d\u5947\u9053\uff1a\u201c\u600e\u9ebd\u5b98\u519b\u4e2d\u4e5f\u6709\u5982\u6b64\u82f1\u96c4\u4eba\u7269\uff01\u201d\u5bb9\u4e00\u4e1c\u89c1\u6b64\u60c5\u5f62\uff0c\u6599\u77e5\u6709\u53d8\uff0c\u5fc3\u4e2d\u4e00 \u614c\uff0c\u7389\u7f57\u5239\u5237\u5237\u4e24\u5251\uff0c\u9aa4\u628a\u94c1\u83f8\u6311\u5f00\uff0c\u5bb9\u4e00\u4e1c\u98de\u811a\u8e22\u4e91\u71d5\u5e73\u5012\u5782\u7684\u8111\u888b\uff0c\u7389\u7f57\u5239\u4e00\u58f0\u957f\u5578\uff0c \u8eab\u5f62\u9aa4\u7136\u98de\u8d77\uff0c\u5bb9\u4e00\u4e1c\u4e00\u811a\u8e22\u7a7a\uff0c\u6025\u5fd9\u64a4\u56de\u83f8\u62a4\u5934\uff0c\u90a3\u62a4\u5f97\u4f4f\uff1f\u7389\u7f57\u5239\u5de6\u624b\u628a\u4e91\u71d5\u5e73\u9ad8\u4e3e\u8fc7 \u5934\uff0c\u5728\u534a\u7a7a\u4e00\u5251\u5288\u4e0b\uff0c\u94c1\u83f8\u7acb\u88ab\u9707\u5f00\uff0c\u5bb9\u4e00\u4e1c\u8111\u888b\u4e5f\u7ed9\u5b9d\u5251\u5288\u6210\u4e24\u534a\uff01 \u3000\u3000\u7389\u7f57\u5239\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u6765\u770b\uff0c\u4ed6\u8eab\u4e0a\u53ef\u6709\u4f24\u75d5\uff1f\u201d\u7fa4\u76d7\u65e0\u4e0d\u51db\u7136\u3002\u7389\u7f57\u5239\u6b63\u60f3\u56de \u8eab\u6d17\u52ab\u73e0\u5b9d\uff0c\u5ffd\u89c1\u90a3\u5c11\u5e74\u5c06\u519b\uff0c\u7b56\u9a6c\u9a70\u56de\uff0c\u5317\u7fa4\u76d7\uff0c\u5782\u624b\u8083\u7acb\u4e24\u65c1\u3002\u90a3\u5c11\u5e74\u5c06\u519b\u5927\u53eb\u9053\uff1a \u201c\u8c01\u90fd\u4e0d\u8bb8\u4e71\u52a8\uff1f\u201d\u7389\u7f57\u5239\u751a\u4e3a\u5947\u602a\uff0c\u4e0d\u77e5\u5317\u90a3\u4e9b\u5f3a\u76d7\u5934\u5b50\uff0c\u4f55\u4ee5\u4f1a\u542c\u5b98\u5175\u7684\u8bdd\uff0c\u5fc3\u4e2d\u6709 \u6c14\uff0c\u62d4\u5251\u8fce\u524d\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u9661\u89c1\u90a3\u5c11\u5e74\u5c06\u519b\u53cc\u7738\u803f\u803f\uff0c\u76ee\u95ea\u7cbe\u5149\uff0c\u8fde\u7389\u7f57\u5239\u8fd9\u6837\u6740 \u4eba\u4e0d\u7728\u773c\u7684\u4eba\uff0c\u4e5f\u89c9\u5f97\u4ed6\u522b\u5177\u4e00\u79cd\u5a01\u4e25\uff0c\u4ee4\u4eba\u9707\u6151\u3002\u7389\u7f57\u5239\u5fc3\u9053\uff1a\u201c\u597d\u54c7\uff0c\u8fd9\u56de\u6211\u9047\u5230\u5bf9\u624b \u4e86\u3002\u201d\u8feb\u8fd1\u4e00\u6b65\uff0c\u90a3\u5c11\u5e74\u5c06\u519b\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u662f\u7389\u7f57\u5239\u4e86\uff1f\u5e78\u4f1a\uff0c\u5e78\u4f1a\uff01\u201d\u6b63\u662f\uff1a\u7edd\u4e16\u82f1\u8c6a \u51fa\uff0c\u5929\u4e0b\u5171\u503e\u5fc3\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377\u2014\u2014\u7b2c\u5341\u516b\u56de\u3000\u51a4\u72f1\u6bc1\u957f\u57ce\u3000\u5c06\u661f\u6447\u843d\u3000\u82e6\u5fc3\u62a4\u826f\u53cb\u3000\u5251\u6c14\u817e\u7a7a \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377 \u7b2c\u5341\u516b\u56de \u51a4\u72f1\u6bc1\u957f\u57ce\u3000\u5c06\u661f\u6447\u843d \u82e6\u5fc3\u62a4\u826f\u53cb\u3000\u5251\u6c14\u817e\u7a7a \u3000\u3000\u7389\u7f57\u5239\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u9053\u6211\u5417\uff1f\u5b98\u5175\u78b0\u5230\u4e86\u6211\uff0c\u90a3\u5c31\u662f\u5c0f\u9b3c\u78b0\u7740\u960e\u738b\u4e86\uff01\u201d\u5251\u5c16\u4e00 \u6307\uff0c\u5c11\u5e74\u5c06\u519b\u5fae\u5fae\u53d1\u7b11\uff0c\u5317\u7fa4\u76d7\u4ff1\u90fd\u53d8\u8272\uff0c\u62d4\u51fa\u5175\u5203\uff0c\u634d\u536b\u5c11\u5e74\u5c06\u519b\uff0c\u5357\u7fa4\u76d7\u53eb\u9053\uff1a\u201c\u7ec3\u5973 \u4fa0\uff0c\u8fd9\u4f4d\u662f\u5c0f\u3001\u5c0f\u2026\u2026\u201d\u5c11\u5e74\u5c06\u519b\u8fde\u8fde\u6447\u624b\uff0c\u9053\uff1a\u201c\u90fd\u662f\u81ea\u5df1\u4eba\uff0c\u5927\u5bb6\u6563\u5f00\u3002\u201d\u5c0f\u58f0\u5bf9\u7389\u7f57 \u5239\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\uff0c\u6211\u662f\u5c0f\u95ef\u738b\u674e\u81ea\u6210\u3002\u9ad8\u8fce\u7965\u662f\u4ffa\u8205\u8205\u3002\u8bf7\u5230\u90a3\u8fb9\u6811\u4e0b\u8bf4\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u6014\u4e86\u4e00\u6014\uff0c\u5e76\u4e0d\u662f\u201c\u674e\u81ea\u6210\u201d\u53c1\u5b57\u4f7f\u5979\u5403\u60ca\uff0c\u90a3\u65f6\u674e\u81ea\u6210\u8fd8\u6ca1\u6709\u4ec0\u9ebd\u540d\u5934\uff0c\u5728\u897f \u53c1\u5341\u516d\u8def\u5927\u76d7\u4e4b\u4e2d\uff0c\u738b\u5609\u80e4\u662f\u5176\u4e2d\u4e00\u8def\uff0c\u9ad8\u8fce\u7965\u662f\u738b\u5609\u80e4\u526f\u624b\uff0c\u674e\u81ea\u6210\u4e0d\u8fc7\u662f\u738b\u5609\u80e4\u8fd9\u4e00\u8def \u7684\u4e00\u4e2a\u5934\u76ee\u800c\u5df2\uff1b\u4f46\u552f\u5176\u5982\u6b64\uff0c\u6240\u4ee5\u4ee5\u674e\u81ea\u6210\u5f53\u65f6\u7684\u201c\u8eab\u4efd\u201d\u800c\u80fd\u4ee4\u7fa4\u76d7\u6151\u670d\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u7684 \u672c\u8eab\u624d\u4ee4\u7389\u7f57\u5239\u5403\u60ca\u3002 \u3000\u3000\u7389\u7f57\u5239\u8981\u4e86\u4e00\u56db\u9a6c\uff0c\u548c\u674e\u81ea\u6210\u7b56\u9a6c\u5165\u6797\u3002\u7389\u7f57\u5239\u95ee\u9053\uff1a\u201c\u738b\u5609\u80e4\u7236\u5b50\u597d\u5417\uff1f\u201d\u674e\u81ea\u6210 \u9053\uff1a\u201c\u738b\u8001\u603b\u5df2\u6218\u6b7b\u4e86\uff0c\u73b0\u5728\u662f\u4ffa\u7684\u8205\u8205\u9ad8\u8fce\u7965\u9886\u5934\uff0c\u738b\u7167\u5e0c\u592b\u5987\u548c\u767d\u654f\u90fd\u5728\u519b\u4e2d\u3002\u201d\u7389\u7f57 \u5239\u4e00\u9635\u5fc3\u4f24\uff0c\u60f3\u4e0d\u5230\u79bb\u5f00\u897f\u4e0d\u5230\u4e00\u5e74\uff0c\u53d8\u5316\u5982\u6b64\u4e4b\u5927\u3002\u95ee\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u77e5\u9053\u6211\u90e8\u4e0b\u7684\u4e0b\u843d \u5417\uff1f\u5979\u4eec\u662f\u4e0d\u662f\u5168\u7ed9\u5b98\u5175\u6740\u4e86\uff1f\u8fd8\u6709\u4f60\u4eec\u4e3a\u4ec0\u9ebd\u5047\u626e\u519b\u5b98\uff1f\u201d \u3000\u3000\u674e\u81ea\u6210\u9053\uff1a\u201c\u5218\u5ef7\u5143\u8c03\u4e86\u5ddd\u7518\u664b\u56db\u7701\u7684\u5175\u529b\u4e8c\u5341\u4e07\u4eba\u56f4\u653b\u6211\u4eec\uff0c\u5404\u5bb6\u5144\u5f1f\uff0c\u90fd\u5728\u5b98\u519b\u538b \u529b\u4e4b\u4e0b\u5316\u6574\u4e3a\u96f6\uff0c\u6d41\u6563\u56db\u65b9\u4e86\u3002\u4e0a\u6708\u6211\u4eec\u5192\u4e86\u7edd\u5927\u5371\u9669\uff0c\u5728\u7c73\u8102\u5927\u4f1a\uff0c\u53c1\u5341\u516d\u8def\u7684\u9996\u9886\u6765\u4e86 \u53c1\u5341\u53c1\u4eba\uff0c\u5c31\u4f60\u4eec\u90a3\u8def\u4e0e\u795e\u4e00\u5143\u5144\u5f1f\u6ca1\u6709\u6d3e\u4eba\u6765\u3002\u542c\u8bf4\u4f60\u4eec\u90a3\u8def\u5df2\u7a81\u56f4\u5165\u5ddd\uff0c\u548c\u5176\u4ed6\u5404\u8def\u6bd4 \u8f83\u8d77\u6765\uff0c\u635f\u5931\u8fd8\u4e0d\u7b97\u6700\u4e25\u91cd\u7684\u3002\u5f20\u732e\u5fe0\u4e0a\u6708\u4e5f\u4ece\u56db\u5ddd\u6765\u5230\u7c73\u8102\uff0c\u636e\u4ed6\u8bf4\u5728\u5e7f\u5143\u662d\u5316\u4e4b\u95f4\u66fe\u53d1 \u73b0\u6709\u4e00\u652f\u5a18\u5b50\u519b\uff0c\u4ed6\u60f3\u6d3e\u4eba\u8054\u7edc\uff0c\u5374\u7ed9\u5b98\u519b\u9694\u65ad\u4e86\u3002\u4f60\u53ef\u4ee5\u5230\u90a3\u91cc\u627e\u5979\u4eec\u3002\u201d \u3000\u3000\u7c73\u8102\u53c1\u5341\u516d\u8def\u4e49\u519b\u4e4b\u4f1a\uff0c\u662f\u4e00\u4ef6\u5927\u4e8b\uff0c\u674e\u81ea\u6210\u7684\u201c\u5c0f\u95ef\u738b\u201d\u4e4b\u540d\uff0c\u5c31\u662f\u90a3\u65f6\u5f97\u7684\u3002\u539f\u6765 \u5728\u738b\u5609\u80e4\u6b7b\u5f8c\uff0c\u7eff\u6797\u7fa4\u96c4\u63a8\u9ad8\u8fce\u7965\u4e3a\u9996\uff0c\u9ad8\u8fce\u7965\u624d\u8bc6\u5e73\u5e73\uff0c\u5168\u9760\u674e\u81ea\u6210\u4e4b\u529b\uff0c\u6253\u4e86\u4e24\u6b21\u80dc \u6756\uff0c\u5c40\u9762\u624d\u5f97\u5c0f\u5b89\u3002\u7c73\u8102\u5927\u4f1a\u65f6\uff0c\u56e0\u4e3a\u5404\u8def\u9996\u9886\uff0c\u90fd\u6709\u4e00\u4e2a\u738b\u53f7\uff0c\u4f8b\u5982\u4ec0\u9ebd\u201c\u6a2a\u5929\u738b\u201d\u201c\u6df7 \u4e16\u738b\u201d\u201c\u626b\u5730\u738b\u201d\u7b49\u7b49\uff0c\u65e0\u5947\u4e0d\u6709\u3002\u9ad8\u8fce\u7965\u65b0\u4e3a\u9996\u9886\uff0c\u672a\u6709\u738b\u53f7\u3002\u4ed6\u90e8\u4e0b\u7ed9\u4ed6\u62df\u53f7\uff0c\u4e71\u54c4\u54c4 \u7684\u62df\u4e86\u534a\u5929\uff0c\u8fd8\u62df\u4e0d\u51fa\u4e00\u4e2a\u9002\u5f53\u7684\u6765\u3002\u5f53\u65f6\u674e\u81ea\u6210\u7b11\u9053\uff1a\u201c\u6211\u4eec\u73b0\u5728\u95ef\u4e00\u6b65\u662f\u4e00\u6b65\uff0c\u95ef\u5230\u4ec0 \u9ebd\u5730\u6b65\uff0c\u8c01\u90fd\u4e0d\u77e5\u9053\u3002\u5982\u679c\u5927\u5bb6\u4e0d\u632f\u4f5c\u7684\u8bdd\uff0c\u4e5f\u8bb8\u5c31\u95ef\u4e0d\u51fa\u897f\uff1a\u5982\u679c\u5927\u5bb6\u628a\u751f\u6b7b\u7978\u798f\u7f6e\u4e4b\u5ea6 \u5916\uff0c\u540c\u5fc3\u5408\u529b\u7684\u5f80\u524d\u95ef\u53bb\uff0c\u95ef\u5230\u5317\u4eac\u4e5f\u4e0d\u96be\u3002\u54b1\u4eec\u73b0\u5728\u9996\u8981\u4e4b\u4e8b\u4e43\u662f\u95ef\u3001\u95ef\u3001\u95ef\uff01\u79f0\u4e0d\u79f0 \u738b\uff0c\u79f0\u4ec0\u9ebd\u738b\uff0c\u6211\u89c9\u5f97\u90fd\u65e0\u6240\u8c13\u3002\u6b8a\u4e0d\u5fc5\u4e3a\u8fd9\u4e9b\u865a\u6587\u5c0a\u53f7\uff0c\u6d6a\u8d39\u7cbe\u795e\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u7fa4\u96c4\u7eb7 \u7eb7\u62cd\u624b\uff0c\u8f70\u7136\u53eb\u597d\uff01\u4e0d\u7ea6\u800c\u540c\u7684\u5927\u58f0\u558a\u9053\uff1a\u201c\u95ef\u738b\uff0c\u95ef\u738b\uff01\u8fd9\u4e2a\u738b\u53f7\u597d\u6781\u4e86\uff01\u201d\u81ea\u6b64\u4fbf\u628a\u9ad8 \u8fce\u7965\u79f0\u4e3a\u201c\u95ef\u738b\u201d\uff0c\u628a\u674e\u81ea\u6210\u79f0\u4e3a\u201c\u5c0f\u95ef\u738b\u201d\uff0c\u76f4\u5230\u5f8c\u6765\u9ad8\u8fce\u5f89\u5728\u6f7c\u5173\u6218\u6b7b\uff0c\u674e\u81ea\u6210\u6b63\u5f0f\u88ad \u7528\u201c\u95ef\u738b\u201d\u7684\u5c0a\u53f7\u3002 \u3000\u3000\u518d\u8bf4\u7389\u7f57\u5239\u542c\u5f97\u674e\u81ea\u6210\u8bf4\u51fa\u81ea\u5df1\u90e8\u4f17\u7684\u4e0b\u843d\uff0c\u6068\u4e0d\u5f97\u63d2\u7ffc\u98de\u5230\u5ddd\u897f\u3002\u5f53\u4e0b\u60f3\u9053\uff1a\u201c\u8fd9\u5c0f \u95ef\u738b\u4e5f\u662f\u4e00\u4e2a\u4eba\u7269\uff0c\u8fd9\u6279\u73cd\u5b9d\u5f85\u6211\u4e0e\u4ed6\u5e73\u5206\u4e86\u5427\u3002\u201d\u6b63\u60f3\u5f00\u8a00\uff0c\u674e\u81ea\u6210\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\uff0c\u6211\u6c42 \u4f60\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4ec0\u9ebd\u4e8b\u60c5\uff1f\u201d\u674e\u81ea\u6210\u9053\uff1a\u201c\u8fd9\u6279\u73e0\u5b9d\uff0c\u54b1\u4eec\u5206\u6beb\u90fd\u4e0d\u8981\u52a8 \u5b83\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4ec0\u9ebd\uff1f\u4f60\u4eec\u4e0d\u4e5f\u662f\u6765\u52ab\u73e0\u5b9d\u7684\u5417\uff1f\u201d\u674e\u81ea\u6210\u7b11\u9053\uff1a\u201c\u8d77\u5148\u662f\u60f3\u52ab\u5b83\uff0c\u73b0 \u5728\u6211\u5df2\u67e5\u5f97\u6e05\u695a\uff0c\u8fd9\u6279\u73e0\u5b9d\u53ef\u52a8\u4e0d\u5f97\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u4eec\u5929\u4e0d\u6015\u5730\u4e0d\u6015\uff0c\u7687\u5e1d\u5c0f\u5b50\u7684\u6211\u4eec \u4e5f\u52ab\uff0c\u4e3a\u4f55\u8fd9\u4eba\u7684\u5374\u52ab\u4e0d\u5f97\uff01\u201d\u674e\u81ea\u6210\u53c8\u7b11\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\uff0c\u7687\u5e1d\u7684\u597d\u52ab\uff0c\u5230\u4e86\u8fd9\u4eba\u624b\u4e0a\uff0c\u53ef \u5c31\u4e0d\u597d\u52ab\u4e86\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8fd9\u662f\u4e3a\u4f55\uff1f\u6211\u5012\u8981\u8bf7\u6559\u8bf7\u6559\uff1f\u201d \u3000\u3000\u674e\u81ea\u6210\u7ffb\u8eab\u4e0b\u9a6c\uff0c\u62db\u624b\u8bf7\u7389\u7f57\u5239\u4e0b\u6765\uff0c\u4e00\u540c\u5750\u5728\u5730\u4e0a\uff0c\u6b63\u8272\u8bf4\u9053\uff1a\u201c\u6ee1\u5dde\u56fe\u8c0b\u6211\u4eec\u4e2d\u56fd \u751a\u6025\uff0c\u8fb9\u5173\u5f62\u52bf\u6781\u7d27\uff0c\u8fd9\u4f60\u662f\u77e5\u9053\u7684\u4e86\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8fb9\u9632\u4e4b\u4e8b\u4e0e\u8fd9\u6279\u73e0\u5b9d\u6709\u4f55\u5173\u7cfb\uff1f\u201d \u674e\u81ea\u6210\u9053\uff1a\u201c\u4f60\u542c\u6211\u8bf4\u3002\u5148\u524d\u6211\u8fd8\u4e0d\u77e5\u9053\u8fd9\u756a\u4eba\u8eab\u4efd\uff0c\u6240\u4ee5\u4e5f\u60f3\u52ab\u4ed6\u7684\u73e0\u5b9d\u5145\u5f53\u519b\u9977\u3002\u73b0\u5728 \u67e5\u5f97\u4ed6\u662f\u5357\u7586\u7f57\u5e03\u65cf\u5927\u914b\u957f\u5510\u739b\u7684\u513f\u5b50\uff0c\u5510\u739b\u662f\u5357\u7586\u5404\u65cf\u76df\u4e3b\uff0c\u82e5\u7136\u4ed6\u7684\u513f\u5b50\u88ab\u6740\uff0c\u73e0\u5b9d\u88ab \u593a\uff0c\u4ed6\u4e00\u5b9a\u628a\u8fd9\u7b14\u8d26\u7b97\u5728\u660e\u671d\u7687\u5e1d\u5934\u4e0a\u3002\u8bf4\u4e0d\u5b9a\u5c31\u8981\u8d77\u5175\u62a5\u4ec7\uff0c\u90a3\u65f6\u4e1c\u5317\u897f\u5317\u90fd\u6709\u8fb9\u60a3\uff0c\u7531 \u6821\u8fd9\u5c0f\u5b50\uff0c\u53ef\u6321\u4e0d\u4f4f\uff01\u201d\u7389\u7f57\u5239\u9ed8\u7136\u4e0d\u8bed\uff0c\u4e00\u65f6\u8fd8\u60f3\u4e0d\u8fc7\u6765\u3002\u674e\u81ea\u6210\u53c8\u9053\uff1a\u201c\u6211\u4eec\u867d\u7136\u4e5f\u5174 \u660e\u671d\u7687\u5e1d\u4f5c\u5bf9\uff0c\u53ef\u662f\u82e5\u7136\u5f02\u65cf\u5165\u4fb5\uff0c\u90a3\u9ebd\u6211\u4eec\u5c31\u5b81\u613f\u4e0e\u5b98\u519b\u8054\u5408\uff0c\u5171\u6297\u5f02\u65cf\u7684\u3002\u4f60\u8bf4\u5bf9 \u9ebd\uff1f\u201d\u7389\u7f57\u5239\u70b9\u4e86\u70b9\u5934\u3002\u674e\u81ea\u6210\u9053\uff1a\u201c\u6240\u4ee5\u4e0d\u80fd\u66ff\u660e\u671d\u7687\u5e1d\u518d\u5f00\u8fb9\u3002\u53ef\u60dc\u7684\u662f\u7531\u6821\u8fd9\u5c0f\u5b50\u68da \u6d82\u900f\u9876\uff0c\u52c7\u65bc\u5bf9\u5185\uff0c\u602f\u65bc\u5bf9\u5916\u3002\u62bd\u8c03\u5927\u519b\u6765\u6253\u6211\u4eec\uff0c\u5374\u4e0d\u6574\u987f\u8fb9\u5173\uff0c\u8fde\u718a\u5ef7\u5f3c\u90a3\u6837\u5f97\u529b\u5927\u5c06 \u90fd\u7f62\u514d\u4e86\u3002\u201d\u7389\u7f57\u5239\u4e0d\u89c9\u5fc3\u6298\uff0c\u89c9\u5f97\u674e\u81ea\u6210\u6c14\u5ea6\u4e4b\u5e7f\uff0c\u89c1\u8bc6\u4e4b\u9ad8\uff0c\u6b8a\u975e\u5e38\u4eba\u53ef\u53ca\u3002\u7b11\u9053\uff1a \u201c\u53ef\u60dc\u4f60\u66ff\u7687\u5e1d\u5c0f\u5b50\u6253\u7b97\uff0c\u4ed6\u5374\u8981\u6d3e\u5175\u6253\u4f60\u3002\u201d\u674e\u81ea\u6210\u9053\uff1a\u201c\u90a3\u662f\u4ed6\u7684\u4e8b\u3002\u201d\u7389\u7f57\u5239\u53c8\u7b11 \u9053\uff1a\u201c\u770b\u6837\u5b50\uff0c\u662f\u6ee1\u6d32\uff0c\u660e\u671d\u5c31\u6321\u4e0d\u4f4f\u3002\u4f60\u8fd8\u662f\u8d81\u5728\u6ee1\u6d32\u5175\u672a\u5165\u5173\u4e4b\u524d\uff0c\u8d76\u5feb\u6253\u5230\u5317\u4eac\u5427\u3002 \u7531\u4f60\u6765\u505a\u7687\u5e1d\uff0c\u5c31\u4e0d\u6015\u6ee1\u6d32\u5175\u5165\u4fb5\u4e86\uff1f\u201d\u674e\u81ea\u6210\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u7687\u5e1d\u4eba\u4eba\u53ef\u505a\uff0c\u82e5\u7136\u7531\u6211\u6765 \u505a\uff0c\u53ef\u4ee5\u4fdd\u4f4f\u795e\u5dde\uff0c\u90a3\u9ebd\u5c31\u505a\u505a\u4e5f\u65e0\u6240\u8c13\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u542c\u4ed6\u8bf4\u5f97\u5982\u6b64\u8f7b\u677e\uff0c\u4e5f\u4e0d\u89c9\u5931\u7b11\u3002\u5fc3\u60f3\uff1a\u8fd9\u4eba\u5728\u6700\u5371\u96be\u4e4b\u9645\uff0c\u8fd8\u662f\u5982\u6b64\u96c4\u5fc3\u52c3 \u52c3\u3002\u800c\u4e14\u5b81\u613f\u653e\u8fc7\u4ef7\u503c\u5343\u4e07\u7684\u73e0\u5b9d\uff0c\u53e6\u7b79\u519b\u9977\uff0c\u8270\u82e6\u652f\u6301\u3002\u5982\u6b64\u80f8\u895f\uff0c\u8fde\u718a\u5ef7\u5f3c\u4e5f\u6bd4\u4e0d\u4e0a\u3002 \u770b\u6765\u771f\u6709\u4eba\u541b\u4e4b\u5ea6\uff0c\u521a\u624d\u7684\u8bdd\uff0c\u5012\u4e0d\u662f\u8bf4\u7b11\u7684\u4e86\u3002\u674e\u81ea\u6210\u53c8\u9053\uff1a\u201c\u6240\u4ee5\u6211\u5192\u5145\u5b98\u519b\uff0c\u4e5f\u662f\u4e3a \u5927\u5c40\u7740\u60f3\u3002\u5510\u52aa\u7ed9\u660e\u671d\u6d3e\u6765\u62a4\u9001\u4ed6\u7684\u5fa1\u6797\u519b\u7edf\u9886\u62a2\u52ab\uff0c\u6b64\u4e8b\u6210\u4f55\u4f53\u7edf\uff1f\u7b49\u4e0b\u4f60\u5bf9\u4ed6\u8bf4\uff0c\u90a3\u6279 \u4eba\u662f\u53db\u519b\uff0c\u5e78\u5f97\u671d\u5ef7\u53ca\u65f6\u5bdf\u77e5\uff0c\u6240\u4ee5\u6d3e\u6211\u6765\u6e05\u9664\u53db\u4e71\u3002\u671d\u5ef7\u4e00\u5b9a\u62a4\u9001\u4ed6\u5b89\u5168\u56de\u5230\u5357\u7586\u3002\u7389\u7f57 \u5239\u53cc\u76ee\u95ea\u95ea\u653e\u5149\uff0c\u7b11\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\uff01\u6211\u670d\u4f60\u4e86\uff01\u4f60\u5c45\u7136\u5728\u9003\u547d\u4e4b\u9645\uff0c\u8fd8\u628a\u8fd9\u4ed8\u62c5\u5b50\u653e\u5728 \u80a9\u4e0a\u3002\u8fd9\u9ebd\u8bf4\u662f\u4f60\u8981\u6d3e\u4eba\u62a4\u9001\u4ed6\u4e86\u3002\u201d \u3000\u3000\u674e\u81ea\u6210\u7b11\u9053\uff1a\u201c\u7531\u6211\u4eec\u6d3e\u4eba\u62a4\u9001\uff0c\u8981\u6bd4\u7531\u6821\u8fd9\u5c0f\u5b50\u6240\u6d3e\u7684\u5f97\u529b\u5f97\u591a\u3002\u6b64\u5730\u79bb\u7518\u8083\u4e0d\u8fdc\uff0c \u9001\u5230\u4e86\u7518\u8083\uff0c\u518d\u5165\u9752\u6d77\uff0c\u5c31\u975e\u5b98\u519b\u52bf\u529b\u6240\u53ca\uff0c\u4e5f\u4e0d\u6101\u518d\u6709\u4e91\u71d5\u5e73\u8fd9\u6837\u7684\u5b98\u519b\u5c06\u9886\u6765\u6253\u4ed6\u7684\u4e3b \u610f\u4e86\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u597d\uff0c\u6211\u5c31\u5bf9\u4ed6\u8bf4\u53bb\u3002\u201d\u674e\u81ea\u6210\u53c8\u7b11\u9053\uff1a\u201c\u4e91\u71d5\u5e73\u8fd9\uff0c\u8bf7\u4f60\u501f\u6211\u4e00 \u7528\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8fd9\u79cd\u72d7\u8d3c\uff0c\u6709\u4f55\u7528\u5904\uff1f\u201d\u674e\u81ea\u6210\u7b11\u9053\uff1a\u201c\u5e9f\u7269\u90fd\u53ef\u5229\u7528\uff0c\u4f55\u51b5\u65bc\u4ed6\u3002\u6211 \u4eec\u5404\u8def\u5144\u5f1f\u7ed9\u5927\u519b\u538b\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\u3002\u6211\u60f3\u5229\u7528\u4ed6\u5e2e\u6211\u6253\u4e2a\u80dc\u4ed7\uff0c\u632b\u632b\u4ed6\u4eec\u9510\u6c14\uff0c\u5206\u6563\u4ed6\u4eec\u6ce8 \u610f\u3002\u7136\u5f8c\u6211\u4eec\u624d\u80fd\u5b89\u5168\u64a4\u9000\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u554a\uff0c\u6211\u60f3\u5230\u4e86\u3002\u4f60\u662f\u60f3\u5229\u7528\u8fd9\u8d5a\u57ce\uff0c\u653b\u5360\u53bf\u3002 \u4f60\u4eec\u7a7f\u7684\u90fd\u662f\u5b98\u519b\u670d\u9970\uff0c\u53c8\u6367\u51fa\u4ed6\u4eec\u7684\u4e3b\u5c06\uff0c\u5b88\u57ce\u7684\u5b98\u519b\u4e00\u5b9a\u4e0a\u5f53\u3002\u96be\u4e3a\u4f60\u6536\u96c6\u4e86\u90a3\u9ebd\u591a\u5b98 \u519b\u53f7\u8863\u3002\u201d \u3000\u3000\u518d\u8bf4\u5510\u52aa\u89c1\u7389\u7f57\u5239\u4e0e\u674e\u81ea\u6210\u5e76\u9a6c\u9a70\u5165\u6797\u4e2d\uff0c\u5927\u4e3a\u4e0d\u89e3\uff0c\u95ee\u94c1\u73ca\u745a\u9053\uff1a\u201c\u4ed6\u4eec\u5e72\u4ec0\u9ebd\uff1f\u201d \u94c1\u73ca\u745a\u4e5f\u4e0d\u77e5\u9053\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u662f\u5904\u7f6e\u90a3\u4e9b\u53db\u519b\u5427\u3002\u201d\u7fa4\u76d7\u9996\u9886\u6563\u5728\u56db\u56f4\u864e\u89c6\u7708\u7708\uff0c\u94c1\u73ca\u745a\u9887 \u89c9\u4e0d\u5b89\u3002\u5510\u52aa\u628a\u4e24\u4e2a\u968f\u4ece\u7684\u4f53\u5bfb\u56de\uff0c\u5f53\u573a\u706b\u5316\uff0c\u6309\u7167\u4ed6\u4eec\u7684\u98ce\u4fd7\uff0c\u706b\u5316\u4e4b\u5f8c\uff0c\u6536\u9aa8\u56de\u4e61\u3002\u94c1 \u73ca\u745a\u89c1\u4ed6\u76ee\u4e2d\u6709\u6cea\uff0c\u60f3\u662f\u5fc3\u4e2d\u9887\u4e3a\u60b2\u6124\uff0c\u94c1\u73ca\u745a\u5916\u8868\u5014\u5f3a\uff0c\u5fc3\u9887\u6148\u60b2\u3002\u5fc3\u60f3\uff1a\u8fd9\u51e0\u4e2a\u4eba\u4e07\u91cc \u8fdc\u6765\uff0c\u8eab\u6b7b\u5f02\u4e61\uff0c\u7236\u6bcd\u90fd\u4e0d\u77e5\u9053\uff0c\u8fd9\u624d\u771f\u662f\u4e0d\u503c\u5462\u3002\u89c1\u7389\u7f57\u5239\u4e0e\u90a3\u5c11\u5e74\u5c06\u519b\u5e76\u9a6c\u9a70\u56de\uff0c\u5fc3\u4e2d \u5fd0\u5fd1\u4e0d\u5b89\u3002\u674e\u81ea\u6210\u56de\u5230\u573a\u4e2d\uff0c\u8df3\u4e0b\u9a6c\u4e0e\u5317\u7fa4\u76d7\u5546\u8bae\uff0c\u7389\u7f57\u5239\u76f4\u5411\u5510\u52aa\u8d70\u53bb\u3002\u94c1\u73ca\u745a\u7741\u5927\u4e86 \u773c\uff0c\u53ea\u89c1\u7389\u7f57\u5239\u4e0e\u5510\u52aa\u4f4e\u58f0\u8bf4\u8bdd\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5ffd\u89c1\u5510\u52aa\u4f0f\u5728\u5730\u4e0a\uff0c\u543b\u7389\u7f57\u5239\u8def\u8fc7\u7684\u6ce5\u58eb\u3002\u94c1 \u73ca\u745a\u968f\u7236\u4eb2\u5230\u8fc7\u897f\u5317\uff0c\u77e5\u9053\u8fd9\u662f\u4ed6\u4eec\u6700\u5c0a\u656c\u7684\u793c\u8282\u3002\u8fd9\u624d\u677e\u4e86\u53e3\u6c14\uff0c\u5fc3\u4e2d\u5947\u9053\uff1a\u7389\u7f57\u5239\u6740\u4eba \u5982\u8349\uff0c\u5f3a\u76d7\u62a2\u6765\u7684\u5979\u90fd\u8981\u5206\u4e00\u4efd\u3002\u600e\u9ebd\u5230\u624b\u7684\u73e0\u5b9d\u4e5f\u653e\u8fc7\u4e86\uff1f \u3000\u3000\u5510\u52aa\u4e00\u70b9\u4e0d\u77e5\u7389\u7f57\u5239\u66fe\u52a8\u8fc7\u4ed6\u7684\u4e3b\u610f\uff0c\u611f\u5979\u6551\u547d\u4e4b\u6069\uff0c\u7528\u4ed6\u4eec\u65cf\u4e2d\u6700\u5c0a\u5d07\u7684\u793c\u8282\u5411\u7389\u7f57 \u5239\u53e9\u8c22\uff0c\u5e76\u9053\uff1a\u201c\u82e5\u4f60\u6709\u4e00\u65e5\u5230\u5929\u5c71\u5357\u5317\uff0c\u53ef\u4e00\u5b9a\u8981\u6765\u770b\u6211\u554a\u3002\u201d\u7389\u7f57\u5239\u5e73\u751f\u4ece\u672a\u8bd5\u8fc7\u5185 \u600d\uff0c\u8fd9\u65f6\u5374\u4e0d\u89c9\u6709\u4e86\u69d0\u610f\u3002\u5f53\u4e0b\u628a\u674e\u81ea\u6210\u7684\u8bdd\u8f6c\u8fbe\uff0c\u5510\u52aa\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u4e2d\u56fd\u52a0\u6b64\u5e7f\u5927\uff0c \u81ea\u7136\u597d\u4eba\u574f\u4eba\u90fd\u6709\uff0c\u53db\u519b\u4e4b\u4e8b\uff0c\u4e0d\u5fc5\u63d0\u4e86\u3002\u201d\u548c\u7389\u7f57\u5239\u4e00\u540c\u8fc7\u53bb\u62dc\u8c22\u674e\u81ea\u6210\u3002\u674e\u81ea\u6210\u5df2\u548c\u9655 \u5317\u7fa4\u76d7\u5546\u8bae\u59a5\u5f53\u3002\u7acb\u523b\u6d3e\u9ad8\u8fce\u7965\u624b\u4e0b\u5f97\u529b\u7684\u5934\u76ee\u9ad8\u6770\u548c\u81ea\u5df1\u7684\u5802\u4f84\u674e\u8fc7\uff0c\u9001\u4ed6\u56de\u4e61\u3002 \u3000\u3000\u94c1\u73ca\u745a\u6599\u4e0d\u5230\u4e8b\u60c5\u5982\u6b64\u89e3\u51b3\u3002\u7389\u7f57\u5239\u9053\uff1a\u201c\u73ca\u59b9\uff0c\u7239\u5bfb\u5f97\u4f60\u6025\u5462\uff0c\u53ef\u662f\u73b0\u5728\u5175\u8352\u9a6c\u4e71\uff0c \u4e5f\u4e0d\u5982\u4ed6\u8d70\u5728\u4f55\u65b9\uff1f\u4f60\u548c\u6211\u4e00\u9053\u5230\u5ddd\u897f\u53bb\u5427\uff0c\u6211\uff0e\u53ef\u8981\u8bf7\u4f60\u505a\u5973\u5f3a\u76d7\u5566\uff0c\u54c8\u54c8\uff01\u201d\u94c1\u73ca\u745a\u56e0 \u5cb3\u545c\u73c2\u62d2\u5a5a\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u9887\u6709\u82a5\u8482\uff0c\u8fdf\u8fdf\u4e0d\u7b54\u3002\u7389\u7f57\u5239\u6d4b\u77e5\u5176\u610f\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u59d3\u5cb3\u7684\u5c0f\u5b50\uff0c\u6211 \u4ee5\u524d\u4ee5\u4e3a\u4ed6\u4eba\u54c1\u4e0d\u597d\uff0c\u5176\u5b9e\u4e5f\u8fd8\u4e0d\u9500\u3002\u201d\u5c06\u5cb3\u9e23\u73c2\u501f\u5979\u624b\u5957\uff0c\u6697\u52a9\u5979\u6253\u8d25\u7ea2\u82b1\u9b3c\u6bcd\u7684\u4e8b\u8bf4 \u4e86\u3002\u94c1\u73ca\u745a\u53c8\u6b22\u559c\u53c8\u60b2\u4f24\uff0c\u6b22\u559c\u7684\u662f\u7389\u7f57\u5239\u5bf9\u5cb3\u545c\u73c2\u7684\u8bef\u89e3\u6e10\u6d88\uff1b\u60b2\u4f24\u7684\u662f\u5cb3\u9e23\u73c2\u8f9c\u8d1f\u4e86\u5979 \u7684\u5fc3\u610f\u3002\u542c\u4e86\u7389\u7f57\u5239\u7684\u8bdd\u5f8c\uff1a\u826f\u4e45\uff0c\u826f\u4e45\uff0c\u624d\u9053\u51fa\u4e00\u53e5\u8bdd\u9053\uff1a\u201c\u4ed6\u597d\u4e0d\u597d\u4e0e\u6211\u4f55\u5e72\uff1f\u201d \u3000\u3000\u7389\u7f57\u5239\u542c\u5979\u8bed\u6c14\uff0c\u77e5\u5979\u5b9e\u662f\u60f3\u5ff5\u60c5\u90ce\u3002\u53cd\u6fc0\u5979\u9053\uff1a\u201c\u5929\u4e0b\u81ed\u7537\u5b50\u591a\u7740\u5462\uff01\u6ca1\u6709\u4ed6\u4eec\uff0c\u54b1 \u4eec\u96be\u9053\u5c31\u4e0d\u6210\u5417\uff1f\u4f60\u548c\u6211\u53bb\u5360\u5c71\u4e3a\u738b\uff0c\u6211\u4eec\u9ad8\u5174\u8c01\u5c31\u628a\u8c01\u63b3\u4e0a\u5c71\u4e22\uff0c\u54ed\u54ed\u557c\u557c\u7684\u662f\u8113\u5305\uff1f\u201d \u94c1\u73ca\u745a\u201c\u5478\u201d\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u6ca1\u4f60\u90a3\u6837\u539a\u8138\u76ae\u3002\u201d\u53c8\u9053\uff1a\u201c\u8c01\u54ed\u54ed\u557c\u557c\u4e86\uff1f\u505a\u5973\u5f3a\u76d7\u4fbf\u505a\u5973 \u5f3a\u76d7\uff0c\u96be\u9053\u6211\u4e0d\u6562\u8ddf\u4f60\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u6b63\u8981\u5979\u8bf4\u8fd9\u53e5\u8bdd\uff0c\u514d\u5f97\u5979\u72ec\u81ea\u5728\u6c5f\u6e56\u6d6a\u8361\uff0c\u6697\u5730\u4f24\u5fc3\u3002 \u3000\u3000\u518d\u8bf4\u674e\u81ea\u6210\u628a\u4e8b\u60c5\u529e\u59a5\uff0c\u9001\u8d70\u4e86\u5510\u52aa\u4e4b\u5f8c\uff0c\u548c\u7389\u7f57\u5239\u9053\u522b\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u521a\u624d\u8bf4\u8981\u6253 \u4e0b\u53bf\u4e4b\u5f8c\uff0c\u4fbf\u5168\u5e08\u64a4\u9000\uff0c\u4f60\u4eec\u8981\u64a4\u5230\u90a3\u91cc\uff1f\u201d\u674e\u81ea\u6210\u9053\uff1a\u201c\u9655\u897f\u5c45\u5929\u4e0b\u4e4b\u810a\uff0c\u56db\u5ddd\u662f\u5929\u5e9c\u7cae \u4ed3\uff0c\u6b32\u6210\u4eba\u4e8b\uff0c\u8fd9\u4e24\u7701\u653e\u5f03\u4e0d\u5f97\u3002\u9655\u897f\u8fde\u5e74\u9965\u8352\uff0c\u767e\u59d3\u6d41\u4ea1\u9053\u8def\uff0c\u5f85\u65f6\u673a\u6210\u719f\uff0c\u4e0d\u96be\u805a\u4f17\u767e \u4e07\uff0c\u51fa\u6c49\u4e2d\u800c\u636e\u5df4\u8700\uff0c\u805a\u5175\u805a\u7cae\uff0c\u7136\u5f8c\u518d\u897f\u51fa\u6f7c\u5173\u800c\u4e89\u8c6b\u695a\uff0c\u6325\u97ad\u5317\u4e0a\uff0c\u627c\u6709\u4e2d\u539f\u3002\u5f62\u52bf\u5982 \u6b64\uff0c\u6240\u4ee5\u6211\u6253\u7b97\u5728\u5ddd\u8fb9\u533a\u5efa\u7acb\u57fa\u4e1a\u3002\u79e6\u5cad\u8fde\u7ef5\u516b\u767e\u9980\u91cc\uff0c\u4fbf\u5c01\u5c71\u5f00\u8352\u4e5f\u53ef\u517b\u5175\uff0c\u6211\u662f\u51c6\u5907\u64a4 \u9000\u5230\u79e6\u5cad\u53bb\uff0c\u517b\u7cbe\u84c4\u9510\uff0c\u4e58\u673a\u5f85\u65f6\u3002\u4f60\u610f\u5982\u4f55\uff1f\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u6211\u53ef\u6ca1\u6709\u505a\u5973\u7687\u5e1d\u7684\u96c4 \u5fc3\uff0c\u6211\u5bfb\u5230\u90e8\u4f17\u4e4b\u5f8c\uff0c\u505a\u5c71\u5927\u738b\u53bb\u3002\u201d\u4e24\u4eba\u4e00\u7b11\u9053\u522b\u3002\u674e\u81ea\u6210\u62bc\u4e86\u4e91\u71d5\u5e73\u5f53\u665a\u5c31\u53bb\u8d5a\u57ce\uff0c\u653b \u6253\u53bf\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u4e14\u8bf4\u7389\u7f57\u5239\u548c\u94c1\u73ca\u745a\u5bfb\u5230\u5ddd\u897f\uff0c\u679c\u7136\u5bfb\u5230\u4e86\u90e8\u4f17\uff0c\u94c1\u73ca\u745a\u548c\u7389\u7f57\u5239\u76f8\u5904\u65e5\u4e45\uff0c\u77e5\u5979\u6027\u60c5 \u76f4\u723d\uff0c\u5f53\u65e5\u5f04\u7cdf\u5a5a\u4e8b\uff0c\u4e43\u662f\u5979\u65e0\u5fc3\u4e4b\u5931\uff0c\u4e5f\u4fbf\u4e0d\u518d\u4ecb\u6000\uff0c\u5bf9\u7389\u7f57\u5239\u5982\u540c\u5bf9\u59d0\u59d0\u4e00\u822c\u3002 \u3000\u3000\u5176\u65f6\u5ddd\u519b\u4e8b\u9891\u4ecd\uff0c\u674e\u81ea\u6210\u8fdb\u4e86\u79e6\u5cad\uff0c\u5f20\u732e\u5fe0\u88ab\u9a71\u4eba\u6e56\u5317\uff0c\u6d41\u7a9c\u6c5f\u6dee\u3002\u7389\u7f57\u5239\u5e26\u4e86\u51e0\u767e\u5973 \u5175\uff0c\u5bfb\u5230\u4e86\u5e7f\u5143\u4e03\u5341\u91cc\u5916\u7684\u660e\u6708\u5ce1\u4f5c\u4e3a\u5c71\u5be8\uff0c\u5b89\u5c45\u4e0b\u6765\u3002\u8fd9\u660e\u6708\u5ce1\u662f\u56db\u5ddd\u7740\u540d\u7684\u5929\u9669\u4e4b\u4e00\uff0c \u5c71\u4e0a\u65e0\u8def\u53ef\u901a\uff0c\u6709\u5c71\u6c11\u7528\u6728\u677f\u548c\u6728\u6869\u642d\u6210\u7684\u51e0\u4e4e\u662f\u51cc\u7a7a\u7684\u7f8a\u80a0\u5c0f\u9053\uff0c\u4e0a\u800c\u662f\u5c71\uff0c\u4e0b\u800c\u662f\u5609\u9675 \u6c5f\uff0c\u660e\u6708\u5ce1\u662f\u4e24\u5cf0\u5939\u5cd9\u7684\u5c71\u8c37\u3002\u6709\u65e0\u540d\u6c0f\u8bd7\u4e91\uff1a\u201c\u5929\u9669\u660e\u6708\u5ce1\uff0c\u65ad\u58c1\u9ad8\u63a5\u5929\uff1b\u98de\u9e1f\u98de\u96be\u8fc7\uff0c \u7334\u5b50\u9501\u7709\u5c16\uff1b\u4f4e\u5934\u671b\u5c71\u8c37\uff0c\u767d\u4e91\u811a\u4e0b\u60ac\u3002\u201d\u5f62\u52bf\u9669\u8981\uff0c\u65bc\u6b64\u53ef\u89c1\u3002\u7389\u7f57\u5239\u90e8\u4e0b\u5973\u5175\uff0c\u4e2a\u4e2a\u8eab \u8f7b\u5982\u71d5\uff0c\u5728\u660e\u6708\u5ce1\u5b89\u8425\uff0c\u51fa\u5165\u8981\u6bd4\u7c97\u6c49\u65b9\u4fbf\u5f97\u591a\uff0c\u5b98\u519b\u4e5f\u4e0d\u6613\u8fdb\u88ad\u3002 \u3000\u3000\u53ef\u662f\u660e\u6708\u5ce1\u5730\u65b9\u867d\u597d\uff0c\u5374\u51e0\u4e0e\u5916\u95f4\u9694\u7edd\uff0c\u4e00\u4f4f\u4f4f\u4e86\u53c1\u5e74\uff0c\u8fd8\u662f\u91c7\u4e0d\u5230\u94c1\u98de\u9f99\u6d88\u606f\u3002\u8fd9\u53c1 \u5e74\u95f4\uff0c\u7389\u7f57\u5239\u542c\u5f97\u9053\u8def\u4f20\u95fb\uff0c\u8bf4\u662f\u718a\u5ef7\u5f3c\u518d\u88ab\u8d77\u7528\uff0c\u7763\u5e08\u8fb9\u5173\uff0c\u4e5f\u4e0d\u77e5\u662f\u771f\u662f\u5047\u3002\u94c1\u73ca\u745a\u6302 \u5ff5\u5cb3\u545c\u73c2\uff0c\u4e5f\u65e0\u53ef\u5948\u4f55\u3002 \u3000\u3000\u8fc7\u4e86\u53c1\u5e74\uff0c\u8fd9\u65f6\u5df2\u662f\u5929\u542f\u56db\u5e74\u201c\u300e\u5929\u542f\u300f\u662f\u7531\u6821\u5e74\u53f7\u201d\uff0c\u5ddd\u7684\u5b98\u519b\u6e10\u64a4\uff0c\u6210\u4e3a\u5c0f\u5b89\u5c40 \u9762\u3002\u53ef\u662f\u8fd9\u5e74\u6625\u5929\uff0c\u5e7f\u5143\u53c8\u95f9\u8d77\u9965\u8352\uff0c\u5e7f\u5143\u672c\u662f\u4ea7\u7c73\u4e4b\u533a\uff0c\u4f46\u5b98\u5e9c\u6a2a\u5fb5\u66b4\u655b\uff0c\u5730\u79df\u53c8\u91cd\uff0c\u5e74 \u6210\u597d\u65f6\uff0c\u519c\u6c11\u5c1a\u53ef\u6e29\u9971\uff0c\u5e74\u6210\u4e0d\u597d\uff0c\u9965\u8352\u7acb\u81f3\u3002\u5e7f\u5143\u4e0a\u4e00\u5e74\u5931\u6536\uff0c\u8fd9\u4e00\u5e74\u9752\u9ec4\u4e0d\u63a5\u4e4b\u9645\uff0c\u9965 \u6c11\u9042\u95f9\u51fa\u4e8b\u6765\u3002\u5578\u805a\u56db\u90ca\uff0c\u51c6\u5907\u5165\u57ce\u62a2\u7cae\u3002 \u3000\u3000\u5e7f\u5143\u53bf\u7684\u5c45\u6c11\u51c6\u5907\u62a2\u7cae\uff0c\u6d3e\u4eba\u548c\u7389\u7f57\u5239\u4e92\u901a\u58f0\u6c14\uff0c\u7389\u7f57\u5239\u7b54\u5141\u5e2e\u52a9\u4ed6\u4eec\uff0c\u6d3e\u5973\u5175\u5934\u76ee\u4e54 \u88c5\u4eba\u57ce\u6253\u63a2\u6d88\u606f\u3002\u665a\u4e0a\u56de\u6765\uff0c\u5973\u5934\u76ee\u8bf4\u4e86\u6b63\u4e8b\u4e4b\u5f8c\uff0c\u9053\uff1a\u201c\u4eca\u5929\u8def\u4e0a\u53ef\u70ed\u95f9\u5462\uff0c\u6709\u4eba\u8bf4\u662f\u9053 \u58eb\u8fce\u4eb2\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u90a3\u6709\u9053\u58eb\u8fce\u4eb2\u7684\u9053\u7406\u3002\u201d\u90a3\u5973\u5934\u76ee\u9053\uff1a\u201c\u6211\u4f55\u5c1d\u4e0d\u77e5\u9053\u9053\u58eb \u4e0d\u80fd\u8fce\u4eb2\uff0c\u4e0d\u8fc7\u770b\u8d77\u6765\u5374\u771f\u50cf\u8fce\u4eb2\u7684\u6837\u5b50\uff0c\u602a\u4e0d\u5f97\u8001\u4e61\u90a3\u9ebd\u8bf4\u3002\u201d\u7389\u7f57\u5239\u7b11\u95ee\u9053\uff1a\u201c\u662f\u600e\u9ebd \u4e2a\u6a21\u6837\u5440\uff1f\u201d\u90a3\u5973\u5934\u76ee\u9053\uff1a\u201c\u542c\u5c45\u6c11\u8bf4\uff0c\u4eca\u5929\u6709\u4e00\u5bf9\u5bf9\u7684\u9053\u58eb\u4e58\u9a6c\u897f\u8d70\uff0c\u5927\u7ea6\u6bcf\u9694\u534a\u4e2a\u65f6\u8fb0 \u4fbf\u662f\u4e00\u5bf9\u3002\u6211\u53ea\u77a7\u89c1\u4e00\u5bf9\uff0c\u53ef\u795e\u6c14\u54e9\uff0c\u8eab\u62ab\u5927\u7ea2\u9053\u888d\uff0c\u795e\u8272\u51db\u7136\uff0c\u5c31\u50cf\u505a\u6cd5\u4e8b\u4e00\u9ab0\u3002\u5c45\u6c11 \u8bf4\uff0c\u8d77\u5934\u90a3\u4e00\u5bf9\uff0c\u8fd8\u6367\u7740\u4e00\u4e2a\u7ea2\u5305\u88b1\uff0c\u9ad8\u4e3e\u8fc7\u5934\u3002\u5c31\u50cf\u8fce\u4eb2\u65f6\uff0c\u7537\u5bb6\u5148\u9063\u4eba\u6367\u62dc\u5e16\u5230\u5973\u5bb6\u4e00 \u6837\u3002\u6bcf\u4e00\u5bf9\u9a6c\u7684\u6bdb\u8272\u4e5f\u662f\u76f8\u540c\u3002\u5c31\u5dee\u6ca1\u6709\u5439\u9f13\u624b\uff0c\u8981\u4e0d\u7136\u66f4\u50cf\u8fce\u4eb2\u4e86\u3002\u201d\u7389\u7f57\u5239\u773c\u73e0\u4e00\u8f6c\uff0c \u731b\u7136\u60f3\u8d77\u4e00\u4e8b\uff0c\u9053\uff1a\u201c\u55ef\uff0c\u65f6\u5149\u771f\u5feb\uff0c\u662f\u53c1\u5e74\u4e86\uff01\u201d\u5973\u5934\u76ee\u83ab\u540d\u6240\u4ee5\uff0c\u94c1\u73ca\u745a\u5728\u65c1\u95ee\u9053\uff1a \u201c\u59d0\u59d0\uff0c\u4f60\u65e0\u7aef\u7aef\u611f\u559f\u4ec0\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u9ebd\uff01\u201d\u90a3\u5973\u5934\u76ee\u642d\u8baa\u7b11\u9053\uff1a \u201c\u5be8\u4e3b\u4f60\u8bf4\u50cf\u4e0d\u50cf\u8fce\u4eb2\uff1f\u554a\uff0c\u542c\u5c45\u6c11\u8bf4\uff0c\u9664\u4e86\u9053\u58eb\uff0c\u4e5f\u8fd8\u6709\u4fd7\u4eba\u5462\u3002\u4f46\u9053\u58eb\u591a\u662f\u8001\u5934\uff0c\u4fd7\u4eba \u5219\u5168\u662f\u58ee\u6c49\uff0c\u4e00\u5bf9\u5bf9\u7cbe\u795e\u8d73\u8d73\uff0c\u540c\u6837\u62ab\u7740\u7ea2\u8863\u3002\u6709\u5b69\u5b50\u9017\u4ed6\u4eec\u8bf4\u8bdd\uff0c\u4ed6\u4eec\u8fde\u7709\u6bdb\u4e5f\u4e0d\u7b11\u4e00 \u4e0b\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u9053\u58eb\u8fce\u4eb2\uff0c\u662f\u6b66\u5f53\u6d3e\u63a5\u4ed6\u4eec\u7684\u638c\u95e8\u6765\u4e86\u3002\u6b66\u5f53\u6d3e\u6700\u91cd\u8fd9\u5957\u4eea \u8282\uff0c\u4ee5\u524d\u4ed6\u4eec\u5230\u73ca\u745a\u59b9\u59b9\u5bb6\u4e2d\u5bfb\u638c\u95e8\u4eba\u65f6\uff0c\u4e5f\u662f\u4e00\u5bf9\u5bf9\u7684\u6765\u5462\u3002\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u55ef\uff0c\u90a3\u9ebd\u5353 \u4e00\u822a\u53c8\u8981\u5230\u6b66\u5f53\u5c71\u53d7\u7f6a\u4e86\u3002\u4ed6\u90a3\u51e0\u4e2a\u5e08\u53d4\u771f\u8ba8\u538c\uff0c\u5c24\u5176\u662f\u767d\u77f3\u9053\u4eba\u3002\u59d0\u59d0\uff0c\u4ed6\u4eec\u8fce\u4eb2\uff0c\u6211\u4eec \u62a2\u4eb2\u3002\u201d\u7389\u7f57\u5239\u201c\u5550\u201d\u9053\uff1a\u201c\u80e1\u8bf4\u3002\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u8fc7\u5417\uff1f\u4f60\u559c\u6b22\u8c01\u5c31\u8981\u63b3\u8c01\uff0c\u4e3a \u4ec0\u9ebd\u73b0\u5728\u53c8\u6015\u7f9e\u4e86\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u8fd9\u5c0f\u59ae\u5b50\u597d\u574f\u3002\u4f60\u5f53\u6211\u4e0d\u77e5\u9053\u4f60\u7684\u5fc3\u4e8b\u5417\uff1f\u5353\u4e00 \u822a\u548c\u5cb3\u545c\u73c2\u4e43\u662f\u81f3\u4ea4\u597d\u53cb\uff0c\u4f60\u4e0d\u8fc7\u662f\u60f3\u4ece\u5353\u4e00\u822a\u53e3\u4e2d\u77e5\u9053\u5cb3\u545c\u73c2\u7684\u6d88\u606f\u7f62\u4e86\u3002\u201d\u94c1\u73ca\u745a\u5fc3\u4e8b \u7ed9\u5979\u8bf4\u4e2d\uff0c\u6da8\u7ea2\u4e86\u8138\u4f5c\u72b6\u6253\u5979\u3002\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u4e0d\u8fc7\u54b1\u4eec\u5c31\u662f\u8981\u62a2\u4eb2\uff0c\u4e5f\u5f97\u5f85\u4e00\u6708\u4e4b\u5f8c\uff0c\u65b0 \u90ce\u73b0\u5728\u8fd8\u672a\u8fce\u6765\u5462\uff01\u201d\u94c1\u73ca\u745a\u624b\u6307\u5728\u8138\u4e0a\u4e00\u522e\uff0c\u9053\uff1a\u201c\u539a\u8138\u76ae\uff01\u201d\u7389\u7f57\u5239\u4e00\u7b11\u4f5c\u7f62\u3002 \u3000\u3000\u8fc7\u4e86\u51e0\u5929\uff0c\u9965\u6c11\u5728\u53bf\u91cc\u95f9\u4e8b\uff0c\u5927\u6237\u548c\u53bf\u5b98\u614c\u4e86\uff0c\u4e00\u9762\u5f00\u4ed3\u8d48\u6324\uff0c\u4e00\u9762\u6d3e\u4eba\u5230\u7701\u91cc\u8bf7\u519b\u961f \u6765\uff0c\u8d48\u6324\u4e4b\u7cae\u6709\u9650\uff0c\u6bcf\u4e2a\u9965\u6c11\u6bcf\u5929\u53ea\u80fd\u9886\u4e24\u7897\u8584\u7ca5\uff0c\u53ef\u662f\u8001\u767e\u59d3\u4e5f\u771f\u201c\u7eaf\u826f\u201d\uff0c\u6709\u4e24\u7897\u7ca5\u540a \u547d\uff0c\u4ed6\u4eec\u4fbf\u5df2\u201c\u5b89\u4efd\u201d\u3002\u4ed6\u4eec\u90a3\u77e5\u53bf\u5b98\u5927\u6237\u662f\u800d\u4e24\u9762\u624b\u6cd5\uff0c\u5728\u5175\u529b\u4e0d\u591f\u4e4b\u65f6\uff0c\u4f7f\u7528\u6700\u4f4e\u7684\u4ee3 \u4ef7\u6765\u6000\u67d4\u4ed6\u4eec\uff0c\u7701\u91cc\u7684\u519b\u961f\u4e00\u6765\uff0c\u4ed6\u4eec\u8fde\u4e24\u7897\u8584\u7ca5\u4e5f\u4e0d\u80af\u65bd\u820d\u4e86\u3002\u519b\u961f\u5f53\u5929\u6765\uff0c\u4ed6\u4eec\u5f53\u5929\u5c31 \u65bd\u884c\u201c\u5f39\u538b\u201d\uff0c\u628a\u51e0\u4e2a\u6562\u65bc\u9f13\u566a\u7684\u9965\u6c11\u6740\u4e86\u3002\u8fd9\u4e00\u6765\u9965\u6c11\u5927\u6124\uff0c\u53c8\u6d3e\u4eba\u8bf7\u7389\u7f57\u5239\u5e2e\u4ed6\u4eec\u62a2 \u7cae\u3002\u7389\u7f57\u5239\u6253\u542c\u5f97\u53bf\u4e2d\u7684\u519b\u961f\u7ea6\u6709\u4e8c\u5343\uff0c\u7acb\u523b\u7b54\u5e94\uff0c\u548c\u9965\u6c11\u7ea6\u5b9a\uff0c\u665a\u95f4\u653b\u57ce\u3002 \u3000\u3000\u6021\u6070\u5df4\u5728\u8fd9\u4e00\u5929\uff0c\u6b66\u5f53\u6d3e\u8fce\u63a5\u638c\u95e8\u7684\u961f\u4f0d\u5df2\u7ecf\u4ece\u897f\u56de\u6765\uff0c\u5230\u4e86\u5e7f\u5143\u3002 \u3000\u3000\u5353\u4e00\u822a\u672c\u6765\u4e0d\u60f3\u505a\u6b66\u5f53\u638c\u95e8\uff0c\u53ef\u662f\u53c1\u5e74\u4e4b\u671f\u5df2\u6ee1\uff0c\u65e0\u53ef\u518d\u63a8\u3002\u9ec4\u53f6\u9053\u4eba\u6d3e\u4e86\u7ea2\u4e91\u9053\u4eba\u548c \u767d\u77f3\u9053\u4eba\u7387\u5341\u4e8c\u540d\u5927\u5f1f\u5b50\u6765\u63a5\uff0c\u5353\u4e00\u822a\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u597d\u5728\u5e08\u53d4\u540c\u95e8\u50ac\u4fc3\u4e4b\u4e0b\u767b\u7a0b\uff0c\u53d6\u9053\u56db \u5ddd\uff0c\u5165\u6e56\u5317\uff0c\u56fa\u6b66\u5f53\u5c71\u3002 \u3000\u3000\u8fd9\u65e5\u5230\u4e86\u5e7f\u5143\uff0c\u53ea\u89c1\u57ce\u4e2d\u5201\u6597\u68ee\u4e25\uff0c\u5175\u58eb\u5de1\u8fd8\u8857\u5934\uff0c\u6c14\u6c1b\u8427\u7d22\u3002\u95ee\u8d77\u6765\u624d\u77e5\u662f\u201c\u9965\u6c11\u95f9 \u4e8b\u201d\uff1f\u5353\u4e00\u822a\u5fc3\u4e2d\u53f9\u9053\uff1a\u201c\u5916\u6709\u5bc7\u60a3\uff0c\u5185\u6709\u6d41\u4ea1\u3002\u8fd9\u5927\u660e\u6c5f\u5c71\u662f\u4e0d\u7a33\u4e86\u3002\u201d\u6b66\u5f53\u6d3e\u5728\u5404\u5730\u90fd \u6709\u5f1f\u5b50\u3002\u5e7f\u5143\u57ce\u5185\u6709\u4e00\u5ea7\u6e05\u865a\u89c2\u4fbf\u662f\u6b66\u5f53\u6d3e\u7684\u4eba\u4e3b\u6301\uff0c\u767d\u77f3\u9053\u4eba\u7b49\u8fdb\u57ce\u4e4b\u5f8c\uff0c\u6e05\u865a\u89c2\u7684\u4e3b\u6301 \u4fbf\u628a\u4ed6\u4eec\u63a5\u5230\u89c2\u5185\u3002 \u3000\u3000\u5353\u4e00\u822a\u5e76\u4e0d\u77e5\u9053\u7389\u7f57\u5239\u5c31\u5728\u9644\u8fd1\u5c71\u5934\u843d\u8349\uff0c\u8fd9\u4e00\u665a\u6708\u6697\u661f\u5fae\uff0c\u662f\u5c71\u57ce\u6625\u591c\u7684\u9634\u6c89\u5929\u6c14\uff0c \u5353\u4e00\u822a\u8f97\u8f6c\u53cd\u4fa7\uff0c\u4e2d\u591c\u672a\u7720\u3002\u5ffd\u542c\u5f97\u7a97\u5916\u6709\u4eba\u8f7b\u8f7b\u6572\u4e86\u4e00\u4e0b\uff0c\u5353\u4e00\u822a\u4ee5\u4e3a\u662f\u767d\u77f3\u9053\u4eba\uff0c\u63a8\u5f00 \u7a97\u95e8\uff0c\u4e00\u4e2a\u9ed1\u8863\u6c49\u5b50\u500f\u7136\u8df3\u4e86\u8fdb\u6765\uff0c\u8863\u88f3\u7834\u88c2\uff0c\u9762\u6709\u8840\u6c61\uff0c\u5728\u5fae\u5f31\u7684\u83dc\u6cb9\u706f\u4e0b\uff0c\u663e\u5f97\u5341\u5206\u53ef \u6015\uff0c\u5353\u4e00\u822a\u5403\u4e86\u4e00\u60ca\uff0c\u90a3\u4eba\u9053\uff1a\u201c\u5353\u5144\u7981\u58f0\u3002\u201d\u5353\u4e00\u822a\u77a7\u6e05\u695a\u4e86\uff01\u8fd9\u4eba\u7adf\u7136\u662f\u5cb3\u545c\u73c2\u3002 \u3000\u3000\u5353\u4e00\u822a\u5c0f\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u5566\uff1f\u201d\u5cb3\u545c\u73c2\u4e00\u53e3\u628a\u6cb9\u706f\u5439\u706d\u3002\u9694\u5ba4\u7684\u767d\u77f3\u9053\u4eba\u95ee\u9053\uff1a\u201c\u4e00 \u822a\uff0c\u4f60\u8fd8\u672a\u7761\u5417\uff1f\u201d\u5cb3\u545c\u73c2\u6447\u4e86\u6447\u5934\uff0c\u7528\u624b\u6307\u7740\u81ea\u5df2\uff0c\u53c8\u6446\u4e86\u6446\u624b\uff0c\u793a\u610f\u5353\u4e00\u822a\u4e0d\u8981\u8bf4\u662f\u4ed6 \u5230\u6765\u3002\u5353\u4e00\u822a\u9053\uff1a\u201c\u7761\u5566\uff0c\u6211\u8d77\u6765\u559d\u676f\u8336\u3002\u5e08\u53d4\uff0c\u4f60\u8001\u4eba\u5bb6\u4e5f\u5b89\u6b47\u5427\u3002\u201d\u8bf4\u5b8c\u4e4b\u5f8c\uff0c\u628a\u53e3\u8d34 \u5728\u5cb3\u9e23\u73c2\u8033\u6839\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u5e08\u53d4\u771f\u8ba8\u538c\uff01\u201d\u548c\u5cb3\u9e23\u73c2\u8e51\u624b\u8e51\u811a\uff0c\u8131\u4e86\u978b\u5b50\uff0c\u8eba\u5230\u5e8a\u4e0a\uff0c\u4e24\u4eba \u5171\u4e00\u4e2a\u6795\u5934\uff0c\u8d34\u7740\u8033\u8fb9\u8bf4\u8bdd\u3002\u5cb3\u9e23\u73c2\u8bf4\u51fa\u4e86\u4e00\u6bb5\u60ca\u5fc3\u52a8\u9b44\u7684\u4e8b\u6765\uff01 \u3000\u3000\u539f\u6765\u718a\u5ef7\u5f3c\u7f62\u4e86\u8fbd\u4e1c\u7ecf\u7565\u4e4b\u5f8c\uff0c\u7ee7\u4efb\u7684\u8881\u5e94\u6cf0\u4e0d\u662f\u5c06\u624d\uff0c\u6ee1\u6d32\u519b\u7edf\u5e05\u52aa\u513f\u54c8\u8d64\u81ea\u7edf\u5927 \u519b\uff0c\u6c34\u9646\u4ff1\u8fdb\uff0c\u4e00\u6218\u653b\u4e0b\u6e16\u9633\uff0c\u518d\u6218\u53c8\u653b\u4e0b\u8fbd\u9633\uff0c\u8881\u5e94\u6cf0\u624b\u4e0b\u7684\u4e24\u5458\u5927\u5c06\u8d3a\u4e16\u8d24\u5c24\u4e16\u529f\u88ab\u91d1 \u5175\u201c\u5176\u65f6\u6ee1\u6d32\u5c1a\u672a\u5efa\u201c\u5927\u6e05\u201d\u56fd\u53f7\uff0c\u52aa\u513f\u54c8\u8d64\u81ea\u79f0\u201c\u5927\u6c57\u201d\uff0c\u56fd\u53f7\u201c\u91d1\u201d\uff0c\u81f3\u7687\u592a\u6781\u59cb\u79f0 \u5e1d\u3002\u201d\u4e71\u7bad\u5c04\u6b7b\uff0c\u8881\u5e94\u6cf0\u5728\u8fbd\u9633\u57ce\u4e1c\u5317\u7684\u9547\u8fdc\u697c\u7763\u6218\uff0c\u57ce\u7834\u4e4b\u5f8c\uff0c\u4e3e\u706b\u711a\u697c\u81ea\u6740\u3002\u660e\u671d\u8fb9\u9632 \u5927\u519b\uff0c\u4f24\u4ea1\u516b\u4e5d\uff0c\u6e83\u4e0d\u6210\u519b\u3002\u65bc\u662f\u6cb3\u4e1c\u4e4b\u53c1\u6cb3\u5821\u7b49\u4e94\u5341\u5be8\uff0c\u53e4\u57ce\u3001\u8349\u6cb3\uff0e\u65b0\u7538\uff0c\u5bbd\u7538\uff0c\u5927 \u7538\uff0e\u6c38\u7538\uff0e\u51e4\u51f0\uff0e\u6d77\u5dde\uff0e\u8000\u5dde\u3001\u76ca\u5dde\u3001\u76d6\u5dde\u3001\u590d\u5dde\uff0e\u5168\u5dde\u7b49\u5927\u5c0f\u4e03\u5341\u9980\u57ce\uff0c\u5168\u88ab\u6ee1\u5dde\u519b\u653b \u5360\uff0c\u8fbd\u6cb3\u4ee5\u4e1c\uff0c\u9042\u65e0\u5b8c\u571f\uff01 \u3000\u3000\u7ecf\u6b64\u4e00\u573a\u5927\u8d25\uff0c\u660e\u5ef7\u5927\u9707\u3002\u6731\u7531\u6821\u60f3\u8d77\u4e86\u7236\u7687\u4e4b\u8a00\uff0c\u987f\u4e0b\u51b3\u5fc3\uff0c\u628a\u4ee5\u524d\u5f39\u52be\u718a\u5ef7\u5f3c\u7684\u5927 \u81e3\u5c3d\u90fd\u8d2c\u8c2a\uff0c\u6d3e\u4e13\u4f7f\u6367\u8bcf\u5230\u6e56\u5317\u6c5f\u590f\uff0c\u8bf7\u718a\u5ef7\u5f3c\u590d\u51fa\uff0c\u91cd\u4efb\u7ecf\u7565\uff0c\u590d\u8d50\u4e0a\u65b9\u5251\u3002\u53ef\u662f\u8bdd\u867d\u5982 \u6b64\uff0c\u5b9e\u6743\u4ecd\u4e0d\u5728\u718a\u5ef7\u5f3c\u624b\u4e2d\u3002\u672c\u6765\u6309\u671d\u5ef7\u5236\u5ea6\uff0c\u8fbd\u4e1c\u7ecf\u7565\u8282\u5236\u53c1\u65b9\u3002\u6240\u8c13\u201c\u53c1\u65b9\u201d\u4e43\u662f \u201c\u4e00\u201d\u5e7f\u5b81\u5de1\u629a\uff0c\u7edf\u7387\u9646\u519b\u3002\u201c\u4e8c\u201d\u5929\u6d25\u5de1\u629a\u201c\u53c1\u201d\u767b\u83b1\u5de1\u629a\u3002\u8fd9\u4e24\u4e2a\u5de1\u629a\u5206\u7edf\u6c34\u5e08\uff0c\u800c\u8fbd \u4e1c\u7ecf\u7565\u5219\u9a7b\u5c71\u6d77\u5173\uff0c\u5c45\u4e2d\u8282\u5236\u3002\u718a\u5ef7\u5f3c\u5efa\u8bae\u4ee5\u5e7f\u5b81\u7684\u9646\u519b\u5236\u654c\u5168\u529b\uff0c\u800c\u4ee5\u5929\u6d25\u767b\u83b1\u7684\u6c34\u5e08\u5206 \u6270\u201c\u8fbd\u4e1c\u534a\u5c9b\u201d\uff0c\u8fd9\u4fbf\u662f\u660e\u6e05\u6218\u53f2\u4e0a\u6709\u540d\u7684\u201c\u53c1\u65b9\u5e03\u7f6e\u7b56\u201d\u3002 \u3000\u3000\u5353\u4e00\u822a\u9887\u77e5\u5175\u6cd5\uff0c\u542c\u5cb3\u545c\u73c2\u8c08\u5230\u718a\u5ef7\u5f3c\u6240\u5b9a\u7684\u201c\u53c1\u65b9\u5e03\u7f6e\u7b56\u201d\u5f8c\uff0c\u9053\uff1a\u201c\u718a\u7ecf\u7565\u786e\u662f\u5927 \u5c06\u4e4b\u624d\uff0c\u8fd9\u6218\u7565\u653b\u5b88\u517c\u5907\uff0c\u5b9a\u5f97\u4e0d\u9519\u5440\uff01\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u6218\u7565\u662f\u6b7b\u7684\uff0c\u4eba\u662f\u6d3b\u7684\u3002\u6709\u4e86\u597d\u7684 \u6218\u7565\uff0c\u5374\u65e0\u53ef\u8c03\u4e4b\u5175\uff0c\u5176\u5b9e\u4e5f\u4e0d\u662f\u65e0\u53ef\u8c03\u4e4b\u5175\uff0c\u800c\u662f\u6709\u4e0d\u542c\u8c03\u4e4b\u5c06\uff0c\u4ee5\u81f3\u53c1\u65b9\u5e03\u7f6e\u4e4b\u7b56\uff0c\u6210 \u4e86\u4e00\u7eb8\u5177\u6587\u3002\u201d\u5353\u4e00\u822a\u9a87\u9053\uff1a\u201c\u718a\u7ecf\u7565\u521a\u5f3a\u51b3\u65ad\uff0c\u600e\u9ebd\u6709\u4e0d\u542c\u8c03\u4e4b\u5c06\u3002\u201d\u5cb3\u545c\u73c2\u5728\u4ed6\u8033\u8fb9\u8f7b \u53f9\u9053\uff1a\u201c\u4ee5\u524d\u7684\u5bb0\u76f8\u65b9\u4ece\u54f2\u88ab\u7f62\u5f8c\uff0c\u6362\u6765\u4e86\u4e00\u4e2a\u53f6\u5411\u9ad8\u505a\u5bb0\u76f8\uff0c\u6362\u6765\u6362\u53bb\uff0c\u90fd\u662f\u548c\u9b4f\u5fe0\u8d24\u4e00 \u9f3b\u5b54\u51fa\u6c14\u7684\u4eba\u3002\u5728\u8fbd\u4e1c\u7ecf\u7565\u8282\u5236\u4e0b\u7684\u53c1\u4e2a\u5de1\u629a\u4e4b\u4e2d\uff0c\u5e7f\u5b81\u5de1\u629a\u738b\u5316\u8d1e\u5175\u529b\u6700\u539a\uff0c\u504f\u504f\u4ed6\u5c31\u662f \u53f6\u5411\u9ad8\u7684\u95e8\u751f\uff0c\u4e0d\u80af\u542c\u718a\u5ef7\u5f3c\u7684\u8c03\u9063\u3002\u718a\u7ecf\u7565\u8981\u96c6\u5175\u5e7f\u5b81\uff0c\u4ed6\u5374\u8981\u5206\u5175\u9a7b\u5b88\u3002\u718a\u7ecf\u7565\u4ee5\u524d\u6240 \u5efa\u7684\u519b\u961f\u5728\u8881\u5e94\u6cf0\u7edf\u7387\u4e0b\uff0c\u7ecf\u8fbd\u6e16\u4e24\u6218\uff0c\u5dee\u4e0d\u591a\u5168\u727a\u7272\u4e86\u3002\u718a\u7ecf\u7565\u6367\u5c1a\u65b9\u5b9d\u5251\u51fa\u5173\uff0c\u62db\u52df\u5f97 \u4e49\u519b\u6570\u5343\uff0c\u800c\u738b\u5316\u8d1e\u5374\u62e5\u5175\u5341\u9980\u4e07\u3002\u718a\u7ecf\u7565\u7a7a\u6709\u201c\u7ecf\u7565\u201d\u4e4b\u540d\uff0c\u5b9e\u6743\u53cd\u4e0d\u53ca\u738b\u5316\u8d1e\u8fdc\u751a\u3002\u7ecf \u629a\u4e0d\u548c\uff0c\u4e24\u4eba\u90fd\u62dc\u647a\u4e0a\u671d\uff0c\u5bb0\u76f8\u53f6\u5411\u9ad8\u8892\u62a4\u738b\u5316\u8d1e\uff0c\u64cd\u7eb5\u201c\u5ef7\u8bae\u201d\uff0c\u7adf\u7136\u4e0b\u4ee4\u738b\u5316\u8d1e\u4e0d\u5fc5\u53d7 \u718a\u5ef7\u5f3c\u8282\u5236\u3002\u65bc\u662f\u4e8b\u60c5\u8d8a\u5f04\u8d8a\u7cdf\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u9ebd\u8fbd\u4e1c\u7684\u5371\u5c40\u662f\u65e0\u53ef\u633d\u56de\u7684 \u4e86\u3002\u6211\u5144\u4e0d\u5728\u718a\u7ecf\u7565\u5de6\u53f3\uff0c\u4e00\u4eba\u56de\u5230\u5173\u5185\uff0c\u5374\u662f\u4e3a\u4f55\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u95ee\u4e86\u8fd9\u51e0\u53e5\u8bdd\u5f8c\uff0c\u4e45\u4e45\u4e0d\u89c1\u5cb3\u9e23\u73c2\u56de\u7b54\uff0c\u4f46\u89c9\u9762\u4e0a\u51b0\u51c9\u4e00\u7247\uff0c\u539f\u6765\u662f\u5cb3\u545c\u73c2\u7684\u6cea \u6c34\u3002\u5353\u4e00\u822a\u9053\uff1a\u201c\u600e\u9ebd\u5566\uff1f\u201d\u5cb3\u9e23\u73c2\u5f3a\u6b62\u60b2\u4f24\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f60\u4e14\u542c\u6211\u7ec6\u8bf4\u4e0b\u53bb\u3002\u718a\u7ecf\u7565\u867d \u7136\u624b\u4e0a\u65e0\u5175\uff0c\u53ef\u662f\u4e00\u5230\u8fbd\u4e1c\uff0c\u8fd8\u6253\u4e86\u4e24\u6b21\u80dc\u4ed7\u3002\u53ef\u6068\u738b\u5316\u8d1e\u65e2\u4e0d\u77e5\u5175\uff0c\u5374\u53c8\u8f7b\u654c\uff0c\u6ee1\u6d32\u519b\u5bdf \u77e5\u4ed6\u4eec\u4e8c\u4eba\u4e0d\u548c\uff0c\u52aa\u513f\u54c8\u8d64\u590d\u7387\u5927\u519b\u6e21\u8fc7\u8fbd\u6cb3\uff0c\u738b\u5316\u8d1e\u5206\u5175\u5404\u5730\uff0c\u7adf\u88ab\u5404\u4e2a\u51fb\u7834\u3002\u8fd9\u4e00\u4ed7\u6bd4 \u8fbd\u6e16\u4e4b\u8d25\u66f4\u60e8\uff0c\u738b\u5316\u8d1e\u5168\u519b\u8986\u6ca1\uff0c\u8fd8\u662f\u9760\u718a\u7ecf\u7565\u4eb2\u7387\u7684\u4e94\u5343\u4eb2\u5175\uff0c\u624d\u628a\u4ed6\u63a9\u62a4\u8fdb\u5173\uff0c\u8fbd\u6cb3\u4ee5 \u897f\u5168\u5f52\u654c\u6709\uff0c\u8fde\u5e7f\u5b81\u4e5f\u5931\u9677\u4e86\uff01\u718a\u7ecf\u7565\u548c\u738b\u5316\u8d1e\u56de\u5230\u5173\u5185\uff0c\u7acb\u88ab\u671d\u5ef7\u902e\u6355\u3002\u9b4f\u5fe0\u8d24\u548c\u53f6\u5411\u9ad8 \u5506\u4f7f\u671d\u4e2d\u515a\u7fbd\uff0c\u8054\u7ae0\u5f39\u52be\uff0c\u7531\u6821\u4e0d\u77e5\u8fb9\u60c5\uff0c\u7adf\u7136\u5904\u718a\u7ecf\u7565\u6218\u8d25\u5931\u5b88\u4e4b\u7f6a\u3002\u201d\u5353\u4e00\u822a\u9a87\u9053\uff1a \u201c\u7ed3\u679c\u5982\u4f55\uff1f\u201d\u9762\u4e0a\u53c8\u662f\u4e00\u7247\u51b0\u51c9\u3002\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u53ef\u601c\u718a\u7ecf\u7565\u5c31\u8fd9\u6837\u4e0d\u660e\u4e0d\u767d\u51a4\u6789\u6b7b\u4e86\u3002\u201d \u5353\u4e00\u822a\u5634\u5df4\u4e00\u5f20\uff0c\u51e0\u4e4e\u5931\u58f0\u3002\u5cb3\u545c\u73c2\u6025\u5fd9\u628a\u4ed6\u7684\u5634\u5df4\u63a9\u4f4f\uff0c\u5353\u4e00\u822a\u7684\u6cea\u6c34\u4e5f\u6ef4\u4e86\u51fa\u6765\u3002\u5cb3\u545c \u73c2\u9053\uff1a\u201c\u718a\u7ecf\u7565\u662f\u53bb\u51ac\u5f52\u5929\u7684\u3002\u7531\u6821\u771f\u72e0\u5fc3\uff0c\u542c\u53f6\u5411\u9ad8\u4e4b\u8bae\uff0c\u628a\u8fbd\u4e1c\u5927\u8d25\u4e4b\u8d23\u5168\u63a8\u5728\u718a\u7ecf\u7565 \u5934\u4e0a\u3002\u7ed3\u679c\u718a\u7ecf\u7565\u88ab\u65ab\u4e86\u5934\uff0c\u8fd8\u8981\u4f20\u9996\u4e5d\u8fb9\uff01\u6b7b\u65e0\u5b8c\uff0c\u590d\u53d7\u6218\u8d25\u7684\u803b\u8fb1\u7f6a\u540d\uff0c\u771f\u662f\u4eba\u95f4\u60e8 \u4e8b\uff0c\u83ab\u8fc7\u65bc\u6b64\uff01\u800c\u90a3\u4e2a\u738b\u5316\u8d1e\u5374\u53cd\u800c\u88ab\u5224\u8f7b\u7f6a\uff0c\u662f\u524a\u804c\u4e86\u4e8b\u3002\u201d\u8bf4\u5230\u6b64\u5904\uff0c\u5353\u4e00\u822a\u518d\u4e5f\u5fcd\u53d7 \u4e0d\u4f4f\uff0c\u54fd\u54bd\u6709\u58f0\u3002\u9694\u58c1\u7684\u767d\u77f3\u9053\u4eba\u53c8\u53eb\u9053\uff1a\u201c\u4e00\u822a\uff0c\u4f60\u600e\u9ebd\u8fd8\u672a\u7761\u5417\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u6545\u4f5c\u68a6\u9b47\u4e4b\u72b6\uff0c\u6323\u624e\u4e00\u9635\uff0c\u628a\u811a\u987f\u5f97\u5e8a\u677f\u683c\u683c\u4f5c\u54cd\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u624d\u9053\uff1a\u201c\u55ef\uff0c\u6211 \u68a6\u89c1\u5e08\u5085\u3002\u201d\u767d\u77f3\u9053\uff1a\u201c\u4e0d\u5fc5\u80e1\u601d\u4e71\u60f3\uff0c\u660e\u65e9\u8fd8\u8981\u8d76\u8def\u3002\u201d\u5353\u4e00\u822a\u5e94\u4e86\u4e00\u58f0\uff0c\u8d34\u5728\u5cb3\u9e23\u73c2\u8033 \u8fb9\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u7406\u4ed6\uff0c\u4f60\u518d\u8bf4\u4e0b\u53bb\u3002\u4f60\u6b66\u529f\u5353\u7edd\uff0c\u600e\u9ebd\u4f1a\u53d7\u4f24\u4e86\uff1f\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u718a\u7ecf\u7565\u6789 \u6b7b\u4e4b\u5f8c\uff0c\u9b4f\u5fe0\u8d24\u6d3e\u4eba\u62ff\u6211\u3002\u6211\u7070\u5fc3\u5df2\u6781\uff0c\u60f3\u9003\u5f80\u5929\u5c71\u3002\u6628\u65e5\u9014\u4e2d\uff0c\u548c\u6155\u5bb9\u51b2\u4ed6\u4eec\u906d\u9047\uff0c\u6fc0\u6218 \u534a\u65e5\uff0c\u6211\u6253\u6b7b\u4e86\u56db\u4e2a\u9526\u8863\u536b\u58eb\uff0c\u4fa5\u5e78\u9003\u4e86\u51fa\u6765\u3002\u53ef\u662f\u6155\u5bb9\u51b2\u90a3\u4e5f\u771f\u5389\u5bb3\uff0c\u7d27\u8ffd\u4e0d\u820d\uff0c\u6211\u9003\u5230 \u5e7f\u5143\uff0c\u4ed6\u4eec\u4e5f\u8ffd\u5230\u5e7f\u5143\uff0c\u6211\u8d81\u7740\u5929\u9ed1\uff0c\u7ed5\u4e86\u51e0\u4e2a\u5708\u5b50\uff0c\u8fd9\u624d\u9003\u5230\u8fd9\u91cc\u3002\u55ef\uff0c\u4f60\u7684\u5e08\u53d4\u662f\u63a5\u4f60 \u56de\u53bb\u638c\u95e8\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4ed6\u4eec\u94fa\u5f20\u5176\u4e8b\uff0c\u95f9\u5f97\u9050\u8fe9\u7686\u77e5\uff0c\u6211\u771f\u4e0d\u597d\u610f\u601d\u3002\u201d\u5cb3\u545c\u73c2\u5ffd\u4ece \u6000\u4e2d\u6478\u51fa\u4e00\u672c\u4e66\u6765\uff0c\u585e\u7ed9\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u66ff\u6211\u4fdd\u7ba1\u8fd9\u4e00\u672c\u4e66\uff0c\u82e5\u7136\u4ee5\u5f8c\u518d\u6709\u718a\u7ecf\u7565\u8fd9\u6837\u6709\u80c6 \u6709\u8bc6\u7684\u8fb9\u5173\u5927\u5c06\uff0c\u4f60\u5c31\u8bbe\u6cd5\u628a\u8fd9\u672c\u4e66\u732e\u7ed9\u4ed6\u3002\u55ef\uff0c\u53ea\u6015\u4ee5\u5f8c\u6ca1\u8fd9\u6837\u7684\u4eba\u4e86\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a \u201c\u4ec0\u9ebd\u4e66\uff1f\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u718a\u7ecf\u7565\u5728\u5bb6\u53c1\u5e74\uff0c\u7740\u4e86\u4e00\u672c\u4e66\uff0c\u540d\u4e3a\u201c\u8fbd\u4e1c\u4f20\u201d\uff0c\u5c06\u8fbd\u4e1c\u7684\u6218\u7565 \u8981\u585e\uff0c\u654c\u4eba\u7684\u865a\u5b9e\u5f3a\u5f31\uff0c\u5404\u6b21\u7528\u5175\u7684\u5f97\u5931\uff0c\u5168\u5199\u5728\u91cc\u9762\u3002\u662f\u4e86\u89e3\u654c\u60c5\uff0c\u4e13\u95e8\u5bf9\u4ed8\u6ee1\u6d32\u7684\u4e00\u672c \u4e66\u3002\u9b4f\u5fe0\u8d24\u6d3e\u4eba\u62ff\u6211\uff0c\u53ea\u6050\u591a\u534a\u662f\u4e3a\u4e86\u8fd9\u4e00\u672c\u4e66\u3002\u4f60\u662f\u6b66\u5f53\u638c\u95e8\uff0c\u6536\u85cf\u8fd9\u4e00\u672c\u4e66\u90a3\u662f\u6700\u59a5\u5f53 \u4e0d\u8fc7\u3002\u201d\u5353\u4e00\u822a\u5c06\u4e66\u585e\u5165\u6000\u4e2d\u3002\u5ffd\u542c\u5f97\u5916\u9762\u4f3c\u6709\u58f0\u54cd\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u53ea\u542c\u5f97\u5927\u5e08\u5144\u865e\u65b0\u57ce\u53eb \u9053\uff1a\u201c\u767d\u77f3\u5e08\u53d4\uff0c\u5916\u9762\u6709\u4eba\u62dc\u8bbf\u4f60\u8001\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u7ad6\u8033\u7ebd\u542c\uff0c\u542c\u5f97\u767d\u77f3\u9053\u4eba\u7684\u811a\u6b65\u58f0\u5df2\u51fa\u5230\u5916\u9762\uff0c\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u6211\u8d70\u4e86\u5427\uff01\u53ea\u6050\u6765 \u7684\u4e43\u662f\u8ffd\u5175\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u54b1\u4eec\u6709\u96be\u540c\u5f53\u3002\u82e5\u662f\u8ffd\u5175\uff0c\u4f60\u66f4\u4e0d\u5e94\u5b64\u8eab\u9003\u51fa\u3002\u201d \u3000\u3000\u4e14\u8bf4\u767d\u77f3\u9053\u4eba\u5f00\u4e86\u89c2\u95e8\uff0c\u53ea\u89c1\u6155\u5bb9\u51b2\u548c\u91d1\u72ec\u5f02\u53d4\u4f84\u7ad9\u5728\u5916\u9762\uff0c\u5f8c\u9762\u4e00\u7247\u9ed1\u538b\u538b\u7684\uff0c\u5927\u7ea6 \u8fd8\u6709\u6570\u5341\u4eba\u4e4b\u591a\u3002\u767d\u77f3\u9053\u4eba\u5927\u5403\u4e00\u60ca\u3002\u6155\u5bb9\u51b2\u7b11\u9053\uff1a\u201c\u5e78\u4f1a\uff0c\u5e78\u4f1a\u3002\u54b1\u4eec\u4ee5\u524d\u867d\u6709\u70b9\u5c0f\u5c0f\u7684 \u8fc7\u8282\uff0c\u90a3\u662f\u4f60\u8bef\u5377\u4eba\u53bb\uff0c\u54b1\u4eec\u5f7c\u6b64\u660e\u767d\u3002\u90a3\u70b9\u8fc7\u8282\uff0c\u63ed\u8fc7\u4fbf\u7b97\uff0c\u4e0d\u5fc5\u518d\u63d0\u3002\u53ea\u662f\u4eca\u665a\u4f60\u4eec\u9053 \u89c2\u4e4b\u4e2d\u85cf\u6709\u94a6\u72af\uff0c\u8fd9\u5374\u4e0d\u662f\u5c0f\u4e8b\u4e86\u3002\u4f60\u60f3\u81ea\u8eab\u6e05\u767d\uff0c\u8bf7\u628a\u94a6\u72af\u4ea4\u7ed9\u6211\u4eec\u3002\u201d \u3000\u3000\u767d\u77f3\u9053\u4eba\u8be7\u9053\uff1a\u201c\u4ec0\u9ebd\u94a6\u72af\uff1f\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u5c31\u662f\u5cb3\u545c\u73c2\u90a3\u4e2a\u5c0f\u5b50\u3002\u201d\u767d\u77f3\u6012\u9053\uff1a\u201c\u6211 \u5c82\u4f1a\u5e87\u62a4\u90a3\u4e2a\u5c0f\u5b50\uff1f\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31\u6700\u597d\u4e0d\u8fc7\uff0c\u6211\u4eec\u4e5f\u4e0d\u5fc5\u4eba\u89c2\u5185\u52a8\u624b\u4e86\uff0c \u4f60\u628a\u4ed6\u7f1a\u51fa\u6765\u5427\uff01\u201d\u767d\u77f3\u9053\uff1a\u201c\u6211\u6574\u665a\u90fd\u5728\u89c2\u4e2d\uff0c\u672a\u66fe\u5916\u51fa\uff0c\u4ed6\u6765\u4e86\u6211\u5c82\u6709\u4e0d\u77e5\u4e4b\u7406\uff1f\u8fd9\u9053 \u89c2\u4e2d\u90fd\u662f\u6211\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\uff0c\u90a3\u6709\u4ec0\u9ebd\u5cb3\u9e23\u73c2\u5728\u5185\uff01\u201d\u91d1\u72ec\u5f02\u9053\uff1a\u201c\u767d\u77f3\u9053\u4eba\uff0c\u4e0d\u662f\u6211\u5c0f\u89d1 \u4f60\uff0c\u6709\u672c\u4e8b\u9ad8\u7684\u591c\u884c\u4eba\u6765\uff0c\u4e0d\u89c1\u5f97\u4f60\u5c31\u77e5\u9053\u3002\u5cb3\u9e23\u73c2\u548c\u4f60\u4eec\u6240\u63a5\u7684\u638c\u95e8\u4eba\u6b63\u662f\u81f3\u4ea4\u597d\u53cb\uff0c\u8fd9 \u8c01\u4e0d\u77e5\u9053\uff1f\u201d\u767d\u77f3\u9053\u4eba\u5fc3\u9ad8\u6c14\u50b2\uff0c\u90a3\u7981\u5f97\u4ed6\u8fd9\u4e00\u6fc0\uff0c\u6da8\u7ea2\u4e86\u9762\uff0c\u6c14\u547c\u547c\u7684\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u8fdb \u6765\u641c\uff0c\u82e5\u641c\u4e0d\u51fa\u6765\uff0c\u4f60\u5f97\u7ed9\u6211\u53e9\u53c1\u4e2a\u54cd\u5934\uff01\u201d\u628a\u89c2\u95e8\u5927\u5f00\uff0c\u6155\u5bb9\u51b2\u7b49\u4e00\u6d8c\u800c\u5165\uff01 \u3000\u3000\u89c2\u5185\u7684\u6b66\u5f53\u5f1f\u5b50\u5168\u90fd\u60ca\u8d77\uff0c\u7ea2\u4e91\u9053\u4eba\u4e5f\u8fce\u4e86\u51fa\u6765\uff0c\u6155\u5bb9\u51b2\u5728\u89c2\u5916\u5e03\u6ee1\u536b\u58eb\uff0c\u5728\u89c2\u5185\u5404\u5904 \u4e5f\u6d3e\u4eba\u76d1\u5b88\u3002\u7136\u5f8c\u95ee\u9053\uff1a\u201c\u8bf7\u95ee\u8d35\u6d3e\u638c\u95e8\u5353\u4e00\u822a\u4f4f\u5728\u90a3\u4e00\u95f4\u623f\uff1f\u201d\u767d\u77f3\u9053\u4eba\u4e00\u77a7\uff0c\u5341\u4e8c\u5f1f\u5b50 \u5168\u90fd\u5728\u6b64\uff0c\u53ea\u6709\u5353\u4e00\u822a\u4e0d\u89c1\u51fa\u6765\uff0c\u5fc3\u4e2d\u5fd0\u5fd1\u3002\u4f46\u4e00\u60f3\u5353\u4e00\u822a\u662f\u81ea\u5df2\u90bb\u623f\uff0c\u6709\u4eba\u5077\u8fdb\u4ed6\u7684\u623f \u95f4\uff0c\u81ea\u5df1\u5c82\u6709\u4e0d\u77e5\u4e4b\u7406\u3002\u4fbf\u9053\uff1a\u201c\u6211\u5f15\u4f60\u53bb\u3002\u4f60\u53ef\u8981\u9075\u5b88\u6b66\u6797\u89c4\u77e9\u3002\u201d\u6155\u5bb9\u51b2\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a \u81ea\u7136\uff0c\u5bf9\u4f60\u4eec\u8d35\u6d3e\u638c\u95e8\uff0c\u6211\u5c82\u6562\u7a0d\u5b58\u4e0d\u656c\u4e4b\u5ff5\u3002\u201d\u767d\u77f3\u9053\u4eba\u5e26\u4ed6\u4eec\u5230\u4e86\u5353\u4e00\u822a\u95e8\u5916\uff0c\u6572\u95e8 \u9053\uff1a\u201c\u4e00\u822a\uff0c\u5f00\u95e8\uff01\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u5353\u4e00\u822a\u201c\u54bf\u54d1\u201d\u4e00\u58f0\u628a\u623f\u95e8\u7f13\u7f13\u6253\u5f00\uff0c\u6001\u5ea6\u4ece\u5bb9\uff0c\u7acb\u5728\u623f\u4e2d\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u6765 \u505a\u4ec0\u9ebd\uff1f\u201d\u91d1\u72ec\u5f02\u8de8\u4eba\u623f\u4e2d\uff0c\u56db\u5904\u5f20\u671b\uff0c\u90a3\u6709\u5cb3\u545c\u73c2\u7684\u5f71\u5b50\uff0c\u91d1\u5343\u63ed\u5f00\u5e10\u5b50\uff0c\u67e5\u770b\u5e8a\u5e95\uff0c\u4e5f \u6ca1\u4eba\u5f71\u3002\u5353\u4e00\u822a\u5389\u58f0\u65a5\u9053\uff1a\u201c\u6211\u6b66\u5f53\u6d3e\u4e43\u6b66\u6797\u9886\u8896\uff0c\u5c82\u5ba2\u4eba\u8fd9\u6837\u65e0\u793c\uff1f\u201d\u4ed6\u8fd9\u8bdd\u5b58\u5fc3\u6311\u8d77\u5e08 \u53d4\u5e08\u5144\u7684\u6012\u706b\u3002\u767d\u77f3\u9053\u4eba\u5fc3\u4e2d\u559c\u9053\uff1a\u201c\u4e00\u822a\u8fd9\u5b69\u5b50\u679c\u7136\u4e0d\u9519\uff0c\u50cf\u4e2a\u638c\u95e8\u4eba\u7684\u6837\u5b50\uff01\u6211\u53ef\u5f97\u7ed9 \u4ed6\u6491\u8170\u3002\u201d\u4e5f\u8ddf\u7740\u559d\u9053\uff1a\u201c\u91d1\u8001\u602a\uff0c\u4f60\u82e5\u4e0d\u5411\u6211\u4eec\u638c\u95e8\u8d54\u793c\uff0c\u4f11\u60f3\u51fa\u6b64\u89c2\u95e8\uff01\u201d\u91d1\u72ec\u5f02\u4e00\u58f0 \u51b7\u7b11\uff0c\u4fbf\u60f3\u4e0e\u767d\u77f3\u4ea4\u624b\u3002\u6155\u5bb9\u51b2\u628a\u4ed6\u62c9\u7740\uff0c\u5ffd\u9053\uff1a\u201c\u9694\u90bb\u662f\u8c01\u7684\u623f\u95f4\uff1f\u201d\u767d\u77f3\u9053\u4eba\u66f4\u6c14\uff0c\u6012 \u9053\uff1a\u201c\u662f\u6211\u7684\u623f\u95f4\uff0c\u600e\u9ebd\u6837\uff1f\u201d\u6155\u5bb9\u51b2\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u62db\u547c\u6211\u4eec\u8fdb\u53bb\u5750\u5750\u5417\uff1f\u5230\u4e86\u4f60\u7684\u623f\u95f4\u518d \u7ed9\u4f60\u8d54\u793c\u4e5f\u8fd8\u4e0d\u8fdf\u3002\u5353\u5144\u867d\u662f\u638c\u95e8\uff0c\u4f46\u5230\u5e95\u662f\u4f60\u5c0f\u8f88\uff0c\u8981\u8d54\u793c\u4e5f\u8be5\u5411\u4f60\u8d54\u793c\u5440\uff01\u201d\u8bdd\u8bed\u51b7\u5632 \u70ed\u8bbd\uff0c\u767d\u77f3\u9053\u4eba\u8d8a\u53d1\u5927\u6012\uff0c\u8df3\u4e86\u51fa\u6765\uff0c\u4e00\u638c\u51fb\u5f00\u81ea\u5df1\u7684\u623f\u95e8\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u6765\u2026\u2026\u201d \u201c\u770b\u201d\u5b57\u672a\u66fe\u8bf4\u51fa\uff0c\u5df2\u662f\u76ee\u77aa\u53e3\u5446\uff0c\u5cb3\u545c\u73c2\u7adf\u7136\u5750\u5728\u81ea\u5df1\u5e8a\u4e0a\uff01 \u3000\u3000\u539f\u6765\u767d\u77f3\u9053\u4eba\u4e00\u51fa\uff0c\u5cb3\u9e23\u73c2\u4e0e\u5353\u4e00\u822a\u5df2\u60f3\u597d\u8ba1\u7b56\uff0c\u5cb3\u545c\u73c2\u7acb\u5373\u8fc7\u53bb\uff0c\u6709\u5fc3\u628a\u767d\u77f3\u9053\u4eba\u5377 \u5165\u6f29\u6da1\u3002 \u3000\u3000\u91d1\u72ec\u5f02\u563b\u563b\u51b7\u7b11\uff0c\u6155\u5bb9\u51b2\u62a2\u4e86\u8fdb\u6765\uff0c\u5288\u9762\u4e00\u62f3\uff0c\u5cb3\u545c\u73c2\u4e00\u6251\u4e0b\u5e8a\uff0c\u5251\u950b\u6a2a\u524a\uff0c\u4e24\u4eba\u4ea4 \u624b\uff0c\u987f\u65f6\u684c\u5012\u5e8a\u574d\uff0c\u5728\u623f\u95f4\u91cc\u4e52\u4e52\u4e53\u4e53\u6253\u5f97\u9707\u5929\u4ef7\u54cd\uff01 \u3000\u3000\u767d\u77f3\u9053\u4eba\u505a\u58f0\u4e0d\u5f97\uff0c\u91d1\u72ec\u5f02\u4e00\u6293\u6293\u6765\uff0c\u5353\u4e00\u822a\u62d4\u5251\u6321\u4f4f\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u662f\u4ed6\u4eec\u65e0 \u793c\u5728\u5148\uff0c\u800c\u4e14\u5cb3\u5144\u4e5f\u662f\u54b1\u4eec\u6b66\u5f53\u6d3e\u7684\u670b\u53cb\uff0c\u5c82\u53ef\u968f\u4fbf\u4efb\u4ed6\u6355\u4eba\uff01\u201d\u91d1\u72ec\u5f02\u559d\u9053\uff1a\u201c\u6b66\u5f53\u6d3e\u53c8 \u600e\u6837\uff0c\u5305\u5e87\u94a6\u72af\uff0c\u8fd9\u7f6a\u540d\u4f60\u4eec\u53ef\u515c\u4e0d\u4e86\uff01\u201d\u5353\u4e00\u822a\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u522b\u4fe1\u4ed6\u4eec\u9b3c\u8bdd\uff0c\u4ed6\u4eec \u662f\u4e54\u4f20\u5723\u65e8\uff0c\u56fe\u62a5\u79c1\u4ec7\uff01\u201d\u767d\u77f3\u9053\u4eba\u4e0d\u77e5\u718a\u5ef7\u5f3c\u5df3\u6b7b\uff0c\u60f3\u8d77\u6614\u65e5\u5728\u4eac\uff0c\u4ed6\u4eec\u679c\u7136\u4e5f\u66fe\u4e54\u4f20\u5723 \u65e8\uff0c\u8981\u5bb3\u718a\u5ef7\u5f3c\u7684\u4e8b\u3002\u5cb3\u9e23\u73c2\u662f\u718a\u5ef7\u5f3c\u6700\u5f97\u529b\u7684\u52a9\u624b\uff0c\u4ed6\u4eec\u8981\u5c06\u4ed6\u7f6e\u65bc\u6b7b\u5730\uff0c\u4e5f\u5728\u60c5\u7406\u4e4b \u4e2d\u3002\u767d\u77f3\u9053\u4eba\u80c6\u6c14\u987f\u58ee\uff0c\u60f3\u9053\uff1a\u53ea\u8981\u5cb3\u9e23\u73c2\u4e0d\u662f\u94a6\u72af\uff0c\u90a3\u5c31\u53ea\u80fd\u7b97\u662f\u6c5f\u6e56\u4e0a\u7684\u79c1\u4eba\u4ec7\u6597\uff0c\u8c01 \u90fd\u53ef\u4ee5\u52a9\u62f3\u3002\u6211\u867d\u7136\u4e0d\u6b22\u559c\u5cb3\u545c\u73c2\u8fd9\u5c0f\u5b50\uff0c\u4f46\u53ef\u5f97\u4fdd\u5168\u6b66\u5f53\u6d3e\u7684\u5a01\u540d\u3002\u773c\u770b\u5353\u4e00\u822a\u654c\u4e0d\u4f4f\u91d1 \u72ec\u5f02\u638c\u529b\uff0c\u767d\u77f3\u9053\u4eba\u594b\u7136\u800c\u8d77\uff0c\u62d4\u5251\u52a0\u4eba\u6218\u56e2\uff01 \u3000\u3000\u91d1\u72ec\u5f02\u5927\u559d\u9053\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\uff01\u201d\u767d\u77f3\u53eb\u9053\uff1a\u201c\u6b66\u6797\u5996\u5b7d\uff0c\u4eba\u4eba\u5f97\u800c\u8bdb\u4e4b\uff01\u5403\u6211\u4e00 \u5251\uff01\u201d\u5c55\u5f00\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u593a\u547d\u5251\u6cd5\uff0c\u548c\u91d1\u72ec\u5f02\u6076\u6597\u8d77\u6765\uff01\u5cb3\u545c\u73c2\u4e0e\u6155\u5bb9\u51b2\u4e5f\u4ece\u623f\u5185\u6253\u51fa\u8d70 \u5eca\u3002\u8fd9\u4e00\u6765\uff0c\u89c2\u4e2d\u5927\u4e71\uff0c\u7ea2\u4e91\u9053\u4eba\u548c\u6b66\u5f53\u6d3e\u5341\u4e8c\u4e2a\u5927\u5f1f\u5b50\u4e00\u9f50\u62d4\u5251\uff0c\u4e0e\u6155\u5bb9\u51b2\u5e26\u6765\u7684\u9526\u8863\u536b \u571f\uff0c\u6df7\u6218\u6076\u6597\uff01 \u3000\u3000\u6155\u5bb9\u51b2\u4e0e\u5cb3\u9e23\u73c2\u6349\u5bf9\u6740\uff0c\u4e00\u4e2a\u662f\u795e\u62f3\u65e0\u654c\uff0c\u4e00\u4e2a\u662f\u5251\u6cd5\u901a\u7384\uff0c\u6070\u6070\u6253\u6210\u5e73\u624b\u3002\u767d\u77f3\u9053\u4eba \u672c\u6765\u4e0d\u662f\u91d1\u72ec\u5f02\u5bf9\u624b\uff0c\u4f46\u91d1\u72ec\u5f02\u5728\u53c1\u5e74\u4e4b\u524d\uff0c\u66fe\u7ed9\u7389\u7f57\u5239\u6311\u65ad\u4e86\u7435\u7436\u9aa8\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u7528\u6700\u597d\u7684 \u9a73\u9aa8\u7eed\u7b4b\u4e4b\u672f\uff0c\u7ed9\u4ed6\u533b\u6cbb\uff0c\u7528\u836f\u57f9\u8865\uff0c\u7ecf\u8fc7\u53c1\u5e74\uff0c\u7435\u7436\u9aa8\u624d\u6162\u6162\u751f\u957f\uff0c\u5b8c\u597d\u5982\u521d\u3002\u53ef\u662f\u9aa8\u867d \u53ef\u8865\uff0c\u5143\u6c14\u5374\u5df2\u5927\u4f24\uff0c\u52a0\u4ee5\u53c1\u5e74\u6765\u8352\u5e9f\u6b66\u529f\uff0c\u66f4\u662f\u5927\u4e0d\u5982\u524d\u3002\u8fd9\u4e00\u6765\u6b64\u6d88\u5f7c\u957f\uff0c\u767d\u77f3\u9053\u4eba\u7adf \u4e0e\u91d1\u72ec\u5f02\u65d7\u9f13\u76f8\u5f53\uff0c\u6253\u6210\u5e73\u624b\uff1f \u3000\u3000\u6b66\u5f53\u6d3e\u7684\u5251\u6cd5\u539f\u662f\u4e0a\u4e58\u5251\u6cd5\uff0c\u5341\u4e8c\u4e2a\u5927\u5f1f\u5b50\u53c8\u90fd\u662f\u672c\u6d3e\u4e2d\u51fa\u7c7b\u62d4\u8403\u7684\u4eba\u7269\uff0c\u6155\u5bb9\u51b2\u5e26\u8fdb \u89c2\u4e2d\u7684\u536b\u58eb\uff0c\u7adf\u81ea\u62b5\u6321\u4e0d\u4f4f\uff0c\u6e10\u6e10\u7ed9\u8ffd\u5230\u4e00\u9685\u3002\u6155\u5bb9\u51b2\u5f15\u542d\u957f\u5578\uff0c\u628a\u7559\u5728\u89c2\u5916\u76d1\u5b88\u7684\u536b\u58eb\u90fd \u62db\u4e86\u8fdb\u6765\u3002\u4ee5\u4f17\u51cc\u5be1\uff01\u5f62\u52bf\u53c8\u662f\u4e00\u53d8\uff01 \u3000\u3000\u6df7\u6218\u4e00\u4f1a\uff0c\u9760\u8fd1\u9053\u89c2\u5927\u95e8\u7684\u536b\u58eb\u5ffd\u7136\u558a\u9053\uff1a\u201c\u57ce\u4e2d\u8d77\u706b\uff1f\u201d\u539f\u6765\u662f\u7389\u7f57\u5239\u4e0e\u94c1\u73ca\u745a\u9886\u4e86 \u51e0\u5341\u4e2a\u5973\u5175\uff0c\u6df7\u5165\u96be\u6c11\u4e4b\u4e2d\uff0c\u7ed9\u4ed6\u4eec\u9886\u5934\uff0c\u5c06\u53bf\u8859\u4e00\u628a\u706b\u70e7\u4e86\uff0c\u62a2\u5230\u6b66\u5668\u548c\u57ce\u4e2d\u7684\u9a7b\u519b\u5927\u6253 \u8d77\u6765\uff0c\u6c11\u8d8a\u805a\u8d8a\u591a\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u5df2\u662f\u8fc7\u4e07\uff01\u8981\u77e5\u8fd9\u73ed\u6c11\uff0c\u5e73\u65f6\u4e0d\u6562\u4e0e\u5b98\u519b\u4f5c\u5bf9\uff0c\u4e00\u6765\u662f\u56e0\u4e3a \u53d7\u6b3a\u538b\u8fc7\u4e45\uff0c\u4f46\u51e1\u80fd\u5fcd\u7684\u4e5f\u5c31\u5fcd\u53d7\u8fc7\u53bb\uff0c\u4e8c\u6765\u662f\u65e0\u4eba\u9886\u5934\uff0c\u4e0d\u6562\u95f9\u4e8b\u3002\u800c\u4eca\u5728\u997f\u7ebf\u4e0a\uff0c\u4e0d\u95f9 \u4e8b\u4fbf\u5f97\u997f\u6b7b\uff0c\u5927\u5bb6\u90fd\u820d\u547d\u62da\u4e86\uff0c\u52a0\u4ee5\u6709\u4eba\u9886\u5934\uff0c\u4eba\u4e00\u591a\u80c6\u6c14\u4fbf\u58ee\uff0c\u8fc7\u4e07\u6c11\uff0c\u805a\u96c6\u8d77\u6765\uff0c\u72b9\u5982 \u6d2a\u6c34\u51b2\u7834\u5824\u9632\uff0c\u6d69\u6d69\u8361\u8361\uff0c\u6740\u58f0\u9707\u5929\uff0c\u9510\u4e0d\u53ef\u5f53\u3002\u7389\u7f57\u5239\u4e00\u5251\u51b2\u5165\u5b98\u519b\u961f\u4e2d\uff0c\u628a\u5e26\u5175\u7684\u7edf\u9886 \u4e00\u628a\u6293\u8d77\uff0c\u63b7\u4eba\u706b\u7a9f\u4e4b\u4e2d\uff0c\u5b98\u519b\u987f\u65f6\u5927\u4e71\u3002 \u3000\u3000\u7389\u7f57\u5239\u89c1\u5c40\u9762\u5df2\u5b9a\uff0c\u5b98\u519b\u4e0d\u662f\u6295\u964d\uff0c\u5c31\u662f\u5168\u88ab\u6b7c\u706d\uff0c\u4e00\u7b11\u6740\u51fa\uff0c\u628a\u9886\u5bfc\u6c11\u6b7c\u5b98\u519b\u7684\u4efb\u52a1 \u4ea4\u7ed9\u4e86\u94c1\u73ca\u745a\uff0c\u770b\u770b\u5df2\u8fc7\u5348\u591c\uff0c\u7a0d\u4e00\u601d\u91cf\uff0c\u4fbf\u5411\u57ce\u897f\u7684\u6e05\u865a\u89c2\u75be\u5954\u800c\u53bb\uff01 \u3000\u3000\u518d\u8bf4\u6155\u5bb9\u51b2\u7b49\u89c1\u57ce\u4e2d\u5927\u706b\uff0c\u6740\u58f0\u9690\u9690\u53ef\u95fb\uff0c\u9f50\u90fd\u5403\u60ca\u3002\u53ea\u9053\u662f\u90a3\u4e00\u80a1\u76d7\u532a\uff0c\u653b\u7834\u4e86\u57ce\u3002 \u91d1\u5343\u53eb\u9053\uff1a\u201c\u5408\u529b\u628a\u53db\u8d3c\u6349\u4f4f\uff0c\u6b66\u5f53\u6d3e\u7684\u4e0d\u8981\u7406\u4ed6\u3002\u201d\u8fd9\u4e43\u662f\u5206\u5316\u4e4b\u8ba1\u3002\u4f46\u6b66\u5f53\u6d3e\u7684\u4f17\u5f1f\u5b50 \u90fd\u5df2\u6597\u5f97\u6027\u8d77\uff0c\u90a3\u80af\u8ba9\u4ed6\u4eec\u5408\u653b\u5cb3\u9e23\u73c2\uff0c\u53c8\u6df7\u6218\u4e00\u9635\uff0c\u706b\u5149\u8d8a\u5927\uff0c\u6740\u58f0\u8d8a\u9ad8\uff0c\u91d1\u5343\u820d\u4e86\u767d\u77f3 \u9053\u4eba\uff0c\u731b\u6251\u5cb3\u9e23\u73c2\uff0c\u5353\u4e00\u822a\u4e5f\u820d\u4e86\u5bf9\u654c\u7684\u536b\u58eb\uff0c\u633a\u5251\u62e6\u622a\u3002\u5cb3\u545c\u73c2\u5237\u5237\u4e24\u5251\uff0c\u5c55\u51fa\u5929\u5c71\u5251\u6cd5 \u7684\u7edd\u62db\u201c\u79fb\u661f\u6458\u6597\u201d\uff0c\u4e0a\u523a\u53cc\u76ee\uff0c\u4e2d\u523a\u54bd\u5589\uff0c\u5251\u6cd5\u51cc\u5389\u5f02\u5e38\uff0c\u94d9\u662f\u6155\u5bb9\u51b2\u529f\u529b\u6df1\u6e5b\uff0c\u4e5f\u8feb\u5f97 \u95ea\u8eab\u8eb2\u907f\u3002\u5cb3\u9e23\u73c2\u7fe9\u5982\u5de8\u9e70\uff0c\u9661\u7136\u6740\u51fa\uff01\u5353\u4e00\u822a\u9053\uff1a\u201c\u5cb3\u5144\uff0c\u4f60\u5148\u8d70\uff01\u201d\u91d1\u5343\u6765\u622a\uff0c\u5cb3\u9e23\u73c2 \u53cc\u624b\u6234\u7740\u91d1\u4e1d\u624b\u5957\uff0c\u4e0d\u6015\u6bd2\u4f24\uff0c\u5de6\u638c\u4e00\u9707\uff0c\u5c06\u91d1\u5343\u9707\u5f97\u6b6a\u6b6a\u659c\u659c\uff0c\u7acb\u8eab\u4e0d\u5b9a\u3002 \u3000\u3000\u5353\u4e00\u822a\u6b3a\u8eab\u76f4\u8fdb\uff0c\u4e00\u5251\u659c\u523a\uff0c\u5c06\u91d1\u72ec\u5f02\u624b\u8155\u5212\u4f24\uff0c\u5cb3\u9e23\u73c2\u5df2\u6740\u51fa\u91cd\u56f4\uff0c\u8df3\u4e0a\u5c4b\u9876\uff0c\u8ff3\u81ea \u53bb\u4e86\u3002\u91d1\u72ec\u5f02\u5927\u6012\u559d\u9053\uff1a\u201c\u5353\u4e00\u822a\u662f\u94a6\u72af\u4e00\u5925\uff0c\u62ff\u4e0d\u7740\u94a6\u72af\u4e5f\u8981\u62ff\u4ed6\uff01\u201d\u53cc\u638c\u8fde\u73af\u75be\u51fb\uff0c\u5353 \u4e00\u822a\u90a3\u4e00\u5251\u4e43\u662f\u4e58\u5cb3\u545c\u73c2\u4e4b\u52bf\uff0c\u8bba\u672c\u8eab\u529f\u529b\uff0c\u5374\u8fd8\u4e0d\u662f\u91d1\u72ec\u5f02\u5bf9\u624b\uff0c\u7ed9\u4ed6\u4e00\u8feb\uff0c\u9669\u8c61\u73af\u751f\uff0c \u767d\u77f3\u9053\u4eba\u53c8\u7ed9\u6155\u5bb9\u51b2\u622a\u7740\uff0c\u4e5f\u6b63\u5728\u5403\u7d27\u3002\u6b66\u5f53\u5f1f\u5b50\u867d\u6709\u51e0\u4eba\u62da\u547d\u6740\u51fa\u6765\u6551\uff0c\u53ef\u662f\u91d1\u72ec\u5f02\u4e00\u62db \u7d27\u4f3c\u4e00\u62db\uff0c\u6551\u5175\u672a\u5230\uff0c\u5353\u4e00\u822a\u7684\u5b9d\u5251\u5df2\u7ed9\u4ed6\u4e00\u811a\u8e22\u98de\uff0c\u91d1\u72ec\u5f02\u54c8\u54c8\u5927\u7b11\uff0c\u4e00\u6293\u7167\u5353\u4e00\u822a\u9876\u5fc3 \u6293\u4e0b\uff01 \u3000\u3000\u91d1\u72ec\u5f02\u5927\u7b11\u672a\u505c\uff0c\u5ffd\u7136\u53e6\u6709\u4e00\u4e2a\u5a07\u5a9a\u7684\u6e05\u8106\u7684\u7b11\u58f0\uff0c\u597d\u50cf\u94f6\u9488\u523a\u6765\uff0c\u628a\u91d1\u72ec\u5f02\u7684\u5927\u7b11\u538b \u4e86\u4e0b\u53bb\uff0c\u91d1\u72ec\u5f02\u9762\u8272\u5927\u53d8\uff0c\u624b\u8db3\u8f6f\uff0c\u90a3\u4e00\u6293\u52b2\u9053\u5927\u51cf\uff0c\u8fdf\u7f13\u65e0\u529b\uff0c\u5353\u4e00\u822a\u4e00\u95ea\u95ea\u5f00\uff0c\u53c8\u559c\u53c8 \u60ca\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u7389\u7f57\u5239\u5df2\u5982\u7d2b\u71d5\u63a0\u6ce2\uff0c\u4ece\u5c4b\u9876\u4e0a\u75be\u63a0\u4e0b\u6765\uff01 \u3000\u3000\u91d1\u72ec\u5f02\u5728\u53c1\u5e74\u4e4b\u524d\uff0c\u5c1a\u4e14\u8d25\u5728\u7389\u7f57\u5239\u624b\u4e0b\uff0c\u4f55\u51b5\u5982\u4eca\u529f\u529b\u5df2\u5927\u4e0d\u5982\u524d\u3002\u7389\u7f57\u5239\u4e00\u773c\u77a5\u89c1 \u91d1\u72ec\u5f02\uff0c\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u90a3\u8d24\u6167\u59bb\u5b50\u771f\u597d\u5fc3\uff0c\u5c45\u7136\u53c8\u653e\u4f60\u51fa\u6765\u4e86\uff01\u4f60\u7684\u7435\u7436\u9aa8\u5df2\u5408\u62e2\u4e86 \u5417\uff1f\u201d\u91d1\u72ec\u5f02\u8fd9\u6b21\u539f\u662f\u80cc\u59bb\u79c1\u9003\uff0c\u88ab\u7389\u7f57\u5239\u4e00\u8bf4\uff0c\u987f\u65f6\u60f3\u8d77\u59bb\u5b50\u4ee5\u524d\u7684\u8bdd\uff1a\u82e5\u7136\u4e0d\u670d\u7ba1\u675f\u518d \u6765\u6c5f\u6e56\uff0c\u5c31\u4e0d\u7406\u4ed6\u7684\u6b7b\u6d3b\u3002\u5fc3\u4e2d\u66f4\u614c\uff0c\u820d\u4e86\u5353\u4e00\u822a\uff0c\u593a\u95e8\u800c\u8d70\u3002\u7389\u7f57\u5239\u7b11\u4e2a\u4e0d\u505c\uff0c\u624b\u4e2d\u5251\u5374 \u5982\u95ea\u7535\u60ca\u98d9\uff0c\u8f6c\u77ac\u4e4b\u95f4\u523a\u4f24\u597d\u51e0\u540d\u9526\u8863\u536b\u58eb\uff0c\u76f4\u5411\u91d1\u72ec\u5f02\u523a\u53bb\u3002\u91d1\u72ec\u5f02\u521a\u521a\u8d70\u51fa\u5927\u95e8\uff0c\u7ed9\u5979 \u4e00\u5251\u523a\u4e2d\u8db3\u8ddf\uff0c\u4e00\u4e2a\u6eda\u5730\u846b\u82a6\uff0c\u8dcc\u4e0b\u659c\u5761\u3002\u6155\u5bb9\u51b2\u4e00\u58f0\u5927\u543c\uff0c\u4e00\u62f3\u7167\u7389\u7f57\u5239\u80cc\u5fc3\u731b\u51fb\uff0c\u7389\u7f57 \u5239\u907f\u5f3a\u51fb\u5f31\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c\u547c\u7684\u4e00\u58f0\u63a0\u8fc7\u6155\u5bb9\u51b2\u5934\u9876\uff0c\u5728\u534a\u7a7a\u633d\u4e86\u4e00\u6735\u5251\u82b1\uff0c\u6740\u4e0b\u6765\u65f6\uff0c\u4fe1\u624b \u53c8\u4f24\u4e86\u4e24\u540d\u536b\u58eb\u3002\u7389\u7f57\u5239\u7684\u5251\u62db\uff0c\u6700\u4e3a\u72e0\u8fa3\uff0c\u6240\u523a\u7684\u5168\u662f\u654c\u4eba\u5173\u8282\u7a74\u9053\uff0c\u53d7\u4f24\u7684\u536b\u58eb\u75db\u5f97\u6ee1 \u5730\u6253\u6eda\uff0c\u7389\u7f57\u5239\u6ee1\u573a\u6e38\u8d70\uff0c\u4fe8\u5982\u5f69\u8776\u7a7f\u82b1\uff0c\u4e1c\u523a\u4e00\u5251\uff0c\u897f\u523a\u4e00\u5251\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u53d7\u4f24\u7684\u536b\u58eb\u5df2 \u6709\u5341\u4e8c\u53c1\u540d\uff0c\u5269\u4e0b\u6765\u7684\u5168\u90fd\u80c6\u5bd2\u3002\u7389\u7f57\u5239\u63a0\u8fc7\u767d\u77f3\u9053\u4eba\u8eab\u65c1\uff0c\u7b11\u9053\uff1a\u201c\u53c1\u5e74\u524d\u6597\u5251\u4e4b\u7ea6\u8fd8\u7b97 \u6570\u9ebd\uff1f\u201d\u767d\u77f3\u9053\u4eba\u54ed\u7b11\u4e0d\u5f97\uff0c\u7389\u7f57\u5239\u5237\u5237\u4e24\u5251\uff0c\u7a81\u7136\u4ece\u767d\u77f3\u9053\u4eba\u80c1\u4e0b\u7a7f\u51fa\uff0c\u5c06\u548c\u767d\u77f3\u9053\u4eba\u5bf9 \u6297\u7684\u4e24\u540d\u536b\u58eb\u523a\u4f24\uff0c\u53c8\u7fe9\u7136\u63a0\u51fa\u3002\u6155\u5bb9\u51b2\u6c14\u7ea2\u4e86\u773c\uff0c\u4e00\u62f3\u5c06\u4e00\u540d\u6b66\u5f53\u5f1f\u5b50\u6253\u7ffb\uff0c\u62a2\u8fc7\u6765\u6597\u3002 \u7389\u7f57\u5239\u5ffd\u5730\u653e\u58f0\u7b11\u9053\uff1a\u201c\u6155\u5bb9\u51b2\uff0c\u5730\u4e0b\u6253\u6eda\u7684\u90a3\u4e9b\u540c\u4f34\u5c3d\u591f\u4f60\u6536\u62fe\u4e86\uff0c\u5c11\u966a\u5c11\u966a\uff01\u201d\u7a81\u7136\u63a0 \u8fc7\u5353\u4e00\u822a\u8eab\u8fb9\uff0c\u7b11\u9053\uff1a\u201c\u4f55\u82e6\u5728\u8fd9\u91cc\u4e0e\u4ed6\u4eec\u7f20\u6597\uff1f\u201d\u53cc\u6307\u4e00\u6263\uff0c\u4e00\u4e0b\u6263\u7740\u4e86\u5353\u4e00\u822a\u624b\u8155\u7a74\uff0c \u75be\u5982\u98d8\u98ce\u7684\u51b2\u51fa\u95e8\u5916\u3002\u767d\u77f3\u9053\u4eba\u5927\u58f0\u53eb\u56b7\uff0c\u8d76\u51fa\u770b\u65f6\uff0c\u4e24\u4eba\u5df2\u6d88\u5931\u5728\u51a5\u51a5\u591c\u8272\u4e4b\u4e2d\u3002 \u3000\u3000\u767d\u77f3\u9053\u4eba\u6012\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff01\u201d\u5bf9\u6155\u5bb9\u51b2\u62b1\u62f3\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u4e24\u8d25\u4ff1\u4f24\uff0c\u4e0d\u5fc5\u518d\u6253 \u4e86\u3002\u201d\u6155\u5bb9\u51b2\u4e00\u770b\uff0c\u5cb3\u9e23\u73c2\u4e0e\u5353\u4e00\u822a\u90fd\u5df2\u8d70\u4e86\uff0c\u800c\u4e14\u81ea\u5df1\u8fd9\u8fb9\u53c8\u4f24\u4e86\u8fd9\u9ebd\u591a\u4eba\uff0c\u518d\u6253\u4e5f\u4e0d\u662f \u6b66\u5f53\u8109\u7684\u5bf9\u624b\uff0c\u53ea\u597d\u7f62\u4e86\u3002 \u3000\u3000\u518d\u8bf4\u7389\u7f57\u5239\u5c06\u5353\u4e00\u822a\u5e26\u51fa\u6570\u91cc\u8def\u9065\uff0c\u653e\u677e\u4e86\u624b\u3002\u5353\u4e00\u822a\u6028\u9053\uff1a\u201c\u4f60\u8fd9\u662f\u5e72\u5417\uff1f\u201d\u7389\u7f57\u5239 \u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u6837\uff0c\u4e5f\u8bf7\u4e0d\u5230\u4f60\u6765\u4e86\u3002\u201d\u5353\u4e00\u822a\u60f3\u8d77\u5e08\u53d4\u4eec\u7684\u56fa\u6267\uff0c\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u8fd8\u4ee5\u4e3a \u4f60\u628a\u6211\u63b3\u53bb\u5462\uff01\u4f60\u4f4f\u5728\u90a3\u91cc\uff1f\u201d\u7389\u7f57\u5239\u60f3\u8d77\u201c\u63b3\u4eba\u201d\u201c\u62a2\u4eb2\u201d\u7684\u7b11\u8bdd\uff0c\u5fc3\u9b44\u4e00\u6c64\uff0c\u9053\uff1a\u201c\u4f60 \u8ddf\u6211\u6765\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u8ddf\u7389\u7f57\u5239\u8d70\u5230\u660e\u6708\u5ce1\u65f6\uff0c\u5df2\u662f\u7834\u6653\u65f6\u5206\uff0c\u4e91\u6d77\u4e2d\u9732\u51fa\u4e73\u767d\u8272\u7684\u66d9\u5149\uff0c\u6653\u98ce\u62c2\u4eba\uff0c \u5982\u996e\u9187\u9152\u3002\u7389\u7f57\u5239\u8dd1\u5728\u524d\u5934\uff0c\u8dc3\u4e0a\u5c71\u58c1\uff0c\u6b63\u60f3\u53ec\u5524\u5de1\u903b\u5973\u5175\uff0c\u5ffd\u542c\u5f97\u5353\u4e00\u822a\u5728\u4e0b\u9762\u5c16\u53eb\u4e00 \u58f0\uff0c\u53cd\u8eab\u8dc3\u51fa\u5ce1\u8c37\u3002\u6b63\u662f\uff1a\u79bb\u5408\u51e0\u756a\u7591\u662f\u68a6\u83ab\u6559\u771f\u5883\u4e5f\u8ff7\u79bb\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206 \u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377\u2014\u2014\u7b2c\u5341\u4e5d\u56de\u3000\u5b7d\u503a\u96be\u507f\u3000\u95ee\u82b1\u82b1\u4e0d\u8bed\u3000\u524d\u7f18\u672a\u8bc1\u3000\u5bf9\u6708\u6708\u65e0\u8a00 \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377 \u7b2c\u5341\u4e5d\u56de \u5b7d\u503a\u96be\u507f\u3000\u95ee\u82b1\u82b1\u4e0d\u8bed \u524d\u7f18\u672a\u8bc1\u3000\u5bf9\u6708\u6708\u65e0\u8a00 \u3000\u3000\u7389\u7f57\u5239\u8eab\u5f62\u4e00\u8d77\uff0c\u98de\u71d5\u822c\u75be\u63a0\u800c\u4e0b\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u9ebd\u4e8b\u60c5\uff1f\u201d\u5353\u4e00\u822a\u521a\u521a\u5954\u5230\u8c37\u53e3\uff0c\u7389\u7f57 \u5239\u5df2\u5230\u8eab\u65c1\u3002\u5353\u4e00\u822a\u8df3\u4e0a\u4e00\u5757\u5ca9\u767e\u9053\uff1a\u201c\u6211\u4f3c\u4e4e\u77a7\u89c1\u6709\u4eba\uff0c\u500f\u53c8\u4e0d\u89c1\uff0c\u5728\u5ce1\u8c37\u91cc\u77a7\u4e0d\u6e05\u695a\uff0c \u4f60\u4e0a\u6765\u770b\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8c01\u6562\u5230\u6b64\uff1f\u201d\u8df3\u4e0a\u5ca9\u77f3\uff0c\u56db\u9762\u4e86\u671b\uff0c\u4e0d\u89c1\u4eba\u8ff9\uff0c\u7b11\u9053\uff1a\u201c\u660e\u6708\u5ce1 \u5f62\u52bf\u6781\u9669\uff0c\u654c\u4eba\u82e5\u6562\u5355\u8eab\u5230\u6b64\uff0c\u90a3\u5c31\u662f\u9001\u6b7b\u6765\u4e86\uff0c\u83ab\u975e\u662f\u4f60\u773c\u82b1\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u8df3\u4e0a \u5c71\u58c1\u4e4b\u65f6\uff0c\u6211\u5076\u7136\u5916\u671b\uff0c\u2026\u2026\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7389\u7f57\u5239\u5ffd\u7136\u628a\u624b\u4e00\u626c\uff0c\u4e00\u7247\u94f6\u5149\u707f\u70c2\uff0c\u5411\u4e71\u8349\u4e4b \u4e2d\u63b7\u53bb\uff0c\u539f\u6765\u7389\u7f57\u5239\u8033\u806a\u76ee\u7075\uff0c\u53ea\u4e00\u77a5\u773c\u5df2\u53d1\u89c9\u6709\u4eba\u7aa5\u4f3a\u5728\u4fa7\uff0c\u6545\u4f5c\u6beb\u65e0\u9632\u5907\uff0c\u50b2\u6162\u5730\u8bf4\u51fa \u8f7b\u654c\u4e4b\u8a00\uff0c\u5206\u5176\u5fc3\u5fd7\uff0c\u7136\u5f8c\u7a81\u7136\u51fa\u624b\uff0c\u5c06\u72ec\u95e8\u6697\u5668\u5b9a\u5f62\u9488\uff0c\u6e14\u7fc1\u6492\u7f51\u822c\u5411\u654c\u4eba\u75be\u6492\uff0c\u5fc3\u60f3\uff1a \u4f60\u7eb5\u662f\u9876\u513f\u5c16\u513f\u7684\u89d2\u8272\uff0c\u4e5f\u96be\u9003\u6211\u8fd9\u98de\u9488\u523a\u4f53\u4e4b\u707e \u3000\u3000\u90a3\u6599\u98de\u9488\u6492\u5904\uff0c\u4e00\u7247\u7e41\u97f3\u5bc6\u54cd\u58f0\u4e2d\uff0c\u8346\u68d8\u8349\u4e1b\u91cc\u7a81\u7136\u8df3\u8d77\u4e00\u4eba\uff0c\u7389\u7f57\u5239\u773c\u775b\u4e00\u4eae\uff0c\u7a81\u89c1 \u4e00\u6735\u5927\u7ea2\u82b1\u5728\u773c\u524d\u4e00\u6643\uff0c\u6765\u4eba\u73b0\u51fa\u8eab\u5f62\uff0c\u7adf\u662f\u7ea2\u82b1\u9b3c\u6bcd\u516c\u5b59\u5927\u5a18\uff01 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e00\u522b\u53c1\u5e74\uff0c\u4f60\u51fa\u624b\u8d8a\u6765\u8d8a\u8fa3\u4e86\uff01\u53ea\u662f\u4f60\u5982\u6b64\u63a5\u5f85\u5ba2\u4eba\uff0c\u5c82\u975e\u592a\u8fc7 \u4efd\u9ebd\uff1f\u201d\u9f99\u5934\u62d0\u6756\u987f\u5730\u6709\u58f0\uff0c\u7b11\u5f97\u9b13\u8fb9\u7684\u5927\u7ea2\u82b1\u5728\u6653\u98ce\u4e2d\u4e71\u98a4\uff01 \u3000\u3000\u7389\u7f57\u5239\u5403\u4e86\u4e00\u60ca\uff0c\u968f\u5373\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\uff01\u4f60\u653e\u7740\u4f60\u90a3\u8d3c\u6c49\u5b50\u4e0d\u52a0\u7ba1\u675f\uff0c\u5230\u6b64\u4f55\u4e3a\uff1f\u96be \u9053\u662f\u60f3\u4e0e\u6211\u518d\u6bd4\u4e00\u573a\u9ebd\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u5ffd\u5e84\u5bb9\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u8981\u6bd4\uff0c\u90a3\u5c31\u5168\u770b\u4f60\u4e86\uff01\u201d\u5353\u4e00\u822a\u6025 \u9053\uff1a\u201c\u516c\u5b59\u5927\u5a18\uff0c\u4f60\u662f\u6b66\u6797\u524d\u8f88\uff0c\u4e00\u8bfa\u5343\u91d1\uff0c\u53c1\u5e74\u524d\u4e4b\u7ea6\u96be\u9053\u5c31\u5fd8\u8bb0\u4e86\u5417\uff1f\u600e\u9ebd\u53c8\u63d0\u8d77\u6bd4\u8bd5 \u4e4b\u4e8b\uff1f\u201d \u3000\u3000\u516c\u5b59\u5927\u5a18\u9053\uff1a\u201c\u6211\u6b64\u6765\u4e3a\u7684\u6b63\u662f\u53c1\u5e74\u524d\u4e4b\u7ea6\uff0c\u7389\u7f57\u5239\u6211\u6765\u5411\u4f60\u6c42\u60c5\u4e86\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a \u201c\u4e0d\u6562\uff01\u4f60\u6311\u660e\u201c\u660e\u767d\u76f4\u8bf4\u4e4b\u610f\u201d\uff0c\u5212\u9053\u513f\u201c\u4f60\u610f\u6b32\u5982\u4f55\u5c3d\u7ba1\u5b9a\u4e0b\u529e\u6cd5\u4e4b\u610f\u201d\u5429\u5490\u4e0b\u6765 \u5427\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u90a3\u8d3c\u6c49\u5b50\u662f\u5077\u5077\u6e9c\u51fa\u5bb6\u4e86\uff0c\u4f46\u4ed6\u51fa\u6765\u4e0d\u8fc7\u51e0\u5929\uff0c\u6211\u77e5\u9053\u4ed6\u672a \u505a\u8fc7\u6076\u4e8b\uff0c\u8bf7\u4f60\u624b\u4e0b\u7559\u60c5\uff0c\u5c06\u4ed6\u4ea4\u56de\u4e0e\u6211\uff01\u6211\u4fdd\u4ed6\u4ee5\u5f8c\u4e0d\u518d\u4e0e\u4f60\u4e3a\u9e21\uff01\u201d\u539f\u6765\u516c\u5b59\u5927\u5a18\u53d1\u73b0 \u4e08\u592b\u5077\u8d70\u4e4b\u5f8c\uff0c\u7acb\u5373\u8ffd\u8e2a\uff0c\u5728\u5e7f\u5143\u57ce\u5916\u78b0\u89c1\u8d25\u9003\u7684\u6155\u5bb9\u51b2\uff0c\u6155\u5bb9\u51b2\u8bf3\u5979\u8bf4\uff1a\u201c\u5c0a\u592b\u5df2\u88ab\u7389\u7f57 \u5239\u6349\u53bb\u4e86\u3002\u4f60\u8981\u8ba8\u4eba\u5230\u660e\u6708\u5ce1\u5411\u7389\u7f57\u5239\u8ba8\u53bb\u3002\u5979\u5728\u90a3\u91cc\u505a\u5c71\u5927\u738b\u5462\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u4fe1\u4ee5\u4e3a\u771f\uff0c \u6551\u592b\u5fc3\u5207\uff0c\u7adf\u7136\u4e0d\u95ee\u9752\u7ea2\u7682\u767d\uff0c\u771f\u7684\u4e00\u53e3\u6c14\u8d76\u5230\u660e\u6708\u5ce1\u6765\u5411\u7389\u7f57\u5239\u8981\u4eba\u4e86\u3002 \u3000\u3000\u7389\u7f57\u5239\u542c\u4e86\u7ea2\u82b1\u9b3c\u6bcd\u9053\u51fa\u6765\u610f\u4e4b\u5f8c\uff0c\u5148\u662f\u54c8\u54c8\u4e00\u7b11\uff0c\u7ee7\u800c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u7684\u8d3c\u6c49\u5b50\u4e0d\u5728 \u8fd9\u513f\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u6155\u5bb9\u51b2\u5c82\u6562\u9a97\u6211\uff1f\u201d\u7389\u7f57\u5239\u62b1\u5251\u5f53\u80f8\uff0c\u5e76\u4e0d\u7b54\u8bdd\uff0c\u563f\u563f\u51b7\u7b11\u3002\u7ea2\u82b1 \u9b3c\u6bcd\u6012\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u7b11\u4f60\u6eba\u7231\u4e0d\u660e\uff0c\u7b11\u4f60\u597d\u574f\u4e0d\u5206\u3002\u4f60\u90a3\u8d3c\u6c49\u5b50\u662f\u4f55\u7b49 \u6837\u4eba\uff1f\u4f60\u96be\u9053\u8fd8\u4e0d\u6653\u5f97\uff0c\u4ed6\u6e9c\u4e86\u51fa\u6765\uff0c\u5c82\u6709\u4e0d\u4f5c\u574f\u4e8b\u4e4b\u7406\uff0c\u5c31\u5728\u4e00\u4e2a\u66f4\u6b21\u4e4b\u524d\uff0c\u4ed6\u8fd8\u548c\u6155\u5bb9 \u51b2\u4e00\u9053\uff0c\u653b\u6253\u6e05\u865a\u89c2\uff0c\u8981\u6349\u718a\u7ecf\u7565\u7684\u53c2\u8d5e\u5cb3\u545c\u73c2\u3002\u8fd9\u4e0d\u7b97\u505a\u574f\u4e8b\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u63a5\u53e3\u8bf4\u9053\uff1a \u201c\u53ef\u601c\u718a\u7ecf\u7565\u7ed9\u5978\u9609\u5bb3\u6b7b\uff0c\u4f20\u9996\u4e5d\u8fb9\uff0c\u51a4\u6c89\u6d77\u5e95\uff0c\u4ed6\u4eec\u8fd8\u4e0d\u80af\u653e\u8fc7\uff0c\u8fd8\u8981\u9773\u8349\u9664\u6839\uff0c\u4ed6\u4eec\u77e5 \u9053\u5cb3\u9e23\u73c2\u8eab\u4e0a\u6709\u718a\u7ecf\u7565\u7684\u9057\u4e66\uff0c\u5c31\u4e0d\u60dc\u4e07\u91cc\u8ffd\u8e2a\uff0c\u52a1\u5fc5\u8981\u53bb\u4e4b\u800c\u5f8c\u5feb\uff01\u4ed6\u4eec\u6bc1\u4e86\u56fd\u5bb6\u7684\u4e07\u91cc \u957f\u57ce\uff0c\u8fd8\u8981\u5c06\u718a\u7ecf\u7565\u6240\u7740\u7684\u5236\u654c\u4e4b\u4e66\uff0c\u641c\u53bb\u732e\u5a9a\u5916\u654c\uff01\u516c\u5b59\u5927\u5a18\u524d\u8f88\uff0c\u8bf7\u95ee\u8fd9\u662f\u4e0d\u662f\u4eba\u5929\u5171 \u6124\u4e4b\u4e8b\uff1f\u201d\u516c\u5b59\u5927\u5a18\u548c\u7389\u7f57\u5239\u90fd\u8fd8\u672a\u77e5\u718a\u7ecf\u7565\u906d\u60e8\u6b7b\u4e4b\u4e8b\uff0c\u95fb\u8a00\u5403\u4e86\u4e00\u60ca\uff0c\u90fd\u9053\uff1a\u201c\u8fd9\u6d88\u606f \u662f\u771f\u7684\u5417\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5982\u4f55\u4e0d\u771f\uff1f\u718a\u7ecf\u7565\u7684\u9057\u4e66\u5c31\u5728\u6211\u8fd9\u513f\uff0c\u516c\u5b59\u5927\u5a18\u4f60\u82e5\u60f3\u52a9\u5c0a\u592b\u5f97 \u5978\u9609\u4e4b\u5ba0\uff0c\u730e\u53d6\u8363\u534e\uff0c\u6211\u4fbf\u5c06\u6b64\u4e66\u4e0e\u4f60\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u547c\u7684\u4e00\u6756\uff0c\u5c06\u4e00\u5757\u5ca9\u77f3\u6253\u5f97\u77f3\u5c51\u7eb7\u98de\uff0c \u6012\u9053\uff1a\u201c\u4f60\u5f53\u6211\u662f\u4f55\u7b49\u6837\u4eba\uff1f\u82e5\u4f60\u4eec\u6240\u8bf4\u662f\u771f\uff0c\u6211\u90a3\u8d3c\u6c49\u5b50\u4efb\u7531\u4f60\u4eec\u6740\u5250\uff0c\u82e5\u7136\u4f60\u4eec\u6709\u534a\u53e5 \u865a\u8a00\uff0c\u563f\u563f\uff0c\u7389\u7f57\u5239\uff0c\u90a3\u6211\u53ef\u8981\u548c\u4f60\u518d\u51b3\u4e2a\u80dc\u8d1f\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u5c3d\u7ba1\u518d\u53bb\u67e5\uff0c\u54c8\uff0c\u4f60\u4fe1 \u6155\u5bb9\u51b2\u7684\u8bdd\uff0c\u4e0d\u4fe1\u6211\u7684\u8bdd\uff0c\u4f60\u67e5\u660e\u4e4b\u5f8c\uff0c\u82e5\u4e0d\u5411\u6211\u966a\u7f6a\uff0c\u4f60\u4e0d\u627e\u6211\uff0c\u6211\u4e5f\u8981\u627e\u4f60\u51b3\u4e2a\u80dc\u8d1f \u5462\uff01\u8c01\u8fd8\u6015\u4f60\u4e0d\u6210\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u6ee1\u8179\u72d0\u7591\uff0c\u5fc3\u9053\uff1a\u6211\u4e14\u627e\u6155\u5bb9\u51b2\u6765\u548c\u5979\u5bf9\u8d28\uff0c\u63d0\u8d77\u62d0\u6756\uff0c\u98de \u8eab\u5954\u51fa\u5c71\u8c37\u3002 \u3000\u3000\u7389\u7f57\u5239\u5401\u4e86\u53e3\u6c14\uff0c\u773c\u6cea\u6ef4\u4e86\u51fa\u6765\uff0c\u6f78\u7136\u8bf4\u9053\uff1a\u201c\u718a\u5ef7\u5f3c\u662f\u4e2a\u597d\u4eba\uff0c\u8fd9\u6837\u60e8\u6b7b\uff0c\u771f\u771f\u53ef \u501f\uff01\u201d\u5353\u4e00\u822a\u5174\u7389\u7f57\u5239\u76f8\u8bc6\u4ee5\u6765\uff0c\u4ece\u672a\u89c1\u5979\u54ed\u8fc7\uff0c\u77e5\u5979\u5fc3\u4e2d\u5b9a\u662f\u975e\u5e38\u60b2\u75db\u3002\u7389\u7f57\u5239\u4ee5\u8896\u63e9 \u6cea\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u5c0f\u95ef\u738b\u4e4b\u8a00\u4e0d\u9519\uff0c\u8981\u9760\u671d\u5ef7\u62b5\u5fa1\u5916\u5bc7\uff0c\u90a3\u6bd4\u76fc\u65e5\u5934\u4ece\u897f\u8fb9\u51fa\u8fd8\u96be\uff01\u201d\u5353\u4e00 \u822a\u9053\uff1a\u201c\u8c01\u4e2a\u5c0f\u95ef\u738b\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u90a3\u662f\u4e00\u4f4d\u9876\u5929\u7acb\u5730\u7684\u82f1\u96c4\uff0c\u5c06\u6765\u4ee3\u66ff\u672b\u660e\u800c\u6709\u5929\u4e0b\uff0c \u6211\u770b\u5c31\u662f\u4ed6\u4e86\uff01\u201d\u5353\u4e00\u822a\u4ece\u672a\u542c\u8fc7\u7389\u7f57\u5239\u8fd9\u6837\u79f0\u8d5e\u522b\u4eba\uff0c\u4e0d\u7981\u5927\u4e3a\u60ca\u5947\uff01\u7389\u7f57\u5239\u5ffd\u53c8\u8bf4\u9053\uff1a \u201c\u718a\u5ef7\u5f3c\u4e4b\u6b7b\u56fa\u7136\u53ef\u54c0\uff0c\u4f46\u4e5f\u4e0d\u89c1\u5f97\u9664\u4e86\u4ed6\u4fbf\u65e0\u4eba\u80fd\u5fa1\u5916\u5bc7\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u542c\u201c\u5c0f\u95ef\u738b\u201d \u8fd9\u4e2a\u7ef0\u53f7\uff0c\u60f3\u5fc5\u53c8\u662f\u4e00\u4f4d\u7eff\u6797\u82f1\u96c4\u4e86\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u5353\u4e00\u822a\u9ed8\u7136\u65e0\u8bed\uff0c\u534a\u664c\u5ffd \u9053\uff1a\u201c\u73b0\u4eca\u671d\u5ef7\u5927\u519b\u4e91\u96c6\u897f\u5317\uff0c\u9655\u897f\u53c1\u5341\u516d\u70df\u5c18\u5168\u90fd\u626b\u706d\uff0c\u4f60\u4f55\u82e6\u8fd8\u5728\u7eff\u6797\u6df7\uff1f\u201d\u7389\u7f57\u5239\u7709 \u5934\u4e00\u76b1\uff0c\u5ffd\u53c8\u5c55\u7709\u7b11\u9053\uff1a\u201c\u6211\u548c\u4f60\u53c1\u5e74\u4e0d\u89c1\uff0c\u4e00\u89c1\u9762\u4e14\u5148\u522b\u4e89\u8bba\u5427\u3002\u201d\u64ae\u5507\u4e00\u5578\uff0c\u53ec\u5524\u5de1\u903b \u5973\u5175\uff0c\u5973\u5175\u51fa\u6765\u8fce\u63a5\uff0c\u7389\u7f57\u5239\u4e0e\u5353\u4e00\u822a\u767b\u4e0a\u9ad8\u5c71\uff0c\u7ed5\u7740\u4ed9\u5be8\u5de1\u89c6\u4e00\u5468\uff0c\u5353\u4e00\u822a\u89c1\u5c71\u5be8\u867d\u5c0f\uff0c \u5374\u662f\u4f9d\u7740\u9669\u8981\u7684\u5f62\u52bf\u5efa\uff0c\u5e03\u7f6e\u5f97\u751a\u4e3a\u4e25\u5bc6\uff0c\u5c71\u4e0a\u5947\u5cf0\u7a81\u51fa\uff0c\u72b9\u5982\u4e00\u5934\u731b\u864e\uff0c\u5f20\u7740\u5927\u5634\uff0c\u5bf9\u7740 \u4e0b\u9762\u7684\u5ce1\u8c37\uff0c\u5353\u4e00\u822a\u5fc3\u9053\uff1a\u8fd9\u91cc\u771f\u77e5\u4e16\u5916\u6843\u6e90\uff0c\u6599\u60f3\u5b98\u519b\u6781\u96be\u653b\u5165\u3002 \u3000\u3000\u8fd9\u65f6\u671d\u65e5\u65b9\u5347\uff0c\u5f69\u971e\u8000\u773c\uff0c\u4fef\u89c6\u5c71\u8c37\uff0c\u90c1\u90c1\u82cd\u82cd\uff0c\u6df1\u5e7d\u96be\u6d4b\uff1a\u4ef0\u89c6\u5cf0\u5dc5\uff0c\u5219\u4e91\u6c14\u5f25\u6f2b\uff0c \u8ff7\u79bb\u53d8\u5e7b\u3002\u7389\u7f57\u5239\u5438\u4e86\u4e00\u53e3\u6653\u98ce\uff0c\u60c5\u601d\u60d8\u60d8\uff0c\u643a\u7740\u5353\u4e00\u822a\u7684\u624b\uff0c\u6084\u7136\u95ee\u9053\uff1a\u201c\u4f60\u771f\u7684\u8981\u56de\u6b66 \u5f53\u51fa\u53bb\u5f53\u4ec0\u9ebd\u635e\u4ec0\u5b50\u7684\u638c\u95e8\u5417\uff1f\u201d\u5353\u4e00\u822a\u5fc3\u9b42\u4e00\u6c64\uff0c\u9053\uff1a\u201c\u5e08\u95e8\u6069\u91cd\uff0c\u6211\u867d\u4e0d\u6b32\u4e3a\u4ea6\u8981\u52c9\u529b \u4e3a\u4e4b\u4e86\u3002\u201d\u7389\u7f57\u5239\u5657\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u62a5\u6069\u4e5f\u4e0d\u4e00\u5b9a\u8981\u505a\u638c\u95e8\u5440\uff0c\u6bd4\u5982\uff0c\u6bd4\u5982\u2026\u2026\u201d\u5353\u4e00\u822a \u9053\uff1a\u201c\u6bd4\u5982\u4ec0\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6bd4\u5982\u4f60\u627e\u5230\u4e00\u4f4d\u6b66\u6797\u4e2d\u5fd7\u540c\u9053\u5408\u7684\u670b\u53cb\uff0c\u7ed3\u5e90\u540d\u5c71\uff0c\u7cbe\u7814 \u6b66\u5b66\u3002\u5230\u4ed6\u65e5\u6709\u6240\u6210\u5c31\uff0c\u771f\u80fd\u4e3a\u4f60\u4eec\u6b66\u5f53\u6d3e\u653e\u4e00\u5f02\u5f69\uff0c\u5c82\u4e0d\u4e5f\u662f\u62a5\u5e08\u6069\u4e4b\u4e00\u6cd5\uff1f\u8bf7\u4f60\u6055\u6211\u76f4 \u8a00\uff0c\u6b66\u5f53\u6d3e\u867d\u7136\u540d\u91cd\u5929\u4e0b\uff0c\u4f46\u4f60\u4eec\u524d\u8f88\u7684\u8fbe\u6469\u5251\u6cd5\u5931\u4f20\uff0c\u76f4\u5230\u5982\u4eca\u5374\u8fd8\u672a\u6709\u60ca\u4eba\u7edd\u827a\uff0c\u8db3\u4ee5 \u670d\u4e16\u4f20\u4eba\u7684\u5462\uff01\u865a\u58f0\u4e0d\u80fd\u4e45\u6043\uff0c\u4f60\u5373\u7b97\u4e3a\u6b66\u5f53\u6d3e\u7740\u60f3\uff0c\u4e5f\u8be5\u5728\u6b66\u5b66\u7684\u63a2\u8ba8\u4e0a\uff0c\u597d\u597d\u505a\u4e00\u756a\u529f \u592b\u3002\u201d\u5353\u4e00\u822a\u542c\u4e86\uff0c\u601d\u6f6e\u6d6a\u6d8c\uff0c\u611f\u89e6\u9891\u751f\u3002\u9996\u5148\u611f\u5230\u7684\u662f\uff1a\u8fd9\u4e00\u756a\u8bdd\u4e0d\u662f\u7389\u7f57\u5239\u7b2c\u4e8c\u4eba\u4e5f\u4e0d \u4f1a\u8bf4\u3002\u81ea\u7d2b\u9633\u9053\u957f\u6b7b\u5f8c\uff0c\u6b66\u5f53\u6d3e\u786e\u5982\u65e5\u8fc7\u4e2d\u5929\uff0c\u773c\u770b\u5c31\u8981\u7531\u76db\u800c\u8870\u7684\u4e86\u3002\u53d1\u626c\u4e0e\u91cd\u632f\u672c\u95e8\u7684 \u6b66\u5b66\uff0c\u8d23\u4efb\u7684\u786e\u662f\u4e0d\u5bb9\u65c1\u8d37\u3002\u7ee7\u800c\u60f3\u9053\uff1a\u7389\u7f57\u5239\u592a\u8fc7\u7740\u91cd\u6b66\u529f\uff0c\u5374\u5ffd\u7565\u4e86\u4ee5\u5fb7\u670d\u4eba\uff0c\u8fd9\u4e5f\u7edd \u975e\u9886\u8896\u6b66\u6797\u4e4b\u9053\u3002\u518d\u800c\u60f3\u9053\uff1a\u7389\u7f57\u5239\u8fd9\u756a\u8bdd\u7684\u610f\u601d\uff0c\u660e\u660e\u662f\u60f3\u4e0e\u6211\u7ed3\u4e3a\u795e\u4ed9\u4f34\u4fa3\uff0c\u5408\u85c9\u53cc \u4fee\uff0c\u540c\u7814\u6b66\u529f\uff0c\u5bfb\u5e7d\u63a2\u79d8\u3002\u6211\u4e0e\u5979\u82e5\u5171\u540c\u63a2\u8ba8\uff0c\u4ee5\u6211\u6d3e\u6b63\u5bb6\u7684\u7384\u95e8\u5185\u529f\uff0c\u914d\u5408\u5979\u5999\u7edd\u5929\u4e0b\u7684 \u5251\u6cd5\uff0c\u5404\u91c7\u6240\u957f\uff0c\u9884\u6599\u5fc5\u80fd\u4e3a\u6b66\u5b66\u5927\u653e\u5f02\u5f69\u3002\u4f55\u51b5\u5979\u4e0d\u4f46\u6b66\u529f\u5353\u7edd\uff0c\u800c\u4e14\u7f8e\u82e5\u5929\u4eba\uff0c\u82e5\u5f97\u4e0e \u5979\u540c\u5055\u767d\u9996\uff0c\u771f\u662f\u51e0\u751f\u4fee\u5230\uff1f\u7ec8\u65bc\u5728\u5fc3\u91cc\u53f9\u4e86\u53e3\u6c14\uff0c\u6697\u9053\uff1a\u6015\u53ea\u6015\u60c5\u5929\u6613\u7f3a\uff0c\u597d\u68a6\u96be\u5706\uff0c\u770b \u6765\u8fd9\u4e5f\u53ea\u662f\u4e00\u573a\u6625\u68a6\u800c\u5df2\uff01\u51e0\u4f4d\u5e08\u53d4\u90fd\u628a\u5979\u5f53\u6210\u672c\u95e8\u516c\u654c\uff0c\u9664\u975e\u6211\u8df3\u51fa\u6b66\u5f53\u95e8\u6237\uff0c\u5426\u5219\u6b32\u8981 \u4e0e\u5979\u7ed3\u5408\uff0c\u90a3\u662f\u4e07\u4e07\u4e0d\u80fd\uff01\u4f55\u51b5\u6211\u662f\u5c61\u4ee3\u4e66\u9999\u4e4b\u5f8c\uff0c\u7236\u5e08\u9057\u8bad\uff0c\u4e5f\u7edd\u4e0d\u80fd\u4e0e\u7eff\u6797\u4e2d\u7684\u5973\u9b54\u5934 \u7ed3\u5408\u3002\u5440\uff0c\u771f\u662f\u8f9c\u8d1f\u5979\u5982\u82b1\u7f8e\u8c8c\uff0c\u53ef\u601c\u6211\u798f\u8584\u7f18\u60ad\uff0c\u4e0e\u7389\u7f57\u5239\u767d\u5934\u5055\u8001\u4e4b\u68a6\uff0c\u53ea\u6050\u4eca\u751f\u662f\u65e0 \u671b\u7684\u4e86\uff01 \u3000\u3000\u7389\u7f57\u5239\u89c1\u4ed6\u5782\u9996\u6c89\u601d\uff0c\u4e45\u4e45\u4e0d\u8bed\uff1a\u90a3\u77e5\u4ed6\u7684\u5fc3\u4e2d\u6b63\u5982\u5927\u6d77\u6f6e\u7ffb\uff0c\u5df2\u6d8c\u8fc7\u597d\u51e0\u91cd\u601d\u60f3\u7684\u6ce2 \u6d6a\uff01\u7389\u7f57\u5239\u4f4e\u7709\u4e00\u7b11\uff0c\u7275\u7740\u4ed6\u7684\u624b\u95ee\u9053\uff1a\u201c\u50bb\u5b69\u5b50\uff0c\u4f60\u60f3\u4e9b\u4ec0\u9ebd\u5440\uff01\u201d\u5353\u4e00\u822a\u62ac\u8d77\u4e86\u5934\uff0c\u5450 \u5450\u8bf4\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u6211\u4f55\u5c1d\u4e0d\u60f3\u5f97\u4e00\u77e5\u5df2\uff0c\u7ed3\u5e90\u540d\u5c71\uff0c\u53ea\u662f\uff0c\u53ea\u662f\u2026\u2026\u201d\u7389\u7f57\u5239\u9041\uff1a\u201c\u53ea\u662f \u4ec0\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u5fc3\u4e2d\u4e00\u9178\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u8fd8\u662f\u8fc7\u51e0\u5e74\u518d\u8bf4\u5427\uff01\u201d\u7389\u7f57\u5239\u597d\u751f\u5931\u671b\uff0c\u968f\u624b\u6458\u4e0b \u4e00\u6735\u5c71\u8c37\u4e0a\u7684\u91ce\u82b1\uff0c\u9ed8\u7136\u65e0\u8bed\uff0c\u5353\u4e00\u822a\u642d\u8baa\u7b11\u9053\uff1a\u201c\u8fd9\u82b1\u771f\u7f8e\uff0c\u55ef\uff0c\u6211\u8bf4\u9519\u5566\uff0c\u59d0\u59d0\uff0c\u4f60\u6bd4 \u8fd9\u82b1\u8fd8\u7f8e\uff01\u201d\u7389\u7f57\u5239\u51c4\u7136\u4e00\u7b11\uff0c\u628a\u82b1\u63b7\u4e0b\u5c71\u8c37\uff0c\u9053\uff1a\u201c\u8fd9\u6735\u82b1\u867d\u7136\u597d\u770b\uff0c\u4f46\u6625\u5149\u4e00\u53bb\uff0c\u82b1\u4fbf \u98d8\u96f6\uff0c\u4e0d\u8fc7\u597d\u82b1\u8c22\u4e86\uff0c\u660e\u5e74\u8fd8\u53ef\u91cd\u5f00\uff1a\u4eba\u5462\uff0c\u8fc7\u4e86\u51e0\u5e74\uff0c\u518d\u8fc7\u51e0\u5e74\uff0c\u53c8\u8fc7\u51e0\u5e74\uff0c\u90a3\u65f6\u767d\u53d1\u6ee1 \u5934\uff0c\u591a\u7f8e\u4e5f\u8981\u53d8\u6210\u4e11\u602a\u4e86\uff01\u201d\u5353\u4e00\u822a\u5fc3\u795e\u52a8\u6c64\uff0c\u77e5\u5979\u6b64\u8a00\u6b63\u662f\u4e3a\u81ea\u5df1\u6240\u8bf4\u7684\u201c\u518d\u7b49\u51e0\u5e74\u201d\u800c \u53d1\uff0c\u60f3\u8d77\u201c\u5982\u82b1\u7f8e\u7737\uff0c\u4f3c\u6c34\u6d41\u5e74\u3002\u201d\u8fd9\u4e24\u53e5\u8bdd\uff0c\u4e0d\u89c9\u60b2\u4ece\u4e2d\u6765\uff0c\u96be\u4ee5\u65ad\u7edd\uff01 \u3000\u3000\u7389\u7f57\u5239\u89c1\u4ed6\u773c\u89d2\u9690\u6709\u6cea\u73e0\uff0c\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u50bb\u5b69\u5b50\uff0c\u4e8b\u5728\u4eba\u4e3a\uff0c\u54ed\u4ec0\u9ebd\u5462\uff1f\u201d\u6328\u8fc7\u8eab\u6765\uff0c \u5353\u4e00\u822a\u95fb\u5f97\u7f15\u7f15\u5e7d\u9999\uff0c\u6c81\u4eba\u6b32\u9189\uff0c\u51e0\u4e4e\u6309\u637a\u4e0d\u4f4f\uff0c\u6b32\u628a\u5fc3\u6000\u5256\u8bc9\uff0c\u8ff7\u60d8\u4e4b\u4e2d\uff0c\u51e0\u4e2a\u5e08\u53d4\u7684\u5f71 \u5b50\uff0c\u9661\u7136\u4ece\u8111\u6d77\u4e2d\u63a0\u8fc7\uff0c\u5c24\u5176\u662f\u767d\u77f3\u9053\u4eba\uff0c\u66f4\u597d\u50cf\u77aa\u7740\u773c\u775b\u671b\u81ea\u5df1\u3002\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u987e \u4e00\u5207\uff0c\u4e0e\u7389\u7f57\u5239\u6210\u5a5a\uff0c\u80cc\u53db\u5e08\u95e8\u7684\u5e3d\u5b50\u5fc5\u7136\u88ab\u6234\u4e0a\u5934\u6765\uff0c\u90a3\u65f6\u6211\u8fd8\u6709\u4f55\u9762\u76ee\u89c1\u6b66\u6797\u540c\u9053\u3002\u201d \u7389\u7f57\u5239\u53c8\u63c9\u788e\u4e00\u6735\u91ce\u82b1\uff0c\u629b\u4e0b\u5c71\u8c37\uff0c\u5353\u4e00\u822a\u5446\u5446\u7684\u770b\u82b1\u7247\u5728\u98ce\u4e2d\u98d8\u843d\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u7ec3\u59d0 \u59d0\uff0c\u4f60\u7684\u5bb9\u989c\u5e94\u8be5\u50cf\u5f00\u4e0d\u8d25\u7684\u82b1\u6735\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u75f4\u4eba\u8bf4\u68a6\uff01\u666e\u5929\u4e4b\u4e0b\uff0c\u90a3\u6709\u9752\u6625\u957f\u9a7b \u4e4b\u4eba\uff1f\u6211\u8bf4\uff0c\u8001\u5929\u7237\u82e5\u7136\u50cf\u4eba\u4e00\u6837\uff0c\u601d\u591a\u8651\u591a\uff0c\u8001\u5929\u7237\u4e5f\u4f1a\u8001\u5462\uff01\u54b1\u4eec\u89c1\u4e00\u56de\u5435\u4e00\u56de\uff0c\u4e0b\u6b21 \u4f60\u518d\u89c1\u5230\u6211\u65f6\uff0c\u53ea\u6050\u6211\u5df2\u662f\u767d\u53d1\u6ee1\u5934\u7684\u8001\u5a46\u5a46\u4e86\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u7ed9\u5979\u8bf4\u5f97\u5fc3\u6f6e\u52a8\u6c64\uff0c\u60f3\u9053\uff1a\u201c\u7389\u7f57\u5239\u771f\u662f\u4e2a\u5927\u6709\u6167\u6839\u4e4b\u4eba\uff0c\u5979\u8bfb\u4e66\u4e0d\u591a\uff0c\u4e0d\u4f1a\u505a \u8bd7\uff0c\u4e5f\u4e0d\u4f1a\u586b\u8bcd\uff0c\u4f46\u4fe1\u53e3\u8bf4\u51fa\u6765\u7684\u8bdd\uff0c\u9664\u4e86\u6ca1\u6709\u534f\u5428\u4e4b\u5916\uff0c\u7b80\u76f4\u5c31\u662f\u7edd\u5999\u7684\u8bd7\u8bcd\u3002\u5b8b\u8bcd\u4e91\uff1a \u201c\u5929\u82e5\u6709\u60c5\u5929\u4ea6\u8001\uff0c\u6447\u6447\u5e7d\u6068\u96be\u7981\uff1a\u60c6\u6005\u65e7\u60c5\u5982\u68a6\uff0c\u9192\u6765\u65e0\u5904\u8ffd\u5bfb\uff01\u201d\u53c8\u6709\u53e5\u4e91\uff1a\u201c\u53f9\u51e0\u53e5 \u79bb\u5408\uff0c\u4fbf\u6210\u8fdf\u66ae\u3002\u201d\u5979\u8bf4\u7684\u8bdd\uff0c\u4e0d\u6b63\u5c31\u662f\u8fd9\u4e9b\u8bcd\u53e5\u7684\u6ce8\u91ca\uff1f\u800c\u4e14\u8bf4\u5f97\u6bd4\u8fd9\u4e9b\u8bcd\u53e5\u8fd8\u66f4\u660e\u767d\u52a8 \u4eba\u3002\u201d\u7389\u7f57\u5239\u53c8\u7b11\u9053\uff1a\u201c\u5230\u6211\u767d\u53d1\u6ee1\u5934\u4e4b\u65f6\uff0c\u53ea\u6050\u4f60\u8fde\u770b\u4e5f\u4e0d\u770b\u6211\u4e86\u3002\u201d\u5353\u4e00\u822a\u660e\u77e5\u7389\u7f57\u5239 \u7528\u8bdd\u6324\u8bdd\uff0c\u8981\u81ea\u5df1\u5410\u51fa\u771f\u60c5\uff0c\u53ef\u662f\u81ea\u5df1\u683c\u65bc\u5f62\u52bf\uff0c\u4e07\u96be\u7b54\u8986\uff0c\u53ea\u597d\u5f3a\u7b11\u4e3a\u6b22\uff0c\u628a\u8bdd\u62c9\u5f00\u53bb \u9053\uff1a\u201c\u5230\u4f60\u751f\u51fa\u767d\u53d1\uff0c\u6211\u5c31\u53bb\u6c42\u7075\u4e39\u5999\u836f\uff0c\u8ba9\u4f60\u6062\u590d\u9752\u6625\u3002\u201d\u7389\u7f57\u5239\u53f9\u4e86\u53e3\u6c14\uff0c\u60f3\u9053\uff1a\u201c\u522b \u4eba\u548c\u4f60\u8bf4\u6b63\u7ecf\u8bdd\u513f\uff0c\u4f60\u5374\u5c3d\u8c79\u73a9\u7b11\u3002\u201d\u5fc3\u5934\u4e00\u9178\uff0c\u628a\u8bdd\u5fcd\u4f4f\u3002\u62ac\u5934\u4e00\u671b\uff0c\u7ea2\u65e5\u5df2\u4e0a\u53c1\u7aff\uff0c\u7389 \u7f57\u5239\u5982\u5728\u68a6\u4e2d\u60a0\u7136\u9192\u8f6c\uff0c\u5ffd\u7136\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u54ce\uff0c\u65e5\u5934\u90fd\u8fd9\u9ebd\u9ad8\u4e86\uff0c\u600e\u9ebd\u73ca\u745a\u59b9\u59b9\u8fd8\u672a \u56de\u6765\uff1f\u201d\u5353\u4e00\u822a\u559c\u9053\uff1a\u201c\u94c1\u73ca\u745a\u4e5f\u5728\u8fd9\u91cc\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u70b9\u4e86\u70b9\u5934\u3002\u5353\u4e00\u822a\u9053\uff1a\u201c\u54b1\u4eec\u53eb\u5979\u548c \u9e23\u73c2\u5927\u54e5\u76f8\u89c1\uff0c\u545c\u73c2\u5927\u54e5\u81ea\u718a\u7ecf\u7565\u6b7b\u5f8c\uff0c\u5c31\u5fc3\u7070\u610f\u51b7\uff0c\u4e5f\u8be5\u6709\u4e2a\u4eba\u5b89\u6170\u5b89\u6170\u4ed6\u3002\u201d\u7389\u7f57\u5239\u5fc3 \u9053\uff1a\u201c\u4f60\u81ea\u5df2\u7684\u4e8b\u90fd\u7ba1\u4e0d\u4e86\uff0c\u5374\u5fd9\u7740\u7ba1\u522b\u4eba\u7684\u4e8b\uff01\u5cb3\u9e23\u73c2\u8981\u4eba\u5b89\u6170\uff0c\u6211\u53c8\u4f55\u5c1d\u4e0d\u8981\u4eba\u5b89 \u6170\uff1f\u201d\u4f46\u5979\u5bf9\u94c1\u73ca\u745a\u72b9\u5982\u59b9\u59b9\uff0c\u5173\u6000\u4e4b\u6781\uff0c\u95fb\u8a00\u751a\u559c\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u5cb3\u9e23\u73c2\u5462\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a \u201c\u6211\u4eec\u6628\u665a\u672c\u6765\u540c\u5e8a\u591c\u8bdd\uff0c\u5f8c\u6765\u542c\u5f97\u6155\u5bb9\u51b2\u5165\u89c2\u641c\u7d22\uff0c\u6211\u5c31\u548c\u4ed6\u76f8\u7ea6\uff0c\u53eb\u4ed6\u5148\u884c\u8bbe\u6cd5\u8131\u8eab\uff0c \u5f85\u90a3\u4e9b\u4eba\u53bb\u5f8c\uff0c\u518d\u56de\u6e05\u865a\u89c2\u548c\u6211\u76f8\u89c1\u3002\u60f3\u4e0d\u5230\u4f60\u968f\u5f8c\u5c31\u6765\uff0c\u4e00\u6765\u5c31\u5c06\u6211\u62c9\u5230\u8fd9\u91cc\u3002\u4ed6\u627e\u4e0d\u89c1 \u6211\u4e0d\u6253\u7d27\uff0c\u53ea\u6015\u6211\u7684\u5e08\u53d4\u4f1a\u8fc1\u6012\u65bc\u4ed6\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u4ee5\u524d\u9519\u602a\u4e86\u4ed6\uff0c\u4e0d\u77e5\u4ed6\u8fd8\u602a\u4e0d\u602a \u6211\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4ed6\u77e5\u9053\u94c1\u73ca\u745a\u5728\u4f60\u8fd9\u513f\uff0c\u800c\u4f60\u53c8\u662f\u8fd9\u6837\u70ed\u5fc3\u7684\u6708\u8001\uff0c\u4ed6\u559c\u6b22\u8fd8\u6765\u4e0d\u53ca \u5462\uff1f\u201d\u7389\u7f57\u5239\u60f3\u8d77\u4ee5\u524d\u505a\u5a92\u4e4b\u4e8b\uff0c\u9762\u4e0a\u4e00\u7ea2\u3002\u5be8\u4e2d\u5de1\u903b\u7684\u5973\u5175\u5de1\u5230\u5c71\u5f8c\uff0c\u89c1\u5934\u9886\u548c\u8fd9\u4e2a\u5c11\u5e74 \u5ba2\u4eba\u8c08\u5f97\u6b63\u6b22\uff0c\u8fdc\u8fdc\u907f\u5f00\u3002\u7389\u7f57\u5239\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u51e0\u4e2a\u4eba\u4e0b\u5c71\u63a5\u94c1\u5be8\u4e3b\u53bb\u5427\uff01\u201d \u3000\u3000\u5de1\u8fd8\u7684\u5973\u5175\u5e94\u58f0\u800c\u53bb\uff0c\u5353\u4e00\u822a\u9053\uff1a\u201c\u4e0d\u4f1a\u51fa\u4ec0\u9ebd\u4e8b\u5427\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u57ce\u4e2d\u7684\u5b98\u519b\u5df2\u5168 \u6570\u8986\u706d\uff0c\u62a2\u7cae\u7684\u6c11\u4e0d\u4e0b\u4e07\u4eba\uff0c\u5c31\u662f\u518d\u6765\u51e0\u5343\u5b98\u519b\u4e5f\u4e0d\u6d4e\u4e8b\u3002\u4f55\u51b5\u73ca\u745a\u59b9\u59b9\u8fd1\u5e74\u6b66\u529f\u7cbe\u8fdb\uff0c\u6599 \u60f3\u53ef\u4ee5\u5b89\u7136\u5f52\u6765\u3002\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u5230\u5e95\u62c5\u5fc3\uff0c\u548c\u5353\u4e00\u822a\u5230\u524d\u5c71\u773a\u671b\u3002 \u3000\u3000\u518d\u8bf4\u94c1\u73ca\u745a\u5e26\u9886\u6c11\uff0c\u72b9\u5982\u6d2a\u6c34\u51b2\u7834\u5824\u9632\uff0c\u628a\u57ce\u4e2d\u7684\u4e24\u5343\u5b98\u5175\uff0c\u6740\u5f97\u6b7b\u7684\u6b7b\u9003\u7684\u9003\uff0c\u5c06\u53bf \u8859\u4e5f\u4e00\u628a\u706b\u70e7\u4e86\uff0c\u6c11\u6253\u5f00\u7cae\u4ed3\uff0c\u53ea\u89c1\u5806\u5f97\u6ee1\u6ee1\u7684\uff0c\u5176\u4e2d\u8fd8\u6709\u597d\u51e0\u5e74\u524d\u7684\u9648\u7cae\uff0c\u6c11\u5927\u6124\uff0c\u5c06\u7cae \u62a2\u4e86\uff0c\u7136\u5f8c\u518d\u62a2\u57ce\u4e2d\u5927\u6237\uff0c\u95f9\u5230\u5929\u660e\uff0c\u6bcf\u4e2a\u6c11\u90fd\u62a2\u4e86\u4e00\u4e24\u888b\u7cae\u98df\u3002\u8fd9\u4e9b\u6c11\u58f0\u52bf\u867d\u7136\u6d69\u5927\uff0c\u5230 \u5e95\u4e0d\u662f\u6709\u7ec4\u7ec7\u6709\u8bad\u7ec3\u7684\u961f\u4f0d\uff0c\u62a2\u4e86\u7cae\u98df\uff0c\u5fc3\u6ee1\u610f\u8db3\uff0c\u547c\u5578\u56db\u6563\u3002\u94c1\u73ca\u745a\u5fc3\u60f3\uff0c\u53ef\u60dc\u7ec3\u59d0\u59d0\u53ea \u8981\u5973\u5175\uff0c\u8981\u4e0d\u7136\u628a\u8fd9\u4e9b\u6c11\u805a\u96c6\u8d77\u6765\uff0c\u7acb\u523b\u53ef\u6210\u4e00\u652f\u4e49\u519b\uff0c\u653b\u5360\u5dde\u5e9c\uff01\u5929\u4eae\u4e4b\u5f8c\uff0c\u6c11\u5341\u4e5d\u6563 \u4e86\uff0c\u94c1\u73ca\u745a\u96c6\u5408\u5e26\u6765\u7684\u5973\u5175\uff0c\u5e78\u559c\u5e76\u65e0\u4f24\u635f\uff0c\u4e5f\u4fbf\u51fa\u57ce\u56de\u5c71\u3002 \u3000\u3000\u518d\u8bf4\u6155\u5bb9\u51b2\u5728\u6e05\u865a\u89c2\u5927\u8d25\u4e4b\u5f8c\uff0c\u4e00\u70b9\u53d7\u4f24\u7684\u4e1c\u5382\u536b\u58eb\uff0c\u53ea\u88ab\u7389\u7f57\u5239\u7528\u5251\u523a\u4f24\u5173\u8282\u7a74\u9053\u7684 \u4fbf\u6709\u5341\u4e8c\u4eba\uff0c\u518d\u52a0\u4e0a\u88ab\u6b66\u5f53\u6d3e\u6253\u4f24\u7684\uff0c\u603b\u5171\u4e0d\u4e0b\u4e8c\u5341\u540d\u4e4b\u591a\uff0c\u6ca1\u4f24\u7684\u53ea\u6709\u5341\u4e94\u516d\u4eba\uff0c\u6155\u5bb9\u51b2 \u5927\u4e3a\u4e27\u6c14\uff0c\u53eb\u6ca1\u4f24\u7684\u4eba\uff0c\u6bcf\u4eba\u80cc\u8d77\u4e00\u540d\u4f24\u5458\uff0c\u51e0\u540d\u8f7b\u4f24\u7684\u5219\u4e92\u76f8\u6276\u6301\uff0c\u6478\u4e0b\u5c71\u53bb\u3002 \u3000\u3000\u90a3\u65f6\u6b63\u662f\u6c11\u5728\u57ce\u4e2d\u5927\u95f9\uff0c\u711a\u53bf\u8859\uff0c\u62a2\u7cae\u4ed3\u4e4b\u9645\uff0c\u6155\u5bb9\u51b2\u89c1\u57ce\u4e2d\u706b\u52bf\u6b63\u76db\uff0c\u4e0d\u6562\u56de\u5230\u5e02 \u533a\uff0c\u4ece\u6e05\u865a\u89c2\u80cc\u9762\u7ffb\u4e0b\u5c71\u5761\uff0c\u5728\u5c71\u8fb9\u7684\u4e1b\u6797\u4e2d\u6b47\u606f\uff0c\u770b\u770b\u4e1c\u65b9\u6e10\u4eae\uff0c\u57ce\u4e2d\u6740\u4e4b\u58f0\u6e10\u5f31\uff0c\u6b63\u60f3 \u6d3e\u4eba\u4eba\u57ce\u63a2\u542c\uff0c\u5ffd\u542c\u5f97\u6709\u545c\u545c\u54cd\u7bad\u4e4b\u58f0\uff0c\u53c1\u957f\u4e24\u77ed\uff0c\u6155\u5bb9\u51b2\u559c\u9053\uff1a\u201c\u597d\u5440\uff0c\u5e94\u4fee\u9633\u4ed6\u4eec\u5c45\u7136 \u5e73\u5b89\u65e0\u4e8b\uff0c\u54b1\u4eec\u4e0d\u5fc5\u5165\u57ce\u63a2\u542c\u4e86\u3002\u201d\u539f\u6765\u6155\u5bb9\u51b2\u8fd9\u6b21\u51fa\u4eac\uff0c\u9664\u4e86\u8981\u8ffd\u6355\u5cb3\u9e23\u73c2\u4e4b\u5916\uff0c\u8fd8\u6709\u6253 \u542c\u56db\u5ddd\u201c\u532a\u60c5\u201d\u7684\u4efb\u52a1\uff01\u5176\u65f6\u5f20\u732e\u5fe0\u548c\u674e\u81ea\u6210\u90fd\u5728\u56db\u5ddd\u5883\u5185\u201d\u3002\u81ea\u77f3\u6d69\u8d70\u5f8c\uff0c\u5e94\u4fee\u9633\u5df3\u66ff\u4e86 \u77f3\u6d69\u5728\u7ef5\u8863\u536b\u4e2d\u7684\u4f4d\u7f6e\uff0c\u6240\u4ee5\u9b4f\u5fe0\u8d24\u4e0d\u4f46\u6d3e\u51fa\u4e86\u4e1c\u5382\u7684\u603b\u6559\u5934\uff0c\u5bab\u4e2d\u7b2c\u4e00\u628a\u597d\u624b\u7684\u6155\u5bb9\u51b2\uff0c \u8fd8\u6d3e\u51fa\u4e86\u9526\u8863\u536b\u7684\u7edf\u9886\u5e94\u4fee\u9633\uff0c\u7528\u610f\u5c31\u662f\u8981\u9526\u8863\u536b\u548c\u4e1c\u5382\u4f5c\u201c\u5382\u536b\u201d\u5408\u4f5c\uff0c\u5171\u540c\u8ffd\u6355\u94a6\u72af\uff0c \u6253\u63a2\u654c\u8e2a\uff0c\u90a3\u665a\u6155\u5bb9\u51b2\u5e26\u5165\u641c\u67e5\u6e05\u865a\u89c2\uff0c\u5e94\u4fee\u9633\u5219\u5728\u57ce\u4e2d\u536b\u6240\u7559\u5b88\uff0c\u4ed6\u54cd\u7bad\u662f\u4ed6\u4eec\u7ea6\u597d\u7684\u8054 \u7edc\u4fe1\u53f7\u3002\u6155\u5bb9\u51b2\u62bd\u51fa\u54cd\u7bad\uff0c\u5c04\u4e0a\u5929\u7a7a\uff0c\u4e5f\u662f\u53c1\u957f\u4e24\u77ed\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u5e94\u4fee\u9633\u548c\u56db\u540d\u9526\u8863\u536b\u58eb\uff0c \u6478\u5230\u6797\u4e2d\u3002\u5e94\u4fee\u9633\u89c1\u4e1c\u5382\u536b\u58eb\uff0c\u4f24\u8005\u7d2f\u7d2f\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u600e\u9ebd\uff0c\u6b66\u5f53\u6d3e\u7684\u4eba\u5c45\u7136\u548c\u4f60 \u4eec\u52a8\u624b\u6765\u5566\uff1f\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u6b66\u5f53\u6d3e\u7684\u4e5f\u8fd8\u7f62\u4e86\uff0c\u90a3\u5973\u9b54\u5934\u4e5f\u6765\u5566\u3002\u8fd9\u4e9b\u5f1f\u5144\u4eec\u5341\u4e5d\u90fd\u662f\u5979 \u523a\u4f24\u7684\u3002\u201d\u5e94\u4fee\u9633\u9053\uff1a\u201c\u54a6\uff0c\u524d\u534a\u591c\u6211\u8fd8\u89c1\u5979\u5728\u57ce\u4e2d\u5e26\u9886\u6c11\u5927\u95f9\uff0c\u600e\u9ebd\u4e0b\u534a\u591c\u53c8\u5230\u6e05\u865a\u89c2\u548c \u4f60\u4eec\u4f5c\u5bf9\u53bb\u4e86\u3002\u201d\u6155\u5bb9\u51b2\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u8fd9\u5973\u9b54\u5934\u6765\u53bb\u5982\u98ce\uff0c\u9632\u4e0d\u80dc\u9632\uff0c\u82e5\u4e0d\u628a\u5979\u7fe6\u9664\uff0c\u7ec8\u662f \u6211\u4eec\u5fc3\u8179\u5927\u60a3\uff01\u201d \u3000\u3000\u5e94\u4fee\u9633\u8001\u5de8\u6ed1\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u8ba8\u4e0a\u5fc3\u6765\uff0c\u9053\uff1a\u201c\u8981\u7fe6\u9664\u7389\u7f57\u5239\uff0c\u6b64\u5176\u65f6\u77e3\uff01\u201d\u6155\u5bb9\u51b2\u9053\uff1a \u201c\u4f60\u6709\u4ec0\u9ebd\u5999\u6cd5\uff0c\u8bf4\u5f97\u5982\u6b64\u5bb9\u6613\uff1f\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u6797\u8fb9\u9ed1\u5f71\u6643\u52a8\uff0c\u6155\u5bb9\u51b2\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u6653 \u8272\u8ff7\u8499\u4e2d\u9ed1\u5f71\u722c\u4e0a\u5c71\u5761\uff0c\u539f\u6765\u662f\u9634\u98ce\u6bd2\u7802\u638c\u91d1\u72ec\u5f02\u3002\u4ed6\u6628\u665a\u4e2d\u4e86\u7389\u7f57\u5239\u4e00\u5251\uff0c\u4f24\u4e86\u8db3\u8ddf\uff0c\u6eda \u4e0b\u5c71\u5761\u4e4b\u5f8c\uff0c\u4fbf\u8eb2\u5728\u5c71\u8fb9\u7684\u4e71\u8349\u4e1b\u4e2d\uff0c\u89c1\u57ce\u4e2d\u706b\u8d77\uff0c\u4e0d\u6562\u72ec\u81ea\u56de\u57ce\uff0c\u76f4\u5230\u6b64\u9645\u542c\u4e86\u54cd\u7bad\u4e4b \u58f0\uff0c\u624d\u8d70\u51fa\u6765\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u91d1\u8001\u602a\uff0c\u4f60\u7684\u4f24\u52bf\u5982\u4f55\uff1f\u201d\u91d1\u72ec\u5f02\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u6ca1\u6709\u53d8\u6210\u8ddb\u5b50\u3002\u201d\u7389\u7f57\u5239 \u90a3\u5251\u523a\u4e2d\u7684\u4e0d\u662f\u81f4\u547d\u4e4b\u5904\uff0c\u91d1\u72ec\u5f02\u867d\u7136\u6280\u827a\u7a0d\u8352\uff0c\u5185\u529f\u8fd8\u5728\uff0c\u6577\u4e0a\u91d1\u521b\u836f\u5f8c\uff0c\u8fd0\u6c14\u8c03\u5143\uff0c\u8f7b \u529f\u867d\u7136\u53d7\u4e86\u4e9b\u4e9b\u5f71\u54cd\uff0c\u884c\u8fd0\u5374\u5df2\u5982\u5e38\u3002 \u3000\u3000\u91d1\u72ec\u5f02\u89c1\u8fd9\u9ebd\u591a\u4eba\u53d7\u4f24\uff0c\u4e0d\u7981\u548b\u820c\uff0c\u6068\u6068\u8bf4\u9053\uff1a\u201c\u4e0d\u628a\u90a3\u5973\u9b54\u5934\u5343\u5200\u4e07\u5250\uff0c\u96be\u6d88\u6211\u5fc3\u5934 \u4e4b\u6068\uff01\u201d\u6155\u5bb9\u51b2\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u5ac2\u5b50\u4e0d\u80af\u5e2e\u5fd9\u3002\u201d\u91d1\u72ec\u5f02\u9053\uff1a\u201c\u522b\u63d0\u5979\u5566\uff0c\u53ea\u6015\u5979\u8fd8\u8981\u628a\u6211\u8ffd \u56de\u53bb\u5462\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u6628\u65e5\u5bfb\u5230\u57ce\u4e2d\u536b\u6240\uff0c\u6070\u503c\u91d1\u72ec\u5f02\u5df2\u88ab\u6155\u5bb9\u51b2\u9063\u4ed6\u5230\u6e05\u865a\u89c2\u9644\u8fd1\u57cb\u4f0f\uff0c\u6240 \u4ee5\u7ea2\u82b1\u9b3c\u6bcd\u88ab\u9a97\u5230\u660e\u6708\u5ce1\u4e4b\u4e8b\uff0c\u91d1\u72ec\u5f02\u5c1a\u672a\u77e5\u9053\u3002\u5e94\u4fee\u9633\u7b11\u9053\uff1a\u201c\u5ac2\u5b50\u5df2\u6765\u4e86\u5462\uff01\u201d\u91d1\u72ec\u5f02 \u6253\u4e86\u4e00\u4e2a\u5bd2\u98a4\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u89c1\u7740\u5979\u4e86\uff1f\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u6628\u665a\u6ca1\u7a7a\u8bf4\u7ed9\u4f60\u77e5\uff0c\u5979\u6b64\u523b\u4e0e\u7389\u7f57\u5239 \u6b63\u5728\u52a8\u624b\u4e5f\u672a\u53ef\u77e5\u3002\u201d\u91d1\u72ec\u5f02\u542c\u4e86\u6155\u5bb9\u51b2\u6240\u8bf4\uff0c\u8df3\u8d77\u6765\u9053\uff1a\u201c\u5514\uff0c\u4f60\u4eec\u4e0d\u77e5\u5979\u7684\u813e\u6c14\uff0c\u82e5\u7136 \u7ed9\u5979\u77e5\u9053\u4f60\u4eec\u5f04\u5047\uff0c\u90a3\u65f6\u53ea\u6050\u5979\u4e0d\u627e\u7389\u7f57\u5239\u7684\u6666\u6c14\u53cd\u800c\u8981\u6765\u627e\u4f60\u4eec\u7684\u6666\u6c14\u4e86\u3002\u201d\u6155\u5bb9\u51b2\u53e3\u4e2d \u7b11\u9053\uff1a\u201c\u4e0d\u81f3\u65bc\u5427\uff01\u201d\u5fc3\u4e2d\u5374\u662f\u6697\u60ca\u3002\u5e94\u4fee\u9633\u9053\uff1a\u201c\u522b\u6101\uff0c\u6211\u6709\u529e\u6cd5\u3002\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u597d\uff0c \u4f60\u521a\u624d\u8bf4\u5230\u526a\u9664\u7389\u7f57\u5239\u4e4b\u6cd5\uff0c\u8bf7\u9053\u5177\u8be6\u3002\u201d \u3000\u3000\u5e94\u4fee\u9633\u9053\uff1a\u201c\u7389\u7f57\u5239\u5c06\u5353\u4e00\u822a\u63b3\u53bb\uff0c\u4f60\u662f\u4eb2\u89c1\u7684\u4e86\uff1f\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d\u5e94\u4fee\u9633 \u9053\uff1a\u201c\u5353\u4e00\u822a\u662f\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\uff0c\u638c\u95e8\u88ab\u63b3\uff0c\u4e43\u662f\u5947\u803b\u5927\u8fb1\uff0c\u5c24\u5176\u662f\u6b66\u5f53\u6d3e\u7684\u51e0\u4e2a\u957f\u8001\u6700\u7231\u9762 \u5b50\uff0c\u54b1\u4eec\u4e0d\u5982\u5174\u767d\u77f3\u9053\u4eba\u8bb2\u548c\uff0c\u5316\u654c\u4e3a\u53cb\uff0c\u8054\u540c\u53bb\u653b\u660e\u6708\u5ce1\u3002\u201d\u6155\u5bb9\u51b2\u81ea\u8d1f\u662f\u4e00\u7b49\u4e00\u7684\u9ad8 \u624b\uff0c\u54cd\u5f53\u5f53\u7684\u597d\u6c49\u82f1\u96c4\uff0c\u95fb\u8a00\u76b1\u76b1\u7709\u5934\uff0c\u9053\uff1a\u201c\u82e5\u7136\u5982\u6b64\uff0c\u7eb5\u7b97\u9664\u6389\u4e86\u7389\u7f57\u5239\uff0c\u4e5f\u6559\u5929\u4e0b\u82f1 \u96c4\u7b11\u8bdd\uff01\u201d\u5e94\u4fee\u9633\u7ed9\u4ed6\u4e00\u8bf4\uff0c\u751a\u4e0d\u8212\u670d\uff0c\u4f46\u6155\u5bb9\u51b2\u6b66\u529f\u6743\u804c\u5747\u5728\u4ed6\u4e0a\uff0c\u53d7\u4e86\u62a2\u767d\uff0c\u53ea\u597d\u54d1 \u5fcd\u3002 \u3000\u3000\u91d1\u72ec\u5f02\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u5174\u6b66\u5f53\u6d3e\u8054\u624b\u4e5f\u5f88\u4e0d\u9519\uff0c\u4e0d\u8fc7\u6155\u5bb9\u5927\u54e5\u65e2\u4e0d\u559c\u6b22\uff0c\u54b1\u4eec\u53e6\u60f3\u6cd5 \u5b50\u3002\u201d\u5e94\u4fee\u9633\u773c\u73e0\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u51ed\u5916\u529b\uff0c\u4e5f\u53ef\u9664\u5979\uff01\u201d\u6155\u5bb9\u51b2\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u54b1\u4eec \u5e26\u6765\u7684\u536b\u58eb\uff0c\u4f24\u4ea1\u8fc7\u534a\uff0c\u800c\u4e14\u57ce\u4e2d\u6c11\u53d8\uff0c\u5979\u7684\u52bf\u529b\u66f4\u5927\uff0c\u8981\u60f3\u9664\u5979\uff0c\u8c08\u4f55\u5bb9\u6613\uff01\u201d\u5e94\u4fee\u9633 \u9053\uff1a\u201c\u6155\u5bb9\u5927\u54e5\u77e5\u5176\u4e00\u4e0d\u77e5\u5176\u4e8c\uff0c\u6c11\u867d\u4e91\u58f0\u52bf\u6d69\u5927\uff0c\u5374\u662f\u4e4c\u5408\u4e4b\u4f17\uff0c\u62a2\u4e86\u7cae\u98df\uff0c\u5fc5\u7136\u56db\u6563\u3002 \u6628\u665a\u6211\u5728\u57ce\u4e2d\u5077\u770b\uff0c\u7389\u7f57\u5239\u5e26\u6765\u7684\u5973\u5175\uff0c\u6570\u4e0d\u6ee1\u767e\uff0c\u5c31\u51ed\u6211\u4eec\u8fd9\u73ed\u6ca1\u53d7\u4f24\u7684\u5144\u5f1f\uff0c\u4e5f\u4e0d\u60e7 \u5979\uff01\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u767e\u9980\u5973\u5175\uff0c\u81ea\u7136\u4e0d\u60e7\uff0c\u4f46\u7389\u7f57\u5239\u5462\uff1f\u96be\u9053\u4f60\u7684\u94c1\u62c2\u5c18\u5c31\u654c\u5f97\u4f4f\u5979\u7684\u5b9d\u5251 \u5417\uff1f\u201d \u3000\u3000\u5e94\u4fee\u9633\u9762\u8272\u5c34\u5c2c\uff0c\u4e7e\u54b3\u4e00\u58f0\uff0c\u7b11\u9053\uff1a\u201c\u6211\u81ea\u7136\u4e0d\u662f\u7389\u7f57\u5239\u5bf9\u624b\uff0c\u4f46\u6155\u5bb9\u5927\u54e5\uff0c\u4f60\u603b\u4e0d\u81f3 \u65bc\u5bf9\u7389\u7f57\u5239\u8ba4\u8f93\u5427\uff1f\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u82e5\u7136\u5927\u5bb6\u5404\u51ed\u771f\u5b9e\u672c\u9886\u53d6\u80dc\uff0c\u90a3\u5979\u4e0d\u662f\u6211\u7684\u5bf9\u624b\u3002\u53ea\u662f \u5979\u8f7b\u529f\u5999\u7edd\uff0c\u6211\u662f\u65e0\u6cd5\u5948\u4f55\u3002\u201d\u6155\u5bb9\u51b2\u5185\u529f\u6df1\u539a\uff0c\u795e\u62f3\u65e0\u654c\uff0c\u8bf4\u7684\u5012\u975e\u5938\u5927\u4e4b\u8bcd\u3002\u5e94\u4fee\u9633\u7b11 \u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\u3002\u6628\u665a\u4f60\u4eec\u6240\u5403\u7684\u5927\u4e8f\uff0c\u5168\u56e0\u6b66\u5f53\u6d3e\u90a3\u73ed\u9053\u58eb\u4e0e\u4f60\u4eec\u4f5c\u5bf9\uff0c\u8981\u4e0d\u7136\u7eb8\u51ed\u7389\u7f57 \u5239\u4e00\u4eba\uff0c\u90a3\u5979\u81ea\u4fdd\u4e0d\u6687\u3002\u201d\u91d1\u5343\u9053\uff1a\u201c\u554a\uff0c\u6211\u77e5\u9053\u5e94\u5927\u54e5\u7684\u610f\u601d\u4e86\uff0c\u54b1\u4eec\u8d76\u5148\u4e00\u6b65\uff0c\u5728\u660e\u6708 \u5ce1\u524d\u9762\u9669\u8981\u4e4b\u5730\u622a\u5979\u3002\u201d\u5e94\u4fee\u9633\u9053\uff1a\u201c\u662f\u554a\uff01\u54b1\u4eec\u8fd9\u73ed\u672a\u53d7\u4f24\u7684\u5f1f\u5144\uff0c\u5c3d\u9119\u5bf9\u4ed8\u5979\u7684\u5973\u55bd \u5175\u3002\u6155\u5bb9\u5927\u54e5\u548c\u91d1\u5927\u54e5\u4e8c\u4eba\u8054\u624b\uff0c\u7389\u7f57\u5239\u8f7b\u529f\u867d\u5999\uff0c\u4e5f\u96be\u9003\u8131\u3002\u5c0f\u5f1f\u4e0d\u624d\uff0c\u51ed\u7740\u8fd9\u679a\u94c1\u62c2\u5c18 \u5835\u622a\uff0c\u603b\u4e5f\u53ef\u4ee5\u548c\u5979\u4ea4\u624b\u51e0\u4e2a\u56de\u5408\u3002\u6211\u5077\u51fa\u57ce\u4e4b\u65f6\uff0c\u89c1\u5979\u6b63\u96c6\u5408\u5973\u5175\uff0c\u60f3\u5fc5\u73b0\u5728\u5c31\u8981\u64a4\u56de\u660e \u6708\u5ce1\u4e86\u3002\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u54a6\uff0c\u5979\u53c8\u56de\u5230\u57ce\u4e2d\u53bb\u4e86\uff1f\u201d\u4ed6\u4e0d\u77e5\u5e94\u4fee\u9633\u662f\u8bef\u628a\u94c1\u73ca\u745a\u5f53\u6210\u7389\u7f57 \u5239\uff0c\u5fc3\u4e2d\u6697\u6697\u5403\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u5979\u8f6c\u773c\u4e4b\u95f4\uff0c\u53c8\u4ece\u6e05\u865a\u89c2\u56de\u5230\u57ce\u7684\u4e2d\u5fc3\uff0c\u90a3\u8f7b\u529f\u5c82\u4e0d\u5df2\u5230\u4e86\u4e0d \u53ef\u601d\u8bae\u4e4b\u5883\uff01\u201d\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u4ee5\u81ea\u5df1\u7684\u672c\u9886\uff0c\u6700\u5c11\u53ef\u548c\u5979\u6253\u6210\u5e73\u624b\uff0c\u91d1\u72ec\u5f02\u867d\u7136\u8352\u5e9f\u53c1\u5e74\uff0c \u6b66\u529f\u7a0d\u900a\u4e00\u7b79\uff0c\u4e5f\u8fd8\u662f\u4e2a\u4e00\u6d41\u597d\u624b\uff0c\u66f4\u52a0\u4e0a\u5e94\u4fee\u9633\uff0c\u90a3\u9ebd\u5373\u7b97\u7389\u7f57\u5239\u672c\u9886\u518d\u9ad8\uff0c\u4e5f\u672a\u5fc5\u9003\u5f97 \u51fa\u81ea\u5df1\u638c\u4e0b\u3002\u5f53\u4e0b\u7acb\u5373\u70b9\u4e86\u5341\u4e94\u540d\u536b\u58eb\uff0c\u62a2\u53bb\u5835\u622a\u3002\u5e94\u4fee\u9633\u53c8\u5bf9\u7559\u4e0b\u5b88\u62a4\u4f24\u5458\u7684\u536b\u58eb\u5429\u5490\u4e00 \u756a\uff0c\u7b11\u9053\uff1a\u201c\u4e00\u5207\u51c6\u5907\u505c\u5f53\uff0c\u800c\u4e14\u4e0d\u8bba\u91d1\u5ac2\u5b50\u662f\u5426\u8bc6\u7a7f\u6155\u5bb9\u5927\u54e5\u7684\u8c0e\u8bdd\uff0c\u6211\u4e5f\u6709\u529e\u6cd5\u53eb\u5979\u518d \u5230\u660e\u6708\u5ce1\u53bb\u3002\u91d1\u5927\u54e5\uff0c\u90a3\u4f60\u5c31\u66f4\u4e0d\u5fc5\u62c5\u5fc3\u5566\uff01\u201d\u91d1\u72ec\u5f02\u5927\u559c\uff0c\u5f53\u4e0b\u4e00\u884c\u4eba\u5c31\u5728\u4e1c\u65b9\u672a\u5927\u767d\u4e4b \u9645\uff0c\u4fbf\u7acb\u5373\u6284\u5c0f\u8def\uff0c\u8d70\u6377\u5f84\uff0c\u8d76\u5230\u660e\u6708\u5ce1\u524d\u3002 \u3000\u3000\u518d\u8bf4\u94c1\u73ca\u745a\u5e26\u9886\u767e\u540d\u5973\u5175\uff0c\u5174\u9ad8\u91c7\u70c8\u7684\u79bb\u5f00\u5e7f\u5143\uff0c\u5c06\u52ab\u5f97\u7684\u91d1\u94f6\u73e0\u5b9d\uff0c\u7528\u4e24\u5339\u9a6c\u9a6e\uff0c\u62bc \u56de\u5c71\u5be8\u3002\u4e00\u8def\u4e0a\u90fd\u6709\u8001\u767e\u59d3\u9001\u8336\u9001\u996d\uff0c\u884c\u7a0b\u803d\u6401\uff0c\u8d70\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5230\u4e86\u5c71\u533a\uff0c\u624d\u6ca1\u8001\u767e\u59d3\u51fa \u6765\u3002\u94c1\u73ca\u745a\u62ac\u5934\u4e00\u671b\uff0c\u65e5\u5934\u5df2\u50cf\u706b\u7403\u4e00\u6837\uff0c\u5347\u5f97\u5f88\u9ad8\uff0c\u7b11\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\u4e00\u5b9a\u7b49\u5f97\u6025\u4e86\u3002\u201d \u3000\u3000\u518d\u8d70\u4e00\u7a0b\uff0c\u8fdb\u4eba\u5916\u9762\u5c71\u53e3\uff0c\u4e24\u5cf0\u5939\u5cd9\u4e4b\u95f4\u5f62\u6210\u76d8\u8c37\uff0c\u4e24\u8fb9\u602a\u77f3\u6797\u7acb\uff0c\u5c71\u8305\u91ce\u8349\uff0c\u9ad8\u903e\u534a \u8eab\uff0c\u94c1\u73ca\u745a\u9053\uff1a\u201c\u9a6c\u513f\u4e0d\u80fd\u4e0a\u5c71\uff0c\u5c06\u91d1\u94f6\u5305\u88f9\u5378\u4e0b\uff0c\u628a\u9a6c\u513f\u653e\u5230\u8c37\u4e2d\u5403\u8349\u5427\u3002\u201d\u8bdd\u521a\u8bf4\u5b8c\uff0c \u5ffd\u542c\u5f97\u547c\u5578\u4e4b\u58f0\u56db\u8d77\uff0c\u4e71\u77f3\u4e1b\u4e2d\u9aa4\u7136\u6d8c\u51fa\u8bb8\u591a\u5065\u6c49\u3002\u91d1\u72ec\u5f02\u4e00\u9a6c\u5f53\u5148\uff0c\u9634\u607b\u607b\u7684\u7b11\u9053\uff1a \u201c\u54c8\uff0c\u539f\u6765\u662f\u94c1\u59d1\u5a18\uff0c\u7389\u7f57\u5239\u5462\uff1f\u201d\u94c1\u73ca\u745a\u5927\u5403\u4e00\u60ca\uff0c\u7389\u7bab\u4e00\u70b9\uff0c\u91d1\u72ec\u5f02\u6a2a\u7a9c\u659c\u5288\uff0c\u94c1\u73ca\u745a \u9053\uff1a\u201c\u91d1\u8001\u602a\uff0c\u4f60\u6562\u653e\u8086\uff0c\u6211\u7239\u7239\u7edd\u4e0d\u80fd\u9976\u4f60\uff01\u201d\u91d1\u72ec\u5f02\u5e73\u638c\u4e00\u7f29\uff0c\u5e94\u4fee\u9633\u53eb\u9053\uff1a\u201c\u7ba1\u5979\u7684 \u4ec0\u9ebd\u7239\u7239\uff0c\u94c1\u8001\u513f\u8fd8\u5728\u5c71\u897f\uff0c\u54b1\u4eec\u5148\u628a\u4ed6\u7684\u5973\u513f\u64d2\u4e0b\uff0c\u8c01\u53eb\u5979\u548c\u90a3\u5973\u9b54\u4e00\u8def\uff1f\u201d\u91d1\u72ec\u5f02\u4e0d\u89c1 \u7389\u7f57\u5239\uff0c\u53c8\u6015\u94c1\u98de\u9f99\u4e5f\u5728\u8fd9\u513f\uff0c\u82e5\u4ed6\u548c\u7389\u7f57\u5239\u8054\u624b\u5bfb\u4ec7\uff0c\u90a3\u53ef\u96be\u65bc\u62b5\u5fa1\uff0c\u95fb\u8a00\u653e\u4e0b\u4e86\u5fc3\uff0c\u5f20 \u5f00\u84b2\u6247\u822c\u7684\u5927\u624b\uff0c\u4e00\u6293\u5411\u94c1\u73ca\u745a\u5f53\u5934\u6293\u4e0b-\uff0c \u3000\u3000\u94c1\u73ca\u745a\u659c\u8eab\u4e00\u8dc3\uff0c\u53cd\u624b\u70b9\u5012\u4e00\u540d\u536b\u58eb\uff0c\u5973\u55bd\u5175\u7eb7\u7eb7\u6d8c\u4e0a\u3002\u94c1\u73ca\u745a\u968f\u7389\u7f57\u5239\u53c1\u5e74\uff0c\u8f7b\u529f\u8fdb \u6b65\u4e0d\u5c11\uff0c\u800c\u91d1\u72ec\u5f02\u5374\u56e0\u811a\u8e1d\u53d7\u4f24\uff0c\u817e\u632a\u4e4b\u9645\uff0c\u4e0d\u82e5\u4ee5\u524d\u7075\u6d3b\uff0c\u8fd9\u4e00\u6293\u7adf\u7ed9\u94c1\u73ca\u745a\u907f\u5f00\u4e86\u3002 \u3000\u3000\u94c1\u73ca\u745a\u5927\u53eb\uff1a\u201c\u6563\u5f00\u901f\u9000\uff01\u201d\u5e94\u4fee\u9633\u54c8\u54c8\u5927\u7b11\uff0c\u7387\u5148\u51b2\u5165\u5973\u55bd\u5175\u961f\u4e2d\uff0c\u90a3\u4e9b\u5973\u55bd\u5175\u867d\u7136 \u8bad\u7ec3\u6709\u7d20\uff0c\u5374\u654c\u4e0d\u4f4f\u4e1c\u5382\u536b\u58eb\u7684\u52c7\u6b66\uff0c\u6df7\u6218\u4e2d\u53ea\u542c\u5f97\u5c16\u53eb\u4e4b\u58f0\u4e0e\u8863\u88f3\u788e\u88c2\u4e4b\u58f0\u4e71\u6210\u4e00\u7247\uff0c\u94c1 \u73ca\u745a\u84e6\u5730\u98de\u8eab\u4e0a\u9a6c\uff0c\u628a\u9a6c\u80cc\u4e0a\u7684\u5305\u88f9\u9aa4\u63b7\u4e0b\u6765\uff0c\u91d1\u94f6\u73e0\u5b9d\uff0c\u6ee1\u5730\u6eda\u52a8\uff0c\u90a3\u4e9b\u536b\u58eb\u773c\u775b\u53d1\u4eae\uff0c \u6709\u4e9b\u4eba\u4fbf\u62a2\u62fe\u73e0\u5b9d\uff0c\u6155\u5bb9\u51b2\u53eb\u9053\uff1a\u201c\u5148\u6b7c\u654c\u4eba\uff0c\u5f8c\u62fe\u73e0\u5b9d\uff0c\u8fdd\u4ee4\u8005\u65a9\u3002\u201d\u7f13\u4e86\u4e00\u7f13\uff0c\u94c1\u73ca\u745a \u53cc\u817f\u4e00\u5939\uff0c\u8de8\u4e0b\u7684\u6218\u9a6c\u957f\u5636\u4e00\u58f0\uff0c\u51b2\u5165\u4e86\u7b2c\u4e00\u9053\u8c37\u53e3\uff0c\u660e\u6708\u5ce1\u5728\u7fa4\u5c71\u4e4b\u4e2d\uff0c\u5cf0\u5ce6\u8d77\u4f0f\uff0c\u5f62\u6210 \u8bb8\u591a\u5c71\u8c37\uff0c\u6709\u5982\u91cd\u95e8\u53e0\u6237\uff0c\u94c1\u73ca\u745a\u5fc3\u60f3\uff1a\u53ea\u8981\u51b2\u8fdb\u7b2c\u53c1\u9053\u8c37\u53e3\uff0c\u5927\u58f0\u53eb\u558a\uff0c\u7389\u7f57\u5239\u4fbf\u53ef\u542c\u5230 \u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u5973\u55bd\u5175\u56db\u6563\uff0c\u5404\u81ea\u722c\u4e0a\u4e24\u65c1\u5c71\u58c1\uff0c\u5e94\u4fee\u9633\u9053\uff1a\u201c\u64d2\u8d3c\u64d2\u738b\uff0c\u8ffd\u90a3\u96cc\u513f\uff01\u201d\u91d1\u72ec\u5f02 \u9053\uff1a\u201c\u662f\u554a\uff01\u5c06\u8fd9\u4e2b\u5934\u64d2\u4e86\uff0c\u4e0d\u6101\u5f15\u4e0d\u51fa\u7389\u7f57\u5239\u6765\uff01\u201d\u660e\u6708\u5ce1\u5ced\u58c1\u9661\u7acb\uff0c\u722c\u4e0a\u53bb\u8981\u8d39\u8bb8\u591a\u6c14 \u529b\uff0c\u800c\u4e14\u5728\u4e0a\u9762\u6253\u6597\uff0c\u8f7b\u529f\u597d\u7684\u4e5f\u5360\u4fbf\u5b9c\u3002\u6155\u5bb9\u51b2\u542c\u5f97\u91d1\u72ec\u5f02\u53eb\u558a\uff0c\u4e00\u60f3\u4e0d\u9519\uff0c\u8be5\u628a\u7389\u7f57\u5239 \u5f15\u4e0b\u6765\u3002\u672c\u6765\u4ed6\u4e0d\u5c51\u4eb2\u624b\u64d2\u62ff\u4e00\u4e2a\u65e0\u540d\u7684\u5c11\u5973\uff0c\u8fd9\u65f6\u4e5f\u6025\u6025\u62a2\u4e86\u4e00\u5339\u6218\u9a6c\uff0c\u968f\u5f8c\u8ffd\u8d76\u4e86\uff01 \u3000\u3000\u5c71\u8c37\u5e95\u4e0b\u602a\u77f3\u5d99\u5ccb\uff0c\u94c1\u73ca\u745a\u8def\u719f\uff0c\u7b56\u9a6c\u98de\u9003\uff0c\u4ece\u5c71\u8305\u91ce\u8349\u4e2d\u51b2\u8fc7\uff0c\u90a3\u4e9b\u5c71\u8305\u91ce\u8349\uff0c\u72b6\u867d \u53ef\u6016\uff0c\u5730\u5e95\u5374\u6ca1\u6709\u5c16\u5229\u7684\u77f3\u5934\uff0c\u94c1\u73ca\u745a\u4ee5\u7389\u7bab\u62e8\u5f00\u8305\u8349\uff0c\u770b\u770b\u5c31\u5feb\u51b2\u5165\u7b2c\u4e8c\u9053\u5c71\u53e3\uff0c\u6155\u5bb9\u51b2 \u653e\u9a6c\u8ffd\u8d76\uff0c\u51b7\u4e0d\u9632\u78b0\u7740\u4e00\u5757\u5e73\u5730\u7a81\u8d77\u5f62\u5982\u5200\u5251\u7684\u5229\u77f3\uff0c\u9a6c\u513f\u60e8\u5636\u4e00\u58f0\uff0c\u6251\u5730\u5012\u4e0b\uff0c\u94c1\u73ca\u745a\u5df2 \u8fdb\u4e86\u7b2c\u4e8c\u9053\u5c71\u53e3\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u5927\u6012\uff0c\u7ffb\u8eab\u4e00\u6eda\uff0c\u8fc5\u5373\u8dc3\u8d77\uff0c\u624b\u4e2d\u62fe\u4e86\u51e0\u5757\u5c16\u77f3\uff0c\u8fde\u73e0\u731b\u53d1\uff0c\u6155\u5bb9\u51b2\u8155\u529b\u60ca\u4eba\uff0c \u76f8\u8ddd\u767e\u6b65\uff0c\u5c45\u7136\u7ed9\u4ed6\u6253\u4e2d\uff0c\u94c1\u73ca\u745a\u7684\u9a6c\u4e5f\u60e8\u5636\u4e00\u58f0\uff0c\u56db\u8e44\u5c48\u4e0b\uff0c\u94c1\u73ca\u745a\u7ed9\u6454\u4e0b\u9a6c\u6765\uff0c\u5bc2\u7136\u4e0d \u52a8\u3002 \u3000\u3000\u91d1\u72ec\u5f02\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u5f04\u6b7b\u8fd9\u4e2a\u4e2b\u5934\uff01\u201d\u6155\u5bb9\u51b2\u6697\u9053\uff1a\u8fd9\u4e2b\u5934\u6b66\u529f\u600e\u9ebd\u8fd9\u6837\u4e0d\u6d4e\uff0c\u83ab\u975e\u771f \u4e2a\u6b7b\u4e86\uff1f\u6211\u8981\u62ff\u5979\u6765\u5f15\u51fa\u7389\u7f57\u5239\uff0c\u53ef\u4e0d\u60f3\u591a\u60f9\u94c1\u98de\u9f99\u8fd9\u4e2a\u5f3a\u654c\u3002\u4e0a\u524d\u5bdf\u770b\uff0c\u5ffd\u5730\u5fae\u98ce\u98d2\u7136\uff0c \u51e0\u679d\u51b7\u7bad\u9aa4\u7136\u5c04\u5230\uff0c\u539f\u6765\u662f\u94c1\u73ca\u745a\u7684\u7389\u7bab\u4e4b\u4e2d\uff0c\u85cf\u6709\u77ed\u7bad\uff0c\u94c1\u73ca\u745a\u4f0f\u5730\u4e00\u5439\uff0c\u628a\u77ed\u7bad\u5439\u51fa\uff0c \u79bb\u5730\u6570\u5bf8\uff0c\u75be\u5c04\u6155\u5bb9\u51b2\u5de6\u53f3\u819d\u76d6\uff0c\u6155\u5bb9\u51b2\u51b7\u4e0d\u53ca\u9632\uff0c\u6025\u95ea\u907f\u65f6\uff0c\u5de6\u8fb9\u817f\u5f2f\u5df2\u4e2d\u4e00\u7bad\u3002\u6155\u5bb9\u51b2 \u79f0\u96c4\u534a\u4e16\uff0c\u5374\u7740\u4e86\u94c1\u73ca\u745a\u7684\u6697\u7b97\uff0c\u6b63\u662f\u53c1\u5341\u5e74\u8001\u5a18\u5012\u7ef7\u5b69\u513f\uff0c\u6c14\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u53cc\u6307\u4e00\u7b9d\uff0c\u628a \u77ed\u7bad\u62d4\u51fa\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u63d2\u7ffc\u4e0a\u5929\uff0c\u8001\u5b50\u4e5f\u8981\u628a\u4f60\u6349\u4e0b\u6765\uff01\u201d\u98de\u6b65\u6025\u8ffd\uff0c\u8fd9\u65f6\u94c1\u73ca\u745a\u5df2\u8fdb \u4eba\u7b2c\u53c1\u9053\u5c71\u53e3\uff0c\u6155\u5bb9\u51b2\u3001\u91d1\u72ec\u5f02\u4e0e\u5e94\u4fee\u9633\u4ece\u53c1\u9762\u8ffd\u6765\uff0c\u76f8\u8ddd\u5df2\u7ecf\u4e0d\u5230\u4e8c\u5341\u6b65\u4e86\uff01\u6b63\u662f\uff1a\u5c71\u8c37 \u65e0\u4eba\u8c01\u63f4\u624b\uff0c\u8352\u5c71\u72fc\u864e\u82e6\u76f8\u8ffd\u3002\u6b32\u77e5\u94c1\u73ca\u745a\u80fd\u5426\u8131\u9669\uff0c\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u56de\u3000\u4e00\u66f2\u7bab\u58f0\u3000\u7adf\u6210\u5e7f\u9675\u6563\u3000\u591a\u5e74\u68a6\u9192\u3000\u60ed\u4f5c\u672a\u4ea1\u4eba \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377 \u7b2c\u4e8c\u5341\u56de \u4e00\u66f2\u7bab\u58f0\u3000\u7adf\u6210\u5e7f\u9675\u6563 \u591a\u5e74\u68a6\u9192\u3000\u60ed\u4f5c\u672a\u4ea1\u4eba \u3000\u3000\u94c1\u73ca\u745a\u628a\u7389\u7bab\u51d1\u5728\u5507\u8fb9\uff0c\u9f13\u6c14\u4e00\u5439\uff0c\u7bab\u58f0\u51e0\u4e2a\u8f6c\u6298\uff0c\u8d8a\u5439\u8d8a\u9ad8\uff0c\u6e05\u5cfb\u4e4b\u6781\uff01\u91d1\u72ec\u5f02\u9053\uff1a \u201c\u54c8\uff0c\u4f60\u8fd8\u6709\u95f2\u5fc3\u5439\u7bab\u3002\u201d\u5ffd\u7136\u811a\u5e95\u4e00\u9635\u9707\u52a8\uff0c\u5c71\u4e0a\u54cd\u8d77\u8f70\u8f70\u4e4b\u58f0\uff0c\u5e94\u4fee\u9633\u5927\u53eb\uff1a\u201c\u4e0d\u597d\uff0c \u96ea\u5d29\uff01\u201d\u970e\u65f6\u95f4\u78e8\u76d8\u5927\u7684\u5c71\u77f3\uff0c\u548c\u51b0\u96ea\u6742\u5728\u4e00\u9053\uff0c\u6eda\u6eda\u800c\u4e0b\uff0c\u539f\u6765\u660e\u6708\u5ce1\u4e24\u8fb9\u5c71\u5cf0\u7684\u79ef\u96ea\uff0c \u6b63\u5728\u8fd9\u6625\u6696\u82b1\u5f00\u7684\u65f6\u5019\uff0c\u89e3\u51bb\u96ea\u878d\uff0c\u6bcf\u5e74\u89e3\u51bb\u4e4b\u65f6\uff0c\u5c71\u53e3\u90fd\u8981\u88ab\u5c71\u4e0a\u5012\u584c\u4e0b\u6765\u7684\u5c71\u6ce5\u77f3\u5757\u6240 \u5c01\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u7b49\u53c1\u4eba\u6b66\u529f\u5353\u7edd\uff0c\u5728\u6ee1\u5c71\u96ea\u5757\u98de\u6eda\u4e4b\u4e2d\uff0c\u817e\u8eab\u8dc3\u4e0b\u5c71\u8c37\uff0c\u8033\u9645\u8f70\u8f70\u4e4b\u58f0\uff0c\u9707\u8033\u6b32 \u804b\uff0c\u5c18\u7802\u5f25\u6f2b\u4e2d\u53ea\u89c1\u94c1\u73ca\u745a\u62da\u547d\u98de\u5954\uff0c\u6155\u5bb9\u51b2\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u90a3\u91cc\u8d70\uff01\u201d\u53cc\u81c2\u4e00\u632f\uff0c\u4ece\u5730\u63a0 \u8d77\uff0c\u51cc\u7a7a\u6251\u4e0b\uff0c\u94c1\u73ca\u745a\u518d\u628a\u77ed\u7bad\u5439\u51fa\uff0c\u6155\u5bb9\u51b2\u5df2\u6709\u9632\u5907\uff0c\u6a2a\u7a7a\u4e00\u638c\uff0c\u628a\u77ed\u7bad\u6253\u843d\uff0c\u5de6\u624b\u5f80\u4e0b \u4e00\u6251\u4e00\u6293\uff0c\u6293\u7740\u4e86\u94c1\u73ca\u745a\u9888\u9879\uff0c\u94c1\u73ca\u745a\u987f\u65f6\u534a\u8eab\u6728\uff0c\u52a8\u5f39\u4e0d\u5f97\uff0c\u53eb\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\u5feb\u6765\uff01\u201d\u6155 \u5bb9\u51b2\u7b11\u9053\uff1a\u201c\u6211\u5c31\u662f\u8981\u7b49\u4f60\u7684\u7ec3\u59d0\u59d0\uff01\u201d\u96ea\u5d29\u4e4b\u58f0\u60ed\u6b62\uff0c\u6155\u5bb9\u51b2\u56de\u5934\u4e00\u770b\uff0c\u5c71\u53e3\u5df2\u88ab\u5c71\u6ce5\u5ca9 \u77f3\u5835\u585e\uff0c\u975e\u6709\u7edd\u9876\u8f7b\u529f\uff0c\u4e0d\u80fd\u4ece\u5ced\u58c1\u90a3\u8fb9\u722c\u4e0b\u6765\uff0c\u9664\u4e86\u91d1\u72ec\u5f02\u548c\u5e94\u4fee\u9633\u5df2\u8fdb\u5165\u5c71\u8c37\u5916\uff0c\u5176\u4ed6 \u536b\u58eb\u90fd\u88ab\u963b\u9694\u5728\u5c71\u53e3\u4e4b\u5916\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u631f\u8d77\u4e86\u94c1\u73ca\u745a\uff0c\u6101\u9053\uff1a\u201c\u5f1f\u5144\u4eec\u90fd\u88ab\u6401\u5728\u5916\u9762\uff0c\u82e5\u7136\u7389\u7f57\u5239\u5e26\u5973\u5175\u6740\u4e0b\uff0c\u54b1\u4eec\u53ef \u662f\u5be1\u4e0d\u654c\u4f17\uff01\u201d\u5e94\u4fee\u9633\u9053\uff1a\u201c\u65e2\u7136\u64d2\u4e86\u8fd9\u4e2a\u4e2b\u5934\uff0c\u4e0d\u5982\u5148\u56de\u53bb\u5427\u3002\u7389\u7f57\u5239\u8fd9\u5973\u9b54\u5934\u81ea\u6043\u6b66 \u529f\uff0c\u80c6\u5927\u5305\u5929\uff0c\u5979\u7ed3\u4e49\u7684\u59d0\u59b9\u5728\u54b1\u4eec\u624b\u4e2d\uff0c\u5979\u4e00\u5b9a\u4f1a\u820d\u547d\u6765\u6551\u3002\u90a3\u65f6\u54b1\u4eec\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u4ee5\u9038\u4ee3 \u52b3\uff0c\u66f4\u5360\u4fbf\u5b9c\u3002\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u597d\uff0c\u90a3\u9ebd\u54b1\u4eec\u5feb\u722c\u5c71\u8d70\u5427\u3002\u201d\u53c1\u4eba\u6500\u767b\u5ced\u58c1\uff0c\u6155\u5bb9\u51b2\u6b66\u529f\u5353 \u7edd\uff0c\u8f7b\u529f\u867d\u7136\u4e0d\u53ca\u7389\u7f57\u5239\u4f73\u5999\uff0c\u4ea6\u81ea\u4e0d\u51e1\uff0c\u631f\u7740\u94c1\u73ca\u745a\u6500\u767b\u5ced\u58c1\uff0c\u4ecd\u7136\u5982\u5c65\u5e73\u5730\u3002\u5e94\u4fee\u9633\u6b66 \u529f\u7a0d\u900a\uff0c\u4f46\u7a7a\u624b\u6500\u63f4\uff0c\u4e5f\u80fd\u4ea6\u6b65\u4ea6\u8d8b\u3002\u53ea\u662f\u82e6\u4e86\u91d1\u72ec\u5f02\uff0c\u4ed6\u6b66\u529f\u867d\u9ad8\uff0c\u811a\u8e1d\u6240\u4e2d\u7684\u521b\u4f24\u5c1a\u672a \u5b8c\u5168\u5e73\u590d\uff0c\u5728\u5e73\u5730\u884c\u8d70\uff0c\u5c1a\u6ca1\u4ec0\u9ebd\uff0c\u8df3\u8dc3\u6500\u63f4\u5374\u662f\u4e0d\u4fbf\uff0c\u8d70\u51e0\u6b65\uff0c\u6b47\u4e00\u6b47\uff0c\u6155\u5bb9\u51b2\u751a\u4e0d\u8010 \u70e6\uff0c\u5bf9\u5e94\u4fee\u9633\u9053\uff1a\u201c\u4f60\u6276\u4ed6\u4e00\u628a\u5427\u3002\u201d\u5e94\u4fee\u9633\u7684\u8f7b\u529f\u4ec5\u80fd\u81ea\u987e\uff0c\u5fc3\u4e2d\u5f88\u4e0d\u613f\u610f\uff0c\u65e0\u5948\u8fd9\u662f\u6155 \u5bb9\u51b2\u7684\u547d\u4ee4\uff0c\u53ea\u80fd\u786c\u7740\u5934\u76ae\uff0c\u56de\u5934\u53bb\u6276\u3002\u6155\u5bb9\u51b2\u6b47\u811a\u7b49\u5019\uff0c\u80c1\u4e0b\u631f\u7740\u7684\u94c1\u73ca\u745a\u5ffd\u7136\u5c16\u53eb\u4e00 \u58f0\uff0c\u6155\u5bb9\u51b2\u559d\u9053\uff1a\u201c\u4f60\u627e\u6b7b\u9ebd\uff1f\u201d\u62ac\u5934\u4e00\u770b\uff0c\u5ffd\u89c1\u5c71\u5cf0\u4e0a\u6709\u4e00\u6761\u4eba\u5f71\uff0c\u75be\u82e6\u661f\u4e38\uff0c\u98de\u8dc3\u800c \u4e0b\uff0c\u91d1\u72ec\u5f02\u60ca\u9053\uff1a\u201c\u662f\u7389\u7f57\u5239\u6765\u4e86\uff01\u201d\u6155\u5bb9\u51b2\u70b9\u4e86\u94c1\u73ca\u745a\u7a74\u9053\uff0c\u653e\u5728\u4e00\u8fb9\uff0c\u51dd\u795e\u5f85\u654c\uff0c\u53ea\u89c1 \u5c71\u5cf0\u4e0a\u4e0d\u662f\u4e00\u6761\u4eba\u5f71\u800c\u662f\u4e24\u6761\u4eba\u5f71\uff0c\u5148\u5934\u7684\u4e00\u4eba\u5728\u53e6\u4e00\u8fb9\uff0c\u5e76\u4e0d\u4e0b\u6765\uff0c\u800c\u662f\u75be\u63a0\u8fc7\u4e00\u4e2a\u4e2a\u7684 \u5cf0\u5934\uff0c\u5411\u660e\u6708\u5ce1\u90a3\u8fb9\u4e3b\u5cf0\u5954\u53bb\uff0c\u8fd9\u4eba\u770b\u6765\u4f3c\u662f\u5973\u4eba\uff0c\u53e6\u4e00\u6761\u8dc3\u4e0b\u6765\u7684\u4eba\u5f71\u5728\u5371\u5ca9\u602a\u77f3\u4e4b\u95f4\u9690 \u73b0\uff0c\u9762\u5f62\u867d\u7136\u8fd8\u672a\u77a7\u5f97\u5341\u5206\u6e05\u695a\uff0c\u4f46\u5374\u663e\u7136\u4e0d\u4f3c\u5973\u4eba\u3002 \u3000\u3000\u518d\u8bf4\u7389\u7f57\u5239\u548c\u5353\u4e00\u822a\u8d70\u5230\u5c71\u5934\u773a\u671b\uff0c\u5ffd\u542c\u5f97\u5c71\u98ce\u4e2d\u9001\u6765\u7684\u95f7\u96f7\u4e4b\u58f0\uff0c\u7389\u7f57\u5239\u53eb\u9053\uff1a\u201c\u524d \u5c71\u96ea\u5d29\u5566\uff01\u73ca\u745a\u59b9\u5b50\u4e00\u5b9a\u88ab\u963b\u5728\u5916\u9762\u4e86\uff01\u201d\u6b63\u60f3\u4e0b\u5c71\uff0c\u5ffd\u89c1\u5bf9\u9762\u5c71\u5934\uff0c\u4e00\u6761\u4eba\u5f71\u98de\u5954\u800c\u6765\uff0c \u5b9a\u775b\u4e00\u770b\uff0c\u5374\u662f\u7ea2\u82b1\u9b3c\u6bcd\u3002\u5353\u4e00\u822a\u9053\uff1a\u201c\u7ea2\u82b1\u9b3c\u6bcd\u518d\u6765\uff0c\u5fc5\u662f\u53d7\u4eba\u86ca\u60d1\uff0c\u7ec3\u59d0\u59d0\uff0c\u4f60\u53ef\u5f97\u5f53 \u5fc3\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u5728\u8fd9\u91cc\u5019\u5979\uff0c\u6211\u56de\u5c71\u5be8\u4e00\u8f6c\u4fbf\u6765\u3002\u201d\u53cd\u8eab\u5954\u56de\u5c71\u5be8\uff0c\u5353\u4e00\u822a\u72ec\u7acb\u5c71 \u5934\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u5df2\u662f\u58f0\u5230\u4eba\u5230\u3002 \u3000\u3000\u539f\u6765\u7ea2\u82b1\u9b3c\u6bcd\u9ece\u660e\u65f6\u5206\u79bb\u5f00\u4e86\u660e\u6708\u5ce1\u5f8c\uff0c\u5bf9\u7389\u7f57\u5239\u7684\u8bdd\u5c06\u4fe1\u5c06\u7591\uff0c\u4e00\u5ffd\u513f\u60f3\u9053\uff1a\u6211\u90a3\u8d3c \u6c49\u5b50\u5c61\u529d\u4e0d\u6539\uff0c\u505a\u51fa\u574f\u4e8b\u6765\u4ea6\u672a\u53ef\u6599\uff1a\u4e00\u5ffd\u513f\u60f3\u9053\uff1a\u4e0d\u4f1a\u5440\u4e0d\u4f1a\uff0c\u4ed6\u5077\u6e9c\u51fa\u6765\uff0c\u6ca1\u6709\u51e0\u5929\uff0c \u800c\u4e14\u7b2c\u4e8c\u5929\u6211\u4fbf\u8ddf\u8e2a\u8ffd\u4ed6\uff0c\u4ed6\u90a3\u80fd\u817e\u51fa\u65f6\u95f4\u548c\u6155\u5bb9\u51b2\u4ed6\u4eec\u5546\u91cf\u4f5c\u6076\u3002\u6b8a\u4e0d\u77e5\u91d1\u72ec\u5f02\u8fd9\u6b21\u9003 \u51fa\uff0c\u4e43\u662f\u6697\u4e2d\u548c\u5e94\u4fee\u9633\u4ed6\u4eec\u5b9a\u8c0b\uff0c\u8d81\u7740\u7ea2\u82b1\u9b3c\u6bcd\u8bbf\u53cb\u4e4b\u65f6\uff0c\u5077\u5077\u6e9c\u51fa\u6765\u7684\uff0c\u4ed6\u4eec\u662f\u65e9\u6709\u63a5\u5e94 \u7684\u4e86\u3002\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u731c\u7591\u4e0d\u5b9a\uff0c\u5fc3\u9053\uff1a\u7389\u7f57\u5239\u65e2\u8bf4\u4ed6\u5230\u8fc7\u6e05\u865a\u89c2\uff0c\u6211\u4e14\u5230\u6e05\u865a\u89c2\u95ee\u95ee\u3002\u7ea2\u82b1\u9b3c \u6bcd\u4e0d\u77e5\u767d\u77f3\u9053\u4eba\u4fbf\u5728\u6e05\u865a\u89c2\u4e2d\uff0c\u89c1\u9762\u4e4b\u4e0b\uff0c\u51e0\u4e4e\u60f9\u51fa\u4e00\u573a\u5927\u6253\u3002\u5728\u53cc\u65b9\u9a82\u6218\u4e2d\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u5df2 \u63a2\u5f97\u81ea\u5df1\u7684\u4e08\u592b\u786e\u5b9e\u5230\u8fc7\u6e05\u865a\u89c2\uff0c\u4f46\u4e5f\u786e\u5b9e\u662f\u88ab\u7389\u7f57\u5239\u6240\u523a\u4f24\u3002\u767d\u77f3\u9053\u4eba\u9a82\u9053\uff1a\u201c\u8c01\u6709\u7a7a\u7ed9 \u4f60\u7ba1\u6c49\u5b50\uff0c\u8dd1\u5230\u8fd9\u91cc\u6765\u627e\u6c49\u5b50\uff0c\u771f\u662f\u5929\u5927\u7684\u7b11\u8bdd\uff01\u8981\u627e\u6c49\u5b50\u4f60\u5411\u7389\u7f57\u5239\u8981\u53bb\uff0c\u54fc\uff0c\u54fc\uff01\u7389\u7f57 \u5239\u7684\u5b9d\u5251\u53ef\u4e0d\u7559\u60c5\uff0c\u4f60\u7684\u6c49\u5b50\u5df2\u906d\u4e86\u90a3\u5973\u9b54\u5934\u7684\u6bd2\u624b\u5566\uff01\u4f60\u627e\u5979\uff0c\u5979\u4e5f\u672a\u5fc5\u8fd8\u5f97\u4e00\u4e2a\u6d3b\u7684\u7ed9 \u4f60\uff01\u201d\u767d\u77f3\u9053\u4eba\u632b\u8d25\u4e4b\u9980\uff0c\u867d\u7136\u89c2\u4e2d\u5f1f\u5b50\u4f17\u591a\uff0c\u5fc3\u4e2d\u5bf9\u7ea2\u82b1\u9b3c\u6bcd\uff0c\u5374\u662f\u5185\u602f\uff0c\u6240\u4ee5\u6545\u610f\u7528\u8bdd \u6311\u62e8\uff0c\u5b9e\u884c\u79fb\u7978\u6c5f\u4e1c\u4e4b\u7b56\u3002 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u6551\u592b\u5fc3\u5207\uff0c\u65e0\u5fc3\u4e0e\u6b66\u5f53\u6d3e\u518d\u6597\uff0c\u95fb\u8a00\u5954\u51fa\u9053\u89c2\uff0c\u8d70\u51fa\u9053\u89c2\u95e8\u53e3\u624d\u89e6\u8d77\u4e00\u4e8b\uff0c\u56de \u5934\u95ee\u9053\uff1a\u201c\u90a3\u4e2a\u4ec0\u9ebd\u5cb3\u545c\u73c2\u5462\uff1f\u201d\u767d\u77f3\u9053\u4eba\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u8c01\u4e0e\u4f60\u7ba1\u8fd9\u9ebd\u591a\u95f2\u4e8b\uff0c\u4e0d\u77e5 \u9053\uff01\u201d\u6b66\u5f53\u7684\u5f1f\u5b50\u7830\u7136\u628a\u5927\u95e8\u5173\u4e86\u3002\u7ea2\u82b1\u9b3c\u6bcd\u597d\u4e0d\u751f\u6c14\uff0c\u672c\u5f85\u518d\u8df3\u5165\u89c2\u4e2d\uff0c\u53ef\u662f\u56de\u5fc3\u4e00\u60f3\uff1a \u4e08\u592b\u7684\u751f\u6b7b\u672a\u660e\uff0c\u65e2\u77e5\u4ed6\u662f\u88ab\u7389\u7f57\u5239\u6240\u4f24\uff0c\u4f55\u5fc5\u8fd8\u5728\u8fd9\u91cc\u548c\u767d\u77f3\u8fd9\u7ea0\u7f20\u3002 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u6025\u6025\u4e0b\u5c71\uff0c\u53c8\u5230\u57ce\u4e2d\u536b\u6240\u627e\u6155\u5bb9\u51b2\uff0c\u5176\u65f6\u62a2\u7cae\u7684\u9965\u6c11\u5df2\u6563\uff0c\u90a3\u4e9b\u53d7\u4f24\u7684\u536b\u58eb\u5df2 \u88ab\u62ac\u56de\u536b\u6240\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u4e00\u5230\uff0c\u4fbf\u542c\u5f97\u51c4\u60e8\u547c\u53f7\u4e4b\u58f0\uff0c\u5148\u81ea\u5fc3\u60ca\u8089\u8df3\uff0c\u4eba\u53bb\u4e00\u770b\uff0c\u53ea\u89c1\u53d7\u4f24\u7684 \u5341\u5c45\u516b\u4e5d\uff0c\u90fd\u662f\u7a74\u9053\u5173\u8282\u4e4b\u5904\uff0c\u88ab\u5251\u523a\u4f24\uff0c\u8fd9\u5206\u660e\u662f\u7389\u7f57\u5239\u7684\u624b\u6cd5\u4e86\uff01\u7ea2\u82b1\u9b3c\u6bcd\u4e0d\u89c1\u6155\u5bb9 \u51b2\uff0c\u4e5f\u4e0d\u89c1\u5e94\u4fee\u9633\uff0c\u4fbf\u95ee\u7559\u5728\u536b\u6240\u4e2d\u7684\u536b\u58eb\uff0c\u90a3\u4e9b\u536b\u58eb\u65e9\u5f97\u4e86\u5e94\u4fee\u9633\u7684\u6307\u6559\uff0c\u7b54\u9053\uff1a\u201c\u6155\u5bb9 \u603b\u7ba1\u548c\u5e94\u90fd\u5934\u53bb\u6551\u91d1\u8001\u7239\u5566\uff01\u4f60\u8001\u4eba\u5bb6\u5230\u660e\u6708\u5ce1\u53bb\u5427\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\u8981\u5230\u660e\u6708 \u5ce1\uff1f\u201d\u7559\u5b88\u7684\u536b\u58eb\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u8001\u4eba\u5bb6\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u91d1\u8001\u524d\u8f88\u88ab\u7389\u7f57\u5239\u523a\u4f24\uff0c\u751f\u64d2\u53bb\u5566\uff1f\u201d \u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u90a3\u4e2a\u4ec0\u9ebd\u5cb3\u545c\u73c2\u5462\uff1f\u54ce\uff0c\u8fd8\u6709\uff0c\u718a\u7ecf\u7565\u662f\u5426\u88ab\u671d\u5ef7\u6740\u4e86\uff1f\u201d\u536b\u58eb\u9053\uff1a\u201c\u5cb3\u9e23 \u73c2\uff1f\u55ef\uff0c\u662f\u6709\u90a3\u9ebd\u4e00\u4e2a\u5cb3\u545c\u73c2\uff01\u9119\u662f\u8fd9\u6837\u7684\u65e0\u540d\u5c0f\u5352\uff0c\u4f60\u8001\u4eba\u5bb6\u600e\u9ebd\u4f1a\u77e5\u9053\u7684\u5440\uff1f\u4ed6\u8d81\u7740\u7edf \u5e05\u88ab\u671d\u5ef7\u5904\u6b7b\uff0c\u5077\u4e86\u5e94\u8be5\u6ca1\u5165\u56fd\u5e93\u7684\u4e1c\u897f\uff0c\u671d\u5ef7\u8981\u8ffd\u8d43\u54e9\u3002\u4e0d\u8fc7\uff0c\u6211\u4eec\u53ef\u4e0d\u662f\u4e13\u4e3a\u8ffd\u6355\u4ed6\u6765 \u7684\u3002\u81f3\u65bc\u718a\u5ef7\u5f3c\u4e3a\u4ec0\u9ebd\u88ab\u5904\u6b7b\uff0c\u90a3\uff0c\u6211\u4eec\u5c31\u4e0d\u6e05\u695a\u4e86\u3002\u542c\u8bf4\u662f\u901a\u756a\u5356\u56fd\u7684\u7f6a\u540d\u54e9\u3002\u201d\u7ea2\u82b1\u9b3c \u6bcd\u542c\u5b8c\uff0c\u7acb\u523b\u51fa\u57ce\uff0c\u5411\u660e\u6708\u5ce1\u98de\u5954\u800c\u53bb\u3002 \u3000\u3000\u5c06\u8fd1\u660e\u6708\u5ce1\u65f6\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u5df2\u9065\u89c1\u8ffd\u654c\u536b\u58eb\uff0c\u6025\u5fd9\u8d76\u4e0a\u53bb\u95ee\uff0c\u5ffd\u542c\u5f97\u8f70\u8f70\u7136\u5982\u96f7\u545c\uff0c\u7206\u77f3 \u7684\u96ea\u5d29\u4e4b\u58f0\uff0c\u5176\u65f6\u91d1\u72ec\u5f02\u548c\u6155\u5bb9\u51b2\u5df2\u8fdb\u4eba\u7b2c\u53c1\u9053\u5c71\u53e3\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u521a\u8fdb\u7b2c\u4e00\u9053\u5c71\u53e3\uff0c\u95fb\u58f0\u77e5\u662f \u5d29\u96ea\u5c01\u5c71\uff0c\u62e6\u4f4f\u843d\u5f8c\u7684\u536b\u58eb\u4e00\u95ee\uff0c\u90a3\u540d\u536b\u58eb\u6b63\u662f\u5e94\u4fee\u9633\u7684\u5f92\u5f1f\uff0c\u72e1\u733e\u4e0d\u51cf\u4e43\u5e08\uff0c\u7b54\u9053\uff1a\u201c\u54b1 \u4eec\u6765\u6551\u91d1\u8001\u7239\uff0c\u5728\u8def\u4e0a\u5c31\u548c\u5979\u7684\u5973\u55bd\u5175\u6253\u8d77\u6765\u4e86\u3002\u4f60\u8001\u4eba\u5bb6\u6765\u597d\u6781\u5566\uff01\u5d29\u96ea\u5c01\u5c71\uff0c\u6211\u4eec\u8fc7\u4e0d \u53bb\uff0c\u4f60\u53ef\u4ee5\u6500\u767b\u5c71\u9876\uff0c\u7ed5\u8fc7\u5c71\u53e3\u5230\u660e\u6708\u5ce1\u53bb\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u4e00\u542c\u4e0d\u9519\uff0c\u907f\u5f00\u6b63\u9762\u7684\u96ea\u5d29\u4e4b\u5904\uff0c \u65bd\u5c55\u4e0a\u4e58\u8f7b\u529f\uff0c\u6500\u4e0a\u5c71\u5cf0\u3002\u5728\u5979\u4e0a\u5230\u5cf0\u5dc5\u4e4b\u65f6\uff0c\u6b63\u662f\u6155\u5bb9\u51b2\u4ed6\u4eec\u722c\u4e0a\u5ced\u58c1\u7684\u65f6\u5019\uff0c\u5ced\u58c1\u4e0a\u7a81 \u51fa\u6765\u7684\u5ca9\u77f3\u548c\u5728\u77f3\u9699\u4e2d\u4f38\u51fa\u7684\u8513\u6b63\u628a\u6155\u5bb9\u51b2\u4ed6\u4eec\u906e\u7740\u3002\u56e0\u6b64\u7ea2\u82b1\u9b3c\u6bcd\u4e00\u70b9\u4e0d\u77e5\u4e08\u592b\u4fbf\u5728\u4e0b \u9762\uff0c\u4ee5\u81f4\u5931\u4e4b\u4ea4\u81c2\u3002\u9002\u503c\u6b64\u65f6\uff0c\u5ffd\u53c8\u89c1\u6709\u4e00\u6761\u4eba\u5f71\uff0c\u5728\u4fa7\u9762\u5c71\u5cf0\u51fa\u73b0\uff0c\u75be\u903e\u6d41\u661f\uff0c\u98de\u4e0b\u5e7d \u8c37\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u5fc3\u9053\uff1a\u201c\u8fd9\u4efd\u8f7b\u529f\u7684\u786e\u8d85\u51e1\u7edd\u4fd7\uff0c\u770b\u6765\u4e0e\u7389\u7f57\u5239\u4e43\u662f\u4f2f\u4ef2\u4e4b\u95f4\u3002\u4e0d\u77e5\u7adf\u662f\u90a3\u4f4d \u4e16\u5916\u9ad8\u4eba\u6765\u4e86\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u6697\u6570\u6c5f\u6e56\u4e0a\u7684\u5404\u6d3e\u540d\u5bb6\uff0c\u65e0\u4eba\u6709\u6b64\u672c\u9886\uff0c\u56e0\u6b64\u7adf\u7591\u4e0d\u77e5\u662f\u90a3\u4f4d\u9690 \u5c45\u7684\u524d\u8f88\u9ad8\u4eba\u3002\u7ea2\u82b1\u9b3c\u6bcd\u82e5\u5728\u5e73\u65f6\uff0c\u89c1\u6b64\u9ad8\u4eba\uff0c\u5fc5\u5b9a\u4f1a\u8ffd\u4e0b\u53bb\u76f8\u4f1a\u3002\u53ef\u662f\u6b64\u523b\u5979\u4e00\u6765\u662f\u6551\u592b \u5fc3\u5207\uff0c\u4e8c\u6765\u53c8\u4e0d\u77e5\u6b64\u4eba\u662f\u654c\u662f\u53cb\uff1f\u662f\u654c\u56fa\u7136\u6709\u4e00\u756a\u6740\uff0c\u662f\u53cb\u4e5f\u6709\u4e00\u573a\u5bd2\u6684\u3002\u660e\u6708\u5ce1\u5c31\u5728\u9762 \u524d\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u90a3\u8fd8\u6709\u95f2\u5fc3\u5728\u6b64\u803d\u6401\u3002\u770b\u90a3\u9ed1\u5f71\u98de\u4e0b\u5e7d\u8c37\uff0c\u5979\u4e5f\u63d0\u4e00\u53e3\u6c14\uff0c\u5728\u5c71\u9876\u4e0a\u75be\u63a0\u8f7b \u9a70\uff0c\u8fc7\u4e86\u4e00\u4e2a\u4e2a\u7684\u5c71\u5cf0\uff0c\u76f4\u5230\u660e\u6708\u5ce1\u5c71\u4e0a\u7389\u7f57\u5239\u7684\u5927\u5be8\u3002 \u3000\u3000\u6b64\u65f6\u5353\u4e00\u822a\u6b63\u5728\u5c71\u5934\u773a\u671b\uff0c\u5fc3\u4e2d\u60f4\u60f4\u3002\u7ea2\u82b1\u9b3c\u6bcd\u58f0\u5230\u4eba\u5230\uff0c\u559d\u9053\uff1a\u201c\u7389\u7f57\u5239\u5462\uff1f\u201d\u5353\u4e00 \u822a\u8eac\u8170\u95ee\u9053\uff1a\u201c\u8001\u524d\u8f88\u91cd\u6765\uff0c\u6709\u4f55\u6307\u6559\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u4e0d\u5e72\u4f60\u4e8b\uff0c\u4f60\u53eb\u7389\u7f57\u5239\u6765\uff01\u201d\u5353 \u4e00\u822a\u9053\uff1a\u201c\u8001\u524d\u8f88\uff0c\u4f60\u7a0d\u5f85\u4e00\u4f1a\uff0c\u5979\u5c31\u51fa\u6765\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u89c1\u5be8\u95e8\u7d27\u95ed\uff0c\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u662f\u66ff\u5979 \u65bd\u7f13\u5175\u4e4b\u8ba1\uff0c\u8001\u5a18\u53ef\u4e0d\u4e0a\u4f60\u4eec\u7684\u5f53\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u4ee5\u4e3a\u7389\u7f57\u5239\u81ea\u77e5\u7406\u4e8f\uff0c\u4e0d\u6562\u89c1\u5979\uff0c\u5173\u4e0a\u5be8 \u95e8\uff0c\u8981\u5077\u5077\u7684\u4ece\u5c71\u5be8\u5f8c\u6e9c\u4e0b\u5c71\u53bb\u3002\u5fc3\u5934\u6025\u8e81\uff0c\u5de6\u638c\u4e00\u63a8\u628a\u5353\u4e00\u822a\u63a8\u5f00\uff0c\u5954\u4e0a\u524d\u53bb\uff0c\u6697\u8fd0\u5185\u5bb6 \u771f\u529b\uff0c\u547c\u7684\u4e00\u62d0\uff0c\u628a\u5be8\u95e8\u6253\u88c2\uff0c\u8fd0\u638c\u4e00\u5288\uff0c\u5be8\u95e8\u5012\u4e0b\uff0c\u5973\u55bd\u5175\u7eb7\u7eb7\u9003\u907f\u3002\u7389\u7f57\u5239\u98de\u5954\u800c\u51fa\uff0c \u5927\u6012\u559d\u9053\uff1a\u201c\u7ea2\u82b1\u9b3c\u6bcd\uff0c\u4f60\u6562\u6253\u5d29\u6211\u7684\u5be8\u95e8\uff1f\u201d\u5237\u5237\u4e24\u5251\uff0c\u76f4\u523a\u7ea2\u82b1\u9b3c\u6bcd\u524d\u5fc3\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u9707 \u62d0\u4e00\u6321\uff0c\u7389\u7f57\u5239\u5df2\u75be\u5982\u98de\u9e1f\u822c\u63a0\u8fc7\u5979\u7684\u5934\u9876\uff0c\u62a2\u4e0a\u9ad8\u5730\uff0c\u559d\u9053\uff1a\u201c\u6765\uff0c\u6765\uff0c\u6765\uff01\u54b1\u4eec\u518d\u6597\u53c1 \u767e\u56de\u5408\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u53cd\u624b\u4e00\u626c\uff0c\u559d\u9053\uff1a\u201c\u7389\u7f57\u5239\uff0c\u4f60\u6562\u9a97\u6211\uff0c\u628a\u4eba\u8fd8\u6211\uff0c\u8981\u4e0d\u7136\u4eca\u65e5\u7edd\u4e0d\u5174 \u4f60\u5e72\u4f11\uff01\u201d\u7389\u7f57\u5239\u660e\u77e5\u5979\u5fc5\u662f\u88ab\u4eba\u6b3a\u5f04\uff0c\u4f46\u6068\u5979\u6253\u584c\u5be8\u95e8\uff0c\u6c14\u5728\u5934\u4e0a\uff0c\u4e5f\u4e0d\u8be6\u52a0\u5206\u8fa8\uff0c\u51b7\u7b11 \u559d\u9053\uff1a\u201c\u4f60\u4e0d\u66ff\u6211\u4fee\u597d\u5be8\u95e8\uff0c\u6211\u8ba4\u5f97\u4f60\uff0c\u6211\u7684\u5251\u8ba4\u4e0d\u5f97\u4f60\uff0c\u5c31\u662f\u4f60\u60f3\u5e72\u4f11\u6211\u4e5f\u7edd\u4e0d\u5174\u4f60\u5e72 \u4f11\uff01\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u624b\u4e2d\u5b9d\u5251\u5df2\u8fde\u53d1\u4e86\u516d\u4e03\u4e2a\u8fa3\u62db\uff0c\u771f\u662f\u5feb\u901f\u4e4b\u6781\uff01 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u5927\u6012\uff0c\u9f99\u5934\u62d0\u6756\u6a2a\u626b\u76f4\u683c\uff0c\u547c\u547c\u631f\u98ce\uff0c\u4fbf\u5728\u5c71\u5be8\u4e4b\u524d\u4e0e\u7389\u7f57\u5239\u5927\u6218\u8d77\u6765\uff01 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u6551\u592b\u5fc3\u5207\uff0c\u53c8\u6068\u7389\u7f57\u5239\u5bf9\u5979\u65e0\u793c\uff0c\u8fd9\u56de\u7adf\u662f\u62da\u547d\u6740\uff0c\u62d0\u91cd\u5982\u5c71\uff0c\u7389\u7f57\u5239\u5728\u660e\u6708 \u5ce1\u82e6\u4fee\u4e86\u53c1\u5e74\u5185\u529f\uff0c\u662f\u611f\u5230\u62db\u67b6\u4e0d\u6613\u3002\u53ef\u662f\u7389\u7f57\u5239\u8f7b\u529f\u5353\u7edd\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u6253\u5f97\u7802\u77f3\u7eb7\u98de\uff0c\u5374\u4e5f \u6253\u4e0d\u7740\u5979\uff01\u7389\u7f57\u5239\u5ffd\u800c\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u53c1\u5e74\u591a\u6765\uff0c\u6ca1\u6709\u8fd9\u6837\u75db\u75db\u5feb\u5feb\u6253\u8fc7\u4e86\uff01\u201d\u68cb\u9022\u5bf9\u624b\uff0c\u7cbe \u795e\u500d\u957f\uff0c\u628a\u72ec\u95e8\u5251\u6cd5\u4f7f\u5f97\u51cc\u5389\u65e0\u524d\uff0c\u5251\u5f0f\u5c55\u5f00\uff0c\u5929\u77eb\u5982\u795e\u9f99\u98de\u821e\uff0c\u51fb\u523a\u64a9\u62b9\uff0c\u4e4d\u8fdb\u4e4d\u9000\uff0c\u500f \u4e0a\u500f\u4e0b\uff0c\u65f6\u5b9e\u65f6\u865a\uff0c\u65e0\u4e00\u62db\u4e0d\u662f\u6697\u85cf\u51e0\u4e2a\u53d8\u5316\uff0c\u65e0\u4e00\u62db\u4e0d\u662f\u5999\u5230\u6beb\u5dc5\u3002\u7ea2\u82b1\u9b3c\u6bcd\u5f3a\u653b\u4e0d\u4e0b\uff0c \u5927\u6012\u559d\u9053\uff1a\u201c\u597d\uff0c\u6211\u4e0e\u4f60\u62da\u5566\uff01\u201d\u62d0\u638c\u517c\u65bd\uff0c\u6253\u5f97\u8d8a\u53d1\u51f6\u731b\uff0c\u90a3\u679d\u9f99\u5934\u62d0\u6756\uff0c\u5288\u626b\u76d8\u6253\uff0c\u6070 \u5982\u9a87\u7535\u60ca\u9706\uff0c\u65e0\u4e00\u62db\u4e0d\u662f\u5954\u5411\u7389\u7f57\u5239\u8981\u5bb3\uff0c\u5de6\u638c\u66f4\u7528\u6392\u5c71\u638c\u529b\uff0c\u6c64\u6c14\u6210\u98ce\uff0c\u9707\u6b6a\u7389\u7f57\u5239\u7684\u5251 \u70b9\uff0c\u5353\u4e00\u822a\u5728\u65c1\u8fb9\u770b\u5f97\u5341\u5206\u7740\u6025\uff0c\u5927\u53eb\uff1a\u201c\u6709\u8bdd\u597d\u8bf4\uff01\u91d1\u8001\u524d\u8f88\u786e\u662f\u4e0d\u5728\u8fd9\u91cc\uff01\u201d\u4e24\u4eba\u62da\u6b63 \u70c8\uff0c\u90a3\u80af\u6536\u624b\uff0c\u8fde\u5206\u795e\u8bf4\u8bdd\u90fd\u4e0d\u613f\u610f\uff0c\u53cc\u65b9\u4ee5\u653b\u5bf9\u653b\uff0c\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u5df2\u62da\u4e86\u53c1\u767e\u591a\u62db\u4e86\uff01 \u3000\u3000\u8fd9\u756a\u6fc0\u6218\u4e0e\u524d\u6b21\u5728\u79d8\u9b54\u5d16\u4e4b\u6218\uff0c\u53c8\u4e0d\u76f8\u540c\u3002\u4e0a\u6b21\u6709\u767d\u77f3\u9053\u4eba\u4e0e\u94c1\u98de\u9f99\u5148\u6321\u4e24\u9635\uff0c\u8017\u4e86\u7ea2 \u82b1\u9b3c\u6bcd\u4f53\u529b\uff0c\u53c8\u6709\u5cb3\u9e23\u73c2\u7684\u624b\u5957\u62a4\u7740\uff0c\u624d\u8ba9\u7389\u7f57\u5239\u6361\u4e86\u4fbf\u5b9c\uff0c\u8fd9\u56de\u5374\u662f\u53cc\u65b9\u90fd\u7528\u672c\u529b\u62da\uff0c\u7389 \u7f57\u5239\u5251\u62db\u867d\u72e0\uff0c\u8f7b\u529f\u867d\u5999\uff0c\u5185\u5bb6\u771f\u529b\u4e0d\u5982\u5bf9\u65b9\uff0c\u62da\u4e00\u4e45\uff0c\u60ed\u89c9\u547c\u5438\u7d27\u4fc3\uff0c\u5904\u5728\u4e0b\u98ce\u3002 \u3000\u3000\u5353\u4e00\u822a\u7126\u6025\u65e0\u8ba1\uff0c\u8981\u63d2\u624b\u4e5f\u63d2\u4e0d\u8fdb\u53bb\uff0c\u84e6\u542c\u5f97\u7ea2\u82b1\u9b3c\u6bcd\u559d\u9053\uff1a\u201c\u7740\uff01\u201d\u9f99\u5934\u62d0\u6756\u5f80\u4e0a\u4e00 \u62bd\uff0c\u987a\u52bf\u53cd\u5c55\uff0c\u75be\u5982\u95ea\u7535\uff0c\u628a\u7389\u7f57\u5239\u7684\u5b9d\u5251\u538b\u5728\u4e0b\u9762\uff0c\u5de6\u638c\u53cd\u624b\u4e00\u626b\uff0c\u63b4\u5411\u7389\u7f57\u5239\u9762\u95e8\uff01\u5973 \u55bd\u5175\u60ca\u547c\u4e2d\u5ffd\u542c\u5f97\u7389\u7f57\u5239\u4e00\u58f0\u5a07\u7b11\uff1a\u201c\u4e0d\u89c1\u5f97\uff01\u201d\u4e5f\u4e0d\u77e5\u5979\u4f7f\u4e2a\u4ec0\u9ebd\u8eab\u6cd5\uff0c\u5728\u95f4\u4e0d\u5bb9\u53d1\u4e4b \u9645\uff0c\u5c45\u7136\u4ece\u7ea2\u82b1\u9b3c\u6bcd\u6756\u5e95\u94bb\u51fa\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u4ee5\u7259\u8fd8\u7259\uff0c\u5251\u5c16\u53c8\u6307\u5230\u7ea2\u82b1\u9b3c\u6bcd\u5fc3\u7a9d\u3002\u539f\u6765\u7389\u7f57 \u5239\u81ea\u79d8\u9b54\u5d16\u4e00\u6218\u4e4b\u5f8c\uff0c\u628a\u7ea2\u82b1\u9b3c\u6bcd\u8ba4\u4e3a\u5e73\u751f\u52b2\u654c\uff0c\u82e6\u5fc3\u79ef\u8651\u8981\u7834\u5979\u7684\u6756\u6cd5\uff0c\u867d\u56e0\u5185\u5bb6\u771f\u529b\u4e0d \u5982\u5bf9\u65b9\uff0c\u7834\u5979\u4e0d\u4e86\uff0c\u4f46\u5bf9\u5979\u7684\u6756\u6cd5\u8def\u9053\u5df2\u7ecf\u6478\u719f\uff0c\u4e34\u5371\u4e4b\u9645\uff0c\u4ed7\u7740\u8f7b\u529f\u5353\u7edd\uff0c\u5728\u5979\u4e24\u62db\u76f8\u63a5 \u4e4b\u9645\uff0c\u9aa4\u7136\u9003\u51fa\uff01 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u6ee1\u4ee5\u4e3a\u8fd9\u4e00\u4e0b\u7389\u7f57\u5239\u7edd\u96be\u9003\u907f\uff0c\u90a3\u6599\u4ecd\u7136\u7ed9\u5979\u9003\u8131\uff0c\u4e0d\u89c9\u8d77\u4e86\u7231\u624d\u4e4b\u5fc3\uff0c\u60f3 \u9053\uff1a\u201c\u8fd9\u5973\u5a03\u5b50\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u80fd\u7ec3\u5230\u8fd9\u822c\u672c\u9886\uff0c\u4e5f\u771f\u4e0d\u5bb9\u6613\uff01\u8981\u5979\u4e0d\u66fe\u628a\u6211\u90a3\u8d3c\u6c49\u5b50\u6740\u5bb3\uff0c\u6211 \u8fd8\u53ef\u94d9\u5979\u3002\u201d\u62d0\u6756\u4e00\u6c64\uff0c\u628a\u7389\u7f57\u5239\u7684\u5b9d\u5251\u6c64\u5f00\uff0c\u53cc\u65b9\u7f13\u4e86\u4e00\u7f13\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u559d\u9053\uff1a\u201c\u6211\u90a3\u8d3c\u6c49 \u5b50\u662f\u6b7b\u662f\u6d3b\uff1f\u4f60\u8bf4\u4e0d\u8bf4\uff1f\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u4ed6\u662f\u6b7b\u662f\u6d3b\u3002\u6211\u600e\u77e5\u9053\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u6c14\u5f80\u4e0a\u51b2\uff0c \u9053\uff1a\u201c\u4e0d\u662f\u4f60\u628a\u4ed6\u523a\u4f24\u4e86\u9ebd\uff1f\u4f60\u600e\u9ebd\u4e0d\u77e5\u9053\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u662f\u6211\u628a\u4ed6\u523a\u4f24\u4e86\uff0c\u4ed6\u7ed9 \u6211\u523a\u4f24\u4e4b\u65f6\uff0c\u5f53\u7136\u8fd8\u662f\u6d3b\u7740\uff0c\u73b0\u5728\u662f\u6b7b\u662f\u6d3b\uff0c\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\uff1f\u201d \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u5fc3\u5934\u4e00\u75bc\uff0c\u4ee5\u4e3a\u4e08\u592b\u662f\u88ab\u7389\u7f57\u5239\u64d2\u4e86\uff0c\u4f24\u91cd\u5c06\u6b7b\uff0c\u6240\u4ee5\u7389\u7f57\u5239\u5982\u6b64\u8bf4\u6cd5\u3002\u5927\u53eb \u9053\uff1a\u201c\u4f60\u4e0e\u6211\u5230\u5be8\u91cc\u53bb\u770b\uff0c\u82e5\u4ed6\u672a\u6b7b\uff0c\u8d76\u5feb\u65bd\u6551\uff0c\u82e5\u7136\u6b7b\u4e86\uff0c\u54fc\uff0c\u90a3\u53ef\u5f97\u8981\u4f60\u7684\u547d\u62b5\u507f\u3002\u201d \u7389\u7f57\u5239\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6709\u672c\u4e8b\u5c31\u81ea\u5df1\u8fdb\u53bb\uff01\u201d\u6a2a\u5251\u5f53\u80f8\uff0c\u84c4\u52bf\u5f85\u53d1\u3002\u5353\u4e00\u822a\u53c8\u6025\u53eb\u9053\uff1a\u201c\u91d1\u8001 \u524d\u8f88\u786e\u662f\u4e0d\u5728\u8fd9\u513f\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u76ee\u559d\u9053\uff1a\u201c\u5728\u90a3\u91cc\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4ed6\u6628\u665a\u4e2d\u4e86\u4e00\u5251\uff0c\u6eda\u4e0b \u5c71\u5761\uff0c\u60f3\u662f\u56de\u5230\u57ce\u4e2d\u627e\u6155\u5bb9\u51b2\u53bb\u4e86\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u6155\u5bb9\u51b2\u5c31\u5728\u5916\u9762\u5c71\u8c37\uff0c\u73b0\u5728\u88ab \u96ea\u5d29\u6240\u963b\uff0c\u7b49\u4e0b\u4fbf\u5230\uff0c\u4ed6\u82e5\u56de\u5230\u57ce\u4e2d\uff0c\u6155\u5bb9\u51b2\u600e\u4f1a\u8fd8\u5230\u8fd9\u91cc\u6551\u4ed6\uff1f\u201d\u7389\u7f57\u5239\u5fc3\u4e2d\u4e00\u9707\uff0c\u5fc3 \u9053\uff1a\u201c\u6211\u56fe\u81ea\u5df1\u75db\u5feb\uff0c\u4e0e\u5979\u6253\u67b6\u73a9\u800d\uff0c\u4e0d\u6599\u6155\u5bb9\u51b2\u4ed6\u4eec\u6740\u6765\uff0c\u6015\u73ca\u745a\u59b9\u59b9\u88ab\u4ed6\u4eec\u8ffd\u5230\uff0c\u73ca\u745a \u59b9\u59b9\u53ef\u4e0d\u662f\u4ed6\u4eec\u5bf9\u624b\u3002\u201d\u6025\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u9ebd\u9a6c\u4e0a\u627e\u6155\u5bb9\u51b2\u5bf9\u8d28\uff0c\u5c82\u4e0d\u662f\u7701\u4e8b\u5f97\u591a\uff01\u201d \u7ea2\u82b1\u9b3c\u6bcd\u51b7\u7b11\u9053\uff1a\u201c\u6551\u4eba\u5982\u6551\u706b\uff0c\u4ed6\u7ed9\u4f60\u7684\u5251\u523a\u4f24\u7a74\u9053\u8981\u5bb3\uff0c\u6211\u90a3\u6709\u95f2\u529f\u592b\u548c\u4f60\u53bb\u627e\u6155\u5bb9 \u51b2\uff01\u201d\u7389\u7f57\u5239\u6021\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u523a\u4f24\u4ed6\u7684\u7a74\u9053\u8981\u5bb3\u4e86\uff1f\u4f60\u7684\u6c49\u5b50\u6b66\u529f\u4e5f\u975e\u5e73\u5eb8\u4e4b\u8f88\uff0c \u8001\u5b9e\u8bf4\uff0c\u6211\u662f\u60f3\u523a\u4ed6\u7684\u7a74\u9053\u8981\u5bb3\u7684\uff0c\u53ef\u662f\u4ed6\u95ea\u5f97\u5012\u5feb\uff0c\u5927\u7ea6\u662f\u7ed9\u5251\u5c16\u523a\u4f24\u811a\u8e1d\uff0c\u4f60\u6025\u4ec0 \u9ebd\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u4f60\u8bdd\u5f53\u771f\uff1f\u4ed6\u786e\u662f\u4e0d\u5728\u8fd9\u91cc\u4e86\u54fc\uff0c\u7389\u7f57\u5239\u4f60\u53ef\u522b\u9a97\u4eba\u5566\uff0c\u4eca\u671d\u6211\u95ee\u4f60 \u65f6\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u63d0\u4ed6\u53d7\u4f24\u4e4b\u4e8b\uff1f\u201d\u7389\u7f57\u5239\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u70b9\u5c0f\u4e8b\uff0c\u4e5f\u503c\u5f97\u63d0\uff1f\u6211\u95ee\u4f60\uff0c\u4f60\u5931 \u62db\u4e22\u4e11\u4e4b\u4e8b\uff0c\u53ef\u613f\u968f\u4fbf\u63d0\u9ebd\uff1f\u201d\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u4ec0\u9ebd\uff1f\u6211\u51e0\u65f6\u5931\u62db\u4e22\u4e11\u4e86\u4e86\u4f60\u662f\u63d0\u4e0a\u6b21\u79d8\u9b54 \u5d16\u4e4b\u4e8b\u9ebd\uff1f\u90a3\u6b21\u4f60\u4eec\u662f\u8f66\u8f6e\u6218\uff0c\u4e0d\u80fd\u7b97\u6570\uff01\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u6211\u662f\u6253\u4e2a\u6bd4\u65b9\uff0c\u4f60\u7684\u6c49\u5b50\uff0c\u76ee \u524d\u6b66\u529f\u5df2\u8fdc\u4e0d\u5982\u6211\uff0c\u6211\u8fd8\u523a\u4e0d\u4e2d\u4ed6\u7a74\u9053\u8981\u5bb3\uff0c\u4e0d\u662f\u5931\u62db\u4e22\u4e11\u9ebd\uff1f\u63d0\u8d77\u6765\u6211\u90fd\u4e0d\u597d\u610f\u601d\u3002\u201d\u7ea2 \u82b1\u9b3c\u6bcd\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u5c45\u7136\u5982\u6b64\u81ea\u8d1f\uff01\u201d\u4f46\u8fd9\u9ebd\u4e00\u8bf4\u5979\u5012\u76f8\u4fe1\u4e86\u3002\u9053\uff1a \u201c\u597d\uff0c\u90a3\u9ebd\u54b1\u4eec\u9a6c\u4e0a\u53bb\u770b\uff01\u201d \u3000\u3000\u4e0d\u6599\u7389\u7f57\u5239\u5374\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e0d\u6210\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u8be7\u9053\uff1a\u201c\u4e0d\u662f\u4f60\u81ea\u5df1\u8bf4\u8981\u627e\u6155\u5bb9\u51b2\u5bf9\u8d28\u7684 \u9ebd\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4e0d\u9519\u4e8c\u4f46\u4f60\u6253\u584c\u6211\u7684\u5be8\u95e8\uff0c\u53ef\u5f97\u5411\u6211\u8d54\u793c\uff0c\u81f3\u65bc\u91cd\u4fee\u4e4b\u4e8b\uff0c\u90a3\u6211\u53ef\u8ba9\u4f60 \u89c1\u4e86\u6155\u5bb9\u51b2\u5bf9\u8d28\u4e4b\u5f8c\u518d\u8bf4\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u6c14\u5f80\u4e0a\u51b2\uff0c\u62d0\u6756\u4e00\u987f\uff0c\u9053\uff1a\u201c\u7389\u7f57\u5239\uff0c\u4f60\u5bf9\u6211\u5982\u6b64\u620f \u4fae\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u662f\u4e00\u5be8\u4e4b\u4e3b\uff0c\u6253\u584c\u6211\u7684\u5be8\u95e8\uff0c\u5c31\u7b49\u5982\u63a8\u7ffb\u7687\u5e1d\u7684\u9f99\u5e8a\uff0c\u6495\u788e\u9556\u5c40\u7684\u9556 \u65d7\uff0c\u4f60\u61c2\u4e0d\u61c2\u6c5f\u6e56\u89c4\u77e9\u4e86\u8d76\u5feb\u8d54\u793c\uff0c\u54b1\u4eec\u597d\u53bb\u627e\u4eba\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u4e00\u6014\uff0c\u6c5f\u6e56\u4e0a\u7684\u89c4\u77e9\u786e\u662f\u5982 \u6b64\u3002\u53ef\u662f\u4e8b\u672a\u5206\u660e\uff0c\u4e08\u592b\u5728\u4e0d\u5728\u5979\u7684\u5be8\u4e2d\u5c1a\u672a\u53ef\u77e5\uff0c\u600e\u62c9\u5f97\u4e0b\u8fd9\u4e2a\u9762\u5b50\uff0c\u5411\u5979\u4f4e\u5934\u8d54\u793c\u5341\u6148 \u9053\uff1a\u201c\u4f60\u8981\u6211\u8d54\u793c\u9ebd\u4e86\u884c\u201c\u4f60\u518d\u6765\u6597\u6597\u6211\u8fd9\u679d\u62d0\u6756\uff0c\u6211\u7684\u62d0\u6756\u82e5\u7136\u4f4e\u5934\uff0c\u6211\u4e5f\u5411\u4f60\u4f4e\u5934\u3002\u201d \u5353\u4e00\u822a\u5927\u6025\uff0c\u9887\u602a\u7389\u7f57\u5239\u8282\u5916\u751f\u679d\uff0c\u90a3\u6599\u7389\u7f57\u5239\u5f3a\u9879\u4e4b\u6853\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u9ebd\u54b1\u4eec\u5c31\u518d\u6597\u53c1\u767e \u62db\uff01\u4e00\u822a\uff0c\u4f60\u5230\u524d\u5c71\u53bb\u770b\u770b\u73ca\u745a\u59b9\u662f\u4e0d\u662f\u56de\u6765\u4e86\uff1f\u201d \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u5927\u6012\uff0c\u62d0\u6756\u4e00\u6325\uff0c\u4e00\u62db\u201c\u5e73\u6c99\u843d\u96c1\u201d\uff0c\u626b\u8170\u51fb\u817f\u3002\u7389\u7f57\u5239\u53eb\u9053\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d \u970d\u5730\u6643\u8eab\u4e0a\u8df3\uff0c\u9f99\u5934\u62d0\u6756\u5728\u5979\u811a\u4e0b\u4e00\u63a0\u800c\u8fc7\u3002\u7389\u7f57\u5239\u8eab\u5b50\u60ac\u7a7a\uff0c\u62db\u6570\u5374\u4e1d\u6beb\u4e0d\u7f13\uff0c\u4e00\u62db\u201c\u767d \u8679\u8d2f\u65e5\u201d\uff0c\u51cc\u7a7a\u4e0b\u51fb\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u6a2a\u6756\u4e00\u6321\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u5251\u62d0\u76f8\u4ea4\uff0c\u7389\u7f57\u5239\u6574\u4e2a\u8eab\u5b50\u53cd\u5f39\u8d77 \u6765\uff0c\u8d81\u52bf\u659c\u63a0\u51fa\u6570\u4e08\u4e4b\u5916\u3002\u5ffd\u542c\u5f97\u9635\u9635\u7bab\u58f0\uff0c\u9690\u9690\u4f20\u6765\uff0c\u97f3\u7ec6\u800c\u6e05\uff0c\u4fe8\u82e5\u6e38\u4e1d\u8885\u7a7a\uff0c\u82e5\u65ad\u82e5 \u7eed\uff0c\u4f3c\u4ece\u5929\u5916\u4f20\u6765\uff0c\u53c8\u4f3c\u4e91\u95f4\u8bd5\u4e0b\uff0c\u7389\u7f57\u5239\u9762\u8272\u500f\u53d8\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u4e00\u62d0\u6253\u6765\uff0c\u7389\u7f57\u5239\u4e00\u95ea\u95ea \u5f00\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u8d54\u793c\u4e4b\u4e8b\uff0c\u4e5f\u53ef\u8ba9\u4f60\u5174\u6155\u5bb9\u51b2\u5bf9\u8d28\u4e4b\u5f8c\u518d\u8bf4\u3002\u201d\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u6211\u662f\u4efb\u4f60 \u620f\u800d\u7684\u5417\uff1f\u201d\u4e3e\u62d0\u6b32\u51fb\uff0c\u7bab\u58f0\u6e05\u8d8a\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u4e5f\u542c\u5230\u4e86\uff0cQ;\u89c9\u90a3\u7bab\u58f0\u4e2d\u4f3c\u5408\u7740\u65e0\u9650\u54c0\u6028\uff0c\u53c8 \u4f3c\u975e\u5e38\u6fc0\u6124\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u5fc3\u5934\u4e00\u9707\uff0c\u4e0d\u89c9\u95ee\u9053\uff1a\u201c\u8c01\u4eba\u5728\u6b64\u5439\u7bab\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u94c1\u98de\u9f99\u7684\u5973 \u513f\u94c1\u73ca\u745a\uff0c\u96ea\u5d29\u5c01\u5c71\uff0c\u5979\u60f3\u5fc5\u662f\u88ab\u56f0\u4f4f\u4e86\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u82e5\u662f\u91d1\u8001\u524d\u8f88\u53d7\u4f24\u4e0d\u91cd\uff0c\u60f3\u5fc5\u4e5f \u4f1a\u4e0e\u6155\u5bb9\u51b2\u540c\u6765\uff0c\u54ce\u5440\uff0c\u4e0d\u597d\uff01\u201d\u4ed6\u662f\u60f3\u5230\u94c1\u73ca\u745a\u5982\u88ab\u56f0\u4f4f\uff0c\u5982\u4f55\u8131\u5f97\u6155\u5bb9\u51b2\u4ed6\u4eec\u7684\u9b54\u638c\u3002 \u7ea2\u82b1\u9b3c\u6bcd\u5fc3\u5934\u4e00\u9707\uff0c\u5fc3\u4e2d\u4e5f\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u54ce\u5440\uff0c\u4e0d\u597d\uff01\u201d\u6697\u9053\uff1a\u6211\u6ee1\u5fc3\u4ee5\u4e3a\u90a3\u8d3c\u6c49\u5b50\u5728\u7389\u7f57 \u5239\u8fd9\u513f\uff0c\u5b8c\u5168\u6ca1\u60f3\u5230\u4ed6\u4f1a\u548c\u6155\u5bb9\u51b2\u540c\u6765\uff0c\u82e5\u7136\u4ed6\u771f\u7684\u6765\u4e86\uff0c\u5251\u4f24\u65b0\u521b\uff0c\u600e\u9003\u5f97\u4e86\u96ea\u5d29\u4e4b\u707e\uff1f \u5ffd\u800c\u53c8\u60f3\u5230\uff1a\u82e5\u7136\u4ed6\u771f\u7684\u6765\u4e86\uff0c\u54ce\u54ce\uff0c\u90a3\u4e0d\u662f\u7389\u7f57\u5239\u6240\u8a00\u975e\u5047\uff0c\u4ed6\u4e00\u51fa\u5bb6\u95e8\u4fbf\u53c8\u5e72\u574f\u4e8b\u4e86\uff1f \u5440\uff01\u90a3\u6211\u600e\u6837\u5411\u7389\u7f57\u5239\u4ea4\u4ee3\uff1f\u4eb2\u624b\u5e9f\u4e86\u4ed6\uff0c\u8fd8\u662f\u4efb\u7531\u7389\u7f57\u5239\u51cc\u8fb1\uff1f\u54fc\u54fc\uff0c\u4e0d\u884c\uff0c\u5230\u5e95\u662f\u51e0\u5341 \u5e74\u592b\u59bb\uff01\u54ce\u5440\uff0c\u4e0d\u884c\uff0c\u5305\u5e87\u4ed6\u4e5f\u4e0d\u884c\uff0c\u90a3\u5c82\u4e0d\u6c38\u8ba9\u6b66\u6797\u7b11\u8bdd\uff1f \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u601d\u6f6e\u8d77\u4f0f\u4e0d\u5b9a\uff0c\u7389\u7f57\u5239\u542c\u4e86\u94c1\u73ca\u745a\u7684\u7bab\u58f0\uff0c\u5fc3\u6025\u5982\u711a\uff0c\u6697\u4e2d\u8d23\u9a82\u81ea\u5df2\uff0c\u4e0d\u5e94\u4e0e \u7ea2\u82b1\u9b3c\u6bcd\u7ea0\u7f20\uff0c\u6643\u5251\u8bd5\u8eab\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u4e0d\u53bb\u6211\u4e5f\u53bb\u4e86\uff01\u4f60\u6709\u539a\u8138\u76ae\uff0c\u5c31\u5728\u8fd9\u91cc\u6b3a\u8d1f\u6211\u7684\u5973\u5175 \u5427\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u9053\uff1a\u201c\u65fa\uff0c\u4e8b\u60c5\u975e\u5230\u6c34\u843d\u77f3\u51fa\uff0c\u4f60\u98de\u5230\u5929\u8fb9\uff0c\u6211\u4e5f\u8ddf\u4f60\uff01\u201d\u62d0\u6756\u70b9\u5730\uff0c\u8eab\u5f62 \u75be\u8d77\uff0c\u7d27\u8ddf\u5728\u7389\u7f57\u5239\u5f8c\u9762\u3002\u5176\u95f4\u53ea\u82e6\u4e86\u4e2a\u5353\u4e00\u822a\uff0c\u8fd0\u7528\u4e86\u5168\u8eab\u672c\u9886\uff0c\u4ecd\u662f\u843d\u5f8c\u6570\u5341\u4e08\u4e4b\u9065\u3002 \u3000\u3000\u518d\u8bf4\u5cb3\u9e23\u73c2\u6628\u665a\u9003\u51fa\u6e05\u865a\u89c2\u5f8c\uff0c\u5c31\u4f0f\u5728\u5c71\u6797\u4e4b\u4e2d\uff0c\u5230\u4e86\u56db\u66f4\u65f6\u4efd\uff0c\u6797\u4e2d\u811a\u6b65\u58f0\u5927\u4f5c\uff0c\u89c1 \u6155\u5bb9\u51b2\u4ed6\u4eec\u4e00\u5927\u5806\u4eba\u90fd\u8d70\u4e0b\u5c71\uff0c\u6bcf\u4eba\u80cc\u7740\u4e00\u540d\u53d7\u4f24\u7684\u540c\u4f34\u3002\u5cb3\u545c\u73c2\u5fc3\u9053\uff1a\u54a6\uff0c\u767d\u77f3\u9053\u4eba\u5c45\u7136 \u8fd8\u4e0d\u9519\u54e9\uff0c\u6155\u5bb9\u51b2\u4ed6\u4eec\u5403\u4e86\u6b66\u5f53\u6d3e\u7684\u5927\u4e8f\u4e86\u3002\u4ed6\u4e0d\u77e5\u7389\u7f57\u5239\u5df2\u7ecf\u6765\u8fc7\u53c8\u53bb\u4e86\uff0c\u56e0\u4e0b\u5c71\u7684\u65b9\u5411 \u4e0d\u540c\uff0c\u6240\u4ee5\u6ca1\u6709\u770b\u89c1\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u8fde\u65e5\u5954\u6ce2\uff0c\u53c8\u5728\u6fc0\u6218\u4e4b\u5f8c\uff0c\u7cbe\u795e\u56f0\u5026\uff0c\u89c1\u6155\u5bb9\u51b2\u4ed6\u4eec\u8d70\u8fdc\uff0c\u677e\u4e86\u53e3\u6c14\uff0c\u5fc3\u9053\uff1a\u6211 \u4e14\u7a0d\u7761\u7247\u65f6\uff0c\u5f85\u5929\u660e\u4e4b\u5f8c\uff0c\u518d\u53bb\u5411\u767d\u77f3\u9053\u4eba\u8bf7\u7f6a\uff0c\u5e76\u4e0e\u5353\u5144\u6700\u5f8c\u9053\u522b\u3002\u4e5f\u4e0d\u77e5\u7761\u4e86\u591a\u5c11\u65f6 \u5019\uff0c\u5ffd\u88ab\u58f0\u97f3\u60ca\u9192\uff0c\u5cb3\u9e23\u73c2\u8eba\u5728\u4e24\u5757\u5ca9\u77f3\u4e4b\u95f4\uff0c\u4ece\u77f3\u9699\u4e2d\u671b\u51fa\uff0c\u53ea\u89c1\u4e00\u4e2a\u76f8\u8c8c\u5947\u4e11\u7684\u8001\u5973 \u4eba\uff0c\u5b89\u8fb9\u63d2\u7740\u4e00\u6735\u5927\u7ea2\u82b1\uff0c\u53e3\u4e2d\u5583\u5583\u6709\u58f0\uff0c\u7eb5\u6b65\u5982\u98de\uff0c\u5411\u57ce\u4e2d\u7684\u65b9\u5411\u5954\u53bb\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u51db\u7136\u4e00\u60ca\uff1a\u83ab\u975e\u6b64\u4eba\u5c31\u662f\u7ea2\u82b1\u9b3c\u6bcd\uff0c\u770b\u5979\u8f7b\u529f\u8d85\u5999\uff0c\u4e0d\u5728\u81ea\u5df1\u4e4b\u4e0b\uff0c\u500f\u773c\u4e0d\u89c1\u3002 \u5cb3\u9e23\u73c2\u8df3\u4e86\u51fa\u6765\uff0c\u6574\u4e86\u8863\u51a0\uff0c\u518d\u4e0a\u5c71\u53bb\u53e9\u6e05\u865a\u89c2\u7684\u5927\u95e8\u3002 \u3000\u3000\u767d\u77f3\u9053\u4eba\u7ed9\u7389\u7f57\u5239\u4e0e\u7ea2\u82b1\u9b3c\u6bcd\u5148\u5f8c\u4e00\u95f9\uff0c\u6b63\u81ea\u6c14\u607c\u975e\u5e38\uff0c\u4e0d\u6599\u7ea2\u82b1\u9b3c\u6bcd\u521a\u8d70\uff0c\u5cb3\u9e23\u73c2\u53c8 \u6765\uff0c\u767d\u77f3\u9053\u4eba\u4e00\u89c1\uff0c\u6012\u4ece\u5fc3\u8d77\uff0c\u5cb3\u9e23\u73c2\u4f9d\u8c12\u524d\u8f88\u4e4b\u793c\uff0c\u5bf9\u767d\u77f3\u9053\u4eba\u62b1\u62f3\u4f5c\u63d6\uff0c\u95ee\u9053\uff1a\u201c\u5353\u5144 \u65e0\u6059\u9ebd\uff1f\u201d\u767d\u77f3\u9053\u4eba\u6012\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u662f\u548c\u7389\u7f57\u5239\u90a3\u5996\u5973\u5728\u4e00\u8d77\u5417\uff1f\u201d\u5cb3\u9e23\u73c2\u9053\uff0e\u5165\u4ec0\u9ebd\uff1f\u201d \u767d\u77f3\u9053\uff1a\u201c\u4f60\u8fd8\u4f5c\u4ec0\u9ebd\u5047\u60fa\u60fa\uff0c\u7389\u7f57\u5239\u628a\u6211\u4eec\u7684\u638c\u95e8\u4eba\u63b3\u53bb\u5566\uff01\u201d\u5cb3\u545c\u73c2\u5947\u9053\uff1a\u201c\u771f\u7684\u4e86\u6709 \u8fd9\u6837\u7684\u4e8b\uff1f\u90a3\u9ebd\u7389\u7f57\u5239\u4e5f\u5728\u5e7f\u5143\u4e86\uff1f\u201d\u767d\u77f3\u9053\u4eba\u8d8a\u53d1\u751f\u6c14\uff0c\u9a82\u9053\uff1a\u201c\u5cb3\u545c\u73c2\uff0c\u4f60\u8fd9\u5c0f\u8f88\u771f\u662f \u80c6\u5927\u5984\u4e3a\uff0c\u4f60\u9677\u5bb3\u6211\u4eec\u7684\u6b66\u5f53\u6d3e\u4e0e\u5b98\u5bb6\u4f5c\u5bf9\u8fd8\u4e0d\u7b97\uff0c\u53c8\u52fe\u7ed3\u7389\u7f57\u5239\u620f\u4fae\u6211\u4eec\uff01\u201d\u638c\u95e8\u4eba\u88ab \u4fd8\uff0c\u90a3\u662f\u4e00\u6d3e\u7684\u5947\u803b\u5927\u539a\uff0c\u6240\u4ee5\u767d\u77f3\u9053\u4eba\u60bb\u60bb\u7136\u89c1\u65bc\u8f9e\u8272\u3002\u5cb3\u9e23\u73c2\u606d\u8170\u7b54\u9053\uff1a\u201c\u6628\u665a\u4e4b\u4e8b\uff0c \u5c0f\u8f88\u8be5\u5411\u4f60\u966a\u7f6a\u3002\u53ea\u662f\u4e0e\u7389\u7f57\u5239\u52fe\u7ed3\u4e4b\u4e8b\uff0c\u90a3\u5374\u662f\u524d\u8f88\u8bef\u4f1a\u4e86\uff01\u201d\u767d\u77f3\u9053\u4eba\u55d6\u7684\u4e00\u58f0\u62d4\u51fa\u957f \u5251\uff0c\u559d\u9053\uff1a\u201c\u5c31\u51ed\u6628\u665a\u4e4b\u4e8b\uff0c\u4f60\u4fbf\u8be5\u5403\u6211\u4e00\u5251\uff01\u8fd9\u6837\u5927\u4e8b\uff0c\u5c82\u662f\u966a\u7f6a\u5f97\u4e86\uff01\u201d\u767d\u77f3\u9053\u4eba\u7684\u8fde \u73af\u593a\u547d\u5251\u6cd5\u8fc5\u6377\u4e4b\u6781\uff0c\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u8fde\u8fdb\u6570\u62db\uff0c\u5cb3\u545c\u73c2\u8feb\u5f97\u62d4\u5251\u4e00\u6321\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u5c06\u767d\u77f3\u9053\u4eba \u7684\u957f\u5251\u9707\u5f00\uff0c\u767d\u77f3\u9053\u4eba\u53eb\u9053\uff1a\u201c\u4f17\u5f1f\u5b50\u8fd8\u4e0d\u901f\u4e0a\uff01\u201d\u5cb3\u9e23\u73c2\u865a\u6643\u4e00\u5251\uff0c\u8df3\u51fa\u5927\u95e8\uff0c\u5982\u98de\u800c \u53bb\uff01\u767d\u77f3\u9053\u4eba\u8ffd\u4e4b\u4e0d\u53ca\uff0c\u597d\u81ea\u5df1\u751f\u6c14\uff01 \u3000\u3000\u5cb3\u545c\u73c2\u81ea\u718a\u5ef7\u5f3c\u6b7b\u5f8c\uff0c\u672c\u5df2\u5fc3\u7070\u610f\u51b7\uff0c\u51e0\u6b21\u53c1\u756a\u60f3\u524a\u53d1\u4e3a\u50e7\uff0c\u5f52\u9690\u5929\u5c71\u3002\u53ea\u56e0\u5fc3\u5934\u4e0a\u8fd8 \u6709\u4e00\u4e2a\u94c1\u73ca\u745a\uff0c\u59d4\u51b3\u4e0d\u4e0b\u3002\u81ea\u90a3\u6b21\u7389\u7f57\u5239\u9c81\u83bd\u63d0\u5a5a\uff0c\u5cb3\u9e23\u73c2\u63aa\u8bcd\u4e0d\u5f53\uff0c\u88ab\u94c1\u98de\u9f99\u7236\u5973\u542c\u5230\uff0c \u94c1\u73ca\u745a\u4e00\u6c14\u800c\u8d70\u4e4b\u5f8c\u3002\u5cb3\u545c\u73c2\u6df1\u81ea\u5f15\u8d23\uff0c\u5185\u759a\u4e4b\u6781\uff0c\u7acb\u8a93\u8981\u627e\u5230\u94c1\u73ca\u745a\u5411\u5979\u966a\u4e00\u53e5\u7f6a\uff0c\u8fd9\u624d \u5fc3\u5b89\u3002\u56e0\u620e\u9a6c\u5306\u5306\uff0c\u6b64\u613f\u65e0\u7531\u5b9e\u73b0\u3002\u800c\u4eca\u542c\u5f97\u7389\u7f57\u5239\u6628\u665a\u51fa\u73b0\uff0c\u60f3\u9053\uff1a\u7389\u7f57\u5239\u65e2\u5728\u6b64\u5730\uff0c\u5979 \u5fc5\u80fd\u77e5\u94c1\u73ca\u745a\u4e0b\u843d\u3002\u5979\u867d\u5174\u6211\u4e0d\u548c\uff0c\u6211\u4e5f\u8981\u627e\u5979\u95ee\u53bb\u3002\u65bc\u662f\u5cb3\u9e23\u73c2\u4e0b\u5c71\u63a2\u95ee\uff0c\u7389\u7f57\u5239\u5728\u660e\u6708 \u5ce1\uff0c\u5e7f\u5143\u7684\u5c45\u6c11\u5341\u4e5d\u77e5\u9053\uff0c\u5cb3\u9e23\u73c2\u95ee\u660e\u4e86\u53bb\u660e\u6708\u5ce1\u7684\u8def\uff0c\u4fbf\u7acb\u523b\u52a8\u8eab\u3002\u5176\u65f6\u7ea2\u82b1\u9b3c\u6bcd\u4e5f\u6b63\u4ece \u57ce\u4e2d\u536b\u6240\u51fa\u6765\uff0c\u5411\u660e\u6708\u5ce1\u524d\u53bb\u3002\u5cb3\u9e23\u73c2\uff0c\u4e0e\u7ea2\u82b1\u9b3c\u6bcd\u4e00\u5148\u4e00\u5f8c\uff0c\u4e24\u4eba\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u5c06\u8fd1\u660e\u6708\u5ce1\u65f6\uff0c\u4e5f\u9065\u89c1\u8c37\u5e95\u8ffd\u654c\u7684\u536b\u58eb\uff0c\u5e76\u89c1\u5c71\u5761\u4e0a\u6709\u9003\u907f\u7684\u5973\u55bd\u5175\uff0c\u5927\u4e3a\u60ca \u5947\uff0c\u622a\u7740\u4e00\u4e2a\u5973\u55bd\u5175\u8be2\u95ee\uff0c\u5973\u55bd\u5175\u89c1\u4ed6\u4e0d\u662f\u536b\u58eb\uff0c\u95ee\u4ed6\u662f\u8c01\u3002\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u6211\u662f\u4f60\u4eec\u7ec3\u5be8\u4e3b \u7684\u670b\u53cb\u3002\u201d\u5973\u55bd\u5175\u9002\u624d\u89c1\u4ed6\u767b\u5c71\u65f6\u8fc5\u903e\u733f\u7334\uff0c\u6599\u662f\u6b66\u6797\u4e2d\u7684\u9ad8\u624b\uff0c\u559c\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u5feb\u53bb\u6551\u6211 \u4eec\u7684\u94c1\u5be8\u4e3b\u5427\uff01\u5979\u88ab\u9e70\u72ac\u6240\u8ffd\uff0c\u6b63\u8fdb\u5165\u90a3\u8fb9\u5c71\u53e3\u3002\u201d\u5cb3\u9e23\u73c2\u8df3\u8d77\u6765\u9053\uff1a\u201c\u8c01\uff1f\u201d\u5973\u55bd\u5175\u9053\uff1a \u201c\u4f60\u4e0d\u8ba4\u5f97\u6211\u4eec\u7684\u94c1\u5be8\u4e3b\u5417\uff1f\u5979\u662f\u897f\u5317\u94c1\u8001\u82f1\u96c4\u7684\u5973\u513f\uff0c\u5c0f\u540d\u53eb\u73ca\u745a\u3002\u201d\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5cb3\u9e23\u73c2 \u5df2\u5982\u98de\u51b2\u53bb\uff0c\u5b9b\u4f3c\u4e00\u56e2\u767d\u5f71\uff0c\u9690\u73b0\u5728\u5371\u5ca9\u4e71\u77f3\u4e4b\u95f4\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u7684\u8f7b\u529f\u5174\u7389\u7f57\u5239\u51e0\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u8ffd\u654c\u7684\u536b\u58eb\u773c\u529b\u597d\u7684\uff0c\u89c1\u5c71\u5761\u4e0a\u4e00\u56e2\u4e1c\u897f\u4e00\u63a0\u5373 \u8fc7\uff0c\u4e5f\u4e0d\u77e5\u662f\u9b3c\u662f\u4eba\uff0c\u66f4\u8bf4\u4e0d\u5230\u6562\u4e0a\u53bb\u62e6\u622a\u4e86\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u5954\u4eba\u7b2c\u4e00\u9053\u5c71\u53e3\u4e4b\u65f6\uff0c\u6b63\u662f\u94c1\u73ca\u745a\u521a\u8e0f\u5165\u7b2c\u53c1\u9053\u5c71\u53e3\uff0c\u7b2c\u4e00\u6b21\u5439\u7bab\u5411\u7389\u7f57\u5239\u62a5\u8b66 \u7684\u65f6\u5019\uff0c\u90a3\u6b21\u5439\u4e86\u51e0\u58f0\uff0c\u4fbf\u88ab\u96ea\u5d29\u6240\u963b\uff0c\u7389\u7f57\u5239\u6ca1\u6709\u542c\u89c1\uff0c\u201c\u7389\u7f57\u5239\u542c\u5230\u7684\u662f\u7b2c\u4e8c\u6b21\u7bab\u58f0\u201d \u4f46\u5cb3\u9e23\u73c2\u5374\u542c\u5230\u4e86\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u4e00\u542c\u7bab\u58f0\uff0c\u5fc3\u4e2d\u72c2\u559c\uff0c\u5583\u5583\u8bed\u9053\uff1a\u201c\u8c22\u5929\u8c22\u5730\uff0c\u679c\u7136\u662f\u5979\uff01\u201d\u731b\u7136\u95f4\u5c71\u8c37\u91cc\u54cd\u8d77 \u5de8\u5927\u7684\u96f7\u9e23\u58f0\uff0c\u4e07\u5cf0\u56de\u5e94\uff0c\u9707\u8033\u6b32\u804b\uff0c\u5cb3\u545c\u73c2\u5728\u897f\u5317\u957f\u5927\uff0c\u77e5\u662f\u96ea\u5d29\uff0c\u6025\u5411\u5c71\u9876\u9ad8\u5904\u8dc3\u53bb\uff0c \u8fc7\u4e86\u4e00\u9635\uff0c\u96ea\u5d29\u6e10\u6b62\uff0c\u5cb3\u9e23\u73c2\u6025\u6025\u8dc3\u8fc7\u51e0\u4e2a\u5cf0\u5934\uff0c\u9065\u89c1\u7b2c\u53c1\u9053\u5c71\u53e3\u5df2\u88ab\u96ea\u5c01\uff0c\u518d\u6781\u76ee\u8fdc\u773a\uff0c \u524d\u65b9\u65e0\u4eba\uff0c\u60f3\u9053\uff1a\u73ca\u745a\u59b9\u59b9\u5fc5\u7136\u662f\u88ab\u56f0\u5728\u4e0b\u9762\u7684\u6df1\u8c37\u4e86\uff0c\u82e5\u7136\u654c\u4eba\u5728\u96ea\u5d29\u4e4b\u524d\u4e5f\u6709\u7a9c\u4eba\uff0c\u90a3 \u53ef\u4e0d\u5999\uff01\u5438\u4e00\u53e3\u6c14\uff0c\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u4ece\u5c71\u9876\u4e0a\u6ed1\u8d70\u4e0b\u6765\uff0c\u5c31\u5728\u6b64\u9645\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u5728\u5c71\u9876\u4e0a\uff0c\u79bb \u4ed6\u6570\u4e08\u4e4b\u5730\u63a0\u8fc7\uff0c\u5cb3\u9e23\u73c2\u542c\u5f97\u98ce\u58f0\uff0c\u6602\u5934\u4e00\u77a5\uff0c\u77e5\u662f\u7ea2\u82b1\u9b3c\u6bcd\uff0c\u9887\u4e3a\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u5979\u624d\u5230\u6e05 \u865a\u89c2\uff0c\u53c8\u6765\u660e\u6708\u5ce1\uff0c\u5954\u5954\u6ce2\u6ce2\uff0c\u4e0d\u77e5\u5374\u662f\u4e3a\u4f55\uff1f\u4f46\u5cb3\u545c\u73c2\u6551\u4eba\u5fc3\u5207\uff0c\u4e5f\u61d2\u5f97\u53bb\u7406\u7ea2\u82b1\u9b3c\u6bcd\uff0c \u624b\u6500\u845b\u85e4\uff0c\u811a\u70b9\u5371\u5ca9\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u6ed1\u5230\u5c71\u8170\uff0c\u5ffd\u542c\u5f97\u6155\u5bb9\u51b2\u5927\u58f0\u559d\u9053\uff1a\u201c\u4e0d\u8bb8\u8d70\u6765\uff01\u201d \u3000\u3000\u5cb3\u9e23\u73c2\u4e00\u773c\u77a7\u53bb\uff0c\u53ea\u89c1\u6155\u5bb9\u51b2\u4e00\u8138\u72de\u7b11\uff0c\u80c1\u4e0b\u631f\u7740\u7684\u6b63\u662f\u4ed6\u671d\u601d\u66ae\u60f3\u7684\u94c1\u73ca\u745a\uff0c\u5cb3\u9e23\u73c2 \u53c8\u60ca\u53c8\u6012\uff0c\u957f\u5251\u500f\u7684\u51fa\u9798\uff0c\u53eb\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u62da\u4e86\uff01\u201d\u6155\u5bb9\u51b2\u63d0\u8d77\u94c1\u73ca\u745a\u8fce\u98ce\u4e00\u6643\uff0c\u7b11\u9053\uff1a \u201c\u5f88\u597d\uff0c\u4f60\u8fdb\u62db\u5427\uff01\u201d\u5cb3\u9e23\u73c2\u53eb\u9053\uff1a\u201c\u4f60\u6562\u4f24\u5979\u4e00\u6839\u6beb\u53d1\uff0c\u4eca\u65e5\u6211\u4e0e\u4f60\u4eec\u53c1\u4eba\u540c\u4e27\u5e7d\u8c37\uff01\u201d \u91d1\u72ec\u5f02\u5ffd\u7136\u558a\u9053\uff1a\u201c\u54b1\u4eec\u4e0b\u53bb\u8bf4\u3002\u201d\u539f\u6765\u91d1\u72ec\u5f02\u811a\u8e1d\u523a\u75db\uff0c\u5e94\u4fee\u9633\u6276\u7740\u4ed6\uff0c\u4e24\u4eba\u90fd\u611f\u5403\u529b\u3002 \u91d1\u72ec\u5f02\u5fc3\u60f3\uff0c\u82e5\u662f\u4e0d\u628a\u88ab\u96ea\u5d29\u5c01\u7740\u7684\u5c71\u53e3\u6398\u51fa\u8def\u6765\uff0c\u8981\u60f3\u751f\u51fa\u6b64\u5c71\uff0c\u53ea\u6015\u6bd4\u767b\u5929\u8fd8\u96be\u3002\u770b\u5cb3 \u9e23\u73c2\u5982\u6b64\u60c5\u6025\uff0c\u4e0d\u5982\u62ff\u94c1\u73ca\u745a\u6765\u8981\u631f\u4ed6\uff0c\u53eb\u4ed6\u4ee3\u81ea\u5df2\u53bb\u592e\u6c42\u7389\u7f57\u5239\uff0c\u6d3e\u5973\u55bd\u5175\u6398\u51fa\u4e00\u6761\u8def \u6765\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u5fc3\u4e2d\u53e6\u6709\u76d8\u7b97\uff1a\u5cb3\u9e23\u73c2\u4e43\u662f\u9b4f\u5fe0\u8d24\u6307\u5b9a\u6240\u8981\u8ffd\u6355\u7684\u4eba\uff0c\u4e0d\u4f46\u6bd4\u94c1\u73ca\u745a\u91cd\u8981\uff0c\u6bd4\u7389 \u7f57\u5239\u4e5f\u91cd\u8981\u5f97\u591a\uff01\u4f46\u5cb3\u9e23\u73c2\u6b66\u529f\u9ad8\u5f3a\uff0c\u81ea\u5df1\u867d\u4e0d\u60e7\u4ed6\uff0c\u6fc0\u6218\u5374\u662f\u96be\u514d\uff0c\u5373\u7b97\u5408\u53c1\u4eba\u4e4b\u529b\u53ef\u4ee5 \u5c06\u4ed6\u64d2\u7740\uff0c\u4f46\u4e5f\u975e\u4e00\u65f6\u534a\u523b\u6240\u80fd\u89e3\u51b3\uff0c\u5018\u82e6\u7389\u7f57\u5239\u5e26\u5175\u6740\u5230\uff0c\u90a3\u53ef\u662f\u9003\u8131\u4e0d\u4e86\u3002\u56e0\u6b64\u4ed6\u4e5f\u60f3 \u62ff\u94c1\u73ca\u745a\u6765\u8981\u631f\u5cb3\u9e23\u73c2\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u968f\u4ed6\u4eec\u53c1\u4eba\u4e0b\u4e86\u5ce1\u8c37\uff0c\u6155\u5bb9\u51b2\u51b7\u7b11\u9053\uff1a\u201c\u5cb3\u9e23\u73c2\uff0c\u4f60\u60f3\u600e\u9ebd\uff1f\u201d\u5cb3\u9e23\u73c2\u89c1\u94c1\u73ca\u745a \u9762\u8272\u60e8\u767d\uff0c\u5934\u53d1\u6563\u4e71\uff0c\u8863\u88f3\u7834\u788e\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5f97\u4e00\u9635\u9635\u96be\u8fc7\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6b3a\u4fae\u5973\u5b50\u7b97\u4ec0\u9ebd\u82f1 \u96c4\uff0c\u4f60\u628a\u5979\u653e\u4e86\uff01\u201d\u6155\u5bb9\u51b2\u51b7\u7b11\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u8bf4\u5f97\u597d\u5bb9\u6613\uff01\u4f60\u8981\u6211\u628a\u5979\u653e\u8d70\uff0c\u9664\u975e\u4f60\u4e56\u4e56\u7684 \u968f\u6211\u56de\u4eac\u9762\u5723\u3002\u201d\u5cb3\u545c\u73c2\u77a7\u4e86\u94c1\u73ca\u745a\u4e00\u773c\uff0c\u6168\u7136\u8bf4\u9053\uff1a\u201c\u968f\u4f60\u4eba\u4eac\uff0c\u672a\u5c1d\u4e0d\u53ef\uff0c\u4e0d\u8fc7\u6211\u8981\u5148 \u77e5\u9053\u5979\u4f24\u52bf\u5982\u4f55\uff1f\u201d \u3000\u3000\u6155\u5bb9\u51b2\u62da\u6307\u4e00\u6233\uff0c\u89e3\u5f00\u94c1\u73ca\u745a\u7684\u7a74\u9053\uff0c\u94c1\u73ca\u745a\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4e0d\u8981\u968f\u4ed6\u8fdb\u4eac\uff01\u201d\u6155\u5bb9\u51b2 \u7b11\u9053\uff1a\u201c\u4f60\u770b\u5979\u4e0d\u662f\u597d\u597d\u7684\uff1f\u54b1\u4eec\u516c\u5e73\u4ea4\u6613\uff0c\u6211\u65ad\u4e0d\u4f1a\u628a\u5979\u5f04\u6210\u6b8b\u5e9f\u6765\u9a97\u4f60\u5165\u4eac\u3002\u201d\u5cb3\u9e23\u73c2 \u773c\u73e0\u4e00\u8f6c\uff0c\u5fc3\u9053\uff1a\u718a\u7ecf\u7565\u7684\u9057\u4e66\u6211\u5df2\u4ea4\u7ed9\u4e86\u5353\u4e00\u822a\uff0c\u5fc3\u4e2d\u5df2\u662f\u522b\u65e0\u7275\u6302\uff0c\u62da\u7740\u4e00\u6b7b\u968f\u4ed6\u5165\u4eac \u4fbf\u4e86\u3002\u53ea\u662f\u73ca\u745a\u59b9\u59b9\u4e0d\u77e5\u6709\u5426\u88ab\u4ed6\u6697\u7b97\uff0c\u5047\u5982\u7ed9\u4ed6\u7528\u5185\u529b\u9707\u64bc\u5fc3\u810f\uff0c\u90a3\u867d\u4fdd\u5f97\u4e00\u65f6\uff0c\u5341\u5929\u534a \u6708\uff0c\u4e5f\u4f1a\u8eab\u4ea1\uff0c\u975e\u5f97\u770b\u6e05\u695a\u4e0d\u53ef\uff0c\u82e5\u7136\u662f\u53d7\u4e86\u4f24\uff0c\u90a3\u5c31\u5f97\u8d76\u5feb\u7ed9\u5979\u6551\u6cbb\u3002\u94c1\u73ca\u745a\u53c8\u53eb\u9053\uff1a \u201c\u5927\u54e5\uff0c\u4e0d\u8981\u4e0a\u4ed6\u7684\u5f53\uff01\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u4f60\u5438\u4e00\u53e3\u6c14\u770b\u770b\uff0c\u770b\u808b\u9aa8\u662f\u5426\u4f5c\u75db\uff1f\u201d\u6155\u5bb9\u51b2\u53eb \u9053\uff1a\u201c\u4f60\u5c82\u6709\u6b64\u7406\uff0c\u6211\u6155\u5bb9\u51b2\u5c82\u662f\u6697\u7b97\u5987\u4eba\u5b7a\u5b50\u4e4b\u4eba\uff01\u201d\u94c1\u73ca\u745a\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5438\u4e86\u53e3\u6c14\uff0c\u6545\u610f \u8bf4\u9053\uff1a\u201c\u597d\u50cf\u6709\u70b9\u75db\u3002\u201d\u6155\u5bb9\u51b2\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4f60\u8bc8\u6b7b\uff01\u201d\u94c1\u73ca\u745a\u9053\uff1a\u201c\u4f60\u8ba9\u6211\u5439\u7bab\u7ed9\u5927 \u54e5\u542c\u542c\u3002\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u4f60\u5439\u7bab\u8bd5\u8bd5\uff0c\u6211\u542c\u542c\u4f60\u7684\u7bab\u58f0\uff0c\u4fbf\u77e5\u4f60\u6709\u6ca1\u6709\u53d7\u5185\u4f24\u4e86\u3002\u201d \u3000\u3000\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u597d\uff0c\u5439\u5427\uff01\u201d\u53eb\u91d1\u72ec\u5f02\u9053\uff1a\u201c\u8fc7\u6765\uff01\u201d\u5c06\u94c1\u73ca\u745a\u62c9\u8fc7\u4e00\u8fb9\uff0c\u5bf9\u91d1\u72ec\u5f02\u9053\uff1a \u201c\u4f60\u770b\u7740\u5979\uff0c\u4e0d\u8981\u8ba9\u5979\u5f04\u9b3c\uff01\u201d\u91d1\u72ec\u5f02\u4e00\u624b\u6309\u5728\u5979\u80a9\u5934\u7435\u7436\u9aa8\u4e0a\uff0c\u4e00\u624b\u62b5\u7740\u5979\u7684\u5f8c\u5fc3\uff0c\u91d1\u72ec \u5f02\u7684\u6bd2\u7802\u638c\u5929\u4e0b\u65e0\u5339\uff0c\u8f7b\u529f\u867d\u56e0\u4f24\u524a\u51cf\uff0c\u638c\u529b\u8fd8\u662f\u96c4\u52b2\u5f02\u5e38\uff0c\u53cc\u638c\u6309\u5728\u94c1\u73ca\u745a\u8981\u5bb3\u4e4b\u5904\uff0c\u53ea \u8981\u5979\u7a0d\u6709\u5f02\u52a8\uff0c\u638c\u529b\u4e00\u53d1\uff0c\u5373\u7b97\u94c1\u73ca\u745a\u6b66\u529f\u518d\u9ad8\u5341\u500d\uff0c\u4e94\u8155\u516d\u8151\u4e5f\u8981\u7ed9\u4ed6\u9707\u88c2\uff01 \u3000\u3000\u6155\u5bb9\u51b2\u653e\u5f00\u4e86\u94c1\u73ca\u745a\uff0c\u62a2\u5728\u91d1\u72ec\u5f02\u4e0e\u5cb3\u545c\u73c2\u4e4b\u95f4\uff0c\u76ef\u7740\u5cb3\u545c\u73c2\u9632\u4ed6\u9aa4\u7136\u53d1\u96be\uff0c\u771f\u53ef\u8bf4\u662f \u9632\u8303\u5f97\u5341\u5206\u4e25\u5bc6\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u5566\uff0c\u8d31\u4e2b\u5934\uff0c\u4f60\u600e\u9ebd\u8fd8\u4e0d\u5439\u5440\uff1f\u201d \u3000\u3000\u94c1\u73ca\u745a\u5fc3\u4e2d\u65e0\u9650\u51c4\u9178\uff0c\u628a\u7389\u7bab\u51d1\u5230\u5507\u8fb9\uff0c\u8f7b\u8f7b\u7684\u5439\u5c06\u8d77\u6765\uff0c\u5176\u58f0\u751a\u7ec6\uff0c\u6e10\u6e10\u8d8a\u5439\u8d8a\u9ad8\uff0c \u7bab\u58f0\u5148\u662f\u4e00\u7247\u6b22\u60a6\u4e4b\u97f3\uff0c\u597d\u50cf\u6625\u6696\u82b1\u5f00\u4e4b\u65e5\uff0c\u548c\u7231\u4fa3\u643a\u624b\u540c\u6e38\uff0c\u5581\u5581\u7ec6\u8bed\u4e00\u822c\u3002\u5cb3\u545c\u73c2\u4e0d\u7531 \u5f97\u60f3\u8d77\u6614\u65e5\u548c\u5979\u4e07\u91cc\u540c\u884c\uff0c\u6625\u90ca\u8bd5\u9a6c\u7684\u60c5\u666f\uff0c\u4e0d\u89c9\u5fc3\u795e\u5982\u9189\u3002\u7bab\u58f0\u4e00\u53d8\uff0c\u5ffd\u5982\u4ece\u8202\u6696\u82b1\u5f00\u7684 \u65f6\u65e5\u5230\u4e86\u6728\u53f6\u6447\u843d\u7684\u6df1\u79cb\uff0c\u6709\u5982\u5b64\u96c1\u54c0\u9e23\uff0c\u5bd2\u8749\u51c4\u5207\uff0c\u5cb3\u545c\u73c2\u60f3\u5230\u5979\u5728\u6c5f\u6e56\u6d6a\u6c64\uff0c\u5b64\u72ec\u53ef \u601c\uff0c\u5fc3\u4e2d\u76ca\u589e\u5185\u759a\u3002\u7bab\u58f0\u518d\u53d8\uff0c\u97f3\u8c03\u8d8a\u9ad8\uff0c\u5176\u58f0\u6108\u82e6\uff0c\u771f\u5982\u9c9b\u4eba\u591c\u6ce3\uff0c\u53c1\u5ce1\u733f\u557c\uff0c\u60b2\u54c0\u4e2d\u53c8 \u9690\u6709\u6124\u6fc0\u4e4b\u60c5\u3002\u5cb3\u545c\u73c2\u60f3\u9053\uff1a\u6211\u771f\u4e0d\u8be5\u62d2\u5979\u5a5a\u4e8b\uff0c\u5f04\u5f97\u5979\u5982\u6b64\u4f24\u5fc3\u3002\u7bab\u58f0\u53c1\u53d8\uff0c\u97f3\u7ec6\u800c\u6e05\uff0c \u5b9b\u5982\u6e38\u4e1d\u8885\u7a7a\uff0c\u79bb\u4eba\u8bdd\u522b\uff0c\u82e5\u65ad\u82e5\u7eed\uff0c\u5982\u6ce3\u5982\u8bc9\uff0c\u53c8\u5982\u542c\u4eba\u54bd\u6cea\u957f\u6b4c\u67f3\u6c38\u7684\u8bcd\uff1a\u201c\u6267\u624b\u76f8\u770b \u6cea\u773c\uff0c\u7adf\u65e0\u8bed\u51dd\u564e\u3002\u5ff5\u53bb\u53bb\u3001\u5343\u91cc\u70df\u6ce2\uff0c\u66ae\u972d\u6c89\u6c89\u695a\u5929\u9614\u3002\u591a\u60c5\u81ea\u53e4\u4f24\u522b\u79bb\uff0c\u66f4\u90a3\u582a\u51b7\u843d\u6e05 \u79cb\u8282\uff01\u4eca\u5bb5\u9152\u9192\u4f55\u5904\uff1f\u6768\u67f3\u5cb8\uff0c\u6653\u98ce\u6b8b\u6708\uff01\u201d\u7bab\u58f0\u5439\u5f97\u4eba\u4eba\u90fd\u89c9\u60b2\u9178\uff0c\u8fde\u6155\u5bb9\u51b2\u90a3\u6837\u7684\u94c1\u77f3 \u5fc3\u80a0\uff0c\u773c\u89d2\u4e5f\u6da6\u6e7f\u4e86\u3002\u5cb3\u9e23\u73c2\u5fc3\u4e2d\u4e00\u7247\u51c4\u82e6\uff0c\u60f3\u9053\uff1a\u600e\u9ebd\u5979\u4f1a\u5439\u51fa\u8fd9\u751f\u6742\u6b7b\u522b\u4e4b\u97f3\uff0c\u55ef\uff0c\u83ab \u975e\u5979\u820d\u4e0d\u5f97\u6211\u53bb\u9001\u6b7b\uff01\u4eba\u751f\u5f97\u4e00\u77e5\u5df1\uff0c\u6b7b\u53ef\u65e0\u61be\u3002\u6211\u662f\u867d\u6b7b\u72b9\u6b22\uff0c\u53ea\u6068\u5979\u8981\u6c38\u751f\u5b64\u72ec\uff01 \u3000\u3000\u7bab\u58f0\u4e0d\u6b47\uff0c\u6155\u5bb9\u51b2\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u5439\u4e86\uff0c\u8fd8\u672a\u591f\u5417\uff1f\u201d \u3000\u3000\u94c1\u73ca\u745a\u5fc3\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\u4e00\u5b9a\u8be5\u542c\u89c1\u4e86\uff01\u201d\u7bab\u58f0\u4e00\u505c\uff0c\u6155\u5bb9\u51b2\u559d\u9053\uff1a\u201c\u5cb3\u545c\u73c2\u4f60\u53ef\u542c\u6e05\u695a \u4e86\uff0c\u5979\u90a3\u6709\u534a\u70b9\u5185\u4f24\u3002\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u597d\uff0c\u4f60\u628a\u5979\u653e\u4e86\uff0c\u6211\u968f\u4f60\u53bb\uff01\u201d\u6155\u5bb9\u51b2\u5ffd\u7136\u7b11\u9053\uff1a \u201c\u4f60\u8fd8\u5f97\u4f9d\u6211\u4e00\u4e8b\u3002\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u4ec0\u9ebd\u4e8b\uff1f\u4f60\u53ef\u4e0d\u8bb8\u8282\u5916\u751f\u679d\u3002\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u7edd\u975e\u8282\u5916 \u751f\u679d\uff0c\u4f60\u66ff\u6211\u628a\u4f60\u81ea\u5df1\u90a3\u53ea\u53f3\u624b\u65a9\u6389\uff01\u201d\u5cb3\u545c\u73c2\u60ca\u53eb\u9053\uff1a\u201c\u4ec0\u9ebd\uff1f\u201d\u6155\u5bb9\u51b2\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60 \u6b66\u529f\u9ad8\u5f3a\uff0c\u7f1a\u4f60\u7f1a\u4e0d\u7262\uff0c\u70b9\u7a74\u4f60\u81ea\u5df1\u53c8\u4f1a\u89e3\uff0c\u4e07\u91cc\u957f\u884c\uff0c\u8001\u7237\u4eec\u53ef\u4e0d\u8010\u70e6\u5c3d\u8d1d\u7ba1\u4f60\uff01\u4f60\u4e0d\u76f8 \u4fe1\u6211\uff0c\u6211\u4e5f\u4e0d\u76f8\u4fe1\u4f60\u3002\u628a\u53f3\u624b\u65a9\u6389\uff0c\u5927\u5bb6\u653e\u5fc3\u3002\u54c8\u54c8\uff0c\u4f60\u6015\u75db\u5417\uff1f\u201d \u3000\u3000\u94c1\u73ca\u745a\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4e0d\u8981\uff0c\u4e0d\u8981\uff01\u4f60\u6b7b\u4e86\u6211\u4e5f\u4e0d\u80fd\u72ec\u6d3b\uff01\u201d\u5cb3\u9e23\u73c2\u53eb\u9053\uff1a\u201c\u73ca\u745a\u59b9 \u59b9\uff0c\u4f60\u7684\u60c5\u610f\u6211\u5fc3\u9886\u4e86\u3002\u4f60\u8fd8\u5e74\u8f7b\uff0c\u5343\u4e07\u8981\u6d3b\u4e0b\u53bb\u3002\u4f60\u548c\u7ec3\u59d0\u59d0\u4e00\u9053\uff0c\u4e0d\u8981\u6302\u5ff5\u6211\u3002\u201d\u6155\u5bb9 \u51b2\u51b7\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u771f\u662f\u60c5\u610f\u7ef5\u7ef5\uff0c\u4f60\u4eec\u8fd8\u6709\u591a\u5c11\u8bdd\u8981\u8bf4\uff1f\u201d\u5cb3\u9e23\u73c2\u53eb\u9053\uff1a\u201c\u541b\u5b50\u4e00\u8a00\uff0c\u5feb\u9a6c \u4e00\u97ad\uff01\u6211\u7531\u4f60\u6446\u5e03\uff0c\u4f60\u53ef\u4e0d\u8bb8\u52a0\u5bb3\u65bc\u5979\uff01\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u8c01\u4eba\u53cd\u6094\uff0c\u8d3b\u7b11\u6b66\u6797\uff01\u201d\u5cb3\u545c\u73c2\u53eb \u58f0\uff1a\u201c\u597d\uff01\u201d\u5de6\u624b\u6267\u5251\uff0c\u5411\u53f3\u624b\u624b\u8155\u4e00\u5251\u5207\u4e0b\uff01 \u3000\u3000\u5ffd\u542c\u5f97\u4e00\u58f0\u60e8\u53eb\uff0c\u5cb3\u545c\u73c2\u51b7\u68ee\u68ee\u7684\u5251\u950b\u5df2\u89e6\u624b\u8155\uff0c\u500f\u5ffd\u505c\u4f4f\uff0c\u53ea\u89c1\u94c1\u73ca\u745a\u4e0e\u91d1\u72ec\u5f02\u90fd\u6eda \u5012\u5730\u4e0b\uff01\u539f\u6765\u94c1\u73ca\u745a\u5439\u7bab\u62a5\u8b66\uff0c\u7528\u7684\u539f\u662f\u7f13\u5175\u4e4b\u8ba1\uff0c\u60f3\u7b49\u7389\u7f57\u5239\u95fb\u58f0\u6765\u6551\uff0c\u90a3\u77e5\u6155\u5bb9\u51b2\u53c8\u60f3 \u51fa\u90a3\u9ebd\u6bd2\u8fa3\u7684\u529e\u6cd5\uff0c\u770b\u770b\u5cb3\u545c\u73c2\u5c31\u8981\u628a\u53f3\u624b\u65a9\u6389\uff0c\u94c1\u73ca\u745a\u5fc3\u9053\uff1a\u201c\u800c\u4eca\u6211\u5df2\u77e5\u4ed6\u76f8\u7231\u4e4b\u6df1\uff0c \u4e0d\u6b7b\u4f55\u5f85\uff1f\u201d\u84e6\u7136\u53d1\u96be\uff0c\u624b\u8098\u5411\u5f8c\u4e00\u649e\uff0c\u56de\u8eab\u4e00\u6309\u7389\u7bab\uff0c\u5f00\u52a8\u673a\u62ec\uff0c\u53c1\u679d\u77ed\u7bad\uff0c\u91d1\u5c04\u8fdb\u91d1\u72ec \u5f02\u8eab\u4e2d\uff0c\u94c1\u73ca\u745a\u662f\u540d\u6b66\u5bb6\u4e4b\u5973\uff0c\u6b66\u529f\u867d\u975e\u4e0a\u4e0a\uff0c\u5374\u6709\u6740\u624b\u7edd\u62db\uff0c\u8fd9\u4e00\u4e0b\uff0c\u8098\u649e\u5fc3\u7a9d\uff0c\u7bad\u4f24\u8981 \u5bb3\uff0c\u9976\u662f\u91d1\u72ec\u5f02\u5185\u529f\u6df1\u6e5b\uff0c\u6b66\u827a\u9ad8\u5f3a\uff0c\u4e5f\u75db\u5f97\u773c\u775b\u53d1\u9ed1\uff0c\u638c\u529b\u4e00\u53d1\uff0c\u4e24\u4eba\u90fd\u53d7\u4e86\u91cd\u4f24\uff0c\u6eda\u5012 \u5730\u4e0a\u3002\u94c1\u73ca\u745a\u5012\u5728\u5730\u4e0a\uff0c\u72b9\u81ea\u5389\u58f0\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u8981\u95ef\u51fa\u53bb\uff0c\u65e5\u5f8c\u4e3a\u6211\u62a5\u4ec7\uff0c\u54b1\u4eec\u6765\u751f\u518d \u89c1\uff01\u201d \u3000\u3000\u5cb3\u545c\u73c2\u4e00\u75db\u6b32\u7edd\uff0c\u91d1\u72ec\u5f02\u5fcd\u75db\u8dc3\u8d77\uff0c\u5cb3\u9e23\u73c2\u731b\u7136\u53eb\u9053\uff1a\u201c\u62a5\u4ec7\u4fbf\u5728\u4eca\u65e5\uff01\u201d\u957f\u5251\u4e00\u7ffb\uff0c \u5954\u6740\u8fc7\u53bb\uff0c\u6155\u5bb9\u51b2\u4e00\u62f3\u6363\u51fa\uff0c\u89c1\u5cb3\u9e23\u73c2\u53cc\u773c\u901a\u7ea2\uff0c\u52bf\u5982\u75af\u864e\uff0c\u4e00\u62f3\u51fb\u7a7a\uff0c\u7acb\u5373\u95ea\u907f\uff0c\u5cb3\u545c\u73c2 \u8eab\u968f\u5251\u8d70\uff0c\u75be\u82e5\u60ca\u98d5\uff0c\u91d1\u72ec\u5f02\u521a\u521a\u8d77\u7acb\uff0c\u5cb3\u9e23\u73c2\u5927\u559d\u4e00\u58f0\uff1a\u201c\u62ff\u8fc7\u5934\u6765\uff01\u201d\u817e\u8d77\u4e00\u811a\uff0c\u628a\u91d1 \u72ec\u5f02\u8e22\u7ffb\uff0c\u6155\u5bb9\u51b2\u8d76\u6765\u76f8\u6551\uff0c\u5df2\u662f\u4e0d\u53ca\uff0c\u53ea\u542c\u5f97\u91d1\u72ec\u5f02\u60e8\u53eb\u4e00\u58f0\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u91d1\u72ec\u5f02\u7684\u5934\u9885 \u5df2\u62ff\u5728\u5cb3\u545c\u73c2\u624b\u4e2d\uff01 \u3000\u3000\u6155\u5bb9\u51b2\u5927\u5403\u4e00\u60ca\uff0c\u5cb3\u9e23\u73c2\u957f\u5251\u6740\u5230\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8981\u6211\u56de\u4eac\u9762\u5723\uff0c\u6211\u8981\u4f60\u5230\u9ec4\u6cc9\u53bb\u89c1\u960e \u738b\uff0c\u201d\u957f\u5251\u98ce\u7ffb\u4e91\u6d8c\uff0c\u7740\u7740\u51f6\u8fa3\uff0c\u6155\u5bb9\u51b2\u89c1\u4ed6\u62da\u547d\u76f8\u6251\uff0c\u77e5\u9053\u4eca\u65e5\u4e4b\u4e8b\uff0c\u975e\u6b7b\u6597\u4e0d\u80fd\u8131\u8eab\uff0c \u4e5f\u8c41\u4e86\u6027\u547d\uff0c\u7384\u529f\u5185\u8fd0\uff0c\u53cc\u62f3\u654c\u4e00\u5251\uff0c\u5728\u9c9c\u8840\u67d3\u7ea2\u7684\u5ce1\u8c37\u6076\u6597\u8d77\u6765\uff1f \u3000\u3000\u4e24\u4eba\u529f\u529b\u6089\u654c\uff0c\u5cb3\u545c\u73c2\u53d1\u5251\u4f3c\u6e38\u9f99\uff0c\u6155\u5bb9\u51b2\u51fa\u62f3\u5982\u864e\u8c79\uff0c\u970e\u5ffd\u6597\u4e86\u4e8c\u53c1\u5341\u62db\uff0c\u5cb3\u9e23\u73c2\u62da \u4e86\u4e00\u6b7b\uff0c\u7740\u7740\u62a2\u653b\uff0c\u6155\u5bb9\u51b2\u4e0d\u89c9\u5fc3\u602f\u3002\u5e94\u4fee\u9633\u5728\u65c1\u8fb9\u770b\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u6211\u82e5\u8eab \u6b7b\uff0c\u4f60\u7109\u80fd\u72ec\u81ea\u9003\u751f\uff01\u201d\u7528\u610f\u662f\u53eb\u4ed6\u76f8\u52a9\uff0c\u90a3\u77e5\u5e94\u4fee\u9633\u88ab\u4ed6\u4e00\u8a00\u60ca\u9192\uff0c\u5fc3\u9053\uff1a\u201c\u770b\u8fd9\u5cb3\u9e23\u73c2 \u52bf\u5982\u75af\u864e\uff0c\u4e0d\u987e\u547d\u7684\u62da\uff0c\u6211\u4fbf\u4e0a\u524d\u76f8\u52a9\uff0c\u4e5f\u672a\u5fc5\u80fd\u591f\u80dc\u4ed6\u3002\u4f55\u51b5\u8fd8\u8981\u62c5\u5fc3\u7389\u7f57\u5239\u6740\u6765\uff0c\u6b64\u65f6 \u4e0d\u8d70\uff0c\u66f4\u5f85\u4f55\u65f6\uff1f\u201d\u624b\u811a\u5e76\u7528\uff0c\u6500\u4e0a\u5ced\u58c1\uff0c\u6155\u5bb9\u51b2\u6c14\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u5cb3\u545c\u73c2\u8d8a\u653b\u8d8a\u731b\uff0c\u6155\u5bb9\u51b2 \u5c31\u662f\u60f3\u8d70\u4e5f\u8131\u4e0d\u4e86\u8eab\u3002 \u3000\u3000\u518d\u8bf4\u7389\u7f57\u5239\u548c\u7ea2\u82b1\u9b3c\u6bcd\u4e00\u5148\u4e00\u5f8c\uff0c\u6765\u5230\u524d\u9762\u5c71\u5cf0\uff0c\u7389\u7f57\u5239\u6765\u5feb\u4e00\u6b65\uff0c\u542c\u5f97\u4e0b\u9762\u6740\u4e4b\u58f0\uff0c \u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u8eab\u5b50\u817e\u7a7a\u4e0b\u8dc3\uff0c\u770b\u770b\u8981\u78b0\u7740\u7a81\u51fa\u6765\u7684\u77f3\u5757\uff0c\u5251\u5c16\u4e00\u70b9\uff0c\u53c8\u817e\u7a7a\u800c\u8d77\uff0c\u518d\u5f80\u4e0b \u843d\uff0c\u5982\u6b64\u51e0\u756a\u75bc\u8dc3\uff0c\u5df2\u5230\u5c71\u8170\uff0c\u5e94\u4fee\u9633\u521a\u521a\u7a9c\u4e0a\uff0c\u7389\u7f57\u5239\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u90a3\u5439\u5728\u534e\u5c71\u7edd\u9876\uff0c\u88ab \u4f60\u9003\u751f\uff0c\u4eca\u56de\u4f60\u53ef\u9003\u4e0d\u4e86\uff01\u201d\u5e94\u4fee\u9633\u5fc3\u80c6\u4ff1\u5bd2\uff0c\u62c2\u5c18\u4e00\u7ed5\uff0c\u7f20\u5251\u659c\u95ea\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u54c8\uff0c\u4f60 \u8fd8\u8981\u52a8\u624b\uff01\u201d\u5251\u628a\u4e00\u6c89\uff0c\u4e00\u7f15\u5bd2\u5149\uff0c\u75be\u5982\u7535\u63a3\uff0c\u4e0d\u67b6\u654c\u62db\uff0c\u53cd\u622a\u654c\u8155\uff0c\u5e94\u4fee\u9633\u5728\u5e73\u5730\u4e0a\u5c1a\u8fdc \u975e\u7389\u7f57\u5239\u4e4b\u654c\uff0c\u4f55\u51b5\u73b0\u5728\u9762\u4e34\u6df1\u8c37\uff0c\u8eab\u5728\u5371\u5ca9\uff0c\u5fc3\u4e2d\u4e00\u614c\uff0c\u811a\u4e0b\u4e00\u6ed1\uff0c\u7389\u7f57\u5239\u7684\u5251\u950b\u5c1a\u672a\u89e6 \u53ca\u4ed6\u7684\u8eab\u4f53\uff0c\u4ed6\u5df2\u5495\u549a\u549a\u76f4\u8dcc\u4e0b\u53bb\u3002\u7389\u7f57\u5239\u4e00\u7b11\u8dc3\u4e0b\uff0c\u653e\u773c\u4e00\u770b\uff0c\u4e0d\u89c9\u5927\u5403\u4e00\u60ca\uff01 \u3000\u3000\u8352\u8c37\u4e2d\u53ea\u89c1\u6155\u5bb9\u51b2\u4e0e\u5cb3\u545c\u73c2\u62da\u547d\u6251\uff0c\u4e00\u5177\u65e0\u5934\u8eab\u6a2a\u5728\u4e71\u77f3\u8305\u8349\u4e4b\u4e2d\uff0c\u79bb\u8eab\u4e0d\u8fdc\u4e4b\u5904\uff0c\u94c1 \u73ca\u745a\u6251\u5366\u5730\u4e0a\u3002\u7389\u7f57\u5239\u53eb\u9053\uff1a\u201c\u73ca\u745a\u59b9\u59b9\u3002\u201d\u5954\u8fc7\u53bb\u5c06\u94c1\u73ca\u745a\u7684\u8eab\u8eaf\u7ffb\u8f6c\uff0c\u53ea\u542c\u5f97\u4e00\u58f0\u5fae\u5f31 \u7684\u53f9\u58f0\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u4f60\u6765\u8fdf\u4e86\u3002\u70e6\u4f60\u544a\u8bc9\u6211\u7239\uff0c\u53eb\u4ed6\u4e0d\u8981\u6302\u5ff5\u6211\u3002\u201d \u3000\u3000\u94c1\u73ca\u745a\u58f0\u97f3\u867d\u7136\u5fae\u5f31\uff0c\u5cb3\u545c\u73c2\u542c\u4e86\uff0c\u5374\u5982\u95fb\u6625\u96f7\u590d\u82cf\u4e4b\u58f0\uff0c\u5fc3\u9053\uff1a\u201c\u5514\uff0c\u5979\u8fd8\u672a\u6b7b\uff01\u201d \u64a4\u5251\u56de\u8eab\uff0c\u5411\u94c1\u73ca\u745a\u75be\u8dd1\u8fc7\u53bb\u3002\u6155\u5bb9\u51b2\u6b63\u60f3\u8dc3\u4e0a\u5c71\u58c1\uff0c\u89c1\u5c71\u4e0a\u7ea2\u82b1\u4e00\u95ea\uff0c\u6025\u5fd9\u4ece\u53e6\u4e00\u9762\u767b \u5c71\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\uff0c\u4f60\u53bb\u8ffd\u6155\u5bb9\u51b2\uff0c\u8ba9\u6211\u770b\u770b\u73ca\u745a\u59b9\u59b9\u3002\u201d\u7389\u7f57\u5239\u51c4\u7136\u4e00\u7b11\uff0c\u62b1\u8d77\u94c1 \u73ca\u745a\u653e\u5728\u5cb3\u545c\u73c2\u6000\u4e2d\u3002 \u3000\u3000\u5cb3\u9e23\u73c2\u8f7b\u543b\u94c1\u73ca\u745a\u7684\u773c\u76ae\uff0c\u53eb\u9053\uff1a\u201c\u73ca\u745a\u59b9\u59b9\uff0c\u4f60\u5f20\u5f00\u773c\u775b\u770b\u770b\uff0c\u6211\u5728\u8fd9\u513f\u3002\u201d\u94c1\u73ca\u745a \u661f\u7738\u534a\u505a\uff0c\u5fae\u7b11\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u5f88\u9ad8\u5174\u3002\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u6211\u5bf9\u4e0d\u4f4f\u4f60\uff0c\u6211\u6765\u8fdf\u4e86\uff01\u201d\u94c1\u73ca \u745a\u9053\uff1a\u201c\u4f60\u6ca1\u6765\u8fdf\uff0c\u662f\u6211\u8981\u5148\u8d70\u4e86\u3002\u201d\u94c1\u73ca\u745a\u88ab\u91d1\u72ec\u5f02\u638c\u529b\u9707\u88c2\u5fc3\u810f\uff0c\u62da\u7740\u6700\u5f8c\u4e00\u53e3\u6c14\uff0c\u548c \u5cb3\u9e23\u73c2\u89c1\u4e86\u4e34\u7ec8\u4e00\u9762\uff0c\u8bf4\u4e86\u8fd9\u4e24\u65ec\u8bdd\u5f8c\uff0c\u5728\u4ed6\u6000\u4e2d\uff0c\u53ea\u89c9\u5982\u7761\u5728\u5929\u9e45\u7ed2\u4e0a\u4e00\u822c\uff0c\u975e\u5e38\u6e29\u6696\uff0c \u5fc3\u6ee1\u610f\u8db3\uff0c\u53c8\u5982\u56de\u5230\u513f\u65f6\u60c5\u666f\uff0c\u7236\u4eb2\u62b1\u7740\u81ea\u5df2\u5728\u957f\u5b89\u9644\u8fd1\u7684\u6e29\u6cc9\u6c90\u6d74\uff0c\u6696\u5f97\u4ee4\u4eba\u773c\u76ae\u6c89\u91cd\uff0c \u5c31\u50cf\u8981\u5728\u6e29\u6cc9\u4e2d\u7761\u53bb\uff0c\u8eab\u4f53\u5f80\u4e0b\u6c89\uff0c\u5f80\u4e0b\u6c89\uff0c\u5f80\u4e0b\u6c89\u2026\u2026 \u3000\u3000\u5cb3\u9e23\u73c2\u624b\u4e2d\u5374\u611f\u5230\u4e00\u7247\u51b0\u51b7\uff0c\u94c1\u73ca\u745a\u5df2\u7ecf\u6c14\u7edd\u4e86\uff01\u8fd9\u4e00\u5239\u90a3\uff0c\u5cb3\u9e23\u73c2\u4ec0\u9ebd\u4e5f\u4e0d\u60f3\uff0c\u8111\u5b50 \u7a7a\u7a7a\u6d1e\u6d1e\u7684\uff0c\u4ec0\u9ebd\u90fd\u7edd\u671b\u4e86\uff0c\u53ea\u662f\u611f\u5230\u51b7\uff0c\u8fde\u5fc3\u4e5f\u51b7\u900f\uff0c\u5468\u56f4\u7684\u7a7a\u6c14\u4e5f\u597d\u50cf\u8981\u51b7\u5f97\u51dd\u7ed3\u4e86\u3002 \u3000\u3000\u518d\u8bf4\u7ea2\u82b1\u9b3c\u6bcd\u4ece\u5c71\u4e0a\u4e0b\u6765\uff0c\u8fdc\u8fdc\u671b\u89c1\u7389\u7f57\u5239\u8ffd\u9010\u6155\u5bb9\u51b2\uff0c\u4e0a\u4e86\u5bf9\u9762\u7684\u5c71\u5cf0\uff0c\u5927\u5403\u4e00\u60ca\uff0c \u53eb\u9053\uff1a\u201c\u91d1\u8001\u5927\uff0c\u91d1\u8001\u5927\uff01\u201d\u5cb3\u545c\u73c2\u88ab\u7ea2\u82b1\u9b3c\u6bcd\u523a\u8033\u7684\u53eb\u58f0\u9707\u52a8\uff0c\u597d\u50cf\u4ece\u6076\u68a6\u4e2d\u9661\u7136\u9192\u8f6c\uff0c \u628a\u94c1\u73ca\u745a\u8f7b\u8f7b\u653e\u5728\u5730\u4e0a\uff0c\u62ac\u8d77\u91d1\u72ec\u5f02\u7684\u4eba\u5934\uff0c\u6012\u6c14\u51b2\u51b2\u7684\u558a\u9053\uff1a\u201c\u4f60\u7684\u91d1\u8001\u5927\u5728\u8fd9\u513f\uff01\u201d\u7ea2 \u82b1\u9b3c\u6bcd\u4e00\u77a7\uff0c\u4e5f\u5982\u5cb3\u545c\u73c2\u9002\u624d\u4e00\u6837\uff0c\u4ece\u5934\u9876\u76f4\u51b7\u5230\u811a\u8ddf\uff01\u518d\u77a7\u4e86\u77a7\uff0c\u4eba\u5934\u867d\u7136\u8840\u8089\u6a21\u7cca\uff0c\u5374 \u4e07\u786e\u5343\u771f\u662f\u81ea\u5df2\u51e0\u5341\u5e74\u7684\u8001\u4f34\uff01 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u5dc5\u5dc5\u5dcd\u5dcd\u7684\u4e3e\u8d77\u62d0\u6756\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u662f\u4f60\u628a\u4ed6\u6740\u4e86\uff1f\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u4f60\u7684\u81ed\u6c49 \u5b50\u5341\u4e2a\u4e5f\u62b5\u4e0d\u4e0a\u6211\u7684\u73ca\u745a\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u6012\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff0c\u6211\u8981\u628a\u4f60\u6740\u4e86\u586b\u4ed6\u6027\u547d\uff01\u201d\u5cb3\u545c\u73c2 \u6012\u53eb\u9053\uff1a\u201c\u5cb3\u67d0\u4eba\u5728\u5343\u519b\u4e07\u9a6c\u4e4b\u4e2d\u51e0\u5341\u6b21\u9669\u6b7b\u8fd8\u751f\uff0c\u5728\u5978\u9609\u8ffd\u6355\u4e4b\u4e0b\u4e5f\u65e9\u5df3\u628a\u6027\u547d\u7f6e\u65bc\u5ea6 \u5916\uff0c\u54c8\u54c8\uff0c\u4f60\u8981\u6740\u6211\u586b\u547d\uff01\u718a\u7ecf\u7565\u7684\u6027\u547d\uff0c\u6211\u73ca\u745a\u59b9\u59b9\u7684\u6027\u547d\u8c01\u4eba\u6765\u586b\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u987f\u65f6\u5982 \u53d7\u96f7\u6b9b\uff0c\u7389\u7f57\u5239\u7684\u8bdd\u7adf\u7136\u4e00\u53e5\u4e0d\u5047\uff0c\u8fd9\u8d31\u6c49\u5b50\u679c\u7136\u662f\u52a9\u7ea3\u4e3a\u8650\uff0c\u8feb\u5bb3\u5fe0\u826f\u7684\u4e86\uff01\u9119\u601c\u81ea\u5df1\u51e0 \u5341\u5e74\u6765\u82e6\u5fc3\u79ef\u8651\uff0c\u671b\u4ed6\u6539\u597d\uff0c\u4ecd\u7136\u843d\u5f97\u8fd9\u6837\u4e00\u4e2a\u4e0b\u573a\uff01 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u53ea\u89c9\u56db\u80a2\u65e0\u529b\uff0c\u62d0\u6756\u6162\u6162\u7684\u5782\u4e86\u4e0b\u6765\uff0c\u5cb3\u545c\u73c2\u6012\u6c14\u7a0d\u51cf\uff0c\u9053\uff1a\u201c\u4f60\u5f85\u600e\u9ebd\uff1f\u201d \u7ea2\u82b1\u9b3c\u6bcd\u6709\u6c14\u6ca1\u529b\u7684\u95ee\u9053\uff1a\u201c\u4f60\u53eb\u5cb3\u545c\u73c2\uff1f\u662f\u718a\u7ecf\u7565\u7684\u53c2\u8d5e\uff1f\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u53eb \u7ea2\u82b1\u9b3c\u6bcd\uff0c\u54fc\u54fc\uff0c\u4eba\u4eec\u53eb\u9519\u4f60\u4e86\uff0c\u4f60\u7684\u4e08\u592b\u624d\u662f\u4e2a\u9b3c\uff01\u201d\u7ea2\u82b1\u9b3c\u6bcd\u4e00\u58f0\u957f\u53f9\uff0c\u5fc3\u9053\uff1a\u7f62\u4e86\uff0c \u7f62\u4e86\uff01\u6211\u8fd8\u6709\u4f55\u9762\u76ee\u518d\u89c1\u6b66\u6797\u540c\u9053\uff1f\u6d3b\u5728\u8fd9\u4e16\u4e0a\u8fd8\u6709\u4ec0\u9ebd\u5473\u513f\uff0c\u4e00\u65f6\u60f3\u4e0d\u8fc7\u6765\uff0c\u9aa4\u7136\u5411\u77f3\u5c71 \u4e0a\u4e00\u5934\u649e\u53bb\uff0c\u53ef\u601c\u7ea2\u82b1\u9b3c\u6bcd\u4e00\u4e16\u79f0\u96c4\uff0c\u7adf\u56e0\u8bef\u5ac1\u532a\u4eba\uff0c\u7d2f\u5f97\u5979\u809d\u8111\u6d82\u5730\uff0c\u8840\u6e85\u5e7d\u8c37\uff01 \u3000\u3000\u5cb3\u545c\u73c2\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u800c\u72c2\u7b11\u53eb\u9053\uff1a\u201c\u5927\u5bb6\u6b7b\u4e86\u5012\u4e5f\u4e7e\u51c0\uff01\u201d\u7eb5\u8d77\u4e86\u8eab\uff0c\u4e5f\u5411\u5c71\u77f3\u4e00\u5934\u649e \u53bb\uff01 \u3000\u3000\u518d\u8bf4\u7389\u7f57\u5239\u8ffd\u9010\u6155\u5bb9\u51b2\uff0c\u6155\u5bb9\u51b2\u5df2\u722c\u4e0a\u9ad8\u5c71\uff0c\u5c45\u9ad8\u4e34\u4e0b\uff0c\u628a\u5927\u77f3\u4e71\u63a8\u4e0b\u6765\uff0c\u72b9\u5982\u51b0\u96f9\u9aa4 \u843d\uff0c\u6ee1\u5c71\u4e71\u6eda\uff0c\u7389\u7f57\u5239\u8df3\u907f\u95ea\u8dc3\uff0c\u653b\u4e0d\u4e0a\u53bb\uff0c\u5ffd\u95fb\u5f97\u4e0b\u9762\u7ea2\u82b1\u9b3c\u6bcd\u4e0e\u5cb3\u9e23\u73c2\u9a82\u6218\u4e4b\u58f0\uff0c\u6697 \u9053\uff1a\u4e0d\u597d\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u5b9a\u8981\u548c\u4ed6\u62da\u547d\u3002\u5fc3\u4e2d\u53c8\u60ac\u6302\u94c1\u73ca\u745a\u6027\u547d\u5b89\u5371\uff0c\u53eb\u9053\uff1a\u201c\u6155\u5bb9\u51b2\uff0c\u4eca\u65e5\u94d9 \u4f60\u4e00\u547d\uff01\u201d\u8f6c\u8eab\u5954\u56de\u5ce1\u8c37\uff0c\u5ffd\u89c1\u7ea2\u82b1\u9b3c\u6bcd\u649e\u5ca9\u81ea\u6740\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u69fd\u4e86\uff0c\u7cdf\u4e86\uff0c\u4ece\u6b64\u53c8 \u5c11\u4e00\u4e2a\u5bf9\u624b\u4e86\uff01\u4e00\u63a0\u800c\u524d\uff0c\u6765\u5f97\u6b63\u662f\u65f6\u5019\uff1f \u3000\u3000\u5cb3\u545c\u73c2\u4e00\u5934\u649e\u53bb\uff0c\u5934\u9876\u79bb\u5ca9\u4e0d\u5230\u4e94\u5bf8\uff0c\u7389\u7f57\u5239\u6070\u6070\u8d76\u5230\uff0c\u4e00\u624b\u6349\u7740\u4ed6\u7684\u8db3\u8ddf\uff0c\u786c\u751f\u751f\u62c9 \u4e86\u56de\u6765\uff0c\u5cb3\u545c\u73c2\u53ea\u542c\u5f97\u8033\u8fb9\u6709\u4eba\u8bf4\u9053\uff1a\u201c\u4e00\u65e5\u4e4b\u95f4\uff0c\u4e0d\u80fd\u8fde\u6b7b\u4e24\u4e2a\u9ad8\u624b\uff01\u201d\u7741\u773c\u4e00\u770b\uff0c\u5374\u539f \u6765\u662f\u7389\u7f57\u5239\u5728\u5bf9\u81ea\u5df1\u8bf4\u8bdd\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u8dcc\u5750\u5730\u4e0a\uff0c\u628a\u624b\u4e00\u6307\u9053\uff1a\u201c\u73ca\u745a\u6b7b\u4e86\uff0c\u6211\u6d3b\u7740\u8fd8\u6709\u4ec0\u9ebd\u610f\u601d\uff1f\u201d\u7389\u7f57\u5239\u5fc3\u4e2d\u5927 \u75db\uff0c\u4f46\u6551\u751f\u4e0d\u6551\u6b7b\uff0c\u5f3a\u7528\u6781\u5927\u7684\u5b9a\u529b\u538b\u4f4f\u60b2\u75db\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u5cb3\u9e23\u73c2\u4f60\u6015\u548c\u6211\u6bd4\u5251\u9ebd\uff1f\u201d \u3000\u3000\u5cb3\u9e23\u73c2\u6c14\u5f80\u4e0a\u51b2\uff0c\u5fc3\u9053\uff1a\u94c1\u73ca\u745a\u662f\u4f60\u8c0a\u59b9\uff0c\u4f60\u5374\u5982\u6b64\u6ca1\u6709\u5fc3\u809d\uff0c\u8fd9\u4e2a\u65f6\u5019\u8fd8\u6709\u5fc3\u60c5\u8981\u548c \u6211\u6bd4\u5251\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u8981\u6bd4\u5251\uff1f\u6765\uff0c\u6765\uff01\u9119\u60dc\u73ca\u745a\u59b9\u59b9\u770b\u4e0d\u5230\u5979\u8c0a\u59d0\u7684\u5a01\u98ce\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u4e0d\u662f\u73b0\u5728\u8981\u548c\u4f60\u6bd4\u5251\u3002\u54b1\u4eec\u7684\u5e08\u7236\u5404\u521b\u4e00\u5bb6\u5251\u672f\uff0c\u4e00\u6b63\u4e00\u53cd\uff0c\u76f8\u514b\u76f8 \u751f\uff0c\u6211\u7684\u5e08\u7236\u539f\u610f\u662f\u5f85\u5251\u672f\u7ec3\u597d\u4e4b\u5f8c\uff0c\u548c\u4f60\u7684\u5e08\u7236\u8f83\u91cf\u4e00\u4e0b\uff0c\u5370\u8bc1\u5370\u8bc1\u5f7c\u6b64\u7684\u6b66\u529f\u3002\u53ef\u60dc\u6211 \u7684\u5e08\u7236\u6b7b\u4e86\uff0c\u4ed6\u4eec\u4e24\u4f4d\u8001\u4eba\u5bb6\u6bd4\u4e0d\u6210\u5566\u3002\u6211\u4eec\u5404\u81ea\u627f\u7ee7\u4e00\u5bb6\u5251\u672f\uff0c\u662f\u4ed6\u4fe9\u8001\u7684\u552f\u4e00\u4f20\u4eba\uff0c\u5c06 \u6765\u6709\u54b1\u4eec\u5b8c\u6210\u4e0a\u8f88\u7684\u5fc3\u613f\uff0c\u4f60\u4e0d\u548c\u6211\u6bd4\u5251\uff0c\u6211\u8fd8\u627e\u8c01\u53bb\u6bd4\u4e86\u54b1\u4eec\u518d\u7ec3\u5b83\u4e00\u4e8c\u5341\u5e74\uff0c\u628a\u672c\u95e8\u5251 \u6cd5\u7ec3\u5f97\u7cbe\u901a\u719f\u900f\u4e4b\u5f8c\uff0c\u90a3\u65f6\u518d\u597d\u597d\u8f83\u91cf\u4e00\u4e0b\uff0c\u5206\u4e2a\u9ad8\u4e0b\u3002\u73b0\u5728\u6bd4\uff0c\u5de6\u53f3\u4e0d\u8fc7\u6253\u4e2a\u5e73\u624b\uff0c\u6ca1\u6709 \u4ec0\u9ebd\u610f\u601d\u3002\u201d \u3000\u3000\u5cb3\u9e23\u73c2\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u539f\u6765\u5979\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u6211\u5e08\u7236\u73b0\u5728\u4e5f\u5df2\u98ce\u70db\u6b8b\u5e74\uff0c\u65ad\u4e0d\u4f1a\u6709\u7b2c\u4e8c \u4e2a\u4f20\u4eba\u7684\u4e86\u3002\u6211\u679c\u7136\u4e0d\u5e94\u8f7b\u751f\uff0c\u4ee4\u672c\u95e8\u5251\u672f\u81f3\u6211\u800c\u65ad\u3002\u601d\u5ff5\u53ca\u6b64\uff0c\u987f\u5982\u51b7\u6c34\u6d47\u5934\uff0c\u500f\u7136\u800c \u9192\u3002\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8c22\u8c22\u4f60\u7684\u52c9\u52b1\uff0c\u4e8c\u5341\u5e74\u5f8c\uff0c\u6211\u5728\u5929\u5c71\u7b49\u4f60\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u677e\u4e86\u53e3\u6c14\uff0c\u8fd9\u65f6\u624d\u89c9\u5fc3\u4e2d\u521b\u75db\uff0c\u62b1\u7740\u94c1\u73ca\u745a\u7684\u4f53\u545c\u545c\u7684\u54ed\u8d77\u6765\uff0c\u5cb3\u9e23\u73c2\u6697\u9053\uff1a\u539f \u6765\u5979\u8868\u9762\u867d\u51f6\uff0c\u5fc3\u4e2d\u5374\u662f\u81f3\u6027\u81f3\u60c5\u3002\u6b63\u8981\u4e0a\u524d\u529d\u6170\uff0c\u5c71\u4e0a\u53c8\u5954\u4e0b\u4e00\u4eba\uff0c\u539f\u6765\u662f\u5353\u4e00\u822a\uff0c\u4ed6\u8f7b \u529f\u8f83\u900a\uff0c\u76f4\u5230\u73b0\u5728\u624d\u6765\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u54bd\u6cea\u53eb\u9053\uff1a\u201c\u5353\u89c1\uff0c\u73ca\u745a\u6b7b\u5566\uff0c\u4f60\u53bb\u529d\u5979\u3002\u201d\u5353\u4e00\u822a\u5403\u4e86\u4e00\u60ca\uff0c\u4e0a\u524d\u53bb\u628a\u7389\u7f57\u5239 \u6276\u8d77\u3002\u7389\u7f57\u5239\u5ffd\u7136\u60f3\u9053\uff1a\u201c\u5cb3\u545c\u73c2\u548c\u94c1\u73ca\u745a\u867d\u7136\u4e0d\u80fd\u7f14\u7ed3\u826f\u7f18\uff0c\u76f8\u7231\u4e4b\u8bda\uff0c\u4eca\u65e5\u5c3d\u89c1\u3002\u73ca\u745a \u59b9\u5b50\u5f97\u4ed6\u5982\u6b64\u76f8\u7231\uff0c\u6b7b\u5f8c\u4e5f\u5f53\u7791\u76ee\u7684\u4e86\uff01\u201d\u7389\u7f57\u5239\u6df1\u89c9\u94c1\u73ca\u745a\u8f83\u5979\u5e78\u798f\uff0c\u77a7\u4e86\u5353\u4e00\u822a\u4e00\u773c\uff0c \u6df1\u60c5\u6028\u6068\uff0c\u5c3d\u5728\u773c\u5149\u4e00\u77a5\u4e4b\u4e2d\u3002 \u3000\u3000\u5353\u4e00\u822a\u4e3a\u5979\u773c\u5149\u6240\u6151\uff0c\u4f4e\u4e0b\u5934\u53bb\u3002\u7389\u7f57\u5239\u601d\u6f6e\u8d77\u4f0f\uff0c\u5ffd\u89c9\u771f\u6b63\u53ef\u54c0\u7684\u4e0d\u662f\u94c1\u73ca\u745a\u800c\u662f\u81ea \u5df1\uff0c\u75f4\u75f4\u5446\u60f3\uff0c\u4e0d\u89c9\u6536\u4e86\u773c\u6cea\u3002\u826f\u4e45\uff0c\u826f\u4e45\uff0c\u624d\u62ac\u8d77\u5934\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u5c31\u5728\u8fd9\u4e2a\u5c71\u8c37\u5c06\u5979\u57cb \u4e86\u3002\u5f85\u6eb6\u96ea\u5f00\u5c71\u4e4b\u5f8c\uff0c\u518d\u7ed9\u5979\u9020\u5893\u3002\u201d \u3000\u3000\u53c1\u4eba\u4ee5\u5251\u5f53\u9504\uff0c\u52a8\u624b\u6316\u571f\uff0c\u6316\u4e86\u4e00\u9053\u6df1\u6c9f\uff0c\u5c06\u94c1\u73ca\u745a\u7684\u4f53\u653e\u4e86\u4e0b\u53bb\u3002\u7389\u7f57\u5239\u9053\uff1a\u201c\u518d\u6316 \u591a\u4e00\u4e2a\uff01\u201d\u5c06\u7ea2\u82b1\u9b3c\u6bcd\u7684\u4f53\u62b1\u6765\uff0c\u9053\uff1a\u201c\u5979\u4e5f\u662f\u4e2a\u53ef\u601c\u7684\u4eba\u3002\u201d\u6316\u597d\u5893\u7a74\uff0c\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u8ba9 \u5979\u5174\u5979\u7684\u6c49\u5b50\u5408\u846c\u3002\u201d\u628a\u91d1\u72ec\u5f02\u7684\u9996\u7ea7\u548c\u4f53\u63b7\u5165\u7a74\u4e2d\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u672c\u5f85\u628a\u4ed6\u7684\u9996\u7ea7\u796d\u73ca\u745a\u59b9 \u59b9\uff0c\u770b\u4ed6\u7684\u59bb\u5b50\u4efd\u4e0a\uff0c\u4fbf\u5b9c\u4ed6\u4e86\u3002\u201d \u3000\u3000\u53c1\u4eba\u5c06\u6ce5\u58eb\u76d6\u4e0a\u5893\u7a74\uff0c\u9ed8\u9ed8\u81f4\u54c0\u3002\u5ffd\u95fb\u5f97\u4f4e\u4f4e\u547b\u541f\u4e4b\u58f0\uff0c\u5cb3\u545c\u73c2\u56de\u5934\u4e00\u770b\uff0c\u5374\u662f\u5e94\u4fee\u9633 \u5728\u5730\u4e0a\u6eda\u52a8\uff0c\u4ed6\u88ab\u7389\u7f57\u5239\u8feb\u4e0b\u6df1\u8c37\uff0c\u626d\u4f24\u8db3\u8e1d\uff0c\u76ee\u7779\u91d1\u72ec\u5f02\u88ab\u6740\u548c\u7ea2\u82b1\u9b3c\u6bcd\u649e\u5ca9\u7b49\u60e8\u70c8\u60c5 \u666f\uff0c\u4f24\u867d\u4e0d\u91cd\uff0c\u5df2\u5413\u5f97\u8f6f\u4e86\u3002 \u3000\u3000\u5cb3\u545c\u73c2\u6068\u6068\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\uff0c\u597d\uff0c\u54b1\u4eec\u518d\u6316\u591a\u4e00\u4e2a\uff0c\u628a\u4ed6\u751f\u7406\uff01\u201d\u5c06\u5e94\u4fee\u9633\u4e00\u628a\u63d0 \u8d77\uff0c\u7389\u7f57\u5239\u5ffd\u9053\uff1a\u201c\u7559\u4ed6\u72d7\u547d\uff01\u201d\u5353\u4e00\u822a\u4e5f\u9192\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u7559\u4ed6\u72d7\u547d\u3002\u54b1\u4eec\u8981\u4ed6\u62db\u51fa \u79c1\u901a\u6ee1\u6d32\u7684\u540c\u515a\u6765\uff01\u201d\u5cb3\u545c\u73c2\u60f3\u8d77\u5f53\u5e74\u5728\u534e\u5c71\u7edd\u9876\u90d1\u6d2a\u53f0\u62db\u4f9b\u4e4b\u4e8b\uff0c\u9053\uff1a\u201c\u90a3\u9ebd\u8fd9\u4e8b\u8981\u62dc\u6258 \u7ec3\u5973\u4fa0\u4e86\u3002\u201d \u3000\u3000\u4e24\u756a\u5267\u6597\uff0c\u4e00\u573a\u4f24\u5fc3\uff0c\u81ea\u9ece\u660e\u95f9\u81f3\u6b64\u523b\uff0c\u5df2\u662f\u65e5\u5f71\u897f\u79fb\uff0c\u5927\u5c06\u5782\u66ae\u3002\u7389\u7f57\u5239\u65e0\u5fc3\u5ba1\u95ee\uff0c \u8bf4\u9053\uff1a\u201c\u5c06\u4ed6\u5148\u5e26\u56de\u5c71\u5be8\uff0c\u8ba9\u4ed6\u591a\u6d3b\u4e24\u5929\u3002\u201d\u5cb3\u9e23\u73c2\u9053\uff1a\u201c\u4e00\u5207\u7531\u4f60\u5904\u7f6e\uff0c\u8c05\u4ed6\u63d2\u7ffc\u96be \u9003\u3002\u201d\u628a\u5e94\u4fee\u9633\u63d0\u4e86\u8d77\u6765\uff0c\u5982\u98de\u4e0a\u5c71\u3002 \u3000\u3000\u56de\u5230\u5c71\u5be8\uff0c\u7389\u7f57\u5239\u7acb\u523b\u6d3e\u9063\u5973\u5175\uff0c\u6316\u901a\u5c71\u53e3\u901a\u8def\u3002\u665a\u996d\u4e4b\u5f8c\uff0c\u65b0\u6708\u521d\u4e0a\uff0c\u5df2\u5c06\u94c1\u73ca\u745a\u5e26 \u53bb\u7684\u5973\u5175\u63a5\u4e86\u56de\u6765\uff0c\u5e78\u559c\u5e76\u65e0\u635f\u4f24\uff0c\u5979\u4eec\u5954\u6ce2\u4e86\u4e00\u5929\u4e00\u591c\uff0c\u4e2a\u4e2a\u75b2\u5026\u4e0d\u582a\uff0c\u9971\u9910\u4e4b\u5f8c\uff0c\u5404\u81ea \u6b47\u606f\u3002 \u3000\u3000\u7389\u7f57\u5239\u548c\u5353\u4e00\u822a\u5cb3\u9e23\u73c2\u5374\u662f\u65e0\u5fc3\u6b47\u606f\uff0c\u53c1\u4eba\u5728\u5c71\u4e2d\u6f2b\u6b65\uff0c\u9ed8\u9ed8\u65e0\u8a00\uff0c\u6708\u8272\u6eb6\u6eb6\uff0c\u53c1\u4eba\u90fd \u5404\u81ea\u6005\u89e6\u3002\u5cb3\u545c\u73c2\u5ffd\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\uff0c\u6211\u6709\u6709\u4e00\u4e8b\u91cd\u6258\u4f60\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8bf7\u8bf4\u3002\u201d\u5cb3\u545c\u73c2 \u9053\uff1a\u201c\u718a\u7ecf\u7565\u8eab\u906d\u60e8\u6b7b\uff0c\u4f20\u9996\u4e5d\u8fb9\uff0c\u613f\u4f60\u5c06\u4ed6\u9996\u7ea7\u53d6\u56de\uff0c\u7ed9\u4ed6\u5b89\u846c\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u718a\u7ecf\u7565 \u662f\u6211\u7684\u670b\u53cb\uff0c\u8fd9\u4e8b\u6211\u7d27\u8bb0\u5728\u5fc3\uff0c\u5c3d\u529b\u53bb\u505a\u4fbf\u662f\u3002\u201d\u5cb3\u9e23\u73c2\u53c8\u9053\uff1a\u201c\u5353\u5144\uff0c\u5c06\u718a\u7ecf\u7565\u9057\u4e66\u4ea4\u4e0e \u9002\u5f53\u4e4b\u4eba\uff0c\u8fd9\u4e8b\u4e5f\u91cd\u6258\u4f60\u4e86\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5c0f\u5f1f\u5f53\u5f97\u5c3d\u529b\uff0c\u53ea\u6015\u4eca\u5f8c\u56de\u53bb\u638c\u95e8\uff0c\u96be\u5f97\u5728\u6c5f \u6e56\u8d70\u52a8\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u56de\u53bb\u4f5c\u638c\u95e8\u5417\uff1f\u201d\u5353\u4e00\u822a\u4f4e\u5934\u4e0d\u8bed\uff0c\u5cb3\u545c\u73c2\u66ff\u4ed6\u89e3\u56f4\u9053\uff1a \u201c\u5353\u5144\u56de\u53bb\u4f5c\u638c\u95e8\u4e5f\u597d\uff0c\u603b\u80dc\u65bc\u8ba9\u4ed6\u7684\u5e08\u53d4\u638c\u95e8\u3002\u201d\u5353\u4e00\u822a\u4e00\u58f0\u82e6\u7b11\uff0c\u5cb3\u545c\u73c2\u7eed\u9053\uff1a\u201c\u8fd9\u4e66 \u5c31\u662f\u89c5\u4e0d\u5230\u4e3b\u4eba\uff0c\u653e\u5728\u4f60\u90a3\u513f\u4e5f\u597d\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5cb3\u5144\u653e\u5fc3\uff0c\u5c0f\u5f1f\u7eb5\u4e0d\u80fd\u4eb2\u81ea\u66ff\u8fd9\u4e66\u7269\u8272 \u4e3b\u4eba\uff0c\u4e5f\u4e00\u5b9a\u4ea4\u7ed9\u53ef\u9760\u7684\u670b\u53cb\u4ee3\u529e\u3002\u201d\u7389\u7f57\u5239\u9887\u89c9\u5cb3\u9e23\u73c2\u795e\u8272\u6709\u5f02\uff0c\u53ea\u6015\u4ed6\u8fd8\u60f3\u4e0d\u5f00\uff0c\u7b11 \u9053\uff1a\u201c\u5eff\u5e74\u5f8c\u6bd4\u5251\u4e4b\u7ea6\uff0c\u4e0d\u8981\u5fd8\u4e86\u3002\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u7edd\u5fd8\u4e0d\u4e86\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5cb3\u5144\uff0c\u4f60\u4eca \u5f8c\u6253\u7b97\u5982\u4f55\uff1f\u201d\u5cb3\u545c\u73c2\u9053\uff1a\u201c\u968f\u7f18\u800c\u4f4f\uff0c\u968f\u9047\u800c\u5b89\uff0c\u4efb\u5b83\u7ea2\u5c18\u6270\u6270\uff0c\u6211\u81ea\u4e00\u74e2\u6765\u5f80\u3002\u201d\u7389\u7f57 \u5239\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u8bf4\u4ec0\u9ebd\uff1f\u771f\u50cf\u8001\u548c\u5c1a\u5ff5\u7ecf\u3002\u201d\u5353\u4e00\u822a\u77e5\u4ed6\u770b\u7834\u5c18\u7f18\uff0c\u6240\u8bf4\u7684\u5df2\u662f\u609f\u9053\u4e4b\u8bed\u3002 \u5fc3\u9053\uff1a\u4ed6\u505a\u548c\u5c1a\u4e5f\u597d\uff0c\u6211\u8fd8\u6ca1\u798f\u4efd\u505a\u548c\u5c1a\u5462\uff01 \u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u5cb3\u545c\u73c2\u679c\u7136\u4e0d\u8f9e\u800c\u884c\uff0c\u7ed9\u5353\u4e00\u822a\u548c\u7389\u7f57\u5239\u7559\u4e86\u4e00\u5c01\u4e66\u4fe1\uff0c\u8bf4\u662f\u5e08\u7236\u8001\u8fc8\uff0c \u81ea\u5df1\u8981\u56de\u5929\u5c71\u4f8d\u5949\uff0c\u4eca\u5f8c\u9980\u5e74\uff0c\u5c06\u81f4\u529b\u65bc\u5251\u672f\u4e91\u4e91\u3002\u6b64\u4e8b\u65e9\u5728\u5353\u4e00\u822a\u548c\u7389\u7f57\u5239\u610f\u6599\u4e4b\u4e2d\uff0c\u4f46 \u4ecd\u7136\u4e0d\u514d\u611f\u6168\u3002 \u3000\u3000\u662f\u65e5\uff0c\u7389\u7f57\u5239\u4eb2\u81ea\u7763\u5de5\uff0c\u5c06\u94c1\u73ca\u745a\u548c\u7ea2\u82b1\u9b3c\u6bcd\u7684\u575f\u5893\u5efa\u597d\uff0c\u665a\u4e0a\u56de\u6765\uff0c\u548c\u5353\u4e00\u822a\u5403\u4e86\u665a \u996d\u4e4b\u5f8c\uff0c\u72ec\u81ea\u6b47\u4e86\u4e00\u4f1a\uff0c\u6b63\u60f3\u628a\u5e94\u4fee\u9633\u63d0\u6765\u5ba1\u95ee\uff0c\u5ffd\u89c1\u7cae\u4ed3\u8d77\u706b\uff0c\u7389\u7f57\u5239\u5927\u5403\u4e00\u60ca\uff0c\u62d4\u5251\u800c \u8d77\uff0c\u5916\u9762\u5973\u55bd\u5175\u4e71\u6210\u4e00\u7247\uff0c\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u5b98\u519b\u6740\u6765\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u5b98\u519b\u90a3\u6709\u5982\u6b64\u672c\u9886\uff1f\u201d \u63d0\u5251\u51b2\u51fa\u5be8\u95e8\uff0c\u5ffd\u89c1\u6155\u5bb9\u51b2\u7387\u9886\u51e0\u5341\u540d\u5b98\u5175\uff0c\u5230\u5904\u653e\u706b\uff0c\u7389\u7f57\u5239\u5927\u6012\u9053\uff1a\u201c\u4f60\u4fa5\u5e78\u9003\u8131\u6027 \u547d\uff0c\u8fd8\u6562\u5230\u6b64\u3002\u201d\u628a\u624b\u4e00\u6325\uff0c\u4f17\u55bd\u5175\u89c1\u7389\u7f57\u5239\u51fa\u6765\uff0c\u519b\u5fc3\u5927\u5b9a\uff0c\u968f\u7389\u7f57\u5239\u624b\u52bf\uff0c\u6392\u6210\u5706\u9635\uff0c \u548c\u5b98\u5175\u6df7\u6218\u3002\u7389\u7f57\u5239\u4e00\u5251\u51b2\u524d\uff0c\u5355\u89c5\u6155\u5bb9\u51b2\u6740\u3002\u6b63\u6df7\u6218\u95f4\uff0c\u897f\u89d2\u53c8\u4e71\uff0c\u6708\u5149\u4e0b\u53ea\u89c1\u4e00\u7fa4\u9053 \u58eb\uff0c\u624b\u6267\u957f\u5251\uff0c\u51b2\u8fdb\u5c71\u5be8\u3002 \u3000\u3000\u539f\u6765\u6155\u5bb9\u51b2\u5f53\u65e5\u9003\u8131\u4e4b\u5f8c\uff0c\u6536\u62fe\u4f24\u4ea1\uff0c\u9664\u4e86\u88ab\u7389\u7f57\u5239\u523a\u4f24\u7684\u536b\u58eb\u4e4b\u5916\uff0c\u53c8\u6709\u51e0\u540d\u5728\u96ea\u5d29 \u4e4b\u9645\uff0c\u88ab\u5c71\u77f3\u6eda\u4e0b\uff0c\u6253\u5f97\u8db3\u65ad\u624b\u6298\u3002\u5269\u4e0b\u80fd\u591f\u4f5c\u6218\u7684\u536b\u58eb\uff0c\u4e0d\u5230\u5341\u540d\u3002\u672c\u5df2\u80c6\u5bd2\uff0c\u60f3\u56de\u4eac\u518d \u9080\u5e2e\u624b\u3002\u5176\u65f6\u9002\u503c\u5e7f\u5143\u9965\u6c11\u5927\u95f9\u4e4b\u5f8c\uff0c\u7701\u4e2d\u5b98\u519b\u95fb\u8b66\u5f00\u6765\uff0c\u9b4f\u5fe0\u8d24\u6d3e\u5728\u201c\u527f\u532a\u519b\u201d\u4e2d\u7684\u76d1\u519b \u8fde\u57ce\u864e\u4e5f\u6765\u5230\u4e86\u3002\u8fde\u57ce\u864e\u662f\u4ee5\u524d\u897f\u5382\u7684\u603b\u6559\u5934\uff0c\u548c\u6155\u5bb9\u51b2\u539f\u662f\u540c\u50da\uff0c\u95fb\u5f97\u6155\u5bb9\u51b2\u5728\u6b64\uff0c\u6025\u6765 \u76f8\u89c1\uff0c\u6155\u5bb9\u51b2\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u6709\u751f\u4ee5\u6765\uff0c\u4ece\u672a\u53d7\u8fc7\u5982\u6b64\u632b\u6298\u3002\u201d\u8fde\u57ce\u864e\u7ec6\u95ee\u60c5\u7531\uff0c\u6155\u5bb9\u51b2\u4e00 \u4e00\u8bf4\u4e86\u3002\u8fde\u57ce\u864e\u542c\u5f97\u91d1\u72ec\u5f02\u8eab\u6b7b\uff0c\u5c1a\u6ca1\u4ec0\u9ebd\uff0c\u95fb\u5f97\u5e94\u4fee\u9633\u88ab\u64d2\uff0c\u5374\u662f\u9762\u8272\u5927\u53d8\u3002\u539f\u6765\u9b4f\u5fe0\u8d24 \u5e94\u4fee\u9633\u548c\u8fde\u57ce\u864e\u90fd\u662f\u6ee1\u6d32\u7684\u5185\u5e94\uff0c\u8fde\u57ce\u864e\u751f\u6015\u5e94\u4fee\u9633\u88ab\u8feb\u62db\u8ba4\u51fa\u6765\uff0c\u9732\u65bc\u5929\u4e0b\u3002\u6025\u6025\u95ee\u9053\uff1a \u201c\u7389\u7f57\u5239\u7684\u540d\u5934\u6211\u4e5f\u66fe\u542c\u8bf4\u8fc7\uff0c\u5979\u6709\u591a\u5c11\u55bd\u5175\uff1f\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u5927\u7ea6\u6709\u51e0\u767e\u5427\uff0c\u90fd\u662f\u5973 \u7684\u3002\u201d\u8fde\u57ce\u864e\u7b11\u9053\uff1a\u201c\u51e0\u767e\u5973\u55bd\u5175\u6015\u5979\u4ec0\u9ebd\uff0c\u54b1\u4eec\u7387\u5175\u626b\u5e73\u5979\u7684\u5c71\u5be8\u3002\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u51e0\u767e \u5973\u55bd\u5175\u867d\u6ca1\u4ec0\u9ebd\uff0c\u53ef\u662f\u660e\u6708\u5ce1\u5947\u9669\uff0c\u5927\u961f\u5b98\u519b\uff0c\u5982\u4f55\u80fd\u5f00\u4e0a\u53bb\uff1f\u52a0\u4ee5\u96ea\u5d29\u5c01\u5c71\uff0c\u6b64\u8def\u66f4\u96be\u901a \u4e86\u3002\u201d\u8fde\u57ce\u864e\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u542c\u4f60\u6240\u8bf4\uff0c\u5f53\u65e5\u5be8\u4e2d\u5973\u5175\uff0c\u4e5f\u6709\u8bb8\u591a\u88ab\u96ea\u5d29\u6240\u963b\uff0c\u4e0d\u80fd\u56de \u5c71\u3002\u90a3\u9ebd\u7389\u7f57\u5239\u975e\u5f00\u901a\u5c71\u9053\u63a5\u5979\u4eec\u56de\u53bb\u4e0d\u53ef\u3002\u6211\u5728\u519b\u4e2d\u5927\u7ea6\u53ef\u6311\u51fa\u51e0\u5341\u540d\u6709\u8f7b\u529f\u6839\u5e95\u7684\uff0c\u548c \u4f60\u6478\u8fdb\u5c71\u53bb\u3002\u201d\u6155\u5bb9\u51b2\u6447\u6447\u5934\u9053\uff1a\u201c\u8fd8\u662f\u4e0d\u884c\uff0c\u519b\u4e2d\u7684\u6b66\u58eb\uff0c\u867d\u7136\u80fd\u6478\u8fdb\u5c71\u5be8\uff0c\u7528\u6765\u62b5\u654c\u7389 \u7f57\u5239\u8bad\u7ec3\u6709\u7d20\u7684\u5973\u55bd\u5175\uff0c\u6570\u5341\u540d\u5c1a\u5acc\u4e0d\u79fb\u3002\u4f55\u51b5\u90a3\u7389\u7f57\u5239\u548c\u5cb3\u9e23\u73c2\u7684\u5251\u672f\u7684\u786e\u975e\u6bd4\u5bfb\u5e38\u3002\u800c \u4e14\u5176\u4e2d\u8fd8\u7275\u6d89\u7740\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u3002\u201d\u8fde\u57ce\u864e\u9053\uff1a\u201c\u600e\u9ebd\u4e86\u6211\u542c\u8bf4\u6b66\u5f53\u6d3e\u9009\u51fa\u65b0\u638c\u95e8\u4e86\uff0c\u540d\u53eb\u4ec0 \u9ebd\u5353\u4e00\u822a\u7684\uff0c\u4ed6\u4eec\u6b66\u5f53\u6d3e\u7d20\u6765\u4e0d\u4e0e\u5b98\u5e9c\u4f5c\u5bf9\uff0c\u96be\u9053\u5353\u4e00\u822a\u8fd8\u4f1a\u4e0e\u90a3\u5973\u9b54\u5934\u5728\u4e00\u5904\u5417\uff1f\u201d\u6155\u5bb9 \u51b2\u9053\uff1a\u201c\u6b63\u662f\uff0c\u5353\u4e00\u822a\u975e\u4f46\u548c\u90a3\u5973\u9b54\u751a\u4e3a\u4eb2\u5bc6\uff0c\u800c\u4e14\u8fd8\u5305\u5e87\u5cb3\u545c\u73c2\uff0c\u5353\u4e00\u822a\u4e00\u4eba\u5012\u4e0d\u8db3\u60e7\uff0c \u53ea\u662f\u6b66\u5f53\u6d3e\u7684\u9053\u58eb\uff0c\u4e2a\u4e2a\u6b66\u529f\u7cbe\u5f3a\uff0c\u5728\u5e7f\u5143\u57ce\u4e2d\u7684\u5c31\u6709\u51e0\u5341\u540d\u4e4b\u591a\uff0c\u628a\u4ed6\u4eec\u4e5f\u5377\u8fdb\u6f29\u6da1\uff0c\u90a3 \u5c31\u66f4\u68d8\u624b\u4e86\u3002\u201d \u3000\u3000\u8fde\u57ce\u864e\u9762\u8272\u5927\u53d8\uff0c\u9053\uff1a\u201c\u5e94\u4fee\u9633\u975e\u6551\u51fa\u4e0d\u53ef\u3002\u201d\u4f4e\u58f0\u5728\u6155\u5bb9\u51b2\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u5e94\u4fee\u9633\u662f\u9b4f \u516c\u516c\u5fc3\u8179\uff0c\u5f97\u5ba0\u4e0d\u5728\u4f60\u6211\u4e4b\u4e0b\uff0c\u9b4f\u516c\u516c\u66fe\u51e0\u6b21\u53eb\u6211\u591a\u7167\u5e94\u4ed6\u3002\u201d\u6155\u5bb9\u51b2\u672c\u6765\u4e0d\u5927\u77a7\u5f97\u8d77\u5e94\u4fee \u9633\uff0c\u95fb\u8a00\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u662f\u975e\u6551\u4ed6\u4e0d\u53ef\u7684\u4e86\u3002\u4e0d\u89c9\u60f3\u8d77\u5e94\u4fee\u9633\u4ee5\u524d\u6240\u8bf4\u7684\u529e \u6cd5\uff0c\u9053\uff1a\u201c\u5e94\u4fee\u9633\u5012\u662f\u6709\u4e00\u5999\u7b56\uff0c\u53ea\u662f\u6211\u8f88\u6240\u4e0d\u5c51\u4e3a\u3002\u201d\u8fde\u57ce\u864e\u5fd9\u95ee\u9053\uff1a\u201c\u4ec0\u9ebd\u5999\u7b56\uff1f\u201d\u6155 \u5bb9\u51b2\u9053\uff1a\u201c\u5174\u6b66\u5f53\u6d3e\u5316\u654c\u4e3a\u53cb\uff0c\u5411\u767d\u77f3\u9053\u4eba\u966a\u7f6a\uff0c\u6c42\u4ed6\u4eec\u548c\u6211\u4eec\u5408\u5925\u653b\u5c71\u3002\u201d\u8fde\u57ce\u864e\u62cd\u638c\u7b11 \u9053\uff1a\u201c\u597d\u554a\uff0c\u6b63\u8be5\u8fd9\u6837\u3002\u767d\u77f3\u9053\u4eba\u6c14\u91cf\u72ed\u7a84\uff0c\u4ed6\u7684\u638c\u95e8\u5f1f\u5b50\u88ab\u63b3\uff0c\u54b1\u4eec\u51ed\u8fd9\u4e00\u70b9\u5c31\u53ef\u8bf4\u5f97\u52a8 \u4ed6\u3002\u201d \u3000\u3000\u5e94\u4fee\u9633\u4e0e\u8fde\u57ce\u864e\u6599\u5f97\u4e0d\u5dee\uff0c\u767d\u77f3\u9053\u4eba\u7b49\u4e86\u4e24\u5929\u4e0d\u89c1\u5353\u4e00\u822a\u56de\u6765\uff0c\u6b63\u81ea\u751f\u6c14\uff0c\u4f46\u81ea\u5df2\u4e0d\u662f \u7389\u7f57\u5239\u7684\u5bf9\u624b\uff0c\u53c8\u4e0d\u6562\u5230\u660e\u6708\u5ce1\u8981\u4eba\uff0c\u542c\u4e86\u6155\u5bb9\u51b2\u548c\u8fde\u57ce\u864e\u7684\u8bf4\u8bcd\uff0c\u548c\u7ea2\u4e91\u9053\u4eba\u8003\u8651\u8bb8\u4e45\uff0c \u7adf\u7136\u63a5\u7eb3\uff0c\u4e0d\u8fc7\u63d0\u51fa\u4e86\u53c1\u4e2a\u6761\u4ef6\u3002 \u3000\u3000\u767d\u77f3\u9053\u4eba\u63d0\u51fa\u7684\u53c1\u4e2a\u6761\u4ef6\u662f\uff1a\u4e00\u3001\u5404\u5e72\u5404\u7684\uff0c\u5404\u4e0d\u76f8\u6d89\u3002\u4ed6\u4eec\u6c42\u5bfb\u56de\u638c\u95e8\uff0c\u7edd\u4e0d\u7ed9\u5b98\u5175 \u52a9\u6218\u3002\u4e8c\u3001\u9664\u4e86\u7389\u7f57\u5239\u5916\uff0c\u4ed6\u4eec\u4e0d\u613f\u4f24\u4eba\uff0c\u82e5\u6709\u5973\u55bd\u5175\u6765\u653b\uff0c\u4ed6\u4eec\u6c42\u81ea\u4fdd\u3002\u56e0\u6b64\u8981\u5b98\u519b\u5148 \u53bb\uff0c\u628a\u5973\u55bd\u5175\u654c\u4f4f\uff0c\u597d\u8ba9\u4ed6\u4eec\u8fdb\u5c71\u5be8\u641c\u7d22\u3002\u53c1\uff0c\u4e8b\u60c5\u4e00\u8fc7\uff0c\u5404\u8d70\u5404\u7684\u3002\u4ee5\u524d\u6069\u6028\u4e5f\u4e00\u7b14\u52fe \u9500\uff0c\u5b98\u4e2d\u536b\u58eb\u4e0d\u80fd\u518d\u627e\u6b66\u5f53\u6d3e\u7684\u9ebb\u70e6\u3002\u6155\u5bb9\u51b2\u4e00\u4e00\u7b54\u5e94\uff0c\u5c31\u6b64\u7ea6\u5b9a\uff0c\u5f53\u665a\u5404\u81ea\u4e0a\u5c71\u3002 \u3000\u3000\u518d\u8bf4\u7389\u7f57\u5239\u89c1\u5230\u767d\u77f3\u9053\u4eba\u7387\u4f17\u51b2\u5165\u5c71\u5be8\uff0c\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u767d\u77f3\u9053\u4eba\uff0c\u4f60\u4e5f\u52a9\u7ea3\u4e3a \u8650\uff01\u201d\u5973\u55bd\u5175\u89c1\u5be8\u4e3b\u52a8\u4e86\u771f\u6012\uff0c\u53c8\u89c1\u8fd9\u7fa4\u9053\u58eb\u51b2\u5165\u5c71\u5be8\uff0c\u81ea\u7136\u7684\u5206\u51fa\u4eba\u6765\u62e6\u622a\uff0c\u767d\u77f3\u9053\u4eba\u559d \u9053\uff1a\u201c\u628a\u5979\u4eec\u624b\u4e2d\u7684\u5175\u5668\u6253\u6389\uff01\u201d\u5973\u55bd\u5175\u4e2a\u4e2a\u594b\u52c7\uff0c\u6b66\u5f53\u805a\u5f1f\u5b50\u4e0d\u613f\u4f24\u4eba\uff0c\u4e00\u65f6\u95f2\u5374\u4e5f\u4e0d\u80fd \u8f7b\u6613\u5c06\u5973\u55bd\u5175\u7684\u5175\u5668\u593a\u51fa\u624b\u53bb\u3002\u767d\u77f3\u9053\u4eba\u4e0e\u7ea2\u4e91\u9053\u4eba\u8fde\u8882\u653b\u5165\uff0c\u7ea2\u4e91\u9053\u4eba\u5251\u4ea4\u5de6\u624b\uff0c\u4e0e\u767d\u77f3 \u9053\u4eba\u5de6\u53f3\u5206\u8fdb\uff0c\u6b66\u5f53\u4e8c\u8001\u7684\u529f\u529b\u975e\u6bd4\u5bfb\u5e38\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u628a\u5341\u9980\u540d\u5973\u55bd\u5175\u7684\u5175\u5668\u78d5\u98de\uff0c\u5200\u67aa\u4e71 \u821e\uff0c\u5be8\u4e2d\u5927\u4e71\u3002 \u3000\u3000\u7389\u7f57\u5239\u90a3\u77e5\u767d\u77f3\u9053\u4eba\u4e0e\u6155\u5bb9\u51b2\u6709\u90a3\u53c1\u4e2a\u534f\u5b9a\uff0c\u89c1\u4ed6\u4eec\u653b\u5165\u5927\u5be8\uff0c\u53ea\u9053\u4ed6\u4eec\u5df2\u4e0e\u5b98\u519b\u4e00 \u5925\uff0c\u751f\u6015\u4ed6\u4eec\u4e5f\u8981\u6740\u4eba\u653e\u706b\uff01\u53f1\u54fc\u4e00\u58f0\uff0c\u5237\u5237\u4e24\u5251\uff0c\u5c06\u6155\u5bb9\u51b2\u6740\u5f97\u95ea\u8fc7\u4e00\u8fb9\uff0c\u51b2\u51fa\u91cd\u56f4\uff0c\u5954 \u56de\u5927\u5be8\uff0c\u4e00\u67c4\u5251\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u6b66\u5f53\u6d3e\u5f1f\u5b50\u90a3\u622a\u5f97\u4f4f\uff0c\u76f4\u7ed9\u5979\u6740\u5165\u6838\u5fc3\uff0c\u767d\u77f3\u9053\u4eba\u6012 \u559d\u9053\uff1a\u201c\u5996\u5973\uff0c\u5feb\u628a\u6211\u4eec\u7684\u638c\u95e8\u5f1f\u5b50\u4ea4\u56de\u3002\u8981\u4e0d\u7136\u4f60\u4eca\u65e5\u96be\u9003\u516c\u9053\u3002\u201d\u7389\u7f57\u5239\u6012\u9053\uff1a\u201c\u4f60\u771f \u662f\u8fb1\u6ca1\u4e86\u7d2b\u9633\u9053\u957f\u7684\u82f1\u540d\uff0c\u6559\u5929\u4e0b\u82f1\u96c4\u7b11\u8bdd\uff1f\u201d\u5251\u62db\u75be\u5c55\uff0c\u628a\u767d\u77f3\u7ea2\u4e91\u4e8c\u4eba\u5168\u88f9\u5728\u5251\u5149\u4e4b \u4e2d\u3002 \u3000\u3000\u518d\u8bf4\u5353\u4e00\u822a\u5c1a\u672a\u5c31\u5bdd\uff0c\u84e6\u89c1\u5e08\u53d4\u7387\u540c\u95e8\u6740\u4eba\uff0c\u5413\u5f97\u5446\u4e86\u3002\u63c9\u63c9\u773c\u775b\uff0c\u77e5\u9053\u5e76\u975e\u6076\u68a6\uff0c\u96be \u8fc7\u4e4b\u6781\uff0c\u4e0d\u77e5\u5982\u4f55\u81ea\u5904\u3002\u8fc7\u4e86\u4e00\u9635\uff0c\u542c\u5f97\u60e8\u53eb\u4e4b\u58f0\u5927\u4f5c\uff0c\u539f\u6765\u7389\u7f57\u5239\u95ef\u56de\u5927\u5be8\uff0c\u5c71\u5be8\u5916\u7684\u5973 \u55bd\u5175\u90a3\u654c\u5f97\u6155\u5bb9\u51b2\u4ed6\u4eec\u7684\u8fdb\u653b\uff0c\u66f4\u517c\u5175\u529b\u5355\u8584\uff0c\u9635\u52bf\u5927\u4e71\uff0c\u4f24\u4ea1\u65e0\u6570\u3002\u8fde\u57ce\u864e\u7387\u4f17\u653b\u5165\u5927 \u5be8\uff0c\u5c31\u5728\u5be8\u4e2d\u653e\u8d77\u706b\u6765\uff0c\u5c71\u5be8\u90fd\u662f\u6728\u6750\u8305\u8349\u6240\u5efa\uff0c\u4e0d\u6bd4\u7816\u77f3\u623f\u5c4b\uff0c\u4e00\u88ab\u70b9\u71c3\uff0c\u52bf\u5373\u71ce\u539f\uff0c\u4e0d \u53ef\u6536\u62ac\u3002 \u3000\u3000\u5353\u4e00\u822a\u8033\u95fb\u60e8\u53eb\uff0c\u76ee\u7779\u706b\u5149\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u51b2\u4e86\u51fa\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u6211\u5728\u8fd9\u513f\u3002\u4f60 \u4eec\u4f55\u82e6\u7ed9\u5b98\u519b\u52a9\u6218\uff01\u201d\u767d\u77f3\u9053\u4eba\u9053\uff1a\u201c\u597d\uff0c\u4f60\u7acb\u5373\u548c\u6211\u56de\u5c71\u3002\u201d\u7387\u6b66\u5f53\u5f1f\u5b50\u53bb\u63a5\u5e94\u5353\u4e00\u822a\uff0c \u7389\u7f57\u5239\u6740\u5f97\u7ea2\u4e86\u773c\u775b\uff0c\u7d27\u8ffd\u4e0d\u820d\uff0c\u5979\u8eab\u6cd5\u5feb\u75be\uff0c\u62a2\u5148\u51b2\u5230\u5353\u4e00\u822a\u8eab\u8fb9\uff0c\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u8ba9\u6211 \u8d70\uff0c\u62b5\u6321\u5b98\u519b\u8981\u7d27\u3002\u201d\u628a\u5cb3\u9e23\u73c2\u7684\u4e66\u629b\u7ed9\u5979\u9053\uff1a\u201c\u5cb3\u5144\u4e4b\u6258\uff0c\u4f60\u66ff\u6211\u529e\u5427\u3002\u201d\u539f\u6765\u4ed6\u89c1\u5e08\u53d4 \u5982\u6b64\uff0c\u8fd9\u756a\u56de\u53bb\uff0c\u867d\u662f\u638c\u95e8\uff0c\u4e5f\u5fc5\u88ab\u770b\u7ba1\uff0c\u6240\u4ee5\u8981\u628a\u718a\u7ecf\u7565\u5173\u7cfb\u56fd\u8fd0\u7684\u5947\u4e66\uff0c\u8f6c\u4ea4\u7ed9\u7389\u7f57 \u5239\u3002 \u3000\u3000\u7389\u7f57\u5239\u6014\u4e86\u4e00\u6014\uff0c\u767d\u77f3\u9053\u4eba\u5df2\u5230\u8eab\u5f8c\uff0c\u7389\u7f57\u5239\u53cd\u624b\u4e00\u5251\uff0c\u53ee\u5f53\u4e00\u58f0\uff0c\u767d\u77f3\u9053\u4eba\u7684\u5251\u51e0\u4e4e \u7ed9\u5979\u9707\u98de\uff0c\u7ea2\u4e91\u9053\u4eba\u53eb\u9053\uff1a\u201c\u6211\u4eec\u63a5\u4e86\u638c\u95e8\u4fbf\u8d70\u3002\u7389\u7f57\u5239\u4f60\u786c\u8981\u4e0e\u6211\u4eec\u6b66\u5f53\u6d3e\u4f5c\u5bf9\u505a\u4ec0 \u9ebd\uff1f\u201d\u5be8\u4e2d\u547c\u58f0\u9707\u5929\uff01\u7389\u7f57\u5239\u54ac\u7259\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u8ba9\u4f60\u4eec\u8d70\uff01\u201d\u8eab\u5b50\u4e00\u4fa7\uff0c\u95ef\u51fa\u4eba\u4e1b\u3002\u6b66\u5f53\u6d3e \u5f1f\u5b50\u62e5\u7740\u5353\u4e00\u822a\u5168\u5e08\u800c\u9000\u3002 \u3000\u3000\u8fd9\u65f6\u5927\u5be8\u5df2\u5168\u88ab\u706b\u820c\u7b3c\u7f69\uff0c\u8fde\u57ce\u864e\u62a2\u4eba\u5be8\u5f8c\u641c\u4eba\uff0c\u6155\u5bb9\u51b2\u548c\u7389\u7f57\u5239\u5728\u706b\u5149\u4e2d\u6076\u6218\u3002\u5b98\u519b \u4e0e\u5973\u55bd\u5175\u7eb7\u7eb7\u51b2\u51fa\u5927\u5be8\uff0c\u970e\u90a3\u95f2\uff0c\u706b\u52bf\u8d8a\u70e7\u8d9f\u76db\uff0c\u770b\u770b\u4fbf\u6210\u706b\u6d77\u3002\u6155\u5bb9\u51b2\u4e0e\u7389\u7f57\u5239\u8d81\u7740\u706b\u52bf \u5c1a\u672a\u5408\u62e2\uff0c\u8fb9\u6253\u8fb9\u8d70\uff0c\u95ef\u51fa\u5916\u9762\u3002\u9003\u4e0d\u53ca\u7684\u5b98\u519b\u4e0e\u5973\u55bd\u5175\u5728\u706b\u6d77\u4e2d\u547c\u53f7\uff0c\u8f6c\u77ac\u5316\u6210\u7070\u70ec\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5973\u55bd\u5175\u5341\u4e5d\u4f24\u4ea1\uff0c\u5b98\u519b\u4e5f\u6298\u635f\u8fc7\u534a\u3002\u7389\u7f57\u5239\u6012\u6781\u6c14\u6781\uff0c\u6599\u4e0d\u5230\u53c1\u5e74\u6765\u7684\u5fc3\u8840\uff0c\u82e6 \u5fc3\u5efa\u7acb\u7684\u6839\u57fa\uff0c\u4e00\u65e6\u7070\u98de\u70df\u706d\uff01\u66f4\u4f24\u5fc3\u7684\u662f\uff1a\u51e0\u767e\u540d\u5973\u5175\uff0c\u6570\u5e74\u6765\u540c\u751f\u5171\u6b7b\uff0c\u60c5\u540c\u59d0\u59b9\uff0c\u800c \u4eca\u5374\u4e0d\u77e5\u80fd\u5269\u51e0\u4e2a\u9003\u751f\u3002\u4f24\u5fc3\u5230\u6781\uff0c\u62da\u4e86\u6027\u547d\uff0c\u5251\u622e\u638c\u5288\uff0c\u8eab\u6cd5\u5982\u98ce\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u8fde\u6bd9\u5341\u9980 \u5b98\u519b\uff0c\u6155\u5bb9\u51b2\u8d76\u6765\u622a\u51fb\uff0c\u4f46\u4ed6\u8eab\u6cd5\u4e0d\u82e5\u7389\u7f57\u5239\u8f7b\u7075\uff0c\u7389\u7f57\u5239\u5728\u5b98\u519b\u4e2d\u7a7f\u6765\u63d2\u53bb\uff0c\u8f6c\u77ac\u4e4b\u95f4\uff0c \u53c8\u6bd9\u4e86\u5341\u540d\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u5ffd\u95fb\u5f97\u6709\u4eba\u558a\u9053\uff1a\u201c\u4f60\u4eec\u6563\u5f00\uff0c\u8ffd\u6355\u55bd\u5175\uff0c\u8ba9\u6211\u4eec\u6765\u5bf9\u4ed8\u8fd9\u4e2a\u5996\u5973\u3002\u201d\u539f\u6765\u662f\u8fde \u57ce\u864e\u5df2\u5c06\u5e94\u4fee\u9633\u6551\u51fa\uff0c\u5e94\u4fee\u9633\u517b\u4e86\u4e24\u5929\uff0c\u811a\u4f24\u5df2\u6108\uff0c\u5927\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u653e\u8d70\u8fd9\u4e2a\u5996\u5973\uff01\u201d\u4e0e\u8fde \u57ce\u864e\u5de6\u53f3\u5835\u622a\uff0c\u7389\u7f57\u5239\u5927\u6012\uff0c\u8fce\u9762\u4e00\u5251\uff0c\u523a\u5589\u5499\uff0c\u6233\u5fc3\u7a9d\uff0c\u653b\u52bf\u5947\u5e7b\u65e0\u6bd4\uff0c\u5e94\u4fee\u9633\u529b\u6321\u4e00 \u62db\uff0c\u7389\u7f57\u5239\u4e8c\u53c1\u4e24\u62db\uff0c\u63a5\u8fde\u53d1\u51fa\uff0c\u62db\u62db\u90fd\u662f\u6740\u624b\uff0c\u5e94\u4fee\u9633\u9669\u4e27\u751f\u5728\u5251\u950b\u4e4b\u4e0b\uff0c\u5e78\u5f97\u8fde\u57ce\u864e\u80cc \u5f8c\u88ad\u5230\uff0c\u53cc\u94a9\u95ea\u95ea\uff0c\u6025\u6765\u6551\u62a4\uff0c\u7389\u7f57\u5239\u53cd\u624b\u4e00\u5251\uff0c\u53ee\u5f53\u4e00\u58f0\uff0c\u5c06\u53cc\u94a9\u683c\u5f00\uff0c\u5404\u81ea\u9707\u9000\u51e0\u6b65\uff0c \u5e94\u4fee\u9633\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u4e3e\u8d77\u62c2\u5c18\uff0c\u53ea\u6562\u5728\u4fa7\u9762\u52a9\u653b\u3002 \u3000\u3000\u8fde\u57ce\u864e\u66fe\u4e3a\u897f\u5382\u536b\u58eb\u7684\u603b\u6559\u5934\uff0c\u5728\u5bab\u5ef7\u7684\u6821\u5c09\u536b\u58eb\u4e2d\uff0c\u6b66\u529f\u4ec5\u5728\u6155\u5bb9\u51b2\u4e4b\u4e0b\uff0c\u5374\u5728\u5e94\u4fee \u9633\u4e4b\u4e0a\uff0c\u53cc\u94a9\u906e\u62e6\u653b\u62d2\uff0c\u5c45\u7136\u654c\u4e86\u5341\u591a\u62db\uff0c\u6155\u5bb9\u51b2\u6325\u62f3\u51b2\u4e0a\uff0c\u6210\u4e86\u5408\u56f4\u4e4b\u52bf\u3002\u5c06\u7389\u7f57\u5239\u56f0\u5728 \u6838\u5fc3\u3002 \u3000\u3000\u8fd9\u65f6\u5269\u4e0b\u7684\u5973\u55bd\u5175\u7eb7\u7eb7\u9003\u751f\uff0c\u8fb9\u9003\u8fb9\u53eb\u9053\uff1a\u201c\u5be8\u4e3b\uff0c\u5feb\u9003\u51fa\u6765\u5427\uff01\u201d\u6709\u719f\u77e5\u7389\u7f57\u5239\u6027\u683c \u7684\u8fd8\u53eb\u9053\uff1a\u201c\u5be8\u4e3b\uff0c\u7559\u5f97\u9752\u5c71\u5728\uff0c\u4e0d\u6015\u6ca1\u67f4\u70e7\u3002\u4e0d\u8981\u4e0e\u4ed6\u4eec\u786c\u62da\u3002\u201d\u7389\u7f57\u5239\u5fc3\u5934\u4e00\u9707\uff0c\u53ef\u662f \u8fd9\u65f6\u60f3\u9003\u5df2\u662f\u4e0d\u80fd\u3002\u6155\u5bb9\u51b2\u7684\u6b66\u529f\u4e0e\u5979\u76f8\u5f53\uff01\u8fde\u57ce\u864e\u6bd4\u5979\u4e5f\u4ec5\u7565\u900a\u4e00\u7b79\uff0c\u5e94\u4fee\u9633\u867d\u7136\u8f83\u5dee\uff0c \u4f46\u5728\u53c1\u4eba\u5408\u56f4\u7684\u60c5\u52bf\u4e4b\u4e0b\uff0c\u4ed6\u4e5f\u53ef\u4ee5\u62db\u67b6\u5f97\u4f4f\u3002\u7389\u7f57\u5239\u8f7b\u529f\u867d\u597d\uff0c\u4f46\u5df3\u88ab\u6155\u5bb9\u51b2\u62f3\u98ce\u6240\u7f69\uff0c \u82e5\u7136\u6536\u5251\u9003\u65f6\uff0c\u5fc5\u88ab\u638c\u529b\u6240\u4f24\u3002\u4f55\u51b5\u8fde\u57ce\u864e\u7684\u65e5\u6708\u53cc\u94a9\uff0c\u65e2\u53ef\u9501\u62ff\u5175\u5668\uff0c\u53c8\u53ef\u9493\u62c9\u624b\u8db3\uff0c\u82e5 \u7136\u98de\u8eab\u8dc3\u8d77\uff0c\u4e5f\u6050\u88ab\u4ed6\u53cc\u94a9\u6240\u4f24\u3002 \u3000\u3000\u5973\u55bd\u5175\u6b7b\u7684\u6b7b\uff0c\u4f24\u7684\u4f24\uff0c\u9003\u7684\u9003\uff0c\u660e\u6708\u5ce1\u7684\u5c71\u5934\u4e0a\u5269\u4e0b\u7389\u7f57\u5239\u4e00\u4eba\u5174\u5b98\u519b\u6740\u3002\u6155\u5bb9\u51b2\u7b49 \u53c1\u4eba\u8d8a\u653b\u8d8a\u7d27\uff0c\u7389\u7f57\u5239\u4e00\u67c4\u5251\u4f7f\u5f97\u51fa\u795e\u5165\u5316\uff0c\u53d8\u5e7b\u65e0\u7a77\uff0c\u4f46\u4e5f\u4ec5\u80fd\u81ea\u4fdd\u3002\u6740\u4e86\u4e2a\u591a\u65f6\u8fb0\uff0c\u62da \u6597\u4f55\u6b62\u5343\u62db\uff0c\u65f6\u95f4\u5df2\u8fd1\u5348\u591c\uff0c\u7389\u7f57\u5239\u6c14\u529b\u6e10\u7aed\uff0c\u529b\u4e0d\u4ece\u5fc3\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u9053\u6211\u4eca\u665a\u4e27\u547d\u6b64 \u5730\uff1f\u201d\u5b98\u519b\u4eec\u56f4\u5728\u56db\u5468\uff0c\u867d\u7136\u4e0d\u6562\u63d2\u624b\uff0c\u5374\u5728\u65c1\u8fb9\u5450\u558a\u52a9\u5a01\uff0c\u5927\u58f0\u7b11\u9a82\u3002\u6709\u4eba\u7b11\u9053\uff1a\u201c\u8fd9\u6837 \u7f8e\u7684\u8d3c\u5a46\u5a18\u6211\u53ef\u820d\u4e0d\u5f97\u4f24\u5979\uff1f\u201d\u6709\u4eba\u7b11\u9053\uff1a\u201c\u5478\uff0c\u6349\u4e86\u5979\u4e5f\u8f6e\u4e0d\u5230\u4f60\uff01\u201d\u7389\u7f57\u5239\u6c14\u5f97\u53d1\u660f\uff0c \u5251\u62db\u6e10\u4e71\u3002 \u3000\u3000\u6b63\u5728\u5b98\u519b\u95f2\u7b11\u4e4b\u9645\uff0c\u5ffd\u5730\u6709\u4eba\u5de8\u96f7\u822c\u7684\u5927\u559d\u9053\uff1a\u201c\u8d3c\u5a18\u7684\uff0c\u4f60\u4eec\u6562\u6b3a\u4fae\u6211\u7684\u4e7e\u5973\u513f\uff01\u201d \u559d\u58f0\u672a\u505c\uff0c\u5b98\u519b\u60e8\u53eb\u5df2\u8d77\uff0c\u94c1\u98de\u9f99\u76f4\u51b2\u4eba\u6765\uff0c\u4e00\u624b\u4e00\u4e2a\uff0c\u50cf\u6454\u7a3b\u8349\u4eba\u4e00\u6837\uff0c\u5c06\u5b98\u519b\u4e00\u4e2a\u4e2a\u6454 \u4e0b\u5c71\u8c37\u3002 \u3000\u3000\u6b63\u662f\uff1a\u9739\u96f3\u4e00\u58f0\u5bd2\u8d3c\u80c6\uff0c\u4eca\u5bb5\u53c8\u89c1\u8001\u82f1\u5806\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u4e00\u56de\u3000\u6bc1\u5be8\u5269\u9980\u54c0\u3000\u60c5\u7559\u5757\u571f\u3000\u8bd5\u62db\u9980\u4e00\u7b11\u3000\u6168\u8d60\u85cf\u73cd \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377 \u7b2c\u4e8c\u5341\u4e00\u56de \u6bc1\u5be8\u5269\u9980\u54c0\u3000\u60c5\u7559\u5757\u571f \u8bd5\u62db\u9980\u4e00\u7b11\u3000\u6168\u8d60\u85cf\u73cd \u3000\u3000\u6155\u5bb9\u51b2\u6012\u9053\uff1a\u201c\u53c8\u662f\u4f60\u8fd9\u4e2a\u8001\u5339\u592b\u3002\u201d\u94c1\u98de\u9f99\u559d\u9053\uff1a\u201c\u8001\u592b\u8981\u4f60\u7684\u547d\uff01\u201d\u6155\u5bb9\u51b2\u52b2\u62f3\u6363 \u51fa\uff0c\u94c1\u98de\u9f99\u6a2a\u638c\u4e00\u63a5\uff0c\u6b63\u5982\u77f3\u5802\u9047\u7740\u94c1\u626b\u628a\uff0c\u201c\u7830\u201d\u7136\u4e00\u58f0\uff0c\u4e24\u4eba\u90fd\u7ed9\u5bf9\u65b9\u7684\u52b2\u529b\u649e\u5f97\u6b6a\u6b6a \u659c\u659c\u9000\u8fc7\u4e00\u8fb9\u3002 \u3000\u3000\u7389\u7f57\u5239\u7cbe\u795e\u5927\u632f\uff0c\u4e00\u62db\u201c\u661f\u6a2a\u6597\u8f6c\u201d\uff0c\u5c06\u8fde\u57ce\u864e\u7684\u53cc\u94a9\u62e6\u8fc7\u4e00\u8fb9\uff0c\u6155\u5bb9\u51b2\u594b\u8eab\u518d\u4e0a\uff0c\u94c1 \u98de\u9f99\u5df2\u62a2\u8fc7\u6765\u63a5\u4f4f\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u5f62\u52bf\u5927\u53d8\uff0c\u94c1\u98de\u9f99\u53f1\u54fc\u8fde\u58f0\uff0c\u6309\u7740\u4e94\u884c\u516b\u5366\u65b9\u4f4d\uff0c\u5f3a\u653b\u731b\u6253\uff0c\u6155\u5bb9\u51b2\u6c89\u8170\u5750\u9a6c\uff0c \u597d\u50cf\u9489\u5728\u5730\u4e0a\u4f3c\u7684\uff0c\u89c1\u62db\u62c6\u62db\uff0c\u89c1\u5f0f\u7834\u5f0f\uff0c\u5206\u6beb\u4e5f\u4e0d\u79fb\u52a8\uff0c\u8fd9\u4e24\u4eba\u4e00\u4e2a\u662f\u638c\u529b\u6c89\u96c4\uff0c\u4e00\u4e2a\u662f \u795e\u62f3\u65e0\u654c\uff0c\u4e00\u653b\u4e00\u5b88\uff0c\u53ea\u6253\u5f97\u7802\u77f3\u7eb7\u98de\uff0c\u5b98\u519b\u4eec\u7eb7\u7eb7\u8d70\u907f\u3002 \u3000\u3000\u7389\u7f57\u5239\u5c11\u4e86\u4e00\u4e2a\u5f3a\u654c\uff0c\uff0c\u4e00\u53e3\u5251\u9f99\u98de\u51e4\u821e\uff0c\u7740\u7740\u5f3a\u653b\uff0c\u5c06\u8fde\u57ce\u864e\u4e0e\u5e94\u4fee\u9633\u6740\u5f97\u5fc3\u60ca\u80c6 \u6218\u3002\u6b63\u6fc0\u6218\u95f4\uff0c\u5ffd\u95fb\u5f97\u94c1\u98de\u9f99\u95ee\u9053\uff1a\u201c\u4f60\u7684\u73ca\u745a\u59b9\u5b50\u5462\uff1f\u201d\u7389\u7f57\u5239\u5fc3\u5934\u4e00\u9707\uff0c\u8fde\u57ce\u864e\u5de6\u94a9\u4e00 \u62c9\uff0c\u53f3\u94a9\u4e00\u63d2\uff0c\u7389\u7f57\u5239\u8f6c\u8eab\u7a0d\u8fdf\uff0c\u8863\u8896\u7adf\u7ed9\u6495\u53bb\u4e00\u7247\u3002\u7389\u7f57\u5239\u52c3\u7136\u5927\u6012\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u559d\u58f0 \u201c\u7740\uff01\u201d\u8fde\u57ce\u864e\u53cc\u94a9\u56de\u6551\u4e0d\u53ca\uff0c\u201c\u6ce2\u201d\u7684\u4e00\u58f0\uff0c\u80a9\u80db\u9aa8\u7ed9\u5251\u523a\u7a7f\uff0c\u7389\u7f57\u5239\u5ffd\u5730\u54c8\u54c8\u72c2\u7b11\uff0c\u8fde \u57ce\u864e\u4e0e\u5e94\u4fee\u9633\u62da\u547d\u5954\u9003\uff0c\u7389\u7f57\u5239\u5982\u75f4\u82e5\u72c2\uff0c\u7adf\u7136\u4e0d\u6653\u5f97\u8ffd\u8d76\u3002 \u3000\u3000\u94c1\u98de\u9f99\u9a87\u7136\u5fc3\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u5566\uff1f\u201d\u547c\u547c\u4e24\u638c\uff0c\u5f3a\u626b\u6155\u5bb9\u51b2\u4e2d\u76d8\uff0c\u6155\u5bb9\u51b2\u6253\u4e86\u534a \u591c\uff0c\u6c14\u529b\u4e0a\u5df2\u5403\u4e86\u4e8f\uff1a\u89c1\u540c\u4f34\u8d25\u9003\uff0c\u65e0\u5fc3\u604b\u6218\uff0c\u594b\u529b\u4e00\u67b6\uff0c\u8f6c\u8eab\u4ea6\u9003\u3002 \u3000\u3000\u94c1\u98de\u9f99\u5fc3\u77e5\u6709\u5f02\uff0c\u62a2\u8fc7\u6765\u5c06\u7389\u7f57\u5239\u6276\u7740\uff0c\u7389\u7f57\u5239\u72c2\u7b11\u5982\u54ed\uff0c\u94c1\u98de\u9f99\u9053\uff1a\u201c\u654c\u4eba\u90fd\u5df2\u9003 \u5566\uff01\u201d\u7389\u7f57\u5239\u4e00\u8de4\u8dcc\u843d\u5730\u4e0a\uff0c\u53eb\u9053\uff1a\u201c\u7239\uff0c\u6211\u5bf9\u4e0d\u4f4f\u4f60\uff01\u201d\u94c1\u98de\u9f99\u9a87\u6781\u8bf4\u9053\uff1a\u201c\u6709\u8bdd\u6162 \u8bf4\u3002\u201d\u7389\u7f57\u5239\u5927\u75db\u4e4b\u5f8c\uff0c\u7ee7\u4ee5\u6fc0\u6218\uff0c\u8fd9\u65f6\u53ea\u89c9\u767e\u9ab8\u6b32\u6563\uff0c\u8ff7\u8ff7\u7cca\u7cca\uff0c\u53cc\u773c\u4e00\u5408\uff0c\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u94c1\u98de\u9f99\u9053\uff1a\u201c\u53ef\u601c\u7684\u5b69\u5b50\uff0c\u4f60\u7d2f\u591f\u5566\uff01\u201d\u8fd9\u65f6\u5c71\u5be8\u5df2\u5316\u6210\u7070\u70ec\uff0c\u706b\u52bf\u5c1a\u81ea\u5411\u6797\u4e2d\u8513\u5ef6\u3002 \u94c1\u98de\u9f99\u5343\u8f9b\u4e07\u82e6\uff0c\u5386\u4e86\u53c1\u5e74\uff0c\u59cb\u91c7\u5f97\u94c1\u73ca\u745a\u548c\u7389\u7f57\u5239\u7684\u4e0b\u843d\uff0c\u4e0d\u6599\u8fdc\u9053\u8d76\u6765\uff0c\u5374\u6b63\u51d1\u5f97\u4e0a\u89c1 \u5c71\u5be8\u6bc1\u706d\u3002\u5fc3\u5934\u9e7f\u649e\uff0c\u72c2\u8df3\u4e0d\u4f11\uff0c\u628a\u773c\u56db\u671b\uff0c\u5b98\u519b\u90fd\u5df2\u9003\u51c0\uff0c\u5bc2\u65e0\u4eba\u58f0\uff0c\u706b\u5149\u4e2d\u53ea\u95fb\u5f97\u6797\u9e1f \u60ca\u98de\uff0c\u733f\u7334\u54c0\u53eb\u3002 \u3000\u3000\u94c1\u98de\u9f99\u53eb\u9053\uff1a\u201c\u73ca\u745a\uff0c\u73ca\u745a\uff01\u201d\u58f0\u97f3\u6563\u4eba\u6797\u4e2d\uff0c\u6709\u5c71\u5cf0\u56de\u54cd\u3002\u94c1\u98de\u9f99\u5f15\u542d\u9ad8\u547c\uff0c\u8fc7\u4e86\u8bb8 \u4e45\uff0c\u4e24\u4e2a\u5973\u55bd\u5175\u722c\u4e86\u4e0a\u6765\uff0c\u5979\u4eec\u662f\u4fa5\u5e78\u9003\u8131\u8eb2\u5728\u5c71\u8170\u8305\u8349\u4e2d\u7684\u3002 \u3000\u3000\u4e24\u4e2a\u5973\u55bd\u5175\u4e0d\u77e5\u94c1\u98de\u9f99\u662f\u4f55\u7b49\u6837\u4eba\uff0c\u4f46\u89c1\u4ed6\u7a7f\u7684\u662f\u5e73\u6c11\u670d\u9970\uff0c\u5c71\u5934\u4e0a\u53c8\u5df2\u65e0\u5b98\u519b\uff0c\u6599\u4ed6 \u5b9a\u662f\u5be8\u4e3b\u670b\u53cb\uff0c\u722c\u4e86\u4e0a\u6765\uff0c\u6ce3\u7136\u8bf4\u9053\uff1a\u201c\u94c1\u5be8\u4e3b\u65e9\u5df2\u6b7b\u5566\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u4e00\u75db\u996e\u7edd\uff0c\u4ed6\u6709\u8fd9\u4e2a\u5973\u513f\uff0c\u6599\u4e0d\u5230\u4e07\u6c34\u5343\u5c71\u5bfb\u8e2a\u89c5\u8ff9\uff0c\u7adf\u4e0d\u80fd\u89c1\u4e0a\u4e00\u9762\u3002 \u3000\u3000\u826f\u4e45\u826f\u4e45\uff0c\u94c1\u98de\u9f99\u624d\u8bf4\u5f97\u51fa\u58f0\uff0c\u542c\u5973\u55bd\u5175\u5c06\u8fd9\u51e0\u65e5\u6765\u5c71\u5be8\u7684\u53d8\u6545\u8bf4\u5f8c\uff0c\u864e\u76ee\u6d41\u6cea\uff0c\u72c2\u53eb \u9053\uff1a\u201c\u6211\u6765\u8fdf\u4e86\uff01\u201d \u3000\u3000\u5973\u55bd\u5175\u89c1\u6b64\u60c5\u5f62\uff0c\u9a87\u7136\u8bf4\u9053\uff1a\u201c\u8001\u5148\u751f\u83ab\u975e\u5c31\u662f\u5a01\u9707\u897f\u5317\u7684\u94c1\u8001\u82f1\u96c4\uff1f\u201d\u94c1\u98de\u9f99\u7acb\u5982\u50f5 \u77f3\uff0c\u773c\u775b\u5982\u5b9a\u73e0\uff0c\u8111\u6d77\u4e2d\u6b63\u98d8\u6d6e\u7740\u94c1\u73ca\u745a\u513f\u65f6\u6d3b\u6cfc\u5b09\u620f\u7684\u5f71\u5b50\uff0c\u5bf9\u5973\u55bd\u5175\u7684\u8bdd\u542c\u800c\u4e0d\u95fb\uff0c\u5c31 \u50cf\u7acb\u5728\u5c71\u5934\u7684\u4e00\u5c0a\u77f3\u50cf\u3002 \u3000\u3000\u5973\u55bd\u5175\u53c8\u53d1\u73b0\u4e86\u5367\u5728\u5730\u4e0a\u7684\u7389\u7f57\u5239\uff0c\u8fd9\u4e00\u5413\u66f4\u662f\u975e\u540c\u5c0f\u9119\uff0c\u8d70\u8fc7\u53bb\u63a8\u4e86\u4e24\u63a8\uff0c\u7389\u7f57\u5239\u8f6c \u4e86\u4e2a\u8eab\uff0c\u6d51\u5982\u672a\u89c9\uff0c\u5973\u55bd\u5175\u5413\u5f97\u614c\u4e86\uff0c\u8dd1\u8fc7\u53bb\u62b1\u7740\u94c1\u98de\u9f99\u7684\u817f\u53eb\u9053\uff1a\u201c\u94c1\u8001\u82f1\u96c4\uff0c\u4f60\u770b\u770b\u6211 \u4eec\u7684\u5be8\u4e3b\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u500f\u7136\u9192\u8f6c\uff0c\u54fd\u54bd\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u653e\u5fc3\uff0c\u8fd9\u4e2a\u4e7e\u5973\u513f\u6211\u518d\u4e5f\u4e0d\u80fd\u5931\u4e86\uff01\u201d\u7389\u7f57\u5239\u8f6c\u4e86 \u4e2a\u8eab\uff0c\u53eb\u9053\uff1a\u201c\u73ca\u745a\u59b9\uff0c\u6211\u66ff\u4f60\u62a5\u4ec7\uff01\u201d\u94c1\u98de\u9f99\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u8fd8\u5e94\u66ff\u5973\u513f\u62a5 \u4ec7\uff01\u201d\u7389\u7f57\u5239\u53c8\u8f6c\u4e86\u4e2a\u8eab\uff0c\u53eb\u9053\uff1a\u201c\u5353\u4e00\u822a\uff0c\u4f60\u597d\u2026\u2026\u201d\u94c1\u98de\u9f99\u65e0\u9650\u4f24\u5fc3\uff0c\u4ed6\u5df2\u4ece\u5973\u55bd\u5175\u53e3 \u4e2d\u77e5\u9053\u4eca\u665a\u4e4b\u4e8b\uff0c\u5fc3\u9053\uff1a\u201c\u53ef\u601c\u4f60\u7231\u9519\u4eba\u4e86\u3002\u4ed6\u662f\u5b98\u5bb6\u5b50\u5f1f\u51fa\u8eab\uff0c\u6240\u5c11\u7684\u6b63\u662f\u7eff\u6797\u8c6a\u6770\u7684\u6c14 \u6982\uff0c\u51e1\u4e8b\u62ff\u4e0d\u8d77\u653e\u4e0d\u4e0b\uff0c\u5bf9\u5a5a\u59fb\u5927\u4e8b\u4e5f\u662f\u4e00\u822c\u3002\u7eb5\u6ca1\u6709\u4ed6\u7684\u5e08\u53d4\u963b\u62e6\uff0c\u4f60\u4eec\u4e24\u4eba\u4e5f\u5e76\u4e0d\u5339 \u914d\u3002\u201d\u8fd9\u65f6\u5ffd\u89c9\u81ea\u5df2\u5973\u513f\u7684\u773c\u5149\u8fd8\u8981\u6bd4\u7389\u7f57\u5239\u9ad8\u660e\uff0c\u5fc3\u4e2d\u66f4\u89c9\u51c4\u82e6\u3002 \u3000\u3000\u94c1\u98de\u9f99\u8d70\u8fd1\u4e24\u6b65\uff0c\u542c\u5f97\u7389\u7f57\u5239\u53c8\u72c2\u7b11\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u4f60\u4eec\u90fd\u8d70\u5566\uff01\u73ca\u745a\u5b50\uff0c\u4f60\u8d70\u5f97\u597d\uff0c\u9e23 \u73c2\uff0c\u4f60\u8fd9\u5c0f\u5b50\u4e5f\u8d70\u5f97\u597d\uff0c\u4e00\u822a\u5440\u4e00\u822a\uff0c\u6709\u4f60\u8d70\u5f97\u4e0d\u597d\uff01\u2026\u2026\u201d\u94c1\u98de\u9f99\u77e5\u5979\u75db\u6781\u75af\u72c2\uff0c\u4e00\u624b\u628a \u5979\u62c9\u5230\u6000\u4e2d\uff0c\u5fcd\u7740\u60b2\u75db\uff0c\u8f7b\u8f7b\u5524\u9053\uff1a\u201c\u88f3\u513f\uff0c\u4f60\u770b\u770b\uff0c\u6211\u5728\u8fd9\u513f\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u60a0\u60a0\u9192\u8f6c\uff0c\u770b\u4e86\u94c1\u98de\u9f99\u4e00\u773c\uff0c\u63a9\u9762\u5927\u54ed\uff0c\u94c1\u98de\u9f99\u9053\uff1a\u201c\u54b1\u4eec\u7236\u5973\u76f8\u4f9d\u4e3a\u547d\uff0c\u4eca\u5f8c \u4e0d\u800d\u518d\u8d70\u6563\u4e86\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u7239\uff0c\u6211\u4fdd\u62a4\u4e0d\u4e86\u73ca\u745a\u59b9\u59b9\uff0c\u6211\u771f\u8be5\u6b7b\uff01\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u8fd9\u4e2a \u602a\u4e0d\u4e86\u4f60\uff0c\u522b\u54ed\uff0c\u522b\u54ed\uff0c\u4f60\u5e26\u6211\u770b\u73ca\u745a\u7684\u5893\u5427\u3002\u201d\u4ed6\u529d\u7389\u7f57\u5239\u522b\u54ed\uff0c\u81ea\u5df1\u5374\u6ef4\u51fa\u6cea\u4e86\u3002 \u3000\u3000\u7389\u7f57\u5239\u7275\u7740\u94c1\u98de\u9f99\u7684\u624b\uff0c\u9ed8\u9ed8\u8d70\u4e0b\u5c71\u8c37\uff0c\u5973\u55bd\u5175\u8ddf\u7740\u4e0b\u5c71\uff0c\u6cbf\u9014\u547c\u5524\uff0c\u6709\u5341\u591a\u4e2a\u9003\u5f97\u6027 \u547d\u7684\u5973\u55bd\u5175\u95fb\u58f0\u805a\u96c6\u4e86\u6765\uff0c\u89c1\u7389\u7f57\u5239\u9762\u8272\u60e8\u767d\uff0c\u53cc\u5507\u7d27\u95ed\uff0c\u8c01\u90fd\u4e0d\u6562\u8bf4\u8bdd\uff0c\u8ddf\u7740\u5979\u76f4\u8d70\u5230\u8c37 \u5e95\u90a3\u4e24\u4e2a\u65b0\u5efa\u7684\u575f\u5893\u4e4b\u524d\u3002\u7389\u7f57\u5239\u64ae\u571f\u4e3a\u9999\uff0c\u62dc\u4e86\u53c1\u62dc\uff0c\u94c1\u98de\u9f99\u5750\u5728\u575f\u5934\uff0c\u51dd\u671b\u591c\u7a7a\uff0c\u4e0d\u8a00 \u4e0d\u8bed\uff0c\u4f3c\u4e4e\u8fde\u773c\u6cea\u4e5f\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u94c1\u98de\u9f99\u4e0e\u7389\u7f57\u5239\u4e00\u4e2a\u5750\u5728\u575f\u5934\uff0c\u4e00\u4e2a\u7acb\u5728\u5893\u524d\uff0c\u76f8\u5bf9\u65e0\u8a00\uff0c\u4e0d\u89c9\u4e1c\u65b9\u5df2\u767d\u3002\u5973\u55bd\u5175\u9053\uff1a \u201c\u5be8\u4e3b\uff0c\u6b7b\u8005\u4e0d\u80fd\u590d\u751f\uff0c\u54b1\u4eec\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u4e00\u58f0\u51c4\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u53eb\u6211\u56de\u5230\u90a3\u91cc\u53bb\uff1f\u201d\u5973\u55bd\u5175\u60f3\u8d77\u5c71\u5be8\u5df2\u6210\u7070\u70ec\uff0c\u540c\u4f34\u5341\u4e5d\u4f24 \u4ea1\uff0c\u6570\u8f7d\u7ecf\u8425\uff0c\u6bc1\u65bc\u4e00\u65e5\u201d\uff0c\u771f\u662f\u6b32\u5f52\u65e0\u5904\uff0c\u5927\u5bb6\u54bd\u6cea\u4f24\u5fc3\uff0c\u53c8\u90fd\u4e0d\u6562\u8bf4\u8bdd\u3002 \u3000\u3000\u518d\u8fc7\u4e00\u9635\uff0c\u671d\u9633\u5347\u8d77\uff0c\u9633\u5149\u5df2\u4ece\u6811\u53f6\u4e1b\u4e2d\u900f\u4e0b\u6df1\u8c37\uff0c\u5973\u55bd\u5175\u6b63\u60f3\u518d\u884c\u529d\u8bf4\uff0c\u5ffd\u95fb\u5f97\u5c71\u53e3 \u5916\u6709\u4eba\u9a6c\u884c\u8d70\u4e4b\u58f0\uff0c\u7389\u7f57\u5239\u500f\u7136\u8df3\u8d77\uff0c\u6012\u9053\uff1a\u201c\u54fc\uff0c\u4ed6\u4eec\u8fd8\u60f3\u65a9\u5c3d\u6740\u7edd\uff1f\u201d\u94c1\u98de\u9f99\u8df3\u4e0a\u5c71 \u5761\uff0c\u624b\u6273\u5927\u77f3\uff0c\u8bf4\u9053\uff1a\u201c\u8ba9\u4ed6\u4eec\u8fdb\u6765\uff0c\u6211\u8981\u628a\u4ed6\u4eec\u90fd\u57cb\u5728\u5c71\u8c37\uff1f\u201d\u4e24\u4eba\u90fd\u4ee5\u4e3a\u6765\u7684\u5b9a\u662f\u5b98 \u519b\uff0c\u4e00\u8154\u6012\u6c14\uff0c\u7d27\u5f20\u5f85\u654c\u3002 \u3000\u3000\u90a3\u5c71\u8c37\u524d\u65e5\u88ab\u5d29\u96ea\u6240\u5c01\uff0c\u867d\u7136\u7ed9\u5973\u55bd\u5175\u6398\u5f00\uff0c\u4ec5\u53ef\u4f9b\u4e00\u4eba\u4e00\u9a91\u901a\u8fc7\u3002\u94c1\u98de\u9f99\u4f0f\u5728\u5c71\u4e0a\uff0c \u51c6\u5907\u5b98\u519b\u4e00\u4eba\u5c71\u53e3\uff0c\u4fbf\u5c06\u5927\u77f3\u63a8\u4e0b\uff0c\u5c06\u4ed6\u4eec\u751f\u57cb\uff01 \u3000\u3000\u4e0d\u4e00\u523b\uff0c\u8c37\u53e3\u65d7\u5e1c\u98d8\u626c\uff0c\u9a6c\u8e44\u5f97\u5f97\u4e00\u5f6a\u4eba\u9a6c\uff0c\u5217\u6210\u5355\u884c\u8d70\u8fdb\u3002\u94c1\u98de\u9f99\u6012\u543c\u4e00\u58f0\uff0c\u624b\u63a8\u5927 \u77f3\uff0c\u7389\u7f57\u5239\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u4e14\u6162\u3002\u201d\u90a3\u5757\u5927\u77f3\u5df2\u5e26\u7740\u5c18\u571f\u6eda\u4e0b\u5c71\u5761\uff01\u94c1\u98de\u9f99\u6025\u5fd9\u4f4f\u624b\uff0c\u770b\u6e05\u695a \u65f6\uff0c\u53ea\u89c1\u8d70\u5165\u5c71\u53e3\u90a3\u5f6a\u4eba\u9a6c\uff0c\u7adf\u5168\u662f\u5a18\u5b50\u519b\uff01 \u3000\u3000\u7389\u7f57\u5239\u53eb\u9053\uff1a\u201c\u7cdf\uff0c\u4e0d\u662f\u5b98\u519b\uff01\u201d\u548c\u94c1\u98de\u9f99\u98de\u8eab\u6251\u53bb\u62a2\u6551\uff0c\u90a3\u5757\u77f3\u5934\u6eda\u5f97\u751a\u5feb\uff0c\u5230\u4e86\u5c71 \u8170\uff0c\u78b0\u7740\u53e6\u4e00\u5757\u51f8\u51fa\u6765\u7684\u5ca9\u77f3\uff0c\u7a81\u7136\u51cc\u7a7a\u98de\u5815\uff0c\u5176\u52bf\u731b\u6781\uff01\u7389\u7f57\u5239\u548c\u94c1\u98de\u9f99\u8eab\u6cd5\u518d\u5feb\uff0c\u4e5f\u8d76 \u4e0d\u4e0a\u90a3\u5757\u5927\u77f3\u4e0b\u5815\u4e4b\u52bf\uff01 \u3000\u3000\u94c1\u98de\u9f99\u53eb\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u201d\u961f\u4f0d\u4e2d\u8d70\u5728\u524d\u5934\u7684\u4e00\u540d\u5973\u5c06\uff0c\u7a81\u4ece\u9a6c\u80cc\u4e0a\u98de\u8eab\u63a0\u8d77\uff0c\u624b\u821e\u957f \u67aa\uff0c\u5411\u98de\u5815\u4e0b\u6765\u7684\u5927\u77f3\u4e00\u649e\uff0c\u53ea\u542c\u5f97\u201c\u5580\u5693\u201d\u4e00\u58f0\uff0c\u957f\u67aa\u65ad\u4e3a\u4e24\u622a\uff0c\u5973\u5c06\u9707\u5f97\u5728\u534a\u7a7a\u6253\u4e86\u4e00 \u4e2a\u6597\uff0c\u8dcc\u4e0b\u6765\u65f6\uff0c\u6021\u6021\u843d\u5728\u9a6c\u80cc\uff0c\u59ff\u52bf\u7f8e\u5999\u4e4b\u6781\uff01\u800c\u90a3\u5757\u5927\u77f3\u4e5f\u98de\u8fc7\u5bf9\u9762\u5c71\u5761\u843d\u4e0b\u5c71\u6da7\u4e2d \u4e86\uff01 \u3000\u3000\u7389\u7f57\u5239\u4e0d\u7981\u53eb\u9053\uff1a\u201c\u597d\u529f\u592b\uff01\u201d\u90a3\u5973\u5c06\u50ac\u9a6c\u4e0a\u524d\uff0c\u5fae\u7b11\u95ee\u9053\uff1a\u201c\u6765\u7684\u53ef\u662f\u7ec3\u5be8\u4e3b\u5417\uff1f\u201d \u7389\u7f57\u5239\u89c1\u90a3\u5973\u5c06\u4e00\u8eab\u7ea2\u88f3\uff0c\u95ee\u9053\uff1a\u201c\u6b63\u662f\uff0c\u4f60\u53ef\u662f\u6c5f\u6e56\u4e0a\u79f0\u4e3a\u7ea2\u5a18\u5b50\u7684\u5973\u82f1\u96c4\u5417\uff1f\u201d\u90a3\u5973\u5c06 \u8eac\u8170\u7b54\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u5c0f\u95ef\u738b\u53eb\u4ffa\u95ee\u5019\u59d0\u59d0\u3002\u201d\u8fd9\u65f6\u961f\u4f0d\u4e2d\u8d70\u51fa\u5341\u9980\u5973\u5175\uff0c\u7fa4\u547c\u5be8\u4e3b\uff0c\u7389\u7f57\u5239 \u4e00\u770b\u6b63\u662f\u81ea\u5df1\u7684\u90e8\u4e0b\u3002\u7ea2\u5a18\u5b50\u9053\uff1a\u201c\u5236\u5c06\u519b\u674e\u5ca9\u6628\u65e5\u7edf\u5175\u653b\u4e0b\u53bf\u57ce\uff0c\u548c\u9965\u6c11\u8054\u5408\uff0c\u628a\u7701\u57ce\u5f00 \u6765\u7684\u201c\u527f\u532a\u519b\u201d\u5168\u6b7c\u706d\u4e86\u3002\u6211\u4eec\u5949\u5c0f\u95ef\u738b\u4e4b\u547d\uff0c\u8bf7\u59d0\u59d0\u51fa\u5c71\u3002\u4e0d\u6599\u6765\u8fdf\u4e00\u5929\uff0c\u81f4\u4ee4\u5c71\u5be8\u88ab \u711a\uff0c\u65e0\u6cd5\u633d\u6551\uff0c\u7279\u6765\u8bf7\u7f6a\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u5c71\u5be8\u906d\u52ab\uff0c\u4e43\u662f\u6211\u7684\u758f\u5ffd\uff0c\u8fd9\u4e9b\u59d0\u59b9\u8499\u4f60\u6536\u5bb9\uff0c\u6211\u662f\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d\u95ee\u90a3\u4e9b \u5973\u55bd\u5175\u9053\uff1a\u201c\u4f60\u4eec\u9003\u51fa\u6765\u7684\uff0c\u5df2\u5168\u90e8\u5728\u6b64\u4e86\u9ebd\uff1f\u201d\u5973\u55bd\u5175\u4e00\u9f50\u6ce3\u4e0b\u3002\u7389\u7f57\u5239\u4e00\u6570\uff0c\u8fde\u8ddf\u81ea\u5df2 \u7684\u5341\u9980\u540d\u5728\u5185\uff0c\u4e00\u5171\u5269\u4e0b\u4e8c\u5341\u4e03\u4eba\uff0c\u7b97\u6765\u4e94\u767e\u9980\u5973\u55bd\u5175\uff0c\u9003\u751f\u7684\u4e0d\u5230\u5341\u5206\u4e4b\u4e00\uff0c\u60f3\u8d77\u90a3\u4e9b\u591a \u5e74\u6765\u540c\u751f\u5171\u6b7b\uff0c\u5982\u540c\u59d0\u59b9\u7684\u90e8\u5c5e\uff0c\u4e0d\u89c9\u6f78\u7136\u6cea\u4e0b\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u9053\uff1a\u201c\u59d0\u59d0\u4e0d\u5fc5\u60b2\u4f24\uff0c\u5f53\u4eca\u5929\u4e0b\u5927\u4e71\uff0c\u65e0\u5bb6\u53ef\u5f52\u8005\u4f55\u6b62\u5343\u4e07\uff0c\u8981\u767b\u9ad8\u4e00\u547c\uff0c\u8c6a\u6770 \u7acb\u805a\u3002\u90a3\u65f6\u59d0\u59d0\u518d\u7ec3\u4e00\u652f\u5dfe\u5e3c\u96c4\u5e08\uff0c\u6613\u5982\u53cd\u638c\u3002\u201d\u7389\u7f57\u5239\u82e6\u7b11\u4e0d\u8bed\uff0c\u7ea2\u5a18\u5b50\u9053\uff1a\u201c\u674e\u5ca9\u5728\u57ce \u4e2d\u5fd9\u65bc\u629a\u6064\u6d41\u4ea1\uff0c\u53eb\u6211\u4ee3\u95ee\u5019\u59d0\u59d0\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8c01\u662f\u674e\u5ca9\uff1f\u201d\u7ea2\u5a18\u5b50\u9053\uff1a\u201c\u4ed6\u662f\u5c0f\u95ef\u738b \u90e8\u4e0b\u7684\u201c\u5236\u5c06\u519b\u201d\u201c\u5b98\u540d\u201d\uff0c\u4e5f\u662f\u4ffa\u7684\u6c49\u5b50\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u5931\u656c\uff0c\u5931\u656c\uff01\u201d\u94c1\u98de\u9f99\u8d70\u4e86\u8fc7 \u6765\uff0c\u4e0e\u7ea2\u5a18\u5b50\u76f8\u89c1\uff0c\u5f7c\u6b64\u95fb\u540d\uff0c\u5404\u81ea\u4ef0\u6155\u3002\u94c1\u98de\u9f99\u9053\uff1a\u201c\u5c0a\u592b\u53ef\u662f\u5175\u90e8\u5c1a\u4e66\u674e\u7cbe\u767d\u7684\u516c\u5b50 \u9ebd\uff1f\u201d\u7ea2\u5a18\u5b50\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u7389\u7f57\u5239\u773c\u775b\u4e00\u4eae\uff0c\u5353\u4e00\u822a\u7684\u5f71\u5b50\u4ece\u8111\u6d77\u4e2d\u7a81\u7136\u63a0\u8fc7\uff0c\u4e0d\u89c9\u767e\u611f \u4ea4\u96c6\u3002 \u3000\u3000\u539f\u6765\u7ea2\u5a18\u5b50\u4e43\u6cb3\u5357\u7684\u5973\u76d7\uff0c\u540d\u6c14\u867d\u4e0d\u5982\u7389\u7f57\u5239\u5927\uff0c\u5728\u6c5f\u6e56\u4e0a\u4e5f\u9887\u6709\u58f0\u540d\u3002\u674e\u5ca9\u5219\u662f\u6cb3\u5357 \u675e\u53bf\u7684\u4e3e\u5b50\uff0c\u7236\u4eb2\u674e\u7cbe\u767d\u66fe\u505a\u5230\u5175\u90e8\u5c1a\u4e66\u7684\u5927\u5b98\u3002\u56e0\u6b64\u674e\u5ca9\u7684\u51fa\u8eab\u548c\u5353\u4e00\u822a\u9887\u6709\u76f8\u540c\u4e4b\u5904\uff0c \u4f46\u674e\u5ca9\u7236\u4eb2\u65e9\u6b7b\uff0c\u6240\u4ee5\u4ed6\u7236\u4eb2\u7684\u5b98\u867d\u7136\u6bd4\u5353\u4e00\u822a\u7684\u7956\u7236\u8fd8\u9ad8\u4e00\u7ea7\uff0c\u4f46\u5728\u5bb6\u4e61\u7684\u58f0\u52bf\u53cd\u4e0d\u5982\u5353 \u5bb6\u8d6b\u3002 \u3000\u3000\u674e\u5ca9\u4e5f\u50cf\u5353\u4e00\u822a\u4e00\u6837\uff0c\u5b66\u4e66\u5b66\u5251\uff0c\u6587\u6b66\u5168\u624d\u3002\u4e00\u5e74\u6cb3\u5357\u95f9\u5927\u707e\u8352\uff0c\u674e\u5ca9\u770b\u5230\u707e\u6c11\u51c4\u60e8\u7684 \u60c5\u51b5\uff0c\u5f88\u4e3a\u540c\u60c5\uff0c\u66fe\u81ea\u52a8\u62ff\u51fa\u79ef\u5b58\u7684\u51e0\u767e\u77f3\u7cae\u98df\u8d48\u6d4e\u707e\u6c11\uff0c\u8fd8\u505a\u4e86\u4e00\u9996\u201c\u529d\u8d48\u6b4c\u201d\u529d\u5176\u4ed6\u8c6a \u7ec5\u4e5f\u62ff\u51fa\u8c37\u7c73\u6765\u3002\u5176\u4e2d\u6709\u51e0\u53e5\u662f\uff1a\u201c\u5b98\u5e9c\u5fb5\u7cae\u5982\u864e\u5dee\uff0c\u8c6a\u5bb6\u7d22\u503a\u5982\u72fc\u8c7a\uff1a \u3000\u3000\u53ef\u601c\u6b8b\u5598\u5b58\u547c\u5438\uff0c\u9b42\u9b44\u5148\u5f52\u6cc9\u58e4\u57cb\u3002\u201d \u3000\u3000\u4ed6\u4f5c\u4e86\u8fd9\u6837\u7684\u6b4c\u6765\u201c\u529d\u8d48\u201d\uff0c\u5f53\u7136\u89e6\u4e86\u5176\u4ed6\u8c6a\u7ec5\u4e4b\u5fcc\uff0c\u7ed3\u679c\u88ab\u902e\u6355\u4e0b\u72f1\uff0c\u634f\u4ee5\u717d\u52a8\u9965\u6c11 \u201c\u9020\u53cd\u201d\u7684\u7f6a\u540d\uff0c\u76d1\u72f1\u50cf\u4e00\u4e2a\u6d2a\u7089\uff0c\u5c06\u4ed6\u953b\u6210\u94a2\uff0c\u6240\u4ee5\u5f8c\u6765\u7ea2\u5a18\u5b50\u5e26\u5175\u653b\u4e0b\u675e\u53bf\u53bf\u57ce\u4e4b\u5f8c\uff0c \u4ed6\u4e5f\u5c31\u8ddf\u7ea2\u5a18\u5b50\u8d70\u4e86\u3002 \u3000\u3000\u7389\u7f57\u5239\u4e5f\u66fe\u542c\u5230\u8fc7\u674e\u5ca9\u7684\u540d\u5b57\uff0c\u53ef\u6ca1\u6599\u5230\u4ed6\u548c\u7ea2\u5a18\u5b50\u5df2\u6210\u592b\u5987\uff0c\u66f4\u6295\u6599\u5230\u4ed6\u73b0\u5728\u5df2\u662f\u95ef \u738b\u90e8\u4e0b\u7684\u4e00\u4e2a\u5c06\u519b\u3002\u6240\u4ee5\u521d\u521d\u542c\u7ea2\u5a18\u5b50\u8bf4\u51fa\u674e\u5ca9\u7684\u540d\u5b87\u65f6\uff0c\u8fd8\u4e0d\u77e5\u9053\u4fbf\u662f\u8fd9\u4e2a\u66fe\u505a\u201c\u529d\u8d48 \u6b4c\u201d\u7684\u674e\u5ca9\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u7389\u7f57\u5239\u7a81\u7136\u60f3\u8d77\u4e86\u5353\u4e00\u822a\u6765\uff0c\u5fc3\u60f3\uff1a\u201c\u4e49\u7236\u5e38\u8bf4\u5353\u4e00\u822a\u662f\u5b98\u5bb6\u5b50\u5f1f\uff0c\u548c\u6211\u6050 \u96be\u76f8\u914d\u3002\u90a3\u674e\u5ca9\u4f55\u5c1d\u4e0d\u4e5f\u662f\u5b98\u5bb6\u5b50\u5f1f\uff1f\u4ed6\u548c\u7ea2\u5a18\u5b50\u5374\u7ed3\u4e86\u5927\u597d\u826f\u7f18\u3002\u201d\u6b8a\u4e0d\u77e5\u674e\u5ca9\u4e0e\u5353\u4e00\u822a \u51fa\u8eab\u867d\u7136\u76f8\u540c\uff0c\u751f\u6d3b\u7684\u9053\u8def\u5374\u6709\u5dee\u5f02\uff0c\u674e\u5ca9\u65e9\u5df2\u8131\u80ce\u6362\u9aa8\uff0c\u8fd9\u5c31\u975e\u5353\u4e00\u822a\u6240\u80fd\u76f8\u6bd4\u4e86\u3002\u8fd9\u9053 \u7406\u7389\u7f57\u5239\u5374\u662f\u60f3\u4e0d\u901a\u7684\u3002 \u3000\u3000\u518d\u8bf4\u7ea2\u5a18\u5b50\u548c\u7389\u7f57\u5239\u76f8\u89c1\u4e4b\u5f8c\uff0c\u8bf7\u5979\u540c\u56de\u53bf\u57ce\u3002\u7389\u7f57\u5239\u60f3\u4e86\u4e00\u60f3\uff0c\u4e5f\u4fbf\u7b54\u5141\u3002 \u3000\u3000\u5e7f\u5143\u7684\u666f\u8c61\u4e0e\u524d\u51e0\u5929\u5df2\u4e0d\u5927\u76f8\u540c\uff0c\u6570\u4e07\u9965\u6c11\u88ab\u674e\u5ca9\u7f16\u6210\u4e86\u96c4\u8d73\u8d73\u7684\u961f\u4f0d\uff0c\u4ed6\u4eec\u867d\u7136\u5927\u534a \u6ca1\u6709\u5175\u5668\uff0c\u4f46\u63ed\u7aff\u4e3a\u65d7\uff0c\u524a\u6728\u4e3a\u5175\uff0c\u4e00\u4e2a\u4e2a\u7cbe\u795e\u9971\u6ee1\uff0c\u4fe8\u5982\u4e00\u652f\u8bad\u7ec3\u6709\u4eac\u7684\u96c4\u5e08\u3002 \u3000\u3000\u7389\u7f57\u5239\u770b\u4e86\u8fd9\u6837\u7684\u666f\u8c61\uff0c\u6697\u6697\u53f9\u670d\u3002\u62ac\u5934\u89c1\u8857\u9053\u901a\u8862\u4e4b\u5904\uff0c\u6302\u8d77\u767d\u5e03\u6a2a\u5e45\uff0c\u4e0a\u9762\u6597\u5927\u822c \u7684\u5b57\u5199\u7740\uff1a\u201c\u5403\u4ed6\u5a18\uff0c\u7a7f\u4ed6\u5a18\uff0c\u5f00\u4e86\u5927\u95e8\u8fce\u95ef\u738b\uff0c\u95ef\u738b\u6765\u65f6\u4e0d\u7eb3\u7cae\u3002\u201d\u4e0d\u89c9\u5c55\u7709\u559d\u201c\u597d\u201d\uff01 \u8fd9\u51e0\u53e5\u8bdd\u7b80\u6709\u529b\uff0c\u4e00\u70b9\u9178\u6e9c\u6e9c\u7684\u5473\u9053\u90fd\u6ca1\u6709\uff0c\u751a\u5bf9\u7389\u7f57\u5239\u7684\u80c3\u53e3\u3002 \u3000\u3000\u8425\u95e8\u5f00\u5904\uff0c\u674e\u5ca9\u8fce\u4e86\u51fa\u6765\uff0c\u7ea2\u5a18\u5b50\u7b11\u9053\uff1a\u201c\u6211\u66ff\u4f60\u5c06\u8d35\u5ba2\u63a5\u6765\u4e86\u3002\u201d\u674e\u5ca9\u4e00\u7b11\u8fce\u4eba\uff0c\u5bf9 \u7389\u7f57\u5239\u9053\uff1a\u201c\u73b0\u5728\u8c6a\u6770\u7eb7\u8d77\uff0c\u95ef\u738b\u5927\u519b\uff0c\u5373\u5c06\u81ea\u79e6\u5cad\u897f\u51fa\uff0c\u5148\u53d6\u6f7c\u5173\uff0c\u5f8c\u4e89\u8c6b\u695a\u3002\u7ec3\u5be8\u4e3b\u53ef \u613f\u52a0\u76df\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u6c89\u601d\u6709\u9877\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5929\u4e0b\u662f\u4f60\u4eec\u7684\u4e86\u3002\u6211\u4e5f\u5e2e\u4e0d\u4e86\u4ec0\u9ebd\u3002\u6211\u7684\u90e8\u5c5e\u8bf7 \u7ea2\u59d0\u59d0\u7167\u987e\uff0c\u6211\u53ef\u8981\u8d70\u4e86\u3002\u201d\u674e\u5ca9\u672c\u4ee5\u4e3a\u7389\u7f57\u5239\u5fc5\u5b9a\u52a0\u76df\uff0c\u542c\u4e86\u6b64\u8bdd\uff0c\u9887\u51fa\u610f\u5916\u3002 \u3000\u3000\u674e\u5ca9\u4e0d\u77e5\u7389\u7f57\u5239\u53e6\u6709\u5fc3\u601d\u3002\u7389\u7f57\u5239\u542c\u4e86\u674e\u5ca9\u529d\u5979\u52a0\u76df\u4e4b\u5f8c\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u73ca\u745a\u59b9\u59b9\u4e4b\u4ec7 \u672a\u62a5\uff0c\u6211\u600e\u80fd\u56f0\u5728\u519b\u4e2d\uff1f\u800c\u4e14\u52a0\u76df\u4e4b\u5f8c\uff0c\u60f3\u548c\u5353\u4e00\u822a\u76f8\u89c1\uff0c\u90a3\u5c31\u66f4\u662f\u96be\u4e86\u3002\u201d\u8981\u77e5\u7389\u7f57\u5239\u5bf9 \u5353\u4e00\u822a\u53c8\u6028\u53c8\u7231\uff0c\u5979\u607c\u6839\u4e4b\u65f6\uff0c\u867d\u7136\u4e5f\u66fe\u60f3\u8fc7\u8981\u548c\u5353\u4e00\u822a\u51b3\u7edd\uff0c\u4f46\u6028\u6c14\u7a0d\u6d88\uff0c\u5374\u53c8\u5ff5\u5ff5\u4e0d \u5fd8\u3002 \u3000\u3000\u674e\u5ca9\u89c1\u5979\u62d2\u7edd\uff0c\u9887\u4e3a\u4e0d\u5feb\u3002\u7ea2\u5a18\u5b50\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u4f60\u7684\u5c71\u5be8\u88ab\u5b98\u519b\u6240\u6bc1\uff0c\u6b64\u4ec7\u5c82\u53ef\u4e0d \u62a5\uff01\u201d\u7389\u7f57\u5239\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6709\u4f60\u4eec\u5728\uff0c\u6211\u4f55\u5fc5\u64cd\u5fc3\uff1f\u519b\u65c5\u4e4b\u4e8b\uff0c\u975e\u6211\u6240\u957f\uff0c\u6211\u53c8\u7d20\u6027\u4e0d\u7f81\uff0c \u4f46\u613f\u4e00\u5251\u7eb5\u6a2a\uff0c\u65e0\u62d8\u65e0\u675f\uff0c\u54b1\u4eec\u5404\u5e72\u5404\u7684\uff0c\u4e0d\u4e5f\u597d\u9ebd\uff1f\u201d\u674e\u5ca9\u5fc3\u60f3\uff1a\u602a\u4e0d\u5f97\u5979\u6709\u5973\u9b54\u5934\u4e4b \u53f7\uff0c\u679c\u7136\u91ce\u6027\u96be\u9a6f\u3002\u6536\u5bb9\u4e86\u5979\uff0c\u53ea\u6050\u5979\u4e71\u4e86\u519b\u7eaa\u3002\u4fbf\u4e5f\u4e0d\u518d\u63d0\u4e86\u3002 \u3000\u3000\u674e\u5ca9\u521a\u521a\u653b\u4e0b\u53bf\u57ce\uff0c\u519b\u52a1\u751a\u5fd9\uff0c\u9644\u8fd1\u7684\u51e0\u80a1\u76d7\u532a\uff0c\u90fd\u6765\u6295\u9644\uff0c\u5148\u6d3e\u4eba\u63a5\u6d3d\uff0c\u8981\u7cae\u8981\u9977\uff0c \u95f9\u6210\u4e00\u7247\u3002\u7389\u7f57\u5239\u5750\u5728\u4e00\u65c1\uff0c\u770b\u4ed6\u53d1\u4ed8\uff0c\u53ea\u89c1\u4ed6\u6765\u8005\u4e0d\u62d2\uff0c\u4e00\u4e00\u63a5\u7eb3\uff0c\u95ee\u660e\u4e86\u90e8\u961f\u4eba\u6570\u4e4b \u5f8c\uff0c\u7acb\u5373\u53d1\u653e\u7cae\u9977\uff0c\u95f9\u4e86\u534a\u5929\uff0c\u8fd9\u4e9b\u4eba\u624d\u5fc3\u6ee1\u610f\u8db3\uff0c\u5404\u5404\u6563\u53bb\u3002 \u3000\u3000\u7389\u7f57\u5239\u5947\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u8fd9\u6837\u5bf9\u4ed8\u5f3a\u76d7\u5934\u5b50\uff1f\u201d\u674e\u5ca9\u9053\uff1a\u201c\u8bf7\u59d0\u59d0\u6307\u6559\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a \u201c\u6211\u5728\u5357\u4e4b\u65f6\uff0c\u6709\u6211\u5411\u5404\u8def\u5c71\u5be8\u8981\u8d22\u7269\u8981\u7cae\u8349\uff0c\u90a3\u6709\u98a0\u5012\u8fc7\u6765\uff0c\u53cd\u7ed9\u4ed6\u4eec\u4e4b\u7406\uff1f\u201d\u674e\u5ca9\u5fae\u5fae \u4e00\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u4ee5\u529b\u670d\u4eba\uff0c\u600e\u80fd\u6210\u5f97\u5927\u4e8b\uff1f\u201d\u7ea2\u5a18\u5b50\u5728\u65c1\u4ee3\u7b54\u9053\uff1a\u201c\u82e5\u975e\u8fd9\u6837\uff0c\u4ed6\u4eec\u4e5f\u4e0d \u80af\u5fc3\u7518\u60c5\u613f\u6765\u6295\u9760\u6211\u4eec\u4e86\u3002\u671d\u5ef7\u9a7b\u5728\u5ddd\u4e24\u7701\u7684\u5927\u519b\uff0c\u6b63\u60f3\u5bf9\u6211\u4eec\u5404\u4e2a\u51fb\u7834\uff0c\u6211\u4eec\u82e5\u4e0d\u8054\u6210\u4e00 \u6c14\uff0c\u53ea\u6050\u7acb\u8db3\u4e5f\u96be\uff0c\u66f4\u83ab\u8bf4\u897f\u51fa\u6f7c\u5173\uff0c\u6325\u97ad\u5317\u4e0a\u4e86\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f46\u7eff\u6797\u5f3a\u76d7\u4e5f\u6709\u5404\u79cd\u5404 \u7c7b\uff0c\u4f60\u4e0d\u62c5\u5fc3\u6709\u4eba\u9a97\u4f60\u4eec\u7684\u7cae\u9977\u5417\uff1f\u201d\u674e\u5ca9\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\u8bf4\u7684\u662f\uff0c\u6211\u4eec\u81ea\u5f53\u5206\u522b\u5bf9\u4ed8\u3002\u4e0d\u8fc7 \u90a3\u662f\u4ee5\u5f8c\u4e4b\u4e8b\uff0c\u800c\u4e14\u7eff\u6797\u8bb2\u4e49\u6c14\u7684\u591a\uff0c\u6211\u4eec\u4e0d\u80fd\u56e0\u4e3a\u6709\u4e00\u4e8c\u8d25\u7c7b\uff0c\u4fbf\u90fd\u95ed\u95e8\u4e0d\u7eb3\u3002\u201d\u7389\u7f57\u5239 \u9053\uff1a\u201c\u4f60\u4e5f\u8bf4\u5f97\u662f\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u6709\u591a\u5c11\u7cae\u9977\uff0c\u53ef\u4ee5\u53d1\u4ed8\u4ed6\u4eec\uff1f\u53bf\u57ce\u4e2d\u6709\u591a\u5c11\u5b58 \u7cae\u548c\u5e93\u94f6\uff0c\u6211\u4e5f\u7565\u77e5\u5927\u6982\uff0c\u53ea\u6050\u4e0d\u8db3\u9965\u6c11\u4e00\u6708\u4e4b\u7528\u5427\uff1f\u201d\u674e\u5ca9\u82e6\u7b11\u9053\uff1a\u201c\u90a3\u6709\u4ee5\u5f8c\u518d\u60f3\u6cd5\u5b50 \u4e86\u3002\u201d\u7389\u7f57\u5239\u5ffd\u7b11\u9053\uff1a\u201c\u52a0\u76df\u6211\u662f\u4e0d\u52a0\u4e86\uff0c\u4f46\u6211\u5012\u6709\u4e00\u70b9\u5c0f\u5c0f\u7684\u793c\u7269\u8981\u9001\u7ed9\u7ea2\u59d0\u59d0\u3002\u201d\u7ea2\u5a18 \u5b50\u6447\u624b\u9053\uff1a\u201c\u59d0\u59d0\u4e0d\u5fc5\u5ba2\u6c14\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8fd9\u793c\u7269\u4f60\u4e0d\u6536\u4e5f\u4e0d\u884c\uff0c\u660e\u65e5\u4f60\u5e26\u4e00\u961f\u5973\u5175\u548c\u6211 \u5230\u660e\u6708\u5ce1\u5427\u3002\u201d\u8bf4\u5b8c\u4f38\u4e86\u4e00\u4e2a\u61d2\u8170\uff0c\u6253\u54c8\u6b20\u9053\uff1a\u201c\u770b\u4f60\u4eec\u5fd9\u5fd9\u788c\u788c\uff0c\u6211\u4e5f\u5934\u660f\u773c\u82b1\u3002\u54c8\uff0c\u6211 \u53ef\u8981\u7761\u5566\uff01\u201d\u674e\u5ca9\u5fd9\u53eb\u4eba\u6536\u62fe\u623f\u95f4\uff0c\u8bf7\u7389\u7f57\u5239\u548c\u94c1\u98de\u9f99\u6b47\u606f\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65f1\uff0c\u7ea2\u5a18\u5b50\u679c\u7136\u7387\u4e86\u4e00\u961f\u5973\u5175\uff0c\u968f\u7389\u7f57\u5239\u518d\u5230\u660e\u6708\u5ce1\uff0c\u7ea2\u5a18\u5b50\u89c1\u5979\u884c\u4e8b\u602a\u5f02\uff0c \u5fc3\u9887\u751f\u7591\u3002\u4e34\u884c\u524d\u6084\u6084\u5bf9\u674e\u5ca9\u9053\uff1a\u201c\u5979\u4e0d\u77e5\u8981\u9001\u4ec0\u9ebd\u4e1c\u897f\u7ed9\u6211\uff0c\u4f55\u4ee5\u5174\u5e08\u52a8\u4f17\uff0c\u5982\u6b64\u7d27 \u5f20\uff1f\u201d\u674e\u5ca9\u7b11\u9053\uff1a\u201c\u6b64\u4e8b\u6211\u5df2\u6599\u5230\u4e03\u516b\uff0c\u4f60\u4f46\u53bb\u65e0\u59a8\u3002\u6211\u9001\u4f60\u4eec\u4e00\u7a0b\u3002\u201d\u9001\u51fa\u57df\u5916\uff0c\u674e\u5ca9\u52d2 \u9a6c\u5f85\u56de\uff0c\u7389\u7f57\u5239\u5ffd\u9053\uff1a\u201c\u4f60\u4e5f\u4e00\u540c\u53bb\u5427\u3002\u201d\u7ea2\u5a18\u5b50\u5fc3\u60f3\uff1a\u201c\u8fd9\u5973\u9b54\u5934\u600e\u9ebd\u5982\u6b64\u4e0d\u8fd1\u4eba\u60c5\uff0c\u4ed6 \u519b\u52a1\u7e41\u5fd9\uff0c\u4f60\u53c8\u4e0d\u662f\u4e0d\u77e5\u9053\uff1f\u201d\u7ea2\u5a18\u5b50\u4ee5\u4e3a\u4e08\u592b\u5fc5\u5b9a\u4e0d\u4f1a\u7b54\u5e94\uff0c\u4e0d\u6599\u674e\u5ca9\u5fae\u5fae\u4e00\u7b11\uff0c\u7adf\u7b54\u5e94 \u4e86\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u9053\uff1a\u201c\u4eca\u65e5\u4e0d\u662f\u8fd8\u6709\u4e24\u80a1\u7eff\u6797\u5934\u76ee\u8981\u7ea6\u4f60\u89c1\u9762\u5417\uff1f\u201d\u674e\u5ca9\u9053\uff1a\u201c\u53eb\u526f\u5c06\u519b\u66ff\u6211\u4ee3 \u89c1\u5427\u3002\u201d\u547d\u968f\u4ece\u643a\u4ee4\u56de\u57ce\uff0c\u6beb\u4e0d\u72b9\u7591\u968f\u7389\u7f57\u5239\u540c\u5f80\u3002 \u3000\u3000\u660e\u6708\u5ce1\u7684\u5c71\u5be8\u5df2\u5316\u6210\u7070\u70ec\uff0c\u7389\u7f57\u5239\u5728\u70e7\u7126\u4e86\u7684\u6ce5\u571f\u4e0a\u5f98\u5f8a\u4e00\u9635\uff0c\u9ed8\u9ed8\u65e0\u8a00\u3002\u674e\u5ca9\u9053\uff1a \u201c\u59d0\u59d0\u4e0d\u5fc5\u5fc3\u4f24\uff0c\u5b98\u519b\u6bc1\u4e86\u6211\u4eec\u4e00\u4e2a\u5c71\u5be8\uff0c\u6211\u4eec\u4fbf\u8981\u5360\u4ed6\u5341\u4e2a\u5dde\u5e9c\u3002\u201d\u7389\u7f57\u5239\u5ffd\u9053\uff1a\u201c\u4f60\u8170 \u60ac\u5b9d\u5251\uff0c\u60f3\u5fc5\u4e5f\u7cbe\u65bc\u5251\u672f\u7684\u4e86\u4e86\u54b1\u4eec\u53cd\u6b63\u65e0\u4e8b\uff0c\u5728\u8fd9\u91cc\u8bd5\u51e0\u62db\u5982\u4f55\uff1f\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u6c14\u5f80\u4e0a\u51b2\uff0c\u5fc3\u9053\uff1a\u201c\u54fc\uff0c\u8fd9\u4e2a\u5973\u9b54\u5934\u8bf4\u4ec0\u9ebd\u9001\u6fa7\u7269\uff0c\u5374\u539f\u6765\u8981\u4f38\u91cf\u6211\u4eec\u3002\u201d\u6b63\u60f3 \u53d1\u8bdd\uff0c\u5ffd\u89c1\u674e\u5ca9\u5411\u81ea\u5df1\u629b\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u793a\u610f\u53eb\u5979\u4e0d\u8981\u4f5c\u58f0\u3002 \u3000\u3000\u674e\u5ca9\u6700\u521d\u4e5f\u6014\u4e86\u4e00\u6014\uff0c\u968f\u5373\u7b11\u9053\uff1a\u201c\u6211\u7684\u5251\u672f\u600e\u80fd\u4e0e\u59d0\u59d0\u76f8\u6bd4\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u6b47\u4e86 \u4e24\u5929\uff0c\u65e0\u4eba\u5bf9\u624b\uff0c\u624b\u4e5f\u75d2\u4e86\uff0c\u4f60\u7528\u4f73\u80b4\u7f8e\u9152\u62db\u5f85\u6211\uff0c\u5012\u4e0d\u5982\u966a\u6211\u8d70\u4e0a\u4e24\u62db\uff0c\u6211\u66f4\u9886\u4f60 \u60c5\u3002\u201d\uff0e \u3000\u3000\u674e\u5ca9\u9053\uff1a\u201c\u597d\uff0c\u8bf7\u59d0\u59d0\u8fdb\u62db\uff01\u201d\u7389\u7f57\u5239\u5251\u8bc0\u4e00\u634f\uff0c\u5251\u6765\u5982\u98ce\uff0c\u4e00\u7f15\u9752\u5149\uff0c\u76f4\u523a\u674e\u5ca9\u624b \u8155\uff0c\u674e\u5ca9\u7684\u5251\u672f\u662f\u592a\u6781\u6d3e\u540d\u624b\u738b\u540c\u6240\u6388\uff0c\u5251\u950b\u63a0\u4e0b\uff0c\u987a\u52bf\u633d\u4e86\u4e00\u4e2a\u5e73\u82b1\uff0c\u4e0d\u6551\u654c\u62db\uff0c\u53cd\u523a\u654c \u8db3\uff0c\u7389\u7f57\u5239\u9053\u58f0\uff1a\u201c\u4e0d\u9519\uff01\u201d\u77ac\u606f\u4e4b\u95f4\uff0c\u8fde\u53d8\u4e24\u62db\uff0c\u4e00\u5251\u4e0b\u65a9\uff0c\u4e00\u5251\u4e0a\u6311\uff0c\u674e\u5ca9\u6478\u4e0d\u6e05\u5979\u653b \u52bf\u6240\u5728\uff0c\u957f\u5251\u5f53\u80f8\u4e00\u5212\uff0c\u7528\u201c\u5982\u5c01\u4f3c\u95ed\u201d\u7684\u5251\u5f0f\uff0c\u5c06\u654c\u5251\u5c01\u51fa\u5916\u95e8\uff0c\u90a3\u77e5\u7389\u7f57\u5239\u7684\u5251\u6cd5\u5947\u8be1 \u5f02\u5e38\uff0c\u5251\u52bf\u672a\u6536\uff0c\u624b\u5fc3\u7684\u52b2\u529b\u5411\u5916\u4e00\u987f\uff0c\u5251\u62db\u53c8\u53d1\uff0c\u8fd9\u4e00\u62db\u6765\u5f97\u66f4\u72e0\uff0c\u5251\u5c16\u95ea\u95ea\uff0c\uff0e\u7adf\u4ece\u5de6 \u4fa7\u523a\u5230\u9888\u9879\uff0c\u674e\u5ca9\u6ed1\u6b65\u4e00\u8f6c\uff0c\u5de6\u624b\u865a\u6643\uff0c\u53f3\u8db3\u76f4\u8e22\u7389\u7f57\u5239\u7ea4\u8170\uff0c\u8fd9\u4e00\u62db\u5374\u662f\u201c\u6b66\u677e\u9189\u6253\u848b\u95e8 \u795e\u201d\u4e2d\u7684\u8fde\u73af\u817f\u5bb6\u6570\uff0c\u4ed6\u7684\u5251\u672f\u4e0d\u8db3\u5e94\u4ed8\uff0c\u62f3\u811a\u4e0a\u7684\u529f\u592b\u4e5f\u65bd\u5c55\u51fa\u6765\uff0c\u7389\u7f57\u5239\u201c\u5514\u201d\u4e86\u4e00\u58f0 \u9053\uff1a\u201c\u4e5f\u8fd8\u914d\u5408\u5f97\u597d\uff1f\u201d\u94c1\u8170\u4e00\u6298\uff0c\u957f\u5251\u5377\u5730\u523a\u6765\uff0c\u674e\u5ca9\u53ea\u8db3\u4e00\u8df3\uff0c\u957f\u5251\u4e00\u8f6c\uff0c\u9669\u9669\u907f\u8fc7\u8fd9 \u62db\uff0c\u7389\u7f57\u5239\u8d8a\u653b\u8d8a\u75be\uff0c\u5251\u5149\u970d\u970d\uff0c\u53ea\u89c1\u56db\u9762\u516b\u65b9\u90fd\u662f\u5979\u7684\u5f71\u5b50\uff0c\u7ea2\u5a18\u5b50\u5012\u5438\u4e86\u201d\u53e3\u51c9\u6c14\uff0c\u5fc3 \u9053\uff1a\u201c\u8fd9\u5973\u9b54\u5934\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01\u201d\u5ffd\u89c1\u7389\u7f57\u5239\u957f\u5251\u4e00\u7ede\uff0c\u642d\u4e0a\u4e86\u674e\u5ca9\u7684\u5b9d\u5251\u8f6c\u4e86\u4e24\u8f6c\uff0c\u94ff\u9535 \u6709\u58f0\uff0c\u7ea2\u5a18\u5b50\u9053\u58f0\uff1a\u201c\u4e0d\u597d\uff01\u201d\u7eb5\u51fa\u573a\u5fc3\uff0c\u53ea\u542c\u5f97\u7389\u7f57\u5239\u4e00\u58f0\u957f\u7b11\uff0c\u4e24\u4eba\u500f\u5ffd\u5206\u5f00\u3002\u7ea2\u5a18\u5b50 \u83ab\u540d\u6240\u4ee5\uff0c\u674e\u5ca9\u63d2\u5251\u5f52\u9798\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\u5251\u6cd5\u5929\u4e0b\u65e0\u53cc\uff01\u4f69\u670d\uff0c\u4f69\u670d\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u9762\u8272\u4e00\u7aef\uff0c\u9053\uff1a\u201c\u90a3\u662f\u4f60\u8fc7\u8a89\u4e86\uff01\u201d\u65cb\u53c8\u7b11\u9053\uff1a\u201c\u6211\u5728\u53c1\u5341\u62db\u4e4b\u5185\uff0c\u4e0d\u80fd\u593a\u4f60\u7684 \u5251\uff0c\u6211\u7684\u793c\u7269\u4f60\u6709\u8d44\u683c\u53d6\u4e86\u3002\u201d\u7ea2\u5a18\u5b50\u597d\u751f\u7eb3\u95f7\uff0c\u5fc3\u4e2d\u9a82\u9053\uff1a\u201c\u5929\u4e0b\u90a3\u6709\u8fd9\u79cd\u9001\u793c\u4e4b\u6cd5\uff1f\u9001 \u793c\u4e4b\u524d\u5148\u8981\u4f38\u91cf\u4eba\u5bb6\uff01\u8c01\u5e0c\u7f55\u4f60\u7684\u793c\u7269\uff01\u201d\u674e\u5ca9\u5374\u9053\uff1a\u201c\u90a3\u9ebd\u6211\u5148\u591a\u8c22\u4e86\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u7f13\u7f13\u5411\u5c71\u5ca9\u8fb9\u8d70\u53bb\uff0c\u8fb9\u8d70\u8fb9\u8bf4\u9053\uff1a\u201c\u6628\u65e5\u6211\u89c1\u8bc6\u4e86\u4f60\u7684\u6587\u624d\u667a\u7565\uff0c\u4eca\u65e5\u53c8\u89c1\u8bc6\u4e86 \u4f60\u7684\u6b66\u827a\uff0c\u8fd9\u793c\u7269\u4ed8\u6258\u5f97\u4eba\u4e86\u3002\u201d\u7389\u7f57\u5239\u7684\u5c71\u5be8\u4f9d\u7740\u5c71\u52bf\u5efa\uff0c\u5c71\u5ca9\u9001\u5c1a\u6709\u70e7\u7126\u7684\u6728\u67f1\u3002\u7389\u7f57 \u5239\u6a2a\u638c\u4e00\u5288\uff0c\u5c06\u6728\u67f1\u6253\u6298\uff0c\u5411\u7ea2\u5a18\u5b50\u62db\u624b\u9053\uff1a\u201c\u8bf7\u4f60\u4eec\u987a\u7740\u8fd9\u91cc\u6398\u4e0b\u53bb\uff0c\u5c06\u5730\u4e0b\u7684\u6728\u5934\u6398\u51fa \u6765\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u597d\u4e0d\u751f\u6c14\uff0c\u9053\uff1a\u201c\u7d22\u6027\u6211\u591a\u53eb\u4e9b\u4eba\u6765\uff0c\u4e00\u5e76\u7ed9\u4f60\u6e05\u7406\u4e86\u8fd9\u74e6\u783e\u573a\u5427\u3002\u201d\u6b64\u8bdd\u6697\u5b58 \u8ba5\u8bee\uff0c\u7389\u7f57\u5239\u9762\u8272\u4e00\u6c89\uff0c\u9053\uff1a\u201c\u4eca\u751f\u4eca\u4e16\uff0c\u6211\u518d\u4e5f\u4e0d\u4f1a\u56de\u5230\u8fd9\u513f\u6765\u4e86\uff0c\u8fd8\u6e05\u7406\u5b83\u4f5c\u751a\uff1f\u201d\u7389 \u7f57\u5239\u53c1\u5e74\u591a\u7ecf\u8425\u7684\u5c71\u5be8\u6bc1\u65bc\u4e00\u65e6\uff0c\u7ed9\u7ea2\u5a18\u5b50\u7684\u8bdd\u64a9\u8d77\u4f24\u5fc3\uff0c\u542c\u4e0d\u51fa\u8bdd\u4e2d\u6240\u542b\u7684\u8ba5\u8bbd\u4e4b\u610f\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u89c1\u5979\u4f24\u5fc3\uff0c\u597d\u751f\u8fc7\u610f\u4e0d\u53bb\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5973\u9b54\u5934\u813e\u6c14\u867d\u602a\uff0c\u6027\u60c5\u5374\u662f\u7387\u76f4\u3002\u201d\u6307\u6325 \u5973\u5175\u6398\u5730\uff0c\u628a\u57cb\u5728\u5730\u4e2d\u7684\u6728\u9876\u6398\u4e86\u8d77\u6765\uff0c\u6398\u4e86\u4e00\u9635\uff0c\u5ffd\u89c9\u6ce5\u571f\u751a\u677e\uff0c\u5973\u5175\u4e00\u9504\u6398\u53bb\uff0c\u9677\u4e86\u4e00 \u4e2a\u5927\u6d1e\uff0c\u518d\u6398\u4e00\u9504\uff0c\u5f53\u7684\u4e00\u58f0\uff0c\u9504\u5934\u5076\u7740\u4e00\u5757\u77f3\u677f\uff0c\u7389\u7f57\u5239\u4e00\u8dc3\u800c\u4e0b\uff0c\u5c06\u77f3\u677f\u63ed\u5f00\uff0c\u53ea\u89c1\u5b9d \u5149\u8000\u76ee\uff0c\u91d1\u94f6\u73e0\u7389\uff0c\u5806\u6ee1\u7a9f\u4e2d\u3002\u539f\u6765\u8fd9\u6b63\u662f\u7389\u7f57\u5239\u6570\u5e74\u6765\u52d2\u7d22\u5f3a\u76d7\u5934\u5b50\u7684\u8d21\u7269\uff0c\u4ee5\u53ca\u62a2\u52ab\u5bcc \u6237\u7684\u79ef\u805a\u3002 \u3000\u3000\u6398\u5730\u7684\u5973\u5175\u5413\u5f97\u5446\u4e86\uff0c\u7ea2\u5a18\u5b50\u4e5f\u9887\u4e3a\u60ca\u8be7\uff0c\u53ea\u6709\u674e\u5ca9\u5fae\u5fae\u53d1\u7b11\uff0c\u4f3c\u4e4e\u4e00\u5207\u90fd\u5728\u4ed6\u610f\u6599\u4e4b \u4e2d\u3002 \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u8bf7\u4f60\u4eec\u628a\u8fd9\u4e9b\u4e1c\u897f\u90fd\u642c\u51fa\u6765\u3002\u201d\u5973\u5175\u4eec\u90a3\u66fe\u89c1\u8fc7\u8fd9\u4e9b\u73cd\u5b9d\uff0c\u8e51\u624b\u8e51\u811a\uff0c\u5c0f \u5fc3\u5180\u5180\u7684\u4e00\u4ef6\u4e00\u4ef6\u6367\u4e86\u51fa\u6765\uff0c\u751f\u6015\u78b0\u574f\u4f3c\u7684\u3002\u7389\u7f57\u5239\u7b11\u563b\u563b\u7684\u5bf9\u94c1\u98de\u9f99\u89e3\u91ca\uff0c\u90a3\u679d\u73ca\u745a\u662f\u4ece \u90a3\u4e2a\u5f3a\u76d7\u5934\u5b50\u624b\u4e2d\u62a2\u6765\uff0c\u90a3\u5757\u7eff\u7389\u53c8\u662f\u90a3\u4e2a\u5e2e\u4f1a\u8235\u4e3b\u6240\u8d21\uff0c\u751a\u4e3a\u5f97\u610f\u3002\u94c1\u98de\u9f99\u76b1\u7709\u8bf4\u9053\uff1a \u201c\u4f60\u8d39\u8fd9\u9ebd\u5927\u5fc3\u673a\u5f04\u6765\u8fd9\u9ebd\u591a\u94dc\u81ed\u4e4b\u7269\u5e72\u561b\uff1f\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u7239\uff0c\u4f60\u89c1\u8fc7\u9ad8\u624b\u4e0b\u68cb\u535a\u5f69 \u5417\uff1f\u4ed6\u4eec\u5e76\u4e0d\u5728\u4e4e\u533a\u533a\u5f69\u7269\uff0c\u4f46\u6709\u4e86\u5f69\u7269\uff0c\u5374\u66f4\u589e\u52a0\u4e0b\u68cb\u7684\u5174\u8da3\u3002\u6211\u4ee5\u524d\u5728\u9655\u5357\u538b\u670d\u7eff\u6797\uff0c \u8feb\u4ed6\u4eec\u5411\u6211\u8fdb\u8d21\uff0c\u4e5f\u4e0d\u8fc7\u7b49\u65bc\u68cb\u624b\u4e4b\u8981\u5f69\u7269\u7f62\u4e86\u3002\u201d\u94c1\u98de\u9f99\u8fd9\u4e24\u65e5\u6765\u6101\u80a0\u767e\u7ed3\uff0c\u5374\u7ed9\u5979\u7684\u8bdd \u9017\u5f97\u5f00\u7709\u4e00\u7b11\u3002 \u3000\u3000\u7ea2\u5a18\u5b50\u5e26\u6765\u7684\u5973\u5175\u5c06\u91d1\u94f6\u73e0\u5b9d\u90fd\u642c\u51fa\u6765\u4e4b\u5f8c\uff0c\u7389\u7f57\u5239\u5bf9\u674e\u5ca9\u4e00\u63d6\u8bf4\u9053\uff1a\u201c\u533a\u533a\u8584\u793c\uff0c\u9001 \u7ed9\u8d24\u4f09\u4fea\u6dfb\u519b\u9977\u3002\u201d\u674e\u5ca9\u9053\uff1a\u201c\u90a3\u9ebd\u6211\u66ff\u707e\u6c11\u548c\u5144\u5f1f\u591a\u8c22\u4f60\u4e86\u3002\u201d\u7389\u7f57\u5239\u968f\u624b\u63d0\u8d77\u4e00\u4e2a\u91d1\u9a6c \u978d\uff0c\u9eef\u7136\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u4eec\u4ee5\u524d\u7684\u8001\u5be8\u4e3b\u738b\u5609\u80e4\u53eb\u4ed6\u7684\u513f\u5b50\u9001\u7ed9\u6211\u7684\uff0c\u73b0\u5728\u4ed6\u5df2\u6b7b\u4e86\uff0c\u4f60\u5c06 \u8fd9\u4e2a\u9a6c\u978d\u4ea4\u56de\u7ed9\u4ed6\u7684\u513f\u5b50\u738b\u7167\u5e0c\u5427\uff0c\u7b97\u6211\u7ed9\u4ed6\u7684\u5a5a\u793c\u3002\u201d \u3000\u3000\u7ea2\u5a18\u5b50\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u4e0d\u9009\u4e00\u4e24\u6837\u4e1c\u897f\u7559\u5ff5\u5417\uff1f\u201d\u9ed1\u9053\u4e0a\u7684\u89c4\u77e9\uff0c\u51fa\u624b\u505a\u6848\uff0c\u603b\u4e0d\u80fd\u7a7a\u624b \u800c\u56de\uff0c\u82e5\u7136\u662f\u78b0\u5230\u6709\u6765\u5934\u7684\u4eba\uff0c\u4e0d\u4fbf\u52ab\u65f6\uff0c\u90a3\u5c31\u53d6\u4e00\u6587\u94dc\u94b1\u4e5f\u662f\u8981\u7684\uff0c\u8fd9\u662f\u56fe\u4e2a\u5409\u5229\u7684\u610f \u601d\u3002\u5982\u4eca\u7389\u7f57\u5239\u5c06\u8fd9\u6279\u7ecf\u6570\u5e74\u79ef\u805a\uff0c\u4ef7\u503c\u8fde\u57ce\u7684\u8d43\u7269\u62f1\u624b\u9001\u5949\uff0c\u56e0\u6b64\u7ea2\u5a18\u5b50\u4e5f\u6309\u9ed1\u9053\u4e0a\u7684\u89c4 \u77e9\uff0c\u53eb\u5979\u53d6\u56de\u4e00\u4e24\u6837\u4e1c\u897f\u3002 \u3000\u3000\u7389\u7f57\u5239\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4ece\u6b64\u6d17\u624b\u4e0d\u5e72\uff0c\u9000\u51fa\u7eff\u6797\uff0c\u8fd8\u8981\u8fd9\u4e9b\u8eab\u5916\u4e4b\u7269\u505a\u4ec0\u9ebd\uff1f\u201d\u54c8 \u54c8\u4e00\u7b11\u4e4b\u5f8c\uff0c\u773c\u73e0\u4e00\u8f6c\uff0c\u5ffd\u9053\uff1a\u201c\u597d\uff0c\u6211\u8981\u4e00\u6837\u4e1c\u897f\u3002\u201d\u5f2f\u4e0b\u8170\u8eaf\uff0c\u5728\u5730\u4e0a\u62fe\u8d77\u4e00\u5757\u6ce5\u571f\uff0c \u9053\uff1a\u201c\u6211\u5230\u8fd9\u91cc\u53c1\u5e74\u591a\u4e86\uff0c\u5f88\u5c11\u5728\u4e00\u4e2a\u5730\u65b9\u4f4f\u8fc7\u8fd9\u9ebd\u4e45\u3002\u6211\u5f88\u719f\u6089\u8fd9\u6ce5\u571f\u7684\u9999\u5473\u3002\u201d\u9001\u5230\u9f3b \u7aef\u95fb\u4e86\u4e00\u95fb\uff0c\u53c8\u9053\uff1a\u201c\u8fd9\u6ce5\u571f\u8fd8\u67d3\u6709\u6211\u59ca\u59b9\u7684\u8840\uff0c\u518d\u6ca1\u6709\u4ec0\u9ebd\u4e1c\u897f\u6bd4\u8fd9\u4e2a\u66f4\u503c\u5f97\u7559\u5ff5\u4e86\u3002\u201d \u5c06\u6ce5\u571f\u653e\u4eba\u6000\u4e2d\uff0c\u5174\u94c1\u98de\u9f99\u6253\u4e86\u4e2a\u62db\u547c\uff0c\u5982\u98de\u4e0b\u5c71\u3002\u7ea2\u5a18\u5b50\u5927\u58f0\u547c\u5524\uff0c\u53ea\u89c1\u7389\u7f57\u5239\u8863\u8882\u98d8 \u98d8\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u8ff3\u81ea\u53bb\u4e86\u3002\u6b63\u662f\uff1a\u5f02\u5b9d\u5947\u73cd\u90fd\u4e0d\u8981\uff0c\u7559\u6ce5\u571f\u5bc4\u6df1\u60c5\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b \u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u4e8c\u56de\u3000\u516d\u6708\u98de\u971c\u3000\u4eac\u57ce\u6784\u51a4\u72f1\u3000\u6df1\u5bab\u8bfb\u647a\u3000\u4fa0\u5973\u9001\u5947\u4e66 \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377 \u7b2c\u4e8c\u5341\u4e8c\u56de \u516d\u6708\u98de\u971c\u3000\u4eac\u57ce\u6784\u51a4\u72f1 \u6df1\u5bab\u8bfb\u647a\u3000\u4fa0\u5973\u9001\u5947\u4e66 \u3000\u3000\u534a\u6708\u4e4b\u5f8c\uff0c\u7389\u7f57\u5239\u548c\u94c1\u98de\u9f99\u5df2\u9a70\u9a8b\u5728\u6210\u90fd\u5e73\u539f\u4e4b\u4e0a\uff0c\u4e24\u4eba\u90fd\u662f\u9ed1\u591c\u7384\u88f3\uff0c\u8de8\u7740\u67a3\u7ea2\u5065 \u9a6c\uff0c\u9887\u60f9\u4eba\u6ce8\u76ee\u3002\u94c1\u98de\u9f99\u66fe\u529d\u7389\u7f57\u5239\u4e54\u88c5\u7537\u5b50\uff0c\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u6211\u8981\u4e3a\u5dfe\u5e3c\u88d9\u9497\u626c\u7709\u5410\u6c14\uff0c \u4e3a\u4f55\u8981\u626e\u7537\u4eba\uff1f\u201d\u94c1\u98de\u9f99\u4e00\u7b11\u4f5c\u7f62\u3002\u5e78\u4ed6\u4e8c\u4eba\u6b66\u827a\u9ad8\u5f3a\uff0c\u516c\u95e8\u4e2d\u4eba\uff0c\u7eb5\u6709\u8ba4\u8bc6\u7389\u7f57\u5239\u7684\uff0c\u78b0 \u7740\u5979\u4e5f\u4e0d\u6562\u52a8\u624b\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u4ed6\u4eec\u5230\u4e86\u5f6d\u53bf\uff0c\u79bb\u6210\u90fd\u53ea\u6709\u767e\u9980\u91cc\u4e86\u3002\u7389\u7f57\u5239\u5ffd\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8fd9\u4e24\u65e5\u53ef\u66fe\u53d1\u73b0\u5927 \u8def\u4e0a\u5e38\u6709\u516c\u4eba\u51fa\u6ca1\u5417\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4eba\u4e0d\u6270\u6211\uff0c\u6211\u4e0d\u6270\u4eba\uff0c\u54b1\u4eec\u6709\u81ea\u5df1\u7684\u4e8b\u60c5\uff0c\u7406\u4ed6\u4eec\u5e72 \u5417\uff1f\uff0c\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4e0d\u7136\uff0c\u4ed6\u4eec\u597d\u50cf\u662f\u8ffd\u6355\u5f3a\u76d7\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u6d17\u624b\u4e0d\u5e72\u7eff\u6797\u4e86 \u5417\uff1f\u5b98\u5dee\u8ffd\u6355\u5f3a\u76d7\uff0c\u90a3\u662f\u6781\u5bfb\u5e38\u7684\u4e8b\u60c5\uff0c\u600e\u7406\u5f97\u8fd9\u9ebd\u591a\uff1f\u83ab\u975e\u4f60\u53c8\u624b\u75d2\u96be\u71ac\uff0c\u60f3\u627e\u4eba\u6740\u4e86 \u5417\uff1f\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u7239\uff0c\u6b63\u662f\u8fd9\u6837\uff01\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u8981\u6740\u4e5f\u5f97\u627e\u4e2a\u597d\u5bf9\u624b\uff0c\u50cf\u8fd9\u4e9b\u7a00\u677e\u8113 \u5305\u7684\u6355\u5934\uff0c\u6740\u4e86\u4ed6\u4e5f\u6ca1\u610f\u601d\u3002\u201d\u5176\u5b9e\u7389\u7f57\u5239\u4e5f\u5e76\u6ca1\u610f\u601d\u627e\u6355\u5934\u6740\uff0c\u53ea\u662f\u5979\u89c1\u94c1\u98de\u9f99\u81ea\u5973\u513f\u6b7b \u5f8c\uff0c\u4e1d\u662f\u90c1\u90c1\u4e0d\u6b22\uff0c\u6240\u4ee5\u4e00\u8def\u4e0a\uff0c\u5e38\u5e38\u627e\u4e9b\u8bdd\u9017\u94c1\u98de\u9f99\u8bf4\u7b11\uff0c\u597d\u8ba9\u4ed6\u6e10\u91ca\u6101\u6000\u3002 \u3000\u3000\u9ec4\u660f\u65f6\u5206\uff0c\u4e24\u4eba\u5728\u4e07\u53bf\u6295\u5bbf\uff0c\u8fdb\u4e86\u5ba2\u5e97\uff0c\u7389\u7f57\u5239\u5ffd\u9053\uff1a\u201c\u7239\uff0c\u6211\u77a7\u89c1\u6355\u5934\u4eec\u7559\u4e0b\u7684\u6697 \u53f7\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4ec0\u9ebd\u6697\u53f7\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4ed6\u4eec\u8ffd\u6355\u7684\u597d\u50cf\u8fd8\u662f\u91cd\u8981\u72af\u4eba\u5462\uff0c\u5ba2\u5e97\u5916\u7684 \u5899\u58c1\u4e0a\u753b\u6709\u4e00\u53ea\u82b1\u8774\u8776\uff0c\u90a3\u662f\u6210\u90fd\u540d\u6355\u7518\u5929\u7acb\u7684\u6807\u5fd7\uff0c\u4ed6\u64c5\u7528\u6bd2\u836f\u8774\u8776\u9556\uff0c\u89c1\u8840\u5c01\u5589\uff0c\u662f\u7eff \u6797\u7684\u4e00\u4e2a\u5927\u654c\uff0c\u6211\u5728\u660e\u6708\u5ce1\u65f6\uff0c\u66fe\u6709\u9ed1\u9053\u7684\u670b\u53cb\uff0c\u8bf7\u6211\u53bb\u9664\u4ed6\u3002\u6211\u89c1\u5230\u6210\u90fd\u8def\u8fdc\uff0c\u5b98\u519b\u52bf\u529b \u53c8\u5927\uff0c\u8bda\u6050\u53bb\u4e86\uff0c\u5c71\u5be8\u4f1a\u7ed9\u5b98\u519b\u4e58\u865a\u653b\u88ad\uff0c\u6240\u4ee5\u6ca1\u6709\u7b54\u5e94\u3002\u7518\u5929\u7acb\u8fd8\u6709\u4e00\u4e2a\u628a\u5144\u53eb\u505a\u7126\u5316\uff0c \u5916\u5bb6\u529f\u592b\uff0c\u9887\u6709\u706b\u5019\uff0c\u4e5f\u662f\u6210\u90fd\u7684\u6355\u5934\u3002\u521a\u624d\u6211\u89c1\u7518\u5929\u7acb\u7559\u4e0b\u7684\u6697\u8bb0\uff0c\u5c31\u662f\u7559\u7ed9\u4ed6\u7684\u628a\u5144\u7126 \u5316\uff0c\u53eb\u4ed6\u901f\u901f\u8d76\u5230\u98de\u72d0\u5cad\u62e6\u622a\u72af\u4eba\u7684\uff0c\u82e5\u975e\u91cd\u8981\u72af\u4eba\uff0c\u90a3\u987b\u4ed6\u4eec\u4e8c\u4eba\u8054\u540c\u8ffd\u6355\u3002\u201d\u94c1\u98de\u9f99 \u9053\uff1a\u201c\u7ba1\u4ed6\u4ec0\u9ebd\u72af\u4eba\uff0c\u8fd8\u662f\u4e0d\u8981\u62db\u60f9\u95f2\u4e8b\u4e3a\u5999\u3002\u6b64\u5730\u9760\u8fd1\u6210\u90fd\uff0c\u54b1\u4eec\u82e5\u8d38\u7136\u51fa\u624b\uff0c\u5fc5\u60ca\u52a8\u4ed6 \u4eec\u4e0e\u54b1\u4eec\u505a\u5bf9\u3002\u54b1\u4eec\u867d\u7136\u4e0d\u6015\uff0c\u4f46\u884c\u7a0b\u90a3\u662f\u5fc5\u7136\u53d7\u963b\u7684\u4e86\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u62bf\u4e86\u62bf\u5634\uff0c\u7b11\u9053\uff1a\u201c\u7239\uff0c\u6211\u770b\u4f60\u8d8a\u6765\u8d8a\u6015\u4e8b\u4e86\uff01\u201d\u94c1\u98de\u9f99\u4f6f\u6012\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u6015 \u4e8b\uff0c\u5c06\u6765\u5230\u4e86\u4eac\u57ce\uff0c\u4f60\u518d\u77a7\u77a7\u6211\u7684\u3002\u201d\u7389\u7f57\u5239\u4e00\u7b11\u4e0d\u8bed\uff0c\u5728\u623f\u4e2d\u5750\u5b9a\u4e4b\u5f8c\uff0c\u6b63\u60f3\u5429\u5490\u5e97\u5c0f\u4e8c \u5f00\u996d\uff0c\u623f\u95e8\u6572\u4e86\u4e24\u4e0b\uff0c\u95e8\u5f00\u5904\u5374\u662f\u638c\u67dc\u8d70\u6765\uff0c\u63a9\u4e86\u623f\u95e8\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u5a18\u5b50\u53ef\u662f\u7ec3\u5973\u4fa0 \u9ebd\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u77e5\u9053\u6211\u7684\u540d\u5b57\uff1f\u201d\u638c\u67dc\u7684\u966a\u7b11\u9053\uff1a\u201c\u5c0f\u7684\u5ba2\u5e97\u62db\u5f85\u6765\u5f80\u5ba2\u5546\uff0c\u9ed1 \u9053\u4e0a\u7684\u670b\u53cb\uff0c\u6709\u65f6\u4e5f\u6765\u501f\u4f4f\u3002\u4e0d\u7792\u4f60\u8001\uff0c\u6731\u5be8\u4e3b\u4e5f\u66fe\u5728\u8fd9\u91cc\u4f4f\u8fc7\uff0c\u63d0\u8fc7\u4f60\u8001\u7684\u540d\u5b57\u3002\u201d\u7389\u7f57 \u5239\u9053\uff1a\u201c\u90a3\u4e2a\u6731\u5be8\u4e3b\uff1f\u201d\u638c\u67dc\u7684\u9053\uff1a\u201c\u7ef0\u53f7\u706b\u7075\u733f\u7684\u90a3\u4f4d\u5be8\u4e3b\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f \u706b\u7075\u733f\u6731\u5b9d\u693f\uff0c\u4ed6\u5728\u8fd9\u9644\u8fd1\u843d\u8349\u5417\uff1f\u201d\u638c\u67dc\u7684\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u8bf4\u7740\u6162\u6162\u4ece\u6000\u4e2d\u6478\u51fa\u4e00\u5c01\u4fe1 \u6765\u3002 \u3000\u3000\u706b\u7075\u733f\u6731\u5b9d\u693f\u662f\u4ee5\u524d\u5ddd\u9655\u8fb9\u5883\u7684\u5927\u76d7\u4e4b\u4e00\uff0c\u66fe\u53c2\u4e0e\u8fc7\u52ab\u738b\u7199\u5e0c\u7684\u91d1\u9a6c\u978d\u4e4b\u4e8b\u3002\u7389\u7f57\u5239 \u9053\uff1a\u201c\u8fd9\u5c01\u4fe1\u662f\u4ed6\u7ed9\u6211\u7684\u5417\uff1f\u201d\u638c\u67dc\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u662f\u53e6\u5916\u4e00\u4e2a\u5ba2\u4eba\u7ed9\u7684\u3002\u4ed6\u5148\u662f\u63d0\u8d77\u6731\u5be8\u4e3b \u7684\u540d\u53f7\uff0c\u60f3\u9001\u4fe1\u7ed9\u4ed6\uff0c\u5f8c\u6765\u6539\u4e86\u4e3b\u610f\uff0c\u7559\u4fe1\u7ed9\u4f60\u3002\u201d\u7389\u7f57\u5239\u5947\u9053\uff1a\u201c\u4ec0\u9ebd\u5ba2\u4eba\uff0c\u4ed6\u53c8\u600e\u4f1a\u77e5 \u9053\u6211\u5230\u8fd9\u91cc\uff1f\u201d\u638c\u67dc\u7684\u7b11\u9053\uff1a\u201c\u5ddd\u4e24\u7701\u9ed1\u9053\u4e0a\u7684\u670b\u53cb\uff0c\u8c01\u4e0d\u8ba4\u8bc6\u4f60\u8001\u4eba\u5bb6\u3002\u4f60\u8fd8\u6ca1\u6765\uff0c\u98ce\u58f0 \u65e9\u5df2\u64ad\u5230\u8fd9\u513f\u6765\u4e86\u3002\u8fd9\u4e2a\u5c0f\u5730\u65b9\u7b97\u5c0f\u7684\u5ba2\u5e97\u8fd8\u50cf\u4e2a\u6a21\u6837\uff0c\u8fd9\u4f4d\u5ba2\u4eba\u6599\u4f60\u8001\u4eba\u5bb6\u4e0d\u6765\u5219\u5df3\uff0c\u6765 \u4e86\u5927\u534a\u4f1a\u4f4f\u5728\u8fd9\u513f\u3002\u201d\u7389\u7f57\u5239\u7ed9\u4ed6\u4e00\u6367\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u597d\uff0c\u6211\u5012\u8981\u770b\u4ed6\u662f\u8c01\uff1f\u201d\u4ece\u638c\u67dc\u624b\u4e2d \u628a\u4fe1\u63a5\u8fc7\uff0c\u62c6\u5f00\u4e00\u770b\uff0c\u53ea\u89c1\u4e0a\u9762\u753b\u7740\u4e00\u53ea\u602a\u624b\uff0c\u9c9c\u8840\u6dcb\u6f13\uff0c\u5e76\u65e0\u6587\u5b57\u3002\u7389\u7f57\u5239\u9053\uff1a\u201c\u54c8\uff0c\u539f \u6765\u662f\u4ed6\uff0c\u4ed6\u5230\u5e95\u9047\u5230\u4ec0\u9ebd\u4e8b\u4e86\uff0c\u4f60\u8bf4\uff01\u201d\u638c\u67dc\u7684\u9053\uff1a\u201c\u4ed6\u6ca1\u6709\u8bf4\uff0c\u5c0f\u7684\u4e5f\u4e0d\u6562\u95ee\u3002\u4ed6\u753b\u5f97\u5f88 \u5306\u5fd9\uff0c\u521a\u521a\u753b\u597d\uff0c\u95e8\u5916\u5c31\u4f20\u6765\u9a6c\u94c3\u4e4b\u58f0\uff0c\u4ed6\u628a\u4fe1\u4ea4\u7ed9\u4e86\u6211\uff0c\u5c31\u7ffb\u5f8c\u5899\u8d70\u4e86\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a \u201c\u54e6\uff0c\u539f\u6765\u5982\u6b64\uff0c\u602a\u4e0d\u5f97\u4ed6\u8fde\u4e00\u4e2a\u5b87\u4e5f\u6ca1\u6709\u5199\u3002\u201d\u95ee\u9053\uff1a\u201c\u5f8c\u6765\u6765\u7684\u90a3\u4f4d\u5b98\u5dee\u662f\u4e0d\u662f\u8774\u8776\u9556 \u7518\u5929\u7acb\uff01\u201d\u638c\u67dc\u7684\u9053\uff1a\u201c\u6b63\u662f\uff0c\u4f60\u8001\u4eba\u5bb6\u600e\u9ebd\u77e5\u9053\uff1f\u4ed6\u8fd8\u548c\u53e6\u5916\u4e00\u4f4d\u5b98\u7237\u5728\u4e00\u8d77\u3002\u201d\u7389\u7f57\u5239 \u9053\uff1a\u201c\u4ed6\u5728\u4f60\u7684\u5ba2\u5e97\u5916\u9762\u7559\u4e0b\u6807\u5fd7\u5566\uff01\u201d\u638c\u67dc\u7684\u5413\u4e86\u4e00\u8df3\uff0c\u9053\uff1a\u201c\u4ec0\u9ebd\uff1f\u4ed6\u77e5\u5c0f\u5e97\u548c\u9ed1\u9053\u4e0a \u6709\u6765\u5f80\u5417\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u4ed6\u662f\u7ea6\u540c\u4f34\u53bb\u8ffd\u6355\u90a3\u4f4d\u5ba2\u4eba\u5566\u3002\u201d\u987f\u4e86\u4e00\u987f\u95ee\u9053\uff1a\u201c\u4f60\u77e5 \u9053\u98de\u72d0\u5cad\u5728\u90a3\u513f\uff1f\u201d\u638c\u67dc\u7684\u9053\uff1a\u201c\u79bb\u8fd9\u513f\u5341\u591a\u91cc\uff0c\u662f\u5230\u5ddd\u897f\u7684\u5c0f\u8def\u4e4b\u4e00\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a \u201c\u597d\uff0c\u4f60\u7ed9\u8fd9\u4f4d\u8001\u7237\u5b50\u70e7\u51e0\u5473\u5c0f\u83dc\uff0c\u5c31\u8981\u8fa3\u5b50\u9e21\u4e01\uff0c\u6a1f\u8336\u9e2d\uff0c\u6293\u7f8a\u8089\u3001\u7206\u53c1\u6837\u597d\u5566\u3002\u7239\uff0c\u8fd9 \u51e0\u6837\u5c0f\u83dc\u4f60\u633a\u6b22\u559c\u7684\u662f\u4e0d\u662f\u4e86\u53e6\u5916\u518d\u70eb\u4e00\u58f6\u6c7e\u9152\u3002\u201d\u638c\u67dc\u7684\u89c1\u7389\u7f57\u5239\u5bf9\u94c1\u98de\u9f99\u751a\u4e3a\u606d\u656c\uff0c\u8fd8 \u53e3\u53e3\u58f0\u58f0\u53eb\u4ed6\u505a\u201c\u7239\u201d\uff0c\u5927\u4e3a\u60ca\u5f02\u3002\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u7684\u670b\u53cb\u90fd\u53eb\u6211\u7389\u7f57\u5239\uff0c\u4f60\u4e5f\u53eb\u6211 \u7389\u7f57\u5239\u597d\u5566\u3002\u4e0d\u5fc5\u79f0\u4ec0\u9ebd\u201c\u8001\u4eba\u5bb6\u201d\uff0c\u5bf9\u8fd9\u4f4d\u8001\u7237\u5b50\u4f60\u624d\u5e94\u53eb\u8001\u4eba\u5bb6\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u54c8\uff0c \u6211\u4e5f\u8fd8\u4e0d\u670d\u8001\u54e9\u3002\u201d\u638c\u67dc\u7684\u9053\uff1a\u201c\u662f\u3002\u4e24\u4f4d\u8001\u4eba\u5bb6\u90fd\u8bf4\u7684\u662f\u3002\u54ce\uff0c\u6211\u53eb\u60ef\u4e86\u5634\uff0c\u6539\u4e0d\u4e86\u3002\u201d \u3000\u3000\u638c\u67dc\u7684\u544a\u9000\u4e4b\u5f8c\uff0c\u94c1\u98de\u9f99\u7b11\u9053\uff1a\u201c\u4f60\u7684\u540d\u6c14\u5012\u5f88\u5927\uff0c\u6211\u5728\u897f\u5317\u6df7\u4e86\u51e0\u5341\u5e74\uff0c\u5230\u4e86\u56db\u5ddd\uff0c \u5c31\u7ed9\u4eba\u5f53\u6210\u7cdf\u8001\u5934\u5b50\u5566\u3002\u201d\u7389\u7f57\u5239\u4e5f\u7b11\u9053\uff1a\u201c\u7239\u662f\u6210\u540d\u7684\u8001\u82f1\u96c4\uff0c\u5c0f\u4e00\u8f88\u7684\u8fd8\u4e0d\u914d\u8ba4\u8bc6\u4f60 \u5462\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u90a3\u4e2a\u7559\u4fe1\u7ed9\u4f60\u7684\u662f\u4ec0\u9ebd\u4eba\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u662f\u7f57\u94c1\u81c2\uff0c\u4ee5\u524d\u5728\u5ddd\u8fb9\u5883\u7684 \u7c73\u4ed3\u5c71\u5b89\u7a91\u7acb\u5be8\uff0c\u548c\u6731\u5b9d\u693f\u4ed6\u4eec\u90fd\u662f\u540c\u65f6\u7ed9\u6211\u6536\u670d\u7684\u3002\u5f8c\u6765\u5b98\u519b\u5927\u4e3e\u8fdb\u88ad\uff0c\u897f\u5404\u8def\u5be8\u4e3b\u90fd\u9003 \u7a9c\u4e86\uff0c\u6211\u4e5f\u5c31\u4e0d\u77e5\u4ed6\u7684\u4e0b\u843d\u4e86\u3002\u60f3\u4e0d\u5230\u4eca\u665a\u4ed6\u5374\u51fa\u73b0\u5728\u8fd9\u513f\u3002\u4ed6\u867d\u7136\u6709\u70b9\u540d\u6c14\uff0c\u6b66\u529f\u4e5f\u5f88\u4e0d \u9519\uff0c\u5374\u4e0d\u662f\u4ec0\u9ebd\u4e86\u4e0d\u8d77\u7684\u5927\u76d7\uff0c\u4e0d\u77e5\u4e3a\u4ec0\u9ebd\u6210\u90fd\u7684\u4e24\u4e2a\u540d\u6355\u786c\u90fd\u8981\u8ffd\u6355\u4ed6\u3002\u7239\uff0c\u4ed6\u548c\u6211\u6709\u8fc7 \u70b9\u9999\u706b\u4e4b\u60c5\uff0c\u5b5d\u656c\u8fc7\u4e0d\u5c11\u4e1c\u897f\u3002\u4fd7\u8bed\u8bf4\uff1a\u5f97\u4eba\u94b1\u8d22\u4e0e\u4eba\u6d88\u707e\uff0c\u6211\u5f97\u5230\u4ed6\u7684\u5b5d\u656c\uff0c\u4ed6\u6709\u96be\u544a \u6025\uff0c\u6211\u4e0d\u80fd\u8896\u624b\u4e0d\u7406\u3002\u201d\u94c1\u98de\u9f99\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u53bb\u6253\u67b6\u662f\u771f\u3002\u65e2\u7136\u4ed6\u662f\u4f60\u7684\u65e7\u5c5e\uff0c\u6211\u4e0d\u62e6\u4f60\u3002 \u6211\u548c\u4f60\u540c\u53bb\u5427\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u51e0\u4e2a\u6355\u5934\uff0c\u4f55\u987b\u52b3\u70e6\u5230\u4f60\u3002\u4f60\u5750\u7740\u559d\u9152\uff0c\u4e0d\u5230\u5929\u4eae\uff0c\u6211\u5c31\u56de \u6765\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u51fa\u4e86\u5ba2\u5e97\uff0c\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u4e0d\u8fc7\u534a\u4e2a\u65f6\u8fb0\uff0c\u5c31\u5230\u4e86\u98de\u72d0\u5cad\u4e0b\u3002\u98de\u72d0\u5cad\u53ea\u662f\u4e00\u5ea7\u5c0f \u5c0f\u7684\u5c71\u5c97\uff0c\u7389\u7f57\u5239\u5728\u5cad\u7684\u8fd9\u8fb9\uff0c\u5c31\u542c\u5f97\u90a3\u4e00\u8fb9\u7684\u6740\u4e4b\u58f0\uff0c\u5fc3\u9053\uff1a\u201c\u54c8\uff0c\u6765\u5f97\u6b63\u662f\u65f6\u5019\uff0c\u4ed6\u4eec \u679c\u7136\u52a8\u8d77\u624b\u5566\uff01\u6211\u4e14\u770b\u770b\u7f57\u94c1\u81c2\u7684\u6b66\u529f\u8fdb\u5883\u5982\u4f55\uff1f\u201d\u53c1\u4e94\u4e4b\u591c\uff0c\u6708\u5149\u768e\u768e\uff0c\u7389\u7f57\u5239\u4e0a\u4e86\u5c71 \u5934\uff0c\u4fef\u9996\u4e0b\u671b\uff0c\u53ea\u89c1\u5c71\u811a\u5c0f\u8def\u4e0a\u53c1\u4e2a\u4eba\u56f4\u7740\u7f57\u94c1\u81c2\u6740\uff0c\u9664\u4e86\u7518\u5929\u7acb\u4e0e\u7126\u5316\u4e4b\u5916\uff0c\u53e6\u5916\u4e00\u4eba\u4e5f \u4f3c\u5728\u90a3\u513f\u89c1\u8fc7\u4f3c\u7684\uff0c\u7389\u7f57\u5239\u770b\u4e86\u4e00\u770b\uff0c\u8bb0\u8d77\u8fd9\u662f\u5728\u5357\u88ab\u81ea\u5df1\u8ffd\u5f97\u671b\u98ce\u800c\u9003\u7684\u9526\u8863\u536b\u6307\u6325\u77f3 \u6d69\uff0c\u5fc3\u9053\uff1a\u201c\u542c\u8bf4\u77f3\u6d69\u5df2\u5347\u4e86\u897f\u5382\u7684\u526f\u603b\u6869\u5934\uff0c\u600e\u9ebd\u4ed6\u4e5f\u6765\u5566\u3002\u201d\u518d\u770b\u6e05\u695a\u65f6\uff0c\u7f57\u94c1\u81c2\u8fd8\u80cc \u7740\u4e00\u4e2a\u5c0f\u5b69\uff0c\u5728\u53c1\u4eba\u56f4\u653b\u4e4b\u4e0b\uff0c\u5341\u5206\u5371\u6025\uff01 \u3000\u3000\u7389\u7f57\u5239\u957f\u7b11\u4e00\u58f0\uff0c\u62d4\u5251\u51b2\u4e0b\uff0c\u77f3\u6d69\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u7389\u7f57\u5239\u6765\u5566\uff01\u201d\u4e00\u62db\u201c\u5012\u6d77\u7ffb\u6c5f\u201d\uff0c \u53cc\u638c\u6025\u626b\uff0c\u7f57\u94c1\u81c2\u7ad6\u81c0\u4e00\u683c\uff0c\u7518\u5929\u7acb\u5355\u5200\u4ece\u4fa7\u88ad\u5230\uff0c\u4e5f\u662f\u5371\u6025\u4e4b\u6781\uff0c\u7f57\u94c1\u81c2\u8f6c\u8eab\u4e00\u95ea\uff0c \u201c\u5361\u201d\u7684\u4e00\u58f0\uff0c\u80a9\u4e0a\u4e2d\u4e86\u4e00\u5200\uff0c\u80cc\u4e0a\u7684\u5b69\u5b50\u201c\u54c7\u201d\u58f0\u5927\u53eb\uff0c\u821e\u52a8\u4e24\u53ea\u5c0f\u624b\uff0c\u5411\u77f3\u6d69\u62cd\u53bb\uff0c\u77f3 \u6d69\u54c8\u54c8\u4e00\u7b11\uff0c\u5de6\u624b\u4e00\u4f38\uff0c\u628a\u5c0f\u5b69\u62a2\u4e86\u8fc7\u6765\u3002\u7f57\u94c1\u81c2\u4e00\u58f0\u6012\u543c\uff0c\u53f3\u638c\u76f4\u5288\uff0c\u5de6\u817f\u6a2a\u626b\uff0c\u7126\u5316\u5de6 \u8155\u865a\u52fe\uff0c\u53f3\u62f3\u75be\u5410\uff0c\u6b63\u4e2d\u8fdb\u62db\uff0c\u4ed6\u7528\u7684\u662f\u4f0f\u864e\u62f3\u4e2d\u201c\u6a2a\u6253\u91d1\u949f\u201d\u62f3\u5f0f\uff0c\u5de6\u865a\u53f3\u5b9e\uff0c\u62f3\u51fb\u7f57\u94c1 \u81c2\u7684\u201c\u80a9\u4e95\u7a74\u201d\uff0c\u8fd9\u4e00\u62db\u751a\u4e3a\u9634\u6bd2\uff0c\u4ed6\u4ee5\u4e3a\u7f57\u94c1\u81c2\u7a81\u7136\u95ea\u907f\uff0c\u90a3\u9ebd\u4e0b\u4e00\u62db\u5c31\u53ef\u914d\u5408\u7518\u5929\u7acb\u7684 \u5355\u5200\u653b\u4ed6\u4e0b\u76d8\uff0c\u90a3\u77e5\u7f57\u94c1\u81c2\u62da\u4e86\u6027\u547d\uff0c\u4e00\u638c\u51fb\u4e0b\uff0c\u4e24\u4eba\u78b0\u4e2a\u6b63\u7740\uff0c\u7f57\u94c1\u81c2\u4e00\u638c\u51fb\u4e2d\u4ed6\u7684\u524d \u80f8\uff0c\u4ed6\u4e5f\u4e00\u62f3\u6253\u788e\u4e86\u7f57\u94c1\u81c2\u80a9\u9aa8\uff0c\u4e24\u4eba\u90fd\u662f\u75db\u6781\u60e8\u547c\uff0c\u817e\u8eab\u5012\u9000\u6570\u4e08\uff01 \u3000\u3000\u8fd9\u51e0\u62db\u6025\u5982\u7535\u5149\u6d41\u706b\uff0c\u4f46\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u7389\u7f57\u5239\u5df2\u7136\u51b2\u5230\uff0c\u7f57\u94c1\u81c2\u53eb\u9053\uff1a\u201c\u5148\u6551\u90a3\u4e2a \u5b69\u5b50\uff01\u201d\u77f3\u6d69\u62a2\u4e86\u5b69\u5b50\uff0c\u5df2\u9003\u51fa\u5341\u9980\u4e08\u4e4b\u9047\uff0c\u7389\u7f57\u5239\u53eb\u58f0\uff1a\u201c\u90a3\u91cc\u8d70\uff01\u201d\u8db3\u5c16\u70b9\u5730\uff0c\u53c1\u8d77\u53c1 \u4f0f\uff0c\u6025\u903e\u6d41\u661f\uff0c\u970e\u5ffd\u8d76\u5230\u8eab\u5f8c\uff0c\u77f3\u6d69\u63d0\u8d77\u5b69\u5b50\uff0c\u53cd\u8eab\u4e00\u6321\uff0c\u7389\u7f57\u5239\u9a82\u9053\uff1a\u201c\u4e0d\u8981\u8138\u7684\u4e0b\u6d41\u62db \u6570\uff01\u201d\u77f3\u6d69\u7a81\u611f\u624b\u8155\u4e00\uff0c\u7389\u7f57\u5239\u51fa\u624b\u5982\u7535\uff0c\u62e2\u6307\u4e00\u62c2\uff0c\u5939\u624b\u5c06\u5c0f\u5b69\u62a2\u8fc7\uff0c\u6708\u5149\u4e0b\u53ea\u89c1\u5c0f\u5b69\u9762 \u5982\u6ee1\u6708\uff0c\u5f20\u53e3\u8bf4\u9053\uff1a\u201c\u59d1\u59d1\uff0c\u591a\u8c22\u4f60\u3002\u201d\u7389\u7f57\u5239\u6014\u4e86\u4e00\u6014\uff0c\u5728\u8fd9\u6837\u7684\u6fc0\u6597\u5371\u9669\u4e4b\u4e2d\uff0c\u8fd9\u5c0f\u5b69 \u5c45\u7136\u4e0d\u54ed\uff0c\u9762\u8272\u4e5f\u5e76\u4e0d\u663e\u5f97\u600e\u6837\u60ca\u60f6\uff0c\u8fd8\u6562\u5f00\u53e3\u5411\u81ea\u5df1\u62db\u547c\uff0c\u771f\u662f\u89c1\u6240\u672a\u89c1\u95fb\u6240\u672a\u95fb\u7684\u5927\u80c6 \u5b69\u5b50\uff01 \u3000\u3000\u7389\u7f57\u5239\u7a0d\u5fae\u8be7\u6115\uff0c\u505c\u4e86\u4e00\u505c\uff0c\u77f3\u6d69\u62da\u547d\u5954\u9003\uff0c\u53c8\u5df2\u63a0\u51fa\u5341\u9980\u4e08\u5916\uff0c\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u597d\u5b69 \u5b50\uff0c\u4f60\u770b\u6211\u628a\u8fd9\u6076\u4eba\u7ed9\u4f60\u6349\u56de\u6765\uff0c\u8ba9\u4f60\u6253\u4ed6\u4e24\u5df4\u638c\uff0c\u6d88\u6d88\u6c14\u3002\u201d\u731b\u542c\u5f97\u7f57\u94c1\u81c2\u4e00\u58f0\u60e8\u53eb\uff0c\u90a3 \u5b69\u5b50\u9053\uff1a\u201c\u6211\u8981\u7f57\u53d4\u53d4\uff0c\u6076\u4eba\u4ee5\u5f8c\u518d\u6253\uff0c\u59d1\u59d1\uff0c\u4f60\u53bb\u6551\u7f57\u53d4\u53d4\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u6025\u5fd9\u8f6c\u8eab\uff0c\u53ea\u89c1\u7518\u5929\u7acb\u6276\u7740\u7126\u5316\uff0c\u8df3\u4e0b\u5c71\u8def\uff0c\u9003\u5165\u9ea6\u5730\u4e4b\u4e2d\u3002\u7f57\u94c1\u81c2\u4e00\u53ea\u624b\u81c2\u540a \u4e86\u4e0b\u6765\uff0c\u9762\u8272\u60e8\u767d\uff0c\u6447\u6447\u6b32\u5012\u3002\u7389\u7f57\u5239\u4e0a\u524d\u4e00\u770b\uff0c\u53ea\u89c1\u4ed6\u7684\u5de6\u81c2\u88ab\u5229\u5200\u6240\u5288\uff0c\u53ea\u6709\u4e00\u70b9\u9aa8\u5934 \u8fd8\u8fde\u7740\u80a9\u818a\uff0c\u663e\u89c1\u4e0d\u80fd\u6cbb\u4e86\u3002\u800c\u4e14\u90a3\u53ea\u540a\u4e0b\u6765\u7684\u624b\u81c2\uff0c\u53c8\u9ed1\u53c8\u80bf\uff0c\u597d\u50cf\u5c0f\u6c34\u6876\u4e00\u822c\uff01 \u3000\u3000\u7f57\u94c1\u81c2\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4e2d\u4e86\u4ed6\u7684\u8774\u8776\u9556\uff0c\u53c8\u88ab\u4ed6\u65ab\u4e86\u4e00\u5200\u3002\u6b63\u597d\uff01\u8fd9\u53cd\u800c\u80fd\u963b\u6b62\u6bd2\u6c14\u4e0d\u4e0a \u5347\u5566\u3002\u201d\u7389\u7f57\u5239\u4f38\u624b\u53bb\u6478\u91d1\u521b\u836f\uff0c\u7f57\u94c1\u81c2\u9053\uff1a\u201c\u4e0d\u4e2d\u7528\u5566\uff01\u201d\u53f3\u624b\u6478\u51fa\u89e3\u8155\u5c16\u5200\uff0c\u201c\u5580\u5693\u201d \u4e00\u58f0\uff0c\u628a\u5de6\u81c2\u9f50\u80a9\u5207\u4e0b\uff0c\u987f\u65f6\u8840\u6d41\u5982\u6ce8\uff0c\u90a3\u5c0f\u5b69\u5b50\u521a\u624d\u4e0d\u54ed\uff0c\u73b0\u5728\u5374\u7741\u5927\u773c\u775b\uff0c\u54c7\u7684\u4e00\u58f0\u54ed \u4e86\u51fa\u6765\u3002 \u3000\u3000\u7389\u7f57\u5239\u653e\u4e0b\u5b69\u5b50\uff0c\u6495\u4e86\u4e00\u5e45\u8863\u895f\uff0c\u6d82\u4e86\u91d1\u521b\u836f\u66ff\u4ed6\u5305\u88f9\u4f24\u53e3\uff0c\u7b11\u9053\uff1a\u201c\u597d\u7537\u5b50\uff0c\u4f60\u4e0d\u6127 \u662f\u6211\u7684\u670b\u53cb\uff01\u201d\u7f57\u94c1\u81c2\u54fc\u4e5f\u4e0d\u54fc\u4e00\u58f0\uff0c\u5438\u4e86\u53e3\u6c14\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8981\u4f60\u8001\u4eba\u5bb6\u670d\u4f8d\uff0c\u6298\u715e\u6211 \u4e86\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u73b0\u5728\u4f60\u8fd8\u8bb2\u90a3\u5957\u89c4\u77e9\u4f5c\u751a\uff1f\u6211\u4e5f\u6d17\u624b\u4e0d\u5e72\u7eff\u6797\u5566\u3002\u54b1\u4eec\u73b0\u5728\u662f\u670b\u53cb\u3002\u201d \u7f57\u94c1\u81c2\u201c\u55c4\u201d\u4e86\u4e00\u58f0\uff0c\u4f3c\u9887\u8be7\u5f02\u3002\u989d\u4e0a\u7684\u6c57\u73e0\u6ef4\u4e86\u4e0b\u6765\uff0c\u60f3\u662f\u751a\u4e3a\u75db\u695a\uff0c\u4f46\u4ed6\u4ecd\u7136\u5fcd\u7740\uff0c\u4f4e \u58f0\u5b89\u6170\u90a3\u5b69\u5b50\u9053\uff1a\u201c\u9aa2\u513f\uff0c\u522b\u54ed\uff0c\u522b\u54ed\u201c\u4f60\u53d4\u53d4\u6b7b\u4e0d\u4e86\uff01\u201d\u90a3\u5b69\u5b50\u89c1\u4e24\u4e2a\u5927\u4eba\u90fd\u6709\u8bf4\u6709\u7b11\uff0c \u53ea\u5f53\u5e76\u4e0d\u788d\u4e8b\uff0c\u679c\u7136\u4e0d\u54ed\u4e86\u3002\u7f57\u94c1\u81c2\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u59d1\u662f\u5f53\u4eca\u5929\u4e0b\u6700\u6709\u672c\u4e8b\u7684\u5973\u82f1\u96c4\uff0c\u4f60\u78b0\u7740 \u5979\u662f\u5929\u5927\u7684\u8fd0\u6c14\uff0c\u8fd8\u4e0d\u53e9\u5934\u9053\u8c22\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u597d\u4e56\uff0c\u4ed6\u5df2\u8c22\u8fc7\u5566\uff01\u201d\u90a3\u5b69\u5b50\u542c \u4e86\u7f57\u94c1\u81c2\u7684\u8bdd\uff0c\u679c\u7136\u53e9\u5934\u518d\u8c22\u3002 \u3000\u3000\u7389\u7f57\u5239\u770b\u8fd9\u5b69\u5b50\u5b9e\u5728\u53ef\u7231\uff0c\u7b11\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u8c01\u5bb6\u7684\u5b69\u5b50\uff0c\u591a\u5c11\u5c81\u5566\uff1f\u53eb\u4ec0\u9ebd\u540d\u5b57\uff1f\u600e\u9ebd \u4f1a\u8ddf\u4f60\u9003\u5230\u8fd9\u91cc\u6765\uff1f\u201d\u90a3\u5b69\u5b50\u62a2\u7740\u7b54\u9053\uff1a\u201c\u6211\u53eb\u6768\u4e91\u9aa2\uff0c\u8fd9\u4e2a\u6708\u5341\u516d\u521a\u597d\u4e94\u5c81\uff0c\u6211\u7684\u7238\u7238\u53eb \u6768\u6d9f\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u554a\uff0c\u539f\u6765\u662f\u6768\u6d9f\u7684\u5b69\u5b50\u3002\u4f60\u7236\u4eb2\u53ef\u6ca1\u6709\u4f60\u7684\u80c6\u91cf\u3002\u201d\u6768\u4e91\u9aa2\u9053\uff1a \u201c\u8c01\u8bf4\u6ca1\u6709\uff1f\u4ed6\u5e38\u5e38\u5728\u5bb6\u91cc\u8bf4\u8981\u6740\u5978\u81e3\uff0c\u5f88\u5927\u5f88\u5927\u7684\u5978\u81e3\u3002\u7f57\u53d4\u53d4\u5bf9\u6211\u8bf4\uff0c\u5978\u81e3\u548c\u7687\u5e1d\u5f88\u8981 \u597d\uff0c\u6211\u7238\u7238\u4e0d\u6015\u5978\u81e3\uff0c\u4e5f\u4e0d\u6015\u7687\u5e1d\uff0c\u8fd8\u6ca1\u6709\u80c6\u91cf\u5417\uff1f\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u597d\uff0c\u7b97\u6211\u8bf4\u9519\uff0c\u4f60\u7238 \u7238\u6709\u80c6\u91cf\uff01\u201d\u8fd9\u8fd8\u662f\u7389\u7f57\u5239\u6709\u751f\u4ee5\u6765\u7b2c\u4e00\u6b21\u8ba4\u9519\uff0c\u8fd9\u5b69\u5b50\u90a3\u91cc\u77e5\u9053\uff0c\u8fd8\u5f97\u610f\u7684\u7b11\u4e86\u4e00\u7b11\u3002 \u3000\u3000\u7f57\u94c1\u81c2\u4f4e\u58f0\u9053\uff1a\u201c\u53c1\u5e74\u4e4b\u524d\uff0c\u6211\u5728\u9655\u897f\u7acb\u4e0d\u4f4f\u8db3\uff0c\u9063\u6563\u4e86\u90e8\u5c5e\u4e4b\u5f8c\uff0c\u6d41\u6d6a\u6c5f\u6e56\uff0c\u5f8c\u6765\u6709 \u4eba\u8350\u6211\u5230\u6768\u5927\u4eba\u5bb6\u4e2d\u505a\u62a4\u9662\uff0c\u6211\u5c31\u53bb\u5566\u3002\u201d\u7389\u7f57\u5239\u5148\u662f\u9762\u8272\u4e00\u6c89\uff0c\u7ee7\u800c\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u6768\u5927 \u4eba\u5c31\u662f\u6768\u6d9f\u5417\uff1f\u201d\u7f57\u94c1\u81c2\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u6768\u6d9f\u6211\u4e5f\u4e0d\u4f1a\u53bb\u4e86\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6768\u6d9f\u662f\u4e2a\u597d\u5b98\uff0c \u6211\u4e0d\u8d23\u602a\u4f60\uff0c\u4f60\u8bf4\u4e0b\u53bb\u3002\u201d\u6768\u4e91\u9aa2\u542c\u7389\u7f57\u5239\u8bf4\u4ed6\u7236\u4eb2\u662f\u4e2a\u597d\u5b98\uff0c\u53c8\u7b11\u4e86\u4e00\u7b11\u3002 \u3000\u3000\u7f57\u94c1\u81c2\u7eed\u9053\uff1a\u201c\u6768\u5927\u4eba\u5f85\u6211\u5f88\u597d\uff0c\u6211\u4e5f\u4e50\u5f97\u6258\u5e87\u5728\u4ed6\u7684\u95e8\u4e0b\uff0c\u57cb\u540d\u9690\u59d3\uff0c\u8fc7\u4e86\u53c1\u5e74\u3002\u4eca \u5e74\u6b63\u6708\uff0c\u4e00\u5929\u665a\u4e0a\uff0c\u6768\u5927\u4eba\u628a\u6211\u53eb\u8fdb\u5185\u5ba4\uff0c\u5bf9\u6211\u8bf4\u4ed6\u8981\u4e0a\u758f\u52be\u9b4f\u5fe0\u8d24\uff0c\u5982\u679c\u53c2\u52be\u4e0d\u5012\uff0c\u53ef\u80fd \u6709\u6284\u5bb6\u706d\u65cf\u4e4b\u7978\uff0c\u56e0\u6b64\u8981\u6211\u628a\u4ed6\u7684\u513f\u5b50\u5148\u5e26\u51fa\u4eac\uff0c\u4ed6\u7b49\u6211\u8d70\u4e86\u5341\u5929\u4e4b\u5f8c\uff0c\u624d\u4e0a\u5f39\u7ae0\u3002\u73b0\u5728\u77f3 \u6d69\u7518\u5929\u7acb\u7126\u5316\u4ed6\u4eec\u90fd\u8054\u540c\u6765\u8ffd\u6355\u6211\uff0c\u60f3\u5fc5\u4ed6\u7684\u5f39\u7ae0\u5df2\u4e0a\uff0c\u4e8b\u60c5\u5df2\u8d25\u4e86\u3002\u201d\u7f57\u94c1\u81c2\u8bf4\u4e86\u4e00\u9635 \u8bdd\uff0c\u53c8\u75db\u5f97\u6c57\u73e0\u76f4\u6ef4\uff0c\u541e\u4e86\u4e00\u9897\u6b62\u75db\u836f\u4e38\uff0c\u7a0d\u7a0d\u597d\u8f6c\u3002\u7389\u7f57\u5239\u5ffd\u95ee\u9053\uff1a\u201c\u4f60\u8981\u628a\u8fd9\u5b69\u5b50\u5e26\u5230 \u90a3\u91cc\u53bb\uff1f\u201d \u3000\u3000\u7f57\u94c1\u81c2\u9053\uff1a\u201c\u6211\u60f3\u7ed9\u4ed6\u627e\u4e00\u4f4d\u5e08\u5085\uff0c\u82e5\u4ed6\u7236\u4eb2\u88ab\u5978\u81e3\u6240\u5bb3\u2026\u2026\u201d\u6768\u4e91\u9aa2\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211 \u5c31\u66ff\u4ed6\u62a5\u4ec7\u3002\u201d\u7f57\u94c1\u81c2\u7b11\u4e86\u4e00\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\uff0c\u4f60\u8981\u4e0d\u8981\u5f92\u5f1f\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8fd9\u5b69\u5b50 \u6211\u6781\u559c\u6b22\uff0c\u4f46\u6211\u73b0\u5728\u4e0d\u80fd\u6536\u5f92\u5f1f\u3002\u201d\u60f3\u4e86\u4e00\u60f3\uff0c\u5ffd\u9053\uff1a\u201c\u82e5\u975e\u6709\u964d\u9f99\u4f0f\u864e\u7684\u672c\u9886\uff0c\u542b\u6c5f\u5305\u6d77 \u7684\u80f8\u895f\uff0c\u4e5f\u4e0d\u914d\u505a\u8fd9\u5b69\u5b50\u7684\u5e08\u5085\u3002\u6211\u5fc3\u76ee\u4e2d\u5012\u6709\u4e00\u4eba\uff0c\u53ea\u662f\u4f4f\u5f97\u592a\u8fdc\uff0c\u4ed6\u4f4f\u5728\u5929\u5c71\u4e4b\u4e0a\uff0c\u4f60 \u4e0d\u6015\u8def\u9014\u8270\u9669\u5417\uff1f\u201d\u7f57\u94c1\u81c2\u773c\u775b\u4e00\u4eae\uff0c\u5fc3\u60f3\u4ec0\u9ebd\u4eba\u503c\u5f97\u7389\u7f57\u5239\u5982\u6b64\u63a8\u5d07\u4e86\u8bf4\u9053\uff1a\u201c\u6211\u6b7b\u5c1a\u4e0d \u6015\uff0c\u4f55\u60e7\u8270\u9669\u4e86\u8bf7\u95ee\u662f\u90a3\u4f4d\u524d\u8f88\u82f1\u96c4\uff1f\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u4ed6\u662f\u5c11\u5e74\u82f1\u96c4\uff0c\u6bd4\u6211\u5927\u4e0d\u4e86\u94b1\u5c81\uff0c \u73b0\u5728\u5927\u6982\u505a\u4e86\u548c\u5c1a\u4e86\u3002\u5582\uff0c\u5cb3\u9e23\u73c2\u7684\u540d\u5b57\u4f60\u542c\u8fc7\u5417\uff1f\u201d\u7f57\u94c1\u81c2\u9053\uff1a\u201c\u542c\u6768\u5927\u4eba\u8bf4\u8fc7\u3002\u718a\u7ecf\u7565 \u662f\u6768\u5927\u4eba\u6700\u597d\u7684\u670b\u53cb\uff0c\u5cb3\u545c\u73c2\u662f\u718a\u7ecf\u7565\u7684\u53c2\u8d5e\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u4ee5\u4e3a\u4ed6\u662f\u4e2a\u5fae\u4e0d\u8db3\u9053\u7684\u5e55\u50da\uff0c\u4ed6\u7684\u5251\u6cd5\u7eb5\u4e0d\u80fd\u79f0\u76d6\u4e16\u65e0\u53cc\uff0c\u4e5f\u6ca1\u6709 \u8c01\u80fd\u8d85\u51fa\u4ed6\u4e86\u3002\u4f60\u628a\u8fd9\u5b69\u5b50\u62b1\u53bb\u627e\u4ed6\uff0c\u5c31\u8bf4\u662f\u6211\u7389\u7f57\u5239\u8981\u4ed6\u6536\u7684\uff01\u201d\u7f57\u94c1\u81c2\u8bf4\uff1a\u201c\u597d\uff0c\u6211\u5c31 \u51ed\u7740\u4e00\u53ea\u624b\u81c2\uff0c\u4e5f\u80fd\u628a\u4ed6\u62b1\u4e0a\u5929\u5c71\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u73b0\u5728\u8d70\u5f97\u52a8\u5417\uff1f\u201d\u7f57\u94c1\u81c2\u9053\uff1a\u201c\u8d70\u5f97 \u52a8\uff01\u201d\u7389\u7f57\u5239\u524a\u4e86\u4e00\u6839\u6811\u679d\u7ed9\u4ed6\u4f5c\u62d0\u6756\uff0c\u9053\uff1a\u201c\u77f3\u6d69\u4ed6\u4eec\u89c1\u6211\u51fa\u624b\u6551\u4f60\uff0c\u5728\u4ed6\u4eec\u672a\u89c5\u5f97\u66f4\u9ad8 \u660e\u7684\u5e2e\u624b\u4e4b\u524d\uff0c\u8c05\u4e0d\u6562\u56de\u6765\u627e\u9877\u3002\u201d\u7f57\u94c1\u81c2\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u89c1\u4e86\u4f60\u8001\u4eba\u5bb6\u5982\u9f20\u89c1\u732b\uff0c\u6211\u770b\u4ed6\u4eec \u5b9a\u9003\u56de\u6210\u90fd\u53bb\u5566\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6731\u5b9d\u693f\u5c31\u5728\u9644\u8fd1\u843d\u8349\uff0c\u4f60\u662f\u77e5\u9053\u7684\u4e86\u3002\u4f60\u6162\u6162\u8d70\u53bb\uff0c\u5929\u4eae \u4e4b\u5f8c\u4e5f\u603b\u53ef\u8d70\u5230\u4ed6\u90a3\u513f\u3002\u7136\u5f8c\u4f60\u53eb\u4ed6\u548c\u4f60\u4e00\u9053\u5230\u5e7f\u5143\u53bb\u89c1\u674e\u5ca9\uff0c\u5c31\u8bf4\u8fd9\u5b69\u5b50\u662f\u6211\u8981\u4f60\u9001\u5230\u5929 \u5c71\u7684\u3002\u897f\u5317\u662f\u4ed6\u4eec\u7684\u5929\u4e0b\uff0c\u4ed6\u4e00\u5b9a\u6709\u529e\u6cd5\u62a4\u9001\u4f60\u51fa\u7389\u95e8\u5173\u3002\u201d\u7f57\u94c1\u81c2\u9053\u4e86\u58f0\u8c22\uff0c\u6323\u624e\u8d77\u6765\uff0c \u6276\u7740\u62d0\u6756\uff0c\u4e00\u6b65\u4e00\u6b65\u7684\u5411\u524d\u8d70\u53bb\u3002\u6768\u4e91\u9aa2\u8ddf\u5728\u5f8c\u9762\uff0c\u8fde\u8dd1\u5e26\u8dc3\uff0c\u8fd8\u4e0d\u65f6\u56de\u5934\u5411\u7389\u7f57\u5239\u62db\u62db \u624b\u3002\u7389\u7f57\u5239\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u4eb2\u81ea\u62b1\u4ed6\u53bb\u627e\u6731\u5b9d\u6869\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u5c0f\u5b69\u5b50\u4e0d\u591a\u53d7\u78e8\u7ec3\uff0c\u4e0d\u591a\u7ecf \u8270\u9669\uff0c\u4e5f\u96be\u6210\u5927\u5668\uff0c\u7531\u4ed6\u53bb\u5427\uff01\u201d\u770b\u4e8c\u4eba\u8d70\u8fdc\uff0c\u4e5f\u4fbf\u8f6c\u56de\u5ba2\u5e97\u3002 \u3000\u3000\u518d\u8bf4\u94c1\u98de\u9f99\u5403\u4e86\u665a\u996d\u4e4b\u5f8c\uff0c\u7b49\u4e86\u4e00\u9635\uff0c\u4e0d\u89c1\u7389\u7f57\u5239\u56de\u6765\uff0c\u5fc3\u9053\uff1a\u201c\u90a3\u51e0\u4e2a\u6355\u5934\u5c82\u662f\u88f3\u513f \u5bf9\u624b\uff0c\u6211\u4f55\u5fc5\u6302\u5fc3\u3002\u201d\u6b63\u60f3\u7761\u89c9\uff0c\u5ffd\u95fb\u5916\u9762\u9690\u9690\u4f20\u6765\u4e89\u5435\u4e4b\u58f0\uff0c\u638c\u67dc\u7684\u5ffd\u7136\u63a8\u95e8\u8fdb\u6765\uff0c\u4f4e\u58f0 \u8bf4\u9053\uff1a\u201c\u706b\u7075\u733f\u6731\u5be8\u4e3b\u6765\u5566\uff0c\u5728\u5916\u9762\u548c\u4eba\u5403\u8bb2\u8336\uff0c\u597d\u50cf\u662f\u9884\u5148\u7ea6\u5b9a\u6765\u7684\u3002\u73b0\u5728\u5435\u7ffb\u4e86\uff0c\u4f60\u8001 \u51fa\u53bb\u529d\u529d\u3002\u201d\u8fd9\u5ba2\u5e97\u867d\u7136\u662f\u53c1\u6559\u4e5d\u6d41\u9ed1\u9053\u767d\u9053\u90fd\u4e00\u5f8b\u62db\u5f85\uff0c\u4f46\u82e5\u5f04\u51fa\u4eba\u547d\uff0c\u603b\u662f\u4e0d\u597d\u3002\u6240\u4ee5 \u638c\u67dc\u7684\u6025\u5fd9\u8bf7\u4eba\u529d\u67b6\u3002 \u3000\u3000\u94c1\u98de\u9f99\u53d7\u4e86\u638c\u67dc\u7684\u6bb7\u52e4\u62db\u5f85\uff0c\u4e0d\u597d\u610f\u601d\u4e0d\u7ba1\uff0c\u4fbf\u968f\u7740\u638c\u67dc\u8d70\u51fa\u5916\u9762\u9762\u8336\u5385\uff0c\u53ea\u89c1\u5f53\u4e2d\u4e00 \u5f20\u684c\u5b50\uff0c\u6731\u5b9d\u693f\u5750\u5728\u4e0a\u9996\uff0c\u4e24\u4e2a\u5ba2\u4eba\u5750\u5728\u4e24\u8fb9\uff0c\u6b63\u5728\u5435\u5435\u56b7\u56b7\uff0c\u94c1\u98de\u9f99\u542c\u5f97\u5de6\u4fa7\u7684\u5c11\u5e74\u56b7 \u9053\uff1a\u201c\u6211\u4e07\u53bf\u5510\u5bb6\u4ece\u4e0d\u4e0e\u4eba\u8ba8\u9556\uff0c\u4f60\u4e0d\u8981\u656c\u9152\u4e0d\u5403\u5403\u7f5a\u9152\uff01\u201d\u6731\u5b9d\u693f\u62cd\u53f0\u6012\u9053\uff1a\u201c\u597d\u54c7\uff0c\u4f60 \u62ff\u5510\u5bb6\u7684\u540d\u5934\u6765\u552c\u6211\uff1f\u6211\u504f\u4e0d\u7ed9\uff01\u5929\u7687\u8001\u5b50\u6765\u6211\u4e5f\u4e0d\u7ed9\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u539f\u6765\u662f\u5510\u5bb6\u7684\u4eba\uff0c\u8fd9\u4e8b\u66f4\u4e0d\u80fd\u4e0d\u7ba1\u4e86\u3002\u201d\u90a3\u5c11\u5e74\u4e00\u638c \u51fb\u684c\uff0c\u968f\u7740\u201c\u7830\u201d\u7136\u5de8\u54cd\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6731\u5be8\u4e3b\u65e2\u7136\u4e0d\u7559\u60c5\u9762\uff0c\u90a3\u9ebd\u5728\u4e0b\u7684\u4e0d\u77e5 \u5929\u9ad8\u5730\u539a\uff0c\u4fbf\u5728\u6b64\u8981\u8bf7\u6559\u51e0\u62db\uff01\u4e3a\u670b\u53cb\u4e24\u80c1\u63d2\u5200\uff0c\u6731\u5be8\u4e3b\u4f60\u4fbf\u662f\u5c06\u6211\u53c1\u5200\u516d\u6d1e\uff0c\u6211\u4e5f\u6b7b\u800c\u65e0 \u6028\u3002\u201d \u3000\u3000\u6731\u5b9d\u693f\u663e\u7136\u4e5f\u662f\u4e2a\u6027\u6025\u7684\u6c49\u5b50\uff0c\u5916\u8863\u4e00\u629b\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u4e5f\u9053\uff1a\u201c\u90a3\u597d\u6781\u4e86\uff0c\u4f60\u8981\u6bd4\u5175 \u5203\uff1f\u6bd4\u62f3\u811a\u4e86\u8fd8\u662f\u6bd4\u6697\u5668\uff1f\u54c8\uff0c\u4f60\u4eec\u5510\u5bb6\u7684\u6697\u5668\u5929\u4e0b\u95fb\u540d\uff0c\u54b1\u4eec\u4e7e\u8106\u5c31\u6bd4\u6697\u5668\u4e86\u5427\u3002\u5916\u9762\u5730 \u65b9\u5bbd\u655e\uff0c\u8bf7\u5230\u5916\u9762\u53bb\u3002\u6211\u7684\u4e1c\u897f\u5df2\u7ecf\u5e26\u6765\uff0c\u4f60\u6709\u672c\u4e8b\uff0c\u5c3d\u7ba1\u53d6\u53bb\uff01\u201d \u3000\u3000\u4e24\u4eba\u8d8a\u8bf4\u8d8a\u50f5\uff0c\u4fe8\u5982\u7bad\u5728\u5f26\u4e0a\uff0c\u52bf\u5c06\u5373\u53d1\u3002\u94c1\u98de\u9f99\u54c8\u54c8\u4e00\u7b11\uff0c\u5927\u6b65\u8d70\u6765\uff0c\u7b11\u58f0\u4e0d\u5927\uff0c\u5ea7 \u4e0a\u53c1\u4eba\u90fd\u89c9\u9707\u8033\u523a\u5fc3\uff0c\u5413\u4e86\u4e00\u8df3\u3002\u6731\u5b9d\u693f\u548c\u90a3\u4e2a\u59d3\u5510\u7684\u5c11\u5e74\u540c\u58f0\u53eb\u9053\uff1a\u201c\u4f60\u662f\u90a3\u6761\u7ebf\u4e0a\u7684\u670b \u53cb\uff1f\u8bf7\u7559\u4e07\u513f\uff01\u201d\u4e24\u65b9\u90fd\u4ee5\u4e3a\u94c1\u98de\u9f99\u662f\u7ed9\u5bf9\u65b9\u52a9\u62f3\u7684\u4eba\u3002 \u3000\u3000\u94c1\u98de\u9f99\u5927\u6b65\u8d70\u5230\u684c\u524d\uff0c\u7aef\u4e86\u4e00\u5f20\u51f3\u5b50\uff0c\u91d1\u5200\u5927\u9a6c\u7684\u5750\u4e86\u4e0b\u6765\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u6731\u5be8\u4e3b\u5427 \u4e86\u5e78\u4f1a\uff0c\u5e78\u4f1a\uff01\u9063\u4f4d\u662f\u5bb6\u74a7\u5144\u5427\uff1f\u5e74\u5c11\u82f1\u96c4\uff0c\u6211\u8001\u592b\u51e0\u4e4e\u4e0d\u8ba4\u8bc6\u4e86\u3002\u8fd9\u4f4d\u670b\u53cb\u5462\uff1f\u8001\u592b\u773c \u62d9\uff0c\u8fd8\u8981\u8bf7\u6559\u59d3\u540d\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6765\u53cc\u65b9\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u6731\u5b9d\u693f\u5728\u7eff\u6797\u591a\u5e74\uff0c\u964c\u751f\u4eba\u8ba4\u8bc6\u4ed6\u5e76\u4e0d\u8be7\u5f02\uff0c\u53ef\u662f\u542c\u94c1\u98de\u9f99\u79f0 \u5bf9\u65b9\u4e3a\u201c\u5bb6\u74a7\u5144\u201d\uff0c\u663e\u7136\u662f\u76f8\u719f\u7684\u4eba\uff0c\u8fd9\u53ef\u4e0d\u80fd\u4e0d\u5c0f\u5fc3\u5728\u610f\uff0c\u5fc3\u9053\uff1a\u201c\u8bf4\u8fc7\u53cc\u65b9\u4e0d\u53e6\u7ea6\u4eba\u52a9 \u638c\uff0c\u4ed6\u5374\u9080\u4e86\u6a2a\u624b\u6765\uff0c\u4ee5\u5510\u5bb6\u7684\u58f0\u540d\uff0c\u5c45\u7136\u5e72\u8fd9\u79cd\u4e8b\uff0c\u7b49\u4e0b\u6211\u4e14\u7528\u8bf4\u8bdd\u538b\u7740\u4ed6\u3002\u201d \u3000\u3000\u90a3\u5510\u5bb6\u74a7\u66f4\u662f\u5403\u60ca\u3002\u539f\u6765\u4ed6\u4eec\u5510\u5bb6\u4e16\u5c45\u4e07\u53bf\uff0c\u4ee5\u6697\u5668\u4e4b\u7cbe\uff0c\u79f0\u96c4\u6b66\u6797\u3002\u5510\u5bb6\u74a7\u4eca\u5e74\u624d\u4e8c \u5341\u5c81\uff0c\u8fd8\u662f\u7b2c\u4e00\u6b21\u5949\u7236\u4eb2\u4e4b\u547d\u51fa\u6765\u529e\u4e8b\uff0c\u60f3\u4e0d\u900f\u94c1\u98de\u9f99\u4f55\u4ee5\u4e00\u89c1\u9762\u5c31\u80fd\u8bf4\u51fa\u4ed6\u7684\u540d\u5b57\u3002 \u3000\u3000\u5510\u5bb6\u74a7\u7684\u90a3\u4f4d\u670b\u53cb\u7ad9\u4e86\u8d77\u6765\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u5c0f\u59d3\u675c\u8d31\u53f7\u660e\u5fe0\uff0c\u4e0d\u77e5\u8001\u5148\u751f\u6709\u4f55\u6307\u6559\uff1f\u201d \u4ed6\u597d\u50cf\u7ecf\u8fc7\u4e16\u9762\uff0c\u6001\u5ea6\u6bd4\u5510\u5bb6\u74a7\u9547\u9759\u5f97\u591a\u3002 \u3000\u3000\u94c1\u98de\u9f99\u9053\uff1a\u201c\u51a4\u5bb6\u5b9c\u89e3\u4e0d\u5b9c\u7ed3\uff0c\u8001\u592b\u4e0d\u63e3\u5192\u6627\uff0c\u60f3\u8bf7\u4e24\u5bb6\u559d\u4e00\u676f\u8336\u3002\u201d\u63d0\u8d77\u8336\u58f6\uff0c\u4fbf\u5f85 \u659f\u4e0b\u3002\u6731\u5b9d\u693f\u548c\u5510\u5bb6\u74a7\u90fd\u9053\uff1a\u201c\u4e14\u6162\uff01\u201d\u539f\u6765\u6c5f\u6e56\u4e0a\u5403\u8bb2\u8336\u7684\u89c4\u77e9\uff0c\u82e5\u5403\u4e86\u8c03\u89e3\u4eba\u6240\u659f\u7684 \u8336\uff0c\u90a3\u4fbf\u662f\u613f\u610f\u548c\u597d\u4e86\u3002\u73b0\u5728\u53cc\u65b9\u90fd\u4e0d\u8ba4\u8bc6\u94c1\u98de\u9f99\uff0c\u90a3\u80fd\u51ed\u4ed6\u4e00\u8bed\u91ca\u5acc\u3002 \u3000\u3000\u94c1\u98de\u9f99\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e00\u676f\u8336\u5927\u5bb6\u90fd\u4e0d\u80af\u8d4f\u9762\u5417\uff1f\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u8336\u5df2\u659f\u4e0b\uff0c\u90a3\u5ba2\u5e97\u6240\u7528 \u7684\u8336\u676f\uff0c\u662f\u7528\u9ec4\u6768\u6728\u6316\u7a7a\u505a\u7684\uff0c\u6709\u5982\u7897\u5927\uff0c\u751a\u4e3a\u575a\u5b9e\u3002\u94c1\u98de\u9f99\u968f\u8bf4\u968f\u659f\uff0c\u70ed\u8336\u5165\u676f\uff0c\u53ea\u542c\u5f97 \u201c\u903c\u535c\u201d\u58f0\u54cd\uff0c\u6728\u676f\u987f\u65f6\u70b8\u5f00\uff0c\u8fde\u659f\u53c1\u676f\uff0c\u53c1\u4e2a\u676f\u5b50\u90fd\u788e\u88c2\u4e86\uff0c\u70ed\u8336\u6cfb\u6ee1\u684c\u9762\uff01\u8fd9\u4e00\u6765\u6731\u5b9d \u693f\u548c\u5510\u5bb6\u74a7\u90fd\u5927\u4e3a\u5403\u60ca\uff0c\u8981\u77e5\u82e5\u51ed\u638c\u529b\u634f\u788e\u6728\u676f\u5df2\u662f\u96be\u80fd\uff0c\u66f4\u4f55\u51b5\u7528\u70ed\u8336\u7684\u52b2\u5ea6\u5c31\u80fd\u5c06\u6728\u676f \u70b8\u5f00\uff1f\u8fd9\u79cd\u529f\u592b\u4ed6\u4eec\u90fd\u662f\u89c1\u6240\u672a\u89c1\uff0c\u95fb\u6240\u672a\u95fb\uff0c\u987f\u65f6\u7ed9\u94c1\u98de\u9f99\u7684\u5a01\u52bf\u6151\u4f4f\uff01 \u3000\u3000\u94c1\u98de\u9f99\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u4eec\u4e0d\u613f\u5403\u8336\uff0c\u8fd9\u8336\u4e5f\u5403\u4e0d\u6210\u5566\u3002\u5e97\u5bb6\u4f60\u7684\u676f\u5b50\u662f\u4ec0\u9ebd\u505a\u7684\uff0c\u600e \u9ebd\u5982\u6b64\u4e0d\u582a\uff0c\u5feb\u8fc7\u6765\u63e9\u51c0\u684c\u5b50\uff01\u201d \u3000\u3000\u638c\u67dc\u7684\u5728\u65c1\u770b\u5f97\u53c8\u60ca\u53c8\u559c\uff0c\u5f13\u8170\u9053\uff1a\u201c\u662f\uff01\u201d\u62ff\u4e86\u684c\u5e03\u6765\u62b9\u3002\u94c1\u98de\u9f99\u9053\uff1a\u201c\u597d\uff0c\u6362\u8fc7\u676f \u5b50\uff0c\u6211\u8fd8\u8981\u8bf7\u8bf8\u4f4d\u8d4f\u9762\u3002\u201d \u3000\u3000\u6731\u5b9d\u6869\u548c\u5510\u5bb6\u74a7\u540c\u58f0\u8bf4\u9053\uff1a\u201c\u8001\u82f1\u96c4\u8bf7\u542c\u6211\u4e00\u8a00\u3002\u201d\u94c1\u98de\u9f99\u6307\u7740\u5510\u5bb6\u74a7\u9053\uff1a\u201c\u4f60\u5148 \u8bf4\uff01\u201d \u3000\u3000\u5510\u5bb6\u74a7\u6ee1\u9762\u901a\u7ea2\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u675c\u5144\u662f\u6211\u5bb6\u7684\u670b\u53cb\uff0c\u4ed6\u5e26\u6709\u4e24\u4ef6\u5b9d\u7269\uff0c\u6731\u5be8\u4e3b\u52ab\u4e86\u3002\u5bb6 \u7236\u9063\u6211\u6765\u5411\u6731\u5be8\u4e3b\u6c42\u60c5\uff0c\u8bf7\u4ed6\u6168\u4e88\u53d1\u8fd8\u3002\u201d\u94c1\u98de\u9f99\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5514\uff0c\u6c5f\u6e56\u4e0a\u7684\u4e49\u6c14\u662f\u65e0\u4ef7\u4e4b \u5b9d\uff0c\u90a3\u4e24\u4ef6\u5b9d\u7269\u662f\u4ec0\u9ebd\u4e1c\u897f\uff0c\u6731\u5be8\u4e3b\u4f60\u8bf4\uff0c\u4f60\u662f\u4e0d\u662f\u820d\u4e0d\u5f97\u653e\u624b\u3002\u201d \u3000\u3000\u6731\u5b9d\u693f\u4e5f\u6da8\u7ea2\u4e86\u8138\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u675c\u5144\u662f\u897f\u5de1\u629a\u9648\u5947\u745c\u7684\u5e55\u5ba2\uff0c\u4ed6\u5e26\u4e86\u4e00\u679d\u5343\u5e74\u9996 \u4e4c\uff0c\u4e00\u4ef6\u767d\u72d0\u88d8\u5b50\uff0c\u8981\u4e0a\u4eac\u9001\u7ed9\u9b4f\u5fe0\u8d24\uff0c\u8fd9\u4e24\u4ef6\u4e1c\u897f\u4e0e\u5176\u7ed9\u9b4f\u5fe0\u8d24\u4e0d\u5982\u7ed9\u6211\uff0c\u8001\u82f1\u96c4\u4f60\u82e5\u8981 \u4e5f\u6210\u3002\u6211\u4e0d\u662f\u89ca\u89ce\u5b9d\u7269\uff0c\u5c31\u662f\u4e0d\u60f3\u4fbf\u5b9c\u5978\u9609\u3002\u201d \u3000\u3000\u94c1\u98de\u9f99\u7709\u5934\u4e00\u76b1\uff0c\u95ee\u5510\u5bb6\u74a7\u9053\uff1a\u201c\u675c\u5144\u7684\u793c\u7269\u662f\u9001\u7ed9\u8c01\u7684\uff0c\u4e8b\u5148\u4f60\u77e5\u9053\u5417\uff1f\u201d\u5510\u5bb6\u74a7 \u9053\uff1a\u201c\u4ed6\u65e9\u4e0e\u5bb6\u7236\u8bf4\u8fc7\u3002\u201d\u5510\u5bb6\u74a7\u7684\u7236\u4eb2\u5510\u9752\u5ddd\uff0c\u5a01\u9707\u5ddd\u897f\uff0c\u548c\u94c1\u98de\u9f99\u751a\u6709\u4ea4\u60c5\uff0c\u5341\u591a\u5e74\u524d \u94c1\u98de\u9f99\u8fd8\u5728\u4ed6\u5bb6\u4f4f\u8fc7\u53c1\u6708\uff0c\u6df1\u77e5\u5510\u9752\u5ddd\u4e3a\u4eba\uff0c\u5fc3\u9053\uff1a\u201c\u5510\u8001\u5927\u7edd\u4e0d\u4f1a\u90a3\u6837\u7cca\u6d82\uff0c\u65e2\u7136\u4e8b\u5148\u4e0e \u4ed6\u8bf4\u8fc7\uff0c\u800c\u4ed6\u53c8\u613f\u9063\u513f\u5b50\u6765\u4fdd\uff0c\u5176\u4e2d\u5b9a\u6709\u522b\u60c5\u3002\u6211\u4e14\u7ec6\u7ec6\u95ee\u660e\uff0c\u518d\u4f5c\u533a\u5904\u3002\u201d \u3000\u3000\u90a3\u675c\u660e\u5fe0\u4e5f\u7ad9\u4e86\u8d77\u6765\uff0c\u53cc\u624b\u636e\u684c\uff0c\u521a\u8bf4\u5f97\u4e00\u53e5\u201c\u8001\u82f1\u96c4\u8bf7\u542c\u6211\u8bf4\u8bdd\u2026\u2026\u201d\u5916\u9762\u4e00\u9635\u602a \u7b11\uff0c\u95e8\u5f00\u5904\u4e24\u4e2a\u4eba\u8d70\u4e86\u8fdb\u6765\uff0c\u8fd9\u4e24\u4eba\u4e00\u6a21\u4e00\u6837\uff0c\u90fd\u662f\u4e00\u5934\u4e71\u53d1\uff0c\u53c8\u9ad8\u53c8\u7626\uff0c\u9762\u65e0\u8840\u8272\uff0c\u53c1\u5206 \u50cf\u4eba\uff0c\u4e03\u5206\u50cf\u9b3c\uff0c\u5c31\u5982\u521a\u521a\u4ece\u5893\u91cc\u8d70\u51fa\u6765\u7684\u50f5\uff01 \u3000\u3000\u6731\u5b9d\u693f\u8df3\u4e86\u8d77\u6765\uff0c\u53eb\u9053\uff1a\u201c\u795e\u8001\u5927\uff0c\u795e\u8001\u4e8c\uff0c\u4f60\u4eec\u6765\u505a\u4ec0\u9ebd\uff1f\u201d\u94c1\u98de\u9f99\u5fc3\u9053\uff1a\u201c\u539f\u6765\u662f \u795e\u5bb6\u5144\u5f1f\u3002\u4e45\u95fb\u5f97\u4ed6\u4eec\u6b66\u529f\u602a\u5f02\uff0c\u884c\u4e8b\u8352\u8c2c\uff0c\u4e0d\u60f3\u4eca\u665a\u76f8\u9022\u3002\u201d\u8fd9\u795e\u5bb6\u5144\u5f1f\uff0c\u8001\u5927\u53eb\u795e\u5927 \u5143\uff0c\u8001\u4e8c\u53eb\u795e\u4e00\u5143\uff0c\u662f\u5317\u7eff\u6797\u4e2d\u54cd\u5f53\u5f53\u7684\u89d2\u8272\u3002\u5e73\u751f\u4e0d\u80af\u670d\u4eba\u3002\u53c1\u5e74\u524d\u738b\u5609\u80e4\u6218\u6b7b\u672a\u4e45\uff0c\u9ad8 \u8fce\u7965\u542c\u674e\u81ea\u6210\u7684\u7b56\u5212\uff0c\u5728\u7c73\u8102\u53ec\u96c6\u7eff\u6797\u53c1\u5341\u516d\u8def\u9996\u9886\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u80af\u8d74\u4f1a\u3002\u6d41\u7a9c\u5230\u56db\u5ddd\u4e4b\u5f8c\uff0c \u548c\u5f20\u732e\u5fe0\u6c14\u5473\u76f8\u6295\uff0c\u8054\u6210\u4e00\u6c14\uff0c\u53d7\u5f20\u732e\u5fe0\u5c01\u4e3a\u4e00\u5b57\u5e76\u80a9\u738b\u3002 \u3000\u3000\u6731\u5b9d\u693f\u5728\u7eff\u6797\u4e2d\u7684\u5730\u4f4d\uff0c\u6bd4\u4e8c\u795e\u5dee\u5f97\u5f88\u8fdc\uff0c\u53c8\u77e5\u4ed6\u4eec\u6bd2\u8fa3\uff0c\u4e0d\u7981\u6050\u60e7\u3002\u795e\u4e00\u5143\u677f\u7740\u602a \u9762\uff0c\u51b7\u68ee\u68ee\u7b11\u9053\uff1a\u201c\u542c\u8bf4\u4f60\u5f97\u4e86\u4e24\u4ef6\u597d\u4e1c\u897f\uff0c\u5feb\u4ea4\u51fa\u6765\uff0c\u516b\u5927\u738b\u8981\uff01\u201d\u201c\u516b\u5927\u738b\u201d\u662f\u5f20\u732e\u5fe0 \u7684\u201c\u532a\u53f7\u201d\uff0c\u5f20\u732e\u5fe0\u4e0e\u674e\u81ea\u6210\u4e0d\u540c\uff0c\u4ed6\u65e2\u8d2a\u8d22\u8d27\uff0c\u91d1\u94f6\u73e0\u5b9d\uff0c\u591a\u5c11\u90fd\u8981\uff0c\u53c8\u55dc\u6740\u4eba\uff0c\u6b63\u662f\u7eff \u6797\u4e2d\u4e00\u4e2a\u6df7\u4e16\u9b54\u738b\u3002 \u3000\u3000\u6731\u5b9d\u693f\u53d8\u4e86\u9762\u8272\uff0c\u4ea4\u51fa\u6765\u5fc3\u6709\u4e0d\u7518\uff0c\u4e0d\u4ea4\u53c8\u4e3a\u52bf\u6240\u80c1\uff0c\u6b63\u81ea\u59d4\u51b3\u4e0d\u4e0b\uff0c\u795e\u5927\u5143\u9053\uff1a\u201c\u4f60 \u4e0d\u4ea4\u6211\u5c31\u81ea\u53d6\u5566\uff01\u201d\u4e5f\u4e0d\u89c1\u4ed6\u600e\u6837\u4f5c\u52bf\uff0c\u4e00\u4e0b\u5b50\u5c31\u5230\u4e86\u6731\u5b9d\u693f\u773c\u524d\uff0c\u5c06\u4ed6\u8170\u95f4\u6240\u7cfb\u7684\u5305\u88f9\u62ff \u53bb\uff0c\u6731\u5b9d\u693f\u9192\u89c9\u4e4b\u65f6\uff0c\u53ea\u89c1\u795e\u5927\u5143\u7684\u602a\u624b\u5df2\u88ad\u5230\u80f8\u524d\uff01 \u3000\u3000\u6731\u5b9d\u6869\u5413\u5f97\u614c\u4e86\uff0c\u817e\u5730\u6251\u5230\u5730\u4e0a\uff0c\u5411\u5f8c\u4e00\u7ffb\uff0c\u6eda\u4e86\u5f00\u53bb\uff0c\u5e78\u4ed6\u95ea\u907f\u5f97\u5feb\uff0c\u6ca1\u7ed9\u795e\u5927\u5143\u5288 \u4e2d\u3002\u5510\u5bb6\u74a7\u675c\u660e\u5fe0\u89c1\u72b6\u5927\u60ca\uff0c\u53cc\u53cc\u8df3\u8fc7\u684c\u5b50\uff0c\u6251\u6765\u62a2\u90a3\u5305\u88f9\uff0c\u94c1\u98de\u9f99\u5fc3\u9053\uff1a\u201c\u8fd9\u53ef\u8981\u7cdf\u3002\u201d \u53ea\u542c\u5f97\u4e24\u58f0\u60e8\u53eb\uff0c\u5510\u5bb6\u74a7\u548c\u675c\u660e\u4e03\u90fd\u7ed9\u6454\u5230\u5899\u6839\uff0c\u795e\u5927\u5143\u51fa\u624b\u5982\u7535\uff0c\u638c\u4f24\u4e86\u675c\u660e\u5fe0\uff0c\u53c8\u70b9\u4e86 \u5510\u5bb6\u74a7\u7684\u201c\u5de8\u9aa8\u7a74\u201d\u3002 \u3000\u3000\u795e\u5927\u5143\u54c8\u54c8\u5927\u7b11\uff0c\u643a\u4e86\u5305\u88b1\uff0c\u626c\u957f\u800c\u53bb\uff0c\u94c1\u98de\u9f99\u53eb\u9053\uff1a\u201c\u5582\uff0c\u4e14\u6162\u8d70\uff01\u201d\u8eab\u5f62\u4e00\u8d77\uff0c\u98de \u8eab\u62e6\u5728\u95e8\u524d\u3002\u795e\u5927\u5143\u6012\u9053\uff1a\u201c\u8001\u5339\u592b\uff0c\u4f60\u6562\u62e6\u6211\uff01\u201d\u4e00\u638c\u5f80\u94c1\u98de\u9f99\u5934\u9876\u76f4\u5288\u4e0b\u53bb\uff01 \u3000\u3000\u94c1\u98de\u9f99\u80a9\u5934\u4e00\u7f29\uff0c\u795e\u5927\u5143\u638c\u52bf\u8fc5\u6377\u65e0\u4f26\uff0c\u5288\u4ed6\u4e0d\u4e2d\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u542c \u5f97\u94c1\u98de\u9f99\u5927\u543c\u4e00\u58f0\uff0c\u51fa\u624b\u53cd\u51fb\uff0c\u795e\u5927\u5143\u5ffd\u89c9\u4e00\u80a1\u52b2\u98ce\uff0c\u5411\u8170\u95f4\u51fb\u5230\uff0c\u53cd\u624b\u5f80\u5916\u4e00\u52fe\uff0c\u53cc\u81c2\u76f8 \u4ea4\uff0c\u7adf\u7ed9\u94c1\u98de\u9f99\u7684\u5f3a\u529b\u8feb\u5f97\u659c\u649e\u51fa\u53bb\u3002\u795e\u4e00\u5143\u5927\u5403\u4e00\u60ca\uff0c\u53cc\u638c\u9f50\u98de\uff0c\u63a9\u62a4\u5144\u957f\uff0c\u94c1\u98de\u9f99\u53c8\u662f \u4e00\u58f0\u5927\u543c\uff0c\u53cd\u624b\u4e00\u638c\uff0c\u5288\u654c\u80a9\u5934\uff0c\u53cc\u638c\u672a\u4ea4\uff0c\u795e\u5927\u5143\u53cd\u8eab\u518d\u6251\uff0c\u94c1\u98de\u9f99\u4e00\u4e2a\u53d8\u62db\uff0c\u53f3\u638c\u62d2 \u5f1f\uff0c\u5de6\u62f3\u51fb\u5144\uff0c\u53c1\u4eba\u6362\u4e86\u4e00\u62db\u7acb\u523b\u7531\u5408\u800c\u5206\uff0c\u5404\u81ea\u5c01\u95ed\u95e8\u6237\u3002 \u3000\u3000\u94c1\u98de\u9f99\u867d\u7136\u7528\u638c\u529b\u628a\u795e\u5927\u5143\u9707\u9000\uff0c\u80a9\u5934\u4e5f\u662f\u8fa3\u8fa3\u4f5c\u75db\u3002\u5fc3\u9053\uff1a\u8fd9\u4e24\u5144\u5f1f\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c \u602a\u4e0d\u5f97\u5982\u6b64\u7316\u72c2\uff01\u795e\u5bb6\u5144\u5f1f\u5706\u7741\u602a\u76ee\uff0c\u4f0f\u8eab\u4f5c\u52bf\uff0c\u84e6\u7136\u540c\u58f0\u602a\u53eb\uff0c\u653b\u52bf\u9aa4\u53d1\uff0c\u94c1\u98de\u9f99\u5de6\u638c\u6a2a \u5288\uff0c\u53f3\u817f\u76f4\u8e22\uff0c\u628a\u4e24\u5144\u5f1f\u7684\u62db\u6570\u540c\u65f6\u7834\u5f00\uff0c\u795e\u5927\u5143\u5fc3\u5934\u706b\u8d77\uff0c\u624b\u638c\u53d8\u5288\u4e3a\u524a\uff0c\u968f\u52bf\u626b\u6765\uff0c\u795e \u4e00\u5143\u4e5f\u626c\u62f3\u5288\u51fb\uff0c\u94c1\u98de\u9f99\u53c8\u662f\u4e00\u58f0\u5de8\u559d\uff0c\u62f3\u638c\u9f50\u51fa\uff0c\u795e\u5bb6\u5144\u5f1f\u867d\u7136\u6709\u4e00\u8eab\u6a2a\u7ec3\u7684\u529f\u592b\uff0c\u53ef\u4e5f \u4e0d\u6562\u6321\u8fd9\u91d1\u521a\u731b\u6251\u3002\u4e24\u5144\u5f1f\u8eab\u5b50\u9661\u7136\u62d4\u8d77\uff0c\u8dc3\u8fc7\u684c\u5b50\uff0c\u94c1\u98de\u9f99\u6a2a\u817f\u4e00\u626b\uff0c\u90a3\u5f20\u684c\u5b50\u7ed9\u8e22\u5f97\u98de \u5230\u5c4b\u9876\uff0c\u8033\u9686\u4e00\u58f0\u9707\u7834\u5c4b\u74e6\uff0c\u684c\u88c2\u74e6\u98de\uff0c\u74e6\u843d\u5c4b\u4e2d\uff0c\u684c\u98de\u5c4b\u5916\uff0c\u6731\u5b9d\u693f\u95ea\u5230\u5899\u89d2\uff0c\u795e\u5bb6\u5144\u5f1f \u8eab\u6cd5\u751a\u5feb\uff0c\u94c1\u98de\u9f99\u8fd9\u4e00\u817f\u626b\u4ed6\u4eec\u4e0d\u7740\uff0c\u53cc\u62f3\u4e00\u7acb\uff0c\u4e24\u5144\u5f1f\u53c8\u5df2\u6251\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u756a\u6597\u5f97\u66f4\u662f\u60ca\u4eba\uff01\u795e\u5bb6\u4e24\u5144\u5f1f\u4e00\u5de6\u4e00\u53f3\uff0c\u5939\u51fb\u5f3a\u654c\uff0c\u548c\u94c1\u98de\u9f99\u5bf9\u62a2\u653b\u52bf\u3002\u6bcf\u51fa\u4e00 \u62f3\uff0c\u9aa8\u8282\u4fbf\u683c\u683c\u4f5c\u54cd\uff0c\u94c1\u98de\u9f99\u77e5\u9053\u4ed6\u4eec\u5916\u5bb6\u529f\u592b\u5df2\u7ec3\u81f3\u767b\u5cf0\u9020\u6781\uff0c\u4e5f\u4e0d\u6562\u6020\u6162\uff0c\u6309\u7740\u4e94\u884c\u516b \u5366\u65b9\u4f4d\uff0c\u521a\u67d4\u5e76\u8fdb\uff0c\u653b\u5b88\u517c\u65bd\u3002\u6253\u4e86\u4e00\u9635\uff0c\u795e\u4e00\u5143\u5356\u4e2a\u7834\u7efd\uff0c\u94c1\u98de\u9f99\u5fc3\u9053\uff1a\u201c\u4f60\u8fd9\u79cd\u8bf1\u654c\u4e4b \u6280\uff0c\u5c82\u80fd\u7792\u6211\uff1f\u201d\u5c06\u8ba1\u5c31\u8ba1\uff0c\u4ece\u201c\u826e\u201d\u4f4d\u547c\u7684\u4e00\u638c\u5288\u51fa\uff0c\u8fc5\u5373\u8df3\u5230\u201c\u79bb\u201d\u65b9\u6070\u6070\u62a2\u4eba\u4e86\u7a7a \u6863\uff0c\u8d81\u7740\u795e\u5927\u5143\u672a\u66fe\u8865\u4e0a\uff0c\u5de6\u638c\u60ca\u96f7\u9a87\u7535\u822c\u5411\u795e\u4e00\u5143\u624b\u8155\u5207\u4e0b\u3002\u94c1\u98de\u9f99\u6240\u8d70\u7684\u65b9\u4f4d\u5999\u5230\u6beb \u5dc5\uff0c\u672c\u6765\u770b\u51c6\u4e86\u795e\u4e00\u5143\u4e0d\u80fd\u53cd\u51fb\uff0c\u90a3\u77e5\u795e\u4e00\u5143\u624b\u81c2\u4e00\u6325\uff0c\u9aa8\u8282\u683c\u683c\u4f5c\u54cd\uff0c\u624b\u81c2\u7adf\u7136\u66b4\u957f\u4e24 \u5bf8\uff0c\u53d8\u638c\u4e3a\u6307\uff0c\u53cd\u70b9\u94c1\u98de\u9f99\u7684\u201c\u81c2\u5112\u7a74\u201d\uff0c\u9ad8\u624b\u5bf9\u654c\uff0c\u662f\u6beb\u4e4b\u5dee\uff0c\u94c1\u98de\u9f99\u6599\u654c\u4e0d\u53ca\uff01\u9aa4\u611f\u624b \u81c2\u4e00\uff0c\u6025\u5c06\u638c\u65b9\u5916\u5410\uff0c\u817e\u8eab\u4e00\u95ea\uff0c\u582a\u582a\u907f\u8fc7\u795e\u4e00\u5143\u7684\u653b\u88ad\uff0c\u53ea\u542c\u5f97\u795e\u4e00\u5143\u54c7\u54c7\u602a\u53eb\uff0c\u94c1\u98de\u9f99 \u6025\u5fd9\u8fd0\u6c14\u6d3b\u8840\uff0c\u795e\u5927\u5143\u5df2\u628a\u5f1f\u5f1f\u62c9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u94c1\u98de\u9f99\u8fd9\u4e00\u638c\u867d\u7136\u6253\u4e2d\u4e86\u795e\u4e00\u5143\uff0c\u4f46\u52b2\u529b\u53d1\u51fa\u5728\u7a74\u9053\u88ab\u70b9\u4e4b\u5f8c\uff0c\u638c\u529b\u5df3\u5f31\uff0c\u867d\u7136\u628a\u795e\u4e00 \u5143\u6253\u5f97\u75db\u4eba\u5fc3\u813e\uff0c\u4ed6\u7684\u624b\u8155\u603b\u7b97\u4fdd\u5168\u4e86\u3002\u795e\u5927\u5143\u9053\uff1a\u201c\u788d\u4e8b\u9ebd\uff01\u201d\u795e\u4e00\u5143\u6325\u62f3\u821e\u4e86\u4e00\u4e2a\u5f27 \u5f62\uff0c\u9053\uff1a\u201c\u65e0\u59a8\uff1f\u201d\u4e24\u5144\u5f1f\u6325\u62f3\u53c8\u4e0a\u3002 \u3000\u3000\u94c1\u98de\u9f99\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u8fd8\u7ec3\u8fc7\u6613\u7b4b\u7f29\u9aa8\u7684\u529f\u592b\uff01\u201d\u638c\u6cd5\u4e00\u53d8\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u76f4\u5982\u5de8\u65a7\u5f00 \u5c71\uff0c\u94c1 \u3000\u3000\u51ff\u77f3\uff0c\u795e\u5bb6\u5144\u5f1f\u89c1\u4ed6\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u5c45\u7136\u82e5\u65e0\u5176\u4e8b\uff0c\u8fd9\u4e00\u60ca\u66f4\u662f\u975e\u540c\u5c0f\u9119\uff0c\u867d\u7136\u7ec3\u6709\u602a\u5f02 \u7684\u201c\u4e03\u715e\u638c\u201d\u201c\u94c1\u72d0\u62f3\u201d\uff0c\u4e5f\u4e0d\u6562\u6b3a\u8eab\u8fdb\u903c\u3002 \u3000\u3000\u53c1\u4eba\u6253\u5f97\u96be\u5206\u96be\u89e3\uff0c\u4f46\u94c1\u98de\u9f99\u638c\u529b\u6c89\u96c4\uff0c\u4e24\u5144\u5f1f\u88ab\u4ed6\u638c\u529b\u9707\u6c64\uff0c\u8868\u9762\u8fd8\u4e0d\u89c9\u4ec0\u9ebd\uff0c\u547c\u5438 \u5df2\u662f\u6e10\u6765\u6e10\u4fc3\u3002\u6b63\u5728\u96be\u652f\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u5a07\u7b11\uff1a\u201c\u7239\uff0c\u8fd9\u4e24\u4eba\u8ba9\u7ed9\u6211\u5566\uff01\u6211\u53bb\u6253\u5c0f\u867e\uff0c\u4f60\u5374\u5728 \u8fd9\u91cc\u9493\u5927\u9c7c\uff0c\u8fd9\u4e0d\u516c\u5e73\uff0c\u6211\u7684\u624b\u75d2\u54af\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u54c8\u54c8\u4e00\u7b11\uff0c\u500f\u5730\u8df3\u51fa\u6838\u5fc3\uff0c\u9053\uff1a\u201c\u597d\uff0c\u5c31\u8ba9\u4f60\u6361\u4fbf\u5b9c\uff01\u201d\u795e\u5bb6\u5144\u5f1f\u9aa4\u611f\u538b\u529b\u4e00 \u677e\uff0c\u547c\u5438\u8212\u7545\uff0c\u7389\u7f57\u5239\u58f0\u5230\u4eba\u5230\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u53c8\u5df2\u62e6\u5728\u4ed6\u4eec\u9762\u524d\u3002\uff0e\u795e\u5927\u5143\u9053\uff1a\u201c\u4f60\u662f\u7389\u7f57 \u5239\u5417\uff1f\u201d\u7389\u7f57\u5239\u77a7\u4ed6\u4e00\u773c\uff0c\u76c8\u76c8\u7b11\u9053\uff1a\u201c\u77a7\u4f60\u4eec\u8fd9\u602a\u6a21\u6837\uff0c\u5b9a\u662f\u795e\u5bb6\u5144\u5f1f\u4e86\u3002\u201d\u6731\u5b9d\u693f\u5728\u5899 \u89d2\u53eb\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\u53eb\u4ed6\u4eec\u628a\u90a3\u5305\u88f9\u4ea4\u56de\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u60f3\u8d77\u674e\u81ea\u6210\u5bf9\u5979\u8bf4\u8fc7\u795e\u5bb6\u5144\u5f1f\u4e0d\u53c2\u52a0\u7c73\u8102\u5927\u4f1a\u4e4b\u4e8b\uff0c\u7b11\u9053\uff1a\u201c\u4ee5\u5f80\u4f60\u5728\u5317\uff0c\u6211\u5728 \u5357\uff0c\u5f7c\u6b64\u65e0\u6d89\u3002\u5982\u4eca\u4f60\u548c\u6211\u7684\u7239\u7239\u4f5c\u5bf9\uff0c\u6211\u53ef\u8981\u770b\u770b\u4f60\u4eec\u5144\u5f1f\u6709\u4ec0\u9ebd\u80fd\u4e3a\uff0c\u6562\u8fd9\u6837\u9a84\u72c2 \u5566\uff01\u201d\u5251\u5149\u4e00\u95ea\uff0c\u5237\u5237\u4e24\u5251\uff0c\u7adf\u7136\u5728\u5f39\u6307\u4e4b\u95f4\uff0c\u5206\u523a\u4e8c\u4eba\u3002 \u3000\u3000\u795e\u5bb6\u5144\u5f1f\u4e00\u5411\u6a2a\u86ee\uff0c\u4e0d\u6599\u7389\u7f57\u5239\u6bd4\u4ed6\u4eec\u66f4\u6a2a\uff0c\u4e00\u6253\u8bdd\u4fbf\u7acb\u5373\u52a8\u624b\uff0c\u4e24\u5144\u5f1f\u6c14\u5f97\u54c7\u54c7\u602a \u53eb\uff0c\u201c\u4e03\u715e\u638c\u201d\u201c\u98de\u72d0\u62f3\u201d\u90fd\u7528\u4e86\u51fa\u6765\uff0c\u7389\u7f57\u5239\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u4e00\u53e3\u6c14\u8fde\u653b\u4e86\u53c1\u5341\u591a \u62db\uff0c\u8fd9\u624d\u7b11\u9053\uff1a\u201c\u6709\u70b9\u529f\u592b\uff0c\u4f46\u4e5f\u8fd8\u4e0d\u80fd\u7b97\u662f\u4e00\u6d41\u811a\u8272\u3002\u5582\uff0c\u600e\u9ebd\u4f60\u4eec\u51ed\u8fd9\u70b9\u529f\u592b\u5c31\u6562\u79f0\u738b \u9053\u9738\uff01\u201d\u4e00\u9762\u5632\u7b11\uff0c\u4e00\u9762\u8fdb\u62db\uff0c\u628a\u795e\u5bb6\u4e24\u5144\u5f1f\u903c\u5f97\u56e2\u56e2\u4e71\u8f6c\u3002 \u3000\u3000\u5176\u5b9e\u7389\u7f57\u5239\u786e\u662f\u5360\u4e86\u4fbf\u5b9c\u3002\u672c\u6765\u4e24\u5144\u5f1f\u5408\u529b\u8fdb\u653b\uff0c\u7389\u7f57\u5239\u867d\u7136\u4e0d\u60e7\uff0c\u8981\u80dc\u4ed6\u4eec\u5374\u4e5f\u4e0d \u6613\uff0c\u4f46\u4ed6\u4eec\u5df2\u88ab\u94c1\u98de\u9f99\u6253\u6298\u4e86\u9510\u6c14\uff0c\u7b4b\u9aa8\u4e5f\u7ed9\u94c1\u98de\u9f99\u7684\u638c\u65b9\u9707\u5f97\u9690\u9690\u4f5c\u75db\uff0c\u56e0\u6b64\u518d\u6597\u7389\u7f57\u5239 \u4e4b\u65f6\uff0c\u66f4\u662f\u4e0d\u6d4e\uff0c\u4e00\u5f00\u9996\u5c31\u88ab\u7389\u7f57\u5239\u5360\u5c3d\u653b\u52bf\uff0c\u53c1\u5341\u62db\u8fc7\u5f8c\uff0c\u66f4\u662f\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u6beb\u65e0\u8fd8\u624b\u4e4b \u529b\u3002 \u3000\u3000\u94c1\u98de\u9f99\u9000\u4e0b\u4e4b\u5f8c\uff0c\u5c06\u5510\u5bb6\u74a7\u7684\u7a74\u9053\u89e3\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u56de\u53bb\u62dc\u4e0a\u4ee4\u5c0a\uff0c\u8bf4\u662f\u9f99\u95e8\u94c1\u98de\u9f99\u95ee \u5019\u3002\u201d\u5510\u5bb6\u74a7\u554a\u5440\u62dc\u5012\uff0c\u8bf4\u9053\uff1a\u201c\u539f\u6765\u662f\u94c1\u53d4\u53d4\uff0c\u602a\u4e0d\u5f97\u6709\u6b64\u529f\u529b\uff01\u5c0f\u4f84\u4eca\u665a\u51fa\u4e11\u7f62\u4e86\u3002\u201d \u94c1\u98de\u9f99\u9053\uff1a\u201c\u5e74\u8f7b\u4eba\u53d7\u4e00\u70b9\u632b\u6298\u7b97\u4e0d\u4e86\u4ec0\u9ebd\u3002\u201d\u518d\u770b\u675c\u660e\u5fe0\u7684\u638c\u4f24\uff0c\u53ea\u89c1\u80a9\u5934\u4e0a\u7d2b\u9ed1\u4e00\u7247\uff0c \u94c1\u98de\u9f99\u628a\u4e00\u9897\u836f\u4e38\u9001\u4eba\u4ed6\u7684\u53e3\u4e2d\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u795e\u5bb6\u5144\u5f1f\u8fd8\u7ec3\u6709\u6bd2\u7802\u638c\uff0c\u8fd9\u53ef\u8981\u4ed6\u4eec\u672c\u95e8\u89e3 \u836f\u3002\u201d \u3000\u3000\u5510\u5bb6\u74a7\u521d\u6b21\u51fa\u9053\uff0c\u4fbf\u5403\u5927\u4e8f\uff0c\u597d\u4e0d\u751f\u6c14\uff0c\u7ed9\u89e3\u4e86\u7a74\u9053\u4e4b\u5f8c\uff0c\u5f80\u6697\u5668\u56ca\u4e2d\u4e00\u63a2\uff0c\u7a81\u7136\u628a\u624b \u4e00\u626c\uff0c\u4e24\u4ef6\u5947\u5f62\u6697\u5668\uff0c\u5206\u5411\u795e\u5bb6\u4e24\u5144\u5f1f\u6253\u53bb\uff01 \u3000\u3000\u795e\u5bb6\u5144\u5f1f\u88ab\u7389\u7f57\u5239\u6740\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u84e6\u7136\u542c\u5f97\u545c\u545c\u602a\u53eb\uff0c\u95ea\u907f\u4e0d\u53ca\uff0c\u4e24\u5144\u5f1f\u90fd\u4e2d\u4e86\u5510\u5bb6\u7684 \u6bd2\u84ba\u85dc\u3002 \u3000\u3000\u5510\u5bb6\u6697\u5668\uff0c\u9a70\u540d\u6c5f\u6e56\uff0c\u6bd2\u84ba\u85dc\u5c24\u5176\u5389\u5bb3\uff0c\u7aef\u7684\u662f\u89c1\u8840\u5c01\u5589\u3002\u795e\u5bb6\u4e24\u5144\u5f1f\u8dd1\u4e86\u4e24\u6b65\uff0c\u9762\u8272 \u5927\u53d8\uff0c\u7a81\u7136\u53ea\u53cc\u7eb5\u8d77\u5411\u5510\u5bb6\u74a7\u6293\u53bb\uff0c\u94c1\u98de\u9f99\u4e00\u62db\u201c\u94c1\u95e8\u5220\u201d\uff0c\u4e00\u526a\u4e00\u5220\uff0c\u4e24\u5144\u5f1f\u5012\u6eda\u5730\u4e0a\uff0c \u7834\u53e3\u5927\u9a82\uff0c\u8d8a\u9a82\u8d8a\u5f31\u3002 \u3000\u3000\u5510\u5bb6\u74a7\u751a\u4e3a\u5f97\u610f\uff0c\u56de\u9a82\u9053\uff1a\u201c\u4f60\u4eec\u51fa\u624b\u4f24\u4eba\uff0c\u5982\u4eca\u4e5f\u53eb\u4f60\u4eec\u77e5\u9053\u5c11\u7237\u7684\u5389\u5bb3\uff01\u201d\u62ac\u5934\u4e00 \u770b\uff0c\u5ffd\u89c1\u7389\u7f57\u5239\u6740\u6c14\u6ee1\u9762\uff0c\u51b7\u51b0\u51b0\u7684\u7ad9\u5728\u81ea\u5df1\u9762\u524d\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u597d\u6697\u5668\uff0c\u597d\u624b\u6cd5\uff01\u8c01\u8981\u4f60\u5e2e \u4e86\u5feb\u628a\u89e3\u836f\u62ff\u6765\uff01\u201d\u5510\u5bb6\u74a7\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u9119\uff0c\u9053\uff1a\u201c\u8fd9\uff0c\u8fd9\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u5fd9\u9053\uff1a\u201c\u88f3\u513f\uff0c\u8fd9\u4f4d\u662f\u5510\u8d24\u4f84\u3002\u201d\u62a2\u7740\u8fc7\u6765\uff0c\u50ac\u9053\uff1a\u201c\u628a\u89e3\u836f\u62ff\u51fa\u6765\u5427\u3002\u201d\u5510\u5bb6 \u74a7\u65e0\u5948\uff0c\u62ff\u51fa\u89e3\u836f\uff0c\u6c14\u547c\u547c\u7684\u9053\uff1a\u201c\u675c\u5144\u53d7\u4e86\u4ed6\u4eec\u7684\u6bd2\u722a\u5b50\u6293\u4f24\uff0c\u8fd9\u53c8\u600e\u9ebd\u8bf4\uff1f\u201d \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u6025\u4ec0\u9ebd\uff1f\u201d\u4e00\u628a\u5c06\u89e3\u836f\u62ff\u8fc7\uff0c\u629b\u7ed9\u795e\u5927\u5143\u9053\uff1a\u201c\u4f60\u4e5f\u628a\u89e3\u836f\u62ff\u6765\uff01\u201d \u3000\u3000\u795e\u5bb6\u5144\u5f1f\u9887\u611f\u610f\u5916\uff0c\u9a82\u58f0\u987f\u6b62\uff0c\u541e\u4e86\u89e3\u836f\uff0c\u679c\u89c1\u8212\u7545\uff0c\u4fbf\u4e5f\u628a\u89e3\u836f\u638f\u51fa\uff0c\u629b\u7ed9\u7389\u7f57\u5239\uff0c \u7389\u7f57\u5239\u559d\u9053\uff1a\u201c\u628a\u5305\u88b1\u7559\u4e0b\uff0c\u7acb\u523b\u7ed9\u6211\u6eda\uff01\u201d\u795e\u5927\u5143\u4e00\u58f0\u4e0d\u54cd\uff0c\u629b\u4e0b\u5305\u88b1\uff0c\u62c9\u8d77\u5f1f\u5f1f\uff0c\u8dd1\u51fa \u95e8\u5916\uff0c\u56de\u5934\u76ef\u4e86\u7389\u7f57\u5239\u4e00\u773c\uff0c\u6068\u6068\u8bf4\u9053\uff1a\u201c\u597d\u54c7\uff0c\u7389\u7f57\u5239\uff0c\u54b1\u4eec\u5f8c\u4f1a\u6709\u671f\uff01\u201d\u7389\u7f57\u5239\u4e00\u58f0\u957f \u7b11\uff0c\u624b\u6478\u5251\u67c4\uff0c\u795e\u5bb6\u5144\u5f1f\u5413\u5f97\u98de\u8dd1\uff0c\u518d\u4e5f\u4e0d\u6562\u53d1\u8bdd\u3002 \u3000\u3000\u6731\u5b9d\u693f\u5510\u5bb6\u74a7\u675c\u660e\u5fe0\u90fd\u6251\u53bb\u62a2\u90a3\u5305\u88b1\uff0c\u7389\u7f57\u5239\u811a\u5c16\u4e00\u70b9\uff0c\u8f7b\u8f7b\u628a\u90a3\u5305\u88b1\u8e0f\u7740\uff0c\u674f\u773c\u4e00 \u7741\uff0c\u6731\u5b9d\u693f\u8fde\u5fd9\u9000\u5f8c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5305\u88b1\u91cc\u6709\u5343\u5e74\u4f55\u9996\u4e4c\u4e0e\u767d\u72d0\u88d8\u5b50\uff0c\u4ed6\u4eec\u8981\u62ff\u53bb\u5b5d\u656c\u9b4f\u5fe0 \u8d24\uff0c\u662f\u6211\u628a\u5b83\u52ab\u4e86\uff0c\u60f3\u7559\u6765\u5b5d\u656c\u4f60\u8001\u3002\u4f60\u8001\u4eba\u5bb6\u8bf4\u4e00\u53e5\uff1a\u8fd9\u4e1c\u897f\u6211\u52ab\u5f97\u5bf9\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u662f\u9ebd\uff1f\u201d\u675c\u660e\u5fe0\u6602\u5934\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u6837\u4e1c\u897f\u662f\u60f3\u9001\u7ed9\u9b4f\u5fe0\u8d24\uff0c\u4f46\u6211\u662f\u8981\u62ff\u5b83 \u53bb\u6551\u4eba\u7684\u3002\u5de6\u90fd\u5fa1\u53f2\u5de6\u5149\u6597\u662f\u4ffa\u7684\u8205\u8205\uff0c\u4ed6\u548c\u6768\u6d9f\u7b49\u8054\u5408\u4e0a\u758f\uff0c\u7ed9\u9b4f\u5fe0\u8d24\u4e0b\u4e86\u5929\u7262\uff0c\u9648\u5de1\u629a \u8bfb\u4e86\u90b8\u6284\uff0c\u901a\u77e5\u6211\u8d76\u4e0a\u4eac\u90fd\uff0c\u8bbe\u6cd5\u8425\u6551\u3002\u6211\u65e2\u65e0\u6cd5\u4e0e\u5978\u9605\u76f8\u6297\uff0c\u8feb\u5f97\u5fcd\u8fb1\u6c42\u60c5\u3002\u5de6\u5149\u6597\u662f\u4e1c \u6797\u6b63\u4eba\uff0c\u5929\u4e0b\u5171\u77e5\uff0c\u6211\u6551\u4ed6\u53c8\u6709\u4f55\u4e0d\u5bf9\uff1f\u201d \u3000\u3000\u7389\u7f57\u5239\u6014\u4e86\u4e00\u6014\uff0c\u9053\uff1a\u201c\u597d\uff0c\u5305\u88b1\u7ed9\u4f60\u3002\u201d\u5bf9\u6731\u5b9d\u693f\u9053\uff1a\u201c\u7f57\u94c1\u81c2\u6551\u4e86\u6768\u6d9f\u7684\u9053\u5b64\uff0c\u6b63 \u5728\u627e\u4f60\uff0c\u4f60\u8d76\u56de\u53bb\u5427\u3002\u201d\u6731\u5b9d\u6869\u9053\uff1a\u201c\u4f60\u4eec\u4f55\u4e0d\u65e9\u8bf4\uff0c\u65e2\u7136\u662f\u4e3a\u4e86\u6551\u4eba\uff0c\u6211\u4e5f\u4e0d\u52ab\u5b83\u4e86\u3002\u201d \u62f1\u624b\u544a\u8f9e\uff0c\u8d76\u56de\u5c71\u5be8\u3002 \u3000\u3000\u675c\u660e\u5fe0\u4e0a\u524d\u53e9\u8c22\uff0c\u7389\u7f57\u5239\u773c\u73e0\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u7239\uff0c\u54b1\u4eec\u4e5f\u4e0a\u4eac\u77a7\u70ed\u95f9\u53bb\u3002\u201d\u94c1\u98de\u9f99\u5fc3\u60f3\uff1a \u6740\u5973\u513f\u7684\u6b63\u51f6\u91d1\u8001\u602a\u5df2\u88ab\u5cb3\u545c\u73c2\u6740\u4e86\uff0c\u8fd8\u6709\u4e24\u4e2a\u4ec7\u4eba\u4e00\u4e2a\u662f\u6155\u5bb9\u51b2\u4e00\u4e2a\u662f\u5e94\u4fee\u9633\u90fd\u5728\u5bab\u4e2d\u6267 \u5f79\uff0c\u4e0b\u624b\u867d\u96be\uff0c\u4f46\u4ed6\u4eec\u7ec8\u987b\u56de\u4eac\uff0c\u5728\u4eac\u57ce\u7b49\u5019\u673a\u4f1a\uff0c\u4e5f\u662f\u529e\u6cd5\u3002\u4fbf\u4e5f\u9053\u597d\u3002 \u3000\u3000\u5510\u5bb6\u74a7\u5c34\u5c2c\u4e4b\u6001\uff0c\u89c1\u65bc\u8f9e\u8272\uff0c\u5bf9\u675c\u660e\u601d\u62f1\u624b\u9053\uff1a\u201c\u4f60\u6709\u94c1\u53d4\u53d4\u62a4\u9001\u4e0a\u4eac\uff0c\u5c0f\u5f1f\u544a\u9000 \u4e86\u3002\u201d\u94c1\u98de\u9f99\u5c06\u4ed6\u9001\u51fa\u95e8\u5916\uff0c\u56de\u6765\u7b11\u9053\uff1a\u201c\u88f3\u513f\uff0c\u4ee5\u5f8c\u4e0d\u51c6\u4f60\u5413\u521d\u51fa\u9053\u7684\u96cf\u513f\uff01\u201d \u3000\u3000\u53c1\u4eba\u4e00\u8def\u540c\u884c\uff0c\u8def\u4e0a\u4ea4\u8c08\uff0c\u7389\u7f57\u5239\u624d\u77e5\u9053\u675c\u660e\u5fe0\u539f\u6765\u4e5f\u66fe\u5728\u718a\u7ecf\u7565\u5e55\u4e0b\u4f5c\u5e55\uff0c\u7ba1\u529e\u6587 \u4e66\uff0c\u4e5f\u8ba4\u8bc6\u5cb3\u545c\u73c2\u3002\u7389\u7f57\u5239\u4e0d\u7981\u8bf4\u9053\uff1a\u201c\u718a\u7ecf\u7565\u6b7b\u5f8c\uff0c\u5f8c\u7ee7\u65e0\u4eba\uff0c\u8fb9\u9632\u8d25\u574f\uff0c\u660e\u671d\u7684\u6c5f\u5c71\u6015 \u5feb\u8981\u5b8c\u4e86\u3002\u201d\u675c\u660e\u5fe0\u9053\uff1a\u201c\u4e0d\u7136\uff0c\u4e0d\u662f\u5f8c\u7ee7\u65e0\u4eba\uff0c\u662f\u6015\u671d\u5ef7\u4e0d\u4e88\u91cd\u7528\u3002\u201d\u7389\u7f57\u5239\u5fc3\u5ff5\u4e00\u52a8\uff0c \u95ee\u9053\uff1a\uff0e\u201c\u4f60\u770b\u8c01\u53ef\u7ee7\u627f\u718a\u7ecf\u7565\uff0c\u91cd\u9547\u8fb9\u5173\uff1f\u201d\u675c\u660e\u5fe0\u9053\uff1a\u201c\u8fbd\u4e1c\u519b\u4e2d\u7684\u98df\u4e8b\u8881\u5d07\u7115\u5c31\u662f\u5f53 \u4e16\u5947\u624d\uff01\u4ed6\u672c\u6765\u662f\u4e00\u4e2a\u4e03\u54c1\u53bf\u5b98\uff0c\u4e3a\u718a\u7ecf\u7565\u8d4f\u8bc6\uff0c\u4fdd\u4ed6\u5de1\u8fb9\uff0c\u5e7f\u5b81\u90a3\u5f79\uff0c\u718a\u7ecf\u7565\u88ab\u738b\u5316\u8d1f\u6240 \u7d2f\uff0c\u5927\u8d25\u5f03\u57ce\uff0c\u8881\u5d07\u7115\u5355\u9a91\u51fa\u5173\uff0c\u904d\u9605\u5f62\u52bf\uff0c\u56de\u6765\u8bf7\u5175\uff0c\u81ea\u987f\u5b88\u8fbd\u6cb3\u4ee5\u4e1c\uff0c\u53ef\u60dc\u90a3\u65f6\u718a\u7ecf\u7565 \u6709\u4e94\u5343\u90e8\u4f17\uff0c\u671d\u5ef7\u53c8\u4e0d\u80af\u6d3e\u5175\u3002\u5f8c\u6765\u5728\u5175\u8d25\u5c06\u9003\u4e4b\u9645\uff0c\u718a\u7ecf\u7565\u53eb\u4ed6\u53bb\u7ecf\u7406\u519b\u4e8b\uff0c\u5b89\u7f6e\u6e38\u6c11\uff0c \u767d\u5929\u654c\u519b\u51fa\u6ca1\uff0c\u65e0\u6cd5\u6d3b\u52a8\uff0c\u4ed6\u5c31\u5728\u665a\u4e0a\u6df1\u5165\u8346\u68d8\u8499\u8338\u864e\u8c79\u6f5c\u4f0f\u4e4b\u5730\uff0c\u8d70\u904d\u654c\u5f8c\u4e61\u6751\uff0c\u628a\u6e38\u6c11 \u767e\u59d3\u91cd\u7ec4\u8d77\u6765\u3002\u6240\u4ee5\u5f8c\u6765\u624d\u6709\u516b\u91cc\u94fa\u7684\u5c0f\u6377\uff0c\u624d\u6709\u5728\u5c71\u6d77\u5173\u5bf9\u5cd9\u4e4b\u52bf\uff0c\u8981\u4e0d\u7136\u6e05\u5175\u65e9\u4eba\u5173 \u4e86\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u5fc3\u9053\uff1a\u201c\u82e5\u7136\u771f\u6709\u5982\u6b64\u4e4b\u4eba\uff0c\u718a\u7ecf\u7565\u7684\u9057\u4e66\u5012\u53ef\u4ed8\u6258\u7ed9\u4ed6\u3002\u53ea\u662f\u4ed6\u8fdc\u5728\u5173\u5916\uff0c\u5982 \u4f55\u5bfb\u627e\uff1f\u201d \u3000\u3000\u53c1\u4eba\u6765\u5230\u4eac\u57ce\uff0c\u5df2\u662f\u6b63\u6708\u4e0b\u65ec\uff0c\u8fd9\u4e00\u65e5\u8fdb\u4e86\u57ce\u95e8\uff0c\u4fbf\u89c1\u8857\u9053\u4e71\u54c4\u54c4\u7684\uff0c\u6570\u5341\u540d\u4eac\u5b98\u62ac\u7740 \u9b4f\u5fe0\u8d24\u7684\u91d1\u8eab\u5851\u50cf\uff0c\u6253\u9523\u6253\u9f13\u5728\u5317\u4eac\u8857\u9053\u6e38\u884c\uff0c\u5e02\u6c11\u8fdc\u8fdc\u7684\u77a7\u70ed\u95f9\uff0c\u4f4e\u58f0\u553e\u9a82\u3002\u94c1\u98de\u9f99\u4e00 \u95ee\uff0c\u624d\u77e5\u662f\u7ed9\u9b4f\u5fe0\u8d24\u5efa\u201c\u751f\u7960\u201d\u3002 \u3000\u3000\u5176\u65f6\u662f\u5929\u6563\u56db\u5e74\uff0c\u9b4f\u5fe0\u8d24\u64cd\u7eb5\u671d\u7eb2\uff0c\u6743\u503e\u4e2d\u5916\uff0c\u6c11\u95f4\u7684\u7ae5\u8c23\u9053\uff1a\u201c\u59d4\u9b3c\u5f53\u671d\u7acb\uff0c\u8304\u82b1\u6ee1 \u5730\u7ea2\u3002\u201d\u201c\u59d4\u9b3c\u201d\u662f\u201c\u9b4f\u201d\u5b57\uff0c\u201c\u8304\u201d\u4e0e\u201c\u5ba2\u201d\u540c\u97f3\uff0c\u4ece\u8fd9\u7ae5\u8c23\uff0c\u4ea6\u53ef\u89c1\u5ba2\u9b4f\u52bf\u529b\u4e4b\u5927\u3002\u671d \u4e2d\u9601\u81e3\u9b4f\u5e7f\u5fb5\u8ba4\u662f\u4ed6\u7684\u4f84\u5b50\uff0c\u962e\u5927\u9488\u3001\u5d14\u5448\u79c0\u3001\u987e\u79c9\u8c26\u3001\u5085\u6a3e\u3001\u502a\u6587\u7115\u3001\u6768\u7ef4\u57a3\u7b49\u5927\u81e3\u4ff1\u62dc \u5fe0\u8d24\u4e3a\u7236\u5ba2\u6c0f\u4e3a\u6bcd\uff0c\u6d59\u6c5f\u5de1\u629a\u6f58\u6c5d\u6862\u5e76\u9996\u5148\u5021\u8bae\u4e3a\u9b4f\u5fe0\u8d24\u5efa\u7acb\u751f\u7960\uff0c\u7ee7\u4e4b\u5168\u56fd\u5404\u5730\u90fd\u7eb7\u7eb7\u5efa \u7acb\uff0c\u771f\u662f\u96c6\u5c3d\u4eba\u95f4\u65e0\u803b\u4e4b\u5927\u6210\uff0c\u6700\u5f8c\u5728\u5317\u4eac\u4e5f\u5efa\u8d77\u6765\u4e86\uff0c\u81ea\u79f0\u201c\u8bfb\u5b54\u5b50\u4e66\u201d\u7684\u76d1\u751f\u9646\u4e07\u9f84\u5e76 \u4e0a\u9882\u5fb7\u8868\u65e5\uff1a\u201c\u5b54\u5b50\u4f5c\u6625\u79cb\uff0c\u5382\u81e3\u4f5c\u201c\u8981\u5178\u201d\uff1a\u201c\u5382\u81e3\u5373\u9b4f\u5fe0\u8d24\u3002\u201d\u5b54\u5b50\u8bdb\u5c11\u6b63\u536f\uff0c\u5382\u81e3\u8bdb \u4e1c\u6797\u515a\u4eba\uff0c\u793c\u5b9c\u5e76\u5c0a\uff0c\u5c81\u7940\u5982\u5b54\u5b50\u3002\u201d\u8fd9\u4e9b\u8bdd\u4e5f\u771f\u4e8f\u4ed6\u8bf4\u5f97\u51fa\u6765\u3002 \u3000\u3000\u7389\u7f57\u5239\u770b\u5230\u90a3\u4e9b\u5927\u5b98\u7684\u65e0\u803b\u6a21\u6837\uff0c\u6c14\u5f97\u51e0\u4e4e\u8981\u62d4\u5251\u53bb\u4e71\u6740\u4e00\u901a\uff0c\u94c1\u98de\u9f99\u628a\u5979\u62c9\u5f00\u9053\uff1a \u201c\u522b\u770b\u4e86\uff0c\u6211\u7684\u80c3\u51e0\u4e4e\u8981\u4f5c\u5455\u5566\uff01\u201d \u3000\u3000\u5230\u4eac\u4e4b\u5f8c\u94c1\u3001\u7389\u4e8c\u4eba\u548c\u675c\u660e\u5fe0\u5206\u9053\u626c\u9573\uff0c\u94c1\u7389\u4e8c\u4eba\u4f4f\u5728\u957f\u5b89\u9556\u5c40\uff0c\u675c\u660e\u5fe0\u5219\u6295\u9760\u4ed6\u7684\u8868 \u4eb2\u5175\u90e8\u5927\u5458\u5b59\u627f\u5b97\u3002\u5206\u624b\u65f6\uff0c\u7389\u7f57\u5239\u5fae\u5fae\u51b7\u7b11\u5bf9\u675c\u660e\u5fe0\u9053\uff1a\u201c\u4f60\u53bb\u5411\u9b4f\u5fe0\u8d24\u8d3f\u8d42\u6c42\u60c5\uff0c\u6211\u770b \u672a\u5fc5\u6709\u6548\u3002\u201d\u675c\u660e\u5fe0\u9053\uff1a\u201c\u6211\u662f\u5c3d\u529b\u800c\u4e3a\uff0c\u5c06\u6765\u4e5f\u8bb8\u8fd8\u8981\u8bf7\u4f60\u4eec\u5e2e\u5fd9\u3002\u201d\u94c1\u7389\u4e8c\u4eba\u89c1\u4ed6\u867d\u7136 \u6709\u70b9\u7cca\u6d82\u4e5f\u8fd8\u4e0d\u5931\u4e3a\u6b63\u4eba\u541b\u5b50\uff0c\u4fbf\u628a\u957f\u5b89\u9556\u5c40\u7684\u5730\u5740\u7ed9\u4e86\u4ed6\u3002 \u3000\u3000\u957f\u5b89\u9556\u5c40\u7684\u603b\u9556\u5934\u9f99\u8fbe\u53c1\u662f\u94c1\u98de\u9f99\u7684\u597d\u53cb\uff0c\u89c1\u94c1\u7389\u4e8c\u4eba\u5230\u6765\uff0c\u81ea\u662f\u6bb7\u52e4\u62db\u5f85\u3002\u665a\u996d\u4e4b \u5f8c\uff0c\u7389\u7f57\u5239\u95ee\u8d77\u6768\u6d9f\u88ab\u6355\u4e0b\u72f1\u7684\u4e8b\u60c5\uff0c\u9f99\u8fbe\u53c1\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u771f\u662f\u4e00\u8a00\u96be\u5c3d\u54ea\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u8ffd\u95ee\u6240\u4ee5\uff0c\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u9609\u515a\u5174\u4e1c\u6797\u515a\u4e4b\u4e89\uff0c\u4f60\u4eec\u662f\u77e5\u9053\u7684\u4e86\u3002\u9609\u515a\u5c31\u662f\u9b4f\u5fe0\u8d24 \u7684\u515a\u7fbd\u3002\u9b4f\u5fe0\u8d24\u81ea\u5c01\u201c\u4e5d\u5343\u5c81\u201d\uff0c\u624b\u4e0b\u7684\u5927\u5b98\u4e5f\u6210\u4e86\u201c\u5343\u5c81\u7237\u201d\u3002\u4ed6\u95e8\u4e0b\u7684\u6587\u81e3\u6b66\u5c06\u6709\u201c\u4e94 \u864e\u201d\u3001\u201c\u4e94\u5f6a\u201d\u3001\u201c\u5341\u72d7\u201d\u3001\u201c\u5341\u5b69\u513f\u201d\u3001\u201c\u56db\u5341\u5b59\u201d\u7b49\u7b49\u79f0\u53f7\u3002\u4ed6\u4eec\u4e13\u53cd\u201c\u4e1c\u6797\u201d\u3002\u201c\u4e1c \u6797\u201d\u672c\u6765\u662f\u56e0\u88ab\u8d2c\u5927\u81e3\u9ad8\u6500\u9f99\u4e8e\u5b54\u8c26\u7b49\u5728\u65e0\u4e1c\u6797\u4e66\u9662\u8bb2\u5b66\u800c\u5f97\u540d\uff0c\u5230\u4e86\u73b0\u5728\uff0c\u51e1\u4e00\u5207\u6b63\u6d3e\u4eba \u7269\uff0c\u90fd\u88ab\u51a0\u4ee5\u201c\u4e1c\u6797\u515a\u201d\u7684\u5e3d\u5b50\uff0c\u6210\u4e3a\u7f6a\u540d\u4e86\u3002\u9b4f\u5fe0\u8d24\u7684\u515a\u7fbd\u738b\u7ecd\u5fbd\u628a\u4e1c\u6797\u515a\u4e2d\u91cd\u8981\u7684\u4eba\u7269 \u767e\u96f6\u516b\u4eba\u7f16\u4e3a\u201c\u70b9\u5c06\u5f55\u201d\uff0c\u6bd4\u4e4b\u4e3a\u201c\u6881\u5c71\u6cca\u767e\u96f6\u516b\u5c06\u201d\u4ed6\u4eec\u9609\u515a\u81ea\u79f0\u201c\u6b63\u4eba\u201d\uff0c\u800c\u628a\u201c\u4e1c\u6797 \u515a\u201d\u8d2c\u4e3a\u201c\u90aa\u6d3e\u201d\uff0c\u51c6\u5907\u6309\u540d\u5355\u4e00\u4e00\u9677\u5bb3\u3002\u6768\u6d9f\u3001\u5de6\u5149\u6597\u3001\u8881\u5316\u4e2d\u7b49\u5728\u201c\u70b9\u5c06\u5f55\u201d\u4e2d\u90fd\u662f\u540d \u5217\u524d\u8305\u7684\u4eba\u7269\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u6012\u9053\uff1a\u201c\u771f\u662f\u98a0\u5012\u662f\u975e\uff0c\u6210\u4f55\u4e16\u754c\uff01\u201d\u9f99\u8fbe\u53c1\u7eed\u9053\uff1a\u201c\u718a\u7ecf\u7565\u88ab\u5bb3\u6b7b\u5f8c\uff0c\u6768\u6d9f\u89c1 \u5ba2\u9b4f\u4e13\u6a2a\uff0c\u6124\u4e0d\u53ef\u904f\uff0c\u4e0a\u758f\u52be\u9b4f\u5fe0\u8d24\u5eff\u56db\u6761\u5927\u7f6a\uff0c\u4e0d\u6599\u4e0a\u758f\u7684\u7b2c\u4e8c\u5929\u4f7f\u6709\u65e8\u8c34\u8d23\u6768\u6d9f\u3002\u671d\u4e2d \u6b63\u76f4\u7684\u5927\u81e3\u90fd\u88ab\u6fc0\u6012\u4e86\uff0c\u4e00\u9762\u8054\u5408\u4e0a\u758f\uff0c\u4e00\u9762\u51c6\u5907\u5728\u7687\u5e1d\u5750\u671d\u65f6\u9762\u594f\u3002\u9b4f\u5fe0\u8d24\u53ea\u624b\u906e\u5929\uff0c\u5c45 \u7136\u963b\u6b62\u7687\u5e1d\u4e00\u8fde\u53c1\u5929\u4e0d\u5750\u671d\uff0c\u5728\u53c1\u5929\u4e2d\u4ed6\u7684\u5e03\u7f6e\u5df2\u7ecf\u5b8c\u6210\uff0c\u5230\u4e86\u7b2c\u56db\u5929\uff0c\u9b4f\u5fe0\u8d24\u53cd\u4ee5\u201c\u548c\u718a \u5ef7\u5f3c\u52fe\u7ed3\u201d\u7684\u7f6a\u540d\uff0c\u628a\u53cd\u5bf9\u4ed6\u7684\u4e3a\u9996\u4eba\u7269\uff1a\u6768\u6d9f\u3001\u5de6\u5149\u6597\u3001\u9b4f\u5927\u4e2d\u3001\u987e\u5927\u7ae0\uff0c\u8881\u5316\u4e2d\u3001\u5468\u671d \u745e\u7b49\u516d\u4eba\u902e\u6355\u4e0b\u72f1\uff0c\u5173\u5728\u5317\u9547\u629a\u53f8\u5927\u7262\u3002\u9b4f\u5fe0\u8d24\u597d\u4e0d\u9634\u6bd2\uff0c\u8bf4\u4ed6\u4eec\u66fe\u63a5\u53d7\u718a\u5ef7\u5f3c\u7684\u201c\u8d43 \u6b3e\u201d\uff0c\u8981\u5411\u4ed6\u4eec\u201c\u8ffd\u8d43\u201d\uff0c\u4ed6\u4eec\u90fd\u662f\u7a77\u5b98\u513f\uff0c\u90a3\u4ea4\u5f97\u51fa\u4ec0\u9ebd\u201c\u8d43\u6b3e\u201d\uff01\u65bc\u662f\u4fbf\u4e94\u5929\u4e00\u6bd4\uff0c\u6bcf \u201c\u6bd4\u201d\u6253\u56db\u5341\u68cd\uff0c\u5939\u4e94\u5341\uff0c\u4eca\u4ed6\u4eec\u6c42\u751f\u4e0d\u5f97\uff0c\u6c42\u6b7b\u4e0d\u80fd\u3002\u4ed6\u4eec\u4e4b\u4e2d\uff0c\u6709\u51e0\u4e2a\u71ac\u4e0d\u4e86\u5211\uff0c\u4e5f\u66fe \u6388\u610f\u53eb\u6709\u94b1\u7684\u95e8\u751f\u4eb2\u6545\u7b79\u6b3e\u201c\u7f34\u8d43\u201d\uff0c\u53ef\u662f\u90a3\u201c\u8d43\u6b3e\u201d\u591a\u5be1\u4efb\u7531\u9b4f\u5fe0\u8d24\u5f00\u53e3\uff0c\u201c\u7f34\u8d43\u201d\u603b\u7f34 \u4e0d\u591f\uff0c\u53cd\u7ed9\u9b4f\u5fe0\u8d24\u591a\u8f9f\u4e86\u4e00\u6761\u8d22\u8def\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u62cd\u817f\u53eb\u9053\uff1a\u201c\u53ef\u60dc\u4e86\u90a3\u679d\u5343\u5e74\u4f55\u9996\u4e4c\uff01\u201d\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u4ec0\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u4e00\u7b11\u4e0d \u8bed\uff0c\u9053\uff1a\u201c\u597d\u5440\uff0c\u4eca\u665a\u6211\u5c31\u77a7\u6768\u6d9f\u53bb\u3002\u201d\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u5317\u9547\u629a\u53f8\uff0c\u975e\u6bd4\u5bfb\u5e38\u6240\u5728\uff0c\u59d1\u5a18\u4e0d\u53ef \u9020\u6b21\u3002\u201d\u7389\u7f57\u5239\u5927\u7b11\u9053\uff1a\u201c\u7687\u5bab\u5927\u5185\uff0c\u6211\u5c1a\u81ea\u8fdb\u51fa\u81ea\u5982\uff0c\u5317\u9547\u629a\u53f8\u7b97\u4ec0\u9ebd\u4e1c\u897f\uff1f\u5582\uff0c\u6155\u5bb9\u51b2 \u4ed6\u4eec\u56de\u6765\u6ca1\u6709\uff1f\u201d\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u6ca1\u542c\u8bf4\uff0c\u660e\u5929\u6211\u66ff\u4f60\u67e5\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u548c\u94c1\u98de\u9f99\u90fd\u662f\u5929\u4e0d\u6015\u5730\u4e0d\u6015\u7684\u4eba\u7269\uff0c\u8bf4\u5e72\u5c31\u5e72\uff0c\u5f53\u5929\u665a\u4e0a\uff0c\u4fbf\u6362\u4e86\u591c\u884c\u8863\u670d\uff0c\u76f4 \u91c7\u5929\u7262\u3002 \u3000\u3000\u7262\u72f1\u5899\u9ad8\u53c1\u4e08\uff0c\u5899\u4e0a\u63d2\u6ee1\u94c1\u9489\uff0c\u4f46\u5374\u963b\u4e0d\u4e86\u7389\u7f57\u5239\u4ed6\u4eec\u3002\u94c1\u98de\u9f99\u8dc3\u4e0a\u5899\u5934\uff0c\u9053\uff1a\u201c\u4f60\u53bb \u63a2\u76d1\uff0c\u6211\u6321\u654c\u4eba\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u597d\u6781\uff01\u201d\u8df3\u4eba\u91cc\u9762\uff0c\u771f\u5982\u4e00\u53f6\u98d8\u843d\uff0c\u5815\u5730\u65e0\u58f0\u3002 \u3000\u3000\u7389\u7f57\u5239\u4f0f\u5728\u8fc7\u9053\u6697\u89d2\uff0c\u4e0d\u4e45\u4fbf\u6709\u72f1\u5352\u63d0\u706f\u5de1\u8fc7\uff0c\u7389\u7f57\u5239\u4e00\u8df3\u800c\u51fa\uff0c\u660e\u6643\u6643\u7684\u5251\u5c16\u5728\u72f1\u5352 \u9762\u95e8\u4e00\u95ea\uff0c\u4f4e\u58f0\u559d\u9053\uff1a\u201c\u6768\u6d9f\u4f4f\u5728\u90a3\u53f7\u7262\u623f\uff1f\u201d\u72f1\u5352\u5413\u4e86\u4e00\u8df3\uff0c\u542c\u4e86\u7389\u7f57\u5239\u7684\u8bdd\u5f8c\uff0c\u559c\u9053\uff1a \u201c\u4f60\u662f\u6551\u6768\u5927\u4eba\u7684\u5417\uff1f\u4ed6\u5728\u897f\u8fb9\u7b2c\u516d\u53f7\u7262\u623f\uff0c\u4ece\u8fd9\u91cc\u5411\u53f3\u9996\u8f6c\u8fc7\u4fbf\u662f\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u82e5\u8bf4\u5047\u8bdd\uff0c\u6211\u5c31\u628a\u4f60\u4e00\u5251\u65a9\u4e86\u3002\u201d\u72f1\u5352\u987f\u8db3\u9053\uff1a\u201c\u6768\u5927\u4eba\u88ab\u6253\u5f97\u5944\u5944\u4e00 \u606f\uff0c\u4f60\u8981\u6551\u5feb\u70b9\u53bb\u6551\uff01\u201d\u7389\u7f57\u5239\u770b\u4ed6\u795e\u60c5\uff0c\u77e5\u4ed6\u7edd\u4e0d\u4f1a\u53eb\u56b7\u7834\u58e4\uff0c\u4fbf\u4f9d\u7740\u4ed6\u7684\u6307\u70b9\uff0c\u8f6c\u4e86 \u4e2a\uff0c\u6478\u5230\u7b2c\u516d\u53f7\u7262\u623f\u3002 \u3000\u3000\u7262\u623f\u7684\u94c1\u95e8\u539a\u8fbe\u4e94\u5bf8\uff0c\u95e8\u4e0a\u7528\u4e00\u628a\u5927\u94c1\u9501\u9501\u7740\uff0c\u624b\u529b\u591a\u5f3a\u4e5f\u634f\u4e0d\u788e\uff0c\u666e\u901a\u4eba\u4f11\u60f3\u8fdb\u5f97\u3002 \u53ef\u662f\u8fd9\u5374\u96be\u4e0d\u4e86\u7389\u7f57\u5239\uff0c\u5979\u5728\u7eff\u6797\u591a\u5e74\uff0c\u5bf9\u5f00\u9501\u7684\u6280\u672f\uff0c\u7cbe\u719f\u5f02\u5e38\u3002\u53ea\u89c1\u5979\u5728\u767e\u5b9d\u56ca\u4e2d\u53d6\u51fa \u4e00\u6761\u66f2\u66f2\u7684\u94c1\u7ebf\uff0c\u63d2\u8fdb\u9501\u5b54\u4e00\u64a9\uff0c\u94c1\u9501\u5e94\u624b\u4fbf\u5f00\uff0c\u7389\u7f57\u5239\u6478\u4eba\u7262\u5185\u3002 \u3000\u3000\u7262\u623f\u91cc\u9ed1\u9edd\u9edd\u7684\uff0c\u4f46\u95fb\u5f97\u5fae\u5f31\u7684\u547b\u541f\u4e4b\u58f0\uff0c\u7389\u7f57\u5239\u64e6\u71c3\u706b\u77f3\uff0c\u53ea\u89c1\u6768\u6d9f\u62ab\u67b7\u5e26\u9501\uff0c\u8840\u8089 \u6a21\u7cca\uff0c\u51e0\u4e4e\u4e0d\u80fd\u8fa8\u8ba4\u3002 \u3000\u3000\u6768\u6d9f\u9aa4\u7136\u89c1\u6709\u4eba\u6765\uff0c\u5df2\u5403\u4e86\u60ca\uff0c\u5230\u770b\u6e05\u695a\u662f\u7389\u7f57\u5239\u65f6\uff0c\u66f4\u662f\u5403\u60ca\u975e\u5c0f\uff0c\u6323\u624e\u559d\u9053\uff1a\u201c\u4f60 \u6765\u505a\u4ec0\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6765\u6551\u4f60\u51fa\u53bb\uff01\u201d\u6768\u6d9f\u6012\u9053\uff1a\u201c\u6211\u662f\u671d\u5ef7\u5927\u81e3\uff0c\u5c82\u80fd\u968f\u4f60\u8d8a\u72f1\uff01\u201d \u7389\u7f57\u5239\u6c14\u9053\uff1a\u201c\u4f60\u73b0\u5728\u8fd8\u8bb2\u8fd9\u5957\uff0c\u4f60\u4e0d\u8981\u6027\u547d\u4e86\u9ebd\uff1f\u201d\u6768\u6d9f\u9053\uff1a\u201c\u6211\u7eb5\u7136\u88ab\u6740\u88ab\u540a\uff0c\u4e5f\u4e0d\u5173 \u4f60\u7684\u4e8b\u3002\u4f60\u4e0d\u5b88\u738b\u6cd5\uff0c\u6211\u5c82\u80fd\u4e0e\u4f60\u4e00\u6837\uff1f\u201d\u7389\u7f57\u5239\u9a82\u9053\uff1a\u201c\u738b\u6cd5\uff0c\u738b\u6cd5\uff01\u6211\u8bf4\u4f60\u662f\u4e2a\u5927\u8822 \u6750\uff1f\u201d\u6768\u6d9f\u6323\u624e\u53eb\u9053\uff1a\u201c\u4f60\u518d\u8fc7\u6765\uff0c\u6211\u4fbf\u4e00\u5934\u78b0\u6b7b\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u7684\u513f\u5b50\u5df2\u7ed9\u7f57\u94c1\u81c2\u5e26\u5230\u56db\u5ddd\u53bb\u4e86\uff0c\u4f60\u4e0d\u60f3\u5ff5\u4ed6\u5417\uff1f\u201d\u5979\u672c\u60f3\u4ee5\u4eb2\u5b50\u4e4b\u60c5 \u6253\u6d88\u4ed6\u611a\u5fe0\u4e4b\u5ff5\uff0c\u5c82\u6599\u6768\u6d9f\u53cd\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u9aa2\u513f\u65e0\u6059\uff0c\u6211\u5c1a\u4f55\u5fe7\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u662f \u4e2a\u5927\u5fe0\u81e3\uff0c\u4f46\u4f60\u4eec\u6b7b\u5f8c\uff0c\u671d\u4e2d\u5c3d\u662f\u5978\u81e3\uff0c\u660e\u671d\u7684\u6c5f\u5c71\u5c82\u4e0d\u662f\u66f4\u5feb\u5b8c\u86cb\uff1f\u201d\u6768\u6d9f\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5ffd \u53c8\u201c\u5478\u201d\u4e86\u4e00\u53e3\u8bf4\u9053\uff1a\u201c\u5fe0\u81e3\u5c82\u662f\u6740\u5f97\u5c3d\u7684\uff1f\u4f60\u5f53\u6211\u671d\u4e2d\u65e0\u4eba\u9ebd\u4e86\u4f60\u770b\u718a\u5ef7\u5f3c\u6b7b\u4e86\u4fbf\u6709\u8881\u5d07 \u7115\u7ee7\u8d77\uff0c\u53f6\u5411\u9ad8\u53bb\u4e86\u53c8\u6709\u6d2a\u627f\u7574\u63a5\u4efb\u3002\u5927\u660e\u6c5f\u5c71\u80e1\u864f\u593a\u4e0d\u53bb\uff0c\u4f60\u4eec\u6d41\u5bc7\u4e5f\u62a2\u4e0d\u53bb\uff1f\u201d\u6768\u6d9f\u4ee5 \u5175\u90e8\u5927\u897f\u5347\u4efb\u5de6\u526f\u90fd\u5fa1\u53f2\uff0c\u505a\u4e86\u51e0\u5341\u5e74\u5b98\uff0c\u90a3\u6b63\u7edf\u7684\u5fe0\u541b\u89c2\u5ff5\u5df2\u6df1\u4eba\u5fc3\u80ba\uff0c\u4ed6\u628a\u81ea\u5df1\u548c\u671d\u5ef7 \u89c6\u540c\u4e00\u4f53\uff0c\u8fde\u6765\u6551\u4ed6\u7684\u7389\u7f57\u5239\uff0c\u4e5f\u7ed9\u4ed6\u5f53\u6210\u201c\u6d41\u5bc7\u201d\u654c\u4eba\u4e86\u3002\u4ed6\u90a3\u6599\u5230\u660e\u671d\u7684\u6c5f\u5c71\u5728\u4ed6\u6b7b\u5f8c \u4fbf\u88ab\u6ee1\u6e05\u593a\u53bb\uff0c\u800c\u4ed6\u6240\u63a8\u5d07\u7684\u6d2a\u627f\u7574\u5f8c\u6765\u4e5f\u505a\u4e86\u6c49\u5978\u3002\u7389\u7f57\u5239\u6c14\u5f80\u4e0a\u51b2\uff0c\u9053\uff1a\u201c\u54fc\uff0c\u4e0d\u662f\u770b \u4f60\u88ab\u6253\u6210\u8fd9\u6837\uff0c\u6211\u5c31\u5148\u628a\u4f60\u6740\u4e86\uff01\u201d\u8fd9\u5239\u90a3\u95f4\uff0c\u5979\u89c9\u5f97\u6768\u6d9f\u65e2\u53ef\u601c\uff0c\u53c8\u53ef\u7b11\uff0c\u65e2\u53ef\u607c\uff0c\u4f46\u4ea6 \u53ef\u4f69\uff0c\u53ef\u4f69\u7684\u662f\u4ed6\u4e0d\u754f\u6743\u52bf\uff0c\u6562\u52be\u5978\u9609\uff0c\u53ef\u601c\u53ef\u7b11\u53ef\u607c\u7684\u5374\u662f\u4ed6\u81f3\u6b7b\u4e0d\u609f\u7684\u611a\u5fe0\uff01 \u3000\u3000\u6768\u6d9f\u58f0\u8c03\u4e00\u4f4e\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u53bb\u5427\uff01\u4f60\u65e5\u5f8c\u89c1\u4e86\u6211\u513f\uff0c\u53eb\u4ed6\u4e0d\u8981\u4e3a\u5b98\uff0c\u4f46\u4f60\u4e5f\u4e0d\u80fd\u53eb\u4ed6\u4e3a \u5bc7\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u4f60\u513f\u5b50\u5c06\u6765\u4e4b\u4e8b\u4f60\u4e5f\u8981\u7ba1\u9ebd\uff1f\u54fc\uff0c\u4ed6\u53ef\u6bd4\u4f60\u5f3a\u5f97\u591a\uff0c\u6211\u624d\u4e0d\u53eb\u4ed6\u5b66\u4f60 \u7684\u7cdf\u6837\u5b50\u3002\u201d\u6768\u6d9f\u53cc\u773c\u4e00\u7ffb\uff0c\u75f0\u5f80\u4e0a\u6d8c\uff0c\u6655\u4e86\u8fc7\u53bb\u3002\u8fd9\u65f6\u5916\u9762\u5df2\u4f20\u6765\u811a\u6b65\u5954\u8dd1\u4e4b\u58f0\uff0c\u7247\u523b\u5f8c \u201c\u6349\u52ab\u72f1\u8d3c\u5440\uff01\u201d\u4e4b\u58f0\u5927\u8d77\u3002 \u3000\u3000\u8fd9\u65f6\u7389\u7f57\u5239\u672c\u53ef\u4f38\u624b\u5c06\u6768\u6d9f\u6551\u53bb\uff0c\u4f46\u5979\u5374\u6253\u6d88\u8fd9\u4e2a\u5ff5\u5934\u4e86\uff0c\u4e00\u8f6c\u8eab\u95ef\u51fa\u7262\u623f\uff0c\u4fbf\u8df3\u4e0a\u74e6 \u9762\u3002 \u3000\u3000\u74e6\u9762\u4e0a\u94c1\u98de\u9f99\u6b63\u5728\u4ee5\u7816\u74e6\u4f5c\u4e3a\u6b66\u5668\uff0c\u63b7\u4e0b\u53bb\u6253\u90a3\u4e9b\u60f3\u8df3\u4e0a\u6765\u7684\u9526\u8863\u536b\u3002\u94c1\u98de\u9f99\u63b7\u5f97\u53c8\u51c6 \u53c8\u52b2\uff0c\u9526\u8863\u536b\u4e00\u88ab\u6253\u4e2d\u4fbf\u662f\u5934\u7834\u8840\u6d41\u3002 \u3000\u3000\u94c1\u98de\u9f99\u89c1\u5979\u7a7a\u624b\u4e0a\u6765\uff0c\u5927\u4e3a\u5931\u671b\uff0c\u95ee\u9053\uff1a\u201c\u627e\u4e0d\u89c1\u5417\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u51b3\u4e0d\u6551\u4ed6 \u4e86\uff01\u201d\u94c1\u98de\u9f99\u5fc3\u9053\uff1a\u8fd9\u5b69\u5b50\u813e\u6c14\u771f\u602a\u3002\u4f46\u673a\u4f1a\u7a0d\u7eb5\u5373\u901d\uff0c\u8fd9\u65f6\u9526\u8863\u536b\u5df2\u6709\u6570\u4eba\u8df3\u4e0a\uff0c\u518d\u60f3\u52ab \u72f1\uff0c\u5df2\u662f\u4e0d\u80fd\u3002 \u3000\u3000\u94c1\u98de\u9f99\u9053\uff1a\u201c\u90a3\u9ebd\u54b1\u4eec\u5c31\u95ef\u51fa\u53bb\uff01\u201d\u7389\u7f57\u5239\u4e00\u53e3\u95f7\u6c14\uff0c\u65e0\u5904\u53d1\uff0c\u4e00\u58f0\u957f\u7b11\uff0c\u6740\u4eba\u9526\u8863\u536b \u7fa4\u4e2d\uff0c\u5237\u5237\u51e0\u5251\uff0c\u968f\u610f\u6325\u6d12\uff0c\u5251\u5c16\u6240\u89e6\uff0c\u4e0d\u662f\u7a74\u9053\u8981\u5bb3\uff0c\u4fbf\u662f\u5173\u8282\u6240\u5728\uff0c\u90a3\u4e9b\u9526\u8863\u536b\uff0c\u51e0\u66fe \u89c1\u8fc7\u8fd9\u6837\u7684\u5251\u6cd5\uff0c\u7247\u523b\u4e4b\u95f4\u5df2\u6709\u6570\u4eba\u4e2d\u5251\u6eda\u4e0b\u74e6\u9762\uff0c\u75db\u5f97\u72c2\u547c\u60e8\u53f7\u3002 \u3000\u3000\u94c1\u98de\u9f99\u9053\uff1a\u201c\u88f3\u513f\uff0c\u4e0d\u8981\u591a\u6740\u4e86\uff01\u201d\u53cc\u638c\u75be\u5288\uff0c\u5c06\u74e6\u9762\u4e0a\u5269\u4e0b\u90a3\u51e0\u4e2a\u536b\u58eb\u626b\u4e86\u4e0b\u53bb\uff0c\u548c \u7389\u7f57\u5239\u817e\u8eab\u98de\u4e0a\u6c11\u623f\uff0c\u970e\u5ffd\u4e0d\u89c1\u3002 \u3000\u3000\u518d\u8bf4\u81ea\u7389\u7f57\u5239\u53bb\u5f8c\uff0c\u6768\u6d9f\u81ea\u77e5\u8fc7\u4e0d\u4e86\u4eca\u5915\uff0c\u5446\u7136\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5317\u9547\u629a\u53f8\u8bb8\u663e\u7eaf\u548c\u9526\u8863\u536b\u6307 \u6325\u5d14\u5e94\u5143\u8d70\u4e86\u8fdb\u6765\uff0c\u5f8c\u9762\u8ddf\u7740\u4e24\u4e2a\u72f1\u5352\uff0c\u63d0\u7740\u571f\u888b\uff0c\u8bb8\u663e\u7eaf\u9053\uff1a\u201c\u6768\u5927\u4eba\uff0c\u8bf7\u6012\u65e0\u793c\uff0c\u4eca\u665a \u8981\u9001\u5927\u4eba\u5f52\u5929 \u3000\u3000\u6768\u6d9f\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e14\u5f85\u987b\u81fe\uff0c\u5f85\u6211\u7559\u4e0b\u8840\u4e66\uff0c\u70e6\u4f60\u4ea4\u7ed9\u7687\u4e0a\uff0c\u53ef\u4e0d\u53ef\u4ee5\uff1f\u201d\u5d14\u5e94 \u5143\u9053\uff1a\u201c\u5927\u4eba\u8bf7\u5199\u3002\u201d\u6768\u6d9f\u4ee5\u6307\u8638\u8840\uff0c\u6495\u4e0b\u767d\u5e03\u886c\u8863\uff0c\u5199\u9053\uff1a \u3000\u3000\u201c\u6d9f\u4eca\u6b7b\u6756\u4e0b\u77e3\uff0c\u75f4\u5fc3\u62a5\u4e3b\uff0c\u611a\u76f4\u96e0\u4eba\uff0c\u4e45\u62da\u4e03\u5c3a\uff0c\u4e0d\u590d\u6302\u5ff5\u3002\u4e0d\u4e3a\u5f20\u4fed\u9003\u4ea1\uff0c\u4ea6\u4e0d\u4e3a \u6768\u9707\u4ef0\u836f\uff0c\u6b32\u4ee5\u6027\u547d\u5f52\u4e4b\u671d\u5ef7\u2026\u2026\u96f7\u9706\u96e8\u9732\uff0c\u83ab\u975e\u5929\u6069\uff0c\u6b7b\u65bc\u8bcf\u72f1\uff0c\u96be\u8a00\u4e0d\u5f97\u6b7b\u6240\uff0c\u4f55\u61be\u65bc \u5929\uff0c\u4f55\u6028\u65bc\u4eba\u3002\u60df\u6211\u8eab\u526f\u5baa\u81e3\uff0c\u66fe\u53d7\u987e\u547d\u3002\u5b54\u5b50\u4e91\uff1a\u201c\u6258\u5b64\u5bc4\u547d\uff0c\u4e34\u5927\u8282\u800c\u4e0d\u53ef\u593a\u3002\u201d\u6301\u6b64 \u4e00\u5ff5\u53ef\u4ee5\u89c1\u5148\u5e1d\u65bc\u5929\uff0c\u5bf9\u4e8c\u7956\u5341\u5b97\uff0c\u7687\u5929\u540e\u571f\uff0c\u5929\u4e0b\u4e07\u4e16\u77e3\uff01\u5927\u7b11\u5927\u7b11\u8fd8\u5927\u7b11\uff0c\u5200\u65ab\u4e1c\u98ce\uff0c \u65bc\u6211\u4f55\u6709\u54c9\uff1f\u201d \u3000\u3000\u5d14\u5e94\u5143\u770b\u5230\u201c\u5927\u7b11\u5927\u7b11\u8fd8\u5927\u7b11\uff0c\u5200\u65ab\u4e1c\u98ce\uff0c\u65bc\u6211\u4f55\u6709\u54c9\uff1f\u201d\u51e0\u53e5\uff0c\u51e0\u4e4e\u559d\u8d77\u91c7\u6765\uff0c\u8bb8\u663e \u7eaf\u662f\u9b4f\u5fe0\u8d24\u4e7e\u513f\u5b50\uff0c\u77a7\u4e86\u4e00\u773c\uff0c\u9634\u6c89\u6c89\u7684\u9053\uff1a\u201c\u8fd8\u672a\u5199\u5b8c\u5417\uff1f\u201d \u3000\u3000\u6768\u6d9f\u4ee5\u6307\u8638\u8840\uff0c\u7eed\u5199\u9053\uff1a\u201c\u2026\u2026\u8840\u8089\u6dcb\uff0c\u6b7b\u751f\u9877\u523b\uff0c\u672c\u53f8\u8ffd\u810f\uff0c\u9650\u9650\u72e0\u6253\uff0c\u6b64\u5c82\u7687\u4e0a\u5982 \u5929\u4e4b\u4ec1\uff0c\u4e0d\u8fc7\u4ec7\u6211\u8005\u8feb\u6211\u6027\u547d\uff0c\u501f\u5c01\u7586\u4e3a\u9898\uff0c\u5f92\u4f7f\u6789\u81e3\u5b50\u4e4b\u540d\uff0c\u5f52\u4e4b\u7687\u4e0a\u2026\u2026\u201d \u3000\u3000\u8bb8\u98a2\u7eaf\u4e00\u628a\u62a2\u8fc7\uff0c\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u8fd9\u76f4\u5230\u5982\u4eca\u8fd8\u6562\u6028\u603c\u5382\u81e3\uff1f\u201d\u201c\u6309\uff1a\u9b4f\u5fe0\u8d24\u638c\u7ba1\u5382\u536b\uff0c \u6545\u79f0\u5382\u81e3\u3002\u201d\u559d\u9053\uff1a\u201c\u5feb\u52a8\u624b\uff01\u201d\u4e24\u4e2a\u72f1\u5352\uff0c\u5c06\u76db\u6ee1\u6ce5\u571f\u7684\u571f\u888b\u538b\u5728\u6768\u6d9f\u7684\u9762\u4e0a\u548c\u80f8\u4e0a\uff0c\u4e0d \u6d88\u591a\u4e45\uff0c\u6768\u6d9f\u4fbf\u6c14\u7edd\u8eab\u4ea1\u3002\u8bb8\u663e\u7eaf\u9053\uff1a\u201c\u628a\u5de6\u5149\u6597\u548c\u9b4f\u5927\u4e2d\u4e5f\u4e00\u5e76\u505a\u4e86\uff0c\u514d\u5f97\u62c5\u5fc3\u52ab\u72f1\u3002\u201d \u81f3\u65bc\u5468\u671d\u745e\u8881\u5316\u4e2d\u548c\u987e\u5927\u7ae0\u5374\u56e0\u5173\u5728\u53e6\u4e00\u76d1\u7262\uff0c\u4fa5\u5e78\u5f97\u4ee5\u6682\u9003\u6027\u547d\u3002 \u3000\u3000\u6768\u6d9f\u7684\u7edd\u547d\u4e66\uff0c\u8bb8\u663e\u7eaf\u5f53\u7136\u4e0d\u4f1a\u62ff\u7ed9\u7687\u5e1d\uff0c\u53ef\u662f\u5d14\u5e94\u5143\u5df3\u7ecf\u8bb0\u719f\uff0c\u4ed6\u662f\u540c\u60c5\u6768\u6d9f\u7684\u4eba\uff0c \u5f8c\u6765\u5f03\u804c\u5f52\u9690\uff0c\u6768\u6d9f\u7684\u7edd\u547d\u4e66\u4e5f\u5c31\u6d41\u4f20\u5f00\u6765\uff0c\u810d\u7099\u4eba\u53e3\u4e86\u3002\u8fd9\u7edd\u547d\u4e66\u65e2\u6709\u611a\u5fe0\u4e4b\u5ff1\uff0c\u4ea6\u6709\u8c6a \u8fc8\u4e4b\u6c14\uff0c\u771f\u662f\u6587\u5982\u5176\u4eba\uff0c\u65e2\u4ee4\u4eba\u89c9\u5f97\u53ef\u7b11\u53ef\u601c\uff0c\u4ea6\u4ee4\u4eba\u89c9\u5f97\u53ef\u94a6\u53ef\u4f69\u3002 \u3000\u3000\u518d\u8bf4\u7389\u7f57\u5239\u548c\u94c1\u98de\u9f99\u56de\u5230\u957f\u5b89\u9556\u5c40\uff0c\u8bf4\u8d77\u6768\u6d9f\u4e4b\u611a\uff0c\u7389\u7f57\u5239\u72b9\u89c9\u6c14\u95f7\u3002\u94c1\u98de\u9f99\u5ffd\u9053\uff1a \u201c\u4ed6\u867d\u611a\u5fe0\uff0c\u5012\u5e95\u8fd8\u662f\u4e00\u6761\u6c49\u5b50\u3002\u82e5\u7687\u4e0a\u6709\u8bcf\u653e\u4ed6\uff0c\u90a3\u5c31\u597d\u4e86\u3002\u201d\u7389\u7f57\u5239\u62cd\u638c\u7b11\u9053\uff1a\u201c\u662f \u554a\uff0c\u6211\u65e9\u8be5\u60f3\u5230\u8fd9\u7740\uff0c\u6211\u4eec\u4eca\u6b21\u6765\u4eac\uff0c\u4e3a\u7684\u53c1\u4e8b\uff0c\u4e00\u662f\u7269\u8272\u53ef\u4f20\u718a\u7ecf\u7565\u9057\u4e66\u4e4b\u4eba\uff1a\u4e8c\u4e3a\u73ca\u745a \u59b9\u5b50\u62a5\u4ec7\uff0c\u627e\u6155\u5bb9\u51b2\u548c\u5e94\u4fee\u9633\u7684\u6666\u6c14\uff1a\u53c1\u662f\u6551\u8fd9\u4e2a\u987d\u56fa\u4e0d\u5316\u7684\u6768\u6d9f\u3002\u7b2c\u4e00\u4ef6\u4e8b\u53ef\u9047\u800c\u4e0d\u53ef \u6c42\uff1a\u4e8c\u53c1\u4e24\u4e8b\u53ef\u5f97\u4eba\u7687\u5bab\u4e00\u8d9f\uff0c\u55ef\uff0c\u4e0d\u5982\u660e\u665a\u6211\u5c31\u5355\u8eab\u4eba\u5bab\uff0c\u7ed9\u4f60\u770b\u770b\u6155\u5bb9\u51b2\u56de\u6765\u6ca1\u6709\uff1f\u201d \u94c1\u98de\u9f99\u4f4e\u9996\u6c89\u541f\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8ba9\u6211\u53bb\u5427\uff0c\u5bab\u4e2d\u8def\u9053\u6211\u6bd4\u4f60\u719f\uff0c\u800c\u4e14\u4eca\u665a\u95f9\u4e86\u5929\u7262\u4e4b \u5f8c\uff0c\u5bab\u4e2d\u9ad8\u624b\uff0c\u5fc5\u7136\u8c03\u6765\uff0c\u6211\u6b63\u53ef\u4e58\u865a\u800c\u5165\u3002\u201d\u94c1\u98de\u9f99\u60f3\u8d77\u5979\u7684\u8f7b\u529f\u6bd4\u81ea\u5df2\u9ad8\u5999\uff0c\u51e0\u4e4e\u5230\u4e86 \u6765\u53bb\u65e0\u8e2a\u7684\u5730\u6b65\u3002\u4fbf\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5c0f\u5fc3\u70b9\uff01\u82e5\u7136\u6155\u5bb9\u51b2\u5df2\u7ecf\u56fa\u6765\uff0c\u4f60\u4e0d\u8981\u60f9\u4ed6\uff0c\u5f85\u6211\u60f3\u529e\u6cd5 \u7ea6\u4ed6\u5355\u6253\u72ec\u6597\u3002\u201d\u7389\u7f57\u5239\u70b9\u5934\u7b54\u5e94\uff0c\u5979\u5374\u672a\u6599\u5230\uff0c\u5c31\u5728\u5979\u79bb\u5f00\u5929\u7262\u4e4b\u5f8c\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u6768\u6d9f \u5df2\u88ab\u571f\u888b\u95f7\u6b7b\u4e86\u3002 \u3000\u3000\u7389\u7f57\u5239\u827a\u9ad8\u80c6\u5927\uff0c\u7b2c\u4e8c\u665a\u679c\u7136\u5077\u5077\u7684\u6e9c\u4eba\u7687\u5bab\u3002\u4f46\u5979\u5374\u4e0d\u77e5\u7687\u5e1d\u4f4f\u5728\u4ec0\u9ebd\u5730\u65b9\uff0c\u5fc3\u60f3\uff1a \u201c\u90a3\u6deb\u5987\u5ba2\u6c0f\u7684\u201c\u4e73\u5a18\u5e9c\u201d\u6211\u662f\u77e5\u9053\u7684\uff0c\u4e0d\u5982\u5148\u5230\u90a3\u91cc\uff0c\u5f88\u53ef\u80fd\u5c0f\u7687\u5e1d\u5c31\u5728\u90a3\u513f\u3002\u201d\u4e3b\u610f\u6253 \u5b9a\uff0c\u65bd\u5c55\u7edd\u9876\u8f7b\u529f\uff0c\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u5165\u4e86\u4e73\u5a18\u5e9c\uff0c\u98de\u4e0a\u5ba2\u6c0f\u5bdd\u5b98\u5916\u9762\u7684\u5927\u6881\uff0c\u5ba2\u6c0f\u6b63\u5728\u91cc\u9762 \u548c\u5973\u513f\u8c08\u8bdd\u3002 \u3000\u3000\u7389\u7f57\u5239\u5fc3\u9053\uff1a\u201c\u542c\u8bf4\u5ba2\u6c0f\u7684\u5973\u513f\u662f\u7ea2\u82b1\u9b3c\u6bcd\u7684\u5f92\u5f1f\uff0c\u4e0d\u77e5\u5979\u5fc3\u6027\u5982\u4f55\uff1f\u201d\u51dd\u795e\u9759\u542c\u3002\u53ea \u542c\u5f97\u5ba2\u6c0f\u9053\uff1a\u201c\u5a77\u513f\uff0c\u4f60\u5e74\u7eaa\u4e5f\u4e0d\u5c0f\u4e86\uff0c\u6211\u53eb\u7687\u4e0a\u7eb3\u4f60\u505a\u8d35\u5983\u5982\u4f55\uff01\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u5988\uff0c\u4f60 \u53c8\u672a\u8001\uff0c\u600e\u9ebd\u8bf4\u8bdd\u5982\u6b64\u7cca\u6d82\uff1f\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u624d\u7cca\u6d82\uff0c\u505a\u8d35\u5983\u6709\u4ec0\u9ebd\u4e0d\u597d\uff1f\u4f60\u5148\u505a\u8d35 \u5983\uff0c\u7136\u5f8c\u6211\u8bbe\u6cd5\u4ee4\u7687\u4e0a\u628a\u7687\u540e\u5e9f\u6389\uff0c\u90a3\u65f6\u4f60\u5c31\u662f\u7687\u540e\u4e86\u3002\u201d\u5a09\u5a77\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u5b88\u5be1\u3002\u201d\u5ba2\u6c0f \u9053\uff1a\u201c\u54a6\uff0c\u4f60\u8bf4\u4ec0\u9ebd\u4e86\u4f60\u600e\u9ebd\u5492\u6211\u7684\u7531\u54e5\u513f\uff1f\u201d\u5a09\u5a77\u9053\uff1a\u201c\u8c01\u5492\u4ed6\uff0c\u5988\uff0c\u4f60\u8be5\u77e5\u9053\u6211\u5b66\u8fc7\u6b66 \u529f\uff0c\u5bf9\u4eba\u7684\u4f53\u8d28\u5f3a\u5f31\uff0c\u53ea\u8981\u4e00\u671b\u4fbf\u77e5\u3002\u5c0f\u7687\u5e1d\u8868\u9762\u867d\u6ca1\u4ec0\u9ebd\uff0c\u4f46\u4f60\u542c\u4ed6\u8bf4\u8bdd\u77ed\u4fc3\uff0c\u6beb\u65e0\u9057 \u97f3\uff0c\u8eab\u5b50\u865a\u6d6e\uff0c\u8d70\u8def\u8f7b\u98d8\uff0c\u76ee\u524d\u4e0d\u8fc7\u662f\u7528\u8865\u836f\u652f\u6491\u7f62\u4e86\u3002\u5988\uff0c\u6211\u6562\u8ddf\u4f60\u6253\u8d4c\uff0c\u4ed6\u7edd\u5bf9\u4e0d\u80fd\u518d \u6d3b\u53c1\u5e74\uff01\u201d \u3000\u3000\u5ba2\u6c0f\u4e00\u60f3\uff0c\u5973\u513f\u6240\u8bf4\uff0c\u786e\u662f\u5b9e\u60c5\u3002\u4f46\u4ecd\u7136\u8bf4\u9053\uff1a\u201c\u5982\u82e5\u4f60\u6240\u8bf4\uff0c\u90a3\u5c31\u66f4\u8981\u9884\u65e9\u56fe\u8c0b\u4e86\u3002 \u6211\u73b0\u5728\u867d\u7136\u6709\u6743\u6709\u52bf\uff0c\u4f46\u5343\u53e4\u4ee5\u6765\uff0c\u51e0\u66fe\u89c1\u8fc7\u6709\u4e73\u5a18\u53ef\u4ee5\u957f\u9738\u5bab\u4e2d\u4e4b\u4e8b\u3002\u9664\u975e\u662f\u7687\u592a\u540e\u624d\u53ef \u5782\u542c\u653f\uff0c\u6c38\u4fdd\u7e41\u534e\u3002\u5973\u513f\uff0c\u4f60\u505a\u4e86\u7687\u540e\uff0c\u7687\u5e1d\u6b7b\u5f8c\uff0c\u4f60\u4fbf\u662f\u7687\u592a\u540e\uff0c\u54c8\uff0c\u5230\u4e86\u90a3\u65f6\uff0c\u4f60\u968f\u5fc3 \u6240\u6b32\uff0c\u6015\u4ec0\u9ebd\u5b88\u5be1 \u3000\u3000\u7389\u7f57\u5239\u5fc3\u9053\uff1a\u201c\u8fd9\u5973\u4eba\u771f\u662f\u65e0\u803b\u4e4b\u5c24\uff0c\u6211\u82e5\u975e\u6015\u6253\u8349\u60ca\u86c7\uff0c\u4e00\u5251\u5c31\u628a\u5979\u7ed3\u675f\uff01\u201d \u3000\u3000\u5ba2\u5a09\u5a77\u5fc3\u4e2d\u4e5f\u662f\u6c14\u95f7\u975e\u5e38\uff0c\u5979\u5165\u5bab\u4e4b\u5f8c\uff0c\u89c1\u6bcd\u4eb2\u5982\u6b64\u8352\u6deb\uff0c\u5df2\u662f\u6781\u96be\u5fcd\u53d7\uff0c\u542c\u4e86\u6b64\u8bdd\uff0c \u66f4\u662f\u53c8\u7f9e\u53c8\u6c14\uff0c\u84e6\u7136\u53d1\u813e\u6c14\u9053\uff1a\u201c\u5988\uff0c\u6211\u660e\u5929\u8981\u56de\u5bb6\u3002\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u56de\u5bb6\uff0c\u4f60\u56de\u4ec0\u9ebd\u5bb6\uff1f\u8fd9 \u91cc\u5c31\u662f\u4f60\u7684\u5bb6\u4e86\uff01\u201d\u5a09\u5a77\u9053\uff1a\u201c\u6211\u8981\u627e\u5e08\u7236\u53bb\uff01\u201d \u3000\u3000\u5ba2\u6c0f\u9053\uff1a\u201c\u4f60\u90a3\u5e08\u7236\u6b66\u529f\u867d\u7136\u662f\u5f53\u4e16\u7b2c\u4e00\uff0c\u5374\u662f\u4e0d\u8bc6\u65f6\u52a1\u3002\u201d\u5a09\u5a77\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\uff0c\u6211\u4e00 \u5b9a\u8981\u53bb\u627e\u5979\u3002\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u6211\u6709\u4f60\u4e00\u4e2a\u5973\u513f\uff0c\u5bab\u4e2d\u53c8\u662f\u5371\u673a\u9690\u4f0f\uff0c\u4f60\u522b\u77a7\u6211\u6709\u6743\u6709\u52bf\uff0c\u7531\u54e5 \u513f\u82e5\u7136\u6b7b\u4e86\uff0c\u6211\u7ed9\u4eba\u5bb3\u6b7b\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u4f60\u65e2\u4f1a\u6b66\u529f\uff0c\u6211\u5c31\u5168\u9760\u4f60\u4fdd\u62a4\u4e86\u3002\u201d\u5a09\u5a77\u773c\u775b\u4e00\u6e7f\uff0c \u9053\uff1a\u201c\u90a3\u4f60\u5c31\u83ab\u8feb\u6211\u505a\u4ec0\u9ebd\u8d35\u5983\uff0c\u4f60\u4e00\u8feb\u6211\uff0c\u6211\u9a6c\u4e0a\u5c31\u8d70\u3002\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u613f\u610f\uff0c\u6211 \u5c31\u53e6\u7ed9\u4f60\u6311\u4e00\u95e8\u4eb2\u4e8b\uff0c\u65b0\u79d1\u72b6\u5143\u597d\u4e0d\u597d\uff1f\u6587\u72b6\u5143\u6b66\u72b6\u5143\u968f\u4fbf\u4f60\u9009\u3002\u201d\u5a09\u5a77\u7ef7\u8138\u6012\u9053\uff1a\u201c\u5988\uff0c \u6211\u4e0d\u51c6\u4f60\u8bf4\u8fd9\u4e2a\u3002\u8001\u5b9e\u8bf4\uff0c\u6211\u5728\u8fd9\u5bab\u91cc\u4f4f\u5f97\u95f7\u900f\u5566\u3002\u5988\uff0c\u660e\u5929\u6211\u4e22\u897f\u5c71\u770b\u82b1\uff0c\u4f60\u53bb\u4e0d\u53bb\uff1f\u201d \u5ba2\u6c0f\u9053\uff1a\u201c\u6211\u8001\u54af\uff0c\u63d0\u4e0d\u8d77\u8fd9\u4e2a\u52b2\u5566\u3002\u4f60\u770b\u82b1\u89e3\u89e3\u95f7\u5012\u662f\u65e0\u59a8\u3002\u6211\u524d\u5929\u624d\u53eb\u5de7\u5320\u505a\u4e86\u4e00\u8f86\u900d \u9065\u8f66\uff0c\u5c31\u5728\u5916\u9762\u8d70\u5eca\u6446\u7740\uff0c\u4f60\u53bb\u53ef\u4ee5\u5750\u900d\u9065\u8f66\u53bb\u3002\u5728\u8f66\u91cc\u4f60\u53ef\u4ee5\u770b\u5230\u522b\u4eba\uff0c\u522b\u4eba\u770b\u4e0d\u5230\u4f60\uff0c \u4f60\u77a7\uff0c\u5988\u591a\u75bc\u4f60\u3002\u201d \u3000\u3000\u5a09\u5a77\u9762\u4e0a\u73b0\u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u5ba2\u6c0f\u5ffd\u9053\uff1a\u201c\u4f60\u66ff\u6211\u7aef\u4e00\u7897\u53c2\u6c64\u9001\u7ed9\u7687\u4e0a\u5427\uff01\u201d\u5a09\u5a77\u9053\uff1a\u201c\u6211 \u4e0d\u53bb\uff01\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u53c8\u53d1\u813e\u6c14\u5566\uff01\u597d\uff0c\u4e0d\u8981\u53bb\u3002\u6625\u6842\uff0c\u4f60\u6765\uff01\u201d\u5524\u8fc7\u4e00\u540d\u5bab\u5a25\uff0c\u53eb\u5979\u5c06\u53c2\u6c64 \u9001\u7ed9\u7687\u4e0a\u3002 \u3000\u3000\u5bab\u5a25\u63d0\u4e86\u4e00\u4e2a\u94c1\u76d2\uff0c\u76d2\u5185\u76db\u6709\u53c2\u6c64\uff0c\u76d2\u5e95\u70e7\u7740\u9152\u7cbe\u3002\u7389\u7f57\u5239\u77a7\u5979\u8d70\u51fa\u5bab\u95e8\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c \u8f7b\u98d8\u98d8\u7684\u8ddf\u5728\u5979\u7684\u5f8c\u9762\uff0c\u5bab\u5a25\u7adf\u662f\u4e1d\u6beb\u4e0d\u89c9\u3002 \u3000\u3000\u7687\u5e1d\u4f4f\u7684\u5730\u65b9\uff0c\u8ddd\u79bb\u4e73\u5a18\u5e9c\u4e0d\u8fdc\uff0c\u5bab\u5a25\u8d70\u4e86\u4e00\u4f1a\u5c31\u5230\u4e86\u3002\u7389\u7f57\u5239\u89c1\u5b98\u5916\u6709\u536b\u58eb\u5de1\u903b\uff0c\u4fbf \u4f0f\u5728\u5047\u5c71\u8f6c\u89d2\uff0c\u5230\u90a3\u5bab\u5a25\u51fa\u6765\u65f6\uff0c\u7389\u7f57\u5239\u6413\u4e86\u4e00\u7c92\u5c0f\u5c0f\u7684\u6ce5\u4e38\uff0c\u5939\u5728\u4e24\u6307\u4e4b\u95f4\uff0c\u8f7b\u8f7b\u4e00\u5f39\uff0c \u5bab\u5a25\u989d\u89d2\u4e0a\u7740\u4e86\u4e00\u5f39\uff0c\u5927\u58f0\u53eb\u56b7\uff0c\u536b\u58eb\u9053\uff1a\u201c\u4ec0\u9ebd\u4e8b\u60c5\uff1f\u201d\u8dd1\u8fc7\u53bb\u770b\uff0c\u5bab\u5a25\u9053\uff1a\u201c\u6211\u7ed9\u4eba\u6253 \u4e86\u4e00\u4e0b\u3002\u4f60\u770b\u6211\u7684\u5934\u53d1\u90fd\u4e71\u5566\uff0c\u75db\u5f97\u5f88\uff01\u201d\u536b\u58eb\u7b11\u9053\uff1a\u201c\u4f60\u89c1\u9b3c\u5566\uff0c\u6211\u770b\u6253\u7740\u90a3\u91cc\uff1f\u201d\u4e58\u673a \u63e9\u6cb9\uff0c\u629a\u6469\u5bab\u5a25\u7684\u8138\u86cb\u3002\u7389\u7f57\u5239\u8d81\u8fd9\u65f6\u673a\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c\u63a0\u4e0a\u7409\u7483\u74e6\uff0c\u98d8\u8eab\u8fdb\u5165\u5185\u9662\uff0c\u53c8\u8dc3\u4e0a \u7687\u5e1d\u4e66\u623f\u5916\u9762\u7684\u6a2a\u6881\uff0c\u5916\u9762\u7684\u536b\u58eb\u6b63\u5728\u98d8\u98d8\u7136\u548c\u5bab\u5a25\u6253\u60c5\u9a82\u4fcf\uff0c\u90a3\u91cc\u77e5\u9053\u3002 \u3000\u3000\u4e66\u623f\u5185\u5c0f\u7687\u5e1d\u7531\u6821\u6b63\u5728\u6279\u9605\u594f\u758f\uff0c\u5927\u81e3\u7684\u594f\u647a\u90fd\u7ed9\u9b4f\u5fe0\u8d24\u622a\u53bb\u4e86\uff0c\u4ed6\u80fd\u770b\u4e00\u4e9b\u5c0f\u5b98\u7684\u594f \u758f\u89e3\u89e3\u95f7\u3002\u770b\u5230\u4e00\u672c\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4e2a\u4eba\u5012\u5927\u80c6\uff0c\u5c45\u7136\u4e0a\u758f\u66ff\u718a\u5ef7\u5f3c\u558a\u51a4\uff0c\u8fd8\u8981\u6715 \u6740\u9b4f\u5fe0\u8d24\u4ee5\u8c22\u5929\u4e0b\uff0c\u6211\u770b\u4ed6\u53eb\u4ec0\u9ebd\u540d\u5b57\u3002\u201d\u7531\u6821\u539f\u975e\u5341\u5206\u7cca\u6d82\uff0c\u53ea\u662f\u53d7\u5236\u65bc\u5ba2\u6c0f\u5df2\u4e45\uff0c\u65e0\u6cd5 \u81ea\u62d4\u3002\u4ed6\u73b0\u5728\u5df2\u662f\u4e8c\u5341\u5c81\u7684\u5c11\u5e74\u4e86\uff0c\u505a\u7740\u6709\u540d\u65e0\u5b9e\u7684\u5080\u5121\u7687\u5e1d\uff0c\u4e5f\u89c9\u6c14\u95f7\u3002\u6240\u4ee5\u6709\u65f6\u4e5f\u627e\u4e9b \u594f\u758f\u6279\u6279\uff0c\u804a\u4e14\u8fc7\u8fc7\u7687\u5e1d\u7684\u763e\u3002 \u3000\u3000\u8fd9\u4e00\u672c\u594f\u758f\u4ed6\u5374\u4e0d\u6562\u6279\u4e86\uff0c\u53c8\u4e0d\u7518\u5fc3\u9001\u7ed9\u9b4f\u5fe0\u8d24\uff0c\u770b\u4e86\u594f\u758f\u5f8c\u9762\u7684\u540d\u5b57\uff0c\u5583\u5583\u8bf4\u9053\uff1a \u201c\u8881\u5d07\u7115\uff0c\u8fbd\u4e1c\u5927\u8425\u98df\u4e8b\uff0c\u5514\uff0c\u6211\u4e14\u628a\u4ed6\u8bb0\u5728\u5fc3\u5934\u3002\u60f3\u6cd5\u7528\u4ed6\u3002\u554a\uff0c\u4ed6\u5df2\u7ecf\u6765\u4eac\u542c\u5019\u5dee\u4e8b\uff0c \u4e5f\u597d\uff0c\u8fc7\u51e0\u5929\u6211\u53eb\u5927\u5b66\u58eb\u53bb\u53ec\u4ed6\u3002\u53ef\u662f\u8fd9\u594f\u758f\u600e\u6837\u5904\u7f6e\u5462\uff1f\u201d\u6414\u5934\u65e0\u8ba1\uff0c\u5ffd\u7136\u7a97\u95e8\u6253\u5f00\uff0c\u4e00 \u80a1\u52b2\u98ce\u6251\u4e86\u8fdb\u6765\uff01 \u3000\u3000\u7531\u6821\u60ca\u53eb\u4e00\u58f0\uff0c\u4e66\u6848\u4e0a\u51ed\u7a7a\u591a\u4e86\u4e00\u628a\u660e\u6643\u6643\u7684\u5315\u9996\uff0c\u63d2\u5728\u684c\u5b50\u4e2d\u592e\uff0c\u5200\u5c16\u4e0a\u8fd8\u63d2\u6709\u4e00\u5f20 \u5b57\u6761\uff0c\uff0e\u6f66\u8349\u7684\u5b57\u8ff9\u5199\u7740\uff1a\u201c\u901f\u91ca\u6768\u6d9f\uff0c\u793c\u846c\u5ef7\u5f3c\uff0c\u82e5\u4e0d\u4f9d\u4ece\uff0c\u53d6\u4f60\u9996\u7ea7\uff01\u201d\u7531\u6821\u5927\u53eb\u201c\u6765 \u4eba\u5440\uff01\u201d\u731b\u7136\u60f3\u8d77\u90a3\u4e2a\u594f\u758f\uff0c\u8981\u6361\u8d77\u65f6\uff0c\u90a3\u594f\u758f\u5df2\u4e0d\u89c1\u4e86\uff1f \u3000\u3000\u8fd9\u81ea\u7136\u662f\u7389\u7f57\u5239\u7684\u6770\u4f5c\uff0c\u5979\u4ee5\u95ea\u7535\u822c\u7684\u8eab\u624b\uff0c\u5bc4\u7b80\u7559\u5200\uff0c\u53c8\u53d6\u4e86\u8881\u5d07\u7115\u7684\u594f\u758f\u98de\u8eab\u4fbf \u8d70\uff0c\u63a0\u8fc7\u5047\u5c71\uff0c\u84e6\u5730\u91cc\u547c\u547c\u98ce\u54cd\uff0c\u773c\u524d\u50cf\u98de\u6765\u4e00\u7247\u7ea2\u4e91\uff0c\u4e00\u4e2a\u5e9e\u5927\u7684\u8eab\u5f71\u631f\u7740\u4e24\u7247\u602a\u5175\u5668\u9aa4 \u7136\u538b\u4e0b\uff0c\u7389\u7f57\u5239\u6a2a\u5251\u4e00\u62ab\uff0c\u53ea\u542c\u5f97\u4e00\u7247\u7834\u9523\u4f3c\u7684\u54cd\u58f0\uff0c\u9707\u8033\u6b32\u804b\uff0c\u5b9d\u5251\u51e0\u4e4e\u7ed9\u90a3\u4e24\u7247\u602a\u5175\u5668 \u631f\u51fa\u624b\u53bb\u3002\u5b9a\u775b\u4e00\u770b\uff0c\u6765\u7684\u662f\u4e2a\u7a7f\u7740\u5927\u7ea2\u50e7\u888d\u7684\u5587\u561b\uff0c\u8fd9\u4eba\u53eb\u505a\u660c\u94a6\u5927\u5587\u561b\uff0c\u9664\u4e86\u4e00\u8eab\u6b66\u529f \u4e4b\u5916\uff0c\u8fd8\u7cbe\u65bc\u5236\u70bc\u8865\u836f\u4e0e\u623f\u4e2d\u672f\uff0c\u7531\u6821\u56e0\u4e3a\u65e0\u804a\uff0c\u7eb5\u60c5\u58f0\u8272\uff0c\u9b4f\u5fe0\u8d24\u6295\u5176\u6240\u597d\uff0c\u7279\u522b\u793c\u8058\u8fd9 \u4e2a\u5587\u561b\u51fa\u6765\uff0c\u8ba9\u4ed6\u670d\u4f8d\u7687\u4e0a\u3002\u81f3\u65bc\u7687\u4e0a\u662f\u5426\u4f1a\u56e0\u5403\u4e86\u90a3\u79cd\u201c\u8865\u836f\u201d\u800c\u77ed\u5bff\uff0c\u90a3\u5374\u4e0d\u653e\u5728\u9b4f\u5fe0 \u8d24\u5fc3\u4e0a\u4e86\u3002 \u3000\u3000\u660c\u94a6\u5587\u54bb\u867d\u7136\u4e00\u8eab\u90aa\u6c14\uff0c\u6b66\u529f\u5374\u662f\u975e\u540c\u5c0f\u9119\uff0c\u624b\u4f7f\u4e24\u7247\u94dc\u94b9\uff0c\u771f\u6709\u4e07\u592b\u4e0d\u5f53\u4e4b\u52c7\uff0c\u4ed6\u672a \u80fd\u628a\u7389\u7f57\u5239\u7684\u5251\u593a\u8d70\uff0c\u4e5f\u662f\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7389\u7f57\u5239\u5237\u5237\u4e24\u5251\uff0c\u95ea\u7535\u523a\u6765\uff0c\u660c\u94a6 \u5587\u561b\u5c55\u5f00\u4e24\u7247\u94dc\u94b9\uff0c\u5de6\u53f3\u5206\u6321\uff0c\u4e0d\u6599\u7389\u7f57\u5239\u7684\u5251\u6cd5\u5947\u8be1\u7edd\u4f26\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u7a81\u7136\u622e\u5411\u4e2d\u76d8\uff0c\u660c \u94a6\u5587\u561b\u542b\u80f8\u5438\u8179\u628a\u94dc\u94b9\u4e00\u7f29\uff0c\u672a\u80fd\u631f\u7740\u5b9d\u5251\uff0c\u675f\u888d\u7684\u8170\u5e26\u5374\u88ab\u6311\u65ad\uff0c\u5413\u5f97\u8fde\u8fde\u5f8c\u9000\uff0c\u7389\u7f57\u5239 \u98de\u8eab\u4fbf\u8d70\uff0c\u8fd9\u65f6\u5bab\u4e2d\u62a5\u8b66\u4e4b\u58f0\u56db\u8d77\uff0c\u536b\u58eb\u7eb7\u7eb7\u8d76\u6765\uff0c\u666f\u4ec1\u5b98\u7684\u7409\u7483\u74e6\u4e0a\uff0c\u7a81\u7136\u73b0\u51fa\u4e00\u6761\u4eba \u5f71\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u7389\u7f57\u5239\uff0c\u4f60\u597d\u5927\u80c6\uff0c\u8fd9\u56de\u4f60\u63d2\u5180\u96be\u9003\uff01\u201d\u53d1\u8bdd\u7684\u6b63\u662f\u6155\u5bb9\u51b2\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u6df1\u77e5\u7389\u7f57\u5239\u8f7b\u529f\u9ad8\u5999\uff0c\u64d2\u5979\u4e0d\u6613\uff0c\u5e76\u4e0d\u8df3\u4e0b\u6765\u62da\uff0c\u53ea\u662f\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u8981\u614c\u4e71\uff0c \u901f\u95ed\u5916\u51fa\u7684\u5bab\u95e8\uff0c\u660e\u706f\u653e\u7bad\uff0c\u5b88\u7740\u5bab\u5899\uff0c\u7136\u5f8c\u641c\u7d22\uff0c\u5979\u9003\u4e0d\u4e86\u3002\u201d\u6155\u5bb9\u51b2\u5185\u529f\u6df1\u539a\uff0c\u58f0\u97f3\u76f4 \u4f20\u51fa\u5b98\u5916\uff0c\u987f\u65f6\u5bab\u5899\u4e0a\u4eae\u8d77\u5343\u4e07\u76cf\u660e\u706f\uff0c\u536b\u58eb\u90fd\u73b0\u51fa\u8eab\u6765\uff0c\u8981\u60f3\u786c\u95ef\u51fa\u53bb\uff0c\u90a3\u771f\u662f\u4e07\u4e07\u4e0d \u80fd\u3002 \u3000\u3000\u7389\u7f57\u5239\u4eba\u6025\u8ba1\u751f\uff0c\u90a3\u5bab\u5899\u4e0a\u7684\u706f\u7b3c\u867d\u5982\u7e41\u661f\u5bc6\u5e03\uff0c\u5149\u7ebf\u5374\u5e76\u4e0d\u80fd\u5c04\u5230\u5bab\u4e2d\u5185\u82d1\uff0c\u7389\u7f57\u5239 \u4e00\u8eab\u9ed1\u8272\u8863\u88f3\uff0c\u7a7f\u82b1\u7ed5\u6811\uff0c\u4e13\u62e3\u6697\u8def\u6f5c\u884c\uff0c\u5e76\u65f6\u4e0d\u65f6\u65bd\u5c55\u58f0\u4e1c\u51fb\u897f\u4e4b\u6280\uff0c\u7528\u77f3\u5757\u629b\u51fa\u53bb\u5f15\u5f00 \u8ffd\u8fd1\u8eab\u8fb9\u7684\u536b\u58eb\u3002\u5c45\u7136\u7ed9\u5979\u8d70\u5230\u4e86\u5ba2\u6c0f\u7684\u4e73\u5a18\u5e9c\u5916\u3002 \u3000\u3000\u5ba2\u6c0f\u542c\u5f97\u5916\u9762\u6740\u4e4b\u58f0\uff0c\u65e9\u5df2\u5413\u5f97\u7d27\u95ed\u623f\u95e8\uff0c\u9041\u5165\u5730\u7a9f\u3002\u5ba2\u5a09\u5a77\u4ed7\u5251\u5b88\u62a4\uff0c\u5bab\u4e2d\u65e0\u4eba\uff0c\u7389 \u7f57\u5239\u98d8\u7136\u98de\u4eba\uff0c\u89c1\u4e86\u90a3\u67b6\u900d\u9065\u8f66\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u5377\u8d77\u8f66\uff0c\u8eb2\u8fdb\u8f66\u5185\u3002\u5bab\u4e2d\u536b\u58eb\u7eb7\u6270\u534a\u591c\uff0c\u4e0d\u89c1 \u6709\u4eba\u95ef\u51fa\uff0c\u5927\u4e3a\u5947\u602a\uff0c\u6155\u5bb9\u51b2\u7387\u536b\u58eb\u6b65\u6b65\u4e3a\u8425\uff0c\u4ed4\u7ec6\u641c\u7d22\uff0c\u76f4\u95f9\u5230\u5929\u660e\u4e4b\u5f8c\uff0c\u95ed\u5bab\u5927\u641c\uff0c\u4ecd \u7136\u4e0d\u89c1\u3002\u6155\u5bb9\u51b2\u5927\u4e3a\u4e27\u6c14\uff0c\u53ea\u9053\u7389\u7f57\u5239\u5df2\u4ed7\u7740\u5979\u90a3\u7edd\u5999\u7684\u8f7b\u529f\uff0c\u4e0d\u77e5\u4ece\u4ec0\u9ebd\u5730\u65b9\u6e9c\u51fa\u53bb\u4e86\u3002 \u53ea\u597d\u4f20\u4ee4\u505c\u6b62\u641c\u7d22\uff0c\u4ee5\u5f8c\u52a0\u7d27\u6212\u5907\u3002\u8c01\u77e5\u7389\u7f57\u5239\u6b63\u8eb2\u5728\u900d\u9065\u8f66\u5185\u7761\u89c9\uff0c\u8212\u670d\u975e\u5e38\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u4e2d\u5348\uff0c\u5bab\u4e2d\u53c8\u5df2\u5b81\u9759\u5982\u5e38\u3002\u5ba2\u5a09\u5a77\u672c\u60f3\u65e9\u4e0a\u51fa\u53bb\uff0c\u56e0\u6155\u5bb9\u51b2\u95ed\u5b98\u5927\u641c\uff0c\u5df2\u95f7\u4e86\u534a \u5929\uff0c\u8fd9\u65f6\u6212\u4e25\u4ee4\u89e3\uff0c\u5bab\u95e8\u5f00\u653e\uff0c\u6025\u6025\u9a71\u8f66\u51fa\u5916\uff0c\u5ba2\u5a09\u5a77\u65f6\u65f6\u51fa\u5bab\u6e38\u73a9\uff0c\u536b\u58eb\u53f8\u7a7a\u89c1\u60ef\uff0c\u89c1\u5979 \u9a71\u8f66\u51fa\u5bab\uff0c\u8c01\u6562\u641c\u7d22\uff1f \u3000\u3000\u900d\u9065\u8f66\u679c\u7136\u8212\u670d\uff0c\u5750\u5728\u4e0a\u9762\u4e00\u70b9\u4e0d\u89c9\u98a0\u7c38\uff0c\u4e0d\u4e45\u5230\u4e86\u897f\u5c71\u3002\u5ba2\u5a09\u5a77\u6b63\u60f3\u4e0b\u8f66\u8d4f\u82b1\uff0c\u5ffd\u95fb \u5f97\u8f66\u4e2d\u6709\u7ec6\u7ec6\u5480\u56bc\u4e4b\u58f0\uff0c\u597d\u50cf\u8001\u9f20\u5077\u98df\u4f3c\u7684\u3002\u5ba2\u5a09\u5a77\u602a\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u6837\u534e\u4e3d\u65b0\u9020\u7684\u8f66\u5b50\u600e\u4f1a \u6709\u9f20\u5b50\u8eb2\u85cf\uff1f\u201d\u6b63\u60f3\u63ed\u5f00\u5750\u57ab\uff0c\u5ffd\u7136\u6709\u4e00\u80a1\u529b\u5411\u4e0a\u4e00\u9876\uff0c\u5ba2\u5a09\u5a77\u8df3\u4e86\u8d77\u6765\uff0c\u5750\u57ab\u6380\u5f00\uff0c\u5728\u90a3 \u957f\u957f\u7684\u53ef\u5e76\u5750\u4e24\u4eba\u7684\u72d0\u88f3\u4e3a\u57ab\u7684\u9760\u80cc\u6905\u5b50\u4e0b\u9762\uff0c\u4e00\u4e2a\u4eba\u7a81\u7136\u5750\u4e86\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u597d\u5440\uff0c\u591a \u8c22\u4f60\u7684\u871c\u67a3\u548c\u5408\u6843\u812f\u3002\u201d \u3000\u3000\u539f\u6765\u662f\u7389\u7f57\u5239\u5fcd\u4e0d\u7740\u997f\uff0c\u5077\u5979\u5e26\u6765\u7684\u4e1c\u897f\u5403\uff0c\u8d8a\u5403\u8d8a\u6709\u5473\uff0c\u4ee5\u81f4\u5480\u56bc\u51fa\u58f0\u3002\u5ba2\u5a09\u5a77\u5927\u5403 \u4e00\u60ca\uff0c\u672a\u5f85\u62d4\u5251\uff0c\u7389\u7f57\u5239\u5df2\u4e00\u62f3\u6253\u788e\u73bb\u7483\uff0c\u8df3\u51fa\u8f66\u5916\u53bb\u4e86\u3002\u7389\u7f57\u5239\u8fb9\u8dd1\u8fb9\u558a\u9053\uff1a\u201c\u5582\uff0c\u4f60\u7684 \u5e08\u5085\u5df2\u7ecf\u6b7b\u5566\uff0c\u4f60\u4e0d\u51fa\u5bab\uff0c\u4f60\u5e08\u5085\u4f20\u4f60\u7684\u6b66\u529f\u53ef\u5c31\u767d\u8d39\u5fc3\u8840\u5566\uff01\u201d\u5ba2\u5a09\u5a77\u53eb\u9053\uff1a\u201c\u662f\u8c01\u6740 \u7684\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8c01\u4e5f\u6ca1\u6709\u6740\u5979\u3002\u5979\u662f\u7ed9\u5979\u7684\u8d3c\u6c49\u5b50\u6c14\u6b7b\u7684\uff0c\u73b0\u5728\u6b66\u6797\u4e4b\u4e2d\uff0c\u5f97\u5979\u771f\u4f20 \u7684\uff0c\u6709\u4f60\u5566\uff01\u5979\u7684\u513f\u5b50\u662f\u4e2a\u8113\u5305\uff0c\u4e0d\u9876\u4e8b\u3002\u4f60\u4e0d\u51fa\u53bb\u626c\u540d\u7acb\u4e07\uff0c\u66ff\u5e08\u95e8\u4e89\u6c14\uff0c\u4f60\u5e08\u5085\u6b7b\u4e0d\u7791 \u76ee\uff01\u201d\u8bdd\u58f0\u505c\u5f8c\uff0c\u4eba\u4e5f\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u518d\u8bf4\u94c1\u98de\u9f99\u7b49\u4e86\u4e00\u5929\u4e00\u591c\uff0c\u6b63\u662f\u5fe7\u5fc3\u4ef2\u5fe1\uff0c\u89c1\u7389\u7f57\u5239\u56de\u6765\uff0c\u6025\u95ee\u7ecf\u8fc7\u3002\u7389\u7f57\u5239\u4e00\u4e00\u544a \u8bc9\uff0c\u94c1\u98de\u9f99\u542c\u5230\u6155\u5bb9\u51b2\u56de\u6765\uff0c\u9762\u8272\u4e00\u6c89\uff0c\u542c\u5230\u7389\u7f57\u5239\u5077\u5ba2\u5a09\u5a77\u7684\u4e1c\u897f\u98df\uff0c\u53c8\u54c8\u54c8\u5927\u7b11\u3002\u8bf4\u5b8c \u4e4b\u5f8c\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u6155\u5bb9\u51b2\u6682\u65f6\u96be\u4ee5\u627e\u4ed6\u6666\u6c14\uff0c\u4ee5\u5f8c\u518d\u63d0\u3002\u718a\u7ecf\u7565\u7684\u9057\u4e66\uff0c\u6211\u5374\u89c5\u5f97\u9002\u5f53\u7684 \u4eba\u53ef\u4ee5\u9001\u4e86\u3002\u201d \u3000\u3000\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u8881\u5d07\u7115\u5417\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6b63\u662f\u3002\u8d77\u521d\u6211\u542c\u5f97\u675c\u660e\u5fe0\u8bf4\u4ed6\u662f\u5f53\u4e16 \u5947\u624d\uff0c\u8fd8\u4e0d\u76f8\u4fe1\uff0c\u5f8c\u6765\u6768\u6d9f\u8bf4\u4ed6\u53ef\u7ee7\u627f\u718a\u5ef7\u5f3c\uff0c\u6211\u4e5f\u8fd8\u672a\u5c3d\u4fe1\uff0c\u73b0\u5728\u770b\u4e86\u4ed6\u7684\u594f\u758f\uff0c\u8fd9\u4eba\u7684 \u786e\u6709\u80c6\u6709\u8bc6\uff0c\u53ef\u4ee5\u9001\u4e66\u7ed9\u4ed6\u4e86\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u718a\u7ecf\u7565\u7684\u9057\u4e66\u6709\u5173\u56fd\u8fd0\uff0c\u4e0d\u53ef\u4e0d\u614e\u3002\u4ed6\u65e2\u7136 \u5728\u4eac\uff0c\u6211\u53eb\u9f99\u5927\u54e5\u6253\u63a2\u4ed6\u7684\u4f4f\u5740\uff0c\u54b1\u4eec\u518d\u53bb\u8bd5\u4ed6\u4e00\u8bd5\u3002\u201d \u3000\u3000\u518d\u8bf4\u8881\u5d07\u7115\u4ece\u5173\u5916\u9063\u6563\u56de\u6765\uff0c\u542c\u5019\u5206\u53d1\uff0c\u50cf\u4ed6\u8fd9\u822c\u7684\u4e2d\u7ea7\u5c06\u9886\uff0c\u5728\u5bab\u4e2d\u6570\u4ee5\u767e\u8ba1\uff0c\u5175\u90e8 \u6839\u672c\u5c31\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u4ed6\u62da\u6b7b\u4e0a\u758f\uff0c\u4e5f\u65e0\u4e0b\u6587\uff0c\u8fd9\u665a\u95f7\u95f7\u65e0\u804a\uff0c\u6ce1\u4e86\u4e00\u58f6\u6d53\u8336\uff0c\u72ec\u5750\u9605\u8bfb\u5b59\u5b50 \u5175\u6cd5\uff0c\u521a\u770b\u4e86\u51e0\u9875\uff0c\u623f\u95e8\u5ffd\u7136\u88ab\u4eba\u63a8\u5f00\uff0c\u8d70\u8fdb\u4e00\u4e2a\u8001\u5934\u4e00\u4e2a\u5c11\u5973\u3002\u90a3\u5c11\u5973\u559d\u9053\uff1a\u201c\u8881\u5d07\u7115\uff0c \u4f60\u597d\u5927\u80c6\uff0c\u5c45\u7136\u6562\u4e0e\u9b4f\u516c\u516c\u4f5c\u5bf9\uff0c\u4f60\u8fd8\u60f3\u6d3b\u5417\uff1f\u201d \u3000\u3000\u8881\u5d07\u7115\u9053\uff1a\u201c\u4f60\u4eec\u662f\u8c01\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6765\u6740\u4f60\u7684\uff01\u201d\u4ece\u6000\u4e2d\u62bd\u51fa\u594f\u647a\uff0c\u671d\u684c\u4e0a\u4e00\u63b7\uff0c \u559d\u9053\uff1a\u201c\u8fd9\u662f\u4e0d\u662f\u4f60\u5199\u7684\uff1f\u201d \u3000\u3000\u8881\u5d07\u7115\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u6211\u6765\u4eac\u4e4b\u5f8c\uff0c\u5c31\u95fb\u8bf4\u594f\u758f\u591a\u7ed9\u5ba2\u6c0f\u6263\u4e0b\uff0c\u53c8\u542c\u8bf4\u5ba2\u6c0f\u6709\u4e2a\u5973 \u513f\u901a\u6653\u6b66\u827a\u3002\u83ab\u975e\u6211\u7684\u594f\u758f\u4e5f\u7ed9\u5ba2\u6c0f\u62c6\u53bb\u770b\u4e86\uff0c\u53eb\u5979\u5973\u513f\u548c\u536b\u58eb\u6765\u6740\u6211\uff1f\u201d\u5374\u4e5f\u6602\u7136\u4e0d\u60e7\uff0c \u5927\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u6211\u5199\u7684\u53c8\u600e\u9ebd\u6837\uff1f\u201d\u6b63\u662f\uff1a\u80f8\u4e2d\u5b58\u6b63\u6c14\uff0c\u4e00\u6b7b\u53c8\u4f55\u59a8\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b \u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u4e09\u56de\u3000\u5251\u6c14\u817e\u9704\u3000\u53c1\u756a\u60ca\u5927\u5185\u3000\u5bab\u95f1\u7aa5\u79d8\u3000\u4e00\u6124\u8d70\u5929\u6daf \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377 \u7b2c\u4e8c\u5341\u4e09\u56de \u5251\u6c14\u817e\u9704\u3000\u53c1\u756a\u60ca\u5927\u5185 \u5bab\u95f1\u7aa5\u79d8\u3000\u4e00\u6124\u8d70\u5929\u6daf \u3000\u3000\u7389\u7f57\u5239\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u771f\u4e0d\u6015\u6b7b\u9ebd\uff1f\u201d\u4f38\u51fa\u7ea4\u7ea4\u7389\u6307\uff0c\u5728\u7d2b\u6a80\u684c\u4e0a\u4e71\u5212\uff0c\u8bf4\u8bdd\u505c\u65f6\uff0c\u684c\u4e0a \u5df2\u73b0\u51fa\u4e00\u4e2a\u5927\u5927\u7684\u201c\u6740\u201d\u5b57\uff0c\u5165\u6728\u6570\u5206\u3002 \u3000\u3000\u8881\u5d07\u7115\u5927\u7b11\u9053\uff1a\u201c\u6211\u82e5\u6015\u6b7b\uff0c\u4e5f\u4e0d\u4e0a\u8fd9\u672c\u594f\u758f\u4e86\uff0c\u4f60\u8981\u6740\u4fbf\u6740\uff0c\u4f55\u5fc5\u5356\u5f04\uff1f\u201d\u7389\u7f57\u5239\u55d6 \u7684\u4e00\u58f0\u62d4\u51fa\u5251\u6765\uff0c\u8881\u5d07\u7115\u5411\u524d\u4e00\u633a\uff0c\u201c\u5478\u201d\u7684\u4e00\u53e3\u553e\u6cab\u5410\u53bb\uff0c\u773c\u524d\u4eba\u5f71\u5ffd\u7136\u4e0d\u89c1\uff0c\u53ea\u542c\u5f97\u7389 \u7f57\u5239\u5728\u8033\u8fb9\u7b11\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u6ca1\u7ed9\u4f60\u5f04\u810f\u6211\u7684\u8863\u88f3\uff0c\u82e5\u5f04\u810f\u4e86\uff0c\u4f60\u8fd9\u4e2a\u7a77\u5b98\u513f\u8d54\u5f97\u8d77\u5417\uff1f\u201d \u3000\u3000\u8881\u5d07\u7115\u4e00\u6014\uff0c\u53ea\u89c1\u7389\u7f57\u5239\u7b11\u76c8\u76c8\u7684\u7ad9\u5728\u4ed6\u7684\u65c1\u8fb9\uff0c\u5b9d\u5251\u4e5f\u5df2\u63d2\u56de\u9798\u4e2d\u3002\u8881\u5d07\u7115\u83ab\u540d\u6240 \u4ee5\uff0c\u94c1\u98de\u9f99\u9053\uff1a\u201c\u88f3\u513f\uff0c\u522b\u5f00\u73a9\u7b11\u4e86\u3002\u201d\u7389\u7f57\u5239\u6361\u887d\u65bd\u793c\uff0c\u9053\uff1a\u201c\u5f88\u597d\uff0c\u4f60\u7684\u786e\u662f\u4e2a\u4e0d\u6015\u6b7b \u7684\u82f1\u96c4\uff01\u201d \u3000\u3000\u8881\u5d07\u7115\u8fd8\u4e86\u4e00\u793c\uff0c\u8be7\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4f4d\u4e0d\u662f\u5ba2\u9b4f\u6d3e\u6765\u7684\u523a\u5ba2\u9ebd\uff1f\u201d \u3000\u3000\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u6211\u4eec\u662f\u7ed9\u4f60\u9001\u4e1c\u897f\u6765\u7684\u3002\u201d\u8881\u5d07\u7115\u9053\uff1a\u201c\u4ec0\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u89e3\u5f00\u5305\u88b1\uff0c\u5c06 \u4e66\u53d6\u51fa\uff0c\u653e\u5728\u684c\u4e0a\uff0c\u8881\u5d07\u7115\u4e00\u89c1\u5c01\u9762\u4e0a\u6240\u9898\u7684\u201c\u8fbd\u4e1c\u8bba\u201d\u53c1\u5b57\uff0c\u6b63\u662f\u718a\u5ef7\u5f3c\u7684\u5b57\u8ff9\uff0c\u614c\u5fd9\u62ff \u4e86\u8d77\u6765\uff0c\u63ed\u4e86\u51e0\u9875\uff0c\u201c\u554a\u5440\u201d\u4e00\u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u9053\uff1a\u201c\u718a\u7ecf\u7565\u7684\u4e66\u600e\u9ebd\u5230\u4e86\u4f60\u624b\u4e0a\uff1f\u201d \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u95f4\u3002\u4f60\u82e5\u8ba4\u4e3a\u8fd9\u672c\u4e66\u5bf9\u4f60\u8fd8\u6709\u7528\u5904\uff0c\u5c3d\u9119\u6536\u4e0b\u3002\u201d\u8881\u5d07\u7115\u9053\uff1a\u201c\u4f60 \u82e5\u4e0d\u8bf4\u660e\u767d\uff0c\u6211\u600e\u80fd\u8981\u718a\u7ecf\u7565\u7684\u9057\u4e66\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u6709\u9152\u5417\uff1f\u201d\u8881\u5d07\u7115\u9053\uff1a\u201c\u6709\u3002\u201d\u7389 \u7f57\u5239\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u7136\u6709\u9152\uff0c\u4e3a\u4f55\u4e0d\u62ff\u51fa\u6765\uff1f\u6b64\u4e8b\u8bf4\u6765\u8bdd\u957f\uff0c\u6ca1\u6709\u9152\u6da6\u5589\uff0c\u600e\u9ebd\u8bf4\u5f97\u5462\u3002\u201d\u8881 \u5d07\u7115\u5927\u7b11\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u53ef\u60dc\u6ca1\u6709\u4e0b\u9152\u7684\u4e1c\u897f\u3002\u201d\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u8fd9\u4e2a\u5973\u5b50\u5012\u771f\u723d\u5feb\uff01\u201d \u3000\u3000\u8881\u5d07\u7115\u53d6\u51fa\u4e00\u58f6\u767d\u9152\uff0c\u659f\u4e86\u53c1\u676f\u3002\u7389\u7f57\u5239\u9053\uff1a\u201c\u6709\u5f97\u610f\u4e4b\u4e8b\uff0c\u4fbf\u53ef\u4e0b\u9152\u3002\u7239\uff0c\u6211\u4eca\u65e5\u53ef \u8981\u5f00\u9152\u6212\u5566\uff01\u201d\u94c1\u98de\u9f99\u8fde\u559d\u53c1\u676f\uff0c\u7b11\u9053\uff1a\u201c\u8001\u673d\u5728\u718a\u7ecf\u7565\u4e4b\u5f8c\uff0c\u53c8\u5f97\u89c1\u5f53\u4e16\u82f1\u96c4\uff0c\u8fd9\u9152\u6212\u6211 \u4e5f\u5f00\u5566\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u4e00\u8fb9\u559d\u9152\uff0c\u4e00\u8fb9\u8bf4\u8bdd\uff0c\u628a\u718a\u5ef7\u5f3c\u5c06\u9057\u4e66\u6258\u7ed9\u5cb3\u545c\u73c2\uff0c\u5cb3\u9e23\u73c2\u6258\u7ed9\u5353\u4e00\u822a\uff0c\u800c\u5353\u4e00 \u822a\u53c8\u6258\u7ed9\u5979\u7b49\u4e8b\u8bf4\u4e86\u3002\u8881\u5d07\u7115\u542c\u5f97\u6cea\u627f\u53cc\u776b\uff0c\u5411\u5929\u62dc\u4e86\u53c1\u62dc\uff0c\u5c06\u4e66\u6536\u4e86\u3002 \u3000\u3000\u7389\u7f57\u5239\u9152\u91cf\u4e0d\u5927\uff0c\u559d\u4e86\u51e0\u676f\uff0c\u5df2\u5fae\u6709\u9152\u610f\u3002\u6b63\u60f3\u544a\u8f9e\uff0c\u5ffd\u542c\u5f97\u53eb\u95e8\u4e4b\u58f0\u3002\u8881\u5d07\u7115\u542c\u5979\u521a \u624d\u6240\u8bf4\uff0c\u5df2\u77e5\u5979\u4fbf\u662f\u540d\u9707\u6c5f\u6e56\u7684\u7389\u7f57\u5239\uff0c\u4fbf\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\uff0c\u4f60\u4eec\u6682\u907f\u4e00\u907f\u5427\u3002\u201d\u8bf7\u4ed6\u4eec\u8fdb\u5165 \u53a2\u623f\uff0c\u628a\u9152\u64a4\u4e86\uff0c\u53c8\u53d6\u4e86\u4e00\u5f20\u684c\u5e03\uff0c\u94fa\u5728\u4e66\u684c\u4e4b\u4e0a\uff0c\u5c06\u7389\u7f57\u5239\u521a\u624d\u6240\u5212\u7684\u201c\u6740\u201d\u5b57\u906e\u63a9\uff0c\u7136 \u5f8c\u5f00\u95e8\u3002 \u3000\u3000\u8fdb\u6765\u7684\u662f\u4e2a\u6b66\u5b98\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u60f3\u5fc5\u662f\u8881\u76f8\u516c\u4e86\uff1f\u201d\u8881\u5d07\u7115\u5fc3\u9053\uff1a\u201c\u8fd9\u4eba\u6050\u6015\u662f\u5ba2\u9b4f\u6d3e \u6765\u7684\u4e86\uff1f\u201d\u9053\uff1a\u201c\u8881\u5d07\u7115\u4fbf\u662f\u6211\uff01\u201d\u90a3\u6b66\u5b98\u9053\uff1a\u201c\u7687\u7237\u4e45\u6155\u76f8\u516c\u4e4b\u540d\uff0c\u6e34\u6b32\u4e00\u89c1\u3002\u201d\u8881\u5d07\u7115 \u9053\uff1a\u201c\u4f60\u662f\u90a3\u4e2a\u7687\u5e9c\u7684\uff1f\u201d\u6b66\u5b98\u9053\uff1a\u201c\u6211\u662f\u4fe1\u7687\u5e9c\u7684\u3002\u201d\u4fe1\u738b\u6731\u7531\u68c0\u4e43\u5f53\u4eca\u5929\u5b50\u4e4b\u5f1f\uff0c\u9887\u6709 \u793c\u8d24\u4e0b\u58eb\u4e4b\u540d\uff0c\u8881\u5d07\u7115\u542c\u4e86\uff0c\u53c8\u662f\u4e00\u6115\u3002 \u3000\u3000\u90a3\u6b66\u5b98\u9053\uff1a\u201c\u8881\u76f8\u516c\u5728\u516b\u91cc\u94fa\u4e4b\u5f79\uff0c\u5927\u8d25\u6ee1\u6d32\u519b\u961f\uff0c\u8c01\u4e0d\u77e5\u9053\uff1f\u6211\u4eec\u7684\u738b\u7237\u94a6\u4f69\u5f97 \u5f88\u3002\u201d\u8881\u5d07\u7115\u5fc3\u9053\uff1a\u201c\u671d\u5ef7\u4fbf\u4e0d\u77e5\u9053\u3002\u8fd9\u4e2a\u738b\u7237\u80fd\u7559\u5fc3\u8fb9\u5173\u4e4b\u4e8b\uff0c\u786e\u662f\u4e0d\u9519\u3002\u201d \u3000\u3000\u539f\u6765\u6731\u7531\u6821\u7684\u5f1f\u5f1f\u6731\u7531\u68c0\u201c\u5373\u5f8c\u6765\u7684\u5d07\u6862\u7687\u5e1d\u201d\u6bd4\u4ed6\u7684\u54e5\u54e5\u8981\u7cbe\u660e\u5f97\u591a\uff0c\u6731\u7531\u6821\u8eab\u5b50\u865a \u5f31\uff0c\u53c8\u65e0\u592a\u5b50\uff0c\u6731\u7531\u68c0\u65e9\u5c31\u628a\u7687\u4f4d\u89c6\u4e3a\u201c\u56ca\u4e2d\u4e4b\u7269\u201d\uff0c\u4e5f\u65e9\u5c31\u6253\u7b97\u597d\u5728\u505a\u4e86\u7687\u5e1d\u4e4b\u5f8c\uff0c\u8981\u628a \u9b4f\u5fe0\u8d24\u6536\u62fe\u3002\u53ef\u662f\u4ed6\u624b\u4e0b\u5e76\u65e0\u5fc3\u8179\u5927\u5c06\uff0c\u56e0\u6b64\u672a\u96e8\u7ef8\u7f2a\uff0c\u60f3\u628a\u8881\u5d07\u7115\u6536\u4e3a\u5df1\u7528\u3002 \u3000\u3000\u8881\u5d07\u7115\u8fd9\u65f6\u6b63\u662f\u90c1\u4e0d\u5f97\u5fd7\uff0c\u6709\u4eba\u8d4f\u8bc6\uff0c\u4e5f\u4e0d\u7981\u8d77\u4e86\u77e5\u9047\u4e4b\u611f\uff0c\u5c06\u6731\u7531\u68c0\u7684\u8bf7\u5e16\u6536\u4e0b\uff0c\u8bf4 \u9053\uff1a\u201c\u70e6\u8d35\u5b98\u56de\u8986\u7687\u7237\uff0c\u8bf4\u8881\u67d0\u65e9\u665a\u5fc5\u6765\u8c12\u89c1\u3002\u201d \u3000\u3000\u6b63\u60f3\u7aef\u8336\u9001\u5ba2\uff0c\u5916\u9762\u53c8\u6709\u6572\u95e8\u4e4b\u58f0\uff0c\u8881\u5d07\u7115\u5fc3\u4e2d\u6697\u7b11\uff1a\u201c\u6211\u56de\u6765\u5019\u804c\uff0c\u65e0\u4eba\u7406\u776c\u3002\u4eca\u665a \u5374\u4e00\u8fde\u6765\u4e86\u51e0\u62e8\u5165\uff0c\u83ab\u975e\u65f6\u6765\u8fd0\u8f6c\u4e86\u9ebd\uff1f\u201d\u5f00\u95e8\u5904\uff0c\u4e24\u4e2a\u4eba\u51b2\u4e86\u8fdb\u6765\uff0c\u53ea\u89c1\u4e00\u4e2a\u662f\u5e74\u7ea6\u4e94\u5341 \u7684\u8001\u5934\uff0c\u9e70\u9f3b\u72ee\u53e3\uff0c\u76f8\u8c8c\u4e11\u964b\uff0c\u53e6\u4e00\u4e2a\u5374\u662f\u9526\u8863\u536b\u670d\u9970\u7684\u6b66\u5b98\u3002 \u3000\u3000\u7389\u7f57\u5239\u5728\u53a2\u623f\u5077\u5077\u5f20\u671b\uff0c\u89c1\u8fd9\u4e2a\u9526\u8863\u536b\u6b63\u662f\u77f3\u6d69\uff0c\u5fc3\u4e2d\u8be7\u9053\uff1a\u201c\u77f3\u6d69\u6765\u505a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u53ea\u89c1\u77f3\u6d69\u8fc8\u524d\u4e24\u6b65\uff0c\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u4f60\u4e0d\u662f\u4fe1\u738b\u5e9c\u7684\u9ebd\uff1f\u4f60\u5230\u8fd9\u91cc\u6765\u505a\u4ec0\u9ebd\uff1f\u201d\u4fe1\u738b\u5dee\u6765 \u9080\u8bf7\u8881\u5d07\u7115\u7684\u6b66\u5b98\u540d\u53eb\u767d\u5e7f\u6069\uff0c\u7cbe\u901a\u6454\u89d2\u4e4b\u6280\uff0c\u4e43\u662f\u4fe1\u738b\u5e9c\u4e2d\u6570\u4e00\u6570\u4e8c\u7684\u6559\u5934\uff0c\u89c1\u77f3\u6d69\u559d\u7834 \u4ed6\u7684\u6765\u5386\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u597d\u3002\u8fd9\u77f3\u6d69\u4e43\u662f\u9b4f\u5fe0\u8d24\u7684\u5fc3\u8179\uff0c\u82e5\u88ab\u4ed6\u8bc6\u7834\u738b\u7237\u7528\u610f\uff0c\u5b9e\u6709\u672a\u4fbf\u3002\u201d \u4ed7\u7740\u672c\u9886\u9ad8\u5f3a\uff0c\u5148\u53d1\u5236\u4eba\uff0c\u5fae\u7b11\u8d77\u7acb\uff0c\u62f1\u624b\u8bf4\u9053\uff1a\u201c\u77f3\u6307\u6325\uff0c\u4f60\u597d\uff01\u201d\u51b7\u4e0d\u9632\u624b\u81c2\u4e00\u5708\uff0c\u811a \u4e0b\u4e00\u62e8\uff0c\u556a\u7684\u4e00\u58f0\uff0c\u5c06\u77f3\u6d69\u631e\u4e0b\u53f0\u9636\uff01 \u3000\u3000\u8881\u5d07\u7115\u5403\u4e86\u4e00\u60ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u4e0e\u77f3\u6d69\u540c\u6765\u7684\u90a3\u4e2a\u8001\u4eba\u4e00\u58f0\u602a\u5578\uff0c\u970e\u773c\u4fbf\u6b3a\u5230 \u4e86\u767d\u5e7f\u6069\u773c\u524d\uff0c\u767d\u5e7f\u6069\u8eab\u8eaf\u4e00\u77ee\uff0c\u53cc\u81c2\u53cd\u62b1\uff0c\u8981\u7528\u6454\u89d2\u4e2d\u7684\u7edd\u6280\u201c\u91d1\u9ca4\u7ffb\u8eab\u201d\uff0c\u5c06\u4ed6\u80cc\u8d1f\u8d77 \u6765\uff0c\u518d\u5c06\u4ed6\u631e\u6b7b\u3002 \u3000\u3000\u767d\u5e7f\u6069\u62db\u6570\u65b9\u53d1\uff0c\u5ffd\u542c\u5f97\u90a3\u8001\u4eba\u5728\u8033\u8fb9\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u627e\u6b7b\u5566\uff01\u201d\u80a9\u5934\u4e00\u9635\u5267\u75db\uff0c \u6709\u529b\u4e5f\u53d1\u4e0d\u51fa\u6765\u3002\u8881\u5d07\u7115\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u9ebd\u4eba\uff1f\u80c6\u6562\u5230\u6211\u8fd9\u91cc\u52a8\u7c97\uff01\u201d\u817e\u5730\u8dc3\u51fa\uff0c\u4e00\u638c\u6a2a \u626b\u3002 \u3000\u3000\u90a3\u8001\u4eba\u53eb\u58f0\u201c\u597d\uff01\u201d\u53cc\u624b\u4e00\u9001\uff0c\u5c06\u767d\u5e7f\u6069\u4e5f\u63b7\u4e0b\u53f0\u9636\uff0c\u95ea\u8eab\u907f\u8fc7\u4e86\u8881\u5d07\u7115\u4e00\u638c\uff0c\u7b11\u9053\uff1a \u201c\u4f60\u8fd9\u5c0f\u5b50\u4e0d\u9519\uff0c\u602a\u4e0d\u5f97\u6211\u4eec\u7684\u5927\u6c57\u770b\u4e0a\u4f60\u5566\uff01\u201d \u3000\u3000\u8881\u5d07\u7115\u609a\u7136\u4e00\u60ca\uff0c\u7f29\u624b\u559d\u9053\uff1a\u201c\u4ec0\u9ebd\u5927\u6c57\uff1f\u201d\u90a3\u8001\u4eba\u7b11\u9053\uff1a\u201c\u4e0d\u6253\u4e0d\u76f8\u8bc6\uff0c\u4f60\u4e0e\u6211\u4eec\u7684 \u5927\u6c57\u66fe\u51e0\u5ea6\u5175\u620e\u76f8\u89c1\uff0c\u8fd8\u8981\u95ee\u9ebd\uff1f\u201d\u8881\u5d07\u7115\u9053\uff1a\u201c\u4f60\u662f\u52aa\u513f\u54c8\u8d64\u6d3e\u6765\u7684\u9ebd\uff1f\u201d\u90a3\u8001\u4eba\u7b11\u9053\uff1a \u201c\u6b63\u662f\u3002\u6211\u4eec\u7684\u5927\u6c57\u60f3\u8bf7\u4f60\u51fa\u5173\uff0c\u53c8\u6015\u4f60\u6446\u67b6\u5b50\uff0c\u8bf7\u4f60\u4e0d\u52a8\uff0c\u6240\u4ee5\u53eb\u6211\u6765\u5566\uff01\u201d \u3000\u3000\u8881\u5d07\u7115\u52c3\u7136\u5927\u6012\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u8fd9\u6ee1\u6d32\u72d7\u8d3c\uff0c\u5c45\u7136\u6562\u5230\u5317\u4eac\u6a2a\u884c\uff0c\u4e0d\u7ed9\u4f60\u70b9\u5389\u5bb3\uff0c\u4f60\u5f53\u6211 \u4eec\u4e2d\u56fd\u65e0\u4eba\u4e86\uff1f\u201d\u547c\u547c\u4e24\u638c\uff0c\u8fde\u73af\u75be\u5288\uff1f \u3000\u3000\u90a3\u6ee1\u5dde\u6b66\u5e08\u9053\uff1a\u201c\u8bf7\u4f60\u4e0d\u52a8\uff0c\u6211\u53ef\u8981\u65e0\u793c\u5566\uff01\u201d\u5de6\u62f3\u53f3\u6307\uff0c\u62f3\u51fb\u547d\u95e8\uff0c\u6307\u63a2\u7a74\u9053\u3002\u8881\u5d07 \u7115\u867d\u662f\u5927\u5c06\u4e4b\u6750\uff0c\u9a6c\u4e0a\u9a6c\u4e0b\u529f\u592b\u90fd\u6781\u4e86\u5f97\uff0c\u4f46\u8fd9\u79cd\u817e\u632a\u95ea\u5c55\uff0c\u62f3\u5288\u6307\u622e\u7684\u529f\u592b\u5374\u4e0d\u64c5\u957f\u3002\u6b63 \u5728\u5403\u7d27\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u5a07\u7b11\uff1a\u201c\u8881\u76f8\u516c\uff0c\u4f60\u600e\u9ebd\u548c\u5ba2\u4eba\u6253\u8d77\u6765\u5566\uff01\u201d\u90a3\u6ee1\u6d32\u6b66\u5e08\u773c\u775b\u4e00\u4eae\uff0c\u53ea \u89c1\u4e00\u4e2a\u5c11\u5973\u8f7b\u79fb\u7389\u6b65\uff0c\u7b11\u76c8\u76c8\u7684\u8d70\u4e86\u51fa\u6765\uff0c\u4f46\u89c9\u5bb9\u5149\u8feb\u4eba\uff0c\u6559\u4eba\u4e0d\u6562\u4ef0\u89c6\u3002 \u3000\u3000\u7389\u7f57\u5239\u62db\u624b\u7b11\u9053\uff1a\u201c\u6765\uff0c\u6765\uff01\u4f60\u7ed9\u6211\u8bf4\u4f60\u7684\u4e3b\u4eba\u4e3a\u4ec0\u9ebd\u8981\u8bf7\u8881\u76f8\u516c\uff0c\u8bf4\u5f97\u6709\u7406\uff0c\u6211\u4fbf\u53eb \u4ed6\u968f\u4f60\u53bb\u3002\u201d\u90a3\u6ee1\u6d32\u6b66\u5e08\u5fc3\u9b42\u8ff7\u4e71\uff0c\u8eab\u4e0d\u7531\u5df3\u7684\u8d70\u4e86\u51e0\u6b65\uff0c\u84e6\u7136\u60f3\u9053\uff1a\u201c\u8fd9\u6837\u7f8e\u82e5\u5929\u4ed9\u7684\u5973 \u5b50\uff0c\u4f55\u4e0d\u5c06\u5979\u4e00\u5e76\u6349\u53bb\u732e\u7ed9\u5927\u6c57\uff1f\u201d\u7389\u7f57\u5239\u53c8\u7b11\u9053\uff1a\u201c\u4f60\u4ece\u5173\u5916\u8fdc\u6765\uff0c\u6709\u9526\u8863\u536b\u7684\u6307\u6325\u66ff\u4f60 \u5e26\u8def\uff0c\u60f3\u5fc5\u662f\u5927\u6709\u6765\u5934\u7684\u4e86\u3002\u4f60\u7ed9\u6211\u8bf4\uff0c\u4f60\u662f\u671d\u5ef7\u4e2d\u90a3\u4e00\u4f4d\u8d35\u5b98\u7684\u5ba2\u4eba\uff1f\u201d \u3000\u3000\u8881\u5d07\u7115\u9053\uff1a\u201c\u8fd9\u662f\u6ee1\u6d32\u5978\u7ec6\uff0c\u4f55\u5fc5\u4e0e\u4ed6\u591a\u8bf4\uff1f\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u4e0d\u7136\uff0c\u4fd7\u8bed\u4e91\u5355\u7ebf\u4e0d\u6210 \u7ebf\uff0c\u4ed6\u82e5\u65e0\u4eba\u5305\u5e87\u6536\u5bb9\uff0c\u600e\u6562\u5728\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\uff0c\u5728\u4eac\u57ce\u52d2\u8feb\u63b3\u4eba\uff1f\u201d\u8881\u5d07\u7115\u5fc3\u4e2d\u4e00\u51db\uff0c\u8ba9\u8fc7 \u4e00\u65c1\uff0c\u4efb\u7389\u7f57\u5239\u5bf9\u4ed8\u8fd9\u4e2a\u6ee1\u6d32\u6b66\u5e08\u3002 \u3000\u3000\u90a3\u6ee1\u6d32\u6b66\u5e08\u6447\u6447\u5934\u9053\uff1a\u201c\u5c0f\u5a18\u5b50\uff0c\u8fd9\u4e0d\u5173\u4f60\u7684\u4e8b\u3002\u4f60\u4e0d\u5982\u4e5f\u968f\u6211\u53bb\u5427\u3002\u6211\u4eec\u7684\u5927\u6c57\u89c1\u4e86 \u4f60\uff0c\u4e00\u5b9a\u559c\u6b22\uff0c\u90a3\u4f60\u5c31\u4e00\u751f\u5bcc\u8d35\u8363\u534e\uff0c\u4eab\u53d7\u4e0d\u5c3d\u4e86\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u9762\u8272\u4e00\u53d8\uff0c\u500f\u53c8\u7b11\u9053\uff1a\u201c\u662f\u9ebd\uff1f\u4f60\u5230\u5e95\u8bf4\u4e0d\u8bf4\uff1f\u201d\u90a3\u6ee1\u6d32\u6b66\u5e08\u89c1\u5979\u7b11\u8bed\u76c8\u76c8\uff0c\u4e0d \u4ee5\u4e3a\u610f\uff0c\u563b\u76ae\u7b11\u8138\uff0c\u4f38\u624b\u6765\u62ff\u7389\u7f57\u5239\u7684\u7693\u8155\uff0c\u7389\u7f57\u5239\u624b\u8155\u4e00\u7f29\uff0c\u7b11\u9053\uff1a\u201c\u6211\u6bd4\u8881\u76f8\u516c\u66f4\u4f1a\u6b3e \u5f85\u5ba2\u4eba\uff0c\u4f60\u4e0d\u6015\u9ebd\uff1f\u201d\u90a3\u6ee1\u6d32\u6b66\u5e08\u9053\uff1a\u201c\u5f97\u5c0f\u5a18\u5b50\u6b3e\u5f85\uff0c\u90a3\u662f\u6c42\u4e4b\u4e0d\u5f97\uff01\u201d\u4f38\u624b\u53c8\u62ff\uff0c\u7389\u7f57 \u5239\u84e6\u5730\u5c06\u684c\u5e03\u63ed\u8d77\uff0c\u9732\u51fa\u90a3\u4e2a\u4eba\u6728\u53c1\u5206\u7684\u201c\u6740\u201d\u5b57\uff0c\u90a3\u6ee1\u6d32\u6b66\u5e08\u9aa4\u5403\u4e00\u60ca\uff0c\u84e6\u89c9\u638c\u98ce\u98d2\u7136\uff0c \u6025\u95ea\u907f\u65f6\uff0c\u5de6\u8fb9\u800c\u4e0a\uff0c\u5df2\u7740\u4e86\u4e00\u4e0b\uff0c\u75db\u4eba\u5fc3\u80ba\u3002\u8fd9\u6ee1\u5dde\u6b66\u5e08\u540d\u53eb\u5bdf\u514b\u56fe\uff0c\u4e43\u52aa\u513f\u54c8\u8d64\u5e10\u4e0b\u6570 \u4e00\u6570\u4e8c\u7684\u52c7\u58eb\uff0c\u5403\u4e86\u5927\u4e8f\uff0c\u6012\u543c\u4e00\u58f0\uff0c\u547c\u7684\u4e00\u638c\uff0c\u5c06\u4e66\u684c\u5288\u7ffb\uff0c\u7389\u7f57\u5239\u65e9\u5df2\u62d4\u5251\u5728\u624b\uff0c\u5237\u5237 \u4e24\u5251\uff0c\u5206\u5fc3\u76f4\u523a\u3002 \u3000\u3000\u5bdf\u514b\u56fe\u867d\u7136\u52c7\u731b\uff0c\u600e\u6321\u5f97\u7389\u7f57\u5239\u5251\u6cd5\u795e\u5947\uff0c\u5341\u6570\u62db\u4e00\u8fc7\uff0c\u4fbf\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u65e0\u8fd8\u624b\u4e4b\u529b\u3002 \u7389\u7f57\u5239\u6597\u5f97\u6027\u8d77\uff0c\u4e00\u58f0\u957f\u7b11\uff0c\u811a\u8e0f\u4e2d\u5bab\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u76f4\u523a\u54bd\u5589\uff0c\u5ffd\u542c\u5f97\u94c1\u98de\u9f99\u558a\u9053\uff1a\u201c\u5251\u5e95 \u7559\u4eba\uff01\u201d\u7389\u7f57\u5239\u5251\u950b\u4e00\u8f6c\uff0c\u5728\u654c\u4eba\u5173\u8282\u8981\u5bb3\u4e4b\u5904\u4e00\u70b9\uff0c\u7b11\u9053\uff1a\u201c\u7239\uff0c\u4e0d\u662f\u4f60\u63d0\u9192\uff0c\u6211\u51e0\u4e4e\u628a \u4ed6\u6740\u4e86\uff01\u201d \u3000\u3000\u5bdf\u514b\u56fe\u4e2d\u5251\u5012\u5730\uff0c\u5947\u75db\u5f7b\u9aa8\uff0c\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u4f60\u771f\u662f\u656c\u9152\u4e0d\u5403\u5403\u7f5a\u9152\uff0c\u6211\u8981\u95ee\u4f60\u7684\u8bdd\uff0c \u521a\u624d\u90fd\u5df2\u95ee\u4e86\uff0c\u4f60\u8fd8\u4e0d\u4f9d\u5b9e\u8bf4\u9ebd\uff1f\u201d\u5bdf\u514b\u56fe\u54ac\u7740\u7259\u6839\uff0c\u62b5\u53d7\u75db\u82e6\uff0c\u95ed\u53e3\u4e0d\u8a00\uff0c\u7389\u7f57\u5239\u9053\uff1a \u201c\u54fc\uff0c\u4f60\u8fd8\u5192\u5145\u4ec0\u9ebd\u597d\u6c49\uff1f\u7239\uff0c\u628a\u90a3\u77f3\u6d69\u63d0\u4e0a\u6765\uff0c\u8ba9\u4ed6\u4e5f\u6765\u770b\u770b\uff01\u201d\u94c1\u98de\u9f99\u5728\u7389\u7f57\u5239\u52a8\u624b\u7684 \u65f6\u5019\uff0c\u5df2\u5c06\u767d\u5e7f\u6069\u4e0e\u77f3\u6d69\u6276\u8d77\uff0c\u767d\u5e7f\u6069\u53d7\u4f24\u4e0d\u91cd\uff0c\u81ea\u4eba\u53a2\u623f\u6b47\u606f\u3002\u77f3\u6d69\u626d\u4f24\u4e86\u817f\u8e1d\uff0c\u88ab\u94c1\u98de \u9f99\u6309\u5728\u6905\u4e0a\uff0c\u4e0d\u80fd\u52a8\u5f39\uff0c\u773c\u7741\u7741\u7684\u770b\u7740\u7389\u7f57\u5239\u51b2\u7740\u4ed6\u51b7\u7b11\u3002 \u3000\u3000\u77f3\u6d69\u6bdb\u9aa8\u609a\u7136\uff0c\u53ea\u542c\u5f97\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u77f3\u6d69\uff0c\u4f60\u4e24\u6b21\u5728\u6211\u5251\u5e95\u4e0b\u9003\u751f\uff0c\u4eca\u756a\u672c\u6765\u4e0d\u5e94\u9976 \u4f60\u3002\u4f46\u4f60\u82e5\u80af\u4e56\u4e56\u542c\u8bdd\uff0c\u6211\u4e5f\u8fd8\u53ef\u7f51\u5f00\u4e00\u9762\uff0c\u7559\u4f60\u6b8b\u751f\u3002\u201d\u77f3\u6d69\u4e0d\u6562\u4f5c\u58f0\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211 \u4e14\u5148\u53eb\u4f60\u770b\u770b\u699c\u6837\u3002\u201d\u8c08\u7b11\u58f0\u4e2d\uff0c\u9661\u7136\u4e00\u638c\u5411\u5bdf\u514b\u56fe\u80c1\u4e0b\u62cd\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u4f3c\u4e4e\u8f7b\u98d8\u98d8\u7684\u6beb\u4e0d\u7528\u529b\uff0c\u4f46\u5bdf\u514b\u56fe\u53d7\u4e86\uff0c\u5374\u987f\u65f6\u60e8\u53eb\u72c2\u55e5\uff0c\u5728\u5730\u4e0a\u6eda\u6765\u6eda\u53bb\u3002\u521a \u624d\u6240\u53d7\u7684\u5251\u4f24\uff0c\u867d\u7136\u75db\u4eba\u5fc3\u80ba\uff0c\u8fd0\u6c14\u8fd8\u53ef\u5fcd\u53d7\uff1a\u800c\u73b0\u5728\u88ab\u7389\u7f57\u5239\u8f7b\u8f7b\u4e00\u62cd\uff0c\u4f53\u5185\u987f\u5982\u6709\u5343\u4e07 \u6761\u6bd2\u86c7\u4e71\u7a9c\u4e71\u54ac\uff0c\u771f\u4f3c\u5fc3\u80ba\u5bf8\u65ad\uff0c\u4e94\u810f\u7ffb\u817e\uff0c\u9976\u662f\u94c1\u94f8\u91d1\u521a\uff0c\u4e5f\u96be\u5fcd\u53d7\uff0c\u4e0d\u7981\u5931\u58f0\u53eb\u9053\uff1a \u201c\u6211\u8bf4\uff0c\u6211\u8bf4\uff01\u6c42\u5973\u82f1\u96c4\u6682\u8d50\u7f13\u5211\u3002\u201d\u7389\u7f57\u5239\u98de\u8d77\u4e00\u811a\uff0c\u8e22\u4ed6\u5de6\u80c1\u7a74\u9053\uff0c\u4e00\u75db\u8fc7\u5f8c\uff0c\u8840\u8109\u8212 \u7545\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u5bdf\u514b\u56fe\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u6c57\u6d3e\u6211\u505a\u4f7f\u8005\uff0c\u6765\u89c1\u9b4f\u516c\u516c\u3002\u201d\u6b64\u4e8b\u5728\u94c1\u98de\u9f99\u4e0e\u7389\u7f57 \u5239\u610f\u6599\u4e4b\u4e2d\uff0c\u5374\u5728\u8881\u5d07\u7115\u610f\u6599\u4e4b\u5916\uff0c\u53c8\u6c14\u53c8\u6012\uff0c\u5fcd\u7740\u4e0d\u53d1\u3002\u53ea\u542c\u5f97\u5bdf\u514b\u56fe\u7eed\u9053\uff1a\u201c\u6211\u4e34\u884c \u65f6\uff0c\u5927\u6c57\u5bf9\u6211\u8bf4\uff0c\u718a\u86ee\u5b50\u6b7b\u5f8c\uff0c\u4e2d\u539f\u6709\u8881\u5d07\u7115\u8fd8\u662f\u4e00\u4e2a\u4eba\u624d\uff0c\u4ed6\u73b0\u5728\u867d\u7136\u804c\u4f4e\u4f4d\u5fae\uff0c\u4f46\u4e00\u65e6 \u63e1\u4e86\u5175\u6743\uff0c\u53ef\u662f\u54b1\u4eec\u7684\u52b2\u654c\u3002\u4f60\u4eec\u5230\u4e86\u5317\u4eac\u4e4b\u5f8c\uff0c\u53ef\u8bbe\u6cd5\u5c06\u4ed6\u63b3\u6765\uff0c\u82e5\u662f\u4e0d\u80fd\u751f\u64d2\uff0c\u90a3\u5c31\u5c06 \u4ed6\u6740\u4e86\u3002\u201d\u7389\u7f57\u5239\u542c\u5230\u8fd9\u91cc\uff0c\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff01\u201d\u8881\u5d07\u7115\u4e0d\u89e3\u5176\u610f\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u654c\u4eba\u5bf9\u4f60\u8fd9 \u6837\u5fcc\u514b\uff0c\u718a\u7ecf\u7565\u7684\u9057\u4e66\u4ed8\u6258\u5f97\u4eba\u4e86\u3002\u8fd9\u4e0d\u662f\u5f88\u597d\u9ebd\uff1f\u201d \u3000\u3000\u5bdf\u514b\u56fe\u7eed\u9053\uff1a\u201c\u6211\u8bf7\u9b4f\u516c\u516c\u8bbe\u6cd5\u67e5\u63a2\u8881\u76f8\u516c\u4f4f\u5740\uff0c\u9b4f\u516c\u516c\u6d3e\u4eba\u5230\u5175\u90e8\u4e00\u95ee\uff0c\u5175\u90e8\u6863\u6848\u4e2d \u5b58\u6709\u8881\u76f8\u516c\u5230\u4eac\u5f8c\u6240\u5448\u9012\u7684\u5c65\u5386\u4e66\uff0c\u7acb\u523b\u67e5\u4e86\u51fa\u6765\u3002\u53ef\u7b11\u9b4f\u516c\u516c\u4e0d\u8bc6\u4eba\u624d\uff0c\u8fd8\u9053\uff1a\u4e00\u4e2a\u5c0f\u5c0f \u7684\u4f65\u4e8b\uff0c\u4e5f\u503c\u5f97\u4f60\u4eec\u5927\u6c57\u64cd\u5fc3\u3002\u6211\u5c06\u4ed6\u4f20\u6765\u4fbf\u662f\u3002\u56e0\u6b64\u4ed6\u6d3e\u4e86\u77f3\u6307\u6325\u5e26\u5c0f\u4eba\u6765\u3002\u201d\u8881\u5d07\u7115\u5fc3 \u9053\uff1a\u201c\u597d\u9669\uff01\u5e78\u559c\u81ea\u5df1\u804c\u4f4d\u5351\u5fae\uff0c\u4e0d\u4e3a\u9b4f\u5fe0\u8d24\u6240\u6ce8\u610f\uff0c\u8981\u4e0d\u7136\u53ea\u6050\u5f85\u4e0d\u5230\u4eca\u5929\uff0c\u5df2\u906d\u4ed6\u6697\u5bb3 \u4e86\u3002\u201d \u3000\u3000\u94c1\u98de\u9f99\u770b\u4e86\u5bdf\u514b\u56fe\u4e24\u773c\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u51e0\u6b21\u5978\u9609\uff1f\u201d\u5bdf\u514b\u56fe\u4e00\u6115\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u5978\u9609 \u5c31\u662f\u9b4f\u5fe0\u8d24\u90a3\uff0c\u4f60\u4e0d\u61c2\u9ebd\uff1f\u201d\u5bdf\u514b\u56fe\u9053\uff1a\u201c\u89c1\u8fc7\u4e24\u6b21\u3002\u4e00\u6b21\u662f\u5448\u9012\u5927\u6c57\u7684\u4fe1\u4ef6\uff0c\u4e00\u6b21\u662f\u7d22\u8881 \u76f8\u516c\u4f4f\u5740\u3002\u201d\u94c1\u98de\u9f99\u95ee\u9053\uff1a\u201c\u662f\u767d\u5929\u8fd8\u662f\u665a\u4e0a\uff1f\u201d\u5bdf\u514b\u56fe\u9053\uff1a\u201c\u4e24\u6b21\u90fd\u662f\u665a\u4e0a\u3002\u201d\u94c1\u98de\u9f99 \u9053\uff1a\u201c\u4f60\u89c1\u5978\u9609\u4e4b\u65f6\uff0c\u79bb\u5f97\u8fd1\u9ebd\uff1f\u201d\u5bdf\u514b\u56fe\u9053\uff1a\u201c\u4ed6\u8d50\u6211\u5728\u5ba2\u4f4d\u4e0a\u5750\uff0c\u79bb\u5f97\u4e0d\u8fd1\u4e5f\u4e0d\u8fdc\u3002\u201d \u94c1\u98de\u9f99\u9053\uff1a\u201c\u7ea6\u6709\u591a\u8fdc\uff1f\u201d\u5bdf\u514b\u56fe\u9053\uff1a\u201c\u4ed6\u5728\u4e1c\u9996\uff0c\u6211\u5728\u897f\u9996\u3002\u4e2d\u95f4\u76f8\u8ddd\u7ea6\u6709\u4e00\u4e08\u3002\u201d \u3000\u3000\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u6240\u8bf4\u7684\u90fd\u662f\u5b9e\u8bdd\u9ebd\uff1f\u201d\u5bdf\u514b\u56fe\u9053\uff1a\u201c\u65e0\u534a\u5b57\u865a\u8a00\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u5f88 \u597d\uff0c\u4f60\u8bf4\u4e86\u5b9e\u8bdd\uff0c\u6211\u4e5f\u5bf9\u4f60\u6148\u60b2\u4e86\u3002\u201d\u5bdf\u514b\u56fe\u201c\u8c22\u201d\u5b57\u672a\u8bf4\u51fa\u53e3\uff0c\u7389\u7f57\u5239\u6a2a\u638c\u5728\u4ed6\u8111\u95e8\u4e00 \u51fb\uff0c\u5bdf\u514b\u56fe\u54fc\u4e5f\u4e0d\u54fc\u4e00\u58f0\uff0c\u7acb\u523b\u6c14\u7edd\uff01\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u88ab\u6211\u5904\u6b7b\u4e4b\u4eba\uff0c\u50cf\u4ed6\u8fd9\u6837\u5f97\u4ee5\u75db\u5feb\u8eab \u4ea1\u7684\uff0c\u603b\u5171\u8fd8\u4e0d\u5230\u53c1\u4e2a\u3002\u4e0d\u662f\u89c1\u4ed6\u8bf4\u4e86\u5b9e\u8bdd\uff0c\u6211\u771f\u4e0d\u80af\u8fd9\u6837\u6148\u60b2\uff01\u201d\u77f3\u6d69\u542c\u5f97\u5fc3\u60ca\u8089\u8df3\uff0c\u9762 \u65e0\u4eba\u8272\uff01 \u3000\u3000\u7389\u7f57\u5239\u53c8\u9053\uff1a\u201c\u6211\u8fde\u4ed6\u7684\u4f53\u4e5f\u4e00\u5e76\u5f00\u6d88\u4e86\u5427\uff0c\u514d\u5f97\u8fde\u7d2f\u8881\u76f8\u516c\u3002\u201d\u6478\u51fa\u4e00\u4e2a\u94f6\u74f6\uff0c\u5c06\u836f \u672a\u6d12\u5728\u4e0a\uff0c\u7247\u523b\u4e4b\u5f8c\uff0c\u90a3\u5e9e\u5927\u7684\u8eab\u5316\u4e3a\u4e00\u644a\u6d53\u8840\uff0c\u7389\u7f57\u5239\u4ee5\u5251\u6316\u571f\uff0c\u5c06\u8840\u8ff9\u57cb\u4e86\u3002\u5bf9\u77f3\u6d69 \u9053\uff1a\u201c\u73b0\u5728\u8f6e\u5230\u4f60\u4e86\u3002\u6211\u8981\u4f60\u505a\u4ec0\u9ebd\u4f60\u4fbf\u8981\u505a\u4ec0\u9ebd\uff0c\u6562\u9053\u534a\u4e2a\u4e0d\u5b57\uff0c\u4fbf\u53eb\u4f60\u6b7b\u5f97\u6bd4\u4ed6\u8fd8 \u60e8\uff1f\u201d \u3000\u3000\u77f3\u6d69\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4f46\u51ed\u5973\u4fa0\u5429\u5490\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u7239\uff0c\u4f60\u5bf9\u4ed6\u8bf4\uff01\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u5e26 \u6211\u53bb\u89c1\u9b4f\u5fe0\u8d24\u3002\u201d\u77f3\u6d69\u4e00\u60ca\uff0c\u7389\u7f57\u5239\u77aa\u4ed6\u4e00\u773c\uff0c\u77f3\u6d69\u5fd9\u9053\uff1a\u201c\u6211\u4f9d\uff0c\u6211\u4f9d\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u9053\uff1a\u201c\u8881\u76f8\u516c\uff0c\u8fd9\u91cc\u4f60\u4e0d\u80fd\u4f4f\u4e86\uff0c\u4f60\u5230\u4fe1\u738b\u5e9c\u6682\u907f\u4e00\u907f\u5427\u3002\u767d\u5e7f\u6069\u4f24\u52bf\u4e0d\u91cd\uff0c\u8fd8 \u53ef\u4ee5\u8d70\u3002\u201d\u63d0\u8d77\u77f3\u6d69\uff0c\u548c\u7389\u7f57\u5239\u5148\u884c\u544a\u8f9e\u3002 \u3000\u3000\u539f\u6765\u94c1\u98de\u9f99\u89c1\u5bdf\u514b\u5706\u76f8\u8c8c\u548c\u4ed6\u6709\u4e9b\u76f8\u4f3c\uff0c\u5fc3\u4e2d\u8d77\u4e86\u4e00\u4e2a\u5ff5\u5934\uff0c\u60f3\u5192\u5145\u6ee1\u6d32\u4f7f\u8005\uff0c\u5c06\u9b4f\u5fe0 \u8d24\u523a\u6740\u3002\u662f\u591c\u94c1\u98de\u9f99\u548c\u7389\u7f57\u5239\u5728\u957f\u5b89\u9556\u5c40\u8c08\u8bba\uff0c\u7389\u7f57\u5239\u6015\u4ed6\u5b64\u638c\u96be\u9e23\uff0c\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4e0d\u5165\u864e \u7a74\uff0c\u7109\u5f97\u864e\u5b50\uff01\u6211\u4e0d\u6015\u9b4f\u5fe0\u8d24\u770b\u7834\uff0c\u53ea\u8981\u4ed6\u80af\u51fa\u6765\uff0c\u6211\u672a\u5bb9\u4ed6\u770b\u5f97\u6e05\u695a\uff0c\u5df2\u4e00\u638c\u5c06\u4ed6\u6253\u6740 \u4e86\u3002\u201d\u7389\u7f57\u5239\u662f\u4e2a\u5929\u4e0d\u6015\u5730\u4e0d\u6015\u7684\u5973\u9b54\u5934\uff0c\u7b11\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4e5f\u5165\u5bab\u4fbf\u662f\u3002\u54b1\u4eec\u4e00\u4e2a\u660e \u4eba\uff0c\u4e00\u4e2a\u6697\u4eba\u3002\u4f60\u4e00\u5f97\u624b\uff0c\u6211\u4eec\u4fbf\u7acb\u523b\u95ef\u51fa\u6765\uff01\u201d \u3000\u3000\u4e14\u8bf4\u77f3\u6d69\u5728\u7389\u7f57\u5239\u4e0e\u94c1\u98de\u9f99\u5a01\u80c1\u4e4b\u4e0b\uff0c\u4e0d\u6562\u4e0d\u4f9d\u3002\u7b2c\u4e8c\u65e5\u665a\u4e0a\uff0c\u679c\u7136\u5e26\u4e86\u94c1\u98de\u9f99\u6084\u6084\u8fdb \u5bab\u3002 \u3000\u3000\u9b4f\u5fe0\u8d24\u867d\u7136\u79c1\u901a\u6ee1\u6d32\uff0c\u4f46\u9664\u4e86\u6781\u6709\u9650\u7684\u51e0\u4e2a\u5fc3\u8179\u4e4b\u5916\uff0c\u8fd8\u662f\u4e0d\u613f\u4eba\u77e5\uff0c\u6240\u4ee5\u63a5\u89c1\u6ee1\u6d32\u4f7f \u8005\uff0c\u90fd\u662f\u5728\u66f4\u6df1\u591c\u9759\u4e4b\u65f6\uff0c\u8fde\u6155\u5bb9\u51b2\u4e5f\u4e0d\u8ba9\u77e5\u9053\u3002\u8fd9\u665a\u6b63\u8981\u5c31\u5bdd\uff0c\u542c\u5f97\u77f3\u6d69\u6c42\u89c1\uff0c\u7acb\u523b\u62ab\u8863 \u51fa\u89c1\uff0c\u8d70\u51fa\u623f\u95e8\uff0c\u9065\u89c1\u77f3\u6d69\u548c\u90a3\u6ee1\u6d32\u4f7f\u8005\u7acb\u5728\u5385\u524d\uff0c\u9b4f\u5fe0\u8d24\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u524d\u65e5\u90a3\u6ee1\u6d32 \u4f7f\u8005\u8bf4\u8d77\u8881\u5d07\u7115\u65f6\uff0c\u8bf4\u52aa\u513f\u54c8\u8d64\u5bf9\u6b64\u4eba\u751a\u4e3a\u770b\u91cd\uff0c\u6211\u4e00\u65f6\u597d\u5947\uff0c\u66fe\u53eb\u4ed6\u82e5\u63b3\u4e86\u8881\u5d07\u7115\u5f8c\uff0c\u5148 \u5e26\u6765\u8ba9\u6211\u4e00\u89c1\u3002\u73b0\u5728\u53ea\u6709\u4ed6\u4eec\u4e8c\u4eba\uff0c\u96be\u9053\u8881\u5d07\u7115\u5df2\u7ecf\u8d70\u5f00\uff0c\u6216\u8005\u662f\u56e0\u4e3a\u62d2\u6355\u7ed9\u4ed6\u51fb\u6bd9\u4e86 \u9ebd\uff1f\u201d \u3000\u3000\u9b4f\u5fe0\u8d24\u5fc3\u6709\u6240\u7591\uff0c\u5411\u5c0f\u9ec4\u95e8\u6084\u6084\u5429\u5490\u51e0\u53e5\uff0c\u8d70\u51fa\u5385\u6765\uff0c\u8ddd\u79bb\u6570\u4e08\uff0c\u4fbf\u80cc\u501a\u5899\u58c1\uff0c\u626c\u58f0\u53eb \u9053\uff1a\u201c\u53e4\u9c81\u9c81\uff0c\u53e4\u683c\u56fe\u9c81\uff0c\u5df4\u683c\u7eb3\u7279\u79d1\u56fe\u56fe\uff01\u201d\u9b4f\u5fe0\u8d24\u66fe\u8ddf\u5bdf\u514b\u56fe\u5b66\u8fc7\u51e0\u53e5\u5e94\u916c\u5e38\u7528\u7684\u6ee1\u6d32 \u8bdd\uff0c\u73b0\u5728\u4eff\u6ee1\u6d32\u8bdd\u7684\u8154\u8c03\uff0c\u4e71\u8bf4\u4e00\u6c14\uff0c\u82e5\u7136\u6765\u7684\u771f\u662f\u6ee1\u6d32\u4f7f\u8005\uff0c\u5fc5\u5b9a\u54c8\u54c8\u5927\u7b11\uff0c\u7528\u6ee1\u6d32\u8bdd\u7ea0 \u6b63\u3002 \u3000\u3000\u8981\u77e5\u9b4f\u5fe0\u8d24\u80fd\u628a\u6301\u671d\u653f\uff0c\u5f53\u7136\u4e5f\u6709\u70b9\u5c0f\u806a\u660e\u4e0e\u5e94\u6025\u4e4b\u624d\uff0c\u679c\u7136\u4e00\u8bd5\u4e4b\u4e0b\uff0c\u94c1\u98de\u9f99\u6014\u4e86\u4e00 \u6014\uff0c\u5f85\u9192\u609f\u65f6\uff0c\u9aa4\u7136\u53d1\u96be\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u5411\u9b4f\u5fe0\u8d24\u6025\u65bd\u6251\u51fb\uff0c\u9b4f\u5fe0\u8d24\u5df2\u5978\u7b11\u4e00\u58f0\uff0c\u6309\u52a8\u5899\u58c1\u673a \u5173\uff0c\u9690\u4eba\u590d\u58c1\u7684\u6697\u5ba4\u53bb\u4e86\u3002 \u3000\u3000\u94c1\u98de\u9f99\u4e00\u51fb\u4e0d\u4e2d\uff0c\u77e5\u5df2\u4e2d\u8ba1\uff0c\u5f80\u5916\u4fbf\u95ef\uff0c\u90a3\u77f3\u6d69\u4e5f\u662f\u6e9c\u6ed1\u975e\u5e38\uff0c\u4e58\u7740\u94c1\u98de\u9f99\u5411\u9b4f\u5fe0\u8d24\u6251 \u51fb\u4e4b\u9645\uff0c\u98de\u4e00\u822c\u5954\u51fa\u6bbf\u5916\uff0c\u9ad8\u53eb\u6349\u8d3c\uff01 \u3000\u3000\u970e\u65f6\u95f4\uff0c\u9b4f\u5fe0\u8d24\u7684\u4eb2\u4fe1\u62a4\u536b\u7eb7\u7eb7\u6251\u51fa\uff0c\u94c1\u98de\u9f99\u4e00\u58f0\u6012\u543c\uff0c\u8eab\u8eaf\u4e00\u8f6c\uff0c\u53cd\u638c\u4e00\u6325\uff0c\u556a\u4e24 \u58f0\uff0c\u5355\u638c\u51fb\u6bd9\u4e24\u540d\u4e1c\u5382\u6869\u5934\uff0c\u53e6\u4e00\u540d\u693f\u5934\uff0c\u662f\u5bab\u4e2d\u6709\u540d\u7684\u529b\u58eb\uff0c\u624b\u6325\u56db\u5341\u65a4\u91cd\u7684\u5927\u94c1\uff0c\u8d81\u52bf \u51b2\u5165\uff0c\u8fce\u5934\u6253\u4e0b\uff0c\u94c1\u98de\u9f99\u53c8\u662f\u4e00\u58f0\u5927\u543c\uff0c\u5de6\u638c\u5f80\u4e0a\u4e00\u63a8\uff0c\u90a3\u5927\u94c1\u4e0b\u51fb\u4e4b\u52bf\uff0c\u7adf\u7136\u7ed9\u4ed6\u6321\u7740\uff0c \u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u4ed6\u53f3\u624b\u4e00\u8d77\uff0c\u628a\u90a3\u540d\u536b\u58eb\u5012\u63d0\u8d77\u6765\uff0c\u65cb\u98ce\u4e00\u821e\uff0c\u556a\u54d2\u4e00\u58f0\uff0c\u6454\u5230\u4e24\u53c1 \u4e08\u5916\uff01 \u3000\u3000\u94c1\u98de\u9f99\u638c\u529b\u4e4b\u96c4\uff0c\u6c5f\u6e56\u7b2c\u4e00\uff0c\u6b66\u6797\u65e0\u53cc\uff0c\u8fde\u7ea2\u82b1\u9b3c\u6bcd\u4e5f\u8981\u60e7\u4ed6\u53c1\u5206\uff0c\u536b\u62a4\u9b4f\u5fe0\u8d24\u7684\u4e1c\u5382 \u6869\u5934\uff0c\u51e0\u66fe\u89c1\u8fc7\u5982\u6b64\u9635\u4ed7\uff0c\u4e00\u65f6\u95f4\u7eb7\u7eb7\u5f8c\u9000\u3002\u94c1\u98de\u9f99\u6740\u5f97\u5174\u8d77\uff0c\u5f80\u4eba\u4e1b\u4e2d\u95ef\u53bb\uff0c\u5ffd\u89c1\u9752\u5149\u4e00 \u95ea\uff0c\u91d1\u5203\u5288\u98ce\u4e4b\u58f0\u88ad\u5230\u80cc\u5f8c\uff0c\u94c1\u98de\u9f99\u53cd\u624b\u4e00\u638c\uff0c\u6ca1\u6709\u5288\u7740\uff0c\u6765\u88ad\u7684\u4e43\u662f\u897f\u5382\u7684\u603b\u6559\u5934\u8fde\u57ce \u864e\uff0c\u4ed6\u672c\u6765\u88ab\u9b4f\u5fe0\u8d24\u8c03\u5230\u56db\u5ddd\u53bb\u5f53\u201c\u88ad\u532a\u76d1\u519b\u201d\uff0c\u73b0\u5728\u53c8\u5df2\u8c03\u56de\u5bab\u5185\u3002 \u3000\u3000\u8fde\u57ce\u864e\u7684\u6b66\u529f\u4ec5\u5728\u6155\u5bb9\u51b2\u4e4b\u4e0b\uff0c\u624b\u4f7f\u7684\u4e00\u5bf9\u864e\u5934\u94a9\uff0c\u4e5f\u662f\u6781\u5389\u5bb3\u7684\u5916\u95e8\u5175\u5203\uff0c\u94c1\u98de\u9f99\u7ed9 \u4ed6\u4e00\u638c\uff0c\u7f13\u4e86\u4e00\u7f13\uff0c\u5468\u56f4\u536b\u58eb\uff0c\u53c8\u7eb7\u7eb7\u6251\u4e0a\u3002\u94c1\u98de\u9f99\u594b\u8d77\u795e\u5a01\uff0c\u638c\u5288\u6307\u6233\uff0c\u5374\u662f\u65e0\u6cd5\u8131\u51fa\u91cd \u56f4\u3002\u4f46\u8fde\u57ce\u864e\u4e0e\u536b\u58eb\u4eec\u6015\u4ed6\u638c\u529b\u5389\u5bb3\uff0c\u4e5f\u4e0d\u6562\u6b3a\u8eab\u8fdb\u8feb\uff01 \u3000\u3000\u6b63\u6fc0\u6218\u95f2\uff0c\u5ffd\u542c\u5f97\u4e00\u4eba\u559d\u9053\uff1a\u201c\u4f60\u4eec\u9000\u4e0b\uff0c\u5f85\u6211\u6765\u64d2\u8fd9\u4e2a\u8001\u8d3c\uff01\u201d\u58f0\u5230\u4eba\u5230\uff0c\u94c1\u98de\u9f99\u4e00 \u638c\u5288\u53bb\uff0c\u84e6\u89c9\u4e00\u80a1\u5927\u529b\u53cd\u63a8\u56de\u6765\uff0c\u5012\u9000\u6570\u6b65\uff0c\u770b\u6e05\u695a\u65f6\uff0c\u539f\u6765\u662f\u6155\u5bb9\u51b2\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u7ed9\u4ed6\u638c\u529b\u4e00\u9707\uff0c\u4e5f\u662f\u5f8c\u9000\u6570\u6b65\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u8001\u513f\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff01\u201d\u4e00\u9000\u590d\u8fdb\uff0c\u548c \u94c1\u98de\u9f99\u6076\u6597\u3002\u8f6c\u773c\u4e4b\u95f4\uff0c\u62da\u4e86\u5341\u9980\u5eff\u62db\uff0c\u662f\u4e0d\u5206\u80dc\u8d25\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u662f\u5b98\u4e2d\u7b2c\u4e00\u597d\u624b\uff0c\u5e73\u65e5\u751a\u4e3a\u81ea\u8d1f\u3002\u6240\u4ee5\u4e1c\u897f\u4e24\u5382\u7684\u6869\u5934\u201c\u201c\u6869\u5934\u201d\u5b98\u540d\uff0c\u76f8\u5f53\u65bc \u961f\u957f\u201d\uff0c\u5927\u5185\u7684\u536b\u58eb\uff0c\u7ecf\u4ed6\u4e00\u559d\u4e4b\u5f8c\uff0c\u90fd\u4e0d\u6562\u4e0a\u524d\u52a9\u6218\u3002 \u3000\u3000\u9b4f\u5fe0\u8d24\u4ece\u590d\u58c1\u4e2d\u518d\u8d70\u51fa\u6765\uff0c\u89c1\u6b64\u60c5\u5f62\uff0c\u4e0d\u89c9\u7126\u8e81\uff0c\u5fc3\u9053\uff1a\u201c\u6709\u523a\u5ba2\u4eba\u5bab\uff0c\u8fd8\u8981\u987e\u4ec0\u9ebd\u8eab \u4efd\uff0c\u6446\u4ec0\u9ebd\u67b6\u5b50\uff1f\u201d\u5bf9\u6155\u5bb9\u51b2\u9887\u4e3a\u4e0d\u6ee1\u3002\u559d\u9053\uff1a\u201c\u8fde\u603b\u7ba1\uff0c\u4f60\u4e0a\u53bb\u52a9\u6155\u5bb9\u51b2\u628a\u523a\u5ba2\u62ff\u4e0b\u3002\u201d \u8fde\u57ce\u864e\u4e00\u7eb5\u9075\u547d\uff0c\u62a4\u624b\u94a9\u659c\u91cc\u5288\u8fdb\uff0c\u94c1\u98de\u9f99\u53cd\u624b\u4e00\u593a\uff0c\u7ed9\u6155\u5bb9\u51b2\u683c\u5f00\uff0c\u518d\u817e\u8d77\u4e00\u811a\uff0c\u8fde\u57ce\u864e \u5df2\u95ea\u5230\u8eab\u5f8c\uff0c\u62a4\u624b\u94a9\u5f80\u4e0b\u4e00\u62c9\uff0c\u773c\u770b\u5c31\u8981\u628a\u94c1\u98de\u9f99\u7684\u76ae\u8089\u6495\u5f00\uff0c\u800c\u6155\u5bb9\u51b2\u5de6\u62f3\u53f3\u638c\uff0c\u53c8\u5df2\u6253 \u5230\u80f8\u524d\u3002 \u3000\u3000\u597d\u4e2a\u94c1\u98de\u9f99\uff0c\u4e34\u5371\u4e0d\u4e71\uff0c\u6a2a\u811a\u4e00\u6321\uff0c\u5c06\u6155\u5bb9\u51b2\u7684\u62f3\u638c\u4e00\u9f50\u6c64\u5f00\uff0c\u84e6\u7136\u4e00\u4e2a\u65cb\u8eab\uff0c\u62e2\u6307\u4e00 \u62c2\uff0c\u8fde\u57ce\u864e\u53cc\u94a9\u65b9\u51fa\uff0c\u5ffd\u89c9\u624b\u8155\u4e00\u75db\uff0c\u6025\u5fd9\u8df3\u5f00\uff0c\u53ea\u89c1\u5bf8\u5173\u5c3a\u5904\uff0c\u53c8\u7ea2\u53c8\u80bf\uff0c\u7adf\u5982\u7ed9\u706b\u94c1\u70d9 \u6210\u4e86\u4e00\u4e2a\u6307\u5370\uff1a\u5927\u5403\u4e00\u60ca\uff0c\u4e0d\u6562\u5077\u88ad\uff0c\u53cc\u94a9\u4e00\u7acb\uff0c\u62a4\u8eab\u5f85\u654c\u3002\u6155\u5bb9\u51b2\u4e24\u8bb0\u51b2\u62f3\uff0c\u5c06\u94c1\u98de\u9f99\u62db \u6570\u5f15\u8fc7\u4e00\u8fb9\u3002\u8fde\u57ce\u864e\u5b9a\u4e86\u5b9a\u795e\uff0c\u8fd9\u624d\u628a\u53cc\u94a9\u5c55\u5f00\uff0c\u4ece\u65c1\u4fa7\u51fb\uff01 \u3000\u3000\u9b4f\u5fe0\u8d24\u559d\u9053\uff1a\u201c\u4f60\u4eec\u52a1\u8981\u628a\u8fd9\u523a\u5ba2\u751f\u64d2\u3002\u770b\u4ed6\u662f\u4f55\u4eba\u6307\u4f7f\uff1f\u201d\u628a\u624b\u4e00\u6325\uff0c\u6869\u5934\u536b\u58eb\u5728\u56db \u5468\u5e03\u6210\u4e86\u94dc\u5899\u94c1\u58c1\uff0c\u5e94\u4fee\u9633\u65b0\u62db\u7eb3\u4e86\u4e24\u4e2a\u9ad8\u624b\uff0c\u4e5f\u6765\u52a9\u6218\u3002\u94c1\u98de\u9f99\u6597\u6155\u5bb9\u51b2\u4e00\u4eba\u5df2\u611f\u5403\u529b\uff0c \u4ee5\u4e00\u654c\u56db\uff0c\u66f4\u662f\u4e0d\u582a\u3002\u770b\u5f62\u52bf\u51b2\u53c8\u51b2\u4e0d\u51fa\u4e22\u3002\u53ea\u6709\u62da\u547d\u652f\u6491\uff0c\u7b49\u7389\u7f57\u5239\u6765\u63f4\u3002\u504f\u504f\u7389\u7f57\u5239\u53c8 \u6beb\u65e0\u5f71\u8ff9\u3002\u4e0d\u77e5\u9053\u90a3\u91cc\u53bb\u4e86\u3002 \u3000\u3000\u518d\u8bf4\u5ba2\u5a09\u5a77\u90a3\u65e5\u4ece\u897f\u5c71\u56de\u6765\u4e4b\u5f8c\uff0c\u5fc3\u4e2d\u90c1\u90c1\uff0c\u9547\u65e5\u65e0\u6b22\u3002\u60f3\u8d77\u4e86\u7389\u7f57\u5239\u7684\u8bdd\uff0c\u4e0d\u77e5\u662f\u771f \u662f\u5047\u3002\u8fd9\u65e5\u72ec\u5750\u6df1\u5bab\uff0c\u601d\u6f6e\u6d6a\u6d8c\uff0c\u4e00\u5ffd\u513f\u60f3\u9053\uff1a\u7389\u7f57\u5239\u662f\u6b66\u6797\u4e2d\u7684\u6210\u540d\u4eba\u7269\uff0c\u60f3\u6765\u4e0d\u4f1a\u4e71 \u8bf4\u3002\u82e5\u7136\u6211\u7684\u5e08\u7236\u771f\u4e2a\u6b7b\u4e86\uff0c\u6211\u8fd8\u7559\u5728\u5bab\u4e2d\u505a\u751a\uff1f\u4e00\u5ffd\u513f\u60f3\u9053\uff1a\u6211\u6bcd\u4eb2\u53ea\u6709\u6211\u4e00\u4e2a\u5973\u513f\uff0c\u5bab \u4e2d\u53c8\u662f\u5371\u673a\u9690\u4f0f\uff0c\u5979\u4e0e\u6211\u76f8\u4f9d\u4e3a\u547d\uff0c\u6211\u53c8\u600e\u5fcd\u4e0e\u5979\u5206\u79bb\uff1f\u6b63\u81ea\u601d\u91cf\u4e0d\u5b9a\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u5728\u7a97\u5916 \u8f7b\u8f7b\u6572\u4e86\u4e24\u4e0b\uff0c\u5ba2\u5a09\u5a77\u95ee\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u7a97\u5916\u4e00\u4e2a\u4f4e\u6c89\u7684\u58f0\u97f3\u5e94\u9053\uff1a\u201c\u4e0d\u8981\u4f5c\u58f0\uff0c\u662f\u6211\uff0c\u5feb \u5feb\u5f00\u95e8\uff01\u201d \u3000\u3000\u8fd9\u58f0\u97f3\u597d\u719f\uff0c\u5ba2\u5a09\u5a77\u6014\u4e86\u4e00\u6014\uff0c\u4f4e\u4f4e\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u7389\u7f57\u5239\uff1f\u201d\u95e8\u5916\u7684\u4eba\u7b11\u9053\uff1a\u201c\u662f\u5440\uff01 \u6211\u6709\u4e8b\u6c42\u4f60\u6765\u4e86\uff01\u201d \u3000\u3000\u6309\u8bf4\u7389\u7f57\u5239\u66fe\u4e0e\u7ea2\u82b1\u9b3c\u6bcd\u4e3a\u654c\uff0c\u53c8\u5174\u5ba2\u9b4f\u4f5c\u5bf9\uff0c\u4e43\u662f\u5ba2\u5a09\u5a77\u7684\u201c\u654c\u4eba\u201d\uff0c\u53ef\u662f\u5ba2\u5a09\u5a77\u4e0d \u77e5\u600e\u7684\uff0c\u5bf9\u5979\u6beb\u65e0\u201c\u654c\u610f\u201d\uff0c\u5c24\u5176\u662f\u524d\u4e24\u65e5\u4e0e\u5979\u63a5\u89e6\u4e4b\u5f8c\uff0c\u66f4\u89c9\u5f97\u7389\u7f57\u5239\u6709\u4e00\u79cd\u5f02\u4e4e\u5e38\u4eba\u7684 \u5438\u5f15\u529b\uff0c\u5979\u90a3\u8c6a\u8fc8\u7684\u6027\u683c\uff0c\u723d\u6717\u7684\u7b11\u58f0\uff0c\u4f3c\u4e4e\u662f\u4ece\u53e6\u4e00\u4e2a\u4e16\u754c\u4e2d\u6765\u7684\u4eba\uff01\u5c24\u5176\u5f53\u5ba2\u5a09\u5a77\u62ff\u5979 \u4e0e\u5b98\u4e2d\u90a3\u4e9b\u4eba\u76f8\u6bd4\u7684\u65f6\u5019\uff0c\u8fd9\u79cd\u611f\u89c9\u4e0e\u5370\u8c61\uff0c\u4fbf\u66f4\u9c9c\u660e\u3002\u5ba2\u5a09\u5a77\u53c8\u89c9\u5f97\u5979\u5728\u67d0\u4e9b\u5730\u65b9\uff0c\u6709\u70b9 \u4f3c\u81ea\u5df1\u7684\u5e08\u7236\uff0c\u4f46\u6bd4\u81ea\u5df1\u7684\u5e08\u7236\uff0c\u66f4\u4e3a\u521a\u5f3a\u53ef\u7231\uff0c\u751a\u81f3\u7389\u7f57\u5239\u7684\u751f\u6d3b\uff0c\u4e5f\u6784\u6210\u4e86\u5ba2\u5a09\u5a77\u5e7b\u60f3 \u7684\u4e00\u90e8\u5206\u3002\u90a3\u79cd\u98ce\u9ad8\u6708\u5f02\uff0c\u4e00\u5251\u5f80\u6765\uff0c\u95ef\u8361\u6c5f\u6e56\uff0c\u7eb5\u6a2a\u7eff\u6797\u7684\u751f\u6d3b\uff0c\u5bf9\u65bc\u5728\u6df1\u5bab\u4e2d\u7684\u5ba2\u5a09 \u5a77\uff0c\u7b80\u76f4\u662f\u4e00\u79cd\u8bf1\u60d1\uff0c\u5ba2\u5a09\u5a77\u6bcf\u5f53\u60f3\u8d77\u4e86\u7389\u7f57\u5239\u65f6\uff0c\u4e5f\u5e38\u8054\u60f3\u5230\u5916\u9762\u65e0\u9650\u5e7f\u9614\u7684\u4e16\u754c\uff0c\u8054\u60f3 \u5230\u90a3\u4e9b\u5e26\u7740\u4f20\u5947\u8272\u5f69\u7684\u6c5f\u6e56\u4eba\u7269\u3002\u5ba2\u5a09\u5a77\u5bf9\u65bc\u7389\u7f57\u5239\u4e0d\u4ec5\u662f\u7fa1\u6155\uff0c\u7b80\u76f4\u662f\u6709\u70b9\u503e\u5012\u4e86\u3002 \u3000\u3000\u4eca\u665a\uff0c\u7389\u7f57\u5239\u4f4e\u6c89\u7684\u7b11\u58f0\uff0c\u53c8\u5728\u5979\u7684\u8033\u8fb9\u54cd\u8d77\u6765\u4e86\uff0c\u8fd9\u58f0\u97f3\uff0c\u8fd9\u5e26\u7740\u547d\u4ee4\u8bed\u6c14\u7684\u8bed\u97f3\uff0c \u4ee4\u5ba2\u5a09\u5a77\u611f\u5230\u6709\u4e00\u80a1\u4e0d\u80fd\u6297\u62d2\u7684\u529b\u91cf\uff0c\u5979\u6beb\u4e0d\u8e0c\u8e87\u7684\u6253\u5f00\u4e86\u95e8\uff0c\u628a\u5979\u7684\u201c\u654c\u4eba\u201d\u653e\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u7389\u7f57\u5239\u50cf\u4e00\u80a1\u98ce\u4f3c\u7684\u8dd1\u4e86\u8fdb\u6765\uff0c\u968f\u624b\u628a\u623f\u95e8\u63a9\u4e0a\uff0c\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u53c8\u5077\u8fdb\u5bab\u6765\uff1f\u6211 \u7684\u900d\u9065\u8f66\u5c0f\u7687\u5e1d\u8981\u53bb\u4e86\uff0c\u53ef\u6ca1\u529e\u6cd5\u628a\u4f60\u518d\u5e26\u51fa\u5bab\u4e86\u3002\u201d\u7389\u7f57\u5239\u5657\u55e4\u4e00\u7b11\uff0c\u5ffd\u800c\u7aef\u8083\u9762\u5bb9\uff0c\u4f4e \u58f0\u8bf4\u9053\uff1a\u201c\u5ba2\u5a09\u5a77\uff0c\u6211\u8981\u95ee\u4f60\u4e00\u53e5\u8bdd\uff01\u201d \u3000\u3000\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u8bf7\u8bf4\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u613f\u4e0d\u613f\u6ee1\u5dde\u9791\u5b50\u6253\u8fdb\u5173\u6765\uff0c\u613f\u4e0d\u613f\u4ed6\u4eec\u628a\u54b1\u4eec\u6c49 \u4eba\u7684\u6c5f\u5c71\u5360\u53bb\uff01\u201d\u5ba2\u5a09\u5a77\u8df3\u8d77\u6765\u9053\uff1a\u201c\u8fd9\u8fd8\u7528\u95ee\u5417\uff1f\u5f53\u7136\u4e0d\u613f\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u597d\uff0c\u4f60\u65e2\u7136 \u4e0d\u613f\uff0c\u90a3\u9ebd\u5c31\u66ff\u6211\u505a\u4e24\u4ef6\u4e8b\uff01\u201d \u3000\u3000\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u4f60\u8bf4\u5427\uff0c\u8981\u6211\u505a\u5f97\u5230\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u7b2c\u4e00\u4ef6\u662f\u66ff\u6211\u628a\u9b4f\u5fe0\u8d24\u523a\u6740\u4e86\uff01\u201d \u5ba2\u5a09\u5a77\u60ca\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u5ba2\u5a09\u5a77\u867d\u7136\u4e0d\u77e5\u9053\u81ea\u5df1\u4e43\u662f\u9b4f\u5fe0\u8d24\u7684\u79c1\u751f\u5973\u513f\uff0c\u4f46\u9b4f\u5fe0\u8d24\u5bf9\u5979\u5341 \u5206\u5ba0\u7231\uff0c\u5979\u5374\u611f\u89c9\u5f97\u5230\u3002\u800c\u4e14\u9b4f\u5fe0\u8d24\u548c\u4ed6\u6bcd\u4eb2\u5341\u5206\u8981\u597d\uff0c\u5e38\u5e38\u805a\u5728\u5bc6\u5ba4\u8c08\u8bdd\uff0c\u5979\u4e5f\u662f\u77e5\u9053 \u7684\u3002 \u3000\u3000\u7389\u7f57\u5239\u89c1\u5979\u9762\u8272\u60ca\u7591\uff0c\u5728\u5979\u8033\u8fb9\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4ed6\u4fbf\u662f\u901a\u756a\u5356\u56fd\u7684\u6c49\u5978\uff01\u201d\u5ba2\u5a09\u5a77\u8eab\u8eaf\u98a4 \u6218\uff0c\u7389\u7f57\u5239\u90a3\u79cd\u65a9\u9489\u622a\u94c1\u7684\u8bed\u8c03\uff0c\u4ee4\u5979\u4e0d\u80fd\u4e0d\u4fe1\uff0c\u4e0d\u7981\u95ee\u9053\uff1a\u201c\u8fd8\u6709\u8c01\u5417\uff1f\u201d\u5979\u5341\u5206\u5bb3\u6015\u6bcd \u4eb2\u4e5f\u548c\u9b4f\u5fe0\u8d24\u540c\u8c0b\uff0c\u5bd2\u610f\u76f4\u900f\u5fc3\u5934\uff0c\u58f0\u97f3\u4e5f\u98a4\u6296\u4e86\u3002 \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u8fd8\u6709\u8c01\u6211\u4e5f\u4e0d\u5c3d\u77e5\u9053\uff0c\u6211\u53ea\u77e5\u9053\u8fd8\u6709\u4e00\u4e2a\u5e94\u4fee\u9633\u3002\u5e94\u4fee\u9633\u7684\u6b66\u529f\u5728\u4f60\u4e4b \u4e0a\uff0c\u4f60\u4e0d\u5fc5\u6253\u8349\u60ca\u86c7\uff0c\u8ba9\u6211\u4eec\u6765\u6536\u62fe\u4ed6\u5427\u3002\u201d \u3000\u3000\u5ba2\u5a09\u5a77\u900f\u4e86\u53e3\u6c14\uff0c\u95ee\u9053\uff1a\u201c\u7b2c\u4e8c\u4ef6\u4e8b\u53c8\u662f\u4ec0\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u7684\u4e49\u7236\u88ab\u4ed6\u4eec\u56f4\u56f0\u5728 \u524d\u9762\u9752\u9633\u5bab\u4e2d\uff0c\u4f60\u8bbe\u6cd5\u5c06\u4ed6\u6551\u51fa\u6765\uff01\u201d \u3000\u3000\u539f\u6765\u7389\u7f57\u5239\u8d81\u7740\u77f3\u6d69\u5e26\u94c1\u98de\u9f99\u5165\u5bab\u7684\u5f53\u513f\uff0c\u4e5f\u6697\u6697\u8ddf\u5165\uff0c\u5979\u5230\u9b4f\u5fe0\u8d24\u6240\u5c45\u7684\u9752\u9633\u5bab\u65f6\uff0c \u94c1\u98de\u9f99\u5df2\u548c\u6155\u5bb9\u51b2\u8fde\u57ce\u864e\u6253\u4e86\u8d77\u6765\u3002\u7389\u7f57\u5239\u4e00\u770b\u4e0b\u9762\u5f62\u52bf\uff0c\u5fc3\u9053\uff1a\u201c\u7cdf\u4e86\uff0c\u6211\u53ea\u9053\u4e49\u7236\u4e00\u4e3e \u624b\u4fbf\u80fd\u5c06\u90a3\u5978\u9609\u9664\u6389\uff0c\u8c01\u77e5\u53c8\u88ab\u5978\u9605\u9003\u8131\uff0c\u53cd\u800c\u628a\u5bab\u4e2d\u4f8d\u536b\u5168\u90fd\u60ca\u52a8\uff0c\u5c31\u662f\u81ea\u5df1\u4e0b\u53bb\uff0c\u4e5f\u53ea \u80fd\u5e2e\u52a9\u4e49\u7236\u591a\u62b5\u5fa1\u4e00\u4e9b\u65f6\u5019\uff0c\u8981\u9003\u51fa\u53bb\uff0c\u8fd9\u53ef\u662f\u4e07\u4e07\u4e0d\u80fd\uff01\u201d\u7126\u6025\u4e4b\u6781\uff0c\u84e6\u7136\u60f3\u8d77\u4e86\u5ba2\u5a09 \u5a77\uff0c\u60f3\u8d77\u4e86\u5ba2\u5a09\u5a77\u90a3\u665a\u548c\u5979\u6bcd\u4eb2\u7684\u4e89\u8bba\u3002\u5fc3\u60f3\uff1a\u770b\u90a3\u5ba2\u5a09\u5a77\u7684\u8a00\u884c\u4e3e\u6b62\uff0c\u548c\u5979\u6bcd\u4eb2\u5927\u5927\u4e0d \u540c\uff0c\u6211\u59d1\u4e14\u53bb\u8bd5\u4e00\u8bd5\u3002 \u3000\u3000\u5ba2\u5a09\u5a77\u542c\u4e86\u7389\u7f57\u5239\u6240\u6c42\u7684\u7b2c\u4e8c\u4ef6\u4e8b\uff0c\u53c8\u662f\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u6211\u672c\u4e8b\u4f4e\u5fae\uff0c\u5982\u4f55\u80fd\u6551\u4f60\u7684\u4e49 \u7236\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6597\u667a\u4e0d\u6597\u529b\uff0c\u4f60\u53ea\u8981\u8bbe\u6cd5\u628a\u5bab\u4e2d\u7684\u51e0\u4e2a\u9ad8\u624b\u5f15\u5f00\u4fbf\u884c\u3002\u201d\u5ba2\u5a09\u5a77\u60f3\u4e86\u4e00 \u60f3\uff0c\u8ba1\u4e0a\u5fc3\u5934\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u542c\u59d0\u59d0\u7684\u8bdd\uff0c\u59d1\u4e14\u8bd5\u5b83\u4e00\u8bd5\u3002\u201d\u5728\u7389\u7f57\u5239\u8033\u8fb9\u8bf4\u4e86\u51e0\u53e5\uff0c\u7389\u7f57 \u5239\u7b11\u9053\uff1a\u201c\u597d\uff0c\u5c31\u8fd9\u6837\u529e\u5427\uff0c\u4f60\u771f\u662f\u6211\u7684\u597d\u59b9\u59b9\u3002\u201d\u5728\u5979\u989d\u4e0a\u8f7b\u8f7b\u4eb2\u4e86\u4e00\u4e0b\uff0c\u201d\u7acb\u523b\u7a7f\u7a97\u98de \u51fa\uff0c\u5ba2\u5a09\u5a77\u51b2\u53e3\u53eb\u4e86\u4e00\u53e5\u201c\u59d0\u59d0\u201d\uff0c\u6b63\u81ea\u4e0d\u597d\u610f\u601d\uff0c\u5ffd\u542c\u7389\u7f57\u5239\u4e5f\u79f0\u5979\u201c\u59b9\u59b9\u201d\uff0c\u8fdc\u4eb2\u4e86\u5979 \u4e00\u4e0b\uff0c\u5fc3\u4e2d\u751c\u4e1d\u4e1d\u7684\uff0c\u4ec0\u9ebd\u4e5f\u613f\u66ff\u7389\u7f57\u5239\u505a\uff0c\u81ea\u5df1\u4e5f\u83ab\u540d\u5176\u5999\uff0c\u4e3a\u4ec0\u9ebd\u7389\u7f57\u5239\u5bf9\u5979\u7684\u5438\u5f15\u529b \u5982\u6b64\u4e4b\u5927\u3002 \u3000\u3000\u518d\u8bf4\u94c1\u98de\u9f99\u82e6\u6597\u56db\u540d\u9ad8\u624b\uff0c\u521d\u65f6\u8fd8\u80fd\u4ee5\u638c\u529b\u81ea\u4fdd\uff0c\u6e10\u6e10\u529b\u7aed\u7b4b\u75b2\uff0c\u9669\u62db\u5c61\u89c1\uff0c\u7389\u7f57\u5239\u4ecd \u4e0d\u89c1\u6765\u3002\u5fc3\u9053\uff1a\u4e0d\u9053\u6211\u4eca\u65e5\u6bd9\u547d\u65bc\u6b64\uff0c\u6211\u6b7b\u4e5f\u5f97\u628a\u90a3\u9609\u7684\u9634\u8c0b\u63ed\u9732\uff01\u8fd9\u65f6\u6155\u5bb9\u51b2\u770b\u770b\u4fbf\u5c06\u5f97 \u624b\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u5288\u9762\u4e00\u62f3\uff0c\u5c06\u94c1\u98de\u9f99\u7684\u62db\u6570\u5f15\u5f00\uff0c\u5de6\u624b\u9a88\u6307\u7167\u4ed6\u7684\u80c1\u4e0b\u5173\u5143\u7a74\u4e00\u70b9\uff0c\u5ffd\u542c\u5f97 \u94c1\u98de\u9f99\u5927\u53eb\u9053\uff1a\u201c\u9b4f\u5fe0\u8d24\u901a\u756a\u5356\u56fd\uff0c\u4e07\u6b7b\u4e0d\u8db3\u4ee5\u853d\u5176\u8f9c\uff0c\u4f60\u4eec\u4e3a\u864e\u4f5c\u4f25\uff0c\u5c06\u6765\u4e5f\u96be\u9003\u516c \u9053\uff01\u201d\u6155\u5bb9\u51b2\u84e6\u5403\u4e00\u60ca\uff0c\u624b\u6307\u659c\u659c\u5f80\u5916\u4e00\u6ed1\u3002\u9b4f\u5fe0\u8d24\u5927\u6012\u559d\u9053\uff1a\u201c\u8d3c\u5b50\u80e1\u8bf4\uff0c\u628a\u4ed6\u51fb\u6740\u4e86 \u5427\uff01\u201d \u3000\u3000\u6155\u5bb9\u51b2\u7565\u4e00\u72b9\u7591\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u706b\uff0c\u706b\uff01\u201d\u9b4f\u5fe0\u8d24\u5403\u4e86\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u5feb\u51fa\u53bb \u770b\uff0c\u662f\u90a3\u91cc\u8d77\u706b\uff1f\u201d\u8bdd\u58f0\u672a\u505c\uff0c\u5ffd\u5730\u4e00\u58f0\u60e8\u5389\u7684\u53eb\u558a\u63a0\u8fc7\u591c\u7a7a\uff1a\u201c\u6551\u547d\u5440\uff0c\u6551\u547d\uff01\u201d\u9b4f\u5fe0\u8d24 \u5fc3\u60ca\u80c6\u6218\uff0c\u8fd9\u6b63\u662f\u5ba2\u5a09\u5a77\u7684\u547c\u6551\u4e4b\u58f0\u3002\u8fd1\u95e8\u53e3\u4e86\u671b\u7684\u536b\u58eb\u62a5\u9053\uff1a\u201c\u5949\u5723\u592b\u4eba\u5bab\u4e2d\u8d77\u706b\uff01\u201d \u3000\u3000\u7d27\u63a5\u7740\u5ba2\u5a09\u5a77\u51c4\u5389\u7684\u53eb\u58f0\u4e4b\u5f8c\uff0c\u5916\u9762\u53c8\u4f20\u6765\u4e00\u58f0\u957f\u7b11\uff0c\u63a5\u7740\u662f\u56db\u9762\u5c4b\u74e6\u629b\u63b7\u4e4b\u58f0\uff0c\u77f3\u6d69 \u7ad9\u5728\u9b4f\u5fe0\u8d24\u4e4b\u5f8c\uff0c\u987f\u65f6\u9762\u8272\u7070\u767d\uff0c\u60e8\u65e0\u4eba\u8272\uff0c\u98a4\u58f0\u53eb\u9053\uff1a\u201c\u662f\u3001\u662f\u7389\u2026\u2026\u7389\u2026\u2026\u7389\u7f57\u5239\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u66fe\u4e24\u6b21\u5927\u95f9\u7687\u5bab\uff0c\u9b4f\u5fe0\u8d24\u6df1\u77e5\u5979\u7684\u5389\u5bb3\uff0c\u800c\u4e14\u542c\u5916\u9762\u58f0\u54cd\uff0c\u4f3c\u4e4e\u6765\u7684\u8fd8\u4e0d\u6b62\u4e00 \u4eba\uff0c\u5413\u5f97\u8fde\u5fd9\u53eb\u9053\uff1a\u201c\u5feb\u5206\u51fa\u4eba\u53bb\u6551\u5949\u5723\u592b\u4eba\uff01\u201d \u3000\u3000\u8fd9\u4e9b\u90fd\u662f\u5ba2\u5a09\u5a77\u4e0e\u7389\u7f57\u5239\u7684\u6545\u5f04\u7384\u865a\u3002\u5ba2\u5a09\u5a77\u81ea\u5df1\u653e\u706b\uff0c\u81ea\u5df1\u53eb\u558a\uff0c\u88c5\u4f5c\u7ed9\u4eba\u8ffd\u6740\u7684\u6837 \u5b50\uff1a\u800c\u7389\u7f57\u5239\u5219\u4ed7\u7740\u7edd\u5999\u7684\u8f7b\u529f\uff0c\u5728\u7409\u7483\u74e6\u4e0a\uff0c\u4e1c\u63b7\u4e00\u7247\u5c4b\u74e6\uff0c\u897f\u629b\u4e00\u4e2a\u7816\u5934\uff0c\u542c\u8d77\u6765\u5c31\u597d \u4f3c\u56db\u9762\u90fd\u6709\u654c\u4eba\u3002\u9b4f\u5fe0\u8d24\u6240\u4f4f\u7684\u9752\u9633\u5bab\u548c\u5ba2\u6c0f\u6240\u4f4f\u7684\u4e73\u5a18\u5e9c\u76f8\u8ddd\u751a\u8fd1\uff0c\u706b\u5149\u878d\u878d\uff0c\u89e6\u76ee\u60ca \u5fc3\uff0c\u66f4\u52a0\u4e0a\u5ba2\u5a09\u5a77\u9ad8\u53eb\u6551\u547d\u4e4b\u58f0\uff0c\u548c\u7389\u7f57\u5239\u6ee1\u542b\u6740\u6c14\u7684\u7b11\u58f0\uff0c\u6742\u6210\u4e00\u7247\uff0c\u66f4\u52a0\u5f3a\u4e86\u6050\u6016\u7684\u6c14 \u6c1b\u3002\u56f4\u5835\u94c1\u98de\u9f99\u7684\u6869\u5934\u536b\u58eb\uff0c\u5df2\u6709\u4e00\u534a\u51b2\u51fa\u95e8\u53bb\u3002\u6155\u5bb9\u51b2\u865a\u6643\u4e00\u62f3\uff0c\u4e5f\u5954\u51fa\u95e8\u5916\u3002 \u3000\u3000\u94c1\u98de\u9f99\u7cbe\u795e\u5927\u632f\uff0c\u547c\u547c\u4e24\u638c\uff0c\u628a\u8fde\u57ce\u864e\u4e0e\u53e6\u4e00\u9ad8\u624b\u8feb\u5f00\uff0c\u9aa4\u7136\u62d4\u51fa\u4e00\u6839\u5315\u9996\uff0c\u5411\u6155\u5bb9\u51b2 \u80cc\u5fc3\u4e00\u63b7\uff0c\u9ad8\u53eb\u9053\uff1a\u201c\u6155\u5bb9\u8d3c\u5b50\uff0c\u63a5\u8fd9\u4e2a\uff01\u201d\u6155\u5bb9\u51b2\u5934\u4e5f\u4e0d\u56de\uff0c\u53cd\u624b\u4e00\u6349\uff0c\u5c06\u5315\u9996\u63a5\u7740\uff0c\u6b63 \u60f3\u8fd8\u63b7\uff0c\u5ffd\u542c\u5f97\u94c1\u98de\u9f99\u53c8\u53eb\u9053\uff1a\u201c\u4f60\u597d\u597d\u770b\u6e05\u695a\u4e86\uff01\u201d\u6155\u5bb9\u51b2\u5fc3\u5ff5\u4e00\u52a8\uff0c\u968f\u624b\u5c06\u5315\u9996\u653e\u4eba\u6697 \u5668\u56ca\u4e2d\uff0c\u7eb5\u8eab\u51fa\u95e8\uff0c\u76f4\u5954\u5ba2\u6c0f\u7684\u4e73\u5a18\u5e9c\u3002 \u3000\u3000\u9b4f\u5fe0\u8d24\u53c8\u53eb\u9053\uff1a\u201c\u8fde\u57ce\u864e\uff0c\u4f60\u4eec\u5c06\u8fd9\u8001\u513f\u4e71\u5200\u65a9\u6b7b\u7b97\u4e86\u3002\u201d\u5269\u4e0b\u7684\u4e00\u5c0f\u534a\u536b\u58eb\uff0c\u5200\u67aa\u7eb7 \u4e3e\uff0c\u56db\u9762\u622e\u6765\uff0c\u94c1\u98de\u9f99\u4e00\u58f0\u5927\u559d\uff0c\u75be\u7684\u6293\u7740\u4e00\u540d\u536b\u58eb\u5f8c\u5fc3\uff0c\u5411\u5916\u4fbf\u6454\uff0c\u90a3\u536b\u58eb\u5e9e\u5927\u7684\u8eab\u8eaf\u4ece \u5200\u67aa\u6797\u7acb\u7684\u4e0a\u7a7a\u98de\u8fc7\uff0c\u4f17\u4eba\u53d1\u4e00\u58f0\u558a\uff0c\u6025\u6025\u95ea\u5f00\uff0c\u94c1\u98de\u9f99\u54c8\u54c8\u5927\u7b11\uff0c\u4f9d\u6cd5\u70ae\u5236\uff0c\u8fde\u63b7\u53c1\u540d\u6869 \u5934\uff0c\u8fde\u57ce\u864e\u5927\u6012\uff0c\u53cc\u94a9\u6025\u65ab\u3002\u84e6\u5730\u91cc\u4e00\u58f0\u957f\u7b11\uff0c\u7389\u7f57\u5239\u7a81\u7136\u4ece\u7409\u7483\u74e6\u9762\u8df3\u4e86\u4e0b\u6765\uff0c\u5728\u534a\u7a7a\u8fde \u4eba\u5e26\u5251\u8f6c\u4e86\u4e2a\u5927\u5706\u5708\uff0c\u5b9b\u5982\u4e00\u56e2\u94f6\u8272\u7684\u5149\u73af\uff0c\u4ece\u7a7a\u98de\u964d\uff0c\u62a2\u8fc7\u6765\u7684\u51e0\u540d\u6869\u5934\u536b\u58eb\uff0c\u7ed9\u5251\u5149\u4e00 \u6c64\uff0c\u624b\u65ad\u8db3\u6298\uff0c\u7eb7\u7eb7\u95ea\u8ba9\uff01 \u3000\u3000\u9b4f\u5fe0\u8d24\u5927\u5403\u4e00\u60ca\uff0c\u77f3\u6d69\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u5feb\u8eb2\uff01\u201d\u9b4f\u5fe0\u8d24\u8eb2\u8fdb\u6697\u5ba4\uff0c\u77f3\u6d69\u6025\u5fd9\u4e5f\u8ddf\u4e86\u8fdb \u53bb\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u56f4\u653b\u94c1\u98de\u9f99\u7684\u867d\u7136\u8fd8\u6709\u5341\u9980\u5eff\u4eba\uff0c\u5df2\u90fd\u6298\u4e86\u9510\u6c14\u3002\u7389\u7f57\u5239\u5c55\u5f00\u72ec\u95e8\u5251\u6cd5\uff0c\u62db \u62db\u5feb\uff0c\u62db\u62db\u8fa3\uff0c\u95ea\u7535\u60ca\u98d5\uff0c\u6070\u5982\u5f69\u8776\u7a7f\u82b1\uff0c\u5de6\u4e00\u5251\uff0c\u53f3\u4e00\u5251\uff0c\u5251\u5931\u6240\u523a\uff0c\u90fd\u662f\u654c\u4eba\u7684\u5173\u8282\u8981 \u5bb3\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u5df2\u6709\u4e94\u516d\u540d\u536b\u58eb\u4e2d\u5251\u5012\u5730\uff0c\u58f0\u58f0\u60e8\u53f7\uff0c\u7389\u7f57\u5239\u559d\u9053\uff1a\u201c\u6321\u6211\u8005\u6b7b\uff0c\u8ba9\u6211\u8005 \u751f\uff01\u201d\u957f\u7b11\u58f0\u4e2d\uff0c\u51b2\u5f00\u4e86\u4e00\u6761\u8840\u8def\uff0c\u6740\u4eba\u91cd\u56f4\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u8fde\u57ce\u864e\u5174\u5e94\u4fee\u9633\u65b0\u62db\u8bf7\u6765\u7684\u4e24\u540d\u9ad8\u624b\u4e5f\u6709\u70b9\u614c\u4e86\uff01\u7389\u7f57\u5239\u633a\u5251\u731b\u6251\uff0c\u4e00\u62db\u201c\u7389 \u5973\u7a7f\u9488\u201d\uff0c\u75be\u523a\u8fde\u57ce\u864e\u80cc\u5f8c\u7684\u201c\u9b42\u95e8\u7a74\u201d\uff0c\u8fde\u57ce\u864e\u53cc\u94a9\u4e00\u526a\uff0c\u94c1\u98de\u9f99\u5ffd\u7136\u5927\u559d\u4e00\u58f0\uff0c\u5288\u624b\u628a \u94a9\u593a\u8fc7\uff0c\u4e00\u94a9\u94a9\u53bb\uff0c\u53ea\u542c\u5f97\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u8fde\u57ce\u864e\u8863\u895f\u6495\u4e0b\u4e00\u5927\u5757\uff01\u4f46\u8fde\u57ce\u864e\u4e5f\u9003\u51fa\u53bb \u4e86\u3002 \u3000\u3000\u9ad8\u624b\u9041\u9003\uff0c\u4f17\u536b\u58eb\u65e0\u5fc3\u604b\u6218\uff0c\u7389\u7f57\u5239\u8fd0\u5251\u5982\u98ce\uff0c\u76f4\u6740\u51fa\u53bb\uff0c\u94c1\u98de\u9f99\u62f3\u6253\u638c\u5288\uff0c\u72b9\u5982\u5de8\u65a7 \u94c1\uff0c\u66f4\u662f\u9510\u4e0d\u53ef\u5f53\uff0c\u536b\u58eb\u4eec\u90a3\u91cc\u6562\u8ffd\u3002\u7389\u7f57\u5239\u719f\u6089\u5bab\u4e2d\u9053\u8def\uff0c\u7247\u523b\u4e4b\u5f8c\u5df2\u5e26\u4e86\u94c1\u98de\u9f99\u95ef\u51fa\u4e86 \u795e\u6b66\u95e8\uff0c\u7ffb\u8fc7\u91cf\u5c71\u53bb\u4e86\u3002 \u3000\u3000\u518d\u8bf4\u6155\u5bb9\u51b2\u7b49\u8d76\u53bb\u6551\u706b\uff0c\u53ea\u89c1\u5ba2\u5a09\u5a77\u62ab\u5934\u6563\u53d1\uff0c\u5de6\u80a9\u67d3\u8840\uff0c\u6155\u5bb9\u51b2\u5927\u5403\u4e00\u60ca\uff0c\u5374\u4e0d\u89c1\u654c \u4eba\uff0c\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u523a\u5ba2\u5df2\u7ecf\u8d70\u4e86\uff0c\u6211\u7ed9\u90a3\u5973\u9b54\u5934\u523a\u4e86\u4e00\u5251\uff0c\u5e78\u597d\u53d7\u4f24\u4e0d\u91cd\uff0c\u6551\u706b\u8981\u7d27\uff01\u201d\u6155 \u5bb9\u51b2\u4e00\u770b\uff0c\u5fc3\u91cc\u8d77\u7591\uff0c\u6697\u60f3\u9053\uff1a\u201c\u7389\u7f57\u5239\u5251\u6cd5\u4f55\u7b49\u5389\u5bb3\uff0c\u4e00\u51fa\u624b\u4fbf\u662f\u523a\u4eba\u5173\u8282\u7a74\u9053\uff0c\u96be\u9053\u5979 \u5bf9\u8fd9\u5c0f\u4e2b\u5934\u5374\u624b\u4e0b\u7559\u60c5\u9ebd\uff1f\u201d \u3000\u3000\u706b\u52bf\u4e0d\u5927\uff0c\u4eba\u591a\u624b\u4f17\uff0c\u4e0d\u7528\u591a\u4e45\uff0c\u4fbf\u628a\u706b\u6251\u706d\uff0c\u5ba2\u6c0f\u628a\u5973\u513f\u62c9\u4eba\u623f\u53bb\u6362\u8863\u670d\uff0c\u88f9\u4f24\u53e3\uff0c \u5c06\u7389\u7f57\u5239\u5492\u9a82\u4e0d\u4f11\uff0c\u5ba2\u5a09\u5a77\u5374\u6697\u6697\u597d\u7b11\u3002\u8fd9\u521b\u4f24\u662f\u5979\u81ea\u5df1\u523a\u7684\uff0c\u4e0d\u8fc7\u5c06\u76ae\u80a4\u5272\u5f00\u4e86\u4e00\u6761\u88c2\u53e3 \u800c\u5df2\uff0c\u8fde\u9aa8\u5934\u90fd\u6ca1\u6709\u89e6\u7740\uff0c\u6839\u672c\u7b97\u4e0d\u4e86\u4ec0\u9ebd\u3002 \u3000\u3000\u95f9\u4e86\u534a\u591c\uff0c\u795e\u6b66\u95e8\u7684\u5b88\u536b\u62a5\u9053\u523a\u5ba2\u5df2\u7ecf\u9003\u53bb\uff0c\u9b4f\u5fe0\u8d24\u8fd9\u624d\u5401\u4e86\u53e3\u6c14\uff0c\u5429\u5490\u624b\u4e0b\u8f6e\u73ed\u770b \u5b88\uff0c\u4e0d\u5f97\u653e\u677e\uff0c\u81ea\u5df1\u5374\u6084\u6084\u53bb\u4e73\u5a18\u5e9c\u63a2\u671b\u5ba2\u6c0f\u3002 \u3000\u3000\u8fd9\u65f6\u5ba2\u5a09\u5a77\u5df2\u6362\u4e86\u8863\u670d\uff0c\u8eba\u5728\u5e8a\u4e0a\u5047\u5bdd\uff0c\u7389\u7f57\u5239\u7684\u8bdd\u4e00\u76f4\u5728\u5979\u5fc3\u4e0a\u7ffb\u817e\uff0c\u5ffd\u542c\u5f97\u6bcd\u4eb2\u548c \u9b4f\u5fe0\u8d24\u7684\u811a\u6b65\u58f0\u5230\u4e86\u95e8\u5916\uff0c\u5ba2\u5a09\u5a77\u7684\u5fc3\u2014\u2014\u4e71\u8df3\uff0c\u60f3\u9053\uff1a\u201c\u6211\u5e94\u4e0d\u5e94\u542c\u7389\u7f57\u5239\u7684\u8bdd\uff0c\u5c06\u4ed6\u523a \u6740\u5462\uff1f\u201d \u3000\u3000\u623f\u4e2d\u706b\u5149\u4e00\u4eae\uff0c\u5ba2\u5a09\u5a77\u611f\u89c9\u5230\u9b4f\u5fe0\u8d24\u6b63\u5f2f\u4e0b\u5934\u6765\u770b\u5979\u3002\u5ba2\u5a09\u5a77\u60f3\u9053\uff1a\u201c\u6211\u73b0\u5728\u53ea\u8981\u7565\u4e00 \u52a8\u624b\uff0c\u5c31\u53ef\u5c06\u4ed6\u6740\u6389\uff0c\u53ef\u662f\u6bcd\u4eb2\u5728\u8fd9\u513f\uff0c\u6211\u600e\u53ef\u4eca\u5979\u89c1\u7740\u9c9c\u8840\u6dcb\uff01\u201d \u3000\u3000\u5ba2\u6c0f\u4f4e\u58f0\u5524\u9053\uff1a\u201c\u5a77\u513f\uff01\u201d\u5ba2\u5a09\u5a77\u5047\u88c5\u719f\u7761\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002\u5ba2\u6c0f\u9053\uff1a\u201c\u55ef\uff0c\u5979\u7761\u7740\u5566\uff01\u201d \u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u5979\u7684\u4f24\u5389\u5bb3\u5417\uff1f\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u5e78\u800c\u8fd8\u4e0d\u7d27\u8981\u3002\u201d\u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u55ef\uff0c\u5979\u4e5f\u53ef\u601c\uff0c \u54b1\u4eec\u628a\u5979\u63a5\u5230\u5b98\u5185\uff0c\u539f\u662f\u60f3\u8ba9\u5979\u4eab\u798f\uff0c\u4eca\u591c\u53cd\u800c\u7d2f\u4e86\u5979\u66ff\u6211\u53d7\u4f24\u4e86\u3002\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u4ec0\u9ebd\uff1f\u66ff \u4f60\u53d7\u4f24\uff1f\u201d\u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u5417\uff1f\u90a3\u4e9b\u523a\u5ba2\u672c\u6765\u662f\u60f3\u523a\u6740\u6211\u7684\u3002\u201d\u5ba2\u5a09\u5a77\u8eab\u8eaf\u5fae\u5fae\u98a4 \u52a8\uff0c\u9b4f\u5fe0\u8d24\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u8981\u5728\u8fd9\u513f\u8c08\u8bdd\u5566\uff0c\u63d0\u9632\u628a\u5979\u5435\u9192\u3002\u201d\u643a\u7740\u5ba2\u6c0f\u7684\u624b\uff0c\u8f7b\u8f7b\u8d70 \u4e86\u51fa\u53bb\uff0c\u53c8\u8f7b\u8f7b\u628a\u95e8\u5173\u4e0a\u3002 \u3000\u3000\u5ba2\u5a09\u5a77\u542c\u5728\u8033\u5185\uff0c\u4e0d\u89c9\u7591\u56e2\u5927\u8d77\uff0c\u60f3\u9053\uff1a\u4e3a\u4ec0\u9ebd\u9b4f\u5fe0\u8d24\u5bf9\u6211\u8fd9\u6837\u597d\uff1f\u597d\u50cf\u628a\u6211\u5f53\u6210\u5973\u513f \u4e00\u822c\uff1f\u5c31\u7b97\u4ed6\u548c\u6bcd\u4eb2\u8981\u597d\uff0c\u4e5f\u4e0d\u5fc5\u5bf9\u6211\u8fd9\u6837\u597d\uff1f\u542c\u8bf4\u4ed6\u5bf9\u4e1c\u6797\u515a\u4eba\u975e\u5e38\u6bd2\u8fa3\uff0c\u4f46\u5374\u53c8\u5bf9\u6211\u8fd9 \u6837\u6148\u7965\uff1f\u8fd9\u662f\u4e3a\u4e86\u4ec0\u9ebd\uff0c\u4e3a\u4e86\u4ec0\u9ebd\u5462\uff1f \u3000\u3000\u4ee5\u5f80\uff0c\u5ba2\u5a09\u5a77\u56e0\u4e3a\u618e\u538c\u9b4f\u5fe0\u8d24\uff0c\u6bcf\u9022\u4ed6\u6765\u627e\u6bcd\u4eb2\u8c08\u8bdd\u65f6\uff0c\u5979\u603b\u662f\u907f\u5f00\uff0c\u538b\u6839\u513f\u6ca1\u6709\u8d77\u8fc7 \u5077\u542c\u7684\u5ff5\u5934\u3002\u53ef\u662f\u4eca\u665a\u7389\u7f57\u5239\u7684\u8bdd\u5f15\u8d77\u4e86\u5979\u5fc3\u91cc\u7684\u6ce2\u6f9c\uff0c\u9b4f\u5fe0\u8d24\u7684\u6001\u5ea6\u53c8\u5f15\u8d77\u4e86\u5979\u7684\u7591\u60d1\uff0c \u65bc\u662f\u5979\u6084\u6084\u7684\u62ab\u8863\u8d77\u5e8a\uff0c\u5faa\u7740\u9b4f\u5fe0\u8d24\u548c\u6bcd\u4eb2\u7684\u811a\u6b65\u58f0\uff0c\u8ddf\u8e2a\u5077\u542c\u3002 \u3000\u3000\u5bc6\u5ba4\u4e2d\u70db\u5149\u6447\u66f3\uff0c\u5ba2\u5a09\u5a77\u5077\u5077\u7528\u53e3\u6c34\u6e7f\u4e86\u7a97\u7eb8\uff0c\u5077\u770b\u8fdb\u53bb\uff0c\u53ea\u89c1\u9b4f\u5fe0\u8d24\u7684\u624b\u642d\u5728\u6bcd\u4eb2\u80a9 \u4e0a\uff0c\u5f62\u72b6\u5341\u5206\u4eb2\uff0c\u5ba2\u5a09\u5a77\u76b1\u4e86\u7709\u5934\uff0c\u53ea\u542c\u5f97\u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u518d\u8fc7\u51e0\u5929\u4fbf\u662f\u5a77\u513f\u4e8c\u5341\u5c81\u7684\u751f\u65e5 \u4e86\uff0c\u662f\u5417\uff1f\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4ee5\u4e3a\u4f60\u5fd8\u8bb0\u4e86\uff0c\u8fd8\u7b97\u4f60\u6709\u70b9\u826f\u5fc3\u3002\u201d \u3000\u3000\u5ba2\u5a09\u5a77\u7684\u5fc3\u535c\u901a\u4e00\u8df3\uff0c\u60f3\u9053\uff1a\u201c\u54a6\uff0c\u4ed6\u600e\u9ebd\u77e5\u9053\u6211\u7684\u751f\u65e5\uff1f\u201d\u53ea\u542c\u5f97\u9b4f\u5fe0\u8d24\u53c8\u9053\uff1a\u201c\u81ea \u4ece\u628a\u5979\u63a5\u5230\u7687\u5bab\u4e4b\u5f8c\uff0c\u5979\u597d\u50cf\u6709\u4ec0\u9ebd\u5fc3\u4e8b\u4f3c\u7684\uff0c\u7ebf\u662f\u90c1\u90c1\u4e0d\u4e50\u3002\u4e3a\u4e86\u4ec0\u9ebd\uff0c\u4f60\u6709\u95ee\u8fc7\u5979\u5417\uff1f \u662f\u4e0d\u662f\u5e74\u7eaa\u5927\u4e86\uff0c\u60f3\u8981\u5973\u5a7f\u4e86\u3002\u5979\u4e0d\u613f\u505a\u7687\u4e0a\u7684\u5983\u5b50\u4e5f\u4e0d\u7d27\u8981\uff0c\u671d\u4e2d\u6587\u6b66\u5927\u81e3\uff0c\u7687\u5b59\u5206\u5b50\uff0c \u8981\u5979\u6b22\u559c\u5c31\u6210\u3002\u201d \u3000\u3000\u5ba2\u6c0f\u5657\u55e4\u4e00\u7b11\uff0c\u5ffd\u800c\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u5509\u58f0\u8bf4\u9053\uff1a\u201c\u662f\u60f3\u5973\u5a7f\u5012\u597d\u529e\u4e86\u3002\u5979\u624d\u4e0d\u60f3\u8981\u5973\u5a7f \u5462\u3002\u6211\u4e5f\u4e0d\u77e5\u9053\u5979\u4e3a\u4e86\u4ec0\u9ebd\u4e0d\u4e50\uff0c\u5c0f\u65f6\u5019\u8e66\u8e66\u8df3\u8df3\u987d\u76ae\u900f\u9876\u7684\u5b69\u5b50\uff0c\u73b0\u5728\u4f60\u60f3\u9017\u5979\u591a\u8bf4\u4e24\u53e5 \u8bdd\u4e5f\u96be\uff0c\u6bcf\u9022\u548c\u5979\u8c08\u8bdd\uff0c\u5979\u4e0d\u662f\u8bf4\u60f3\u56de\u4ee5\u524d\u7684\u8001\u5bb6\uff0c\u5c31\u662f\u8bf4\u60f3\u53bb\u627e\u5e08\u7236\u3002\u771f\u628a\u6211\u6c14\u574f\u4e86\u3002\u201d \u3000\u3000\u9b4f\u5fe0\u8d24\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e2b\u5934\u96be\u9053\u662f\u5929\u751f\u7684\u8d31\u547d\uff1f\u201d\u5ba2\u6c0f\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u8fd9\u6837 \u8bf4\u3002\u5176\u5b9e\u4ee5\u524d\u5728\u4e61\u4e0b\u7684\u65e5\u5b50\u867d\u7136\u82e6\u4e9b\uff0c\u4e5f\u6709\u5b83\u7684\u597d\u5904\u3002\u201d\u9b4f\u5fe0\u8d24\u6de1\u6de1\u4e00\u7b11\u3002\u5ba2\u6c0f\u7eed\u9053\uff1a\u201c\u60f3 \u8d77\u4ee5\u524d\uff0c\u54b1\u4eec\u5728\u4e61\u4e0b\u4f55\u7b49\u98ce\u6d41\u5feb\u6d3b\uff1f\u201d\u9b4f\u5fe0\u8d24\u7b11\u9053\uff1a\u201c\u4f60\u73b0\u5728\u4f55\u5c1d\u4e0d\u98ce\u6d41\u5feb\u6d3b\uff1f\u201d\u5ba2\u6c0f\u9762\u4e0a \u4e00\u7ea2\uff0c\u201c\u5550\u201d\u4e86\u4e00\u53e3\u9053\uff1a\u201c\u771f\u662f\u72d7\u5634\u91cc\u957f\u4e0d\u51fa\u8c61\u7259\u3002\u6211\u662f\u8bf4\u73b0\u5728\u53ef\u8981\u6bd4\u4ece\u524d\u64cd\u5fc3\u591a\u4e86\uff0c\u65e2\u8981 \u63d0\u9632\u4e1c\u6797\u515a\u4eba\u7684\u653b\u51fb\uff1a\u53c8\u8981\u62c5\u5fc3\u7687\u5e1d\u957f\u5927\u4e4b\u5f8c\uff0c\u54b1\u4eec\u7684\u6743\u4f4d\u4e0d\u80fd\u4e45\u957f\uff0c\u542c\u5a09\u5a77\u8bf4\uff0c\u8fd9\u5c0f\u7687\u5e1d \u8eab\u5b50\u865a\u5f31\uff0c\u53ea\u6015\u6027\u547d\u4e0d\u4e45\uff0c\u82e5\u6362\u4e86\u65b0\u7687\u5e1d\uff0c\u54b1\u4eec\u7684\u4e0b\u573a\u5982\u4f55\uff0c\u8fd8\u4e0d\u77e5\u9053\u5462\uff01\u201d\u9b4f\u5fe0\u8d24\u5927\u7b11 \u9053\uff1a\u201c\u73b0\u5728\u6ee1\u671d\u6587\u6b66\uff0c\u4e0d\u662f\u6211\u7684\u4e7e\u513f\uff0c\u4fbf\u662f\u6211\u7684\u95e8\u751f\uff0c\u6211\u53c8\u638c\u7ba1\u4e1c\u897f\u4e8c\u5382\uff0c\u65b0\u7687\u5e1d\u53c8\u600e\u9ebd \u6837\uff1f\u8c01\u542c\u8bdd\u54b1\u4eec\u5c31\u7ed9\u8c01\u505a\u7687\u5e1d\u3002\u54c8\u54c8\uff0c\u60f3\u5f53\u65e5\u6211\u5728\u4e61\u4e0b\u88ab\u4eba\u9a82\u505a\u6d41\u6c13\u201c\u6df7\u6df7\u201d\uff0c\u90a3\u4e9b\u4eba\u53ef\u6599 \u4e0d\u5230\u6211\u4eca\u65e5\u505a\u4e86\u201c\u4e5d\u5343\u5c81\u201d\uff0c\u54fc\uff0c\u4e0d\u6b62\u662f\u201c\u4e5d\u5343\u5c81\u201d\uff0c\u8fde\u201c\u4e07\u5c81\u201d\u4e5f\u5728\u6211\u8fd9\u4e2a\u201d\u4e5d\u5343\u5c81\u201d\u7684 \u638c\u63e1\u4e4b\u4e2d\u3002\u201d \u3000\u3000\u5ba2\u6c0f\u4ecd\u662f\u6beb\u65e0\u7b11\u5bb9\uff0c\u7eed\u9053\uff1a\u201c\u800c\u4e14\u8fd8\u8981\u62c5\u5fc3\u523a\u5ba2\uff0c\u50cf\u4eca\u5929\u665a\u4e0a\uff0c\u8fde\u5a09\u5a77\u90fd\u7ed9\u5f04\u4f24\uff0c\u771f\u628a \u6211\u5413\u6b7b\u4e86\u3002\u4e0d\u662f\u8bf4\u7b11\u8bdd\uff0c\u6211\u7b80\u76f4\u89c9\u5f97\u6bd4\u8d77\u4ee5\u524d\u5728\u4e61\u4e0b\u548c\u4f60\u5077\u60c5\u4e4b\u65f6\uff0c\u8fd8\u66f4\u62c5\u5fc3\u5bb3\u6015\uff01\u201d\u9b4f\u5fe0 \u8d24\u53c8\u662f\u4e00\u9635\u5927\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u9ebd\u8bf4\u6765\uff0c\u4f60\u5f53\u5e74\u8fd8\u662f\u4e0d\u8981\u8fdb\u5bab\u505a\u4e73\u6bcd\u7684\u597d\uff1a\u800c\u6211\uff0c\u51c0\u4e86\u8eab\u505a\u592a \u76d1\uff0c\u90a3\u5c31\u66f4\u51a4\u6789\u5566\uff01\u82e5\u4e0d\u662f\u8d2a\u56e1\u5bcc\u8d35\uff0c\u54b1\u4eec\u5728\u4f60\u90a3\u75e8\u75c5\u9b3c\u4e08\u592b\u6b7b\u4e86\u4e4b\u5f8c\uff0c\u53ef\u4ee5\u5149\u660e\u6b63\u5927\u4f4f\u5728 \u4e00\u5757\uff0c\u591a\u517b\u51e0\u4e2a\u80d6\u5a03\u5a03\uff0c\u4ffa\u9b4f\u5fe0\u8d24\u4e5f\u4e0d\u81f3\u65bc\u65ad\u5b50\u7edd\u5b59\uff0c\u73b0\u5728\u6709\u4e00\u4e2a\u8d31\u4e2b\u5934\uff0c\u800c\u4e14\u8fd8\u4e0d\u80fd\u53eb\u5979 \u77e5\u9053\u6211\u662f\u5979\u7684\u751f\u8eab\u7236\u4eb2\u3002\u201d \u3000\u3000\u5ba2\u5a09\u5a77\u4e00\u8def\u542c\u4e00\u8def\u53d1\u614c\uff0c\u542c\u5230\u8fd9\u91cc\uff0c\u53ea\u89c9\u624b\u8db3\u51b0\u51b7\uff0c\u5fc3\u5982\u5200\u5272\uff0c\u5979\u7edd\u672a\u6599\u5230\u9b4f\u5fe0\u8d24\u8fd9\u5978 \u9609\u7adf\u662f\u5979\u7684\u751f\u8eab\u7236\u4eb2\uff0c\u4e00\u65f6\u95f4\u6124\u6012\uff0c\u7f9e\u60ed\u3001\u53d7\u4fae\u5c4f\u3001\u53d7\u635f\u5bb3\uff0c\u79cd\u79cd\u60c5\u7eea\u7ea0\u7ed3\u5728\u4e00\u8d77\uff0c\u90a3\u79cd\u611f \u89c9\u5c31\u5982\u7ed9\u4eba\u5410\u4e86\u4e00\u53e3\u553e\u6cab\u5728\u8138\u4e0a\u4e00\u822c\uff0c\u6bd4\u6b7b\u8fd8\u8981\u96be\u53d7\uff01 \u3000\u3000\u5ba2\u5a09\u5a77\u6068\u4e0d\u5f97\u6709\u4e2a\u5730\u6d1e\u94bb\u4e0b\u53bb\uff0c\u4ece\u6b64\u6c38\u4e0d\u89c1\u4eba\u3002\u5979\u63a9\u7740\u8138\u5b54\u51e0\u4e4e\u54ed\u51fa\u58f0\u6765\uff0c\u65e0\u5fc3\u518d\u542c\uff0c \u8f6c\u8eab\u4fbf\u8dd1\uff0c\u521a\u7ed5\u8fc7\u56de\u5eca\uff0c\u5ffd\u89c1\u4e00\u6761\u4eba\u5f71\uff0c\u75be\u5982\u9e70\u96bc\u7684\u4ece\u7409\u7483\u74e6\u9762\u98de\u6765\uff0c\u5ba2\u5a09\u5a77\u7f29\u8eab\u5728\u76d8\u9f99\u5927 \u67f1\u4e4b\u5f8c\uff0c\u770b\u6e05\u695a\u8fd9\u4eba\u5f71\u4e43\u662f\u6155\u5bb9\u51b2\uff0c\u5947\u9053\uff1a\u201c\u8fd9\u6837\u6df1\u591c\uff0c\u4ed6\u8fd8\u6765\u8fd9\u91cc\u505a\u4ec0\u9ebd\uff1f\u201d\u6155\u5bb9\u51b2\u98de\u8eab \u6500\u4e0a\u4e86\u5ba2\u6c0f\u5bdd\u5b98\u5916\u9762\u7684\u5927\u6881\uff0c\u8737\u4f0f\u4e0d\u52a8\u3002\u5ba2\u5a09\u5a77\u8fd9\u65f6\u60c5\u7eea\u5341\u5206\u6fc0\u52a8\uff0c\u4e5f\u4e0d\u613f\u73b0\u8eab\u548c\u6155\u5bb9\u51b2\u62db \u547c\uff0c\u7ed5\u8fc7\u56de\u5eca\uff0c\u62d0\u4e86\u4e24\u4e2a\u5f2f\uff0c\u56de\u5230\u81ea\u5df1\u623f\u4e2d\uff0c\u5c31\u5728\u9ed1\u6697\u4e4b\u4e2d\uff0c\u5750\u5728\u5e8a\u4e0a\uff0c\u75f4\u75f4\u9ed8\u60f3\u3002 \u3000\u3000\u4e14\u8bf4\u6155\u5bb9\u51b2\u5728\u94c1\u98de\u9f99\u4e0e\u7389\u7f57\u5239\u8d70\u5f8c\uff0c\u6251\u706d\u4e86\u4e73\u5a18\u5e9c\u7684\u706b\uff0c\u56de\u5230\u623f\u4e2d\uff0c\u6478\u51fa\u94c1\u98de\u9f99\u63b7\u4ed6\u7684 \u90a3\u67c4\u5315\u9996\u4e00\u770b\uff0c\u53ea\u89c1\u5315\u9996\u5c16\u7aef\uff0c\u7a7f\u7740\u4e00\u5f20\u7eb8\u7247\uff0c\u4e0a\u9762\u5199\u9053\uff1a\u201c\u6211\u7ea6\u4f60\u5728\u5df1\u65e5\u5f8c\u4e2d\u5348\u65f6\u5206\uff0c\u5728 \u79d8\u9b54\u5ca9\u5355\u6253\u72ec\u6597\uff0c\u53cc\u65b9\u4e0d\u8bb8\u9080\u8bf7\u5e2e\u624b\u52a9\u638c\uff0c\u6562\u6765\u662f\u82f1\u96c4\uff0c\u4e0d\u6562\u6765\u662f\u72d7\u718a\u201c\u94c1\u98de\u9f99\u767d\u3002\u201d\u6155\u5bb9 \u51b2\u6c14\u9053\uff1a\u201c\u94c1\u8001\u8d3c\u6b3a\u6211\u592a\u751a\uff0c\u6211\u80dc\u4e0d\u4e86\u4f60\u4e5f\u4e0d\u89c1\u5f97\u4f1a\u8d25\u5728\u4f60\u7684\u624b\u4e0a\uff0c\u6015\u4f60\u4ec0\u9ebd\uff1f\u201d\u968f\u624b\u628a\u7eb8 \u7247\u4e00\u56e2\uff0c\u4e22\u5728\u5730\u4e0a\u3002 \u3000\u3000\u82e5\u5728\u5e73\u65e5\uff0c\u6155\u5bb9\u51b2\u63a5\u5230\u8fd9\u6837\u4e00\u4e2a\u52b2\u654c\u7684\u6bd4\u6b66\u9080\u5e16\uff0c\u5fc5\u7136\u6f5c\u5fc3\u7ec6\u60f3\u7834\u654c\u4e4b\u6cd5\u3002\u53ef\u662f\u4eca\u665a\u4ed6 \u7684\u601d\u60f3\u5374\u88ab\u53e6\u4e00\u4ef6\u66f4\u91cd\u5927\u7684\u4e8b\u60c5\u5438\u5f15\u4e86\u53bb\uff0c\u94c1\u98de\u9f99\u5728\u9752\u9633\u5bab\u5f53\u4f17\u5927\u9a82\u7684\u58f0\u97f3\uff1a\u201c\u9b4f\u5fe0\u8d24\uff0c\u4f60 \u8fd9\u901a\u756a\u5356\u56fd\u7684\u5978\u8d3c\uff01\u201d\u5c31\u50cf\u5728\u4ed6\u5fc3\u4e0a\u6295\u4e0b\u4e00\u5757\u5927\u77f3\uff0c\u6fc0\u8d77\u4e86\u6ce2\u6d9b\u3002 \u3000\u3000\u201c\u9b4f\u5fe0\u8d24\u5230\u5e95\u662f\u4e0d\u662f\u901a\u756a\u5356\u56fd\u7684\u6c49\u5462\uff1f\u201d\u6155\u5bb9\u51b2\u60f3\u3002\u4ed6\u60f3\u8d77\u4e86\u5f53\u94c1\u98de\u9f99\u5927\u9a82\u4e4b\u5f8c\uff0c\u9b4f\u5fe0 \u8d24\u66b4\u6012\u5982\u96f7\u7684\u795e\u60c5\uff0c\u53c8\u60f3\u8d77\u4e86\u5e73\u65e5\u9b4f\u5fe0\u8d24\u548c\u5e94\u4fee\u9633\u8fde\u57ce\u864e\u7b49\u805a\u8c08\uff0c\u5e38\u5e38\u5c06\u4ed6\u6487\u5f00\u7684\u4e8b\uff0c\u6108\u60f3 \u6108\u53ef\u7591\uff0c\u5fc3\u9053\uff1a\u8fd9\u94c1\u8001\u8d3c\u867d\u7136\u6a2a\u86ee\uff0c\u4f46\u5728\u6b66\u6797\u4e2d\u5374\u662f\u6709\u8eab\u4efd\u7684\u4eba\u7269\uff0c\u6599\u4ed6\u4e0d\u4f1a\u80e1\u8bf4\u4e71\u9053\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u662f\u7518\u8083\u56de\u4eba\uff0c\u5929\u751f\u795e\u529b\uff0c\u5f8c\u6765\u88ab\u897f\u5317\u7684\u72ec\u884c\u5927\u76d7\u7126\u86ee\u5b50\u6536\u4e3a\u5f92\u5f1f\uff0c\u7ec3\u4e86\u9e70\u722a\u529f\u548c \u94c1\u5e03\u886b\uff0c\u53c8\u5230\u6606\u4ed1\u5c71\u5b9a\u865a\u5927\u5e08\u95e8\u4e0b\u5b66\u4e86\u4e03\u5341\u4e8c\u8def\u795e\u62f3\uff0c\u4ece\u6b64\u95ef\u8361\u6c5f\u6e56\uff0c\u58f0\u540d\u5927\u8d77\u3002\u5f8c\u6765\u795e\u5b97 \u5f00\u699c\u62db\u8003\u7981\u536b\u519b\uff0c\u4ed6\u60f3\u56fe\u4e2a\u529f\u540d\uff0c\u5c01\u59bb\u836b\u5b50\uff0c\u4fbf\u8fdb\u4eac\u6295\u8003\uff0c\u53c8\u5f97\u4eba\u4fdd\u8350\uff0c\u4fbf\u5728\u7981\u536b\u519b\u4e2d\u5f53\u4e0a \u4e86\u4e00\u540d\u201c\u90fd\u6307\u6325\u201d\uff0c\u4e00\u505a\u4fbf\u505a\u4e86\u5341\u9980\u5e74\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u6b66\u529f\u867d\u7136\u6781\u9ad8\uff0c\u53ef\u662f\u4e0d\u5584\u5df4\u7ed3\uff0c\u800c\u4e14\u4ed6\u53c8\u81ea\u6043\u672c\u9886\uff0c\u76ee\u7a7a\u4e00\u5207\uff0c\u548c\u540c\u50da\u4e5f\u4e0d\u878d \u6d3d\uff0c\u56e0\u6b64\u505a\u4e86\u5341\u591a\u5e74\u7684\u201c\u90fd\u6307\u6325\u201d\uff0c\u59cb\u7ec8\u4e0d\u5f97\u5347\u7ea7\u3002\u76f4\u5230\u9b4f\u5fe0\u8d24\u63e1\u6743\u4e4b\u5f8c\uff0c\u77e5\u4ed6\u6b66\u529f\u786e\u662f\u9ad8 \u5f3a\uff0c\u60f3\u628a\u4ed6\u6536\u4e3a\u5df2\u7528\u3002\u65bc\u662f\u4e00\u5347\u5c31\u628a\u4ed6\u8fde\u5347\u53c1\u7ea7\uff0c\u4e0d\u5230\u534a\u5e74\uff0c\u4fbf\u505a\u5230\u4e86\u4e1c\u5382\u7684\u603b\u6559\u5934\u3002\u6155\u5bb9 \u51b2\u6ee1\u8111\u5b50\u5bcc\u8d35\u529f\u540d\u4e4b\u5ff5\uff0c\u5f97\u9b4f\u5fe0\u8d24\u4e00\u624b\u63d0\u62d4\uff0c\u5f53\u7136\u611f\u6fc0\u3002\u53ef\u662f\u4ed6\u4e5f\u8fd8\u6709\u51e0\u5206\u6897\u76f4\uff0c\u5bf9\u9b4f\u5fe0\u8d24 \u7684\u6b8b\u5bb3\u5fe0\u8d24\uff0c\u6709\u65f6\u4e5f\u4f1a\u53cd\u611f\u3002\u4f46\u867d\u7136\u5982\u6b64\uff0c\u4ed6\u6c42\u5bcc\u8d35\u529f\u540d\u4e4b\u5ff5\uff0c\u538b\u5012\u4e86\u90a3\u4e00\u70b9\u5584\u826f\u6b63\u76f4\u4e4b \u5fc3\uff0c\u65bc\u662f\u4e0d\u81ea\u89c9\u7684\u88ab\u9b4f\u5fe0\u8d24\u5229\u7528\uff0c\u505a\u4e86\u4ed6\u7684\u8d70\u72d7\u3002 \u3000\u3000\u53ef\u662f\uff0c\u4eca\u591c\uff0c\u5f53\u6155\u5bb9\u51b2\u60f3\u8d77\u4e86\u9b4f\u5fe0\u8d24\u786e\u6709\u79c1\u901a\u6ee1\u5dde\u7684\u5acc\u7591\u65f6\uff0c\u4ed6\u518d\u4e5f\u6291\u5236\u4e0d\u4f4f\u60c5\u7eea\u7684\u6ce2 \u52a8\u4e86\u3002\u4ed6\u60f3\uff1a\u201c\u82e5\u7136\u9b4f\u5fe0\u8d24\u771f\u662f\u6c49\u5978\u7684\u8bdd\uff0c\u5c82\u4e0d\u8fde\u7d2f\u6211\u4e5f\u8499\u4e86\u6076\u540d\uff1f\u201d\u8981\u77e5\u6155\u5bb9\u51b2\u7d20\u4ee5\u82f1\u96c4 \u81ea\u547d\uff0c\u867d\u7136\u5176\u5b9e\u4ed6\u4e0d\u8fc7\u662f\u6743\u95e8\u9e70\u72ac\uff0c\u4f46\u81ea\u5df1\u5374\u4e0d\u81ea\u77e5\u3002\u8fd9\u65f6\u4ed6\u4e00\u60f3\u518d\u60f3\uff0c\u82e6\u95f7\u975e\u5e38\u3002\u60f3\u79bb\u5f00 \u9b4f\u5fe0\u8d24\u53c8\u820d\u4e0d\u5f97\u76ee\u524d\u5730\u4f4d\uff0c\u82e5\u4e0d\u6742\u5f00\uff0c\u53c8\u6015\u9b4f\u5fe0\u8d24\u771f\u662f\u6c49\u5978\u3002 \u3000\u3000\u60f3\u4e86\u8bb8\u4e45\uff0c\u542c\u5f97\u6572\u4e86\u56db\u66f4\uff0c\u4ed6\u5ffd\u7136\u8d77\u4e86\u4e00\u4e2a\u5ff5\u5934\uff1a\u4f55\u4e0d\u81ea\u5df1\u53bb\u67e5\u4e2a\u6c34\u843d\u77f3\u51fa\u3002\u65bc\u662f\u4ed6\u5148 \u5230\u9b4f\u5fe0\u8d24\u7684\u9752\u9633\u5bab\uff0c\u518d\u5230\u5ba2\u6c0f\u7684\u4e73\u5a18\u5e9c\u3002 \u3000\u3000\u9b4f\u5fe0\u8d24\u548c\u5ba2\u6c0f\u7684\u8c08\u8bdd\u8fd8\u5728\u7ee7\u7eed\uff0c\u6155\u5bb9\u51b2\u4f0f\u5728\u5916\u9762\u5927\u6881\u7f6e\u8033\u7ec6\u542c\u3002\u53ea\u542c\u5f97\u9b4f\u5fe0\u8d24\u7b11\u6b96\uff1a \u201c\u5a09\u5a77\u60f3\u4e9b\u4ec0\u9ebd\uff0c\u6211\u4e5f\u61d2\u5f97\u518d\u7ba1\u5979\u4e86\u3002\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u5478\uff0c\u81ea\u5df1\u7684\u4eb2\u751f\u5973\u513f\u90fd\u4e0d\u7ba1\u9ebd\uff1f\u201d\u6155\u5bb9 \u51b2\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u539f\u6765\u90a3\u5c0f\u4e2b\u5934\u7adf\u662f\u4ed6\u7684\u5973\u513f\uff01 \u3000\u3000\u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u83c5\uff0c\u4f60\u4e0d\u89c1\u6211\u5f88\u75bc\u5979\u9ebd\uff1f\u662f\u7ba1\u4e0d\u4e86\uff0c\u4e0d\u597d\u7ba1\u3002\u5979\u6bcf\u6b21\u89c1\u6211\u90fd\u4e0d\u559c\u6b22 \u548c\u6211\u8bf4\u8bdd\uff0c\u6211\u600e\u9ebd\u80fd\u8ddf\u5979\u8c08\u5fc3\u3002\u201d\u5ba2\u6c0f\u9ed8\u7136\u4e0d\u8bed\uff0c\u4e45\u4e45\u65b9\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u8981\u4e0d\u8981\u544a\u8bc9\u5979\u751f\u8eab\u4e4b \u7236\u662f\u8c01\uff1f\u201d\u9b4f\u5fe0\u8d24\u5fd9\u6447\u624b\u9053\uff1a\u201c\u5343\u4e07\u522b\u8bf4\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u9b4f\u5fe0\u8d24\u53c8\u9053\uff1a\u201c\u4f60\u62c5\u5fc3\u4e07\u4e00\u5c06\u6765\u65b0\u7687\u5e1d\u5373\u4f4d\uff0c\u4f1a\u5bf9\u54b1\u4eec\u4e0d\u5229\uff0c\u6211\u770b\uff0c\u4f60\u8fd9\u62c5 \u5fc3\u5927\u53ef\u4e0d\u5fc5\u3002\u201d\u5ba2\u6c0f\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\u4e86\u4f60\u8fd8\u662f\u6043\u7740\u6ee1\u671d\u6587\u6b66\uff0c\u4e0d\u662f\u4f60\u7684\u4e7e\u513f\u5c31\u662f\u4f60\u7684\u95e8\u751f\u5417\uff1f \u53ef\u662f\u4f60\u8fd9\u4e9b\u4e7e\u513f\u95e8\u751f\uff0c\u90fd\u662f\u4e9b\u8d8b\u708e\u9644\u52bf\u4e4b\u8f88\uff0c\u51b0\u5c71\u6b32\u5012\u4e4b\u65f6\uff0c\u4f60\u6015\u4ed6\u4eec\u4e0d\u53e6\u627e\u9760\u5c71\u9ebd\uff1f\u201d \u3000\u3000\u9b4f\u5fe0\u8d38\u4e7e\u7b11\u96e8\u58f0\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\uff0c\u4e5f\u5728\u6211\u610f\u6599\u4e4b\u4e2d\uff0c\u53ef\u662f\uff0c\u5a18\u5b50\uff0c\u4f60\u6709\u6240\u4e0d\u77e5\u3002\u201d\u5ba2\u6c0f \u9053\uff1a\u201c\u4ec0\u9ebd\uff1f\u201d\u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u53ea\u6015\u7b49\u4e0d\u5230\u65b0\u541b\u5373\u4f4d\uff0c\u6ee1\u5dde\u9791\u5b50\uff0c\u4fbf\u8981\u6253\u8fdb\u5173\u4e86\u3002\u201d\u5ba2\u6c0f\u9053\uff1a \u201c\u90a3\u5c82\u4e0d\u66f4\u7cdf\uff1f\u201d\u9b4f\u5fe0\u8d24\u7b54\u9053\uff1a\u201c\u90a3\u6709\u4ec0\u9ebd\u53ef\u6015\uff1f\u6ee1\u5dde\u5f97\u4e86\u5929\u4e0b\uff0c\u54b1\u4eec\u7684\u5bcc\u8d35\u66f4\u53ef\u4fdd\u6301\uff1f\u201d \u5ba2\u6c0f\u53eb\u9053\uff1a\u201c\u4ec0\u9ebd\u4e86\u4f60\u79c1\u901a\u6ee1\u5dde\u5417\uff1f\u201d\u9b4f\u5fe0\u8d24\u9053\uff1a\u201c\u5c0f\u58f0\u4e00\u70b9\u3002\u4fd7\u8bed\u4e91\uff1a\u8bc6\u65f6\u52a1\u8005\u4e3a\u4fca\u6770\u3002 \u73b0\u5728\u5185\u6709\u76d7\u5bc7\u7eb7\u8d77\uff0c\u5916\u6709\u5f3a\u654c\u7aa5\u4f3a\u3002\u4e0d\u4ea1\u65bc\u5bc7\uff0c\u4fbf\u4ea1\u65bc\u654c\uff0c\u603b\u4e4b\uff0c\u660e\u5ba4\u7684\u6c5f\u5c71\u662f\u4e0d\u80fd\u957f\u4e45\u7684 \u4e86\u3002\u4e0e\u5176\u4ea1\u65bc\u6d41\u5bc7\uff0c\u4e0d\u5982\u4ea1\u65bc\u6ee1\u5dde\uff0c\u4ea1\u65bc\u6d41\u5bc7\uff0c\u54b1\u4eec\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff0c\u4ea1\u65bc\u6ee1\u5dde\uff0c\u54b1\u4eec\u6700\u4e0d\u6d4e \u8fd8\u6709\u53e3\u996d\u5403\u3002\u4f60\u8bf4\u5427\uff0c\u6211\u8bf4\u7684\u6709\u6ca1\u6709\u9053\u7406\uff1f\u201d\u5ba2\u6c0f\u6c89\u601d\u826f\u4e45\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4f60\u7684\u806a\u660e\u8ba1\u667a\uff0c \u4e00\u5411\u5728\u6211\u4e4b\u4e0a\uff0c\u4e0d\u8fc7\uff0c\u6211\u603b\u4e0d\u613f\u4f60\u80cc\u4e0a\u901a\u756a\u5356\u56fd\u7684\u6076\u540d\u3002\u5440\uff0c\u4e8b\u5230\u5982\u4eca\uff0c\u6211\u4e5f\u6ca1\u6709\u4e3b\u610f \u4e86\uff01\u201d \u3000\u3000\u201c\u6211\u4e5f\u6ca1\u6709\u4e3b\u610f\u4e86\uff01\u201d\u6155\u5bb9\u51b2\u542c\u5230\u8fd9\u513f\uff0c\u53ea\u611f\u5230\u4e00\u9635\u6df7\u4e71\u8ff7\u832b\uff0c\u51e0\u4e4e\u8dcc\u4e0b\u5927\u6881\uff0c\u60f3\u9053\uff1a \u201c\u4ed6\u679c\u771f\u662f\u901a\u756a\u5356\u56fd\uff0c\u8fd9\u53ef\u600e\u9ebd\u597d\u5462\uff1f\u82e6\u80cc\u5224\u4ed6\u5427\uff1f\u4ed6\u662f\u4e00\u624b\u63d0\u62d4\u81ea\u5df1\u7684\u6069\u4eba\uff01\u987a\u4ece\u4ed6\u5427\uff1f \u4e8b\u60c5\u8d25\u9732\uff0c\u5fc5\u7136\u4e3a\u4eba\u553e\u9a82\uff0c\u90a3\u65f6\u5c31\u771f\u7684\u4e0d\u662f\u82f1\u96c4\u800c\u662f\u72d7\u718a\u4e86\uff01\u201d\u542c\u5f97\u9b4f\u5fe0\u8d24\u5411\u5ba2\u6c0f\u544a\u8f9e\uff0c\u6155 \u5bb9\u51b2\u6025\u5fd9\u98d8\u8eab\u5148\u51fa\u3002 \u3000\u3000\u63a0\u8fc7\u4e24\u91cd\u74e6\u9762\uff0c\u5ffd\u542c\u5f97\u4e0b\u9762\u6709\u4f4e\u4f4e\u555c\u6ce3\u4e4b\u58f0\u3002\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u4e0d\u662f\u5ba2\u5a09\u5a77\u5417\uff1f\u5979\u600e \u9ebd\u73b0\u5728\u672a\u7761\uff1f\u201d\u60f3\u8d77\u5979\u4eca\u665a\u6240\u53d7\u201c\u5251\u4f24\u201d\u7684\u53ef\u7591\u75d5\u8ff9\uff0c\u4e0d\u89c9\u505c\u4e0b\u6b65\u6765\u3002\u6b63\u662f\uff1a\u7d27\u8981\u5173\u5934\u4e34\u8003 \u6361\uff0c\u5404\u6000\u5fc3\u4e8b\u53e3\u96be\u8a00\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u56db\u56de\u3000\u8f6c\u5ff5\u5f03\u5c60\u5200\u3000\u6df1\u4ec7\u987f\u89e3\u3000\u771f\u60c5\u4f20\u5f69\u3000\u7b14\u65e7\u4fa3\u96be\u5fd8 \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377 \u7b2c\u4e8c\u5341\u56db\u56de \u8f6c\u5ff5\u5f03\u5c60\u5200\u3000\u6df1\u4ec7\u987f\u89e3 \u771f\u60c5\u4f20\u5f69\u3000\u7b14\u65e7\u4fa3\u96be\u5fd8 \u3000\u3000\u6155\u5bb9\u51b2\u811a\u52fe\u5c4b\u6a90\uff0c\u7528\u4e2a\u201c\u5012\u5377\u201d\u7684\u59ff\u52bf\uff0c\u4f0f\u8033\u7ec6\u542c\u3002\u5ffd\u89c1\u623f\u4e2d\u706b\u5149\u4e00\u4eae\uff0c\u5ba2\u5a09\u5a77\u70b9\u8d77\u7ea2 \u70db\uff0c\u545c\u54bd\u53eb\u9053\uff1a\u201c\u6211\u4e0d\u8981\u8fd9\u6837\u7684\u7236\u4eb2\u3002\u201d\u6155\u5bb9\u51b2\u5fc3\u60f3\uff1a\u201c\u54a6\uff0c\u5979\u77e5\u9053\u4e86\uff01\u201d\u5077\u7aa5\u8fdb\u53bb\uff0c\u89c1\u5ba2 \u5a09\u5a77\u62ff\u8d77\u4e00\u4e2a\u78be\u7389\u4eba\u50cf\uff0c\u5728\u70db\u5149\u4e0b\u4e00\u6643\uff0c\u5550\u4e86\u4e00\u53e3\uff0c\u7a81\u7136\u7528\u529b\u4e00\u6454\uff0c\u628a\u5b83\u6454\u5f97\u56db\u5206\u4e94\u88c2\u3002 \u3000\u3000\u8fd9\u662f\u9b4f\u5fe0\u8d24\u7684\u5851\u50cf\u3002\u56db\u5ddd\u5de1\u629a\u4e3a\u4e86\u8ba8\u597d\u9b4f\u5fe0\u8d24\u548c\u5ba2\u6c0f\uff0c\u89c5\u4e86\u4e0a\u597d\u7684\u548c\u9617\u7f8e\u7389\uff0c\u5851\u4e86\u9b4f\u5fe0 \u8d24\u548c\u5ba2\u6c0f\u7684\u50cf\uff0c\u9001\u7ed9\u4ed6\u4eec\u3002\u5ba2\u6c0f\u60f3\u4ee4\u5973\u513f\u5bf9\u9b4f\u5fe0\u8d24\u53d1\u751f\u597d\u611f\uff0c\u56e0\u6b64\u4e00\u5b9a\u8981\u628a\u8fd9\u4e24\u5ea7\u78be\u7389\u4eba \u50cf\uff0c\u6446\u5728\u5ba2\u5a09\u5a77\u7684\u53f0\u4e0a\u3002\u5ba2\u5a09\u5a77\u5f53\u65f6\u83ab\u540d\u5176\u5999\uff0c\u4e5f\u66fe\u63d0\u8fc7\u7591\u95ee\uff0c\u5ba2\u6c0f\u7b54\u9053\uff1a\u201c\u8fd9\u6837\u7684\u7f8e\u7389\u662f \u7a00\u4e16\u5947\u73cd\uff0c\u9001\u7ed9\u4f60\u505a\u5c0f\u6446\u8bbe\u4e0d\u597d\u5417\uff1f\u4f60\u4f55\u5fc5\u7ba1\u5b83\u662f\u8c01\u7684\u50cf\u3002\u201d\u5ba2\u5a09\u5a77\u867d\u7136\u618e\u538c\u9b4f\u5fe0\u8d24\uff0c\u4f46\u4e3a \u4e86\u987a\u4ece\u6bcd\u610f\uff0c\u5bf9\u8fd9\u70b9\u5c0f\u4e8b\u4e5f\u5c31\u4e0d\u518d\u4e89\u8bba\uff0c\u968f\u624b\u628a\u5b83\u6446\u5728\u4e00\u4e2a\u89d2\u843d\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u77a7\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u6697\u53eb\u53ef\u60dc\uff0c\u60f3\u9053\uff1a\u201c\u5979\u600e\u9ebd\u628a\u9b4f\u5fe0\u8d24\u6068\u6210\u8fd9\u6837\uff1f\u201d\u53ea\u89c1\u5ba2\u5a09\u5a77\u53c8 \u62ff\u8d77\u4e86\u6bcd\u4eb2\u7684\u5851\u50cf\uff0c\u7aef\u8be6\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u60f3\u6454\u5374\u53c8\u6536\u56de\uff0c\u5583\u5583\u8bf4\u9053\uff1a\u201c\u5982\u679c\u4f60\u4e5f\u50cf\u4ed6\u4e00\u6837\u901a\u756a \u5356\u56fd\uff0c\u6211\u4e5f\u4e0d\u8981\u4f60\u8fd9\u6837\u7684\u6bcd\u4eb2\uff1f\u201d \u3000\u3000\u5ba2\u5a09\u5a77\u54fa\u54fa\u81ea\u8bed\uff0c\u58f0\u97f3\u751a\u5c0f\uff0c\u4f46\u542c\u5728\u6155\u5bb9\u51b2\u5fc3\u4e0a\uff0c\u5374\u5982\u4e00\u58f0\u9739\u96f3\uff0c\u9707\u52a8\u8d77\u6765\u3002\u5fc3\u9053\uff1a \u201c\u5ba2\u5a09\u5a77\u5728\u5bab\u4e2d\u6bd4\u516c\u4e3b\u8fd8\u8981\u5c0a\u8363\uff0c\u4f46\u5f53\u5979\u77e5\u9053\u751f\u8eab\u4e4b\u7236\u662f\u4e2a\u56fd\u756a\u5356\u56fd\u7684\u6c49\u5978\u4e4b\u5f8c\uff0c\u5c31\u6068\u6210\u8fd9 \u6837\uff01\u201d \u3000\u3000\u5ba2\u5a09\u5a77\u5728\u623f\u4e2d\u6089\u6089\u7d22\u7d22\u6536\u62ac\u8863\u7269\uff0c\u6253\u4e86\u4e00\u4e2a\u5c0f\u5305\u88f9\uff0c\u6ee1\u5c4b\u73cd\u5b9d\u73a9\u7269\uff0c\u5979\u4e00\u4ef6\u90fd\u4e0d\u8981\u3002\u6700 \u5f8c\u5979\u62ff\u8d77\u4e86\u6bcd\u4eb2\u7684\u5851\u50cf\uff0c\u5728\u70db\u5149\u4e0b\u53c8\u7aef\u8be6\u4e86\u597d\u4e00\u4f1a\u5b50\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u60f3\u628a\u5b83\u585e\u5165\u5305\u88f9\uff0c\u5ffd\u53c8\u653e \u4e0b\uff0c\u54fa\u5583\u81ea\u8bed\u9053\uff1a\u201c\u8fd9\u4e24\u5ea7\u50cf\u539f\u662f\u76f8\u8fde\u7684\uff0c\u6211\u65e2\u6454\u4e86\u90a3\u5ea7\uff0c\u8981\u8fd9\u5ea7\u505a\u4ec0\u9ebd\uff0c\u8fd8\u662f\u4e0d\u5e26\u7684 \u597d\u3002\u201d\u968f\u624b\u53c8\u628a\u90a3\u7389\u50cf\u653e\u56de\u53f0\u3002 \u3000\u3000\u957f\u591c\u5c06\u5c3d\uff0c\u5929\u8fb9\u9732\u51fa\u4e00\u7ebf\u66d9\u5149.\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u6bcd\u4eb2\u554a\uff01\u8fd9\u662f\u6211\u966a\u4f34\u4f60\u7684\u6700\u5f8c\u4e00\u665a\u4e86\u3002\u201d \u62ac\u5934\u671b\u7a97\u5916\u5929\u8272\uff0c\u81ea\u8bed\u9053\uff1a\u201c\u73b0\u5728\u8fd8\u4e0d\u80fd\u51fa\u53bb\u3002\u201d\u5750\u5728\u68b3\u53f0\u524d\uff0c\u6284\u51fa\u51e0\u5f20\u96ea\u767d\u7684\u9526\u7b3a\uff0c\u63d0\u8d77 \u72fc\u6beb\u4fbf\u5199\uff0c\u5199\u4e00\u884c\uff0c\u505c\u4e00\u505c\uff0c\u53c8\u4f4e\u4f4e\u62bd\u6ce3\u8d77\u6765\u4e86\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u5fc3\u9053\uff1a\u201c\u5979\u60f3\u5fc5\u662f\u7559\u4e66\u7ed9\u5979\u7684\u6bcd\u4eb2\uff0c\u4ece\u6b64\u6c38\u4e0d\u56de\u5bab\u4e86\u3002\u201d\u53c8\u60f3\u9053\uff1a\u201c\u5ba2\u5a09\u5a77\u8eab\u4efd \u5982\u540c\u516c\u4e3b\uff0c\u5979\u4e5f\u6beb\u4e0d\u7559\u604b\u3002\u6211\u8fd9\u4e1c\u5382\u603b\u6559\u5934\u7b97\u5f97\u4ec0\u9ebd\uff1f\u201d\u8840\u6db2\u6cb8\u817e\uff0c\u9762\u4e0a\u9635\u9635\u53d1\u70ed\u3002\u60f3\u9053\uff1a \u201c\u6211\u4e03\u5c3a\u4e4b\u8eaf\uff0c\u6602\u85cf\u7537\u5b50\uff0c\u96be\u9053\u5c31\u6bd4\u4e0d\u4e0a\u8fd9\u5c0f\u4e2b\u5934\u3002\u201d\u98d8\u8eab\u98de\u51fa\u5bab\u6bbf\uff0c\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5ba2\u5a09 \u5a77\u5df2\u5439\u7184\u70db\u5149\uff0c\u5929\u8272\u5927\u767d\u4e86\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u8d70\u5230\u5fa1\u82d1\u4f4e\u9996\u6c89\u601d\uff0c\u5ffd\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u6155\u5bb9\u603b\u7ba1\uff0c\u4f60\u65e9\uff01\u201d\u6155\u5bb9\u51b2\u62fe\u5934\u4e00\u770b\uff0c \u5374\u662f\u5e94\u4fee\u9633\u3002\u84e6\u7136\u60f3\u8d77\u6b64\u4eba\u4ea6\u662f\u901a\u756a\u5356\u56fd\u7684\u6c49\u5978\u4e4b\u4e00\uff0c\u60f3\u9053\uff1a\u201c\u6211\u82e5\u8981\u628a\u4ed6\u6253\u6b7b\uff0c\u90a3\u662f\u6613\u5982 \u53cd\u638c\uff01\u4f46\u9b4f\u5fe0\u8d24\u5230\u5e95\u66fe\u662f\u63d0\u62d4\u8fc7\u6211\u7684\u4eba\uff0c\u6211\u4e0d\u5e2e\u4ed6\uff0c\u4e5f\u4e0d\u5fc5\u4e0e\u4ed6\u4e3a\u654c\u3002\u7f62\uff0c\u7f62\uff0c\u6211\u6155\u5bb9\u51b2\u6240 \u9047\u975e\u4eba\uff0c\u53ea\u597d\u5012\u6963\u8fd9\u4e00\u8f88\u5b50\uff0c\u4ece\u6b64\u9041\u8ff9\u6df1\u5c71\uff0c\u518d\u4e5f\u4e0d\u7406\u4e16\u4e8b\u4e86\uff01\u201d\u5e94\u4fee\u9633\u89c1\u6155\u5bb9\u51b2\u795e\u8272\u6709 \u5f02\uff0c\u751a\u4e3a\u60ca\u8be7\uff0c\u4e0a\u524d\u62cd\u6155\u5bb9\u51b2\u7684\u80a9\u818a\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u51b7\u51b7\u7684\u628a\u5e94\u4fee\u9633\u63a8\u5f00\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u6837\u65e9\u5e72\u561b\uff1f\u201d\u5e94\u4fee\u9633\u8c04\u7b11\u9053\uff1a\u201c\u6211\u53bb\u95ee\u5019\u5949\u5723 \u592b\u4eba\u3002\u4f60\u53bb\u4e0d\u53bb\uff1f\u201d\u6155\u5bb9\u51b2\u597d\u751f\u538c\u70e6\uff0c\u9053\uff1a\u201c\u4e0d\u53bb\uff01\u201d\u5e94\u4fee\u9633\u66f4\u662f\u8be7\u5f02\uff0c\u76ee\u9001\u6155\u5bb9\u51b2\u7684\u80cc\u5f71 \u9690\u5728\u5047\u5c71\u82b1\u6728\u4e1b\u4e2d\uff0c\u4fbf\u6025\u5fd9\u5bfb\u89c5\u4e86\u4e00\u4e2a\u5185\u76d1\uff0c\u4f4e\u4f4e\u5429\u5490\u51e0\u53e5\uff0c\u7136\u5f8c\u5230\u4e73\u5a18\u5e9c\u606d\u5019\u901a\u4f20\u3002 \u3000\u3000\u518d\u8bf4\u94c1\u98de\u9f99\u56de\u5230\u957f\u5b89\u9556\u5c40\uff0c\u542c\u7389\u7f57\u5239\u8c08\u8d77\u5ba2\u5a09\u5a77\u653e\u706b\u711a\u5c4b\u4e4b\u4e8b\uff0c\u6380\u9b13\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5ba2 \u6c0f\u8fd9\u5996\u5987\u8fd8\u6709\u5982\u6b64\u4e00\u4e2a\u5973\u513f\uff01\u201d\u63a5\u7740\u4ed6\u4e5f\u548c\u7389\u7f57\u5239\u8c08\u8d77\uff0c\u5df2\u7ea6\u4e86\u6155\u5bb9\u51b2\u5355\u6253\u72ec\u6597\u4e4b\u4e8b\u3002 \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u6562\u6240\u5b9a\u4ed6\u771f\u80af\u5355\u8eab\u8d74\u7ea6\u5417\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6155\u5bb9\u51b2\u4ee5\u82f1\u96c4\u81ea\u547d\uff0c\u4ed6\u82e5\u4e0d \u6765\uff0c\u5c82\u4e0d\u6015\u6c5f\u6e56\u7b11\u8bdd\u5417\uff1f\u88f3\u513f\uff0c\u6211\u53ef\u8981\u5148\u548c\u4f60\u8bf4\u597d\uff0c\u4f60\u5343\u4e07\u4e0d\u80fd\u51fa\u624b\uff0c\u4f60\u82e5\u51fa\u624b\uff0c\u54b1\u4eec\u7236\u5973 \u7684\u60c5\u4efd\u4fbf\u65ad\u4e86\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u8fd9\u70b9\u6c5f\u6e56\u89c4\u77e9\u96be\u9053\u6211\u8fd8\u4e0d\u61c2\uff01\u201d\u94c1\u98de\u9f99\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60 \u61c2\u3002\u4f46\u6211\u4e5f\u77e5\u9053\u4f60\u6700\u559c\u6b22\u548c\u4eba\u6253\u67b6\u3002\u201d\u7389\u7f57\u5239\u4e00\u7b11\u8d70\u51fa\uff0c\u5077\u5077\u53bb\u627e\u957f\u5b89\u9556\u5c40\u7684\u603b\u9556\u5934\u9f99\u8fbe\u53c1 \u5546\u91cf\u3002 \u3000\u3000\u53c1\u5929\u4e4b\u5f8c\uff0c\u94c1\u98de\u9f99\u5230\u79d8\u9b54\u5d16\u7b49\u5019\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u679c\u7136\u89c1\u6155\u5bb9\u51b2\u5355\u8eab\u524d\u6765\u3002\u94c1\u98de\u9f99\u60f3\u8d77\u5973\u513f \u7684\u4ec7\u6068\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u5927\u543c\u4e00\u58f0\uff0c\u6258\u5730\u8df3\u5c06\u51fa\u53bb\u3002\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u94c1\u8001\u513f\uff0c\u4f60\u6211\u4f55\u5fc5\u4e00\u62da\u751f \u6b7b\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6740\u4eba\u507f\u547d\uff0c\u6b20\u503a\u8fd8\u94b1\uff01\u201d\u6155\u5bb9\u51b2\u6253\u4e86\u4e00\u4e2a\u5bd2\u98a4\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u51e0\u5e74\u6765\uff0c\u6211 \u867d\u7136\u66ff\u9b4f\u5fe0\u8d24\u6740\u8fc7\u4e0d\u5c11\u597d\u4eba\u3002\u4f46\u94c1\u73ca\u745a\u53ef\u4e0d\u662f\u6211\u52a8\u624b\u6740\u7684\u3002\u201d\u94c1\u98de\u9f99\u53c8\u559d\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u80af\u4e0a \u524d\u9886\u6b7b\u5417\uff1f\u4f60\u60f3\u5411\u6211\u6c42\u60c5\u90a3\u53ef\u662f\u4e07\u4e07\u4e0d\u80fd\uff01\u201d\u6155\u5bb9\u51b2\u54fa\u54fa\u8bf4\u9053\uff1a\u201c\u6740\u4eba\u507f\u547d\uff0c\u6b20\u503a\u8fd8\u94b1\uff1f\u597d \u5440\uff01\u90a3\u4f60\u5c31\u52a8\u624b\u5427\uff01\u9119\u662f\u6211\u6155\u5bb9\u51b2\u4e5f\u975e\u65e0\u540d\u4e4b\u8f88\uff0c\u54b1\u4eec\u5c31\u6bd4\u53c1\u573a\uff0c\u4f60\u82e5\u80dc\u4e86\uff0c\u6211\u628a\u547d\u7ed9 \u4f60\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u600e\u9ebd\u6bd4\u6cd5\uff1f\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u6587\u6bd4\u4e00\u573a\uff0c\u6b66\u6bd4\u4e00\u573a\uff0c\u534a\u6587\u534a\u6b66\u4e5f\u6bd4\u4e00 \u573a\u3002\u201d\u94c1\u98de\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u54fc\uff0c\u8c01\u8010\u70e6\u548c\u4f60\u6587\u6bd4\u6b66\u6bd4\uff0c\u7f57\u5506\u4e0d\u6e05\u3002\u6211\u544a\u8bc9\u4f60\uff0c\u8fd9\u662f\u62a5\u4ec7\uff0c\u4e0d\u662f \u6bd4\u6b66\u3002\u54b1\u4eec\u4e7e\u8106\u5728\u62f3\u5934\u4e0a\u89c1\u4e2a\u8f93\u5b34\uff01\u201d\u53cc\u81c2\u7b95\u5f20\uff0c\u4e00\u63a0\u4e08\u8bb8\uff0c\u9aa4\u7136\u5c55\u51fa\u4e03\u64d2\u638c\u4e2d\u7684\u6740\u624b\uff0c\u5411 \u6155\u5bb9\u51b2\u80cc\u5fc3\u4fbf\u6293\uff0c\u6155\u5bb9\u51b2\u6012\u53eb\u9053\uff1a\u201c\u94c1\u8001\u513f\uff0c\u4f60\u6b3a\u6211\u592a\u751a\uff1f\u201d\u60f3\u9053\uff1a\u201c\u6211\u672c\u5e94\u548c\u4ed6\u89e3\u91ca\uff0c\u4f46 \u6b64\u65f6\u8ba9\u4ed6\uff0c\u4ed6\u53cd\u5f53\u6211\u662f\u6015\u4ed6\u4e86\u3002\u201d\u8eab\u8eaf\u4e00\u77ee\uff0c\u55d6\u7684\u4e00\u62f3\u5411\u94c1\u98de\u9f99\u80f8\u819b\u6253\u53bb\uff0c\u94c1\u98de\u9f99\u4e00\u638c\u62e8 \u5f00\uff0c\u4e24\u4eba\u98ce\u9a70\u7535\u63a3\u822c\u7684\u6253\u5c06\u8d77\u6765\uff0c\u62f3\u638c\u8d77\u5904\uff0c\u5168\u5e26\u52b2\u98ce\uff0c\u970e\u65f6\u95f4\u7802\u98de\u77f3\u8d70\uff0c\u6797\u9e1f\u60ca\u98de\u3002 \u3000\u3000\u4e00\u4e2a\u662f\u795e\u62f3\u65e0\u654c\uff0c\u4e00\u4e2a\u662f\u94c1\u638c\u65e0\u53cc\uff0c\u6597\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u662f\u4e0d\u5206\u80dc\u8d1f\u3002\u7389\u7f57\u5239\u9690\u8eab\u5728\u5cf0\u9876\u89c2 \u671b\uff0c\u4e5f\u89c9\u89e6\u76ee\u60ca\u5fc3\u3002\u672c\u6765\u94c1\u98de\u9f99\u662f\u4e0d\u8bb8\u7389\u7f57\u5239\u6765\u7684\uff0c\u7389\u7f57\u5239\u5374\u5077\u5077\u548c\u9f99\u8fbe\u53c1\u6765\u4e86\u3002\u4ed6\u4eec\u6015\u94c1 \u98de\u9f99\u770b\u89c1\u3002\u6240\u4ee5\u57cb\u4f0f\u5728\u79d8\u9b54\u5ca9\u5ca9\u9876\u7684\u5927\u77f3\u4e4b\u5f8c\uff0c\u5728\u77f3\u9699\u4e2d\u5f20\u671b\u51fa\u53bb\u3002 \u3000\u3000\u6253\u4e86\u4e00\u9635\uff0c\u5ffd\u89c1\u6155\u5bb9\u51b2\u8fde\u8fde\u5927\u543c\uff0c\u62f3\u5982\u96e8\u4e0b\uff0c\u94c1\u98de\u9f99\u6b65\u6b65\u9000\u8ba9\uff0c\u9f99\u8fbe\u53c1\u5927\u4e3a\u5403\u60ca\u3002\u7389\u7f57 \u5239\u7b11\u9053\uff1a\u201c\u65e0\u59a8\uff0c\u6155\u5bb9\u51b2\u4ed7\u7740\u8eab\u5f3a\u529b\u58ee\uff0c\u60f3\u4e00\u9f13\u6c14\u628a\u6211\u7239\u7239\u6253\u5012\u3002\u7528\u7684\u662f\u5ce8\u7709\u9547\u5c71\u4e4b\u5b9d\u7684\u964d \u9f99\u4f0f\u864e\u62f3\u3002\u53ef\u662f\u6211\u7239\u7239\u89e3\u62c6\u5f97\u975e\u5e38\u4e4b\u597d\uff0c\u4ed6\u7528\u7684\u662f\u534a\u5b88\u534a\u653b\u7684\u96f7\u9706\u516b\u5366\u638c\uff0c\u4f60\u4e0d\u89c1\u4ed6\u7684\u638c\u6cd5 \u6b65\u6cd5\u4e1d\u6beb\u4e0d\u4e71\u5417\uff1f\u201d \u3000\u3000\u9f99\u8fbe\u53c1\u4e00\u770b\uff0c\u89c1\u94c1\u98de\u9f99\u811a\u8e0f\u516b\u5366\u65b9\u4f4d\u3002\u4e86\u867d\u7136\u8fde\u8fde\u5f8c\u9000\uff0c\u5374\u679c\u7136\u662f\u4e1d\u6beb\u4e0d\u4e71\uff0c\u638c\u6cd5\u6c89\u7a33 \u4e4b\u6781\uff0c\u5c71\u98ce\u8fc7\u8033\uff0c\u9690\u9690\u631f\u6709\u98ce\u96f7\u4e4b\u58f0\u3002\u9f99\u8fbe\u53c1\u7b11\u9053\uff1a\u201c\u6211\u4e45\u95fb\u94c1\u8001\u7684\u96f7\u9706\u516b\u5366\u638c\u4e2d\u6709\u4e00\u79cd\u4e13 \u89e3\u5f3a\u654c\u653b\u52bf\u7684\u53cd\u653b\u638c\u6cd5\uff0c\u5374\u4ece\u672a\u66fe\u89c1\u4ed6\u7528\u8fc7\u3002\u4e0d\u56fe\u4eca\u65e5\u5f97\u89c1\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u5feb\u770b\uff01\u8198\u770b\uff01 \u8fd9\u6837\u7684\u62f3\u6cd5\uff0c\u4f60\u82e5\u9519\u8fc7\uff0c\u4eca\u751f\u5c31\u96be\u5f97\u518d\u6709\u673a\u4f1a\u4e86\u3002\u201d\u9f99\u8fbe\u53c1\u4e0d\u518d\u8bf4\u8bdd\uff0c\u51dd\u795e\u89c2\u770b\uff0c\u53ea\u89c1\u5ca9\u4e0b \u5f62\u52bf\u53c8\u53d8\uff0c\u94c1\u98de\u9f99\u4e00\u58f0\u5927\u543c\uff0c\u53cc\u638c\u8fde\u73af\u75be\u51fb\uff0c\u6eda\u6eda\u800c\u4e0a\uff0c\u8fd9\u56de\u8f6e\u5230\u6155\u5bb9\u51b2\u8fde\u8fde\u5f8c\u9000\u4e86\u3002 \u3000\u3000\u9f99\u8fbe\u53c1\u559c\u9053\uff1a\u201c\u59dc\u662f\u8001\u7684\u8fa3\u3002\u6155\u5bb9\u51b2\u6b66\u529f\u867d\u9ad8\uff0c\u4e0d\u662f\u4f60\u4e7e\u7239\u5bf9\u624b\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u8981\u5206 \u80dc\u8d1f\uff0c\u90a3\u8fd8\u65e9\u5462\uff01\u201d\u4f46\u89c1\u6155\u5bb9\u51b2\u867d\u7136\u5f8c\u9000\uff0c\u62f3\u6cd5\u4e5f\u4ecd\u4e0d\u4e71\u3002\u539f\u6765\u6155\u5bb9\u51b2\u4e5f\u662f\u7ecf\u9a8c\u975e\u5e38\u4e30\u5bcc\u4e4b \u4eba\uff0c\u4e00\u89c1\u5f3a\u653b\u4e0d\u4e0b\uff0c\u7acb\u523b\u53d8\u62db\u3002\u5c06\u4e03\u5341\u4e8c\u8def\u795e\u62f3\uff0c\u5c55\u5f97\u98ce\u96e8\u4e0d\u900f\uff01\u94c1\u98de\u9f99\u638c\u6cd5\u867d\u7136\u51cc\u5389\u4e4b \u6781\uff0c\u5374\u4e5f\u653b\u4e0d\u7834\u4ed6\u7684\u94c1\u58c1\u94dc\u5899\u3002 \u3000\u3000\u4e24\u4eba\u62da\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u662f\u4e0d\u5206\u80dc\u8d1f\u3002\u84e6\u542c\u5f97\u94c1\u98de\u9f99\u548c\u6155\u5bb9\u51b2\u4e00\u9f50\u5927\u543c\uff0c\u6155\u5bb9\u51b2\u201c\u84ec\u201d\u7684 \u4e00\u62f3\uff0c\u6253\u4e2d\u4e86\u94c1\u98de\u9f99\u80a9\u818a\uff1a\u94c1\u98de\u9f99\u9739\u96f3\u4e00\u638c\uff0c\u4e5f\u626b\u4e2d\u4e86\u6155\u5bb9\u51b2\u8170\u9aa8\uff0c\u4e24\u4eba\u5404\u8fd0\u5185\u529f\u62b5\u5fa1\uff0c\u5411 \u65c1\u659c\u8dc3\u53c1\u6b65\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u53eb\u9053\uff1a\u201c\u94c1\u8001\u513f\uff0c\u4f60\u6253\u4e0d\u5b34\u6211\uff0c\u6211\u4e5f\u6253\u4e0d\u5b34\u4f60\uff0c\u8fd9\u573a\u626f\u5e73\u4e86\u5427\u3002\u86ee\u6253\u6709\u4ec0\u9ebd\u610f \u601d\uff1f\u201d\u94c1\u98de\u9f99\u6012\u9053\uff1a\u201c\u54b1\u4eec\u4eca\u65e5\u662f\u6b7b\u65b9\u4f11\uff01\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u8fd9\u573a\u7b97\u662f\u6b66\u6bd4\uff0c\u54b1\u4eec\u518d\u6bd4\u4e00\u573a\u6587 \u7684\uff0c\u4e00\u573a\u534a\u6587\u534a\u6b66\u7684\u3002\u6211\u82e5\u8f93\u4e86\uff0c\u5c31\u5728\u4f60\u7684\u773c\u524d\u81ea\u5c3d\uff01\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u541b\u5b50\u4e00\u8a00\uff0c\u201d\u6155\u5bb9\u51b2 \u63a5\u9053\uff1a\u201c\u5feb\u9a6c\u4e00\u97ad\uff01\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u90a3\u9ebd\u5c31\u4f9d\u4f60\u5212\u51fa\u7684\u9053\u513f\u529e\uff0c\u5982\u4f55\u6bd4\u6cd5\uff1f\u201d \u3000\u3000\u6155\u5bb9\u51b2\u53d6\u51fa\u4e00\u4e2a\u76db\u6697\u5668\u7684\u76ae\u56ca\u5c06\u6697\u5668\u503e\u5728\u5730\u4e0a\u3002\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6bd4\u6697\u5668\u5417\uff1f\u8fd9\u53ef\u662f\u6bd4\u6b66 \u5440\uff01\u201d\u5fc3\u9053\uff1a\u6211\u4ece\u6765\u4e0d\u7528\u6697\u5668\uff0c\u4ed6\u82e5\u8981\u548c\u6211\u6bd4\u6697\u5668\uff0c\u6211\u5c31\u4ecd\u662f\u7ed9\u4ed6\u86ee\u6253\u3002\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u6697\u5668 \u662f\u6253\u5c0f\u8d3c\u7528\u7684\uff0c\u5bf9\u4ed8\u4f60\u8fd9\u4e2a\u8001\u8d3c\uff0c\u6697\u5668\u9876\u5f97\u4ec0\u9ebd\u7528\uff1f\u201d\u94c1\u98de\u9f99\u634b\u987b\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u9053\u5c31\u597d \u4e86\uff01\u201d\u6155\u5bb9\u51b2\u8fd9\u51e0\u53e5\u8bdd\uff0c\u4ed6\u5341\u5206\u53d7\u7528\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u4f60\u6211\u90fd\u4ee5\u62f3\u638c\u79f0\u96c4\uff0c\u5185\u529f\u89c1\u80dc\u3002\u54b1\u4eec\u5c31\u6bd4\u6bd4\u8155\u529b\u3002\u8fd9\u91cc\u6709\u8bb8\u591a\u5927\u6811\uff0c\u54b1\u4eec \u5c31\u4ee5\u624b\u4f5c\u65a7\uff0c\u5404\u65ab\u5341\u682a\u3002\u770b\u5230\u5e95\u662f\u8c01\u5389\u5bb3\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u597d\uff0c\u6211\u5949\u966a\uff0c\u4f46\u82e5\u4f60\u6211\u90fd\u80fd\u505a \u5230\uff0c\u53c8\u600e\u9ebd\u6837\uff1f\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u6240\u4ee5\u5c31\u8981\u8fd9\u4e2a\u76ae\u56ca\u4e86\u3002\u201d\u6298\u4e86\u4e00\u6839\u6811\u679d\uff0c\u5411\u76ae\u56ca\u4e00\u63d2\uff0c\u523a\u7a7f \u4e86\u4e00\u4e2a\u5c0f\u5f0a\uff0c\u5230\u5c71\u6da7\u53bb\u88c5\u6ee1\u4e86\u6c34\uff0c\u6c34\u4ece\u76ae\u56ca\u4e2d\u4e00\u6ef4\u6ef4\u7684\u6e2d\u51fa\u6765\u3002\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u4f60\u660e\u767d\u4e86\u5417\uff1f \u4f10\u4e86\u5341\u682a\u6811\u5f8c\uff0c\u6c34\u672a\u6f0f\u5b8c\u7684\u5c31\u7b97\u80dc\u4e86\u3002\u82e5\u5927\u5bb6\u90fd\u80fd\u5728\u672a\u6f0f\u5b8c\u4e4b\u524d\u5c06\u6811\u65ab\u5012\uff0c\u90a3\u9ebd\u5c31\u770b\u76ae\u56ca\u4e2d \u5269\u6c34\u7684\u591a\u5be1\uff0c\u4ee5\u5b9a\u8f93\u5b34\uff0c\u54b1\u4eec\u90fd\u4e0d\u662f\u80e1\u8d56\u7684\u4eba\uff0c\u4f60\u8fd8\u6709\u4ec0\u9ebd\u7591\u95ee\uff1f\u201d \u3000\u3000\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6811\u7684\u5927\u5c0f\u4e5f\u6709\u4e0d\u540c\u3002\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u90a3\u66f4\u6613\u529e\u4e86\uff0c\u54b1\u4eec\u5148\u5708\u5b9a\u4e8c\u5341\u682a\u6811\uff0c \u5206\u6210\u4e24\u7ec4\uff0c\u6bcf\u7ec4\u5341\u682a\uff0c\u76f8\u5dee\u603b\u4e0d\u4f1a\u8fdc\u4e86\u3002\u201d\u94c1\u98de\u9f99\u9053\u4e86\u58f0\u597d\uff0c\u548c\u6155\u5bb9\u51b2\u9009\u4e86\u4e8c\u5341\u682a\u6700\u575a\u5b9e\u7684 \u5927\u6811\uff0c\u4e00\u4e00\u505a\u4e86\u8bb0\u53f7\u3002 \u3000\u3000\u94c1\u98de\u9f99\u9053\uff1a\u201c\u884c\u4e86\uff1f\u201d\u6155\u5bb9\u51b2\u518d\u5230\u5c71\u6da7\u4e2d\u88c5\u6ee1\u4e86\u6c34\uff0c\u7528\u7eb8\u56e2\u585e\u7740\u5c0f\u5f0a\uff0c\u6302\u5728\u6811\u4e0a\uff0c\u9053\uff1a \u201c\u8fd9\u6837\u8c01\u4e5f\u505a\u4e0d\u4e86\u624b\u811a\u3002\u201d\u94c1\u98de\u9f99\u4e00\u5377\u8863\u8896\uff0c\u4fbf\u8981\u52a8\u624b\u4f10\u6811\uff0c\u6155\u5bb9\u51b2\u5ffd\u53eb\u9053\uff1a\u201c\u6211\u5148\u6765\uff01\u8fd9 \u4e3b\u610f\u662f\u6211\u51fa\u7684\uff0c\u5e94\u8be5\u5148\u505a\u7ed9\u4f60\u770b\u3002\u6211\u4e00\u52a8\u624b\uff0c\u4f60\u4fbf\u628a\u7eb8\u56e2\u62d4\u51fa\u6765\u3002\u201d\u94c1\u98de\u9f99\u9000\u5230\u76ae\u8944\u65c1\u8fb9\uff0c \u542c\u4ed6\u4e00\u58f0\u5927\u53eb\uff0c\u7acb\u523b\u4fbf\u62d4\u7eb8\u56e2\uff0c\u53ea\u89c1\u6155\u5bb9\u51b2\u51b2\u7740\u5927\u6811\u84ec\u84ec\u84ec\u6253\u4e86\u4e94\u4e03\u62f3\uff0c\u53cc\u624b\u5408\u62b1\u4e00\u6273\uff0c\u559d \u58f0\u201c\u5012\uff01\u201d\u90a3\u682a\u5927\u6811\u679c\u7136\u5e94\u58f0\u5012\u4e0b\uff0c\u94c1\u98de\u9f99\u5fc3\u9053\uff1a\u201c\u5514\uff0c\u4ed6\u662f\u4ee5\u5185\u529b\u9707\u52a8\u5927\u6811\uff0c\u7136\u5f8c\u6273\u5012\uff0c \u867d\u7136\u6709\u70b9\u53d6\u5de7\uff0c\u5185\u529f\u4e5f\u7b97\u767b\u5cf0\u9020\u6781\u7684\u4e86\uff01\u201d \u3000\u3000\u6155\u5bb9\u51b2\u4f9d\u6cd5\u62d4\u5012\u5341\u682a\u5927\u6811\uff0c\u5c06\u76ae\u56ca\u53d6\u4e0b\uff0c\u56ca\u4e2d\u7684\u6c34\u521a\u521a\u6ef4\u5b8c\u3002\u5b88\u4e86\u8bb8\u4e45\uff0c\u624d\u6ef4\u5f97\u4e00\u6ef4\uff0c \u4ee5\u5f8c\u5c31\u6ca1\u6709\u4e86\u3002\u6155\u5bb9\u51b2\u7b11\u9053\uff1a\u201c\u597d\u9669\u3002\u73b0\u5728\u8f6e\u5230\u4f60\u4e86\u3002\u201d\u94c1\u98de\u9f99\u4e5f\u5230\u5c71\u6da7\u4e2d\u5c06\u76ae\u56ca\u76db\u6ee1\u4e86 \u6c34\uff0c\u4ee5\u7eb8\u56e2\u585e\u7740\uff0c\u6302\u5728\u6811\u4e0a\u3002\u770b\u4e86\u6155\u5bb9\u51b2\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u5fc5\u4f60\u62d4\u7eb8\u56e2\u3002\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u90a3 \u4f60\u5c82\u4e0d\u5403\u4e8f\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6211\u5b81\u613f\u7a0d\u5403\u70b9\u4e8f\u3002\u201d\u500f\u7684\u62d4\u5f00\u4e86\u7eb8\u56e2\uff0c\u7136\u5f8c\u53bb\u5bfb\u505a\u4e86\u8bb0\u53f7\u7684\u5927 \u6811\u3002\u539f\u6765\u94c1\u98de\u9f99\u662f\u6015\u6155\u5bb9\u51b2\u5728\u62d4\u7eb8\u56e2\u65f6\u5f04\u9b3c\u3002\u6155\u5bb9\u51b2\u6697\u7b11\u9053\uff1a\u201d\u4e0d\u6015\u4f60\u8001\u9b3c\u6210\u7cbe\uff0c\u4f60\u4e5f\u8981\u4e0a \u6211\u7684\u5f53\u3002\u6211\u4f55\u5fc5\u505a\u90a3\u4e9b\u4e0b\u53c1\u6d41\u7684\u4e8b\uff0c\u5728\u62d4\u7eb8\u56e2\u65f6\u5f04\u9b3c\u4e86\u4e0d\u9700\u8fd9\u6837\uff0c\u4f60\u5df2\u5403\u4e8f\u3002\u201d \u3000\u3000\u94c1\u98de\u9f99\u4f10\u6811\u53c8\u4e0e\u6155\u5bb9\u51b2\u4e0d\u540c\uff0c\u53ea\u89c1\u4ed6\u7ed5\u6811\u4e00\u531d\uff0c\u53cc\u638c\u6a2a\u5288\uff0c\u8fc5\u75be\u4e4b\u6781\uff0c\u7136\u5f8c\u7528\u529b\u4e00\u63a8\uff0c \u90a3\u682a\u6811\u4fbf\u9f50\u6839\u65ad\u4e86\uff0c\u771f\u5982\u65a7\u4f10\u4e00\u822c\u3002\u6155\u5bb9\u51b2\u6697\u6697\u5403\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u4ed6\u7684\u638c\u529b\u679c\u7136\u5728\u6211\u4e4b\u4e0a\u3002\u6211 \u82e5\u5230\u4ed6\u90a3\u6837\u5e74\u7eaa\uff0c\u4e00\u5b9a\u6253\u4ed6\u4e0d\u8fc7\u4e86\u3002\u201d\u94c1\u98de\u9f99\u4f9d\u6cd5\u5288\u4e86\u5341\u682a\u5927\u6811\uff0c\u81ea\u89c9\u6240\u7528\u7684\u65f6\u95f4\u8981\u6bd4\u6155\u5bb9 \u51b2\u77ed\uff0c\u559c\u6d0b\u6d0b\u7684\u56de\u6765\uff0c\u5c06\u76ae\u56ca\u53d6\u4e0b\uff0c\u4e0d\u6599\u56ca\u4e2d\u7684\u6c34\u4e5f\u662f\u521a\u521a\u6ef4\u5b8c\uff0c\u5b88\u4e86\u8bb8\u4e45\uff0c\u624d\u6ef4\u51fa\u4e00\u6ef4\uff0c \u4ee5\u5f8c\u5c31\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u94c1\u98de\u9f99\u5927\u60d1\u4e0d\u89e3\uff0c\u5fc3\u60f3\uff1a\u201c\u6309\u8bf4\u6155\u5bb9\u51b2\u4e0d\u4f1a\u505a\u624b\u811a\u3002\u800c\u4e14\u6211\u5288\u6811\u4e4b\u65f6\uff0c\u4e5f\u4e00\u76f4\u7559\u5fc3\uff0c\u4ed6 \u82e5\u505a\u4e86\u624b\u811a\uff0c\u4e5f\u7792\u4e0d\u8fc7\u6211\u7684\u773c\u775b\u3002\u201d\u53ef\u662f\u4ed6\u5374\u4e00\u65f6\u60f3\u4e0d\u8d77\u6765\uff1a\u76ae\u56ca\u7684\u5c0f\u5f0a\uff0c\u53d7\u6c34\u529b\u6240\u538b\uff0c\u5fc5 \u7136\u4f1a\u6162\u6162\u6269\u5927\uff0c\u867d\u7136\u6240\u6269\u751a\u5fae\uff0c\u4f46\u5230\u5e95\u662f\u6709\u6240\u5dee\u5f02\u3002\u6240\u4ee5\u8c01\u5148\u52a8\u624b\uff0c\u4fbf\u8c01\u5360\u4fbf\u5b9c\u3002\u6155\u5bb9\u51b2\u8fd9 \u6b21\u5728\u5fc3\u91cc\u53eb\u58f0\uff1a\u201c\u597d\u9669\u3002\u201d\u89c1\u94c1\u98de\u9f99\u4e00\u6d3e\u60d8\u7136\u7684\u795e\u60c5\uff0c\u7b11\u9053\uff1a\u201c\u5982\u4f55\uff01\u8fd9\u4e00\u573a\u54b1\u4eec\u53c8\u626f\u5e73 \u4e86\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u5728\u5ca9\u9876\u5c06\u4e00\u5207\u90fd\u770b\u4eba\u773c\u5185\uff0c\u542c\u5230\u8033\u4e2d\uff0c\u4e0d\u89c9\u5bf9\u9f99\u8fbe\u53c1\u9053\uff1a\u201c\u54fc\uff0c\u6155\u5bb9\u51b2\u597d\u4e0d\u8981 \u8138\uff0c\u660e\u660e\u662f\u8f93\u4e86\uff0c\u5374\u8bf4\u662f\u626f\u5e73\u3002\u6211\u4e0b\u53bb\u63ed\u7834\u4ed6\uff01\u201d\u9f99\u8fbe\u53c1\u5728\u5979\u8033\u8fb9\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u94c1\u8001\u8d23\u602a \u9ebd\uff1f\u201d\u7389\u7f57\u5239\u4e00\u542c\uff0c\u53ea\u597d\u5fcd\u4f4f\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4e14\u518d\u770b\u4e00\u573a\uff0c\u770b\u4ed6\u8fd8\u6709\u4ec0\u9ebd\u574f\u4e3b\u610f\u3002\u201d \u3000\u3000\u6155\u5bb9\u51b2\u6b63\u60f3\u518d\u8bf4\u51fa\u7b2c\u53c1\u573a\u76f8\u6bd4\u4e4b\u6cd5\uff0c\u94c1\u98de\u9f99\u53cc\u773c\u4e00\u7ffb\uff0c\u5ffd\u5730\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u592b\u51e0\u5341\u5e74\u6253 \u730e\uff0c\u53cd\u7ed9\u96c1\u513f\u5544\u4e86\u773c\u775b\uff0c\u4e0d\u8fc7\uff0c\u4f60\u867d\u7136\u662f\u53d6\u5de7\uff0c\u4e5f\u8fd8\u4e0d\u7b97\u4e0b\u6d41\u3002\u201d\u6155\u5bb9\u51b2\u77e5\u4ed6\u5df2\u770b\u7834\u5176\u4e2d\u5965 \u5999\uff0c\u6de1\u6de1\u4e00\u7b11\u3002\u94c1\u98de\u9f99\u9053\uff1a\u201c\u7b2c\u53c1\u573a\u8be5\u662f\u534a\u6587\u534a\u6b66\u7684\u6bd4\u8bd5\u4e86\uff0c\u662f\u9ebd\uff1f\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u662f \u5440\uff01\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u8fd9\u4e00\u573a\u6211\u5012\u60f3\u8d77\u4e00\u79cd\u6bd4\u6cd5\uff0c\u4f60\u770b\u6210\u4e0d\u6210\uff01\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u8bf7\u8bf4\uff01\u201d\u94c1\u98de \u9f99\u9053\uff1a\u201c\u6211\u7684\u6bd4\u6cd5\uff0c\u53cc\u65b9\u662f\u7edd\u4e0d\u80fd\u53d6\u5de7\u7684\u4e86\uff01\u201d \u3000\u3000\u6155\u5bb9\u51b2\u9762\u8272\u5c34\u5c2c\uff0c\u9053\uff1a\u201c\u4e0d\u5fc5\u7f57\u5506\uff0c\u4f60\u8bf4\u51fa\u6765\uff0c\u6211\u6155\u5bb9\u51b2\u4e00\u51c6\u5949\u966a\u4fbf\u662f\u3002\u201d\u94c1\u98de\u9f99\u8df3\u4e0a \u4e00\u5757\u5927\u77f3\uff0c\u62db\u624b\u53eb\u6155\u5bb9\u51b2\u4e0a\u6765\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u73a9\u73a9\u63a8\u638c\u3002\u201d\u53eb\u6155\u5bb9\u51b2\u4f38\u51fa\u53cc\u638c\uff0c\u4e0e\u81ea\u5df1\u53cc\u638c\u76f8 \u62b5\uff0c\u9053\uff1a\u201c\u8c01\u7ed9\u63a8\u4e0b\u5ca9\u77f3\uff0c\u4fbf\u7b97\u8f93\u4e86\u3002\u8fd9\u6837\u867d\u7136\u56db\u638c\u76f8\u4ea4\uff0c\u5374\u53c8\u5e76\u975e\u8089\u640f\uff0c\u5c82\u4e0d\u662f\u534a\u6587\u534a\u5f0f \u7684\u6bd4\u6b66\u9ebd\uff1f\u201d\u6155\u5bb9\u51b2\u6697\u6697\u53eb\u82e6\uff0c\u5fc3\u60f3\uff1a\u201c\u770b\u6765\u8fd9\u8001\u513f\u7684\u5185\u529b\u6bd4\u6211\u9ad8\u51fa\u4e00\u7b79\u3002\u597d\u5427\uff0c\u53cd\u6b63\u6211\u4e5f \u4e0d\u6253\u7b97\u6d3b\u547d\u7684\u4e86\uff0c\u53ef\u662f\u56e0\u8f83\u6280\u8f93\u4e86\u800c\u6b7b\uff0c\u8fd9\u5374\u6b7b\u5f97\u5e76\u4e0d\u5149\u5f69\u3002\u201d\u7b97\u76d8\u4ed6\u672a\u5b9a\uff0c\u94c1\u98de\u9f99\u638c\u5fc3\u52b2 \u529b\u5df2\u53d1\uff0c\u8fd9\u65f6\u53ea\u8981\u7a0d\u4e00\u5206\u5fc3\uff0c\u4fbf\u8981\u7ed9\u5bf9\u65b9\u638c\u65b9\u9707\u4f24\u810f\u8151\u3002\u56e0\u6b64\u6155\u5bb9\u51b2\u53ea\u597d\u54ac\u5b9e\u7259\u5173\uff0c\u8fd0\u51fa\u5185 \u5bb6\u771f\u529b\uff0c\u4e0e\u94c1\u98de\u9f99\u76f8\u6297\u3002 \u3000\u3000\u4e24\u4eba\u5728\u77f3\u4e0a\u76d8\u819d\u800c\u5750\uff0c\u8fd0\u6c14\u8fd0\u529b\uff0c\u56db\u638c\u76f8\u4ea4\uff0c\u56db\u76ee\u76f8\u89c6\uff0c\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u4e24\u4eba\u90fd\u6c57\u51fa\u5982 \u96e8\u3002\u8fd9\u6837\u7684\u6bd4\u8bd5\uff0c\u6bd4\u5200\u67aa\u76f8\u62da\uff0c\u8fd8\u8981\u51f6\u9669\u767e\u500d\uff01\u53ea\u8981\u8c01\u4e00\u677e\u61c8\uff0c\u4fbf\u662f\u51c6\u6b7b\u65e0\u7591\u3002\u94c1\u98de\u9f99\u5185\u529f \u8f83\u6df1\uff0c\u53ef\u662f\u6155\u5bb9\u51b2\u80dc\u5728\u5e74\u8f7b\u529b\u58ee\uff0c\u6c14\u8db3\u795e\u5065\uff0c\u867d\u7136\u7565\u900a\u4e00\u7b79\uff0c\u4e5f\u8fd8\u62b5\u5fa1\u5f97\u4f4f\u3002 \u3000\u3000\u518d\u8fc7\u4e00\u523b\uff0c\u4e24\u4eba\u66f4\u89c9\u5fc3\u5934\u6eda\u70ed\uff0c\u5589\u5499\u7126\u71e5\uff0c\u6c57\u51fa\u66f4\u591a\uff0c\u5168\u8eab\u6e7f\u900f\u3002\u6155\u5bb9\u51b2\u77e5\u9053\u65f6\u95f4\u4e00 \u4e45\uff0c\u81ea\u5df1\u5fc5\u7136\u843d\u8d25\u3002\u53ef\u662f\u8fd9\u65f6\u5df2\u5230\u4e86\u6700\u7d27\u8981\u4e4b\u9645\uff0c\u8c01\u90fd\u4e0d\u80fd\u9000\u8ba9\u3002\u9664\u975e\u540c\u65f6\u64a4\u638c\u5316\u52b2\uff0c\u5426\u5219 \u5fc5\u53d7\u91cd\u4f24\u3002\u6155\u5bb9\u51b2\u53cc\u638c\u4f3c\u88ab\u80f6\u7740\uff0c\u60f3\u8ba4\u8f93\u4e5f\u4e0d\u53ef\u80fd\uff0c\u4f55\u51b5\u53c8\u4e0d\u6562\u5206\u5fc3\u8bf4\u8bdd\u3002 \u3000\u3000\u518d\u8fc7\u7247\u523b\uff0c\u6155\u5bb9\u51b2\u5934\u4e0a\u70ed\u6c14\u76f4\u5192\uff0c\u5fc3\u4e2d\u7126\u8e81\uff0c\u8fd0\u8db3\u6c14\u529b\u76f8\u5fa1\u3002\u4ed6\u4eec\u6240\u5750\u90a3\u5757\u77f3\u5934\uff0c\u6b63\u5bf9 \u7740\u79d8\u9b54\u5ca9\u4e0b\u7684\u5ca9\u6d1e\uff0c\u6b63\u5728\u5403\u7d27\u4e4b\u9645\uff0c\u5ca9\u6d1e\u4e2d\u5ffd\u7136\u53d1\u51fa\u4e00\u58f0\u602a\u7b11\uff0c\u8fde\u57ce\u864e\u5e26\u7740\u51e0\u4e2a\u4eba\u5728\u6d1e\u4e2d\u51b2 \u4e86\u51fa\u6765\uff0c\u51e0\u822c\u5175\u5668\u500f\u7684\u5411\u94c1\u98de\u9f99\u8eab\u4e0a\u65ab\u53bb\uff01 \u3000\u3000\u539f\u6765\u90a3\u65e5\u5e94\u4fee\u9633\u53d1\u73b0\u6155\u5bb9\u51b2\u795e\u8272\u6709\u5f02\uff0c\u6084\u6084\u53eb\u5185\u76d1\u4f20\u8bdd\u7ed9\u8fde\u57ce\u864e\uff0c\uff0e\u53eb\u4ed6\u7559\u795e\u6155\u5bb9\u51b2\u3002 \u81ea\u5df1\u4ecd\u5230\u4e73\u5a18\u5e9c\u5411\u5ba2\u6c0f\u6bcd\u5973\u8bf7\u5b89\u3002\u8fde\u57ce\u864e\u5230\u6155\u5bb9\u51b2\u623f\u95f4\u53bb\u770b\uff0c\u6155\u5bb9\u51b2\u5df2\u7ecf\u4e0d\u5728\uff0c\u67e5\u95ee\u4e4b\u4e0b\uff0c \u77e5\u9053\u6155\u5bb9\u51b2\u4e00\u65e9\u51fa\u5bab\u53bb\u4e86\u3002\u8fde\u57ce\u864e\u5fc3\u4e2d\u8d77\u7591\uff0c\u4ed4\u7ec6\u641c\u4ed6\u623f\u95f4\uff0c\u5728\u5899\u89d2\u53d1\u73b0\u94c1\u98de\u9f99\u7ea6\u4ed6\u5355\u6253\u72ec \u6597\u7684\u5c0f\u7eb8\u56e2\u3002\u5fc3\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\u3002\u4ed6\u60f3\u5fc5\u662f\u53e6\u9080\u9ad8\u624b\u53bb\u4e86\u3002\u4f46\u4e3a\u4ec0\u9ebd\u8fde\u6211\u4eec\u4e5f\u4e0d\u544a\u8bc9\u4e00\u58f0 \u5462\uff1f\u201d\u62ff\u4e86\u7eb8\u56e2\uff0c\u53bb\u627e\u5e94\u4fee\u9633\u3002\u5374\u4e0d\u6599\u904d\u627e\u65e0\u8e2a\uff0c\u65e2\u4e0d\u5728\u4e73\u5a18\u5e9c\uff0c\u4e5f\u4e0d\u5728\u9752\u9633\u5bab\uff0c\u7adf\u4e0d\u77e5\u5230 \u90a3\u91cc\u53bb\u4e86\u3002\u5230\u4e86\u508d\u665a\u65f6\u4efd\uff0c\u4e73\u5a18\u5e9c\u4f20\u51fa\u66f4\u60ca\u4eba\u7684\u6d88\u606f\uff1a\u5ba2\u5a09\u5a77\u4e5f\u5931\u8e2a\u4e86\u3002 \u3000\u3000\u5ba2\u5a09\u5a77\u662f\u5348\u95f4\u4e58\u900d\u9065\u8f66\u51fa\u5bab\u7684\uff0c\u5230\u4e86\u508d\u665a\uff0c\u4e0d\u89c1\u56de\u6765\uff0c\u5ba2\u6c0f\u6d3e\u4eba\u5230\u4eac\u90ca\u5404\u5730\u540d\u80dc\u6240\u5728\u5bfb \u89c5\uff0c\u5728\u897f\u5c71\u53d1\u73b0\u900d\u9065\u8f66\u88ab\u6253\u5f97\u7c89\u788e\uff0c\u4eba\u5374\u56db\u89c5\u65e0\u8e2a\uff0c\u5ba2\u6c0f\u54ed\u5f97\u6b7b\u53bb\u6d3b\u6765\uff0c\u4e00\u9762\u6d3e\u4eba\u53bb\u627e\uff0c\u4e00 \u9762\u5230\u5973\u513f\u623f\u4e2d\u67e5\u770b\uff0c\u4e00\u67e5\u4e4b\u4e0b\uff0c\u624d\u5728\u62bd\u5c49\u4e2d\u53d1\u73b0\u5ba2\u5a09\u5a77\u7684\u7559\u4e66\uff0c\u8bf4\u662f\u6c38\u4e0d\u56de\u6765\u4e86\u3002 \u3000\u3000\u8fde\u57ce\u864e\u5c06\u4e00\u5207\u60c5\u51b5\u62a5\u544a\u7ed9\u9b4f\u5fe0\u8d24\u3002\u9b4f\u5fe0\u8d24\u7d20\u6027\u591a\u7591\uff0c\u5fc3\u60f3\uff1a\u83ab\u975e\u5e94\u4fee\u9633\u7684\u5931\u8e2a\uff0c\u5ba2\u5a09\u5a77 \u7684\u51fa\u8d70\uff0c\u4e0e\u6155\u5bb9\u51b2\u7684\u8d74\u7ea6\uff0c\u90fd\u6709\u8fde\u5e26\u5173\u7cfb\uff0c\u4fbf\u9053\uff1a\u201c\u6155\u5bb9\u51b2\u4e00\u5929\u4e00\u591c\u4e0d\u89c1\u56de\u6765\uff0c\u53c8\u4e0d\u5c06\u94c1\u98de \u9f99\u7ea6\u4ed6\u6bd4\u6b66\u4e4b\u4e8b\u4e0a\u7980\uff0c\u6b64\u4e8b\u53ef\u7591\u3002\u5468\u65e5\u4f60\u548c\u51e0\u4f4d\u597d\u624b\u8d74\u79d8\u9b54\u5ca9\u770b\u5427\u3002\u82e5\u6155\u5bb9\u51b2\u679c\u662f\u4e0e\u654c\u4eba\u6bd4 \u6b66\uff0c\u4f60\u4eec\u4fbf\u8d81\u673a\u5e2e\u624b\u3002\u82e5\u7136\u4e0d\u662f\uff0c\u4f60\u4eec\u5c31\u5c06\u4ed6\u4e00\u5e76\u9664\u4e86\u3002\u201d \u3000\u3000\u8fde\u57ce\u864e\u542c\u4e86\u9b4f\u5fe0\u8d24\u5429\u5490\uff0c\u5230\u4e86\u6155\u5bb9\u51b2\u6bd4\u6b66\u4e4b\u65e5\uff0c\u7ea6\u4e86\u5e94\u4fee\u9633\u6240\u9080\u8bf7\u6765\u7684\u53c1\u540d\u9ad8\u624b\uff0c\u4ee5\u53ca \u5c0f\u7687\u5e1d\u7684\u62a4\u8eab\u6cd5\u5e08\u660c\u94a6\u5927\u5587\u561b\uff0c\u5171\u662f\u53c1\u4eba\uff0c\u4e00\u65e9\u5230\u79d8\u9b54\u5ca9\u7684\u5ca9\u6d1e\u57cb\u4f0f\u3002\u4ed6\u4eec\u672c\u65f1\u60f3\u51b2\u51fa\u6765\uff0c \u53ef\u662f\u8fde\u57ce\u864e\u6015\u94c1\u98de\u9f99\u638c\u529b\u5389\u5bb3\uff0c\u5978\u7b11\u8bf4\u9053\uff1a\u201c\u7b49\u4ed6\u4eec\u4e24\u4eba\u62da\u5f97\u7b4b\u75b2\u529b\u7aed\u4e4b\u65f6\uff0c\u54b1\u4eec\u518d\u51fa\u624b\u4e0d \u662f\u66f4\u597d\u9ebd\uff1f\u201d\u660c\u94a6\u5587\u561b\u70b9\u5934\u79f0\u662f\u3002\u5176\u4ed6\u53c1\u540d\u9ad8\u624b\u4e5f\u4e50\u5f97\u6361\u4fbf\u5b9c\uff0c\u65bc\u662f\u90fd\u4f0f\u5728\u6d1e\u4e2d\u9759\u5019\u65f6\u673a\u3002 \u76f4\u5230\u6155\u5bb9\u51b2\u4e0e\u94c1\u98de\u9f99\u5404\u4ee5\u5185\u529b\u76f8\u62da\uff0c\u770b\u770b\u5c31\u8981\u4e24\u8d25\u4ff1\u4f24\u4e4b\u9645\uff0c\u4ed6\u4eec\u624d\u51b2\u51fa\u6765\u3002 \u3000\u3000\u518d\u8bf4\u94c1\u98de\u9f99\u6b63\u5728\u9ad8\u5174\uff0c\u5ffd\u89c1\u51e0\u822c\u660e\u6643\u6643\u7684\u5175\u5203\uff0c\u9f50\u5411\u81ea\u5df1\u6233\u6765\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u6155\u5bb9\u8d3c \u5b50\uff0c\u4eca\u65e5\u8001\u592b\u5f52\u5929\uff0c\u4e5f\u8981\u5148\u628a\u4f60\u5e9f\u4e86\uff01\u201d\u53cc\u638c\u53d1\u529b\uff0c\u52b2\u9053\u731b\u4e0d\u53ef\u5f53\uff01\u5ffd\u89c1\u6155\u5bb9\u51b2\u5927\u559d\u4e00\u58f0\uff0c \u53cc\u638c\u9aa4\u7136\u677e\u5f00\uff0c\u5168\u4e0d\u9632\u5fa1\u3002\u94c1\u98de\u9f99\u638c\u529b\u6253\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u4f46\u5c31\u5728\u8fd9\u7535\u5149\u77f3\u706b\u7684\u5239\u90a3\uff0c\u6155\u5bb9\u51b2\u53cd \u638c\u4e00\u626b\uff0c\u8fde\u57ce\u864e\u53cc\u94a9\u521a\u521a\u6cbe\u7740\u94c1\u98de\u9f99\u8863\u670d\uff0c\u88ab\u6155\u5bb9\u51b2\u638c\u529b\u4e00\u9707\uff0c\u53cc\u94a9\u98de\u4e0a\u534a\u7a7a\uff0c\u8155\u9aa8\u788e\u88c2\uff0c \u987f\u65f6\u683d\u5012\uff01\u6155\u5bb9\u51b2\u55b7\u51fa\u4e00\u5927\u53e3\u9c9c\u8840\uff0c\u4e5f\u6eda\u4e0b\u77f3\u53f0\u3002 \u3000\u3000\u4e8b\u51fa\u610f\u5916\uff0c\u660c\u94a6\u5927\u6cd5\u5e08\u7b49\u56db\u540d\u5927\u5185\u597d\u624b\u9f50\u90fd\u6115\u7136\uff0c\u5ffd\u542c\u5f97\u5ca9\u9876\u4e0a\u4e00\u58f0\u957f\u7b11\uff0c\u7389\u7f57\u5239\u8863\u8882 \u98d8\u98d8\uff0c\u4ece\u534a\u7a7a\u98de\u63a0\u4e0b\u6765\u3002\u957f\u5251\u5bd2\u5149\u4e00\u95ea\uff0c\u9996\u5148\u4fbf\u5954\u660c\u94a6\u6cd5\u5e08\u3002\u540c\u65f6\u53c8\u542c\u5f97\u5ca9\u4e0a\u6709\u4eba\u558a\u9053\uff1a \u201c\u9c7c\u513f\u4e0a\u9493\u4e86\uff0c\u5f1f\u5144\u4eec\u5feb\u51fa\u6765\u6349\u554a\uff1f\u201d\u58f0\u97f3\u6c89\u96c4\uff0c\u5c71\u545c\u8c37\u5e94\u3002\u539f\u6765\u7389\u7f57\u5239\u6015\u81ea\u5df1\u5be1\u4e0d\u654c\u4f17\uff0c \u6240\u4ee5\u53eb\u9f99\u8fbe\u53c1\u51fa\u58f0\u606b\u5413\u3002 \u3000\u3000\u660c\u94a6\u679c\u7136\u80c6\u5bd2\u3002\u4ed6\u9886\u6559\u8fc7\u7389\u7f57\u5239\u7684\u5389\u5bb3\uff0c\u8fde\u57ce\u864e\u53c8\u53d7\u4e86\u91cd\u4f24\uff0c\u5fc3\u60f3\u5269\u4e0b\u56db\u4eba\uff0c\u672a\u5fc5\u662f\u7389 \u7f57\u5239\u548c\u94c1\u98de\u9f99\u7684\u5bf9\u624b\uff0c\u800c\u4e14\u4ed6\u4eec\u8fd8\u6709\u57cb\u4f0f\uff0c\u6597\u5fd7\u4e00\u6d88\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u98ce\u7d27\uff0c\u626f\u4e4e\uff01\u201d\u53cc\u94b9\u529b \u6321\u7389\u7f57\u5239\u53c1\u5251\uff0c\u53e6\u4e00\u540d\u9ad8\u624b\u80cc\u8d77\u4e86\u8fde\u57ce\u864e\uff0c\u6025\u5fd9\u64a4\u9000\u4e0b\u5c71\u3002\u7389\u7f57\u5239\u6309\u5251\u4e0d\u8ffd\uff0c\u53ea\u89c1\u94c1\u98de\u9f99\u9762 \u8272\u7070\u767d\u3002\u7389\u7f57\u5239\u9053\uff1a\u201c\u7239\uff0c\u4f60\u53d7\u4e86\u4f24\u9ebd\uff1f\u201d\u94c1\u98de\u9f99\u6728\u7136\u4e0d\u8bed\uff0c\u7389\u7f57\u5239\u8e0f\u524d\u4e00\u6b65\uff0c\u5251\u5c16\u6307\u7740\u6155 \u5bb9\u51b2\uff0c\u94c1\u98de\u9f99\u5ffd\u9053\uff1a\u201c\u4e0d\u8981\u6740\u4ed6\uff01\u201d\u7389\u7f57\u5239\u6115\u7136\u6536\u5251\uff0c\u53ea\u542c\u5f97\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6211\u6ca1\u53d7\u4f24\uff0c\u662f\u4ed6 \u6551\u4e86\u6211\u7684\u6027\u547d\u3002\u5c06\u4ed6\u80cc\u4e0b\u5c71\u5427\u3002\u201d\u58f0\u97f3\u4f4e\u6c89\u82cd\u51c9\uff0c\u597d\u4f3c\u5927\u75c5\u521d\u6108\u7684\u4eba\u4e00\u822c\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u9f99\u8fbe\u53c1\u4e5f\u722c\u4e86\u4e0b\u6765\uff0c\u7b11\u9053\uff1a\u201c\u7ec3\u5973\u4fa0\uff0c\u4f60\u521a\u624d\u98de\u4e0b\u5371\u5ca9\uff0c\u5927\u7ea6\u6ca1\u770b\u6e05\u695a\u3002\u94c1 \u8001\u7684\u6027\u547d\u679c\u771f\u662f\u8fd9\u4eba\u6551\u7684\u3002\u60f3\u4e0d\u5230\u4ed6\u4f1a\u8fd9\u6837\uff01\u201d\u94c1\u98de\u9f99\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u73ca\u745a\u7684\u4ec7\u4e0d\u80fd\u62a5\u4e86\u3002\u201d \u7389\u7f57\u5239\u9053\uff1a\u201c\u73ca\u745a\u59b9\u59b9\u4e4b\u6b7b\uff0c\u4ed6\u4e5f\u662f\u51f6\u624b\u4e4b\u4e00\uff0c\u53ef\u662f\u4e0d\u662f\u4e3b\u51f6\u3002\u4e3b\u51f6\u662f\u91d1\u8001\u602a\uff0c\u5f53\u573a\u5df2\u88ab\u5cb3 \u9e23\u73c2\u6740\u4e86\u3002\u8fd8\u6709\u4e00\u4eba\u662f\u5e94\u4fee\u9633\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u5c31\u7b97\u4ed6\u662f\u4e3b\u51f6\uff0c\u4e5f\u9976\u4ed6\u4e86\u3002\u201d\u4f38\u4e86\u4f38\u8170\uff0c\u8212 \u6563\u4e00\u4e0b\u7b4b\u9aa8\uff0c\u4eb2\u81ea\u628a\u6155\u5bb9\u51b2\u80cc\u56de\u957f\u5b89\u9556\u5c40\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u53d7\u4f24\u751a\u91cd\uff0c\u94c1\u98de\u9f99\u867d\u7136\u4e86\u4ed6\u51e0\u7c92\u836f\u4e38\uff0c\u4e00\u8def\u4e0a\u4ecd\u662f\u660f\u8ff7\u4e0d\u9192\uff0c\u9f3b\u5b54\u6d41\u8840\u3002\u7389\u7f57\u5239 \u9053\uff1a\u201c\u770b\u6765\u4ed6\u4e0d\u80fd\u6d3b\u4e86\u3002\u201d\u94c1\u98de\u9f99\u751a\u4e3a\u96be\u8fc7\uff0c\u9053\uff1a\u201c\u60f3\u529e\u6cd5\u6551\u6d3b\u4ed6\uff01\u201d \u3000\u3000\u56de\u5230\u957f\u5b89\u7f25\u5c40\uff0c\u5929\u5df2\u9ec4\u660f\uff0c\u526f\u9556\u5934\u6797\u632f\u86df\u51fa\u95e8\u76f8\u63a5\uff0c\u9053\uff1a\u201c\u8c22\u5929\u8c22\u5730\uff0c\u4f60\u4eec\u5e73\u5b89\u56de\u6765 \u4e86\u3002\u54a6\uff0c\u94c1\u8001\u5c06\u4ec7\u4eba\u4e5f\u751f\u64d2\u56de\u6765\u4e86\u5417\uff1f\u771f\u597d\u672c\u9886\uff01\u4eca\u5929\u6211\u4eec\u9556\u5c40\u91cc\u51fa\u4e86\u4e00\u4ef6\u602a\u4e8b\u5462\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u4f4e\u4f4e\u5e94\u4e86\u4e00\u58f0\uff0c\u9f99\u8fbe\u53c1\u5411\u4ed6\u6253\u4e86\u4e00\u4e0b\u773c\u8272\uff0c\u95ee\u9053\uff1a\u201c\u9556\u5c40\u91cc\u51fa\u4e86\u4ec0\u9ebd\u602a\u4e8b\u5440\uff1f\u201d \u6797\u632f\u86df\u9053\uff1a\u201c\u4f60\u4eec\u8d70\u5f8c\u4e0d\u4e45\uff0c\u6709\u4e00\u4e2a\u7f69\u7740\u9762\u7eb1\u7684\u59d1\u5a18\u4e58\u7740\u9a6c\u8f66\u6765\u5230\u54b1\u4eec\u9556\u5c40\uff0c\u8bf4\u6709\u4e00\u5305\u4e1c\u897f \u8981\u4ea4\u7ed9\u7ec3\u5973\u4fa0\uff0c\u53eb\u6211\u4eec\u4e0d\u8981\u6253\u5f00\u3002\u8bf4\u7f62\u4fbf\u5728\u8f66\u4e0a\u63d0\u8d77\u4e00\u53ea\u5927\u888b\uff0c\u5411\u9556\u5c40\u7684\u9662\u5b50\u4e00\u629b\uff0c\u8ff3\u81ea\u8d70 \u4e86\u3002\u6211\u63d0\u8d77\u888b\uff0c\u6c89\u7538\u7538\u7684\uff0c\u6478\u4e00\u6478\uff0c\u91cc\u9762\u88c5\u7684\u4f3c\u4e4e\u662f\u4eba\u3002\u6211\u6025\u5fd9\u63d0\u56de\u5185\u5ba4\uff0c\u89e3\u5f00\u888b\u4e00\u770b\uff0c\u91cc \u9762\u679c\u7136\u662f\u4eba\uff01\u660f\u8ff7\u4e0d\u9192\u3002\u9152\u6c14\u6251\u9f3b\uff0c\u4f3c\u4e4e\u662f\u7ed9\u836f\u9152\u8ff7\u7740\u7684\uff0c\u518d\u770b\u4e00\u770b\uff0c\u4ed6\u8fd8\u7ed9\u4eba\u70b9\u4e86\u6655\u7a74\u3002 \u6211\u4e0d\u77e5\u8fd9\u4eba\u662f\u4ec0\u9ebd\u8eab\u4efd\uff0c\u4e0d\u6562\u5984\u81ea\u89e3\u5f00\u3002\u888b\u91cc\u6709\u4e00\u5c01\u4fe1\uff0c\u5199\u7740\u201c\u70e6\u4ea4\u7389\u7f57\u5239\u5c0f\u59d0\u201d\u3002\u201d\u7389\u7f57 \u5239\u5657\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e00\u5b9a\u662f\u5ba2\u5a09\u5a77\u4e86\u3002\u5979\u4e0d\u77e5\u9053\u6211\u7684\u540d\u5b57\uff0c\u6240\u4ee5\u53eb\u505a\u6211\u7389\u7f57\u5239\u3002\u201d\u94c1\u98de\u9f99 \u9053\uff1a\u201c\u770b\u5979\u628a\u4ec0\u9ebd\u4eba\u9001\u6765\uff1f\u201d\u62b1\u8d77\u6155\u5bb9\u51b2\u548c\u4f17\u4eba\u8d70\u8fdb\u5f8c\u9662\uff0c\u7389\u7f57\u5239\u4e00\u77a7\uff0c\u53eb\u8d77\u6765\u9053\uff1a\u201c\u54a6\uff0c \u662f\u5e94\u4fee\u9633\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u5927\u611f\u9a7e\u5947\uff0c\u9053\uff1a\u201c\u73ca\u745a\u7684\u4ec7\u53ef\u4ee5\u62a5\u4e86\uff01\u201d\u7389\u7f57\u5239\u628a\u4fe1\u62c6\u5f00\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u9053\uff1a\u201c\u7389 \u7f57\u5239\u59d0\u59d0\uff1a\u6211\u6ca1\u6709\u9762\u89c1\u4f60\uff0c\u6211\u4e0d\u80fd\u6740\u9b4f\u5fe0\u8d24\u3002\u8c28\u9001\u4e0a\u5978\u8d3c\u5e94\u4fee\u9633\u4e00\u540d\u8d4e\u7f6a\u3002\u5ba2\u5a09\u5a77\u3002\u201d\u7389\u7f57 \u5239\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u5979\u5230\u5e95\u6709\u4ec0\u9ebd\u5fc3\u4e8b\u5462\uff1f\u201d\u5bf9\u5979\u80fd\u5c06\u5e94\u4fee\u9633\u751f\u64d2\uff0c\u4e5f\u751a\u611f\u5947\u602a\u3002\u5fc3\u9053\uff1a\u201c\u9b4f \u5fe0\u8d24\u867d\u6709\u597d\u624b\u76f8\u62a4\uff0c\u5728\u6211\u4eec\u770b\u6765\uff0c\u662f\u6740\u5e94\u4fee\u9633\u6613\uff0c\u6740\u9b4f\u5fe0\u8d24\u96be\uff1a\u4f46\u4ee5\u5ba2\u5a09\u5a77\u6240\u5904\u7684\u5730\u4f4d\uff0c\u5927 \u6709\u673a\u4f1a\u548c\u9b4f\u5fe0\u8d24\u5355\u72ec\u76f8\u5bf9\uff0c\u90a3\u53ef\u662f\u6740\u9b4f\u5fe0\u8d24\u8981\u6bd4\u6740\u5e94\u4fee\u9633\u5bb9\u6613\u5f97\u591a\u3002\u5979\u65e2\u7136\u7518\u5192\u5982\u5929\u7684\u5927 \u9669\uff0c\u4e3a\u4f55\u5374\u53c8\u4e0d\u80af\u6740\u9b4f\u5fe0\u8d24\uff1f\u201d\u5fc3\u4e2d\u5927\u60d1\u4e0d\u89e3\u3002 \u3000\u3000\u94c1\u98de\u9f99\u5c06\u6155\u5bb9\u51b2\u653e\u5728\u6e7f\u5730\u4e0a\uff0c\u8ba9\u4ed6\u5438\u5730\u4e0a\u51c9\u6c14\u3002\u8d70\u8fc7\u53bb\u5c06\u5e94\u4fee\u9633\u7a74\u9053\u89e3\u5f00\uff0c\u5e94\u4fee\u9633\u9189\u4e86 \u51e0\u65e5\uff0c\u6d51\u8eab\u65e0\u529b\uff0c\u8ff7\u60d8\u60d8\u5982\u5728\u68a6\u4e2d\uff0c\u7761\u773c\u4e00\u770b\uff0c\u89c1\u94c1\u98de\u9f99\u77aa\u773c\u770b\u7740\u81ea\u5df1\uff0c\u7389\u7f57\u5239\u53c8\u5728\u65c1\u8fb9\u51b7 \u7b11\uff0c\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u60f3\u8df3\u8d77\u6765\uff0c\u53cc\u811a\u4e4f\u529b\uff0c\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u8fd9\u91cc\u662f\u4ec0\u9ebd\u5730\u65b9\u4e86\u5a09\u5a77\u53c8\u5230\u90a3\u91cc\u53bb \u4e86\uff1f\u201d\u4ed6\u8fd8\u5e0c\u671b\u8fd9\u662f\u4e00\u4e2a\u6076\u68a6\u3002\u7389\u7f57\u5239\u811a\u5c16\u5728\u4ed6\u80f8\u819b\u8f7b\u8f7b\u4e00\u70b9\uff0c\u5e94\u4fee\u9633\u987f\u65f6\u75db\u5f97\u5982\u6740\u732a\u822c\u5927 \u53eb\uff0c\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4e0d\u8981\u9a6c\u4e0a\u6bd9\u4ed6\uff0c\u5148\u95ee\u4ed6\u53e3\u4f9b\uff0c\u8fd9\u4e8b\u4ea4\u7ed9\u4f60\u529e\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u8ffd\u4f9b\u4e4b \u4e8b\uff0c\u6211\u6700\u5728\u884c\uff0c\u7239\uff0c\u4f60\u653e\u5fc3\u597d\u4e86\u3002\u201d\u94c1\u98de\u9f99\u5168\u795e\u8d2f\u6ce8\u66ff\u6155\u5bb9\u51b2\u6cbb\u4f24\uff0c\u7389\u7f57\u5239\u5219\u628a\u5e94\u4fee\u9633\u63d0\u5230 \u5bc6\u5ba4\u91cc\u53bb\u5ba1\u95ee\u3002 \u3000\u3000\u539f\u6765\u90a3\u65e5\u5e94\u4fee\u9633\u5230\u4e73\u5a18\u5e9c\u5411\u5ba2\u6c0f\u6bcd\u5973\u95ee\u5b89\u3002\u5ba2\u5a09\u5a77\u4e00\u8154\u6012\u6c14\uff0c\u6b63\u81ea\u65e0\u5904\u53d1\uff0c\u4e00\u89c1\u662f\u4ed6\uff0c \u5fc3\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u4e00\u540d\u6c49\u5978\uff0c\u597d\uff0c\u6211\u5c31\u628a\u4ed6\u6349\u53bb\u9001\u793c\u3002\u201d\u5e94\u4fee\u9633\u8c04\u7b11\u95ee\u5b89\uff0c\u5ba2\u5a09\u5a77\u538b\u7740\u6012\u6c14\uff0c \u4e5f\u88c5\u51fa\u7b11\u8138\u76f8\u8fce\uff0c\u5e76\u62ff\u51fa\u9152\u6765\u6b3e\u5f85\u3002\u5e94\u4fee\u9633\u53d7\u5ba0\u82e5\u60ca\uff0c\u4efb\u4ed6\u4f55\u7b49\u8001\u5978\u5de8\u733e\uff0c\u4e5f\u7edd\u6599\u4e0d\u5230\u5ba2\u5a09 \u5a77\u4f1a\u6697\u7b97\u65bc\u4ed6\uff0c\u6ee1\u6ee1\u996e\u4e86\u53c1\u676f\u3002\u8fd9\u9152\u4e43\u662f\u5bab\u4e2d\u5bc6\u917f\u7684\u201c\u767e\u65e5\u9189\u201d\uff0c\u540d\u79f0\u867d\u7136\u5938\u5927\uff0c\u4f46\u80fd\u9189\u4e24 \u53c1\u65e5\u5374\u662f\u771f\u7684\uff0c\u996e\u4e86\u53c1\u676f\u3002\u987f\u65f6\u9189\u5012\u3002\u5ba2\u5a09\u5a77\u8fd8\u4e0d\u653e\u5fc3\uff0c\u53c8\u628a\u4ed6\u70b9\u4e86\u6655\u7a74\uff0c\u7136\u5f8c\u628a\u4ed6\u653e\u5728\u6d88 \u9065\u8f66\u7684\u5939\u5c42\uff0c\u5c06\u4ed6\u5e26\u8d70\u3002\u201d\u5230\u4e86\u5c71\u897f\uff0c\u5ba2\u5a09\u5a77\u5c06\u6d88\u9065\u8f66\u6253\u4e2a\u7c89\u788e\u3002\u7136\u5f8c\u628a\u5e94\u4fee\u9633\u653e\u4eba\u9884\u5907\u597d \u7684\u9ebb\u888b\u4e2d\uff0c\u6362\u4e86\u8863\u670d\u3002\u5230\u90ca\u5916\u4e00\u95f4\u6c11\u5bb6\u6295\u5bbf\u3002 \u3000\u3000\u5ba2\u5a09\u5a77\u53c8\u6015\u9152\u529b\u6613\u89e3\uff0c\u6bcf\u5230\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u53c8\u5c06\u4ed6\u6655\u7a74\u91cd\u70b9\u3002\u6240\u4ee5\u8fd9\u53c1\u65e5\u6765\u5e94\u4fee\u9633\u4e00\u76f4\u672a \u9192\u3002\u5ba2\u5a09\u5a77\u672c\u6765\u4e0d\u77e5\u9053\u7389\u7f57\u5239\u5728\u957f\u5b89\u9556\u5c40\uff0c\u5f8c\u6765\u60f3\u5f53\u5e74\u5e08\u7236\u201c\u7ea2\u82b1\u9b3c\u6bcd\u201d\u548c\u94c1\u98de\u9f99\u7389\u7f57\u5239\u6bd4 \u6b66\u4e4b\u5f8c\u66fe\u5bf9\u5979\u63d0\u8fc7\uff0c\u8bf4\u662f\u94c1\u98de\u9f99\u548c\u957f\u5b89\u9556\u5c40\u7684\u603b\u9556\u5934\u6709\u8fc7\u547d\u7684\u4ea4\u60c5\uff0c\u5ba2\u5a09\u5a77\u5fc3\u60f3\uff1a\u4e0d\u7ba1\u7389\u7f57 \u5239\u662f\u5426\u4f4f\u5728\u957f\u5b89\u9556\u5c40\uff0c\u5c06\u5e94\u4fee\u9633\u9001\u5230\u90a3\u91cc\uff0c\u5979\u4e00\u5b9a\u80fd\u591f\u6536\u5230\u3002\u65bc\u662f\u4fbf\u6253\u542c\u957f\u5b89\u9556\u5c40\u7684\u5730\u5740\uff0c \u96c7\u4e86\u9a6c\u8f66\uff0c\u7f69\u4e86\u9762\u7eb1\uff0c\u5c06\u888b\u91cc\u88c5\u7684\u5e94\u4fee\u9633\u629b\u5165\u9556\u5c40\u3002 \u3000\u3000\u518d\u8bf4\u7389\u7f57\u5239\u5c06\u5e94\u4fee\u9633\u63d0\u5165\u5bc6\u5ba4\u8feb\u4f9b\uff0c\u5979\u7684\u4f24\u7a74\u6b8b\u8eab\u624b\u6cd5\u8d5b\u8fc7\u5929\u4e0b\u6240\u6709\u7684\u6bd2\u5211\uff0c\u5e94\u4fee\u9633\u7ed9 \u5979\u6cbb\u5f97\u6b7b\u53bb\u6d3b\u6765\uff0c\u7ec8\u65bc\u628a\u4ed6\u6240\u77e5\u9053\u901a\u756a\u5356\u56fd\u7684\u6c49\u5978\u90fd\u4f9b\u51fa\u6765\u4e86\u3002\u7389\u7f57\u5239\u5c06\u4ed6\u4eec\u7684\u59d3\u540d\u5b98\u804c\uff0c \u4e00\u4e00\u5199\u5728\u7eb8\u4e0a\u3002\u4fbf\u628a\u4ed6\u63d0\u51fa\u6765\u4ea4\u7ed9\u94c1\u98de\u9f99\u3002 \u3000\u3000\u94c1\u98de\u9f99\u60f3\u5c3d\u4e86\u529e\u6cd5\uff0c\u7ed9\u6155\u5bb9\u51b2\u8212\u7b4b\u6d3b\u8840\uff0c\u88f9\u521b\u7597\u4f24\uff0c\u6155\u5bb9\u51b2\u867d\u7136\u60a0\u60a0\u9192\u8f6c\uff0c\u53ef\u662f\u4f24\u52bf\u4ecd \u662f\u5341\u5206\u6c89\u91cd\uff0c\u6709\u6c14\u65e0\u529b\uff0c\u4e0d\u80fd\u8bf4\u8bdd\u3002 \u3000\u3000\u5e94\u4fee\u9633\u89c1\u6155\u5bb9\u51b2\u8eba\u5728\u5730\u4e0a\uff0c\u53c8\u5403\u4e00\u60ca\uff0c\u94c1\u98de\u9f99\u51b7\u7b11\u9053\uff1a\u201c\u5e94\u4fee\u9633\uff0c\u4f60\u770b\u4ec0\u9ebd\u3002\u6155\u5bb9\u51b2\u53ef \u4e0d\u50cf\u4f60\u3002\u201d\u7389\u7f57\u5239\u5728\u4ed6\u8033\u8fb9\u8bf4\u4e86\u51e0\u65ec\uff0c\u94c1\u98de\u9f99\u9053\uff1a\u201c\u597d\uff0c\u65e2\u7136\u5f97\u4e86\u540d\u5355\uff0c\u9001\u4ed6\u53bb\u89c1\u960e\u7f57\u3002\u201d \u53eb\u9053\uff1a\u201c\u5973\u513f\u5440\uff0c\u4f60\u7684\u4ec7\u4eba\u90fd\u5df2\u6740\u4e86\uff0c\u4f60\u53ef\u4ee5\u7791\u76ee\u4e86\u3002\u201d\u547c\u7684\u4e00\u638c\uff0c\u628a\u5e94\u4fee\u9633\u5929\u7075\u76d6\u9707\u788e\uff0c \u987f\u65f6\u8001\u6cea\u7eb5\u6a2a\u3002\u7389\u7f57\u5239\u6025\u5fd9\u6276\u4ed6\u4eba\u623f\u6b47\u606f\u3002\u94c1\u98de\u9f99\u8fb9\u884c\u8fb9\u9053\uff1a\u201c\u88f3\u513f\u5440\uff0c\u6155\u5bb9\u51b2\u4e0d\u662f\u6211\u4eec\u7684 \u4ec7\u4eba\u4e86\uff0c\u4f60\u4eec\u8981\u5c0f\u5fc3\u7167\u6599\u3002\u201d\u7389\u7f57\u5239\u542b\u6cea\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e24\u5929\uff0c\u6155\u5bb9\u51b2\u867d\u7136\u7ecf\u4ed6\u4eec\u7ec6\u5fc3\u7167\u6599\uff0c\u4f24\u52bf\u4ecd\u662f\u4e0d\u89c1\u597d\u8f6c\u3002\u8981\u77e5\u94c1\u98de\u9f99\u638c\u529b\u53ef\u4ee5\u5288\u788e \u77f3\u7891\uff0c\u6d1e\u7a7f\u725b\u8179\uff0c\u82e5\u7136\u4e0d\u662f\u6155\u5bb9\u51b2\u5185\u529f\u6df1\u539a\uff0c\u65e9\u5c31\u6b7b\u4e86\u3002 \u3000\u3000\u7b2c\u53c1\u5929\u6155\u5bb9\u51b2\u6c14\u606f\u66f4\u662f\u5fae\u5f31\uff0c\u6323\u624e\u8bf4\u9053\uff1a\u201c\u94c1\u8001\u513f\uff0c\u8c22\u8c22\u4f60\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6155\u5bb9\u8001 \u5f1f\uff0c\u662f\u6211\u9519\u4e86\u3002\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u6211\u6295\u8f93\u7ed9\u4f60\u3002\u201d\u4ed6\u4e34\u6b7b\u5c1a\u8bb0\u6302\u7740\u6bd4\u6b66\u4e4b\u4e8b\uff0c\u94c1\u98de\u9f99\u8fd9\u65f6\u4e00\u70b9 \u4e5f\u4e0d\u89c9\u5f97\u597d\u7b11\uff0c\u70b9\u70b9\u5934\u9053\uff1a\u201c\u662f\uff0c\u4f60\u6ca1\u8f93\u7ed9\u6211\u3002\u201d\u6155\u5bb9\u51b2\u9762\u4e0a\u63a0\u8fc7\u4e00\u4e1d\u7b11\u5bb9\uff0c\u95ed\u4e86\u53cc\u773c\u3002\u94c1 \u98de\u9f99\u6478\u5b83\u9f3b\u606f\u672a\u65ad\uff0c\u542c\u4ed6\u5fc3\u623f\u5c1a\u8df3\uff0c\u4e0d\u5fcd\u54ed\u51fa\u58f0\u6765\u3002 \u3000\u3000\u6b63\u81ea\u4f24\u5fc3\uff0c\u7389\u7f57\u5239\u5ffd\u7136\u8e66\u8e66\u8df3\u8df3\uff0c\u7b11\u7740\u63a8\u5f00\u623f\u95e8\u3002\u94c1\u98de\u9f99\u7709\u5934\u4e00\u76b1\uff0c\u9053\uff1a\u201c\u5435\u4ec0\u9ebd\uff1f\u75c5 \u4eba\u8981\u5b89\u7761\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u6155\u5bb9\u51b2\u6709\u6551\u4e86\u3002\u201d\u94c1\u98de\u9f99\u8df3\u4e86\u8d77\u6765\uff0c\u5ffd\u53c8\u76b1\u7709\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u54c4 \u6211\u6b22\u559c\u4e86\uff0c\u4ed6\u7ed9\u6211\u4f24\u6210\u8fd9\u6837\uff0c\u5c82\u80fd\u6709\u6551\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u4e00\u7b11\u62c9\u4ed6\u7684\u624b\uff0c\u8dd1\u51fa\u5385\u5802\uff0c\u9053\uff1a\u201c\u4f60\u770b\u662f\u8c01\u6765\u4e86\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u554a\uff0c\u662f\u675c\u5144\u6765 \u4e86\u3002\u201d \u3000\u3000\u6765\u7684\u6b63\u662f\u8981\u4e0a\u4eac\u6551\u8205\u7236\u7684\u675c\u660e\u5fe0\u3002\u94c1\u98de\u9f99\u8fde\u65e5\u64cd\u5fc3\uff0c\u4e00\u65f6\u60f3\u4e0d\u8d77\u675c\u660e\u5fe0\u548c\u6155\u5bb9\u51b2\u7684\u751f\u547d \u6709\u4ec0\u9ebd\u5173\u7cfb\u3002\u7389\u7f57\u5239\u9053\uff1a\u201c\u8fd9\u4f4d\u675c\u5144\u9001\u6211\u4e00\u4efd\u539a\u793c\uff0c\u4f60\u8bf4\u6211\u8be5\u4e0d\u8be5\u8981\uff01\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4ec0 \u9ebd\uff1f\u201d\u7389\u7f57\u5239\u5c06\u684c\u4e0a\u4e00\u4e2a\u957f\u5323\u6253\u5f00\uff0c\u53ea\u89c1\u91cc\u9762\u4e00\u682a\u4e4c\u9ed1\u53d1\u4eae\uff0c\u72b6\u82e5\u5a74\u513f\u7684\u836f\u6750\u3002\u94c1\u98de\u9f99\u53eb \u9053\uff1a\u201c\u8fd9\u662f\u5343\u5e74\u4f55\u9996\u4e4c\u5440\uff01\u675c\u5144\u6ca1\u6709\u9001\u7ed9\u5978\u9609\u5417\uff1f\u201d \u3000\u3000\u675c\u660e\u5fe0\u773c\u5708\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4ffa\u7684\u8205\u8205\u5df2\u7ed9\u9609\u5904\u6b7b\u4e86\u3002\u542c\u8bf4\u6b7b\u4e86\u5341\u591a\u5929\u4e86\u3002\u662f\u5728\u76d1\u72f1\u91cc\u7ed9\u79d8 \u5bc6\u5904\u6b7b\u7684\u3002\u6211\u5728\u5927\u524d\u5929\u624d\u77e5\u9053\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u7684\u8205\u8205\u662f\u5de6\u5149\u6597\uff1f\u201d\u675c\u660e\u5fe0\u9053\uff1a\u201c\u662f\u3002\u4f60 \u8fd8\u8bb0\u5f97\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4ed6\u548c\u6768\u6d9f\u540c\u4e00\u4e2a\u76d1\u72f1\uff0c\u540c\u88ab\u5173\u5728\u5317\u9547\u629a\u53f8\u3002\u201d\u675c\u660e\u5fe0\u9053\uff1a\u201c\u662f\uff0c\u4f60 \u600e\u9ebd\u77e5\u9053\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u53bb\u770b\u8fc7\u6768\u6d9f\u3002\u201d\u675c\u660e\u5fe0\u9053\uff1a\u201c\u4ed6\u4eec\u516d\u4eba\u90fd\u7ed9\u5904\u6b7b\u4e86\u3002\u4ffa\u8205\u8205\u548c \u6768\u6d9f\u542c\u8bf4\u662f\u540c\u4e00\u5929\u665a\u4e0a\u6b7b\u7684\u3002\u6b7b\u5f97\u5f88\u60e8\uff0c\u662f\u7ed9\u571f\u888b\u538b\u6b7b\u7684\u3002\u201d\u7389\u7f57\u5239\u4e0d\u89c9\u6006\u7136\uff0c\u5fc3\u60f3\uff1a\u4e00\u5b9a \u662f\u6211\u5927\u95f9\u5929\u7262\u90a3\u4e00\u665a\u5904\u6b7b\u7684\u4e86\u3002 \u3000\u3000\u675c\u660e\u5fe0\u9053\uff1a\u201c\u6211\u6094\u6ca1\u6709\u542c\u7ec3\u5973\u4fa0\u7684\u529d\u544a\uff0c\u8fd8\u60f3\u5411\u5978\u9605\u6c42\u60c5\u5462\u3002\u597d\u5728\u95e8\u8def\u672a\u642d\u597d\uff0c\u5c31\u77e5\u9053 \u4e86\u8205\u8205\u7684\u6b7b\u8baf\u3002\u8fd9\u682a\u4f55\u9996\u4e4c\u548c\u90a3\u4ef6\u767d\u72d0\u88d8\u5b50\u624d\u6ca1\u6709\u51a4\u6789\u9001\u6389\u3002\u7ec3\u5973\u4fa0\uff0c\u60f3\u5f53\u65e5\u5728\u4e07\u53bf\u4e4b\u65f6\uff0c \u6211\u53d7\u4e86\u90a3\u795e\u5927\u5143\u6bd2\u722a\u6293\u4f24\uff0c\u5168\u9760\u4f60\u8feb\u4ed6\u62ff\u51fa\u89e3\u836f\u3002\u6211\u65e0\u53ef\u62a5\u7b54\uff0c\u53ea\u6709\u62ff\u8fd9\u682a\u4f55\u9996\u4e4c\u9001\u7ed9\u4f60\u3002 \u4e5f\u8bb8\u5408\u4f60\u7528\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u5408\u7528\u6781\u4e86\u3002\u201d\u5c06\u4f55\u9996\u4e4c\u6536\u8d77\u3002\u9053\uff1a\u201c\u4f60\u4ee5\u524d\u7684\u540c\u50da\u8881\u5d07\u7115\u5728\u8fd9\u91cc\uff0c\u4f60\u77e5\u9053 \u5417\uff1f\u201d\u675c\u660e\u5fe0\u9053\uff1a\u201c\u542c\u8bf4\u8fc7\uff0c\u4f46\u627e\u4e0d\u7740\u4ed6\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4ed6\u5728\u4fe1\u738b\u5e9c\uff0c\u4f60\u5feb\u53bb\u627e\u4ed6\u5427\u3002 \u5440\uff0c\u4f60\u7b49\u4e00\u7b49\uff0c\u6211\u6709\u4e00\u5c01\u4fe1\u6258\u4f60\u4ea4\u7ed9\u4ed6\u3002\u201d\u675c\u660e\u5fe0\u9053\uff1a\u201c\u4e00\u5b9a\u9001\u5230\u3002\u201d\u7389\u7f57\u5239\u56de\u5230\u5f8c\u5802\uff0c\u5c06 \u5e94\u4fee\u9633\u90a3\u5f20\u540d\u5355\u5957\u4eba\u4fe1\u5c01\uff0c\u518d\u8be6\u7ec6\u5199\u4e86\u4e00\u5c01\u4fe1\uff0c\u8bf4\u660e\u539f\u59d4\uff0c\u7389\u7f57\u5239\u5fc3\u60f3\uff1a\u73b0\u5728\u9b4f\u5fe0\u8d24\u52bf\u5927\u6ed4 \u5929\uff0c\u867d\u6709\u4ed6\u901a\u756a\u5356\u56fd\u7684\u771f\u51ed\u5b9e\u636e\u4e5f\u53c2\u4ed6\u4e0d\u5012\uff0c\u4e0d\u5982\u4ea4\u7ed9\u8881\u5d07\u7115\uff0c\u4ed6\u5e74\u82e5\u662f\u4fe1\u738b\u5373\u4f4d\uff0c\u8fd9\u5f20\u540d \u5355\u5c31\u8db3\u5b9a\u4ed6\u6b7b\u7f6a\u6709\u9980\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u7c97\u901a\u6587\u58a8\uff0c\u5199\u4e00\u5c01\u4fe1\u5199\u5f97\u751a\u4e45\uff0c\u94c1\u98de\u9f99\u8bb0\u6302\u7740\u6155\u5bb9\u51b2\uff0c\u7b49\u5f97\u5f88\u4e0d\u8010\u70e6\u3002\u597d\u4e0d\u5bb9\u6613 \u7b49\u5230\u7389\u7f57\u5239\u51fa\u6765\uff0c\u5c06\u4fe1\u4ea4\u7ed9\u4e86\u675c\u660e\u5fe0\uff0c\u4fbf\u60f3\u7aef\u8336\u9001\u5ba2\uff0c\u675c\u660e\u5fe0\u5374\u5c1a\u65e0\u8f9e\u610f\uff0c\u94c1\u98de\u9f99\u89c1\u6709\u7389\u7f57 \u5239\u966a\u5ba2\uff0c\u4fbf\u95ee\u5979\u53d6\u4e86\u90a3\u682a\u4f55\u9996\u4e4c\uff0c\u5411\u675c\u660e\u5fe0\u544a\u4e86\u4e2a\u7f6a\uff0c\u81ea\u53bb\u714e\u836f\u7ed9\u6155\u5bb9\u51b2\u559d\u3002 \u3000\u3000\u675c\u660e\u5fe0\u548c\u7389\u7f57\u5239\u5ba2\u5957\u51e0\u53e5\uff0c\u9053\uff1a\u201c\u718a\u7ecf\u7565\u7684\u4e8b\u4f60\u77e5\u9053\u5417\uff1f\u201d\u7389\u7f57\u5239\u95ee\u9053\uff1a\u201c\u4ec0\u9ebd\u4e8b \u5462\uff1f\u201d\u675c\u660e\u5fe0\u9053\uff1a\u201c\u4ed6\u6b7b\u5f8c\u4e0d\u662f\u88ab\u4f20\u9996\u4e5d\u8fb9\u5417\uff1f\u524d\u51e0\u5929\u4ed6\u7684\u9996\u7ea7\u88ab\u4f20\u56de\u4eac\u57ce\uff0c\u7687\u5e1d\u7a81\u7136\u4e0b \u4ee4\uff0c\u8bf4\u662f\u5ff5\u5728\u4ed6\u4ee5\u524d\u6709\u529f\u56fd\u5bb6\uff0c\u5c06\u9996\u7ea7\u201c\u8d50\u201d\u56de\u4ed6\u7684\u5bb6\u4eba\uff0c\u800c\u4e14\u51c6\u4ed6\u5bb6\u4eba\u539a\u846c\uff0c\u51fa\u6ba1\u7684\u706f\u7b3c \u4e5f\u51c6\u6302\u7ecf\u7565\u5b98\u8854\u3002\u201d\u7389\u7f57\u5239\u77e5\u662f\u90a3\u665a\u7ed9\u5c0f\u7687\u5e1d\u5bc4\u7b80\u7559\u5200\u4e4b\u4e8b\u751f\u4e86\u6548\u529b\uff0c\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u539f \u8be5\u5982\u6b64\u3002\u201d\u53c8\u9053\uff1a\u201c\u8881\u5d07\u7115\u4ed6\u65e5\u5fc5\u5f53\u5927\u7528\uff0c\u4f60\u4eec\u5c06\u6765\u53ef\u80fd\u91cd\u6267\u5e72\u6208\u4fdd\u536b\u8fb9\u7586\u3002\u201d\u675c\u660e\u5fe0 \u9053\uff1a\uff0e\u201c\u4f46\u613f\u5982\u6b64\uff0c\u6015\u53ea\u6015\u5978\u81e3\u5f53\u9053\uff0c\u8881\u5d07\u7115\u5c31\u7b97\u505a\u4e86\u7ecf\u7565\uff0c\u4e5f\u672a\u5fc5\u80fd\u5c3d\u6240\u80fd\u3002\u201d\u5f8c\u6765\u8881\u5d07 \u7115\u5728\u5d07\u6862\u5373\u4f4d\u4e4b\u5f8c\uff0c\u679c\u7136\u88ab\u4efb\u4e3a\u8fbd\u4e1c\u7ecf\u7565\uff0c\u675c\u660e\u5fe0\u4e5f\u6210\u4e3a\u4ed6\u9ebe\u4e0b\u4e00\u5458\u5927\u5c06\u3002\u53ef\u662f\u5386\u53f2\u91cd\u6f14\uff0c \u5341\u9980\u5e74\u5f8c\u4ed6\u4e5f\u50cf\u718a\u5ef7\u5f3c\u4e00\u6837\u88ab\u5978\u4eba\u4e0e\u654c\u5bc7\u4e32\u540c\u9677\u5bb3\uff0c\u800c\u4e14\u6b7b\u5728\u8d4f\u8bc6\u4ed6\u63d0\u62d4\u4ed6\u7684\u5d07\u6862\u7687\u5e1d\u624b \u4e0a\u3002\u8fd9\u662f\u5f8c\u8bdd\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u4e14\u8bf4\u7389\u7f57\u5239\u9001\u8d70\u5ba2\u4eba\u4e4b\u5f8c\uff0c\u4fbf\u8dd1\u53bb\u770b\u6155\u5bb9\u51b2\uff0c\u542c\u5f97\u623f\u4e2d\u4f4e\u4f4e\u8c08\u8bdd\u4e4b\u58f0\uff0c\u63a8\u95e8\u4e00\u770b\uff0c\u53ea\u89c1 \u94c1\u98de\u9f99\u559c\u9053\uff1a\u201c\u771f\u662f\u7075\u836f\uff01\u786e\u80fd\u8d77\u6b7b\u56de\u751f\u3002\u559d\u4e86\u4e0d\u4e45\uff0c\u9762\u8272\u4e5f\u8f6c\u4e86\u3002\u201d \u3000\u3000\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u591a\u8c22\u4f60\u4eec\u3002\u94c1\u8001\u82f1\u96c4\uff0c\u4f60\u5bf9\u6211\u771f\u662f\u6069\u540c\u518d\u9020\u3002\u201d\u94c1\u98de\u9f99\u5927\u7b11\u9053\uff1a\u201c\u4f60\u6551\u4e86 \u6211\u7684\u6027\u547d\uff0c\u6211\u4e5f\u6551\u4e86\u4f60\u7684\u6027\u547d\u3002\u8fd9\u7b97\u5f97\u4e86\u4ec0\u9ebd\uff01\u4f60\u521a\u597d\u8f6c\uff0c\u4e0d\u53ef\u4f24\u795e\uff0c\u518d\u6b47\u6b47\u5427\u3002\u201d \u3000\u3000\u8fc7\u4e86\u51e0\u5929\uff0c\u6155\u5bb9\u51b2\u66f4\u6709\u8d77\u8272\uff0c\u51e0\u5929\u6765\u4ed6\u548c\u94c1\u98de\u9f99\u7389\u7f57\u5239\u8c08\u8c08\u8bb2\u8bb2\uff0c\u9887\u53d7\u611f\u52a8\u3002\u6155\u5bb9\u51b2 \u9053\uff1a\u201c\u9b4f\u5fe0\u8d24\u4e00\u5b9a\u6068\u6b7b\u6211\u4e86\u3002\u6211\u75c5\u597d\u4e4b\u5f8c\uff0c\u7edd\u4e0d\u518d\u7559\u5728\u4eac\u57ce\uff0c\u52a9\u7ea3\u4e3a\u8650\u4e86\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a \u201c\u4f60\u4e0d\u80fd\u5728\u4eac\u57ce\u7acb\u8db3\uff0c\u53ef\u4ee5\u5230\u95ef\u738b\u519b\u4e2d\uff0c\u9713\u88f3\u548c\u4ed6\u4eec\u5f88\u719f\uff0c\u53ef\u4ee5\u7ed9\u4f60\u62c5\u4fdd\u3002\u201d\u6155\u5bb9\u51b2\u9ed8\u7136\u4e0d \u8bed\uff0c\u94c1\u98de\u9f99\u77e5\u4ed6\u5c1a\u4e0d\u613f\u4e0e\u9b4f\u5fe0\u8d24\u4e3a\u654c\uff0c\u4e5f\u4e0d\u52c9\u5f3a\u3002\u4e00\u65e5\uff0c\u7389\u7f57\u5239\u4e0e\u94c1\u98de\u9f99\u95f2\u8bdd\uff0c\u5ffd\u542c\u5f97\u9556\u5c40 \u6709\u4eba\u62a5\u9053\uff1a\u201c\u5916\u9762\u6709\u4e2a\u6076\u4e10\u95f9\u4e8b\uff0c\u6b63\u526f\u9556\u5934\u4e0d\u5728\uff0c\u4f60\u51fa\u53bb\u770b\u770b\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6709\u8fd9\u6837\u7684 \u4e8b\uff1f\u600e\u6837\u95f9\u6cd5\uff1f\u201d\u9556\u5c40\u5925\u8ba1\u9053\uff1a\u201c\u4ed6\u8981\u5316\u4e00\u4e07\u4e24\u94f6\u5b50\u3002\u8fd9\u6076\u4e10 \u3000\u3000\u6709\u4e00\u53ea\u624b\u81c2\uff0c\u53ef\u5389\u5bb3\u54e9\u3002\u4ed6\u5750\u5728\u5730\u4e0a\uff0c\u4e3e\u8d77\u4e00\u53ea\u624b\u81c2\uff0c\u6258\u7740\u4e00\u4e2a\u77f3\u94b5\uff0c\u8981\u6211\u4eec\u628a\u5143\u5b9d\u88c5 \u6ee1\uff0c\u6211\u4eec\u5341\u51e0\u4e2a\u4eba\u63a8\u4ed6\u90fd\u63a8\u4e0d\u52a8\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6025\u5fd9\u8d76\u51fa\u53bb\u770b\uff0c\u90a3\u6076\u4e10\u7a81\u7136\u8df3\u8d77\u6765\uff0c\u5531\u4e86\u4e2a\u8bfa\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u5982\u6b64\uff0c\u4e5f \u4e0d\u80fd\u5f15\u5f97\u4f60\u8001\u4eba\u5bb6\u51fa\u6765\u3002\u201d\u7389\u7f57\u5239\u4e00\u770b\uff0c\u539f\u6765\u662f\u7f57\u94c1\u81c2\uff0c\u7b11\u9053\uff1a\u201c\u4f55\u5fc5\u5982\u6b64\uff0c\u8fdb\u91cc\u9762\u8bf4\u3002\u201d \u9556\u5c40\u7684\u4eba\u89c1\u4ed6\u4eec\u76f8\u8bc6\uff0c\u624d\u77e5\u9053\u8fd9\u53eb\u5316\u5b50\u4e43\u662f\u98ce\u5c18\u5f02\u4eba\uff0c\u6545\u610f\u4e54\u88c5\u6076\u4e10\uff0c\u6c42\u89c1\u7389\u7f57\u5239\u7684\u3002 \u3000\u3000\u7389\u7f57\u5239\u5c06\u7f57\u94c1\u81c2\u5e26\u5165\u5f8c\u9662\uff0c\u7f57\u94c1\u81c2\u9053\uff1a\u201c\u6211\u5230\u4eac\u57ce\u51e0\u5929\u4e86\uff0c\u672c\u6765\u662f\u60f3\u63a2\u95ee\u6768\u5927\u4eba\u7684\uff0c\u8c01 \u77e5\u6768\u5927\u4eba\u5df2\u7ecf\u6b7b\u4e86\u3002\u6211\u60f3\u4f60\u8001\u4eba\u5bb6\u53ef\u80fd\u4f4f\u5728\u8fd9\u4e2a\u9556\u5c40\uff0c\u6240\u4ee5\u5192\u6627\u6765\u8bbf\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u5c06 \u6768\u6d9f\u7684\u513f\u5b50\u62b1\u5230\u5929\u5c71\u4e86\u5417\uff1f\u53ef\u89c1\u5230\u5cb3\u545c\u73c2\u9ebd\uff1f\u201d\u7f57\u94c1\u81c2\u9053\uff1a\u201c\u5cb3\u9e23\u73c2\u7684\u5e08\u7236\u5929\u90fd\u5c45\u58eb\u5df2\u7ecf\u6b7b \u4e86\uff0c\u4ed6\u73b0\u5728\u5df2\u524a\u53d1\u4e3a\u50e7\uff0c\u6539\u540d\u53eb\u505a\u6666\u660e\u5e08\uff0c\u4e0d\u53eb\u5cb3\u9e23\u73c2\u4e86\u3002\u4ed6\u5f88\u559c\u6b22\u6768\u4e91\u9aa2\uff0c\u8bf4\u5728\u5341\u5e74\u4e4b \u5f8c\uff0c\u5c31\u8981\u628a\u4ed6\u8c03\u6559\u6210\u5929\u4e0b\u7b2c\u4e00\u7684\u5251\u5ba2\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u4ed6\u6562\u5938\u4e0b\u8fd9\u6837\u7684\u6d77\u53e3\uff1f\u597d\uff0c\u5341\u5e74\u4e4b \u5f8c\uff0c\u6211\u4e5f\u6559\u4e00\u4e2a\u5973\u5f92\u5f1f\u53bb\u6536\u670d\u4ed6\u3002\u201d\u94c1\u98de\u9f99\u89c1\u4ed6\u4eec\u63d0\u8d77\u5cb3\u545c\u73c2\uff0c\u672c\u6765\u6ee1\u6000\u60c6\u6005\uff0c\u542c\u5230\u7389\u7f57\u5239 \u5b69\u5b50\u6c14\u7684\u8bf4\u8bdd\uff0c\u4e0d\u7981\u7b11\u9053\uff1a\u201c\u4ed6\u505a\u4e86\u548c\u5c1a\uff0c\u4f60\u8fd8\u8981\u548c\u4ed6\u6597\u6c14\uff1f\u201d \u3000\u3000\u7f57\u94c1\u81c2\u53c8\u9053\uff1a\u201c\u6211\u56de\u6765\u4e4b\u65f6\uff0c\u8def\u8fc7\u6b66\u5f53\u5c71\uff0c\u5728\u5c71\u4e0a\u4f4f\u4e86\u51e0\u665a\u3002\u201d\u7389\u7f57\u5239\u9ed8\u7136\u4e0d\u8bed\u3002\u7f57\u94c1 \u81c2\u9053\uff1a\u201c\u5353\u4e00\u822a\u73b0\u5728\u662f\u638c\u95e8\u5f1f\u5b50\uff0c\u55ef\uff0c\u4ed6\u4e5f\u53ef\u601c\u2026\u2026\u201d\u7389\u7f57\u5239\u773c\u5708\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u63d0\u4ed6\u4f5c \u751a\uff1f\u201d\u7f57\u94c1\u81c2\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u55ef\uff0c\u4ed6\u4e5f\u53ef\u601c\uff0c\u5440\uff0c\u8fd8\u662f\u4e0d\u5148\u8bf4\u8fd9\uff0c\u4f60\u5148\u770b\u770b\u4ed6\u7ed9\u4f60\u7684\u4fe1\u2026\u2026\u201d \u7389\u7f57\u5239\u53e3\u91cc\u867d\u4e0d\u63d0\uff0c\u5fc3\u4e2d\u5374\u662f\u6e34\u671b\u77e5\u9053\u5353\u4e00\u822a\u7684\u60c5\u51b5\uff0c\u6025\u5fd9\u628a\u4fe1\u5c55\u5f00\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u4e86\u53c1\u9996\u5c0f \u8bd7\uff1a?\u201c\u4e00\u201d \u3000\u3000\u8776\u821e\u83ba\u8001\u53c8\u4e00\u5e74\uff0c\u82b1\u5f00\u82b1\u843d\u6bcf\u51c4\u7136\uff0c \u3000\u3000\u6b64\u60c5\u65e9\u4ed8\u4e1c\u6d41\u6c34\uff0c\u5374\u8d81\u8202\u6f6e\u5230\u773c\u524d\uff01\u201c\u4e8c\u201d \u3000\u3000\u6d6e\u6c89\u9053\u529b\u672a\u80fd\u575a\uff0c\u6167\u5251\u96be\u6325\u81ea\u601c\uff0c \u3000\u3000\u8d62\u5f97\u6708\u660e\u957f\u4e0b\u62dc\uff0c\u5fc3\u968f\u660e\u6708\u9010\u88d9\u8fb9\u3002\u201c\u53c1\u201d \u3000\u3000\u8865\u5929\u65e0\u8ba1\u7a7a\u5782\u6cea\uff0c\u6068\u6d77\u96be\u586b\u6709\u6028\u79bd\uff0c \u3000\u3000\u4f46\u613f\u6545\u4eba\u80fd\u8c05\u6211\uff0c\u4e0d\u987b\u8a00\u8bed\u8868\u6df1\u5fc3\u3002 \u3000\u3000\u8fd9\u51e0\u9996\u8bd7\u8bcd\u53e5\u6d45\u767d\uff0c\u7389\u7f57\u5239\u867d\u7c97\u901a\u6587\u58a8\uff0c\u4e5f\u89e3\u5176\u4e2d\u60c5\u610f\u3002\u4e0d\u89c9\u6ef4\u4e0b\u6cea\u6765\u3002\u60f3\u8d77\u81ea\u5df1\u4ee5\u524d \u5728\u660e\u6708\u5ce1\u63c9\u788e\u91ce\u82b1\uff0c\u629b\u4e0b\u5c71\u8c37\uff0c\u4ee5\u82b1\u55bb\u4eba\uff0c\u4f24\u5e74\u534e\u4e4b\u6613\u901d\uff0c\u611f\u6765\u65e5\u4e4b\u65e0\u591a\u7684\u60c5\u4e8b\uff0c\u518d\u5480\u56bc\u5353 \u4e00\u822a\u201c\u82b1\u5f00\u82b1\u843d\u6bcf\u51c4\u7136\u201d\u7684\u8bd7\u53e5\uff0c\u4e0d\u89c9\u75f4\u4e86\u3002 \u3000\u3000\u7f57\u94c1\u81c2\u9053\uff1a\u201c\u5353\u4e00\u822a\u867d\u505a\u4e86\u638c\u95e8\uff0c\u4f46\u975e\u5e38\u6d88\u6c89\uff0c\u5982\u766b\u4f3c\u50bb\uff0c\u4eba\u4e5f\u7626\u4e86\u3002\u542c\u8bf4\u4ed6\u51e0\u4f4d\u5e08\u53d4 \u5bf9\u4ed6\u90fd\u5f88\u5931\u671b\u3002\u6211\u548c\u4ed6\u8c08\u4e86\u51e0\u665a\uff0c\u4ed6\u53ea\u8bf4\u6094\u4e0d\u5f53\u521d\u3002\u201d\u7389\u7f57\u5239\u4e00\u9635\u5fc3\u9178\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u8bf4 \u4e86\u3002\u201d \u3000\u3000\u7f57\u94c1\u81c2\u9053\uff1a\u201c\u4ed6\u76fc\u671b\u4f60\u53bb\u770b\u4ed6\u3002\u201d\u7389\u7f57\u5239\u9ed8\u7136\u4e0d\u8bed\u3002\u7f57\u94c1\u81c2\u9053\uff1a\u201c\u6211\u544a\u8f9e\u4e86\u3002\u201d\u7389\u7f57\u5239 \u4ecd\u7136\u4e0d\u8bed\u3002\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u53bb\u90a3\u513f\uff1f\u201d\u7f57\u94c1\u81c2\u9053\uff1a\u201c\u8c7a\u72fc\u5f53\u9053\uff0c\u4e2d\u539f\u6270\u6518\uff0c\u6211\u4e5f\u8981\u5b66\u6666\u660e \u5e08\uff0c\u5230\u5929\u5c71\u53bb\u4e86\u3002\u201d\u94c1\u98de\u9f99\u5c06\u7f57\u94c1\u81c2\u9001\u51fa\u95e8\u5916\uff0c\u56de\u6765\u4e00\u770b\uff0c\u7389\u7f57\u5239\u4ecd\u7136\u7aef\u5750\u72b9\u5982\u77f3\u50cf\uff0c\u5fc3\u4e2d \u4f24\u611f\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u4e5f\u771f\u53ef\u601c\uff01\u201d\u4e0a\u524d\u6273\u7389\u7f57\u5239\u80a9\u818a\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u7136\u60f3\u5ff5\u4ed6\uff0c\u5c31\u53bb\u770b\u4ed6 \u5427\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u773c\u4e2d\u6d6e\u51fa\u5353\u4e00\u822a\u754f\u7f29\u53ef\u601c\u7684\u6837\u5b50\uff0c\u7a81\u7136\u6012\u9053\uff1a\u201c\u8c01\u53bb\u770b\u4ed6\uff1f\u6211\u624d\u4e0d\u53bb\u3002\u4ee5\u5f8c\u522b\u63d0 \u4ed6\u4e86\u3002\u201d\u94c1\u98de\u9f99\u77e5\u9053\u5979\u7684\u813e\u6c14\uff0c\u5374\u4e0d\u8a00\u8bed\u3002 \u3000\u3000\u518d\u8fc7\u534a\u6708\uff0c\u6155\u5bb9\u51b2\u7684\u4f24\u5df2\u7ecf\u75ca\u5e94\uff0c\u987b\u518d\u5728\u9556\u5c40\u4f11\u517b\u4e00\u4e24\u4e2a\u6708\uff0c\u6b66\u529f\u4fbf\u53ef\u6062\u590d\u5982\u521d\u3002\u94c1\u98de \u9f99\u5bf9\u7389\u7f57\u5239\u9053\uff1a\u201c\u54b1\u4eec\u518d\u53bb\u95ef\u8361\u6c5f\u6e56\u5427\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u5230\u90a3\u91cc\u53bb\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5 \u95ee\uff0c\u6211\u603b\u4e0d\u4f1a\u5e26\u4f60\u5230\u4f60\u6240\u4e0d\u613f\u610f\u53bb\u7684\u5730\u65b9\u3002\u201d\u7389\u7f57\u5239\u9ed8\u9ed8\u65e0\u8a00\uff0c\u6536\u62fe\u8d77\u88c5\uff0c\u8ddf\u7740\u94c1\u98de\u9f99\u5411\u9f99 \u8fbe\u53c1\u548c\u6155\u5bb9\u51b2\u544a\u8f9e\u3002\u6155\u5bb9\u51b2\u7ecf\u8fc7\u4e86\u8fd9\u4e00\u4e2a\u6708\uff0c\u5fc3\u7075\u8089\u4f53\uff0c\u90fd\u5982\u6b7b\u91cc\u91cd\u751f\uff0c\u5bf9\u94c1\u98de\u9f99\u4e0e\u7389\u7f57\u5239 \u751f\u4e86\u611f\u60c5\uff0c\u4e0e\u4ed6\u4eec\u4e00\u518d\u614e\u91cd\u9053\u522b\u3002 \u3000\u3000\u8d70\u5230\u5e7f\u9614\u7684\u6c5f\u6e56\uff0c\u7389\u7f57\u5239\u6101\u70e6\u6e10\u51cf\uff0c\u548c\u94c1\u98de\u9f99\u6709\u8bf4\u6709\u7b11\u3002\u8fc7\u4e86\u4e00\u4e2a\u591a\u6708\uff0c\u4ed6\u4eec\u5df3\u4ece\u5317\u4eac \u5357\u4e0b\uff0c\u7ecf\u6cb3\u5357\u800c\u5230\u6e56\u5317\uff0c\u7389\u7f57\u5239\u77e5\u4ed6\u662f\u60f3\u5f15\u81ea\u5df1\u5230\u6b66\u5f53\u5c71\uff0c\u4f6f\u4f5c\u4e0d\u77e5\uff0c\u968f\u4ed6\u524d\u5f80\u3002 \u3000\u3000\u8fd9\u4e00\u53e3\u5230\u4e86\u6e56\u5317\u8944\u9633\uff0c\u79bb\u57ce\u56db\u5341\u91cc\u5916\u7684\u6f33\u5357\u4e61\uff0c\u4e43\u662f\u4ee5\u524d\u7ea2\u82b1\u9b3c\u6bcd\u9690\u5c45\u4e4b\u6240\uff0c\u4e5f\u5373\u5ba2\u6c0f \u6545\u5c45\u3002\u7389\u7f57\u5239\u65f1\u5df2\u5728\u65c5\u9014\u63a2\u542c\u6e05\u695a\u3002\u7389\u7f57\u5239\u77e5\u9053\u94c1\u98de\u9f99\u867d\u7136\u4e00\u8def\u9017\u5979\u8bf4\u7b11\uff0c\u5176\u5b9e\u4ed6\u81ea\u5df1\u4e5f\u5f88 \u90c1\u95f7\u3002\u81ea\u4ece\u4ed6\u66ff\u73ca\u745a\u62a5\u4e86\u4ec7\u5f8c\uff0c\u597d\u50cf\u5df2\u65e0\u6240\u8426\u6000\uff0c\u7cbe\u795e\u4f3c\u66f4\u663e\u5f97\u7a7a\u865a\u3002\u5230\u4e86\u8944\u9633\uff0c\u7389\u7f57\u5239\u7a81 \u7136\u60f3\u8d77\u4e86\u5ba2\u5a09\u5a77\uff0c\u5ffd\u800c\u53c8\u60f3\u94c1\u98de\u9f99\u4ee5\u524d\u7684\u7231\u59be\u7a46\u4e5d\u5a18\uff0c\u5fc3\u9053\uff1a\u4e0d\u77e5\u5ba2\u5a09\u5a77\u662f\u5426\u5df2\u56de\u5230\u5bb6\u4e2d\uff1f \u7a46\u4e5d\u5a18\u548c\u7ea2\u82b1\u9b3c\u6bcd\u7684\u513f\u5b50\u516c\u5b59\u96f7\u662f\u5426\u4ecd\u4f4f\u5728\u90a3\u91cc\uff1f\u8fd9\u665a\u5979\u8bd5\u63a2\u95ee\u9053\uff1a\u201c\u7239\uff0c\u54b1\u4eec\u53bb\u770b\u770b\u5ba2\u5a09 \u5a77\u600e\u9ebd\u6837\uff1f\u6211\u5b9e\u5728\u60f3\u5ff5\u5979\uff01\u201d\u94c1\u98de\u9f99\u9762\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u53bb\u4fbf\u81ea\u5df2\u53bb\u3002\u6211\u4e0d\u53bb\uff01\u201d\u7389\u7f57 \u5239\u5fc3\u4e2d\u6697\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u7239\u7684\u813e\u6c14\u548c\u6211\u76f8\u540c\u3002\u4ed6\u8bf4\u4e0d\u60f3\u53bb\uff0c\u5176\u5b9e\u5374\u662f\u60f3\u53bb\u3002\u4ed6\u8001\u5e74\u5b64\u72ec\uff0c\u9664\u4e86 \u6211\u4e4b\u5916\uff0c\u6709\u7a46\u4e5d\u5a18\u52c9\u5f3a\u8bf4\u5f97\u662f\u4ed6\u7684\u4eb2\u4eba\u3002\u54ce\uff0c\u7a46\u4e5d\u5a18\u6211\u61d2\u5f97\u7ba1\u5979\uff0c\u5ba2\u5a09\u5a77\u8fd9\u5c0f\u59d1\u5a18\u5374\u771f\u53ef \u7231\uff0c\u65e2\u5230\u6b64\u5730\uff0c\u5c82\u53ef\u4e0d\u8bbf\u5979\u4e00\u8bbf\u3002\u201d \u3000\u3000\u8fd9\u4e00\u665a\uff0c\u4ed6\u4eec\u4f4f\u5728\u57ce\u4e2d\u5ba2\u5e97\uff0c\u5230\u4e86\u5348\u591c\uff0c\u94c1\u98de\u9f99\u5ffd\u7136\u542c\u5f97\u90bb\u623f\u7684\u7389\u7f57\u5239\u60e8\u53eb\u4e00\u58f0\uff0c\u6025\u5fd9 \u62ab\u8863\u800c\u8d77\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u7a97\u95e8\u5ffd\u7136\u547c\u7684\u4e00\u58f0\u6253\u5f00\uff0c\u522e\u8fdb\u4e00\u80a1\u5f3a\u98ce\u3002\u94c1\u98de\u9f99\u559d\u9053\uff1a\u201c\u9f20\u5b50\u6562\u65bd\u6697 \u7b97\uff01\u201d\u53cd\u624b\u4e00\u6349\uff0c\u5c06\u5916\u9762\u6253\u6765\u7684\u6697\u5668\u6349\u7740\uff0c\u5374\u662f\u4e00\u53ea\u70c2\u8349\u978b\uff01 \u3000\u3000\u94c1\u98de\u9f99\u5927\u6012\uff0c\u7a7f\u7a97\u98de\u51fa\uff0c\u9065\u89c1\u4e00\u6761\u9ed1\u5f71\uff0c\u5df2\u767b\u4e0a\u5bf9\u9762\u6c11\u623f\u3002\u8eab\u5f62\u4f3c\u9887\u9ad8\u5927\uff0c\u9ed1\u591c\u4e2d\u770b\u4e0d \u6e05\u695a\u3002\u6025\u5fd9\u8fc7\u7389\u7f57\u5239\u7684\u623f\u4e2d\u5f20\u671b\uff0c\u7389\u7f57\u5239\u5df2\u7ecf\u4e0d\u89c1\u3002\u94c1\u98de\u9f99\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u4ec0\u9ebd\u4eba\u6709\u8fd9 \u6837\u8eab\u624b\uff1f\u7ea2\u82b1\u9b3c\u6bcd\u590d\u751f\uff0c\u672c\u9886\u4e5f\u6ca1\u5982\u6b64\u9ad8\u5f3a\uff01\u201d\u65bd\u5c55\u8f7b\u529f\uff0c\u8df3\u4e0a\u6c11\u623f\u8ffd\u90a3\u9ed1\u5f71\uff0c\u90a3\u9ed1\u5f71\u5ffd\u5feb \u5ffd\u6162\uff0c\u94c1\u98de\u9f99\u5feb\u65f6\u4ed6\u5feb\uff0c\u94c1\u98de\u9f99\u6162\u65f6\u4ed6\u6162\uff0c\u603b\u662f\u8ffd\u4ed6\u4e0d\u4e0a\u3002\u6b63\u662f\uff1a\u5f3a\u4e2d\u66f4\u6709\u5f3a\u4e2d\u624b\uff0c\u4e00\u5c71\u8fd8 \u6709\u4e00\u5c71\u9ad8\u3002\u6b32\u77e5\u8fd9\u9ed1\u5f71\u662f\u8c01\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u4e94\u56de\u3000\u83b2\u51fa\u6c61\u6ce5\u3000\u51b3\u5fc3\u79bb\u7236\u6bcd\u3000\u80f8\u65e0\u6742\u5ff5\u3000\u4e00\u610f\u4f1a\u60c5\u90ce \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377 \u7b2c\u4e8c\u5341\u4e94\u56de \u83b2\u51fa\u6c61\u6ce5\u3000\u51b3\u5fc3\u79bb\u7236\u6bcd \u80f8\u65e0\u6742\u5ff5\u3000\u4e00\u610f\u4f1a\u60c5\u90ce \u3000\u3000\u8bdd\u8bf4\u94c1\u98de\u9f99\u8ffd\u90a3\u9ed1\u5f71\uff0c\u89c1\u90a3\u4eba\u62ab\u7740\u4e00\u4ef6\u6597\u7bf7\uff0c\u76d6\u8fc7\u5934\u9762\uff0c\u94c1\u98de\u9f99\u518d\u4ed4\u7ec6\u4e00\u770b\uff0c\u539f\u6765\u4e0d\u662f \u8eab\u6750\u9ad8\u5927\u800c\u662f\u6597\u7bf7\u5bbd\u5927\uff0c\u663e\u5f97\u5f88\u4e0d\u79f0\u8eab\u3002\u94c1\u98de\u9f99\u60f3\u6765\u60f3\u53bb\uff0c\u60f3\u4e0d\u51fa\u8fd9\u662f\u4f55\u4eba\uff0c\u9a82\u4ed6\u4ed6\u53c8\u4e0d \u7b54\uff0c\u597d\u50cf\u662f\u5b58\u5fc3\u8981\u5f15\u94c1\u98de\u9f99\u5230\u4ec0\u9ebd\u5730\u65b9\u3002 \u3000\u3000\u94c1\u98de\u9f99\u8ffd\u4e86\u4e00\u9635\uff0c\u53ea\u89c1\u524d\u9762\u73b0\u51fa\u4e00\u4e2a\u8352\u50fb\u7684\u6751\u5e84\uff0c\u9690\u9690\u7ea6\u7ea6\u6709\u51e0\u95f4\u623f\u5c4b\u3002\u94c1\u98de\u9f99\u5fc3\u5ff5\u4e00 \u52a8\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u5f00\u4ec0\u9ebd\u73a9\u7b11\uff1f\u201d\u524d\u9762\u7684\u4eba\u5657\u55e4\u7b11\u51fa\u58f0\u6765\u3002\u628a\u6597\u7bf7\u8131\u4e0b\uff0c\u7b11\u9053\uff1a\u201c\u7ea2\u82b1\u9b3c\u6bcd\u4ee5 \u524d\u4fbf\u4f4f\u5728\u8fd9\u4e2a\u6751\u5b50\u91cc\uff0c\u4f60\u4e0d\u8fdb\u53bb\u770b\u770b\u5417\uff1f\u201d\u5374\u662f\u7389\u7f57\u5239\u3002 \u3000\u3000\u539f\u6765\u7389\u7f57\u5239\u60e6\u8bb0\u7740\u5ba2\u5a09\u5a77\uff0c\u5f88\u60f3\u5230\u7ea2\u82b1\u9b3c\u6bcd\u7684\u6545\u5c45\u91c7\u671b\uff0c\u770b\u5ba2\u5a09\u5a77\u662f\u5426\u56de\u5230\u90a3\u513f\u3002\u4f46\u56e0 \u94c1\u98de\u9f99\u4e0d\u613f\u89c1\u7a46\u4e5d\u5a18\uff0c\u4e0d\u80af\u540c\u53bb\u3002\u7389\u7f57\u5239\u987d\u76ae\u6027\u8d77\uff0c\u4fbf\u548c\u4e7e\u7239\u5f00\u4e86\u8fd9\u4e00\u4e2a\u73a9\u7b11\u3002\u5979\u5728\u5ba2\u5bd3\u91cc \u968f\u624b\u6986\u4e86\u4e00\u4e2a\u80d6\u5b50\u7684\u6597\u7bf7\uff0c\u76d6\u8fc7\u5934\u9762\uff0c\u5047\u88c5\u88ab\u4eba\u523a\u4f24\uff0c\u5c06\u94c1\u98de\u9f99\u5f15\u4e86\u51fa\u6765\u3002 \u3000\u3000\u94c1\u98de\u9f99\u9762\u8272\u4e00\u6c89\uff0c\u7389\u7f57\u5239\u9053\uff1a\u201c\u7239\uff0c\u4f60\u522b\u751f\u6c14\u3002\u7ea2\u82b1\u9b3c\u6bcd\u4e5f\u7b97\u662f\u4f60\u7684\u670b\u53cb\uff0c\u4f60\u5c31\u662f\u89c1\u89c1 \u6545\u4eba\u7684\u513f\u5b50\u4e5f\u6ca1\u4ec0\u9ebd\u5173\u7cfb\u3002\u201d\u94c1\u98de\u9f99\u9ed8\u7136\u4e0d\u7b54\uff0c\u4ed6\u4eb2\u8fd1\u7684\u4eba\u548c\u540c\u4e00\u8f88\u7684\u670b\u53cb\u5df2\u6240\u9980\u65e0\u51e0\uff0c\u7a46 \u4e5d\u5a18\u548c\u4ed6\u540c\u4f4f\u8fc7\u5341\u591a\u5e74\uff0c\u8001\u5e74\u4eba\u6b22\u559c\u5ff5\u65e7\uff0c\u4ed6\u4e5f\u9887\u60f3\u77e5\u9053\u7a46\u4e5d\u5a18\u8fd1\u51b5\uff0c\u4f46\u60f3\u60f3\u8fd8\u662f\u4e0d\u89c1\u7684 \u597d\u3002\u53ef\u662f\u7389\u7f57\u5239\u5374\u628a\u4ed6\u5f15\u6765\u4e86\u3002 \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u7239\uff0c\u5c31\u8fdb\u53bb\u770b\u770b\u5427\u3002\u5a09\u5a77\u8fd9\u5c0f\u4e2b\u5934\u7ed9\u6211\u4eec\u9001\u6765\u4e86\u5e94\u4fee\u9633\uff0c\u6211\u4eec\u8fd8\u6ca1\u5411\u5979\u9053 \u8c22\u5462\u3002\u201d\u94c1\u98de\u9f99\u6b63\u5728\u8e0c\u8e87\uff0c\u591c\u98ce\u4e2d\u5ffd\u9001\u6765\u547c\u53f7\u4e4b\u58f0\u3002\u4f3c\u4e4e\u8fd8\u6742\u6709\u5175\u5668\u78b0\u51fb\u4e4b\u58f0\u3002\u94c1\u98de\u9f99\u542c\u4e86 \u4e00\u542c\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u53bb\u770b\uff01\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\u4e5f\u5927\u51fa\u7389\u7f57\u5239\u610f\u5916\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u662f\u6709\u4ec0\u9ebd\u4eba\u5411\u7ea2\u82b1\u9b3c\u6bcd\u7684\u5f8c\u88d4\u5bfb\u4ec7\u3002\u201d\u75be\u5c55\u8f7b \u529f\uff0c\u5411\u524d\u9762\u6751\u5e84\u6251\u53bb\uff0c\u53ea\u89c1\u5176\u4e2d\u4e00\u95f4\u7816\u5c4b\uff0c\u9732\u51fa\u706f\u706b\uff0c\u7389\u7f57\u5239\u98de\u8eab\u4e0a\u5c4b\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u9a82\u9053\uff1a \u201c\u662f\u7ea2\u82b1\u9b3c\u6bcd\u7684\u5f92\u5f1f\u6b63\u597d\uff0c\u628a\u5979\u6349\u8d70\uff0c\u4e5f\u51fa\u4e00\u53e3\u9e1f\u6c14\uff01\u201d\u7389\u7f57\u5239\u671d\u4e0b\u4e00\u671b\uff0c\u9662\u5b50\u91cc\u7684\u4e00\u53cc\u7537 \u5973\uff0c\u6b63\u5728\u6740\u3002\u90a3\u5973\u7684\u4e0d\u662f\u5ba2\u5a09\u5a77\u662f\u8c01\uff1f\u53a2\u623f\u91cc\u8fd8\u6709\u4e00\u4e2a\u5973\u4eba\u5636\u54d1\u53eb\u53f7\uff0c\u65ad\u65ad\u7eed\u7eed\u7684\u8bed\u97f3\u53eb \u7740\uff1a\u201c\u6211\u7684\u513f\u5b50\u6709\u4ec0\u9ebd\u7f6a\uff1f\u4f60\u4eec\u6740\u4e86\u6211\u7684\u4e08\u592b\uff0c\u8fd8\u4e0d\u653e\u8fc7\u4ed6\u5417\uff1f\u628a\u6211\u7684\u513f\u5b50\u7559\u4e0b\uff0c\u7559 \u4e0b\u2026\u2026\u201d\u8fd9\u58f0\u97f3\u6b63\u662f\u7a46\u4e5d\u5a18\u7684\uff01\u7389\u7f57\u5239\u5927\u5403\u4e00\u60ca\uff0c\u63d0\u5251\u4fbf\u95ef\u4e0b\u53bb\uff01 \u3000\u3000\u53ea\u89c1\u4e00\u4e2a\u7c97\u8c6a\u7684\u6c49\u5b50\uff0c\u4f7f\u4e00\u53e3\u952f\u9f7f\u52fe\u9570\u5200\uff0c\u529b\u5927\u62db\u731b\uff0c\u628a\u5ba2\u5a09\u5a77\u8feb\u5f97\u6b65\u6b65\u5f8c\u9000\uff0c\u5ead\u9662\u4e2d \u8fd8\u6709\u53c1\u4eba\u65c1\u7acb\u89c2\u6218\uff0c\u563b\u563b\u51b7\u7b11\uff0c\u201d\u8fd9\u53c1\u4eba\uff0c\u4e00\u4e2a\u662f\u548c\u5c1a\uff0c\u4e00\u4e2a\u662f\u9053\u58eb\uff0c\u8fd8\u6709\u4e00\u4e2a\u662f\u5e74\u5c06\u82b1\u7532 \u7684\u8001\u5934\u3002\u7389\u7f57\u5239\u4e00\u58f0\u957f\u7b11\uff0c\u53eb\u9053\uff1a\u201c\u5a09\u5a77\u59b9\u5b50\uff0c\u4f60\u4e0d\u8981\u614c\uff0c\u6211\u6765\u4e86\uff01\u201d\u58f0\u5230\u4eba\u5230\uff0c\u5251\u5149\u4e00 \u95ea\uff0c\u75be\u82e5\u60ca\u98d5\uff0c\u90a3\u7c97\u8c6a\u6c49\u5b50\u5ffd\u89c9\u51b7\u6c14\u68ee\u68ee\uff0c\u5bd2\u98ce\u6251\u9762\uff0c\u52fe\u9570\u5200\u672a\u53ca\u6536\u62db\u62a4\u9762\uff0c\u624b\u8155\u5173\u8282\u4e4b\u5904 \u5df3\u4e2d\u4e86\u4e00\u5251\uff0c\u7acb\u523b\u6eda\u5730\u72c2\u53f7\uff01 \u3000\u3000\u7389\u7f57\u5239\u8eab\u624b\u4e4b\u5feb\uff0c\u65e0\u6cd5\u5f62\u5bb9\uff0c\u65c1\u89c2\u53c1\u4eba\u8fd9\u65f6\u624d\u770b\u6e05\u6765\u7684\u662f\u4e2a\u5c11\u5973\uff0c\u90a3\u548c\u5c1a\u9996\u5148\u4e00\u58f0\u5927 \u543c\uff0c\u624b\u6325 \u3000\u3000\u6756\uff0c\u5f53\u5934\u5288\u4e0b\uff0c\u7389\u7f57\u5239\u4fa7\u8eab\u4e00\u5251\uff0c\u90a3\u548c\u5c1a\u6756\u5c3e\u4e00\u6296\uff0c\u4e00\u62db\u201c\u4e3e\u706b\u64a9\u5929\u201d\uff0c\u7adf\u5c06\u5b9d\u5251\u6c64 \u5f00\uff0c\u5251\u5c16\u55e1\u55e1\u4f5c\u54cd\uff0c\u6446\u52a8\u4e0d\u4f11\uff0c\u7389\u7f57\u5239\u66f4\u4e0d\u6362\u62db\uff0c\u8155\u52b2\u4e00\u53d1\uff0c\u5251\u950b\u84e6\u5730\u53cd\u5708\u56de\u6765\uff0c\u76f4\u53d6\u654c\u4eba \u80a9\u80cc\u3002\u90a3\u548c\u5c1a\u6599\u4e0d\u5230\u7389\u7f57\u5239\u5251\u6cd5\u5982\u6b64\u8be1\u8c32\u795e\u5947\uff01\u6756\u8eab\u4e00\u6446\uff0c\u6ca1\u6709\u6321\u7740\uff0c\u6025\u5fd9\u5438\u8179\u541e\u80f8\uff0c\u8eab\u5b50 \u5f8c\u4ef0\uff0c\u53ea\u542c\u5f97\u55e4\u7684\u4e00\u58f0\uff0c\u618e\u888d\u5df2\u88ab\u6311\u5f00\uff0c\u7389\u7f57\u5239\u5251\u52bf\u653e\u5c3d\uff0c\u8e0f\u8fdb\u4e00\u6b65\uff0c\u633a\u5251\u518d\u523a\uff0c\u90a3\u9053\u58eb\u4e5f \u84e6\u7136\u51fa\u624b\uff0c\u957f\u5251\u4e00\u6296\uff0c\u529b\u538b\u7389\u7f57\u5239\u7684\u5b9d\u5251\uff0c\u7389\u7f57\u5239\u7a81\u7136\u677e\u52b2\uff0c\u5251\u628a\u4e00\u62bd\uff0c\u90a3\u9053\u58eb\u4e00\u4e2a\u8e09\u8dc4\uff0c \u7389\u7f57\u5239\u8f6c\u8eab\u4e00\u5251\uff0c\u90a3\u9053\u58eb\u4e5f\u771f\u4e86\u5f97\uff0c\u957f\u5251\u659c\u4f38\uff0c\u5c45\u7136\u628a\u7389\u7f57\u5239\u7684\u5251\u9ecf\u51fa\u5916\u95e8\uff0c\u7389\u7f57\u5239\u5fc3\u9053\uff1a \u201c\u54a6\uff0c\u90a3\u91cc\u6765\u7684\u725b\u9f3b\u5b50\u548c\u79c3\u9a74\uff0c\u5c45\u7136\u8fd8\u6709\u4e24\u5ea6\u6563\u624b\uff1f\u201d\u5b9d\u5251\u4e00\u63a2\uff0c\u89e3\u4e86\u654c\u4eba\u5185\u52b2\uff0c\u6362\u62db\u518d \u523a\uff0c\u90a3\u548c\u5c1a\u60ca\u9b42\u65b9\u5b9a\uff0c\u633a\u6756\u6597\u65f6\uff0c\u5ffd\u53c8\u542c\u5f97\u5361\u5587\u5587\u4e00\u9635\u5de8\u54cd\uff0c\u53ea\u89c1\u4e00\u4e2a\u5e9e\u5927\u7684\u8eab\u5f71\uff0c\u4ece\u5c4b\u9876 \u75be\u8df3\u4e0b\u6765\uff0c\u53ea\u4e00\u638c\u5c31\u628a\u53a2\u623f\u7684\u7a97\u53e3\u94c1\u679d\u6253\u65ad\uff0c\u7eb5\u8eab\u8fdb\u53bb\u3002\u90a3\u65c1\u89c2\u7684\u8001\u5934\u53eb\u9053\uff1a\u201c\u6765\u7684\u662f\u94c1\u8001 \u5417\uff1f\u201d\u7565\u4e00\u8fdf\u7591\uff0c\u672a\u53ca\u963b\u6321\uff0c\u94c1\u98de\u9f99\u5df2\u7eb5\u8eab\u4eba\u5185\uff0c\u7acb\u5373\u628a\u4e00\u6761\u5927\u6c49\u63b7\u4e86\u51fa\u6765\uff0c\u91cc\u9762\u5a74\u513f\u7684\u54ed \u58f0\u4e0e\u7a46\u4e5d\u5a18\u60ca\u559c\u7684\u53eb\u58f0\u6742\u6210\u4e00\u7247\u3002\u7389\u7f57\u5239\u53eb\u9053\uff1a\u201c\u7239\uff0c\u5feb\u51fa\u6765\u6536\u62fe\u8fd9\u53c1\u4e2a\u6076\u8d3c\uff0c\u8981\u4e0d\u7136\u6211\u5c31 \u4e00\u4eba\u72ec\u541e\uff0c\u6ca1\u4f60\u7684\u4efd\u4e86\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u547c\u7684\u8df3\u51fa\uff0c\u53eb\u9053\uff1a\u201c\u88f3\u513f\u505c\u624b\uff01\u201d\u7389\u7f57\u5239\u6115\u7136\u6536\u5251\uff0c\u90a3\u548c\u5c1a\u3001\u9053\u58eb\u7eb5\u8eab\u9000\u5f8c\uff0c\u5174 \u90a3\u65c1\u89c2\u7684\u8001\u5934\u7ad9\u5728\u5ead\u9662\u4e2d\u7684\u69d0\u6811\u4e0b\u9762\uff0c\u7389\u7f57\u5239\u8fd9\u624d\u6ce8\u610f\u5230\u69d0\u6811\u80cc\u9633\u7684\u90a3\u8fb9\uff0c\u540a\u7740\u4e00\u4e2a\u6b7b\u4eba\uff0c \u4f53\u6447\u6447\u6643\u6643\uff0c\u7adf\u662f\u7ea2\u82b1\u9b3c\u6bcd\u7684\u72ec\u751f\u513f\u5b50\u516c\u5b59\u96f7\u3002 \u3000\u3000\u94c1\u98de\u9f99\u6012\u9053\uff1a\u201c\u970d\u8001\u4e8c\uff0c\u62d9\u9053\u4eba\u3001\u667a\u4e0a\u4eba\uff0c\u4f60\u4eec\u53c1\u4eba\u90fd\u662f\u6b66\u6797\u4e2d\u7684\u8001\u524d\u8f88\u4e86\uff0c\u4e3a\u4f55\u5e26\u4e86 \u5f92\u5f1f\uff0c\u8054\u624b\u6765\u6b3a\u4fae\u5987\u5b7a\uff1f\u201d\u90a3\u8001\u5934\u9053\uff1a\u201c\u94c1\u98de\u9f99\uff0c\u4f60\u4e0e\u7ea2\u82b1\u9b3c\u6bcd\u4e0d\u4e5f\u662f\u4e3a\u654c\u7684\u5417\uff1f\u8bb0\u5f97\u5f53\u5e74 \u6211\u4eec\u9080\u4f60\u5408\u6597\u7ea2\u82b1\u9b3c\u6bcd\u4e4b\u65f6\uff0c\u4f60\u867d\u56e0\u4e8b\u4e0d\u80fd\u524d\u5f80\uff0c\u4e5f\u672a\u66fe\u63a8\u8f9e\u3002\u201d \u3000\u3000\u94c1\u98de\u9f99\u62ac\u773c\u671b\u5929\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4e00\u6b7b\u767e\u4ec7\u6d88\uff0c\u4f60\u4eec\u8fd8\u8bb0\u7740\u5f53\u5e74\u4e4b\u4e8b\u5417\uff1f\u800c\u4e14\u7ea2\u82b1\u9b3c\u6bcd\u4e4b \u4e8b\uff0c\u4e0e\u5979\u7684\u513f\u5ab3\u5f92\u5f1f\u4f55\u5e72\uff1f\u201d \u3000\u3000\u62d9\u9053\u4eba\u9996\u5148\u60ca\u8be7\u51fa\u58f0\uff0c\u62a2\u7740\u95ee\u9053\uff1a\u201c\u7ea2\u82b1\u9b3c\u6bcd\u5df2\u6b7b\u4e86\u5417\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u5df2\u6b7b\u4e86\u534a\u5e74\u591a \u4e86\uff01\u201d\u667a\u4e0a\u4eba\u5931\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7684\u4ec7\u4e0d\u80fd\u62a5\u4e86\uff01\u201d\u94c1\u98de\u9f99\u6307\u7740\u516c\u5b59\u96f7\u7684\u4f53\u9053\uff1a\u201c\u4f60\u4eec\u7684\u624b\u6bb5 \u4e5f\u672a\u514d\u592a\u6bd2\u8fa3\u4e86\uff0c\u54fc\uff0c\u54fc\u201c\u6211\u8001\u94c1\u5c31\u770b\u4e0d\u8fc7\u773c\u3002\u201d \u3000\u3000\u62d9\u9053\u4eba\u6012\u9053\uff1a\u201c\u8001\u94c1\uff0c\u4f60\u60f3\u53cd\u53cb\u4e3a\u654c\u5417\uff1f\u201d\u970d\u8001\u5934\u4e5f\u6012\u9053\uff1a\u201c\u4f60\u770b\u4e0d\u8fc7\u773c\u53c8\u600e\u6837\u4e86\u4f60\u6253 \u4f24\u4e86\u6211\u7684\u5f92\u5f1f\uff0c\u6211\u8fd8\u672a\u5411\u4f60\u7b97\u5e10\u5462\uff01\u201d\u94c1\u98de\u9f99\u4e00\u58f0\u5927\u543c\uff0c\u6325\u638c\u5288\u53bb\uff0c\u667a\u4e0a\u4eba\u6a2a\u6756\u4e00\u626b\uff0c\u94c1\u98de \u9f99\u53d8\u638c\u4e3a\u62ff\uff0c\u559d\u9053\uff1a\u201c\u64a4\u624b\uff01\u201d\u94c1\u98de\u9f99\u5185\u529b\u60ca\u4eba\uff0c\u8fdc\u5728\u7389\u7f57\u5239\u4e4b\u4e0a\uff0c\u667a\u4e0a\u4eba\u53ea\u89c9\u864e\u53e3\u4e00\u75db\uff0c \u62da\u529b\u652f\u6301\uff0c\u62d9\u9053\u4eba\u5251\u51fa\u5982\u98de\uff0c\u6025\u523a\u94c1\u98de\u9f99\u624b\u8155\u3002\u94c1\u98de\u9f99\u6709\u638c\u4e00\u626b\uff0c\u5de6\u638c\u4e00\u5708\uff0c\u8f7b\u62e8\u62d9\u9053\u4eba\u7684 \u5251\u628a\uff0c\u53f3\u624b\u62e2\u6307\u4e00\u62c2\uff0c\u53c8\u559d\u58f0\uff1a\u201c\u7740\uff01\u201d\u62d9\u9053\u4eba\u6025\u9000\u65f6\uff0c\u624b\u8155\u5df2\u88ab\u4ed6\u7684\u6307\u5c16\u62c2\u7740\uff0c\u987f\u65f6\u73b0\u51fa \u4e94\u6761\u70d9\u5370\uff01 \u3000\u3000\u8fd9\u51e0\u62db\u5feb\u5982\u95ea\u7535\uff0c\u5c31\u5728\u62d9\u9053\u4eba\u7ed9\u94c1\u98de\u9f99\u6307\u529b\u6240\u4f24\u4e4b\u65f6\uff0c\u667a\u4e0a\u4eba\u88ab\u4ed6\u7684\u638c\u529b\u4e00\u9001\uff0c\u201c\u5427\u201d \u4e00\u58f0\u8dcc\u5012\u5730\u4e0a\uff0c\u864e\u53e3\u6d41\u8840\uff0c\u6756\u4e5f\u88ab\u62d7\u66f2\uff0c\u5e78\u90a3\u6756\u662f\u7cbe\u94a2\u6240\u94f8\uff0c\u8981\u4e0d\u7136\u771f\u4f1a\u62d7\u65ad\u3002\u970d\u8001\u5934\u77e5\u4e24 \u4eba\u4e0d\u662f\u94c1\u98de\u9f99\u5bf9\u624b\uff0c\u6025\u5fd9\u89e3\u4e0b\u8f6f\u97ad\uff0c\u62e6\u8170\u56f0\u6765\uff0c\u90a3\u970d\u8001\u5934\u540d\u5524\u970d\u5143\u4ef2\uff0c\u662f\u897f\u540d\u6b66\u5e08\u4e16\u5bb6\uff0c\u529f \u592b\u751a\u5f3a;\u8f6f\u97ad\u8d77\u5904\uff0c\u52b2\u98ce\u62c2\u9762\u3002\u94c1\u98de\u9f99\u559d\u58f0\uff1a\u201c\u597d\uff01\u201d\u56de\u8eab\u62d7\u6b65\uff0c\u638c\u80cc\u5fae\u6258\u97ad\u8eab\uff0c\u638c\u950b\u659c\u659c \u7684\u6b3a\u8eab\u75be\u5288\uff1a\u970d\u5143\u4ef2\u970d\u5730\u7528\u4e2a\u201c\u602a\u87d2\u7ffb\u8eab\u201d\u8fde\u4eba\u5e26\u97ad\u6025\u65cb\u56de\u6765\uff0c\u4f7f\u51fa\u8fde\u73af\u53c1\u97ad\uff0c\u201c\u56de\u98ce\u626b \u67f3\u201d\u7684\u7edd\u6280\uff0c\u5237\uff0c\u5237\uff0c\u5237\uff01\u98ce\u58f0\u547c\u7a96\uff0c\u5377\u8d77\u4e00\u56e2\u97ad\u5f71\uff0c\u4ee5\u653b\u4e3a\u5b88\uff0c\u624d\u80fd\u5c01\u95ed\u95e8\u6237\u3002\u667a\u4e0a\u4eba\u4e0e \u62d9\u9053\u4eba\u4e00\u6756\u4e00\u5251\uff0c\u5de6\u53f3\u5206\u4e0a\uff0c\u5c06\u94c1\u98de\u9f99\u56f4\u5728\u6838\u5fc3\u3002\u970d\u5143\u4ef2\u53eb\u9053\uff1a\u201c\u8001\u94c1\uff0c\u6211\u6709\u8bdd\u8bf4\uff01\u201d\u94c1\u98de \u9f99\u559d\u9053\uff1a\u201c\u4e22\u4e0b\u5175\u5668\uff0c\u518d\u548c\u4f60\u8bf4\u8bdd\uff01\u8fd9\u70b9\u89c4\u77e9\uff0c\u4f60\u4eec\u90fd\u4e0d\u61c2\u5417\uff1f\u201d\u4e22\u4e0b\u5175\u5668\uff0c\u5c31\u7b49\u65bc\u8ba4\u7f6a\u670d \u8f93\uff0c\u4e22\u4e0b\u5175\u5668\u518d\u8bf4\u8bdd\uff0c\u90a3\u5c31\u662f\u544a\u7f6a\u6c42\u94d9\u4e86\u3002\u970d\u5143\u4ef2\u6012\u9053\uff1a\u201c\u8001\u94c1\uff0c\u4f60\u6b3a\u6211\u592a\u751a\uff01\u201d\u8f6f\u97ad\u4e00 \u6296\uff0c\u7f20\u8eab\u7ed5\u8155\uff0c\u667a\u4e0a\u4eba\u4e0e\u62d9\u9053\u4eba\u4e5f\u8fd0\u638c\u4f7f\u5251\uff0c\u5408\u529b\u8fdb\u653b\u3002 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u5f53\u5e74\u4e3a\u4e86\u6551\u62a4\u65e0\u6076\u4e0d\u4f5c\u7684\u4e08\u592b\uff0c\u66fe\u4e0e\u897f\u5317\u5341\u53c1\u540d\u9ad8\u624b\u4e3a\u654c\uff0c\u4ee5\u60ca\u4eba\u7684\u6b66\u529f\uff0c\u5c06 \u5341\u53c1\u540d\u9ad8\u624b\u5168\u90e8\u6740\u9000\u3002\u8fd9\u5341\u53c1\u540d\u9ad8\u624b\u5f15\u4e3a\u5947\u803b\u5927\u5b71\uff0c\u77e2\u8a93\u62a5\u4ec7\u3002\u4f46\u5176\u5f8c\u4e0d\u4e45\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u5c31\u8fdc \u79bb\u897f\u5317\uff0c\u9041\u8ff9\u7a77\u4e61\uff0c\u8fc7\u4e86\u51e0\u5341\u5e74\uff0c\u8fd9\u5341\u53c1\u540d\u9ad8\u624b\u9646\u7eed\u901d\u4e16\uff0c\u53ea\u5269\u4e0b\u970d\u5143\u4ef2\uff0c\u62d9\u9053\u4eba\u548c\u667a\u4e0a\u4eba \u5c1a\u5728\u4eba\u95f4\uff0c\u8fd9\u53c1\u4eba\u82e6\u7ec3\u4e86\u51e0\u5341\u5e74\uff0c\u81ea\u4fe1\u53ef\u4ee5\u518d\u6597\u4e00\u6597\u7ea2\u82b1\u9b3c\u6bcd\u4e86\u3002\u6070\u597d\u5728\u8fd9\u4e00\u5e74\uff0c\u53c8\u542c\u5230\u7ea2 \u82b1\u9b3c\u6bcd\u518d\u51fa\u73b0\u7684\u6d88\u606f\uff0c\u65bc\u662f\u51fa\u6765\u67e5\u8bbf\u3002\u4ed6\u4eec\u5e76\u4e0d\u77e5\u9053\u654c\u4eba\u5df2\u6b7b\uff0c\u4e00\u76f4\u5bfb\u5230\u8944\u9633\u3002 \u3000\u3000\u672c\u6765\u4ed6\u4eec\u8fd8\u4e0d\u77e5\u9053\u7ea2\u82b1\u9b3c\u6bcd\u662f\u9690\u5c45\u5728\u8944\u9633\u4e61\u4e0b\u7684\u3002\u504f\u504f\u90a3\u7ea2\u82b1\u9b3c\u6bcd\u7684\u5b9d\u8d1d\u513f\u5b50\u516c\u5b59\u96f7\u95ef \u51fa\u4e86\u4e00\u573a\u5927\u7978\uff0c\u8fd9\u624d\u5c06\u4ed6\u4eec\u5f15\u6765\u3002 \u3000\u3000\u7ea2\u82b1\u9b3c\u6bcd\u6b7b\u5f8c\uff0c\u516c\u5b59\u96f7\u6ca1\u4e86\u7ba1\u5934\uff0c\u6e10\u6e10\u4e3a\u975e\u4f5c\u6076\u3002\u90a3\u65f6\u4ed6\u7684\u59bb\u5b50\u7a46\u4e5d\u5a18\u5df2\u6000\u5b55\u4e03\u516b\u4e2a \u6708\uff0c\u4ed6\u5728\u5916\u9762\u62c8\u82b1\u60f9\u8349\uff0c\u770b\u4e0a\u4e86\u4e00\u4e2a\u9556\u5ba2\u7684\u59bb\u5b50\uff0c\u9556\u5ba2\u5728\u5916\u4fdd\u9556\uff0c\u7559\u4e0b\u59bb\u5b50\u72ec\u5b88\u5bb6\u4e2d\uff0c\u516c\u5b59 \u96f7\u6570\u5ea6\u52fe\u5f15\uff0c\u4e0d\u80fd\u5f97\u624b\uff0c\u53cd\u88ab\u90a3\u9556\u5ba2\u7684\u59bb\u5b50\u75db\u9a82\u4e00\u573a\u3002\u516c\u5b59\u96f7\u4e00\u65f6\u6012\u8d77\uff0c\u7adf\u7136\u5728\u4e00\u4e2a\u665a\u4e0a\uff0c \u5077\u53bb\u5c06\u90a3\u9556\u5ba2\u7684\u59bb\u5b50\u5f3a\u5978\uff0c\u5f04\u5f97\u5979\u60ac\u6881\u81ea\u5c3d\u3002\u9556\u5ba2\u56de\u6765\uff0c\u627e\u4ed6\u7b97\u5e10\u3002\u516c\u5b59\u96f7\u548c\u4ed6\u6253\u5f97\u4e0d\u5206\u80dc \u8d1f\uff0c\u629b\u51fa\u7ea2\u82b1\u9b3c\u6bcd\u7684\u540d\u5934\uff0c\u624d\u5c06\u4ed6\u5413\u9000\uff0c\u4e0d\u6599\u8fd9\u9556\u5ba2\u5374\u662f\u970d\u5143\u4ef2\u7684\u5f92\u5f1f\u3002\u95fb\u8baf\u4e4b\u5f8c\uff0c\u7acb\u5373\u548c \u667a\u60ee\u4e0a\u4eba\u4e0e\u62d9\u9053\u4eba\u4e00\u540c\u8d76\u6765\u3002 \u3000\u3000\u8fd9\u65f6\u7a46\u4e5d\u5a18\u751f\u4e0b\u4e86\u4e00\u5b50\uff0c\u672a\u6ee1\u5341\u65e5\uff0c\u4ea7\u5f8c\u751f\u75c5\uff0c\u5367\u5728\u5e8a\u4e0a\uff0c\u773c\u7741\u7741\u7684\u770b\u654c\u4eba\u5c06\u4e08\u592b\u7f6a\u6076 \u6570\u8bf4\u4e4b\u5f8c\uff0c\u4fbf\u884c\u540a\u6b7b\u3002\u7a46\u4e5d\u5a18\u6c14\u5f97\u6655\u4e86\u8fc7\u53bb\u3002\u970d\u5143\u4ef2\u7684\u4e24\u4e2a\u5f92\u5f1f\u201c\u5373\u90a3\u4e2a\u9556\u5ba2\u548c\u4ed6\u7684\u5e08\u5f1f\u201d \u6012\u706b\u5c1a\u672a\u5e73\u7184\uff0c\u4e00\u4e2a\u6765\u62a2\u7a46\u4e5d\u5a18\u7684\u513f\u5b50\uff0c\u4e00\u4e2a\u8981\u628a\u5ba2\u5a09\u5a77\u64d2\u53bb\u4fae\u8fb1\u3002\u5e78\u4e8f\u94c1\u98de\u9f99\u548c\u7389\u7f57\u5239\u53ca \u65f6\u8d76\u5230\uff0c\u8981\u4e0d\u7136\u771f\u662f\u4e0d\u582a\u8bbe\u60f3\u3002 \u3000\u3000\u4e14\u8bf4\u5ba2\u5a09\u5a77\u89c1\u5230\u4e86\u7389\u7f57\u5239\u4e4b\u5f8c\uff0c\u60ca\u559c\u4ea4\u96c6\uff0c\u62c9\u7740\u7389\u7f57\u5239\u7684\u624b\uff0c\u6cea\u5149\u6676\u83b9\uff0c\u534a\u664c\u624d\u53eb\u5f97\u51fa \u4e00\u58f0\uff1a\u201c\u59d0\u59d0\u3002\u201d\u7389\u7f57\u5239\u77a5\u4e86\u4e00\u773c\u5ead\u9662\u4e2d\u7684\u6253\u6597\u5f62\u52bf\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u53c1\u4e2a\u4eba\u4e45\u6218\u975e\u6211\u4e7e\u7239\u5bf9 \u624b\uff0c\u59b9\u5b50\uff0c\u54b1\u4eec\u4e14\u5148\u53d9\u53d9\u522b\u5f8c\u4e4b\u60c5\uff0c\u4e0d\u5fc5\u5fd9\u7740\u52a9\u6218\u3002\u201d \u3000\u3000\u5ba2\u5a09\u5a77\u4fa7\u8033\u542c\u53a2\u623f\u5185\u5a74\u513f\u7684\u54ed\u58f0\uff0c\u9053\uff1a\u201c\u54b1\u4eec\u5148\u77a7\u77a7\u7a46\u4e5d\u5a18\u5427\uff0c\u5979\u6bcd\u5b50\u53d7\u4e86\u8fd9\u573a\u853c\u6050\uff0c \u4e0d\u77e5\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u7389\u7f57\u5239\u968f\u5979\u8d70\u4eba\u53a2\u623f\uff0c\u53ea\u89c1\u7a46\u4e5d\u5a18\u5f62\u5bb9\u67af\u69c1\uff0c\u624b\u8db3\u6218\u98a4\uff0c\u5c06\u513f\u5b50\u7d27\u62b1\u8d34\u5728\u80f8\u524d\u3002\u5ba2\u5a09\u5a77 \u95ee\u9053\uff1a\u201c\u5ac2\u5ac2\uff0c\u4f84\u513f\u6ca1\u53d7\u635f\u5bb3\u9ebd\uff1f\u6211\u66ff\u4f60\u62b1\uff0c\u4f60\u6b47\u6b47\u5427\u3002\u201d \u3000\u3000\u7a46\u4e5d\u5a18\u6c14\u82e5\u6e38\u4e1d\uff0c\u5598\u5401\u5401\u7684\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u6210\u5566\u3002\u8ba9\u6211\u591a\u62b1\u4ed6\u4e00\u4f1a\u5427\u3002\u5e78\u597d\u6ca1\u906d\u7740\u4ec0\u9ebd\u4f24 \u5bb3\u3002\u201d\u7389\u7f57\u5239\u5bf9\u7a46\u4e5d\u5a18\u672c\u6765\u662f\u5341\u5206\u538c\u6076\uff0c\u89c1\u6b64\u60c5\u666f\uff0c\u5fc3\u4e2d\u4e00\u9178\uff0c\u6012\u6c14\u4e0a\u51b2\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u66ff\u4f60 \u628a\u90a3\u51e0\u4e2a\u4eba\u5168\u90e8\u6740\u6389\uff01\u201d\u7a46\u4e5d\u5a18\u6025\u6323\u624e\u53eb\u9053\uff1a\u201c\u4e0d\u8981\uff0c\u4e0d\u8981\uff01\u201d\u7389\u7f57\u5239\u5947\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u66ff\u4f60 \u7684\u4e08\u592b\u62a5\u4ec7\u5417\uff1f\u201d\u7a46\u4e5d\u5a18\u9053\uff1a\u201c\u8fd9\u90fd\u662f\u4ed6\u9020\u7684\u5b7d\uff0c\u4ed6\uff0c\u4ed6\u2026\u2026\u201d\u58f0\u97f3\u98a4\u6296\uff0c\u8bf4\u4e0d\u4e0b\u53bb\u3002\u5ba2\u5a09 \u5a77\u4e5f\u9053\uff1a\u201c\u51a4\u5bb6\u5b9c\u89e3\u4e0d\u5b9c\u7ed3\uff0c\u6211\u7684\u5e08\u54e5\u7f6a\u6709\u5e94\u5f97\uff0c\u4f46\u4ed6\u4eec\u7684\u624b\u6bb5\u4e5f\u6bd2\u8fa3\u4e86\u4e9b\uff0c\u8981\u4ed6\u4eec\u4e0d\u6d89\u53ca \u65e0\u8f9c\uff0c\u5c31\u8ba9\u4ed6\u4eec\u53bb\u5427\u3002\u201d\u7389\u7f57\u5239\u7741\u5927\u4e86\u773c\uff0c\u5ba2\u5a09\u5a77\u5728\u5979\u8033\u8fb9\u4f4e\u4f4e\u8bf4\u9053\uff1a\u201c\u662f\u6211\u5e08\u54e5\u5f3a\u5978\u4e86\u522b \u4eba\u7684\u59bb\u5b50\uff0c\u624d\u60f9\u4e86\u8fd9\u73ed\u4eba\u4e0a\u95e8\u7684\uff01\u201d\u7a46\u4e5d\u5a18\u6599\u77e5\u4ed6\u4eec\u8bf4\u7684\u662f\u4ec0\u9ebd\uff0c\u4ee5\u624b\u63a9\u9762\uff0c\u4fa7\u8f6c\u4e86\u8eab\u3002 \u3000\u3000\u7389\u7f57\u5239\u53c8\u662f\u4e00\u6012\uff0c\u5979\u6700\u6068\u7537\u4eba\u6b3a\u8d1f\u5973\u4eba\uff0c\u4f55\u51b5\u662f\u5f3a\u5978\u8feb\u6b7b\u4ea1\u4e8b\u3002\u8fd9\u65f6\u5ead\u9662\u4e2d\u6253\u6597\u5f97\u5341\u5206 \u6fc0\u70c8\uff0c\u5ffd\u542c\u5f97\u90a3\u970d\u8001\u5934\u5b50\u5927\u53eb\u4e00\u58f0\uff0c\u4f3c\u4e4e\u662f\u7ed9\u94c1\u98de\u9f99\u638c\u529b\u626b\u4e2d\u3002 \u3000\u3000\u7389\u7f57\u5239\u51b2\u51fa\u623f\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\u4f4f\u624b\uff01\u201d\u94c1\u98de\u9f99\u5288\u4e86\u970d\u5143\u4ef2\u4e00\u638c\uff0c\u8feb\u5f97\u4ed6\u97ad\u6cd5\u6563\u4e71\uff0c\u4e3b \u529b\u524a\u5f31\uff0c\u654c\u52bf\u53ef\u7834\uff0c\u95fb\u8a00\u4e00\u6014\uff0c\u7389\u7f57\u5239\u53c8\u53eb\u9053\uff1a\u201c\u4e0d\u80fd\u5168\u602a\u4ed6\u4eec\uff0c\u7239\u7239\u4f4f\u624b\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u6115\u7136\u6536\u638c\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u8feb\u6b7b\u4eba\u547d\uff0c\u51cc\u8fb1\u5987\u5b7a\uff0c\u5fc3\u72e0\u624b\u8fa3\uff0c\u7f6a\u6076\u6ed4\u5929\uff0c\u600e\u9ebd\u53ef\u4ee5\u8f7b \u9976\uff1f\u201d \u3000\u3000\u970d\u5143\u4ef2\u4ee5\u624b\u629a\u4f24\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u7ea2\u82b1\u9b3c\u6bcd\u5df2\u6b7b\uff0c\u5979\u7684\u4ec7\u6211\u4eec\u4e0d\u5fc5\u8bf4\u4e86\u3002\u201d\u4f38\u624b\u4e00\u6307\u516c\u5b59\u96f7 \u7684\u8eab\u9053\uff1a\u201c\u5979\u7684\u5b9d\u8d1d\u513f\u5b50\uff0c\u8feb\u5978\u6211\u5f92\u5f1f\u7684\u59bb\u5b50\uff0c\u81f3\u4ee4\u5979\u60ac\u6881\u81ea\u5c3d\uff0c\u5982\u4eca\u6211\u4eec\u5c06\u4ed6\u540a\u6b7b\uff0c\u4e00\u62a5 \u8fd8\u4e00\u62a5\uff0c\u6709\u4ec0\u9ebd\u4e0d\u5bf9\u7684\u5730\u65b9\uff1f\u201d \u3000\u3000\u94c1\u98de\u9f99\u6115\u7136\u95ee\u9053\uff1a\u201c\u88f3\u513f\uff0c\u4ed6\u4eec\u7684\u8bdd\u53ef\u662f\u771f\u7684\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u662f\u771f\u7684\uff01\u201d\u970d\u5143\u4ef2\u51b7\u7b11 \u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u95ee\u60c5\u7531\uff0c\u6a2a\u91cc\u63d2\u624b\uff0c\u6253\u4f24\u4e86\u6211\uff0c\u5c1a\u6ca1\u4ec0\u9ebd\uff1f\u8fd8\u91cd\u4f24\u4e86\u6211\u7684\u5f92\u513f\uff0c\u8fd9\u8be5\u600e\u8bf4\uff1f\u201d \u3000\u3000\u7389\u7f57\u5239\u8fc8\u524d\u4e00\u6b65\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u6709\u8bdd\u8bf4\uff01\u201d\u674f\u773c\u4e00\u7741\uff0c\u51b7\u68ee\u68ee\u7684\u76ee\u5149\u5728\u53c1\u4eba\u9762\u4e0a\u626b \u8fc7\u3002\u970d\u5143\u4ef2\u867d\u662f\u6210\u540d\u7684\u524d\u8f88\u4eba\u7269\uff0c\u4e5f\u89c9\u5fc3\u5185\u4e00\u5bd2\u3002\u5fd9\u9053\uff1a\u201c\u8bf7\u8d50\u6559\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u4e00\u4eba\u505a\u4e8b\u4e00\u4eba\u5f53\uff0c\u516c\u5b59\u96f7\u9020\u4e86\u7f6a\u5b7d\uff0c\u4f60\u4eec\u5c06\u4ed6\u540a\u6b7b\u4e5f\u4fbf\u7f62\u4e86\u3002\u8fd9\u5173\u4ed6\u7684\u59bb \u5b50\u4e0e\u5e08\u59b9\u4f55\u4e8b\uff1f\u5df2\u6240\u4e0d\u6b32\uff0c\u52ff\u65bd\u65bc\u4eba\u3002\u54fc\uff0c\u54fc\uff0c\u4f60\u4eec\u5f53\u5973\u4eba\u662f\u597d\u6b3a\u8d1f\u7684\u5417\uff1f\u201d \u3000\u3000\u970d\u5143\u4ef2\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u7389\u7f57\u5239\u8bed\u8c03\u7a0d\u7f13\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u505a\u5f97\u4e0d\u5f53\uff0c\u53d7\u4e86\u4e00\u638c\uff0c\u4e5f\u662f\u5e94\u5f53\u3002\u4f60 \u7684\u8fd9\u4e2a\u5f92\u513f\u5c45\u7136\u60f3\u4fae\u8fb1\u6211\u7684\u5a09\u5a77\u59b9\u5b50\uff0c\u672c\u5c5e\u7f6a\u65e0\u53ef\u902d\uff0c\u59d1\u5ff5\u4ed6\u662f\u56e0\u7231\u59bb\u60e8\u6b7b\uff0c\u6c14\u6012\u653b\u5fc3\uff0c\u62a5 \u590d\u903e\u4efd\uff0c\u6211\u53ef\u94d9\u4ed6\u4e00\u6b7b\u3002\u201d\u90a3\u9556\u5ba2\u7ed9\u7389\u7f57\u5239\u523a\u4e2d\u7a74\u9053\uff0c\u75db\u695a\u5f02\u5e38\uff0c\u7389\u7f57\u5239\u7684\u5251\u5c16\u523a\u7a74\uff0c\u53c8\u662f \u72ec\u95e8\u7edd\u6280\uff0c\u4ed6\u4eba\u65e0\u6cd5\u53ef\u89e3\uff0c\u6240\u4ee5\u81f3\u4eca\u5c1a\u5728\u5730\u4e0b\u8f97\u8f6c\u547b\u541f\u3002\u7389\u7f57\u5239\u8bdd\u58f0\u4e00\u987f\uff0c\u7a81\u7136\u98de\u8eab\u7eb5\u8d77\uff0c \u4e00\u811a\u5411\u4ed6\u7684\u8170\u7b72\u8e22\u53bb\uff01\u970d\u5143\u4ef2\u5927\u6012\u559d\u9053\uff1a\u201c\u4f60\u505a\u4ec0\u9ebd\uff1f\u201d\u62e6\u963b\u4e0d\u53ca\uff0c\u8f6f\u97ad\u5237\u7684\u4e00\u626b\u3002\u7389\u7f57\u5239 \u65e9\u5df2\u8df3\u5f00\uff0c\u7b11\u9053\uff1a\u201c\u72d7\u54ac\u5415\u6d1e\u5bbe\uff0c\u4e0d\u8bc6\u597d\u4eba\u5fc3\u3002\u4f60\u7684\u5f92\u513f\u4f55\u66fe\u53d7\u4e86\u91cd\u4f24\uff1f\u4f60\u770b\uff0c\u4ed6\u73b0\u5728\u4e0d\u662f \u5df2\u7ecf\u597d\u4e86\uff1f\u201d \u3000\u3000\u90a3\u7f25\u5ba2\u7ed9\u5979\u4e00\u8e22\u4e4b\u5f8c\uff0c\u8840\u8109\u6d41\u901a\uff0c\u75db\u695a\u82e5\u5931\uff0c\u970d\u5730\u7ad9\u4e86\u8d77\u6765\u3002\u7389\u7f57\u5239\u53c8\u9053\uff1a\u201c\u8fd8\u6709\u4f60\u90a3 \u4e2a\u5f92\u5f1f\uff0c\u6b3a\u4fae\u5987\u5b7a\uff0c\u66f4\u662f\u4e0d\u8be5\u3002\u6211\u8981\u8ba9\u4ed6\u7559\u4e0b\u4e00\u70b9\u8bb0\u53f7\u3002\u201d\u624b\u6307\u4e00\u5f39\uff0c\u72ec\u95e8\u6697\u5668\u5b9a\u5f62\u9488\u500f\u7684 \u51fa\u624b\uff0c\u90a3\u4eba\u521a\u624d\u7ed9\u94c1\u98de\u9f99\u4e00\u6454\uff0c\u6298\u65ad\u4e86\u4e24\u6839\u808b\u9aa8\uff0c\u6b63\u501a\u5728\u6811\u4e0a\u5598\u606f\uff0c\u7a81\u89c1\u4e24\u70b9\u94f6\u5149\uff0c\u95ea\u7535\u98de \u5230\uff0c\u53ea\u89c9\u8033\u9645\u4e00\u51c9\uff0c\u4e00\u9635\u523a\u75db\uff0c\u4e24\u8fb9\u8033\u73e0\u90fd\u7ed9\u7a7f\u4e86\u4e00\u4e2a\u5c0f\u6d1e\u3002 \u3000\u3000\u7389\u7f57\u5239\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u7239\uff0c\u6211\u90fd\u66ff\u4f60\u53d1\u843d\u4e86\uff0c\u4f60\u8fd8\u6709\u4ec0\u9ebd\u8981\u8bf4\u7684\u9ebd\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a \u201c\u970d\u8001\u4e8c\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u5df2\u6b7b\uff0c\u4f60\u7684\u5f92\u5f1f\u4e4b\u4ec7\u4ea6\u5df2\u62a5\u4e86\uff0c\u4f60\u8fd8\u7559\u5728\u8fd9\u91cc\u505a\u4ec0\u9ebd\u4e86\u6211\u8fd9\u4e2a\u4e7e\u5973\u513f\u7684 \u813e\u6c14\u6bd4\u6211\u66f4\u786c\u3002\u4f60\u518d\u7f57\u5506\uff0c\u53ea\u6709\u81ea\u8ba8\u82e6\u5403\uff01\u201d \u3000\u3000\u970d\u5143\u4ef2\u7b49\u89c1\u8fc7\u7389\u7f57\u5239\u7684\u672c\u9886\uff0c\u5fc3\u60f3\uff1a\u94c1\u98de\u9f99\u4e00\u4eba\u5df2\u662f\u96be\u6597\uff0c\u4f55\u51b5\u53c8\u6dfb\u4e0a\u8fd9\u4e2a\u5973\u9b54\u5934\u3002\u5fc3 \u867d\u4e0d\u670d\uff0c\u4e5f\u53ea\u597d\u62f1\u62f1\u624b\u9053\uff1a\u201c\u8001\u94c1\uff0c\u54b1\u4eec\u4eca\u65e5\u4e00\u573a\u8bef\u4f1a\uff0c\u8bf4\u5f00\u4fbf\u7b97\uff0c\u5f8c\u4f1a\u6709\u671f\u3002\u201d\u5e26\u9886\u4e24\u4e2a \u5f92\u5f1f\u6c14\u547c\u547c\u7684\u8f6c\u8eab\u4fbf\u8d70\uff0c\u667a\u4e0a\u4eba\u4e0e\u62d9\u9053\u4eba\u4e5f\u8ddf\u7740\u8d70\u4e86\u3002 \u3000\u3000\u94c1\u98de\u9f99\u53f9\u4e86\u53e3\u6c14\u3002\u53a2\u623f\u91cc\u7a46\u4e5d\u5a18\u7684\u58f0\u97f3\u65ad\u65ad\u7eed\u7eed\uff0c\u4f3c\u4e4e\u662f\u5728\u4f4e\u58f0\u547c\u5524\u8c01\u4eba\u3002\u7389\u7f57\u5239\u6084\u6084 \u8bf4\u9053\uff1a\u201c\u7239\uff0c\u6211\u770b\u5979\u662f\u4e0d\u6210\u4e86\uff0c\u54b1\u4eec\u77a7\u77a7\u5979\u5427\u3002\u201d\u94c1\u98de\u9f99\u9ed8\u9ed8\u65e0\u8a00\u968f\u7389\u7f57\u5239\u8d70\u8fdb\u53a2\u623f\u3002 \u3000\u3000\u7a46\u4e5d\u5a18\u9762\u5982\u91d1\u7eb8\uff0c\u89c1\u94c1\u98de\u9f99\u8d70\u8fdb\uff0c\u9053\uff1a\u201c\u8001\u7237\uff0c\u6211\u6c42\u4f60\u4e00\u4e8b\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u7a46\u4e5d\u5a18\u9053\uff1a\u201d\u6211\u60f3\u628a\u8fd9\u513f\u5b50\u9001\u7ed9\u4f60\u505a\u5b59\u513f\uff0c\u6c42\u4f60\u6536\u7559\u3002\u5c06\u6765\u4ed6\u7ed3\u5a5a\u751f\u5b50\uff0c\u7b2c\u4e00\u4e2a\u4fbf\u59d3\u94c1\uff0c\u7ee7 \u627f\u94c1\u5bb6\u7684\u9999\u70df\uff0c\u7b2c\u4e8c\u4e2a\u624d\u59d3\u516c\u5b59\uff0c\u8ba9\u4ed6\u7559\u4e0b\u6211\u5a46\u5a46\u7684\u4e00\u8109\u3002\u82e5\u8fd8\u6709\u7b2c\u53c1\u4e2a\u7537\u5b69\u7684\u8bdd\uff0c\u90a3\u624d\u59d3 \u91d1\u3002\u201d\u7a46\u4e5d\u5a18\u672c\u662f\u94c1\u98de\u9f99\u4ee5\u524d\u7684\u59be\u4f8d\uff0c\u5982\u4eca\u5374\u628a\u513f\u5b50\u9001\u7ed9\u4ed6\u505a\u5b59\u513f\uff0c\u6b64\u4e8b\u8bf4\u6765\u53ef\u7b11\u3002\u53ef\u662f\u94c1 \u98de\u9f99\u6b64\u9645\u90a3\u91cc\u8fd8\u4f1a\u8ba1\u8f83\u5230\u8f88\u4efd\u79f0\u8c13\u7684\u95ee\u9898\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f2\uff0c\u524d\u5c18\u5f80\u4e8b\uff0c\u4e00\u4e00\u4ece\u94c1\u98de\u9f99\u8111\u6d77\u4e2d\u63a0\u8fc7\u3002\u4ed6\u60f3\u8d77\u4e86\u81ea\u5df1\u81ea\u4ece\u53d1\u59bb\u6b7b\u5f8c\uff0c\u4e3a\u4e86\u73ca \u745a\u65e0\u4eba\u7167\u7ba1\uff0c\u4e5f\u4e3a\u4e86\u8981\u627e\u4e00\u4e2a\u4eba\u6765\u6170\u81ea\u5df1\u7684\u5bc2\u5be5\uff0c\u65bc\u662f\u8ba8\u4e86\u8fd9\u4e2a\u5728\u6c5f\u6e56\u4e0a\u5356\u89e3\u7684\u5973\u5b50\u2014\u2014\u7a46 \u4e5d\u5a18\u3002\u5f53\u65f6\u81ea\u5df1\u5b8c\u5168\u6ca1\u8003\u8651\u5230\u5e74\u9f84\u7684\u76f8\u5dee\uff0c\u6027\u60c5\u7684\u662f\u5426\u6295\u5408\uff0c\u5c31\u628a\u5979\u8ba8\u56de\u6765\u4e86\u3002\u800c\u4e14\u53c8\u4e0d\u7ed9 \u5979\u4ee5\u59bb\u5b50\u7684\u540d\u4e49\uff0c\u5927\u5927\u7684\u635f\u5bb3\u4e86\u5979\u7684\u5c0a\u4e25\u3002\u201c\u5979\u672c\u6765\u662f\u4e0d\u613f\u610f\u7684\u554a\uff0c\u5341\u591a\u5e74\u6765\u5979\u548c\u6211\u5728\u4e00 \u8d77\uff0c\u4ece\u6765\u672a\u5f97\u8fc7\u5feb\u6d3b\uff0c\u602a\u4e0d\u5f97\u5979\u5fc3\u751f\u5916\u5411\uff0c\u5979\u79bb\u5f00\u6211\u672c\u662f\u5e94\u8be5\uff0c\u53ef\u60dc\u5979\u4e00\u9519\u518d\u9519\uff0c\u4e3a\u4e86\u6025\u65bc \u6c42\u5076\uff0c\u5374\u7ed3\u4e0b\u4e86\u8fd9\u6bb5\u5b7d\u7f18\u3002\u867d\u8bf4\u662f\u7ea2\u82b1\u9b3c\u6bcd\u7684\u5b9d\u8d1d\u513f\u5b50\u7d2f\u4e86\u5979\uff0c\u4f46\u8ffd\u6e90\u7978\u59cb\uff0c\u5bb3\u5979\u7684\u4eba\u5c82\u4e0d \u662f\u6211\u5417\uff1f\u201d\u94c1\u98de\u9f99\u6df1\u89c9\u5185\u759a\uff0c\u89c9\u5f97\u8fd9\u662f\u81ea\u5df1\u5e73\u751f\u7684\u4e00\u5927\u8fc7\u9519\u3002 \u3000\u3000\u7a46\u4e5d\u5a18\u5e26\u7740\u5931\u671b\u7684\u773c\u5149\uff0c\u7785\u7740\u94c1\u98de\u9f99\uff0c\u4f4e\u4f4e\u8bf4\u9053\uff1a\u201c\u8001\u7237\uff0c\u4f60\u8fd8\u6068\u6211\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a \u201c\u4e0d\uff0c\u6211 \u3000\u3000\u662f\u6c42\u4f60\u4e0d\u8981\u6068\u6211\u3002\u201d\u7a46\u4e5d\u5a18\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u6068\u4f60\u3002\u4f60\u987f\u610f\u6536\u7559\u6211\u7684\u513f\u5b50\u5417\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a \u201c\u6211\u628a\u4ed6\u5f53\u505a\u4eb2\u5b59\u513f\u770b\u5f85\u3002\u201d\u7a46\u4e5d\u5a18\u6ee1\u610f\u7684\u7b11\u4e86\u4e00\u7b11\uff0c\u9616\u4e0a\u53cc\u773c\u3002 \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u5979\u5df2\u53bb\u4e86\u3002\u201d\u94c1\u98de\u9f99\u51c4\u7136\u65e0\u8bed\uff0c\u51e0\u4e4e\u6ef4\u51fa\u6cea\u6765\u3002\u5ba2\u5a09\u5a77\u5ffd\u9053\uff1a\u201c\u7239\uff0c\u6211\u4e5f \u6709\u8bdd\u8bf4\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u4e5f\u8ddf\u6211\u4e00\u6837\u79f0\u547c\uff1f\u4f60\u6162\u70b9\u8bf4\uff0c\u8ba9\u6211\u731c\u731c\u4f60\u60f3\u8bf4\u7684\u8bdd\u3002\u5514\uff0c\u4f60\u4e5f\u4e00 \u5b9a\u662f\u60f3\u8ba4\u771f\u4e7e\u7239\u4e86\u3002\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u6211\u7684\u4f84\u513f\u662f\u94c1\u8001\u524d\u8f88\u7684\u5b59\u513f\uff0c\u90a3\u4f60\u8bf4\u6211\u4e0d\u8be5\u53eb\u4ed6\u505a\u7239 \u5417\uff1f\u201d\u94c1\u96c9\u9f99\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u6211\u6b7b\u4e86\u4e00\u4e2a\u5973\u513f\uff0c\u5374\u591a\u4e86\u4e24\u4e2a\uff0c\u8fd8\u6709\u5b59\u513f\uff0c\u60f3\u4e0d\u5230\u6211\u7684\u665a\u666f\u5012 \u771f\u4e0d\u9519\u3002\u201d\u5ba2\u5a09\u5a77\u77e5\u4ed6\u5df2\u5141\uff0c\u5927\u559c\u78d5\u5934\u3002\u94c1\u98de\u9f99\u62c9\u5979\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5c06\u4f60\u7684\u5e08\u54e5\u5e08\u5ac2\u57cb\u6389 \u5427\u3002\u201d \u3000\u3000\u53c1\u4eba\u5c31\u5728\u90a3\u69d0\u6811\u4e0b\u6398\u4e00\u4e2a\u5893\u7a74\uff0c\u5c06\u516c\u5b59\u96f7\u548c\u7a46\u4e5d\u5a18\u7684\u8eab\u653e\u4e0b\u63a9\u57cb\u3002\u7389\u7f57\u5239\u6b63\u5728\u4ee5\u94f2\u62e8 \u571f\uff0c\u4fa7\u8033\u4e00\u542c\uff0c\u5ffd\u7136\u8bf4\u9053\uff1a\u201c\u54a6\uff0c\u6709\u4eba\u6765\u5566\uff1f\u201d\u5ba2\u5a09\u5a77\u4e00\u70b9\u4e5f\u542c\u4e0d\u51fa\u4ec0\u9ebd\uff0c\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u6211\u505a\u5f3a\u76d7\u591a\u5e74\uff0c\u522b\u7684\u6ca1\u5b66\u5230\uff0c\u8fd9\u4f0f\u5730\u542c\u58f0\u7684\u672c\u9886\uff0c\u5374\u662f\u767e\u4e0d\u5931\u4e00\u3002\u201d\u94c1\u98de\u9f99 \u9053\uff1a\u201c\u6709\u591a\u5c11\u4eba\uff1f\u201d\u7389\u7f57\u5239\u542c\u4e86\u4e00\u9635\uff0c\u9053\uff1a\u201c\u56db\u4e2a\u4eba\u90fd\u9a91\u7740\u9a6c\u3002\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u6211\u7684 \u5a18\u6d3e\u4eba\u6765\u8ffd\u6211\u56de\u53bb\u4e86\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u59b9\u5b50\uff0c\u4f60\u4e0d\u8981\u614c\uff0c\u8ba9\u6211\u4eec\u6765\u66ff\u4f60\u53d1\u4ed8\u3002\u201d\u5ba2\u5a09\u5a77\u9053\uff1a \u201c\u4f60\u53ef\u4e0d\u8981\u628a\u4ed6\u4eec\u5168\u90fd\u6740\u6389\u554a\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u4f60\u4e5f\u5f53\u6211\u771f\u7684\u662f\u6740\u4eba\u4e0d\u7728\u773c\u7684\u5973 \u9b54\u738b\u5417\uff1f\u5982\u679c\u6765\u4eba\u4e4b\u4e2d\u6ca1\u6709\u901a\u756a\u5356\u56fd\u7684\u5978\u8d3c\uff0c\u6211\u603b\u53ef\u9976\u4ed6\u4eec\u4e00\u6b7b\u3002\u201d \u3000\u3000\u518d\u8fc7\u4e00\u9635\uff0c\u8e44\u58f0\u5f97\u5f97\u5df2\u5230\u95e8\u524d\u3002\u94c1\u98de\u9f99\u4e0e\u7389\u7f57\u5239\u9000\u5165\u53a2\u623f\uff0c\u53ea\u542c\u5f97\u5916\u9762\u7684\u4eba\u62cd\u95e8\u53eb\u9053\uff1a \u201c\u8bf7\u5bab\u4e3b\u5f00\u95e8\u3002\u201d\u5ba2\u5a09\u5a77\u5728\u5bab\u4e2d\u88ab\u5e95\u4e0b\u4eba\u5c0a\u4e3a\u201c\u5bab\u4e3b\u201d\uff0c\u201c\u5bab\u201d\u201c\u516c\u201d\u540c\u97f3\uff0c\u6240\u4eab\u53d7\u7684\u5c0a\u8363 \u548c\u516c\u4e3b\u4e5f\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u5ba2\u5a09\u5a77\u6253\u5f00\u5927\u95e8\uff0c\u53ea\u89c1\u6765\u7684\u679c\u662f\u56db\u4eba\uff0c\u90fd\u662f\u81ea\u5df1\u6bcd\u4eb2\u6240\u517b\u7684\u536b\u58eb\u3002\u4e3a\u9996\u7684\u53eb\u505a\u9ec4\u5f6a\uff0c\u662f \u201c\u4e73\u5a18\u5e9c\u201d\u7684\u603b\u7ba1\u3002\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u4f60\u4eec\u6765\u505a\u4ec0\u9ebd\uff1f\u201d\u9ec4\u5f6a\u9053\uff1a\u201c\u5949\u5723\u592b\u4eba\u8bf7\u5bab\u4e3b\u56de\u53bb\u3002\u201d\u5ba2 \u5a09\u5a77\u51b7\u51b7\u4e00\u7b11\uff0c\u6447\u9996\u8bf4\u9053\uff1a\u201c\u6211\u662f\u7edd\u4e0d\u56de\u53bb\u7684\u4e86\uff01\u201d \u3000\u3000\u9ec4\u5f6a\u8eac\u8170\u8bf4\u9053\uff1a\u201c\u5949\u5723\u592b\u4eba\u601d\u5ff5\u5bab\u4e3b\uff0c\u8336\u996d\u65e0\u5fc3\uff0c\u5bab\u4e3b\u82e5\u4e0d\u56de\u53bb\uff0c\u53ea\u6050\u5979\u4f1a\u601d\u5ff5\u6210 \u75be\u3002\u201d\u5ba2\u5a09\u5a77\u5fc3\u4e2d\u4e00\u9178\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8fdc\u9053\u800c\u6765\uff0c\u6b47\u4e00\u6b47\u5427\u3002\u7ed9\u6211\u8bf4\u8bf4\u5bab\u4e2d\u7684\u8fd1\u4e8b\u3002\u201d\u5ba2\u5a09\u5a77 \u662f\u60f3\u63a2\u95ee\u6bcd\u4eb2\u7684\u60c5\u51b5\uff0c\u9ec4\u5f6a\u5374\u4ee5\u4e3a\u5979\u5c1a\u604b\u6155\u5bab\u5ef7\u7684\u7e41\u534e\uff0c\u89c1\u5979\u53e3\u98ce\u4f3c\u8f6f\uff0c\u5750\u4e86\u4e0b\u6765\uff0c\u7b11\u9053\uff1a \u201c\u5bab\u4e3b\u662f\u660e\u767d\u4eba\uff0c\u5929\u4e0b\u65e0\u4e0d\u662f\u7684\u7236\u6bcd\uff0c\u8fd8\u662f\u56de\u53bb\u7684\u597d\u3002\u201d\u5ba2\u5a09\u5a77\u542c\u4e86\u201c\u5929\u4e0b\u65e0\u4e0d\u662f\u7684\u7236\u6bcd\u201d \u8fd9\u53e5\uff0c\u4e0d\u89c9\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\u3002\u9ec4\u5f6a\u53c8\u9053\uff1a\u201c\u9b4f\u516c\u516c\u7684\u6743\u529b\u8d8a\u53d1\u5927\u4e86\uff0c\u53c8\u6709\u597d\u51e0\u7701\u7684\u7763\u629a\uff0c\u6c42\u4ed6 \u6536\u505a\u4e7e\u513f\uff0c\u9001\u4e86\u91cd\u793c\uff0c\u4ed6\u8fd8\u4e0d\u5927\u613f\u610f\u6536\u5462\u3002\u73b0\u5728\u5bab\u91cc\u5bab\u5916\uff0c\u90fd\u53eb\u4ed6\u505a\u4e5d\u5343\u5c81\u3002\u9b4f\u516c\u516c\u4e5f\u5f88\u60f3 \u5ff5\u5bab\u4e3b\uff0c\u53eb\u6211\u4eec\u52a1\u5fc5\u5c06\u5b98\u4e3b\u5bfb\u56de\u3002\u201d\u9ec4\u5f6a\u4e0d\u63d0\u9b4f\u5fe0\u8d24\u5c1a\u53ef\uff0c\u63d0\u8d77\u4e86\u9b4f\u5fe0\u8d24\uff0c\u5ba2\u5a09\u5a77\u987f\u89c9\u4e00\u9635 \u6076\u5fc3\uff0c\u5fc3\u9053\uff1a\u201c\u8c01\u8bf4\u5929\u4e0b\u65e0\u4e0d\u662f\u7684\u7236\u6bcd\uff1f\u8981\u6211\u56de\u53bb\uff0c\u770b\u7740\u9b4f\u5fe0\u8d24\u548c\u6211\u7684\u6bcd\u4eb2\u6df7\uff0c\u90a3\u771f\u4e0d\u5982\u6b7b \u4e86\u8fd8\u597d\u3002\u201d \u3000\u3000\u9ec4\u5f6a\u89c1\u5ba2\u5a09\u5a77\u6da8\u7ea2\u4e86\u8138\uff0c\u773c\u5149\u5947\u5f02\uff0c\u82e5\u6028\u82e5\u6012\uff0c\u505c\u4e86\u8bf4\u8bdd\uff0c\u6b63\u60f3\u8bbe\u8f9e\u5a49\u529d\uff0c\u5ba2\u5a09\u5a77\u5ffd\u7136 \u62c2\u8896\u800c\u8d77\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u70e6\u4f60\u4eec\u66ff\u6211\u56de\u53bb\u7968\u544a\u6bcd\u4eb2\uff0c\u53eb\u5979\u81ea\u5df1\u4fdd\u91cd\uff0c\u6211\u662f\u7edd\u4e0d\u56de\u53bb\u7684\u4e86\uff01\u201d \u3000\u3000\u9ec4\u5f6a\u6115\u7136\u8d77\u7acb\uff0c\u9053\uff1a\u201c\u5bab\u4e3b\uff0c\u5bab\u4e3b\uff0c\u8fd9\uff0c\u8fd9\uff0c\u8fd9\u53eb\u6211\u4eec\u600e\u6837\u5411\u5949\u5723\u592b\u4eba\u548c\u9b4f\u516c\u516c\u4ea4 \u4ee3\uff1f\u201d\u5176\u4ed6\u53c1\u540d\u536b\u58eb\u4e5f\u90fd\u7ad9\u4e86\u8d77\u6765\uff0c\u56db\u89d2\u5206\u7acb\uff0c\u5c06\u5ba2\u5a09\u5a77\u62e6\u5728\u5f53\u4e2d\u3002 \u3000\u3000\u53a2\u623f\u5185\u5ffd\u7136\u51b7\u7b11\u4e00\u58f0\uff0c\u7389\u7f57\u5239\u548c\u94c1\u98de\u9f99\u4e00\u540c\u8d70\u51fa\u3002\u7389\u7f57\u5239\u51b7\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u60f3\u7ed1\u67b6\u5417\uff1f \u5582\uff0c\u5f3a\u76d7\u7684\u7956\u5b97\u5c31\u5728\u8fd9\u91cc\uff0c\u4f60\u4eec\u7167\u5b50\u201c\u773c\u775b\u201d\u653e\u4eae\u4e00\u70b9\uff0c\u8981\u7ed1\u7968\u4e5f\u5f97\u8981\u6211\u70b9\u5934\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u548c\u94c1\u98de\u9f99\u66fe\u5927\u95f9\u5bab\u95f1\u5185\u82d1\uff0c\u536b\u58eb\u4eec\u8c01\u4eba\u4e0d\u6653\uff0c\u8fd9\u4e00\u4e0b\u7a81\u5982\u5176\u6765\uff0c\u56db\u540d\u536b\u58eb\u5168\u90fd\u614c \u4e86\u3002\u94c1\u98de\u9f99\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u88f3\u513f\uff0c\u4e0d\u8981\u5413\u552c\u4ed6\u4eec\u3002\u5404\u4f4d\u8fdc\u9053\u800c\u6765\uff0c\u518d\u5750\u4e00\u5750\uff0c\u518d\u5750\u4e00\u5750\u3002\u5a09\u5a77 \u662f\u6211\u7684\u4e7e\u5973\u513f\uff0c\u4f60\u4eec\u8bf7\u5979\u56de\u5b98\uff0c\u5c31\u4e0d\u8bf7\u6211\u5417\uff1f\u54c8\u54c8\uff0c\u6211\u7684\u4e7e\u5973\u513f\u56de\u53bb\u505a\u5b98\u4e3b\u90a3\u662f\u4e0d\u9519\uff0c\u53ef\u662f \u4f60\u4eec\u53eb\u6211\u8fd9\u4e2a\u5b64\u5be1\u8001\u5934\u53c8\u501a\u9760\u8c01\u554a\uff01\u8981\u8bf7\u5c31\u8be5\u8fde\u6211\u4e5f\u4e00\u540c\u8bf7\u53bb\u3002\u201d\u7389\u7f57\u5239\u4e5f\u7b11\u9053\uff1a\u201c\u662f\u5440\uff0c \u5a09\u5a77\u4e5f\u662f\u6211\u7684\u4e7e\u5b50\uff0c\u6211\u548c\u5979\u4eb2\u5982\u59d0\u59b9\uff0c\u820d\u4e0d\u5f97\u5206\u79bb\uff0c\u4f60\u4eec\u8981\u8bf7\uff0c\u6211\u4e5f\u8981\u540c\u53bb\u3002\u5fa1\u82b1\u56ed\u5f88\u597d \u73a9\uff0c\u4ee5\u524d\u4f60\u4eec\u4e0d\u8bf7\u6211\u4e5f\u53bb\u8fc7\u3002\u82e5\u5f97\u4f60\u4eec\u9080\u8bf7\uff0c\u5c31\u662f\u5a09\u5a77\u4e0d\u53bb\uff0c\u6211\u4e5f\u8981\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ec4\u5f6a\u66f4\u662f\u5403\u60ca\uff0c\u4ed6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u5ba2\u5a09\u5a77\u4f1a\u8ba4\u8fd9\u4e24\u4e2a\u8001\u5c11\u9b54\u5934\u505a\u4e7e\u7239\u4e49\u59d0\u3002\u9762\u8272\u5ffd\u9752\u5ffd \u767d\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u6323\u624e\u8bf4\u51fa\u51e0\u53e5\u8bdd\u6765\uff1a\u201c\u4e24\u4f4d\u8981\u53bb\uff0c\u5f85\u6211\u56de\u53bb\u7980\u8fc7\u9b4f\u516c\u516c\u518d\u9068\u8bf7\u5427\u3002\u201d\u7389\u7f57 \u5239\u51b7\u7b11\u9053\uff1a\u201c\u8c01\u7406\u4f60\u4eec\u7684\u9b4f\u516c\u516c\uff01\u201d\u9ec4\u5f6a\u9053\uff1a\u201c\u6211\u4eec\u662f\u6253\u524d\u7ad9\u7684\uff0c\u968f\u5f8c\u8fd8\u6709\u4eba\u6765\u8fce\u63a5\u3002\u90a3\u4e9b \u4eba\u548c\u4e24\u4f4d\u66fe\u4ea4\u8fc7\u624b\uff0c\u89c1\u4e86\u53ea\u6050\u4e0d\u4fbf\u3002\u8fd8\u662f\u6211\u4eec\u56de\u53bb\u5148\u758f\u901a\u89e3\u91ca\u7684\u597d\u3002\u201d\u9ec4\u5f6a\u5fc3\u60ca\u80c6\u6218\uff0c\u6df1\u6015 \u94c1\u98de\u9f99\u548c\u7389\u7f57\u5239\u5f53\u573a\u52a8\u624b\uff0c\u6240\u4ee5\u7528\u8bf4\u8bdd\u70b9\u51fa\u81ea\u5df2\u5f8c\u9762\u8fd8\u6709\u63f4\u5175\u3002\u7389\u7f57\u5239\u53c8\u662f\u51b7\u51b7\u4e00\u7b11\uff0c\u9ec4\u5f6a \u5ffd\u89c9\u8170\u9645\u4e00\uff0c\u60ac\u5728\u8170\u95f4\u7684\u5175\u5668\u9f99\u5f62\u94c1\u68d2\u88ab\u7389\u7f57\u5239\u4e00\u4f38\u624b\u5c31\u53d6\u53bb\u4e86\uff0c\u53ea\u542c\u5f97\u7389\u7f57\u5239\u51b7\u7b11\u9053\uff1a \u201c\u4f60\u4eec\u60f3\u62ff\u9b4f\u5fe0\u8d24\u6765\u5413\u6211\u5417\uff1f\u54fc\uff0c\u54fc\uff01\u6211\u504f\u4e0d\u6015\uff01\u201d \u3000\u3000\u9ec4\u5f6a\u5413\u5f97\u9762\u65e0\u4eba\u8272\uff0c\u94c1\u98de\u9f99\u9053\uff1a\u201c\u88f3\u513f\uff0c\u5c06\u90a3\u6253\u72d7\u68d2\u7ed9\u6211\u3002\u201d\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u8fd9\u94c1\u68d2\u4e0d \u662f\u7528\u6765\u6253\u72d7\u7684\uff0c\u8fd9\u662f\u5927\u536b\u58eb\u7684\u5175\u5668\uff0c\u7528\u6765\u6253\u4eba\u7684\u3002\u201d\u94c1\u98de\u9f99\u5c06\u94c1\u68d2\u63a5\u8fc7\uff0c\u968f\u624b\u4e00\u62d7\uff0c\u6298\u4e3a\u4e24 \u6bb5\uff0c\u9053\uff1a\u201c\u6211\u5e73\u751f\u6700\u6068\u8c6a\u95e8\u6076\u72ac\uff0c\u8fd9\u94c1\u68d2\u65e2\u7136\u4e0d\u80fd\u7528\u6765\u6253\u72d7\uff0c\u8981\u5b83\u4f55\u7528\uff1f\u201d\u4e22\u5728\u5730\u4e0a\u3002\u5ba2\u5a09 \u5a77\u9053\uff1a\u201c\u4f60\u4eec\u56de\u53bb\u5427\uff0c\u6211\u662f\u7edd\u4e0d\u56de\u5bab\u7684\u4e86\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u8d70\uff0c\u96be\u9053\u8fd8\u8981\u6211\u4eec\u7236\u5973\u9001 \u4f60\u4eec\u4e00\u7a0b\u5417\uff1f\u201d \u3000\u3000\u9ec4\u5f6a\u8fd9\u65f6\u90a3\u91cc\u8fd8\u6562\u591a\u8bdd\uff0c\u6025\u5fd9\u7387\u4f17\u62b1\u5934\u9f20\u7a9c\u800c\u53bb\u3002\u7389\u7f57\u5239\u4e0e\u94c1\u98de\u9f99\u76f8\u5bf9\u5927\u7b11\u3002\u5ba2\u5a09\u5a77 \u9053\uff1a\u201c\u6211\u6015\u4ed6\u4eec\u518d\u6765\u9a9a\u6270\uff0c\u8fd9\u91cc\u662f\u4e0d\u80fd\u518d\u4f4f\u7684\u4e86\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u597d\uff0c\u90a3\u9ebd\u54b1\u4eec\u9a6c\u4e0a\u5c31 \u8d70\u3002\u201d\u8fdb\u5165\u5367\u623f\uff0c\u5c06\u5a74\u513f\u62b1\u8d77\uff0c\u90a3\u5a74\u513f\u751a\u4f3c\u7a46\u4e5d\u5a18\uff0c\u62b1\u5728\u94c1\u98de\u9f99\u624b\u4e0a\uff0c\u5c45\u7136\u4e0d\u54ed\u3002 \u3000\u3000\u53c1\u4eba\u8fde\u591c\u79bb\u5f00\u7ea2\u82b1\u9b3c\u6bcd\u7684\u6545\u5c45\uff0c\u7b2c\u4e8c\u65e5\u5230\u4e86\u8944\u6a0a\uff0c\u6b47\u4e86\u4e00\u5bbf\uff0c\u6298\u5411\u897f\u5317\uff0c\u8d70\u4e86\u4e24\u5929\uff0c\u53ea \u89c1\u524d\u9762\u5c71\u5ce6\u8fde\u7ef5\uff0c\u5ced\u5cf0\u5bf9\u7acb\uff0c\u94c1\u98de\u9f99\u6307\u70b9\u8bf4\u9053\uff1a\u201c\u90a3\u5c31\u662f\u6b66\u5f53\u5c71\u4e86\uff0c\u88f3\u513f\uff0c\u7239\u6ca1\u5e26\u4f60\u8d70\u9519\u8def \u5427\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u867d\u7136\u65e9\u77e5\u94c1\u98de\u9f99\u662f\u60f3\u5f15\u5979\u5230\u6b66\u5f53\u5c71\uff0c\u8fd9\u65f6\u4e00\u89c1\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u6026\u7136\u9707\u6c64\u3002\u8fc7\u4e86\u4e00 \u9635\uff0c\u6602\u9996\u8bf4\u9053\uff1a\u201c\u7239\uff0c\u6211\u4e0d\u60f3\u7792\u4f60\uff0c\u6211\u786e\u662f\u60f3\u89c1\u90a3\u4eba\u4e00\u9762\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u542c\u7f57\u94c1\u81c2\u6240\u8bf4\uff0c \u4ed6\u5bf9\u4f60\u601d\u5ff5\u751a\u6bb7\uff0c\u6211\u4e5f\u671b\u4f60\u65e9\u4e86\u591a\u5e74\u5fc3\u613f\u3002\u6211\u867d\u7136\u4e0d\u613f\u89c1\u6b66\u5f53\u5c71\u90a3\u51e0\u4e2a\u8001\u9053\u58eb\uff0c\u4f46\u4f60\u82e5\u662f\u8981 \u6211\u540c\u53bb\u7684\u8bdd\uff0c\u6211\u5c31\u62da\u7740\u548c\u4ed6\u4eec\u518d\u6253\u4e00\u67b6\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u6b64\u53bb\u5e76\u4e0d\u60f3\u627e\u4ed6\u4eec\u6253\u67b6\uff0c\u6211\u53ea\u662f \u60f3\u53bb\u89c1\u5353\u4e00\u822a\uff0c\u95ee\u4ed6\u5230\u5e95\u662f\u613f\u505a\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\uff0c\u8fd8\u662f\u613f\u548c\u6211\u4e00\u540c\u51fa\u8d70\u3002\u4ed6\u82e5\u613f\u548c\u6211\u4e00\u540c\u51fa \u8d70\uff0c\u90a3\u5c31\u8c01\u4e5f\u626a\u963b\u4e0d\u4e86\u3002\u5ba2\u9b4f\u6d3e\u6765\u7684\u4eba\uff0c\u8bf7\u4e0d\u5230\u5a09\u5a77\u59b9\u5b50\u56de\u5b98\uff0c\u4e00\u5b9a\u4e0d\u80af\u653e\u624b\u3002\u6211\u4eec\u867d\u7136\u4e0d \u6015\u90a3\u4e9b\u9152\u56ca\u996d\u888b\uff0c\u4f46\u6cbf\u9014\u82e5\u7ed9\u4ed6\u4eec\u9a9a\u6270\uff0c\u5230\u5e95\u4e0d\u4fbf\u3002\u4f55\u51b5\u4f60\u53c8\u5e26\u7740\u5a74\u513f\u3002\u4f60\u4eec\u8fd8\u662f\u4e0d\u8981\u803d \u6401\uff0c\u5148\u56de\u5c71\u897f\u53bb\u5427\u3002\u897f\u5317\u4e49\u519b\u52bf\u529b\u6781\u5927\uff0c\u5230\u4e86\u90a3\u8fb9\uff0c\u53ef\u4ee5\u5b89\u5c45\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c \u6211\u4eec\u5c31\u5148\u8d70\u4e86\u3002\u4f60\u53ef\u8981\u5c0f\u5fc3\u4e00\u70b9\uff0c\u90a3\u51e0\u4e2a\u8001\u9053\u58eb\u4ee5\u7384\u95e8\u6b63\u6d3e\u81ea\u5c45\uff0c\u53ea\u6015\u4e0d\u8f7b\u6613\u653e\u4ed6\u4e0b\u5c71\u3002\u201d \u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u8bf4\u7406\u6253\u67b6\u6211\u90fd\u4e0d\u6015\u4ed6\u4eec\u3002\u201d\u94c1\u98de\u9f99\u5fc3\u9053\uff1a\u201c\u53ea\u6015\u5353\u4e00\u822a\u53c8\u518d\u53d8\u5366\u3002\u4f46 \u6210\u4e0e\u4e0d\u6210\uff0c\u4e5f\u8be5\u8ba9\u5979\u4e0a\u5c71\u5f97\u4e2a\u5206\u6653\u3002\u8981\u4e0d\u7136\u95f7\u5728\u5fc3\u91cc\uff0c\u66f4\u4e0d\u597d\u53d7\u3002\u201d\u7389\u7f57\u5239\u53c8\u9053\uff1a\u201c\u6211\u660e\u65e5 \u4e00\u65f1\uff0c\u4fbf\u4e0a\u6b66\u5f53\u5c71\u53bb\uff0c\u6309\u6b66\u6797\u89c4\u77e9\uff0c\u89c1\u4ed6\u4eec\u7684\u638c\u95e8\u3002\u201d\u7b11\u4e86\u4e00\u7b11\uff0c\u7eed\u9053\uff1a\u201c\u7136\u5f8c\u8ba9\u5353\u4e00\u822a\u5c06 \u638c\u95e8\u4ea4\u4ee3\uff0c\u6211\u4eec\u9a6c\u4e0a\u5c31\u56de\u5c71\u897f\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u8fd9\u4e2a\u6708\u6765\uff0c\u65e5\u91cc\u591c\u91cc\uff0c\u5fc3\u4e2d\u90fd\u5ff5\u7740\u5353\u4e00\u822a\u5199\u7ed9\u5979\u7684\u8bd7\u53e5\uff0c\u5fc3\u60f3\u5353\u4e00\u822a\u8fd9\u6b21\u4e00\u5b9a\u4e0d \u4f1a\u8d1f\u5979\u3002\u6240\u4ee5\u8bf4\u5f97\u5341\u5206\u80af\u5b9a\uff0c\u597d\u50cf\u5353\u4e00\u822a\u548c\u5979\u540c\u8d70\uff0c\u5df2\u7ecf\u662f\u5fc5\u7136\u4e4b\u4e8b\u3002 \u3000\u3000\u94c1\u98de\u9f99\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\u3002\u201d\u8fd9\u665a\u4ed6\u4eec\u5728\u6b66\u5f53\u5c71\u4e0b\u7684\u4e00\u4e2a\u5c0f\u9547\u6b47\u5bbf\uff0c\u5230\u4e86\u56db\u66f4 \u65f6\u5206\uff0c\u7389\u7f57\u5239\u4fbf\u722c\u8d77\u8eab\u6765\uff0c\u5411\u94c1\u98de\u9f99\u548c\u5ba2\u5a09\u5a77\u9053\u58f0\u6682\u522b\uff0c\u5355\u8eab\u80cc\u5251\uff0c\u72ec\u4e0a\u5c71\u53bb\u3002\u94c1\u98de\u9f99\u770b\u5979 \u7684\u80cc\u5f71\u6d88\u5931\u5728\u6c89\u6c89\u591c\u8272\u4e4b\u4e2d\uff0c\u4e0d\u89c9\u53f9\u4e86\u53e3\u6c14\uff0c\u54fa\u5583\u8bf4\u9053\uff1a\u201c\u4f46\u613f\u5979\u6b64\u53bb\u80fd\u4e86\u591a\u5e74\u5fc3\u613f\uff0c\u4e0d\u8981 \u50cf\u6211\u90a3\u82e6\u547d\u7684\u73ca\u745a\u3002\u201d\u6b63\u662f\uff1a\u8f9b\u9178\u513f\u5973\u6cea\uff0c\u6005\u89e6\u8001\u4eba\u60c5\u3002\u6b32\u77e5\u7389\u7f57\u5239\u6b64\u53bb\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206 \u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u516d\u56de\u3000\u5251\u95ef\u540d\u5c71\u3000\u7ea2\u989c\u89c5\u77e5\u5df1\u3000\u971e\u8f89\u5e7d\u8c37\u3000\u767d\u53d1\u6362\u9752\u4e1d \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377 \u7b2c\u4e8c\u5341\u516d\u56de \u5251\u95ef\u540d\u5c71\u3000\u7ea2\u989c\u89c5\u77e5\u5df1 \u971e\u8f89\u5e7d\u8c37\u3000\u767d\u53d1\u6362\u9752\u4e1d \u3000\u3000\u8fd9\u4e00\u65e5\u6b63\u662f\u6b66\u5f53\u6d3e\u524d\u4efb\u638c\u95e8\u7d2b\u9633\u9053\u957f\u7684\u4e94\u5468\u5e74\u796d\uff0c\u6b66\u5f53\u6d3e\u81ea\u7d2b\u9633\u9053\u957f\u6b7b\u5f8c\uff0c\u6e10\u5448\u8870\u843d\u4e4b \u8c61\uff0c\u9ec4\u53f6\u9053\u4eba\u672c\u5bc4\u5e0c\u671b\u65bc\u5353\u4e00\u822a\uff0c\u8c01\u77e5\u5343\u65b9\u767e\u8ba1\uff0c\u63a5\u5f97\u5353\u4e00\u822a\u56de\u5c71\u505a\u4e86\u638c\u95e8\u4e4b\u5f8c\uff0c\u4e00\u5e74\u591a \u6765\uff0c\u5353\u4e00\u822a\u90fd\u662f\u6d88\u6781\u9893\u5510\uff0c\u5982\u75f4\u4f3c\u50bb\uff0c\u52a0\u4e4b\u51e0\u4e2a\u5e08\u53d4\u6837\u6837\u5305\u529e\uff0c\u4e45\u800c\u4e45\u4e4b\uff0c\u4ed6\u5bf9\u672c\u6d3e\u5e94\u5174\u5e94 \u9769\u4e4b\u4e8b\uff0c\u4e5f\u4fbf\u6f20\u4e0d\u5173\u5fc3\uff0c\u4e00\u5207\u4e8b\u60c5\uff0c\u90fd\u8ba9\u5e08\u53d4\u51fa\u5934\uff0c\u5353\u4e00\u822a\u6302\u7740\u638c\u95e8\u4eba\u7684\u540d\u4e49\uff0c\u5b9e\u9645\u5374\u662f\u9ec4 \u53f6\u9053\u4eba\u62c5\u5f53\u3002\u6b66\u5f53\u7684\u56db\u4e2a\u957f\u8001\u548c\u56db\u5927\u5f1f\u5b50\u201c\u56db\u4e2a\u957f\u8001\u7684\u9996\u5f92\u201d\u89c1\u6b64\u666f\u8c61\uff0c\u90fd\u5fe7\u5fc3\u5fe1\u4ef2\u3002\u8fd9\u65e5 \u5fae\u660e\u65f6\u5206\uff0c\u9ec4\u53f6\u9053\u4eba\u4fbf\u51fa\u4e86\u9053\u89c2\uff0c\u5230\u7d2b\u9633\u9053\u957f\u7684\u575f\u524d\u5de1\u89c6\uff0c\u5ffd\u89c1\u767d\u77f3\u9053\u4eba\u5750\u5728\u575f\u5934\uff0c\u5fae\u5fae\u53f9 \u606f\u3002 \u3000\u3000\u9ec4\u53f6\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4f60\u4e5f\u6765\u4e86\uff1f\u201d\u767d\u77f3\u9053\uff1a\u201c\u5927\u5e08\u5144\u4e94\u5468\u5e74\u796d\uff0c\u6211\u7761\u4e0d\u7740\uff0c\u6240\u4ee5\u6765\u4e86\u3002\u60f3 \u5927\u5e08\u5144\u5728\u65e5\uff0c\u6211\u6d3e\u76db\u6781\u4e00\u65f6\uff0c\u6c5f\u6e56\u4e4b\u4e0a\uff0c\u8c01\u4e0d\u656c\u754f\u3002\u60f3\u4e0d\u5230\u4eca\u65e5\u5982\u6b64\uff0c\u8fde\u7389\u7f57\u5239\u8fd9\u6837\u4e00\u4e2a\u5996 \u5973\uff0c\u4e5f\u6562\u6b3a\u8d1f\u5230\u6211\u4eec\u6b66\u5f53\u6d3e\u5934\u4e0a\uff0c\u5927\u5e08\u5144\u82e5\u5730\u4e0b\u6709\u77e5\uff0c\u5b9a\u5f53\u75db\u54ed\u3002\u201d \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u7389\u7f57\u5239\u5174\u6211\u4eec\u4f5c\u5bf9\u5012\u662f\u5c0f\u4e8b\u3002\u6211\u4eec\u6b66\u5f53\u6d3e\u7ee7\u8d77\u65e0\u4eba\uff0c\u90a3 \u624d\u771f\u662f\u4ee4\u4eba\u5fc3\u5fe7\u54ea\uff1f\u201d\u8fd9\u4e24\u8001\u7f05\u6000\u65e7\u65e5\u5149\u8363\uff0c\u4e0d\u89c9\u550f\u5618\u592a\u606f\u3002 \u3000\u3000\u767d\u77f3\u9053\u4eba\u4ee5\u8896\u62c2\u62ed\u5893\u7891\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u5927\u5e08\u5144\u6700\u770b\u91cd\u4e00\u822a\uff0c\u60f3\u4e0d\u5230\u4ed6\u5982\u6b64\u9893\u5510\uff0c\u5b8c\u5168\u4e0d \u50cf\u4e2a\u638c\u95e8\u4eba\u7684\u6837\u5b50\u3002\u201d\u767d\u77f3\u9053\u4eba\u6ca1\u6709\u60f3\u5230\uff0c\u4ed6\u6837\u6837\u8981\u63d2\u624b\u5e72\u6d89\uff0c\u5353\u4e00\u822a\u53c8\u600e\u80fd\u505a\u5f97\u4e86\u4e2a\u201c\u50cf \u6837\u7684\u638c\u95e8\u201d\uff01 \u3000\u3000\u9ec4\u53f6\u95ee\u9053\uff1a\u201c\u4e00\u822a\u4ee5\u5f80\u548c\u4f60\u9887\u4e3a\u4eb2\u8fd1\uff0c\u4ed6\u6709\u548c\u4f60\u8c08\u8fc7\u5fc3\u4e8b\u9ebd\uff1f\u201d\u767d\u77f3\u6447\u6447\u9996\u9053\uff1a\u201c\u81ea\u660e \u6708\u5ce1\u5f52\u6765\u4e4b\u5f8c\uff0c\u4ed6\u603b\u907f\u5f00\u548c\u6211\u8c08\u5fc3\u3002\u201d \u3000\u3000\u5bc5\u53f6\u9053\uff1a\u201c\u4f60\u770b\u4ed6\u662f\u4e0d\u662f\u8fd8\u604b\u7740\u90a3\u4e2a\u5996\u5973\uff1f\u201d\u767d\u77f3\u9053\uff1a\u201c\u6211\u770b\u6bdb\u75c5\u5df4\u51fa\u5728\u8fd9\u513f\u3002\u54fc\uff0c \u54fc\uff0c\u90a3\u5996\u5973\u592a\u4e0d\u81ea\u91cf\uff0c\u5979\u60f3\u5ac1\u6211\u4eec\u6b63\u6d3e\u7684\u638c\u95e8\uff0c\u4eca\u751f\u5979\u53ef\u4f11\u60f3\uff01\u201d \u3000\u3000\u9ec4\u53f6\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u4e00\u822a\u82e5\u5bf9\u5979\u5ff5\u5ff5\u4e0d\u5fd8\uff0c\u65e0\u5fc3\u505a\u6211\u6d3e\u638c\u95e8\uff0c\u6b64\u4e8b\u4e5f\u7ec8\u975e\u4e86\u5c40\u3002\u201d \u3000\u3000\u767d\u77f3\u9053\uff1a\u201c\u4eca\u65e5\u662f\u5927\u5e08\u5144\u7684\u5fcc\u8fb0\uff0c\u4e0d\u5982\u7531\u4f60\u53ec\u96c6\u95e8\u4eba\u5c06\u5353\u4e00\u822a\u7684\u638c\u95e8\u5e9f\u4e86\u3002\u7136\u5f8c\u7ed9\u4ed6\u6311 \u4e00\u95e8\u5408\u9002\u7684\u4eb2\u4e8b\uff0c\u8ba9\u4ed6\u7cbe\u795e\u6062\u590d\u6b63\u5e38\u4e4b\u5f8c\uff0c\u624d\u7ed9\u4ed6\u7ee7\u4efb\u638c\u95e8\u3002\u201d \u3000\u3000\u9ec4\u53f6\u9053\uff1a\u201c\u4ed6\u7684\u638c\u95e8\u662f\u7d2b\u9633\u9053\u5144\u9057\u5631\u6307\u5b9a\u7684\uff0c\u5e9f\u4e86\u6050\u4e0d\u5927\u597d\u3002\u201d\u767d\u77f3\u9053\uff1a\u201c\u6211\u6d3e\u6025\u56fe\u632f \u8870\u53bb\u5f0a\uff0c\u8ba9\u4ed6\u5c38\u4f4d\u7d20\u9910\uff0c\u5c82\u975e\u66f4\u4e0d\u597d\u3002\u201d \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u6c89\u601d\u534a\u664c\uff0c\u5ffd\u9053\uff1a\u201c\u4e00\u822a\u8868\u9762\u867d\u662f\u9893\u5510\uff0c\u4f46\u6211\u770b\u4ed6\u6b66\u529f\u5374\u4f3c\u9887\u6709\u8fdb\u5883\uff0c\u4f60\u770b\u5f97 \u51fa\u6765\u9ebd\uff1f\u201d \u3000\u3000\u767d\u77f3\u6447\u5934\u9053\uff1a\u201c\u6211\u6ca1\u6709\u6ce8\u610f\u3002\u201d\u4ed6\u81ea\u5973\u513f\u5ac1\u4e86\u674e\u7533\u65f6\u5f8c\uff0c\u5bf9\u4e00\u822a\u9887\u6709\u82a5\u8482\uff0c\u4e0d\u4f3c\u4ee5\u524d\u90a3 \u6837\u5904\u5904\u5173\u5fc3\u3002\u5bf9\u4e00\u822a\u7684\u6b66\u529f\u66f4\u65e0\u8003\u5bdf\u3002 \u3000\u3000\u9ec4\u53f6\u9053\uff1a\u201c\u6211\u770b\u4ed6\u7684\u773c\u795e\u811a\u6b65\uff0c\u5185\u529f\u751a\u6709\u6839\u57fa\uff0c\u548c\u524d\u5927\u5927\u4e0d\u540c\u3002\u4e5f\u4e0d\u77e5\u4ed6\u4f55\u4ee5\u8fdb\u5883\u5982\u6b64 \u4e4b\u901f\u3002\u6240\u4ee5\u5e9f\u7acb\u638c\u95e8\u4e4b\u4e8b\uff0c\u8fd8\u662f\u4ece\u957f\u8ba1\u8bae\u5427\u3002\u7b2c\u4e8c\u4ee3\u95e8\u4eba\u4e2d\u4e5f\u6311\u4e0d\u51fa\u50cf\u4ed6\u90a3\u6837\u7684\u4eba\u624d\u3002\u201d \u3000\u3000\u4e24\u4eba\u6b63\u5728\u5546\u91cf\uff0c\u9ec4\u53f6\u9053\u4eba\u5076\u7136\u5411\u5c71\u4e0b\u4e00\u671b\uff0c\u5ffd\u7136\u53eb\u51fa\u58f0\u6765\uff01 \u3000\u3000\u767d\u77f3\u9053\u4eba\u968f\u7740\u5e08\u5144\u6240\u6307\u7684\u65b9\u5411\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u56e2\u767d\u5f71\uff0c\u75be\u98de\u800c\u6765\uff0c\u767d\u77f3\u53eb\u9053\uff1a\u201c\u6765\u8005\u4f55 \u4eba\uff1f\u201d\u5239\u90a3\u4e4b\u95f4\uff0c\u767d\u5f71\u5df2\u5230\u534a\u5c71\uff0c\u6765\u5f97\u592a\u75be\uff0c\u770b\u4e0d\u6e05\u9762\u8c8c\uff0c\u767d\u77f3\u9053\u4eba\u5fc3\u5ff5\u4e00\u52a8\uff0c\u62d4\u5251\u98de\u524d\uff0c \u4f46\u542c\u5f97\u4e00\u58f0\u7b11\u9053\uff1a\u201c\u767d\u77f3\u9053\u4eba\uff0c\u6211\u53c8\u4e0d\u662f\u627e\u4f60\uff0c\u4e0d\u6562\u6709\u52b3\u4f60\u6765\u8fce\u9a7e\u3002\u201d \u3000\u3000\u767d\u77f3\u9053\u4eba\u53c8\u60ca\u53c8\u6012\uff0c\u53eb\u9053\uff1a\u201c\u7389\u7f57\u5239\u4f60\u5c45\u7136\u6562\u5e26\u5251\u4e0a\u5c71\uff01\u201d\u957f\u5251\u4e00\u6296\uff0c\u4e00\u62db\u201c\u957f\u86c7\u4eba \u6d1e\u201d\uff0c\u75be\u523a\u8fc7\u53bb\u3002\u7389\u7f57\u5239\u53eb\u9053\uff1a\u201c\u4eca\u65e5\u6211\u4e0d\u60f3\u5174\u4f60\u52a8\u624b\uff0c\u4f60\u8ba9\u4e0d\u8ba9\u8def\uff1f\u201d\u767d\u77f3\u9053\u4eba\u54ac\u7259\u5207 \u9f7f\uff0c\u201c\u5237\uff0c\u5237\u201d\u53c8\u662f\u4e24\u5251\uff0c\u6b66\u5f53\u6d3e\u7684\u8fde\u73af\u593a\u547d\u5251\u6cd5\uff0c\u4e00\u62db\u7d27\u63a5\u4e00\u62db\uff0c\u5341\u5206\u51cc\u5389\uff0c\u7389\u7f57\u5239\u6012 \u9053\uff1a\u201c\u4f60\u771f\u4e2a\u4e0d\u77e5\u8fdb\u9000\u9ebd\uff1f\u201d\u98de\u8eab\u8dc3\u8d77\uff0c\u75be\u907f\u53c1\u62db\uff0c\u624b\u4e2d\u5251\u4e00\u4e2a\u76d8\u65cb\uff0c\u4f46\u89c1\u5251\u82b1\u9519\u843d\uff0c\u5f53\u5934 \u7f69\u4e0b\u3002\u731b\u5730\u91cc\uff0c\u659c\u523a\u4e00\u5251\u98de\u6765\uff0c\u53ea\u542c\u5f97\u53ee\u5f53\u4e24\u58f0\uff0c\u7389\u7f57\u5239\u7684\u5251\u76f4\u6c64\u51fa\u53bb\uff0c\u770b\u6e05\u695a\u65f6\uff0c\u6765\u8005\u539f \u6765\u662f\u9ec4\u53f6\u9053\u4eba\u3002 \u3000\u3000\u5bc5\u53f6\u9053\u4eba\u529f\u529b\u5728\u4f17\u5e08\u5f1f\u4e4b\u4e0a\uff0c\u4f46\u9002\u624d\u53cc\u5251\u76f8\u4ea4\uff0c\u8ba8\u4e0d\u4e86\u4e1d\u6beb\u4fbf\u5b9c\uff0c\u5fc3\u4e2d\u4e5f\u662f\u4e00\u9707\u3002\u7389\u7f57 \u5239\u559d\u9053\uff1a\u201c\u9ec4\u53f6\u9053\u4eba\uff0c\u4f60\u662f\u6b66\u5f53\u957f\u8001\u4e2d\u7684\u957f\u8001\uff0c\u4e5f\u4e0e\u767d\u77f3\u9053\u4eba\u4e00\u6837\u89c1\u8bc6\u9ebd\uff1f\u201d\u9ec4\u53f6\u9053\uff1a\u201c\u4f60 \u5148\u628a\u5251\u629b\u4e0b\uff0c\u6211\u6b66\u5f53\u5c71\u4e0a\u4e0d\u51c6\u5916\u4eba\u5e26\u5251\u524d\u6765\u3002\u201d\u7389\u7f57\u5239\u6012\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u51ed\u4f60\u4eec\u5c31\u6562\u6446\u8fd9\u4e2a\u67b6 \u5b50\uff1f\u201d\u5251\u5c16\u500f\u7684\u4e0a\u6311\uff0c\u9ec4\u53f6\u9053\u4eba\u6a2a\u5251\u4e00\u5c01\uff0c\u4e0d\u6599\u7389\u7f57\u5239\u5251\u62db\u602a\u7edd\uff0c\u4f3c\u4e0a\u53cd\u4e0b\uff0c\u5251\u950b\u4e00\u98a4\uff0c\u4e2d \u523a\u80f8\u53e3\uff0c\u4e0b\u5212\u819d\u76d6\uff0c\u9ec4\u53f6\u9053\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u8db3\u5c16\u4e00\u65cb\uff0c\u8eab\u5f62\u4e00\u8f6c\u4e4b\u95f4\uff0c\u5251\u5149\u6c64\u5411\u56db\u65b9\uff0c\u52a0 \u4e0a\u767d\u77f3\u9053\u4eba\u4ece\u65c1\u4fa7\u51fb\uff0c\u8fd9\u624d\u628a\u7389\u7f57\u5239\u7684\u62db\u6570\uff0c\u521a\u521a\u5316\u89e3\u3002 \u3000\u3000\u9ec4\u53f6\u5fc3\u9053\uff1a\u201c\u8fd9\u5996\u5973\u5251\u6cd5\u679c\u7136\u4e86\u5f97\uff0c\u602a\u4e0d\u5f97\u5979\u5982\u6b64\u7316\u72c2\u3002\u201d\u6697\u8fd0\u5185\u529b\uff0c\u6c89\u5251\u4e00\u5f15\uff0c\u5251\u62db \u751a\u7f13\uff0c\u4f46\u7389\u7f57\u5239\u5251\u5c16\u89e6\u5904\uff0c\u5374\u53cd\u53d7\u6f5c\u529b\u63a8\u5f00\u3002\u7389\u7f57\u5239\u559d\u58f0\uff1a\u201c\u597d\uff0c\u6b66\u5f53\u6d3e\u4e2d\u4f60\u7b97\u662f\u7b2c\u4e00\u9ad8\u624b \u4e86\uff0c\u6bd4\u4f60\u7684\u5e08\u5f1f\u5f3a\u5f97\u591a\uff01\u201d\u7a81\u7136\u52b2\u529b\u4e00\u677e\uff0c\u9ec4\u53f6\u4e00\u5251\u6420\u7a7a\uff0c\u4f46\u89c1\u7389\u7f57\u5239\u8eab\u5982\u4e00\u9875\u8584\u7eb8\uff0c\u8f7b\u98d8 \u98d8\u7684\u968f\u7740\u5251\u98ce\u76f4\u6643\u51fa\u53bb\uff0c\u9ec4\u53f6\u5185\u529b\u867d\u96c4\uff0c\u5374\u5948\u5979\u4e0d\u5f97\u3002\u9ec4\u53f6\u559d\u9053\uff1a\u201c\u4f60\u6765\u505a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u7389\u7f57\u5239\u8df3\u5f00\u4e00\u6b65\uff0c\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u4e0d\u8981\u6211\u629b\u5251\u4e86\u9ebd\uff1f\u6211\u4eca\u65e5\u6765\u89c1\u4f60\u4eec\u6b66\u5f53\u6d3e\u638c\u95e8\uff0c\u4f60\u4eec \u61c2\u4e0d\u61c2\u6b66\u6797\u89c4\u77e9\uff1f\u201d\u6309\u8bf4\u6709\u6b66\u6797\u9ad8\u624b\u6765\u62dc\u89c1\u672c\u6d3e\u638c\u95e8\uff0c\u90a3\u5c31\u4e0d\u8bba\u6765\u7684\u662f\u53cb\u662f\u654c\uff0c\u672c\u6d3e\u4e2d\u4eba\u90fd \u8be5\u5f15\u6765\u4eba\u5148\u89c1\u4e86\u638c\u95e8\u518d\u8bf4\u3002 \u3000\u3000\u53ef\u662f\u9ec4\u53f6\u3001\u767d\u77f3\u662f\u5353\u4e00\u822a\u7684\u5e08\u53d4\uff0c\u4e00\u5411\u53c8\u628a\u7389\u7f57\u5239\u5f53\u6210\u672c\u95e8\u516c\u654c\uff0c\u517c\u4e4b\u4ee5\u7384\u95e8\u6b63\u6d3e\u7684\u5251 \u5b66\u5927\u5b97\u5e08\u81ea\u547d\uff0c\u90a3\u80af\u548c\u5979\u8bb2\u4ec0\u9ebd\u201c\u6b66\u6797\u89c4\u77e9\u201d\uff0c\u767d\u77f3\u9996\u5148\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u5996\u5973\uff0c\u60f3\u89c1\u6211\u6d3e\u638c \u95e8\uff1f\u54fc\uff0c\u54fc\uff0c\u4f60\u4e3a\u4f55\u4e0d\u6984\u955c\u81ea\u7167\uff1f\u201d\u9ec4\u53f6\u4e5f\u9053\uff1a\u201c\u6211\u6b66\u5f53\u6d3e\u7684\u95e8\u4eba\uff0c\u7d20\u6765\u4e0d\u4ea4\u90aa\u9b54\u6b6a\u9053\uff0c\u4f60 \u5feb\u6eda\u4e0b\u5c71\u53bb\uff0c\u94d9\u4f60\u4e00\u6b7b\u3002\u201d\u7389\u7f57\u5239\u6012\u9053\uff1a\u201c\u54fc\uff0c\u6211\u8fd8\u672a\u66fe\u4e0e\u4f60\u4eec\u6b66\u5f53\u6d3e\u7b97\u5e10\uff0c\u4f60\u4eec\u5c45\u7136\u80e1\u8bf4 \u4e71\u9a82\uff01\u201d\u5b9d\u5251\u4e00\u6325\uff0c\u98d8\u5ffd\u4e0d\u5b9a\uff0c\u4f3c\u523a\u767d\u77f3\uff0c\u53c8\u4f3c\u5954\u5411\u9ec4\u53f6\uff0c\u767d\u77f3\u53eb\u9053\uff1a\u201c\u5e08\u5144\uff0c\u4eca\u65e5\u7edd\u4e0d\u80fd \u653e\u8d70\u8fd9\u5973\u9b54\u5934\u4e86\uff01\u201d\u9ec4\u53f6\u64ae\u5507\u4e00\u5578\uff0c\u53ec\u5524\u540c\u95e8\uff0c\u957f\u5251\u5212\u4e86\u4e00\u4e2a\u5706\u5f27\uff0c\u8981\u628a\u7389\u7f57\u5239\u7684\u5b9d\u5251\u5708 \u4f4f\u3002 \u3000\u3000\u7389\u7f57\u5239\u6321\u4e86\u51e0\u62db\uff0c\u9ec4\u53f6\u9053\u4eba\u53c8\u662f\u64ae\u5507\u957f\u5578\uff0c\u7389\u7f57\u5239\u5fc3\u9053\uff1a\u201c\u6211\u867d\u4e0d\u6015\u8fd9\u4e24\u4e2a\u725b\u9f3b\u5b50\u8001 \u9053\uff0c\u4f46\u7ed9\u4ed6\u4eec\u7f20\u7740\uff0c\u5374\u662f\u4e0d\u5999\u3002\u7b49\u4f1a\u513f\u4e00\u822a\u6765\u4e86\uff0c\u5c82\u4e0d\u662f\u53eb\u4ed6\u843d\u4e0d\u4e86\u53f0\u9636\uff1f\u201d\u9ec4\u53f6\u9053\u4eba\u5251\u5251 \u53d6\u52bf\uff0c\u4ed7\u7740\u5185\u529b\u6c89\u52b2\uff0c\u4ece\u4e0a\u65b9\u5288\u538b\u4e0b\u6765\u3002\u7389\u7f57\u5239\u8eab\u5f62\u4e00\u98d8\uff0c\u731b\u7136\u95f4\u6b3a\u8eab\u76f4\u8fdb\uff0c\u5251\u8d77\u5904\u201c\u7389\u5973 \u6295\u68ad\u201d\u201c\u94f6\u9488\u6697\u5ea6\u201d\u201c\u5f69\u7ebf\u659c\u98d8\u201d\uff0c\u53c1\u62db\u4f3c\u67d4\u5b9e\u521a\u7684\u5251\u6cd5\u63a5\u8fde\u53d1\u51fa\uff0c\u7740\u7740\u8feb\u5411\u767d\u77f3\u9053\u4eba\u3002\u767d \u77f3\u9053\u4eba\u88ab\u8feb\u5f97\u4fa7\u8eab\u95ea\u907f\u3002\u7389\u7f57\u5239\u4e00\u58f0\u957f\u7b11\uff0c\u8eab\u5f62\u8d77\u5904\uff0c\u75be\u5982\u95ea\u7535\uff0c\u5411\u7f3a\u53e3\u76f4\u51b2\u51fa\u53bb\uff0c\u970e\u5ffd\u95f2 \u4fbf\u8f6c\u8fc7\u4e86\u4e00\u4e2a\u5c71\u5773\u3002 \u3000\u3000\u9ec4\u53f6\u9053\uff1a\u201c\u8fd9\u5973\u9b54\u5934\u8eab\u6cd5\u597d\u5feb\uff0c\u54b1\u4eec\u4e0d\u5fc5\u8ffd\u5979\u3002\u770b\u5979\u53bb\u5904\uff0c\u662f\u60f3\u5954\u5411\u6211\u4eec\u5c71\u4e0a\u9053\u89c2\uff0c\u54b1 \u4eec\u53ec\u96c6\u95e8\u4eba\u5f1f\u5b50\uff0c\u5e03\u6210\u5730\u7f51\u5929\u7f57\uff0c\u5979\u672c\u9886\u518d\u9ad8\uff0c\u4e5f\u9003\u4e0d\u4e86\u3002\u201d\u767d\u77f3\u9053\uff1a\u201c\u5e08\u5144\u8bf4\u7684\u662f\u3002\u4eca\u65e5 \u82e5\u53eb\u5979\u9003\u4e86\uff0c\u54b1\u4eec\u6b66\u5f53\u6d3e\u5c31\u518d\u4e5f\u4e0d\u80fd\u9886\u8896\u6b66\u6797\u4e86\u3002\u201d\u4ed6\u5954\u5411\u5c71\u4e0a\uff0c\u4e00\u8def\u547c\u5524\u3002 \u3000\u3000\u6b66\u5f53\u5c71\u5cf0\u5ce6\u91cd\u53e0\uff0c\u4e00\u5cf0\u9ad8\u4f3c\u4e00\u5cf0\uff0c\u5728\u7d2b\u9633\u9053\u957f\u7684\u5893\u5730\u867d\u7136\u53ef\u4ee5\u9065\u89c1\u5c71\u4e0a\u9053\u89c2\uff0c\u8ddd\u79bb\u5176\u5b9e \u9887\u8fdc\u3002\u7389\u7f57\u5239\u767b\u4e86\u4e24\u5ea7\u5c71\u5cf0\uff0c\u542c\u5f97\u89c2\u4e2d\u949f\u7f44\u9f50\u545c\uff0c\u5c71\u4e0a\u5df2\u6709\u4eba\u5954\u4e0b\u3002\u8fd9\u65f6\u8981\u518d\u4e0a\u4e00\u4e2a\u5c71\u5cf0\uff0c \u4fbf\u662f\u5927\u6bbf\u6240\u5728\u3002\u7389\u7f57\u5239\u5fc3\u9053\uff1a\u201c\u82e6\u4e5f\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u600e\u80fd\u548c\u5353\u4e00\u822a\u5355\u72ec\u6664\u8c08\uff1f\u201d \u3000\u3000\u5c71\u5773\u5904\u4eba\u5f71\u4e00\u95ea\uff0c\u7389\u7f57\u5239\u4e00\u770b\uff0c\u5374\u662f\u4e00\u7537\u4e00\u5973\uff0c\u4fd7\u5bb6\u6253\u626e\u3002\u770b\u6e05\u695a\u65f6\uff0c\u539f\u6765\u662f\u767d\u77f3\u9053\u4eba \u7684\u5973\u513f\uff0c\u5973\u5a7f\u2014\u2014\u4f55\u843c\u534e\u548c\u674e\u7533\u65f6\u3002\u8fd9\u4e24\u4eba\u88ab\u767d\u77f3\u9053\u4eba\u5e26\u4e0a\u6b66\u5f53\u5c71\u91cd\u5b66\u6b66\u5f53\u5251\u6cd5\uff0c\u5c0f\u4e24\u53e3\u5b50 \u5929\u5929\u65e9\u4e0a\u90fd\u5728\u5c71\u8170\u98ce\u666f\u4e4b\u5730\u4e60\u6b66\u7ec3\u5251\u3002 \u3000\u3000\u7389\u7f57\u5239\u4e00\u89c1\uff0c\u75be\u8df3\u4e0a\u524d\uff0c\u4f55\u843c\u534e\u521a\u8f6c\u8fc7\u8eab\uff0c\u80a9\u5934\u88ab\u5979\u62cd\u4e86\u4e00\u4e0b\uff0c\u5947\u9053\uff1a\u201c\u54a6\uff0c\u662f\u4f60\uff01\u6211 \u542c\u5f97\u9ec4\u53f6\u5e08\u4f2f\u5578\u58f0\u793a\u8b66\uff0c\u89c2\u4e2d\u53c8\u662f\u949f\u7f44\u957f\u545c\uff0c\u53ea\u5f53\u662f\u4ec0\u9ebd\u5f3a\u654c\u6765\u4e86\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u4eec\u5c0f\u4e24\u53e3\u5b50\u597d\u5feb\u6d3b\uff01\u5582\uff0c\u5353\u4e00\u822a\u5728\u90a3\u513f\uff0c\u6211\u8981\u627e\u4ed6\uff01\u201d \u3000\u3000\u4f55\u843c\u534e\u4ee5\u524d\u51e0\u4e4e\u7ed9\u5979\u7236\u4eb2\u8feb\u7740\u5ac1\u5353\u4e00\u822a\uff0c\u597d\u5728\u5f8c\u6765\u77e5\u9053\u5353\u4e00\u822a\u60c5\u6709\u6240\u953a\uff0c\u53c8\u5f97\u59d1\u59d1\u8bf4 \u9879\uff0c\u8fd9\u624d\u4e0d\u81f4\u94f8\u6210\u6028\u5076\u3002\u6240\u4ee5\u5728\u4f55\u843c\u534e\u5fc3\u4e2d\uff0c\u5bf9\u7389\u7f57\u5239\u867d\u65e0\u7279\u6b8a\u597d\u611f\uff0c\u5374\u4e5f\u65e0\u6076\u611f\u3002\u95fb\u8a00\u5fc3 \u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u5728\u60c5\u573a\u4e4b\u4e0a\uff0c\u6211\u662f\u8fc7\u6765\u4eba\u4e86\u3002\u4e0d\u80fd\u548c\u81ea\u5df1\u610f\u4e2d\u4eba\u7ed3\u5a5a\uff0c\u90a3\u662f\u6bd5\u751f\u9057\u61be\u3002\u6211 \u7684\u7236\u4eb2\u597d\u6ca1\u6765\u7531\uff0c\u5f3a\u8981\u7981\u6b62\u638c\u95e8\u5e08\u5144\u548c\u5979\u6765\u5f80\u3002\u201d\u5fc3\u4e2d\u8d77\u4e86\u540c\u60c5\u4e4b\u5ff5\uff0c\u9053\uff1a\u201c\u4e00\u822a\u8fd9\u5341\u591a\u5929 \u6765\uff0c\u6bcf\u5929\u7edd\u65e9\u90fd\u5230\u201c\u77f3\u83b2\u53f0\u201d\u7ec3\u5251\u3002\u201d\u7389\u7f57\u5239\u6025\u9053\uff1a\u201c\u77f3\u83b2\u53f0\u5728\u90a3\u513f\uff1f\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u5de6\u9762 \u6709\u4e00\u4e2a\u5f62\u4f3c\u83b2\u82b1\u7684\u5c71\u5cf0\uff0c\u6709\u4e00\u6761\u7011\u5e03\u4ece\u5c71\u5cf0\u4e0a\u5012\u6cfb\u4e0b\u6765\uff0c\u4f60\u89c1\u4e86\u90a3\u6761\u7011\u5e03\uff0c\u5c31\u5411\u5de6\u659c\u65b9\u8d70\uff0c \u5728\u7011\u5e03\u65c1\u8fb9\uff0c\u6709\u4e00\u5757\u5927\u77f3\uff0c\u90a3\u5c31\u662f\u77f3\u83b2\u53f0\u4e86 \u3000\u3000\u7389\u7f57\u5239\u9053\u58f0\uff1a\u201c\u591a\u8c22\uff01\u201d\u4f9d\u7740\u4f55\u843c\u534e\u6240\u6307\u7684\u65b9\u5411\u4fbf\u8dd1\uff0c\u8fd9\u65f6\u6668\u5149\u5fae\u66e6\uff0c\u6653\u65e5\u65b9\u9732\uff0c\u6797\u4e2d \u5bbf\u9e1f\u88ab\u4eba\u58f0\u60ca\u8d77\uff0c\u7eb7\u7eb7\u98de\u51fa\u3002\u7389\u7f57\u5239\u5fc3\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u8981\u5728\u7ed9\u89c2\u4e2d\u4f17\u9053\u58eb\u53d1\u73b0\u4e4b\u524d\u89c1\u7740\u5353\u4e00 \u822a\u3002\u201d\u80cc\u5f8c\u4f20\u6765\u4e86\u767d\u77f3\u9053\u4eba\u53eb\u5524\u5973\u513f\u7684\u58f0\u97f3\uff0c\u63a5\u7740\u5230\u5904\u662f\u4eba\u58f0\u547c\u5524\u3002\u7389\u7f57\u5239\u4ed7\u7740\u7edd\u9876\u8f7b\u529f\uff0c \u6025\u6025\u6500\u767b\u4e0a\u90a3\u5f62\u5982\u83b2\u82b1\u7684\u5c71\u5cf0\uff0c\u679c\u7136\u89c1\u7740\u4e00\u6761\u7011\u5e03\u3002 \u3000\u3000\u7011\u5e03\u98de\u73e0\u6e85\u7389\uff0c\u548c\u5d16\u77f3\u51b2\u51fb\uff0c\u53d1\u51fa\u8033\u9e23\u4e4b\u58f0\u3002\u7389\u7f57\u5239\u65e0\u5fc3\u89c2\u8d4f\uff0c\u987a\u7740\u7011\u5e03\uff0c\u5411\u5de6\u659c\u65b9\u76f4 \u8d70\uff0c\u7011\u5e03\u58f0\u4e2d\uff0c\u604d\u60da\u542c\u5230\u541f\u54e6\u4e4b\u58f0\uff0c\u7389\u7f57\u5239\u5fc3\u9053\uff1a\u201c\u8fd9\u4e00\u5b9a\u662f\u90a3\u4e2a\u9178\u4e01\u4e86\u3002\u201d\u811a\u6b65\u4e00\u7d27\uff0c\u7247 \u523b\u5230\u4e86\u4e0a\u9762\u3002 \u3000\u3000\u518d\u8bf4\u5353\u4e00\u822a\u81ea\u88ab\u767d\u77f3\u9053\u4eba\u903c\u8feb\u56de\u5c71\u4e4b\u5f8c\uff0c\u5fc3\u4e2d\u90c1\u90c1\uff0c\u9547\u65e5\u65e0\u6b22\uff0c\u5e78\u7d2b\u9633\u9053\u957f\u7559\u6709\u5251\u8c31\u7ed9 \u4ed6\uff0c\u957f\u65e5\u65e0\u804a\uff0c\u552f\u6709\u7a77\u7814\u5251\u8c31\u4ee5\u89e3\u5c91\u5bc2\u3002\u5728\u5251\u8c31\u4e2d\u4ed6\u53d1\u73b0\u6709\u51e0\u62db\u602a\u62db\uff0c\u6b66\u5f53\u5251\u6cd5\u90fd\u662f\u4e00\u5957\u5957 \u7684\uff0c\u72ec\u6709\u8fd9\u51e0\u62db\u602a\u62db\uff0c\u9996\u5c3e\u5e76\u4e0d\u8fde\u8d2f\uff0c\u65e0\u6cd5\u5e94\u7528\u3002\u5353\u4e00\u822a\u53bb\u95ee\u5e08\u53d4\uff0c\u624d\u77e5\u8fd9\u51e0\u62db\u662f\u8fbe\u6469\u5251\u6cd5 \u4e2d\u7684\u62db\u6570\uff0c\u8fbe\u6469\u5251\u6cd5\u5171\u4e00\u767e\u96f6\u516b\u5f0f\uff0c\u539f\u662f\u6b66\u5f53\u6d3e\u7684\u9547\u5c71\u5251\u6cd5\uff0c\u53ef\u662f\u5728\u5143\u4ee3\u4e2d\u53f6\uff0c\u201c\u8fbe\u6469\u4e00\u767e \u96f6\u516b\u5f0f\u201d\u7684\u771f\u672c\u5ffd\u7136\u4e0d\u89c1\uff0c\u65bc\u662f\u4ee3\u4ee3\u4f20\u4e0b\u9057\u8a00\uff0c\u8981\u5f8c\u4e16\u5f1f\u5b50\u5bfb\u89c5\u6b64\u4e66\u3002\u540c\u65f6\u8fd9\u4e00\u767e\u96f6\u516b\u5f0f\u7684 \u771f\u672c\u867d\u7136\u5931\u8e2a\uff0c\u4f46\u56e0\u6545\u8001\u76f8\u4f20\uff0c\u8fd8\u5927\u7565\u8bb0\u5f97\u51e0\u4e2a\u62db\u5f0f\u3002\u7d2b\u9633\u957f\u8001\u5c06\u5b83\u5f55\u4eba\u5251\u8c31\u4e4b\u4e2d\uff0c\u4ee5\u524d\u4e5f \u66fe\u5bf9\u5353\u4e00\u822a\u8bf4\u8fc7\uff0c\u53ea\u662f\u5353\u4e00\u822a\u4e0d\u77e5\u8fd9\u51e0\u62db\u4fbf\u662f\u8fbe\u6469\u5251\u5f0f\u7f62\u4e86\u3002 \u3000\u3000\u95ee\u660e\u4e86\u5e08\u53d4\u4e4b\u5f8c\uff0c\u5353\u4e00\u822a\u5fc3\u60f3\uff1a\u5e08\u53d4\u4eec\u90fd\u8bf4\u8fd9\u51e0\u62db\u602a\u62db\u96f6\u788e\u6563\u6f2b\uff0c\u5e76\u4e0d\u8fde\u8d2f\uff0c\u53ea\u80fd\u7559\u7ed9 \u5f8c\u4e16\u5f1f\u5b50\u505a\u6837\u672c\uff0c\u4ee5\u5907\u5c06\u6765\u5bfb\u89c5\u771f\u672c\u4e4b\u65f6\uff0c\u53ef\u4ee5\u4f5c\u4e3a\u5370\u8bc1\uff0c\u5bf9\u65bc\u5b9e\u7528\uff0c\u5374\u662f\u6beb\u65e0\u5e2e\u52a9\u3002\u4f46\u8fd9 \u5957\u5251\u6cd5\u65e2\u7136\u662f\u6b66\u5b66\u4e2d\u4e0d\u4f20\u4e4b\u79d8\uff0c\u4e00\u62db\u4e00\u5f0f\uff0c\u90fd\u5fc5\u5b9a\u6709\u5b83\u7684\u9053\u7406\uff0c\u5373\u4e0d\u80fd\u8fde\u8d2f\u5e94\u7528\uff0c\u4e5f\u5f53\u6709\u5b83 \u7684\u5a01\u529b\uff0c\u6211\u5c82\u80fd\u56ff\u65bc\u5148\u4eba\u4e4b\u89c1\uff0c\u7f6e\u4e4b\u4e0d\u7406\u3002\u56e0\u6b64\u5353\u4e00\u822a\u4e0d\u7406\u5b83\u80fd\u5426\u5b9e\u7528\uff0c\u4e00\u5473\u82e6\u5fc3\u7814\u8ba8\uff0c\u6bcf \u65e9\u90fd\u5230\u77f3\u83b2\u53f0\u7ec3\u5251\u3002\u90a3\u8fbe\u6469\u5251\u6cd5\u4ee5\u9759\u5236\u52a8\uff0c\u4ee5\u6c14\u8fd0\u529b\uff0c\u5bf9\u5185\u529f\u4fee\u7ec3\u5927\u6709\u5e2e\u52a9\uff0c\u5353\u4e00\u822a\u867d\u7136\u4e0d \u660e\u5176\u4e2d\u5999\u8482\uff0c\u4f46\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u4e2d\u5927\u6709\u8fdb\u76ca\u3002 \u3000\u3000\u8fd9\u4e00\u65e5\u65e9\u6668\uff0c\u5353\u4e00\u822a\u7ec3\u5251\u4e4b\u5f8c\uff0c\u975e\u4f46\u4e0d\u5026\uff0c\u4e14\u89c9\u6c14\u8840\u8212\u7545\uff0c\u7cbe\u795e\u9971\u6ee1\u3002\u4ed6\u6628\u665a\u56e0\u601d\u5ff5\u7389 \u7f57\u5239\uff0c\u534a\u591c\u5931\u7720\uff0c\u672c\u4ee5\u4e3a\u4eca\u65e5\u5b9a\u65e0\u7cbe\u795e\uff0c\u8c01\u77e5\u7ec3\u5251\u4e4b\u5f8c\uff0c\uff0c\u7cbe\u795e\u53cd\u800c\u8f6c\u597d\u3002\u5fc3\u4e2d\u5927\u559c\uff0c\u77e5\u9053 \u8fd9\u5fc5\u662f\u8fbe\u6469\u5251\u6cd5\u7684\u5999\u7528\uff0c\u65bc\u662f\u4e13\u5fc3\u4e00\u5fd7\uff0c\u51a5\u601d\u9ed8\u7d22\u5176\u4e2d\u5999\u7406\uff0c\u8fde\u5e08\u53d4\u7684\u5578\u58f0\uff0c\u5c71\u9876\u9053\u89c2\u7684\u949f \u7f44\u58f0\uff0c\u4e5f\u542c\u800c\u4e0d\u95fb\u4e86\u3002 \u3000\u3000\u6b63\u5728\u51fa\u795e\uff0c\u5ffd\u5730\u6709\u4eba\u4f38\u624b\u5728\u4ed6\u989d\u5934\u4e00\u6233\uff0c\u5353\u4e00\u822a\u500f\u7136\u8df3\u8d77\uff0c\u60ca\u559c\u83ab\u540d\uff0c\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u5728 \u4ed6\u9762\u524d\u7684\u7adf\u662f\u671d\u601d\u591c\u60f3\u7684\u7389\u7f57\u5239\uff01\u5446\u5446\u7684\u4e00\u65ec\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u7389\u7f57\u5239\u9053\u58f0\uff1a\u201c\u4f60\u597d\u2014\u2014\u201d\u58f0\u97f3\u54fd\u54bd\uff0c\u8bf4\u4e0d\u4e0b\u53bb\u3002\u4e24\u4eba\u90fd\u6709\u4e07\u8bed\u5343\u8a00\uff0c\u5374\u4e0d\u77e5\u4ece\u4f55\u8bf4 \u8d77\u3002\u90a3\u77f3\u83b2\u53f0\u7855\u5927\u65e0\u670b\uff0c\u4e00\u5757\u5927\u77f3\uff0c\u660e\u4eae\u5982\u955c\uff0c\u53ef\u5bb9\u767e\u6570\u5341\u4eba\uff0c\u7389\u7f57\u5239\u5076\u4e00\u4f4e\u5934\uff0c\u5ffd\u89c1\u77f3\u53f0 \u4e0a\u6709\u6570\u884c\u5c0f\u5b57\uff0c\u60f3\u662f\u5353\u4e00\u822a\u7528\u5251\u523b\u51fa\u6765\u7684\u3002\u7389\u7f57\u5239\u9ed8\u5ff5\u4e0b\u53bb\uff0c\u539f\u6765\u662f\u4e00\u9996\u201c\u53cc\u8c03\u5fc6\u6c5f\u5357\u201d\u7684 \u5c0f\u4ee4\uff0c\u8bcd\u9053\uff1a \u3000\u3000\u201c\u79cb\u591c\u9759\uff0c\u72ec\u81ea\u5bf9\u6b8b\u706f\uff0c\u557c\u7b11\u975e\u975e\u8c01\u8bc6\u6211\uff0c\u5750\u884c\u68a6\u68a6\u5c3d\u7f18\u541b\uff0c\u4f55\u6240\u6170\u6d88\u6c89\u3002 \u3000\u3000\u98ce\u5377\u96e8\uff0c\u96e8\u590d\u5377\u4fac\u5fc3\uff0c\u5fc3\u4f3c\u6b32\u968f\u98ce\u96e8\u53bb\uff0c\u832b\u832b\u5927\u6d77\u4efb\u6d6e\u6c89\uff0c\u65e0\u7231\u4ea6\u65e0\u618e\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u6ef4\u4e0b\u6cea\u6765\uff0c\u5e7d\u5e7d\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u6628\u665a\u5199\u7684\u5417\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u9053\uff1a\u201c\u6628\u665a\u5c71\u4e2d\u542c\u96e8\uff0c\u7761\u4e0d\u7740\u89c9\uff0c\u80e1\u4e71\u5199\u4e86\u8fd9\u9ebd\u51e0\u53e5\uff0c\u53eb\u4f60\u89c1\u7b11\u3002\u201d\u7389\u7f57\u5239\u53f9 \u9053\uff1a\u201c\u8fd9\u662f\u4f55\u82e6\uff01\u4f46\u6559\u4f60\u4e0b\u5f97\u51b3\u5fc3\uff0c\u53c8\u4f55\u81f3\u6d88\u6c89\u5982\u6b64\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u662f\u6211\u9519 \u4e86\uff01\u201d\u7389\u7f57\u5239\u8f7b\u63a0\u4e91\u9b13\uff0c\u773c\u775b\u4e00\u4eae\uff0c\u4e00\u4e1d\u7b11\u610f\uff0c\u73b0\u65bc\u7709\u68a2\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fc7\u53bb\u7684\u4e0d\u8981\u63d0\u4e86\u2014 \u2014\u201d\u5353\u4e00\u822a\u62a2\u7740\u8bf4\u9053\uff1a\u201c\u6211\u5df2\u6253\u5b9a\u4e3b\u610f\uff0c\u4eca\u5f8c\u613f\u968f\u59d0\u59d0\u6d6a\u8ff9\u5929\u6daf\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d \u9053\u89c2\u949f\u58f0\uff0c\u53c8\u968f\u98ce\u4f20\u5230\uff0c\u5353\u4e00\u822a\u4fa7\u8033\u4e00\u542c\uff0c\u7a7a\u8c37\u4f20\u58f0\uff0c\u5916\u9762\u8fd8\u4f3c\u4e4e\u6709\u4eba\u5728\u53eb\u5524\u4ed6\u7684\u540d\u5b57\u3002\u7389 \u7f57\u5239\u9053\uff1a\u201c\u6211\u5df2\u89c1\u8fc7\u4f60\u7684\u4e24\u4f4d\u5e08\u53d4\u4e86\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u90a3\u4e24\u4f4d\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u9ec4\u53f6\u9053\u4eba\u548c \u767d\u77f3\u9053\u4eba\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u7709\u5934\u4e00\u76b1\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u548c\u4ed6\u4eec\u8bf4\u4e9b\u4ec0\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u6211\u8bf4\u8981\u89c1\u4f60\uff0c\u4ed6\u4eec\u4e0d \u8bb8\u3002\u4f46\u54b1\u4eec\u5230\u5e95\u662f\u89c1\u7740\u4e86\uff1f\u201d\u5728\u6b3e\u6b3e\u6df1\u8c08\u4e4b\u4e2d\uff0c\u4e24\u4eba\u7684\u624b\u4e0d\u77e5\u4e0d\u89c9\u7d27\u63e1\u8d77\u6765\u3002\u5353\u4e00\u822a\u4f46\u89c9\u7389 \u7f57\u5239\u624b\u5fc3\u706b\u70ed\uff0c\u53eb\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u8fd9\u4e00\u5e74\u6765\u4f60\u4e5f\u82e6\u900f\u4e86\u3002\u6211\uff0c\u6211\u2026\u2026\u201d\u7389\u7f57\u5239\u7eed\u9053\uff1a\u201c\u4f60\u7684\u4e24 \u4e2a\u5e08\u53d4\u628a\u6211\u5f53\u505a\u654c\u4eba\uff1a\uff1a\uff0e\u201d\u5353\u4e00\u822a\u82e6\u7b11\u9053\uff1a\u201c\u4ed6\u4eec\u5982\u6b64\uff0c\u6211\u4e5f\u6ca1\u6cd5\u3002\u201d\u9053\u89c2\u949f\u58f0\u53c8\u8d77\uff0c\u8c37 \u5916\u4eba\u58f0\u66f4\u8fd1\u3002\u5353\u4e00\u822a\u970d\u7136\u60ca\u8d77\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u6211\u7684\u5e08\u53d4\u53ec\u96c6\u540c\u95e8\uff0c\u8981\u6765\u5bf9\u4ed8\u4f60\u4e86\uff1f\u201d \u3000\u3000\u7389\u7f57\u5239\u773c\u775b\u6e9c\u5706\u6676\u4eae\uff0c\u5b9a\u795e\u7684\u770b\u7740\u5353\u4e00\u822a\uff0c\u4e00\u5b57\u4e00\u53e5\u7684\u95ee\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u5c06\u5982\u4f55\uff1f\u662f\u52a9\u4f60 \u7684\u5e08\u53d4\u62ff\u6211\uff0c\u8fd8\u662f\u2014\u2014\u201d\u4ece\u6307\u5c16\u7684\u98a4\u6296\u4e2d\uff0c\u7389\u7f57\u5239\u611f\u5230\u5353\u4e00\u822a\u5185\u5fc3\u6b63\u5728\u4ea4\u6218\uff0c\u4e0d\u89c9\u4e00\u9635\u98a4 \u6817\uff0c\u8bf4\u4e0d\u4e0b\u53bb\uff0c\u53ea\u542c\u5f97\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u7edd\u4e0d\u4e0e\u4f60\u4e3a\u654c\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4ec5\u5982\u6b64\u5417\uff1f\u201d\u5353\u4e00\u822a \u9053\uff1a\u201c\u6211\u51b3\u610f\u4e0d\u505a\u8fd9\u635e\u4ec0\u5b50\u7684\u638c\u95e8\u4e86\u3002\u201d\u7389\u7f57\u5239\u4ecd\u9053\uff1a\u201c\u4ec5\u5982\u6b64\u5417\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4eca\u65e5\u662f \u6211\u7684\u5e08\u7236\u4e94\u5468\u5e74\u5fcc\u65e5\u3002\u7b49\u4f1a\u5e08\u53d4\u5230\u6765\uff0c\u6211\u4fbf\u7980\u544a\u4ed6\u4eec\uff0c\u5f85\u796d\u8fc7\u5e08\u7236\u4e4b\u5f8c\uff0c\u6211\u4fbf\u548c\u4f60\u4e00\u540c\u8d70\u4e0b \u6b64\u5c71\u3002\u6b64\u5f8c\u5730\u8001\u5929\u8352\uff0c\u54b1\u4eec\u518d\u4e5f\u4e0d\u5206\u79bb\u4e86\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u677e\u4e86\u53e3\u6c14\uff0c\u8138\u6655\u7ea2\u6f6e\uff0c\u534a\u664c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u6211\u5c31\u4ec0\u9ebd\u4e5f\u4e0d\u6015\u4e86\u3002\u201d\u5353\u4e00\u822a \u9053\uff1a\u201c\u4e0d\u8fc7\u2014\u2014\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4e0d\u8fc7\u4ec0\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4e0d\u8fc7\u6211\u51e0\u4e2a\u5e08\u53d4\u7684\u813e\u6c14\u4f60\u4e5f\u77e5 \u9053\u3002\u7b49\u4f1a\u4f60\u4e0d\u8981\u548c\u4ed6\u4eec\u52a8\u786c\u7684\u3002\u4f60\u770b\u5728\u6211\u7684\u9762\u4e0a\uff0c\u59d4\u5c48\u4e00\u4e9b\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u8981\u6211\u5411\u4ed6\u4eec \u6c42\u60c5\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u55ef\u3002\u6c42\u60c5\u7684\u4e8b\u4e0d\u5fc5\u4f60\u8bf4\uff0c\u5f85\u6211\u6765\u8bf4\u3002\u82e5\u7136\u4ed6\u4eec\u9a82\u4f60\uff0c\u4f60\u4e0d\u8981\u9a6c\u4e0a\u9876\u56de \u53bb\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u597d\uff0c\u8981\u4f60\u662f\u771f\u5fc3\u5b9e\u610f\uff0c\u6211\u4fbf\u53d7\u4e9b\u59d4\u5c48\uff0c\u53c8\u6709\u4f55\u59a8\uff1f\u201d\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u6b66\u5f53\u6d3e\u7684 \u95e8\u4eba\u5df2\u6709\u4e00\u7fa4\u8fdb\u4e86\u5c71\u8c37\uff0c\u5faa\u7740\u7011\u5e03\u6500\u767b\u800c\u4e0a\uff0c\u9661\u7136\u89c1\u7740\u5353\u4e00\u822a\u548c\u7389\u7f57\u5239\u5e76\u7acb\u77f3\u53f0\uff0c\u65e0\u4e0d\u9a87 \u5f02\u3002 \u3000\u3000\u5353\u4e00\u822a\u5df2\u4e0b\u4e86\u51b3\u5fc3\uff0c\u9762\u8272\u4e0d\u53d8\u3002\u548c\u7389\u7f57\u5239\u7684\u624b\u63e1\u5f97\u66f4\u7d27\uff0c\u7389\u7f57\u5239\u633a\u80f8\u6602\u9996\uff0c\u671b\u4e5f\u4e0d\u671b\u90a3 \u7fa4\u9053\u58eb\u3002\u8fd9\u65f6\uff0c\u5979\u53ea\u89c9\u559c\u60a6\u5145\u585e\u5fc3\u80f8\uff0c\u4efb\u5b83\u5916\u754c\u55a7\u56a3\uff0c\u5979\u53ea\u89c9\u8fd9\u5929\u5730\u4e4b\u95f4\uff0c\u6709\u5353\u4e00\u822a\u548c\u5979\u800c \u5df2\uff01 \u3000\u3000\u767d\u77f3\u3001\u7ea2\u4e91\u4e8c\u4eba\u8d70\u5728\u524d\u5934\uff0c\u6c89\u7740\u9762\u8272\uff0c\u6012\u6781\u6c14\u6781\uff0c\u5230\u4e86\u77f3\u83b2\u53f0\u4e0b\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4e00\u822a\uff0c \u4e00\u822a\uff0c \u3000\u3000\u5353\u4e00\u822a\u5e94\u9053\uff1a\u201c\u5e08\u53d4\u3002\u201d\u767d\u77f3\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u8eab\u4e3a\u638c\u95e8\uff0c\u89c2\u4e2d\u9e23\u949f\u62a5\u8b66\uff0c\u4f60\u542c\u4e0d\u89c1 \u5417\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6765\u4e86\u4ec0\u9ebd\u654c\u4eba\u5440\uff01\u201d\u767d\u77f3\u6012\u9053\uff1a\u201c\u4f60\u7f9e\u4e5f\u4e0d\u7f9e\uff01\u4f60\u8fd9\u662f\u660e\u77e5\u6545\u95ee\u3002\u8fd9\u5996 \u5973\u5c31\u662f\u672c\u95e8\u516c\u654c\uff0c\u4f60\u5374\u548c\u5979\u6df7\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5979\u5e76\u4e0d\u662f\u6211\u4eec\u7684\u654c\u4eba\u3002\u201d\u767d\u77f3\u9053\uff1a\u201c\u80e1\u8bf4\uff0c \u5979\u5c61\u6b21\u4e0e\u6211\u4eec\u6b66\u5f53\u6d3e\u4f5c\u5bf9\uff0c\u600e\u9ebd\u4e0d\u662f\u654c\u4eba\u4e86\u4f60\u662f\u638c\u95e8\uff0c\u5f53\u7740\u4e00\u4f17\u540c\u95e8\uff0c\u4f60\u597d\u610f\u601d\u9ebd\uff1f\u600f\u628a\u5979 \u62ff\u4e0b\u6765\u3002\u201d\u4e00\u822a\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u6211\u6709\u8bdd\u8bf4\uff01\u201d\u767d\u77f3\u9053\uff1a\u201c\u4f60\u8fd8\u8bf4\u4ec0\u9ebd\uff1f\u4f60\u8981\u4e3a\u8fd9\u5996\u5973\u80cc\u53db\u672c\u95e8 \u5417\uff1f\u4f55\u53bb\u4f55\u4ece\uff0c\u4f60\u9a6c\u4e0a\u6289\u62e9\uff0c\u6ca1\u6709\u4ec0\u9ebd\u53ef\u8bf4\u7684\u4e86\uff01\u201d \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u7f13\u7f13\u800c\u51fa\uff0c\u9053\uff1a\u201c\u5e08\u5f1f\uff0c\u4e14\u8ba9\u4ed6\u8bf4\u3002\u4e00\u822a\uff0c\u4f60\u60f3\u6e05\u695a\u4e9b\uff0c\u4f60\u8bf4\u5427\uff0c\u4f60\u610f\u6b32\u5982 \u4f55\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5f1f\u5b50\u5fb7\u8584\u80fd\u9c9c\uff0c\u8eab\u4efb\u638c\u95e8\uff0c\u6127\u65e0\u5efa\u6811\u3002\u6c42\u5e08\u53d4\u4eec\u53e6\u9009\u8d24\u80fd\uff0c\u5f1f\u5b50\u8981\u544a\u9000 \u4e86\u3002\u201d\u767d\u77f3\u6012\u9053\uff1a\u201c\u4f60\u505a\u4e0d\u505a\u638c\u95e8\u662f\u53e6\u4e00\u56de\u4e8b\uff0c\u8fd9\u5996\u5973\u662f\u672c\u95e8\u516c\u654c\uff0c\u4f60\u548c\u5979\u6df7\uff0c\u5927\u662f\u4e0d \u8be5\u3002\u201d\u5353\u4e00\u822a\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u4eba\u5404\u6709\u5fd7\uff0c\u6211\u613f\u4ee5\u4eca\u5f8c\u9980\u751f\uff0c\u52e4\u4fee\u5251\u6cd5\u3002\u82e5\u4ed6\u65e5\u80fd\u6709\u5bf8\u8fdb\uff0c\u4e5f\u7b97 \u5f97\u662f\u62a5\u7b54\u6069\u5e08\u3002\u201d\u767d\u77f3\u6012\u9053\uff1a\u201c\u4f60\u8981\u548c\u5979\u4e00\u540c\u7ec3\u5251\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u55ef\uff0c\u6211\u603b\u5f97\u6709\u4eba\u6307\u70b9 \u5440\uff01\u201d\u767d\u77f3\u6012\u4e0d\u53ef\u6b47\uff0c\u9a82\u9053\uff1a\u201c\u6b66\u5f53\u5251\u6cd5\u662f\u5929\u4e0b\u6b66\u5b66\u6b63\u5b97\uff0c\u4f60\u8fd8\u8981\u5b66\u4ec0\u9ebd\u90aa\u9b54\u6b6a\u9053\uff1f\u201d\u9ec4\u53f6 \u9053\u4eba\u4e5f\u5f88\u4e0d\u9ad8\u5174\uff0c\u559d\u9053\uff1a\u201c\u4e00\u822a\uff0c\u4f60\u542c\u4e0d\u542c\u5e08\u53d4\u7684\u8bdd\uff0c\u5feb\u653e\u624b\uff1f\u201d\u5353\u4e00\u822a\u7ed9\u4ed6\u4e00\u559d\uff0c\u624b\u6307\u677e \u5f00\uff0c\u4f46\u4ecd\u9053\uff1a\u201c\u5f1f\u5b50\u5b66\u5251\u4e4b\u5fc3\uff0c\u5df2\u4e0d\u53ef\u6539\u3002\u201d\u9ec4\u53f6\u767d\u77f3\u7ea2\u4e91\u9752\u56db\u4e2a\u957f\u8001\u90fd\u8dc3\u4e0a\u5e73\u53f0\u3002\u767d\u77f3\u9053 \u4eba\u51b7\u7b11\u9053\uff1a\u201c\u5b66\u5251\uff0c\u5251\uff0c\u5251\uff01\u6b66\u5f53\u5c71\u5148\u5c31\u4e0d\u8bb8\u5916\u4eba\u643a\u5251\u4e0a\u6765\uff1f\u201d\u9ec4\u53f6\u9053\uff1a\u201c\u4e00\u822a\uff0c\u4f60\u771f\u7684\u53bb \u610f\u5df2\u51b3\u4e86\u5417\uff1f\u201d\u5353\u4e00\u822a\u8f7b\u8f7b\u70b9\u4e86\u70b9\u5934\u3002\u9ec4\u53f6\u5ffd\u9053\uff1a\u201c\u4f60\u7ad9\u8fc7\u4e00\u8fb9\uff0c\u5728\u672a\u662d\u544a\u4f60\u5e08\u7236\u4ee5\u524d\uff0c\u4f60 \u8fd8\u662f\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u5f1f\u5b50\u3002\u201d\u5353\u4e00\u822a\u8d70\u8fc7\u4e00\u8fb9\u3002\u9ec4\u53f6\u9762\u5411\u7389\u7f57\u5239\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u5929\u4e0b\u591a\u5c11\u7537\u4eba\uff0c \u4f60\u4e3a\u4f55\u504f\u8981\u7f20\u4ed6\uff1f\u201d \u3000\u3000\u7389\u7f57\u5239\u6012\u706b\u5df2\u8d77\uff0c\u82e5\u5728\u5e73\u65f6\uff0c\u5b9a\u8981\u4e00\u5251\u628a\u9ec4\u53f6\u6420\u4e2a\u900f\u660e\u7a9f\u7abf\uff0c\u6b64\u9645\u5f3a\u6291\u6012\u706b\uff0c\u51b7\u7b11\u7b54 \u9053\uff1a\u201c\u5929\u4e0b\u591a\u5c11\u6b63\u7ecf\u4e8b\u60c5\uff0c\u4f60\u4e0d\u53bb\u7ba1\uff0c\u4e3a\u4f55\u4f60\u504f\u8981\u7406\u8fd9\u95f2\u4e8b\uff1f\u201d\u9ec4\u53f6\u9053\u4eba\u628a\u624b\u4e00\u62db\uff0c\u865e\u65b0\u57ce \u7b49\u56db\u5927\u5f1f\u5b50\uff0c\u548c\u5176\u4ed6\u5404\u638c\u7ecf\u62a4\u6cd5\u7684\u8f83\u6709\u5730\u4f4d\u7684\u5f1f\u5b50\u90fd\u8df3\u4e86\u51fa\u6765\u3002 \u3000\u3000\u9ec4\u53f6\u53c8\u95ee\u9053\uff1a\u201c\u7389\u7f57\u5239\uff0c\u4f60\u8fd9\u6b21\u662f\u6709\u5fc3\u524d\u6765\u6363\u4e71\uff0c\u8981\u5c06\u5353\u4e00\u822a\u5e26\u8d70\u9ebd\uff1f\u201d\u7389\u7f57\u5239\u9053\uff1a \u201c\u53c8\u4e0d\u662f\u6211\u8feb\u4ed6\u8d70\u7684\u3002\u201d\u9ec4\u53f6\u9053\uff1a\u201c\u4f60\u8981\u8d70\u4e5f\u672a\u5c1d\u4e0d\u53ef\uff0c\u5148\u628a\u5251\u653e\u4e0b\u6765\uff01\u201d\u7389\u7f57\u5239\u77a5\u4e86\u5353\u4e00 \u822c\u4e00\u773c\uff0c\u5353\u4e00\u822a\u4ee5\u4e3a\u5e08\u53d4\u8981\u7389\u7f57\u5239\u5f03\u5251\u4e4b\u5f8c\uff0c\u5c31\u53ef\u8ba9\u4ed6\u4eec\u540c\u8d70\u3002\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u5c71\u4e0a\u7684\u89c4 \u77e9\u3002\u201d\u7389\u7f57\u5239\u54c8\u54c8\u4e00\u7b11\uff0c\u5c06\u5251\u629b\u843d\u77f3\u53f0\uff0c\u9053\uff1a\u201c\u6211\u5c31\u4f9d\u4f60\u4eec\u7684\u81ed\u89c4\u77e9\uff0c\u73b0\u5728\u53ef\u4ee5\u8ba9\u6211\u548c\u4ed6\u540c \u8d70\u4e86\u5427\uff1f\u201d \u3000\u3000\u865e\u65b0\u57ce\u4fef\u8170\u62ac\u8d77\u5b9d\u5251\uff0c\u5e73\u4e3e\u5934\u4e0a\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5916\u6d3e\u5996\u90aa\uff0c\u5df2\u670d\u5a01\u89e3\u5251\uff0c\u8bf7\u957f\u8001\u53d1 \u843d\uff01\u201d\u865e\u65b0\u57ce\u5728\u7b2c\u4e8c\u4ee3\u5f1f\u5b50\u4e2d\u8f88\u4efd\u6700\u9ad8\uff0c\u73b0\u4efb\u62a4\u6cd5\u5f1f\u5b50\uff0c\u5bf9\u6b66\u5f53\u6d3e\u7684\u4f20\u7edf\u4e00\u529b\u7ef4\u62a4\u3002\u7adf\u7136\u628a \u7389\u7f57\u5239\u5f53\u6210\u88ab\u6253\u8d25\u7684\u654c\u4eba\uff0c\u8981\u4e3e\u884c\u732e\u5251\u4eea\u5f0f\u3002 \u3000\u3000\u7389\u7f57\u5239\u51e0\u4e4e\u6c14\u70b8\u5fc3\u80ba\uff0c\u53ea\u542c\u5f97\u9ec4\u53f6\u9053\u4eba\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u732e\u5251\uff0c\u4ee5\u5f80\u4e0d\u548e\uff0c\u4f60\u5feb\u6eda\u4e0b\u5c71 \u53bb\uff01\u5353\u4e00\u822a\u662f\u6211\u6d3e\u638c\u95e8\uff0c\u5c82\u662f\u4f60\u8fd9\u5996\u5973\u6240\u80fd\u5339\u914d\uff0c\u4f60\u8d81\u65e9\u6b7b\u4e86\u8fd9\u6761\u5fc3\u5427\uff01\u201d\u7389\u7f57\u5239\u53cc\u773c\u4e00 \u7ffb\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6211\u504f\u4e0d\u8d70\uff01\u201d\u767d\u77f3\u7ea2\u4e91\u4e8c\u4eba\u90fd\u66fe\u88ab\u7389\u7f57\u5239\u6298\u8fb1\uff0c\u53cc\u53cc\u8dc3\u51fa\uff0c\u559d\u9053\uff1a\u201c\u4f60\u8d70\u4e0d \u8d70\uff1f\u771f\u672a\u66fe\u89c1\u8fc7\u4f60\u8fd9\u9ebd\u4e0b\u8d31\u7684\u5973\u4eba\uff0c\u5c45\u7136\u8dd1\u5230\u6211\u4eec\u6b66\u5f53\u5c71\u6765\u8981\u4e08\u592b\u3002\u201d\u7389\u7f57\u5239\u84e6\u5730\u4e00\u58f0\u51b7 \u7b11\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u62cd\u7684\u4e00\u4e0b\uff0c\u767d\u77f3\u9053\u4eba\u6371\u4e86\u4e00\u8bb0\u8033\u5149\u3002\u6025\u5fd9\u4f38\u624b\u62d4\u5251\uff0c\u53ea\u542c\u5f97\u865e\u65b0\u57ce\u5927\u53eb\u4e00 \u58f0\uff0c\u539f\u6765\u5c31\u5728\u4ed6\u8e0f\u6b63\u65b9\u6b65\uff0c\u76ee\u4e0d\u659c\u89c6\uff0c\u8981\u5c06\u5251\u732e\u7ed9\u9ec4\u53f6\u9053\u4eba\u4e4b\u9645\uff0c\u84e6\u7136\u7ed9\u7389\u7f57\u5239\u5c06\u5251\u62a2\u53bb\uff0c \u987a\u624b\u4e5f\u6253\u4e86\u4ed6\u4e00\u8bb0\u8033\u5149\u3002 \u3000\u3000\u767d\u77f3\u7ea2\u4e91\u6012\u53eb\u9053\uff1a\u201c\u53cd\u4e86\uff0c\u53cd\u4e86\uff01\u201d\u53cc\u5251\u9f50\u51fa\uff0c\u75be\u523a\u7389\u7f57\u5239\u547d\u95e8\u8981\u7a74\uff0c\u7389\u7f57\u5239\u4e00\u62db\u201c\u5012 \u5377\u661f\u6cb3\u201d\uff0c\u5b9d\u5251\u631f\u98ce\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u4ece\u4e24\u4eba\u5934\u9876\u63a0\u8fc7\u3002\u8033\u8fb9\u542c\u5f97\u9ec4\u53f6\u9053\u4eba\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u770b\u4f4f\u638c \u95e8\u5e08\u5144\uff0c\u4ed6\u4eca\u65e5\u6709\u75c5\uff0c\u795e\u667a\u4e0d\u6e05\uff0c\u53d7\u90aa\u9b54\u6b6a\u9053\u6240\u60d1\uff0c\u4e0d\u53ef\u8ba9\u4ed6\u4e71\u8d70\u3002\u201d\u5353\u4e00\u822a\u5728\u79ef\u5a01\u4e4b\u4e0b\uff0c \u867d\u662f\u6124\u6068\u586b\u80f8\uff0c\u5374\u4e0d\u6562\u53d1\u4f5c\u3002 \u3000\u3000\u7389\u7f57\u5239\u8bb0\u6302\u7740\u5353\u4e00\u822a\uff0c\u5077\u773c\u4e00\u77a5\uff0c\u89c1\u4ed6\u9762\u8272\u94c1\u9752\uff0c\u5750\u5728\u77f3\u4e0a\u4e0d\u52a8\u3002\u767d\u77f3\u3001\u7ea2\u4e91\u53cc\u5251\u9f50 \u5c55\uff0c\u5251\u5251\u6307\u5411\u8981\u5bb3\u3002\u7389\u7f57\u5239\u9887\u4e3a\u5931\u671b\uff0c\u5fc3\u60f3\uff1a\u201c\u4e00\u822a\u5440\uff0c\u4f60\u65e2\u7136\u8bf4\u5f97\u5982\u6b64\u575a\u51b3\uff0c\u4e3a\u4f55\u6b64\u9645\u5374 \u4e0d\u51fa\u4e00\u8a00\uff1f\u201d\u9ad8\u624b\u6bd4\u5251\uff0c\u90a3\u5bb9\u5206\u5fc3\uff0c\u767d\u77f3\u9053\u4eba\u4e00\u4e2a\u201c\u76d8\u819d\u62d7\u6b65\u201d\uff0c\u957f\u5251\u5237\u7684\u4e00\u6307\uff0c\u4e00\u7f15\u9752 \u5149\uff0c\u70b9\u5230\u54bd\u5589\uff0c\u7389\u7f57\u5239\u51e0\u4e4e\u4e2d\u5251\uff0c\u5fc3\u4e2d\u5927\u6012\uff0c\u4fa7\u8eab\u4e00\u95ea\uff0c\u5b9d\u5251\u8fc5\u5982\u7535\u63a3\uff0c\u626c\u7a7a\u4e00\u5212\uff0c\u56de\u524a\u767d \u77f3\u624b\u8155\uff0c\u7ea2\u4e91\u9053\u4eba\u4e00\u5251\u51fb\u51fa\uff0c\u4e0e\u767d\u77f3\u8054\u5251\uff0c\u594b\u529b\u6321\u5f00\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u7389\u7f57\u5239\u5728\u77ac\u606f\u4e4b \u95f4\uff0c\u8fde\u8fdb\u53c1\u62db\uff0c\u9976\u662f\u767d\u77f3\u7ea2\u4e91\u53cc\u5251\u8054\u9632\uff0c\u4e5f\u88ab\u8feb\u5f97\u624b\u5fd9\u811a\u4e71\uff0c\u7389\u7f57\u5239\u4e00\u5251\u5feb\u4f3c\u4e00\u5251\uff0c\u5251\u98ce\u6c64 \u8d77\uff0c\u8863\u8882\u98d8\u626c\uff0c\u767d\u77f3\u7ea2\u4e91\u62da\u529b\u62b5\u6321\uff0c\u4f46\u89c9\u51b7\u6c14\u68ee\u68ee\uff0c\u5251\u82b1\u8000\u773c\uff01 \u3000\u3000\u7389\u7f57\u5239\u6740\u5f97\u6027\u8d77\uff0c\u9ad8\u58f0\u9a82\u9053\uff1a\u201c\u767d\u77f3\u8d3c\u9053\uff0c\u4f60\u5e26\u9886\u5b98\u519b\u8df5\u8e0f\u6211\u660e\u6708\u5ce1\u7684\u5c71\u5be8\uff0c\u6211\u591a\u5c11\u59d0 \u59b9\u5728\u90a3\u6b21\u9635\u4ea1\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u6211\u672c\u60f3\u9976\u4f60\uff0c\u4f60\u5374\u8fd8\u8981\u901e\u5f3a\uff0c\u4eca\u65e5\u4e0d\u7ed9\u4f60\u7559\u70b9\u8bb0\u53f7\uff0c\u6211\u4e5f\u6789\u4e3a\u7389 \u7f57\u5239\u4e86\uff01\u201d\u5251\u62db\u4e00\u53d8\uff0c\u987f\u65f6\u94f6\u5149\u904d\u4f53\uff0c\u7d2b\u7535\u98de\u7a7a\uff0c\u7740\u7740\u8fdb\u653b\uff0c\u62db\u62db\u72e0\u8fa3\uff01 \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u89e6\u76ee\u60ca\u5fc3\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5973\u9b54\u5934\u51fa\u624b\u51f6\u8fa3\uff0c\u770b\u5979\u8bf4\u5f97\u5230\u505a\u5f97\u5230\uff0c\u83ab\u53eb\u5979\u771f\u7684\u5c06\u767d \u77f3\u5e08\u5f1f\u4f24\u4e86\uff0c\u5728\u4f17\u4eba\u9762\u524d\uff0c\u53ef\u4e0d\u597d\u770b\u3002\u201d\u53eb\u9752\u9053\u4eba\u4e0a\u524d\u52a9\u9635\uff0c\u4ed6\u81ea\u5df1\u5219\u4ecd\u8981\u7aef\u7740\u8eab\u5206\uff0c\u4e0d\u613f \u5f53\u7740\u4e00\u4f17\u95e8\u4eba\u5f1f\u5b50\uff0c\u5408\u6b66\u5f53\u56db\u5927\u957f\u8001\u5168\u529b\uff0c\u53bb\u56f4\u653b\u4e00\u4e2a\u5973\u4eba\u3002 \u3000\u3000\u9752\u9053\u4eba\u5251\u6cd5\u751a\u7cbe\uff0c\u5251\u8bc0\u4e00\u9886\uff0c\u8d70\u659c\u8fb9\u6025\u4e0a\uff0c\u7389\u7f57\u5243\u5927\u7b11\u9053\uff1a\u201c\u597d\u5440\uff01\u53c8\u4e00\u4e2a\u6b66\u5f53\u957f\u8001\u6765 \u4e86\uff01\u4f60\u4eec\u81ea\u547d\u4e3a\u5929\u4e0b\u7b2c\u4e00\u7684\u5251\u6cd5\uff0c\u539f\u6765\u662f\u4ee5\u591a\u4e3a\u80dc\u7684\u5417\uff1f\u201d\u767d\u77f3\u7ea2\u4e91\u9752\u90fd\u4e0d\u51fa\u58f0\uff0c\u53c1\u67c4\u5251\u6025 \u523a\u6025\u524a\uff0c\u4e92\u76f8\u547c\u5e94\uff0c\u5c06\u7389\u7f57\u5239\u56f4\u5728\u6838\u5fc3\uff0c\u6b64\u53bb\u5f7c\u6765\uff0c\u8fde\u756a\u51b2\u51fb\uff0c\u7389\u7f57\u5239\u5251\u62db\u867d\u7136\u5feb\u6377\uff0c\u5230\u5e95 \u8fd8\u8981\u6362\u62db\u7684\u529f\u592b\uff0c\u529b\u654c\u53c1\u4eba\uff0c\u6e10\u611f\u5403\u529b\u3002 \u3000\u3000\u767d\u77f3\u9053\u4eba\u538b\u529b\u4e00\u677e\uff0c\u8fd9\u624d\u7eb5\u58f0\u56de\u9a82\uff1a\u201c\u6b66\u5f53\u7684\u5251\u6cd5\u5982\u4f55\uff1f\u54fc\uff0c\u54fc\uff0c\u770b\u662f\u4f60\u4f24\u5f97\u4e86\u6211\uff0c\u8fd8 \u662f\u6211\u4f24\u5f97\u4e86\u4f60\uff0c\u770b\u5251\uff01\u8d1d\u5251\uff01\u201d\u5237\u5237\u4e24\u5251\uff0c\u6b3a\u8eab\u76f4\u523a\u3002\u4e0d\u6599\u7389\u7f57\u5239\u53c8\u662f\u4e00\u58f0\u957f\u7b11\u65a5\u9053\uff1a\u201c\u4e95 \u5e95\u4e4b\u86d9\uff0c\u5c82\u77e5\u6d77\u6cb3\u4e4b\u5927\uff0c\u53eb\u4f60\u4eec\u5f00\u5f00\u773c\u754c\uff01\u201d\u5251\u6cd5\u53c8\u53d8\uff0c\u4e00\u67c4\u5251\u72b9\u5982\u795e\u9f99\u620f\u6c34\uff0c\u98de\u8679\u76d8\u7a7a\uff0c \u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u8eab\u5f62\u75be\u8f6c\uff0c\u531d\u5730\u94f6\u5149\uff0c\u987f\u65f6\u56db\u9762\u516b\u65b9\uff0c\u90fd\u662f\u7389\u7f57\u5239\u7684\u5f71\u5b50\u3002 \u3000\u3000\u539f\u6765\u7389\u7f57\u5239\u81ea\u4e0e\u7ea2\u82b1\u9b3c\u6bcd\u7ecf\u4e86\u4e24\u573a\u5927\u6218\u4e4b\u5f8c\uff0c\u5438\u6536\u4e86\u6559\u8bad\uff0c\u5251\u6cd5\u66f4\u7cbe\uff0c\u5979\u77e5\u9053\u4ee5\u4e00\u654c \u53c1\uff0c\u7eb5\u4e0d\u843d\u8d25\uff0c\u4e5f\u96be\u53d6\u80dc\u3002\u5fc3\u60f3\uff1a\u4ee5\u4ed6\u4eec\u53c1\u4eba\u4e4b\u529b\uff0c\u5927\u7ea6\u548c\u4e00\u4e2a\u7ea2\u82b1\u9b3c\u6bcd\u76f8\u5f53\u3002\u6211\u7684\u8f7b\u529f\u4e5f \u8fdc\u51fa\u4ed6\u4eec\u4e4b\u4e0a\uff0c\u5927\u53ef\u7528\u6597\u7ea2\u82b1\u9b3c\u6bcd\u7684\u65b9\u6cd5\u6765\u6740\u8d25\u4ed6\u4eec\u3002\u56e0\u6b64\u907f\u5b9e\u51fb\u865a\uff0c\u4ed7\u7740\u7edd\u5999\u7684\u8eab\u6cd5\uff0c\u5728 \u53c1\u5251\u4ea4\u51fb\u7f1d\u4e2d\uff0c\u94bb\u6765\u94bb\u53bb\uff0c\u4e00\u51fa\u624b\u4fbf\u662f\u8fa3\u62db\uff0c\u53eb\u53c1\u4eba\u773c\u82b1\u64a9\u4e71\uff0c\u5404\u4eba\u90fd\u8981\u5e94\u4ed8\u5077\u88ad\uff0c\u6e10\u6e10\u4e0d \u80fd\u914d\u5408\uff0c\u867d\u7136\u662f\u53c1\u5251\u8054\u653b\uff0c\u5b9e\u9645\u5374\u662f\u5404\u81ea\u4f5c\u6218\u3002 \u3000\u3000\u53c8\u6597\u4e86\u4e94\u4e03\u5341\u62db\uff0c\u53c1\u4eba\u5251\u6cd5\u6e10\u4e71\u3002\u5353\u4e00\u822a\u53eb\u9053\uff1a\u201c\u51a4\u5bb6\u5b9c\u89e3\u4e0d\u5b9c\u7ed3\uff0c\u53c8\u4e0d\u662f\u4ec0\u9ebd\u5927\u4e0d\u4e86 \u7684\u51a4\u4ec7\uff0c\u7f62\u6218\u4e86\u5427\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u6b66\u5f53\u56db\u8001\u548c\u7389\u7f57\u5239\u90fd\u4e0d\u6ee1\u610f\u3002\u56db\u8001\u5fc3\u60f3\uff1a\u5353\u4e00\u822a\u7adf\u7136\u5e2e\u5916 \u4eba\u8bf4\u9879\uff0c\u80f3\u818a\u5916\u5f2f\uff01\u7389\u7f57\u5239\u5fc3\u60f3\uff1a\u5230\u6211\u5360\u4e0a\u98ce\u65f6\u4f60\u624d\u53eb\u6211\u4f11\u6218\uff0c\u867d\u9053\u6211\u8981\u5e73\u767d\u53d7\u4ed6\u4eec\u51cc\u8fb1\u3002 \u5728\u6b64\u7d27\u8981\u5173\u5934\uff0c\u4f60\u4e0d\u75db\u5207\u9648\u8a00\uff0c\u8868\u660e\u5fc3\u8ff9\uff0c\u5374\u6765\u5982\u6b64\u529d\u67b6\u3002\u4e24\u8fb9\u90fd\u6012\uff0c\u6597\u5f97\u66f4\u70c8\u3002\u9ec4\u53f6\u9053\u4eba \u8d70\u5230\u5353\u4e00\u822a\u9762\u524d\uff0c\u6c89\u91cd\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u5173\u7cfb\u6b66\u5f53\u8363\u8fb1\u3002\u4e8b\u5df2\u81f3\u6b64\uff0c\u4f60\u82e5\u7136\u518d\u604b\u79c1\u60c5\uff0c\u66ff \u5979\u8bf4\u9879\uff0c\u90a3\u5c31\u4e0d\u5355\u662f\u672c\u6d3e\u53db\u5f92\uff0c\u800c\u4e14\u4e5f\u5fc5\u4e3a\u5929\u4e0b\u6b66\u6797\u6240\u4e0d\u9f7f\uff01\u4f60\u53c8\u4e0d\u662f\u666e\u901a\u95e8\u4eba\uff0c\u4f60\u5e94\u77e5\u4f60 \u662f\u638c\u95e8\u5f1f\u5b50\uff01\u4e3a\u672c\u6d3e\u8363\u8fb1\u800c\u6218\uff0c\u662f\u638c\u95e8\u4eba\u7684\u5929\u804c\uff0c\u7eb5\u7c89\u8eab\u788e\u9aa8\uff0c\u4e5f\u5f53\u4e0d\u8f9e\uff0c\u4f60\u77e5\u9053\u5417\uff1f\u201d\u5353 \u4e00\u822a\u4f24\u900f\u4e86\u5fc3\uff0c\u54ed\u51fa\u58f0\u9053\uff1a\u201c\uff0e\u5979\u4e00\u4e2a\u5b64\u5355\u5973\u5b50\uff0c\u5c82\u80fd\u6218\u80dc\u6211\u6d3e\uff1f\u5e08\u53d4\uff0c\u4f60\u4e0d\u8981\u8feb\u6211\u548c\u5979\u4f5c \u5bf9\uff01\u201d\u9ec4\u53f6\u9053\u4eba\u9762\u8272\u767d\u91cc\u6cdb\u9752\uff0c\u53cc\u77b3\u55b7\u706b\uff0c\u65a5\u9053\uff1a\u201c\u6211\u8ba9\u4f60\u591a\u60f3\u4e00\u4f1a\uff0c\u4f60\u662f\u8bfb\u4e66\u660e\u7406\u4e4b\u4eba\uff0c \u6211\u4e0d\u613f\u89c1\u4f60\u6ca6\u4e3a\u88ab\u4eba\u553e\u9a82\u7684\u53db\u5f92\uff01\u201d\u53cc\u773c\u5706\u7741\uff0c\u626b\u4e86\u5353\u4e00\u822a\u4e00\u773c\uff0c\u53c8\u518d\u6ce8\u89c6\u6597\u573a\u3002\u53ea\u89c1\u7389\u7f57 \u5239\u5251\u6cd5\u795e\u5999\u5f02\u5e38\uff0c\u5df2\u628a\u53c1\u4eba\u6740\u5f97\u9996\u5c3e\u4e0d\u80fd\u517c\u987e\uff01\u66f4\u96be\u582a\u7684\u662f\u7389\u7f57\u5239\u8fb9\u6253\u8fb9\u7b11\uff0c\u597d\u50cf\u5168\u4e0d\u628a\u6b66 \u5f53\u6d3e\u653e\u5728\u773c\u5185\uff01 \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u6124\u7136\u8bf4\u9053\uff1a\u201c\u597d\u72e0\u7684\u5973\u9b54\u5934\uff0c\u4f60\u4ea4\u7684\u597d\u670b\u53cb\uff01\u575d\u7136\u8981\u628a\u6211\u6b66\u5f53\u6d3e\u8df5\u5728\u811a\u5e95\uff01\u638c \u95e8\u4e0d\u51fa\uff0c\u6211\u867d\u5e74\u8fc8\uff0c\u7c89\u8eab\u788e\u9aa8\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u5979\u5728\u6b64\u901e\u51f6\u3002\u201d\u6c14\u547c\u547c\u7684\u62d4\u51fa\u5b9d\u5251\uff0c\u7eb5\u5165\u573a\u5fc3\uff0c\u5353 \u4e00\u822a\u75db\u54ed\u5931\u58f0\uff0c\u56f4\u5728\u4ed6\u8eab\u8fb9\u7684\u5e08\u5144\u5f1f\u65e0\u4e00\u4eba\u76f8\u529d\u3002 \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u8eab\u4e3a\u56db\u8001\u4e4b\u957f\uff0c\u529f\u529b\u975e\u6bd4\u5bfb\u5e38\uff0c\u53ea\u89c1\u4ed6\u5251\u5149\u970d\u970d\u5c55\u5f00\uff0c\u9690\u9690\u5e26\u6709\u98ce\u96f7\u4e4b\u58f0\uff0c\u4e00 \u62bd\u4e00\u538b\uff0c\u7389\u7f57\u5239\u7684\u5251\u52bf\u987f\u7136\u53d7\u963b\uff0c\u767d\u77f3\u7b49\u53c1\u4eba\u677e\u4e86\u53e3\u6c14\uff0c\u53c8\u6025\u653b\u8fc7\u6765\u3002\u7389\u7f57\u5239\u72c2\u7b11\u9053\uff1a \u201c\u54c8\uff0c\u54c8\uff0c\u6b66\u5f53\u56db\u8001\u5168\u90fd\u6765\u4e86\uff01\u6211\u4eca\u65e5\u5c3d\u4f1a\u6b66\u5f53\u9ad8\u624b\uff0c\u771f\u662f\u4f55\u5e78\u5982\u4e4b\uff01\u201d\u9ec4\u53f6\u9053\u4eba\u542c\u5728\u5fc3 \u91cc\uff0c\u53c8\u7f9e\u53c8\u6012\uff0c\u559d\u9053\uff1a\u201c\u5996\u5973\u4f11\u5f97\u7316\u72c2\uff0c\u770b\u5251\uff01\u201d\u4e00\u62db\u201c\u98ce\u96f7\u4ea4\u51fb\u201d\uff0c\u8fd0\u8db3\u5185\u529b\uff0c\u76f4\u538b\u4e0b \u53bb\u3002 \u3000\u3000\u7389\u7f57\u5239\u53cd\u81c2\u4e00\u5251\uff0c\u53ea\u89c9\u4e00\u80a1\u6f5c\u529b\u76f4\u8feb\u8fc7\u6765\uff0c\u7389\u7f57\u5239\u8eab\u5f62\u5feb\u6781\uff0c\u968f\u7740\u5251\u98ce\uff0c\u8eab\u5982\u67f3\u7d6e\uff0c\u76f4 \u98d8\u51fa\u53bb\uff0c\u5251\u8d77\u5904\uff0c\u4e00\u62db\u201c\u731b\u9e21\u5544\u6817\u201d\u6025\u88ad\u767d\u77f3\u9053\u4eba\uff0c\u5251\u5230\u4e2d\u9014\uff0c\u731b\u53c8\u53d8\u4e3a\u201c\u795e\u9a79\u5c55\u8db3\u201d\uff0c\u5ffd \u523a\u7ea2\u4e91\u811a\u8ddf\uff0c\u7ea2\u4e91\u957f\u5251\u4e0b\u622a\uff0c\u7389\u7f57\u5239\u5251\u628a\u4e00\u98a4\uff0c\u90a3\u67c4\u5251\u9661\u7136\u4e00\u6307\uff0c\u5374\u53c8\u53d8\u4e3a\u201c\u91d1\u9e4f\u7ffc\u201d\uff0c\u4e00 \u5251\u523a\u5230\u9752\u9053\u4eba\u8170\u80c1\u7684\u201c\u7ae0\u95e8\u7a74\u201d\u3002\u5728\u8fd9\u7535\u5149\u6d41\u706b\u4e4b\u95f4\uff0c\u7389\u7f57\u5239\u5df2\u904d\u88ad\u53c1\u540d\u9ad8\u624b\uff0c\u9ec4\u53f6\u9053\u4eba\u5927 \u5927\u5403\u60ca\u3002\u6025\u628a\u5251\u5149\u4f38\u5c55\uff0c\u62a4\u7740\u53c1\u540d\u5e08\u5f1f\uff0c\u7528\u4e00\u4e2a\u201c\u9ecf\u5b57\u8bc0\u201d\uff0c\u7d27\u7d27\u76ef\u7740\u7389\u7f57\u5239\u3002\u8fd9\u201c\u9ecf\u5b57 \u8bc0\u201d\u975e\u662f\u5185\u5bb6\u529f\u592b\u5df2\u5230\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u4e0d\u80fd\u8fd0\u7528\u81ea\u5982\u3002\u62f3\u7ecf\u6240\u8c13\uff1a\u201c\u820d\u5df2\u4ece\u4eba\u201d\uff0c\u201c\u968f\u66f2\u5c31 \u4f38\u201d\uff0c\u201c\u4e0d\u6297\u4e0d\u9876\u201d\uff0c\u201c\u52b2\u6025\u5219\u6025\u5e94\u201d\uff0c\u201c\u52b2\u7f13\u5219\u7f13\u968f\u201d\uff0c\u5982\u78c1\u5438\u94c1\uff0c\u7d27\u9ecf\u4e0d\u5f03\uff0c\u4fbf\u662f\u8fd9\u79cd \u201c\u6cbe\u9ecf\u52b2\u201d\u7684\u529f\u592b\u3002\u9ec4\u53f6\u9053\u4eba\u7528\u51fa\u6bd5\u751f\u8654\u4fee\u7684\u7edd\u6280\uff0c\u7389\u7f57\u5239\u867d\u7136\u75be\u903e\u98d8\u98ce\uff0c\u88ab\u4ed6\u7d27\u968f\u4e0d\u820d\uff0c \u5a01\u529b\u96be\u5c55\uff0c\u800c\u4e14\u767d\u77f3\u7b49\u53c1\u4eba\u4e5f\u90fd\u662f\u5f53\u4e16\u9ad8\u624b\uff0c\u7389\u7f57\u5239\u987f\u65f6\u88ab\u8feb\u5f97\u5904\u65bc\u4e0b\u98ce\uff01 \u3000\u3000\u53c8\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u7389\u7f57\u5239\u989d\u5934\u89c1\u6c57\uff0c\u8fde\u756a\u51b2\u523a\uff0c\u6740\u4e0d\u51fa\u53bb\uff0c\u628a\u5fc3\u4e00\u6a2a\uff0c\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c \u5c55\u5f00\u4e86\u62da\u547d\u7684\u62db\u6570\uff0c\u907f\u5f3a\u51fb\u5f31\uff0c\u4e13\u5411\u767d\u77f3\u9752\u7ea2\u4e91\u7b49\u53c1\u4eba\u4e0b\u624b\uff0c\u4e00\u51fa\u624b\u4fbf\u662f\u51f6\u6781\u4f24\u6b8b\u7684\u5251\u6cd5\uff0c \u9ec4\u53f6\u5927\u60ca\uff0c\u672c\u6765\u6709\u51e0\u6b21\u53ef\u4ee5\u4f24\u5f97\u4e86\u5979\uff0c\u4f46\u4e3a\u4e86\u536b\u62a4\u5e08\u5f1f\uff0c\u4e0d\u80fd\u4e0d\u79fb\u5251\u76f8\u62d2\u3002\u9ec4\u53f6\u9053\uff1a\u201c\u6211\u5b88 \u5fa1\u5979\u7684\u5251\u52bf\uff0c\u4f60\u4eec\u75be\u653b\u3002\u201d\u957f\u5251\u968f\u7740\u7389\u7f57\u5239\u5251\u5149\u8fd0\u8f6c\uff0c\u767d\u77f3\u7b49\u53c1\u4eba\u8fd0\u5251\u5982\u98ce\uff0c\u72e0\u72e0\u653b\u523a\u3002\u4e94 \u5251\u4ea4\u950b\uff0c\u6709\u5982\u4e00\u7247\u5149\u7f51\uff0c\u7389\u7f57\u5239\u5251\u52bf\u6240\u5230\uff0c\u6709\u5982\u78b0\u7740\u94c1\u58c1\u94dc\u5899\uff0c\u800c\u767d\u77f3\u7b49\u53c1\u4eba\u7684\u8fde\u73af\u5251\u6cd5\u53c8 \u9996\u5c3e\u76f8\u8854\uff0c\u65e0\u6687\u53ef\u51fb\u3002\u7389\u7f57\u5239\u53ea\u597d\u6c89\u795e\u5e94\u6218\uff0c\u773c\u89c2\u56db\u9762\uff0c\u8033\u542c\u516b\u65b9\u3002\u4ed7\u7740\u7edd\u9876\u8f7b\u529f\uff0c\u817e\u632a\u95ea \u5c55\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u53c8\u6597\u4e86\u6570\u5341\u6765\u62db\uff01 \u3000\u3000\u8fd9\u4e00\u573a\u5927\u6218\uff0c\u771f\u662f\u4e16\u95f4\u7f55\u89c1\uff0c\u6b66\u5f53\u6d3e\u7684\u5f1f\u5b50\u770b\u5f97\u773c\u82b1\u64a9\u4e71\uff0c\u4e00\u4e2a\u4e2a\u5c4f\u4e86\u547c\u5438\uff0c\u76ee\u6ce8\u6597 \u573a\u3002\u5353\u4e00\u822a\u4e5f\u65e9\u5df2\u6536\u4e86\u773c\u6cea\uff0c\u88ab\u573a\u4e2d\u7684\u5267\u6597\u6240\u5438\u5f15\u4e86\u3002\u8fd9\u65f6\uff0c\u672c\u6765\u662f\u6b66\u5f53\u56db\u8001\u5360\u4e86\u4e0a\u98ce\uff0c\u53ef \u662f\u5728\u4f17\u5f1f\u5b50\u770b\u6765\uff0c\u4f46\u89c1\u5251\u6c14\u7eb5\u6a2a\uff0c\u5149\u8292\u8000\u773c\uff0c\u5251\u82b1\u6735\u6735\uff0c\u6709\u5982\u9ed1\u591c\u7e41\u661f\uff0c\u5343\u70b9\u4e07\u70b9\uff0c\u904d\u7a7a\u98de \u6d12\uff0c\u4e94\u6761\u4eba\u5f71\u7eb5\u6a2a\u7a7f\u63d2\uff0c\u8fa8\u4e0d\u51fa\u6765\u3002\u5353\u4e00\u822a\u770b\u5f97\u5fc3\u60ca\u52a8\u9b44\uff0c\u77e5\u9053\u6b64\u573a\u6076\u6218\uff0c\u975e\u6709\u6b7b\u4f24\uff0c\u7edd\u96be \u7f62\u4f11\u3002\u5fc3\u4e2d\u77db\u76fe\u4e4b\u6781\uff0c\u4e5f\u4e0d\u77e5\u613f\u90a3\u4e00\u65b9\u5f97\u80dc\u3002 \u3000\u3000\u865e\u65b0\u57ce\u5ffd\u9053\uff1a\u201c\u56db\u4f4d\u5e08\u53d4\uff0c\u5e74\u7eaa\u8001\u8fc8\uff0c\u529b\u5fa1\u5f3a\u654c\uff0c\u82e5\u6709\u758f\u5931\uff0c\u6211\u8f88\u5f1f\u5b50\u4f55\u5730\u5bb9\u8eab\uff0c\u638c\u95e8 \u5e08\u5144\uff0c\u4f60\u770b\u8be5\u600e\u9ebd\u529e\uff1f\u201d\u5353\u4e00\u822a\u5982\u542c\u800c\u4e0d\u95fb\uff0c\u4e0d\u4f5c\u56de\u7b54\u3002\u865e\u65b0\u57ce\u51b7\u7b11\u9053\uff1a\u201c\u5e08\u53d4\u5728\u573a\u4e2d\u62da \u547d\uff0c\u6211\u4eec\u5f1f\u5b50\u5c82\u5bb9\u8896\u624b\u65c1\u89c2\uff01\u201d\u9ec4\u53f6\uff0c\u7ea2\u4e91\uff0c\u767d\u77f3\uff0c\u9752\u5404\u6709\u9996\u5f92\uff0c\u53f7\u79f0\u7b2c\u4e8c\u8f88\u4e2d\u7684\u56db\u5927\u5f1f \u5b50\u3002\u865e\u65b0\u57ce\u662f\u9ec4\u53f6\u7684\u9996\u5f92\uff0c\u8eab\u4e3a\u56db\u5927\u5f1f\u5b50\u4e4b\u9996\uff0c\u62db\u547c\u5176\u9980\u53c1\u4eba\u9053\uff1a\u201c\u6211\u4eec\u4e00\u540c\u51fa\u53bb\u3002\u548c\u56db\u4f4d \u5e08\u53d4\u5e03\u6210\u6b66\u5f53\u5251\u9635\uff0c\u52a1\u5fc5\u4e0d\u4ee4\u8fd9\u5996\u5973\u751f\u9003\u3002\u201d\u8bf4\u5b8c\u4e4b\u5f8c\uff0c\u53c8\u5411\u5353\u4e00\u822a\u4f5c\u4e86\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u638c\u95e8 \u5e08\u5144\uff0c\u8bf7\u6055\u6211\u4eec\u4e0d\u5f85\u5429\u5490\uff0c\u5148\u51fa\u53bb\u4e86\uff01\u201d\u7387\u4f17\u51b2\u51fa\uff0c\u5353\u4e00\u822a\u5927\u4e3a\u96be\u8fc7\u3002\u53ea\u542c\u5f97\u80cc\u5f8c\u6709\u4eba\u563f\u563f \u51b7\u7b11\uff0c\u56de\u5934\u4e00\u770b\uff0c\u5374\u662f\u540c\u95e8\u7684\u5e08\u5144\u5f1f\u803f\u7ecd\u5357\u3002\u53ea\u89c1\u4ed6\u9762\u9732\u9119\u8096\u4e4b\u5bb9\uff0c\u5353\u4e00\u822a\u7684\u773c\u5149\u548c\u4ed6\u4e00 \u89e6\uff0c\u4ed6\u7406\u4e5f\u4e0d\u7406\uff0c\u8fc5\u5373\u628a\u773c\u5149\u79fb\u5f00\u3002 \u3000\u3000\u803f\u7ecd\u5357\u66fe\u53d7\u7389\u7f57\u5239\u5229\u5251\u65ad\u6307\u4e4b\u8fb1\uff0c\u5bf9\u7389\u7f57\u5239\u6068\u4e4b\u5165\u9aa8\u3002\u53ea\u56e0\u81ea\u77e5\u672c\u4e8b\u4f4e\u5fae\uff0c\u975e\u6b66\u5f53\u56db\u5927 \u5f1f\u5b50\u53ef\u6bd4\uff0c\u6240\u4ee5\u4e0d\u6562\u51fa\u53bb\u3002\u4f46\u4ed6\u5fc3\u4e2d\u5374\u5728\u76d8\u7b97\u4e3b\u610f\uff0c\u60f3\u628a\u5353\u4e00\u822a\u6fc0\u5f97\u52a8\u624b\u3002 \u3000\u3000\u5353\u4e00\u822a\u8eab\u53d7\u5e08\u53d4\u8d23\u9a82\uff0c\u53c8\u88ab\u540c\u95e8\u9119\u89c6\uff0c\u72b9\u5982\u4e0d\u575a\u5b9e\u7684\u63d0\u9632\uff0c\u63a5\u4e8c\u8fde\u53c1\uff0c\u53d7\u98ce\u6d6a\u6240\u88ad\u51fb\uff0c \u7cbe\u795e\u9707\u6c64\uff0c\u8111\u75db\u6b32\u88c2\uff0c\u771f\u6bd4\u53d7\u5211\u8fd8\u82e6\uff0c\u795e\u667a\u6e10\u89c9\u8ff7\u7cca\u3002 \u3000\u3000\u518d\u8bf4\u7389\u7f57\u5239\u529b\u654c\u6b66\u5f53\u56db\u8001\uff0c\u5df2\u611f\u5403\u529b\u975e\u5e38\uff0c\u56db\u5927\u5f1f\u5b50\u4e00\u52a0\u4eba\u6765\uff0c\u66f4\u662f\u96be\u652f\u3002\u8fd9\u56db\u4eba\u867d\u7136 \u672c\u9886\u8f83\u4f4e\uff0c\u4ea6\u975e\u5eb8\u624b\u3002\u800c\u4e14\u5c24\u5176\u5389\u5bb3\u7684\u662f\uff0c\u8fd9\u56db\u4eba\u52a0\u4eba\u4e4b\u5f8c\uff0c\u516b\u4e2a\u65b9\u4f4d\uff0c\u90fd\u7ad9\u6709\u4eba\uff0c\u5e03\u6210\u4e86 \u4e25\u5bc6\u7684\u5251\u9635\uff0c\u6709\u5982\u94c1\u58c1\u94dc\u5899\uff0c\u8fde\u82cd\u8747\u4e5f\u98de\u4e0d\u51fa\u53bb\u3002\u7389\u7f57\u5239\u672c\u9886\u518d\u9ad8\uff0c\u8f7b\u529f\u518d\u5999\uff0c\u4e5f\u662f\u96be\u5f53\u3002 \u8fd9\u65f6\u4f46\u89c1\u6ee1\u573a\u5175\u5203\u98de\u821e\uff0c\u628a\u7389\u7f57\u5239\u56f0\u5728\u6838\u5fc3\uff0c\u72b9\u5982\u4e00\u53f6\u5b64\u821f\uff0c\u5728\u98ce\u6d6a\u4e2d\u6323\u624e\uff0c\u84e6\u7136\u88ab\u5377\u5165\u6f29 \u6da1\uff0c\u52a8\u6c64\u98d8\u6447\uff0c\u52bf\u5c06\u6ca1\u9876\uff0c\u5f62\u52bf\u9669\u7edd\uff01 \u3000\u3000\u7389\u7f57\u5239\u81ea\u6668\u81f3\u5348\uff0c\u62da\u6597\u4f55\u6b62\u5343\u62db\uff0c\u6b66\u5f53\u516b\u5927\u9ad8\u624b\u7684\u56f4\u653b\uff0c\u6bd4\u5f53\u5e74\u5728\u534e\u5c71\u7edd\u9876\u6240\u9047\u7684\u201c\u4e03 \u7edd\u9635\u201d\u8fd8\u8981\u5389\u5bb3\u6570\u500d\u3002\u7389\u7f57\u5239\u6c14\u529b\u6e10\u51cf\uff0c\u8eab\u6cd5\u5df2\u4e0d\u82e5\u4ee5\u524d\u8f7b\u7075\u3002\u6b66\u5f53\u516b\u4e2a\u9ad8\u624b\u89c1\u5c06\u5f97\u624b\uff0c\u56f4 \u653b\u8d8a\u7d27\uff0c\u5982\u6f6e\u6c34\u822c\u500f\u8fdb\u500f\u9000\uff0c\u516b\u53e3\u660e\u6643\u6643\u7684\u5229\u5251\uff0c\u5728\u7389\u7f57\u5239\u7684\u8eab\u524d\u8eab\u5f8c\u5de6\u8eab\u53f3\uff0c\u4ea4\u53c9\u7a7f\u63d2\uff0c \u770b\u6837\u5b50\u975e\u628a\u7389\u7f57\u5239\u5207\u6210\u516b\u5757\uff0c\u4e0d\u80af\u5e72\u4f11\u3002\u5353\u4e00\u822a\u60ca\u5fc3\u6035\u76ee\uff0c\u4e0d\u5fcd\u518d\u770b\uff0c\u628a\u8138\u79fb\u5f00\u3002\u803f\u7ecd\u5357\u54c8 \u54c8\u5927\u7b11\uff0c\u62c9\u5353\u4e00\u822a\u7684\u81c2\u818a\u9053\uff1a\u201c\u638c\u95e8\u5e08\u5144\uff0c\u4f60\u770b\uff0c\u4f60\u770b\u5440\uff0c\u9ec4\u53f6\u5e08\u53d4\u8fd9\u4e00\u5251\u597d\u6781\u4e86\uff0c\u767d\u77f3\u5e08 \u53d4\u8fd9\u4e00\u5251\u4e5f\u4e0d\u9519\uff0c\u5440\uff0c\u53ef\u60dc\uff0c\u53ef\u60dc\uff0c\u9752\u5e08\u53d4\u8fd9\u4e00\u5251\u660e\u660e\u5df2\u523a\u5230\u5979\u7684\u54bd\u5589\uff0c\u600e\u9ebd\u53c8\u7ed9\u5979\u907f\u5f00 \u4e86\u3002\u5514\uff0c\u65b0\u57ce\u5e08\u5144\u4e5f\u4e0d\u843d\u5f8c\uff0c\u8fd9\u4e00\u5251\u51e0\u4e4e\u524a\u6389\u5979\u7684\u819d\u76d6\u3002\u554a\uff01\u554a\uff01\u597d\u597d\uff01\u4e2d\u4e86\uff0c\u4e2d\u4e86\uff0c\u201d\u5353 \u4e00\u822a\u5ffd\u542c\u5f97\u7389\u7f57\u5239\u4e00\u58f0\u60e8\u53eb\uff0c\u63a5\u7740\u53c8\u662f\u4e00\u58f0\uff0c\u6025\u7741\u773c\u770b\uff0c\u53ea\u89c1\u7389\u7f57\u5239\u6447\u6447\u6b32\u5760\uff0c\u811a\u6b65\u51cc\u4e71\uff0c \u72b9\u5982\u4e00\u5934\u75af\u864e\uff0c\u5de6\u51b2\u53f3\u7a81\uff0c\u51b2\u4e0d\u51fa\u53bb\uff0c\u5251\u5149\u6587\u6620\u4e4b\u4e2d\uff0c\u4f46\u89c1\u4e00\u56e2\u7ea2\u8272\u6643\u52a8\uff0c\u72b9\u5982\u5728\u767d\u7691\u7691\u7684 \u96ea\u5730\u4e0a\u67d3\u4e0a\u80ed\u8102\uff0c\u60f3\u662f\u7389\u7f57\u5239\u88ab\u5251\u6240\u4f24\uff0c\u8840\u900f\u8863\u88f3\u4e86\uff01\u5353\u4e00\u822a\u4e0d\u89c9\u5927\u53eb\u4e00\u58f0\uff0c\u51e0\u4e4e\u6655\u4e86\u8fc7 \u53bb\u3002 \u3000\u3000\u7389\u7f57\u5239\u5de6\u81c2\u4e2d\u4e86\u9ec4\u53f6\u4e00\u5251\uff0c\u53f3\u8155\u53c8\u7ed9\u767d\u77f3\u5251\u950b\u5272\u4f24\uff0c\u672c\u5df2\u6447\u6447\u6b32\u5012\uff0c\u5ffd\u95fb\u5f97\u5353\u4e00\u822a\u60ca\u547c \u60e8\u53eb\u4e4b\u5f8c\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u5c1a\u662f\u5728\u5173\u5fc3\u6211\u7684\u3002\u201d\u9661\u7136\u95f4\u7cbe\u795e\u4e00\u957f\uff0c\u4e5f\u4e0d\u5982\u662f\u90a3\u91cc\u6765\u7684\u529b\u91cf\uff0c \u5251\u8bc0\u4e00\u9886\uff0c\u76d8\u65cb\u98de\u821e\uff0c\u987f\u5982\u96e8\u9aa4\u98ce\u72c2\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u51e0\u4e4e\u5316\u6210\u4e86\u4e00\u9053\u767d\u5149\uff0c\u76f4\u5411\u9ec4\u53f6\u9053\u4eba\u51b2 \u53bb\uff0c\u9ec4\u53f6\u9053\u4eba\u4ecd\u7528\u201c\u9ecf\u5b57\u8bc0\u201d\uff0c\u968f\u66f2\u5c31\u4f38\uff0c\u5251\u52bf\u4e00\u65bd\uff0c\u60f3\u8fd0\u5185\u5bb6\u771f\u529b\uff0c\u5c06\u5979\u75af\u72c2\u7684\u6765\u52bf\u5316\u89e3 \u65bc\u65e0\u5f62\uff0c\u90a3\u77e5\u7389\u7f57\u5239\u6765\u5f97\u592a\u75be\uff0c\u9ec4\u53f6\u9053\u4eba\u7684\u5185\u529b\u672a\u900f\u5251\u5c16\uff0c\u5251\u950b\u5df2\u88ab\u5979\u4e00\u5251\u524a\u65ad\uff0c\u9ec4\u53f6\u9053\u4eba \u6a2a\u638c\u4e00\u63a8\uff0c\u7389\u7f57\u5239\u968f\u7740\u4ed6\u7684\u638c\u98ce\u5f39\u4e86\u8d77\u6765\uff0c\u51b2\u52bf\u66f4\u731b\uff0c\u767d\u5149\u4e00\u7ed5\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u65ad\u91d1\u621b\u7389\u4e4b \u58f0\uff0c\u7ea2\u4e91\u9053\u4eba\u7684\u5251\u4e5f\u7ed9\u524a\u65ad\uff0c\u7389\u7f57\u5239\u4e00\u58f0\u72c2\u7b11\uff0c\u5237\u5237\u4e24\u5251\uff0c\u767d\u77f3\u9053\u4eba\u53cd\u81c2\u523a\u624e\uff0c\u201c\u661f\u6a2a\u6597 \u8f6c\u201d\u4e00\u62db\uff0c\u521a\u521a\u4f7f\u51fa\uff0c\u7389\u7f57\u5239\u5251\u950b\u4e00\u6307\uff0c\u75be\u5982\u7535\u95ea\uff0c\u76f4\u523a\u54bd\u5589\u3002 \u3000\u3000\u767d\u77f3\u9053\u4eba\u5fc3\u80c6\u4ff1\u5bd2\uff0c\u7edd\u9669\u4e2d\u6025\u5c55\u201c\u94c1\u677f\u6865\u201d\u529f\u592b\uff0c\u5de6\u8db3\u6491\u5730\uff0c\u53f3\u811a\u8e6c\u7a7a\uff0c\u8170\u5411\u5f8c\u5f2f\uff0c\u89e6 \u53ca\u5730\u9762\uff0c\u7389\u7f57\u5239\u547c\u7684\u4e00\u5251\u5728\u4ed6\u9762\u95e8\u63a0\u8fc7\uff0c\u9752\u9053\u4eba\u4f0f\u8eab\u4e00\u8dc3\uff0c\u957f\u5251\u4e00\u65cb\uff0c\u786c\u63a5\u4e86\u5979\u7684\u4e00\u62db\u3002\u6b63 \u5728\u6b64\u9645\uff0c\u5ffd\u542c\u5f97\u7389\u7f57\u5239\u53c8\u662f\u60e8\u53eb\u4e00\u58f0\uff0c\u4e24\u773c\u7ffb\u767d\uff0c\u5251\u52bf\u7a81\u7f13\u3002\u9752\u9053\u4eba\u5f04\u5f97\u83ab\u540d\u5176\u5999\uff0c\u53ea\u542c\u5f97 \u7389\u7f57\u5239\u54c0\u53eb\u9053\uff1a\u201c\u5353\u4e00\u822a\uff0c\u662f\u4f60\uff0c\u4f60\u4e5f\u8fd9\u6837\u5bf9\u6211\u5417\uff1f\u201d \u3000\u3000\u539f\u6765\u5728\u7389\u7f57\u5239\u524a\u65ad\u9ec4\u53f6\u7ea2\u4e91\u7684\u5251\uff0c\u51e0\u4e4e\u6740\u4e86\u767d\u77f3\u4e4b\u65f6\uff0c\u803f\u7ecd\u5357\u5728\u5353\u4e00\u822a\u8033\u8fb9\u5927\u559d\u9053\uff1a \u201c\u638c\u95e8\u5e08\u5144\uff0c\u4f60\u8fd8\u4e0d\u5feb\u6551\u5e08\u53d4\uff1f\u7528\u6697\u9752\u5b50\u5979\u5440\uff01\u8198\uff0c\u5feb\uff01\u201d\u628a\u5f39\u5f13\u585e\u5230\u5353\u4e00\u822a\u624b\u4e2d\uff0c\u5353\u4e00\u822a \u5df2\u5165\u534a\u660f\u8ff7\u72b6\u6001\uff0c\u7cbe\u795e\u90a3\u5bb9\u5f97\u5982\u6b64\u6467\u6b8b\uff0c\u88ab\u4ed6\u4e00\u559d\uff0c\u5982\u53d7\u50ac\u7720\uff0c\u7cca\u7cca\u6d82\u6d82\u7684\u62c9\u8d77\u5f39\u5f13\uff0c\u55d6\u55d6 \u55d6\u8fde\u53d1\u53c1\u5f39\uff0c\u8fd9\u53c1\u5f39\u88ab\u6ee1\u573a\u4ea4\u6c64\u7684\u5251\u98ce\u9707\u5f97\u7c89\u788e\uff0c\u5f53\u7136\u6253\u4e0d\u5230\u7389\u7f57\u5239\u8eab\u4e0a\uff0c\u53ef\u662f\u5374\u6253\u4f24\u4e86\u7389 \u7f57\u5239\u7684\u5fc3\uff01 \u3000\u3000\u767d\u77f3\u9053\u4eba\u65b9\u9003\u9669\u96be\uff0c\u53c8\u8d77\u6740\u673a\uff0c\u4e58\u52bf\u4e00\u8dc3\u800c\u8d77\uff0c\u5251\u628a\u4e00\u7ffb\uff0c\u65cb\u98ce\u6025\u523a\uff0c\u9752\u9053\u4eba\u4e5f\u8d81\u52bf\u4e00 \u5251\uff0c\u76f4\u6302\u80f8\u819b\uff0c\u659c\u523a\u8170\u80c1\u3002\u5c31\u5728\u6b64\u9645\uff0c\u77f3\u53f0\u90a3\u8fb9\u53c8\u4f20\u6765\u4e86\u5353\u4e00\u822a\u60ca\u53eb\u4e4b\u58f0\uff0c\u7389\u7f57\u5239\u4f9d\u7a00\u542c\u5f97 \u4ed6\u53eb\u9053\uff1a\u201c\u6211\u505a\u4e86\u4ec0\u9ebd\uff0c\u6211\u505a\u4e86\u4ec0\u9ebd\u5440\uff1f\u201d\u63a5\u7740\u662f\u5495\u549a\u4e00\u58f0\uff0c\u4f3c\u4e4e\u5df2\u662f\u8dcc\u5012\u5730\u4e0a\u3002 \u3000\u3000\u767d\u77f3\u9752\u53cc\u5251\u9f50\u5230\uff0c\u7389\u7f57\u5239\u5b9d\u5251\u6a2a\u80f8\uff0c\u4f3c\u4e4e\u5fd8\u4e86\u51fa\u62db\uff0c\u4e8c\u4eba\u5927\u559c\uff0c\u90fd\u60f3\u523a\u5979\u7684\u7a74\u9053\u5c06\u5979\u751f \u64d2\uff0c\u7136\u5f8c\u518d\u7531\u540c\u95e8\u516c\u51b3\u53d1\u843d\uff0c\u4e24\u4eba\u62b1\u7740\u540c\u4e00\u5fc3\u601d\uff0c\u8ba4\u7a74\u52b2\uff0c\u52bf\u9053\u7565\u7f13\u3002\u53cc\u5251\u582a\u582a\u523a\u5230\uff0c\u770b\u770b \u6cbe\u8863\u4e4b\u9645\uff0c\u7389\u7f57\u5239\u624b\u8155\u500f\u7ffb\uff0c\u628a\u5251\u4e00\u6325\uff0c\u5176\u75be\u5982\u7535\uff0c\u8fd9\u4e00\u62db\uff0c\u62ff\u634f\u65f6\u5019\uff0c\u5999\u5230\u6beb\u98a0\uff0c\u5728\u7389\u7f57 \u5239\u8fd9\u65b9\u662f\u84c4\u52b2\u7a81\u53d1\uff0c\u6709\u5982\u6d2a\u6ce2\u9aa4\u8d77\uff0c\u6e83\u56f4\u800c\u51fa\uff1a\u5728\u767d\u77f3\u9752\u8fd9\u65b9\u662f\u5f3a\u5f29\u4e4b\u672a\uff0c\u5ffd\u906d\u53cd\u51fb\uff0c\u52b2\u529b \u53cd\u4e3a\u5bf9\u65b9\u6240\u501f\u3002\u4e00\u6325\u4e00\u63a5\uff0c\u91d1\u94c1\u4ea4\u545c\uff0c\u767d\u77f3\u9752\u7684\u5251\u90fd\u98de\u4e0a\u534a\u7a7a\u3002\u9ec4\u53f6\u9053\u4eba\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff01\u201d \u4e00\u63a0\u4e08\u9980\uff0c\u8fd0\u638c\u6025\u653b\uff0c\u9ec4\u53f6\u5df2\u5feb\uff0c\u4f46\u7389\u7f57\u5239\u66f4\u5feb\uff0c\u53ea\u542c\u5f97\u767d\u77f3\u9752\u540c\u65f6\u60e8\u53eb\uff0c\u5c31\u5728\u8fd9\u77ac\u606f\u4e4b \u95f4\uff0c\u4e24\u4eba\u624b\u81c2\u5173\u8282\uff0c\u90fd\u7ed9\u7389\u7f57\u5239\u5251\u5c16\u523a\u4e86\u3002\u9ec4\u53f6\u4e00\u638c\u6251\u7a7a\uff0c\u7389\u7f57\u5239\u6325\u5251\u72c2\u7b11\uff0c\u65cb\u98ce\u822c\u76f4\u5377\u51fa \u53bb\uff01 \u3000\u3000\u6b66\u5f53\u56db\u4e2a\u957f\u8001\uff0c\u4e24\u4eba\u7684\u5251\u88ab\u524a\u65ad\uff0c\u4e24\u4eba\u53d7\u4e86\u91cd\u4f24\uff0c\u7b2c\u4e8c\u4ee3\u7684\u56db\u5927\u5f1f\u5b50\uff0c\u90a3\u6562\u62e6\u622a\uff0c\u7389\u7f57 \u5239\u5251\u98ce\u6240\u6307\uff0c\u6321\u8005\u8f9f\u6613\u3002\u8fc5\u5373\u51b2\u51fa\u91cd\u56f4\uff0c\u8df3\u4e0b\u77f3\u53f0\uff0c\u6b66\u5f53\u6d3e\u7684\u95e8\u4eba\u5f1f\u5b50\u867d\u7136\u8fd1\u767e\uff0c\u90fd\u88ab\u5979\u7684 \u795e\u5a01\u6740\u6c14\u6240\u6151\uff0c\u7eb7\u7eb7\u95ea\u907f\uff0c\u9ec4\u53f6\u9053\u4eba\u9893\u7136\u957f\u53f9\uff0c\u773c\u7741\u7741\u7684\u770b\u7740\u7389\u7f57\u5239\u5728\u5927\u95f9\u6b66\u5f53\u5c71\u4e4b\u5f8c\uff0c\u72c2 \u7b11\u800c\u53bb\u3002 \u3000\u3000\u767d\u77f3\u9752\u4e8c\u4eba\u88ab\u7389\u7f57\u5239\u7684\u72ec\u95e8\u523a\u7a74\u4e4b\u6cd5\uff0c\u4f24\u4e86\u5173\u8282\u7a74\u9053\uff0c\u9ec4\u53f6\u9053\u4eba\u4e5f\u65e0\u6cd5\u53ef\u89e3\uff0c\u53ea\u80fd\u66ff\u4ed6 \u4eec\u63a8\u8840\u8fc7\u5bab\uff0c\u51cf\u8f7b\u75db\u82e6\uff0c\u53eb\u56db\u5927\u5f1f\u5b50\u5c06\u4ed6\u4eec\u62ac\u5165\u4e91\u623f\uff0c\u8ba9\u4ed6\u4eec\u9759\u517b\uff0c\u8981\u8fc7\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u7a74\u9053 \u65b9\u80fd\u81ea\u89e3\u3002 \u3000\u3000\u9ec4\u53f6\u9053\u4eba\u5429\u5490\u5df2\u6bd5\uff0c\u53cc\u773c\u6a2a\u626b\uff0c\u53ea\u89c1\u95e8\u4e0b\u5f1f\u5b50\uff0c\u4e2a\u4e2a\u5782\u5934\u4e27\u6c14\uff0c\u4e0d\u7981\u53c8\u662f\u4e00\u58f0\u957f\u53f9\uff0c\u5c06 \u7f3a\u4e86\u950b\u5203\u7684\u957f\u5251\u629b\u4e0b\u5c71\u8c37\u3002\u7f13\u7f13\u8d70\u8fd1\u5353\u4e00\u822a\u8eab\u8fb9\uff0c\u5353\u4e00\u822a\u6655\u5728\u5730\u4e0a\uff0c\u6000\u4e2d\u72b9\u81ea\u7d27\u62b1\u5f39\u5f13\u3002 \u3000\u3000\u9ec4\u53f6\u9053\uff1a\u201c\u5728\u7d27\u6025\u5173\u5934\uff0c\u4f60\u53d1\u5f39\u52a9\u6218\uff0c\u5c1a\u662f\u6211\u6b66\u5f53\u5f1f\u5b50\u3002\u201d\u4f38\u624b\u5728\u4ed6\u201c\u4f0f\u5154\u7a74\u201d\u4e00\u62cd\uff0c \u50ac\u52a8\u8840\u8109\u6d41\u901a\uff0c\u5353\u4e00\u822a\u5ffd\u7136\u5927\u53eb\u4e00\u58f0\uff0c\u817e\u8eab\u8df3\u8d77\uff0c\u66f3\u5f00\u5f39\u5f13\uff0c\u55d6\u55d6\u55d6\u8fde\u53d1\u6570\u5f39\uff0c\u56db\u9762\u4e71\u8098\uff0c \u5927\u53eb\u9053\uff1a\u201c\u6253\u5440\uff0c\u6253\u5440\uff0c\u8c01\u6562\u4e0a\u6b66\u5f53\u5c71\u8005\uff0c\u6253\uff01\u8c01\u6562\u62e6\u963b\u6211\u8005\uff0c\u6253\uff01\u591a\u7ba1\u95f2\u4e8b\u8005\uff0c\u6253\uff01\u54c8\uff0c \u54c8\uff0c\u4f60\u80c6\u5927\u5305\u5929\uff0c\u89e6\u72af\u4e86\u6211\u7684\u7956\u5e08\u7237\u4e86\uff0c\u6253\uff01\u201d\u9ec4\u53f6\u559d\u9053\uff1a\u201c\u4f60\u75af\u4e86\u5417\uff1f\u201d\u5353\u4e00\u822a\u77aa\u76ee\u8df3 \u8dc3\uff0c\u5927\u53eb\u5927\u56b7\uff0c\u9ec4\u53f6\u7eb5\u8eab\u4e00\u638c\uff0c\u5c06\u4ed6\u5f39\u5f13\u5288\u65ad\uff0c\u803f\u7ecd\u5357\u8df3\u4e0a\u6765\u5c06\u5353\u4e00\u822a\u4e00\u62b1\uff0c\u5353\u4e00\u822a\u7a81\u7136\u53cd \u624b\u4e00\u638c\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u6253\u5728\u803f\u7ecd\u5357\u9762\u4e0a\uff0c\u8fd9\u4e00\u638c\u52b2\u529b\u5947\u5927\uff0c\u803f\u7ecd\u5357\u5927\u53eb\u4e00\u58f0\uff0c\u5f20\u53e3\u55b7\u51fa\u4e00\u5806\u9c9c \u8840\uff0c\u4e24\u53ea\u95e8\u7259\u3002\u9ec4\u53f6\u6025\u5fd9\u4f38\u6307\u4e00\u70b9\uff0c\u70b9\u4e86\u4ed6\u7684\u6655\u7729\u7a74\uff0c\u9053\uff1a\u201c\u7ecd\u5357\uff0c\u4f60\u7684\u638c\u95e8\u5e08\u5144\u75af\u4e86\u3002\u4f60 \u6709\u6ca1\u6709\u7ed9\u4ed6\u6253\u4f24\uff1f\u201d\u803f\u7ecd\u5357\u6367\u7740\u7ea2\u80bf\u7684\u9762\uff0c\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u662f\u5916\u4f24\u3002\u201d\u9ec4\u53f6\u9053\uff1a\u201c\u4f60\u62ac\u4ed6\u56de \u53bb\uff0c\u5c06\u4ed6\u9501\u5728\u5f8c\u9762\u623f\uff0c\u597d\u597d\u770b\u5b88\u3002\u201d\u95f9\u4e86\u534a\u5929\uff0c\u5929\u8272\u5df2\u8fd1\u9ec4\u660f\uff0c\u7d2b\u9633\u9053\u4eba\u7684\u4e94\u5468\u5e74\u796d\uff0c\u4e5f\u56e0 \u6b64\u4e00\u95f9\uff0c\u6ca1\u6cd5\u4e3e\u884c\u4e86\u3002 \u3000\u3000\u518d\u8bf4\u7389\u7f57\u5239\u8df3\u51fa\u5c71\u8c37\uff0c\u4f24\u5fc3\uff0c\u6124\u6012\uff0c\u7231\u6068\u4ea4\u7ec7\uff0c\u53e3\u4e2d\u7126\u559d\uff0c\u8179\u5185\u9965\u997f\uff0c\u4fef\u8eab\u4e00\u770b\uff0c\u9c9c\u8840 \u67d3\u7ea2\u4e86\u5916\u8863\u3002\u7389\u7f57\u5239\u6068\u6068\u8bf4\u9053\uff1a\u201c\u5f85\u6211\u4f11\u606f\u4e00\u5bb5\uff0c\u518d\u6765\u4e0e\u4f60\u4eec\u8fd9\u4e9b\u725b\u9f3b\u5b50\u8001\u9053\u5927\u6253\u4e00\u67b6\u3002\u6211 \u8981\u6293\u7740\u4ed6\u95ee\uff1a\u4f60\u5230\u5e95\u613f\u4e0d\u613f\u8ddf\u6211\u8d70\uff1f\u4f60\u8bf4\u5f97\u90a3\u9ebd\u771f\u8bda\uff0c\u90a3\u9ebd\u6073\u5207\uff0c\u96be\u9053\u90fd\u662f\u5047\u7684\uff1f\u54c8\uff0c\u54c8\uff0c \u4f60\u8fd8\u7528\u5f39\u5f13\u6253\u6211\uff0c\u6253\u6211\uff01\u54c8\uff0c\u597d\u5728\u6211\u8fd8\u6ca1\u6709\u6b7b\u54e9\u3002\u201d\u6124\u6068\u4e4b\u6781\u3002\u5ffd\u800c\u8f6c\u5ff5\u4e00\u60f3\uff1a\u201c\u82e5\u4e0d\u662f\u4ed6 \u90a3\u4e00\u58f0\u53eb\u558a\uff0c\u6211\u4e5f\u6ca1\u529b\u6c14\u518d\u6253\u4e0b\u53bb\u3002\u4e00\u822a\u5440\uff0c\u4f60\u52a9\u6211\u6b7b\u91cc\u9003\u751f\uff0c\u4f60\u53c8\u8981\u7f6e\u6211\u65bc\u6b7b\u5730\uff0c\u4f60\u60f3\u7684 \u662f\u4ec0\u9ebd\u4e86\u4f60\u5f53\u6211\u662f\u4eb2\u4eba\u8fd8\u662f\u5f53\u6211\u662f\u4ec7\u654c\uff1f\u201d\u7231\u4e4b\u6781\uff0c\u6068\u4e4b\u6781\uff0c\u6839\u4e4b\u6781\u4e5f\u662f\u7231\u4e4b\u6781\uff01\u7389\u7f57\u5239\u8111 \u5b50\u4e00\u7247\u660f\u4e71\uff0c\u811a\u6b65\u865a\u6d6e\uff0c\u5979\u6076\u6218\u4e86\u534a\u5929\uff0c\u8fde\u4e2d\u4e24\u5251\uff0c\u75b2\u7d2f\u4e0d\u582a\uff0c\u8ff7\u832b\u832b\u7684\u8fdb\u5165\u4e00\u5904\u5c71\u8c37\uff0c\u638f \u5c71\u6cc9\u6d17\u6da4\u4e86\u4f24\u53e3\uff0c\u6577\u4e0a\u4e86\u91d1\u521b\u5723\u836f\uff0c\u5e78\u559c\u6ca1\u6709\u4f24\u7740\u8981\u5bb3\uff0c\u6b62\u4e86\u8840\u5f8c\uff0c\u5403\u4e86\u4e00\u70b9\u4e7e\u7cae\uff0c\u773c\u76ae\u4e00 \u9616\uff0c\u518d\u4e5f\u7981\u4e0d\u7740\u75b2\u5026\u7684\u4fb5\u88ad\uff0c\u9893\u7136\u5012\u5367\u3002\u53cc\u8db3\u6d78\u5230\u5c71\u6da7\u4e4b\u4e2d\uff0c\u5979\u4e5f\u6beb\u4e0d\u77e5\u89c9\u3002 \u3000\u3000\u6726\u6726\u80e7\u80e7\u4e2d\uff0c\u5ffd\u89c1\u5353\u4e00\u822a\u542b\u7b11\u8d70\u6765\uff0c\u7389\u7f57\u5239\u4f38\u51fa\u6307\u5934\u5728\u4ed6\u7684\u989d\u4e0a\u4e00\u6233\uff0c\u5353\u4e00\u822a\u9053\uff1a\u201c\u4e0d \u662f\u6211\u8981\u4f24\u4f60\u7684\u5440\uff0c\u662f\u4ed6\u4eec\u8feb\u7684\uff01\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u4f60\u662f\u5927\u4eba\u8fd8\u662f\u5c0f\u5b69\uff0c\u4f60\u81ea\u5df1\u6ca1\u4e3b\u610f\u7684\u5417\uff1f\u201d \u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u662f\u4e00\u53ea\u7ef5\u7f8a\u3002\u201d\u7389\u7f57\u5239\u9053\uff1a\u201c\u597d\uff0c\u4f60\u662f\u7ef5\u7f8a\uff0c\u6211\u5c31\u662f\u7267\u4eba\uff0c\u6211\u8981\u62ff\u76ae\u97ad\u6253 \u4f60\uff01\u201d\u7a81\u7136\u95f4\uff0c\u624b\u4e0a\u5ffd\u7136\u6709\u4e86\u4e00\u6761\u76ae\u97ad\uff0c\u7389\u7f57\u5239\u8fce\u98ce\u6325\u52a8\uff0c\u97ad\u58f0\u5237\u5237\u3002\u5ffd\u7136\u524d\u9762\u7684\u5353\u4e00\u822a\u4e0d \u89c1\u4e86\uff0c\u7389\u7f57\u5239\u811a\u4e0b\u5310\u4f0f\u7740\u4e00\u53ea\u7f94\u7f8a\uff0c\u8eab\u8eaf\u8d64\u7ea2\uff0c\u9732\u51fa\u6c42\u9976\u7684\u76ee\u5149\u3002\u7389\u7f57\u5239\u4e00\u97ad\u6253\u51fa\uff0c\u6025\u53c8\u7f29 \u56de\uff0c\u4f38\u624b\u53bb\u6478\u90a3\u5c0f\u7f94\u7f8a\u7684\u89d2\uff0c\u90a3\u7f94\u7f8a\u5ffd\u7136\u5927\u543c\u4e00\u58f0\uff0c\u4e0d\u662f\u7f94\u7f8a\uff0c\u800c\u662f\u4e00\u53ea\u731b\u864e\u4e86\uff0c\u90a3\u731b\u864e\u5f20 \u7259\u821e\u722a\uff0c\u53ea\u4e00\u6251\u5c31\u628a\u7389\u7f57\u5239\u6251\u7ffb\u5730\u4e0a\uff0c\u5f20\u5f00\u5927\u53e3\uff0c\u952f\u9f7f\uff0c\u54ac\u5979\u7684\u54bd\u5589\u3002\u7389\u7f57\u5239\u672c\u6709\u964d\u9f99\u4f0f\u864e \u4e4b\u80fd\uff0c\u6b64\u65f6\u4e0d\u77e5\u600e\u7684\uff0c\u6c14\u529b\u5b8c\u5168\u6d88\u5931\uff0c\u90a3\u8001\u864e\u767d\u7684\u7259\u9f7f\u5df2\u556e\u7740\u5979\u7684\u5589\u5499\uff0c\u7389\u7f57\u5239\u5927\u53eb\u4e00\u58f0\uff0c \u6323\u624e\u8df3\u8d77\uff0c\u7ef5\u7f8a\uff0c\u8001\u864e\uff0c\u5353\u4e00\u822a\u5168\u90fd\u4e0d\u89c1\u4e86\uff01 \u3000\u3000\u7389\u7f57\u5239\u5f20\u773c\u4e00\u77a7\uff0c\u4f46\u89c9\u971e\u5149\u8000\u76ee\uff0c\u539f\u6765\u5df2\u7761\u4e86\u4e00\u4e2a\u957f\u591c\uff0c\u521a\u624d\u6240\u53d1\u7684\u4e43\u662f\u4e00\u573a\u6076\u68a6\u3002\u7389 \u7f57\u5239\u53c8\u89c9\u9888\u9879\u6c81\u51c9\uff0c\u4f38\u624b\u4e00\u6478\uff0c\u539f\u6765\u662f\u5c71\u6da7\u6c34\u6da8\uff0c\u6c81\u5230\u4e86\u5979\u7684\u9888\u9879\uff0c\u800c\u5979\u5728\u719f\u7761\u8f6c\u4fa7\u4e4b\u95f4\uff0c \u5f8c\u6795\u6795\u7740\u4e00\u5757\u5c16\u77f3\uff0c\u54bd\u5589\u4e5f\u78b0\u7740\u77f3\u5934\uff0c\u6240\u4ee5\u68a6\u4e2d\u751f\u4e86\u88ab\u8001\u864e\u6240\u556e\u7684\u5e7b\u8c61\u3002 \u3000\u3000\u7389\u7f57\u5239\u7ffb\u8eab\u5750\u8d77\uff0c\u6e7f\u6dcb\u6dcb\u7684\u5934\u53d1\u62ab\u6563\u80a9\u5934\uff0c\u6781\u4e0d\u8212\u670d\uff0c\u6c34\u4e2d\u7167\u5f71\uff0c\u53ea\u89c1\u5c71\u6da7\u91cc\u73b0\u51fa\u4e86\u4e00 \u4e2a\u964c\u751f\u7684\u767d\u53d1\u5973\u4eba\uff0c\u7389\u7f57\u5239\u60ca\u53eb\u4e00\u58f0\uff0c\u8fd9\u666f\u8c61\u6bd4\u68a6\u4e2d\u6240\u89c1\u7684\u8001\u864e\u8fd8\u8981\u53ef\u6015\u4e07\u5206\uff01 \u3000\u3000\u7389\u7f57\u5239\u9053\uff1a\u201c\u96be\u9053\u6211\u8fd8\u5728\u68a6\u4e2d\u672a\u9192\uff1f\u201d\u628a\u624b\u6307\u9001\u4eba\u53e3\u4e2d\uff0c\u7528\u529b\u4e00\u54ac\uff0c\u76ae\u7834\u8840\u6d41\uff0c\u75bc\u5230\u5fc3 \u91cc\u3002\u8fd9\u7edd\u4e0d\u662f\u5669\u68a6\u4e86\u3002\u7389\u7f57\u5239\u6025\u5fd9\u5c06\u957f\u53d1\u62e2\u5230\u624b\u4e2d\uff0c\u4ed4\u7ec6\u4e00\u770b\uff0c\u90a3\u8fd8\u6709\u534a\u6761\u4e4c\u9ed1\u7684\u9752\u4e1d\uff1f\u5df2 \u5168\u6591\u767d\u4e86\uff01 \u3000\u3000\u7389\u7f57\u5239\u8df3\u8d77\u6765\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u6211\uff0c\u8fd9\u4e0d\u662f\u6211\uff01\u201d\u6c34\u4e2d\u4eba\u5f71\u6447\u6643\uff0c\u6c34\u6ce2\u6c64\u767e\u53d1\u58f0\uff0c\u4f3c\u4e4e\u662f\u90a3 \u4eba\u5f71\u5728\u8bf4\uff1a\u201c\u6211\u5c31\u662f\u4f60\uff0c\u6211\u5c31\u662f\u4f60\uff01\u201d \u3000\u3000\u8981\u77e5\u7389\u7f57\u5239\u751f\u5c31\u7edd\u4e16\u5bb9\u989c\uff0c\u5bf9\u81ea\u5df1\u7684\u7f8e\u8c8c\u6700\u4e3a\u7231\u60dc\uff0c\u90a3\u77e5\u4e00\u591c\u4e4b\u95f4\uff0c\u7adf\u4ece\u5c11\u5973\u53d8\u6210\u4e86\u767d \u53d1\u76c8\u5934\uff0c\u5f62\u5bb9\u67af\u69c1\u7684\u8001\u5987\u3002\u8fd9\u4e00\u4efd\u96be\u53d7\uff0c\u7b80\u76f4\u65e0\u53ef\u5f62\u5bb9\u3002\u7389\u7f57\u5239\u9893\u7136\u5012\u5728\u5730\u4e0a\uff0c\u8111\u5b50\u7a7a\u7a7a\u6d1e \u6d1e\u7684\u4ec0\u9ebd\u4e5f\u4e0d\u6562\u60f3\u3002\u4f46\u89c1\u6708\u6708\u6d6e\u4e91\u98d8\u8fc7\u5934\u9876\uff0c\u6653\u65e5\u900f\u8fc7\u4e91\u6d77\uff0c\u7167\u5c04\u4e0b\u6765\uff0c\u4e3d\u5f69\u971e\u8f89\uff0c\u8000\u773c\u751f \u64b7\u3002\u91ce\u82b1\u9001\u9999\uff0c\u6797\u9e1f\u4e89\u9e23\uff0c\u677e\u98ce\u751f\u5578\uff0c\u6ee1\u5c71\u90fd\u662f\u751f\u673a\u84ec\u52c3\uff0c\u72ec\u7389\u7f57\u5239\u7684\u8fd9\u9897\u5fc3\u5df2\u50f5\u786c\u4e86\u3002\u6d6e \u4e91\u5e7b\u6210\u5404\u79cd\u5f62\u8c61\uff0c\u7389\u7f57\u5239\u53c8\u604d\u60da\u4f3c\u89c1\u5353\u4e00\u822a\u5728\u4e91\u7aef\u91cc\u542b\u7b11\u5411\u5979\u51dd\u89c6\u3002\u8033\u8fb9\u54cd\u8d77\u4e86\u8fd9\u6837\u7684\u58f0 \u97f3\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u4f60\u7684\u5bb9\u989c\u5e94\u8be5\u50cf\u5f00\u4e0d\u8d25\u7684\u82b1\u6735\u3002\u201d\u201c\u75f4\u4eba\u8bf4\u68a6\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u90a3\u6709\u9752\u6625\u957f\u9a7b\u4e4b \u4eba\uff1f\u2026\u2026\u4e0b\u6b21\u4f60\u89c1\u5230\u6211\u65f6\uff0c\u53ea\u6050\u6015\u6211\u5df2\u662f\u767d\u53d1\u6ee1\u5934\u7684\u8001\u5a46\u5a46\u4e86\uff01\u201d\u201c\u5230\u4f60\u751f\u51fa\u767d\u53d1\uff0c\u6211\u5c31\u53bb \u6c42\u7075\u4e39\u5999\u836f\uff0c\u8ba9\u4f60\u6062\u590d\u9752\u6625\uff01\u201d\u8fd9\u662f\u7389\u7f57\u5239\u4e0e\u5353\u4e00\u822a\u5728\u660e\u6708\u5ce1\u5410\u9732\u771f\u60c5\u4e4b\u65f6\u7684\u5bf9\u8bdd\u3002\u800c\u4eca\u5374 \u662f\u6614\u65e5\u620f\u8a00\u4e4b\u4e8b\uff0c\u4eca\u671d\u90fd\u5230\u773c\u524d\uff01\u4e91\u5f71\u53d8\u5e7b\uff0c\u201c\u5353\u4e00\u822a\u201d\u53c8\u4e0d\u89c1\u4e86\u3002\u7389\u7f57\u5239\u82e6\u7b11\u9053\uff1a\u201c\u5929\u4e0b \u90a3\u6709\u7075\u4e39\u5999\u836f\uff0c\u4eca\u751f\u6211\u662f\u518d\u4e5f\u4e0d\u89c1\u4f60\u4e86\u3002\u201d \u3000\u3000\u7389\u7f57\u5239\u672c\u6765\u51c6\u5907\u5728\u7cbe\u529b\u6062\u590d\u4e4b\u5f8c\uff0c\u518d\u53bb\u5927\u95f9\u6b66\u5f53\uff0c\u5411\u5353\u4e00\u822a\u95ee\u4e2a\u660e\u767d\uff0c\u60f3\u4e0d\u5230\u4e00\u591c\u4e4b \u95f4\uff0c\u7a81\u751f\u53d8\u5316\uff0c\u6b64\u65f6\u6b64\u9645\uff0c\u7389\u7f57\u5239\u7684\u5fc3\u60c5\u96be\u8fc7\u4e4b\u6781\uff0c\u5c31\u7b97\u5353\u4e00\u822a\u8d70\u8fd1\u524d\u6765\uff0c\u6050\u6015\u5979\u4e5f\u8981\u907f\u5f00 \u4e86\u3002 \u3000\u3000\u7389\u7f57\u5239\u8eba\u4e86\u534a\u5929\uff0c\u8863\u88f3\u5df2\u4e7e\uff0c\u5c71\u98ce\u4e2d\u53c8\u9001\u6765\u9053\u89c2\u7684\u949f\u58f0\u3002\u7389\u7f57\u5239\u4e00\u58f0\u51c4\u7b11\uff0c\u5fc3\u4e2d\u7a81\u7136\u6709 \u4e86\u4e00\u4e2a\u51b3\u5b9a\uff0e\uff0c\u8fce\u98ce\u8bf4\u9053\uff1a\u201c\u81ea\u6b64\u4e16\u754c\u4e0a\u518d\u4e5f\u6ca1\u6709\u7389\u7f57\u5239\u4e86\uff0c\u6211\u8981\u5230\u6211\u8be5\u53bb\u7684\u5730\u65b9\u3002\u201d\u5934\u4e5f \u4e0d\u56de\uff0c\u4e0b\u5c71\u75be\u8dd1\u3002 \u3000\u3000\u518d\u8bf4\u7ecf\u6b64\u4e00\u6218\uff0c\u6b66\u5f53\u6d3e\u635f\u4f24\u60e8\u91cd\uff0c\u767d\u77f3\u9752\u4e8c\u4eba\u8fc7\u4e86\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u7a74\u9053\u867d\u89e3\uff0c\u5173\u8282\u7b4b\u9aa8\u5df2 \u88ab\u6311\u65ad\uff0c\u4e0d\u80fd\u4f7f\u5251\uff0c\u8981\u7528\u67f3\u679d\u63a5\u9aa8\u4e4b\u6cd5\uff0c\u7ecf\u8fc7\u534a\u5e74\u57f9\u517b\uff0c\u624d\u80fd\u590d\u539f\u3002\u9ec4\u53f6\u9053\u4eba\u6781\u6015\u7389\u7f57\u5239\u518d \u6765\uff0c\u63d0\u5fc3\u540a\u80c6\u6570\u65e5\uff0c\u5e78\u559c\u65e0\u4e8b\u3002\u800c\u5353\u4e00\u822a\u7684\u75af\u75be\u4e5f\u4f3c\u6709\u597d\u8f6c\u4e4b\u5146\u3002\u4e0d\u518d\u5927\u53eb\u5927\u56b7\u4e86\u3002 \u3000\u3000\u53ef\u662f\uff0c\u5353\u4e00\u822a\u867d\u7136\u4e0d\u518d\u75af\u72c2\u80e1\u95f9\uff0c\u5374\u662f\u76ee\u5149\u5446\u6ede\uff0c\u72b9\u5982\u767d\u75f4\u3002\u9ec4\u53f6\u9053\u4eba\u5341\u5206\u4f24\u5fc3\uff0c\u4e25\u7981 \u95e8\u5f92\uff0c\u4e0d\u51c6\u5728\u4ed6\u9762\u524d\u63d0\u8d77\u7389\u7f57\u5239\u7684\u540d\u5b57\uff0c\u6089\u5fc3\u66ff\u4ed6\u6cbb\u7597\uff0c\u5982\u662f\u8005\u8fc7\u4e86\u53c1\u6708\uff0c\u5353\u4e00\u822a\u8bf4\u8bdd\u6709\u65f6 \u4e5f\u5982\u597d\u4eba\uff0c\u53ef\u662f\u5374\u4e0d\u5927\u80af\u5f00\u53e3\uff0c\u5bf9\u5e08\u53d4\u5bf9\u540c\u95e8\u90fd\u4f3c\u843d\u843d\u96be\u5408\u3002\u9ec4\u53f6\u9053\u4eba\u65e5\u591c\u6d3e\u4eba\u5b88\u5728\u4ed6\u7684\u623f \u5916\uff0c\u770b\u7ba1\u751a\u4e25\u3002\u9ec4\u53f6\u8fd8\u6015\u4ed6\u4f1a\u81ea\u5bfb\u77ed\u89c1\uff0c\u5e38\u5e38\u591c\u95f4\u5728\u7a97\u9699\u5077\u7aa5\uff0c\u6bcf\u5929\u90fd\u89c1\u4ed6\u95ed\u76ee\u7ec3\u529f\uff0c\u5e76\u65e0 \u5f02\u72b6\u3002\u9ec4\u53f6\u9053\u4eba\u653e\u4e0b\u4e86\u5fc3\uff0c\u60f3\u9053\uff1a\u201c\u4ed6\u8fd8\u80af\u7528\u5fc3\u7ec3\u529f\uff0c\u90a3\u662f\u7edd\u4e0d\u4f1a\u81ea\u6740\u7684\u4e86\u3002\u201d\u95e8\u4eba\u4e2d\u4e5f\u6709 \u4eba\u63d0\u8fc7\u5e9f\u7acb\u4e4b\u4e8b\uff0c\u9ec4\u53f6\u603b\u4e0d\u7b54\u5141\u3002\u6b66\u5f53\u7b2c\u4e8c\u4ee3\u5b9e\u5728\u627e\u4e0d\u51fa\u53ef\u4ee5\u7ee7\u627f\u7684\u4eba\u624d\uff0c\u800c\u5353\u4e00\u822a\u5185\u529f\u8fdb \u5883\u4e4b\u901f\uff0c\u53c8\u662f\u6709\u76ee\u5171\u7779\u4e4b\u4e8b\u3002 \u3000\u3000\u4e00\u65e5\uff0c\u6b66\u5f53\u5c71\u5ffd\u7136\u6765\u4e86\u4e00\u540d\u4e0d\u901f\u4e4b\u5ba2\uff0c\u4e43\u662f\u6155\u5bb9\u51b2\u3002\u6155\u5bb9\u51b2\u4f24\u597d\u4e4b\u5f8c\uff0c\u79bb\u5f00\u5317\u4eac\u3002\u5fc3\u4e2d \u601d\u5ff5\u94c1\u98de\u9f99\u4e0e\u7389\u7f57\u5239\u7684\u6069\u4e49\uff0c\u6f2b\u6e38\u8fc7\u6b66\u5f53\u5c71\u65f6\uff0c\u60f3\u8d77\u5353\u4e00\u822a\u548c\u7389\u7f57\u5239\u4e43\u662f\u81f3\u4ea4\uff0c\u4ed6\u4e5f\u77e5\u9053\u767d \u77f3\u9053\u4eba\u963b\u6320\u5a5a\u59fb\u4e4b\u4e8b\u3002\u5fc3\u60f3\uff1a\u6b66\u5f53\u6d3e\u4e0e\u7389\u7f57\u5239\u7684\u7ed3\u51a4\uff0c\u6211\u4e5f\u6709\u4e00\u4e9b\u8d23\u4efb\u3002\u60f3\u5f53\u5e74\u6211\u548c\u767d\u77f3\u9053 \u4eba\u8054\u5408\uff0c\u7834\u4e86\u7389\u7f57\u5239\u7684\u660e\u6708\u5ce1\u5c71\u5be8\uff0c\u4e24\u5bb6\u7ed3\u51a4\u6781\u6df1\u3002\u800c\u4eca\u6211\u4e0e\u7389\u7f57\u5239\u5316\u654c\u4e3a\u53cb\uff0c\u6b64\u4e8b\u4e5f\u8be5\u6211 \u6765\u8c03\u89e3\u3002\u65bc\u662f\u6765\u5230\u6b66\u5f53\u5c71\u4e0a\uff0c\u8bf7\u89c1\u767d\u77f3\u9053\u4eba\u3002 \u3000\u3000\u767d\u77f3\u9053\u4eba\u4f24\u52bf\u672a\u6108\uff0c\u5c1a\u5728\u4e91\u623f\u9759\u517b\uff0c\u4e0d\u4fbf\u89c1\u5ba2\u3002\u6155\u5bb9\u51b2\u53c8\u8bf7\u89c1\u638c\u95e8\u5f1f\u5b50\u5353\u4e00\u822a\u3002\u9ec4\u53f6\u9053 \u4eba\u89c1\u4e86\u62dc\u5e16\uff0c\u60f3\u8d77\u6155\u5bb9\u51b2\u548c\u6b66\u5f53\u6d3e\u6709\u8fc7\u4e00\u6bb5\u6e0a\u6e90\uff0c\u4fbf\u4ee3\u767d\u77f3\u9053\u4eba\u63a5\u89c1\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u4e0e\u9ec4\u53f6\u9053\u4eba\u76f8\u89c1\u4e4b\u5f8c\uff0c\u5404\u9053\u4ef0\u6155\u4e4b\u5ff1\uff0c\u7ea2\u4e91\u9053\u4eba\u4e5f\u6765\u966a\u5ba2\uff0c\u95ee\u9053\uff1a\u201c\u6155\u5bb9\u603b\u7ba1\u600e \u9ebd\u6709\u5982\u6b64\u95f2\u60c5\u9038\u81f4\uff0c\u9a7e\u4e34\u8352\u5c71\uff1f\u73b0\u5728\u5929\u4e0b\u6b63\u662f\u591a\u4e8b\u4e4b\u79cb\uff0c\u4e07\u5c81\u7237\u653e\u5fc3\u8ba9\u603b\u7ba1\u51fa\u4eac\u9ebd\uff1f\u201d\u6155\u5bb9 \u51b2\u7b11\u9053\uff1a\u201c\u6211\u73b0\u5728\u5df2\u662f\u65e0\u5b98\u4e00\u8eab\u8f7b\uff0c\u4e0d\u518d\u5728\u540d\u5229\u573a\u4e2d\u6253\u6597\u4e86\u3002\u201d\u7ea2\u4e91\u4e00\u6014\uff0c\u4e0d\u4fbf\u7ec6\u95ee\u3002\u9ec4\u53f6 \u7b11\u9053\uff1a\u201c\u597d\u6781\uff0c\u597d\u6781\u201c\u91ce\u9e64\u95f2\u4e91\uff0c\u80dc\u65bc\u9ad8\u5b98\u591a\u77e3\uff01\u201d\u5bd2\u6684\u4e24\u53e5\uff0c\u6155\u5bb9\u51b2\u8bf7\u89c1\u5353\u4e00\u822a\u3002\u9ec4\u53f6 \u9053\uff1a\u201c\u4ed6\u4e0d\u5927\u8212\u670d\u3002\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u4ec0\u9ebd\u75c5\u3002\u201d\u9ec4\u53f6\u9053\u4eba\u4e0d\u60ef\u8bf4\u8c0e\uff0c\u8baa\u8baa\u8bf4\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u4ec0 \u9ebd\u75c5\u3002\u201d\u6155\u5bb9\u51b2\u9762\u8272\u4e0d\u60a6\uff0c\u9053\uff1a\u201c\u6211\u5174\u5353\u5144\u4e5f\u662f\u719f\u4eba\uff0c\u5343\u91cc\u8fdc\u6765\uff0c\u4f46\u6c42\u4e00\u89c1\u3002\u201d\u9ec4\u53f6\u7ea2\u4e91\u7b54 \u4e0d\u51fa\u8bdd\u3002\u6155\u5bb9\u51b2\u53c8\u9053\uff1a\u201c\u8d35\u6d3e\u662f\u6b66\u6797\u4e2d\u7684\u6cf0\u5c71\u5317\u6597\uff0c\u60f3\u662f\u6211\u6155\u5bb9\u51b2\u4e0d\u914d\u89c1\u8d35\u6d3e\u7684\u638c\u95e8\u4e86\u3002\u201d \u3000\u3000\u6155\u5bb9\u51b2\u662f\u6b66\u6797\u4e2d\u6709\u6570\u7684\u6210\u540d\u4eba\u7269\uff0c\u4f9d\u6b66\u6797\u89c4\u77e9\uff0c\u6210\u540d\u7684\u82f1\u96c4\u6765\u89c1\u638c\u95e8\uff0c\u82e5\u7136\u4e0d\u89c1\uff01\u4fbf\u662f \u4e00\u79cd\u4fae\u8fb1\u3002\u9ec4\u53f6\u6025\u9053\uff1a\u201c\u6155\u5bb9\u5148\u751f\u8a00\u91cd\u4e86\uff0c\u6211\u5c31\u53eb\u4e00\u822a\u51fa\u6765\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u5353\u4e00\u822a\u5728\u865e\u65b0\u57ce\u548c\u803f\u7ecd\u5357\u966a\u540c\u4e4b\u4e0b\uff0c\u6765\u5230\u5ba2\u6bbf\u3002\u6155\u5bb9\u51b2\u89c1\u5353\u4e00\u822a\u6b65\u5c65\u7a33\u5065\uff0c \u9762\u8272\u7ea2\u6da6\uff0c\u7b11\u9053\uff1a\u201c\u5353\u5144\uff0c\u4f60\u597d\uff01\u201d\u5353\u4e00\u822a\u4e0d\u77e5\u6155\u5bb9\u51b2\u5df2\u4e0e\u7389\u7f57\u5239\u548c\u89e3\uff0c\u7741\u773c\u8bf4\u9053\uff1a\u201c\u597d\u5f97 \u5f88\u5440\uff01\u4f60\u6765\u505a\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u6211\u4e00\u6765\u5411\u4f60\u95ee\u5019\uff0c\u4e8c\u6765\u5411\u4f60\u95ee\u7389\u7f57\u5239\u7684\u4e0b\u843d\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u6ee1\u5ea7\u7686\u60ca\uff0c\u5353 \u4e00\u822a\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff01\u201d\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u5353\u5144\u4f11\u5f97\u8bef\u4f1a\u3002\u5c0f\u5f1f\u4e0d\u662f\u5bfb\u4ec7\uff0c\u800c\u662f\u89c5\u5979\u62a5\u6069\u3002\u5979 \u5b9e\u5728\u662f\u4e2a\u81f3\u60c5\u81f3\u6027\uff0c\u6709\u6069\u6709\u4e49\u7684\u5947\u5973\u5b50\u5440\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u4e00\u6014\uff0c\u5ffd\u7136\u75db\u54ed\u5931\u58f0\u3002\u6155\u5bb9\u51b2\u9053\uff1a\u201c\u5353\u5144\u4e5f\u662f\u6027\u60c5\u4e2d\u4eba\uff0c\u4f60\u4eec\u76f8\u7231\u4e4b\u6df1\uff0c\u8be5\u6210\u9e33 \u4fa3\u3002\u9ec4\u53f6\u9053\u5144\uff0c\u6055\u6211\u4e0d\u63e3\u5192\u6627\uff0c\u6211\u8981\u505a\u6708\u8001\u4e86\u3002\u54c8\uff0c\u54c8\uff01\u201d\u9ec4\u53f6\u52c3\u7136\u53d8\u8272\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d \u51c6\u63d0\u8fd9\u4e2a\u6deb\u8d31\u7684\u5973\u9b54\u5934\uff0c\u4e00\u822a\uff0c\u4f60\u56de\u53bb\uff01\u65b0\u57ce\uff0c\u7ecd\u5357\uff0c\u6276\u4ed6\u56de\u53bb\uff01\u201d \u3000\u3000\u6155\u5bb9\u51b2\u662f\u4e2a\u50b2\u5cb8\u4e4b\u4eba\uff0c\u5e73\u751f\u6240\u670d\u8005\u552f\u6709\u94c1\u98de\u9f99\u4e0e\u7389\u7f57\u5239\uff0c\u95fb\u8a00\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u9ec4\u53f6\u9053 \u4eba\uff0c\u4f60\u4fae\u8fb1\u6211\u8fd8\u7f62\u4e86\uff0c\u4f60\u8fd8\u6562\u6c61\u8511\u6211\u7684\u6069\u4eba\uff01\u201d\u547c\u7684\u4e00\u62f3\u6363\u51fa\uff0c\u9ec4\u53f6\u6a2a\u81c2\u4e00\u6321\uff0c\u4e24\u4eba\u5185\u529f\u90fd \u6781\u6df1\u6e5b\uff0c\u53ef\u662f\u6155\u5bb9\u51b2\u529b\u6c14\u8f83\u5927\uff0c\u53cc\u81c2\u4e00\u683c\uff0c\u84ec\u7684\u4e00\u58f0\uff0c\u9ec4\u53f6\u9053\u4eba\u7ed9\u4ed6\u9707\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u6155\u5bb9\u51b2 \u4e5f\u6447\u6447\u6643\u6643\uff0c\u9000\u5f8c\u53c1\u6b65\u3002\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5353\u4e00\u822a\uff0c\u4f60\u4f1a\u54ed\uff0c\u4e0d\u5bb3\u7f9e\u9ebd\uff1f\u7389\u7f57\u5239\u6562\u4f5c\u6562\u4e3a\uff0c\u4f60\u96be \u9053\u5c31\u4e0d\u5982\u4e00\u4e2a\u5973\u5b50\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u6291\u90c1\u6570\u6708\uff0c\u672c\u6765\u5c31\u5982\u4e00\u4e2a\u5c06\u8981\u7206\u53d1\u7684\u706b\u5c71\uff0c\u88ab\u6155\u5bb9\u51b2\u76f4\u8a00\u4e00\u559d\uff0c\u7acb\u523b\u6536\u6cea\uff0c\u5927\u58f0 \u8bf4\u9053\uff1a\u201c\u8bf7\u5e08\u53d4\u539f\u8c05\uff0c\u53e6\u9009\u638c\u95e8\uff0c\u5f1f\u5b50\u53bb\u4e86\uff01\u201d\u9ec4\u53f6\u7ea2\u4e91\u9f50\u58f0\u559d\u9053\uff1a\u201c\u4e0d\u51c6\u53bb\uff01\u201d\u9ec4\u53f6\u53d1\u8eab \u8dc3\u8d77\uff0c\u6155\u5bb9\u51b2\u4e00\u62f3\u4e0a\u51fb\uff0c\u628a\u9ec4\u53f6\u8feb\u9000\u4e0b\u6765\u3002\u7ea2\u4e91\u4f38\u624b\u4e00\u6293\uff0c\u6293\u7740\u4e86\u5353\u4e00\u822a\u80a9\u80cc\uff0c\u7a81\u89c9\u6ed1\u4e0d\u7559 \u624b\uff0c\u5353\u4e00\u822a\u80a9\u5934\u4e00\u6446\uff0c\u5982\u6e38\u9c7c\u822c\u8131\u4e86\u51fa\u53bb\u3002\u539f\u6765\u4ed6\u7684\u5185\u529b\u5df2\u6709\u4e86\u706b\u5019\uff0c\uff0e\u4e0e\u7ea2\u4e91\u5df2\u4e0d\u76f8\u4e0a \u4e0b\uff0c\u7ea2\u4e91\u53c8\u4e0d\u6562\u65bd\u5c55\u6740\u624b\uff0c\u90a3\u6293\u5f97\u4ed6\u7740\u3002\u7ea2\u4e91\u9053\u4eba\u4e3e\u6b65\u8981\u8ffd\u3002\u6155\u5bb9\u51b2\u53c8\u662f\u4e00\u58f0\u5927\u559d\uff0c\u5de6\u638c\u6293 \u4ed6\u80f3\uff0c\u53f3\u811a\u8e22\u4ed6\u4e0b\u76d8\uff0c\u7ea2\u4e91\u9053\u4eba\u80ef\u8eab\u6025\u95ea\uff0c\u6155\u5bb9\u51b2\u5927\u7b11\u9053\uff1a\u201c\u725b\u9f3b\u5b50\u8001\u9053\uff0c\u4f60\u4eec\u4e0d\u51c6\u6211\u505a\u5927 \u5a92\uff0c\u6211\u53ef\u4e0d\u4f9d\uff01\u201d\u9ec4\u53f6\u6251\u6765\uff0c\u6155\u5bb9\u51b2\u62e6\u95e8\u4e00\u7ad9\uff0c\u4f38\u638c\u8e22\u817f\uff0c\u72e0\u6597\u4e8c\u4eba\u3002\u803f\u7ecd\u5357\u4e0e\u865e\u65b0\u57ce\u90a3\u62e6 \u5f97\u7740\u5353\u4e00\u822a\uff0c\u88ab\u4ed6\u5de6\u53f3\u4e00\u63a8\uff0c\u4e24\u4eba\u90fd\u8dcc\u5012\u5730\u4e0a\u3002\u9ec4\u53f6\u5174\u7ea2\u4e91\u66b4\u8e81\u5982\u96f7\uff0c\u53ef\u662f\u6155\u5bb9\u51b2\u53f7\u79f0\u201c\u795e \u62f3\u65e0\u654c\u201d\uff0c\u5728\u62f3\u811a\u4e0a\u7684\u529f\u592b\u6bd4\u4ed6\u4eec\u4fe9\u90fd\u8981\u9ad8\u660e\uff0c\u62e6\u95e8\u4e00\u7ad9\uff0c\u72b9\u5982\u91d1\u521a\u628a\u5173\uff0c\u4e24\u4eba\u51b2\u51fb\u5341\u6570\u56de \u5408\uff0c\u90fd\u51b2\u4e0d\u51fa\u53bb\u3002\u6155\u5bb9\u51b2\u5fd6\u5ea6\u5353\u4e00\u822a\u5df2\u9003\u5230\u5c71\u4e0b\uff0c\u8fd9\u624d\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u725b\u9f3b\u5b50\u8001\u9053\uff0c\u4f60\u7684\u638c\u95e8 \u4eba\u5e74\u7eaa\u4e5f\u4e0d\u5c0f\u5566\uff0c\u4ed6\u53bb\u627e\u5ab3\u5987\u513f\u4f60\u4eec\u4e5f\u8981\u7ba1\u5417\u3002\u54c8\uff0c\u54c8\uff0c\u4e0d\u7528\u64cd\u5fc3\u5566\u3002\u6211\u4e5f\u8981\u53bb\u8d76\u7740\u5403\u559c \u9152\uff0c\u5931\u966a\uff0c\u5931\u966a\uff01\u201d\u9ec4\u53f6\u9053\u4eba\u4e00\u4e2a\u8098\u5e95\u7a7f\u638c\uff0c\u76f4\u63d2\u8fc7\u53bb\uff0c\u7ea2\u4e91\u9053\u4eba\u811a\u8e0f\u4e2d\u5bab\uff0c\u53cc\u62f3\u9f50\u51fa\u3002\u6155 \u5bb9\u51b2\u54c8\u54c8\u5927\u7b11\uff0c\u4e00\u4e2a\u201c\u5367\u864e\u56de\u5934\u201d\u53f3\u62f3\u5411\u5f8c\u731b\u53d1\uff0c\u5c06\u9ec4\u53f6\u9053\u4eba\u683c\u9000\uff0c\u518d\u970d\u5730\u5411\u5f8c\u4e00\u6492\u8eab\uff0c\u53cc \u811a\u8fde\u73af\u98de\u8d77\uff0c\u201c\u5206\u82b1\u62c2\u67f3\u201d\uff0c\u8e22\u7ea2\u4e91\u53cc\u8de8\u3002\u7ea2\u4e91\u6b66\u529f\u7a0d\u4f4e\uff0c\u53ea\u542c\u5f97\u7830\u7830\u4e24\u58f0\uff0c\u88ab\u4ed6\u8e22\u8fc7\u6b63 \u7740\uff0c\u987f\u65f6\u4f3c\u4e00\u4e2a\u76ae\u7403\uff0c\u629b\u8d77\u4e00\u4e08\u591a\u9ad8\uff0c\u201c\u5427\u201d\u7684\u4e00\u58f0\uff0c\u8dcc\u5728\u795e\u5ea7\u4e4b\u4e0b\uff0c\u989d\u5934\u78b0\u8d77\u8001\u5927\u4e00\u4e2a\u7599 \u7629\uff0c\u8fd8\u5e78\u6155\u5bb9\u51b2\u811a\u4e0b\u7559\u60c5\uff0c\u4e0d\u7528\u5168\u529b\uff0c\u8981\u4e0d\u7136\u8fde\u4ed6\u7684\u53cc\u817f\u4e5f\u8981\u626b\u65ad\u3002 \u3000\u3000\u6155\u5bb9\u51b2\u62f1\u624b\u9053\uff1a\u201c\u5f97\u7f6a\uff0c\u5f97\u7f6a\uff01\u5931\u966a\uff0c\u5931\u966a\uff01\u201d\u593a\u95e8\u5954\u51fa\u3002\u7ea2\u4e91\u6c14\u547c\u547c\u7684\u722c\u8d77\u6765\uff0c\u9053\uff1a \u201c\u5e08\u5144\uff0c\u545c\u949f\u51fb\u7f44\uff0c\u805a\u96c6\u95e8\u4eba\uff0c\u8ffd\u8fd9\u51f6\u5f92\u3002\u201d\u9ec4\u53f6\u9053\u4eba\u82e6\u7b11\u9053\uff1a\u201c\u4e0d\u5fc5\u591a\u4e8b\u4e86\u3002\u7ed3\u4e86\u4e00\u4e2a\u51a4 \u5bb6\u8fd8\u4e0d\u591f\u5417\uff0c\u4e0d\u8981\u518d\u7ed3\u4e86\u3002\u201d\u5176\u5b9e\u7ea2\u4e91\u4e5f\u662f\u5728\u6c14\u5934\u4e0a\uff0c\u53e3\u4e0d\u62e9\u8a00\u3002\u7ec6\u60f3\u4e00\u60f3\uff1a\u767d\u77f3\u9752\u8d1f\u4f24\u672a \u6108\uff0c\u81ea\u5df1\u548c\u5e08\u5144\u4e0d\u662f\u4eba\u5bb6\u5bf9\u624b\uff0c\u4f17\u5f1f\u5b50\u66f4\u4e0d\u7528\u8bf4\u4e86\uff0c\u51ed\u4ec0\u9ebd\u53ef\u62e6\u622a\u6155\u5bb9\u51b2\u3002 \u3000\u3000\u9ec4\u53f6\u9053\uff1a\u201c\u6155\u5bb9\u51b2\u6211\u4eec\u4e0d\u5fc5\u7406\u4ed6\uff0c\u5353\u4e00\u822a\u53ef\u8981\u5bfb\u56de\u3002\u6211\u8fd1\u6765\u8d8a\u60f3\u8d8a\u5fc3\u5bd2\uff0c\u6b66\u5f53\u6d3e\u82e5\u4e0d\u80fd \u627e\u5230\u4e00\u4e2a\u6709\u80fd\u4e3a\u7684\u638c\u95e8\uff0c\u632f\u4f5c\u4e00\u756a\uff0c\u53ea\u6050\u518d\u8fc7\u6570\u5e74\uff0c\u6b66\u5f53\u6d3e\u7684\u540d\u53f7\u66f4\u53eb\u4e0d\u54cd\u4e86\u3002\u201d\u53ef\u662f\u5353\u4e00 \u822a\u4e00\u8d70\uff0c\u6709\u5982\u9c7c\u8dc3\u6df1\u6e0a\uff0c\u9e70\u98de\u5929\u5916\uff0c\u90a3\u91cc\u8fd8\u80fd\u627e\u5f97\u7740\u4ed6\u3002 \u3000\u3000\u518d\u8bf4\u94c1\u98de\u9f99\u548c\u5ba2\u5a09\u5a77\u56de\u5230\u5c71\u897f\u9f99\u95e8\u6545\u5c45\uff0c\u65e5\u5915\u76fc\u671b\u7389\u7f57\u5239\u80fd\u548c\u5353\u4e00\u822a\u540c\u6765\uff0c\u4e00\u76f4\u8fc7\u4e86\u6570 \u6708\uff0c\u65f6\u5e8f\u5df2\u4ece\u521d\u79cb\u8f6c\u5165\u5bd2\u51ac\uff0c\u7389\u7f57\u5239\u4ecd\u662f\u8fde\u4fe1\u606f\u4e5f\u65e0\u4e00\u4e2a\u3002\u5ba2\u5a09\u5a77\u751a\u4e3a\u7126\u6025\uff0c\u9053\uff1a\u201c\u83ab\u975e\u5979 \u7ed9\u6b66\u5f53\u5c71\u90a3\u7fa4\u9053\u58eb\u5bb3\u4e86\uff1f\u201d\u94c1\u98de\u9f99\u7b11\u9053\uff1a\u201c\u90a3\u4e0d\u81f3\u65bc\uff0c\u6211\u6015\u7684\u662f\u5353\u4e00\u822a\u53d8\u4e86\u5fc3\u4e86\u3002\u201d\u5ba2\u5a09\u5a77 \u9053\uff1a\u201c\u6765\u6625\u6211\u4eec\u5230\u6b66\u5f53\u5c71\u63a2\u671b\u6d88\u606f\u5427\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u7389\u7f57\u5239\u4e0e\u6211\u5982\u540c\u7236\u5973\uff0c\u4e0e\u4f60\u4ea6\u5982\u59d0 \u59b9\uff0c\u4ee5\u5979\u7684\u6027\u5b50\uff0c\u5373\u7b97\u5931\u610f\u60c5\u573a\uff0c\u4e5f\u65ad\u4e0d\u4f1a\u81ea\u5bfb\u77ed\u89c1\u3002\u6211\u770b\u5979\u8fdf\u65e9\u90fd\u4f1a\u56de\u6765\u3002\u201d\u53ef\u662f\u65e5\u8fc7\u4e00 \u65e5\uff0c\u7389\u7f57\u5239\u4ecd\u4e0d\u56de\u6765\u3002\u5ba2\u5a09\u5a77\u4fee\u4e60\u7ea2\u82b1\u9b3c\u6bcd\u7684\u6b66\u529f\u79d8\u7b08\uff0c\u9887\u6709\u8fdb\u5883\u3002\u4e00\u665a\uff0c\u591c\u8fc7\u53c1\u66f4\uff0c\u5ba2\u5a09 \u5a77\u5348\u591c\u68a6\u56de\uff0c\u5ffd\u89c1\u7a97\u53e3\u4f38\u8fdb\u4e00\u4e2a\u5934\u6765\uff0c\u767d\u53d1\u62ab\u80a9\uff0c\u9762\u8272\u60e8\u767d\uff0c\u773c\u775b\u95ea\u70c1\uff0c\u6709\u5982\u706b\uff0c\u5ba2\u5a09\u5a77\u5413 \u5f97\u9b42\u4e0d\u9644\u4f53\uff0c\u5927\u53eb\u201c\u6709\u9b3c\u5440\uff01\u201d\u90a3\u4eba\u5934\u6025\u5fd9\u7f29\u51fa\u3002 \u3000\u3000\u94c1\u98de\u9f99\u95fb\u58f0\u60ca\u8d77\uff0c\u63a8\u7a97\u4e00\u671b\uff0c\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u53ef\u662f\u94c1\u98de\u9f99\u4e45\u5386\u6c5f\u6e56\uff0c\u5230\u5e95\u80c6\u5927\uff0c\u4ed4\u7ec6\u4e00 \u770b\uff0c\u90a3\u767d\u53d1\u62ab\u80a9\u7684\u201c\u5973\u9b3c\u201d\u5411\u4ed6\u62dc\u4e86\u4e24\u62dc\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002\u94c1\u98de\u9f99\u5927\u53eb\u9053\uff1a\u201c\u88f3\u513f\uff0c\u56de\u6765\uff01\u5a09 \u5a77\uff0c\u5feb\u51fa\u6765\u63a5\u4f60\u59d0\u59d0\uff01\u201d\u5ba2\u5a09\u5a77\u62ab\u8863\u51b2\u51fa\uff0c\u90a3\u767d\u53d1\u5973\u4eba\u5df2\u98de\u51fa\u5c4b\u5916\uff0c\u94c1\u98de\u9f99\u548c\u5ba2\u5a09\u5a77\u6025\u5fd9\u8ffd \u51fa\uff0c\u4e00\u4e2a\u53eb\u9053\uff1a\u201c\u88f3\u513f\u56de\u6765\uff01\u201d\u4e00\u4e2a\u53eb\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u56de\u6765\uff01\u201d\u90a3\u56e2\u767d\u5f71\u7a81\u56de\u8eab\u8bf4\u9053\uff1a\u201c\u5a77 \u59b9\uff0c\u6211\u4e0d\u662f\u6709\u610f\u5413\u4f60\u3002\u201d\u5a09\u5a77\u9053\uff1a\u201c\u6211\u4e0d\u6015\uff0c\u4f60\u5c31\u662f\u771f\u7684\u53d8\u4e86\u5973\u9b3c\uff0c\u6211\u4e5f\u4e0d\u6015\uff01\u201d\u90a3\u767d\u5f71\u7eed \u9053\uff1a\u201c\u4f60\u8981\u597d\u597d\u7167\u987e\u7239\uff0c\u6709\u4f60\u4f3a\u5019\u4ed6\u8001\u4eba\u5bb6\uff0c\u6211\u4e0d\u7528\u62c5\u5fc3\u4e86\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u56de\u6765\u5427\u3002\u201d \u90a3\u767d\u5f71\u8f6c\u8eab\u53c8\u62dc\u4e86\u4e24\u62dc\uff0c\u9053\uff1a\u201c\u7239\uff0c\u4f60\u81ea\u5df1\u4fdd\u91cd\u3002\u6211\u8fd8\u4e86\u6211\u5e08\u7236\u5fc3\u613f\uff0c\u4e5f\u8981\u53bb\u8df5\u5cb3\u9e23\u73c2\u4e4b\u7ea6 \u4e86\uff01\u201d\u8f6c\u8eab\u75be\u8d70\uff0c\u521d\u89c1\u8fd8\u89c1\u96ea\u5730\u4e0a\u4e00\u56e2\u767d\u5f71\u6eda\u52a8\uff0c\u6e10\u6e10\u4eba\u96ea\u4e0d\u5206\uff0c\u4f46\u89c1\u7691\u7691\u8352\u539f\uff0c\u661f\u6597\u660e \u706d\uff0c\u7389\u7f57\u5239\u5df2\u53bb\u5f97\u8fdc\u4e86\uff01 \u3000\u3000\u94c1\u98de\u9f99\u9eef\u7136\u56de\u5c4b\uff0c\u5ba2\u5a09\u5a77\u6cea\u6d41\u6ee1\u9762\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\u600e\u9ebd\u5f04\u6210\u8fd9\u9ebd\u6837\u5b50\uff1f\u53ef\u60dc\u5979\u7edd\u4e16\u5bb9\u989c\uff0c \u672a\u8001\u767d\u5934\u3002\u5979\u4e5f\u771f\u5fcd\u5fc3\uff0e\u4e3a\u4ec0\u9ebd\u4e0d\u80af\u548c\u6211\u4eec\u540c\u4f4f\uff1f\u201d\u94c1\u98de\u9f99\u53f9\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u5353\u4e00\u822a\u53d8\u4e86\u5fc3 \u4e86\u3002\u4f24\u5fc3\u6613\u8001\uff0c\u4f0d\u5b50\u80e5\u8fc7\u662d\u5173\u4e00\u591c\u767d\u5934\uff0c\u5fe7\u80fd\u4f24\u4eba\uff0c\u6709\u5982\u6b64\u8005\u3002\u4f60\u59d0\u59d0\u7d20\u6765\u7231\u60dc\u5bb9\u989c\uff0c\u542c\u5979 \u53e3\u6c14\uff0c\u4e00\u5b9a\u8981\u5230\u8352\u6f20\u7a77\u8fb9\u4e4b\u5730\uff0c\u6f5c\u5fc3\u7ec3\u5251\uff0e\u518d\u4e0d\u89c1\u4e16\u4fd7\u4e4b\u4eba\u4e86\u3002\u201d\u4e24\u7236\u5973\u5401\u55df\u53f9\u606f\uff0c\u4e45\u4e45\u4e0d \u80fd\u5165\u7761\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u5a09\u5a77\u4ecd\u662f\u90c1\u90c1\u4e0d\u4e50\uff0c\u4e00\u4e2a\u4eba\u5230\u6751\u5916\u6563\u6b65\uff0c\u5ffd\u95fb\u5f97\u8fdc\u5904\u9a6c\u94c3\u53ee\u5f53\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u4e00\u5339 \u9a6c\u75be\u9a70\u800c\u6765\uff0c\u9a6c\u4e0a\u4eba\u8840\u6d41\u6ee1\u9762\uff0c\u51b2\u5230\u5979\u7684\u773c\u524d\uff0c\u5ffd\u7136\u8dcc\u843d\u9a6c\u80cc\uff0c\u90a3\u5339\u9a6c\u8eab\u4e0a\u63d2\u6709\u51e0\u679d\u7fbd\u7bad\uff0c \u9a91\u5ba2\u8dcc\u5730\uff0c\u9a6c\u5636\u4e00\u58f0\uff0c\u53d1\u8e44\u75be\u8d70\uff0c\u5ba2\u5a09\u5a77\u5c06\u90a3\u4eba\u6276\u8d77\uff0c\u662f\u4e00\u4e2a\u6d53\u7709\u5927\u773c\u7684\u5c11\u5e74\uff0c\u5c11\u5e74\u95ee\u9053 \u201c\u8fd9\u91cc\u662f\u9f99\u95e8\u94c1\u5bb6\u5e84\u5417\uff1f\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u662f\u5440\uff0c\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u5feb\u6551\u6211\u4e00\u6551\u3002\u201d \u6b63\u662f\uff1a\u8352\u6751\u6765\u5f02\u5ba2\uff0c\u5e73\u5730\u8d77\u6ce2\u6f9c\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u4e03\u56de\u3000\u65e0\u610f\u7559\u540d\u3000\u5c11\u5e74\u6c42\u5e87\u62a4\u3000\u5fcf\u60c5\u9057\u6068\u3000\u516c\u5b50\u82e6\u76f8\u5bfb \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377 \u7b2c\u4e8c\u5341\u4e03\u56de \u65e0\u610f\u7559\u540d\u3000\u5c11\u5e74\u6c42\u5e87\u62a4 \u5fcf\u60c5\u9057\u6068\u3000\u516c\u5b50\u82e6\u76f8\u5bfb \u3000\u3000\u90a3\u5c11\u5e74\u8eab\u53d7\u91cd\u4f24\uff0c\u75b2\u5026\u4e0d\u582a\uff0c\u8dcc\u4e0b\u9a6c\u5f8c\uff0c\u722c\u4e0d\u8d77\u6765\u3002\u5ba2\u5a09\u5a77\u5c06\u4ed6\u6276\u8d77\u95ee\u9053\uff1a\u201c\u4f60\u5230\u5e95\u662f \u4ec0\u9ebd\u4eba\uff1f\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u599e\u513f\u597d\u7f57\u5cfb\uff0c\u4f60\u613f\u6551\u6211\uff0c\u5c31\u5feb\u628a\u94c1\u98de\u9f99\u53eb\u51fa\u6765\uff0c\u4f60\u82e5\u4e0d\u613f\u6551 \u6211\uff0c\u5c31\u8bf7\u5c06\u6211\u8eab\u4e0a\u7684\u4f69\u5200\u62d4\u51fa\u6765\u7ed9\u6211\uff01\u201d\u5ba2\u5a09\u5a77\u4e0d\u77e5\u4ed6\u662f\u4ec0\u9ebd\u6765\u5386\u3002\u672c\u60f3\u95ee\u4e2a\u6e05\u695a\uff0c\u5982\u4eca\u770b \u51fa\u4ed6\u53d7\u4e86\u91cd\u4f24\uff0c\u601c\u60af\u4e4b\u5fc3\u6cb9\u7136\u800c\u751f\u3002 \u3000\u3000\u6751\u5b50\u5916\u9a6c\u94c3\u4e4b\u58f0\u53c8\u9690\u9690\u4f20\u6765\uff0c\u5c11\u5e74\u53eb\u9053\uff1a\u201c\u6765\u4e0d\u53ca\u4e86\uff0c\u628a\u4f69\u5200\u7ed9\u6211\uff01\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u4f60 \u8981\u5b83\u505a\u4ec0\u9ebd\uff1f\u201d\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u6211\u5b81\u6b7b\u4e5f\u4e0d\u843d\u5728\u5978\u4eba\u4e4b\u624b\uff01\u201d\u5ba2\u5a09\u5a77\u5fc3\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u76f4\u7387\u53ef \u559c\uff0c\u800c\u4e14\u5b81\u6b7b\u4e0d\u8fb1\uff0c\u770b\u6765\u4e0d\u662f\u574f\u4eba\u3002\u201d\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211\u6551\u4f60\uff01\u201d\u9a6c\u8e44\u58f0\u6765\u5f97\u66f4\u8fd1\u3002\u5ba2\u5a09 \u5a77\u5c06\u90a3\u5c11\u5e74\u4e00\u628a\u62b1\u8d77\uff0c\u653e\u5728\u8def\u65c1\u9ea6\u7530\u91cc\u7684\u4e00\u4e2a\u67af\u8349\u5806\u4e2d\u3002\u5ba2\u5a09\u5a77\u4e00\u751f\u4ece\u672a\u8fd9\u6837\u63a5\u89e6\u8fc7\u7537\u5b50\uff0c \u90a3\u5c11\u5e74\u8eab\u5b50\u53c8\u91cd\uff0c\u538b\u5f97\u5979\u80f8\u53e3\u900f\u4e0d\u8fc7\u6c14\u3002\u597d\u4e0d\u5bb9\u6613\u5c06\u4ed6\u63a9\u85cf\u597d\u4e86\uff0c\u8ffd\u5175\u5df2\u8fdb\u5165\u6751\u53e3\u3002\u5ba2\u5a09\u5a77 \u4e5f\u7b97\u7cbe\u7ec6\uff0c\u6025\u628a\u5916\u8863\u8131\u4e0b\uff0c\u585e\u5165\u8349\u5806\uff0c\u53ea\u624b\u5728\u6ce5\u571f\u4e0a\u4e00\u62b9\uff0c\u628a\u8840\u8ff9\u6df7\u5408\u3002 \u3000\u3000\u7247\u523b\u4e4b\u5f8c\uff0c\u8ffd\u5175\u5df2\u5230\uff0c\u6765\u7684\u662f\u4e94\u540d\u9a91\u5ba2\uff0c\u597d\u50cf\u662f\u516c\u5dee\u7684\u6837\u5b50\uff0c\u4e3a\u9996\u7684\u95ee\u9053\uff1a\u201c\u5582\uff0c\u5c0f\u59d1 \u5a18\uff0c\u4f60\u53ef\u89c1\u6709\u4e00\u4e2a\u53d7\u4f24\u7684\u5c11\u5e74\uff0c\u9a91\u9a6c\u5728\u8fd9\u91cc\u7ecf\u8fc7\u5417\uff1f\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u89c1\u7740\u7684\uff01\u4ed6\u5411\u524d\u9762\u8dd1 \u4e86\uff1f\u201d\u4e00\u624b\u6307\u94c1\u5bb6\u5e84\u7684\u65b9\u5411\u3002\u5c11\u5e74\u90a3\u5339\u9a6c\uff0c\u672c\u6765\u53d7\u4e86\u597d\u51e0\u5904\u7bad\u4f24\uff0c\u6cbf\u9014\u6ef4\u4e0b\u9a6c\u8840\u3002\u90a3\u51e0\u540d\u9a91 \u5ba2\u770b\u4e86\u4e00\u9635\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u524d\u9762\u662f\u94c1\u5bb6\u5e84\u5417\uff1f\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u90a3\u5c11\u5e74\u8fdb\u5165\u94c1\u5bb6\u5e84 \u4e86\u3002\u201d \u3000\u3000\u4e94\u540d\u9a91\u5ba2\u4e00\u9f50\u4e0b\u9a6c\uff0c\u4ea4\u5934\u63a5\u8033\u5546\u8bae\u4e00\u9635\uff0c\u4e00\u4eba\u9053\uff1a\u201c\u94c1\u98de\u9f99\u813e\u6c14\u53e4\u602a\uff0c\u4e0d\u80fd\u95ee\u4ed6\u786c \u8981\u3002\u201d\u4e00\u4eba\u9053\uff1a\u201c\u6211\u4eec\u4e94\u5144\u5f1f\u96be\u9053\u6597\u4ed6\u4e0d\u8fc7\u3002\u54b1\u4eec\u5148\u793c\u5f8c\u5175\uff0c\u53e9\u5e84\u7d22\u4eba\u3002\u201d\u53c8\u4e00\u4eba\u6447\u4e86\u6447 \u5934\uff0c\u8868\u793a\u5f88\u4e0d\u540c\u610f\u3002\u8fd9\u51e0\u4eba\u5546\u8bae\u4e4b\u65f6\uff0c\u5ba2\u5a09\u5a77\u7ad9\u5728\u8def\u8fb9\uff0c\u51dd\u795e\u9759\u542c\uff0c\u76ee\u4e0d\u8f6c\u77ac\u3002 \u3000\u3000\u4e00\u540d\u9a91\u5ba2\u7a81\u7136\u5982\u6709\u6240\u609f\uff0c\u8fc8\u524d\u4e24\u6b65\uff0c\u6840\u6840\u7b11\u9053\uff1a\u201c\u5582\uff0c\u4f60\u662f\u4ec0\u9ebd\u4eba\uff1f\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u6211 \u662f\u519c\u5bb6\u5973\u5b50\uff0c\u4e00\u65e9\u51fa\u6765\u62fe\u8349\u7684\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u94c1\u5bb6\u5e84\u91cc\u7684\u5417\uff1f\u201d\u5ba2\u5a09\u5a77\u7b54\u9053\uff1a\u201c\u6211\u662f \u9644\u8fd1\u6751\u5b50\u7684\u3002\u201d\u5ba2\u5a09\u5a77\u81ea\u5230\u4e86\u94c1\u5bb6\u5e84\u5f8c\uff0c\u6d17\u51c0\u94c5\u534e\uff0c\u6539\u6210\u6751\u5973\u6253\u626e\uff0c\u4fca\u4fcf\u7684\u8138\u4e0a\u53c8\u6709\u6ce5\u6c61\uff0c \u8c01\u4e5f\u60f3\u4e0d\u5230\u5979\u5728\u4e0d\u4e45\u4e4b\u524d\uff0c\u8fd8\u662f\u4e00\u4e2a\u6bd4\u516c\u4e3b\u66f4\u534e\u8d35\u7684\u5973\u4eba\u3002 \u3000\u3000\u53ef\u662f\u8fd9\u540d\u9a91\u5ba2\u6c5f\u6e56\u9605\u5386\u751a\u6df1\uff0c\u770b\u4e86\u4e00\u9635\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u8dd1\u904d\u5929\u5357\u5730\u5317\uff0c\u51e0\u4e4e\u7ed9\u8fd9\u5c0f \u599e\u513f\u8499\u9a97\u8fc7\u53bb\u3002\u6765\uff0c\u4f60\u4eec\u77a7\u2014\u2014\u201d\u4f38\u624b\u4e00\u6307\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u77a7\uff0c\u5979\u9762\u6709\u6ce5\u6c61\uff0c\u8eab\u4e0a\u8fd9\u4ef6\u7d27\u8eab \u68c9\u6fb3\uff0c\u53ef\u5149\u9c9c\u5f97\u5f88\u54e9\uff01\u8bf4\u8bdd\u53c8\u8fd9\u6837\u6e05\u695a\u4fd0\u843d\uff0c\u90a3\u91cc\u662f\u4ec0\u9ebd\u519c\u5bb6\u5973\u513f\uff01\u201d \u3000\u3000\u5ba2\u5a09\u5a77\u5fc3\u4e2d\u4e00\u9707\uff0c\u53ea\u542c\u5f97\u90a3\u4eba\u559d\u9053\uff1a\u201c\u5feb\u8bf4\uff0c\u4f60\u628a\u4ed6\u85cf\u5230\u90a3\u91cc\u53bb\u4e86\uff1f\u4ed6\u662f\u4e07\u6076\u4e0d\u8d66\u7684\u5f3a \u76d7\uff0c\u4f60\u6562\u628a\u4ed6\u6536\u85cf\uff0c\u4f60\u7684\u5c0f\u547d\u8fd8\u60f3\u8981\u5417\uff1f\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u4ec0\u9ebd\u5f3a\u76d7\uff0c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u90a3\u4eba\u5927\u559d \u4e00\u58f0\uff0c\u4e0a\u524d\u8981\u6349\u5ba2\u5a09\u5a77\u3002\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u4e0d\u53ef\u9020\u6b21\uff0c\u95ee\u5979\u662f\u94c1\u98de\u9f99\u7684\u4ec0\u9ebd\u4eba\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u94c1 \u98de\u9f99\u7684\u5973\u513f\u65e9\u5df2\u6b7b\u4e86\uff0c\u53c8\u6ca1\u6536\u6709\u5973\u5f92\u5f1f\uff0c\u6211\u6599\u5979\u662f\u76d7\u515a\uff01\u201d\u811a\u6b65\u4e0d\u505c\uff0c\u4f38\u624b\u4fbf\u6293\uff01 \u3000\u3000\u5ba2\u5a09\u5a77\u56de\u8eab\u4e00\u95ea\uff0c\u90a3\u4eba\u53eb\u9053\uff1a\u201c\u5413\uff0c\u597d\u5feb\uff0c\u597d\u4fca\u7684\u8eab\u6cd5\uff0c\u5c45\u7136\u662f\u4f1a\u5bb6\u5b50\u5462\uff01\u201d\u5ba2\u5a09\u5a77\u8fd9 \u4e00\u51fa\u624b\uff0c\u4e94\u540d\u9a91\u5ba2\u5168\u90fd\u52a8\u5bb9\uff0c\u77e5\u9053\u5979\u7edd\u4e0d\u662f\u4ec0\u9ebd\u666e\u901a\u7684\u519c\u5bb6\u59d1\u5a18\u4e86\u3002 \u3000\u3000\u548c\u5ba2\u5a09\u5a77\u52a8\u624b\u7684\u90a3\u540d\u9a91\u5ba2\u6b66\u529f\u751a\u662f\u4e0d\u5f31\uff0c\u4f7f\u7684\u662f\u5317\u6d3e\u5288\u6302\u638c\uff0c\u624b\u811a\u8d77\u5904\uff0c\u5168\u5e26\u52b2\u98ce\u3002\u53ef \u662f\u5ba2\u5a09\u5a77\u5f97\u7684\u662f\u7ea2\u82b1\u9b3c\u6bcd\u7684\u771f\u4f20\uff0c\u7ea2\u82b1\u9b3c\u6bcd\u5f53\u5e74\u4ee5\u4e00\u62d0\u53cc\u638c\uff0c\u7eb5\u6a2a\u6c5f\u6e56\uff0c\u6b66\u529f\u975e\u540c\u5c0f\u9119\uff0c\u638c \u6cd5\u521a\u67d4\u5e76\u6d4e\uff0c\u52b2\u529b\u5185\u85cf\uff0c\u5389\u5bb3\u4e4b\u6781\u3002\u5ba2\u5a09\u5a77\u867d\u7136\u706b\u5019\u672a\u5230\uff0c\u53ef\u662f\u638c\u6cd5\u4f7f\u5f00\uff0c\u56de\u73af\u6eda\u65ab\uff0c\u90a3\u540d \u9a91\u5ba2\u5df2\u662f\u5e94\u4ed8\u4e3a\u96be\u3002 \u3000\u3000\u89c2\u6218\u7684\u4e00\u540d\u9a91\u5ba2\u9053\uff1a\u201c\u8fd9\u5c0f\u599e\u513f\u51c6\u662f\u76d7\u515a\u65e0\u7591\uff0c\u54b1\u4eec\u4e0a\u554a\u3002\u201d\u8fd9\u4e94\u540d\u9a91\u5ba2\u90fd\u662f\u897f\u603b\u7763\u9648 \u5947\u745c\u5e10\u4e0b\u7684\u6b66\u58eb\uff0c\u5949\u547d\u8ffd\u8e2a\u90a3\u4e2a\u5c11\u5e74\u7684\u3002\u53ef\u662f\u8fd9\u4e94\u540d\u6b66\u58eb\u7684\u6765\u5386\u53c8\u6709\u4e0d\u540c\uff0c\u5176\u4e2d\u53c1\u540d\u539f\u662f\u897f \u7684\u76d7\u9996\uff0c\u88ab\u9648\u5947\u745c\u62db\u5b89\u8fc7\u53bb\u7684\u3002\u53e6\u4e24\u540d\u5219\u662f\u4e1c\u5382\u7684\u6869\u5934\uff0c\u5916\u8c03\u5230\u897f\u603b\u7763\u519b\u4e2d\uff0c\u534f\u52a9\u7f09\u532a\u7684\u3002 \u3000\u3000\u548c\u5ba2\u5a09\u5a77\u52a8\u624b\u7684\u8fd9\u4eba\uff0c\u4fbf\u662f\u53d7\u62db\u5b89\u7684\u76d7\u9996\u4e4b\u4e00\uff0c\u548c\u4ed6\u540c\u53d7\u62db\u5b89\u7684\u4e24\u4e2a\u540c\u4f34\u89c1\u72b6\u4e0d\u4f73\uff0c\u62d4 \u4e86\u5175\u5668\uff0c\u53cc\u53cc\u8dc3\u51fa\uff0c\u90a3\u4e24\u540d\u4e1c\u5382\u6869\u5934\uff0c\u77a7\u4e86\u4e00\u9635\uff0c\u5374\u51dd\u8eab\u4e0d\u52a8\uff0c\u5f7c\u6b64\u5bf9\u89c6\uff0c\u9762\u6709\u8be7\u5f02\u4e4b\u5bb9\u3002 \u3000\u3000\u5ba2\u5a09\u5a77\u72ec\u6218\u53c1\u540d\u6b66\u58eb\uff0c\u5374\u4e5f\u4e0d\u60e7\uff0c\u53cc\u638c\u4ea4\u9519\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u51cc\u5389\u4e4b\u4e2d\u89c1\u7ef5\u5bc6\uff0c \u65ab\u622a\u4e4b\u4e2d\u6742\u70b9\u7a74\uff0c\u53c1\u540d\u6b66\u58eb\uff0c\u62da\u529b\u56f4\u653b\uff0c\u53c8\u6597\u4e86\u4e94\u4e03\u5341\u62db\uff0c\u662f\u672a\u5206\u80dc\u8d1f\u3002 \u3000\u3000\u53ef\u662f\u5ba2\u5a09\u5a77\u7a76\u7adf\u662f\u521d\u51fa\u9053\u7684\u96cf\u513f\uff0c\u4e45\u6218\u4e0d\u4e0b\uff0c\u6c14\u529b\u4e0d\u652f\uff0c\u638c\u6cd5\u8f6c\u4e71\uff0c\u654c\u4eba\u56f4\u653b\u66f4\u7d27\uff0c\u4e00 \u5200\u4e00\u97ad\u53cc\u638c\uff0c\u914d\u5408\u547c\u5e94\uff0c\u7740\u7740\u8fdb\u8feb\u3002\u5ba2\u5a09\u5a77\u6c57\u900f\u8863\u88f3\uff0c\u9762\u4e0a\u7684\u6ce5\u6c61\uff0c\u4e5f\u7ed9\u6c57\u6c34\u51b2\u6389\u4e86\u3002 \u3000\u3000\u6fc0\u621f\u4e2d\u5ba2\u5a09\u5a77\u4e00\u4e2a\u758f\u795e\uff0c\u51b7\u4e0d\u9632\u7ed9\u654c\u4eba\u7684\u97ad\u68a2\u5728\u80a9\u5934\u4e0a\u626b\u4e86\u4e00\u4e0b\uff0c\u75db\u5f97\u201c\u54ce\u5537\u201d\u4e00\u58f0\u53eb \u4e86\u8d77\u6765\uff0c\u539f\u662f\u52a8\u624b\u7684\u90a3\u540d\u654c\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5973\u532a\u8fd8\u4e0d\u964d\u987a\uff1f\u5feb\u5feb\u62db\u4f9b\uff01\u201d\u5ba2\u5a09\u5a77\u53eb\u9053\uff1a \u201c\u7239\uff0c\u5feb\u6765\u554a\uff01\u6709\u4eba\u6b3a\u8d1f\u4f60\u7684\u5973\u513f\u5440\uff01\u201d\u90a3\u53c1\u540d\u6b66\u58eb\u6014\u4e86\u4e00\u6014\uff0c\u559d\u9053\uff1a\u201c\u94c1\u98de\u9f99\u662f\u4f60\u7684\u4ec0\u9ebd \u4eba\uff1f\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u662f\u6211\u7684\u7239\uff0c\u600e\u9ebd\u6837\uff1f\u201d\u53c1\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u9f50\u9053\uff1a\u201c\u4f60\u8fd8\u6765\u8499\u6df7\u6211\u4eec\uff0c\u4f60\u60f3 \u5413\u552c\u6211\u4eec\uff0c\u771f\u662f\u7b11\u8bdd\uff01\u201d\u56f4\u653b\u66f4\u7d27\uff01 \u3000\u3000\u8349\u5806\u91cc\u5ffd\u7136\u6089\u6089\u7d22\u7d22\u7684\u4e71\u54cd\uff0c\u90a3\u53d7\u4f24\u7684\u5c11\u5e74\u722c\u4e86\u51fa\u6765\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u5173\u5979\u7684\u4e8b\uff0c\u6211\u5728 \u8fd9\u513f\uff0c\u4f60\u4eec\u5c06\u6211\u5e26\u53bb\uff0c\u628a\u5979\u653e\u5f00\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u5927\u51fa\u4f17\u4eba\u610f\u5916\uff0c\u90a3\u53c1\u540d\u6b66\u58eb\u53d1\u4e00\u58f0\u558a\uff0c\u820d\u4e86\u5ba2\u5a09\u5a77\uff0c\u4e0a\u524d\u6355\u6349\u201c\u6b63\u70b9\u201d\uff0c\u5ba2\u5a09 \u5a77\u5446\u4e86\u4e00\u5446\uff0c\u5ffd\u5730\u91cc\u53c8\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5bab\u4e3b\u5417\uff1f\u5582\uff0c\u9f99\u8001\u4e8c\uff0c\u4e14\u6162\u52a8\u624b\uff0c\u8fd9\u4f4d\u59d1\u5a18 \u662f\u5949\u5723\u592b\u4eba\u7684\u5343\u91d1\uff01\u201d \u3000\u3000\u8fd9\u4e24\u4e2a\u4e1c\u5382\u6869\u5934\uff0c\u5916\u8c03\u4e4b\u524d\uff0c\u66fe\u5728\u5185\u5ead\u6267\u5f79\uff0c\u90a3\u65f6\u5ba2\u5a09\u5a77\u5728\u5bab\u4e2d\u5c0a\u8363\u4e4b\u6781\uff0c\u4e24\u4eba\u804c\u4f4d\u4f4e \u5fae\uff0c\u8fd8\u6ca1\u8d44\u683c\u548c\u5979\u4eb2\u8fd1\u3002\u4f46\u867d\u7136\u5982\u6b64\uff0c\u4ed6\u4eec\u4e5f\u66fe\u89c1\u8fc7\u51e0\u6b21\u3002\u9002\u624d\u521d\u9047\u4e4b\u65f6\uff0c\u4ed6\u4eec\u4e07\u6599\u4e0d\u5230\u5ba2 \u5a09\u5a77\u4fbf\u662f\u8fd9\u4e2a\u6751\u5973\uff0c\u5f8c\u6765\u6c57\u6c34\u51b2\u6389\u4e86\u5ba2\u5a09\u5a77\u9762\u4e0a\u7684\u6ce5\u6c61\uff0c\u4ed6\u4eec\u624d\u8ba4\u5f97\u51fa\u6765\u3002\u8d76\u5fd9\u5927\u53eb\u201c\u5bab \u4e3b\u201d\uff01 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u90a3\u53c1\u4e2a\u548c\u5ba2\u5a09\u5a77\u5bf9\u654c\u7684\u4eba\u5403\u60ca\u4e0d\u5c11\uff0c\u6536\u4e86\u5175\u5668\uff0c\u5413\u5f97\u5446\u4e86\u3002\u90a3\u53d7\u4f24\u7684\u5c11\u5e74\u4e5f\u6781 \u4e3a\u60ca\u5947\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u4ec0\u9ebd\uff0c\u4f60\u662f\u5ba2\u6c0f\u7684\u5973\u513f\uff0c\u4f60\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u6551\u6211\uff1f\u6211\u4e0d\u9886\u4f60\u7684 \u60c5\uff0c\u4f60\u4eec\u628a\u6211\u62ff\u53bb\uff01\u201d \u3000\u3000\u5ba2\u5a09\u5a77\u5fc3\u75db\u5982\u7ede\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u6c5f\u6e56\u4e0a\u7684\u597d\u6c49\uff0c\u5982\u6b64\u618e\u6068\u6211\u7684\u6bcd\u4eb2\u3002\u201d\u90a3\u4e24\u4e2a\u4e1c\u5382\u6869\u5934 \u65bd\u4e86\u4e00\u793c\uff0c\u606d\u656c\u8bf4\u9053\uff1a\u201c\u5bab\u4e3b\uff0c\u8fd9\u4eba\u662f\u548c\u671d\u5ef7\u4f5c\u5bf9\u7684\u53db\u5f92\uff0c\u662f\u9b4f\u5b97\u4e3b\u6240\u8981\u6355\u6349\u7684\u72af\u4eba\uff0c\u8bf7\u4f60 \u5c06\u4ed6\u4ea4\u7ed9\u6211\u4eec\u5e26\u56de\uff01\u201d\u5ba2\u5a09\u5a77\u65a5\u9053\uff1a\u201c\u6eda\u5f00\uff0c\u8fd9\u4eba\u6211\u7559\u4e0b\u4e86\uff0c\u4f60\u4eec\u8981\u4eba\uff0c\u53eb\u9b4f\u5fe0\u8d24\u4eb2\u81ea\u6765 \u8981\uff01\u201d \u3000\u3000\u90a3\u5148\u524d\u548c\u5ba2\u5a09\u5a77\u5bf9\u654c\u7684\u53c1\u4eba\u60ca\u9b42\u7a0d\u5b9a\uff0c\u4e0d\u7ea6\u800c\u540c\u60f3\u9053\uff1a\u201c\u8fd9\u56de\u7cdf\u4e86\uff0c\u5979\u662f\u5ba2\u6c0f\u7684\u5b9d\u8d1d\u5973 \u513f\uff0c\u4eca\u6b21\u88ab\u6211\u4eec\u6240\u4f24\uff0c\u56de\u5bab\u4e00\u8bf4\uff0c\u6211\u4eec\u6b7b\u7f6a\u96be\u9976\uff0c\u53cd\u6b63\u662f\u6b7b\uff0c\u4e0d\u5982\u5c06\u5979\u6740\u4e86\u706d\u53e3\u3002\u201d\u90a3\u7528\u76ae \u97ad\u626b\u4f24\u5ba2\u5a09\u5a77\u7684\u6b66\u58eb\u53cc\u773c\u4e00\u7741\uff0c\u84e6\u7136\u559d\u9053\uff1a\u201c\u80e1\u8bf4\uff0c\u5979\u90a3\u91cc\u662f\u4ec0\u9ebd\u5bab\u4e3b\uff0c\u5929\u4e0b\u5c3d\u6709\u76f8\u8c8c\u76f8\u540c \u4e4b\u4eba\uff0c\u82e5\u7136\u5979\u662f\u5bab\u4e3b\uff0c\u5c82\u6709\u8fdc\u79bb\u6df1\u5bab\uff0c\u72ec\u5904\u8352\u6751\u7684\u9053\u7406\uff01\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u90a3\u4e24\u4e2a\u4e1c\u5382\u6869\u5934\u4e5f\u7acb \u523b\u4f1a\u610f\uff0c\u6b63\u81ea\u72b9\u7591\u4e0d\u51b3\uff0c\u4e0d\u77e5\u662f\u52a9\u540c\u4f34\u6740\u5979\u706d\u53e3\u7684\u597d\uff0c\u8fd8\u662f\u6551\u62a4\u5979\u597d\u3002\u90a3\u53c1\u4eba\u5df2\u53d1\u4e00\u58f0\u558a\uff0c \u53c8\u6325\u5200\u62a1\u97ad\uff0c\u4e0a\u524d\u6251\u653b\u3002 \u3000\u3000\u5c0f\u9053\u4e0a\u4eba\u5f71\u4e00\u95ea\uff0c\u94c1\u98de\u9f99\u5982\u98de\u5954\u5230\uff0c\u987b\u7709\u5012\u7ad6\uff0c\u6012\u559d\u9053\uff1a\u201c\u8c01\u6562\u6b3a\u8d1f\u6211\u7684\u5973\u513f\uff1f\u201d\u58f0\u5230 \u4eba\u5230\uff0c\u58f0\u4f3c\u5954\u96f7\uff0c\u638c\u5982\u9a87\u7535\uff0c\u90a3\u53c1\u4eba\u521a\u60f3\u62b5\u6321\uff0c\u94c1\u98de\u9f99\u5de6\u53f3\u5f00\u5f13\uff0c\u53cc\u638c\u4e00\u9707\uff0c\u53f3\u8db3\u75be\u8e22\uff0c\u53cc \u638c\u4e00\u811a\uff0c\u628a\u53c1\u4e2a\u654c\u4eba\u5168\u90fd\u6253\u5012\u3002\u90a3\u4e24\u4e2a\u4e1c\u5382\u6869\u5934\u6025\u53eb\u9053\uff1a\u201c\u94c1\u8001\u82f1\u96c4\uff0c\u4e0d\u5173\u6211\u4eec\u7684\u4e8b\uff01\u201d\u94c1 \u98de\u9f99\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u6ca1\u6709\u52a8\u624b\u5417\uff1f\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u6ca1\u6709\u3002\u94d9\u4ed6\u4eec\u5427\uff01\u201d\u94c1\u98de\u9f99\u559d\u9053\uff1a\u201c\u5979\u662f\u6211 \u7684\u5973\u513f\uff0c\u4f60\u4eec\u8981\u627e\u5bab\u4e3b\uff0c\u5230\u522b\u5904\u53bb\u627e\uff0c\u4ee5\u5f8c\u4f60\u4eec\u82e5\u518d\u7ed9\u6211\u649e\u5230\uff0c\u6211\u7acb\u523b\u6253\u65ad\u4f60\u4eec\u7684\u72d7\u817f\uff01\u201d \u94c1\u98de\u9f99\u4e0d\u77e5\u4ed6\u4eec\u662f\u8ffd\u6355\u72af\u4eba\uff0c\u8fd8\u4ee5\u4e3a\u5f1b\u4eec\u662f\u627e\u5ba2\u5a09\u5a77\u6765\u7684\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u4e1c\u5382\u6869\u5934\u62b1\u5934\u9f20\u7a9c\uff0c\u6025\u6025\u5954\u9003\u3002\u5ba2\u5a09\u5a77\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u7239\uff0c\u4ed6\u4eec\u4e0d\u662f\u627e\u6211\u6765\u7684\u3002\u4ed6 \u4eec\u662f\u8ffd\u6355\u8fd9\u4f4d\u5c11\u5e74\u5ba2\u4eba\u6765\u7684\u3002\u201d\u94c1\u98de\u9f99\u968f\u7740\u5ba2\u5a09\u5a77\u6240\u6307\uff0c\u77a5\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u662f\u88ab \u4f60\u6253\u4f24\u7684\u5462\u3002\u54a6\uff0c\u4f60\u662f\u8c01\uff1f\u4f60\u4e0d\u662f\u4ee5\u524d\u548c\u738b\u7167\u5e0c\u4e00\u9053\u7684\u50bb\u5c0f\u5b50\u5417\uff1f\u201d\u90a3\u53d7\u4f24\u7684\u5c11\u5e74\u65e9\u60f3\u51fa \u58f0\uff0c\u53ef\u662f\u63d2\u4e0d\u4e86\u53e3\uff0c\u89c1\u4ed6\u4e00\u95ee\uff0c\u8fd9\u624d\u50bb\u864e\u864e\u7684\u7b11\u9053\uff1a\u201c\u662f\u5440\uff0c\u4f60\u8001\u4eba\u5bb6\u597d\u8bb0\u6027\uff0c\u6211\u662f\u767d\u654f\u3002 \u6211\u7684\u5e08\u59b9\u66fe\u5728\u4f60\u7684\u5b9d\u5e84\u4f4f\u8fc7\u3002\u201d\u94c1\u848b\u9f99\u8bb0\u4e0d\u8d77\u4ed6\u7684\u540d\u5b57\uff0c\u8131\u53e3\u53eb\u4ed6\u505a\u201c\u50bb\u5c0f\u5b50\u201d\uff0c\u89c1\u4ed6\u7b11\u563b \u563b\u7684\u81ea\u8ba4\uff0c\u4e0d\u7981\u7b11\u9053\uff1a\u201c\u8001\u4e86\uff0c\u8bb0\u6027\u4e0d\u597d\u4e86\uff0c\u4f60\u522b\u89c1\u602a\u3002\u5582\uff0c\u4f60\u662f\u600e\u9ebd\u53d7\u4f24\u7684\uff1f\u8bf4\u7ed9\u6211 \u542c\uff01\u201d \u3000\u3000\u767d\u654f\u9053\uff1a\u201c\u7167\u5e0c\u5144\u53eb\u6211\u6765\u62dc\u5019\u4f60\u8001\u4eba\u5bb6\u3002\u201d\u94c1\u98de\u9f99\u8be7\u9053\uff1a\u201c\u4ed6\u8f85\u52a9\u95ef\u738b\uff0c\u519b\u52a1\u7e41\u5fd9\uff0c\u5c45 \u7136\u8fd8\u60e6\u8bb0\u7740\u6211\u8fd9\u4e2a\u8001\u5934\u513f\u5417\uff01\u201d\u767d\u654f\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\u4e13\u4e3a\u4f60\u8001\u4eba\u5bb6\u624d\u53eb\u6211\u6765\u7684\uff0c\u4ed6\u662f\u8981\u6211\u987a\u9053 \u8fc7\u8bbf\uff0c\u54b3\uff0c\u8bf4\u6765\u8bdd\u957f\u2026\u2026\u201d\u94c1\u98de\u9f99\u89c1\u4ed6\u8bf4\u8bdd\u4e0d\u52a0\u63a9\u9970\uff0c\u5fc3\u4e2d\u751a\u559c\u3002\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u7239\uff0c\u4f60\u770b\u4ed6 \u4f24\u6210\u8fd9\u4e2a\u6837\u513f\uff0c\u5c06\u4ed6\u6276\u56de\u5bb6\u4e2d\uff0c\u8ba9\u4ed6\u597d\u597d\u6b47\u8fc7\u4e4b\u5f8c\u518d\u8bf4\u5427\u3002\u201d\u94c1\u98de\u9f99\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u662f\u6211\u8001\u7cca \u6d82\u4e86\uff0c\u4f60\u6bd4\u6211\u901a\u8fbe\u4eba\u60c5\u5f97\u591a\u3002\u4e0d\u8fc7\u4ed6\u7684\u4f24\u867d\u7136\u770b\u6765\u5389\u5bb3\uff0c\u5374\u4e0d\u7d27\u8981\uff0c\u4ed6\u53d7\u7684\u662f\u7bad\u4f24\u5200\u4f24\uff0c\u635f \u4e86\u4e00\u4e9b\u76ae\u8089\u9aa8\u5934\uff0c\u6211\u5305\u4ed6\u5728\u4e94\u5929\u4e4b\u5185\uff0c\u4fbf\u80fd\u6cbb\u597d\u3002\u201d \u3000\u3000\u767d\u654f\u8eab\u4f53\u58ee\u5065\uff0c\u5728\u94c1\u5bb6\u5e84\u517b\u4e86\u53c1\u65e5\u4fbf\u80fd\u8d70\u52a8\uff0c\u5ba2\u5a09\u5a77\u957f\u5904\u5bab\u4e2d\uff0c\u63a5\u89e6\u5230\u7684\u591a\u662f\u865a\u4f2a\u5c0f \u4eba\uff0c\u89c1\u4e86\u4ed6\u5f8c\uff0c\u5f88\u6b22\u559c\u4ed6\u771f\u8bda\u8001\u5b9e\u7684\u6027\u683c\uff0c\u548c\u4ed6\u8c08\u5f97\u751a\u6b22\u3002\u94c1\u98de\u9f99\u5fc3\u4e2d\u6697\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u771f\u662f \u4eba\u7ed3\u4eba\u7f18\uff0c\u5a09\u5a77\u8fd9\u6837\u5a07\u751f\u60ef\u517b\u7684\u59d1\u5a18\uff0c\u5c45\u7136\u4f1a\u6b22\u559c\u4e2a\u50bb\u5c0f\u5b50\u3002\u201d \u3000\u3000\u767d\u654f\u5c06\u4ed6\u53d7\u4f24\u7684\u7ecf\u8fc7\u8bf4\u51fa\uff0c\u539f\u6765\u674e\u81ea\u6210\u8eb2\u8fdb\u79e6\u5cad\u4e4b\u5f8c\uff0c\u7ecf\u8fc7\u51e0\u5e74\u4f11\u517b\u751f\u606f\uff0c\u5b9e\u529b\u5927\u589e\u3002 \u800c\u897f\u5c71\u897f\u4e24\u5e74\u9057\u7559\u4e0b\u7684\u4e49\u519b\uff0c\u8fd9\u51e0\u5e74\u6765\u4e5f\u9887\u6709\u53d1\u5c55\u3002\u674e\u81ea\u6210\u8ba1\u753b\u91cd\u56de\u897f\uff0c\u518d\u897f\u51fa\u6f7c\u5173\u4ee5\u4e89\u5929 \u4e0b\uff0c\u56e0\u738b\u7167\u5e0c\u662f\u4ee5\u524d\u9655\u5317\u5404\u8def\u5927\u76d7\u603b\u5934\u9886\u738b\u5609\u80e4\u7684\u513f\u5b50\uff0c\u56e0\u6b64\u5c06\u8054\u7edc\u5c71\u897f\u897f\u4e24\u7701\u4e49\u519b\u7684\u91cd\u8981 \u4efb\u52a1\u4ea4\u4ed8\u7ed9\u4ed6\u3002\u738b\u7167\u5e0c\u6d3e\u767d\u654f\u5148\u884c\uff0c\u901a\u77e5\u4e24\u7701\u4e49\u519b\u7684\u91cd\u8981\u9996\u9886\u5230\u6307\u5b9a\u5730\u70b9\u805a\u4f1a\u3002\u9655\u897f\u7684\u5df2\u7ecf \u8054\u7edc\u597d\u4e86\u3002\u5c71\u897f\u7684\u5219\u5b9a\u5728\u4e03\u65e5\u4e4b\u5f8c\u5230\u4e2d\u6761\u5c71\u76f8\u805a\uff0c\u4e2d\u6761\u5c71\u8ddd\u79bb\u94c1\u98de\u9f99\u6240\u4f4f\u7684\u9f99\u95e8\u4e0d\u5230\u53c1\u767e \u91cc\uff0c\u56e0\u6b64\u738b\u7167\u5e0c\u4fbf\u53eb\u767d\u654f\u628a\u4e8b\u529e\u597d\u4e4b\u5f8c\uff0c\u987a\u9053\u5230\u9f99\u95e8\u62dc\u5019\u94c1\u98de\u9f99\u3002\u4e0d\u6599\u767d\u654f\u5728\u5404\u5904\u4f20\u9012\u6d88 \u606f\uff0c\u88ab\u9648\u5947\u745c\u5e10\u4e0b\u7684\u6b66\u58eb\u6ce8\u610f\uff0c\u4e00\u8def\u8ffd\u8e2a\uff0c\u672a\u5230\u9f99\u95e8\uff0c\u5df2\u53d7\u4f24\u4e86\u3002 \u3000\u3000\u767d\u654f\u53c8\u9053\uff1a\u201c\u7167\u5e0c\u5144\u51c6\u5907\u5728\u4f1a\u671f\u524d\u4e8c\u53c1\u65e5\u8d76\u5230\uff0c\u4ed6\u53eb\u6211\u5728\u6b64\u7b49\u4ed6\u3002\u4ed6\u8fd8\u60f3\u4e13\u8bda\u6765\u8bf7\u4f60\u8001 \u4eba\u5bb6\u51fa\u5c71\u5462\u3002\u201d\u94c1\u98de\u9f99\u6380\u987b\u7b11\u9053\uff1a\u201c\u6211\u8001\u4e86\uff0c\u4e0d\u4e2d\u7528\u4e86\uff0c\u5c06\u6765\u6211\u8fd9\u4e2a\u5973\u513f\u6216\u8bb8\u80fd\u52a9\u4f60\u4eec\u4e00\u81c2 \u4e4b\u529b\u3002\u201d\u767d\u654f\u9053\uff1a\u201c\u5979\u4e0d\u662f\u5ba2\u6c0f\u7684\u5973\u513f\u5417\uff1f\u201d\u94c1\u98de\u9f99\u4e0d\u7b54\uff0c\u5374\u95ee\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u4ed6\u4eec\u4e0e\u671d\u5ef7\u4f5c \u5bf9\uff0c\u5174\u9b4f\u5fe0\u8d24\u52bf\u4e0d\u4e24\u7acb\u3002\u4f60\u613f\u5e2e\u52a9\u4ed6\u4eec\u5417\uff1f\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u8981\u7239\u8bf4\u80fd\u5e2e\uff0c\u6211\u6b66\u827a\u7ec3\u6210\u4e4b\u5f8c\uff0c \u4fbf\u5f53\u968f\u519b\u52be\u529b\u3002\u201d\u767d\u654f\u7741\u5927\u773c\u775b\uff0c\u5bf9\u5ba2\u5a09\u5a77\u7684\u89c2\u611f\u5b8c\u5168\u53d8\u4e86\u3002 \u3000\u3000\u94c1\u98de\u9f99\u60f3\u8d77\u4ee5\u524d\u66fe\u60f3\u628a\u5973\u513f\u8bb8\u914d\u7ed9\u738b\u7167\u5e0c\u7684\u5f80\u4e8b\uff0c\u5fc3\u4e2d\u4e0d\u65e0\u611f\u6168\u3002\u95ee\u9053\uff1a\u201c\u7167\u5e0c\u548c\u76c2\u5c0f \u59d0\u6210\u4eb2\u4e86\u5417\uff1f\u201d\u767d\u654f\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u6709\u4e86\u4e24\u5c81\u5927\u7684\u4f84\u513f\u5566\u3002\u5b5f\u5e08\u59b9\u4e2a\u5b50\u5f88\u5c0f\uff0c\u4eba\u53c8\u6587\u9759\uff0c\u751f\u4e0b \u7684\u5a03\u5a03\u5374\u53c8\u767d\u53c8\u80d6\uff0c\u987d\u76ae\u5f97\u5f88\uff0c\u54c8\u54c8\uff01\u5b5f\u5e08\u4e5f\u5f88\u60f3\u4f1a\u4f60\u8001\u4eba\u5bb6\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u89c1\u4ed6 \u4eec\u4e00\u89c1\u3002\u201d \u3000\u3000\u53ef\u662f\u5230\u4e86\u7ea6\u4f1a\u524d\u4e24\u5929\uff0c\u8fd8\u4e0d\u89c1\u738b\u7167\u5e0c\u5230\u6765\uff0c\u767d\u654f\u751a\u4e3a\u7126\u6025\u3002\u94c1\u98de\u9f99\u60f3\u4e86\u597d\u4e45\uff0c\u9053\uff1a\u201c\u54b1 \u4eec\u53bb\u63a5\u4ed6\u5427\uff0c\u767d\u654f\u4f60\u7684\u4f24\u5168\u597d\u4e86\u5417\uff1f\uff0c\u201d\u767d\u654f\u9053\uff1a\u201c\u5168\u597d\u4e86\u3002\u201d\u65bc\u662f\u53c1\u4eba\u4e00\u9053\u767b\u7a0b\uff0c\u540c\u5f80\u4e2d \u6761\u5c71\u53bb\u3002 \u3000\u3000\u5f53\u94c1\u98de\u9f99\u7b49\u4eba\u8d76\u5f80\u4e2d\u6761\u5c71\u7684\u65f6\u5019\uff0c\u4e2d\u6761\u5c71\u8fb9\uff0c\u6b63\u6709\u4e00\u4eba\u8e3d\u8e3d\u72ec\u884c\uff0c\u8fd9\u4eba\u4fbf\u662f\u9003\u51fa\u6b66\u5f53\u5c71 \u7684\u5353\u4e00\u822a\u3002 \u3000\u3000\u201c\u5979\u8fd8\u613f\u89c1\u6211\u5417\uff1f\u5979\u8fd8\u4f1a\u7406\u776c\u6211\u5417\uff1f\u201d\u8fd9\u4e2a\u95ee\u9898\u5728\u4ed6\u5fc3\u4e0a\u6253\u4e86\u4e00\u4e2a\u5927\u7ed3\uff0c\u8fd9\u4e2a\u7ed3\u975e\u89c1\u5230 \u7389\u7f57\u5239\u4e0d\u80fd\u89e3\u5f00\uff0c\u56e0\u6b64\u4ed6\u4e0d\u7ba1\u7389\u7f57\u5239\u613f\u4e0d\u613f\u89c1\u4ed6\uff0c\u4e0d\u7ba1\u6d77\u89d2\u5929\u6daf\uff0c\u5343\u5c71\u4e07\u6c34\uff0c\u4e5f\u4e00\u5b9a\u8981\u5bfb\u5230 \u5979\u3002 \u3000\u3000\u201c\u5230\u90a3\u91cc\u53bb\u5bfb\u89c5\u5979\u5462\uff1f\u201d\u5353\u4e00\u822a\u9996\u5148\u60f3\u8d77\u4e86\u94c1\u98de\u9f99\uff0c\u4ed6\u60f3\uff1a\u7389\u7f57\u5239\u662f\u94c1\u98de\u9f99\u7684\u4e49\u5973\uff0c\u94c1 \u98de\u9f99\u5e94\u8be5\u77e5\u9053\u5979\u7684\u6d88\u606f\uff0c\u4e5f\u8bb8\u7389\u7f57\u5239\u5c31\u5728\u4ed6\u7684\u5bb6\u4e2d\u3002 \u3000\u3000\u65bc\u662f\u4ed6\u4e00\u5251\u5355\u8eab\uff0c\u8fce\u6653\u98ce\uff0c\u8e0f\u6b8b\u6708\uff0c\u7a7f\u8fc7\u53c1\u5ce1\u4e4b\u9669\uff0c\u4ece\u6e56\u5317\u5230\u4e86\u56db\u5ddd\uff0c\u4ece\u56db\u5ddd\u8fdb\u5165\u9655 \u897f\uff0c\u53c8\u4ece\u9655\u897f\u6765\u5230\u5c71\u897f\u3002\u51e0\u4e2a\u6708\u7684\u65c5\u7a0b\uff0c\u65f6\u5e8f\u5df2\u7ecf\u4ece\u6728\u53f6\u6447\u843d\u7684\u79cb\u5929\u5230\u96ea\u82b1\u98de\u821e\u7684\u5bd2\u51ac\u4e86\u3002 \u3000\u3000\u8fd9\u65e5\u4ed6\u5230\u4e86\u4e2d\u6761\u5c71\u8fb9\uff0c\u8ddd\u79bb\u94c1\u98de\u9f99\u6240\u4f4f\u7684\u9f99\u95e8\u4e0d\u5230\u53c1\u767e\u91cc\u4e86\uff0c\u5929\u8272\u9634\u973e\uff0c\u66ae\u8272\u56db\u5408\uff0c\u96ea \u8d8a\u4e0b\u8d8a\u5927\uff0c\u5353\u4e00\u822a\u60f3\u8d77\u518d\u8fc7\u4e24\u65e5\u4e5f\u8bb8\u4fbf\u80fd\u89c1\u7740\u4f0a\u4eba\uff0c\u867d\u7136\u6714\u98ce\u523a\u9aa8\uff0c\u5bd2\u6c14\u4fb5\u808c\uff0c\u4ed6\u7684\u5fc3\u5934\u5374 \u662f\u706b\u70ed\uff0c\u4e3a\u4e86\u8d76\u8def\uff0c\u9519\u8fc7\u5bbf\u5934\uff0c\u4e0d\u77e5\u4e0d\u89c9\u4e4b\u95f4\uff0c\u5929\u5df2\u5b8c\u5168\u9ed1\u4e86\u3002 \u3000\u3000\u5c71\u8def\u96be\u884c\uff0c\u591c\u5bd2\u96ea\u6ed1\uff0c\u5353\u4e00\u822a\u56db\u987e\u82cd\u832b\uff0c\u51b2\u7740\u5bd2\u98ce\uff0c\u5fae\u541f\u9053\uff1a\u201c\u96ea\u82b1\u96be\u51b7\u6545\u4eba\u5fc3\uff0c\u6d77\u89d2 \u5929\u6daf\u9065\u76fc\u66f4\u60c5\u6df1\uff01\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u53ef\u662f\u5230\u5e95\u56e0\u8d76\u4e86\u8bb8\u591a\u5929\u8def\uff0c\u75b2\u5026\u4e0d\u582a\uff0c\u53c8\u51b7\u53c8\u997f\u4e86\u3002 \u3000\u3000\u5c71\u8fb9\u6709\u4e2a\u91ce\u5e99\uff0c\u90a3\u662f\u5c71\u6c11\u5949\u675e\u7684\u5c71\u795e\u5e99\uff0c\u60f3\u662f\u56e0\u5bd2\u51ac\u814a\u6708\uff0c\u65e0\u4eba\u8fdb\u9999\uff0c\u8352\u51c9\u4e4b\u6781\u3002\u91ce\u9e1f \u8759\u8760\uff0c\u5728\u5e99\u4e2d\u7ed3\u5de2\u907f\u51ac\uff0c\u89c1\u6709\u4eba\u58f0\uff0c\u6251\u6251\u98de\u51fa\u3002\u5353\u4e00\u822a\u5fc3\u9053\uff1a\u201c\u6211\u4e14\u4e0e\u9e1f\u517d\u540c\u7fa4\uff0c\u5728\u8fd9\u91cc\u6253 \u4e00\u4e2a\u76f9\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u8fdb\u4e86\u91ce\u5e99\uff0c\u559d\u4e86\u4e00\u70b9\u51b7\u6c34\u9001\u4e0b\u4e7e\u7cae\uff0c\u63ed\u5f00\u795e\u5e54\uff0c\u89c1\u795e\u50cf\u80cc\u5f8c\u7684\u5730\u65b9\u6bd4\u8f83\u4e7e\u51c0\uff0c\u4fbf \u548c\u8863\u5367\u5012\u3002\u672c\u6765\u662f\u60f3\u6253\u4e2a\u778c\u7761\uff0c\u5374\u56e0\u592a\u8fc7\u75b2\u5026\uff0c\u4e00\u8eba\u4e0b\u53bb\u4fbf\u719f\u7761\u4e86\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u7761\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u68a6\u4e2d\u6b63\u89c1\u7740\u7389\u7f57\u5239\u8d70\u6765\uff0c\u4e00\u58f0\u957f\u5578\uff0c\u84e6\u7136\u60ca\u9192\u3002\u7b11\u58f0\u72b9\u81ea\u5728\u8033\uff0c \u5ffd\u7136\u53d8\u4e86\uff0c\u5c24\u5982\u67ad\u9e1f\u5389\u545c\uff0c\u60ca\u5fc3\u52a8\u9b44\u3002\u5353\u4e00\u822a\u5947\u9053\uff1a\u201c\u96be\u9053\u6211\u505a\u7684\u4e0d\u662f\u68a6\uff1f\u771f\u662f\u7ec3\u59d0\u59d0\u6765\u4e86 \u4e86\u4e0d\uff0c\u7edd\u4e0d\u662f\u5979\uff01\u5979\u7684\u7b11\u58f0\u7edd\u4e0d\u662f\u8fd9\u4e2a\u6837\u513f\uff0c\u8fd9\u9ebd\u53ef\u6015\uff01\u201d\u6b63\u60f3\u722c\u8d77\uff0c\u5ffd\u542c\u5f97\u811a\u6b65\u4e4b\u58f0\uff0c\u5df2 \u6709\u4eba\u8fdb\u5165\u5e99\u5185\u3002 \u3000\u3000\u5353\u4e00\u822a\u62c9\u5f00\u4e00\u89d2\u795e\u5e54\uff0c\u5f20\u773c\u671b\u53bb\uff0c\u51e0\u4e4e\u5413\u5f97\u51fa\u58f0\uff0c\u9760\u7740\u5e99\u4e2d\u5ead\u5b50\u91cc\u79ef\u96ea\u6240\u53d1\u7684\u5bd2\u5149\uff0c\u53ea \u89c1\u4e24\u4e2a\u9762\u65e0\u8840\u8272\u53c1\u5206\u50cf\u4eba\u4e03\u5206\u50cf\u9b3c\u7684\u5bb6\u4f19\uff0c\u6b63\u5728\u6840\u6840\u602a\u7b11\u3002\u4e24\u4eba\u90fd\u662f\u4e00\u5934\u4e71\u53d1\uff0c\u53c8\u9ad8\u53c8\u7626\uff0c \u4e00\u6a21\u4e00\u6837\uff01 \u3000\u3000\u5353\u4e00\u822a\u5b9a\u4e86\u5b9a\u795e\uff0c\u542c\u5f97\u5176\u4e2d\u4e00\u4eba\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u54b1\u4eec\u4e14\u5413\u4e00\u5413\u4ed6\uff0c\u7ed9\u4ed6\u4e2a\u4e0b\u9a6c\u5a01\uff01\u201d\u4ece\u76ae \u56ca\u4e2d\u53d6\u51fa\u4e24\u4e2a\u5706\u5ffd\u5ffd\u7684\u4e1c\u897f\uff0c\u5353\u4e00\u822a\u51dd\u795e\u671b\u53bb\uff0c\u7adf\u7136\u662f\u4e24\u4e2a\u9996\u7ea7\uff01 \u3000\u3000\u8bf4\u8bdd\u7684\u4eba\u628a\u9996\u7ea7\u4f9b\u5728\u795e\u684c\u4e0a\uff0c\u5353\u4e00\u822a\u770b\u4e0d\u89c1\u4e86\uff0c\u4f46\u542c\u5f97\u64e6\u706b\u77f3\u4e4b\u58f0\uff0c\u4e0d\u4e45\u4fbf\u6709\u9999\u70df\u523a \u76ee\uff0c\u4e0d\u77e5\u4ed6\u4eec\u6363\u4ec0\u9ebd\u9b3c\uff1f \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u5e99\u5916\u4f20\u6765\u4e86\u9a6c\u5636\u4e4b\u58f0\uff0c\u90a3\u4e24\u4eba\u970d\u7136\u7ad9\u8d77\uff0c\u602a\u53eb\u9053\uff1a\u201c\u738b\u5144\u771f\u662f\u4fe1\u4eba\uff0c\u679c\u7136\u4f9d \u65f6\u6765\u4e86\uff01\u6709\u597d\u670b\u53cb\u5728\u8fd9\u91cc\u7b49\u4f60\u6765\u5462\uff01\u201d \u3000\u3000\u5916\u9762\u7684\u4eba\u7b54\u9053\uff1a\u201c\u795e\u8001\u5927\uff0c\u795e\u8001\u4e8c\uff0c\u4f60\u4eec\u6765\u5f97\u597d\u65e9\u3002\u4f60\u4eec\u8fd8\u7ea6\u4e86\u8c01\u5462\uff1f\u4e0d\u662f\u8bf4\u597d\u53ea\u662f\u6211 \u4eec\u5148\u8c08\u5417\uff1f\u201d\u5353\u4e00\u822a\u4e00\u542c\uff0c\u58f0\u97f3\u975e\u5e38\u719f\u7a14\uff0c\u539f\u6765\u7adf\u7136\u662f\u738b\u7167\u5e0c\u3002 \u3000\u3000\u5353\u4e00\u822a\u5e73\u751f\u6709\u4e24\u4e2a\u6700\u597d\u7684\u670b\u53cb\uff0c\u4e00\u4e2a\u662f\u5cb3\u545c\u73c2\uff0c\u53e6\u4e00\u4e2a\u4fbf\u662f\u738b\u7167\u5e0c\u4e86\u3002\u4ed6\u548c\u738b\u7167\u5e0c\u867d\u7136 \u9053\u8def\u4e0d\u540c\uff0c\u5374\u662f\u809d\u80c6\u76f8\u7167\uff0c\u542c\u4e86\u4ed6\u7684\u58f0\u97f3\u4e0d\u89c9\u4e00\u559c\uff0c\u542c\u5b8c\u4ed6\u7684\u8bf4\u8bdd\uff0c\u53c8\u662f\u4e00\u60ca\u3002\u5fc3\u9053\uff1a\u201c\u795e \u8001\u5927\uff0c\u795e\u8001\u4e8c\uff1f\u54ce\u54df\uff0c\u83ab\u975e\u8fd9\u4e24\u4eba\u4fbf\u662f\u9655\u5317\u4e8c\u795e\uff0c\u795e\u5927\u5143\u548c\u795e\u4e00\u5143\uff1f\u4e45\u95fb\u8fd9\u4e8c\u4eba\u6b66\u529f\u602a\u5f02\uff0c \u884c\u4e8b\u8352\u8c2c\uff0c\u4f55\u4ee5\u738b\u7167\u5e0c\u5374\u7ea6\u4ed6\u4eec\u5728\u8fd9\u91cc\u76f8\u4f1a\uff1f\u201d \u3000\u3000\u5e99\u95e8\u5f00\u5904\uff0c\u738b\u7167\u5e0c\u7f13\u7f13\u8d70\u8fdb\uff0c\u5ffd\u7136\u60ca\u53eb\u8d77\u6765\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u591c\u732b\u5b50\u675c\u4e94\u548c\u5c04\u5929\u96d5\u5f20\u56db\u7237 \u5417\uff1f\u4f60\uff0c\u4f60\u4eec\u600e\u9ebd\u4e0b\u4e86\u8fd9\u4e2a\u6bd2\u624b\uff01\u201d \u3000\u3000\u795e\u5927\u5143\u6840\u6840\u602a\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u542c\u516b\u5927\u738b\u53f7\u4ee4\uff0c\u6211\u4eec\u662f\u8feb\u4e0d\u5f97\u5df2\u6740\u9e21\u5106\u7334\uff01\u201d \u3000\u3000\u738b\u7167\u5e0c\u9053\uff1a\u201c\u8fd9\u4e00\u5b9a\u4e0d\u662f\u516b\u5927\u738b\u7684\u4e3b\u610f\uff0c\u516b\u5927\u738b\u548c\u6211\u4eec\u7684\u5c0f\u95ef\u738b\u7ed3\u62dc\u4e86\u5144\u5f1f\uff0c\u4ed6\u600e\u80fd\u6740 \u6211\u4eec\u7684\u4eba\uff1f\u201d \u3000\u3000\u795e\u4e00\u5143\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u95ef\u738b\uff1f\u54fc\uff0c\u4ec0\u9ebd\u5c0f\u95ef\u738b\u4e86\u6211\u4eec\u95ef\u9053\u4e4b\u65f6\uff0c\u4ed6\u8fd8\u5728\u5a18\u809a\u5b50\u91cc\u95ef\u5462\uff0c \u4ed6\u51ed\u4ec0\u9ebd\u6765\u53f7\u4ee4\u5c71\u4e24\u7701\u7684\u82f1\u96c4\u8c6a\u6770\uff1f\u516b\u5927\u738b\u80af\u548c\u4ed6\u7ed3\u62dc\uff0c\u6211\u4eec\u5374\u4e0d\u5356\u8fd9\u4e2a\u5e10\uff01\u201d \u3000\u3000\u516b\u5927\u738b\u662f\u5f20\u732e\u5fe0\uff0c\u5c0f\u95ef\u738b\u662f\u674e\u81ea\u6210\uff0c\u5f20\u732e\u5fe0\u51e0\u5e74\u524d\u66fe\u7387\u53c1\u5341\u516d\u8425\u76d7\u515a\uff0c\u4e8c\u5341\u9980\u4e07\u4eba\u653b\u63a0 \u5c71\u897f\uff0c\u8d25\u65bc\u660e\u603b\u7763\u6d2a\u627f\u7574\uff0c\u9980\u4f17\u6d41\u5165\u6cb3\u5357\u6cb3\u5317\u4e24\u7701\uff0c\u53c8\u906d\u660e\u519b\u963b\u904f\uff0c\u518d\u81ea\u6cb3\u5357\u6d41\u5165\u6e56\u5317\u5883\uff0c \u81ea\u6e56\u5317\u53c8\u8f6c\u5165\u56db\u5ddd\u3002\u5176\u65f6\u674e\u81ea\u6210\u4ea6\u81ea\u897f\u5165\u5ddd\uff0c\u5728\u79e6\u5cad\u7ec3\u5175\uff0c\u4e24\u4eba\u4e43\u7ed3\u4e3a\u5144\u5f1f\u3002\u5f20\u732e\u5fe0\u5728\u56db\u5ddd \u7684\u52bf\u529b\u8f83\u5927\uff0c\u65bc\u662f\u674e\u81ea\u6210\u4e43\u548c\u4ed6\u534f\u5b9a\uff0c\u5c06\u56db\u5ddd\u8ba9\u7ed9\u4ed6\u505a\u57fa\u5730\uff0c\u81ea\u5df1\u5219\u56de\u897f\u3002\u81f3\u65bc\u5c71\u897f\uff0c\u8ffd\u6eaf \u5386\u53f2\u6e0a\u6e90\uff0c\u672c\u6765\u662f\u5f20\u732e\u5fe0\u7684\u5730\u76d8\uff0c\u4f46\u5f20\u732e\u5fe0\u5f97\u4e86\u56db\u5ddd\uff0c\u5df2\u5fc3\u6ee1\u610f\u8db3\uff0c\u5fc3\u60f3\uff1a\u97ad\u957f\u83ab\u53ca\uff0c\u5f97\u4e86 \u201c\u5929\u5e9c\u4e4b\u56fd\u201d\uff0c\u4f55\u5fc5\u8fd8\u8981\u8d2b\u7a77\u7684\u5c71\u897f\uff1f\u56e0\u6b64\u5728\u53e3\u5934\u4e0a\u7b54\u5e94\u4e86\u674e\u81ea\u6210\uff0c\u8ba9\u674e\u81ea\u6210\u5728\u5c71\u897f\u53d1\u5c55\u3002 \u8fd9\u4fbf\u662f\u674e\u81ea\u6210\u6d3e\u738b\u7167\u5e0c\u8054\u7edc\u897f\u5c71\u897f\u4e24\u7701\u4e49\u519b\u7684\u7531\u6765\u3002 \u3000\u3000\u4e0d\u6599\u795e\u5bb6\u5144\u5f1f\u4e0d\u670d\uff0c\u4ed6\u4eec\u5f97\u77e5\u4e86\u5f20\u732e\u5fe0\u548c\u674e\u81ea\u6210\u7684\u534f\u5b9a\uff0c\u4fbf\u53bb\u89c1\u5f20\u732e\u5fe0\uff0c\u529b\u8a00\u4e0d\u8be5\u5c06\u5c71 \u897f\u7684\u5730\u76d8\u653e\u5f03\u3002\u5f20\u732e\u5fe0\u88ab\u4ed6\u4eec\u8bf4\u52a8\uff0c\u4f46\u53c8\u4e0d\u597d\u610f\u601d\u6bc1\u7ea6\uff0c\u4fbf\u653e\u624b\u8ba9\u4ed6\u4eec\u53bb\u6405\u3002\u795e\u5bb6\u5144\u5f1f\u77e5\u9053 \u738b\u7167\u5e0c\u5df2\u7ea6\u4e86\u5c71\u897f\u5404\u8def\u4e49\u519b\u9996\u9886\uff0c\u5373\u5c06\u5728\u4e2d\u6761\u5c71\u805a\u4f1a\u3002\u4ed6\u4eec\u4fbf\u5728\u4f1a\u671f\u7684\u524d\u4e24\u5929\uff0c\u5148\u7ea6\u738b\u7167\u5e0c \u8c08\u5224\uff0c\u738b\u7167\u5e0c\u98ce\u95fb\u4ed6\u4eec\u5728\u5c71\u897f\u6d3b\u52a8\u4e4b\u4e8b\uff0c\u4e5f\u60f3\u4e0e\u4ed6\u4eec\u8c08\u4e2a\u6e05\u695a\uff0c\u4fbf\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u4e0d\u60f3\u4e8c\u795e\u5fc3\u72e0\u624b\u8fa3\uff0c\u7adf\u628a\u529b\u4e3b\u63a5\u53d7\u95ef\u738b\u53f7\u4ee4\u7684\u4e24\u4e2a\u4e49\u519b\u9996\u9886\u675c\u4e94\u548c\u5f20\u56db\u6740\u4e86\uff0c\u8fd8\u5c06\u4ed6\u4eec \u7684\u9996\u7ea7\u5e26\u6765\u5413\u738b\u7167\u5e0c\u3002 \u3000\u3000\u738b\u7167\u5e0c\u5f3a\u6291\u6012\u706b\u548c\u4ed6\u4eec\u8c08\u8bba\uff0c\u8d8a\u8c08\u8d8a\u50f5\u3002\u738b\u7167\u5e0c\u9053\uff1a\u201c\u672c\u6765\u6211\u4eec\u5e94\u540c\u5fc3\u5408\u529b\uff0c\u5171\u56fe\u5927 \u4e8b\uff0c\u8c01\u505a\u9996\u9886\uff0c\u90fd\u662f\u4e00\u6837\u3002\u4e0d\u8fc7\u65e2\u7136\u7ea6\u597d\u5f7c\u6b64\u5206\u5934\u4e3e\u4e8b\uff0c\u4fbf\u4e0d\u8be5\u593a\u5229\u4e89\u6743\u3002\u81ea\u76f8\u6b8b\u6740\uff0c\u66f4\u662f \u4e0d\u5408\uff01\u4f60\u4eec\u5982\u6b64\uff0c\u6211\u53ea\u597d\u5728\u5f8c\u65e5\u8bf7\u4f17\u82f1\u96c4\u516c\u51b3\u4e86\u3002\u201d\u795e\u5927\u5143\u602a\u773c\u4e00\u7ffb\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u60f3 \u6d3b\u5230\u5f8c\u5929\u5417\uff1f\u201d \u3000\u3000\u738b\u7167\u5e0c\u6012\u9053\uff1a\u201c\u4f60\u60f3\u600e\u6837\uff1f\u201d\u795e\u5927\u5143\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u8f88\uff0c\u4f60\u7236\u4eb2\u5728\u4e16\u65f6\u4e5f\u4e0d\u6562\u6307\u8d23\u6211\u4eec\uff0c \u4f60\u65e2\u6562\u65e0\u793c\uff0c\u6211\u4eec\u597d\u8bf7\u4f60\u548c\u591c\u732b\u5b50\uff0c\u5c04\u5929\u96d5\u4e00\u9053\u8d70\u4e86\uff0c\u4e50\u5f97\u8033\u6839\u6e05\u51c0\uff01\u201d\u738b\u7167\u5e0c\u559d\u9053\uff1a\u201c\u4f60 \u6562\uff01\u201d\u795e\u5927\u5143\u7eb5\u58f0\u72c2\u7b11\uff0c\u559d\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u9ebd\u4e0d\u6562\uff01\u201d\u4e00\u8dc3\u800c\u524d\uff0c\u624b\u81c2\u4e00\u6325\uff0c\u63a2\u8eab\u76f4\u53d6\u3002\u738b\u7167 \u5e0c\u4ea6\u975e\u5eb8\u624b\uff0c\u8f7b\u8f7b\u4e00\u95ea\uff0c\u5b9d\u5251\u51fa\u9798\uff0c\u795e\u5927\u5143\u4e00\u638c\u5288\u6765\uff0c\u738b\u7167\u5e0c\u53cd\u624b\u4fbf\u524a\uff0c\u795e\u5927\u5143\u7b11\u9053\uff1a\u201c\u5a03 \u513f\uff0c\u4f60\u8fd8\u6709\u4ec0\u9ebd\u80fd\u8010\uff1f\u4e00\u5e76\u65bd\u5c55\u4e86\u5427\uff01\u201d\u731b\u5730\u6b3a\u8eab\u76f4\u8fdb\uff0c\u5de6\u638c\u91cc\u5377\u5185\u52b2\uff0c\u6a2a\u62e8\u5251\u628a\uff0c\u8ba9\u62db\u9012 \u638c\uff0c\u53f3\u638c\u4e00\u6c89\uff0c\u6a2a\u80b1\u4fbf\u649e\uff0c\u4e0b\u524a\u819d\u76d6\uff0c\u4e0a\u51fb\u5c0f\u8179\u3002\u8fd9\u662f\u201c\u91ce\u72d0\u62f3\u201d\u4e2d\u4e00\u62db\u53c1\u5f0f\u7684\u7edd\u6280\uff0c\u795e\u5927 \u5143\u5fc3\u60f3\uff1a\u738b\u5609\u80e4\u7684\u6b66\u529f\u4e0e\u81ea\u5df1\u4e5f\u4e0d\u8fc7\u662f\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u4ed6\u7684\u513f\u5b50\u8fd8\u80fd\u6709\u591a\u5927\u80fd\u8010\uff0c\u8fd9\u4e00\u62db\u4ed6\u7edd\u9003 \u4e0d\u4e86\u3002 \u3000\u3000\u5c82\u77e5\u738b\u7167\u5e0c\u9752\u51fa\u65bc\u84dd\u66f4\u80dc\u65bc\u84dd\uff0c\u53ea\u89c1\u4ed6\u53f3\u5251\u4e00\u843d\uff0c\u6a2a\u622a\u6765\u52bf\uff0c\u5de6\u624b\u4e00\u52fe\uff0c\u76f4\u63b3\u654c\u8155\uff0c\u540c \u65f6\u53d1\u51fa\u4e24\u62db\uff0c\u4e00\u653b\u4e00\u5b88\uff0c\u5999\u5230\u6beb\u5dc5\uff0c\u6070\u6070\u628a\u795e\u5927\u5143\u7684\u7edd\u62db\u7834\u89e3\u4e86\uff01 \u3000\u3000\u795e\u5927\u5143\u5fae\u5403\u4e00\u60ca\uff0c\u4e0d\u6562\u8f7b\u654c\uff0c\u84b2\u6247\u822c\u7684\u5927\u624b\u4e00\u62e8\uff0c\u5de6\u624b\u9a88\u6307\u5982\u621f\uff0c\u4e00\u8f6c\u8eab\u4fbf\u70b9\u4ed6\u8111\u5f8c \u201c\u5929\u7a81\u7a74\u201d\uff0c\u738b\u7167\u5e0c\u542c\u5f97\u8111\u5f8c\u98ce\u751f\uff0c\u8eab\u5f62\u4e00\u77ee\uff0c\u957f\u5251\u6eda\u5730\u8fdb\u62db\uff0c\u5316\u4e3a\u201c\u9ed1\u864e\u5377\u5c3e\u201d\u7684\u62db\u6570\uff0c \u8ff3\u626b\u4e0b\u76d8\uff0c\u795e\u5927\u5143\u559d\u58f0\u201c\u597d\uff01\u201d\u8eab\u5b50\u98ce\u8f66\u4e00\u8f6c\uff0c\u5ffd\u62f3\u5ffd\u638c\uff0c\u5ffd\u800c\u70b9\u7a74\uff0c\u62db\u62db\u6bd2\u8fa3\uff0c\u5c06\u738b\u7167\u5e0c \u903c\u5f97\u900f\u4e0d\u8fc7\u6c14\u6765\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u573a\u6fc0\u6218\uff0c\u53ea\u5413\u5f97\u5e99\u4e2d\u8759\u8760\u60ca\u98de\uff0c\u5431\u5431\u4e71\u53eb\uff0c\u79ef\u5c18\u5377\u8d77\uff0c\u56db\u5904\u98de\u626c\uff0c\u52a0\u4e0a\u795e\u5bb6\u5144\u5f1f \u7684\u602a\u6a21\u6837\uff0c\u66f4\u663e\u5f97\u9634\u98ce\u60e8\u60e8\uff0c\u9a87\u76ee\u60ca\u5fc3\u3002 \u3000\u3000\u5353\u4e00\u822a\u770b\u4e86\u4e00\u4f1a\uff0c\u53ea\u89c1\u738b\u7167\u5e0c\u5251\u6cd5\u867d\u662f\u751a\u7cbe\uff0c\u5230\u5e95\u662f\u5b88\u591a\u653b\u5c11\u3002\u90a3\u795e\u5927\u5143\u51fa\u638c\u602a\u5f02\uff0c\u660e \u660e\u770b\u4ed6\u6253\u4e0d\u5230\u90a3\u4e2a\u65b9\u4f4d\uff0c\u5374\u4f1a\u500f\u7136\u653b\u81f3\uff0c\u800c\u4e14\u865a\u865a\u5b9e\u5b9e\u96be\u4ee5\u6349\u6478\uff0c\u5353\u4e00\u822a\u4e5f\u770b\u4e0d\u51fa\u5176\u4e2d\u9053 \u7406\u3002 \u3000\u3000\u53c8\u6253\u4e86\u4e00\u4f1a\uff0c\u795e\u5927\u5143\u8d8a\u653b\u8d8a\u6025\uff0c\u738b\u7167\u5e0c\u7f29\u5c0f\u5708\u5b50\uff0c\u5251\u5149\u821e\u5f97\u5982\u4e00\u5708\u957f\u8679\uff0c\u62a4\u7740\u8eab\u8eaf\u3002\u795e \u4e00\u5143\u53eb\u9053\uff1a\u201c\u54e5\u54e5\u4e0d\u8981\u548c\u4ed6\u7f20\u4e86\uff0c\u628a\u4ed6\u6253\u53d1\u4e86\u5427\uff01\u201d\u795e\u5927\u5143\u9053\uff1a\u201c\u597d\uff0c\u4f60\u7528\u91cd\u624b\u6cd5\u6253\u4ed6\u5f8c \u5fc3\u3002\u201d\u4e24\u5144\u5f1f\u6b66\u529f\u7684\u8def\u5b50\u76f8\u540c\uff0c\u5e73\u65f6\u9047\u7740\u5f3a\u654c\uff0c\u603b\u662f\u4e00\u9f50\u5bf9\u654c\u3002\u4eca\u591c\u4ed6\u4eec\u56e0\u738b\u7167\u5e0c\u662f\u5c0f\u8f88\uff0c \u6240\u4ee5\u51fa\u4e00\u4eba\uff0c\u8c01\u77e5\u4ee5\u4e00\u4eba\u4e4b\u529b\uff0c\u867d\u7136\u4ea6\u5360\u4e0a\u98ce\uff0c\u5374\u662f\u4e45\u6218\u4e0d\u4e0b\u3002 \u3000\u3000\u795e\u4e00\u5143\u4e00\u4e0a\uff0c\u738b\u7167\u5e0c\u987f\u65f6\u80cc\u8179\u53d7\u654c\uff0c\u9669\u8c61\u73af\u751f\uff0c\u738b\u7167\u5e0c\u62da\u529b\u652f\u6491\uff0c\u524d\u906e\u5f8c\u6321\uff0c\u593a\u8def\u6b32 \u9003\uff0c\u795e\u5927\u5143\u5927\u7b11\u9053\uff1a\u201c\u9664\u975e\u8fd9\u5e99\u4e2d\u7684\u5c71\u795e\u663e\u7075\u6551\u4f60\uff0c\u4f60\u60f3\u9003\u51fa\uff0c\u4e07\u4e07\u4e0d\u80fd\uff01\u201d\u8fd0\u638c\u6025\u653b\uff0c\u5c06 \u738b\u7167\u5e0c\u8feb\u5f97\u6b65\u6b65\u5f8c\u9000\u3002\u6e10\u6e10\u79fb\u81f3\u795e\u5ea7\u4e4b\u524d\u3002\u795e\u4e00\u5143\u8fd0\u638c\u4e00\u5288\uff0c\u638c\u98ce\u6240\u81f3\uff0c\u795e\u5e54\u98de\u626c\uff0c\u4e00\u7f15\u9752 \u5149\uff0c\u7a81\u7136\u7535\u5c04\u800c\u51fa\uff0c\u795e\u5927\u5143\u731d\u4e0d\u53ca\u9632\uff0c\u811a\u8e1d\u4e2d\u4e86\u4e00\u5251\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u7b11\u9053\uff1a\u201c\u5c71\u795e\u6765\u4e86\uff01\u201d \u3000\u3000\u738b\u7167\u5e0c\u53eb\u9053\uff1a\u201c\u54a6\uff0c\u5353\u5144\uff0c\u4f60\u600e\u9ebd\u4e5f\u5728\u8fd9\u91cc\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5148\u628a\u8fd9\u4e24\u4e2a\u6076\u8d3c\u6253\u53d1\uff0c\u54b1 \u4eec\u518d\u8c08\u3002\u201d\u633a\u5251\u76f4\u53d6\u795e\u4e00\u5143\uff0c\u738b\u7167\u5e0c\u4e5f\u7ffb\u8eab\u518d\u6597\uff0c\u548c\u795e\u5927\u5143\u6740\u5f97\u96be\u5206\u96be\u89e3\u3002 \u3000\u3000\u795e\u4e00\u5143\u8ba4\u5f97\u5353\u4e00\u822a\uff0c\u5e76\u4e0d\u628a\u4ed6\u653e\u5728\u773c\u5185\uff0c\u5de6\u81c2\u4e00\u6325\uff0c\u4f5c\u52bf\u62a2\u4ed6\u5b9d\u5251\uff0c\u53f3\u638c\u500f\u7136\u7a7f\u51fa\uff0c\u968f \u624b\u4e00\u626b\uff0c\u5288\u4ed6\u819d\u76d6\uff0c\u5353\u4e00\u822a\u811a\u8ddf\u4e00\u65cb\uff0c\u795e\u4e00\u5143\u638c\u52bf\u8fc5\u901f\u65e0\u4f26\uff0c\u7adf\u7136\u5288\u4ed6\u4e0d\u4e2d\uff0c\u5fc3\u4e2d\u4e00\u51db\uff0c\u8bf4 \u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5353\u4e00\u822a\u5251\u8bc0\u4e00\u9886\uff0c\u9752\u5149\u75be\u95ea\uff0c\u4e00\u62db\u201c\u4e58\u9f99\u5f15\u51e4\u201d\uff0c\u4e58\u52bf\u53cd\u51fb\uff0c\u53ea\u542c\u5f97\u201c\u5237 \u5566\u201d\u4e00\u58f0\u54cd\uff0c\u795e\u4e00\u5143\u8896\u5b50\u5df2\u88ab\u524a\u53bb\u4e00\u622a\u3002\u8fd8\u5e78\u4ed6\u95ea\u5f97\u751a\u5feb\uff0c\u8981\u4e0d\u7136\u8fd9\u4e00\u5251\u4fbf\u662f\u65ad\u8155\u7a7f\u8179\u4e4b \u707e\u3002 \u3000\u3000\u795e\u4e00\u5143\u5927\u6012\uff0c\u624b\u81c2\u4e00\u6325\uff0c\u9aa8\u8282\u683c\u683c\u4f5c\u54cd\uff0c\u624b\u81c2\u7adf\u7136\u66b4\u957f\u4e24\u5bf8\uff0c\u53d8\u638c\u4e3a\u6307\uff0c\u53cd\u70b9\u5353\u4e00\u822a\u5de6 \u80c1\u201c\u671f\u95e8\u7a74\u201d\uff0c\u8fd9\u662f\u795e\u5bb6\u5144\u5f1f\u7684\u72ec\u95e8\u6b66\u529f\uff0c\u602a\u5f02\u975e\u51e1\uff01\u672c\u6765\u9ad8\u624b\u5bf9\u654c\uff0c\u5dee\u6beb\uff0c\u8fd9\u4e00\u4e0b\u5353\u4e00\u822a \u672c\u96be\u9003\u907f\uff1a\u5e78\u4e8f\u4ed6\u5728\u9002\u624d\u65c1\u89c2\u4e4b\u65f6\uff0c\u5df2\u77e5\u795e\u5bb6\u5144\u5f1f\u6709\u6b64\u602a\u5f02\u6b66\u529f\uff0c\u65e9\u6709\u9632\u5907\uff0c\u5f97\u52bf\u4e4b\u65f6\uff0c\u5e76 \u4e0d\u8ffd\u51fb\uff0c\u795e\u4e00\u5143\u4e00\u62db\u53d1\u51fa\uff0c\u4ed6\u5df2\u4e00\u4e2a\u864e\u8df3\uff0c\u95ea\u5230\u5de6\u8fb9\uff0c\u4e00\u5251\u5e73\u6311\uff0c\u6d88\u4e86\u6765\u52bf\u3002 \u3000\u3000\u8fd9\u4e00\u5e74\u6765\uff0c\u5353\u4e00\u822a\u6b66\u529f\u5927\u8fdb\uff0c\u4e03\u5341\u4e8c\u624b\u6b66\u5f53\u8fde\u73af\u5251\u6cd5\uff0c\u4f7f\u5f97\u51cc\u5389\u65e0\u524d\uff0c\u5982\u81c2\u8fd0\u638c\uff0c\u968f\u5fc3 \u6240\u6b32\uff0c\u653b\u5b88\u5982\u610f\uff0c\u771f\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u8f7b\u7075\u7fd4\u52a8\u3002\u9976\u662f\u795e\u4e00\u5143\u6b66\u529f\u602a\u5f02\uff0c\u4e5f\u88ab\u4ed6\u8feb\u5f97\u5904\u5728\u4e0b\u98ce\u3002 \u3000\u3000\u90a3\u8fb9\u53a2\uff0c\u795e\u5927\u5143\u548c\u738b\u7167\u5e0c\u4e5f\u6740\u5f97\u96be\u5206\u96be\u89e3\u3002\u795e\u5927\u5143\u529f\u529b\u751a\u9ad8\uff0c\u6280\u827a\u4e5f\u5728\u738b\u7167\u5e0c\u4e4b\u4e0a\uff0c\u53ef \u662f\u4ed6\u521a\u624d\u811a\u8e1d\u88ab\u5353\u4e00\u822a\u5251\u5c16\u523a\u4f24\uff0c\u817e\u632a\u95ea\u5c55\u4e4b\u9645\uff0c\u8fdc\u4e0d\u5982\u524d\u3002\u56e0\u6b64\u80fd\u548c\u738b\u7167\u5e0c\u6253\u4e2a\u5e73\u624b\uff0c\u800c \u4e14\u60ed\u6e10\u8fd8\u88ab\u8feb\u5904\u5728\u4e0b\u98ce\u3002 \u3000\u3000\u62da\u4e86\u767e\u6570\u5341\u62db\uff0c\u795e\u5bb6\u5144\u5f1f\u77e5\u9053\u4eca\u665a\u7edd\u96be\u5f97\u901e\uff0c\u6253\u4e86\u4e00\u4e2a\u62db\u547c\uff0c\u53cd\u8eab\u6b32\u8d70\u3002\u738b\u7167\u5e0c\u6068\u4ed6\u4eec \u80e1\u4f5c\u975e\u4e3a\uff0c\u7834\u574f\u95ef\u738b\u5927\u8ba1\uff0c\u90a3\u91cc\u80af\u653e\uff0c\u62a2\u524d\u4e24\u6b65\uff0c\u5835\u7740\u5e99\u95e8\uff0c\u5251\u52bf\u66f4\u7d27\uff0c\u795e\u5927\u5143\u5403\u4e8f\u5728\u8df3\u8dc3 \u4e0d\u4fbf\uff0c\u95ef\u4e0d\u51fa\u53bb\u3002\u53ea\u597d\u6a2a\u5fc3\u72e0\u6597\u3002\u81f3\u65bc\u795e\u4e00\u5143\u5219\u5f62\u52bf\u66f4\u52a3\uff0c\u5353\u4e00\u822a\u7684\u5251\u4f7f\u5230\u75be\u5904\uff0c\u4f46\u89c1\u5251\u5149 \u7f2d\u7ed5\uff0c\u5251\u5f71\u7ffb\u98de\uff0c\u795e\u4e00\u5143\u88ab\u88f9\u5728\u5f53\u4e2d\uff0c\u5df2\u662f\u8131\u8eab\u4e0d\u5f97\uff01 \u3000\u3000\u795e\u5bb6\u5144\u5f1f\u6b63\u5728\u5403\u7d27\uff0c\u5e99\u95e8\u5916\u5ffd\u7136\u7fa4\u9a6c\u5636\u9e23\uff0c\u63a5\u7740\u4eba\u58f0\u5608\u6742\uff0c\u4f3c\u6709\u4e00\u5927\u7fa4\u4eba\u4e0b\u9a6c\u5954\u4e0a\u5c71 \u5761\u3002\u795e\u5927\u5143\u6012\u9053\uff1a\u201c\u738b\u7167\u5e0c\u4f60\u8fd9\u5c0f\u8f88\uff0c\u4e3a\u4f55\u4e0d\u5b88\u4fe1\u4e49\uff0c\u7ea6\u4eba\u6765\u6697\u7b97\u8001\u5b50\uff1f\u201d\u738b\u7167\u5e0c\u4e5f\u4ee5\u4e3a\u662f \u795e\u5bb6\u5144\u5f1f\u7ea6\u6765\u7684\u4eba\uff0c\u95fb\u8a00\u4e00\u60ca\uff0c\u53eb\u9053\uff1a\u201c\u4e0d\u662f\u4f60\u7ea6\u6765\u7684\u4eba\u5417\uff1f\u5feb\u522b\u52a8\u624b\uff0c\u5b9a\u662f\u5b98\u519b\u6765\u4e86\uff01\u201d \u3000\u3000\u5e99\u95e8\u8f70\u7684\u4e00\u58f0\u788e\u6210\u51e0\u7247\uff0c\u5341\u51e0\u540d\u6b66\u58eb\u51b2\u4e86\u8fdb\u6765\uff0c\u4e3a\u9996\u7684\u7adf\u662f\u8fde\u57ce\u864e\u548c\u91d1\u5343\u3002\u8fde\u57ce\u864e\u672c\u5df2 \u5347\u4e3a\u4e1c\u5382\u603b\u7ba1\uff0c\u66ff\u4e86\u6155\u5bb9\u51b2\u4e4b\u7f3a\uff0c\u53ea\u56e0\u519b\u60c5\u7d27\u6025\uff0c\u53c8\u88ab\u8c03\u5230\u524d\u65b9\uff0c\u505a\u201c\u88ad\u532a\u519b\u201d\u7684\u603b\u76d1\uff1a\u81f3 \u65bc\u91d1\u5343\u539f\u662f\u91d1\u72ec\u5f02\u7684\u4f84\u513f\uff0c\u91d1\u72ec\u5f02\u88ab\u5cb3\u545c\u73c2\u6740\u5f8c\uff0c\u4ed6\u56e0\u60e7\u602f\u7ea2\u82b1\u9b3c\u6bcd\uff0c\u4e0d\u6562\u56de\u5bb6\uff0c\u7d22\u6027\u6b63\u5f0f \u6295\u9760\uff0c\u505a\u4e86\u897f\u5382\u4e00\u540d\u7edf\u9886\uff0c\u5230\u7ea2\u82b1\u9b3c\u6bcd\u6b7b\u5f8c\uff0c\u4ed6\u66f4\u8086\u65e0\u5fcc\u60ee\u4e86\u3002\u8fd9\u6b21\u4ed6\u4e5f\u5949\u8c03\u51fa\u6765\u534f\u52a9\u8fde\u57ce \u864e\uff0c\u7763\u9648\u5947\u745c\u67e5\u5f97\u738b\u7167\u5e0c\u5728\u664b\u4e24\u7701\u6d3b\u52a8\uff0c\u56e0\u6b64\u592e\u6c42\u4ed6\u4eec\u4eb2\u81ea\u51fa\u9a6c\u641c\u6355\u3002 \u3000\u3000\u8fde\u57ce\u864e\u521d\u610f\u53ea\u662f\u6349\u62ff\u738b\u7167\u5e0c\u4e00\u4eba\uff0c\u5ffd\u89c1\u795e\u5bb6\u5144\u5f1f\u548c\u5353\u4e00\u822a\u4e5f\u5728\u5176\u5185\uff0c\u53c8\u60ca\u53c8\u559c\u3002\u8981\u77e5\u795e \u5bb6\u5144\u5f1f\u4e5f\u662f\u5317\u7740\u540d\u7684\u5267\u76d7\uff0c\u4e3a\u6355\u738b\u7167\u5e0c\u800c\u53d1\u73b0\u4ed6\u4eec\uff0c\u53ef\u7b97\u662f\u610f\u5916\u7684\u6536\u83b7\uff0c\u4f46\u5353\u4e00\u822a\u5374\u662f\u6b66\u5f53 \u6d3e\u7684\u638c\u95e8\u5f1f\u5b50\uff0c\u8fde\u57ce\u864e\u867d\u660e\u77e5\u4ed6\u662f\u738b\u7167\u5e0c\u597d\u53cb\uff0c\u5374\u8fd8\u4e0d\u613f\u4e0e\u4eba\u591a\u52bf\u4f17\u7684\u6b66\u5f53\u6d3e\u7ed3\u4ec7\u3002 \u3000\u3000\u4e14\u8bf4\u738b\u5353\u4e0e\u4e8c\u795e\u521a\u521a\u505c\u6597\uff0c\u5b98\u519b\u95ef\u5165\uff0c\u53ea\u542c\u5f97\u8fde\u57ce\u864e\u5927\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u4f4d\u5353\u516c\u5b50\u662f\u597d\u670b \u53cb\uff0c\u4e0d\u51c6\u4f24\u4ed6\u3002\u64d2\u90a3\u53c1\u4e2a\u6076\u8d3c\u3002\u5353\u516c\u5b50\uff0c\u4f60\u8d81\u65e9\u9000\u51fa\u662f\u975e\u4e4b\u573a\uff0c\u5feb\u5feb\u8d70\u5427\uff01\u201d\u5353\u4e00\u822a\u5927\u6012\uff0c \u4e00\u62db\u201c\u5251\u631f\u98ce\u96f7\u201d\uff0c\u76f4\u523a\u6a2a\u524a\uff0c\u96c4\u52b2\u51cc\u5389\uff0c\u8fde\u57ce\u864e\u731d\u4e0d\u53ca\u9632\uff0c\u624b\u6307\u51e0\u4e4e\u7ed9\u4ed6\u524a\u65ad\uff0c\u6012\u9053\uff1a \u201c\u4f60\u4e0d\u542c\u597d\u8a00\uff0c\u7ec8\u987b\u5f8c\u6094\uff1f\u201d\u53cc\u94a9\u4e00\u5377\uff0c\u88f9\u7740\u5251\u950b\u3002\u91d1\u5343\u7387\u4f17\u6b66\u58eb\u7eb7\u7eb7\u6251\u4e0a\u3002 \u3000\u3000\u795e\u5927\u5143\u53eb\u9053\uff1a\u201c\u6211\u7b49\u5982\u4f55\uff1f\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u540c\u821f\u5171\u6d4e\uff0c\u4e49\u4e0d\u5bb9\u8f9e\uff01\u201d\u5c55\u5251\u5148\u654c\u7740\u4e86\u91d1 \u5343\u3002\u795e\u5bb6\u5144\u5f1f\u602a\u7b11\u4e00\u58f0\uff0c\u9aa4\u7136\u51fa\u624b\uff0c\u628a\u4e24\u540d\u4e1c\u5382\u6869\u5934\u7528\u5927\u6454\u7891\u624b\u76f4\u7529\u51fa\u53bb\uff0c\u98de\u8eab\u5916\u95ef\uff0c\u90a3\u77e5 \u4f17\u6b66\u58eb\u4e2d\u4e5f\u9887\u6709\u9ad8\u624b\uff0c\u89c1\u4ed6\u4eec\u6765\u52bf\u51f6\u72fc\uff0c\u6025\u6025\u5835\u622a\uff0c\u5251\u621f\u5982\u6797\uff0c\u5200\u67aa\u98de\u821e\uff0c\u987f\u65f6\u5c06\u56db\u4e2a\u4eba\u90fd \u56f4\u5728\u6838\u5fc3\u3002 \u3000\u3000\u8fde\u57ce\u864e\u7684\u6b66\u529f\u975e\u540c\u5c0f\u9119\uff0c\u53cc\u94a9\u7ffb\u817e\u98de\u5377\uff0c\u72b9\u5982\u6012\u9f99\u60ca\u87d2\u8981\u4e0d\u662f\u5353\u4e00\u822a\u6b66\u529f\u5927\u8fdb\uff0c\u4e07\u96be\u62b5 \u6321\uff0c\u9976\u662f\u5982\u6b64\uff0c\u4e5f\u53ea\u6709\u62db\u67b6\u4e4b\u529f\uff0c\u65e0\u8fd8\u624b\u4e4b\u529b\u3002\u5e78\u8fde\u57ce\u864e\u5fd7\u5728\u738b\u7167\u5e0c\u800c\u4e0d\u5728\u5353\u4e00\u822a\uff0c\u6df7\u6218\u4e2d \u6bcf\u6bcf\u820d\u4e86\u5353\u4e00\u822a\u800c\u653b\u738b\u7167\u5e0c\u3002\u4f46\u5353\u4e00\u822a\u7d27\u7d27\u9760\u7740\u738b\u7167\u5e0c\uff0c\u5e76\u80a9\u4f5c\u6218\uff0c\u8fde\u57ce\u864e\u8fde\u4e0b\u6740\u624b\uff0c\u4e5f\u4f24 \u4e0d\u4e86\u738b\u7167\u5e0c\u5206\u6beb\u3002 \u3000\u3000\u738b\u5353\u4e8c\u795e\u62da\u529b\u62b5\u6321\uff0c\u81ea\u5348\u591c\u76f4\u81f3\u9ece\u660e\uff0c\u662f\u594b\u6218\u4e0d\u4f11\u3002\u53ef\u662f\u65f6\u95f2\u4e00\u957f\uff0c\u795e\u5927\u5143\u4f24\u53e3\u53d1\u4f5c\uff0c \u8df3\u8dc3\u66f4\u662f\u4e0d\u4fbf\uff0c\u6e10\u9732\u75b2\u6001\u3002\u8fde\u57ce\u864e\u770b\u4e86\u51fa\u6765\uff0c\u559c\u9053\uff1a\u201c\u5148\u628a\u8fd9\u6076\u8d3c\u5e72\u6389\uff01\u201d\u53cc\u94a9\u4e00\u4f38\uff0c\u820d\u4e86 \u738b\u5353\uff0c\u4ea4\u53c9\u4e00\u526a\uff0c\u52fe\u6495\u795e\u5927\u5143\u7684\u9888\u9879\uff0c\u795e\u5927\u5143\u5927\u543c\u4e00\u58f0\uff0c\u53f3\u81c2\u4e00\u6325\uff0c\u53ea\u542c\u5f97\u201c\u556a\u201d\u7684\u4e00\u54cd\uff0c \u8fde\u57ce\u864e\u88ab\u4ed6\u7528\u72ec\u95e8\u7edd\u62db\uff0c\u5728\u80a9\u5934\u4e0a\u51fb\u4e86\u4e00\u4e0b\uff0c\u80a9\u80db\u9aa8\u788e\u4e86\u4e24\u5757\uff0c\u53ef\u662f\u795e\u5927\u5143\u4e5f\u7ed9\u4ed6\u53cc\u94a9\u94a9 \u7740\uff0c\u6495\u4e0b\u4e86\u597d\u5927\u4e00\u5757\u76ae\u8089\u3002\u738b\u7167\u5e0c\u5927\u60ca\uff0c\u5237\u5237\u4e24\u5251\uff0c\u6a2a\u91cc\u7a9c\u51fa\uff0c\u76f4\u523a\u8fc7\u53bb\uff0c\u624d\u6070\u6070\u89e3\u4e86\u795e\u5927 \u5143\u4e4b\u5371\uff01 \u3000\u3000\u795e\u5927\u5143\u8fde\u53d7\u5251\u4f24\u5200\u4f24\uff0c\u66f4\u662f\u4e0d\u652f\u3002\u5728\u56db\u4eba\u4e4b\u4e2d\uff0c\u672c\u6765\u4ee5\u4ed6\u7684\u6b66\u529f\u6700\u5f3a\uff0c\u800c\u4eca\u5374\u53cd\u987b\u5176\u4ed6 \u53c1\u4eba\u7167\u987e\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u5b98\u519b\u8fd9\u65b9\uff0c\u987f\u65f6\u5360\u4e86\u4e0a\u98ce\uff0c\u56f4\u653b\u6108\u7d27\u3002 \u3000\u3000\u6fc0\u6218\u4e4b\u95ee\uff0c\u66d9\u5149\u4e4b\u95f4\uff0c\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u5578\uff0c\u8fdc\u8fdc\u4f20\u6765\uff0c\u5578\u58f0\u4f4e\u6c89\uff0c\u5728\u573a\u7684\u4eba\u90fd\u542c\u5f97\u6e05\u6e05\u695a \u695a\u3002\u738b\u7167\u5e0c\u4e0e\u5353\u4e00\u822a\u95fb\u58f0\u5927\u559c\uff0c\u4e00\u9f50\u53eb\u9053\uff1a\u201c\u662f\u94c1\u8001\u524d\u8f88\u6765\u4e86\uff01\u201d\u738b\u7167\u5e0c\u8fd8\u8865\u4e86\u4e00\u53e5\u9053\uff1a \u201c\u795e\u8001\u5927\uff0c\u4e0d\u8981\u6c14\u9981\uff0c\u6765\u7684\u662f\u5a01\u9707\u897f\u5317\u7684\u94c1\u98de\u9f99\uff0c\u54b1\u4eec\u5c31\u53ef\u89e3\u56f4\u4e86\uff01\u201d\u738b\u7167\u5e0c\u5374\u4e0d\u77e5\u9053\uff0c\u4e8c \u795e\u66fe\u4e0e\u94c1\u98de\u9f99\u7ed3\u6709\u6881\u5b50\uff0c\u5728\u94c1\u98de\u9f99\u638c\u4e0b\u5403\u8fc7\u5927\u4e8f\u3002 \u3000\u3000\u8fde\u57ce\u864e\u542c\u5f97\u5578\u58f0\uff0c\u9762\u8272\u4e00\u53d8\uff0c\u53eb\u9053\uff1a\u201c\u5feb\u628a\u8fd9\u51e0\u540d\u5c0f\u8d3c\u5e72\u6389\uff0c\u5408\u529b\u5bf9\u4ed8\u90a3\u4e2a\u8001\u8d3c\uff01\u201d\u53cc \u94a9\u970d\u970d\uff0c\u8fde\u8d70\u8fa3\u62db\uff01 \u3000\u3000\u91d1\u5343\u7684\u201c\u9634\u98ce\u6bd2\u7802\u638c\u201d\u4e0e\u4e8c\u795e\u7684\u201c\u91ce\u72d0\u62f3\u201d\u4e00\u6837\uff0c\u540c\u662f\u90aa\u6d3e\u6b66\u529f\uff0c\u4ee5\u6bd2\u653b\u6bd2\uff0c\u4e92\u4e0d\u4e0a \u4e0b\uff0c\u8fd9\u65f6\u4e5f\u7d27\u7d27\u8feb\u7740\u795e\u4e00\u5143\u3002\u5b98\u519b\u4e00\u9635\u6025\u653b\uff0c\u770b\u770b\u5c31\u8981\u628a\u738b\u5353\u4e8c\u795e\u7b49\u56db\u4eba\u683c\u6740\u3002 \u3000\u3000\u5578\u58f0\u66f4\u8fd1\u3002\u4e8c\u795e\u662f\u5b6a\u751f\u5144\u5f1f\uff0c\u540c\u4e00\u5fc3\u601d\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u60f3\u9053\uff1a\u201c\u5927\u96be\u6765\u65f6\uff0c\u738b\u7167\u5e0c\u5f53\u7136\u4e0e \u6211\u4eec\u5171\u540c\u62d2\u654c\u3002\u89e3\u56f4\u4e4b\u5f8c\uff0c\u4eba\u5fc3\u96be\u6599\u3002\u82e5\u7136\u4ed6\u4e0e\u94c1\u8001\u8d3c\u8054\u624b\u5bf9\u4ed8\u6211\u4eec\uff0c\u54b1\u4fe9\u5144\u5f1f\u53ef\u662f\u6b7b\u65e0\u846c \u8eab\u4e4b\u5730\uff01\u201d\u4e8c\u795e\u4ee5\u5c0f\u4eba\u4e4b\u5fc3\u5ea6\u541b\u5b50\u4e4b\u8179\uff0c\u6253\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u6b63\u5728\u5403\u7d27\u4e4b\u9645\uff0c\u5ffd\u7136\u53cc\u53cc\u53cd\u624b\u4e00 \u6293\uff0c\u795e\u5927\u5143\u6293\u4f24\u4e86\u738b\u7167\u5e0c\uff0c\u795e\u4e00\u5143\u6293\u4f24\u4e86\u5353\u4e00\u822a\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u52a9\u4f60\u4eec\u4e00\u81c2\u4e4b\u529b\uff0c\u8fd8\u4e0d\u5feb\u5feb \u64d2\u4eba\uff01\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\u53d8\u51fa\u610f\u5916\uff0c\u8fde\u57ce\u864e\u6014\u4e86\u4e00\u6014\uff0c\u53eb\u9053\uff1a\u201c\u597d\uff0c\u597d\uff01\u201d\u795e\u5927\u5143\u8fc8\u8fdb\u4e24\u6b65\uff0c\u6b3a\u5230\u91d1\u5343\u773c \u524d\uff0c\u91d1\u5343\u4ee5\u4e3a\u4e8c\u795e\u9635\u524d\u53cd\u53db\uff0c\u5356\u53cb\u6c42\u8363\uff0c\u5df2\u662f\u81ea\u5df1\u4eba\u4e86\uff0c\u5168\u4e0d\u9632\u5907\uff0c\u90a3\u6599\u795e\u5927\u5143\u9661\u7136\u5927\u559d\u4e00 \u58f0\uff0c\u624b\u81c2\u66b4\u4f38\uff0c\u4e00\u628a\u6293\u7740\u4e86\u91d1\u5343\u9879\u5f8c\u80a5\u8089\uff0c\u6a2a\u4e3e\u8d77\u6765\uff0c\u5f53\u6210\u5175\u5668\uff0c\u65cb\u98ce\u6025\u821e\uff0c\u54c8\u54c8\u7b11\u9053\uff1a \u201c\u54b1\u8001\u5b50\u8c01\u4e5f\u4e0d\u5356\u5e10\uff0c\u5144\u5f1f\u5feb\u8d70\uff01\u201d\u5f80\u5916\u786c\u95ef\uff01 \u3000\u3000\u8fd9\u4e00\u8fde\u4e32\u52a8\u4f5c\uff0c\u90fd\u53d1\u751f\u5728\u77ac\u606f\u4e4b\u95f4\uff0c\u4f17\u6b66\u58eb\u6295\u9f20\u5fcc\u5668\uff0c\u7eb7\u7eb7\u8d70\u907f\uff0c\u5230\u8fde\u57ce\u864e\u5b9a\u4e86\u5fc3\u795e\uff0c \u660e\u767d\u771f\u76f8\u4e4b\u65f6\uff0c\u4e8c\u795e\u5df2\u95ef\u51fa\u5e99\u95e8\u53bb\u4e86\u3002\u8fde\u57ce\u864e\u5927\u6012\uff0c\u53cc\u94a9\u659c\u98de\uff0c\u5206\u53d6\u738b\u5353\u4e8c\u4eba\u3002\u738b\u5353\u90fd\u53d7\u4e86 \u6293\u4f24\uff0c\u817e\u632a\u4e0d\u4fbf\uff0c\u770b\u770b\u53cc\u94a9\u5df2\u5230\uff0c\u65e0\u80fd\u8eb2\u907f\u3002\u7edd\u6025\u4e4b\u9645\uff0c\u5353\u4e00\u822a\u5ffd\u7136\u8eab\u5b50\u4e00\u6b6a\uff0c\u6447\u6447\u6b32\u5012\uff0c \u624b\u4e2d\u5b9d\u5251\u5374\u7a81\u7136\u5f80\u4e0a\u4e00\u6311\uff0c\u8868\u9762\u770b\u6765\u4f3c\u662f\u4e0d\u6210\u7ae0\u6cd5\uff0c\u90a3\u6599\u8fde\u57ce\u864e\u4e00\u94a9\u9493\u53bb\uff0c\u5374\u6251\u4e2a\u7a7a\uff0c\u5353\u4e00 \u822a\u7684\u5251\u52bf\u4f38\u7f29\u4e0d\u5b9a\uff0c\u5728\u8fde\u57ce\u864e\u7edd\u672a\u6599\u5230\u7684\u65b9\u4f4d\u4e0a\u7a81\u7136\u8fdb\u5251\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u8fde\u57ce\u864e\u5de6\u81c2\u523a \u5f97\u900f\u9aa8\u800c\u8fc7\uff01 \u3000\u3000\u8fde\u57ce\u864e\u60e8\u53eb\u4e00\u58f0\uff0c\u6025\u9000\u51e0\u6b65\uff0c\u5947\u75db\u5f7b\u9aa8\uff0c\u5de6\u81c2\u987f\u65f6\u5782\u4e86\u4e0b\u6765\u3002\u4ed6\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\u5353\u4e00\u822a\u7684 \u5251\u6cd5\u5ffd\u7136\u7cbe\u5999\u5982\u65af\uff0c\u4e0d\u89c9\u6c14\u9981\u3002\u539f\u6765\u5353\u4e00\u822a\u8fd9\u4e00\u62db\u5728\u4e34\u5371\u4e4b\u9645\u88ab\u8feb\u51fa\u6765\u7684\u5251\u6cd5\uff0c\u6b63\u662f\u8fbe\u6469\u7956 \u5e08\u9057\u7559\u4e0b\u6765\u7684\u51e0\u4e2a\u5251\u5f0f\u4e4b\u4e00\uff0c\u6b66\u5f53\u6d3e\u7684\u524d\u8f88\u957f\u8001\u56e0\u5b83\u65ad\u7eed\u51cc\u4e71\uff0c\u4e0d\u6210\u7ae0\u6cd5\uff0c\u4ece\u6765\u672a\u66fe\u60f3\u8fc7\u53ef \u4ee5\u4e34\u9635\u5b9e\u7528\uff0c\u5353\u4e00\u822a\u5374\u63e3\u6469\u719f\u900f\uff0c\u5927\u80c6\u8bd5\u7528\uff0c\u51fa\u4e4e\u610f\u6599\uff0c\u7adf\u594f\u5947\u529f\uff0c\u5a01\u529b\u4e4b\u5927\uff0c\u8fd8\u5728\u4ed6\u7684\u60f3 \u50cf\u4e4b\u4e0a\u3002 \u3000\u3000\u5353\u4e00\u822a\u4e00\u5251\u5f97\u624b\uff0c\u80c6\u6c14\u9661\u589e\uff0c\u5237\u5237\u51e0\u5251\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u6307\u5357\u6253\u5317\uff0c\u970e\u773c\u4e4b\u95f4\uff0c\u53c8\u4f24\u4e86\u51e0 \u4eba\u3002\u8fde\u57ce\u864e\u53c8\u60ca\u53c8\u6012\uff0c\u53cc\u94a9\u4e00\u5c55\uff0c\u5de6\u9493\u62a4\u80f8\uff0c\u53f3\u9493\u5e94\u654c\uff0c\u5c0f\u5fc3\u8fdb\u62db\uff0c\u5353\u4e00\u822a\u5230\u5e95\u53ea\u662f\u8bc6\u5f97\u51e0 \u4e2a\u602a\u62db\uff0c\u51e0\u62db\u4e00\u8fc7\uff0c\u53c8\u88ab\u8feb\u9000\u3002\u8fde\u57ce\u864e\u867d\u662f\u53d7\u4f24\uff0c\u6b66\u529f\u5c1a\u5728\uff0c\u66f4\u517c\u738b\u5353\u4e24\u4eba\u4e5f\u662f\u53d7\u4f24\uff0c\u800c\u4e14 \u654c\u4f17\u6211\u5be1\uff0c\u4ed7\u51e0\u62db\u602a\u62db\uff0c\u7ec8\u96be\u9632\u62a4\u3002\u987f\u65f6\u5f62\u52bf\u53c8\u7d27\u8d77\u6765\u3002 \u3000\u3000\u95e8\u5916\u5578\u58f0\u53c8\u8d77\uff0c\u5353\u4e00\u822a\u5927\u559c\uff0c\u62da\u529b\u652f\u6491\uff0c\u9884\u8ba1\u94c1\u98de\u9f99\u9a6c\u4e0a\u53ef\u5230\uff0c\u4e0d\u6599\u5578\u58f0\u5ffd\u6b62\uff0c\u4e0d\u89c1\u8e2a \u5f71\u3002\u8fde\u57ce\u864e\u7387\u4f17\u6b66\u58eb\u6025\u653b\uff0c\u6df7\u6218\u4e2d\u5353\u4e00\u822a\u8170\u80ef\u4e2d\u4e86\u4e00\u5200\uff0c\u75db\u6781\u5927\u53eb\uff0c\u738b\u7167\u5e0c\u6025\u53eb\u9053\uff1a\u201c\u94c1\u8001 \u82f1\u96c4\uff0c\u4f60\u600e\u9ebd\u8fd8\u4e0d\u6765\u5440\uff01\u201d\u8fde\u57ce\u864e\u53cc\u94a9\u4e00\u8d77\uff0c\u7167\u738b\u7167\u5e0c\u53cc\u8155\u6025\u526a\uff01 \u3000\u3000\u4e14\u8bf4\u94c1\u98de\u9f99\u8001\u65bc\u6c5f\u6e56\u4e16\u6545\uff0c\u542c\u767d\u654f\u8bf4\u738b\u7167\u5e0c\u5728\u4e2d\u6761\u5c71\u805a\u4f1a\u4e4b\u524d\u4e24\u53c1\u5929\uff0c\u4f1a\u5230\u81ea\u5df2\u5bb6\u6765\uff0c \u4f46\u5230\u671f\u4e0d\u81f3\uff0c\u6599\u77e5\u5fc5\u6709\u610f\u5916\uff0c\u65bc\u662f\u5e26\u4e86\u767d\u654f\u4e0e\u5ba2\u5a09\u5a77\u4e58\u4e86\u53c1\u9a91\u5065\u9a6c\uff0c\u6cbf\u9014\u7a0d\u4f5c\u6b47\u606f\uff0c\u8d76\u4e86\u4e00 \u65e5\u4e00\u591c\uff0c\u8d76\u5230\u4e2d\u6761\u5c71\u4e0b\u3002\u5ffd\u89c1\u5c71\u5761\u4e0a\u6709\u5341\u9980\u56db\u9a6c\u5403\u8349\uff0c\u4f0f\u5730\u4e00\u542c\uff0c\u5c71\u5761\u4e0a\u53c8\u9690\u9690\u6709\u6740\u4e4b\u58f0\uff0c \u5bf9\u767d\u654f\u9053\uff1a\u201c\u738b\u7167\u5e0c\u4e00\u5b9a\u662f\u9047\u7740\u4f0f\u5175\u4e86\uff0c\u54b1\u4eec\u6765\u5f97\u6b63\u662f\u65f6\u5019\uff01\u201d\u65bc\u662f\u8fde\u53d1\u957f\u5578\uff0c\u5411\u738b\u7167\u5e0c\u4f20 \u8baf\uff0c\u597d\u8ba9\u4ed6\u6709\u52c7\u6c14\u652f\u6301\u3002 \u3000\u3000\u53c1\u4eba\u4e0b\u9a6c\u722c\u4e0a\u5c71\u5761\uff0c\u89c1\u4e86\u90a3\u8352\u5c71\u91ce\u5e99\uff0c\u6740\u4e4b\u58f0\u6b63\u662f\u4ece\u91cc\u9762\u4f20\u51fa\u3002\u767d\u654f\u50bb\u864e\u864e\u7684\u7b11\u9053\uff1a \u201c\u8981\u6253\u67b6\u4e5f\u8be5\u627e\u4e2a\u597d\u5730\u65b9\uff0c\u653e\u7740\u5916\u9762\u8fd9\u4e00\u5927\u7247\u5c71\u5730\u4e0d\u6253\uff0c\u5374\u5728\u5e99\u5b50\u91cc\u6253\uff0c\u96be\u9053\u662f\u60f3\u5413\u6740\u5c71\u795e \u5417\uff1f\u201d\u5ba2\u5a09\u5a77\u5657\u55e4\u4e00\u7b11\uff0c\u767d\u654f\u9053\uff1a\u201c\u5ba2\u59d1\u5a18\uff0c\u6211\u6709\u4ec0\u9ebd\u8bf4\u9519\u4e86\uff1f\u201d \u3000\u3000\u6b63\u8bf4\u8bdd\u95f4\uff0c\u5ffd\u89c1\u5e99\u4e2d\u6709\u4e24\u4eba\u51b2\u51fa\uff0c\u94c1\u98de\u9f99\u53eb\u9053\uff1a\u201c\u7ed9\u6211\u7ad9\u7740\uff01\u201d\u51dd\u7738\u4e00\u770b\uff0c\u5374\u662f\u795e\u5bb6\u4e24 \u4e2a\u602a\u7269\u3002\u795e\u5927\u5143\u8fd8\u80a9\u7740\u91d1\u72ec\u5f02\u7684\u4f84\u5b50\u91d1\u5343\u3002\u8fd9\u4e00\u4e0b\u5927\u51fa\u94c1\u98de\u9f99\u610f\u5916\uff0c\u559d\u95ee\u9053\uff1a\u201c\u738b\u7167\u5e0c\u5728\u91cc \u9762\u9ebd\uff1f\u201d\u795e\u5927\u5143\u9053\uff1a\u201c\u4ec0\u9ebd\u738b\u7167\u5e0c\uff1f\u6211\u4e0d\u77e5\u9053\u201c\u6211\u4eec\u4e24\u5144\u5f1f\u5728\u5c71\u795e\u5e99\u907f\u96ea\u9047\u4f0f\uff0c\u548c\u4e1c\u5382\u6869\u5934 \u53ca\u9648\u5947\u745c\u624b\u4e0b\u7684\u6b66\u58eb\u5341\u591a\u4eba\u6076\u6597\uff0c\u64d2\u4e86\u8fd9\uff0c\u624d\u9003\u5f97\u51fa\u3002\u8001\u94c1\uff0c\u6211\u4e24\u5144\u5f1f\u73b0\u5728\u7b4b\u75b2\u529b\u7aed\uff0c\u4f60\u82e5 \u60f3\u62ff\u6211\u4eec\u4e24\u5144\u5f1f\u732e\u529f\uff0c\u6b63\u662f\u65f6\u5019\u3002\u201d\u94c1\u98de\u9f99\u6012\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff01\u6211\u8001\u94c1\u5c82\u662f\u5982\u6b64\u4e4b\u4eba\u3002\u91cc\u9762 \u8fd8\u6709\u4ec0\u9ebd\u4eba\uff1f\u5b98\u519b\u4e3a\u4ec0\u9ebd\u4e0d\u51b2\u51fa\u6765\u6355\u4f60\u3002\u201d\u795e\u5927\u5143\u54a7\u5634\u7b11\u9053\uff1a\u201c\u94c1\u8001\u513f\uff0c\u4f60\u5f53\u6211\u4eec\u4e24\u5144\u5f1f\u662f \u7b49\u95f2\u4e4b\u8f88\u9ebd\uff1f\u6211\u867d\u7136\u53d7\u4e86\u4f24\uff0c\u4e5f\u628a\u4ed6\u4eec\u7684\u4eba\u6253\u4f24\u4e86\u5341\u51e0\u4e2a\u3002\u4ed6\u4eec\u6b63\u5728\u6551\u6b7b\u6276\u4f24\uff0c\u8fde\u91d1\u5343\u4e5f\u88ab \u6211\u4eec\u4fd8\u83b7\u51fa\u6765\uff0c\u90a3\u91cc\u8fd8\u6562\u8ffd\u6355\uff01\u201d\u94c1\u98de\u9f99\u89c1\u4ed6\u811a\u8e1d\u6d41\u8840\uff0c\u8d70\u8def\u4e00\u8ddb\u4e00\u62d0\uff0c\u800c\u4e14\u786e\u5b9e\u662f\u6349\u4e86\u91d1 \u5343\uff0c\u5fc3\u60f3\u4ed6\u4e24\u5144\u5f1f\u6b66\u529f\u4e0d\u9519\uff0c\u8bf4\u7684\u8bb8\u662f\u5b9e\u60c5\u3002\u65e2\u7136\u91cc\u9762\u6ca1\u6709\u738b\u7167\u5e0c\uff0c\u6211\u4f55\u82e6\u518d\u53bb\u4e0e\u90a3\u4e9b\u53d7\u4f24 \u7684\u5b98\u519b\u4e3a\u96be\uff1f\u505c\u4e00\u811a\u6b65\u3002\u795e\u5927\u5143\u9053\uff1a\u201c\u94c1\u8001\u513f\uff0c\u4f60\u65e2\u4e0d\u60f3\u62ff\u6211\u4eec\u732e\u529f\uff0c\u90a3\u9ebd\uff0c\u5bf9\u4e0d\u4f4f\uff0c\u6211\u4eec \u53ef\u8981\u8d70\u5566\uff01\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u8d70\u4fbf\u8d70\uff0c\u7f57\u5506\u4ec0\u9ebd\uff01\u201d\u795e\u5bb6\u5144\u5f1f\u5411\u5c71\u4e0b\u98de\u8dd1\uff0c\u94c1\u98de\u9f99\u5ffd\u9053\uff1a \u201c\u505c\u7740\uff01\u201d\u795e\u5927\u5143\u56de\u5934\u9053\uff1a\u201c\u600e\u9ebd\uff1f\u53d8\u4e86\u4e3b\u610f\u5417\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u5c06\u91d1\u5343\u7ed9\u6211\u7559\u4e0b\uff01\u201d\u795e\u5927 \u5143\u7528\u529b\u4e00\u629b\uff0c\u91d1\u5343\u5728\u534a\u7a7a\u4e2d\u60e8\u53eb\u4e00\u58f0\uff0c\u843d\u5230\u94c1\u98de\u9f99\u624b\u4e0a\u4e4b\u65f6\uff0c\u5df2\u662f\u5bc2\u7136\u4e0d\u52a8\uff0c\u94c1\u98de\u9f99\u4fef\u8eab\u4e00 \u770b\uff0c\u539f\u6765\u4ed6\u7684\u5589\u9aa8\u5df2\u88ab\u795e\u5927\u5143\u7528\u638c\u529b\u634f\u788e\u3002 \u3000\u3000\u94c1\u98de\u9f99\u9053\uff1a\u201c\u8fd9\u662f\u6740\u5bb3\u8d1e\u4e7e\u9053\u4eba\u7684\u51f6\u624b\uff0c\u5bb3\u6211\u7684\u5973\u513f\u4ed6\u4e5f\u6709\u4efd\uff0c\u6b7b\u4e0d\u8db3\u60dc\uff01\u8ba9\u4ed6\u4e86\u997f\u72fc \u5427\uff01\u201d\u632f\u81c2\u4e00\u629b\uff0c\u5c06\u91d1\u5343\u7684\u8eab\u629b\u4e0b\u5c71\u8c37\u3002\u5ffd\u7136\u60f3\u9053\uff1a\u201c\u4f55\u4ee5\u795e\u5927\u5143\u8981\u5c06\u4ed6\u634f\u6b7b\u4e4b\u5f8c\u624d\u4ea4\u7ed9 \u6211\uff1f\u201d\u94c1\u98de\u9f99\u4e43\u662f\u6c5f\u6e56\u4e0a\u7684\u5927\u884c\u5bb6\uff0c\u5bf9\u9ed1\u9053\u4e0a\u7684\u4f0e\u4fe9\u65e0\u4e00\u4e0d\u8bc6\uff0c\u84e6\u7136\u9192\u8d77\uff1a\u83ab\u975e\u8fd9\u662f\u6740\u4eba\u706d \u53e3\u4e4b\u8ba1\uff0c\u795e\u5927\u5143\u6709\u4ec0\u9ebd\u4e8b\u4e0d\u613f\u8ba9\u6211\u77e5\u9053\uff1f\u6b63\u5728\u6b64\u65f6\uff0c\u5e99\u4e2d\u5ffd\u4f20\u51fa\u5353\u4e00\u822a\u60e8\u53eb\u4e4b\u58f0\uff0c\u63a5\u7740\u662f\u738b \u7167\u5e0c\u547c\u5524\u94c1\u98de\u9f99\u4e4b\u58f0\u3002\u94c1\u98de\u9f99\u53eb\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u4e2d\u4e86\u795e\u5927\u5143\u4e4b\u8ba1\u4e86\uff01\u4ed6\u4f7f\u7684\u662f\u7f13\u5175\u4e4b\u8ba1\uff0c\u8981\u5728 \u6211\u5230\u573a\u4e4b\u524d\uff0c\u501f\u5200\u6740\u4eba\uff0c\u8ba9\u5b98\u519b\u5c06\u738b\u7167\u5e0c\u5e72\u6389\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u8bc6\u7834\u5978\u8ba1\uff0c\u52c3\u7136\u5927\u6012\uff0c\u8fd9\u65f6\u5df2\u65e0\u6687\u518d\u53bb\u8ffd\u795e\u5bb6\u5144\u5f1f\uff0c\u864e\u543c\u4e00\u58f0\uff0c\u8dc3\u4e0a\u5c71\u5761\uff0c\u51b2\u5165 \u5e99\u4e2d\uff0c\u53ea\u89c1\u8fde\u57ce\u864e\u53cc\u94a9\u95ea\u95ea\uff0c\u6b63\u5bf9\u738b\u7167\u5e0c\u65bd\u5c55\u6740\u624b\uff01 \u3000\u3000\u94c1\u98de\u9f99\u7741\u76ee\u5927\u559d\uff0c\u987a\u624b\u4e00\u635e\uff0c\u5c06\u8fce\u4e0a\u524d\u6765\u7684\u4e00\u540d\u6b66\u58eb\u64d2\u7740\uff0c\u5411\u8fde\u57ce\u864e\u63b7\u53bb\uff0c\u8fde\u57ce\u864e\u4fa7\u8eab \u4e00\u95ea\uff0c\u53cc\u94a9\u523a\u4eba\u90a3\u6b66\u58eb\u8089\u4e2d\uff0c\u738b\u7167\u5e0c\u8d81\u52bf\u4e00\u5251\uff0c\u51b2\u523a\u51fa\u53bb\u3002\u5353\u4e00\u822a\u7cbe\u795e\u5927\u632f\uff0c\u8fde\u73af\u53c1\u5251\u8fde\u4f24 \u53c1\u654c\uff0c\u4e5f\u51b2\u6740\u51fa\u6765\uff0c\u94c1\u98de\u9f99\u53eb\u9053\uff1a\u201c\u5353\u4e00\u822a\uff0c\u4f60\u4e5f\u5728\u6b64\u9ebd\uff1f\u201d\u8fde\u57ce\u864e\u65cb\u98ce\u822c\u63a0\u8fc7\u94c1\u98de\u9f99\u8eab \u8fb9\uff0c\u94c1\u98de\u9f99\u53c8\u662f\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u5288\u51fa\uff0c\u8fde\u57ce\u864e\u53cc\u94a9\u4e00\u67b6\uff0c\u4ed6\u5de6\u81c2\u53d7\u4f24\u529b\u5f31\uff0c\u88ab\u94c1\u98de\u9f99\u795e\u529b\u4e00 \u683c\uff0c\u5de6\u624b\u9493\u9707\u4e0a\u534a\u7a7a\uff0c\u523a\u4eba\u5c4b\u6a90\uff0c\u90a3\u6562\u5e94\u6218\uff0c\u6025\u6025\u5916\u95ef\uff0c\u94c1\u98de\u9f99\u62d4\u817f\u4fbf\u8ffd\uff0c\u6b63\u5de7\u5ba2\u5a09\u5a77\u4e0e\u767d \u654f\u53cc\u53cc\u8fdb\u5165\uff0c\u88ab\u8fde\u57ce\u864e\u5355\u9493\u4e00\u62e6\uff0c\u628a\u767d\u654f\u7684\u8f6f\u97ad\u626f\u98de\uff0c\u5c06\u5ba2\u5a09\u5a77\u7684\u5355\u5251\u4e5f\u9501\u7740\uff0c\u4e24\u4eba\u90fd\u7ed9\u4ed6 \u62e6\u8fc7\u4e00\u8fb9\uff0c\u6070\u6070\u963b\u7740\u4e86\u94c1\u98de\u9f99\u7684\u8def\u3002\u8fde\u57ce\u864e\u51b2\u51fa\u5e99\u95e8\uff0c\u6ca1\u547d\u98de\u9003\u53bb\u4e86\u3002 \u3000\u3000\u767d\u654f\u53eb\u9053\uff1a\u201c\u5440\uff0c\u738b\u54e5\u54e5\uff0c\u4f60\u53d7\u4e86\u4f24\u4e86\uff01\u201d\u62a2\u4e86\u4e00\u540d\u6b66\u58eb\u7684\u5200\uff0c\u4e71\u65a9\u654c\u4eba\uff0c\u90a3\u4e9b\u6b66\u58eb\u89c1 \u4e3b\u5e05\u9003\u547d\uff0c\u53d1\u4e00\u58f0\u558a\uff0c\u7eb7\u7eb7\u5411\u5916\u5954\u9003\u3002\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u4e0d\u8981\u4e71\u780d\u4e71\u6740\uff1f\u201d\u767d\u654f\u751a\u4e3a\u542c\u8bdd\uff0c\u679c\u7136 \u505c\u624b\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u90a3\u4e9b\u6b66\u58eb\u9003\u5f97\u4e7e\u4e7e\u51c0\u51c0\u3002 \u3000\u3000\u94c1\u98de\u9f99\u68c0\u89c6\u4e24\u4eba\u4f24\u52bf\uff0c\u9053\uff1a\u201c\u8fd9\u662f\u795e\u5bb6\u5144\u5f1f\u6293\u4f24\u7684\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6b63\u662f\uff01\u201d\u94c1\u98de\u9f99\u6012 \u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u597d\u6bd2\uff01\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u4ed6\u4eec\u4e0d\u80af\u6295\u964d\u5b98\u519b\uff0c\u8fd8\u7b97\u6709\u4e00\u70b9\u5fd7\u6c14\u3002\u53ea\u662f\u4ed6\u4eec\u884c\u4e8b\u5982 \u6b64\u4e56\u8c2c\uff0c\u82e5\u8fd8\u8ba9\u4ed6\u4eec\u5728\u5f20\u732e\u5fe0\u8eab\u8fb9\uff0c\u7ec8\u662f\u5927\u60a3\u3002\u201d\u5f53\u4e0b\u5c06\u795e\u5bb6\u5144\u5f1f\u7684\u884c\u4e8b\u8bf4\u4e86\u3002\u94c1\u98de\u9f99\u9053\uff1a \u201c\u5f85\u6211\u53bb\u89c1\u5f20\u732e\u5fe0\uff0c\u52a1\u5fc5\u53eb\u4ed6\u60e9\u6cbb\u8fd9\u4e24\u4e2a\u6076\u8d3c\u3002\u201d \u3000\u3000\u738b\u5353\u4e8c\u4eba\u5e78\u559c\u53d7\u4f24\u4e0d\u91cd\uff0c\u53ea\u662f\u6597\u4e86\u534a\u591c\uff0c\u75b2\u5026\u4e0d\u582a\uff0c\u94c1\u98de\u9f99\u7ed9\u4ed6\u4eec\u6577\u4e0a\u91d1\u521b\u5723\u836f\uff0c\u8981\u4ed6 \u4eec\u8fd0\u6c14\u9759\u5750\uff0c\u6062\u590d\u75b2\u52b3\u3002\u5ba2\u5a09\u5a77\u548c\u767d\u654f\u5077\u5077\u6307\u7740\u5353\u4e00\u822a\u8c08\u8bba\uff0c\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u8fd9\u4e2a\u767d\u9762\u4e66\u751f\uff0c \u662f\u5353\u4e00\u822a\u5417\uff1f\u201d\u767d\u654f\u9053\uff1a\u201c\u662f\u5440\uff0c\u4f60\u4e0d\u77e5\u9053\u5417\uff1f\u4ed6\u662f\u6211\u7684\u597d\u670b\u53cb\u54e9\uff01\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u54fc\uff01\u8fd9 \u6837\u7684\u597d\u670b\u53cb\uff01\u201d\u767d\u654f\u6781\u4e0d\u9ad8\u5174\uff0c\u5927\u58f0\u95ee\u9053\uff1a\u201c\u4ed6\u6709\u4ec0\u9ebd\u4e0d\u597d\uff1f\u201d\u94c1\u98de\u9f99\u201c\u5618\u201d\u4e86\u4e00\u58f0\uff0c\u793a\u610f \u53eb\u4ed6\u4eec\u5c0f\u58f0\u3002\u5ba2\u5a09\u5a77\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u82e5\u7136\u4ed6\u597d\uff0c\u4e3a\u4f55\u4ee4\u6211\u7ec3\u59d0\u59d0\u4f24\u5fc3\uff01\u201d\u767d\u654f\u6115\u7136\u4e0d\u89e3\uff0c\u95ee \u9053\uff1a\u201c\u90a3\u4e2a\u7ec3\u59d0\u59d0\uff1f\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u5c31\u662f\u7389\u7f57\u5239\u5440\uff01\u201d\u767d\u654f\u5bf9\u7389\u7f57\u5239\u867d\u65e0\u6076\u611f\uff0c\u4ea6\u65e0\u597d\u611f\uff0c \u9053\uff1a\u201c\u90a3\u4e2a\u5973\u9b54\u5934\u4e5f\u4f1a\u4f24\u5fc3\u7684\u5417\uff1f\u201d\u5ba2\u5a09\u5a77\u5658\u5634\u8bf4\u9053\uff1a\u201c\u6789\u4f60\u662f\u7eff\u6797\u4e2d\u4eba\uff0c\u7389\u7f57\u5239\u4e0d\u8fc7\u5ac9\u6076 \u5982\u4ec7\uff0c\u884c\u4e8b\u4efb\u6027\u800c\u5df2\uff0c\u5979\u600e\u9ebd\u662f\u5973\u9b54\u5934\u3002\u201d\u767d\u654f\u9053\uff1a\u201c\u597d\uff0c\u7b97\u6211\u8bf4\u9519\u3002\u5979\u4e0d\u662f\u5973\u9b54\u5934\uff0c\u4f46\u4ee4 \u5979\u4f24\u5fc3\uff0c\u4e5f\u4e0d\u662f\u4ec0\u9ebd\u5927\u4e0d\u4e86\u4e4b\u4e8b\u5440\uff01\u201d\u5ba2\u5a09\u5a77\u6c14\u9053\uff1a\u201c\u4f60\u8fd9\u50bb\u5c0f\u5b50\uff0c\u6211\u95ee\u4f60\uff0c\u6bd4\u5982\u8bf4\uff0c\u4f60\u82e5 \u4ee4\u6211\u4f24\u5fc3\uff0c\u4f60\u8fd8\u80fd\u7b97\u662f\u597d\u4eba\u5417\uff1f\u201d\u767d\u654f\u60f3\u4e86\u4e00\u60f3\uff0c\u9053\uff1a\u201c\u4f60\u6551\u4e86\u6211\uff0c\u4f60\u5f85\u6211\u8fd9\u6837\u597d\uff0c\u6211\u82e5\u4ee4 \u4f60\u4f24\u5fc3\uff0c\u6211\u5c31\u662f\u9f9f\u513f\u5b50\uff01\u201d\u5ba2\u5a09\u5a77\u5657\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u8fd9\u5c31\u662f\u4e86\u3002\u4f60\u8fd8\u4e0d\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u5ba2\u5a09\u5a77\u867d\u7136\u5c0f\u58f0\uff0c\u5353\u4e00\u822a\u9759\u5750\u51dd\u795e\uff0c\u5374\u662f\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u9762\u4e0a\u4e00\u9635\u7ea2\u4e00\u9635\u767d\uff0c\u8fd9\u4efd\u96be \u8fc7\uff0c\u53ef\u522b\u63d0\u5566\uff01\u4ed6\u4e0d\u5f85\u75b2\u52b3\u6062\u590d\uff0c\u84e6\u7136\u8df3\u4e86\u8d77\u6765\u3002 \u3000\u3000\u767d\u654f\u614c\u9053\uff1a\u201c\u5353\u54e5\u54e5\uff0c\u6211\u662f\u8bf4\u6211\u82e5\u4ee4\u5ba2\u59d1\u5a18\u4f24\u5fc3\uff0c\u6211\u5c31\u662f\u9f9f\u513f\u5b50\u3002\u6211\u4e0d\u662f\u8bf4\u4f60\u3002\u4f60\u4e0d\u8981 \u751f\u5144\u5f1f\u7684\u6c14\uff01\u201d\u5353\u4e00\u822a\u5411\u5ba2\u5a09\u5a77\u4f5c\u4e86\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u8d23\u5907\u5f97\u5bf9\uff01\u201d\u58f0\u97f3\u54fd\u54bd\uff0c\u8d70\u5230\u94c1 \u98de\u9f99\u773c\u524d\uff0c\u957f\u63d6\u5230\u5730\uff0c\u95ee\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\u5462\uff1f\u600e\u9ebd\u4e0d\u89c1\u5979\u6765\uff1f\u5979\u4e0d\u5728\u4f60\u8001\u5bb6\u4e2d\u5417\uff1f\u201d \u3000\u3000\u94c1\u98de\u9f99\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u5979\u6765\u8fc7\u5566\u3002\u201d\u5353\u4e00\u822a\u6025\u95ee\u9053\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u5979\u53c8\u53bb \u5566\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5979\u53bb\u90a3\u91cc\uff1f\u201d\u94c1\u98de\u9f99\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002\u201d\u5353\u4e00\u822a\u6025\u9053\uff1a\u201c\u4f60\u4e00\u5b9a \u77e5\u9053\u3002\u4f60\u4e0d\u77e5\u9053\uff0c\u5c31\u6ca1\u6709\u4eba\u77e5\u9053\u5566\uff0c\u6211\u4eca\u751f\u4eca\u4e16\uff0c\u82e5\u4e0d\u89c1\u5979\u4e00\u9762\uff0c\u6b7b\u96be\u7791\u76ee\uff01\u201d \u3000\u3000\u94c1\u98de\u9f99\u62ac\u5934\u671b\u5929\uff0c\u5f69\u971e\u6ee1\u5929\uff0c\u671d\u9633\u5c04\u76ee\uff0c\u5ba2\u5a09\u5a77\u6068\u6068\u8bf4\u9053\uff1a\u201c\u5979\u5728\u5929\u8fb9\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a \u201c\u5979\u5728\u5929\u8fb9\uff0c\u6211\u4e5f\u8981\u53bb\uff01\u201d\u94c1\u98de\u9f99\u51dd\u601d\u4e00\u9635\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u5979\u867d\u7136\u4e0d\u5728\u5929\u8fb9\uff0c\u53ef\u662f\u4e5f\u8ddf\u5728\u5929 \u8fb9\u5dee\u4e0d\u591a\u3002\u6211\u60f3\u5979\u4e5f\u8bb8\u662f\u5230\u5929\u5c71\u53bb\u4e86\u3002\u4f60\u8981\u627e\u5979\uff0c\u53ef\u8981\u8fdc\u8d70\u585e\u5916\uff0c\u6c99\u6f20\u98ce\u5bd2\u4e4b\u82e6\uff0c\u4f60\u8fd9\u8d35\u516c \u5b50\u53d7\u5f97\u4e86\u5417\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f11\u8bf4\u6c99\u6f20\u98ce\u5bd2\uff0c\u5c31\u662f\u6c34\u6df1\u706b\u70ed\uff0c\u6211\u4e5f\u8981\u53bb\uff01\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u5929 \u5c71\u7ef5\u4e98\u53c1\u5343\u591a\u91cc\uff0c\u4f60\u4e5f\u672a\u5fc5\u627e\u5f97\u7740\u5979\uff01\u201d\u5ba2\u5a09\u5a77\u63d2\u53e3\u9053\uff1a\u201c\u5979\u4e5f\u672a\u5fc5\u89c1\u4f60\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u5fc3\u4e2d\u5927\u75db\uff0c\u5782\u4e0b\u6cea\u6765\uff0c\u9053\uff1a\u201c\u5979\u4e0d\u89c1\u6211\uff0c\u6211\u4e5f\u8981\u89c1\u5979\u3002\u5373\u7b97\u7ec8\u65bc\u4e0d\u89c1\uff0c\u4f4f\u5f97\u548c\u5979 \u76f8\u8fd1\u4e00\u4e9b\uff0c\u6211\u4e5f\u5fc3\u5b89\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u4f60\u65e2\u7136\u5982\u6b64\u8bda\u5fc3\uff0c\u90a3\u5c31\u53bb\u5427\uff01\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u53ef\u662f\u5979 \u5934\u53d1\u5df2\u7ecf\u767d\u4e86\uff0c\u5df2\u7ecf\u4e0d\u662f\u4ece\u524d\u7684\u7ec3\u9713\u88f3\u4e86\uff0c\u4f60\u89c1\u4e86\u5979\uff0c\u4e5f\u8bb8\u4f1a\u5931\u671b\u4e86\uff01\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4ec0 \u9ebd\uff1f\u5979\u767d\u4e86\u5934\u53d1\uff0c\u4e00\u5b9a\u662f\u56e0\u6211\u4f24\u5fc3\uff0c\u75db\u6781\u767d\u5934\u7684\u4e86\u3002\u201d\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4fbf\u597d\u3002\u201d\u5353\u4e00\u822a \u4f24\u5fc3\u4e4b\u6781\uff0c\u6b32\u54ed\u65e0\u6cea\uff0c\u6bc5\u7136\u8bf4\u9053\uff1a\u201c\u83ab\u8bf4\u5979\u767d\u4e86\u5934\u53d1\uff0c\u5373\u7b97\u9e21\u76ae\u9e64\u53d1\uff0c\u6211\u4e5f\u7edd\u4e0d\u53d8\u5fc3\u3002\u6d77\u67af \u77f3\u70c2\uff0c\u5929\u8352\u5730\u8001\uff0c\u6b64\u60c5\u4e0d\u53d8\u3002\u7687\u5929\u540e\u571f\uff0c\u53ef\u9274\u6211\u8a00\u3002\u201d \u3000\u3000\u5ba2\u5a09\u5a77\u9053\uff1a\u201c\u4f60\u8fd9\u4e9b\u8bdd\u7559\u5f85\u89c1\u4e86\u7ec3\u59d0\u59d0\u65f6\u518d\u8bf4\u5427\u3002\u201d\u94c1\u98de\u9f99\u62c8\u80e1\u5fae\u7b11\uff0c\u9053\uff1a\u201c\u5a09\u5a77\uff0c\u4e0d \u8981\u53d6\u7b11\u4ed6\u4e86\u3002\u597d\uff0c\u7cbe\u8bda\u6240\u81f3\uff0c\u91d1\u77f3\u4e3a\u5f00\u3002\u4f60\u4f24\u597d\u4e4b\u5f8c\uff0c\u4fbf\u53ef\u524d\u53bb\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u73b0\u5728\u4f24\u5df2\u597d\u4e86\u3002\u201d\u738b\u7167\u5e0c\u505a\u597d\u5185\u529f\u529f\u8bfe\uff0c\u8df3\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5353\u5144\uff0c\u4f60\u5c31 \u8981\u8d70\u4e86\u5417\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u662f\u7684\uff0c\u5c31\u8981\u8d70\u4e86\uff01\u201d\u738b\u7167\u5e0c\u9053\uff1a\u201c\u513f\u5973\u4e4b\u60c5\uff0c\u867d\u7136\u7d27\u8981\uff0c\u5bb6\u56fd\u4e4b \u4e8b\uff0c\u4e5f\u5f53\u6302\u5fc3\uff0c\u6211\u529d\u4f60\u82e5\u662f\u627e\u4e0d\u89c1\u5979\uff0c\u8fd8\u662f\u56de\u6765\u7684\u597d\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5bb6\u56fd\u4e4b\u4e8b\u6709\u4f60\u4eec\u5728\uff0c \u6211\u53ef\u6bcb\u987b\u987e\u8651\u3002\u6211\u82e5\u4e0d\u80fd\u89c1\u5979\uff0c\u4fbf\u957f\u4f4f\u5929\u5c71\u4e86\u3002\u54b1\u4eec\u5f8c\u4f1a\u65e0\u671f\uff0c\u613f\u4f60\u4eec\u80fd\u6210\u5927\u4e1a\u3002\u4ed6\u65e5\u6d88\u606f \u4f20\u6765\uff0c\u6211\u5f53\u5728\u5929\u5c71\u4e3a\u4f60\u4eec\u9065\u795d\u3002\u201d\u94c1\u98de\u9f99\u9053\uff1a\u201c\u5728\u56de\u7586\u4f60\u4e5f\u53ef\u884c\u4fa0\u4ed7\u4e49\uff0c\u90a3\u8fb9\u6c11\u98ce\u7eaf\uff0c\u8bf4\u4e0d \u5b9a\u4ed6\u65e5\u4ea6\u6709\u4f5c\u4e3a\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u884c\u4fa0\u4ed7\u4e49\uff0c\u4e43\u662f\u6211\u8f88\u4efd\u6240\u5f53\u4e3a\uff0c\u8001\u524d\u8f88\u5429\u5490\uff0c\u6211\u5f53\u7262\u8bb0\u5728 \u5fc3\u3002\u201d\u65bc\u662f\u548c\u94c1\u98de\u9f99\u738b\u7167\u5e0c\u73cd\u91cd\u9053\u522b\u3002\u738b\u7167\u5e0c\u76ee\u9001\u4ed6\u80cc\u5f71\u4e0b\u5c71\uff0c\u6447\u4e86\u6447\u5934\uff0c\u534a\u664c\u65e0\u8bed\u3002\u4e4b\u5f8c \u5c31\u548c\u94c1\u98de\u9f99\u767d\u654f\u8ba8\u8bba\u4e2d\u6761\u5c71\u7fa4\u96c4\u805a\u4f1a\u4e4b\u4e8b\uff0c\u518d\u4e5f\u4e0d\u63d0\u5353\u4e00\u822a\u4e86\u3002\u6b63\u662f\uff1a\u516c\u5b50\u5fcf\u60c5\u5f92\u6709\u6068\uff0c\u82f1 \u96c4\u62a5\u56fd\u6700\u5173\u5fc3\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u516b\u56de\u3000\u585e\u5916\u6536\u5f92\u3000\u4e13\u5fc3\u4f20\u5251\u6cd5\u3000\u5929\u6daf\u8bbf\u53cb\u3000\u4e00\u610f\u89c5\u4f0a\u4eba \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377 \u7b2c\u4e8c\u5341\u516b\u56de \u585e\u5916\u6536\u5f92\u3000\u4e13\u5fc3\u4f20\u5251\u6cd5 \u5929\u6daf\u8bbf\u53cb\u3000\u4e00\u610f\u89c5\u4f0a\u4eba \u3000\u3000\u4e14\u8bf4\u5cb3\u545c\u73c2\u5230\u4e86\u5929\u5c71\u4e4b\u5f8c\uff0c\u524a\u53d1\u4e3a\u50e7\uff0c\u6539\u79f0\u6666\u660e\u5e08\uff0c\u6643\u773c\u56db\u5e74\u6709\u9980\u3002\u4ed6\u5230\u5929\u5c71\u5f8c\u7684\u7b2c\u4e8c \u5e74\uff0c\u7f57\u94c1\u81c2\u9001\u6768\u6d9f\u7684\u513f\u5b50\u6768\u4e91\u9aa2\u4e0a\u5c71\uff0c\u8bf4\u662f\u627f\u7389\u7f57\u5239\u7684\u4ecb\u7ecd\uff0c\u8981\u6666\u660e\u5e08\u6536\u6768\u4e91\u9aa2\u4e3a\u5f92\u3002\u6666\u660e \u5e08\u9053\uff1a\u201c\u5979\u600e\u9ebd\u8fd9\u6837\u591a\u4e8b\uff1f\u603b\u8981\u7ed9\u6211\u6dfb\u4e00\u70b9\u70e6\u3002\u201d\u8bdd\u867d\u5982\u6b64\uff0c\u4f46\u89c1\u6768\u4e91\u9aa2\u806a\u660e\u5f02\u5e38\uff0c\u6c14\u5b87\u975e \u51e1\uff0c\u65e9\u5df2\u6ee1\u5fc3\u559c\u7231\u3002\u66f4\u517c\u4ed6\u662f\u5fe0\u81e3\u4e4b\u5f8c\uff0c\u81ea\u7136\u4e00\u8bf4\u4fbf\u5408\u3002\u7acb\u5373\u884c\u4e86\u62dc\u5e08\u4e4b\u793c\u3002 \u3000\u3000\u81ea\u6b64\u6666\u660e\u5e08\u4e00\u9762\u8654\u5fc3\u7ec3\u5251\uff0c\u4e00\u9762\u4f20\u6388\u6768\u4e91\u9a8b\u7684\u6280\u4e1a\uff0c\u6768\u4e91\u9aa2\u4e0e\u6b66\u5b66\u751a\u6709\u5bbf\u7f18\uff0c\u6666\u660e\u5e08\u6559 \u4ed6\u4ece\u7ae5\u5b50\u529f\u8bfe\u7ec3\u8d77\uff0c\u4ed6\u4e0a\u5c71\u65f6\u521a\u6ee1\u4e03\u5c81\uff0c\u4e0d\u8fc7\u53c1\u5e74\uff0c\u4ed6\u5df2\u6839\u57fa\u624e\u5b9e\uff0c\u5f92\u624b\u53ef\u730e\u864e\u8c79\u3002\u6666\u660e\u5e08 \u5341\u5206\u559c\u6b22\uff0c\u5728\u5929\u5c71\u91c7\u4e94\u91d1\u4e4b\u7cbe\uff0c\u5c06\u5e08\u5085\u9057\u7559\u4e0b\u6765\u7684\u4e24\u53e3\u5b9d\u5251\uff0c\u91cd\u65b0\u94f8\u70bc\u3002\u4ed6\u5168\u5fc3\u653e\u5728\u6768\u4e91\u9aa2 \u8eab\u4e0a\uff0c\u4e45\u672a\u4e0b\u5c71\uff0c\u5bf9\u5916\u9762\u4e4b\u4e8b\uff0c\u5341\u5206\u9694\u819c\u3002\u53ea\u662f\u6709\u65f6\u4e2d\u5bb5\u7ec3\u5251\uff0c\u5bf9\u6708\u6000\u4eba\uff0c\u60f3\u8d77\u718a\u5ef7\u5f3c\u4e0e\u94c1 \u73ca\u745a\u7684\u60e8\u6b7b\uff0c\u8fd8\u4e0d\u514d\u5bf9\u6708\u957f\u55df\u3002 \u3000\u3000\u5230\u4e86\u7b2c\u53c1\u5e74\u7684\u65f6\u5019\uff0c\u5ffd\u7136\u4e0d\u65f6\u6709\u6f20\u5916\u7684\u6210\u540d\u4eba\u7269\u6765\u8bbf\u3002\u5411\u4ed6\u6253\u542c\u4e00\u4e2a\u5973\u5b50\u7684\u6765\u5386\uff0c\u539f\u6765 \u6666\u660e\u5e08\u867d\u662f\u5251\u6cd5\u65e0\u53cc\uff0c\u6b66\u529f\u7edd\u9876\uff0c\u53ef\u662f\u4ece\u4e0d\u4ed7\u6280\u9a84\u4eba\uff0c\u56e0\u6b64\u5728\u56de\u7586\u6570\u5e74\uff0c\u751a\u5f97\u4eba\u671b\u3002\u5929\u5c71\u5357 \u5317\u7684\u82f1\u96c4\uff0c\u56e0\u4ed6\u6765\u81ea\u4e2d\u539f\uff0c\u89c1\u8bc6\u5e7f\uff0c\u6240\u4ee5\u9047\u5230\u7591\u96be\u4e4b\u4e8b\uff0c\u6bcf\u591a\u5411\u4ed6\u8bf7\u6559\u3002 \u3000\u3000\u542c\u8fd9\u4e9b\u4eba\u8bf4\uff0c\u8bf4\u662f\u534a\u5e74\u4e4b\u524d\uff0c\u56de\u7586\u6765\u4e86\u4e00\u4e2a\u5973\u5b50\uff0c\u767d\u53d1\u6ee1\u5934\uff0c\u5bb9\u989c\u5374\u5ae9\uff0c\u770b\u5979\u5934\u53d1\uff0c\u50cf \u4e94\u516d\u5341\u5c81\u7684\u8001\u5a46\u5a46\uff0c\u770b\u5979\u9762\u8c8c\uff0c\u50cf\u5eff\u9980\u5c81\u7684\u5c11\u5973\u3002\u8fde\u5979\u662f\u591a\u5927\u5e74\u7eaa\uff0c\u90fd\u731c\u4e0d\u51fa\u6765\u3002\u8fd9\u5973\u4eba\u795e \u51fa\u9b3c\u6ca1\uff0c\u6b66\u529f\u4e4b\u6df1\uff0c\u4e0d\u53ef\u601d\u8bae\u3002\u4e00\u5230\u4e4b\u5f8c\uff0c\u5c31\u5c06\u6a2a\u884c\u5929\u5c71\u5357\u8def\u7684\u6851\u5bb6\u53c1\u5996\u9010\u51fa\u56de\u7586\u3002\u6851\u5bb6\u53c1 \u5996\uff0c\u5404\u6709\u72ec\u95e8\u6b66\u529f\uff0c\u5927\u5996\u6851\u4e7e\uff0c\u7ec3\u7684\u662f\u4e03\u7edd\u8bdb\u9b42\u5251\uff0c\u5251\u5c16\u6709\u6bd2\uff0c\u89c1\u8840\u5c01\u5589\uff1a\u4e8c\u5996\u6851\u5f27\uff0c\u7ec3\u7684 \u662f\u5927\u529b\u91d1\u94a2\u6775\uff0c\u5916\u5bb6\u529f\u592b\uff0c\u767b\u5cf0\u9020\u6781\uff1a\u53c1\u5996\u6851\u4ec1\uff0c\u7ec3\u7684\u662f\u9634\u9633\u5288\u98ce\u638c\uff0c\u4e2d\u4e86\u638c\u529b\uff0c\u4e94\u810f\u9707 \u88c2\u3002\u56e0\u4e3a\u4ed6\u4eec\u6240\u7ec3\u7684\u529f\u592b\u9634\u72e0\u6bd2\u8fa3\uff0c\u6240\u4ee5\u88ab\u79f0\u4e3a\u201c\u53c1\u5996\u201d\u3002\u53c1\u5996\u6a2a\u884c\u5df2\u4e45\uff0c\u89c1\u4e86\u90a3\u767d\u53d1\u5973 \u5b50\uff0c\u884c\u8e2a\u602a\u5f02\uff0c\u4e0d\u5408\u4e0a\u524d\u8c03\u620f\uff0c\u88ab\u90a3\u5973\u5b50\u4e00\u4eba\u4e00\u5251\uff0c\u6740\u5f97\u5927\u8d25\u800c\u9003\uff0c\u51e0\u4e4e\u4e27\u547d\uff0c\u53c1\u5144\u5f1f\u5728\u56de \u7586\u7acb\u4e0d\u4f4f\u8db3\uff0c\u5df2\u9003\u5230\u897f\u85cf\u53bb\u4e86\u3002 \u3000\u3000\u9a71\u9010\u53c1\u5996\uff0c\u4e0d\u8fc7\u662f\u8fd9\u5973\u5b50\u6240\u5e72\u7684\u8bb8\u591a\u4e8b\u60c5\u4e4b\u4e00\u3002\u53c1\u5996\u4e3a\u5bb3\u56de\u7586\uff0c\u88ab\u5979\u9a71\u9010\uff0c\u4eba\u5fc3\u79f0\u5feb\uff0c \u8fd9\u4ef6\u4e8b\u6ca1\u4eba\u8bf4\u5979\u505a\u5f97\u4e0d\u5bf9\u3002\u53ef\u662f\u8fd9\u767d\u53d1\u5973\u5b50\uff0c\u813e\u6c14\u751a\u602a\uff0c\u4e00\u8a00\u4e0d\u5408\uff0c\u4fbf\u5373\u52a8\u624b\u3002\u800c\u4e14\u53c8\u559c\u6b22 \u627e\u6210\u540d\u7684\u4eba\u7269\u6bd4\u8bd5\uff0c\u6bcf\u6bcf\u5728\u6570\u62db\u4e4b\u5185\uff0c\u5c31\u5c06\u522b\u4eba\u51fb\u5012\u3002\u6709\u65f6\u662f\u957f\u7b11\u800c\u53bb\uff0c\u6709\u65f6\u662f\u4ef0\u5929\u957f\u53f9\uff0c \u8bf4\u662f\u81ea\u5230\u56de\u7586\u4ee5\u6765\uff0c\u65e0\u6cd5\u627e\u5230\u5bf9\u624b\uff0c\u4f55\u4ee5\u9063\u6709\u6daf\u4e4b\u751f\uff1f\u5929\u5c71\u5357\u5317\u7684\u6210\u540d\u4eba\u7269\uff0c\u5bf9\u5979\u60e7\u6015\u4e4b \u6781\uff0c\u56e0\u5979\u767d\u53d1\u76c8\u5934\uff0c\u5927\u5bb6\u90fd\u53eb\u5979\u201c\u767d\u53d1\u9b54\u5973\u201d\uff0c\u6e10\u60ed\u6709\u4eba\u4ee5\u8bb9\u4f20\u8bb9\uff0c\u8bf4\u5979\u662f\u59d3\u767d\u7684\u4e86\u3002 \u3000\u3000\u6709\u4e9b\u4eba\u6000\u7591\u5979\u662f\u4ece\u4e2d\u539f\u6765\u7684\u5973\u76d7\uff0c\u8dd1\u6765\u5411\u6666\u660e\u5e08\u67e5\u8be2\u5979\u7684\u6765\u5386\u3002\u8fd8\u6709\u4eba\u529d\u6666\u660e\u5e08\u548c\u5979\u6bd4 \u5251\u3002\u6666\u660e\u5e08\u4e00\u542c\uff0c\u5fc3\u4e2d\u6709\u6570\u3002\u60f3\u9053\uff1a\u8fd9\u4e00\u5b9a\u662f\u7389\u7f57\u5239\u65e0\u7591\uff0c\u4f46\u4e0d\u77e5\u5979\u5982\u4f55\u4f1a\u767d\u4e86\u5934\u53d1\u3002\u5979\u8fdc \u6765\u56de\u7586\uff0c\u4e00\u5b9a\u662f\u9047\u5230\u5931\u610f\u4f24\u5fc3\u4e4b\u4e8b\u4e86\uff0c\u6599\u4e0d\u5230\u5979\u559c\u6b22\u627e\u4eba\u6253\u67b6\u7684\u6027\u5b50\u4ecd\u662f\u672a\u6539\u3002 \u3000\u3000\u6666\u660e\u5e08\u6000\u7591\u5979\u662f\u548c\u94c1\u98de\u9f99\u540c\u6765\uff0c\u95ee\u90a3\u4e9b\u4eba\u9053\uff1a\u201c\u90a3\u767d\u53d1\u9b54\u5973\u6709\u540c\u4f34\u5417\uff1f\u201d\u90a3\u4e9b\u4eba\u9053\uff1a \u201c\u4e00\u4eba\u5df2\u96be\u5bf9\u4ed8\uff0c\u6709\u540c\u4f34\u90a3\u8fd8\u4e86\u5f97\uff01\u5979\u795e\u51fa\u9b3c\u6ca1\uff0c\u72ec\u5f80\u72ec\u6765\uff0c\u98d8\u7136\u800c\u6765\uff0c\u98d8\u7136\u800c\u53bb\uff0c\u65e0\u4eba\u80fd \u77e5\u5979\u7684\u884c\u8e2a\u3002\u201d\u6666\u660e\u5e08\u5fc3\u60f3\uff1a\u7389\u7f57\u5239\u65e2\u662f\u4e00\u4eba\u8eb2\u5230\u56de\u7586\uff0c\u4e00\u5b9a\u4e0d\u613f\u5916\u4eba\u77e5\u5979\u6765\u5386\u3002\u4fbf\u5bf9\u90a3\u4e9b \u4eba\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u5979\u7684\u59d3\u540d\u6765\u5386\uff0c\u65e2\u7136\u6709\u4eba\u8bf4\u5979\u59d3\u767d\uff0c\u90a3\u9ebd\u5c31\u5f53\u5979\u662f\u59d3\u767d\u7684\u597d\u4e86\u3002\u540d\u5b57\u4e0d\u8fc7 \u662f\u4e2a\u8bb0\u53f7\uff0c\u4f55\u5fc5\u67e5\u6839\u95ee\u5e95\u3002\u201d\u90a3\u4e9b\u4eba\u53c8\u95ee\u8d77\u4e2d\u539f\u6709\u4ec0\u9ebd\u7740\u540d\u7684\u5973\u5f3a\u76d7\uff0c\u6666\u660e\u5e08\u9053\uff1a\u201c\u6211\u524a\u53d1 \u4e4b\u524d\uff0c\u968f\u718a\u7ecf\u7565\u5728\u5173\u5916\u5de1\u8fb9\uff0c\u5bf9\u5fd9\u539f\u7684\u7eff\u6797\uff0c\u751f\u758f\u5f97\u5f88\u3002\u4e5f\u8bb8\u5979\u662f\u4e2a\u72ec\u811a\u5927\u76d7\u5427\u3002\u201d\u6709\u4eba\u529d \u4ed6\u4e0b\u5c71\u627e\u767d\u53d1\u9b54\u5973\u6bd4\u5251\uff0c\u6666\u660e\u5e08\u5408\u4ec0\u7b11\u9053\uff1a\u201c\u6211\u662f\u4e2a\u770b\u7834\u7ea2\u5c18\u7684\u51fa\u5bb6\u4e4b\u4eba\uff0c\u4f5b\u6cd5\u4e0d\u5bb9\u6211\u52a8\u4e89 \u5f3a\u597d\u80dc\u4e4b\u5ff5\u3002\u201d\u6709\u4eba\u9053\uff1a\u201c\u90a3\u9ebd\u6211\u4eec\u8fd9\u53e3\u6c14\u4e0d\u80fd\u51fa\u4e86\uff1f\u201d\u6666\u660e\u60ee\u5e08\u53c8\u5f00\u89e3\u9053\uff1a\u201c\u5979\u51fa\u624b\u867d \u8fa3\uff0c\u4f46\u9664\u4e86\u6740\u4f24\u4f5c\u6076\u4e4b\u4eba\u5916\uff0c\u53ef\u6709\u4f24\u5bb3\u8fc7\u6b63\u4eba\u541b\u5b50\u9ebd\uff1f\u201d\u90a3\u4e9b\u4eba\u9053\uff1a\u201c\u8fd9\u5012\u672a\u542c\u8bf4\u8fc7\u3002\u201d\u6666 \u660e\u5e08\u7b11\u9053\uff1a\u201c\u90a3\u9ebd\u5979\u5176\u5b9e\u4e5f\u6ca1\u6709\u548c\u4f60\u4eec\u7ed3\u4ec0\u9ebd\u51a4\u4ec7\uff0c\u6709\u4e9b\u4eba\u6027\u4e4b\u6240\u55dc\uff0c\u65e0\u6cd5\u81ea\u6291\u3002\u6bd4\u5982\u6709\u68cb \u763e\u6781\u5927\u4e4b\u4eba\uff0c\u5728\u65c1\u770b\u4eba\u4e0b\u68cb\uff0c\u68cb\u76d8\u4e0a\u867d\u5199\u6709\u201c\u89c2\u68cb\u4e0d\u8bed\u771f\u541b\u5b50\u201d\u7b49\u5b57\uff0c\u4f46\u4ed6\u5fc5\u5fcd\u4e0d\u4f4f\u63d2\u53e3\uff1a \u751a\u81f3\u53cd\u5ba2\u4e3a\u4e3b\uff0c\u4ee3\u4eba\u8d70\u5b50\u3002\u80fd\u89c2\u68cb\u4e0d\u8bed\u56fa\u4f73\uff0c\u4f46\u89c2\u68cb\u51fa\u8bed\u4ea6\u975e\u5927\u7f6a\u3002\u6211\u731c\u60f3\u767d\u53d1\u9b54\u5973\u6216\u8bb8\u662f \u56e0\u6781\u55dc\u6b66\u827a\uff0c\u56e0\u6b64\u89c1\u5230\u6b66\u6797\u540d\u5bbf\uff0c\u4fbf\u5982\u68cb\u763e\u5927\u8005\u9047\u6709\u597d\u624b\uff0c\u4fbf\u987b\u5165\u5c40\u4e00\u9ab0\u3002\u53ea\u8981\u5979\u4e0d\u662f\u5b58\u5fc3 \u7279\u5f3a\u538b\u5f31\uff0c\u5f7c\u6b64\u89c2\u6469\uff0c\u4e5f\u662f\u4f73\u4e8b\u3002\u4f55\u5fc5\u751f\u5979\u7684\u6c14\uff01\u201d\u90a3\u4e9b\u4eba\u6709\u4e9b\u53f9\u4f69\u6666\u660e\u5e08\u7684\u80f8\u895f\u5e7f\u9614\uff0c\u6709 \u4e9b\u4eba\u4e5f\u4e0d\u4ee5\u4e3a\u7136\u3002\u4f46\u6666\u660e\u5e08\u4e0d\u613f\u4e0b\u5c71\uff0c\u90a3\u4e9b\u4eba\u4e5f\u65e0\u529e\u6cd5\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u534a\u5e74\uff0c\u6e10\u6e10\u4e0d\u95fb\u767d\u53d1\u9b54\u5973\u627e\u4eba\u6bd4\u8bd5\u7684\u4e8b\u4e86\u3002\u6666\u660e\u5e08\u5fc3\u9053\uff1a\u201c\u5b9a\u662f\u5979\u627e\u4e0d\u5230\u5bf9\u624b\uff0c \u89c9\u5f97\u4e00\u4e9b\u6700\u8d1f\u76db\u540d\u7684\u4eba\u7269\u4ea6\u4e0d\u8fc7\u5c14\u5c14\uff0c\u6240\u4ee5\u61d2\u5f97\u6bd4\u8bd5\u4e86\u3002\u201d\u6666\u660e\u5e08\u5728\u6768\u4e91\u9aa2\u4e0a\u5c71\u4e4b\u5f8c\uff0c\u4fbf\u91c7 \u4e94\u91d1\u4e4b\u7cbe\u7528\u5c11\u6797\u79d8\u6cd5\u91cd\u94f8\u5e08\u5085\u5b9d\u5251\uff0c\u8fd9\u65f6\u5df2\u6ee1\u4e86\u53c1\u5e74\uff0c\u70bc\u6210\u4e86\u4e24\u53e3\u5b9d\u5251\uff0c\u4e00\u957f\u4e00\u77ed\uff0c\u957f\u7684\u540d \u4e3a\u201c\u6e38\u9f99\u201d\uff0c\u77ed\u7684\u540d\u4e3a\u201c\u65ad\u7389\u201d\uff0c\u957f\u77ed\u867d\u6709\u4e0d\u540c\uff0c\u5374\u90fd\u662f\u524a\u94c1\u5982\u6ce5\uff0c\u5439\u6bdb\u7acb\u65ad\u7684\u5229\u5668\u3002\u771f\u662f \u4eba\u95f4\u795e\u7269\uff0c\u66f4\u80dc\u4ece\u524d\u3002 \u3000\u3000\u8fd9\u65e5\uff0c\u6666\u660e\u5e08\u9759\u6781\u601d\u52a8\uff0c\u60f3\u51ac\u5929\u5c06\u5230\uff0c\u5e94\u8be5\u4e0b\u5c71\u91c7\u8d2d\u4e00\u4e9b\u8fc7\u51ac\u7684\u7528\u54c1\uff0c\u4fbf\u628a\u65ad\u7389\u5251\u4ea4\u7ed9 \u6768\u4e91\u9aa2\uff0c\u53eb\u4ed6\u770b\u5b88\u95e8\u6237\u3002\u81ea\u5df1\u5e26\u4e86\u6e38\u9f99\u9632\u8eab\uff0c\u524d\u5f80\u5317\u7586\u7684\u4e00\u4e2a\u5927\u57ce\u535a\u4e50\u53bb\u91c7\u8d2d\u7528\u54c1\u3002\u540c\u65f6\u4e5f \u987a\u4fbf\u6253\u542c\u767d\u53d1\u9b54\u5973\u7684\u6d88\u606f\u3002 \u3000\u3000\u4e00\u6708\u4e4b\u5f8c\uff0c\u6666\u660e\u5e08\u4ece\u535a\u4e50\u56de\u6765\uff0c\u5e8f\u5c5e\u6df1\u79cb\uff0c\u585e\u5916\u82e6\u5bd2\uff0c\u5df2\u662f\u6ef4\u6c34\u6210\u51b0\u7684\u6c14\u5019\u3002\u8fd9\u4e00\u65e5\u6666 \u660e\u5e08\u7ecf\u8fc7\u5580\u8fbe\u8349\u539f\uff0c\u5ffd\u89c1\u4e00\u4e2a\u4f3c\u662f\u914b\u957f\u6a21\u6837\u7684\u4eba\uff0c\u7387\u9886\u8bb8\u591a\u5175\u58eb\uff0c\u8d76\u7740\u4e00\u5927\u7fa4\u725b\u7f8a\uff0c\u6a2a\u8fc7\u8349 \u539f\u3002\u4ed6\u7684\u80cc\u5f8c\u8bb8\u591a\u7267\u6c11\u5728\u54c0\u54c0\u75db\u6ce3\u3002\u6666\u660e\u5e08\u597d\u751f\u4e0d\u5fcd\uff0c\u4e0a\u524d\u8be2\u95ee\u7a76\u7adf\uff0c\u7267\u6c11\u9053\uff1a\u201c\u6211\u4eec\u6b20\u4e86 \u5b5f\u8428\u601d\u914b\u957f\u7684\u503a\uff0c\u725b\u7f8a\u90fd\u7ed9\u7275\u8d70\u4e86\u3002\u201d\u6709\u4e00\u4e2a\u7834\u70c2\u7684\u5e10\u7bf7\uff0c\u5e10\u7bf7\u5916\u6709\u4e24\u5177\u6b7b\uff0c\u4e00\u4e2a\u5b69\u5b50\u5728\u6b7b \u65c1\u8fb9\u75db\u54ed\uff0c\u6666\u660e \u3000\u3000\u5e08\u53c8\u4e0a\u53bb\u95ee\uff0c\u65c1\u4eba\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u725b\u7f8a\u90fd\u7ed9\u7275\u8d70\u4e86\uff0c\u4ed6\u7684\u7239\u5a18\u4e5f\u81ea\u6740\u4e86\uff0c\u54ce\uff0c\u6211\u4eec\u547d\u82e6\uff0c \u8fd9\u5b69\u5b50\u66f4\u53ef\u601c\uff01\u201d \u3000\u3000\u6666\u660e\u5e08\u4e00\u770b\uff0c\u8fd9\u5b69\u5b50\u5927\u7ea6\u6709\u516d\u4e03\u5c81\u7684\u6837\u5b50\uff0c\u867d\u7136\u9aa8\u7626\u4f36\u6253\uff0c\u957f\u5f97\u5374\u751a\u673a\u7075\uff0c\u4e24\u53ea\u773c\u775b\u4e4c \u9ed1\u5706\u4eae\u3002\u6666\u660e\u5e08\u77a7\u4e86\u4e00\u773c\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u662f\u6c49\u4eba\u5417\uff1f\u201d\u90a3\u5b69\u5b50\u9053\uff1a\u201c\u6211\u59d3\u695a\uff0c\u522b\u4eba\u53eb\u6211\u5357\u86ee \u5b50\uff0c\u6211\u7239\u8bf4\uff0c\u6211\u4eec\u662f\u4ece\u6e56\u5357\u642c\u6765\u7684\u3002\u6211\u4e5f\u4e0d\u77e5\u9053\u6e56\u5357\u662f\u4e0d\u662f\u6c49\u4eba\u7684\u5730\u65b9\u3002\u7239\u4ee5\u524d\u8bf4\uff0c\u5728\u90a3\u91cc \u5b98\u5e9c\u6bd4\u72fc\u864e\u8fd8\u51f6\uff0c\u6240\u4ee5\u9003\u5230\u8fd9\u91cc\u8c0b\u751f\u3002\u201d\u65c1\u8fb9\u4e00\u4e2a\u8001\u6c49\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u5b5f\u8428\u601d\u914b\u957f\uff0c\u548c\u6c49\u4eba\u7684 \u5b98\u513f\u4e5f\u5dee\u4e0d\u591a\u3002\u201d\u770b\u6837\u5b50\uff0c\u4ed6\u4e5f\u50cf\u662f\u4ece\u5173\u5185\u9003\u8352\u6765\u7684\u3002 \u3000\u3000\u90a3\u5b69\u5b50\u53c8\u54ed\u9053\uff1a\u201c\u7239\u5440\uff0c\u5a18\u5440\uff0c\u4f60\u4eec\u53bb\u4e86\uff0c\u53eb\u6211\u9760\u8c01\u5440\u3002\u201d\u6666\u660e\u5e08\u4e0d\u7981\u52a8\u4e86\u601c\u5ff5\uff0c\u6478\u6478 \u4ed6\u7684\u5934\u53d1\uff0c\u60f3\u9053\uff1a\u201c\u4e91\u9aa4\u6ca1\u6709\u5b69\u5b50\u548c\u4ed6\u73a9\uff0c\u6211\u4e0d\u5982\u591a\u6536\u4e00\u4e2a\u5f92\u5f1f\uff0c\u8ba9\u4ed6\u4eec\u505a\u4e2a\u4f34\u513f\u3002\u8fd9\u5b69\u5b50 \u9aa8\u683c\u4e0d\u9519\uff0c\u770b\u6765\u4e5f\u5f88\u806a\u660e\u4f36\u4fd0\u3002\u8be5\u662f\u4e2a\u4e60\u6b66\u7684\u4eba\u624d\u3002\u201d\u4fbf\u9053\uff1a\u201c\u4f60\u522b\u54ed\uff0c\u4f60\u613f\u8ddf\u6211\u9ebd\uff1f\u6211\u6536 \u4f60\u505a\u5f92\u5f1f\u3002\u201d\u90a3\u5b69\u5b50\u62b9\u62b9\u773c\u6cea\uff0c\u8dea\u4e0b\u53bb\u78d5\u4e86\u53c1\u4e2a\u54cd\u5934\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u7236\u3002\u201d\u6666\u660e\u5e08\u751a\u4e3a\u6b22\u559c\uff0c \u6b63\u60f3\u628a\u4ed6\u62b1\u8d77\uff0c\u5ffd\u7136\u6709\u4eba\u53eb\u9053\uff1a\u201c\u5b5f\u914b\u957f\u53c8\u56de\u6765\u4e86\u3002\u201d\u970e\u65f6\u95f4\uff0c\u7267\u6c11\u4eec\u56db\u6563\u5954\u9003\u3002\u6709\u4e24\u4e2a\u63d2 \u7740\u7fce\u6bdb\u7684\u5175\u4e01\u8dd1\u8fc7\u6765\u5406\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u6e38\u65b9\u91ce\u50e7\u5728\u8fd9\u91cc\u505a\u4ec0\u9ebd\uff01\u201d\u6666\u660e\u5e08\u9053\uff1a\u201c\u8fd9\u4e2a\u5b69\u5b50\u602a \u53ef\u601c\u7684\uff0c\u4f60\u4eec\u522b\u5413\u552c\u4ed6\u4e86\u3002\u201d\u5175\u58eb\u9053\uff1a\u201c\u54fc\uff0c\u6211\u4eec\u8981\u505a\u4ec0\u9ebd\u4fbf\u505a\u4ec0\u9ebd\uff0c\u4f60\u6562\u591a\u5634\uff01\u6211\u4eec\u7684\u914b \u957f\u8bf4\uff0c\u8fd9\u5b69\u5b50\u7684\u7236\u6bcd\u5df2\u7ecf\u6b7b\u4e86\uff0c\u4ed6\u65e0\u4f9d\u65e0\u9760\u6ca1\u4eba\u6536\u7559\u3002\u53eb\u6211\u4eec\u56de\u6765\u62b1\u4ed6\u56de\u53bb\u3002\u4f60\u770b\u6211\u4eec\u7684\u914b \u957f\u591a\u6148\u60b2\u3002\u201d\u5b69\u5b50\u54ed\u9053\uff1a\u201c\u914b\u957f\u597d\u51f6\uff0c\u6211\u4e0d\u8ddf\u4ed6\u3002\u201d\u90a3\u914b\u957f\u8fdc\u8fdc\u53d1\u8bdd\u9053\uff1a\u201c\u90a3\u50e7\u4eba\u662f\u8c01\uff1f\u4f60 \u4eec\u548c\u4ed6\u8bf4\u4ec0\u9ebd\uff1f\u8d76\u5feb\u628a\u90a3\u5b69\u5b50\u62b1\u56de\u6765\u3002\u54b1\u4eec\u8fd8\u8981\u53bb\u522b\u7684\u5730\u65b9\u8ba8\u503a\u3002\u201d\u539f\u6765\u90a3\u914b\u957f\u662f\u60f3\u5c06\u5b69\u5b50 \u62a2\u56de\u53bb\u7ed9\u4ed6\u7684\u513f\u5b50\u505a\u4ec6\u4eba\u3002 \u3000\u3000\u6666\u660e\u5e08\u62a2\u5148\u5c06\u5b69\u5b50\u62b1\u8d77\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u662f\u6211\u7684\u5f92\u5f1f\uff0c\u4f60\u4eec\u9976\u4e86\u4ed6\u5427\uff01\u201d\u90a3\u4e24\u4e2a\u5175\u4e01\u559d \u9053\uff1a\u201c\u597d\u5927\u7684\u80c6\uff0c\u6562\u8ddf\u6211\u4eec\u7684\u914b\u957f\u62a2\u4eba\u3002\u4f60\u653e\u4e0d\u653e\u624b\uff1f\u201d\u6666\u660e\u5e08\u5782\u9996\u8bf4\u9053\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff01\u201d \u90a3\u4e24\u4e2a\u5175\u4e01\u89c1\u6666\u660e\u5e08\u4e0d\u7406\u776c\u4ed6\u4eec\uff0c\u52c3\u7136\u5927\u6012\uff0c\u4e00\u5de6\u4e00\u53f3\uff0c\u4f38\u62f3\u8e22\u817f\uff0c\u6666\u660e\u5e08\u5c06\u5b69\u5b50\u62b1\u5728\u5de6\u624b \u81c2\u5f2f\uff0c\u53c8\u5ff5\u4e86\u58f0\u201c\u963f\u5f25\u9640\u4f5b\uff01\u201d\u4e24\u4e2a\u5175\u4e01\u62f3\u811a\u6253\u5230\u4ed6\u7684\u8eab\u4e0a\uff0c\u5982\u51fb\u8d25\u9769\u3002\u53ea\u542c\u5f97\u201c\u84ec\u84ec\u201d\u4e24 \u58f0\uff0c\u4e24\u540d\u5065\u5352\u90fd\u7ed9\u5f39\u5230\u4e00\u4e08\u5f00\u5916\u3002\u8fd8\u5e78\u662f\u6666\u660e\u5e08\u4e00\u5ff5\u6148\u60b2\uff0c\u8981\u4e0d\u7136\u8fd9\u4e24\u4eba\u8fd8\u8981\u624b\u65ad\u811a\u6298\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u5927\u51fa\u5b5f\u8428\u601d\u914b\u957f\u7684\u610f\u5916\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u628a\u4ed6\u64d2\u4e0b\uff01\u201d\u5b5f\u8428\u601d\u8eab\u8fb9\u6709 \u4e2a\u7a7f\u7740\u5927\u7ea2\u50e7\u8863\u7684\u5587\u561b\uff0c\u671d\u7740\u4e00\u4e2a\u6c49\u4eba\u88c5\u675f\u7684\u540c\u4f34\u8bf4\u4e86\u51e0\u53e5\uff0c\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u914b\u957f\u4e14\u6162\uff0c\u8fd9\u50e7 \u4eba\u9887\u6709\u6765\u5386\uff0c\u5f85\u6211\u95ee\u4ed6\uff1f\u201d\u8d8a\u4f17\u800c\u51fa\uff0c\u970e\u773c\u4e4b\u95f2\uff0c\u5df3\u62a2\u5728\u4f17\u5175\u4e01\u7684\u524d\u5934\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5454\uff0c \u4f60\u8fd9\u50e7\u4eba\u662f\u4ece\u90a3\u513f\u6765\u7684\u4e86\u5feb\u5feb\u62a5\u4e0a\u540d\u6765\uff01\u201d \u3000\u3000\u6666\u660e\u5e08\u9053\uff1a\u201c\u6e38\u65b9\u91ce\u50e7\uff0c\u65e0\u540d\u65e0\u59d3\uff0c\u5927\u5e08\uff0c\u4f60\u9ad8\u62ac\u8d35\u624b\uff01\u201d\u90a3\u7ea2\u8863\u5587\u561b\u84e6\u7136\u602a\u7b11\uff0c\u6717\u58f0 \u8bf4\u9053\uff1a\u201c\u4f60\u5f53\u6211\u4e0d\u77e5\u9053\u4f60\u5417\uff1f\u4f60\u662f\u5cb3\u545c\u73c2\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u6666\u660e\u5e08\u5403\u4e86\u4e00\u60ca\uff0c\u770b\u90a3\u7ea2\u8863\u5587\u561b\u4e00 \u773c\uff0c\u5374\u4e0d\u8ba4\u5f97\uff0c\u4fbf\u9053\uff1a\u201c\u5927\u5e08\uff0c\u4f60\u522b\u8ddf\u6211\u5f00\u73a9\u7b11\u4e86\uff0c\u5929\u5bd2\u5730\u51bb\uff0c\u5b69\u5b50\u53c8\u51b7\u53c8\u997f\uff0c\u6211\u6025\u7740\u8981\u56de \u5c71\u53bb\u4e86\u3002\u201d \u3000\u3000\u90a3\u7ea2\u8863\u5587\u561b\u5927\u58f0\u559d\u9053\uff1a\u201c\u771f\u4eba\u9762\u524d\u4e0d\u8bf4\u5047\u8bdd\uff0c\u4f60\u522b\u4ee5\u4e3a\u505a\u4e86\u548c\u5c1a\uff0c\u4fbf\u53ef\u9003\u8fc7\u3002\u4f60\u5feb\u628a\u718a \u86ee\u5b50\u7684\u5175\u4e66\u4ea4\u51fa\uff0c\u8981\u4e0d\u7136\u4f5b\u7237\u4eca\u65e5\u4fbf\u8981\u66ff\u4f60\u8d85\u6e21\u4e86\uff01\u201d\u6666\u660e\u5e08\u4e00\u542c\uff0c\u5403\u60ca\u66f4\u751a\uff0c\u5fc3\u9053\uff1a\u8fd9\u51f6 \u618e\u600e\u77e5\u718a\u7ecf\u7565\u7684\u5175\u4e66\u66fe\u4ed8\u6258\u7ed9\u6211\uff1f\u53ef\u662f\u4f60\u53ea\u77e5\u5176\u4e00\uff0c\u4e0d\u77e5\u5176\u4e8c\uff0c\u5175\u4e66\u65e9\u5df2\u4ea4\u7ed9\u7389\u7f57\u5239\u4e86\u3002\u8fd9 \u51f6\u50e7\u65e2\u95ee\u5175\u4e66\uff0c\u6599\u662f\u5978\u515a\uff0c\u770b\u6765\u6211\u4eca\u65e5\u53ef\u8981\u5927\u5f00\u6740\u6212\u4e86\uff01 \u3000\u3000\u90a3\u7ea2\u8863\u5587\u561b\u53c8\u559d\u9053\uff1a\u201c\u5cb3\u545c\u73c2\u4f60\u8bf4\u4e0d\u8bf4\uff1f\u201d\u6666\u660e\u60ee\u5e08\u53c8\u5ff5\u4e86\u53e5\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff01\u201d\u8bf4\u9053\uff1a \u201c\u8d2b\u618e\u53ea\u77e5\u6301\u6212\u4fee\uff0c\u538c\u95fb\u6740\u6212\uff0c\u90a3\u4f1a\u6709\u4ec0\u9ebd\u5175\u4e66\uff1f\u201d\u7ea2\u8863\u5587\u561b\u53eb\u9053\uff1a\u201c\u597d\uff0c\u4e0d\u5230\u9ec4\u6cb3\u4f60\u5fc3\u4e0d \u6b7b\uff0c\u4e0d\u53eb\u4f60\u77e5\u9053\u4f5b\u7237\u5389\u5bb3\uff0c\u6599\u4f60\u4e5f\u4e0d\u80af\u4f4e\u5934\uff01\u201d\u5728\u8170\u95f4\u53d6\u51fa\u4e24\u7247\u94dc\u94b9\uff0c\u76f8\u5bf9\u4e00\u649e\uff0c\u53d1\u51fa\u7834\u9523 \u4e5f\u4f3c\u7684\u54cd\u58f0\uff0c\u84e6\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u72b9\u5982\u4e00\u7247\u7ea2\u4e91\uff0c\u5f53\u5934\u538b\u4e0b\u6765\uff0c\u6666\u660e\u60ee\u5e08\u5de6\u624b\u62a4\u7740\u5b69\u5b50\uff0c\u53f3\u624b\u4e00 \u4f38\uff0c\u62cd\u7684\u4e00\u638c\u6253\u53bb\uff0c\u7ea2\u8863\u5587\u561b\u53cc\u94b9\u4e00\u5408\uff0c\u773c\u770b\u8981\u628a\u6666\u660e\u5e08\u624b\u638c\u5939\u7740\uff0c\u8c01\u77e5\u6666\u660e\u5e08\u624b\u638c\u4f3c\u6e38\u9c7c \u822c\u6ed1\u4e86\u51fa\u6765\uff0c\u7a81\u7136\u53d8\u638c\u4e3a\u6307\uff0c\u70b9\u4ed6\u9762\u4e0a\u53cc\u775b\u3002\u90a3\u7ea2\u8863\u5587\u561b\u602a\u53eb\u4e00\u58f0\uff0c\u8eab\u5b50\u98ce\u8f66\u4e00\u8f6c\uff0c\u5de6\u94b9\u4e0a \u524a\uff0c\u53f3\u94b9\u4e0b\u5288\uff0c\u6666\u660e\u5e08\u6025\u6025\u53d8\u62db\uff0c\u5404\u9000\u53c1\u6b65\uff01 \u3000\u3000\u8fd9\u4e00\u6765\u4e24\u4eba\u90fd\u5403\u60ca\u4e0d\u5c11\uff0c\u7ea2\u8863\u5587\u561b\u867d\u77e5\u6666\u660e\u60ee\u5e08\u5251\u6cd5\u901a\u7384\uff0c\u5185\u529f\u6df1\u5965\uff0c\u5374\u6599\u4e0d\u5230\u4ed6\u62b1\u7740 \u5b69\u5b50\uff0c\u5355\u638c\u5e94\u654c\uff0c\u4e5f\u8fd9\u822c\u5389\u5bb3\uff01\u6666\u660e\u60ee\u5e08\u4e5f\u60f3\u4e0d\u5230\u5728\u7edd\u585e\u7a77\u8352\u4e4b\u5730\uff0c\u5c45\u7136\u4e5f\u6709\u6b66\u529f\u5982\u6b64\u9ad8\u5f3a \u4e4b\u4eba\uff01 \u3000\u3000\u6666\u660e\u5e08\u9690\u5c45\u6570\u5e74\uff0c\u4e0d\u77e5\u5916\u9762\u5df2\u8d77\u4e86\u7ffb\u5929\u8986\u5730\u7684\u53d8\u5316\u3002\u539f\u6765\u6731\u7531\u6821\u7eb5\u60c5\u58f0\u8272\uff0c\u8eab\u5b50\u4e8f\u635f\uff0c \u679c\u7136\u88ab\u5ba2\u5a09\u5a77\u6599\u4e2d\uff0c\u77ed\u547d\u800c\u6b7b\u3002\u5728\u4f4d\u4e03\u5e74\uff0c\u5e74\u624d\u4e8c\u5341\u4e8c\u5c81\u3002\u6731\u7531\u6821\u6b7b\u5f8c\uff0c\u5f1f\u5f1f\u6731\u7531\u68c0\u7ee7\u4f4d\uff0c \u6539\u5143\u5d07\u6862\u3002\u63a5\u4f4d\u4e4b\u5f8c\uff0c\u4fbf\u5c06\u9b4f\u5fe0\u8d24\u51cc\u8fdf\u5904\u6b7b\uff0c\u5ba2\u6c0f\u88ab\u9010\u51fa\u5bab\uff0c\u5176\u5f8c\u4ea6\u5904\u6b7b\u3002\u5176\u4ed6\u5978\u515a\u5982\u5d14\u5448 \u79c0\u7b49\u90fd\u5904\u65a9\u51b3\uff0c\u9b4f\u5fe0\u8d24\u7684\u4e7e\u513f\u5b50\u7f6a\u72b6\u8f83\u8f7b\u7684\u6216\u88ab\u5145\u519b\uff0c\u6216\u88ab\u9edc\u4e3a\u6c11\uff0c\u5e76\u8d77\u7528\u8881\u5d07\u7115\u53ca\u4e1c\u6797\u515a \u4eba\uff0c\u4e00\u65f6\u6b63\u6c14\u4f38\u5f20\uff0c\u9887\u6709\u4e2d\u5174\u4e4b\u76f8\u3002\u60dc\u4e4e\u5d07\u6862\u7687\u5e1d\u4e4b\u6740\u9b4f\u5fe0\u8d24\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e3a\u4e2a\u4eba\u6253\u7b97\uff0c\u5728\u626b \u9664\u5978\u515a\u4e4b\u5f8c\uff0c\u5e76\u4e0d\u8d81\u52bf\u5174\u5229\u9664\u5f0a\uff0c\u53cd\u800c\u52a0\u91cd\u6c11\u95f4\u7530\u8d4b\uff0c\u641c\u522e\u6c11\u8d22\uff0c\u4ee5\u81f4\u7ec8\u65bc\u4ea1\u56fd\uff0c\u6b64\u662f\u5f8c \u8bdd\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u4e14\u8bf4\u5ba2\u9b4f\u4e00\u6b7b\uff0c\u6811\u5012\u7322\u72f2\u4fbf\u6563\uff0c\u7531\u6821\u7684\u201c\u5927\u62a4\u6cd5\u201d\u660c\u94a6\u5927\u6cd5\u5e08\u662f\u7ea2\u6559\u5587\u561b\uff0c\u4ed7\u7740\u4e00\u8eab\u6b66 \u529f\uff0c\u9003\u56de\u897f\u85cf\u3002\u9b4f\u5fe0\u8d24\u7684\u5fc3\u8179\u8fde\u57ce\u864e\u4e5f\u9003\u4e86\u51fa\u6765\uff0c\u5230\u897f\u85cf\u53bb\u627e\u660c\u94a6\u6cd5\u5e08\u3002\u5176\u65f6\u56de\u7586\u201c\u5580\u8fbe \u5c14\u201d\u65cf\u7684\u914b\u957f\u76c2\u8428\u601d\u91ce\u5fc3\u6781\u5927\uff0c\u9887\u601d\u7edf\u4e00\u56de\u7586\uff0c\u95fb\u77e5\u660c\u94a6\u5927\u6cd5\u5e08\u56de\u6765\uff0c\u4fbf\u4ee5\u73cd\u5b9d\u91cd\u793c\u8058\u4ed6\u76f8 \u52a9\u3002\u660c\u94a6\u6cd5\u5e08\u66fe\u7ecf\u6ca7\u6d77\uff0c\u672c\u4e0d\u60f3\u5f80\u3002\u4f46\u8fde\u57ce\u864e\u5374\u53e6\u6709\u7528\u5fc3\uff0c\u4ed6\u662f\u6ee1\u5dde\u5185\u5e94\uff0c\u5fc3\u60f3\u6ee1\u5dde\u5fc5\u5f97\u5929 \u4e0b\uff0c\u8fdf\u65e9\u4f1a\u5bf9\u56de\u7586\u7528\u5175\uff0c\u56de\u7586\u5730\u5927\u4eba\u7a00\uff0c\u7528\u5175\u4e0d\u4fbf\uff0c\u4e0d\u5982\u501f\u5b5f\u8428\u601d\u4e4b\u529b\uff0c\u5148\u6253\u597d\u57fa\u7840\u3002\u56e0\u6b64 \u529b\u529d\u660c\u94a6\u5927\u6cd5\u5e08\u5e94\u8058\uff0c\u4ed6\u4e5f\u548c\u660c\u94a6\u5927\u6cd5\u5e08\u4e00\u9053\uff0c\u540c\u5230\u56de\u7586\u3002\u4e0d\u6599\u8fd9\u65e5\u5728\u65e0\u610f\u4e4b\u4e2d\u9047\u5230\u4e86\u5df2\u524a \u53d1\u4e3a\u50e7\u7684\u5cb3\u9e23\u73c2\uff0c\u60f3\u8d77\u4ed6\u6709\u718a\u5ef7\u5f3c\u7684\u9057\u4e66\uff0c\u82e5\u80fd\u53d6\u5230\uff0c\u4fbf\u53ef\u8ff3\u8d74\u5173\u5916\uff0c\u7acb\u53d7\u91cd\u7528\uff0c\u4e0d\u5fc5\u5728\u56de \u7586\u653e\u957f\u7ebf\u540a\u8fdc\u9e5e\u4e86\u3002\u56e0\u6b64\u4fbf\u5506\u4f7f\u660c\u94a6\u6cd5\u5e08\u53bb\u5bf9\u4ed8\u6666\u660e\u5e08\u3002 \u3000\u3000\u660c\u94a6\u5927\u6cd5\u5e08\u6b66\u529f\u975e\u540c\u5c0f\u9119\uff0c\u624b\u4f7f\u4e24\u7247\u94dc\u94b9\uff0c\u771f\u6709\u4e07\u592b\u4e0d\u5f53\u4e4b\u52c7\u3002\u90a3\u6b21\u7389\u7f57\u5239\u4e3a\u6551\u6768\u6d9f\u800c \u72ec\u95ef\u6df1\u5bab\uff0c\u4fbf\u66fe\u548c\u4ed6\u6597\u8fc7\u3002\u90a3\u6b21\u7389\u7f57\u5239\u7528\u65cb\u98ce\u5251\u6cd5\u6740\u8d25\u4e86\u4ed6\uff0c\u4f46\u4e5f\u6597\u4e86\u4e8c\u53c1\u5341\u62db\u3002\u660c\u94a6\u5927\u6cd5 \u5e08\u5e73\u751f\u66fe\u8d25\u7ed9\u8fc7\u7389\u7f57\u5239\uff0c\u6240\u4ee5\u751a\u4e3a\u81ea\u8d1f\u3002\u4e0d\u6599\u8fd9\u6b21\u78b0\u5230\u4e86\u6666\u660e\u5e08\uff0c\u7adf\u6bd4\u5f53\u5e74\u7684\u7389\u7f57\u5239\u66f4\u4e3a\u5389 \u5bb3\uff0c\u6666\u660e\u5e08\u62b1\u7740\u5c0f\u5b69\uff0c\u5355\u638c\u8fdb\u62db\uff0c\u4efb\u4ed6\u53cc\u94b9\u7ffb\u98de\uff0c\u8fd8\u81ea\u5c61\u5c61\u6b3a\u8eab\u8fdb\u903c\u3002 \u3000\u3000\u660c\u94a6\u5927\u6cd5\u5e08\u6697\u6697\u5fc3\u5bd2\uff0c\u6666\u660e\u5e08\u56e0\u62b1\u7740\u5b69\u5b50\uff0c\u4e5f\u81ea\u6212\u60e7\u3002\u53ef\u5e78\u8fd9\u5b69\u5b50\u80c6\u5b50\u7adf\u4f3c\u6768\u4e91\u9aa2\u4e00 \u6837\uff0c\u4e0d\u77e5\u5bb3\u6015\uff0c\u770b\u6666\u660e\u5e08\u7a7a\u624b\u5c06\u4e00\u4e2a\u53c8\u9ad8\u53c8\u5927\u7684\u756a\u50e7\uff0c\u903c\u5f97\u8fde\u8fde\u5f8c\u9000\uff0c\u800c\u90a3\u4e2a\u756a\u50e7\u4f7f\u7684\u602a\u5175 \u5668\u53c8\u65f6\u4e0d\u65f6\u53d1\u51fa\u7834\u9523\u4f3c\u7684\u58f0\u97f3\uff0c\u89c9\u5f97\u5341\u5206\u6709\u8da3\uff0c\u8fde\u7236\u6bcd\u53cc\u4ea1\u4e4b\u75db\u4e5f\u5fd8\u8bb0\u4e86\uff0c\u770b\u5230\u7cbe\u91c7\u4e4b\u5904\uff0c \u8fde\u53eb\uff1a\u201c\u597d\u5440\uff01\u597d\u5440\uff01\u5e08\u7236\uff0c\u4f60\u53ef\u5f97\u628a\u8fd9\u672c\u9886\u6559\u6211\uff01\u201d\u8fd8\u4e0d\u65f6\u628a\u5934\u91c7\u51fa\u6765\u770b\u6666\u660e\u5e08\u600e\u6837\u548c\u4ed6 \u6253\u3002\u6666\u660e\u5e08\u867d\u7136\u6b22\u559c\u4ed6\u80c6\u5b50\u5947\u5927\uff0c\u5374\u66f4\u6015\u4ed6\u53d7\u4e86\u4f24\u5bb3\u3002\u518d\u6362\u51e0\u62db\uff0c\u5356\u4e2a\u7834\u7efd\uff0c\u56de\u8eab\u4fbf\u8d70\u3002\u660c \u94a6\u5927\u6cd5\u5e08\u89c1\u6666\u660e\u5e08\u6beb\u65e0\u8d25\u8c61\uff0c\u7a81\u7136\u9000\u5f8c\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u53cc\u94b9\u521a\u6b32\u8fdb\u62db\uff0c\u84e6\u89c9\u773c\u524d\u4e00\u4eae\uff0c\u5bd2\u6c14\u6c81 \u808c\uff0c\u6666\u660e\u5e08\u5df2\u5728\u8fd9\u4e00\u9000\u4e00\u8fdb\u7684\u5239\u90a3\uff0c\u5c06\u6e38\u9f99\u5b9d\u5251\u62d4\u4e86\u51fa\u6765\u3002\u53cd\u624b\u4e00\u64a9\uff0c\u53ea\u542c\u5f97\u201c\u5f53\u201d\u7684\u4e00\u58f0 \u5de8\u54cd\uff0c\u660c\u94a6\u5de6\u624b\u94dc\u94b9\uff0c\u5df2\u7ed9\u5288\u4e3a\u4e24\u534a\uff01 \u3000\u3000\u660c\u94a6\u6cd5\u5e08\u5927\u60ca\uff0c\u5413\u5f97\u8fde\u8fde\u5f8c\u9000\u3002\u6666\u660e\u5e08\u89c1\u5b9d\u5251\u70bc\u6210\uff0c\u5a01\u529b\u5947\u5927\uff0c\u5341\u5206\u9ad8\u5174\uff0c\u60f3\u518d\u8bd5\u4e24 \u4e0b\uff0c\u8eab\u5f62\u4e00\u8d77\uff0c\u633d\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u7167\u660c\u94a6\u6cd5\u5e08\u80cc\u5f8c\u7684\u201c\u9b42\u95e8\u7a74\u201d\u53c8\u523a\uff0c\u60f3\u8feb\u4ed6\u56de\u8eab\u62b5\u6321\uff0c\u5b5f\u8428 \u601d\u4e00\u58f0\u53f7\u4ee4\uff0c\u7bad\u4f3c\u98de\u8757\uff0c\u63a9\u62a4\u660c\u94a6\u6cd5\u5e08\u3002\u6666\u660e\u5e08\u821e\u52a8\u5b9d\u5251\uff0c\u56db\u9762\u62ab\u6c64\uff0c\u53ea\u542c\u5f97\u53ee\u53ee\u5f53\u5f53\u4e4b \u58f0\uff0c\u4e0d\u7edd\u65bc\u8033\uff0c\u90a3\u4e9b\u98de\u7bad\uff0c\u4e00\u88ab\u5251\u5149\u7ed5\u8fc7\uff0c\u7acb\u523b\u6298\u65ad\uff01 \u3000\u3000\u6666\u660e\u5e08\u54c8\u54c8\u4e00\u7b11\uff0c\u53c8\u5ff5\u4e86\u65ec\u201c\u963f\u5f25\u9640\u4f5b\u201d\uff0c\u9053\uff1a\u201c\u6055\u4e0d\u5949\u966a\uff0c\u8d2b\u50e7\u8d70\u4e86\uff01\u201d\u660c\u94a6\u5927\u6cd5\u5e08 \u8d81\u4ed6\u62b5\u6321\u7bad\u96e8\u7684\u65f6\u5019\uff0c\u628a\u5927\u7ea2\u8888\u88df\u8131\u4e0b\uff0c\u66ff\u4ee3\u5de6\u624b\u94dc\u94b9\uff0c\u84e6\u7136\u53c8\u4e00\u63a0\u800c\u524d\uff0c\u559d\u9053\uff1a\u201c\u4f5b\u7237\u8fd8 \u8981\u4e0e\u4f60\u89c1\u4e2a\u9ad8\u4e0b\uff01\u201d\u8888\u88df\u4e00\u6296\uff0c\u4f3c\u4e00\u7247\u7ea2\u4e91\u76f4\u538b\u4e0b\u6765\uff0c\u7adf\u60f3\u8fd0\u7528\u5185\u5bb6\u4e70\u529b\uff0c\u4ee5\u67d4\u514b\u521a\uff0c\u593a\u53d6 \u5b9d\u5251\u3002\u6666\u660e\u5e08\u6120\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u82e6\u7f20\u9ebd\uff01\u201d\u5251\u950b\u4e00\u8d77\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u5927\u7ea2\u8888\u88df\u6495\u4e0b\u4e00\u5757\uff0c\u4f46 \u8888\u88df\u4e0d\u6bd4\u5175\u5668\uff0c\u5175\u5668\u88ab\u524a\u65ad\u4e86\u4fbf\u4e0d\u80fd\u518d\u4f7f\uff0c\u8888\u88df\u88ab\u6495\u88c2\u4e86\u5374\u4ecd\u7136\u53ef\u7528\u3002\u660c\u94a6\u6cd5\u5e08\u8d81\u52bf\u4e00\u9001\uff0c \u8888\u88df\u7ffb\u52a8\uff0c\u60f3\u628a\u5b9d\u5251\u88f9\u7740\uff0c\u53f3\u624b\u94dc\u94b9\u547c\u547c\u98ce\u54cd\uff0c\u6a2a\u524a\u8fc7\u6765\u3002\u6666\u660e\u5e08\u5185\u5916\u529f\u592b\u90fd\u5df2\u767b\u5cf0\u9020\u6781\uff0c \u7109\u80fd\u4e2d\u4ed6\u8be1\u8ba1\uff0c\u5251\u950b\u5fae\u5fae\u4e00\u98a4\uff0c\u5df2\u62bd\u4e86\u51fa\u6765\uff0c\u8fce\u7740\u94dc\u94b9\u4fbf\u524a\uff0c\u660c\u94a6\u6cd5\u5e08\u77e5\u9053\u5389\u5bb3\uff0c\u4e0d\u6562\u786c \u63a5\uff0c\u6a2a\u8dc3\u53c1\u6b65\uff0c\u907f\u8fc7\u5251\u950b\u3002\u90a3\u77e5\u6666\u660e\u5e08\u7684\u5929\u5c71\u5251\u6cd5\u7cbe\u5999\u7edd\u4f26\uff0c\u4e00\u88ab\u9ecf\u4e0a\uff0c\u65e0\u6cd5\u8131\u8eab\uff0c\u660c\u94a6\u5de6 \u8dc3\u53f3\u8dc3\uff0c\u5251\u5149\u4e0d\u79bb\u8eab\u5f8c\u3002\u6666\u660e\u5e08\u6b63\u60f3\u518d\u524a\u65ad\u4ed6\u53f3\u624b\u94dc\u94b9\uff0c\u5b5f\u8428\u601d\u624b\u4e0b\u7684\u5341\u591a\u540d\u6b66\u58eb\u5df2\u56f4\u6d8c\u4e0a \u6765\uff0c\u6666\u660e\u5e08\u5fc3\u5ff5\u8fd9\u4e9b\u6b66\u58eb\u4e0d\u582a\u4e00\u51fb\uff0c\u60f3\u5f85\u89e3\u51b3\u4e86\u660c\u94a6\u6cd5\u5e08\u4e4b\u5f8c\uff0c\u518d\u524a\u65ad\u4ed6\u4eec\u7684\u5175\u5668\uff0c\u4e0d\u6599\u5176 \u4e2d\u4e00\u4eba\u51fa\u624b\u5947\u5feb\uff0c\u624b\u4f7f\u65e5\u6708\u53cc\u94a9\uff0c\u9493\u5149\u4e00\u95ea\uff0c\u7adf\u5bf9\u4ed6\u7684\u6000\u4e2d\u5b69\u5b50\uff0c\u731b\u65bd\u6740\u624b\uff01 \u3000\u3000\u6666\u660e\u5e08\u811a\u5c16\u4e00\u70b9\uff0c\u7bad\u4e00\u822c\u7684\u6a2a\u7a9c\u51fa\u53bb\uff0c\u6000\u4e2d\u5b69\u5b50\uff0c\u54c7\u7136\u5927\u53eb\uff0c\u6666\u660e\u5e08\u56de\u5934\u4e00\u770b\uff0c\u4e0d\u89c9\u51b7 \u7b11\u9053\uff1a\u201c\u54c8\uff0c\u6211\u9053\u662f\u8c01\uff01\u539f\u6765\u662f\u8fde\u5927\u603b\u7ba1\uff01\u4f60\u4eec\u5bb3\u4e86\u718a\u7ecf\u7565\u8fd8\u4e0d\u79fb\u5417\uff1f\u6211\u4eca\u65e5\u5df2\u524a\u53d1\u4e3a\u50e7\uff0c \u9b4f\u5fe0\u8d24\u4e5f\u8be5\u653e\u5fc3\u4e86\u3002\u5cb3\u67d0\u533a\u533a\uff0c\u600e\u6562\u52b3\u4f60\u4eec\u8fdc\u5230\u585e\u5916\u3002\u201d\u6666\u660e\u5e08\u4e0d\u77e5\u9b4f\u5fe0\u8d24\u5df2\u88ab\u51cc\u8fdf\u5904\u6b7b\uff0c \u8fd8\u9053\u4ed6\u4ecd\u5982\u65e7\u65e5\u5f53\u6743\uff0c\u6d3e\u8fde\u57ce\u864e\u4e0e\u8fd9\u4e2a\u5587\u561b\u8ffd\u8e2a\u81ea\u5df1\u3002 \u3000\u3000\u8fde\u57ce\u864e\u82e6\u7b11\u51e0\u58f0\uff0c\u53cc\u94a9\u659c\u5c55\uff0c\u53c8\u4e0e\u660c\u94a6\u6cd5\u5e08\u5de6\u6709\u5206\u88ad\u3002\u8fde\u57ce\u864e\u6781\u4e3a\u6b79\u6bd2\uff0c\u89c1\u4ed6\u5de6\u624b\u62b1\u7740 \u5b69\u5b50\uff0c\u5de6\u8fb9\u4e43\u662f\u5f31\u70b9\u6240\u5728\uff0c\u53cc\u94a9\u95ea\u95ea\uff0c\u4e13\u5411\u4ed6\u7684\u5f31\u70b9\u8fdb\u653b\uff0c\u6000\u4e2d\u7684\u5b69\u5b50\u80c6\u5b50\u7eb5\u7136\u518d\u5927\uff0c\u4e5f\u5413 \u5f97\u614c\u4e86\uff0c\u5c0f\u624b\u6273\u7740\u6666\u660e\u5e08\u80a9\u5934\uff0c\u65f6\u4e0d\u65f6\u53d1\u51fa\u60ca\u60e7\u7684\u53eb\u558a\uff0c\u660c\u94a6\u6cd5\u5e08\u53f3\u624b\u94dc\u94b9\u5de6\u624b\u50e7\u888d\uff0c\u4e5f\u53cd \u5b88\u4e3a\u653b\u3002\u6666\u660e\u5e08\u5927\u6012\uff0c\u5251\u8bc0\u4e00\u9886\uff0c\u5c06\u81ea\u5df1\u4e0e\u5e08\u7236\u5408\u521b\u7684\u5929\u5c71\u5251\u970d\u970d\u5c55\u5f00\uff0c\u4f46\u89c1\u7d2b\u7535\u98de\u7a7a\uff0c\u5bd2 \u5149\u9aa4\u8d77\uff0c\u867d\u5982\u6e38\u9f99\uff0c\u9000\u82e5\u60ca\u9e3f\uff0c\u5251\u98ce\u6307\u5904\uff0c\u8349\u539f\u4e0a\u90a3\u9ad8\u903e\u534a\u8eab\u7684\u91ce\u8349\u90fd\u98d2\u98d2\u4f5c\u54cd\u3002\u8fde\u57ce\u864e\u4e0e \u660c\u94a6\u6cd5\u5e08\u90a3\u6562\u8feb\u8fd1\uff1f \u3000\u3000\u53ef\u662f\u8fde\u57ce\u864e\u4e0e\u660c\u94a6\u6cd5\u5e08\u90fd\u662f\u9876\u513f\u5c16\u513f\u7684\u89d2\u8272\uff0c\u6666\u660e\u5e08\u62b1\u7740\u5b69\u5b50\uff0c\u4e0d\u6562\u653e\u5fc3\u6740\uff0c\u62da\u6597\u4e86\u4e94 \u4e03\u5341\u62db\uff0c\u7adf\u662f\u6253\u6210\u5e73\u624b\u3002\u5b5f\u8428\u601d\u7387\u9886\u624b\u4e0b\u6b66\uff01\u5728\u767e\u6b65\u4e4b\u5916\uff0c\u56f4\u6210\u4e00\u4e2a\u5706\u5708\uff0c\u5f20\u5f3a\u5f13\u642d\u5229\u7bad\uff0c \u53ea\u5f85\u6666\u660e\u5e08\u4e00\u8d25\uff0c\u4fbf\u4e71\u7bad\u5c04\u4ed6\uff0c\u524d\u5f8c\u5939\u653b\u3002 \u3000\u3000\u6b63\u5728\u76f8\u6301\u4e0d\u4e0b\u4e4b\u9645\uff0c\u5ffd\u7136\u8fdc\u8fdc\u542c\u5f97\u4e00\u58f0\u957f\u7b11\uff0c\u6666\u660e\u5e08\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1 \u4e00\u56e2\u767d\u5f71\uff0c\u98de\u63a0\u8349\u539f\uff0c\u7247\u523b\u4e4b\u5f8c\uff0c\u53eb\u58f0\u56db\u8d77\uff0c\u5b5f\u8428\u601d\u624b\u4e0b\u5c1a\u672a\u770b\u6e05\uff0c\u5df2\u6709\u591a\u4eba\u4e2d\u5251\u5012\u5730\u3002\u6709 \u4eba\u60ca\u53eb\u9053\uff1a\u201c\u5feb\u8d70\u5440\uff0c\u767d\u53d1\u9b54\u5973\u6765\u4e86\uff01\u201d \u3000\u3000\u6666\u660e\u5e08\u8fd0\u5251\u4e00\u5c01\uff0c\u5c06\u660c\u94a6\u6cd5\u5e08\u4e0e\u8fde\u57ce\u864e\u8feb\u9000\u6570\u6b65\uff0c\u53ea\u542c\u5f97\u767d\u53d1\u9b54\u5973\u53eb\u9053\uff1a\u201c\u522b\u6765\u65e0\u6059\uff01 \u7ea6\u4f1a\u4e4b\u671f\u672a\u5230\uff0c\u6211\u5df2\u63d0\u524d\u6765\u4e86\uff01\u201d\u4ecd\u662f\u65e7\u65e5\u8c6a\u60c5\uff0c\u53ea\u662f\u58f0\u97f3\u5df2\u663e\u5f97\u6bd4\u524d\u82cd\u8001\u4e86\uff1f \u3000\u3000\u660c\u94a6\u6cd5\u5e08\u4e00\u77a5\u4e4b\u4e0b\uff0c\u5413\u5f97\u9b42\u6d88\u9b44\u6563\u3002\u767d\u53d1\u9b54\u5973\u867d\u7136\u4e0d\u4f3c\u65e7\u65e5\u7eee\u5e74\u7389\u8c8c\uff0c\u5f62\u5bb9\u4ecd\u7136\u53ef\u8fa8\u3002 \u660c\u94a6\u6cd5\u5e08\u66fe\u5403\u8fc7\u5979\u7684\u5927\u4e8f\uff0c\u5fc3\u60f3\uff1a\u5cb3\u545c\u73c2\u5df2\u8fd9\u9ebd\u5389\u5bb3\uff0c\u8fd9\u5973\u9b54\u5934\u53c8\u6765\u3002\u82e5\u7ed9\u4ed6\u4eec\u4e8c\u4eba\u8054\u624b\u5408 \u653b\uff0c\u90a3\u662f\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u3002\u4e00\u4e2a\u65cb\u8eab\uff0c\u6025\u6025\u98de\u9003\u3002\u8fde\u57ce\u864e\u53cc\u94a9\u4e00\u64a4\uff0c\u4e5f\u6b32\u9003\u547d\uff0c\u4f46\u4ed6\u8f7b\u8eab\u529f \u592b\uff0c\u7565\u900a\u65bc\u660c\u94a6\u6cd5\u5e08\uff0c\u767d\u53d1\u9b54\u5973\u6765\u5f97\u4f55\u7b49\u5feb\u6377\uff0c\u4ed6\u672a\u9003\u51fa\u5341\u4e08\u4e4b\u5730\uff0c\u767d\u53d1\u9b54\u5973\u5df2\u5982\u5f71\u9644\u5f62\uff0c \u8ffd\u5230\u8eab\u5f8c\uff0c\u5251\u5149\u4e00\u8d77\uff0c\u76f4\u523a\u5f8c\u5fc3\u3002\u8fde\u57ce\u864e\u594b\u529b\u6321\u4e86\u6570\u62db\uff0c\u767d\u53d1\u9b54\u5973\u5251\u5149\u98d8\u77a5\u4e0d\u5b9a\uff0c\u884c\u524d\u5ffd \u5f8c\uff0c\u4f3c\u5de6\u53cd\u53f3\uff0c\u8fde\u57ce\u864e\u5927\u7ea6\u6321\u4e86\u5341\u9980\u62db\u7684\u5149\u666f\uff0c\u767d\u53d1\u9b54\u5973\u5ffd\u53eb\u58f0\uff1a\u201c\u7740\uff01\u201d\u58f0\u5230\u5251\u5230\uff0c\u8fde\u57ce \u864e\u5fc3\u80c6\u7686\u5bd2\uff0c\u8fa8\u4e0d\u6e05\u5979\u5251\u52bf\u8d70\u5411\u4f55\u65b9\uff0c\u819d\u5934\u4e00\u75db\uff0c\u5495\u549a\u5012\u5730\uff01 \u3000\u3000\u767d\u53d1\u9b54\u5973\u5c06\u8fde\u57ce\u864e\u4e00\u628a\u6293\u8d77\uff0c\u4fe1\u624b\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u5bf9\u6666\u660e\u5e08\u9053\uff1a\u201c\u8d70\uff01\u201d\u6666\u660e\u5e08\u9053\uff1a \u201c\u53bb\u90a3\u513f\uff1f\u201d\u767d\u53d1\u9b54\u5973\u9053\uff1a\u201c\u4f60\u53bb\u90a3\u513f\u6211\u53bb\u90a3\u513f\uff0c\u4f60\u4e0d\u6562\u548c\u6211\u6bd4\u8d5b\u9ebd\uff1f\u201d\u6666\u660e\u5e08\u8fd9\u624d\u77e5\u9053\u767d \u53d1\u9b54\u5973\u662f\u60f3\u8f83\u91cf\u4ed6\u7684\u8f7b\u529f\u3002 \u3000\u3000\u6666\u660e\u5e08\u5fc3\u4e2d\u6697\u7b11\uff1a\u4e00\u522b\u6570\u5e74\uff0c\u5f02\u5730\u76f8\u9022\uff0c\u5979\u7adf\u7136\u4e0d\u5148\u53d9\u5951\u9614\uff0c\u4e00\u89c1\u9762\u5c31\u8981\u6bd4\u8d5b\u8f7b\u529f\u3002\u767d \u53d1\u9b54\u5973\u9053\uff1a\u201c\u8d70\u5440\uff0c\u6211\u80cc\u7684\u662f\u5927\u4eba\uff0c\u4f60\u80cc\u7684\u662f\u5b69\u5b50\uff0c\u4f60\u8fd8\u6015\u8f93\u7ed9\u6211\u5417\uff1f\u201d\u6666\u660e\u5e08\u5fae\u5fae\u4e00\u7b11\uff0c \u5fc3\u9053\uff1a\u51e0\u5e74\u524d\uff0c\u8f7b\u529f\u6211\u4e0d\u5982\u4f60\uff0c\u4eca\u65e5\u82e5\u6bd4\uff0c\u80dc\u8d1f\u5c1a\u672a\u53ef\u77e5\uff0c\u4f60\u600e\u9ebd\u5982\u6b64\u5938\u5927\uff01\u8981\u77e5\u6666\u660e\u5e08\u4e0e \u767d\u53d1\u9b54\u5973\u7684\u6b66\u529f\u672c\u6765\u662f\u540c\u51fa\u4e00\u6e90\uff0c\u4e00\u6b63\u4e00\u53cd\uff0c\u4e43\u662f\u6666\u660e\u7684\u5e08\u7236\u5929\u90fd\u5c45\u58eb\u4e0e\u59bb\u5b50\u8d4c\u6c14\uff0c\u5404\u521b\u51fa \u6765\u7684\u3002\u5929\u90fd\u5c45\u58eb\u66fe\u9053\uff1a\u4e00\u6b63\u4e00\u53cd\uff0c\u867d\u7136\u5404\u6709\u7279\u957f\u3002\u4f46\u82e6\u7ec3\u81f3\u51fa\u795e\u4eba\u5316\u4e4b\u5883\uff0c\u7089\u706b\u7eaf\u9752\u4e4b\u65f6\uff0c \u6b63\u5fc5\u80dc\u53cd\uff0c\u6b64\u4e43\u662f\u4e0d\u6613\u4e4b\u7406\u7684\u3002\u6240\u4ee5\u6666\u660e\u5e08\u4e5f\u60f3\u501f\u767d\u53d1\u9b54\u5973\u7684\u8f83\u8003\uff0c\u6d4b\u91cf\u81ea\u5df2\u7684\u529f\u592b\u3002\u65e2\u88ab \u767d\u53d1\u9b54\u5973\u4e00\u50ac\u518d\u8feb\uff0c\u4fbf\u542b\u7b11\u9053\u201c\u597d\u201d\u3002\u8eab\u5f62\u4e00\u8d77\uff0c\u75be\u903e\u98d8\u98ce\uff0c\u767d\u53d1\u9b54\u5973\u7d27\u8ddf\u5728\u5f8c\uff0c\u6070\u5982\u767d\u5f71 \u4e24\u56e2\uff0c\u5728\u5927\u8349\u539f\u4e0a\u6eda\u8fc7\u3002 \u3000\u3000\u8dd1\u4e86\u534a\u65e5\uff0c\u6e10\u60ed\u5df2\u5230\u8349\u539f\u4e4b\u8fb9\uff0c\u518d\u8fc7\u53bb\u5c31\u662f\u5929\u5c71\u5c71\u8109\u6240\u6784\u6210\u7684\u9ad8\u539f\u4e86\u3002\u6666\u660e\u5e08\u56e0\u5148\u8d77\u811a \u6b65\uff0c\u6240\u80cc\u7684\u5b69\u5b50\u53c8\u8f7b\u65bc\u767d\u53d1\u9b54\u5973\u6240\u63d0\u7684\u5927\u4eba\uff0c\u56e0\u6b64\u7adf\u7136\u5360\u5148\u4e86\u5341\u9980\u6b65\u3002\u767d\u53d1\u9b54\u5973\u500f\u7136\u505c\u6b65\uff0c \u9053\uff1a\u201c\u4e0d\u5fc5\u6bd4\u4e86\uff0c\u8fd9\u56de\u54b1\u4eec\u662f\u4e0d\u76f8\u4e0a\u4e0b\u4e86\u3002\u4f60\u82e6\u7ec3\u51e0\u5e74\uff0c\u8fdb\u6b65\u795e\u901f\uff0c\u53ef\u53ef\u8d3a\u3002\u201d\u6666\u660e\u5e08\u6697\u6697 \u9053\u58f0\u60ed\u6127\uff0c\u4e24\u4eba\u505c\u4e86\u4e0b\u6765\u3002\u90a3\u5b69\u5b50\u559c\u5f97\u62cd\u624b\u53eb\u9053\uff1a\u201c\u5e08\u5085\uff0c\u4f60\u662f\u4f1a\u4ed9\u6cd5\u7684\u9ebd\uff1f\u6211\u5728\u4f60\u7684\u80cc \u4e0a\uff0c\u597d\u50cf\u817e\u4e91\u9a7e\u96fe\u4e00\u822c\u3002\u201d\u6666\u660e\u5e08\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u8f7b\u529f\uff0c\u4e0d\u662f\u4ed9\u6cd5\u3002\u4f60\u957f\u5927\u4e86\u5c31\u77e5\u9053\u4e86\u3002\u201d\u90a3 \u5b69\u5b50\u9053\uff1a\u201c\u5e08\u7236\uff0c\u8fd9\u4e2a\u6211\u4e5f\u8981\u5b66\u3002\u201d\u767d\u7230\u9b54\u5973\u77a7\u4e86\u8fd9\u5b69\u5b50\u4e00\u773c\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u65b0\u6536\u7684\u5f92\u5f1f \u5417\uff1f\u201d\u6666\u660e\u5e08\u70b9\u4e86\u70b9\u5934\uff0c\u767d\u53d1\u9b54\u5973\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u7684\u806a\u660e\u4e0d\u5728\u6768\u4e91\u9aa2\u4e4b\u4e0b\uff0c\u5fc3\u672f\u5374\u4f3c\u4e0d\u5982\u3002\u201d \u6666\u660e\u5e08\u9053\uff1a\u201c\u4ed6\u5e74\u7eaa\u5c1a\u5c0f\uff0c\u597d\u4e0e\u4e0d\u597d\uff0c\u6210\u4e0d\u6210\u6750\uff0c\u8a00\u4e4b\u5c1a\u65e9\u5462\u3002\u201d\u767d\u53d1\u9b54\u5973\u5c06\u8fde\u57ce\u864e\u653e\u4e86\u4e0b \u6765\uff0c\u89e3\u5f00\u4ed6\u7684\u7a74\u9053\uff0c\u7b11\u9053\uff1a\u201c\u73b0\u5728\u8be5\u5ba1\u95ee\u4ed6\u4e86\uff01\u201d\u6666\u660e\u5e08\u9053\uff1a\u201c\u5148\u95ee\u95ee\u4ed6\uff0c\u9b4f\u5fe0\u8d24\u6d3e\u4e86\u591a\u5c11 \u4eba\u6765\uff1f\u201d\u8fde\u57ce\u864e\u9053\uff1a\u201c\u9b4f\u5b97\u4e3b\u5df2\u7ecf\u6b7b\u4e86\uff01\u201d\u6666\u660e \u3000\u3000\u5e08\u4e0e\u767d\u53d1\u9b54\u5973\u4e0d\u7981\u6115\u7136\uff0c\u767d\u53d1\u9b54\u5973\u6025\u9053\uff1a\u201c\u600e\u9ebd\u6b7b\u7684\uff1f\u201d\u8fde\u57ce\u864e\u9053\uff1a\u201c\u88ab\u65b0\u7687\u5e1d\u51cc\u8fdf\u5904 \u6b7b\u7684\u3002\u201d\u6666\u660e\u5e08\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u6211\u8fd8\u9053\u4ed6\u662f\u5bff\u7ec8\u6b63\u5bdd\uff0c\u90a3\u5c31\u4fbf\u5b9c\u4ed6\u4e86\u3002\u201d\u767d\u53d1\u9b54\u5973\u9053\uff1a\u201c\u5ba2\u6c0f \u5462\uff1f\u201d\u8fde\u57ce\u864e\u9053\uff1a\u201c\u4e5f\u88ab\u5904\u6b7b\u4e86\u3002\u201d\u6666\u660e\u5e08\u56e0\u66fe\u76ee\u7779\u5ba2\u6c0f\u7684\u6deb\u90aa\u548c\u628a\u6301\u671d\u653f\uff01\u5fc3\u4e2d\u6697\u6697\u79f0 \u5feb\u3002\u767d\u53d1\u9b54\u5973\u5374\u4e3a\u5ba2\u5a09\u5a77\u611f\u5230\u6709\u70b9\u4f24\u5fc3\u3002\u9053\uff1a\u201c\u5176\u5b9e\u5979\u662f\u7ed9\u9b4f\u5fe0\u8d24\u6240\u5229\u7528\uff0c\u5c06\u5979\u9010\u51fa\u5bab\u4e5f\u53ef \u4ee5\u4e86\u3002\u201d\u518d\u95ee\u8be6\u7ec6\u60c5\u5f62\uff0c\u8fde\u57ce\u864e\u6015\u767d\u53d1\u9b54\u5973\u7684\u6bd2\u5211\uff0c\u4e00\u4e00\u8bf4\u4e86\uff0c\u53ea\u9690\u7792\u4e86\u81ea\u5df1\u662f\u6ee1\u5dde\u7684\u5185\u5e94 \u548c\u5230\u56de\u7586\u7684\u539f\u56e0\u3002\u5c82\u77e5\u767d\u53d1\u9b54\u5973\u65e9\u4ece\u5e94\u4fee\u9633\u7684\u4f9b\u8bcd\u4e2d\u77e5\u9053\u8fde\u57ce\u864e\u4e43\u662f\u5185\u5978\u3002\u5f85\u4ed6\u8bf4\u5b8c\u4e4b\u5f8c\uff0c \u5fae\u7b11\u9053\uff1a\u201c\u4f60\u6240\u8bf4\u7684\u8fd8\u6709\u4e0d\u5c3d\u4e0d\u5b9e\u4e4b\u5904\u5427\uff1f\u201d\u8fde\u57ce\u864e\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u786c\u7740\u5934\u76ae\u8bf4\u9053\uff1a\u201c\u6ca1\u6709 \u5440\uff01\u201d\u767d\u53d1\u9b54\u5973\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u662f\u6ee1\u6d32\u7684\u5185\u5e94\uff0c\u4e3a\u4f55\u9690\u7792\u4e0d\u8bf4\uff1f\u201d\u8fde\u57ce\u864e\u9762\u65e0\u4eba\u8272\uff0c\u820c\u5934\u6253 \u7ed3\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u3002\u767d\u53d1\u9b54\u5973\u9053\uff1a\u201c\u4f60\u4f5c\u6076\u591a\u7aef\uff0c\u9976\u4f60\u4e0d\u5f97\u3002\u201d\u5251\u5149\u4e00\u8d77\uff0c\u5c06\u4ed6\u5288\u4e3a\u4e24\u6bb5\u3002 \u3000\u3000\u767d\u53d1\u9b54\u5973\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5cb3\u9e23\u73c2\uff0c\u4e0d\uff0c\u6211\u5fd8\u6389\u4f60\u505a\u4e86\u548c\u5c1a\u4e86\u3002\u6666\u660e\u5e08\uff0c\u54b1\u4eec\u518d\u6bd4\u4e00\u6bd4 \u5251\u6cd5\u5982\u4f55\uff1f\u201d \u3000\u3000\u6666\u660e\u5e08\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u516c\u5e73\u3002\u201d\u767d\u53d1\u9b54\u5973\u9053\uff1a\u201c\u600e\u4e0d\u516c\u5e73\uff01\u201d\u6666\u660e\u5e08\u5c06\u6e38\u9f99\u5251\u62d4\u51fa\uff0c\u968f\u624b \u4e00\u6325\uff0c\u5c06\u4e00\u5757\u77f3\u5934\u65a9\u4e3a\u4e24\u534a\u3002\u767d\u53d1\u9b54\u5973\u597d\u751f\u8273\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u8fd8\u4f1a\u70bc\u5251\u3002\u201d\u6666\u660e\u5e08\u9053\uff1a\u201c\u5176 \u5b9e\u6b66\u529f\u82e5\u5230\u4e86\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u7528\u4ec0\u9ebd\u5175\u5668\u90fd\u662f\u4e00\u6837\u3002\u6211\u82e6\u5fc3\u94f8\u70bc\u4e24\u628a\u5b9d\u5251\uff0c\u4e0d\u8fc7\u662f\u60f3\u4f20\u7ed9\u5f92 \u5f1f\uff0c\u8ba9\u4ed6\u9632\u8eab\u7f62\u4e86\u3002\u201d\u767d\u53d1\u9b54\u5973\u610f\u4f3c\u4e0d\u4fe1\uff0c\u9053\uff1a\u201c\u7528\u5b9d\u5251\u603b\u5360\u70b9\u4fbf\u5b9c\u3002\u201d\u6666\u660e\u5e08\u9053\uff1a\u201c\u6211\u8f88 \u529f\u529b\u672a\u7eaf\uff0c\u5251\u6cd5\u76f8\u5dee\u4e0d\u8fdc\uff0c\u90a3\u81ea\u7136\u662f\u6709\u5b9d\u5251\u7684\u5360\u4fbf\u5b9c\u4e86\u3002\u201d\u987f\u4e86\u4e00\u987f\uff0c\u53c8\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u6211\u7684 \u5251\u6cd5\u529f\u529b\u90fd\u5dee\u4e0d\u591a\uff0c\u4e0d\u5982\u4f60\u8bd5\u7528\u6211\u8fd9\u628a\u5b9d\u5251\uff0c\u770b\u80fd\u5426\u5728\u767e\u62db\u4e4b\u5185\uff0c\u5c06\u6211\u6253\u8d25\u3002\u201d\u767d\u53d1\u9b54\u5973\u6697 \u6697\u751f\u6c14\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u82e5\u4f7f\u6b64\u5b9d\u5251\uff0c\u4f55\u7528\u767e\u62db\u3002\u201d\u4fbf\u4e0d\u5ba2\u6c14\uff0c\u5c06\u6e38\u9f99\u5251\u63a5\u8fc7\uff0c\u968f\u4fbf\u7acb\u4e86\u95e8\u6237\uff0c \u53eb\u9053\uff1a\u201c\u8fdb\u62db\uff01\u201d \u3000\u3000\u6666\u660e\u5e08\u9053\uff1a\u201c\u4f60\u5148\u8bf7\u3002\u201d\u767d\u53d1\u9b54\u5973\u4e00\u58f0\u201c\u6709\u50ed\u201d\uff0c\u5251\u950b\u4e00\u98a4\uff0c\u6a2a\u5251\u4fbf\u523a\u3002\u6666\u660e\u5e08\u6c89\u5251\u4e00 \u5f15\uff0c\u5c06\u5979\u7684\u653b\u52bf\u5316\u89e3\u65bc\u65e0\u5f62\u3002\u767d\u53d1\u9b54\u5973\u8f6c\u950b\u53cd\u524a\uff0c\u6666\u660e\u5e08\u5e76\u4e0d\u62db\u67b6\uff0c\u53cd\u624b\u4e00\u5251\uff0c\u62a2\u653b\u5979\u7684\u7a7a \u95e8\uff0c\u8fd9\u4e00\u62db\u662f\u653b\u654c\u4e4b\u6240\u5fc5\u6551\uff0c\u767d\u53d1\u9b54\u5973\u8feb\u5f97\u79fb\u5251\u76f8\u62d2\uff0c\u6666\u660e\u5e08\u75be\u8fdb\u6570\u5251\uff0c\u4e00\u6cbe\u5373\u8d70\uff0c\u6559\u5979\u867d \u6709\u5b9d\u5251\uff0c\u4e5f\u65e0\u80fd\u4e3a\u529b\u3002\u767d\u53d1\u9b54\u5973\u6597\u5f97\u6027\u8d77\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u4fbf\u548c\u4f60\u62a2\u653b\uff0c\u770b\u4f60\u600e\u80fd\u8eb2\u907f\u5f97\u4e86\uff1f\u201d \u8eab\u5f62\u4e00\u8d77\uff0c\u5251\u6cd5\u75be\u53d8\uff0c\u6666\u660e\u5e08\u9ed8\u8fd0\u7384\u529f\uff0c\u51dd\u8eab\u4e0d\u52a8\uff0c\u5f85\u5979\u5251\u5230\uff0c\u53cd\u624b\u4e00\u7ede\uff0c\u4e24\u5251\u5e73\u9ecf\uff0c\u5982\u78c1 \u5438\u94c1\uff0c\u767d\u53d1\u9b54\u5973\u7684\u5251\u6307\u5411\u4e1c\u65b9\uff0c\u6666\u660e\u5e08\u7684\u5251\u4e5f\u8ddf\u7740\u5230\u4e1c\uff0c\u767d\u53d1\u9b54\u5973\u7684\u5251\u5230\u897f\uff0c\u4ed6\u4e5f\u8ddf\u7740\u5230 \u897f\uff0c\u672a\u5230\u767e\u62db\uff0c\u767d\u53d1\u9b54\u5973\u5df2\u500f\u7136\u6536\u5251\uff0c\u6c14\u9053\uff1a\u201c\u8fd8\u662f\u4e8c\u5341\u5e74\u5f8c\u518d\u6bd4\u5427\uff1f\u201d\u5c06\u6e38\u9f99\u5251\u4ea4\u56de\u6666\u660e \u5e08\uff0c\u63a5\u56de\u81ea\u5df1\u7684\u5251\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u98de\u8eab\u4fbf\u8d70\u3002\u6666\u660e\u5e08\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u600e\u9ebd\u8fd8\u662f\u5982\u6b64\u597d\u80dc\uff1f\u201d \u4ed6\u672c\u60f3\u95ee\u5353\u4e00\u822a\u738b\u7167\u5e0c\u7b49\u4e00\u73ed\u65e7\u53cb\u7684\u6d88\u606f\u4ee5\u53ca\u5979\u7684\u7ecf\u5386\uff0c\u90fd\u6765\u4e0d\u53ca\u95ee\u4e86\u3002 \u3000\u3000\u6666\u660e\u5e08\u5c06\u5b69\u5b50\u5e26\u56de\u5929\u5c71\uff0c\u7ed9\u4ed6\u53d6\u540d\u695a\u662d\u5357\uff0c\u9664\u4e86\u4eb2\u81ea\u6559\u4ed6\u7ec3\u91cd\u5b50\u529f\u4e4b\u5916\uff0c\u5e76\u53eb\u6768\u4e91\u9a8b\u6559 \u4ed6\u7684\u57fa\u672c\u529f\u592b\uff0c\u5982\uff1a\u7ec3\u773c\u795e\u7ec3\u8170\u6b65\u7ec3\u638c\u6cd5\u7b49\u7b49\u3002\u8f6c\u77ac\u8fc7\u4e86\u6570\u6708\uff0c\u5df2\u662f\u9686\u51ac\uff0c\u5929\u5c71\u6c14\u5019\u5947\u5bd2\uff0c \u4e24\u4e2a\u5c0f\u5b69\u5b50\u6bcf\u65e5\u6e05\u6668\uff0c\u5fc5\u5728\u5916\u9762\u7ec3\u6b66\u6696\u8eab\u3002\u4e00\u65e5\u6666\u660e\u5e08\u6b63\u5728\u623f\u9759\u5750\uff0c\u5ffd\u542c\u5f97\u5916\u9762\u4e24\u4e2a\u5c0f\u5b69\u5b50 \u4f3c\u5728\u548c\u4eba\u8bf4\u8bdd\u3002\u6666\u660e\u5e08\u8d70\u51fa\u9662\uff0c\u53ea\u89c1\u4e00\u4e2a\u76f8\u8c8c\u4e11\u964b\u7684\u8001\u5a46\u5a46\u7ad9\u5728\u5f53\u4e2d\uff0c\u4efb\u7531\u4e24\u4e2a\u5b69\u5b50\u5411\u5979\u53d1 \u638c\uff0c\u5979\u4e1c\u4e00\u98d8\u897f\u4e00\u6c64\uff0c\u5f15\u5f97\u4e24\u4e2a\u5b69\u5b50\u8ddf\u7740\u5979\u56e2\u56e2\u4e71\u8f6c\u3002\u6666\u660e\u5e08\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u9686\u51ac\u65f6 \u4efd\uff0c\u80fd\u4e0a\u5929\u5c71\uff0c\u6b66\u529f\u5df2\u662f\u975e\u540c\u5c0f\u9119\u3002\u201d\u770b\u5979\u7684\u8eab\u6cd5\u66f4\u662f\u6700\u4e0a\u4e58\u7684\u529f\u592b\uff0c\u800c\u4e14\u4f3c\u66fe\u89c1\u8fc7\u3002\u4e0d\u7981 \u95ee\u9053\uff1a\u201c\u5582\uff0c\u4f60\u662f\u4f55\u4eba\uff0c\u600e\u9ebd\u6b3a\u8d1f\u5b69\u5b50\uff1f\u201d\u695a\u662d\u5357\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u5feb\u52a8\u624b\uff0c\u5979\u8bf4\u6211\u4eec\u7684\u5929\u5c71 \u638c\u6cd5\u6709\u865a\u540d\u5462\u3002\u201d\u90a3\u8001\u5a46\u5a46\u4e00\u58f0\u4e0d\u53d1\uff0c\u5ffd\u7136\u4e00\u638c\u5411\u6666\u660e\u5e08\u62cd\u6765\uff0c\u638c\u52bf\u8f7b\u98d8\uff0c\u52b2\u529b\u5374\u662f\u5341\u8db3\u3002 \u6666\u660e\u5e08\u8fd0\u638c\u62b5\u5fa1\uff0c\u6597\u4e86\u7247\u523b\uff0c\u5df2\u662f\u5fc3\u4e2d\u96ea\u4eae\uff0c\u5374\u4e0d\u5148\u8bf4\u7834\uff0c\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u5b34\u4e86\u4e00\u638c\uff0c\u90a3\u8001 \u5a46\u5a46\u817e\u8eab\u4fbf\u8d70\u3002\u6666\u660e\u5e08\uff1a\u201c\u5582\uff0c\u4f60\u8fdc\u4e0a\u5929\u5c71\uff0c\u5c31\u662f\u5355\u4e3a\u627e\u6211\u6bd4\u638c\u5417\uff1f\u201d\u8ffd\u8fc7\u4e24\u4e2a\u5c71\u5cf0\uff0c\u90a3\u8001 \u5a46\u5a46\u500f\u7136\u505c\u6b65\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u624b\u4e0a\u62ff\u7740\u4e00\u5f20\u9762\u5177\u3002 \u3000\u3000\u8fd9\u201c\u8001\u5a46\u5a46\u201d\u6b63\u662f\u767d\u53d1\u9b54\u5973\uff0c\u5979\u4e0d\u77e5\u4ece\u90a3\u91cc\u5f04\u6765\u4e86\u4e00\u5f20\u9762\u5177\uff0c\u628a\u81ea\u5df1\u53d8\u6210\u4e11\u964b\u96be\u770b\u7684\u8001 \u5987\u4eba\u3002\u6666\u660e\u5e08\u6120\u9053\uff1a\u201c\u4f60\u4f55\u5fc5\u5f00\u8fd9\u4e2a\u73a9\u7b11\uff1f\u201d\u767d\u53d1\u9b54\u5973\u9762\u5bb9\u6c89\u90c1\uff0c\u5e7d\u5e7d\u8bf4\u9053\uff1a\u201c\u8fd9\u9762\u5177\u914d\u4e0a \u6211\u7684\u4e00\u5934\u767d\u53d1\uff0c\u4e0d\u6b63\u597d\u5417\uff1f\u201d\u6666\u660e\u5e08\u89c1\u5979\u4e1d\u6beb\u4e0d\u50cf\u8bf4\u7b11\u7684\u6837\u5b50\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6599\u60f3\u5979\u5fc5\u6709\u4f24\u5fc3 \u4e4b\u4e8b\uff0c\u4fbf\u9ed8\u7136\u65e0\u8bed\uff0c\u542c\u5979\u8bf4\u8bdd\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u767d\u53d1\u9b54\u5973\u53f9\u4e86\u53e3\u6c14\uff0c\u5f00\u58f0\u95ee\u9053\uff1a\u201c\u5353\u4e00\u822a\u66fe\u627e\u8fc7\u4f60\u5417\uff1f\u201d\u6666\u660e\u5e08\u8be7\u9053\uff1a\u201c\u5353 \u4e00\u822a\u51e0\u65f6\u6765\u4e86\u56de\u7586\uff01\u201d\u767d\u53d1\u9b54\u5973\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4f60\u4eec\u8fd8\u672a\u66fe\u76f8\u89c1\u3002\u201d\u6666\u660e\u5e08\u9053\uff1a\u201c\u4ed6\u82e5\u5230 \u6765\uff0c\u5f53\u7136\u662f\u5148\u53bb\u627e\u4f60\u3002\u201d\u767d\u53d1\u9b54\u5973\u51c4\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4ed6\u662f\u5728\u627e\u6211\u3002\u201d\u6666\u660e\u5e08\u9053\uff1a\u201c\u4f60\u4eec\u5c1a\u672a \u76f8\u9022\u5417\uff1f\u6211\u771f\u4e0d\u660e\u767d\uff0c\u4f60\u4eec\u672c\u53ef\u662f\u795e\u4ed9\u7737\u5c5e\uff0c\u4f55\u4ee5\u4e0d\u76f8\u805a\u4e00\u5757\uff0c\u5374\u95f9\u5230\u7a77\u8fb9\u585e\u5916\uff1f\u201d\u767d\u53d1\u9b54 \u5973\u53c8\u6447\u4e86\u6447\u5934\u3002\u6666\u660e \u3000\u3000\u5e08\u6b63\u60f3\u518d\u95ee\uff0c\u767d\u53d1\u9b54\u5973\u5ffd\u9053\uff1a\u201c\u4ed6\u82e5\u6765\u89c1\u4f60\uff0c\u4f60\u53ef\u529d\u4ed6\u65e9\u65e9\u56de\u53bb\uff0c\u4e0d\u8981\u518d\u627e\u6211\u4e86\u3002\u201d\u6666 \u660e\u5e08\u56b7\u9053\uff1a\u201c\u4e3a\u4ec0\u9ebd\uff1f\u201d\u767d\u53d1\u9b54\u5973\u9762\u8272\u500f\u53d8\uff0c\u53f9\u9053\uff1a\u201c\u6211\u8be5\u8d70\u4e86\uff01\u201d\u6666\u660e\u5e08\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4e14 \u6162\u8d70\uff0c\u4f60\u4eec\u5230\u5e95\u5728\u95f9\u4ec0\u9ebd\uff1f\u201d\u767d\u53d1\u9b54\u5973\u9053\uff1a\u201c\u5929\u5c71\u5357\u5317\u4e8c\u5cf0\uff0c\u76f8\u8ddd\u5343\u91cc\uff0c\u4f60\u5360\u4e86\u5317\u9ad8\u5cf0\uff0c\u6211 \u53ea\u597d\u5360\u5357\u9ad8\u5cf0\u4e86\u3002\u201d\u6666\u660e\u5e08\u9053\uff1a\u201c\u5353\u4e00\u822a\u82e5\u6765\uff0c\u6211\u5c31\u53eb\u4ed6\u627e\u4f60\u3002\u201d\u767d\u53d1\u9b54\u5973\u9053\uff1a\u201c\u4f60\u4f55\u5fc5\u591a \u4e8b\uff1f\u6211\u662f\u518d\u4e5f\u4e0d\u89c1\u4ed6\u4e86\uff01\u201d\u8bf4\u7f62\u98de\u5954\u4e0b\u5c71\u3002\u6666\u660e\u5e08\u60f3\u8ffd\u4e4b\u65e0\u76ca\uff0c\u53f9\u9053\uff1a\u201c\u60c5\u7f18\u6613\u7ed3\u96be\u89e3\u5f00\uff0c \u4f24\u5fc3\u4e16\u4e8b\u77e5\u591a\u5c11\uff1f\u201d\u9762\u4e0a\u7a81\u7136\u4e00\u9635\u53d1\u70ed\uff0c\u60f3\u8d77\u81ea\u5df1\u4ee5\u5f80\u7684\u60c5\u5b7d\uff0c\u5fc3\u52a8\u4e71\uff0c\u6025\u6025\u56de\u623f\u9759\u5750\u3002 \u3000\u3000\u5927\u7ea6\u53c8\u8fc7\u4e86\u534a\u4e2a\u6708\u7684\u5149\u666f\uff0c\u4e00\u65e5\u9ec4\u660f\uff0c\u6708\u7259\u521d\u73b0\uff0c\u6666\u660e\u5e08\u5728\u5929\u5c71\u4e4b\u5dc5\u7ec3\u5251\uff0c\u4f7f\u5230\u75be\u5904\uff0c \u5251\u5149\u6708\u8272\u6eb6\u6210\u4e00\u5904\u3002\u5ffd\u542c\u5f97\u5c71\u8170\u5904\u6709\u6089\u6089\u7d22\u7d22\u4e4b\u58f0\uff0c\u6666\u660e\u5e08\u6025\u8d70\u8fc7\u53bb\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u8d5e\u9053\uff1a \u201c\u597d\u5251\u6cd5\uff01\u201d\u6666\u660e\u5e08\u62e8\u5f00\u79ef\u96ea\u8513\uff0c\u53ea\u89c1\u5353\u4e00\u822a\u51bb\u5f97\u6ee1\u9762\u901a\u7ea2\uff0c\u624b\u8db3\u50f5\u786c\uff0c\u722c\u5728\u79ef\u96ea\u5806\u4e2d\u3002\u6666 \u660e\u5e08\u9053\uff1a\u201c\u4f60\u8f9b\u82e6\u4e86\uff01\u201d\u5353\u4e00\u822a\u7ad9\u4e86\u8d77\u6765\uff0c\u6413\u6413\u624b\u8db3\uff0c\u7b11\u9053\uff1a\u201c\u73b0\u5728\u5df2\u60ef\u4e9b\u4e86\uff0c\u521d\u6765\u65f6\u66f4\u8f9b \u82e6\u5462\uff01\u53ea\u662f\u8fd9\u51e0\u65e5\u7279\u522b\u5bd2\u51b7\uff0c\u5475\u6c14\u6210\u51b0\uff0c\u6211\u51e0\u4e4e\u4ee5\u4e3a\u4e0a\u4e0d\u5230\u5c71\u5dc5\u5462\uff01\u201d \u3000\u3000\u6666\u660e\u5e08\u6025\u5c06\u4ed6\u5e26\u56de\u9662\uff0c\u53eb\u6768\u4e91\u9aa2\u5012\u70ed\u8336\u7ed9\u4ed6\u559d\uff0c\u5f85\u4ed6\u6b47\u606f\u4e4b\u5f8c\uff0c\u7ec6\u95ee\u7ecf\u8fc7\uff0c\u624d\u77e5\u5353\u4e00\u822a \u56e0\u521d\u6b21\u5b64\u8eab\u8fdc\u884c\uff0c\u53c8\u4e0d\u719f\u897f\u5317\u5730\u7406\uff0c\u4ece\u5c71\u897f\u5230\u56de\u7586\u6765\u51e0\u4e4e\u8d70\u4e86\u4e00\u5e74\uff0c\u5230\u4e86\u56de\u7586\u4e4b\u5f8c\uff0c\u5728\u90a3\u7ef5 \u4e98\u53c1\u5343\u9980\u91cc\u7684\u5929\u5c71\u4e4b\u4e2d\u6478\u7d22\uff0c\u6e34\u4fbf\u56bc\u96ea\uff0c\u997f\u4fbf\u730e\u53d6\u96ea\u7f8a\u70e7\u70e4\u6765\u5403\uff0c\u53c8\u7ecf\u8fc7\u534a\u5e74\u591a\uff0c\u624d\u6478\u5230\u8fd9 \u91cc\u3002\u597d\u5728\u867d\u7136\u5386\u5c3d\u7f16\u695a\uff0c\u8eab\u4f53\u5374\u7ec3\u5f97\u975e\u5e38\u7ed3\u5b9e\uff0c\u6b66\u529f\u4e5f\u6bd4\u524d\u5927\u8fdb\u4e86\u3002 \u3000\u3000\u597d\u53cb\u76f8\u9022\uff0c\u5f53\u7136\u662f\u5341\u5206\u9ad8\u5174\u3002\u5353\u4e00\u822a\u7559\u5728\u5929\u5c71\u6570\u65e5\uff0c\u5c06\u522b\u5f8c\u4e8b\u60c5\uff0c\u4e00\u4e00\u503e\u5410\u3002\u8bf4\u5230\u7389\u7f57 \u5239\u5728\u6b66\u5f53\u5c71\u5927\u6218\u4e4b\u5f8c\uff0c\u4f24\u5fc3\u800c\u53bb\u7684\u4e8b\uff0c\u4e0d\u89c9\u6389\u4e0b\u6cea\u6765\u3002\u5cb3\u545c\u73c2\u7b11\u9053\uff1a\u201c\u7389\u7f57\u5239\u524d\u51e0\u5929\u521a\u521a\u6765 \u8fc7\u3002\u554a\uff0c\u6211\u5fd8\u8bb0\u544a\u8bc9\u4f60\uff0c\u8fd9\u91cc\u7684\u4eba\u90fd\u53eb\u5979\u505a\u767d\u53d1\u9b54\u5973\uff0c\u6ca1\u4eba\u77e5\u9053\u5979\u4fbf\u662f\u5f53\u5e74\u5a01\u9707\u6c5f\u6e56\u7684\u7389\u7f57 \u5239\u4e86\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u53f9\u9053\uff1a\u201c\u662f\u554a\uff0c\u5979\u4e3a\u6211\u767d\u4e86\u5934\u53d1\uff0c\u6211\u5374\u65e0\u6cd5\u627e\u5bfb\u7075\u4e39\u5999\u836f\uff0c\u66ff\u5979\u6062\u590d\u9752\u6625\u3002\u201d\u6666 \u660e\u5e08\u60f3\u8d77\u5929\u5c71\u5357\u5317\u7267\u6c11\u7684\u4e00\u4e2a\u4f20\u8bf4\uff0c\u7b11\u9053\uff1a\u201c\u6062\u590d\u9752\u6625\u7684\u5999\u836f\u4e5f\u8bb8\u6ca1\u6709\uff0c\u4f46\u4eca\u767d\u53d1\u53d8\u56de\u9752 \u4e1d\uff0c\u800c\u4e14\u53ef\u4ee5\u4fdd\u4f4f\u9752\u6625\u7684\u5999\u836f\u5374\u672a\u5c1d\u6ca1\u6709\u3002\u201d\u5353\u4e00\u822a\u6025\u95ee\u9053\uff1a\u201c\u5728\u90a3\u513f\u6709\uff1f\u201d\u6666\u660e\u5e08\u9053\uff1a \u201c\u636e\u8349\u539f\u4e0a\u7684\u7267\u6c11\u4f20\u8bf4\uff0c\u6709\u4e00\u79cd\u82b1\u53eb\u505a\u4f18\u6619\u4ed9\u82b1\uff0c\u6bcf\u516d\u5341\u5e74\u624d\u5f00\u82b1\u4e00\u6b21\uff0c\u6bcf\u6b21\u5f00\u82b1\uff0c\u5fc5\u7ed3\u4e24 \u6735\uff0c\u4e00\u767d\u4e00\u7ea2\uff0c\u5927\u5982\u5de8\u7897\u3002\u636e\u8bf4\u53ef\u4ee4\u767d\u53d1\u53d8\u9ed1\uff0c\u8fd4\u8001\u8fd8\u7ae5\u3002\u6211\u60f3\u8fd9\u5927\u7ea6\u662f\u6bd4\u4f55\u9996\u4e4c\u66f4\u73cd\u8d35\u7684 \u836f\u6750\u3002\u8fd4\u8001\u8fd8\u7ae5\u6211\u4e0d\u76f8\u4fe1\uff0c\u80fd\u4ee4\u767d\u53d1\u53d8\u9ed1\uff0c\u5374\u4e0d\u7a00\u5947\u3002\u201d\u5353\u4e00\u822a\u542c\u8bf4\u8981\u516d\u5341\u5e74\u624d\u5f00\u82b1\u4e00\u6b21\uff0c \u800c\u4e14\u8fd8\u4e0d\u77e5\u957f\u5728\u4ec0\u9ebd\u5730\u65b9\uff0c\u597d\u751f\u5931\u671b\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u82e5\u662f\u6b64\u82b1\u521a\u521a\u5f00\u8fc7\uff0c\u518d\u7b49\u516d\u5341\u5e74\u5979\u5c82\u4e0d\u662f \u76f8\u8fd1\u767e\u5c81\u3002\u201d \u3000\u3000\u6666\u660e\u7985\u5e08\u53c8\u8bf4\u8d77\u767d\u53d1\u9b54\u652f\u90a3\u65e5\u7684\u8a00\u8bed\u548c\u795e\u60c5\u3002\u5353\u4e00\u822a\u9053\uff1a\u201c\u5979\u82e5\u7edd\u60c5\u4e0d\u613f\u89c1\u6211\uff0c\u4e0d\u4f1a\u8bf4 \u51fa\u5979\u7684\u4f4f\u5904\u3002\u201d\u6666\u660e\u5e08\u9053\uff1a\u201c\u5357\u9ad8\u5cf0\u6bd4\u8fd9\u91cc\u66f4\u51b7\uff0c\u800c\u4e14\u4e00\u8def\u884c\u4e0a\u53bb\u90fd\u662f\u6e3a\u65e0\u4eba\u8ff9\u7684\u5927\u68ee\u6797\u3002 \u53ea\u6050\u6bd4\u6211\u8fd9\u91cc\u66f4\u4e0d\u6613\u627e\u5bfb\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5373\u7b97\u51bb\u6210\u5316\u77f3\uff0c\u547d\u4e27\u8352\u5c71\uff0c\u6211\u4e5f\u662f\u8981\u53bb\u7684\u3002\u201d \u3000\u3000\u6666\u660e\u5e08\u9053\uff1a\u201c\u90a3\u9ebd\u7b49\u521d\u590f\u89e3\u51bb\u4e4b\u5f8c\u518d\u53bb\u5427\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u5fc3\u6025\u5982\u711a\uff0c\u5982\u4f55\u7b49\u5230\u521d \u590f\uff1f\u201d\u6666\u660e\u5e08\u575a\u7559\u4ed6\u518d\u4f4f\u4e03\u65e5\uff0c\u5728\u8fd9\u4e03\u5929\u4e2d\u548c\u4ed6\u7814\u4e60\u5185\u529f\uff0c\u5353\u4e00\u822a\u672c\u6765\u6709\u6839\u5e95\uff0c\u7ecf\u6666\u660e\u5e08\u6307 \u70b9\uff0c\u8fdb\u76ca\u4e0d\u5c11\u3002\u5353\u4e00\u822a\u53f9\u9053\uff1a\u201c\u6211\u7684\u51e0\u4e2a\u5e08\u53d4\u72b9\u5982\u4e95\u5e95\u4e4b\u86d9\uff0c\u4e0d\u77e5\u6ca7\u6d77\u4e4b\u5927\uff0c\u81ea\u4ee5\u4e3a\u6b66\u529f\u76d6 \u4e16\u65e0\u53cc\uff0c\u6bd4\u8d77\u4f60\u4eec\uff0c\u771f\u662f\u5dee\u5f97\u592a\u8fdc\u3002\u201d\u6666\u660e\u5e08\u9053\uff1a\u201c\u4ed6\u4eec\u867d\u7136\u7a0d\u5fae\u81ea\u5927\uff0c\u5176\u5b9e\u6b66\u5f53\u7684\u5185\u529f\u5fc3 \u6cd5\uff0c\u90a3\u7684\u786e\u662f\u6b66\u6797\u6240\u94a6\u4f69\u7684\u3002\u5927\u7ea6\u662f\u4f60\u4eec\u8fbe\u6469\u79df\u5e08\u7684\u79d8\u7b08\u5931\u4f20\u4e4b\u5f8c\uff0c\u73b0\u5728\u5df2\u65e0\u4eba\u80fd\u7aa5\u5176\u5802\u5965 \u4e86\u5427\u3002\u201d\u5353\u4e00\u822a\u9887\u4e3a\u611f\u6168\uff0c\u9053\uff1a\u201c\u6211\u771f\u60f3\u62dc\u4f60\u4e3a\u5e08\uff0c\u8654\u4fee\u5251\u6cd5\u3002\u201d\u6666\u660e\u5e08\u7b11\u9053\uff1a\u201c\u5353\u89c1\uff0c\u4f60 \u8bf4\u7b11\u8bdd\u4e86\uff0c\u54b1\u4eec\u5f7c\u6b64\u7422\u78e8\uff0c\u90a3\u8fd8\u53ef\u4ee5\uff0c\u600e\u9ebd\u8bf4\u5f97\u4e0a\u4f20\u6388\u3002\u5176\u5b9e\uff0c\u4f60\u73b0\u5728\u653e\u7740\u4e00\u4e2a\u826f\u5e08\u76ca\u53cb\u795e \u4ed9\u7737\u5c5e\uff0c\u4f55\u5fc5\u4ed6\u6c42\u3002\u201d\u5353\u4e00\u822a\u77e5\u4ed6\u6240\u6307\uff0c\u53c8\u82e6\u7b11\u9053\uff1a\u201c\u82e5\u80fd\u5f97\u5979\u89c1\u6211\uff0c\u5df2\u662f\u5fc3\u6ee1\u610f\u8db3\u3002\u8c08\u5230 \u59fb\u7f18\u4e8c\u5b57\uff0c\u53ea\u6015\u6b64\u751f\u65e0\u671b\u4e86\u3002\u201d \u3000\u3000\u4e03\u65e5\u4e4b\u671f\u4e00\u6ee1\uff0c\u5353\u4e00\u822a\u62dc\u522b\u4e86\u6666\u660e\u5e08\uff0c\u53c8\u5411\u5357\u9ad8\u5cf0\u800c\u53bb\u3002\u5728\u539f\u59cb\u5927\u68ee\u6797\u4e2d\u884c\u4e86\u4e2a\u591a\u6708\uff0c \u53d7\u5c3d\u98ce\u971c\u96e8\u96ea\u4e4b\u82e6\uff0c\u866b\u86c7\u91ce\u517d\u4e4b\u60ca\uff0c\u597d\u5bb9\u6613\u624d\u671b\u5230\u5357\u9ad8\u5cf0\u3002\u4f46\u89c1\u96ea\u5c71\u63d2\u4e91\uff0c\u51b0\u6cb3\u5012\u6302\uff0c\u9e70\u76d8 \u65cb\uff0c\u96ea\u7f8a\u7ade\u8d70\uff0c\u5947\u5bd2\u5f7b\u9aa8\uff0c\u8352\u51c9\u9a87\u76ee\u3002\u5353\u4e00\u822a\u6709\u5982\u671d\u62dc\u5723\u5730\u7684\u4fe1\u5f92\uff0c\u6392\u9664\u4e00\u5207\u56f0\u96be\uff0c\u6500\u767b\u9ad8 \u5cf0\uff0c\u884c\u4e86\u53c1\u65e5\uff0c\u59cb\u5230\u5c71\u8170\u3002\u5e78\u4ed6\u5185\u529f\u5927\u8fdb\uff0c\u8981\u4e0d\u7136\u7edd\u96be\u652f\u6301\u3002\u8fd9\u65e5\u6b63\u5728\u6500\u767b\u4e4b\u9645\uff0c\u5bd2\u98ce\u9661 \u8d77\uff0c\u628a\u91ce\u8349\u5c71\u8305\u522e\u5f97\u547c\u5566\u5566\u54cd\uff0c\u78e8\u76d8\u4f3c\u7684\u5927\u96ea\u5757\uff0c\u904d\u5c71\u4e71\u6eda\u3002\u5353\u4e00\u822a\u6025\u5fd9\u6b62\u6b65\uff0c\u5728\u51e0\u68f5\u53c2\u5929 \u53e4\u6728\u6240\u56f4\u6210\u7684\u5929\u7136\u5c4f\u969c\u91cc\uff0c\u76d8\u819d\u9759\u5750\uff0c\u8eb2\u907f\u98ce\u96ea\u4e4b\u707e\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u987f\u996d\u7684\u65f6\u5019\uff0c\u98ce\u96ea\u6e10\u6b62\u3002\u5353\u4e00\u822a\u6b63\u60f3\u8d77\u8eab\u524d\u884c\uff0c\u5ffd\u542c\u5f97\u4e0d\u8fdc\u5904\uff0c\u4f3c\u6709\u4eba\u58f0\uff0c\u6e05\u6670 \u53ef\u95fb\u3002\u53ea\u542c\u5f97\u6709\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u4f60\u62ff\u5f97\u51c6\u767d\u53d1\u9b54\u5973\u5c31\u662f\u7389\u7f57\u5239\u5417\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u5403\u4e86\u4e00\u60ca\uff0c\u53ea\u542c\u5f97\u53e6\u4e00\u4eba\u7b54\u9053\uff1a\u201c\u7edd\u4e0d\u4f1a\u9519\u3002\u5979\u867d\u767d\u4e86\u5934\u53d1\uff0c\u989c\u5bb9\u6194\u60b4\uff0c\u4f46\u8fd8\u53ef \u8fa8\u8ba4\u51fa\u6765\u3002\u800c\u4e14\u90a3\u624b\u5251\u6cd5\uff0c\u5929\u4e0b\u4e5f\u65e0\u7b2c\u4e8c\u4e2a\u4eba\u4f1a\u4f7f\u3002\u201d\u5353\u4e00\u822a\u5411\u5916\u4e00\u671b\uff0c\u53ea\u89c1\u79bb\u81ea\u5df1\u5341\u9980\u4e08 \u5730\uff0c\u4ece\u6811\u4e1b\u4e2d\u8d70\u51fa\u56db\u4eba\uff0c\u60f3\u6765\u4e5f\u662f\u50cf\u81ea\u5df2\u4e00\u6837\uff0c\u8eb2\u907f\u98ce\u96ea\u4e4b\u707e\u7684\u3002 \u3000\u3000\u8fd9\u56db\u4eba\u88c5\u675f\u5404\u4e0d\u76f8\u540c\uff0c\u4e00\u4e2a\u662f\u62ab\u7740\u5927\u7ea2\u8888\u88df\u7684\u5587\u561b\uff0c\u4e00\u4e2a\u662f\u9ed1\u8863\u7384\u88f3\u7684\u9053\u58eb\uff0c\u4e00\u4e2a\u662f\u811a \u767b\u978b\uff0c\u9888\u9879\u6302\u6709\u51e0\u4e2a\u9ab7\u9ac5\u7684\u602a\u5f02\u50e7\u4eba\uff0c\u53e6\u4e00\u4e2a\u5374\u662f\u5e74\u5c06\u82b1\u7532\u7684\u8001\u5934\u3002\u5353\u4e00\u822a\u5927\u4e3a\u60ca\u5f02\uff0c\u5fc3 \u9053\uff1a\u201c\u96be\u9053\u8fd9\u56db\u4e2a\u4eba\u90fd\u662f\u51b2\u7740\u7389\u7f57\u5239\u6765\u7684\uff1f\u201d \u3000\u3000\u90a3\u8001\u5934\u8033\u76ee\u7279\u522b\u7075\u654f\uff0c\u5353\u4e00\u822a\u62ac\u5934\u5916\u671b\uff0c\u624b\u62e8\u5c71\u8305\uff0c\u53d1\u51fa\u4e9b\u5fae\u58f0\u606f\uff0c\u4ed6\u7acb\u5373\u60ca\u8d77\uff0c\u559d \u9053\uff1a\u201c\u6709\u4eba\uff01\u201d\u56db\u4eba\u4e00\u5217\u6446\u5f00\uff0c\u5982\u4e34\u5927\u654c\uff0c\u5353\u4e00\u822a\u77e5\u9053\u4e0d\u80fd\u518d\u8eb2\uff0c\u4ed6\u4fbf\u6602\u7136\u8d70\u51fa\uff0c\u65bd\u4e86\u4e00 \u6a59\uff0c\u95ee\u9053\uff1a\u201c\u5404\u4f4d\u90fd\u662f\u4e0a\u5357\u9ad8\u5cf0\u7684\u5417\uff1f\u201d \u3000\u3000\u8fd9\u56db\u4eba\u89c1\u4e0d\u662f\u767d\u53d1\u9b54\u5973\uff0c\u677e\u4e86\u53e3\u6c14\u3002\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u96ea\u5730\u51b0\u5929\uff0c\u5355\u8eab\u4e0a\u5357\u9ad8\u5cf0\u4f5c \u751a\uff1f\u201d\u5353\u4e00\u822a\u6b63\u5728\u8003\u8651\u8be5\u4e0d\u8be5\u8bf4\u5b9e\u8bdd\uff0c\u90a3\u7ea2\u8863\u5587\u561b\u5df2\u53d1\u8bdd\u9053\uff1a\u201c\u4e0d\u5fc5\u95ee\u4e86\uff0c\u4e00\u5b9a\u662f\u4e0a\u5357\u9ad8\u5cf0 \u627e\u767d\u53d1\u9b54\u5973\u7684\uff0c\u662f\u4e5f\u4e0d\u662f\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\uff1f\u201d\u7ea2\u8863\u5587\u561b\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u627e\u5979\u6666\u6c14\u7684 \u5417\uff1f\u201d\u5353\u4e00\u822a\u4e00\u542c\uff0c\u77e5\u9053\u8fd9\u56db\u4eba\u4e43\u662f\u7389\u7f57\u5239\u7684\u4ec7\u4eba\uff0c\u6c14\u5f80\u4e0a\u51b2\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u50cf\u6211\u8fd9\u6837\u7684\u4eba\uff0c \u518d\u591a\u5341\u4e2a\uff0c\u4e5f\u4e0d\u6562\u627e\u5979\u6666\u6c14\u3002\u201d\u90a3\u8001\u5934\u53d8\u4e86\u989c\u8272\uff0c\u559d\u9053\uff1a\u201c\u4f60\u662f\u4f55\u4eba\uff1f\u201d\u5353\u4e00\u822a\u50b2\u7136\u7b54\u9053\uff1a \u201c\u6b66\u5f53\u6d3e\u95e8\u4e0b\u5f1f\u5b50\u5353\u4e00\u822a\u3002\u201d\u90a3\u8001\u4eba\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\uff0c\u4f60\u653e\u7740\u597d\u597d\u7684\u638c\u95e8 \u4e0d\u505a\uff0c\u5374\u5230\u8fd9\u513f\u6765\u627e\u9b54\u5973\uff0c\u54fc\uff0c\u54fc\uff0c\u6211\u53ef\u8981\u6559\u8bad\u4f60\u4e86\uff01\u201d\u5728\u8170\u9645\u89e3\u4e0b\u4e00\u6761\u8f6f\u97ad\uff0c\u8fce\u98ce\u4e00\u6325\uff0c \u97ad\u58f0\u5237\u5237\uff0c\u968f\u624b\u4e00\u6296\uff0c\u7adf\u4f3c\u4e00\u6761\u98de\u86c7\uff0c\u5411\u5353\u4e00\u822a\u5f53\u8170\u7f20\u5230\uff01 \u3000\u3000\u539f\u6765\u8fd9\u56db\u4eba\uff0c\u4e00\u4e2a\u662f\u660c\u94a6\u5927\u6cd5\u5e08\uff0c\u4e00\u4e2a\u662f\u970d\u5143\u4ef2\uff0c\u4e00\u4e2a\u662f\u62d9\u9053\u4eba\uff0c\u8fd8\u6709\u4e00\u4e2a\u5374\u662f\u897f\u85cf\u5929 \u9f99\u6d3e\u7684\u4e4c\u5934\u957f\u8001\u3002\u660c\u94a6\u6cd5\u5e08\u5403\u4e86\u767d\u53d1\u9b54\u5973\u7684\u5927\u4e8f\u4e4b\u5f8c\uff0c\u4fbf\u9080\u4e86\u81ea\u5df2\u7684\u597d\u53cb\u4e4c\u5934\u957f\u8001\u51fa\u6765\u52a9 \u9635\u3002\u81f3\u65bc\u970d\u5143\u4ef2\u548c\u62d9\u9053\u4eba\u672c\u662f\u7ea2\u82b1\u9b3c\u6bcd\u5f53\u5e74\u7684\u654c\u4eba\uff0c\u81ea\u90a3\u6b21\u60f3\u627e\u7ea2\u82b1\u9b3c\u6bcd\u62a5\u4ec7\uff0c\u88ab\u94c1\u98de\u9f99\u548c \u7389\u7f57\u5239\u6253\u8d25\u4e4b\u5f8c\uff0c\u9000\u56de\u897f\u85cf\u9690\u5c45\u3002\u4e4c\u5934\u957f\u8001\u548c\u4ed6\u4eec\u76f8\u719f\uff0c\u56e0\u6b64\u5c06\u4ed6\u4eec\u4e5f\u9080\u51fa\u6765\u4e86\u3002 \u3000\u3000\u970d\u5143\u4ef2\u548c\u7d2b\u9633\u9053\u957f\u662f\u540c\u4e00\u8f88\u7684\u4eba\uff0c\u51e0\u5341\u5e74\u524d\u4e5f\u66fe\u89c1\u8fc7\u7d2b\u9633\u9053\u957f\u4e00\u9762\u3002\u5353\u4e00\u822a\u662f\u6b66\u5f53\u6d3e\u5f53 \u4eca\u638c\u95e8\uff0c\u6b66\u6797\u4e2d\u4eba\uff0c\u4eba\u4eba\u77e5\u9053\u3002\u970d\u5143\u4ef2\u5f53\u5e74\u8c08\u8bba\u6b66\u529f\uff0c\u53c8\u66fe\u53d7\u8fc7\u9ec4\u53f6\u9053\u4eba\u548c\u767d\u77f3\u9053\u4eba\u7684\u6c14\uff0c \u5982\u4eca\u89c1\u5353\u4e00\u822a\u4e00\u4eba\u5230\u6765\uff0c\u800c\u4e14\u53c8\u662f\u6765\u627e\u767d\u53d1\u9b54\u5973\u7684\uff0c\u970d\u5143\u4ef2\u5fc3\u5730\u72ed\u7a84\uff0c\u4e43\u7aef\u8d77\u4e86\u524d\u8f88\u7684\u8eab \u5206\uff0c\u8981\u8d76\u5353\u4e00\u822a\u4e0b\u5c71\u3002 \u3000\u3000\u5353\u4e00\u822a\u6068\u4ed6\u4eec\u5174\u7389\u7f57\u5239\u4e3a\u4ec7\uff0c\u62d4\u51fa\u5b9e\u5251\uff0c\u4e5f\u4e0d\u76f8\u8ba9\u3002\u970d\u5143\u4ef2\u6325\u97ad\u731b\u626b\uff0c\u6709\u5982\u6012\u87d2\u7ffb\u817e\uff0c \u53d8\u5316\u60ca\u4eba\u3002\u5353\u4e00\u822a\u5c55\u5f00\u6b66\u5f53\u5251\u6cd5\uff0c\u4ea6\u5982\u795e\u9f99\u592d\u77eb\uff0c\u865a\u5b9e\u83ab\u6d4b\uff0c\u970d\u5143\u4ef2\u5403\u4e86\u4e00\u60ca\uff0c\u60f3\u4e0d\u5230\u6b66\u5f53 \u7b2c\u4e8c\u4ee3\u5f1f\u5b50\uff0c\u4e5f\u5389\u5bb3\u5982\u65af\u3002\u660c\u94a6\u6cd5\u5e08\u89c1\u970d\u5143\u4ef2\u6218\u5353\u4e00\u822a\u4e0d\u4e0b\uff0c\u9887\u4e3a\u5931\u671b\uff0c\u5fc3\u9053\uff1a\u201c\u970d\u8001\u4e8c\u600e \u9ebd\u8fd9\u6837\u4e0d\u6d4e\uff01\u201d\u4e4c\u5934\u957f\u8001\u6027\u5b50\u66b4\u8e81\uff0c\u559d\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u65e2\u662f\u767d\u53d1\u9b54\u5973\u7684\u540c\u5925\uff0c\u548c\u4ed6\u5ba2\u6c14\u4f5c \u751a\uff1f\u201d\u6756\u4e00\u6446\uff0c\u4fbf\u51b2\u4e0a\u524d\u3002 \u3000\u3000\u4e4c\u5934\u957f\u8001\u529f\u529b\u6df1\u539a\uff0c\u6756\u98ce\u5f3a\u52b2\uff0c\u547c\u547c\u6570\u6756\uff0c\u5c06\u5353\u4e00\u822a\u8feb\u5f97\u8fde\u8fde\u5f8c\u9000\u3002\u6b63\u5728\u7d27\u6025\uff0c\u5ffd\u542c\u5f97 \u6709\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u4ec0\u9ebd\u4eba\u6562\u5728\u8fd9\u91cc\u62ff\u5200\u5f04\u6756\uff1f\u201d\u5353\u4e00\u822a\u8fd9\u4e00\u559c\u975e\u540c\u5c0f\u9119\uff0c\u53eb\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u7ec3 \u59d0\u59d0\uff01\u201d\u7741\u773c\u4e00\u770b\uff0c\u4e0d\u89c9\u5446\u4e86\uff0c\u9762\u524d\u7adf\u662f\u4e00\u4e2a\u9e21\u76ae\u9e64\u53d1\u7684\u8001\u5987\u4eba\u3002\u6666\u660e\u5e08\u5f53\u65e5\u53d9\u8ff0\u4e4b\u65f6\uff0c\u8bf4 \u6f0f\u4e86\u767d\u53d1\u9b54\u5973\u66fe\u6234\u9762\u5177\u4e4b\u4e8b\u3002\u5353\u4e00\u822a\u53eb\u4e86\u4e00\u58f0\uff0c\u4e0d\u6562\u518d\u53eb\u3002\u5fc3\u60f3\uff1a\u7eb5\u4ee4\u7ec3\u59d0\u59d0\u767d\u4e86\u5934\u53d1\uff0c\u4e5f \u7edd\u4e0d\u4f1a\u8001\u4e11\u5982\u65af\uff01 \u3000\u3000\u660c\u94a6\u6cd5\u5e08\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\u4eba\uff1f\u201d\u767d\u53d1\u9b54\u5973\u4e00\u8a00\u4e0d\u53d1\uff0c\u8eab\u5b50\u5e73\u7a7a\u98de\u63a0\uff0c\u5982\u602a\u9e1f\u4e00\u822c\uff0c\u5411\u4e4c \u5934\u957f\u8001\u6251\u53bb\uff0c\u957f\u5251\u4e00\u62db\u201c\u5012\u6302\u51b0\u6cb3\u201d\uff0c\u51cc\u7a7a\u4e0b\u51fb\uff0c\u4e4c\u5934\u957f\u8001\u4e24\u80a9\u4e00\u6446\uff0c\u8eab\u8eaf\u534a\u8f6c\uff0c\u6756\u5411\u5f8c\u4e00 \u626b\uff0c\u53ea\u542c\u5f97\u201c\u5237\u201d\u7684\u4e00\u58f0\uff0c\u80a9\u5934\u5df2\u4e2d\u4e86\u4e00\u5251\u3002\u660c\u94a6\u6cd5\u5e08\u4e0e\u62d9\u9053\u4eba\u6025\u62d4\u5175\u5668\u5408\u653b\uff0c\u767d\u53d1\u9b54\u5973\u51b7 \u7b11\u9053\uff1a\u201c\u970d\u5143\u4ef2\uff0e\u62d9\u9053\u4eba\uff0c\u4f60\u4eec\u4e8c\u4eba\u8fd8\u4e0d\u670d\u6c14\uff0c\u5c45\u7136\u4e5f\u5230\u8fd9\u91cc\u627e\u6b7b\u5417\uff1f\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u970d\u5143\u4ef2\u9a7e\u53eb\u9053\uff1a\u201c\u8fd9\u4eba\u4fbf\u662f\u767d\u53d1\u9b54\u5973\uff01\u201d\u5353\u4e00\u822a\u770b\u4e86\u5979\u7684\u5251\u6cd5\uff0c\u4ea6\u5df2\u77e5\u5979\u786e \u662f\u7389\u7f57\u5239\u65e0\u7591\uff0c\u8fd8\u672a\u5f00\u58f0\uff0c\u767d\u53d1\u9b54\u5973\u5df2\u662f\u5251\u8d70\u8fde\u73af\uff0c\u5bf9\u56db\u4e2a\u654c\u4eba\u75db\u4e0b\u6740\u624b\uff01 \u3000\u3000\u5353\u4e00\u822a\u542c\u5979\u9053\u51fa\u4e24\u4eba\u540d\u5b87\uff0c\u731b\u60f3\u8d77\u5e08\u7236\u5728\u65e5\uff0c\u66fe\u63d0\u8fc7\u548c\u8fd9\u4e8c\u4eba\u6709\u70b9\u4ea4\u60c5\u3002\u6025\u5fd9\u8bf4\u9053\uff1a \u201c\u7ec3\u59d0\u59d0\uff0c\u9976\u8fd9\u4e8c\u4eba\u5427\uff01\u201d\u767d\u53d1\u9b54\u5973\u4e0d\u7406\u4e0d\u776c\uff0c\u4e00\u5251\u7d27\u4f3c\u4e00\u5251\uff0c\u5353\u4e00\u822a\u597d\u751f\u6ca1\u8da3\uff0c\u53ea\u597d\u62da\u529b \u653b\u88ad\u660c\u94a6\u6cd5\u5e08\u3002\u6fc0\u6218\u4e2d\u5ffd\u542c\u5f97\u201c\u54ce\u54df\u201d\u8fde\u58f0\uff0c\u970d\u5143\u4ef2\u548c\u62d9\u9053\u4eba\u5404\u4e2d\u4e00\u5251\u3002\u767d\u53d1\u9b54\u5973\u559d\u9053\uff1a \u201c\u8fd8\u4e0d\u4e0e\u6211\u6eda\u4e0b\u5c71\u53bb\uff0c\u8fd8\u60f3\u591a\u7559\u4e24\u5904\u8bb0\u53f7\u5417\uff1f\u201d\u970d\u5143\u4ef2\u4e0e\u62d9\u9053\u4eba\u6599\u4e0d\u5230\u767d\u53d1\u9b54\u5973\u7684\u5251\u6cd5\u6bd4\u524d \u66f4\u5389\u5bb3\u8bb8\u591a\uff0c\u4e2d\u5251\u53d7\u4f24\uff0c\u9b42\u4e0d\u9644\u4f53\uff0c\u6025\u5fd9\u8df3\u51fa\u5708\u5b50\uff0c\u62b1\u5934\u4e00\u6eda\uff0c\u5728\u79ef\u96ea\u7684\u5c71\u5761\u4e0a\u76f4\u6ed1\u4e0b\u53bb\u3002 \u5353\u4e00\u822a\u5fc3\u4e2d\u6697\u559c\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u5979\u8fd8\u80af\u542c\u6211\u7684\u529d\u544a\u3002\u201d \u3000\u3000\u56db\u4e2a\u654c\u4eba\u8d70\u4e86\u4e24\u4e2a\uff0c\u53ea\u5269\u4e0b\u4e4c\u5934\u957f\u8001\u4e0e\u660c\u94a6\u6cd5\u5e08\uff0c\u66f4\u611f\u4e0d\u652f\u3002\u53c8\u6597\u4e86\u4e8c\u53c1\u5341\u62db\uff0c\u7389\u7f57\u5239 \u731b\u559d\u58f0\u201c\u7740\u201d\uff0c\u4e00\u5251\u6a2a\u62ab\uff0c\u8fc5\u5982\u63a3\u7535\uff0c\u5c06\u4e4c\u5934\u957f\u8001\u7684\u5934\u9885\u5272\u6389\uff0c\u9c9c\u8840\u6cc9\u6d8c\uff0c\u96ea\u5730\u67d3\u7ea2\u3002\u660c\u94a6 \u6cd5\u5e08\u54ac\u5b9e\u7259\u6839\uff0c\u628a\u94b9\u4e00\u63b7\uff0c\u5206\u53d6\u5353\u4e00\u822a\u548c\u767d\u53d1\u9b54\u5973\uff0c\u94dc\u94b9\u51fa\u624b\uff0c\u7acb\u5373\u4e5f\u6eda\u4e0b\u5c71\u53bb\u3002 \u3000\u3000\u5353\u4e00\u822a\u4e00\u5251\u628a\u94dc\u94b9\u78d5\u98de\uff0c\u767d\u53d1\u9b54\u5973\u51b7\u7b11\u4e00\u58f0\uff0c\u7528\u5251\u5c16\u8f7b\u8f7b\u5411\u94dc\u94b9\u4e00\u9876\uff0c\u5c06\u5b83\u53d6\u4e0b\u4e86\u6765\uff0c \u559d\u9053\uff1a\u201c\u4f60\u7684\u5175\u5668\u6211\u4e0d\u5408\u7528\uff0c\u8fd8\u7ed9\u4f60\u5427\uff01\u201d\u5c06\u94dc\u94b9\u5f80\u4e0b\u4e00\u98de\uff0c\u90a3\u94dc\u94b9\u56db\u8fb9\u950b\u5229\uff0c\u8fce\u98ce\u53d1\u51fa\u545c \u545c\u602a\u54cd\uff0c\u53bb\u52bf\u5982\u7535\uff0c\u660c\u94a6\u6cd5\u5e08\u521a\u6eda\u81f3\u534a\u5c71\uff0c\u88ab\u94dc\u94b9\u4e00\u524a\uff0c\u987f\u65f6\u8eab\u9996\u4e24\u6bb5\uff0c\u8eab\u6eda\u4e0b\u51b0\u6cb3\uff01 \u3000\u3000\u5353\u4e00\u822a\u4e0d\u6562\u4e0b\u770b\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u53ea\u89c1\u767d\u53d1\u9b54\u5973\u90a3\u5f20\u9762\u51b7\u68ee\u68ee\u7684\u6728\u65e0\u8868\u60c5\u3002\u5353\u4e00\u822a\u4e0d\u77e5\u5979\u6234 \u7684\u9762\u5177\uff0c\u4e0d\u89c9\u4e00\u9635\u5bd2\u610f\u76f4\u900f\u5fc3\u5934\uff0c\u9f13\u8d77\u52c7\u6c14\u53eb\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u7ec3\u59d0\u59d0\uff01\u201d\u767d\u53d1\u9b54\u5973\u76ef\u4ed6\u4e00 \u773c\uff0c\u5ffd\u7136\u626d\u5934\u4fbf\u8d70\u3002\u5353\u4e00\u822a\u7d27\u8ffd\u4e0d\u820d\uff0c\u72c2\u53eb\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u7ec3\u59d0\u59d0\uff01\u201d\u6309\u8bf4\u767d\u53d1\u9b54\u5973\u7684\u8f7b\u529f \u6bd4\u4ed6\u9ad8\u51fa\u4e0d\u77e5\u51e1\u51e0\uff0c\u82e5\u7136\u771f\u8dd1\uff0c\u5353\u4e00\u822a\u671b\u98ce\u4e0d\u53ca\u3002\u5979\u5374\u6545\u610f\u653e\u6162\u811a\u6b65\uff0c\u603b\u4fdd\u6301\u7740\u4e8c\u53c1\u5341\u6b65\u7684 \u8ddd\u7bf1\u3002\u5230\u4e86\u4e00\u5904\u5cf0\u5934\uff0c\u5ffd\u7136\u7ad9\u7740\u3002\u56de\u5934\u51dd\u671b\u3002\u6b63\u662f\uff1a\u51e0\u756a\u79bb\u5408\u6210\u8fdf\u66ae\uff0c\u9053\u662f\u65e0\u60c5\u5374\u6709\u60c5\u3002\u6b32 \u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377\u2014\u2014\u7b2c\u4e8c\u5341\u4e5d\u56de\u3000\u7a7a\u8c37\u4f20\u58f0\u3000\u4f0a\u4eba\u4ecd\u4e0d\u89c1\u3000\u8352\u5c71\u9732\u8ff9\u3000\u5978\u515a\u6709\u9634\u8c0b \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377 \u7b2c\u4e8c\u5341\u4e5d\u56de \u7a7a\u8c37\u4f20\u58f0\u3000\u4f0a\u4eba\u4ecd\u4e0d\u89c1 \u8352\u5c71\u9732\u8ff9\u3000\u5978\u515a\u6709\u9634\u8c0b \u3000\u3000\u53ea\u89c1\u5979\u90a3\u53cc\u773c\u73e0\u7741\u5706\u6e9c\u4eae\uff0c\u987e\u76fc\u4e4b\u95f4\uff0c\u5149\u5f69\u7167\u4eba\uff0c\u5c31\u5982\u5728\u4e00\u5f20\u6781\u7c97\u7cd9\u96be\u770b\u7684\u7f8a\u76ae\u4e0a\uff0c\u5d4c \u7740\u4e24\u9897\u5149\u8292\u95ea\u95ea\u7684\u5b9d\u77f3\u3002\u5353\u4e00\u822a\u5fc3\u4e2d\u4e00\u9178\uff1a\u9664\u4e86\u8fd9\u6d41\u6ce2\u5b9b\u8f6c\u7684\u53cc\u773c\uff0c\u8fd8\u662f\u7389\u7f57\u5239\u7684\u5f53\u5e74\u7684\u98ce \u97f5\u4e4b\u5916\uff0c\u5728\u9762\u524d\u8fd9\u9e21\u76ae\u9e64\u53d1\u7684\u8001\u5987\u4eba\u8eab\u4e0a\uff0c\u90a3\u8fd8\u80fd\u627e\u51fa\u5979\u90a3\u4e9b\u5f71\u5b50\uff1f\u5353\u4e00\u822a\u4e0d\u5982\u5979\u6234\u7684\u9762 \u5177\uff0c\u51e0\u4e4e\u7591\u5fc3\u662f\u5728\u6076\u68a6\u4e4b\u4e2d\uff0c\u5c82\u6709\u7edd\u4e16\u5bb9\u989c\u7684\u5c11\u5973\u4f1a\u8001\u4e11\u5982\u65af\uff1f \u3000\u3000\u5353\u4e00\u822a\u4e0d\u89c9\u6ef4\u4e0b\u6cea\u6765\uff0c\u6251\u4e0a\u524d\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff01\u201d\u767d\u53d1\u9b54\u5973\u8f7b\u8f7b\u4e00\u95ea\uff0c\u5353\u4e00\u822a\u6251\u4e86 \u4e2a\u7a7a\uff0c\u51e0\u4e4e\u6ed1\u5012\uff0c\u53ea\u542c\u5f97\u767d\u53d1\u9b54\u5973\u51b7\u7136\u7b11\u9053\uff1a\u201c\u8c01\u662f\u4f60\u7684\u7ec3\u59d0\u59d0\uff1f\u4f60\u8ba4\u9519\u4eba\u5566\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u6211\u627e\u4e86\u4f60\u4e24\u5e74\u591a\u5566\uff01\u201d\u767d\u53d1\u9b54\u652f\u9053\uff1a\u201c\u4f60\u627e\u5979\u505a\u4ec0\u9ebd\uff1f\u201d\u5353\u4e00\u822a \u9053\uff1a\u201c\u6211\u77e5\u9053\u9519\u5566\uff0c\u800c\u4eca\u6211\u5df2\u629b\u4e86\u638c\u95e8\uff0c\u4f46\u613f\u548c\u4f60\u4e00\u8d77\uff0c\u5730\u4e45\u5929\u957f\uff0c\u54b1\u4eec\u518d\u4e5f\u4e0d\u5206\u79bb\u4e86\u3002\u201d \u767d\u53d1\u9b54\u5973\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u8981\u548c\u6211\u5728\u4e00\u8d77\uff01\u54c8\u54c8\uff0c\u6211\u8fd9\u4e2a\u8001\u592a\u5a46\u884c\u5c06\u5c31\u6728\uff0c\u8fd8\u8bf4\u4ec0\u9ebd\u5730\u4e45\u5929 \u957f\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u53c8\u6251\u4e0a\u524d\u53bb\uff0c\u54fd\u54bd\u9053\uff1a\u201c\u90fd\u662f\u6211\u7d2f\u4e86\u4f60\uff01\u201d\u767d\u53d1\u9b54\u5973\u53c8\u662f\u4e00\u95ea\u95ea\u5f00\uff0c\u4ecd\u51b7\u7b11\u9053\uff1a \u201c\u4f60\u7684\u7ec3\u59d0\u59d0\u65e9\u5df2\u6b7b\u5566\uff0c\u4f60\u5c3d\u5411\u6211\u5520\u53e8\u505a\u751a\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u4e0d\u8ba4\u6211\u6211\u4e5f\u8981\u50cf\u5f71\u5b50\u4e00\u6837\u8ffd \u968f\u4f60\uff0c\u4e0d\u7ba1\u4f60\u53d8\u5f97\u5982\u4f55\uff1f\u6211\u7684\u5fc3\u4ecd\u7136\u4e0d\u53d8\uff01\u201d\u767d\u53d1\u9b54\u5973\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\uff0c\u51b7\u68ee\u68ee\u7684\u201c\u9762\u5b54\u201d\u7a81 \u7136\u5411\u5353\u4e00\u822a\u8feb\u89c6\uff0c\u9053\uff1a\u201c\u771f\u7684\uff1f\u4f60\u77a7\u6e05\u695a\u6ca1\u6709\uff1f\u4f60\u7684\u7ec3\u59d0\u59d0\u662f\u8fd9\u4e2a\u6837\u513f\u5417\uff1f\u201d\u5353\u4e00\u822a\u51e0\u66fe\u89c1 \u8fc7\u8fd9\u6837\u795e\u60c5\uff0c\u4e0d\u89c9\u6253\u4e86\u4e2a\u5bd2\u98a4\uff0c\u4f46\u77ac\u606f\u4e4b\u95f4\uff0c\u53c8\u518d\u9f13\u8d77\u52c7\u6c14\uff0c\u4f38\u624b\u53bb\u62c9\u767d\u53d1\u9b54\u5973\uff0c\u6717\u58f0\u8bf4 \u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u4f60\u70e7\u53d8\u4e86\u7070\u6211\u4e5f\u8ba4\u5f97\u4f60\u3002\u5728\u6211\u773c\u4e2d\uff0c\u4f60\u8fd8\u662f\u548c\u5f53\u5e74\u4e00\u6a21\u4e00\u6837\u5566\uff01\u201d \u3000\u3000\u767d\u53d1\u9b54\u5973\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\uff0c\u4e00\u6454\u6454\u8131\u4e86\u5353\u4e00\u822a\u7684\u624b\uff0c\u9053\uff1a\u201c\u4f60\u53bb\u627e\u4f60\u5f53\u5e74\u7684\u7ec3\u59d0\u59d0\u5427\u3002\u53bb \u5440\uff0c\u4f60\u4e3a\u4ec0\u9ebd\u4e0d\u53bb\u5440\uff1f\u201d\u5353\u4e00\u822a\u5ffd\u7136\u5982\u6709\u6240\u609f\uff0c\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u6211\u8bf4\u8fc7\u7684\u8bdd\u7edd\u4e0d\u4f1a\u5fd8\u8bb0\uff0c\u6211 \u4e00\u5b9a\u8981\u4e3a\u4f60\u627e\u5bfb\u7075\u4e39\u5999\u836f\uff0c\u4ee4\u4f60\u6062\u590d\u9752\u6625\u3002\u201d\u767d\u53d1\u9b54\u5973\u9063\uff1a\u201c\u90a3\u662f\u4f60\u7684\u4e8b\u60c5\uff0c\u6211\u4e0d\u7ba1\u4f60\u3002\u4f60 \u662f\u4f60\uff0c\u6211\u662f\u6211\uff0c\u54b1\u4eec\u5f7c\u6b64\u65e0\u6d89\u3002\u4f11\u8bf4\u6211\u4e0d\u662f\u4f60\u7684\u7ec3\u59d0\u59d0\uff0c\u5c31\u7b97\u662f\u5979\uff0c\u4e5f\u7b49\u65bc\u6b7b\u8fc7\u4e86\u4e00\u6b21\uff0c\u8fd8 \u63d0\u90a3\u4e9b\u65e7\u4e8b\u5e72\u561b\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u4e00\u542c\uff0c\u5979\u53e3\u6c14\u867d\u7136\u4e25\u5cfb\uff0c\u4f46\u5df3\u4f3c\u7a0d\u6709\u8f6c\u673a\uff0c\u4fbf\u9053\uff1a\u201c\u6211\u77e5\u9053\u8fd9\u8349\u539f\u4e0a\u6709\u4e00\u79cd\u4ed9 \u82b1\uff0c\u53ef\u4ee4\u4eba\u767d\u53d1\u53d8\u9ed1\uff0c\u8fd4\u8001\u8fd8\u7ae5\uff0c\u54b1\u4eec\u540c\u53bb\u627e\u5427\uff01\u201d\u767d\u53d1\u9b54\u5973\u5ffd\u53c8\u51b7\u7b11\u9053\uff1a\u201c\u6211\u53ef\u6ca1\u6709\u8fd9\u6837 \u529f\u592b\u3002\u4f60\u5bf9\u81ed\u76ae\u56ca\u65e2\u7136\u5982\u6b64\u770b\u91cd\uff0c\u4f60\u81ea\u5df1\u53bb\u627e\uff0c\u4e16\u95f4\u5c3d\u6709\u5982\u82b1\u7f8e\u5973\uff0c\u4e0e\u4f60\u4e00\u540c\u4eab\u7528\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u90a3\u77e5\u767d\u53d1\u9b54\u5973\u5fc3\u60c5\u77db\u76fe\u975e\u5e38\uff0c\u5979\u65e2\u60cb\u60dc\u81ea\u5df1\u7684\u5bb9\u989c\uff0c\u4f46\u53c8\u4e0d\u613f\u6240\u7231\u4e4b\u4eba\u63d0\u8d77\u3002\u5353 \u4e00\u822a\u518d\u6251\u524d\u4e24\u6b65\uff0c\u60f6\u6025\u8bf4\u9053\uff1a\u201c\u4e0d\uff0c\u4e0d\uff01\u7ec3\u59d0\u59d0\uff0c\u6211\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u2026\u2026\u201d\u767d\u53d1\u9b54\u5973\u4e0d\u5f85\u4ed6 \u8bf4\u5b8c\uff0c\u5ffd\u7136\u8f6c\u8eab\u53c8\u8d70\u3002\u5353\u4e00\u822a\u53eb\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u7ec3\u59d0\u59d0\u4f60\u4e0d\u80fd\u8fd9\u6837\u8d70\u5440\uff01\u4f60\u53ef\u601c\u6211\u5386\u5c3d\u4e07\u6c34 \u5343\u5c71\uff0c\u98ce\u971c\u96e8\u96ea\uff0c\u624d\u627e\u5f97\u89c1\u4f60\u5440\uff01\u201d\u767d\u53d1\u9b54\u5973\u500f\u7136\u51dd\u6b65\uff0c\u53c8\u53d1\u51fa\u4e00\u9635\u51b7\u7b11\u3002 \u3000\u3000\u53ea\u542c\u5f97\u767d\u53d1\u9b54\u5973\u9053\uff1a\u201c\u662f\u5440\uff0c\u4f60\u4e43\u8d35\u5bb6\u516c\u5b50\uff0c\u4e00\u6d3e\u638c\u95e8\uff0c\u7adf\u7136\u80af\u53d7\u8fd9\u585e\u5916\u98ce\u971c\u4e4b\u82e6\uff0c\u4f60 \u90a3\u4f4d\u7ec3\u59d0\u59d0\u5e94\u8be5\u611f\u6fc0\u4e0d\u5c3d\u4e86\uff01\u201d\u8bed\u5b58\u8ba5\u8bee\uff0c\u610f\u601d\u662f\u8bf4\uff1a\u8fd9\u53c8\u6709\u4ec0\u9ebd\u8db3\u4ee5\u79f0\u529f\u9053\u52b3\uff0c\u503c\u5f97\u6302\u5728 \u53e3\u8fb9\uff1f\u5353\u4e00\u822a\u4e0d\u89c9\u4e00\u695e\uff0c\u6025\u5207\u95f4\u65e0\u8f9e\u81ea\u8fa9\u3002\u51b7\u7b11\u58f0\u4e2d\uff0c\u767d\u53d1\u9b54\u5973\u5728\u5c71\u5cf0\u4e0a\u4e00\u8dc3\u800c\u4e0b\uff0c\u5353\u4e00\u822a \u60ca\u53eb\u4e00\u58f0\uff0c\u4f46\u89c1\u8863\u8882\u98d8\u626c\uff0c\u4e00\u56e2\u767d\u5f71\uff0c\u968f\u98ce\u800c\u901d\u3002\u767d\u53d1\u9b54\u5973\u5df2\u8fd0\u7edd\u9876\u8f7b\u529f\u8d70\u4e86\u3002 \u3000\u3000\u7b11\u58f0\u5df2\u5bc2\uff0c\u4eba\u5f71\u65e0\u8e2a\u3002\u5353\u4e00\u822a\u9762\u4e34\u767e\u4e08\u5371\u5d16\uff0c\u9893\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u5148\u662f\u6028\u6068\uff0c\u7ee7\u800c\u81ea\u8d23\u3002\u4ed6 \u672c\u4ee5\u4e3a\u81ea\u5df1\u4e00\u7247\u81f3\u8bda\uff0c\u5f53\u80fd\u4ee4\u7389\u7f57\u5239\u611f\u52a8\uff1a\u800c\u4eca\u7ec6\u60f3\uff0c\u4ee5\u524d\u79cd\u79cd\uff0c\u5b9e\u5728\u662f\u6709\u8d1f\u65bc\u7389\u7f57\u5239\u8005 \u591a\uff0c\u800c\u8db3\u4ee5\u8868\u793a\u8bda\u5fc3\u8005\u5c11\u3002\u7231\u81f3\u6df1\u65f6\uff0c\u4e00\u5207\u51fa\u65bc\u81ea\u7136\uff0c\u4e0d\u5f85\u8a00\u8bf4\u3002\u8fdc\u6765\u585e\u5916\uff0c\u98ce\u96ea\u76f8\u4fb5\uff0c\u4e43 \u662f\u4efd\u6240\u5e94\u5f53\u4e4b\u4e8b\uff0c\u771f\u662f\u4f55\u8db3\u9053\u54c9\uff01\u5982\u6b64\u4e00\u60f3\uff0c\u5353\u4e00\u822a\u5012\u89c9\u5f97\u81ea\u5df1\u5bf9\u65bc\u7231\u7684\u4f53\u4f1a\uff0c\u5c1a\u672a\u591f\u6df1 \u4e86\uff01 \u3000\u3000\u8fd9\u6837\u75f4\u75f4\u7684\u60f3\u4e86\u4e00\u5929\uff0c\u5353\u4e00\u822a\u5ffd\u7136\u5982\u6709\u6240\u609f\uff0c\u77e5\u9053\u518d\u5bfb\u7389\u7f57\u5239\uff0c\u7389\u7f57\u5239\u4e5f\u4e0d\u4f1a\u89c1\u4ed6\u4e86\u3002 \u4fbf\u79bb\u5f00\u4e86\u5929\u5c71\u5357\u9ad8\u5cf0\uff0c\u53c8\u5230\u5317\u9ad8\u5cf0\u53bb\u89c1\u6666\u660e\u5e08\u3002\u5288\u5934\u4fbf\u95ee\u9053\uff1a\u201c\u5f31\u6c34\u53c1\u5343\uff0c\u6211\u5982\u4f55\u660e\u4e00\u74e2\u800c \u6e21\uff1f\u201d\u6666\u660e \u3000\u3000\u5e08\u5408\u4ec0\u7b54\u9053\uff1a\u201c\u672c\u6765\u65e0\u5f31\u6c34\uff0c\u4f55\u5fc5\u95ee\u6d6e\u6c89\uff1f\u201d\u5353\u4e00\u822a\u53c8\u95ee\u9053\uff1a\u201c\u5047\u5982\u897f\u5929\u8def\u4e0a\u672c\u6765\u6ca1\u6709 \u96f7\u97f3\u5bfa\uff0c\u5510\u53c1\u85cf\u600e\u6837\u53d6\u788d\uff1f\u5047\u5982\u6709\u96f7\u97f3\u5bfa\uff0c\u6c38\u884c\u4e0d\u5230\u53c8\u6709\u4f55\u6cd5\uff1f\u201d\u6666\u660e\u5e08\u9053\uff1a\u201c\u5510\u53c1\u85cf\u5c82\u662f \u4e3a\u60f3\u6210\u4f5b\u800c\u53d6\u7ecf\uff1f\u897f\u5929\u8def\u4e0a\u6709\u6ca1\u6709\u96f7\u97f3\u5bfa\u53c8\u6709\u4f55\u5173\u7cfb\uff1f\u4f46\u6c42\u4e00\u5fc3\u7688\u4f9d\uff0c\u90a3\u8ba1\u8def\u7a0b\u957f\u77ed\uff1f\u201d\u5353 \u4e00\u822a\u6df1\u6df1\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u656c\u53d7\u6559\u4e86\uff01\u201d\u5306\u5306\u51fa\u95e8\uff0c\u4fbf\u4e0d\u518d\u53d9\u3002\u6666\u660e\u5e08\u4e5f\u4e0d\u633d\u7559\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u7ee7 \u800c\u53c8\u53f9\u4e86\u53e3\u6c14\u3002 \u3000\u3000\u8fd9\u4e00\u756a\u673a\u5bf9\u7b54\uff0c\u5176\u5b9e\u4e43\u662f\u5353\u4e00\u822a\u4e3a\u7389\u7f57\u5239\u4e4b\u4e8b\u800c\u8bf7\u6559\u6666\u660e\u5e08\u3002\u4ed6\u628a\u201c\u7231\u6cb3\u201d\u6bd4\u5982\u201c\u5f31 \u6c34\u201d\uff0c\u201c\u5f31\u6c34\u201d\u6709\u7269\u5373\u6c89\uff0c\u95ee\u6666\u660e\u5e08\u5982\u4f55\u53ef\u4ee5\u98de\u6e21\uff1f\u6666\u660e\u5e08\u529d\u4ed6\u4e0d\u5fc5\u5148\u95ee\u6d6e\u6c89\uff0c\u5f31\u6c34\u672c\u5c31\u65e0 \u6709\u3002\u5353\u4e00\u822a\u53c8\u6015\u81ea\u5df2\u867d\u7136\u5c3d\u529b\u800c\u4e3a\uff0c\u4f46\u82e5\u4ecd\u4e0d\u4e3a\u7389\u7f57\u5239\u6240\u8c05\uff0c\u6216\u5230\u7389\u7f57\u5239\u80fd\u8c05\u89e3\u65f6\uff0c\u5c81\u6708\u5df3 \u865a\u5ea6\u4e86\uff0c\u5374\u53c8\u5982\u4f55\u4e86\u56e0\u6b64\u4e00\u95ee\uff0c\u4e43\u6709\u201c\u5510\u53c1\u85cf\u53d6\u788d\u201d\u7684\u6bd4\u55bb\u3002 \u3000\u3000\u5353\u4e00\u822a\u62dc\u522b\u4e0b\u5c71\uff0c\u60f3\u9053\uff1a\u662f\u554a\uff0c\u53ea\u8981\u6211\u77e2\u5fd7\u4e0d\u6e1d\uff0c\u6b64\u5fc3\u7ec8\u6709\u4e3a\u7ec3\u59d0\u59d0\u8c05\u89e3\u4e4b\u65e5\u3002\u4e5f\u8bb8\u5979 \u8fd9\u756a\u505a\u4f5c\uff0c\u5c31\u662f\u6545\u610f\u7684\u5bf9\u6211\u8003\u9a8c\u6298\u78e8\u3002\u5f92\u7136\u53c8\u60f3\u8d77\u4e86\u90a3\u4f20\u8bf4\u4e2d\u7684\u4f18\u6619\u4ed9\u82b1\uff0c\u5fc3\u9053\uff1a\u6211\u62da\u7740\u518d \u53d7\u5341\u5e74\u96e8\u96ea\u98ce\u971c\uff0c\u4e5f\u8981\u91c7\u5230\u6b64\u82b1\uff0c\u8ba9\u7ec3\u59d0\u59d0\u660e\u767d\u6211\u7684\u7231\u5ff5\u3002 \u3000\u3000\u81ea\u6b64\uff0c\u5353\u4e00\u822a\u5728\u5927\u8349\u539f\u4e0a\u6f2b\u6e38\uff0c\u8d70\u904d\u5929\u5c71\u5357\u5317\uff0c\u4e0d\u89c9\u53c8\u5306\u5306\u8fc7\u4e86\u53c1\u4e2a\u5bd2\u6691\u3002\u4f46\u90a3\u4f20\u8bf4\u4e2d \u7684\u4ed9\u82b1\uff0c\u5374\u59cb\u7ec8\u65e0\u6cd5\u5bfb\u89c5\u3002 \u3000\u3000\u4e00\u65e5\uff0c\u5353\u4e00\u822a\u6df1\u4eba\u5929\u5c71\u4ee5\u5317\uff0c\u88ab\u4e00\u5ea7\u767d\u96ea\u7691\u7691\u7684\u5c71\u5cf0\u6240\u5438\u5f15\u3002\u8fd9\u5ea7\u5c71\u5cf0\u597d\u50cf\u4e00\u5934\u9a86\u9a7c\uff0c \u5934\u4e1c\u5c3e\u897f\uff0c\u62ab\u7740\u6ee1\u8eab\u767d\u8272\u7684\u7ed2\u6bdb\u3002\u5353\u4e00\u822a\u8d70\u81f3\u5c71\u4e0b\uff0c\u5ffd\u89c1\u5c71\u5761\u4e0a\u6709\u4e00\u95ee\u77f3\u5c4b\uff0c\u5929\u5c71\u811a\u4e0b\uff0c\u6709 \u7267\u6c11\u672c\u4e0d\u51fa\u5947\uff0c\u4f46\u5728\u79ef\u96ea\u8986\u76d6\u7684\u5c71\u5761\uff0c\u5374\u6709\u4eba\u79bb\u7fa4\u72ec\u5c45\uff0c\u5374\u662f\u602a\u4e8b\u3002\u597d\u5947\u5fc3\u8d77\uff0c\u9042\u6500\u767b\u4e0a \u53bb\u3002 \u3000\u3000\u8fd9\u51e0\u5e74\u6765\u8f66\u4e00\u822a\u53d7\u4e86\u8bb8\u591a\u78e8\u7ec3\uff0c\u975e\u4f46\u6b66\u529f\u5927\u8fdb\uff0c\u800c\u4e14\u8fdc\u6bd4\u4ee5\u524d\u523b\u82e6\u8010\u52b3\uff0c\u6500\u767b\u9ad8\u5c71\uff0c\u4ea6 \u5982\u5c65\u5e73\u5730\u3002\u4e0d\u4e00\u523b\u4fbf\u6500\u4e0a\u4e86\u5c71\u8170\uff0c\u77f3\u5c4b\u524d\u9762\uff0c\u6b63\u6709\u7740\u51e0\u4e2a\u4eba\u5728\u9ad8\u58f0\u8bf4\u8bdd\u3002 \u3000\u3000\u5353\u4e00\u822a\u9690\u5728\u5ca9\u77f3\u5f8c\u9762\u5411\u5916\u671b\u53bb\uff0c\u53ea\u89c1\u4e24\u4e2a\u5587\u561b\uff0c\u4e00\u8001\u4e00\u5c11\uff0c\u6b63\u5728\u5927\u58f0\u547c\u559d\u3002\u5bf9\u65b9\u5374\u662f\u4e00 \u4e2a\u54c8\u8428\u514b\u65cf\u6253\u626e\u7684\u5c71\u6c11\uff0c\u5e26\u7740\u4e00\u4e2a\u5341\u4e8c\u53c1\u5c81\u7684\u5c0f\u5b69\u5b50\uff0c\u90a3\u5c0f\u5b69\u5b50\u597d\u4f3c\u7626\u76ae\u7334\u4e00\u9ab0\uff0c\u4f46\u4e24\u53ea\u773c \u775b\uff0c\u5374\u751f\u5f97\u53c8\u5706\u53c8\u5927\uff0c\uff0e\u5955\u5955\u6709\u795e\u3002 \u3000\u3000\u90a3\u5e74\u8001\u7684\u5587\u561b\u559d\u9053\uff1a\u201c\u8f9b\u8001\u4e94\uff0c\u4f60\u5e94\u8be5\u4ea4\u7684\u96ea\u83b2\u65e2\u6ca1\u6709\uff0c\u7280\u89d2\u53c8\u4e0d\u591f\uff0c\u8fd9\u662f\u600e\u9ebd\u8bf4\u7684\uff1f \u53eb\u4ffa\u5982\u4f55\u5411\u738b\u7237\u4ea4\u4ee3\uff1f\u201d\u90a3\u5e74\u957f\u7684\u54c8\u8428\u514b\u5c71\u6c11\u54c0\u6c42\u9053\uff1a\u201c\u4eca\u5e74\u4ec5\u627e\u5230\u51e0\u6735\u96ea\u83b2\uff0c\u90fd\u914d\u4e86\u836f\u5356 \u7ed9\u6536\u836f\u6750\u7684\u5546\u4eba\u4e86\uff0c\u7280\u89d2\u4e5f\u53ea\u6709\u4e00\u6839\uff0c\u5927\u5e08\u7236\u4f60\u591a\u62c5\u5f85\u3002\u201d \u3000\u3000\u90a3\u5e74\u8001\u7684\u5587\u561b\u540d\u53eb\u5929\u5fb7\u4e0a\u4eba\uff0c\u4e43\u662f\u897f\u85cf\u5929\u9f99\u6d3e\u7684\u957f\u8001\u4e4b\u4e00\uff0c\u4ed6\u53d7\u54c8\u8428\u514b\u65cf\u7684\u914b\u957f\u8058\u4e3a\u62a4 \u6cd5\u5e08\uff0c\u90a3\u5e74\u8f7b\u7684\u5587\u561b\u662f\u4ed6\u7684\u5f92\u5f1f\u3002\u5565\u8428\u514b\u65cf\u662f\u8349\u539f\u4e0a\u4e00\u4e2a\u6e38\u7267\u6c11\u65cf\uff0c\u65cf\u4eba\u90fd\u6709\u5411\u914b\u957f\u7f34\u4ea4\u8d21 \u7269\u7684\u4e49\u52a1\u3002\u662f\u7267\u6c11\u5c31\u8981\u7f34\u7eb3\u725b\u7f8a\uff0c\u662f\u5c71\u6c11\u730e\u6237\u5c31\u8981\u7f34\u7eb3\u836f\u6750\u548c\u91ce\u5473\u3002\u54c8\u8428\u514b\u65cf\u4eba\u5341\u4e5d\u90fd\u662f\u5728 \u8349\u539f\u4e0a\u755c\u7267\u725b\u7f8a\uff0c\u5c71\u6c11\u730e\u6237\u4ea6\u6709\u767e\u5341\u6765\u5bb6\u3002\u6563\u5c45\u5728\u5929\u5c71\u5c71\u8109\u4e4b\u4e2d\uff0c\u5fb5\u6536\u4e0d\u4fbf\u3002\u5929\u5fb7\u4e0a\u4eba\u522b\u6709 \u7528\u5fc3\uff0c\u81ea\u544a\u594b\u52c7\uff0c\u6bcf\u5e74\u90fd\u66ff\u914b\u957f\u53bb\u5fb5\u6536\u5c71\u6c11\u730e\u6237\u7684\u8d21\u7269\uff0c\u7528\u610f\u5374\u5728\u91c7\u96c6\u5929\u5c71\u540d\u8d35\u7684\u836f\u6750\uff0c\u4ece \u4e2d\u4e2d\u9971\u3002\u4f8b\u5982\u914b\u957f\u8981\u67d0\u5bb6\u4e00\u6839\u7280\u89d2\u7684\uff0c\u4ed6\u5374\u8981\u4e24\u6839\uff0c\u8981\u4e24\u6735\u96ea\u83b2\u7684\uff0c\u4ed6\u5374\u8981\u56db\u6735\uff0c\u5c71\u6c11\u4eec\u65e2 \u65e0\u6cd5\u53bb\u89c1\u914b\u957f\u6c42\u60c5\uff0c\u8981\u53cd\u6297\u53c8\u654c\u4e0d\u8fc7\u4ed6\u4eec\uff0c\u597d\u4efb\u7531\u4ed6\u4eec\u5265\u524a\u3002 \u3000\u3000\u90a3\u8f9b\u6b63\u662f\u54c8\u8428\u514b\u65cf\u6709\u540d\u7684\u730e\u6237\uff0c\u88ab\u8feb\u5f97\u65e0\u6cd5\uff0c\u5411\u4ed6\u6c42\u9976\u3002\u5929\u5fb7\u4e0a\u4eba\u7ffb\u8d77\u4e00\u53cc\u602a\u773c\uff0c\u51b7\u7b11 \u9053\uff1a\u201c\u5356\u7ed9\u6536\u836f\u6750\u7684\u5546\u4eba\uff1f\u54fc\uff0c\u4f60\u597d\u5927\u80c6\uff01\u4e0d\u7f34\u7ed9\u738b\u7237\u5148\u5356\u4e86\uff01\u201d\u8f9b\u6b63\u9053\uff1a\u201c\u4e0d\u5356\u6211\u4eec\u5403\u4ec0 \u9ebd\uff1f\u96ea\u83b2\u53c8\u4e0d\u80fd\u5145\u9965\u3002\u6211\u4eec\u7684\u738b\u7237\u5bf9\u5f85\u65cf\u4eba\u4e00\u5411\u4e0d\u9519\uff0c\u4ee5\u524d\u82e5\u91c7\u4e0d\u5230\u96ea\u83b2\uff0c\u4e24\u53c1\u5e74\u4e0d\u4ea4\uff0c\u4ed6 \u4e5f\u4e0d\u4f1a\u6d3e\u4eba\u6765\u8ba8\uff0c\u5927\u5e08\uff0c\u4f60\u540c\u4ed6\u8bf4\u8bf4\u6211\u4eec\u7684\u82e6\u51b5\uff0c\u738b\u7237\u4e00\u5b9a\u80fd\u591f\u539f\u8c05\u7684\u3002\u201d\u5929\u5fb7\u4e0a\u4eba\u52c3\u7136\u53d8 \u8272\uff0c\u65a5\u9053\uff1a\u201c\u738b\u7237\u597d\u5fc3\u80a0\uff0c\u4f60\u4eec\u5c31\u5201\u987d\u4e86\uff0c\u738b\u7237\u80fd\u539f\u8c05\u4f60\u4eec\uff0c\u6211\u4e0d\u80fd\u539f\u8c05\uff01\u4f60\u7ed9\u4e0d\u7ed9\uff1f\u4e0d\u7ed9 \u5c31\u628a\u4f60\u6293\u53bb\uff01\u201d\u90a3\u5e74\u8f7b\u7684\u5587\u561b\u4e0d\u5f85\u5e08\u7236\u5429\u5490\uff0c\u7acb\u523b\u4fbf\u5954\u4e0a\u524d\u52a8\u624b\u3002\u8f9b\u4e94\u8fde\u8fde\u5f8c\u9000\uff0c\u4e0d\u65ad\u6c42 \u9976\uff0c\u770b\u770b\u5c31\u8981\u88ab\u90a3\u5587\u561b\u6293\u7740\u3002 \u3000\u3000\u6b63\u5728\u5371\u6025\u4e4b\u9645\uff0c\u90a3\u5c0f\u5b69\u5b50\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u4e9b\u5f3a\u76d7\uff0c\u770b\u4f60\u6562\u6b3a\u8d1f\u6211\u7239\u7239\uff01\u201d\u731b\u7136\u5f2f\u4e0b \u8eab\u8eaf\uff0c\u53cc\u8db3\u4e00\u8dc3\uff0c\u5411\u524d\u4e00\u51b2\uff0c\u90a3\u5e74\u8f7b\u7684\u5587\u561b\u6beb\u4e0d\u5728\u610f\uff0c\u88ab\u90a3\u5b69\u5b50\u4e00\u5934\u649e\u6b63\u5c0f\u8179\u4e39\u7530\u4e4b\u5904\uff0c\u5495 \u549a\u4e00\u58f0\uff0c\u987f\u65f6\u5012\u5730\uff01 \u3000\u3000\u5929\u5fb7\u4e0a\u5165\u5fae\u5fae\u4e00\u6115\uff0c\u90a3\u5c0f\u5b69\u5b50\u649e\u5012\u4e00\u4eba\uff0c\u5fc3\u6c14\u66f4\u58ee\uff0c\u4f9d\u6cd5\u70ae\u5236\uff0c\u53c8\u5411\u5929\u5fb7\u4e0a\u4eba\u649e\u6765\uff0c\u5929 \u5fb7\u4e0a\u4eba\u8f7b\u8f7b\u4e00\u95ea\uff0c\u90a3\u5c0f\u5b69\u5b50\u4e00\u5934\u649e\u5728\u4e00\u68f5\u6811\u4e0a\uff0c\u6811\u5e72\u6447\u52a8\uff0c\u5c0f\u5b69\u5b50\u7adf\u7136\u6beb\u4e0d\u53eb\u75db\u3002\u5353\u4e00\u822a\u770b \u5f97\u5927\u4e3a\u60ca\u5947\uff0c\u6599\u4e0d\u5230\u5c0f\u5b69\u5b50\u7adf\u662f\u5929\u751f\u795e\u529b\uff1f \u3000\u3000\u5929\u5fb7\u4e0a\u4eba\u54c8\u54c8\u4e00\u7b11\uff0c\u4e00\u628a\u634f\u7740\u4e86\u90a3\u7626\u5c0f\u5b69\u5b50\u7684\u624b\u81c2\uff0c\u5929\u5fb7\u4e0a\u4eba\u662f\u4e00\u6d3e\u957f\u8001\uff0c\u6b66\u529f\u81ea\u662f\u975e \u540c\u5c0f\u9119\uff0c\u90a3\u5c0f\u5b69\u5b50\u867d\u662f\u5929\u751f\u795e\u529b\uff0c\u5374\u53c8\u52a8\u5f39\u4e0d\u5f97\u3002\u8f9b\u4e94\u53eb\u9053\uff1a\u201c\u5927\u6cd5\u5e08\uff0c\u4ed6\u5c0f\u5b69\u5b50\u4e0d\u61c2\u4e8b\uff0c \u4f60\u8001\u9976\u4e86\u4ed6\u5427\uff0c\u6211\u5192\u9669\u7ed9\u4f60\u627e\u96ea\u83b2\u4fbf\u662f\u3002\u201d \u3000\u3000\u5929\u5fb7\u4e0a\u4eba\u7b11\u9053\uff1a\u201c\u8f9b\u8001\u4e94\uff0c\u7b97\u4f60\u9020\u5316\uff0c\u6709\u8fd9\u9ebd\u4e00\u4e2a\u597d\u513f\u5b50\uff01\u6211\u975e\u4f46\u4e0d\u6742\u4e3a\u4ed6\uff0c\u8fde\u4f60\u7684\u8d21 \u7269\uff0c\u6211\u4e5f\u90fd\u8c41\u514d\u4e86\u3002\u201d\u8f9b\u4e94\u5927\u559c\uff0c\u6b63\u8981\u9053\u8c22\uff0c\u5929\u5fb7\u4e0a\u4eba\u5ffd\u9053\uff1a\u201c\u4e14\u6162\u3002\u4f60\u513f\u5b50\u867d\u7136\u5929\u751f\u6709\u51e0 \u65a4\u86ee\u529b\uff0c\u4e0d\u5f97\u540d\u5e08\u6307\u70b9\uff0c\u5c06\u6765\u4e5f\u4e0d\u8fc7\u4e00\u6761\u86ee\u725b\u7f62\u4e86\uff0c\u6709\u4ec0\u9ebd\u7528\uff1f\u201d \u3000\u3000\u8f9b\u4e94\u4e00\u542c\uff0c\u77e5\u4ed6\u7528\u610f\uff0c\u5374\u4e0d\u4f5c\u58f0\u3002\u5929\u5fb7\u4e0a\u4eba\u624b\u6307\u4e00\u677e\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u5c0f\u5a03\u513f\u77a7\u7740\uff01\u201d \u5ffd\u5730\u4e00\u638c\u5288\u51fa\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u5c06\u90a3\u68f5\u5927\u6811\u5288\u5012\uff0c\u80dc\u65bc\u5200\u65a7\u3002\u9053\uff1a\u201c\u600e\u9ebd\u6837\uff1f\u4f60\u649e\u8fd9\u68f5\u6811\uff0c\u8fde\u6811 \u53f6\u5b50\u4e5f\u6ca1\u6709\u6447\u843d\u51e0\u7247\uff0c\u6211\u4e00\u638c\u4fbf\u5c06\u5b83\u5288\u65ad\u4e86\uff0c\u6211\u7684\u672c\u4e8b\u662f\u4e0d\u662f\u6bd4\u4f60\u5927\u5f97\u591a\uff1f\u201d\u90a3\u7626\u5b69\u5b50\u77aa\u7740 \u4e00\u53cc\u5927\u773c\u775b\uff0c\u9053\uff1a\u201c\u672c\u4e8b\u5927\u53c8\u600e\u9ebd\u6837\uff1f\u4f60\u5e74\u5e74\u90fd\u6765\u6b3a\u8d1f\u6211\u7684\u7239\u7239\uff0c\u6211\u624d\u4e0d\u8981\u8fd9\u79cd\u6b3a\u4eba\u7684\u672c \u4e8b\uff01\u201d \u3000\u3000\u5929\u5fb7\u4e0a\u4eba\u9762\u8272\u4e00\u53d8\uff0c\u5ffd\u53c8\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u4e0d\u77e5\u597d\u6b79\u7684\u91ce\u5b69\u5b50\u3002\u544a\u8bc9\u4f60\uff0c\u4f60\u7684\u8fd0\u9053\u6765\u5566\u3002 \u6211\u8981\u6536\u4f60\u505a\u5f92\u5f1f\uff0c\u4ee5\u5f8c\u6211\u4e5f\u4e0d\u8981\u4f60\u7239\u7684\u4e1c\u897f\u4e86\u3002\u201d\u90a3\u5b69\u5b50\u9762\u8272\u4e00\u559c\uff0c\u5ffd\u800c\u53c8\u9053\uff1a\u201c\u90a3\u9ebd\u4f60\u8fd8 \u8981\u4e0d\u8981\u5176\u4ed6\u53d4\u53d4\u7684\u4e1c\u897f\u5462\uff1f\u201d\u5929\u5fb7\u4e0a\u4eba\u5947\u9053\uff1a\u201c\u4f60\u90a3\u91cc\u6765\u7684\u8bb8\u591a\u53d4\u53d4\uff1f\u201d \u3000\u3000\u90a3\u7626\u5b69\u5b50\u9053\uff1a\u201c\u6211\u7239\u544a\u8bc9\u6211\uff0c\u4ee5\u524d\u738b\u7237\u5e76\u4e0d\u8981\u6211\u4eec\u7f34\u7eb3\u8fd9\u8bb8\u591a\u4e1c\u897f\uff0c\u662f\u4f60\u6765\u4e86\u4e4b\u5f8c\uff0c\u624d \u591a\u8981\u7684\u3002\u5c71\u5916\u9762\u53d4\u53d4\u4eec\u7684\u725b\u7f8a\uff0c\u5c71\u91cc\u9762\u53d4\u53d4\u4eec\u7684\u836f\u6750\uff0c\u4f60\u90fd\u8981\u3002\u201d\u8f9b\u4e94\u5fd9\u9053\uff1a\u201c\u5c0f\u5b69\u5b50\u4e0d\u8981 \u4e71\u8bf4\u8bdd\u3002\u5927\u6cd5\u5e08\uff0c\u6211\u6709\u8fd9\u6761\u547d\u6839\u5b50\uff0c\u6c42\u4f60\u4e0d\u8981\u5c06\u4ed6\u5e26\u8d70\u3002\u201d\u5929\u5fb7\u4e0a\u4eba\u5927\u6012\u559d\u9053\uff1a\u201c\u54fc\uff0c\u4f60\u6562 \u8fdd\u80cc\u4f5b\u7237\uff0c\u4e0d\u77a7\u5728\u4f60\u513f\u5b50\u7684\u4efd\u4e0a\uff0c\u5148\u9001\u4f60\u5f52\u9634\uff01\u96ea\u83b2\u6211\u4e0d\u8981\u4e86\uff0c\u6211\u8981\u4f60\u7684\u513f\u5b50\u3002\u522b\u4eba\u6c42\u6211\u6536 \u5f92\u5f1f\u6211\u8fd8\u4e0d\u6536\u54e9\uff0c\u4f60\u8fd8\u4e0d\u8bc6\u62ac\u4e3e\uff01\u201d \u3000\u3000\u7626\u5b69\u5b50\u53eb\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u9a82\u6211\u7684\u7239\uff0c\u4f60\u6b3a\u8d1f\u6211\u4eec\uff0c\u6211\u4e0d\u505a\u4f60\u7684\u5f92\u5f1f\uff01\u201d\u5929\u5fb7\u4e0a\u4eba\u72de\u7b11 \u9053\uff1a\u201c\u4f60\u4e0d\u505a\u4e5f\u4e0d\u6210\uff0c\u6211\u628a\u4f60\u5e26\u56de\u53bb\uff0c\u5148\u7528\u97ad\u6253\u6389\u4f60\u7684\u91ce\u6027\uff0c\u7b49\u4f60\u670d\u4e86\uff0c\u7136\u5f8c\u518d\u6559\u4f60\u672c \u4e8b\u3002\u201d\u7626\u5b69\u5b50\u7528\u529b\u6323\u624e\uff0c\u88ab\u5929\u5fb7\u4e0a\u5165\u6263\u7740\u8109\u95e8\uff0c\u8d8a\u6323\u624e\u8d8a\u75db\uff0c\u53ef\u662f\u8fd9\u5c0f\u5b69\u5b50\u5374\u662f\u786c\u6717\uff0c\u6beb\u4e0d \u6c42\u9976\u3002 \u3000\u3000\u5353\u4e00\u822a\u770b\u5f97\u5fc3\u5934\u706b\u8d77\uff0c\u4ece\u5ca9\u77f3\u5f8c\u4e00\u8dc3\u800c\u51fa\uff0c\u9ad8\u58f0\u559d\u9053\uff1a\u201c\u5c82\u6709\u8fd9\u6837\u6536\u5f92\u4e4b\u7406\uff01\u201d\u5929\u5fb7\u4e0a \u4eba\u77a7\u4e86\u4e00\u773c\uff0c\u89c1\u5353\u4e00\u822a\u662f\u4e2a\u6c49\u65cf\u7684\u4e66\u751f\u6a21\u6837\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u6536\u5f92\u5f1f\uff0c\u5173\u4f60\u4ec0\u9ebd\u4e8b\uff1f\u201d\u5353\u4e00 \u822a\u9053\uff1a\u201c\u6536\u5f92\u5f1f\u4e5f\u5f97\u4e24\u53a2\u60c5\u613f\u3002\u201d\u5929\u5fb7\u4e0a\u4eba\u7b11\u9053\uff1a\u201c\u4f5b\u7237\u8981\u600e\u9ebd\u4fbf\u600e\u9ebd\uff0c\u4f60\u518d\u591a\u5634\uff0c\u6211\u5c31\u8fde \u4f60\u7684\u817f\u4e5f\u6253\u6298\u3002\u201d\u5353\u4e00\u822a\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u6709\u8fd9\u6837\u5927\u672c\u4e8b\uff1f\u8001\u5b9e\u8bf4\u8fd9\u5c0f\u5b69\u5b50\u5929\u751f\u7f8e\u8d28\uff0c\u51ed \u4f60\u4e5f\u4e0d\u914d\u505a\u4ed6\u7684\u5e08\u7236\uff01\u201d \u3000\u3000\u5929\u5fb7\u4e0a\u4eba\u5927\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u914d\u505a\u4ed6\u7684\u5e08\u7236\uff0c\u4f60\u914d\u505a\u4e0d\u6210\uff1f\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u6562\u60c5\u4f60\u4e5f\u4f1a\u51e0\u624b\u53c1 \u811a\u732b\u7684\u529f\u592b\uff0c\u6765\u6765\u6765\uff0c\u54b1\u4eec\u8f83\u91cf\u8f83\u91cf\uff01\u201d\u5353\u4e00\u822a\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u95f2\u95f2\u5730\u7b11\u9053\uff1a\u201c\u4f60\u65e2\u8981\u8f83\u91cf\uff0c\u4e3a \u4f55\u8fd8\u4e0d\u52a8\u624b\uff0c\u5c3d\u5439\u70ed\u6c14\u505a\u4ec0\u9ebd\uff1f\u201d\u5929\u5fb7\u4e0a\u4eba\u89c1\u4ed6\u4e0d\u62c9\u67b6\u5f0f\uff0c\u4e0d\u7acb\u95e8\u6237\uff0c\u6beb\u4e0d\u5728\u4e4e\u7684\u6837\u5b50\uff0c\u4e0d \u7981\u5927\u6012\uff0c\u50e7\u888d\u4e00\u62c2\uff0c\u5c31\u7528\u521a\u624d\u5288\u65ad\u5927\u6811\u7684\u62db\u5f0f\uff0c\u547c\u7684\u4e00\u638c\uff0c\u6a2a\u91cc\u5288\u6765\uff01 \u3000\u3000\u90a3\u77e5\u8fd9\u4e00\u52a8\u6012\uff0c\u5374\u6b63\u7740\u4e86\u5353\u4e00\u822a\u7684\u9053\u513f\u3002\u539f\u6765\u5353\u4e00\u822a\u89c1\u4ed6\u9002\u624d\u5288\u65ad\u5927\u6811\u7684\u529f\u592b\uff0c\u81ea\u91cf\u867d \u4e0d\u81f3\u65bc\u843d\u8d25\uff0c\u5374\u4e5f\u4e0d\u6613\u53d6\u80dc\u3002\u4ed6\u8868\u9762\u867d\u95f2\u82e5\u65e0\u4e8b\uff0c\u6697\u5730\u91cc\u5374\u662f\u7384\u529f\u9ed8\u8fd0\uff0c\u4ee5\u9759\u5236\u52a8\u3002\u5929\u5fb7\u4e0a \u4eba\u5148\u662f\u8f7b\u654c\u8fc7\u751a\uff0c\u5176\u5f8c\u53c8\u88ab\u6fc0\u6012\uff0c\u8e81\u5219\u6c14\u6d6e\uff0c\u529b\u867d\u731b\u800c\u4e0d\u6c89\uff0c\u62db\u867d\u5feb\u800c\u4e0d\u7a33\u3002\u5353\u4e00\u822a\u5019\u4ed6\u638c \u950b\u582a\u582a\u5288\u5230\uff0c\u770b\u5c06\u6cbe\u8863\u4e4b\u9645\uff0c\u500f\u7136\u6a2a\u638c\u4e00\u5377\uff0c\u624b\u5fc3\u4e4b\u65b9\u5916\u767b\uff0c\u624b\u6307\u4e4b\u529b\u5185\u5377\uff0c\u5929\u5fb7\u4e0a\u4eba\u4e00\u638c \u5288\u53bb\uff0c\u731b\u89c9\u4e00\u6bb7\u5927\u529b\u53cd\u63a8\u51fa\u6765\uff0c\u8eab\u4e0d\u7531\u4e3b\u7684\u5411\u65c1\u503e\u4ec6\uff0c\u6b63\u62df\u8fd0\u7528\u201c\u5343\u65a4\u5760\u201d\u7684\u91cd\u8eab\u6cd5\u7a33\u7740\u8eab \u5f62\uff0c\u4e0d\u6599\u53c8\u88ab\u5353\u4e00\u822a\u5185\u5377\u4e4b\u65b9\u5411\u5f8c\u4e00\u62c9\uff0c\u987f\u65f6\u5931\u4e86\u5e72\u8861\uff0c\u8eab\u5b50\u6447\u6447\u6446\u6446\uff0c\u5353\u4e00\u822a\u5de6\u638c\u4e00\u7ffb\uff0c \u62cd\u7684\u4e00\u638c\u51fb\u5230\u4ed6\u7684\u524d\u80f8\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u53bb\uff01\u201d\u624b\u638c\u4e00\u9001\uff0c\u5929\u5fb7\u4e0a\u4eba\u5e9e\u5927\u7684\u8eab\u8eaf\u987f\u65f6\u98de\u4e86\u8d77 \u6765\uff0c\u4e00\u4e2a\u5012\u683d\u8471\u822c\u5411\u5f8c\u649e\u53bb\uff01 \u3000\u3000\u5353\u4e00\u822a\u54c8\u54c8\u5927\u7b11\uff0c\u90a3\u77e5\u5929\u5fb7\u4e0a\u4eba\u6b66\u529f\u786e\u662f\u975e\u540c\u5c0f\u9119\uff0c\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u5012\u8f6c\uff0c\u5934\u4e0b\u811a\u4e0a\u624b\u5fc3 \u4e00\u89e6\u5730\u9762\uff0c\u7acb\u523b\u7ffb\u4e86\u8fc7\u6765\uff0c\u53cc\u8db3\u4e00\u57ab\u52b2\uff0c\u5c45\u7136\u53c8\u4f3c\u98de\u7bad\u4e00\u822c\u5c04\u4e86\u4e0a\u6765\uff0c\u62a1\u638c\u518d\u6251\u3002 \u3000\u3000\u5353\u4e00\u822a\u89c1\u4ed6\u6b66\u529f\u4e86\u5f97\uff0c\u90a3\u5bb9\u4ed6\u518d\u62a2\u653b\u52bf\uff0c\u7acb\u5373\u659c\u8eab\u4e0a\u6b65\uff0c\u5de6\u638c\u5411\u4ed6\u8155\u4e0b\u4e00\u64a9\uff0c\u53f3\u624b\u9a88\u6307 \u5982\u621f\uff0c\u4e00\u63a2\u8eab\uff0c\u52bf\u6377\u5982\u7535\uff0c\u70b9\u4ed6\u8170\u80c1\uff0c\u5929\u5fb7\u4e0a\u4eba\u53cc\u62f3\u51fb\u7a7a\uff0c\u8170\u80c1\u4e00\uff0c\u6025\u6025\u95ed\u6c14\u62a4\u7a74\uff0c\u8eab\u5f62\u8fdf \u6ede\uff0c\u5353\u4e00\u822a\u53cc\u62f3\u8fde\u73af\u8fdb\u51fb\uff0c\u7740\u7740\u5360\u5148\uff0c\u5929\u5fb7\u4e0a\u4eba\u8fde\u5403\u4e86\u4e24\u6b21\u4e8f\uff0c\u80c6\u8272\u5df2\u9981\uff0c\u53ea\u4e0d\u8fc7\u6597\u4e86\u5341\u591a \u62db\uff0c\u53ea\u89c1\u5353\u4e00\u822a\u5de6\u811a\u4e00\u64a9\uff0c\u53f3\u638c\u84ec\u7684\u4e00\u58f0\uff0c\u51fb\u4e2d\u4ed6\u7684\u80a9\u5934\uff0c\u8fd9\u4e00\u56de\u5353\u4e00\u822a\u7528\u7684\u662f\u6b66\u5f53\u638c\u6cd5\u4e2d \u201c\u4e0a\u4e0b\u4ea4\u5f81\u201d\u7684\u7edd\u62db\uff0c\u62f3\u811a\u517c\u65bd\uff0c\u4e0a\u4e0b\u9f50\u5230\uff0c\u5929\u5fb7\u4e0a\u4eba\u90a3\u91cc\u7ecf\u53d7\u5f97\u7740\uff0c\u5495\u549a\u4e00\u58f0\uff0c\u8dcc\u7ffb\u5728 \u5730\uff0c\u8001\u534a\u5929\u4e5f\u722c\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u90a3\u7626\u5b69\u5b50\u5728\u65c1\u770b\u5f97\u62cd\u624b\u5927\u7b11\uff0c\u53eb\u5353\u4e00\u822a\u9053\uff1a\u201c\u518d\u7ed9\u4ed6\u4e00\u811a\uff0c\u628a\u4ed6\u8e22\u4e0b\u5c71\u53bb\uff01\u201d\u5353\u4e00\u822a\u7b11 \u9053\uff1a\u201c\u4ed6\u81ea\u5df1\u4e0d\u4f1a\u722c\u9ebd\uff1f\u201d\u5929\u5fb7\u4e0a\u4eba\u6ee1\u9762\u7f9e\u60ed\uff0c\u722c\u4e86\u8d77\u6765\uff0c\u4e0d\u6562\u4f5c\u58f0\uff0c\u548c\u4ed6\u7684\u5f92\u5f1f\u4e0b\u5c71\u4fbf \u8dd1\uff0c\u90a3\u5c0f\u5b69\u5b50\u4e50\u5f97\u66f4\u662f\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u8f9b\u4e94\u4e0a\u524d\u9053\u8c22\uff0c\u5353\u4e00\u822a\u9053\uff1a\u201c\u8fd9\u7b97\u5f97\u4e86\u4ec0\u9ebd\uff1f\u8001\u4e08\u4f55\u5fc5\u8a00\u8c22\u3002\u4f60\u8fd9\u5b69\u5b50\u591a\u5927\u4e86\uff0c\u53eb\u4ec0\u9ebd \u540d\u5b57\uff1f\u77a7\u4ed6\u521a\u624d\u90a3\u624b\uff0c\u771f\u662f\u5f8c\u751f\u53ef\u754f\uff01\u201d\u8f9b\u4e94\u9053\uff1a\u201c\u9f99\u5b50\uff0c\u4f60\u8fd8\u4e0d\u8fc7\u6765\u591a\u8c22\u6069\u4eba\uff0c\u8981\u4e0d\u662f\u9047 \u5230\u8fd9\u4f4d\u76f8\u516c\uff0c\u4f60\u5df2\u7ecf\u7ed9\u90a3\u51f6\u50e7\u62c9\u53bb\u5566\uff01\u76f8\u516c\uff0c\u4f60\u522b\u89c1\u7b11\uff0c\u4ed6\u4eca\u5e74\u5341\u53c1\u5c81\u4e86\uff0c\u8fd8\u662f\u4ec0\u9ebd\u4e8b\u4e5f\u4e0d \u61c2\uff0c\u91ce\u5f97\u5f88\uff01\u201d\u90a3\u5b69\u5b50\u5ffd\u7136\u8dea\u5728\u5353\u4e00\u822a\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u6069\u4eba\uff0c\u4f60\u6536\u4ffa\u505a\u5f92\u5f1f\u5427\uff0c\u4ffa\u8f9b\u9f99\u5b50\u7ed9 \u4f60\u78d5\u5934\u4e86\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u672c\u6765\u6ca1\u6709\u6536\u5f92\u5f1f\u4e4b\u5ff5\uff0c\u4f46\u89c1\u8f9b\u9f99\u5b50\u76f8\u8c8c\u5947\u7279\uff0c\u795e\u529b\u5929\u751f\uff0c\u8877\u5fc3\u6b22\u559c\uff0c\u4fbf\u9053\uff1a \u201c\u597d\uff0c\u6211\u6536\u4f60\u4e3a\u5f92\uff0c\u4f60\u5b66\u4e86\u672c\u4e8b\u4e4b\u5f8c\uff0c\u53ef\u4e0d\u8bb8\u6043\u52bf\u6b3a\u4eba\u3002\u201d\u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u6211\u82e5\u6043\u52bf\u6b3a\u4eba\uff0c\u5c31 \u50cf\u521a\u624d\u90a3\u51f6\u50e7\u4e00\u822c\uff0c\u4e0d\u5f97\u597d\u6b7b\u3002\u201d\u8f9b\u4e94\u4e5f\u5f88\u559c\u6b22\uff0c\u4f46\u5374\u6015\u5353\u4e00\u822a\u5c06\u4ed6\u513f\u5b50\u5e26\u8d70\u3002\u5353\u4e00\u822a\u9053\uff1a \u201c\u6211\u77e5\u9053\u4ed6\u662f\u4f60\u7684\u547d\u6839\u5b50\uff0c\u6211\u5728\u8fd9\u91cc\u4f20\u4ed6\u6b66\u529f\u4fbf\u662f\u3002\u201d \u3000\u3000\u8f9b\u4e94\u8bf7\u5353\u4e00\u822a\u8fdb\u77f3\u5c4b\u5185\u5750\uff0c\u77f3\u5c4b\u5185\u8bbe\u5907\u5341\u5206\u7b80\u5355\uff0c\u5899\u58c1\u4e0a\u6302\u6709\u4e24\u526f\u5f13\u7bad\uff0c\u51e0\u5f20\u517d\u76ae\uff0c\u5730 \u4e0a\u6446\u7740\u51e0\u4e2a\u5927\u767e\u5934\uff0c\u5f53\u505a\u53f0\u51f3\uff0c\u5353\u4e00\u822a\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4f55\u4f4f\u5728\u96ea\u5c71\u4e4b\u4e0a\uff1f\u201d\u8f9b\u4e94\u9053\uff1a\u201c\u6211\u4eec \u4e60\u60ef\u4e25\u5bd2\uff0c\u5728\u8fd9\u91cc\u8c0b\u751f\u6bd4\u8f83\u5bb9\u6613\uff0c\u5c71\u4e0a\u96ea\u5cf0\u5f88\u591a\uff0c\u836f\u6750\u4e5f\u5bb9\u6613\u91c7\u3002\u201d\u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u5e08\u7236\uff0c\u660e \u5929\u6211\u5e26\u4f60\u4e0a\u4e0a\u9762\u51b0\u5cf0\u53bb\u73a9\uff0c\u90a3\u91cc\u624d\u597d\u73a9\u5462\u3002\u4e0a\u9762\u6709\u4e2a\u51b0\u6e56\uff0c\u51b0\u6e56\u6709\u4e24\u679d\u96ea\u83b2\uff0c\u6bcf\u53c1\u5e74\u5f00\u82b1\u4e00 \u6b21\uff0c\u53ef\u60dc\u4eca\u5e74\u7684\u96ea\u83b2\u6211\u4eec\u5df2\u7ecf\u91c7\u4e86\u548c\u836f\u6750\u5546\u6362\u76d0\u98df\uff0c\u8981\u4e0d\u7136\u6211\u62ff\u7ed9\u4f60\u770b\uff0c\u90a3\u624d\u53eb\u597d\u770b\u5462\uff0c\u96ea \u767d\u7684\u82b1\uff0c\u53c8\u5927\u53c8\u9999\uff0c\u4e00\u6735\u82b1\u5c31\u53ef\u6362\u5341\u65a4\u76d0\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u96ea\u83b2\u662f\u975e\u5e38\u96be\u5f97\u7684\u836f\u6750\uff0c\u62ff\u5230\u5916 \u9762\uff0c\u4e00\u6735\u82b1\u6700\u5c11\u503c\u4e00\u4e24\u91d1\u5b50\uff0c\u4ee5\u5f8c\u53ef\u522b\u8fd9\u6837\u8d31\u5356\u4e86\u3002\u201d\u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u91d1\u5b50\u6709\u4ec0\u9ebd\u7528\uff0c\u53c8\u4e0d\u80fd \u5f53\u996d\u5403\u3002\u201d\u8f9b\u4e94\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u6211\u4eec\u4f55\u5c1d\u4e0d\u77e5\u9053\u96ea\u83b2\u503c\u94b1\uff1f\u4f46\u62ff\u5230\u5916\u9762\uff0c\u4e5f\u4e0d\u5bb9\u6613\u627e\u5230\u4e70\u4e3b\u80af \u51fa\u516c\u9053\u7684\u4ef7\u94b1\uff0c\u800c\u4e14\u8fd9\u4e00\u6765\u4e00\u56de\u7684\u65c5\u8d39\uff0c\u6211\u4eec\u53c8\u5230\u90a3\u91cc\u53bb\u501f\uff1f\u201d\u5353\u4e00\u822a\u751f\u957f\u5bcc\u8d35\u4e4b\u5bb6\uff0c\u5bf9\u8d2b \u6c11\u7684\u75db\u82e6\u4e86\u89e3\u751a\u5c11\uff0c\u542c\u4e86\u54d1\u7136\u65e0\u58f0\uff0c\u6697\u7b11\u81ea\u5df1\u4e0d\u61c2\u4e16\u52a1\u3002 \u3000\u3000\u8f9b\u9f99\u5b50\u53c8\u7b11\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6211\u60f3\u8d77\u6765\u4e86\uff0c\u4e0a\u9762\u8fd8\u6709\u4e24\u6735\u82b1\uff0c\u6bd4\u96ea\u83b2\u66f4\u597d\u770b\uff0c\u53ef\u60dc\u90a3\u82b1\u8fd8\u6ca1 \u5f00\u3002\u201d\u5353\u4e00\u822a\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6025\u95ee\u9053\uff1a\u201c\u8fd9\u4e24\u6735\u82b1\u662f\u4e0d\u662f\u4e00\u767d\u4e00\u7ea2\uff1f\u201d\u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u662f\u5440\uff0c\u4f60\u600e \u9ebd\u77e5\u9053\uff1f\uff0c\u201d\u5353\u4e00\u822a\u8fd9\u4e00\u559c\u975e\u540c\u5c0f\u9119\uff0c\u6025\u53c8\u95ee\u9053\uff1a\u201c\u6709\u6ca1\u6709\u996d\u7897\u90a3\u9ebd\u5927\uff1f\u201d\u8f9b\u9f99\u5b50\u5931\u7b11\u9053\uff1a \u201c\u6709\u6885\u5b50\u90a3\u9ebd\u5927\uff0c\u82b1\u74e3\u8fd8\u662f\u7d27\u7d27\u5305\u7740\u7684\u5462\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4eca\u5929\u4f60\u5c31\u5e26\u6211\u53bb\u770b\uff0c\u597d\u5417\uff1f\u201d\u8f9b \u9f99\u5b50\u559c\u9053\uff1a\u201c\u5e08\u7236\uff0c\u539f\u6765\u4f60\u4e5f\u7231\u73a9\u3002\u201d\u8f9b\u4e94\u4e5f\u597d\u751f\u5947\u602a\uff0c\u95ee\u513f\u5b50\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u89c1\u5230\u7684\u4e86\u4e3a\u4ec0 \u9ebd\u4e0d\u8bf4\u7ed9\u6211\u542c\uff1f\u201d\u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u524d\u4e24\u5929\u6211\u4e0a\u53bb\u638f\u9e70\u7684\u86cb\uff0c\u5728\u82b1\u4e1b\u4e2d\u53d1\u73b0\u7684\u3002\u90a3\u4e24\u6735\u82b1\u8fd8\u6ca1\u6709 \u5f00\uff0c\u6211\u544a\u8bc9\u4f60\u505a\u4ec0\u9ebd\uff1f\u201d\u8f9b\u4e94\u9053\uff1a\u201c\u50bb\u5b69\u5b50\uff0c\u8fd9\u4e24\u6735\u82b1\u6050\u6015\u5c31\u662f\u8349\u539f\u4e0a\u4f20\u8bf4\u7684\u2026\u2026\u201d\u5353\u4e00\u822a \u63d2\u53e3\u9053\uff1a\u201c\u4f18\u6619\u4ed9\u82b1\uff1f\u201d\u8f9b\u4e94\u5947\u9053\uff1a\u201c\u6069\u516c\uff0c\u4f60\u4e5f\u77e5\u9053\u4f18\u6619\u4ed9\u82b1\u5417\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u6b63\u662f \u4e3a\u627e\u5b83\u6765\u7684\uff01\u201d\u8f9b\u4e94\u751a\u4e3a\u76f4\uff0c\u9053\uff1a\u201c\u6069\u516c\uff0c\u4f60\u6551\u4e86\u6211\u4eec\uff0c\u53c8\u80af\u6559\u5c0f\u513f\u6b66\u827a\uff0c\u6211\u4eec\u65e0\u4ee5\u4e3a\u62a5\uff0c \u5c31\u66ff\u4f60\u5b88\u8fd9\u4e24\u6735\u82b1\u5427\u3002\u542c\u9f99\u5b50\u7684\u8bf4\u8bdd\uff0c\u8fd9\u4e24\u6735\u82b1\u6050\u6015\u8fd8\u8981\u5f88\u4e45\u624d\u5f00\u5462\uff01\u6069\u516c\uff0c\u4f60\u5148\u5403\u70b9\u4e1c \u897f\uff0c\u54b1\u4eec\u518d\u4e0a\u53bb\u770b\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u80e1\u4e71\u5403\u4e86\u4e00\u70b9\u9762\u56e2\u9001\u7092\u91ce\u5473\uff0c\u4fbf\u548c\u8f9b\u4e94\u7236\u5b50\u4e0a\u5c71\uff0c\u8fd9\u5ea7\u5c71\u4e3a\u51b0\u96ea\u8986\u76d6\uff0c\u65f6\u5e8f\u867d\u5df2 \u66ae\u6625\uff0c\u4ecd\u662f\u5bd2\u98ce\u523a\u9aa8\u3002\u5353\u4e00\u822a\u968f\u7740\u8f9b\u9f99\u5b50\u8dd1\u4e0a\u5c71\u5cf0\uff0c\u8d8a\u8d70\u8d8a\u89c9\u5947\u602a\uff0c\u666e\u901a\u7684\u5c71\uff0c\u8d8a\u9ad8\u8d8a\u51b7\uff0c \u4f46\u6500\u767b\u8fd9\u5ea7\u5c71\u5cf0\uff0c\u5374\u521a\u521a\u76f8\u53cd\uff0c\u5c71\u8170\u751a\u51b7\uff0c\u6765\u5230\u4e0a\u9762\uff0c\u53cd\u800c\u6e10\u6e10\u6696\u548c\uff01 \u3000\u3000\u8f9b\u4e94\u7b11\u9053\uff1a\u201c\u8fd9\u5ea7\u5c71\u540d\u53eb\u6728\u4ec0\u5854\u514b\uff0c\u7ef4\u4eba\u79f0\u51b0\u4e3a\u201c\u6728\u4ec0\u201d\uff0c\u79f0\u5c71\u4e3a\u201c\u5854\u514b\u201d\uff0c\u6728\u4ec0\u5854\u514b \u4fbf\u662f\u51b0\u5c71\u7684\u610f\u601d\u3002\u6574\u5ea7\u5c71\u4e3a\u51b0\u96ea\u8986\u76d6\uff0c\u5341\u5206\u5bd2\u51b7\uff0c\u5355\u5355\u8fd9\u4e00\u5ea7\u5c71\u5cf0\u4e0a\u9762\u6e29\u6696\u5982\u8202\u3002\u201d\u5353\u4e00\u822a \u5947\u9053\uff1a\u201c\u4ec0\u9ebd\u7f18\u6545\uff01\u201d\u8f9b\u4e94\u9053\uff1a\u201c\u636e\u4f20\u6570\u5343\u5e74\u524d\uff0c\u8fd9\u5c71\u5cf0\u4e0a\u6709\u4e2a\u706b\u5c71\u53e3\uff0c\u5e38\u5e74\u55b7\u706b\u3002\u5f8c\u6765\u706b \u5c71\u706d\u4e86\uff0c\u5316\u4e3a\u6e56\u6cca\uff0c\u4f46\u9644\u8fd1\u5730\u8109\u8fd8\u4fdd\u7740\u70ed\u6c14\uff0c\u6240\u4ee5\u6e29\u6696\u3002\u201d\u6c99\u6f20\u5730\u5e26\uff0c\u9887\u591a\u8fdc\u53e4\u9057\u7559\u4e0b\u6765\u7684 \u201c\u6b7b\u706b\u5c71\u201d\uff0c\u50cf\u5410\u9c81\u756a\u4ee5\u524d\u7684\u706b\u5c71\uff0c\u5c31\u6781\u4e3a\u7740\u540d\u3002\u6728\u4ec0\u5854\u514b\u5c71\u4e0a\u7684\u706b\u5c71\uff0c\u8fd8\u53ea\u80fd\u7b97\u662f\u5c0f\u7684\u3002 \u3000\u3000\u5353\u4e00\u822a\u7b11\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u8fd9\u91cc\u5012\u662f\u6700\u597d\u7684\u9690\u5c45\u4e4b\u6240\u3002\u201d\u52a0\u901f\u811a\u6b65\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u6500\u4e0a\u5c71 \u9876\uff0c\u53ea\u89c9\u773c\u524d\u4e00\u4eae\uff0c\u4f46\u89c1\u6ee1\u5c71\u662f\u7eff\u8335\u8335\u7684\u8349\u5730\uff0c\u4e00\u80a1\u6e05\u6cc9\u81ea\u5c71\u5cf0\u4e0a\u6d41\u6cfb\u4e0b\u6765\uff0c\u6c47\u6210\u4e00\u4e2a\u5c0f\u5c0f \u7684\u6e56\u6cca\uff0c\u6e56\u4e0a\u6709\u968f\u5c71\u6cc9\u51b2\u4e0b\u6765\u3001\u5c1a\u672a\u88ab\u5730\u6c14\u878d\u5316\u7684\u6d6e\u51b0\uff0c\u8fd8\u6709\u96f6\u843d\u7684\u82b1\u74e3\u3002\u51b0\u6e56\u4e4b\u65c1\uff0c\u7e41\u82b1 \u5982\u6d77\uff0c\u8f9b\u9f99\u5b50\u6307\u7740\u4e00\u5904\u82b1\u4e1b\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u6765\u770b\u5440\uff0c\u90a3\u4e24\u6735\u672a\u5f00\u7684\u4ed9\u82b1\uff0c\u4fbf\u5728\u8fd9\u91cc\u4e86\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u62e8\u5f00\u7e41\u679d\u5bc6\u53f6\uff0c\u94bb\u8fdb\u82b1\u4e1b\uff0c\u5ffd\u95fb\u5947\u9999\u6251\u9f3b\uff0c\u7cbe\u795e\u987f\u723d\u3002\u4ed4\u7ec6\u770b\u65f6\uff0c\u53ea\u89c1\u4e24\u6735\u84d3 \u857e\uff0c\u90fd\u5982\u62c7\u6307\u822c\u5927\u3002\u7ea2\u7684\u6709\u5982\u80ed\u8102\uff0c\u767d\u7684\u5b9b\u5982\u767d\u7389\uff0c\u90fd\u88ab\u82b1\u74e3\u7d27\u7d27\u5305\u7740\u3002\u5353\u4e00\u822a\u5148\u662f\u4e00\u559c\uff0c \u7ee7\u800c\u4e00\u5fe7\u3002\u559c\u7684\u662f\u7ec8\u65bc\u89c1\u5230\u4e86\u4f18\u6619\u4ed9\u82b1\uff0c\u5fe7\u7684\u662f\u4e0d\u77e5\u5b83\u4ec0\u9ebd\u65f6\u5019\u624d\u5f00\uff1f \u3000\u3000\u5353\u4e00\u822a\u770b\u4e86\u4e00\u4f1a\uff0c\u62db\u624b\u53eb\u8f9b\u4e94\u8fc7\u6765\uff0c\u8f9b\u4e94\u4e00\u770b\uff0c\u95ee\u9053\uff1a\u201c\u6069\u4eba\uff0c\u4f60\u8981\u8fd9\u4e24\u6735\u4ed9\u82b1\u505a\u4ec0 \u9ebd\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u7684\u4e00\u4f4d\u670b\u53cb\u672a\u8001\u767d\u5934\uff0c\u6211\u6025\u7740\u8981\u8fd9\u4e24\u6735\u82b1\u66ff\u5979\u6062\u590d\u9752\u6625\u3002\u201d\u7126\u6025\u4e4b \u60c5\uff0c\u89c1\u65bc\u8f9e\u8272\u3002\u8f9b\u4e94\u542c\u4e86\uff0c\u534a\u664c\u65e0\u8bdd\u3002\u5fc3\u9053\uff1a\u201c\u5f85\u5f97\u8fd9\u4e24\u6735\u4ed9\u82b1\u5f00\u82b1\u65f6\uff0c\u6211\u513f\u5b50\u7684\u5934\u53d1\u6050\u6015 \u4e5f\u8981\u767d\u4e86\u3002\u201d\u4f20\u8bf4\u4e2d\u7684\u4f18\u6619\u4ed9\u82b1\uff0c\u516d\u5341\u5e74\u624d\u5f00\u4e00\u6b21\uff0c\u5f00\u65f6\uff0c\u82b1\u5982\u6d77\u7897\uff0c\u707f\u82e5\u4e91\u971e\uff0c\u6b64\u82b1\u5728 \u201c\u5341\u5c81\u201d\u4e4b\u524d\uff0c\u4ec5\u5982\u62c7\u6307\uff0c\u5341\u5c81\u4e4b\u5f8c\uff0c\u624d\u6e10\u957f\u5927\u3002\u5353\u4e00\u822a\u53ea\u77e5\u9053\u4f20\u8bf4\u4e2d\u6709\u8fd9\u9ebd\u4e00\u79cd\u4ed9\u82b1\uff0c\u5374 \u4e0d\u77e5\u9053\u5224\u522b\u201c\u82b1\u9f84\u201d\u4e4b\u6cd5\u3002\u4e00\u518d\u95ee\u9053\uff1a\u201c\u4f60\u770b\u4e86\u5b83\u4ec0\u9ebd\u65f6\u5019\u624d\u80fd\u5f00\u4e86\u4f60\u4eec\u8349\u539f\u4e0a\u53e4\u8001\u7684\u4f20 \u8bf4\uff0c\u8fd8\u6709\u4ec0\u9ebd\u6709\u5173\u8fd9\u79cd\u4ed9\u82b1\u7684\u9ebd\uff1f\u201d \u3000\u3000\u8f9b\u4e94\u89c1\u4ed6\u5982\u6b64\u7126\u6025\uff0c\u4e0d\u5fcd\u76f4\u8bf4\uff0c\u4f46\u9053\uff1a\u201c\u8c01\u4e5f\u6ca1\u6709\u89c1\u8fc7\u4f18\u6619\u4ed9\u82b1\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4ec0\u9ebd\u65f6 \u5019\u624d\u5f00\uff0c\u4e5f\u8bb8\u4e94\u5e74\uff0c\u4e5f\u8bb8\u5341\u5e74\uff0c\u4e5f\u8bb8\u4e8c\u5341\u5e74\uff0c\u600e\u8bf4\u5f97\u5b9a\u5462\uff1f\u201d\u5176\u5b9e\u662f\u6700\u5c11\u8fd8\u8981\u4e94\u5341\u5e74\uff0c\u8f9b\u4e94 \u6545\u610f\u5c11\u8bf4\u4e86\u3002 \u3000\u3000\u5353\u4e00\u822a\u7d27\u8e59\u53cc\u7709\uff0c\u9ed8\u7136\u4e0d\u8bed\u3002\u8f9b\u4e94\u9053\uff1a\u201c\u6069\u516c\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u4eec\u7236\u5b50\u66ff\u4f60\u770b\u5b88\u8fd9\u4e24\u6735\u4ed9 \u82b1\uff0c\u6211\u6b7b\u4e86\u8fd8\u6709\u9f99\u5b50\u5462\uff0c\u6211\u4eec\u4e4b\u4e2d\u603b\u6709\u4eba\u80fd\u89c1\u7740\u4ed9\u82b1\u5f00\u653e\u3002\u201d\u5353\u4e00\u822a\u51c4\u7136\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e5f \u597d\u3002\u5b88\u5f97\u82b1\u5f00\uff0c\u4e0d\u7ba1\u4eba\u5bff\u5982\u4f55\uff0c\u4e5f\u4e1d\u7b97\u8fd8\u4e86\u5fc3\u613f\u3002\u201d \u3000\u3000\u8f9b\u4e94\u6162\u6162\u8d70\u51fa\u82b1\u4e1b\uff0c\u60f3\u8d77\u4e00\u4e8b\uff0c\u5ffd\u9053\uff1a\u201c\u5c31\u53ea\u6015\u90a3\u51f6\u50e7\u8fd8\u4f1a\u518d\u6765\u9a9a\u6270\uff0c\u90a3\u65f6\u6211\u4eec\u7236\u5b50\u60f3 \u66ff\u4f60\u770b\u5b88\u4ed9\u82b1\u4e5f\u770b\u5b88\u4e0d\u6210\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u60f3\u4e86\u4e00\u4f1a\uff0c\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u672c\u6765\u6211\u5bf9\u4f60\u4eec\u8349\u539f\u4e0a\u5404\u65cf\u7684\u4e8b\u60c5\uff0c\u4e0d\u987f\u7406\u4f1a\u3002\u4f46\u90a3\u51f6\u50e7 \u65e2\u7136\u8fd9\u6837\u53ef\u6076\uff0c\u6211\u597d\u548c\u4ed6\u518d\u6597\u4e00\u6597\u4e86\u3002\u201d\u8f9b\u4e94\u9053\uff1a\u201c\u6069\u516c\u8981\u518d\u53bb\u627e\u4ed6\u6666\u6c14\u9ebd\uff1f\u5929\u9f99\u6d3e\u9887\u6709\u52bf \u529b\uff0c\u90a3\u51f6\u50e7\u5c24\u5176\u5f97\u6211\u4eec\u914b\u957f\u4fe1\u4efb\uff0c\u6069\u516c\u53ef\u5f97\u5c0f\u5fc3\u3002\u201d\u8f9b\u9f99\u5b50\u5374\u62cd\u624b\u56b7\u9053\uff1a\u201c\u597d\u5440\uff0c\u5e08\u7236\u53bb\u518d \u6253\u4ed6\u4e00\u987f\uff0c\u6700\u597d\u628a\u4ed6\u8d76\u51fa\u6211\u4eec\u7684\u8349\u539f\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u9f99\u5b50\uff0c\u4f60\u8981\u8bb0\u7740\uff1a\u5b66\u6b66\u4e4b\u4eba\uff0c\u5e94\u6212\u597d\u52c7\u6597\u72e0\u3002\u6211\u662f\u60f3\u628a\u4ed6\u8d76\u51fa \u8349\u539f\uff0c\u4f46\u5374\u4e0d\u60f3\u548c\u4ed6\u6253\u67b6\u4e86\u3002\u201d\u505c\u4e86\u4e00\u505c\uff0c\u5bf9\u8f9b\u4e94\u9053\uff1a\u201c\u6211\u5728\u5929\u5c71\u5357\u5317\u6f2b\u6e38\u4e86\u51e0\u5e74\uff0c\u5bf9\u4f60\u4eec \u8349\u539f\u4e0a\u5404\u65cf\u7684\u60c5\u5f62\uff0c\u4e5f\u5927\u81f4\u77e5\u9053\u4e00\u4e9b\u3002\u5728\u5404\u65cf\u5404\u90e8\u4e4b\u4e2d\uff0c\u4ee5\u54c8\u8428\u514b\u65cf\u3001\u5580\u8fbe\u5c14\u65cf\u3001\u7f57\u5e03\u65cf\u53c1 \u65cf\u6700\u4e3a\u5f3a\u76db\uff0c\u5c24\u5176\u4ee5\u7f57\u5e03\u65cf\u7684\u914b\u957f\u5510\u52aa\uff0c\u66f4\u662f\u82f1\u540d\u8fdc\u64ad\uff0c\u5f97\u4eba\u5c0a\u656c\u3002\u5580\u8fbe\u5c14\u65cf\u7684\u914b\u957f\u5b5f\u8428\u601d \u867d\u7136\u4e5f\u662f\u6781\u80fd\u5e72\u7684\u4eba\uff0c\u4f46\u4ed6\u4e3a\u4eba\u6b8b\u66b4\uff0c\u91ce\u5fc3\u53c8\u5927\uff0c\u522b\u4eba\u53ea\u662f\u6015\u4ed6\u5374\u4e0d\u5c0a\u656c\u4ed6\u3002\u4f60\u4eec\u7684\u914b\u957f\u4e3a \u4eba\u672c\u6765\u4e0d\u9519\uff0c\u53ef\u60dc\u4e3a\u90a3\u51f6\u50e7\u548c\u4e00\u4e9b\u4e0d\u8096\u7684\u90e8\u4e0b\u6240\u8499\u853d\uff0c\u6240\u4ee5\u8fd1\u5e74\u884c\u4e8b\u597d\u574f\u53c2\u534a\u3002\u53ef\u662f\u8fd9\u6837 \u9ebd\uff1f\u201d\u8f9b\u4e94\u9053\uff1a\u201c\u6069\u516c\u8bf4\u5f97\u4e0d\u9519\u3002\u201d\u5353\u4e00\u822a\u7eed\u9053\uff1a\u201c\u56e0\u6b64\u6211\u60f3\u53bb\u89c1\u4f60\u4eec\u7684\u914b\u957f\uff0c\u5c06\u90a3\u51f6\u50e7\u6b3a \u538b\u767e\u59d3\u7684\u4e8b\u8bf4\u51fa\u6765\u3002\u8bf7\u4f60\u4eec\u7684\u914b\u957f\u5c06\u4ed6\u8d76\u51fa\u53bb\u3002\u201d\u8f9b\u4e94\u6c89\u601d\u534a\u664c\uff0c\u9053\uff1a\u201c\u8fd9\u6562\u60c5\u597d\uff0c\u4e0d\u8fc7\uff0c \u6211\u6015\u758f\u4e0d\u95f4\u4eb2\uff0c\u6069\u516c\u53bb\u65f6\uff0c\u6700\u597d\u5148\u89c1\u6211\u4eec\u914b\u957f\u7684\u526f\u624b\u5df4\u9f99\uff0c\u8fd9\u4eba\u5bf9\u8001\u914b\u957f\u5fe0\u5fc3\u803f\u803f\uff0c\u5bf9\u65cf\u4eba \u4e5f\u5f88\u597d\uff0c\u542c\u8bf4\u4ed6\u548c\u90a3\u51f6\u50e7\u4e5f\u662f\u5bf9\u5934\uff0c\u5148\u548c\u4ed6\u5546\u8c08\uff0c\u884c\u4e8b\u4fbf\u5bb9\u6613\u5f97\u591a\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u597d\uff0c\u6211 \u5148\u4f20\u6388\u9f99\u5b50\u4e00\u70b9\u672c\u95e8\u7684\u5165\u95e8\u529f\u592b\uff0c\u7136\u5f8c\u518d\u53bb\u3002\u201d \u3000\u3000\u8f9b\u9f99\u5b50\u5728\u51b0\u5c71\u9a7c\u5cf0\u4e4b\u4e0a\u957f\u5927\uff0c\u81ea\u5e7c\u4fbf\u8ffd\u9010\u9e1f\u517d\uff0c\u52a9\u7236\u4eb2\u6253\u730e\uff0c\u953b\u6210\u4e00\u526f\u77eb\u5065\u7684\u8eab\u624b\uff0c\u4e14 \u53c8\u751f\u6210\u795e\u529b\uff0c\u56e0\u6b64\u5b66\u8d77\u6b66\u6765\uff0c\u5341\u5206\u5bb9\u6613\u4e0a\u624b\u3002\u5353\u4e00\u822a\u6559\u4e86\u4ed6\u4e00\u4e9b\u5165\u95e8\u529f\u592b\uff0c\u53c8\u4f20\u4e86\u4ed6\u4e00\u5957\u4e5d \u5bab\u795e\u884c\u638c\u6cd5\uff0c\u5728\u9a7c\u5cf0\u4e0a\u4f4f\u4e86\u53c1\u4e2a\u6708\uff0c\u770b\u8f9b\u9f99\u5b50\u5df2\u6253\u597d\u521d\u6b65\u6839\u57fa\u4e4b\u5f8c\uff0c\u4fbf\u53eb\u4ed6\u81ea\u884c\u7ec3\u4e60\uff0c\u79bb\u5f00 \u9a7c\u5cf0\uff0c\u76f4\u5411\u5317\u7586\u5404\u65cf\u805a\u5c45\u7684\u8349\u539f\u800c\u53bb\u3002 \u3000\u3000\u4e00\u65e5\uff0c\u5353\u4e00\u822a\u6b63\u7a7f\u8fc7\u5929\u5c71\u652f\u8109\u7684\u6155\u58eb\u5854\u683c\u5c71\uff0c\u8fc7\u4e86\u6b64\u5c71\uff0c\u4fbf\u662f\u5317\u7586\u6c34\u8349\u80a5\u7f8e\u7684\u5929\u7136\u5927\u7267 \u573a\u4e86\u3002\u8fd9\u6155\u58eb\u5854\u683c\u5c71\u867d\u4e0d\u5982\u5929\u5c71\u4e3b\u5cf0\u7684\u9ad8\u8038\u5165\u4e91\uff0c\u4f46\u5374\u662f\u7fa4\u5cf0\u73af\u62b1\uff0c\u6709\u5982\u91cd\u95e8\u67e5\u6237\uff0c\u9669\u5cfb\u975e \u5e38\u3002\u56e0\u4e3a\u6b64\u5c71\u4ecd\u662f\u5357\u5317\u7586\u7684\u901a\u9053\uff0c\u5c71\u8170\u4e4b\u5904\uff0c\u6709\u5c71\u6c11\u5f00\u9274\u7684\u4e00\u6761\u7f8a\u80a0\u5c0f\u9053\uff0c\u4f46\u56e0\u884c\u8d70\u7684\u4eba\u4e0d \u591a\uff0c\u4e5f\u957f\u6ee1\u4e86\u91ce\u8349\u8346\u68d8\uff0c\u5353\u4e00\u822a\u62e8\u8349\u5f00\u8def\uff0c\u4f46\u89c1\u524d\u9762\u4e24\u5cf0\u5bf9\u7acb\uff0c\u4e0b\u4e34\u5e7d\u8c37\uff0c\u5c71\u9053\u873f\u8713\uff0c\u5c31\u5982 \u4e00\u6761\u957f\u86c7\u4ece\u4e24\u5cf0\u4e4b\u95f4\u7a7f\u8fc7\uff0c\u770b\u90a3\u5c71\u8def\uff0c\u53ea\u80fd\u5bb9\u4e00\u4eba\u4e00\u9a91\uff0c\u5353\u4e00\u822a\u5fc3\u9053\uff1a\u201c\u8fd9\u771f\u662f\u4e00\u592b\u5f53\u5173\uff0c \u4e07\u592b\u83ab\u654c\u7684\u9669\u5730\u3002\u201d \u3000\u3000\u6b63\u884c\u8d70\u95f4\uff0c\u5ffd\u542c\u5f97\u524d\u9762\u6709\u9a6c\u94c3\u4e4b\u58f0\uff0c\u5728\u8fd9\u6837\u5d0e\u5c96\u7684\u5c71\u9053\u7eb5\u9a6c\u5954\u9a70\uff0c\u82e5\u975e\u9a91\u672f\u7cbe\u7edd\uff0c\u4e07\u4e07 \u4e0d\u80fd\u3002\u5353\u4e00\u822a\u597d\u5947\u5fc3\u8d77\uff0c\u767b\u9ad8\u773a\u671b\uff0c\u53ea\u89c1\u8fdc\u5904\u4e24\u5339\u9a6c\u5148\u5f8c\u5954\u6765\uff0c\u521a\u521a\u5230\u4e86\u4e24\u8702\u5bf9\u9501\u7684\u5c71\u53e3\uff0c \u84e6\u7136\u542c\u5f97\u4e00\u58f0\u53e3\u54e8\uff0c\u5f13\u5f26\u75be\u54cd\uff0c\u4e24\u5339\u9a6c\u60e8\u5636\u58f0\u58f0\uff0c\u9a6c\u4e0a\u4eba\u7ffb\u4e86\u4e2a\u7b4b\u6597\uff0c\u5728\u9a6c\u80cc\u4e0a\u76f4\u8dcc\u4e0b\u65e0\u5e95 \u6df1\u8c37\uff01 \u3000\u3000\u5353\u4e00\u822a\u5927\u5403\u4e00\u60ca\uff0c\u4ee5\u4e3a\u662f\u5c71\u8d3c\u4f0f\u52ab\u9a91\u5ba2\uff0c\u9a6c\u5339\u4e2d\u7bad\uff0c\u9a91\u5ba2\u7ffb\u5815\uff0c\u6551\u5df2\u65e0\u53ca\uff0c\u5353\u4e00\u822a\u5fc3\u4e2d \u6b63\u81ea\u6124\u6012\uff0c\u5ffd\u89c1\u90a3\u4e24\u540d\u9a91\u5ba2\u5728\u534a\u7a7a\u6253\u4e86\u4e2a\u6597\uff0c\u5c45\u7136\u5728\u843d\u5730\u4e4b\u524d\uff0c\u5404\u81ea\u63a5\u4e86\u4e00\u652f\u7fbd\u7bad\uff0c\u5c31\u7528\u8fd9 \u652f\u7fbd\u7bad\uff0c\u53c8\u62e8\u6253\u5f00\u51e0\u652f\u8fd1\u8eab\u7684\u4e71\u7bad\uff0c\u811a\u5c16\u4e00\u70b9\u524a\u58c1\uff0c\u5c45\u7136\u53c8\u7ffb\u4e0a\u6765\uff0c\u8fd9\u65f6\u4e71\u8349\u4e1b\u4e2d\uff0c\u5ca9\u77f3\u5806 \u91cc\uff0c\u7a81\u7136\u94bb\u51fa\u5341\u51e0\u6761\u5065\u6c49\uff0c\u6709\u7684\u5f20\u5f13\u5c04\u7bad\uff0c\u6709\u7684\u6325\u5200\u821e\u5251\uff0c\u7acb\u523b\u56f4\u653b\u8fd9\u4e24\u540d\u9a91\u5ba2\u3002 \u3000\u3000\u8fd9\u4e9b\u4eba\u90fd\u662f\u7f57\u5e03\u65cf\u4eba\u88c5\u626e\uff0c\u4e2a\u4e2a\u77eb\u5065\u975e\u5e38\uff0c\u5728\u5371\u5ca9\u4e71\u77f3\u7684\u524a\u58c1\u8fb9\u7f18\uff0c\u5c45\u7136\u884c\u52a8\u81ea\u5982\uff0c\u542c \u90a3\u55d6\u55d6\u7684\u7bad\u58f0\uff0c\u52b2\u9053\u66f4\u662f\u5341\u8db3\u3002\u5353\u4e00\u822a\u653e\u58f0\u559d\u9053\uff1a\u201c\u9752\u5929\u767d\u65e5\uff0c\u6d69\u8361\u4e7e\u5764\uff0c\u6076\u8d3c\u4f11\u5f97\u884c \u51f6\uff01\u201d\u62d4\u5251\u5954\u53bb\uff0c\u5ffd\u89c1\u90a3\u4e24\u540d\u9a91\u5ba2\uff0c\u7fe9\u5982\u5de8\u9e70\u822c\u63a0\u7a7a\u98de\u8fc7\uff0c\u63a5\u7740\u6709\u60e8\u53eb\u4e4b\u58f0\uff0c\u6709\u4e24\u540d\u7f57\u5e03\u65cf \u4eba\u5df2\u88ab\u4ed6\u4eec\u6253\u4e0b\u5e7d\u8c37\uff01 \u3000\u3000\u8fd9\u4e24\u540d\u9a91\u5ba2\u8131\u51fa\u5305\u56f4\uff0c\u7acb\u523b\u98de\u5954\uff0c\u80cc\u5f8c\u7684\u7f57\u5e03\u65cf\u4eba\u8854\u5c3e\u75be\u8ffd\uff0c\u9886\u5934\u7684\u4e00\u4eba\u5934\u9876\u63d2\u7740\u53c1\u6839 \u7fbd\u6bdb\uff0c\u5728\u5c71\u8def\u4e0a\u98de\u5954\uff0c\u5982\u5c65\u5e73\u5730\uff0c\u53ea\u89c1\u4ed6\u62c9\u5f00\u94c1\u5f13\uff0c\u55d6\u55d6\u55d6\u8fde\u73e0\u7bad\u7834\u7a7a\u5c04\u51fa\uff0c\u90a3\u4e24\u540d\u9a91\u5ba2\u5404 \u7528\u8170\u5200\u6321\u7bad\uff0c\u811a\u6b65\u7a0d\u7f13\uff0c\u770b\u770b\u5c31\u8981\u88ab\u7f57\u5e03\u65cf\u4eba\u8d76\u4e0a\u3002 \u3000\u3000\u5353\u4e00\u822a\u5927\u53eb\u9053\uff1a\u201c\u518d\u6321\u4e00\u9635\uff0c\u6211\u6765\u6551\u4f60\u4eec\uff01\u201d\u65bd\u5c55\u4e0a\u4e58\u8f7b\u529f\uff0c\u4ece\u5c71\u8170\u4e0a\u75be\u51b2\u800c\u4e0b\u3002\u5ffd\u89c1 \u90a3\u7f57\u5e03\u65cf\u4eba\u5df2\u8ffd\u5230\u4e24\u540d\u9a91\u5ba2\u80cc\u5f8c\uff0c\u62d4\u5200\u75be\u65a9\uff0c\u5176\u4ed6\u7684\u7f57\u5e03\u65cf\u4eba\u4e5f\u5c06\u8ffd\u5230\u3002\u4e24\u540d\u9a91\u5ba2\uff0c\u84e6\u7136\u56de \u8f6c\u5934\u6765\uff0c\u5927\u559d\u58f0\uff1a\u201c\u5012\u4e0b\uff01\u201d\u5176\u4e2d\u4e00\u4eba\u548c\u90a3\u7f57\u5e03\u65cf\u4eba\u62b1\u5728\u4e00\u8d77\uff0c\u7ffb\u7ffb\u6eda\u6eda\uff0c\u50cf\u4e24\u4e2a\u5927\u76ae\u7403\u6eda \u4e0b\u5c71\u5761\u3002 \u3000\u3000\u53e6\u4e00\u540d\u9a91\u5ba2\u8d81\u52bf\u5954\u9003\uff0c\u8fd9\u65f6\uff0c\u8ddd\u79bb\u5df2\u8fd1\uff0c\u5353\u4e00\u822a\u4e00\u773c\u671b\u53bb\uff0c\u53ea\u89c9\u8fd9\u540d\u9a91\u5ba2\u76f8\u8c8c\u751a\u719f\u3002\u90a3 \u9a91\u5ba2\u53eb\u9053\uff1a\u201c\u5353\u516c\u5b50\u6551\u6211\uff01\u201d\u6b64\u4eba\u975e\u4ed6\uff0c\u7adf\u662f\u4ee5\u524d\u7684\u9526\u8863\u536b\u90fd\u6307\u6325\u77f3\u6d69\uff01 \u3000\u3000\u8fd9\u4e00\u4e0b\u5927\u51fa\u5353\u4e00\u822a\u610f\u6599\u4e4b\u5916\uff0c\u4ed6\u66fe\u542c\u94c1\u98de\u9f99\u8bf4\u8fc7\uff0c\u77f3\u6d69\u591c\u5e26\u6ee1\u5dde\u4f7f\u8005\u6349\u62ff\u8881\u5d07\u7115\u4e4b\u4e8b\uff0c \u770b\u6765\u4ed6\u4e5f\u548c\u6ee1\u6d32\u9887\u6709\u5173\u7cfb\uff0c\u800c\u4eca\u60f3\u662f\u56e0\u4e3a\u5ba2\u9b4f\u5012\u4e86\uff0c\u6240\u4ee5\u9041\u9003\u585e\u5916\u3002\u5353\u4e00\u822a\u88ab\u4ed6\u4e00\u53eb\uff0c\u4e0d\u89c9 \u6115\u7136\uff0c\u5148\u524d\u7684\u63a8\u60f3\uff1a\u76d7\u8d3c\u4f0f\u52ab\u9a91\u5ba2\uff0c\u770b\u6765\u672a\u5fc5\u53ef\u9760\u3002\u8feb\u5207\u4e4b\u95f4\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff1f\u53ea\u542c\u5f97\u77f3\u6d69 \u53eb\u9053\uff1a\u201c\u4f60\u66ff\u6211\u6682\u6321\u8ffd\u5175\uff0c\u6211\u53bb\u6551\u90a3\u5144\u5f1f\u3002\u201d\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u600f\uff0c\u90a3\u7fa4\u7f57\u5e03\u65cf\u4eba\u5df2\u8ffd\u4e86\u4e0a\u6765\uff0c \u4e71\u7bad\u94bb\u5c04\uff01\u5353\u4e00\u822a\u8feb\u5f97\u8fd0\u5251\u9632\u8eab\uff0c\u77f3\u6d69\u51b7\u4e0d\u9632\u659c\u91cc\u7a9c\u51fa\uff0c\u6709\u624b\u4e00\u626c\uff0c\u6697\u5668\u75be\u98de\uff0c\u5411\u5c71\u5761\u4e0a\u6b63 \u5728\u548c\u4ed6\u540c\u4f34\u7f20\u6597\u7684\u90a3\u540d\u7f57\u5e03\u65cf\u4eba\u5c04\u53bb\u3002\u542c\u90a3\u6697\u5668\u5636\u98ce\u4e4b\u58f0\uff0c\u4f3c\u662f\u84ba\u85dc\u4e4b\u7c7b\u7684\u6697\u5668\uff0c\u800c\u4e14\u662f\u8fde \u73af\u53d1\u51fa\u3002\u5353\u4e00\u822a\u53eb\u9053\uff1a\u201c\u77f3\u6d69\u4e14\u6162\u52a8\u624b\uff01\u201d\u628a\u624b\u4e00\u6284\uff0c\u8fde\u63a5\u7f57\u5e03\u65cf\u4eba\u5c04\u6765\u7684\u53c1\u679d\u94c1\u7bad\uff0c\u5411\u77f3 \u6d69\u90a3\u8fb9\u4e00\u7529\uff0c\u628a\u4ed6\u5f8c\u6765\u6240\u53d1\u7684\u51e0\u679d\u6697\u5668\u6253\u843d\uff0c\u53ef\u662f\u5148\u524d\u90a3\u679d\u6697\u5668\uff0c\u5df2\u5c04\u5230\u4e86\u90a3\u4f4d\u7f57\u5e03\u65cf\u9996\u9886 \u7684\u8eab\u4e0a\u3002 \u3000\u3000\u7f57\u5e03\u65cf\u4eba\u7eb7\u7eb7\u6012\u53eb\uff0c\u77f3\u6d69\u8d81\u7740\u4ed6\u4eec\u548c\u5353\u4e00\u822a\u52a8\u624b\u53ca\u53bb\u6551\u4ed6\u4eec\u914b\u957f\u7684\u65f6\u5019\uff0c\u6025\u6025\u98de\u5954\u800c \u9003\u3002\u7f57\u5e03\u65cf\u4eba\u8ffd\u4e4b\u4e0d\u53ca\uff0c\u5374\u7eb7\u7eb7\u6765\u6251\u653b\u5353\u4e00\u822a\uff01 \u3000\u3000\u5353\u4e00\u822a\u53eb\u9053\uff1a\u201c\u8bf7\u4f60\u4eec\u606f\u6012\uff0c\u6211\u548c\u4ed6\u4e0d\u662f\u4e00\u8def\uff01\u201d\u7f57\u5e03\u65cf\u4eba\u90a3\u80af\u76f8\u4fe1\uff0c\u8fb9\u6253\u8fb9\u559d\u9a82\u9053\uff1a \u201c\u4f60\u4eec\u6f5c\u4eba\u8349\u539f\u5174\u6ce2\u4f5c\u6d6a\uff0c\u505a\u6ee1\u5dde\u4eba\u7684\u5185\u5e94\uff0c\u800c\u4eca\u53c8\u4f24\u4e86\u6211\u4eec\u7684\u914b\u957f\uff0c\u975e\u628a\u4f60\u4eec\u788e\u4e07\u6bb5\uff0c\u6211 \u4eec\u4e5f\u4e0d\u7b97\u662f\u82f1\u96c4\u7684\u7f57\u5e03\u65cf\u4eba\uff01\u201d\u5353\u4e00\u822a\u6697\u53eb\u4e00\u58f0\u82e6\u4e5f\uff0c\u60f3\u4e0d\u5230\u88ab\u77f3\u6d69\u6697\u5668\u6240\u4f24\u7684\uff0c\u7adf\u662f\u5728\u8349 \u539f\u4e0a\u6700\u6709\u58f0\u671b\u7684\u7f57\u5e03\u65cf\u914b\u957f\uff0c\u82f1\u96c4\u5510\u52aa\uff01 \u3000\u3000\u5353\u4e00\u822a\u4ed7\u7740\u4e0a\u4e58\u8f7b\u529f\uff0c\u4e1c\u8eb2\u897f\u95ea\uff0c\u4e00\u9762\u5077\u7a7a\u7aa5\u63a2\uff0c\u53ea\u89c1\u77f3\u6d69\u7684\u540c\u4f34\u9a91\u5728\u5510\u52aa\u8eab\u4e0a\uff0c\u8170\u5200 \u5f80\u4e0b\u529b\u65ab\uff0c\u5510\u52aa\u7528\u529b\u6258\u7740\u4ed6\u7684\u624b\u8155\uff0c\u62da\u547d\u6323\u624e\u3002\u7f57\u5e03\u65cf\u7684\u51e0\u540d\u6b66\u58eb\uff0c\u521a\u8981\u5954\u53bb\u89e3\u6551\uff0c\u5c1a\u672a\u5230 \u4e24\u4eba\u773c\u524d\uff0c\u5ffd\u542c\u5f97\u90a3\u4eba\u5927\u559d\u9053\uff1a\u201c\u4f60\u4eec\u518d\u4e0a\u524d\u4e00\u6b65\uff0c\u6211\u5c31\u628a\u4f60\u4eec\u914b\u957f\u7684\u9996\u7ea7\u5272\u4e0b\uff01\u201d\u7f57\u5e03\u65cf \u7684\u6b66\u58eb\u867d\u7136\u6124\u6068\u586b\u80f8\uff0c\u5374\u88ab\u4ed6\u7684\u58f0\u52bf\u5413\u4f4f\uff0c\u6295\u9f20\u5fcc\u5668\uff0c\u4e0d\u6562\u5411\u524d\uff01 \u3000\u3000\u5353\u4e00\u822a\u89c1\u52bf\u5371\u6025\uff0c\u9661\u7136\u632f\u5251\u4e00\u6c64\uff0c\u53ea\u542c\u5f97\u4e00\u9635\u65ad\u91d1\u621b\u7389\u4e4b\u58f0\uff0c\u8fd1\u8eab\u51e0\u540d\u7f57\u5e03\u65cf\u6b66\u58eb\u7684\u5200 \u5251\u5df2\u88ab\u524a\u65ad\u3002\u60ca\u53eb\u8d77\u6765\uff0c\u8feb\u5f97\u5f8c\u9000\u3002\u5353\u4e00\u822a\u4e58\u52bf\u51b2\u51fa\uff0c\u76f4\u5954\u5510\u52aa\u3002\u5510\u52aa\u9644\u8fd1\u7684\u90a3\u51e0\u540d\u6b66\u58eb\u4e0a \u524d\u8fce\u654c\uff0c\u5353\u4e00\u822a\u75be\u5982\u98de\u7bad\uff0c\u8eab\u5f62\u98d8\u5ffd\uff0c\u4e00\u5f2f\u4e00\u7ed5\uff0c\u4ece\u8fce\u654c\u8005\u7684\u8eab\u65c1\u75be\u7a7f\u51fa\u53bb\u3002\u77f3\u6d69\u7684\u540c\u4f34\u4ee5 \u4e3a\u4ed6\u662f\u540c\u4e00\u8def\u4e4b\u4eba\uff0c\u5927\u559c\u53eb\u9053\uff1a\u201c\u4e0d\u7528\u8fc7\u6765\u4e86\uff0c\u6211\u6ca1\u53d7\u4f24\uff0c\u4f60\u66ff\u6211\u5f00\u8def\uff0c\u54b1\u4eec\u51b2\u51fa\u53bb\u3002\u201d\u5353 \u4e00\u822a\u4e0d\u58f0\u4e0d\u54cd\uff0c\u53cc\u6307\u4e00\u5f39\uff0c\u628a\u6697\u4e2d\u6263\u7740\u7684\u6885\u82b1\u9488\u9aa4\u5c04\u51fa\u53bb\u3002\u90a3\u4eba\u558a\u58f0\u672a\u6bd5\uff0c\u624b\u8155\u7a81\u7136\u4e00\u75db\uff0c \u8170\u5200\u843d\u5730\uff0c\u5510\u52aa\u632f\u8d77\u795e\u5a01\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u7ffb\u8d77\u8eab\u6765\uff0c\u6307\u987e\u4e4b\u95f4\uff0c\u4e3b\u5ba2\u6613\u52bf\uff0c\u5012\u9a91\u5728\u90a3\u4eba\u8eab\u4e0a\u3002 \u3000\u3000\u4e0e\u77f3\u6d69\u540c\u884c\u7684\u90a3\u540d\u9a91\u5ba2\uff0c\u540d\u53eb\u79d1\u56fe\uff0c\u4e43\u662f\u6ee1\u5dde\u6d3e\u5230\u5580\u8fbe\u5c14\u65cf\u7684\u4f7f\u8005\uff0c\u6b66\u529f\u59d4\u5b9e\u4e0d\u5f31\uff0c\u867d \u7136\u9aa4\u88ab\u51fb\u5012\uff0c\u4ecd\u662f\u987d\u5f3a\u62b5\u6297\u3002\u5510\u52aa\u4e2d\u4e86\u77f3\u6d69\u7684\u6697\u5668\u6bd2\u84ba\u85dc\uff0c\u8fd9\u65f6\u5df2\u7ecf\u53d1\u4f5c\uff0c\u7528\u529b\u8fc7\u731b\uff0c\u5ffd\u89c9 \u5934\u660f\u773c\u82b1\uff0c\u79d1\u56fe\u5de6\u81c2\u6a2a\u80b1\u6297\u7740\u5510\u52aa\u7684\u4e0b\u51fb\u4e4b\u52bf\uff0c\u53f3\u624b\u4e94\u6307\u5982\u94a9\uff0c\u529b\u53c9\u5510\u52aa\u54bd\u5589\u3002 \u3000\u3000\u5353\u4e00\u822a\u98de\u9488\u53d1\u51fa\uff0c\u4e00\u63a0\u800c\u524d\uff0c\u6765\u5f97\u6b63\u662f\u65f6\u5019\uff0c\u9a88\u6307\u5411\u79d1\u56fe\u80c1\u4e0b\u4e00\u622e\uff0c\u79d1\u56fe\u5168\u8eab\u8f6f\uff0c\u4ecd\u7136 \u624b\u6307\u5c48\u66f2\u5982\u94a9\uff0c\u5374\u5df2\u52a8\u5f39\u4e0d\u5f97\uff01 \u3000\u3000\u5353\u4e00\u822a\u7684\u98de\u9488\u70b9\u7a74\uff0c\u5374\u662f\u8fc5\u75be\u5f02\u5e38\uff0c\u7f57\u5e03\u65cf\u7684\u6b66\u58eb\u4e0d\u77e5\u79d1\u56fe\u4e4b\u88ab\u51fb\u5012\uff0c\u4e43\u662f\u5353\u4e00\u822a\u7684\u529f \u52b3\uff0c\u4ecd\u7136\u8702\u62e5\u800c\u6765\uff0c\u5200\u67aa\u7eb7\u4e3e\u3002\u5510\u52aa\u5728\u5730\u4e0a\u6323\u624e\u5750\u8d77\uff0c\u5636\u58f0\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u6069\u4eba\uff01\u201d \u3000\u3000\u7f57\u5e03\u65cf\u7684\u6b66\u58eb\u5927\u4e3a\u60ca\u6115\uff0c\u6709\u4eba\u53eb\u9053\uff1a\u201c\u4ed6\u540c\u6211\u4eec\u6253\uff0c\u653e\u8d70\u4e86\u6ee1\u5dde\u5978\u7ec6\uff0c\u5982\u4f55\u53cd\u662f\u6069 \u4eba\uff1f\u201d\u5510\u52aa\u4e5f\u731c\u4e0d\u900f\u5353\u4e00\u822a\u6765\u610f\uff0c\u9053\uff1a\u201c\u4f60\u6551\u4e86\u6211\uff0c\u6211\u7edd\u4e0d\u4f1a\u5bf9\u4f60\u4e3a\u96be\uff0c\u4f46\u6211\u5012\u8981\u8bf7\u6559\uff1a\u4f60 \u6551\u4e86\u6211\uff0c\u53c8\u653e\u4e86\u6ee1\u6d32\u5978\u7ec6\uff0c\u5374\u662f\u4e3a\u4f55\uff1f\u201d\u5353\u4e00\u822a\u597d\u751f\u96be\u8fc7\uff0c\u5ffd\u7136\u4ece\u767e\u5b9d\u56ca\u4e2d\u53d6\u51fa\u4e00\u4e2a\u7f8a\u8102\u767d \u7389\u74f6\uff0c\u5c06\u91cc\u9762\u7684\u836f\u7c89\u6311\u4e86\u4e00\u4e9b\u51fa\u6765\uff0c\u653e\u5728\u4e00\u7247\u624b\u638c\u822c\u5927\u7684\u6811\u53f6\u4e0a\u3002\u7f57\u5e03\u65cf\u7684\u6b66\u58eb\u559d\u9053\uff1a\u201c\u4f60 \u5e72\u4ec0\u9ebd\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u4eec\u7684\u738b\u7237\u4e2d\u4e86\u6bd2\u84ba\u85dc\u4e86\uff0c\u62ff\u8fd9\u5305\u836f\u53bb\uff0c\u4e00\u9762\u5916\u670d\uff0c\u4e00\u9762\u5185\u6577\uff0c\u5341\u4e8c\u4e2a\u65f6 \u8fb0\u4e4b\u5f8c\uff0c\u53ef\u4ee5\u6062\u590d\u5982\u521d\u3002\u201d\u7f57\u5e03\u65cf\u7684\u6b66\u58eb\u5bf9\u5353\u4e00\u822a\u5c1a\u672a\u76f8\u4fe1\uff0c\u4e0d\u6562\u5373\u63a5\u3002\u5510\u52aa\u9053\uff1a\u201c\u62ff\u6765\u7ed9 \u6211\uff01\u4ed6\u82e5\u8981\u5bb3\u6211\uff0c\u4f55\u5fc5\u5982\u4eca\uff1f\u201d\u5510\u52aa\u8bf4\u8bdd\u5766\u7387\u4e4b\u6781\uff0c\u4e00\u53e3\u9053\u7834\u90e8\u4e0b\u7684\u7591\u8651\u3002\u5353\u4e00\u822a\u89c1\u4ed6\u76f8\u4fe1 \u81ea\u5df1\uff0c\u751a\u4e3a\u611f\u6fc0\u3002\u5510\u52aa\u63a5\u8fc7\u89e3\u836f\uff0c\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u53ef\u60dc\u8981\u5341\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u4e0d\u80fd\u53bb\u8ffd\u90a3\u6ee1\u6d32\u5978\u7ec6 \u4e86\uff01\u201d\u63a5\u7740\u53c8\u95ee\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u6551\u4e86\u6211\u7684\u6027\u547d\uff0c\u5374\u53c8\u653e\u4e86\u6211\u7684\u654c\u4eba\uff0c\u7a76\u7adf\u662f\u4f55\u9053\u7406\uff1f\u201d\u5353\u4e00 \u822a\u4e00\u770b\u65e5\u5f71\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u66ff\u4f60\u5c06\u5978\u7ec6\u62ff\u56de\u6765\u4fbf\u662f\uff01\u4f60\u4eec\u7559\u4e0b\u4e00\u4e9b\u4eba\u6765\u5728\u8fd9\u91cc\u7b49\u5019\uff0c\u6211\u9ec4 \u660f\u65f6\u5206\uff0c\u4fbf\u53ef\u540c\u6765\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u7f57\u5e03\u65cf\u6b66\u58eb\u90fd\u9732\u51fa\u4e0d\u76f8\u4fe1\u7684\u795e\u6c14\uff0c\u4ed6\u4eec\u773c\u89c1\u77f3\u6d69\u811a\u7a0b\u751a \u600f\uff0c\u8fc7\u4e86\u8fd9\u9ebd\u4e9b\u65f6\u5019\uff0c\u5c11\u8bf4\u4e5f\u5df2\u8d70\u4e86\u5341\u591a\u91cc\u7684\u5c71\u8def\uff0c\u5982\u4f55\u8fd8\u80fd\u8ffd\u8d76\u5f97\u4e0a\uff1f\u5353\u4e00\u822a\u65e0\u6687\u591a\u8bf4\uff0c \u62d4\u811a\u4fbf\u8dd1\uff0c\u53ea\u542c\u5f97\u5510\u52aa\u53eb\u9053\uff1a\u201c\u4f60\u62ff\u4e86\u5978\u7ec6\uff0c\u4e0d\u5fc5\u56de\u6765\uff0c\u4ea4\u7ed9\u5df4\u9f99\u5427\uff0c\u5df4\u9f99\u5728\u6700\u5916\u9762\u90a3\u91cd\u5173 \u53e3\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u5fc3\u4e2d\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u539f\u6765\u4ed6\u4eec\u662f\u7ea6\u597d\u4e86\u5728\u5c71\u5916\u5c71\u5185\u9669\u8981\u4e4b\u5904\u57cb\u4f0f\uff0c\u6349\u62ff\u5978\u7ec6\u7684\u3002 \u6211\u6b63\u8981\u89c1\u5df4\u9f99\uff0c\u62ff\u77f3\u6d69\u8fd9\u5f53\u89c1\u9762\u793c\uff0c\u6b63\u662f\u4e00\u4e3e\u4e24\u5f97\u3002\u201d\u7acb\u523b\u65bd\u5c55\u4e0a\u4e58\u8f7b\u529f\uff0c\u5982\u98de\u8ffd\u8d76\u3002 \u3000\u3000\u6155\u58eb\u5854\u683c\u5c71\u867d\u662f\u5929\u5c71\u652f\u8109\uff0c\u4e5f\u7ef5\u4e98\u4e00\u767e\u9980\u91cc\uff0c\u90a3\u6761\u5386\u4ee3\u5c71\u6c11\u6240\u5f00\u51ff\u7684\u5c71\u8def\uff0c\u8fc2\u56de\u66f2\u6298\uff0c \u66f4\u4e0d\u6b62\u767e\u91cc\u3002\u5353\u4e00\u822a\u8fd1\u5e74\u6b66\u529f\u5927\u8fdb\uff0c\u53c8\u884c\u60ef\u5c71\u8def\uff0c\u5fc3\u60f3\u77f3\u6d69\u8f7b\u529f\u867d\u597d\uff0c\u4f46\u5c1a\u4e0d\u5982\u81ea\u5df1\uff0c\u4f30\u91cf \u65e0\u8bba\u5982\u4f55\uff0c\u5728\u4ed6\u672a\u51fa\u6155\u58eb\u5854\u683c\u5c71\u4e4b\u524d\uff0c\u4e00\u5b9a\u80fd\u5c06\u4ed6\u8ffd\u4e0a\u3002 \u3000\u3000\u8ffd\u4e86\u7ea6\u4e00\u4e2a\u65f6\u8fb0\uff0c\u77f3\u6d69\u7684\u80cc\u5f71\u5df2\u9690\u9690\u53ef\u89c1\uff0c\u5353\u4e00\u822a\u53eb\u9053\uff1a\u201c\u77f3\u6d69\uff0c\u662f\u6211\u6765\u4e86\uff0c\u4f60\u7b49\u4e00 \u7b49\uff0c\u54b1\u4eec\u505a\u4e2a\u540c\u4f34\u3002\u201d\u77f3\u6d69\u6beb\u4e0d\u7406\u776c\uff0c\u4ecd\u5411\u524d\u8dd1\u3002\u5353\u4e00\u822a\u5fc3\u9053\uff1a\u201c\u770b\u4ed6\u5982\u6b64\uff0c\u679c\u662f\u5fc3\u865a\uff0c\u5510 \u52aa\u8bf4\u4ed6\u662f\u6ee1\u6d32\u5978\u7ec6\uff0c\u4e0d\u4f1a\u51a4\u8d56\u4ed6\u4e86\u3002\u54fc\uff0c\u4f60\u4e0d\u7b49\u6211\uff0c\u96be\u9053\u6211\u5c31\u8ffd\u4f60\u4e0d\u4e0a\uff1f\u201d\u811a\u6b65\u4e00\u7d27\uff0c\u8ffd\u5f97 \u66f4\u5feb\u3002 \u3000\u3000\u53c8\u8ffd\u8fc7\u4e86\u4e24\u5904\u5c71\u53e3\uff0c\u76f8\u8ddd\u76ca\u8fd1\u3002\u77f3\u6d69\u5ffd\u7136\u957f\u5578\u96e8\u58f0\uff0c\u84e6\u7136\u505c\u6b65\u3002\u56de\u5934\u7b11\u9053\uff1a\u201c\u5353\u4e00\u822a\uff0c \u4f60\u8ffd\u6211\u5e72\u561b\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u6599\u5b9a\u77f3\u6d69\u5df2\u662f\u7b3c\u4e2d\u4e4b\u9e1f\uff0c\u7f51\u4e2d\u4e4b\u9c7c\uff0c\u7d22\u6027\u6253\u5f00\u5929\u7a97\u8bf4\u4eae\u8bdd\uff0c\u4e0d\u518d\u548c\u4ed6\u5ba2\u5957\uff0c\u51b7\u51b7 \u7b11\u9053\uff1a\u201c\u6211\u4e3a\u4f55\u8ffd\u4f60\uff0c\u4f60\u81ea\u5df1\u5e94\u8be5\u77e5\u9053\u3002\u201d\u77f3\u6d69\u5b09\u76ae\u7b11\u8138\uff0c\u53cc\u80a9\u4e00\u8038\uff0c\u644a\u638c\u7b11\u9053\uff1a\u201c\u6211\u53c8\u4e0d \u662f\u4f60\u809a\u91cc\u86d4\u866b\uff0c\u600e\u80fd\u77e5\u9053\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u90a3\u4e2a\u540c\u4f34\u662f\u4ec0\u9ebd\u4eba\uff01\u201d\u77f3\u6d69\u7b11\u9053\uff1a\u201c\u5353\u516c\u5b50\uff0c \u4f60\u4f55\u5fc5\u591a\u7406\u95f2\u4e8b\uff1f\u201d\u5353\u4e00\u822a\u677f\u8138\u8bf4\u9053\uff1a\u201c\u8fd9\u6b21\u6211\u504f\u504f\u8981\u7406\u3002\u4f60\u8bf4\uff0c\u4f60\u90a3\u4f4d\u540c\u4f34\u662f\u4e0d\u662f\u6ee1\u5dde\u6d3e \u6765\u7684\u4f7f\u8005\uff1f\u201d\u77f3\u6d69\u51b7\u7b11\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\uff1f\u201d\u5353\u4e00\u822a\u6012\u6c14\u4e0a\u51b2\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u8981\u6211\u52a8\u624b\u5417\uff1f\u8ddf\u6211 \u56de\u53bb\uff01\u201d\u77f3\u6d69\u5927\u7b11\u9053\uff1a\u201c\u5353\u516c\u5b50\uff0c\u4f60\u653e\u7740\u597d\u597d\u7684\u638c\u95e8\u4e0d\u505a\uff0c\u5374\u5230\u8fd9\u7a77\u8fb9\u585e\u5916\uff0c\u4e71\u7ba1\u95f2\u4e8b\u3002\u54c8 \u54c8\uff0c\u53ef\u60dc\u4f60\u6765\u5f97\u8fdf\u4e86\uff0c\u8fd9\u95f2\u4e8b\u8f6e\u4e0d\u5230\u4f60\u7ba1\u5566\uff1f\u201d \u3000\u3000\u77f3\u6d69\u8bdd\u58f0\u672a\u6b47\uff0c\u53ea\u542c\u5f97\u6709\u4eba\u53eb\u9053\uff1a\u201c\u77f3\u5927\u54e5\uff0c\u8fd9\u5c0f\u5b50\u662f\u4ec0\u9ebd\u4eba\uff1f\u4ed6\u8981\u7ba1\u4ec0\u9ebd\u95f2\u4e8b\u554a\uff01\u201d \u63a5\u7740\u53c8\u6709\u4e00\u4e2a\u756a\u50e7\u5495\u54e9\u5495\u565c\u7684\u559d\u9a82\u58f0\uff0c\u5c71\u5773\u5904\u540c\u65f6\u94bb\u51fa\u4e24\u4e2a\u4eba\u6765\uff0c\u4e00\u4e2a\u662f\u54c8\u8428\u514b\u6b66\u58eb\u7684\u88c5 \u626e\uff0c\u4e00\u4e2a\u5374\u662f\u62ab\u7740\u5927\u7ea2\u50e7\u888d\u7684\u5934\u9640\u3002 \u3000\u3000\u77f3\u6d69\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u6765\u5934\u53ef\u4e0d\u5c0f\u5462\uff0c\u4ed6\u662f\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\uff01\u201d\u90a3\u5934\u9640\u53cc\u773c\u4e00\u7ffb\uff0c\u76ef\u4e86\u5353\u4e00\u822a \u4e00\u773c\uff0c\u5ffd\u7136\u7528\u751f\u786c\u7684\u6c49\u8bed\u8bf4\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\uff0c\u4f60\u662f\uff1f\u4e45\u95fb\u6b66\u5f53\u6d3e\u7684\u6b66\u529f\uff0c\u5728\u4e2d\u539f \u53f7\u79f0\u7b2c\u4e00\uff0c\u4ffa\u5012\u8981\u548c\u4f60\u8f83\u91cf\u8f83\u91cf\u3002\u201d \u3000\u3000\u77f3\u6d69\u9053\uff1a\u201c\u5353\u516c\u5b50\uff0c\u6211\u770b\u5728\u4f60\u9002\u624d\u66ff\u6211\u6253\u63a9\u62a4\u7684\u4efd\u4e0a\uff0c\u4e0d\u613f\u6740\u4f60\uff0c\u4f60\u5feb\u6eda\u51fa\u56de\u7586\uff0c\u56de\u6b66 \u5f53\u5c71\u53bb\u5427\uff0c\u8fd9\u91cc\u6ca1\u6709\u4f60\u79f0\u5f3a\u9053\u9738\u7684\u5730\u65b9\uff01\u201d\u5353\u4e00\u822a\u65a5\u9053\uff1a\u201c\u4e71\u81e3\u8d3c\u5b50\uff0c\u4eba\u4eba\u5f97\u800c\u8bdb\u4e4b\uff0c\u5c11\u8bf4 \u5e9f\u8bdd\uff0c\u4f60\u4eec\u53c1\u4eba\u4e00\u9f50\u6765\u5427\uff01\u201d \u3000\u3000\u77f3\u6d69\u5f97\u4e86\u63a5\u5e94\uff0c\u5fc3\u4e2d\u5927\u5b9a\uff0c\u6162\u6761\u65ad\u7406\u7684\u8bf4\u9053\uff1a\u201c\u5353\u516c\u5b50\uff0c\u4f60\u8981\u6253\u5417\uff1f\u54b1\u4eec\u4e5f\u8be5\u5148\u901a\u901a\u540d \u5440\uff0c\u6211\u7ed9\u4f60\u5f15\u89c1\u5f15\u89c1\u3002\u8fd9\u4f4d\u662f\u5929\u9f99\u4e0a\u4eba\u7684\u9996\u5f92\u96f7\u8499\u6cd5\u5e08\uff0c\u5929\u9f99\u6d3e\u5728\u585e\u5916\u7684\u52bf\u529b\u5c31\u5982\u4f60\u4eec\u6b66\u5f53 \u6d3e\u5728\u4e2d\u539f\u7684\u52bf\u529b\u4e00\u6837\uff0c\u8fd9\u91cc\u662f\u4ed6\u4eec\u7684\u5730\u5934\uff0c\u4e0d\u662f\u4f60\u7684\u5730\u5934\uff0c\u4f60\u53ef\u5f97\u653e\u660e\u767d\u70b9\u3002\u8fd8\u6709\u8fd9\u4e00\u4f4d\uff0c \u662f\u54c8\u8428\u514b\u7740\u540d\u7684\u6b66\u58eb\u54c8\u5ddd\uff0c\u4f60\u5230\u8fd9\u513f\u6709\u591a\u4e45\u4e86\uff0c\u8fd8\u4e3a\u542c\u8fc7\u4ed6\u7684\u540d\u5934\u5417\uff1f\u201d \u3000\u3000\u77f3\u6d69\u4e5f\u6df1\u77e5\u6b66\u5f53\u6d3e\u7684\u6b66\u529f\u5389\u5bb3\uff0c\u6240\u4ee5\u5148\u7528\u8bf4\u8bdd\u8981\u6fc0\u5353\u4e00\u822a\u706b\u8d77\u3002\u5353\u4e00\u822a\u8fd9\u51e0\u5e74\u6765\u9605\u5386\u4e0e \u6b66\u529f\u4ff1\u589e\uff0c\u4eba\u6bd4\u4ee5\u524d\u6c89\u7740\u8bb8\u591a\uff0c\u90a3\u4f1a\u4e2d\u4ed6\u5708\u5957\uff0c\u4e00\u9762\u542c\u4ed6\u8bf4\u8bdd\uff0c\u4e00\u9762\u51dd\u795e\u5f85\u654c\u3002\u679c\u7136\u96f7\u8499\u6cd5 \u5e08\u4e58\u7740\u4ed6\u4eec\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u7a81\u7136\u6697\u88ad\u3002\u628a\u624b\u4e00\u626c\uff0c\u6253\u51fa\u7ea2\u6559\u5587\u561b\u7684\u72ec\u95e8\u6697\u5668\u201c\u6eda\u5200\u73af\u201d\uff0c\u90a3\u73af \u534a\u5f84\u4e0d\u8fc7\u4e94\u5bf8\uff0c\u5185\u4e2d\u5374\u5d4c\u7740\u5341\u4e8c\u628a\u5c0f\u5200\uff0c\u8fd1\u654c\u4e4b\u65f6\uff0c\u5341\u4e8c\u628a\u5c0f\u5200\u53ef\u4ee5\u540c\u65f6\u5c04\u51fa\uff0c\u5353\u4e00\u822a\u542c\u5f97 \u90a3\u6697\u5668\u5e26\u7740\u545c\u545c\u58f0\u54cd\uff0c\u6a2a\u98de\u8fc7\u6765\uff0c\u500f\u5730\u7eb5\u8eab\uff0c\u65bd\u5c55\u201c\u4e00\u9e64\u51b2\u5929\u201d\u7684\u8f7b\u529f\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u76f4\u8fce\u4e0a \u53bb\uff0c\u5b9d\u5251\u8f7b\u8f7b\u4e00\u6311\uff0c\u628a\u90a3\u53e3\u201c\u5200\u73af\u201d\u6311\u8d77\u56db\u4e94\u4e08\u9ad8\uff0c\u73af\u5185\u7684\u5341\u4e8c\u628a\u5c0f\u5200\u5728\u534a\u7a7a\u4e2d\u5c04\u51fa\uff0c\u90fd\u5982 \u6d41\u661f\u6b92\u77f3\u822c\u5815\u4e0b\u5c71\u8c37\u4e2d\u53bb\u4e86\u3002 \u3000\u3000\u96f7\u8499\u6cd5\u5e08\u52c3\u7136\u5927\u6012\uff0c\u6756\u4e00\u6446\uff0c\u4fbf\u626b\u8fc7\u6765\uff0c\u5353\u4e00\u822a\u5fc3\u9053\uff1a\u201c\u4f60\u7684\u5e08\u53d4\u5c1a\u4e0d\u662f\u6211\u7684\u5bf9\u624b\uff0c\u4f60 \u6562\u7316\u72c2\uff1f\u201d\u5c82\u6599\u96f7\u8499\u6cd5\u5e08\u867d\u662f\u5929\u5fb7\u4e0a\u4eba\u7684\u5e08\u4f84\uff0c\u4f46\u4ed6\u4e43\u5929\u9f99\u6d3e\u5b97\u4e3b\u7684\u9996\u5f92\uff0c\u5929\u9f99\u4e0a\u4eba\u7684\u6b66\u529f \u6bd4\u5e08\u5f1f\u4eec\u9ad8\u51fa\u8bb8\u591a\uff0c\u6240\u4ee5\u96f7\u8499\u6cd5\u5e08\u548c\u5e08\u53d4\u4eec\u7adf\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u5353\u4e00\u822a\u4e00\u5ff5\u8f7b\u654c\uff0c\u51e0\u4e4e\u7ed9\u4ed6\u7684\u6756\u5c06 \u5b9d\u5251\u6253\u98de\u3002 \u3000\u3000\u96f7\u8499\u6cd5\u5e08\u54c8\u54c8\u5927\u7b11\uff0c\u9053\uff1a\u201c\u89c1\u9762\u80dc\u4f3c\u95fb\u540d\uff0c\u6b66\u5f53\u638c\u95e8\u4ea6\u4e0d\u8fc7\u5982\u6b64\uff01\u201d\u7b11\u58f0\u672a\u6bd5\uff0c\u51b7\u4e0d\u9632 \u5353\u4e00\u822a\u4e00\u5251\u523a\u6765\uff0c\u53c8\u72e0\u53c8\u51c6\uff0c\u96f7\u8499\u6cd5\u5e08\u6a2a\u6756\u6321\u65f6\uff0c\u5353\u4e00\u822a\u5251\u8bc0\u4e00\u9886\uff0c\u5de6\u4e00\u5251\u201c\u5b54\u96c0\u5254\u7fce\u201d\uff0c \u53f3\u4e00\u5251\u201c\u674e\u5e7f\u5c04\u77f3\u201d\uff0c\u55e4\u55e4\u4e24\u58f0\uff0c\u628a\u96f7\u8499\u6cd5\u5e08\u50e7\u888d\u7684\u675f\u5e26\u5272\u65ad\uff0c\u8bf4\u9053\uff1a\u201c\u6b66\u5f53\u6d3e\u7684\u5251\u6cd5\u5982 \u4f55\uff1f\u201d\u96f7\u8499\u6cd5\u5e08\u5927\u5403\u4e00\u60ca\uff0c\u505a\u58f0\u4e0d\u5f97\u3002\u5353\u4e00\u822a\u8fd0\u5251\u5982\u98ce\uff0c\u7740\u7740\u8fdb\u8feb\uff0c\u96f7\u8499\u6cd5\u5e08\u6c14\u53d7\u632b\uff0c\u66f4\u517c \u50e7\u888d\u655e\u5f00\uff0c\u963b\u624b\u788d\u811a\uff0c\u88ab\u5353\u4e00\u822a\u6740\u5f97\u624b\u5fd9\u811a\u4e71\uff01 \u3000\u3000\u77f3\u6d69\u672c\u4ee5\u4e3a\u96f7\u8499\u7eb5\u96be\u53d6\u80dc\uff0c\u4ea6\u4e0d\u6613\u843d\u8d25\uff0c\u89c1\u72b6\u5927\u60ca\uff0c\u62d4\u5200\u52a9\u6218\u3002\u5353\u4e00\u822a\u6068\u6781\u77f3\u6d69\uff0c\u865a\u67b6 \u96f7\u8499\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u76f4\u53d6\u77f3\u6d69\u3002\u77f3\u6d69\u4ee5\u524d\u5728\u9b4f\u5fe0\u8d24\u624b\u4e0b\uff0c\u4ec5\u6b21\u65bc\u6155\u5bb9\u51b2\u8fde\u57ce\u864e\u674e\u5929\u626c\u5e94\u4fee\u9633\u56db \u4eba\uff0c\u800c\u540d\u5217\u7b2c\u4e94\uff0c\u6b66\u529f\u81ea\u662f\u4e0d\u5f31\uff0c\u6321\u4e86\u51e0\u62db\uff0c\u5404\u65e0\u8fdb\u5c55\u3002\u96f7\u8499\u8fd0\u6756\u53cd\u51fb\uff0c\u4ee5\u4e8c\u654c\u4e00\uff0c\u582a\u582a\u6253 \u6210\u5e73\u624b\u3002 \u3000\u3000\u54c8\u8428\u514b\u90a3\u540d\u6b66\u58eb\u54c8\u5ddd\u89c1\u5353\u4e00\u822a\u5251\u6cd5\u51cc\u5389\uff0c\u4e5f\u8df3\u4e0a\u524d\u6765\u52a9\u6218\uff0c\u4ed6\u624b\u63d0\u72ec\u811a\u94dc\u4eba\uff0c\u4e00\u4e0a\u6765\u4fbf \u662f\u4e00\u62db\u201c\u6cf0\u5c71\u538b\u9876\u201d\uff0c\u5f53\u5934\u7838\u4e0b\u3002\u5353\u4e00\u822a\u89c1\u4ed6\u4e00\u8eab\u86ee\u529b\uff0c\u4e0d\u6562\u786c\u63a5\uff0c\u4e00\u95ea\u95ea\u5f00\uff0c\u4ee5\u4e3a\u6709\u86ee\u529b \u4e4b\u4eba\uff0c\u8f7b\u529f\u5fc5\u5b9a\u8f83\u5f31\uff0c\u4e00\u95ea\u4e4b\u5f8c\uff0c\u4fbf\u7acb\u523b\u5251\u8d70\u659c\u8fb9\uff0c\u53d6\u4ed6\u4e0b\u76d8\uff0c\u90a3\u77e5\u54c8\u5ddd\u6b66\u529f\uff0c\u53e6\u6210\u4e00\u5bb6\uff0c \u4ed6\u8f7b\u529f\u786e\u662f\u5e73\u5e73\uff0c\u4f46\u5374\u7cbe\u65bc\u201c\u6454\u8de4\u201d\u4e4b\u6280\uff0c\u5353\u4e00\u822a\u6b3a\u8eab\u76f4\u8fdb\uff0c\u84e6\u7136\u7ed9\u4ed6\u4f38\u811a\u4e00\u52fe\uff0c\u8eab\u5b50\u503e \u659c\uff0c\u5251\u52bf\u5931\u4e86\u51c6\u5934\uff0c\u54c8\u5ddd\u4e00\u58f0\u72de\u7b11\uff0c\u72ec\u811a\u94dc\u4eba\u5bf9\u80f8\u4fbf\u649e\uff0c\u5e78\u559c\u5353\u4e00\u822a\u4e34\u5371\u4e0d\u4e71\uff0c\u53d8\u62db\u5feb\u6781\uff0c \u89c1\u4ed6\u94dc\u4eba\u6765\u52bf\u6781\u731b\uff0c\u95ea\u907f\u5df2\u662f\u4e0d\u53ca\uff0c\u8d81\u7740\u8eab\u5b50\u524d\u503e\u4e4b\u52bf\uff0c\u9aa4\u7136\u9a88\u6307\u5411\u4ed6\u624b\u8155\u4e00\u70b9\uff0c\u54c8\u5ddd\u6b63\u5728 \u53d1\u529b\uff0c\u5ffd\u7136\u624b\u8155\u4e00\uff0c\u94dc\u4eba\u5782\u4e86\u4e0b\u6765\uff0c\u5353\u4e00\u822a\u6025\u5fd9\u4e00\u65cb\u811a\u8ddf\uff0c\u8f6c\u4e86\u51fa\u53bb\uff0c\u5237\u5237\u4e24\u5251\uff0c\u540c\u65f6\u6321\u5f00 \u4e86\u77f3\u6d69\u4e0e\u96f7\u8499\u7684\u5175\u5668\u3002 \u3000\u3000\u54c8\u5ddd\u662f\u54c8\u8428\u514b\u65cf\u4e2d\u6570\u4e00\u6570\u4e8c\u7684\u6b66\u58eb\uff0c\u6454\u8de4\u4e4b\u6280\uff0c\u66f4\u662f\u79f0\u96c4\u585e\u5916\uff0c\u8fd9\u4e00\u52fe\u52fe\u5353\u4e00\u822a\u4e0d\u5230\uff0c \u53cd\u800c\u5403\u4e86\u5927\u4e8f\uff0c\u771f\u662f\u5927\u51fa\u610f\u5916\u3002\u5f53\u4e0b\u4e0d\u6562\u8f7b\u654c\uff0c\u6296\u64de\u7cbe\u795e\uff0c\u4ee5\u53c1\u654c\u4e00\u3002 \u3000\u3000\u5353\u4e00\u822a\u523a\u54c8\u5ddd\u4e0d\u5012\uff0c\u4e5f\u89c9\u5fc3\u60ca\u3002\u4ed6\u672c\u60f3\u65bd\u5404\u4e2a\u51fb\u7834\u4e4b\u6280\uff0c\u5148\u523a\u4f24\u8eab\u6cd5\u6700\u5dee\u7684\u54c8\u5ddd\uff0c\u5374\u56e0 \u8981\u987e\u5fcc\u4ed6\u7684\u6454\u8de4\u7edd\u6280\uff0c\u53cd\u800c\u4e0d\u6562\u8fc7\u65bc\u8feb\u8fd1\u3002\u81f3\u65bc\u77f3\u6d69\u4e0e\u96f7\u8499\u4e8c\u4eba\uff0c\u6b66\u529f\u6bd4\u5353\u4e00\u822a\u4ec5\u5dee\u4e00\u7b79\uff0c \u7edd\u975e\u53c1\u62db\u4e24\u5f0f\uff0c\u5c31\u53ef\u5c06\u4ed6\u4eec\u523a\u4f24\uff0c\u56e0\u6b64\u8981\u5404\u4e2a\u51fb\u7834\uff0c\u5b9e\u662f\u96be\u80fd\u3002 \u3000\u3000\u53cc\u65b9\u6076\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u5353\u4e00\u822a\u6e10\u5904\u4e0b\u98ce\uff0c\u96f7\u8499\u559d\u9053\uff1a\u201c\u5ff5\u4f60\u662f\u4e00\u6d3e\u638c\u95e8\uff0c\u5c06\u5251\u732e\u7ed9\u4f5b \u7237\uff0c\u51c6\u4f60\u9003\u547d\u3002\u201d\u77f3\u6d69\u6025\u9053\uff1a\u201c\u653e\u864e\u5bb9\u6613\u6349\u864e\u96be\uff0c\u5c82\u53ef\u8f7b\u9976\uff01\u201d\u6325\u5200\u970d\u970d\uff0c\u6025\u6025\u8fdb\u653b\u3002\u77f3\u6d69 \u77e5\u9053\u6b66\u5f53\u6d3e\u7684\u4eba\uff0c\u5bf9\u5916\u6700\u662f\u9f50\u5fc3\uff0c\u53c8\u77e5\u5353\u4e00\u822a\u653e\u4ed6\u4e0d\u8fc7\uff0c\u6240\u4ee5\u53cd\u9762\u6210\u4ec7\uff0c\u72e0\u8d77\u5fc3\u80a0\uff0c\u8981\u5c06\u5353 \u4e00\u822a\u788e\u706d\u53e3\u3002 \u3000\u3000\u5353\u4e00\u822a\u662f\u540d\u95e8\u5b50\u5f1f\uff0c\u6b63\u6d3e\u638c\u95e8\uff0c\u81ea\u6709\u51e0\u5206\u50b2\u6c14\uff0c\u96f7\u8499\u559d\u4ed6\u732e\u5251\uff0c\u4ed6\u5df2\u662f\u6c14\u70b8\u5fc3\u80ba\uff0c\u88ab\u77f3 \u6d69\u90a3\u9ebd\u4e00\u8bf4\uff0c\u66f4\u662f\u6012\u6c14\u51b2\u5929\u3002\u5927\u58f0\u559d\u9053\uff1a\u201c\u4eca\u65e5\u6709\u4f60\u65e0\u6211\uff0c\u5353\u67d0\u5c82\u662f\u6c42\u94d9\u4e4b\u4eba\u4e86\u770b\u5251\uff01\u201d\u5251 \u6cd5\u4e00\u53d8\uff0c\u5c06\u81ea\u5df1\u5999\u609f\u7684\u90a3\u51e0\u62db\u8fbe\u6469\u5251\u5f0f\uff0c\u4f7f\u5f97\u51cc\u5389\u65e0\u524d\u3002 \u3000\u3000\u77f3\u6d69\u7b49\u53c1\u4eba\u89c1\u4ed6\u5251\u6cd5\u7a81\u7136\u5389\u5bb3\u5f88\u591a\uff0c\u4e0d\u89c9\u5927\u60ca\uff0c\u5404\u5404\u8fd0\u7528\u5175\u5668\u62a4\u8eab\uff0c\u53ea\u6c42\u81ea\u4fdd\u3002\u5353\u4e00\u822a \u82e5\u7136\u8d81\u6b64\u65f6\u673a\u51b2\u51fa\uff0c\u4ed6\u4eec\u53c1\u4eba\u90fd\u4e0d\u6562\u8ffd\u8d76\u3002\u4f46\u4ed6\u6c14\u5728\u5934\u4e0a\uff0c\u770b\u5251\u6cd5\u89c1\u6548\uff0c\u8fde\u8fde\u53cd\u51fb\u3002\u6597\u4e86\u4e00 \u9635\uff0c\u77f3\u6d69\u89c1\u4ed6\u6700\u51f6\u6700\u96be\u62b5\u5fa1\u7684\u5251\u6cd5\u4ea6\u4e0d\u8fc7\u51e0\u62db\uff0c\u5927\u7b11\u9053\uff1a\u201c\u5353\u4e00\u822a\uff0c\u4f60\u9ed4\u9a74\u4e4b\u6280\u5df2\u7a77\uff0c\u8fd9\u91cc \u4fbf\u662f\u4f60\u846c\u8eab\u4e4b\u5730\u4e86\uff01\u201d\u628a\u624b\u4e00\u6325\uff0c\u4e0e\u96f7\u8499\u54c8\u5ddd\u5e03\u6210\u7284\u89d2\u4e4b\u52bf\uff0c\u53c8\u518d\u5408\u56f4\u53cd\u51fb\uff01 \u3000\u3000\u8fd9\u4e00\u756a\u6597\u5f97\u66f4\u70c8\uff0c\u5353\u4e00\u822a\u5728\u6b66\u5f53\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u5251\u6cd5\u4e4b\u4e2d\uff0c\u6742\u4ee5\u8fbe\u6469\u5251\u5f0f\uff0c\u9760\u90a3\u51e0\u62db\u8fbe\u6469 \u5251\u5f0f\uff0c\u4ec5\u80fd\u81ea\u4fdd\u3002\u4f46\u6c14\u529b\u5374\u6e10\u60ed\u4e0d\u652f\uff0c\u518d\u6597\u4e86\u4e00\u767e\u6765\u62db\uff0c\u5df2\u662f\u6c14\u5598\u5fc3\u8df3\uff0c\u6c57\u5982\u96e8\u4e0b\u3002 \u3000\u3000\u77f3\u6d69\u5927\u559c\uff0c\u653b\u5f97\u66f4\u6025\uff0c\u8d81\u7740\u5353\u4e00\u822a\u62b5\u5fa1\u54c8\u5ddd\u7684\u72ec\u811a\u94dc\u4eba\u4e4b\u9645\uff0c\u970d\u5730\u4e00\u5200\uff0c\u75be\u780d\u5353\u4e00\u822a\u624b \u8155\u3002 \u3000\u3000\u5c31\u5728\u5353\u4e00\u822a\u751f\u6b7b\u547c\u5438\uff0c\u6027\u547d\u4fc4\u9877\u4e4b\u9645\uff0c\u5c71\u5cf0\u4e0a\u5ffd\u7136\u4f20\u6765\u4e00\u58f0\u957f\u7b11\uff0c\u77f3\u6d69\u5fc3\u98a4\u624b\u9707\uff0c\u90a3\u4e00 \u5200\u672c\u6765\u662f\u770b\u51c6\u4e86\u624d\u780d\u7684\uff0c\u7adf\u7136\u6b6a\u8fc7\u4e00\u8fb9\u3002\u5353\u4e00\u822a\u5927\u559c\u53eb\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff01\u201d \u3000\u3000\u96f7\u8499\u5174\u54c8\u5ddd\u5ffd\u89c1\u77f3\u6d69\u9762\u5982\u571f\u8272\uff0c\u5927\u4e3a\u8be7\u5f02\uff0c\u540c\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u6015\u4ec0\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u53c8\u53eb\u4e86\u4e00 \u58f0\uff1a\u201c\u7ec3\u59d0\u59d0\uff01\u201d\u96f7\u8499\u6deb\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6709\u59d0\u59d0\u8981\u6765\u52a9\u6218\u9ebd\u4e86\u77a7\u4f60\u7684\u6a21\u6837\uff0c\u4f60\u7684\u59d0\u59d0\u4e5f\u4e00\u5b9a\u957f \u5f97\u4e0d\u9519\uff01\u201d\u8bdd\u58f0\u521a\u4e86\uff0c\u5ffd\u7136\u60e8\u53eb\u4e00\u58f0\uff0c\u5411\u5f8c\u4fbf\u5012\uff0c\u54c8\u5ddd\u6025\u5c55\u72ec\u811a\u94dc\u4eba\u6765\u6551\uff0c\u5353\u4e00\u822a\u523a\u7684\u4e00\u5251 \u523a\u5728\u4ed6\u5de6\u80c1\u9b42\u95e8\u8981\u7a74\uff0c\u54c8\u5ddd\u4ee5\u4e3a\u77f3\u6d69\u8fd8\u5728\u5de6\u8fb9\uff0c\u4e0d\u52a0\u9632\u5907\uff0c\u88ab\u5353\u4e00\u822a\u523a\u4e2a\u6b63\u7740\uff0c\u987f\u65f6\u8dcc\u5012\uff01 \u3000\u3000\u77f3\u6d69\u66fe\u6709\u51e0\u6b21\u9669\u5728\u7389\u7f57\u5239\u624b\u4e0b\u4e27\u751f\uff0c\u8fd9\u65f6\u542c\u89c1\u7b11\u58f0\uff0c\u5982\u732b\u9047\u9f20\uff0c\u6025\u6025\u5954\u9003\uff0c\u4f46\u624b\u811a\u90fd\u5df2 \u8f6f\u4e86\uff0c\u8d8a\u6025\u8d8a\u8dd1\u4e0d\u5feb\uff0c\u88ab\u5353\u4e00\u822a\u53c1\u811a\u4e24\u6b65\uff0c\u8d76\u5230\u80cc\u5f8c\uff0c\u624b\u8d77\u4e00\u5251\uff0c\u53c8\u628a\u4ed6\u6420\u7ffb\u5730\u4e0b\u3002 \u3000\u3000\u5353\u4e00\u822a\u65e0\u6687\u7406\u4f1a\u77f3\u6d69\uff0c\u5954\u4e0a\u5c71\u5cf0\u53eb\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u4f60\u51fa\u6765\u89c1\u6211\u5440\uff01\u201d\u5c71\u4e0a\u767d\u96ea\u7247\u7247\u98de \u8fc7\uff0c\u5374\u662f\u6e3a\u65e0\u4eba\u8ff9\u3002 \u3000\u3000\u5353\u4e00\u822a\u53c8\u53eb\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u6211\u5728\u6728\u4ec0\u5854\u514b\u5c71\u7684\u9a7c\u8702\u4e4b\u4e0a\uff0c\u66ff\u4f60\u627e\u5230\u4ed9\u82b1\u5566\uff0c\u4f60\u4e0b\u6765 \u5440\uff01\u201d\u5c71\u98ce\u9001\u58f0\uff0c\u7fa4\u5cf0\u56de\u54cd\uff0c\u4ecd\u7136\u4e0d\u89c1\u4eba\u5f71\u3002 \u3000\u3000\u5353\u4e00\u822a\u5927\u4e3a\u61ca\u4e27\uff0c\u9893\u7136\u8dcc\u5750\u77f3\u4e0a\u3002\u60f3\u9053\uff1a\u201c\u5979\u80af\u51fa\u624b\u6551\u6211\uff0c\u4e3a\u4f55\u4e0d\u80af\u89c1\u6211\uff1f\u54e6\uff0c\u5979\u6765\u53bb \u65e0\u8e2a\uff0c\u8fd9\u51e0\u5e74\u6765\u4e5f\u8bb8\u5e38\u5e38\u5728\u6211\u7684\u8eab\u8fb9\uff0c\u6211\u90fd\u4e0d\u77e5\u9053\u3002\u201d\u6b22\u559c\uff0c\u5931\u671b\uff0c\u671f\u5f85\uff0c\u8f9b\u9178\u7b49\u7b49\u60c5\u7eea\uff0c \u5239\u65f6\u95f4\u90fd\u4e0a\u5fc3\u5934\uff01\u5353\u4e00\u822a\u76ee\u9001\u767d\u4e91\uff0c\u72ec\u7acb\u5c71\u5934\uff0c\u5982\u75f4\u4f3c\u9189\uff01 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u5c11\u65f6\u5019\uff0c\u5ffd\u542c\u5f97\u5c71\u53e3\u5916\u53c8\u4f20\u6765\u4eba\u9a6c\u884c\u8d70\u4e4b\u58f0\uff0c\u5353\u4e00\u822a\u970d\u7136\u60f3\u8d77\u77f3\u6d69\u4e0e\u54c8\u5ddd \u8fd8\u5728\u4e0b\u9762\u5c71\u8def\u4e0a\uff0c\u5fc3\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\u4e0d\u80af\u89c1\u6211\uff0c\u6211\u5728\u8fd9\u91cc\u4e5f\u662f\u65e0\u7528\u3002\u6765\u7684\u8fd9\u5f6a\u4eba\u9a6c\u4e0d\u77e5\u662f\u4ec0\u9ebd \u4eba\uff1f\u82e5\u662f\u77f3\u6d69\u4ed6\u4eec\u540c\u515a\uff0c\u5c06\u4eba\u6551\u53bb\uff0c\u6211\u5c82\u4e0d\u662f\u5931\u4e86\u5bf9\u5510\u52aa\u7684\u8bfa\u8a00\uff0c\u8d1f\u4e86\u7ec3\u59d0\u59d0\u76f8\u6551\u7684\u60c5 \u610f\u3002\u201d\u601d\u5ff5\u53ca\u6b64\uff0c\u6025\u6025\u5954\u4e0b\u3002 \u3000\u3000\u5353\u4e00\u822a\u521a\u624d\u90a3\u5251\u7528\u529b\u751a\u731b\uff0c\u77f3\u6d69\u7684\u80eb\u9aa8\u5df2\u88ab\u523a\u7a7f\uff0c\u5728\u5730\u4e0b\u6323\u624e\u722c\u884c\uff0c\u8fd8\u5dee\u4e08\u8bb8\u4e4b\u5730\u5c31\u8981 \u722c\u5230\u54c8\u5ddd\u8eab\u8fb9\u3002\u770b\u4ed6\u6837\u5b50\uff0c\u4f3c\u4e4e\u662f\u60f3\u66ff\u54c8\u5ddd\u89e3\u5f00\u88ab\u523a\u7684\u7a74\u9053\uff0c\u7136\u5f8c\u53eb\u54c8\u5ddd\u80cc\u4ed6\u51fa\u5c71\u3002 \u3000\u3000\u77f3\u6d69\u6b63\u662f\u5982\u6b64\u5b58\u5fc3\uff0c\u4e0d\u6599\u529f\u8d25\u5782\u6210\uff0c\u53c8\u7ed9\u5353\u4e00\u822a\u5236\u4f0f\u3002\u5353\u4e00\u822a\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u524a\u4e86\u4e00\u6761 \u5c71\uff0c\u5c06\u4ed6\u4eec\u4e8c\u4eba\u7f1a\u5728\u4e00\u5904\uff0c\u7136\u5f8c\u53bb\u770b\u90a3\u96f7\u8499\u6cd5\u5e08\u3002\u53ea\u89c1\u90a3\u96f7\u8499\u6cd5\u5e08\u9762\u671d\u5929\u4ef0\u5367\u9053\u65c1\uff0c\u54bd\u5589\u6bb7 \u7ea2\u4e00\u7247\u3002\u5353\u4e00\u822a\u4e3e\u8db3\u8f7b\u8f7b\u4e00\u8e22\uff0c\u96f7\u8499\u52a8\u4e5f\u4e0d\u52a8\uff0c\u7adf\u662f\u6b7b\u4e86\uff01\u5353\u4e00\u822a\u4fef\u8eab\u5bdf\u770b\uff0c\u53ea\u89c1\u4ed6\u54bd\u5589\u4e0a \u63d2\u6709\u4e00\u53e3\u94f6\u9488\uff0c\u4ec5\u6709\u5c11\u8bb8\u9732\u51fa\u5916\u9762\uff0c\u4e0d\u89c9\u9a87\u7136\u5931\u58f0\uff01 \u3000\u3000\u96f7\u8499\u6cd5\u5e08\u54bd\u5589\u4e0a\u7684\u90a3\u53e3\u94f6\u9488\uff0c\u4e0d\u95ee\u53ef\u77e5\uff0c\u4e43\u662f\u7389\u7f57\u5239\u7684\u72ec\u95e8\u6697\u5668-\uff0c\u4e5d\u661f\u5b9a\u5f62\u9488\u3002\u6885\u82b1 \u9488\u4e4b\u7c7b\u7684\u7ec6\u5c0f\u6697\u5668\uff0c\u53ea\u80fd\u53ca\u8fd1\uff0c\u4e0d\u80fd\u53ca\u8fdc\u3002\u800c\u7389\u7f57\u5239\u5c45\u7136\u80fd\u4e0d\u73b0\u8eab\u5f62\uff0c\u4fbf\u5236\u654c\u6b7b\u547d\uff0c\u5373\u7b97\u5979 \u4f0f\u5728\u6700\u8fd1\u7684\u5ca9\u77f3\u5806\u4e2d\uff0c\u8ddd\u79bb\u4e5f\u5728\u4e94\u4e08\u4e4b\u5916\uff0c\u5728\u90a3\u9ebd\u8fdc\u7684\u8ddd\u79bb\uff0c\u80fd\u53d1\u9488\u654c\uff0c\u4e0d\u4f46\u6697\u5668\u4e0a\u7684\u529f\u592b \u51fa\u795e\u5165\u5316\uff0c\u5185\u5bb6\u7684\u52b2\u9053\u4ea6\u9a87\u4eba\u542c\u95fb\u3002\u5353\u4e00\u822a\u53f9\u9053\uff1a\u4e0d\u9053\u7ec3\u59d0\u59d0\u7684\u529f\u592b\u5df2\u8fbe\u5230\u5982\u6b64\u5883\u754c\uff0c\u53ea\u662f \u672a\u514d\u592a\u72e0\u8fa3\u4e86\u3002 \u3000\u3000\u9a6c\u8e44\u58f0\u6765\u5f97\u66f4\u8fd1\uff0c\u884c\u4e86\u7247\u523b\uff0c\u53ea\u89c1\u4e00\u5c0f\u961f\u54c8\u8428\u514b\u5175\u58eb\uff0c\u5217\u6210\u5355\u884c\uff0c\u51b2\u8fdb\u5ce1\u53e3\u3002\u9886\u5934\u7684\u662f \u4e00\u4f4d\u8001\u5c06\u519b\uff0c\u624b\u6a2a\u91d1\u80cc\u65ab\u5c71\u5200\uff0c\u957f\u9b13\u98d8\u7136\uff0c\u5341\u5206\u5a01\u98ce\u3002\u5353\u4e00\u822a\u8fce\u4e0a\u53bb\u9053\uff1a\u201c\u6765\u7684\u53ef\u662f\u54c8\u8428\u514b \u7684\u8001\u82f1\u96c4\u5df4\u9f99\u5c06\u519b\u9ebd\uff1f\u201d \u3000\u3000\u90a3\u8001\u5c06\u519b\u9762\u6709\u8be7\u5f02\u4e4b\u5bb9\uff0c\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u4f60\u8fd9\u6c49\u4eba\u600e\u9ebd\u77e5\u9053\u6211\u7684\u540d\u5b57\uff1f\u201d\u653e\u773c\u4e00\u77a7\uff0c\u5ffd \u89c1\u54c8\u5ddd\u4e0e\u77f3\u6d69\u88ab\u7f1a\u505a\u4e00\u5806\uff0c\u4e0d\u7981\u5931\u58f0\u53eb\u9053\uff1a\u201c\u54c8\u5ddd\uff0c\u4f60\u4e5f\u662f\u6ee1\u5dde\u7684\u5185\u5e94\u5417\uff1f\u201d \u3000\u3000\u54c8\u5ddd\u7741\u773c\u559d\u9053\uff1a\u201c\u4ec0\u9ebd\u6ee1\u5dde\u5185\u5e94\uff1f\u6211\u8981\u52a9\u914b\u957f\u7edf\u4e00\u5929\u5c71\u5357\u5317\uff0c\u5927\u597d\u8ba1\u753b\uff0c\u5374\u88ab\u4f60\u4eec\u7834\u574f \u4e86\uff01\u201d\u5df4\u9f99\u9053\uff1a\u201c\u4ec0\u9ebd\u8ba1\u753b\uff1f\u201d\u54c8\u5ddd\u9053\uff1a\u201c\u90a3\u6ee1\u5dde\u5175\u8fdc\u5728\u5173\u5916\uff0c\u600e\u5a01\u80c1\u5f97\u4e86\u6211\u4eec\u3002\u54b1\u4eec\u82e5\u4e0e \u5b83\u8054\u76df\u6297\u660e\uff0c \u3000\u3000\u6709\u597d\u5904\uff0c\u6ca1\u6709\u574f\u5904\u3002\u53ef\u6068\u4f60\u8fd9\u8001\u5e9f\u7269\u4ece\u4e2d\u963b\u6320\uff0c\u81f3\u4ee4\u738b\u7237\u201c\u5927\u914b\u957f\u901a\u79f0\u738b\u516c\u201d\u4e0d\u4fe1\u6211\u7684 \u8bf4\u8bdd\u3002\u6211 \u3000\u3000\u597d\u4e0e\u5929\u5fb7\u4e0a\u4eba\u540c\u8c0b\uff0c\u66f4\u5f97\u5580\u8fbe\u5c14\u65cf\u7684\u738b\u7237\u76f8\u52a9\uff0c\u613f\u5949\u6211\u4eec\u7684\u738b\u7237\u4e3a\u5404\u65cf\u76df\u4e3b\u3002\u5c06\u6765\u6ee1\u5dde \u5175\u4eba\u5173\u628a\u5927\u660e\u4ea1\u4e86\u4e4b\u5f8c\uff0c\u6211\u4eec\u5728\u585e\u5916\u81ea\u6210\u4e00\u56fd\uff0c\u6709\u4f55\u4e0d\u597d\uff1f\u201d \u3000\u3000\u54c8\u5ddd\u672c\u662f\u54c8\u8428\u514b\u65cf\u4e2d\u6570\u4e00\u6570\u4e8c\u7684\u6b66\u58eb\uff0c\u53ef\u60dc\u6709\u52c7\u65e0\u8c0b\uff0c\u5934\u8111\u7cca\u6d82\uff0c\u4ee5\u81f4\u7adf\u4e0e\u864e\u8c0b\u76ae\uff0c\u5c1a \u672a\u9192\u609f\u3002\u5df4\u9f99\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u54c8\u5ddd\uff0c\u4f60\u597d\u7cca\u6d82\u3002\u4f60\u53d7\u4e86\u5978\u4eba\u5229\u7528\uff0c\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u201d\u4e00\u9762\u53f9 \u6c14\uff0c\u4e00\u9762\u5374\u53c8\u6697\u559c\u54c8\u5ddd\u76f4\u80a0\u76f4\u809a\uff0c\u5c06\u5b5f\u8428\u601d\u3001\u5929\u5fb7\u4e0a\u4eba\u4e0e\u6ee1\u5dde\u52fe\u7ed3\u7684\u9634\u8c0b\u6296\u9732\u51fa\u6765\uff0c\u8349\u539f\u4e0a \u7684\u707e\u7978\u4e5f\u53ef\u53ca\u65f6\u6d88\u5f2d\u4e86\u3002 \u3000\u3000\u5df4\u9f99\u95ee\u5353\u4e00\u822a\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u662f\u4f60\u6349\u7740\u7684\u5417\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u662f\u3002\u201d\u5df4\u9f99\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u9ebd \u8981\u6349\u4ed6\u4eec\uff1f\u96be\u9053\u4f60\u4e5f\u77e5\u9053\u4ed6\u4eec\u662f\u6ee1\u5dde\u7684\u5978\u7ec6\u5417\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u5373\u7b97\u4e0d\u77e5\u9053\u4ed6\u4eec\u7684\u5978\u8c0b\uff0c \u4e5f\u8981\u62ff\u4ed6\u3002\u201d\u6307\u7740\u77f3\u6d69\u9053\uff1a\u201c\u8001\u82f1\u96c4\uff0c\u4f60\u53ef\u77e5\u9053\u4ed6\u662f\u8c01\u4e86\u4ed6\u5c31\u662f\u660e\u5ef7\u4ee5\u524d\u90a3\u4e2a\u7978\u56fd\u6b83\u6c11\u7684\u9b4f \u5fe0\u8d24\u7684\u5fc3\u8179\uff0c\u66fe\u505a\u5230\u9526\u8863\u536b\u90fd\u6307\u6325\u7684\u77f3\u6d69\uff01\u201d\u9b4f\u5fe0\u8d24\u638c\u6743\u591a\u5e74\uff0c\u81ed\u540d\u8fdc\u626c\uff0c\u585e\u5916\u7684\u4eba\u4e5f\u90fd\u77e5 \u9053\u3002\u5df4\u9f99\u4e0d\u89c9\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8349\u539f\u4e0a\u6709\u53e5\u4fd7\u8bdd\uff1a\u662f\u5783\u573e\u5c31\u503e\u505a\u4e00\u5806\uff0c\u602a\u4e0d\u5f97\u4ed6 \u548c\u5929\u5fb7\u90a3\u79c3\u8d3c\u52fe\u7ed3\u4e86\u3002\u201d \u3000\u3000\u54c8\u5ddd\u7741\u5927\u4e86\u773c\u775b\uff0c\u751a\u89c9\u8ff7\u60d1\u3002\u542c\u4e86\u8fd9\u8bdd\uff0c\u5ffd\u7136\u53d1\u6012\u602a\u6765\uff0c\u56b7\u9053\uff1a\u201c\u5df4\u9f99\uff0c\u4f60\u9a82\u6211\u4e5f\u662f\u5783 \u573e\uff1f\u201d\u5df4\u9f99\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5783\u573e\uff0c\u4f46\u5374\u88ab\u5783\u573e\u7684\u81ed\u5473\u8ff7\u7740\u4e86\uff01\u201d\u987f\u4e86\u4e00\u987f\uff0c\u5ffd\u5bf9\u5353\u4e00\u822a\u9053\uff1a \u201c\u8fd9\u4e24\u4eba\u662f\u4f60\u64d2\u83b7\u7684\uff0c\u672c\u8be5\u7531\u4f60\u5904\u7f6e\u3002\u4f46\u6211\u5374\u8981\u5411\u4f60\u8ba8\u4e2a\u60c5\uff0c\u5c06\u54c8\u5ddd\u7684\u7f1a\u89e3\u5f00\u597d\u5417\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f46\u51ed\u5c06\u519b\u5904\u7f6e\u3002\u201d\u5df4\u9f99\u5c06\u54c8\u5ddd\u7684\u7f1a\u89e3\u8131\uff0c\u628a\u4ed6\u62c9\u8fc7\u4e00\u8fb9\uff0c\u6162\u6162\u548c\u4ed6\u8c08\u8bba\u9053 \u7406\uff0c\u5353\u4e00\u822a\u4e5f\u5c06\u6240\u89c1\u6240\u95fb\uff0c\u5929\u5fb7\u4e0a\u4eba\u5982\u4f55\u538b\u69a8\u767e\u59d3\uff0c\u7792\u4e0a\u6b3a\u4e0b\u7684\u4e8b\u60c5\u8bf4\u4e86\u51fa\u6765\u3002\u4e24\u4eba\u8bf4\u4e86\u534a \u5929\uff0c\u628a\u54c8\u5ddd\u8bf4\u5f97\u53c8\u60ed\u53c8\u6124\uff0c\u6c57\u6d41\u6d43\u80cc\u3002\u8df3\u8d77\u6765\u9053\uff1a\u201c\u597d\uff0c\u4f60\u4eec\u6709\u7406\uff01\u5929\u5fb7\u8fd9\u9a97\u6211\u7ed9\u4ed6\u505a\u6253 \u624b\uff0c\u6211\u8981\u56de\u53bb\u4e0e\u4ed6\u7b97\u5e10\u3002\u201d\u5df4\u9f99\u9053\uff1a\u201c\u7528\u4e0d\u7740\u8fd9\u6837\u6025\u3002\u54b1\u4eec\u603b\u8981\u548c\u4ed6\u7b97\u5e10\u3002\u90a3\u9ebd\u6211\u95ee\u4f60\uff0c\u4f60 \u4eca\u5929\u5230\u8fd9\u513f\u6765\uff0c\u4e5f\u662f\u5949\u5929\u5fb7\u8fd9\u4e4b\u547d\u9ebd\uff1f\u201d\u54c8\u5ddd\u9053\uff1a\u201c\u662f\u4ed6\u53eb\u6211\u548c\u4ed6\u7684\u5e08\u4f84\u540c\u6765\u63a5\u5e94\u90a3\u4e2a\u6ee1\u6d32 \u4f7f\u8005\u7684\u3002\u4e0d\u6599\u6ee1\u6d32\u4f7f\u8005\u672a\u89c1\uff0c\u5374\u53ea\u89c1\u4e86\u8fd9\u4e2a\u4ec0\u9ebd\u77f3\u6d69\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u90a3\u4e2a\u6ee1\u6d32\u4f7f\u8005\u65f1\u5df2\u88ab \u5510\u52aa\u6349\u7740\u4e86\u3002\u201d\u5df4\u9f99\u559c\u9053\uff1a\u201c\u5510\u52aa\u771f\u6210\uff0c\u4ed6\u65e9\u5df2\u6253\u542c\u51fa\u90a3\u6ee1\u6d32\u4f7f\u8005\u5728\u5580\u8fbe\u5c14\u738b\u516c\u5b5f\u8428\u6069\u90a3\u513f \u6d3b\u52a8\uff0c\u788d\u65bc\u5b5f\u8428\u601d\u7684\u52bf\u529b\uff0c\u4e0d\u80fd\u6349\u4ed6\u3002\u6240\u4ee5\u8d81\u4ed6\u79bb\u5f00\u4e4b\u65f6\uff0c\u9080\u6211\u4f0f\u5175\u8ffd\u6355\u3002\u53ef\u60dc\u6211\u8fd8\u662f\u6765\u8fdf \u4e86\u4e00\u6b65\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u5c06\u77f3\u6d69\u4ea4\u4e0e\u5df4\u9f99\u3002\u9053\uff1a\u201c\u5929\u5fb7\u90a3\u4f5c\u6076\u7684\u4e8b\u60c5\uff0c\u4f60\u5df2\u77e5\u9053\u4e86\uff0c\u8bf7\u4f60\u529d\u544a\u4f60\u4eec\u7684\u738b\u7237 \u5c06\u4ed6\u8d76\u51fa\u8349\u539f\u53bb\u5427\u3002\u6211\u544a\u8f9e\u4e86\u3002\u201d\u5df4\u9f99\u9053\uff1a\u201c\u4e49\u58eb\uff0c\u6211\u8fd8\u8981\u8bf7\u4f60\u5e2e\u5fd9\u3002\u201d\u5353\u4e00\u822a\u95ee\u4ed6\u5e2e\u4ec0\u9ebd \u5fd9\u3002\u5df4\u9f99\u9053\uff1a\u201c\u5f8c\u5929\u662f\u6211\u4eec\u5317\u7586\u5404\u65cf\u5728\u5580\u6c81\u8349\u539f\u4f1a\u76df\u4e4b\u671f\uff0c\u5728\u8fd9\u6b21\u4f1a\u76df\u4e0a\uff0c\u5c06\u63a8\u51fa\u6211\u4eec\u5404\u65cf \u7684\u76df\u4e3b\u3002\u53ea\u6050\u5b5f\u8428\u601d\u4ed6\u4eec\u4f1a\u95f9\u51fa\u4e8b\u60c5\uff0c\u800c\u4e14\u5929\u5fb7\u90a3\u6b66\u529f\u7cbe\u5f3a\uff0c\u7b49\u95f2\u4e5f\u4e0d\u5bb9\u6613\u5bf9\u4ed8\u3002\u597d\u8bf7\u4f60\u518d \u51fa\u70b9\u529b\uff01\u6211\u4eec\u611f\u8c22\u4e0d\u5c3d\u3002\u201d\u5353\u4e00\u822a\u4e49\u4e0d\u5bb9\u8f9e\uff0c\u4fbf\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u5df4\u9f99\u8001\u8c0b\u6df1\u7b97\uff0c\u5e26\u4e86\u5353\u4e00\u822a\u4e0e\u54c8\u5ddd\u4e8c\u4eba\uff0c\u548c\u5fc3\u8179\u90e8\u4e0b\u6f5c\u56de\u8349\u539f\uff0c\u5374\u4e0d\u53bb\u89c1\u914b\u957f\uff0c\u5148\u81ea\u6697 \u4e2d\u5e03\u7f6e\uff0c\u6309\u4e0b\u4e0d\u8868\u3002 \u3000\u3000\u4e14\u8bf4\u53c1\u65e5\u4e4b\u5f8c\uff0c\u5404\u65cf\u5404\u90e8\u843d\u7684\u914b\u957f\uff0c\u90fd\u5e26\u4e86\u672c\u65cf\u4e2d\u6709\u58f0\u671b\u5730\u4f4d\u7684\u4eba\u8d76\u5230\u5580\u6c81\u8349\u539f\u4f1a\u76df\u3002 \u54c8\u8428\u514b\u65cf\u7684\u914b\u957f\u751a\u4e3a\u70e6\u607c\uff0c\u4ed6\u7684\u526f\u624b\u5df4\u9f99\u8fd9\u51e0\u5929\u5ffd\u7136\u4e0d\u77e5\u53bb\u5411\uff0c\u5728\u8fd9\u79cd\u91cd\u8981\u7684\u4f1a\u671f\u4e4b\u524d\u5931 \u8e2a\uff0c\u771f\u662f\u4e0d\u53ef\u60f3\u50cf\u4e4b\u4e8b\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u662f\u708e\u590f\u65f6\u8282\uff0c\u8349\u539f\u4e0a\u767d\u5929\u6709\u5982\u70d8\u7089\uff0c\u665a\u4e0a\u6c14\u5019\u751a\u662f\u51c9\u723d\uff0c\u8981\u7a7f\u5939\u8863\uff0c\u56e0\u6b64\u4e00\u5207\u6d3b \u52a8\u90fd\u5728\u665a\u95f4\u4e3e\u884c\u3002 \u3000\u3000\u665a\u971e\u6d88\u901d\uff0c\u8349\u539f\u4e0a\u65b0\u6708\u5347\u8d77\u3002\u5df4\u9f99\u8fd8\u672a\u89c1\u56de\u3002\u54c8\u8428\u514b\u65cf\u7684\u914b\u957f\u53ea\u597d\u5e26\u4e86\u5929\u5fb7\u8fd9\u4e00\u73ed\u4eba\u53bb \u53c2\u52a0\u4f1a\u76df\u3002\u8349\u539f\u4e0a\u70e7\u8d77\u4e00\u5927\u5806\u706b\uff0c\u5404\u65cf\u914b\u957f\u548c\u4ed6\u4eec\u6240\u5e26\u6765\u7684\u4eba\uff0c\u90fd\u805a\u96c6\u5728\u7bf7\u5e10\u6240\u73af\u7ed5\u7684\u8349\u539f \u4e0a\u3002 \u3000\u3000\u4e00\u5f00\u9996\u5c31\u662f\u4e00\u573a\u6fc0\u8fa9\u3002\u5580\u8fbe\u5c14\u65cf\u7684\u914b\u957f\u5b5f\u8428\u601d\u8981\u4e89\u505a\u76df\u4e3b\uff0c\u7f57\u5e03\u65cf\u7684\u914b\u957f\u5510\u52aa\u5374\u628a\u90a3\u540d \u88ab\u64d2\u83b7\u7684\u6ee1\u6d32\u4f7f\u8005\u63a8\u4e86\u51fa\u6765\u3002\u5c06\u4ed6\u548c\u6ee1\u6d32\u52fe\u7ed3\u7684\u4e8b\u79d1\u9732\u51fa\u6765\u3002\u79c1\u901a\u6ee1\u6d32\u5728\u4e2d\u56fd\u672c\u571f\u662f\u4e00\u4e2a\u4e0d \u5bb9\u7f6e\u8fa9\u7684\u5927\u7f6a\u540d\uff0c\u4f46\u6ee1\u6d32\u548c\u8349\u539f\u5404\u65cf\u5e76\u65e0\u4ea4\u6218\u72b6\u6001\u5b58\u5728\uff0c\u6240\u4ee5\u201c\u79c1\u901a\u6ee1\u6d32\u201d\u4fbf\u53ea\u662f\u4e00\u4e2a\u7b56\u7565 \u4e0a\u7684\u4e89\u8fa9\u3002\u5b5f\u8428\u601d\u53cd\u800c\u6307\u8d23\u5510\u52aa\u4e0d\u5e94\u6263\u7559\u6765\u62a5\u8058\u4ed6\u7684\u6ee1\u6d32\u4f7f\u8005\u3002 \u3000\u3000\u4e00\u573a\u6fc0\u8fa9\uff0c\u5927\u591a\u6570\u7684\u914b\u957f\u90fd\u4e0d\u8d5e\u540c\u8054\u6ee1\u53cd\u660e\u3002\u4f46\u5bf9\u5510\u52aa\u4e4b\u6263\u7559\u6ee1\u6d32\u4f7f\u8005\uff0c\u4e5f\u5f88\u6709\u4e9b\u4eba\u4e0d \u4ee5\u4e3a\u7136\u3002\u6b63\u5728\u4e89\u8bba\u5f97\u4e0d\u53ef\u5f00\u4ea4\u4e4b\u9645\uff0c\u5b88\u536b\u7684\u6b66\u58eb\u8fdb\u6765\u62a5\u9053\uff1a\u201c\u54c8\u8428\u514b\u7684\u5df4\u9f99\u5c06\u519b\u5e26\u4eba\u5230 \u6765\uff01\u201d\u6b63\u662f\uff1a\u5171\u65bd\u670d\u864e\u64d2\u9f99\u624b\uff0c\u8981\u628a\u4e4c\u4e91\u4e00\u626b\u6e05\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377\u2014\u2014\u7b2c\u4e09\u5341\u56de\u3000\u5929\u9645\u770b\u5bd2\u661f\u3000\u60c5\u6000\u60d8\u60d8\u3000\u8349\u539f\u60ca\u6076\u6597\u3000\u5251\u6c14\u68ee\u68ee \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377 \u7b2c\u4e09\u5341\u56de \u5929\u9645\u770b\u5bd2\u661f\u3000\u60c5\u6000\u60d8\u60d8 \u8349\u539f\u60ca\u6076\u6597\u3000\u5251\u6c14\u68ee\u68ee \u3000\u3000\u5df4\u9f99\u5728\u54c8\u8428\u514b\u65cf\u4e2d\u7684\u58f0\u671b\u5730\u4f4d\u4ec5\u6b21\u65bc\u914b\u957f\uff0c\u8fd9\u6b21\u8fdf\u8fdf\u800c\u6765\uff0c\u5404\u65cf\u914b\u957f\u90fd\u751a\u8be7\u5f02\uff0c\u54c8\u8428\u514b \u65cf\u7684\u914b\u957f\u66f4\u4e0d\u9ad8\u5174\u3002 \u3000\u3000\u6fc0\u8fa9\u6682\u505c\uff0c\u4f17\u4eba\u6ce8\u76ee\u8fce\u63a5\uff0c\u53ea\u89c1\u5df4\u9f99\u5e26\u4e86\u4e00\u4e2a\u6c49\u4eba\uff0c\u6602\u7136\u5165\u573a\uff0c\u5929\u5fb7\u4e0a\u4eba\u89c1\u4e86\uff0c\u52c3\u7136\u53d8 \u8272\u3002\u6b64\u5165\u975e\u4ed6\uff0c\u6b63\u662f\u66fe\u6253\u8d25\u4ed6\u7684\u5353\u4e00\u822a\u3002 \u3000\u3000\u5580\u8fbe\u5c14\u65cf\u7684\u914b\u957f\u76c2\u8428\u601d\u9996\u5148\u6297\u8bae\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4f1a\uff0c\u4e43\u6211\u4eec\u8349\u539f\u4e0a\u5404\u65cf\u4e4b\u4f1a\uff0c\u5982\u4f55\u4ee4\u6c49\u4eba \u53c2\u52a0\uff1f\u201d\u5df4\u9f99\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u6c49\u4eba\u548c\u6211\u4eec\u8fd9\u6b21\u7684\u4f1a\u76df\u5927\u6709\u5173\u7cfb\u3002\u800c\u4e14\u5929\u5fb7\u4e0a\u4eba\u4e5f\u4e0d\u662f\u8349\u539f\u4e0a\u7684 \u4eba\uff0c\u4ed6\u53c2\u52a0\u4f1a\u76df\uff0c\u4e3a\u4ec0\u9ebd\u4f60\u53c8\u4e0d\u53cd\u5bf9\uff1f\u201d\u5b5f\u8428\u601d\u54d1\u7136\u65e0\u8bed\uff0c\u9053\uff1a\u201c\u4ed6\u548c\u6211\u4eec\u6709\u4ec0\u9ebd\u5173\u7cfb\uff1f\u90a3 \u9ebd\u6709\u8bdd\u8ba9\u4ed6\u5148\u8bf4\uff0c\u8bf4\u5b8c\u4e4b\u5f8c\uff0c\u4fbf\u5f53\u9000\u51fa\u3002\u201d \u3000\u3000\u5728\u5df4\u9f99\u548c\u5b5f\u8428\u601d\u4e89\u8bba\u4e4b\u65f6\uff0c\u5353\u4e00\u822a\u73af\u987e\u5168\u573a\uff0c\u548c\u7f57\u5e03\u65cf\u7684\u914b\u957f\u5510\u52aa\u76f8\u89c6\u800c\u7b11\u3002\u5b5f\u8428\u601d\u8bf4 \u5b8c\u4e4b\u5f8c\uff0c\u5353\u4e00\u822a\u6b63\u5f85\u8bf4\u8bdd\uff0c\u5ffd\u89c1\u56f4\u5728\u5916\u9762\u7684\u4eba\u7fa4\u95ea\u52a8\uff0c\u6709\u4eba\u7b11\u9053\uff1a\u201c\u5510\u52aa\u738b\u7237\uff0c\u4f60\u7684\u516c\u4e3b\u4e0d \u613f\u5728\u5916\u9762\u73a9\uff0c\u8981\u6765\u8ddf\u4f60\u5462\uff01\u201d \u3000\u3000\u8349\u539f\u4e0a\u7684\u96c6\u4f1a\uff0c\u672c\u6765\u5c31\u6ca1\u6709\u5f88\u4e25\u683c\u7684\u201c\u4f1a\u573a\u79e9\u5e8f\u201d\u4e4b\u7c7b\u7684\uff0c\u6240\u4ee5\u4e00\u4e2a\u914b\u957f\u7684\u5973\u513f\u8dd1\u4eba\u6765 \u627e\u7236\u4eb2\uff0c\u4f17\u4eba\u4e5f\u4e0d\u4ee5\u4e3a\u602a\u3002 \u3000\u3000\u5df4\u9f99\u5750\u5728\u5353\u4e00\u822a\u8eab\u8fb9\uff0c\u7b11\u9053\uff1a\u201c\u5510\u52aa\u53ea\u6709\u8fd9\u4e2a\u72ec\u751f\u5973\u513f\uff0c\u5b9d\u8d1d\u975e\u5e38\uff0c\u5230\u4ec0\u9ebd\u5730\u65b9\u53bb\u90fd\u5e26 \u7740\u5979\u8d70\u3002\u6211\u4eec\u90fd\u5f88\u559c\u6b22\u5979\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u6697\u6697\u597d\u7b11\uff0c\u4f46\u89c1\u4eba\u7fa4\u95ea\u5904\uff0c\u4e00\u4e2a\u5927\u7ea6\u6709\u5341\u4e00\u4e8c\u5c81\u5927\u7684\u5c0f\u5973\u5b69\u8dd1\u4e86\u8fdb\u6765\u3002\u524d\u989d\u8986\u7740 \u5218\u6d77\uff0c\u5934\u4e0a\u68b3\u4e86\u4e24\u4e2a\u4e2b\u89d2\uff0c\u7a7f\u7684\u662f\u7d27\u8eab\u9752\u8272\u7bad\u8863\uff0c\u6253\u626e\u5f97\u50cf\u4e00\u4e2a\u5c0f\u6b66\u58eb\uff0c\u4e2b\u89d2\u4e0a\u8fd8\u7ed3\u7740\u4e00\u6761 \u7ea2\u7ef8\u5dfe\uff0c\u8fce\u98ce\u98d8\u626c\uff0c\u5341\u5206\u795e\u6c14\u3002 \u3000\u3000\u90a3\u5973\u5b69\u56b7\u9053\uff1a\u201c\u7239\uff0c\u5916\u9762\u4e0d\u597d\u73a9\uff0c\u98ce\u7802\u53c8\u5927\uff0c\u6211\u8981\u8fdb\u6765\u548c\u4f60\u4e00\u9053\u70e4\u706b\u3002\u5582\uff0c\u4f60\u4eec\u7b49\u4e0b\u6709 \u6ca1\u6709\u6bd4\u6b66\u7684\u8282\u76ee\u7684\uff1f\u201d\u8349\u539f\u4e0a\u7684\u4f1a\u76df\uff0c\u82e5\u7136\u76df\u4f1a\u4e89\u6301\u4e0d\u4e0b\uff0c\u5e38\u4ee5\u6bd4\u8bd5\u9a91\u9a6c\u5c04\u7bad\u7b49\u9879\u76ee\u6765\u5b9a\u76df \u4e3b\u3002\u8fd9\u5973\u5b69\u5927\u7ea6\u662f\u5728\u5916\u9762\u542c\u5230\u90a3\u4e9b\u5b88\u536b\u6b66\u58eb\uff0c\u8bf4\u8d77\u91cc\u9762\u6b63\u5728\u5927\u4e89\u5927\u5435\uff0c\u6240\u4ee5\u8dd1\u8fdb\u95e8\u6765\u95ee\u3002 \u3000\u3000\u5510\u52aa\u7b11\u9053\uff1a\u201c\u4f60\u522b\u5927\u56b7\u5927\u53eb\uff0c\u4f60\u8981\u5728\u8fd9\u91cc\uff0c\u5c31\u5f97\u4e56\u4e56\u7684\u4e00\u58f0\u4e0d\u51fa\uff0c\u8981\u4e0d\u7136\u6211\u5c31\u628a\u4f60\u8d76\u51fa \u53bb\u3002\u201d\u6709\u7684\u914b\u957f\u9017\u5979\u9053\uff1a\u201c\u6211\u4eec\u7684\u5c0f\u98de\u7ea2\u5dfe\uff0c\u6709\u6bd4\u6b66\u65f6\u8bf7\u4f60\u88c1\u5224\uff0c\u597d\u5417\uff1f\u201d\u90a3\u5973\u5b69\u5b50\u770b\u4e86\u4ed6 \u7239\u4e00\u773c\uff0c\u4e0d\u6562\u5927\u58f0\u56de\u7b54\uff0c\u53ea\u628a\u6307\u5934\u6401\u5728\u5634\u4e0a\uff0c\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u53c8\u70b9\u4e86\u70b9\u5934\uff0c\u597d\u50cf\u5728\u8bf4\uff1a\u201c\u597d \u6781\uff0c\u597d\u6781\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u7684\u8bf4\u8bdd\u88ab\u8fd9\u5c0f\u5973\u5b69\u6253\u65ad\uff0c\u672c\u6765\u4e0d\u5927\u9ad8\u5174\uff0c\u4f46\u89c1\u4e86\u5979\u6d3b\u6cfc\u53ef\u7231\u7684\u795e\u6001\uff0c\u4e5f\u7981\u4e0d\u4f4f\u88ab \u5979\u9017\u5f97\u7b11\u4e86\u3002\u4f4e\u58f0\u95ee\u5df4\u9f99\u9053\uff1a\u201c\u600e\u9ebd\u5510\u52aa\u5973\u513f\u7684\u540d\u5b57\u5982\u6b64\u53e4\u602a\uff0c\u53eb\u505a\u98de\u7ea2\u5dfe\uff1f\u201d\u5df4\u9f99\u7b11\u9053\uff1a \u201c\u90a3\u4e0d\u662f\u5979\u7684\u771f\u6b63\u540d\u5b87\u3002\u5979\u7684\u540d\u5b57\u53eb\u54c8\u739b\u96c5\u3002\u4f46\u56e0\u4e3a\u5979\u5934\u4e0a\u603b\u662f\u7ed3\u7740\u7ea2\u5dfe\uff0c\u5979\u53c8\u559c\u6b22\u9a91\u9a6c\uff0c \u4f60\u522b\u77a7\u5979\u5e74\u7eaa\u5c0f\uff0c\u9a91\u8d77\u9a6c\u53ef\u8dd1\u5f97\u5feb\u5462\uff0c\u5c31\u50cf\u98de\u7684\u4e00\u6837\uff0c\u6240\u4ee5\u5927\u5bb6\u53eb\u5979\u505a\u98de\u7ea2\u5dfe\u3002\u201d \u3000\u3000\u5728\u6fc0\u8fa9\u4e4b\u4e2d\uff0c\u98de\u7ea2\u5dfe\u5e26\u6765\u4e86\u8f7b\u677e\u7684\u6c14\u6c1b\u3002\u4f17\u914b\u957f\u4e5f\u8d81\u673a\u4f1a\u8212\u6563\u4e00\u4e0b\u3002\u7b49\u5230\u7b11\u95f9\u505c\u6b62\uff0c\u5b5f \u8428\u601d\u53c8\u6273\u8d77\u8138\u5b54\u3002\u8bf4\u9053\uff1a\u201c\u5df4\u9f99\uff0c\u4f60\u5e26\u6765\u7684\u90a3\u4e2a\u6c49\u4eba\u53eb\u4ec0\u9ebd\u540d\u5b57\uff1f\u4ed6\u6709\u4f55\u8bdd\u8981\u8bf4\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u7f13\u7f13\u6b65\u51fa\u573a\u5fc3\uff0c\u56db\u65b9\u4e00\u63d6\uff0c\u9053\uff1a\u201c\u6211\u540d\u53eb\u5353\u4e00\u822a\uff0c\u4e43\u662f\u4e2d\u539f\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u5f1f \u5b50\u3002\u201d\u6b64\u8a00\u4e00\u51fa\uff0c\u5929\u5fb7\u4e0a\u4eba\u54d7\u7136\u53eb\u9053\uff1a\u201c\u5df4\u9f99\u5174\u6c49\u65cf\u7684\u6b66\u6797\u5b97\u6d3e\u52fe\u7ed3\uff0c\u83ab\u975e\u662f\u60f3\u7be1\u4f4d\u9ebd\uff1f\u201d \u5df4\u9f99\u51b7\u51b7\u4e00\u7b11\uff0c\u54c8\u8428\u514b\u7684\u914b\u957f\u867d\u7136\u7d20\u77e5\u5df4\u9f99\u5fe0\u5fc3\u803f\u803f\uff0c\u4f46\u8fd9\u51e0\u65e5\u5df4\u9f99\u7684\u7a81\u7136\u5931\u8e2a\uff0c\u5374\u4e5f\u4e0d\u80fd \u4e0d\u5f15\u8d77\u4ed6\u7684\u6000\u7591\uff0c\u542c\u4e86\u5929\u5fb7\u4e0a\u4eba\u7684\u6311\u62e8\uff0c\u4e0d\u7981\u95ee\u9053\uff1a\u201c\u6211\u7d20\u95fb\u6b66\u5f53\u6d3e\u4e43\u662f\u4e2d\u539f\u6b66\u6797\u7684\u5b97\u4e3b\uff0c \u4f60\u65e2\u662f\u6b66\u5f53\u7684\u638c\u95e8\u5f1f\u5b50\uff0c\u4e3a\u4f55\u5374\u5230\u6b64\u5730\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u4eec\u8fd9\u4e9b\u6c5f\u6e56\u4eba\u7269\u4e1c\u98d8\u897f\u6c64\u4e43\u662f\u5e38\u4e8b\uff0c\u738b\u7237\u4f60\u95ee\u6211\u4e3a\u4f55\u5230\u6b64\uff0c\u4e0d\u5982\u95ee\u9b4f\u5fe0 \u8d24\u7684\u9057\u515a\uff0c\u56e0\u4f55\u4e5f\u4f1a\u5230\u6b64\uff1f\u6211\u5230\u6b64\u4e0d\u8fc7\u662f\u4e3a\u4e86\u79c1\u4e8b\uff0c\u9b4f\u5fe0\u8d24\u7684\u9057\u515a\u4e0e\u6ee1\u5dde\u4f7f\u8005\u5230\u6b64\uff0c\u624d\u771f\u662f \u60f3\u7be1\u593a\u4f60\u7684\u6743\u67c4\uff0c\u751a\u81f3\u60f3\u8c0b\u6740\u4f60\u5462\uff01\u201d \u3000\u3000\u5929\u5fb7\u4e0a\u4eba\u9762\u8272\u5927\u53d8\uff0c\u65a5\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u8fd9\u91cc\u6709\u4ec0\u9ebd\u9b4f\u5fe0\u8d24\u9057\u515a\uff1f\u4f60\u4eec\u6c49\u65cf\u7684\u5185\u4e89\u4e0e\u6211 \u4eec\u4f55\u5e72\uff1f\u201d\u9b4f\u5fe0\u8d24\u638c\u653f\u4e4b\u65f6\uff0c\u66fe\u52d2\u7d22\u5404\u85e9\u5c5e\u738b\u516c\u591a\u7f34\u8d21\u7269\uff0c\u6240\u4ee5\u65b0\u7586\u5404\u65cf\u4e5f\u90fd\u77e5\u6b64\u4eba\u4e43\u660e\u671d \u7684\u5978\u9609\uff0c\u88ab\u52d2\u7d22\u4e86\u4e00\u4e24\u6b21\u5f8c\uff0c\u5f8c\u6765\u5c31\u7d22\u6027\u4e0d\u671d\u8d21\u4e86\u3002\u4f46\u5bf9\u9b4f\u5fe0\u8d24\u5374\u662f\u6df1\u6076\u75db\u7edd\u3002 \u3000\u3000\u5353\u4e00\u822a\u51b7\u51b7\u4e00\u7b11\uff0c\u7f24\u9053\uff1a\u201c\u8981\u6211\u628a\u4eba\u6307\u51fa\u6765\u7ed9\u4f60\u8ba4\u5417\uff1f\u201d\u5df4\u9f99\u957f\u5578\u4e00\u58f0\uff0c\u4ed6\u5728\u5e10\u84ec\u5916\u9884 \u65e9\u5e03\u7f6e\u597d\u7684\u624b\u4e0b\u7acb\u5373\u628a\u4e24\u4e2a\u4eba\u63a8\u4e86\u8fdb\u6765\uff0c\u8fd9\u4e24\u4eba\u4e00\u662f\u77f3\u6d69\uff0c\u4e00\u662f\u54c8\u5ddd\u3002 \u3000\u3000\u5353\u4e00\u822a\u6307\u7740\u77f3\u6d69\u9053\uff1a\u201c\u4ed6\u66fe\u5728\u4f60\u7684\u5e10\u7bf7\u4e2d\u4f4f\u8fc7\u51e0\u5929\uff0c\u4f60\u8fd9\u6837\u5feb\u5c31\u4e0d\u8ba4\u5f97\u4e86\u5417\uff1f\u201d\u77f3\u6d69\u81ea \u77e5\u662f\u7f51\u4e2d\u4e4b\u9c7c\uff0c\u53ea\u6c42\u514d\u6b7b\uff0c\u4e3a\u4e86\u60f3\u51cf\u8f7b\u81ea\u5df1\u7684\u7f6a\uff0c\u4e5f\u4f5c\u8bc1\u9053\uff1a\u201c\u4e0a\u4eba\uff0c\u6ca1\u6709\u4f60\u548c\u5b5f\u8428\u601d\u738b\u7237 \u7684\u6536\u5bb9\uff0c\u6211\u4e00\u4e2a\u5b64\u8eab\u6c49\u4eba\uff0c\u4e5f\u4e0d\u6562\u5230\u6b64\u5174\u98ce\u4f5c\u6d6a\u5440\uff01\u201d \u3000\u3000\u5b5f\u8428\u601d\u5fc3\u5934\u5927\u9707\uff0c\u5374\u5f3a\u4f5c\u9547\u5b9a\uff0c\u65a5\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u4e9b\u6c49\u4eba\u72e1\u733e\u591a\u7aef\uff0c\u7109\u77e5\u4f60\u4e0d\u662f\u4e70\u901a\u6b64 \u4eba\uff0c\u8981\u4ed6\u201c\u5192\u5145\u9b4f\u5fe0\u8d24\u9057\u515a\uff0c\u4e32\u540c\u8bec\u634f\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u5929\u4e0b\u5230\u5904\u90fd\u6709\u733e\u4e4b\u4eba\uff0c\u5c82\u662f\u6c49\u65cf\u624d\u6709\uff0c\u54c8\u8428\u514b\u7684\u5927\u738b\u516c\uff0c\u4f60\u82e5\u4e0d\u4fe1 \u4efb\u6c49\u4eba\uff0c\u8fd9\u91cc\u8fd8\u6709\u4e00\u4f4d\u4f60\u5fe0\u5fc3\u7684\u90e8\u4e0b\u3002\u201d \u3000\u3000\u54c8\u5ddd\u5e94\u58f0\u800c\u51fa\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u738b\u7237\uff0c\u6211\u5bf9\u4e0d\u4f4f\u4f60\uff01\u5929\u5fb7\u4e0a\u4eba\u8d77\u5148\u672c\u662f\u548c\u6211\u8bf4\u8981\u6276\u52a9\u4f60\u505a \u5404\u65cf\u76df\u4e3b\uff0c\u56e0\u6b64\u6211\u624d\u542c\u4ed6\u7684\u8bdd\uff0c\u548c\u5b5f\u8428\u601d\u738b\u7237\u53ca\u6ee1\u6d32\u4f7f\u8005\u8054\u7edc\uff0c\u51c6\u5907\u5c06\u6765\u4e00\u7edf\u5929\u5c71\u5357\u5317\u3002\u73b0 \u5728\u6211\u624d\u77e5\u9053\u4ed6\u4eec\u53e6\u6709\u9634\u8c0b\uff01\u4ed6\u4eec\u662f\u51c6\u5907\u5229\u7528\u6211\u6765\u4ee3\u66ff\u5df4\u9f99\uff0c\u5c06\u4f60\u7684\u5175\u6743\u7be1\u593a\u4e4b\u5f8c\uff0c\u7136\u5f8c\u8feb\u4f60 \u5c31\u8303\uff0c\u505a\u4ed6\u4eec\u7684\u5080\u5121\u3002\u82e5\u4f60\u4e0d\u542c\uff0c\u5c31\u5c06\u4f60\u6740\u6389\u3002\u5f85\u5c06\u6765\u6ee1\u6d32\u4eba\u5173\u4e4b\u5f8c\uff0c\u518d\u7531\u5b5f\u8428\u601d\u738b\u7237\u5e76\u541e \u5404\u65cf\u5f00\u56fd\u79f0\u5e1d\uff0c\u505a\u6ee1\u6d32\u4eba\u7684\u5c5e\u56fd\uff01\u201d \u3000\u3000\u54c8\u5ddd\u7684\u8bdd\u8bf4\u51fa\uff0c\u5168\u573a\u8f70\u52a8\u3002\u5b5f\u8428\u601d\u559d\u9053\uff1a\u201c\u4f60\u6709\u4f55\u8bc1\u636e\uff1f\u4f60\u542b\u8840\u55b7\u4eba\uff01\u54c8\u8428\u514b\u5927\u738b\u516c\uff01 \u4f60\u7684\u90e8\u4e0b\u8bcb\u6bc1\u6211\uff0c\u6211\u5411\u4f60\u95ee\u7f6a\uff01\u201d\u5565\u8428\u514b\u7684\u738b\u516c\u4e5f\u614c\u4e86\uff0c\u559d\u9053\uff1a\u201c\u54c8\u5ddd\uff0c\u4f60\u6ca1\u6709\u8bc1\u636e\uff0c\u53ef\u4e0d \u80fd\u4e71\u8bf4\uff01\u201d \u3000\u3000\u54c8\u5ddd\u4e0d\u614c\u4e0d\u5fd9\u8bf4\u9053\uff1a\u201c\u8bc1\u636e\u9ebd\uff1f\u6211\u65e9\u5df2\u5e26\u6765\u4e86\uff1f\u201d\u5929\u5fb7\u4e0a\u4eba\u53cc\u6307\u4e00\u5f39\uff0c\u4e00\u628a\u53c9\u725b\u8089\u7684\u5c0f \u53c9\u95ea\u7535\u4e00\u822c\u5411\u54c8\u5ddd\u54bd\u5589\u98de\u6765\uff01 \u3000\u3000\u5353\u4e00\u822a\u65e9\u5c31\u63d0\u795e\u9632\u5907\uff0c\u8dc3\u524d\u4e24\u6b65\uff0c\u628a\u624b\u4e00\u6284\uff0c\u5c06\u90a3\u67c4\u5c0f\u53c9\u63a5\u5230\u638c\u5fc3\uff0c\u5927\u53eb\u9053\uff1a\u201c\u5929\u5fb7\u4e0a \u4eba\u60f3\u884c\u51f6\u706d\u53e3\uff0c\u8fd9\u4e5f\u662f\u8bc1\u636e\uff01\u201d\u5510\u52aa\u559d\u9053\uff1a\u201c\u628a\u4ed6\u5148\u62ff\u4e0b\u6765\uff01\u201d \u3000\u3000\u7eb7\u4e71\u4e2d\uff0c\u5ffd\u542c\u5f97\u5973\u5b69\u5b50\u7684\u5c16\u53eb\uff0c\u5929\u5fb7\u4e0a\u4eba\u7a81\u7136\u4e00\u624b\u631f\u8d77\u4e86\u98de\u7ea2\u5dfe\uff0c\u8df3\u4e0a\u5728\u8349\u5730\u4e0a\u642d\u8d77 \u7684\u3001\u51c6\u5907\u5728\u4f1a\u5f8c\u4e3e\u884c\u796d\u5929\u5178\u793c\u7684\u53f0\u4e0a\uff0c\u72de\u7b11\u9053\uff1a\u201c\u5510\u52aa\uff0c\u5df4\u9f99\uff0c\u4f60\u4eec\u4e70\u901a\u4e86\u54c8\u5ddd\u4e0e\u8fd9\u4e24\u4e2a\u6c49 \u4eba\uff0c\u60f3\u9677\u5bb3\u6211\u9ebd\uff1f\u54fc\uff0c\u6211\u4e5f\u4e0d\u662f\u597d\u76f8\u4e0e\u7684\uff01\u4f60\u5973\u513f\u7684\u6027\u547d\u5728\u6211\u638c\u5fc3\uff0c\u6211\u53ea\u8fd9\u9ebd\u4e00\u4f7f\u52b2\uff0c\u5979\u5c31 \u5b8c\u4e86\uff01\u201d\u8bf4\u65f6\u4ee5\u624b\u4f5c\u6001\uff0c\u634f\u7740\u98de\u7ea2\u5dfe\u9888\u9aa8\u3002 \u3000\u3000\u5510\u52aa\u559d\u9053\uff1a\u201c\u65e0\u803b\u51f6\u50e7\uff0c\u628a\u5979\u653e\u4e0b\uff01\u201d\u5404\u65cf\u914b\u957f\u4ea6\u65e0\u4e0d\u6124\u6012\uff0c\u53ef\u662f\u98de\u7ea2\u5dfe\u5728\u4ed6\u624b\u4e2d\uff0c\u5948 \u4ed6\u4e0d\u5f97\uff01 \u3000\u3000\u5580\u5c71\u65cf\u7684\u8001\u914b\u957f\u9053\uff1a\u201c\u5929\u5fb7\uff0c\u6709\u8bdd\u597d\u8bf4\uff0c\u4f60\u6b3a\u8d1f\u4e00\u4e2a\u5973\u5b69\u5b50\u4e0d\u5bb3\u81ca\u9ebd\uff1f\u201d\u5929\u5fb7\u4e0a\u4eba\u7b11 \u9053\uff1a\u201c\u5bf9\u5440\uff0c\u5927\u5bb6\u6709\u8bdd\u597d\u8bf4\u624d\u662f\u9053\u7406\u3002\u6211\u4e5f\u4e0d\u60f3\u5728\u4f60\u4eec\u7684\u8349\u539f\u4e0a\u4e86\u3002\u5510\u52aa\u4f60\u9001\u6211\u56de\u897f\u85cf\u53bb\uff0c \u5230\u4e86\u897f\u85cf\u4e4b\u5f8c\uff0c\u6211\u518d\u628a\u5973\u513f\u4ea4\u8fd8\u7ed9\u4f60\uff01\u201d \u3000\u3000\u5929\u5fb7\u4e0a\u4eba\u81ea\u77e5\u4e0d\u5bb9\u65bc\u4f17\uff0c\u6240\u4ee5\u8981\u85c9\u6b64\u8131\u8eab\u3002\u5510\u52aa\u5927\u6124\uff0c\u5ffd\u89c1\u98de\u7ea2\u5dfe\u5728\u53f0\u4e0a\u5411\u4ed6\u7750\u773c\uff0c\u4e0d \u7981\u53eb\u9053\uff1a\u201c\u54c8\u739b\u96c5\uff0c\u4f60\u4e0d\u8981\u5bb3\u6015\u3002\u6211\u7b54\u5e94\u4ed6\u4fbf\u662f\uff01\u201d\u98de\u7ea2\u5dfe\u5728\u53f0\u4e0a\u53eb\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u5bb3\u6015 \u5440\uff1f\u201d \u3000\u3000\u5929\u5fb7\u4e0a\u4eba\u542c\u5f97\u5510\u52aa\u7b54\u5e94\uff0c\u5fc3\u4e2d\u4e00\u559c\uff0c\u624b\u6307\u653e\u677e\u3002\u5176\u5b9e\u4ed6\u4e5f\u662f\u6015\u634f\u5f97\u7d27\u4e86\uff0c\u5f04\u6b7b\u4e86\u8fd9\u5973\u5b69 \u5b50\u65f6\u81ea\u5df1\u4e5f\u8131\u8eab\u4e0d\u4e86\u3002\u90a3\u77e5\u624b\u6307\u521a\u521a\u653e\u677e\uff0c\u51b7\u4e0d\u9632\u98de\u7ea2\u5dfe\u5c0f\u624b\u5411\u4ed6\u80c1\u4e0b\u4e00\u62cd\uff0c\u62cd\u7684\u5730\u65b9\uff0c\u6b63 \u662f\u8981\u5bb3\uff0c\u5929\u5fb7\u4e0a\u4eba\u5927\u53eb\u4e00\u58f0\uff0c\u98de\u7ea2\u5dfe\u6323\u8131\u4ed6\u7684\u638c\u63e1\uff0c\u843d\u5728\u53f0\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u4e43\u662f\u6781\u51f6\u6b8b\u7684\u638c\u6cd5\uff0c\u5e78\u800c\u98de\u7ea2\u5dfe\u529b\u5c0f\uff0c\u8981\u4e0d\u7136\u4fbf\u662f\u808b\u65ad\u9aa8\u6298\u4e4b\u6b83\uff0c\u94d9\u662f\u5982\u6b64\uff0c\u5929 \u5fb7\u4e0a\u4eba\u5df2\u75db\u5f97\u54c7\u54c7\u5927\u53eb\uff0c\u98de\u8eab\u4e00\u8d77\uff0c\u53c8\u6251\u8fc7\u53bb\u3002\u98de\u7ea2\u5dfe\u8eab\u6cd5\u7adf\u6781\u8f7b\u7075\uff0c\u5ffd\u7136\u4e00\u4e2a\u8f6c\u8eab\u626c\u624b\uff0c \u5929\u5fb7\u4e0a\u4eba\u7a81\u89c9\u773c\u524d\u4e00\u7247\u94f6\u5149\u4e71\u95ea\uff0c\u6025\u5fd9\u6325\u821e\u50e7\u888d\u62b5\u6321\uff0c\u98de\u7ea2\u5dfe\u8fde\u53d1\u4e24\u628a\u98de\u9488\uff0c\u90fd\u88ab\u4ed6\u62c2\u843d \u4e86\u3002 \u3000\u3000\u8fd9\u51e0\u4e0b\u5b50\u5feb\u5982\u7535\u5149\u77f3\u706b\uff0c\u53f0\u4e0b\u7684\u4eba\u8fd8\u770b\u4e0d\u6e05\u98de\u7ea2\u5dfe\u662f\u600e\u6837\u6323\u8131\u51fa\u6765\u7684\uff0c\u5353\u4e00\u822a\u8fd9\u4e00\u60ca\u5374 \u662f\u975e\u540c\u5c0f\u9119\uff01\u98de\u7ea2\u5dfe\u90a3\u4e0b\u624b\u6cd5\uff0c\u6b63\u662f\u7389\u7f57\u5239\u7684\u72ec\u95e8\u7edd\u6280\uff0c\u7389\u7f57\u5239\u5f53\u5e74\u638c\u51fb\u5f52\u6709\u7ae0\uff0c\u62a2\u593a\u91d1\u9a6c \u978d\uff0c\u7528\u7684\u5c31\u662f\u8fd9\u4e00\u624b\uff01\u800c\u90a3\u4e24\u628a\u98de\u9488\uff0c\u4e5f\u6b63\u662f\u7389\u7f57\u5239\u72ec\u95e8\u6697\u5668\u2014\u2014\u4e5d\u661f\u5b9a\u5f62\u9488\u7684\u6253\u6cd5\u3002\u5353\u4e00 \u822a\u505a\u68a6\u4e5f\u60f3\u4e0d\u5230\uff0c\u8fd9\u4e2a\u8349\u539f\u4e0a\u738b\u516c\u7684\u5c0f\u5973\u513f\uff0c\u7adf\u7136\u5f97\u4e86\u7389\u7f57\u5239\u7684\u771f\u4f20\uff01 \u3000\u3000\u8fd9\u65f6\u5510\u52aa\u3001\u5df4\u9f99\u7b49\u7eb7\u7eb7\u6251\u53bb\uff0c\u5353\u4e00\u822a\u957f\u5578\u4e00\u58f0\uff0c\u8eab\u5f62\u6025\u8d77\uff0c\u5f8c\u53d1\u5148\u81f3\uff0c\u63a0\u8fc7\u4f17\u4eba\u5934\u9876\uff0c \u98de\u5230\u53f0\u4e0a\u3002\u5929\u5fb7\u4e0a\u4eba\u62c2\u843d\u4e86\u98de\u7ea2\u5dfe\u7684\u94f6\u9488\u6697\u5668\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u521a\u521a\u6293\u4e0b\uff0c\u88ab\u5353\u4e00\u822a\u4e00\u6321\uff0c\u9000\u5f8c \u51e0\u6b65\uff0c\u98de\u7ea2\u5dfe\u4e00\u7b11\u8dc3\u843d\u53f0\u4e0b\uff0c\u8df3\u5230\u4e86\u7236\u4eb2\u7684\u6000\u62b1\u4e4b\u4e2d\u3002 \u3000\u3000\u53f0\u4e0a\u5929\u5fb7\u4e0a\u4eba\u9762\u8272\u7070\u767d\uff0c\u53cc\u77b3\u55b7\u706b\uff0c\u62d4\u51fa\u957f\u5251\uff0c\u72b9\u81ea\u8d1f\u9685\u987d\u6297\u3002\u5353\u4e00\u822a\u66f4\u4e0d\u6253\u8bdd\uff0c\u5251\u5f0f \u4e00\u4eae\uff0c\u7acb\u523b\u8fdb\u62db\u3002\u5ffd\u542c\u5f97\u53f0\u4e0b\u53c8\u662f\u4e00\u9635\u5927\u4e71\u3002 \u3000\u3000\u539f\u6765\u662f\u5580\u8fbe\u5c14\u65cf\u7684\u914b\u957f\u5b5f\u8428\u601d\u89c1\u9634\u8c0b\u8d25\u9732\uff0c\u5e26\u4e86\u624b\u4e0b\u7684\u4eba\u79bb\u5f00\u4f1a\u573a\uff0c\u5927\u58f0\u53d1\u8bdd\u9053\uff1a\u201c\u76df \u4e3b\u6211\u4e0d\u505a\u4e86\uff0c\u6b64\u5f8c\u6211\u4e0e\u4f60\u4eec\u5404\u4e0d\u76f8\u6d89\uff01\u201d\u4f17\u4eba\u867d\u7136\u6068\u4ed6\u6240\u4e3a\uff0c\u4f46\u4ed6\u5230\u5e95\u662f\u4e00\u65cf\u4e4b\u957f\uff0c\u5927\u5bb6\u4e5f \u4e0d\u4fbf\u62e6\u963b\uff0c\u8ba9\u4ed6\u79bb\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u5fb7\u4e0a\u4eba\u5df2\u4e0e\u5353\u4e00\u822a\u4ea4\u624b\uff0c\u5929\u5fb7\u4e0a\u4eba\u62da\u4e86\u6027\u547d\uff0c\u52c7\u731b\u8fdb\u653b\uff0c\u4ed6\u7684\u201c\u5929\u9f99\u5251\u6cd5\u201d\u4e5f\u786e \u5b9e\u51cc\u5389\u975e\u5e38\uff0c\u5341\u516b\u8def\u4e00\u767e\u516d\u5341\u4e8c\u624b\u5faa\u73af\u53d8\u5316\uff0c\u65bd\u5c55\u5f00\u6765\uff0c\u5251\u98ce\u864e\u864e\uff0c\u75be\u5982\u98ce\u96e8\uff0c\u5353\u4e00\u822a\u89c1\u4ed6 \u62da\u547d\uff0c\u5012\u4e5f\u4e0d\u6562\u8f7b\u654c\uff0c\u5c55\u6b66\u5f53\u5251\u6cd5\u62a4\u7740\u5168\u8eab\uff0c\u6c14\u5b9a\u795e\u95f2\uff0c\u4ece\u5bb9\u5e94\u4ed8\u3002 \u3000\u3000\u54c8\u5ddd\u8d70\u5230\u914b\u957f\u8eab\u8fb9\uff0c\u628a\u4e00\u5927\u675f\u6587\u4ef6\u9012\u8fc7\u53bb\u8bf4\u9053\uff1a\u201c\u4e3b\u516c\uff0c\u8fd9\u5c31\u662f\u6211\u521a\u624d\u6240\u8bf4\u7684\u8bc1\u636e\u3002\u201d \u539f\u6765\u5df4\u9f99\u8001\u8c0b\u6df1\u7b97\uff0c\u6f5c\u56de\u8349\u539f\uff0c\u4f8d\u5929\u5fb7\u4e0a\u4eba\u53bb\u5f00\u4f1a\u4e4b\u65f6\uff0c\u624d\u5e26\u5353\u4e00\u822a\u4e0e\u54c8\u5ddd\u51b2\u8fdb\u4ed6\u7684\u5e10\u5e55\uff0c \u5929\u5fb7\u4e0a\u4eba\u7559\u4e0b\u770b\u5b88\u7684\u51e0\u4e2a\u5f92\u5f1f\uff0c\u90a3\u662f\u4ed6\u4eec\u5bf9\u624b\uff0c\u4e00\u7f51\u6210\u64d2\u3002\u5df4\u9f99\u5728\u5e10\u5e55\u4e2d\u4e00\u641c\uff0c\u641c\u51fa\u5929\u5fb7\u4e0a \u4eba\u4e0e\u5b5f\u8428\u601d\u7684\u6765\u5f80\u4e66\u4fe1\uff0c\u8fd8\u6709\u4e0e\u6ee1\u6d32\u4f7f\u8005\u8054\u7edc\u7684\u6587\u4ef6\u7b49\u7b49\uff0c\u90fd\u5305\u6210\u4e86\u4e00\u5305\uff0c\u4ea4\u7ed9\u54c8\u5ddd\u3002\u73b0\u5728 \u54c8\u5ddd\u5c31\u5c06\u8fd9\u675f\u6587\u4ef6\u4ea4\u7ed9\u914b\u957f\u3002 \u3000\u3000\u54c8\u8428\u514b\u7684\u914b\u957f\u6124\u7136\u9053\uff1a\u201c\u4e0d\u5fc5\u770b\u4e86\uff01\u6211\u5f15\u72fc\u4eba\u5ba4\uff0c\u5b9e\u5728\u6127\u5bf9\u4f60\u4eec\uff01\u201d\u54c8\u5ddd\u9053\uff1a\u201c\u4ee5\u524d\u6211 \u4e5f\u53d7\u4ed6\u8499\u6df7\uff0c\u770b\u4e86\u8fd9\u4e9b\u4e66\u4fe1\u6587\u4ef6\uff0c\u624d\u77e5\u4ed6\u4eec\u7684\u5978\u8c0b\u5982\u6b64\u4e4b\u5927\u3002\u201d\u5510\u52aa\u643a\u4e86\u98de\u7ea2\u5dfe\uff0c\u6765\u5230\u54c8\u8428 \u514b\u914b\u957f\u773c\u524d\uff0c\u7b11\u9053\uff1a\u201c\u5982\u4eca\u5df3\u6c34\u843d\u77f3\u51fa\uff0c\u8bc1\u636e\u786e\u662f\u4e0d\u5fc5\u770b\u4e86\uff0c\u54b1\u4eec\u4e14\u770b\u4ed6\u4eec\u6597\u5251\u3002\u201d \u3000\u3000\u54c8\u8428\u514b\u7684\u914b\u957f\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u5bf9\u54c8\u5ddd\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u4e0a\u53bb\u52a9\u90a3\u6c49\u4eba\uff1f\u201d\u54c8\u5ddd\u7b11\u9053\uff1a\u201c\u8fd9\u6c49 \u4eba\u662f\u4e00\u6d3e\u638c\u95e8\uff0c\u4e0d\u559c\u6b22\u522b\u4eba\u5e2e\u7684\u3002\u201d\u54c8\u5ddd\u662f\u7ec3\u6b66\u4e4b\u4eba\uff0c\u591a\u5c11\u61c2\u5f97\u6c49\u65cf\u6b66\u6797\u7684\u89c4\u77e9\u3002 \u3000\u3000\u54c8\u8428\u514b\u7684\u914b\u957f\u89c1\u5929\u5fb7\u4e0a\u4eba\u51f6\u731b\u4e4b\u6781\uff0c\u5251\u5149\u970d\u970d\uff0c\u7adf\u4f3c\u5df2\u628a\u5353\u4e00\u822a\u5708\u5728\u5f53\u4e2d\uff0c\u4e0d\u7981\u62c5\u5fe7 \u9053\uff1a\u201c\u5929\u5fb7\u8fd9\u6b66\u529f\u5389\u5bb3\u975e\u51e1\uff0c\u8fd9\u4e2a\u6c49\u4eba\u80fd\u662f\u4ed6\u7684\u5bf9\u624b\u5417\uff1f\u201d \u3000\u3000\u54c8\u5ddd\u9053\uff1a\u201c\u5728\u524d\u51e0\u5929\uff0c\u6211\u4e5f\u4ee5\u4e3a\u8fd9\u7684\u6b66\u529f\u5929\u4e0b\u65e0\u654c\u3002\u201d\u54c8\u8428\u514b\u914b\u957f\u8be7\u9053\uff1a\u201c\u600e\u9ebd\uff0c\u6709\u4eba \u6bd4\u4ed6\u66f4\u5f3a\u5417\uff1f\u201d\u9700\u77e5\u54c8\u8428\u514b\u7684\u914b\u957f\u5c31\u662f\u56e0\u4e3a\u5929\u5fb7\u4e0a\u4eba\u66fe\u5728\u4ed6\u9762\u524d\u663e\u9732\u4e86\u6781\u5389\u5bb3\u7684\u6b66\u529f\uff0c\u624d\u8058 \u8bf7\u4ed6\u4e3a\u62a4\u6cd5\u5e08\u7684\u3002\u800c\u8fd9\u51e0\u5e74\u6765\uff0c\u5929\u5fb7\u4e0a\u4eba\u4e5f\u786e\u662f\u4ece\u65e0\u5bf9\u624b\uff0c\u6240\u4ee5\u54c8\u8428\u514b\u7684\u914b\u957f\u5bf9\u4ed6\u7684\u6b66\u529f\u5df2 \u5230\u4e86\u8ff7\u4fe1\u7a0b\u5ea6\u3002 \u3000\u3000\u54c8\u5ddd\u9053\uff1a\u201c\u8fd9\u6c49\u4eba\u5c31\u6bd4\u4ed6\u5f3a\u5f97\u591a\uff1f\u201d\u54c8\u8428\u514b\u7684\u914b\u957f\u5c06\u4fe1\u5c06\u7591\uff0c\u65bc\u662f\u54c8\u5ddd\u4e00\u9762\u770b\u53f0\u4e0a\u6597 \u5251\uff0c\u4e00\u9762\u5c06\u90a3\u65e5\u5728\u6155\u58eb\u5854\u5c71\u4ee5\u53c1\u654c\u4e00\uff0c\u88ab\u5353\u4e00\u822a\u6253\u8d25\u4e4b\u4e8b\u8bf4\u51fa\uff0c\u5f85\u8bf4\u5b8c\u4e4b\u65f6\uff0c\u53f0\u4e0a\u7684\u5f62\u52bf\u5df2 \u662f\u5927\u53d8\uff1f \u3000\u3000\u5353\u4e00\u822a\u7684\u5251\u6cd5\u672c\u5c31\u6bd4\u5929\u5fb7\u4e0a\u4eba\u5389\u5bb3\uff01\u56e0\u4e0d\u60f3\u548c\u4ed6\u62da\u547d\uff0c\u6240\u4ee5\u8d77\u521d\u5b88\u4e0d\u653b\u3002\u8fd9\u65f6\u5929\u5fb7\u4e0a\u4eba \u4e00\u767e\u516d\u5341\u4e8c\u624b\u7684\u5929\u9f99\u5251\u6cd5\u5df2\u5168\u90e8\u4f7f\u5b8c\uff0c\u81ea\u8ba8\u4e0d\u5230\u534a\u70b9\u4fbf\u5b9c\uff0c\u9510\u6c14\u987f\u6298\uff0c\u5fc3\u53c8\u7126\u71e5\uff0c\u5251\u6cd5\u6e10\u6e10 \u6563\u4e71\u3002\u5353\u4e00\u822a\u731b\u559d\u4e00\u58f0\uff0c\u5251\u6cd5\u9aa4\u53d8\uff0c\u72b9\u5982\u60ca\u96f7\u9a87\u7535\uff0c\u63a5\u8fde\u53cd\u51fb\uff0c\u76f4\u4ee4\u53f0\u4e0b\u7684\u4eba\u770b\u5f97\u76ee\u7729\u795e \u6447\u3002\u9163\u6597\u4e4b\u4e2d\uff0c\u5ffd\u89c1\u5929\u5fb7\u4e0a\u4eba\u731b\u529b\u4e00\u51b2\uff0c\u957f\u5251\u500f\u5730\u6307\u5230\u5353\u4e00\u822a\u9762\u95e8\uff01 \u3000\u3000\u54c8\u8428\u514b\u7684\u914b\u957f\u201c\u554a\u5440\u201d\u4e00\u58f0\uff0c\u4ee5\u4e3a\u662f\u5929\u5fb7\u4e0a\u4eba\u4e34\u8d25\u4f7f\u51fa\u7edd\u62db\uff0c\u8fd9\u6c49\u4eba\u96be\u9003\u6bd2\u624b\u4e86\u3002\u54c8\u5ddd \u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u5ffd\u542c\u5f97\u5353\u4e00\u822a\u559d\u58f0\uff1a\u201c\u7740\uff01\u201d\u770b\u4e5f\u672a\u770b\u6e05\u695a\uff0c\u53ea\u89c1\u5929\u5fb7\u4e0a\u4eba\u5e9e\u5927\u7684\u8eab\u8eaf\u5df2\u88ab\u8e22 \u7ffb\u53f0\u4e0b\uff0c\u80f8\u53e3\u88ab\u5251\u6420\u4e86\u4e00\u4e2a\u7a9f\u7abf\uff0c\u8840\u5982\u6cc9\u6d8c\uff0c\u663e\u89c1\u4e0d\u80fd\u6d3b\u4e86\u3002\u539f\u6765\u5929\u5fb7\u4e0a\u4eba\u60c5\u6025\u62da\u547d\uff0c\u5353\u4e00 \u822a\u6545\u610f\u5356\u4e2a\u7834\u7efd\uff0c\u4ee4\u4ed6\u5251\u62db\u7528\u8001\uff0c\u7136\u5f8c\u731b\u65bd\u6740\u624b\uff0c\u4ee4\u4ed6\u65e0\u6cd5\u64a4\u5251\u9632\u8eab\u3002\u8fd9\u6b63\u662f\u6b66\u5f53\u8fde\u73af\u5251\u4e2d \u7684\u593a\u547d\u62db\u6570\u3002 \u3000\u3000\u54c8\u8428\u514b\u914b\u957f\u5927\u4e3a\u4f69\u670d\uff0c\u8fde\u58f0\u8d5e\u53f9\u9053\uff1a\u201c\u4eca\u65e5\u5927\u5f00\u773c\u754c\uff0c\u8fd9\u624d\u662f\u5929\u4e0b\u65e0\u53cc\u7684\u5251\u6cd5\u3002\u201d\u5510\u52aa \u5fae\u5fae\u4e00\u7b11\uff0c\u7b11\u4ed6\u89c1\u95fb\u4e0d\u5e7f\uff0c\u5fc3\u9053\uff1a\u8981\u662f\u4ee4\u4ed6\u89c1\u5230\u54c8\u739b\u96c5\u7684\u5e08\u5085\uff0c\u4ed6\u66f4\u8981\u4e94\u4f53\u6295\u5730\u54e9\u3002 \u3000\u3000\u5929\u5fb7\u4e0a\u4eba\u5df2\u6b7b\uff0c\u5b5f\u8428\u601d\u5df2\u9003\uff0c\u5404\u65cf\u7684\u4f1a\u76df\uff0c\u5f88\u5feb\u5c31\u5f97\u51fa\u7ed3\u679c\uff0c\u7f57\u5e03\u65cf\u7684\u914b\u957f\u5510\u52aa\u5f97\u5230\u591a \u6570\u62e5\u62a4\uff0c\u88ab\u63a8\u4e3a\u5317\u7586\u5404\u65cf\u3001\u5404\u90e8\u843d\u7684\u76df\u4e3b\u3002\u5178\u793c\u5b8c\u6210\u4e4b\u5f8c\uff0c\u89c2\u793c\u7684\u5404\u65cf\u65cf\u4eba\u6b22\u547c\u9707\u5929\uff0c\u63a5\u7740 \u4fbf\u662f\u4e00\u4e2a\u901a\u5bb5\u8fbe\u65e6\u7684\u72c2\u6b22\u5927\u4f1a\u3002 \u3000\u3000\u54c8\u8428\u514b\u7684\u914b\u957f\u518d\u53c1\u5411\u5353\u4e00\u822a\u9053\u8c22\uff0c\u5e76\u60f3\u633d\u7559\u4ed6\u5728\u54c8\u8428\u514b\u65cf\u4e2d\u4f20\u6388\u4ed6\u5e10\u4e0b\u6b66\u58eb\u5251\u6cd5\u3002\u5353\u4e00 \u822a\u59d4\u5a49\u63a8\u8f9e\uff0c\u5374\u72ec\u81ea\u53bb\u627e\u5510\u52aa\u3002 \u3000\u3000\u5510\u52aa\u6b63\u5728\u548c\u5973\u513f\u770b\u8349\u539f\u4e0a\u7684\u8d5b\u9a6c\u6e38\u620f\u3002\u5353\u4e00\u822a\u95ee\u9053\uff1a\u201c\u738b\u7237\uff0c\u6211\u60f3\u548c\u4f60\u8bf4\u51e0\u53e5\u8bdd\uff0c\u884c \u5417\uff1f\u201d\u5510\u52aa\u9053\uff1a\u201c\u6211\u4e5f\u6b63\u60f3\u627e\u4f60\u9053\u8c22\u5462\u3002\u4f60\u5728\u53c1\u5929\u4e4b\u4e2d\uff0c\u63a5\u8fde\u6551\u4e86\u6211\u4eec\u7236\u5973\u7684\u6027\u547d\uff0c\u6211\u4eec\u4e0d \u77e5\u8be5\u600e\u6837\u8c22\u4f60\u624d\u597d\uff01\u201d\u98de\u7ea2\u5dfe\u4e5f\u5f88\u559c\u6b22\u5353\u4e00\u822a\uff0c\u8ffd\u7b11\u8fb9\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u771f\u662f\u597d\u4eba\uff0c\u4e0d\u662f\u4f60 \u6765\uff0c\u6211\u51e0\u4e4e\u88ab\u90a3\u51f6\u50e7\u518d\u6293\u7740\u4e86\u3002\u6211\u5e08\u5085\u8bf4\uff0c\u7537\u4eba\u5f88\u5c11\u597d\u7684\uff0c\u53eb\u6211\u957f\u5927\u4e86\u4e0d\u8981\u7406\u90a3\u4e9b\u81ed\u7537\u5b50\uff0c \u6211\u770b\u4f60\u5c31\u5f88\u597d\u561b\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u4e0d\u89c9\u82e6\u7b11\u3002\u5510\u52aa\u643a\u4e86\u5973\u513f\u548c\u4ed6\u79bb\u5f00\u4e86\u55a7\u56a3\u7684\u4eba\u7fa4\uff0c\u5728\u8349\u539f\u4e0a\u6f2b\u6b65\u3002\u4ef2\u590f\u591c\u7684\u8349 \u539f\uff0c\u5929\u7a7a\u7279\u522b\u660e\u51c0\uff0c\u6ee1\u5929\u661f\u6597\uff0c\u5c31\u50cf\u4e00\u7c92\u7c92\u5b9d\u77f3\u5d4c\u5728\u84dd\u7ed2\u5e55\u4e0a\uff0c\u95ea\u95ea\u53d1\u5149\u3002\u5353\u4e00\u822a\u51dd\u89c6\u661f \u8fb0\uff0c\u604d\u60da\u5982\u68a6\u3002\u5510\u52aa\u597d\u751f\u5947\u602a\uff0c\u95ee\u9053\uff1a\u201c\u5353\u5148\u751f\u6709\u4ec0\u9ebd\u8bdd\u8bf4\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u9053\uff1a\u201c\u6055\u6211\u5192\u6627\uff0c\u8bf7\u95ee\u6559\u8fd9\u4f4d\u5c0f\u516c\u4e3b\u6b66\u827a\u7684\u7a76\u662f\u4f55\u4eba\uff1f\u201d\u98de\u7ea2\u5dfe\u7750\u7750\u987d\u76ae\u7684\u773c \u775b\uff0c\u7b11\u9053\uff1a\u201c\u5e08\u5085\u5429\u5490\u8fc7\uff0c\u4e0d\u51c6\u6211\u5bf9\u4efb\u4f55\u4eba\u8bf4\u7684\u3002\u201d\u5510\u52aa\u7b11\u9053\uff1a\u201c\u5353\u5148\u751f\u4e0d\u6bd4\u522b\u4eba\uff0c\u4f46\u8bf4\u65e0 \u59a8\u3002\u201d\u98de\u7ea2\u5dfe\u9053\uff1a\u201c\u90a3\u9ebd\u7239\u7239\u4f60\u8bf4\u3002\u5e08\u7236\u77e5\u9053\u4e86\u4e5f\u4e0d\u80fd\u602a\u6211\u3002\u201d\u5510\u52aa\u7b11\u9053\uff1a\u201c\u4f60\u771f\u662f\u4f60\u5e08\u7236 \u7684\u597d\u5f92\u5f1f\u3002\u201d \u3000\u3000\u5510\u52aa\u7ea7\u9053\uff1a\u201c\u5353\u5148\u751f\uff0c\u4f60\u5148\u542c\u6211\u8bf4\u4e00\u4e2a\u6545\u4e8b\u3002\u6211\u5728\u7ea6\u5341\u5e74\u4e4b\u524d\uff0c\u66fe\u5230\u5317\u4eac\u8fdb\u8d21\u3002\u90a3\u4e2a\u7687 \u5e1d\u5e74\u7eaa\u5f88\u8f7b\uff0c\u5bf9\u6211\u5f88\u662f\u4e0d\u9519\u3002\u6211\u56de\u6765\u65f6\uff0c\u4ed6\u8d50\u4e86\u6211\u8bc8\u591a\u793c\u7269\uff0c\u4e0d\u60f3\u5c31\u56e0\u4e3a\u8fd9\u4e9b\u5b9d\u7269\uff0c\u6211\u51e0\u4e4e \u547d\u4e27\u5f02\u4e61\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5374\u662f\u4e3a\u4f55\uff1f\u671d\u5ef7\u6ca1\u6709\u6d3e\u4eba\u62a4\u9001\u5417\uff1f\u201d\u5510\u52aa\u9053\uff1a\u201c\u522b\u63d0\u5566\uff0c\u90a3\u4e9b\u62a4 \u9001\u7684\u4eba\u7adf\u7136\u4e32\u540c\u671d\u5ef7\u7684\u53db\u519b\uff0c\u5408\u5925\u52ab\u6211\u3002\u597d\u5728\u6211\u547d\u4e0d\u8be5\u6b7b\uff0c\u5728\u6700\u5371\u9669\u7684\u65f6\u5019\uff0c\u6709\u4e00\u4e2a\u5973\u82f1\u96c4 \u7a81\u5982\u5176\u6765\uff0c\u5c06\u6211\u6551\u4e86\u3002\u201d\u5353\u4e00\u822a\u867d\u672a\u542c\u7389\u7f57\u5239\u8bf4\u8fc7\u8fd9\u4e2a\u6545\u4e8b\uff0c\u8fd9\u65f6\u4ea6\u5df2\u6599\u5230\u662f\u5979\uff0c\u4e0d\u7981\u53eb \u9053\uff1a\u201c\u662f\u7389\u7f57\u5239\uff01\u201d \u3000\u3000\u5510\u52aa\u6115\u7136\u9053\uff1a\u201c\u4ec0\u9ebd\u7389\u7f57\u5239\uff0c\u5979\u53eb\u505a\u7ec3\u9713\u88f3\uff0c\u5230\u4e86\u6211\u4eec\u7684\u8349\u539f\u4e4b\u5f8c\uff0c\u53c8\u6709\u4eba\u53eb\u5979\u767d\u53d1\u9b54 \u5973\u3002\u201d\u98de\u7ea2\u5dfe\u63d2\u5634\u9053\uff1a\u201c\u6211\u7684\u5e08\u7236\u5934\u53d1\u867d\u7136\u5168\u81ea\uff0c\u9762\u8c8c\u5374\u597d\u770b\u5f97\u5f88\u3002\u6211\u8981\u662f\u957f\u5f97\u50cf\u5979\u90a3\u6837\u7f8e \u4e3d\uff0c\u90a3\u5c31\u597d\u4e86\u3002\u201d\u5353\u4e00\u822a\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6697\u53eb\u5947\u602a\u3002\u53ea\u542c\u5f97\u5510\u52aa\u7eed\u9053\uff1a\u201c\u6240\u4ee5\u5979\u662f\u6211\u7b2c\u4e00\u4e2a\u6551\u547d \u6069\u4eba\u3002\u5f53\u65f6\u6211\u66fe\u5bf9\u5979\u8bf4\uff1a\u5982\u679c\u4f60\u6709\u4e00\u65e5\u5230\u5929\u5c71\u5357\u5317\uff0c\u53ef\u4e00\u5b9a\u8981\u6765\u770b\u6211\u3002\u6211\u5f53\u65f6\u4e5f\u53ea\u662f\u8bf4\u8bf4\u800c \u5df2\uff0c\u6599\u4e0d\u5230\u5979\u524d\u51e0\u5e74\u771f\u7684\u6765\u4e86\u3002\u5979\u8fd8\u6ca1\u6709\u5fd8\u8bb0\u6211\uff0c\u6709\u4e00\u5929\u679c\u7136\u6765\u770b\u6211\u4e86\uff0c\u5979\u89c1\u4e86\u54c8\u739b\u96c5\uff0c\u975e \u5e38\u6b22\u559c\u3002\u4e5f\u8bb8\u5979\u4eec\u771f\u6709\u70b9\u7f18\u4efd\uff0c\u5979\u672c\u6765\u662f\u4f4f\u4e00\u4e24\u5929\u5c31\u8981\u8d70\u7684\uff0c\u89c1\u4e86\u54c8\u739b\u96c5\u5f8c\uff0c\u5374\u4f4f\u4e86\u4e0b \u6765\u3002\u201d\u5353\u4e00\u822a\u6025\u9053\uff1a\u201c\u90a3\u9ebd\uff0c\u5979\u73b0\u5728\u5728\u4f60\u90a3\u91cc\u9ebd\uff1f\u201d\u5510\u52aa\u9053\uff1a\u201c\u4f60\u522b\u5fd9\u5440\uff0c\u5f85\u6211\u544a\u8bc9\u4f60\u3002\u5979 \u8bf4\u5979\u6709\u4e2a\u670b\u53cb\u4f4f\u5728\u5929\u5c71\u5317\u9ad8\u5cf0\u7684\uff0c\u6536\u6709\u4e00\u4e2a\u975e\u5e38\u597d\u7684\u5f92\u5f1f\uff0c\u6240\u4ee5\u5979\u4e5f\u8981\u6536\u4e00\u4e2a\u597d\u5f92\u5f1f\u66ff\u5979\u4e89 \u6c14\u3002\u201d\u5353\u4e00\u822a\u5fc3\u4e2d\u6697\u7b11\uff0c\u60f3\u9053\uff1a\u7ec3\u59d0\u59d0\u8fd8\u662f\u8fd9\u6837\u597d\u80dc\uff0c\u5979\u603b\u4e0d\u80af\u8ba9\u5cb3\u545c\u73c2\u5360\u5979\u4e0a\u98ce\u3002\u8fde\u6536\u5f92 \u5f1f\u4e5f\u8981\u7ade\u8d5b\u3002\u53c8\u60f3\u9053\uff1a\u957f\u6c5f\u5f8c\u6d6a\u63a8\u524d\u6d6a\uff0c\u8fd9\u4e9b\u5c0f\u8f88\u4e00\u4e2a\u4e2a\u90fd\u662f\u826f\u6750\u7f8e\u8d28\uff0c\u4ee4\u4eba\u559c\u7231\u3002\u5cb3\u9e23\u73c2 \u6709\u6768\u4e91\u9aa2\uff0c\u7ec3\u59d0\u59d0\u6709\u98de\u7ea2\u5dfe\uff0c\u6211\u7684\u8f9b\u9f99\u5b50\u4e5f\u4e0d\u4f1a\u8f93\u7ed9\u4ed6\u4eec\u3002 \u3000\u3000\u5510\u52aa\u7eed\u9053\uff1a\u201c\u56e0\u6b64\u5979\u8981\u54c8\u739b\u96c5\u505a\u5979\u5f92\u5f1f\uff0c\u8fd9\u5728\u6211\u81ea\u7136\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u54c8\u739b\u96c5\u5df2\u8ddf\u5979\u5b66\u4e86\u4e24 \u5e74\u591a\u4e86\uff0c\u5353\u5148\u751f\uff0c\u4f60\u770b\u5979\u8fd8\u53ef\u9020\u5c31\u5417\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5c0f\u516c\u4e3b\u7684\u529f\u592b\u4fca\u6781\u4e86\uff01\u201d\u5fc3\u6025\u5982\u711a\uff0c \u53c8\u95ee\u9053\uff1a\u201c\u90a3\u9ebd\u5979\u73b0\u5728\u8fd8\u4f4f\u5728\u4f60\u90a3\u91cc\u5417\uff1f\u201d\u5510\u52aa\u9053\uff1a\u201c\u5979\u6027\u5b50\u5f88\u602a\uff0c\u6bcf\u6b21\u6765\u6307\u70b9\u4e86\u54c8\u739b\u96c5\u5341 \u5929\u534a\u6708\uff0c\u4fbf\u53c8\u8d70\u4e86\u3002\u4e0d\u8fc7\u6bcf\u5e74\u8981\u6765\u53c1\u4e94\u6b21\u3002\u201d\u98de\u7ea2\u5dfe\u7b11\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u6765\u8fdf\u4e86\u51e0\u5929\uff0c\u8981\u4e0d\u7136\u4f60 \u53ef\u4ee5\u89c1\u7740\u5979\u3002\u5979\u7684\u5251\u6cd5\u6bd4\u4f60\u8fd8\u597d\u5462\u3002\u6211\u89c1\u8fc7\u5979\u4e00\u8df3\u5c31\u80fd\u8df3\u5230\u6811\u4e0a\u53bb\u523a\u4e2d\u90a3\u4f4e\u98de\u7684\u9e1f\u513f\uff01\u201d\u5353 \u4e00\u822a\u90a3\u6709\u5fc3\u60c5\u548c\u98de\u7ea2\u5dfe\u95f2\u8bdd\uff0c\u6025\u9053\uff1a\u201c\u55ef\uff0c\u90a3\u771f\u4e0d\u5de7\uff0c\u5979\u53c8\u8d70\u4e86\uff01\u4f60\u53ef\u77e5\u9053\u5979\u8981\u53bb\u90a3\u513f \u5417\uff1f\u201d\u5510\u52aa\u9053\uff1a\u201c\u8fd9\u53ef\u4e0d\u77e5\u9053\u5440\u3002\u5979\u53bb\u90a3\u513f\uff0c\u4ece\u6765\u4e0d\u4f1a\u8bf4\u7684\u3002\u4e0d\u8fc7\u5979\u8fd9\u6b21\u5374\u4ea4\u4ee3\u4e0b\u4e00\u4e9b \u8bdd\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4ec0\u9ebd\u8bdd\u5462\uff1f\u201d\u5510\u52aa\u9053\uff1a\u201c\u5979\u4e34\u8d70\u4e4b\u524d\u8bf4\uff0c\u6709\u4e00\u4e2a\u670b\u53cb\u8981\u6765\u770b\u5979\u3002\u4f46\u5979\u8fd8 \u4e0d\u613f\u89c1\u90a3\u4e2a\u670b\u53cb\u3002\u5979\u4ea4\u6258\u6211\uff0c\u82e5\u6709\u4eba\u5f88\u7740\u6025\u7684\u67e5\u95ee\u5979\uff0c\u5c31\u5bf9\u90a3\u4eba\u8bf4\uff0c\u53eb\u4ed6\u4e0d\u8981\u6025\uff0c\u8fc7\u4e00\u4e9b\u65f6 \u5019\uff0c\u5979\u5c31\u4f1a\u53bb\u770b\u4ed6\u4e86\u3002\u201d\u5353\u4e00\u822a\u5927\u559c\u9053\uff1a\u201c\u771f\u7684\uff1f\u201d\u98de\u7ea2\u5dfe\u5658\u7740\u5634\u513f\u9053\uff1a\u201c\u6211\u7239\u7239\u4ece\u6765\u4e0d\u8bf4 \u5047\u8bdd\uff01\u201d\u5510\u52aa\u7b11\u9053\uff1a\u201c\u5353\u5148\u751f\uff0c\u770b\u6765\u4f60\u5c31\u662f\u8981\u627e\u5979\u7684\u90a3\u4f4d\u670b\u53cb\u4e86\uff0c\u662f\u5417\uff1f\u201d\u5353\u4e00\u822a\u70b9\u70b9\u5934 \u9053\uff1a\u201c\u662f\u7684\uff01\u201d\u62ac\u5934\u4ef0\u671b\u5929\u7a7a\uff0c\u4e07\u91cc\u65e0\u4e91\uff0c\u957f\u7a7a\u6f84\u78a7\u3002\u5353\u4e00\u822a\u7684\u5fc3\u60c5\u8fd9\u65f6\u4e5f\u50cf\u626b\u51c0\u4e86\u9634\u973e\u7684 \u5929\u7a7a\u4e00\u6837\uff0c\u611f\u5230\u4e86\u591a\u5e74\u6765\u6240\u672a\u6709\u8fc7\u7684\u559c\u60a6\u3002\u98de\u7ea2\u5dfe\u5ffd\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4f60\u4e5f\u6b22\u559c\u770b\u661f\u661f\u5417\uff1f\u201d\u5353 \u4e00\u822a\u9053\uff1a\u201c\u662f\u7684\u3002\u6211\u6b22\u559c\u661f\u661f\u7684\u5149\uff0c\u5979\u4eec\u79bb\u6211\u4eec\u5f88\u8fdc\uff0c\u53c8\u597d\u50cf\u5f88\u8fd1\u3002\u201d\u8bf4\u4e86\u4e4b\u5f8c\uff0c\u54d1\u7136\u5931 \u7b11\uff0c\u5fc3\u9053\uff1a\u8fd9\u4e9b\u8bdd\u5b69\u5b50\u90a3\u80fd\u660e\u767d\u5462\u4e86\u98de\u7ea2\u5dfe\u5ffd\u7136\u6307\u7740\u5929\u8fb9\u4e00\u7c92\u660e\u4eae\u7684\u661f\u9053\uff1a\u201c\u6211\u7684\u5e08\u7236\u4e5f\u559c \u6b22\u770b\u661f\u661f\uff0c\u5e08\u7236\u8bf4\uff0c\u5979\u662f\u5929\u8fb9\u90a3\u7c92\u5317\u6781\u661f\uff0c\u8981\u4e00\u70b9\u4e4c\u4e91\u90fd\u6ca1\u6709\uff0c\u5317\u6781\u661f\u624d\u4f1a\u653e\u5149\u3002\u201d\u5353\u4e00\u822a \u604d\u7136\u5982\u6709\u6240\u609f\uff0c\u518d\u62ac\u5934\u770b\u661f\uff0c\u4f46\u591c\u665a\u5df2\u7ecf\u6e10\u6e10\u6d88\u901d\uff0c\u661f\u5149\u4e5f\u5fae\u5f31\u4e86\u3002 \u3000\u3000\u8349\u539f\u4f1a\u76df\u4e4b\u5f8c\u51e0\u5929\uff0c\u5353\u4e00\u822a\u544a\u522b\u4e86\u5510\u52aa\uff0c\u5fc3\u4e2d\u5145\u6ee1\u559c\u60a6\uff0c\u4ed6\u77e5\u9053\u7389\u7f57\u5239\u4e0d\u65f6\u4f1a\u5728\u4ed6\u7684\u8eab \u8fb9\uff0c\u50cf\u661f\u661f\u4e00\u6837\u5077\u671b\u7740\u4ed6\u3002\u8bf4\u4e0d\u5b9a\u4eca\u665a\u6216\u8bb8\u660e\u671d\u5979\u5c31\u4f1a\u7a81\u7136\u51fa\u73b0\u5728\u4ed6\u7684\u9762\u524d\uff0c\u4e0d\u5fc5\u4ed6\u591a\u8bf4\u4e00 \u53e5\u8bdd\u3002 \u3000\u3000\u65bc\u662f\u4ed6\u53c8\u5728\u5927\u8349\u539f\u4e0a\u6f2b\u6e38\uff0c\u671f\u5f85\u7740\u6e34\u671b\u7684\u201c\u5947\u8ff9\u201d\uff0c\u53ef\u662f\uff0c\u5341\u5929\u8fc7\u53bb\u4e86\uff0c\u534a\u6708\u8fc7\u53bb\u4e86\uff0c \u4e00\u6708\u8fc7\u53bb\u4e86\uff0c\u4e24\u6708\u8fc7\u53bb\u4e86\uff0c\u592a\u9633\u843d\u4e0b\uff0c\u661f\u661f\u5347\u8d77\uff0c\u9ed1\u591c\u8fc7\u53bb\uff0c\u767d\u5929\u5230\u6765\uff0c\u65f6\u5149\u6d41\u8f6c\uff0c\u4f0a\u4eba\u65e0 \u8e2a\uff0c\u5927\u8349\u539f\u65e0\u8fb9\u65e0\u9645\uff0c\u7389\u7f57\u5239\u7684\u5f71\u5b50\u59cb\u7ec8\u6ca1\u6709\u51fa\u73b0\u3002\u5353\u4e00\u822a\u53c8\u6e10\u6e10\u5931\u671b\u4e86\u3002 \u3000\u3000\u4ed6\u60f3\u8d77\u4e86\u8f9b\u9f99\u5b50\uff0c\u60f3\u8d77\u4e86\u9a7c\u5cf0\u4e0a\u90a3\u4e24\u6735\u4ed9\u82b1\u3002\u65bc\u662f\u4ed6\u53c8\u6a2a\u8fc7\u8349\u539f\uff0c\u60f3\u56de\u5230\u6728\u4ec0\u5854\u514b\u7684\u9a7c \u5cf0\u4e0a\u53bb\uff0c\u5b88\u5019\u82b1\u5f00\uff0c\u7b49\u5019\u4eba\u6765\u3002 \u3000\u3000\u5728\u6a2a\u8fc7\u5927\u8349\u539f\u4e4b\u65f6\uff0c\u4ed6\u5ffd\u7136\u53d1\u73b0\u201c\u5947\u8ff9\u201d\u4e86\uff0c\u53ef\u662f\u8fd9\u5e76\u4e0d\u662f\u4ed6\u6240\u6e34\u671b\u7684\u201c\u5947\u8ff9\u201d\uff0c\u800c\u662f \u5728\u8349\u539f\u4e0a\u53d1\u73b0\u4e00\u4e9b\u6c5f\u6e56\u4eba\u7269\u7684\u6807\u8bb0\uff0c\u6709\u65f6\u662f\u5728\u5ca9\u77f3\u4e0a\u753b\u7740\u5947\u602a\u7684\u82b1\u7eb9\uff0c\u6709\u65f6\u662f\u5728\u8349\u5730\u4e0a\u753b\u7740 \u7bad\u5934\uff0c\u597d\u50cf\u662f\u6307\u8def\u6807\u4f3c\u7684\u3002\u5353\u4e00\u822a\u827a\u9ad8\u80c6\u5927\uff0c\u4e5f\u4e0d\u53bb\u7406\u4f1a\u5b83\u3002 \u3000\u3000\u4e00\u65e5\u4ed6\u884c\u8fc7\u8349\u539f\u4e4b\u95f4\u7684\u6c99\u6f20\u533a\uff0c\u70c8\u65e5\u5f53\u7a7a\uff0c\u95f7\u70ed\u4e4b\u6781\uff0c\u5ffd\u7136\u522e\u8d77\u5927\u98ce\uff0c\u6c99\u6f20\u4e0a\u9ec4\u6c99\u56db \u8d77\u3002\u5353\u4e00\u822a\u77e5\u9053\u5728\u6c99\u6f20\u522e\u98ce\u65f6\u5019\u6700\u4e3a\u5371\u9669\uff0c\u4e00\u4e0d\u5c0f\u5fc3\uff0c\u5c31\u4f1a\u88ab\u79fb\u52a8\u7684\u6c99\u4e18\u6d3b\u57cb\u751f\u846c\u3002\u5e78\u559c\u4ed6 \u5728\u8fd9\u51e0\u5e74\u5bf9\u65bc\u6c99\u6f20\u7684\u98ce\u6c99\uff0c\u5df2\u9887\u6709\u7ecf\u9a8c\uff0c\u4fbf\u627e\u4e00\u4e2a\u80cc\u98ce\u7684\u5730\u65b9\u8eb2\u85cf\uff0c\u5927\u98ce\u626c\u6c99\u4e4b\u4e2d\uff0c\u5ffd\u89c1\u51e0 \u9a91\u5065\u9a6c\u5982\u98de\u800c\u8fc7\u3002 \u3000\u3000\u90a3\u4e00\u573a\u5927\u98ce\u53ea\u662f\u9aa4\u7136\u63a0\u8fc7\u7684\u6c99\u6f20\u70ed\u98ce\uff0c\u6765\u5f97\u5feb\u53bb\u5f97\u4e5f\u5feb\uff0c\u7ea6\u4e00\u987f\u996d\u7684\u65f6\u95f4\uff0c\u98ce\u66b4\u4fbf\u8fc7\u53bb \u4e86\u3002\u5353\u4e00\u822a\u8d76\u5feb\u51fa\u6765\uff0c\u5e0c\u671b\u65e9\u65e9\u7a7f\u8fc7\u6c99\u6f20\u5730\u5e26\uff0c\u597d\u5230\u8349\u539f\u4e0a\u53bb\u627e\u98df\u6c34\u3002 \u3000\u3000\u6c99\u6f20\u90a3\u5934\u5ffd\u7136\u4f20\u6765\u4e86\u8ffd\u9010\u6740\u4e4b\u58f0\uff0c\u5353\u4e00\u822a\u5fc3\u9053\uff1a\u201c\u96be\u9053\u662f\u90a3\u4e9b\u6c5f\u6e56\u4eba\u7269\uff0c\u8ffd\u8e2a\u4ec7\u4eba\uff0c\u8ffd \u5230\u6c99\u6f20\u4e0a\u6765\u6740\u3002\u201d\u5fcd\u4e0d\u4f4f\u5411\u58f0\u97f3\u5bfb\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u5c11\u5e74\u5973\u5b50\uff0c\u8dd1\u5728\u524d\u5934\uff0c\u80cc\u5f8c\u8ffd\u7740\u4e24\u540d\u5927\u6c49\uff0c \u90a3\u5973\u5b50\u8dd1\u5f97\u751a\u5feb\uff0c\u4f46\u8fd8\u662f\u7ed9\u4eba\u8ffd\u4e0a\uff0c\u53c1\u6761\u4eba\u5f71\uff0c\u5c31\u5728\u6c99\u6f20\u4e0a\u6740\u8d77\u6765\u3002 \u3000\u3000\u5353\u4e00\u822a\u5fc3\u9053\uff1a\u8fd9\u56de\u8be5\u95ee\u660e\u767d\u624d\u597d\u52a8\u624b\u4e86\uff0c\u83ab\u4e0d\u8981\u50cf\u4e0a\u6b21\u90a3\u6837\uff0c\u4ee5\u4e3a\u662f\u6551\u88ab\u9a6c\u8d3c\u6240\u52ab\u7684\u5ba2 \u5546\u5374\u6551\u9519\u4e86\u574f\u4eba\u3002 \u3000\u3000\u5353\u4e00\u822a\u8d70\u4e0a\u524d\u9762\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u4e0d\u89c9\u5403\u4e86\u4e00\u60ca\uff0c\u8ffd\u8e2a\u5c11\u5973\u7684\u90a3\u6761\u5927\u6c49\u7adf\u7136\u662f\u795e\u5927\u5143\u548c\u795e\u4e00 \u5143\u4e24\u5144\u5f1f\u3002\u5353\u4e00\u822a\u597d\u751f\u5947\u602a\uff1a\u96be\u9053\u5f20\u732e\u5fe0\u5df2\u7ed9\u5b98\u519b\u6253\u5f97\u571f\u5d29\u74e6\u89e3\u4e86\u9ebd\uff1f\u8981\u4e0d\u7136\u8fd9\u4e24\u4e2a\u6d3b\u5b9d\u8d1d \u600e\u9ebd\u4f1a\u8ffd\u5230\u6c99\u6f20\u4e0a\u6765\uff1f\u7ed9\u4ed6\u4eec\u8ffd\u8e2a\u7684\u5c11\u5973\u5374\u53c8\u662f\u8c01\u5462\uff1f \u3000\u3000\u5353\u4e00\u822a\u521a\u60f3\u62d4\u5251\uff0c\u5ffd\u542c\u5f97\u90a3\u5c11\u5973\u5927\u58f0\u53eb\u9053\uff1a\u201c\u5353\u5927\u54e5\uff01\u201d\u5353\u4e00\u822a\u4e0d\u89c9\u4e00\u6014\uff0c\u53ea\u542c\u5f97\u90a3\u5c11 \u5973\u53c8\u9053\uff1a\u201c\u6211\u662f\u843c\u534e\u5440\uff01\u5927\u54e5\uff0c\u4f60\u5feb\u6765\u5e2e\u6211\uff01\u201d\u8fd9\u5239\u90a3\u95f4\u5353\u4e00\u822a\u4e0d\u89c9\u53c8\u60ca\u53c8\u559c\uff0c\u8fd9\u5c11\u5973\u539f\u6765 \u7adf\u662f\u767d\u77f3\u5e08\u53d4\u7684\u5c0f\u5973\u513f\uff0c\u8bb0\u5f97\u5728\u5d69\u5c71\u521d\u89c1\u4e4b\u65f6\uff0c\u5979\u4e0d\u8fc7\u662f\u4e03\u516b\u5c81\uff0c\u5982\u4eca\u5374\u957f\u5f97\u8fd9\u9ebd\u9ad8\u4e86\uff01\u4e0d \u77e5\u767d\u77f3\u5e08\u53d4\u53ef\u6709\u6ca1\u6709\u6765\u5462\uff1f \u3000\u3000\u60ca\u559c\u5fe7\u8651\uff0c\u970e\u65f6\u95f4\u90fd\u4e0a\u5fc3\u5934\uff0c\u53ef\u662f\u5374\u5bb9\u4e0d\u5f97\u5353\u4e00\u822a\u7ec6\u60f3\u4e86\u3002\u795e\u5bb6\u5144\u5f1f\u7684\u6b66\u529f\u975e\u540c\u5c0f\u9119\uff0c \u4f55\u843c\u534e\u7ed9\u4ed6\u4eec\u4e8c\u4eba\u5939\u51fb\uff0c\u6b63\u662f\u9669\u8c61\u73af\u751f\u3002 \u3000\u3000\u5353\u4e00\u822a\u5927\u559d\u4e00\u58f0\uff0c\u62d4\u5251\u4fbf\u4e0a\u3002\u795e\u5927\u5143\u602a\u7b11\u9053\uff1a\u201c\u771f\u662f\u4eba\u751f\u65e0\u5904\u4e0d\u76f8\u9022\uff0c\u6599\u4e0d\u5230\u5728\u8fd9\u513f\u53c8 \u78b0\u5230\u4f60\u4e86\uff01\u201d\u5353\u4e00\u822a\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4f55\u6b3a\u8d1f\u6211\u7684\u5c0f\u5e08\u59b9\uff1f\u201d\u795e\u4e00\u5143\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fde\u4f60\u7684\u5e08\u53d4 \u6211\u4eec\u4e5f\u8981\u6b3a\u8d1f\uff0c\u600e\u9ebd\u6837\uff1f\u201d\u5353\u4e00\u822a\u5927\u6012\uff0c\u5c55\u5251\u4fbf\u523a\u3002\u548c\u795e\u5bb6\u5144\u5f1f\u5728\u6c99\u6f20\u4e0a\u6076\u6597\u8d77\u6765\uff01 \u3000\u3000\u795e\u5bb6\u5144\u5f1f\u6599\u4e0d\u5230\u6570\u5e74\u4e0d\u89c1\uff0c\u5353\u4e00\u822a\u7684\u6b66\u529f\u5df2\u5927\u4e3a\u7cbe\u8fdb\uff0c\u4e00\u53e3\u5251\u65cb\u98ce\u6025\u821e\uff0c\u6709\u5982\u620f\u6c34\u795e \u9f99\uff0c\u76d8\u7a7a\u602a\u9e1f\uff0c\u800c\u4e14\u5f0f\u5f0f\u76f8\u8fde\uff0c\u62db\u62db\u7d27\u8feb\uff0c\u7ef5\u5bc6\u51cc\u5389\uff0c\u517c\u800c\u6709\u4e4b\u3002\u4ee5\u795e\u5bb6\u5144\u5f1f\u90a3\u6837\u9ad8\u7684\u6b66 \u529f\uff0c\u7adf\u7136\u5948\u4f55\u4ed6\u4e0d\u5f97\u3002 \u3000\u3000\u672c\u6765\u795e\u5bb6\u5144\u5f1f\u82e5\u7136\u4ee5\u4e8c\u654c\u4e00\uff0c\u867d\u7136\u4e0d\u80fd\u53d6\u80dc\uff0c\u4e5f\u53ef\u7a0d\u5360\u4e0a\u98ce\u3002\u4f46\u5374\u8fd8\u6709\u4e00\u4e2a\u4f55\u843c\u534e\u3002\u4f55 \u843c\u534e\u7684\u5251\u6cd5\u867d\u7136\u8fdc\u6bd4\u4e0d\u4e0a\u5353\u4e00\u822a\uff0c\u4f46\u4e5f\u662f\u6b66\u5f53\u6d3e\u7684\u771f\u4f20\uff0c\u5979\u53c8\u6253\u5f97\u975e\u5e38\u806a\u660e\uff0c\u6bcf\u6bcf\u8d81\u7740\u5353\u4e00 \u822a\u5c06\u654c\u4eba\u8feb\u7d27\u4e4b\u65f6\uff0c\u5c31\u51b7\u4e0d\u9632\u4ece\u65c1\u4e00\u5251\uff0c\u6270\u4e71\u654c\u4eba\u7684\u5fc3\u795e\uff0c\u4e8c\u795e\u6beb\u65e0\u529e\u6cd5\u3002 \u3000\u3000\u6253\u4e86\u4e00\u9635\uff0c\u795e\u4e00\u5143\u4e2d\u4e86\u4e00\u5251\uff0c\u8fde\u8fde\u5f8c\u9000\uff0c\u795e\u5927\u5143\u65e0\u5fc3\u604b\u6218\uff0c\u62a4\u7740\u5f1f\u5f1f\uff0c\u62d4\u817f\u4fbf\u9003\u3002\u5353\u4e00 \u822a\u4e5f\u4e0d\u8ffd\u8d76\uff0c\u6025\u6025\u95ee\u4f55\u843c\u534e\u9053\uff1a\u201c\u4f60\u600e\u9ebd\u4f1a\u548c\u4ed6\u4eec\u6253\u8d77\u6765\u7684\uff1f\u767d\u77f3\u5e08\u53d4\u6765\u4e86\u6ca1\u6709\uff1f\u201d\u4f55\u843c\u534e \u4e3e\u8896\u62b9\u4e7e\u51c0\u8138\u4e0a\u7684\u98ce\u6c99\uff0c\u7b11\u9053\uff1a\u201c\u7239\u82e5\u4e0d\u6765\uff0c\u6211\u4e00\u4e2a\u4eba\u600e\u6562\u8fdc\u5230\u585e\u5916\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u5fc3\u5934\u9e7f\u649e\uff0c\u535c\u535c\u4e71\u8df3\u3002\u53ea\u542c\u5f97\u4f55\u843c\u534e\u7ee7\u7eed\u9053\uff1a\u201c\u4e8c\u5e08\u4f2f\u8fd8\u60f3\u627e\u4f60\u56de\u53bb\u505a\u638c\u95e8\uff0c\u53eb \u6211\u7239\u6765\u5bfb\u4f60\u3002\u59d0\u59d0\u5df2\u51fa\u5ac1\u4e86\uff0c\u59d0\u592b\u524d\u51e0\u5e74\u8fd8\u5728\u6b66\u5f53\u5c71\uff0c\u73b0\u5728\u5df2\u5f52\u5b97\u5ce8\u7709\uff0c\u59d0\u59d0\u4e5f\u8ddf\u4ed6\u53bb\u4e86\u3002 \u7239\u8eab\u8fb9\u53ea\u6709\u6211\u4e00\u4eba\uff0c\u5bc2\u5bde\u5f97\u5f88\u3002\u6211\u5728\u6b66\u5f53\u5c71\u4f4f\u5f97\u538c\u4e86\uff0c\u7f20\u7740\u4ed6\u8981\u8ddf\u4ed6\u5230\u585e\u5916\u6765\u5f00\u5f00\u773c\u754c\uff0c\u4ed6 \u7ed9\u6211\u7f20\u5f97\u6ca1\u6cd5\uff0c\u597d\u7b54\u5e94\u3002\u201d\u4f55\u843c\u534e\u806a\u660e\u6d3b\u6cfc\uff0c\u4e00\u526f\u987d\u76ae\u795e\u6c14\uff0c\u548c\u5979\u59d0\u59d0\u7684\u6587\u9759\uff0c\u9887\u662f\u4e0d\u540c\u201d \u3000\u3000\u5353\u4e00\u822a\u505a\u58f0\u4e0d\u5f97\uff0c\u5fc3\u4e2d\u6b63\u81ea\u76d8\u7b97\u89c1\u4e86\u5e08\u53d4\u4e4b\u5f8c\u5982\u4f55\u63aa\u8f9e\u3002\u4f55\u843c\u534e\u53c8\u9053\uff1a\u201c\u6211\u4eec\u5230\u4e86\u6c99 \u6f20\uff0c\u6c34\u56ca\u91cc\u7684\u6c34\u5df2\u6240\u5269\u65e0\u591a\u3002\u90a3\u8fb9\u6709\u4e2a\u5c0f\u5c71\uff0c\u6211\u4eec\u9690\u7ea6\u770b\u89c1\u4e00\u4e2a\u5ca9\u6d1e\uff0c\u6211\u7239\u8bf4\u5ca9\u6d1e\u91cc\u4e5f\u8bb8\u6709 \u6c34\uff0c\u4fbf\u53bb\u627e\u6c34\u3002\u4ed6\u89c1\u6211\u75b2\u5026\uff0c\u53eb\u6211\u5728\u8fd9\u91cc\u7b49\u4ed6\u3002\u4e0d\u6599\u4ed6\u53bb\u4e86\u4e0d\u4e45\uff0c\u4fbf\u522e\u98ce\u4e86\u3002\u6211\u8eb2\u5230\u5c0f\u6c9f\u91cc \u907f\u98ce\uff0c\u98ce\u6b62\u4e4b\u5f8c\uff0c\u4fbf\u89c1\u7740\u4e86\u8fd9\u4e24\u4e2a\u4eba\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u4eec\u600e\u6837\u4f1a\u77e5\u9053\u6211\u7239\u7684\u540d\u5b57\uff0c\u4e24\u4e2a\u4eba\u8dd1\u6765\u8ffd \u6211\uff0c\u8981\u4e0d\u662f\u78b0\u89c1\u4f60\uff0c\u53ef\u7cdf\u900f\u5566\uff0c\u8fd9\u4e24\u4e2a\u4eba\u5c31\u50cf\u6b66\u5f53\u5c71\u5e99\u91cc\u7684\u90a3\u4e24\u4e2a\u65e0\u5e38\u4e00\u6837\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u4e3e\u76ee\u8fdc\u773a\uff0c\u53ea\u89c1\u90a3\u5934\u679c\u7136\u6709\u4e2a\u5c0f\u5c0f\u7684\u4e18\u9675\uff0c\u8fd9\u6c99\u6f20\u662f\u4e24\u5757\u5927\u8349\u539f\u4e4b\u95f4\u7684\u6c99\u6f20\uff0c\u6240 \u4ee5\u4e0d\u50cf\u5176\u4ed6\u5927\u6c99\u6f20\u4e00\u6837\u5168\u662f\u4e00\u671b\u65e0\u9645\u7684\u9ec4\u6c99\uff0c\u5353\u4e00\u822a\u770b\u4e86\u4e00\u9635\uff0c\u5ffd\u9053\uff1a\u201c\u5e08\u53d4\u7d20\u6765\u7cbe\u660e\uff0c\u90a3 \u5c0f\u5c71\u79bb\u8fd9\u91cc\u4e0d\u7b97\u5f88\u8fdc\uff0c\u4e3a\u4f55\u4ed6\u542c\u4e0d\u5230\u4f60\u7684\u558a\u58f0\uff1f\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u5c31\u662f\u5440\uff0c\u6211\u4e5f\u4e0d\u660e\u767d\u3002\u201d\u5353 \u4e00\u822a\u6025\u6025\u548c\u4f55\u843c\u534e\u8d76\u53bb\uff0c\u5230\u4e86\u90a3\u5ea7\u5c0f\u5c71\uff0c\u627e\u904d\u4e86\u4e5f\u4e0d\u89c1\u767d\u77f3\u9053\u4eba\u7684\u5f71\u5b50\uff0c\u90a3\u5c0f\u5ca9\u6d1e\u4e00\u773c\u89c1 \u5e95\uff0c\u6700\u591a\u53ea\u5bb9\u5f97\u4e00\u4eba\uff0c\u91cc\u9762\u5806\u6ee1\u5927\u98ce\u522e\u6765\u7684\u6c99\u77f3\u3002\u5353\u4e00\u822a\u6697\u53eb\u5947\u602a\u3002\u6b63\u5728\u5bfb\u89c5\u547c\u5524\uff0c\u5ffd\u7136\u542c \u5f97\u4f55\u843c\u534e\u4e00\u58f0\u9a87\u53eb\u3002\u6b63\u662f\uff1a\u59cb\u77e5\u6c99\u6f20\u98ce\u4e91\u9669\uff0c\u53d8\u5316\u79bb\u5947\u4e0d\u6613\u731c\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982\u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206 \u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377\u2014\u2014\u7b2c\u4e09\u5341\u4e00\u56de\u3000\u5e7d\u6068\u5bc4\u9065\u5929\u3000\u76f8\u601d\u79cd\u79cd\u3000\u7434\u58f0\u98de\u5927\u6f20\u3000\u8bef\u4f1a\u91cd\u91cd \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377 \u7b2c\u4e09\u5341\u4e00\u56de \u5e7d\u6068\u5bc4\u9065\u5929\u3000\u76f8\u601d\u79cd\u79cd \u7434\u58f0\u98de\u5927\u6f20\u3000\u8bef\u4f1a\u91cd\u91cd \u3000\u3000\u4e14\u8bf4\u5353\u4e00\u822a\u56db\u5904\u5bfb\u89c5\uff0c\u90fd\u4e0d\u89c1\u767d\u77f3\u9053\u4eba\u7684\u5f71\u5b50\uff0c\u5ffd\u95fb\u4f55\u843c\u534e\u9a87\u53eb\u4e00\u58f0\uff0c\u5353\u4e00\u822a\u5fd9\u51d1\u8fc7\u53bb \u770b\uff0c\u4f55\u843c\u534e\u62e8\u5f00\u5c0f\u5ca9\u6d1e\u5916\u9762\u7684\u7a00\u758f\u91ce\u8349\uff0c\u628a\u624b\u4e00\u6307\uff0c\u53ea\u89c1\u6c99\u77f3\u4e0a\u6709\u51e0\u70b9\u6de1\u6de1\u7684\u8840\u6e0d\uff0c\u4f55\u843c\u534e \u82b1\u5bb9\u53d8\u8272\uff0c\u98a4\u58f0\u8bf4\u9053\uff1a\u201c\u83ab\u975e\u6211\u7684\u7239\u7239\u5df2\u9047\u5bb3\u4e86\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u518d\u4ed4\u7ec6\u5ba1\u89c4\uff0c\u9664\u4e86\u8fd9\u51e0\u70b9\u8840\u6e0d\u4e4b\u5916\uff0c\u522b\u65e0\u5f02\u72b6\uff0c\u5c55\u989c\u7b11\u9053\uff1a\u201c\u534e \u59b9\uff0c\u4f60\u4e0d\u5fc5\u62c5\u5fc3\uff0c\u767d\u77f3\u5e08\u53d4\u82e5\u7136\u9047\u5bb3\uff0c\u5c82\u6b62\u8fd9\u51e0\u70b9\u8840\u6e0d\uff1f\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u90a3\u9ebd\u4ed6\u53bb\u4e86\u90a3 \u91cc\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6c99\u6f20\u72c2\u98ce\uff0c\u5a01\u529b\u6781\u5927\uff0c\u5f80\u5f80\u4e00\u573a\u5927\u98ce\u8fc7\u5f8c\uff0c\u6c99\u4e18\u6613\u5f62\uff0c\u4eba\u755c\u8ff7\u8def\u3002\u4e5f\u8bb8 \u4ed6\u51fa\u6765\u627e\u4f60\uff0c\u8ff7\u5931\u5728\u5927\u6c99\u6f20\u4e2d\u4e86\u3002\u90a3\u51e0\u70b9\u8840\u6e0d\uff0c\u53ef\u80fd\u662f\u88ab\u6c99\u77f3\u522e\u7834\u7684\u3002\u201d\u4f55\u843c\u534e\u60f3\u60f3\u9887\u6709\u9053 \u7406\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u4e24\u4e2a\u8d3c\u4eba\u89c1\u6211\u65f6\uff0c\u66fe\u8bf4\u51fa\u6211\u7239\u7684\u540d\u5b57\uff0c\u597d\u50cf\u4ed6\u4eec\u548c\u6211\u7239\u7239\u751a\u6709\u4ec7\u6068\uff0c\u82e5\u679c\u4ed6 \u4eec\u8fd8\u6709\u515a\u7fbd\uff0c\u7239\u51fa\u6765\u627e\u6211\u65f6\uff0c\u4e0d\u662f\u8981\u548c\u4ed6\u4eec\u78b0\u4e0a\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u8d3c\u4eba\u662f\u6211\u8ba4\u8bc6\u7684\uff0c\u4ed6\u4eec\u4e0e\u6211\u6d3e\u4e95\u6c34\u4e0d\u72af\u6cb3\u6c34\uff0c\u6309\u8bf4\u4e0d\u8be5\u6709\u4ec0\u9ebd\u4ec7 \u6068\u3002\u800c\u4e14\u5e08\u53d4\u5251\u6cd5\u7cbe\u5999\uff0c\u6b66\u529f\u9ad8\u5f3a\uff0c\u4e5f\u4e0d\u6015\u4ed6\u4eec\u8fd9\u51e0\u4e2a\u5c0f\u8d3c\uff0c\u6211\u5012\u662f\u62c5\u5fc3\u4ed6\u8ff7\u4e86\u8def\u4e86\u3002\u201d \u3000\u3000\u65bc\u662f\u4e24\u4eba\u518d\u5728\u6c99\u6f20\u4e0a\u5bfb\u89c5\uff0c\u5bfb\u4e86\u534a\u5929\uff0c\u4ecd\u662f\u65e0\u5f71\u65e0\u8e2a\u3002\u7ea2\u65e5\u897f\u6c89\uff0c\u51b7\u98ce\u9661\u8d77\uff0c\u5353\u4e00\u822a \u9053\uff1a\u201c\u5e08\u53d4\u8fd9\u5b8f\u5927\u7684\u4eba\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u5931\u6389\u3002\u4e5f\u8bb8\u4ed6\u627e\u4f60\u4e0d\u89c1\uff0c\u7a7f\u8fc7\u90a3\u8fb9\u8349\u539f\u4e86\u3002\u73b0\u5728\u767d\u65e5\u5c06 \u901d\uff0c\u6c99\u6f20\u4e0a\u5bd2\u51b7\u96be\u5f53\uff0c\u800c\u4e14\u54b1\u4eec\u6ca1\u5e26\u7bf7\u5e10\uff0c\u5728\u6c99\u6f20\u4e0a\u6b47\u606f\uff0c\u4e5f\u5f88\u4e0d\u65b9\u4fbf\uff0c\u54b1\u4eec\u4e5f\u4e0d\u5982\u7a7f\u8fc7\u90a3 \u8fb9\u8349\u539f\u53bb\u5427\u3002\u201d \u3000\u3000\u8fd9\u6c99\u6f20\u662f\u4e24\u5757\u5927\u8349\u539f\u4e4b\u95f4\u7684\u5c0f\u6c99\u6f20\uff0c\u4e24\u4eba\u4e0d\u9700\u591a\u5c11\u65f6\u5019\uff0c\u4fbf\u8d70\u5230\u4e86\u90a3\u8fb9\u7684\u8349\u539f\u3002\u8fd9\u65f6\u66ae \u8272\u76f8\u5408\uff0c\u661f\u661f\u53c8\u5df2\u5728\u8349\u539f\u4e0a\u5347\u8d77\uff0c\u8349\u539f\u8fdc\u5904\uff0c\u5929\u5c71\u9ad8\u51fa\u4e91\u9704\uff0c\u7691\u7691\u51b0\u8702\uff0c\u5728\u591c\u8272\u4e2d\u50cf\u6c34\u6676\u4e00 \u6837\u95ea\u95ea\u53d1\u5149\uff0c\u51b7\u98ce\u4f4e\u5578\uff0c\u63a0\u8fc7\u8349\u539f\uff0c\u8349\u539f\u4e0a\u6709\u7f9a\u7f8a\u5954\u8d70\uff0c\u5140\u9e70\u76d8\u65cb\u4e4b\u58f0\uff0c\u4e00\u6d3e\u585e\u5916\u60c5\u8c03\u3002\u5353 \u4e00\u822a\u9065\u671b\u661f\u661f\uff0c\u60a0\u7136\u5b58\u6069\uff0c\u5ffd\u559f\u7136\u53f9\u9053\uff1a\u201c\u5341\u5e74\u4e0d\u89c1\uff0c\u4f60\u90fd\u8fd9\u9ebd\u5927\u4e86\uff0c\u5c81\u6708\u6613\u9006\uff0c\u80fd\u4e0d\u611f \u4f24\uff1f\u201d \u3000\u3000\u4f55\u843c\u534e\u62ac\u8d77\u773c\u775b\uff0c\u7b11\u9053\uff1a\u201c\u5353\u5927\u54e5\uff0c\u4e3a\u4ec0\u9ebd\u4f60\u597d\u50cf\u4e0d\u4f1a\u8001\u4f3c\u7684\uff0c\u8fd8\u50cf\u4ece\u524d\u4e00\u6837\uff0c\u53ea\u662f\u9ed1 \u4e86\u70b9\u513f\u3002\u6211\u8fd8\u8bb0\u5f97\u4f60\u521d\u4e0a\u5d69\u5c71\u4e4b\u65f6\uff0c\u7239\u53eb\u4f60\u548c\u6211\u59d0\u59d0\u76f8\u89c1\uff0c\u4f60\u7f9e\u602f\u602f\u7684\u50cf\u4e2a\u5927\u59d1\u5a18\u3002\u6211\u548c\u59d0 \u59d0\u80cc\u5f8c\u8fd8\u7b11\u4f60\u5462\u3002\u54ce\uff0c\u90a3\u65f6\u5019\u4f60\u8fd8\u62b1\u8fc7\u6211\uff0c\u9017\u6211\u73a9\u5462\uff0c\u4f60\u8bb0\u5f97\u5417\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u82e6\u7b11\u9053\uff1a\u201c\u600e\u4e0d\u8bb0\u5f97\uff1f\u201d\u90a3\u65f6\u5019\uff0c\u8981\u4e0d\u662f\u767d\u77f3\u9053\u4eba\u6a2a\u751f\u679d\u8282\uff0c\u4ed6\u548c\u7389\u7f57\u5239\u4e5f\u4e0d\u81f3 \u65bc\u95f9\u51fa\u90a3\u8bb8\u591a\u98ce\u6ce2\u3002 \u3000\u3000\u4f55\u843c\u534e\u9053\uff1a\u201c\u5353\u5927\u54e5\uff0c\u4f60\u4e0d\u60f3\u56de\u53bb\u4e86\u5417\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u585e\u5916\u8349\u539f\u4fbf\u662f\u6211\u7684\u5bb6\u4e86\uff0c\u6211\u8fd8 \u56de\u53bb\u505a\u4ec0\u9ebd\uff1f\u201d\u6c89\u601d\u534a\u664c\uff0c\u95ee\u4f55\u843c\u534e\u9053\uff1a\u201c\u6211\u4eec\u6b66\u5f53\u6d3e\u73b0\u5728\u600e\u9ebd\u6837\u4e86\u3002\u4e8c\u5e08\u4f2f\u7cbe\u795e\u8fd8\u597d \u5417\uff1f\u201d\u4f55\u843c\u534e\u53f9\u53e3\u6c14\u9053\uff1a\u201c\u4e8c\u5e08\u4f2f\u81ea\u4f60\u8d70\u5f8c\uff0c\u7ec8\u65e5\u8eb2\u5728\u4e91\u623f\uff0c\u4e0d\u8f7b\u6613\u8d70\u51fa\u6765\u3002\u4ed6\u8870\u8001\u591a\u4e86\uff0c \u53bb\u5e74\u79cb\u5929\uff0c\u8fd8\u751f\u8fc7\u4e00\u573a\u5927\u75c5\uff0c\u53e3\u53e3\u58f0\u58f0\u8981\u6211\u7239\u628a\u4f60\u627e\u56de\u6765\u3002\u5c71\u4e0a\u4e5f\u51b7\u843d\u8bb8\u591a\uff0c\u4e0d\u590d\u4f3c\u5f53\u5e74\u7684 \u70ed\u95f9\u60c5\u666f\u4e86\u3002\u201d\u5353\u4e00\u822a\u542c\u4e86\uff0c\u4e0d\u7981\u4e00\u58f0\u957f\u53f9\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u9ec4\u53f6\u9053\u4eba\u7684\u5f71\u5b50\u9aa4\u7136\u4ece\u4ed6\u5fc3\u5934\u63a0\u8fc7\uff0c\u90a3\u4e25\u5389\u7684\u800c\u53c8\u662f\u671f\u671b\u7684\u773c\u5149\u4f3c\u4e4e\u5728\u6ce8\u89c6 \u7740\u4ed6\uff0c\u5ffd\u7136\u95f4\uff0c\u4ed6\u89c9\u5f97\u5e08\u53d4\u4eec\u867d\u7136\u53ef\u538c\uff0c\u5374\u4e5f\u53ef\u601c\u3002\u4f55\u843c\u534e\u53c8\u95ee\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u771f\u7684\u4e0d\u56de\u53bb \u4e86\u5417\uff1f\u201d\u5353\u4e00\u822a\u4e3e\u5934\u671b\u661f\uff0c\u5e7d\u5e7d\u7b54\u9053\uff1a\u201c\u55ef\uff0c\u4e0d\u56de\u53bb\u4e86\uff01\u201d \u3000\u3000\u4f55\u843c\u534e\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u627e\u5230\u4e86\u5979\u5417\uff01\u201d\u5353\u4e00\u822a\u5fc3\u5934\u4e00\u9707\uff0c\u95ee\u9053\uff1a\u201c\u8c01\uff1f\u201d\u4f55\u843c\u534e\u7b11\u9053\uff1a \u201c\u5927\u54e5\u4e0e\u7389\u7f57\u5239\u4e4b\u4e8b\uff0c\u5929\u4e0b\u65e0\u4eba\u4e0d\u77e5\uff0c\u8fd8\u5f85\u95ee\u5417\uff1f\u53ef\u60dc\u6211\u6ca1\u6709\u89c1\u8fc7\u5979\uff0c\u5e08\u53d4\u4eec\u90fd\u8bf4\u5979\u662f\u672c\u95e8 \u516c\u654c\uff0c\u7239\u7239\u66f4\u662f\u6068\u5979\uff0c\u53ea\u662f\u6211\u59d0\u59d0\u5374\u6ca1\u6709\u8bf4\u8fc7\u5979\u7684\u574f\u8bdd\u3002\u201d\u5353\u4e00\u822a\u82e6\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60 \u5462\uff1f\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u6211\u8fd8\u672a\u89c1\u8fc7\u5979\uff0c\u6211\u600e\u77e5\u9053\uff1f\u672c\u95e8\u7684\u5e08\u53d4\u5e08\u5144\u867d\u7136\u90fd\u9a82\u5979\u662f\u5973\u9b54\u5934\uff0c\u4f46\u6211 \u5374\u89c9\u5f97\u5979\u4e00\u4e2a\u5973\u5b50\u800c\u80fd\u79f0\u9738\u6b66\u6797\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u662f\u4e00\u4e2a\u5dfe\u5e3c\u987b\u7709\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u53c8\u7b11\u4e86\u7b11\u3002\u4f55\u843c\u534e\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u771f\u7684\u8981\u548c\u5979\u8001\u6b7b\u585e\u5916\u5417\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u6ca1 \u6709\u627e\u7740\u5979\uff0c\u4e0d\uff0c\u5979\u5c31\u50cf\u6c99\u6f20\u4e0a\u522e\u98ce\uff0c\u500f\u7136\u800c\u6765\uff0c\u5377\u8d77\u4e00\u7247\u9ec4\u6c99\uff0c\u500f\u7136\u4e4b\u95f4\uff0c\u53c8\u8fc7\u53bb\u4e86\u3002\u201d\u4f55 \u843c\u534e\u4f38\u4e86\u4e00\u4f38\u820c\u5934\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u9ebd\uff0c\u5927\u54e5\u4f60\u53ef\u5f97\u5c0f\u5fc3\u4e86\uff0c\u88ab\u57cb\u5728\u522e\u98ce\u5377\u8d77\u7684\u98ce\u6c99\u4e4b\u4e2d\uff0c\u53ef\u4e0d \u662f\u597d\u73a9\u7684\u5440\uff01\u201d \u3000\u3000\u8349\u539f\u4e0a\u5bd2\u98ce\u53c8\u522e\u8d77\u6765\u4e86\uff0c\u591c\u8272\u8d8a\u6d53\uff0c\u5bd2\u6c14\u8d8a\u751a\u3002\u5353\u4e00\u822a\u89c1\u8fdc\u5904\u6709\u4e00\u56e2\u706b\u5149\uff0c\u9053\uff1a\u201c\u90a3\u8fb9 \u60f3\u662f\u6709\u7267\u6c11\u751f\u706b\u53d6\u6696\uff0c\u8349\u539f\u4e0a\u7684\u7267\u6c11\u6700\u4e3a\u597d\u5ba2\uff0c\u54b1\u4eec\u4e0d\u5982\u8fc7\u53bb\u5174\u4ed6\u4eec\u540c\u5ea6\u8fd9\u4e2a\u5bd2\u591c\u3002\u201d \u3000\u3000\u8d70\u8fd1\u53bb\u770b\uff0c\u56f4\u7ed5\u5728\u706b\u5806\u8fb9\u7684\u662f\u4e00\u5927\u7fa4\u54c8\u8428\u514b\u4eba\uff0c\u5e26\u6709\u5341\u591a\u5339\u9a86\u9a7c\uff0c\u9a6e\u6709\u8d27\u7269\uff0c\u4f3c\u4e4e\u4e0d\u662f \u7267\u6c11\uff0c\u800c\u662f\u7a7f\u8d8a\u6c99\u6f20\u7684\u5ba2\u5546\uff0c\u4ed6\u4eec\u4e4b\u4e2d\u6709\u4eba\u61c2\u5f97\u6c49\u8bed\uff0c\u89c1\u4e86\u5353\u4e00\u822a\u548c\u4f55\u843c\u534e\u8fc7\u6765\uff0c\u60ca\u7591\u7684\u671b \u4e86\u4e00\u773c\uff0c\u5353\u4e00\u822a\u8bf4\u5728\u522e\u98ce\u4e4b\u5f8c\u8ff7\u8def\uff0c\u7acb\u523b\u4fbf\u6709\u4eba\u8ba9\u51fa\u4f4d\u7f6e\u6765\uff0c\u8bf7\u4ed6\u4eec\u5750\u4e0b\u3002 \u3000\u3000\u6c99\u6f20\u4e0a\u7684\u884c\u5546\uff0c\u4ee5\u9a86\u9a7c\u4e3a\u5bb6\uff0c\u5e76\u65e0\u56fa\u5b9a\u4f4f\u5740\uff0c\u56e0\u6b64\u8d38\u6613\u5f80\u8fd4\uff0c\u4e00\u5bb6\u5927\u5c0f\u90fd\u8981\u540c\u884c\uff0c\u53c8\u56e0 \u6c99\u6f20\u591a\u9669\uff0c\u5f80\u5f80\u662f\u5ac2\u5bb6\u4eba\u7ed3\u4f34\u540c\u884c\uff0c\u7ec4\u6210\u4e86\u9a86\u9a7c\u9a6c\u961f\uff0c\u548c\u6e38\u7267\u90e8\u843d\u4e5f\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u54c8\u8428\u514b\u4eba\u6700\u559c\u6b4c\u821e\uff0c\u5e74\u8f7b\u7684\u5c0f\u5925\u5b50\u4fbf\u56f4\u8d77\u706b\u5806\u5531\u8d77\u6b4c\u6765\uff0c\u6709\u4e00\u4e2a\u5c11\u5973\uff0c\u6b4c\u5589\u751a\u597d\uff0c\u4e0d\u4e45 \u5408\u5531\u53d8\u6210\u72ec\u5531\uff0c\u4e00\u4e2a\u5c11\u5e74\u62c9\u8d77\u80e1\u7434\u62cd\u548c\uff0c\u5353\u4e00\u822a\u5230\u4e86\u8349\u539f\u51e0\u5e74\uff0c\u5927\u81f4\u61c2\u5f97\u4ed6\u4eec\u7684\u8bed\u8a00\uff0c\u53ea\u542c \u5f97\u90a3\u5c11\u5973\u5531\u9053\uff1a \u3000\u3000\u5927\u98ce\u5377\u8d77\u4e86\u9ec4\u6c99\uff0c \u3000\u3000\u5929\u8fb9\u7684\u9e70\u76d8\u65cb\u6b32\u4e0b\uff1a \u3000\u3000\u54e5\u5440\uff0c\u4f60\u5c31\u662f\u5929\u8fb9\u7684\u90a3\u53ea\u9e70\uff0c \u3000\u3000\u4f60\u867d\u7136\u4e0d\u6015\u98ce\u6c99\uff0c\u4f60\u4e5f\u4e0d\u8981\u4e0b\u6765\u5440\uff01 \u3000\u3000\u5927\u98ce\u5377\u8d77\u4e86\u9ec4\u6c99\uff0c \u3000\u3000\u5929\u8fb9\u7684\u9e70\u76d8\u65cb\u6b32\u4e0b\uff1a \u3000\u3000\u6211\u4e0d\u662f\u4e0d\u6015\u98ce\u6c99\uff0c \u3000\u3000\u59b9\u5440\uff0c\u6211\u662f\u4e3a\u4e86\u8981\u89c1\u4f60\u7684\u9762\uff0c \u3000\u3000\u6211\u8981\u4e58\u98ce\u6765\u627e\u4f60\u56de\u5bb6\uff1f \u3000\u3000\u7434\u58f0\u6e05\u8d8a\u7f8e\u5999\uff0c\u6b4c\u58f0\u8c6a\u8fc8\u7f20\u7ef5\uff0c\u5353\u4e00\u822a\u542c\u5f97\u5982\u75f4\u4f3c\u9189\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u53ef\u60dc\u6211\u4e0d\u662f\u9e70\uff0c\u5979 \u662f\u9e70\uff0c\u5374\u53c8\u4e0d\u80af\u4e58\u98ce\u627e\u6211\u3002\u201d \u3000\u3000\u90a3\u4e9b\u54c8\u8428\u514b\u4eba\u8f7d\u6b4c\u8f7d\u821e\uff0c\u95f9\u4e86\u4e00\u9635\uff0c\u5e74\u9752\u7684\u5c0f\u5925\u5b50\u9053\uff1a\u201c\u8bf7\u8fd9\u4e24\u4f4d\u8fdc\u65b9\u6765\u7684\u5ba2\u4eba\uff0c\u4e5f\u7ed9 \u6211\u4eec\u5531\u4e00\u652f\u6b4c\u3002\u201d\u8bf4\u7f62\u4fbf\u6709\u4eba\u628a\u80e1\u7434\u9012\u7ed9\u4f55\u843c\u534e\uff0c\u5148\u8bf7\u5353\u4e00\u822a\u5531\u3002 \u3000\u3000\u5353\u4e00\u822a\u6ee1\u6000\u6101\u7eea\uff0c\u90a3\u6709\u5fc3\u60c5\u6b4c\u821e\uff0c\u53ef\u662f\u8fd9\u4e43\u662f\u54c8\u8428\u514b\u7684\u6c11\u65cf\u793c\u8282\uff0c\u82e5\u7136\u5ba2\u4eba\u4e0d\u5531\uff0c\u4e3b\u4eba \u4f1a\u4ee5\u4e3a\u5ba2\u4eba\u5fc3\u91cc\u4e0d\u9ad8\u5174\u3002\u5353\u4e00\u822a\u63a8\u8f9e\u4e0d\u5f97\uff0c\u53ea\u597d\u5531\u9053\uff1a \u3000\u3000\u6005\u671b\u6d6e\u751f\u6025\u666f\uff0c\u51c4\u51c9\u5b9d\u745f\u9980\u97f3\uff0c\u695a\u5ba2\u591a\u60c5\u504f\u6028\u522b\uff0c\u78a7\u5c71\u8fdc\u6c34\u767b\u4e34\u3002 \u3000\u3000\u76ee\u9001\u8fde\u5929\u8870\u8349\uff0c\u591c\u9611\u51e0\u5904\u758f\u7827\u3002\u9ec4\u53f6\u65e0\u98ce\u81ea\u843d\uff0c\u79cb\u4e91\u4e0d\u96e8\u957f\u9634\uff0c \u3000\u3000\u5929\u82e5\u6709\u60c5\u5929\u4ea6\u8001\uff0c\u6447\u6447\u5e7d\u6068\u96be\u7981\u3002\u501c\u6005\u65e7\u6b22\u5982\u68a6\uff0c\u89c9\u6765\u65e0\u5904\u8ffd\u5bfb\u3002 \u3000\u3000\u5531\u5230\u201c\u5929\u82e5\u6709\u5029\u5929\u4ea6\u8001\u201d\u4e4b\u53e5\u65f6\uff0c\u773c\u6cea\u9669\u9669\u843d\u4e86\u4e0b\u6765\uff0c\u58f0\u97f3\u4e14\u6709\u70b9\u5636\u54d1\u4e86\u3002\u7389\u7f57\u5239\u4ee5\u524d \u5728\u660e\u6708\u5ce1\u65f6\u548c\u4ed6\u6240\u8bf4\u7684\u8bdd\uff1a\u201c\u666e\u5929\u4e4b\u4e0b\uff0c\u90a3\u6709\u9752\u6625\u957f\u9a7b\u4e4b\u4eba\uff1f\u6211\u8bf4\uff0c\u8001\u5929\u7237\u82e5\u7136\u50cf\u4eba\u4e00\u6837\uff0c \u601d\u591a\u8651\u591a\uff0c\u8001\u5929\u7237\u4e5f\u4f1a\u8001\u5440\uff01\u54b1\u4eec\u89c1\u4e00\u56de\u5435\u4e00\u56de\uff0c\u4e0b\u6b21\u4f60\u518d\u89c1\u5230\u6211\u65f6\uff0c\u53ea\u6050\u6211\u5df2\u662f\u767d\u53d1\u6ee1\u5934 \u7684\u8001\u5a46\u5a46\u4e86\uff01\u201d\u8fd9\u4e9b\u8bdd\u4e0d\u6599\u5982\u4eca\u7adf\u6210\u8c36\u8bed\uff0c\u800c\u8fd9\u9996\u8bcd\u201c\u8bcd\u724c\u540d\u201c\u6cb3\u6ee1\u5b50\u201d\uff0c\u5b8b\u4ee3\u5b59\u6d19\u6240 \u4f5c\u3002\u201d\u6b63\u662f\u5353\u4e00\u822a\u56e0\u6709\u611f\u65bc\u7389\u7f57\u5239\u4e4b\u8a00\u800c\u5531\u51fa\u6765\u7684\uff0c\u5531\u51fa\u4e4b\u5f8c\uff0c\u624d\u611f\u5230\u5174\u6b22\u4e50\u7684\u6c14\u6c1b\u592a\u4e0d\u76f8 \u8c03\u548c\u3002 \u3000\u3000\u4e00\u6b4c\u65e2\u6bd5\uff0c\u6ee1\u5ea7\u65e0\u6b22\uff0c\u54c8\u8428\u514b\u4eba\u867d\u7136\u5927\u534a\u4e0d\u61c2\u6c49\u8bed\uff0c\u4f46\u4e5f\u542c\u5f97\u51fa\u90a3\u51c4\u607b\u7684\u97f3\u8c03\u3002\u4f55\u843c\u534e \u5fc3\u9053\uff1a\u201c\u522b\u4eba\u6b63\u81ea\u6b22\u4e50\uff0c\u4f60\u5374\u5531\u8fd9\u6837\u7684\u6b4c\uff01\u201d\u4e0d\u5f85\u54c8\u8428\u514b\u4eba\u9068\u8bf7\uff0c\u4fbf\u9053\uff1a\u201c\u6211\u4e5f\u5531\u4e00\u652f \u5427\u3002\u201d\u53eb\u5353\u4e00\u822a\u66ff\u5979\u62c9\u7434\uff0c\u5531\u9053\uff1a \u3000\u3000\u665a\u98ce\u524d\uff0c\u67f3\u68a2\u9e26\u5b9a\uff0c\u5929\u8fb9\u6708\u4e0a\u3002\u9759\u6084\u6084\uff0c\u63a7\u91d1\u94a9\uff0c\u706f\u706d\u94f6\u8679\u3002\u6625\u7720\u62e5\u7ee3\u5e8a\uff0c\u9e9d\u5170\u9999\u6563\u8299 \u84c9\u5e10\u3002\u731b\u542c\u5f97\u811a\u6b65\u58f0\u54cd\u5230\u7eb1\u7a97\u3002\u4e0d\u89c1\u8427\u90ce\uff0c\u591a\u7ba1\u662f\u8981\u4eba\u513f\u8eb2\u5728\u56de\u5eca\u3002\u6563\u53cc\u6247\u6b32\u9a82\u8f7b\u72c2\uff0c\u4f46\u89c1 \u4e9b\u98ce\u7b5b\u7af9\u5f71\uff0c\u9732\u5815\u82b1\u9999\u3002\u53f9\u4e00\u58f0\u75f4\u5fc3\u5984\u60f3\uff0c\u6dfb\u591a\u5c11\u6df1\u95fa\u9b54\u969c\u3002 \u3000\u3000\u8fd9\u4e43\u662f\u6c5f\u5357\u4e00\u5e26\u6d41\u884c\u7684\u6c11\u95f4\u5c0f\u66f2\uff0c\u6b4c\u58f0\u7f2d\u7ed5\uff0c\u66f2\u8c03\u8f7b\u5feb\uff0c\u987f\u65f6\u95f4\u628a\u6c14\u6c1b\u626d\u8f6c\u8fc7\u6765\u3002\u54c8\u8428 \u514b\u7684\u9752\u5e74\u5c0f\u5925\u5b50\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u5531\u5f97\u771f\u597d\uff01\u201d\u628a\u4e00\u628a\u540d\u8d35\u7684\u80e1\u7434\u9001\u7ed9\u4f55\u843c\u534e\uff0c\u4ee5\u793a\u656c\u610f\u3002\u5353 \u4e00\u822a\u544a\u8bc9\u5979\u8fd9\u662f\u54c8\u8428\u514b\u65cf\u7684\u793c\u8282\uff0c\u4e0d\u80fd\u63a8\u8f9e\uff0c\u4f55\u843c\u534e\u542b\u7b11\u6536\u4e86\u3002\u90a3\u51e0\u4e2a\u5e74\u8f7b\u5c0f\u5925\u5b50\u5bf9\u5979\u751a\u4e3a \u597d\u611f\uff0c\u56f4\u5728\u5979\u7684\u8eab\u8fb9\u8c08\u8bdd\u3002\u4f55\u843c\u534e\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4ece\u90a3\u513f\u6765\u7684\uff1f\u201d\u6709\u61c2\u5f97\u6c49\u8bed\u7684\u5c11\u5e74\u7b54\u9053\uff1a \u201c\u6211\u4eec\u662f\u4ece\u4f0a\u7281\u6765\u7684\uff0c\u66fe\u7a7f\u8fc7\u64a4\u9a6c\u62c9\u7f55\u7684\u5927\u6c99\u6f20\u5462\uff01\u201d\u4f55\u843c\u534e\u5fc3\u5ff5\u4e00\u52a8\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u4eca\u65e5 \u5728\u65c5\u9014\u4e0a\u53ef\u66fe\u78b0\u89c1\u8fc7\u8fd9\u6837\u7684\u9053\u58eb\u9ebd\uff1f\u201d\u5c06\u4ed6\u7236\u4eb2\u7684\u5f62\u8c8c\u8be6\u7ec6\u8bf4\u4e86\u3002\u90a3\u54c8\u8428\u514b\u9752\u5e74\u9053\uff1a\u201c\u54e6\uff0c \u78b0\u89c1\u8fc7\u7684\u3002\u4f60\u4eec\u548c\u4ed6\u662f\u4e00\u8def\u7684\u5417\uff1f\u90a3\u9053\u58eb\u771f\u602a\uff0c\u6ee1\u8138\u6012\u5bb9\u5750\u5728\u9a6c\u80cc\u4e0a\uff0c\u6df7\u5728\u4e00\u7fa4\u5587\u561b\u7684\u4e2d \u95f4\u3002\u201d\u4f55\u843c\u534e\u5947\u9053\uff1a\u201c\u4ec0\u9ebd\uff1f\u5587\u561b\uff01\u201d\u5979\u7684\u7236\u4eb2\u548c\u5587\u561b\u53ef\u4ece\u6765\u6ca1\u6709\u4ea4\u60c5\u5440\uff01\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u662f \u5440\uff0c\u6211\u4eec\u4e5f\u89c9\u5f97\u51fa\u5947\uff0c\u4e00\u4e2a\u6c49\u65cf\u7684\u9053\u58eb\u6df7\u5728\u897f\u85cf\u5587\u561b\u7684\u4e2d\u95f4\uff0c\u523a\u773c\u6781\u4e86\u3002\u90a3\u4e9b\u5587\u561b\u4e5f\u9a91\u7740 \u9a6c\uff0c\u4e2a\u4e2a\u90fd\u50cf\u51f6\u5f97\u5f88\uff01\u201d \u3000\u3000\u4f55\u843c\u534e\u5403\u4e86\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u90a3\u9053\u58eb\u662f\u88ab\u4ed6\u4eec\u7f1a\u5728\u9a6c\u80cc\u4e0a\u7684\u5417\uff1f\u201d\u90a3\u5c0f\u5925\u5b50\u6447\u4e86\u6447\u5934\uff0c\u8bf4 \u9053\uff1a\u201c\u6211\u53ef\u6ca1\u77a7\u6e05\u695a\u3002\u90a3\u8001\u9053\u58eb\u6742\u5728\u5587\u561b\u7684\u9a6c\u7fa4\u4e2d\u95f4\uff0c\u5782\u5934\u4e27\u6c14\u7684\u6837\u5b50\u3002\u4ed6\u4eec\u7684\u9a6c\u7fa4\u8dd1\u5f97\u5f88 \u5feb\uff0c\u6211\u4eec\u8ba9\u8def\u4e0d\u53ca\uff0c\u8fd8\u7ed9\u4ed6\u4eec\u5237\u4e86\u51e0\u97ad\u3002\u201d\u5353\u4e00\u822a\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u5411\u90a3\u65b9\u8d70\uff01\u201d\u90a3\u5c0f\u4f19\u5b50\u9053\uff1a \u201c\u5411\u6211\u4eec\u6765\u7684\u65b9\u5411\u8d70\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u90a3\u9ebd\u4ed6\u4eec\u4e5f\u8981\u6a2a\u8fc7\u64a4\u9a6c\u62c9\u7f55\u7684\u5927\u6c99\u6f20\u4e86\u3002\u201d\u6c89\u601d\u534a \u664c\uff0c\u5ffd\u4ece\u884c\u56ca\u4e2d\u53d6\u51fa\u51e0\u6735\u96ea\u83b2\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u770b\u8fd9\u51e0\u6735\u96ea\u83b2\u5982\u4f55\uff1f\u201d\u8fd9\u51e0\u6735\u96ea\u83b2\u662f\u5353\u4e00\u822a\u4e0a\u5929 \u5c71\u5317\u9ad8\u5cf0\u63a2\u671b\u6666\u660e\u5e08\u4e4b\u65f6\u6240\u91c7\uff0c\u6bcf\u4e00\u6735\u90fd\u6709\u51e0\u5341\u7247\u82b1\u74e3\uff0c\u5c42\u5c42\u5305\u88f9\uff0c\u597d\u50cf\u4e00\u4e2a\u96ea\u7403\u3002\u90a3\u4e9b\u54c8 \u8428\u514b\u4eba\u60ca\u53f9\u4e0d\u5df2\uff0c\u90fd\u9053\uff1a\u201c\u8fd9\u6837\u5927\u7684\u96ea\u83b2\uff0c\u6211\u4eec\u89c1\u90fd\u8fd8\u672a\u89c1\u8fc7\uff0c\u4f60\u5230\u5e95\u662f\u4ece\u90a3\u91cc\u91c7\u6765\u7684\uff1f\u201d \u5353\u4e00\u822a\u7b11\u4e86\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u6211\u5c06\u8fd9\u51e0\u6735\u96ea\u83b2\u4e0e\u4f60\u4eec\u4ea4\u6362\u4e00\u56db\u9a86\u9a7c\uff0c\u4e00\u5f20\u5e10\u5e55\uff0c\u4f60\u4eec\u53ef\u613f\u610f \u9ebd\uff1f\u201d\u90a3\u4e9b\u54c8\u8428\u514b\u4eba\u5012\u5f88\u516c\u9053\uff0c\u8bf4\u9053\uff1a\u201c\u9a86\u9a7c\u6613\u5f97\uff0c\u96ea\u83b2\u96be\u6c42\uff0c\u8fd9\u51e0\u6735\u96ea\u83b2\u6bd4\u4e00\u5339\u9a86\u9a7c\u8981\u503c \u94b1\u5f97\u591a\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u5728\u6211\u6765\u8bf4\uff0c\u5374\u662f\u9a86\u9a7c\u96be\u5f97\uff0c\u96ea\u83b2\u6613\u91c7\u3002\u65e2\u7136\u4f60\u4eec\u613f\u610f\uff0c\u54b1\u4eec\u5c31\u4ea4\u6362 \u4e86\u5427\u3002\u201d\u90a3\u4e9b\u54c8\u8428\u514b\u4eba\u5927\u559c\uff0c\u8fd8\u9644\u9001\u4e86\u4ed6\u4eec\u4e00\u4e9b\u6c99\u6f20\u4e0a\u7684\u7528\u5177\u548c\u4e7e\u7cae\u3002 \u3000\u3000\u7b2c\u4e8c\u65e5\u4e00\u65e9\uff0c\u5353\u4e00\u822a\u4e0e\u54c8\u8428\u514b\u4eba\u5206\u624b\uff0c\u548c\u4f55\u843c\u534e\u9a91\u4e0a\u9a86\u9a7c\uff0c\u76f4\u5411\u897f\u884c\u3002\u4f55\u843c\u534e\u95ee\u9053\uff1a \u201c\u4f60\u4e3a\u4ec0\u9ebd\u8981\u8fd9\u9a86\u9a7c\uff0c\u8fd9\u9a86\u9a7c\u6bd4\u6211\u4eec\u884c\u5f97\u8fd8\u6162\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u64a4\u9a6c\u62c9\u7f55\u5927\u6c99\u6f20\u8fde\u8d2f\u56de\u7586\u5357 \u5317\uff0c\u9ec4\u6c99\u5343\u91cc\uff0c\u4f60\u53c8\u4e0d\u662f\u4e60\u60ef\u6c99\u6f20\u7684\u4eba\uff0c\u82e5\u65e0\u8fd9\u6c99\u6f20\u4e4b\u821f\uff0c\u5982\u4f55\u53bb\u5f97\uff1f\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u6211\u7684 \u7239\u7239\u600e\u9ebd\u4f1a\u548c\u90a3\u7fa4\u5587\u561b\u540c\u8d70\uff0c\u771f\u662f\u4ee4\u4eba\u731c\u60f3\u4e0d\u900f\uff0c\u96be\u9053\u662f\u88ab\u4ed6\u4eec\u7ed1\u67b6\u4e86\u9ebd\uff1f\u53ef\u662f\u6211\u7684\u7239\u4ece\u672a \u5230\u8fc7\u585e\u5916\uff0c\u548c\u5587\u557c\u66f4\u65e0\u4ea4\u845b\uff0c\u8fd9\u4e8b\u4e5f\u672a\u514d\u592a\u5947\u602a\u4e86\u3002\u201d\u5353\u4e00\u822a\u5374\u60f3\u8d77\u81ea\u5df1\u548c\u897f\u85cf\u5929\u9f99\u6d3e\u5587\u561b \u7ed3\u6028\u4e4b\u4e8b\uff0c\u5fc3\u9053\uff1a\u201c\u83ab\u975e\u662f\u5929\u9f99\u6d3e\u7684\u5587\u6240\u4e3a\u3002\u53ef\u662f\u4ed6\u4eec\u53c8\u600e\u77e5\u4ed6\u662f\u6211\u7684\u5e08\u53d4\uff1f\u800c\u4e14\u767d\u77f3\u5e08\u53d4 \u5251\u6cd5\u5728\u672c\u95e8\u4e2d\u6570\u4e00\u6570\u4e8c\uff0c\u53c8\u600e\u4f1a\u88ab\u4ed6\u4eec\u6697\u7b97\uff1f\u201d\u4e5f\u662f\u731c\u60f3\u4e0d\u900f\uff0c\u53ea\u9053\uff1a\u201c\u65e2\u7136\u77e5\u9053\u4ed6\u4eec\u5df2\u7a7f \u5165\u5927\u6c99\u6f20\u4e2d\uff0c\u54b1\u4eec\u53ea\u6709\u4e00\u8def\u8ffd\u8e2a\u53bb\u91c7\u5bfb\u6d88\u606f\u3002\u201d \u3000\u3000\u5927\u6c99\u6f20\u9ec4\u6c99\u5343\u91cc\uff0c\u6e3a\u65e0\u4eba\u70df\uff0c\u5e78\u597d\u662f\u4e24\u4eba\u7ed3\u4f34\u540c\u884c\uff0c\u53ef\u89e3\u5bc2\u5bde\u3002\u4f55\u843c\u534e\u4ec5\u662f\u5341\u4e03\u516b\u5c81\u7684 \u5c0f\u59d1\u5a18\uff0c\u53c8\u662f\u7b2c\u4e00\u6b21\u6765\u5230\u585e\u5916\uff0c\u5bf9\u6c99\u6f20\u7684\u666f\u8c61\uff0c\u6837\u6837\u611f\u5230\u65b0\u5947\uff0c\u5bf9\u6c5f\u6e56\u4e0a\u7684\u4e8b\u60c5\uff0c\u4e5f\u5e38\u5e38\u53d1 \u95ee\uff0c\u5353\u4e00\u822a\u548c\u5979\u8c08\u8c08\u8bf4\u8bf4\uff0c\u65e5\u5b50\u5012\u4e0d\u96be\u8fc7\uff0c\u53ea\u662f\u6bcf\u5f53\u4f55\u843c\u534e\u95ee\u53ca\u7389\u7f57\u5239\u7684\u4e8b\u65f6\uff0c\u5353\u4e00\u822a\u4fbf\u5f80 \u5f80\u7b11\u800c\u4e0d\u7b54\uff0c\u6216\u987e\u800c\u8a00\u4ed6\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u8d70\u4e86\u534a\u6708\uff0c\u4e5f\u4e0d\u65f6\u5728\u6c99\u6f20\u4e0a\u53d1\u73b0\u9a7c\u9a6c\u7684\u8db3\u5370\uff0c\u53ef\u662f\u8ddf\u7740\u90a3\u4e9b\u8db3\u5370\u8d70\u65f6\uff0c\u8db3\u5370\u53c8 \u5f80\u5f80\u56e0\u98ce\u6c99\u7684\u53d8\u5e7b\u800c\u88ab\u906e\u63a9\u3002\u4f55\u843c\u534e\u8d70\u4e86\u8fd9\u9ebd\u591a\u5929\u4ecd\u672a\u8d70\u51fa\u6c99\u6f20\uff0c\u4e0d\u89c9\u5fc3\u7126\uff0c\u4e00\u65e5\u5c06\u8fd1\u9ec4 \u660f\uff0c\u5ffd\u7136\u4e00\u9635\u9635\u98ce\u8fce\u9762\u522e\u6765\uff0c\u9ec4\u8272\u7684\u6c99\u96fe\u8fce\u98ce\u626c\u8d77\u3002\u5353\u4e00\u822a\u9053\uff1a\u201c\u770b\u6837\u5b50\uff0c\u4eca\u665a\u53c8\u8981\u522e\u5927\u98ce \u4e86\uff0c\u54b1\u4eec\u627e\u80cc\u98ce\u7684\u5730\u65b9\u5b89\u4e0b\u7bf7\u5e10\u5427\u3002\u201d\u665a\u4e0a\u72c2\u98ce\u679c\u7136\u522e\u5730\u800c\u6765\uff0c\u6c99\u6f20\u4e0a\u65e0\u6708\u65e0\u661f\uff0c\u9ec4\u7070\u8272\u7684 \u6c99\u96fe\uff0c\u5c31\u50cf\u539a\u539a\u7684\u4e00\u5f20\u9ec4\u5e10\uff0c\u906e\u5929\u853d\u5730\u3002 \u3000\u3000\u5353\u4e00\u822a\u62e3\u80cc\u98ce\u7684\u5730\u65b9\u642d\u8d77\u5e10\u5e55\uff0c\u56db\u8fb9\u7cfb\u4e0a\u5927\u77f3\uff0c\u9a86\u9a7c\u5728\u5e10\u5e55\u5916\u53c8\u50cf\u4e00\u9762\u5899\u58c1\uff0c\u5835\u7740\u98ce \u6c99\u3002\u94d9\u662f\u5982\u6b64\uff0c\u5e10\u5e55\u4ecd\u7136\u88ab\u98ce\u522e\u5f97\u547c\u62c9\u62c9\u54cd\u3002\u4f55\u843c\u534e\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u585e\u5916\u98ce\u6c99\uff0c\u5982\u6b64\u5389\u5bb3\uff1f\u201d \u5353\u4e00\u822a\u7b11\u9053\uff1a\u201c\u73b0\u5728\u8fd8\u4e0d\u662f\u98ce\u5b63\u5462\uff0c\u82e5\u662f\u98ce\u5b63\uff0c\u6c99\u4e18\u90fd\u4f1a\u88ab\u98ce\u79fb\u52a8\uff0c\u5f53\u98ce\u4e4b\u5904\uff0c\u4eba\u755c\u4e5f\u4f1a\u88ab \u98ce\u5377\u4e0a\u534a\u7a7a\uff0c\u9664\u4e86\u5e9e\u7136\u5927\u7269\u7684\u9a86\u9a7c\uff0c\u8c01\u90fd\u62b5\u6321\u4e0d\u4f4f\u3002\u8fd9\u573a\u98ce\u8fd8\u4e0d\u7b97\u5927\u7684\uff0c\u770b\u6765\u5f88\u5feb\u5c31\u4f1a\u8fc7 \u53bb\u3002\u201d \u3000\u3000\u8fc7\u4e86\u4e00\u9635\uff0c\u98ce\u52bf\u60ed\u5f31\uff0c\u4e24\u4eba\u6b63\u60f3\u6b47\u606f\uff0c\u5ffd\u95fb\u5f97\u5e10\u5916\u9a86\u9a7c\u957f\u5636\u4e00\u58f0\uff0c\u5353\u4e00\u822a\u62a2\u51fa\u5e10\u5916\uff0c\u53ea \u89c1\u4e24\u6761\u9ed1\u5f71\u5728\u9a86\u9a7c\u65c1\u8fb9\u500f\u7136\u7a7f\u51fa\u3002\u5353\u4e00\u822a\u4e3e\u624b\u53eb\u9053\uff1a\u201c\u98ce\u6c99\u672a\u8fc7\uff0c\u4e24\u4f4d\u4f55\u4e0d\u8bf7\u8fdb\u5e10\u4e2d\u7a0d \u805a\u3002\u201d \u3000\u3000\u90a3\u4e24\u4eba\u505c\u4e0b\u6b65\u6765\uff0c\u7adf\u662f\u6c49\u4eba\u8863\u7740\uff0c\u4e0a\u524d\u5531\u4e86\u4e2a\u504c\uff0c\u9053\uff1a\u201c\u6211\u4eec\u7684\u9a6c\u88ab\u98ce\u522e\u5012\uff0c\u5944\u5944\u4e00 \u606f\uff0c\u4e0d\u80fd\u7528\u4e86\u3002\u5f97\u76f8\u516c\u62db\u547c\uff0c\u90a3\u662f\u518d\u597d\u4e0d\u8fc7\u3002\u201d\u4fbf\u8ddf\u7740\u5353\u4e00\u822a\u53cc\u53cc\u4eba\u5185\u3002 \u3000\u3000\u5353\u4e00\u822a\u660e\u77e5\u4ed6\u4eec\u662f\u60f3\u5077\u9a86\u9a7c\uff0c\u4f46\u60f3\u8d77\u98ce\u6c99\u4e4b\u9669\uff0c\u4ed6\u4eec\u6ca1\u6709\u5750\u9a91\uff0c\u60f3\u5077\u9a86\u9a7c\u4e5f\u60c5\u6709\u53ef\u539f\uff0c \u56e0\u6b64\u5e76\u4e0d\u63ed\u7a7f\uff0c\u4ecd\u7136\u5ba2\u6c14\u62db\u5f85\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u6c49\u4eba\u8170\u60ac\u4ec6\u5200\uff0c\u6ee1\u8138\u6a2a\u8089\uff0c\u4f55\u843c\u534e\u77a5\u4e86\u5353\u4e00\u822a\u4e00\u773c\uff0c\u795e\u8272\u751a\u4e0d\u559c\u6b22\u3002\u5353\u4e00\u822a\u5fae\u7b11 \u9053\uff1a\u201c\u6c99\u6f20\u591c\u5bd2\uff0c\u751f\u8d77\u706b\u6765\uff0c\u5f04\u70b9\u5f00\u6c34\u5427\u3002\u201d\u4f55\u843c\u534e\u751f\u8d77\u4e86\u706b\u53d6\u51fa\u4e00\u4e2a\u94dc\u58f6\u5c06\u6c34\u56ca\u7684\u6c34\u503e \u4eba\uff0c\u9053\uff1a\u201c\u4f60\u642d\u4e2a\u7076\u5427\uff0c\u8981\u4e0d\u7136\u6c34\u58f6\u53ef\u6ca1\u5904\u653e\u5475\u3002\u201d\u5353\u4e00\u822a\u626b\u4e86\u4e00\u773c\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u6ca1\u6709\u788e \u77f3\uff0c\u538b\u5e10\u7bf7\u7684\u5927\u82e5\u77f3\u5934\u53ef\u4e0d\u5408\u7528\uff0c\u600e\u9ebd\u529e\u5462\uff1f\u201d\u90a3\u4e24\u4e2a\u6c49\u4eba\u9053\uff1a\u201c\u76f8\u516c\u4e0d\u7528\u5ba2\u6c14\uff0c\u6211\u4eec\u4e45\u5728 \u6c99\u6f20\uff0c\u6371\u5f97\u98ce\u5bd2\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f55\u5fc5\u7528\u8eab\u5b50\u6765\u6371\uff0c\u5f85\u6211\u60f3\u6cd5\u3002\u201d\u53c8\u626b\u4e86\u4e00\u773c\u9053\uff1a\u201c\u6211\u6709\u529e \u6cd5\u4e86\uff0c\u4e14\u8bd5\u4e00\u8bd5\u3002\u201d\u5c06\u538b\u5e10\u7bf7\u7684\u4e00\u5927\u5757\u5927\u77f3\u642c\u5230\u5e10\u4e2d\uff0c\u6697\u8fd0\u5185\u5bb6\u771f\u529b\uff0c\u53cc\u638c\u731b\u7136\u4e00\u62cd\uff0c\u559d \u58f0\uff1a\u201c\u5f00\uff01\u201d\u90a3\u5757\u5927\u77f3\u88c2\u4e3a\u56db\u5757\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e0d\u5c31\u884c\u4e86\uff01\u201d\u7acb\u523b\u642d\u8d77\u7076\u6765\uff0c\u90a3\u4e24\u4eba\u76ee\u77aa\u53e3 \u5446\uff0c\u505a\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u5353\u4e00\u822a\u63d0\u9632\u8fd9\u4e24\u4eba\u662f\u58e4\u4eba\uff0c\u6545\u610f\u9732\u4e86\u8fd9\u624b\uff0c\u4ecd\u7136\u82e5\u65e0\u5176\u4e8b\u7684\u548c\u4ed6\u4eec\u95f2\u8bdd\uff0c\u5f85\u6c34\u6eda\u65f6\uff0c\u5916 \u9762\u98ce\u6c99\u5df2\u6b62\uff0c\u90a3\u4e24\u4eba\u559d\u591f\u4e86\u6c34\uff0c\u62dc\u8f9e\u9053\uff1a\u201c\u591a\u8c22\u76f8\u516c\u62db\u547c\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u591c\u665a\u8d76\u8def\uff0c\u4e0d\u65b9 \u4fbf\u5427\uff1f\u201d\u90a3\u4e24\u4eba\u9053\uff1a\u201c\u6211\u4eec\u957f\u5e74\u5954\u8d70\uff0c\u5df2\u7ecf\u60ef\u4e86\u3002\u73b0\u5728\u4e0d\u662f\u98ce\u5b63\uff0c\u96be\u5f97\u522e\u4e00\u573a\u98ce\uff0c\u8fd9\u573a\u98ce\u522e \u8fc7\u4e4b\u5f8c\uff0c\u53c1\u4e94\u65e5\u5185\uff0c\u60f3\u5fc5\u4e0d\u4f1a\u518d\u522e\uff0c\u65e5\u95f4\u8d76\u8def\u548c\u665a\u95f4\u8d76\u8def\uff0c\u90fd\u662f\u4e00\u6837\u3002\u800c\u4e14\u76f8\u516c\u643a\u6709\u5973\u7737\uff0c \u6211\u4eec\u4e5f\u4e0d\u65b9\u4fbf\u518d\u53e8\u6270\u4e0b\u53bb\u3002\u201d\u4f55\u843c\u534e\u9762\u4e0a\u4e00\u7ea2\uff0c\u5353\u4e00\u822a\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u795d\u4e24\u4f4d\u8def\u4e0a\u5e73 \u5b89\u3002\u201d\u9001\u51fa\u5e10\u5916\u3002\u90a3\u4e24\u4e2a\u6c49\u4eba\u5ffd\u540c\u58f0\u95ee\u9053\uff1a\u201c\u8bf7\u76f8\u516c\u7559\u4e0b\u5927\u540d\uff0c\u65e5\u5f8c\u62a5\u7b54\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a \u201c\u4e9b\u4e9b\u5c0f\u4e8b\uff0c\u4f55\u8db3\u6302\u9f7f\uff1f\u201d\u90a3\u4e24\u4e2a\u6c49\u4eba\u76f8\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u518d\u53c1\u79f0\u8c22\u800c\u53bb\u3002 \u3000\u3000\u5353\u4e00\u822a\u56de\u5230\u5e10\u4e2d\uff0c\u4f55\u843c\u534e\u57cb\u6028\u9053\uff1a\u201c\u4eba\u5fc3\u96be\u6d4b\uff0c\u4f60\u600e\u9ebd\u4e0d\u95ee\u6e05\u695a\uff0c\u5c31\u9068\u8bf7\u4ed6\u4eec\u3002\u201d\u5353\u4e00 \u822a\u9053\uff1a\u201c\u6211\u8f88\u4fa0\u4e49\u4e2d\u4eba\uff0c\u5c82\u80fd\u89c1\u96be\u4e0d\u6551\u3002\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u90a3\u4e24\u4eba\u6ee1\u8138\u6a2a\u8089\uff0c\u6211\u4e00\u89c1\u5c31\u8ba8\u538c\u3002 \u4ed6\u4eec\u4e00\u5b9a\u4e0d\u662f\u597d\u4eba\uff0c\u5e78\u597d\u4f60\u9732\u4e86\u90a3\u624b\uff0c\u5c06\u4ed6\u4eec\u9547\u4f4f\u3002\u6211\u731c\u4ed6\u4eec\u4e00\u5b9a\u662f\u4f5c\u8d3c\u5fc3\u865a\uff0c\u5f8c\u6765\u89c1\u4f60\u8eab \u6000\u7edd\u6280\uff0c\u8fd9\u624d\u8d76\u5feb\u8d70\u7684\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u7b11\u9053\uff1a\u201c\u4e8b\u5df2\u8fc7\u53bb\uff0c\u4e0d\u5fc5\u80e1\u4e71\u731c\u6d4b\u4e86\u3002\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u7684\u529f\u592b\u771f\u597d\uff0c\u53ea \u662f\u53cc\u638c\u4e00\u538b\uff0c\u5c31\u80fd\u5c06\u90a3\u5927\u77f3\u88c2\u4e3a\u56db\u5757\uff0c\u8fde\u6211\u7684\u7239\u7239\u90fd\u672a\u5fc5\u80fd\u591f\uff0c\u6211\u770b\u9664\u4e86\u4e8c\u5e08\u4f2f\u5916\uff0c\u672c\u95e8\u4e2d \u4eba\uff0c\u8c01\u4e5f\u6ca1\u6709\u8fd9\u6837\u7684\u529f\u529b\u4e86\uff0c\u602a\u4e0d\u5f97\u5e08\u53d4\u4eec\u4e00\u5b9a\u8981\u8bf7\u4f60\u56de\u5c71\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u8fbe\u6469\u7956\u5e08\u7684\u6b66 \u529f\u7cbe\u6df1\u535a\u5927\u4e0d\u53ef\u601d\u8bae\uff0c\u6211\u4e0d\u8fc7\u662f\u7565\u5f97\u76ae\u6bdb\u800c\u5df2\u3002\u5982\u679c\u80fd\u5c06\u8fbe\u6469\u7956\u5e08\u7684\u79d8\u7b08\u5bfb\u56de\uff0c\u6211\u6d3e\u6b66\u529f\u90a3 \u624d\u771f\u662f\u65e0\u654c\u65bc\u5929\u4e0b\u3002\u201d\u5353\u4e00\u822a\u8fd9\u65f6\u5df2\u6697\u6697\u7acb\u4e0b\u8a93\u613f\uff1a\u6b66\u5f53\u5c71\u4eca\u751f\u4eca\u4e16\u662f\u7edd\u4e0d\u56de\u53bb\u7684\u4e86\uff0c\u53ef\u662f \u4e3a\u4e86\u62a5\u7b54\u5e08\u95e8\u4e4b\u6069\uff0c\u90a3\u6b66\u5f53\u79d8\u5c8c\uff0c\u5374\u662f\u975e\u627e\u56de\u4e0d\u53ef\uff0c\u7eb5\u4f7f\u81ea\u5df2\u6b7b\u5728\u585e\u5916\uff0c\u4e5f\u8981\u547d\u8f9b\u9f99\u5b50\u627e \u56de\u3002 \u3000\u3000\u98ce\u6c99\u5df2\u6b62\uff0c\u591c\u4ea6\u6e10\u6df1\uff0c\u4e24\u4eba\u8c08\u4e86\u4e00\u4f1a\uff0c\u5404\u81ea\u6b47\u606f\uff0c\u90a3\u4e24\u540d\u964c\u751f\u5ba2\u4eba\u65e2\u8d70\uff0c\u4f55\u843c\u534e\u653e\u4e0b\u4e86 \u5fc3\uff0c\u4e0d\u4e00\u4f1a\u5c31\u547c\u547c\u719f\u7761\uff0c\u5fae\u5f31\u7684\u706b\u5149\u6620\u7740\u5979\u82f9\u679c\u822c\u7684\u8138\u5e9e\uff0c\u7a1a\u6c14\u4e4b\u4e2d\u900f\u7740\u8ff7\u4eba\u7684\u5c11\u5973\u60c5\u6001\uff0c \u5353\u4e00\u822a\u6697\u6697\u53f9\u4e86\u53e3\u6c14\uff0c\u4e0d\u7531\u5f97\u60f3\u8d77\u5728\u9ec4\u9f99\u6d1e\u521d\u4f1a\u7389\u7f57\u5239\u7684\u60c5\u666f\uff0c\u90a3\u65f6\u7389\u7f57\u5239\u88c5\u7761\u88c5\u5f97\u6781\u4f3c\uff0c \u8138\u4e0a\u4e5f\u662f\u4e00\u6d3e\u5929\u771f\u65e0\u90aa\u7684\u6837\u5b50\uff0c\u8bb0\u5f97\u81ea\u5df1\u6015\u5979\u7740\u51c9\uff0c\u8fd8\u8f7b\u8f7b\u7684\u8131\u4e86\u5927\u8863\uff0c\u76d6\u5728\u5979\u7684\u8eab\u4e0a\u2026\u2026 \u500f\u800c\u53c8\u60f3\u4e86\u201c\u7f8e\u4eba\u81ea\u53e4\u5982\u540d\u5c06\uff0c\u4e0d\u8bb8\u4eba\u95f4\u89c1\u767d\u5934\u201d\u7684\u8bd7\u53e5\uff0c\u60f3\u8d77\u81ea\u5df1\u8f9c\u8d1f\u5982\u82b1\u7f8e\u7737\uff0c\u4f3c\u6c34\u6d41 \u5e74\uff0c\u7531\u4e0d\u5f97\u6f5c\u7136\u592a\u606f\u3002 \u3000\u3000\u60c5\u6000\u6005\u89e6\uff0c\u6101\u601d\u5982\u6f6e\uff0c\u5353\u4e00\u822a\u4e45\u4e45\u4e0d\u80fd\u5165\u7761\uff0c\u770b\u7740\u90a3\u4e00\u5806\u706b\u60ed\u6e10\u5c31\u8981\u7184\u706d\uff0c\u6b63\u60f3\u8d77\u8eab\u52a0 \u4e00\u628a\u706b\uff0c\u5ffd\u95fb\u5f97\u5e10\u5916\u9a86\u9a7c\u53c8\u662f\u4e00\u58f0\u957f\u5636\uff0c\u5353\u4e00\u822a\u5fc3\u9053\uff1a\u201c\u96be\u9053\u90a3\u4e24\u4e2a\u5bb6\u4f19\u53c8\u56de\u6765\u4e86\uff1f\u201d\u6b20\u8eab \u6b32\u8d77\uff0c\u5ffd\u5730\u4e00\u58f0\u88c2\u5e1b\uff0c\u5e10\u5e55\u7a81\u7136\u6495\u5f00\u4e86\u4e00\u6761\u88c2\u53e3\uff0c\u52b2\u98ce\u75be\u5439\uff0c\u5bd2\u5149\u4e00\u95ea\uff0c\u4e00\u67c4\u660e\u6643\u6643\u7684\u98de\u5200 \u63b7\u4e86\u5165\u6765\uff0c\u5353\u4e00\u822a\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u6307\u4e00\uff0c\u5c06\u98de\u5200\u7529\u4e0b\u5730\u4e0a\uff0c\u62d4\u51fa\u968f\u8eab\u5b9d\u5251\uff0c\u7528\u4e2a\u201c\u767d\u86c7\u51fa\u6d1e\u201d \u62db\u5f0f\uff0c\u5251\u5c16\u5411\u5916\u4e00\u5410\uff0c\u56db\u56f4\u4e00\u6c64\uff0c\u9884\u9632\u6697\u7b97\uff0c\u8eab\u5b50\u968f\u7740\u5251\u5149\u7a7f\u51fa\u5e10\u5e55\u3002 \u3000\u3000\u5e10\u5e55\u5916\u7684\u654c\u4eba\u5374\u5e76\u672a\u518d\u62d6\u6697\u5668\uff0c\u5929\u9ed1\u6c89\u6c89\uff0c\u5353\u4e00\u822a\u53ea\u4f9d\u7a00\u89c1\u7740\u53c1\u6761\u9b41\u68a7\u7684\u8eab\u5f71\uff0c\u5411\u897f\u75be \u8dd1\uff0c\u5353\u4e00\u822a\u5927\u6012\u559d\u9053\uff1a\u201c\u5077\u9a86\u9a7c\u7684\u5c0f\u8d3c\uff0c\u6211\u597d\u5fc3\u62db\u547c\u4f60\u4eec\u8eb2\u907f\u98ce\u6c99\uff0c\u4f60\u4eec\u5374\u6069\u5c06\u4ec7\u62a5\uff0c\u8fd8\u6562 \u9080\u96c6\u540c\u515a\uff0c\u6697\u65bd\u6bd2\u624b\uff0c\u6211\u82e5\u4e0d\u60e9\u6212\u4f60\u4eec\uff0c\u5929\u7406\u96be\u5bb9\uff01\u201d\u5251\u968f\u8eab\u8d70\uff0c\u65cb\u98ce\u822c\u7684\u6251\u4e0a\u524d\u53bb\uff0c\u5239\u90a3 \u4e4b\u95f4\uff0c\u5c31\u8ffd\u5230\u4e86\u53c1\u4eba\u8eab\u5f8c\u3002 \u3000\u3000\u5353\u4e00\u822a\u4ee5\u4e3a\u8fd9\u53c1\u4eba\u4e2d\uff0c\u5176\u4e2d\u4e24\u4eba\u4e00\u5b9a\u662f\u5148\u524d\u7684\u6c49\u4eba\u3002\u5c82\u77e5\u521a\u521a\u8ffd\u4e0a\uff0c\u90a3\u53c1\u4eba\u5ffd\u7136\u56de\u8fc7\u5934 \u6765\uff0c\u5176\u4e2d\u4e00\u4eba\u559d\u9053\uff1a\u201c\u8001\u5b50\u7eb5\u6a2a\u585e\u5916\uff0c\u8981\u5077\u4e5f\u662f\u5077\u73cd\u5947\u5b9d\u8d1d\uff0c\u8c01\u8981\u5077\u4f60\u9a86\u9a7c\uff01\u201d\u53c8\u4e00\u4eba\u9053\uff1a \u201c\u6211\u5012\u8981\u770b\u770b\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u6709\u4ec0\u9ebd\u672c\u9886\uff1f\u503c\u5f97\u6211\u4eec\u9999\u4e3b\u8d39\u8fd9\u9ebd\u5927\u7684\u6c14\u529b\uff0c\u7279\u522b\u9080\u8bf7\uff1f\u201d\u8fd9\u53c1 \u4e2a\u4eba\u90fd\u4ee5\u9ed1\u7eb1\u8499\u9762\uff0c\u8bf4\u8bdd\u7684\u4e24\u4eba\u53e3\u97f3\u6709\u70b9\u6c99\u54d1\uff0c\u5e76\u4e0d\u662f\u5148\u524d\u7684\u90a3\u4e24\u4e2a\u6c49\u4eba\uff0c\u53e6\u4e00\u4e2a\u8499\u9762\u4eba\u5374 \u53ea\u662f\u53d1\u51fa\u563b\u563b\u7684\u51b7\u7b11\uff0c\u5e76\u4e0d\u8bf4\u8bdd\u3002 \u3000\u3000\u5353\u4e00\u822a\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u53c1\u4e2a\u8499\u9762\u4eba\u884c\u5f84\u4e0e\u8bf4\u8bdd\u7684\u53e4\u602a\uff0c\u5b8c\u5168\u51fa\u4e4e\u4ed6\u610f\u6599\u4e4b\u5916\uff01 \u3000\u3000\u542c\u8fd9\u4e9b\u4eba\u53e3\u6c14\uff0c\u9887\u6709\u6765\u5386\uff0c\u4f46\u6697\u4e2d\u5077\u88ad\uff0c\u5374\u662f\u6b66\u6797\u6240\u4e0d\u9f7f\u7684\u884c\u4e3a\uff0c\u6309\u8bf4\u6709\u6765\u5934\u7684\u4eba\uff0c\u4e0d \u5e94\u51fa\u6b64\u3002\u6b64\u5176\u4e00\u3002\u201c\u9999\u4e3b\u201d\u4e43\u662f\u4e2d\u539f\u5e2e\u4f1a\u9996\u9886\u7684\u4e00\u79cd\u5c0a\u79f0\uff0c\u5728\u585e\u5916\u8fb9\u9119\u4e4b\u5730\uff0c\u4f55\u4ee5\u6709\u5173\u5185 \u201c\u9999\u5802\u201d\u7684\u7ec4\u7ec7\uff1f\u6b64\u5176\u4e8c\u3002\u5353\u4e00\u822a\u8fd9\u51e0\u5e74\u6765\u867d\u7136\u9605\u5386\u5927\u589e\uff0c\u5bf9\u6b64\u5374\u662f\u4e07\u5206\u4e0d\u89e3\u3002\u4ed6\u672c\u6765\u53c8\u6000 \u7591\u8fc7\u8fd9\u51e0\u4e2a\u8499\u9762\u4eba\u662f\u897f\u85cf\u5929\u9f99\u6d3e\u7684\u5587\u561b\uff0c\u4f46\u542c\u4ed6\u4eec\u6c49\u8bdd\u8bf4\u5f97\u5982\u6b64\u6d41\u5229\uff0c\u5374\u53c8\u4e0d\u4f3c\u3002 \u3000\u3000\u8fd9\u65f6\u53cc\u65b9\u5df2\u5982\u7bad\u5728\u5f26\uff0c\u90a3\u5bb9\u5f97\u5353\u4e00\u822a\u7ec6\u7ec6\u63a8\u6572\u3002\u8bf4\u8bdd\u7684\u90a3\u4e24\u4e2a\u8499\u9762\u4eba\u4e00\u4e2a\u8f6c\u8eab\uff0c\u7acb\u523b\u52a8 \u624b\u3002\u4e00\u4e2a\u624b\u4f7f\u5224\u5b98\u7b14\uff0c\u70b9\u6253\u5d29\u6572\uff0c\u5341\u5206\u51cc\u5389\uff1a\u4e00\u4e2a\u53cc\u638c\u5288\u626b\uff0c\u864e\u864e\u751f\u98ce\uff0c\u638c\u529b\u4ea6\u751a\u96c4\u52b2\u3002 \u3000\u3000\u5353\u4e00\u822a\u4e0d\u610f\u5728\u5927\u6f20\u4e4b\u4e2d\uff0c\u9aa4\u9047\u9ad8\u624b\uff0c\u609a\u7136\u4e00\u9707\uff0c\u6253\u9192\u7cbe\u795e\uff0c\u6025\u5c55\u6b66\u5f53\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u5251\u6cd5 \u8fce\u654c\uff0c\u5237\u5237\u4e24\u5251\uff0c\u5206\u53d6\u4e8c\u4eba\uff0c\u5feb\u5982\u63a3\u7535\uff0c\u4f7f\u5224\u5b98\u7b14\u7684\u5de6\u7b14\u4e00\u5c01\uff0c\u53f3\u7b14\u659c\u70b9\u5353\u4e00\u822a\u7684\u201c\u7b11\u8170 \u7a74\u201d\uff0c\u53ea\u542c\u5f97\u5f53\u7684\u4e00\u58f0\uff0c\u706b\u82b1\u98de\u6e85\uff0c\u5224\u5b98\u7b14\u88ab\u6c64\u51fa\u53bb\uff0c\u5353\u4e00\u822a\u864e\u53e3\u4e5f\u5fae\u5fae\u53d1\u70ed\u3002\u5353\u4e00\u822a\u53d8\u62db \u4f55\u7b49\u5feb\u6377\uff0c\u4ed6\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u5251\u7ef5\u7ef5\u4e0d\u7edd\uff0c\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u5df2\u662f\u8eab\u79fb\u6b65\u6362\uff0c\u5411\u53e6\u4e00\u540d\u654c\u4eba\u75be\u8fdb \u53c1\u62db\uff0c\u90a3\u540d\u654c\u4eba\u4e5f\u597d\u751f\u5389\u5bb3\uff0c\u8eab\u8eaf\u4e00\u77ee\u907f\u8fc7\u4e86\u4e0a\u76d8\u7684\u4e00\u5251\uff0c\u5de6\u624b\u4e00\u6307\uff0c\u6709\u638c\u5f80\u5de6\u81c2\u4e0b\u4e00\u7a7f\uff0c \u6307\u622e\u638c\u5288\uff0c\u8feb\u5f97\u5353\u4e00\u822a\u7684\u7b2c\u53c1\u5251\u504f\u8fc7\u4e00\u65c1\uff0c\u63a5\u7740\u53cc\u8db3\u4e00\u57ab\u52b2\uff0c\u5237\u7684\u98de\u8eab\u800c\u8d77\uff0c\u5411\u53f3\u4fa7\u7eb5\u51fa\u4e00 \u4e08\u5f00\u5916\uff0c\u5353\u4e00\u822a\u653b\u52bf\u5341\u5206\u51cc\u5389\u7684\u8fce\u95e8\u53c1\u62db\uff0c\u7adf\u7ed9\u4ed6\u534a\u653b\u534a\u5b88\uff0c\u5168\u907f\u5f00\u53bb\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c \u4f7f\u5224\u5b98\u7b14\u7684\u8499\u9762\u4eba\u53c8\u7f20\u4e86\u4e0a\u6765\uff0c\u53cc\u7b14\u659c\u98de\uff0c\u52bf\u6377\u529b\u731b\uff0c\u5353\u4e00\u822a\u56de\u8eab\u4e00\u5251\uff0c\u4e3e\u817f\u6a2a\u626b\uff0c\u6b66\u5f53\u6d3e \u7684\u201c\u9e33\u9e2f\u8fde\u73af\u817f\u201d\u4e0e\u5251\u6cd5\u540c\u6837\u9a70\u540d\uff0c\u8fd9\u4e00\u62db\u201c\u4e0a\u4e0b\u4ea4\u5f81\u201d\uff0c\u5251\u817f\u5e76\u7528\uff0c\u90a3\u4f7f\u5224\u5b98\u7b14\u7684\u8499\u9762\u4eba \u82e5\u907f\u523a\u5411\u4e0a\u76d8\u7684\u5251\uff0c\u5c31\u907f\u4e0d\u5f00\u626b\u5411\u4e0b\u76d8\u7684\u817f\uff1a\u82e5\u907f\u626b\u5411\u4e0b\u76d8\u7684\u817f\uff0c\u5c31\u907f\u4e0d\u5f00\u523a\u5411\u4e0a\u76d8\u7684\u5251\uff0c \u5f62\u52bf\u5341\u5206\u5371\u6025\u3002 \u3000\u3000\u5251\u817f\u9f50\u98de\uff0c\u5251\u5148\u5230\uff0c\u817f\u5f8c\u5230\uff0c\u90a3\u8499\u9762\u4eba\u521a\u521a\u67b6\u5f00\u4e0a\u76d8\u7684\u5251\uff0c\u5353\u4e00\u822a\u7684\u98de\u811a\u5de6\u626b\u53f3\u8e22\uff0c\u5df2 \u5230\u524d\u5fc3\u3002\u4f46\u5728\u8fd9\u77ac\u606f\u4e4b\u95f4\uff0c\u90a3\u88ab\u5353\u4e00\u822a\u8feb\u5f00\u7684\u6c49\u5b50\u5df2\u662f\u4e00\u9000\u590d\u4e0a\uff0c\u98de\u8dc3\u800c\u6765\uff0c\u84e6\u7136\u53cc\u638c\u4e0b \u62ff\uff0c\u7adf\u662f\u201c\u5927\u64d2\u62ff\u624b\u201d\u4e2d\u7684\u201c\u98de\u9e70\u6293\u5154\u201d\u62db\u6570\uff0c\u82e5\u88ab\u4ed6\u62ff\u7740\u817f\u5f2f\uff0c\u6b66\u529f\u591a\u5f3a\uff0c\u4e5f\u8981\u5f53\u573a\u683d \u5012\u3002\u5353\u4e00\u822a\u5413\u7684\u4e00\u70b9\u8db3\uff0c\u4e5f\u659c\u7a9c\u51fa\u516d\u4e03\u5c3a\u5916\uff0c\u5fc3\u4e2d\u597d\u4e0d\u8be7\u5f02\uff0c\u8fd9\u4eba\u7684\u624b\u6cd5\u8eab\u6cd5\uff0c\u4f3c\u4e4e\u662f\u5728\u90a3 \u513f\u89c1\u8fc7\u4f3c\u7684\u3002 \u3000\u3000\u4e24\u8499\u9762\u4eba\u559d\u9053\uff1a\u201c\u90a3\u91cc\u8d70\uff01\u201d\u5de6\u53f3\u5305\u6284\uff0c\u5206\u8fdb\u5408\u51fb\uff0c\u7b14\u8d77\u9f99\u86c7\uff0c\u638c\u98ce\u98d5\u98d5\uff0c\u5e76\u529b\u5f3a\u653b\u3002 \u5353\u4e00\u822a\u6012\u9053\uff1a\u201c\u6211\u8fd8\u6015\u4f60\u4e0d\u6210\uff1f\u53ea\u662f\u77a7\u4f60\u4e24\u4eba\u8eab\u4efd\uff0c\u4ea6\u975e\u51e1\u4fd7\uff0c\u5374\u505a\u4e0b\u53c1\u6d41\u7684\u52fe\u5f53\uff0c\u53ef\u60dc\u53ef \u60dc\uff01\u201d\u90a3\u4f7f\u5224\u5b98\u7b14\u7684\u4eba\u5927\u7b11\u9053\uff1a\u201c\u8bd5\u8bd5\u4f60\u7684\u8eab\u624b\uff0c\u600e\u80fd\u7b97\u5f97\u4e0b\u6d41\uff1f\u201d\u5353\u4e00\u822a\u65e0\u6687\u4e0e\u4ed6\u5206\u8fa8\uff0c \u5c55\u5251\u75be\u523a\u3002\u90a3\u4eba\u867d\u7136\u8bf4\u662f\u8bd5\u62db\uff0c\u90a3\u53cc\u7b14\u5374\u662f\u4e13\u5411\u4eba\u8eab\u53c1\u5341\u516d\u9053\u5927\u7a74\u4e0b\u624b\uff1f\u6beb\u4e0d\u7559\u60c5\uff0c\u800c\u90a3\u540d \u901a\u6653\u201c\u5927\u64d2\u62ff\u624b\u201d\u7684\u5bb6\u4f19\uff0c\u66f4\u662f\u72e0\u653b\u6076\u6253\uff0c\u4fe8\u5982\u5bf9\u4ed8\u5927\u654c\u5f3a\u4ec7\uff01 \u3000\u3000\u5353\u4e00\u822a\u5927\u6012\uff0c\u4f7f\u51fa\u5e73\u751f\u7edd\u6280\uff0c\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u5251\u7ef5\u7ef5\u4e0d\u7edd\uff0c\u6709\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u6eda\u6eda\u800c\u4e0a\uff0c\u4ee5 \u653b\u5bf9\u653b\uff0c\u6253\u5f97\u96be\u5206\u96be\u89e3\u3002\u8f97\u8f6c\u6597\u4e86\u53c1\u4e94\u5341\u62db\uff0c\u662f\u4e0d\u5206\u80dc\u8d1f\u3002 \u3000\u3000\u53c1\u4e2a\u8499\u9762\u4eba\uff0c\u6709\u4e24\u4eba\u5174\u5353\u4e00\u822a\u6076\u6597\uff0c\u5c1a\u6709\u4e00\u4eba\u5374\u60a0\u7136\u81ea\u5f97\uff0c\u7acb\u5728\u65c1\u8fb9\u89c2\u6218\uff0c\u65f6\u4e0d\u65f6\u53d1\u51fa \u4e00\u4e24\u58f0\u7b11\u58f0\u3002\u5353\u4e00\u822a\u597d\u751f\u8be7\u5f02\uff0c\u4f46\u5374\u4ea6\u4e0d\u80fd\u4e0d\u9632\u4ed6\u6765\u5077\u88ad\u3002\u5fc3\u4e2d\u731c\u4e0d\u900f\u4ed6\u4eec\u662f\u4f55\u7b49\u6837\u4eba\uff1f \u3000\u3000\u6b63\u9163\u6597\u4e2d\uff0c\u4f55\u843c\u534e\u5df2\u4ece\u5e10\u5e55\u4e2d\u51b2\u51fa\uff0c\u5982\u98de\u8d76\u81f3\u3002\u5353\u4e00\u822a\u987e\u8651\u5f3a\u654c\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u4e0d\u5fc5 \u4e0a\u524d\u3002\u201d\u4f55\u843c\u534e\u90a3\u91cc\u80af\u542c\uff0c\u65cb\u98ce\u822c\u75be\u4e0a\uff01\u5237\u7684\u4e00\u5251\uff0c\u4fbf\u523a\u90a3\u4f7f\u5224\u5b98\u7b14\u7684\u51e4\u773c\u7a74\uff0c\u90a3\u4eba\u56de\u7b14\u6a2a \u67b6\uff0c\u4f55\u843c\u534e\u5341\u5206\u6e9c\u6ed1\uff0c\u62db\u5f0f\u4e00\u8f6c\uff0c\u8eab\u5b50\u5df2\u8f6c\u5230\u53e6\u4e00\u4eba\u7684\u53f3\u4fa7\uff0c\u5251\u5c16\u4e00\u6307\uff0c\u523a\u7684\u662f\u8170\u80cc\u201c\u7cbe\u4fc3 \u7a74\u201d\uff0c\u90a3\u4eba\u53cd\u624b\u4e00\u638c\uff0c\u638c\u98ce\u6c64\u8863\uff0c\u4f55\u7f18\u534e\u201c\u5413\u201d\u7684\u4e00\u8df3\uff0c\u53eb\u9053\uff1a\u201c\u597d\u5389\u5bb3\uff01\u201d\u53c8\u8df3\u5f00\u4e86\u3002 \u3000\u3000\u4f55\u843c\u534e\u7684\u5251\u6cd5\u4e43\u662f\u767d\u77f3\u9053\u4eba\u6089\u5fc3\u4f20\u6388\uff0c\u867d\u7136\u8fdc\u6bd4\u4e0d\u4e0a\u5353\u4e00\u822a\uff0c\u4f46\u8fd9\u4e24\u4eba\u5728\u5353\u4e00\u822a\u51cc\u5389\u5251 \u62db\u7684\u5a01\u80c1\u4e0b\uff0c\u4e00\u65f6\u4e4b\u95f4\u5374\u4e5f\u5948\u4f55\u5979\u4e0d\u5f97\uff0c\u800c\u4e14\u5979\u7684\u8eab\u6cd5\u8f7b\u7075\uff0c\u6253\u6cd5\u6e9c\u6ed1\uff0c\u8f6c\u6765\u8f6c\u53bb\uff0c\u5de6\u4e00 \u5251\uff0c\u53f3\u4e00\u5251\uff0c\u4e0a\u4e00\u5251\uff0c\u4e0b\u4e00\u5251\uff0c\u6240\u523a\u7684\u4e5f\u90fd\u662f\u4eba\u8eab\u7a74\u9053\u6240\u5728\uff0c\u90a3\u4e24\u4eba\u867d\u7136\u4e0d\u628a\u5979\u5f53\u6210\u5f3a\u654c\uff0c \u5374\u4e5f\u4e0d\u5f97\u4e0d\u9632\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u5f62\u52bf\u5927\u53d8\u3002\u90a3\u4e24\u4eba\u6218\u5353\u4e00\u822a\u5df2\u662f\u5403\u529b\uff0c\u52a0\u4e0a\u4e86\u4e00\u4e2a\u4f55\u843c\u534e\u4ece\u4e2d\u7a9c\u6270\uff0c\u7acb\u611f\u4e0d \u652f\u3002\u90a3\u5728\u65c1\u89c2\u6218\u7684\u8499\u9762\u4eba\u8fd9\u65f6\u5fcd\u4e0d\u7740\u4e86\uff0c\u5ffd\u5730\u957f\u5578\u4e00\u58f0\uff0c\u89e3\u4e0b\u675f\u8170\u7684\u76ae\u5e26\uff0c\u968f\u624b\u4e00\u6325\uff0c\u5288\u556a \u4f5c\u54cd\uff0c\u90a3\u76ae\u5e26\u5728\u4ed6\u624b\u91cc\uff0c\u5c31\u5982\u8f6f\u97ad\u4e00\u822c\uff0c\u5237\u7684\u4e00\u4e2a\u76d8\u65cb\uff0c\u7167\u5353\u4e00\u822a\u80a9\u5934\u4fbf\u626b\uff0c\u5353\u4e00\u822a\u4e00\u4e2a \u201c\u5012\u8e29\u4e03\u661f\u201d\uff0c\u5de7\u6b65\u65cb\u8eab\uff0c\u8fde\u4eba\u5e26\u5251\uff0c\u8f6c\u5230\u654c\u4eba\u8eab\u5f8c\uff0c\u5251\u5c16\u4e00\u6307\uff0c\u75be\u82e5\u98d8\u98ce\uff0c\u90a3\u8499\u9762\u4eba\u76f4\u50cf \u80cc\u5f8c\u957f\u7740\u773c\u775b\u4e00\u6837\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u76ae\u5e26\u53cd\u624b\u4e00\u5377\uff0c\u5353\u4e00\u822a\u5927\u5403\u4e00\u9a7e\uff0c\u614c\u5fd9\u7f29\u624b\uff0c\u6599\u4e0d\u5230\u8fd9\u8499\u9762 \u4eba\u7adf\u7136\u901a\u6653\u201c\u542c\u98ce\u8fa8\u5668\u201d\u4e4b\u672f\uff0c\u6b66\u529f\u4e5f\u9ad8\u51fa\u5148\u524d\u4e8c\u4eba\u8bb8\u591a\u3002 \u3000\u3000\u4f7f\u76ae\u5e26\u7684\u8499\u9762\u4eba\u52a0\u5165\u4e4b\u5f8c\uff0c\u5f62\u52bf\u53c8\u53d8\uff0c\u5353\u4e00\u822a\u4f55\u843c\u534e\u4ee5\u4e8c\u654c\u53c1\uff0c\u6e10\u6e10\u53ea\u6709\u62db\u67b6\u7684\u4efd\u513f\u3002 \u90a3\u4f7f\u5224\u5b98\u7b14\u7684\u654c\u4eba\u53c8\u53d1\u8a00\u51b7\u5632\u9053\uff1a\u201c\u54c8\uff0c\u6b66\u5f53\u638c\u95e8\uff0c\u4ea6\u4e0d\u8fc7\u5982\u6b64\uff01\u9999\u4e3b\u5bf9\u4ed6\u4e5f\u672a\u514d\u592a\u8fc7\u770b\u91cd \u4e86\uff01\u201d\u5353\u4e00\u822a\u5927\u6012\uff0c\u5251\u950b\u4e00\u8f6c\uff0c\u76f4\u5982\u9e70\u96bc\u7a7f\u6797\uff0c\u63a0\u6ce2\u5de8\u9e1f\uff0c\u500f\u7136\u4ece\u4f7f\u76ae\u5e26\u7684\u654c\u4eba\u8eab\u8fb9\u7a7f\u51fa\uff0c \u4e00\u62db\u201c\u731b\u9e21\u593a\u7c9f\u201d\uff0c\u5251\u5149\u95ea\u70c1\uff0c\u523a\u4ed6\u9762\u4e0a\u53cc\u775b\uff0c\u90a3\u4eba\u4f7f\u4e2a\u201c\u6a2a\u67b6\u91d1\u6881\u201d\uff0c\u53cc\u7b14\u5411\u4e0a\u6a2a\u67b6\uff0c\u90a3 \u77e5\u5353\u4e00\u822a\u8fd9\u62db\u5374\u662f\u865a\u62db\uff0c\u53ea\u89c1\u4e00\u7f15\u9752\u5149\uff0c\u5251\u968f\u8eab\u8f6c\uff0c\u201c\u55e4\u201d\u7684\u4e00\u54cd\uff0c\u5df2\u628a\u4ed6\u8863\u895f\u523a\u7a7f\u4e86\u4e00\u4e2a \u5927\u6d1e\uff0c\u8fd9\u8fd8\u662f\u4ed6\u95ea\u5c55\u817e\u632a\u5feb\u75be\uff0c\u8981\u4e0d\u7136\u8fd9\u4e00\u5251\u4fbf\u662f\u6d1e\u8179\u7a7f\u80c1\u4e4b\u707e\u3002 \u3000\u3000\u4f7f\u5224\u5b98\u7b14\u7684\u8499\u9762\u4eba\u5413\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u5353\u4e00\u822a\u5251\u62db\u4e4b\u602a\uff0c\u5927\u51fa\u4ed6\u4eec\u610f\u6599\u4e4b\u5916\uff0c\u4f7f\u76ae\u5e26\u7684\u8499\u9762 \u4eba\u201c\u566b\u201d\u4e86\u4e00\u58f0\uff0c\u7adf\u4e0d\u662f\u6b66\u5f53\u4e03\u5341\u4e8c\u624b\u8fde\u73af\u5251\u7684\u5bb6\u6570\uff0c\u6070\u5982\u5e73\u5730\u751f\u6ce2\uff0c\u5947\u5cf0\u7a81\u51fa\uff0c\u500f\u7136\u800c \u6765\uff0c\u5bc2\u7136\u800c\u901d\uff0c\u4ee4\u4eba\u6349\u6478\u4e0d\u5b9a\uff0c\u9632\u4e0d\u80dc\u9632\uff0c\u4e00\u8fde\u51e0\u62db\uff0c\u5c06\u53c1\u4e2a\u8499\u9762\u4eba\u8feb\u5f97\u8fde\u8fde\u5f8c\u9000\u3002\u4ed6\u4eec\u90a3 \u91cc\u731c\u60f3\u5f97\u5230\uff0c\u8fd9\u51e0\u62db\u4e43\u662f\u6b66\u6797\u7edd\u5b66\uff0c\u4e45\u5df2\u5931\u4f20\u7684\u8fbe\u6469\u5251\u5f0f\u3002 \u3000\u3000\u8fd9\u53c1\u4e2a\u8499\u9762\u4eba\u60ef\u7ecf\u5927\u654c\uff0c\u5747\u975e\u5eb8\u624b\uff0c\u89c1\u5353\u4e00\u822a\u5251\u62db\u602a\u5f02\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u9000\u5b88\u8054\u9632\u3002\u8fbe\u6469\u5251 \u5f0f\u867d\u7136\u5389\u5bb3\uff0c\u53ef\u662f\u5353\u4e00\u822a\u4f1a\u7684\u53ea\u4e0d\u8fc7\u51e0\u62db\uff0c\u7528\u4ee5\u7a81\u88ad\uff0c\u90a3\u8fd8\u53ef\u4ee5\uff0c\u7528\u4ee5\u4e45\u6218\uff0c\u5374\u662f\u4e0d\u80fd\u3002\u6570 \u62db\u4e00\u8fc7\uff0c\u654c\u4eba\u770b\u7834\u865a\u5b9e\uff0c\u53c8\u56f4\u4e86\u4e0a\u6765\u3002\u5353\u4e00\u822a\u53ea\u5f97\u4ecd\u7528\u6b66\u5f53\u7684\u8fde\u73af\u5251\u6cd5\uff0c\u6742\u4ee5\u8fbe\u6469\u5251\u5f0f\uff0c\u62b5 \u5fa1\u5f3a\u654c\u3002 \u3000\u3000\u53c8\u62da\u6597\u4e86\u53c1\u4e94\u5341\u62db\uff0c\u5353\u4f55\u4e8c\u4eba\u66f4\u5904\u4e0b\u98ce\uff0c\u53c1\u4e2a\u8499\u9762\u4eba\u653b\u5f97\u66f4\u7d27\uff0c\u4f46\u5353\u4e00\u822a\u5251\u52bf\u7ef5\u5bc6\uff0c\u4f55 \u843c\u534e\u8eab\u6cd5\u8f7b\u7075\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u5374\u4e5f\u672a\u9732\u8d25\u8c61\u3002\u90a3\u4f7f\u76ae\u5e26\u7684\u8499\u9762\u4eba\u6740\u5f97\u6027\u8d77\uff0c\u4f7f\u51fa\u201c\u56de\u98ce\u626b\u67f3\u201d \u7684\u8f6f\u97ad\u62db\u6570\uff0c\u547c\u547c\u98ce\u54cd\uff0c\u731b\u5377\u8fc7\u6765\u3002\u5353\u4e00\u822a\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5ffd\u7136\u5931\u58f0\u53eb\u9053\uff1a\u201c\u970d\u8001\u524d\u8f88\uff0c\u4f60\u4f55\u6545 \u4e24\u6b21\u53c1\u756a\u4e0e\u6211\u4e3a\u654c\uff1f\u201d\u201d\u8fd9\u4e2a\u8499\u9762\u4eba\u6b63\u662f\u66fe\u4e0a\u5929\u5c71\u5357\u9ad8\u5cf0\uff0c\u88ab\u7389\u7f57\u5239\u6253\u8d25\u7684\u970d\u5143\u4ef2\uff0c\u970d\u5143\u4ef2 \u7684\u8f6f\u97ad\u5728\u6b66\u6797\u4e2d\u4e43\u662f\u4e00\u7edd\uff0c\u5353\u4e00\u822a\u5148\u524d\u56e0\u4ed6\u4e00\u6765\u8499\u9762\uff0c\u4e8c\u6765\u6539\u7528\u8170\u5e26\uff0c\u6240\u4ee5\u5230\u73b0\u5728\u624d\u8ba4\u5f97\u51fa \u6765\u3002 \u3000\u3000\u970d\u5143\u4ef2\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u7684\u7389\u7f57\u5239\u5462\uff1f\u201d\u5353\u4e00\u822a\u6012\u9053\uff1a\u201c\u4f60\u4e0e\u7389\u7f57\u5239\u6709\u4ec7\uff0c\u7406\u8be5\u524d\u53bb \u627e\u5979\uff0c\u6789\u4f60\u662f\u524d\u8f88\u82f1\u96c4\uff0c\u5374\u505a\u8fd9\u9f20\u7a83\u72d7\u6478\u7684\u52fe\u5f53\uff0c\u6a2a\u65bd\u4e00\u5200\uff0c\u6697\u5c04\u4e00\u7bad\uff0c\u6211\u82e5\u8bf4\u4e0e\u6b66\u6797\u540c\u9053 \u77e5\u9053\uff0c\u770b\u4f60\u8fd9\u8001\u9762\u76ae\u5f80\u90a3\u91cc\u653e\uff1f\u201d\u970d\u5143\u4ef2\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8c01\u6697\u7b97\u4f60\u4e86\uff0c\u4f60\u56de\u5e10\u5e55\u53bb\u770b\uff0c\u6211\u66ff\u4f60 \u9001\u8bf7\u5e16\u6765\u5462\uff01\u7389\u7f57\u5239\u4e5f\u6709\u4eba\u9001\u8bf7\u5e16\u53bb\u4e86\uff0c\u6709\u80c6\u7684\u4f60\u4eec\u5c31\u4f9d\u671f\u8d74\u4f1a\uff01\u201d\u8bf4\u7f62\uff0c\u53c8\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c \u53eb\u9053\uff1a\u201c\u8bd5\u62db\u591f\u4e86\uff0c\u8fd9\u5c0f\u5b50\u505a\u4f60\u4eec\u9999\u4e3b\u7684\u5ba2\u4eba\uff0c\u8fd8\u4e0d\u81f3\u65bc\u57cb\u6ca1\u4f60\u4eec\u5427\uff1f\u201d\u76ae\u5e26\u6325\u4e86\u4e00\u4e2a\u534a \u5f27\uff0c\u89e3\u5f00\u5353\u4e00\u822a\u653b\u6765\u7684\u4e00\u5251\uff0c\u500f\u7136\u9000\u4e0b\u3002 \u3000\u3000\u5353\u4e00\u822a\u6014\u4e86\u4e00\u6014\uff0c\u5374\u4e0d\u6599\u5c31\u5728\u4ed6\u548c\u970d\u5143\u4ef2\u8bf4\u8bdd\u4e4b\u65f6\uff0c\u65e0\u6687\u517c\u987e\uff0c\u90a3\u4e24\u4e2a\u8499\u9762\u4eba\u5ffd\u5730\u5411\u4f55 \u843c\u534e\u731b\u65bd\u6740\u624b\uff0c\u4f7f\u5224\u5b98\u7b14\u7684\u67b6\u7740\u4f55\u843c\u534e\u7684\u5251\uff0c\u53e6\u4e00\u4eba\u5de6\u624b\u5982\u94a9\uff0c\u64d2\u62ff\u7693\u8155\uff0c\u53f3\u638c\u4e00\u6325\uff0c\u5370\u5979 \u80f8\u819b\uff0c\u4f55\u843c\u534e\u88ab\u90a3\u4f7f\u5224\u5b98\u7b14\u7684\u7f20\u7740\uff0c\u65e0\u6cd5\u62b5\u5fa1\uff0c\u53ea\u89c9\u638c\u98ce\u5982\u5200\uff0c\u98d2\u7136\u6cbe\u8863\uff0c\u4e0d\u89c9\u5931\u58f0\u5c16\u53eb\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5239\u90a3\u4e4b\u95f4\uff0c\u7d27\u63a5\u7740\u53c8\u662f\u4e00\u58f0\u5c16\u53eb\uff0c\u968f\u7740\u201c\u5495\u549a\u201d\u4e00\u58f0\uff0c\u6709\u4eba\u7ffb\u8eab\u5012\u5730\u3002\u539f\u6765\u662f\u5353 \u4e00\u822a\u98de\u8eab\u5f80\u6551\uff0c\u4e00\u62db\u8fbe\u6469\u5251\u5f0f\u4e2d\u7684\u201c\u4e00\u82c7\u6e21\u6c5f\u201d\uff0c\u5c06\u90a3\u4eba\u53f3\u638c\u6d1e\u7a7f\uff0c\u53ef\u662f\u56e0\u4ed6\u6025\u65bc\u6551\u4eba\uff0c\u98de \u649e\u8fc7\u53bb\uff0c\u80a9\u5934\u66ff\u4f55\u843c\u534e\u53d7\u4e86\u4e00\u6293\uff0c\u53ea\u89c9\u706b\u8fa3\u8fa3\u822c\u4f5c\u75db\u3002 \u3000\u3000\u970d\u5143\u4ef2\u53eb\u9053\uff1a\u201c\u53d7\u4f24\u4e86\u9ebd\uff1f\u201d\u90a3\u4f7f\u5224\u5b98\u7b14\u7684\u95f7\u58f0\u4e0d\u54cd\uff0c\u80cc\u8d77\u540c\u4f34\u3002\u56de\u8eab\u4fbf\u8dd1\uff0c\u970d\u5143\u4ef2\u53eb \u9053\uff1a\u201c\u5353\u4e00\u822a\uff0c\u4f60\u82e5\u4e0d\u6015\u522b\u4eba\u62a5\u8fd9\u4e00\u5251\u4e4b\u4ec7\uff0c\u54b1\u4eec\u98ce\u7802\u94c1\u5821\u518d\u89c1\uff01\u201d\u5353\u4e00\u822a\u8fde\u58f0\u51b7\u7b11\uff0c\u6309\u5251 \u4e0d\u8ffd\u3002 \u3000\u3000\u4f55\u843c\u534e\u95ee\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u88ab\u4ed6\u7684\u9b3c\u624b\u6293\u7740\u4e86\uff1f\uff0c\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u9ebd\uff0c\u54b1\u4eec\u56de \u53bb\u3002\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u4f60\u8ba4\u8bc6\u4ed6\u4eec\u7684\u5417\uff0c\u4ed6\u4eec\u65e2\u8bf4\u662f\u8bd5\u62db\uff0c\u4e3a\u4f55\u8fd9\u6837\u72e0\u6bd2\uff1f\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211 \u8ba4\u8bc6\u90a3\u4f7f\u76ae\u5e26\u7684\u4eba\u662f\u970d\u5143\u4ef2\u3002\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u55ef\uff0c\u970d\u5143\u4ef2\uff0c\u4ed6\u548c\u6211\u7239\u7239\u6709\u8fc7\u4e00\u6bb5\u6881\u5b50\uff0c\u6211\u770b \u6211\u7684\u7239\u7239\u4e00\u5b9a\u662f\u88ab\u4ed6\u4eec\u6697\u7b97\u4e86\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u8be7\u5f02\u95ee\u9053\uff1a\u201c\u4ec0\u9ebd\u6881\u5b50\uff0c\u6211\u5012\u6ca1\u542c\u767d\u77f3\u5e08\u53d4\u8bf4\u8fc7\u3002\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u6211\u4e5f\u662f\u5230\u4e86\u585e \u5916\u4e4b\u5f8c\uff0c\u624d\u542c\u4ed6\u8bf4\u8d77\u7684\u3002\u636e\u7239\u7239\u8bf4\uff0c\u53c1\u5341\u5e74\u524d\u970d\u5143\u4ef2\u66fe\u548c\u4ed6\u8c08\u8bba\u6b66\u529f\uff0c\u4e0d\u670d\u6b66\u5f53\u5251\u6cd5\u662f\u5929\u4e0b \u7b2c\u4e00\uff0c\u7239\u7239\u5c31\u548c\u4ed6\u6bd4\u8bd5\uff0c\u53c1\u5341\u62db\u4e4b\u5185\uff0c\u4fbf\u5c06\u4ed6\u523a\u4e86\u4e00\u5251\uff0c\u95ee\u4ed6\u670d\u4e86\u6ca1\u6709\u4e86\u90a3\u970d\u5143\u4ef2\u4e5f\u786c\uff0c\u95ed \u53e3\u4e0d\u7b54\uff0c\u6211\u7239\u7239\u53c8\u523a\u4e86\u4ed6\u4e00\u5251\uff0c\u4e00\u76f4\u8feb\u4ed6\u8bf4\u51fa\u670d\u4e86\uff0c\u8fd9\u624d\u5e72\u4f11\u3002\u201d\u5353\u4e00\u822a\u53f9\u9053\uff1a\u201c\u5e08\u53d4\u5c11\u5e74 \u4e4b\u65f6\uff0c\u6c14\u4e5f\u592a\u76db\u4e86\u3002\u201d\u5176\u5b9e\u767d\u77f3\u9053\u4eba\u8001\u4e86\uff0c\u813e\u6c14\u4e5f\u8fd8\u672a\u6539\u3002\u4f55\u843c\u534e\u9053\uff1a\u201c\u662f\u5440\uff0c\u8fd9\u4ef6\u4e8b\u6211\u7239 \u7239\u662f\u505a\u5f97\u6709\u70b9\u8fc7\u4efd\u4e86\u3002\u6240\u4ee5\u4ed6\u8fd9\u6b21\u548c\u6211\u8fdc\u6765\u585e\u5916\uff0c\u5c31\u5bf9\u6211\u8bf4\uff0c\u585e\u5916\u5e76\u65e0\u9ad8\u624b\uff0c\u53ea\u662f\u8981\u63d0\u9632\u4e2a \u970d\u5143\u4ef2\uff0c\u6050\u9632\u4ed6\u62a5\u53c1\u5341\u5e74\u524d\u4e24\u5251\u4e4b\u4ec7\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u51ed\u970d\u5143\u4ef2\u7684\u6b66\u529f\uff0c\u4ed6\u73b0\u5728\u6700\u591a\u4e5f\u4e0d\u8fc7 \u4e0e\u4f60\u7239\u6253\u4e2a\u5e73\u624b\u3002\u4f60\u7239\u7239\u8c05\u4e0d\u81f3\u65bc\u53d7\u4ed6\u6697\u7b97\uff0c\u53ea\u6050\u8fd9\u91cc\u9762\u8fd8\u7275\u6d89\u6709\u4eba\u3002\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u662f \u5440\uff0c\u970d\u5143\u4ef2\u521a\u624d\u4e0d\u662f\u8bf4\u4ec0\u9ebd\u98ce\u7802\u94c1\u5821\uff0c\u53c8\u8bf4\u4ec0\u9ebd\u8bf7\u5e16\u5417\uff1f\u96be\u9053\u4ed6\u53e6\u6709\u540c\u515a\uff0c\u8d81\u8fd9\u7a7a\u6863\u5230\u54b1\u4eec \u5e10\u7bf7\u4e2d\u9001\u5e16\u5b50\u4e86\uff01\u54b1\u4eec\u5012\u4e0d\u53ef\u4e0d\u9632\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u56de\u5230\u5e10\u7bf7\u5916\u9762\uff0c\u5353\u4e00\u822a\u6253\u71c3\u706b\u77f3\uff0c\u4ee5\u5251\u6311\u5f00\u5e10\u7bf7\uff0c\u5f80\u91cc\u4e00\u7167\uff0c\u4f46\u89c1\u6b8b \u706b\u5df2\u706d\uff0c\u5e10\u4e2d\u7a7a\u65e0\u4e00\u4eba\u3002\u4f55\u843c\u534e\u8fdb\u53bb\u52a0\u4e86\u4e00\u4e9b\u539f\u6765\u662f\u51c6\u5907\u7ed9\u9a86\u9a7c\u5403\u7684\u67af\u8349\uff0c\u62e8\u8d77\u706b\u82d7\uff0c\u7eb3\u95f7 \u9053\uff1a\u201c\u970d\u5143\u4ef2\u80e1\u8bf4\u516b\u9053\uff0c\u90a3\u91cc\u6709\u4ec0\u9ebd\u8bf7\u5e16\uff01\u201d\u5353\u4e00\u822a\u773c\u5229\uff0c\u4e00\u773c\u77a5\u89c1\u521a\u624d\u7ed9\u81ea\u5df1\u7529\u5728\u5730\u4e0b\u7684 \u98de\u5200\uff0c\u5200\u5c16\u4e0a\u7a7f\u7740\u4e00\u5f20\u7eb8\u6761\uff0c\u6025\u5fd9\u62ac\u8d77\uff0c\u9053\uff1a\u201c\u54e6\uff0c\u8bf7\u5e16\u539f\u6765\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u98de\u5200\u9001\u5e16\uff0c\u5728\u6c5f\u6e56\u4e0a\u5012\u662f\u5e38\u6709\u7684\u4e8b\uff0c\u7528\u610f\u4e0d\u5728\u4f24\u4eba\uff0c\u56e0\u4e4b\u4e0d\u80fd\u7b97\u662f\u5077\u88ad\u3002\u5353\u4e00\u822a\u53d6\u4e0b\u5b57 \u6761\uff0c\u7b11\u9053\uff1a\u201c\u6211\u8fd8\u9053\u970d\u5143\u4ef2\u8fd9\u8001\u5934\u600e\u4f1a\u505a\u90a3\u4e0b\u6d41\u7684\u52fe\u5f53\uff0c\u53ea\u662f\u4ed6\u4e5f\u662f\u6709\u8eab\u4efd\u7684\u4eba\uff0c\u6211\u4e14\u770b\u4ed6 \u80af\u66ff\u4ec0\u9ebd\u4eba\u9001\u5e16\uff1f\u201d\u4f55\u843c\u534e\u51d1\u8fc7\u53bb\u770b\uff0c\u53ea\u89c1\u5b57\u6761\u4e0a\u5199\u9053\uff1a\u201c\u4e45\u95fb\u6b66\u5f53\u6d3e\u79f0\u9738\u4e2d\u539f\uff0c\u501f\u4e07\u91cc\u5173 \u5c71\uff0c\u65e0\u7f18\u8bf7\u6559\uff0c\u4eca\u8d35\u638c\u95e8\u65e2\u8fdc\u6e38\u8fb9\u9119\uff0c\u5c82\u53ef\u4e0d\u7a0d\u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff0c\u4e03\u5915\u4e4b\u671f\uff0c\u5821\u4e2d\u5019\u6559\u3002\u98ce\u7802\u5821 \u5821\u4e3b\u656c\u7ea6\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u76b4\u7709\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u970d\u5143\u4ef2\u8fd9\u6653\u820c\uff0c\u5230\u5904\u8bf4\u6211\u662f\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\uff0c\u4ee5\u81f4\u5f15\u51fa\u8fd9\u79cd\u9877\u3002 \u6211\u90a3\u8fd8\u6709\u5fc3\u60c5\u5728\u6b66\u6797\u4e89\u96c4\u963f\uff01\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u4e3a\u4e86\u6211\u7684\u7239\u7239\uff0c\u4f60\u4e0d\u60f3\u4e89\u96c4\uff0c\u4e5f\u8981\u4e89\u4e00\u4e0b \u4e86\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u90a3\u4e9b\u54c8\u8428\u514b\u4eba\u8bf4\u4f60\u7239\u7239\u548c\u4e00\u7fa4\u5587\u561b\u540c\u8d70\uff0c\u672a\u5fc5\u5c31\u662f\u5728\u98ce\u7802\u5821\u4e2d\u3002\u201d\u4f55\u843c \u534e\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u6761\u7ebf\u7d22\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u98ce\u7802\u5821\u5230\u5e95\u5750\u843d\u4f55\u65b9\uff0c\u6211\u4eec\u4e5f\u4e0d\u77e5 \u9053\u3002\u201d\u80a9\u5934\u4f24\u5904\uff0c\u5fae\u5fae\u4f5c\u75db\uff0c\u4f55\u843c\u534e\u89c1\u4ed6\u76b4\u8d77\u7709\u5934\uff0c\u6025\u5fd9\u53d6\u51fa\u91d1\u521b\u836f\uff0c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u54b1\u4eec\u5148 \u6577\u4e86\u836f\u518d\u8bf4\u5427\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u55ef\uff0c\u7ed9\u6211\u3002\u201d\u80cc\u97e9\u4e86\u9762\uff0c\u6495\u5f00\u80a9\u4e0a\u7684\u8863\u88f3\uff0c\u81ea\u5df1\u6577\u836f\u3002\u4f55\u843c \u534e\u5929\u771f\u70c2\u6f2b\uff0c\u5e73\u65e5\u4e0d\u62d8\u75d5\u8ff9\u3002\u5353\u4e00\u822a\u548c\u5979\u76f8\u5904\uff0c\u65f6\u65f6\u63d0\u5fc3\u540a\u80c6\uff0c\u6015\u7389\u7f57\u5239\u7a81\u7136\u51fa\u73b0\uff0c\u5f15\u8d77\u8bef \u89e3\uff0c\u6240\u4ee5\u603b\u907f\u514d\u548c\u5979\u808c\u80a4\u76f8\u63a5\uff0c\u89c1\u5979\u60f3\u66ff\u81ea\u5df1\u6577\u836f\uff0c\u6025\u5fd9\u81ea\u5df1\u52a8\u624b\u3002 \u3000\u3000\u4f55\u843c\u534e\u5fc3\u4e2d\u6697\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u4e8f\u4ed6\u8fd8\u662f\u638c\u95e8\u5462\uff1f\u8fd9\u6837\u5ff8\u6029\u4f5c\u6001\u3002\u201d\u5e10\u7bf7\u5916\u5ffd\u7136\u53c8\u6709\u811a\u6b65\u58f0 \u54cd\uff0c\u9a86\u9a7c\u53c8\u5636\u9e23\u8d77\u6765\u3002 \u3000\u3000\u5353\u4e00\u822a\u6454\u4e0b\u836f\u818f\uff0c\u62d4\u5251\u559d\u9053\uff1a\u201c\u8c01\uff1f\u201d\u5e10\u7bf7\u5f00\u5904\uff0c\u5148\u524d\u90a3\u4e24\u4e2a\u6c49\u4eba\u53c8\u8d70\u4e86\u56de\u6765\uff0c\u9053\uff1a \u201c\u5353\u76f8\u516c\uff0c\u6211\u4eec\u5411\u4f60\u8bf7\u7f6a\u6765\u4e86\uff01\u201d\u4f55\u843c\u534e\u6012\u9053\uff1a\u201c\u4f60\u4eec\u5f04\u4ec0\u9ebd\u7384\u865a\uff0c\u6211\u770b\u4f60\u4eec\u5b9a\u662f\u970d\u5143\u4ef2\u7684 \u4e00\u7caa\u3002\u201d\u90a3\u4e24\u4eba\u9053\uff1a\u201c\u59d1\u5a18\u4f60\u731c\u5bf9\u4e86\uff0c\u4f46\u4f60\u4eec\u4e5f\u731c\u9519\u4e86\u3002\u54ce\u54df\uff0c\u4f60\u53d7\u4e86\u4f24\u4e86\uff0c\u8fd9\u662f\u6bd2\u7802\u638c\u4e4b \u4f24\uff0c\u5728\u8fd9\u8fb9\u8352\u5927\u6f20\uff0c\u5982\u4f55\u6551\u6cbb\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u89c1\u4f24\u53e3\u75d2\uff0c\u5df2\u5728\u6000\u7591\uff0c\u542c\u4ed6\u4eec\u53eb\u56b7\uff0c\u4e00\u7b11\u9053\uff1a\u201c\u679c\u7136\u662f\u91d1\u8001\u602a\u6240\u4f20\u7684\u6bd2\u638c\u3002\u201d\u90a3 \u4e24\u4eba\u9053\uff1a\u201c\u5353\u76f8\u516c\u65e2\u77e5\u4ed6\u7684\u6765\u5386\uff0c\u8fd8\u4e0d\u53ca\u65e9\u60f3\u6cd5\u6551\u6cbb\uff1f\u201d\u5353\u4e00\u822a\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u5c31\u662f\u518d\u5019\u5341 \u4e8c\u4e2a\u65f6\u8fb0\uff0c\u8ba9\u5b83\u53d1\u4f5c\uff0c\u6211\u4e5f\u8fd8\u80fd\u6551\u6cbb\u3002\u6bd2\u7802\u638c\u6709\u4ec0\u9ebd\u4e86\u4e0d\u8d77\uff0c\u7528\u5f97\u7740\u8fd9\u9ebd\u7740\u6025\uff1f\u4f60\u4eec\u4e14\u8bf4\uff0c \u4f60\u4eec\u8981\u5411\u6211\u8bf7\u4ec0\u9ebd\u7f6a\uff1f\u201d \u3000\u3000\u4f55\u843c\u534e\u89c1\u8bf4\u662f\u6bd2\u7802\u638c\uff0c\u5374\u53d8\u4e86\u989c\u8272\uff0c\u539f\u6765\u6b66\u5f53\u6d3e\u4f20\u6709\u79d8\u65b9\uff0c\u64c5\u533b\u6bd2\u7802\u638c\uff0c\u53ef\u662f\u5374\u8981\u70e7\u5341 \u5927\u9505\u70ed\u6c34\uff0c\u5229\u7528\u6c34\u84b8\u6c14\u7684\u70ed\u529b\u5c06\u4f53\u5185\u7684\u6bd2\u8feb\u51fa\u6765\uff0c\u8fd9\u6837\u914d\u5408\u89e3\u836f\uff0c\u624d\u80fd\u89c1\u6548\u3002\u5728\u8fd9\u6c99\u6f20\uff0c\u6ef4 \u6c34\u5982\u91d1\uff0c\u9a86\u9a7c\u7684\u6c34\u56ca\u4ec5\u8db3\u4f9b\u6570\u65e5\u4e4b\u7528\uff0c\u5982\u4f55\u80fd\u70e7\u90a3\u5341\u5927\u9505\u70ed\u6c34\uff1f \u3000\u3000\u5353\u4e00\u822a\u5374\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u610f\uff0c\u50ac\u90a3\u4e24\u4eba\u5feb\u8bf4\u3002\u90a3\u4e24\u4eba\u9053\uff1a\u201c\u6211\u4eec\u662f\u98ce\u7802\u5821\u7684\u5821\u4e01\u3002\u201d\u5353\u4e00\u822a \u9053\uff1a\u201c\u55ef\uff0c\u6211\u521a\u521a\u6536\u5230\u4f60\u4eec\u5821\u4e3b\u7684\u8bf7\u5e16\u3002\u201d\u90a3\u4e24\u4eba\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u4eec\u5df2\u7ecf\u77e5\u9053\u3002\u201d\u4f55\u843c\u534e\u8feb\u4e0d \u53ca\u5f85\uff0c\u62a2\u7740\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u7684\u5821\u4e3b\u59d3\u4ec0\u540d\u8c01\uff1f\u4ed6\u4e3a\u4ec0\u9ebd\u8981\u7ea6\u6211\u7684\u5927\u54e5\u6bd4\u6b66\uff1f\u201d \u3000\u3000\u9762\u524d\u7684\u90a3\u4eba\u7b54\u9053\uff1a\u201c\u6211\u4eec\u7684\u5821\u4e3b\u53eb\u6210\u7ae0\u4e94\uff0c\u4ed6\u672c\u6765\u662f\u4ece\u5173\u5185\u6765\u7684\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6ca1\u542c \u8fc7\u8fd9\u4e2a\u540d\u5b87\u3002\u201d\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u4ed6\u6765\u4e86\u51e0\u5341\u5e74\u4e86\u3002\u5353\u76f8\u516c\u7684\u5e08\u53d4\u4e5f\u8bb8\u77e5\u9053\u3002\u4ed6\u4ee5\u524d\u4e5f\u5728\u6dee\u5357\u5f00 \u8bbe\u9999\u5802\uff0c\u8d29\u8fd0\u79c1\u76d0\uff0c\u5f8c\u6765\u88ab\u5b98\u519b\u8feb\u5f97\u7d27\u8981\uff0c\u65e0\u5904\u7acb\u8db3\uff0c\u5e26\u4e86\u4e9b\u5144\u5f1f\u9003\u5230\u585e\u5916\u6765\uff0c\u4e5f\u5feb\u53c1\u5341\u5e74 \u4e86\uff0c\u5f53\u5e74\u7684\u5144\u5f1f\u5269\u4e0b\u7684\u4e5f\u6709\u9650\u4e86\u3002\u4ed6\u624d\u5728\u585e\u5916\u5b9a\u5c45\u3002\u6211\u4eec\u7684\u7236\u4eb2\u5c31\u662f\u8ddf\u4ed6\u9003\u6765\u7684\u3002\u64a4\u9a6c\u62c9\u7f55 \u6c99\u6f20\u7684\u8fb9\u7f18\uff0c\u6709\u4e00\u7247\u6c34\u8349\u5bcc\u9976\u4e4b\u5730\uff0c\u7267\u6c11\u6015\u98ce\u7802\u4fb5\u88ad\uff0c\u4e0d\u6562\u5230\u90a3\u8fb9\u7267\u7f8a\u3002\u4ed6\u5374\u5728\u90a3\u91cc\u5efa\u8d77\u5e84 \u5821\uff0c\u4e3b\u5821\u7528\u94c1\u5efa\u6210\u5854\u5f62\uff0c\u4e0d\u6015\u98ce\u7802\uff0c\u56e0\u6b64\u5c31\u53eb\u505a\u98ce\u7802\u5821\uff0c\u5916\u4eba\u4e5f\u79f0\u4e3a\u98ce\u7802\u94c1\u5821\u3002\u51e0\u5341\u5e74\u6765\uff0c \u4ed6\u7387\u9886\u6211\u4eec\u8fd9\u4e00\u7fa4\u6c49\u4eba\u5728\u90a3\u91cc\u57a6\u8352\u755c\u7267\uff0c\u65e5\u5b50\u5012\u8fd8\u8fc7\u5f97\u53bb\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u90a3\u5f88\u4e0d\u9519\u561b\uff0c\u597d \u597d\u7684\u65e5\u5b50\u4ed6\u4e0d\u8fc7\uff0c\u4e3a\u4f55\u53c8\u8981\u627e\u6211\u751f\u4e8b\uff1f\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u70c8\u58eb\u66ae\u5e74\uff0c\u58ee\u5fc3\u672a\u5df2\u3002\u524d\u51e0\u5e74\uff0c\u4e2d\u539f\u6765\u4e86\u4e00\u4e2a\u767d\u53d1\u9b54\u5973\uff0c\u585e\u5916\u5404\u65cf\u82f1 \u96c4\uff0c\u4e0d\u8bba\u80e1\u6c49\uff0c\u6709\u540d\u7684\u90fd\u51e0\u4e4e\u53d7\u8fc7\u5979\u7684\u6298\u8fb1\u3002\u6211\u4eec\u56e0\u5728\u6c99\u6f20\u4e4b\u8fb9\uff0c\u540c\u65f6\u5821\u4e3b\u5f52\u9690\u5df2\u4e45\uff0c\u4fa5\u5e78 \u5979\u6ca1\u6765\u8fc7\u3002\u53ef\u662f\u53d7\u8fc7\u5979\u6298\u8fb1\u7684\u4eba\uff0c\u6709\u4eba\u77e5\u9053\u6211\u4eec\u7684\u5821\u4e3b\u662f\u4e2a\u6709\u672c\u9886\u4e4b\u4eba\uff0c\u5c31\u66fe\u9080\u8fc7\u4ed6\u51fa\u5c71\uff0c \u8981\u9664\u6389\u90a3\u4e2a\u9b54\u5973\uff0c\u6211\u4eec\u7684\u5821\u4e3b\u4e00\u76f4\u4e5f\u6ca1\u6709\u7b54\u5e94\u3002\u201d \u3000\u3000\u4f55\u843c\u534e\u53eb\u9053\uff1a\u201c\u53c8\u662f\u767d\u53d1\u9b54\u5973\uff01\u6211\u544a\u8bc9\u4f60\u4eec\uff0c\u767d\u53d1\u9b54\u5973\u662f\u6211\u4eec\u6b66\u5f53\u6d3e\u7684\u4ec7\u4eba\uff0c\u4f60\u4eec\u7684\u5821 \u4e3b\u4e3a\u4f55\u53cd\u800c\u627e\u5230\u6211\u4eec\u6b66\u5f53\u6d3e\u7684\u5934\u4e0a\uff1f\u201d\u90a3\u4eba\u7b11\u9053\uff1a\u201c\u6211\u4eec\u5821\u4e3b\u5df2\u7ecf\u77e5\u9053\uff0c\u767d\u53d1\u9b54\u5973\u53c8\u53eb\u505a\u7389 \u7f57\u5239\uff0c\u5353\u76f8\u516c\u5c31\u662f\u56e0\u5979\u624d\u4f1a\u5230\u585e\u5916\u6765\u7684\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u9762\u4e0a\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u7684\u5821\u4e3b\u662f\u56e0\u5979\u800c\u8fde\u53ca\u6211\u5417\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u4e5f\u4e0d\u5c3d\u662f\u5982\u6b64\u3002 \u4eca\u5e74\u6625\u5929\uff0c\u970d\u5143\u4ef2\u6765\u5230\u5821\u4e2d\uff0c\u529d\u6211\u4eec\u5821\u4e3b\u91cd\u7acb\u9999\u5802\uff0c\u79f0\u96c4\u585e\u5916\u3002\u897f\u85cf\u5929\u9f99\u6d3e\u7684\u4eba\u66f4\u613f\u5e2e\u6211\u4eec \u5821\u4e3b\u5728\u585e\u5916\u79f0\u738b\u3002\u542c\u8bf4\u56e0\u4e3a\u5929\u9f99\u6d3e\u7684\u4eba\u66fe\u88ab\u5353\u76f8\u516c\u6240\u6740\uff0c\u53c8\u88ab\u54c8\u8428\u514b\u4eba\u9a71\u9010\uff0c\u6240\u4ee5\u5929\u9f99\u6d3e\u6559 \u4e3b\u613f\u52a9\u5580\u8fbe\u5c14\u65cf\u7684\u914b\u957f\u548c\u6211\u4eec\u5821\u4e3b\u5408\u4f5c\uff0c\u5728\u6c99\u6f20\u8349\u539f\u4e4b\u4e0a\uff0c\u636e\u5730\u5c01\u738b\u3002\u540c\u65f6\u5929\u9f99\u6d3e\u7684\u4eba\u4e5f\u66fe \u5403\u8fc7\u767d\u53d1\u9b54\u5973\u7684\u4e8f\uff0c\u56e0\u6b64\uff0c\u5929\u9f99\u4e0a\u4eba\u4e5f\u613f\u4e0e\u8349\u539f\u6c99\u6f20\u82f1\u96c4\u8c6a\u6770\uff0c\u8054\u624b\u6297\u5979\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u5c82\u4e0d\u662f\u53d8\u6210\u4e86\u897f\u85cf\u56de\u7586\u4e24\u5730\u7684\u597d\u624b\u90fd\u6765\u5bf9\u4ed8\u6211\u4eec \u4e86\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u662f\u5440\uff0c\u6211\u4eec\u7684\u5821\u4e3b\u8fd8\u6015\u654c\u4e0d\u8fc7\u81ea\u53d1\u9b54\u5973\uff0c\u6240\u4ee5\u5230\u5904\u9080\u96c6\u597d\u624b\uff0c\u6211\u4eec\u5c31\u662f\u4ed6 \u6d3e\u5230\u5317\u7586\u53bb\u8bf7\u4eba\u7684\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u4eec\u53c8\u4e3a\u4ec0\u9ebd\u53c8\u6765\u544a\u8bc9\u65bc\u6211\uff1f\u201d \u3000\u3000\u90a3\u4eba\u9053\uff1a\u201c\u6211\u4eec\u65e5\u5b50\u8fc7\u5f97\u4e0d\u9519\uff0c\u6211\u4eec\u4e5f\u4e0d\u613f\u5821\u4e3b\u5927\u52a8\u5e72\u6208\uff0c\u542c\u8bf4\u90a3\u767d\u53d1\u9b54\u5973\u5341\u5206\u5389\u5bb3\uff0c \u82e5\u7136\u4e24\u8d25\u4ff1\u4f24\uff0c\u5982\u4f55\u662f\u597d\uff1f\u800c\u4e14\u5353\u76f8\u516c\u4e3a\u4eba\u5982\u6b64\u4e4b\u597d\uff0c\u660e\u77e5\u6211\u4eec\u60f3\u5077\u9a86\u9a7c\uff0c\u4e5f\u613f\u6536\u5bb9\uff0c\u6211\u4eec \u53c8\u600e\u5fcd\u76f8\u516c\u8d74\u9669\u3002\u201d \u3000\u3000\u4f55\u843c\u534e\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f55\u4ee5\u4f60\u4eec\u521a\u624d\u53c8\u4e0d\u8bf4\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u90a3\u65f6\u6211\u8fd8\u4e0d\u77e5\u9053\u5c31\u662f\u5353\u76f8 \u516c\uff0c\u5f8c\u6765\u78b0\u5230\u526f\u5821\u4e3b\u548c\u970d\u5143\u4ef2\uff0c\u6211\u4eec\u8bf4\u8d77\u6709\u8fd9\u9ebd\u4e00\u4e2a\u201c\u5f02\u4eba\u201d\uff0c\u970d\u5143\u4ef2\u7acb\u523b\u731c\u51fa\u662f\u5353\u76f8\u516c\u3002 \u970d\u5143\u4ef2\u597d\u50cf\u5f88\u719f\u6089\u4f60\u4eec\u2026\u2026\u201d\u5353\u4e00\u822a\u63d2\u53e3\u9053\uff1a\u201c\u7389\u7f57\u5239\u548c\u6211\u90fd\u66fe\u4e0e\u4ed6\u4ea4\u8fc7\u624b\u3002\u201d\u90a3\u4eba\u9053\uff1a \u201c\u602a\u4e0d\u5f97\u3002\u767d\u53d1\u9b54\u5973\u53c8\u540d\u7389\u7f57\u5239\u4e5f\u662f\u4ed6\u8bf4\u7684\u3002\u8bb8\u591a\u4eba\u90fd\u4e0d\u77e5\u5462\u3002\u201d \u3000\u3000\u90a3\u4eba\u7eed\u9053\uff1a\u201c\u5f8c\u6765\u4ed6\u4eec\u53c1\u4eba\u5c31\u6765\u627e\u4f60\u3002\u4ed6\u4eec\u672c\u6765\u662f\u5821\u4e3b\u8bf7\u6765\u63a2\u542c\u4f60\u4eec\u884c\u8e2a\u7684\u3002\u201d\u5353\u4e00\u822a \u9053\uff1a\u201c\u6162\u7740\uff0c\u90a3\u4e00\u4e2a\u662f\u526f\u5821\u4e3b\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\u6211\u4eec\u7684\u526f\u5821\u4e3b\u662f\u70b9\u7a74\u540d\u5bb6\u2026\u2026\u201d\u5353\u4e00\u822a\u9053\uff1a \u201c\u54e6\uff0c\u90a3\u4e0d\u7528\u8bf4\u4e86\uff0c\u4ed6\u662f\u4f7f\u5224\u5b98\u7b14\u7684\u3002\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u53c8\u662f\u8c01\uff1f\u201d\u90a3\u4eba\u9053\uff1a\u201c\uff0e\u542c \u8bf4\u662f\u4ee5\u524d\u79f0\u96c4\u897f\u5317\u7684\u201d\u9634\u98ce\u6bd2\u7802\u638c\u201d\u91d1\u72ec\u5f02\u7684\u4e00\u4e2a\u95e8\u4eba\u3002\u91d1\u72ec\u5f02\u7684\u95e8\u4eba\u5f88\u591a\uff0c\u4ed6\u6b7b\u4e86\u4e4b\u5f8c\uff0c \u6709\u4e9b\u95e8\u4eba\u8d70\u5230\u585e\u5916\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u6211\u5bf9\u4ed6\u7684\u638c\u6cd5\u4f3c\u66fe\u76f8\u8bc6\u3002\u201d\u4f55\u843c\u534e\u53c8\u95ee\u9053\uff1a\u201c\u90a3 \u9ebd\u767d\u77f3\u9053\u4eba\u4f60\u77e5\u9053\u5417\uff1f\u201d\u90a3\u4eba\u6447\u64ad\u5934\u9053\uff1a\u201c\u6ca1\u542c\u8bf4\u8fc7\uff0c\u4e0d\u8fc7\u524d\u51e0\u5929\uff0c\u5929\u9f99\u6d3e\u7684\u5587\u561b\u6765\u4e86\u4e00\u5927 \u6279\uff0c\u6709\u4eba\u8bf4\u5939\u6709\u4e00\u4e2a\u9053\u58eb\u5728\u5185\uff0c\u4e5f\u8bb8\u5c31\u662f\u4f60\u6240\u8bf4\u7684\u90a3\u4e2a\u767d\u77f3\u9053\u4eba\u4e5f\u672a\u53ef\u77e5\u3002\u201d\u4f55\u843c\u534e\u8df3\u4e86\u8d77 \u6765\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u7684\u5821\u4e3b\u6ca1\u53d1\u8bf7\u5e16\u7ed9\u6211\u3002\u6211\u4e5f\u8981\u53bb\u4e86\u3002\u5582\uff0c\u4eca\u65e5\u662f\u4ec0\u9ebd\u65e5\u5b50\uff1f\u5728\u5927\u6f20\u4e4b\u4e2d\uff0c\u89c1 \u65e5\u8d77\u65e5\u843d\uff0c\u65f6\u8282\u65e5\u5b50\u90fd\u5fd8\u8bb0\u4e86\u3002\u201d\u90a3\u4eba\u9053\uff1a\u201c\u4eca\u65e5\u662f\u4e03\u6708\u521d\u56db\uff0c\u4e03\u5915\u4e4b\u671f\uff0c\u4fbf\u662f\u6211\u4eec\u5821\u4e3b\u91cd \u7acb\u9999\u5802\u7684\u65e5\u5b50\u3002\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u8fd9\u91cc\u79bb\u98ce\u7802\u5821\u8fd8\u6709\u591a\u8fdc\uff1f\u201d\u90a3\u4eba\u60f3\u4e86\u4e00\u60f3\uff0c\u5ffd\u7b11\u9053\uff1a\u201c\u5982\u679c \u4f60\u4eec\u662f\u8d3a\u5ba2\uff0c\u53ef\u4ee5\u521a\u597d\u5728\u4e03\u5915\u4e4b\u671f\u8d76\u5230\u3002\u201d\u5353\u4e00\u822a\u7b11\u9053\uff1a\u201c\u6211\u4eec\u5c31\u662f\u8981\u53bb\u9053\u8d3a\u3002\u201d \u3000\u3000\u90a3\u4eba\u6025\u9053\uff1a\u201c\u5353\u76f8\u516c\u8fd8\u662f\u4e0d\u53bb\u7684\u597d\u3002\u6211\u8fd8\u60f3\u8bf7\u5353\u76f8\u516c\u529d\u90a3\u767d\u53d1\u9b54\u5973\u4e5f\u4e0d\u8981\u53bb\u3002\u4e24\u864e\u76f8 \u6597\uff0c\u5fc5\u6709\u4e00\u4f24\u3002\u4f24\u4e86\u5353\u76f8\u516c\u56fa\u7136\u4e0d\u597d\uff0c\u4f24\u4e86\u6211\u4eec\u7684\u5821\u4e3b\u4e5f\u4e0d\u597d\u3002\u201d\u5353\u4e00\u822a\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\u3002 \u6211\u4eec\u81ea\u6709\u4e3b\u610f\u3002\u4f60\u4eec\u7684\u5821\u4e3b\u65e2\u7136\u8981\u4f60\u4eec\u53bb\u8bf7\u4eba\uff0c\u4f60\u4eec\u5c31\u5feb\u8d70\u5427\u3002\u201d\u90a3\u4e24\u4eba\u544a\u8f9e\u4e4b\u5f8c\uff0c\u4f55\u843c\u534e \u5ffd\u7136\u62cd\u638c\u8bf4\u9053\uff1a\u201c\u771f\u662f\u610f\u60f3\u4e0d\u5230\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u6115\u7136\u95ee\u9053\uff1a\u201c\u4ec0\u9ebd\u610f\u60f3\u4e0d\u5230\uff1f\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u770b\u8fd9\u4e24\u4eba\u9762\u751f\u6a2a\u8089\uff0c\u5374\u4e5f\u77e5\u6069\u5584 \u62a5\u3002\u55ef\uff0c\u5927\u54e5\u8fd9\u6c99\u6f20\u4e4b\u5730\uff0c\u5982\u4f55\u627e\u5f97\u5341\u5927\u9505\u6c34\u3002\u201d\u5353\u4e00\u822a\u77e5\u5979\u8bb0\u6302\u81ea\u5df1\u6240\u53d7\u7684\u6bd2\u7802\u638c\u4f24\uff0c\u7b11 \u9053\uff1a\u201c\u8fd9\u4e2a\u5bb9\u6613\uff0c\u4f60\u542c\u6211\u8bf4\u2026\u2026\u201d\u5ffd\u7136\u8e59\u4e86\u53cc\u7709\uff0c\u8bf4\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u539f\u6765\u5353\u4e00\u822a\u9002\u624d\u81ea\u5fd6\uff0c\u4ee5\u81ea\u5df1\u73b0\u5728\u7684\u5185\u529f\u9020\u8be3\uff0c\u5927\u53ef\u4e0d\u5fc5\u5229\u7528\u6c34\u6c14\u4e4b\u529b\uff0c\u51ed\u201c\u7384\u529f\u5185 \u8fd0\u201d\uff0c\u4e5f\u53ef\u5c06\u4f53\u5185\u7684\u6bd2\u81ea\u5df2\u8feb\u53d1\u51fa\u6765\u3002\u53ef\u662f\u518d\u4ed4\u7ec6\u4e00\u60f3\uff1a\u5728\u7384\u529f\u5185\u8fd0\u4e4b\u65f6\uff0c\u81ea\u5df1\u4e00\u52a8\u4e5f\u4e0d\u80fd \u52a8\uff0c\u8fd9\u65f6\u9700\u8981\u6709\u4eba\u7ed9\u81ea\u5df1\u63a8\u63c9\u7a74\u9053\uff0c\u82e5\u662f\u7537\u4eba\uff0c\u90a3\u8fd8\u7f62\u4e86\uff0c\u504f\u504f\u4f55\u843c\u534e\u5374\u662f\u5973\u5b50\uff1a\u82e5\u4f55\u843c\u534e \u529f\u529b\u6781\u6df1\uff0c\u90a3\u9ebd\u9694\u8863\u8ba4\u7a74\u63a8\u63c9\uff0c\u90a3\u4e5f\u8fd8\u53ef\u4ee5\uff0c\u504f\u504f\u5979\u529f\u529b\u5c1a\u6d45\uff0c\u5fc5\u987b\u8131\u4e86\u4e0a\u8863\uff0c\u8ba9\u5979\u4eb2\u63a5\u808c \u80a4\u3002 \u3000\u3000\u4f55\u843c\u534e\u4e0d\u77e5\u6240\u4ee5\uff0c\u89c1\u4ed6\u53cc\u7709\u7d27\u8e59\uff0c\u4e0d\u89c9\u614c\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u4e3a\u6211\u53d7\u4e86\u8fd9\u4f24\uff0c\uff0e\u6211\u5374 \u65e0\u6cd5\u76f8\u6551\uff0c\u5982\u4f55\u662f\u597d\u4e86\u5927\u54e5\uff0c\u6211\u9760\u4f60\u53bb\u627e\u7239\u7239\uff0c\u5927\u5f8c\u5929\u4f7f\u662f\u4e03\u5915\uff0c\u4f60\u7684\u4f24\uff0c\u8fd9\uff0c\u8fd9\u600e\u9ebd \u529e\uff1f\u201d\u5353\u4e00\u822a\u5fc3\u9053\uff1a\u4e8b\u6025\u4ece\u6743\uff0c\u4e0d\u80fd\u987e\u8651\u8fd9\u9ebd\u591a\u4e86\u3002\u4f55\u843c\u534e\u6cea\u76c8\u53cc\u776b\uff0c\u4e0a\u524d\u62c9\u5353\u4e00\u822a\uff0c\u5353\u4e00 \u822a\u9053\uff1a\u201c\u6bd2\u7802\u638c\u7b97\u4e0d\u4e86\u4ec0\u9ebd\uff0c\u53ea\u662f\u8981\u4f60\u5e2e\u5fd9\u3002\u201d\u4f55\u843c\u534e\u9053\uff1a\u201c\u600e\u6837\u5e2e\u5fd9\uff1f\u201d\u5353\u4e00\u822a\u5c06\u65b9\u6cd5\u8bf4 \u4e86\uff0c\u5e76\u6559\u5979\u600e\u6837\u63a8\u63c9\u7a74\u9053\u3002\u4f55\u843c\u534e\u7834\u6d95\u4e3a\u7b11\uff0c\u683c\u683c\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u4e2a\u4eba\u771f\u602a\uff0c\u65e2\u7136\u5982\u6b64\u5bb9\u6613\uff0c \u4f55\u4e0d\u65e9\u8bf4\uff1f\u5feb\u76d8\u819d\u5750\u4e0b\u3002\u201d\u5353\u4e00\u822a\u89e3\u4e86\u4e0a\u8863\uff0c\u8c03\u597d\u547c\u5438\uff0c\u773c\u89c2\u9f3b\uff0c\u9f3b\u89c2\u5fc3\uff0c\u6709\u5982\u8001\u50e7\u4eba\u5b9a\u3002 \u4f55\u843c\u534e\u66ff\u4ed6\u63a8\u63c9\u7a74\u9053\uff0c\u52a9\u4ed6\u53d1\u6563\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\u53ea\u89c1\u5353\u4e00\u822a\u6ee1\u8eab\u70ed\u6c14\u817e\u817e\uff0c\u7741\u773c\u8bf4\u9053\uff1a\u201c\u884c \u4e86\uff0c\u53ea\u662f\u70ed\u5f97\u96be\u53d7\u3002\u201d\u4f55\u843c\u534e\u62c9\u5f00\u5e10\u7bf7\u4e00\u89d2\uff0c\u8ba9\u51b7\u98ce\u5439\u8fdb\uff0c\u9053\uff1a\u201c\u6b47\u4f1a\u513f\u4f60\u518d\u7a7f\u4e0a\u8863\u670d\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5353\u4e00\u822a\u8fd0\u529f\u5df2\u6bd5\uff0c\u70ed\u5f97\u76f4\u5598\u6c14\u3002\u4f55\u7eff\u534e\u5fc3\u60f3\uff1a\u4e0d\u5982\u9017\u4ed6\u8bf4\u8bdd\uff0c\u8ba9\u4ed6\u5206\u5fc3\uff0c\u90a3\u5c31\u6ca1\u6709 \u8fd9\u6837\u70ed\u4e86\u3002\u65bc\u662f\u95ee\u9053\uff1a\u201c\u4f60\u548c\u7389\u7f57\u5239\u5f88\u8981\u597d\u5417\uff1f\u201d\u5353\u4e00\u822a\u201c\u5514\u201d\u4e86\u4e00\u58f0\uff0c\u4f3c\u7b54\u975e\u7b54\u3002\u4f55\u7eff\u534e \u6545\u610f\u9017\u4ed6\u9053\uff1b\u201c\u6211\u4e0d\u4fe1\uff0c\u4f60\u4eec\u600e\u4f1a\u597d\u5f97\u8d77\u6765\uff1f\u201d\u5353\u4e00\u822a\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u9053\uff1a\u7537\u5973\u4e4b\u60c5\uff0c\u5947\u5999\u65e0 \u6bd4\uff0c\u4f60\u8fd8\u662f\u4e2a\u9ec4\u6bdb\u5c0f\u4e2b\u5934\uff0c\u5982\u4f55\u61c2\u5f97\uff1f\u4f55\u843c\u534e\u7eed\u9053\uff1a\u201c\u7389\u7f57\u5239\u559c\u6b22\u6253\u67b6\uff0c\u662f\u5417\uff1f\u201d\u5353\u4e00\u822a\u70b9 \u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u82e5\u4e0d\u662f\u5979\u6b22\u559c\u627e\u4eba\u6bd4\u8bd5\uff0c\u4e5f\u4e0d\u81f4\u60f9\u51fa\u8fd9\u9ebd\u591a\u9ebb\u70e6\u4e86\u3002\u201d\u4f55\u843c\u534e\u53c8\u9053\uff1a\u201c\u4f60\u4e0d \u6b22\u559c\u6253\u67b6\uff0c\u662f\u5417\uff1f\u201d\u5353\u4e00\u822a\u53c8\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u4f55\u843c\u534e\u683c\u683c\u7b11\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u5417\uff1f\u4f60\u4eec\u4e24\u4eba\u6027\u5b50\u6839\u672c\u4e0d\u540c\u3002\u5979\u662f\u6709\u540d\u7684\u201c\u9b54\u5973\u201d\uff0c\u4f60\u5374\u50cf \u4e2a\u6587\u96c5\u7684\u4e66\u751f\u3002\u602a\u4e0d\u5f97\u5979\u548c\u4f60\u95f9\u7ffb\uff0c\u672c\u5c31\u5408\u4e0d\u8d77\u6765\u561b\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u6014\u4e86\u4e00\u6014\uff0c\u8fd9\u8bdd\u4e5f\u8bf4\u5f97\u6709\u51e0\u5206\u9053\u7406\u3002\u53c8\u6015\u5979\u53e3\u6ca1\u906e\u62e6\uff0c\u88ab\u7389\u7f57\u5239\u6697\u4e2d\u542c\u89c1\uff0c\u5fc3\u4e2d \u4e00\u70e6\uff0c\u70ed\u6c14\u66f4\u5192\u3002\u6025\u9053\uff1a\u201c\u4e0d\u8981\u518d\u63d0\u7389\u7f57\u5239\u4e86\uff0c\u597d\u5417\uff1f\u201d\u4f55\u843c\u534e\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u9ebd\u6211\u62c9 \u80e1\u7434\u5531\u7ed9\u4f60\u542c\uff0c\u6211\u7239\u7239\u5fc3\u70e6\u7684\u65f6\u5019\uff0c\u4e5f\u662f\u559c\u6b22\u542c\u6211\u5531\u6b4c\u7684\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u5fc3\u9053\uff1a\u53ea\u8981\u4f60\u4e0d\u80e1\u8a00\u4e71\u8bed\uff0c\u5531\u4ec0\u9ebd\u90fd\u597d\u3002\u4fbf\u70b9\u4e86\u70b9\u5934\u3002\u4f55\u843c\u534e\u62ff\u51fa\u54c8\u8428\u514b\u4eba\u9001\u5979 \u7684\u90a3\u628a\u80e1\u7434\u7f20\u95ee\u5353\u4e00\u822a\u559c\u542c\u4ec0\u9ebd\uff1f\u5353\u4e00\u822a\u9053\uff1a\u201c\u4f60\u5c31\u5531\u4e00\u652f\u6b22\u5feb\u7684\u6c5f\u5357\u5c0f\u8c03\u5427\u3002\u201d \u3000\u3000\u4f55\u843c\u534e\u7406\u597d\u7434\u5f26\uff0c\u8fb9\u62c9\u8fb9\u5531\u9053\uff1a \u3000\u3000\u83ab\u4e0d\u662f\u96ea\u7a97\u8424\u706b\u65e0\u95f2\u6687\uff0c\u83ab\u4e0d\u662f\u5356\u98ce\u6d41\u5bbf\u67f3\u7720\u82b1\uff1f\u83ab\u4e0d\u662f\u8ba2\u5e7d\u671f\uff1a\u9519\u8bb0\u4e86\u8336\u863c\u67b6\uff1f\u83ab\u4e0d \u662f\u8f7b\u821f\u9a8f\u9a6c\uff0c\u8fdc\u53bb\u5929\u6daf\uff1f\u83ab\u4e0d\u662f\u62db\u6447\u8bd7\u9152\uff0c\u9189\u5012\u8c01\u5bb6\uff1f\u83ab\u4e0d\u662f\u7b11\u8c08\u95f4\u607c\u7740\u4ed6\uff1f\u83ab\u4e0d\u662f\u6015\u6696\u55d4 \u5bd2\uff0c\u75c5\u75c7\u513f\u52a0\uff1f\u4e07\u79cd\u5343\u6761\u597d\u6559\u6211\u7591\u5fc3\u513f\u653e\u4e0d\u4e0b\uff01 \u3000\u3000\u8fd9\u8c03\u5b50\u672c\u662f\u6c5f\u5357\u4e00\u5e26\u7684\u6b4c\u5993\u4ece\u201c\u897f\u53a2\u8bb0\u201d\u7684\u66f2\u8c03\u53d8\u5316\u51fa\u6765\u7684\uff0c\u63cf\u5199\u5f20\u751f\u8fdc\u53bb\u4e4b\u5f8c\uff0c\u4e45\u4e45 \u4e0d\u5f52\uff0c\u83ba\u83ba\u60e6\u8bb0\u4e4b\u60c5\u3002\u53ea\u56e0\u6587\u8bcd\u6d3b\u6cfc\u98ce\u96c5\uff0c\u6545\u6b64\u6d41\u4f20\u6c11\u95f4\uff0c\u5927\u5bb6\u95fa\u79c0\u4e5f\u6b22\u559c\u5531\u3002\u4f55\u843c\u534e\u89c1\u4ed6 \u8bf4\u6b22\u559c\u6b22\u5feb\u7684\u8c03\u5b50\uff0c\u4fbf\u968f\u53e3\u5531\u4e86\u51fa\u6765\u3002\u5353\u4e00\u822a\u5999\u89e3\u97f3\u5f8b\uff0c\u4e0d\u89c9\u8f7b\u8f7b\u53eb\u4e86\u58f0\uff1a\u201c\u7ec3\u59d0\u59d0\u3002\u201d \u3000\u3000\u4f55\u843c\u534e\u4e0d\u7981\u5657\u55e4\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d\u63d0\u7389\u7f57\u5239\uff0c\u4f60\u81ea\u5df1\u53c8\u63d0\u4e86\uff1f\u5582\uff0c\u542c\u8bf4\u7389\u7f57\u5239\u7f8e\u82e6\u5929 \u4ed9\uff0c\u53ef\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u5fc3\u9053\uff1a\u201c\u7537\u5973\u4e4b\u60c5\uff0c\u5c82\u662f\u56e0\u5bb9\u8c8c\u76f8\u60a6\u800c\u8d77\uff1f\u201d\u4fbf\u9053\uff1a\u201c\u5979\u73b0\u5728\u767d\u53d1\u6ee1\u5934\uff0c\u5bb9\u989c\u975e \u6614\uff0c\u8981\u8bf4\u7f8e\u5417\uff1f\u5979\u53ef\u8fd8\u6bd4\u4e0d\u4e0a\u4f60\uff0c\u53ef\u662f\u2026\u2026\u201d\u6b63\u60f3\u89e3\u8bf4\u4e3a\u4ec0\u9ebd\u7eb5\u4f7f\u7389\u7f57\u5239\u53c8\u8001\u53c8\u4e11\uff0c\u81ea\u5df2\u4e5f \u8fd8\u559c\u6b22\u5979\u7684\u9053\u7406\u3002\u5ffd\u542c\u5f97\u4e00\u58f0\u957f\u7b11\uff0c\u8106\u82e5\u94f6\u94c3\uff0c\u5e10\u7bf7\u4e0a\u55e4\u7684\u4e00\u54cd\uff0c\u7389\u7f57\u5239\u5272\u5f00\u4e00\u4e2a\u88c2\u53e3\uff0c\u8df3 \u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5353\u4e00\u822a\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u9119\uff0c\u201c\u7ec3\u59d0\u59d0\u201d\u53c1\u5b57\u60f3\u53eb\u5374\u672a\u53eb\u5f97\u51fa\u6765\uff0c\u53ea\u89c1\u5979\u94f6\u4e1d\u8986\u989d\uff0c\u5bb9\u5149\u4ecd \u4f3c\u5c11\u5973\uff0c\u67f3\u7709\u4e00\u7ad6\uff0c\u773c\u5982\u5229\u526a\uff0c\u6a2a\u626b\u4e86\u4f55\u843c\u534e\u4e00\u773c\uff0c\u5374\u4ecd\u662f\u7b11\u541f\u541f\u7684\u9053\uff1a\u201c\u597d\u4fca\u7684\u4eba\u513f\uff0c\u597d \u7f8e\u7684\u7434\u58f0\uff0c\u4e3a\u4ec0\u9ebd\u4e0d\u5f39\u4e0b\u53bb\uff1f\u201d\u5353\u4e00\u822a\u6025\u9053\uff1a\u201c\u8fd9\u4e0d\u5173\u5979\u7684\u4e8b\uff0c\u662f\u6211\uff0c\u662f\u6211\u2026\u2026\u201d\u6b63\u60f3\u8bf4 \u201c\u662f\u56e0\u6211\u53d7\u4e86\u6bd2\u7802\u638c\uff0c\u5979\u66ff\u6211\u6cbb\u3002\u201d\u90a3\u77e5\u8fd9\u9ebd\u4e00\u8bf4\uff0c\u8bef\u4f1a\u66f4\u589e\uff0c\u7389\u7f57\u5239\u4e00\u58f0\u51b7\u7b11\u9053\uff1a\u201c\u662f \u4f60\uff0c\u4f60\u597d\u5440\uff01\u201d\u55d6\u7684\u4e00\u58f0\uff0c\u62d4\u51fa\u4f69\u5251\uff0c\u671d\u5353\u4e00\u822a\u5206\u5fc3\u4fbf\u523a\u3002 \u3000\u3000\u539f\u6765\u5353\u4e00\u822a\u6f2b\u6e38\u8349\u539f\u7684\u65f6\u5019\uff0c\u5979\u5df2\u5230\u6155\u58eb\u5854\u683c\u5c71\u7684\u9a7c\u8702\u770b\u8fc7\u8f9b\u9f99\u5b50\u5b88\u62a4\u7684\u4ed9\u82b1\uff0c\u867d\u77e5\u8fd9 \u4ed9\u82b1\u8981\u51e0\u5341\u5e74\u5f8c\u624d\u5f00\uff0c\u53ef\u4e5f\u611f\u5ff5\u5353\u4e00\u822a\u610f\u5ff5\u4e4b\u8bda\uff0c\u56e0\u6b64\u4e5f\u5230\u8349\u539f\u8ffd\u8e2a\uff0c\u4e0d\u6599\u4eca\u665a\u76f8\u89c1\uff0c\u5374\u521a \u597d\u89c1\u5230\u4ed6\u8d64\u88f8\u4e0a\u8eab\uff0c\u542c\u4f55\u843c\u534e\u62c9\u7434\uff1a\u53c8\u542c\u5230\u4ed6\u548c\u4f55\u843c\u534e\u8c08\u8bba\u81ea\u5df2\u7684\u5bb9\u8c8c\uff0c\u8fd9\u4e00\u4e0b\u7231\u610f\u53cd\u6210\u6012 \u6c14\uff0c\u6068\u6781\u6c14\u6781\uff0c\u4e0d\u7531\u5f97\u62d4\u5251\u51fa\u9798\u3002 \u3000\u3000\u4f55\u843c\u534e\u60ca\u53eb\u9053\uff1a\u201c\u7389\u7f57\u5239\uff0c\u4f60\u8fd9\u662f\u5e72\u4ec0\u9ebd\uff1f\u4f60\u6740\u4e86\u4ed6\uff0c\u6ca1\u4eba\u6551\u6211\u7684\u7239\uff0c\u6211\u53ef\u8981\u548c\u4f60 \u62da\u3002\u201d\u62d4\u5251\u95ef\u4e0a\u3002 \u3000\u3000\u5353\u4e00\u822a\u8fc8\u4e0a\u4e00\u6b65\uff0c\u633a\u80f8\u8fce\u63a5\u5251\u5c16\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u80fd\u6b7b\u5728\u4f60\u7684\u5251\u4e0b\uff0c\u5728\u6211\u662f\u6c42\u4e4b\u4e0d \u5f97\uff01\u539f\u6765\u4f60\u7231\u6211\u8fd8\u662f\u5982\u6b64\u4e4b\u6df1\uff01\u201d\u7389\u7f57\u5239\u9762\u8272\u4e00\u53d8\uff0c\u6025\u5fd9\u7f29\u624b\uff0c\u4f55\u843c\u534e\u5251\u5230\u5f8c\u5fc3\uff0c\u88ab\u5979\u968f\u624b \u4e00\u64a9\uff0c\u98de\u51fa\u5e10\u5916\u3002 \u3000\u3000\u8fd9\u5239\u90a3\u95f4\uff0c\u7389\u7f57\u5239\u5fc3\u5934\u6d6a\u6d8c\uff0c\u662f\u7231\u662f\u6068\uff0c\u4ea6\u5df2\u96be\u660e\u3002\u5353\u4e00\u822a\u5411\u524d\u4e00\u6251\uff0c\u62c9\u5730\u8863\u89d2\u3002\u7389\u7f57 \u5239\u51c4\u7136\u7b11\u9053\uff1a\u201c\u4f60\u662f\u5b98\u5bb6\u5b50\u5f1f\uff0c\u6b63\u6d3e\u638c\u95e8\uff0c\u62c9\u6211\u8fd9\u4e2a\u8349\u91ce\u5973\u5b50\u505a\u4ec0\u9ebd\uff0c\u4f60\u968f\u5979\u56de\u6b66\u5f53\u5c71\u53bb \u5427\uff01\u201d\u8f7b\u8f7b\u4e00\u8df3\uff0c\u5353\u4e00\u822a\u6251\u4e86\u4e2a\u7a7a\uff0c\u7389\u7f57\u5239\u7684\u5f71\u5b50\u53c8\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u5353\u4e00\u822a\u9893\u7136\u8dcc\u5012\uff0c\u4f55\u843c\u534e\u83ab\u540d\u5176\u5999\uff0c\u9053\uff1a\u201c\u54a6\uff0c\u7389\u7f57\u5239\u600e\u9ebd\u8fd9\u6837\u5927\u7684\u813e\u6c14\u554a\uff01\u201d\u5979\u5929\u771f \u65e0\u90aa\uff0c\u7adf\u662f\u8fde\u60f3\u4e5f\u60f3\u4e0d\u5230\u7389\u7f57\u5239\u4f1a\u5403\u5979\u7684\u918b\u3002\u6b63\u662f\uff1a\u7434\u58f0\u98de\u5927\u6f20\uff0c\u5f26\u8005\u500d\u5173\u60c5\u3002\u6b32\u77e5\u5f8c\u4e8b\u5982 \u4f55\uff1f\u8bf7\u770b\u4e0b\u56de\u5206\u89e3\u3002  \u6f47\u6e58\u4e66\u9662\xb7\u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377\u2014\u2014\u7b2c\u4e09\u5341\u4e8c\u56de\u3000\u6f20\u6f20\u9ec4\u6c99\u3000\u57cb\u60c5\u4f24\u53ea\u5f71\u3000\u8fe2\u8fe2\u94f6\u6c49\u3000\u4f20\u6068\u76fc\u53cc\u661f \u6881\u7fbd\u751f\u300a\u767d\u53d1\u9b54\u5973\u4f20\u300b\u4e0b\u5377 \u7b2c\u4e09\u5341\u4e8c\u56de \u6f20\u6f20\u9ec4\u6c99\u3000\u57cb\u60c5\u4f24\u53ea\u5f71 \u8fe2\u8fe2\u94f6\u6c49\u3000\u4f20\u6068\u76fc\u53cc\u661f \u3000\u3000\u53c1\u65e5\u4e4b\u5f8c\uff0c\u5df2\u662f\u4e03\u5de7\u4e4b\u671f\u3002\u98ce\u6c99\u5821\u4e2d\uff0c\u7fa4\u8c6a\u96c6\u805a\uff0c\u9f99\u86c7\u6df7\u6742\u3002\u6709\u5929\u9f99\u4e0a\u4eba\u548c\u4ed6\u95e8\u4e0b\u5f1f \u5b50\uff1a\u4e5f\u6709\u5929\u5c71\u5357\u5317\u7684\u5404\u8def\u82f1\u96c4\u3002\u5821\u4e3b\u6210\u7ae0\u4e94\u62e3\u8fd9\u65e5\u91cd\u7acb\u9999\u5802\uff0c\u610f\u56fe\u5728\u585e\u5916\u518d\u5e72\u4e0b\u4e00\u756a\u4e8b\u4e1a\u3002 \u3000\u3000\u5178\u793c\u8fc7\u5f8c\uff0c\u5df2\u8fd1\u9ec4\u660f\uff0c\u5821\u5916\u98ce\u6c99\u547c\u5578\uff0c\u5821\u4e2d\u5374\u548c\u6696\u5982\u6625\u3002\u6210\u7ae0\u4e94\u970d\u5143\u4ef2\u5174\u54c8\u8428\u514b\u540d\u6b66\u5e08 \u9686\u547c\u56fe\u53ca\u5929\u9f99\u4e0a\u4eba\u95f2\u5750\u5546\u8c08\uff0c\u9686\u547c\u96c5\u56fe\u9053\uff1a\u201c\u6210\u5821\u4e3b\uff0c\u4f60\u5230\u4e86\u8349\u539f\u8fd9\u9ebd\u591a\u5e74\uff0c\u6211\u4eec\u90fd\u5df2\u628a\u4f60 \u5f53\u6210\u81ea\u5df1\u4eba\u4e86\u3002\u6211\u4eec\u5e76\u4e0d\u662f\u4ec7\u89c6\u6c49\u4eba\uff0c\u53ea\u5948\u90a3\u767d\u53d1\u9b54\u5973\u59d4\u5b9e\u6b3a\u4eba\uff0c\u4e0d\u628a\u6211\u4eec\u585e\u5916\u82f1\u8c6a\u653e\u5728\u773c \u5185\uff0c\u8fd9\u53e3\u6c14\u4e0d\u80fd\u4e0d\u5410\u3002\u201d \u3000\u3000\u5929\u9f99\u4e0a\u4eba\u7b11\u9053\uff1a\u201c\u8c05\u90a3\u767d\u53d1\u9b54\u5973\u4e5f\u4e0d\u662f\u53c1\u5934\u516d\u81c2\uff0c\u6211\u4eec\u56db\u4eba\u968f\u4fbf\u4e00\u4e2a\u5df2\u591f\u5979\u6597\u4e86\uff0c\u4f55\u51b5 \u8fd8\u6709\u8bb8\u591a\u597d\u6c49\u4e0e\u5979\u4e3a\u4ec7\u3002\u60f3\u90a3\u767d\u77f3\u9053\u4eba\u4e5f\u66fe\u5938\u8fc7\u6d77\u53e3\u8bf4\u585e\u5916\u6ca1\u6709\u9ad8\u624b\uff0c\u7ed3\u679c\u8fd8\u4e0d\u662f\u88ab\u6211\u4eec\u64d2 \u56de\u6765\u4e86\u3002\u8c05\u90a3\u767d\u53d1\u9b54\u5973\u4e5f\u5389\u5bb3\u4e0d\u5230\u90a3\u91cc\u53bb\u3002\u201d \u3000\u3000\u9686\u547c\u96c5\u56fe\u7b11\u9053\uff1a\u201c\u6210\u5821\u4e3b\uff0c\u6b66\u5f53\u638c\u95e8\u82e5\u6765\u8d74\u7ea6\uff0c\u4f60\u5c06\u4ed6\u6253\u5012\uff0c\u53ef\u771f\u662f\u5927\u5927\u9732\u9762\u4e4b\u4e8b\u3002\u201d \u6210\u7ae0\u4e94\u7528\u610f\u4e5f\u662f\u60f3\u8d81\u91cd\u5efa\u9999\u5802\u4e4b\u65e5\uff0c\u6253\u5012\u4e00\u4e2a\u540d\u624b\uff0c\u6811\u7acb\u5a01\u98ce\u3002\u4ed6\u4e4b\u7ea6\u5353\u4e00\u822a\u6bd4\u8bd5\uff0c\u5176\u5b9e\u6b63 \u662f\u56e0\u4e3a\u5353\u4e00\u822a\u4e43\u6b66\u5f53\u6d3e\u638c\u95e8\uff0c\u6b63\u662f\u6311\u6218\u7684\u6700\u7406\u60f3\u4eba\u9009\u3002\u5e76\u975e\u4ed6\u548c\u5353\u4e00\u822a\u6709\u4ec0\u9ebd\u4ec7\u3002 \u3000\u3000\u5929\u9f99\u4e0a\u4eba\u9053\uff1a\u201c\u53ef\u4e0d\u77e5\u4ed6\u6562\u4e0d\u6562\u6765\u3002\u201d\u970d\u5143\u4ef2\u9053\uff1a\u201c\u4ed6\u5e08\u53d4\u5728\u6b64\uff0c\u4e00\u5b9a\u4f1a\u6765\u3002\u5353\u4e00\u822a\u5e76 \u4e0d\u96be\u6597\uff0c\u6210\u5821\u4e3b\u5b9a\u53ef\u64cd\u80dc\u5238\u3002\u6b66\u5f53\u6d3e\u6c14\u9a84\u4eba\uff0c\u5f85\u4f1a\u6210\u5821\u4e3b\u5c06\u5353\u4e00\u822a\u51fb\u5012\u4e4b\u5f8c\uff0c\u54b1\u4eec\u518d\u628a\u767d\u77f3 \u9053\u4eba\u62c9\u51fa\u6765\uff0c\u5404\u8d4f\u4e94\u5341\u76ae\u97ad\uff0c\u5c06\u4ed6\u4eec\u8d76\u51fa\u56de\u7586\uff0c\u597d\u53eb\u5173\u5185\u82f1\u96c4\u4e5f\u540c\u58f0\u4e00\u7b11\u3002\u201d \u3000\u3000\u6210\u7ae0\u4e94\u9053\uff1a\u201c\u970d\u5144\u4e4b\u8a00\uff0c\u751a\u5408\u6211\u5fc3\u3002\u5353\u4e00\u822a\u4e0d\u6bd4\u767d\u53d1\u9b54\u5973\uff0c\u53ef\u4ee5\u9976\u4ed6\u4e00\u547d\u3002\u201d\uff0c \u3000\u3000\u5929\u9f99\u4e0a\u4eba\u9053\uff1a\u201c\u5353\u4e00\u822a\u548c\u6211\u4eec\u53ef\u6709\u70b9\u8fc7\u8282\uff0c\u6210\u5821\u4e3b\u5728\u8d76\u8d70\u4ed6\u4e4b\u524d\uff0c\u6211\u53ef\u8fd8\u8981\u548c\u4ed6\u8c08 \u8bba\u3002\u201d \u3000\u3000\u9ec4\u660f\u65e5\u843d\uff0c\u6210\u7ae0\u4e94\u5728\u5821\u5185\u6446\u4e0b\u7b75\u5e2d\uff0c\u5927\u5bb4\u7fa4\u8c6a\uff0c\u56db\u8fb9\u5899\u58c1\uff0c\u90fd\u63d2\u6709\u7c97\u5982\u4eba\u81c2\u7684\u5927\u725b\u6cb9 \u70db\uff0c\u628a\u573a\u5b50\u7167\u5f97\u901a\u660e\u3002\u4f17\u4eba\u7eb7\u7eb7\u5411\u6210\u7ae0\u4e94\u9053\u8d3a\uff0c\u8c08\u8bba\u5353\u4e00\u822a\u6562\u4e0d\u6562\u6765\u3002 \u3000\u3000\u9152\u8fc7\u53c1\u5de1\uff0c\u5916\u9762\u628a\u95e8\u7684\u5821\u4e01\u8fdb\u6765\uff0c\u732e\u4e0a\u4e00\u5f20\u7280\u725b\u76ae\u5e16\u5b50\uff0c\u4e0a\u9762\u5199\u7740\uff1a\u6b66\u5f53\u6d3e\u95e8\u4e0b\u5f1f\u5b50\u5353 \u4e00\u822a\u7b54\u62dc\u3002\u7280\u725b\u76ae\u6781\u539a\uff0c\u666e\u901a\u7684\u5200\u5b50\u4e5f\u5272\u4e0d\u5f00\uff0c\u90a3\u51e0\u4e2a\u5927\u5b57\u5374\u4e0d\u662f\u7528\u7b14\u5199\u7684\uff0c\u800c\u662f\u7528\u6307\u5934\u5212 \u51fa\u6765\u7684\u3002\u6210\u7ae0\u4e94\u513f\u4e86\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u7acb\u523b\u53eb\u4eba\u5f00\u95e8\u8fce\u63a5\u3002 \u3000\u3000\u4e14\u8bf4\u5353\u4e00\u822a\u867d\u56e0\u60c5\u6d77\u7ffb\u6ce2\uff0c\u4f24\u5fc3\u4e4b\u6781\uff1a\u53ef\u662f\u4e3a\u4e86\u8981\u6551\u5e08\u53d4\uff0c\u4ecd\u7136\u4f9d\u671f\u800c\u6765\uff0c\u6295\u4e0b\u5e16\u5b50\u4e4b \u5f8c\uff0c\u4fbf\u548c\u4f55\u843c\u534e\u5927\u6b65\u8fc8\u8fdb\u3002 \u3000\u3000\u53ea\u89c1\u573a\u5b50\u5806\u6ee1\u4e86\u4eba\uff0c\u6709\u4e00\u7fa4\u5587\u561b\u4e2a\u4e2a\u6012\u76ee\u76f8\u5411\uff1b\u8fd8\u6709\u970d\u5143\u4ef2\u548c\u795e\u5bb6\u5144\u5f1f\u4e5f\u6742\u5728\u4eba\u7fa4\u4e4b \u4e2d\u3002\u5353\u4e00\u822a\u50b2\u7136\u4e0d\u60e7\uff0c\u4f55\u843c\u534e\u4e5f\u795e\u8272\u81ea\u5982\u7d27\u7d27\u8ddf\u968f\u3002 \u3000\u3000\u6210\u7ae0\u4e94\u8d8a\u4f17\u800c\u51fa\uff0c\u9053\uff1a\u201c\u98ce\u6c99\u5821\u4e3b\u6210\u7ae0\u4e94\u656c\u5019\uff0c\u5353\u5148\u751f\u679c\u662f\u4fe1\u4eba\u3002\u8fd9\u4f4d\u5c0f\u59d1\u5a18\u662f\u8c01\uff1f\u201d \u5353\u4e00\u822a\u9053\uff1a\u201c\u5979\u662f\u6211\u767d\u77f3\u5e08\u53d4\u7684\u5973\u513f\u3002\u201d\u4f38\u624b\u4e00\u62c9\uff0c\u5404\u8fd0\u5185\u529b\uff0c\u76f8\u6301\u4e0d\u4e0b\u3002\u6210\u7ae0\u4e94\u54c8\u54c8\u4e00 \u7b11\uff0c\u9053\uff1a\u201c\u8bf7\u5148\u996e\u53c1\u676f\uff01\u201d\u5353\u4e00\u822a\u653e\u5f00\u4e86\u624b\uff0c\u9053\uff1a\u201c\u591a\u8c22\u5821\u4e3b\u76db\u60c5\uff0c\u7f8e\u9152\u6162\u9886\uff0c\u8bf7\u5148\u628a\u6211\u7684 \u5e08\u53d4\u653e\u51fa\u6765\uff01\u201d \u3000\u3000\u6210\u7ae0\u4e94\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u5bb9\u6613\u3002\u96be\u5f97\u6b66\u5f53\u638c\u95e8\u5230\u6b64\uff0c\u6211\u8001\u513f\u53ef\u60f3\u5148\u9886\u6559\u51e0\u62db\u3002\u201d\u5353\u4e00\u822a \u9053\uff1a\u201c\u5821\u4e3b\u662f\u524d\u8f88\u82f1\u96c4\uff0c\u65e2\u8981\u8d50\u6559\uff0c\u5353\u67d0\u5c82\u6562\u63a8\u8f9e\uff1f\u4e0d\u8fc7\u2026\u2026\u201d\u6a2a\u773c\u4e00\u626b\u5168\u573a\uff0c\u9053\uff1a\u201c\u54b1\u4eec \u8fd8\u662f\u5148\u8bb2\u597d\u7684\u597d\uff0c\u6211\u53ef\u548c\u5821\u4e3b\u6253\u4ea4\u9053\uff0c\u8fd9\u9ebd\u591a\u7684\u82f1\u96c4\u597d\u6c49\uff0c\u8bf7\u6055\u6211\u62db\u547c\u4e0d\u5468\u4e86\uff01\u201d\u610f\u601d\u662f\u8981 \u7167\u6b66\u6797\u89c4\u77e9\uff0c\u4ee5\u4e00\u654c\u4e00\uff0c\u5b9a\u4e2a\u5b34\u8f93\u3002 \u3000\u3000\u6210\u7ae0\u4e94\u53c8\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u627f\u638c\u95e8\u8d4f\u9762\uff0c\u77a7\u5f97\u8d77\u6211\uff0c\u8001\u673d\u5b9e\u662f\u60f6\u6127\uff0c\u8fd9\u4e2a\u62dc\u5e16\u2026\u2026\u201d\u8bf4\u5230\u6b64 \u5904\uff0c\u62ff\u8d77\u90a3\u5f20\u7280\u725b\u76ae\uff0c\u5353\u4e00\u822a\u9053\uff1a\u201c\u8352\u6f20\u65c5\u9014\u65e0\u7eb8\u7b14\uff0c\u597d\u730e\u4e86\u4e00\u5934\u7280\u725b\uff0c\u5265\u5b83\u7684\u76ae\uff0c\u6743\u5145\u62dc \u5e16\uff0c\u53eb\u5821\u4e3b\u89c1\u7b11\u4e86\u3002\u201d\u6210\u7ae0\u4e94\u6447\u6447\u624b\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u60f3\u6b66\u5f53\u6d3e\u5a01\u9707\u4e2d\u539f\uff0c\u8001\u673d\u5982\u4f55\u6562 \u6536\u638c\u95e8\u7684\u62dc\u5e16\uff1f\u201d\u968f\u624b\u4e00\u6293\uff0c\u5c06\u90a3\u5f20\u7280\u725b\u76ae\u6293\u5f97\u56db\u5206\u4e94\u88c2\uff0c\u653e\u5728\u638c\u5fc3\u4e00\u6413\uff0c\u518d\u653e\u5f00\u624b\u65f6\uff0c\u90a3 \u5f20\u7280\u725b\u76ae\u7adf\u50cf\u5377\u6210\u4e86\u4e00\u4e2a\u7eb8\u56e2\uff0c\u7ed9\u6210\u7ae0\u4e94\u629b\u51fa\u5f88\u8fdc\u3002\u5353\u4e00\u822a\u609a\u7136\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u8fd9\u8001\u513f\u7684\u9e70\u722a \u529f\u4e5f\u7b97\u5f97\u662f\u4e0a\u4e58\u7684\u4e86\uff0c\u4e0d\u53ef\u8f7b\u654c\u3002 \u3000\u3000\u6210\u7ae0\u4e94\u663e\u4e86\u8fd9\u624b\uff0c\u6b63\u60f3\u4e0b\u573a\uff0c\u4eba\u7fa4\u4e2d\u5ffd\u7136\u95ea\u51fa\u4e00\u4e2a\u5c11\u5973\uff0c\u53eb\u9053\uff1a\u201c\u7239\u7239\uff0c\u5f85\u5973\u513f\u5148\u73a9\u4e00 \u573a\u3002\u4e45\u95fb\u6b66\u5f53\u5251\u6cd5\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u6211\u60f3\u5148\u5411\u8fd9\u4f4d\u59d0\u59d0\u8bf7\u6559\uff0c\u5f00\u5f00\u773c\u754c\u3002\u201d\u8fd9\u5c11\u5973\u6b63\u662f\u6210\u7ae0\u4e94\u7684 \u5973\u513f\uff0c\u540d\u53eb\u6210\u638c\u73e0\u3002 \u3000\u3000\u6210\u7ae0\u4e94\u634b\u987b\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e5f\u597d\u3002\u6211\u4eec\u62db\u5f85\u638c\u95e8\uff0c\u4e5f\u4e0d\u8be5\u51b7\u843d\u4e86\u8fd9\u4f4d\u59d1\u5a18\u3002\u4f60\u5c31\u5411\u5979\u597d\u597d \u8bf7\u6559\u5427 \u3000\u3000\u4f55\u843c\u534e\u4e00\u809a\u5b50\u6c14\uff0c\u89c1\u6210\u638c\u73e0\u6307\u540d\u7d22\u6218\uff0c\u4e5f\u4e0d\u63a8\u8f9e\u3002\u4e24\u4eba\u4e0b\u4e86\u573a\u5b50\uff0c\u4e00\u4e2a\u7528\u5200\uff0c\u4e00\u4e2a\u4f7f \u5251\uff0c\u5bd2\u55a7\u51e0\u53e5\uff0c\u4fbf\u52a8\u8d77\u624b\u6765\u3002\u4e24\u4eba\u90fd\u662f\u5341\u4e03\u516b\u5c81\u7684\u5c0f\u59d1\u5a18\uff0c\u4e00\u4e2a\u767d\u8863\u7ea2\u88d9\uff0c\u4e00\u4e2a\u9752\u8272\u730e\u88c5\uff0c \u7ea2\u767d\u9752\u53c1\u8272\u98de\u626c\uff0c\u4e24\u4e2a\u5c0f\u59d1\u5a18\u50cf\u7c89\u8774\u8776\u822c\u6251\u6765\u6251\u53bb\uff0c\u529f\u592b\u867d\u975e\u4e0a\u4e58\uff0c\u795e\u6001\u5374\u662f\u597d\u770b\u4e4b\u6781\uff1f \u3000\u3000\u4f55\u843c\u534e\u5251\u8d70\u8f7b\u7075\uff0c\u7a7f\u6765\u7ed5\u53bb\uff1b\u6210\u638c\u73e0\u5374\u662f\u5200\u6c89\u529b\u91cd\uff0c\u8d5b\u8fc7\u7537\u513f\u3002\u4e24\u4eba\u6597\u4e86\u4e94\u4e03\u5341\u62db\uff0c\u4f55 \u843c\u534e\u4e0d\u6562\u786c\u63a5\u5175\u5203\uff0c\u6210\u638c\u73e0\u5374\u4e5f\u65ab\u4e0d\u5230\u5979\u3002\u4e24\u4eba\u5404\u6709\u64c5\u957f\uff0c\u5012\u662f\u96be\u5206\u9ad8\u4e0b\u3002 \u3000\u3000\u6210\u7ae0\u4e94\u4e00\u9762\u770b\u4e00\u9762\u5fae\u7b11\uff0c\u5fc3\u559c\u5973\u513f\u867d\u7136\u4ece\u672a\u548c\u4eba\u6b63\u5f0f\u5bf9\u6253\u8fc7\uff0c\u5374\u4e5f\u4e0d\u9519\u3002\u90a3\u77e5\u6210\u638c\u73e0\u5c31 \u5403\u4e8f\u5728\u4ece\u65e0\u5bf9\u654c\u7684\u7ecf\u9a8c\uff0c\u4e94\u4e03\u5341\u62db\u4e00\u8fc7\uff0c\u88ab\u4f55\u843c\u534e\u770b\u51fa\u7834\u7efd\uff0c\u6c89\u5251\u4e00\u5f15\uff0c\u5f85\u6210\u638c\u73e0\u4e00\u5200\u78d5 \u4e0b\uff0c\u624b\u4e2d\u5251\u7a81\u7136\u4e00\u63d0\u4e00\u7ffb\uff0c\u9752\u5149\u95ea\u5904\uff0c\u4e00\u62db\u201c\u6a35\u592b\u95ee\u8def\u201d\uff0c\u5237\u7684\u5411\u5bf9\u65b9\u201c\u534e\u76d6\u7a74\u201d\u624e\u53bb\uff0c\u6210 \u638c\u73e0\u614c\u5fd9\u4f7f\u4e2a\u201c\u6a2a\u67b6\u91d1\u6881\u201d\uff0c\u6a2a\u5200\u529b\u78d5\uff0c\u90a3\u77e5\u4f55\u843c\u534e\u8fd9\u62db\u5374\u662f\u865a\u62db\uff0c\u9752\u5149\u518d\u95ea\uff0c\u5a07\u559d\u4e00\u58f0\uff1a \u201c\u64a4\u5200\uff01\u201d\u5251\u950b\u5237\u7684\u6307\u5230\u624b\u8155\uff0c\u6210\u638c\u73e0\u6025\u5fd9\u677e\u624b\u9000\u95ea\uff0c\u90a3\u53e3\u5200\u545b\u5f53\u5f53\u4e22\u4e86\u4e0b\u5730\u3002\u674f\u8138\u7f9e\u7ea2\uff0c \u8dd1\u56de\u7236\u4eb2\u8eab\u65c1\u3002 \u3000\u3000\u6210\u7ae0\u4e94\u9053\uff1a\u201c\u6b66\u5f53\u5251\u6cd5\u679c\u7136\u5999\u7edd\uff0c\u5c0f\u5973\u4e0d\u77e5\u81ea\u91cf\uff0c\u89c1\u7b11\u65b9\u5bb6\u3002\u8fd8\u662f\u54b1\u4eec\u4e0b\u573a\u5427\u3002\u201d\u5353\u4e00 \u822a\u9053\u58f0\uff1a\u201c\u597d\uff01\u201d\u6210\u7ae0\u4e94\u5374\u7aef\u8d77\u9152\u676f\uff0c\u8fde\u559d\u53c1\u676f\uff0c\u7b11\u9053\uff1a\u201c\u8d35\u5ba2\u8fdc\u6765\uff0c\u672a\u5c3d\u676f\u9152\uff0c\u5982\u4f55\u4f7f \u5f97\uff1f\u4e7e\u4e86\u6b64\u676f\u518d\u4e0b\u573a\u5427\uff01\u201d\u84e6\u7136\u53cc\u624b\u9f50\u626c\uff0c\u4e00\u676f\u9152\u548c\u4e00\u67c4\u53c9\u7740\u725b\u8089\u7684\u5c0f\u53c9\uff0c\u4e00\u9f50\u5411\u5353\u4e00\u822a\u9762 \u95e8\u98de\u6765\uff01 \u3000\u3000\u5353\u4e00\u822a\u53cc\u6307\u4e00\u4f38\uff0c\u5c06\u90a3\u676f\u9152\u4e00\u52fe\u4e00\u65cb\uff0c\u65cb\u5230\u53e3\u8fb9\uff0c\u53e3\u4e00\u5f00\uff0c\u53c8\u628a\u90a3\u67c4\u98de\u53c8\u54ac\u7740\uff0c\u5403\u4e86\u725b \u8089\uff0c\u5410\u51fa\u98de\u53c9\uff0c\u5c06\u9152\u5012\u5165\u53e3\u4e2d\uff0c\u63b7\u676f\u7b11\u9053\uff1a\u201c\u8c22\u5821\u4e3b\uff1f\u201d\u4e0e\u6210\u7ae0\u4e94\u76f8\u5bf9\u62f1\u624b\uff0c\u53cc\u53cc\u5954\u4e0b\u573a \u5fc3\u3002 \u3000\u3000\u8fd9\u4e00\u6218\u5174\u521a\u624d\u5c0f\u513f\u5973\u7684\u76f8\u640f\uff0c\u5927\u662f\u4e0d\u540c\u3002\u53ea\u89c1\u6210\u7ae0\u4e94\u53cc\u81c2\u7b95\u5f20\uff0c\u5411\u5916\u4e00\u5c55\uff0c\u6402\u5934\u75be\u6293\uff0c \u5353\u4e00\u822a\u7adf\u4e0d\u907f\u62db\uff0c\u500f\u7136\u8f6c\u8eab\uff0c\u5237\u7684\u4e00\u5251\uff0c\u4fbf\u523a\u654c\u4eba\u8f6f\u808b\u3002\u7ae0\u4e94\u559d\u58f0\uff1a\u201c\u6765\u5f97\u597d\uff01\u201d\u5f80\u65c1\u4e00\u4e2a \u6ed1\u6b65\uff0c\u8eab\u5f62\u4e00\u4fef\uff0c\u5de6\u638c\u76f4\u63d2\u54bd\u5589\uff0c\u53f3\u624b\u6a2a\u80b1\u649e\u80c1\uff0c\u5353\u4e00\u822a\u817e\u8eab\u4e00\u8df3\uff0c\u5237\u5237\u4e24\u5251\u659c\u524a\u4e86\u6765\uff0c\u6210 \u7ae0\u4e94\u8eab\u8eaf\u4e00\u7ffb\uff0c\u8fd0\u9000\u6b65\u8fde\u73af\u638c\u6cd5\uff0c\u534a\u653b\u534a\u5b88\uff0c\u4fe8\u5982\u795e\u809b\u76d8\u65cb\uff0c\u9f99\u86c7\u75be\u8d70\uff0c\u5353\u4e00\u822a\u4e00\u8fde\u6570\u5251\uff0c \u90fd\u843d\u4e86\u7a7a\uff01 \u3000\u3000\u6210\u7ae0\u4e94\u6697\u6697\u5403\u60ca\uff0c\u6599\u4e0d\u5230\u5353\u4e00\u822a\u4e0d\u8fc7\u53c1\u5341\u591a\u5c81\u6837\u5b50\uff0c\u5251\u6cd5\u706b\u5019\u90fd\u6781\u8001\u5230\uff0c\u4e24\u4eba\u5168\u795e\u8d2f \u6ce8\uff0c\u4e0d\u6562\u8f7b\u654c\u3002\u6210\u7ae0\u4e94\u53ea\u638c\u7ffb\u7ffb\u6eda\u6eda\uff0c\u5ffd\u626b\u5ffd\u62cd\uff0c\u5ffd\u6293\u5ffd\u6233\uff0c\u638c\u98ce\u6fc0\u6c64\uff0c\u987b\u7709\u4ff1\u5f20\uff0c\u5353\u4e00\u822a \u4e00\u5251\u56de\u65cb\uff0c\u75be\u5982\u9e70\u96bc\uff0c\u5251\u6c14\u7eb5\u6a2a\uff0c\u53d8\u5316\u83ab\u6d4b\u3002\u53ea\u89c1\u638c\u98ce\u5230\u5904\uff0c\u6c99\u77f3\u98de\u626c\uff0c\u5251\u6c14\u51b2\u9704\uff0c\u4eba\u5f71\u83ab \u8fa8\u3002\u6597\u5230\u4e86\u4e00\u767e\u6765\u62db\uff0c \u3000\u3000\u662f\u4e0d\u5206\u80dc\u8d1f\u3002 \u3000\u3000\u6210\u7ae0\u4e94\u529f\u529b\u8f83\u9ad8\uff0c\u4f46\u5353\u4e00\u822a\u5251\u52bf\u7ef5\u5bc6\uff0c\u5374\u4e5f\u653b\u4e0d\u8fdb\u53bb\u3002\u53c8\u6597\u4e86\u4e00\u9635\uff0c\u6210\u7ae0\u4e94\u5fc3\u4e2d\u7126\u71e5\uff0c \u594b\u529b\u5f3a\u653b\uff0c\u6fc0\u6597\u4e4b\u4e2d\uff0c\u98de\u8eab\u7a81\u8d77\uff0c\u4e94\u722a\u5982\u94a9\uff0c\u6293\u5353\u4e00\u822a\u9876\u5fc3\uff0c\u5353\u4e00\u822a\u4e00\u5251\u4e0a\u64a9\uff0c\u6210\u7ae0\u4e94\u7adf\u7136 \u5728\u534a\u7a7a\u4e2d\u8eab\u5b50\u4e00\u5c48\uff0c\u4e00\u638c\u6c64\u5f00\u5353\u4e00\u822a\u7684\u5251\u52bf\uff0c\u4ecd\u7136\u98de\u6293\u4e0a\u6765\uff0c\u5353\u4e00\u822a\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5c55\u71d5\u9752\u5341 \u516b\u7ffb\u7684\u529f\u592b\uff0c\u4f0f\u5730\u53c1\u6eda\uff0c\u624d\u907f\u5f00\u4e86\u6210\u7ae0\u4e94\u4e00\u6293\uff0c\u98ce\u6c99\u5821\u4f17\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u5403\u8fc7\u5353\u4e00\u822a\u4e4b\u4e8f\u7684\u526f \u5821\u4e3b\u66f4\u7eb5\u58f0\u5927\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u4eec\u770b\u5230\u4e86\u6ca1\u6709\uff1f\u597d\u4e00\u4e2a\u4e4c\u9f9f\u722c\u5730\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u95f7\u58f0\u4e0d\u54cd\uff0c\u633a\u5251\u518d\u6597\uff0c\u8fc7\u4e86\u4e00\u9635\uff0c\u6210\u7ae0\u4e94\u53c8\u7528\u524d\u6cd5\uff0c\u98de\u8eab\u7eb5\u8d77\uff0c\u626c\u722a\u4e0b\u64d2\uff0c\u5353\u4e00 \u822a\u8eab\u5b50\u7a81\u7136\u659c\u63a0\uff0c\u5251\u5c16\u4e00\u63a0\uff0c\u6210\u7ae0\u4e94\u4f9d\u6837\u846b\u82a6\uff0c\u5de6\u638c\u5288\u4e0b\uff0c\u6709\u722a\u4e00\u62ff\uff0c\u90a3\u77e5\u638c\u98ce\u5230\u5904\uff0c\u6251\u4e86 \u4e2a\u7a7a\uff0c\u5353\u4e00\u822a\u957f\u5251\u4e00\u62d6\uff0c\u53cd\u624b\u4e00\u524a\uff0c\u53c8\u72e0\u53c8\u75be\uff0c\u5c31\u50cf\u5728\u591c\u7a7a\u4e2d\u95ea\u8fc7\u4e00\u9053\u7535\u5149\uff0c\u6210\u7ae0\u4e94\u5927\u53eb\u4e00 \u58f0\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u75be\u51b2\u51fa\u53c1\u4e08\u5f00\u5916\uff0c\u63a5\u5730\u4e4b\u9645\uff0c\u624d\u4e00\u4e2a\u6597\u7ffb\u4e86\u8fc7\u6765\uff0c\u7f20\u7740\u624b\u8155\u62a4\u624b\u7684\u76ae\u5957\u5df2\u88ab \u5272\u5f00\uff0c\u5e78\u597d\u4eba\u8fd8\u672a\u4f24\u3002\u98ce\u6c99\u5821\u4f17\u4eba\u76f8\u987e\u5931\u8272\uff0c\u4f55\u843c\u534e\u4e5f\u7eb5\u58f0\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u4f60\u4eec\u770b\u5230\u4e86\u6ca1\u6709\u4e86 \u597d\u4e00\u4e2a\u8001\u72d7\u7ffb\u8eab\uff1f\u201d \u3000\u3000\u6210\u7ae0\u4e94\u53eb\u9053\uff1a\u201c\u4e00\u6293\u4e00\u5251\uff0c\u5404\u4e0d\u8f93\u4e8f\uff0c\u518d\u6765\uff0c\u518d\u6765\uff01\u201d\u98de\u8eab\u53c8\u6251\uff0c\u5251\u638c\u518d\u5ea6\u4ea4\u950b\u3002\u5353\u4e00 \u822a\u7ec6\u5fc3\u9632\u5907\uff0c\u6597\u4e86\u4e8c\u53c1\u5341\u62db\uff0c\u5374\u672a\u89c1\u4ed6\u518d\u65bd\u524d\u6280\u3002 \u3000\u3000\u539f\u6765\u6210\u7ae0\u4e94\u98de\u8eab\u4e00\u6251\uff0c\u4e43\u662f\u9e70\u722a\u529f\u7684\u7cbe\u534e\u6240\u805a\uff0c\u5389\u5bb3\u975e\u51e1\u3002\u529f\u529b\u6700\u6df1\u7684\u53ef\u4ee5\u5728\u534a\u7a7a\u4e2d\u8f6c \u6298\u56de\u65cb\uff0c\u5c48\u4f38\u5982\u610f\uff0c\u6251\u4e0b\u6765\u65f6\uff0c\u5c31\u771f\u5982\u5de8\u9e70\u6251\u5154\u4e00\u6837\uff0c\u65e0\u53ef\u56de\u907f\uff0c\u53ef\u662f\u6210\u7ae0\u4e94\u5c1a\u672a\u4fee\u5230\u4e0a\u4e0a \u7684\u529f\u592b\uff0c\u80fd\u5728\u534a\u7a7a\u4e2d\u4e00\u4e2a\u56de\u65cb\uff0c\u6240\u4ee5\u5f8c\u6765\u5353\u4e00\u822a\u4f7f\u51fa\u8fbe\u6469\u602a\u62db\uff0c\u7acb\u523b\u8fd8\u523a\u4e86\u4ed6\u4e00\u5251\u3002 \u3000\u3000\u5353\u4e00\u822a\u867d\u7136\u53ea\u8bc6\u51e0\u62db\u8fbe\u6469\u5251\u5f0f\uff0c\u4f46\u7528\u65bc\u5e94\u4ed8\u6210\u7ae0\u4e94\u7684\u98de\u64d2\u7a81\u88ad\uff0c\u5374\u662f\u529f\u6548\u975e\u5e38\uff0c\u6210\u7ae0\u4e94 \u8bd5\u8fc7\u5403\u4e8f\uff0c\u4e0d\u77e5\u4ed6\u7684\u865a\u5b9e\uff0c\u7adf\u7136\u4e0d\u6562\u518d\u7528\u8fd9\u95e8\u7edd\u6280\u3002 \u3000\u3000\u6210\u7ae0\u4e94\u4e0d\u7528\u98de\u64d2\u6251\u51fb\u7684\u7edd\u6280\uff0c\u5353\u4e00\u822a\u4e5f\u4e0d\u7528\u8fbe\u6469\u5251\u5f0f\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u4ecd\u53d8\u6210\u4e86\u6b66\u5f53\u6d3e\u7684\u4e03 \u5341\u4e8c\u624b\u8fde\u73af\u5251\u6cd5\u6597\u4ed6\u7684\u9e70\u722a\u529f\u64d2\u62ff\u638c\u6cd5\uff0c\u6062\u590d\u4e86\u5148\u524d\u7684\u72b6\u51b5\u3002\u6210\u7ae0\u4e94\u867d\u7136\u529f\u529b\u8f83\u9ad8\uff0c\u53ef\u662f\u5353 \u4e00\u822a\u5374\u80dc\u5728\u5e74\u8f7b\u529b\u58ee\uff0c\u4e45\u6218\u4e0d\u8870\uff0c\u52a0\u4e0a\u6210\u7ae0\u4e94\u4f7f\u4e0d\u51fa\u7edd\u6280\uff0c\u5fc3\u4e2d\u5df2\u602f\uff0c\u950b\u8292\u6e10\u51cf\uff0c\u5927\u4e0d\u5982 \u524d\u3002\u5929\u9f99\u4e0a\u4eba\u76b1\u8d77\u7709\u5934\uff0c\u4f55\u843c\u534e\u770b\u5f97\u5927\u4e3a\u9ad8\u5174\u3002 \u3000\u3000\u518d\u6597\u4e86\u53c1\u4e94\u5341\u62db\uff0c\u5353\u4e00\u822a\u6e10\u62a2\u4e0a\u98ce\uff0c\u5929\u9f99\u4e0a\u4eba\u5ffd\u7136\u8dc3\u4e0b\u573a\u5b50\uff0c\u53cc\u638c\u4e00\u5206\uff0c\u559d\u58f0\uff1a\u201c\u4f4f \u624b\uff01\u201d\u5353\u4e00\u822a\u7a81\u89c9\u4e00\u80a1\u731b\u529b\u63a8\u6765\uff0c\u6025\u6025\u95ea\u5f00\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6210\u5821\u4e3b\uff0c\u8fd9\u662f\u600e\u9ebd\u4e2a\u8bf4\u6cd5\uff1f\u201d \u3000\u3000\u5929\u9f99\u4e0a\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u6597\u4e86\u8bb8\u591a\u65f6\u5019\uff0c\u4ecd\u662f\u4e0d\u5206\u4e0a\u4e0b\uff0c\u5c31\u7b97\u5e73\u624b\u4e86\u5427\u3002\u201d\u5353\u4e00\u822a\u4e00\u60f3\uff1a\u5f7c \u4f17\u6211\u5be1\uff0c\u4e5f\u4e0d\u597d\u592a\u8fc7\u626b\u4ed6\u9762\u5b50\uff0c\u4fbf\u9053\uff1a\u201c\u591a\u8c22\u5821\u4e3b\u624b\u4e0b\u7559\u60c5\uff0c\u5353\u67d0\u5e78\u672a\u843d\u8d25\uff0c\u6211\u7684\u5e08\u53d4\u53ef\u4ee5 \u653e\u51fa\u6765\u4e86\u5427\uff1f\u201d \u3000\u3000\u6210\u7ae0\u4e94\u9762\u8272\u5c34\u5c2c\uff0c\u652f\u543e\u96be\u7b54\uff0c\u5929\u9f99\u4e0a\u4eba\u9053\uff1a\u201c\u90a3\u662f\u4f60\u548c\u6210\u5821\u4e3b\u7684\u4e8b\uff0c\u6211\u672c\u6765\u4e0d\u597d\u5e72\u9884\uff0c \u53ef\u662f\u6211\u548c\u4f60\u4e5f\u6709\u70b9\u5c0f\u5c0f\u8fc7\u8282\uff0c\u6211\u6562\u5192\u6627\u8bf7\u6210\u5821\u4e3b\u51c6\u5141\uff0c\u5c06\u4e24\u4ef6\u4e8b\u60c5\u62da\u5728\u4e00\u8d77\uff0c\u4f60\u6211\u7684\u5e10\u7b97\u6e05 \u4e4b\u5f8c\uff0c\u5929\u9f99\u6d3e\u4ece\u6b64\u4e0d\u5411\u4f60\u5bfb\u4ec7\uff0c\u767d\u77f3\u9053\u4eba\u4e5f\u653e\u8fd8\u7ed9\u4f60\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u5fc3\u5ff5\u8fd9\u573a\u6076\u6597\u65e0\u53ef\u907f\u514d\uff0c\u6717\u58f0\u95ee\u9053\uff1a\u201c\u5982\u4f55\u7b97\u6cd5\uff1f\u4f60\u4eec\u5929\u9f99\u6d3e\u4eba\u591a\u52bf\u4f17\uff0c\u82e5\u8981\u7fa4 \u6bb4\uff0c\u90a3\u9ebd\u5353\u67d0\u5c06\u5934\u5949\u9001\u7ed9\u4f60\uff0c\u62b5\u4f60\u5e08\u5f1f\u5f92\u5f1f\u7684\u547d\u4fbf\u7f62\uff01\u201d\u5fc3\u5ff5\u5929\u9f99\u4e0a\u4eba\u4e5f\u662f\u4e00\u6d3e\u5b97\u7956\uff0c\u81ea\u5df1 \u5148\u7528\u8bf4\u8bdd\u5c06\u4ed6\u9547\u4f4f\uff0c\u8c05\u4ed6\u4e0d\u6562\u4e0d\u8981\u9762\u5b50\u3002 \u3000\u3000\u5929\u9f99\u4e0a\u4eba\u679c\u7136\u7b11\u9053\uff1a\u201c\u4f60\u662f\u6b66\u5f53\u6d3e\u638c\u95e8\uff0c\u6211\u662f\u5929\u9f99\u6d3e\u6559\u4e3b\uff0c\u65d7\u9f13\u76f8\u5f53\uff0c\u4f55\u5fc5\u65c1\u4eba\u76f8\u52a9\u3002 \u4f60\u82e5\u80dc\u5f97\u4e86\u6211\uff0c\u767d\u77f3\u9053\u4eba\u51b3\u5c11\u4e0d\u4e86\u4e00\u6839\u6beb\u6bdb\u3002\u53ef\u662f\u4f60\u82e6\u8f93\u4e86\uff0c\u4e5f\u5f97\u4f9d\u6211\u4eec\u7684\u89c4\u77e9\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u9053\uff1a\u201c\u4ec0\u9ebd\u89c4\u77e9\uff1f\u201d\u5929\u9f99\u4e0a\u4eba\u9053\uff1a\u201c\u6211\u4eec\u897f\u57df\u7684\u6d6e\u5c60\u5f1f\u5b50\uff0c\u7d20\u6765\u6709\u4e00\u4e2a\u89c4\u77e9\uff0c\u4e0d \u8bba\u662f\u8fa9\u8bba\u4f5b\u6cd5\uff0c\u6216\u6bd4\u8bd5\u6b66\u529f\uff0c\u8f93\u7684\u90a3\u65b9\uff0c\u4e00\u662f\u6295\u964d\u80dc\u65b9\uff0c\u81ea\u613f\u505a\u80dc\u65b9\u7684\u5f1f\u5b50\uff1b\u82e5\u7136\u4e0d\u613f\u505a\u5f97 \u80dc\u8005\u7684\u5f1f\u5b50\uff0c\u90a3\u4fbf\u8981\u5c06\u5934\u5272\u4e0b\uff0c\u4ee5\u8d4e\u7f6a\u884d\u3002\u201d \u3000\u3000\u5353\u4e00\u822a\u6012\u9053\uff1a\u201c\u4f60\u6211\u6bd4\u8bd5\u4fbf\u662f\uff0c\u4f55\u5fc5\u591a\u8a00\uff0c\u6211\u82e5\u8f93\u4e86\uff0c\u4eba\u5934\u5949\u9001\u3002\u201d\u5929\u9f99\u4e0a\u4eba\u54c8\u54c8\u7b11 \u9053\uff1a\u201c\u597d\uff0c\u4e00\u8a00\u4e3a\u5b9a\uff0c\u5217\u4f4d\u82f1\u96c4\u4f5c\u4e2a\u89c1\u8bc1\u3002\u659f\u4e24\u676f\u9152\u6765\uff01\u201d \u3000\u3000\u5929\u9f99\u6d3e\u95e8\u4e0b\u5f1f\u5b50\u6367\u4e0a\u4e24\u676f\u6ee1\u6ee1\u7684\u9152\uff0c\u5353\u4e00\u822a\u9053\uff1a\u201c\u4e0d\u5fc5\u591a\u963b\u65f6\u5019\uff0c\u559d\u4ec0\u9ebd\u9152\uff1f\u201d\u5929\u9f99\u4e0a \u4eba\u9053\uff1a\u201c\u6211\u4eec\u897f\u57df\u89c4\u77e9\uff0c\u4e34\u6b7b\u8bc0\u522b\uff0c\u5fc5\u5f97\u5c3d\u4e00\u676f\u9152\uff0c\u542c\u8bf4\u4f60\u4eec\u5173\u5185\u7684\u89c4\u77e9\uff0c\u6b7b\u56da\u5f85\u51b3\uff0c\u72f1\u5352 \u4e5f\u5f97\u656c\u4ed6\u53c1\u676f\u3002\u54b1\u4eec\u4e8c\u4eba\u51b3\u6597\u4e0b\u6765\uff0c\u603b\u6709\u4e00\u4e2a\u8981\u6b7b\uff0c\u7406\u5e94\u4e92\u656c\u4e00\u676f\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u5927\u6012\uff0c\u7aef\u8d77\u9152\u676f\uff0c\u7167\u9762\u5288\u53bb\uff0c\u5c31\u5728\u540c\u4e00\u65f6\u523b\uff0c\u5929\u9f99\u4e0a\u4eba\u90a3\u4e00\u676f\u9152\u4e5f\u7167\u9762\u5288\u6765\uff0c\u5353 \u4e00\u822a\u60f3\u715e\u4ed6\u6c14\u7130\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5356\u5f04\u4e86\u4e00\u624b\u4e0a\u4e58\u529f\u592b\uff0c\u5de6\u638c\u5411\u524d\u4e00\u63a8\uff0c\u8fd0\u638c\u529b\u538b\u7740\u9152\u676f\uff0c\u7eb5\u8eab \u4e00\u8dc3\uff0c\u5c06\u90a3\u9152\u676f\u53d6\u4e86\u8fc7\u6765\uff0c\u676f\u4e2d\u9152\u7adf\u7136\u4e1d\u6beb\u672a\u6ef4\uff01\u5353\u4e00\u822a\u4e00\u53e3\u559d\u5c3d\uff0c\u4ee5\u4e3a\u5fc5\u7136\u6709\u4eba\u559d\u91c7\uff0c\u90a3 \u6599\u6ee1\u573a\u9e26\u96c0\u65e0\u58f0\uff0c\u5353\u4e00\u822a\u7eb5\u76ee\u4e00\u770b\uff0c\u4e0d\u89c9\u5927\u60ca\u5931\u8272\uff01 \u3000\u3000\u53ea\u89c1\u5929\u9f99\u4e0a\u4eba\u4f38\u957f\u9888\u5b50\uff0c\u5411\u7a7a\u4e2d\u5439\u6c14\uff0c\u90a3\u9152\u676f\u88ab\u4ed6\u5439\u5f97\u5411\u4e0a\u817e\u8d77\uff0c\u843d\u4e0d\u4e0b\u6765\uff0c\u89c1\u5353\u4e00\u822a \u671b\u4ed6\uff0c\u8fd9\u624d\u7b11\u9053\uff1a\u201c\u8d35\u5ba2\u65e2\u4e7e\u4e86\u676f\uff0c\u6211\u4e5f\u8be5\u5949\u966a\u4e86\uff01\u201d\u8bf4\u8bdd\u4e4b\u9645\uff0c\u7a7a\u4e2d\u7684\u9152\u676f\u7ffb\u8dcc\u4e0b\u6765\uff0c\u9152 \u5982\u4e00\u6761\u94f6\u7ebf\uff0c\u4ece\u7a7a\u5c04\u4e0b\uff0c\u5929\u9f99\u4e0a\u4eba\u5f20\u53e3\u4e00\u5438\uff0c\u5438\u5f97\u4e7e\u4e7e\u51c0\u51c0\uff0c\u62b9\u62b9\u5634\u9053\uff1a\u201c\u8461\u8404\u7f8e\u9152\uff0c\u597d\u9999 \u597d\u9999\uff01\u201d\u6ee1\u573a\u91c7\u58f0\u96f7\u52a8\u3002 \u3000\u3000\u5353\u4e00\u822a\u5403\u60ca\u975e\u5c0f\uff1a\u5929\u9f99\u4e0a\u4eba\u7adf\u662f\u8fdc\u975e\u4ed6\u7684\u5e08\u5f1f\u53ef\u6bd4\uff0c\u5185\u529f\u5728\u5df1\u4e4b\u4e0a\u3002\u5fc3\u4e2d\u6697\u6697\u76d8\u7b97\u62b5\u654c \u4e4b\u6cd5\uff0c\u53ea\u542c\u5f97\u5929\u9f99\u4e0a\u4eba\u5f97\u610f\u6d0b\u6d0b\uff0c\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u6211\u4eec\u90fd\u662f\u4e00\u6d3e\u9886\u8896\uff0c\u52a8\u624b\u52a8\u811a\uff0c\u6709\u5931\u5c0a\u4e25\uff0c \u4e0d\u5982\u6587\u6bd4\u4e86\u5427\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u9053\u2026\u201c\u600e\u9ebd\u6bd4\u6cd5\uff1f\u201d\u5929\u9f99\u4e0a\u4eba\u9053\uff1a\u201c\u6211\u5750\u5728\u53f0\u4e0a\uff0c\u7531\u4f60\u8fde\u51fb\u53c1\u638c\uff0c\u6211\u4e0d\u8fd8\u624b\uff0c\u82e5 \u80fd\u5c06\u6211\u51fb\u5012\uff0c\u4f60\u4fbf\u5b34\u4e86\u3002\u201d\u8fd9\u4e2a\u6bd4\u6cd5\uff0c\u770b\u6765\u662f\u5353\u4e00\u822a\u5360\u5c3d\u4fbf\u5b9c\uff0c\u5176\u5b9e\u5374\u662f\u5929\u9f99\u4e0a\u4eba\u7684\u8001\u8c0b\u6df1 \u7b97\u3002 \u3000\u3000\u539f\u6765\u5929\u9f99\u4e0a\u4eba\u7528\u676f\u9152\u8bd5\u51fa\u4ed6\u7684\u5185\u529f\u4e0d\u5982\u81ea\u5df1\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u5353\u4e00\u822a\u5251\u6cd5\u8d85\u5999\uff0c\u6211\u867d\u80fd\u80dc \u4ed6\uff0c\u6050\u6015\u4e5f\u8981\u767e\u62db\u4ee5\u5916\uff1a\u4e0d\u5982\u7528\u8fd9\u4e2a\u6bd4\u6cd5\uff0c\u53c1\u638c\u4e4b\u5f8c\uff0c\u7acb\u5373\u80dc\u4ed6\uff0c\u4f55\u7b49\u5149\u5f69\uff01 \u3000\u3000\u5353\u4e00\u822a\u4e5f\u60f3\u9053\uff1a\u82e5\u5174\u4ed6\u786c\u62da\uff0c\u770b\u6765\u975e\u4ed6\u654c\u624b\uff0c\u4ed6\u65e2\u5982\u6b64\u6258\u5927\uff0c\u6211\u5c31\u8bd5\u4ed6\u4e00\u8bd5\uff0c\u4e0d\u4fe1\u4ed6\u662f \u94c1\u94f8\u91d1\u521a\uff0c\u6253\u4ed6\u4e0d\u5012\u3002 \u3000\u3000\u5f53\u4e0b\u4e24\u65b9\u9882\u610f\uff0c\u5929\u9f99\u4e0a\u4eba\u8df3\u4e0a\u9ad8\u53f0\uff0c\u76d8\u819d\u5750\u4e0b\uff0c\u633a\u8d77\u4e00\u4e2a\u5927\u809a\u76ae\uff0c\u5b9b\u5982\u5f25\u52d2\u4f5b\u50cf\uff0c\u54c8\u54c8 \u7b11\u9053\uff1a\u201c\u6b66\u5f53\u6d3e\u7684\u5927\u638c\u95e8\uff0c\u4f5b\u7237\u5728\u6b64\u5019\u6559\u4e86\uff01\u201d\u5353\u4e00\u822a\u8df3\u4e0a\u53f0\u4e0a\uff0c\u5c0f\u81c2\u4e00\u6325\uff0c\u5212\u4e86\u4e00\u4e2a\u534a \u5f27\uff0c\u547c\u7684\u4e00\u638c\uff0c\u5c31\u5411\u4ed6\u7684\u5927\u809a\u76ae\u51fb\u53bb\uff0c\u4e0d\u6599\u638c\u950b\u6240\u53ca\uff0c\u72b9\u5982\u4e00\u56e2\u68c9\u7d6e\uff0c\u800c\u4e14\u6709\u4e00\u80a1\u5438\u529b\uff0c\u7adf \u628a\u81ea\u5df2\u7684\u624b\u638c\u7d27\u7d27\u88f9\u4f4f\uff0c\u5353\u4e00\u822a\u5927\u5403\u4e00\u60ca\uff0c\u6025\u628a\u52b2\u529b\u4e00\u677e\uff0c\u624b\u638c\u987a\u4ed6\u5438\u52bf\uff0c\u8f7b\u8f7b\u4e00\u63a8\uff0c\u659c\u659c \u7684\u5728\u809a\u76ae\u4e0a\u6ed1\u8131\u51fa\u6765\u3002\u5929\u9f99\u4e0a\u4eba\u89c1\u5438\u4e0d\u7740\u4ed6\u7684\u624b\u638c\uff0c\u4e5f\u5fae\u5fae\u4e00\u60ca\uff0c\u5374\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u662f\u7b2c\u4e00 \u638c\u4e86\uff0c\u518d\u6765\uff0c\u518d\u6765\uff01\u201d\u53f0\u4e0b\u4f17\u4eba\uff0c\u7eb7\u7eb7\u62cd\u638c\uff01 \u3000\u3000\u5353\u4e00\u822a\u7565\u4e00\u601d\u7d22\uff0c\u8fc8\u524d\u4e00\u6b65\uff0c\u6a2a\u638c\u4e00\u626b\uff0c\u8fd9\u4e00\u638c\u4e0d\u626b\u4ed6\u7684\u809a\u76ae\uff0c\u5374\u5288\u4ed6\u7684\u9762\u95e8\uff0c\u5fc3\u4e2d\u60f3 \u9053\uff1a\u201c\u4efb\u4ed6\u5185\u529f\u591a\u597d\uff0c\u4e5f\u4e0d\u4f1a\u7ec3\u5230\u9762\u76ae\u4e0a\u6765\uff01\u201d\u90a3\u77e5\u4e00\u638c\u5288\u53bb\uff0c\u5929\u9f99\u4e0a\u4eba\u7a81\u7136\u7709\u5934\u4e00\u62ac\uff0c \u201c\u84ec\u201d\u7684\u4e00\u58f0\uff0c\u786c\u63a5\u4e86\u5353\u4e00\u822a\u4e00\u638c\uff0c\u5353\u4e00\u822a\u638c\u950b\u6240\u53ca\uff0c\u5982\u89e6\u94a2\u677f\uff0c\u5353\u4e00\u822a\u7ed9\u9707\u5f97\u5012\u9000\u53c1\u6b65\uff0c \u51e0\u4e4e\u8dcc\u843d\u53f0\u4e0b\uff0c\u5929\u9f99\u4e0a\u4eba\u4e5f\u88ab\u9707\u5f97\u5c41\u80a1\u79fb\u8fc7\u4e00\u8fb9\uff0c\u632a\u4e86\u4e00\u4e2a\u65b9\u4f4d\u3002\u4e0d\u8fc7\u6709\u8a00\u5728\u524d\uff0c\u8981\u5c06\u4ed6\u51fb \u5012\uff0c\u624d\u7b97\u5f97\u80dc\uff0c\u4ed6\u79fb\u4e86\u4e00\u4e2a\u65b9\u4f4d\uff0c\u4ecd\u7b97\u4ed6\u8d62\u3002\u53f0\u4e0b\u4f17\u4eba\uff0c\u53c8\u662f\u5927\u58f0\u559d\u91c7\uff01 \u3000\u3000\u5929\u9f99\u4e0a\u4eba\u5927\u7b11\u9053\uff1a\u201c\u6709\u6700\u5f8c\u4e00\u638c\u4e86\uff0c\u4f60\u82e5\u51fb\u6211\u4e0d\u5012\uff0c\u4e0d\u505a\u6211\u7684\u5f1f\u5b50\uff0c\u4fbf\u8981\u5272\u4e0b\u9996\u7ea7 \u4e86\uff01\u201d\u5353\u4e00\u822a\u6599\u4e0d\u5230\u4ed6\u5185\u529f\u5916\u529f\u5747\u662f\u767b\u5cf0\u9020\u6781\uff0c\u4e00\u65f6\u95f4\u60f3\u4e0d\u51fa\u5411\u4f55\u5904\u843d\u624b\uff0c\u624b\u638c\u6325\u5728\u534a\u7a7a\u4e2d \u5c06\u843d\u672a\u843d\u3002\u5929\u9f99\u4e0a\u4eba\u751a\u4e0d\u8010\u70e6\uff0c\u559d\u9053\uff1a\u201c\u4f60\u6015\u6b7b\u9ebd\uff1f\u4e3a\u4f55\u4e0d\u6253\uff1f\u201d \u3000\u3000\u5821\u5f8c\u9762\u5ffd\u7136\u4e00\u9635\u55a7\u54d7\uff0c\u6210\u7ae0\u4e94\u559d\u9053\uff1a\u201c\u4ec0\u9ebd\u4eba\u80e1\u95f9\uff1f\u5feb\u4eba\u53bb\u770b\u3002\u201d\u53f0\u4e0b\u4f17\u4eba\uff0c\u4ecd\u662f\u76ee\u4e0d \u8f6c\u775b\uff0c\u8981\u77a7\u5353\u4e00\u822a\u8fd9\u6700\u5f8c\u4e00\u638c\u3002 \u3000\u3000\u5c31\u5728\u6b64\u9645\uff0c\u5821\u5185\u4f20\u6765\u4e00\u58f0\u957f\u7b11\uff0c\u91cc\u9762\u4e00\u5927\u5806\u4eba\uff0c\u8dcc\u8dcc\u649e\u649e\uff0c\u6d8c\u5954\u9003\u51fa\uff0c\u5353\u4e00\u822a\u5927\u559c\u53eb \u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff01\u201d\u968f\u624b\u4e00\u638c\uff0c\u5411\u5929\u9f99\u4e0a\u4eba\u8170\u80c1\u62cd\u4e0b\uff0c\u5929\u9f99\u4e0a\u4eba\u5ffd\u89c9\u80c1\u4e0b\u4e00\uff0c\u88ab\u5353\u4e00\u822a\u8f7b\u8f7b\u4e00 \u9001\uff0c\u8dcc\u843d\u53f0\u4e0b\u3002\u5929\u9f99\u4e0a\u4eba\u83ab\u540d\u5176\u5999\uff0c\u5fc3\u4e2d\u6000\u7591\u6709\u4eba\u6697\u7b97\uff0c\u53ef\u662f\u5374\u770b\u4e0d\u51fa\u6765\uff0c\u81ea\u5df1\u662f\u4e00\u6d3e\u5b97 \u7956\uff0c\u53d7\u4eba\u6697\u7b97\u800c\u65e0\u6cd5\u9632\u5907\uff0c\u8bf4\u4e86\u51fa\u6765\uff0c\u66f4\u662f\u4e22\u8138\uff0c\u53ea\u597d\u9f13\u7740\u4e00\u809a\u5b50\u6c14\uff0c\u5fcd\u7740\u54d1\u4e8f\uff0c\u817e\u8eab\u8df3 \u8d77\u3002\u4e3e\u76ee\u4e00\u671b\uff0c\u4f46\u89c1\u4e00\u4e2a\u767d\u53d1\u5973\u5b50\uff0c\u4ece\u5821\u5185\u76f4\u8dd1\u51fa\u6765\uff0c\u624b\u6301\u957f\u5251\uff0c\u968f\u610f\u6325\u6d12\uff0c\u88ab\u5979\u5251\u5c16\u89e6\u53ca \u7684\u987f\u65f6\u5012\u5730\u72c2\u547c\uff0c\u970e\u773c\u5df2\u51b2\u5230\u573a\u5fc3\uff0c\u5927\u7fa4\u5821\u4e01\u7eb7\u7eb7\u9003\u907f\uff0c\u4e0d\u6562\u8fd1\u5979\u8eab\u8fb9\u3002 \u3000\u3000\u6210\u7ae0\u4e94\u5927\u53eb\u9053\uff1a\u201c\u8fd9\u662f\u767d\u53d1\u9b54\u5973\uff01\u201d\u548c\u5341\u51e0\u4e2a\u6709\u540d\u9ad8\u624b\uff0c\u62d4\u51fa\u5175\u5203\uff0c\u5411\u524d\u5835\u4f4f\uff0c\u5ffd\u89c1\u5f8c \u9762\u8fd8\u6709\u4e00\u4eba\uff0c\u6c14\u547c\u547c\u7684\u6301\u5251\u8dd1\u51fa\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u5929\u9f99\u5996\u50e7\u3001\u970d\u5143\u4ef2\u8001\u8d3c\uff0c\u5403\u6211\u4e00\u5251\uff01\u201d\u8fd9\u4eba \u6b63\u662f\u767d\u77f3\u9053\u4eba\uff0c\u4f55\u843c\u534e\u5927\u559c\u53eb\u9053\uff1a\u201c\u7239\u7239\uff01\u201d\u5353\u4e00\u822a\u5df2\u8df3\u4e0b\u53f0\uff0c\u5c06\u5979\u62c9\u7740\uff0c\u9053\uff1a\u201c\u4e0d\u8981\u51b2\u4e0a \u53bb\uff0c\u7389\u7f57\u5239\u6765\u4e86\uff0c\u6211\u4eec\u7edd\u80fd\u8131\u9669\uff01\u201d \u3000\u3000\u4f60\u9053\u7389\u7f57\u5239\u4f55\u4ee5\u4f1a\u7a81\u7136\u800c\u6765\uff0c\u539f\u6765\u5979\u5728\u90a3\u665a\u542c\u4e86\u4f55\u843c\u534e\u4e4b\u8a00\u5f8c\uff0c\u89c1\u8bf4\u767d\u77f3\u9053\u4eba\u88ab\u64d2\uff0c\u7b2c \u4e8c\u65e5\u4fbf\u53bb\u67e5\u63a2\uff0c\u59cb\u77e5\u6210\u7ae0\u4e94\u4e0e\u5929\u9f99\u4e0a\u4eba\u7ea6\u4e86\u4e00\u5927\u7fa4\u4eba\uff0c\u5bf9\u4ed8\u81ea\u5df1\uff0c\u767d\u77f3\u9053\u4eba\u88ab\u64d2\uff0c\u4e0d\u8fc7\u662f\u4e2a \u966a\u886c\uff0c\u4e0d\u7531\u5927\u4e3a\u751f\u6c14\uff0c\u5979\u867d\u7136\u618e\u538c\u767d\u77f3\u9053\u4eba\uff0c\u81f3\u6b64\u4e5f\u4e0d\u80fd\u4e0d\u6551\u3002\u4f55\u51b5\u5979\u53c8\u63a2\u77e5\u5353\u4e00\u822a\u5728\u4e03\u5915 \u4e4b\u671f\uff0c\u4fbf\u5c06\u8d74\u7ea6\uff0c\u4e0d\u7ba1\u5979\u5fc3\u4e2d\u6709\u6068\uff0c\u603b\u8fd8\u4e0d\u5fcd\u5353\u4e00\u822a\u5b64\u8eab\u9001\u6b7b\u3002\u56e0\u6b64\uff0c\u4fbf\u4e58\u7740\u5353\u4e00\u822a\u5728\u524d\u9762 \u548c\u4ed6\u4eec\u76f8\u6597\u4e4b\u9645\uff0c\u6084\u6084\u7684\u6e9c\u8fdb\u5821\u4e2d\u53bb\u89e3\u6551\u767d\u77f3\u9053\u4eba\u3002 \u3000\u3000\u7389\u7f57\u5239\u8f7b\u529f\u5353\u7edd\uff0c\u6765\u53bb\u65e0\u58f0\uff0c\u66f4\u517c\u4e00\u4f17\u9ad8\u624b\uff0c\u90fd\u5728\u524d\u9762\u770b\u5353\u4e00\u822a\u4e0e\u6210\u7ae0\u4e94\u53ca\u5929\u9f99\u4e0a\u4eba\u6bd4 \u8bd5\uff0c\u88ab\u5979\u795e\u4e0d\u5982\u9b3c\u4e0d\u89c9\u6e9c\u4eba\u5821\u4e2d\uff0c\u6b63\u82e6\u65bc\u4e0d\u5982\u767d\u77f3\u9053\u4eba\u56da\u5728\u4f55\u5904\uff0c\u5ffd\u89c1\u5899\u89d2\u6bcf\u9694\u4e0d\u8fdc\uff0c\u4fbf\u6709 \u9ec4\u6ce5\u6240\u753b\u7684\u7bad\u5934\uff0c\u7389\u7f57\u5239\u751a\u4e3a\u5947\u602a\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u77e5\u662f\u90a3\u4f4d\u9ad8\u624b\uff0c\u5148\u6211\u800c\u6765\uff1f\u201d\u4f9d\u7740\u7bad\u5934\uff0c\u4e00 \u8def\u627e\u53bb\uff0c\u679c\u7136\u627e\u5230\u4e86\u767d\u77f3\u9053\u4eba\u7684\u56da\u623f\uff0c\u7389\u7f57\u5239\u51fb\u6655\u770b\u5b88\uff0c\u5c06\u767d\u77f3\u9053\u4eba\u7684\u9563\u94d0\u524a\u65ad\uff0c\u61d2\u5f97\u542c\u4ed6 \u9053\u8c22\uff0c\u4fbf\u5148\u8dd1\u4e86\u51fa\u6765\u3002\u6b63\u9047\u7740\u5353\u4e00\u822a\u7b2c\u53c1\u638c\u5c06\u8981\u51fb\u4e0b\uff0c\u7389\u7f57\u5239\u4e58\u7740\u6df7\u4e71\u4e4b\u9645\uff0c\u5077\u53d1\u4e86\u4e00\u679a\u5979 \u7684\u72ec\u95e8\u6697\u5668\u201c\u4e5d\u661f\u5b9a\u5f62\u9488\u201d\u3002\u98de\u9488\u6781\u5c0f\uff0c\u5929\u9f99\u4e0a\u4eba\u53c8\u6b63\u5728\u5168\u795e\u8d2f\u6ce8\uff0c\u9632\u5353\u4e00\u822a\u7684\u7b2c\u53c1\u638c\uff0c\u56e0 \u6b64\u4e1d\u6beb\u6ca1\u6709\u53d1\u73b0\u3002 \u3000\u3000\u518d\u8bf4\u767d\u77f3\u9053\u4eba\u90a3\u65e5\u5728\u5927\u6c99\u6f20\u98ce\u7802\u4e4b\u9645\uff0c\u88ab\u5929\u9f99\u4e0a\u4eba\u4e0e\u970d\u5143\u4ef2\u5408\u529b\u6240\u64d2\uff0c\u56da\u5728\u5821\u4e2d\u591a\u65e5\uff0c \u6c14\u95f7\u975e\u5e38\uff0c\u53c8\u7a81\u7136\u88ab\u7389\u7f57\u5239\u6240\u6551\uff0c\u66f4\u662f\u96be\u4ee5\u4e3a\u60c5\uff0c\u51b2\u4e86\u51fa\u53bb\uff0c\u4fbf\u7acb\u523b\u5954\u5411\u5929\u9f99\u4e0a\u4eba\uff0c\u8981\u548c\u4ed6 \u518d\u51b3\u751f\u6b7b\u3002\u7389\u7f57\u5239\u5374\u8f7b\u8f7b\u4e00\u7b11\uff0c\u94c1\u638c\u4e00\u6325\uff0c\u51b7\u4e0d\u9632\u5c06\u767d\u77f3\u9053\u4eba\u6325\u51fa\u4e00\u4e08\u5f00\u5916\uff0c\u4ee4\u767d\u77f3\u9053\u4eba\u51e0 \u4e4e\u8dcc\u5012\u3002\u767d\u77f3\u9053\u4eba\u6599\u4e0d\u5230\u7389\u7f57\u5239\u6551\u4e86\u4ed6\u5374\u53c8\u4ee4\u4ed6\u5f53\u573a\u51fa\u4e11\uff0c\u77aa\u5927\u4e86\u773c\uff0c\u53ea\u542c\u5f97\u7389\u7f57\u5239\u51b7\u7b11 \u9053\uff1a\u201c\u767d\u77f3\u9053\u4eba\uff0c\u4f60\u4e0d\u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u4e56\u4e56\u7684\u7ad9\u8fc7\u4e00\u8fb9\u5427\uff01\u201d\u767d\u77f3\u9053\u4eba\u6c14\u5f97\u4e00\u4f5b\u51fa\u4e16\uff0c\u4e8c\u4f5b\u6d85 \u76d8\uff0c\u4f46\u4e00\u6765\u662f\u5979\u6240\u6551\uff0c\u4e8c\u6765\u5927\u654c\u5f53\u524d\uff0c\u5374\u4e5f\u4e0d\u6562\u56de\u5634\uff0c\u6ee1\u8154\u6012\u6c14\uff0c\u90fd\u8981\u5fcd\u7740\uff01 \u3000\u3000\u5929\u9f99\u4e0a\u4eba\u89c1\u7389\u7f57\u5239\u5a01\u52bf\uff0c\u4e5f\u81ea\u5fc3\u5bd2\uff0c\u4f46\u5f53\u7740\u4f17\u5f1f\u5b50\u9762\u524d\uff0c\u4ecd\u5f97\u786c\u7740\u5934\u76ae\u9a82\u9053\uff1a\u201c\u767d\u53d1\u9b54 \u5973\uff0c\u522b\u4eba\u6015\u4f60\uff0c\u6211\u4e0d\u6015\u4f60\uff01\u6765\uff0c\u6765\uff0c\u6765\uff0c\u4f5b\u7237\u548c\u4f60\u6597\u53c1\u767e\u56de\u5408\uff01\u201d\u7389\u7f57\u5239\u76c8\u76c8\u4e00\u7b11\uff0c\u4e1d\u6beb\u4e0d \u50cf\u8981\u548c\u4ed6\u5bf9\u654c\u7684\u6837\u5b50\uff0c\u5929\u9f99\u4e0a\u4eba\u6014\u4e86\u4e00\u6014\uff0c\u7834\u53e3\u9a82\u9053\uff1a\u201c\u4f5b\u7237\u662f\u767e\u70bc\u91d1\u521a\uff0c\u5c82\u4f60\u8fd9\u9b54\u5973\u6240\u80fd \u8bf1\u60d1\uff01\u201d\u4e0d\u6599\u7389\u7f57\u5239\u4e00\u7b11\u4e4b\u5f8c\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u7684\u4e0d\u6015\u6211\u9ebd\u4e86\u4f60\u771f\u7684\u662f\u767e\u70bc\u91d1\u521a\u9ebd\uff1f\u4f60\u8bd5 \u6478\u6478\u4f60\u8170\u810a\u9aa8\uff0c\u81ea\u4e0b\u6570\u4e0a\u7684\u7b2c\u4e03\u8282\u770b\uff01\u201d\u5929\u9f99\u4e0a\u4eba\u7531\u4e0d\u5f97\u4f38\u624b\u4e00\u6478\uff0c\u53ea\u89c9\u53c8\u75d2\u53c8\u75db\uff0c\u5927\u6012\u559d \u9053\uff1a\u201c\u4f60\u8fd9\u9b54\u5973\uff0c\u539f\u6765\u662f\u4f60\u6697\u7b97\u4f5b\u7237\uff01\u201d\u62d4\u51fa\u62c2\u5c18\uff0c\u4fbf\u60f3\u62da\u547d\uff0c\u7389\u7f57\u5239\u53c8\u662f\u8f7b\u8f7b\u4e00\u7b11\uff0c\u8bf4 \u9053\uff1a\u201c\u4f60\u4e2d\u4e86\u6211\u7684\u6697\u5668\uff0c\u82e5\u7136\u4e0d\u518d\u52a8\u6012\uff0c\u4e0d\u518d\u7528\u529b\uff0c\u56de\u53bb\u9759\u517b\u4e03\u4e03\u56db\u5341\u4e5d\u5929\uff0c\u4ee5\u4f60\u8fd9\u70b9\u9053 \u884c\uff0c\u8fd8\u53ef\u4ee5\u81ea\u5df1\u8fd0\u6c14\u5c06\u6697\u5668\u8feb\u51fa\u6765\u3002\u4f60\u82e5\u8fd8\u8981\u52a8\u6c14\uff0c\u4e0d\u5fc5\u6211\u518d\u51fa\u624b\uff0c\u53c1\u65e5\u4e4b\u5185\uff0c\u4fbf\u662f\u4f60\u7684\u6b7b \u671f\uff01\u201d\u8bf4\u5b8c\u4e4b\u5f8c\uff0c\u84e6\u7136\u53cd\u8138\u4e00\u559d\uff1a\u201c\u5ff5\u4f60\u662f\u4e00\u6d3e\u5b97\u4e3b\uff0c\u4fee\u7ec3\u4e0d\u6613\uff0c\u9976\u4f60\u4e00\u6b7b\uff0c\u4f60\u8fd8\u4e0d\u5feb\u6eda \u9ebd\uff1f\u201d\u8fd9\u4e00\u559d\u523a\u8033\u94bb\u5fc3\uff0c\u5929\u9f99\u4e0a\u4eba\u4e0d\u7531\u81ea\u5df1\u7684\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u5fc3\u60f3\uff1a\u6027\u547d\u4ea4\u5173\uff0c\u5b81\u53ef\u4fe1\u5176 \u6709\uff0c\u4e0d\u53ef\u4fe1\u5176\u65e0\uff0c\u56de\u8eab\u4fbf\u9000\uff0c\u5929\u9f99\u6d3e\u7684\u5f1f\u5b50\u4e00\u54c4\u800c\u6563\uff0c\u8ddf\u7740\u6559\u4e3b\u9003\u51fa\u98ce\u7802\u94c1\u5821\u3002 \u3000\u3000\u6210\u7ae0\u4e94\u6c14\u5f97\u9762\u8272\u9752\u767d\uff0c\u6599\u4e0d\u5230\u5929\u9f99\u4e0a\u4eba\u5982\u6b64\u8113\u5305\uff0c\u53ea\u89c1\u7389\u7f57\u5239\u773c\u73e0\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u53c8\u7b11 \u9053\uff1a\u201c\u98ce\u7802\u5821\u4e3b\uff0c\u4f60\u9080\u96c6\u4e86\u8fd9\u9ebd\u591a\u4eba\uff0c\u4e3a\u4f55\u8fd8\u4e0d\u52a8\u624b\uff1f\u54c8\uff0c\u795e\u5927\u5143\uff0c\u795e\u4e00\u5143\uff0c\u4f60\u8fd9\u4e24\u4e2a\u5b9d\u8d1d \u4e5f\u5728\u8fd9\u91cc\uff0c\u6211\u548c\u7239\u7239\u66fe\u4e24\u6b21\u94d9\u4f60\uff0c\u4eca\u756a\u53ef\u653e\u4f60\u4e0d\u8fc7\uff0c\u970d\u5143\u4ef2\uff0c\u4f60\u4e5f\u5728\u8fd9\u91cc\u9ebd\uff1f\u5357\u9ad8\u5cf0\u4e0a\u7684\u6559 \u8bad\uff0c\u4f60\u5c31\u8fd9\u6837\u5feb\u5fd8\u8bb0\u4e86\u9ebd\uff1f\u201d \u3000\u3000\u795e\u5927\u5143\u5927\u53eb\u9053\uff1a\u201c\u8fd9\u9b54\u5973\u5fc3\u72e0\u624b\u8fa3\uff0c\u800c\u4eca\u9a91\u864e\u53f9\u4e0b\uff0c\u5927\u5bb6\u548c\u5979\u62da\u5427\uff01\u201d\u6210\u7ae0\u4e94\u4e0d\u77e5\u5389 \u5bb3\uff0c\u628a\u624b\u4e00\u6325\uff0c\u5341\u51e0\u4e8c\u5341\u540d\u9ad8\u624b\u4e00\u62e5\u800c\u4e0a\uff0c\u7389\u7f57\u5239\u4e00\u58f0\u957f\u7b11\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u5237\u5237\u5237\u63a5\u8fde\u53c1\u5251\uff0c \u5c06\u53c1\u540d\u597d\u624b\u523a\u7ffb\u5730\u4e0a\uff0c\u6210\u7ae0\u4e94\u4e00\u6293\u6251\u4e0b\u7389\u7f57\u5239\u9053\uff1a\u201c\u597d\uff0c\u8bd5\u8bd5\u4f60\u7684\u9e70\u722a\u529f\u592b\uff01\u201d\u5de6\u638c\u5f80\u4e0a\u4e00 \u52fe\uff0c\u6210\u7ae0\u4e94\u864e\u53e3\u6d41\u8840\uff0c\u5267\u75db\u96be\u5f53\uff0c\u6323\u8131\u4e4b\u5f8c\uff0c\u5927\u6012\u559d\u9053\uff1a\u201c\u4f17\u5144\u5f1f\u4e00\u9f50\u56f4\u4e0a\uff0c\u7eb5\u7136\u8eab\u6b7b\uff0c\u4e0d \u80fd\u53d7\u8fb1\uff01\u201d\u5821\u5185\u7fa4\u8c6a\u867d\u7136\u4e2a\u4e2a\u5fc3\u60ca\uff0c\u5821\u4e3b\u4ee4\u4e0b\uff0c\u5374\u90fd\u89c4\u6b7b\u5982\u5f52\uff0c\u4eba\u4eba\u4e89\u4e0a\u3002 \u3000\u3000\u7389\u7f57\u5239\u70b9\u4e01\u70b9\u987d\uff0c\u5fc3\u9053\uff1a\u770b\u6765\u8fd9\u5821\u4e3b\u8fd8\u6df1\u5f97\u4eba\u5fc3\u3002\u526f\u5821\u4e3b\u662f\u70b9\u7a74\u540d\u5bb6\uff0c\u5224\u5b98\u7b14\u4e58\u7a7a\u5077 \u88ad\uff0c\u7389\u7f57\u5239\u76f4\u50cf\u80cc\u5f8c\u957f\u7740\u773c\u775b\uff0c\u53cd\u624b\u4e00\u70b9\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u4e5f\u8bd5\u8bd5\u4f60\u7684\u70b9\u7a74\u529f\u592b\uff01\u201d\u526f\u5821\u4e3b\u5927\u53eb \u4e00\u58f0\uff0c\u5f53\u573a\u8dcc\u5012\uff0c\u5821\u4e01\u6025\u5fd9\u5c06\u4ed6\u62ac\u51fa\u3002 \u3000\u3000\u8fd9\u65f6\u5821\u5185\u7fa4\u8c6a\u5df2\u5c06\u7389\u7f57\u5239\u3001\u767d\u77f3\u9053\u4eba\u3001\u5353\u4e00\u822a\uff0e\u4f55\u843c\u534e\u56db\u4eba\u90fd\u5305\u56f4\u8d77\u6765\u3002\u6210\u7ae0\u4e94\u7387\u795e\u5927 \u5143\u7b49\u4e03\u4eba\u540d\u4e00\u6d41\u9ad8\u624b\uff0c\u7d27\u7d27\u7f20\u7740\u7389\u7f57\u5239\uff0c\u7389\u7f57\u5239\u867d\u7136\u5389\u5bb3\uff0c\u5bf9\u65b9\u4eba\u6570\u592a\u591a\uff0c\u4e00\u65f6\u95f4\u5374\u4e5f\u51b2\u4e0d \u51fa\u53bb\u3002\u53ea\u4ed7\u7740\u7edd\u9876\u8f7b\u7075\u7684\u8eab\u6cd5\uff0c\u5728\u5175\u5203\u4ea4\u51fb\u7f1d\u4e2d\uff0c\u7a7f\u6765\u63d2\u53bb\uff0c\u4e00\u6709\u673a\u4f1a\uff0c\u4fbf\u7acb\u523b\u5c06\u6b66\u529f\u8f83\u5f31 \u7684\u523a\u7ffb\u5730\u4e0a\uff0c\u970e\u65f6\u95ee\u53f7\u53eb\u4e4b\u58f0\u56db\u8d77\uff0c\u6210\u7ae0\u4e94\u6c14\u7ea2\u4e86\u773c\uff0c\u7d27\u7d27\u5305\u56f4\uff0c\u6b7b\u6218\u4e0d\u653e\u3002\u767d\u77f3\u9053\u4eba\u5728\u4eba \u4e1b\u4e2d\u8ffd\u89c5\u970d\u5143\u4ef2\uff0c\u5353\u4e00\u822a\u5219\u56e0\u4f55\u843c\u534e\u6b66\u529f\u6700\u5f31\uff0c\u4e00\u67c4\u5251\u9f99\u98de\u51e4\u821e\uff0c\u7d27\u7d27\u508d\u5728\u4f55\u843c\u534e\u8eab\u8fb9\u3002 \u3000\u3000\u6df7\u6218\u4e2d\uff0c\u7389\u7f57\u5239\u6570\u5ea6\u5728\u5353\u4e00\u822a\u8eab\u8fb9\u7a7f\u8fc7\uff0c\u770b\u4e5f\u4e0d\u770b\u4ed6\u4e00\u773c\uff0c\u5353\u4e00\u822a\u8fde\u58f0\u53eb\u9053\uff1a\u201c\u7ec3\u59d0 \u59d0\uff0c\u7ec3\u59d0\u59d0\uff01\u201d\u7389\u7f57\u5239\u632f\u5251\u529b\u6218\uff0c\u6beb\u4e0d\u7406\u776c\u3002\u6fc0\u6218\u4e2d\u5353\u4e00\u822a\u4e0d\u6562\u5206\u5fc3\uff0c\u4e0d\u80fd\u89e3\u91ca\uff0c\u53ea\u6709\u5fc3\u4e2d \u6697\u81ea\u60b2\u54c0\u3002 \u3000\u3000\u767d\u77f3\u9053\u4eba\u5728\u4eba\u4e1b\u4e2d\u89c5\u7740\u4e86\u970d\u5143\u4ef2\uff0c\u4e00\u809a\u5b50\u6c14\u90fd\u53d1\u5728\u4ed6\u8eab\u4e0a\uff0c\u8fd0\u5251\u5982\u98ce\uff0c\u72e0\u72e0\u8ffd\u51fb\u3002\u5c82\u77e5 \u970d\u5143\u4ef2\u8eab\u624b\u4e5f\u751a\u4e0d\u5f31\uff0c\u5373\u7b97\u4ee5\u4e00\u5bf9\u4e00\uff0c\u4ed6\u867d\u7565\u900a\u65bc\u767d\u77f3\u9053\u4eba\uff0c\u4e5f\u53ef\u62b5\u6321\u767e\u6570\u5341\u62db\uff0c\u4f55\u51b5\u5728\u4f17 \u5be1\u76f8\u654c\u7684\u60c5\u51b5\u4e0b\uff0c\u767d\u77f3\u9053\u4eba\u66f4\u4e0d\u6613\u5f97\u624b\uff0c\u65b9\u6597\u4e86\u4e94\u4e03\u62db\uff0c\u54c8\u8428\u514b\u7684\u540d\u6b66\u5e08\u9686\u547c\u96c5\u56fe\u659c\u523a\u51b2 \u5230\uff0c\u624b\u4e3e\u94c1\u690e\uff0c\u5f53\u5934\u75be\u5288\uff0c\u9686\u547c\u96c5\u56fe\u529f\u592b\u4e0d\u5728\u6210\u7ae0\u4e94\u4e4b\u4e0b\uff0c\u4e00\u8fde\u53c1\u690e\uff0c\u6253\u5f97\u767d\u77f3\u9053\u4eba\u624b\u5fd9\u811a \u4e71\uff0c\u970d\u5143\u4ef2\u4e58\u52bf\u5237\u5237\u4e24\u97ad\uff0c\u8fde\u62bd\u767d\u77f3\u9053\u4eba\u5de6\u53f3\u8170\u80cc\uff0c\u5c06\u767d\u77f3\u9053\u4eba\u8863\u88f3\u6253\u5f97\u788e\u6210\u5c0f\u7247\uff0c\u8170\u80cc\u6cdb \u8d77\u4e24\u9053\u8840\u75d5\uff0c\u970d\u5143\u4ef2\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u4e24\u97ad\u8fd8\u4e24\u5251\uff0c\u4e0d\u6536\u4f60\u7684\u5229\u606f\u4e86\uff01\u201d\u6536\u97ad\u95ef\u51fa\u4eba\u4e1b\uff0c\u4e00\u6e9c\u70df \u822c\u5982\u98de\u6e9c\u8d70\u3002\u4ece\u6b64\u9690\u5c45\uff0c\u518d\u4e5f\u4e0d\u7406\u95f2\u4e8b\u3002 \u3000\u3000\u767d\u77f3\u9053\u4eba\u6c14\u70b8\u5fc3\u80ba\uff0c\u72c2\u51b2\u731b\u523a\uff0c\u4f24\u4e86\u4e24\u4eba\uff0c\u5374\u53c8\u88ab\u9686\u547c\u96c5\u56fe\u6321\u7740\uff0c\u7389\u7f57\u5239\u53eb\u9053\uff1a\u201c\u4f60\u8fd8 \u4e0d\u5feb\u5feb\u56de\u6765\u4e0e\u6211\u4eec\u8054\u624b\uff0c\u60f3\u627e\u6b7b\u9ebd\uff1a\u201c\u767d\u77f3\u9053\u4eba\u53cc\u77b3\u55b7\u8840\uff0c\u504f\u4e0d\u95ef\u56de\uff0c\u88ab\u9686\u547c\u96c5\u56fe\u8054\u5408\u51e0\u4e2a \u9ad8\u624b\u4e00\u9635\u731b\u653b\uff0c\u9669\u8c61\u73af\u751f\uff0c\u51e0\u906d\u4e0d\u6d4b\uff0c\u5353\u4e00\u822a\u4f55\u843c\u534e\u53cc\u5251\u62a2\u6551\uff0c\u5353\u4e00\u822a\u8fd9\u65f6\u7684\u6b66\u529f\u5df2\u5728\u5e08\u53d4 \u4e4b\u4e0a\uff0c\u4e00\u8fde\u51e0\u62db\u8fbe\u6469\u5251\u5f0f\uff0c\u602a\u5f02\u72e0\u75be\uff0c\u4f24\u4e86\u51e0\u4eba\uff0c\u62a2\u5230\u767d\u77f3\u9053\u4eba\u8eab\u8fb9\uff0c\u7389\u7f57\u5239\u770b\u4e86\u4e5f\u6697\u6697\u79f0 \u8d5e\uff0c\u4f46\u4ea6\u6015\u4ed6\u6709\u5931\uff0c\u6025\u5fd9\u6740\u5f00\u6761\u8def\uff0c\u53c8\u548c\u767d\u77f3\u9053\u4eba\u7b49\u8054\u5728\u4e00\u8d77\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u9f99\u6d3e\u7684\u6e9c\u8d70\u65bc\u524d\uff0c\u970d\u5143\u4ef2\u6e9c\u8d70\u65bc\u5f8c\uff0c\u98ce\u7802\u5821\u8fd9\u8fb9\uff0c\u5b9e\u529b\u5927\u51cf\u3002\u6fc0\u6218\u4e2d\u5353\u4e00\u822a\u53c8\u53eb \u4e86\u4e24\u58f0\u201c\u7ec3\u59d0\u59d0\uff01\u201d\u7389\u7f57\u5239\u5ffd\u9053\uff1a\u201c\u4e00\u822a\uff0c\u597d\u597d\u62a4\u536b\u4f60\u7684\u5e08\u53d4\uff0c\u4e0d\u8981\u8ba9\u4ed6\u518d\u7ed9\u4eba\u4f24\u4e86\u3002\u201d\u5353 \u4e00\u822a\u5ffd\u542c\u5f97\u5979\u51fa\u58f0\u7b54\u8bdd\uff0c\u5982\u5949\u7eb6\u97f3\uff0c\u4e0d\u6687\u7ec6\u60f3\uff0c\u614c\u5fd9\u7b54\u9053\uff1a\u201c\u662f\uff01\u6211\u542c\u59d0\u59d0\u5429\u5490\uff0c\u4e0d\u80fd\u518d\u8ba9 \u5e08\u53d4\u7ed9\u4eba\u4f24\u4e86\uff1f\u201d\u767d\u77f3\u9053\u4eba\u53cc\u773c\u7ffb\u767d\uff0c\u51e0\u4e4e\u6c14\u6b7b\uff01\u4f55\u843c\u534e\u8fde\u95ee\u4ed6\u4e24\u58f0\uff1a\u201c\u7239\uff0c\u4f60\u7684\u4f24\u788d\u4e8b \u9ebd\uff1f\u201d\u4ed6\u4e5f\u5982\u542c\u800c\u4e0d\u95fb\uff0c\u95ed\u5634\u4e0d\u7b54\u3002\u4f55\u843c\u534e\u89c1\u4ed6\u795e\u8272\u9a87\u4eba\uff0c\u4f4e\u4f4e\u5bf9\u5353\u4e00\u822a\u9053\uff1a\u201c\u7239\u4f3c\u662f\u75af \u4e86\u3002\u54b1\u4eec\u7d27\u62a4\u7740\u4ed6\uff01\u201d\u5353\u4e00\u822a\u70b9\u4e86\u70b9\u5934\uff0c\u4e00\u67c4\u5251\u592d\u77eb\u5982\u9f99\uff0c\u4e0d\u79bb\u767d\u77f3\u9053\u4eba\u8eab\u5f8c\u3002 \u3000\u3000\u7389\u7f57\u5239\u5631\u5490\u4e86\u5353\u4e00\u822a\u4e4b\u5f8c\uff0c\u4e00\u58f0\u957f\u7b11\uff0c\u811a\u5c16\u4e00\u70b9\uff0c\u8eab\u5b50\u7a81\u7136\u817e\u7a7a\u98de\u4e86\u8d77\u6765\uff0c\u4ece\u6210\u7ae0\u4e94\u7b49 \u4eba\u7684\u5934\u9876\u98de\u8d8a\u8fc7\u53bb\uff0c\u5728\u534a\u7a7a\u4e2d\u633d\u4e86\u4e2a\u5251\u82b1\uff0c\u5411\u795e\u5927\u5143\u731b\u523a\uff0c\u795e\u5927\u5143\u5413\u5f97\u614c\u4e86\uff0c\u56de\u8eab\u4e00\u907f\uff0c\u53cd \u624b\u4e00\u6293\uff0c\u795e\u5927\u5143\u7684\u91ce\u72d0\u62f3\u672c\u6765\u4e5f\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u5389\u5bb3\u975e\u51e1\u3002\u53ef\u662f\u7389\u7f57\u5239\u81ea\u5230\u585e\u5916\u4e4b\u5f8c\uff0c\u6f5c\u5fc3\u7814 \u4e60\u5e08\u7236\u6240\u7559\u4e0b\u7684\u5251\u8c31\uff0c\u5251\u6cd5\u5df2\u5230\u51fa\u795e\u5165\u5316\u4e4b\u5883\uff0c\u795e\u5927\u5143\u6251\u524d\u4e00\u6293\uff0c\u88ab\u5979\u4e58\u52bf\u4e00\u5251\uff0c\u76f4\u900f\u5f8c \u5fc3\uff0c\u795e\u4e00\u5143\u8981\u5f85\u8d70\u65f6\uff0c\u53c8\u88ab\u5979\u671d\u7740\u5f8c\u5fc3\u4e00\u8e22\uff0c\u987f\u65f6\u5455\u51fa\u9ed1\u8840\uff0c\u4ec6\u5730\u8eab\u4ea1\uff01 \u3000\u3000\u7389\u7f57\u5239\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u98ce\u7802\u5821\u4e3b\uff0c\u795e\u5bb6\u5144\u5f1f\u6bd4\u4f60\u5982\u4f55\uff1f\u4f60\u5c1a\u4e0d\u5982\u8fdb\u9000\uff0c\u6211\u53ef\u8981\u5927\u5f00\u6740\u6212 \u4e86\uff01\u201d\u6210\u7ae0\u4e94\u6012\u9053\uff1a\u201c\u6211\u5c82\u662f\u754f\u6b7b\u4e4b\u4eba\uff01\u201d\u7adf\u7136\u8fce\u7740\u7389\u7f57\u5239\u5251\u5c16\uff0c\u6325\u638c\u731b\u51fb\uff01 \u3000\u3000\u7389\u7f57\u5239\u80a9\u5934\u4e00\u7f29\uff0c\u5de6\u624b\u8f7b\u8f7b\u4e00\u5e26\uff0c\u6210\u7ae0\u4e94\u811a\u6b65\u4e0d\u7a33\uff0c\u8e09\u8e09\u8dc4\u8dc4\u7684\u51b2\u8fc7\u4e00\u8fb9\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c \u7389\u7f57\u5239\u53c8\u523a\u4f24\u4e86\u6570\u4eba\uff0c\u6210\u7ae0\u6b63\u5fc3\u4e2d\u5927\u75db\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u6740\u4f24\u6211\u4e00\u4f17\u5144\u5f1f\uff0c\u6211\u4e0e\u4f60\u662f\u9664\u6b7b\u65b9\u4f11\uff01 \u4f60\u4e0d\u5fc5\u624b\u4e0b\u7559\u60c5\uff0c\u6740\u8fc7\u6765\u5427\uff0c\u6211\u6b7b\u4e5f\u5f97\u4e0e\u4f17\u5144\u5f1f\u540c\u6b7b\u3002\u201d\u7389\u7f57\u5239\u8eab\u5f62\u5feb\u6781\uff0c\u970e\u5ffd\u4e4b\u95f4\uff0c\u53c8\u4f24 \u4e86\u51e0\u4eba\uff0c\u6210\u7ae0\u4e94\u8ffd\u4e4b\u4e0d\u53ca\uff0c\u60f3\u4e0e\u5979\u62da\u6b7b\uff0c\u4e5f\u4e0d\u53ef\u80fd\u3002 \u3000\u3000\u7389\u7f57\u5239\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u98ce\u7802\u5821\u4e3b\uff0c\u6211\u4f55\u66fe\u6740\u4e86\u4f60\u7684\u5f1f\u5144\uff1f\u201d\u6210\u7ae0\u4e94\u6124\u6012\u4e4b\u6781\uff0c\u671b\u7740\u6ee1\u573a\u7ffb \u6eda\u547b\u541f\u7684\u5f1f\u5144\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u9b54\u5973\u8fd8\u8bf4\u98ce\u51c9\u7684\u8bdd\u513f\uff01\u201d\u7eb5\u8eab\u8ffd\u5979\uff0c\u5ffd\u542c\u5f97\u4e00\u9635\u6728\u9c7c\u58f0 \u54cd\uff0c\u201c\u963f\u5c14\u9640\u4f5b\u201d\u4e4b\u58f0\u5728\u8033\u8fb9\u54cd\u4e86\u8d77\u6765\uff0c\u6210\u7ae0\u4e94\u7eb5\u76ee\u4e00\u671b\uff0c\u53ea\u89c1\u4e00\u4e2a\u548c\u5c1a\u4e0d\u77e5\u4ec0\u9ebd\u65f6\u5019\u8d70\u4e86 \u8fdb\u6765\uff0c\u6c89\u58f0\u5ff5\u9053\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff0c\u51a4\u5bb6\u5b9c\u89e3\u4e0d\u5b9c\u7ed3\uff0c\u8bf7\u5feb\u505c\u4e86\u5e72\u6208\u65ab\u4f10\u4e4b\u58f0\uff01\u201d \u3000\u3000\u6210\u7ae0\u4e94\u9080\u6765\u7684\u5929\u5c71\u5357\u5317\u9ad8\u624b\uff0c\u6709\u8fc7\u534a\u8ba4\u8bc6\u8fd9\u4e2a\u9ad8\u50e7\uff0c\u4e0d\u7981\u540c\u58f0\u547c\u9053\uff1a\u201c\u6666\u660e\u5e08\uff01\u8fd9\u9b54\u5973 \u6740\u4eba\u5982\u8349\uff0c\u8bf7\u5feb\u6765\u76f8\u52a9\uff01\u201d\u7389\u7f57\u5239\u5fae\u5fae\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5cb3\u545c\u73c2\uff0c\u539f\u6765\u662f\u4f60\uff01\u201d \u3000\u3000\u4f17\u4eba\u89c1\u7389\u7f57\u5239\u548c\u6666\u660e\u5e08\u62db\u547c\uff0c\u66f4\u662f\u5403\u60ca\u3002\u6666\u660e\u5e08\u51fb\u4e86\u4e00\u4e0b\u6728\u9c7c\uff0c\u5408\u4ec0\u8bf4\u9053\uff1a\u201c\u963f\u5f25\u9640 \u4f5b\uff0c\u4e24\u8fb9\u90fd\u505c\u624b\u4e86\u5427\uff01\u201d \u3000\u3000\u6666\u660e\u5e08\u5230\u5929\u5c71\u5df2\u6709\u516b\u5e74\uff0c\u6b66\u529f\u65e2\u662f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u4eba\u53c8\u8c26\u548c\u5e73\u6613\uff0c\u5929\u5c71\u5357\u5317\u82f1\u96c4\u65e0\u4e0d\u670d\u4ed6\u3002 \u89c1\u4ed6\u4e00\u8bf4\uff0c\u7eb7\u7eb7\u8df3\u51fa\u5708\u5b50\uff0c\u53ea\u6709\u6210\u7ae0\u4e94\u8fd8\u4e0d\u80af\u5e72\u4f11\uff0c\u62ab\u5934\u6563\u53d1\uff0c\u72e0\u72e0\u8ffd\u51fb\uff0c\u8981\u548c\u7389\u7f57\u5239\u62da \u547d\u3002\u6666\u660e\u5e08\u5408\u4ec0\u558a\u9053\uff1a\u201c\u5821\u4e3b\u4f4f\u624b\uff0c\u5979\u5e76\u6ca1\u6709\u8bf4\u9519\uff0c\u4f60\u624b\u4e0b\u5f1f\u5144\uff0c\u5e76\u65e0\u4e00\u4eba\u4e27\u547d\u3002\u4f24\u4e86\u7684\u6211 \u66ff\u4f60\u6551\uff0c\u8bf7\u77a7\u5728\u8d2b\u50e7\u9762\u4e0a\uff0c\u4f4f\u624b\u4e86\u5427\uff01\u201d \u3000\u3000\u98ce\u7802\u5821\u4e3b\u6115\u7136\u4f4f\u624b\uff0c\u9053\uff1a\u201c\u4f24\u5f97\u5982\u6b64\u4e4b\u91cd\uff0c\u8fd8\u80fd\u4e2a\u4e2a\u90fd\u6551\u6d3b\u5417\uff1f\u201d\u6666\u660e\u5e08\u9053\uff1a\u201c\u5979\u867d\u53f7 \u79f0\u9b54\u5973\uff0c\u5176\u5b9e\u5fc3\u4e2d\u5374\u8fd8\u5b58\u7740\u4e00\u70b9\u6148\u60b2\u3002\u5979\u7684\u5251\u5c16\u523a\u7684\u90fd\u662f\u5173\u8282\uff1b\u867d\u7136\u4e0d\u80fd\u8d77\u7acb\uff0c\u5374\u975e\u81f4\u547d\u4e4b \u5904\u3002\u6211\u6709\u4e0a\u597d\u5929\u5c71\u96ea\u83b2\u914d\u5236\u6210\u7684\u78a7\u7075\u4e39\uff0c\u5f00\u6c34\u5185\u670d\u5916\u6577\uff0c\u75db\u695a\u7acb\u5931\uff0c\u4e0d\u987b\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4fbf\u53ef\u884c \u52a8\u5982\u5e38\u3002\u201d \u3000\u3000\u6666\u660e\u5e08\u53d6\u51fa\u4e86\u6570\u5341\u9897\u78a7\u7075\u4e39\uff0c\u4ea4\u4e0e\u672a\u4f24\u4e4b\u4eba\uff0c\u53eb\u4ed6\u4eec\u4e00\u540c\u6551\u6cbb\u4f24\u8005\uff0c\u7247\u523b\u4e4b\u5f8c\uff0c\u679c\u7136\u4e00 \u4e2a\u4e2a\u90fd\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u7389\u7f57\u5239\u7b11\u9053\uff1a\u201c\u9e23\u73c2\uff0c\u8fd9\u6b21\u53c8\u662f\u6211\u906d\u4eba\u9a82\uff0c\u4f60\u5145\u597d\u4eba\u4e86\u3002\u4f60\u522b\u5f97\u610f\uff0c\u5c06\u6765\u6211\u8fd8\u8981\u4e0e\u4f60\u6bd4 \u5251\uff01\u201d \u3000\u3000\u6210\u7ae0\u4e94\u5ffd\u7136\u5411\u7389\u7f57\u5239\u515c\u5934\u4e00\u63d6\uff0c\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u4eca\u65e5\u6211\u65b9\u77e5\u5929\u5916\u6709\u5929\uff0c\u8fd9\u9999\u5802\u6211\u51b3\u628a\u5b83 \u6563\u4e86\uff0c\u4ece\u6b64\u4e0d\u518d\u4e89\u5f3a\uff01\u6211\u8fd8\u8981\u8c22\u4f60\u624b\u4e0b\u7559\u60c5\uff01\u201d \u3000\u3000\u6666\u660e\u5e08\u7b11\u9053\uff1a\u201c\u77a7\uff0c\u8fd9\u4e0d\u662f\u6709\u4eba\u5411\u4f60\u9053\u597d\u4e86\uff1f\u201d\u56de\u5934\u5411\u5353\u4e00\u822a\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u4e8b\u60c5\u5df2\u4e86\uff0c \u8d2b\u50e7\u4e5f\u8be5\u8d70\u4e86\uff01\u4f60\u4eec\u8fd9\u5bf9\u6b22\u559c\u51a4\u5bb6\uff0c\u4e5f\u8be5\u548c\u597d\u4e86\u5427\uff1f\u201d\u8bdd\u521a\u8bf4\u5b8c\uff0c\u5ffd\u89c1\u7389\u7f57\u5239\u9762\u8272\u5927\u53d8\uff0c\u5389 \u58f0\u559d\u9053\uff1a\u201c\u5353\u4e00\u822a\uff0c\u4f60\u8fd9\u6b66\u5f53\u6d3e\u7684\u5f97\u610f\u5f1f\u5b50\uff0c\u8fd8\u4e0d\u968f\u4f60\u5e08\u53d4\u56de\u5c71\u53bb\u9ebd\uff1f\u201d\u5353\u4e00\u822a\u9a87\u9053\uff1a\u201c\u59d0 \u59d0\uff0c\u4f60\u542c\u6211\u8bf4\u2026\u2026\u201d\u788d\u65bc\u767d\u77f3\u9053\u4eba\u7236\u5973\u5728\u65c1\uff0c\u4e0d\u597d\u89e3\u91ca\u90a3\u665a\u4e4b\u4e8b\uff0c\u5450\u5450\u8bf4\u9053\uff1a\u201c\u59d0\u59d0\uff0c\u4e0d\u7ba1 \u4f60\u5bf9\u6211\u5982\u4f55\uff0c\u6211\u5df2\u662f\u51b3\u5fc3\u7ec8\u8001\u8fb9\u8352\uff0c\u8ffd\u968f\u4f60\u4e86\uff01\u201d\u7389\u7f57\u5239\u51b7\u51b7\u4e00\u7b11\uff0c\u5ffd\u89c1\u767d\u77f3\u9053\u4eba\u53cc\u988a\u706b \u7ea2\uff0c\u7a81\u7136\u671d\u5979\u4e00\u63d6\uff01 \u3000\u3000\u7389\u7f57\u5239\u4e00\u95ea\u95ea\u5f00\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u6211\u4e43\u90aa\u6d3e\u9b54\u5973\uff0c\u600e\u6562\u53d7\u6b66\u5f53\u4e94\u8001\u4e4b\u62dc\uff01\u201d\u767d\u77f3\u9053\u4eba\u54d1\u58f0\u53eb \u9053\uff1a\u201c\u8fd9\u4e00\u62dc\u662f\u8c22\u4f60\u76f8\u6551\u4e4b\u6069\uff0c\u4f46\u6211\u4e5f\u4e0d\u767d\u9886\u4f60\u7684\u60c5\u3002\u6211\u4eec\u672c\u6765\u8981\u4e00\u822a\u56de\u5c71\u638c\u95e8\uff0c\u73b0\u5728\u6211\u4e00 \u80a9\u62c5\u8d77\uff0c\u5c06\u4ed6\u8ba9\u4e0e\u4f60\u4e86\u3002\u4e00\u822a\uff0c\u4ece\u6b64\u4f60\u4e0e\u6b66\u5f53\u6d3e\u4e24\u65e0\u5e72\u7cfb\uff0c\u7ec8\u751f\u670d\u4f8d\u4f60\u7684\u7ec3\u59d0\u59d0\u5427\uff01\u201d\u5353\u4e00 \u822a\u55eb\u5685\u8bf4\u9053\uff1a\u201c\u5e08\u53d4\uff0c\u8fd9\u662f\u4ec0\u9ebd\u8bdd\uff1f\u201d \u3000\u3000\u767d\u77f3\u9053\u4eba\u643a\u4e86\u5973\u513f\u5982\u98de\u5954\u8dd1\uff0c\u7389\u7f57\u5239\u8fde\u8fde\u51b7\u7b11\uff0c\u4f55\u843c\u534e\u5374\u56de\u5934\u9053\uff1a\u201c\u7389\u7f57\u5239\uff0c\u4f60\u53ef\u5f97\u597d \u597d\u5f85\u6211\u5927\u54e5\uff0c\u4e0d\u8981\u901e\u5f3a\u6b3a\u8d1f\u4ed6\uff01\u201d\u7389\u7f57\u5239\u5fae\u5fae\u4e00\u6115\uff0c\u6b32\u5f85\u95ee\u65f6\uff0c\u4f55\u843c\u534e\u5df2\u968f\u767d\u77f3\u9053\u4eba\u5954\u51fa\u3002 \u3000\u3000\u5353\u4e00\u822a\u5446\u82e5\u6728\u9e21\uff0c\u4ed6\u53d7\u7d2b\u9633\u9053\u957f\u683d\u57f9\u629a\u80b2\uff0c\u867d\u7136\u5341\u591a\u5e74\u6765\uff0c\u56e0\u4e0e\u7389\u7f57\u5239\u76f8\u604b\u4e4b\u4e8b\uff0c\u4e3a\u540c \u95e8\u6240\u4e0d\u8c05\uff0c\u53ef\u662f\u4e00\u5fc3\u90fd\u8fd8\u60f3\u62a5\u7b54\u672c\u95e8\uff0c\u5c82\u6599\u767d\u77f3\u5e08\u53d4\u5374\u8981\u628a\u4ed6\u9010\u51fa\u95e8\u5899\uff0c\u8fd9\u600e\u80fd\u4e0d\u4ee4\u4ed6\u5fc3 \u75db\u3002\u4ed6\u5374\u6ca1\u6709\u60f3\u5230\uff0c\u4ed6\u7684\u638c\u95e8\uff0c\u6709\u7531\u540c\u95e8\u516c\u51b3\uff0c\u624d\u80fd\u514d\u6389\u3002\u767d\u77f3\u9053\u4eba\u6839\u672c\u6ca1\u6709\u6743\u529b\u5c06\u4ed6\u9010\u51fa \u95e8\u5899\u3002 \u3000\u3000\u7389\u7f57\u5239\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\uff0c\u5353\u4e00\u822a\u5982\u68a6\u521d\u9192\uff0c\u5954\u4e0a\u53bb\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\uff0c\u4f60\u53ef\u660e\u767d\u4e86\u9ebd\uff1f\u90a3\u665a\u4e4b \u4e8b\uff0c\u5b9e\u5728\u662f\u4e2a\u5927\u5927\u7684\u8bef\u4f1a\uff01\u201d \u3000\u3000\u7389\u7f57\u5239\u5fc3\u7070\u5df2\u6781\uff0c\u60f3\u8d77\u5341\u591a\u5e74\u6765\u7684\u6ce2\u6298\uff0c\u5982\u4eca\u5934\u53d1\u4e5f\u767d\u4e86\uff0c\u7eb5\u8bb8\u518d\u6210\u9e33\u4fa3\u4e5f\u6ca1\u6709\u4ec0\u9ebd\u610f \u601d\u3002\u7389\u7f57\u5239\u7684\u60f3\u6cd5\u5c31\u5f02\u4e4e\u5bfb\u5e38\u5973\u5b50\uff0c\u5728\u5979\u60f3\u548c\u5353\u4e00\u822a\u8c08\u8bba\u5a5a\u5ac1\u4e4b\u65f6\uff0c\u4fbf\u4e00\u5fc3\u6392\u9664\u4e07\u96be\uff0c\u4e0d\u987e \u4e00\u5207\u3002\u5230\u5982\u4eca\u51e0\u5ea6\u4f24\u5fc3\u4e4b\u5f8c\uff0c\u5979\u89c9\u5f97\u5a5a\u5ac1\u5df2\u662f\u6ca1\u6709\u610f\u601d\uff0c\u4e5f\u5c31\u4e0d\u613f\u518d\u542c\u5353\u4e00\u822a\u89e3\u91ca\uff0c\u5b81\u613f\u7559 \u4e00\u70b9\u672a\u4e86\u4e4b\u60c5\uff0c\u5f7c\u6b64\u76f8\u5fc6\u4e86\uff01 \u3000\u3000\u5353\u4e00\u822a\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53ea\u89c1\u7389\u7f57\u5239\u5df2\u98d8\u7136\u800c\u53bb\uff0c\u5353\u4e00\u822a\u72c2\u547c\u8ffd\u8d76\uff0c\u90a3\u91cc\u8ffd\u8d76\u5f97\u4e0a\uff1f\u4f46\u89c1\u5929\u4e0a \u662f\u803f\u803f\u94f6\u6cb3\uff0c\u5730\u4e0b\u662f\u9ec4\u6c99\u6f20\u6f20\uff0c\u7389\u7f57\u5239\u7684\u5f71\u5b50\u53c8\u4e0d\u89c1\u4e86\uff01 \u3000\u3000\u5353\u4e00\u822a\u5931\u58f0\u75db\u54ed\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u5ffd\u89c9\u6709\u4eba\u8f7b\u8f7b\u629a\u81ea\u5df1\u80a9\u80cc\uff0c\u8f7b\u8f7b\u8bf4\u9053\uff1a\u201c\u60c5\u5b7d\uff0c\u60c5\u5b7d\uff01\u201d \u6666\u660e\u5e08\u4e00\u76f4\u5c31\u8ddf\u5728\u4ed6\u7684\u8eab\u5f8c\uff0c\u8ba9\u4ed6\u54ed\u5f97\u591f\u4e86\uff0c\u8fd9\u624d\u51fa\u58f0\u6170\u89e3\u3002 \u3000\u3000\u5353\u4e00\u822a\u9ed8\u7136\u4e0d\u8bed\uff0c\u548c\u6666\u660e\u5e08\u5728\u6c99\u6f20\u8d70\u4e86\u4e00\u7a0b\uff0c\u8fd9\u624d\u8bf4\u9053\uff1a\u201c\u7ec3\u59d0\u59d0\u6b64\u53bb\uff0c\u4ee5\u5f8c\u76f8\u89c1\u66f4\u96be \u4e86\uff01\u201d\u62ac\u5934\u671b\u5929\uff0c\u5929\u4e0a\u53cc\u661f\u95ea\u8000\uff0c\u731b\u7136\u8bb0\u8d77\uff0c\u4eca\u591c\u6b63\u662f\u4e03\u5915\u4f73\u671f\uff0c\u53c8\u4e0d\u7981\u6005\u7136\u53f9\u9053\uff1a\u201c\u5929\u4e0a \u9e4a\u6865\u805a\u4f1a\uff0c\u4eba\u95f4\u52b3\u71d5\u5206\u98de\uff0c\u8001\u5929\u7237\u4e5f\u672a\u514d\u592a\u4f5c\u5f04\u6211\u4e86\uff01\u201d \u3000\u3000\u6666\u660e\u5e08\u4e5f\u62ac\u8d77\u4e86\u5934\uff0c\u770b\u725b\u90ce\u7ec7\u5973\u661f\u5189\u5189\u63a0\u8fc7\u5929\u7a7a\uff0c\u5ffd\u7136\u95f4\u9053\uff1a\u201c\u4f60\u9971\u8bfb\u8bd7\u8bcd\uff0c\u53ef\u8bb0\u5f97\u79e6 \u5c11\u6e38\u548f\u4e03\u5915\u7684\u201c\u9e4a\u6865\u4ed9\u201d\u4e00\u8bcd\u9ebd\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u60c5\u6000\u6005\u89e6\uff0c\u4f4e\u58f0\u541f\u9053\uff1a \u3000\u3000\u7ea4\u4e91\u5f04\u5de7\uff0c\u98de\u661f\u4f20\u6068\uff0c\u94f6\u6c49\u8fe2\u8fe2\u6697\u5ea6\u3002\u91d1\u98ce\u7389\u9732\u4e00\u76f8\u9022\uff0c\u4fbf\u80dc\u5374\u4eba\u95f4\u65e0\u6570\u3002 \u3000\u3000\u67d4\u60c5\u4f3c\u6c34\uff0c\u4f73\u671f\u5982\u68a6\uff0c\u5fcd\u987e\u9e4a\u6865\u5f52\u8def\uff0c\u4e24\u60c5\u82e5\u662f\u4e45\u957f\u65f6\uff0c\u53c8\u5c82\u5728\u671d\u671d\u66ae\u66ae\u3002 \u3000\u3000\u6666\u660e\u5e08\u9053\uff1a\u201c\u53ef\u4e0d\u662f\u9ebd\uff1f\u82e5\u5979\u8fd8\u5bf9\u4f60\u6709\u60c5\uff0c\u53c8\u4f55\u5fc5\u671d\u66ae\u76f8\u5904\u3002\u4eba\u95f4\u767e\u5e74\uff0c\u5929\u4e0a\u4e00\u77ac\uff0c\u4f60 \u82e5\u4f5c\u5982\u662f\u89c2\uff0c\u5219\u4e24\u60c5\u76f8\u8c05\u4e4b\u65e5\uff0c\u4e5f\u5e76\u975e\u5730\u4e45\u5929\u957f\uff01\u201d\u4e24\u4eba\u8e0f\u7740\u661f\u5149\uff0c\u7a7f\u8fc7\u6c99\u6f20\uff0c\u725b\u90ce\u7ec7\u5973\u661f \u5347\u8d77\u4e86\u53c8\u843d\u4e0b\u4e86\uff01 \u3000\u3000\u7ecf\u8fc7\u98ce\u7802\u94c1\u5821\u4e00\u6218\uff0c\u767d\u53d1\u9b54\u5973\u5a01\u540d\u8fdc\u64ad\uff0c\u5929\u5c71\u5357\u5317\uff0c\u65e0\u4eba\u6562\u518d\u60f9\u5979\uff0c\u4f46\u5927\u6f20\u8349\u539f\uff0c\u5374\u4e5f \u518d\u96be\u89c1\u5979\u7684\u5f71\u5b50\uff0c\u5979\u5df2\u9690\u5c45\u5929\u5c71\u5357\u9ad8\u5cf0\uff0c\u6700\u521d\u51e0\u5e74\u8fd8\u4e00\u5e74\u4e00\u5ea6\u5230\u5510\u52aa\u5904\u4f5c\u5ba2\u5341\u5929\u516b\u5929\uff0c\u4f20\u98de \u7ea2\u5dfe\u6b66\u827a\uff0c\u4ee5\u5f8c\u5c31\u96be\u5f97\u4e0b\u5c71\u4e86\u3002 \u3000\u3000\u5353\u4e00\u822a\u9001\u6666\u660e\u5e08\u56de\u5230\u5929\u5c71\u5317\u9ad8\u5cf0\u5f8c\uff0c\u4fbf\u56de\u5230\u6155\u5341\u5854\u683c\u5c71\u9a7c\u5cf0\u4e4b\u4e0a\uff0c\u8f9b\u9f99\u5b50\u51fa\u6765\u8fce\u63a5\uff0c\u544a \u8bc9\u4ed6\u9053\uff1a\u201c\u6570\u6708\u4e4b\u524d\uff0c\u6709\u4e00\u4e2a\u767d\u53d1\u6ee1\u5934\u7684\u5973\u5b50\uff0c\u6500\u4e0a\u9a7c\u5cf0\u63a2\u671b\u3002\u201d\u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u6211\u6015\u5979\u6bc1\u574f \u4ed9\u82b1\uff0c\u4e0a\u524d\u559d\u95ee\u3002\u5979\u8f7b\u8f7b\u628a\u6211\u63a8\u5f00\uff0c\u5bf9\u4ed9\u82b1\u770b\u4e86\u597d\u4e45\uff0c\u53f9\u606f\u51e0\u58f0\uff0c\u9762\u4e0a\u5ffd\u53c8\u73b0\u51fa\u5fae\u7b11\uff0c\u7ec8\u65bc \u8d70\u4e86\u3002\u8fd9\u5973\u4eba\u597d\u5947\u602a\uff0c\u5e08\u7236\uff0c\u5979\u53ef\u662f\u4f60\u7684\u670b\u53cb\u9ebd\uff1f\u201d \u3000\u3000\u5353\u4e00\u822a\u6005\u7136\u592a\u606f\uff0c\u8fc7\u4e86\u597d\u4e45\uff0c\u5ffd\u53eb\u8f9b\u9f99\u5b50\u4e0a\u524d\u95ee\u9053\uff1a\u201c\u4f60\u4f9d\u5b9e\u544a\u8bc9\u6211\uff0c\u4f60\u53ef\u77e5\u9053\u8fd9\u4e24\u6735 \u4ed9\u82b1\u4ec0\u9ebd\u65f6\u5019\u624d\u5f00\u5417\uff1f\u201d\u8f9b\u9f99\u5b50\u9053\uff1a\u201c\u6211\u95ee\u8fc7\u7239\u7239\uff0c\u542c\u7239\u7239\u8bf4\u4e5f\u8bb8\u8981\u4e94\u516d\u5341\u5e74\uff01\u201d \u3000\u3000\u5353\u4e00\u822a\u9053\uff1a\u201c\u597d\uff0c\u5c06\u6765\u6211\u6b7b\u4e86\u4e4b\u5f8c\uff0c\u4f60\u4e5f\u8981\u5b88\u7740\u8fd9\u4e24\u6735\u4ed9\u82b1\u3002\u201d\u8f9b\u9f99\u5b50\u6ee1\u8179\u7591\u56e2\uff0c\u89c1\u5e08 \u7236\u76ee\u4e2d\u8574\u6cea\uff0c\u795e\u8272\u5947\u5f02\uff0c\u4e0d\u6562\u53d1\u95ee\u3002 \u3000\u3000\u662f\u591c\uff0c\u53c8\u662f\u6de1\u6708\u758f\u661f\u4e4b\u591c\uff0c\u5353\u4e00\u822a\u72ec\u4e0a\u9a7c\u5cf0\uff0c\u51c4\u7136\u5357\u671b\uff0c\u907f\u9065\u89c1\u5357\u9ad8\u5cf0\u9ad8\u51fa\u4e91\u8868\uff0c\u5728\u90a3 \u53d8\u5e7b\u7684\u4e91\u6d77\u4e4b\u4e2d\uff0c\u4f3c\u4e4e\u6709\u4e00\u4e2a\u4eba\u4e5f\u5728\u5411\u4ed6\u9065\u671b\u3002 \u3000\u3000\u5353\u4e00\u822a\u53f9\u4e86\u53e3\u6c14\uff0c\u5341\u6570\u5e74\u6765\u60c5\u4e8b\uff0c\u4e00\u4e00\u5728\u4ed6\u5fc3\u5934\u63a0\u8fc7\uff1a\u9ec4\u9f99\u6d1e\u7684\u521d\u4f1a\uff0c\u660e\u6708\u5ce1\u7684\u591c\u8bdd\uff0c \u6b66\u5f53\u5c71\u4e0a\u7684\u7ea0\u7eb7\uff0c\u5927\u6c99\u6f20\u4e0a\u7684\u79bb\u522b\uff0c\u5386\u5386\u5982\u5728\u76ee\u524d\uff0c\u6709\u5fcf\u6094\uff0c\u6709\u60c5\u4f24\uff0c\u6709\u871c\u610f\u67d4\u60c5\uff0c\u6709\u60ca\u5fc3 \u8c23\u8bfc\uff0c\u6700\u4f24\u5fc3\u7684\u662f\u5f80\u8005\u5df2\u77e3\uff0c\u6765\u8005\u53c8\u672a\u5fc5\u53ef\u8ffd\uff0c\u6240\u80fd\u505a\u7684\uff0c\u4e5f\u53ea\u6709\u591c\u591c\u5728\u6b64\u76f8\u671b\u7f62\u4e86\u3002 \u3000\u3000\u5353\u4e00\u822a\u60f3\u5f97\u5982\u9189\u4f3c\u75f4\uff0c\u770b\u7740\u5934\u9876\u4e0a\u7a7a\u7684\u661f\u661f\uff0c\u60f3\u8d77\u98de\u7ea2\u5dfe\u6240\u8f6c\u8fbe\u7684\u7389\u7f57\u5239\u7684\u8bdd\uff0c\u53ea\u89c9\u7389 \u7f57\u5239\u5c31\u50cf\u5934\u9876\u4e0a\u7684\u661f\u661f\uff0c\u79bb\u81ea\u5df1\u50cf\u662f\u5f88\u8fd1\u53c8\u50cf\u5f88\u8fdc\uff0c\u5fc3\u6e56\u6d6a\u6d8c\uff0c\u60b2\u4ece\u4e2d\u6765\uff0c\u4e0d\u53ef\u65ad\u7edd\uff0c\u4e0d\u89c9 \u7528\u5251\u5728\u77f3\u58c1\u4e0a\u523b\u4e0b\u4e86\u4e00\u9996\u5f8b\u8bd7\uff0c\u8bd7\u9053\uff1a\u522b\u5f8c\u97f3\u4e66\u4e24\u4e0d\u95fb\uff0c\u9884\u77e5\u8c10\u8bfc\u5fc5\u7eb7\u7ead\uff0c\u53ea\u7f18\u6d77\u5185\u5b58\u77e5 \u5df1\uff0c\u59cb\u4fe1\u5929\u6daf\u82e5\u6bd4\u90bb\uff1a\u5386\u52ab\u4e86\u65e0\u751f\u6b7b\u5ff5\uff0c\u7ecf\u971c\u65b9\u663e\u50b2\u5bd2\u5fc3\uff01\u51ac\u98ce\u5c3d\u6298\u82b1\u5343\u6811\uff0c\u5c1a\u6709\u5e7d\u9999\u653e\u4e0a \u6797\u3002\u523b\u4e86\u4e4b\u5f8c\uff0c\u653e\u58f0\u541f\u8bf5\uff0c\u9980\u97f3\u8885\u8885\uff0c\u6563\u5728\u5c71\u5dc5\u6c34\u6daf\uff0c\u5929\u4e0a\u7684\u5317\u6781\u661f\u53c8\u5347\u8d77\u4e86\uff01\u9644\u5f55\uff1a\u672c\u4e66 \u6d89\u53ca\u7684\u91cd\u8981\u5386\u53f2\u4e8b\u5b9e\u548c\u4eba\u7269\u8fbd\u9977\u9526\u8863\u536b\u6883\u51fb\u6848\u9b4f\u5fe0\u8d24\u4e1c\u5382\u897f\u5382\u4e1c\u6797\u515a\u987e\u5baa\u6210\u718a\u5ef7\u5f3c\u7ea2\u4e38\u6848\u6768 \u6d9f\u52aa\u5c14\u54c8\u8d64\u5de6\u5149\u6597\u8881\u5d07\u7115\u962e\u5927\u9488\u5d14\u5448\u79c0\u5b59\u627f\u5fe0\u9ad8\u6500\u9f99\u6d2a\u627f\u7574 \u3000\u3000\u8fbd\u9977\u2014\u2014\u660e\u671d\u672a\u5e74\u8fbd\u4e1c\u9a7b\u519b\u7684\u9977\u9879\uff1b\u53c8\u6307\u4e3a\u7b79\u63aa\u8fd9\u79cd\u519b\u9977\u800c\u52a0\u6d3e\u7684\u7530\u8d4b\u94f6\u3002\u8fd9\u91cc\u6b63\u662f\u6307 \u52a0\u6d3e\u7684\u7530\u8d4b\u94f6\u3002\u4e07\u5386\u56db\u5341\u516d\u5e74\u201c\u4e00\u516d\u4e00\u516b\u5e74\u201d\u8fbd\u4e1c\u519b\u9977\u9aa4\u589e\u53c1\u767e\u4e07\u4e24\uff0c\u5bab\u5185\u867d\u6709\u79ef\u50a8\uff0c\u4f46\u4e0d \u80af\u62e8\u53d1\uff0c\u65bc\u662f\u63f4\u5fa1\u502d\u4f8b\uff0c\u6bcf\u4ea9\u52a0\u6d3e\u53c1\u5398\u4e94\u6beb\uff0c\u5171\u589e\u8d4b\u94f6\u4e8c\u767e\u591a\u4e07\u4e24\u3002\u4ee5\u5f8c\u4e0d\u65ad\u52a0\u589e\uff0c\u5230\u5d07\u6862 \u672b\u5e74\uff0c\u8fbd\u9977\u5df2\u589e\u81f3\u4e5d\u767e\u4e07\u4e24\u3002 \u3000\u3000\u9526\u8863\u536b\u2014\u2014\u660e\u671d\u7684\u5b98\u7f72\u540d\uff0c\u5373\u9526\u8863\u4eb2\u519b\u90fd\u6307\u6325\u4f7f\u53f8\u3002\u660e\u6d2a\u6b66\u5341\u4e94\u5e74\u201c\u4e00\u53c1\u516b\u4e8c\u5e74\u201d\u8bbe \u7f6e\u3002\u539f\u4e3a\u62a4\u536b\u7687\u5bab\u7684\u4eb2\u519b\uff0c\u638c\u7ba1\u7687\u5e1d\u51fa\u5165\u4eea\u4ed7\u3002\u592a\u7956\u52a0\u5f3a\u4e13\u5236\u7edf\u6cbb\uff0c\u7279\u4ee4\u517c\u7ba1\u5211\u72f1\uff0c\u8d4b\u4e88\u5de1 \u5bdf\u7f09\u6355\u7684\u6743\u529b\u3002\u6700\u9ad8\u957f\u5b98\u4e3a\u6307\u6325\u4f7f\uff0c\u5e38\u7531\u529f\u81e3\uff0c\u5916\u621a\u5145\u4efb\u3002\u9526\u8863\u536b\u6240\u5c5e\u4e4b\u9547\u629a\u53f8\u5206\u5357\u5317\u4e24 \u90e8\uff0c\u5317\u9547\u629a\u53f8\u4e13\u7406\u8bcf\u72f1\uff0c\u76f4\u63a5\u53d6\u65e8\u884c\u4e8b\uff0c\u7528\u5211\u5c24\u4e3a\u60e8\u9177\u3002\u660e\u4e2d\u53f6\u5f8c\u9526\u8863\u536b\u4e0e\u53e6\u4e00\u7279\u52a1\u7ec4\u7ec7 \u4e1c\u3001\u897f\u5382\u5e76\u5217\uff0c\u6d3b\u52a8\u52a0\u5f3a\uff0c\u53f2\u79f0\u201c\u5382\u536b\u201d\u3002 \u3000\u3000\u6883\u51fb\u6848\u2014\u2014\u4e07\u5386\u56db\u5341\u53c1\u5e74\u201c\u4e00\u516d\u4e00\u4e94\u5e74\u201d\uff0c\u5f20\u5dee\u624b\u6267\u6728\u68cd\uff0c\u95ef\u8fdb\u592a\u5b50\u201c\u5149\u5b97\u201d\u4f4f\u7684\u6148\u5e86 \u5bab\uff0c\u6253\u4f24\u5b88\u95e8\u592a\u76d1\u3002\u88ab\u6267\u5f8c\u4f9b\u79f0\u5f97\u90d1\u8d35\u5983\u624b\u4e0b\u592a\u76d1\u5e9e\u4fdd\u3001\u5218\u6210\u5f15\u8fdb\u3002\u65f6\u4eba\u6000\u7591\u90d1\u8d35\u5983\u6b32\u8c0b\u6740 \u592a\u5b50\u3002\u795e\u5b97\u4e0e\u592a\u5b50\u4e0d\u6b32\u8ffd\u7a76\uff0c\u4ee5\u75af\u766b\u5978\u5f92\u4e4b\u7f6a\uff0c\u6740\u5f20\u5dee\u65bc\u5e02\uff0c\u5e76\u6bd9\u5e9e\u3001\u5218\u65bc\u5185\u5ef7\u4e86\u6848\u3002\u53f2\u79f0 \u633a\u51fb\u6848\uff0c\u4e0e\u201c\u7ea2\u4e38\u201d\uff0e\u201c\u79fb\u5bab\u201d\u4e8c\u6848\u5e76\u79f0\u665a\u660e\u53c1\u5927\u6848\u3002 \u3000\u3000\u9b4f\u5fe0\u8d24\u2014\u2014\u201c\u4e00\u4e94\u516d\u516b\u2014\u2014\u4e00\u516d\u4e8c\u4e03\u5e74\u201d\u660e\u5b98\uff0c\u6cb3\u95f4\u8083\u5b81\u201c\u4eca\u5c5e\u6cb3\u5317\u201d\u4eba\uff0c\u4e07\u5386\u65f6\u5165 \u5bab\u3002\u6cf0\u660c\u5143\u5e74\u201c\u4e00\u516d\u4e8c0\u5e74\u201d\uff0c\u71b9\u5b97\u5373\u4f4d\uff0c\u4efb\u53f8\u793c\u76d1\u79c9\u7b14\u592a\u76d1\uff0c\u5f8c\u53c8\u517c\u638c\u4e1c\u5382\uff0c\u52fe\u7ed3\u71b9\u5b97\u4e73 \u6bcd\u5ba2\u6c0f\uff0c\u4e13\u65ad\u56fd\u653f\u3002\u5929\u6563\u4e94\u5e74\uff01\u516d\u4e8c\u4e94\u5e74\u201d\u5174\u5927\u72f1\uff0c\u6740\u4e1c\u6797\u515a\u4eba\u6768\u6d9f\u7b49\u3002\u81ea\u79f0\u4e5d\u5343\u5c81\uff0c\u4e0b\u6709 \u4e94\u864e\u3001\u4e94\u5f6a\uff0c\u5341\u72d7\u7b49\u540d\u76ee\uff0c\u4ece\u5185\u9601\u516d\u90e8\u81f3\u56db\u65b9\u7763\u629a\uff0c\u90fd\u6709\u79c1\u515a\u3002\u5d07\u6862\u5373\u4f4d\u5f8c\uff0c\u9edc\u804c\uff0c\u5b89\u7f6e\u51e4 \u9633\uff0c\u65cb\u547d\u902e\u6cbb\uff0c\u5728\u9014\u4e2d\u754f\u7f6a\u81ea\u6740\u3002 \u3000\u3000\u4e1c\u5382\u2014\u2014\u660e\u6210\u7956\u4e3a\u9547\u538b\u4eba\u6c11\u548c\u5b98\u5458\u4e2d\u7684\u53cd\u5bf9\u6d3e\uff0c\u65bc\u6c38\u4e50\u5341\u516b\u5e74\u201c\u4e00\u56db\u4e8c0\u5e74\u201d\u5728\u4eac\u5e08\u4e1c \u5b89\u95e8\u5317\u8bbe\u7acb\u7279\u52a1\u5b98\u7f72\uff0c\u7528\u5b98\u63d0\u7763\uff0c\u5e38\u4ee5\u53f8\u793c\u76d1\u79c9\u7b14\u592a\u76d1\u4e4b\u7b2c\u4e8c\uff0e\u7b2c\u53c1\u4eba\u5145\u4efb\uff0c\u5c5e\u5b98\u6709\u638c\u5211\u5343 \u6237\u3001\u7406\u5211\u767e\u6237\u5404\u4e00\u5458\uff0c\u7531\u9526\u8863\u536b\u5343\u6237\uff0c\u767e\u6237\u5145\u5f53\uff0c\u79f0\u8d34\u5211\u5b98\uff1b\u68e3\u5f79\u3001\u7f09\u4e8b\u7b49\u5b98\u6821\u4ea6\u7531\u9526\u8863\u536b \u62e8\u7ed9\uff0c\u4ece\u4e8b\u7279\u52a1\u6d3b\u52a8\uff0c\u8bf8\u4e8b\u53ef\u76f4\u63a5\u62a5\u544a\u7687\u5e1d\uff0c\u6743\u529b\u5728\u9526\u8863\u536b\u4e4b\u4e0a\u3002 \u3000\u3000\u897f\u5382\u2014\u2014\u660e\u5baa\u5b97\u65f6\u4e3a\u52a0\u5f3a\u7279\u52a1\u7edf\u6cbb\uff0c\u65bc\u6210\u5316\u5341\u53c1\u5e74\u201c\u4e00\u56db\u4e03\u4e03\u5e74\u201d\u5728\u4e1c\u5382\u4ee5\u5916\u589e\u8bbe\u897f \u5382\uff0c\u7528\u592a\u76d1\u6c6a\u76f4\u63d0\u7763\u3002\u5176\u4eba\u5458\u6743\u529b\u8d85\u8fc7\u4e1c\u5382\uff0c\u6d3b\u52a8\u8303\u56f4\u81ea\u4eac\u5e08\u904d\u53ca\u5404\u5730\uff0c\u5f8c\u56e0\u906d\u5230\u53cd\u5bf9\uff0c\u88ab \u8feb\u64a4\u9500\u3002\u6b66\u5b97\u65f6\u5ba6\u5b98\u5218\u747e\u4e13\u6743\uff0c\u53c8\u4e00\u5ea6\u6062\u590d\uff0c\u5218\u747e\u670d\u6cd5\u5f8c\u5e9f\u3002 \u3000\u3000\u4e1c\u6797\u515a\u2014\u2014\u665a\u660e\u4ee5\u6c5f\u5357\u58eb\u5927\u592b\u4e3a\u4e3b\u7684\u653f\u6cbb\u96c6\u56e2\u3002\u795e\u5b97\u5f8c\u671f\uff0c\u653f\u6cbb\u65e5\u76ca\u8150\u8d25\uff0c\u793e\u4f1a\u77db\u76fe\u6fc0 \u5316\u3002\u4e07\u5386\u4e8c\u5341\u4e8c\u5e74\u201c\u4e00\u4e94\u4e5d\u56db\u5e74\u201d\u65e0\u9521\u4eba\u987e\u5baa\u6210\u9769\u804c\u8fd8\u4e61\uff0c\u4e0e\u9ad8\u6500\u9f99\u3001\u94b1\u4e00\u672c\u7b49\u5728\u4e1c\u6797\u4e66\u9662 \u8bb2\u5b66\uff0c\u8bae\u8bba\u671d\u653f\uff0c\u5f97\u5230\u90e8\u5206\u58eb\u5927\u592b\u7684\u652f\u6301\uff0c\u53f2\u79f0\u201c\u4e1c\u6797\u515a\u201d\u3002\u4ed6\u4eec\u53cd\u5bf9\u77ff\u76d0\uff0c\u7a0e\u76d0\u7684\u63a0\u593a\uff0c \u4e3b\u5f20\u5f00\u653e\u8a00\u8def\uff0c\u5b9e\u884c\u6539\u826f\uff0c\u906d\u5230\u5728\u671d\u6743\u8d35\u7684\u5ac9\u89c6\u3002\u71b9\u5b97\u65f6\u5ba6\u5b98\u9b4f\u5fe0\u8d24\u4e13\u653f\uff0c\u515a\u4eba\u6768\u6d9f\u3001\u5de6\u5149 \u6597\u7b49\u56e0\u5f39\u52be\u9b4f\u5fe0\u8d24\u906d\u6355\uff0c\u4e0e\u9ec4\u5c0a\u7d20\u3001\u5468\u987a\u660c\u7b49\u540c\u906d\u6740\u5bb3\u3002\u9b4f\u5fe0\u8d24\u4f7f\u4eba\u7f16\u201c\u738b\u671d\u5178\u8981\u201d\uff0c\u501f\u6883 \u51fb\u3001\u7ea2\u4e38\uff0c\u79fb\u5bab\u53c1\u6848\u4e3a\u9898\uff0c\u6253\u51fb\u4e1c\u6797\u515a\uff0c\u66f4\u55fe\u4f7f\u5176\u515a\u7fbd\u9020\u4f5c\u201c\u4e1c\u6797\u70b9\u5c06\u5f55\u201d\u7b49\u6587\u4ef6\uff0c\u60f3\u628a\u515a \u4eba\u4e00\u7f51\u6253\u5c3d\u3002\u5929\u542f\u4e03\u5e74\u201c\u4e00\u516d\u4e8c\u4e03\u5e74\u201d\u601d\u5b97\u201c\u5d07\u6862\u5e1d\u201d\u5373\u4f4d\u5f8c\uff0c\u902e\u6cbb\u9b4f\u5fe0\u8d24\uff0c\u5bf9\u5927\u6279\u9609\u515a\u5b9a \u4e3a\u9006\u6848\uff0c\u5206\u522b\u6cbb\u7f6a\uff0c\u4e1c\u6797\u515a\u4eba\u6240\u53d7\u8feb\u5bb3\u624d\u544a\u7ec8\u6b62\u3002 \u3000\u3000\u987e\u5baa\u6210\u201c\u4e00\u4e94\u4e940\u2014\u2014\u4e00\u516d\u4e00\u4e8c\u5e74\u201d\u660e\u6c5f\u82cf\u65e0\u9521\u4eba\uff0c\u5b57\u53d4\u65f6\uff0c\u4e16\u79f0\u4e1c\u6797\u5148\u751f\uff0c\u4ea6\u79f0\u6cfe\u9633 \u5148\u751f\uff0c\u4e07\u5386\u8fdb\u58eb\uff0c\u5b98\u81f3\u540f\u90e8\u6587\u9009\u53f8\u90ce\u4e2d\u3002\u4e07\u5386\u4e8c\u5341\u4e8c\u5e74\u201c\u4e00\u4e94\u4e5d\u56db\u5e74\u201d\u9769\u804c\u8fd8\u4e61\uff0c\u4e0e\u5f1f\u5141\u6210 \u548c\u9ad8\u6500\u9f99\u7b49\u5728\u4e1c\u6797\u4e66\u9662\u8bb2\u5b66\uff0c\u8bae\u8bba\u671d\u653f\uff0c\u9887\u5f97\u58eb\u5927\u592b\u652f\u6301\uff0c\u6e10\u6210\u96c6\u56e2\uff0c\u53f2\u79f0\u4e1c\u6797\u515a\u3002\u7740\u6709 \u201c\u5c0f\u5fc3\u658b\u672d\u8bb0\u201d\u3001\u201c\u6cfe\u768b\u85cf\u7a3f\u201d\u3001\u201c\u987e\u7aef\u6587\u9057\u4e66\u201d\u3002\u718a\u5ef7\u5f3c\u201c\u4e00\u4e94\u516d\u4e5d\u2014\u2014\u4e00\u516d\u4e8c\u4e94\u5e74\u201d\u2014 \u2014\u660e\u6e56\u5e7f\u6c5f\u590f\u201c\u4eca\u6e56\u5317\u6b66\u660c\u201d\u4eba\uff0c\u5b57\u98de\u767e\uff0c\u4e07\u5386\u8fdb\u58eb\u3002\u4e07\u5386\u56db\u5341\u4e03\u5e74\u201c\u4e00\u516d\u4e00\u4e5d\u725b\u201d\u4efb\u8fbd\u4e1c \u7ecf\u7565\u3002\u5f53\u65f6\u5f8c\u91d1\u201c\u6e05\u201d\u5d1b\u8d77\uff0c\u4ed6\u53ec\u96c6\u6d41\u4ea1\uff0c\u6574\u8083\u519b\u4ee4\uff0c\u8bad\u7ec3\u90e8\u961f\uff0c\u52a0\u5f3a\u9632\u52a1\u3002\u5728\u804c\u5e74\u9980\uff0c\u5f8c \u91d1\u519b\u4e0d\u6562\u8fdb\u653b\u3002\u71b9\u5b97\u5373\u4f4d\uff0c\u9b4f\u5fe0\u8d24\u4e13\u6743\uff0c\u4ed6\u53d7\u6392\u6324\u53bb\u804c\u3002\u5929\u542f\u5143\u5e74\u201c\u4e00\u516d\u4e8c\u4e00\u5e74\u201d\u8fbd\u9633\uff0e\u6e16 \u9633\u5931\u5b88\uff0c\u518d\u4efb\u7ecf\u7565\uff0c\u800c\u5b9e\u6743\u843d\u4eba\u5e7f\u5b81\u201c\u4eca\u8fbd\u5b81\u5317\u9501\u201d\u5de1\u629a\u738b\u5316\u8d1e\u624b\u4e2d\uff0c\u5316\u8d1e\u5927\u8a00\u8f7b\u654c\uff0c\u4e0d\u53d7 \u8c03\u5ea6\uff0c\u6b21\u5e74\u5927\u8d25\u6e83\u9000\uff0c\u4ed6\u540c\u9000\u5165\u5173\uff0c\u5f8c\u88ab\u9b4f\u5fe0\u8d24\u51a4\u6740\u3002\u6709\u201c\u8fbd\u4e2d\u4e66\u724d\u201d\u3001\u201c\u718a\u8944\u610d\u516c\u96c6\u201d\u3002 \u3000\u3000\u7ea2\u4e38\u6848\u2014\u2014\u6cf0\u660c\u5143\u5e74\u201c\u4e00\u516d\u4e8c0\u5e74\u201d\u5149\u5b97\u5373\u4f4d\u5f8c\u751f\u91cd\u75c5\uff0c\u53f8\u793c\u76d1\u79c9\u7b14\u517c\u638c\u5fa1\u836f\u623f\u592a\u76d1\u5d14 \u6587\u5347\u4e0b\u6cfb\u836f\uff0c\u75c5\u76ca\u5267\u3002\u9e3f\u80ea\u5bfa\u4e1e\u674e\u53ef\u707c\u8fdb\u7ea2\u4e38\uff0c\u81ea\u79f0\u4ed9\u65b9\u3002\u5149\u5b97\u670d\u5f8c\u5373\u5d29\u3002\u5f53\u65f6\u6709\u4eba\u7591\u795e\u5b97 \u7684\u90d1\u8d35\u5983\u6307\u4f7f\u4e0b\u6bd2\uff0c\u5f15\u8d77\u8bb8\u591a\u4e89\u8bba\uff0c\u7ed3\u679c\u5d14\u6587\u5347\u53d1\u9063\u5357\u4eac\uff0c\u674e\u53ef\u707c\u9063\u620d\u3002\u9b4f\u5fe0\u8d24\u4e13\u653f\u65f6\u7ffb \u6848\uff0c\u514d\u674e\u53ef\u707c\u620d\uff0c\u64e2\u5d14\u6587\u5347\u603b\u7763\u6f15\u8fd0\u3002 \u3000\u3000\u6768\u6d9f\u201c\u5175\u90e8\u7ed9\u4e8b\u4e2d\u201d\u201c\u4e00\u4e94\u4e03\u4e8c\u2014\u2014\u4e00\u516d\u4e8c\u4e94\u5e74\u201d\u660e\u6e56\u5e7f\u5e94\u5c71\u201c\u4eca\u5c5e\u6e56\u5317\u201d\u4eba\uff0c\u5b57\u6587 \u5b7a\uff0c\u53f7\u5927\u6d2a\u3002\u4e07\u5386\u8fdb\u58eb\u3002\u5b98\u81f3\u5de6\u526f\u90fd\u5fa1\u53f2\u3002\u5929\u542f\u56db\u5e74\u201c\u4e00\u516d\u4e8c\u56db\u5e74\u201d\u4e0a\u758f\u5f39\u52be\u9b4f\u5fe0\u8d24\u4e8c\u5341\u56db \u5927\u7f6a\u3002\u6b21\u5e74\u4e3a\u9b4f\u5fe0\u8d24\u8bec\u9677\uff0c\u6b7b\u65bc\u72f1\u4e2d\u3002\u6709\u201c\u6768\u5927\u6d2a\u96c6\u201d\u3002 \u3000\u3000\u52aa\u5c14\u54c8\u8d64\u201c\u4e00\u4e94\u4e94\u4e5d\u2014\u2014\u4e00\u516d\u4e8c\u516d\u5e74\u201d\u5373\u6e05\u592a\u7956\uff0c\u59d3\u7231\u65b0\u89c9\u7f57\uff0c\u6ee1\u65cf\u3002\u5148\u4e16\u53d7\u660e\u518c\u5c01\uff0c \u4e3a\u5efa\u5dde\u5de6\u536b\u201c\u5728\u4eca\u8fbd\u5b81\u7701\u65b0\u5bbe\u53bf\u5883\u201d\u90fd\u6307\u6325\u4f7f\uff0c\u5341\u516d\u4e16\u7eaa\u5f8c\u671f\uff0c\u7531\u65bc\u5973\u771f\u793e\u4f1a\u7684\u53d1\u5c55\uff0c\u51fa\u73b0 \u7edf\u4e00\u7684\u8d8b\u52bf\u3002\u4e00\u4e94\u516b\u53c1\u2014\u2014\u4e00\u4e94\u516b\u516b\u5e74\u9996\u5148\u7edf\u4e00\u5efa\u5dde\u5404\u90e8\uff0c\u53d7\u660e\u5c01\u4e3a\u90fd\u7763\u4f65\u4e8b\uff0c\u9f99\u864e\u5c06\u519b\u7b49 \u5b98\uff0c\u66f4\u52a0\u5f3a\u4e86\u4e0e\u5173\u5185\u7684\u7ecf\u6d4e\u5173\u7cfb\u3002\u4ee5\u5f8c\u53c8\u5408\u5e76\u677e\u82b1\u6c5f\u6d41\u57df\u7684\u6d77\u897f\u5404\u90e8\u548c\u957f\u767d\u5c71\u4e1c\u5317\u7684\u4e1c\u6d77\u8bf8 \u90e8\u3002\u5728\u7edf\u4e00\u8fc7\u7a0b\u4e2d\u521b\u5efa\u516b\u65d7\u5236\u5ea6\u548c\u6ee1\u6587\u3002\u4e07\u5386\u56db\u5341\u56db\u5e74\u201c\u4e00\u516d\u4e00\u516d\u5e74\u201d\u5efa\u7acb\u5f8c\u91d1\uff0c\u79f0\u91d1\u56fd \u6c57\uff0c\u5272\u636e\u8fbd\u4e1c\uff0c\u5efa\u5143\u5929\u547d\u3002\u5929\u547d\u5341\u5e74\u201c\u4e00\u516d\u4e8c\u4e94\u5e74\u201d\u8fc1\u90fd\u6e16\u9633\uff0c\u6b21\u5e74\u8fdb\u653b\u5b81\u8fdc\u201c\u4eca\u8fbd\u5b81\u5174 \u57ce\u201d\uff0c\u4e3a\u8881\u5d07\u7115\u51fb\u8d25\uff0c\u53d7\u4f24\uff0c\u4e0d\u4e45\u5373\u53bb\u4e16\u3002\u4ed6\u7edf\u4e00\u5973\u771f\u5404\u90e8\uff0c\u5728\u6ee1\u65cf\u521d\u671f\u53d1\u5c55\u4e2d\u8d77\u4e86\u91cd\u8981\u4f5c \u7528\uff0c\u6545\u6e05\u671d\u5efa\u7acb\u5f8c\u8ffd\u5c0a\u4e3a\u592a\u6cae\u3002 \u3000\u3000\u5de6\u5149\u6597\u201c\u4e00\u4e94\u4e03\u4e94\u2014\u2014\u4e00\u516d\u4e8c\u4e94\u5e74\u201d\u660e\u5b89\u5e86\u6850\u57ce\u201c\u4eca\u5c5e\u5b89\u5fbd\u201d\u4eba\uff0c\u5b57\u9057\u76f4\u3002\u4e07\u5386\u4e2d\u4e0e\u6768 \u6d9f\u540c\u4e3e\u8fdb\u58eb\u3002\u4efb\u5fa1\u53f2\u65f6\u529e\u7406\u5c6f\u7530\uff0c\u5728\u5317\u65b9\u5174\u6c34\u5229\uff0c\u63d0\u5021\u79cd\u7a3b\u3002\u5929\u542f\u56db\u5e74\u201c\u4e00\u516d\u4e8c\u56db\u5e74\u201d\u4efb\u5de6 \u4f65\u90fd\u5fa1\u53f2\u3002\u6768\u6d9f\u52be\u9b4f\u5fe0\u8d24\uff0c\u4ed6\u53c2\u4e0e\u5176\u4e8b\u3002\u53c8\u4eb2\u52be\u9b4f\u5fe0\u8d24\u53c1\u5341\u4e8c\u65a9\u7f6a\u3002\u6b21\u5e74\uff0c\u4e0e\u6768\u6d9f\u540c\u906d\u8bec \u9677\uff0c\u6b7b\u65bc\u72f1\u4e2d\u3002 \u3000\u3000\u8881\u5d07\u7115\u201c\u4e00\u4e94\u516b\u56db\u2014\u2014\u4e00\u516d\u53c10\u5e74\u201d\u660e\u519b\u4e8b\u5bb6\u3002\u5b57\u5143\u7d20\uff0c\u5e7f\u4e1c\u4e1c\u839e\u4eba\u3002\u4e07\u5386\u8fdb\u58eb\u3002\u5929\u542f \u4e8c\u5e74\u201c\u4e00\u516d\u4e8c\u4e8c\u5e74\u201d\u5355\u9a91\u51fa\u5173\uff0c\u8003\u5bdf\u5f62\u52bf\uff0c\u8fd8\u4eb2\u81ea\u8bf7\u5b88\u8fbd\u3002\u4ed6\u5b81\u8fdc\u201c\u4eca\u8fbd\u5b81\u5174\u57ce\u201d\u7b49\u57ce\uff0c\u5c61 \u6b21\u51fb\u9000\u5f8c\u91d1\u201c\u6e05\u201d\u519b\u7684\u8fdb\u653b\u3002\u516d\u5e74\u83b7\u5b81\u8fdc\u5927\u6377\uff0c\u52aa\u5c14\u54c8\u8d64\u53d7\u4f24\u6b7b\u3002\u6388\u8fbd\u4e1c\u5de1\u629a\u3002\u6b21\u5e74\u83b7\u5b81\u9526 \u5927\u6377\uff0c\u7687\u592a\u6781\u53c8\u5927\u8d25\u800c\u53bb\uff0c\u5d07\u6862\u6388\u4ee5\u5175\u90e8\u5c1a\u4e66\uff0c\u7763\u5e08\u84df\u8fbd\u3002\u5d07\u6862\u4e8c\u5e74\u201c\u4e00\u516d\u4e8c\u4e5d\u725b\u201d\u5f8c\u91d1\u519b \u7ed5\u9053\u53e4\u5317\u53e3\u5165\u957f\u57ce\uff0c\u8fdb\u56f4\u5317\u4eac\uff0c\u4ed6\u661f\u591c\u9a70\u63f4\uff0c\u5d07\u6862\u4e2d\u53cd\u95f4\u8ba1\uff0c\u6740\u4e4b\u3002 \u3000\u3000\u962e\u5927\u9488\u201c\u7ea6\u4e00\u4e94\u516b\u4e03\u2014\u2014\u7ea6\u4e00\u516d\u56db\u516d\u5e74\u201d\u660e\u672a\u6000\u5b81\u201c\u4eca\u5c5e\u5b89\u5fbd\u201d\u4eba\uff0c\u53f7\u5706\u6d77\u3002\u5929\u542f\u65f6\u4f9d \u9644\u9b4f\u5fe0\u8d24\uff0c\u5d07\u6862\u65f6\u5e9f\u9edc\uff0c\u533f\u5c45\u5357\u4eac\u3002\u5f18\u5149\u65f6\uff0c\u9a6c\u58eb\u82f1\u6267\u653f\uff0c\u4efb\u5175\u90e8\u5c1a\u4e66\uff0c\u4e0e\u4e1c\u6797\uff0c\u590d\u793e\u4e3a \u654c\u3002\u5f8c\u964d\u6e05\uff0c\u4ece\u653b\u4ed9\u971e\u5cad\u800c\u6b7b\uff0c\u7740\u6709\u201c\u71d5\u5b50\u7b3a\u201d\u7b49\u4f20\u5947\u3002 \u3000\u3000\u5d14\u5448\u79c0\u201c\uff1f\u2014\u2014\u4e00\u516d\u4e8c\u4e03\u5e74\u201d\u660e\u84df\u6d32\u4eba\uff0c\u4e07\u5386\u8fdb\u58eb\u3002\u5929\u542f\u521d\u6c42\u9644\u4e1c\u6797\uff0c\u88ab\u62d2\uff0c\u56db\u5e74\u201c\u4e00 \u516d\u4e8c\u56db\u5e74\u201d\u4ee5\u8d2a\u6c61\u9769\u804c\u8bae\u7f6a\uff0c\u4e43\u89c1\u9b4f\u5fe0\u8d24\uff0c\u6c42\u4e3a\u517b\u5b50\uff0c\u76f8\u4e0e\u5bc6\u8c0b\u9677\u5bb3\u4e1c\u6797\u515a\u4eba\u3002\u4ece\u6b64\u4e3a\u9609\u515a \u9b41\u9996\uff0c\u5b98\u81f3\u5175\u90e8\u5c1a\u4e66\u517c\u5de6\u90fd\u5fa1\u53f2\u3002\u5d07\u6862\u5373\u4f4d\uff0c\u4ee4\u9769\u804c\u902e\u6cbb\uff0c\u4e43\u81ea\u7f22\u800c\u6b7b\u3002 \u3000\u3000\u5b59\u627f\u5fe0\u201c\u4e00\u4e94\u516d\u53c1\u2014\u2014\u4e00\u516d\u53c1\u516b\u5e74\u201d\u660e\u4fdd\u5b9a\u9ad8\u9633\u201c\u4eca\u5c5e\u6cb3\u5317\u201d\u4eba\uff0c\u5b87\u96c5\u7ef3\uff0c\u4e07\u5386\u8fdb\uff01\u5929 \u542f\u4e8c\u5e74\u201c\u4e00\u516d\u4e8c\u4e8c\u5e74\u201d\u4efb\u5175\u90e8\u5c1a\u4e66\u7ecf\u7565\u84df\u8fbd\uff0c\u5728\u56db\u5e74\uff0c\u7ec3\u5175\u5c6f\u7530\uff0c\u4fee\u57ce\u5821\u6570\u5341\uff0c\u5f8c\u4e3a\u9b4f\u5fe0\u8d24 \u6392\u6324\u53bb\u804c\u3002\u5d07\u6862\u4e8c\u5e74\u201c\u4e00\u516d\u4e8c\u4e5d\u5e74\u201d\uff0c\u5b88\u901a\u5dde\uff0c\u5f8c\u79fb\u9547\u9501\u5c71\u6d77\u5173\uff0c\u6536\u590d\u6c38\u5e73\uff0c\u9075\u5316\u7b49\u5730\uff0c\u56db \u5e74\u7f62\u804c\u5f52\u91cc\uff0c\u5341\u4e00\u5e74\u6e05\u5175\u653b\u9ad8\u9633\uff0c\u9616\u5bb6\u6297\u6218\uff0c\u57ce\u7834\u81ea\u6740\u3002 \u3000\u3000\u9ad8\u6500\u9f99\u201c\u4e00\u4e94\u516d\u4e8c\u2014\u2014\u4e00\u516d\u4e8c\u516d\u5e74\u201d\u2014\u2014\u660e\u65e0\u9521\u201c\u4eca\u5c5e\u6c5f\u82cf\u201d\u4eba\uff0c\u5b57\u4e91\u4ece\uff0c\u4e07\u5386\u8fdb\u58eb\uff0c \u71b9\u5b97\u65f6\u5b98\u5de6\u90fd\u5fa1\u53f2\uff0c\u56e0\u53cd\u5bf9\u9b4f\u5fe0\u8d24\uff0c\u9769\u804c\uff0c\u4e43\u4e0e\u987e\u5baa\u6210\u5728\u65e0\u9521\u4e1c\u6797\u4e66\u9662\u8bb2\u5b66\uff0c\u65f6\u79f0\u201c\u9ad8 \u987e\u201d\uff0c\u4e3a\u4e1c\u6797\u515a\u9996\u9886\u4e4b\u4e00\uff0c\u5f8c\u9b4f\u515a\u8d70\u72d7\u5d14\u5448\u79c0\u5f80\u6355\uff0c\u6295\u6c34\u800c\u6b7b\u3002\u7740\u6709\u201c\u9ad8\u5b50\u9057\u4e66\u201d\u3002 \u3000\u3000\u6d2a\u627f\u7574\u201c\u4e00\u4e94\u4e5d\u53c1\u2014\u2014\u4e00\u516d\u516d\u4e94\u5e74\u201d\u6963\u5efa\u5357\u5b89\u4eba\uff0c\u53f7\u4ea8\u4e5d\uff0c\u4e07\u5386\u8fdb\u58eb\uff0c\u5d07\u6862\u65f6\u4efb\u5175\u90e8\u5c1a \u4e66\u603b\u7763\u6cb3\u5357\uff0c\u5c71\u897f\uff0c\u9655\u3001\u5ddd\uff0c\u6e56\u519b\u52a1\u7b49\u804c\uff0c\u9547\u538b\u519c\u6c11\u519b\uff0c\u5f8c\u8c03\u4efb\u84df\u8fbd\u603b\u7763\uff0c\u6297\u51fb\u6e05\u5175\u3002\u5d07\u6862 \u5341\u56db\u5e74\u201c\u4e00\u516d\u56db\u4e00\u5e74\u201d\u7387\u516b\u603b\u5175\u5341\u53c1\u4e07\u4eba\u4e0e\u6e05\u519b\u4f1a\u6218\u65bc\u677e\u5c71\u201c\u4eca\u8fbd\u5b81\u9526\u5dde\u5357\u201d\uff0c\u5927\u8d25\uff0c\u88ab\u4fd8 \u964d\u6e05\u3002\u987a\u6cbb\u5143\u5e74\u201c\u4e00\u516d\u56db\u56db\u5e74\u201d\u4ece\u6e05\u519b\u4eba\u5173\uff0c\u6b21\u5e74\u81f3\u5357\u4eac\uff0c\u603b\u7763\u519b\u52a1\uff0c\u9501\u538b\u6297\u6e05\u4e49\u519b\u3002\u5f8c\u53d7 \u547d\u7ecf\u7565\u6e56\u5e7f\u7b49\u5730\uff0c\u81f3\u5341\u516d\u5e74\u653b\u5360\u4e91\u5357\u5f8c\u59cb\u56de\u5317\u4eac\uff0c\u5341\u516b\u5e74\u9000\u804c\u3002 \u3000\u3000\uff08\u5168\u4e66\u5b8c\uff09"}),showOnHomepage:!0}]}}]);