"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[82127],{45526:(o,a,U)=>{U.r(a),U.d(a,{default:()=>q});var e=U(74848);const q=[{name:"\u7b2c\u4e00\u7ae0\u3000\u9646\u5c0f\u51e4\u9003\u4ea1",content:(0,e.jsx)(e.Fragment,{children:"\u5149\u6cfd\u67d4\u6da6\u53e4\u94dc\u9547\u7eb8\u4e0b\uff0c\u57ab\u7740\u5341\u4e8c\u5f20\u767d\u7eb8\u5361\uff0c\u5f62\u5f0f\u9ad8\u96c5\u7684\u516b\u4ed9\u684c\u65c1\u5750\u7740\u4e03\u4e2a\u4eba\u3002 \u4e03\u4e2a\u540d\u52a8\u5929\u4e0b\uff0c\u8a89\u6ee1\u6c5f\u6e56\u7684\u4eba\u3002 \u53e4\u677e\u5c45\u58eb\u3001\u6728\u9053\u4eba\u3001\u82e6\u74dc\u548c\u5c1a\u3001\u5510\u4e8c\u5148\u751f\u3001\u6f47\u6e58\u5251\u5ba2\u3001\u53f8\u7a7a\u6458\u661f\u3001\u82b1\u6ee1\u697c\u3002 \u8fd9\u4e03\u4e2a\u4eba\u7684\u8eab\u5206\u90fd\u5f88\u5947\u7279\uff0c\u6765\u5386\u66f4\u4e0d\u540c\uff0c\u5176\u4e2d\u6709\u50e7\u9053\u3001\u6709\u9690\u58eb\u3001\u6709\u72ec\u884c\u4fa0\u76d7\u3001\u6709\u5927 \u5185\u9ad8\u624b\uff0c\u6709\u6d6a\u8ff9\u5929\u6daf\u7684\u540d\u95e8\u5b50\u5f1f\u3001\u4e5f\u6709\u6e38\u620f\u98ce\u5c18\u7684\u6b66\u6797\u524d\u8f88\u3002 \u4ed6\u4eec\u76f8\u805a\u5728\u8fd9\u91cc\uff0c\u53ea\u56e0\u4e3a\u4ed6\u4eec\u6709\u4e00\u70b9\u76f8\u540c\u4e4b\u5904\u3002 \u4ed6\u4eec\u90fd\u662f\u9646\u5c0f\u51e4\u7684\u670b\u53cb\u3002 \u73b0\u5728\u4ed6\u4eec\u8fd8\u6709\u2014\u70b9\u76f8\u540c\u4e4b\u5904\u4e03\u4e2a\u4eba\u7684\u8868\u60c5\u90fd\u5f88\u4e25\u8083\uff0c\u5fc3\u60c5\u90fd\u5f88\u6c89\u91cd\u3002 \u5c24\u5176\u662f\u6728\u9053\u4eba\u3002 \u6bcf\u4e2a\u4eba\u90fd\u5728\u770b\u7740\u4ed6\uff0c\u7b49\u7740\u4ed6\u5f00\u53e3\u3002 \u4ed6\u4eec\u90fd\u662f\u4ed6\u627e\u6765\u7684\uff0c\u8fd9\u5e76\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\uff0c\u4ed6\u5f53\u7136\u6709\u6781\u91cd\u8981\u7684\u7406\u7531\u3002 \u684c\u4e0a\u6709\u9152\uff0c\u5374\u6ca1\u6709\u4eba\u4e3e\u676f\uff0c\u6709\u83dc\uff0c\u4e5f\u6ca1\u6709\u4eba\u52a8\u8fc7\u3002 \u6709\u98ce\u5439\u8fc7\uff0c\u6ee1\u697c\u82b1\u9999\uff0c\u5728\u8fd9\u98ce\u5149\u660e\u5a9a\u7684\u5b63\u8282\u91cc\uff0c\u672c\u8be5\u662f\u4eba\u4eec\u5fc3\u60c5\u6700\u6b22\u7545\u7684\u65f6\u5019\u3002 \u4ed6\u4eec\u672c\u90fd\u662f\u6700\u6d12\u8131\u8c6a\u653e\u7684\u4eba\uff0c\u4e3a\u4ec0\u4e48\u504f\u504f\u4f1a\u6709\u8fd9\u8bb8\u591a\u5fc3\u4e8b\uff1f \u82b1\u6ee1\u697c\u662f\u778e\u5b50\uff0c\u778e\u5b50\u4e0d\u8be5\u71c3\u706f\u7684\uff0c\u4f46\u70b9\u7740\u684c\u4e0a\u90a3\u76cf\u516d\u89d2\u94dc\u706f\u7684\u4eba\uff0c\u5374\u504f\u504f\u5c31\u662f\u4ed6\u3002 \u4e16\u4e0a\u672c\u5c31\u6709\u5f88\u591a\u4e8b\u90fd\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u4e0d\u8be5\u53d1\u751f\u7684\uff0c\u5374\u504f\u504f\u53d1\u751f\u4e86\u3002 \u6728\u9053\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u7ec8\u4e8e\u5f00\u53e3\uff0c\u6bcf\u4e2a\u4eba\u90fd\u6709\u505a\u9519\u4e8b\u7684\u65f6\u5019\uff0c\u53ea\u8981\u77e5\u9519\u80fd\u6539\uff0c\u5c31\u662f\u597d \u7684\u3002\u201d\u4ed6\u867d\u7136\u5c3d\u529b\u5728\u63a7\u5236\u81ea\u5df1\uff0c\u58f0\u97f3\u8fd8\u662f\u663e\u5f97\u5f88\u6fc0\u52a8\uff0c\u4f46\u6709\u4e9b\u4e8b\u5374\u662f\u4e07\u4e07\u9519\u4e0d\u5f97\u7684\uff0c\u4f60 \u53ea\u8981\u505a\u9519\u4e86\u4e00\u6b21\uff0c\u5c31\u53ea\u6709\u4e00\u6761\u8def\u53ef\u8d70\u3002\u201d \u201c\u6b7b\u8def\uff1f\u201d\u53f8\u7a7a\u6458\u661f\u95ee\u3002 \u6728\u9053\u4eba\u70b9\u70b9\u5934\uff0c\u62ff\u8d77\u4e86\u684c\u4e0a\u7684\u53e4\u94dc\u9547\u7eb8\uff0c\u5341\u4e8c\u5f20\u7eb8\u5361\u4e0a\uff0c\u6709\u5341\u4e8c\u4e2a\u4eba\u7684\u540d\u5b57\u3002 \u5341\u4e8c\u4e2a\u4e86\u4e0d\u8d77\u7684\u540d\u5b57\uff01 \u201c\u4ed6\u4eec\u672c\u90fd\u4e0d\u8be5\u6b7b\u7684\uff0c\u65e0\u8bba\u8c01\u8981\u6740\u4ed6\u4eec\uff0c\u90fd\u5f88\u4e0d\u5bb9\u6613\uff0c\u53ea\u53ef\u60dc\u4ed6\u4eec\u90fd\u72af\u4e86\u4e2a\u81f4\u547d\u7684 \u9519\u8bef\u3002\u201d \u4ed6\u4ece\u53e0\u7eb8\u5361\u4e2d\u62bd\u51fa\u4e86\u56db\u5f20\uff0c\u5c24\u5176\u662f\u8fd9\u56db\u4e2a\u4eba\uff0c\u4ed6\u4eec\u7684\u540d\u5b50\u4f60\u4eec\u60f3\u5fc5\u4e5f\u542c\u8bf4\u8fc7\uff01 \u56db\u5f20\u7eb8\u5361\uff0c\u56db\u4e2a\u540d\u5b57\u3002 \u9ad8\u6d9b\uff1a\u98ce\u5c3e\u5e2e\u5185\u4e09\u5802\u9999\u4e3b\u3002 \u7f6a\u540d\uff1a\u901a\u654c\u53db\u53cb\u3002 \u6355\u6740\u8005\uff1a\u897f\u95e8\u5439\u96ea\u3002 \u7ed3\u679c\u9003\u4ea1\u5341\u4e8c\u65e5\uff0c\u6b7b\u4e8e\u6cbc\u6cfd\u4e2d\u3002 \u987e\u98de\u4e91\uff1a\u5df4\u5c71\u5251\u5ba2\u8863\u94b5\u4f20\u4eba\u3002 \u7f6a\u540d\uff1a\u6740\u53cb\u4eba\u5b50\uff0c\u6deb\u53cb\u4eba\u59bb\u3002 \u6355\u6740\u8005\uff1a\u897f\u95e8\u5439\u96ea\u3002 \u7ed3\u679c\uff1a\u9003\u4ea1\u5341\u4e94\u65e5\uff0c\u6b7b\u4e8e\u95f9\u5e02\u4e2d\u3002 \u67f3\u9752\u9752\uff0c\u6dee\u5357\u5927\u4fa0\u5973\uff0c\u70b9\u82cd\u5251\u5ba2\u8c22\u575a\u59bb\u3002 \u7f6a\u540d\uff1a\u901a\u5978\uff0c\u6740\u592b\u3002 \u6355\u6740\u8005\uff1a\u897f\u95e8\u5439\u96ea\u3002 \u7ed3\u679c\uff1a\u9003\u4ea1\u5341\u4e5d\u65e5\uff0c\u6b7b\u4e8e\u8352\u6f20\u4e2d\u3002 \u201c\u72ec\u81c2\u795e\u9f99\u201d\u6d77\u5947\u9614\u3002 \u7f6a\u540d\uff1a\u6b8b\u6740\u65e0\u8f9c\u3002 \u6355\u6740\u8005\uff1a\u897f\u95e8\u5439\u96ea\u3002 \u7ed3\u679c\uff1a\u9003\u4ea1\u5341\u4e5d\u65e5\uff0c\u6d77\u4e0a\u8986\u821f\u6b7b\u3002 \u8fd9\u56db\u4e2a\u4eba\u7684\u540d\u5b57\uff0c\u5927\u5bb6\u5f53\u7136\u5168\u90fd\u542c\u8bf4\u8fc7\uff0c\u4f46\u5927\u5bb6\u6700\u719f\u6089\u7684\uff0c\u5374\u8fd8\u662f\u897f\u95e8\u5439\u96ea\uff01 \u53ea\u8981\u662f\u7ec3\u8fc7\u6b66\u7684\u4eba\uff0c\u6709\u8c01\u4e0d\u77e5\u9053\u897f\u95e8\u5439\u96ea\uff1f\u53c8\u6709\u8c01\u6562\u8bf4\u4ed6\u7684\u5251\u6cd5\u4e0d\u662f\u5929\u4e0b\u7b2c\u4e00\uff01 \u6f47\u6e58\u5251\u5ba2\u5ffd\u7136\u9053\uff1a\u201c\u6211\u89c1\u8fc7\u897f\u95e8\u5439\u96ea\u3002\u201d \u7ecf\u8fc7\u4e86\u7d2b\u7981\u4e4b\u98a0\u90a3\u4e00\u6218\u4e4b\u540e\uff0c\u8fde\u8fd9\u4f4d\u5927\u5185\u7b2c\u4e00\u9ad8\u624b\uff0c\u90fd\u4e0d\u80fd\u4e0d\u627f\u8ba4\u4ed6\u7684\u5251\u6cd5\u5b9e\u5728\u65e0 \u4eba\u80fd\u53ca\uff0c\u4f46\u6211\u5374\u770b\u4e0d\u51fa\u4ed6\u662f\u4e2a\u597d\u7ba1\u95f2\u4e8b\u7684\u4eba\u3002\u201d \u82b1\u6ee1\u697c\u9053\uff1a\u201c\u4ed6\u7ba1\u7684\u5e76\u4e0d\u662f\u95f9\u4e8b\u3002\u201d \u53f8\u7a7a\u6458\u661f\u7acb\u523b\u63a5\u7740\u9053\uff1a\u201c\u4ed6\u81ea\u5df1\u867d\u7136\u5f88\u5c11\u4ea4\u670b\u53cb\uff0c\u5374\u6700\u6068\u51fa\u5356\u670b\u53cb\u7684\u4eba\u3002\u201d \u6f47\u6e58\u5251\u5ba2\u95ed\u4e0a\u4e86\u5634\uff0c\u5510\u4e8c\u5148\u751f\u5374\u5f00\u4e86\u53e3\u3002 \u8700\u4e2d\u5510\u95e8\u7684\u6bd2\u836f\u6697\u5668\u540d\u9707\u5929\u4e0b\uff0c\u5510\u4e8c\u5148\u751f\u7684\u4e0d\u559c\u6b22\u8bf4\u8bdd\u4e5f\u540c\u6837\u5f88\u6709\u540d\uff0c\u73b0\u5728\u5374\u5ffd\u7136 \u95ee\u5230\uff0c\u4f60\u8ba4\u4e3a\u4ed6\u4eec\u72af\u7684\u81f4\u547d\u9519\u8bef\u662f\u51fa\u5356\u670b\u53cb\uff1f\u201d \u53f8\u7a7a\u6458\u661f\u9053\uff1a\u201c\u96be\u9053\u4e0d\u662f\uff1f\u201d \u5510\u4e8c\u5148\u751f\u6447\u6447\u5934\uff0c\u6ca1\u6709\u518d\u8bf4\u4e00\u4e2a\u5b87\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u4ed6\u610f\u601d\u4e00\u5b9a\u5df2\u6709\u4eba\u660e\u767d\u3002 \u679c\u7136\u6709\u4eba\u660e\u767d\uff0c\u4ed6\u4eec\u72af\u81f4\u547d\u7684\u9519\u8bef\u5374\u662f\u76f8\u540c\u7684\u3002\u201d \u201c\u54ea\u4e00\u70b9\u76f8\u540c\uff1f\u201d \u201c\u897f\u95e8\u5439\u96ea\uff0c\u6728\u9053\u4eba\u7f13\u7f13\u9053\uff1a\u201c\u897f\u95e8\u5439\u96ea\u82e5\u8981\u6740\u4eba\u65f6\uff0c\u6ca1\u6709\u4eba\u80fd\u9003\u5f97\u4e86\u7684\u3002\u201d \u5c31\u7b97\u9003\uff0c\u4e5f\u9003\u4e0d\u8fc7\u5341\u4e5d\u5929\u3002 \u201c\u8fd9\u5341\u4e8c\u4e2a\u4eba\u90fd\u662f\u6b7b\u5728\u897f\u95e8\u5439\u96ea\u5251\u4e0b\u7684\u3002\u201d\u6728\u9053\u4eba\u7684\u8868\u60c5\u66f4\u6c89\u91cd\uff1a\u73b0\u5728\u53c8\u6709\u4e2a\u72af\u4e86 \u548c\u4ed6\u4eec\u540c\u6837\u81f4\u547d\u7684\u9519\u8bef\uff0c\u800c\u4e14\u9519\u5f97\u66f4\u4e25\u91cd\u3002\u201d \u201c\u54e6\uff1f\u201d \u201c\u4ed6\u4e0d\u4f46\u51fa\u5356\u4e86\u670b\u53cb\uff0c\u800c\u4e14\u51fa\u5356\u7684\u5c31\u662f\u897f\u95e8\u5439\u96ea\u3002\u201d \u201c\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u201c\u9646\u5c0f\u51e4\uff01 \u4e00\u9635\u6c89\u9ed8\uff0c\u6c89\u9ed8\u5f97\u4ee4\u4eba\u7a92\u606f\u3002 \u9996\u5148\u6253\u7834\u6c89\u9ed8\u7684\u662f\u6f47\u6e58\u5251\u5ba2\uff1a\u201c\u6211\u77e5\u9053\u9646\u5c0f\u51e4\u4e0d\u4f46\u662f\u897f\u95e8\u5439\u96ea\u7684\u670b\u53cb\uff0c\u8fd8\u662f\u4ed6\u7684\u6069 \u4eba\u3002\u201d \u6728\u9053\u4eba\u53f9\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6069\u5df2\u62a5\u8fc7\u4e86\uff0c\u4ec7\u5374\u8fd8\u6ca1\u6709\u62a5\uff01\u201d \u6f47\u6e58\u5251\u5ba2\u9053\uff1a\u201c\u4ec0\u4e48\u4ec7\uff1f\u201d \u6728\u9053\u4eba\u9053\uff1a\u201c\u593a\u59bb\u3002\u201d \u851a\u6e58\u5251\u5ba2\u8038\u7136\u52a8\u5bb9\uff0c\u9053\uff1a\u201c\u6709\u8bc1\u636e\uff1f\u201d \u6728\u9053\u4eba\u9053\uff1a\u201c\u6709\u3002\u201d \u8427\u6e58\u5251\u5ba2\u9053\uff1a\u4ec0\u4e48\u8bc1\u636e\u201d \u6728\u9053\u4eba\u9053\uff1a\u201c\u4ed6\u4eb2\u773c\u770b\u89c1\u4ed6\u4eec\u5728\u5e8a\u4e0a\u7684\u3002\u201d \u6f47\u6e58\u5251\u5ba2\u5ffd\u7136\u62ff\u8d77\u9762\u524d\u7684\u9152\u676f\uff0c\u4e00\u996e\u800c\u5c3d\uff0c\u53f8\u7a7a\u6458\u661f\u559d\u5f97\u6bd4\u4ed6\u66f4\u5feb\u3002 \u552f\u4e00\u8fd8\u80fd\u4fdd\u6301\u9547\u9759\u7684\u662f\u82b1\u6ee1\u697c\uff0c\u9152\u676f\u662f\u6ee1\u7684\uff0c\u4ed6\u5374\u53ea\u6d45\u6d45\u555c\u4e86\u4e00\u53e3\uff1a\u9646\u5c0f\u51e4\u7edd\u4e0d\u662f \u90a3\u79cd\u4eba\uff0c\u8fd9\u4ef6\u4e8b\u5176\u4e2d\u4e00\u5b9a\u8fd8\u6709\u522b\u7684\u5185\u60c5\u3002\u201d \u53f8\u7a7a\u6458\u661f\u7acb\u523b\u540c\u610f\uff1a\uff0c\u4e5f\u8bb8\u4ed6\u4e2d\u4e86\u8ff7\u836f\uff0c\u4e5f\u8bb8\u4ed6\u4eec\u5728\u5e8a\u4e0a\u6839\u672c\u5c31\u6ca1\u6709\u505a\u4ec0\u4e48\u4e8b\u3002 \u8fd9\u4e9b\u7406\u7531\u90fd\u4e0d\u592a\u597d\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u4e0d\u592a\u6ee1\u610f\uff0c\u6240\u4ee5\u4ed6\u53c8\u559d\u4e86\u4e00\u676f\u3002 \u4e0b\u7ed3\u8bba\u7684\u4eba\u901a\u5e38\u90fd\u662f\u6700\u5c11\u5f00\u53e3\u7684\u4eba\u3002 \u201c\u6211\u4e0d\u8ba4\u5f97\u9646\u5c0f\u51e4\uff0c\u53ef\u662f\u6211\u77e5\u9053\u4ed6\u5bf9\u5510\u5bb6\u6709\u6069\u3002\u201d\u5510\u4e8c\u5148\u751f\u4e0b\u4e86\u7ed3\u8bba\uff0c\u4e0d\u7ba1\u8fd9\u4ef6\u4e8b \u662f\u5426\u522b\u6709\u5185\u60c5\uff0c\u6211\u4eec\u90fd\u8981\u627e\u4ed6\u4eec\u5f53\u9762\u95ee\u6e05\u695aoo \u6728\u9053\u4eba\u5374\u5728\u6447\u5934\u3002 \u53f8\u7a7a\u6458\u661f\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u53bb\u627e\uff1f\u201d \u6728\u9053\u4eba\u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u60f3\u627e\uff0c\u662f\u627e\u4e0d\u5230\u3002\u201d \u8fd9\u4ef6\u4e8b\u4e00\u53d1\u751f\uff0c\u9646\u5c0f\u51e4\u5c31\u5df2\u9003\u4ea1\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u9003\u5230\u54ea\u91cc\u53bb\u4e86\u3002 \u6728\u9053\u4eba\u5c55\u5f00\u90a3\u5341\u4e8c\u5f20\u5361\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u6211\u8bf7\u4f60\u4eec\u6765\u770b\u8fd9\u4e9b\uff0e\uff0e\uff0e\uff0e\uff0e\uff0e\u201d \u53f8\u7a7a\u6458\u661f\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u9646\u5c0f\u51e4\u65e2\u4e0d\u662f\u9ad8\u6d9b\uff0c\u4e5f\u4e0d\u662f\u72ec\u81c2\u795e\u9f99\uff0c\u8fd9\u4e9b\u6df7\u8d26\u738b \u516b\u86cb\u7684\u4e8b\uff0c\u8ddf\u6211\u4eec\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u6728\u9053\u4eba\u9053\uff1a\u201c\u6709\u4e00\u70b9\u5173\u7cfb\u3002\u2019 \u53f8\u7a7a\u6458\u661f\u9053\uff1a\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u6728\u9053\u4eba\u9053\uff1a\u201c\u4ed6\u4eec\u9003\u4ea1\u7684\u8def\u7ebf\u3002\u201d \u8981\u60f3\u627e\u5230\u9646\u5c0f\u51e4\uff0c\u5c31\u4e00\u5b9a\u8981\u5148\u5224\u65ad\u4ed6\u662f\u4ece\u54ea\u6761\u8def\u4e0a\u9003\u7684\u3002 \u6728\u9053\u4eba\u53c8\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u4e0d\u4f46\u6b66\u529f\u90fd\u5f88\u9ad8\uff0c\u800c\u4e14\u90fd\u662f\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u72e1\u733e\u673a\u8b66\u7684\u8001\u6c5f\u6e56\uff0c \u4ed6\u4eec\u51c6\u5907\u9003\u4ea1\u7684\u65f6\u5019\uff0c\u4e00\u5b9a\u90fd\u7ecf\u8fc7\u5f88\u5468\u5bc6\u7684\u8ba1\u5212\uff0c\u4ed6\u4eec\u9009\u62e9\u7684\u8def\u7ebf\uff0c\u4e00\u5b9a\u90fd\u60f3\u5f53\u4e0d \u9519\u3002\u201d \u53f8\u7a7a\u6458\u661f\u51b7\u51b7\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4ed6\u4eec\u8fd8\u662f\u9003\u4e0d\u4e86\u3002\u201d \u6728\u9053\u4eba\u9053\uff1a\u201c\u867d\u7136\u9003\u4e0d\u4e86\uff0c\u5374\u8fd8\u662f\u53ef\u4ee5\u4f5c\u4e3a\u6211\u4eec\u53c2\u8003\u3002\u201d \u8fd9\u5341\u4e8c\u4e2a\u4eba\u9009\u62e9\u7684\u9003\u4ea1\u8def\u7ebf\uff0c\u5927\u81f4\u53ef\u4ee5\u5206\u4e3a\u56db\u6761\u2014 \u4e70\u821f\u4eba\u6d77\u3002 \u51fa\u5173\u5165\u6c99\u6f20\u3002 \u6df7\u8ff9\u4e8e\u95f9\u5e02\u3002 \u6d41\u7a9c\u4e8e\u7a77\u5c71\u6076\u6c34\u4e2d\u3002 \u6728\u9053\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u9646\u5c0f\u51e4\u7684\u8001\u670b\u53cb\uff0c\u90fd\u5f88\u4e86\u89e3\u4ed6\u7684\u813e\u6c14\uff0c\u4f60\u4eec\u60f3\u4ed6\u4f1a\u9009\u62e9\u54ea\u6761 \u8def\uff1f\u201d \u6ca1\u6709\u4eba\u80fd\u56de\u7b54\u3002 \u8c01\u4e5f\u4e0d\u6562\u8ba4\u4e3a\u81ea\u5df1\u7684\u5224\u65ad\u7edd\u5bf9\u6b63\u786e\u3002 \u82b1\u6ee1\u697c\u7f13\u7f13\u9053\uff1a\u201c\u4ed6\u7edd\u4e0d\u4f1a\u5230\u6d77\u4e0a\u53bb\uff0c\u4e5f\u4e0d\u4f1a\u5165\u6c99\u6f20\u3002\u201d \u6ca1\u6709\u4eba\u95ee\u4ed6\u600e\u4e48\u80fd\u786e\u5b9a\u8fd9\u4e00\u70b9\u7684\uff0c\u56e0\u4e3a\u6bcf\u4e2a\u4eba\u90fd\u77e5\u9053\u4ed6\u6709\u79cd\u5947\u5f02\u7684\u672c\u80fd\u548c\u89e6\u89c9\u3002 \u53f8\u7a7a\u6458\u661f\u559d\u5e72\u4e86\u7b2c\u516b\u676f\u9152\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u80fd\u786e\u5b9a\u4e00\u70b9\u3002\u201d \u5927\u5bb6\u90fd\u5728\u542c\u7740\u3002 \u53f8\u7a7a\u6458\u661f\u9053\uff1a\u201c\u9646\u5c0f\u51e4\u7edd\u4e0d\u4f1a\u6b7b\u3002 \u4ed6\u7684\u5224\u65ad\u6709\u4eba\u6000\u7591\u4e86\uff0c\u4e3a\u4ec0\u4e48\uff1f\u201d \u53f8\u7a7a\u6458\u661f\u9053\uff1a\u201c\u6211\u77e5\u9053\u9646\u5c0f\u51e4\u7684\u6b66\u529f\uff0c\u4e5f\u89c1\u8fc7\u897f\u95e8\u5439\u96ea\u7684\u5251\u6cd5\u3002\u201d \u4ed6\u5f53\u7136\u4e5f\u4e0d\u80fd\u5426\u8ba4\u897f\u95e8\u5439\u96ea\u5251\u6cd5\u4e4b\u5feb\u901f\u51c6\u786e\uff0c\u53ef\u662f\u81ea\u4ece\u4ed6\u5a36\u59bb\u751f\u5b50\u540e\uff0c\u4ed6\u7684\u5251\u6cd5\u5c31 \u53d8\u5f97\u8f6f\u5f31\u4e86\uff0c\u56e0\u4e3a\u4ed6\u7684\u5fc3\u5df2\u8f6f\u5f31\u3002\u201d \u56e0\u4e3a\u4ed6\u5df2\u4e0d\u518d\u662f\u5251\u4e4b\u795e\uff0c\u5df2\u6e10\u6e10\u6709\u4e86\u4eba\u6027\u3002 \u6728\u9053\u4eba\u9053\uff1a\u201c\u6211\u672c\u6765\u4e5f\u8ba4\u4e3a\u5982\u6b64\uff0c\u73b0\u5728\u624d\u77e5\u9053\u6211\u4eec\u90fd\u9519 \u53f8\u7a7a\u6458\u661f\u9053\uff1a\u201c\u6211\u4eec\u6ca1\u6709\u9519ao \u6728\u9053\u4eba\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u5728\u7d2b\u7981\u4e4b\u98a0\u90a3\u4e00\u6b21\u51b3\u6218\u524d\uff0c\u4ed6\u7684\u5251\u786e\u5b9e\u5df2\u6e10\u8f6f\u5f31\uff0c\u56e0\u4e3a\u4ed6\u5bf9 \u59bb\u5b50\uff0c\u5df2\u8d85\u8d8a\u4e86\u4ed6\u5bf9\u5251\u7684\u72c2\u70ed\u3002\u201d \u8427\u6e58\u5251\u5ba2\u663e\u7136\u5df2\u4e86\u89e3\u8fd9\u53e5\u8bdd\u4e2d\u7684\u6df1\u610f\uff0c\u53ef\u662f\u4ed6\u6218\u80dc\u4e86\u81ea\u4e91\u57ce\u4e3b\u540e\uff0c\u5c31\u4e0d\u540c\u4e86\u3002\u201d \u65e0\u8bba\u8c01\u51fb\u8d25\u4e86\u767d\u4e91\u57ce\u4e3b\u8fd9\u79cd\u7edd\u4e16\u9ad8\u624b\u540e\uff0c\u90fd\u96be\u514d\u4f1a\u89c9\u5f97\u610f\u6c14\u98ce\u53d1\uff0c\u60f3\u66f4\u4e0a\u5c42\u697c\u3002 \u7d2b\u7981\u4e4b\u98a0\u90a3\u4e00\u6218\uff0c\u65e0\u7591\u53c8\u6fc0\u53d1\u4e86\u4ed6\u5bf9\u5251\u7684\u72c2\u70ed\uff0c\u53c8\u8d85\u8d8a\u4e86\u4ed6\u5bf9\u59bb\u5b50\u7684\u7231\u3002 \u4e5f\u8bb8\u5c31\u56e0\u4e3a\u4ed6\u51b7\u843d\u4e86\u59bb\u5b50\uff0c\u5f15\u8d77\u4e86\u9646\u5c0f\u51e4\u7684\u540c\u60c5\uff0c\u624d\u4f1a\u53d1\u751f\u8fd9\u4ef6\u4e8b\u3002 \u6bcf\u4e2a\u4eba\u5fc3\u91cc\u90fd\u60f3\u5230\u4e86\u8fd9\u4e00\u70b9\uff0c\u5374\u6ca1\u6709\u4eba\u613f\u610f\u8bf4\u51fa\u53e3\u3002 \u6728\u9053\u4eba\u9053\uff1a\u201c\u524d\u4e9b\u65f6\u6211\u89c1\u8fc7\u9646\u5c0f\u51e4\uff0c\u4ed6\u81ea\u5df1\u544a\u8bc9\u6211\uff0c\u897f\u95e8\u5439\u96ea\u7684\u5251\u6cd5\uff0c\u5df2\u8fbe\u5230\u2018\u65e0 \u5251\u2019\u7684\u5883\u754c\u3002 \u4ec0\u4e48\u53eb\u201c\u65e0\u5251\u201d\u7684\u5883\u754c\u3002 \u4ed6\u7684\u638c\u4e2d\u867d\u65e0\u5251\uff0c\u53ef\u662f\u4ed6\u7684\u5251\u4ecd\u5728\uff0c\u5230\u5904\u90fd\u5728\u3002 \u4ed6\u7684\u4eba\u5df2\u4e0e\u5251\u6eb6\u4e3a\u4e00\u4f53\uff0c\u4ed6\u7684\u4eba\u5c31\u662f\u5251\uff0c\u53ea\u8981\u4ed6\u7684\u4eba\u5728\uff0c\u5929\u5730\u4e07\u7269\uff0c\u90fd\u662f\u4ed6\u7684 \u5251\u300f\u2019 \u8fd9\u79cd\u5883\u754c\u51e0\u4e4e\u5df2\u5230\u8fbe\u4e86\u5251\u672f\u4e2d\u7684\u98a0\u5cf0\uff0c\u51e0\u4e4e\u5df2\u6ca1\u6709\u4eba\u80fd\u8d85\u8d8aJ \u6728\u9053\u4eba\u53f9\u606f\u7740\uff0c\u53c8\u9053\uff1a\u201c\u6211\u89c1\u5230\u9646\u5c0f\u51e4\u65f6\uff0c\u4ed6\u5df2\u9189\u4e86\uff0c\u4ed6\u8fd8\u97f3\u8bc9\u6211\uff0c\u5047\u5982\u8fd9\u4e16\u4e0a\u8fd8 \u6709\u4e00\u4e2a\u4eba\u80fd\u6740\u4ed6\uff0c\u8fd9\u4e2a\u4eba\u5c31\u662f\u897f\u95e8\u5439\u96eaJo \u53c8\u662f\u4e00\u9635\u6c89\u9ed8\uff0c\u5927\u5bb6\u5fc3\u91cc\u90fd\u6709\u4e86\u7ed3\u8bba \u53ea\u8981\u897f\u95e8\u5439\u96ea\u8ffd\u4e0a\u9646\u5c0f\u51e4\uff0c\u9646\u5c0f\u51e4\u5c31\u5fc5\u5c06\u6b7b\u5728\u4ed6\u7684\u5251 \u73b0\u5728\u7684\u95ee\u9898\u662f \u9646\u5c0f\u51e4\u7a76\u7adf\u9003\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u80fd\u9003\u591a\u4e45\uff1f \u201c\u65e2\u7136\u4ed6\u4e0d\u4f1a\u5230\u6d77\u4e0a\u53bb\uff0c\u4e5f\u4eba\u6c99\u6f20\uff0c\u90a3\u4e48\u4ed6\u4e0d\u662f\u6df7\u8ff9\u5728\u95f9\u5e02\u4e2d\uff0c\u5c31\u662f\u6d41\u7a9c\u5728\u7a77\u5c71\u6076 \u6c34\u95f4\u3002\u201d \u8fd9\u8303\u56f4\u5df2\u7f29\u5c0f\uff0c\u53ef\u662f\u53c8\u6709\u8c01\u77e5\u9053\u8fd9\u4e16\u4e0a\u7684\u95f9\u5e02\u6709\u591a\u5c11\uff1f\u5c71\u4e0d\u6709\u591a\u5c11\uff1f\u201d \u5510\u4e8c\u5148\u751f\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u8d70\u51fa\u53bb\u3002 \u53f8\u7a7a\u6458\u661f\u5f15\u676f\u5728\u624b\uff0c\u5927\u58f0\u95ee\uff0c\u4f60\u60f3\u8d70\uff1f\u201d \u5510\u4e8c\u5148\u751f\u51b7\u51b7\u9053\uff1a\u201c\u6211\u4e0d\u662f\u6765\u559d\u9152\u7684\u3002\u201d \u53f8\u7a7a\u6458\u661f\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u96be\u9053\u4f60\u4e0d\u60f3\u7ba1\uff1f\u201d \u5510\u4e8c\u5148\u751f\u9053\uff1a\u201c\u4e0d\u662f\u4e0d\u60f3\u7ba1\uff0c\u662f\u7ba1\u4e0d\u4e86\u3002\u201d \u53e4\u677e\u5c45\u58eb\u5ffd\u7136\u4e5f\u957f\u957f\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u54fa\u54fa\u9053\uff1a\u201c\u7684\u786e\u7ba1\u4e0d \u82e6\u74dc\u548c\u5c1a\u7acb\u523b\u70b9\u5934\uff0c\u9053\uff1a\u201c\u7684\u786e\u7684\u786e\u7684\u786e\u2026\u2026\u201d \u4ed6\u8bf4\u5230\u7b2c\u4e09\u4e2a\u201c\u7684\u786e\u201d\u65f6\uff0c\u4ed6\u4eec\u4e09\u4e2a\u90fd\u5df2\u8d70\u4e86\u51fa\u53bb\u3002 \u6f47\u6e58\u5251\u5ba2\u8d70\u5f97\u4e5f\u5e76\u4e0d\u6bd4\u4ed6\u4eec\u6162\u3002 \u53f8\u7a7a\u6458\u661f\u770b\u4e86\u770b\u676f\u4e2d\u7684\u9152\uff0c\u5ffd\u7136\u91cd\u91cd\u7684\u653e\u4e0b\u9152\u676f\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u662f\u6765\u559d\u9152\u7684\uff0c \u54ea\u4e2a\u5b59\u5b50\u738b\u516b\u86cb\u624d\u662f\u6765\u559d\u9152\u7684\u3002\u201d \u4ed6\u5c45\u7136\u4e5f\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\u3002 \u5c4b\u5b50\u91cc\u5ffd\u7136\u53ea\u5269\u4e0b\u4e24\u4e2a\u4eba\uff0c\u8fd8\u80fd\u4fdd\u6301\u9547\u9759\u7684\u5374\u53ea\u6709\u82b1\u6ee1\u697c\u4e00\u4e2a\u3002 \u201c\u6ce2\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u6728\u9053\u4eba\u624b\u91cc\u7684\u9152\u676f\u5df2\u7c89\u788e\u3002 \u82b1\u6ee1\u697c\u5374\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u4eec\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u6728\u9053\u4eba\u51b7\u51b7\u9053\uff1a\u201c\u9b3c\u77e5\u9053\u3002\u201d \u82b1\u6ee1\u697c\u9053\uff1a\u201c\u6211\u77e5\u9053\uff1a\u201c \u4ed6\u8fd8\u5728\u5fae\u7b11\uff0c\u6211\u4e0d\u662f\u9b3c\uff0c\u4f46\u662f\u6211\u77e5\u9053\uff1a\u201c \u6728\u9053\u4eba\u5fcd\u4e0d\u4f73\u95ee\uff1a\u201c\u4f60\u8bf4\u4ed6\u4eec\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u82b1\u6ee1\u697c\u9053\uff1a\u201c\u73b0\u5728\u6211\u4eec\u82e5\u8d76\u5230\u897f\u95e8\u5c71\u5e84\u53bb\uff0c\u5c31\u4e00\u5b9a\u53ef\u4ee5\u627e\u5230\u4ed6\u4eec\uff0c\u8fde\u4e00\u4e2a\u90fd\u4e0d\u4f1a \u5c11\u3002\u201d \u6728\u9053\u4eba\u4e0d\u61c2\u3002 \u82b1\u6ee1\u697c\u53c8\u9053\uff1a\u201c\u4ed6\u4eec\u5230\u90a3\u91cc\u53bb\uff0c\u53ea\u56e0\u4ed6\u4eec\u90fd\u60f3\u77e5\u9053\u4e00\u4ef6\u4e8b\uff0d\uff0d\u201d \u5047\u5982\u6211\u662f\u9646\u5c0f\u51e4\uff0c\u8981\u4ece\u8fd9\u91cc\u5f00\u59cb\u9003\u4ea1\uff0c\u6211\u4f1a\u8d70\u90a3\u6761\u8def\u3002 \u82b1\u6ee1\u697c\u9053\uff1a\u201c\u7b49\u4ed6\u4eec\u60f3\u901a\u4e86\u65f6\uff0c\u4ed6\u4eec\u4e00\u5b9a\u4f1a\u671d\u90a3\u6761\u8def\u4e0a\u8ffd\u4e0b\u53bbao \u6728\u9053\u4eba\u9053\uff1a\u201c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\uff1f\u201d \u82b1\u6ee1\u697c\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4eec\u751f\u6015\u81ea\u5df1\u5224\u65ad\u9519\u8bef\uff0c\u5f71\u54cd\u4e86\u522b\u4eba\u3002 \u6728\u9053\u4eba\u9053\uff1a\u201c\u4f60\u6709\u628a\u63e1\u786e\u5b9a\uff1f\u2026 \u82b1\u6ee1\u697c\u70b9\u70b9\u5934\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u6211\u6709\u628a\u63e1\uff0c\u56e0\u4e3a\u6211\u77e5\u9053\u4ed6\u4eec\u90fd\u662f\u9646\u5c0f\u51e4\u7684\u670b\u53cb\u3002\u201d \u4ed6\u7684\u8138\u4e0a\u5728\u53d1\u5149\uff0c\u4ed6\u7684\u5fae\u7b11\u4e5f\u5728\u53d1\u7740\u5149\uff0c\u4ed6\u70ed\u7231\u751f\u547d\uff0c\u5bf9\u4eba\u6027\u4e2d\u5584\u826f\u7684\u4e00\u9762\uff0c\u4ed6\u6c38 \u8fdc\u90fd\u5145\u6ee1\u4e86\u4fe1\u5fc3\u3002 \u6728\u9053\u4eba\u7ec8\u4e8e\u957f\u957f\u53f9\u606f\uff0c\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u80fd\u6709\u9646\u5c0f\u51e4\u8fd9\u4e48\u591a\u670b\u53cb\uff0c\u5b9e\u5728\u771f\u4e0d\u9519\uff0c\u53ea\u53ef\u60dc \u4ed6\u81ea\u5df1\u8fd9\u4e00\u6b21\u5374\u9519\u4e86\u3002\u201d \u4ed6\u62cd\u4e86\u62cd\u82b1\u6ee1\u697c\u7684\u80a9\uff0c\u9053\uff1a\u201c\u6211\u4eec\u8d70\uff0c\u5047\u5982\u8fd9\u4e16\u4e0a\u8fd8\u6709\u4e00\u4e2a\u4eba\u80fd\u627e\u5230\u9646\u5c0f\u51e4\uff0c\u90a3\u4e2a \u4eba\u4e00\u5b9a\u5c31\u662f\u4f60\u3002\u201d \u82b1\u6ee1\u697c\u9053\uff1a\u201c\u4e0d\u662f\u6211\u3002\u201d \u6728\u9053\u4eba\u9053\uff1a\u201c\u4e0d\u662f\u4f60\u662f\u8c01\uff1f\u201d \u82b1\u6ee1\u697c\u9053\uff1a\u201c\u662f\u4ed6\u81ea\u5df1\u3002\u201d \u4e00\u4e2a\u4eba\u82e5\u5df2\u8ff7\u5931\u4e86\u81ea\u5df1\uff0c\u90a3\u4e48\u9664\u4e86\u4ed6\u81ea\u5df1\u5916\uff0c\u8fd8\u6709\u8c01\u80fd\u627e\u5f97\u5230\u4ed6\u5462\uff1f\u201d \u7b2c\u4e8c\u7ae0\u3000\u540c\u75c5\u76f8\u601c>> \u53e4\u9f99\u300a\u9646\u5c0f\u51e4\u7cfb\u5217\xb7\u5e7d\u7075\u5c71\u5e84\u300b \u7b2c\u4e8c\u7ae0\u3000\u540c\u75c5\u76f8\u601c \u5c31\u7b97\u9646\u5c0f\u51e4\u5df2\u8ff7\u5931\u4e86\u81ea\u5df1\uff0c\u81f3\u5c11\u8fd8\u6ca1\u6709\u8ff7\u5931\u65b9\u5411\u3002 \u4ed6\u786e\u4fe1\u8fd9\u6761\u8def\u662f\u5f80\u6b63\u897f\u65b9\u8d70\u7684\uff0c\u8d70\u8fc7\u9762\u524d\u7684\u5c71\u6e24\uff0c\u5c31\u53ef\u4ee5\u627e\u5230\u6e05\u6cc9\u98df\u6c34\u3002 \u73b0\u5728\u591c\u5df2\u6df1\uff0c\u5c71\u4e2d\u96fe\u6b63\u6d53\uff0c\u4ed6\u8fd8\u662f\u76f8\u4fe1\u81ea\u5df1\u7684\u5224\u65ad\u7edd\u5bf9\u6b63\u786e\u3002\u53ef\u662f\u8fd9\u4e00\u6b21\u4ed6\u53c8\u9519 \u5382\u3002 \u524d\u9762\u65e2\u6ca1\u6709\u5c71\u52a9\uff0c\u66f4\u6ca1\u6709\u6cc9\u6c34\uff0c\u53ea\u6709\u4e00\u7247\u83bd\u83bd\u5bc6\u5bc6\u7684\u539f\u59cb\u4e1b\u6797\u3002 \u9965\u997f\u672c\u662f\u4eba\u7c7b\u6700\u5927\u75db\u82e6\u4e4b\u4e00\uff0c\u53ef\u662f\u548c\u5e72\u6e34\u6bd4\u8d77\u6765\uff0c\u9965\u997f\u5c31\u53d8\u6210\u4e86\u4e00\u79cd\u6bd4\u8f83\u5bb9\u6613\u5fcd\u53d7 \u7684\u4e8b\u3002 \u4ed6\u7684\u5634\u5507\u5df2\u5e72\u88c2\uff0c\u8863\u670d\u5df2\u7834\u788e\uff0c\u80f8\u819b\u4e0a\u7684\u4f24\u53e3\u5df2\u5f00\u59cb\u6708\u4e2d\u3002 \u4ed6\u5728\u8fd9\u8fde\u6cc9\u6c34\u90fd\u627e\u4e0d\u5230\u7684\u7a77\u5c71\u6076\u8c37\u95f4\uff0c\u9003\u4ea1\u5df2\u6574\u6574\u4e09\u3002 \u73b0\u5728\u5c31\u7b97\u662f\u4ed6\u7684\u670b\u53cb\u770b\u89c1\u4ed6\uff0c\u672a\u5fc5\u80fd\u8ba4\u5f97\u51fa\u4ed6\u5c31\u662f\u9646\u5c0f\u98ce\u3002 \u90a3\u4e2a\u98ce\u6d41\u6f47\u6d12\uff0c\u603b\u662f\u8ba9\u5973\u5b69\u5b50\u7740\u8ff7\u7684\u9646\u5c0f\u51e4\u3002 \u4e1b\u6797\u4e2d\u4e00\u7247\u9ed1\u6697\uff0c\u9ed1\u6697\u4e2d\u5145\u6ee1\u4e86\u5404\u5f0f\u5404\u6837\u7684\u5371\u9669\uff0c\u6bcf\u4e00\u79cd\u90fd\u8db3\u4ee5\u81f4\u547d\uff0c\u82e5\u662f\u5728\u4e1b\u6797 \u4e2d\u8ff7\u5931\u4e86\u65b9\u5411\uff0c\u9965\u6e34\u5c31\u8db3\u4ee5\u81f4\u547d\uff01 \u4ed6\u662f\u4e0d\u662f\u80fd\u8d70\u5f97\u51fa\u8ff7\u7247\u4e1b\u6797\uff0c\u4ed6\u81ea\u5df1\u4e5f\u5b8c\u5168\u6ca1\u6709\u628a\u63e1\u3002 \u4ed6\u5bf9\u81ea\u5df1\u7684\u5224\u65ad\u5df2\u5931\u53bb\u4e86\u4fe1\u5fc3\u3002 \u53ef\u662f\u4ed6\u53ea\u6709\u5f80\u524d\uff0c\u65e2\u6ca1\u6709\u522b\u7684\u8def\u8ba9\u4ed6\u9009\u62e9\uff0c\u66f4\u4e0d\u80fd\u9000\uff01 \u540e\u9000\u53ea\u6709\u66f4\u5371\u9669\uff0c\u66f4\u53ef\u6015\u3002 \u56e0\u4e3a\u897f\u95e8\u5439\u96ea\u5c31\u5728\u4ed6\u540e\u9762\u9489\u7740\u4ed6\uff01 \u867d\u7136\u4ed6\u770b\u4e0d\u89c1\uff0c\u5374\u80fd\u611f\u89c9\u5f97\u5230\u2014\u2014\u611f\u89c9\u5230\u90a3\u79cd\u6740\u4eba\u7684\u5251 \u4ed6\u968f\u65f6\u968f\u5730\uff0c\u90fd\u4f1a\u5ffd\u7136\u65e0\u7f18\u65e0\u6545\u7684\u89c9\u5f97\u80cc\u810a\u53d1\u51b7\uff0c\u8fd9\u65f6\u4ed6\u5c31\u77e5\u9053\u897f\u95e8\u5439\u96ea\u5df2\u79bb\u4ed6\u5f88 \u8fd1\u4e86\u3002 \u9003\u4ea1\u672c\u8eab\u5c31\u662f\u79cd\u75db\u82e6\u3002 \u9965\u6e34\u3001\u75b2\u5026\u3001\u6050\u60e7\u3001\u5fe7\u8651\u2026\u2026\u5c31\u50cf\u65e0\u6570\u6839\u97ad\u5b50\uff0c\u5728\u4e0d\u505c\u7684\u62bd\u6253\u7740\u4ed6\u3002 \u8fd9\u5df2\u8db3\u591f\u4f7f\u4ed6\u7684\u8eab\u5fc3\u5d29\u6e83\uff0c\u4f55\u51b5\u4ed6\u8fd8\u53d7\u4e86\u4f24\u3002 \u5251\u4f24\uff01 \u6bcf\u5f53\u4f24\u53e3\u53d1\u75bc\u65f6\uff0c\u4ed6\u5c31\u4f1a\u60f3\u5230\u90a3\u5feb\u5f97\u4ee4\u4eba\u4e0d\u53ef\u601d\u8bae\u7684\u4e00\u5251\uff01 \u638c\u4e2d\u672c\u5df2\u201c\u65e0\u5251\u201d\u7684\u897f\u95e8\u5439\u96ea\uff0c\u6bd5\u7adf\u53c8\u62d4\u51fa\u4ed6\u7684\u5251\uff01 \u4e8c\u4e8c\u6211\u7528\u90a3\u67c4\u5251\u51fb\u8d25\u4e86\u53f6\u5b64\u57ce\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u8fd8\u6709\u8c01\u80fd\u914d\u8ba9\u6211\u518d\u7528\u90a3\u67c4\u5251\uff1f \u9646\u5c0f\u51e4\uff0c\u53ea\u6709\u9646\u5c0f\u51e4\uff01 \u4e3a\u4e86\u4f60\uff0c\u6211\u518d\u7528\u8fd9\u67c4\u5251\uff0c\u73b0\u5728\u6211\u7684\u5251\u5df2\u62d4\u51fa\uff0c\u4e0d\u67d3\u4e0a\u4f60\u7684\u8840\uff0c\u7edd\u4e0d\u5165\u9798\uff01 \u6ca1\u6709\u4eba\u80fd\u5f62\u5bb9\u90a3\u4e00\u5251\u950b\u8292\u548c\u901f\u5ea6\uff0c\u6ca1\u6709\u4eba\u80fd\u60f3\u50cf\uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u95ea\u907f\u3002 \u5982\u679c\u5929\u5730\u95f4\u771f\u6709\u4ed9\u4f5b\u9b3c\u795e\uff0c\u4e5f\u5fc5\u5b9a\u4f1a\u56e0\u8fd9\u4e00\u5251\u800c\u5931\u8272\u52a8\u5bb9\u3002 \u5251\u5149\u4e00\u95ea\uff0c\u9c9c\u8840\u6e85\u51fa\uff01 \u6ca1\u6709\u4eba\u80fd\u62db\u67b6\u95ea\u907f\u8fd9\u4e00\u5251\uff0c\u8fde\u9646\u5c0f\u51e4\u4e5f\u4e0d\u80fd\uff0c\u53ef\u662f\u4ed6\u5e76\u6ca1\u6709\u6b7b\uff01 \u80fd\u4e0d\u6b7b\u5df2\u662f\u82e5\u8ff9\uff01 \u5929\u4e0a\u5730\u4e0b\uff0c\u80fd\u5728\u90a3\u5251\u7684\u950b\u8292\u4e0b\u9003\u751f\u7684\uff0c\u6050\u6015\u4e5f\u53ea\u6709\u9646\u5c0f\u51e4\uff01 \u9ed1\u6697\uff0c\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u6697\u3002 \u9ed1\u6697\u4e2d\u7a76\u7adf\u6f5c\u4f0f\u7740\u591a\u5c11\u5371\u9669\uff1f \u9646\u5c0f\u51e4\u8fde\u60f3\u90fd\u6ca1\u6709\u53bb\u60f3\uff0c\u82e5\u662f\u591a\u60f3\u60f3\uff0c\u4ed6\u5f88\u53ef\u80fd\u5c31\u5df2\u5d29\u6e83\uff0c\u751a\u81f3\u4f1a\u53d1\u75af\u3002 \u4ed6\u4e00\u8d70\u5165\u4e86\u8fd9\u7247\u9ed1\u6697\u7684\u4e1b\u6797\uff0c\u5c31\u7b49\u4e8e\u91ce\u517d\u5df2\u843d\u4eba\u9677\u9631\uff0c\u5df2\u5b8c\u5168\u8eab\u4e0d\u7531\u4e3b\u3002 \u8fd8\u662f\u6ca1\u6709\u6c34\uff0c\u6ca1\u6709\u98df\u7269\u3002 \u4ed6\u6298\u4e0b\u4e00\u6839\u6811\u679d\uff0c\u6478\u7d22\u7740\u4e00\u6b65\u6b65\u5f80\u524d\u8d70\uff0c\u5c31\u50cf\u662f\u4e2a\u778e \u8fd9\u6839\u6811\u679d\uff0c\u5c31\u662f\u4ed6\u7684\u660e\u6756\u3002 \u4e00\u4e2a\u6d3b\u751f\u751f\u7684\u4eba\uff0c\u7adf\u8981\u501a\u8d56\u4e00\u6839\u6ca1\u6709\u751f\u547d\u7684\u6728\u5934\u2014\u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u9646\u5c0f\u51e4\u5c31\u7b11\u4e86\u3002 \u4e00\u79cd\u5145\u6ee1\u4e86\u5c48\u8fb1\u3001\u60b2\u54c0\u3001\u75db\u82e6\u548c\u8ba5\u6d88\u7684\u60e8\u7b11\u3002 \u76f4\u5230\u73b0\u5728\uff0c\u4ed6\u624d\u771f\u6b63\u660e\u4e86\u778e\u5b50\u7684\u75db\u82e6\uff0c\u4e5f\u771f\u6b63\u4e86\u89e3\u4e86\u82b1\u6ee1\u697c\u7684\u4f1f\u5927\u3002 \u4e00\u4e2a\u778e\u5b50\u8fd8\u80fd\u6d3b\u5f97\u90a3\u4e48\u5e73\u9759\uff0c\u90a3\u4e48\u5feb\u4e50\uff0c\u4ed6\u7684\u5fc3\u91cc\u80fd\u6709\u591a\u5c11\u7231\uff1f \u524d\u9762\u6709\u6811\uff0c\u4e00\u68f5\u53c8\u9ad8\u53c8\u5927\u7684\u6811\u3002 \u9646\u5c0f\u51e4\u5728\u8fd9\u68f5\u6811\u4e0b\u505c\u4e0b\u6765\uff0c\u5598\u606f\u7740\uff0c\u73b0\u5728\u4e5f\u8bb8\u5df2\u662f\u552f\u4e00\u53ef\u4ee5\u8ba9\u4ed6\u5598\u606f\u7684\u673a\u4f1a\u3002 \u2014\u897f\u95e8\u5439\u96ea\u5728\u8ffd\u4eba\u8fd9\u7247\u4e1b\u6797\u4e4b\u524d\uff0c\u4e5f\u5fc5\u5b9a\u4f1a\u8003\u8651\u7247\u523b\u7684\u3002 \u53ef\u662f\u4ed6\u4e00\u5b9a\u4f1a\u8ffd\u8fdb\u6765\u3002 \u5929\u4e0a\u5730\u4e0b\uff0c\u51e0\u4e4e\u5df2\u6ca1\u6709\u4efb\u4f55\u4e8b\u80fd\u963b\u6b62\u4ed6\uff0c\u4ed6\u5df2\u51b3\u5fc3\u8981\u9646\u5c0f\u98ce\u6b7b\u5728\u4ed6\u7684\u5251\u4e0b\uff01 \u6697\u4e2d\u51e0\u4e4e\u5b8c\u5168\u6ca1\u6709\u58f0\u97f3\uff0c\u53ef\u662f\u8fd9\u7edd\u5bf9\u7684\u9759\u5bc2\uff0c\u4e5f\u6b63\u662f\u79cd\u6700\u53ef\u6015\u7684\u58f0\u97f3\u3002 \u9646\u5c0f\u51e4\u7684\u547c\u5438\u4eff\u4f5b\u4e5f\u5df2\u505c\u987f\uff0c\u7a81\u7136\u95ea\u7535\u822c\u51fa\u624b\uff0c\u7528\u4e24\u6839\u624b\u6307\u4e00\u5939J \u4ec0\u4e48\u90fd\u6ca1\u6709\u770b\u89c1\uff0c\u4f46\u4ed6\u5df2\u51fa\u624b\u3002 \u4ed6\u7684\u51fa\u624b\u5f88\u5c11\u843d\u7a7a\u3002 \u82e5\u662f\u5230\u4e86\u771f\u6b63\u5371\u9669\u7684\u65f6\u5019\uff0c\u4eba\u7c7b\u4e5f\u4f1a\u53d8\u5f97\u50cf\u91ce\u517d\u4e00\u6837\uff0c\u4e5f\u6709\u4e86\u50cf\u91ce\u517d\u822c\u7684\u672c\u80fd\u548c\u7b2c \u516d\u611f\u3002 \u4ed6\u5939\u4f4f\u7684\u662f\u6761\u86c7\u3002 \u4ed6\u5939\u4f4f\u86c7\u5c3e\uff0c\u2014\u63b7\u3001\u4e00\u7529\uff0c\u7136\u540e\u5c31\u4e00\u53e3\u54ac\u5728\u86c7\u7684\u4e03\u5bf8\u4e0a\u3002 \u53c8\u8165\u53c8\u82e6\u86c7\u8840\uff0c\u4ece\u4ed6\u7684\u54bd\u5589\uff0c\u6d41\u5165\u4ed6\u7684\u80c3\u3002 \u4ed6\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u597d\u50cf\u771f\u7684\u5df2\u53d8\u6210\u91ce\u517d\u3002 \u4f46\u662f\u4ed6\u5e76\u6ca1\u6709\u505c\u6b62\uff0c\u86c7\u8840\u6d41\u4e0b\u65f6\uff0c\u4ed6\u7acb\u523b\u5c31\u611f\u89c9\u5230\u4e00\u79cd\u751f\u547d\u8dc3\u52a8\uff01 \u53ea\u8981\u80fd\u7ed9\u4ed6\u751f\u547d\uff0c\u53ea\u8981\u80fd\u8ba9\u4ed6\u6d3b\u4e0b\u53bb\uff0c\u65e0\u8bba\u4ec0\u4e48\u4e8b\u4ed6\u90fd\u63a5\u53d7\uff01 \u4ed6\u4e0d\u60f3\u6b7b\uff0c\u4e0d\u80fd\u6b7bI \u5982\u679c\u4ed6\u73b0\u5728\u5c31\u6b7b\u4e86\uff0c\u4ed6\u4e5f\u8981\u5316\u6210\u51a4\u9b42\u5389\u9b3c\uff0c\u91cd\u56de\u4eba\u95f4\uff0c\u6765\u6d17\u6e05\u4ed6\u7684\u5c48\u8fb1\u300f \u9ed1\u6697\u5df2\u6e10\u6e10\u6de1\u4e86\uff0c\u53d8\u6210\u4e86\u4e00\u79cd\u5947\u5f02\u7684\u6b7b\u7070\u8272\u3002 \u8fd9\u6f2b\u6f2b\u957f\u591c\u4ed6\u603b\u7b97\u5df2\u6328\u4e86\u8fc7\u53bb\uff0c\u73b0\u5728\u603b\u7b97\u5df2\u5230\u9ece\u660e\u65f6\u5019\u3002 \u53ef\u662f\u5c31\u7b97\u5929\u4eae\u4e86\u53c8\u5982\u4f55\uff1f \u7eb5\u7136\u9ed1\u6697\u5df2\u8fdc\u53bb\uff0c\u6b7b\u4ea1\u8fd8\u662f\u5728\u7d27\u903c\u7740\u4ed6\uff01 \u5730\u4e0a\u6709\u843d\u53f6\uff0c\u4ed6\u6293\u2014\u628a\uff0c\u64e6\u51c0\u4e86\u624b\u4e0a\u7684\u8165\u76bf\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u5ffd\u7136\u542c\u89c1\u4e86\u58f0\u97f3\u3002 \u4eba\u7684\u58f0\u97f3\u3002 \u58f0\u97f3\u4e5f\u4e0d\u77e5\u662f\u4ece\u4ec0\u4e48\u5730\u65b9\u4f20\u8fc7\u6765\u7684\uff0c\u4eff\u4f5b\u6709\u4eba\u5728\u547b\u541f\u5598\u76ee \u6b64\u65f6\u6b64\u5730\uff0c\u600e\u4e48\u4f1a\u6709\u4eba\uff1f \u82e5\u4e0d\u662f\u5df2\u88ab\u903c\u5f97\u65e0\u8def\u53ef\u8d70\uff0c\u53c8\u6709\u8c01\u4f1a\u8d70\u5165\u8fd9\u7247\u4e1b\u6797\uff1f\u8d70\u4e0a\u8fd9\u6761\u6b7b\u8def\uff01 \u96be\u9053\u662f\u897f\u95e8\u5439\u96ea\uff1f \u9646\u5c0f\u51e4\u7a81\u7136\u89c9\u5f97\u5168\u8eab\u90fd\u5df2\u51b0\u51b7\u50f5\u786c\uff0c\u505c\u6b62\u4e86\u547c\u5438\uff0c\u9759\u9759\u7684\u542c\u7740\u3002 \u5fae\u5f31\u7684\u547b\u541f\u5598\u606f\u58f0\uff0c\u65ad\u65ad\u7eed\u7eed\u4f20\u8fc7\u6765\uff0c\u58f0\u97f3\u4e2d\u5145\u6ee1\u4e86\u75db\u82e6\u3002 \u4e00\u79cd\u5145\u6ee1\u4e86\u6050\u60e7\u7684\u75db\u82e6\uff0c\u4e00\u79cd\u51e0\u4e4e\u5df2\u63a5\u8fd1\u7edd\u671b\u7684\u75db\u82e6\u3002 \u8fd9\u79cd\u75db\u82e6\u7edd\u4e0d\u80fd\u4f2a\u88c5\u7684\u3002 \u5c31\u7b97\u8fd9\u4e2a\u4eba\u771f\u7684\u662f\u897f\u95e8\u5439\u96ea\uff0c\u73b0\u5728\u4ed6\u6240\u5fcd\u53d7\u7684\u75db\u82e6\u4e5f\u7edd\u4e0d\u4f1a\u6bd4\u9646\u5c0f\u51e4\u5c11\u3002 \u96be\u9053\u4ed6\u4e5f\u906d\u53d7\u4e86\u4ec0\u4e48\u81f4\u547d\u7684\u6253\u51fb\uff1f\u5426\u5219\u600e\u4e48\u4f1a\u8fde\u90a3\u79cd\u6740\u4eba\u7684\u5251\u6c14\u90fd\u5df2\u6d88\u5931\u3002 \u9646\u5c0f\u51e4\u51b3\u5fc3\u53bb\u627e\uff0c\u4e0d\u7ba1\u8fd9\u4e2a\u4eba\u662f\u4e0d\u662f\u897f\u95e8\u5439\u96ea\uff0c\u4ed6\u90fd\u8981\u627e\u5230\u3002 \u4ed6\u5f53\u7136\u627e\u5f97\u5230\u3002 \u843d\u53f6\u662f\u6e7f\u7684\uff0c\u6ce5\u571f\u4e5f\u662f\u6e7f\u7684\u3002 \u4e00\u4e2a\u4eba\u5012\u5728\u843d\u53f6\u6e7f\u6ce5\u4e2d\uff0c\u5168\u8eab\u90fd\u5df2\u56e0\u75db\u82e6\u800c\u626d\u66f2\u3002 \u4e00\u4e2a\u4e24\u9b03\u6591\u767d\u7684\u4eba\uff0c\u8870\u8001\u3001\u6194\u60b4\u3001\u75b2\u5026\u3001\u60b2\u4f24\u800c\u6050\u60e7\u3002 \u4ed6\u770b\u89c1\u4e86\u9646\u5c0f\u51e4\uff0c\u4eff\u4f5b\u60f3\u6323\u624e\u7740\u8df3\u8d77\u6765\uff0c\u5374\u53ea\u4e0d\u8fc7\u6362\u6765\u4e86\u4e00\u9635\u75db\u82e6\u7684\u75c9\u631b\u3002 \u4ed6\u624b\u91cc\u6709\u5251\uff0c\u5f62\u5f0f\u53e4\u96c5\uff0c\u94a2\u8d28\u6781\u7eaf\uff0c\u65e0\u8bba\u8c01\u90fd\u770b\u5f97\u51fa\u8fd9\u662f\u67c4\u597d\u5251\u3002 \u53ef\u662f\u8fd9\u67c4\u5251\u5e76\u4e0d\u53ef\u6015\uff0c\u56e0\u4e3a\u8fd9\u4e2a\u4eba\u5e76\u4e0d\u662f\u897f\u95e8\u5439\u96ea\u3002 \u9646\u5c0f\u51e4\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u4e0d\u662f\u7684\uff0c\u4e0d\u662f\u4ed6\u3002\u201d \u8001\u5589\u7ed3\u4e0a\u4e0b\u6eda\u52a8\u7740\uff0c\u5145\u6ee1\u4e86\u6050\u60e7\u7684\u773c\u775b\u91cc\u9732\u51fa\u4e00\u4e1d\u5e0c\u671b\uff0c\u5598\u606f\u7740\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u662f \u8c01\uff1f\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u8c01\u90fd\u4e0d\u662f\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u8fc7\u8def\u4eba\u3002\u201d \u8001\u4eba\u9053\uff1a\u201c\u8fc7\u8def\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u5728\u5947\u602a\uff0c\u8fd9\u6761\u8def\u4e0a\u600e\u4e48\u8fd8\u4f1a\u6709\u8fc7\u8def\u7684\u4eba\u3002\u201d \u8001\u4eba\u4e0a\u4e0a\u4e0b\u4e0b\u7684\u6253\u91cf\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u5ffd\u7136\u53c8\u9732\u51fa\u79cd\u72d0\u72f8\u822c\u7684\u72e1\u9ee0\uff0c\u9053\uff1a\u201c\u96be\u9053\u4f60\u8d70\u7684 \u4e5f\u662f\u6211\u540c\u4e00\u6761\u8def\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5f88\u53ef\u80fd\u3002\u201d \u8001\u4eba\u7b11\u4e86\u3002 \u4ed6\u7684\u7b11\u51c4\u51c9\u800c\u82e6\u6da9\uff0c\u4e00\u7b11\u8d77\u6765\uff0c\u5c31\u5f00\u59cb\u4e0d\u505c\u7684\u54b3\u55fd\u3002 \u9646\u5c0f\u51e4\u53d1\u73b0\u4ed6\u4e5f\u53d7\u4e86\u4f24\uff0c\u4f24\u53e3\u4e5f\u5728\u80f8\u819b\u4e0a\uff0c\u4f24\u5f97\u66f4\u91cd\u3002 \u8001\u4eba\u5ffd\u53c8\u9053\uff1a\u201c\u4f60\u672c\u6765\u4ee5\u4e3a\u6211\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u662f\u53e6\u5916\u4e00\u4e2a\u4eba\u3002\u201d\u8001\u4eba\u9053\uff1a\u201c\u662f\u4e0d\u662f\u8981\u6765\u6740\u4f60\u7684\u4eba\uff1f\u5165 \u9646\u5c0f\u51e4\u4e5f\u7b11\u4e86\uff0c\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u672c\u6765\u4ee5\u4e3a\u6211\u662f\u4ec0\u4e48\u4eba\uff0c\u662f\u4e0d\u662f\u6765\u6740\u4f60\u7684\u4eba\uff1f\u201d \u8001\u4eba\u60f3\u5426\u8ba4\uff0c\u53c8\u4e0d\u80fd\u5426\u8ba4\u3002 \u4e24\u4e2a\u4eba\u4e92\u76f8\u51dd\u89c6\u7740\uff0c\u773c\u775b\u91cc\u7684\u8868\u60c5\uff0c\u5c31\u50cf\u662f\u4e24\u5934\u8d1f\u4e86\u4f24\u7684\u91ce\u517d\u3002 \u6ca1\u6709\u4eba\u80fd\u4e86\u89e3\u4ed6\u4eec\u8fd9\u79cd\u8868\u60c5\uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u4e86\u89e3\u4ed6\u4eec\u5fc3\u91cc\u7684\u611f\u89c9\u3002 \u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u8001\u4eba\u5ffd\u7136\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u8d70\u5427\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u8981\u6211\u8d70\uff1f\u201d\u8001\u4eba\u9053\uff1a\u201c\u5c31\u7b97\u6211\u4e0d\u8ba9\u4f60\u8d70\uff0c\u4f60\u53cd\u6b63\u4e5f\u4e00\u6837\u8981\u8d70\u7684\u3002\u201d \u4ed6\u8fd8\u5728\u7b11\uff0c\u7b11\u5f97\u66f4\u82e6\u6da9\u3002\u201d\u6211\u7684\u60c5\u51b5\u597d\u50cf\u6bd4\u4f60\u66f4\u7cdf\uff0c\u5f53\u7136\u5e2e\u4e0d\u4e86\u4f60\u7684\u5fd9\uff0c\u4f60\u6839\u672c\u4e0d \u8ba4\u5f97\u6211\uff0c\u5f53\u7136\u4e5f\u4e0d\u4f1a\u5e2e\u6211\u3002\u201d \u9646\u5c0f\u51e4\u6ca1\u6709\u5f00\u53e3\uff0c\u4e5f\u6ca1\u6709\u518d\u7b11\u2026 \u4ed6\u77e5\u9053\u8fd9\u8001\u4eba\u8bf4\u7684\u662f\u5b9e\u8bdd\uff0c\u4ed6\u7684\u60c5\u51b5\u4e5f\u5f88\u7cdf\uff0c\u751a\u81f3\u6bd4\u8fd9\u8001\u4eba\u60f3\u50cf\u4e2d\u66f4\u7cdf\u3002 \u4ed6\u81ea\u5df1\u4e00\u4e2a\u4eba\u9003\uff0c\u5df2\u672a\u5fc5\u80fd\u9003\u5f97\u4e86\uff0c\u5f53\u7136\u4e0d\u80fd\u518d\u52a0\u4e0a\u4e2a\u5305\u88b1\u3002 \u8fd9\u8001\u4eba\u65e0\u7591\u662f\u4e2a\u5f88\u91cd\u7684\u5305\u88b1\u3002 \u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u9646\u5c0f\u51e4\u4e5f\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u7684\u786e\u5e94\u8be5\u8d70\u7684\u3002 \u8001\u4eba\u70b9\u70b9\u5934\uff0c\u95ed\u4e0a\u773c\u775b\uff0c\u8fde\u770b\u90fd\u4e0d\u518d\u770b\u4ed6\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5047\u5982\u4f60\u53ea\u4e0d\u8fc7\u662f\u6761\u91ce\u72d7\uff0c\u73b0\u5728\u6211\u4e00\u5b9a\u65e9\u5c31\u8d70\u4e86\uff0c\u53ea\u53ef\u60dc\u2026\u2026\u201d \u8001\u4eba\u5ffd\u7136\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u4e0d\u662f\u72d7\uff0c\u662f\u4eba\u3002\u201d\u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u53ea\u53ef \u60dc\u6211\u4e5f\u4e0d\u662f\u72d7\uff0c\u6211\u4e5f\u662f\u4eba\u3002\u201d\u8001\u4eba\u9053\uff1a\u201c\u5b9e\u5728\u53ef\u60dcaU \u4ed6\u867d\u7136\u597d\u50cf\u95ed\u7740\u773c\u775b\uff0c\u5176\u5b9e\u5374\u5728\u5077\u5077\u7684\u8198\u7740\u9646\u5c0f\u51e4\u3002 \u4ed6\u773c\u775b\u91cc\u53c8\u9732\u51fa\u90a3\u79cd\u72d0\u72f8\u822c\u7684\u72e1\u9ee0\u3002\u9646\u5c0f\u51e4\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u65e9\u5df2\u77e5\u9053\u6211\u7edd\u4e0d \u4f1a\u8d70\u7684\u3002\u201d \u8001\u4eba\u9053\uff1a\u201c\u54e6\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u662f\u4eba\uff0c\u6211\u4e5f\u662f\u4eba\uff0c\u6211\u5f53\u7136\u4e0d\u80fd\u770b\u7740\u4f60\u70c2\u6b7b\u5728\u8fd9\u91cc\u3002\u201d \u8001\u4eba\u7684\u773c\u775b\u5ffd\u7136\u7741\u5f00\uff0c\u7741\u5f97\u5f88\u5927\uff0c\u770b\u7740\u9646\u5c0f\u51e4\uff0c\u9053\uff1a\u201c\u4f60\u80af\u5e26\u6211\u8d70\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u731c\u5462\uff1f\u201d \u8001\u4eba\u5728\u7728\u773c\uff0c\u9053\uff1a\u201c\u4f60\u5f53\u7136\u4f1a\u5e26\u6211\u8d70\uff0c\u56e0\u4e3a\u4f60\u662f\u4eba\uff0c\u6211\u4e5f\u662f\u3002\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8fd9\u7406 \u7531\u8fd8\u4e0d\u591f\u3002\u201d \u8001\u4eba\u9053\uff1a\u201c\u8fd8\u4e0d\u591f\uff1f\u8fd8\u6709\u4ec0\u4e48\u7406\u7531\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6df7\u86cb\u4e5f\u662f\u4eba\u3002\u201d \u4ed6\u5ffd\u7136\u8bf4\u51fa\u8fd9\u53e5\u8bdd\uff0c\u8c01\u90fd\u542c\u4e0d\u61c2\uff0c\u8001\u4eba\u4e5f\u4e0d\uff0c\u53ea\u6709\u7b49\u7740\u4ed6\u8bf4\u4e0b\u53bb\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u5e26\u4eba\u8d70\uff0c\u53ea\u56e0\u4e3a\u6211\u4e0d\u4f46\u662f\u4eba\uff0c\u8fd8\u662f\u4e2a\u6df7\u86cb\uff0c\u7279\u5927\u53f7\u7684\u6df7\u86cb\u3002\u201d \u662f\u6625\u5929\u3002 \u662f\u5929\u5730\u95f4\u4e07\u7269\u90fd\u5728\u8301\u53d1\u751f\u957f\u7684\u6625\u5929\u3002 \u51cb\u8c22\u4e86\u7684\u6728\u53f6\uff0c\u53c8\u957f\u5f97\u5bc6\u5bc6\u7684\uff0c\u4e1b\u6797\u4e2d\u7684\u6728\u53f6\u83bd\u83bd\u5bc6\u5bc6\uff0c\u8fde\u9633\u5149\u90fd\u7167\u4e0d\u8fdb\u6765\u3002 \u6811\u5e72\u53f6\u95f4\uff0c\u8fd8\u662f\u4e00\u7247\u8ff7\u8ff7\u8499\u8499\u7684\u7070\u767d\u8272\uff0c\u8ba9\u4f60\u53ea\u80fd\u770b\u5f97\u89c1\u4e00\u70b9\u8ff7\u8ff7\u8499\u8499\u7684\u5f71\u5b50\u3002 \u770b\u5f97\u89c1\uff0c\u5374\u770b\u4e0d\u8fdc\u3002 \u9646\u5c0f\u51e4\u5148\u8ba9\u8001\u4eba\u8eba\u4e0b\u53bb\uff0c\u81ea\u5df1\u4e5f\u8eba\u4e86\u4e0b\u53bb\uff0c\u73b0\u5728\u4ed6\u4e0d\u5c31\u7b97\u660e\u77e5\u897f\u95e8\u5439\u96ea\u5df2\u8fd1\u5728\u54ab \u5c3a\uff0c\u4e5f\u8d70\u4e0d\u52a8\u534a\u6b65\u4e86\u3002 \u4ed6\u4eec\u5df2\u8d70\u4e86\u5f88\u8fdc\u7684\u4e00\u6bb5\u8def\uff0c\u53ef\u662f\u4ed6\u4f4e\u4e0b\u5934\u65f6\uff0c\u5c31\u7acb\u523b\u53c8\u770b\u89c1\u4e8e\u5408\u5df1\u7684\u8db3\u8ff9\u3002 \u4ed6\u62fc\u4e86\u547d\uff0c\u7528\u5c3d\u4e86\u6240\u6709\u7684\u529b\u91cf\u5954\u8dd1\uff0c\u5374\u53c8\u56de\u5230\u4ed6\u65e9\u5df2\u8d70\u8fc7\u7684\u5730\u65b9\u3002 \u8fd9\u5df2\u4e0d\u662f\u8bbd\u523a\uff0c\u5df2\u7ecf\u662f\u60b2\u54c0\uff0c\u4e00\u79cd\u4eba\u4eec\u53ea\u6709\u5728\u63a5\u8fd1\u7edd\u671b\u65f6\u624d\u4f1a\u611f\u89c9\u5230\u7684\u60b2\u54c0\u3002 \u4ed6\u5728\u5598\u606f\uff0c\u8001\u4eba\u4e5f\u5728\u5598\u606f\u3002 \u4e00\u6761\u87d2\u86c7\u4ece\u679d\u65f6\u95f4\u6ed1\u4e0b\u6765\uff0c\u5de8\u5927\u7684\u87d2\u86c7\uff0c\u529b\u91cf\u5f53\u7136\u4e5f\u540c\u6837\u5de8\u5927\uff0c\u8db3\u4ee5\u7ede\u6740\u4e00\u5207\u751f \u547d\u3002 \u53ef\u662f\u4ed6\u4e0d\u60f3\u53bb\uff0c\u8001\u4eba\u4e0d\u80fd\u52a8\uff0c\u87d2\u86c7\u5c45\u7136\u4e5f\u6ca1\u6709\u52a8\u4ed6\u4eec\uff0c\u5c45\u7136\u5c31\u6084\u6084\u7684\u4ece\u4ed6\u4eec\u8eab\u65c1\u6ed1 \u4e86\u8fc7\u53bb\u3002 \u9646\u5c0f\u51e4\u7b11\u4e86\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u81ea\u5df2\u600e\u4e48\u8fd8\u80fd\u7b11\u5f97\u51fa\u6765\u7684\u3002 \u8001\u4eba\u4fa7\u8fc7\u5934\uff0c\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e0d\u80fd\u5c31\u53eb\u4f60\u6df7\u86cb\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u53eb\u6211\u5927\u6df7\u86cb\u3002\u201d \u4ed6\u8fd8\u5728\u7b11\u3002 \u7b11\u6709\u5f88\u591a\u79cd\uff0c\u6709\u79cd\u7b11\u6bd4\u54ed\u66f4\u60b2\u54c0\uff0c\u4ed6\u7684\u7b11\u5c31\u662f\u8fd9\u79cd\u3002 \u53ea\u6709\u7b11\uff0c\u6ca1\u6709\u7b11\u58f0\uff0c\u56db\u4e0b\u8fde\u4e00\u70b9\u58f0\u97f3\u90fd\u6ca1\u6709\u65f6\u5149\u5728\u9759\u9759\u4e2d\u8fc7\u5f97\u597d\u50cf\u7279\u522b\u6162\u3002 \u8fc7\u6709\u5f88\u4e45\uff0c\u8001\u4eba\u5ffd\u53c8\u9053\uff1a\u201c\u5927\u6df7\u86cb\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u55ef\u3002\u201d \u8001\u4eba\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u95ee\u6211\u662f\u8c01\uff1f\u53eb\u4ec0\u4e48\u540d\u5b57\u3002\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u4e0d\u5fc5\u95ee\u3002\u201d \u8001\u4eba\u9053\uff1a\u201c\u4e0d\u5fc5\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53cd\u6b63\u6211\u4eec\u73b0\u5728\u90fd\u5df2\u5feb\u6b7b\u4e86\uff0c\u4f60\u51e0\u65f6\u542c\u89c1\u8fc7\u6b7b\u4eba\u95ee\u6b7b\u4eba\u7684\u540d\u5b57\uff1f\u201d \u8001\u4eba\u770b\u7740\u4ed6\uff1b\u53c8\u8fc7\u6709\u5f88\u4e45\uff0c\u60f3\u8bf4\u8bdd\uff0c\u6ca1\u6709\u8bf4\uff0c\u518d\u770b\u770b\u4ed6\u7684\u7709\u6bdb\u548c\u80e1\u5b50\uff0c\u7ec8\u4e8e\u9053\uff1a \u201c\u6211\u5ffd\u7136\u60f3\u8d77\u4e86\u4e00\u4e2a\u4eba\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u8001\u4eba\u9053\uff1a\u201c\u9646\u5c0f\u51e4\uff0c\u6709\u56db\u6761\u7709\u3002\u201d \u9646\u5c0f\u51e4\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u65e9\u5c31\u8be5\u60f3\u5230\u7684\uff0c\u5929\u4e0b\u552f\u4e00\u7279\u5927\u53f7\u7684\u5927\u6df7\u86cb\uff0c\u5c31\u662f\u9646\u5c0f \u51e4\u3002\u201d \u8001\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f46\u6211\u5374\u60f3\u4e0d\u5230\u9646\u5c0f\u51e4\u4f1a\u53d8\u6210\u8fd9\u79cd\u6837 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u9646\u5c0f\u51e4\u5e94\u8be5\u662f\u4ec0\u4e48\u6837\u5b50\u7684\uff1f\u201d \u8001\u4eba\u9053\uff1a\u201c\u6211\u5f88\u4e45\u4ee5\u524d\u5c31\u542c\u8bf4\u8fc7\uff0c\u9646\u5c0f\u51e4\u662f\u4e2a\u5f88\u8ba8\u5973\u4eba\u559c\u6b22\u7684\u82b1\u82b1\u516c\u5b50\uff0c\u800c\u4e14\u6b66\u529f \u6781\u9ad8\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4e00\u76f4\u8ba4\u4e3a\uff0c\u9646\u5c0f\u51e4\u4e00\u5b9a\u662f\u4e2a\u5f88\u82f1\u4fca\uff0c\u5f88\u795e\u6c14\u7684\u4eba\uff0c\u53ef\u662f\u4f60\u73b0\u5728 \u770b\u6765\uff0c\u5374\u50cf\u662f\u6761\u2026\u2026\u201d \u4ed6\u6ca1\u6709\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u9646\u5c0f\u51e4\u5374\u66ff\u4ed6\u8bf4\u4e86\u4e0b\u53bb\u3002\u201d\u5374\u50cf\u662f\u6761\u88ab\u4eba\u8ffd\u5f97\u65e0\u8def\u53ef\u8d70\u7684\u91ce\u72d7 ao \u8001\u4eba\u4e5f\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u770b\u6765\u4f60\u60f9\u7684\u9ebb\u70e6\u4e00\u5b9a\u4e0d\u5c0f\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5f88\u4e0d\u5c0f\u3002\u201d \u8001\u4eba\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4e3a\u5973\u4eba\u60f9\u7684\u9ebb\u70e6\uff1f\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u3002 \u8001\u4eba\u9053\uff1a\u201c\u90a3\u5973\u4eba\u7684\u4e08\u592b\u662f\u8c01\uff1f\u542c\u8bf4\u4f60\u8fde\u767d\u4e91\u57ce\u4e3b\u7684\u90a3\u4e00\u5251\u2018\u5929\u5916\u98de\u4ed9\u2019\u90fd\u80fd\u63a5\u5f97 \u4f4f\uff0c\u5929\u4e0b\u8fd8\u6709\u8c01\u80fd\u628a\u4f60\u903c\u5f97\u65e0\u8def\u53ef\u8d70\uff1f\u201d \u9646\u5c0f\u51e4\u3002\u201d\u53ea\u6709\u4e00\u4e2a\u4eba\u3002\u201d \u8001\u4eba\u9053\uff1a\u201c\u6211\u60f3\u6765\u60f3\u53bb\uff0c\u597d\u50cf\u4e5f\u53ea\u6709\u4e00\u4e2a\u4eba\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u60f3\u7684\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u8001\u4eba\u9053\uff1a\u201c\u662f\u4e0d\u662f\u897f\u95e8\u5439\u96ea\uff1f\u201d \u9646\u5c0f\u51e4\u53c8\u5728\u82e6\u7b11\uff0c\u53ea\u6709\u82e6\u7b11\u3002 \u8001\u4eba\u53f9\u9053\uff1a\u201c\u4f60\u60f9\u7684\u8fd9\u9ebb\u70e6\u5b9e\u5728\u4e0d\u5c0f\uff0c\u6211\u5b9e\u5728\u60f3\u4e0d\u901a\u4f60\u600e\u4e48\u4f1a\u60f9\u4e0b\u8fd9\u79cd\u9ebb\u70e6\u7684\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u6ca1\u6709\u505a\u4ec0\u4e48\uff0c\u53ea\u4e0d\u8fc7\u5076\u5c14\u8ddf\u4ed6\u8001\u5a46\u7761\u5728\u4e00\u5f20\u5e8a\u4e0a\uff0c\u53c8\u6070\u5de7\u88ab \u4ed6\u770b\u89c1\u4e86\u3002\u201d \u8001\u4eba\u5403\u60ca\u770b\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u6447\u5934\u8bf4\u3002\u201d\u539f\u6765\u4f60\u7684\u80c6\u5b50\u4e5f\u4e0d\u5c0f\u3002\u201d \u9646\u5c0f\u51e4\u5ffd\u7136\u53cd\u95ee\u3002\u201d\u4f60\u5462\uff0c\u4f60\u60f9\u4e86\u4ec0\u4e48\u9ebb\u70e6\uff1f\u201d \u8001\u4eba\u6c89\u9ed8\u7740\uff0c\u4e5f\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u53f9\u606f\u7740\u9053\uff1a\u201c\u6211\u60f9\u7684\u9ebb\u70e6\u4e5f\u4e0d\u5c0f\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u770b\u5f97\u51fa\u3002\u201d \u8001\u4eba\u9053\uff1a\u201c\u54e6\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5982\u679c\u4e00\u4e2a\u4eba\u8eab\u4e0a\u7a7f\u7740\u7684\u662f\u503c\u4e09\u767e\u4e24\u94f6\u5b50\u4e00\u5957\u7684\u8863\u670d\uff0c\u624b\u62ff\u7740\u7684\u662f\u503c\u4e09 \u5e72\u4e24\u94f6\u5b50\u4e00\u67c4\u597d\u5251\uff0c\u5374\u4e0d\u50cf\u662f\u6761\u91ce\u72d7\u822c\u88ab\u4eba\u8ffd\u5f97\u843d\u8352\u800c\u9003\uff0c\u8fd9\u4e2a\u4eba\u60f9\u7684\u9ebb\u70e6\u5f53\u7136\u4e5f\u5f88\u4e0d \u5c0f\u3002\u201d\u8001\u4eba\u4e5f\u4e0d\u7981\u82e6\u7b11\uff0c\u9053\uff1a\u201c\u6211\u60f9\u7684\u9ebb\u70e6\u8fd8\u4e0d\u6b62\u4e00\u4e2a\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6709\u51e0\u4e2a\uff1f\u201d \u8001\u4eba\u4f38\u51fa\u4e24\u6839\u624b\u6307\uff0c\u9053\uff1a\u201c\u4e00\u4e2a\u662f\u53f6\u5b64\u9e3f\uff0c\u4e00\u4e2a\u662f\u7c89\u71d5\u5b50 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6b66\u5f53\u5c0f\u767d\u9f99\u53f6\u5b64\u9e3f\uff1f\u201d \u8001\u4eba\u70b9\u5934\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e07\u91cc\u8e0f\u82b1\u7c89\u71d5\u5b50\uff1f\u201d \u8001\u4eba\u53c8\u70b9\u5934\u3002 \u9646\u5c0f\u51e4\u53f9\u9053\uff1a\u201c\u4f60\u60f9\u7684\u8fd9\u4e24\u4e2a\u5012\u5b9e\u5728\u771f\u4e0d\u5c0f\u3002\u201d \u53f6\u5b64\u9e3f\u662f\u6b66\u5f53\u6d3e\u7684\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u4e5f\u662f\u6b66\u5f53\u95e8\u4e0b\u5b50\u5f1f\u4e2d\u7684\u540e\u8d77\u4e4b\u79c0\uff0c\u636e\u8bf4\u8fd8\u662f\u767d\u4e91\u57ce\u4e3b \u7684\u8fdc\u623f\u5802\u5f1f\uff0c\u767d\u4e91\u57ce\u4e3b\u8fd8\u4eb2\u81ea\u6307\u70b9\u4ed6\u7684\u5251\u62db\u3002 \u201c\u4e07\u91cc\u8e0f\u82b1\u201d\u7c89\u71d5\u5b50\u5728\u6c5f\u6e56\u4e2d\u7684\u540d\u5934\u66f4\u54cd\uff0c\u8f7b\u529f\u6697\u5668\u9ed1\u9053\u4e2d\u5df2\u5f88\u5c11\u6709\u4eba\u80fd\u6bd4\u5f97\u4e0a\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u53f6\u5b64\u9e3f\u662f\u540d\u95e8\u5b50\u5f1f\uff0c\u7c89\u71d5\u5b50\u5374\u662f\u4e0b\u4e94\u95e8\u7684\u5927\u76d7\uff0c\u4f60\u600e\u4e48\u4f1a\u540c\u65f6 \u60f9\u5f97\u4e0a\u8fd9\u4e24\u4e2a\u4eba\u7684\u3002\u201d \u8001\u4eba\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u901a\uff1f\u201d \u9646\u5c0f\u51e4\u6447\u5934\u3002 \u8001\u4eba\u9053\uff1a\u201c\u5176\u5b9e\u8fd9\u9053\u7406\u4e5f\u7b80\u5355\u5f97\u5f88\uff0c\u53f6\u5b64\u9e3f\u662f\u6211\u5916\u7525\uff0c\u7c89\u71d5\u5b50\u6070\u5de7\u4e5f\u662f\u7684\uff0c\u4ed6\u4eec\u4e24 \u4e2a\u4eba\u7684\u8001\u5a46\u53c8\u6070\u5de7\u90fd\u5728\u6211\u5bb6\u4f5c\u5ba2 \u53f6\u5b64\u9e3f\u6e38\u4fa0\u6c5f\u6e56\uff0c\u7c89\u71d5\u5b50\u4e07\u91cc\u8e0f\u82b1\uff0c\u4ed6\u4eec\u7684\u59bb\u5b50\u5f53\u7136\u90fd\u5f88\u5bc2\u5bde\u3002 \u8001\u4eba\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4e5f\u4e0d\u80fd\u4e0d\u5b89\u6170\u5979\u4eec\uff0c\u8c01\u77e5\u9053\u4e5f\u6070\u5de7\u88ab\u4ed6\u4eec\u770b\u89c1\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u5403\u60ca\u7684\u770b\u7740\u4ed6\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u82e6\u7b11\u9053\uff1a\u201c\u770b\u6765\u4f60\u975e\u4f46\u80c6\u5b50\u4e0d\u5c0f\uff0c\u800c\u4e14\u7b80\u76f4\u516d \u4eb2\u4e0d\u8ba4\u3002\u201d \u8001\u4eba\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u96be\u9053\u4f60\u4ee5\u4e3a\u6211\u4e0d\u662f\uff1f\u201d \u9646\u5c0f\u51e4\u663e\u5f97\u66f4\u5403\u60ca\uff0c\u9053\uff1a\u201c\u96be\u9053\u4f60\u672c\u6765\u5c31\u662f\uff1f\u201d \u8001\u4eba\u9053\uff1a\u201c\u8fd1\u5341\u5e74\u6765\uff0c\u6c5f\u6e56\u4e2d\u5df2\u5f88\u5c11\u6709\u4eba\u77e5\u9053\u6211\u7684\u540d\u5b57\u4e86\uff0c\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u77e5\u9053\u3002\u201d \u4e8c\u5341\u5e74\u524d\uff0c\u6c5f\u6e56\u4e2d\u6709\u4e09\u4e2a\u540d\u5934\u6700\u54cd\u7684\u72ec\u884c\u5927\u76d7\uff0c\u7b2c\u4e00\u4e2a\u5c31\u53eb\u3002\u201d\u516d\u4eb2\u4e0d\u8ba4\u201d\u72ec\u5b64 \u7f8e\u3002 \u5982\u679c\u4e00\u4e2a\u4eba\u7684\u540d\u5b57\u5c31\u53eb\u505a\u201c\u516d\u4eb2\u4e0d\u8ba4\uff3c\u8fd9\u4e2a\u4eba\u6709\u591a\u4e48\u5fc3\u9ed1\u624b\u8fa3\uff0c\u4f60\u60f3\u60f3\u770b\u5c31\u53ef\u4ee5\u77e5 \u9053\u4e86\u3002 \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u770b\u6765\u4f60\u8fd9\u540d\u5b57\u5230\u771f\u8fde\u4e00\u70b9\u6ca1\u6709\u9519\u3002\u201d \u5b64\u72ec\u7f8e\u6de1\u6de1\u9053\uff1a\u201c\u6211\u516d\u4eb2\u4e0d\u8ba4\uff0c\u4f60\u91cd\u8272\u8f7b\u53cb\uff0c\u4f60\u662f\u4e2a\u5927\u6df7\u86cb\uff0c\u6211\u4e5f\u5dee\u4e0d\u591a\uff0c\u6211\u4eec\u4e24 \u4e2a\u672c\u5c31\u662f\u5fd7\u540c\u9053\u5408\uff0c\u6240\u4ee5\u624d\u4f1a\u8d70\u4e0a\u540c\u4e00\u6761\u8def\u3002\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5e78\u597d\u6211\u4eec\u8fd8\u6709\u4e00\u70b9\u4e0d \u540c\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u73b0\u5728\u6211\u8fd8\u53ef\u4ee5\u8d70\uff0c\u4f60\u5374\u53ea\u6709\u8eba\u5728\u8fd9\u91cc\u7b49\u6b7b\u3002\u201d \u5b64\u72ec\u7f8e\u7b11\u4e86\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u82e5\u8ba4\u4e3a\u73b0\u5728\u6211\u8fd8\u786c\u4e0d\u8d77\u8fd9\u5fc3\u80a0\u6765\uff0c\u4f60\u5c31\u9519\u4e86\u4f60\u65e2\u7136\u53ef\u4ee5\u516d\u4eb2\u4e0d\u8ba4\uff0c \u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u3002\u201d\u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4f60\u5f53\u7136\u80fd\u3002\u201d \u9646\u5c0f\u51e4\u5df2\u7ad9\u8d77\u6765\uff0c\u8bf4\u8d70\u5c31\u8d70\u3002 \u5b64\u72ec\u7f8e\u770b\u7740\u4ed6\u7ad9\u8d77\u6765\uff0c\u624d\u6162\u6162\u7684\u63a5\u7740\u9053\uff1a\u201c\u53ef\u662f\u6211\u4fdd\u8bc1\u4f60\u8d70\u4e86\u4e4b\u540e\uff0c\u4e00\u5b9a\u4f1a\u540e\u6094\u7684 qU \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u56de\u5934\uff0c\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u4e0d\u4f46\u6709\u5403\u4eba\u7684\u91ce\u517d\uff0c\u8fd8\u6709\u5403\u4eba\u7684\u4eba\u3002\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4f60 \u5c31\u662f\u5403\u4eba\u7684\u4eba\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4e16\u4e0a\u6709\u79cd\u4e1c\u897f\u4e5f\u4f1a\u5403\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u8bf4\u662f\u4ec0\u4e48\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u6811\u6797\u5b50\uff0c\u6709\u7684\u6811\u6797\u5b50\u4e5f\u4f1a\u5403\u4eba\u7684\uff0c\u4e0d\u8ba4\u5f97\u8def\u7684\u4eba\uff0c\u53ea\u8981\u4e00\u8d70\u8fdb\u8fd9\u79cd\u6811 \u6797\uff0c\u7acb\u523b\u5c31\u4f1a\u88ab\u5403\u6389\uff0c\u6c38\u8fdc\u90fd\u4f11\u60f3\u6d3b\u7740\u8d70\u51fa\u53bb\u3002 \u73b0\u5728\u867d\u7136\u5df2\u5c06\u8fd1\u6b63\u5348\uff0c\u56db\u9762\u8fd8\u662f\u4e00\u7247\u8ff7\u8ff7\u8499\u8499\u7684\u6b7b\u7070\u8272 \u5de8\u5927\u4e11\u6076\u7684\u6811\u6728\u6821\u53f6\uff0c\u8150\u81ed\u53d1\u70c2\u7684\u843d\u53f6\u6cbc\u6cfd\u5730\uff0c\u6839\u672c\u5c31\u65e0\u8def\u53ef\u8d70\u3002 \u4e16\u4e0a\u82e5\u771f\u6709\u5403\u4eba\u7684\u6811\u6797\uff0c\u8fd9\u91cc\u4e00\u5b9a\u5c31\u662f\u7684\u3002 \u9646\u5c0f\u51e4\u7ec8\u4e8e\u8f6c\u56de\u8eab\uff0c\u76ef\u7740\u8001\u4eba\u7684\u8138\uff0c\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u8def\uff1f\u4f60\u6709\u628a\u63e1\u80fd\u8d70\u51fa\u53bb\uff1f\u201d \u5b64\u72ec\u7f8e\u53c8\u7b11\u4e86\u7b11\uff0c\u60a0\u7136\u9053\uff1a\u201c\u6211\u4e0d\u4f46\u80fd\u5e26\u4f60\u8d70\u51fa\u53bb\uff0c\u8fd8\u80fd\u53eb\u897f\u95e8\u5439\u96ea\u4e00\u8f88\u5b50\u90fd\u627e\u4e0d \u5230\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u51b7\u7b11\u3002 \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u5e26\u4f60\u5230\u4e00\u4e2a\u5730\u65b9\u53bb\uff0c\u5c31\u7b97\u897f\u95e8\u5439\u96ea\u6709\u5929\u5927\u7684\u672c\u4e8b\uff0c\u4e5f\u627e\u4e0d\u5230\u7684 ao \u9646\u5c0f\u51e4\u76ef\u7740\u4ed6\uff0c\u6ca1\u6709\u52a8\uff0c\u6ca1\u6709\u5f00\u53e3\uff0c\u8fdc\u5904\u5374\u6709\u4eba\u5728\u51b7\u7b11\u3002 \u5b64\u72ec\u7f8e\u7b11\u4e86\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u82e5\u8ba4\u4e3a\u73b0\u5728\u6211\u8fd8\u786c\u4e0d\u8d77\u8fd9\u5fc3\u80a0\u6765\uff0c\u4f60\u5c31\u9519\u4e86\u4f60\u65e2\u7136\u53ef\u4ee5\u516d\u4eb2\u4e0d\u8ba4\uff0c \u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u3002\u201d\u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4f60\u5f53\u7136\u80fd\u3002\u201d \u9646\u5c0f\u51e4\u5df2\u7ad9\u8d77\u6765\uff0c\u8bf4\u8d70\u5c31\u8d70\u3002 \u5b64\u72ec\u7f8e\u770b\u7740\u4ed6\u7ad9\u8d77\u6765\uff0c\u624d\u6162\u6162\u7684\u63a5\u7740\u9053\uff1a\u201c\u53ef\u662f\u6211\u4fdd\u8bc1\u4f60\u8d70\u4e86\u4e4b\u540e\uff0c\u4e00\u5b9a\u4f1a\u540e\u6094\u7684 qU \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u56de\u5934\uff0c\u95ee\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u4e0d\u4f46\u6709\u5403\u4eba\u7684\u91ce\u517d\uff0c\u8fd8\u6709\u5403\u4eba\u7684\u4eba\u3002\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u4f60 \u5c31\u662f\u5403\u4eba\u7684\u4eba\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4e16\u4e0a\u6709\u79cd\u4e1c\u897f\u4e5f\u4f1a\u5403\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u8bf4\u662f\u4ec0\u4e48\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u6811\u6797\u5b50\uff0c\u6709\u7684\u6811\u6797\u5b50\u4e5f\u4f1a\u5403\u4eba\u7684\uff0c\u4e0d\u8ba4\u5f97\u8def\u7684\u4eba\uff0c\u53ea\u8981\u4e00\u8d70\u8fdb\u8fd9\u79cd\u6811 \u6797\uff0c\u7acb\u523b\u5c31\u4f1a\u88ab\u5403\u6389\uff0c\u6c38\u8fdc\u90fd\u4f11\u60f3\u6d3b\u7740\u8d70\u51fa\u53bb\u3002 \u73b0\u5728\u867d\u7136\u5df2\u5c06\u8fd1\u6b63\u5348\uff0c\u56db\u9762\u8fd8\u662f\u4e00\u7247\u8ff7\u8ff7\u8499\u8499\u7684\u6b7b\u7070\u8272 \u5de8\u5927\u4e11\u6076\u7684\u6811\u6728\u6821\u53f6\uff0c\u8150\u81ed\u53d1\u70c2\u7684\u843d\u53f6\u6cbc\u6cfd\u5730\uff0c\u6839\u672c\u5c31\u65e0\u8def\u53ef\u8d70\u3002 \u4e16\u4e0a\u82e5\u771f\u6709\u5403\u4eba\u7684\u6811\u6797\uff0c\u8fd9\u91cc\u4e00\u5b9a\u5c31\u662f\u7684\u3002 \u9646\u5c0f\u51e4\u7ec8\u4e8e\u8f6c\u56de\u8eab\uff0c\u76ef\u7740\u8001\u4eba\u7684\u8138\uff0c\u9053\uff1a\u201c\u4f60\u8ba4\u5f97\u8def\uff1f\u4f60\u6709\u628a\u63e1\u80fd\u8d70\u51fa\u53bb\uff1f\u201d \u5b64\u72ec\u7f8e\u53c8\u7b11\u4e86\u7b11\uff0c\u60a0\u7136\u9053\uff1a\u201c\u6211\u4e0d\u4f46\u80fd\u5e26\u4f60\u8d70\u51fa\u53bb\uff0c\u8fd8\u80fd\u53eb\u897f\u95e8\u5439\u96ea\u4e00\u8f88\u5b50\u90fd\u627e\u4e0d \u5230\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u51b7\u7b11\u3002 \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u5e26\u4f60\u5230\u4e00\u4e2a\u5730\u65b9\u53bb\uff0c\u5c31\u7b97\u897f\u95e8\u5439\u96ea\u6709\u5929\u5927\u7684\u672c\u4e8b\uff0c\u4e5f\u627e\u4e0d\u5230\u7684 ao \u9646\u5c0f\u51e4\u76ef\u7740\u4ed6\uff0c\u6ca1\u6709\u52a8\uff0c\u6ca1\u6709\u5f00\u53e3\uff0c\u8fdc\u5904\u5374\u6709\u4eba\u5728\u51b7\u7b11\u3002 \u51b7\u51b0\u51b0\u7684\u7b11\u58f0\uff0c\u672c\u6765\u8fd8\u8fdc\u5728\u5341\u4e08\u5916\uff0c\u5ffd\u7136\u5c31\u5230\u4e86\u9762\u524d\u3002 \u6765\u7684\u4eba\u5374\u4e0d\u662f\u90a3\u4ee5\u8f7b\u529f\u6210\u540d\u7684\u7c89\u71d5\u5b50\uff0c\u662f\u4e2a\u82cd\u767d\u7684\u4eba \u82cd\u767d\u7684\u8138\uff0c\u82cd\u767d\u7684\u624b\uff0c\u82cd\u767d\u7684\u5251\uff0c\u4e00\u8eab\u767d\u8863\u5982\u96ea\u3002 \u5728\u8fd9\u9ed1\u6697\u7684\u6cbc\u6cfd\u68ee\u6797\u4e2d\u641c\u7d22\u8ffd\u6355\u4e86\u4e8c\u5341\u4e2a\u65f6\u8fb0\u540e\uff0c\u4ed6\u7684\u795e\u60c5\u8fd8\u662f\u50cf\u51b0\u96ea\u822c\u51b7\u6f20\u9547 \u5b9a\uff0c\u8863\u670d\u4e0a\u4e5f\u53ea\u4e0d\u8fc7\u6cbe\u67d3\u4e86\u51e0\u70b9\u6ce5\u57a2\u3002\uff0c \u4ed6\u7684\u4eba\u5c31\u50cf\u662f\u4ed6\u7684\u5251\uff0c\u9c9c\u8840\u4e0d\u67d3\uff0c\u6ce5\u6c61\u4e5f\u4e0d\u67d3\uff01 \u5c31\u5728\u4ed6\u51fa\u73b0\u7684\u8fd9\u4e00\u77ac\u95f4\uff0c\u9646\u5c0f\u51e4\u5168\u8eab\u5ffd\u7136\u50f5\u786c\uff0c\u53c8\u5ffd\u7136\u653e\u677e\u3002 \u5b64\u72ec\u7f8e\u5374\u7b11\u4e86\uff0c\u7b11\u5bb9\u4e2d\u5145\u6ee1\u8ba5\u6d88\uff0c\u9053\uff1a\u201c\u4f60\u4ee5\u4e3a\u4ed6\u662f\u897f\u95e8\u5439\u96ea\uff1f\u201d \u9646\u5c0f\u51e4\u4e0d\u80fd\u5426\u8ba4\u3002 \u8fd9\u5c11\u5e74\u7684\u786e\u50cf\u6781\u4e86\u897f\u95e8\u5439\u96ea\u82cd\u767d\u7684\u8138\uff0c\u51b7\u9177\u9a84\u50b2\u7684\u8868\u60c5\uff0c\u96ea\u767d\u7684\u8863\u670d\uff0c\u751a\u81f3\u8fde\u7ad9\u7740 \u7684\u59ff\u6001\u90fd\u548c\u897f\u95e8\u5439\u96ea\u5b8c\u5168\u4e00\u6837\u3002 \u867d\u7136\u4ed6\u8fdc\u6bd4\u897f\u95e8\u5439\u96ea\u5e74\u8f7b\u5f97\u591a\uff0c\u9762\u76ee\u8f6e\u5ed3\u4e5f\u8fdc\u6bd4\u897f\u95e8\u5439\u96ea\u67d4\u5f31\uff0c\u53ef\u662f\u4ed6\u6574\u4e2a\u4eba\u770b\u8d77 \u6765\uff0c\u5374\u50cf\u662f\u897f\u95e8\u5439\u96ea\u7684\u5f71\u5b50aH \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4ed6\u59d3\u53f6\uff0c\u53eb\u53f6\u5b64\u9e3f\uff0c\u8fde\u4ed6\u7684\u7956\u5b97\u516b\u4ee3\u90fd\u8ddf\u897f\u95e8\u5439\u96ea\u62c9\u4e0d\u4e0a\u4e00\u70b9\u5173\u7cfb\uff0c \u53ef\u662f\u4ed6\u770b\u8d77\u6765\u5374\u504f\u504f\u50cf\u662f\u897f\u95e8\u5439\u96ea\u7684\u513f\u5b50\u3002\u201d \u9646\u5c0f\u51e4\u4e5f\u4e0d\u7981\u7b11\u4e86\u3002\u201d\u7684\u786e\u6709\u70b9\u50cf\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u600e\u4e48\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\u7684\uff1f\u201d \u9646\u5c0f\u51e4\u6447\u6447\u5934\u3002 \u5b64\u72ec\u7f8e\u51b7\u7b11\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u5fc3\u91cc\u6839\u672c\u5c31\u6068\u4e0d\u5f97\u53bb\u505a\u897f\u95e8\u5439\u96ea\u7684\u513f\u5b50\u3002\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e5f \u8bb8\u4ed6\u53ea\u4e0d\u8fc7\u60f3\u505a\u7b2c\u4e8c\u4e2a\u897f\u95e8\u5439\u96ea\u3002\u201d \u5b64\u72ec\u7f8e\u51b7\u51b7\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u897f\u95e8\u5439\u96ea\u7684\u597d\u5904\u4ed6\u8fde\u4e00\u70b9\u90fd\u6ca1\u6709\u5b66\u4f1a\uff0c\u6bdb\u75c5\u5374\u5b66\u5168\u4e86\u3002\u201d \u8fdc\u5c71\u4e0a\u51b0\u96ea\u822c\u9ad8\u50b2\u7684\u6027\u683c\uff0c\u51ac\u591c\u91cc\u6d41\u661f\u822c\u95ea\u4eae\u7684\u751f\u547d\uff0c\u5929\u4e0b\u65e0\u53cc\u7684\u5251\u2026\u2026\u201d \u6c5f\u6e56\u4e2d\u5b66\u4f1a\u7684\u5c11\u5e74\u4eec\uff0c\u53c8\u6709\u51e0\u4e2a\u4e0d\u628a\u897f\u95e8\u5439\u96ea\u5f53\u505a\u4ed6\u5fc3\u76ee\u4e2d\u795e\u62b5\uff1f \u9646\u5c0f\u51e4\u76ee\u5149\u9065\u89c6\u7740\u8fdc\u65b9\uff0c\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u897f\u95e8\u5439\u96ea\u81f3\u5c11\u6709\u4e00\u70b9\u662f\u522b\u4eba\u5b66\u4e0d\u50cf \u7684\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4ed6\u7684\u5251\uff1f\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\u7684\u5251\uff0c\u662f\u4ed6\u7684\u5bc2\u5bde\u3002 \u5bc2\u5bde\u3002 \u8fdc\u5c71\u4e0a\u51b0\u96ea\u822c\u5bd2\u51b7\u7684\u5bc2\u5bde\uff0c\u51ac\u591c\u91cc\u6d41\u661f\u822c\u5b64\u72ec\u7684\u5bc2\u5bde\u3002 \u53ea\u6709\u4e00\u4e2a\u771f\u6b63\u80fd\u4f53\u4f1a\u5230\u8fd9\u79cd\u5bc2\u5bde\uff0c\u800c\u4e14\u7518\u613f\u5fcd\u53d7\u8fd9\u79cd\u5bc2\u5bde\u7684\u4eba\uff0c\u624d\u80fd\u8fbe\u5230\u897f\u95e8\u5439\u96ea \u5df2\u8fbe\u5230\u4e86\u7684\u90a3\u79cd\u5883\u754c\u3002 \u53f6\u5b64\u9e3f\u4e00\u76f4\u5728\u51b7\u51b7\u7684\u76ef\u7740\u9646\u5c0f\u51e4\uff0c\u76f4\u5230\u8fd9\u65f6\u624d\u5f00\u53e3\u3002 \u4ed6\u5ffd\u7136\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u4e5f\u914d\u5728\u6211\u9762\u524d\u8c08\u8bba\u4ed6\u3002\u201d \u9646\u5c0f\u51e4\u53ea\u6709\u82e6\u7b11\u3002 \u4ed6\u77e5\u9053\u5b64\u72ec\u7f8e\u4e00\u5b9a\u4f1a\u62a2\u7740\u4ed6\u56de\u7b54\u8fd9\u53e5\u8bdd\u7684\u4ed6\u679c\u7136\u6ca1\u6709\u731c\u9519\u3002 \u5b64\u72ec\u7f8e\u5df2\u7b11\u9053\uff1a\u201c\u4ed6\u4e5f\u4e0d\u80fd\u7b97\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u4eba\u800c\u5df2\uff0c\u53ef\u662f\u8fd9\u4e16\u754c\u5047\u5982\u8fd8 \u6709\u4e00\u4e2a\u4eba\u591f\u8d44\u683c\u8c08\u8bba\u897f\u95e8\u5439\u96ea\uff0c\u8fd9\u4e2a\u4eba\u5c31\u662f\u4ed6\u3002 \u53f6\u5b64\u9e3f\u5fcd\u4e0d\u4f4f\u95ee\u3002\u201d\u4e3a\u4ec0\u4e48\uff1f\u201d \u5b64\u72ec\u7f8e\u60a0\u7136\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u6709\u56db\u6761\u7709\u6bdb\uff0c\u4e5f\u56e0\u4e3a\u8fd9\u4e16\u4e0a\u53ea\u6709\u4ed6\u4e00\u4e2a\u4eba\u8ddf\u897f\u95e8\u5439\u96ea\u7684\u8001 \u5a46\u7761\u8fc7\u89c9\u3002\u201d \u53f6\u5b64\u9e3f\u8038\u7136\u52a8\u5bb9\u9053\uff1a\u201c\u9646\u5c0f\u51e4\uff0c\u4f60\u5c31\u662f\u9646\u5c0f\u51e4\uff1f\u201d \u9646\u5c0f\u51e4\u53ea\u6709\u627f\u8ba4\u3002 \u53f6\u5b64\u9e3f\u63e1\u5251\u7684\u624b\u5df2\u56e0\u7528\u529b\u800c\u51f8\u51fa\u9752\u7b4b\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u672c\u8be5\u5148\u66ff\u897f\u95e8\u5439\u96ea\u6740\u4e86\u4f60 \u7684\u2026\u2026\u201d \u6811\u68a2\u4e0a\u5ffd\u7136\u6709\u4eba\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u53ea\u53ef\u60dc\u6211\u4eec\u8fd9\u6b21\u8981\u6740\u7684\u4eba\u5e76\u4e0d\u662f\u4ed6\u3002 \u6d53\u5bc6\u7684\u65f6\u95f4\u3002\u201d\u54d7\u5566\u5566\u201d\u4e00\u58f0\u54cd\uff0c\u4e00\u4e2a\u4eba\u71d5\u5b50\u822c\u98de\u4e0b\u6765\u3002 \u7c89\u7ea2\u7684\u71d5\u5b50\u3002 \u2014\u5f20\u5c11\u5973\u822c\u5ae3\u7ea2\u7684\u8138\uff0c\u4e00\u8eab\u526a\u88c1\u6781\u5408\u8eab\u7684\u7c89\u7ea2\u8272\u8863\u88f3\uff0c\u7c89\u7ea2\u8272\u8170\u5e26\u65c1\uff0c\u659c\u63a5\u7740\u4e00\u652f \u7c89\u7ea2\u8272\u7684\u76ae\u56ca\u3002 \u751a\u81f3\u8fde\u4ed6\u773c\u775b\u91cc\u90fd\u5e26\u7740\u79cd\u7c89\u7ea2\u8272\u7684\u8868\u60c5\u5c31\u662f\u5927\u591a\u6570\u7537\u4eba\u4eec\u770b\u89c1\u5c11\u5973\u8d64\u88f8\u7684\u5927\u817f\u65f6\u90a3 \u79cd\u8868\u60c5\u3002 \u8981\u547d\u7684\u662f\uff0c\u4ed6\u770b\u7740\u9646\u5c0f\u51e4\u65f6\uff0c\u773c\u775b\u91cc\u5c45\u7136\u4e5f\u5e26\u7740\u8fd9\u79cd\u8868\u60c5\u3002 \u9646\u5c0f\u51e4\u5ffd\u7136\u60f3\u5410\u3002 \u7c89\u71d5\u5b50\u5bf9\u4ed6\u7684\u53cd\u5e94\u5374\u5b8c\u5168\u4e0d\u5728\u4e4e\uff0c\u8fd8\u662f\u5fae\u7b11\u7740\uff0c\u770b\u7740\u4ed6\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u9646\u5c0f\u51e4\u679c\u7136\u4e0d \u6127\u662f\u9646\u5c0f\u51e4\uff0c\u679c\u7136\u6ca1\u6709\u8ba9\u6211\u5931\u671b\u2019\u2019 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u54e6\uff1f\u201d \u7c89\u71d5\u5b50\u9053\uff1a\u201c\u4f60\u73b0\u5728\u7684\u6837\u5b50\u770b\u6765\u867d\u7136\u4e0d\u592a\u597d\uff0c\u53ef\u662f\u53ea\u8981\u7ed9\u4f60\u4e00\u76c6\u70ed\u6c34\uff0c\u4e00\u5757\u9999\u80f0 \u5b50\uff0c\u8ba9\u4f60\u597d\u597d\u7684\u6d17\u4e2a\u6fa1\uff0c\u4f60\u5c31\u4e00\u5b9a\u662f\u4e2a\u5f88\u597d\u770b\u7684\u7537\u4eba\u4e86\u3002\u201d \u4ed6\u772f\u7740\u773c\u775b\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u7684\u6253\u91cf\u7740\u9646\u5c0f\u51e4\u3002\u201d\u6211\u73b0\u5728\u5c31\u53ef\u4ee5\u60f3\u50cf\u5f97\u5230\u3002\u201d \u9646\u5c0f\u51e4\u5ffd\u7136\u53c8\u4e0d\u592a\u60f3\u5410\u4e86\uff0c\u56e0\u4e3a\u4ed6\u73b0\u5728\u6700\u60f3\u505a\u7684\u4e00\u4ef6\u4e8b\uff0c\u662f\u4e00\u62f3\u6253\u6241\u8fd9\u4e2a\u4eba\u7684\u9f3b \u5b50\u3002 \u5e78\u597d\u8fd9\u65f6\u7c89\u71d5\u5b50\u5df2\u8f6c\u8fc7\u8138\u53bb\u770b\u53f6\u5b64\u9e3f\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u662f\u6211\u7684\uff0c\u6211\u4e0d\u8bb8\u4f60\u78b0\u4ed6\u3002\u201d \u53f6\u5b64\u9e3f\u8138\u4e0a\u4e5f\u9732\u51fa\u79cd\u60f3\u5455\u5410\u7684\u8868\u60c5\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u7537\u4eba\u5973\u4eba\u4f60\u90fd\u8981\uff1f\u201d \u7c89\u71d5\u5b50\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6709\u65f6\u5019\u6211\u8fde\u4f60\u90fd\u60f3\u8981\u3002\u201d \u53f6\u5b64\u9e3f\u82cd\u767d\u7684\u8138\u5df2\u53d1\u9752\u3002 \u7c89\u71d5\u5b50\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u76f4\u5f88\u8ba8\u538c\u6211\uff0c\u5374\u53c8\u504f\u504f\u5c11\u4e0d\u4e86\u6211\uff0c\u56e0\u4e3a\u8fd9\u6b21\u5047\u5982\u4f60\u6ca1\u6709 \u6211\uff0c\u975e\u4f46\u627e\u4e0d\u5230\u8fd9\u8001\u72d0\u72f8\uff0c\u800c\u4e14\u4f60\u4e5f\u4f11\u60f3\u80fd\u6d3b\u7740\u56de\u53bb\u3002\u201d \u4ed6\u5fae\u7b11\u7740\uff0c\u63a5\u7740\u9053\uff1a\u201c\u50cf\u4f60\u8fd9\u79cd\u540d\u95e8\u6b63\u6d3e\u7684\u5c11\u5e74\u82f1\u96c4\uff0c\u5728\u5916\u9762\u867d\u7136\u8000\u6b66\u626c\u5a01\uff0c\u5230\u4e86 \u8fd9\u5403\u4eba\u7684\u6811\u6797\u91cc\uff0c\u5f88\u53ef\u80fd\u8fde\u4e24\u4e2a\u65f6\u8fb0\u90fd\u6d3b\u4e0d\u4e0b\u53bb\u3002\u201d \u53f6\u5b64\u9e3f\u5c45\u7136\u6ca1\u6709\u5426\u8ba4\u3002 \u7c89\u71d5\u5b50\u8f7b\u8f7b\u5410\u51fa\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u73b0\u5728\u6211\u82e5\u80af\u628a\u8fd9\u8001\u72d0\u72f8\u8ba9\u7ed9\u4f60\uff0c\u4f60\u5c31\u5e94\u8be5\u89c9\u5f97 \u5f88\u6ee1\u610f\u4e86\u3002\u201d \u53f6\u5b64\u9e3f\u7684\u624b\u53c8\u63e1\u7d27\u5251\u67c4\uff0c\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u8ba9\u6211\u51fa\u624b\uff0c\u4f60\u77e5\u9053\u6211\u5df2\u53d1\u4e0b\u91cd\u8a93\uff0c\u4e00\u5b9a\u8981 \u4eb2\u624b\u4f17\u4e86\u4ed6\u3002\u201d \u7c89\u71d5\u5b50\u9053\uff1a\u201c\u9646\u5c0f\u51e4\u5417\uff1f\u201d \u53f6\u5b64\u9e3f\u54ac\u4e86\u54ac\u7259\uff0c\u9053\uff1a\u201c\u9646\u5c0f\u51e4\u662f\u4f60\u7684\uff0c\u53ea\u8981\u4ed6\u2026\u2026\u201d \u5b64\u72ec\u7f8e\u5ffd\u7136\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u9519\u4e86\uff0c\u9646\u5c0f\u51e4\u65e2\u4e0d\u662f\u4ed6\u7684\uff0c\u4e5f\u4e0d\u662f\u4f60\u7684\u300f\u201d \u7c89\u71d5\u5b50\u9053\uff1a\u201c\u662f\u8c01\u7684\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u662f\u6211\u7684\u3002\u201d \u7c89\u71d5\u5b50\u4e5f\u5927\u7b11\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u4ed6\u4e5f\u6709\u6211\u2014\u4f6f\u7684\u6bdb\u75c5\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u770b\u4e0a\u4f60\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u82e5\u60f3\u6d3b\u4e0b\u53bb\uff0c\u5c31\u4e0d\u80fd\u8ba9\u6211\u6b7b\u5728\u4f60\u4eec\u624b\u7530\u2019\u2019 \u7c89\u71d5\u5b50\u53c8\u8f6c\u8eab\u9762\u5bf9\u9646\u5c0f\u51e4\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u53ea\u8981\u4f60\u4e0d\u7ba1\u6211\u4eec\u7684\u4e8b\uff0c\u6211\u4e5f\u4e00\u6837\u53ef\u4ee5\u8ba9\u4f60\u6d3b \u4e0b\u53bb\u3002\u201d \u9646\u5c0f\u51e4\u6ca1\u6709\u53cd\u5e94\u3002 \u7c89\u71d5\u5b50\u53c8\u5410\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53f6\u5927\u5c11\u7237\uff0c\u4f60\u73b0\u5728\u597d\u50cf\u5df2\u7ecf\u53ef\u4ee5\u51fa\u624b\u4e86\uff01\u201d \u53f6\u5b64\u9e3f\u9053\uff1a\u201c\u597d\u3002\u201d \u597d\u5b87\u51fa\u53e3\uff0c\u5251\u5df2\u51fa\u9798\u3002 \u4ed6\u62d4\u5251\u7684\u901f\u5ea6\u4e5f\u8bb8\u8fd8\u6bd4\u4e0d\u4e0a\u897f\u95e8\u5439\u96ea\uff0c\u5374\u7edd\u4e0d\u6bd4\u522b\u4eba\u6162\u3002 \u4ed6\u7684\u51fa\u624b\u8f7b\u7075\u72e0\u6bd2\u8f9b\u8fa3\uff0c\u9664\u4e86\u5ae1\u4f20\u7684\u6b66\u5f53\u5fc3\u6cd5\u5916\uff0c\u81f3\u5c11\u8fd8\u6eb6\u5408\u4e86\u53e6\u5916\u4e24\u5bb6\u7684\u5251\u6cd5\u7279 \u957f\u3002 \u8fd9\u4e00\u5251\u5df2\u662f\u4ed6\u5251\u6cd5\u4e2d\u7684\u7cbe\u7cb9\u3002 \u8fd9\u4e5f\u662f\u81f4\u547d\u7684\u4e00\u5251\uff0c\u4e00\u5251\u5fc5\u4e2d\uff0c\u4e0d\u7559\u540e\u7740\u3002 \u5b64\u72ec\u7f8e\u5f20\u5927\u4e86\u5634\uff0c\u60f3\u547c\u558a\uff0c\u5374\u8fde\u4e00\u70b9\u58f0\u97f3\u90fd\u6ca1\u6709\u53d1\u51fa\u6765\u3002 \u9646\u5c0f\u51e4\u5c45\u7136\u771f\u7684\u6ca1\u6709\u963b\u62e6\u3002 \u7c89\u71d5\u5b50\u8fd8\u5728\u7b11\uff0c\u7b11\u5bb9\u5374\u7a81\u7136\u51bb\u7ed3\u3002 \u4e00\u622a\u5251\u5c16\u5ffd\u7136\u4ece\u4ed6\u5fc3\u53e3\u4e0a\u9732\u4e86\u51fa\u6765\uff0c\u9c9c\u8840\u98de\u6e85\uff0c\u6d12\u843d\u5728\u4ed6\u81ea\u5df1\u773c\u524d\u3002 \u8fd9\u662f\u4ed6\u81ea\u5df1\u7684\u8840\uff1f \u4ed6\u4e0d\u4fe1\uff01 \u53ea\u53ef\u60dc\u73b0\u5728\u4ed6\u5df2\u4e0d\u80fd\u4e0d\u4fe1\u3002 \u4ed6\u4f38\u624b\uff0c\u60f3\u53bb\u638f\u4ed6\u56ca\u4e2d\u7684\u6697\u5668\uff0c\u53ef\u662f\u4ed6\u7684\u4eba\u5df2\u5012\u4e86\u4e0b \u5251\u5c16\u8fd8\u5728\u6ef4\u7740\u8840\u3002 \u53f6\u5b64\u9e3f\u51dd\u89c6\u7740\u5251\u5c16\u7684\u8840\u73e0\uff0c\u8f7b\u8f7b\u7684\u5439\u843d\u4e86\u6700\u540e\u4e00\u6ef4\u3002 \u8fd9\u672c\u662f\u897f\u95e8\u5439\u96ea\u72ec\u7279\u7684\u4e60\u60ef\uff0c\u4ed6\u6bcf\u4e00\u4e2a\u52a8\u4f5c\u90fd\u5b66\u5f97\u5f88\u50cf\u3002 \u53ea\u53ef\u60dc\u4ed6\u4e0d\u662f\u897f\u95e8\u5439\u96ea\uff0c\u7edd\u4e0d\u662f\u300f \u6bcf\u5f53\u6740\u4eba\u540e\uff0c\u897f\u95e8\u5439\u96ea\u5c31\u4f1a\u7acb\u523b\u53d8\u5f97\u8bf4\u4e0d\u51fa\u7684\u5b64\u72ec\u5bc2\u5bde\uff0c\u8bf4\u4e0d\u51fa\u7684\u538c\u5026\u3002 \u4ed6\u5439\u843d\u4ed6\u5251\u5c16\u6700\u540e\u7684\u4e00\u6ef4\u8840\uff0c\u53ea\u4e0d\u8fc7\u50cf\u98ce\u96ea\u4e2d\u7684\u591c\u5f52\u4eba\u6296\u843d\u8863\u895f\u4e0a\u6700\u540e\u4e00\u7247\u96ea\u82b1\u3002 \u4ed6\u5439\u7684\u662f\u96ea\uff0c\u4e0d\u662f\u8840\u3002 \u73b0\u5728\u53f6\u5b64\u9e3f\u773c\u775b\u91cc\u5374\u5e26\u7740\u79cd\u8bf4\u4e0d\u51fa\u7684\u5174\u594b\u548c\u6fc0\u52a8\uff0c\u5c31\u50cf\u662f\u6b63\u51c6\u5907\u51b2\u5165\u98ce\u96ea\u4e2d\u53bb\u7684\u5f81 \u4eba\u3002 \u4ed6\u5439\u7684\u662f\u8840\uff0c\u4e0d\u662f\u96ea\u3002 \u6700\u540e\u4e00\u6ef4\u8840\u6070\u5de7\u6ef4\u843d\u5728\u7c89\u71d5\u5b50\u8138\u4e0a\uff0c\u4ed6\u8138\u4e0a\u7684\u8089\u4eff\u4f5b\u8fd8\u5728\u62bd\u6410\uff0c\u773c\u73e0\u5374\u5df2\u6b7b\u9c7c\u822c\u51f8 \u51fa\uff0c\u518d\u4e5f\u770b\u4e0d\u89c1\u90a3\u79cd\u7c89\u7ea2\u8272\u7684\u8868\u60c5\u3002 \u9646\u5c0f\u51e4\u5ffd\u7136\u89c9\u5f97\u8fd9\u4e2a\u4eba\u5f88\u53ef\u601c\u3002 \u4ed6\u4e00\u76f4\u90fd\u5f88\u601c\u60af\u90a3\u4e9b\u81f3\u6b7b\u8fd8\u4e0d\u77e5\u9053\u81ea\u5df1\u4e3a\u4f55\u800c\u6b7b\u7684\u4eba\uff0c\u4ed6\u77e5\u9053\u8fd9\u4e2a\u4eba\u4e00\u5b9a\u6b7b\u4e0d\u669d \u76ee\u3002 \u8840\u5df2\u5e72\u4e86\uff0c\u5251\u5df2\u5165\u9798\u3002 \u53f6\u5b64\u9e3f\u5ffd\u7136\u8f6c\u8fc7\u8138\uff0c\u77aa\u7740\u5b64\u72ec\u7f8e\u3002 \u5b64\u72ec\u7f8e\u4e5f\u5728\u77aa\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u6000\u7591\u7684\u60ca\u8be7\u3002 \u53f6\u5b64\u9e3f\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u60f3\u4e0d\u5230\u6211\u4e3a\u4ec0\u4e48\u8981\u6740\u4ed6\uff1f\u201d \u5b64\u72ec\u7f8e\u7684\u786e\u60f3\u4e0d\u5230\uff0c\u65e0\u8bba\u8c01\u90fd\u60f3\u4e0d\u5230\u3002 \u53f6\u5b64\u9e3f\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u6740\u6211\u7684\uff1f\u201d \u53f6\u5b64\u9e3f\u9053\uff1a\u201c\u6211\u4e0d\u662f\u3002\u201d \u5b64\u72ec\u7f8e\u66f4\u60ca\u8bb6\uff0c\u9053\uff1a\u201c\u53ef\u4f60\u672c\u6765\u2026\u2026\u201d \u53f6\u5b64\u9e3f\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u6211\u672c\u6765\u7684\u786e\u5df2\u51b3\u5fc3\u8981\u4f60\u6b7b\u5728\u6211\u5251\u4e0b\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u73b0\u5728\u4f60\u4e3a\u4ec0\u4e48\u5ffd\u7136\u6539\u53d8\u4e86\u4e3b\u610f\uff1f\u201d \u53f6\u5b64\u9e3f\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u73b0\u5728\u5df2\u77e5\u9053\u4f60\u4e0d\u662f\u6d3b\u4eba\u3002\u201d \u8fd9\u53e5\u8bdd\u8bf4\u5f97\u66f4\u5947\u602a\uff0c\u66f4\u6559\u4eba\u542c\u4e0d\u61c2\uff0c\u5b64\u72ec\u7f8e\u5374\u53cd\u800c\u597d\u50cf\u542c\u61c2\u4e86\uff0c\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c \u9053\uff1a\u201c\u96be\u9053\u4f60\u4e5f\u662f\u5c71\u5e84\u91cc\u7684\u4eba\uff1f\u201d \u53f6\u5b64\u9e3f\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u5230\uff1f\u201d\u5b64\u72ec\u7f8e\u627f\u8ba4\uff1a\u2018\u6211\u505a\u68a6\u4e5f\u6ca1\u6709\u60f3\u5230\u8fc7\u3002\u201d \u53f6\u5b64\u9e3f\u773c\u775b\u91cc\u5ffd\u53c8\u9732\u51fa\u79cd\u8ba5\u6d88\u7684\u7b11\u610f\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u5f53\u7136\u60f3\u4e0d\u5230\u7684\uff0c \u6709\u4e9b\u4eba\u81ea\u5df1\u505a\u7684\u4e8b\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u60f3\u4e0d\u5230\u3002\u201d \u5b64\u72ec\u7f8e\u4e5f\u5728\u53f9\u606f\uff0c\u9053\uff1a\u201c\u5c71\u5e84\u91cc\u7684\u4eba\uff0c\u597d\u50cf\u90fd\u662f\u522b\u4eba\u6c38\u8fdc\u60f3\u4e0d\u5230\u7684\u3002\u201d\u53f6\u5b64\u9e3f\u9053\uff1a \u201c\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u6240\u4ee5\u5b83\u624d\u80fd\u5b58\u5728\u3002\u201d \u5b64\u72ec\u7f8e\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u5ffd\u7136\u6539\u53d8\u8bdd\u9898\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u770b\u89c1\u8fc7\u9646\u5c0f\u51e4\u51fa\u624b\uff1f\u201d \u53f6\u5b64\u9e3f\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u7684\u6b66\u529f\u6df1\u6d45\uff1f\u201d \u53f6\u5b64\u9e3f\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff1a\u201c \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u5bf9\u4ed6\u8fd9\u4e2a\u4eba\u4f60\u77e5\u9053\u4e9b\u4ec0\u4e48\uff1f\u201d \u53f6\u5b64\u9e3f\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u66fe\u7ecf\u63a5\u4f4f\u8fc7\u767d\u4e91\u57ce\u4e3b\u7684\u4e00\u5251\u2018\u5929\u5916\u98de\u4ed9\u2019\u3002\u201d\u5b64\u72ec\u7f8e\u9053\uff1a \u201c\u53ef\u662f\u4ed6\u73b0\u5728\u5374\u5df2\u4f24\u5728\u897f\u95e8\u5439\u96ea\u5251\u4e0b\u3002\u201d \u53f6\u5b64\u9e3f\u9053\uff1a\u201c\u6211\u770b\u5f97\u51fa\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u73b0\u5728\u6211\u518d\u95ee\u4f60\u4e00\u53e5\u8bdd\uff0c\u4f60\u4e00\u5b9a\u8981\u591a\u52a0\u8003\u8651\uff0c\u624d\u80fd\u56de\u7b54\u3002\u201d \u4ed6\u7684\u8868\u60c5\u53d8\u5f97\u5f88\u4e25\u8083\uff0c\u4e00\u5b87\u5b57\u63a5\u7740\u9053\uff1a\u201c\u73b0\u5728\u4f60\u6709\u6ca1\u6709\u628a\u63e1\u6740\u4e86\u4ed6\uff1f\u201d \u53f6\u5b64\u9e3f\u6c89\u9ed8\u7740\u3002\u773c\u775b\u91cc\u53c8\u9732\u51fa\u90a3\u79cd\u8ba5\u6d88\u7684\u7b11\u610f\uff0c\u989d\u4e0a\u9752\u7b4b\u4e00\u6839\u6839\u51f8\u8d77\uff0c\u53c8\u8fc7\u4e86\u5f88 \u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u6211\u4e0d\u662f\u897f\u95e8\u5439\u96eaqo \u5b64\u72ec\u7f8e\u770b\u7740\u4ed6\uff0c\u4e5f\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u8f6c\u8fc7\u8138\u53bb\u770b\u9646\u5c0f\u51e4\u3002 \u9646\u5c0f\u51e4\u8138\u4e0a\u4e00\u70b9\u8868\u60c5\u4e5f\u6ca1\u6709\uff0c\u4ed6\u4eec\u521a\u624d\u8bf4\u7684\u8bdd\uff0c\u4ed6\u597d\u50cf\u5168\u542c\u4e0d\u61c2\u3002 \u5b64\u72ec\u7f8e\u5ffd\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u521a\u624d\u5e76\u6ca1\u6709\u51fa\u624b\u6551\u6211\u3002\u201d \u9646\u5c0f\u51e4\u6c89\u9ed8\u3002 \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u73b0\u5728\u6211\u4e5f\u4e0d\u613f\u51fa\u624b\u6740\u4f60\uff0c\u56e0\u4e3a\u6211\u4eec\u6ca1\u6709\u628a\u63e1\u6740\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u6c89\u9ed8\u3002 \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u6211\u4eec\u672c\u6765\u7d20\u6627\u5e73\u751f\uff0c\u4e92\u4e0d\u60f3\u8bc6\uff0c\u73b0\u5728\u8fd8\u662f\u5982\u6b64\u3002\u201d \u9646\u5c0f\u51e4\u7ec8\u4e8e\u5f00\u53e3\uff0c\u9053\uff1a\u201c\u53ef\u662f\u6211\u4eec\u521a\u624d\u8d70\u7684\u597d\u50cf\u8fd8\u662f\u540c\u4e00\u6761\u8def\u3002\u201d \u5b64\u72ec\u7f8e\u6de1\u6de1\u9053\uff1a\u201c\u4e16\u4e8b\u5982\u767d\u4e91\u82cd\u72d7\uff0c\u968f\u65f6\u968f\u5730\u90fd\u53ef\u80fd\u5e72\u4e07\u79cd\u53d8\u5316\uff0c\u53c8\u4f55\u51b5\u4f60\u6211\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6709\u7406\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u6211\u5f53\u7136\u6709\u6211\u7684\u8def\u53ef\u8d70\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ec0\u4e48\u8def\uff1f\u5230\u5c71\u5e84\u53bb\u7684\u8def\uff1f\u201d \u5b64\u72ec\u7f8e\u6c89\u4e0b\u8138\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u65e2\u7136\u5df2\u542c\u89c1\uff0c\u53c8\u4f55\u5fc5\u518d\u95ee\u3002\u2019 \u9646\u5c0f\u51e4\u5374\u504f\u504f\u8fd8\u662f\u8981\u95ee\u3002\u201d\u4f60\u8981\u53bb\u7684\u662f\u4ec0\u4e48\u5c71\u5e84\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u662f\u4e2a\u4f60\u53bb\u4e0d\u5f97\u7684\u5c71\u5e84\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u53bb\u4e0d\u5f97\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u56e0\u8fd9\u4f60\u4e0d\u662f\u6b7b\u4eba\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u90a3\u5c71\u5e84\u53ea\u6709\u6b7b\u4eba\u53bb\u5f97\uff01\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u5df2\u662f\u6b7b\u4e86\uff1f \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\u3002\u201d\u4f60\u4eec\u8d70\u5427\u3002\u201d \u4ed6\u5fae\u7b11\u7740\u6325\u624b\u3002\u201d\u6211\u65e2\u4e0d\u60f3\u5230\u6b7b\u4eba\u7684\u5c71\u5e84\u53bb\uff0c\u4e5f\u4e0d\u60f3\u505a\u6b7b\u4eba\uff0c\u53ea\u8981\u80fd\u6d3b\u7740\uff0c\u591a\u6d3b\u534a \u4e2a\u65f6\u8fb0\u4e5f\u662f\u597d\u7684\u3002\u201d \u4ed6\u8d70\u5f97\u5c45\u7136\u5f88\u6d12\u8131\uff0c\u5728\u7070\u767d\u8272\u7684\u4e1b\u6797\u4e2d\u3002 \u76f4\u5230\u4ed6\u7684\u4eba\u5f71\u6d88\u5931\uff0c\u5b64\u72ec\u7f8e\u624d\u50cf\u662f\u5ffd\u7136\u8b66\u89c9\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u771f\u7684\u8ba9\u4ed6\u8d70\uff1f\u201d \u53f6\u5b64\u9e3f\u51b7\u51b7\u9053\uff1a\u201c\u4ed6\u5df2\u7ecf\u8d70\u4e86\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u4ed6\u6cc4\u9732\u5c71\u5e84\u7684\u79d8\u5bc6\uff1f\u201d \u53f6\u5b64\u9e3f\u9053\uff1a\u201c\u4ed6\u77e5\u9053\u7684\u79d8\u5bc6\u5e76\u4e0d\u591a\uff0c\u4f55\u51b5\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u4ed6\u5f88\u53ef\u80fd\u771f\u7684\u6d3b\u4e0d\u5230\u534a\u4e2a \u65f6\u8fb0\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u76f4\u5c11\u4ed6\u73b0\u5728\u8fd8\u6ca1\u6709\u6b7b\uff0c\u8fd8\u53ef\u4ee5\u5728\u6697\u4e2d\u8ddf\u7740\u6211\u4eec\u53bbaU \u53f6\u5b64\u9e3f\u9053\uff1a\u201c\u6211\u4eec\u8981\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u5f53\u7136\u662f\u5230\u5c71\u5e84\u53bb\u3002\u201d \u53f6\u5b64\u9e3f\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u9519\u4e86\uff0c\u5e76\u4e0d\u662f\u6211\u4eec\u8981\u5230\u5c71\u5e84\u53bb\uff0c\u662f\u4f60\u8981\u53bb\uff0c\u4f60\u4e00\u4e2a\u4eba\u53bb\uff01 \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4f60\u4e0d\u53bb\uff1f\u201d \u53f6\u5b64\u9e3f\u6de1\u6de1\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u53bb\uff1f\u201d \u5b64\u72ec\u7f8e\u8138\u8272\u53d8\u4e86\u3002 \u53f6\u5b64\u9e3f\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u548c\u5c71\u5e84\u6709\u4e86\u5408\u7ea6\uff0c\u5f53\u7136\u4e0d\u80fd\u6740\u4f60\uff0c\u4f46\u662f\u6211\u4e5f\u6ca1\u6709\u8bf4\u8981\u5e26 \u53bb\u3002\u201d \u5b64\u72ec\u7f8e\u7684\u8138\u5df2\u56e0\u6124\u6012\u6050\u60e7\u800c\u53d8\u5f62\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u53ef\u662f\u4e5f\u4e5f\u5e94\u8be5\u770b\u5f97\u51fa\u73b0\u5728\u6211\u8fde\u2014\u6b65\u8def \u90fd\u4e0d\u80fd\u8d70\u3002\u201d \u53f6\u5b64\u9e3f\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u662f\u4f60\u7684\u4e8b\u4e86\uff0c\u8ddf\u6211\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u4ed6\u7a81\u53c8\u62d4\u5251\uff0c\u524a\u843d\u4e00\u5927\u7247\u6811\u76ae\uff0c\u94fa\u5728\u4e00\u5757\u6bd4\u8f83\u5e72\u71e5\u7684\u6ce5\u571f\u4e0a\uff0c\u76d8\u819d\u5750\u4e86\u4e0b\u53bb\u3002 \u5b64\u72ec\u7f8e\u6068\u6068\u7684\u76ef\u7740\u4ed6\uff0c\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u8d70\uff1f\u201d \u53f6\u5b64\u9e3f\u60a0\u7136\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u8d70\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u5728\u7b49\u7740\u770b\u6211\u6b7b\uff1f\u201d \u53f6\u5b64\u9e3f\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u6162\u6162\u7684\u6b7b\uff0c\u6211\u5e76\u4e0d\u7740\u6025\u3002\u201d \u4ed6\u770b\u6765\u4e0d\u4f46\u5f88\u60a0\u95f2\uff0c\u800c\u4e14\u5f88\u8212\u670d\uff0c\u56e0\u4e3a\u4ed6\u8eab\u4e0a\u5c45\u7136\u8fd8\u5e26\u7740\u5757\u6cb9\u7eb8\u5305\u7740\u7684\u725b\u8089\uff0c\u751a\u81f3 \u8fd8\u6709\u74f6\u9152\u3002 \u5bf9\u4e00\u4e2a\u9965\u6e34\u4e2d\u6323\u624e\u4e86\u4e8c\u5341\u516d\u4e2a\u65f6\u8fb0\u7684\u8001\u4eba\u8bf4\u6765\uff0c\u725b\u8089\u548c\u9152\u7684\u9999\u6c14\uff0c\u5df3\u4e0d\u518d\u662f\u8bf1\u60d1\uff0c \u800c\u662f\u79cd\u8650\u5f85\u3002 \u56e0\u4e3a\u4ed6\u53ea\u80fd\u770b\u7740\uff0c\u4e00\u9635\u9635\u9999\u6c14\u5c31\u50cf\u662f\u4e00\u6839\u6839\u9488\uff0c\u523a\u6fc0\u5f97\u4ed6\u5168\u8eab\u76ae\u80a4\u90fd\u8d77\u4e86\u6218\u6817\u3002 \u6d45\u6d45\u7684\u77aa\u4e86\u4e00\u53e3\u9152\uff0c\u53f6\u5b64\u9e3f\u6ee1\u610f\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u73b0\u5728\u5fc3\u91cc\u2014\u5b9a\u5728 \u540e\u6094\uff0c\u521a\u624d\u4e0d\u8be5\u8ba9\u9646\u5c0f\u51e4\u8d70\u7684\uff0c\u4f46\u6709\u4ef6\u4e8b\u4f60\u5374\u4e0d\u77e5\u9053\u3002\u201d \u5b64\u72ec\u7f8e\u6b63\u60f3\u4ee5\u8c08\u8bdd\u5206\u6563\u81ea\u5df1\u7684\u6ce8\u610f\u529b\uff0c\u7acb\u523b\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u53f6\u5b64\u9e3f\u9053\uff1a\u201c\u6211\u4e0d\u6740\u9646\u5c0f\u51e4\uff0c\u5e76\u4e0d\u662f\u56e0\u4e3a\u6211\u6ca1\u6709\u628a\u63e1\u6740\u4ed6\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u6211\u60c5\u613f\u8ba9\u4ed6 \u6b7b\u5728\u897f\u95e8\u5439\u96ea\u624b\u91cc\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u54e6\uff01 \u53f6\u5b64\u9e3f\u50b2\u7136\u9053\uff1a\u201c\u73b0\u5728\u4ed6\u82e5\u6562\u518d\u6765\uff0c\u6211\u4e00\u5251\u51fa\u9798\uff0c\u5c31\u8981\u4ed6\u8840\u6e85\u4e94\u6b65\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u4e0d\u662f\u8bf4\u3002\u5929\u4e0b\u5df2\u6ca1\u4eba\u80fd\u6551\u5f97\u4e86\u6211\uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u6551\u5f97\u4e86\u9646\u5c0f \u51e4\u3002\u201d\u53f6\u5b64\u9e3f\u9053\uff1a\u201c\u7edd\u6ca1\u6709\u3002\u201d \u8fd9\u4e09\u4e2a\u5b57\u521a\u8bf4\u5b8c\u5ffd\u7136\u95f4\uff0c\u4e00\u53ea\u624b\u4ece\u6811\u540e\u4f38\u51fa\u6765\uff0c\u62ff\u8d70\u4e86\u4ed6\u624b\u91cc\u7684\u9152\u3002 \u4ed6\u7684\u53cd\u5e94\u5e76\u4e0d\u6162\u3002 \u8fd9\u53ea\u624b\u7f29\u56de\u53bb\u7684\u65f6\u5019\uff0c\u4ed6\u7684\u4eba\u4e5f\u5df2\u5230\u6811\u540e\u3002 \u6811\u540e\u5374\u6ca1\u6709\u4eba\u3002 \u7b49\u4ed6\u518d\u8f6c\u51fa\u6765\u65f6\uff0c\u9152\u74f6\u5df2\u5728\u5b64\u72ec\u7f8e\u624b\u91cc\uff0c\u6b63\u5c06\u6700\u540e\u4e00\u6ef4\u9152\u5012\u5165\u81ea\u5df1\u7684\u5634\u3002 \u521a\u624d\u8fd8\u5728\u6811\u76ae\u4e0a\u7684\u6cb9\u7eb8\u5305\u4e2d\u8089\uff0c\u73b0\u5728\u5374\u5df2\u4e0d\u89c1\u4e86\u3002 \u53f6\u5b64\u9e3f\u6ca1\u6709\u518d\u52a8\uff0c\u751a\u81f3\u8fde\u547c\u5438\u90fd\u5df2\u505c\u987f\uff0c\u7070\u767d\u8272\u7684\u4e1b\u6797\uff0c\u6b7b\u5bc2\u5982\u575f\u5893\u3002 \u8fde\u98ce\u90fd\u6ca1\u6709\uff0c\u6811\u68a2\u5374\u5ffd\u7136\u6709\u6837\u4e1c\u897f\u98d8\u98d8\u843d\u5e72\u3002 \u53f6\u5b64\u9e3f\u62d4\u5251\uff0c\u7a7f\u900f\u3002 \u63d2\u5728\u4ed6\u5251\u5c16\u4e0a\u7684\uff0c\u7adf\u662f\u521a\u624d\u5305\u7740\u7684\u725b\u8089\u7684\u90a3\u5757\u6cb9\u7eb8\u3002 \u5b64\u72ec\u7f8e\u7b11\u4e86\uff0c\u5927\u7b11\uff0c\u7b11\u5f97\u8fde\u773c\u6cea\u90fd\u6d41\u4e86\u51fa\u6765\u3002 \u53f6\u5b64\u9e3f\u597d\u50cf\u5b8c\u5168\u542c\u4e0d\u89c1\uff0c\u8138\u8272\u5374\u5df2\u53d1\u9752\uff0c\u6162\u6162\u7684\u6458\u4e0b\u5251\u5c16\u4e0a\u7684\u6cb9\u7eb8\u3002 \u5b64\u72ec\u7f8e\u7b11\u9053\uff1a\u201c\u6cb9\u7eb8\u4e0a\u6ca1\u6709\u8840\uff0c\u4f60\u5439\u4ec0\u4e48\uff1f\u201d \u53f6\u5b64\u9e3f\u8fd8\u662f\u542c\u4e0d\u89c1\uff0c\u5251\u5149\u4e00\u95ea\uff0c\u5251\u5165\u9798\u3002 \u4ed6\u5374\u53c8\u5728\u5757\u6811\u76ae\u4e0a\u5750\u4e0b\u6765\uff0c\u6df1\u6df1\u7684\u547c\u5438\u4e86\u4e24\u6b21\uff0c\u4ece\u8863\u8896\u91cc\u62ff\u51fa\u7eb8\u5377\uff0c\u7528\u4e00\u6839\u6839\u9488\u9489 \u5728\u8eab\u540e\u7684\u6811\u5e72\u4e0a\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u51fa\u6797\u5165\u5c71\u7684\u8be6\u56fe\uff0c\u8c01\u6709\u672c\u4e8b\uff0c\u4e5f\u4e0d\u59a8\u62ff\u8d70\u3002\u201d \u7136\u540e\u4ed6\u8fd8\u662f\u80cc\u5bf9\u7740\u6811\u5e72\uff0c\u52a8\u4e5f\u4e0d\u52a8\u7684\u5750\u5728\u90a3\u91cc\uff0c\u751a\u81f3\u8fde\u773c\u775b\u90fd\u5df2\u95ed\u4e0a\uff0c\u4eff\u4f5b\u8001\u50e7\u5df2 \u4eba\u5b9a\u3002 \u5b64\u72ec\u7f8e\u7b11\u58f0\u4e5f\u5df2\u505c\u987f\uff0c\u7741\u5927\u4e86\u773c\u775b\uff0c\u76ef\u7740\u6811\u5e72\u4e0a\u7684\u7eb8\u5201\u76ee\u81e3\u3002 \u4ed6\u77e5\u9053\u8fd9\u5c31\u662f\u53f6\u5b64\u9e3f\u7528\u6765\u9493\u9c7c\u7684\u9975\u3002 \u6b66\u5f53\u672c\u662f\u5185\u5bb6\u6b63\u5b97\uff0c\u53f6\u5f27\u9e3f\u56db\u5c81\u65f6\u5c31\u5728\u6b66\u5f53\uff0c\u5185\u529f\u4e00\u5b9a\u65e9\u5df3\u767b\u5802\u5165\u5ba4\u3002 \u73b0\u5728\u4ed6\u5c4f\u606f\u5185\u89c6\uff0c\u5fc3\u795e\u5408\u4e00\uff0c\u867d\u7136\u95ed\u7740\u773c\u775b\uff0c\u53ef\u662f\u4e94\u5341\u4e08\u65b9\u5706\u5185\u7684\u4e00\u9488\u4e00\u53f6\uff0c\u90fd\u4f11 \u60f3\u9003\u8fc7\u4ed6\u7684\u8033\u76ee\u3002 \u4ed6\u7684\u9975\u5df2\u5b89\u6392\u597d\u6b79\uff0c\u9c7c\u5462\uff1f\u201d \u9c7c\u662f\u4e0d\u662f\u4f1a\u4e0a\u94a9\uff1f\u201d \u5b64\u72ec\u7f8e\u7684\u547c\u5438\u5ffd\u7136\u4e5f\u505c\u987f\uff0c\u4ed6\u5df2\u770b\u89c1\u4e00\u53ea\u624b\u6084\u6084\u7684\u4ece\u6811\u540e\u4f38\u51fa\u6765\u3002 \u8fd9\u53ea\u624b\u7684\u52a8\u4f5c\u8f7b\u5feb\uff0c\u5f88\u7075\u5de7\uff0c\u624b\u4e00\u4f38\u51fa\uff0c\u5c31\u6478\u7740\u4e86\u6811\u5e72\u4e0a\u7684\u5377\u3002 \u5c31\u5728\u8fd9\u65f6\u3001\u5251\u5149\u53c8\u4e00\u95ea\uff0c\u5982\u95ea\u7535\u60ca\u8679\uff0c\u53ea\u542c\u201c\u593a\u201d\u7684\u2018\u54cd\uff0c\u5251\u5c16\u4eba\u6728\uff0c\u7adf\u6d3b\u751f\u751f\u7684 \u628a\u8fd9\u53ea\u624b\u9489\u5728\u6811\u4e0a\u3002\u5b64\u72ec\u7f8e\u7684\u8138\u8272\u53d8\u4e86\uff0c\u53f6\u5b64\u9e3f\u7684\u8138\u8272\u4e5f\u53d8\u4e86\u3002 \u4ed6\u6ca1\u6709\u770b\u89c1\u8840\u3002 \u624b\u4e0d\u662f\u6cb9\u7eb8\uff0c\u600e\u4e48\u4f1a\u6ca1\u6709\u8840\u3002 \u5b64\u72ec\u7f8e\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u4ed6\u5df2\u770b\u51fa\u8fd9\u53ea\u624b\u5e76\u6ca1\u6709\u88ab\u5251\u5c16\u9489\u4f4f\uff0c\u5251\u5c16\u5374\u5df2\u88ab\u8fd9\u53ea\u624b\u5939 \u4f4f\u3002 \u7528\u4e24\u6839\u624b\u6307\u5939\u4f4f\u3002 \u53f6\u5b64\u9e3f\u94c1\u9752\u7740\u8138\u5ffd\u53c8\u53d1\u7ea2\uff0c\u6ee1\u5934\u6c57\u73e0\u6eda\u6eda\u800c\u843d\uff0c\u4ed6\u5df2\u7528\u5c3d\u5168\u8eab\u6c14\u529b\u6765\u62d4\u4ed6\u7684\u5251\uff0c\u8fd9 \u67c4\u5251\u5374\uff1f \u7b2c\u4e09\u7ae0\u3000\u540c\u821f\u5171\u6d4e>> \u53e4\u9f99\u300a\u9646\u5c0f\u51e4\u7cfb\u5217\xb7\u5e7d\u7075\u5c71\u5e84\u300b \u7b2c\u4e09\u7ae0\u3000\u540c\u821f\u5171\u6d4e \u9003\u4ea1\u5e76\u6ca1\u6709\u7ec8\u6b62\uff0c\u9ed1\u6697\u5df2\u6765\u4e34\u3002 \u9ed1\u6697\u4e2d\u53ea\u542c\u5598\u606f\u58f0\uff0c\u4e24\u4e2a\u4eba\u7684\u5598\u606f\u58f0\u3002\u58f0\u97f3\u5df2\u505c\u4e0b\u6765\uff0c\u4eba\u5df2\u5012\u4e0b\u53bb\u3002 \u4e0d\u7ba1\u4e0b\u9762\u662f\u5e72\u571f\u4e5f\u597d\uff0c\u662f\u6e7f\u6ce5\u4e5f\u597d\uff0c\u4ed6\u4eec\u5df2\u5b8c\u5168\u6ca1\u6709\u9009\u62e9\u7684\u4f59\u5730\u3002 \u4e00\u5b9a\u8981\u8eba\u4e0b\u53bb\uff0c\u5c31\u7b97\u897f\u95e8\u5439\u96ea\u7684\u5251\u950b\u5df2\u5728\u54bd\u5589\uff0c\u90fd\u5f97\u8eba\u4e0b\u53bb\u3002 \u73b0\u5728\u5c31\u7b97\u7528\u5c3d\u4e16\u4e0a\u6240\u6709\u7684\u529b\u91cf\uff0c\u90fd\u5df2\u65e0\u6cd5\u8ba9\u4ed6\u518d\u5f80\u524d\u8d70\u4e00\u6b65\u3002 \u4ece\u9ed1\u6697\u4e2d\u770b\u8fc7\u53bb\uff0c\u6bcf\u9694\u51e0\u68f5\u6811\uff0c\u5c31\u6709\u4e00\u70b9\u661f\u5149\u822c\u7684\u78f7\u5149\u95ea\u52a8\u3002 \u2019\u5149\u8292\u6781\u5fae\u5f31\uff0c\u5c31\u7b97\u5728\u7edd\u5bf9\u7684\u9ed1\u6697\u4e2d\uff0c\u4e5f\u5f97\u5f88\u6ce8\u610f\u624d\u80fd\u770b\u5f97\u89c1\u3002 \u53ea\u8981\u6709\u4e00\u70b9\u70b9\u5929\u5149\uff0c\u78f7\u5149\u5c31\u4f1a\u6d88\u5931\u3002 \u987a\u7740\u8fd9\u78f7\u5149\u8d70\uff0c\u5c31\u80fd\u8d70\u51fa\u53bb\uff1f\u201d \u201c\u55ef\u201d \u201c\u4f60\u6709\u628a\u63e1\uff1f\u201d \u201c\u55ef\u3002\u201d\u5b64\u72ec\u7f8e\u867d\u7136\u5df2\u7d2f\u5f97\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u51fa\uff0c\u5374\u8fd8\u662f\u4e0d\u80fd\u4e0d\u56de\u7b54\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u9646\u5c0f\u51e4 \u4e00\u5b9a\u4f1a\u7ee7\u7eed\u95ee\u4e0b\u53bb\u3002 \u201c\u6211\u7edd\u5bf9\u6709\u628a\u63e1\u3002\u201d\u4ed6\u5598\u606f\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u53ea\u8981\u8ddf\u4ed6\u4eec\u6709\u4e86\u5408\u7ea6\uff0c\u4ed6\u4eec\u5c31\u7edd\u4e0d\u4f1a\u51fa \u5356\u4f60\u3002\u201d \u201c\u4ed6\u4eec\u662f\u8c01\uff1f\u201d\u9646\u5c0f\u51e4\u679c\u7136\u53c8\u5728\u95ee\u3002\u201d\u662f\u4e0d\u662f\u5c71\u5e84\u91cc\u7684\u4eba\uff1f\u201d \u201c\u55ef\u3002\u201d \u201c\u4ec0\u4e48\u5c71\u5e84\uff1f\u5728\u54ea\u91cc\uff1f\u201d\u9646\u5c0f\u51e4\u8fd8\u5728\u95ee\u3002\u201d\u4f60\u8ddf\u4ed6\u4eec\u8ba2\u7684\u662f\u4ec0\u4e48\u5408\u7ea6\uff1f\u201d \u5b64\u72ec\u7f8e\u6ca1\u6709\u56de\u7b54\uff0c\u542c\u4ed6\u7684\u547c\u5438\uff0c\u4eff\u4f5b\u5df2\u7761\u7740\u3002 \u65e0\u8bba\u4ed6\u662f\u4e0d\u662f\u5df2\u7761\u7740\uff0c\u4ed6\u663e\u7136\u5df2\u51b3\u5fc3\u62d2\u7edd\u518d\u56de\u7b54\u8fd9\u4e9b\u95ee\u9898\u3002 \u9646\u5c0f\u51e4\u597d\u50cf\u4e5f\u89c9\u5f97\u81ea\u5df1\u95ee\u5f97\u592a\u591a\uff0c\u5c45\u7136\u4e5f\u95ed\u4e0a\u5634\uff0c\u66f4\u60f3\u95ed\u4e0a\u773c\u775b\u7761\u4e00\u89c9\u3002 \u53ef\u662f\u4ed6\u504f\u504f\u7761\u4e0d\u7740\u3002 \u8fdc\u5904\u7684\u78f7\u5149\u95ea\u52a8\uff0c\u5ffd\u8fdc\u5ffd\u8fd1\u3002 \u4ed6\u7684\u77b3\u5b54\u7adf\u5df2\u75b2\u5026\u5f97\u8fde\u8fdc\u8fd1\u8ddd\u79bb\u90fd\u5206\u4e0d\u51fa\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u7761\u4e0d\u7740\uff1f \u53ea\u6709\u5728\u7edd\u5bf9\u9ed1\u6697\u4e2d\uff0c\u624d\u80fd\u5206\u8fa8\u51fa\u8fd9\u4e9b\u6307\u8def\u7684\u6697\u8bb0\uff0c\u82e5\u662f\u7528\u4e86\u706b\u6298\u5b50\uff0c\u53cd\u800c\u770b\u4e0d\u51fa \u4e86\uff0c\u767d\u5929\u5f53\u7136\u66f4\u770b\u4e0d\u51fa\u3002 \u8fd9\u4e00\u70b9\u53ea\u6015\u8fde\u897f\u95e8\u5439\u96ea\u90fd\u60f3\u4e0d\u5230\uff0c\u6240\u4ee5\u4ed6\u5f53\u7136\u4e5f\u4e0d\u4f1a\u5728\u8fd9\u79cd\u7edd\u5bf9\u9ed1\u6697\u4e2d\u8d70\u8def\u3002 \u770b\u6765\u5c71\u5e84\u4e2d\u90a3\u4e9b\u4eba\u5b9e\u5728\u5f88\u806a\u660e\uff0c\u4ed6\u4eec\u7684\u8ba1\u5212\u4e2d\u6bcf\u4e00\u70b9\u90fd\u60f3\u5f97\u5f88\u7edd\uff0c\u53c8\u5f88\u5468\u5230\u3002 \u5b64\u72ec\u7f8e\u662f\u4e0d\u662f\u771f\u7684\u4f1a\u5e26\u6211\u5230\u90a3\u5c71\u5e84\u53bb\uff1f \u4ed6\u6709\u5408\u7ea6\uff0c\u6211\u5374\u6ca1\u6709\uff0c\u6211\u53bb\u4e86\u4e4b\u540e\uff0c\u4ed6\u4eec\u662f\u4e0d\u662f\u80af\u6536\u5bb9\u6211\uff1f \u2014\u90a3\u5730\u65b9\u662f\u4e0d\u662f\u771f\u7684\u5b89\u5168\u9690\u79d8\uff1f\u8fde\u897f\u95e8\u5439\u96ea\u90fd\u627e\u4e0d\u5230\uff1f \u2014\u4e3a\u4ec0\u4e48\u90a3\u5730\u65b9\u53ea\u6709\u6b7b\u4eba\u624d\u80fd\u53bb\uff1f \u9646\u5c0f\u51e4\u7761\u4e0d\u7740\uff0c\u56e0\u4e3a\u4ed6\u5fc3\u91cc\u5b9e\u5728\u6709\u592a\u591a\u7684\u89e3\u4e0d\u5f00\u7684\u7ed3\uff0c\u4e00\u4e2a\u7ed3\uff0c\u4e00\u4e2a\u8c1c\uff1f \u7edd\u5bf9\u9ed1\u6697\uff0c\u5c31\u662f\u7edd\u5bf9\u7684\u5b89\u9759\u3002 \u5b64\u72ec\u7f8e\u7684\u547c\u5438\u4e5f\u6e10\u6e10\u53d8\u5f97\u5b89\u5b9a\u800c\u5747\u5300\uff0c\u5728\u9ed1\u6697\u4e2d\u542c\u6765\uff0c\u751a\u81f3\u6709\u70b9\u50cf\u662f\u97f3\u4e50\u3002 \u201c\u59b9\u59b9\u80cc\u7740\u6ce5\u5a03\u5a03\uff0c \u8d70\u5230\u82b1\u56ed\u6765\u770b\u82b1\uff0c \u5a03\u5a03\u54ed\u4e86\u53eb\u5988\u5988\uff0c \u6811\u4e0a\u7684\u5c0f\u9e1f\u7b11\u54c8\u54c8\u2026\u2026\u201d \u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u9646\u5c0f\u51e4\u7adf\u4ece\u8fd9\u516d\u4eb2\u4e0d\u8ba4\u7684\u8001\u4eba\u547c\u5438\u58f0\u4e2d\uff0c\u5fc6\u8d77\u81ea\u5df1\u7ae5\u5e74\u65f6\u7684\u513f \u6b4c\u3002 \u4ed6\u81ea\u5df1\u4e5f\u89c9\u5f97\u5f88\u597d\u7b11\uff0c\u53ef\u662f\u4ed6\u5e76\u6ca1\u6709\u7b11\u51fa\u6765\uff0c\u56e0\u4e3a\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u9ed1\u6697\u4e2d\u5ffd\u7136\u54cd\u8d77\u4e86 \u4e00\u58f0\u60e8\u547c\u3002 \u63a5\u7740\uff0c\u53c8\u662f\u201c\u9686\u201d\u7684\u4e00\u54cd\uff0c\u4e00\u4e2a\u4eba\u7684\u8eab\u5b50\u5f39\u8d77\u6765\uff0c\u53c8\u91cd\u91cd\u6454\u5728\u6ce5\u6cbc\u91cc\u3002 \u201c\u662f\u4f60\uff1f\u201d\u9646\u5c0f\u51e4\u5931\u58f0\u95ee\u3002 \u6ca1\u6709\u4eba\u56de\u7b54\u3002 \u8fc7\u4e86\u5f88\u4e45\uff0c\u9ed1\u6697\u4e2d\u624d\u54cd\u8d77\u4e86\u5b64\u72ec\u7f8e\u7684\u547b\u541f\u58f0\uff0c\u4eff\u4f5b\u53d7\u4e86\u4f24\u3002 \u662f\u6dee\u5728\u9ed1\u6697\u4e2d\u7a81\u88ad\u4ed6\uff1f \u9646\u5c0f\u51e4\u53ea\u89c9\u5f97\u5fc3\u8df3\u52a0\u5feb\uff0c\u5589\u5499\u53d1\u5e72\uff0c\u638c\u5fc3\u5374\u6e7f\u4e86\uff0c\u5728\u8fd9\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\u7684\u9ed1\u6697\u4e2d\uff0c\u4ed6 \u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\u3002\u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u542c\u89c1\u5b64\u72ec\u7f8e\u547b\u541f\u7740\u9053\uff1a\u201c\u86c7\u2026\u2026\u6bd2\u86c7\u3002\u201d \u9646\u5c0f\u51e4\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u662f\u6bd2\u86c7\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u6211\u88ab\u5b83\u54ac\u5230\u7684\u5730\u65b9\uff0c\u4e00\u70b9\u90fd\u4e0d\u75bc\uff0c\u53ea\u53d1\u9ebb\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f24\u53e3\u5728\u54ea\u91cc\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u5c31\u5728\u6211\u5de6\u80a9\u4e0a\u3002 \u9646\u5c0f\u51e4\u6478\u7d22\u7740\uff0c\u627e\u5230\u4ed6\u7684\u5de6\u80a9\uff0c\u6495\u5f00\u8863\u670d\uff0c\u6307\u5c16\u611f\u89c9\u5230\u4e00\u70b9\u80bf\u5757\uff0c\u5c31\u4f4e\u4e0b\u5934\uff0c\u5f20\u5f00 \u5634\uff0c\u7528\u529b\u5438\u542e\uff0c\u76f4\u5230\u5b64\u72ec\u7f8e\u53eb\u8d77\u6765\u624d\u505c\u6b62\u3002 \u201c\u4f60\u5df2\u89c9\u5f97\u75db\u4e86\uff1f\u201d \u201c\u55ef\u3002\u201d \u65e2\u7136\u5df2\u80fd\u611f\u89c9\u5230\u75bc\u75db\uff0c\u4f24\u53e3\u91cc\u7684\u6bd2\u663e\u7136\u5df2\u5168\u90e8\u88ab\u5438\u4e86\u51fa\u6765\u3002 \u9646\u5c0f\u51e4\u53c8\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u8fd8\u80fd\u7761\uff0c\u5c31\u7761\u4e00\u4e0b\uff0c\u7761\u4e0d\u7740\u5c31\u6328\u4e00\u4f1a\u513f\uff0c\u53cd\u6b63\u5929\u5df2 \u5feb\u4eae\u4e86\u3002 \u5b64\u72ec\u7f8e\u547b\u541f\u7740\uff0c\u826f\u4e45\u826f\u4e45\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u672c\u6765\u4e0d\u5fc5\u8fd9\u4e48\u505a\u7684\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u54e6\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u73b0\u5728\u4f60\u65e2\u7136\u5df2\u77e5\u9053\u51fa\u8def\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u629b\u4e0b\u6211\u4e00\u4e2a\u4eba\u8d70\uff1f\u201d \u9646\u5c0f\u51e4\u4e5f\u6c89\u9ed8\u4e86\u5f88\u4e45\u624d\u56de\u7b54\u3002\u201d\u4e5f\u8bb8\u53ea\u56e0\u4e3a\u4f60\u8fd8\u4f1a\u7b11\u3002\u201d \u5b64\u72ec\u7f8e\u4e0d\u61c2\u3002 \u9646\u5c0f\u51e4\u6162\u6162\u63a5\u7740\u9053\uff1a\u201c\u6211\u603b\u89c9\u5f97\uff0c\u4e00\u4e2a\u4eba\u53ea\u8981\u8fd8\u4f1a\u7b11\uff0c\u5c31\u4e0d\u80fd\u7b97\u662f\u516d\u4eb2\u4e0d\u8ba4\u7684 \u4eba\u3002\u201d \u5929\u4e00\u4eae\uff0c\u6307\u8def\u7684\u78f7\u5149\u5c31\u770b\u4e0d\u89c1\u4e86\u3002 \u73b0\u5728\u5929\u5df2\u5feb\u4eae\uff0c\u9646\u5c0f\u51e4\u603b\u7b97\u5df2\u4f11\u606f\u4e86\u7247\u523b\u3002 \u6709\u4e9b\u4eba\u7684\u7cbe\u529b\u5c31\u50cf\u662f\u8349\u539f\u4e2d\u7684\u91ce\u706b\u4e00\u6837\uff0c\u968f\u65f6\u90fd\u53ef\u80fd\u518d\u88ab\u71c3\u8d77 \u9646\u5c0f\u51e4\u5c31\u662f\u8fd9\u79cd\u4eba\u3002 \u4ed6\u8fd9\u4e00\u6b21\u91cd\u65b0\u71c3\u8d77\u7684\u7cbe\u529b\u8fd8\u6ca1\u6709\u71c3\u5c3d\uff0c\u5c31\u5ffd\u7136\u53d1\u73b0\u4ed6\u4eec\u7ec8\u4e8e\u5df2\u8131\u51fa\u4e86\u90a3\u5403\u4eba\u7684\u6811\u6797\uff01 \u524d\u9762\u662f\u4e00\u7247\u6625\u5929\uff0c\u65ed\u65e5\u521a\u521a\u4ece\u9752\u7fe0\u8fdc\u5c71\u5916\u5347\u8d77\uff0c\u5fae\u98ce\u4e2d\u5e26\u7740\u8fdc\u5c71\u65b0\u53d1\u6728\u53f6\u7684\u82ac\u82b3\uff0c \u9732\u73e0\u5728\u9633\u5149\u4e0b\u95ea\u4eae\u5f97\u5c31\u50cf\u662f\u521d\u604b\u60c5\u4eba\u7684\u773c\u775b\u3002 \u9646\u5c0f\u51e4\u63c9\u4e86\u63c9\u81ea\u5df1\u7684\u773c\u775b\uff0c\u51e0\u4e4e\u4e0d\u6562\u60f3\u4fe1\u8fd9\u662f\u771f\u7684\uff0c\u8fd9\u7b80\u76f4\u662f\u5947\u8ff9\uff0c\u7b80\u76f4\u5c31\u50cf\u662f\u68a6 \u5883\u3002 \u96be\u9053\u4ed6\u521a\u4ece\u76df\u68a6\u4e2d\u9192\u6765\uff0c\u5c31\u5230\u4e86\u53e6\u4e00\u4e2a\u68a6\u5883\u4e2d\u3002 \u4f0f\u5728\u4ed6\u80cc\u4e0a\u7684\u5b64\u72ec\u7f8e\uff0c\u547c\u5438\u4e5f\u53d8\u5f97\u6025\u4fc3\u4e86\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u524d\u9762\u662f\u4e0d\u662f\u6709\u68f5\u5927\u677e \u6811\uff1f\u201d \u662f\u7684\u3002 \u4e00\u68f5\u53e4\u677e\uff0c\u5b64\u96f6\u96f6\u7684\u77d7\u7acb\u5728\u524d\u9762\u7684\u5ca9\u77f3\u95f4\uff0c\u8fdc\u79bb\u7740\u8fd9\u7247\u83bd\u5bc6\u7684\u4e1b\u6797\uff0c\u5c31\u597d\u50cf\u4e0d\u5c51\u4e0e \u8fd9\u4e9b\u4fd7\u6728\u4e3a\u4f0d\u3002 \u201c\u677e\u6811\u4e0b\u662f\u4e0d\u662f\u6709\u5757\u5927\u77f3\u5757\uff1f\u201d \u662f\u7684\u3002 \u662f\u4e2a\u5927\u5982\u684c\u9762\u7684\u9752\u77f3\uff0c\u77f3\u8d28\u7eaf\u7f8e\uff0c\u67d4\u6da6\u5982\u7389\u3002 \u9646\u5c0f\u51e4\u8d70\u8fc7\u53bb\uff0c\u5728\u77f3\u4e0a\u5750\u4e0b\uff0c\u653e\u4e0b\u4e86\u4ed6\u80cc\u8d1f\u7740\u7684\u4eba\uff0c\u624d\u957f\u957f\u5410\u51fa\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4eec\u603b \u7b97\u51fa\u6765\u4e86\u3002\u201d \u5b64\u72ec\u7f8e\u5598\u606f\u7740\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u8fd9\u91cc\u8fd8\u4e0d\u80fd\u7b97\u662f\u5b89\u5168\u7684\u5730\u5200\uff0c\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u603b\u7b97\u8fd8 \u6ca1\u6709\u88ab\u90a3\u5403\u4eba\u7684\u6811\u6797\u5b50\u5403\u4e0b\u53bb\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u8fd8\u662f\u968f\u65f6\u90fd\u53ef\u80fd\u6b7b\u5728\u897f\u95e8\u5439\u96ea\u5251\u4e0b\u2019\u2019 \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u8bf4\u4e24\u53e5\u8ba9\u4eba\u542c\u4e86\u6bd4\u8f83\u9ad8\u5174\u7684\u8bdd\uff1f\u201d\u5b64\u72ec\u7f8e\u7b11 \u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u60f3\u544a\u8bc9\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d \u9646\u5c0f\u51e4\u5728\u542c\u7740\u3002 \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u8fd9\u4e16\u4e0a\u672c\u6765\u5df2\u6ca1\u6709\u4eba\u80fd\u6551\u5f97\u4e86\u4f60\uff0c\u4f46\u4f60\u5374\u81ea\u5df1\u6551\u4e86\u81ea\u5df1ao \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u54e6\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4f60\u521a\u624d\u6551\u6211\u7684\u65f6\u5019\uff0c\u4e5f\u540c\u65f6\u6551\u4e86\u4f60\u81ea\u5df1\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u672c\u6765\u5e76\u4e0d\u662f\u771f\u7684\u60f3\u5e26\u6211\u5230\u90a3\u5c71\u5e84\u53bb\u7684\uff1f\u201d \u5b64\u72ec\u7f8e\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u53ef\u662f\u6211\u73b0\u5728\u5df2\u6539\u53d8\u5382\u4e3b\u610f\uff0c\u56e0\u4e3a\u6211\u5c31\u7b97\u662f\u516d\u4eb2\u4e0d\u8ba4\u7684\u4eba\uff0c\u603b \u7b97\u8fd8\u662f\u4e2a\u4eba\u3002\u201d \u4ed6\u51dd\u89c6\u7740\u9646\u5c0f\u51e4\uff0c\u72e1\u9ad3\u950b\u5229\u7684\u76ee\u5149\u5ffd\u7136\u53d8\u5f97\u5f88\u67d4\u548c\u3002\u201d\u4f60\u5728\u90a3\u79cd\u60c5\u51b5\u4e0b\u90fd\u6ca1\u6709\u7529\u535c \u6211\uff0c\u73b0\u5728\u6211\u5f53\u7136\u4e5f\u4e0d\u80fd\u7529\u4e0b\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\u3002 \u4eba\u603b\u6709\u4eba\u6027\uff0c\u4eba\u6027\u4e2d\u603b\u6709\u5584\u826f\u7684\u4e00\u9762\uff0c\u5bf9\u8fd9\u4e00\u70b9\u4ed6\u6c38\u8fdc\u90fd\u5145\u6ee1\u4fe1\u5fc3\u3002 \u6811\u6839\u4e0b\u8fd8\u6709\u5757\u6bd4\u8f83\u5c0f\u7684\u9752\u77f3\uff0c\u5b64\u72ec\u7f8e\u53c8\u9053\uff1a\u201c\u53bb\u642c\u5f00\u90a3\u5757\u77f3\u5934\u770b\u770b\uff0c\u4e0b\u9762\u662f\u4e0d\u662f\u6709 \u53e3\u7bb1\u5b50\u3002\u201d \u662f\u7684\u3002 \u85e4\u6761\u7f16\u6210\u7684\u7bb1\u5b50\uff0c\u91cc\u9762\u6709\u4e00\u5757\u719f\u8089\uff0c\u4e00\u53ea\u98ce\u9e21\uff0c\u4e00\u74f6\u9152\uff0c\u4e00\u5305\u5200\u4f24\u836f\uff0c\u8fd8\u6709\u4e00\u53ea\u54e8 \u5b50\u548c\u4e00\u5c01\u4fe1\u3002 \u54e8\u5b50\u7684\u5f62\u5f0f\u5f88\u5947\u7279\uff0c\u4fe1\u7eb8\u548c\u4fe1\u5c01\u7684\u989c\u8272\u4e5f\u5f88\u5947\u7279\uff0c\u770b\u6765\u5c31\u50cf\u662f\u6b7b\u4eba\u7684\u76ae\u80a4\u3002\u4fe1\u4e0a\u53ea \u5199\u7740\u5341\u4e2a\u5b87\u3002\u201d\u5439\u54e8\u5b50\uff0c\u542c\u56de\u58f0\uff0c\u5faa\u58f0\u800c\u884c\u3002\u201d \u9646\u5c0f\u51e4\u559d\u4e86\u53e3\u9152\u3002\u201d\u597d\u9152\u3002\u201d \u4ed6\u6ee1\u610f\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4e9b\u4eba\u60f3\u5f97\u5b9e\u5728\u5468\u5230\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4ed6\u4eec\u505a\u4e8b\u4e0d\u4f46\u8ba1\u5212\u5468\u5bc6\uff0c\u800c\u4e14\u4fe1\u8a89\u5353\u8457\uff0c\u4f60\u53ea\u8981\u8ddf\u4ed6\u4eec\u6709\u4e86\u5408\u7ea6\uff0c\u4ed6 \u4eec\u5c31\u4e00\u5b9a\u4f1a\u8d1f\u8d23\u9001\u4f60\u5230\u5c71\u5e84\u53bb\u3002\u201d \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u5408\u7ea6\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u6551\u547d\u5408\u7ea6\u3002\u201d \u8fd9\u4e00\u6b21\u4ed6\u5c45\u7136\u6ca1\u6709\u9003\u907f\u9646\u5c0f\u51e4\u7684\u95ee\u9898\uff0c\u6240\u4ee5\u9646\u5c0f\u51e4\u7acb\u523b\u53c8\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u5c71\u5e84\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u5e7d\u7075\u5c71\u5e84\u3002\u201d \u5e7d\u7075\u5c71\u5e84\u300f \u90a3\u5730\u65b9\u53ea\u6709\u6b7b\u4eba\u624d\u80fd\u53bb\u300f \u9646\u5c0f\u51e4\u53ea\u89c9\u5f97\u638c\u5fc3\u51b7\u51b7\u7684\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u96be\u9053\u90a3\u5730\u65b9\u5168\u662f\u6b7b\u4eba\u7684\u5e7d\u7075\uff1f\u201d \u5b64\u72ec\u7f8e\u7b11\u4e86\u7b11\uff0c\u7b11\u5f97\u5f88\u795e\u79d8\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u90a3\u5730\u65b9\u5168\u90fd\u662f\u6b7b\u4eba\u7684\u5e7d\u7075\uff0c\u6240\u4ee5\u6ca1 \u6709\u4e00\u4e2a\u6d3b\u4eba\u80fd\u627e\u5f97\u5230\uff0c\u66f4\u6ca1\u6709\u4e00\u4e2a\u6d3b\u4eba\u6562\u95ef\u8fdb\u53bb\u300f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u5b64\u72ec\u7f8e\u7b11\u5f97\u66f4\u795e\u79d8\uff0c\u60a0\u7136\u9053\uff1a\u201c\u6211\u65e2\u7136\u5df2\u8d70\u4e0a\u6b7b\u8def\uff0c\u5f53\u7136\u975e\u6b7b\u4e0d\u53ef\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u65e2\u7136\u5df2\u975e\u6b7b\u4e0d\u53ef\uff0c\u5f53\u7136\u5c31\u5df2\u662f\u4e2a\u6b7b\u4eba\u300f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u73b0\u5728\u4f60\u603b\u7b97\u660e\u767d\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u660e\u767d\uff0c\u4e00\u70b9\u4e5f\u4e0d\u660e\u767d\u3002\u201d \u54e8\u5b50\u5c31\u5728\u4ed6\u624b\u91cc\u2606 \u4ed6\u5fcd\u4e0d\u4f4f\u62ff\u8d77\u6765\uff0c\u8f7b\u8f7b\u5439\u4e86\u5439\uff0c\u5c16\u9510\u5947\u7279\u7684\u54e8\u58f0\u7a81\u7136\u54cd\u8d77\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u5413\u4e86\u4e00\u8df3\u3002 \u5c31\u5728\u8fd9\u65f6\uff0c\u8fdc\u5916\u5df2\u6709\u540c\u6837\u7684\u4e00\u58f0\u54e8\u5b50\u4f20\u4e86\u8fc7\u6765\uff0c\u65b9\u5411\u5728\u6b63\u897f\u300f \u7a7a\u5c71\u5bc2\u5bc2\uff0c\u8981\u5206\u8fa8\u54e8\u5b50\u7684\u58f0\u97f3\u5e76\u4e0d\u56f0\u96be\u3002 \u4ed6\u4eec\u5faa\u58f0\u800c\u884c\uff0c\u6e10\u884c\u6e10\u9ad8\uff0c\u56db\u9762\u767d\u53bb\u98d8\u6e3a\uff0c\u4ed6\u4eec\u7684\u4eba\u5df2\u5728\u767d\u4e91\u4e2d\u3002 \u559d\u4e86\u5927\u534a\u74f6\u9152\uff0c\u5403\u4e86\u534a\u53ea\u9e21\uff0c\u9646\u5c0f\u51e4\u53ea\u89c9\u5f97\u65e0\u8bba\u591a\u8fdc\u7684\u8def\u90fd\u53ef\u4ee5\u8d70\u4e0b\u53bb\u3002 \u5b64\u72ec\u7f8e\u7684\u60c5\u51b5\u5374\u8d8a\u6765\u8d8a\u7cdf\u4e86\uff0c\u8fde\u9646\u5c0f\u51e4\u90fd\u5df2\u55c5\u5230\u4ed6\u4f24\u53e3\u91cc\u53d1\u51fa\u7684\u6076\u81ed\u3002 \u53ef\u662f\u9646\u5c0f\u51e4\u4e00\u70b9\u4e5f\u4e0d\u5728\u4e4e\u3002 \u201c\u897f\u95e8\u5439\u96ea\u5f53\u7136\u4e0d\u662f\u4e2a\u804b\u5b50\u3002\u201d \u201c\u5f53\u7136\u4e0d\u662f\u3002\u201d \u201c\u4ed6\u5f53\u7136\u4e5f\u80fd\u542c\u5f97\u89c1\u54e8\u5b50\u7684\u58f0\u97f3\u4e8c\u201d \u201c\u55ef\u3002\u201d \u201c\u6240\u4ee5\u4ed6\u968f\u65f6\u90fd\u53ef\u80fd\u8ffd\u4e0a\u6765\u3002\u201d \u201c\u53ef\u80fd\u3002\u201d \u201c\u73b0\u5728\u4f60\u65e2\u7136\u5df2\u77e5\u9053\u4e86\u5165\u5c71\u7684\u6cd5\u5b50\uff0c\u8fd8\u662f\u653e\u4e0b\u6211\u7684\u597d\u3002\u201d\u5b64\u72ec\u7f8e\u7684\u8138\u53c8\u5df2\u56e0\u75db\u82e6\u800c \u626d\u66f2\u3002\u201d\u4f60\u4e00\u4e2a\u4eba\u603b\u6bd4\u8f83\u8d70\u5f97\u5feb\u4e9b\uff0c\u4f55\u51b5\uff0c\u6211\u7684\u4eba\u5df2\u4e0d\u884c\u4e86\uff0c\u5c31\u7b97\u5230\u4e86\u90a3\u91cc\uff0c\u4e5f\u672a\u5fc5\u80fd \u6d3b\u591a\u4e45\u3002\u201d \u4ed6\u8bf4\u7684\u662f\u771f\u5fc3\u8bdd\uff0c\u4f46\u9646\u5c0f\u51e4\u5374\u597d\u50cf\u8fde\u4e00\u4e2a\u5b87\u90fd\u6ca1\u6709\u542c\u89c1\u3002 \u4ed6\u8d70\u5f97\u66f4\u5feb\uff0c\u767d\u4e91\u5ffd\u7136\u5df2\u5230\u4e86\u4ed6\u7684\u811a\u4e0b\uff0c\u4ed6\u7684\u773c\u775b\u8c41\u7136\u5f00\u6717\u3002 \u524d\u9762\u9752\u5929\u5982\u6d17\uff0c\u8fdc\u5c71\u5982\u753b\u3002 \u9646\u5c0f\u51e4\u7684\u5fc3\u5374\u6c89\u4e86\u4e0b\u53bb\uff0c\u6c89\u5f97\u5f88\u6df1\u3002 \u4ed6\u524d\u9762\u7adf\u662f\u4e00\u9053\u6df1\u4e0d\u89c1\u5e95\u7684\u4e07\u4e08\u6df1\u6e0a\uff0c\u90a3\u56fe\u753b\u822c\u7684\u8fdc\u5c71\u867d\u7136\u5c31\u5728\u773c\u524d\uff0c\u5374\u5df2\u65e0\u8def\u53ef \u8d70\u3002 \u4ed6\u6361\u8d77\u4e00\u5757\u77f3\u5934\u629b\u4e0b\u53bb\uff0c\u7adf\u8fde\u4e00\u70b9\u56de\u58f0\u90fd\u542c\u4e0d\u89c1\u3002 \u4e0b\u9762\u767d\u4e91\u7ee6\u7ed5\uff0c\u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\uff0c\u5c31\u8fde\u6b7b\u4eba\u7684\u5e7d\u7075\u90fd\u770b\u4e0d\u89c1\u3002 \u96be\u9053\u90a3\u5e7d\u7075\u5c71\u5e84\u5c31\u5728\u4e07\u4e08\u6df1\u58d1\u4e0b\uff1f \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u8981\u5230\u5e7d\u7075\u5c71\u5e84\u53bb\uff0c\u770b\u6765\u4e5f\u5e76\u4e0d\u662f\u4ec0\u4e48\u56f0\u96be\u7684\u4e8b\uff0c\u4f60\u53ea\u8981\u5f80\u4e0b\u9762\u4e00 \u8df3\uff0c\u4fdd\u8bc1\u7acb\u523b\u5c31\u4f1a\u53d8\u6210\u4e2a\u6b7b\u4eba\u3002\u201d \u5b64\u72ec\u7f8e\u5598\u606f\u7740\uff0c\u9053\uff1a\u201c\u4f60\u518d\u5439\u4e00\u58f0\u54e8\u5b50\u8bd5\u8bd5\u770b\uff1f\u201d \u5c16\u9510\u7684\u54e8\u58f0\uff0c\u5212\u7834\u6c89\u5bc2\uff0c\u4e5f\u5212\u7834\u4e86\u81ea\u4e91\u3002 \u767d\u4e91\u95f4\u5ffd\u7136\u51fa\u73b0\u4e00\u4e2a\u4eba\u3002 \u9752\u5929\u4e0a\u6709\u767d\u4e91\uff0c\u7edd\u58d1\u4e0b\u4e5f\u6709\u767d\u4e91\uff0c\u8fd9\u4e2a\u4eba\u5c31\u5728\u767d\u4e91\u95f4\uff0c\u5c31\u50cf\u662f\u51cc\u7a7a\u7ad9\u5728\u90a3\u91cc\u7684\u3002 \u4ec0\u4e48\u4eba\u80fd\u51cc\u7a7a\u7ad9\u5728\u767d\u4e91\u91cc\uff1f \u6b7b\u4eba\uff1f\u6b7b\u4eba\u7684\u5e7d\u7075\uff1f \u9646\u5c0f\u51e4\u5410\u51fa\u53e3\u6c14\uff0c\u5ffd\u7136\u53d1\u73b0\u8fd9\u4e2a\u4eba\u5728\u79fb\u52a8\uff0c\u79fb\u52a8\u5f97\u5f88\u5feb\uff0c\u53c8\u50cf\u662f\u5fa1\u98ce\u800c\u884c\uff0c\u8f6c\u773c\u95f4 \u5c31\u53ef\u4ee5\u5206\u8fa8\u51fa\u4ed6\u8863\u670d\u7684\u989c\u8272\uff0c\u4e5f\u5e94\u8be5\u53ef\u4ee5\u5206\u8fa8\u51fa\u4ed6\u9762\u76ee\u7684\u8f6e\u5ed3\u3002 \u53ef\u662f\u4ed6\u6839\u672c\u5c31\u6ca1\u6709\u9762\u76ee\u8f6e\u5ed3\uff0c\u4ed6\u7684\u8138\u8d6b\u7136\u5df2\u88ab\u4eba\u4e00\u5200\u524a\u5e73\u4e86\u300f \u6ca1\u6709\u4eb2\u773c\u89c1\u8fc7\u4ed6\u7684\u4eba\uff0c\u7edd\u65e0\u6cd5\u60f3\u50cf\u90a3\u662f\u5f20\u4ec0\u4e48\u6837\u7684\u8138\u3002 \u9646\u5c0f\u51e4\u7684\u80c6\u5b50\u5e76\u4e0d\u5c0f\uff0c\u53ef\u662f\u4ed6\u770b\u89c1\u8fd9\u5f20\u8138\u65f6\uff0c\u8fde\u817f\u90fd\u8f6f\u4e86\uff0c\u51e0\u4e4e\u4e00\u72e1\u8dcc\u4e0b\u4e07\u4e08\u7edd\u58d1 \u4e2d\u53bb\u3002 \u4ed6\u53ef\u4ee5\u611f\u89c9\u5230\u80cc\u4e0a\u7684\u5b64\u72ec\u7f8e\u4e5f\u5728\u53d1\u6296\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u8fd9\u4e2a\u4eba\u5df2\u6765\u5230\u4ed6\u4eec\u9762\u524d\uff0c\u6765\u5f97\u597d \u5feb\u3002 \u867d\u7136\u5df2\u63a0\u4e0a\u5c71\u5d16\uff0c\u8fd9\u4e2a\u4eba\u8eab\u5b50\u79fb\u52a8\u65f6\u770b\u6765\u8fd8\u662f\u8f7b\u98d8\u98d8\u7684\uff0c\u811a\u5e95\u8ddd\u79bb\u5730\u9762\u81f3\u5c11\u6709\u534a \u5c3a\u3002 \u9646\u5c0f\u51e4\u4e00\u5411\u8ba4\u6c5f\u6e56\u4e2d\u8f7b\u529f\u6700\u9ad8\u7684\u4e09\u4e2a\u4eba\u662f\u53f8\u7a7a\u6458\u661f\u3001\u897f\u95e8\u5439\u96ea\u548c\u4ed6\u81ea\u5df1\u3002 \u73b0\u5728\u4ed6\u624d\u77e5\u9053\u81ea\u5df1\u9519\u4e86\u3002 \u8fd9\u4e2a\u4eba\u8f7b\u529f\u8eab\u6cd5\u602a\u5f02\uff0c\u5c31\u548c\u4ed6\u7684\u8138\u4e00\u6837\uff0c\u9664\u975e\u4f60\u4eb2\u773c\u770b\u89c1\uff0c\u5426\u5219\u7b80\u76f4\u65e0\u6cd5\u601d\u8bae\u3002 \u73b0\u5728\u4ed6\u6b63\u5728\u76ef\u7740\u9646\u5c0f\u51e4\uff0c\u4e00\u53cc\u773c\u775b\u770b\u6765\u5c31\u50cf\u662f\u521a\u521a\u8fd8\u55b7\u51fa\u8fc7\u6eb6\u5ca9\u7684\u706b\u5c71\u53e3\uff0c\u707c\u70ed\u800c \u5371\u9669\u3002 \u9762\u5bf9\u7740\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\uff0c\u9646\u5c0f\u51e4\u5b9e\u5728\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\u3002 \u5b64\u72ec\u7f8e\u5374\u5ffd\u7136\u95ee\u3002\u201d\u4f60\u5c31\u662f\u5e7d\u7075\u5c71\u5e84\u7684\u52fe\u9b42\u4f7f\u8005\uff1f\u201d \u4ed6\u770b\u89c1\u8fd9\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u7acb\u523b\u63a5\u7740\u9053\uff1a\u201c\u6211\u53eb\u5b64\u72ec\u7f8e\uff0c\u6211\u7684\u9b42\u5df2\u6765\u4e86\u3002\u201d\u8fd9\u4e2a\u4eba\u7ec8\u4e8e \u5f00\u53e3\u3002\u201d\u6211\u77e5\u9053\uff0c\u6211\u77e5\u9053\u4f60\u4f1a\u6765\u7684\u3002\u201d \u4ed6\u8bf4\u8bdd\u7684\u58f0\u97f3\u7f13\u6162\u3001\u602a\u5f02\u800c\u8270\u6da9\uff0c\u56e0\u4e3a\u4ed6\u6ca1\u6709\u5634\u5507\u3002 \u6ca1\u6709\u770b\u89c1\u8fc7\u4ed6\u7684\u4eba\uff0c\u4e5f\u6c38\u8fdc\u65e0\u6cd5\u60f3\u50cf\u4e00\u4e2a\u6ca1\u6709\u5634\u5507\u7684\u4eba\u8bf4\u8bdd\u662f\u4ec0\u4e48\u6837\u5b50\u7684\u3002 \u5b64\u72ec\u7f8e\u8fde\u770b\u90fd\u4e0d\u6562\u770b\u4ed6\u4e00\u773c\uff0c\u4ed6\u751f\u6015\u81ea\u5df1\u4f1a\u5fcd\u4e0d\u4f4f\u5455\u9669\u3002 \u8fd9\u4e2a\u52fe\u9b42\u5e26\u8def\u7684\u4eba\u7a81\u53c8\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4e0d\u6562\u770b\u6211\uff1f\u662f\u4e0d\u662f\u56e0\u4e3a\u6211\u592a\u4e11\uff1f\u201d \u5b64\u72ec\u7f8e\u7acb\u523b\u5426\u8ba4\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u662f\u2026\u2026\u201d\u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u65e2\u7136\u4e0d\u662f\uff0c\u5c31\u770b\u7740\u6211 \u8bf4\u8bdd\uff0c\u770b\u7740\u6211\u7684\u8138ao \u5b64\u72ec\u7f8e\u53ea\u597d\u770b\u7740\u4ed6\u7684\u8138\uff0c\u5374\u6ca1\u6709\u5f00\u53e3\uff0c\u56e0\u4e3a\u4ed6\u7684\u5589\u5499\u548c\u80c3\u90fd\u5df2\u56e0\u6050\u60e7\u800c\u6536\u7f29\uff0c\u8fde\u58f0 \u97f3\u90fd\u53d1\u4e0d\u51fa\u3002 \u52fe\u9b42\u4f7f\u8005\u5374\u7b11\u4e86\uff0c\u4ed6\u597d\u50cf\u5f88\u559c\u6b22\u770b\u5230\u522b\u4eba\u5bb3\u6015\u96be\u53d7\u7684\u6837\u5b50\uff0c\u559c\u6b22\u522b\u4eba\u6015\u4ed6\u3002 \u53ef\u662f\u4ed6\u7684\u7b11\u58f0\u5f88\u5feb\u7ed3\u675f\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u672c\u8be5\u4e00\u4e2a\u4eba\u6765\u7684\uff0c\u73b0\u5728\u4e3a\u4ec0\u4e48\u6709\u4e24\u4e2a\uff1f\u201d \u5b64\u72ec\u7f8e\u8fd8\u662f\u4e0d\u80fd\u5f00\u53e3\uff0c\u8fd9\u95ee\u9898\u4ed6\u4e5f\u56de\u7b54\u4e0d\u51fa\u3002 \u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u4f60\u7559\u4e0b\uff0c\u4ed6\u8d70\u300f\u201d\u5b64\u72ec\u7f8e\u5ffd\u7136\u9f13\u6c14\u52c7\u6c14\uff0c\u9053\uff1a\u201c\u4ed6\u4e5f\u4e0d\u8d70ao\u52fe\u9b42 \u4f7f\u8005\u9053\uff1a\u201c\u4ed6\u4e0d\u8d70\uff0c\u4f60\u8d70qo \u5b64\u72ec\u7f8e\u5927\u58f0\u6297\u8bae\uff0c\u9053\uff1a\u201c\u6211\u6709\u5408\u7ea6\uff0c\u662f\u4f60\u4eec\u81ea\u5df1\u8ba2\u7684\u5408\u7ea6\u3002\u201d\u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u4f60 \u6709\uff0c\u4ed6\u6ca1\u6709\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4ed6\u662f\u6211\u670b\u53cb\uff0c\u4ed6\u7684\u5408\u7ea6\u91d1\uff0c\u6211\u53ef\u4ee5\u66ff\u4ed6\u4ed8\u3002\u201d \u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u73b0\u5728\u5c31\u4ed8\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u968f\u65f6\u90fd\u53ef\u4ee5\u4ed8\uff0c\u6211\u8eab\u4e0a\u5e26\u7740\u6709\u2026\u2026\u201d \u52fe\u9b42\u4f7f\u8005\u7a81\u53c8\u6253\u65ad\u4ed6\u7684\u8bdd\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5c31\u7b97\u73b0\u5728\u4ed8\uff0c\u4e5f\u5df2\u592a\u8fdf\u4e86oo \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u8bf4\u7684\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u65e2\u7136\u6765\u5230\u8fd9\u91cc\uff0c\u5c31\u7edd\u4e0d\u80fd\u518d\u6d3b\u7740\u56de\u52fe\u9b42\u4f7f\u8005\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u82e5\u60f3 \u6551\u4ed6\uff0c\u4f60\u5c31\u81ea\u5df1\u8d70\uff0c\u7559\u4e0b\u4ed6aU \u4ed6\u6ca1\u6709\u5634\u5507\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u5c31\u50cf\u662f\u6765\u81ea\u5730\u72f1\uff0c\u5df2\u7ecf\u88ab\u9b54\u706b\u70bc\u8fc7\uff0c\u7edd\u65e0\u66f4\u6539\u3002 \u9646\u5c0f\u51e4\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u6211\u8d70\u3002\u201d \u4ed6\u8f7b\u8f7b\u7684\u653e\u4e0b\u5b64\u72ec\u7f8e\uff0c\u62cd\u4e86\u62cd\u8eab\u4e0a\u7684\u8863\u670d\uff0c\u5c45\u7136\u771f\u7684\u8bf4\u8d70\u5c31\u8d70\u3002 \u5b64\u72ec\u7f8e\u5598\u606f\u7740\uff0c\u5ffd\u7136\u4e00\u628a\u62c9\u7740\u4ed6\u8863\u89d2\uff0c\u9053\uff1a\u201c\u4f60\u7559\u4e0b\uff0c\u6211\u4e8c\u58eb\u4e8c\uff0c\uff0c\u4eba\u4e0a\u4e8c\u3002 \u9646\u5c0f\u51e4\u7b11\u4e86\u7b11\u3002\u201d\u4f60\u7528\u4e0d\u7740\u62c5\u5fc3\uff0c\u6211\u65e2\u7136\u80fd\u6d3b\u7740\u6765\u5230\u8fd9\u91cc\uff0c\u5c31\u4e00\u5b9a\u6709\u6cd5\u5b50\u6d3b\u7740\u56de \u53bb\u3002\u201d \u5b64\u72ec\u7f8e\u5c45\u7136\u4e5f\u7b11\u4e86\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u6ca1\u6709\u628a\u6b7b\u6d3b\u653e\u5728\u5fc3\u4e0a\uff0c\u6211\u5374\u5f88\u6015\u6b7b\u2026\u2026\u201d \u9646\u5c0f\u51e4\u62a2\u7740\u66ff\u4ed6\u63a5\u4e86\u4e0b\u53bb\u3002\u201d\u53ef\u662f\u4f60\u73b0\u5728\u5df2\u7ecf\u4e0d\u6015\u4e86\u3002\u201d \u5b64\u72ec\u7f8e\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u2026\u2026\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u53cd\u6b63\u4e5f\u6d3b\u4e0d\u957f\u7684\uff0c\u4e0d\u5982\u628a\u673a\u4f1a\u8ba9\u7ed9\u6211\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u8fd9\u662f\u552f\u4e00\u7684\u673a\u4f1a\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8fd9\u4e9b\u8bdd\u6211\u65e9\u5c31\u542c\u4f60\u8bf4\u8fc7\uff0c\u4f60\u7684\u610f\u601d\u6211\u4e5f\u5f88\u660e\u767d\uff0c\u53ea\u4e0d\u8fc7\u2026\u2026\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4f60\u8fd8\u662f\u4e0d\u80af\uff1f\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u80fd\u591f\u8ddf\u4e00\u4e2a\u516d\u4eb2\u4e0d\u8ba4\u7684\u4eba\u4ea4\u4e0a\u670b\u53cb\uff0c\u6211\u5df2\u7ecf\u5f88\u6ee1\u610f\u4e86\uff0c\u53ea\u53ef \u60dc\u6211\u4e00\u5411\u6ca1\u6709\u8981\u670b\u53cb\u66ff\u6211\u6b7b\u7684\u4e60\u60ef\u3002\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u8d70\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u8d70\u5f97\u4e00\u5b9a\u6bd4\u4f60\u5feb\u3002\u201d \u52fe\u9b42\u4f7f\u8005\u51b7\u51b7\u7684\u770b\u7740\u4ed6\u4eec\uff0c\u773c\u775b\u91cc\u5e26\u7740\u79cd\u8bf4\u4e0d\u51fa\u7684\u618e\u6076\u3002 \u4ed6\u618e\u6076\u53cb\u60c5\uff0c\u618e\u6076\u4e16\u4e0a\u6240\u6709\u7f8e\u597d\u7684\u4e8b\uff0c\u5c31\u50cf\u662f\u8759\u8760\u618e\u6076\u9633\u5149\u3002 \u5ffd\u7136\u95f4\uff0c\u8fdc\u5904\u6709\u4eba\u5728\u547c\u5524\u3002\u201d\u5e26\u4ed6\u4eec\u8fdb\u6765\uff0c\u4e24\u4e2a\u4eba\u5168\u90fd\u5e26\u8fdb\u6765oo \u6e05\u8106\u7684\u58f0\u97f3\uff0c\u6765\u81ea\u767d\u4e91\u95f4\uff0c\u767d\u4e91\u95f4\u5ffd\u7136\u53c8\u51fa\u73b0\u4e86\u4e00\u6761\u6de1\u7ea2\u8272\u7684\u4eba\u5f71\uff0c\u4eff\u4f5b\u4e5f\u662f\u51cc\u7a7a \u7ad9\u5728\u90a3\u91cc\u7684\uff0c\u6b63\u5728\u5411\u8fd9\u8fb9\u6325\u624b\u3002 \u201c\u8c01\u8bf4\u8981\u5c06\u4ed6\u4eec\u5168\u90fd\u5e26\u8fdb\u53bb\uff1f\u201d\u201c\u8001\u5200\u628a\u5b50\u3002\u201d \u8fd9\u56db\u4e2a\u5b57\u7adf\u50cf\u662f\u5229\u7b26\u5492\uff0c\u5ffd\u7136\u95f4\u5c31\u5c06\u9646\u5c0f\u51e4\u5e26\u4eba\u4e86\u522b\u2014\u4e2a\u5929\u5730\u3002 \u6ca1\u6709\u4eba\u80fd\u51cc\u7a7a\u7ad9\u5728\u767d\u4e91\u95f4\uff0c\u4e5f\u6ca1\u6709\u4eba\u80fd\u771f\u7684\u5fa1\u98ce\u800c\u884c\u3002 \u52fe\u9b42\u4f7f\u8005\u4e5f\u662f\u4eba\uff0c\u5e76\u4e0d\u662f\u865a\u65e0\u7684\u9b3c\u9b42\uff0c\u4ed6\u662f\u600e\u4e48\u6765\u7684\uff1f \u9646\u5c0f\u51e4\u8d70\u8fc7\u53bb\u4e4b\u540e\uff0c\u624d\u770b\u51fa\u767d\u4e91\u91cc\u6709\u6761\u5f88\u7c97\u7684\u94a2\u7d22\uff0c\u6a2a\u8d2f\u4e86\u4e24\u65c1\u7684\u5c71\u5d16\u3002 \u8fd9\u5c31\u662f\u4ed6\u4eec\u7684\u6865\u3002 \u4ece\u5c18\u4e16\u901a\u5411\u5e7d\u7075\u4e4b\u95e8\u7684\u6865\u3002 \u5c71\u5d16\u8fd9\u8fb9\u3002\u6709\u4e2a\u5f88\u5927\u7684\u7af9\u7bee\uff0c\u7528\u6ed1\u8f6e\u94c1\u94a9\u6302\u5728\u94a2\u7d22\u4e0a\u3002 \u8fd9\u8fb9\u7684\u5c71\u5d16\u6bd4\u8f83\u9ad8\uff0c\u89e3\u5f00\u4e00\u6761\u7ef3\u5b50\uff0c\u7af9\u7bee\u5c31\u4f1a\u5411\u5bf9\u9762\u6ed1\u8fc7\u53bb\u3002 \u5b64\u72ec\u7f8e\u5df2\u7ecf\u5728\u7af9\u7bee\u91cc\u3002 \u52fe\u9b42\u4f7f\u8005\u51b7\u51b7\u7684\u7785\u7740\u9646\u5c0f\u51e4\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u4e5f\u60f3\u5750\u8fdb\u53bb\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u6709\u817f\u3002\u201d \u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u82e5\u662f\u4e00\u7834\u8dcc\u4e0b\u53bb\uff0c\u5c31\u6ca1\u6709\u817f\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u770b\u5f97\u51fa\u3002 \u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u975e\u4f46\u6ca1\u6709\u9b42\uff0c\u8fde\u5c38\u9aa8\u90fd\u6ca1\u6709\uff0c\u4e00\u8dcc\u4e0b\u53bb\uff0c\u4eba\u5c31\u53d8\u6210\u4e86\u8089\u9171\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u60f3\u5f97\u5230\u3002\u201d \u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u8fd9\u6761\u94a2\u7d22\u5f88\u6ed1\uff0c\u5c71\u91cc\u7684\u98ce\u5f88\u5927\uff0c\u65e0\u8bba\u8f7b\u529f\u591a\u4e48\u597d\u7684\u4eba\uff0c\u8d70\u5728\u4e0a\u9762\uff0c \u90fd\u53ef\u80fd\u8dcc\u4e0b\u53bb\u3002\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8dcc\u4e0b\u53bb\u8fc7\uff1f\u201d\u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u559c\u6b22\u6211\uff1f\u201d \u52fe\u9b42\u4f7f\u8005\u51b7\u7b11\u3002 \u9646\u5c0f\u51e4\u6de1\u6de1\u9053\uff1a\u201c\u65e2\u7136\u4f60\u6ca1\u6709\u8dcc\u4e0b\u53bb\u8fc7\uff0c\u53c8\u600e\u4e48\u4f1a\u77e5\u9053\u6211\u4f1a\u8dcc\u4e0b\u53bb\uff0c\u65e2\u7136\u4f60\u5e76\u4e0d\u559c \u6b22\u6211\uff0c\u53c8\u4f55\u5fc5\u5173\u5fc3\u6211\u7684\u6b7b\u6d3b\uff1f\u201d \u52fe\u9b42\u4f7f\u8005\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5148\u8d70\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u8981\u5728\u540e\u9762\u7b49\u7740\u770b\u6211\u8dcc\u4e0b\u53bb\uff1f\u201d\u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u8fd9\u79cd\u673a\u4f1a\u5f88\u591a\uff0c\u6211\u4e00 \u5411\u4e0d\u613f\u9519\u8fc7\u3002\u201d \u9646\u5c0f\u51e4\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u4e00\u6b21\u6211\u4fdd\u8bc1\u4f60\u4e00\u5b9a\u4f1a\u5931\u671b\u7684\u3002\u201d \u94a2\u7d22\u679c\u7136\u5f88\u6ed1\uff0c\u5c71\u98ce\u679c\u7136\u5f88\u5927\uff0c\u4eba\u8d70\u5728\u4e0a\u9762\uff0c\u5c31\u50cf\u662f\u98ce\u4e2d\u7684\u6b8b\u70db\u3002 \u653e\u773c\u671b\u8fc7\u53bb\uff0c\u56db\u9762\u90fd\u662f\u767d\u4e91\uff0c\u98d8\u98d8\u6e3a\u6e3a\uff0c\u6d6e\u6d6e\u52a8\u52a8\uff0c\u6574\u4e2a\u5929\u5730\u90fd\u597d\u50cf\u5728\u6d6e\u52a8\u4e2d\uff0c\u8981 \u60f3\u5e73\u5e73\u7a33\u7a33\u7684\u5728\u4e0a\u9762\u8d70\uff0c\u5b9e\u5728\u5f88\u4e0d\u5bb9\u6613\u3002 \u8d8a\u4e0d\u5bb9\u6613\u7684\u4e8b\uff0c\u9646\u5c0f\u51e4\u8d8a\u559c\u6b22\u505a\u3002 \u4ed6\u8d70\u5f97\u5e76\u4e0d\u5feb\uff0c\u56e0\u4e3a\u5feb\u6bd4\u6162\u5bb9\u6613\uff0c\u4ed6\u6162\u6162\u7684\u8d70\u7740\uff0c\u597d\u50cf\u5728\u4e00\u6761\u5e73\u5766\u7684\u5927\u9053\u4e0a\u8e6c\u65b9 \u6b65\u3002 \u90a3\u4e2a\u52fe\u9b42\u7684\u4f7f\u8005\uff0c\u53ea\u6709\u5728\u540e\u9762\u8ddf\u7740\u3002 \u6240\u4ee5\u9646\u5c0f\u51e4\u89c9\u5f97\u66f4\u6109\u5feb\u3002 \u98ce\u4ece\u4ed6\u88e4\u6321\u4e0b\u5439\u8fc7\u53bb\uff0c\u767d\u4e91\u4e00\u7247\u7247\u4ece\u4ed6\u773c\u524d\u98de\u8fc7\uff0c\u4ed6\u5ffd\u7136\u89c9\u5f97\u5929\u5730\u95f4\u5b9e\u5728\u6ca1\u6709\u4ec0\u4e48 \u503c\u5f97\u4ed6\u70e6\u607c\u7684\u4e8b\uff0c\u5c31\u7b97\u771f\u7684\u6389\u4e0b\u53bb\uff0c\u4ed6\u4e5f\u4e0d\u5728\u4e4e\u3002 \u4ed6\u55d3\u5b50\u4e00\u5411\u5f88\u7cdf\uff0c\u800c\u4e14\u4e94\u97f3\u4e0d\u5168\uff0c\u6240\u4ee5\u4e5d\u5c81\u540e\u5c31\u6ca1\u6709\u5531\u8fc7\u6b4c\u3002 \u53ef\u662f\u73b0\u5728\u4ed6\u5374\u5ffd\u7136\u6709\u4e86\u79cd\u653e\u58f0\u9ad8\u5531\u7684\u51b2\u52a8\uff0c\u5c45\u7136\u771f\u7684\u5531\u4e86\u8d77\u6765\uff0c\u5531\u7684\u662f\u513f\u6b4c\u3002 \u56e0\u4e3a\u4ed6\u53ea\u4f1a\u5531\u513f\u6b4c\uff1a \u201c\u59b9\u59b9\u80cc\u7740\u6ce5\u5a03\u5a03\uff0c \u8d70\u5230\u82b1\u56ed\u6765\u770b\u82b1\u2026\u2026\u201d \u5ffd\u7136\u95f4\u3002\u201d\u547c\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u4e00\u9635\u98ce\u4ece\u4ed6\u5934\u9876\u5439\u8fc7\uff0c\u4e00\u4e2a\u4eba\u5728\u4ed6\u773c\u524d\u3002 \u4e00\u4e2a\u6ca1\u6709\u8138\u7684\u4eba\u3002 \u9646\u5c0f\u51e4\u7b11\u4e86\u3002\u201d\u6211\u5531\u7684\u6b4c\u597d\u4e0d\u597d\u542c\uff1f\u201d \u52fe\u9b42\u4f7f\u8005\u51b7\u51b7\u9053\uff1a\u201c\u90a3\u4e0d\u662f\u5531\u6b4c\uff0c\u662f\u9a74\u5b50\u53eb\u3002\u201d \u9646\u5c0f\u51e4\u5927\u7b11\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u6709\u53d7\u4e0d\u4e86\u7684\u65f6\u5019\uff0c\u597d\uff0c\u597d\u6781 \u4ed6\u53c8\u5531\u4e86\u8d77\u6765\uff0c\u5531\u7684\u58f0\u97f3\u66f4\u5927\u3002 \u201c\u5a03\u5a03\u54ed\u4e86\u53eb\u5988\u5988\uff0c \u6811\u4e0a\u5c0f\u9e1f\u7b11\u54c8\u54c8\u2026\u2026\u201d \u52fe\u9b42\u4f7f\u8005\u51b7\u51b7\u7684\u770b\u7740\u4ed6\uff0c\u7b49\u4ed6\u5531\u5b8c\u4e86\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u662f\u9646\u5c0f\u51e4\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u600e\u4e48\u6211\u4e00\u5531\u6b4c\u4f60\u5c31\u8ba4\u51fa\u6211\u6765\u4e86\uff1f\u96be\u9053\u6211\u7684\u6b4c\u58f0\u6bd4\u6211\u7684\u4eba\u8fd8\u51fa\u540d\uff1f\u201d \u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u4f60\u771f\u7684\u662f\u9646\u5c0f\u51e4\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u9664\u4e86\u9646\u5c0f\u51e4\u5916\uff0c\u8fd8\u6709\u8c01\u80fd\u5531\u8fd9\u6837\u7684\u6b4c\uff1f\u201d \u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u3002\u201d \u4ed6\u53c8\u7b11\u4e86\u7b11\u3002\u201d\u8fd9\u4e16\u4e0a\u4e0d\u8981\u8138\u7684\u4eba\u867d\u591a\uff0c\u5374\u8fd8\u6ca1\u6709\u4e00\u4e2a\u505a\u5f97\u50cf\u4f60\u8fd9\u4e48\u5f7b\u5e95\u7684\u3002\u201d \u52fe\u9b42\u4f7f\u8005\u773c\u775b\u91cc\u4eff\u4f5b\u53c8\u6709\u706b\u7130\u5728\u71c3\u70e7\uff0c\u5ffd\u7136\u62d4\u4e0b\u5934\u53d1\u4e0a\u7684\u4e00\u6839\u4e4c\u6728\u66ff\uff0c\u5411\u9646\u5c0f\u51e4\u523a \u4e86\u8fc7\u53bb\u3002 \u4ed6\u7684\u51fa\u624b\u770b\u6765\u5e76\u4e0d\u5947\u7a81\uff0c\u62db\u5f0f\u95f4\u4e5f\u6ca1\u6709\u4ec0\u4e48\u53d8\u5316\uff0c\u4f46\u5374\u5b9e\u5728\u592a\u5feb\uff0c\u5feb\u5f97\u4ee4\u4eba\u65e0\u6cd5\u601d \u8bae\u3002 \u9646\u5c0f\u51e4\u6765\u4e0d\u53ca\u9000\uff0c\u4e5f\u4e0d\u80fd\u95ea\u907f\uff0c\u53ea\u6709\u4f38\u51fa\u624b\uff0c\u7528\u4e24\u6839\u624b\u6307\u4e00\u5939\uff01 \u8fd9\u672c\u662f\u5929\u4e0b\u65e0\u53cc\uff0c\u4e07\u5143\u4e00\u5931\u7684\u7edd\u6280\uff0c\u8fd9\u4e00\u6b21\u5374\u504f\u504f\u5931\u624b \u2014\u5f88\u5e73\u5e73\u51e1\u51e1\u7684\u4e4c\u6728\u66ff\uff0c\u597d\u50cf\u5ffd\u7136\u53d8\u6210\u4e86\u4e24\u6839\uff0c\u95ea\u7535\u822c\u523a\u5411\u4ed6\u7684\u773c\u775b\u3002 \u82e5\u662f\u5728\u5e73\u5730\u4e0a\uff0c\u8fd9\u4e00\u62db\u4ed6\u4e5f\u4e0d\u662f\u4e0d\u80fd\u95ea\u907f\uff0c\u4f46\u73b0\u5728\u4ed6\u811a\u4e0b\u5e76\u4e0d\u662f\u575a\u5b9e\u53ef\u9760\u7684\u571f\u5730\uff0c \u800c\u662f\u6761\u6ed1\u4e0d\u7559\u8db3\u7684\u94a2\u7d22\u3002 \u4ed6\u8eab\u5b50\u2014\u95ea\uff0c\u811a\u4e0b\u5c31\u7ad9\u4e0d\u4f4f\u4e86\uff0c\u2014\u4e2a\u4eba\u5012\u683d\u8471\uff0c\u4eba\u5c31\u6389\u4e0b\u53bb\uff0c\u5411\u90a3\u6df1\u4e0d\u53ef\u6d4b\u7684\u4e07\u4e08 \u7edd\u58d1\u4e2d\u6389\u4e86\u4e0b\u53bb\u3002 \u4e00\u8dcc\u4e0b\u53bb\uff0c\u4eba\u5c31\u53d8\u6210\u4e86\u8089\u9171\u3002 \u4ed6\u5e76\u6ca1\u6709\u53d8\u6210\u8089\u9171\u3002 \u52fe\u9b42\u4f7f\u8005\u5782\u4e0b\u5934\uff0c\u5c31\u770b\u89c1\u4e00\u652f\u811a\u52fe\u5728\u94c1\u7d22\u4e0a\uff0c\u9646\u5c0f\u51e4\u7684\u4eba\u5c31\u50cf\u662f\u6761\u6302\u5728\u9493\u94a9\u4e0a\u7684 \u9c7c\uff0c\u4e0d\u505c\u7684\u5728\u98ce\u4e2d\u6447\u6765\u6643\u53bb\u3002 \u4ed6\u597d\u50cf\u8fd8\u662f\u4e00\u70b9\u4e5f\u4e0d\u5728\u4e4e\uff0c\u53cd\u800c\u89c9\u5f97\u5f88\u6709\u8da3\uff0c\u5c45\u7136\u53c8\u5531\u4e86\u8d77\u6765\uff1a \u201c\u6447\u5440\u6447\uff0c \u6447\u5230\u5916\u5a46\u6865\uff0c \u5916\u5a46\u53eb\u6211\u597d\u5b9d\u5b9d\u2026\u2026\u201d \u4ed6\u6ca1\u6709\u5531\u4e0b\u53bb\uff0c\u53ea\u56e0\u4e3a\u4e0b\u9762\u7684\u6b4c\u8bcd\u4ed6\u5df2\u5fd8\u4e86\u3002 \u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u770b\u6765\u4f60\u771f\u7684\u662f\u9646\u5c0f\u51e4\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u73b0\u5728\u867d\u7136\u8fd8\u662f\u9646\u5c0f\u51e4\uff0c\u7b49\u4e00\u4e0b\u8bf4\u4e0d\u5b9a\u5c31\u4f1a\u53d8\u6210\u4e86\u4e00\u5806\u8089\u9171\u4e86\u3002\u201d \u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u4f60\u771f\u7684\u4e0d\u6015\u6b7b\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5047\u7684\u3002\u201d \u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u4ed6\u7684\u4eba\u5ffd\u7136\u98ce\u8f66\u822c\u4e00\u8f6c\uff0c\u53c8\u5e73\u5e73\u7a33\u7a33\u7684\u7ad9\u5728\u94a2\u7d22\u4e0a\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u770b \u6765\u4f60\u597d\u50cf\u4e5f\u4e0d\u662f\u771f\u7684\u8981\u6211\u6b7b\u3002\u201d\u52fe\u9b42\u4f7f\u8005\u51b7\u51b7\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u60f3\u8981\u4f60\u77e5\u9053\u4e00\u4ef6\u4e8b\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u52fe\u9b42\u4f7f\u8005\u7684\u773c\u775b\u53c8\u5728\u71c3\u70e7\uff0c\u4e00\u5b57\u5b57\u9053\uff1a\u201c\u6211\u8981\u4f60\u77e5\u9053\uff0c\u897f\u4eec\u5439\u96ea\u5e76\u4e0d\u662f\u5929\u4e0b\u65e0\u53cc\u7684 \u5feb\u5251\uff0c\u6211\u6bd4\u4ed6\u66f4\u5feb\u3002\u201d \u8fd9\u4e00\u6b21\u9646\u5c0f\u51e4\u5c45\u7136\u6ca1\u6709\u7b11\uff0c\u76ee\u4e2d\u5ffd\u7136\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u76ef\u7740\u4ed6\u95ee\u9053\uff1a\u201c\u4f60\u7a76\u7adf \u4ec0\u4e48\u4eba\uff1f\u201d \u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u662f\u4e2a\u4e0d\u8981\u8138\u7684\u4eba\u3002\u201d\u4ed6\u4e0d\u8981\u8138\uff0c\u4e5f\u6ca1\u6709\u8138\uff0c\u8138\u4e0a\u5f53\u7136\u5168\u65e0\u8868\u60c5\uff0c\u53ef \u662f\u4ed6\u7684\u58f0\u97f3\u65f6\uff0c\u5374\u4eff\u4f5b\u5ffd\u7136\u6709\u4e86\u79cd\u8bf4\u4e0d\u51fa\u7684\u60b2\u54c0\u3002 \u9646\u5c0f\u51e4\u8fd8\u60f3\u518d\u95ee\u65f6\uff0c\u4ed6\u7684\u4eba\u5df2\u98de\u9e1f\u822c\u63a0\u8d77\uff0c\u8f6c\u773c\u95f4\u5c31\u6d88\u5931\u5728\u767d\u4e91\u91cc\u3002 \u81ea\u4e91\u98d8\u6e3a\uff0c\u9646\u5c0f\u51e4\u75f4\u75f4\u7684\u7ad9\u5728\u4e91\u91cc\uff0c\u4e5f\u4e0d\u77e5\u9053\u5728\u60f3\u4ec0 \u8fc7\u4e86\u5f88\u4e45\uff0c\u4ed6\u624d\u5f00\u59cb\u5f80\u524d\u8d70\uff0c\u7ec8\u4e8e\u5230\u4e86\u5bf9\u5cb8\uff0c\u53ea\u89c1\u5c71\u5d16\u524d\u9762\u4e24\u6839\u7af9\u7aff\u7cfb\u7740\u7ea2\u7ebf\uff0c\u6a2a \u6321\u5728\u4ed6\u9762\u524d\uff0c\u8fdc\u5904\u6709\u4eba\u6b63\u5728\u51b7\u51b7\u7684\u5bf9\u4ed6\u8bf4\u3002\u201d\u51b2\u8fc7\u8fd9\u6761\u751f\u6b7b\u7ebf\uff0c\u4f60\u5df2\u662f\u4e2a\u6b7b\u4eba\u4e86\u3002\u201d\u58f0 \u97f3\u51b7\u5982\u5200\u950b\u3002\u201d\u6240\u4ee5\u4f60\u6700\u597d\u60f3\u4e00\u60f3\uff0c\u662f\u8d70\u8fc7\u6765\uff0c\u8fd8\u662f\u56de\u5934\u53bb\uff1f\u201d\u9646\u5c0f\u51e4\u5fc3\u91cc\u4e5f\u5728\u95ee\u81ea \u5df1\u3002\u201d\u662f\u51b2\u8fc7\u53bb\uff0c\u8fd8\u662f\u56de\u4e70\u53bb\u3002\u201d \u51b2\u8fc7\u53bb\u4e86\u662f\u4e2a\u6b7b\u4eba\uff0c\u56de\u5934\u6050\u6015\u4e5f\u53ea\u6709\u4e00\u6761\u6b7b\u8def\u3002 \u8fd9\u6761\u7ea2\u7ebf\u867d\u7136\u4e00\u78b0\u5c31\u65ad\uff0c\u4f46\u4e16\u4e0a\u53c8\u6709\u51e0\u4e2a\u4eba\u80fd\u51b2\u5f97\u8fc7\u4e91\uff1f \u9646\u5c0f\u51e4\u5ffd\u7136\u7b11\u4e86\u3002\u201d\u6709\u65f6\u5019\u6211\u5929\u5929\u60f3\u6b7b\u90fd\u6b7b\u4e0d\u6210\uff0c\u60f3\u4e0d\u5230\u4eca\u5929\u7ade\u6b7b\u5f97\u8fd9\u4e48\u5bb9\u6613\u3002\u201d \u4ed6\u5fae\u7b11\u7740\uff0c\u8f7b\u8f7b\u677e\u677e\u7684\u5c31\u8d70\u4e86\u8fc7\u53bb\uff0c\u8d70\u5165\u4e86\u4e00\u4e2a\u4ed6\u4ee5\u524d\u5b8c\u5168\u6ca1\u6709\u68a6\u60f3\u8fc7\u7684\u4e16\u754c\u3002 \u8d70\u5165\u4e86\u4e00\u4e2a\u6b7b\u4eba\u7684\u4e16\u754c\u3002 \u653e\u773c\u56db\u671b\uff0c\u4e00\u7247\u7a7a\u8499\uff0c\u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\uff0c\u8fde\u90a3\u52fe\u9b42\u7684\u4f7f\u8005\u90fd\u4e0d\u77e5\u5230\u54ea\u91cc\u53bb\u4e86\u3002 \u5b64\u72ec\u7f8e\u4e5f\u4e0d\u77e5\u5230\u54ea\u91cc\u53bb\u4e86\u3002 \u8fd9\u91cc\u7a76\u7adf\u662f\u4ec0\u4e48\u5730\u65b9\uff1f \u96be\u9053\u6211\u771f\u7684\u5df2\u662f\u4e2a\u6b7b\u4eba\uff1f \u9646\u5c0f\u51e4\u633a\u8d77\u80f8\uff0c\u5927\u6b65\u5411\u524d\u8d70\u53bb\uff0c\u5634\u91cc\u53c8\u5531\u8d77\u4e86\u513f\u6b4c\uff1a \u201c\u59b9\u59b9\u80cc\u7740\u6ce5\u5a03\u5a03 \u8d70\u5230\u82b1\u56ed\u2026\u2026\u201d \u8fd9\u4e00\u53e5\u8fd8\u6ca1\u6709\u5531\u5b8c\uff0c\u7a81\u542c\u65c1\u8fb9\u6709\u4e2a\u4eba\u547b\u541f\u7740\u9053\uff1a\u201c\u6c42\u6c42\u4f60\uff0c\u9976\u4e86\u6211\u5427-- \u7b2c\u56db\u7ae0\u3000\u53f6\u5b64\u9e3f\u81ea\u88c1>> \u53e4\u9f99\u300a\u9646\u5c0f\u51e4\u7cfb\u5217\xb7\u5e7d\u7075\u5c71\u5e84\u300b \u7b2c\u56db\u7ae0\u3000\u53f6\u5b64\u9e3f\u81ea\u88c1 \u58f0\u97f3\u662f\u6267\u4e00\u95f4\u5c0f\u6728\u5c4b\u91cc\u4f20\u51fa\u6765\u7684\u3002 \u4e00\u95f4\u7070\u767d\u8272\u7684\u5c0f\u6728\u5c4b\uff0c\u5728\u8fd9\u8ff7\u96fe\u822c\u7684\u81ea\u4e91\u91cc\uff0c\u4e00\u5b9a\u8981\u5f88\u6ce8\u610f\u624d\u80fd\u770b\u5f97\u89c1\u3002 \u9646\u5c0f\u51e4\u7ec8\u4e8e\u770b\u89c1\u4e86\u53ea\u770b\u89c1\u4e86\u8fd9\u95f4\u5c0f\u6728\u5c4b\uff0c\u5e76\u6ca1\u6709\u770b\u89c1\u4eba\u3002 \u547b\u541f\u58f0\u8fd8\u6ca1\u6709\u505c\uff0c\u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u95ee\u3002\u201d\u4f60\u53d7\u4e86\u4f24\uff1f\u201d \u201c\u6ca1\u6709\u53d7\u4f24\uff0c\u5374\u5feb\u8981\u6b7b\u5382\u3002\u201d\u662f\u5c11\u5973\u7684\u58f0\u97f3\u3002\u201d\u5feb\u8981\u88ab\u4f60\u5531\u6b7b\u4e86\u3002\u201d \u201c\u4f60\u65e2\u7136\u5728\u8fd9\u91cc\uff0c\u5f53\u7136\u4e5f\u662f\u4e2a\u6b7b\u4eba\uff0c\u518d\u6b7b\u4e00\u6b21\u53c8\u4f55\u59a8\uff1f\u201d \u201c\u4f60\u5531\u7684\u8fd9\u79cd\u6b4c\u8fde\u6d3b\u9b3c\u90fd\u53d7\u4e0d\u4e86\uff0c\u4f55\u51b5\u6b7b\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u5927\u7b11\u3002 \u6728\u5c4b\u91cc\u7684\u58f0\u97f3\u53c8\u5728\u95ee\u3002\u201d\u4f60\u77e5\u4e0d\u77e5\u9053\u521a\u624d\u6551\u4f60\u7684\u4eba\u662f\u8c01\uff1f\u201d \u201c\u662f\u4f60\uff1f\u201d \u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u5c31\u662f\u6211\u3002\u201d\u5979\u7684\u7b11\u58f0\u5f88\u751c\u3002\u201d\u6211\u59d3\u53f6\uff0c\u53eb\u53f6\u7075\uff0c\u522b\u4eba\u90fd\u53eb\u6211\u5c0f \u53f6\u3002\u201d \u201c\u597d\u540d\u5b57\u3002\u201d \u201c\u4f60\u7684\u540d\u5b57\u4e5f\u4e0d\u9519\uff0c\u53ef\u662f\u6211\u4e0d\u61c2\uff0c\u4e00\u4e2a\u5927\u7537\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8981\u53eb\u5c0f\u51e4\u51f0\uff1f\u201d \u9646\u5c0f\u51e4\u7684\u7b11\u53d8\u6210\u4e86\u82e6\u7b11\uff0c\u9053\uff1a\u201c\u6211\u53eb\u9646\u5c0f\u51e4\uff0c\u4e0d\u53eb\u5c0f\u51e4\u51f0\u3002\u201d \u53f6\u7075\u53c8\u95ee\u3002\u201d\u8fd9\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u51e4\u51f0\u662f\u4e00\u5bf9\uff0c\u4e0d\u662f\u4e00\u53ea\uff0c\u98ce\u662f\u516c\uff0c\u51f0\u624d\u662f\u6bcd\u7684\u3002\u201d \u4ed6\u6162\u6162\u7684\u8d70\u8fc7\u53bb\uff0c\u5c4b\u4e8e\u91cc\u5374\u5ffd\u7136\u6c89\u9ed8\u4e86\u4e0b\u6765\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u542c\u89c1\u53f6\u7075\u8f7b\u8f7b\u7684\u53f9\u4e86\u53e3 \u6c14\uff0c\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u662f\u7247\u5c0f\u53f6\u5b50\uff0c\u65e2\u6ca1\u6709\u4e00\u5bf9\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u516c\u7684\uff0c\u8fd8\u662f\u6bcd\u7684\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u4f60\u5012\u7528\u4e0d\u7740\u62c5\u5fc3\uff0c\u6211\u4fdd\u8bc1\u53ea\u8981\u770b\u4e00\u773c\uff0c\u5c31\u53ef\u4ee5\u770b\u51fa\u4f60\u662f\u516c\u7684\uff0c \u8fd8\u662f\u6bcd\u7684\uff1f\u201d \u4ed6\u5ffd\u7136\u63a8\u5f00\u95e8\uff0c\u95ef\u8fdb\u4e86\u5c4b\u5b50\u3002 \u5728\u5916\u9762\u770b\u8fd9\u5c4b\u5b50\u5df2\u7ecf\u5c0f\u5f97\u5f88\u53ef\u601c\u4e86\uff0c\u8d70\u8fdb\u53bb\u4e4b\u540e\uff0c\u66f4\u50cf\u662f\u8d70\u8fdb\u95f4\u9e3d\u5b50\u7b3c\u3002 \u53ef\u662f\u9e3d\u5b50\u867d\u5c0f\uff0c\u4e94\u810f\u4ff1\u5168\uff0c\u8fd9\u5c4b\u5b50\u4e5f\u4e00\u6837\uff0c\u522b\u4eba\u5bb6\u7684\u5c4b\u91cc\u6709\u4e9b\u4ec0\u4e48\uff0c\u8fd9\u5c4b\u5b50\u91cc\u51e0\u4e4e \u4e5f\u4e00\u6837\u4e0d\u7f3a\uff0c\u751a\u81f3\u8fd8\u6709\u4e2a\u91d1\u6f06\u9a6c\u6876\u3002 \u9646\u5c0f\u51e4\u5e76\u4e0d\u662f\u4e2a\u4f1a\u5bf9\u9a6c\u6876\u6709\u5174\u8da3\u7684\u4eba\uff0c\u73b0\u5728\u4ed6\u6ce8\u610f\u8fd9\u4e2a\u9a6c\u6876\uff0c\u53ea\u56e0\u4e3a\u4ed6\u8d70\u8fdb\u6765\u7684\u65f6 \u5019\uff0c\u8fd9\u4e2a\u7a7f\u7ea2\u8863\u670d\u7684\u5c0f\u59d1\u5a18\u6b63\u5750\u5728\u9a6c\u6876\u4e0a\u3002 \u7a7f\u5f97\u6574\u6574\u9f50\u9f50\u7684\u5750\u5728\u9a6c\u6876\u4e0a\uff0c\u7528\u4e00\u53cc\u4e4c\u6e9c\u6e9c\u7684\u5927\u773c\u775b\u77aa\u7740\u9646\u5c0f\u51e4\u3002 \u9646\u5c0f\u51e4\u7684\u8138\u6709\u70b9\u7ea2\u4e86\u3002 \u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4e00\u4e2a\u5973\u5b69\u5b50\u5750\u5728\u9a6c\u6876\u4e0a\u7684\u65f6\u5019\uff0c\u7537\u4eba\u603b\u4e0d\u8be5\u95ef\u8fdb\u6765\u7684\u3002 \u53ef\u662f\u65e2\u7136\u5df2\u95ef\u8fdb\u6765\u4e86\uff0c\u518d\u6e9c\u51fa\u53bb\u5c82\u975e\u66f4\u4e0d\u597d\u610f\u601d\uff1f \u6076\u4eba\u5148\u544a\u72b6\uff0c\u9646\u5c0f\u51e4\u773c\u73e0\u5b50\u8f6c\u4e86\u8f6c\uff0c\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u4f60\u5e73\u5e38\u90fd\u662f\u5750\u5728\u9a6c\u6876\u4e0a\u89c1\u4eba \u7684\uff1f\u201d \u53f6\u7075\u4e00\u672c\u6b63\u7ecf\u7684\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u53ea\u6709\u5728\u4e24\u79cd\u60c5\u51b5\u4e0b\u6211\u624d\u4f1a\u5750\u5230\u9a6c\u6876\u4e0a\u3002\u201d \u6709\u4e00\u79cd\u60c5\u51b5\u662f\u4efb\u4f55\u4eba\u90fd\u4e0d\u5fc5\u95ee\u7684\uff0c\u53e6\u5916\u4e00\u79cd\u60c5\u51b5\u5462\uff1f \u53f6\u7075\u9053\uff1a\u201c\u5c31\u662f\u9a6c\u6876\u91cc\u6709\u4e1c\u897f\u8981\u94bb\u51fa\u6765\u7684\u65f6\u5019\u3002\u201d \u9646\u5c0f\u51e4\u53c8\u5feb\u7b11\u4e0d\u51fa\u4e86\u3002 \u9a6c\u6876\u8fd8\u4f1a\u6709\u4ec0\u4e48\u4e1c\u897f\u94bb\u51fa\u6765\uff1f\u9664\u4e86\u81ed\u6c14\u5916\u8fd8\u4f1a\u6709\u4ec0\u4e48\u522b\u7684\uff1f \u53f6\u7075\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u60f3\u770b\u770b\u91cc\u9762\u662f\u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u51e4\u7acb\u523b\u6447\u5934\uff0c\u9053\uff1a\u201c\u4e0d\u60f3\u3002\u201d \u65f6\u7075\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u4e0d\u60f3\u770b\u4e5f\u5f97\u770b\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u91cc\u7684\u4e1c\u897f\u90fd\u662f\u9001\u7ed9\u4f60\u7684\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u4e0d\u8981\u4e5f\u4e0d\u884c\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u5f53\u7136\u4e0d\u884c\u3002\u201d \u770b\u7740\u5979\u7ad9\u8d77\u6765\u6380\u9a6c\u6876\u7684\u76d6\u5b50\uff0c\u9646\u5c0f\u51e4\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u593a\u95e8\u800c\u9003\u3002 \u4ed6\u6ca1\u6709\u9003\u3002 \u9a6c\u6876\u7684\u5473\u9053\u975e\u4f46\u4e00\u70b9\u4e5f\u4e0d\u81ed\uff0c\u800c\u4e14\u9999\u5f97\u5f88\u3002 \u968f\u7740\u8fd9\u9635\u9999\u6c14\u98de\u51fa\u6765\u7684\uff0c\u610f\u662f\u4e00\u53cc\u71d5\u5b50\uff0c\u4e00\u5bf9\u8774\u8776\u3002 \u71d5\u5b50\u548c\u8774\u8776\u521a\u4ece\u5c0f\u7a97\u98de\u51fa\u53bb\uff0c\u65f6\u7075\u53c8\u50cf\u662f\u53d8\u620f\u6cd5\u4e00\u6837\uff0c\u4ece\u9a6c\u6876\u91cc\u62ff\u51fa\u4e86\u4e00\u5957\u5d2d\u65b0\u7684 \u8863\u670d\uff0c\u4e00\u53cc\u67d4\u8f6f\u7684\u978b\u889c\uff0c\u4e00\u5c0f\u575b\u9152\uff0c\u4e00\u5bf9\u9152\u676f\uff0c\u4e24\u53cc\u7b77\u5b50\uff0c\u4e00\u4e2a\u5927\u74e6\u7f50\uff0c\u4e00\u4e2a\u5927\u6c64\u5319\uff0c \u56db\u4e94\u4e2a\u9992\u5934\uff0c\u8fd8\u6709\u4e00\u675f\u9c9c\u82b1\u3002 \u9646\u5c0f\u51e4\u770b\u5446\u4e86\u3002 \u65e0\u8bba\u8c01\u4e5f\u60f3\u4e0d\u5230\u9a6c\u6876\u91cc\u5c45\u7136\u80fd\u62ff\u5f97\u51fa\u8fd9\u4e48\u591a\u4e1c\u897f\u6765\u3002 \u53f6\u7075\u9053\uff1a\u201c\u71d5\u5b50\u548c\u8774\u8776\u662f\u4e3a\u4e86\u8868\u793a\u6211\u4eec\u5bf9\u4f60\u7684\u6b22\u8fce\uff0c\u8863\u670d\u548c\u978b\u889c\u4e00\u5b9a\u5408\u4f60\u7684\u53e6\u4e59\u9152 \u662f\u9648\u5e74\u7af9\u53f6\u9752\uff0c\u74e6\u7f50\u91cc\u662f\u539f\u6c41\u5a49\u9e21\uff0c\u9992\u5934\u4e5f\u662f\u521a\u51fa\u7b3c\u7684\u3002\u201d \u5979\u62fe\u8d77\u5934\uff0c\u770b\u7740\u9646\u5c0f\u51e4\uff0c\u6de1\u6de1\u7684\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u4e9b\u4e1c\u897f\u4f60\u559c\u6b22\u4e0d\u559c\u6b22\uff1f\u201d \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u7b80\u76f4\u559c\u6b22\u5f97\u8981\u547d\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u4f60\u8981\u4e0d\u8981\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e0d\u8981\u7684\u662f\u571f\u72d7\u3002\u201d \u53f6\u7075\u7b11\u4e86\uff0c\u7b11\u5f97\u5c31\u50cf\u662f\u4e00\u6735\u82b1\u3001\u4e00\u5757\u7cd6\u3001\u4e00\u6761\u5c0f\u72d0\u72f8\u3002 \u53ef\u4ee5\u5bb3\u5f97\u6b7b\u4eba\uff0c\u4e5f\u53ef\u4ee5\u8ff7\u5f97\u6b7b\u4eba\u7684\u5c0f\u72d0\u72f8\u3002 \u9646\u5c0f\u51e4\u770b\u7740\u5979\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u662f\u6bcd\u7684\uff0c\u80af\u5b9a\u662f\u6bcd\u7684aU \u9c9c\u82b1\u521a\u63d2\u5165\u82b1\u74f6\uff0c\u9152\u5df2\u5230\u4e86\u9646\u5c0f\u51e4\u809a\u5b50\u91cc\u3002 \u5c0f\u53f6\u5b50\u770b\u7740\u4ed6\u628a\u6e05\u4f8b\u7684\u7af9\u53f6\u9752\u50cf\u5012\u6c34\u4e00\u6837\u5f80\u809a\u5b50\u91cc\u5012\uff0c\u597d\u50cf\u4e0d\u4f46\u89c9\u5f97\u5f88\u60ca\u5947\uff0c\u8fd8\u89c9 \u5f97\u5f88\u53ef\u60dc\uff0c\u5ffd\u7136\u53f9\u606f\u7740\u9053\uff1a\u201c\u53ea\u6709\u4e00\u70b9\u9519\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u4e0d\u61c2\u3002 \u5c0f\u65f6\u5df2\u7ecf\u89e3\u91ca\u3002\u201d\u6709\u4eba\u8bf4\u4f60\u7684\u673a\u667a\u3001\u6b66\u529f\u3001\u9152\u91cf\u3001\u8138\u76ae\u4e4b\u539a\uff0c\u548c\u597d\u8272\u90fd\u662f\u5f88\u5c11\u6709\u4eba \u80fd\u6bd4\u5f97\u4e0a\u7684\uff0c\u3002\u201d \u9646\u5c0f\u51e4\u653e\u4e0b\u7a7a\u9152\u575b\uff0c\u7b11\u7740\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5df2\u770b\u8fc7\u4e86\u6211\u7684\u9152\u91cf\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u6211\u4e5f\u770b\u8fc7\u4f60\u7684\u6b66\u529f\uff0c\u4f60\u521a\u624d\u6ca1\u6709\u6389\u4e0b\u53bb\uff0c\u8fde\u6211\u90fd\u6709\u70b9\u4f69\u670d\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ef\u662f\u6211\u5e76\u4e0d\u597d\u8272\uff0c\u6240\u4ee5\u8fd9\u4e00\u70b9\u81f3\u5c11\u9519\u4e86\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u6ca1\u6709\u9519\u3002 \u9646\u5c0f\u51e4\u751f\u6c14\u4e86\uff0c\u9053\uff1a\u201c\u6211\u6709\u6ca1\u6709\u5bf9\u4f60\u975e\u793c\u8fc7\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u5230\u73b0\u5728\u4e3a\u6b62\u8fd8\u6ca1\u6709\uff0c\u53ef\u662f\u4f60\u770b\u7740\u6211\u7684\u65f6\u5019\uff0c\u90a3\u53cc\u773c\u775b\u5c31\u50cf\u2026\u2026\u201d \u9646\u5c0f\u51e4\u8d76\u7d27\u6253\u65ad\u4e86\u5979\u7684\u8bdd\u3002\u201d\u4f60\u8bf4\u662f\u54ea\u70b9\u9519\u4e86\uff1f\u201d\u53f6\u7075\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u7684\u8138\u76ae\u5e76 \u4e0d\u539a\uff0c\u4f60\u8fd8\u4f1a\u8138\u7ea2\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u96be\u9053\u4f60\u672c\u6765\u8ba4\u4e3a\u6211\u8fd9\u4e00\u8f88\u5b50\u90fd\u6ca1\u6709\u8138\u7ea2\u8fc7\uff1f\u96be\u9053\u90a3\u4e2a\u4eba\u8bf4\u7684\u8bdd\u4f60\u5168\u90fd \u76f8\u4fe1\uff1f\u201d \u53f6\u7075\u7728\u4e86\u7728\u773c\uff0c\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u4e9b\u8bdd\u662f\u8c01\u8bf4\u7684\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u8001\u5200\u628a\u5b50\u3002\u201d \u5c31\u662f\u8fd9\u4e2a\u4eba\uff0c\u5c31\u662f\u8fd9\u4e2a\u540d\u5b57\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u6709\u8fd9\u4e48\u5927\u7684\u9b54\u5200\uff1f \u9646\u5c0f\u51e4\u8bd5\u63a2\u7740\u95ee\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u4f60\u4eec\u7684\u8001\u5927\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u4e0d\u4f46\u662f\u6211\u4eec\u7684\u8001\u5927\uff0c\u4e5f\u662f\u6211\u4eec\u7684\u8001\u677f\uff0c\u6211\u4eec\u7684\u8001\u5b50\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u7a76\u7adf\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u80fd\u8ba9\u5927\u5bb6\u7518\u5fc3\u60c5\u613f\u8ba4\u4ed6\u4e3a\u8001\u5b50\u7684\u4eba\uff0c\u4f60\u8bf4\u5e94\u8be5\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u4eba\u613f\u610f\u505a\u6211\u7684\u513f\u5b50\uff0c\u6211\u4e5f\u4ece\u6765\u4e0d\u60f3\u505a\u4eba\u7684\u513f \u5b50\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u4f60\u53ea\u4e0d\u8fc7\u60f3\u77e5\u9053\u4ed6\u7684\u59d3\u540d\u6765\u5386\u800c\u5df2\u3002\u201d\u9646\u5c0f\u51e4\u4e0d\u80fd\u5426\u8ba4\u3002\u201d\u6211\u7684\u786e\u60f3\uff0c \u60f3\u5f97\u8981\u547d\u3002\u201d \u53f6\u7075\u51b7\u51b7\u9053\uff1a\u201c\u5982\u679c\u4f60\u771f\u7684\u60f3\uff0c\u53ea\u6015\u5c31\u771f\u7684\u4f1a\u8981\u4e86\u4f60\u7684\u547d\u3002\u201d \u5979\u8138\u4e0a\u7684\u8868\u60c5\u5ffd\u7136\u53d8\u5f97\u5f88\u4e25\u8083\u3002\u201d\u4f60\u82e5\u60f3\u5728\u8fd9\u91cc\u8fc7\u5f97\u597d\u4e9b\uff0c\u5343\u4e07\u4e0d\u8981\u53bb\u6253\u542c\u522b\u4eba\u7684 \u5e95\u7ec6\uff0c\u5426\u5219\u2026\u2026\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5426\u5219\u600e\u4e48\u6837\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u5426\u5219\u5c31\u7b97\u4f60\u7684\u6b66\u529f\u518d\u9ad8\u4e00\u767e\u500d\uff0c\u8fd8\u662f\u968f\u65f6\u90fd\u53ef\u80fd\u5931\u8e2a\u7684qo \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5931\u8e2a\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u5931\u8e2a\u7684\u610f\u601d\uff0c\u5c31\u662f\u4f60\u8fd9\u4e2a\u4eba\u5ffd\u7136\u4e0d\u89c1\u4e86\uff0c\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba\u77e5\u9053\u4f60\u53bb\u4e86 \u54ea\u91cc\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8fd9\u91cc\u5e38\u5e38\u6709\u4eba\u5931\u8e2a\uff1f\u201d\u53f6\u7075\u9053\uff1a\u201c\u5e38\u6709ao \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u6211\u672c\u6765\u4ee5\u4e3a\u8fd9\u91cc\u5f88\u5b89\u5168\uff0c\u5f88\u6709\u89c4\u77e9\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u8fd9\u91cc\u672c\u6765\u5c31\u5f88\u6709\u89c4\u77e9\uff0c\u4e09\u4e2a\u89c4\u77e9\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u54ea\u4e09\u4e2a\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u4e0d\u80fd\u6253\u542c\u522b\u4eba\u7684\u8fc7\u53bb\uff0c\u4e0d\u80fd\u5192\u72af\u8001\u9a6c\u628a\u5b50\uff0c\u66f4\u4e0d\u80fd\u8fdd\u80cc\u4ed6\u7684\u547d\u4ee4\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u8981\u6211\u5e72\u4ec0\u4e48\uff0c\u6211\u5c31\u5f97\u5e72\u4ec0\u4e48\uff1f\u201d \u53f6\u7075\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u8981\u4f60\u53bb\u5403\u5c4e\uff0c\u4f60\u5c31\u5f97\u53bb\u5403\u3002\u201d\u9646\u5c0f\u51e4\u53ea\u5b58\u82e6\u7b11\u3002 \u53f6\u7075\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u8981\u5230\u8fd9\u91cc\u6765\uff0c\u544a\u8bc9\u4f60\u8fd9\u4e9b\u8bdd\uff1f\u201d \u9646\u5c0f\u51e4\u7684\u7b11\u5ffd\u7136\u53c8\u53d8\u5f97\u5f88\u6109\u5feb\uff0c\u9053\uff1a\u201c\u5f53\u7136\u662f\u56e0\u4e3a\u4f60\u559c\u6b22\u6211\u3002\u2026 \u53f6\u7075\u4e5f\u7b11\u4e86\u3002\u201d\u770b\u6765\u4ed6\u8fd8\u662f\u6ca1\u6709\u9519\uff0c\u4f60\u7684\u8138\u76ae\u4e4b\u539a\uff0c\u5f88\u53ef\u80fd\u8fde\u67aa\u5c16\u90fd\u523a\u4e0d\u8fdb\u53bb\u3002 \u5979\u7b11\u5f97\u6bd4\u82b1\u8fd8\u7f8e\uff0c\u6bd4\u7cd6\u8fd8\u751c\uff0c\u8f7b\u8f7b\u7684\u63a5\u7740\u9053\uff1a\u201c\u53ef\u662f\u4f60\u5982\u679c\u72af\u4e86\u6211\u7684\u89c4\u77e9\uff0c\u6211\u5c31\u628a \u4f60\u8138\u4e0a\u8fd9\u5f20\u76ae\u5265\u4e0b\u6765\uff0c\u505a\u6211\u7684\u76ae\u62d6\u978b\u3002 \u9646\u5c0f\u51e4\u53c8\u4e0d\u7981\u82e6\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u81f3\u5c11\u5e94\u8be5\u5148\u8ba9\u6211\u77e5\u9053\u4f60\u6709\u4e9b\u4ec0\u4e48\u89c4\u77e9\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u6211\u53ea\u6709\u4e8c\u4e2a\u89c4\u77e9\uff0c\u4e0d\u8981\u53bb\u60f9\u5927\u53f6\u5b50\uff0c\u4e0d\u8981\u8ba9\u5973\u4eba\u8fdb\u9646\u516c\u9986\u95e8\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5927\u53f6\u5b50\u662f\u4e2a\u4eba\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u5927\u53f6\u5b50\u662f\u5c0f\u53f6\u5b50\u7684\u59d0\u59d0\uff0c\u9646\u516c\u9986\u5c31\u662f\u9646\u5c0f\u51e4\u7684\u516c\u9986\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u9646\u516c\u9986\u54ea\u91cc\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u91cc\u3002\u201d \u5979\u63a5\u7740\u9053\uff1a\u201c\u4ece\u73b0\u5728\u5f00\u59cb\uff0c\u8fd9\u91cc\u5c31\u662f\u4f60\u7684\u5bb6\uff0c\u4f60\u665a\u4e0a\u8981\u7761\u5728\u8fd9\u91cc\uff0c\u767d\u5929\u6700\u597d\u4e5f\u8001\u8001 \u5b9e\u5b9e\u7684\u803d\u5728\u8fd9\u91cc\uff0c\u6211\u968f\u65f6\u90fd\u4f1a\u6765\u68c0\u67e5\u7684\u3002\u201d \u9646\u5c0f\u51e4\u53c8\u7b11\u4e86\uff0c\u7b11\u5f97\u5f88\u5947\u602a\u3002 \u53f6\u7075\u77aa\u8d77\u4e86\u773c\uff0c\u9053\uff1a\u201c\u4f60\u6562\u7b11\u6211\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u7b11\u4f60\uff0c\u6211\u662f\u5728\u7b11\u6211\u81ea\u5df1\u3002\u201d \u4ed6\u7b11\u5f97\u4e0d\u4f46\u6709\u70b9\u5947\u602a\uff0c\u8fd8\u6709\u70b9\u60b2\u54c0\u3002\u201d\u6211\u6d3b\u4e86\u4e09\u5341\u5e74\uff0c\u8fd9\u8fd8\u662f\u7b2c\u4e00\u6b21\u6709\u81ea\u5df1\u7684\u5bb6\uff0c \u81ea\u5df1\u7684\u623f\u5b50\u2026\u2026\u201d \u4ed6\u6ca1\u6709\u518d\u8bf4\u4e0b\u53bb\uff0c\u56e0\u4e3a\u53f6\u7075\u5df2\u5c01\u4f4f\u4e86\u4ed6\u7684\u5634\u7528\u81ea\u5df1\u7684\u5634\u5c01\u4f4f\u4e86\u4ed6\u7684\u5634\u3002 \u5979\u7684\u5634\u5507\u51b0\u51c9\u800c\u67d4\u8f6f\u3002 \u4e24\u4e2a\u4eba\u7684\u5634\u5507\u53ea\u4e0d\u8fc7\u8f7b\u8f7b\u4e00\u89e6\uff0c\u5979\u5ffd\u7136\u53c8\u4e00\u62f3\u6253\u5728\u9646\u5c0f\u98ce\u7684\u809a\u5b50\u4e0a\u3002 \u5979\u7684\u51fa\u624b\u53c8\u786c\u53c8\u91cd\u3002 \u9646\u5c0f\u51e4\u88ab\u6253\u5f97\u8fde\u8170\u90fd\u5f2f\u4e86\u4e0b\u53bb\uff0c\u5979\u5374\u5403\u5403\u7684\u7b11\u7740\uff0c\u6e9c\u4e86\u51fa\u53bb\u3002 \u201c\u8bb0\u4f4f\uff0c\u4e0d\u8981\u8ba9\u4efb\u4f55\u5973\u4eba\u8fdb\u95e8\u3002\u201d\u5979\u7684\u58f0\u97f3\u5df2\u5230\u4e86\u95e8\u5916\uff1a\u201c\u5c24\u5176\u4e0d\u80fd\u82b1\u5be1\u5987\u8fdb \u6765\u3002\u201d \u201c\u82b1\u5be1\u5987\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u201c\u5979\u4e0d\u662f\u4eba\uff0c\u662f\u6761\u6bcd\u72d7\uff0c\u4f1a\u5403\u4eba\u7684\u6bcd\u72d7\u3002\u201d \u9646\u5c0f\u51e4\u6709\u56db\u6761\u7709\u6bdb\uff0c\u5374\u53ea\u6709\u4e24\u53ea\u624b\u3002 \u4ed6\u7528\u5de6\u624b\u63c9\u7740\u809a\u5b50\uff0c\u7528\u53f3\u624b\u629a\u7740\u5634\u5507\uff0c\u8138\u4e0a\u7684\u8868\u60c5\u4e5f\u4e0d\u77e5\u662f\u5728\u54ed\uff1f\u8fd8\u662f\u5728\u7b11\u3002 \u5c31\u8fd9\u4e48\u6837\uff0c\u4ed6\u5c31\u7cca\u91cc\u6e56\u6d82\u7684\u7531\u6d3b\u4eba\u53d8\u6210\u4e86\u6b7b\u4eba\uff0c\u7cca\u91cc\u6e56\u5f90\u7684\u6709\u4e86\u4e2a\u5bb6\u3002 \u4ed6\u8fd8\u6709\u4e24\u6761\u817f\uff0c\u5374\u5df2\u8fde\u4ec0\u4e48\u5730\u65b9\u90fd\u4e0d\u80fd\u53bb\u4e86\u3002 \u4ed6\u5ffd\u7136\u5c31\u5df2\u7761\u7740\uff0c\u7761\u4e86\u4e00\u4e0b\u5b50\u5c31\u5f00\u59cb\u505a\u68a6\uff0c\u68a6\u89c1\u81ea\u5df1\u88ab\u4e00\u7247\u51b0\u51b0\u51b7\u51b7\u7684\u5927\u53f6\u5b50\u5305 \u4f4f\uff0c\u53c8\u68a6\u89c1\u4e00\u6761\u5168\u8eab\u90fd\u751f\u6ee1\u4e86\u82b1\u7684\u6bcd\u72d7\u5728\u5543\u4ed6\u7684\u9aa8\u5934\uff0c\u8fde\u5543\u9aa8\u5934\u7684\u58f0\u97f3\u4ed6\u90fd\u80fd\u542c\u5f97\u6e05\u6e05 \u695a\u695a\u3002 \u7136\u540e\u4ed6\u5c31\u53d1\u73b0\u5728\u5c4b\u5b50\u91cc\u771f\u7684\u6709\u4e2a\u4eba\u5728\u5543\u9aa8\u5934\u3002 \u4e0d\u662f\u4ed6\u7684\u9aa8\u5934\uff0c\u662f\u9e21\u9aa8\u5934\u3002 \u5750\u5728\u90a3\u91cc\u5543\u9aa8\u5934\u7684\u4e5f\u4e0d\u662f\u6761\u6bcd\u72d7\uff0c\u662f\u4e2a\u4eba\u3002 \u9646\u5c0f\u51e4\u4e00\u9192\uff0c\u8fd9\u4e2a\u4eba\u7acb\u523b\u5c31\u6709\u4e86\u8b66\u89c9\uff0c\u5c31\u50cf\u662f\u91ce\u517d\u4e00\u6837\u7684\u8b66\u89c9\u3002 \u4ed6\u626d\u8fc7\u5934\uff0c\u76ef\u7740\u9646\u5c0f\u51e4\uff0c\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u654c\u610f\u3002 \u53ef\u662f\u4ed6\u7684\u5634\u91cc\u8fd8\u5728\u5543\u7740\u9e21\u9aa8\u5934\u3002 \u9646\u5c0f\u51e4\u4ece\u6765\u90fd\u6ca1\u6709\u770b\u89c1\u8fc7\u5bf9\u9e21\u9aa8\u5934\u8fd9\u4e48\u6709\u5174\u8da3\u7684\u4eba\uff0c\u4e5f\u6ca1\u6709\u770b\u89c1\u8fc7\u8fd9\u4e48\u7626\u7684\u4eba\u3002 \u4e8b\u5b9e\u4e0a\uff0c\u8fd9\u4e2a\u4eba\u8eab\u4e0a\u7684\u8089\uff0c\u7edd\u4e0d\u4f1a\u6bd4\u4ed6\u5634\u91cc\u5543\u7740\u7684\u9e21\u9aa8\u5934\u591a\u5f88\u591a\u3002 \u4ed6\u8eab\u4e0a\u7a7f\u7740\u7684\u8863\u670d\u5374\u53c8\u5f88\u534e\u4e3d\uff0c\u7edd\u4e0d\u50cf\u7a77\u5f97\u8981\u5543\u9e21\u9aa8\u5934\u7684\u4eba\u3002 \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u8bd5\u63a2\u7740\u95ee\u3002\u201d\u4f60\u662f\u4e0d\u662f\u6709\u75c5\uff1f\u201d \u201c\u4f60\u624d\u6709\u75c5\u3002\u201d \u8fd9\u4e2a\u4eba\u201c\u6696\u201d\u7684\u4e00\u58f0\uff0c\u628a\u5634\u91cc\u7684\u9aa8\u5934\u5410\u5f97\u6ee1\u5730\u90fd\u662f\uff0c\u9732\u51fa\u4e86\u4e00\u53e3\u96ea\u767d\u7684\u7259\u9f7f\uff0c\u72e0\u72e0 \u7684\u76ef\u7740\u9646\u5c0f\u51e4\u3002 \u201c\u4f60\u4ee5\u4e3a\u6211\u4f1a\u6709\u4ec0\u4e48\u75c5\uff1f\u997f\u75c5\uff01\u201d \u201c\u4f60\u4e0d\u997f\uff1f\u201d \u201c\u6211\u6bcf\u5929\u5403\u4e09\u987f\uff0c\u6709\u65f6\u5019\u8fd8\u52a0\u4e0a\u4e00\u987f\u5bb5\u591c\u3002\u201d \u201c\u4f60\u5486\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d \u201c\u6211\u5403\u996d\u3001\u5403\u9762\u3001\u5403\u8089\u3001\u5403\u83dc\uff0c\u53ea\u8981\u80fd\u5403\u7684\uff0c\u6211\u4ec0\u4e48\u90fd\u5403\u3002\u201d \u201c\u4eca\u5929\u4f60\u5403\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d \u201c\u4eca\u5929\u4e2d\u5348\u6211\u5403\u7684\u662f\u5317\u65b9\u83dc\uff0c\u4e00\u6837\u662f\u7ea2\u70e7\u8e44\u8180\uff0c\u4e00\u6837\u662f\u9ec4\u70af\u7f8a\u8089\uff0c\u2014\u6837\u662f\u4e09\u9c9c\u9e2d \u5b50\uff0c\u4e00\u6837\u662f\u9505\u8d34\u8c46\u8150\uff0c\u4e00\u6837\u662f\u867e\u5b50\u4e4c\u53c2\uff0c\u4e00\u6837\u662f\u4e94\u6885\u9e3d\u5b50\uff0c\u53e6\u5916\u8fd8\u6709\u4e00\u7897\u9ec4\u74dc\u5ddd\u4e38\u5b50 \u6c64\u3002\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\u3002 \u8fd9\u4e2a\u4eba\u53c8\u77aa\u8d77\u4e86\u773c\u3002\u201d\u4f60\u4e0d\u4fe1\u3002\uff1f \u201c\u6211\u53ea\u4e0d\u8fc7\u5947\u602a\uff0c\u4e00\u4e2a\u597d\u597d\u7684\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8981\u95ef\u8fdb\u522b\u4eba\u5bb6\u91cc\u6765\u5543\u9e21\u9aa8\u5934\u3002\u201d \u201c\u56e0\u4e3a\u6211\u9ad8\u5174\u3002\u201d\u9646\u5c0f\u51e4\u53c8\u7b11\u4e86\u3002\u201d\u53ea\u8981\u4f60\u9ad8\u5174\uff0c\u53ea\u8981\u6211\u8fd9\u91cc\u6709\u9e21\u9aa8\u5934\uff0c\u968f\u65f6\u90fd\u6b22 \u8fce\u4f60\u6765oU \u8fd9\u4e2a\u4eba\u773c\u775b\u91cc\u53cd\u800c\u9732\u51fa\u4e86\u8b66\u6212\u6000\u7591\u7684\u795e\u8272\u3002\u201d\u4f60\u6b22\u8fce\u6211\u6765\uff1f\u4e3a\u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u662f\u6211\u7b2c\u4e00\u6b21\u6709\u5bb6\uff0c\u56e0\u4e3a\u4f60\u662f\u6211\u5bb6\u7684\u7b2c\u4e00\u4e2a\u5ba2\u4eba\uff0c\u56e0\u4e3a\u6211\u559c\u6b22\u670b \u53cb\u3002\u201d \u8fd9\u4e2a\u4eba\u7684\u6837\u5b50\u66f4\u51f6\u3002\u201d\u6211\u4e0d\u662f\u4f60\u7684\u670b\u53cb\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u73b0\u5728\u4e5f\u8bb8\u4e0d\u662f\uff0c\u4ee5\u540e\u4e00\u5b9a\u4f1a\u662f\u7684\u3002\u201d \u8fd9\u4e2a\u4eba\u867d\u7136\u8fd8\u5728\u76ef\u7740\u4ed6\uff0c\u795e\u8272\u5374\u5df2\u6e10\u6e10\u5e73\u9759\u4e86\u4e0b\u6765\u3002 \u65e0\u8bba\u8c01\u90fd\u4e0d\u80fd\u4e0d\u627f\u8ba4\uff0c\u9646\u5c0f\u51e4\u4e00\u5411\u90fd\u5f88\u4f1a\u4ea4\u670b\u53cb\uff0c\u670b\u53cb\u4eec\u4e5f\u90fd\u5f88\u559c\u6b22\u4ed6\u3002 \u65e0\u8bba\u7537\u670b\u53cb\uff0c\u5973\u670b\u53cb\u90fd\u4e00\u6837\u3002 \u9646\u5c0f\u51e4\u5df2\u5750\u8d77\u6765\uff0c\u5ffd\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u8fd9\u91cc\u6ca1\u6709\u9152\u4e86\uff0c\u5426\u5219\u6211\u2014\u5b9a\u8ddf\u4f60\u559d \u4e24\u676f\u3002\u201d \u8fd9\u4e2a\u4eba\u773c\u775b\u91cc\u7acb\u523b\u53d1\u51fa\u4e86\u5149\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u6ca1\u6709\u9152\uff0c\u4f60\u96be\u9053\u4e0d\u80fd\u5230\u5916\u9762\u53bb\u627e\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u521a\u6765\u8fd8\u4e0d\u5230\u534a\u5929\uff0c\u8fd9\u5730\u65b9\u6211\u8fd8\u4e0d\u719f\uff0c\u53ef\u662f\u6211\u4fdd\u8bc1\uff0c\u4e0d\u51fa\u4e09\u5929\uff0c\u4f60\u65e0 \u8bba\u8981\u559d\u4ec0\u4e48\uff0c\u6211\u90fd\u80fd\u627e\u5f97\u56de\u6765\u3002\u201d \u8fd9\u4e2a\u4eba\u53c8\u76ef\u7740\u4ed6\u770b\u4e86\u534a\u5929\uff0c\u7ec8\u4e8e\u5410\u51fa\u53e3\u6c14\uff0c\u5168\u8eab\u7684\u8b66\u6212\u4e5f\u7acb\u523b\u677e\u9a70\u3002\u201d\u6211\u662f\u4e2a\u6e38 \u9b42\uff0c\u8bf4\u4e0d\u5b9a\u968f\u65f6\u90fd\u4f1a\u95ef\u6765\u7684\uff0c\u4f60\u771f\u7684\u4e0d\u5728\u4e4e\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u4e0d\u5728\u4e4e\u3002 \u4ed6\u771f\u7684\u4e0d\u5728\u4e4e\u3002 \u4ed6\u7ecf\u5e38\u4e09\u66f4\u534a\u591c\u91cc\uff0c\u628a\u670b\u53cb\u4ece\u70ed\u88ab\u7a9d\u91cc\u62d6\u51fa\u6765\u966a\u4ed6\u559d\u9152\uff0c\u670b\u53cb\u4eec\u4e5f\u4e0d\u5728\u4e4e\u3002 \u56e0\u4e3a\u5927\u5bb6\u90fd\u77e5\u9053\uff0c\u82e5\u6709\u4eba\u534a\u591c\u4e09\u66f4\u53bb\u627e\u4ed6\uff0c\u4ed6\u975e\u4f46\u4e0d\u4f1a\u751f\u6c14\uff0c\u53cd\u800c\u9ad8\u5174\u5f97\u8981\u547d\u3002 \u591c\u8272\u5df2\u7b3c\u7f69\u7740\u5927\u5730\uff0c\u665a\u98ce\u4e2d\u5ffd\u7136\u4f20\u6765\u4e86\u949f\u58f0\u3002 \u201c\u8fd9\u662f\u665a\u98df\u949f\u3002\u201d \u9646\u5c0f\u51e4\u4e0d\u61c2\uff0c\u6e38\u9b42\u53c8\u89e3\u91ca\u3002\u201d\u665a\u98df\u949f\u5c31\u662f\u53eb\u5927\u5bb6\u5230\u5385\u91cc\u53bb\u5403\u665a\u996d\u7684\u949f\u58f0\u3002\u201d \u201c\u6bcf\u4e2a\u90fd\u8981\u53bb\uff1f\u201d \u201c\u55ef\u3002\u201d \u201c\u5929\u5929\u90fd\u8981\u53bb\uff1f\u201d \u201c\u4e00\u4e2a\u6708\u6700\u591a\u53ea\u6709\u56db\u4e94\u5929\u3002 \u201c\u90fd\u662f\u5728\u4ec0\u4e48\u65f6\u5019\uff1f\u201d \u201c\u521d\u4e00\u5341\u4e94\uff0c\u9022\u5e74\u8fc7\u8282\uff0c\u6709\u540d\u4eba\u7b2c\u4e00\u5929\u5230\u8fd9\u91cc\u6765\u7684\u65f6\u5019\u3002 \u4ed6\u4e0a\u4e0a\u4e0b\u4e0b\u7684\u6253\u91cf\u7740\u9646\u5c0f\u51e4\u3002\u201d\u4f60\u4e00\u5b9a\u4e5f\u662f\u4e2a\u540d\u4eba\uff0c\u96be\u4f60\u5c31\u662f\u90a3\u4e2a\u957f\u7740\u56db\u6761\u7709\u6bdb\u7684 \u9646\u5c0f\u51e4\uff1f\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u3002\u201d\u53ea\u53ef\u60dc\u73b0\u5728\u7684\u9646\u5c0f\u51e4\uff0c\u5df2\u7ecf\u4e0d\u662f\u4ece\u524d\u7684\u4e2a\u9646\u5c0f\u51e4\u4e86oH \u6e38\u9b42\u60f3\u8bf4\u8bdd\uff0c\u53c8\u5fcd\u4f50\uff0c\u5ffd\u7136\u7ad9\u8d77\u6765\u3002\u201d\u9a6c\u4e0a\u5c31\u4f1a\u6709\u4eba\u6765\u4f60\u53bb\u5403\u996d\uff0c\u6211\u975e\u8d70\u4e0d\u53ef\uff0c\u4f60 \u6700\u597d\u4e0d\u8981\u544a\u8bc9\u522b\u4eba\uff0c\u6211\u5230\u8fd9\u91cc\u8fc7\u3002\u201d \u9646\u5c0f\u51e4\u5e76\u6ca1\u6709\u95ee\u4e3a\u4ec0\u4e48\u3002 \u522b\u4eba\u82e5\u6709\u4e8b\u6c42\u4ed6\uff0c\u4ed6\u53ea\u8981\u80af\u7b54\u5e94\uff0c\u5c31\u4ece\u4e0d\u95ee\u522b\u4eba\u662f\u4e3a\u4ec0\u4e48\uff1f \u5c31\u56e0\u4e3a\u8fd9\u2014\u70b9\uff0c\u4ed6\u5df2\u5e94\u8be5\u6709\u5f88\u591a\u670b\u53cb\u3002 \u6e38\u9b42\u663e\u7136\u4e5f\u5bf9\u8fd9\u4e00\u70b9\u5f88\u6ee1\u610f\uff0c\u5ffd\u53c8\u538b\u4f4e\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4eca\u4f60\u5230\u4e86\u5927\u5385\uff0c\u4ed6\u4eec\u2014\u5b9a\u4f1a\u7ed9 \u4f60\u4e2a\u4e0b\u9a6c\u5a01\u7684\u3002\u201d \u9646\u4e0d\u98ce\u9053\uff1a\u201c\u54e6\uff1f\u201d \u6e38\u9b42\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u91cc\u7684\u4eba\u81f3\u5c11\u6709\u4e00\u534a\u662f\u75af\u5b50\uff0c\u4ed6\u4eec\u552f\u4e00\u55dc\u597d\uff0c\u5c31\u662f\u8650\u5f85\u522b\u4eba\uff0c\u770b\u522b \u4eba\u53d7\u82e6\uff0c\u5176\u4e2d\u8fd8\u6709\u516d\u4e03\u4e2a\u4eba\u75af\u66f4\u53ef\u6015\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u662f\u54ea\u4e03\u4e2a\u4eba\uff1f\u201d \u6e38\u9b42\u9053\uff1a\u201c\u4e00\u4e2a\u53eb\u7ba1\u5bb6\u5a46\uff0c\u4e00\u4e2a\u53eb\u5927\u5c06\uff0c\u4e00\u4e2a\u53eb\u8868\u54e5\uff0c\u4e2a\u53eb\u94a9\u5b50\u2026\u2026\u201d \u4ed6\u53ea\u8bf4\u51fa\u56db\u4e2a\u4eba\u7684\u540d\u5b57\uff0c\u8eab\u5b50\u5c31\u5ffd\u7136\u63a0\u8d77\u4e2d \u5c4b\u91cc\u7684\u7a97\u4e8e\u5f88\u5c0f\uff0c\u53ef\u662f\u4ed6\u7684\u624b\u5f80\u4e0a\u9762\u4e00\u642d\uff0c\u4eba\u5c31\u5df2\u94bb\u51fa\u53bb\u3002 \u770b\u6765\u4ed6\u4e0d\u4f46\u8f7b\u529f\u5f88\u9ad8\uff0c\u8fd8\u4f1a\u7f29\u9aa8\u3002 \u8fd9\u4e24\u79cd\u529f\u592b\u672c\u662f\u53f8\u7a7a\u6458\u661f\u72ec\u95e8\u7edd\u6280\uff0c\u4ed6\u548c\u53f8\u7a7a\u6458\u661f\u6709\u4e48\u5173\u7cfb\uff1f \u9646\u5c0f\u51e4\u6ca1\u6709\u60f3\u4e0b\u53bb\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u542c\u89c1\u4e86\u5916\u9762\u7684\u811a\u6b65\u58f0\u3002 \u5f88\u8f7b\u5f88\u8f7b\uff0c\u53ea\u6709\u811a\u5e95\u957f\u7740\u8089\u638c\u90a3\u79cd\u91ce\u517d\u811a\u6b65\u4f1a\u8fd9\u4e48\u8f7b\u3002 \u53ea\u6709\u8f7b\u529f\u6781\u9ad8\u7684\u8001\u6c5f\u6e56\uff0c\u8d70\u8def\u65f6\u624d\u4f1a\u50cf\u8fd9\u79cd\u91ce\u517d\u3002 \u5e7d\u7075\u5c71\u5e84\u4e2d\uff0c\u54ea\u91cc\u6765\u7684\u8fd9\u4e48\u591a\u8f7b\u529f\u9ad8\u624b\uff1f \u9646\u5c0f\u51e4\u6b63\u5728\u5403\u60ca\uff0c\u5c31\u542c\u89c1\u4e86\u6572\u95e8\u7684\u58f0\u97f3\u3002 \u4ed6\u5b9e\u5728\u60f3\u770b\u770b\u6765\u7684\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u957f\u5f97\u662f\u4ec0\u4e48\u6837\u5b50\uff1f\u4ed6\u7acb\u523b\u5c31\u53bb\u5f00\u95e8\u3002 \u5f00\u4e86\u95e8\u4e4b\u540e\uff0c\u4ed6\u66f4\u5403\u60ca\u3002 \u6572\u95e8\u7684\u5c45\u7136\u4e0d\u662f\u4eba\uff0c\u5c45\u7136\u771f\u7684\u662f\u53ea\u811a\u5e95\u957f\u7740\u8089\u638c\u7684\u91ce\u517d\uff0c \u662f\u4e00\u6761\u72d7\uff01 \u4e00\u6761\u5168\u8eab\u6f06\u9ed1\uff0c\u9ed1\u5f97\u53d1\u4eae\u7684\u5927\u72d7\uff0c\u5728\u591c\u8272\u4e2d\u770b\u6765\u7b80\u76f4\u5c31\u50cf\u662f\u53ea\u8c79\u5b50\uff0c \u53ef\u662f\u5b83\u5bf9\u4eba\u5e76\u4e0d\u51f6\u6076\uff0c\u4e00\u79cd\u6781\u4e25\u683c\u800c\u957f\u4e45\u7684\u8bad\u7ec3\uff0c\u5df2\u6d88\u9664\u4e86\u5b83\u672c\u6027\u4e2d\u5bf9\u4eba\u7c7b\u7684\u654c \u610f\u3002 \u5b83\u4e5f\u6ca1\u6709\u53eb\uff0c\u56e0\u4e3a\u5b83\u5634\u91cc\u8854\u7740\u4e00\u5f20\u7eb8\u3002 \u7eb8\u4e0a\u53ea\u6709\u56db\u4e2a\u5b57\u3002\u201d\u8bf7\u968f\u6211\u6765\u3002\u201d \u8fd9\u6761\u72d7\u7adf\u662f\u6765\u5e26\u9646\u5c0f\u51e4\u53bb\u5403\u665a\u996d\u7684\u3002 \u9646\u5c0f\u51e4\u7b11\u4e86\u3002 \u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u6709\u996d\u5403\u603b\u662f\u4ef6\u4ee4\u4eba\u6109\u5feb\u7684\u4e8b\uff0c\u5c24\u5176\u662f\u73b0\u5728\uff0c\u4ed6\u5b9e\u5728\u5f88\u9700\u8981\u4e00\u987f\u4e30\u5bcc\u800c \u53ef\u53e3\u7684\u665a\u996d\u3002 \u201c\u7ea2\u70e7\u8e44\u8180\uff0c\u4e09\u9c9c\u9e2d\u5b50\uff0c\u867e\u5b50\u4e4c\u53c2\u2026\u2026\u201d\u2014\u542c\u89c1\u90a3\u4f4d\u6e38\u9b42\u8bf4\u8d77\u8fd9\u4e9b\u597d\u83dc\u6765\uff0c\u4ed6\u7684\u53e3 \u6c34\u5c31\u7f6e\u5dee\u70b9\u6d41\u4e86\u4e0b\u6765\u3002 \u72d7\u5bf9\u4ed6\u6447\u5c3e\u5df4\uff0c\u4ed6\u4e5f\u62cd\u4e86\u62cd\u72d7\u7684\u5934\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u5b81\u613f\u8ba9\u4f60\u5e26\u8def\uff1f\u56e0 \u4e3a\u8fd9\u91cc\u7684\u72d7\u5b9e\u5728\u6bd4\u4eba\u53ef\u7231\u5f97\u591a\u3002\u201d \u591c\u5df2\u6df1\uff0c\u96fe\u8fd8\u6ca1\u6709\u6563\uff0c\u51b7\u96fe\u95f4\u867d\u7136\u4e5f\u6709\u51e0\u5341\u70b9\u5bd2\u661f\u822c\u7684\u706f\u706b\uff0c\u5374\u886c\u5f97\u56db\u4e0b\u66f4\u9ed1\u6697\u3002 \u9ed1\u72d7\u5728\u7b49\u4ed6\u7684\u773c\u775b\u5df2\u4e60\u60ef\u4e8e\u9ed1\u6697\u65f6\uff0c\u4ed6\u624d\u53d1\u73b0\u81ea\u5df1\u6b63\u8d70\u5728\u4e00\u6761\u5f88\u5f2f\u66f2\u7684\u5c0f\u8def\u4e0a\u3002 \u8def\u7684\u4e24\u65c1\uff0c\u6709\u5404\u5f0f\u5404\u6837\u7684\u6811\u6728\uff0c\u8fd8\u6709\u4e9b\u4e0d\u77e5\u540d\u7684\u82b1\u8349\u3002 \u5728\u9633\u5149\u666e\u7167\u7684\u65f6\u5019\uff0c\u8fd9\u5c71\u8c37\u4e00\u5b9a\u5f88\u7f8e\u3002 \u53ef\u662f\u8fd9\u5c71\u8c37\u91cc\u662f\u4e0d\u662f\u4e5f\u6709\u9633\u5149\u666e\u7167\u7684\u65f6\u5019\uff1f \u9646\u5c0f\u51e4\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u771f\u6b63\u6700\u6e34\u671b\u89c1\u5230\u7684\uff0c\u5e76\u4e0d\u662f\u4e00\u53ea\u7ea2\u5f97\u53d1\u4eae\u7684\u7ea2\u70e7\u8e44\u8180\uff0c\u800c\u662f\u9633 \u5149\u3002 \u4ed6\u4e5f\u50cf\u522b\u4eba\u4e00\u6837\uff0c\u4e5f\u66fe\u8bc5\u5492\u8fc7\u9633\u5149\u3002 \u6bcf\u5f53\u4ed6\u5728\u9a84\u9633\u5982\u706b\u7684\u590f\u65e5\uff0c\u88ab\u6652\u5f97\u6ee1\u8138\u5927\u6c57\uff0c\u6c14\u5598\u5982\u725b\u65f6\uff0c\u5c31\u5fcd\u4e0d\u4f4f\u8981\u8bc5\u5492\u9633\u5149\u3002 \u53ef\u662f\u73b0\u5728\u4ed6\u6700\u6e34\u671b\u7684\uff0c\u4e5f\u6b63\u662f\u8fd9\u79cd\u9633\u5149\u3002 \u4e16\u754c\u4e0a\u6709\u5f88\u591a\u4e8b\u90fd\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u53ea\u6709\u5f53\u4f60\u5931\u53bb\u5b83\u7684\u65f6\u5019\uff0c\u624d\u77e5\u9053\u5b83\u7684\u73cd\u8d35\u3002 \u9646\u5c0f\u51e4\u5728\u5fc3\u91cc\u53f9\u4e86\u53e3\u6c14\uff0c\u5ffd\u7136\u542c\u89c1\u9644\u8fd1\u4e5f\u6709\u4eba\u5728\u53f9\u6c14\uff0c\u4e0d\u4f46\u6709\u53f9\u6c14\uff0c\u800c\u4e14\u6709\u4eba\u8bf4 \u8bdd\u3002\u201d\u9646\u5c0f\u51e4\uff0c\u6211\u77e5\u9053\u4f60\u4f1a\u6765\u7684\uff0c\u6211\u65e9\u5c31\u5728\u8fd9\u91cc\u7b49\u7740\u4f60\u4e86\u3002\u201d \u8fd9\u91cc\u662f\u5e7d\u7075\u5c71\u5e84\uff0c\u9ed1\u6697\u4e2d\u672c\u5c31\u4e0d\u77e5\u6709\u591a\u5c11\u5e7d\u7075\u8eb2\u85cf\uff0c\u8fd9\u4e2a\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\u4e5f\u98d8\u6e3a\u9634\u68ee \u5982\u9b3c\u9b42\u3002 \u9646\u5c0f\u51e4\u638c\u5fc3\u634f\u8d77\u628a\u51b7\u6c57\u3002 \u4ed6\u660e\u660e\u542c\u89c1\u8bf4\u8bdd\u7684\u58f0\u97f3\u5c31\u5728\u9644\u8fd1\uff0c\u9644\u8fd1\u5374\u504f\u504f\u8fde\u4e2a\u4eba\u5f71\u90fd\u770b\u4e0d\u89c1\u3002 \u201c\u4f60\u770b\u4e0d\u89c1\u6211\u7684\u3002\u201d\u58f0\u97f3\u53c8\u54cd\u8d77\uff1a\u201c\u4e00\u4e2a\u771f\u6b63\u7684\u6076\u9b3c\u8981\u5411\u4eba\u7d22\u547d\u65f6\uff0c\u662f\u7edd\u4e0d\u4f1a\u8ba9\u4eba \u770b\u89c1\u7684\u3002\u201d \u201c\u6211\u6b20\u4e86\u4f60\u4e00\u6761\u547d\uff1f\u201d\u9646\u5c0f\u51e4\u8bd5\u63a2\u7740\u95ee\u3002 \u201c\u55ef\u3002\u201d \u201c\u8c01\u7684\u547d\uff1f\u201d \u201c\u6211\u7684\u547d\u3002\u201d \u201c\u4f60\u662f\u8c01\uff1f\u201d\u201c\u6211\u5c31\u662f\u6b7b\u5728\u4f60\u624b\u4e0a\u7684\u84dd\u80e1\u5b50\u3002\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\uff0c\u5927\u7b11\u3002 \u4e00\u4e2a\u4eba\u5728\u771f\u6b63\u7d27\u5f20\u6050\u60e7\u65f6\uff0c\u5f80\u5f80\u4f1a\u83ab\u540d\u5176\u5999\u7684\u7b11\u8d77\u6765\u3002 \u4ed6\u7684\u7b11\u58f0\u867d\u7136\u5927\uff0c\u5374\u5f88\u77ed\u3002 \u4ed6\u5ffd\u7136\u53d1\u73b0\u8bf4\u8bdd\u7684\u65e2\u4e0d\u662f\u4eba\uff0c\u4e5f\u4e0d\u662f\u9b3c\uff0c\u800c\u662f\u90a3\u6761\u72d7\u3002 \u672c\u6765\u8d70\u5728\u4ed6\u9762\u524d\u7684\u9ed1\u72d7\uff0c\u5df2\u8f6c\u8fc7\u5934\uff0c\u7528\u4e00\u53cc\u6b7b\u9c7c\u822c\u7684\u773c\u775b\u77aa\u7740\u4ed6\u3002 \u201c\u6211\u5c31\u662f\u6b7b\u5728\u4f60\u624b\u4e0a\u7684\u84dd\u80e1\u5b50\u3002\u201d\u8fd9\u53e5\u8bdd\u7684\u786e\u662f\u4ece\u72d7\u5634\u91cc\u8bf4\u51fa\u6765\u7684\uff0c\u6bcf\u4e2a\u5b87\u90fd\u662f\u3002 \u72d7\u600e\u4e48\u4f1a\u8bf4\u4eba\u8bdd\uff1f \u96be\u9053\u84dd\u80e1\u5b50\u7684\u9b3c\u9b42\u5df2\u9644\u5728\u8fd9\u6761\u72d7\u7684\u8eab\u4e0a\uff1f \u9646\u5c0f\u51e4\u7684\u80c6\u5b50\u518d\u5927\uff0c\u4e5f\u4e0d\u7981\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u5c31\u5728\u8fd9\u65f6\uff0c\u8fd9\u6761\u72d7\u5df2\u72c2\u543c\u7740\u5411\u4ed6\u6251\u4e86\u8fc7 \u6765\u3002 \u4ed6\u521a\u60f3\u53bb\u6349\u72d7\u7684\u524d\u722a\uff0c\u8c01\u77e5\u72d7\u7684\u809a\u5b50\u91cc\u7adf\u7a81\u7136\u4f38\u51fa\u4e00\u53ea \u4e00\u53ea\u4eba\u7684\u624b\uff0c\u624b\u4e0a\u62ff\u7740\u4e00\u628a\u5200\uff0c\u624b\u4e00\u626c\uff0c\u5200\u98de\u51fa\uff0c\u76f4\u6253\u9646\u5c0f\u51e4\u7684\u5c0f\u8179\u3002 \u8fd9\u4e00\u7740\u66f4\u662f\u610f\u5916\u4e2d\u7684\u610f\u5916\uff0c\u4e16\u4e0a\u80fd\u8eb2\u8fc7\u8fd9\u4e00\u5200\u7684\u4eba\u80fd\u6709\u51e0\u4e2a\uff1f \u81f3\u5c11\u6709\u2014\u4e2a\u3002 \u9646\u5c0f\u51e4\u7684\u5c0f\u8179\u7a81\u7136\u6536\u7f29\uff0c\u4f38\u51fa\u4e24\u6839\u624b\u6307\u4e00\u5939\uff0c\u679c\u7136\u5939\u4f4f\u4e86\u5200\u950b\u3002 \u90a3\u6761\u72d7\u5374\u5df2\u51cc\u7a7a\u7ffb\u8eab\uff0c\u5012\u63a0\u4e09\u4e08\uff0c\u8f6c\u773c\u95f4\u5c31\u5df2\u6ca1\u4eba\u9ed1\u6697\u4e2d \u9ed1\u6697\u4e2d\u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\u4e86\u3002 \u9646\u5c0f\u51e4\u62ac\u8d77\u5934\u770b\u7740\u8fdc\u65b9\u7684\u9ed1\u6697\uff0c\u4f4e\u4e0b\u5934\u770b\u7740\u81ea\u5df1\u624b\u91cc\u7684\u5c16\u5200\uff0c\u53ea\u6709\u81ea\u5df1\u5bf9\u81ea\u5df1\u82e6 \u7b11\u3002 \u8fd9\u672c\u6765\u660e\u660e\u5e94\u8be5\u662f\u573a\u5669\u68a6\uff0c\u5374\u53c8\u504f\u504f\u4e0d\u662f\u68a6\u3002 \u5728\u8fd9\u68a6\u5883\u822c\u7684\u5e7d\u7075\u5c71\u5e84\u4e2d\uff0c\u4e00\u4ef6\u4e8b\u7a76\u7adf\u662f\u771f\u662f\u68a6\uff1f\u672c\u6765\u5c31\u5f88\u96be\u5206\u5f97\u6e05\u695a\u3002 \u53ea\u4e0d\u8fc7\u4ed6\u603b\u7b97\u660e\u767d\u4e00\u4ef6\u4e8b\u3002\u201d\u8fd9\u5730\u65b9\u7684\u72d7\u4e5f\u5e76\u4e0d\u6bd4\u4eba\u53ef\u7231\u3002 \u9ed1\u6697\u4e2d\u5ffd\u7136\u53c8\u6709\u4eba\u58f0\u4f20\u51fa\u6765\u3002\u201d\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5df2\u613f\u610f\u8ba9\u4eba\u6765\u5e26\u8def\u4e86\uff1f\u201d \u8fd9\u6b21\u4ed6\u770b\u89c1\u7684\u5c45\u7136\u771f\u662f\u4e2a\u4eba\u3002 \u4ed6\u53c8\u770b\u89c1\u4e86\u53f6\u7075\u3002 \u96fe\u4e00\u822c\u7684\u706f\u5149\uff0c\u660f\u706f\u822c\u7684\u8ff7\u96fe\uff0c\u53f6\u7075\u8fd8\u662f\u7b11\u5f97\u90a3\u4e48\u751c\u3002 \u201c\u73b0\u5728\u4f60\u603b\u8be5\u660e\u767d\uff0c\u8fd9\u5730\u65b9\u7a76\u7adf\u662f\u4eba\u53ef\u7231\uff0c\u8fd8\u662f\u72d7\u53ef\u7231\u4e86\uff0c\uff0c\u201c\u6211\u4e0d\u660e\u767d\u3002\u201d \u201c\u4f60\u8fd8\u4e0d\u660e\u767d\uff1f\u201d \u201c\u6211\u53ea\u660e\u767d\u4e00\u4ef6\u4e8b\u3002\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6709\u65f6\u8fd9\u5730\u65b9\u7684\u72d7\u5c31\u662f\u4eba\uff0c\u4eba\u5c31\u662f\u72d7\u3002\u201d \u82b1\u5be1\u5987\u672a\u5fc5\u771f\u7684\u662f\u6761\u6bcd\u72d7\uff0c\u8fd9\u6761\u9ed1\u72d7\u5374\u771f\u7684\u662f\u4e2a\u4eba\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6c5f\u6e56\u4e2d\u5b81\u613f\u505a\u72d7\u7684\u4eba\u867d\u7136\u4e0d\u5c11\uff0c\u80fd\u505a\u5f97\u8fd9\u4e48\u5f7b\u5e95\u7684\u5374\u53ea\u6709\u4e00\u4e2a\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u662f\u8c01\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u72d7\u90ce\u541b\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u4f60\u65e9\u5df2\u77e5\u9053\uff1f\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u81f3\u5c11\u77e5\u9053\u84dd\u80e1\u5b50\u5e76\u4e0d\u662f\u6b7b\u5728\u6211\u624b\u4e0a\u7684\uff0c\u4ed6\u81ea\u5df1\u5f53\u7136\u4e5f\u5e94\u8be5 \u77e5\u9053\uff0c\u6240\u4ee5\u4ed6\u5c31\u7b97\u53d8\u6210\u4e86\u6076\u9b3c\uff0c\u4e5f\u4e0d\u8be5\u6765\u627e\u6211ao \u53f6\u7075\u7b11\u4e86\uff0c\u7728\u7740\u773c\u7b11\u9053\uff1a\u201c\u5c31\u7b97\u6076\u9b3c\u4e0d\u627e\u4f60\uff0c\u997f\u9b3c\u5374\u4e00\u5b9a\u4f1a\u6765\u627e\u4f60ao \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u997f\u9b3c\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u997f\u9b3c\u7684\u610f\u601d\uff0c\u5c31\u662f\u4e3a\u4e86\u7b49\u4f60\u5403\u996d\u7b49\u5f97\u997f\u6b7b\u7684\uff0c\u4f60\u82e5\u8fd8\u4e0d\u8d76\u7d27\u53bb\uff0c\u4eca\u5929\u665a \u4e0a\u5c31\u8981\u591a\u51fa\u4e09\u5341\u4e03\u4e2a\u997f\u9b3c\u6765\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5c31\u7b97\u6211\u8fd8\u4e0d\u53bb\uff0c\u771f\u6b63\u7684\u997f\u9b3c\u4e5f\u53ea\u6709\u4e00\u4e2a\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u8c01\u3002\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u3002\u201d \u7b2c\u4e94\u7ae0\u3000\u82e6\u51b5\u4e0d\u582a\u8a00>> \u53e4\u9f99\u300a\u9646\u5c0f\u51e4\u7cfb\u5217\xb7\u5e7d\u7075\u5c71\u5e84\u300b \u7b2c\u4e94\u7ae0\u3000\u82e6\u51b5\u4e0d\u582a\u8a00 \u6628\u5929\u662f\u94a9\u5b50\u4e03\u5341\u5c81\u7684\u751f\u65e5\uff0c\u4eca\u5929\u4ed6\u9192\u6765\u65f6\uff0c\u5bbf\u9189\u4ecd\u672a\u9192\uff0c\u53ea\u89c9\u5f97\u5934\u75bc\u5982\u88c2\uff0c\u6027\u6b32\u51b2 \u52a8\u3002 \u7b2c\u4e00\u4e2a\u73b0\u8c61\u8868\u793a\u4ed6\u5df2\u8001\u4e86\u3002 \u6628\u5929\u4ed6\u53ea\u4e0d\u8fc7\u559d\u4e86\u56db\u5341\u65a4\u9ec4\u9152\uff0c\u4eca\u5929\u5934\u5c31\u75db\u5f97\u6068\u4e0d\u5f97\u4e00\u5200\u628a\u8111\u888b\u780d\u4e0b\u6765\u3002 \u5341\u5e74\u524d\u4ed6\u8fd8\u66fe\u7ecf\u6709\u8fc7\u4e00\u591c\u75db\u996e\u516b\u5341\u65a4\u9ec4\u9152\u7684\u8bb0\u5f55\uff0c\u7761\u4e86\u4e24\u4e2a\u65f6\u8fb0\u540e\uff0c\u5c31\u5df2\u7cbe\u795e\u6296 \u64de\uff0c\u53ea\u7528\u4e00\u53ea\u624b\uff0c\u5c31\u627c\u65ad\u4e86\u592a\u884c\u4e09\u5341\u516d\u53cb\u4e2d\u4e8c\u5341\u4e09\u4e2a\u4eba\u7684\u54bd\u5589\u3002 \u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u4ed6\u89c9\u5f97\u75db\u6068\uff0c\u6068\u5929\u6068\u5730\uff0c\u4e5f\u6068\u81ea\u5df1\u50cf\u6211\u8fd9\u6837\u7684\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4e5f\u4f1a\u8001\uff1f \u53ef\u662f\u53d1\u89c9\u4e86\u7b2c\u4e8c\u4e2a\u73b0\u8c61\u540e\uff0c\u4ed6\u53c8\u4e0d\u7981\u89c9\u5f97\u5f88\u5b89\u6170\uff0c\u4ed6\u8eab\u4f53\u7684\u67d0\u4e00\u90e8\u5206\uff0c\u7b80\u76f4\u786c\u5f97\u50cf \u662f\u88c5\u5728\u4ed6\u6709\u8155\u4e0a\u7684\u94c1\u94a9\u4e00\u6837\u3002 \u4e03\u5341\u5c81\u7684\u8001\u4eba\uff0c\u6709\u51e0\u4e2a\u80fd\u50cf\u4ed6\u8fd9\u4e48\u5f3a\u58ee\uff1f \u53ea\u53ef\u60dc\u8fd9\u5730\u65b9\u7684\u5973\u4eba\u592a\u5c11\uff0c\u80fd\u88ab\u4ed6\u770b\u4e0a\u773c\u7684\u5973\u4eba\u66f4\u5c11\u3002 \u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u770b\u5f97\u4e0a\u773c\u7684\u5973\u4eba\u4e00\u5171\u53ea\u6709\u4e09\u4e2a\uff0c\u8fd9\u4e09\u4e2a\u8be5\u6b7b\u7684\u5973\u4eba\u53c8\u504f\u504f\u603b\u662f\u8981\u540a\u4ed6\u7684 \u80c3\u53e3\u3002 \u5c24\u5176\u662f\u90a3\u53c8\u7cbe\u53c8\u9b3c\u7684\u5c0f\u72d0\u72f8\uff0c\u5df2\u7ecf\u7b54\u5e94\u8fc7\u4ed6\u4e09\u6b21\uff0c\u8981\u5230\u4ed6\u623f\u91cc\u6765\uff0c\u5bb3\u5f97\u767d\u767d\u7a7a\u7b49\u4e86 \u4e8c\u591c\u3002 \u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u4ed6\u5fc3\u91cc\u66f4\u6068\uff0c\u6068\u4e0d\u5f97\u73b0\u5728\u5c31\u628a\u90a3\u5c0f\u72d0\u72f8\u6293\u8fc7\u6765\uff0c\u6309\u5728\u5e8a\u4e0a\u3002 \u8fd9\u79cd\u60f3\u6cd5\u4f7f\u5f97\u4ed6\u66f4\u6da8\u5f97\u96be\u53d7\uff0c\u4eca\u5929\u82e5\u518d\u4e0d\u53d1\u6cc4\u4e00\u4e0b\uff0c\u8bf4\u4e0d\u8d70\u771f\u7684\u4f1a\u88ab\u618b\u6b7b\u3002 \u4ed6\u5fc3\u91cc\u6b63\u5728\u5e7b\u60f3\u7740\u90a3\u6ee1\u8138\u751c\u7b11\u7684\u5c0f\u72d0\u72f8\uff0c\u548c\u5979\u90a3\u51b7\u82e5\u51b0\u971c\u7684\u59d0\u59d0\uff0c\u8fd8\u6709\u90a3\u5df2\u719f\u5f97\u70c2 \u900f\u4e86\u7684\u82b1\u5be1\u5987\u2026\u2026\u201d \u4ed6\u6b63\u60f3\u4f38\u51fa\u4ed6\u7684\u624b\uff0c\u5916\u9762\u5ffd\u7136\u6709\u4eba\u5728\u6572\u95e8\uff0c\u6572\u5f97\u5f88\u54cd\u3002 \u53ea\u6709\u4e24\u4e09\u4eba\u6562\u8fd9\u4e48\u6837\u6572\u4ed6\u7684\u95e8\uff0c\u6765\u7684\u4e0d\u662f\u7ba1\u5bb6\u5a46\uff0c\u5c31\u662f\u8868\u54e5\u3002 \u8fd9\u4e24\u4e2a\u4eba\u867d\u7136\u90fd\u662f\u4ed6\u7684\u6b7b\u515a\uff0c\u4ed6\u8fd8\u662f\u5fcd\u4e0d\u4f73\u89c9\u5f97\u6709\u80a1\u6012\u6c14\u4e0a\u6d8c\u3002 \u60c5\u6b32\u88ab\u6253\u65ad\u65f6\uff0c\u901a\u5e38\u7acb\u523b\u4f1a\u53d8\u6210\u6124\u6012\u3002 \u4ed6\u62c9\u8fc7\u6761\u8584\u88ab\u76d6\u4f4f\u81ea\u5df1\uff0c\u4f4e\u58f0\u6012\u543c\u3002\u201d\u8fdb\u6765\uff01\u201d \u8868\u54e5\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u7ad9\u5728\u95e8\u5916\uff0c\u5149\u6ed1\u767d\u51c0\u7684\u8138\uff0c\u770b\u6765\u5c31\u50cf\u662f\u4e2a\u521a\u5265\u4e86\u58f3\u7684\u9e21\u86cb\u3002 \u770b\u5230\u8fd9\u5f20\u8138\uff0c\u6ca1\u6709\u4eba\u80fd\u731c\u51fa\u4ed6\u7684\u5e74\u7eaa\u3002 \u5bf9\u4e8e\u8fd9\u4e00\u70b9\uff0c\u4ed6\u81ea\u5df1\u4e00\u5411\u89c9\u5f97\u5f88\u6ee1\u610f\uff0c\u6709\u65f6\u751a\u81f3\u8fde\u4ed6\u81ea\u5df1\u90fd\u5fd8\u4e86\u81ea\u5df1\u7684\u5e74\u7eaa\u3002 \u542c\u89c1\u94a9\u5b50\u7684\u6012\u543c\u58f0\uff0c\u4ed6\u5c31\u77e5\u9053\u8fd9\u8001\u8272\u9b3c\u4eca\u5929\u53c8\u52a8\u4e86\u6625\u60c5\u3002 \u4ed6\u5e26\u7740\u7b11\u63a8\u5f00\u95e8\u8d70\u8fdb\u53bb\uff0c\u770b\u7740\u90a3\u4e00\u70b9\u5728\u8584\u88ab\u91cc\u51f8\u8d77\u7684\u90e8\u4f4d\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u770b\u6765\u4f60\u4eca \u5929\u7684\u60c5\u51b5\u8fd8\u4e0d\u9519\uff0c\u8981\u4e0d\u8981\u6211\u66ff\u4f60\u6458\u4e24\u628a\u53f6\u5b50\u56de\u6765oH \u94a9\u5b50\u53c8\u5728\u6012\u543c\u3002\u201d\u5feb\u95ed\u4e0a\u4f60\u7684\u8d3c\u773c\u548c\u81ed\u5634\uff0c\u8001\u5b50\u8981\u627e\u5973\u4eba\uff0c\u81ea\u5df1\u4f1a\u53bb\u627e\u3002 \u8868\u54e5\u9053\uff1a\u201c\u4f60\u627e\u5230\u51e0\u4e2a\uff1f\u201d \u94a9\u5b50\u66f4\u6124\u6012\uff0c\u4e00\u4e0b\u5b50\u8df3\u8d77\u6765\uff0c\u51b2\u5230\u4ed6\u9762\u524d\uff0c\u7528\u53f3\u624b\u7684\u94c1\u94a9\u62b5\u4f50\u4ed6\u809a\u5b50\uff0c\u54ac\u7740\u7259\u9053\uff1a \u201c\u4f60\u6562\u518d\u8bf4\u4e00\u4e2a\u5b57\uff0c\u8001\u5b50\u5c31\u628a\u4f60\u5fc3\u809d\u4e94\u810f\u4e00\u8d77\u94a9\u51fa\u6765\u3002 \u8868\u54e5\u975e\u4f46\u4e00\u70b9\u4e0d\u5bb3\u6015\uff0c\u53cd\u800c\u7b11\u5f97\u66f4\u6109\u5feb\u3002\u201d\u6211\u5e76\u4e0d\u662f\u5728\u6c14\u4f60\uff0c\u53ea\u4e0d\u8fc7\u5728\u66ff\u4f60\u6cbb\u75c5\uff0c \u4f60\u770b\u4f60\u73b0\u5728\u662f\u4e0d\u662f\u5df2\u7ecf\u8f6f\u4e86\u3002\u201d \u94a9\u5b50\u72e0\u72e0\u7684\u76ef\u7740\u4ed6\uff0c\u5ffd\u7136\u5927\u7b11\uff0c\u5927\u7b11\u7740\u677e\u5f00\u624b\uff1a\u201c\u4f60\u4e5f\u7528\u4e0d\u7740\u795e\u6c14\uff0c\u82e5\u4e0d\u662f\u56e0\u4e3a\u8fd9 \u5730\u65b9\u7684\u7537\u4eba\u6bd4\u5973\u4eba\u597d\u627e\uff0c\u4f60\u7684\u75c5\u4fdd\u8bc1\u6bd4\u6211\u8fd8\u5389\u5bb3\u3002\u201d \u8868\u54e5\u65bd\u65bd\u7136\u8d70\u8fc7\u53bb\uff0c\u5728\u9760\u7a97\u7684\u6905\u5b50\u4e0a\u5750\u4e0b\uff0c\u60a0\u7136\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u8fd9\u5730\u65b9\u771f\u6b63\u7684\u7537\u4eba\u5df2 \u8d8a\u6765\u8d8a\u5c11\u4e86\uff0c\u6211\u771f\u6b63\u770b\u5f97\u4e0a\u773c\u7684\u4e5f\u8bb8\u53ea\u6709\u4e00\u4e2a\u3002\u201d \u94a9\u5b50\u9053\uff1a\u201c\u662f\u4e0d\u662f\u5c06\u519b\uff1f\u201d \u8868\u54e5\u51b7\u7b11\u6447\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u592a\u8001\u3002\u201d \u94a9\u5b50\u9053\uff1a\u201c\u662f\u5c0f\u6e05\uff1f\u201d\u8868\u54e5\u9053\uff1a\u201c\u4ed6\u53ea\u4e0d\u8fc7\u662f\u4e2a\u7ee3\u82b1\u6795\u5934\u3002\u201d \u94a9\u5b50\u9053\uff1a\u201c\u96be\u662f\u7ba1\u5bb6\u5a46\uff1f\u2026 \u8868\u54e5\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4ed6\u81ea\u5df1\u5c31\u662f\u8001\u592a\u5a46\uff0c\u4ed6\u4e0d\u6765\u627e\u6211\uff0c\u6211\u5df2\u7ecf\u8c22\u5929\u8c22\u5730\u4e86\u3002\u201d \u94a9\u4e88\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u8868\u54e5\u9053\uff1a\u201c\u9646\u5c0f\u51e4\u3002\u201d \u94a9\u5b50\u53eb\u8d77\u6765\u3002\u201d\u9646\u5c0f\u51e4\uff1f\u5c31\u662f\u90a3\u4e2a\u957f\u7740\u56db\u6761\u7709\u6bdb\u7684\u9646\u5c0f\u98ce\uff1f\u201d \u8868\u54e5\u772f\u7740\u773c\u7b11\u9053\uff1a\u201c\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u8fd8\u6709\u8c01\u80fd\u8ba9\u6211\u52a8\u5fc3\uff1f\u201d \u94a9\u5b50\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u4f1a\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d \u8868\u54e5\u9053\uff1a\u201c\u636e\u8bf4\u662f\u56e0\u4e3a\u4ed6\u73a9\u4e86\u897f\u95e8\u5439\u96ea\u7684\u8001\u5a46\u3002\u201d \u94a9\u5b50\u9053\uff1a\u201c\u4f60\u5df2\u89c1\u8fc7\u4ed6\uff1f\u201d \u8868\u54e5\u9053\uff1a\u201c\u53ea\u5077\u770b\u4e86\u4e24\u773c\u3002\u201d \u94a9\u5b50\u9053\uff1a\u201c\u4ed6\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u8868\u54e5\u53c8\u772f\u8d77\u4e86\u773c\uff0c\u9053\uff1a\u201c\u5f53\u7136\u662f\u4e2a\u771f\u6b63\u7684\u7537\u4eba\uff0c\u7537\u4eba\u4e2d\u7684\u7537\u4eba\u3002\u201d \u94a9\u5b50\u521a\u5750\u4e0b\uff0c\u53c8\u7ad9\u8d77\u6765\uff0c\u8d64\u7740\u811a\u8d70\u5230\u7a97\u53e3\u3002 \u7a97\u5916\u96fe\u8272\u51c4\u8ff7\u3002 \u4ed6\u5ffd\u7136\u56de\u5934\uff0c\u76ef\u7740\u8868\u54e5\uff0c\u9053\uff1a\u201c\u6211\u8981\u6740\u4e86\u4ed6\u300f\u201d \u8868\u54e5\u4e5f\u8df3\u8d77\u6765\u3002\u201d\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u94a9\u5b50\u9053\uff1a\u201c\u6211\u8bf4\u6211\u8981\u6740\u4e86\u4ed6\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u5973\u4eba\u5c31\u8981\u6740\u4eba\uff1f\u201d \u94a9\u5b50\u63e1\u7d27\u53cc\u62f3\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4ed6\u4eca\u5e74\u53ea\u4e0d\u8fc7\u624d\u4e09\u5341\u5de6\u53f3\uff0c\u6211\u5374\u5df2\u4e03\u5341\u4e86\uff0c\u4f46\u6211\u5374\u8fd8\u662f \u4e00\u5b9a\u80fd\u6740\u5b9a\u4ed6\uff0c\u6211\u6709\u628a\u63e1\uff01\u201d \u770b\u5230\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\uff0c\u65e0\u8bba\u8c01\u90fd\u770b\u5f97\u51fa\u4ed6\u6740\u4eba\u4e0d\u4ec5\u4e3a\u4e86\u8981\u53d1\u6cc4\uff0c\u4e5f\u662f\u4e3a\u4e86\u8981\u8bc1\u660e\u81ea\u5df1 \u8fd8\u5e74\u8f7b\u3002 \u6709\u5f88\u591a\u8001\u4eba\u60f3\u627e\u5e74\u8f7b\u7684\u5973\u5b69\u5b50\uff0c\u5c82\u975e\u4e5f\u662f\u56e0\u4e3a\u540c\u6837\u7684\u7406\u7531\u3002 \u4ed6\u4eec\u53ea\u5fd8\u4e86\u4e00\u70b9\uff0c\u9752\u6625\u867d\u7136\u7f8e\u5999\uff0c\u8001\u5e74\u4e5f\u6709\u8001\u5e74\u7684\u4e50\u8da3\u3002 \u6709\u4f4d\u897f\u65b9\u7684\u667a\u8005\u66fe\u7ecf\u8bf4\u8fc7\u4e00\u6bb5\u8bdd\uff0c\u4e00\u6bb5\u8001\u5e74\u4eba\u90fd\u5e94\u8be5\u542c\u542c\u7684\u8bdd\u3002 \u5e74\u534e\u8001\u53bb\uff0c\u5e76\u4e0d\u662f\u4e00\u4e2a\u9010\u6e10\u8870\u9000\u7684\u8fc7\u7a0b\uff0c\u800c\u662f\u4ece\u4e00\u4e2a\u5e73\u539f\u843d\u5230\u53e6\u4e00\u4e2a\u5e73\u539f\uff0c\u8fd9\u867d\u7136 \u4f7f\u4eba\u54c0\u4f24\uff0c\u53ef\u662f\u5f53\u6211\u4eec\u7ad9\u8d77\u6765\u65f6\uff0c\u53d1\u73b0\u9aa8\u5934\u5e76\u672a\u6298\u65ad\uff0c\u773c\u524d\u53c8\u662f\u4e00\u7247\u7e41\u82b1\u5982\u9526\u7684\u65b0\u5929 \u5730\uff0c\u8fd8\u4e0d\u8da3\u6709\u5f85\u6211\u4eec\u53bb\u63a2\u67e5\uff0c\u8fd9\u5c82\u975e\u4e5f\u662f\u7f8e\u5999\u7684\u4e8b\uff1f\u201d \u94a9\u5b50\u5f53\u7136\u6ca1\u6709\u542c\u8fc7\u8fd9\u4e9b\u8bdd\uff0c\u8868\u54e5\u4e5f\u6ca1\u6709\u3002 \u4ed6\u770b\u7740\u94a9\u5b50\u8138\u4e0a\u7684\u8868\u60c5\uff0c\u7ec8\u4e8e\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u5e2e\u4f60\u6740\u4ed6\uff0c\u53ef\u662f\u4f60\u4e5f\u5f97\u5e2e\u6211 \u5148\u505a\u4e86\u4ed6\u3002\u201d \u94a9\u5b50\u9053\uff1a\u3002\u201d\u597d\uff01\u201d \u7a81\u542c\u95e8\u5916\u4e00\u4e2a\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u597d\u867d\u7136\u597d\uff0c\u53ea\u53ef\u60dc\u4f60\u4eec\u90fd\u5df2\u8fdf\u4e86\u4e00\u6b65qo \u968f\u7740\u7b11\u58f0\u8d70\u8fdb\u6765\u7684\uff0c\u662f\u4e2a\u53c8\u7626\u53c8\u9ad8\uff0c\u9a7c\u80cc\u9e70\u9f3b\u7684\u8001\u4eba\u3002 \u8868\u54e5\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u5c31\u77e5\u9053\u4f60\u8fd9\u7ba1\u5bb6\u5a46\u4e00\u5b9a\u4f1a\u6765\u7ba1\u6211\u4eec\u95f9\u4e8b\u7684\u3002\u201d \u7ba1\u5bb6\u5a46\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u6765\u544a\u8bc9\u4f60\u4eec\u4e00\u4e2a\u6d88\u606f\u3002\u201d \u94a9\u5b50\u62a2\u7740\u95ee\u3002\u201d\u4ec0\u4e48\u6d88\u606f\uff1f\u201d \u7ba1\u5bb6\u5a46\u9053\uff1a\u201c\u90a3\u6761\u9ed1\u72d7\u5df2\u7ecf\u5148\u53bb\u627e\u9646\u5c0f\u51e4\u4e86\uff0c\u5c31\u7b97\u4ed6\u4e0d\u80fd\u5f97\u624b\uff0c\u8fd8\u6709\u5c06\u519b\u3002\u201d \u94a9\u5b50\u52a8\u5bb9\u9053\uff1a\u201c\u5c06\u519b\u51c6\u5907\u600e\u4e48\u6837\uff1f\u201d \u7ba1\u5bb6\u5a46\u9053\uff1a\u201c\u4ed6\u5df2\u5728\u524d\u9762\u6446\u4e0b\u4e86\u9e3f\u95e8\u5bb4\uff0c\u6b63\u5728\u7b49\u7740\u9646\u5c0f\u98ce\u3002\u201d \u591c\u8fd8\u662f\u540c\u6837\u7684\u591c\uff0c\u96fe\u8fd8\u662f\u540c\u6837\u7684\u96fe\uff0c\u5c71\u8c37\u8fd8\u662f\u540c\u6837\u7684\u5c71\u8c37\u3002 \u53ef\u662f\u9646\u5c0f\u51e4\u5fc3\u91cc\u7684\u611f\u89c9\u5df2\u4e0d\u540c\u3002 \u548c\u4e00\u4e2a\u53c8\u751c\u53c8\u7f8e\u53c8\u806a\u660e\u7684\u5973\u5b69\u5b50\u5e76\u80a9\u6f2b\u6b65\uff0c\u5f53\u7136\u6bd4\u8ddf\u5728\u4e00\u6761\u72d7\u540e\u9762\u8d70\u6109\u5feb\u5f97\u591a\u3002 \u53f6\u7075\u7528\u773c\u89d2\u779f\u7740\u9646\u5c0f\u51e4\u3002\u201d\u770b\u6837\u5b50\u4f60\u597d\u50cf\u5f88\u6109\u5feb\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u81f3\u5c11\u6bd4\u521a\u624d\u6109\u5feb\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u77e5\u9053\u6211\u4e0d\u4f1a\u54ac\u4f60\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u4e5f\u6bd4\u521a\u624d\u90a3\u6761\u72d7\u6f02\u4eae\uff0c\u6bd4\u4efb\u4f55\u4e00\u6761\u72d7\u90fd\u6f02\u4eae\u3002\u201d \u53f6\u7075\u7b11\u4e86\uff0c\u7b11\u5f97\u771f\u751c\u3002\u201d\u96be\u9053\u6211\u53ea\u6bd4\u5b83\u5f3a\u8fd9\u4e48\u4e00\u70b9\u70b9\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5f53\u7136\u8fd8\u6709\u522b\u7684\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u8fd8\u6709\u4ec0\u4e48\uff1f\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u4f1a\u8bf4\u8bdd\uff0c\u6211\u559c\u6b22\u542c\u4f60\u8bf4\u8bdd\u3002\u201d \u53f6\u7075\u7728\u7740\u773c\uff0c\u9053\uff1a\u201c\u4f60\u559c\u6b22\u542c\u6211\u8bf4\u4e9b\u4ec0\u4e48\uff1f\u662f\u4e0d\u662f\u559c\u6b22\u542c\u6211\u8bf4\u8bf4\u8fd9\u5730\u65b9\u7684\u79d8\u5bc6\uff1f\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\u3002 \u4ed6\u7684\u7b11\u4e5f\u8bb8\u6709\u5f88\u591a\u79cd\u610f\u601d\uff0c\u5374\u7edd\u5bf9\u8fde\u4e00\u70b9\u5426\u8ba4\u7684\u610f\u601d\u90fd\u6ca1\u6709\u3002 \u53f6\u7075\u9053\uff1a\u201c\u4f60\u8981\u4ece\u54ea\u91cc\u5f00\u59cb\u8bf4\u8d77\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5c31\u4ece\u94a9\u5b50\u5f00\u59cb\u5982\u4f55\uff1f\u201d \u53f6\u7075\u7741\u5927\u4e86\u773c\u775b\uff0c\u5403\u60ca\u7684\u770b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u9053\u94a9\u5b50\uff1f\u4f60\u600e\u4e48\u4f1a\u77e5\u9053\u7684\uff1f\u201d \u9646\u5c0f\u51e4\u60a0\u7136\u9053\uff1a\u201c\u6211\u4e0d\u4f46\u77e5\u9053\u94a9\u5b50\uff0c\u8fd8\u77e5\u9053\u5c06\u519b\uff0c\u8868\u54e5\u548c\u7ba1\u5bb6\u5a46\u3002\u201d \u53f6\u7075\u8d70\u8fc7\u53bb\uff0c\u6458\u4e0b\u7247\u6811\u53f6\uff0c\u53c8\u8d70\u56de\u6765\uff0c\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u9053\u7684\u8863\u7ecf\u592a\u591a \u4e86\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4f60\u82e5\u4e00\u5b9a\u8981\u95ee\uff0c\u6211\u8fd8\u662f\u53ef\u4ee5\u544a\u8bc9\u4f60\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u6700\u597d\u8fd8\u662f\u5148\u4ece\u94a9\u5b50\u5f00\u59cb\u3002\u201d \u65f6\u7075\u9053\uff1a\u201c\u4ed6\u662f\u4e2a\u6740\u4eba\u7684\u94a9\u5b50\uff0c\u4e5f\u662f\u6761\u597d\u8272\u7684\u516c\u72fc\uff0c\u73b0\u5728\u4ed6\u6700\u60f3\u505a\u7684\u4e00\u4ef6\u4e8b\uff0c\u5c31\u662f \u628a\u6211\u7684\u88e4\u5b50\u6495\u70c2\uff0c\u628a\u6211\u6309\u5230\u5e8a\u4e0a \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u7528\u4e0d\u7740\u8bf4\u5f97\u8fd9\u4e48\u5766\u767d\u7684\u3002\u201d \u53f6\u7075\u7741\u5927\u4e86\u5979\u90a3\u7eaf\u771f\u65e0\u90aa\u7684\u773c\u775b\uff0c\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u662f\u4e2a\u5766\u767d\u7684\u5973\u4eba\uff0c\u53c8\u6070\u5de7\u662f\u4e2a\u6700 \u4e86\u89e3\u7537\u4eba\u7684\u5973\u4eba\u3002\u201d \u9646\u5c0f\u51e4\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u771f\u662f\u5de7\u5f97\u5f88\uff0c\u53ea\u53ef\u60dc\u6211\u5e76\u4e0d\u60f3\u542c\u6709\u591a\u5c11\u7537\u4eba\u8981\u8131\u4f60 \u88e4\u5b50\u3002\u201d \u53f6\u7075\u7728\u4e86\u7728\u773c\uff0c\u9053\uff1a\u201c\u5047\u5982\u6709\u4eba\u8981\u4f60\u8131\u88e4\u5b50\uff0c\u4f60\u60f3\u4e0d\u60f3\u542c\uff1f\u201d\u9646\u5c0f\u51e4\u7b11\u9053\uff1a\u201c\u8fd9\u79cd \u4e8b\u4e5f\u5e73\u5e38\u5f97\u5f88\u6211\u4e5f\u4e0d\u662f\u7b2c\u4e00\u6b21\u9047\u5230\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u5047\u5982\u6709\u4eba\u8981\u8131\u4f60\u88e4\u5b50\u7684\u662f\u4e2a\u7537\u4eba\u5462\uff1f\u201d \u9646\u5c0f\u51e4\u53eb\u4e86\u8d77\u6765\u3002\u201d\u662f\u4e2a\u7537\u4eba\uff1f\u201d \u53f6\u7075\u5ae3\u7136\u9053\uff1a\u201c\u6211\u8bf4\u9519\u4e86\uff0c\u4e0d\u662f\u2014\u4e2a\u7537\u4eba\uff0c\u662f\u4e24\u4e2a\u3002\u201d \u9646\u5c0f\u51e4\u8fde\u53eb\u90fd\u53eb\u4e0d\u51fa\u4e86\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u8bd5\u63a2\u7740\u95ee\u9053\uff1a\u201c\u662f\u4e0d\u662f\u8868\u54e5\u548c\u7ba1\u5bb6\u5a46\u3002\u201d\u53f6 \u7075\u53c8\u7741\u5927\u773c\u775b\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d\u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4eba\u7684\u540d\u5b57\u542c\u8d77\u6765\u5c31\u6709\u70b9 \u90aa\u6c14\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u53ef\u662f\u6700\u53ef\u6015\u7684\u4e00\u4e2a\u4eba\u5e76\u4e0d\u662f\u4ed6\u4eec\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u54e6\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u89c1\u8fc7\u53ef\u4ee5\u7528\u4e00\u53cc\u7a7a\u624b\u6d3b\u6d3b\u7684\u628a\u4e00\u6761\u91ce\u725b\u6495\u6210\u4e24\u534a\u7684\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u7acb\u523b\u6447\u5934\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u89c1\u8fc7\u53ea\u7528\u2014\u2014\u6839\u624b\u6307\u5c31\u53ef\u4ee5\u628a\u522b\u4eba\u8111\u888b\u6572\u5f97\u7a00\u70c2\u7684\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u65f6\u7075\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5c31\u5feb\u89c1\u5230\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u54bd\u4e0b\u5634\u91cc\u7684\u4e00\u53e3\u82e6\u6c34\uff0c\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u5c06\u519b\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u4e5f\u5728\u7b49\u6211\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u4e0d\u4f46\u5728\u7b49\u4f60\uff0c\u800c\u4e14\u5df2\u7ecf\u7b49\u5f97\u5f88\u4e0d\u8010\u70e6\u4e86\uff0c\u6240\u4ee5\u4f60\u6700\u597d\u5148\u53bb\u627e\u4e2a\u5927\u94c1\u9505 \u6765\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8981\u94c1\u9505\u5e72\u4ec0\u4e48\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u76d6\u4f4f\u4f60\u7684\u8111\u888b\u3002\u201d \u5c06\u519b\u6b63\u7ad9\u5728\u9ad8\u53f0\u4e0a\u3002 \u4ed6\u8eab\u9ad8\u516b\u5c3a\u516b\u5bf8\uff0c\u91cd\u4e00\u767e\u4e03\u5341\u4e09\u65a4\uff0c\u5bbd\u80a9\uff0c\u539a\u80f8\uff0c\u53cc\u817f\u7c97\u5982\u6811\u5e72\uff0c\u624b\u638c\u4f38\u5f00\u65f6\u5927\u5982 \u84b2\u6247\uff0c\u638c\u5fc3\u7684\u8005\u8327\u539a\u8fbe\u4e00\u5bf8\uff0c\u65e0\u8bba\u591a\u4e48\u950b\u5229\u7684\u5200\u5251\uff0c\u88ab\u4ed6\u7a7a\u624b\u4e00\u63e1\uff0c\u7acb\u523b\u9504\u65ad\u300f \u4ed6\u9762\u524d\u5c45\u7136\u771f\u7684\u6709\u53e3\u5927\u94c1\u9505\uff01 \u94c1\u9505\u6446\u5728\u706b\u7089\u4e0a\uff0c\u706b\u7089\u6446\u5728\u9ad8\u53f0\u524d\uff0c\u9ad8\u53f0\u5c31\u5728\u5927\u5385\u91cc\u3002 \u5927\u5385\u9ad8\u56db\u4e08\uff0c\u77f3\u53f0\u9ad8\u4e03\u5c3a\uff0c\u94c1\u9505\u4e5f\u6709\u4e09\u5c3a\u591a\u9ad8\u3002 \u7089\u706b\u6b63\u65fa\uff0c\u9505\u91cc\u716e\u7740\u6c14\u817e\u817e\u7684\u4e00\u9505\u8089\uff0c\u9999\u5f97\u7b80\u76f4\u53ef\u4ee5\u628a\u5341\u91cc\u4e4b\u5185\u7684\u4eba\u548c\u72d7\u90fd\u5f15\u3002 \u9646\u5c0f\u51e4\u8fdb\u6765\u7684\u65f6\u5019\uff0c\u5c06\u519b\u6b63\u7528\u4e00\u53ea\u5927\u6728\u52fa\u5728\u6405\u52a8\u9505\u91cc\u7684\u8089\u3002 \u770b\u89c1\u9646\u5c0f\u51e4\uff0c\u4ed6\u7acb\u523b\u653e\u4e0b\u6728\u52fa\uff0c\u77aa\u8d77\u4e86\u773c\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u9646\u5c0f\u51e4\u3002\u201d \u559d\u58f0\u5982\u775b\u7a7a\u9739\u96f7\uff0c\u9646\u5c0f\u51e4\u5374\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u7728\u4e00\u7728\uff0c\u4e5f\u559d\u4e00\u58f0\u3002\u201d\u5c06\u519b\uff1f\u201d \u5c06\u519b\u9053\uff1a\u201c\u4f60\u6765\u4e0d\u6765\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u6765\u3002\u201d \u4ed6\u771f\u7684\u8d70\u8fc7\u53bb\uff0c\u6b65\u5b50\u8fc8\u5f97\u6bd4\u5e73\u5e38\u8fd8\u8981\u5927\u5f97\u591a\u3002 \u5c06\u519b\u77aa\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u9505\u91cc\u662f\u8089\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u662f\u8089\u3002\u201d \u5c06\u519b\u9053\uff1a\u201c\u4f60\u5403\u8089\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5403\u3002\u201d \u5c06\u519b\u9053\uff1a\u201c\u5403\u5f97\u591a\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u591a\u3002\u201d \u5c06\u519b\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5403\uff01\u201d \u4ed6\u5c06\u624b\u91cc\u7684\u5927\u6728\u52fa\u4ea4\u7ed9\u9646\u5c0f\u51e4\uff0c\u9646\u5c0f\u51e4\u63a5\u8fc7\u6765\u5c31\u6ee1\u6ee1\u76db\u4e86\u4e00\u52fa\u3002 \u4e00\u52fa\u8089\u5c31\u6709\u4e00\u7897\u8089\uff0c\u6eda\u70eb\u7684\u8089\u3002 \u9646\u5c0f\u51e4\u4e0d\u6015\u70eb\uff0c\u5403\u5f97\u5feb\uff0c\u4e00\u52fa\u8089\u5403\u5b8c\uff0c\u4ed6\u624d\u5410\u4e00\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\u8089\u3002\u201d \u5c06\u519b\u9053\uff1a\u201c\u672c\u5c31\u662f\u597d\u8089ao \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u4e5f\u5403\u8089\uff1f\u201d \u5c06\u519b\u9053\uff1a\u201c\u5403\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e5f\u5403\u5f97\u591a\uff1f\u201d \u5c06\u519b\u4e00\u628a\u593a\u8fc7\u4ed6\u624b\u91cc\u6728\u52fa\uff0c\u4e5f\u6ee1\u6ee1\u7684\u5403\u4e86\u4e00\u52fa\uff0c\u4ef0\u9762\u957f\u5618\u3002\u201d\u597d\u8089\uff01 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u662f\u597d\u8089\u3002\u201d \u5c06\u519b\u9053\uff1a\u201c\u4f60\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u8089\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e0d\u77e5\u9053\uff1a\u201c \u5c06\u519b\u9053\uff1a\u201c\u4f60\u4e0d\u6015\u8fd9\u662f\u4eba\u8089\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6015\u3002 \u5c06\u519b\u9053\uff1a\u201c\u6015\u4e5f\u8981\u5403\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5403\u4eba\u603b\u6bd4\u88ab\u5403\u597d\u3002\u201d \u5c06\u519b\u53c8\u77aa\u7740\u4ed6\u770b\u4e86\u5f88\u4e45\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5403\u3002\u201d \u4e00\u52fa\u8089\u5c31\u662f\u4e00\u7897\u8089\uff0c\u4e00\u7897\u8089\u5c31\u6709\u4e00\u65a4\uff0c\u9646\u5c0f\u51e4\u53c8\u5403\u4e86\u4e00 \u52fa \u5c06\u519b\u4e5f\u5403\u4e00\u52fa\uff0c\u4ed6\u518d\u5403\u4e00\u52fa\u3002 \u7247\u523b\u4e4b\u95f4\uff0c\u81f3\u5c11\u5df2\u6709\u4e94\u65a4\u6eda\u70eb\u7684\u8089\u4e0b\u4e86\u4ed6\u7684\u809a\u3002 \u5403\u5230\u7b2c\u516d\u52fa\u65f6\uff0c\u5c06\u519b\u624d\u95ee\u3002\u201d\u4f60\u8fd8\u80fd\u5403\uff1f\u201d \u9646\u5c0f\u51e4\u4e0d\u5f00\u53e3\uff0c\u5374\u5ffd\u7136\u7ffb\u8d77\u8ddf\u5934\u6765\uff0c\u4e00\u53e3\u6c14\u7ffb\u4e86\u4e09\u767e\u516d\u5341\u4e2a\u8ddf\u5934\uff0c\u624d\u7ad9\u8d77\u6765\u56de \u7b54\u3002\u201d\u6211\u8fd8\u80fd\u5403\u3002\u201d \u5c06\u519b\u9053\uff1a\u597d\uff0c\u518d\u5403\u3002\u201d \u518d\u5403\u5c31\u518d\u5403\uff0c\u5403\u4e00\u52fa\uff0c\u7ffb\u4e94\u4e2a\u8ddf\u5934\uff0c\u4e24\u5343\u4e2a\u8ddf\u5934\u7ffb\u8fc7\uff0c\u9646\u5c0f\u51e4\u8fd8\u662f\u9762\u4e0d\u6539\u8272\u3002 \u5c06\u519b\u5374\u4e0d\u7981\u52a8\u5bb9\u3002\u9053\uff1a\u201c\u597d\u8ddf\u5934\u3002\u201d\u4e09\u4e2a\u5b57\u521a\u51fa\u53e3\uff0c\u5657\u7684\u4e00\u58f0\u54cd\uff0c\u4ed6\u809a\u5b50\u7684\u76ae\u8170\u5e26 \u5df2\u65ad\u6210\u4e24\u622a\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u8fd8\u80fd\u5403\uff1f\u201d \u5c06\u519b\u4e5f\u4e0d\u7b54\u8bdd\uff0c\u5374\u8df3\u4e0b\u9ad8\u53f0\uff0c\u4e00\u628a\u6284\u4f4f\u4e86\u706b\u7089\u7684\u811a\u3002 \u706b\u7089\u662f\u751f\u94dc\u6253\u6210\u7684\uff0c\u518d\u52a0\u4e0a\u7089\u4e0a\u7684\u94c1\u9505\uff0c\u5c11\u8bf4\u4e5f\u6709\u4e94\u4e03\u767e\u65a4\uff01 \u4ed6\u7528\u4e00\u53ea\u624b\u5c31\u4e3e\u8d77\u6765\uff0c\u518d\u653e\u4e0b\uff0c\u53c8\u4e3e\u8d77\u6765\uff0c\u4e00\u53e3\u6c14\u505a\u4e86\u4e09\u767e\u516d\u5341\u6b21\uff0c\u624d\u653e\u4e0b\u706b\u7089\uff0c \u593a\u8fc7\u6728\u52fa\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u770b\u7740\u3002\u201d \u8fd9\u6b21\u4ed6\u5403\u4e86\u4e24\u52fa\u3002 \u9646\u5c0f\u51e4\u770b\u7740\u4ed6\u624b\u91cc\u6728\u52fa\uff0c\u8fde\u773c\u775b\u90fd\u4f3c\u5df2\u770b\u5f97\u53d1\u76f4\uff0c\u5ffd\u7136\u4e5f\u6284\u8d77\u706b\u7089\uff0c\u4e3e\u9ad8\u653e\u4e0b\uff0c\u4e00 \u53e3\u6c14\u505a\u4e86\u4e09\u767e\u516d\u5341\u6b21\uff0c\u593a\u8fc7\u6728\u52fa\uff0c\u5403\u4e86\u4e24\u52fa\u3002 \u5c06\u519b\u7684\u773c\u775b\u4e5f\u5df2\u770b\u5f97\u53d1\u76f4\u3002 \u9646\u5c0f\u51e4\u5598\u7740\u6c14\uff0c\u9053\uff1a\u201c\u518d\u5403\uff1f\u201d \u5c06\u519b\u54ac\u4e86\u54ac\u7259\uff0c\u9053\uff1a\u201c\u518d\u5403\u3002\u201d \u4ed6\u63a5\u8fc7\u6728\u52fa\uff0c\u4e00\u52fa\u5b50\u52fa\u4e0b\u53bb\uff0c\u53ea\u542c\u53c8\u662f\u201c\u5657\u201d\u7684\u4e00\u58f0\u54cd\u3002 \u8fd9\u6b21\u5e76\u4e0d\u662f\u76ae\u5e26\u65ad\u4e86\uff0c\u800c\u662f\u6728\u52fa\u5df2\u78b0\u5230\u9505\u5e95\u3002 \u4e00\u52fa\u8089\u5c31\u662f\u4e00\u65a4\uff0c\u4e00\u9505\u8089\u603b\u6709\u4e09\u4e94\u5341\u52fa\uff0c\u5b8c\u5168\u90fd\u88ab\u4ed6\u4eec\u5403\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u9646\u5c0f\u51e4\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u6478\u7740\u5df2\u51f8\u8d77\u6765\u7684\u809a\u5b50\uff0c\u9053\uff1a\u201c\u597d\u8089\u3002\u201d\u5c06\u519b\u9053\uff1a\u201c\u672c\u5c31\u662f\u597d \u8089\u3002\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u6ca1\u6709\u8089\u6bd4\u6709\u8089\u8fd8\u597d\u3002\u201d\u5c06\u519b\u77aa\u7740\u4ed6\uff0c\u5ffd\u7136\u5927\u7b11\uff0c\u9053\uff1a\u201c\u597d\u5f97 \u591a\u4e86\u3002\u201d \u4e24\u4e2a\u4eba\u4e00\u8d77\u5927\u7b11\uff0c\u5ffd\u7136\u53c8\u4e00\u8d77\u5012\u4e86\u4e0b\u53bb\uff0c\u8eba\u5728\u77f3\u53f0\u4e0a\uff0c\u8eba\u7740\u8fd8\u5728\u7b11\u3002 \u53f0\u4e0b\u5f53\u7136\u8fd8\u6709\u4eba\uff0c\u6240\u6709\u7684\u4eba\u65e9\u5df2\u5168\u90fd\u77aa\u5927\u4e86\u773c\u775b\uff0c\u5f20\u5927\u4e86\u5634\uff0c\u9762\u9762\u76f8\u89d1\uff0c\u8bf4\u4e0d\u51fa\u8bdd \u6765\u3002 \u5c06\u519b\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u4f60\u7684\u809a\u5b50\u8fd8\u6ca1\u6709\u7834\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u5c06\u519b\u9053\uff1a\u201c\u5012\u770b\u4e0d\u51fa\u4f60\u8fd9\u5c0f\u5c0f\u7684\u809a\u5b50\u91cc\uff0c\u80fd\u88c5\u5f97\u4e0b\u5982\u6b64\u591a\u9614\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u8fd8\u6bd4\u4f60\u591a\u5486\u4e86\u4e00\u52fa\u3002\u201d \u5c06\u519b\u9053\uff1a\u201c\u6211\u6bcf\u52fa\u8089\u90fd\u6bd4\u4f60\u591a\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u672a\u5fc5\u3002\u201d \u5c06\u519b\u7a81\u7136\u53c8\u8df3\u8d77\u6765\uff0c\u77aa\u7740\u4ed6\u3002 \u9646\u5c0f\u51e4\u5374\u8fd8\u662f\u56db\u5e73\u516b\u7a33\u7684\u8eba\u7740\u3002 \u5c06\u519b\u9053\uff1a\u201c\u7ad9\u8d77\u6765\uff0c\u518d\u716e\u4e00\u9505\u8089\u6765\u6bd4\u8fc7\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e0d\u6bd4\u4e86\u3002\u201d \u5c06\u519b\u9053\uff1a\u201c\u4f60\u8ba4\u8f93\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u672c\u6765\u5df2\u80dc\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u6bd4\uff1f\u6211\u672c\u6765\u5df2\u8d62\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8981\u8ba4\u8f93\uff1f\u201d \u5c06\u519b\u77aa\u7740\u4ed6\uff0c\u989d\u4e0a\u9752\u7b4b\u4e00\u6839\u6839\u51f8\u8d77\uff0c\u6bcf\u6839\u7b4b\u90fd\u6bd4\u522b\u4eba\u7684\u624b\u6307\u8fd8\u7c97\u3002\u9646\u5c0f\u51e4\u6de1\u6de1\u9053\uff1a \u201c\u539f\u6765\u4f60\u4e0d\u4f46\u809a\u5b50\u4e86\u6da8\uff0c\u5934\u4e5f\u5728\u53d1\u6da8aU \u5c06\u519b\u53cc\u62f3\u5ffd\u7136\u63e1\u7d27\uff0c\u5168\u8eab\u9aa8\u8282\u7acb\u523b\u53d1\u51fa\u4e00\u8fde\u4e32\u7206\u7af9\u822c\u7684\u58f0\u54cd\uff0c\u672c\u6765\u5df2\u6709\u516b\u5c3a\u516b\u5bf8\u9ad8 \u7684\u8eab\u6750\u2019\uff0c\u597d\u50cf\u53c8\u589e\u957f\u4e86\u534a\u5c3a\u3002 \u770b\u6765\u8fd9\u4e2a\u4eba\u4e0d\u4f46\u5929\u751f\u795e\u529b\uff0c\u4e00\u8eab\u786c\u529f\uff0c\u4e5f\u5df2\u7ec3\u5230\u98a0\u5cf0\u3002 \u9646\u5c0f\u51e4\u5374\u7b11\u4e86\u3002\u201d\u4f60\u60f3\u6253\u67b6\uff1f\u201d \u5c06\u519b\u95ed\u7740\u5634\u3002 \u73b0\u5728\u4ed6\u5df2\u5c06\u5168\u8eab\u529b\u91cf\u96c6\u4e2d\uff0c\u4e00\u5f00\u53e3\u8bf4\u8bdd\uff0c\u6c14\u529b\u5c31\u5206\u6563 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5403\u8089\u6211\u867d\u7136\u5df2\u6ca1\u6709\u5174\u8da3\uff0c\u6253\u67b6\u6211\u5012\u53ef\u4ee5\u5949\u966a\u3002\u201d \u5c06\u519b\u7a81\u7136\u5927\u559d\uff0c\u5410\u6c14\u5f00\u58f0\uff0c\u4e00\u62f3\u51fb\u51fa\u3002 \u4ed6\u84c4\u52bf\u5df2\u4e45\uff0c\u6b63\u5982\u5f3a\u5f13\u5f15\u6ee1\uff0c\u8fd9\u4e00\u62f3\u4e4b\u5a01\uff0c\u51e0\u4e4e\u5df2\u4ee4\u4eba\u65e0\u6cd5\u60f3\u50cf\u3002 \u53ea\u542c\u201c\u54d7\u5566\u5566\uff0c\u53ee\u53ee\u5f53\u201d\u4e00\u7247\u54cd\uff0c\u94c1\u9505\u94dc\u7089\u7ffb\u5012\uff0c\u8fde\u4e00\u4e08\u5916\u7684\u684c\u6905\u4e5f\u88ab\u9707\u5012\uff0c\u684c\u4e0a \u7684\u676f\u76d8\u7897\u76cf\uff0c\u6709\u7684\u7ef0\u5728\u5730\u4e0a\uff0c\u6709\u7684\u5728\u684c\u4e0a\u5df2\u88ab\u9707\u788e\u3002 \u9646\u5c0f\u51e4\u7684\u4eba\u5c45\u7136\u4e5f\u88ab\u62f3\u98ce\u6253\u5f97\u98de\u4e86\u51fa\u53bb\uff0c\u98d8\u98d8\u8361\u8361\u7684\u98de\u8fc7\u4e09\u56db\u5f20\u957f\u684c\uff0c\u98de\u8fc7\u5341\u6765\u4e2a \u4eba\u7684\u5934\u9876\uff0c\u98de\u8fc7\u5341\u591a\u4e08\u957f\u7684\u5927\u5385\uff0c\u5c31\u50cf\u662f\u4e2a\u65ad\u4e86\u7ebf\u7684\u98ce\u7b5d\u3002 \u5927\u5385\u91cc\u7acb\u523b\u54cd\u8d77\u2014\u9635\u559d\u91c7\u58f0\uff0c\u5c06\u519b\u72ec\u7acb\u9ad8\u53f0\uff0c\u770b\u6765\u66f4\u5a01\u98ce\u51db\u51db\uff0c\u4e0d\u53ef\u4e00\u4e16\u3002 \u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u53ea\u542c\u201c\u547c\u201d\u7684\u4e00\u58f0\u98ce\u58f0\uff0c\u9646\u5c0f\u51e4\u5ffd\u7136\u53c8\u56de\u5230\u4e86\u4ed6\u9762\u524d\uff0c\u8138\u4e0a\u5c45\u7136\u8fd8 \u5e26\u7740\u5fae\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u4e00\u62f3\u6253\u5f97\u6211\u597d\u51c9\u5feb\uff0c\u518d\u6765\u4e00\u62f3\u5982\u4f55\uff1f\u201d \u5c06\u519b\u6012\u543c\uff0c\u8fde\u51fb\u4e09\u62f3\u3002 \u4ed6\u7684\u62f3\u6cd5\u7edd\u65e0\u82b1\u4fcf\uff0c\u4f46\u6bcf\u4e00\u62f3\u51fb\u51fa\uff0c\u90fd\u786e\u5b9e\u800c\u6709\u6548\u3002 \u8fd9\u4e09\u62f3\u7684\u529b\u91cf\u867d\u5df2\u4e0d\u5982\u7b2c\u4e00\u62f3\u5a01\u731b\uff0c\u5374\u8fdc\u6bd4\u7b2c\u4e00\u62f3\u5feb\u5f97\u591a\u3002 \u9646\u5c0f\u51e4\u53c8\u88ab\u6253\u5f97\u98de\u8d77\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u4e00\u6b21\u5e76\u6ca1\u6709\u98de\u51fa\u53bb\uff0c\u7a81\u7136\u51cc\u7a7a\u7ffb\u8eab\uff0c\u843d\u5728\u5c06\u519b\u8eab \u540e\u3002 \u5c06\u519b\u8eab\u5b50\u867d\u9b41\u4f1f\uff0c\u53cd\u5e94\u5374\u6781\u7075\u6d3b\uff0c\u52a8\u4f5c\u66f4\u5feb\uff0c\u5750\u9a6c\u62e7\u8170\uff0c\u9738\u738b\u5378\u7532\uff0c\u5c06\u519b\u8131\u888d\uff0c\u56de \u5f13\u5c04\u6708\uff0c\u8fde\u6e9c\u5e26\u6253\uff0c\u53c8\u662f\u4e09\u62db\u51fb\u51fa\u3002 \u8fd9\u672c\u662f\u62f3\u6cd5\u4e2d\u6700\u57fa\u672c\u666e\u901a\u7684\u62db\u5f0f\uff0c\u53ef\u662f\u5728\u4ed6\u624b\u4e0a\u4f7f\u51fa\u6765\uff0c\u5c31\u7edd\u4e0d\u662f\u666e\u901a\u4eba\u80fd\u62db\u67b6\u62b5 \u6321\u7684\u3002 \u5e78\u597d\u9646\u5c0f\u51e4\u4e5f\u4e0d\u662f\u666e\u901a\u4eba\uff0c\u8fd9\u4e16\u4e0a\u6839\u672c\u5c31\u518d\u4e5f\u627e\u4e0d\u51fa\u7b2c\u4e8c\u4e2a\u9646\u5c0f\u51e4\u3002 \u4ed6\u8eab\u5b50\u4e00\u95ea\uff0c\u7a81\u7136\u4ece\u5c06\u519b\u591c\u4e0b\u94bb\u8fc7\u53bb\uff0c\u7a81\u7136\u4f38\u624b\uff0c\u6258\u4f4f\u4e86\u5c06\u519b\u7684\u8098\uff0c\u4e00\u5934\u649e\u5728\u5c06\u519b \u7684\u808b\u9aa8\u4e0a\u3002 \u5c06\u519b\u4e00\u767e\u4e03\u5341\u4e09\u5385\u91cd\u7684\u8eab\u5b50\uff0c\u7adf\u88ab\u4ed6\u649e\u5f97\u8ddf\u8dea\u540e\u9000\uff0c\u51e0\u4e4e\u8dcc\u4e0b\u9ad8\u53f0\u3002 \u53ef\u662f\u9646\u5c0f\u51e4\u66f4\u5403\u60ca\u3002 \u4ed6\u5ffd\u7136\u53d1\u73b0\u8fd9\u4e2a\u4eba\u7adf\u6709\u4e00\u8eab\u6a2a\u7ec3\u529f\u592b\uff0c\u4ed6\u4e00\u5934\u649e\u4e0a\u53bb\uff0c\u5c31\u50cf\u662f\u649e\u5728\u77f3\u5934\u5899\u4e0a\uff0c\u649e\u5f97 \u5934\u90fd\u53d1\u4e86\u6655\u3002 \u5c31\u56e0\u4e3a\u5fc3\u60ca\u5934\u6655\uff0c\u6240\u4ee5\u4ed6\u7b11\u5f97\u58f0\u97f3\u66f4\u5927\uff0c\u5927\u7b11\u9053\uff1a\u201c\u4f60\u53c8\u8f93\u4e86\u3002\u201d\u5c06\u519b\u9053\uff1a\u201c\u653e \u5c41\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u4e00\u62f3\u5c31\u51e0\u4e4e\u628a\u4f60\u6253\u5012\uff0c\u4f60\u8fd8\u4e0d\u8ba4\u8f93\uff1f\u201d \u5c06\u519b\u9053\uff1a\u201c\u4f60\u7528\u7684\u662f\u4ec0\u4e48\u62f3\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5934\u62f3\u3002\u201d \u5c06\u519b\u9053\uff1a\u201c\u8fd9\u7b97\u662f\u54ea\u95e8\u529f\u592b\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6253\u67b6\u7684\u529f\u592b\uff0c\u53ea\u8981\u80fd\u628a\u5bf9\u65b9\u6253\u5012\uff0c\u968f\u4fbf\u4ec0\u4e48\u90fd\u53ef\u4ee5\u7528\u3002\u201d \u5c06\u519b\u51b7\u7b11\u9053\uff1a\u201c\u6211\u5012\u8981\u770b\u770b\u4f60\u8fd8\u80fd\u7528\u4ec0\u4e48\uff1f\u201d \u4ed6\u6c89\u8170\u5750\u9a6c\uff0c\u518d\u6b21\u51fa\u624b\uff0c\u62f3\u5f0f\u66f4\u5bc6\uff0c\u51fa\u624b\u66f4\u5feb\uff0c\u5b58\u5fc3\u8981\u5148\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\u3002 \u8fd9\u4e00\u6b21\u4ed6\u62f3\u6cd5\u65bd\u5c55\u5f00\uff0c\u624d\u770b\u5f97\u51fa\u4ed6\u7684\u771f\u529f\u592b\u3002 \u9646\u5c0f\u51e4\u6839\u672c\u653b\u4e0d\u8fdb\u53bb\u3002\u8fd9\u8d9f\u62f3\u6cd5\u5c55\u5f00\uff0c\u5929\u4e0b\u7edd\u9648\u6717\u6da7\u4eba\u80fd\u653b\u5f97\u8fdb\u53bb\u3002 \u9646\u5c0f\u51e4\u597d\u50cf\u4e5f\u60f3\u901a\u4e86\u8fd9\u4e00\u70b9\uff0c\u7d22\u6027\u653e\u5f03\u4e86\u653b\u52bf\uff0c\u8fdc\u8fdc\u7684\u9000\u5230\u77f3\u53f0\u7684\u89d2\u843d\u4e0a\uff0c\u5ffd\u7136\u5f2f \u4e0b\u8170\uff0c\u62b1\u8d77\u809a\u5b50\u3002\u201d\u4e0d\u884c\uff0c\u6211\u809a\u5b50\u75db\u5f97\u8981\u547d\u3002\u201d \u5176\u5b9e\u4ed6\u81ea\u5df1\u5f53\u7136\u4e5f\u77e5\u9053\uff0c\u5c31\u7b97\u4ed6\u809a\u5b50\u75db\u6b7b\uff0c\u4e5f\u6ca1\u6709\u4eba\u7ba1\u7684\u3002 \u5c06\u519b\u4e00\u4e2a\u7bad\u6b65\u7a9c\u8fc7\u53bb\uff0c\u6325\u62f3\u75db\u51fb\u3002 \u8c01\u77e5\u9646\u529b\u3001\u98ce\u5c31\u5728\u4ed6\u8eab\u5b50\u8e7f\u8d77\u65f6\uff0c\u4eba\u5df2\u6e38\u9c7c\u822c\u8d34\u7740\u77f3\u53f0\u4ece\u4ed6\u811a\u5e95\u6ed1\u8fc7\uff0c\u7a81\u7136\u53cc\u624b\u6309 \u5730\uff0c\u4e00\u4e2a\u9ca4\u9c7c\u6253\u633a\uff0c\u4e00\u5c41\u80a1\u649e\u5728\u4ed6\u5c41\u80a1\u4e0a\u3002 \u5c06\u519b\u672c\u5c31\u662f\u5168\u8eab\u8fdb\u51fb\uff0c\u54ea\u91cc\u8fd8\u80fd\u6536\u5f97\u4f4f\u52bf\uff0c\u8fd9\u4e00\u6b21\u7adf\u771f\u7684\u88ab\u4ed6\u649e\u4e0b\u77f3\u53f0\uff0c\u51e0\u4e4e\u4e00\u8e6c \u8dcc\u5012\u3002 \u9646\u5c0f\u51e4\u62cd\u638c\u5927\u7b11\uff0c\u2019\u9053\uff1a\u201c\u4f60\u53c8\u8f93\u4e86\u3002\u201d \u5c06\u519b\u7684\u8138\u53d1\u9752\uff0c\u5634\u5507\u53d1\u6296\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\u4f60\u4e3a\u4f55\u4e0d\u95ee\u6211\u7528\u7684\u4ec0\u4e48\u62f3\uff1f\u201d \u5c06\u519b\u4e0d\u95ee\uff0c\u4e0d\u5f00\u53e3\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u2018\u8fd9\u7528\u7684\u662f\u80a1\u62f3\u3002\u201d \u4ed6\u5fae\u7b11\u7740\uff0c\u53c8\u9053\uff1a\u201c\u4e0b\u6b21\u4f60\u82e5\u518d\u89c1\u5230\u8fde\u5c41\u80a1\u90fd\u80fd\u6253\u4eba\u7684\u89d2\u8272\uff0c\u6700\u597d\u8eb2\u5f97\u8fdc\u4e9b\uff0c\u56e0\u4e3a \u4f60\u4e00\u5b9a\u4e0d\u662f\u4ed6\u7684\u654c\u624b\u3002\u201d \u5c06\u519b\u7a81\u53c8\u5927\u543c\uff0c\u4e00\u62f3\u51fb\u51fa\uff0c\u8fd9\u6b21\u4ed6\u6253\u7684\u4e0d\u662f\u4eba\uff0c\u662f\u77f3\u53f0\u3002 \u7528\u9752\u77f3\u780c\u6210\u7684\u9ad8\u53f0\uff0c\u7adf\u88ab\u4ed6\u6253\u584c\u4e86\u4e00\u89d2\uff0c\u788e\u77f3\u4e71\u7bad\u822c\u98de \u51fa\u3002 \u4ed6\u8eab\u5b50\u4e5f\u8ddf\u7740\u98de\u8dc3\u800c\u8d77\uff0c\u4eba\u8fd8\u662f\u7a7a\u4e2d\uff0c\u5c31\u5df2\u51fb\u51fa\u4e86\u7b2c\u4e8c \u62f3\u3002 \u51cc\u7a7a\u4e0b\u51fb\u7684\u62db\u5f0f\uff0c\u5a01\u52bf\u867d\u731b\uff0c\u5374\u6700\u6613\u66b4\u9732\u81ea\u5df1\u7684\u5f31\u70b9\uff0c\u672c\u6765\u53ea\u80fd\u7528\u4e8e\u4ee5\u5f3a\u51fb\u5f31\u3002\u9646 \u5c0f\u51e4\u7edd\u4e0d\u6bd4\u4ed6\u5f31\uff0c\u4ed6\u8fd9\u4e00\u62db\u5b9e\u5728\u7528\u5f97\u6781\u9669\uff0c\u56e0\u4e3a\u4ed6\u65e9\u5c31\u7b97\u51c6\u4e86\u9646\u5c0f\u51e4\u5df2\u7ad9\u4e0d\u7a33\u3002 \u65e0\u8bba\u8c01\u90fd\u6ca1\u6cd5\u5b50\u5728\u5d29\u77f3\u53f0\u4e0a\uff0c\u4e71\u7bad\u4e2d\u7684\u788e\u77f3\u4e2d\u7ad9\u7a33\u7684\u3002 \u7ad9\u4e0d\u7a33\u5c31\u65e0\u6cd5\u8fd8\u51fb\uff0c\u4e0d\u80fd\u8fd8\u51fb\u5c31\u53ea\u6709\u9000\u8ba9\u95ea\u907f\uff0c\u65e0\u8bba\u600e\u4e48\u6837\u95ea\u907f\u3002\u90fd\u96be\u514d\u8981\u88ab\u4ed6\u62f3 \u98ce\u626b\u53ca\u3002 \u4ed6\u8fd9\u4e00\u62db\u7528\u5f97\u867d\u9669\uff0c\u5374\u6b63\u662f\u201c\u7f6e\u4e4b\u6b7b\u5730\u800c\u540e\u751f\u201d\u7684\u6740\u624b\uff01 \u9646\u5c0f\u51e4\u7684\u4f24\u8fd8\u6ca1\u6709\u597d\uff0c\u8eab\u5b50\u8fd8\u5f88\u5f31\uff0c\u4ee5\u5c06\u519b\u62f3\u98ce\u4e4b\u5f3a\uff0c\u4ed6\u7edd\u5bf9\u6328\u4e0d\u8d77\u3002 \u4ed6\u6ca1\u6709\u6328\u3002 \u4ed6\u5c45\u7136\u8fd8\u80fd\u53cd\u51fb\uff0c\u5728\u7edd\u5bf9\u4e0d\u53ef\u80fd\u53cd\u51fb\u7684\u60c5\u51b5\u4e0b\u51fa\u624b\u53cd \u51fb\u201d \u5c06\u519b\u8eab\u7ecf\u767e\u6218\uff0c\u51b3\u80dc\u4e8e\u77ac\u606f\u4e4b\u95f4\uff0c\u4ed6\u672c\u5df2\u7b97\u65e0\u9057\u7b56\u3002 \u53ea\u53ef\u60dc\u8fd9\u6b21\u4ed6\u7b97\u9519\u4e86\u4e00\u62db\u3002 \u9646\u5c0f\u51e4\u505a\u7684\u4e8b\uff0c\u672c\u5c31\u6709\u5f88\u591a\u90fd\u662f\u522b\u4eba\u8ba4\u4e3a\u7edd\u4e0d\u53ef\u80fd\u505a\u5230\u7684\u3002 \u8fd9\u4e00\u6b21\u4ed6\u7528\u7684\u65e2\u4e0d\u662f\u5934\u62f3\uff0c\u4e5f\u4e0d\u662f\u80a1\u62f3\uff0c\u800c\u662f\u4ed6\u7684\u624b\uff0c\u4ed6\u7684\u624b\u6307\uff01 \u72ec\u4e00\u65e0\u4e8c\u7684\u9646\u5c0f\u51e4\uff0c\u72ec\u4e00\u65e0\u4e8c\u7684\u7075\u7280\u6307\u3002 \u4ed6\u8eab\u5b50\u5ffd\u7136\u659c\u659c\u98de\u8d77\uff0c\u4f38\u51fa\u4e24\u6839\u624b\u6307\u6765\u8f7b\u8f7b\u4e00\u5f39\uff0c\u98df\u6307\u5f39\u5c06\u519b\u7684\u62f3\u5934\uff0c\u4e2d\u6307\u5f39\u7740\u4e86 \u5c06\u519b\u7684\u80f8\u819b\u3002 \u4e00\u51fb\u5c31\u53ef\u4ee5\u51fb\u788e\u77f3\u53f0\u7684\u94c1\u62f3\uff0c\u8fde\u94a2\u5200\u90fd\u780d\u4e0d\u5f00\u7684\u80f8\u819b\uff0c\u4ed6\u4e24\u6839\u624b\u6307\u5f39\u4e0a\u53bb\uff0c\u6709\u4ec0\u4e48 \u7528\uff1f \u6709\u7528\uff1f \u6ca1\u6709\u4eba\u80fd\u60f3\u8c61\u4ed6\u8fd9\u4e24\u6307\u4e00\u5f39\u7684\u529b\u91cf\uff1f \u5c06\u519b\u72c2\u543c\uff0c\u98de\u51fa\uff0c\u8dcc\u4e0b\uff0c\u91cd\u91cd\u7684\u8dcc\u5728\u788e\u77f3\u5806\u4e0a\u3002 \u5927\u5385\u91cc\u8fd8\u6709\u4e09\u5341\u516d\u4e2a\u4eba\uff0c\u90fd\u5728\u77aa\u7740\u9646\u5c0f\u51e4\uff0c\u773c\u775b\u91cc\u90fd\u5e26\u7740\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\u3002 \u9646\u5c0f\u51e4\u5728\u82e6\u7b11\u3002 \u4ed6\u53ea\u6709\u82e6\u7b11\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\u8fd9\u4e9b\u4eba\u7eb5\u7136\u4e0d\u662f\u5c06\u519b\u7684\u670b\u53cb\uff0c\u73b0\u5728\u4e5f\u5df2\u53d8\u6210\u4e86\u4ed6\u7684\u5bf9\u5934\u3002 \u4e00\u4e2a\u4eba\u521a\u5230\u4e86\u4e00\u4e2a\u964c\u751f\u7684\u5730\u65b9\uff0c\u5ffd\u7136\u95f4\u5c31\u7ed3\u4e0b\u4e86\u4e09\u5341\u516d\u4e2a\u5bf9\u5934\uff0c\u65e0\u8bba\u5bf9\u8c01\u6765\u8bf4\uff0c\u90fd \u7edd\u4e0d\u662f\u4ef6\u5f88\u6109\u5feb\u7684\u4e8b\u3002 \u4ed6\u53ea\u5e0c\u671b\u5c06\u519b\u4f24\u5f97\u4e0d\u592a\u91cd\u3002 \u7b49\u4ed6\u8f6c\u5934\u53bb\u770b\u65f6\uff0c\u672c\u6765\u5012\u5728\u788e\u77f3\u5806\u4e0a\u7684\u5c06\u519b\uff0c\u7ade\u5df2\u4e0d\u89c1 \u4ed6\u518d\u56de\u5934\uff0c\u5c31\u770b\u89c1\u4e00\u4e2a\u7070\u8863\u4eba\u6162\u6162\u7684\u5728\u5f80\u95e8\u5916\u8d70\uff0c\u5c06\u519b\u5c31\u5728\u8fd9\u4e2a\u4eba\u7684\u6000\u62b1\u91cc\u3002 \u4ee5\u9646\u5c0f\u51e4\u8033\u76ee\u4e4b\u7075\uff0c\u5c45\u7136\u6ca1\u6709\u53d1\u89c9\u8fd9\u4e2a\u4eba\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u66f4\u6ca1\u6709\u53d1\u89c9\u4ed6\u600e\u4e48\u80fd\u62b1\u8d70 \u5c06\u519b\uff0c\u5ffd\u7136\u95f4\u5c31\u5df2\u5230\u4e86\u95e8\u53e3\u3002 \u9646\u5c0f\u51e4\u2018\u6014\u4f4f\u3002 \u7070\u8863\u4eba\u5df2\u8d70\u51fa\u4e86\u95e8\u3002 \u5927\u5385\u91cc\u4e09\u5341\u516d\u4e2a\u4eba\u4e5f\u5168\u90fd\u7ad9\u8d77\u6765\uff0c\u8ddf\u7740\u4ed6\u6162\u6162\u7684\u8d70\u4e86\u51fa\u53bb\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u56de\u5934\u518d\u770b\u9646 \u5c0f\u51e4\u4e00\u773c\uff0c\u5c31\u597d\u50cf\u5df2\u7ecf\u5c06\u9646\u5c0f\u51e4\u5f53\u505a\u4e2a\u6b7b\u4eba\u3002 \u65e0\u8bba\u591a\u597d\u770b\u7684\u6b7b\u4eba\uff0c\u4e5f\u6ca1\u6709\u4eba\u613f\u610f\u591a\u770b\u4e00\u773c\u7684\u3002 \u9646\u5c0f\u51e4\u81ea\u5df1\u4e5f\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u597d\u50cf\u7ad9\u5728\u4e00\u5ea7\u575f\u5893\u91cc\uff0c\u6ca1\u6709\u4eba\uff0c\u6ca1\u6709\u58f0\u97f3\uff0c\u706f\u70ae\u867d\u7136\u8fd8 \u4eae\u7740\uff0c\u5374\u4eff\u4f5b\u4e5f\u5df2\u53d8\u5f97\u6bd4\u9ed1\u6697\u8fd8\u9ed1\u6697\u3002 \u5982\u679c\u4f60\u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\uff0c\u8fde\u4e00\u70b9\u5e0c\u671b\u90fd\u770b\u4e0d\u89c1\uff0c\u706f\u5149\u5bf9\u4f60\u53c8\u6709\u4ec0\u4e48\u7528\uff1f \u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u8fd8\u662f\u5446\u679c\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u52a8\u90fd\u6ca1\u6709\u5229\uff0c \u8fd9\u91cc\u672c\u5c31\u662f\u4e2a\u5b8c\u5168\u964c\u751f\u7684\u5730\u65b9\uff0c\u4ed6\u80fd\u5f80\u54ea\u91cc\u8d70\uff1f\u4ed6\u672c\u5df2\u8d70\u4eba\u4e86\u7edd\u8def\uff0c\u8fd8\u80fd\u5f80\u54ea\u91cc \u53bb\uff1f \u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u770b\u89c1\u4e86\u4e00\u53cc\u773c\u775b\uff0c\u4e00\u53ea\u624b\u3002 \u4e00\u53ea\u53c8\u767d\u53c8\u5c0f\u7684\u624b\uff0c\u4e00\u53cc\u5e26\u7740\u7b11\u7684\u773c\u775b\uff0c\u53f6\u7075\u6b63\u5728\u95e8\u5916\u5411\u4ed6\u62db\u624b\u3002 \u9646\u5c0f\u51e4\u7acb\u523b\u8d70\u8fc7\u53bb\uff0c \u5c31\u7b97\u95e8\u5916\u6709\u4e00\u5343\u4e2a\u9677\u4e95\uff0c\u4e00\u4e07\u79cd\u57cb\u4f0f\u5728\u7b49\u7740\u4ed6\uff0c\u4ed6\u4e5f\u4f1a\u6beb\u4e0d\u8fdf\u7591\u7684\u8d70\u51fa\u53bb\u3002 \u56e0\u4e3a\u4ed6\u5ffd\u7136\u53d1\u89c9\uff0c\u90a3\u79cd\u7edd\u671b\u800c\u65e0\u52a9\u7684\u5b64\u72ec\uff0c\u8fdc\u6bd4\u6b7b\u8fd8\u53ef\u6015\u5f97\u591a\u3002 \u95e8\u5916\u4ec0\u4e48\u90fd\u6ca1\u6709\uff0c\u53ea\u6709\u4e006\u4e2a\u4eba\uff0c\u4e00\u7247\u9ed1\u6697\u3002 \u53f6\u7075\u7684\u773c\u775b\u7eb5\u7136\u5728\u9ed1\u6697\u4e2d\u770b\u6765\uff0c\u8fd8\u662f\u4eae\u5f97\u50cf\u79cb\u591c\u4e2d\u5347\u8d77\u7684\u7b2c\u4e00\u9897\u661f\u3002 \u5979\u770b\u7740\u9646\u5c0f\u51e4\uff0c\u5fae\u7b11\u7740\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u606d\u559c\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u4e0d\u61c2\u3002\u201d\u4e3a\u4ec0\u4e48\u606d\u559c\u6211\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u8fd8\u6ca1\u6709\u6b7b\uff0c\u4e00\u4e2a\u4eba\u53ea\u8981\u80fd\u6d3b\u7740\uff0c\u5c31\u662f\u4ef6\u53ef\u8d3a\u53ef\u559c\u7684\u4e8b\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u672c\u6765\u6211\u5df2\u8be5\u6b7b\u4e86\uff1f\u201d \u53f6\u7075\u70b9\u70b9\u5934\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u73b0\u5728\u4f60\u81f3\u5c11\u8fd8\u80fd\u5728\u5e7d\u7075\u5c71\u5e84\u91cc\u6d3b\u4e0b\u53bb\u3002\u201d \u9646\u5c0f\u51e4\u5410\u51fa\u53e3\u6c14\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u95ee\u9053\uff1a\u201c\u521a\u624d\u90a3\u7070\u8863\u4eba\u662f\u8c01\uff1f\u201d \u65f6\u7075\u9053\uff1a\u201c\u4f60\u731c\u4e0d\u51fa\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u662f\u8001\u5200\u628a\u5b50\uff1f\u201d \u53f6\u7075\u773c\u6ce2\u8f6c\u4e86\u8f6c\uff0c\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4ed6\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u662f\u4e2a\u53ef\u6015\u7684\u4eba\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u4ed6\u7684\u6b66\u529f\u600e\u4e48\u6837\uff1f\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u770b\u4e0d\u51fa\u3002\u201d\u53f6\u7075\u9053\uff1a\u201c\u8fde\u4f60 \u90fd\u770b\u4e0d\u51fa\u3002\u201d\u9646\u5c0f\u51e4\u53f9\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u8fde\u6211\u90fd\u770b\u4e0d\u51fa\uff0c\u6240\u4ee5\u624d\u53ef\u6015\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u8001\u5200\u628a\u5b50\u5e94\u8be5\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\uff1e\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5f53\u7136\u662f\u4e2a\u5f88\u53ef\u6015\u7684\u4eba\uff01\u201d \u53f6\u7075\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u4ed6\u5f53\u7136\u5c31\u662f\u8001\u5200\u628a\u5b50\uff0c\u4f60\u6839\u672c\u5c31\u4e0d\u5fc5\u95ee\u7684\u3002\u201d \u9646\u5c0f\u51e4\u4e5f\u5728\u7b11\uff0c\u7b11\u5bb9\u770b\u6765 \u7b2c\u516d\u7ae0\u3000\u56db\u9762\u695a\u6b4c>> \u53e4\u9f99\u300a\u9646\u5c0f\u51e4\u7cfb\u5217\xb7\u5e7d\u7075\u5c71\u5e84\u300b \u7b2c\u516d\u7ae0\u3000\u56db\u9762\u695a\u6b4c \u7b2c\u4e8c\u5929\u65e9\u4e0a\uff0c\u5c71\u8c37\u91cc\u8fd8\u662f\u6d53\u96fe\u8ff7\u6f2b\uff0c\u5c0f\u6728\u5c4b\u5c31\u597d\u50cf\u98d8\u6d6e\u5728\u4e91\u5806\uff0c\u63a8\u5f00\u95e8\u770b\u51fa\u53bb\uff0c\u8fde \u81ea\u5df1\u7684\u4eba\u90fd\u89c9\u5f97\u98d8\u98d8\u6d6e\u6d6e\u7684\uff0c\u53c8\u50cf\u662f\u6c34\u4e0a\u7684\u4e00\u7247\u6d6e\u840d\u3002 \u8fd9\u4e16\u4e0a\u5c82\u975e\u672c\u5c31\u6709\u5f88\u591a\u4eba\u50cf\u662f\u6d6e\u840d\u4e00\u6837\uff0c\u6ca1\u6709\u5bc4\u6258\uff0c\u4e5f\u6ca1\u6709\u6839\u3002 \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u91cd\u91cd\u7684\u5173\u4e0a\u95e8\uff0c\u60c5\u7eea\u4f4e\u843d\u5f97\u7b80\u76f4\u5c31\u50cf\u662f\u4e2a\u521a\u770b\u89c1\u81ea\u5df1\u60c5\u4eba\u4e0a\u4e86\u522b \u5bb6\u82b1\u8f7f\u7537\u5b69\u5b50\u3002 \u8fd9\u5929\u65e9\u4e0a\u552f\u4e00\u4ee4\u4ed6\u89c9\u5f97\u6709\u70b9\u6109\u5feb\u7684\u58f0\u97f3\uff0c\u5c31\u662f\u9001\u996d\u7684\u6572\u95e8\u58f0\u3002 \u9001\u996d\u6765\u7684\u662f\u4e2a\u9ebb\u5b50\uff0c\u9762\u76ee\u5446\u677f\uff0c\u6ee1\u5634\u9ec4\u7259\uff0c\u5168\u8eab\u4e0a\u4e0b\u552f\u2014\u4ee4\u4eba\u89c9\u5f97\u6709\u70b9\u6109\u5feb\u7684\u5730 \u65b9\uff0c\u5c31\u662f\u4ed6\u7684\u63d0\u7740\u7684\u4e00\u4e2a\u5927\u98df\u76d2\u3002 \u98df\u76d2\u91cc\u56fa\u7136\u6709\u516d\u83dc\u4e00\u6c64\uff0c\u5916\u5e26\u767d\u996d\u3002\u516d\u4e2a\u5927\u789f\u5b50\u91cc\u88c5\u7740\u7684\uff0c\u679c\u7136\u662f\u9646\u5c0f\u51e4\u6628\u5929\u665a\u4e0a \u70b9\u7684\u83dc\u3002 \u53ef\u662f\u6bcf\u6837\u8336\u90fd\u53ea\u6709\u4e00\u5757\uff0c\u5c0f\u5c0f\u7684\u4e00\u5757\uff0c\u773c\u775b\u4e0d\u597d\u7684\u4eba\uff0c\u8fde\u770b\u90fd\u770b\u4e0d\u89c1\uff0c\u98ce\u82e5\u5927\u4e86 \u4e9b\uff0c\u7acb\u523b\u5c31\u4f1a\u88ab\u5439\u8d70\u3002 \u6700\u7edd\u7684\u662f\u90a3\u6837\u4e09\u9c9c\u9e2d\u5b50\uff0c\u53ea\u6709\u4e00\u6839\u9aa8\u5934\uff0c\uff0c\u4e00\u5757\u9e2d\u76ae\uff0c\u2014\u6839\u9e2d\u6bdb\u3002 \u9646\u5c0f\u51e4\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u8fd9\u5c31\u662f\u4e09\u9c9c\u9e2d\u5b50\uff1f\u201d \u9ebb\u5b50\u5c45\u7136\u77aa\u8d77\u4e86\u773c\uff0c\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u9e2d\u5b50\u662f\u4ec0\u4e48\uff0c\u96be\u9053\u662f\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5c31\u7b97\u8fd9\u662f\u9e2d\u5b50\uff0c\u4e09\u9c9c\u5462\uff1f\u201d \u9ebb\u5b50\u9053\uff1a\u201c\u9e2d\u6bdb\u662f\u521a\u62d4\u4e0b\u6765\u7684\uff0c\u9e2d\u76ae\u662f\u521a\u5265\u4e0b\u6765\u7684\uff0c\u9e2d\u9aa8\u5934\u4e5f\u65b0\u9c9c\u5f97\u5f88\uff0c\u4f60\u8bf4\u8fd9\u4e0d \u662f\u4e09\u9c9c\u662f\u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u51e4\u53ea\u6709\u95ed\u4e0a\u5634\u3002 \u9ebb\u5b50\u5df2\u201c\u7830\u201d\u7684\u4e00\u58f0\u5173\u4e0a\u95e8\uff0c\u626c\u957f\u800c\u53bb\u3002 \u9646\u5c0f\u51e4\u770b\u7740\u9762\u524d\u7684\u516d\u6837\u83dc\uff0c\u518d\u770b\u7740\u7897\u91cc\u7684\u4e00\u9897\u996d\uff0c\u4e5f\u4e0d\u77e5\u662f\u8be5\u5927\u54ed\u4e09\u58f0\uff0c\u8fd8\u662f\u5927\u7b11 \u4e09\u58f0\u3002 \u76f4\u5230\u73b0\u5728\u4ed6\u603b\u7b97\u624d\u660e\u767d\uff0c\u90a3\u4f4d\u6e38\u9b42\u5148\u751f\u4e3a\u4ec0\u4e48\u4f1a\u5bf9\u9e21\u9aa8\u5934\u90a3\u6837\u6709\u5174\u8da3\u4e86\u3002 \u4ed6\u62ff\u8d77\u7b77\u5b50\uff0c\u53c8\u653e\u4e0b\uff0c\u5ffd\u7136\u542c\u89c1\u540e\u9762\u7684\u5c0f\u7a97\u5916\u6709\u4eba\u5728\u53f9\u6c14\uff1a\u201c\u4f60\u8fd9\u5757\u7ea2\u70e7\u8e0a\u8180\uff0c\u6bd4 \u6211\u6628\u5929\u7684\u8fd8\u5927\u4e9b\uff0c\u81f3\u5c11\u5927\u4e00\u500d\u3002\u201d \u9646\u5c0f\u51e4\u7528\u4e0d\u7740\u56de\u5934\uff0c\u5c31\u77e5\u9053\u90a3\u4f4d\u6e38\u9b42\u5148\u751f\u53c8\u6765\u4e86\uff0c\u5fcd\u4e0d\u4f73\u95ee\u9053\uff1a\u201c\u8fd9\u79cd\u4f19\u98df\u4f60\u5df2\u7ecf \u5403\u4e86\u591a\u4e45\uff1f\u201d \u6e38\u9b42\u9053\uff1a\u201c\u4e09\u4e2a\u6708\u3002\u201d \u4ed6\u4e00\u4e0b\u5b50\u5c31\u4ece\u7a97\u5916\u94bb\u4e86\u8fdb\u6765\uff0c\u4e00\u53cc\u773c\u775b\u76f4\u52fe\u52fe\u7684\u770b\u7740\u684c\u4e0a\u516d\u6837\u83dc\uff0c\u53c8\u9053\uff1a\u201c\u5403\u8fd9\u79cd \u4f19\u98df\u6709\u4e2a\u79d8\u8bc0\u3002\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ec0\u4e48\u79d8\u8bc0\u3002\u201d \u6e38\u9b42\u9053\uff1a\u201c\u6bcf\u6837\u83dc\u90fd\u4e00\u5b9a\u8981\u6162\u6162\u5403\uff0c\u6700\u597d\u662f\u7528\u95e8\u7259\u53bb\u6162\u6162\u7684\u78e8\uff0c\u518d\u7528\u820c\u5934\u53bb\u8214\uff0c\u624d \u53ef\u4ee5\u5c1d\u51fa\u6ecb\u5473\u6765\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ef\u662f\u4f60\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u6e38\u9b42\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u8fd8\u4e0d\u60f3\u6b7b\uff0c\u522b\u4eba\u8d8a\u60f3\u8981\u6211\u6b7b\uff0c\u6211\u5c31\u8d8a\u8981\u6d3b\u4e0b\u53bb\uff0c\u6d3b\u7ed9\u4ed6\u4eec\u770b\u3002\u201d \u9646\u5c0f\u51e4\u4e5f\u4e0d\u7981\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u80fd\u6d3b\u5230\u73b0\u5728\uff0c\u4e00\u5b9a\u5f88\u4e0d\u5bb9\u6613\u3002 \u6e38\u9b42\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u773c\u89d2\u5ffd\u7136\u6709\u4e24\u6ef4\u773c\u6cea\u6d41\u4e86\u4e0b\u6765\u3002 \u9646\u5c0f\u51e4\u4e0d\u5fcd\u518d\u770b\uff0c\u4e00\u5934\u5012\u5728\u5e8a\u4e0a\uff0c\u7528\u68b3\u5934\u76d6\u4f4f\u4e86\u3002 \u6e38\u9b42\u9053\uff1a\u201c\u996d\u5df2\u9001\u6765\u4e86\uff0c\u4f60\u8fd8\u4e0d\u5403\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u5403\u5427\uff0c\u6211\u4e0d\u997f\u3002\u201d \u6e38\u9b42\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u4e5f\u5f97\u6d3b\u4e0b\u53bb\u3002\u201d \u4ed6\u5ffd\u7136\u4e00\u628a\u6380\u8d77\u9646\u5c0f\u51e4\u7684\u6795\u5934\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u82e5\u60f3\u6b7b\uff0c\u5012\u4e0d\u5982\u73b0\u5728\u5c31\u8ba9\u6211\u4e00\u62f3\u628a\u4f60 \u6253\u6b7b\uff0c\u56e0\u4e3a\u4f60\u73b0\u5728\u8eab\u4e0a\u8fd8\u6709\u8089\uff0c\u8fd8\u53ef\u4ee5\u8ba9\u6211\u75db\u75db\u5feb\u5feb\u7684\u5403\u51e0\u987f\u3002\u201d \u9646\u5c0f\u51e4\u770b\u7740\u4ed6\uff0c\u770b\u7740\u4ed6\u90a3\u5f20\u5df2\u53ea\u5269\u4e0b\u4e00\u5c42\u76ae\u5305\u7740\u9aa8\u5934\u7684\u8138\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u59d3\u9646\uff0c\u53eb \u9646\u5c0f\u51e4\u3002\u201d \u6e38\u9b42\u9053\uff1a\u201c\u6211\u77e5\u9053\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u662f\u8c01\uff1f\u600e\u4e48\u4f1a\u5230\u8fd9\u91cc\u6765\u7684\uff1f\u201d \u8fd9\u4e00\u6b21\u6e38\u9b42\u5c45\u7136\u5e76\u6ca1\u6709\u663e\u5f97\u6fc0\u52a8\uff0c\u53ea\u662f\u7528\u4e00\u53cc\u5df2\u9ab7\u9ac5\u822c\u6df1\u51f9\u4e0b\u53bb\u7684\u773c\u775b\u76ef\u7740\u9646\u5c0f \u51e4\uff0c\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u53c8\u662f\u600e\u4f1a\u9053\u8fd9\u91cc\u6765\u7684\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u201d \u6e38\u9b42\u62a2\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u505a\u4e86\u9519\u4e8b\uff0c\u5df2\u88ab\u4eba\u903c\u5f97\u65e0\u8def\u53ef\u8d70\uff0c\u53ea\u80fd\u8d70\u4e0a\u8fd9\u6761\u6b7b\u8def\u3002\u201d \u9646\u5c0f\u51e4\u627f\u8ba4\u3002 \u6e38\u9b42\u9053\uff1a\u201c\u73b0\u5728\u6c5f\u6e56\u4e2d\u4eba\u4e00\u5b9a\u90fd\u8ba4\u4e3a\u4f60\u5df2\u6b7b\u4e86\uff0c\u897f\u95e8\u5439\u96ea\u4e00\u5b9a\u4e5f\u8ba4\u4e3a\u4f60\u5df2\u6b7b\u4e86\uff0c\u6240 \u4ee5\u4f60\u624d\u80fd\u5728\u8fd9\u91cc\u6d3b\u4e0b\u53bb\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u5462\uff1f \u6e38\u9b42\u9053\uff1a\u201c\u6211\u4e5f\u4e00\u6837\u3002\u201d \u4ed6\u53c8\u8865\u5145\u7740\u9053\uff1a\u201c\u5c06\u519b\u3001\u8868\u54e5\u3001\u94a9\u5b50\u3001\u7ba1\u5bb6\u5a46\u2026\u2026\u8fd9\u4e9b\u4eba\u7684\u60c5\u51b5\u4e5f\u5168\u90fd\u4e00\u6837ao \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ef\u662f\u6211\u5e76\u4e0d\u6015\u8ba9\u4ed6\u4eec\u77e5\u9053\u6211\u7684\u6765\u5386\u5e95\u7ec6\u3002\u201d \u6e38\u9b42\u9053\uff1a\u201c\u4ed6\u4eec\u5374\u6015\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u6e38\u9b42\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4eec\u8fd8\u4e0d\u4fe1\u4efb\u4f60\uff0c\u4ed6\u4eec\u7edd\u4e0d\u80fd\u8ba9\u4efb\u4f55\u4eba\u77e5\u9053\u4ed6\u4eec\u8fd8\u6d3b\u7740\uff0c\u5426 \u5219\u2026\u2026\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5426\u5219\u4ed6\u4eec\u7684\u4ec7\u5bb6\u5f88\u53ef\u80fd\u5c31\u4f1a\u8ffd\u8e2a\u5230\u8fd9\u91cc\u3002\u201d \u6e38\u9b42\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u4e5f\u4e0d\u4fe1\u4efb\u6211\uff1f\u201d \u6e38\u9b42\u9053\uff1a\u201c\u6211\u5c31\u7b97\u4fe1\u4efb\u4f60\uff0c\u4e5f\u4e0d\u80fd\u628a\u6211\u7684\u6765\u5386\u544a\u8bc9\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u6e38\u9b42\u773c\u775b\u91cc\u5ffd\u7136\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u4e5f\u4e0d\u77e5\u662f\u6050\u60e7\uff1f\u8fd8\u662f\u75db\u82e6\uff1f \u201c\u6211\u4e0d\u80fd\u8bf4\uff0c\u7edd\u4e0d\u80fd\u2026\u2026\u201d \u4ed6\u5634\u91cc\u5583\u5583\u81ea\u8bed\uff0c\u4eff\u4f5b\u5728\u8b66\u544a\u81ea\u5df1\uff0c\u4ed6\u7684\u8eab\u5b50\u53c8\u5df2\u5e7d\u7075\u822c\u98d8\u8d77\u3002 \u53ef\u662f\u8fd9\u2014\u6b21\u9646\u5c0f\u51e4\u5df2\u51b3\u5fc3\u4e0d\u8ba9\u4ed6\u8d70\u4e86\uff0c\u95ea\u7535\u822c\u63e1\u4f4f\u4ed6\u7684\u624b\uff0c\u518d\u95ee\u2014\u904d\uff1a\u201c\u4e3a\u4ec0 \u4e48\uff1f\u201d \u201c\u56e0\u4e3a\u2026\u2026\u201d\u6e38\u9b42\u7ec8\u4e8e\u4e0b\u4e86\u51b3\u5fc3\uff0c\u54ac\u7740\u7259\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u82e5\u8bf4\u51fa\u6765\uff0c\u6211\u4eec\u5c31\u7edd\u4e0d\u4f1a\u518d \u662f\u670b\u53cb\u3002\u201d \u9646\u5c0f\u51e4\u8fd8\u662f\u4e0d\u61c2\uff0c\u8fd8\u662f\u8981\u95ee\uff0c\u8c01\u77e5\u6e38\u9b42\u90a3\u53ea\u67af\u7626\u5e72\u786c\u7684\u624b\u7adf\u7a81\u7136\u53d8\u5f97\u67d4\u8f6f\u5982\u4e1d\u7ef5\uff0c \u7adf\u7136\u4ece\u4ed6\u638c\u63e1\u4e2d\u6323\u8131\u3002 \u4ece\u6ca1\u6709\u4efb\u4f55\u4eba\u7684\u624b\u80fd\u4ece\u9646\u5c0f\u51e4\u638c\u63e1\u4e2d\u6323\u8131\u3002 \u4ed6\u518d\u51fa\u624b\u65f6\uff0c\u6e38\u795e\u5df2\u94bb\u51fa\u7a97\u6237\uff0c\u771f\u7684\u5c31\u50cf\u662f\u4e00\u7f15\u6e38\u8361\u7684\u9b42\u9b44\u3002 \u9646\u5c0f\u51e4\u6014\u4f4f\u3002 \u4ed6\u4ece\u6ca1\u6709\u89c1\u8fc7\u4efb\u4f55\u4eba\u7684\u8f6f\u529f\u80fd\u7ec3\u5230\u8fd9\u4e00\u6b65\uff0c\u4e5f\u8bb8\u4ed6\u542c\u8bf4\u8fc7\uff0c\u4ed6\u597d\u50cf\u542c\u53f8\u7a7a\u6458\u661f\u63d0\u8d77 \u8fc7\uff0c\u53ef\u662f\u8fde\u8fd9\u79cd\u8bb0\u5fc6\u90fd\u5df2\u5f88\u6a21\u7cca\u3002 \u6240\u6709\u7684\u8bb0\u5fc6\u90fd\u6e10\u6e10\u6a21\u7cca\uff0c\u9646\u5c0f\u51e4\u88ab\u5173\u5728\u8fd9\u6728\u5c4b\u91cc\u5df2\u6709\u4e24 \u5c24\u5176\u662f\u4e24\u5929\uff1f\u4e09\u5929\uff1f\u8fd8\u662f\u56db\u5929\uff1f\u4ed6\u4e5f\u5df2\u8bb0\u4e0d\u6e05\u4e86\u3002\u539f\u6765\u9965\u997f\u4e0d\u4f46\u80fd\u4f7f\u4eba\u4f53\u529b\u8870\u9000\uff0c\u8fd8 \u80fd\u635f\u4f24\u4eba\u7684\u8111\u529b\uff0c\u8ba9\u4eba\u53ea\u80fd\u60f3\u8d77\u4e00\u4e9b\u4e0d\u8be5\u60f3\u7684\u4e8b\uff0c\u5374\u5c06\u6240\u6709\u5e94\u8be5\u53bb\u60f3\u7684\u4e8b\u5168\u90fd\u5fd8\u8bb0\u3002 \u4e00\u4e2a\u4eba\u5b64\u5b64\u5355\u5355\u7684\u8eba\u5728\u4e2a\u9e3d\u5b50\u7b3c\u822c\u7684\u5c0f\u6728\u5c4b\u6328\u997f\uff0c\u8fd9\u79cd\u75db\u82e6\u8c01\u80fd\u5fcd\u53d7\u3002 \u53ef\u662f\u542c\u5230\u5916\u9762\u6709\u949f\u58f0\u54cd\u8d77\u7684\u65f6\u5019\uff0c\u4ed6\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u9ad8\u5174\u5f97\u8df3\u4e86\u8d77\u6765\u3002 \u201c\u949f\u58f0\u4e0d\u54cd\uff0c\u4e0d\u8bb8\u51fa\u6765\u3002\u201d \u73b0\u5728\u949f\u58f0\u5df2\u54cd\u4e86\uff0c\u4ed6\u8df3\u8d77\u6765\uff0c\u51b2\u51fa\u53bb\uff0c\u8fde\u9774\u5b50\u90fd\u6765\u4e0d\u53ca\u5957\u4e0a\u5c31\u51b2\u4e86\u51fa\u53bb\u3002 \u5916\u9762\u4ecd\u6709\u96fe\uff0c\u6b64\u523b\u6b63\u9ec4\u660f\u3002 \u5915\u9633\u5728\u8ff7\u96fe\u4e2d\u6620\u6210\u4e00\u73af\u4e03\u8272\u5149\u5708\u3002 \u8fd9\u4e16\u754c\u6bd5\u7adf\u8fd8\u662f\u7f8e\u4e3d\u7684\uff0c\u80fd\u6d3b\u7740\u6bd5\u7adf\u662f\u4ef6\u5f88\u6109\u5feb\u7684\u4e8b\u3002 \u5927\u5385\u91cc\u8fd8\u662f\u53ea\u6709\u4e09\u5341\u516d\u4e03\u4e2a\u4eba\uff0c\u9646\u5c0f\u51e4\u8fde\u4e00\u4e2a\u90fd\u4e0d\u8ba4\u5f97\u3002 \u4ed6\u89c1\u8fc7\u7684\u4eba\u5168\u90fd\u4e0d\u5728\u8fd9\u91cc\uff0c\u52fe\u9b42\u4f7f\u8005\u3001\u5c06\u519b\u3001\u6e38\u9b42\u3001\u65f6\u7075\uff0c\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u90fd\u6ca1\u6709\u6765\uff1f \u8fd8\u6709\u72ec\u5b64\u7f8e\uff0c\u4e3a\u4ec0\u4e48\u4e00\u8fdb\u4e86\u8fd9\u5c71\u8c37\u5c31\u4e0d\u89c1\u8e2a\u5f71\uff1f \u9646\u5c0f\u51e4\u5728\u89d2\u843d\u91cc\u627e\u4e2a\u4f4d\u5b50\u5750\u4e0b\u6765\uff0c\u6ca1\u6709\u4eba\u7406\u4ed6\uff0c\u751a\u81f3\u8fde\u591a\u770b\u4ed6\u4e00\u773c\u7684\u4eba\u90fd\u6ca1\u6709\uff0c\u6bcf \u4e2a\u4eba\u7684\u8138\u8272\u90fd\u5f88\u4e25\u8083\uff0c\u5fc3\u60c5\u597d\u50cf\u90fd\u5f88\u6c89\u91cd\u3002 \u751f\u6d3b\u5728\u8fd9\u5730\u65b9\u7684\u4eba\uff0c\u4e5f\u8bb8\u672c\u6765\u5c31\u662f\u8fd9\u6837\u5b50\u7684\u3002 \u9646\u5c0f\u51e4\u5728\u5fc3\u91cc\u53f9\u4e86\u53e3\u6c14\uff0c\u62ac\u8d77\u5934\u5f80\u524d\u770b\uff0c\u624d\u53d1\u73b0\u672c\u6765\u6446\u7740\u8089\u9505\u7684\u9ad8\u53f0\uff0c\u73b0\u5728\u6446\u7740\u7684 \u7adf\u662f\u53e3\u68fa\u6750\u3002 \u5d2d\u65b0\u7684\u68fa\u6750\uff0c\u8fd8\u6ca1\u6709\u9489\u4e0a\u76d6\u3002 \u6b7b\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u662f\u4e0d\u662f\u5c06\u519b\uff1f\u4ed6\u4eec\u627e\u9646\u5c0f\u51e4\u6765\uff0c\u662f\u4e0d\u662f\u4e3a\u4e86\u8981\u66ff\u5c06\u519b\u590d\u4ec7\uff1f \u9646\u5c0f\u51e4\u5fc3\u91cc\u6b63\u6709\u70b9\u5fd7\u745f\u4e0d\u5b9a\uff0c\u5c31\u770b\u89c1\u53f6\u7075\u4ece\u5916\u9762\u51b2\u4e86\u8fdb\u6765\u3002 \u8fd9\u4e2a\u7231\u7a7f\u7ea2\u8863\u88f3\u53c8\u7231\u7b11\u7684\u5c0f\u5973\u5b69\uff0c\u73b0\u5728\u7a7f\u7684\u7adf\u662f\u4ef6\u767d\u9ebb\u5b5d\u670d\uff0c\u800c\u4e14\u5c45\u7136\u54ed\u4e86\uff0c\u54ed\u5f97 \u5f88\u4f24\u5fc3\u3002 \u5979\u4e00\u51b2\u8fdb\u6765\uff0c\u5c31\u6251\u5012\u5728\u68fa\u6750\u4e0a\u54ed\u4e2a\u4e0d\u505c\u3002 \u9646\u5c0f\u51e4\u4ece\u6765\u4e5f\u6ca1\u6709\u60f3\u5230\u8fc7\u5979\u4f1a\u4e3a\u522b\u4eba\u54ed\u5f97\u8fd9\u4e48\u4f24\u5fc3\uff0c\u5979\u8fd8\u5e74\u8f7b\uff0c\u6d3b\u6cfc\u800c\u7f8e\u4e3d\uff0c\u90a3\u4e9b \u60b2\u4f24\u548c\u4e0d\u5e78\u7684\u4e8b\uff0c\u597d\u50cf\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u964d\u4e34\u5230\u5979\u8eab\u4e0a\u7684\u3002 \u6b7b\u7684\u662f\u5979\u4ec0\u4e48\u4eba\uff1f\u600e\u4e48\u4f1a\u6b7b\u7684\uff1f \u9646\u5c0f\u51e4\u6b63\u51c6\u5907\u4ee5\u540e\u627e\u4e2a\u673a\u4f1a\u53bb\u5b89\u6170\u5b89\u6170\u5979\uff0c\u8c01\u77e5\u5979\u5df2\u7ecf\u5728\u547c\u5524\uff1a\u201c\u9646\u5c0f\u51e4\uff0c\u4f60\u8fc7 \u6765\u3002\u201d \u9646\u5c0f\u51e4\u53ea\u6709\u8fc7\u53bb\u3002 \u4ed6\u731c\u4e0d\u5230\u53f6\u7075\u4e3a\u4ec0\u4e48\u4f1a\u5ffd\u7136\u53eb\u4ed6\u8fc7\u53bb\uff0c\u4ed6\u4e0d\u60f3\u8d70\u5f97\u592a\u8fd1\u3002 \u53ef\u662f\u53f6\u7075\u5374\u5728\u4e0d\u505c\u7684\u50ac\u4fc3\uff0c\u53eb\u4ed6\u8d70\u5feb\u4e9b\uff0c\u8d70\u8fd1\u4e9b\uff0c\u8d70\u5230\u77f3\u53f0\u4e0a\u53bb\u3002 \u4ed6\u6307\u8d77\u5934\uff0c\u624d\u53d1\u73b0\u5979\u6b63\u7528\u4e00\u53cc\u542b\u6cea\u7684\u773c\u775b\u5728\u72e0\u72e0\u7684\u76ef\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u5145\u6ee1\u654c\u610f\u3002 \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f73\u95ee\uff1a\u201c\u4f60\u8981\u6211\u4e0a\u53bb\uff1f\u201d \u53f6\u7075\u5728\u70b9\u5934\u3002 \u9646\u5c0f\u51e4\u53c8\u95ee\uff1a\u201c\u4e0a\u53bb\u5e72\u4ec0\u4e48\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u4e0a\u6765\u770b\u770b\u4ed6\u3002\u201d \u201c\u4ed6\u201d\u5f53\u7136\u5c31\u662f\u8eba\u5728\u68fa\u6750\u91cc\u7684\u4eba\uff0c\u4e00\u4e2a\u4eba\u82e5\u5df2\u8fdb\u4e86\u68fa\u6750\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff1f \u53ef\u662f\u5979\u7684\u6001\u5ea6\u5374\u5f88\u575a\u51b3\uff0c\u597d\u50cf\u975e\u8981\u9646\u5c0f\u51e4\u4e0a\u53bb\u770b\u770b\u4e0d\u53ef\u3002 \u9646\u5c0f\u51e4\u53ea\u6709\u4e0a\u53bb\u3002 \u53f6\u7075\u6380\u8d77\u4e86\u68fa\u76d6\uff0c\u4e00\u9635\u6df7\u5408\u7740\u6d53\u9999\u548c\u6076\u81ed\u7684\u6c14\u5473\u7acb\u523b\u6251\u9f3b\u800c\u6765\uff0c\u68fa\u6750\u91cc\u7684\u4eba\u51e0\u4e4e\u5df2 \u5b8c\u5168\u6d6e\u80bf\u8150\u70c2\uff0c\u5979\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u9646\u5c0f\u51e4\u6765\u770b\uff1f \u9646\u5c0f\u51e4\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u5c31\u5df2\u5fcd\u4e0d\u4f4f\u8981\u5455\u5410\u3002 \u8fd9\u4e2a\u4eba\u8d6b\u7136\u7adf\u662f\u53f6\u5b64\u9e3f\u6b7b\u5728\u90a3\u5403\u4eba\u4e1b\u6797\u4e2d\u7684\u53f6\u5b64\u9e3fJ \u53f6\u7075\u54ac\u7740\u7259\uff0c\u72e0\u72e0\u7684\u76ef\u7740\u9646\u5c0f\u51e4\uff0c\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u662f\u8c01\uff1f\u201d \u9646\u5c0f\u51e4\u70b9\u70b9\u5934\u3002 \u53f6\u7075\u9053\uff1a\u201c\u4ed6\u662f\u6211\u7684\u54e5\u54e5\uff0c\u5ae1\u4eb2\u7684\u54e5\u54e5\uff0c\u82e5\u4e0d\u662f\u56e0\u4e3a\u4ed6\u987e\u6211\uff0c\u6211\u65e9\u5df2\u6b7b\u5728\u9634\u6c9f \u91cc\u3002\u201d \u5979\u773c\u775b\u91cc\u5145\u6ee1\u60b2\u4f24\u548c\u4ec7\u6068\uff1a\u201c\u73b0\u5728\u4ed6\u6b7b\u4e86\uff0c\u4f60\u8bf4\u6211\u8be5\u4e0d\u8be5\u4e3a\u4ed6\u590d\u4ec7\uff1f\u201d \u4ed6\u4ece\u4e0d\u613f\u548c\u5973\u4eba\u4e89\u8fa9\uff0c\u4f55\u51b5\u8fd9\u4ef6\u4e8b\u5c31\u6ca1\u6709\u4e89\u8fa9\u7684\u4f59\u5730\u3002 \u53f6\u7075\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u9646\u5c0f\u51e4\u65e2\u4e0d\u80fd\u70b9\u5934\uff0c\u53c8\u4e0d\u80fd\u6447\u5934\uff0c\u65e2\u4e0d\u80fd\u89e3\u91ca\uff0c\u4e5f\u4e0d\u80fd\u5426\u8ba4\uff0c\u53ea\u6068\u4e0d\u5f97\u65c1\u8fb9\u5ffd\u7136\u591a \u51fa\u4e00\u68fa\u6750\u6765\uff0c\u597d\u8ba9\u4ed6\u4e5f\u8eb2\u8fdb\u53bb\u3002 \u53f6\u7075\u51b7\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u5c31\u7b97\u4e0d\u8bf4\uff0c\u6211\u4e5f\u77e5\u9053\uff1f\u201d \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u4ed6\u662f\u6b7b\u5728\u5916\u9762\u90a3\u6811\u6797\u91cc\u7684\uff0c\u6b7b\u4e86\u624d\u4e09\u5929\uff0c\u8fd9\u4e09\u5929\u53ea\u6709\u4f60\u5230\u90a3\u6811\u6797\u91cc\u53bb \u8fc7\u3002\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u96be\u9053\u4f60\u8ba4\u4e3a\u662f\u6211\u6740\u4e86\u4ed6\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u4e0d\u9519\uff01\u201d \u201c\u9519\u4e86\u201d\u201c\u8fd9\u4e09\u5929\u5230\u90a3\u6811\u6797\u91cc\u53bb\u8fc7\u7684\u4eba\uff0c\u7edd\u4e0d\u6b62\u4ed6\u4e00\u4e2a\u3002\u201d \u7ad9\u51fa\u6765\u66ff\u9646\u5c0f\u51e4\u8bf4\u8bdd\u7684\u4eba\uff0c\u7adf\u662f\u90a3\u59cb\u7ec8\u65e0\u6d88\u606f\u7684\u72ec\u5b64\u7f8e\uff1a\u201c\u81f3\u5c11\u6211\u4e5f\u53bb\u8fc7\uff0c\u6211\u4e5f\u662f \u4ece\u90a3\u91cc\u6765\u7684\u3002\u201d \u53f6\u7075\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u4f60\u4e5f\u80fd\u7b97\u662f\u4e2a\u4eba\uff1f\u4f60\u80fd\u6740\u5f97\u4e86\u6211\u54e5\u54e5\uff1f\u201d\u5b64\u72ec\u7f8e\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u5c31\u7b97\u6211\u4e0d\u662f\u4eba\uff0c\u4e5f\u8fd8\u6709\u522b\u4eba\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u8fd8\u6709\u522b\u4eba\uff1f\u201d \u5b64\u72ec\u7f8e\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u5c31\u7b97\u6211\u4e0d\u662f\u4f60\u54e5\u54e5\u7684\u5bf9\u624b\uff0c\u8fd9\u4e2a\u4eba\u8981\u6740\u4f60\u54e5\u5374\u4e0d\u592a\u56f0\u96be\u3002\u201d \u53f6\u7075\u6012\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u8c01\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u897f\u95e8\u5439\u96ea\uff01\u201d \u4ed6\u7684\u773c\u775b\u5728\u7b11\uff0c\u7b11\u5f97\u5c31\u50cf\u662f\u6761\u8001\u72d0\u72f8\uff1a\u201c\u8fd9\u540d\u5b57\u4f60\u662f\u4e0d\u662f\u4e5f\u542c\u8bf4\u8fc7\uff1f\u201d \u53f6\u7075\u7684\u8138\u8272\u53d8\u4e86\uff0c\u8fd9\u540d\u5b57\u5979\u5f53\u7136\u542c\u8bf4\u8fc7\u3002 \u897f\u95e8\u5439\u96ea\uff01 \u5251\u4e2d\u7684\u795e\u5251\uff0c\u4eba\u4e2d\u7684\u5251\u795e\uff01 \u8fd9\u540d\u5b57\u65e0\u8bba\u8c01\u53ea\u8981\u542c\u8bf4\u4e00\u6b21\uff0c\u5c31\u518d\u4e5f\u4e0d\u4f1a\u5fd8\u8bb0\u3002 \u5b64\u72ec\u7f8e\u7528\u773c\u89d2\u8198\u7740\u5979\uff0c\u9053\uff1a\u201c\u4f55\u51b5\uff0c\u9646\u5c0f\u51e4\u90a3\u65f6\u4e5f\u4f24\u5f97\u5f88\u91cd\uff0c\u6700\u591a\u53ea\u80fd\u7b97\u534a\u4e2a\u9646\u5c0f \u51e4\uff0c\u534a\u4e2a\u9646\u5c0f\u51e4\u600e\u4e48\u80fd\u5bf9\u4ed8\u4e00\u4e2a\u6b66\u5f53\u5c0f\u767d\u9f99\uff1f\u201d \u53f6\u7075\u53c8\u53eb\u8d77\u6765\uff1a\u201c\u4f60\u8bf4\u8c0e\uff01\u201d \u5b64\u72ec\u7f8e\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4e00\u4e2a\u516d\u4eb2\u4e0d\u8ba4\u7684\u8001\u5934\u5b50\uff0c\u600e\u4e48\u4f1a\u66ff\u522b\u4eba\u8bf4\u8c0e\uff1f\u201d \u96fe\u591c\uff0c\u7a84\u8def\u3002 \u4ed6\u4eec\u5e76\u80a9\u8d70\u5728\u7a84\u8def\u4e0a\uff0c\u4ed6\u4eec\u5df2\u5e76\u80a9\u8d70\u8fc7\u4e00\u6bb5\u5f88\u957f\u7684\u8def\u3002 \u90a3\u6761\u8def\u8fdc\u6bd4\u8fd9\u6761\u66f4\u7a84\uff0c\u90a3\u672c\u662f\u6761\u6b7b\u8def\u3002 \u9646\u5c0f\u51e4\u7ec8\u4e8e\u5f00\u53e3\uff1a\u201c\u4e00\u4e2a\u516d\u4eb2\u4e0d\u8ba4\u7684\u8001\u5934\u5b50\uff0c\u4e3a\u4ec0\u4e48\u8981\u66ff\u6211\u8bf4\u8c0e\uff1f\u201d\u5b64\u72ec\u7f8e\u7b11\u4e86 \u7b11\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u8001\u5934\u5b50\u559c\u6b22\u4f60\u3002\u201d \u4ed6\u62a2\u7740\u53c8\u9053\uff1a\u201c\u5e78\u597d\u8fd9\u8001\u5934\u5e76\u6ca1\u6709\u7c89\u71d5\u5b50\u90a3\u79cd\u6bdb\u75c5\uff0c\u6240\u4ee5\u4f60\u4e00\u70b9\u4e5f\u7528\u4e0d\u7740\u62db\u5fc3\u3002\u201d \u9646\u5c0f\u51e4\u4e5f\u7b11\u4e86\uff0c\u5927\u7b11\uff1a\u201c\u8fd9\u8001\u5934\u5b50\u6709\u6ca1\u6709\u9152\uff1f\u201d\u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4e0d\u4f46\u6709\u9152\uff0c\u8fd8\u6709 \u8089\u3002\u201d \u9646\u5c0f\u51e4\u8fde\u773c\u775b\u90fd\u7b11\u4e86\uff0c\u771f\u7684\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u4e0d\u4f46\u6709\u8089\uff0c\u8fd8\u6709\u670b\u53cb\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u662f\u4f60\u7684\u670b\u53cb\uff1f\u8fd8\u662f\u6211\u7684\uff1f\u201d \u5b64\u72ec\u7f8e\u9053\uff1a\u201c\u6211\u7684\u670b\u53cb\uff0c\u5c31\u662f\u4f60\u7684\u670b\u53cb\u3002\u201d \u9152\u662f\u597d\u9152\uff0c\u670b\u53cb\u4e5f\u662f\u597d\u670b\u53cb\u3002 \u5bf9\u4e00\u4e2a\u559c\u6b22\u559d\u9152\u7684\u4eba\u6765\u8bf4\uff0c\u597d\u670b\u53cb\u7684\u610f\u601d\uff0c\u901a\u5e38\u5c31\u662f\u9152\u91cf\u5f88\u597d\u7684\u670b\u53cb\u3002 \u8fd9\u4f4d\u670b\u53cb\u4e0d\u4f46\u559d\u9152\u75db\u5feb\uff0c\u8bf4\u8bdd\u4e5f\u75db\u5feb\uff0c\u51e0\u676f\u9152\u4e0b\u809a\uff0c\u4ed6\u5ffd\u7136\u95ee\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u9646\u5c0f \u51e4\uff0c\u4f60\u77e5\u9053\u6211\u662f\u8c01\uff1f\u201d\u201c\u4e0d\u77e5\u9053\uff1a\u201c \u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u95ee\uff1f\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\uff0c\u82e6\u7b11\uff1a\u201c\u56e0\u4e3a\u6211\u5df2\u5f97\u5230\u8fc7\u6559\u8bad\u3002\u201d \u201c\u4f60\u95ee\u8fc7\u522b\u4eba\uff0c\u522b\u4eba\u90fd\u4e0d\u80af\u8bf4\uff1f\u201d \u201c\u55ef\u3002\u201d \u201c\u4f46\u6211\u5374\u4e0d\u662f\u522b\u4eba\uff0c\u6211\u5c31\u662f\u6211\u3002\u201d\u4ed6\u5c06\u5de6\u624b\u62ff\u7740\u7684\u9152\u4e00\u53e3\u6c14\u5531\u4e0b\u53bb\uff0c\u7528\u53f3\u624b\u94a9\u8d77\u4e00 \u5757\u8089\u3002 \u8089\u662f\u88ab\u94a9\u8d77\u6765\u7684\uff0c\u56e0\u4e3a\u4ed6\u7684\u53f3\u624b\u4e0d\u662f\u624b\uff0c\u662f\u4e2a\u94a9\u5b50\uff0c\u94c1\u94a9\u5b50\u3002 \u201c\u4f60\u5c31\u662f\u94a9\u5b50\uff1f\u201d\u9646\u5c0f\u51e4\u7ec8\u4e8e\u60f3\u8d77\u3002 \u94a9\u5b50\u627f\u8ba4\uff01 \u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u542c\u4eba\u8bf4\u8d77\u8fc7\u6211\uff0c\u4f46\u6709\u4ef6\u4e8b\u4f60\u5374\u4e00\u5b9a\u4e0d\u77e5\u9053\uff1a\u201c \u201c\u4ec0\u4e48\u4e8b\uff1f\u201d\u2606\u2018\u4ece\u4f60\u6765\u7684\u90a3\u4e00\u5929\uff0c\u6211\u5c31\u60f3\u8ddf\u4f60\u4ea4\u4e2a\u670b\u53cb\u3002\u201d\u4ed6\u62cd\u4e86\u62cd\u5b64\u72ec\u7f8e\u7684 \u80a9\uff1a\u201c\u56e0\u4e3a\u4f60\u7684\u670b\u53cb\uff0c\u5c31\u662f\u6211\u7684\u670b\u53cb\uff0c\u4f60\u7684\u5bf9\u5934\uff0c\u4e5f\u662f\u6211\u7684\u5bf9\u5934\u3002\u201d \u201c\u6211\u4eec\u7684\u670b\u53cb\u662f\u4ed6\uff0c\u6211\u4eec\u7684\u5bf9\u5934\u662f\u6dee\uff1f\u201d \u201c\u897f\u95e8\u5439\u96ea\uff01\u201d \u9646\u5c0f\u51e4\u8038\u7136\u529f\u5bb9\uff1a\u201c\u4f60\u662f\u2026\u2026\u201d \u94a9\u5b50\u9053\uff1a\u201c\u6211\u5c31\u662f\u6d77\u5947\u9614\u3002\u201d \u9646\u5c0f\u51e4\u66f4\u5403\u60ca\uff1a\u201c\u5c31\u662f\u6614\u5e74\u90a3\u5a01\u9707\u4e03\u6d77\u7684\u2018\u72ec\u81c2\u795e\u9f99\u2019\u6d77\u5947\u9614\uff1f\u201d \u6d77\u5947\u9614\u4ef0\u9762\u5927\u7b11\uff1a\u201c\u60f3\u4e0d\u5230\u9646\u5c0f\u51e4\u5c45\u7136\u4e5f\u77e5\u9053\u6d77\u67d0\u4eba\u7684\u540d\u5b57\u2019\u2019 \u9646\u5c0f\u51e4\u770b\u7740\u4ed6\u76ee\u4e2d\u7684\u60ca\u8bb6\u53c8\u53d8\u4e3a\u6000\u7591\uff0c\u5ffd\u7136\u6447\u5934\u9053\uff1a\u201c\u4f60\u4e0d\u662f\uff0c\u6d77\u5947\u9614\u5df2\u5728\u6d77\u4e0a\u8986 \u821f\u800c\u6b7b\u3002\u201d \u6d77\u5947\u9614\u7b11\u5f97\u66f4\u6109\u5feb\uff1a\u201c\u6b7b\u7684\u662f\u53e6\u5916\u2014\u4e2a\u4eba\uff0c\u4e00\u4e2a\u7a7f\u7740\u6211\u7684\u6eda\u9f99\u888d\uff0c\u5e26\u7740\u6211\u7684\u6eda\u9f99 \u5200\uff0c\u957f\u50cf\u4e5f\u8ddf\u6211\u5dee\u4e0d\u591a\u7684\u66ff\u6b7b\u9b3c\u3002\u201d \u4ed6\u53c8\u89e3\u91ca\u7740\u9053\uff1a\u201c\u5728\u8fd9\u91cc\u7684\u4eba\uff0c\u6bcf\u4e2a\u90fd\u5df2\u5728\u5916\u9762\u6b7b\u8fc7\u4e00\u6b21\uff0c\u4f60\u5c82\u975e\u4e5f\u4e00\u6837\uff1f\u201d \u9646\u5c0f\u51e4\u7ec8\u4e8e\u660e\u767d\uff1a\u201c\u8fd9\u91cc\u672c\u5c31\u662f\u5e7d\u7075\u5c71\u5e84\uff0c\u53ea\u6709\u6b7b\u4eba\u624d\u80fd\u6765\u3002\u201d \u6d77\u5947\u9614\u5927\u7b11\u9053\uff1a\u201c\u897f\u95e8\u5439\u96ea\u82e5\u662f\u77e5\u9053\u6211\u4eec\u8fd8\u5728\u8fd9\u91cc\u996e\u9152\u5403\u8089\uff0c\u53ea\u4f2f\u8981\u6d3b\u6d3b\u6c14\u6b7b\u3002 \u9646\u5c0f\u51e4\u5fae\u7b11\u9053\uff1a\u201c\u770b\u6765\u5728\u8fd9\u91cc\u6211\u4e00\u5b9a\u8fd8\u6709\u4e0d\u5c11\u670b\u53cb\u3002\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\uff0c\u8fd9\u65f6\u81f3\u5c11\u6709\u5341\u516d\u4e2a\u4eba\u662f\u88ab\u897f\u95e8\u5439\u96ea\u903c\u6765\u7684oo \u9646\u5c0f\u51e4\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u662f\u4e0d\u662f\u6709\u51e0\u4e2a\u662f\u88ab\u6211\u903c\u6765\u7684\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u5c31\u7b97\u6709\uff0c\u4f60\u4e5f\u7528\u4e0d\u7740\u62c5\u5fc3\u3002\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u5df2\u6709\u4e86\u4f60\u4eec\u8fd9\u4e9b\u670b \u53cb\u3002\u201d\u6d77\u5947\u9614\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u4ed6\u5927\u7b11\u4e3e\u676f\uff0c\u5ffd\u53c8\u538b\u4f4e\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u53ea\u6709\u4e00\u4e2a\u4eba\u4f60\u8981\u7279\u522b\u7559\u610f\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8c01\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u5176\u5b9e\u4ed6\u6839\u672c\u4e0d\u80fd\u7b97\u662f\u4eba\uff0c\u53ea\u4e0d\u8fc7\u662f\u6761\u6e38\u9b42\u800c\u5df2\u3002\u201d \u9646\u5c0f\u51e4\u5931\u58f0\u9053\uff1a\u201c\u6e38\u9b42\uff1f\u201d \u6d77\u5947\u9614\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u89c1\u8fc7\u4ed6\uff1f\u201d \u9646\u5c0f\u51e4\u6ca1\u6709\u5426\u8ba4\u3002 \u6d77\u5947\u9614\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u5f88\u60f3\u77e5\u9053\uff1a\u201c \u6d77\u5947\u9614\u9053\uff1a\u201c\u8fd9\u91cc\u6709\u4e2a\u5f88\u5947\u602a\u7684\u7ec4\u7ec7\uff0c\u53eb\u5143\u8001\u4f1a\uff0c\u8001\u5200\u628a\u5b50\u4e0d\u5728\u7684\u65f6\u5019\uff0c\u8fd9\u91cc\u6240\u6709 \u7684\u4e8b\uff0c\u90fd\u7531\u5143\u8001\u4f1a\u8d1f\u8d23\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5143\u8001\u4f1a\u91cc\u7684\u4eba\uff0c\u5f53\u7136\u90fd\u662f\u5143\u8001\uff0c\u9601\u4e0b\u5f53\u7136\u4e5f\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u9664\u4e86\u6211\u4e4b\u5916\uff0c\u5143\u8001\u4f1a\u8fd8\u6709\u516b\u4e2a\u4eba\uff0c\u5176\u5b9e\u771f\u6b63\u7684\u5143\u8001\uff0c\u5374\u53ea\u6709\u4e24\u4e2a\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u54ea\u4e24\u4e2a\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u2018\u4e00\u4e2a\u662f\u6e38\u9b42\uff0c\u4e00\u4e2a\u662f\u52fe\u9b42\uff0c\u4ed6\u4eec\u548c\u53f6\u5bb6\u5144\u59b9\u7684\u8001\u5b50\uff0c\u90fd\u662f\u6614\u5e74\u8ddf\u8001\u5200 \u628a\u5b50\u4e00\u8d77\u5f00\u521b\u8fd9\u5c40\u9762\u7684\u4eba\uff0c\u73b0\u5728\u8001\u53f6\u5df2\u6b7b\u4e86\uff0c\u8fd9\u5730\u65b9\u7684\u4eba\u5df2\u6ca1\u6709\u4e00\u4e2a\u6bd4\u4ed6\u4eec\u8d44\u683c\u66f4\u8001 \u7684\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ea\u56e0\u4e3a\u8fd9\u4e00\u70b9\uff0c\u6211\u5c31\u8be5\u7279\u522b\u7559\u610f\u4ed6\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u70b9\u3002\u201d \u9646\u5c0f\u51e4\u62ff\u8d77\u9152\u676f\uff0c\u7b49\u7740\u4ed6\u8bf4\u4e0b\u53bb\u3002 \u6d77\u5947\u9614\u9053\uff1a\u201c\u4ed6\u662f\u8fd9\u91cc\u7684\u5143\u8001\uff0c\u4ed6\u82e5\u60f3\u6740\u4f60\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u627e\u5230\u673a\u4f1a\uff0c\u4f60\u5374\u8fde\u78b0\u90fd\u4e0d \u80fd\u78b0\u4ed6\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u6709\u7406\u7531\u8981\u6740\u6211JU \u6d77\u5947\u9614\u9053\uff1a\u201c\u6709\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ec0\u4e48\u7406\u7531\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u4f60\u6740\u4e86\u4ed6\u7684\u513f\u5b50\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u7684\u513f\u5b50\u662f\u8c01\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u98de\u5929\u7389\u864e\u3002\u201d \u9646\u5c0f\u51e4\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u5ffd\u7136\u89c9\u5f97\u521a\u559d\u4e0b\u53bb\u7684\u9152\u90fd\u53d8\u6210\u4e86\u9178\u6c34\u3002 \u6d77\u5947\u9614\u9053\uff1a\u201c\u9ed1\u864e\u5e2e\u672c\u662f\u4ed6\u4e00\u624b\u521b\u7acb\u7684\uff0c\u7b49\u5230\u9ed1\u864e\u5e2e\u7684\u6839\u57fa\u5c06\u8981\u7a33\u56fa\u65f6\uff0c\u4ed6\u5374\u8ddf\u7740 \u8001\u5200\u628a\u5b50\u5230\u8fd9\u91cc\u6765\u4e86\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u5f97\u7f6a\u4e86\u4e00\u4e2a\u7edd\u4e0d\u8be5\u5f97\u7f6a\u7684\u4eba\uff0c\u4e5f\u5df2\u88ab\u903c\u5f97\u65e0\u8def\u53ef\u8d70\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u5f97\u7f6a\u4e86\u8c01\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u6728\u9053\u4eba\uff0c\u6b66\u5f53\u7684\u7b2c\u4e00\u540d\u5bbf\u6728\u9053\u4eba\u3002\u201d \u9646\u5c0f\u51e4\u53c8\u4e0d\u7981\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u76f4\u5230\u73b0\u5728\u4ed6\u624d\u660e\u767d\uff0c\u4e3a\u4ec0\u4e48\u6e38\u9b42\u4e00\u76f4\u4e0d\u8bf4\u51fa\u81ea\u5df1\u7684\u6765 \u5386\u3002 \u6d77\u5947\u9614\u9053\uff1a\u201c\u9ed1\u864e\u5e2e\u662f\u6bc1\u5728\u4f60\u624b\u91cc\u7684\uff0c\u6728\u9053\u4eba\u5374\u6070\u5de7\u53c8\u662f\u4f60\u7684\u597d\u670b\u53cb\uff0c\u4f60\u8bf4\u4ed6\u662f\u4e0d \u662f\u5df2\u6709\u8db3\u591f\u7684\u7406\u7531\u6740\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u4ed6\u6709\u3002\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u6700\u8981\u547d\u7684\u662f\uff0c\u4f60\u867d\u7136\u660e\u77e5\u4ed6\u8981\u6740\u4f60\uff0c\u4e5f\u4e0d\u80fd\u52a8\u4ed6\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u662f\u5143\u8001\u4e2d\u7684\u5143\u8001\u3002\u201d \u6d77\u5947\u9614\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u9664\u4e86\u4ed6\u8fd9\u4e4b\u5916\uff0c\u5143\u8001\u4f1a\u8fd8\u6709\u516b\u4e2a\u4eba\uff0c\u4f60\u82e5\u6740\u4e86\u4ed6\uff0c\u8fd9\u516b\u4e2a\u4eba \u7edd\u4e0d\u4f1a\u653e\u8fc7\u4f60\u3002\u201d \u4ed6\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u6211\u53ea\u6709\u7b49\u7740\u4ed6\u51fa\u624b\u3002\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u4e0d\u5230\u4e00\u51fb\u5fc5\u4e2d\u65f6\uff0c\u4ed6\u7edd\u4e0d\u4f1a\u51fa\u624b\uff0c\u73b0\u5728\u4ed6\u8fd8\u6ca1\u6709\u51fa\u624b\uff0c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u4ed6 \u8fd8\u5728\u7b49\u673a\u4f1a\u3002\u201d \u9646\u5c0f\u51e4\u867d\u7136\u4e0d\u518d\u8bf4\u8bdd\uff0c\u5374\u6ca1\u6709\u95ed\u4e0a\u5634\u3002 \u4ed6\u7684\u5634\u6b63\u5728\u5fd9\u7740\u559d\u9152\u3002 \u6d77\u5947\u9614\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u559d\u9189\u4e86\uff0c\u4ed6\u7684\u673a\u4f1a\u5c31\u6765\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u77e5\u9053\uff1a\u201c \u6d77\u5947\u9614\u9053\uff1a\u201c\u4f46\u662f\u4f60\u8fd8\u8981\u559d\uff1f\u201d\u9646\u5c0f\u51e4\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u65e2\u7136\u4ed6\u662f\u5143\u8001\uff0c\u53cd\u6b63\u603b \u4f1a\u7b49\u5230\u4e2a\u673a\u4f1a\u7684\uff0c\u6211\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u4e58\u7740\u6ca1\u6709\u6b7b\u7684\u65f6\u5019\u591a\u559d\u51e0\u676f\u3002\u201d \u559d\u9152\u548c\u5403\u996d\u4e0d\u540c\u3002 \u5e73\u65f6\u5403\u4e09\u7897\u996d\u7684\u4eba\uff0c\u7edd\u5bf9\u5403\u4e0d\u4e0b\u4e8c\u5341\u7897\uff0c\u53ef\u662f\u5e73\u65f6\u5e72\u676f\u4e0d\u9189\u7684\u4eba\uff0c\u6709\u65f6\u53ea\u559d\u51e0\u676f\u5c31 \u5df2\u9189\u4e86\u3002 \u9646\u5c0f\u51e4\u662f\u4e0d\u662f\u5df2\u9189\u4e86\uff1f \u201c\u6211\u8fd8\u6ca1\u5b58\u9189\u3002\u201d\u4ed6\u63a8\u5f00\u5b64\u72ec\u7f8e\u548c\u6d77\u5947\u9614\uff1a\u201c\u6211\u8fd8\u8ba4\u5f97\u8def\u56de\u53bb\uff0c\u4f60\u4eec\u4e0d\u5fc5\u9001\u6211\u3002\u201d \u4ed6\u679c\u7136\u6ca1\u6709\u8d70\u9519\u8def\u3002 \u6709\u65f6\u4e00\u4e2a\u4eba\u7eb5\u7136\u5df2\u559d\u5f97\u4eba\u4e8b\u4e0d\u77e5\uff0c\u8fd8\u662f\u4e00\u6837\u80fd\u8ba4\u5f97\u56de\u5bb6\u7684\uff0c\u56de\u5230\u5bb6\u4e4b\u540e\uff0c\u624d\u4f1a\u5012\u4e0b \u53bb\u3002 \u4f60\u82e5\u4e5f\u662f\u559d\u9152\u7684\u4eba\uff0c\u4f60\u4e00\u5b9a\u4e5f\u6709\u8fc7\u8fd9\u79cd\u7ecf\u9a8c\u3002 \u9646\u5c0f\u51e4\u6709\u8fc7\u8fd9\u79cd\u7ecf\u9a8c\uff0c\u5e38\u5e38\u6709\u3002 \u201c\u8fd9\u662f\u6211\u7684\u5bb6\uff0c \u6211\u4eec\u90fd\u7231\u5b83\uff0c \u524d\u9762\u517b\u7740\u9c7c\uff0c \u540e\u9762\u79cd\u7740\u82b1\u3002\u201d \u867d\u7136\u8fd9\u5c0f\u6728\u5c4b\u524d\u9762\u5e76\u6ca1\u6709\u517b\u9c7c\uff0c\u540e\u9762\u4e5f\u6ca1\u6709\u79cd\u82b1\uff0c\u6bd5\u7adf\u603b\u7b97\u662f\u4ed6\u7684\u5bb6\u3002 \u4e00\u4e2a\u6ca1\u6709\u6839\u7684\u6d6a\u5b50\uff0c\u5728\u5927\u9189\u4e4b\u540e\u3002\u5ffd\u7136\u53d1\u73b0\u5c45\u7136\u5df2\u6709\u4e2a\u5bb6\u53ef\u4ee5\u56de\u53bb \u8fd9\u662f\u79cd\u591a\u4e48\u6109\u5feb\u7684\u611f\u89c9\uff1f\u9664\u4e86\u6211\u4eec\u8fd9\u4e9b\u6d6a\u5b50\u5916\uff0c\u53c8\u6709\u8c01\u77e5\u9053\uff1f \u9646\u5c0f\u51e4\u53c8\u5531\u8d77\u513f\u6b4c\uff0c\u5531\u7684\u58f0\u97f3\u5f88\u5927\uff0c\u56e0\u4e3a\u4ed6\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u7684\u6b4c\u5589\u8d8a\u6765\u8d8a\u597d\u542c\u4e86\u3002 \u5c4b\u5b50\u91cc\u6ca1\u6709\u706f\uff0c\u53ef\u662f\u4ed6\u4e00\u63a8\u5f00\u95e8\uff0c\u5c31\u611f\u89c9\u5230\u91cc\u9762\u6709\u4e2a\u4eba\u3002\u201c\u6211\u77e5\u9053\u4f60\u662f\u8c01\uff0c\u4f60\u4e0d\u51fa \u58f0\u6211\u4e5f\u77e5\u9053\u3002\u201d\u9646\u5c0f\u51e4\u5728\u7b11\uff0c\u7b11\u7684\u58f0\u97f3\u4e5f\u5f88\u5927\uff1a\u201c\u4f60\u662f\u6e38\u9b42\uff0c\u662f\u8fd9\u91cc\u7684\u5143\u8001\uff0c\u4f60\u5728\u8fd9\u91cc \u7b49\u7740\u6211\uff0c\u662f\u4e0d\u662f\u771f\u7684\u60f3\u6740\u6211\uff1f\u201d \u5c4b\u5b50\u91cc\u7684\u4eba\u8fd8\u662f\u4e0d\u51fa\u58f0\u3002 \u9646\u5c0f\u51e4\u5927\u7b11\u9053\uff1a\u201c\u4f60\u5c31\u7b97\u60f3\u6740\u6211\uff0c\u4e5f\u4e0d\u4f1a\u6697\u7b97\u6211\u7684\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u56e0\u4e3a\u4f60\u662f\u6b66\u5f53\u4fd7\u5bb6\u5f1f \u5b50\u4e2d\u7684\u7b2c\u4e00\u4f4d\u540d\u4eba\uff0c\u56e0\u4e3a\u4f60\u5c31\u662f\u949f\u5148\u751f\uff0c\u949f\u65e0\u9aa8\u3002\u201d \u4ed6\u8d70\u8fdb\u53bb\uff0c\u5173\u4e0a\u95e8\uff0c\u5f00\u59cb\u627e\u706b\u6298\u5b50\uff1a\u201c\u5176\u5b9e\u4f60\u672c\u6765\u4e5f\u662f\u6728\u9053\u4eba\u7684\u8001\u670b\u53cb\uff0c\u4f46\u4f60\u5374\u4e0d \u8be5\u5077\u5077\u6478\u6478\u5728\u5916\u9762\u7ec4\u7ec7\u9ed1\u864e\u5e2e\u7684\uff0c\u5426\u5219\u6728\u9053\u4eba\u53c8\u600e\u4e48\u4f1a\u5bf9\u4ed8\u4f60\uff1f\u201d \u8fd8\u662f\u6ca1\u6709\u56de\u58f0\uff0c\u5374\u6709\u4e86\u706b\u5149\u3002 \u706b\u6298\u5b50\u4eae\u8d77\uff0c\u7167\u7740\u4e00\u4e2a\u4eba\u7684\u8138\uff0c\u4e00\u5f20\u53ea\u5269\u4e0b\u76ae\u5305\u7740\u9aa8\u5934\u7684\u8138\uff0c\u90a3\u53cc\u5df2\u9ab7\u9ac5\u822c\u6df1\u9677\u4e0b \u53bb\u7684\u773c\u775b\uff0c\u6b63\u7728\u4e5f\u4e0d\u7728\u7684\u76ef\u7740\u9646\u5c0f\u98ce\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u73b0\u5728\u6211\u4eec\u65e2\u7136\u90fd\u5df2\u662f\u6b7b\u4eba\uff0c\u53c8\u4f55\u5fc5\u518d\u8ba1\u8f83\u4ee5\u524d\u7684\u6069\u6028\uff0c\u4f55\u51b5\u2026\u2026\u201d \u4ed6\u6ca1\u6709\u8bf4\u4e0b\u53bb\u3002 \u4ed6\u7684\u58f0\u97f3\u7a81\u7136\u4e2d\u65ad\uff0c\u624b\u91cc\u7684\u706b\u6298\u5b50\u4e5f\u7a81\u7136\u7184\u706d\u3002 \u4ed6\u5ffd\u7136\u53d1\u73b0\u8fd9\u4f4d\u949f\u5148\u751f\u5df2\u771f\u7684\u662f\u4e2a\u6b7b\u4eba\u300f \u5c4b\u5b50\u91cc\u4e00\u7247\u6f06\u9ed1\uff0c\u9646\u5c0f\u51e4\u52a8\u4e5f\u4e0d\u52a8\u7684\u7ad9\u5728\u9ed1\u6697\u4e2d\uff0c\u53ea\u89c9\u5f97\u624b\u811a\u51b0\u51b7\uff0c\u5168\u8eab\u90fd\u5df2\u51b0 \u51b7\uff0c\u5c31\u597d\u50cf\u4e00\u4e0b\u5b50\u8dcc\u4eba\u4e86\u51b7\u7a96\u91cc\u3002 \u8fd9\u4e0d\u662f\u51b7\u7a96\uff0c\u8fd9\u662f\u4e2a\u9677\u9631\u3002 \u4ed6\u5df2\u770b\u51fa\u6765\uff0c\u53ef\u662f\u4ed6\u5df2\u9003\u4e0d\u51fa\u53bb\u3002 \u4ed6\u6839\u672c\u5df2\u65e0\u8def\u53ef\u9003\uff01 \u4e8e\u662f\u4ed6\u7d22\u6027\u5750\u4e0b\u6765\uff0c\u521a\u5750\u4e0b\u6765\uff0c\u5c31\u542c\u89c1\u5916\u9762\u4f20\u6765\u4e00\u9635\u811a\u6b65\u58f0\uff0c\u63a5\u7740\u5c31\u6709\u4eba\u5728\u6572\u95e8\u3002 \u201c\u4f60\u7761\u4e86\u6ca1\u6709\uff1f\u6211\u6709\u8bdd\u8ddf\u4f60\u8bf4\u300f\u201d\u58f0\u97f3\u8f7b\u67d4\uff0c\u662f\u53f6\u7075\u7684\u58f0\u66f0\uff0c \u9646\u5c0f\u51e4\u95ed\u7740\u5634\u3002 \u201c\u6211\u77e5\u9053\u4f60\u6ca1\u6709\u7761\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5f00\u95e8\uff1f\u201d\u53f6\u7075\u7684\u58f0\u97f3\u53d8\u51f6\u4e86\uff1a\u201c\u662f\u4e0d\u662f\u4f60\u5c4b\u5b50\u91cc\u85cf \u7740\u5973\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u7ec8\u4e8e\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u5c4b\u5b50\u91cc\u8fde\u534a\u4e2a\u5973\u4eba\u90fd\u6ca1\u6709\uff0c\u5374\u6709\u4e00\u4e2a\u534a\u6b7b\u4eba\u3002\u201d \u53f6\u7075\u7684\u58f0\u97f3\u66f4\u51f6\uff1a\u201c\u6211\u8bf4\u8fc7\uff0c\u4f60\u82e5\u6562\u8ba9\u5973\u4eba\u8fdb\u4f60\u7684\u5c4b\u5b50\uff0c\u6211\u5c31\u6740\u4e86\u4f60\uff0c\u65e0\u8bba\u6b7b\u6d3b\u90fd \u4e0d\u884c\u3002\u201d \u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u95e8\u88ab\u649e\u5f00\u4e86\u3002 \u201c\u8fd9\u91cc\u7684\u5973\u4eba\uff0c\u672c\u5c31\u90fd\u662f\u6b7b\u5973\u4eba\u3002\u201d \u201c\u8fd9\u4e2a\u6b7b\u4eba\u5374\u6070\u5de7\u662f\u7537\u7684\u3002\u201d \u706b\u6298\u5b50\u53c8\u4eae\u8d77\uff0c\u53f6\u7075\u7ec8\u4e8e\u770b\u89c1\u8fd9\u4e2a\u6b7b\u4eba\uff1a\u201c\u8fd8\u6709\u534a\u4e2a\u5462\uff1f\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u534a\u4e2a\u6b7b\u4eba\u5c31\u662f\u6211\u300f\u201d \u53f6\u7075\u770b\u7740\u4ed6\uff0c\u53c8\u770b\u770b\u6b7b\u4eba\uff0c\u5ffd\u7136\u8df3\u8d77\u6765\uff1a\u201c\u4f60\u6740\u4e86\u4ed6\uff1f\u4f60\u600e\u80fd\u6740\u4ed6\uff1f\u4f60\u77e5\u4e0d\u77e5\u9053\u4ed6\u662f \u8c01\uff1f\u201d \u9646\u5c0f\u51e4\u6ca1\u6709\u5f00\u53e3\uff0c\u4e5f\u4e0d\u5fc5\u5f00\u53e3\uff0c\u5916\u9762\u5df2\u6709\u4eba\u66ff\u4ed6\u56de\u7b54\uff1a\u201c\u4ed6\u77e5\u9053oo \u5c4b\u5b50\u5f88\u5c0f\uff0c\u7a97\u4e8e\u4e5f\u5f88\u5c0f\uff0c\u53f6\u7075\u6321\u5728\u95e8\u53e3\uff0c\u5916\u9762\u7684\u4eba\u6839\u672c\u8d70\u4e0d\u8fdb\u6765\u3002 \u4f46\u4ed6\u4eec\u6709\u522b\u7684\u6cd5\u5b50\u3002 \u5ffd\u7136\u95f4\uff0c\u53c8\u662f\u201c\u7830\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u4ed6\u65e2\u6ca1\u6709\u4f38\u624b\u53bb\u6321\uff0c\u8fde\u5c4b\u9876\u90fd\u584c\u4e0b\uff0c\u672c\u6765\u5750\u5728\u5c4b\u91cc \u7684\u4eba\u5ffd\u7136\u5c31\u5df2\u5230\u4e86\u9732\u5929\u91cc\u3002 \u9646\u5c0f\u51e4\u6ca1\u6709\u52a8\u3002 \u5c4b\u9876\u5012\u584c\uff0c\u6253\u5728\u4ed6\u8eab\u4e0a\uff0c\u4ed6\u65e2\u6ca1\u6709\u4f38\u624b\u53bb\u6321\uff0c\u4e5f\u6ca1\u6709\u95ea\u907f\uff0c\u53ea\u4e0d\u8fc7\u53f9\u4e86\u53e3\u6c14\u3002 \u8fd9\u662f\u4ed6\u7b2c\u4e00\u6b21\u6709\u5bb6\uff0c\u5f88\u53ef\u80fd\u4e5f\u662f\u6700\u540e\u4e00\u6b21\u3002\u201c\u539f\u6765\u8fd9\u4e16\u4e0a\u4e0d\u4f46\u6709\u5012\u9709\u7684\u4eba\uff0c\u4e5f\u6709\u5012 \u9709\u7684\u5c4b\u5b50\uff1a\u201c\u9646\u5c0f\u98ce\u53f9\u606f\u7740\u9053\uff1a\u201c\u5c4b\u5b50\u5012\u9709\uff0c\u662f\u56e0\u4e3a\u9009\u9519\u4e86\u4e3b\u4eba\uff0c\u4eba\u5012\u9709\u662f\u56e0\u4e3a\u4ea4\u9519\u4e86 \u670b\u53cbao\u201c\u4f60\u5012\u9709\u5374\u662f\u56e0\u4e3a\u505a\u9519\u4e86\u4e8b\u3002\u201d \u201c\u4f60\u4ec0\u4e48\u4e8b\u90fd\u53ef\u4ee5\u505a\uff0c\u4e3a\u4ec0\u4e48\u504f\u504f\u8981\u6740\u4ed6\uff1f\u201d \u201c\u6211\u65e9\u5c31\u544a\u8bc9\u8fc7\u4f60\uff0c\u5c31\u7b97\u4f60\u660e\u77e5\u4ed6\u8981\u6740\u4f60\uff0c\u4e5f\u4e0d\u80fd\u6740\u4ed6\u7684\uff0c\u5426\u5219\u8fde\u6211\u90fd\u4e0d\u4f1a\u653e\u8fc7 \u4f60\u3002\u201d \u6700\u540e\u4e00\u4e2a\u8bf4\u8bdd\u7684\u662f\u6d77\u5947\u9614\uff0c\u53e6\u5916\u7684\u4e24\u4e2a\u4eba\uff0c\u4e00\u4e2a\u767d\u9762\u65e0\u987b\uff0c\u670d\u9970\u534e\u4e3d\uff0c\u4e00\u4e2a\u53c8\u9ad8\u53c8 \u7626\uff0c\u9e70\u9f3b\u9a7c\u80cc\uff0c\u4e00\u4e2a\u8138\u4e0a\u603b\u662f\u5e26\u7740\u7b11\uff0c\u8fde\u81ea\u5df1\u90fd\u5bf9\u81ea\u5df1\u5f88\u6b23\u8d4f\u7684\uff0c\u4e00\u4e2a\u603b\u662f\u6101\u7709\u82e6\u8138\uff0c \u8fde\u81ea\u5df1\u90fd\u4e0d\u6b23\u8d4f\u81ea\u5df1\u3002 \u9646\u5c0f\u51e4\u5ffd\u7136\u95ee\uff1a\u201c\u8c01\u662f\u8868\u54e5\uff1f\u201d \u8868\u54e5\u5149\u6ed1\u767d\u51c0\u7684\u8138\u4e0a\u867d\u7136\u8fd8\u5e26\u7740\u7b11\uff0c\u5374\u6545\u610f\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u5e78\u597d\u6211\u4e0d\u662f\u4f60\u7684\u8868\u54e5\uff0c\u5426 \u5219\u5c82\u975e\u8fde\u6211\u90fd\u5728\u88ab\u4f60\u8fde\u7d2f\u3002\u201d \u9646\u5c0f\u51e4\u4e5f\u6545\u610f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5e78\u597d\u4f60\u4e0d\u662f\u6211\u8868\u54e5\uff0c\u5426\u5219\u6211\u7b80\u76f4\u8981\u4e00\u5934\u649e\u6b7b\u3002\u201d \u8868\u54e5\u7b11\u9053\uff1a\u201c\u6211\u4fdd\u8bc1\u4f60\u4e0d\u5fc5\u81ea\u5df1\u4e00\u5934\u649e\u6b7b\uff0c\u6211\u4eec\u4e00\u5b9a\u53ef\u4ee5\u60f3\u51fa\u5f88\u591a\u522b\u7684\u6cd5\u5b50\u8ba9\u4f60 \u6b7b\u3002\u201d \u4ed6\u7b11\u5f97\u66f4\u6109\u5feb\uff0c\u4ed6\u5bf9\u81ea\u5df1\u8bf4\u51fa\u7684\u6bcf\u53e5\u8bdd\u90fd\u5f88\u6b23\u8d4f\uff0c\u5f88\u6ee1 \u53e6\u2014\u4eba\u5ffd\u7136\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u662f\u4e2a\u7ba1\u5bb6\u5a46\uff0c\u8fd9\u4ef6\u4e8b\u6211\u66f4\u975e\u7ba1\u4e0d\u53ef\u3002\u201d \u4ed6\u6101\u7709\u82e6\u8138\u7684\u53f9\u606f\u7740\uff1a\u201c\u5176\u5b9e\u6211\u6839\u672c\u4e00\u70b9\u4e5f\u4e0d\u559c\u6b22\u7ba1\u95f2\u4e8b\uff0c\u6211\u5df2\u7ecf\u6709\u51e0\u4e2a\u6708\u6ca1\u6709\u597d \u597d\u7761\u8fc7\u4e00\u89c9\u4e86\uff0c\u6700\u8fd1\u53c8\u8001\u662f\u8170\u9178\u80cc\u75bc\uff0c\u7259\u9f7f\u66f4\u75db\u5f97\u8981\u547d\u2026\u2026\u201d \u4ed6\u5520\u5520\u53e8\u53e8\uff0c\u4e0d\u505c\u7684\u8bc9\u82e6\uff0c\u975e\u4f46\u5bf9\u81ea\u5df1\u7684\u751f\u6d3b\u5f88\u4e0d\u6ee1\u610f\uff0c\u5bf9\u81ea\u5df1\u7684\u4eba\u4e5f\u4e0d\u6ee1\u610f\u3002 \u9646\u5c0f\u51e4\u82eb\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5143\u8001\u4f1a\u7684\u4eba\u2014\u4e0b\u5b50\u5c31\u6765\u4e86\u4e09\u4f4d\u3002 \u53f6\u7075\u5ffd\u7136\u9053\uff1a\u201c\u56db\u4f4d\uff1a\u201c\u2019 \u9646\u5c0f\u51e4\u5f88\u5403\u60ca\uff1a\u201c\u4f60\u4e5f\u662f\uff1f\u201d \u53f6\u7075\u677f\u7740\u8138\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5143\u8001\u7684\u610f\u601d\u662f\u8d44\u683c\u8001\uff0c\u4e0d\u662f\u5e74\u7eaa \u8001\u3002 \u8868\u54e5\u5fae\u7b11\u9053\uff1a\u201c\u8bf4\u5f97\u597d\u3002\u201d \u7ba1\u5bb6\u5a46\u9053\uff1a\u201c\u8001\u5200\u628a\u5b50\u4e0d\u5728\uff0c\u53ea\u8981\u5143\u8001\u4f1a\u4e2d\u591a\u6570\u4eba\u540c\u610f\uff0c\u5c31\u53ef\u4ee5\u51b3\u5b9a\u4e00\u4ef6\u4e8b\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u4efb\u4f55\u4e8b\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u591a\u6570\u4eba\u662f\u51e0\u4e2a\u4eba\uff1f\u201d \u7ba1\u5bb6\u5a46\u9053\uff1a\u201c\u5143\u8001\u4f1a\u6709\u51e0\u4e2a\u4eba\uff0c\u591a\u6570\u4eba\u5c31\u662f\u4e94\u4e2a\u4eba\u3002\u201d \u9646\u5c0f\u51e4\u677e\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u73b0\u5728\u4f60\u4eec\u597d\u50cf\u53ea\u5230\u4e86\u56db\u4f4d\u3002\u201d \u7ba1\u5bb6\u5a46\u9053\uff1a\u201c\u4e94\u4f4d\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u6b7b\u4e86\u7684\u4e5f\u7b97\uff1f \u8868\u54e5\u9053\uff1a\u201c\u8fd9\u91cc\u672c\u5c31\u5168\u90fd\u662f\u6b7b\u4eba\uff0c\u949f\u5148\u751f\u53ea\u4e0d\u8fc7\u591a\u6b7b\u4e86\u4e00\u6b21\u800c\u5df2\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4eec\u73b0\u5728\u5df2\u7ecf\u53ef\u4ee5\u51b3\u5b9a\u4e00\u4ef6\u4e8b\u4e86\u3002\u201d \u8868\u54e5\u60a0\u7136\u9053\uff1a\u201c\u4f60\u5f88\u806a\u660e\uff0c\u4f60\u5f53\u7136\u5e94\u8be5\u77e5\u9053\u6211\u4eec\u8981\u51b3\u5b9a\u7684\u662f\u4ec0\u4e48\u4e8boo \u7ba1\u5bb6\u5a46\u9053\uff1a\u201c\u6211\u4eec\u8981\u51b3\u5b9a\u4f60\u662f\u4e0d\u662f\u8be5\u6b7b\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u96be\u9053\u6211\u5c31\u6ca1\u6709\u8fa9\u767d\u7684\u673a\u4f1a\uff1f\u201d \u7ba1\u5bb6\u5a46\u9053\uff1a\u201c\u6ca1\u6709\u3002 \u9646\u5c0f\u51e4\u53ea\u6709\u82e6\u7b11\u3002 \u6d77\u5947\u9614\u9053\uff1a\u201c\u4f60\u4eec\u770b\u4ed6\u662f\u4e0d\u662f\u8be5\u6b7b\uff1f\u2026 \u7ba1\u5bb6\u5a46\u9053\uff1a\u201c\u5f53\u7136\u8be5\u6b7b\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u94c1\u5b9a\u8be5\u6b7b\u3002\u201d \u6d77\u5947\u9614\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u60f3\u949f\u5148\u751f\u7684\u610f\u601d\u5f53\u7136\u4e5f\u8ddf\u4f60\u4eec\u4e00\u6837oo \u8868\u54e5\u9053\uff1a\u201c\u73b0\u5728\u53ea\u770b\u5c0f\u53f6\u59d1\u5a18\u7684\u610f\u601d\u4e86\u3002\u201d \u53f6\u7075\u54ac\u7740\u5634\u5507\uff0c\u7528\u773c\u89d2\u8198\u7740\u9646\u5c0f\u51e4\uff0c\u90a3\u773c\u5c31\u50cf\u662f\u6761\u5df2\u7ecf\u628a\u8001\u9f20\u6293\u5728\u624b\u91cc\u7684\u732b\u3002 \u5c31\u5728\u8fd9\u65f6\uff0c\u540e\u9762\u7684\u6697\u6797\u4e2d\u5ffd\u7136\u6709\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u95ee\u95ee\u6211\u7684\u610f\u601d\u3002\u201d \u6697\u6797\u4e2d\u5ffd\u7136\u6709\u4e86\u706f\u5149\u95ea\u52a8\uff0c\u4e00\u4e2a\u5bab\u9b13\u4e3d\u670d\u7684\u5c11\u5973\uff0c\u624b\u63d0\u7740\u7eb1\u706f\u8d70\u51fa\u6765\uff0c\u4e00\u4e2a\u5934\u53d1\u5f88 \u957f\u5f88\u957f\u7684\u5b89\u4eba\uff0c\u61d2\u6d0b\u6d0b\u7684\u8ddf\u5728\u4ed6\u4eec\u8eab\u540e\u3002 \u5979\u957f\u5f97\u5e76\u4e0d\u7f8e\uff0c\u98a7\u9aa8\u592a\u9ad8\u4e86\u4e9b\uff0c\u5634\u4e5f\u592a\u5927\u4e86\u4e9b\uff0c\u4e00\u53cc\u8ff7\u8ff7\u8499\u8499\u7684\u773c\u795e\uff0c\u603b\u50cf\u662f\u8fd8\u6ca1 \u6709\u7761\u9192\u3002 \u5979\u7a7f\u7740\u5f88\u968f\u4fbf\uff0c\u8eab\u4e0a\u2014\u4ef6\u5f88\u5bbd\u5927\u7684\u9ed1\u7761\u888d\uff0c\u597d\u50cf\u8fd8\u662f\u7537\u4eba\u7528\u7684\uff0c\u53ea\u7528\u4e00\u6839\u5e03\u5e26\u968f\u968f \u4fbf\u4fbf\u7684\u7cfb\u4f0e\uff0c\u957f\u53d1\u62ab\u6563\uff0c\u8d64\u7740\u53cc\u767d\u751f\u751f\u7684\u811a\uff0c\u8fde\u978b\u5b50\u90fd\u6ca1\u6709\u3002 \u4f46\u5979\u5374\u65e0\u7591\u662f\u4e2a\u5f88\u7279\u522b\u7684\u5973\u4eba\uff0c\u5927\u591a\u6570\u7537\u4eba\u53ea\u8981\u770b\u5979\u2014\u773c\uff0c\u7acb\u523b\u5c31\u4f1a\u88ab\u5979\u5438\u5f15\u4f4f\u3002 \u770b\u89c1\u5979\u8d70\u8fc7\u6765\uff0c\u8868\u54e5\u5374\u76b1\u8d77\u4e86\u7709\uff0c\u53f6\u7075\u5728\u6487\u5634\uff0c\u7ba1\u5bb6\u5a46\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u4f60\u770b\u4ed6\u662f\u4e0d\u662f \u8be5\u6b7b\uff1f\u201d \u5979\u7684\u56de\u7b54\u5f88\u5e72\u8106\uff1a\u201c\u4e0d\u8be5\u3002\u201d \u53f6\u7075\u672c\u6765\u5e76\u6ca1\u6709\u8868\u793a\u610f\u89c1\u7684\uff0c\u73b0\u5728\u5374\u4e00\u4e0b\u5b50\u8df3\u4e86\u8d77\u6765\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u8be5\uff1f\u201d \u8fd9\u5973\u4eba\u61d2\u6d0b\u6d0b\u7684\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8981\u5224\u4eba\u6b7b\u7f6a\uff0c\u81f3\u5c11\u603b\u5f97\u6709\u70b9\u8bc1\u636e\uff0c\u4f60\u4eec\u6709\u4ec0\u4e48\u8bc1 \u636e\uff1f\u201d \u7ba1\u5bb6\u5a46\u9053\uff1a\u201c\u949f\u5148\u751f\u7684\u5c38\u4f53\u5c31\u662f\u8bc1\u636e\u3002\u201d \u7a7f\u888d\u7684\u5973\u4eba\u9053\uff1a\u201c\u4f60\u6740\u4e86\u4eba\u540e\uff0c\u8fd8\u4f1a\u4e0d\u4f1a\u628a\u4ed6\u7684\u5c38\u4f53\u85cf\u5728\u81ea\u5df1\u7684\u5c4b\u91cc\uff1f\u201d \u7ba1\u5bb6\u5a46\u770b\u770b\u8868\u54e5\uff0c\u8868\u54e5\u770b\u770b\u6d77\u5947\u9614\uff0c\u4e09\u4e2a\u4eba\u90fd\u6ca1\u6709\u5f00\u53e3\u3002 \u53f6\u7075\u5374\u53c8\u8df3\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u6ca1\u6709\u8bc1\u636e\uff0c\u6211\u6709\u3002\u201d \u7a7f\u9ed1\u888d\u7684\u5973\u4eba\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\uff1f\u201d\u53f6\u7075\u9053\uff1a\u201c\u6211\u4eb2\u773c\u770b\u89c1\u4ed6\u51fa\u624b\u7684\u3002\u201d \u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u4e0d\u4f46\u9646\u5c0f\u51e4\u5413\u4e86\u2014\u8df3\uff0c\u8fde\u8868\u54e5\u4ed6\u4eec\u90fd\u597d\u50cf\u89c9\u5f97\u5f88\u610f\u5916\u3002 \u7a7f\u9ed1\u888d\u7684\u5973\u4eba\u8138\u4e0a\u5374\u8fde\u4e00\u70b9\u8868\u60c5\u90fd\u6ca1\u6709\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u771f\u7684\u770b\u89c1\u4e86\u4e5f\u6ca1\u6709 \u7528\u3002\u201d \u53f6\u7075\u9053\uff1a\u8c01\u8bf4\u6ca1\u6709\u7528\uff1f\u201d \u8fd9\u5973\u4eba\u9053\uff1a\u201c\u6211\u8bf4\u7684\u3002\u201d \u5979\u61d2\u6d0b\u6d0b\u7684\u8d70\u5230\u9646\u5c0f\u51e4\u9762\u524d\uff0c\u7528\u4e00\u53ea\u624b\u52fe\u4f4f\u8170\u5e26\uff0c\u4e00\u53ea\u624b\u62e2\u4e86\u62e2\u5934\u53d1\uff1a\u201c\u4f60\u4eec\u82e5\u6709 \u4eba\u4e0d\u670d\u6c14\uff0c\u4e0d\u59a8\u5148\u6765\u52a8\u52a8\u6211\u3002\u201d \u6d77\u5947\u9614\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u8fd9\u4e48\u6837\u505a\uff1f\u4e3a\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u7a7f\u9ed1\u888d\u7684\u5973\u4eba\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u9ad8\u5174\uff0c\u56e0\u4e3a\u4f60\u7ba1\u4e0d\u7740\u3002\u201d \u6d77\u5947\u9614\u77aa\u773c\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u903c\u6211\u4eec\u52a8\u624b\uff1f\u201d \u8fd9\u5973\u4eba\u9053\uff1a\u201c\u4f60\u6562\uff1f\u201d \u6d77\u5947\u9614\u77aa\u7740\u5979\uff0c\u773c\u775b\u91cc\u597d\u50cf\u8981\u55b7\u51fa\u706b\u6765\uff0c\u5374\u8fde\u4e00\u6839\u624b\u6307\u90fd\u4e0d\u6562\u52a8\u3002 \u8868\u54e5\u8138\u4e0a\u7684\u7b11\u5bb9\u5df2\u770b\u4e0d\u89c1\u4e86\uff0c\u8138\u8272\u5df2\u94c1\u9752\uff1a\u201c\u82b1\u5be1\u5987\uff0c\u4f60\u6700\u597d\u653e\u660e\u767d\u4e9b\uff0c\u59d3\u6d77\u7684\u5bf9 \u4f60\u6709\u610f\u601d\uff0c\u6211\u53ef\u6ca1\u6709\u3002\u201d \u82b1\u5be1\u5987\u7528\u773c\u89d2\u8198\u4e86\u5979\u4e00\u773c\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u80fd\u600e\u4e48\u6837\uff0c\u5c31\u51ed\u4f60\u4ece\u5df4\u5c71\u8001\u9053\u90a3\u91cc\u5b66\u6765\u7684 \u51e0\u624b\u5251\u6cd5\uff0c\u4e5f\u6562\u5728\u6211\u9762\u524d\u653e\u8086\uff1f\u201d \u8868\u54e5\u94c1\u9752\u7684\u8138\u7a81\u53c8\u6da8\u5f97\u901a\u7ea2\uff0c\u7a81\u7136\u5927\u559d\uff0c\u62d4\u5251\uff0c\u4e00\u67c4\u53ef\u4ee5\u7cfb\u5728\u8170\u4e0a\u7684\u8f6f\u5251\u3002 \u8f6f\u5251\u8fce\u98ce\u4e00\u6296\uff0c\u4f38\u5f97\u7b14\u76f4\uff0c\u5251\u5149\u95ea\u52a8\u95f4\uff0c\u4ed6\u5df2\u6251\u4e86\u8fc7\u6765\u3002 \u8fde\u9646\u5c0f\u51e4\u90fd\u60f3\u4e0d\u5230\u8fd9\u4e2a\u9634\u6c89\u505a\u4f5c\u7684\u4eba\uff0c\u813e\u6c14\u4e00\u53d1\u4f5c\u65f6\uff0c\u7adf\u4f1a\u53d8\u5f97\u5982\u6b64\u66b4\u8e81\u51b2\u52a8\u3002 \u82b1\u5be1\u5987\u5374\u65e9\u5df2\u60f3\u5230\u4e86\uff0c\u52fe\u5728\u8863\u5e26\u4e0a\u7684\u624b\u4e00\u6296\uff0c\u8fd9\u6761\u8f6f\u8f6f\u7684\u5e03\u5e26\u7adf\u4e5f\u88ab\u5979\u8fce\u98ce\u6296\u5f97\u7b14 \u76f4\uff0c\u6bd2\u86c7\u822c\u4e00\u5377\uff0c\u5df2\u5377\u4f4f\u4e86\u8868\u54e5\u7684\u5251\u3002 \u53ea\u6709\u6700\u597d\u7684\u94c1\uff0c\u624d\u80fd\u6253\u9020\u8f6f\u5251\uff0c\u8c01\u77e5\u4ed6\u7684\u5251\u950b\u7adf\u8fde\u8863\u5e26\u90fd\u5272\u4e0d\u65ad\u3002\u82b1\u5be1\u5987\u7684\u624b\u518d\u4e00 \u6296\uff0c\u8863\u5e26\u53c8\u98de\u51fa\uff1a\u201c\u62cd\u201d\u7684\u4e00\u58f0\uff0c\u6253\u5728\u8868\u54e5\u8138\u4e0a\u3002 \u8868\u54e5\u7684\u8138\u7ea2\u4e86\uff0c\u9646\u5c0f\u51e4\u7684\u8138\u4e5f\u6709\u70b9\u53d1\u7ea2\u3002 \u4ed6\u5ffd\u7136\u53d1\u73b0\u82b1\u5be1\u5987\u7684\u5bbd\u888d\u4e0b\u4ec0\u4e48\u90fd\u6ca1\u6709\u3002 \u8863\u5e26\u98de\u51fa\uff0c\u8863\u895f\u6563\u5f00\uff0c\u5979\u8eab\u4e0a\u6700\u91cd\u8981\u7684\u90e8\u5206\u51e0\u4e4e\u5168\u9732\u4e86\u51fa\u6765\u3002 \u53ef\u662f\u5979\u81ea\u5df1\u4e00\u70b9\u4e5f\u4e0d\u5728\u4e4e\uff0c\u8fd8\u662f\u61d2\u6d0b\u6d0b\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8fd8\u60f3\u8bd5\u8bd5\uff1f\u201d \u8868\u54e5\u7684\u786e\u8fd8\u60f3\u8bd5\u8bd5\uff0c\u53ef\u60dc\u7ba1\u5bb6\u5a46\u548c\u6d77\u5947\u9614\u5df2\u6321\u4f4f\u4e86\u4ed6\u3002 \u6d77\u5947\u9614\u5589\u7ed3\u6eda\u52a8\uff0c\u60f3\u628a\u76ee\u5149\u4ece\u82b1\u5be1\u5987\u8863\u895f\u91cc\u79fb\u5f00\uff0c\u5374\u8fde\u2014\u5bf8\u90fd\u4e0d\u52a8\u3002 \u82b1\u5be1\u5987\u7684\u5e74\u7eaa\u7b97\u6765\u5df2\u4e0d\u5c0f\uff0c\u53ef\u662f\u5979\u7684\u8eaf\u8eab\u770b\u6765\u8fd8\u662f\u50cf\u5c11\u5973\u4e00\u6837\uff0c\u53ea\u4e0d\u8fc7\u8fdc\u6bd4\u5c11\u5973\u66f4 \u8bf1\u4eba\uff0c\u66f4\u6210\u719f\u3002 \u6d77\u5947\u9614\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u5148\u628a\u8863\u670d\u7cfb\u4e0a\u518d\u8bf4\u8bdd\uff1f\u201d \u82b1\u5be1\u5987\u7684\u56de\u7b54\u8fd8\u662f\u90a3\u4e48\u5e72\u8106\uff1a\u201c\u4e0d\u80fd\u3002\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u9ad8\u5174\uff0c\u4e5f\u56e0\u4e3a\u4f60\u7ba1\u4e0d\u7740\u3002 \u7ba1\u5bb6\u5a46\u62a2\u7740\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u7a76\u7adf\u60f3\u600e\u4e48\u6837\uff1f\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u60f3\u600e\u4e48\u6837\uff0c\u53ea\u4e0d\u8fc7\u9646\u5c0f\u51e4\u662f\u8001\u5200\u628a\u5b50\u81ea\u5df1\u653e\u8fdb\u6765\u7684\u4eba\uff0c\u65e0\u8bba\u8c01 \u8981\u6740\u4ed6\uff0c\u90fd\u5f97\u7b49\u8001\u5200\u628a\u5b50\u56de\u6765\u518d\u8bf4\u3002\u201d \u7ba1\u5bb6\u5a46\u9053\uff1a\u201c\u73b0\u5728\u5462\uff1f\u201d\u82b1\u5be1\u5987\u9053\uff1a\u201c\u73b0\u5728\u5f53\u7136\u7531\u6211\u628a\u4ed6\u5e26\u8d70\u3002\u201d \u53f6\u7075\u53c8\u8df3\u8d77\u6765\uff0c\u8df3\u5f97\u66f4\u9ad8\uff1a\u201c\u51ed\u4ec0\u4e48\u4f60\u8981\u628a\u4ed6\u5e26\u8d70\uff1f\u201d \u82b1\u5be1\u5987\u6de1\u6de1\u9053\uff1a\u201c\u53ea\u51ed\u6211\u8fd9\u6761\u5e26\u5b50\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u8fd9\u6761\u5e26\u5b50\u80fd\u600e\u4e48\u6837\uff1f\u201d \u82b1\u5be1\u5987\u60a0\u7136\u9053\uff1a\u201c\u8fd9\u6761\u5e26\u5b50\u4e5f\u4e0d\u80fd\u600e\u4e48\u6837\uff0c\u6700\u591a\u53ea\u4e0d\u8fc7\u80fd\u7ed1\u4f4f\u4f60\uff0c\u5265\u5149\u4f60\u7684\u8863\u88f3\uff0c \u8ba9\u94a9\u5b50\u9a91\u5728\u4f60\u8eab\u4e0a\u53bb\u3002\u201d \u53f6\u7075\u7684\u8138\u5df2\u6da8\u5f97\u901a\u7ea2\uff0c\u62f3\u5934\u4e5f\u5df4\u63e1\u7d27\uff0c\u5374\u504f\u504f\u4e0d\u6562\u6253\u51fa\u6765\uff0c\u53ea\u6709\u8dfa\u7740\u811a\uff0c\u6068\u6068\u9053\uff1a \u201c\u6211\u59d0\u59d0\u82e5\u662f\u56de\u6765\u4e86\uff0c\u770b\u4f60\u8fd8\u6562\u4e0d\u6562\u8fd9\u4e48\u653e\u8086\u3002\u201d \u82b1\u5be1\u5987\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u59d0\u59d0\u6ca1\u6709\u56de\u6765\uff0c\u6240\u4ee5\u4f60\u53ea\u6709\u770b\u7740\u6211\u628a\u4ed6\u5e26\u8d70\u3002\u201d \u5979\u62c9\u8d77\u4e86\u9646\u5c0f\u51e4\u7684\u624b\uff0c\u56de\u7738\u7b11\u9053\uff1a\u201c\u6211\u90a3\u91cc\u6709\u5f20\u7279\u522b\u5927\u7684\u5e8a\uff0c\u8db3\u591f\u8ba9\u6211\u4eec\u4e24\u4e2a\u4eba\u90fd \u7761\u5f97\u5f88\u8212\u670d\uff0c\u4f60\u8fd8\u4e0d\u8d76\u5feb\u8ddf\u6211\u8d70\uff1f\u201d \u5979\u5c45\u7136\u771f\u7684\u5e26\u7740\u9646\u5c0f\u51e4\u8d70\u4e86\uff0c\u5927\u5bb6\u5c45\u7136\u771f\u7684\u53ea\u6709\u773c\u7741\u7741\u7684\u770b\u7740\u3002 \u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u53f6\u7075\u5ffd\u7136\u9053\uff1a\u201c\u8001\u94a9\u5b50\uff0c\u4f60\u662f\u4e0d\u662f\u4e1c\u897f\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u6211\u4e0d\u662f\u4e1c\u897f\uff0c\u6211\u662f\u4eba\u3002\u201d \u53f6\u7075\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4ed6\u5988\u7684\u4e5f\u80fd\u7b97\u662f\u4e2a\u4eba\uff1f\u8fd9\u91cc\u660e\u660e\u53ea\u6709\u4f60\u80fd\u5bf9\u4ed8\u90a3\u6bcd\u72d7\uff0c\u4f60\u4e3a\u4ec0\u4e48 \u4e0d\u6562\u51fa\u4e4e\uff1f\u201d\u6d77\u5947\u9614\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u8fd8\u60f3\u8981\u5979\u966a\u6211\u7761\u89c9\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u4f60\u771f\u7684\u8fd9\u4e48\u60f3\u5973\u4eba\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u60f3\u5f97\u8981\u547d\u3002\u201d\u53f6\u7075\u9053\uff1a\u201c\u597d\uff0c\u4f60\u82e5\u6740\u4e86\u5979\uff0c\u6211\u5c31\u966a\u4f60\u7761\u89c9\uff0c\u7761\u4e09 \u5929\u3002\u201d \u6d77\u5947\u9614\u7b11\u4e86\uff1a\u201c\u4f60\u5728\u5403\u918b\uff1f\u4f60\u4e5f\u559c\u6b22\u9646\u5c0f\u51e4\uff1f\u201d \u53f6\u7075\u54ac\u7740\u7259\uff0c\u72e0\u72e0\u9053\uff1a\u201c\u4e0d\u7ba1\u6211\u662f\u4e0d\u662f\u5403\u918b\uff0c\u53cd\u6b63\u6211\u8fd9\u6b21\u8bf4\u7684\u8bdd\u4e00\u5b9a\u7b97\u6570\uff0c\u6211\u8fd8\u5e74 \u9752\uff0c\u90a3\u6bcd\u72d7\u5374\u5df2\u662f\u8001\u592a\u5a46\u4e86\uff0c\u81f3\u5c11\u8fd9\u4e00\u70b9\u6211\u603b\u6bd4\u5979\u5f3a\u3002\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u201d \u53f6\u7075\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u60f3\u5148\u770b\u770b\u8d27\uff1f\u597d\uff01\u201d \u5979\u5ffd\u7136\u6495\u5f00\u81ea\u5df1\u7684\u88e4\u811a\uff0c\u9732\u51fa\u2014\u53cc\u5149\u6ed1\u5706\u6da6\u7684\u817f\u3002 \u6d77\u5947\u9614\u7684\u773c\u775b\u53c8\u53d1\u76f4\u4e86\uff1a\u201c\u6211\u53ea\u80fd\u770b\u8fd9\u4e48\u591a\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u4f60\u82e5\u8fd8\u60f3\u770b\u522b\u7684\uff0c\u5148\u53bb\u5bb0\u4e86\u90a3\u6bcd\u72d7\u518d\u8bf4\u3002\u201d \u7b2c\u4e03\u7ae0\u3000\u501f\u9152\u540c\u6d47\u6101>> \u53e4\u9f99\u300a\u9646\u5c0f\u51e4\u7cfb\u5217\xb7\u5e7d\u7075\u5c71\u5e84\u300b \u7b2c\u4e03\u7ae0\u3000\u501f\u9152\u540c\u6d47\u6101 \u5e8a\u679c\u7136\u5f88\u5927\uff0c\u5e8a\u5355\u96ea\u767d\uff0c\u88ab\u8925\u5d2d\u65b0\uff0c\u4e00\u8d70\u8fdb\u6765\uff0c\u82b1\u5be1\u5987\u5c31\u61d2\u6d0b\u6d0b\u7684\u5012\u5728\u5e8a\u4e0a\u3002 \u9646\u5c0f\u98ce\u7ad9\u7740\uff0c\u7ad9\u5728\u5e8a\u5934\u3002 \u82b1\u5be1\u5987\u7528\u2014\u53cc\u8ff7\u8ff7\u8499\u8499\u7684\u773c\u775b\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u7684\u6253\u91cf\u7740\u4ed6\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u73b0\u5728\u4f60\u60f3\u5fc5\u5df2\u77e5 \u9053\u6211\u5c31\u662f\u90a3\u4e2a\u53ef\u6015\u7684\u82b1\u5be1\u5987\u3002\u201d\u9646\u5c0f\u98ce\u70b9\u70b9\u5934\u3002 \u82b1\u5be1\u5987\u9053\uff1a\u201c\u4f60\u5f53\u7136\u4e5f\u542c\u4eba\u8bf4\u8fc7\u6211\u662f\u6761\u6bcd\u72d7\uff0c\u4f1a\u5403\u4eba\u7684\u6bcd\u72d7\u3002\u201d \u9646\u5c0f\u98ce\u53c8\u70b9\u70b9\u5934\u3002 \u82b1\u5be1\u5987\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u91cc\u6bcf\u4e2a\u4eba\u90fd\u8ba4\u4e3a\u6211\u968f\u65f6\u53ef\u4ee5\u966a\u4ed6\u4e0a\u5e8a\u7761\u89c9\uff1f\u201d \u9646\u5c0f\u98ce\u8fd8\u662f\u5728\u70b9\u5934\u3002 \u82b1\u5be1\u5987\u773c\u775b\u91cc\u4eff\u4f5b\u6709\u96fe\uff1a\u201c\u90a3\u672b\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u4e0a\u6765\uff1f\u201d \u9646\u5c0f\u98ce\u8fde\u52a8\u90fd\u6ca1\u6709\u52a8\u3002 \u82b1\u5be1\u5987\u9053\uff1a\u201c\u4f60\u4e0d\u6562\uff1f\u201d \u9646\u5c0f\u98ce\u4e0d\u518d\u70b9\u5934\uff0c\u4e5f\u6ca1\u6709\u6447\u5934\u3002 \u82b1\u5be1\u5987\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u5f53\u7136\u8fd8\u4e0d\u6562\uff0c\u56e0\u4e3a\u6211\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u4f60\u8fd8\u4e0d\u77e5\u9053\uff01\u201d \u9646\u5c0f\u98ce\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u80fd\u5c06\u6dee\u5357\u67f3\u5bb6\u7684\u72ec\u95e8\u771f\u6c14\uff0c\u548c\u70b9\u82cd\u79d8\u4f20\u2018\u6d41\u4e91\u5251\u6cd5\u2019\u6eb6 \u800c\u4e3a\u4e00\u7684\u4eba\u5e76\u4e0d\u591a\uff0c\u6240\u4ee5\u2026\u2026\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u6240\u4ee5\u600e\u4e48\u6837\uff1f\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4e00\u5b9a\u662f\u6dee\u5357\u5927\u4fa0\u7684\u5973\u4eba\uff0c\u70b9\u82cd\u5251\u5ba2\u7684\u59bb\u5b50\u67f3\u9752\u9752\u3002\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u9053\u6211\u8ddf\u8c22\u575a\u56db\u4e2a\u6700\u597d\u7684\u670b\u53cb\u90fd\u4e0a\u8fc7\u5e8a\uff1f\u201d \u9646\u5c0f\u98ce\u627f\u8ba4\uff0c\u8fd9\u672c\u5c31\u662f\u4ef6\u5f88\u8f70\u52a8\u7684\u4e11\u95fb\u3002 \u82b1\u5be1\u5987\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4ec0\u4e48\u90fd\u77e5\u9053\u4e86\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u4e0a\u6765\uff1f\u201d \u9646\u5c0f\u98ce\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e0d\u9ad8\u5174\uff0c\u4e5f\u56e0\u4e3a\u4f60\u7ba1\u4e0d\u7740\u3002 \u82b1\u5be1\u5987\u4e5f\u7b11\u4e86\uff1a\u201c\u770b\u6765\u4f60\u8fd9\u4e2a\u4eba\u679c\u7136\u8ddf\u522b\u7684\u7537\u4eba\u6709\u70b9\u4e0d\u540c\u3002\u201d \u5979\u5ffd\u53c8\u4ece\u5e8a\u4e0a\u4e00\u8dc3\u800c\u8d77\uff1a\u201c\u6765\uff0c\u6211\u8bf7\u4f60\u559d\u9152\u3002\u201d \u9152\u610f\u6e10\u6d53\uff0c\u5979\u773c\u775b\u91cc\u7684\u96fe\u4e5f\u66f4\u6d53\u3002 \u5c31\u56e0\u4e3a\u8fd9\u5c71\u8c37\u91cc\u603b\u662f\u6709\u96fe\uff0c\u6240\u4ee5\u6c38\u8fdc\u90fd\u80fd\u4fdd\u6301\u5b83\u7684\u795e\u79d8\u3002 \u5979\u7684\u4eba\u662f\u4e0d\u662f\u4e5f\u4e00\u6837\uff1f \u8981\u770b\u5230\u5979\u8d64\u88f8\u7684\u8eaf\u4f53\u4e5f\u8bb8\u5e76\u4e0d\u56f0\u96be\uff0c\u8981\u770b\u5230\u5979\u7684\u5fc3\u4e5f\u8bb8\u5c31\u5f88\u4e0d\u5bb9\u6613\u4e86\u3002 \u53c8\u559d\u4e86\u676f\u9152\uff0c\u5979\u5ffd\u7136\u95ee\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6d77\u5947\u9614\u4e3a\u4ec0\u4e48\u603b\u60f3\u8981\u6211\u966a\u4ed6\u4e0a\u5e8a\uff1f\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u8ba4\u4e3a\u4f60\u8ddf\u8fd9\u5730\u65b9\u522b\u7684\u7537\u4eba\u90fd\u4e0a\u8fc7\u5e8a\u3002 \u82b1\u5be1\u5987\u7b11\u4e86\uff1a\u201c\u6bcf\u4e2a\u4eba\u90fd\u8fd9\u4e48\u60f3\uff0c\u5176\u5b9e\u2026\u2026\u6211\u771f\u6b63\u966a\u8fc7\u51e0\u4e2a\u7537\u4eba\u4e0a\u5e8a\uff0c\u53ea\u6015\u8fde\u4f60\u90fd \u60f3\u4e0d\u5230\u3002\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u5728\u8fd9\u91cc\u4e00\u4e2a\u90fd\u6ca1\u6709\uff1f\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u53ea\u6709\u4e00\u4e2a\u3002\u201d \u9646\u5c0f\u98ce\u5f00\u59cb\u559d\u9152\u3002 \u82b1\u5be1\u5987\u7684\u773c\u6ce2\u5374\u4f3c\u5df2\u5230\u4e86\u8fdc\u65b9\uff0c\u8fdc\u65b9\u6709\u4e00\u6761\u98d8\u6e3a\u7684\u4eba\u5f71\uff0c\u5979\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u7231\u6155\u3002 \u8fc7\u4e86\u5f88\u4e45\uff0c\u5979\u624d\u4ece\u68a6\u4e2d\u60ca\u9192\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u95ee\u6211\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u95ee\uff1f\u201d \u82b1\u5be1\u5987\u7b11\u4e86\uff1a\u201c\u4f60\u8fd9\u4eba\u679c\u7136\u5f88\u7279\u522b\uff0c\u6211\u559c\u6b22\u7279\u522b\u7684\u7537\u4eba\u3002 \u5979\u7684\u7b11\u5bb9\u5ffd\u53c8\u6d88\u5931\uff1a\u201c\u8c22\u575a\u672c\u6765\u4e5f\u662f\u4e2a\u5f88\u7279\u522b\u7684\u7537\u4eba\uff0c\u6211\u5ac1\u7ed9\u4ed6\uff0c\u53ea\u56e0\u4e3a\u90a3\u65f6\u6211\u771f \u7684\u559c\u6b22\u4ed6\u3002\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u53ef\u662f\u540e\u6765\u4f60\u53d8\u4e86\u3002\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u53d8\u7684\u4e0d\u662f\u6211\uff0c\u662f\u4ed6\u3002\u201d \u5979\u773c\u775b\u91cc\u7684\u96fe\u5ffd\u7136\u88ab\u5212\u5f00\u4e86\u4e00\u7ebf\uff0c\u88ab\u4e00\u67c4\u5145\u6ee1\u4e86\u4ec7\u6068\u548c\u60b2\u75db\u548c\u5229\u5251\u5212\u5f00\u7684\uff1a\u201c\u4f60\u6c38 \u8fdc\u4e0d\u4f1a\u60f3\u5230\u4ed6\u53d8\u6210\u4e86\u4e2a\u4ec0\u4e48\u6837\u7684\uff0c\u4eba\uff0c\u66f4\u4e0d\u4f1a\u60f3\u5230\u4ed6\u505a\u7684\u4e8b\u6709\u591a\u4e48\u53ef\u6015\u3002\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u53ef\u6015\uff1f\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u4e3a\u4ec0\u4e48\u4f1a\u8ddf\u4ed6\u7684\u597d\u670b\u53cb\u4e0a\u5e8a\u7684\uff1f\u201d \u5979\u7684\u624b\u63e1\u7d27\uff0c\u773c\u4e2d\u5df2\u6709\u6cea\u73e0\u6eda\u4e0b\uff1a\u201c\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u4ed6\u8981\u6211\u8fd9\u4e48\u6837\u505a\uff0c\u4ed6\u559c\u6b22\u770b\u2026\u2026 \u4ed6\u751a\u81f3\u4e0d\u60dc\u8dea\u4e0b\u6765\u6c42\u6211\uff0c\u751a\u81f3\u7528\u4ed6\u7684\u5251\u6765\u903c\u6211\u2026\u2026\u201d \u9646\u5c0f\u98ce\u5ffd\u7136\u626d\u8fc7\u5934\uff0c\u996e\u5c3d\u4e86\u676f\u4e2d\u7684\u9152\uff0c\u4ed6\u5ffd\u7136\u89c9\u5f97\u80c3\u90e8\u62bd\u7f29\uff0c\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u5455\u5410\u3002 \u7b49\u4ed6\u56de\u8fc7\u5934\u6765\u65f6\uff0c\u82b1\u5be1\u5987\u5df2\u6084\u6084\u7684\u5c06\u9762\u4e0a\u6cea\u75d5\u64e6\u5e72\u4e86\u3002 \u5979\u4e5f\u559d\u5e72\u4e86\u676f\u4e2d\u7684\u9152\uff1a\u201c\u4f60\u4e00\u5b9a\u5f88\u5947\u602a\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u544a\u8bc9\u4f60\u8fd9\u4e9b\u4e8b\u3002 \u9646\u5c0f\u98ce\u5e76\u4e0d\u5947\u602a\uff0c\u4e00\u70b9\u4e5f\u4e0d\u5947\u602a\u3002 \u4e00\u4e2a\u4eba\u5fc3\u91cc\u7684\u75db\u82e6\u548c\u60b2\u4f24\uff0c\u82e5\u662f\u5df2\u88ab\u9690\u85cf\u6291\u5236\u5f97\u592a\u4e45\uff0c\u603b\u662f\u8981\u627e\u4e2a\u4eba\u503e\u8bc9\u7684\u3002 \u82b1\u5be1\u5987\u7684\u75db\u82e5\u867d\u7136\u6709\u4e86\u53d1\u6cc4\uff0c\u9152\u610f\u5374\u66f4\u6d53\uff1a\u201c\u4ed6\u867d\u7136\u5df2\u662f\u4e2a\u8001\u4eba\uff0c\u5374\u662f\u4e2a\u771f\u6b63\u7684\u7537 \u4eba\uff0c\u4e5f\u4f17\u4e0d\u540c\u7684\u7537\u4eba\uff0c\u4e5f\u8bb8\u6211\u5e76\u4e0d\u559c\u6b22\u4ed6\uff0c\u53ef\u662f\u6211\u4f69\u670d\u4ed6\uff0c\u53ea\u8981\u80fd\u8ba9\u4ed6\u6109\u5feb\uff0c\u6211\u613f\u610f\u4e3a \u4ed6\u505a\u4efb\u4f55\u4e8b\u3002\u201d \u5979\u62ac\u8d77\u5934\uff0c\u76ef\u7740\u9646\u5c0f\u98ce\uff1a\u201c\u7b49\u4f60\u89c1\u8fc7\u4ed6\u4e4b\u540e\uff0c\u4e00\u5b9a\u4e5f\u4f1a\u559c\u6b22\u4ed6\u8fd9\u4e2a\u4eba\u7684\u3002\u201d \u9646\u5c0f\u98ce\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u2026\u2026\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u6211\u8bf4\u7684\u662f\u8001\u5200\u628a\u5b50\u3002\u201d \u9646\u5c0f\u98ce\u5403\u4e86\u4e00\u60ca\uff1a\u201c\u8001\u5200\u628a\u5b50\uff1f\u201d \u82b1\u5be1\u5987\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u6211\u5728\u8fd9\u91cc\u552f\u4e00\u7684\u4e00\u4e2a\u7537\u4eba\uff0c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u60f3\u4e0d\u5230 \u7684\u3002\u201d \u5979\u7b11\u4e86\u7b11\uff0c\u7b11\u5f97\u5f88\u51c4\u51c9\uff1a\u201c\u6211\u672c\u6765\u603b\u8ba4\u4e3a\u8fd9\u4e16\u754c\u4e0a\u5df2\u6ca1\u6709\u4eba\u4f1a\u4e86\u89e3\u6211\uff0c\u540c\u60c5\u6211\uff0c\u53ef \u662f\u4ed6\u4e86\u89e3\u6211\uff0c\u540c\u60c5\u6211\uff0c\u800c\u4e14\u51fa\u81ea\u771f\u5fc3\u3002\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u732e\u8eab\u7ed9\u4ed6\uff1f\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u6211\u751a\u81f3\u53ef\u4ee5\u4e3a\u4ed6\u727a\u7272\u4e00\u5207\uff0c\u5c31\u7b97\u4ed6\u53eb\u6211\u53bb\u6b7b\uff0c\u6211\u4e5f\u4f1a\u53bb\u6b7b\u7684\uff0c\u53ef \u662f\u2026\u2026\u53ef\u662f\u2026\u2026\u201d \u5979\u5f88\u5feb\u7684\u53c8\u559d\u4e86\u676f\u9152\uff1a\u201c\u53ef\u662f\u6211\u5e76\u4e0d\u559c\u6b22\u4ed6\uff0c\u6211\u2026\u2026\u6211 \u5979\u6ca1\u6709\u8bf4\u4e0b\u53bb\uff0c\u8fd9\u79cd\u60c5\u611f\u5c31\u662f\u65e0\u6cd5\u53d9\u8bf4\u7684\uff0c\u5979\u77e5\u9053\u9646\u5c0f\u98ce\u4e00\u5b9a\u80fd\u4e86\u89e3\u3002 \u9646\u5c0f\u98ce\u7684\u786e\u80fd\u4e86\u89e3\uff0c\u4e0d\u4f46\u80fd\u4e86\u89e3\u8fd9\u79cd\u611f\u60c5\uff0c\u4e5f\u4e86\u89e3\u4e86\u8001\u5200\u628a\u5b50\u8fd9\u4e2a\u4eba\u3002 \u201c\u6211\u82e5\u662f\u4f60\uff0c\u6211\u4e5f\u4f1a\u8fd9\u4e48\u6837\u505a\u7684\u3002\u201d\u4ed6\u67d4\u58f0\u9053\uff1a\u201c\u6211\u60f3\u4ed6\u4e00\u5b9a\u662f\u4e2a\u5f88\u4e0d\u5e73\u51e1\u7684 \u4eba\u3002\u201d \u82b1\u5be1\u5987\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u5c31\u597d\u50cf\u521a\u653e\u4e0b\u526f\u5f88\u91cd\u5f88\u91cd\u7684\u62c5 \u77e5\u9053\u8fd9\u4e16\u4e0a\u8fd8\u6709\u4e2a\u4eba\u80fd\u4e86\u89e3\u81ea\u5df1\u7684\u60b2\u75db\u548c\u82e6\u607c\uff0c\u65e0\u8bba\u5bf9\u8c01\u8bf4\u6765\uff0c\u90fd\u662f\u4ef6\u5f88\u4e0d\u9519\u7684 \u4e8b\u3002 \u5979\u770b\u7740\u9646\u5c0f\u98ce\uff0c\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u6b23\u6170\u548c\u611f\u6fc0\uff1a\u201c\u81ea\u4ece\u5230\u8fd9\u91cc\u6765\u4e86\u4e4b\u540e\uff0c\u6211\u4ece\u6765\u4e5f\u6ca1\u6709 \u50cf\u4eca\u5929\u8fd9\u4e48\u6837\u5f00\u5fc3\u8fc7\uff0c\u6765\uff0c\u6211\u656c\u4f60\uff0c\u656c\u4f60\u4e09\u676f\u3002\u201d \u201c\u518d\u559d\u53ea\u6015\u5c31\u8981\u9189\u4e86\u3002\u201d \u201c\u9189\u4e86\u53c8\u4f55\u59a8\u3002\u201d\u5979\u518d\u4e3e\u676f\uff1a\u201c\u5047\u5982\u771f\u7684\u80fd\u9189\uff0c\u6211\u66f4\u611f\u6fc0\u4f60\u3002\u201d \u9646\u5c0f\u98ce\u5927\u7b11\uff1a\u201c\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u6211\u4e5f\u65e9\u5c31\u60f3\u75db\u75db\u5feb\u5feb\u7684\u5927\u9189\u2014\u6b21\u3002\u201d \u4e8e\u662f\u4ed6\u4eec\u90fd\u9189\u4e86\uff0c\u9189\u5012\u5728\u5e8a\u4e0a\u3002 \u4ed6\u4eec\u4e92\u76f8\u62e5\u62b1\u7740\uff0c\u8bf4\u4e9b\u522b\u4eba\u6c38\u8fdc\u90fd\u542c\u4e0d\u61c2\u7684\u9189\u8bdd\uff0c\u56e0\u4e3a\u4ed6\u4eec\u5fc3\u91cc\u90fd\u592a\u5bc2\u5bde\uff0c\u90fd\u6709\u592a \u591a\u89e3\u4e0d\u5f00\u7684\u7ed3\u3002 \u4ed6\u4eec\u867d\u7136\u62e5\u62b1\u5f97\u5f88\u7d27\uff0c\u4e00\u9897\u5fc3\u5374\u7eaf\u6d01\u5f97\u50cf\u662f\u4e2a\u5b69\u5b50\uff0c\u4e5f\u8bb8\u5728\u4ed6\u4eec\u8fd9\u4e00\u751f\u4e2d\u90fd\u6ca1\u6709\u50cf \u73b0\u5728\u8fd9\u4e48\u6837\u7eaf\u6d01\u5766\u7136\u8fc7\u3002 \u8fd9\u53c8\u662f\u79cd\u4ec0\u4e48\u6837\u7684\u611f\u60c5\uff1f \u9752\u6625\u5df2\u5c06\u901d\u53bb\uff0c\u5f80\u4e8b\u4e0d\u582a\u56de\u9996\uff0c\u2014\u4e2a\u53d7\u5c3d\u4e86\u553e\u9a82\u4fae\u8fb1\u7684\u5973\u4eba\uff0c\u2014\u4e2a\u6ca1\u6709\u6839\u7684\u6d6a\u5b50\uff0c \u8fd9\u4e16\u4e0a\u53c8\u6709\u8c01\u80fd\u4e86\u89e3\u4f60\u4eec\u7684\u611f\u60c5\uff1f \u4f60\u4eec\u65e2\u7136\u540c\u662f\u6ca6\u843d\u5728\u5929\u6daf\u7684\u4eba\uff0c\u4f60\u4eec\u65e2\u7136\u5df2\u76f8\u9022\u76f8\u8bc6\uff0c\u53c8\u4f55\u5fc5\u8981\u522b\u4eba\u6765\u4e86\u89e3\u4f60\u4eec\u7684 \u611f\u60c5\uff1f \u7a97\u5916\u591c\u6df1\u6c89\uff0c\u96fe\u4e5f\u6df1\u6c89\u3002 \u7a97\u5b50\u5c45\u7136\u6ca1\u6709\u5173\u7d27\uff0c\u51b7\u96fe\u4e2d\u5ffd\u7136\u51fa\u73b0\u4e86\u4e00\u6761\u4eba\u5f71\uff0c\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u6028\u6bd2\u548c\u5ac9\u6068\u3002 \u7136\u540e\u7a97\u9699\u91cc\u53c8\u51fa\u73b0\u4e86\u4e00\u6839\u5439\u7ba1\u3002 \u4e4c\u9ed1\u7684\u5439\u7ba1\uff0c\u6697\u7d2b\u8272\u7684\u70df\u3002 \u70df\u96fe\u6563\u5f00\uff0c\u4e0d\u9189\u7684\u4eba\u4e5f\u8981\u9189\u4e86\uff0c\u975e\u9189\u4e0d\u53ef\u3002 \u8fd9\u4e2a\u4eba\u6709\u628a\u63e1\uff0c\u56e0\u4e3a\u4ed6\u7528\u7684\u662f\u8ff7\u9999\u4e2d\u6700\u6709\u6548\u7684\u4e00\u79cd\u201c\u9500\u9b42\u8680\u9aa8\u6563\u201d\u4ed6\u5df2\u7528\u8fc7\u5341\u4e09 \u6b21\uff0c\u4ece\u672a\u6709\u2018\u6b21\u5931\u624b\u3002 \u9646\u5c0f\u98ce\u548c\u82b1\u5be1\u5987\u9192\u6765\u65f6\uff0c\u5df2\u4e0d\u5728\u90a3\u5f20\u5bbd\u5927\u800c\u67d4\u8f6f\u7684\u5e8a \u5730\u7a96\u91cc\u5bd2\u51b7\u800c\u6f6e\u6e7f\uff0c\u4ed6\u4eec\u5c31\u8eba\u5728\u8fd9\u5730\u7a96\u7684\u89d2\u843d\u91cc\uff0c\u6709\u8c01\u77e5\u9053\u4ed6\u4eec\u662f\u600e\u4e48\u4f1a\u5230\u8fd9\u91cc\u6765 \u7684\uff1f \u53ea\u6709\u4e00\u4e2a\u4eba\u77e5\u9053\u3002 \u5730\u7a96\u91cc\u53ea\u6709\u4e00\u5f20\u6905\u5b50\uff0c\u8868\u54e5\u5c31\u5750\u5728\u8fd9\u5f20\u6905\u5b50\u4e0a\uff0c\u51b7\u51b7\u7684\u770b\u7740\u4ed6\u4eec\uff0c\u773c\u775b\u91cc\u5145\u6ee1\u4e86\u6028 \u6bd2\u548c\u5ac9\u6068\u3002 \u770b\u89c1\u4e86\u4ed6\uff0c\u82b1\u5be1\u5987\u5c31\u5fcd\u4e0d\u4f4f\u53eb\u4e86\u8d77\u6765\uff1a\u201c\u662f\u4f60\uff01\u201d \u201c\u4f60\u60f3\u4e0d\u5230\uff1f\u201d \u201c\u6211\u7684\u786e\u60f3\u4e0d\u5230\u3002\u201d\u82b1\u5be1\u5987\u51b7\u7b11\u9053\uff1a\u201c\u5df4\u5c71\u5251\u5ba2\u95e8\u4e0b\u5b50\u5f1f\uff0c\u5c45\u7136\u4e5f\u4f1a\u7528\u8fd9\u79cd\u4e0b\u4e94\u95e8 \u7684\u8ff7\u9999\u6697\u5668\u3002 \u201c\u4f60\u60f3\u4e0d\u5230\u7684\u4e8b\u60c5\u6709\u5f88\u591a\u3002\u201d\u8868\u54e5\u5728\u5fae\u7b11\u3002 \u201c\u53ef\u662f\u73b0\u5728\u6211\u603b\u7b97\u5df2\u5168\u90fd\u60f3\u901a\u4e86\u3002\u201d \u5230\u8fd9\u91cc\u6765\u7684\u4eba\uff0c\u90fd\u662f\u6709\u5408\u7ea6\u7684\uff0c\u8001\u5200\u628a\u5b50\u7684\u5408\u7ea6\u4e00\u5411\u5b89\u5168\u53ef\u9760\u3002 \u4f46\u662f\u8fd1\u5e74\u6765\u5e7d\u7075\u5c71\u5e84\u91cc\u4e5f\u6709\u5f88\u591a\u4eba\u65e0\u7f18\u65e0\u6545\u7684\u5931\u8e2a\u4e86\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u4eba\u4e0b\u7684\u6bd2 \u624b\uff01 \u201c\u662f\u4f60\uff1a\u201c\u82b1\u5be1\u5987\u4e0b\u4e86\u7ed3\u8bba\uff1a\u201c\u73b0\u5728\u6211\u624d\u77e5\u9053\u662f\u4f60\uff01\u201d \u8868\u54e5\u5e76\u4e0d\u5426\u8ba4\u3002 \u201c\u53ea\u53ef\u60dc\u8c01\u4e5f\u60f3\u4e0d\u5230\u662f\u6211\u3002\u201d\u4ed6\u5fae\u7b11\u7740\uff1a\u201c\u8fd9\u4e00\u6b21\u6211\u6740\u4e86\u4f60\u4eec\uff0c\u8fd8\u662f\u4e0d\u4f1a\u6709\u4eba\u6000\u7591 \u5230\u6211\u524d\u3002\u201d \u4ed6\u6709\u628a\u63e1\uff1a\u201c\u56e0\u4e3a\u8fd9\u7b14\u5e10\u4e00\u5b9a\u4f1a\u7b97\u5230\u90a3\u8001\u94a9\u5b50\u8eab\u4e0a\u53bb\u3002\u201d \u82b1\u5be1\u5987\u4e5f\u4e0d\u80fd\u5426\u8ba4\u3002 \u5e7d\u7075\u5c71\u5e84\u7684\u4eba\uff0c\u51e0\u4e4e\u5df2\u5168\u90fd\u77e5\u9053\u94a9\u5b50\u5bf9\u5979\u6709\u91ce\u5fc3\uff0c\u4e5f\u77e5\u9053\u94a9\u5b50\u8981\u6740\u9646\u5c0f\u98ce\u3002 \u7537\u4eba\u4e3a\u4e86\u5ac9\u5992\u800c\u6740\u4eba\uff0c\u8fd9\u7edd\u4e0d\u662f\u7b2c\u4e00\u6b21\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u662f\u6700\u540e\u4e00\u6b21\u3002\u82b1\u5be1\u5987\u9053\uff1a\u201c\u5176\u5b9e \u6211\u4e5f\u77e5\u9053\u4f60\u6068\u6211\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u54e6\uff1f\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u559c\u6b22\u7537\u4eba\uff0c\u7537\u4eba\u559c\u6b22\u7684\u5374\u662f\u6211\u3002\u201d \u8868\u54e5\u7b11\u4e86\uff1a\u201c\u4e5f\u8bb8\u6211\u8fd8\u6709\u522b\u7684\u7406\u7531\u3002\u201d \u82b1\u5be1\u5987\u95ee\uff1a\u201c\u4ec0\u4e48\u7406\u7531\uff1f\u201d\u8868\u54e5\u7b11\u5f97\u5f88\u5947\u602a\uff1a\u201c\u4e5f\u8bb8\u6211\u662f\u4e3a\u4e86\u8981\u66ff\u8001\u94a9\u5b50\u51fa\u6c14\u3002\u201d \u4ed6\u5728\u7b11\uff0c\u5730\u7a96\u4e0a\u4e5f\u6709\u4eba\u5728\u7b11\uff1a\u201c\u4e5f\u8bb8\u4f60\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u5ffd\u7136\u53d1\u73b0\u8001\u94a9\u5b50\u5df2\u5230\u4e86\u4f60\u5934\u9876 \u4e0a\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u2014\u4e0b\u94a9\u4f4f\u4f60\u7684\u8111\u888b\u3002 \u6765\u7684\u8fd8\u6709\u7ba1\u5bb6\u5a46\u3002 \u5c31\u597d\u50cf\u5929\u4e0b\u6240\u6709\u7684\u7ba1\u5bb6\u5a46\u4e00\u6837\uff0c\u8fd9\u4e2a\u4eba\u65e0\u8bba\u5728\u4ec0\u4e48\u65f6\u5019\u51fa\u73b0\uff0c\u603b\u662f\u4e00\u526f\u6101\u7709\u82e6\u8138\u7684 \u6837\u5b50\u3002\u94a9\u5b50\u5374\u7b11\u5f97\u5f88\u6109\u5feb\u3002 \u8868\u54e5\u4e5f\u5728\u7b11\uff0c\u7b11\u5f97\u5f88\u4e0d\u6109\u5feb\u3002 \u6d77\u5947\u9614\u867d\u7136\u6ca1\u6709\u4e00\u4e0b\u94a9\u4f0e\u4ed6\u8111\u888b\uff0c\u5374\u52fe\u4f4f\u4e86\u4ed6\u7684\u80a9\uff0c\u5c31\u597d\u50cf\u5c60\u592b\u7528\u94a9\u5b50\u94a9\u8d77\u5757\u6b7b\u8089 \u4e00\u6837\u3002\u8fd9\u79cd\u611f\u89c9\u5f53\u7136\u5f88\u4e0d\u6109\u5feb\u3002 \u4e16\u754c\u4e0a\u504f\u504f\u5c31\u6709\u79cd\u4eba\u559c\u6b22\u5c06\u81ea\u5df1\u7684\u6109\u5feb\u5efa\u7b51\u5728\u522b\u4eba\u7684\u4e0d\u6109\u5feb\u4e0a\uff0c\u6d77\u5947\u9614\u6070\u5de7\u5c31\u662f\u8fd9 \u79cd\u4eba\u3002 \u4ed6\u5e26\u7740\u7b11\u9053\uff1a\u201c\u4f60\u521a\u624d\u662f\u4e0d\u662f\u8bf4\u8981\u628a\u8fd9\u7b14\u5e10\u63a8\u5230\u6211\u5934\u4e0a\u6765\uff1f\u201d \u8868\u54e5\u6ca1\u6709\u5426\u8ba4\uff0c\u4ed6\u4e0d\u80fd\u5426\u8ba4\u3002\u6d77\u5947\u9614\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u60f3\u6740\u4ed6\u4eec\uff0c\u53c8\u6015\u8001\u5200\u628a\u5b50\u4e0d\u7b54 \u5e94\u3002\u201d \u8868\u54e5\u4e5f\u4e0d\u80fd\u5426\u8ba4\u3002\u6d77\u5947\u9614\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u4e00\u6837\u3002\u201d \u8868\u54e5\u4e0d\u61c2\uff1a\u201c\u4f60\u4e5f\u4e00\u6837\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u6740\u4e86\u9646\u5c0f\u98ce\uff0c\u6211\u4e5f\u6015\u8001\u5200\u628a\u5b50\u4e0d\u7b54\u5e94\uff0c\u6211\u4eec\u53ea\u6709\u4e00\u70b9\u4e0d\u540c\u3002\u201d \u8868\u54e5\u53c8\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u6211\u6bd4\u4f60\u8fd0\u6c14\u597d\uff0c\u6211\u627e\u5230\u4e86\u4e00\u4e2a\u66ff\u6211\u80cc\u9ed1\u9505\u7684 \u8868\u54e5\u5176\u5b9e\u65e9\u5c31\u61c2\u4e86\uff0c\u5374\u6545\u610f\u95ee\uff1a\u201c\u8c01\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u4f60\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u4f60\u8981\u6211\u66ff\u4f60\u53bb\u6740\u4e86\u9646\u5c0f\u98ce\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u4f60\u4e0d\u80af\uff1f\u201d \u8868\u54e5\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80af\uff1f\u6211\u672c\u5c31\u60f3\u6740\u4e86\u4ed6\u7684\uff0c\u5426\u5219\u6211\u4e3a\u4ec0\u4e48\u8981\u7ed1\u4ed6\u6765\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u90a3\u65f6\u4f60\u6740\u4e86\u4ed6\uff0c\u53ef\u4ee5\u8981\u6211\u66ff\u4f60\u80cc\u9ed1\u9505\uff0c\u73b0\u5728\u5462\uff1f\u201d \u8868\u54e5\u82e6\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\u6211\u82e5\u4e0d\u80af\u53bb\u6740\u4ed6\uff0c\u4f60\u5c31\u4f1a\u6740\u4e86\u6211\u3002\u201d \u6d77\u5947\u9614\u5927\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u679c\u7136\u662f\u4e2a\u660e\u767d\u4eba\uff0c\u6240\u4ee5\u6211\u4e00\u76f4\u90fd\u5f88\u559c\u6b22\u4f60\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u6211\u82e5\u53bb\u6740\u4e86\u4ed6\uff0c\u4f60\u5c31\u80af\u653e\u4e86\u6211\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u6211\u73b0\u5728\u5c31\u653e\u4e86\u4f60\uff0c\u53cd\u6b63\u4f60\u603b\u9003\u4e0d\u8fc7\u6211\u7684\u624b\u638c\u5fc3\u3002\u201d \u4ed6\u62ff\u5f00\u4e86\u4ed6\u7684\u94a9\u5b50\u3002 \u8868\u54e5\u677e\u4e86\u53e3\u6c14\uff0c\u56de\u5934\u770b\u7740\u4ed6\uff0c\u8138\u4e0a\u53c8\u9732\u51fa\u4e86\u5fae\u7b11\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u770b\u6211\u50cf\u4e0d\u50cf\u662f\u4e2a \u5f88\u51b2\u52a8\uff0c\u5f88\u6c89\u4e0d\u4f4f\u6c14\u7684\u4eba\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u4f60\u4e0d\u50cf\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u6211\u77e5\u4e0d\u77e5\u9053\u82b1\u5be1\u5987\u662f\u4e2a\u5f88\u5389\u5bb3\uff0c\u5f88\u4e0d\u597d\u60f9\u7684\u5973\u4eba\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u4f60\u77e5\u9053qo \u8868\u54e5\u9053\uff1a\u201c\u90a3\u672b\u6211\u521a\u529b\u2018\u4e3a\u4ec0\u4e48\u8981\u5bf9\u5979\u51fa\u624b\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\uff1f\u201d \u8868\u54e5\u7684\u7b11\u5bb9\u53c8\u53d8\u5f97\u5f88\u5947\u602a\uff1a\u201c\u56e0\u4e3a\u6211\u8981\u4f60\u4eec\u8ba4\u4e3a\u6211\u7684\u6b66\u529f\u5f88\u5dee\u52b2\u3002\u201d \u6d77\u5947\u9614\u4e0d\u7b11\u4e86\uff1a\u201c\u5176\u5b9e\u5462\uff1f\u201d \u8868\u54e5\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e00\u62db\u5c31\u53ef\u4ee5\u6740\u4e86\u4f60\uff01\u201d \u8fd9\u53e5\u8bdd\u6709\u5341\u4e00\u4e2a\u5b87\uff0c\u8bf4\u5230\u7b2c\u4e03\u4e2a\u5b87\u4ed6\u624d\u51fa\u624b\uff0c\u8bf4\u5230\u6700\u540e\u2014\u4e2a\u5b57\u65f6\uff0c\u4ed6\u5df2\u7ecf\u6740\u4e86\u6d77\u5947 \u9614\u3002 \u4ed6\u51fa\u624b\u8fc5\u901f\u800c\u6709\u6548\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u4eba\u80fd\u770b\u6e05\u4ed6\u662f\u600e\u4e48\u51fa\u624b\u7684\uff0c\u53ea\u542c\u89c1\u4e24\u54cd\u6c89 \u91cd\u4e3d\u4ee4\u4eba\u4f5c\u5455\u7684\u58f0\u97f3\uff0c\u4e5f\u6b63\u50cf\u662f\u5c60\u592b\u7684\u5200\u780d\u5728\u5757\u6b7b\u8089\u4e0a\uff0c\u7136\u540e\u6d77\u5947\u9614\u5c31\u50cf\u662f\u5757\u6b7b\u8089\u822c\u8f6f \u762b\u4e86\u4e0b\u53bb\u3002 \u9646\u5c0f\u98ce\u548c\u82b1\u5be1\u5987\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u7ba1\u5ba2\u5a46\u5f53\u7136\u66f4\u5403\u60ca\u3002 \u8868\u54e5\u62cd\u4e86\u62cd\u624b\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u6211\u65e9\u5c31\u542c\u8bf4\u98ce\u5c3e\u5e2e\u5185\u4e8c\u5802\u7684\u9999\u4e3b\u90fd\u662f\u5f88\u4e86\u4e0d\u8d77\u7684\u4eba\uff0c\u5c24 \u5176\u662f\u5927\u603b\u7ba1\u9ad8\u6d9b\u66f4\u4e86\u4e0d\u8d77\uff0c\u53ea\u53ef\u60dc\u4e00\u76f4\u5230\u73b0\u5728\u6211\u90fd\u6ca1\u89c1\u8fc7\u90a3\u51e0\u624b\u5a01\u9547\u6c5f\u6e56\u7684\u7edd\u6280\u3002\u201d \u672c\u6765\u5df2\u6101\u7709\u82eb\u8138\u7684\u7ba1\u5bb6\u5a46\uff0c\u73b0\u5728\u66f4\u597d\u50cf\u968f\u65f6\u90fd\u8981\u54ed\u51fa\u6765\u7684\u6837\u5b50\uff1a\u201c\u6211\u54ea\u6709\u4ec0\u4e48\u7edd\u6280\uff1f \u6211\u552f\u4e00\u7684\u672c\u4e8b\u53ea\u4e0d\u8fc7\u662f\u4f1a\u66ff\u4eba\u6253\u6742\u7ba1\u5bb6\u800c\u5df2oH \u8868\u54e5\u9053\uff1a\u201c\u4f60\u4e0d\u4f1a\u6740\u4eba\uff1f\u201d\u7ba1\u5bb6\u5a46\u7acb\u523b\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u3002\u201d \u8868\u54e5\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u4e0d\u5982\u8d76\u5feb\u8ba9\u6211\u6740\u4e86\u4f60\u3002\u201d \u7ba1\u5bb6\u5a46\u4e5f\u53f9\u5382\u53e3\u6c14\uff0c\u8eab\u5b50\u7a81\u7136\u51cc\u7a7a\u4e00\u8f6c\uff0c\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u81f3\u5c11\u5df2\u6709\u56db\u4e94\u5341\u4ef6\u6697\u5668 \u98de\u51fa\uff0c\u6ee1\u5929\u5bd2\u5149\u95ea\u52a8\uff0c\u5168\u90fd\u5f80\u8868\u54e5\u6253\u4e86\u8fc7\u53bb\uff0c \u539f\u6765\u8fd9\u4e2a\u4eba\u5168\u8eab\u4e0a\u4e0b\u90fd\u5e26\u7740\u81f4\u547d\u7684\u6697\u5668\uff0c\u800c\u4e14\u968f\u65f6\u90fd\u53ef\u4ee5\u53d1\u51fa\u6765\u3002 \u80fd\u5728\u2014\u5239\u90a3\u95f4\u53d1\u51fa\u8fd9\u4e48\u591a\u6697\u5668\u7684\u4eba\uff0c\u5929\u4e0b\u7edd\u4e0d\u8d85\u8fc7\u5341 \u80fd\u5728\u4e00\u5239\u90a3\u95f4\u8eb2\u8fc7\u8fd9\u4e48\u591a\u6697\u5668\u7684\u4eba\u5f53\u7136\u66f4\u5c11\u3002 \u8868\u54e5\u5374\u504f\u504f\u5c31\u662f\u8fd9\u5c11\u6570\u51e0\u4e2a\u4eba\u5176\u4e2d\u4e4b\u4e00\uff0c\u4ed6\u4e0d\u4f46\u65e9\u5df2\u7b97\u51c6\u4e86\u7ba1\u5bb6\u5a46\u8fd9\u4e00\u624b\uff0c\u800c\u4e14\u65e9 \u5df3\u51c6\u5907\u597d\u5bf9\u4ed8\u7684\u6cd5\u5b50\u3002 \u6697\u5668\u53d1\u51fa\uff0c\u4ed6\u7684\u5251\u5df2\u7ecf\u5728\u7b49\u7740\u3002 \u5251\u5149\u98de\u8d77\u5316\u4f5c\u4e86\u4e00\u7247\u65cb\u5149\uff0c\u5377\u788e\u4e86\u6240\u6709\u7684\u6697\u5668\uff0c\u5251\u5149\u518d\u4e00\u95ea\uff0c\u7ba1\u5bb6\u5a46\u4e5f\u5012\u4e0b\uff0c\u5012\u5728 \u5730\u4e0a\u540e\uff0c\u9c9c\u8840\u624d\u5f00\u59cb\u6e85\u51fa\u6765\u3002 \u9c9c\u8840\u6e85\u51fa\u6765\u7684\u65f6\u5019\uff0c\u9646\u5c0f\u98ce\u624d\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u5df4\u5c71\u4e03\u4e03\u56db\u5341\u4e5d\u624b\u56de\u98ce\u821e\u5251 \u67f3\u5251\uff1f\u201d \u8868\u54e5\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u4f60\u5c31\u662f\u5df4\u5c71\u5251\u5ba2\u552f\u2014\u7684\u8863\u94b5\u4f20\u4eba\u987e\u98de\u4e91\uff1f\u201d \u8868\u54e5\u9053\uff1a\u201c\u5c31\u662f\u6211\u3002\u201d \u9646\u5c0f\u98ce\u53f9\u9053\uff1a\u201c\u5df4\u5c71\u795e\u5251\uff0c\u679c\u7136\u662f\u597d\u5251\u6cd5\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u672c\u6765\u5c31\u662f\u7684\u3002\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u4f46\u6211\u5374\u60f3\u4e0d\u901a\uff0c\u50cf\u4f60\u8fd9\u6837\u7684\u5165\uff0c\u600e\u4e48\u4e5f\u4f1a\u88ab\u897f\u95e8\u5439\u96ea\u903c\u5f97\u65e0\u8def\u53ef \u8d70\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u4f60\u5f53\u7136\u4e5f\u60f3\u4e0d\u901a\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u6740\u4e86\u4ed6\u4eec\uff0c\u5374\u4e0d\u6740\u4f60\uff1f\u201d \u9646\u5c0f\u98ce\u7684\u786e\u60f3\u4e0d\u901a\u3002 \u8868\u54e5\u7b11\u4e86\u7b11\uff0c\u9053\u5e7f\u8fd9\u9053\u7406\u5176\u5b9e\u7b80\u5355\u5f97\u5f88\uff0c\u53ea\u56e0\u4e3a\u6211\u672c\u6765\u5c31\u4e0d\u60f3\u6740\u4f60qo \u9646\u5c0f\u98ce\u66f4\u4e0d\u61c2\u3002 \u8868\u54e5\u9053\uff1a\u201c\u8001\u5200\u628a\u5b50\u603b\u8ba4\u4e3a\u8fd9\u7ec4\u7ec7\u5f88\u79d8\u5bc6\uff0c\u5176\u5b9e\u6c5f\u6e56\u4e2d\u65e9\u5df2\u6709\u4e09\u4e2a\u4eba\u77e5\u9053\u4e86\uff0c\u7b2c\u4e00 \u4e2a\u77e5\u9053\u7684\u5c31\u662f\u5bb6\u5e08\u3002\u201d \u9646\u5c0f\u98ce\u52a8\u5bb9\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u2026\u2026\u201d \u8868\u54e5\u9053\uff1a\u201c\u6211\u5c31\u662f\u4ed6\u4eec\u7279\u5730\u6d3e\u5230\u8fd9\u91cc\u6765\u5367\u5e95\u7684\uff0c\u56e0\u4e3a\u4ed6\u4eec\u867d\u7136\u77e5\u9053\u6c5f\u6e56\u4e2d\u6709\u4e2a\u5e7d\u7075 \u5c71\u5e84\uff0c\u5bf9\u4e8e\u8fd9\u7ec4\u7ec7\u4e2d\u7684\u865a\u5b9e\u79d8\u5bc6\u77e5\u9053\u5f97\u5e76\u4e0d\u591a\u3002\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u4eec\u6545\u610f\u8981\u4f60\u88ab\u897f\u95e8\u5439\u96ea\u903c\u5f97\u65e0\u8def\u53ef\u8d70\uff1f\u201d \u8868\u54e5\u9053\uff1a\u201c\u90a3\u4ef6\u4e8b\u672c\u6765\u5c31\u662f\u4e2a\u5708\u5957\uff0c\u4ed6\u4eec\u65e9\u5df2\u7b97\u51c6\u4e86\u897f\u95e8\u5439\u96ea\u4e00\u5b9a\u4f1a\u6765\u7ba1\u8fd9\u4ef6\u4e8b\uff0c \u4e5f\u65e9\u5df2\u7b97\u51c6\u4e86\u5e7d\u7075\u5c71\u5e84\u4f1a\u6d3e\u4eba\u6765\u8ddf\u6211\u63a5\u5934\u8ba2\u5408\u7ea6\u7684\u3002\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u8868\u54e5\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u521a\u7ee7\u627f\u4e86\u4e00\u7b14\u5f88\u53ef\u89c2\u7684\u9057\u4ea7\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u4ed8\u5f97\u51fa\u5341\u4e07\u4e24\u94f6\u5b50\u3002\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u5408\u7ea6\u91d1\u8981\u5341\u4e07\u4e24\uff1f\u2019\u2019 \u8868\u54e5\u9053\uff1a\u201c\u4e3a\u4e86\u4e70\u56de\u81ea\u5df1\u7684\u4e00\u6761\u547d\uff0c\u5341\u4e07\u4e24\u5e76\u4e0d\u7b97\u591a\u3002\u201d\u9646\u5c0f\u98ce\u627f\u8ba4\uff1a\u201c\u7684\u786e\u4e0d \u591a\u3002\u201d \u751f\u547d\u672c\u5c31\u662f\u65e0\u4ef7\u7684\uff0c\u8fd9\u4e16\u4e0a\u8fd8\u6709\u4ec0\u4e48\u4e8b\u80fd\u6bd4\u81ea\u5df1\u7684\u751f\u547d\u66f4\u73cd\u8d35\u3002 \u8868\u54e5\u9053\uff1a\u201c\u4ed6\u4eec\u8981\u6211\u6765\uff0c\u6700\u91cd\u8981\u7684\u4f7f\u547d\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u6211\u67e5\u660e\u8005\u5200\u628a\u5b50\u8fd9\u4e2a\u4eba\u3002\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u8fde\u4ed6\u4eec\u90fd\u4e0d\u77e5\u9053\u8001\u5200\u628a\u5b50\u7684\u6765\u5386\u548c\u5e95\u7ec6\uff1f\u2019\u2019\u8868\u54e5\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u77e5\u9053 ao \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u8868\u54e5\u82e6\u7b11\u9053\uff1a\u201c\u6211\u6765\u4e86\u867d\u7136\u5df2\u6709\u4e0d\u5c11\u65f6\u5019\uff0c\u5374\u8fde\u4ed6\u7684\u771f\u9762\u76ee\u90fd\u6ca1\u6709\u770b\u89c1\u8fc7\uff0c\u6240\u4ee5\u6211 \u66f4\u6025\u7740\u8981\u627e\u51fa\u90a3\u4e2a\u4eba\u6765\u3002\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u90a3\u4e2a\u4eba\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u8868\u54e5\u9053\uff1a\u201c\u6765\u63a5\u5e94\u6211\u7684\u4eba\u3002\u201d \u4ed6\u53c8\u89e3\u91ca\uff1a\u201c\u4ed6\u4eec\u672c\u6765\u7b54\u5e94\uff0c\u5c3d\u5feb\u6d3e\u4eba\u6765\u63a5\u5e94\u6211\uff0c\u53ef\u662f\u65b0\u6765\u7684\u4eba\u884c\u52a8\u90fd\u4e0d\u80fd\u81ea\u7531\uff0c \u4e5f\u5f88\u96be\u53d1\u73b0\u987e\u98de\u4e91\u5c31\u662f\u8868\u54e5\u3002\u201d\u9646\u5c0f\u98ce\u9053\uff1a\u201c\u4f60\u7b49\u5f97\u7740\u6025\uff0c\u5c31\u53ea\u597d\u5148\u53bb\u627e\u4ed6\u4eec\u3002\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u6211\u5df2\u627e\u8fc7\u5341\u4e8c\u4e2a\u4eba\u3002\u201d\u9646\u5c0f\u98ce\u9053\uff1a\u201c\u4f60\u5168\u90fd\u627e\u9519\u4e86\u3002\u201d\u8868\u54e5\u9053\uff1a\u201c\u6240\u4ee5 \u6211\u53ea\u597d\u6740\u4e86\u4ed6\u4eec\u706d\u53e3\u3002\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u8fd9\u4e00\u6b21\u4f60\u8ba4\u4e3a\u6211\u5c31\u662f\u6765\u63a5\u5e94\u4f60\u7684\u4eba\uff1f\u201d \u8868\u54e5\u76ef\u7740\u4ed6\uff0c\u4e00\u5b87\u5b87\u9053\uff1a\u201c\u6211\u53ea\u5e0c\u671b\u8fd9\u4e00\u6b21\u6ca1\u6709\u9519\uff01\u201d\u9646\u5c0f\u98ce\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211 \u4e5f\u5e0c\u671b\u4f60\u8fd9\u2014\u6b21\u6ca1\u6709\u9519\u3002\u201d \u8868\u54e5\u76ee\u5149\u5df2\u53d8\u5f97\u51b7\u5982\u5200\u950b\uff0c\u5ffd\u7136\u95ee\u9053\u5e7f\u9664\u4e86\u5bb6\u5e08\u5df4\u5c71\u5251\u5ba2\u5916\uff0c\u8fd8\u6709\u4e24\u4e2a\u4eba\u662f\u8c01\uff1f\u662f \u8c01\u8981\u4f60\u6765\u7684\uff1f\u4f60\u7684\u4ee3\u53f7\u662f\u4ec0\u4e48\uff1f\u201d\u9646\u5c0f\u98ce\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u8bf4\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u6839\u672c\u5c31\u4e0d\u77e5\u9053\uff01\u201d \u9646\u5c0f\u98ce\u70b9\u70b9\u5934\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5b9e\u5728\u62b1\u6b49\u5f97\u5f88\uff0c\u8fd9\u4e00\u6b21\u4f60\u597d\u50cf\u53c8\u627e\u9519\u4e86\u3002 \u5730\u7a96\u91cc\u6709\u706f\uff0c\u73b0\u5728\u662f\u66ae\u6625\uff0c\u672c\u6765\u5e76\u4e0d\u4f1a\u4ee4\u4eba\u89c9\u5f97\u592a\u51b7\u3002 \u9646\u5c0f\u98ce\u5374\u7a81\u7136\u89c9\u5f97\u6bdb\u9aa8\u60ca\u7136\u8fd9\u5e76\u4e0d\u662f\u56e0\u4e3a\u8868\u54e5\u7684\u624b\u53c8\u63e1\u4f4f\u4e86\u5251\u67c4\uff0c\u800c\u662f\u56e0\u4e3a\u5730\u7a96\u91cc \u5ffd\u7136\u591a\u4e86\u4e00\u4e2a\u4eba\uff0c\u4e00\u4e2a\u7a7f\u7740\u7070\u888d\uff0c\u6234\u7740\u7af9\u7b7e\u7684\u4eba\u3002 \u8868\u54e5\u7684\u624b\u521a\u63e1\u4f4f\u67c4\uff0c\u8fd9\u4e2a\u4eba\u5c31\u5230\u4e86\u4ed6\u8eab\u540e\u3002 \u9646\u5c0f\u98ce\u770b\u89c1\u8fd9\u4e2a\u4eba\uff0c\u82b1\u5be1\u5987\u4e5f\u770b\u89c1\u4e86\u8fd9\u4e2a\u4eba\uff0c\u8868\u54e5\u81ea\u5df1\u5374\u8fde\u2014\u70b9\u611f\u89c9\u90fd\u6ca1\u6709\u3002 \u8fd9\u4e2a\u4eba\u5c31\u50cf\u662f\u4e2a\u6709\u5f62\u800c\u65e0\u8d28\u7684\u9b3c\u9b42\u3002 \u4e00\u9876\u5f62\u5f0f\u5947\u7279\u7684\u7af9\u7b20\uff0c\u906e\u4f4f\u4e86\u4ed6\u7684\u8138\uff0c\u9646\u5c0f\u98ce\u5b8c\u5168\u770b\u4e0d\u89c1\u4ed6\u7684\u9762\u76ee\uff0c\u5374\u5df2\u731c\u51fa\u4ed6\u662f \u8c01\u4e86\u3002\u82b1\u5be1\u5987\u8138\u4e0a\u6ca1\u6709\u8868\u60c5\uff0c\u773c\u775b\u91cc\u5374\u5df2\u5fcd\u4e0d\u4f4f\u9732\u51fa\u4e86\u559c\u8272\u3002 \u8fd9\u4e2a\u4eba\u6b63\u5728\u5411\u5979\u62db\u624b\u3002 \u8868\u54e5\u597d\u50cf\u4e5f\u89c9\u5f97\u6709\u70b9\u4e0d\u5bf9\u4e86\u3002\u970d\u7136\u56de\u8eab\u3002 \u540e\u9762\u6ca1\u6709\u4eba\uff0c\u8fde\u4eba\u5f71\u90fd\u6ca1\u6709\u3002 \u8fd9\u4e2a\u4eba\u5c31\u50cf\u5f71\u5b50\u822c\u8d34\u5728\u4ed6\u8eab\u540e\uff0c\u53c8\u5411\u82b1\u5be1\u5987\u6446\u6446\u624b\u3002 \u7b49\u5230\u4ed6\u518d\u56de\u5934\u53bb\u770b\u65f6\uff0c\u5979\u5df2\u6c89\u4e0b\u4e86\u8138\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u662f\u60f3\u5149\u6740\u9646\u5c0f\u98ce\uff1f\u8fd8\u662f\u60f3\u5149\u6740 \u6211\uff1f\u201d \u8868\u54e5\u6162\u6162\u7684\u5750\u4e0b\uff0c\u60a0\u7136\u9053\uff1a\u201c\u4f60\u4eec\u770b\u8d77\u6765\u597d\u50cf\u90fd\u4e0d\u592a\u6015\u6b7b\u3002\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u65e2\u7136\u5df4\u975e\u6b7b\u4e0d\u53ef\uff0c\u5bb3\u6015\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u53ea\u4e0d\u8fc7\u8868\u54e5\u9053\uff1a\u201c\u53ea\u4e0d\u8fc7\u4f60\u4e0d\u60f3 \u6b7b\u5f97\u592a\u7cca\u6d82\u800c\u5df2\u3002 \u82b1\u5be1\u5987\u627f\u8ba4\uff0c\u8fd9\u53e5\u8bdd\u7684\u786e\u8bf4\u4e2d\u4e86\u5979\u7684\u5fc3\u610f\u3002 \u8868\u54e5\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u4e5f\u60f3\u95ee\u95ee\u6211\uff0c\u9664\u4e86\u6211\u5e08\u5085\u5df4\u5c71\u5251\u5ba2\u5916\uff0c\u77e5\u9053\u8fd9\u79d8\u5bc6\u7684\u8fd8\u6709\u8c01\uff1f\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u65e2\u7136\u6211\u4eec\u5df2\u975e\u6b7b\u4e0d\u53ef\uff0c\u4f60\u8bf4\u51fa\u6765\u53c8\u6709\u4f55\u59a8\uff1f\u201d \u8868\u54e5\u76ef\u7740\u5979\uff0c\u5ffd\u7136\u7b11\u4e86\uff0c\u5927\u7b11\u3002 \u82b1\u5be1\u5987\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d \u8868\u54e5\u9053\uff1a\u201c\u6211\u5728\u7b11\u4f60\u3002\u4f60\u660e\u660e\u77e5\u9053\u7684\uff0c\u53c8\u4f55\u5fc5\u6765\u95ee\u6211\uff1f\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u8868\u54e5\u9053\uff1a\u201c\u9664\u4e86\u6211\u5e08\u5085\u5916\uff0c\u53e6\u5916\u4e24\u4e2a\u4eba\uff0c\u4e00\u4e2a\u662f\u6728\u9053\u4eba\uff0c\u8fd8\u6709\u4e00\u4e2a\u5c31\u662f\u4f60\u8001\u5b50\uff0c\u4f60 \u660e\u660e\u4e5f\u8ddf\u6211\u4e00\u6837\uff0c\u4e5f\u662f\u5230\u8fd9\u91cc\u6765\u5367\u5e95\u7684\uff0c\u53c8\u4f55\u5fc5\u88c5\u849c\uff1f\u201d \u82b1\u5be1\u5987\u7684\u8138\u8272\u53d8\u4e86\u3002 \u8868\u54e5\u9053\uff1a\u201c\u6211\u60f3\u4f60\u73b0\u5728\u4e00\u5b9a\u5df2\u77e5\u9053\u8001\u5200\u628a\u5b50\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\u4e86\uff0c\u56e0\u4e3a\u4f60\u662f\u4e2a\u5973\u4eba\uff0c \u4f60\u53ef\u4ee5\u966a\u4ed6\u4e0a\u5e8a\u53bb\u7761\u89c9\u3002\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u4f60\u60f3\u62d6\u6211\u4e0b\u6c34\uff1f\u201d \u8868\u54e5\u9053\uff1a\u201c\u5176\u5b9e\u6211\u65e9\u5c31\u77e5\u9053\u4f60\u7684\u79d8\u5bc6\u4e86\uff0c\u6211\u8fd9\u4e48\u6837\u505a\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5708\u5957\uff0c\u60f3\u8bf1\u4f60\u81ea \u5df1\u8bf4\u51fa\u8fd9\u79d8\u5bc6\u6765\uff0c\u6211\u5b81\u53ef\u6740\u9519\u4e00\u767e\u4e2a\u4eba\uff0c\u4e5f\u4e0d\u80fd\u5bb9\u2018\u4e2a\u5978\u7ec6\u5b58\u5728\u3002\u201d \u82b1\u5be1\u5987\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u5e76\u4e0d\u662f\u60f3\u62d6\u6211\u4e0b\u6c34\uff0c\u800c\u662f\u60f3\u627e\u4e2a\u66ff\u6b7b \u9b3c\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u627e\u66ff\u6b7b\u9b3c\uff1f\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u867d\u7136\u6ca1\u6709\u770b\u89c1\u8001\u5200\u628a\u5b50\uff0c\u5374\u77e5\u9053\u4ed6\u5df2\u7ecf\u6765\u4e86\u3002\u201d \u5979\u53c8\u53f9\u53e3\u6c14\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4f60\u7684\u786e\u53ef\u4ee5\u7b97\u662f\u4e2a\u4eba\u624d\uff0c\u53ea\u53ef\u60dc\u6709\u4ef6\u4e8b\u4f60\u8fd8\u4e0d\u660e\u767d\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u8fd9\u7684\u786e\u662f\u4e2a\u5708\u5957\uff0c\u88ab\u5957\u91cc\u53bb\u7684\u4eba\u5374\u4e0d\u662f\u6211\uff0c\u662f\u4f60\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u54e6\uff1f\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u6211\u548c\u8001\u5200\u628a\u5b50\u65e9\u5df2\u6000\u7591\u5230\u4f60\uff0c\u6240\u4ee5\u624d\u4f1a\u8bbe\u4e0b\u8fd9\u5708\u5957\u6765\u8ba9\u4f60\u4e0a\u5f53\uff0c\u4f60\u82e5 \u4ee5\u4e3a\u6211\u771f\u7684\u4e2d\u4e86\u4f60\u7684\u9500\u9b42\u6563\uff0c\u4f60\u4e5f\u9519\u4e86\u3002o \u5979\u62cd\u4e86\u62cd\u8863\u895f\uff0c\u6162\u6162\u7684\u7ad9\u4e86\u8d77\u6765\u4e2d\u4e86\u9500\u9b42\u6563\u7684\u4eba\uff0c\u2014\u4e2a\u5bf9\u65f6\u4e2d\u65e0\u836f\u53ef\u89e3\uff0c\u53ef\u662f\u5979\u73b0 \u5728\u5df2\u7ecf\u7ad9\u4e86\u8d77\u6765\u3002 \u8868\u54e5\u5374\u8fd8\u662f\u52a8\u4e5f\u4e0d\u52a8\u7684\u5750\u5728\u90a3\u91cc\uff0c\u5ffd\u7136\u8f6c\u5411\u9646\u5c0f\u98ce\uff0c\u9053\uff1a\u201c\u4f60\u770b\u600e\u4e48\u6837\uff1f\u201d \u9646\u5c0f\u98ce\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u4eba\u624d\uff0c\u6211\u4f69\u670d\u4f60\u4eec\u3002\u201d \u8868\u54e5\u5ffd\u53c8\u5927\u7b11\uff1a\u201c\u80fd\u591f\u8ba9\u9646\u5c0f\u98ce\u8fd9\u6837\u7684\u4eba\u4f69\u670d\uff0c\u6211\u987e\u98de\u4e91\u6b7b\u800c\u65e0\u61be\u3002\u201d\u2026\u4ed6\u5c45\u7136\u771f \u7684\u8bf4\u6b7b\u5c31\u6b7b\uff0c\u6b7b\u5f97\u771f\u5feb\uff0c\u751a\u81f3\u6bd4\u4ed6\u53bb\u6740\u522b\u4eba\u7684\u65f6\u5019\u66f4\u5feb\u3002 \u5251\u950b\u4e00\u8f6c\uff0c\u9c9c\u8840\u98de\u6e85\uff0c\u4ed6\u7684\u4eba\u5df2\u5012\u4e0b\u53bb\u3002 \u4ed6\u7edd\u4e0d\u80fd\u7559\u4e0b\u81ea\u5df1\u7684\u6d3b\u53e3\uff0c\u8ba9\u522b\u4eba\u6765\u903c\u95ee\u4ed6\u7684\u53e3\u4f9b\u3002 \u4f60\u82e5\u60f3\u53bb\u523a\u63a2\u522b\u4eba\u7684\u79d8\u5bc6\uff0c\u5c31\u5f97\u5148\u51c6\u5907\u968f\u65f6\u727a\u7272\u81ea\u82b1\u5be1\u5987\u76b1\u7709\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4ed6\u771f\u7684 \u4e00\u70b9\u4e5f\u4e0d\u6015\u6b7b\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u6015\u6b7b\u7684\u4eba\u6839\u672c\u4e0d\u80fd\u505a\u8fd9\u79cd\u4e8b\uff0c\u592a\u806a\u660e\u7684\u4eba\u4e5f\u4e0d\u80fd\u505a\u3002\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u8fd8\u6709\u79cd\u4eba\u66f4\u4e0d\u80fd\u505a\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u54e6\uff1f\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u6709\u79cd\u4eba\u65e0\u8bba\u8d70\u5230\u54ea\u91cc\u597d\u4f60\u90fd\u4f1a\u6709\u9ebb\u70e6\u7684\uff0c\u5c31\u7b97\u4ed6\u4e0d\u60f3\u53bb\u60f9\u9ebb\u70e6\uff0c\u9ebb\u70e6 \u4e5f\u4f1a\u627e\u4e0a\u4ed6\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u5c31\u662f\u8fd9\u79cd\u4eba\uff1f\u201d \u9646\u5c0f\u98ce\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4e00\u5411\u5f88\u6709\u81ea\u77e5\u4e4b\u660e\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u66ff\u6211\u60f9\u7684\u9ebb\u70e6\u7684\u786e\u4e0d\u5c11\u2026\u2026\u201d\u9646\u5c0f\u98ce\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u4f46\u662f \u4f60\u7edd\u4e0d\u80fd\u6740\u6211\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u5e76\u4e0d\u60f3\u5230\u8fd9\u91cc\u6765\uff0c\u662f\u4f60\u81ea\u5df1\u8981\u627e\u6765\u7684\uff0c\u6240\u4ee5\u522b\u4eba\u90fd\u80fd\u6740\u6211\uff0c\u53ea \u6709\u4f60\u4e0d\u80fd\uff0c\u56e0\u4e3a\u6211\u662f\u4f60\u7684\u5ba2\u4eba\u3002\u201d \u8001\u5200\u628a\u5b50\u6c89\u9ed8\u7740\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u4e0d\u6740\u4f60\uff0c\u53ea\u8981\u4f60\u7b54\u5e94\u6211\u4e00\u4ef6\u4e8b\uff01\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u5b88\u53e3\u5982\u74f6\uff0c\u6c38\u4e0d\u6cc4\u9732\u8fd9\u91cc\u7684\u79d8\u5bc6\u3002\u201d\u9646\u5c0f\u98ce\u7acb\u523b\u9053\uff1a\u201c\u6211\u7b54\u5e94\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u597d\uff0c\u6211\u4fe1\u4efb\u4f60\uff0c\u4f60\u8d70\u5427\uff01\u201d \u9646\u5c0f\u98ce\u6014\u4f50\uff0c\u4f60\u8981\u6211\u8d70\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u5c31\u7b97\u4e3b\u4eba\u4e0d\u80fd\u6740\u5ba2\u4eba\uff0c\u81f3\u5c11\u603b\u80fd\u8bf7\u5ba2\u4eba\u8d70\u7684\u3002\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u53ef\u662f\u5916\u9762\u2026\u2026\u201d \u8001\uff1a\u201c\u4e0d\u7ba1\u5916\u9762\u6709\u4ec0\u4e48\u4eba\u5728\u7b49\u7740\u4f60\uff0c\u81f3\u5c11\u603b\u6bd4\u73b0\u5728\u5c31\u6b7b\u5728\u8fd9\u91cc\u597d\u3002 \u9646\u5c0f\u98ce\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u4ed6\u770b\u5f97\u51fa\u73b0\u5728\u65e0\u8bba\u518d\u8bf4\u4ec0\u4e48\u90fd\u5df2\u6ca1\u6709\u7528\u3002\u4ed6\u53ea\u6709\u8d70\u3002 \u8001\u5200\u628a\u5b50\u5374\u53c8\u53eb\u4ed6\u56de\u6765\uff0c\u9053\uff1a\u201c\u53ef\u662f\u4f60\u603b\u7b97\u505a\u8fc7\u6211\u7684\u5ba2\u4eba\uff0c\u800c\u4e14\u603b\u7b97\u6ca1\u6709\u51fa\u5356\u6211\uff0c \u6240\u4ee5\u4f60\u82e5\u9700\u8981\u4ec0\u4e48\uff0c\u6211\u90fd\u53ef\u4ee5\u8ba9\u4f60\u5e26\u8d70\uff01\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u65e0\u8bba\u6211\u8981\u4ec0\u4e48\u90fd\u884c\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u53ea\u8981\u662f\u4f60\u80fd\u5e26\u8d70\u7684\u3002\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u6211\u8981\u5e26\u5979\u8d70\uff1f\u201d \u4ed6\u8981\u5e26\u8d70\u7684\u7adf\u662f\u82b1\u5be1\u5987\u3002 \u8001\u5200\u628a\u5b50\u95ed\u4e0a\u4e86\u5634\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u5e26\u5979\u8d70\uff0c\u53ef\u662f\u4ee5\u540e\u6700\u597d\u6c38\u8fdc\u83ab \u8981\u518d\u8ba9\u6211\u770b\u89c1\u4f60\uff01\u201d \u5c71\u8c37\u95f4\u8fd8\u662f\u4e91\u96fe\u51c4\u8ff7\uff0c\u8981\u627e\u5230\u90a3\u6761\u8457\u6709\u82e5\u65e0\u7684\u94c1\u7d22\u6865\u5df2\u7ecf\u5f88\u4e0d\u5bb9\u6613\uff0c\u8981\u8d70\u8fc7\u53bb\u66f4\u4e0d \u5bb9\u6613\u3002 \u8d70\u8fc7\u53bb\u4e4b\u540e\u5462\uff1f\u5c71\u8c37\u91cc\u662f\u5e7d\u7075\u7684\u4e16\u754c\uff0c\u5c71\u8c37\u5916\u662f\u4ec0\u4e48\uff1f\u6709\u591a\u5c11\u6740\u4eba\u7684\u9677\u9631\u3002 \u9646\u5c0f\u98ce\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u5ffd\u7136\u7b11\u4e86\u3002 \u82b1\u5be1\u5987\u770b\u7740\u4ed6\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u4e0d\u6015\uff1f\u201d \u9646\u5c0f\u98ce\u9053\uff1a\u201c\u4f2f\u4ec0\u4e48\uff1f\u201d \u82b1\u5be1\u5987\u9053\uff1a\u201c\u6b7b\u3002\u201d \u5979\u8f7b\u8f7b\u7684\u63e1\u7740\u4ed6\u7684\u624b\uff1a\u201c\u4f60\u4e0d\u6015\u4e00\u8d70\u51fa\u8fd9\u5c71\u8c37\uff0c\u5c31\u6b7b\u5728\u522b\u4eba\u7684\u5251\u4e0b\uff1f\u201d \u9646\u5c0f\u98ce\u5fae\u7b11\u9053\uff1a\u201c\u6211\u53cd\u6b63\u5df2\u6b7b\u8fc7\u4e00\u6b21\uff0c\u518d\u6b7b\u4e00\u6b21\u53c8\u4f55\u59a8\uff1f\u201d \u82b1\u5be1\u5987\u4e5f\u7b11\u5382\uff0c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4ed6\u4eec\u603b\u7b97\u5df2\u8d70\u51fa\u4e86\u5e7d\u7075\u5c71\u5e84\uff0c\u8d70\u51fa\u4e86\u8fd9\u6b7b\u4eba\u7684\u4e16\u754c\u3002 \u82b1\u5be1\u5987\u67d4\u58f0\u9053\uff1a\u201c\u6211\u65f6\u5e38\u90fd\u5728\u60f3\uff0c\u53ea\u8981\u80fd\u8ba9\u6211\u518d\u771f\u6b63\u6d3b\u4e00\u5929\uff0c\u6211\u5c31\u5df2\u5e94\u8be5\u5fc3\u6ee1\u610f\u8db3 \u4e86\u3002\u201d"}),showOnHomepage:!0},{name:"\u7b2c\u516b\u7ae0\u3000\u91cd\u7528\u9646\u5c0f\u51e4",content:(0,e.jsx)(e.Fragment,{children:"\u8fd9\u7247\u5c71\u5ca9\u4e0a\u6ca1\u6709\u8349\uff0c\u94ee\u5d58\u7684\u7684\u5c71\u77f3\uff0c\u5229\u5982\u5200\u950b\u3002 \u82b1\u5be1\u5987\u5ffd\u7136\u505c\u4e0b\u6765\u3002\u4f4e\u4e0b\u5934\u53bb\u770b\u81ea\u5df1\u7684\u811a\u3002\u5979\u7684\u811a\u7ea4\u79c0\u67d4\u7f8e\uff0c\u5374\u6709\u4e00\u4e1d\u9c9c\u8840\u6b63\u4ece\u5979 \u811a\u5e95\u6d41\u51fa\u6765\u3002 \u201c\u4f60\u6ca1\u6709\u7a7f\u978b\uff1f\u201d \u201c\u6ca1\u6709\u3002\u201d\u82b1\u5be1\u94a2\u8fd8\u5728\u7b11\uff1a\u201c\u6211\u2014\u5411\u5f88\u5c11\u8d70\u8def\u3002\u201d \u5979\u8fde\u978b\u90fd\u6ca1\u6709\u7a7f\u5c31\u8ddf\u7740\u4ed6\u8d70\u4e86\uff0c\u5979\u8d70\u7684\u65f6\u5019\u4ec0\u4e48\u90fd\u6ca1\u6709\u2019\u7a7f\u3002 \u201c\u4f60\u4ec0\u4e48\u90fd\u4e2a\u8981\uff0c\u53ea\u8981\u6211\u8ddf\u4f60\u8d70\u3002\u6211\u8fd8\u8981\u4ec0\u4e48\uff1f\u201d\u5979\u7684\u8138\u867d\u5df2\u56e0\u75c5\u695a\u800c\u53d1\u767d\uff0c\u7b11\u5f97 \u5374\u8fd8\u662f\u5f88\u6e29\u67d4\uff1a\u201c\u8fd9\u4e16\u4e0a\u8fd8\u6709\u4ec0\u4e48\u80fd\u6bd4\u771f\u60c5\u66f4\u53ef\u8d35\uff1f\u201d \u9646\u5c0f\u51e4\u770b\u7740\u5979\uff0c\u53ea\u89c9\u5f97\u4e00\u80a1\u67d4\u60c5\u5df2\u5982\u6625\u6c34\u822c\u6d8c\u4e0a\u4ed6\u5fc3\u5934 \u4ed6\u62b1\u8d77\u4e86\u5979\uff0c\u8d70\u8fc7\u4e86\u8fd9\u7247\u5c71\u5ca9\u3002 \u5979\u5728\u4ed6\u8033\u8fb9\u4f4e\u8bed\uff1a\u201c\u73b0\u5728\u897f\u95e8\u5439\u96ea\u2014\u5b9a\u4e5f\u8ba4\u4e3a\u4f60\u5df2\u6b7b\u4e86\uff0c\u53ea\u8981\u4f60\u613f\u610f\uff0c\u6211\u4eec\u4e00\u5b9a\u53ef \u4ee5\u627e\u4e2a\u5b89\u9759\u7684\u5730\u65b9\u597d\u597d\u6d3b\u4e0b\u53bb\uff0c\u7edd\u4e0d\u6b62\u6d3b\u2014\u5929\u3002\u201d \u201c\u672c\u6765\u6211\u5df2\u51b3\u5fc3\u8981\u4e3a\u8001\u5200\u628a\u5b50\u6b7b\u7684\uff0c\u53ef\u662f\u6211\u9047\u89c1\u4e86\u4f60\u3002\u201d\u5979\u53c8\u63a5\u7740\u9053\uff1a\u201c\u4ed6\u4e5f\u6ca1\u6709 \u4e00\u5b9a\u8981\u7559\u4e0b\u6211\uff0c\u6240\u4ee5\u6211\u5e0c\u671b\u4f60\u4ee5\u540e\u6c38\u8fdc\u5fd8\u4e86\u82b1\u5be1\u5987\u8fd9\u4e2a\u4eba\uff0c\u6211\u59d3\u67f3\uff0c\u53eb\u67f3\u9752\u9752\u3002\u201d \u524d\u9762\u8349\u8272\u9752\u9752\uff0c\u6728\u53f6\u4e5f\u9752\u9752\u3002 \u9646\u5c0f\u51e4\u5e76\u6ca1\u6709\u76f4\u63a5\u8d70\u8fdb\u53bb\uff0c\u4ed6\u5e76\u6ca1\u6709\u5fd8\u8bb0\u8fd9\u662f\u7247\u5403\u4eba\u7684\u6811\u6797\u3002 \u4ed6\u4eec\u5728\u6797\u5916\u7684\u5c71\u5761\u4e0a\u5750\u4e0b\u6765\uff0c\u9752\u9752\u7684\u8349\u5730\u4e0a\uff0c\u6709\u7247\u7247\u843d\u53f6\u201c \u8fd8\u662f\u6625\u5929\uff0c\u600e\u4e48\u4f1a\u6709\u843d\u53f6\uff1f \u9646\u5c0f\u51e4\u62fe\u8d77\u4e86\u2014\u7247\uff0c\u53ea\u770b\u4e86\u4e24\u773c\uff0c\u638c\u5fc3\u5ffd\u7136\u5192\u51fa\u4e86\u51b7 \u67f3\u9752\u9752\u7acb\u523b\u53d1\u89c9\u4e86\u4ed6\u5f02\u6837\u7684\u8868\u60c5\uff0c\u7acb\u523b\u95ee\u9053\uff1a\u201c\u4f60\u5728\u770b\u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u51e4\u6307\u4e86\u6307\u843d\u53f6\u7684\u6839\u8482\uff0c\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u88ab\u98ce\u5439\u843d\u7684\u3002\u201d \u53f6\u8482\u4e0a\u7684\u5207\u53e3\u5e73\u6ed1\u548c\u6574\u9f50\u3002 \u67f3\u9752\u9752\u76b1\u8d77\u4e86\u7709\uff0c\u9053\uff1a\u201c\u4e0d\u662f\u98ce\uff0c\u96be\u9053\u662f\u5251\u950b\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u5251\u950b\uff0c\u662f\u5251\u6c14\uff01\u201d \u67f3\u9752\u9752\u7684\u8138\u8272\u53d8\u4e86\u8c01\u624b\u4e0a\u7684\u5251\u80fd\u53d1\u51fa\u5982\u6b64\u950b\u9510\u7684\u5251\u5de6\u624b\u547c \u9646\u5c0f\u51e4\u53c8\u4ece\u8349\u5730\u4e0a\u62fe\u8d77\u4e86\u2014\u6839\u7fbd\u6bdb\uff0c\u4e5f\u662f\u88ab\u5251\u6c14\u6467\u843d\u7f51\u3002 \u6797\u5916\u6709\u98de\u9e1f\uff0c\u98de\u9e1f\u53ef\u5145\u9965\u3002 \u53ef\u662f\u5929\u4e0b\u53c8\u6709\u51e0\u4eba\u80fd\u7528\u5251\u6c14\u51fb\u843d\u98de\u9e1f\uff1f\u9664\u4e86\u897f\u95e8\u5439\u96ea\u5916\u8fd8\u6709\u8c01\uff1f \u67f3\u9752\u9752\u5df2\u4e0d\u518d\u7b11\uff1a\u201c\u4ed6\u8fd8\u6ca1\u6709\u8d70\uff1f\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u4ed6\u4e00\u5411\u662f\u4e2a\u4e0d\u5bb9\u6613\u6b7b\u5fc3\u7684\u4eba\u3002\u201d \u67f3\u9752\u9752\u5782\u4e0b\u5934\uff0c\u9053\uff1a\u6211\u77e5\u9053\u4ed6\u662f\u4e2a\u600e\u4e48\u6837\u7684\u4eba\uff0c\u6211\u89c1\u8fc7\u4ed6\u3002\u201d \u5979\u5ffd\u53c8\u62ac\u8d77\u5934\uff1a\u201c\u53ef\u662f\u6211\u4eec\u7528\u4e0d\u7740\u4f2f\u4ed6\uff0c\u4ee5\u6211\u4eec\u4e24\u4e2a\u4eba\u4e4b\u529b\uff0c\u96be\u9053\u5bf9\u4ed8\u4e0d\u4e86\u4ed6\u4e00 \u4e2a\uff1f\u201d \u9646\u5c0f\u51e4\u6447\u6447\u5934\u3002 \u67f3\u9752\u9752\u9053\uff1a\u201c\u4f60\u8fd8\u6015\u4ed6\uff1f\u4e3a\u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u51e4\u4e5f\u5782\u4e0b\u5934\uff0c\u9eef\u7136\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u5fc3\u91cc\u6709\u6127\u3002\u201d \u67f3\u9752\u9752\u8272\u201c\u4f60\u771f\u7684\u505a\u8fc7\u90a3\u79cd\u4e8b\uff1f\u201d \u9646\u5c0f\u51e4\u53f9\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\u90fd\u6709\u505a\u9519\u4e8b\u7684\u65f6\u5019\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u4f46\u4f60\u5374\u4e0d\u662f\u4e2a\u7cca\u6d82\u4eba\u3002\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e0d\u7cca\u6d82\u7684\u4eba\u4e5f\u96be\u514d\u4e00\u65f6\u7cca \u6d82\u3002\u201d \u67f3\u9752\u9752\u7684\u8138\u8272\u66f4\u9eef\u6de1\uff0c\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u6211\u4eec\u4e00\u5b9a\u8d70\u4e0d\u51fa\u8fd9\u7247\u6811\u6797\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6240\u4ee5\u73b0\u5728\u6211\u4eec\u53ea\u6709\u4e00\u6761\u8def\u53ef\u8d70\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u54ea\u6761\u8def\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56de\u5934\u7684\u8def\u3002\u201d \u67f3\u9752\u9752\u5403\u60ca\u7684\u770b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u518d\u56de\u5e7d\u7075\u5c71\u5e84\u53bb\uff1f\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u65e0\u8bba\u90a3\u91cc\u9762\u6709\u4ec0\u4e48\u5728\u7b49\u7740\u6211\uff0c\u603b\u6bd4\u6b7b\u5728\u8fd9\u6811\u6797\u91cc\u597dao \u5c71\u8c37\u91cc\u8fd8\u662f\u4e91\u96fe\u51c4\u8ff7\uff0c\u8d70\u56de\u53bb\u4e5f\u548c\u8d70\u51fa\u6765\u540c\u6837\u4e0d\u5bb9\u6613\u3002 \u5bf9\u9762\u7684\u5c71\u5ca9\u4e0a\uff0c\u4e00\u4e2a\u4eba\u4eff\u4f5b\u6b63\u5f85\u4e58\u98ce\u800c\u53bb\uff0c\u6b63\u662f\u90a3\u52fe\u9b42\u4f7f\u8005\u3002 \u4ed6\u867d\u7136\u6ca1\u6709\u8138\uff0c\u6ca1\u6709\u540d\u59d3\uff0c\u53ef\u662f\u4ed6\u6709\u624b\uff0c\u6709\u5251\u3002 \u5251\u5df2\u5728\u624b\uff0c\u5251\u5df2\u51fa\u9798\u3002 \u4ed6\u51b7\u51b7\u770b\u7740\u9646\u5c0f\u51e4\uff0c\u9053\uff1a\u201c\u4f60\u65e2\u7136\u5df2\u51fa\u53bb\uff0c\u4e3a\u4ec0\u4e48\u53c8\u56de\u6765\uff1f\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u60f3\u5bb6\u3002\u201d\u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u8fd9\u91cc\u4e0d\u662f\u4f60\u7684\u5bb6\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u672c\u6765\u4e0d\u662f\uff0c\u73b0\u5728\u5374\u662f\uff0c\u56e0\u4e3a\u6211\u5df2\u6ca1\u6709\u522b\u7684\u5730\u65b9\u53ef\u53bboU \u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u4f60\u770b\u770b\u6211\u624b\u91cc\u662f\u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u597d\u50cf\u662f\u628a\u5251\u3002\u201d \u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u4f60\u80fd\u80dc\u5f97\u4e86\u6211\u624b\u4e2d\u8fd9\u67c4\u5251\uff0c\u6211\u5c31\u653e\u4f60\u8fc7 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u529d\u4f60\u6700\u597d\u4e0d\u8981\u8bd5\uff1f\u201d \u52fe\u9b42\u4f7f\u8005\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u6709\u628a\u63e1\u80fd\u80dc\u6211\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u6ca1\u6709\u628a\u63e1\uff0c\u8fde\u4e00\u5206\u628a\u63e1\u90fd\u6ca1\u6709\uff0c\u53ef\u662f\u6211\u81f3\u5c11\u6709\u628a\u63e1\u80fd\u63a5\u5f97\u4f4f\u4f60\u5341 \u62db\u3002\u201d \u52fe\u9b42\u4f7f\u8005\u9053\uff1a\u201c\u80fd\u63a5\u4efb\u6211\u5341\u62db\u53c8\u5982\u4f55\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u4e5f\u6709\u628a\u63e1\u5728\u5341\u62db\u4e4b\u4e2d\u770b\u51fa\u4f60\u7684\u6b66\u529f\u6765\u5386\u3002 \u4ed6\u53c8\u7b11\u4e86\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u6211\u60f3\u4f60\u4e00\u5b9a\u4e0d\u613f\u8ba9\u4eba\u77e5\u9053\u4f60\u7684\u6765\u5386\u3002\u201d \u52fe\u9b42\u4f7f\u8005\u95ed\u4e0a\u4e86\u5634\uff0c\u63e1\u5251\u7684\u624b\u80cc\u4e0a\uff0c\u9752\u7b4b\u6bd2\u86c7\u822c\u51f8\u8d77\u3002 \u9646\u5c0f\u51e4\u5374\u8fde\u770b\u90fd\u4e0d\u518d\u770b\u4ed6\u4e00\u773c\uff0c\u5c31\u65bd\u65bd\u7136\u4ece\u4ed6\u5251\u4e0b\u8d70\u4e86\u8fc7\u53bb\uff0c\u67f3\u9752\u9752\u4e5f\u53ea\u6709\u8ddf\u7740\u3002 \u4ed6\u624b\u4e0a\u9752\u7b4b\u6bd2\u86c7\u822c\u626d\u52a8\uff0c\u5251\u5c16\u4e5f\u6709\u5bd2\u5149\u98a4\u52a8\u3002 \u9646\u5c0f\u51e4\u6ca1\u6709\u56de\u5934\uff0c\u67f3\u9752\u9752\u5374\u8fde\u8863\u9886\u90fd\u6e7f\u4e86\uff0c\u5979\u770b\u5f97\u51fa\u9646\u5c0f\u98ce\u5168\u8eab\u4e0a\u4e0b\u8fde\u4e00\u70b9\u8b66\u6212\u90fd \u6ca1\u6709\uff0c\u8fd9\u4e00\u5251\u82e5\u662f\u523a\u51fa\uff0c\u5c31\u51ed\u5251\u5c16\u90a3\u4e00\u9053\u98a4\u52a8\u7684\u5bd2\u5149\uff0c\u5df2\u8db3\u4ee5\u5236\u4ed6\u7684\u6b7b\u547d\u3002 \u53ef\u662f\u52fe\u9b42\u4f7f\u90fd\u5c45\u7136\u4e5f\u5c31\u8fd9\u4e48\u6837\u770b\u7740\u4ed6\u8d70\u8fc7\u53bb\uff0c\u76f4\u7b49\u4ed6\u8d70\u51fa\u5f88\u8fdc\uff0c\u5251\u624d\u843d\u4e0b\u3002 \u53ea\u542c\u4e00\u58f0\u9f99\u541f\uff0c\u706b\u661f\u56db\u6fc0\uff0c\u4e00\u5757\u5ca9\u77f3\u5df2\u5728\u4ed6\u5251\u4ea7\u88c2\u6210\u56db \u67f3\u9752\u9752\u5077\u5077\u7684\u56de\u5934\u77a7\u4e86\u4e00\u773c\uff0c\u8fde\u80cc\u5fc3\u90fd\u6e7f\u900f\u4e86\u3002 \u8fd9\u5c71\u8c37\u91cc\u7684\u5ca9\u77f3\u6bcf\u4e00\u5757\u90fd\u575a\u5982\u7cbe\u94a2\uff0c\u5c31\u7b97\u7528\u94c1\u59fb\u5229\u65a7\uff0c\u4e5f\u672a\u5fc5\u80fd\u780d\u5f97\u52a8\u5206\u6beb\uff0c\u8fd9\u4e00 \u5251\u7684\u950b\u9510\u548c\u529b\u91cf\uff0c\u5b9e\u5728\u592a\u53ef\u6015\u3002 \u53c8\u8d70\u51fa\u5f88\u8fdc\u540e\uff0c\u5979\u624d\u8f7b\u8f7b\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u770b\u5230\u90a3\u4e00\u5251\u6ca1\u6709\uff1f\u201d\u9646\u5c0f\u51e4\u6de1\u6de1\u9053\uff1a \u201c\u90a3\u4e5f\u6ca1\u4ec0\u4e48\u4e86\u4e0d\u8d77\u3002\u201d \u67f3\u9752\u9752\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u8981\u600e\u4e48\u6837\u7684\u5251\u6cd5\u624d\u7b97\u4e86\u4e0d\u8d77\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u90a3\u2014\u5251\u82e5\u80fd\u4ece\u4ece\u5bb9\u5bb9\u7684\u6536\u56de\u53bb\uff0c\u624d\u7b97\u4e86\u4e0d\u8d77\u3002\u201d \u521a\u624d\u52fe\u9b42\u4f7f\u8005\u76db\u6012\u4e4b\u4e0b\uff0c\u771f\u529b\u53d1\u52a8\uff0c\u805a\u5728\u5251\u5c16\uff0c\u5c31\u597d\u50cf\u5f13\u5df2\u5f15\u6ee1\uff0c\u4e0d\u5f97\u4e0d\u53d1\uff0c\u6240\u4ee5 \u90a3\u2014\u5251\u51fb\u51fa\uff0c\u5a01\u52bf\u81ea\u7136\u60ca\u4eba\u3002 \u53ef\u662f\u8fd9\u4e5f\u8bc1\u660e\u4e86\u4ed6\u8fd8\u4e0d\u80fd\u63a7\u5236\u81ea\u5df1\u7684\u706b\u6c14\uff0c\u771f\u529b\u8fd8\u4e0d\u80fd\u6536\u53d1\u81ea\u5982\uff0c\u82e5\u662f\u80fd\u5c06\u8fd9\u4e00\u5251 \u4ece\u5bb9\u6536\u56de\uff0c\u624d\u771f\u6b63\u662f\u7089\u706b\u7eaf\u9752\u7684\u5883\u754c\u3002 \u67f3\u9752\u9752\u540d\u95e8\u4e4b\u540e\uff0c\u5f53\u7136\u61c2\u5f97\u8fd9\u9053\u7406\uff0c\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u5c31\u7b97\u90a3\u4e00\u5251\u6ca1\u4ec0\u4e48\u4e86\u4e0d \u8d77\uff0c\u5982\u679c\u7528\u6765\u5bf9\u4ed8\u4f60\uff0c\u4f60\u6709\u628a\u63e1\u80fd\u907f\u5f00\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u6ca1\u6709\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u4f60\u6709\u628a\u63e1\u786e\u5b9a\u4ed6\u4e0d\u4f1a\u6740\u4f60\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u4e5f\u6ca1\u6709\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u4f46\u4f60\u5374\u597d\u50cf\u4e00\u70b9\u4e5f\u4e0d\u5728\u4e4e\uff1f\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e00\u4e2a\u5df2\u65e0\u8def\u53ef\u8d70\u7684\u4eba\uff0c\u505a\u4e8b\u603b\u662f\u4e0d\u80fd\u4e0d\u5192\u4e00\u70b9\u9669\u7684\u3002\u201d \u67f3\u9752\u9752\u53f9\u4e86\u53e3\u6c14\uff0c\u8fd8\u6ca1\u6709\u5f00\u53e3\uff0c\u5c31\u770b\u89c1\u4e00\u4e2a\u5934\u6234\u7af9\u7b20\u7684\u7070\u8863\u4eba\uff0c\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u65bd\u65bd \u7136\u5728\u524d\u9762\u8d70\u3002 \u201c\u8001\u5200\u628a\u5b50\uff01\u201d \u9646\u5c0f\u51e4\u558a\u4e86\u4e00\u58f0\uff0c\u6ca1\u6709\u56de\u5e94\uff0c\u60f3\u8ffd\u4e0a\u53bb\uff0c\u8fd9\u7070\u8863\u4eba\u8d70\u8def\u867d\u7136\u662f\u5728\u8e31\u65b9\u6b65\uff0c\u4ed6\u5374\u504f\u504f \u8ffd\u4e0d\u4e0a\u3002 \u7b49\u5230\u4ed6\u51c6\u5907\u653e\u5f03\u65f6\uff0c\u524d\u9762\u7684\u7070\u8863\u4eba\u5374\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u7edd\u4e0d\u662f\u968f\u968f\u4fbf\u4fbf\u5c31\u4f1a\u62ff\u751f\u547d\u53bb\u5192 \u9669\u7684\u90a3\u79cd\u4eba\uff0c\u4f60\u77e5\u9053\u4ed6\u7edd\u4e0d\u4f1a\u6740\u4f60\u7684\uff0c\u4f60\u6709\u628a\u63e1\u3002\u201d \u9646\u5c0f\u51e4\u6ca1\u6709\u5426\u8ba4\uff0c\u4e5f\u4e0d\u80fd\u5426\u8ba4\uff0c\u4ed6\u5ffd\u7136\u53d1\u73b0\u65e0\u8bba\u4efb\u4f55\u4e8b\u90fd\u5f88\u96be\u7792\u8fc7\u8001\u5200\u628a\u5b50\u3002 \u8001\u5200\u628a\u5b50\u53c8\u9053\uff1a\u201c\u4f60\u51ed\u4ec0\u4e48\u6709\u8fd9\u79cd\u628a\u63e1\uff1f\u201d \u9646\u5c0f\u51e4\u53ea\u6709\u8bf4\u5b9e\u8bdd\uff1a\u201c\u6211\u770b\u5f97\u51fa\u4ed6\u7684\u8138\u662f\u88ab\u5251\u950b\u524a\u6389\u7684\uff0c\u4ee5\u4ed6\u7684\u5251\u6cd5\uff0c\u4e16\u4e0a\u53ea\u6709\u4e00 \u4e2a\u4eba\u80fd\u4e00\u5251\u524a\u53bb\u4ed6\u7684\u8138\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u8c01\uff1f\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u81ea\u5df1\u3002\u201d \u8001\u5200\u628a\u5b50\u51b7\u7b11\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u5b81\u53ef\u6bc1\u6389\u81ea\u5df1\u7684\u8138\uff0c\u4e5f\u4e0d\u613f\u8ba9\u4eba\u8ba4\u51fa\u4ed6\uff0c\u5f53\u7136\u4e5f\u4e0d\u613f\u8ba9\u6211\u770b\u51fa\u4ed6\u7684 \u6765\u5386\uff0c\u6240\u4ee5\u6211\u786e\u5b9a\u4ed6\u7edd\u4e0d\u4f1a\u51fa\u624b\u7684\u3002\u201d \u8001\u5200\u628a\u5b50\u970d\u7136\u56de\u5934\uff0c\u76ef\u7740\u4ed6\uff0c\u76ee\u5149\u5728\u7af9\u7b7e\u624d\uff0c\u770b\u6765\u8fd8\u662f\u9510\u5982\u5200\u950b\uff1a\u201c\u4f60\u5982\u6b64\u6709\u628a \u63e1\uff0c\u662f\u4e0d\u662f\u56e0\u4e3a\u4f60\u65e9\u5df2\u731c\u51fa\u4ed6\u662f\u8c01\u4e86\uff1f\u201d \u9646\u5c0f\u51e4\u52c9\u5f3a\u7b11\u4e86\u7b11\u3002\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u5076\u5c14\u60f3\u8d77\u4e86\u2014\u4ef6\u4e8b\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u8bf4\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e8c\u5341\u5e74\u524d\uff0c\u6b66\u5f53\u6700\u8d1f\u76db\u540d\u7684\u5251\u5ba2\u672c\u662f\u77f3\u9e64\uff0c\u6700\u6709\u5e0c\u671b\u7ee7\u627f\u6b66\u5f53\u9053\u7edf\u7684 \u4e5f\u662f\u4ed6\uff0c\u53ef\u662f\u5c31\u5728\u4ed6\u5df4\u5c06\u63a5\u957f\u95e8\u6237\uff0c\u7684\u524d\u5915\uff0c\u6c5f\u6e56\u4e2d\u5374\u7a81\u7136\u4f20\u51fa\u4ed6\u5df2\u66b4\u6bd9\u7684\u6d88\u606f\u3002 \u90a3\u65f6\u4ed6\u6b63\u5f53\u76db\u5e74\uff0c\u2018\u4e2a\u5185\u5916\u517c\u4fee\u7684\u4e2d\u5e74\u4eba\uff0c\u600e\u4e48\u4f1a\u7a81\u7136\u66b4\u6bd9\uff1f \u9646\u5c0f\u51e4\u53c8\u9053\uff1a\u201c\u6240\u4ee5\u6c5f\u6e56\u4eba\u4e2d\u5bf9\u4ed6\u7684\u6b7b\uff0c\u90fd\u96be\u514d\u6709\u4e9b\u6000\u7591\uff0c\u5f53\u65f6\u8c23\u8a00\u7eb7\u7eb7\uff0c\u6709\u4eba\u751a \u81f3\u8bf4\u4ed6\u662f\u56e0\u4e3a\u4e0d\u5b88\u6e05\u89c4\uff0c\u88ab\u9010\u51fa\u95e8\u6237\uff0c\u624d\u6124\u800c\u81ea\u5c3d\u7684\uff0c\u6211\u5374\u6000\u7591\u4ed6\u4e00\u76f4\u90fd\u6d3b\u5728\u4e16\u4e0a\uff0c\u53ea \u4e0d\u8fc7\u65e0\u989c\u89c1\u4eba\u800c\u5df2\u3002\u201d \u8001\u5200\u628a\u5b50\u9759\u8c89\u7684\u542c\u7740\uff0c\u7b49\u4ed6\u8bf4\u5b8c\u4e86\uff0c\u624d\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u8be5\u518d\u6765\u89c1\u6211\u7684\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ef\u662f\u6211\u4e5f\u77e5\u9053\u4f60\u7edd\u4e0d\u4f1a\u6740\u6211\u3002\u201d \u8001\u5200\u628a\u5b50\u5389\u58f0\u9053\uff1a\u201c\u4f60\u51ed\u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u73b0\u5728\u6b63\u662f\u8981\u7528\u4eba\u7684\u65f6\u5019\uff0c\u4f60\u4e5f\u5e94\u8be5\u77e5\u9053\u6211\u662f\u4e2a\u5f88\u6709\u7528\u7684 \u4eba\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u7528\u4f60\uff1f\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8981\u505a\u5927\u4e8b\uff0c\u5c31\u2014\u5b9a\u8981\u7528\u6709\u7528\u7684 \u4eba\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u8981\u505a\u5927\u4e8b\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8981\u521b\u7acb\u8fd9\u7247\u57fa\u4e1a\u5df2\u4e0d\u77e5\u8981\u8017\u5c3d\u591a\u5c11\u4eba\u529b\u7269\u529b\uff0c\u8981\u7ef4\u6301\u4e0b\u53bb\u66f4\u4e0d\u5bb9\u6613\uff0c \u5c31\u7b97\u4f60\u8ba2\u5408\u7ea6\u6bcf\u4eba\u90fd\u8981\u6536\u8d39\u5341\u4e07\u4e24\uff0c\u4e5f\u672a\u5fc5\u80fd\u5e94\u4ed8\u4f60\u7684\u670b\u53cb\uff0c\u5c31\u7b97\u80fd\u8d5a\u4e00\u70b9\uff0c\u4ee5\u4f60\u7684\u4e3a \u4eba\uff0c\u4e5f\u7edd\u4e0d\u4e3a\u8fd9\u533a\u533a\u4e00\u70b9\u94b1\u8d22\u800c\u82b1\u8d39\u8fd9\u4e48\u591a\u82e6\u5fc3\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u8bf4\u4e0b\u53bb\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6240\u4ee5\u6211\u65ad\u5b9a\u4f60\u8fd9\u4e48\u6837\u505a\uff0c\u4e00\u5b9a\u662f\u522b\u6709\u6240\u56fe\uff0c\u4ee5\u4f60\u7684\u624d\u667a\uff0c\u6240\u56fe\u8c0b\u7684\u5f53 \u7136\u662f\u4ef6\u5927\u4e8b\u3002\u201d \u8001\u5200\u628a\u5b50\u51b7\u51b7\u7684\u770b\u7740\u4ed6\uff0c\u76ee\u5149\u66f4\u9510\u5229\uff0c\u5ffd\u53c8\u8f6c\u8eab\uff0c\u9053\uff1a\u201c\u8ddf\u6211\u6765\u3002 \u66f2\u6298\u5a49\u8712\u5c0f\u8def\u7684\u5c3d\u5934\uff0c\u662f\u2014\u680b\u5f62\u5f0f\u53e4\u8001\u62d9\u6734\u7684\u77f3\u5c4b\uff0c\u91cc\u9762\u7684\u751a\u81f3\u5e26\u7740\u79cd\u9634\u68ee\u6797\u7684\u611f \u89c9\uff0c\u663e\u89c1\u4e0d\u5e38\u6709\u4eba\u5c45\u4f4f\u3002\u53ef\u662f\u73b0\u5728\u5c4b\u5b50\u91cc\u5374\u5df2\u6709\u4e09\u4e2a\u4eba\u5728\u7b49\u7740\uff0c\u4e09\u4e2a\u672c\u5df2\u662f\u6b7b\u4e86\u7684\u4eba\u3002 \u94a9\u5b50\u3001\u8868\u54e5\u3001\u7ba1\u5bb6\u5a46\uff0c\u4e09\u4e2a\u4eba\u6b63\u7ad9\u5728\u4e00\u5f20\u9ec4\u5e4c\u4f4e\u5782\u7684\u795e\u6848\u65c1\uff0c\u8138\u4e0a\u5e26\u7740\u79cd\u4e0d\u6000\u597d\u610f \u7684\u8be1\u7b11\uff0c\u7528\u773c\u89d2\u8198\u7740\u9646\u5c0f\u51e4\u3002 \u9646\u5c0f\u51e4\u867d\u7136\u5728\u5c3d\u529b\u63a7\u5236\u7740\u81ea\u5df1\uff0c\u8fd8\u662f\u96be\u514d\u663e\u5f97\u5f88\u5403\u60ca\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u73b0\u5728\u4f60\u603b\u7b97\u5df2\u660e\u767d\u4e86\u5427\uff1f\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u660e\u767d\uff0c\u4e00\u70b9\u90fd\u4e0d\u660e\u767d\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4ece\u5934\u5230\u5c3e\uff0c\u6839\u672c\u5c31\u662f\u4e2a\u5708\u5957\u3002\u201d \u9646\u5c0f\u51e4\u8fd8\u662f\u4e0d\u660e\u767d\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4ed6\u4eec\u505a\u7684\u4e8b\uff0c\u90fd\u662f\u6211\u5b89\u6392\u7684\uff0c\u4e3a\u7684\u53ea\u4e0d\u8fc7\u662f\u8981\u8bd5\u63a2\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u6000\u7591\u6211\u662f\u6765\u5367\u5e95\u7684\u5978\u7ec6\uff1f\u201d \u8005\u5200\u628a\u5b50\u9053\uff1a\u201c\u65e0\u8bba\u8c01\u6211\u90fd\u6000\u7591\uff0c\u8fd9\u91cc\u6bcf\u4e2a\u4eba\u90fd\u662f\u7ecf\u8fc7\u8003\u9a8c\u7684\uff0c\u987e\u98de\u4e91\u6740\u7684\u5c31\u662f\u90a3 \u4e9b\u7ecf\u4e0d\u8d77\u8003\u9a8c\u7684\u4eba\u3002\u201d \u9646\u5c0f\u51e4\u7ec8\u4e8e\u660e\u767d\uff1a\u201c\u4f60\u6545\u610f\u653e\u6211\u8d70\uff0c\u4e5f\u662f\u4e3a\u4e86\u8bd5\u63a2\u6211\uff0c\u662f\u4e0d\u662f\u771f\u7684\u5df2\u88ab\u897f\u95e8\u5439\u96ea\u903c \u5f97\u65e0\u8def\u53ef\u8d70\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u56de\u5934\uff0c\u6b64\u523b\u4e00\u5b9a\u5df2\u6b7b\u5728\u90a3\u5403\u4eba\u7684\u6811\u6797\u91cc\u3002 \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u7b97\u51c6\u4e86\u6211\u4f1a\u628a\u67f3\u9752\u9752\u5e26\u8d70\u7684\uff0c\u6b63\u597d\u8981\u5979\u6765\u6740\u6211\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u90a3\u5012\u662f\u4e2a\u610f\u5916\uff0c\u4f60\u82e5\u4e0d\u56de\u5934\uff0c\u5979\u4e5f\u5f97\u966a\u4f60\u6b7b\u3002\u201d \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u8f6c\u8fc7\u5934\uff0c\u67f3\u9752\u9752\u4e5f\u6b63\u5728\u76ef\u7740\u4ed6\u3002 \u4e24\u4e2a\u4eba\u90fd\u6ca1\u6709\u5f00\u53e3\uff0c\u4e0d\u7ba1\u8981\u8bf4\u4ec0\u4e48\uff0c\u90fd\u5df2\u5728\u8fd9\u773c\u6ce2\u2014\u89e6\u95f4\u8bf4\u5b8c\u4e86\u3002 \u6240\u4ee5\u5979\u65e2\u6ca1\u6709\u57cb\u6028\uff0c\u4ed6\u4e5f\u6ca1\u6709\u6b49\u75af\u3002 \u8fd9\u4e16\u4e0a\u672c\u5c31\u6709\u79cd\u5947\u5999\u7684\u611f\u60c5\uff0c\u662f\u4e0d\u5fc5\u57cb\u6028\uff0c\u4e5f\u65e0\u9700\u6b49\u759a\u7684\u3002 \u8001\u5200\u628a\u5b50\u770b\u7740\u4ed6\u4eec\uff0c\u76f4\u7b49\u9646\u5c0f\u51e4\u518d\u56de\u8f6c\u8138\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u5df2\u660e\u767d\u6211\u4e3a \u4ec0\u4e48\u8981\u8fd9\u6837\u505a\uff1f\u201d \u9646\u5c0f\u51e4\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u8981\u770b\u770b\u6211\uff0c\u662f\u4e0d\u662f\u4e2a\u503c\u5f97\u88ab\u4f60\u7528\u7684\u4eba\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u5f88\u4e0d\u9519\u3002\u201d \u4ed6\u7684\u8bed\u58f0\u5ffd\u7136\u53d8\u5f97\u5f88\u548c\u7f13\uff1a\u201c\u4f60\u7684\u6b66\u529f\u673a\u667a\u90fd\u4e0d\u9519\uff0c\u6700\u91cd\u8981\u7684\u662f\uff0c\u4f60\u6ca1\u6709\u5728\u6211\u9762\u524d \u8bf4\u8c0e\u3002\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u65e2\u7136\u660e\u660e\u77e5\u9053\u9a97\u4e0d\u8fc7\u4f60\uff0c\u53c8\u4f55\u5fc5\u8bf4\u8c0e\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u6211\u559c\u6b22\u806a\u660e\u4eba\uff0c\u6240\u4ee5\u4ece\u4eca\u4ee5\u540e\uff0c\u4f60\u5c31\u662f\u6211\u7684\u4f19\u4f34 \u4e86\uff0c\u53ea\u8981\u4e0d\u8d70\u51fa\u8fd9\u5c71\u5e84\uff0c\u968f\u4fbf\u4f60\u8981\u5e72\u4ec0\u4e48\u90fd\u884c\uff0c\u6211\u76f8\u4fe1\u4f60\u8fd9\u4e48\u806a\u660e\u7684\u4eba\uff0c\u7edd\u4e0d\u4f1a\u505a\u50bb\u4e8b \u7684\u3002\u201d \u4ed6\u56de\u5934\u5429\u5490\u7ba1\u5bb6\u5a46\uff1a\u201c\u4f20\u8bdd\u4e0b\u53bb\uff0c\u4eca\u5929\u665a\u4e0a\u6446\u5bb4\u4e3a\u4ed6\u63a5\u98ce\u3002\u201d \u7ba1\u5bb6\u5a46\u9000\u4e0b\uff0c\u8868\u54e5\u548c\u94a9\u5b50\u4e5f\u968f\u7740\u9000\u4e0b\u3002 \u8001\u5200\u628a\u5b50\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u7684\u5bb6\u5df2\u88ab\u5165\u62c6\u4e86\uff0c\u4ece\u4eca\u5929\u8d77\u4f60\u53ef\u4ee5\u642c\u5230\u9752\u9752\u90a3\u91cc\u53bboo \u9646\u5c0f\u51e4\u8fdf\u7591\u7740\uff0c\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u201d \u8001\u5200\u628a\u5b50\u4e0d\u8ba9\u4ed6\u8bf4\u4e0b\u53bb\uff0c\u53c8\u9053\uff1a\u201c\u6211\u5df2\u662f\u4e2a\u8001\u4eba\uff0c\u8001\u4eba\u603b\u662f\u5bb9\u6613\u5fd8\u8bb0\u5f88\u591a\u4e8b\u7684\u3002\u201d \u4ed6\u7ad9\u8d77\u6765\uff0c\u8f6c\u8fc7\u8eab\uff0c\u9762\u5bf9\u7740\u90a3\u9ec4\u5e4c\u4f4e\u5782\u7684\u795e\u5baa\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u53ea\u6709\u4e00\u4ef6\u4e8b\u6211\u8fd8\u4e0d\u80fd\u5fd8 \u8bb0\uff0c\u5230\u4e86\u65f6\u5019\uff0c\u6211\u4e00\u5b9a\u4f1a\u544a\u8bc9\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u6ca1\u6709\u518d\u95ee\uff0c\u4ed6\u77e5\u9053\u8001\u5200\u628a\u5b50\u8bf4\u7684\u8bdd\u5c31\u662f\u547d\u4ee4\u300f \u9152\u83dc\u4e30\u5bcc\u800c\u7cbe\u7f8e\uff0c\u9152\u7684\u79cd\u7c7b\u5c31\u6709\u5341\u4e8c\u79cd\uff0c\u5bb4\u5e2d\u7684\u5f62\u5f0f\u662f\u53e4\u98ce\u7684\uff0c\u5341\u516b\u5f20\u957f\u684c\u6446\u6210\u534a \u4e2a\u201c\u53e3\u201d\u5b87\uff0c\u8001\u5200\u628a\u5b50\u5750\u5728\u6b63\u4e2d\uff0c\u4ed6\u7684\u5de6\u8fb9\u5c31\u662f\u9646\u5c0f\u51e4\u3002 \u5927\u5bb6\u5bf9\u9646\u5c0f\u51e4\u7684\u770b\u6cd5\u5f53\u7136\u5df2\u548c\u524d\u4e24\u5929\u5927\u4e0d\u76f8\u540c\uff0c\u4e0d\u4f46\u56e0\u4e3a\u4ed6\u662f\u8fd9\u5bb4\u4f1a\u7684\u4e3b\u5bbe\uff0c\u800c\u4e14 \u5ffd\u7136\u53d8\u6210\u4e86\u8001\u5200\u628a\u5b50\u7684\u4eb2\u4fe1\u3002 \u7b2c\u4e00\u4e2a\u7ad9\u8d77\u6765\u5411\u4ed6\u656c\u9152\u81f4\u8d38\u662f\u201c\u94a9\u5b50\u201d\u6d77\u5947\u9614\uff0c\u7136\u540e\u662f\u8868\u54e5\u3001\u7ba1\u5bb6\u5a46\uff0c\u72ec\u5b64\u7f8e\u3002 \u53ea\u6709\u53f6\u7075\u59cb\u7ec8\u8fde\u770b\u90fd\u6ca1\u6709\u770b\u4ed6\u2014\u773c\uff0c\u56e0\u4e3a\u4ed6\u65c1\u8fb9\u5750\u7740\u7684\u5c31\u662f\u67f3\u9752\u9752\uff0e\u8fd9\u4e2a\u5403\u4eba\u7684\u5be1 \u5987\u597d\u50cf\u4e5f\u53d8\u4e86\uff0c\u53d8\u5f97\u5b89\u9759\u800c\u6e29\u67d4\u3002 \u8001\u5200\u628a\u5b50\u8fd8\u662f\u6234\u7740\u90a3\u5f62\u5f0f\u5947\u7279\u7684\u7af9\u7b7e\uff0c\u5c31\u8fde\u5750\u5728\u4ed6\u8eab\u65c1\u7684\u9646\u5c0f\u51e4\uff0c\u90fd\u5b8c\u5168\u770b\u4e0d\u89c1\u4ed6 \u7684\u9762\u76ee\u3002 \u4ed6\u5403\u5f97\u6781\u5c11\uff0c\u559d\u5f97\u66f4\u5c11\uff0c\u8bdd\u4e5f\u8bf4\u5f97\u4e0d\u591a\uff0c\u53ef\u662f\u65e0\u8bba\u8c01\u770b\u7740\u4ed6\u65f6\uff0c\u76ee\u4e2d\u90fd\u5e26\u7740\u670d\u4ece\u548c \u5c0a\u656c\u3002 \u5217\u5e2d\u7684\u4eba\u6bd4\u5f80\u65e5\u591a\uff0c\u2014\u5171\u6709\u4e94\u5341\u4e5d\u4e2a\uff0c\u9646\u5c0f\u51e4\u867d\u7136\u5927\u591a\u4e0d\u8ba4\u5f97\uff0c\u5374\u53ef\u4ee5\u60f3\u50cf\u5f97\u5230\uff0c \u8fd9\u4e9b\u4eba\u6614\u65e5\u4e00\u5b9a\u90fd\u6709\u6bb5\u8f89\u714c\u7684\u5386\u53f2\uff0c\u4e0d\u662f\u5bb6\u8d22\u5de8\u4e07\u7684\u4e16\u5bb6\u5b50\u5f1f\uff0c\u5c31\u662f\u96c4\u9738\u4e00\u65b9\u7684\u6b66\u6797\u8c6a \u6770\uff0c\u4e0d\u4f46\u8eab\u4efd\u90fd\u5f88\u9ad8\uff0c\u6b66\u529f\u4e5f\u4e00\u5b9a\u90fd\u4e0d\u9519\uff0c\u5426\u5219\u5c31\u6839\u672c\u6ca1\u6709\u8d44\u683c\u5230\u8fd9\u5e7d\u7075\u5c71\u5e84\u6765\u3002 \u201c\u662f\u4e0d\u662f\u4eba\u90fd\u5230\u9f50\u4e86\uff1f\u201d\u9646\u5c0f\u51e4\u6084\u6084\u7684\u95ee\u3002 \u201c\u53ea\u6709\u4e24\u4e2a\u4eba\u6ca1\u6709\u6765\u3002\u67f3\u9752\u9752\u4e5f\u6084\u6084\u7684\u56de\u7b54\uff1a\u201c\u4e00\u4e2a\u662f\u52fe\u9b42\u4f7f\u8005\uff0c\u4ed6\u4ece\u4e0d\u548c\u522b\u4eba\u76f8 \u5904\u3002\u201d \u201c\u8fd8\u6709\u2014\u4e2a\u662f\u8c01\uff1f\u201d \u201c\u53f6\u7075\u7684\u59d0\u59d0\uff0c\u53f6\u96ea\u3002\u201d\u67f3\u9752\u9752\u9053\uff1a\u201c\u5979\u559c\u6b22\u6253\u730e\uff0c\u7ecf\u5e38\u2014\u51fa\u53bb\u5c31\u662f\u5341\u6765\u5929\u3002\u201d \u201c\u5979\u4e3a\u4ec0\u4e48\u53ef\u4ee5\u81ea\u7531\u51fa\u5165\u3002\u201d \u201c\u90a3\u662f\u8001\u5200\u628a\u5b50\u7279\u8bb8\u7684\u3002\u201d\u67f3\u9752\u9752\u5728\u51b7\u7b11\uff1a\u201c\u8fd9\u5973\u4eba\u662f\u4e2a\u602a\u7269\uff0c\u5979\u8981\u505a\u7684\u4e8b\uff0c\u4ece\u6765 \u4e5f\u6ca1\u6709\u4eba\u80fd\u62e6\u5f97\u4f4f\u5979\uff0c\u5c31\u7b97\u5728\u8fd9\u91cc\u7684\u65f6\u5019\uff0c\u4e5f\u4ece\u6765\u4e0d\u8ddf\u522b\u4eba\u8bf4\u8bdd\u3002\u201d \u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u201c\u56e0\u4e3a\u5979\u603b\u89c9\u5f97\u81ea\u5df1\u6bd4\u522b\u4eba\u5f3a\u5f97\u591a\uff1a\u201c\u67f3\u9752\u9752\u663e\u7136\u5f88\u4e0d\u613f\u610f\u8c08\u8bba\u8fd9\u4e2a \u4eba\uff0c\u66f4\u4e0d\u613f\u548c\u9646\u5c0f\u51e4\u8c08\u8bba\u8fd9\u4e2a\u4eba\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u4eec\u4e5f\u65e0\u6cd5\u518d\u8bf4\u4e0b\u53bb\uff0c\u56e0\u4e3a\u4ed6\u4eec\u521a\u8bf4\u5230\u66f9 \u64cd\uff0c\u66f9\u64cd\u5c31\u5df2\u5230\u4e86\u3002 \u5ffd\u7136\u95f4\uff0c\u4e00\u53ea\u8c79\u5b50\u4ece\u95e8\u5916\u98de\u8fdb\u6765\uff0c\u91cd\u91cd\u7684\u843d\u5230\u4ed6\u4eec\u684c\u5b50\u524d\u9762\u3002 \u53f6\u96ea\u5c31\u662f\u8ddf\u7740\u8fd9\u8c79\u5b50\u4e00\u8d77\u8fdb\u6765\u7684\uff0c\u8c79\u5b50\u843d\u4e0b\uff0c\u9646\u5c0f\u51e4\u5c31\u770b\u89c1\u4e86\u5979\u7684\u4eba\u3002 \u5979\u7684\u4eba\u4e5f\u6b63\u50cf\u8c79\u5b50\u4e00\u6837\uff0c\u7f8e\u4e3d\u3001\u654f\u6377\u3001\u51b7\u9759\u3001\u6b8b\u9177\uff0c\u552f\u2014\u4e0d\u540c\u7684\u662f\uff0c\u8fd9\u8c79\u5b50\u5df2\u6b7b \u4e86\uff0c\u6b7b\u5728\u5979\u624b\u91cc\u3002 \u6b7b\u5728\u5979\u624b\u91cc\u7684\u8c79\u5b50\u8fd9\u5df2\u662f\u7b2c\u5341\u4e09\u53ea\uff0c\u9644\u8fd1\u5c71\u8c37\u91cc\u7684\u8c79\u5b50\u51e0\u4e4e\u5df2\u5168\u90fd\u6b7b\u5728\u5979\u624b\u91cc\u3002 \u5979\u559c\u6b22\u6253\u730e\uff0c\u66f4\u559c\u6b22\u730e\u8c79\u3002 \u4eba\u4eec\u4e3a\u4ec0\u4e48\u603b\u662f\u559c\u6b22\u730e\u6740\u81ea\u5df1\u7684\u540c\u7c7b\uff1f \u6240\u6709\u7684\u91ce\u517d\u4e2d\uff0c\u6700\u51f6\u608d\u654f\u6377\uff0c\u6700\u96be\u5bf9\u4ed8\u7684\u5c31\u662f\u8c79\u5b50\u3002 \u5c31\u7b97\u662f\u7ecf\u9a8c\u6781\u4e30\u5bcc\u7684\u730e\u4eba\uff0c\u4e5f\u7edd\u4e0d\u6562\u5355\u8eab\u53bb\u8ffd\u6355\u2014\u5934\u8c79\u5b50\uff0c\u51e0\u4e4e\u6ca1\u6709\u4eba\u6562\u53bb\u505a\u8fd9\u79cd \u611a\u8822\u800c\u5371\u9669\u7684\u4e8b\u3002 \u5979\u4e0d\u4f46\u6562\u505a\uff0c\u800c\u4e14\u505a\u5230\u4e86\u3002 \u5979\u662f\u4e2a\u6c89\u9759\u800c\u5185\u5411\u7684\u5973\u4eba\uff0c\u53ef\u5979\u730e\u8c79\uff0c\u5979\u770b\u6765\u7f8e\u4e3d\u800c\u67d4\u5f31\uff0c\u5374\u53c8\u50cf\u8c79\u5b50\u822c\u654f\u6377\u51b7 \u9177\u3002 \u8fd9\u8bb8\u591a\u79cd\u590d\u6742\u800c\u77db\u76fe\u7684\u6027\u683c\uff0c\u9020\u6210\u5979\u4e00\u79cd\u5947\u7279\u7684\u9b45\u529b\u3002 \u5c31\u8fde\u9646\u5c0f\u51e4\u90fd\u4ece\u672a\u770b\u89c1\u8fc7\u8fd9\u79cd\u5973\u4eba\uff0c\u4ed6\u770b\u89c1\u5979\uff0c\u51e0\u4e4e\u5fd8\u4e86\u8eab\u65c1\u67f3\u9752\u9752\u3002 \u53f6\u96ea\u5374\u59cb\u7ec8\u5728\u76ef\u7740\u8001\u5200\u628a\u5b50\uff0c\u82cd\u767d\u7684\u8138\uff0c\u82cd\u767d\u7684\u5634\u5507\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u8868\u54e5\u6b7b \u4e86\uff1f\u201d \u8001\u5200\u628a\u5b50\u70b9\u70b9\u5934\u3002 \u53f6\u96ea\u9053\uff1a\u201c\u4f60\u77e5\u9053\u662f\u8c01\u6740\u4e86\u4ed6\uff1f\u201d \u8001\u5200\u628a\u5b50\u53c8\u70b9\u70b9\u5934\u3002 \u53f6\u96ea\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u9646\u5c0f\u51e4\u4e00\u9897\u5fc3\u5ffd\u7136\u63d0\u8d77\uff0c\u4e00\u4e2a\u730e\u8c79\u7684\u5973\u4eba\uff0c\u4e3a\u4e86\u590d\u4ec7\uff0c\u662f\u4e0d\u60dc\u505a\u4efb\u4f55\u4e8b\u7684\u3002 \u4ed6\u4e0d\u60f3\u505a\u88ab\u6355\u6740\u7684\u8c79\u5b50\u3002 \u53ef\u662f\u8001\u5200\u628a\u5b50\u7684\u56de\u7b54\u5374\u4ee4\u4ed6\u5f88\u610f\u5916\uff1a\u201c\u662f\u897f\u95e8\u5439\u96ea\u3002\u201d \u53f6\u96ea\u7684\u8138\u8272\u66f4\u82cd\u767d\uff0c\u4e00\u53cc\u624b\u7a81\u7136\u63e1\u7d27\u3002 \u8001\u5200\u628a\u5b50\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u603b\u8bb0\u5f97\uff0c\u4f60\u54e5\u54e5\u4ee5\u524d\u5c31\u8bf4\u8fc7\uff0c\u82e5\u662f\u6b7b\u5728\u897f\u95e8\u5439\u96ea\u624b\u4e0b\uff0c\u7edd\u4e0d \u8bb8\u4efb\u4f55\u4eba\u4e3a\u4ed6\u590d\u4ec7\uff0c\u56e0\u4e3a\u90a3\u4e00\u5b9a\u662f\u573a\u516c\u5e73\u7684\u51b3\u6597\u3002\u201d \u4e00\u4e5f\u56e0\u4e3a\u4ed6\u4e0d\u613f\u4e3a\u4ed6\u53bb\u590d\u4ec7\u7684\u4eba\u518d\u6b7b\u4e8e\u897f\u95e8\u5439\u96ea\u5251 \u4e0b \u53f6\u96ea\u7684\u5634\u5507\u5728\u53d1\u6296\uff0c\u63e1\u7d27\u7684\u624b\u4e5f\u5728\u53d1\u6296\uff0c\u5ffd\u7136\u5750\u4e0b\u6765\uff0c\u5750\u5230\u5730\u4e0a\uff0c\u9053\uff1a\u201c\u62ff\u9152 \u6765\u3002\u201d \u4e3a\u5979\u9001\u9152\u53bb\u7684\u662f\u7ba1\u5bb6\u5a46\uff0c\u521a\u5f00\u5c01\u7684\u4e00\u575b\u9152\u3002 \u53f6\u96ea\u8fde\u773c\u89d2\u90fd\u6ca1\u6709\u770b\u4ed6\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u6700\u597d\u8d70\u8fdc\u70b9\uff0c\u8d8a\u8fdc\u8d8a\u597d\uff01 \u7ba1\u5bb6\u5a46\u5c45\u7136\u771f\u7684\u8d70\u4e86\uff0c\u8d70\u5f97\u5f88\u8fdc\u3002 \u53f6\u96ea\u9053\uff1a\u201c\u8c01\u6765\u966a\u6211\u559d\u9152\uff1f\u201d \u6d77\u5947\u9614\u62a2\u7740\u9053\uff1a\u201c\u6211\u3002\u201d \u53f6\u96ea\u9053\uff1a\u201c\u4f60\u4e0d\u914d\u3002\u201d \u8001\u5200\u628a\u5b50\u5ffd\u7136\u62cd\u4e86\u62cd\u9646\u5c0f\u51e4\uff0c\u9646\u5c0f\u51e4\u6162\u6162\u7684\u7ad9\u8d77\u6765\uff0c\u8d70\u8fc7\u53bb\u3002 \u53f6\u96ea\u7ec8\u4e8e\u770b\u4e86\u4ed6\u4e00\u773c\uff1a\u201c\u4f60\u5c31\u662f\u9646\u5c0f\u51e4\uff1f\u201d \u9646\u5c0f\u51e4\uff0c\u70b9\u70b9\u5934\u3002 \u53f6\u96ea\u9053\uff1a\u201c\u4f60\u80fd\u559d\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u80fd\u3002\u201d\u53f6\u96ea\u9053\uff1a\u201c\u597d\uff0c\u62ff\u7897\u6765\uff0c\u5927\u7897qo \u7897\u5f88\u5927\uff0c\u5979\u559d\u2014\u7897\uff0c\u9646\u5c0f\u51e4\u559d\u4e00\u7897\uff0c\u5979\u4e0d\u8bf4\u8bdd\uff0c\u9646\u5c0f\u51e4\u4e5f\u4e0d\u5f00\u53e3\uff0c\u5979\uff0c\u9646\u5c0f\u51e4\u4e5f\u6ca1 \u6709\u518d\u770b\u5979\u3002 \u4e24\u4e2a\u4eba\u5c31\u8fd9\u4e48\u6837\u9762\u5bf9\u9762\u7684\u5750\u5728\u5730\u4e0a\uff0c\u4f60\u4e00\u7897\uff0c\u6211\u4e00\u7897\u3002 \u4e00\u7897\u81f3\u5c11\u6709\u516b\u4e24\u3002 \u5341\u6765\u7897\u559d\u4e0b\u53bb\uff0c\u5979\u5c45\u7136\u8fd8\u662f\u9762\u4e0d\u6539\u8272\u3002 \u7b49\u5230\u9152\u575b\u9152\u559d\u5149\uff0c\u5979\u5c31\u7ad9\u8d77\u6765\uff0c\u5934\u4e5f\u4e0d\u56de\u7684\u8d70\u4e86\u51fa\u53bb\uff0c\u6ca1\u6709\u518d\u8bf4\u4e00\u53e5\u8bdd\uff0c\u4e00\u4e2a\u5b57\u3002 \u9646\u5c0f\u51e4\u7ad9\u8d77\u6765\u65f6\uff0c\u5934\u5df2\u6709\u4e9b\u6655\u4e86\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\uff0c\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u5230\u5979\u6709\u8fd9\u4e48\u597d\u7684\u9152\u91cf\uff0c\u5b9e\u5728\u60f3\u4e0d\u5230\u3002\u201d \u8001\u5200\u628a\u5b50\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u60f3\u4e0d\u5230\uff0c\u6211\u4ece\u6765\u6ca1\u6709\u770b\u89c1\u8fc7\u5979\u559d\u9152\u3002\u201d \u9646\u5c0f\u51e4\u5f88\u5403\u60ca\uff1a\u201c\u4f60\u4e5f\u6ca1\u6709\u89c1\u8fc7\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u65e0\u8bba\u8c01\u90fd\u6ca1\u6709\u89c1\u8fc7\uff0c\u8fd9\u662f\u5979\u5e73\u751f\u7b2c\u4e00\u6b21\u559d\u9152\u3002 \u5bf9\u4e00\u4e2a\u5df2\u559d\u5f97\u5934\u6655\u8111\u6da8\u7684\u4eba\u6765\u8bf4\uff0c\u4e16\u4e0a\u7edd\u6ca1\u6709\u4efb\u4f55\u4e8b\u80fd\u6bd4\u4e00\u5f20\u5e8a\u770b\u6765\u66f4\u52a8\u4eba\u4e86\uff0c\u4f55 \u51b5\u8fd9\u5f20\u5e8a\u672c\u5c31\u5f88\u5bbd\u5927\uff0c\u5f88\u8212\u670d\u3002 \u53ea\u53ef\u60dc\u6709\u4e2a\u4eba\u504f\u504f\u5c31\u662f\u4e0d\u80af\u8ba9\u4ed6\u8212\u8212\u670d\u670d\u7684\u8eba\u5230\u5e8a\u4e0a \u2014\u8fdb\u5c4b\u5b50\uff0c\u67f3\u9752\u9752\u5c31\u627e\u4e86\u575b\u9152\uff0c\u5750\u5230\u5730\u4e0a\uff0c\u9053\uff1a\u201c\u8c01\u6765\u966a\u6211\u559d\u9152\uff1f\u201d \u9646\u5c0f\u51e4\u524d\u770b\u770b\uff0c\u540e\u770b\u770b\uff0c\u5de6\u770b\u770b\uff0c\u53f3\u770b\u770b\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u5c4b\u5b50\u91cc\u597d\u50cf\u53ea\u6709\u6211\u4e00\u4e2a \u4eba\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u4f60\u80fd\u559d\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u80fd\u4e0d\u80fd\u4e0d\u559d\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u4e0d\u80fd\u3002\u201d \u9646\u5c0f\u51e4\u53ea\u6709\u5750\u4e0b\u53bb\u966a\u5979\u559d\uff0c\u4ed6\u5750\u4e0b\u53bb\u7684\u65f6\u5019\uff0c\u5c31\u5df2\u7ecf\u51c6\u5907\u9189\u4e86\u3002 \u4ed6\u771f\u7684\u9189\u4e86\u3002 \u7b49\u5230\u4ed6\u9192\u6765\u65f6\uff0c\u67f3\u9752\u9752\u5df2\u4e0d\u5728\u5c4b\u91cc\uff0c\u4ed6\u4e00\u4e2a\u4eba\u8eba\u5728\u5e8a\u4e0a\uff0c\u8fde\u9774\u5b50\u90fd\u6ca1\u6709\u8131\uff0c\u5934\u75bc\u5f97 \u5c31\u597d\u50cf\u968f\u65f6\u90fd\u53ef\u80fd\u4f1a\u88c2\u5f00\u3002 \u4ed6\u4e0d\u60f3\u8d77\u6765\uff0c\u4e5f\u8d77\u4e0d\u6765\uff0c\u53ef\u662f\u7a97\u5b50\u5916\u9762\u5374\u504f\u504f\u6709\u4eba\u5728\u53eb\u4ed6\u3002 \u7a97\u5b50\u662f\u5f00\u7740\u7684\uff0c\u4eba\u662f\u72ec\u5b64\u7f8e\uff1a\u201c\u6211\u5df2\u7ecf\u6765\u8fc7\u4e09\u6b21\u4e86\uff0c\u770b\u4f60\u7761\u5f97\u597d\u719f\uff0c\u4e5f\u4e0d\u6562\u5435\u9192 \u4f60\u3002\u201d \u201c\u4f60\u627e\u6211\u6709\u4e8b\uff1f\u201d \u201c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e8b\uff0c\u53ea\u4e0d\u8fc7\u597d\u4e45\u4e0d\u89c1\u4e86\uff0c\u60f3\u8ddf\u4f60\u804a\u804a\u3002\u201d \u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4ed6\u603b\u662f\u4e2a\u670b\u53cb\uff0c\u6709\u670b\u53cb\u6765\u627e\u9646\u5c0f\u51e4\u804a\u5929\uff0c\u4ed6\u5c31\u7b97\u5934\u771f\u7684\u5df2\u75bc\u5f97\u88c2\u5f00\uff0c \u4e5f\u4e0d\u4f1a\u62d2\u7edd\u7684\u3002 \u201c\u6211\u4eec\u6700\u597d\u51fa\u53bb\u804a\uff0c\u6211\u6cca\u770b\u89c1\u90a3\u4f4d\u82b1\u5be1\u5987\u3002\u201d \u5916\u9762\u8fd8\u662f\u6709\u96fe\uff0c\u51b7\u800c\u6f6e\u6e7f\u7684\u96fe\uff0c\u5bf9\u4e00\u4e2a\u5bbf\u9189\u672b\u9192\u7684\u4eba\u5374\u5f88\u6709\u76ca\u3002 \u72ec\u5b64\u7f8e\u4f24\u52bf\u867d\u7136\u597d\u5f97\u5f88\u5feb\uff0c\u770b\u6765\u5374\u597d\u50cf\u6709\u70b9\u5fc3\u4e8b\uff1a\u201c\u5176\u5b9e\u6211\u65e9\u5c31\u60f3\u6765\u627e\u4f60\uff0c\u53ea\u6015\u4f60 \u751f\u6211\u7684\u6c14\u3002\u201d \u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u751f\u6c14\uff1f\u201d\u2026\u56e0\u4e3a\u94a9\u5b50\u4ed6\u4eec\u662f\u6211\u4ecb\u7ecd\u7ed9\u4f60\u7684\uff0c\u6211\u771f\u7684\u4e0d\u77e5\u9053\u4ed6\u4eec\u4f1a\u5bb3 \u4f60\u3002\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\uff1a\u201c\u4f60\u5f53\u7136\u4e0d\u77e5\u9053\uff0c\u4f60\u662f\u6211\u7684\u670b\u53cb\uff0c\u4f60\u4e00\u76f4\u90fd\u5728\u5e2e\u6211\u7684\u5fd9\u3002\u201d \u72ec\u5b64\u7f8e\u8fdf\u7591\u7740\uff0c\u7ec8\u4e8e\u9f13\u8d77\u52c7\u6c14\uff0c\u9053\uff1a\u201c\u53ef\u662f\u6628\u5929\u665a\u4e0a\u6211\u53c8\u505a\u9519\u4e86\u4e00\u4ef6\u4e8b\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u72ec\u5b64\u7f8e\u9053\uff1a\u201c\u6628\u5929\u665a\u4e0a\u6211\u4e5f\u9189\u4e86\uff0c\u7cca\u91cc\u7cca\u6d82\u7684\u5c31\u628a\u79d8\u5bc6\u6cc4\u9732\u4e86\u51fa\u53bb\uff0c\u73b0\u5728\u4ed6\u4eec\u4e8c\u4e2a \u4eba\u90fd\u5df2\u77e5\u9053\u53f6\u5b64\u9e3f\u662f\u6b7b\u5728\u4f60\u624b\u4e0a\u7684\u3002\u201d \u4ed6\u4eec\u4e09\u4e2a\u4eba\uff0c\u5f53\u7136\u5c31\u662f\u8868\u54e5\u3001\u94a9\u5b50\u3001\u7ba1\u5bb6\u5a46\u3002 \u9646\u5c0f\u51e4\u7b11\u4e0d\u51fa\u4e86\u3002 \u867d\u7136\u53ea\u89c1\u9762\u2014\u6b21\uff0c\u4ed6\u5df2\u5f88\u4e86\u89e3\u53f6\u96ea\u8fd9\u4e2a\u4eba\uff0c\u4ed6\u5f53\u7136\u66f4\u4e86\u89e3\u53f6\u7075\u3002 \u201c\u636e\u8bf4\u8fd9\u91cc\u6700\u96be\u60f9\u7684\u5c31\u662f\u5979\u4eec\u59d0\u59b9\u4e24\u4e2a\uff0c\u5979\u4eec\u82e5\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u4e00\u5b9a\u4f1a\u6765\u627e\u4f60\u6324 \u547d\u3002\u2019\uff01\u72ec\u5b64\u7f8e\u8bf4\u5f97\u5f88\u5a49\u8f6c\uff1a\u201c\u4f60\u867d\u7136\u4e0d\u6015\uff0c\u53ef\u662f\u660e\u67aa\u6613\u8eb2\uff0c\u6697\u7bad\u96be\u9632\uff0c\u6240\u4ee5\u2026\u2026\u201d \u201c\u6240\u4ee5\u600e\u4e48\u6837\uff1f\u201d \u201c\u6240\u4ee5\u4f60\u6700\u597d\u60f3\u6cd5\u5b50\u5835\u4f4f\u4ed6\u4eec\u7684\u5634\u3002\u201d \u9646\u5c0f\u51e4\u53c8\u7b11\u4e86\uff0c\u4ed6\u5df2\u660e\u767d\u72ec\u5b64\u7f8e\u7684\u610f\u601d\uff1a\u201c\u4f60\u662f\u8981\u6211\u5bf9\u4ed6\u4eec\u53cb\u5584\u4e00\u70b9\uff0c\u4e0d\u8981\u8ddf\u4ed6\u4eec \u4f5c\u5bf9\uff0c\u5047\u5982\u4ed6\u4eec\u6709\u4e8b\u627e\u6211\uff0c\u6211\u6700\u597d\u4e5f\u4e0d\u8981\u62d2\u7edd\u3002 \u72ec\u5b64\u7f8e\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u7528\u529b\u63e1\u4e86\u63e1\u4ed6\u7684\u624b\uff0c\u9053\uff1a\u201c\u6211\u5bf9\u4e0d\u8d77\u4f60\u3002\u201d \u53ea\u8bf4\u4e86\u8fd9\u4e94\u4e2a\u5b87\uff0c\u4ed6\u5c31\u8d70\u4e86\uff0c\u770b\u7740\u4ed6\u4f5d\u507b\u7684\u80cc\u5f71\u6d88\u5931\uff0c\u9646\u5c0f\u51e4\u5b9e\u5728\u731c\u4e0d\u900f\u8fd9\u4e2a\u4eba\u7a76 \u7adf\u662f\u4ed6\u7684\u670b\u53cb\uff1f\u8fd8\u662f\u968f\u65f6\u90fd\u51c6\u5907\u51fa\u5356\u670b\u53cb\u7684\u4eba\u3002 \u73b0\u5728\u4ed6\u53ea\u80fd\u786e\u5b9a\u4e00\u4ef6\u4e8b\u94a9\u5b50\u4ed6\u597dI\u4e00\u5b9a\u5f88\u5feb\u5c31\u4f1a\u6709\u4e8b\u627e\u4ed6\u7684\u3002 \u4f1a\u662f\u4ef6\u4ec0\u4e48\u6837\u7684\u4e8b\uff1f\u4ed6\u8fde\u60f3\u90fd\u4e0d\u6562\u60f3\u4e5f\u6ca1\u7a7a\u53bb\u60f3\u4e86\uff0c\u56e0\u4e3a\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u5df2\u6709\u4e00\u9053\u5251 \u5149\u95ea\u7535\u822c\u5411\u4ed6\u523a\u4e86\u8fc7\u6765\u3002 \u8fd9\u65f6\u72ec\u5b64\u7f8e\u5df2\u8d70\u4e86\u5f88\u4e45\uff0c\u4ed6\u4e5f\u5df2\u8d70\u4e86\u4e00\u6bb5\u8def\uff0c\u5df2\u7ecf\u5feb\u8d70\u56de\u67f3\u9752\u9752\u4f50\u7684\u90a3\u680b\u5e73\u623f\u3002 \u5251\u5149\u5c31\u662f\u4ece\u5c4b\u6a90\u540e\u523a\u4e0b\u6765\u7684\uff0c\u4e0d\u4f46\u8fc5\u901f\uff0c\u800c\u4e14\u51c6\u786e\u3002 \u4e0d\u4f46\u51c6\u786e\uff0c\u800c\u4e14\u6bd2\u8fa3\u3002 \u4ed6\u60f3\u4e0d\u5230\u8fd9\u5730\u65b9\u8fd8\u6709\u4eba\u8981\u6697\u7b97\u4ed6\uff0c\u4ed6\u51e0\u4e4e\u5df2\u5b8c\u5168\u6ca1\u6709\u62db\u67b6\u95ea\u907f\u7684\u4f59\u5730\u3002 \u5e78\u597d\u4ed6\u662f\u9646\u5c0f\u51e4\uff0c\u5e78\u597d\u4ed6\u8fd8\u6709\u624b\u3002 \u4ed6\u7a81\u7136\u4f38\u51fa\u4e24\u6839\u624b\u6307\u6765\u4e00\u5939 \u4e16\u4e0a\u6709\u5343\u5343\u4e07\u4e07\u4e2a\u4eba\uff0c\u6bcf\u4e2a\u4eba\u90fd\u6709\u624b\uff0c\u6bcf\u53cc\u624b\u90fd\u6709\u624b\u6307\u3002 \u53ef\u662f\u4ed6\u8fd9\u4e24\u6839\u624b\u6307\uff0c\u5374\u65e0\u7591\u662f\u6700\u6709\u4ef7\u503c\u7684\uff0c\u56e0\u4e3a\u8fd9\u4e24\u6839\u624b\u6307\u5df2\u6551\u8fc7\u4ed6\u65e0\u6570\u6b21\u3002 \u8fd9\u4e00\u6b21\u4e5f\u4e0d\u4f8b\u5916\u3002 \u624b\u6307\u4e00\u5939\uff0c\u5251\u950b\u5df2\u5728\u624b\u6307\u95f4\u3002 \u51b0\u51b7\u7684\u5251\u950b\uff0c\u5f3a\u800c\u6709\u529b\uff0c\u5374\u6323\u4e0d\u8131\u4ed6\u8fd9\u4e24\u6839\u624b\u6307\uff0c\u4ed6\u62ac\u8d77\u5934\uff0c\u5c31\u770b\u89c1\u4e86\u4e00\u53cc\u51b7\u9177\u800c \u7f8e\u4e3d\u7684\u773c\u775b \u53f6\u96ea\u6b63\u5728\u770b\u7740\u4ed6\u3002 \u9646\u5c0f\u51e4\u5728\u5fc3\u91cc\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\u201c\u4f60\u5df2\u7ecf\u77e5\u9053\u4e86\uff1f\u201d \u53f6\u96ea\u53c8\u76ef\u7740\u4ed6\u770b\u4e86\u5f88\u4e45\uff0c\u624d\u6162\u6162\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u73b0\u5728\u6211\u624d\u77e5\u9053\uff0c\u9646\u5c0f\u51e4\u679c\u7136\u4e0d \u6127\u662f\u9646\u5c0f\u51e4\uff0c\u6211\u603b\u7b97\u6ca1\u6709\u627e\u9519\u4eba\u3002\u201d \u5979\u7684\u58f0\u97f3\u91cc\u5e76\u6ca1\u6709\u4ec7\u6068\uff0c\u9646\u5c0f\u51e4\u7acb\u523b\u8bd5\u63a2\u7740\u95ee\uff1a\u201c\u4f60\u662f\u6765\u627e\u6211\u7684\uff1f\u8fd8\u662f\u6765\u6740\u6211\u7684\uff1f\u201d \u53f6\u96ea\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u60f3\u6765\u770b\u770b\u4f60\u8fd9\u4e00\u62db\u95fb\u540d\u5929\u4e0b\u7684\u7edd\u6280\uff0c\u4f60\u82e5\u80fd\u63a5\u4f4f\u6211\u8fd9\u4e00\u5251\uff0c\u5c31 \u662f\u6211\u8981\u627e\u7684\u4eba\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u82e5\u6b7b\u5728\u4f60\u5251\u4e0b\u5462\uff1f\u201d \u53f6\u96ea\u9053\uff1a\u201c\u4f60\u6d3b\u8be5\u3002\u201d \u9646\u5c0f\u51e4\u53c8\u4e0d\u7981\u82e6\u7b11\u3002 \u4ed6\u65e2\u7136\u8fd8\u6ca1\u6709\u6b7b\uff0c\u5f53\u7136\u5fcd\u4e0d\u4f4f\u8981\u95ee\uff1a\u201c\u73b0\u5728\u6211\u5df2\u662f\u4f60\u8981\u627e\u7684\u4eba\uff1f\u201d \u53f6\u96ea\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u8ddf\u6211\u6765\u3002\u201d \u8d70\u5b8c\u66f2\u6298\u7684\u5c0f\u8def\uff0c\u7a7f\u8fc7\u5e7d\u79d8\u7684\u4e1b\u6797\uff0c\u518d\u8d70\u4e00\u6bb5\u5c71\u5761\uff0c\u5c31\u5446\u4ee5\u542c\u89c1\u6d41\u6c34\u58f0\u3002 \u6c34\u6d41\u5e76\u4e0d\u6025\uff0c\u5728\u8fd9\u65f6\u6c47\u96c6\u6210\u4e00\u4e2a\u5c0f\u6e56\uff0c\u56db\u9762\u5c71\u8272\u7fe0\u7eff\uff0c\u8fde\u96fe\u90fd\u6de1\u4e86\uff0c\u4e00\u4e2a\u4eba\u5982\u679c\u80fd \u9759\u9759\u7684\u5728\u6e56\u7554\u5750\u4e0a\u534a\u5929\uff0c\u4e00\u5b9a\u80fd\u5fd8\u8bb0\u5f88\u591a\u70e6\u607c\u3002 \u201c\u60f3\u4e0d\u5230\u5e7d\u7075\u5c71\u5e84\u91cc\uff0c\u4e5f\u6709\u8fd9\u4e48\u5b89\u9759\u7f8e\u4e3d\u7684\u5730\u65b9\u3002\u201d \u5b69\u5b50\u4eec\u901a\u5e38\u90fd\u6709\u4e2a\u5c5e\u4e8e\u4ed6\u4eec\u81ea\u5df1\u7684\u79d8\u5bc6\u5c0f\u5929\u5730\uff0c\u8fd9\u5730\u65b9\u663e\u7136\u662f\u5c5e\u4e8e\u53f6\u96ea\u7684\u3002 \u5979\u4e3a\u4ec0\u4e48\u5e26\u9646\u5c0f\u51e4\u6765\uff1f \u201c\u4f60\u7a76\u7adf\u8981\u6211\u505a\u4ec0\u4e48\uff1f\u201d\u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u95ee\u3002 \u53f6\u96ea\u7ad9\u5728\u6e56\u7554\uff0c\u773a\u671b\u7740\u8fdc\u5c71\uff0c\u8ba9\u4e00\u5934\u67d4\u53d1\u6cc9\u6c34\u822c\u62ab\u6563\u4e0b\u6765\u3002 \u5979\u7684\u58f0\u97f3\u4e5f\u50cf\u6cc9\u6c34\u822c\u8f7b\u67d4\u5e73\u6de1\uff0c\u53ef\u662f\u5979\u8bf4\u51fa\u6765\u7684\u8bdd\u5374\u8ba9\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u5979\u8bf4\uff1a\u201c\u6211\u8981 \u4f60\u505a\u6211\u7684\u4e08\u592b\u3002\u201d \u9646\u5c0f\u51e4\u53ea\u89c9\u5f97\u81ea\u5df1\u547c\u5438\u5df2\u5ffd\u7136\u505c\u987f\u3002 \u5979\u8f6c\u8fc7\u8eab\uff0c\u51dd\u89c6\u7740\u4ed6\uff0c\u773c\u6ce2\u6e05\u6f88\u800c\u660e\u4eae\uff0c\u5c31\u50cf\u662f\u6e56\u5fc3\u7684\u6c34\u6ce2\u4e00\u6837\u3001 \u7b2c\u4e5d\u7ae0\u3000\u7f8e\u4eba\u5782\u9752>> \u53e4\u9f99\u300a\u9646\u5c0f\u51e4\u7cfb\u5217\xb7\u5e7d\u7075\u5c71\u5e84\u300b \u7b2c\u4e5d\u7ae0\u3000\u7f8e\u4eba\u5782\u9752 \u5e7d\u79d8\u5b81\u9759\u7684\u7eff\u8272\u5c71\u8c37\uff0c\u5b8c\u7f8e\u65e0\u6687\u7684\u5904\u5973\u8eaf\u4f53\uff0c\u6e29\u67d4\u5982\u6c34\u6ce2\u7684\u773c\u6ce2\u2026\u2026 \u9646\u5c0f\u51e4\u5c3d\u529b\u63a7\u5236\u7740\u81ea\u5df1\uff0c\u4e0d\u8981\u518d\u53bb\u60f3\uff0c\u4f46\u662f\u4ed6\u81ea\u5df1\u5df2\u77e5\u9053\u8fd9\u4e9b\u56de\u5fc6\u5fc5\u5c06\u6c34\u7559\u4ed6\u5fc3 \u5e95\u3002 \u4ed6\u8d70\u5f97\u5f88\u5feb\uff0c\u8d70\u4e86\u5f88\u8fdc\uff0c\u672c\u8be5\u5df2\u8d70\u56de\u90a3\u6761\u5c0f\u8def\u4e86\uff0c\u53ef\u662f\u4ed6\u505c\u4e0b\u6765\u7684\u65f6\u5019\uff0c\u5374\u53d1\u73b0\u5165 \u5c71\u5df2\u66f4\u6df1\u3002 \u7136\u540e\u4ed6\u7acb\u523b\u53c8\u53d1\u73b0\u4e86\u4e00\u4ef6\u53ef\u6015\u7684\u4e8b\u4ed6\u53c8\u8ff7\u4e86\u8def\u3002 \u66f4\u53ef\u6015\u7684\u662f\uff0c\u56db\u9762\u7684\u96fe\u53c8\u6e10\u6d53\uff0c\u751a\u81f3\u6bd4\u5e7d\u7075\u5c71\u5e84\u90a3\u8fb9\u66f4\u6d53\uff0c\u65e0\u8bba\u773c\u529b\u591a\u597d\u7684\u4eba\uff0c\u90fd \u5f88\u96be\u770b\u5f97\u5230\u4e24\u4e08\u5916\u53bb\uff0c\u800c\u4e14\u65e0\u8bba\u4ece\u54ea\u4e2a\u65b9\u5411\u8d70\uff0c\u90fd\u53ef\u80fd\u79bb\u5c71\u5e84\u66f4\u8fdc\u3002 \u9646\u5c0f\u51e4\u5374\u8fd8\u662f\u8981\u8bd5\u8bd5\uff0c\u4ed6\u7edd\u4e0d\u662f\u90a3\u79cd\u80fd\u5750\u4e0b\u6765\u7b49\u4e91\u5f00\u96fe\u6563\u7684\u4eba\u3002 \u53c8\u8d70\u4e86\u5f88\u8fdc\uff0c\u8fd8\u662f\u627e\u4e0d\u5230\u8def\uff0c\u5728\u8fd9\u964c\u751f\u7684\u5c71\u6797\uff0c\u8981\u547d\u7684\u6d53\u96fe\u4e2d\uff0c\u8981\u8d70\u5230\u4ec0\u4e48\u65f6\u5019\u624d \u80fd\u8d70\u4e0a\u5f52\u9014\uff1f\u6b63\u5728\u4ed6\u5f00\u59cb\u89c9\u5f97\u9965\u997f\u75b2\u5026\uff0c\u5f00\u59cb\u62c5\u5fc3\u7684\u65f6\u5019\uff0c\u4ed6\u5ffd\u7136\u55c5\u5230\u4e86\u4e00\u80a1\u6551\u547d\u7684\u9999 \u6c14\u3002 \u9999\u6c14\u867d\u7136\u6781\u6de1\uff0c\u53ef\u662f\u4ed6\u7acb\u523b\u5c31\u80fd\u5206\u8fa8\u51fa\u6765\u90a3\u662f\u70e4\u91ce\u5154\u7684\u5473\u9053\u3002 \u8fdc\u5728\u7ae5\u5e74\u65f6\uff0c\u4ed6\u5c31\u5df2\u662f\u4e2a\u80fd\u5e72\u7684\u730e\u4eba\uff0c\u957f\u5927\u540e\u5bf9\u91ce\u5473\u7684\u5174\u8da3\u4e5f\u4e00\u76f4\u90fd\u5f88\u6d53\u539a\u3002 \u5154\u5b50\u7edd\u4e0d\u4f1a\u81ea\u5df1\u70e4\u81ea\u5df1\u7684\uff0c\u70e4\u5154\u5b50\u7684\u5730\u65b9\u5f53\u7136\u4e00\u5b9a\u6709\u4eba\uff0c\u9644\u8fd1\u552f\u4e00\u6709\u4eba\u4f50\u7684\u5730\u65b9\u5c31 \u662f\u5e7d\u7075\u5c71\u5e84\u3002 \u4ed6\u54bd\u4e0b\u53e3\u53e3\u6c34\uff0c\u867d\u7136\u89c9\u5f97\u66f4\u997f\uff0c\u5fc3\u795e\u5374\u632f\u594b\u4e86\u8d77\u6765\uff0c\u5b75\u4f4f\u547c\u5438\u7247\u523b\uff0c\u518d\u6df1\u6df1\u5438\u4e86\u53e3 \u6c14\uff0c\u7acb\u523b\u5c31\u5224\u65ad\u51fa\u9999\u6c14\u662f\u4ece\u504f\u897f\u65b9\u4f20\u6765\u7684\u3002 \u4ed6\u7684\u5224\u65ad\u663e\u7136\u6b63\u786e\uff0c\u56e0\u4e3a\u8d70\u51fa\u4e00\u6bb5\u8def\u540e\uff0c\u9999\u6c14\u5df2\u8d8a\u6765\u8d8a\u6d53\u3002 \u524d\u9762\u7684\u5c71\u52bf\u4eff\u4f5b\u66f4\u9669\uff0c\u5730\u52bf\u5374\u4eff\u4f5b\u5728\u4f4f\u4e0b\u9677\u843d\u3002\u70e4\u5154\u5b50\u7684\u9999\u6c14\u91cc\u4eff\u4f5b\u6df7\u5408\u4e86\u4e00\u79cd\u6cbc \u6cfd\u4e2d\u72ec\u6709\u7684\u8150\u673d\u6076\u81ed\u3002 \u5c31\u7b97\u8fd9\u91cc\u6709\u4eba\uff0c\u8fd9\u5730\u65b9\u4e5f\u7edd\u4e0d\u662f\u5e7d\u7075\u5c71\u5e84\u3002 \u9646\u5c0f\u51e4\u7684\u5fc3\u53c8\u6c89\u4e86\u4e0b\u53bb\uff0c\u662f\u4ec0\u4e48\u6837\u7684\u4eba\u4f1a\u4f4f\u5728\u8fd9\u79cd\u5730\u65b9\uff1f\u4ed6\u7b80\u76f4\u65e0\u6cd5\u60f3\u50cf\u3002 \u5c31\u5728\u8fd9\u65f6\uff0c\u524d\u9762\u5ffd\u7136\u54cd\u8d77\u4e00\u79cd\u602a\u5f02\u7684\u58f0\u97f3\uff0c\u4ed6\u52a0\u7d27\u811a\u6b65\u8d76\u8fc7\u53bb\uff0c\u5c31\u770b\u89c1\u6d53\u96fe\u4e2d\u51fa\u73b0 \u4e86\u4e00\u6761\u6761\u602a\u5f02\u7684\u5f71\u5b50\u3002 \u4ed6\u770b\u5f97\u51fa\u90a3\u7edd\u4e0d\u662f\u4eba\u7684\u5f71\u5b50\uff0c\u5374\u53c8\u504f\u504f\u4e0d\u50cf\u662f\u91ce\u517d\uff0c\u4ed6\u751a\u81f3\u65e0\u6cd5\u5f62\u5bb9\u8fd9\u5f71\u5b50\u7684\u5f62 \u72b6\u3002 \u53ef\u662f\u4ed6\u4e00\u770b\u89c1\u8fd9\u5f71\u5b50\uff0c\u5fc3\u91cc\u7acb\u523b\u89c9\u5f97\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u6050\u60e7\u548c\u6076\u5fc3\uff0c\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u5455 \u5410\u3002 \u5bf9\u9762\u7684\u5f71\u5b50\u4f3c\u4e4e\u4e5f\u5728\u4e0d\u5b89\u7684\u626d\u52a8\u7740\uff0c\u7b49\u5230\u9646\u5c0f\u51e4\u9f13\u8d77\u52c7\u6c14\u51b2\u8fc7\u53bb\u65f6\uff0c\u8fd9\u5f71\u5b50\u53c8\u5ffd\u7136 \u6d88\u5931\uff0c\u5f7b\u5e95\u6d88\u5931\uff0c\u5c31\u597d\u50cf\u4ece\u6765\u90fd\u6ca1\u6709\u51fa\u73b0\u8fc7\u3002 \u9646\u5c0f\u51e4\u7adf\u5fcd\u4e0d\u4f4f\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u7ad9\u5728\u90a3\u91cc\u6014\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u611f\u89c9\u5230\u98ce\u4e2d\u8fd8\u6709\u79cd\u70e7 \u7126\u4e86\u6728\u70ad\u7684\u5473\u9053\u3002 \u8fd9\u91cc\u4e00\u5b9a\u5c31\u662f\u70e4\u5154\u5b50\u7684\u5730\u65b9\uff01 \u4ed6\u76f8\u4fe1\u81ea\u5df1\u7684\u5224\u65ad\u4e00\u5b9a\u6b63\u786e\u65e0\u8bef\uff0c\u53ef\u662f\u9644\u8fd1\u504f\u504f\u53c8\u6ca1\u6709\u4e00\u70b9\u75d5\u8ff9\u7559\u4e0b\u3002 \u5982\u679c\u662f\u522b\u4eba\uff0c\u4e00\u5b9a\u65e9\u5df2\u8d70\u8fc7\u53bb\uff0c\u751a\u81f3\u5df2\u9003\u8d70\u3002 \u4f46\u662f\u4ed6\u7edd\u4e0d\u653e\u5f03\u3002 \u4ed6\u5148\u5c06\u8fd9\u5730\u65b9\u5341\u4e08\u65b9\u5706\u7528\u4e00\u6839\u770b\u4e0d\u89c1\u7684\u7ef3\u5b50\u56f4\u4f4f\uff0c\u7136\u540e\u5c31\u5c55\u5f00\u5730\u6bef\u5f0f\u7684\u641c\u7d22\u3002 \u5730\u4e0a\u7684\u6ce5\u571f\u843d\u53f6\u8005\u6b63\u662f\u63a5\u8fd1\u6cbc\u6cfd\u5730\u533a\u7684\u5f81\u8c61\u3002 \u53ea\u6709\u4e00\u5757\u5730\u7279\u522b\u5e72\u71e5\uff0c\u4e0a\u9762\u7684\u843d\u53f6\u663e\u7136\u662f\u521a\u79fb\u8fc7\u6765\u7684\u3002 \u4ed6\u4f0f\u4e0b\u8eab\uff0c\u6252\u5f00\u843d\u53f6\uff0c\u50cf\u730e\u72ac\u822c\u7528\u9f3b\u5b50\u53bb\u55c5\u6ce5\u571f\uff0c\u751a\u81f3\u8fd8\u64ae\u8d77\u4e00\u70b9\u6ce5\u571f\u6765\u5c1d\u4e86\u5c1d\u3002 \u6ce5\u4e2d\u679c\u7136\u6709\u70e7\u70ad\u7684\u5473\u9053\uff0c\u4eff\u4f5b\u8fd8\u6df7\u5408\u7740\u91ce\u5154\u8eab\u4e0a\u7684\u6cb9\u8102\u3002 \u4ed6\u518d\u5f80\u4e0b\u6316\u6398\uff0c\u5c31\u627e\u5230\u4e86\u4e00\u4e9b\u67af\u679d\uff0c\u51e0\u6839\u5543\u8fc7\u7684\u788e\u9aa8\u5934\uff0c\u4e00\u6839\u7528\u6811\u679d\u505a\u6210\u7684\u70e4\u53c9\uff0c \u53c9\u4e0a\u8fd8\u5e26\u7740\u5757\u5403\u5269\u4e0b\u7684\u5154\u8089\uff0c\u76ae\u6bdb\u524a\u5f97\u5f88\u5e72\u51c0\u3002 \u53ea\u6709\u4eba\u7684\u624b\uff0c\u624d\u80fd\u505a\u5f97\u51fa\u8fd9\u79cd\u70e4\u53c9\uff0c\u53ea\u6709\u4eba\u7684\u7259\u9f7f\uff0c\u624d\u4f1a\u5c06\u9aa8\u5934\u5543\u5f97\u8fd9\u4e48\u5e72\u51c0\uff0c\u800c \u4e14\u4e5f\u53ea\u6709\u4eba\u662f\u719f\u98df\u7684\u52a8\u7269\u3002 \u8fd9\u5730\u65b9\u4e00\u5b9a\u6709\u4eba\u3002 \u8fd9\u4e2a\u4eba\u4e0d\u4f46\u6709\u4e00\u53cc\u5f88\u7075\u654f\u7684\u624b\uff0c\u800c\u4e14\u505a\u4e8b\u6781\u4ed4\u7ec6\uff0c\u82e5\u4e0d\u662f\u9646\u5c0f\u51e4\uff0c\u4efb\u4f55\u4eba\u90fd\u5f88\u96be\u627e \u5f97\u51fa\u4e00\u70b9\u4ed6\u66fe\u7ecf\u5728\u8fd9\u91cc\u70e4\u8fc7\u7684\u75d5\u8ff9\u3002 \u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u4e3a\u4ec0\u4e48\u4f1a\u5230\u8fd9\u91cc\u6765\uff1f\u662f\u4e0d\u662f\u4e5f\u5728\u9003\u907f\u522b\u4eba\u7684\u8ffd\u8e2a\uff1f \u521a\u624d\u90a3\u6781\u66f2\u800c\u602a\u5f02\u7684\u5f71\u5b50\u53c8\u662f\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff1f \u9646\u5c0f\u51e4\u5b8c\u5168\u60f3\u4e0d\u901a\uff0c\u5c31\u56e0\u4e3a\u60f3\u4e0d\u901a\uff0c\u6240\u4ee5\u66f4\u597d\u5947\u3002 \u73b0\u5728\u5bf9\u4ed6\u8bf4\u6765\uff0c\u80fd\u4e0d\u80fd\u627e\u5230\u5f52\u8def\u5df2\u53d8\u6210\u4e0d\u592a\u91cd\u8981\u4e86\uff0c\u56e0\u4e3a\u4ed6\u5df2\u51b3\u5fc3\u8981\u627e\u51fa\u8fd9\u4e9b\u95ee\u9898 \u7684\u7b54\u6848\u3002 \u7b54\u6848\u2014\u5b9a\u5c31\u5728\u8fd9\u9644\u8fd1\uff0c\u53ef\u662f\u9644\u8fd1\u504f\u504f\u53c8\u6ca1\u6709\u4efb\u4f55\u8db3\u8ff9\u3002 \u9646\u5c0f\u51e4\u5750\u4e0b\u6765\uff0c\u5148\u5c06\u90a3\u5757\u5154\u8089\u4e0a\u7684\u6ce5\u571f\u64e6\u5e72\u51c0\uff0c\u518d\u6495\u6210\u4e00\u6761\u6761\u7684\uff0c\u6162\u6162\u5480\u56bc\u3002 \u6ca1\u6709\u76d0\uff0c\u5df2\u7ecf\u88ab\u70e7\u7126\uff0c\u53c8\u88ab\u57cb\u5728\u58eb\u91cc\u7684\u5154\u8089\uff0c\u5403\u8d77\u6765\u4e0d\u4f46\u6de1\u800c\u65e0\u5473\uff0c\u7b80\u76f4\u65e0\u6cd5\u4e0b \u54bd\u3002 \u53ef\u662f\u4ed6\u52c9\u5f3a\u81ea\u5df1\u5168\u90fd\u5403\u4e0b\u53bb\u3002 \u65e0\u8bba\u8981\u505a\u4ec0\u4e48\u4e8b\uff0c\u90fd\u5f97\u8981\u6709\u4f53\u529b\uff0c\u9965\u997f\u5374\u662f\u5b83\u7684\u81f4\u547d\u4f24\u3002 \u809a\u5b50\u91cc\u6709\u4e86\u4e1c\u897f\u540e\uff0c\u679c\u7136\u5c31\u8212\u670d\u4e9b\u4e86\uff0c\u4ed6\u8eba\u4e0b\u6765\uff0c\u51c6\u5907\u5728\u8fd9\u67d4\u8f6f\u7684\u843d\u53f6\u4e0a\u5c0f\u6101\u7247\u523b \u518d\u5f00\u59cb\u641c\u7d22\uff0c\u4ed6\u5f53\u7136\u7edd\u5bf9\u60f3\u4e0d\u5230\uff0c\u8fd9\u4e00\u8eba\u4e0b\u53bb\uff0c\u5c31\u51e0\u4e4e\u6c38\u8fdc\u7ad9\u4e0d\u8d77\u6765\u3002 \u70df\u4e00\u822c\u7684\u6d53\u96fe\u5728\u6728\u65f6\u95f4\u6d6e\u52a8\uff0c\u9646\u5c0f\u51e4\u521a\u8eba\u4e0b\u53bb\uff0c\u7acb\u523b\u5c31\u89c9\u5f97\u8fd9\u4e9b\u70df\u96fe\u9065\u8fdc\u5f97\u5c31\u50cf\u662f \u5929\u4e0a\u7684\u6d6e\u4e91\uff0c\u6240\u6709\u7684\u4e00\u5207\u4e5f\u90fd\u8ddd\u79bb\u4ed6\u8d8a\u6765\u8d8a\u8fdc\u3002 \u4ed6\u6574\u4e2a\u4eba\u5c31\u50cf\u662f\u5ffd\u7136\u6c89\u5165\u4e86\u4e00\u4e2a\u53c8\u8f6f\u53c8\u751c\u871c\u7684\u65e0\u5e95\u6df1\u6d1e\u91cc\uff0c\u4e16\u754c\u4e0a\u6bcf\u4ef6\u4e8b\u90fd\u4eff\u4f5b\u53d8 \u5f97\u9065\u8fdc\u4e86\uff0c\u53d8\u5f97\u7f8e\u4e3d\u4e86\uff0c\u6700\u91cd\u8981\u7684\u4e8b\u4e5f\u53d8\u5f97\u65e0\u8db3\u8f7b\u91cd\uff0c\u6240\u6709\u7684\u75db\u82e6\u90fd\u5df2\u5f97\u5230\u89e3\u8131\u3002 \u8fd9\u79cd\u8f7b\u677e\u800c\u751c\u7f8e\u7684\u611f\u89c9\uff0c\u6b63\u662f\u6bcf\u4e2a\u4eba\u90fd\u5728\u5bfb\u6c42\u7684\uff0c\u53ef\u662f\u9646\u5c0f\u51e4\u5374\u89c9\u5f97\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684 \u6050\u60e7\u3002 \u4ed6\u77e5\u9053\u81ea\u5df1\u7edd\u4e0d\u4f1a\u6709\u8fd9\u79cd\u611f\u89c9\uff0c\u4e5f\u4e0d\u8be5\u6709\uff0c\u4ed6\u8eab\u8d1f\u91cd\u635f\uff0c\u4ed6\u7684\u628a\u5b50\u7edd\u4e0d\u80fd\u5728\u8fd9\u65f6\u653e \u4e0b\u3002 \u66f4\u5927\u7684\u6050\u60e7\u662f\uff0c\u4ed6\u518d\u60f3\u7ad9\u8d77\u6765\u7684\u65f6\u5019\uff0c\u5c31\u53d1\u73b0\u81ea\u5df1\u5168\u8eab\u7684\u808c\u8089\u9aa8\u8282\u90fd\u5df2\u677e\u6563\u8131\u529b\u3002 \u5c31\u5728\u8fd9\u65f6\uff0c\u4ed6\u53c8\u770b\u89c1\u90a3\u602a\u5f02\u7684\u5f71\u5b50\u3002 \u6254\u66f2\u7740\u5f71\u5b50\uff0c\u5728\u6d53\u96fe\u4e2d\u770b\u6765\u5c31\u50cf\u662f\u88ab\u987d\u76ae\u5b69\u5b50\u62e7\u574f\u4e86\u7684\u5e03\u5a03\u5a03\uff0c\u5374\u7edd\u4e0d\u50cf\u4eba\u3002 \u56e0\u4e3a\u201c\u4ed6\u201d\u5168\u8eab\u90fd\u662f\u8f6f\u7684\uff0c\u6bcf\u4e2a\u5730\u65b9\u90fd\u53ef\u4ee5\u968f\u610f\u6781\u66f2\u3002 \u4eba\u6709\u9aa8\u5934\uff0c\u6709\u5173\u8282\u3002 \u4eba\u7edd\u4e0d\u662f\u8fd9\u6837\u5b50\u7684\uff0c\u7edd\u4e0d\u662f\u300f \u9646\u5c0f\u51e4\u6b63\u60f3\u628a\u6269\u6563\u4e86\u7684\u77b3\u5b54\u96c6\u4e2d\u6ce8\u610f\uff0c\u770b\u5f97\u66f4\u6e05\u695a\u4e9b\uff0c\u5c31\u542c\u89c1\u4e86\u5f71\u5b50\u5728\u8bf4\u8bdd\u3002 \u201c\u4f60\u662f\u9646\u5c0f\u51e4\uff1f\u201d \u58f0\u97f3\u602a\u5f02\u3001\u8270\u6da9\u800c\u8fdf\u949d\uff0c\u4f46\u5374\u7edd\u5bf9\u662f\u4eba\u7684\u58f0\u97f3\u3002 \u8fd9\u5f71\u5b50\u4e0d\u4f46\u662f\u4eba\uff0c\u800c\u4e14\u8fd8\u662f\u4e2a\u8ba4\u5f97\u9646\u5c0f\u51e4\u7684\u4eba\uff01 \u5e78\u597d\u8fd9\u65f6\u9646\u5c0f\u51e4\u7684\u89c2\u5ff5\u4e2d\uff0c\u5df2\u5b8c\u5168\u6ca1\u6709\u60ca\u5947\u548c\u6050\u60e7\u5b58\u5728\uff0c\u5426\u531dC\u4ed6\u8bf4\u4e0d\u5b9a\u4f1a\u5413\u5f97\u53d1 \u75af\u3002 \u5f71\u5b50\u5c45\u7136\u8fd8\u5728\u7b11\uff0c\u5403\u5403\u7684\u7b11\u7740\u9053\uff1a\u201c\u636e\u8bf4\u9646\u5c0f\u51e4\u662f\u4ece\u6765\u4e0d\u4f1a\u4e2d\u6bd2\u7684\uff0c\u73b0\u5728\u600e\u4e48\u4e5f\u4e2d \u4e86\u6bd2\uff1f\u201d \u8fd9\u4e00\u70b9\u9646\u5c0f\u51e4\u672c\u5c31\u60f3\u4e0d\u901a\u3002 \u996e\u98df\u4e2d\u53ea\u6709\u4e00\u70b9\u6bd2\uff0c\u65e0\u8bba\u662f\u54ea\u79cd\uff0c\u4ed6\u90fd\u80fd\u7acb\u523b\u8b66\u89c9\u3002 \u5f71\u5b50\u53c8\u7b11\u9053\uff1a\u201c\u544a\u8bc9\u4f60\uff0c\u8fd9\u662f\u5927\u9ebb\u7684\u53f6\u5b50\uff0c\u6211\u559c\u6b22\u7528\u5b83\u6765\u70e4\u8089\u5403\uff0c\u6211\u5403\u4e86\u5c31\u4f1a\u89c9\u5f97 \u50cf\u795e\u4ed9\u822c\u5feb\u6d3b\uff0c\u4f60\u5403\u4e86\u5374\u4f1a\u53d8\u5f97\u50cf\u6761\u6b7b\u72d7ao \u4ed6\u53c8\u89e3\u91ca\uff1a\u201c\u521a\u624d\u4f60\u55c5\u5230\u70e4\u8089\u9999\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u628a\u5b83\u7684\u6bd2\u5438\u8fdb\u53bb\u4e00\u70b9\uff0c\u6240\u4ee5\u7b49\u5230\u4f60\u518d \u5403\u90a3\u5757\u8089\u65f6\uff0c\u5c31\u7edd\u4e0d\u4f1a\u518d\u6709\u8b66\u89c9\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u662f\u6545\u610f\u5f15\u6211\u6765\u7684\uff1f\u201d \u5f71\u5b50\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u90a3\u5757\u8089\u5374\u662f\u6211\u6545\u610f\u7559\u4e0b\u6765\u7684\uff0c\u5426\u5219\u5c31\u7b97\u662f\u4e00\u5339\u9a6c\u6211\u4e5f\u80fd\u5403\u4e0b \u53bb\u3002\u201d \u4ed6\u597d\u50cf\u5bf9\u81ea\u5df1\u8fd9\u53e5\u8bdd\u89c9\u5f97\u5f88\u6b23\u8d4f\u53ea\u6709\u5b64\u72ec\u5df2\u4e45\u7684\u4eba\u624d\u4f1a\u6709\u5583\u5583\u81ea\u8bed\u7684\u4e60\u60ef\uff0c\u53ea\u6709\u8fd9 \u79cd\u4eba\u624d\u4f1a\u6b23\u8d4f\u81ea\u5df1\u8bf4\u7684\u8bdd\u3002 \u4ed6\u5403\u5403\u7684\u7b11\u4e86\u534a\u5929\uff0c\u624d\u63a5\u7740\u9053\uff1a\u201c\u4f60\u82e5\u627e\u4e0d\u5230\u90a3\u5757\u8089\uff0c\u6211\u4e5f\u8bb8\u4f1a\u653e\u4f60\u8d70\u7684\uff0c\u4e0d\u5e78\u4f60 \u6211\u5230\u5382\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e0d\u5e78\uff1f\u201d\u5f71\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e0d\u80fd\u8ba9\u4efb\u4f55\u4eba\u77e5\u9053\u6211\u5728\u8fd9\u91cc\u3002\u201d \u4ed6\u5ffd\u7136\u7528\u4e00\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u602a\u5f02\u8eab\u6cd5\u8df3\u8fc7\u6765\uff0c\u843d\u5230\u9646\u5c0f\u51e4\u8eab\u65c1\uff0c\u70b9\u4e86\u9646\u5c0f\u51e4\u7684\u51e0\u5904\u7a74 \u9053\u3002 \u4ed6\u7684\u624b\u770b\u6765\u5c31\u50cf\u662f\u4e00\u53ea\u8150\u70c2\u4e86\u7684\u86c7\u76ae\u624b\u5957\uff0c\u4f46\u662f\u4ed6\u7684\u51fa\u624b\u5374\u7edd\u5bf9\u51c6\u786e\u800c\u6709\u6548\u3002 \u6bd4\u8d77\u4ed6\u8eab\u4e0a\u522b\u7684\u90e8\u5206\u6765\uff0c\u8fd9\u53ea\u624b\u8fd8\u7b97\u662f\u6bd4\u8f83\u5bb9\u6613\u5fcd\u53d7\u7684\u3002 \u6ca1\u6709\u4eba\u80fd\u5f62\u5bb9\u4ed6\u7684\u6a21\u6837\uff0c\u4e0d\u80fd\uff0c\u4e0d\u6562\uff0c\u4e5f\u4e0d\u5fcd\u5f62\u5bb9\u3002 \u9646\u5c0f\u51e4\u7684\u5fc3\u795e\u867d\u7136\u5b8c\u5168\u5904\u4e8e\u4e00\u79cd\u865a\u65e0\u8ff7\u5e7b\u7684\u60c5\u51b5\u4e2d\uff0c\u53ef\u662f\u770b\u89c1\u4e86\u4ed6\u8fd9\u4e2a\u4eba\uff0c\u8fd8\u662f\u5fcd \u4e0d\u4f4f\u8981\u6218\u6817\u5455\u5410\u3002 \u5f71\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u770b\u89c1\u6211\u4e86\uff0c\u4f60\u662f\u4e2d\u662f\u89c9\u5f97\u6211\u5f88\u4e11\uff1f\u201d \u9646\u5c0f\u51e4\u4e0d\u80fd\u5426\u8ba4\u3002 \u5f71\u5b50\u9053\uff1a\u201c\u4f60\u82e5\u88ab\u4eba\u4ece\u51e0\u767e\u4e08\u9ad8\u7684\u5c71\u5d16\u4e0a\u63a8\u535c\u6765\uff0c\u53c8\u5728\u70c2\u6ce5\u91cc\u6ce1\u4e86\u51e0\u5341\u5929\uff0c\u4f60\u4e5f\u4f1a \u53d8\u6210\u8fd9\u6837\u5b50\u7684\u3002\u201d \u4ed6\u7b11\u7684\u58f0\u97f3\u6bd4\u54ed\u8fd8\u60b2\u54c0\uff1a\u201c\u6211\u4ee5\u524d\u975e\u4f46\u4e0d\u6bd4\u4f60\u4e11\uff0c\u800c\u4e14\u8fd8\u662f\u4e2a\u7f8e\u7537\u5b50\u3002 \u9646\u5c0f\u51e4\u5e76\u6ca1\u6709\u6ce8\u610f\u4ed6\u540e\u9762\u8fd9\u53e5\u8bdd\uff0c\u53ea\u95ee\uff1a\u201c\u4f60\u88ab\u4eba\u4ece\u9ad8\u5d16\u4e0a\u63a8\u4e0b\u6765\uff0c\u53c8\u5728\u70c2\u6ce5\u91cc\u6ce1 \u4e86\u51e0\u5341\u5929\uff0c\u53ef\u662f\u4f60\u8fd8\u6ca1\u6709\u6b7b\u3002\u201d \u5f71\u5b50\u60e8\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u6211\u600e\u4e48\u6d3b\u4e0b\u6765\u7684\uff0c\u5c31\u597d\u50cf\u662f\u8001\u5929\u5728\u5e2e\u6211\u7684\u5fd9\uff0c\u53ef\u662f\u8001\u5929 \u53c8\u597d\u50cf\u662f\u5728\u6545\u610f\u8981\u6211\u53d7\u6298\u78e8\u3002\u201d \u8fd9\u4e2a\u4eba\u80fd\u6d3b\u5230\u73b0\u5728\uff0c\u7684\u786e\u662f\u5947\u8ff9\uff0c\u8fd9\u5947\u8ff9\u5374\u53ea\u4e0d\u8fc7\u662f\u4e9b\u70c2\u6811\u53f6\u9020\u6210\u7684\u3002 \u6cbc\u6cfd\u4e2d\u8150\u70c2\u7684\u6811\u53f6\u751f\u51fa\u79cd\u5947\u5f02\u7684\u9709\u83cc\uff0c\u5c31\u597d\u50cf\u5947\u8ff9\u822c\u80fd\u6cbb\u7597\u4eba\u4eec\u7684\u6e83\u70c2\u4f24\u75db\u3002 \u5f71\u5b50\u9053\uff1a\u201c\u6211\u5c31\u9760\u70c2\u6ce5\u4e2d\u4e00\u4e9b\u8fd8\u6ca1\u6709\u5b8c\u5168\u8150\u70c2\u7684\u4e1c\u897f\u586b\u809a\u5b50\uff0c\u8fc7\u4e86\u51e0\u5341\u5929\u4e4b\u540e\u624d\u80fd \u722c\u51fa\u6765\uff0c\u4ee5\u540e\u6211\u624d\u53d1\u89c9\uff0c\u90a3\u4e9b\u70c2\u6ce5\u597d\u50cf\u5bf9\u6211\u7684\u4f24\u5f88\u6709\u7528\uff0c\u6240\u4ee5\u6bcf\u5230\u6211\u7684\u4f24\u53c8\u5f00\u59cb\u8981\u6d41\u8113 \u7684\u65f6\u5019\uff0c\u6211\u5c31\u5230\u70c2\u6ce5\u91cc\u53bb\u6ce1\u4e00\u6ce1\uff0c\u8fd9\u4e48\u591a\u5e74\u6765\uff0c\u5c45\u7136\u6210\u4e86\u4e60\u60efao \u9646\u5c0f\u51e4\u7ec8\u4e8e\u660e\u767d\uff0c\u8fd9\u4e2a\u4eba\u7684\u8eab\u5b50\u4e3a\u4ec0\u4e48\u80fd\u50cf\u86c7\u4e00\u6837\u80fd\u968f\u610f\u8815\u52a8\u626d\u66f2\u3002 \u5f71\u5b50\u9053\uff1a\u201c\u53ef\u662f\u8fd9\u79cd\u7f6a\u5b9e\u5728\u4e0d\u662f\u4eba\u53d7\u7684\uff0c\u5e78\u597d\u540e\u6765\u6211\u53c8\u5728\u65e0\u610f\u4e2d\u53d1\u73b0\uff0c\u5927\u9ebb\u7684\u53f6\u5b50 \u53ef\u4ee5\u8ba9\u6211\u5fd8\u8bb0\u5f88\u591a\u75db\u82e6\uff0c\u6240\u4ee5\u76f4\u5230\u73b0\u5728\u6211\u8fd8\u6d3b\u7740\u3002\u201d \u751f\u547d\u7684\u5947\u5999\u97e7\u529b\uff0c\u4e07\u7269\u7684\u5947\u5999\u914d\u5408\uff0c\u53c8\u5c82\u662f\u4eba\u7c7b\u6240\u80fd\u60f3\u50cf\u3002 \u9646\u5c0f\u51e4\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u773c\u524d\u7684\u4e8b\u7269\u5df2\u6e10\u6e10\u56de\u590d\u4e86\u539f\u5f62\u3002 \u4ed6\u4e00\u76f4\u5728\u96c6\u4e2d\u81ea\u5df1\u7684\u610f\u5fd7\uff0c\u53ea\u53ef\u60dc\u73b0\u5728\u836f\u529b\u867d\u5df2\u9010\u6e10\u6d88\u5931\uff0c\u7a74\u9053\u5374\u53c8\u88ab\u5236\u4f4f\u3002 \u4ed6\u5ffd\u7136\u95ee\uff1a\u201c\u4f60\u77e5\u9053\u6211\u53eb\u9646\u5c0f\u51e4\uff0c\u4f60\u8ba4\u5f97\u6211\uff1f\u201d \u5f71\u5b50\u9053\uff1a\u201c\u4e0d\u8ba4\u5f97\uff0c\u53ef\u662f\u6211\u89c1\u8fc7\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u51e0\u65f6\u89c1\u8fc7\u7684\uff1f\u201d \u5f71\u5b50\u9053\uff1a\u201c\u521a\u624d\u3002\u201d \u9646\u5c0f\u51e4\u52a8\u5bb9\u9053\uff1a\u201c\u4f60\u521a\u624d\u89c1\u8fc7\u6211\uff1f\u201d \u5f71\u5b50\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4e86\u6211\u7684\u79d8\u5bc6\uff0c\u6211\u672c\u8be5\u6740\u4e86\u4f60\u706d\u53e3\uff0c\u5c31\u56e0\u4e3a\u6211\u521a\u624d\u89c1\u8fc7\u4f60\uff0c\u6240\u4ee5\u4f60 \u8fd8\u6d3b\u7740\u3002\u201d \u9646\u5c0f\u51e4\u66f4\u4e0d\u61c2\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u5f71\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u603b\u7b97\u8fd8\u4e0d\u662f\u4e2a\u574f\u4eba\uff0c\u5e76\u6ca1\u6709\u4e58\u673a\u6b3a\u8d1f\u963f\u96ea \u4ed6\u7684\u58f0\u97f3\u91cc\u5ffd\u7136\u5145\u6ee1\u611f\u60c5\uff1a\u201c\u963f\u96ea\u4e00\u76f4\u662f\u4e2a\u4e56\u5b69\u5b50\uff0c\u6211\u4e0d\u8981\u5979\u88ab\u4eba\u6b3a\u8d1f\u3002\u201d \u9646\u5c0f\u51e4\u5403\u60ca\u7684\u770b\u770b\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u662f\u5979\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u5f71\u5b50\u4e0d\u80af\u56de\u7b54\u8fd9\u53e5\u8bdd\uff0c\u5374\u53cd\u95ee\u9053\uff1a\u201c\u897f\u95e8\u5439\u96ea\u4e3a\u4ec0\u4e48\u8981\u6740\u4f60\uff1f\u4f60\u8ddf\u4ed6\u6709\u4ec0\u4e48\u4ec7\uff1f\u201d \u9646\u5c0f\u51e4\u8fdf\u7591\u7740\uff0c\u7ec8\u4e8e\u51b3\u5b9a\u8bf4\u5b9e\u8bdd\uff1a\u201c\u4ed6\u770b\u89c1\u6211\u8ddf\u4ed6\u8001\u5a46\u7761\u5728\u4e00\u5f20\u5e8a\u4e0a\u3002 \u5f71\u5b50\u95ed\u4e0a\u5634\uff0c\u76ef\u7740\u4ed6\u770b\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u53d1\u51fa\u4e86\u5947\u602a\u7684\u7b11\u58f0\uff0c\u9053\uff1a\u201c\u73b0\u5728\u6211\u603b\u7b97\u660e\u767d\u4f60 \u662f\u4e3a\u4ec0\u4e48\u5230\u5e7d\u7075\u5c71\u5e84\u6765\u7684\u4e86qo \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u662f\u4e3a\u4e86\u907f\u7978\u6765\u7684aU \u5f71\u5b50\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8fde\u4f60\u90fd\u4e0d\u60f3\u6b7b\uff0c\u6211\u5f53\u7136\u66f4\u4e0d\u60f3\u6b7b\u3002\u201d \u5f71\u5b50\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u6015\u6b7b\uff0c\u4f60\u5230\u8fd9\u91cc\u6765\uff0c\u53ea\u4e0d\u8fc7\u4e3a\u4e86\u8981\u53d1\u6398\u51fa\u8fd9\u5730\u65b9\u7684\u79d8\u5bc6\u3002\u201d \u4ed6\u8bf4\u5f97\u5f88\u6709\u628a\u63e1\uff1a\u201c\u8fde\u963f\u96ea\u90a3\u6837\u7684\u5973\u4eba\u4f60\u90fd\u4e0d\u52a8\u5fc3\uff0c\u600e\u4e48\u4f1a\u53bb\u5077\u897f\u95e8\u5439\u96ea\u7684\u8001 \u5a46\u3002\u201d \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u53ea\u95ee\u4f60\u4e00\u53e5\u8bdd\u3002\u201d \u5f71\u5b50\u9053\uff1a\u201c\u95ee\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u82e5\u662f\u5978\u7ec6\uff0c\u8001\u5200\u628a\u5b50\u600e\u4e48\u4f1a\u8ba9\u6211\u6d3b\u5230\u73b0\u5728\uff0c\u4ed6\u662f\u4e2a\u591a\u4e48\u5389\u5bb3\u7684\u89d2 \u8272\uff0c\u4f60\u603b\u8be5\u77e5\u9053\u5f97\u6bd4\u6211\u6e05\u695a\u3002\u201d \u5f71\u5b50\u5ffd\u7136\u53d1\u6296\uff0c\u8eab\u5b50\u7a81\u7136\u7f29\u6210\u4e86\u4e00\u2014\u56e2\uff0c\u773c\u775b\u91cc\u7acb\u523b\u5145\u6ee1\u60b2\u6124\u3001\u4ec7\u6068\u548c\u6050\u60e7\u3002 \u9646\u5c0f\u51e4\u7f13\u7f13\u9053\uff1a\u201c\u4f60\u5f53\u7136\u77e5\u9053\uff0c\u56e0\u4e3a\u4ece\u9ad8\u5d16\u4e0a\u628a\u4f60\u63a8\u4e0b\u6765\u7684\u4eba\u5c31\u662f\u4ed6\uff01\u201d \u5f71\u5b50\u6296\u5f97\u66f4\u5389\u5bb3\u3002 \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f46\u662f\u4f60\u53ef\u4ee5\u653e\u5fc3\uff0c\u6211\u7edd\u4e0d\u4f1a\u628a\u8fd9\u79d8\u5bc6\u8bf4\u51fa\u53bb\u7684\u3002\u201d \u5f71\u5b50\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u771f\u7684\u5f88\u559c\u6b22\u53f6\u96ea\uff0c\u6211\u7edd\u4e0d\u4f1a\u5bb3\u5979\u7684\u7236\u4eb2\uff0c\uff0c \u5f71\u5b50\u53c8\u5f80\u540e\u9000\u7f29\u4e86\u4e00\u6b65\uff0c\u58f0\u97f3\u5df2\u5636\u54d1\uff0c\u9053\uff1a\u201c\u8c01\u662f\u5979\u7684\u7236\u4eb2\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u3002\u201d \u5f71\u5b50\u5ffd\u7136\u5012\u4e86\u4e0b\u53bb\uff0c\u8eba\u5728\u5730\u4e0a\uff0c\u8fde\u547c\u5438\u90fd\u5df2\u505c\u987f\u3002 \u53ef\u662f\u4ed6\u8fd8\u6ca1\u6709\u6b7b\uff0c\u8fc7\u5382\u5f88\u4e45\uff0c\u624d\u53f9\u606f\u7740\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u662f\u7684\uff0c\u5927\u5bb6\u90fd\u4ee5\u4e3a\u6211\u5df2\u6b7b \u4e86\uff0c\u8fde\u4ed6\u4eec\u5a49\u59b9\u90fd\u4ee5\u4e3a\u6211\u5df2\u6b7b\u4e86\u3002\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u81f3\u5c11\u5e94\u8be5\u8ba9\u4ed6\u4eec\u77e5\u9053\u4f60\u8fd8\u6d3b\u7740ao \u5f71\u5b50\u53c8\u8df3\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u5343\u4e07\u4e0d\u80fd\u544a\u8bc9\u4ed6\u4eec\uff0c\u5343\u4e07\u4e0d\u80fd\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u5f71\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u65e0\u8bba\u5982\u4f55\u90fd\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u770b\u89c1\u6211\u73b0\u5728\u8fd9\u6837\u5e7f\u3002\u6211\u5b81\u53ef\u4e5f\u2026\u2026\u201d \u4ed6\u7684\u58f0\u97f3\u7a81\u7136\u505c\u987f\uff0c\u5c06\u8033\u6735\u8d34\u5728\u5730\u4e0a\uff0c\u542c\u4e86\u5f88\u4e45\uff0c\u538b\u4f4e\uff0c\u7a9d\u97f3\u9053\uff1a\u201c\u5343\u4e07\u4e0d\u8981\u8bf4\u770b \u89c1\u8fc7\u6211\uff0c\u6211\u6c42\u4f60\u3002\u201d \u8bf4\u5230\u6700\u540e\u4e09\u4e2a\u5b57\u65f6\uff0c\u4ed6\u5c31\u5df2\u6d88\u5931\uff0c\u8fd9\u4e09\u4e2a\u5b57\u4e2d\u7684\u786e\u5145\u6ee1\u54c0\u6c42\u4e4b\u610f\u3002 \u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u9646\u5c0f\u51e4\u624d\u542c\u89c1\u811a\u6b65\u58f0\uff0c\u4e00\u4e2a\u4eba\u6b63\u8e0f\u7740\u843d\u53f6\u8d70\u8fc7\u6765\u3002 \u9646\u5c0f\u51e4\u53ea\u5e0c\u671b\u6765\u7684\u662f\u53f6\u96ea\u3002 \u6765\u7684\u4e0d\u662f\u53f6\u96ea\uff0c\u662f\u53f6\u7075\u3002 \u5979\u770b\u89c1\u9646\u5c0f\u51e4\u65f6\uff0c\u81ea\u5df1\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u4f46\u7acb\u523b\u5c31\u9547\u5b9a\u4e0b\u6765\u3002 \u8fd9\u5c0f\u59d1\u5a18\u663e\u7136\u6bd4\u4efb\u4f55\u4eba\u60f3\u50cf\u4e2d\u90fd\u51b7\u9759\u5f97\u591a\uff0c\u4e5f\u8001\u7ec3\u5f97\u591a\u3002 \u5979\u95ee\uff1a\u201c\u6211\u521a\u624d\u542c\u89c1\u8fd9\u91cc\u6709\u4eba\u5728\u8bf4\u8bdd\uff0c\u4f60\u5728\u8ddf\u8c01\u8bf4\u8bdd\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8ddf\u6211\u81ea\u5df1\u3002\u201d \u53f6\u7075\u7b11\u4e86\uff0c\u7728\u7740\u773c\u7b11\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u53d8\u5f97\u559c\u6b22\u81ea\u8a00\u81ea\u8bed\u7684\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5c31\u5728\u6211\u53d1\u73b0\u670b\u53cb\u4eec\u90fd\u4e0d\u592a\u53ef\u9760\u7684\u65f6\u5019\u3002 \u53f6\u7075\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u4e00\u4e2a\u4eba\u8eba\u5728\u5730\u4e0a\u5462\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u9ad8\u5174\u3002\u201d \u53f6\u7075\u53c8\u7b11\u4e86\uff0c\u80cc\u8d1f\u7740\u53cc\u624b\uff0c\u56f4\u7740\u9646\u5c0f\u51e4\u8d70\u4e86\u4e24\u5708\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u70b9\u4f4f\u81ea\u5df1\u7a74 \u9053\uff0c\u4e5f\u662f\u56e0\u4e3a\u4f60\u9ad8\u5174\u3002\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u3002 \u4ed6\u4e0d\u80fd\u4e0d\u627f\u8ba4\u8fd9\u5c0f\u59d1\u5a18\u7684\u773c\u529b\u6bd4\u522b\u4eba\u60f3\u50cf\u4e2d\u654f\u9510\uff0c\u53ef\u662f\u4ed6\u76f8\u4fe1\u81ea\u5df1\u8fd8\u662f\u80fd\u5bf9\u4ed8\u5979\u3002 \u50cf\u4ed6\u8fd9\u6837\u7684\u4eba\uff0c\u8981\u9a97\u8fc7\u4e00\u4e2a\u5c0f\u59d1\u5a18\uff0c\u5f53\u7136\u5e76\u4e0d\u662f\u4ef6\u592a\u56f0\u96be\u7684\u4e8b\u3002 \u201c\u8fd9\u91cc\u7684\u6811\u53f6\u548c\u91ce\u83cc\u5927\u90e8\u5206\u90fd\u6709\u6bd2\uff0c\u6211\u65e0\u610f\u4e2d\u5403\u4e86\u4e9b\uff0c\u53ea\u597d\u5b50\u5df2\u70b9\u4f4f\u51e0\u5904\u7a74\u9053\uff0c\u514d \u5f97\u6bd2\u6c14\u653b\u5fc3\uff1a\u201c\u4ed6\u5ffd\u7136\u53d1\u73b0\u8bf4\u8c0e\u4e5f\u4e0d\u592a\u56f0\u96be\u3002 \u53f6\u7075\u770b\u7740\u4ed6\uff0c\u597d\u50cf\u5df2\u76f8\u4fe1\u4e86\uff0c\u5374\u6ca1\u6709\u5f00\u53e3\u3002 \u9646\u5c0f\u51e4\u53f9\u9053\uff1a\u201c\u6211\u70b9\u4e86\u81ea\u5df1\u7684\u7a74\u9053\u540e\uff0c\u624d\u60f3\u5230\u4e00\u4e2a\u5f88\u4e25\u91cd\u7684\u95ee\u9898\uff0c\u56e0\u4e3a\u6211\u5df2\u6ca1\u6cd5\u5b50 \u518d\u5c06\u7a74\u9053\u89e3\u5f00\uff0c\u73b0\u5728\u5e78\u597d\u4f60\u6765\u4e86\uff0c\u771f\u662f\u8c22\u5929\u8c22\u5730\u3002\u201d \u53f6\u7075\u8fd8\u662f\u76ef\u7740\u4ed6\uff0c\u4e0d\u8bf4\u8bdd\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u80fd\u66ff\u6211\u628a\u7a74\u9053\u89e3\u5f00\u7684\uff0c\u4f60\u4e00\u5411\u5f88\u6709\u672c\u4e8b\u3002\u201d \u53f6\u7075\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u7b49\u4e00\u7b49\uff0c\u6211\u9a6c\u4e0a\u56de\u6765\u3002\u201d \u8bf4\u5b8c\u4e86\u8fd9\u53e5\u8bdd\uff0c\u5979\u5c31\u98de\u4e00\u6837\u7684\u8d70\u4e86\uff0c\u8fde\u5934\u90fd\u6ca1\u6709\u56de\u2026 \u9646\u5c0f\u51e4\u5446\u4f4f\u3002 \u5e78\u597d\u53f6\u7075\u4e00\u8d70\uff0c\u5f71\u5b50\u53c8\u5ffd\u7136\u51fa\u73b0\u3002 \u9646\u5c0f\u51e4\u677e\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u8981\u6211\u505a\u7684\u4e8b\uff0c\u6211\u5168\u90fd\u7b54\u5e94\uff0c\u73b0\u5728\u4f60\u80fd\u4e0d\u80fd\u653e\u6211\u8d70\u201d \u5f71\u5b50\u7684\u56de\u7b54\u5f88\u5e72\u8106\uff1a\u201c\u4e0d\u80fd\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u5f71\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u60f3\u770b\u770b\u963f\u7075\u7a76\u7adf\u51c6\u5907\u600e\u4e48\u6837\u5bf9\u4f60\u3002 \u4ed6\u58f0\u97f3\u91cc\u5e26\u7740\u7b11\u610f\uff1a\u201c\u8fd9\u5c0f\u4e2b\u5934\u4ece\u5c0f\u662f\u4e2a\u9b3c\u7075\u7cbe\uff0c\u5979\u73a9\u7684\u82b1\u6837\uff0c\u6709\u65f6\u8fde\u6211\u90fd\u60f3\u4e0d \u5230\u3002\u201d \u9646\u5c0f\u51e4\u60f3\u7b11\uff0c\u5374\u5df2\u7b11\u4e0d\u51fa\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u731c\u4e0d\u51fa\u53f6\u7075\u7a76\u7adf\u60f3\u7528\u4ec0\u4e48\u6cd5\u5b50\u5bf9\u4ed8\u4ed6\uff0c\u4ed6\u53ea\u77e5 \u9053\u8fd9\u9b3c\u4e2b\u5934\u662f\u4ec0\u4e48\u4e8b\u90fd\u80fd\u505a\u5f97\u51fa \u7684\u3002 \u4ed6\u6b63\u60f3\u518d\u8ddf\u5f71\u5b50\u8c08\u8c08\u6761\u4ef6\uff0c\u5f71\u5b50\u5374\u53c8\u4e0d\u89c1\u4e86\uff0c\u7136\u540e\u4ed6\u5c31\u53c8\u542c\u89c1\u4e86\u843d\u53f6\u4e0a\u7684\u811a\u6b65\u58f0\u3002 \u8fd9\u6b21\u7684\u811a\u6b65\u58f0\u6bd4\u4e0a\u6b21\u91cd\uff0c\u53f6\u7075\u4e5f\u6bd4\u4e0a\u6b21\u6765\u5f97\u5feb\uff0c\u5979\u624b\u91cc\u62ff\u7740\u628a\u4e0d\u77e5\u540d\u7684\u836f\u8349\uff0c\u663e\u7136 \u662f\u521a\u91c7\u6765\u7684\uff0c\u4e00\u505c\u4e0b\u6765\u5c31\u5598\u606f\u7740\u9053\uff1a\u201c\u5403\u4e0b\u53bb\uff01\u201d \u9646\u5c0f\u51e4\u5403\u7684\u662f\u4e00\u60ca\uff1a\u201c\u4f60\u8981\u6211\u628a\u8fd9\u4e9b\u4e71\u4e03\u516b\u7cdf\u7684\u91ce\u8349\u5403\u4e0b \u53bb\uff1f\u2019\u2019 \u53f6\u7075\u677f\u7740\u8138\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u91ce\u8349\uff0c\u8fd9\u662f\u6551\u547d\u7684\u836f\uff0c\u662f\u6211\u8f9b\u8f9b\u82e6\u82e6\u53bb\u66ff\u4f60\u91c7\u6765\u7684\u3002\u201d \u5979\u53c8\u89e3\u91ca\uff1a\u201c\u8981\u89e3\u5f00\u4f60\u7684\u7a74\u9053\u5f88\u5bb9\u6613\uff0c\u53ef\u662f\u4f60\u7a74\u9053\u89e3\u5f00\u4e86\u540e\uff0c\u4e07\u4e00\u6bd2\u6c14\u653b\u5fc3\uff0c\u6211\u5c82 \u975e\u53cd\u800c\u5bb3\u4e86\u4f60\uff0c\u6240\u4ee5\u6211\u4e00\u5b9a\u8981\u5148\u66ff\u4f60\u627e\u89e3\u836foo \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u73b0\u5728\u6211\u4e2d\u7684\u6bd2\u597d\u50cf\u5df2\u89e3\u4e86\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u597d\u50cf\u4e0d\u884c\uff0c\u8981\u771f\u7684\u5b8c\u5168\u89e3\u4e86\u624d\u5f97\uff0c\u53cd\u6b63\u8fd9\u79cd\u836f\u8349\u5bf9\u4eba\u53ea\u6709\u597d\u5904\uff0c\u591a\u5403\u4e00 \u70b9\u4e5f\u6ca1\u5173\u7cfb\u3002\u201d \u5979\u7684\u5634\u5728\u8bf4\u8bdd\uff0c\u9646\u5c0f\u51e4\u7684\u5634\u5374\u5df2\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u56e0\u4e3a\u4ed6\u5634\u91cc\u5df2\u88ab\u585e\u6ee1\u4e86\u836f\u8349\u3002 \u4ed6\u5ffd\u7136\u53d1\u73b0\u201c\u826f\u836f\u82e6\u53e3\u201d\u8fd9\u53e5\u8bdd\u5b9e\u5728\u5f88\u6709\u9053\u7406\uff0c\u4e0d\u7ba1\u8fd9\u4e9b\u836f\u8349\u5bf9\u4eba\u6709\u591a\u5927\u7684\u597d\u5904\uff0c \u4ed6\u90fd\u7edd\u4e0d\u60f3\u518d\u5c1d\u8bd5\u7b2c\u4e8c\u6b21\u3002 \u597d\u4e0d\u5bb9\u6613\u603b\u7b97\u5c06\u4e00\u628a\u8349\u5168\u90fd\u54bd\u4e0b\u809a\u5b50\uff0c\u53f6\u7075\u4e5f\u677e\u4e86\u53e3\u6c14\uff0c\u7728\u7740\u773c\u9053\uff1a\u201c\u600e\u4e48\u6837\uff0c\u597d \u4e0d\u597d\u5403\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6664\uff0c\u6664\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u58f0\u97f3\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8fd9\u662f\u7f8a\u7684\u58f0\u97f3\uff0c\u6211\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u597d\u50cf\u5df2\u53d8\u6210\u4e00\u53ea\u7f8a\u3002\u201d \u53f6\u7075\u4e5f\u7b11\u4e86\uff0c\u5ae3\u7136\u9053\uff1a\u201c\u6211\u559c\u6b22\u5c0f\u7ef5\u7f8a\uff0c\u6765\uff0c\u8ba9\u6211\u62b1\u62b1\u4f60\u3002\u201d \u5979\u5c45\u7136\u771f\u7684\u628a\u9646\u5c0f\u51e4\u62b1\u4e86\u8d77\u6765\uff0c\u5979\u7684\u529b\u6c14\u8fd8\u771f\u4e0d\u5c0f\u3002 \u9646\u5c0f\u51e4\u53c8\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u4f60\u62b1\u7740\u6211\u5e72\u4ec0\u4e48\uff1f\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u628a\u6211\u7684\u7a74\u9053\u89e3\u5f00\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u73b0\u5728\u89e3\u836f\u7684\u529b\u91cf\u8fd8\u6ca1\u6709\u5206\u6563\uff0c\u8fd9\u91cc\u53c8\u4e0d\u662f\u4e45\u7559\u3002\u201d\u4e4b\u5730\uff0c\u6211\u53ea\u6709\u5148\u628a\u4f60 \u62b1\u8d70\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u62b1\u6211\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u5f53\u7136\u662f\u4e2a\u597d\u5730\u65b9\uff0c\u5f88\u597d\u5f88\u597d\u7684\u5730\u65b9\u3002\u201d \u9646\u5c0f\u51e4\u53ea\u6709\u82e6\u7b11\u3002 \u88ab\u4e00\u4e2a\u51e0\u4e4e\u5475\u4ee5\u505a\u81ea\u5df1\u5973\u513f\u7684\u5c0f\u59d1\u5a18\u62b1\u7740\u8d70\uff0c\u8fd9\u6ecb\u6627\u603b\u662f\u4e0d\u592a\u597d\u53d7\u7684\u3002 \u53ef\u662f\u8fd9\u5c0f\u59d1\u5a18\u7684\u80f8\u819b\u504f\u504f\u53c8\u8fd9\u4e48\u6210\u719f\uff0c\u8eab\u4e0a\u7684\u5473\u9053\u504f\u504f\u53c8\u8fd9\u4e48\u9999\u3002 \u9646\u5c0f\u51e4\u53ea\u597d\u95ed\u4e0a\u773c\u775b\uff0c\u60f3\u5b66\u4e00\u8001\u50e7\u5165\u5b9a\uff0c\u53f6\u7075\u5374\u5ffd\u7136\u5531\u8d77\u6b4c\u6765\uff1a \u201c\u59b9\u59b9\u62b1\u7740\u6ce5\u5a03\u5a03\uff0c \u8981\u5230\u82b1\u56ed\u53bb\u770b\u82b1\uff0c \u6211\u53eb\u5a03\u5a03\u542c\u6211\u8bdd\uff0c \u5a03\u5a03\u53eb\u6211\u5c0f\u5988\u5988\u3002\u201d \u8fd9\u513f\u6b4c\u6709\u4e00\u534a\u662f\u9646\u5c0f\u51e4\u5531\u51fa\u6765\u7684\u3002\u6709\u4e00\u534a\u662f\u81ea\u5df1\u7f16\u51fa\u6765\u7684\uff0c\u7f16\u5f97\u771f\u7edd\u3002 \u9646\u5c0f\u51e4\u542c\u4e86\u5f53\u7136\u6709\u70b9\u54ed\u7b11\u4e0d\u5f97\uff0c\u5c31\u5728\u8fd9\u65f6\uff0e\u4ed6\u53c8\u53d1\u73b0\u4e86\u4e00\u4ef6\u66f4\u54ed\u7b11\u4e0d\u5f97\u7684\u4e8b\u3002 \u4ed6\u5ffd\u7136\u89c9\u5f97\u4e0d\u5bf9\u4e86\u3002 \u5f00\u59cb\u7684\u65f6\u5019\uff0c\u4ed6\u8fd8\u4e0d\u77e5\u9053\u7a76\u7adf\u662f\u4ec0\u4e48\u5730\u65b9\u4e0d\u5bf9\uff0c\u4e0d\u77e5\u9053\u8fd8\u597d\u4e9b\uff0c\u77e5\u9053\u4e86\u66f4\u7cdf\u4ed6\u5ffd\u7136 \u53d1\u73b0\u81ea\u5df1\u7adf\u4f3c\u5df2\u53d8\u6210\u6761\u70ed\u5c4b\u9876\u4e0a\u7684\u732b\uff0c\u516c\u732b\u3002 \u82e5\u662f\u771f\u7684\u5728\u70ed\u5c4b\u9876\u4e0a\u4e5f\u8fd8\u597d\u4e9b\uff0c\u53ef\u60dc\u4ed6\u504f\u504f\u662f\u5728\u4e00\u4e2a\u5c11\u5973\u53c8\u9999\u53c8\u8f6f\u7684\u6000\u62b1\u91cc\uff0c\u8fd9\u5c11 \u5973\u53c8\u504f\u504f\u662f\u4ed6\u8fde\u52a8\u90fd\u4e0d\u80fd\u52a8\u7684\u3002 \u4ed6\u518d\u4e09\u8b66\u544a\u81ea\u5df1\uff1a\u201c\u5979\u8fd8\u662f\u4e2a\u5c0f\u5973\u5b69\u5b50\uff0c\u6211\u7edd\u4e0d\u80fd\u60f3\u8fd9\u79cd\u4e8b\u3001\u7edd\u5bf9\u4e0d\u80fd\u3002\u2014\u3002\u201d \u53ea\u53ef\u60dc\u6709\u4e9b\u4e8b\u4f60\u4e0d\u60f3\u4e5f\u6ca1\u7528\u3001\u5c31\u597d\u50cf\u201c\u5929\u8981\u4e0b\u96e8\uff0c\u8001\u5a46\u8981\u5077\u4eba\u201d\u4e00\u6837\uff0c\u8c01\u90fd\u62ff\u5b83\u6ca1 \u529e\u6cd5\u3002 \u9646\u5c0f\u51e4\u77e5\u9053\u81ea\u5df1\u8eab\u4f53\u7684\u67d0\u4e00\u90e8\u5206\u5df2\u53d1\u751f\u4e86\u53d8\u5316\uff0c\u4e00\u4e2a\u58ee\u5e74\u7537\u4eba\u7edd\u5bf9\u65e0\u6cd5\u6291\u5236\u7684\u53d8 \u5316\u3002 \u4ed6\u53ea\u5e0c\u671b\u53f6\u7075\u6ca1\u6709\u770b\u89c1\u3002 \u4ed6\u7edd\u4e0d\u53bb\u770b\u53f6\u7075\uff0c\u8fde\u4e00\u773c\u90fd\u4e0d\u6562\u770b\u3002 \u53ef\u662f\u53f6\u7075\u5374\u504f\u504f\u5728\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u7684\u8138\u600e\u4e48\u7ea2\u4e86\uff1f\u662f\u4e0d\u662f\u5728\u53d1\u70e7\uff1f\u201d \u9646\u5c0f\u51e4\u53ea\u597d\u542b\u542b\u7cca\u7cca\u7684\u56de\u7b54\u4e86\u2014\u53e5\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u542c\u4e0d\u6e05\u81ea\u5df1\u5728\u8bf4\u4ec0\u4e48\u3002 \u5e78\u597d\u53f6\u7075\u5c45\u7136\u6ca1\u6709\u8ffd\u95ee\uff0c\u66f4\u5e78\u8fd0\u7684\u662f\uff0c\u4ed6\u6839\u672c\u8fde\u52a8\u90fd\u4e0d\u80fd\u52a8\u3002 \u5982\u679c\u4ed6\u7684\u7a74\u9053\u6ca1\u6709\u88ab\u5236\u4f4f\uff0c\u73b0\u5728\u4f1a\u505a\u51fa\u4ec0\u4e48\u6837\u7684\u4e8b\u6765\uff1f \u4ed6\u8fde\u60f3\u90fd\u4e0d\u6562\u53bb\u60f3\u3002 \u53f6\u7075\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u770b\u6837\u5b50\u2014\u5b9a\u662f\u90a3\u4e9b\u836f\u8349\u7684\u529b\u91cf\u5df2\u53d1\u4f5c \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u90a3\u7a76\u7adf\u662f\u4ec0\u4e48\u8349\uff1f\u662f\u6551\u547d\u7684\uff1f\u8fd8\u662f\u8981\u547d\u7684\uff1f\u201d\u53f6\u7075\u9053\uff1a\u201c\u662f\u8981\u547d \u7684\u3002 \u5979\u5ffd\u7136\u505c\u4e0b\u6765\uff0c\u653e\u4e0b\u4e86\u9646\u5c0f\u51e4\uff0c\u653e\u5728\u4e00\u5806\u8f6f\u8f6f\u7684\u8349\u53f6 \u9646\u5c0f\u51e4\u5f20\u5f00\u773c\uff0c\u624d\u53d1\u73b0\u8fd9\u662f\u4e2a\u5c71\u6d1e\uff0c\u53f6\u7075\u7684\u624b\u53c9\u7740\u8170\uff0c\u7ad9\u5728\u4ed6\u9762\u524d\uff0c\u7b11\u5f97\u5c31\u50cf\u662f\u4e2a \u5c0f\u5996\u7cbe\u3002 \u5979\u7728\u7740\u773c\u9053\uff1a\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u89c9\u5f97\u5f88\u8981\u547d\uff1f\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u7b80\u76f4\u4ed6\u5988\u7684\u8981\u547d\u6781\u4e86\u3002\u201d \u53f6\u7075\u9053\uff1a\u6211\u77e5\u9053\u6709\u79cd\u836f\u80fd\u628a\u4f60\u6cbb\u597d\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ec0\u4e48\u836f\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u6211\u3002\u201d \u5979\u6307\u7740\u81ea\u5df1\u7684\u9f3b\u5b50\uff1a\u201c\u53ea\u6709\u6211\u80fd\u628a\u4f60\u6cbb\u597d\u3002\u201d \u9646\u5c0f\u51e4\u77aa\u7740\u5979\u3002 \u5979\u5b9e\u5728\u5df2\u4e0d\u662f\u4e2a\u5c0f\u5973\u5b69\u5b50\u4e86\uff0c\u5e94\u8be5\u5927\u7684\u5730\u65b9\uff0c\u90fd\u5df2\u7ecf\u5f88\u5927\u3002 \u9646\u5c0f\u51e4\u54ac\u7740\u7259\uff0c\u6068\u6068\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u81ea\u5df1\u627e\u7684\uff0c\u602a\u4e0d\u5f97\u6211\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u6211\u4e0d\u602a\u4f60\uff0c\u4f60\u53c8\u80fd\u600e\u4e48\u6837\uff1f\u201d \u9646\u5c0f\u51e4\u4e0d\u80fd\u600e\u4e48\u6837\uff0c\u4ed6\u6839\u672c\u8fde\u52a8\u90fd\u4e0d\u80fd\u52a8\u8fd9\u4e00\u70b9\u4ed6\u521a\u624d\u8fd8\u89c9\u5f97\u5f88\u5e78\u8fd0\uff0c\u73b0\u5728\u5374\u5df2\u53d8 \u6210\u4e86\u5f88\u4e0d\u5e78\u3002 \u4ed6\u53ea\u89c9\u5f97\u81ea\u5df1\u597d\u50cf\u968f\u65f6\u90fd\u53ef\u80fd\u4f1a\u6da8\u7834\u3002 \u53f6\u7075\u770b\u7740\u4ed6\uff0c\u5403\u5403\u7684\u7b11\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u79cd\u4e8b\u6709\u65f6\u5019\u771f\u4f1a\u8981\u547d\u7684\u3002 \u9646\u5c0f\u51e4\u77e5\u9053\u3002 \u4ed6\u76f8\u4fe1\u73b0\u5728\u5929\u4e0b\u5df2\u7edd\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u6bd4\u4ed6\u77e5\u9053\u5f97\u66f4\u6e05\u695a\u3002 \u66f4\u8981\u547d\u7684\u662f\uff0c\u4ed6\u5df2\u770b\u89c1\u4e86\u5979\u7684\u817f\u3002 \u8fd9\u5c0f\u5996\u7cbe\u7684\u817f\u4e0d\u77e5\u9053\u4ec0\u4e48\u65f6\u5019\u5ffd\u7136\u5c31\u9732\u5728\u8863\u670d\u5916\u9762\u4e86\u3002 \u5979\u7684\u817f\u5747\u5300\u4fee\u957f\u7ed3\u5b9e\u3002 \u9646\u5c0f\u51e4\u7684\u58f0\u97f3\u5df2\u4eff\u4f5b\u662f\u5728\u547b\u541f\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u4e00\u5b9a\u8981\u5bb3\u6b7b\u6211\uff1f\u201d \u53f6\u7075\u67d4\u58f0\u9053\uff1a\u201c\u6211\u5f88\u60f3\u6551\u4f60\uff0c\u6211\u672c\u6765\u5c31\u559c\u6b22\u4f60\uff0c\u53ea\u53ef\u60dc \u5979\u7528\u4e00\u6839\u624b\u6307\u8f7b\u629a\u7740\u9646\u5c0f\u51e4\uff1a\u201c\u6211\u4e5f\u662f\u4e2a\u5904\u5973\uff0c\u4e5f\u4ece\u6765\u6ca1\u6709\u7537\u4eba\u78b0\u8fc7\u6211\u3002\u201d \u8fd9\u662f\u5979\u59d0\u59d0\u8bf4\u8fc7\u7684\u8bdd\uff0c\u5979\u8fde\u53e3\u6c14\u90fd\u5b66\u5f97\u5f88\u50cf\u3002 \u9646\u5c0f\u51e4\u5ffd\u7136\u660e\u767d\uff0c\u53f6\u96ea\u90a3\u79d8\u5bc6\u7684\u5c0f\u5929\u5730\uff0c\u539f\u6765\u5e76\u6ca1\u6709\u5979\u81ea\u5df1\u60f3\u50cf\u4e2d\u90a3\u4e48\u79d8\u5bc6\u3002 \u53f6\u7075\u5ffd\u7136\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u4f60\u4eec\u5728\u90a3\u91cc\u5e72\u4ec0\u4e48\uff0c\u6211\u5168\u90fd\u770b\u89c1\u4e86\uff0c\u770b\u5f97\u6e05\u6e05 \u695a\u695a\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u90a3\u662f\u4f60\u59d0\u59d0\u2026\u2026\u201d \u53f6\u7075\u5927\u58f0\u9053\uff1a\u201c\u5979\u4e0d\u662f\u6211\u59d0\u59d0\uff0c\u5979\u662f\u6211\u5929\u751f\u7684\u5bf9\u5934\uff0c\u53ea\u8981\u662f\u6211\u559c\u6b22\u7684\uff0c\u5979\u90fd\u8981\u62a2 \u8d70\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u2026\u2026\u201d \u53f6\u7075\u53c8\u6253\u65ad\u4ed6\u7684\u8bdd\uff0c\u9053\uff1a\u201c\u5979\u660e\u660e\u77e5\u9053\u662f\u6211\u5148\u770b\u89c1\u4f60\u7684\uff0c\u5979\u4e5f\u8981\u62a2\uff0c\u53ef\u662f\u8fd9\u4e00\u6b21\u6211 \u7edd\u4e0d\u8ba9\u5979\u4e86\uff0c\u4f60\u662f\u6211\u7684\uff0c\u6211\u8981\u5ac1\u7ed9\u4f60\u3002 \u5979\u5ffd\u53c8\u7b11\u4e86\uff0c\u7b11\u5f97\u53c8\u751c\u871c\uff0c\u53c8\u6e29\u67d4\uff1a\u201c\u4f60\u8981\u6211\u5ac1\u7ed9\u4f60\u4e5f\u884c\uff0c\u65e0\u8bba\u4f60\u8bf4\u4ec0\u4e48\u6211\u90fd\u7b54 \u5e94\u3002\u201d \u5230\u4e86\u8fd9\u79cd\u65f6\u5019\uff0c\u9646\u5c0f\u51e4\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\uff1f \u5c71\u6d1e\u91cc\u9edd\u9eef\u800c\u5b89\u9759\uff0c\u66ae\u8272\u5df2\u6e10\u4e34\u3002 \u7247\u523b\u5b89\u9759\u540e\uff0c\u53f6\u7075\u5c31\u54ed\u4e86\uff0c\u54ed\u5f97\u4e5f\u4e0d\u77e5\u6709\u591a\u4f24\u5fc3\uff0c\u5c31\u597d\u50cf\u53d7\u5c3d\u4e86\u59d4\u66f2\u3002 \u201c\u4f60\u6b3a\u8d1f\u6211\uff0c\u4f60\u600e\u4e48\u80fd\u8fd9\u6837\u5b50\u6b3a\u8d1f\u6211\uff1f\u4f60\u5bb3\u4e86\u6211\u4e00\u8f88\u5b50\u3002 \u7a76\u7adf\u662f\u8c01\u5728\u6b3a\u8d1f\u8c01\uff1f\u8c01\u5728\u5bb3\u8c01\uff1f \u9646\u5c0f\u51e4\u53ea\u6709\u82e6\u7b11\uff0c\u8fd8\u4e0d\u6562\u7b11\u51fa\u6765\uff0c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u5979\u603b\u662f\u4e2a\u5973\u5b69\u5b50\uff0c\u800c\u4e14\u771f\u7684\u662f\u4e2a\u4ece \u6765\u4e5f\u6ca1\u6709\u8ba9\u7537\u4eba\u78b0\u8fc7\u7684\u5973\u5b69\u5b50\u3002 \u2014\u4e2a\u7537\u4eba\u5982\u679c\u5bf9\u4e00\u4e2a\u8fd9\u6837\u7684\u5973\u5b69\u5b50\u505a\u4e86\u4ed6\u4eec\u521a\u624d\u505a\u8fc7\u7684\u4e8b\uff0c\u8fd9\u4e2a\u7537\u4eba\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4 \u7684\uff1f \u201c\u4f60\u521a\u624d\u7b54\u5e94\u8fc7\u6211\u7684\u4e8b\uff0c\u73b0\u5728\u662f\u4e0d\u662f\u5c31\u5df2\u7ecf\u540e\u6094\u4e86\uff1f\u201d\u201c\u6211\u6ca1\u6709\u3002 \u201c\u4f60\u771f\u7684\u4e0d\u540e\u6094\uff1f\u201d\u201c\u771f\u7684\u3002\u201d \u5979\u7b11\u4e86\uff0c\u53c8\u7b11\u5f97\u50cf\u662f\u4e2a\u5b69\u5b50\u3002 \u201c\u8d70\uff0c\u6211\u4eec\u56de\u5bb6\u53bb\u3002\u201d\u5979\u62c9\u4f4f\u4ed6\u7684\u624b\uff1a\u4ece\u4eca\u5929\u8d77\uff0c\u4f60\u5c31\u662f\u4e2a\u6709\u5bb6\u6709\u5ba4\u7684\u7537\u4eba\u4e86\uff0c\u53ea \u8981\u4f60\u4e0d\u53bb\u627e\u522b\u7684\u5973\u4eba\uff0c\u6211\u4e00\u5b9a\u4f1a\u50cf\u4f3a\u5019\u7687\u5e1d\u2014\u6837\u4f3a\u5019\u4f60\u3002\u201d \u5915\u9633\u897f\u4e0b\uff0c\u66ae\u8272\u6ee1\u5c71\u3002 \u9646\u5c0f\u51e4\u5ffd\u7136\u89c9\u5f97\u5f88\u75b2\u5026\uff0c\u4ed6\u8fd9\u4e00\u751f\u4e2d\uff0c\u51e0\u4e4e\u4ece\u6765\u4e5f\u6ca1\u6709\u8fd9\u4e48\u6837\u75b2\u5026\u8fc7\u3002 \u8fd9\u5e76\u4e0d\u662f\u56e0\u4e3a\u90a3\u79cd\u8981\u547d\u7684\u8349\uff0c\u4e5f\u4e0d\u662f\u56e0\u4e3a\u90a3\u4ef6\u8981\u547d\u7684\u4e8b\u3002\u8fd9\u79cd\u75b2\u5026\u4eff\u4f5b\u662f\u4ece\u4ed6\u5fc3\u91cc \u751f\u51fa\u7684\u2026\u2014\u4e2a\u4eba\u53ea\u6709\u5728\u81ea\u5df1\u5fc3\u91cc\u5df2\u51c6\u5907\u653e\u5f03\u2014\u5207\u65f6\uff0c\u624d\u4f1a\u751f\u51fa\u8fd9\u79cd\u75b2\u5026\u3002 \u4e5f\u8bb8\u6211\u771f\u7684\u5e94\u8be5\u505a\u4e2a\u201c\u4f4f\u5bb6\u7537\u4eba\u201d\u4e86\u3002 \u5728\u8fd9\u8273\u4e3d\u7684\u5915\u9633\u4e0b\uff0c\u770b\u7740\u53f6\u7075\u8138\u4e0a\u5b69\u5b50\u822c\u7684\u7b11\u53a3\uff0c\u4ed6\u5fc3\u91cc\u7684\u786e\u6709\u8fd9\u79cd\u60f3\u6cd5\u3002 \u4e0d\u7ba1\u5979\u505a\u4e86\u4ec0\u4e48\u4e8b\uff0c\u603b\u662f\u4e3a\u4e86\u559c\u6b22\u6211\u624d\u505a\u7684\u3002 \u5979\u7b11\u5f97\u66f4\u751c\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u62c9\u8d77\u4e86\u5979\u7684\u624b\uff0c\u8fd9\u65f6\u8fdc\u65b9\u6b63\u54cd\u8d77\u4e00\u7247\u949f\u58f0\uff0c\u5e7d\u7075\u5c71\u5e84\u4e2d\u4eff\u4f5b \u53c8\u5c06\u6709\u76db\u5bb4\u5f00\u59cb\u3002 \u96be\u9053\u8001\u5200\u628a\u5b50\u5df2\u4e3a\u4ed6\u4eec\u51c6\u5907\u597d\u559c\u9152\uff1f \u7b2c\u5341\u7ae0\u3000\u5192\u9669\u767b\u7edd\u9601>> \u53e4\u9f99\u300a\u9646\u5c0f\u51e4\u7cfb\u5217\xb7\u5e7d\u7075\u5c71\u5e84\u300b \u7b2c\u5341\u7ae0\u3000\u5192\u9669\u767b\u7edd\u9601 \u5bb4\u4f1a\u8fd8\u6ca1\u884c\u5f00\u59cb\uff0c\u56e0\u4e3a\u5927\u5bb6\u8fd8\u8981\u7b49\u4e00\u4e2a\u4eba\uff0c\u4e00\u4e2a\u4e0d\u80fd\u7f3a\u5c11\u7684\u4eba\u3002 \u9646\u5c0f\u51e4\u6084\u6084\u7684\u8d70\u8fdb\u53bb\uff0c\u53f6\u7075\u5fae\u7b11\u7740\u8ddf\u5728\u4ed6\u8eab\u540e\uff0c\u5979\u7b11\u5f97\u5f88\u6109\u5feb\uff0c\u4ed6\u5374\u6709\u70b9\u6101\u7709\u82e6 \u8138\u7684\u6837\u5b50\uff0c\u53ea\u5e0c\u671b\u4e0d\u8981\u5f15\u8d77\u522b\u4eba\u7684\u6ce8\u610f\uff0c\u53ef\u662f\u5927\u5bb6\u5374\u504f\u504f\u5728\u6ce8\u610f\u4ed6\uff0c\u6bcf\u4e2a\u4eba\u7684\u773c\u775b\u90fd \u5728\u76ef\u7740\u4ed6\uff0c\u8868\u60c5\u90fd\u6709\u70b9\u602a\u3002 \u8001\u5200\u628a\u5b50\u76ef\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u6765\u8fdf\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u8ff7\u4e86\u8def\uff0c\u6211\u2026\u2026\u201d \u8001\u5200\u628a\u5b50\u6839\u672c\u4e0d\u542c\u4ed6\u8bf4\u4ec0\u4e48\uff0c\u9053\uff1a\u201c\u53ef\u662f\u6211\u77e5\u9053\u4f60\u542c\u89c1\u949f\u58f0\u2014\u5b9a\u4f1a\u56de\u6765\u7684\uff0c\u6240\u4ee5 \u5927\u5bb6\u90fd\u5728\u7b49\u4f60\uff0c\u5df2\u7b49\u4e86\u5f88\u4e45\u3002\u201d \u9646\u5c0f\u51e4\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u5927\u5bb6\u672c\u6765\u4e0d\u5fc5\u7b49\u6211\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4eca\u5929\u4e00\u5b9a\u8981\u7b49\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\u4eca\u5929\u6709\u559c\u4e8b\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8c01\u7684\u559c\u4e8b\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u7684\u3002\u201d \u9646\u5c0f\u51e4\u6014\u4f4f\u3002 \u4ed6\u60f3\u4e0d\u901a\u8fd9\u4ef6\u4e8b\u8001\u5200\u628a\u5b50\u600e\u4e48\u4f1a\u73b0\u5728\u5c31\u5df2\u77e5\u9053\uff1f\u96be\u9053\u8fd9\u672c\u5c31\u662f\u8001\u5200\u628a\u5b50\u53eb\u53f6\u7075\u53bb\u505a \u7684\uff1f \u53f6\u7075\u6ca1\u6709\u5f00\u53e3\uff0c\u4ed6\u4e5f\u6ca1\u6709\u56de\u5934\uff0c\u66f4\u4e0d\u6562\u6b63\u89c6\u5750\u5728\u8001\u5200\u628a\u5b50\u8eab\u65c1\u7684\u53f6\u96ea\u3002 \u53f6\u96ea\u4e00\u76f4\u4f4e\u7740\u5934\uff0c\u5c45\u7136\u4e5f\u6ca1\u6709\u770b\u4ed6\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u672c\u6765\u53ea\u6709\u4e27\u4e8b\uff0c\u4f60\u6765\u4e86\u4e4b\u540e\uff0c\u603b\u7b97\u4e3a\u6211\u4eec\u5e26\u6765\u4e86\u4e00\u70b9\u559c \u6c14\u3002\u201d \u4ed6\u7684\u53e3\u6c14\u6e10\u6e10\u548c\u7f13\uff0c\u53c8\u9053\u5927\u5bb6\u4e5f\u90fd\u5f88\u8d5e\u6210\u8fd9\u4ef6\u4e8b\uff0c\u4f60\u548c\u963f\u96ea\u672c\u5c31\u662f\u5f88\u597d\u7684\u4e00 \u5bf9\u3002\u201d \u9646\u5c0f\u51e4\u5403\u4e86\u4e00\u60ca\uff0c\u201c\u963f\u96ea\uff1f\u201d \u8005\u5200\u628a\u5b50\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u5df2\u95ee\u8fc7\u5979\uff0c\u5979\u5b8c\u5168\u542c\u6211\u7684\u8bdd\uff0c\u6211\u60f3\u4f60\u2014\u5b9a\u4e5f\u4e0d\u4f1a\u53cd\u5bf9 \u7684\u3002\u201d \u9646\u5c0f\u51e4\u53c8\u6014\u4f4f\u3002 \u4ed6\u8eab\u540e\u7684\u53f6\u7075\u5374\u5df2\u53eb\u4e86\u8d77\u6765\uff0c\u201c\u6211\u53cd\u5bf9\u3002\u201d \u6bcf\u4e2a\u4eba\u7684\u8138\u8272\u90fd\u53d8\u4e86\uff0c\u8c01\u4e5f\u60f3\u4e0d\u5230\u5c45\u7136\u6709\u4eba\u6562\u53cd\u5bf9\u8001\u5200\u628a\u5b50\u3002 \u53f6\u96ea\u4e5f\u59cb\u8d77\u5934\uff0c\u5403\u60ca\u7684\u770b\u7740\u59b9\u59b9\u3002 \u53f6\u7075\u5df2\u7ad9\u51fa\u6765\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6211\u575a\u51b3\u53cd\u5bf9\uff0c\u6b7b\u4e5f\u8981\u53cd\u5bf9JH\u8001\u5200\u628a\u5b50\u6012\u9053\uff1a\u201c\u90a3\u4e48\u4f60 \u6700\u597d\u5c31\u8d76\u5feb\u53bb\u6b7bJH\u53f6\u7075\u4e00\u70b9\u4e5f\u4e0d\u754f\u60e7\uff0c\u9053\uff1a\u201c\u6211\u82e5\u53bb\u6b7b\uff0c\u9646\u5c0f\u51e4\u4e5f\u5f97\u966a\u6211\u53bb\u6b7b\u3002\u201d \u8001\u5200\u628a\u5b50\u5389\u58f0\u9053\uff1a\u201c\u8c01\u8bf4\u7684\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u65e0\u8bba\u8c01\u90fd\u4f1a\u8fd9\u4e48\u8bf4\u7684\u3002\u56e0\u4e3a\u6211\u8ddf\u4ed6\u5df2\u7ecf\u662f\u540c\u751f\u5171\u6b7b\u7684\u592b\u59bb\u3002\u201d \u8fd9\u53e5\u8bdd\u66f4\u8ba9\u4eba\u5403\u60ca\uff0c\u53f6\u96ea\u7684\u8138\u4e0a\u5ffd\u7136\u5c31\u5df2\u5931\u53bb\u4e86\u8840\u8272\uff1a\u201c\u4f60\u5df2\u5ac1\u7ed9\u4e86\u4ed6\uff1f\u201d \u53f6\u7075\u6602\u8d77\u5934\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5df2\u5ac1\u7ed9\u4e86\u4ed6\uff0c\u5df2\u7ecf\u628a\u6240\u6709\u7684\u4e00\u5207\u90fd\u7ed9\u4e86\u4ed6\uff0c\u8fd9 \u6b21\u6211\u603b\u7b97\u6bd4\u4f60\u62a2\u5148\u4e86\u4e00\u6b65\uff0c\u4ed6\u867d\u7136\u4e0d\u8981\u4f60\uff0c\u53ef\u662f\u4ed6\u8981\u4e86\u6211\u3002\u201d \u53f6\u96ea\u6574\u4e2a\u4eba\u90fd\u5728\u98a4\u6296\uff0c\u9053\uff1a\u201c\u4f60\u2026\u2026\u4f60\u8bf4\u8c0eJo\u53f6\u7075\u633d\u8d77\u4e86\u9646\u5c0f\u51e4\u7684\u81c2\uff0c\u9053\uff1a\u201c\u4f60 \u4e3a\u4ec0\u4e48\u4e0d\u4eb2\u53e3\u544a\u8bc9\u5979\uff1f\u6211\u8bf4\u7684\u6bcf\u4e2a\u5b57\u90fd\u662f\u771f\u8bdd\u3002\u201d \u5979\u8bf4\u7684\u6bcf\u4e2a\u5b87\u90fd\u50cf\u662f\u4e00\u6839\u9488\uff0c\u9646\u5c0f\u51e4\u7528\u4e0d\u7740\u5f00\u53e3\uff0c\u5927\u5bb6\u4e5f\u90fd\u5df1\u77e5\u9053\u8fd9\u4ef6\u4e8b\u4e0d\u5047\u3002 \u53f6\u96ea\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u63a8\u7ffb\u4e86\u9762\u524d\u7684\u684c\u5b50\uff0c\u5934\u4e5f\u4e0d\u56de\u7684\u51b2\u4e86\u51fa\u53bb\u3002 \u53f6\u7075\u66f4\u5f97\u610f\uff0c\u62c9\u7740\u9646\u5c0f\u51e4\u8d70\u5230\u8001\u5200\u628a\u5b50\u9762\u524d\uff0c\u9053\uff1a\u201c\u963f\u96ea\u662f\u4f60\u7684\u5e72\u5973\u513f\uff0c\u6211\u4e5f\u662f \u7684\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u80af\u66ff\u6211\u4f5c\u4e3b\uff1f\u201d \u8001\u5200\u628a\u5b50\u76ef\u7740\u5979\uff0c\u76ee\u5149\u5200\u950b\u822c\u4ece\u7af9\u7b20\u4e2d\u5c04\u51fa\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4eec\u771f\u7684\u613f\u610f\u505a\u4e00\u8f88\u5b50 \u592b\u59bb\uff1f\u201d \u53f6\u7075\u9053\uff1a\u201c\u5f53\u7136\u613f\u610f\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u597d\uff0c\u6211\u66ff\u4f60\u4f5c\u4e3b\uff0c\u4e09\u4e2a\u6708\u540e\uff0c\u6211\u4eb2\u81ea\u66ff\u4f60\u4eec\u529e\u559c\u4e8b\u3002\u201d \u53f6\u7075\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u8981\u7b49\u4e09\u4e2a\u6708\uff1f\u201d \u8001\u5200\u628a\u5b50\u5389\u58f0\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u662f\u6211\u8bf4\u7684\uff0c\u6211\u8bf4\u7684\u8bdd\u4f60\u6562\u4e0d\u542c\uff1f\u201d \u53f6\u7075\u4e0d\u6562\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u8fd9\u4e09\u4e2a\u6708\u91cc\uff0c\u4f60\u4eec\u5f7c\u6b64\u4e0d\u8bb8\u89c1\u9762\uff0c\u4e09\u4e2a\u6708\u540e\uff0c\u4f60\u4eec\u82e5\u662f\u90fd\u6ca1\u6709\u53d8 \u5fc3\uff0c\u6211\u5c31\u8ba9\u4f60\u4eec\u6210\u4eb2aH\u4ed6\u4e0d\u8ba9\u53f6\u7075\u5f00\u53e3\uff0c\u53c8\u5429\u5490\u67f3\u9752\u9752\uff0c\u201c\u8fd9\u4e09\u4e2a\u6708\u6211\u628a\u9646\u5c0f\u51e4\u4ea4\u7ed9 \u4f60\uff01\u201d \u53f6\u7075\u54ac\u7740\u7259\uff0c\u5ffd\u7136\u4e5f\u8dfa\u4e86\u8dfa\u811a\uff0c\u51b2\u4e86\u51fa\u53bb\u51b2\u5230\u95e8\u53e3\uff0c\u53c8\u56de\u8fc7\u5934\uff0c\u72e0\u72e0\u7684\u76ef\u7740\u9646\u5c0f \u51e4\uff0c\u201c\u4f60\u542c\u7740\uff0c\u53ea\u8981\u4f60\u6562\u78b0\u4e00\u78b0\u522b\u7684\u5973\u4eba\u3001\uff0c\u6211\u5c31\u53bb\u5077\u2014\u767e\u4e2a\u7537\u4eba\u7ed9\u4f60\u770b\uff0c\u8ba9\u4f60\u6234\u4e00 \u767e\u9876\u7eff\u5e3d\u5b50\uff0c\uff0c\u5927\u5802\u91cc\u7684\u5bb4\u4f1a\u5df2\u6563\uff0c\u67f3\u9752\u9752\u53eb\u5979\u7684\u5c0f\u53a8\u623f\u51c6\u5907\u4e86\u51e0\u6837\u83dc\u3002 \u83dc\u5f88\u7cbe\u81f4\uff0c\u9152\u4e5f\u5f88\u597d\uff0c\u5979\u4e00\u5411\u662f\u4e2a\u5f88\u61c2\u5f97\u751f\u6d3b\u60c5\u8da3\u7684\u5973\u5979\u4e5f\u5f88\u4e86\u89e3\u7537\u4eba\u3002 \u9646\u5c0f\u51e4\u4e0d\u5f00\u53e3\uff0c\u5979\u4e5f\u5c31\u9ed8\u9ed8\u7684\u5728\u65c1\u8fb9\u966a\u7740\uff0c\u9646\u5c0f\u51e4\u7684\u9152\u676f\u7a7a\u4e86\uff0c\u5979\u5c31\u5012\u9152\u3002 \u83dc\u6ca1\u6709\u52a8\uff0c\u9152\u5374\u6d88\u8017\u5f97\u5f88\u5feb\u3002 \u9646\u5c0f\u51e4\u7ec8\u4e8e\u62ac\u8d77\u5934\uff0c\u51dd\u89c6\u7740\u5979\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u81ed\u9a82\u6211\u2018\u987f\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u9a82\u4f60\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u662f\u4e2a\u6df7\u86cb\uff0c\u56e0\u4e3a\u6211\u2026\u2026\u201d \u67f3\u9752\u9752\u4e0d\u8ba9\u4ed6\u518d\u8bf4\u4e0b\u53bb\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u7528\u4e0d\u7740\u4e3a\u6211\u96be\u53d7\uff0c\u6211\u5e74\u7eaa\u6bd4\u4f60\u5927\uff0c\u672c\u5c31\u6ca1 \u6709\u91ce\u5fc3\u8981\u5ac1\u7ed9\u4f60\uff0c\u6211\u53ea\u60f3\u505a\u4f60\u7684\u670b\u53cb\u3002\u201d \u5979\u7b11\u4e86\u7b11\uff0c\u7b11\u5f97\u98ce\u60c5\u4e07\u79cd\uff0c\u201c\u53ea\u8981\u4f60\u613f\u610f\uff0c\u6211\u751a\u81f3\u53ef\u4ee5\u505a\u4f60\u7684\u60c5\u5987\u3002\u201d \u9646\u5c0f\u51e4\u53ea\u6709\u82e6\u7b11\u3002 \u5982\u679c\u5979\u771f\u7684\u81ed\u9a82\u4ed6\u4e00\u987f\uff0c\u4ed6\u4e5f\u8bb8\u53cd\u800c\u4f1a\u89c9\u5f97\u597d\u53d7\u4e9b\uff0c\u5c31\u7b97\u7ed9\u4ed6\u51e0\u4e2a\u8033\u5149\uff0c\u4ed6\u90fd\u4e0d \u5728\u4e4e\u3002 \u67f3\u9752\u9752\u53c8\u9053\uff1a\u201c\u53ef\u662f\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u4e0d\u6562\u5192\u8fd9\u79cd\u9669\u7684\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5192\u4ec0\u4e48\u9669\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u6234\u7eff\u5e3d\u7684\u5371\u9669\uff0c\u90a3\u5c0f\u9b3c\u4e00\u5411\u8bf4\u5f97\u51fa\uff0c\u505a\u5f97\u5230\u3002\u201d \u5979\u53c8\u7b11\uff0c\u201c\u7b11\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u5979\u4e5f\u4e0d\u80fd\u7b97\u5c0f\u9b3c\u4e86\uff0c\u5979\u4eca\u5e74\u5df2\u5341\u4e03\uff0c\u6211\u5341\u4e03\u7684\u65f6\u5019\u5df2 \u7ecf\u5ac1\u4e86\u4eba\u3002\u201d \u9646\u5c0f\u51e4\u53c8\u5f00\u59cb\u5728\u559d\u95f7\u9152\u3002 \u67f3\u9752\u9752\u770b\u7740\u4ed6\u559d\u4e86\u51e0\u676f\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u5728\u60f3\u963f\u96ea\uff1f \u9646\u5c0f\u51e4\u7acb\u523b\u6447\u5934\u3002 \u67f3\u9752\u9752\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u5979\uff0c\u6211\u5012\u6709\u70b9\u4e3a\u5979\u62c5\u5fc3\uff0c\u5979\u4e00\u5411\u6700\u597d\u5f3a\uff0c\u6700\u8981\u9762\u5b50\uff0c\u4eca\u5929\u5728 \u5927\u5bb6\u9762\u524d\u4e22\u4e86\u8fd9\u4e48\u5927\u4e00\u4e2a\u9762\u5b50\uff0c\u6050\u6015\u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u95ee\uff0c\u201c\u6050\u6015\u600e\u4e48\u6837\uff1f\u201d \u67f3\u9752\u9752\u60f3\u8bf4\uff0c\u53c8\u5fcd\u4f4f\uff0c\u5176\u5b9e\u5979\u6839\u672c\u7528\u4e0d\u7740\u8bf4\u51fa\u6765\uff0c\u5979\u7684\u610f\u601d\u65e0\u8bba\u8c01\u90fd\u4e0d\u4f1a\u4e0d\u61c2\u3002 \u9646\u5c0f\u51e4\u5ffd\u7136\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u82e5\u6015\u5979\u4f1a\u53bb\u6b7b\uff0c\u4f60\u5c31\u9519\u4e86\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u54e6\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5979\u7edd\u4e0d\u662f\u90a3\u79cd\u60f3\u4e0d\u5f00\u7684\u5973\u4eba\uff0c\u5979\u8ddf\u6211\u4e5f\u6ca1\u6709\u5230\u90a3\u79cd\u5173\u7cfb\u3002\u201d \u67f3\u9752\u9752\u6ca1\u6709\u4e89\u8fa9\uff0c\u5979\u770b\u5f97\u51fa\u9646\u5c0f\u51e4\u5df2\u6709\u4e86\u51e0\u5206\u9152\u610f\uff0c\u4e5f\u6709\u4e86\u51e0\u5206\u6094\u610f\u3002 \u4ed6\u540e\u6094\u7684\u662f\u4ec0\u4e48\uff1f\u662f\u4e3a\u4e86\u4ed6\u5bf9\u897f\u95e8\u5439\u96ea\u505a\u7684\u4e8b\uff1f\u8fd8\u662f\u4e3a\u4e86\u53f6\u96ea\uff1f \u65e0\u8bba\u8c01\u62d2\u7edd\u4e86\u90a3\u4e48\u6837\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u90fd\u4f1a\u5fcd\u4e0d\u4f4f\u8981\u540e\u6094\u7684\u3002 \u4e5f\u8bb8\u4ed6\u540e\u6094\u7684\u53ea\u4e0d\u8fc7\u662f\u4ed6\u548c\u53f6\u7075\u7684\u5a5a\u4e8b\uff0c\u4ed6\u4eec\u5b9e\u5728\u4e0d\u80fd\u7b97\u662f\u5f88\u7406\u60f3\u7684\u4e00\u5bf9\u3002 \u67f3\u9752\u9752\u5fc3\u91cc\u53f9\u606f\u7740\uff0c\u53c8\u4e3a\u4ed6\u659f\u6ee1\u2014\u676f\uff0c\u591c\u5df2\u5f88\u6df1\u4e86\uff0c\u592a\u6e05\u9192\u53cd\u800c\u75db\u82e6\uff0c\u8fd8\u4e0d\u5982\u9189 \u4e86\u7684\u597d\u3002 \u6240\u4ee5\u5979\u81ea\u5df1\u4e5f\u659f\u6ee1\u2014\u676f\uff0c\u7a81\u542c\u5916\u9762\u6709\u4eba\u9053\uff1a\u201c\u7559\u2014\u676f\u7ed9\u6211\u3002\u201d \u8fdb\u6765\u7684\u5c45\u7136\u662f\u8868\u54e5\uff0c\u67f3\u9752\u9752\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4ece\u51e0\u65f6\u5f00\u59cb\u8ba4\u4e3a\u6211\u4f1a\u8bf7\u4f60\u559d\u9152\u7684\uff1f\u201d \u8868\u54e5\u7684\u795e\u8272\u5f88\u5947\u7279\uff0c\u547c\u5438\u5f88\u6025\u4fc3\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u6211\u672c\u4e0d\u662f\u6765\u559d\u9152\u7684\u3002 \u67f3\u9752\u9752\u9053\uff1a\u201c\u4f60\u60f3\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u8868\u54e5\u9053\uff1a\u201c\u6765\u62a5\u544a\u4e00\u4ef6\u6d88\u606f\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u73b0\u5728\u4f60\u4e3a\u4ec0\u4e48\u8981\u559d\uff1f\u201d \u8868\u54e5\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u6d88\u606f\u5b9e\u5728\u592a\u574f\u4e86\u3002\u201d \u574f\u6d88\u606f\u603b\u662f\u4f1a\u4ee4\u4eba\u60f3\u559d\u9152\uff0c\u542c\u7684\u4eba\u60f3\u559d\uff0c\u8bf4\u7684\u4eba\u66f4\u60f3\u559d\u3002 \u67f3\u9752\u9752\u7acb\u523b\u5c06\u81ea\u5df1\u624b\u91cc\u2014\u676f\u9152\u9012\u8fc7\u53bb\uff0c\u7b49\u4ed6\u559d\u5b8c\u624d\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u6d88\u606f\uff1f\u201d \u8868\u54e5\u9053\uff1a\u201c\u53f6\u96ea\u5df2\u5165\u4e86\u901a\u5929\u9601\u3002\u201d \u67f3\u9752\u9752\u8138\u4e0a\u7acb\u523b\u4e5f\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u8f6c\u8eab\u9762\u5bf9\u9646\u5c0f\u51e4\uff0c\u7f13\u7f13 \u9053\uff1a\u201c\u9519\u7684\u597d\u50cf\u4e0d\u662f\u6211\uff0c\u662f\u4f60\u3002\u201d \u201c\u901a\u5929\u9601\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u5730\u65b9\uff1f\u201d \u201c\u662f\u95f4\u6728\u5934\u5c4b\u5b50\uff0c\u5c31\u5728\u901a\u5929\u5d16\u4e0a\uff0c\u901a\u5929\u5d16\u5c31\u662f\u540e\u9762\u5c71\u5934\u7684\u90a3\u5757\u9ad8\u5d16\u3002\u201d \u201c\u6211\u597d\u50cf\u4ece\u6765\u6ca1\u6709\u770b\u89c1\u8fc7\u3002\u201d \u201c\u4f60\u5f53\u7136\u6ca1\u6709\u89c1\u8fc7\uff0c\u8fd9\u6728\u5c4b\u672c\u5c31\u662f\u4e34\u65f6\u8d77\u6765\u7684\u3002\u201d \u201c\u90a3\u91cc\u9762\u6709\u4ec0\u4e48\uff1f\u201d \u201c\u4ec0\u4e48\u90fd\u6ca1\u6709\uff0c\u53ea\u6709\u68fa\u6750\u548c\u6b7b\u4eba\u3002\u201d \u5e7d\u7075\u5c71\u5e84\u4e2d\u771f\u6b63\u7684\u6b7b\u4eba\u53ea\u6709\u4e00\u4e2a\u3002 \u201c\u76d6\u8fd9\u95f4\u6728\u5c4b\u662f\u4e3a\u4e86\u8981\u505c\u653e\u53f6\u5b64\u9e3f\u7684\u7075\u67a2\u3002\u201d \u201c\u4e0d\u662f\u4e3a\u4e86\u8981\u505c\u653e\uff0c\u662f\u4e3a\u4e86\u8981\u70e7\u4e86\u5b83\u3002\u201d \u9646\u5c0f\u51e4\u7684\u5fc3\u5df2\u6c89\u4e0b\u53bb\u3002 \u8868\u54e5\u9053\uff1a\u201c\u963f\u96ea\u5230\u90a3\u91cc\u53bb\uff0c\u597d\u50cf\u5c31\u662f\u4e3a\u4e86\u51c6\u5907\u8981\u548c\u5979\u54e5\u54e5\u846c\u5728\u2014\u8d77\uff0c\u706b\u846c\uff01\u201d \u9634\u6c89\u6c89\u7684\u591c\u8272\uff0c\u9634\u68ee\u68ee\u7684\u5c71\u5d16\uff0c\u90a3\u95f4\u5b64\u96f6\u96f6\u7684\u6728\u5c4b\u5728\u591c\u8272\u4e2d\u770b\u6765\uff0c\u5c31\u50cf\u662f\u6b7b\u7070\u8272 \u7684\u3002 \u5e73\u53f0\u822c\u7684\u5d16\u77f3\u4e0b\uff0c\u7ad9\u7740\u4e09\u4e2a\u4eba\uff0c\u6d77\u5947\u9614\uff0c\u7ba1\u5bb6\u5a46\uff0c\u8001\u5200\u628a\u5b50\u3002 \u5c71\u98ce\u5f3a\u52b2\uff0c\u4e09\u4e2a\u4eba\u7684\u8138\u8272\u5168\u90fd\u9634\u6c89\u5982\u591c\u8272\u3002 \u6728\u5c4b\u7684\u56db\u5468\uff0c\u5df2\u5806\u8d77\u4e86\u67af\u679d\u3002 \u9646\u5c0f\u51e4\u8ba9\u8868\u54e5\u548c\u67f3\u9752\u9752\u8d70\u8fc7\u53bb\u53c2\u52a0\u4ed6\u4eec\uff0c\u81ea\u5df1\u5374\u8fdc\u8fdc\u5c31\u505c\u4e0b\u6765\u3002 \u4ed6\u7684\u5fc3\u5f88\u4e71\uff0c\u4ed6\u5fc5\u987b\u5148\u51b7\u9759\u51b7\u9759\u3002 \u67f3\u9752\u9752\u5df2\u7ecf\u5728\u95ee\uff0c\u201c\u5979\u8fdb\u53bb\u4e86\u591a\u4e45\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u591f\u4e45\u4e86\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u8c01\u5148\u53d1\u73b0\u5979\u5728\u8fd9\u91cc\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u53d1\u73b0\uff0c\u662f\u5979\u8981\u6211\u6765\u7684\uff0c\u5979\u53eb\u5728\u8fd9\u91cc\u5b88\u591c\u7684\u4eba\u53bb\u53eb\u6211\uff0c\u56e0\u4e3a \u5979\u8fd8\u6709\u6700\u540e\u4e00\u53e5\u8bdd\u8981\u544a\u8bc9\u6211\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u5979\u8bf4\u4ec0\u4e48\uff1f\u201d \u8001\u5200\u628a\u5b50\u63e1\u7d27\u53cc\u62f3\uff0c\u9053\uff1a\u201c\u5979\u8981\u6211\u627e\u51fa\u771f\u51f6\uff0c\u4e3a\u5979\u54e5\u54e5\u590d\u4ec7Jo\u67f3\u9752\u9752\u9053\uff1a\u201c\u5979\u8bf4 \u8fd9\u662f\u5979\u6700\u540e\u4e00\u53e5\u8bdd\uff1f\u201d \u8001\u5200\u628a\u5b50\u70b9\u70b9\u5934\uff0c\u8138\u8272\u66f4\u6c89\u91cd\uff0c\u6697\u7136\u9053\uff1a\u201c\u5979\u5df2\u7ecf\u51c6\u5907\u6b7b\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u529d\u5979\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u5979\u8bf4\u53ea\u8981\u6211\u4e0a\u53bb\uff0c\u5979\u5c31\u7acb\u523b\u6b7b\u5728\u6211\u9762\u524d\u3002 \u67f3\u9752\u9752\u6ca1\u6709\u518d\u95ee\uff0c\u5979\u5f53\u7136\u4e5f\u77e5\u9053\u53f6\u96ea\u662f\u4e2a\u8bf4\u8bdd\u7b97\u6570\u7684\u4eba\uff0c\u800c\u4e14\u4ece\u6765\u4e0d\u4f1a\u56e0\u4e3a\u4efb\u4f55 \u4e8b\u6539\u53d8\u4e3b\u610f\u3002 \u98ce\u66f4\u51b7\uff0c\u4eff\u4f5b\u9690\u7ea6\u53ef\u4ee5\u542c\u89c1\u4e00\u9635\u9635\u54ed\u6ce3\u58f0\u3002 \u67f3\u9752\u9752\u5fcd\u4e0d\u4f4f\u673a\u4f36\u4f36\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u9053\uff1a\u201c\u6211\u4eec\u96be\u9053\u5c31\u8fd9\u4e48\u6837\u770b\u7740\u5979\u6b7b\uff1f\u201d \u8001\u5200\u628a\u5b50\u538b\u4f4e\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u6211\u6b63\u5728\u7b49\u4f60\u4eec\u6765\uff0c\u4f60\u4eec\u4e5f\u8bb8\u80fd\u6551\u5979\u3002 \u67f3\u9752\u9752\u9053\uff1a\u201c\u4f60\u8981\u6211\u4eec\u5077\u5077\u6e9c\u4e0a\u53bb\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u4eba\u7684\u8f7b\u529f\u6700\u9ad8\uff0c\u4e58\u7740\u98ce\u5927\u7684\u65f6\u5019k\u53bb\uff0c\u963f\u96ea\u7edd\u4e0d\u4f1a\u53d1 \u89c9\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u7136\u540e\u5462\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u8868\u54e5\u5148\u7ed5\u5230\u540e\u9762\u53bb\uff0c\u7834\u58c1\u800c\u5165\uff0c\u6211\u5728\u524d\u9762\u95e8\u53e3\u7b49\u7740\uff0c\u5979\u770b\u89c1\u8868\u54e5 \u65f6\uff0c\u5c31\u7b97\u4e0d\u51fa\u624b\u4e5f\u4f1a\u4e89\u5435\u8d77\u6765\u7684\uff0c\u4f60\u5c31\u8981\u7acb\u523b\u51b2\u8fdb\u53bb\u62b1\u7740\u5979\u3002\u201d \u67f3\u9752\u9752\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u8fd9\u6cd5\u5b50\u4e0d\u597d\u3002\u201d \u8001\u5200\u628a\u5b50\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u80fd\u60f3\u5f97\u51fa\u66f4\u597d\u7684\u6cd5\u5b50\uff1f\u201d \u67f3\u9752\u9752\u60f3\u4e0d\u51fa\uff0c\u6240\u4ee5\u5979\u53ea\u6709\u4e0a\u53bb\u3002 \u5979\u7684\u8f7b\u529f\u679c\u7136\u4e0d\u9519\uff0c\u8868\u54e5\u4e5f\u4e0d\u6bd4\u5979\u5dee\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4e24\u4e2a\u4eba\u7684\u786e\u90fd\u5df2\u53ef\u7b97\u662f\u9876\u5c16\u9ad8 \u624b\uff0c\u4e94\u516d\u4e08\u9ad8\u7684\u5c71\u5d16\uff0c\u4ed6\u4eec\u5f88\u5bb9\u6613\u5c31\u6500\u8d8a\u4e0a\u53bb\u3002 \u6728\u5c4b\u4e2d\u8fd8\u662f\u4e00\u7247\u9ed1\u6697\u6b7b\u5bc2\uff1b\u53f6\u96ea\u679c\u7136\u6ca1\u6709\u53d1\u73b0\u4ed6\u4eec\u7684\u884c\u52a8\u3002 \u67f3\u9752\u9752\u6084\u6084\u6253\u4e86\u4e2a\u624b\u5f0f\uff0c\u8868\u54e5\u5c31\u4ece\u540e\u9762\u7ed5\u4e86\u8fc7\u53bb\uff0c\u7136\u540e\u5c31\u662f\u201c\u8f70\u201d\u7684\u4e00\u54cd\u3002 \u7528\u6613\u71c3\u7684\u6728\u6599\u642d\u6210\u7684\u5c4b\u5b50\uff0c\u8981\u7834\u58c1\u800c\u5165\u5e76\u4e0d\u96be\u3002 \u53ef\u662f\u8fd9\u201c\u8f70\u201d\u7684\u4e00\u54cd\u540e\uff0c\u63a5\u7740\u7acb\u523b\u5c31\u662f\u2014\u58f0\u60e8\u547c\uff0c\u5728\u8fd9\u591c\u534a\u5bd2\u98ce\u4e2d\u542c\u6765\uff0c\u5206\u5916\u51c4 \u5389\u3002 \u591c\u8272\u4e2d\u9690\u7ea6\u4eff\u4f5b\u6709\u5251\u5149\u4e00\u95ea\uff0c\u4e00\u4e2a\u4eba\u4ece\u5c71\u5d16\u4e0a\u843d\u4e0b\u6765\uff0c\u91cd\u91cd\u8dcc\u5728\u5730\u4e0a\uff0c\u534a\u8fb9\u8eab\u5b50 \u9c9c\u8840\u6dcb\u6f13\uff0c\u7adf\u662f\u8868\u54e5\u3002 \u53ea\u542c\u53f6\u96ea\u7684\u58f0\u97f3\u4ece\u98ce\u4e2d\u4f20\u6765\uff0c\u201c\u82b1\u5be1\u5987\uff0c\u4f60\u8fd8\u4e0d\u8d70\uff0c\u6211\u5c31\u8981\u4f60\u966a\u6211\u4e00\u8d77\u6b7b\u3002 \u5979\u7684\u58f0\u97f3\u53c8\u5c16\u9510\uff0c\u53c8\u6025\u8e81\uff0c\u201c\u4f60\u6700\u597d\u56de\u53bb\u544a\u8bc9\u8001\u5200\u628a\u5b50\uff0c\u4ed6\u82e5\u4e0d\u60f3\u518d\u591a\u4f24\u4eba\u547d\uff0c \u6700\u597d\u5c31\u4e0d\u8981\u518d\u53eb\u4eba\u4e0a\u6765\uff0c\u53cd\u6b63\u6211\u662f\u7edd\u4e0b\u4f1a\u6d3b\u7740\u8d70\u51fa\u8fd9\u91cc\u7684\u3002\u201d \u7528\u4e0d\u7740\u67f3\u9752\u9752\u4f20\u8bdd\uff0c\u6bcf\u4e2a\u4eba\u90fd\u5df2\u542c\u89c1\u4e86\u5979\u7684\u8bdd\uff0c\u6bcf\u4e2a\u5b87\u90fd\u542c\u5f97\u6e05\u695a\u3002 \u8005\u5200\u628a\u5b50\u53cc\u62f3\u7d27\u63e1\uff0c\u76ee\u5149\u5200\u950b\u822c\u4ece\u7af9\u7b20\u540e\u77aa\u7740\u8868\u54e5\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u662f\u5df4\u5c71\u987e\u9053\u4eba \u7684\u5f92\u5f1f\uff0c\u4f60\u4e00\u5411\u8ba4\u4e3a\u81ea\u5df1\u6b66\u529f\u5f88\u4e0d\u9519\uff0c\u4f60\u4e3a\u4ec0\u4e48\u5982\u6b64\u4e0d\u4e2d\u7528\uff1f\u201d \u8868\u54e5\u63e1\u7d27\u80a9\u4e0a\u7684\u4f24\u53e3\uff0c\u6307\u7f1d\u95f4\u8fd8\u6709\u9c9c\u76bf\u4e0d\u505c\u7684\u6d8c\u51fa\uff0c\u989d\u89d2\u4e0a\u51b7\u6c57\u5927\u5982\u9ec4\u8c46\u3002 \u8fd9\u4e00\u5251\u65e0\u7591\u4f24\u5f97\u5f88\u91cd\u3002 \u8fc7\u4e86\u5f88\u4e45\uff0c\u4ed6\u624d\u80fd\u6323\u624e\u7740\u5f00\u53e3\uff0c\u201c\u5979\u597d\u50cf\u65e9\u5c31\u7b97\u51c6\u4e86\u6211\u7684\u884c\u52a8\uff0c\u6211\u4e00\u95ef\u8fdb\u53bb\uff0c\u5979 \u7684\u5251\u5df2\u5728\u90a3\u91cc\u7b49\u7740\u3002\u201d \u8001\u5200\u628a\u5b50\u5ffd\u7136\u4ef0\u9762\u53f9\u606f\uff0c\u9053\uff1a\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\u4f60\u4eec\u90fd\u4e0d\u5982\u5979\uff0c\u6e38\u9b42\u5df2\u6b7b\uff0c\u5c06\u519b\u91cd \u4f24\uff0c\u6211\u5df2\u5c11\u4e86\u4e24\u4e2a\u9ad8\u624b\uff0c\u82e5\u662f\u518d\u5c11\u4e86\u5979\u2026\u2026\u201d \u4ed6\u91cd\u91cd\u4e00\u8dfa\u811a\uff0c\u811a\u4e0b\u7684\u5c71\u77f3\u7acb\u523b\u788e\u88c2\u3002 \u5c31\u5728\u8fd9\u65f6\uff0c\u9ed1\u6697\u4e2d\u5ffd\u7136\u6709\u4eba\u9053\uff1a\u201c\u4e5f\u8bb8\u6211\u8fd8\u6709\u6cd5\u5b50\u6551\u5979\u3002\u201d \u6765\u7684\u662f\u72ec\u5b64\u7f8e\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u6709\u6cd5\u5b50\uff1f\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u72ec\u5b64\u7f8e\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u662f\u4e2a\u516d\u4eb2\u4e0d\u8ba4\u7684\u4eba\uff0c\u5f53\u7136\u7edd\u4e0d\u4f1a\u65e0\u7f18\u65e0\u6545\u6551\u4eba\u7684 ao\u4ed6\u7b11\u5f97\u5f88\u5351\u9119\uff0c\u53c8\u72e1\u733e\uff0c\u8001\u5200\u628a\u5b50\u76ef\u7740\u4ed6\u770b\u4e86\u5f88\u4e45\uff0c\u624d\u2019\u95ee\uff0c\u201c\u4f60\u6709\u4ec0\u4e48\u6761\u4ef6\u3002\u201d \u72ec\u5b64\u7f8e\u9053\uff1a\u201c\u6211\u7684\u6761\u4ef6\u5f88\u7b80\u5355\uff0c\u6211\u60f3\u8981\u4e2a\u8001\u5a46\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u8981\u8c01\uff1f\u201d \u72ec\u5b64\u7f8e\u9053\uff1a\u201c\u53f6\u5bb6\u59d0\u59b9\u3001\u82b1\u5be1\u5987\uff0c\u968f\u4fbf\u8c01\u90fd\u884c\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u53ea\u8981\u4f60\u7b54\u5e94\uff0c\u5b83\u5c31\u6709\u6548\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u53ea\u8981\u6709\u6548\uff0c\u6211\u5c31\u7b54\u5e94\u3002\u201d \u72ec\u5b64\u7f8e\u53c8\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u6211\u7684\u6cd5\u5b50\u4e5f\u5f88\u7b80\u5355\uff0c\u53ea\u8981\u628a\u9646\u5c0f\u51e4\u7ed1\u5230\u5d16\u4e0a\u53bb\uff0c\u6211\u53ef\u4ee5\u8bc1 \u660e\u4ed6\u5c31\u662f\u6740\u5bb3\u53f6\u5b64\u9e3f\u7684\u771f\u51f6\uff0c\u56e0\u4e3a\u5f53\u65f6\u6211\u5c31\u5728\u65c1\u8fb9\u770b\u7740\uff0c\u53f6\u59d1\u5a18\u542c\u4e86\u6211\u7684\u8bdd\uff0c\u4e00\u5b9a\u4f1a \u5fcd\u4e0d\u4f4f\u8981\u51b2\u51fa\u6765\u66ff\u5979\u54e5\u54e5\u590d\u4ec7\u7684\uff0c\u7b49\u5979\u4eb2\u624b\u6740\u4e86\u9646\u5c0f\u51e4\u540e\uff0c\u5f53\u7136\u5c31\u4e0d\u4f1a\u60f3\u6b7b\u4e86oH\u8001\u5200 \u628a\u5b50\u9759\u9759\u7684\u542c\u7740\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u9646\u5c0f\u51e4\u5c82\u975e\u662f\u4f60\u5e26\u6765\u7684\uff1f\u201d \u72ec\u5b64\u7f8e\u7b11\u9053\uff1a\u201c\u90a3\u65f6\u6211\u53ea\u4e0d\u5b88\u5076\u7136\u826f\u5fc3\u53d1\u73b0\u4e86\u4e00\u6b21\u800c\u5df2\uff0c\u6211\u6709\u826f\u5fc3\u7684\u65f6\u5019\u5e76\u4e0d \u591a\u3002\u201d \u8001\u5200\u628a\u5b50\u53c8\u6c89\u9ed8\u4e86\u5f88\u4e45\uff0c\u6162\u6162\u7684\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u6cd5\u5b50\u542c\u6765\u597d\u50cf\u5f88\u4e0d\u9519aU\u8fd9\u53e5 \u8bdd\u521a\u8bf4\u5b8c\uff0c\u4ed6\u5df2\u51fa\u624b\uff0c\u8f7b\u8f7b\u4e00\u5df4\u638c\u5c31\u5df2\u5c06\u72ec\u5b64\u7f8e\u6253\u5f97\u70c2\u6ce5\u822c\u762b\u5728\u5730\u4e0a\u3002 \u72ec\u7f8e\u5927\u53eb\uff0c\u201c\u6211\u8fd9\u6cd5\u5b50\u65e2\u7136\u4e0d\u9519\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6253\u6211\uff1f\u201d \u8001\u5200\u628a\u5b50\u51b7\u51b7\u9053\uff1a\u201c\u6cd5\u5b50\u867d\u4e0d\u9519\uff0c\u4f60\u8fd9\u4eba\u5374\u9519\u4e86\u3002\u201d \u4ed6\u7b2c\u4e8c\u6b21\u51fa\u624b\uff0c\u72ec\u5b64\u7f8e\u5c31\u5df2\u53eb\u4e0d\u51fa\uff0c\u4ed6\u7684\u51fa\u624b\u65e2\u4e0d\u592a\u5feb\uff0c\u4e5f\u4e0d\u592a\u91cd\uff0c\u4f46\u5374\u7edd\u5bf9\u51c6 \u786e\u6709\u6548\u3002 \u9646\u5c0f\u51e4\u8fd8\u662f\u8fdc\u8fdc\u7684\u7ad9\u7740\uff0c\u8001\u5200\u628a\u5b50\u5ffd\u7136\u8d70\u8fc7\u53bb\u62cd\u4e86\u62cd\u4ed61\u7684\u80a9\uff0c\u9053\uff1a\u201c\u4f60\u8ddf\u6211 \u6765\u3002\u201d \u5c71\u52a9\u540e\u66f4\u9ed1\u6697\uff0c\u8d70\u5230\u6700\u9ed1\u6697\u5904\u8001\u5200\u628a\u5b50\u624d\u505c\u4e0b\uff0c\u8f6c\u8eab\u9762\u5bf9\u9646\u5c0f\u51e4\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u72ec \u5b64\u7f8e\u7684\u6cd5\u5b50\u672c\u6765\u7684\u786e\u5f88\u6709\u6548\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u7528\uff1f\u201d \u9646\u516c\u98ce\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u77e5\u9053\u6211\u4e0d\u662f\u771f\u51f6\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4e0d\u5bf9ao\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u4e5f\u9700\u8981\u6211\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u5bf9\u4e86\u3002\u201d \u4ed6\u4eec\u5f7c\u6b64\u90fd\u77e5\u9053\u81ea\u5df1\u5728\u5bf9\u65b9\u9762\u524d\u5b8c\u5168\u4e0d\u5fc5\u8bf4\u8c0e\uff0c\u56e0\u4e3a\u4ed6\u4eec\u90fd\u662f\u5f88\u4e0d\u5bb9\u6613\u88ab\u6b3a\u9a97\u7684 \u4eba\uff0c\u8fd9\u4f7f\u5f97\u4ed6\u4eec\u4e4b\u95f4\u6709\u4e86\u79cd\u51e0\u4e4e\u5df2\u63a5\u8fd1\u53cb\u8c0a\u7684\u4e92\u76f8\u4e86\u89e3\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u6211\u5df2\u662f\u4e2a\u8001\u4eba\uff0c\u6211\u61c2\u5f97\u826f\u673a\u4e00\u5931\uff0c\u6c38\u4e0d\u518d\u6765\uff0c\u6240\u4ee5\u2026\u2026\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u9700\u8981\u6211\uff0c\u56e0\u4e3a\u4f60\u7684\u673a\u4f1a\u5df2\u5feb\u8981\u6765\u4e86JU\u8001\u5200\u628a\u5b50\u76f4\u89c6\u7740\u4ed6\uff0c\u7f13 \u7f13\u9053\uff1a\u201c\u6211\u4e5f\u9700\u8981\u53f6\u96ea\uff0c\u56e0\u4e3a\u6211\u8981\u505a\u7684\u662f\u4ef6\u5927\u4e8b\uff0c\u4f60\u4eec\u90fd\u5df2\u662f\u6211\u8fd9\u8ba1\u5212\u4e2d\u4e0d\u80fd\u7f3a\u5c11\u7684 \u4eba\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u8981\u6211\u53bb\u6551\u5979\uff1f\u201d \u8001\u5200\u628a\u5b50\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4e16\u4e0a\u5047\u5973\u53e3\u8fd8\u6709\u2014\u4e2a\u4eba\u80fd\u8ba9\u5979\u6d3b\u4e0b\u53bb\uff0c\u8fd9\u4eba\u5c31\u662f\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u597d\uff0c\u6211\u53bb\uff0c\u53ef\u662f\u6211\u4e5f\u6709\u6761\u4ef6\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u8981\u4f60\u7ed9\u6211\u4e8c\u5341\u56db\u65f6\u8fb0\uff0c\u5728\u8fd9\u671f\u9650\u4e2d\uff0c\u65e0\u8bba\u6211\u505a\u4ec0\u4e48\u4f60\u90fd\u4e0d\u80fd\u5e72 \u6d89\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u505a\u4e8b\u2014\u5411\u559c\u6b22\u7528\u4f60\u81ea\u5df1\u7684\u6cd5\u5b50\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ece\u73b0\u5728\u5f00\u59cb\uff0c\u6211\u4e0d\u8981\u4efb\u4f55\u4eba\u9017\u7559\u5728\u80fd\u591f\u770b\u5f97\u89c1\u6211\u7684\u5730\u65b9\uff0c\u53ea\u8981\u4f60\u7b54 \u5e94\uff0c\u4e24\u5929\u4e4b\u540e\uff0c\u6211\u4e00\u5b9a\u4f1a\u5e26\u5979\u53bb\u89c1\u4f60\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u90a3\u65f6\u5979\u8fd8\u6d3b\u7740\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u4fdd\u8bc1\u3002\u201d \u8001\u5200\u628a\u5b50\u4e0d\u518d\u8003\u8651\uff0c\u201c\u6211\u7b54\u5e94\u3002\u201d \u4eba\u90fd\u5df2\u8d70\u4e86\uff0c\u5c71\u5d16\u4e0a\u7a7a\u8361\u9634\u68ee\uff0c\u6b7b\u7070\u8272\u7684\u6728\u5c4b\u5728\u9ed1\u6697\u4e2d\u770b\u6765\u50cf\u662f\u5b64\u5bc2\u7684\u9b3c\u9b42\u3002 \u9646\u5c0f\u51e4\u8fce\u7740\u98ce\u8d70\u8fc7\u53bb\uff0c\u5c71\u98ce\u53c8\u6e7f\u53c8\u51b7\uff0c\u8fd9\u9b3c\u5730\u65b9\u4e3a\u4ec0\u4e48\u603b\u662f\u6709\u96fe\uff1f \u8fd8\u6ca1\u6709\u8d70\u5f97\u592a\u8fd1\uff0c\u6728\u5c4b\u91cc\u5df2\u4f20\u51fa\u53f6\u96ea\u7684\u58f0\u97f3\uff0c\u53c8\u6e7f\u53c8\u51b7\u58f0\u97f3\uff0c\u201c\u4ec0\u4e48\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u5e94\u8be5\u77e5\u9053\u6211\u662f\u4ec0\u4e48\u4eba\uff0c\u6211\u770b\u4e0d\u89c1\u4f60\uff0c\u4f60\u5374\u770b\u89c1\u6211\u3002\u201d \u6c89\u5bc2\u5f88\u4e45\u540e\uff0c\u56de\u7b54\u53ea\u6709\u4e00\u4e2a\u5b57\uff0c\u201c\u6eda\uff01 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\u89c1\u6211\uff1f\u201d \u56de\u7b54\u8fd8\u662f\u90a3\u4e2a\u5b57\uff0c\u201c\u6eda\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u60f3\u89c1\u6211\uff0c\u4e3a\u4ec0\u4e48\u4e00\u76f4\u8fd8\u5728\u7b49\u6211\uff1f\u201d \u6728\u5c4b\u91cc\u53c8\u662f\u4e00\u9635\u6c89\u5bc2\uff0c\u9646\u5c0f\u51e4\u63a5\u7740\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u8fdf\u65e9\u4e00\u5b9a\u4f1a\u6765\u7684\uff0c\u6240\u4ee5\u4f60\u6ca1\u6709 \u6b7b\u3002\u201d \u4ed6\u8bf4\u5f97\u5f88\u6162\uff0c\u8d70\u5f97\u5f88\u5feb\uff0c\u5ffd\u7136\u95f4\u5c31\u5230\u4e86\u6728\u5c4b\u95e8\u524d\uff1a\u201c\u6240\u4ee5\u6211\u73b0\u5728\u5c31\u8981\u63a8\u95e8\u8d70\u8fdb \u53bb\uff0c\u8fd9\u6b21\u6211\u4fdd\u8bc1\u9644\u8fd1\u7edd\u6ca1\u6709\u7b2c\u4e8c\u4e2a\u4ebaoH\u4ed6\u63a8\u5f00\u4e86\u95e8\u3002 \u6728\u5c4b\u91cc\u66f4\u9634\u68ee\u9ed1\u6697\uff0c\u53ea\u770b\u89c1\u5230\u4e00\u53cc\u53d1\u4eae\u7684\u773c\u775b\uff0c\u773c\u775b\u91cc\u5e26\u7740\u79cd\u65e0\u6cd5\u63cf\u53d9\u7684\u8868\u60c5\uff0c \u4e5f\u4e0d\u77e5\u662f\u60b2\u75db\uff1f\u662f\u4f24\u611f\uff1f\u8fd8\u662f\u4ec7\u6068\uff1f \u9646\u5c0f\u51e4\u8fdc\u8fdc\u505c\u4e0b\uff0c\u9053\uff1a\u201c\u4f60\u6ca1\u6709\u8bdd\u5bf9\u6211\u8bf4\uff1f\u201d \u54ed\u6ce3\u65e9\u5df2\u505c\u6b62\uff0c\u773c\u775b\u5374\u53c8\u6f6e\u6e7f\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u4e0d\u8bf4\u6211\u4e5f\u77e5\u9053\uff0c\u4f60\u8fd9\u4e48\u6837\u505a\u5e76\u4e0d\u662f\u5b8c\u5168\u4e3a\u4e86\u6211\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a \u4f60\u8981\u7684\u4e1c\u897f\uff0c\u4ece\u6ca1\u6709\u88ab\u4eba\u62a2\u8d70\u8fc7\u3002\u201d \u9ed1\u6697\u4e2d\u53c8\u6709\u5bd2\u5149\u95ea\u8d77\uff0c\u4eff\u4f5b\u662f\u5251\u950b\u3002 \u5979\u662f\u60f3\u6740\u4e86\u9646\u5c0f\u51e4\uff1f\u8fd8\u662f\u60f3\u6b7b\u5728\u9646\u5c0f\u51e4\u9762\u524d\uff1f \u9646\u5c0f\u51e4\u638c\u5fc3\u5df2\u634f\u8d77\u628a\u51b7\u6c57\uff0c\u8fd9\u4e00\u523b\u6b63\u662f\u6700\u91cd\u8981\u7684\u5173\u5934\uff0c\u53ea\u8981\u6709\u4e00\u70b9\u9519\u8bef\uff0c\u4ed6\u4eec\u4e24 \u4e2a\u4eba\u4e2d\u5c31\u81f3\u5c11\u6709\u4e00\u4e2a\u8981\u6b7b\u5728\u8fd9\u91cc\u3002 \u4ed6\u7edd\u4e0d\u80fd\u505a\u9519\u4e00\u4ef6\u4e8b\uff0c\u7edd\u4e0d\u80fd\u8bf4\u9519\u4e00\u4e2a\u5b57\u3002 \u9ed1\u6697\u4e2d\u5ffd\u7136\u53c8\u54cd\u8d77\u53f6\u96ea\u7684\u58f0\u97f3\uff0c\u201c\u6211\u8fd9\u4e48\u6837\u505a\uff0c\u53ea\u56e0\u4e3a\u4e16\u4e0a\u5df2\u6ca1\u6709\u4e00\u4e2a\u4eba\u503c\u5f97\u6211 \u6d3b\u4e0b\u53bb\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u4eba\uff0c\u81f3\u5c11\u8fd8\u6709\u4e00\u4e2a\u3002\u201d \u53f6\u96ea\u679c\u7136\u5fcd\u4e0d\u4f4f\u95ee\uff0c\u201c\u8c01\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u7236\u4eb2\u3002\u201d \u4ed6\u4e0d\u8ba9\u53f6\u96ea\u5f00\u53e3\uff0c\u5f88\u5feb\u7684\u63a5\u7740\u9053\uff1a\u201c\u4f60\u7236\u4eb2\u5e76\u6ca1\u6709\u6b7b\uff0c\u6211\u6628\u5929\u665a\u4e0a\u8fd8\u89c1\u8fc7\u4ed6\u3002\u201d \u53f6\u96ea\u5ffd\u7136\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u51ed\u4ec0\u4e48\u8981\u6211\u76f8\u4fe1\u4f60\u8fd9\u79cd\u9b3c\u8bdd\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u9b3c\u8bdd\uff0c\u73b0\u5728\u6211\u5c31\u53ef\u4ee5\u5e26\u4f60\u53bb\u627e\u4ed6\u3002\u201d \u53f6\u96ea\u5df2\u7ecf\u5728\u72b9\u7591\uff0c\u201c\u4f60\u80fd\u627e\u5f97\u5230\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5341\u4e8c\u4e2a\u65f6\u8fb0\u5185\u82e5\u627e\u4e0d\u5230\uff0c\u6211\u8d1f\u8d23\u518d\u9001\u4f60\u56de\u6765\uff0c\u8ba9\u4f60\u5b89\u5b89\u9759\u9759\u7684 \u6b7b\u3002\u201d \u65f6\u96ea\u7ec8\u4e8e\u88ab\u6253\u52a8\uff0c\u201c\u597d\uff0c\u6211\u5c31\u518d\u76f8\u4fe1\u4f60\u8fd9\u4e00\u6b21\u3002\u201d \u9646\u5c0f\u51e4\u677e\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u4e0d\u4f1a\u540e\u6094\u7684\u3002\u201d \u5ffd\u7136\u95f4\uff0c\u5bd2\u5149\u4e00\u95ea\uff0c\u51b0\u51b7\u7684\u5251\u950b\u5df2\u8feb\u5728\u7709\u776b\uff0c\u53f6\u96ea\u7684\u58f0\u97f3\u6bd4\u5251\u950b\u66f4\u51b7\uff0c\u201c\u8fd9\u6b21\u4f60 \u518d\u9a97\u6211\uff0c\u6211\u5c31\u8981\u4f60\u8ddf\u6211\u4e00\u8d77\u6b7b\uff01\u201d \u9ed1\u6697\u7684\u5c71\u8c37\uff0c\u5e7d\u79d8\u7684\u4e1b\u6797\uff0c\u5bf9\u9646\u5c0f\u51e4\u6765\u8bf4\u8fd9\u4e00\u5207\u90fd\u4e0d\u964c\u751f\uff0c\u5c31\u50cf\u662f\u4ed6\u8eab\u65c1\u7684\u5973\u4eba \u4e00\u6837\uff0c\u6709\u65f6\u867d\u7136\u5f88\u53ef\u6015\uff0c\u5374\u53c8\u6709\u79cd\u65e0\u6cd5\u6297\u62d2\u7684\u5438\u5f15\u529b\u3002 \u8fd9\u6b21\u4ed6\u6ca1\u6709\u8ff7\u8def\u3002 \u4ed6\u56de\u53bb\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u51c6\u5907\u518d\u6765\u3002 \u53f6\u96ea\u9ed8\u9ed8\u7684\u8d70\u5728\u4ed6\u8eab\u65c1\uff0c\u82cd\u767d\u7684\u8138\uff0c\u51b0\u51b7\u7684\u773c\u795e\uff0c\u663e\u7136\u5df2\u51b3\u5fc3\u8981\u8ddf\u4ed6\u4fdd\u6301\u4e00\u6bb5\u8ddd \u79bb\u3002 \u53ef\u662f\u5728\u8fd9\u79cd\u5e7d\u79d8\u9ed1\u9eef\u7684\u5c71\u6797\u91cc\uff0c\u65e0\u8bba\u4ec0\u4e48\u4e8b\u90fd\u4f1a\u6539\u53d8\u7684\u3002 \u4ed6\u4eec\u5df2\u8d70\u4e86\u5f88\u4e45\uff0c\u98ce\u4e2d\u53c8\u4f20\u6765\u6cbc\u6cfd\u7684\u6c14\u606f\uff0c\u9646\u5c0f\u51e4\u5ffd\u7136\u505c\u4e0b\u6765\uff0c\u9762\u5bf9\u7740\u5979\uff0c\u201c\u6628 \u5929\u6211\u5c31\u5728\u8fd9\u9644\u8fd1\u770b\u89c1\u4ed6\u7684\u3002\u201d \u53f6\u96ea\u9053\uff1a\u201c\u73b0\u5728\u4ed6\u7684\u4eba\u5462\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e0d\u77e5\u9053qo\u53f6\u96ea\u7684\u624b\u63e1\u7d27\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u4ed6\u5728\u524d\u9762\u7684\u6cbc\u6cfd\u91cc\uff0c\u53ef\u662f\u6211\u4eec\u4e00\u5b9a\u8981\u7b49\u5230\u5929\u4eae\u518d\u53bb\u627e\u3002\u201d \u4ed6\u5750\u4e0b\u6765\uff0c\u201c\u6211\u4eec\u5c31\u5728\u8fd9\u91cc\u7b49\u3002\u201d \u53f6\u96ea\u51b7\u51b7\u7684\u770b\u7740\u4ed6\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u8bf4\u8fc7\uff0c\u8fd9\u6b21\u4f60\u82e5\u518d\u9a97\u6211\u9646\u5c0f\u51e4\u6253\u65ad\u5979\u7684\u8bdd\uff0c \u201c\u6211\u4ece\u6765\u6ca1\u6709\u9a97\u8fc7\u4f60\uff0c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u6211\u4e0d\u80af\u9a97\u4f60\uff0c\u6240\u4ee5\u4f60\u624d\u6068\u6211\u3002\u201d \u53f6\u96ea\u8f6c\u8fc7\u5934\uff0c\u4e0d\u518d\u770b\u4ed6\uff0c\u51b7\u6f20\u7f8e\u4e3d\u7684\u773c\u775b\u91cc\u5ffd\u7136\u9732\u51fa\u5026\u610f\u5979\u7684\u786e\u5df2\u5f88\u75b2\u5026\uff0c\u8eab\u5fc3 \u90fd\u5f88\u75b2\u5026\uff0c\u53ef\u662f\u5979\u575a\u51b3\u4e0d\u80af\u5750\u4e0b\u53bb\uff0c\u5979\u4e00\u5b9a\u8981\u4fdd\u6301\u6e05\u9192\u3002 \u9646\u5c0f\u51e4\u5374\u5df2\u8eba\u5728\u67d4\u8f6f\u7684\u843d\u53f6\u4e0a\uff0c\u95ed\u8d77\u4e86\u773c\u775b\u3002 \u4ed6\u95ed\u4e0a\u773c\u775b\u540e\uff0c\u53f6\u96ea\u5c31\u5728\u77aa\u7740\u4ed6\uff0c\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5979\u7684\u5634\u5507\u5ffd\u7136\u5f00\u59cb\u53d1\u6296\uff0c\u7136 \u540e\u6574\u4e2a\u4eba\u90fd\u5728\u53d1\u6296\uff0c\u5c31\u4eff\u4f5b\u5ffd\u7136\u60f3\u8d77\u4ef6\u5f88\u53ef\u6015\u7684\u4e8b\u3002 \u5979\u7528\u529b\u54ac\u7740\u5634\u5507\uff0c\u5c3d\u529b\u60f3\u63a7\u5236\u81ea\u5df1\uff0c\u600e\u5948\u8fd9\u5730\u65b9\u5b9e\u5728\u592a\u9759\uff0c\u9759\u5f97\u8ba9\u4eba\u53d1\u75af\uff0c\u5979\u60f3 \u5230\u7684\u4e8b\u6070\u5de7\u53c8\u662f\u4efb\u4f55\u5973\u4eba\u90fd\u4e0d\u80fd\u5fcd\u53d7\u7684\u3002 \u5979\u5ffd\u7136\u51b2\u8fc7\u53bb\uff0c\u4e00\u811a\u8e22\u5728\u9646\u5c0f\u51e4\u7684\u808b\u9aa8\u4e0a\uff0c\u5636\u58f0\u9053\uff1a\u201c\u6211\u6068\u4f60\uff0c\u6211\u6068\u4f60\u2026\u2026\u201d \u9646\u5c0f\u51e4\u7ec8\u4e8e\u5f20\u5f00\u773c\uff0c\u5403\u60ca\u7684\u770b\u7740\u5979\u3002 \u53f6\u96ea\u5598\u606f\u7740\u9053\uff1a\u6628\u5929\u665a\u4e0a\u4f60\u8ddf\u6211\u59b9\u59b9\u4e00\u5b9a\u5c31\u5728\u8fd9\u91cc\uff0c\u4eca\u5929\u4f60\u53c8\u5e26\u6211\u6765\uff0c\u4f60\u2026\u2026 \u4f60\u2026\u2026\u201d \u5979\u7684\u58f0\u97f3\u5636\u54d1\uff0c\u773c\u775b\u91cc\u4f3c\u5df2\u9732\u51fa\u75af\u72c2\u4e4b\u8272\uff0c\u5ffd\u7136\u6251\u4e0b\u53bb\u53bb\u627c\u9646\u5c0f\u51e4\u7684\u54bd\u5589\u3002 \u9646\u5c0f\u51e4\u53ea\u6709\u6349\u4f4f\u5979\u7684\u624b\uff0c\u5979\u7528\u529b\uff0c\u4ed6\u53ea\u6709\u66f4\u7528\u529b\u3002 \u4e24\u4e2a\u4eba\u5728\u67d4\u8f6f\u7684\u843d\u53f6\u4e0a\u4e0d\u505c\u7ffb\u6eda\u6323\u624e\uff0c\u9646\u5c0f\u51e4\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u538b\u5728\u5979\u8eab\u4e0a\u3002 \u5979\u7684\u5598\u606f\u5267\u70c8\uff0c\u8eab\u5b50\u5374\u6bd4\u843d\u53f6\u66f4\u67d4\u8f6f\uff0c\u5979\u5df2\u7528\u5c3d\u4e86\u6240\u6709\u7684\u529b\u91cf\u3002 \u7136\u540e\u5979\u5c31\u5ffd\u7136\u5b89\u9759\u4e86\u4e0b\u6765\uff0c\u653e\u5f03\u4e86\u4e00\u5207\u6323\u624e\u548c\u53cd\u6297\uff0c\u7b49\u5979\u518d\u5f20\u5f00\u773c\u770b\u9646\u5c0f\u51e4\u65f6\uff0c \u773c\u775b\u91cc\u5df2\u5145\u6ee1\u6cea\u6c34\u3002 \u5929\u5730\u95f4\u5982\u6b64\u5b89\u9759\uff0c\u5982\u6b64\u9ed1\u6697\uff0c\u4ed6\u4eec\u4e4b\u95f4\u7684\u8ddd\u79bb\u5982\u6b64\u63a5\u8fd1\u3002 \u9646\u5c0f\u51e4\u7684\u5fc3\u5ffd\u7136\u53d8\u5f97\u50cf\u662f\u871c\u7cd6\u4e2d\u7684\u679c\u5b50\u822c\u8f6f\u5316\u4e86\uff0c\u6240\u6709\u7684\u75db\u82e6\u548c\u4ec7\u6068\uff0c\u5728\u8fd9\u4e00\u77ac \u95f4\u90fd\u5df2\u88ab\u9057\u5fd8\u3002 \u6cea\u6c34\u6d8c\u51fa\uff0c\u6d41\u8fc7\u5979\u82cd\u767d\u7684\u9762\u988a\uff0c\u4ed6\u6b63\u60f3\u7528\u81ea\u5df1\u5e72\u71e5\u7684\u5634\u5507\u53bb\u5438\u5e72\u3002 \u5c31\u5728\u8fd9\u65f6\uff0c\u4ece\u6cbc\u6cfd\u90a3\u8fb9\u5439\u6765\u7684\u51b7\u98ce\u4e2d\uff0c\u5ffd\u7136\u5e26\u6765\u4e86\u4e00\u9635\u6b4c\u58f0\u3002 \u60b2\u62a2\u7684\u6b4c\u58f0\uff0c\u8db3\u4ee5\u4ee4\u4eba\u60f3\u8d77\u6240\u6709\u7684\u75db\u82e6\u548c\u4ec7\u6068\u3002 \u53f6\u96ea\u7684\u547c\u5438\u505c\u987f\uff0c\u201c\u662f\u4ed6\uff1f\u201d \u9646\u5c0f\u51e4\u5728\u5fc3\u91cc\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u597d\u50cf\u662f\u7684\u3002\u201d \u53f6\u96ea\u53c8\u54ac\u8d77\u5634\u5507\uff0c\u201c\u4e5f\u8bb8\u4ed6\u77e5\u9053\u6211\u4eec\u5df2\u6765\u4e86\uff0c\u6b63\u5728\u53eb\u6211\u4eec\u9646\u5c0f\u51e4\u9ed8\u9ed8\u7684\u7ad9\u8d77\u6765\uff0c \u62c9\u8d77\u4e86\u5979\u7684\u624b\uff0c\u5c31\u597d\u50cf\u4ece\u6c34\u91cc\u62c9\u8d77\u4e2a\u51e0\u4e4e\u88ab\u6df9\u6b7b\u7684\u4eba\u3002 \u5728\u4ed6\u7684\u611f\u89c9\u4e2d\uff0c\u8fd9\u4e2a\u51e0\u4e4e\u88ab\u6df9\u6b7b\u7684\u5e76\u4e0d\u662f\u53f6\u96ea\uff0c\u800c\u662f\u4ed6\u81ea\u5df1\u3002 \u9664\u4e86\u70c2\u6ce5\u5916\uff0c\u6cbc\u6cfd\u91cc\u8fd8\u6709\u4ec0\u4e48\uff1f\u8150\u70c2\u7684\u6811\u53f6\u548c\u6bd2\u8349\uff0c\u5d29\u843d\u7684\u5ca9\u77f3\uff0c\u65e0\u6570\u79cd\u4e0d\u77e5\u540d\u7684 \u6606\u866b\u548c\u6bd2\u86c7\u3002\u5438\u8840\u7684\u868a\u86d4\u548c\u8682\u8757\u5728\u8fd9\u65e0\u5947\u4e0d\u6709\u7684\u6cbc\u6cfd\u91cc\uff0c\u4f60\u751a\u81f3\u53ef\u4ee5\u627e\u5230\u6210\u5343\u4e0a\u767e\u79cd \u7a00\u5947\u53e4\u602a\u7684\u4e1c\u897f\uff0c\u800c\u4e14\u53ef\u4ee5\u4fdd\u8bc1\u7edd\u6ca1\u6709\u2014\u79cd\u4e0d\u662f\u4ee4\u4eba\u4f5c\u6076\u7684\u3002 \u53ef\u662f\u5728\u9ed1\u6697\u4e2d\u770b\u6765\uff0c\u8fd9\u4ee4\u4eba\u4f5c\u6076\u7684\u6cbc\u6cfd\u5374\u5ffd\u7136\u53d8\u5f97\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u7f8e\uff0c\u9664\u4e86\u90a3\u4e00\u9635 \u9635\u8fde\u9ed1\u6697\u90fd\u63a9\u9970\u4e0d\u4e86\u7684\u6076\u81ed\u5916\uff0c\u7f8e\u5f97\u51e0\u4e4e\u5c31\u50cf\u662f\u4e2a\u795e\u79d8\u800c\u5b81\u9759\u7684\u6e56\u6cca\u3002 \u60b2\u6b4c\u5df2\u505c\u4e0b\uff0c\u9646\u5c0f\u51e4\u4e5f\u6ca1\u6709\u518d\u5f80\u524d\u8d70\u3002 \u4ed6\u4e0d\u5f97\u4e0d\u505c\u4e0b\u6765\uff0c\u56e0\u4e3a\u4ed6\u521a\u624d\u5df2\u4e00\u811a\u8e29\u5165\u6e7f\u6ce5\u91cc\uff0c\u6574\u4e2a\u4eba\u90fd\u9669\u4e9b\u88ab\u5438\u4e0b\u53bb\u3002 \u5c31\u50cf\u662f\u7f6a\u6076\u4e00\u6837\uff0c\u6cbc\u6cfd\u91cc\u4eff\u4f5b\u4e5f\u6709\u79cd\u90aa\u6deb\u7684\u5438\u529b\uff0c\u53ea\u8981\u4f60\u2014\u9677\u4e0b\u53bb\uff0c\u5c31\u53ea\u6709\u6c89\u6ca6 \u5230\u5e95\u3002 \u53f6\u96ea\u7684\u8138\u8272\u66f4\u82cd\u767d\uff0c\u201c\u4f60\u8bf4\u4ed6\u8fd9\u4e9b\u5e74\u6765\u4e00\u76f4\u90fd\u8eb2\u5728\u8fd9\u91cc\uff1f\u201d \u9646\u5c0f\u51e4\u70b9\u70b9\u5934\u3002 \u53f6\u96ea\u9053\uff1a\u201c\u4ed6\u600e\u4e48\u80fd\u5728\u8fd9\u79cd\u5730\u65b9\u6d3b\u4e0b\u53bb\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4e0d\u60f3\u6b7b\u3002\u201d \u4ed6\u7684\u58f0\u97f3\u4e2d\u4e5f\u5e26\u7740\u4f24\u611f\uff0c\u201c\u4e00\u4e2a\u4eba\u82e5\u662f\u771f\u7684\u60f3\u6d3b\u4e0b\u53bb\uff0c\u65e0\u8bba\u75db\u82e6\u591a\u5927\u90fd\u53ef\u4ee5\u5fcd\u53d7 \u7684\u3002\u201d \u8fd9\u662f\u53e5\u5f88\u7b80\u5355\u7684\u8bdd\uff0c\u4f46\u5374\u6709\u5f88\u590d\u6742\u6df1\u5965\u7684\u9053\u7406\uff0c\u53ea\u6709\u9971\u5c1d\u75db\u82e6\u7ecf\u9a8c\u7684\u4eba\u624d\u80fd\u4e86 \u89e3\u3002 \u9ed1\u6697\u4e2d\u6709\u4eba\u5728\u53f9\u606f\uff0c\u201c\u4f60\u8bf4\u5f97\u4e0d\u9519\uff0c\u5374\u505a\u9519\u4e86\uff0c\u4f60\u4e0d\u8be5\u5e26\u522b\u4eba\u6765\u7684\uff0c\uff0c\u5636\u54d1\u82e6\u6da9 \u7684\u58f0\u97f3\u542c\u6765\u5e76\u4e0d\u964c\u751f\uff0c\u53f6\u96ea\u624b\u5df2\u51b0\u51b7\u3002 \u9646\u5c0f\u51e4\u7d27\u63e1\u4ef6\u5979\u7684\u624b\u3001\u9053\uff1a\u201c\u8fd9\u4e0d\u662f\u522b\u4eba\u3002\u662f\u4f60\u7684\u5973\u513f\u3002\u201d \u770b\u4e0d\u89c1\u4eba\uff0c\u542c\u4e0d\u89c1\u56de\u5e94\uff0c\u4ed6\u9762\u5bf9\u7740\u9ed1\u6697\u7684\u6cbc\u6cfd\uff0c\u5927\u58f0\u63a5\u7740\u9053\uff1a\u201c\u4f60\u867d\u7136\u4e0d\u60f3\u8ba9\u5979 \u770b\u89c1\u4f60\uff0c\u4f46\u662f\u4f60\u81f3\u5c11\u5e94\u8be5\u770b\u770b\u5979\uff0c\u5979\u5df2\u7ecf\u957f\u5927\u4e86\u3002\u201d \u5f71\u5b50\u58f0\u97f3\u5ffd\u7136\u6253\u65ad\u4ed6\u7684\u8bdd\uff0c\u201c\u5979\u662f\u4e0d\u662f\u8fd8\u50cf\u4ee5\u524d\u90a3\u4e48\u6837\uff0c\u559c\u6b22\u2014\u4e2a\u4eba\u8eb2\u5728\u9ed1\u623f \u91cc\uff0c\u8ba9\u522b\u4eba\u627e\u4e0d\u5230\u5979\u3002\u201d \u8fd9\u662f\u5979\u7684\u79d8\u5bc6\uff0c\u5979\u5929\u751f\u5c31\u6709\u4e00\u53cc\u80fd\u4f4f\u9ed1\u6697\u4e2d\u89c6\u7269\u7684\u773c\u775b\u3002 \u5979\u559c\u6b22\u8eb2\u5728\u9ed1\u6697\u91cc\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u522b\u4eba\u770b\u4e0d\u89c1\u5979\uff0c\u5979\u5374\u80fd\u770b\u5f97\u89c1\u522b\u4eba\u3002 \u77e5\u9053\u8fd9\u79d8\u5bc6\u7684\u4eba\u5e76\u4e0d\u591a\uff0c\u5979\u8eab\u5b50\u5ffd\u7136\u62bd\u7d27\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u5df2\u542c\u51fa\u4ed6\u662f\u51c6\uff1f\u201d \u53f6\u96ea\u70b9\u70b9\u5934\uff0c\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u6bd4\u6211\u770b\u770b\u4f60\uff0c\u6211\u5c31\u6b7b\u5728\u8fd9\u91cc\u3002 \u53c8\u662f\u4e00\u9635\u9759\u5bc2\uff0c\u9ed1\u6697\u4e2d\u7ec8\u4e8e\u51fa\u73b0\u4e86\u4e00\u56e2\u9ed1\u5f71\uff0c\u7adf\u662f\u5f62\u5f0f\u5947\u7279\u7684\u8239\u5c4b\uff0c\u4e0d\u4f46\u53ef\u4ee5\u98d8 \u6d6e\u5728\u6cbc\u6cfd\u4e0a\uff0c\u8fd8\u53ef\u4ee5\u884c\u8d70\u79fb\u52a8\u3002 \u201c\u4f60\u2014\u5b9a\u8981\u89c1\u6211\uff1f\u201d \u201c\u4e00\u5b9a\u3002\u201d\u53f6\u96ea\u56de\u7b54\u5f97\u5f88\u575a\u51b3\u3002 \u201c\u9646\u5c0f\u51e4\uff0c\u4f60\u4e0d\u8be5\u5e26\u5979\u6765\u7684\uff0c\u771f\u7684\u4e0d\u8be5\u3002\u201d \u5f71\u5b50\u5728\u53f9\u606f\uff0c\u6ca1\u6709\u4eba\u80fd\u6bd4\u4ed6\u66f4\u5382\u89e3\u4ed6\u7684\u4ea5\u513f\u7684\u9a84\u50b2\u548c\u5014\u5f3a\u3002 \u201c\u6211\u53ef\u4ee5\u8ba9\u4f60\u518d\u89c1\u6211\u4e00\u9762\uff0c\u4f46\u662f\u4f60\u20146\u5b9a\u4f1a\u540e\u6094\u7684\uff0c\u56e0\u4e3a\u6211\u5df2\u4e0d\u662f\u4ece\u524d\u2026\u2026\u201d \u53f6\u96ea\u5927\u58f0\u9053\uff1a\u201c\u65e0\u8bba\u4f60\u53d8\u6210\u4ec0\u4e48\u6837\u5b50\uff0c\u4f60\u90fd\u662f\u6211\u7239\uff0c\u5728\u6211\u5fc3\u91cc\uff0c\u4f60\u6c34\u8fdc\u90fd\u4e0d\u4f1a\u53d8 \u7684\uff0c\u4f60\u6c38\u8fdc\u90fd\u662f\u5929\u4e0b\u6700\u82f1\u4fca\u7684\uff0c\u5bf9\u6211\u6700\u597d\u7684\u7537\u4eba\u3002 \u98d8\u6d6e\u79fb\u52a8\u7684\u822c\u5c4b\u5df2\u6e10\u6e10\u8fd1\u4e86\uff0c\u5230\u4e86\u4e24\u4e08\u4e4b\u5185\uff0c\u53f6\u96ea\u5c31\u7eb5\u8eab\u8dc3\u4e86\u4e0a\u53bb\u3002 \u9646\u5c0f\u51e4\u6ca1\u6709\u62e6\u963b\uff0c\u4ed6\u770b\u5f97\u51fa\u4ed6\u4eec\u7236\u5973\u4e4b\u95f4\u5fc5\u5b9a\u6709\u6781\u6df1\u539a\u5bc6\u5207\u7684\u611f\u60c5\u3002 \u4ed6\u5ffd\u7136\u60f3\u5230\u81ea\u5df1\u7684\u7236\u6bcd\uff0c\u60f3\u5230\u4ed6\u81ea\u5df1\u8fd9\u2014\u751f\u4e2d\u7684\u5b64\u72ec\u548c\u5bc2\u5bde\u3002\u4e00\u58f0\u60ca\u547c\uff0c\u6253\u65ad\u4e86 \u4ed6\u7684\u601d\u7eea\u3002 \u547c\u58f0\u662f\u4ece\u8239\u5c4b\u4e2d\u4f20\u51fa\u7684\uff0c\u662f\u53f6\u96ea\u7684\u58f0\u97f3\uff0c\u8239\u5c4b\u53c8\u98d8\u8d70\u4e86\uff0c\u6e10\u6e10\u53c8\u5c06\u6d88\u5931\u5728\u9ed1\u6697 \u4e2d\uff0c\u3002 \u9646\u5c0f\u51e4\u5931\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u5e26\u5979\u8d70\u3002 \u5f71\u5b50\u5728\u7b11\uff0c\u201c\u5979\u65e2\u7136\u662f\u6211\u5973\u513f\uff0c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u5e26\u5979\u8d70\uff1f\u201d \u7b11\u58f0\u4e2d\u5145\u6ee1\u4e86\u8ba5\u6d88\u6076\u6bd2\u4e4b\u610f\u3002 \u9646\u5c0f\u51e4\u5168\u8eab\u51b0\u51b7\uff0c\u4ed6\u5ffd\u7136\u53d1\u73b0\u4e86\u4e00\u4ef6\u53ef\u6015\u7684\u4e8b\uff0c\u201c\u4f60\u4e0d\u662f\u5979\u7684\u7236\u4eb2\uff01 \u5f71\u5b50\u6162\u58f0\u800c\u541f\uff1a\u201c\u6e2d\u6c34\u4e4b\u4e1c\uff0c\u7389\u6811\u4e34\u98ce\u2026\u2026\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5c31\u662f\u2018\u7389\u6811\u5251\u5ba2\u2019\u53f6\u51cc\u98ce\uff0c\u4f46\u4f60\u5374\u4e0d\u662f\u5979\u7684\u7236\u4eb2\u3002\u201d \u5f71\u5b50\u5927\u7b11\uff0c\u201c\u4e0d\u7ba1\u6211\u662f\u5979\u7684\u4ec0\u4e48\u4eba\uff0c\u53cd\u6b63\u6211\u5df2\u5c06\u5979\u5e26\u8d70\uff0c\u56de\u53bb\u544a\u8bc9\u8001\u5200\u628a\u5b50\uff0c\u4ed6 \u82e5\u60f3\u8981\u4eba\uff0c\u53eb\u4ed6\u81ea\u5df1\u6765\u8981\u3002\u201d \u7b11\u58f0\u6e10\u8fdc\uff0c\u8239\u5c4b\u4e5f\u4e0d\u89c1\u4e86\uff0c\u795e\u79d8\u7684\u6cbc\u6cfd\u53c8\u6062\u590d\u4e86\u5b83\u7684\u9ed1\u6697\u5b81\u9759\u3002 \u9646\u5c0f\u51e4\u6c34\u7acb\u5728\u9ed1\u6697\u4e2d\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u957f\u957f\u53f9\u606f\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u5fc5\u56de\u53bb\u544a\u8bc9\u4f60\u3002\u4ed6 \u8bf4\u7684\u8bdd\uff0c\u4f60\u6bcf\u4e2a\u5b57\u90fd\u5e94\u8be5\u542c\u5f97\u5f88\u6e05\u695a\u201d \u4ed6\u5e76\u4e0d\u662f\u81ea\u8a00\u81ea\u8bed\uff0c\u8239\u5c4b\u8fdc\u53bb\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u77e5\u9053\u8001\u5200\u628a\u5b50\u5df2\u5230\u4e86\u4ed6\u8eab\u540e\u3002 \u4ed6\u7528\u4e0d\u7740\u56de\u5934\u53bb\u770b\u5c31\u5df2\u77e5\u9053\u3002 \u8001\u5200\u628a\u5b50\u679c\u7136\u6765\u4e86\uff0c\u4e5f\u957f\u957f\u53f9\u606f\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4ed6\u8bf4\u7684\u6211\u5168\u90fd\u542c\u89c1\uff0c\u53ef\u662f\u6211\u4e00\u76f4\u8ddf \u4f60\u4fdd\u6301\u7740\u5f88\u8fdc\u7684\u8ddd\u79bb\uff0c\u4e5f\u6ca1\u6709\u5e72\u6d89\u4f60\u7684\u884c\u52a8\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u4e2a\u8a00\u800c\u6709\u4fe1\u7684\u4eba\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u8fd8\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u51e4\u970d\u7136\u8f6c\u8eab\uff0c\u76ef\u7740\u4ed6\uff0c\u201c\u963f\u96ea\u5e76\u4e0d\u662f\u53f6\u51cc\u98ce\u7684\u5973\u513f\uff0c\u662f\u4f60\u7684\u3002 \u8001\u5200\u628a\u5b50\u65e2\u4e0d\u5426\u8ba4\uff0c\u4e5f\u6ca1\u6709\u627f\u8ba4\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u53f6\u51cc\u98ce\u77e5\u9053\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u6240\u4ee5\u4f60\u624d\u6740\u4ed6\u3002\u201d \u8001\u5200\u628a\u5b50\u7b11\u4e86\u7b11\uff0c\u7b11\u58f0\u8270\u6da9\u9053\uff1a\u201c\u6211\u60f3\u4e0d\u5230\u4ed6\u5c45\u7136\u6ca1\u6709\u6b7b\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u6d3b\u7740\u867d\u7136\u6bd4\u6b7b\u66f4\u75db\u82e6\uff0c\u5374\u4e00\u76f4\u54ac\u7740\u7259\u5fcd\u53d7\uff0c\uff0c\u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u56e0 \u4e3a\u4ed6\u8981\u590d\u4ec7\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f46\u662f\u4ed6\u4e0d\u6562\u53bb\u627e\u4f60\uff0c\u53ea\u6709\u7528\u8fd9\u6cd5\u5b50\u8981\u4f60\u53bb\u627e\u4ed6\uff0c\u8fd9\u5730\u533a\u4ed6\u6bd4\u4f60\u719f\uff0c \u53c8\u6709\u963f\u96ea\u505a\u4eba\u8d28\uff0c\u4ed6\u7684\u673a\u4f1a\u8fdc\u6bd4\u4f60\u597d\u5f97\u591a\u3002 \u8001\u5200\u628a\u5b50\u51b7\u51b7\u9053\uff1a\u201c\u6211\u672c\u6765\u4ee5\u4e3a\u4f60\u7edd\u4e0d\u4f1a\u4e0a\u5f53\u7684\uff0c\u60f3\u4e0d\u5230\u7ed3\u679c\u8fd8\u662f\u53d7\u4e86\u522b\u4eba\u7684\u5229 \u7528\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5e78\u597d\u6211\u4eec\u7684\u671f\u9650\u8fd8\u6ca1\u6709\u5230\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u6709\u628a\u63e1\u5728\u9650\u671f\u4e4b\u524d\u628a\u5979\u627e\u56de\u6765\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u6ca1\u6709\u628a\u63e1\uff0c\u4f46\u662f\u6211\u2014\u5b9a\u8981\u53bb\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u51c6\u5907\u600e\u4e48\u8d70\uff1f\u50cf\u6ce5\u9cc5\u4e00\u6837\u4ece\u70c2\u6ce5\u4e2d\u94bb\u8fc7\u53bb\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u53ef\u4ee5\u505a\u4e2a\u6728\u7b4f\u3002\u201d \u8001\u5200\u628a\u5b50\u6c89\u541f\u7740\uff0c\u9053\uff1a\u201c\u4f60\u505a\u7684\u6728\u7b4f\u80fd\u8f7d\u5f97\u52a8\u4e24\u4e2a\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ea\u6709\u4e24\u4e2a\u4eba\u4e00\u8d77\u52a8\u624b\u505a\u7684\u6728\u7b4f\uff0c\u624d\u80fd\u8f7d\u5f97\u52a8\u4e24\u4e2a\u4eba\u3002\u201d \u8001\u5200\u628a\u5b50\u7b11\u4e86\uff0c\u201c\u770b\u6765\u8fd9\u4e2a\u4eba\u5012\u771f\u662f\u4ece\u6765\u4e0d\u80af\u5403\u4e8f\u7684\u3002\u201d \u6cbc\u6cfd\u65c1\u672c\u6709\u4e1b\u6797\uff0c\u4e24\u4e2a\u4eba\u4e00\u8d77\u52a8\u624b\uff0c\u7247\u523b\u95f4\u5c31\u780d\u5012\u4e86\u5341\u4e03\u516b\u88f8\u6811\u4e0d\u662f\u7528\u5200\u780d\uff0c\u662f \u7528\u624b\u780d\u3002 \u8001\u5200\u628a\u2606\u5b50\u9053\uff1a\u201c\u4f60\u6765\u5265\u6811\u4e0a\u7684\u6821\u53f6\uff0c\u6211\u53bb\u627e\u7ef3\u5b50\u3002\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u8ddf\u4f60\u8fd9\u79cd\u4eba\u5728\u4e00\u8d77\u505a\u4e8b\uff0c\u60f3\u4e0d\u5403\u4e8f\u90fd\u4e0d\u884c\u201d \u4ed6\u867d\u7136\u660e\u77e5\u81ea\u5df1\u7684\u5dee\u4f7f\u6bd4\u8f83\u82e6\uff0c\u4e5f\u53ea\u6709\u8ba4\u547d\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u77e5\u9053\u8981\u5230\u54ea\u91cc\u53bb\u624d\u80fd\u627e\u5f97 \u5230\u7ef3\u5b50\u3002 \u8001\u5200\u628a\u5b50\u4e5f\u540c\u6837\u627e\u4e0d\u5230\uff0c\u4ed6\u521a\u4fef\u4e0b\u8eab\uff0c\u8001\u5200\u628a\u5b50\u7684\u638c\u950b\u5df2\u5207\u5728\u4ed6\u540e\u9888\u4e0a\uff0c\u4ed6\u4e5f\u5c31 \u50cf\u662f\u4e00\u68f5\u6811\u822c\u5012\u4e86\u4e0b\u53bb\u3002 \u5929\u8272\u9634\u9eef\uff0c\u8fd8\u662f\u6709\u96fe\u3002 \u9646\u5c0f\u51e4\u9192\u6765\u65f6\uff0c\u5df2\u8eba\u5728\u67f3\u9752\u9752\u7684\u5e8a\u4e0a\u3002 \u5c4b\u91cc\u6ca1\u6709\u4eba\uff0c\u5e8a\u5934\u7684\u5c0f\u51e0\u4e0a\u6709\u4e00\u6a3d\u9152\uff0c\u9152\u76cf\u4e0b\u538b\u7740\u5f20\u77ed\u7b3a\uff0c\u201c\u4e00\u65f6\u5931\u624b\uff0c\u8bef\u4f24\u5c0a \u9888\uff0c\u4e14\u559c\u6709\u9152\uff0c\u53ef\u4ee5\u538b\u60ca\uff0c\u9192\u65f6\u4e0d\u59a8\u5148\u4f5c\u5c0f\u996e\uff0c\u5348\u65f6\u524d\u540e\u518d\u6765\u76f8\u6664\u3002\u201d \u770b\u5b8c\u4e86\u8fd9\u5f20\u77ed\u7b3a\uff0c\u9646\u5c0f\u51e4\u624d\u53d1\u73b0\u81ea\u5df1\u8116\u5b50\u75db\u5f97\u8fde\u56de\u5934\u90fd\u5f88\u96be\u3002 \u8fd9\u5f53\u7136\u4e0d\u662f\u8001\u5200\u628a\u5b50\u5931\u624b\u8bef\u4f24\u7684\u3002 \u53ef\u662f\u8001\u5200\u628a\u5b50\u4e3a\u4ec0\u4e48\u8981\u6697\u7b97\u4ed6\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u8ba9\u4ed6\u53bb\u6551\u53f6\u96ea\uff1f \u8fd9\u5176\u4e2d\u8fd8\u6709\u4ec0\u4e48\u4e0d\u53ef\u544a\u4eba\u7684\u79d8\u5bc6\uff1f\u4ed6\u60f3\u4e0d\u901a\uff0c\u6240\u4ee5\u4ed6\u5e72\u8106\u4e0d\u60f3\uff0c\u62ff\u8d77\u9152\u74f6\uff0c\u5c31\u5f80\u5634 \u91cc\u5012\u3002 \u534a\u74f6\u9152\u4e0b\u809a\uff0c\u5916\u9762\u5ffd\u7136\u6709\u72d7\u53eb\u7684\u58f0\u97f3\uff0c\u5f00\u59cb\u65f6\u53ea\u6709\u4e00\u6761\u72d7\uff0c\u5ffd\u7136\u95f4\u5c31\u5df2\u53d8\u6210\u4e03\u516b \u6761\uff0c\u5927\u72d7\u5c0f\u72d7\u516c\u72d7\u6bcd\u72d7\u90fd\u6709\uff0c\u53eb\u5f97\u70ed\u95f9\u6781\u4e86\u3002 \u8fd9\u5e7d\u79d8\u7684\u5c71\u8c37\u4e2d\uff0c\u600e\u4e48\u4f1a\u5ffd\u7136\u6765\u4e86\u8fd9\u4e48\u591a\u72d7\uff1f \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u8981\u53bb\u770b\u770b\uff0c\u521a\u8d70\u8fc7\u53bb\u63a8\u5f00\u95e8\uff0c\u53c8\u4e0d\u7981\u6014\u4f4f\u5916\u9762\u8fde\u4e00\u6761\u72d7\u90fd\u6ca1\u6709\uff0c\u53ea \u6709\u4e00\u4e2a\u4eba\u3002 \u4e00\u4e2a\u53c8\u7626\u53c8\u5e72\u7684\u9ed1\u8863\u4eba\uff0c\u8138\u8272\u8721\u9ec4\uff0c\u4e00\u53cc\u773c\u775b\u5374\u707c\u707c\u6709\u5149\u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11 \u9053\uff1a\u201c\u4f60\u7a76\u7adf\u662f\u4eba\uff1f\u8fd8\u662f\u72d7\uff1f\u201d \u72ac\u90ce\u541b\u9053\uff1a\u201c\u65e2\u4e0d\u662f\u4eba\uff0c\u4e5f\u4e0d\u662f\u72d7\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d \u72ac\u90ce\u541b\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u662f\u4e1c\u897f\uff0c\u6240\u4ee5\u624d\u6765\u627e\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u627e\u6211\u5e72\u4ec0\u4e48\uff1f\u201d \u72ac\u90ce\u541b\u9053\uff1a\u201c\u4f60\u7b54\u5e94\u6211\u2014\u4ef6\u4e8b\uff0c\u6211\u544a\u8bc9\u4f60\u4e24\u4e2a\u6d88\u606f\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u662f\u597d\u6d88\u606f\uff1f\u8fd8\u662f\u574f\u6d88\u606f\uff1f\u201d \u72ac\u90ce\u541b\u7b11\u4e86\uff0c\u9053\uff1a\u201c\u4ece\u6211\u5634\u91cc\u8bf4\u51fa\u6765\u7684\uff0c\u54ea\u6709\u597d\u6d88\u606f\uff1f\u201d \u9646\u5c0f\u51e4\u4e5f\u7b11\u4e86\uff0c\u5ffd\u7136\u95ea\u7535\u822c\u51fa\u624b\uff0c\u7528\u4e24\u6839\u624b\u6307\u5939\u4f4f\u4e86\u4ed6\u7684\u9f3b\u5b50\u3002 \u6b66\u6797\u4e2d\u6700\u6709\u4ef7\u503c\u7684\u4e24\u6839\u624b\u6307\uff0c\u6c5f\u6e56\u4e2d\u6700\u6709\u540d\u7684\u65e0\u53cc\u7edd\u6280\u3002 \u72ac\u90ce\u541b\u6839\u672c\u65e0\u6cd5\u95ea\u907f\uff0c\u5c31\u7b97\u660e\u660e\u77e5\u9053\u8fd9\u4e24\u6839\u624b\u6307\u4f1a\u5939\u8fc7\u6765\uff0c\u8fd8\u662f\u65e0\u6cd5\u95ea\u907f\u3002 \u9646\u5c0f\u51e4\u5fae\u7b11\u9053\uff1a\u201c\u636e\u8bf4\u72d7\u7684\u9f3b\u5b50\u6700\u7075\uff0c\u6ca1\u6709\u9f3b\u5b50\u7684\u72d7\u65e5\u5b50\u4e00\u5b9a\u4e0d\u592a\u597d\u8fc7\u7684\u3002 \u72ac\u90ce\u541b\u814a\u9ec4\u7684\u8138\u5df2\u6da8\u7ea2\uff0c\u8fde\u6c14\u90fd\u900f\u4e0d\u8fc7\u6765\u3002 \u9646\u5c0f\u51e4\u653e\u5f00\u4e86\u624b\uff0c\u9053\uff1a\u201c\u5148\u8bf4\u4f60\u7684\u6d88\u606f\u3002\u201d \u72ac\u90ce\u541b\u957f\u957f\u900f\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\u6d88\u606f\uff1f\u201d \u9646\u5c0f\u51e4\u53c8\u7b11\u4e86\uff0c\u5ffd\u7136\u53c8\u95ea\u7535\u822c\u51fa\u624b\uff0c\u7528\u4e24\u6839\u624b\u6307\u5939\u4f4f\u4e86\u4ed6\u7684\u9f3b\u5b50\u3002 \u72ac\u90ce\u541b\u8fd8\u662f\u8eb2\u4e0d\u5f00\u3002 \u9646\u5c0f\u51e4\u53c8\u653e\u5f00\u4e86\u624b\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u662f\u4ec0\u4e48\u6d88\u606f\uff1f\u201d \u8fd9\u6b21\u72ac\u90ce\u541b\u53ea\u6709\u8bf4\u5b9e\u8bdd\uff0c\u56e0\u4e3a\u4ed6\u5df2\u660e\u767d\u4e00\u4ef6\u4e8b\u53ea\u8981\u9646\u5c0f\u51e4\u51fa\u624b\uff0c\u968f\u65f6\u968f\u523b\u90fd\u53ef\u4ee5 \u5939\u4f50\u4ed6\u7684\u9f3b\u5b50\uff0c\u5c31\u597d\u50cf\u8001\u53eb\u82b1\u6293\u8671\u5b50\u4e00\u6837\u5bb9\u6613\u3002 \u201c\u5c06\u519b\u5feb\u6b7b\u4e86\uff0c\u5c0f\u53f6\u4e0d\u89c1\u4e86\u3002\u201d \u8fd9\u5c31\u662f\u4ed6\u8bf4\u51fa\u6765\u7684\u6d88\u606f\uff0c\u6d88\u606f\u5b9e\u5728\u4e0d\u597d\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6ca1\u6709\u4eba\u77e5\u9053\u5c0f\u53f6\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u201d \u72ac\u90ce\u541b\u82e6\u7b11\u9053\uff1a\u201c\u8fde\u72d7\u90fd\u4e0d\u77e5\u9053\uff0c\u4f55\u51b5\u4eba\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5c06\u519b\u5462\uff1f\u201d \u72ac\u90ce\u541b\u9053\uff1a\u201c\u5c06\u519b\u5728\u7b49\u6b7b\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u77e5\u9053\u81ea\u5df1\u51fa\u624b\u7684\u5206\u91cf\uff0c\u6211\u5e76\u6ca1\u6709\u8981\u4ed6\u6b7b\u3002 \u72ac\u90ce\u541b\u9053\uff1a\u201c\u9664\u4e86\u4f60\u4e4b\u5916\uff0c\u8fd9\u91cc\u8fd8\u6709\u522b\u7684\u4eba\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u522b\u4eba\u6740\u4e86\u4ed6\uff0c\u8fd9\u7b14\u5e10\u8fd8\u662f\u8981\u7b97\u5728\u6211\u5934\u4e0a\u7684\u3002 \u72ac\u90ce\u541b\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5e94\u8be5\u660e\u767d\u6211\u662f\u597d\u610f\uff0c\u5c06\u519b\u8ddf\u8001\u5200\u628a\u5b50\u2014\u5411\u6709\u4ea4\u60c5\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4e5f\u5e94\u8be5\u7b54\u5e94\u4f60\u7684\u4e8b\u3002\u201d \u72ac\u90ce\u541b\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u8981\u4f60\u8d70\u7684\u65f6\u5019\u5e26\u6211\u8d70\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4ef6\u4e8b\uff1f\u201d \u72ac\u90ce\u541b\u9053\uff1a\u201c\u5bf9\u4f60\u6765\u8bf4\uff0c\u8fd9\u662f\u4ef6\u5c0f\u4e8b\uff0c\u5bf9\u6211\u5374\u662f\u4ef6\u5927\u4e8b\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u597d\uff0c\u6211\u7b54\u5e94\u3002\u201d \u72ac\u90ce\u541b\u5ffd\u7136\u8dea\u4e0b\u53bb\uff0c\u91cd\u91cd\u7684\u78d5\u4e86\u4e09\u4e2a\u5934\uff0c\u4ef0\u5929\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u6211\u6ca1\u6709\u5c3e \u5df4\uff0c\u5426\u5219\u6211\u4e00\u89c1\u5230\u4f60\u81f3\u5c11\u6447\u4e09\u6b21\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5c06\u519b\u5728\u54ea\u91cc\u7b49\u6b7b\uff1f\u201d \u72ac\u90ce\u541b\u9053\uff1a\u201c\u5c06\u519b\u5f53\u7136\u5728\u5c06\u519b\u5e9c\u3002\u201d \u5c06\u519b\u5e9c\u5916\u4e00\u7247\u4e1b\u6797\uff0c\u72ac\u90ce\u541b\u5df2\u8d70\u4e86\uff0c\u4e1b\u6797\u4e2d\u5374\u6709\u4eba\u50cf\u72d7\u4e00\u6837\u5728\u5598\u606f\u3002 \u80fd\u5598\u606f\u8fd8\u662f\u5e78\u8fd0\u7684\uff0c\u5c06\u519b\u7684\u547c\u5438\u5df2\u505c\u987f\u3002 \u4e00\u4e2a\u4eba\u5598\u606f\u7740\uff0c\u9a91\u5728\u4ed6\u8eab\u4e0a\uff0c\u7528\u4e00\u53cc\u624b\u627c\u4f4f\u4e86\u4ed6\u7684\u54bd\u5589\u3002 \u8fd9\u4e2a\u4eba\u8d6b\u7136\u7adf\u662f\u72ec\u5b64\u7f8e\u3002 \u9646\u5c0f\u51e4\u51b2\u8fc7\u53bb\uff0c\u53cd\u624b\u4e00\u638c\u5c06\u4ed6\u6253\u5f97\u98de\u4e86\u51fa\u53bb\uff0c\u5c06\u519b\u9762\u5982\u91d1\u7eb8\uff0c\u5fc3\u4eff\u4f5b\u8fd8\u5728\u8df3\uff0c\u773c \u8fd8\u6ca1\u6709\u95ed\uff0c\u4e5e\u601c\u7684\u770b\u7740\u9646\u5c0f\u51e4\uff0c\u597d\u50cf\u6709\u8bdd\u8981\u8bf4\uff0c\u4e00\u4e2a\u4eba\u5728\u4e34\u6b7b\u524d\u8bf4\u51fa\u7684\u8bdd\uff0c\u901a\u5e38\u90fd\u662f \u5f88\u5927\u7684\u79d8\u5bc6\u3002 \u53ef\u60dc\u4ed6\u8fde\u4e00\u4e2a\u5b57\u90fd\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u9646\u5c0f\u51e4\u4fef\u4e0b\u8eab\u65f6\uff0c\u4ed6\u7684\u5fc3\u8df3\u5df2\u505c\u6b62\u3002 \u72ec\u5b64\u7f8e\u8fd8\u5728\u5598\u606f\u3002 \u9646\u5c0f\u51e46\u628a\u63ea\u8d77\u4ed6\uff0c\u9053\uff1a\u4f60\u4eec\u6709\u4ec7\uff1f\u201d \u72ec\u5b64\u7f8e\u6447\u5934\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u8981\u6740\u4f60\uff1f\u201d \u72ec\u5b64\u7f8e\u6447\u5934\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u4e3a\u4f55\u8981\u6740\u4ed6\uff1f\u201d \u72ec\u5b64\u7f8e\u770b\u7740\u4ed6\uff0c\u5598\u606f\u6e10\u6e10\u5e73\u9759\uff0c\u76ee\u5149\u6e10\u6e10\u9510\u5229\uff0c\u5ffd\u7136\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u771f\u7684\u4ee5\u4e3a\u6211\u662f \u2018\u516d\u4eb2\u4e0d\u8ba4\u2019\u72ec\u5b64\u7f8e\uff1f\u201d \u65e0\u8bba\u8c01\u90fd\u60f3\u4e0d\u5230\u4ed6\uff0c\u9646\u5c0f\u51e4\u4e5f\u5f88\u610f\u5916\uff0c\u201c\u4f60\u4e0d\u662f\uff1f\u201d \u72ec\u5b64\u7f8e\u53f9\u4e86\u53e3\u6c14\uff0c\u5ffd\u7136\u53c8\u8bf4\u51fa\u53e5\u4ee4\u4eba\u5403\u60ca\u7684\u8bdd\uff0c\u201c\u628a\u6211\u7684\u88e4\u5b50\u8131\u4e0b\u6765oU\u9646\u5c0f\u51e4\u4e5f \u76ef\u7740\u4ed6\u770b\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u6211\u4ece\u6765\u6ca1\u6709\u8131\u8fc7\u7537\u4eba\u7684\u88e4\u5b50\uff0c\u53ef\u662f\u8fd9\u6b21\u6211\u7834 \u4f8b\u3002\u201d \u72ec\u5b64\u7f8e\u5df2\u662f\u4e2a\u8001\u4eba\uff0c\u4ed6\u81c0\u90e8\u7684\u808c\u8089\u5374\u4ecd\u7136\u663e\u5f97\u7ed3\u5b9e\u800c\u5e74\u9752\uff0c\u201c\u4f60\u6709\u6ca1\u6709\u770b\u89c1\u4e0a\u9762 \u7684\u4e00\u4e2a\u7624\uff1f\u201d \u9646\u5c0f\u51e4\u5f53\u7136\u4e0d\u4f1a\u770b\u4e0d\u89c1\uff0c\u8fd9\u4e2a\u7624\u5df2\u5927\u5f97\u8db3\u591f\u8ba9\u4e00\u91cc\u5916\u7684\u4eba\u90fd\u770b\u5f97\u5f88\u6e05\u695a\u3002 \u72ec\u5b64\u7f8e\u9053\uff1a\u201c\u7528\u8fd9\u628a\u5200\u5272\u5f00\u5b83\u3002\u201d \u4e00\u628a\u5200\u9012\u8fc7\u6765\uff0c\u5200\u950b\u96ea\u4eae\u3002 \u9646\u5c0f\u51e4\u8fd9\u2014\u751f\u4e2d\u4e5f\u4e0d\u77e5\u505a\u8fc7\u591a\u5c11\u79bb\u5947\u53e4\u602a\u7684\u4e8b\uff0c\u53ef\u662f\u4ed6\u63a5\u8fc7\u8fd9\u628a\u5200\u65f6\uff0c\u8fd8\u662f\u5fcd\u4e0d \u4f4f\u8fdf\u7591\u4e86\u5f88\u4e45\uff0c\u624d\u80fd\u5272\u4e0b\u53bb\u3002 \u9c9c\u8840\u98de\u6e85\uff0c\u2014\u4e2a\u91d1\u4e38\u968f\u7740\u9c9c\u8840\u4ece\u5272\u5f00\u4e86\u7684\u8089\u7624\u4e2d\u8fdb\u51fa\u6765\u3002 \u72ec\u5b64\u7f8e\u9053\uff1a\u201c\u518d\u5272\u5f00\u8fd9\u4e2a\u7403\u3002\u201d \u2014\u5200\u5272\u4e0b\u53bb\uff0c\u624d\u53d1\u73b0\u8fd9\u91d1\u4e38\u662f\u7528\u8721\u505a\u7684\uff0c\u5305\u7740\u91d1\u7eb8\uff0c\u91cc\u9762\u85cf\u7740\u5757\u9ec4\u7ee2\uff0c\u4e0a\u9762\u5199 \u7740\uff1a\u6b66\u5f53\u638c\u95e8\u5ea7\u4e0b\u7b2c\u56db\u540d\u5f1f\u5b50\u5b59\u4e0d\u53d8v\u5949\u6e1d\u6613\u5bb9\u6539\u626e\uff0c\u67e5\u8bbf\u53db\u5f92\u884c\u8e2a\uff0c\u6b64\u6e1d\u3002\u201d \u4e0b\u9762\u4e0d\u4f46\u6709\u6b66\u5f53\u638c\u6559\u7684\u5927\u5370\uff0c\u8fd8\u6709\u638c\u95e8\u77f3\u771f\u4eba\u7684\u4eb2\u7b14\u82b1\u62bc\u72ec\u5b64\u7f8e\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u638c \u95e8\u771f\u4eba\u8981\u6211\u5728\u5371\u6025\u4e2d\u7528\u6765\u8bc1\u660e\u8eab\u5206\u7684\uff0c\uff0c\u9646\u5c0f\u51e4\u5403\u60ca\u7684\u770b\u7740\u4ed6\uff0c\u7ec8\u4e8e\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u770b\u6765\u4f60\u597d\u50cf\u771f\u7684\u4e0d\u662f\u72ec\u5b64\u7f8e\u3002\u201d \u5b59\u4e0d\u53d8\u9053\uff1a\u201c\u672a\u4eba\u6b66\u5f53\u524d\uff0c\u6211\u672c\u662f\u82b1\u56db\u59d1\u95e8\u4e0b\u5f1f\u5b50\uff0c\u5316\u7f18\u7684\u6613\u5bb9\u672f\u5999\u7edd\u5929\u4e0b\uff0c\u53ef \u662f\u4e3a\u4e86\u5c0f\u5fc3\u8c28\u614e\uff0c\u6211\u53c8\u6295\u8eab\u5230\u72ec\u5b64\u7f8e\u95e8\u4e0b\u4e3a\u5974\uff0c\u6574\u6574\u82b1\u4e86\u5341\u4e2a\u6708\u529f\u592b\u53bb\u5b66\u4ed6\u7684\u58f0\u5bb9\u795e \u6001\uff0c\u76f4\u7b49\u5230\u6211\u81ea\u5df1\u89c9\u4e07\u65e0\u4e00\u5931\u7684\u65f6\u5019\u624d\u51fa\u624b\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u6740\u4e86\u4ed6\uff1f\u201d \u5b59\u4e0d\u53d8\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u7edd\u4e0d\u80fd\u8ba9\u4efb\u4f55\u4eba\u518d\u627e\u5230\u53e6\u4e00\u4e2a\u72ec\u5b64\u7f8e\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u8981\u67e5\u8bbf\u7684\u53db\u5f92\u662f\u8c01\uff1f\u201d \u5b59\u4e0d\u53d8\u9053\uff1a\u201c\u7b2c\u4e00\u4e2a\u5c31\u662f\u77f3\u9e64\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5df2\u627e\u5230\u4ed6\uff1f\u201d \u5b59\u4e0d\u53d8\u9053\uff1a\u201c\u90a3\u4e5f\u591a\u4e8f\u4e86\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u949f\u65e0\u9aa8\u662f\u6b7b\u5728\u4f60\u624b\u91cc\u7684\uff1f\u201d \u5b59\u4e0d\u53d8\u9053\uff1a\u201c\u4ed6\u4e5f\u662f\u6b66\u5f53\u7684\u53db\u5f92\uff0c\u6211\u7edd\u4e0d\u80fd\u8ba9\u4ed6\u6d3b\u7740\u3002\u201d \u9646\u5c0f\u51e4\u76ee\u5149\u95ea\u52a8\uff0c\u9053\uff1a\u201c\u7389\u6811\u5251\u5ba2\u53f6\u51cc\u98ce\u65e9\u5e74\u662f\u4e0d\u662f\u4e5f\u66fe\u5728\u6b66\u5f53\u95e8\u4e0b\uff1f\u201d \u5b59\u4e0d\u53d8\u9053\uff1a\u201c\u4ed6\u8ddf\u949f\u65e0\u9aa8\u90fd\u662f\u6b66\u5f53\u7684\u4fd7\u5bb6\u5f1f\u5b50\uff0c\u90fd\u662f\u88ab\u5148\u7956\u5e08\u6885\u771f\u4eba\u9010\u51fa\u95e8\u5899 \u7684\u3002\u201d \u6885\u771f\u4eba\u662f\u6728\u9053\u4eba\u7684\u5e08\u5144\uff0c\u6267\u638c\u6b66\u5f53\u95e8\u6237\u5341\u4e03\u5e74\uff0c\u624d\u4f20\u7ed9\u73b0\u5728\u7684\u638c\u95e8\u77f3\u96c1\u3002 \u5b59\u4e0d\u53d8\u9053\uff1a\u201c\u6211\u4eec\u7814\u7a76\u5f88\u4e45\uff0c\u90fd\u8ba4\u4e3a\u53ea\u6709\u7528\u72ec\u5b64\u7f8e\u7684\u8eab\u5206\u505a\u63a9\u62a4\u6700\u5b89\u5168\uff0c\u53ea\u53ef \u60dc\u2026\u2026\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u7684\u79d8\u5bc6\u8fd8\u662f\u88ab\u5c06\u519b\u53d1\u73b0\u4e86\u3002\u201d \u5b59\u4e0d\u53d8\u82eb\u7b11\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u8ba4\u4e3a\u4ed6\u53d7\u4e86\u4f24\u5f88\u91cd\uff0c\u6211\u4e5f\u51e0\u4e4e\u88ab\u9a97\u8fc7\uff0c\u8c01\u77e5\u8eb2\u5728\u5c06\u519b\u5e9c \u517b\u4f24\u7684\u90a3\u4e2a\u4eba\u7adf\u4e0d\u662f\u4ed6\uff0c\u4ed6\u4e00\u76f4\u90fd\u5728\u76ef\u7740\u6211\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u9732\u51fa\u7834\u7efd\u7684\uff1f\u201d \u5b59\u4e0d\u53d8\u9053\uff1a\u201c\u4ed6\u672c\u662f\u72ec\u5b64\u7f8e\u7684\u8001\u53cb\uff0c\u4ed6\u77e5\u9053\u72ec\u5b64\u7f8e\u65e9\u5e74\u7684\u5f88\u591a\u79d8\u5bc6\uff0c\u6211\u5374\u4e0d\u77e5 \u9053\uff0c\u4ed6\u7528\u8bdd\u5957\u4f4f\u4e86\u6211\uff0c\u6211\u53ea\u6709\u6740\u4e86\u4ed6\u706d\u53e3\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u5c06\u8fd9\u79d8\u5bc6\u544a\u8bc9\u6211\uff1f\u201d \u5b59\u4e0d\u53d8\u9053\uff1a\u201c\u73b0\u5728\u4e8b\u60c5\u5371\u6025\uff0c\u6211\u5df2\u4e0d\u80fd\u4e0d\u8bf4\uff0c\u6211\u4e0d\u4f46\u8981\u4f60\u4e3a\u6211\u4fdd\u5b88\u8fd9\u79d8\u5bc6\uff0c\u8fd8\u8981 \u4f60\u52a9\u6211\u4e00\u81c2\u4e4b\u529b\uff0c\u8fd9\u5730\u65b9\u6211\u5df2\u65e0\u6cd5\u5b58\u8eab\uff0c\u2014\u5b9a\u8981\u5c3d\u5feb\u8d76\u56de\u6b66\u5f53\u53bb\u3002\u201d \u4ed6\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e5f\u65e9\u5c31\u770b\u51fa\u4e86\u4f60\u4e0d\u662f\u51fa\u5356\u670b\u53cb\u7684\u4eba\uff0c\u6211\u59cb\u7ec8\u4e0d\u76f8 \u4fe1\u4f60\u771f\u7684\u4f1a\u52fe\u5f15\u897f\u95e8\u5439\u96ea\u7684\u59bb\u5b50\uff0c\u90a3\u4e00\u5b9a\u662f\u4f60\u4eec\u6545\u610f\u6f14\u7684\u4e00\u51fa\u620f\uff0c\u56e0\u4e3a\u4f60\u4eec\u4e5f\u60f3\u63ed\u7834 \u8fd9\u5e7d\u7075\u5c71\u5e84\u7684\u79d8\u5bc6\u3002\u201d \u9646\u5c0f\u51e4\u53c8\u76ef\u7740\u4ed6\u770b\u4e86\u5f88\u4e45\uff0c\u5ffd\u7136\u957f\u957f\u53f9\u6c14\uff0c\u9053\uff1a\u201c\u53ef\u60dc\u53ef\u60dc\uff0c\u5b9e\u5728\u53ef\u60dc\u3002\u201d \u5b59\u4e0d\u53d8\u9053\uff1a\u201c\u53ef\u60dc\u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ef\u60dc\u4f60\u770b\u9519\u4e86\u4eba\u3002\u201d \u5b59\u4e0d\u53d8\u8138\u8272\u5df2\u53d8\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u96be\u9053\u5fd8\u4e86\u662f\u8c01\u5e26\u4f60\u8fc7\u6765\u7684\uff1f\u201d \u9646\u5c0f\u51e4\u51b7\u51b7\u9053\uff1a\u201c\u6211\u6ca1\u6709\u5fd8\uff0c\u6211\u4e5f\u6ca1\u6709\u5fd8\u8bb0\u4f60\u5728\u8fd9\u4e24\u5929\u91cc\u5df2\u5bb3\u8fc7\u6211\u4e09\u6b21\uff0c\u82e5\u4e0d\u662f \u8001\u5200\u628a\u5b50\uff0c\u6211\u5df2\u6b7b\u5728\u4f60\u624b\u91cc\u3002\u201d \u5b59\u4e0d\u53d8\u9053\uff1a\u201c\u96be\u9053\u4f60\u770b\u4e0d\u51fa\u90a3\u662f\u6211\u6545\u610f\u505a\u7ed9\u4ed6\u4eec\u770b\u7684\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u770b\u4e0d\u51fa\u3002\u201d \u5b59\u4e0d\u53d8\u76ef\u7740\u4ed6\uff0c\u5ffd\u7136\u4e5f\u957f\u957f\u53f9\u606f\uff0c\u9053\uff1a\u201c\u597d\uff0c\u4f60\u5f88\u597d\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u4e0d\u597d\uff0c\u4e00\u70b9\u4e5f\u4e0d\u597d\u3002\u201d \u5b59\u4e0d\u53d8\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u8be5\u6b7b\uff01\u201d \u559d\u58f0\u4e2d\uff0c\u4ed6\u7684\u4eba\u5df2\u6251\u8d77\uff0c\u6307\u5c16\u8ddd\u79bb\u9646\u5c0f\u51e4\u80f8\u819b\u8fd8\u6709\u534a\u5c3a\uff0c\u638c\u5fc3\u7a81\u7136\u5411\u524d\u4e00\u5410\u3002\u76f4 \u6253\u7384\u673a\u7a74\uff0c\u7528\u7684\u6b63\u662f\u6b66\u5f53\u5c0f\u5929\u661f\u638c\u529b\uff0c\u800c\u4e14\u8ba4\u7a74\u5947\u51c6\u3002 \u53ea\u53ef\u60dc\u4ed6\u7684\u638c\u529b\u5410\u51fa\u65f6\uff0c\u9646\u5c0f\u51e4\u7684\u7384\u673a\u7a74\u65e9\u5df2\u4e0d\u5728\u90a3\u91cc\uff0c\u4eba\u4e5f\u5df2\u4e0d\u5728\u90a3\u91cc\u3002 \u5b59\u4e0d\u53d8\u624b\u638c\u4e00\u7ffb\uff0c\u7384\u9e1f\u5212\u6c99\uff0c\u5e73\u6c99\u843d\u96c1\uff0c\u5317\u96c1\u5357\u98de\uff0c\u4e00\u62db\u4e09\u5f0f\uff0c\u8fd9\u79cd\u8f7b\u7075\u7ef5\u5bc6\u7684 \u6b66\u5f53\u638c\u6cd5\u5728\u4ed6\u624b\u91cc\u4f7f\u51fa\u6765\uff0c\u4e0d\u4f46\u6781\u89c1\u529f\u529b\uff0c\u53d8\u5316\u4e5f\u6781\u5feb\u3002 \u9646\u5c0f\u51e4\u53f9\u9053\uff1a\u201c\u77f3\u9053\u4eba\u95e8\u4e0b\u7684\u5f1f\u5b50\uff0c\u679c\u7136\u4e86\u5f97\u3002\u201d \u8fd9\u4e24\u53e5\u8bdd\u8bf4\u5b8c\uff0c\u5b59\u4e0d\u53d8\u7684\u62db\u5f0f\u53c8\u5168\u90fd\u843d\u7a7a\uff0c\u65e0\u8bba\u4ed6\u51fa\u624b\u591a\u5feb\uff0c\u9646\u5c0f\u51e4\u597d\u50cf\u603b\u80fd\u6bd4 \u4ed6\u66f4\u5feb\u4e00\u6b65\u3002 \u6b66\u5f53\u638c\u6cd5\u8fd0\u7528\u53d8\u5316\uff0c\u9646\u5c0f\u51e4\u77e5\u9053\u7684\u597d\u50cf\u5e76\u4e0d\u6bd4\u4ed6\u5c11\u3002 \u4ed6\u5ffd\u7136\u505c\u4f4f\u624b\uff0c\u76ef\u7740\u9646\u5c0f\u51e4\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u7ec3\u8fc7\u6b66\u5f53\u529f\u592b\uff1f\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u6ca1\u6709\u7ec3\u8fc7\u6b66\u5f53\u529f\u592b\uff0c\u53ef\u662f\u6211\u6709\u5f88\u591a\u6b66\u5f53\u670b\u53cb\u3002\u201d \u5b59\u4e0d\u53d8\u773c\u775b\u91cc\u53c8\u9732\u51fa\u2014\u7ebf\u5e0c\u671b\uff0c\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u66f4\u8be5\u5e2e\u6211\u9003\u51fa\u53bb\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4f60\u4e0d\u662f\u6211\u7684\u670b\u53cb\uff0c\u4f60\u6551\u6211\u4e00\u6b21\uff0c\u5bb3\u6211\u4e09\u6b21\uff0c\u73b0\u5728\u6211\u53c8\u51fa\u4e86\u4f60 \u516b\u62db\uff0c\u6211\u4eec\u7684\u5e10\u65e9\u5df2\u7ed3\u6e05\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u672c\u6765\u5c31\u5df2\u51c6\u5907\u51fa\u624b\u3002\u201d \u4ed6\u7528\u7684\u5c45\u7136\u4e5f\u662f\u6b66\u5f53\u5c0f\u5929\u661f\u638c\u529b\uff0c\u638c\u5fc3\u5916\u5410\uff0c\u6253\u7684\u4e5f\u662f\u7384\u673a\u7a74\u3002 \u5b59\u4e0d\u53d8\u5f15\u81c2\u7ffb\u8eab\uff0c\u582a\u582a\u907f\u5f00\u8fd9\u2014\u638c\uff0c\u9646\u5c0f\u51e4\u7684\u5de6\u638c\u5374\u5df2\u5207\u5728\u4ed6\u540e\u9888\u7684\u5927\u8840\u7ba1\u4e0a\u3002 \u4ed6\u5012\u4e0b\u53bb\u65f6\uff0c\u8fd8\u5728\u5403\u60ca\u7684\u770b\u7740\u9646\u5c0f\u51e4\u3002 \u9646\u5c0f\u51e4\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u4e0d\u77e5\u9053\u6211\u6709\u4e24\u53ea\u624b\uff1f\u201d \u5b59\u4e0d\u53d8\u5f53\u7136\u77e5\u9053\uff0c\u4f46\u4ed6\u5374\u60f3\u4e0d\u5230\u2014\u4e2a\u4eba\u7684\u624b\u7adf\u80fd\u6709\u8fd9\u4e48\u5feb\u7684\u52a8\u4f5c\u3002 \u8001\u5200\u628a\u5b50\u5750\u5728\u4ed6\u90a3\u5f20\u9648\u65e7\u800c\u5bbd\u5927\u7684\u6728\u6905\u4e0a\uff0c\u770b\u7740\u9646\u5c0f\u98ce\uff0c\u770b\u6765\u4eff\u4f5b\u5f88\u6109\u5feb\u3002 \u65e7\u6728\u6905\u5c31\u597d\u50cf\u8001\u670b\u53cb\u4e00\u6837\uff0c\u603b\u662f\u80fd\u8ba9\u4eba\u89c9\u5f97\u5f88\u8212\u670d\uff0c\u5f88\u6109\u5feb\u7684\u3002 \u53ea\u53ef\u60dc\u9646\u5c0f\u51e4\u8fd8\u662f\u770b\u4e0d\u89c1\u4ed6\u7684\u8138\u3002 \u5b59\u4e0d\u53d8\u5c31\u5728\u4ed6\u9762\u524d\uff0c\u4ed6\u5374\u8fde\u770b\u90fd\u6ca1\u6709\u770b\u4e00\u773c\uff0c\u4ed6\u5bf9\u9646\u5c0f\u98ce\u7684\u5174\u8da3\u663e\u7136\u6bd4\u5bf9\u4efb\u4f55\u4eba \u90fd\u6d53\u539a\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u662f\u5978\u7ec6\uff0c\u4ece\u6b66\u5f53\u6765\u7684\u5978\u7ec6\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u6740\u4e86\u4ed6\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u65e0\u6743\u6740\u4eba\uff0c\u4e5f\u4e0d\u60f3\u6740\u4eba\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u8be5\u653e\u4e86\u4ed6\u3002\u201d \u9646\u5c0f\u51e4\u5f88\u610f\u5916\uff0c\u201c\u653e\u4e86\u4ed6\uff1f\u201d \u8001\u5200\u628a\u5b50\u6de1\u6de1\u9053\uff1a\u201c\u771f\u6b63\u7684\u5978\u7ec6\u90fd\u65e9\u5df2\u6b7b\u4e86\uff0c\u4ece\u6765\u6ca1\u6709\u2014\u4e2a\u80fd\u5728\u8fd9\u91cc\u6d3b\u8fc7\u4e09\u5929 \u7684\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u96be\u9053\u4ed6\u4e0d\u662f\uff1f\u201d \u8005\u5200\u628a\u5b50\u9053\uff1a\u201c\u4ed6\u5f53\u7136\u662f\u4e2a\u5978\u7ec6\uff0c\u5374\u4e0d\u662f\u6b66\u5f53\u7684\u5978\u7ec6\uff0c\u662f\u6211\u7684\uff0c\u591a\u5e74\u524d\u6211\u5c31\u5df2\u9001 \u4ed6\u5230\u6b66\u5f53\u53bb\u5367\u5e95\u3002\u201d \u9646\u5c0f\u51e4\u6014\u4f4f\u3002 \u8001\u5200\u628a\u5b50\u5374\u5728\u7b11\uff0c\u7b11\u5f97\u5f88\u6109\u5feb\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4f60\u90fd\u8be5\u8c22\u8c22\u4ed6\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u8c22\u4ed6\uff1f\u201d \u8005\u5200\u628a\u5b50\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4ed6\uff0c\u6211\u624d\u771f\u6b63\u5b8c\u5168\u4fe1\u4efb\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u4e5f\u662f\u4f60\u6d3e\u53bb\u8bd5\u63a2\u6211\u7684\uff1f\u201d \u8001\u5200\u628a\u5b50\u5fae\u7b11\u9053\uff1a\u201c\u6709\u4e9b\u4eba\u5929\u751f\u5c31\u662f\u5978\u7ec6\uff0c\u4f60\u53ea\u80fd\u8ba9\u4ed6\u53bb\u505a\u5978\u7ec6\u505a\u7684\u4e8b\uff0c\u800c\u4e14\u6c38 \u8fdc\u4e0d\u4f1a\u5931\u671b\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u5c31\u662f\u4e2a\u5929\u751f\u7684\u5978\u7ec6\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4ece\u5934\u5230\u5c3e\u90fd\u662f\u7684\u3002\u201d \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u5ffd\u7136\u4e00\u811a\u5c06\u5b59\u4e0d\u53d8\u8e22\u5f97\u7403\u4e00\u822c\u6eda\u4e86\u51fa\u8001\u5200\u628a\u5b50\u4e5f\u53f9\u4e86\u53e3\u6c14\uff0c \u9053\uff1a\u201c\u505a\u5978\u7ec6\u53ea\u6709\u8fd9\u4e00\u70b9\u574f\u5904\uff0c\u8fd9\u79cd\u4eba\u5c31\u597d\u50cf\u9a74\u5b50\uff0c\u65f6\u5e38\u90fd\u4f1a\u88ab\u4eba\u8e22\u4e24\u811a\u7684\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u53ea\u8e22\u4e86\u4e00\u811a\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u811a\u4f60\u51c6\u5907\u8e22\u8c01\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8e22\u6211\u81ea\u5df1\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u5978\u7ec6\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u4e0d\u662f\u5978\u7ec6\uff0c\u6211\u53ea\u4e0d\u8fc7\u662f\u6761\u9a74\u5b50\uff0c\u5176\u7b28\u65e0\u6bd4\u7684\u7b28\u9a74\u5b50\u3002\u201d \u4ed6\u663e\u5f97\u5f88\u6c14\u6124\uff0c\u201c\u56e0\u4e3a\u6211\u60f3\u62fc\u547d\u53bb\u6551\u4eba\u5bb6\u7684\u5973\u513f\uff0c\u6362\u6765\u7684\u5374\u662f\u4e00\u5df4\u638c\uff0c\u800c\u4e14\u521a\u597d \u780d\u5728\u6211\u8116\u5b50\u4e0a\u3002\u201d \u8001\u5200\u628a\u5b50\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u81ea\u5df1\u4e5f\u8be5\u77e5\u9053\u6211\u7edd\u4e0d\u8ba9\u4f60\u53bb\u6551\u5979\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u90a3\u6cbc\u6cfd\u91cc\u4e0d\u4f46\u5230\u5904\u90fd\u6709\u6740\u4eba\u7684\u9677\u9631\uff0c\u800c\u4e14\u6709\u6d41\u6c99\uff0c\u4e00\u9677\u4e0b\u53bb\uff0c\u5c31 \u5c38\u9aa8\u65e0\u5b58\uff0c\u6211\u600e\u4e48\u80fd\u8ba9\u4f60\u53bb\u5192\u9669\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u9700\u8981\u4f60\uff0c\u5c06\u519b\u548c\u949f\u65e0\u9aa8\u90fd\u5df2\u6b7b\u4e86\uff0c\u73b0\u5728\u4f60\u5df2\u662f\u6211\u7684\u53f3\u81c2\uff0c \u82e5\u662f\u518d\u5931\u53bb\u8fd9\u6761\u53f3\u81c2\uff0c\u6211\u8ba1\u5212\u591a\u65f6\u7684\u4e8b\uff0c\u53ea\u6015\u5c31\u8981\u6210\u4e3a\u6ce1\u5f71\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u7684\u610f\u601d\u662f\u4e0d\u662f\u8bf4\uff0c\u73b0\u5728\u4f60\u5df2\u5c11\u4e0d\u4e86\u6211\u3002\u201d \u4ed6\u8bf4\u8bdd\u7684\u65b9\u5f0f\u5f88\u5947\u7279\uff0c\u4e5f\u5f88\u8c28\u614e\uff0c\u4e2d\u6765\u4ed6\u53ea\u7528\u516d\u4e2a\u5b57\u5c31\u53ef\u4ee5\u8bf4\u5b8c\u7684\u8bdd\uff0c\u8fd9\u6b21\u5374\u7528 \u4e86\u5341\u516d\u4e2a\u5b57\u3002 \u8001\u5200\u628a\u5b50\u5b50\u7684\u56de\u7b54\u5374\u7b80\u5355\u800c\u5e72\u8106\uff0c\u201c\u662f\u7684\u3002\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\u3002 \u5c31\u5728\u4ed6\u5f00\u59cb\u7b11\u7684\u65f6\u5019\uff0c\u4ed6\u8eab\u5b50\u5df2\u957f\u9e70\u822c\u63a0\u8d77\uff0c\u4ed6\u7684\u624b\u5c31\u662f\u9e70\u722a\u3002 \u9e70\u722a\u7684\u730e\u7269\u5374\u662f\u8001\u5200\u628a\u5b50\u5934\u4e0a\u7684\u7af9\u7b20\u3002 \u8001\u5200\u628a\u5b50\u8fd8\u662f\u5750\u7740\u6ca1\u6709\u52a8\uff0c\u963f\u5374\u6293\u7a7a\u4e86\u3002 \u5c31\u7b97\u662f\u6700\u7075\u654f\u72e1\u733e\u7684\u72d0\u5154\uff0c\u4e5f\u5f88\u96be\u9003\u8131\u9e70\u722a\u7684\u4e00\u6293\uff0c\u4ed6\u7684\u51fa\u624b\u7edd\u5bf9\u6bd4\u9e70\u722a\u66f4\u8fc5\u901f \u51c6\u786e\u3002 \u53ef\u662f\u4ed6\u6293\u7a7a\u4e86\uff0c\u56e0\u4e3a\u8001\u5200\u628a\u5b50\u8fde\u4eba\u5e26\u6905\u90fd\u5df2\u6ed1\u4e86\u51fa\u53bb\uff0c\u5c31\u50cf\u662f\u6025\u6d41\u4e0a\u7684\u76ae\u7b4f\u822c\u7a81 \u7136\u6ed1\u4e86\u51fa\u53bb\uff0c\u90a3\u6c89\u91cd\u7684\u6728\u6905\u5c31\u597d\u50cf\u5df2\u5728\u4ed6\u8eab\u4e0a\u3002 \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u8eab\u5b50\u98d8\u843d\uff0c\u4ed6\u77e5\u9053\u8fd9\u4e00\u51fb\u4e0d\u4e2d\uff0c\u7b2c\u4e8c\u6b21\u66f4\u96be\u5f97\u624b\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u60f3\u770b\u770b\u6211\uff1f\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u8981\u6211\u4e3a\u4f60\u53bb\u6b7b\uff0c\u81f3\u5c11\u5e94\u8be5\u8ba9\u6211\u770b\u770b\u4f60\u662f\u4ec0\u4e48\u4eba\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u6211\u4e0d\u597d\u770b\uff0c\u6211\u4e5f\u4e0d\u60f3\u8981\u4f60\u4e3a\u6211\u6b7b\uff0c\u8fd9\u4ef6\u4e8b\u6210\u529f\u540e\u5bf9\u5927\u5bb6\u90fd\u6709 \u5229\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u82e5\u662f\u4e0d\u6210\u5462\uff1f\u201d \u8001\u5200\u628a\u5b50\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u5c31\u7b97\u6b7b\u4e86\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u635f\u5931\uff0c\u4f60\u672c\u6765\u5c31\u5df2\u5e94\u8be5\u662f\u4e2a\u6b7b \u4eba\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u521b\u7acb\u8fd9\u5e7d\u7075\u5c71\u5e84\uff0c\u5c31\u662f\u4e3a\u4e86\u8981\u627e\u4eba\u6765\u66ff\u4f60\u5192\u9669\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u5230\u8fd9\u91cc\u6765\u7684\u4eba\uff0c\u672c\u6765\u90fd\u5df2\u5e94\u8be5\u6b7b\u8fc7\u2014\u6b21\uff0c\u518d\u6b7b\u4e00\u6b21\u53c8\u4f55\u59a8\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6b7b\u8fc7\u4e00\u6b21\u7684\u4eba\uff0c\u4e5f\u8bb8\u66f4\u6015\u6b7b\u3002\u201d \u8001\u5200\u628a\u5b50\u540c\u610f\u8fd9\u2014\u70b9\uff0c\u201c\u53ef\u662f\u5728\u8fd9\u91cc\u8eb2\u7740\uff0c\u8ddf\u6b7b\u6709\u4ec0\u4e48\u5206\u522b\uff1f\u201d \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\u3002\u4ed6\u627f\u8ba4\u5206\u522b\u7684\u786e\u4e0d\u5927\u3002 \u8001\u5200\u628a\u5b50\u5200\u950b\u822c\u7684\u76ee\u5149\u5728\u7af9\u6574\u540e\u76ef\u7740\u4ed6\uff0c\u201c\u4f60\u613f\u4e0d\u613f\u610f\u5728\u8fd9\u91cc\u803d\u4e00\u8f88\u5b50\uff1f\u201d \u9646\u5c0f\u51e4\u7acb\u523b\u6447\u5934\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u9664\u4e86\u6211\u4eec\u5916\uff0c\u8fd9\u91cc\u8fd8\u6709\u4e09\u5341\u4e03\u4f4d\u5ba2\u4eba\uff0c\u4f60\u597d\u50cf\u90fd\u5df2\u89c1\u8fc7\uff0c\u4f60\u770b\u51fa\u4e86 \u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4ec0\u4e48\u90fd\u6ca1\u6709\u770b\u51fa\u6765\u3002\u201d \u8001\u5200\u628a\u5b50\u663e\u7136\u5f88\u6ee1\u610f\uff0c\u201c\u4f60\u5f53\u7136\u770b\u4e0d\u51fa\u7684\uff0c\u56e0\u4e3a\u5927\u5bb6\u7684\u68f1\u89d2\u90fd\u5df2\u88ab\u78e8\u5706\uff0c\u201c\uff0c\u770b \u8d77\u6765\u90fd\u662f\u5f88\u5e73\u51e1\u5eb8\u788c\u7684\u4eba\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5176\u5b9e\u5462\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u80fd\u5230\u8fd9\u91cc\u6765\u7684\uff0c\u6bcf\u4e2a\u4eba\u90fd\u662f\u597d\u624b\uff0c\u6bcf\u4e2a\u4eba\u90fd\u6709\u6bb5\u8f89\u714c\u7684\u5386\u53f2\uff0c\u90fd \u8ddf\u4f60\u4e00\u6837\u4e0d\u7518\u5bc2\u5bde\uff0c\u8c01\u4e5f\u4e0d\u613f\u610f\u5728\u8fd9\u91cc\u803d\u2014\u8f88\u5b50\u3002\u201d \u4ed6\u7684\u58f0\u97f3\u5f88\u6109\u5feb\uff0c\u201c\u5927\u5bb6\u552f\u2014\u80fd\u91cd\u89c1\u5929\u65e5\u7684\u673a\u4f1a\uff0c\u5c31\u662f\u505a\u6210\u8fd9\u4ef6\u4e8b\u3002 \u9646\u5c0f\u51e4\u7ec8\u4e8e\u95ee\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u7a76\u7adf\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u5f88\u5feb\u5c31\u4f1a\u77e5\u9053\u7684\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5f88\u5feb\u662f\u4ec0\u4e48\u65f6\u5019\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u5c31\u662f\u73b0\u5728ao\u8fd9\u53e5\u8bdd\u521a\u8bf4\u5b8c\uff0c\u5916\u9762\u5df2\u6709\u949f\u58f0\u54cd\u8d77\uff0c\u8001\u5200\u628a\u5b50\u7ad9\u8d77 \u6765\uff0c\u58f0\u97f3\u66f4\u6109\u5feb\uff0c\u201c\u53ef\u662f\u6211\u4eec\u4e00\u5b9a\u8981\u5148\u5403\u996d\uff0c\u4eca\u5929\u4e2d\u5348\u8fd9\u987f\u996d\u6211\u4fdd\u8bc1\u4f60\u4e00\u5b9a\u4f1a\u6ee1\u610f \u7684\u3002\u201d \u83dc\u5f88\u591a\uff0c\u9152\u5374\u5f88\u5c11\uff0c\u8005\u5200\u628a\u5b50\u663e\u7136\u5e0c\u671b\u6bcf\u4e2a\u4eba\u90fd\u4fdd\u6301\u6e05\u9192\u3002 \u53ef\u662f\u4ed6\u81ea\u5df1\u5374\u559d\u4e86\u7528\u91d1\u6a3d\u88c5\u7740\u7684\u5927\u534a\u676f\u6ce2\u65af\u8461\u8404\u9152\uff0c\u540e\u6765\u5c45\u7136\u8fd8\u6dfb\u4e86\u4e00\u6b21\u3002 \u8fd9\u662f\u9646\u5c0f\u51e4\u7b2c\u4e00\u6b21\u770b\u4ed6\u559d\u9152\u3002 \u201c\u5bf9\u4ed6\u6765\u8bf4\uff0c\u4eca\u5929\u4e00\u5b9a\u662f\u4e2a\u5927\u65e5\u5b50\uff0c\u201c\u9646\u5c0f\u51e4\u5fc3\u91cc\u5728\u60f3\uff1a\u201c\u4e3a\u4e86\u7b49\u8fd9\u4e00\u5929\uff0c\u4ed6\u4e00 \u5b9a\u5df2\u7b49\u4e86\u5f88\u4e45\u3002\u201d \u5927\u5bb6\u90fd\u5728\u4f4e\u7740\u5934\uff0c\u9ed8\u9ed8\u5730\u5403\u996d\uff0c\u5374\u5403\u5f97\u5f88\u5c11\uff0c\u5927\u90e8\u5206\u90fd\u6ca1\u6709\u559d\u9152\u3002 \u6240\u4ee5\u9646\u5c0f\u51e4\u5c31\u53ef\u4ee5\u591a\u559d\u4e00\u70b9\uff0c\u7136\u540e\u624d\u80fd\u4ee5\u6109\u5feb\u7684\u773c\u795e\u53bb\u6253\u91cf\u8fd9\u4e9b\u4eba\u3002 \u867d\u7136\u5927\u5bb6\u7a7f\u7684\u90fd\u662f\u5bbd\u5927\u4fdd\u5b88\u7684\u957f\u888d\uff0c\u5728\u5927\u5385\u91cc\u9634\u6697\u7684\u5149\u7ebf\u4e0b\u770b\u6765\uff0c\u8fd8\u662f\u6709\u51e0\u4e2a\u4eba \u663e\u5f97\u6bd4\u8f83\u89e6\u76ee\u3002 \u4e00\u4e2a\u662f\u957f\u7740\u6ee1\u8138\u91d1\u94b1\u7663\u7684\u58ee\u6c49\uff0c\u4e24\u676f\u9152\u559d\u4e0b\u53bb\uff0c\u5c31\u4f7f\u5f97\u4ed6\u8138\u4e0a\u6bcf\u5757\u7663\u770b\u6765\u90fd\u50cf\u662f \u679a\u53d1\u4eae\u7684\u94dc\u94b1\u3002 \u4e00\u4e2a\u4eba\u7d2b\u9762\u957f\u9aef\uff0c\u770b\u6765\u7adf\u6709\u51e0\u5206\u50cf\u662f\u620f\u53f0\u4e0a\u7684\u5173\u516c\uff0c\u2014\u4e2a\u4eba\u8111\u6ee1\u80a0\u80a5\uff0c\u809a\u5b50\u7403\u4e00 \u822c\u51f8\u51fa\u6765\uff0c\u4e00\u4e2a\u4eba\u76f8\u8c8c\u4e25\u8083\uff0c\u5c31\u50cf\u5750\u5728\u5211\u5802\u4e0a\u7684\u6cd5\u53f2\uff0c\u4e00\u4e2a\u6ee1\u5634\u7259\u90fd\u6389\u5149\u4e86\u7684\u8001\u5a46 \u5a46\uff0c\u5403\u5f97\u5374\u6bd4\u8c01\u90fd\u591a\u3002 \u8fd8\u6709\u51e0\u4e2a\u7279\u522b\u5b89\u9759\u6c89\u9ed8\u7684\u524a\u7626\u8001\u4eba\uff0c\u4ed6\u4eec\u4ee4\u4eba\u89e6\u76ee\uff0c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u4ed6\u4eec\u6c89\u9ed8\uff0e\u9664\u4e86 \u67f3\u9752\u9752\u5916\uff0c\u5e74\u7eaa\u6700\u8f7b\u7684\u662f\u4e2a\u8138\u5706\u5982\u76c6\uff0c\u770b\u6765\u8fd8\u50cf\u662f\u4e2a\u8be5\u5b50\u822c\u7684\u5c0f\u77ee\u5b50\uff0c\u5e74\u7eaa\u6700\u5927\u7684\uff0c \u5c31\u662f\u8fd9\u51e0\u4e2a\u5b89\u9759\u6c89\u9ed8\u7684\u9ed1\u8863\u8001\u4eba\u3002 \u9646\u5c0f\u51e4\u8bd5\u63a2\u7740\uff0c\u60f3\u4ece\u8bb0\u5fc6\u4e2d\u627e\u51fa\u8fd9\u4e9b\u4eba\u7684\u6765\u5386\uff0c\u4ed6\u7b2c\u4e00\u4e2a\u60f3\u5230\u7684\uff0c\u5f53\u7136\u5c31\u662f\u201c\u91d1 \u94b1\u8c79\u201d\u82b1\u9b41\u3002 \u8fd9\u4e2a\u4eba\u8eab\u6750\u9ad8\u5927\uff0c\u9152\u559d\u5f97\u4e0d\u6bd4\u9646\u5c0f\u51e4\u5c11\uff0c\u52a8\u4f5c\u4eff\u4f5b\u5f88\u8fdf\u949d\uff0c\u6ee1\u8138\u7684\u7663\u4f7f\u4ed6\u770b\u8d77\u6765 \u663e\u5f97\u751a\u81f3\u6709\u70b9\u6ed1\u7a3d\u3002 \u53ef\u662f\u7b49\u5230\u4ed6\u6697\u5668\u51fa\u624b\u65f6\uff0c\u5c31\u7edd\u4e0d\u4f1a\u518d\u6709\u4eba\u89c9\u5f97\u6ed1\u7a3d\u4e86\u3002 \u6c5f\u5357\u82b1\u5bb6\u662f\u6c5f\u6e56\u4e2d\u6700\u8d1f\u76db\u540d\u7684\u6697\u5668\u4e16\u5bb6\uff0c\u4ed6\u5c31\u662f\u82b1\u5bb6\u7684\u5ae1\u7cfb\u5b50\u5f1f\u3002 \u6709\u4eba\u751a\u81f3\u8bf4\u4ed6\u7684\u6697\u5668\u529f\u592b\u5df2\u53ef\u6392\u540d\u5728\u5929\u4e0b\u524d\u4e09\u540d\u4e4b\u5185\u3002 \u9646\u5c0f\u51e4\u4e5f\u5df2\u6ce8\u610f\u5230\uff0c\u4ed6\u7684\u9152\u559d\u5f97\u867d\u591a\uff0c\u4e00\u53cc\u624b\u5374\u4ecd\u7136\u5f88\u7a33\u3002 \u90a3\u4e2a\u6cd5\u540f\u822c\u4e25\u8083\u7684\u4eba\uff0c\u662f\u4e0d\u662f\u6614\u5e74\u9ed1\u9053\u4e03\u5341\u4e8c\u5be8\u7684\u5211\u5802\u603b\u5802\u4e3b\u201c\u8fa3\u624b\u8ffd\u9b42\u201d\u6756\u94c1 \u5fc3\uff1f \u90a3\u8001\u5a46\u5a46\u662f\u4e0d\u662f\u201c\u79e6\u5cad\u53cc\u733f\u201d\u4e2d\u7684\u6bcd\u733f\uff1f\u53ea\u4e3a\u4e86\u4e00\u9897\u5728\u4f20\u8bf4\u4e2d\u53ef\u4ee5\u5ef6\u5e74\u76ca\u5bff\u7684\u5f02\u79cd \u87e0\u6843\uff0c\u5c31\u5272\u65ad\u4e86\u4ed6\u8001\u516c\u201c\u5723\u624b\u4ed9\u733f\u201d \u5a04\u5927\u5723\u7684\u8116\u5b50\u3002 \u90a3\u51e0\u4e2a\u4ece\u6765\u6ca1\u6709\u8bf4\u8fc7\u8bdd\u7684\u9ed1\u8863\u8001\u4eba\u662f\u8c01\u5462\uff1f\u8fd8\u6709\u90a3\u5706\u8138\u5927\u5934\u7684\u5c0f\u77ee\u5b50\uff1f \u9646\u5c0f\u51e4\u6ca1\u6709\u518d\u60f3\u4e0b\u53bb\uff0c\u56e0\u4e3a\u67f3\u9752\u9752\u6b63\u5728\u6084\u6084\u7684\u62c9\u4ed6\u8863\u89d2\uff0c\u6084\u6084\u7684\u95ee\u201c\u4f60\u8001\u5a46\u5462\uff1f\u201d \u9646\u5c0f\u51e4\u6014\u4e86\u6014\uff0c\u624d\u60f3\u8d77\u5979\u95ee\u7684\u662f\u53f6\u7075\u201c\u542c\u8bf4\u5979\u4e0d\u89c1\u4e86\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u60f3\u77e5\u9053\u5979\u5728\u54ea\u91cc\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e0d\u60f3\u3002\u201d \u67f3\u9752\u9752\u6487\u4e86\u6487\u5634\uff0c\u6545\u610f\u53f9\u606f\uff0c\u201c\u7537\u4eba\u679c\u7136\u6ca1\u6709\u4e00\u4e2a\u597d\u4e1c\u897f\uff0c\u53ef\u662f\u6211\u504f\u8981\u544a\u8bc9 \u4f60\u3002\u201d \u5979\u58f0\u97f3\u66f4\u4f4e\uff0c\u201c\u73b0\u5728\u5979\u4e00\u5b9a\u5728\u6c34\u91cc\u3002\u201d \u9646\u5c0f\u51e4\u4e0d\u61c2\uff0c\u201c\u5979\u600e\u4e48\u4f1a\u5728\u6c34\u91cc\uff1f\u4f60\u600e\u4e48\u77e5\u9053\u5979\u5728\u6c34\u91cc\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u56e0\u4e3a\u5979\u5077\u4e86\u4eba\u5bb6\u4e00\u4ef6\u5982\u7ade\u9c7c\u76ae\u6c34\u9760\uff0c\u548c\u56db\u5bf9\u5206\u6c34\u98de\u9c7c\u523a\u624d\u8d70\u7684\u3002\u201d \u9646\u5c0f\u51e4\u66f4\u5403\u60ca\uff0c\u4ee4\u4ed6\u5403\u60ca\u7684\u6709\u4e24\u4ef6\u4e8b\uff1a\u6c34\u9760\u548c\u9c7c\u523a\u4e0d\u4e00\u5b9a\u8981\u5728\u6c34\u91cc\u624d\u6709\u7528\uff0c\u5728\u6cbc \u6cfd\u7684\u70c2\u6ce5\u91cc\u4e5f\u540c\u6837\u7528\u5f97\u7740\u3002 \u53f6\u7075\u662f\u4e0d\u662f\u627e\u5979\u59d0\u59d0\u53bb\u4e86\uff1f\u5979\u600e\u4e48\u4f1a\u77e5\u9053\u6cbc\u6cfd\u91cc\u53d1\u751f\u7684\u90a3\u4e9b\u4e8b\u3002 \u5982\u610f\u6c34\u9760\u548c\u98de\u9c7c\u523a\u662f\u6c5f\u6e56\u4e2d\u5f88\u6709\u540d\u7684\u6c34\u4e2d\u5229\u5668\uff0c\u5c5e\u4e8e\u4e00\u4e2a\u5f88\u6709\u540d\u7684\u4eba\u3002 \u201c\u98de\u9c7c\u5c9b\u4e3b\u201d\u4e8e\u8fd8\u4e0d\u4f46\u540d\u52a8\u4e03\u6d77\uff0c\u5728\u4e2d\u539f\u6b66\u6797\u4e5f\u5f88\u6709\u540d\uff0c\u4e0d\u4f46\u6c34\u6027\u6781\u9ad8\uff0c\u5251\u6cd5\u4e5f \u4e0d\u5f31\u3002 \u8fd9\u4e2a\u4eba\u5982\u679c\u8fd8\u6ca1\u6709\u6b7b\uff0c\u5982\u679c\u4e5f\u5728\u8fd9\u91cc\uff0c\u5e94\u8be5\u4e5f\u5f88\u89e6\u76ee\u3002 \u53ef\u662f\u9646\u5c0f\u51e4\u5e76\u6ca1\u6709\u53d1\u73b0\u4ed6\u3002 \u67f3\u9752\u9752\u8fd8\u5728\u7b49\u4ed6\u7684\u53cd\u5e94\uff0c\u6240\u4ee5\u4e00\u76f4\u6ca1\u6709\u5f00\u53e3\u3002 \u9646\u5c0f\u51e4\u6c89\u541f\u7740\uff0c\u7ec8\u4e8e\u95ee\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u8001\u5200\u628a\u5b50\u77e5\u4e0d\u77e5\u9053\uff1f\u201d \u67f3\u9752\u9752\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u91cc\u597d\u50cf\u8fd8\u6ca1\u6709\u4ed6\u4e0d\u77e5\u9053\u7684\u4e8b\u3002\u201d \u53f6\u7075\u53bb\u627e\u5979\u59d0\u59d0\uff0c\u96be\u9053\u4e5f\u662f\u8001\u5200\u628a\u5b50\u6388\u610f\u7684\uff1f\u5426\u5219\u5979\u600e\u4e48\u4f1a\u77e5\u9053\u53f6\u96ea\u7684\u884c\u8e2a\uff1f \u9646\u5c0f\u51e4\u6ca1\u6709\u518d\u95ee\u522b\u7684\u3002\u95ea\u4e3a\u4ed6\u5ffd\u7136\u53d1\u73b0\u6709\u4e2a\u4eba\u5df2\u65e0\u58f0\u65e0\u606f\u7684\u5230\u4e86\u4ed6\u4eec\u8eab\u540e\u3002 \u4ed6\u56de\u8fc7\u5934\uff0c\u5c31\u770b\u89c1\u4e86\u4e00\u5f20\u6ca1\u6709\u8138\u7684\u8138\uff0c\u8d6b\u7136\u6b63\u662f\u90a3\u4ece\u4e0d\u9732\u9762\u7684\u52fe\u9b42\u4f7f\u8005\u3002 \u5927\u5385\u91cc\u6c14\u6c1b\u66f4\u6c89\u91cd\u4e25\u8083\uff0c\u5927\u5bb6\u5bf9\u8fd9\u4e2a\u6ca1\u6709\u8138\u7684\u4eba\u4eff\u4f5b\u90fd\u6709\u4e9b\u754f\u60e7\u3002 \u4ed6\u6ca1\u6709\u5750\u4e0b\uff0c\u53ea\u662f\u52a8\u4e5f\u4e0d\u52a8\u7684\u7ad9\u5728\u8001\u5200\u628a\u5b50\u8eab\u540e\u3002 \u4ed6\u8170\u4e0a\u4f69\u7740\u5251\u3002 \u5f62\u5f0f\u53e4\u96c5\u7684\u5251\u9798\u4e0a\uff0c\u6709\u4e03\u4e2a\u5200\u75a4\u822c\u7684\u5370\u5b50\uff0c\u672c\u6765\u4e0a\u9762\u663e\u7136\u9576\u7740\u6709\u73e0\u7389\u5b9d\u77f3\u3002 \u8fd9\u662f\u4e0d\u662f\u6b66\u5f53\u6d3e\u4e2d\uff0c\u552f\u6709\u638c\u95e8\u4eba\u624d\u80fd\u4f69\u5e26\u7684\u4e03\u91cc\u5b9d\u5251\uff01 \u5c31\u5728\u8fd9\u65f6\uff0c\u6d77\u5947\u9614\u5ffd\u7136\u7ad9\u8d77\u6765\uff0c\u7528\u6d2a\u949f\u822c\u7684\u58f0\u97f3\u5ba3\u5e03\uff1a\u201c\u5929\u96f7\u884c\u52a8\u8863\u5f00\u59cb\uff01\u201d \u7b2c\u5341\u4e00\u7ae0\u3000\u5929\u96f7\u884c\u52a8>> \u53e4\u9f99\u300a\u9646\u5c0f\u51e4\u7cfb\u5217\xb7\u5e7d\u7075\u5c71\u5e84\u300b \u7b2c\u5341\u4e00\u7ae0\u3000\u5929\u96f7\u884c\u52a8 \u5929\u96f7\u884c\u52a8\u7684\u8ba1\u5212\u4e2d\uff0c\u5206\u56db\u4e2a\u6b65\u9aa4 \u7b2c\u4e00\u6b65\u662f\uff1a\u9009\u6d3e\u4eba\u624b\uff0c\u5206\u914d\u4efb\u52a1\u3002 \u7b2c\u4e8c\u6b65\u662f\uff1a\u6613\u5bb9\u6539\u626e\uff0c\u5206\u6279\u4e0b\u5c71\u3002 \u7b2c\u4e09\u6b65\u662f\uff1a\u96c6\u5408\u5f85\u547d\uff0c\u51c6\u5907\u51fa\u51fb\u3002 \u7b2c\u56db\u6b65\u624d\u662f\u6b63\u5f0f\u884c\u52a8\u3002 \u73b0\u5728\u5f00\u59cb\u8fdb\u884c\u7684\u4e0d\u8fc7\u662f\u7b2c\u2014\u6b65\uff0c\u8fdb\u884c\u7684\u8fc7\u7a0b\u5df2\u4ee4\u4eba\u80c6\u6218\u5fc3\u60ca\u3002 \u5927\u5385\u4e2d\u6c14\u6c1b\u7684\u6c89\u91cd\u548c\u7d27\u5f20\u5df2\u8fbe\u5230\u9876\u70b9\uff0c\u8001\u5200\u628a\u5b50\u624d\u7ad9\u8d77\u6765\u3002 \u201c\u8fd9\u4e16\u4e0a\u6709\u5f88\u591a\u4eba\u65e9\u5c31\u8be5\u6b7b\u4e86\uff0c\u5374\u6ca1\u6709\u4eba\u6562\u53bb\u6cbb\u88c1\u4ed6\u4eec\uff0c\u6709\u5f88\u591a\u4e8b\u65e9\u5c31\u8be5\u505a\u4e86\uff0c \u5374\u6ca1\u6709\u4eba\u6562\u53bb\u505a\uff0c\u73b0\u5728\u6211\u4eec\u5c31\u662f\u8981\u53bb\u5bf9\u4ed8\u8fd9\u4e9b\u4eba\uff0c\u53bb\u505a\u8fd9\u4e9b\u4e8b\u3002\u201d \u9646\u5c0f\u51e4\u5ffd\u7136\u53d1\u73b0\u8fd9\u4e2a\u4eba\u7684\u786e\u662f\u4e2a\u5929\u751f\u7684\u9996\u9886\uff0c\u4e0d\u4f46\u6c89\u7740\u51b7\u9759\uff0c\u8ba1\u5212\u5468\u5bc6\uff0c\u800c\u4e14\u53e3 \u624d\u6781\u597d\uff0c\u53ea\u7528\u51e0\u53e5\u8bdd\u5c31\u5df2\u5c06\u8fd9\u6b21\u884c\u52a8\u89e3\u91ca\u5f97\u5f88\u6e05\u695a\u3002 \u201c\u6211\u4eec\u7684\u884c\u52a8\u5c31\u50cf\u662f\u5929\u4e0a\u7684\u96f7\u7586\u9739\u96f7\u4e00\u6837\uff0c\u6240\u4ee5\u5c31\u53eb\u505a\u5929\u96f7\u884c\u52a8\u3002\u201d \u5e7f\u9614\u7684\u5927\u5385\u4e2d\u53ea\u80fd\u542c\u5f97\u5230\u547c\u5438\u58f0\u548c\u5fc3\u8df3\u58f0\uff0c\u6bcf\u4e2a\u4eba\u90fd\u5728\u7b49\u7740\u4ed6\u8bf4\u4e0b\u53bb\u3002 \u8001\u5200\u628a\u5b50\u7684\u58f0\u97f3\u505c\u987f\u4e86\u5f88\u4e45\uff0c\u5c31\u597d\u50cf\u66b4\u98ce\u96e8\u524d\u90a3\u7247\u523b\u9759\u5bc2\u3002\u53c8\u597d\u50cf\u7279\u5730\u8981\u8ba9\u5927\u5bb6 \u5fc3\u91cc\u6709\u4e2a\u51c6\u5907\uff0c\u597d\u542c\u90a3\u4e00\u58f0\u77f3\u7834\u5929\u60ca\u7684\u96f7\u9706\u9739\u96f7\u3002 \u201c\u6211\u4eec\u7b2c\u2014\u6b21\u8981\u5bf9\u4ed8\u7684\u6709\u4e03\u4e2a\u4eba\uff0c\u201c\u4ed6\u53c8\u505c\u987f\u4e86\u2014\u4e0b\uff0c\u624d\u8bf4\u51fa\u8fd9\u4e03\u4e2a\u4eba\u7684\u540d\u5b57\uff0c \u201c\u6b66\u5f53\u77f3\u96c1\uff0c\u5c11\u6797\u94c1\u80a9\uff0c\u4e10\u5e2e\u738b\u5341\u888b\uff0c\u957f\u6c5f\u6c34\u4e0a\u98de\uff0c\u96c1\u8361\u9ad8\u884c\u7a7a\uff0c\u5df4\u5c71\u5c0f\u987e\u9053\u4eba\uff0c\u548c \u5341\u4e8c\u8fde\u73af\u575e\u7684\u9e70\u773c\u8001\u4e03\u3002\u201d \u672c\u5df2\u5f88\u9759\u5bc2\u7684\u5927\u5385\uff0c\u66f4\u6b7b\u5bc2\u5982\u575f\u5893\uff0c\u8fde\u547c\u5438\u5fc3\u8df3\u58f0\u90fd\u5df2\u505c\u6b62\u3002 \u9646\u5c0f\u51e4\u867d\u7136\u65e9\u77e5\u9053\u4ed6\u8981\u505a\u7684\u662f\u4ef6\u5927\u4e8b\uff0c\u53ef\u662f\u6bcf\u542c\u4ed6\u8bf4\u51fa\u4e00\u4e2a\u540d\u5b57\uff0c\u8fd8\u662f\u96be\u514d\u5403 \u60ca\u3002 \u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u6709\u4eba\u5f00\u59cb\u64e6\u6c57\uff0c\u559d\u9152\uff0c\u8fd8\u6709\u51e0\u4e2a\u4eba\u7adf\u6084\u6084\u8eb2\u5230\u684c\u4e0b\u53bb\u5455\u5410\u3002 \u8001\u5200\u628a\u5b50\u7684\u58f0\u97f3\u5374\u66f4\u9547\u5b9a\uff0c\u201c\u8fd9\u6b21\u884c\u52a8\u82e5\u6210\u529f\uff0c\u4e0d\u4f46\u5fc5\u80fd\u4ee4\u5929\u4e0b\u8f70\u52a8\uff0c\u6c5f\u6e56\u4fa7 \u76ee\uff0c\u800c\u4e14\u5bf9\u5927\u5bb6\u90fd\u6709\u597d\u5904\u3002\u201d \u4ed6\u518d\u6b21\u505c\u987f\uff0c\u201c\u6211\u5df2\u5c06\u8fd9\u6b21\u884c\u52a8\u7684\u6bcf\u2014\u4e2a\u7ec6\u8282\u90fd\u8ba1\u5212\u597d\uff0c\u672c\u8be5\u7edd\u5bf9\u6709\u628a\u63e1\u6210\u529f \u7684\uff0c\u53ea\u53ef\u60dc\u6bcf\u4ef6\u4e8b\u90fd\u96be\u514d\u6709\u610f\u5916\uff0c\u6240\u4ee5\u8fd9\u6b21\u884c\u52a8\u8fd8\u662f\u96be\u514d\u6709\u5371\u9669\uff0c\u6240\u4ee5\u6211\u4e5f\u4e0d\u52c9\u5f3a\u4efb \u4f55\u4eba\u53c2\u52a0\u3002\u201d \u4ed6\u76ee\u5149\u626b\u89c6\uff0c\u7a7f\u900f\u7af9\u7b20\uff0c\u5200\u950b\u822c\u4ece\u6bcf\u4e2a\u4eba\u8138\u4e0a\u63a0\u8fc7\uff0c\u201c\u4e0d\u613f\u53c2\u52a0\u7684\u4eba\uff0c\u73b0\u5728\u5c31\u53ef \u4ee5\u7ad9\u8d77\u6765\uff0c\u6211\u7edd\u4e0d\u52c9\u5f3a\u3002\u201d \u5927\u5385\u4e2d\u53c8\u662f\u4e00\u9635\u9759\u5bc2\uff0c\u8001\u5200\u628a\u5b50\u53c8\u7f13\u7f13\u5750\u4e0b\uff0c\u5c45\u7136\u53c8\u6dfb\u4e86\u534a\u676f\u9152\u3002 \u9646\u5c0f\u51e4\u4e5f\u5fcd\u4e0d\u4f4f\u53bb\u62ff\u9152\u676f\uff0c\u624d\u53d1\u73b0\u81ea\u5df1\u7684\u638c\u5fc3\u5df2\u5f00\u59cb\u5192\u6c57\u3002 \u76f4\u5230\u8fd9\u65f6\uff0c\u8fd8\u6ca1\u6709\u4e00\u4e2a\u4eba\u7ad9\u8d77\u6765\uff0c\u5374\u5ffd\u7136\u6709\u4eba\u95ee\u9053\uff1a\u201c\u4e0d\u613f\u53bb\u7684\u4eba\uff0c\u4ee5\u540e\u662f\u4e0d\u662f \u8fd8\u53ef\u4ee5\u7559\u5728\u8fd9\u91cc\uff1f\u201d \u8001\u5200\u628a\u5b50\u7684\u56de\u7b54\u5f88\u786e\u5b9a\uff0c\u201c\u662f\u7684\uff0c\u968f\u4fbf\u4f60\u7559\u591a\u4e45\u90fd\u884c\u3002\u201d \u95ee\u8bdd\u7684\u4eba\u53c8\u8fdf\u7591\u7247\u523b\uff0c\u7ec8\u4e8e\u6162\u6162\u7684\u7ad9\u8d77\u6765\uff0c\u809a\u5b50\u4e5f\u8ddf\u7740\u51f8\u51fa\u3002 \u9646\u5c0f\u51e4\u5ffd\u7136\u60f3\u8d77\u8fd9\u4e2a\u4eba\u662f\u8c01\u4e86\uff0c\u4e8c\u5341\u5e74\u524d\uff0c\u6c5f\u6e56\u4e2d\u66fe\u7ecf\u6709\u56db\u602a\uff0c\u2014\u4e2a\u5947\u80d6\uff0c\u4e00\u4e2a \u5947\u7626\uff0c\u4e00\u4e2a\u5947\u9ad8\uff0c\u4e00\u4e2a\u5947\u77ee\u3002 \u5947\u80d6\u5982\u732a\u7684\u90a3\u4e2a\u4eba\u5c31\u53eb\u505a\u6731\u975e\uff0c\u5012\u8fc7\u6765\u5ff5\u5c31\u6210\u4e86\u201c\u80a5\u732a\uff3c\u53ef\u662f\u8ba4\u5f97\u4ed6\u7684\u4eba\uff0c\u90fd\u77e5 \u9053\u4ed6\u975e\u4f46\u4e0d\u662f\u732a\uff0c\u800c\u4e14\u5341\u5206\u7cbe\u660e\u80fd\u5e72\uff0c\u8ddf\u4ed6\u4ea4\u8fc7\u624b\u7684\u4eba\uff0c\u66f4\u4e0d\u4f1a\u8ba4\u4e3a\u4ed6\u662f\u732a\uff0c\u56e0\u4e3a\u4ed6 \u4e0d\u4f46\u51fa\u624b\u5feb\uff0c\u5e76\u4e14\u624b\u4e5f\u72e0\uff0c\u2014\u624b\u5730\u8d9f\u5200\u6cd5\u201c\u6ee1\u5730\u5f00\u82b1\u516b\u5341\u2014\u5f0f\uff3c\u66f4\u662f\u6b66\u6797\u5c11\u89c1\u7684\u7edd \u6280\u3002 \u9646\u5c0f\u51e4\u77e5\u9053\u8fd9\u4e2a\u4eba\u4e00\u5b9a\u5c31\u662f\u6731\u975e\uff0c\u5374\u60f3\u4e0d\u5230\u7b2c\u4e00\u4e2a\u7ad9\u8d77\u6765\u7684\u4eba\u4f1a\u662f\u4ed6\u3002 \u6731\u975e\u5e76\u4e0d\u662f\u80c6\u5c0f\u6015\u6b7b\u7684\u4eba\u3002 \u201c\u53ef\u662f\u6211\u4e0d\u80fd\u53bb\uff0c\u201c\u4ed6\u6709\u7406\u7531\uff0c\u201c\u56e0\u4e3a\u6211\u592a\u80d6\uff0c\u76ee\u6807\u592a\u660e\u663e\uff0c\u968f\u4fbf\u6211\u600e\u4e48\u6837\u6613\u5bb9 \u6539\u626e\uff0c\u522b\u4eba\u8fd8\u662f\u4e00\u773c\u5c31\u53ef\u4ee5\u8ba4\u51fa\u6211\u8fd9\u7406\u7531\u5f88\u4e0d\u9519\u3002 \u751a\u81f3\u8fde\u8001\u5200\u628a\u5b50\u90fd\u4e0d\u80fd\u4e0d\u627f\u8ba4\uff0c\u5374\u53c8\u4e0d\u7981\u89c9\u5f97\u5f88\u60cb\u60dc\u3002 \u6731\u975e\u7684\u5730\u8d9f\u529f\u592b\uff0c\u6c5f\u6e56\u4e2d\u81f3\u4eca\u65e0\u4eba\u80fd\u53ca\uff0c\u8fd9\u79cd\u4eba\u624d\u8001\u5200\u628a\u5b50\u663e\u7136\u5f88\u9700\u8981\u3002 \u53ef\u662f\u4ed6\u53ea\u4e0d\u8fc7\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u5e76\u6ca1\u6709\u8bf4\u4ec0\u4e48\u3002 \u6240\u4ee5\u522b\u7684\u4eba\u4e5f\u6709\u80c6\u5b50\u7ad9\u8d77\u6765\u6709\u4e86\u7b2c\u2014\u4e2a\uff0c\u5f53\u7136\u5c31\u4f1a\u6709\u7b2c\u4e8c\u4e2a\uff0c\u7136\u540e\u5c31\u8d8a\u6765\u8d8a\u591a\u3002 \u8001\u5200\u628a\u5b50\u4e00\u76f4\u51b7\u51b7\u7684\u770b\u7740\uff0c\u4e0d\u52a8\u58f0\u8272\uff0c\u76f4\u5230\u7b2c\u5341\u4e09\u4e2a\u4eba\u7ad9\u8d77\u6765\uff0c\u4ed6\u624d\u8038\u7136\u52a8\u5bb9\u3002 \u8fd9\u4e2a\u4eba\u624d\u76f8\u8c8c\u5e73\u51e1\uff0c\u8868\u60c5\u5446\u677f\uff0c\u770b\u6765\u5e76\u4e0d\u8d77\u773c\u3002 \u53ef\u662f\u4e00\u4e2a\u4eba\u82e5\u80fd\u4eca\u8001\u5200\u628a\u5b50\u8038\u7136\u52a8\u5bb9\uff0c\u5f53\u7136\u7edd\u4e0d\u4f1a\u662f\u4e2a\u5e73\u51e1\u7684\u4eba\u7269\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u53bb\uff1f\u201d \u8fd9\u4eba\u9762\u4e0a\u6beb\u65e0\u8868\u60c5\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u4f60\u8bf4\u4e0d\u53bb\u7684\u4eba\u7ad9\u8d77\u6765\uff0c\u6211\u5df2\u7ad9\u8d77\u6765\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53bb\uff1f\u201d \u8fd9\u4eba\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u7684\u6c34\u9760\u548c\u9c7c\u523a\u5168\u4e0d\u89c1\u4e86\u3002\u201d \u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u9646\u5c0f\u51e4\u4e5f\u4e0d\u7981\u8038\u7136\u52a8\u5bb9\uff0c\u4ed6\u5b9e\u5728\u60f3\u4e0d\u5230\u8fd9\u4e2a\u5e73\u51e1\u5446\u677f\u7684\u4eba\uff0c\u5c31\u662f \u6614\u5e74\u5357\u6d77\u7fa4\u5251\u4e2d\uff0c\u540d\u58f0\u4ec5\u6b21\u4e8e\u767d\u4e91\u57ce\u4e3b\u7684\u516d\u4f4d\u5c9b\u4e3b\u4e4b\u4e00\u3002 \u8fd9\u4e2a\u4eba\u7adf\u662f\u98de\u9c7c\u5c9b\u4e3b\u4e8e\u8fd8\uff01 \u5728\u9646\u4e0a\uff0c\u767d\u4e91\u57ce\u5b9a\u662f\u540d\u52a8\u5929\u4e0b\u7684\u5251\u5ba2\uff0c\u5728\u6c34\u91cc\uff0c\u4ed6\u5374\u7edd\u5bf9\u6bd4\u4e0d\u4e0a\u4e8e\u8fd8\u3002 \u8001\u5200\u628a\u5b50\u7684\u8fd9\u6b21\u4efb\u52a1\uff0c\u663e\u7136\u4e5f\u5f88\u9700\u8981\u4e00\u4e2a\u6c34\u6027\u7cbe\u719f\u7684\u53ea\u542c\u201c\u6ce2\u201d\u7684\u2014\u58f0\uff0c\u4ed6\u624b\u91cc \u7684\u9152\u676f\u7a81\u7136\u788e\u4e86\uff0c\u7c89\u788e\u3002 \u4e5f\u5c31\u5728\u8fd9\u65f6\uff0c\u4e00\u58f0\u60e8\u547c\u58f0\u8d77\uff0c\u5750\u5728\u675c\u94c1\u5fc3\u8eab\u65c1\u7684\u4e00\u4e2a\u4eba\u521a\u7ad9\u8d77\u6765\uff0c\u53c8\u5012\u4e0b\u53bb\uff0c\u6574 \u4e2a\u4eba\u6251\u5012\u5728\u684c\u4e0a\uff0c\u538b\u788e\u4e86\u4e00\u7247\u676f\u76cf\uff0c\u9152\u6c41\u56db\u6ea2\u3002 \u7136\u540e\u5927\u5bb6\u5c31\u770b\u89c1\u2014\u80a1\u9c9c\u76bf\u968f\u7740\u9152\u6c41\u6ea2\u51fa\uff0c\u67d3\u7ea2\u4e86\u684c\u5e03\u3002 \u675c\u94c1\u5fc3\u624b\u91cc\u7684\u4e00\u53cc\u7b77\u5b50\u4e5f\u65e9\u5df2\u53d8\u6210\u7ea2\u7684\uff0c\u5f53\u7136\u4e5f\u662f\u88ab\u9c9c\u76bf\u67d3\u7ea2\u7684\u3002 \u4e8e\u8fd8\u970d\u7136\u56de\u5934\uff0c\u201c\u4f60\u6740\u4e86\u4ed6\uff1f\u201d \u675c\u94c1\u5fc3\u627f\u8ba4\uff0c\u201c\u8fd9\u8fd8\u662f\u6211\u7b2c\u4e00\u6b21\u7528\u7b77\u5b50\u6740\u4eba\u3002\u201d \u4e8e\u8fd8\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u6740\u4ed6\uff1f\u201d \u675c\u94c1\u5fc3\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u77e5\u9053\u7684\u79d8\u5bc6\u5df2\u592a\u591a\uff0c\u4ed6\u6d3b\u7740\uff0c\u6211\u4eec\u5c31\u53ef\u80fd\u4f1a\u6b7b\u3002 \u4ed6\u7528\u6cbe\u7740\u8840\u7684\u7b77\u5b50\u5939\u4e86\u5757\u5e72\u8d1d\uff0c\u6162\u6162\u5480\u56bc\uff0c\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u7728\u3002 \u201c\u8fa3\u624b\u65e0\u60c5\u201d\u675c\u94c1\u5fc3\uff0c\u672c\u6765\u5c31\u662f\u4e2a\u6740\u4eba\u4e0d\u7728\u773c\u7684\u72e0\u89d2\u8272\u3002 \u4e8e\u8fd8\u76ef\u7740\u4ed6\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u4ed6\u77e5\u9053\u591a\u5c11\u79d8\u5bc6\uff0c\u6211\u4e5f\u540c\u6837\u77e5\u9053\uff0c\u4f60\u662f\u4e0d\u662f\u4e5f\u8981\u6740\u4e86 \u6211\u3002\u201d \u675c\u94c1\u5fc3\u51b7\u51b7\u9053\uff1a\u201c\u662f\u7684qu\u4ed6\u8fd8\u662f\u8fde\u773c\u775b\u90fd\u6ca1\u6709\u7728\uff0c\u201c\u4e0d\u53bb\u7684\u4eba\uff0c\u4e00\u4e2a\u90fd\u4f11\u60f3\u6d3b\u7740 \u8d70\u51fa\u8fd9\u5c4b\u5b50\u3002\u201d \u4e8e\u8fd8\u7684\u8138\u8272\u53d8\u4e86\uff0c\u8fd8\u6ca1\u6709\u5f00\u53e3\uff0c\u5df2\u6709\u4eba\u62a2\u7740\u9053\uff1a\u201c\u8fd9\u53e5\u8bdd\u8457\u662f\u8001\u5200\u628a\u5b50\u8bf4\u7684\uff0c\u6211 \u4e5f\u8ba4\u547d\u4e86\u53ef\u662f\u4f60\u2026\u2026\u201d \u4ed6\u6ca1\u6709\u8bf4\u4e0b\u53bb\uff0c\u56e0\u4e3a\u65c1\u8fb9\u5df2\u5ffd\u7136\u6709\u6839\u7b77\u5b50\u98de\u6765\uff0c\u4ece\u4ed6\u5de6\u8033\u7a7f\u8fdb\uff0c\u6709\u8033\u5bb9\u51fa\u3002 \u90a3\u4e2a\u6ca1\u6709\u7259\u7684\u8001\u5a46\u5a46\u624b\u91cc\u7684\u7b77\u5b50\u5df2\u53ea\u5269\u4e0b\u2014\u6839\uff0c\u6b63\u5728\u53f9\u7740\u6c14\u5583\u5583\u81ea\u8bed\uff0c\u201c\u53cc\u6728\u6865 \u597d\u8d70\uff0c\u72ec\u6728\u6865\u96be\u884c\uff0c\u770b\u6765\u6211\u53ea\u597d\u7528\u624b\u6293\u7740\u5403\u4e86\u3002\u201d \u5979\u679c\u7136\u7528\u624b\u6293\u8d77\u5757\u6392\u9aa8\u6765\uff0c\u7528\u4ec5\u6709\u7684\u4e24\u4e2a\u7259\u9f7f\u5543\u5f97\u6d25\u6d25\u6709\u5473\u3002 \u201c\u54d7\u5566\u5566\u201d\u2014\u2018\u58f0\u54cd\uff0c\u90a3\u8033\u6735\u91cc\u7a7f\u7740\u7b77\u5b50\u7684\u4eba\u4e5f\u5012\u4e86\u4e0b\u53bb\uff0c\u538b\u788e\u4e86\u4e00\u7247\u7897\u76cf\u3002 \u672c\u6765\u7ad9\u7740\u7684\u4eba\u5df2\u6709\u51e0\u4e2a\u60f3\u5077\u5750\u4e0b\u3002 \u675c\u94c1\u5fc3\u51b7\u51b7\u9053\uff1a\u201c\u5df2\u7ecf\u7ad9\u8d77\u6765\u7684\uff0c\u5c31\u4e0d\u8bb8\u5750\u4e0bqo\u6731\u975e\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u8fd9\u662f\u8c01\u7684\u610f \u601d\uff1f\u201d \u675c\u94c1\u5fc3\u9053\uff1a\u201c\u662f\u6211\u4eec\u5927\u5bb6\u7684\u610f\u601d\u3002\u201d \u6731\u975e\u8fdf\u7591\u7740\uff0c\u7ec8\u4e8e\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u6211\u5e76\u4e0d\u662f\u4e0d\u60f3\u53bb\uff0c\u53ea\u53ef\u60dc\u6211\u592a\u80d6\u4e86\uff0c\u82e5\u662f\u8981 \u6211\u53bb\uff0c\u9664\u975e\u628a\u6211\u50cf\u9762\u6761\u4e00\u6837\u6413\u7ec6\u70b9\u3002\u201d \u675c\u94c1\u5fc3\u9053\uff1a\u201c\u597d\u6413\u4ed6\uff01 \u90a3\u4e2a\u5706\u8138\u5927\u5934\u7684\u5c0f\u77ee\u5b50\u5ffd\u7136\u8df3\u8d77\u6765\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6211\u6765\u6413\u3002\u201d \u4ed6\u7684\u5934\u5927\u5982\u6597\uff0c\u8eab\u5b50\u5374\u53c8\u7ec6\u53c8\u5c0f\uff0c\u7ad9\u7740\u7684\u65f6\u5019\uff0c\u5c31\u50cf\u662f\u534a\u622a\u7b14\u7b77\u4e0a\u63d2\u7740\u4e2a\u5706\u67ff \u5b50\uff0c\u5b9e\u5728\u5f88\u6ed1\u7a3d\u53ef\u7b11\u3002 \u6731\u975e\u5374\u7b11\u4e0d\u51fa\uff0c\u8fde\u8138\u8272\u90fd\u53d8\u4e86\uff0c\u8fd9\u4e2a\u4eba\u7ad9\u5728\u4ed6\u9762\u524d\u5c31\u50cf\u662f\u4e2a\u5b69\u5b50\uff0c\u4ed6\u5374\u5bf9\u8fd9\u4e2a\u4eba \u6015\u5f97\u8981\u547d\u3002 \u770b\u770b\u4ed6\u8138\u4e0a\u7684\u60ca\u60e7\u4e4b\u8272\uff0c\u518d\u770b\u770b\u8fd9\u4e2a\u4eba\u7684\u5934\uff0c\u9646\u5c0f\u51e4\u7684\u8138\u8272\u4e5f\u53d8\u4e86\u3002 \u96be\u9053\u8fd9\u4e2a\u4eba\u5c31\u662f\u897f\u6781\u7fa4\u9b3c\u4e2d\uff0c\u6700\u5fc3\u9ed1\u624b\u8fa3\u7684\u201c\u5927\u5934\u9b3c\u738b\u201d \u53f8\u7a7a\u6597\u3002 \u4ed6\u6ca1\u6709\u770b\u9519\uff0c\u6731\u975e\u679c\u7136\u5df2\u558a\u51fa\u4e86\u8fd9\u540d\u5b57\uff0c\u201c\u53f8\u7a7a\u6597\uff0c\u8fd9\u4ef6\u4e8b\u4e0e\u4f60\u65e0\u5173\uff0c\u4f60\u60f3\u5e72\u4ec0 \u4e48\uff1f\u201d \u53f8\u7a7a\u6597\u9053\uff1a\u201c\u6211\u60f3\u6413\u4f60\u3002\u201d \u4ed6\u624b\u91cc\u4e5f\u6709\u53cc\u7b77\u5b50\uff0c\u7528\u4e24\u53ea\u624b\u5939\u5728\u624b\u91cc\uff0c\u5c31\u597d\u50cf\u662f\u5df2\u5c06\u8fd9\u53cc\u7b77\u5b50\u5f53\u4f5c\u4e86\u6731\u975e\uff0c\u7528 \u529b\u78cb\u4e86\u51e0\u6413\uff0c\u638c\u5fc3\u5ffd\u7136\u2026\u80a1\u7c89\u672b\u767d\u96ea\u822c\u843d\u4e0b\u6765\u3002 \u7b49\u4ed6\u644a\u5f00\u624b\u638c\uff0c\u7b77\u5b50\u5df2\u4e0d\u89c1\u4e86\uff0c\u4ed6\u7adf\u7528\u4e00\u53cc\u5b69\u5b50\u822c\u7684\u5c0f\u624b\uff0c\u5c06\u8fd9\u53cc\u53ef\u4ee5\u5f53\u4f5c\u5229\u5251 \u6740\u7684\u7b77\u5b50\uff0c\u6413\u6210\u4e86\u4e00\u5806\u7c89\u672b\u3002 \u6731\u975e\u7684\u8138\u5df3\u6254\u66f2\uff0c\u6574\u4e2a\u4eba\u90fd\u4eff\u4f5b\u8f6f\u4e86\uff0c\u762b\u5728\u6905\u5b50\u4e0a\uff0c\u53ef\u662f\u7b49\u5230\u53f8\u7a7a\u6597\u4f5c\u52bf\u6251\u8d77 \u65f6\uff0c\u4ed6\u5ffd\u7136\u5f80\u684c\u4e0b\u4e00\u94bb\uff0c\u53cc\u8098\u819d\u76d6\u4e00\u8d77\u7528\u529b\uff0c\u7728\u773c\u95f4\u5df2\u94bb\u8fc7\u4e86\u4e03\u516b\u5f20\u684c\u5b50\uff0c\u52a8\u4f5c\u4e4b\u654f \u6377\u7075\u5de7\uff0c\u65e0\u6cd5\u5f62\u5bb9\u3002 \u53ea\u53ef\u60dc\u684c\u5b50\u5e76\u4e0d\u662f\u5f20\u5f20\u90fd\u8fde\u63a5\u7740\u7684\uff0c\u53f8\u7a7a\u6597\u5df2\u5ef7\u8eab\u800c\u8d77\uff0c\u5341\u6307\u7b95\u5f20\uff0c\u770b\u51c6\u4e86\u4ed6\u4e00 \u4ece\u684c\u4e0b\u94bb\u51fa\uff0c\u7acb\u523b\u51cc\u7a7a\u4e0b\u51fb\u3002 \u8c01\u77e5\u6731\u975e\u7684\u52a8\u4f5c\u66f4\u5feb\uff0c\u53f3\u8098\u4e00\u633a\uff0c\u53c8\u94bb\u5165\u4e86\u5bf9\u9762\u7684\u684c\u4e0b\u3002\u53ea\u542c\u201c\u535c\u201d\u7684\u58f0\uff0c\u53f8\u7a7a \u6597\u5341\u6307\u5df2\u6d1e\u7a7f\u684c\u9762\uff0c\u7b49\u4ed6\u7684\u624b\u62d4\u51fa\u6765\uff0c\u684c\u4e0a\u5c31\u591a\u4e86\u5341\u4e2a\u6d1e\u3002 \u6731\u975e\u7d22\u6027\u8d56\u5728\u684c\u4e0b\u4e0d\u51fa\u6765\u4e86\uff0c\u53f8\u7a7a\u6597\u53f3\u81c2\u4e00\u626b\uff0c\u684c\u4e0a\u7684\u7897\u76cf\u5168\u88ab\u626b\u843d\uff0c\u6c64\u6c41\u9152\u83dc \u5c31\u6d12\u5728\u4e00\u4e2a\u4eba\u8eab\u4e0a\uff0c\u4e00\u4e2a\u5b89\u9759\u6c89\u9ed8\u7684\u9ed1\u8863\u8001\u4eba\u3002 \u53f8\u7a7a\u534a\u53cd\u624b\u2014\u638c\uff0c\u6b63\u60f3\u5c06\u684c\u5b50\u9707\u6563\uff0c\u7a81\u542c\u2014\u4e2a\u4eba\u9053\uff1a\u201c\u7b49\u7b49\u4e00\u53cc\u7b77\u5b50\u4f38\u4e86\u8fc7\u6765\uff0c \u5c16\u7aef\u671d\u4e0a\uff0c\u6307\u7740\u4ed6\u7684\u8109\u95e8\uff0c\u53f8\u7a7a\u6597\u8fd9\u4e00\u638c\u82e5\u662f\u62cd\u4e0b\u53bb\uff0c\u8fd9\u53ea\u624b\u5c31\u4f11\u60f3\u518d\u52a8\u4e86\u3002 \u5e78\u597d\u4ed6\u53cd\u5e94\u8fd8\u7b97\u5feb\uff0c\u7acb\u523b\u786c\u751f\u751f\u632b\u4f50\u4e86\u638c\u52bf\u3002 \u56db\u4e2a\u9ed1\u8863\u8001\u8005\u8fd8\u662f\u5b89\u5b89\u9759\u9759\u7684\u5750\u5728\u90a3\u91cc\uff0c\u51b7\u51b7\u7684\u770b\u7740\u4ed6\u3002 \u53f8\u7a7a\u6597\u597d\u50cf\u76f4\u5230\u73b0\u5728\u624d\u770b\u89c1\u4ed6\u4eec\uff0c\u521a\u5f00\u5927\u5634\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u52b3\u9a7e\u56db\u4f4d\u628a\u684c\u5b50 \u4e0b\u90a3\u6761\u80a5\u732a\u8e22\u51fa\u6765\uff1f\u201d \u8eab\u4e0a\u6e85\u4e86\u9152\u6c41\u7684\u9ed1\u8863\u8001\u8005\u51b7\u51b7\u9053\uff1a\u201c\u4e0d\u80fd\u3002\u201d \u53f8\u7a7a\u6597\u9053\uff1a\u201c\u4f60\u60f3\u62a4\u7740\u4ed6\uff1f\u201d \u9ed1\u8863\u8001\u8005\u9053\uff1a\u201c\u4eba\u4e0d\u72af\u6211\uff0c\u6211\u4e0d\u72af\u4eba\u3002\u201d \u53f8\u7a7a\u6597\u9053\uff1a\u201c\u8c01\u72af\u4e86\u4f60\uff1f\u201d \u9ed1\u8863\u8001\u8005\u9053\uff1a\u201c\u4f60\u3002\u201d \u53f8\u7a7a\u6597\u4e0d\u7b11\u4e86\uff0c\u201c\u72af\u4e86\u4f60\u53c8\u600e\u4e48\u6837\uff1f\u201d \u9ed1\u8863\u8001\u8005\u9053\uff1a\u201c\u4eba\u82e5\u662f\u72af\u6211\uff0c\u5c31\u4e0d\u662f\u4eba\u3002\u201d \u53f8\u7a7a\u6597\u9053\uff1a\u201c\u8c01\u4e0d\u662f\u4eba\uff1f\u201d \u9ed1\u8863\u8001\u8005\u9053\uff1a\u201c\u4f60\u3002\u201d \u53f8\u7a7a\u6597\u9053\uff1a\u201c\u6211\u672c\u5c31\u4e0d\u662f\u4eba\uff0c\u662f\u9b3c\u3002\u201d \u9ed1\u8863\u8001\u8005\u9053\uff1a\u201c\u4e5f\u4e0d\u662f\u9b3c\uff0c\u662f\u755c\u751f\u3002\u201d \u4ed6\u51b7\u51b7\u7684\u63a5\u7740\u9053\uff1a\u201c\u6211\u4e0d\u6740\u4eba\uff0c\u53ea\u6740\u755c\u751f\uff0c\u6740\u4e00\u4e24\u4e2a\u755c\u751f\uff0c\u4e0d\u80fd\u7b97\u5f00\u6740\u6212\u3002\u201d \u53f8\u7a7a\u6597\u53cc\u62f3\u4e00\u63e1\uff0c\u5168\u8eab\u7684\u9aa8\u8282\u90fd\u54cd\u4e86\u8d77\u6765\uff0c\u5706\u76c6\u822c\u7684\u8138\u5df2\u53d8\u6210\u94c1\u9752\u8272\u3002 \u8001\u5200\u628a\u5b50\u5ffd\u7136\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u6211\u8fd8\u6709\u7528\uff0c\u5434\u5148\u751f\u653e\u4ed6\u4e00\u9a6c\u5982\u4f55\uff1f\u201d \u9ed1\u8863\u8001\u8005\u6c89\u541f\u7740\uff0c\u7ec8\u4e8e\u70b9\u5934\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u53ea\u8981\u4ed6\u2026\u53ea\u624b\u3002\u201d \u53f8\u7a7a\u6597\u53c8\u7b11\u4e86\uff0c\u5927\u7b11\uff0c\u7b11\u58f0\u5982\u9b3c\u54ed\u3002 \u4ed6\u5de6\u624b\u7ec3\u7684\u662f\u767d\u9aa8\u722a\uff0c\u53f3\u624b\u7ec3\u7684\u662f\u9ed1\u9b3c\u722a\uff0c\u6bcf\u53ea\u624b\u4e0a\u90fd\u4e88\u5c11\u6709\u4e8c\u5341\u5e74\u82e6\u7ec3\u7684\u529f \u529b\uff0c\u8981\u4ed6\u7684\u2014\u53ea\u624b\uff0c\u7b49\u4e8e\u8981\u4ed6\u7684\u534a\u6761\u547d\u3002 \u9ed1\u8863\u8001\u8005\u9053\uff1a\u201c\u6211\u5c31\u8981\u4f60\u7684\u5de6\u624b\u3002\u201d \u53f8\u7a7a\u6597\u9053\uff1a\u201c\u597d\uff0c\u6211\u7ed9\u4f60\u3002\u201d \u201c\u4f60\u201d\u5b57\u51fa\u53e3\uff0c\u53cc\u722a\u9f50\u51fa\uff0c\u4e00\u53ea\u624b\u5df2\u53d8\u5f97\u96ea\u767d\uff0c\u53e6\u4e00\u53ea\u624b\u5374\u53d8\u6210\u6f06\u9ed1\u3002 \u4ed6\u5df2\u5c06\u4e8c\u5341\u5e74\u7684\u529f\u529b\u5168\u90fd\u4f7f\u4e86\u51fa\u6765\uff0c\u53ea\u8981\u88ab\u4ed6\u6307\u5c16\u4e00\u89e6\uff0c\u5c31\u7b97\u662f\u77f3\u4eba\u4e5f\u5f97\u591a\u51fa\u5341 \u4e2a\u6d1e\u3002 \u9ed1\u8863\u8001\u8005\u8fd8\u8db3\u7aef\u7edd\u4e0d\u52a8\uff0c\u53ea\u53f9\u4e86\u53e3\u6c14\uff0c\u957f\u8896\u6d41\u4e91\u822c\u5377\u51fa\u3002 \u53ea\u542c\u201c\u683c\u201d\u7684\u2014\u54cd\uff0c\u5982\u9504\u65ad\u841d\u535c\uff0c\u63a5\u7740\u53c8\u662f\u4e00\u58f0\u60e8\u547c\u3002 \u53f8\u7a7a\u6597\u7684\u4eba\u5df2\u98de\u4e86\u51fa\u53bb\uff0c\u649e\u4e0a\u5899\u58c1\uff0c\u6ed1\u4e0b\u6765\u5c31\u4e0d\u80fd\u52a8\u4e86\uff0c\u53cc\u624b\u9c9c\u8840\u6dcb\u6f13\uff0c\u5341\u6307\u90fd \u5df2\u9504\u65ad\u3002 \u9ed1\u8863\u8001\u8005\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u672c\u6765\u53ea\u60f3\u8981\u4ed6\u4e00\u53ea\u624b\u7684\u3002\u201d \u53e6\u4e00\u767d\u53d1\u8001\u8005\u51b7\u51b7\u9053\uff1a\u201c\u53ea\u8981\u4e00\u53ea\u624b\uff0c\u7528\u4e0d\u7740\u4f7f\u51fa\u4e03\u6210\u529b\u3002 \u9ed1\u8863\u8001\u8005\u9053\uff1a\u201c\u6211\u5df2\u6709\u591a\u5e74\u672a\u51fa\u624b\uff0c\u529b\u91cf\u5df2\u634f\u4e0d\u51c6\u4e86\uff0c\u6211\u4e5f\u9ad8\u4f30\u4e86\u4ed6oo\u767d\u4e86\u8001\u8005 \u9053\uff1a\u201c\u6240\u4ee5\u4f60\u9519\u4e86\uff0c\u755c\u751f\u4e5f\u662f\u4e00\u6761\u547d\uff0c\u4f60\u8fd8\u662f\u5f00\u4e86\u6740\u6212\u3002\u201d \u9ed1\u8863\u8001\u8005\u9053\uff1a\u201c\u662f\uff0c\u6211\u9519\u4e86\uff0c\u6211\u4f5b\u6148\u60b2qo\u56db\u4e2a\u4eba\u540c\u65f6\u53cc\u624b\u5408\u5341\uff0c\u53e3\u8bf5\u4f5b\u53f7\uff0c\u6162\u6162 \u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u9762\u5bf9\u8001\u5200\u628a\u5b50\uff0c\u201c\u6211\u7b49\u5148\u544a\u9000\uff0c\u9762\u58c1\u601d\u8fc7\u4e09\u65e5\uff0c\u4ee5\u8c22\u5e84\u4e3b\u3002\u201d \u8001\u5200\u628a\u5b50\u5c45\u7136\u4e5f\u7ad9\u8d77\u6765\uff0c\u9053\uff1a\u201c\u662f\u4ed6\u81ea\u5bfb\u6b7b\u8def\uff0c\u5148\u751f\u4f55\u5fc5\u81ea\u8d23\uff1f\u201d \u9ed1\u8863\u8001\u8005\u9053\uff1a\u201c\u5e84\u4e3b\u5982\u6709\u5dee\u9063\uff0c\u6211\u7b49\u5fc5\u6765\u6548\u547d\u3002\u201d \u8001\u5200\u628a\u5b50\u4eff\u4f5b\u677e\u4e86\u53e3\u6c14\uff0c\u7acb\u523b\u62f1\u624b\u9053\uff1a\u201c\u8bf7\u3002\u201d \u9ed1\u8863\u8001\u8005\u9053\uff1a\u201c\u8bf7\u3002\u201d \u56db\u4e2a\u4eba\u540c\u65f6\u8d70\u51fa\u53bb\uff0c\u6b65\u5c65\u5b89\u8be6\u7f13\u6162\uff0c\u8d70\u5230\u9646\u5c0f\u51e4\u9762\u524d\uff0c\u5ffd\u7136\u505c\u4e0b\u3002 \u767d\u53d1\u8001\u8005\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u9646\u516c\u5b50\u8fd1\u6765\u53ef\u66fe\u89c1\u5230\u82e6\u74dc\u4e0a\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53bb\u5e74\u89c1\u8fc7\u51e0\u6b21\u3002\u201d \u767d\u53d1\u8001\u8005\u9053\uff1a\u201c\u4e0a\u4eba\u5999\u624b\u70f9\u8c03\uff0c\u505a\u51fa\u7684\u7d22\u658b\u5929\u4e0b\u7b2c\u4e00\uff0c\u9646\u516c\u5b50\u7684\u53e3\u798f\u60f3\u5fc5\u4e0d \u6d45\u3002\u2026\u9646\u5c0f\u51e4\u7b11\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u767d\u53d1\u8001\u8005\u9053\uff1a\u201c\u90a3\u4e48\u4ed6\u7684\u8eab\u5b50\u60f3\u5fc5\u8fd8\u5065\u7855\u5982\u524d\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u662f\u7684oo\u767d\u53d1\u8001\u8005\u53cc\u624b\u5408\u5341\uff0c\u9053\uff1a\u201c\u6211\u4f5b\u6148\u60b2\uff0c\u5929\u4f51\u5584\u4eba\u2026\u2026\u201d \u56db\u4e2a\u4eba\u53c8\u540c\u65f6\u53e3\u8bf5\u4f5b\u53f7\uff0c\u6162\u6162\u7684\u8d70\u4e86\u51fa\u53bb\uff0c\u6b65\u5c65\u8fd8\u662f\u90a3\u4e48\u5b89\u8be6\u5e73\u7a33\u3002 \u9646\u5c0f\u51e4\u7684\u624b\u811a\u5374\u5df2\u51b0\u51b7\u3002 \u4ed6\u7ec8\u4e8e\u60f3\u51fa\u8fd9\u56db\u4e2a\u4eba\u7684\u6765\u5386\uff0c\u770b\u5230\u8001\u5200\u628a\u5b50\u5bf9\u4ed6\u4eec\u7684\u606d\u656c\u795e\u60c5\uff0c\u770b\u5230\u90a3\u4e00\u624b\u6d41\u4e91 \u98de\u8896\u7684\u5a01\u529b\uff0c\u770b\u5230\u4ed6\u4eec\u4f5b\u5bb6\u793c\u6570\uff0c\u4ed6\u624d\u60f3\u8d77\u6765\u7684\u3002 \u4ed6\u4ee5\u524d\u4e00\u76f4\u60f3\u4e0d\u51fa\uff0c\u53ea\u56e0\u4e3a\u4ed6\u4eec\u5df2\u84c4\u4e86\u5934\u53d1\uff0c\u6613\u4e86\u50e7\u8863\uff0c\u4ed6\u5f53\u7136\u4e0d\u4f1a\u60f3\u5230\u4ed6\u4eec\u662f \u51fa\u5bb6\u7684\u548c\u5c1a\uff0c\u66f4\u60f3\u4e0d\u5230\u4ed6\u4eec\u5c31\u662f\u5c11\u6797\u5bfa\u7684\u4e94\u7f57\u6c49\u3002 \u4e94\u7f57\u6c49\u672c\u662f\u5ae1\u4eb2\u7684\u5144\u5f1f\uff0c\u540c\u65f6\u524a\u53d1\u4e3a\u50e7\uff0c\u6295\u5165\u5c11\u6797\uff0c\u73b0\u5728\u53ea\u5269\u4e0b\u56db\u4e2a\u4eba\uff0c\u53ea\u56e0\u4e3a \u5927\u54e5\u65e0\u9f99\u7f57\u6c49\u5df2\u6b7b\u4e86\u3002 \u4ed6\u4eec\u5728\u5c11\u5e74\u65f6\u5c31\u5df2\u7eb5\u6a2a\u6c5f\u6e56\uff0c\u6740\u4eba\u65e0\u7b97\uff0c\u4eba\u79f0\uff0c\u201c\u9f99\u3001\u864e\u3001\u72ee\u3001\u8c61\u3001\u8c79\u201d\u4e94\u6076 \u517d\uff0c\u6bcf\u4e2a\u4eba\u7684\u2014\u53cc\u624b\u4e0a\u90fd\u6cbe\u6ee1\u8840\u8165\u3002 \u53ef\u662f\u653e\u4e0b\u5c60\u5200\uff0c\u7acb\u5730\u6210\u4f5b\uff0c\u6076\u540d\u77a9\u5f70\u7684\u4e94\u6076\u517d\uff0c\u4ece\u6b64\u53d8\u6210\u4e86\u5c11\u6797\u5bfa\u7684\u4e94\u7f57\u6c49\uff0c\u65e0 \u9f99\u3001\u65e0\u864e\u3001\u65e0\u72ee\u3001\u65e0\u8c61\u3001\u65e0\u8c79\uff0c\u53ea\u6709\u2014\u7247\u4f5b\u5fc3\u3002 \u65e0\u9f99\u6709\u62a4\u6cd5\u957f\u8001\u7684\u8eab\u5206\uff0c\u5374\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u2014\u5915\u5ffd\u7136\u5927\u9189\uff0c\u7ffb\u5012\u70db\u53f0\uff0c\u51e0\u4e4e\u5c06\u5c11 \u6797\u7684\u4e2d\u5fc3\u91cd\u5730\u85cf\u7ecf\u9601\uff0c\u70e7\u6210\u4e00\u7247\u5e73\u5730\u3002 \u638c\u95e8\u65b9\u4e08\u9707\u6012\u4e4b\u4e0b\uff0c\u9664\u4e86\u7f5a\u4ed6\u9762\u58c1\u5341\u5e74\u4e45\uff0c\u8fd8\u8d23\u6253\u4e86\u4e8c\u5341\u6212\u68cd\uff0c\u65e0\u9f99\u53d7\u8fb1\uff0c\u542b\u6068 \u800c\u6b7b\uff0c\u624b\u8db3\u8fde\u5fc3\uff0c\u5269\u4e0b\u7684\u56db\u7f57\u6c49\u7684\u4f5b\u5fc3\u5168\u90fd\u5316\u4f5c\u6740\u673a\uff0c\u7adf\u4e0d\u60dc\u8e48\u72af\u5929\u6761\uff0c\u53bb\u523a\u6740\u638c \u95e8\u3002 \u6c5f\u6e56\u4e2d\u4eba\u53ea\u77e5\u9053\u4ed6\u4eec\u90a3\u2014\u6b21\u884c\u523a\u5e76\u672a\u5f97\u624b\uff0c\u5374\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u4eec\u7684\u751f\u6b7b\u4e0b\u843d\uff0c\u66f4\u6ca1 \u6709\u4eba\u77e5\u9053\u65e9\u5df2\u6d17\u5fc3\u9769\u9762\u7684\u5929\u9f99\u7f57\u6c49\uff0c\u600e\u4e48\u4f1a\u5ffd\u7136\u5927\u9189\u7684\uff1f \u8fd9\u4ef6\u4e8b\u4e5f\u5df2\u6210\u4e86\u6b66\u6797\u4e2d\u7684\u7591\u6848\u4e4b\u4e00\uff0c\u6b63\u5982\u8c01\u4e5f\u4e0d\u77e5\u9053\u77f3\u9e64\u600e\u4e48\u4f1a\u88ab\u9010\u51fa\u6b66\u5f53\u7684\u3002 \u53ef\u662f\u9646\u5c0f\u51e4\u73b0\u5728\u5374\u5df2\u77e5\u9053\uff0c\u65e0\u9f99\u7684\u5927\u9189\uff0c\u5fc5\u5b9a\u548c\u82e6\u74dc\u548c\u5c1a\u6709\u5173\u2014\u8981\u5403\u82e6\u74dc\u548c\u5c1a\u90a3 \u5929\u4e0b\u65e0\u53cc\u7684\u7d20\u5e2d\uff0c\u603b\u662f\u96be\u514d\u8981\u559d\u51e0\u676f\u7684\u3002 \u4ed6\u4eec\u521a\u624d\u518d\u4e09\u63a2\u95ee\u82e6\u74dc\u548c\u5c1a\u7684\u5b89\u597d\uff0c\u60f3\u5fc5\u5c31\u662f\u5e0c\u671b\u4ed6\u8fd8\u6d3b\u7740\uff0c\u4ed6\u4eec\u624d\u597d\u53bb\u4eb2\u624b\u590d \u4ec7\u3002 \u521a\u624d\u65e0\u8c79\u4e4d\u4e00\u51fa\u624b\uff0c\u5c31\u4ee4\u4eba\u9aa8\u6298\u547d\u6bd9\uff0c\u53ef\u89c1\u4ed6\u7684\u5fc3\u4e2d\u6028\u6bd2\u5df2\u79ef\u4e86\u591a\u6df1\u3002 \u4ed6\u4eec\u6700\u6068\u7684\u5374\u8fd8\u4e0d\u662f\u82e6\u74dc\uff0c\u800c\u662f\u5c11\u6797\uff0c\u5c31\u6b63\u5982\u77f3\u9e64\u6068\u6b66\u5f53\uff0c\u9ad8\u6d9b\u6068\u51e4\u5c3e\u5e2e\u4e00\u6837\u3002 \u5df4\u5c71\u77ff\u85cf\u6781\u4e30\uff0c\u800c\u4e14\u636e\u8bf4\u8fd8\u6709\u91d1\u7802\uff0c\u987e\u98de\u4e91\u5f53\u7136\u60f3\u5c06\u987e\u5bb6\u9053\u89c2\u7684\u4ea7\u4e1a\uff0c\u4ece\u4ed6\u7684\u5802 \u5f1f\u5c0f\u987e\u9053\u4eba\u624b\u4e2d\u593a\u56de\u6765\u3002 \u6d77\u5947\u9614\u5728\u6d77\u4e0a\u5df2\u80fd\u7acb\u8db3\uff0c\u5f53\u7136\u60f3\u4ece\u6c34\u4e0a\u98de\u624b\u65f6\u593a\u53d6\u957f\u6c5f\u6c34\u9762\u4e0a\u7684\u9738\u4e1a\u3002 \u675c\u94c1\u5fc3\u4e0e\u4e10\u5e2e\u4ec7\u6df1\u5982\u6d77\uff0c\u90a3\u7d2b\u9762\u957f\u9b03\u7684\u8001\u8005\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u6614\u5e74\u548c\u9ad8\u884c\u7a7a\u4e89\u593a\u96c1\u8361 \u95e8\u6237\u7684\u201c\u767e\u80dc\u5200\u738b\u201d\u5173\u5929\u6b66\u3002 \u8001\u5200\u628a\u5b50\u8fd9\u4e00\u6b21\u884c\u52a8\uff0c\u6b63\u597d\u5c06\u4ed6\u4eec\u7684\u51a4\u5bb6\u5bf9\u5934\u4e00\u7f51\u6253\u5c3d\uff0c\u4ed6\u4eec\u5f53\u7136\u4f1a\u5168\u529b\u4ee5\u8d74\u3002 \u53ef\u662f\u8fd9\u4e9b\u4eba\u5927\u90fd\u5df2\u662f\u2018\u6d3e\u5b97\u4e3b\u7684\u8eab\u5206\uff0c\u5e73\u65e5\u5f88\u96be\u76f8\u805a\uff0c\u4ed6\u4eec\u7684\u95e8\u6237\u6240\u5728\u5730\uff0c\u8ddd\u79bb \u53c8\u5f88\u8fdc\uff0c\u600e\u4e48\u80fd\u5728\u4e00\u6b21\u884c\u52a8\u4e2d\u5c31\u5c06\u4ed6\u4eec\u4e00\u7f51\u6253\u5c3d\u3002 \u8001\u5200\u628a\u5b50\u5df2\u7ecf\u5728\u89e3\u91ca\uff0c\u201c\u56db\u6708\u5341\u4e8c\u662f\u5df2\u6545\u53bb\u7684\u6b66\u5f53\u638c\u95e8\u6885\u771f\u4eba\u7684\u5fcc\u65e5\uff0c\u4e5f\u662f\u77f3\u96c1 \u63a5\u638c\u95e8\u6237\u7684\u5341\u5468\u5e74\u5e86\u5178\uff0c\u636e\u8bf4\u4ed6\u8fd8\u8981\u5728\u8fd9\u4e00\u5929\uff0c\u7acb\u4e0b\u7ee7\u627f\u6b66\u5f53\u9053\u7edf\u7684\u638c\u95e8\u5f1f\u5b50\u3002\u201d \u4ed6\u51b7\u7b11\u7740\uff0c\u63a5\u7740\u9053\uff1a\u201c\u5230\u4e86\u90a3\u4e00\u5929\uff0c\u6b66\u5f53\u5c71\u5f53\u7136\u662f\u51a0\u76d6\u4e91\u96c6\uff0c\u70ed\u95f9\u5f97\u5f88\uff0c\u94c1\u80a9\u548c \u738b\u5341\u888b\u90a3\u4e9b\u4eba\uff0c\u4e5f\u4e00\u5b9a\u90fd\u662f\u4f1a\u4e2d\u7684\u8d35\u5175\u3002 \u201c\u6211\u4eec\u662f\u4e0d\u662f\u5df2\u51b3\u5b9a\u5728\u54ea\u4e00\u5929\u52a8\u624b\uff1f\u201d\u8fd9\u53e5\u8bdd\u9646\u5c0f\u51e4\u672c\u6765\u4e5f\u60f3\u95ee\u7684\uff0c\u675c\u94c1\u5fc3\u5374\u62a2\u5148 \u95ee\u4e86\u51fa\u6765\u3002 \u8001\u5200\u628a\u5b50\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4eec\u4e00\u5b9a\u8981\u5728\u56db\u6708\u5341\u4e8c\u65e5\u4e4b\u524d\uff0c\u5c31\u8d76\u5230\u6b66\u5f53\u53bb\u3002\u201d \u53ef\u662f\u4ed6\u4eec\u8fd9\u4e9b\u4eba\u82e5\u662f\u540c\u65f6\u884c\u52a8\uff0c\u7528\u4e0d\u7740\u8d70\u51fa\u8fd9\u7247\u5c71\u533a\uff0c\u5c31\u4e00\u5b9a\u5df2\u8f70\u52a8\u6b66\u6797\u3002 \u8fd9\u6b21\u884c\u52a8\u7edd\u5bf9\u673a\u5bc6\uff0c\u7edd\u4e0d\u80fd\u6253\u8349\u60ca\u86c7\u3002 \u201c\u6240\u4ee5\u6211\u4eec\u4e0d\u4f46\u8981\u5206\u6279\u53bb\uff0c\u800c\u4e14\u6bcf\u4e2a\u4eba\u90fd\u8981\u7ecf\u8fc7\u6613\u5bb9\u6539\u626e\u3002\u201d \u8fd9\u4e9b\u4e8b\u8001\u5200\u628a\u5b50\u4e5f\u65e9\u5df2\u6709\u4e86\u6781\u5468\u5bc6\u7684\u8ba1\u5212\u3002 \u7ba1\u5bb6\u5a46\u9053\uff1a\u201c\u884c\u52a8\u7684\u7ec6\u8282\uff0c\u7531\u6211\u4e3a\u5404\u4f4d\u5b89\u6392\uff0c\u5b8c\u5168\u7528\u4e0d\u7740\u5404\u4f4d\u64cd\u5fc3oU\u8001\u5200\u628a\u5b50 \u9053\uff1a\u201c\u6211\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u8d1f\u8d23\u4e3a\u5404\u4f4d\u6613\u5bb9\u6539\u626e\u7684\uff0c\u7edd\u5bf9\u662f\u5929\u4e0b\u65e0\u53cc\u7684\u597d\u624b\uff0c\u867d\u4e0d\u80fd\u5c06\u5404\u4f4d \u8131\u80ce\u6362\u9aa8\uff0c\u6539\u9020\u6210\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u5374\u7edd\u5bf9\u53ef\u4ee5\u8ba9\u522b\u7684\u4eba\u770b\u4e0d\u51fa\u5404\u4f4d\u7684\u672c\u6765\u9762\u76ee\u3002\u201d \u73b0\u5728\u552f\u4e00\u7684\u95ee\u9898\u662f\uff1a\u201c\u6211\u4eec\u600e\u4e48\u6837\u5c06\u5175\u5203\u5e26\u4e0a\u5c71\u53bb\uff1f\u201d \u6ca1\u6709\u4eba\u80fd\u5e26\u5175\u5203\u4e0a\u6b66\u5f53\u5c71\uff0c\u6240\u6709\u7684\u6b66\u5668\u90fd\u8981\u7559\u5728\u89e3\u5251\u6c60\u65c1\u7684\u89e3\u5251\u5ca9\u4e0a\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f46\u662f\u6211\u4e5f\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u5728\u90a3\u5929\u665a\u4e0a\u51fa\u624b\u4e4b\u524d\uff0c\u6bcf\u4e2a\u4eba\u90fd\u53ef\u4ee5\u5230\u96ea\u9690 \u53bb\u627e\u5230\u4e00\u4ef6\u81ea\u5df1\u8d81\u624b\u7684\u5175\u5203\u3002\u201d \u5a04\u8001\u592a\u592a\u521a\u5543\u5b8c\u4e00\u6761\u9e21\u817f\uff0c\u5c31\u62a2\u7740\u95ee\uff0c\u201c\u96ea\u9690\u5728\u54ea\u91cc\uff1f\u201d \u8001\u5200\u628a\u5b50\u7b11\u9053\uff1a\u201c\u96ea\u9690\u5c31\u662f\u9690\u6240\uff0c\u4e5f\u5c31\u662f\u5395\u6240\u7684\u610f\u601d\u3002\u201d \u5a04\u8001\u592a\u592a\u53c8\u95ee\uff0c\u201c\u660e\u660e\u662f\u5395\u6240\uff0c\u4e3a\u4ec0\u4e48\u504f\u504f\u8981\u53eb\u96ea\u9690\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u8fd9\u662f\u65b9\u5916\u4eba\u7528\u7684\u540d\u8bcd\uff0c\u5b83\u7684\u6765\u5386\u6709\u4e24\u79cd\u8bf4\u6cd5oD\u201c\u96ea\u201d\u5c31\u662f\u96ea\u585e\u5c71 \u7684\u660e\u89c9\u63b8\u5e08\uff0c\u201c\u9690\u201d\u662f\u676d\u5dde\u7684\u7075\u9690\u5bfa\uff0c\u56e0\u4e3a\u96ea\u585e\u66fe\u7ecf\u5728\u7075\u9690\u5bfa\u53f8\u5395\u804c\uff0c\u6240\u4ee5\u5bfa\u5239\u5373\u4ee5 \u96ea\u9690\u79f0\u575du\u3002 \u56e0\u4e3a\u798f\u5dde\u7684\u795e\u50e7\u96ea\u5cf0\u4e49\u5b58\uff0c\u662f\u5728\u6253\u626b\u9690\u6240\u4e2d\u83b7\u5f97\u5927\u609f\u7684\uff0c\u6545\u6709\u6b64\u540d\u3002 \u5a04\u8001\u592a\u592a\u4e0d\u60f3\u518d\u95ee\uff0c\u7ba1\u5bb6\u5a46\u5df2\u9001\u4e86\u76d8\u70e7\u9e21\u8fc7\u53bb\uff0c\u8ba9\u5979\u7528\u9e21\u817f\u585e\u4f0e\u5979\u81ea\u5df1\u7684\u5634\u3002 \u8981\u600e\u4e48\u6837\u624d\u80fd\u585e\u4f4f\u4e8e\u8fd8\u90a3\u4e9b\u4eba\u7684\u5634\uff1f\u4ed6\u4eec\u77e5\u9053\u7684\u79d8\u5bc6\u5c82\u975e\u5df2\u592a\u591a\u4e86\uff01 \u8fd9\u4e9b\u4eba\u7684\u8138\u4e0a\u5df2\u5168\u65e0\u8840\u8272\uff0c\u56e0\u4e3a\u4ed6\u4eec\u81ea\u5df1\u4e5f\u77e5\u9053\u5904\u7406\u8fd9\u79cd\u4e8b\u901a\u5e38\u53ea\u6709\u4e00\u79cd\u6cd5\u5b50\uff01 \u53ea\u6709\u6b7b\u4eba\u624d\u4e0d\u4f1a\u6cc4\u9732\u79d8\u5bc6\u3002 \u8981\u60f3\u5728\u6b7b\u4e2d\u6c42\u6d3b\uff0c\u901a\u5e38\u4e5f\u53ea\u6709\u4e00\u79cd\u6cd5\u5b50\uff0c\u201c\u4f60\u8981\u6740\u6211\u706d\u53e3\uff0c\u6211\u5c31\u5148\u6740\u4e86\u4f60\uff01\u201d \u4e8e\u8fd8\u7a81\u7136\u8dc3\u8d77\uff0c\u5c31\u50cf\u662f\u6761\u8dc3\u51fa\u6c34\u9762\u7684\u98de\u9c7c\u3002 \u4ed6\u7684\u98de\u9c7c\u523a\u6709\u4e94\u5bf9\uff0c\u53f6\u7075\u53ea\u5077\u4e86\u56db\u5bf9\uff0c\u5269\u4e0b\u7684\u4e00\u5bf9\u5c31\u5728\u4ed6\u8863\u8896\u91cc\uff0c\u73b0\u5728\u5df2\u5316\u4f5c\u4e86 \u4e24\u9053\u95ea\u7535\uff0c\u76f4\u6253\u8001\u5200\u628a\u5b50\u3002 \u8001\u5200\u628a\u5b50\u6ca1\u6709\u52a8\uff0c\u4ed6\u8eab\u540e\u7684\u77f3\u9e64\u5374\u52a8\u4e86\uff0c\u4e03\u661f\u76ae\u9798\u4e2d\u7684\u957f\u5251\u5df2\u5316\u4f5c\u98de\u8679\u3002 \u98de\u8679\u8fce\u4e0a\u4e86\u95ea\u7535\uff0c\u201c\u53ee\u3001\u53ee\u201d\u4e24\u58f0\u54cd\uff0c\u95ea\u7535\u7a81\u7136\u65ad\u4e86\uff0c\u4e24\u622a\u94dc\u523a\u534a\u7a7a\u4e2d\u843d\u4e86\u4e0b \u6765\uff0c\u98de\u8679\u4e5f\u5df2\u4e0d\u89c1\uff0c\u5251\u5149\u5df2\u523a\u4eba\u4e8e\u8fd8\u7684\u80f8\u819b\u3002 \u4ed6\u770b\u770b\u624b\u91cc\u5269\u4e0b\u4e24\u622a\u98de\u9c7c\u523a\uff0c\u518d\u770b\u770b\u4ece\u524d\u80f8\u76f4\u523a\u800c\u4eba\u7684\u5251\u950b\uff0c\u7136\u540e\u624d\u62fe\u8d77\u5934\uff0c\u770b \u7740\u9762\u524d\u8fd9\u4e2a\u6ca1\u6709\u5251\u7684\u4eba\uff0c\u597d\u50cf\u8fd8\u80fd\u76f8\u4fe1\u8fd9\u662f\u771f\u7684\u3002 \u77f3\u9e64\u4e5f\u5728\u51b7\u51b7\u7684\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u5251\u6bd4\u65f6\u5b64\u57ce\u7684\u5929\u5916\u98de\u4ed9\u5982\u4f55\uff1f\u201d \u4e8e\u8fd8\u54ac\u7740\u7259\uff0c\u8fde\u4e00\u4e2a\u5b87\u90fd\u6ca1\u6709\u8bf4\uff0c\u626d\u66f2\u7684\u5634\u89d2\u5374\u9732\u51fa\u79cd\u8ba5\u5632\u7684\u7b11\u610f\uff0c\u4eff\u4f5b\u5728\u8bf4\uff1a \u201c\u53f6\u5b64\u57ce\u5df2\u6b7b\u4e86\uff0c\u4f60\u5c31\u7b97\u6bd4\u4ed6\u5f3a\u53c8\u5982\u4f55\uff1f\u201d \u77f3\u9e64\u61c2\u5f97\u4ed6\u7684\u610f\u601d\uff0c\u63e1\u5251\u7684\u624b\u7a81\u7136\u8f6c\u52a8\uff0c\u5251\u950b\u4e5f\u8ddf\u7740\u8f6c\u89c2\u3002 \u4e8e\u8fd8\u7684\u8138\u7acb\u523b\u626d\u66f2\uff0c\u5ffd\u7136\u5927\u543c\u2014\u58f0\uff0c\u6251\u4e86\u4e0a\u6765\uff0c\u4e00\u80a1\u9c9c\u8840\u55b7\u51fa\uff0c\u5251\u950b\u5df2\u7a7f\u80f8\u800c \u8fc7\u3002 \u9646\u5c0f\u51e4\u4e0d\u5fcd\u518d\u770b\uff0c\u5df2\u7ecf\u7ad9\u8d77\u6765\u7684\uff0c\u8fd8\u6709\u51e0\u4e2a\u6ca1\u6709\u5012\u4e0b\uff0c\u4ed6\u4e0d\u80fd\u770b\u7740\u4ed6\u4eec\u4e00\u4e2a\u4e2a\u6b7b \u5728\u773c\u524d\u3002 \u4ed6\u6084\u6084\u7684\u7ad9\u8d77\u6765\uff0c\u6084\u6084\u7684\u8d70\u4e86\u51fa\u53bb\u3002 \u96fe\u53c8\u6e7f\u53c8\u51b7\uff0c\u4ed6\u6df1\u6df1\u7684\u5438\u5165\u4e86\u4e00\u53e3\uff0c\u5c06\u51b7\u96fe\u7559\u5728\u80f8\u819b\u91cc\u4ed6\u5fc5\u987b\u51b7\u9759\u3002 \u201c\u4f60\u4e0d\u559c\u6b22\u6740\u4eba\uff1f\u201d \u8fd9\u662f\u8001\u5200\u628a\u5b50\u7684\u58f0\u97f3\uff0c\u8001\u5200\u628a\u5b50\u4e5f\u8ddf\u7740\u4ed6\u8d70\u4e86\u51fa\u6765\uff0c\u4e5f\u5728\u547c\u5438\u7740\u8fd9\u51b7\u800c\u6f6e\u6e7f\u7684\u96fe \u6c14\u3002 \u9646\u5c0f\u51e4\u6de1\u6de1\u9053\uff1a\u201c\u6211\u559c\u6b22\u559d\u9152\uff0c\u53ef\u662f\u770b\u522b\u4eba\u559d\u9152\u5c31\u662f\u53e6\u5916\u4e00\u56de\u4e8b\u4e86\u3002\u201d \u4ed6\u6ca1\u6709\u56de\u5934\u53bb\u770b\u8001\u5200\u628a\u5b50\uff0c\u4f46\u662f\u4ed6\u542c\u5f97\u8001\u5200\u628a\u5b50\u58f0\u97f3\u91cc\u5e26\u7740\u7b11\u610f\uff0c\u663e\u7136\u5bf9\u4ed6\u7684\u56de \u7b54\u89c9\u5f97\u5f88\u6ee1\u610f\u3002 \u8001\u5200\u628a\u5b50\u6b63\u5728\u8bf4\uff0c\u201c\u6211\u4e5f\u4e0d\u559c\u6b22\u770b\uff0c\u65e0\u8bba\u4ec0\u4e48\u4e8b\uff0c\u81ea\u5df1\u52a8\u624b\u53bb\u505a\u603b\u6bd4\u8f83\u6709\u8da3 \u4e9b\u3002\u201d \u9646\u5c0f\u51e4\u6c89\u9ed8\u7740\uff0c\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\u4f60\u5374\u597d\u50cf\u5e76\u4e0d\u559c\u6b22\u81ea\u5df1\u52a8\u624b\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u54e6\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u77e5\u9053\u53f6\u7075\u5077\u4e86\u4e8e\u8fd8\u7684\u6c34\u9760\u548c\u98de\u9c7c\u523a\uff0c\u4f60\u4e5f\u77e5\u9053\u5979\u53bb\u5e72\u4ec0\u4e48\uff0c\u4f46\u4f60 \u5374\u6ca1\u6709\u963b\u6b62aU\u8001\u5200\u628a\u5b50\u627f\u8ba4\uff0c\u201c\u6211\u6ca1\u6709\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u4e0d\u8ba9\u6211\u53bb\u6551\u53f6\u96ea\uff0c\u4f60\u81ea\u5df1\u4e5f\u4e0d\u53bb\uff0c\u4e3a\u4ec0\u4e48\u8ba9\u5979\u53bb\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u53f6\u51cc\u98ce\u7edd\u4e0d\u4f1a\u4f24\u5bb3\u5979\u7684\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u80fd\u786e\u5b9a\u3002\u201d \u8001\u5200\u628a\u5b50\u70b9\u70b9\u5934\uff0c\u58f0\u97f3\u5ffd\u7136\u53d8\u5f97\u5636\u54d1\uff1a\u201c\u56e0\u4e3a\u5979\u624d\u662f\u53f6\u51cc\u98ce\u4eb2\u751f\u7684\u5973\u513f\uff1f\u201d \u9646\u5c0f\u51e4\u53c8\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u597d\u50cf\u5b8c\u5168\u6ca1\u6709\u6ce8\u610f\u5230\u4ed6\u58f0\u97f3\u91cc\u9732\u51fa\u75db\u82e6\u548c\u4ec7\u6068\uff0c\u201c\u8fd8\u6709 \u2014\u4ef6\u4e8b\uff0c\u4f60\u597d\u50cf\u4e5f\u4e0d\u51c6\u5907\u81ea\u5df1\u52a8\u624b\u3002 \u8001\u5200\u628a\u5b50\u5728\u7b49\u7740\u4ed6\u8bf4\u535c\u53bb\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u8981\u77f3\u9e64\u53bb\u5bf9\u4ed8\u6b66\u5f53\u77f3\u96c1\uff0c\u864e\u8c79\u5144\u5f1f\u4eec\u5bf9\u4ed8\u5c11\u6797\u94c1\u80a9\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u90a3\u662f\u4ed6\u4eec\u81ea\u5df1\u7684\u4ec7\u6068\uff0c\u4ed6\u4eec\u672c\u5c31\u8981\u81ea\u5df1\u53bb\u89e3\u51b3\u3002\u201d \u9646\uff0c\u201c\u535c\u98ce\u9053\uff1a\u201c\u675c\u94c1\u5fc3\u80fd\u5bf9\u4ed8\u738b\u5bf8\u2018\u888b\uff1f\u201d \u8001\u5203\u628a\u5b50\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4ed6\u6b66\u529f\u5df2\u6709\u7cbe\u8fdb\uff0c\u4f55\u51b5\u8fd8\u6709\u5a04\u8001\u592a\u592a\u505a\u4ed6\u7684\u540e\u624b\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5c0f\u987e\u9053\u4eba\u5e94\u8be5\u4e0d\u662f\u8868\u54e5\u7684\u5bf9\u624b\uff0c\u6c34\u4e0a\u98de\u5bf9\u6d77\u5947\u9614\u4f60\u4e70\u8c01\u8d62\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u957f\u6c5f\u662f\u4e2a\u80a5\u5730\u76d8\uff0c\u6c34\u4e0a\u98de\u5df2\u80a5\u5f97\u5feb\u98de\u4e0d\u52a8\u4e86\uff0c\u65e0\u8bba\u662f\u5728\u9646\u4e0a\u8fd8\u662f \u5728\u6c34\u91cc\uff0c\u6211\u90fd\u53ef\u4ee5\u7528\u5341\u5bf9\u4e00\u7684\u76d8\u53e3\uff0c\u8d4c\u6d77\u5947\u9614\u8d62\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ef\u662f\u5173\u5929\u6b66\u7eab\u5df2\u8d25\u5728\u9ad8\u884c\u7a7a\u624b\u4e0b\u4e09\u6b21\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u90a3\u4e8c\u6b21\u90fd\u6709\u4eba\u5728\u6697\u4e2d\u52a9\u4e86\u9ad8\u884c\u7a7a\u4e00\u81c2\u4e4b\u529b\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u662f\u4ec0\u4e48 \u4eba\uff1f\u201d \u8001\u5200\u628a\u5b50\u51b7\u7b11\u9053\uff1a\u201c\uff0c\u9ad8\u884c\u7a7a\u7eb5\u6a2a\u957f\u6c5f\uff0c\u6b66\u5f53\u638c\u95e8\u7684\u5fcc\u65e5\uff0c\u5e72\u4ed6\u4ec0\u4e48\u4e8b\uff1f\u4ed6\u4e3a\u4ec0\u4e48 \u8981\u5df4\u5df4\u7684\u8d76\u53bb\uff1f\u201d \u96be\u9053\u662f\u6b66\u5f53\u5f1f\u5b50\u5728\u6697\u4e2d\u51fa\u624b\u7684\uff1f \u96c1\u8361\u7684\u95e8\u6237\u4e4b\u4e89\uff0c\u6b66\u5f53\u5f1f\u5b50\u4e3a\u4ec0\u4e48\u8981\u53bb\u591a\u7ba1\u95f2\u4e8b\uff1f \u9646\u5c0f\u51e4\u5e76\u4e0d\u60f3\u95ee\u5f97\u592a\u591a\uff0c\u53c8\u9053\uff1a\u201c\u90a3\u4e48\u73b0\u5728\u5269\u4e0b\u7684\u5c31\u53ea\u6709\u9e70\u773c\u8001\u4e03\u4e86\uff0c\u5c31\u7b97\u7ba1\u5bb6 \u5a46\u7ba1\u4e0d\u4f4f\u4ed6\uff0c\u518d\u52a0\u4e0a\u4e00\u4e2a\u82b1\u9b41\u5c31\u8db3\u8db3\u6709\u4f59ao\u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u82b1\u9b41\u8fd8\u6709\u522b\u7684\u4efb\u52a1\uff0c\u9ad8\u6d9b \u4e5f\u7528\u4e0d\u7740\u5e2e\u624b\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6240\u4ee5\u4e3b\u8981\u7684\u4e03\u4e2a\u4eba\u90fd\u5df2\u6709\u4eba\u5bf9\u4ed8\uff0c\u800c\u4e14\u90fd\u5df2\u5341\u62ff\u4e5d\u7a33\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u535c\u62ff\u4e5d\u7a33\u3002\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u90a3\u672b\u4f60\u51c6\u5907\u8981\u6211\u5e72\u4ec0\u4e48\uff1f\u53bb\u5bf9\u4ed8\u90a3\u4e9b\u626b\u5730\u6d17\u8155\u9632\u707e\u5de5\u9053 \u4eba\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u6211\u8981\u4f60\u505a\u7684\u4e8b\uff0c\u624d\u662f\u8fd9\u6b21\u884c\u52a8\u7684\u6210\u8d25\u5173\u952e\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u8001\u5200\u628a\u5b50\u4e5f\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u73b0\u5728\u4f60\u77e5\u9053\u5f97\u5df2\u591f\u591a\u4e86\uff0c\u522b\u7684\u4e8b\u5230\u4e86\u56db\u6708\u5341\u4e8c\u7684\u665a \u4e0a\uff0c\u6211\u518d\u544a\u8bc9\u4f60\u3002\u201d \u4ed6\u62cd\u4e86\u62cd\u9646\u5c0f\u51e4\u7684\u80a9\u201c\u6240\u4ee5\u4eca\u5929\u665a\u4e0a\u4f60\u4e0d\u59a8\u8f7b\u677e\u8f7b\u677e\uff0c\u751a\u81f3\u53ef\u4ee5\u5927\u9189\u4e00\u573a\uff0c\u56e0\u4e3a \u4f60\u660e\u5929\u53ef\u4ee5\u6574\u6574\u7761\u4e0a\u4e00\u5929\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u8981\u7b49\u5230\u540e\u5929\u624d\u4e0b\u5c71\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u662f\u6700\u540e\u4e00\u6279\u4e0b\u5c71\u7684\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u90a3\u6279\u4eba\u91cc\u9762\u8fd8\u6709\u8c01\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u7ba1\u5bb6\u5a46\uff0c\u5a04\u8001\u592a\u592a\uff0c\u8868\u54e5\uff0c\u94a9\u5b50\uff0c\u548c\u67f3\u9752\u9752\u3002 \u4ed6\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u597d\u620f\u603b\u662f\u8981\u7b49\u5230\u6700\u540e\u624d\u767b\u573a\u7684\uff0c\u4f60\u4eec\u5f53\u7136\u8981\u7559\u5728\u6700\u540e\u3002\u201d \u9646\u5c0f\u51e4\u6de1\u6de1\u9053\uff1a\u201c\u4f55\u51b5\u6709\u4ed6\u4eec\u8ddf\u7740\u6211\uff0c\u6211\u81f3\u5c11\u4e0d\u4f1a\u534a\u9014\u6b7b\u5728\u522b\u4eba\u624b\u91cc\u3002\u201d \u8001\u5200\u628a\u5b50\u7684\u7b11\u58f0\u66f4\u6109\u5feb\uff0c\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u5c31\u7b97\u4f60\u5728\u8def\u4e0a\u9047\u89c1\u4e86\u897f\u95e8\u5439\u96ea\uff0c\u4ed6\u4e5f\u7edd \u5bf9\u8ba4\u4e0d\u51fa\u4f60aU\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u8981\u4e3a\u6211\u6613\u5bb9\u6539\u626e\u7684\u90a3\u4e2a\u4eba\uff0c\u662f\u5929\u4e0b\u65e0\u53cc\u7684\u5999\u624b\u3002\u201d \u8001\u5200\u628a\u5b50\u7b11\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u82e5\u80fd\u5c06\u81ea\u5df1\u626e\u6210\u4e00\u6761\u72d7\uff0c\u4f60\u5bf9\u4ed6\u8fd8\u6709\u4ec0\u4e48\u4e0d\u653e\u5fc3\u7684\uff1f\u201d \u4ed6\u8bf4\u7684\u662f\u72ac\u90ce\u541b\u3002 \u72ac\u90ce\u541b\u7684\u4efb\u52a1\u5c31\u662f\u5c06\u6bcf\u4e2a\u4eba\u7684\u5bb9\u8c8c\u90fd\u6539\u53d8\u5f97\u8ba9\u522b\u4eba\u8ba4\u4e0d\u51fa\u6765\u3002 \u4efb\u52a1\u5b8c\u6210\u4e86\u4e4b\u540e\u5462\uff1f \u6211\u53ea\u4e0d\u8fc7\u8981\u4f60\u8d70\u7684\u65f6\u5019\u5e26\u6211\u8d70\u3002 \u9646\u5c0f\u51e4\u7ec8\u4e8e\u660e\u767d\u4e86\u4ed6\u7684\u610f\u601d\uff0c\u4ed6\u5f53\u7136\u4e5f\u5df2\u770b\u51fa\u81ea\u5df1\u7684\u5371\u673a\u3002 \u8001\u5200\u628a\u5b50\u4ef0\u9762\u5411\u5929\uff0c\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u8015\u8018\u7684\u65f6\u5019\u5df2\u8fc7\u53bb\uff0c\u73b0\u5728\u53ea\u7b49\u7740\u6536\u83b7\uff0c\u4ed6\u4eff \u4f5b\u5df2\u80fd\u770b\u89c1\u679c\u5b9e\u4ece\u679d\u5934\u957f\u51fa\u6765\u3002 \u4e00\u68f5\u68f5\u679c\u5b9e\uff0c\u5c31\u662f\u2014\u68f5\u68f5\u5934\u9885\u3002 \u9646\u5c0f\u51e4\u5ffd\u7136\u8f6c\u8138\u770b\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u4f60\u5462\uff1f\u6240\u6709\u7684\u4e8b\u90fd\u6709\u4eba\u505a\u4e86\uff0c\u4f60\u81ea\u5df1\u51c6\u5907\u505a\u4ec0 \u4e48\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u6211\u662f\u503a\u4e3b\uff0c\u6211\u6b63\u51c6\u5907\u7b49\u7740\u4f60\u4eec\u53bb\u66ff\u6211\u628a\u5e10\u6536\u56de\u6765qH\u9646\u5c0f\u51e4\u9053\uff1a \u201c\u6b66\u5f53\u6b20\u4e86\u77f3\u9e64\u4e00\u7b14\u5e10\uff0c\u5c11\u6797\u6b20\u4e86\u864e\u8c79\u5144\u5f1f\uff0c\u8c01\u6b20\u4f60\u7684\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u6bcf\u4e2a\u4eba\u90fd\u6b20\u6211\u7684\u3002\u201d \u4e0d\u4f1a\u6389\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4eba\u76ae\u9762\u5177\uff1f\u4f60\u771f\u7684\u7528\u4eba\u76ae\u505a\u9762\u5177\uff1f\u201d \u72ac\u90ce\u541b\u9053\uff1a\u201c\u4e00\u5b9a\u8981\u7528\u4eba\u76ae\u505a\u7684\u9762\u5177\u7c98\u5728\u8138\u4e0a\uff0c\u624d\u80fd\u5b8c\u5168\u6539\u53d8\u2014\u4e2a\u4eba\u8138\u4e0a\u7684\u8f6e \u5ed3\uff0c\u800c\u4e14\u6bcf\u4e00\u5f20\u4eba\u76ae\u9762\u5177\u90fd\u8981\u5148\u4f9d\u7167\u90a3\u4e2a\u4eba\u7684\u8138\u6253\u597d\u6837\u5b50\u3002\u201d \u4ed6\u5ffd\u7136\u5bf9\u9646\u5c0f\u51e4\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e5f\u5df2\u7167\u4f60\u7684\u8138\u5f62\u505a\u597d\u20146\u957f\u3002\u201d \u9646\u5c0f\u51e4\u82e6\u7740\u8138\u9053\uff1a\u201c\u4e5f\u662f\u4eba\u76ae\u7684\uff1f\u201d \u72ac\u90ce\u541b\u9053\uff1a\u201c\u8d27\u771f\u4ef7\u5b9e\u7684\u4eba\u76aeao\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u4e00\u5171\u505a\u5382\u591a\u5c11\u5f20\uff1f\u201d \u72ac\u90ce\u541b\u9053\uff1a\u201c\u4e8c\u5341\u4e00\u5f20\u3002\u201d \u4ed6\u53c8\u8865\u5145\u7740\u9053\uff1a\u201c\u9664\u4e86\u8001\u5200\u628a\u5b50\u5916\uff0c\u6bcf\u4e2a\u4eba\u90fd\u6709\u4e00\u5f20\u3002\u201d \u8001\u5200\u628a\u5b50\u4e3a\u4ec0\u4e48\u4e0d\u5fc5\u6613\u5bb9\u6539\u626e\uff1f\u96be\u9053\u4ed6\u5230\u4e86\u6b66\u5f53\u8fd8\u80fd\u6234\u7740\u90a3\u7bd3\u5b50\u822c\u7684\u7af9\u7b7e\uff1f \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u7ecf\u8fc7\u6613\u5bb9\u540e\uff0c\u8138ll\u662f\u4e0d\u662f\u8fd8\u7559\u7740\u4e00\u70b9\u7279\u6b8a\u7684\u6807\u5fd7\uff1f\u201d \u72ac\u90ce\u541b\u9053\uff1a\u201c\u4e00\u70b9\u90fd\u6ca1\u6709\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5982\u679c\u5927\u5bb6\u5f7c\u6b64\u90fd\u4e0d\u8ba4\u5f97\uff0c\u5c82\u975e\u96be\u514d\u4f1a\u6740\u9519\u4eba\u201dp\u2019\u72ac\u90ce\u541b\u9053\uff1a\u201c\u7edd\u4e0d \u4f1a\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u72ac\u90ce\u541b\u9053\uff1a\u201c\u56e0\u4e3a\u6bcf\u4e00\u6279\u4e0b\u5c71\u7684\u4eba\u4efb\u52a1\u90fd\u4e0d\u540c\uff0c\u6709\u7684\u4e13\u5bf9\u4ed8\u6b66\u5f53\u9053\u58eb\uff0c\u6709\u7684\u4e13\u5bf9 \u4ed8\u5c11\u6797\u548c\u5c1a\uff0c\u53ea\u8981\u8fd9\u7ec4\u4eba\u80fd\u8bb0\u4f4f\u5f7c\u6b64\u95f4\u6613\u5bb9\u540e\u7684\u6837\u5b50\uff0c\u5c31\u4e0d\u4f1a\u6740\u5230\u81ea\u5df1\u4eba\u8eab\u4e0a\u6765\u4e86aU \u9646\u5c0f\u51e4\u6c89\u541f\u7740\uff0c\u5ffd\u7136\u538b\u4f4e\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u5728\u6bcf\u6279\u4eba\u8138\u4e0a\u90fd\u7559\u4e00\u70b9\u7279\u522b\u7684\u8bb0\u53f7\u4e2d \u8b6c\u5982\u8bf4\uff0c\u4e00\u70b9\u9ebb\u5b50\uff0c\u6216\u8005\u662f\u2014\u9897\u81f3\u3002 \u72ac\u90ce\u541b\u770b\u7740\u4ed6\uff0c\u773c\u775b\u91cc\u5e26\u7740\u79cd\u5947\u602a\u7684\u8868\u60c5\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u6084\u6084\u7684\u95ee\uff0c\u201c\u4f60\u6709\u628a\u63e1\u80fd \u5e26\u6211\u4e00\u8d77\u8d70\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u6709\u628a\u63e1\u3002\u201d \u72ac\u90ce\u541b\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u7b54\u5e94\u4e86\u6211\uff0c\u6211\u5f53\u7136\u4e5f\u7b54\u5e94\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u51c6\u5907\u600e\u4e48\u505a\uff1f\u201d \u72ac\u90ce\u541b\u7728\u4e86\u7728\u773c\uff0c\u9053\uff1a\u201c\u73b0\u5728\u6211\u8fd8\u6ca1\u6709\u60f3\u51fa\u6765\uff0c\u7b49\u6211\u4eec\u4e00\u8d77\u8d70\u7684\u65f6\u5019\uff0c\u6211\u518d\u544a\u8bc9 \u4f60\u3002\u201d \u8fd9\u91cc\u6bcf\u4e2a\u4eba\u597d\u50cf\u90fd\u8ddf\u8001\u5200\u628a\u5b50\u4e00\u6837\uff0c\u9664\u4e86\u81ea\u5df1\u5916\uff0c\u7edd\u4e0d\u4fe1\u4efb\u4f55\u4eba\u3002 \u6709\u65f6\u4ed6\u4eec\u751a\u81f3\u8fde\u81ea\u5df1\u90fd\u4e0d\u4fe1\u4efb\u3002 \u72ac\u90ce\u541b\u5ffd\u53c8\u95ee\u9053\uff1a\u201c\u82b1\u5be1\u5987\u662f\u4e0d\u662f\u8ddf\u4f60\u4e00\u6279\u8d70\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5927\u6982\u662f\u7684ao\u72ac\u90ce\u541b\u9053\uff1a\u201c\u4f60\u60f3\u8ba9\u5979\u53d8\u6210\u4ec0\u4e48\u6837\u5b50\uff1f\u662f\u53c8\u8001\u53c8\u4e11\uff1f\u8fd8\u662f \u5e74\u9752\u6f02\u4eae\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8d8a\u8001\u8d8a\u597d\uff0c\u8d8a\u4e11\u8d8a\u597d\u3002\u201d \u72ac\u90ce\u541b\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u6ca1\u6709\u4eba\u76f8\u4fe1\u9646\u5c0f\u51e4\u4f1a\u8ddf\u4e00\u4e2a\u53c8\u8001\u53c8\u4e11\u7684\u5973\u4eba\u5728\u2014\u8d77\u7684\uff0c\u6240\u4ee5\u4e5f \u6ca1\u6709\u4eba\u4f1a\u76f8\u4fe1\u6211\u5c31\u662f\u9646\u5c0f\u51e4ao\u72ac\u90ce\u541b\u9053\uff1a\u201c\u6240\u4ee5\u5979\u8d8a\u8001\u8d8a\u4e11\uff0c\u4f60\u5c31\u8d8a\u5b89\u5168\uff0c\u4e0d\u4f46\u522b\u4eba \u8ba4\u4e0d\u51fa\u4f60\uff0c\u4f60\u81ea\u5df1\u4e5f\u53ef\u4ee5\u4e0d\u52a8\u5fc3\u3002\u201d \u4ed6\u7728\u7740\u773c\u7b11\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u4f60\u7684\u786e\u8981\u4fdd\u6301\u4f53\u529b\uff0c\u82e5\u662f\u8ddf\u2014\u4e2a\u5e74\u9752\u6f02\u4eae\u7684\u5be1\u5987\u5728\u4e00 \u8d77\uff0c\u8981\u4fdd\u6301\u4f53\u529b\u5c31\u5f88\u4e0d\u5bb9\u6613\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u770b\u7740\u4ed6\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u7684\u6bdb\u75c5\u662f\u4ec0\u4e48\uff1f\u201d \u72ac\u90ce\u541b\u6447\u6447\u5934\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u7684\u6bdb\u75c5\u5c31\u662f\u592a\u591a\u5634\u3002\u201d \u72ac\u90ce\u541b\u8d54\u7b11\u9053\uff1a\u201c\u53ea\u8981\u4f60\u5e26\u6211\u8d70\uff0c\u8fd9\u4e00\u8def\u4e0a\u6211\u4fdd\u8bc1\u8fde\u4e00\u4e2a\u5b57\u90fd\u4e0d\u8bf4ao\u9646\u5c0f\u51e4\u9053\uff1a \u201c\u5c31\u7b97\u4f60\u60f3\u8bf4\uff0c\u6211\u4e5f\u6709\u6cd5\u5b50\u8ba9\u4f60\u8bf4\u4e0d\u51fa\u6765\u3002\u201d \u72ac\u90ce\u541b\u5fcd\u4e0d\u4f4f\u95ee\uff0c\u201c\u4f60\u6709\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u662f\u4e2a\u544a\u8001\u5f52\u7530\u7684\u4eac\u5b98\uff0c\u4e0d\u4f46\u5e26\u7740\u597d\u51e0\u4e2a\u8ddf\u73ed\u968f\u4ece\uff0c\u8fd8\u5e26\u7740\u4e00\u957f\u72d7 qD\u4ed6\u5fae\u7b11\u7740\uff0c\u53c8\u9053\uff1a\u201c\u4f60\u5c31\u662f\u90a3\u6761\u72d7\uff0c\u72d7\u5634\u91cc\u5f53\u7136\u662f\u8bf4\u4e0d\u51fa\u4eba\u8bdd\u6765\u7684oo\u72ac\u90ce\u541b\u77aa\u7740\u4ed6 \u770b\u4e86\u534a\u5929\uff0c\u7ec8\u4e8e\u82e6\u7b11\uff0c\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u5c31\u662f\u90a3\u6761\u72d7\uff0c\u53ea\u6c42\u4f60\u5343\u4e07\u4e0d\u8981\u5fd8\u8bb0\uff0c\u6211\u8fd9\u6761\u72d7 \u53ea\u80fd\u5403\u8089\uff0c\u4e0d\u5543\u9aa8\u5934\uff0c\uff0c\u9646\u5c0f\u51e4\u7b11\u9053\uff1a\u201c\u53ef\u662f\u4f60\u6700\u597d\u4e5f\u4e0d\u8981\u5fd8\u8bb0\uff0c\u4e0d\u542c\u8bdd\u7684\u72d7\u975e\u4f46\u8981 \u5543\u9aa8\u5934\uff0c\u6709\u65f6\u8fd8\u8981\u5403\u5c4e\u3002\u201d \u4ed6\u5927\u7b11\u7740\u8d70\u51fa\u53bb\uff0c\u5ffd\u53c8\u56de\u5934\u9053\uff1a\u201c\u53f6\u96ea\u548c\u53f6\u7075\u672c\u6765\u5e94\u8be5\u662f\u5728\u7b2c\u51e0\u6279\u8d70\u7684\uff1f\u201d \u72ac\u90ce\u541b\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u8001\u5200\u628a\u5b50\u7ed9\u6211\u7684\u540d\u5355\u4e0a\uff0c\u672c\u6765\u6ca1\u6709\u5979\u4eec\u59d0\u59b9\u7684\u540d \u5b57\u3002\u201d \u591c\u66f4\u6df1\u3002 \u9646\u5c0f\u51e4\u5728\u51b7\u96fe\u4e2d\u5750\u4e0b\u6765\uff0c\u5fc3\u91cc\u5728\u4ea4\u6218\u4e00\u73b0\u5728\u662f\u5230\u6cbc\u6cfd\u4e2d\u53bb\u627e\u5979\u4eec\u59d0\u59b9\uff1f\u8fd8\u662f\u53bb\u5927\u9189 \u4e00\u573a\uff1f \u4ed6\u7684\u9009\u62e9\u662f\u5927\u9189\u4e00\u573a\u3002 \u5c31\u7b97\u4e0d\u53bb\u627e\u5979\u4eec\uff0c\u4e5f\u4e0d\u662f\u4e00\u5b9a\u8981\u9189\u7684\uff0c\u4f46\u4ed6\u9189\u4e86\uff0c\u70c2\u9189\u5982\u6ce56\u4ed6\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981 \u9189\uff1f \u96be\u9053\u4ed6\u5fc3\u91cc\u6709\u4ec0\u4e48\u4e0d\u53ef\u544a\u4eba\u7684\u82e6\u8877\uff1f \u56db\u6708\u521d\u4e09\uff0c\u4e0b\u5348\uff0c\u6674\u591a\u96fe\u3002 \u9646\u5c0f\u51e4\u9192\u6765\u65f6\uff0c\u53ea\u89c9\u5f97\u5934\u75bc\u5982\u88c2\uff0c\u6ee1\u5634\u53d1\u82e6\uff0c\u800c\u4e14\u60c5\u7eea\u5341\u5206\u4f4e\u843d\uff0c\u5c31\u597d\u50cf\u5927\u75c5\u4e86 \u4e00\u573a\u3002 \u4ed6\u9192\u4e86\u5f88\u4e45\u624d\u7741\u5f00\u773c\uff0c\u4e00\u7741\u5f00\u773c\u5c31\u51e0\u4e4e\u8df3\u4e86\u8d77\u6765\u3002 \u5a04\u8001\u592a\u592a\u600e\u4e48\u4f1a\u5750\u5230\u4ed6\u5e8a\u5934\u6765\u7684\uff1f\u800c\u4e14\u8fd8\u4e00\u76f4\u5728\u76ef\u7740\u4ed6\uff1f \u4ed6\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u624d\u770b\u51fa\u8fd9\u4e2a\u6b63\u5750\u5728\u4ed6\u5e8a\u5934\u5543\u8695\u8c46\u7684\u8001\u592a\u5a46\u5e76\u4e0d\u662f\u5a04\u8001\u592a\u592a\uff0c\u53ef\u662f \u4ed6\u7edd\u4e0d\u4f1a\u6bd4\u5a04\u8001\u592a\u592a\u5e74\u9752\u591a\u5c11\u3002 \u201c\u4f60\u662f\u8c01\uff1f\u201d \u4ed6\u5fcd\u4e0d\u4f4f\u8981\u95ee\uff0c\u8fd9\u8001\u592a\u592a\u7684\u56de\u7b54\u53c8\u8ba9\u4ed6\u5403\u4e86\u4e00\u60ca\u3002 \u201c\u6211\u662f\u4f60\u8001\u5a46\u3002\u201d\u8001\u592a\u592a\u521a\u5f00\u5e72\u762a\u4e86\u7684\u5634\u51b7\u7b11\uff0c\u201c\u6211\u5ac1\u7ed9\u4f60\u5df2\u6574\u6574\u4e94\u5341\u5e74\u4e86\uff0c\u73b0 \u5728\u4f60\u60f3\u4e0d\u8ba4\u6211\u505a\u8001\u5a46\u4e5f\u4e0d\u884c\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u5403\u60ca\u7684\u770b\u7740\u5979\uff0c\u5ffd\u7136\u5927\u7b11\uff0c\u7b11\u5f97\u5728\u5e8a\u4e0a\u76f4\u6253\u6eda\u3002 \u8fd9\u8001\u592a\u5927\u7adf\u662f\u67f3\u9752\u9752\uff0c\u4ed6\u8fd8\u542c\u5f97\u51fa\u5979\u7684\u58f0\u97f3\u3002 \u201c\u4f60\u600e\u4e48\u4f1a\u53d8\u6210\u8fd9\u6837\u5b50\u7684\uff1f\u201d \u201c\u56e0\u4e3a\u90a3\u4e2a\u738b\u516b\u86cb\u6d3b\u89c1\u4e86\u9b3c\uff0c\u6211\u60f3\u8981\u5e74\u9752\u4e00\u70b9\uff0c\u4ed6\u90fd\u4e0d\u7b54\u5e94\u3002 \u67f3\u9752\u9752\u7528\u529b\u54ac\u7740\u8695\u8c46\uff0c\u6068\u6068\u9053\uff1a\u201c\u73b0\u5728\u6211\u53d8\u6210\u4e86\u8fd9\u4e2a\u6837\u4e86\uff0c\u4f60\u662f\u4e0d\u662f\u5f88\u9ad8\u5174\uff1f\u201d \u9646\u5c0f\u51e4\u6545\u610f\u7728\u4e86\u7728\u773c\u775b\uff0c\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u9ad8\u5174\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u672c\u6765\u5c31\u5e0c\u671b\u6211\u8d8a\u8001\u8d8a\u597d\uff0c\u8d8a\u4e11\u8d8a\u597d\uff0c\u56e0\u4e3a\u4f60\u672c\u6765\u5c31\u4e00\u76f4\u5728\u9003 \u907f\u6211\uff0c\u597d\u50cf\u751f\u6015\u6211\u6d3b\u6d3b\u7684\u628a\u4f60\u541e\u4e86\u4e0b\u9646\u5c0f\u51e4\u8fd8\u662f\u88c5\u4e0d\u61c2\uff0c\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u9003\u907f\u4f60\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u4f60\u82e5\u4e0d\u662f\u5728\u9003\u907f\u6211\uff0c\u4e3a\u4ec0\u4e48\u6bcf\u5929\u90fd\u559d\u5f97\u50cf\u6b7b\u4eba\u2014\u6837\uff1f\u201d \u5979\u51b7\u7b11\u7740\uff0c\u53c8\u9053\uff1a\u201c\u5176\u5b9e\u6211\u4e5f\u77e5\u9053\u4f60\u4e0d\u6562\u78b0\u6211\uff0c\u53ef\u662f\u6211\u53c8\u6709\u70b9\u5947\u602a\uff0c\u8981\u4f60\u6bcf\u5929\u665a \u4e0a\u8ddf\u6211\u8fd9\u4e48\u6837\u4e00\u4e2a\u8001\u592a\u5a46\u7761\u89c9\uff0c\u4f60\u600e\u4e48\u53d7\u5f97\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u5750\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u6bcf\u5929\u665a\u4e0a\u8ddf\u4f60\u7761\u89c9\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u662f\u4e2a\u544a\u8001\u5f52\u7530\u7684\u4eac\u5b98\uff0c\u6211\u5c31\u662f\u4f60\u8001\u5a46\uff0c\u800c\u4e14\u662f\u4e2a\u51fa\u540d\u7684\u918b\u575b \u5b50\u3002\u201d \u9646\u5c0f\u51e4\u8bf4\u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u67f3\u9752\u9752\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4e2a\u597d\u6d88\u606f\u544a\u8bc9\u4f60\uff0c\u6211\u4eec\u7684\u513f\u5b50\u4e5f\u4e00\u76f4\u90fd\u8ddf\u5728\u6211\u4eec\u8eab\u8fb9\u7684\u3002\u201d \u9646\u5c0f\u51e4\u53c8\u5403\u4e86\u4e00\u60ca\uff0c\u201c\u6211\u4eec\u7684\u513f\u5b50\u662f\u8c01\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u662f\u8868\u54e5\u3002\u201d \u9646\u5c0f\u51e4\u5ffd\u7136\u5012\u4e86\u4e0b\u53bb\uff0c\u76f4\u633a\u633a\u7684\u5012\u5728\u5e8a\u4e0a\uff0c\u8fde\u52a8\u90fd\u4e0d\u4f1a\u52a8\uff01 \u67f3\u9752\u9752\u5929\u7b11\uff0c\u5ffd\u7136\u6251\u5728\u4ed6\u8eab\u4e0a\uff0c\u5403\u5403\u7684\u7b11\u9053\uff1a\u201c\u6211\u7684\u4eba\u867d\u8001\uff0c\u5fc3\u5374\u4e0d\u8001\uff0c\u6211\u8fd8\u662f \u6bcf\u5929\u90fd\u8981\u7684\uff0c\u4f60\u60f3\u88c5\u6b7b\u90fd\u4e0d\u884c\u3002\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u6211\u7edd\u4e0d\u88c5\u6b7b\uff0c\u53ef\u662f\u4f60\u82e5\u8981\u6211\u6bcf\u5929\u8ddf\u4f60\u8fd9\u4e48\u6837\u4e00\u4e2a\u8001\u592a\u5a46\u505a\u90a3\u4ef6 \u4e8b\uff0c\u6211\u5c31\u771f\u7684\u8981\u6b7b\u4e86\u3002\u201d \u67f3\u9752\u9752\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u95ed\u8d77\u773c\u775b\u6765\uff0c\u62fc\u547d\u53bb\u60f3\u6211\u4ee5\u524d\u7684\u6837\u5979\u5df2\u7b11\u5f97\u5598\u4e0d\u8fc7\u6c14\uff0c \u201c\u4f55\u51b5\u4f60\u4eec\u7537\u4eba\u4e0d\u662f\u5e38\u5e38\u559c\u6b22\u8bf4\uff0c\u53ea\u8981\u95ed\u8d77\u773c\u775b\u6765\uff0c\u5929\u4e0b\u7684\u5973\u4eba\u5c31\u90fd\u662f\u4e00\u6837\u7684\u3002\u201d \u73b0\u5728\u9646\u5c0f\u51e4\u603b\u7b97\u660e\u767d\u81ea\u4f5c\u81ea\u53d7\u662f\u4ec0\u4e48\u610f\u601d\u4e86\u3002 \u8fd9\u4e2a\u6d1e\u672c\u6765\u662f\u4ed6\u81ea\u5df1\u8981\u6316\u7684\uff0c\u73b0\u5728\u4e00\u5934\u683d\u8fdb\u53bb\u7684\uff0c\u504f\u504f\u5c31\u662f\u4ed6\u81ea\u5df1\u3002 \u72ac\u90ce\u541b\u6765\u7684\u65f6\u5019\uff0c\u67f3\u9752\u9752\u8fd8\u5728\u5598\u606f\u3002 \u770b\u7740\u4e00\u4e2a\u8001\u6389\u4e86\u7259\u7684\u8001\u592a\u592a\uff0c\u5c11\u5973\u822c\u8eba\u5728\u4e00\u4e2a\u5e74\u9752\u7684\u7537\u4eba\u8eab\u65c1\u5598\u606f\uff0c\u5982\u679c\u8fd8\u80fd\u5fcd \u5f97\u4f4f\u4e0d\u7b11\u51fa\u6765\uff0c\u8fd9\u4e2a\u4eba\u7684\u672c\u4e8b\u4e00\u5b9a\u4e0d\u5c0f\u3002 \u72ac\u90ce\u541b\u7684\u672c\u4e8b\u5c31\u4e0d\u5c0f\u3002 \u4ed6\u5c45\u7136\u6ca1\u6709\u7b11\u51fa\u6765\uff0c\u5c45\u7136\u80fd\u88c5\u4f5c\u6ca1\u770b\u89c1\uff0c\u53ef\u662f\u7b49\u5230\u9646\u5c0f\u98ce\u7ad9\u8d77\u6765\uff0c\u4ed6\u5374\u5ffd\u7136\u5411\u9646 \u5c0f\u51e4\u6324\u4e86\u6324\u773c\u775b\uff0c\u597d\u50cf\u5728\u95ee\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d \u9646\u5c0f\u51e4\u7b80\u76f4\u6068\u4e0d\u5f97\u5c06\u4ed6\u8fd9\u53cc\u773c\u73e0\u5b50\u6316\u51fa\u6765\uff0c\u9001\u7ed9\u67f3\u9752\u9752\u5f53\u8695\u8c46\u5403\u3002 \u5e78\u597d\u4ed6\u8fd8\u6ca1\u6709\u52a8\u624b\uff0c\u95e8\u5916\u5df2\u6709\u4e2a\u6bd4\u67f3\u9752\u9752\u548c\u5a04\u8001\u592a\u5927\u52a0\u8d77\u6765\u90fd\u8001\u7684\u8001\u592a\u5a46\u4f38\u8fdb\u5934 \u6765\uff0c\u8d54\u7740\u7b11\u9053\uff1a\u201c\u8001\u7237\u548c\u592a\u592a\u6700\u597d\u8d76\u7d27\u51c6\u5907\uff0c\u6211\u4eec\u5929\u4e00\u4eae\u5c31\u52a8\u8eab\u3002\u201d \u8fd9\u4e2a\u4eba\u5f53\u7136\u5c31\u662f\u7ba1\u5bb6\u5a46\u3002 \u53c8\u6709\u8c01\u80fd\u60f3\u5f97\u5230\uff0c\u6614\u5e74\u4e0d\u53ef\u4e00\u4e16\u7684\u98ce\u5c3e\u5e2e\u5185\u4e09\u5802\u7684\u9ad8\u5802\u4e3b\uff0c\u7adf\u4f1a\u53d8\u6210\u8fd9\u526f\u6837\u5b50\uff1f \u9646\u5c0f\u51e4\u53c8\u89c9\u5f97\u6bd4\u8f83\u6109\u5feb\u4e86\uff0c\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u6211\u90a3\u5b9d\u8d1d\u513f\u5b50\u5462\uff1f\u5feb\u53eb\u4ed6\u8fdb\u6765\u7ed9\u8001\u5929\u8bf7 \u5b89\u3002\u201d \u770b\u8d77\u6765\u597d\u50cf\u53c8\u5e74\u9752\u4e86\u4e8c\u5341\u5c81\u7684\u8868\u54e5\uff0c\u53ea\u597d\u6101\u7709\u82e6\u8138\u7684\u8d70\u8fdb\u6765\u3002 \u9646\u5c0f\u51e4\u677f\u7740\u8138\u9053\uff1a\u201c\u5728\u4eac\u91cc\u505a\u5b98\u7684\u4eba\uff0c\u5bb6\u89c4\u603b\u662f\u6bd4\u8f83\u4e25\u7684\uff0c\u5c31\u7b97\u5728\u8def\u4e0a\uff0c\u4e5f\u9a6c\u864e \u4e0d\u5f97\uff0c\u6240\u4ee5\u4f60\u4ee5\u540e\u6bcf\u5929\u90fd\u8981\u6765\u8ddf\u6211\u78d5\u5934\u8bf7\u5b89\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u201d\u8868\u54e5\u53ea\u6709\u70b9\u5934\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u65e2\u7136\u77e5\u9053\uff0c\u8fd8\u4e0d\u8d76\u7d27\u8dea\u4e0b\u6765\u78d5\u5934\uff1f\u201d \u770b\u7740\u8868\u54e5\u771f\u7684\u8dea\u4e0b\u53bb\uff0c\u9646\u5c0f\u51e4\u7684\u5fc3\u60c5\u66f4\u597d\u4e86\uff0c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u505a\u8001\u5b50\u603b\u6bd4\u505a\u513f\u5b50\u6109 \u5feb\u5f88\u591a\u3002 \u8fd9\u4e00\u8def\u4e0a\u4ed6\u5f53\u7136\u4e5f\u4e0d\u4f1a\u5bc2\u5bde\uff0c\u9664\u4e86\u8001\u5a46\u5916\uff0c\u4ed6\u8fd8\u6709\u4e2a\u513f\u5b50\uff0c\u6709\u4e2a\u7ba1\u5bb6\uff0c\u6709\u4e2a\u7ba1\u5bb6 \u5a46\u3002 \u4ed6\u751a\u81f3\u8fd8\u6709\u4e00\u6761\u72d7\u3002 \u201c\u4e0d\u80fd\u5e26\u8fd9\u6761\u72d7\u53bb\uff01\u201d \u6d77\u5947\u9614\u65ad\u8155\u4e0a\u7684\u94a9\u5b50\u5df2\u5378\u4e0b\u6765\uff0c\u5149\u79c3\u79c3\u7684\u624b\u8155\u5728\u6ca1\u6709\u7528\u8863\u6cb9\u63a9\u76d6\u7740\u7684\u65f6\u5019\uff0c\u663e\u5f97 \u7b28\u62d9\u800c\u6ed1\u7a3d\u3002 \u4ed6\u7684\u8868\u60c5\u5374\u5f88\u4e25\u8083\uff0c\u6001\u5ea6\u66f4\u575a\u51b3\uff0c\u201c\u6211\u4eec\u7edd\u4e0d\u80fd\u5e26\u4ed6\u53bb\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u8001\u5200\u628a\u5b50\u7684\u547d\u4ee4\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u5f53\u7136\u662f\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e0d\u662f\u51c6\u5907\u6740\u4e86\u4ed6\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u662f\u3002\u201d \u73b0\u5728\u72ac\u90ce\u541b\u7684\u4efb\u52a1\u5df4\u7ed3\u675f\uff0c\u4ed6\u4eec\u5df4\u7528\u4e0d\u7740\u5bf9\u4ed6\u6709\u6240\u987e\u5fcc\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8c01\u52a8\u624b\u6740\u4e86\u4ed6\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u6211\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u4e0d\u7528\u94a9\u5b50\u4e5f\u53ef\u4ee5\u6740\u4eba\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u968f\u65f6\u90fd\u53ef\u4ee5\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u597d\uff0c\u90a3\u4e48\u4f60\u73b0\u5728\u5c31\u5148\u8fc7\u6765\u6740\u4e86\u6211\u5427\u3002\u201d \u6d77\u5947\u9614\u8138\u8272\u53d8\u4e86\uff0c\u201c\u4f60\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u9646\u5c0f\u51e4\u6de1\u6de1\u9053\uff1a\u201c\u6211\u7684\u610f\u601d\u5f88\u7b80\u5355\uff0c\u4ed6\u53bb\uff0c\u6211\u5c31\u53bb\uff0c\u4ed6\u6b7b\uff0c\u6211\u5c31\u6b7b\u3002\u201d \u4ed6\u5f53\u7136\u4e0d\u80fd\u6b7b\u3002 \u6d77\u5947\u9614\u770b\u770b\u8868\u54e5\uff0c\u8868\u54e5\u770b\u770b\u7ba1\u5bb6\u5a46\uff0c\u7ba1\u5bb6\u5a46\u770b\u770b\u67f3\u9752\u9752\uff0c\u67f3\u9752\u9752\u770b\u770b\u72ac\u90ce\u541b\uff0c\u5ffd \u7136\u95ee\u9053\uff1a\u201c\u4f60\u662f\u516c\u72d7\uff1f\u8fd8\u662f\u6bcd\u72d7\uff1f\u201d \u72ac\u90ce\u541b\u9053\uff1a\u201c\u662f\u516c\u7684\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u6709\u4e9b\u72d7\u665a\u4e0a\u559c\u6b22\u7761\u5728\u4e3b\u4eba\u7684\u5e8a\u65c1\u8fb9\uff0c\u4f60\u5462\uff1f\u201d \u72ac\u90ce\u541b\u9053\uff1a\u201c\u6211\u559c\u6b22\u7761\u5728\u95e8\u53e3\uff0c\u800c\u4e14\u4e00\u7761\u7740\u5c31\u50cf\u6b7b\u72d7\u2014\u6837\uff0c\u4ec0\u4e48\u90fd\u542c\u4e0d\u89c1\u3002\u201d \u67f3\u9752\u9752\u7b11\u4e86\uff0c\u201c\u53ea\u8981\u4e0d\u662f\u6bcd\u72d7\uff0c\u968f\u4fbf\u4ed6\u60f3\u5e26\u591a\u5c11\u6761\u53bb\uff0c\u6211\u90fd\u4e0d\u53cd\u5bf9\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6709\u6ca1\u6709\u4eba\u53cd\u5bf9\u7684\uff1f\u201d \u6d77\u5947\u9614\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u7ba1\u5bb6\u5a46\u7acb\u523b\u9053\uff1a\u201c\u534a\u4e2a\u4eba\u90fd\u6ca1\u6709\u3002\u201d \u9646\u5c0f\u51e4\u770b\u7740\u8868\u54e5\uff0c\u201c\u4f60\u5462\uff1f\u201d \u8868\u54e5\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u662f\u4e2a\u5b5d\u5b50\uff0c\u6211\u6bd4\u72d7\u8fd8\u542c\u8bdd\u5341\u500d\u3002\u201d \u6240\u4ee5\u6211\u4eec\u7684\u9646\u5927\u8001\u7237\u5c31\u5e26\u7740\u56db\u4e2a\u4eba\u548c\u4e00\u6761\u72d7\uff0c\u6d69\u6d69\u8361\u8361\u7684\u8d70\u51fa\u4e86\u5e7d\u7075\u5c71\u5e84\u3002 \u8fd9\u5df2\u662f\u4ed6\u7b2c\u4e8c\u6b21\u79bb\u5f00\u8fd9\u5730\u65b9\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u8fd9\u4e00\u6b21\u662f\u7edd\u4e0d\u4f1a\u518d\u56de\u6765\u4e86\u3002 \u7b2c\u5341\u4e8c\u7ae0\u3000\u9b3c\u5c4b\u60ca\u9b42>> \u53e4\u9f99\u300a\u9646\u5c0f\u51e4\u7cfb\u5217\xb7\u5e7d\u7075\u5c71\u5e84\u300b \u7b2c\u5341\u4e8c\u7ae0\u3000\u9b3c\u5c4b\u60ca\u9b42 \u56db\u6708\u521d\u4e94\uff0c\u6674\u3002 \u9646\u5c0f\u51e4\u6b63\u5bf9\u7740\u4e00\u9762\u64e6\u5f97\u5f88\u4eae\u7684\u94a2\u955c\u5fae\u7b11\u3002 \u770b\u5230\u955c\u5b50\u91cc\u7684\u4eba\u5c45\u7136\u4e0d\u662f\u81ea\u5df1\uff0c\u8fd9\u79cd\u611f\u89c9\u867d\u7136\u6709\u884c\u602a\u602a\u7684\uff0c\u5374\u5f88\u6709\u8da3\u3002 \u955c\u5b50\u91cc\u8fd9\u4e2a\u8001\u4eba\u5f53\u7136\u6ca1\u6709\u4ed6\u672c\u6765\u90a3\u4e48\u82f1\u4fca\uff0c\u770b\u8d77\u6765\u5374\u5f88\u6709\u5a01\u4e25\uff0c\u5f88\u6709\u6c14\u6d3e\uff0c\u7edd\u4e0d \u662f\u90a3\u79cd\u9152\u8272\u8fc7\u5ea6\uff0c\u2014\u6761\u817f\u5df2\u8fdb\u4e86\u68fa\u6750\u7684\u7cdf\u8001\u5934\u3002 \u8fd9\u4e00\u70b9\u65e0\u7591\u4f7f\u4ed6\u89c9\u5f97\u5f88\u6109\u5feb\uff0c\u552f\u4e00\u7684\u9057\u61be\u5c31\u662f\u4e0d\u80fd\u6d17\u8138\u3002 \u6240\u4ee5\u4ed6\u53ea\u80fd\u7528\u5e72\u6bdb\u5dfe\u8c61\u5f81\u6027\u7684\u5728\u8138\u4e0a\u64e6\u4e86\u64e6\uff0c\u518d\u75db\u75db\u5feb\u5feb\u7684\u6f31\u4e86\u53e3\uff0c\u518d\u8f6c\u8fc7\u5934\u770b \u7740\u5e8a\u4e0a\u7684\u8001\u592a\u5a46\u3002 \u4ed6\u6447\u5934\u53f9\u6c14\u9053\uff1a\u201c\u72ac\u90ce\u541b\u7684\u786e\u5e94\u8be5\u8ba9\u4f60\u5e74\u8f7b\u4e00\u70b9\u7684\uff0c\u73b0\u5728\u4f60\u770b\u6765\u7b80\u76f4\u50cf\u6211\u7684 \u5988\u3002\u201d \u67f3\u9752\u9752\u54ac\u7740\u7259\uff0c\u6068\u6068\u7684\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u522b\u4eba\u968f\u4fbf\u628a\u4f60\u5f04\u6210\u4ec0\u4e48\u6837\u5b50\uff0c\u4f60\u90fd\u4e00\u6837\u80fd \u591f\u81ea\u6211\u9676\u9189\u7684\u3002\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\uff0c\u5927\u7b11\u3002 \u8fd9\u65f6\u90a3\u6761\u542c\u8bdd\u7684\u72d7\u5df2\u6447\u7740\u5c3e\u5df4\u8fdb\u6765\u4e86\uff0c\u5b5d\u987a\u7684\u513f\u5b50\u4e5f\u5df2\u8d76\u6765\u78d5\u5934\u8bf7\u5b89\u3002 \u9646\u5c0f\u51e4\u66f4\u6109\u5feb\uff0c\u4ed6\u7b11\u7740\u9053\uff1a\u201c\u4eca\u5929\u4f60\u4eec\u90fd\u5f88\u4e56\uff0c\u6211\u8bf7\u4f60\u4eec\u5230\u2018\u4e09\u516d\u4e5d\u2019\u53bb\u5403\u706b\u817f \u5e72\u4e1d\u548c\u5c0f\u7b3c\u84b8\u5305\u53bb\u3002\u201d \u201c\u4e09\u516d\u4e5d\u201d\u7684\u84b8\u5305\u5c0f\u5de7\u73b2\u78c1\uff0c\u4e00\u7b3c\u4e8c\u5341\u4e2a\uff0c\u4e00\u53e3\u5403\u4e00\u4e2a\uff0c\u5403\u4e0a\u4e2a\u4e09\u4e94\u7b3c\u4e5f\u4e0d\u5acc \u591a\u3002 \u8fde\u9646\u5927\u8001\u7237\u7684\u72d7\u90fd\u5403\u4e86\u4e09\u7b3c\uff0c\u53ef\u662f\u4ed6\u7684\u7ba1\u5bb6\u548c\u7ba1\u5bb6\u5a46\u5374\u53ea\u80fd\u7ad9\u5728\u540e\u9762\u5f85\u5019\u7740\u3002 \u5728\u4eac\u91cc\u505a\u5b98\u7684\u5927\u8001\u7237\u4eec\uff0c\u89c4\u77e9\u603b\u662f\u6bd4\u522b\u4eba\u5927\u7684\u3002 \u5e97\u91cc\u7684\u8dd1\u5802\u5728\u65c1\u8fb9\u770b\u7740\u53ea\u6447\u5934\uff0c\u7528\u534a\u751f\u4e0d\u719f\u7684\u82cf\u5dde\u5b98\u8bdd\u642d\u6c55\u7740\u9053\uff1a\u201c\u770b\u6765\u80fd\u5728\u5927 \u8001\u7237\u5bb6\u91cc\u505a\u6761\u72d7\uff0c\u4e5f\u662f\u597d\u798f\u6c14\u7684\uff0c\u6bd4\u597d\u4e9b\u4eba\u90fd\u5f3a\u5f97\u591a\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u6b63\u5728\u7528\u81ea\u5df1\u5e26\u6765\u7684\u94f6\u7259\u7b7e\u5254\u7740\u7259\uff0c\u5634\u91cc\u201c\u5567\u5567\u201d\u7684\u76f4\u54cd\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u65e2 \u7136\u559c\u6b22\u5b83\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5e26\u5b83\u51fa\u53bb\u6e9c\u6e9c\uff0c\u968f\u4fbf\u5728\u5916\u9762\u653e\u6ce1\u91ce\u5c4e\uff0c\u56de\u6765\u8001\u7237\u5728\u8d4f\u3002\u201d \u8dd1\u5802\u7684\u8fdf\u7591\u7740\uff0c\u770b\u7740\u7ba1\u5bb6\u548c\u7ba1\u5bb6\u5a46\uff0c\u201c\u8fd9\u4f4d\u7ba1\u5bb6\u8001\u7237\u4e0d\u8001\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u4e0d\u559c\u6b22\u8fd9\u6761\u72d7\uff0c\u6240\u4ee5\u8fd9\u6761\u72d7\u5c31\u559c\u6b22\u54ac\u4ed6\u3002 \u8dd1\u5802\u7684\u5bb3\u6015\u4e86\uff0c\u201c\u8fd9\u4f0e\u72d7\u8001\u7237\u559c\u6b22\u4e0d\u559c\u6b22\u54ac\u522b\u7684\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u4ece\u9f3b\u5b54\u91cc\u201c\u54fc\u201d\u5382\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u522b\u7684\u4eba\u5c31\u7b97\u8bf7\u5b83\u54ac\uff0c\u5b83\u8fd8\u61d2\u5f97\u5f20\u5f00\u53e3 \u54e9\u3002\u201d \u5927\u8001\u7237\u7684\u592b\u4eba\u4e5f\u5728\u65c1\u8fb9\u5f00\u4e86\u8154\uff0c\u201c\u6211\u4eec\u8fd9\u6761\u72d7\u867d\u7136\u4e0d\u54ac\u4eba\uff0c\u4e5f\u4e0d\u5543\u9aa8\u5934\uff0c\u53ef\u662f\u6709 \u70b9\u559c\u6b22\u5403\u5c4e\uff0c\u4f60\u6700\u591a\u53ea\u80fd\u8ba9\u5b83\u8214\u4e00\u8214\uff0c\u5343\u4e07\u4e0d\u80fd\u8ba9\u5b83\u771f\u7684\u5403\u4e0b\u53bb\uff0c\u4ed6\u4f1a\u95f9\u809a\u5b50\u7684\u3002\u201d \u8dd1\u5802\u7684\u53ea\u6709\u8d54\u7740\u7b11\uff0c\u62c9\u8d77\u7275\u72d7\u7684\u76ae\u5e26\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u5e26\u7740\u8fd9\u4f4d\u72d7\u8001\u7237\u6563\u6b65\u53bb\u4e86\u3002 \u7ba1\u5bb6\u770b\u7740\u7ba1\u5bb6\u5a46\uff0c\u7ba1\u5bb6\u5a46\u770b\u770b\u5b5d\u5b50\uff0c\u5b5d\u5b50\u770b\u770b\u8001\u592a\u592a\u3002 \u8001\u592a\u592a\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u653e\u5fc3\uff0c\u4f60\u8001\u5b50\u7684\u8fd9\u6761\u72d7\u662f\u4e56\u5b9d\u8d1d\u3001\u7edd\u5bf9\u4e0d\u4f1a\u8dd1\u4e86\u7684\uff0c\u800c\u4e14\u5b83 \u5c31\u7b97\u60f3\u8dd1\uff0c\u4e5f\u8dd1\u4e0d\u4e86\u3002\u201d \u5b5d\u5b50\u5fcd\u4e0d\u4f4f\u95ee\uff0c\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u8001\u592a\u592a\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u4e5f\u8981\u8ddf\u7740\u5b83\u53bb\uff0c\u5b83\u62c9\u5c4e\u7684\u65f6\u5019\uff0c\u4f60\u4e5f\u5f97\u5728\u65c1\u8fb9\u7b49\u7740\u3002\u201d \u8868\u54e5\u679c\u7136\u542c\u8bdd\u5f97\u5f88\u7ad9\u8d77\u6765\u5c31\u8d70\u3002 \u9646\u5c0f\u51e4\u7b11\u4e86\uff0c\u5fae\u7b11\u7740\u9053\uff1a\u201c\u770b\u6765\u6211\u4eec\u8fd9\u4e2a\u513f\u5b50\u5012\u771f\u662f\u4e2a\u5b5d\u5b50\u201d \u9646\u5c0f\u51e4\u6709\u4e2a\u6bdb\u75c5\uff0c\u6bcf\u5929\u5403\u8fc7\u65e9\u70b9\u4e4b\u540e\uff0c\u597d\u50cf\u90fd\u4e00\u5b9a\u8981\u53bb\u65b9\u4fbf\u65b9\u4fbf\u3002 \u4ed6\u7684\u9152\u559d\u5f97\u592a\u591a\uff0c\u6240\u4ee5\u80a0\u80c3\u4e0d\u592a\u597d\u3002 \u8001\u592a\u592a\u5c31\u7b97\u771f\u662f\u4e2a\u7279\u5927\u53f7\u7684\u918b\u575b\u5b50\uff0c\u4eba\u76ef\u4eba\u7684\u672c\u4e8b\u518d\u5927\uff0c\u81f3\u5c11\u8001\u7237\u5728\u65b9\u4fbf\u7684\u65f6 \u5019\uff0c\u5979\u603b\u4e0d\u80fd\u5728\u65c1\u8fb9\u76ef\u7740\u7684\u3002 \u53ef\u662f\u4e00\u6761\u72d7\u82e5\u8981\u76ef\u7740\u4e00\u4e2a\u4eba\u7684\u65f6\u5019\uff0c\u5c31\u6ca1\u6709\u8fd9\u4e48\u591a\u987e\u5fcc\u4e86\uff0c\u4e0d\u7ba1\u4f60\u662f\u5728\u65b9\u4fbf\u4e5f \u597d\uff0c\u662f\u4e0d\u65b9\u4fbf\u4e5f\u597d\uff0c\u5b83\u90fd\u53ef\u4ee5\u8ddf\u7740\u4f60\u3002 \u6240\u4ee5\u9646\u5c0f\u51e4\u6bcf\u6b21\u8981\u65b9\u4fbf\u7684\u65f6\u5019\uff0c\u72ac\u90ce\u541b\u90fd\u4f1a\u6447\u7740\u5c3e\u5df4\u8ddf\u8fdb\u53bb\u3002 \u4eca\u5929\u4e5f\u4e0d\u4f8b\u5916\u3002 \u9646\u5c0f\u51e4\u4e00\u8e72\u4e0b\u53bb\uff0c\u4ed6\u5c31\u7acb\u523b\u538b\u4f4e\u58f0\u97f3\u9053\uff1a\u201c\u90a3\u4e2a\u8dd1\u5802\u7684\u7edd\u4e0d\u662f\u4e2a\u771f\u7684\u8dd1\u5802aH\u6ca1\u6709 \u53cd\u5e94\uff0c\u9646\u5c0f\u51e4\u6839\u672c\u4e0d\u776c\u4ed6\u3002 \u72ac\u90ce\u541b\u9053\uff1a\u201c\u4ed6\u7684\u8f7b\u529f\u4e00\u5b9a\u5f88\u9ad8\uff0c\u6211\u4ece\u4ed6\u7684\u811a\u6b65\u58f0\u5c31\u53ef\u4ee5\u542c\u5f97\u51fa\u6765\u3002\u201d \u8fd8\u662f\u6ca1\u6709\u53cd\u5e94\u3002 \u5c31\u50cf\u5927\u591a\u6570\u4eba\u4e00\u6837\uff0c\u9646\u5c0f\u51e4\u5728\u65b9\u4fbf\u7684\u65f6\u5019\uff0c\u4e5f\u662f\u4e13\u5fc3\u4e00\u610f\uff0c\u5168\u795e\u8d2f\u6ce8\u7684\u3002 \u72ac\u90ce\u541b\u53c8\u9053\uff1a\u201c\u800c\u4e14\u6211\u770b\u4ed6\u4e00\u5b9a\u8fd8\u662f\u4e2a\u6613\u5bb9\u7684\u9ad8\u624b\uff0c\u751a\u81f3\u6bd4\u6211\u8fd8\u9ad8\u3002\u201d \u9646\u5c0f\u51e4\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u662f\u4e2a\u4ec0\u4e48\uff1f\u4f60\u662f\u4e2a\u5996\u602a\u3002\u201d \u72ac\u90ce\u541b\u6014\u4e86\u6014\uff0c\u201c\u5996\u602a\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u2014\u6761\u72d7\u5c45\u7136\u4f1a\u8bf4\u4eba\u8bdd\uff0c\u4e0d\u662f\u5996\u602a\u662f\u4ec0\u4e48\uff1f\u201d \u72ac\u90ce\u541b\u9053\uff1a\u201c\u53ef\u662f\u2026\u2026\u201d \u9646\u5c0f\u51e4\u4e0d\u8ba9\u4ed6\u8bf4\u4e0b\u53bb\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u522b\u4eba\u662f\u600e\u4e48\u5bf9\u4ed8\u5996\u602a\u7684\uff1f\u201d \u72ac\u90ce\u541b\u6447\u6447\u5934\u3002 \u9646\u5c0f\u51e4\u51b7\u51b7\u9053\uff1a\u201c\u4e0d\u662f\u6d3b\u6d3b\u7684\u70e7\u6b7b\uff0c\u5c31\u662f\u6d3b\u6d3b\u7684\u6253\u6b7b\u3002\u201d \u72ac\u90ce\u541b\u8fde\u2018\u4e2a\u5b57\u90fd\u4e0d\u6562\u518d\u8bf4\uff0c\u5c31\u4e56\u4e56\u7684\u631f\u7740\u5c3e\u5df4\u6e9c\u4e86\u3002 \u9646\u5c0f\u51e4\u603b\u7b97\u8f7b\u677e\u4e86\u4e00\u4e0b\u5b50\uff0c\u5bf9\u4ed6\u6765\u8bf4\uff0c\u80fd\u4e00\u4e2a\u4eba\u5b89\u5b89\u9759\u9759\u7684\u5750\u4e0b\u6765\uff0c\u5c31\u7b97\u662f\u5750\u5728 \u9a6c\u6876\u4e0a\uff0c\u4e5f\u5df2\u7ecf\u662f\u79cd\u4eab\u53d7\uff0c\u800c\u4e14\u662f\u79cd\u5f88\uff0c\u56e0\u4e3a\u4ed6\u5ffd\u7136\u6709\u4e86\u4e2a\u4f1a\u76ef\u4eba\u7684\u8001\u5a46\u3002 \u4ed6\u51fa\u53bb\u7684\u65f6\u5019\uff0c\u624d\u53d1\u73b0\u67f3\u9752\u9752\u5c45\u7136\u5df2\u7ecf\u5728\u5916\u9762\u7b49\u7740\uff0c\u800c\u4e14\u50cf\u662f\u5df2\u7b49\u4e86\u5f88\u4e45\uff0c\u5730\u4e0a \u7684\u8695\u8c46\u58f3\u5df2\u6709\u4e00\u5927\u5806\u3002 \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u662f\u559c\u6b22\u770b\u7537\u4eba\u65b9\u4fbf\uff1f\u8fd8\u662f\u559c\u6b22\u55c5\u8fd9\u91cc\u7684\u81ed\u6c14\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u6709\u70b9\u7591\u5fc3\u800c\u5df2\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u7591\u5fc3\u4ec0\u4e48\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u7591\u5fc3\u4f60\u5e76\u4e0d\u662f\u771f\u7684\u60f3\u65b9\u4fbf\uff0c\u53ea\u4e0d\u8fc7\u662f\u60f3\u501f\u673a\u907f\u5f00\u6211\uff0c\u8ddf\u4f60\u7684\u72d7\u670b\u53cb \u8bf4\u6084\u6084\u8bdd\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6240\u4ee5\u4f60\u5c31\u5750\u5728\u5916\u9762\u542c\u6211\u662f\u4e0d\u662f\u771f\u7684\u65b9\u4fbf\u4e86\u3002 \u67f3\u9752\u9752\u7b11\u9053\uff1a\u201c\u73b0\u5728\u6211\u624d\u77e5\u9053\uff0c\u8fd9\u79cd\u58f0\u97f3\u5b9e\u5728\u4e0d\u592a\u597d\u542c\u3002 \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5e78\u597d\u4ed6\u662f\u6761\u516c\u72d7\uff0c\u82e5\u662f\u6bcd\u72d7\uff0c\u90a3\u8fd8\u4e86\u5f97\uff1f\u201d \u67f3\u9752\u9752\u6de1\u6de1\u9053\uff1a\u201c\u82e5\u662f\u6761\u6bcd\u72d7\uff0c\u73b0\u5728\u65e9\u5df2\u662f\u6761\u6b7b\u72d7\u4e86ao\u56db\u6708\u521d\u516d\uff0c\u6674\u65f6\u591a\u4e91\u3002 \u7ba1\u5bb6\u5a46\u7684\u7c3f\u5b50\u4e0a\u8bb0\u7740\uff1a\u201c\u65e9\u70b9\u662f\u5728\u57ce\u4e1c\u594e\u5143\u9986\u5403\u7684\uff0c\u5176\u95f4\u53c8\u4ee4\u4eba\u6e9c\u72d7\u4e00\u6b21\uff0c\u6765\u56de \u7ea6\u4e2d\u4e2a\u65f6\u8fb0\u3002\u201d \u201c\u6e9c\u72d7\u7684\u5802\u9986\u59d3\u738b\uff0c\u5f53\u5730\u58eb\u751f\u571f\u957f\uff0c\u5e72\u5802\u68fa\u5df2\u5341\u56db\u5e74\uff0c\u5df2\u5a36\u59bb\uff0c\u6709\u5b50\u5973\u5404\u4e00\u3002\u201d \u201c\u6b64\u4eba\u5df2\u8c03\u67e5\u786e\u5b9e\uff0c\u7edd\u65e0\u7591\u95ee\u3002\u201d \u8fd9\u7c3f\u5b50\u5f53\u7136\u662f\u8981\u4ea4\u7ed9\u8001\u5200\u628a\u5b50\u770b\u7684\uff0c\u6d77\u5947\u9614\u5374\u53cd\u5bf9\uff0c\u201c\u4e0d\u884c\uff0c\u4e0d\u80fd\u8fd9\u4e48\u5199\u3002\u201d \u7ba1\u5bb6\u5a46\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u6211\u4eec\u6839\u672c\u5c31\u4e0d\u8be5\u5e26\u8fd9\u6761\u72d7\u6765\uff0c\u66f4\u4e0d\u8be5\u8ba9\u4ed6\u627e\u522b\u4eba\u53bb\u6e9c\u72d7\u7684\uff0c\u8001\u5200\u628a \u5b50\u770b\u4e86\uff0c\u4e00\u5b9a\u4f1a\u8ba4\u4e3a\u5176\u4e2d\u6709\u95ee\u9898\u3002\u201d \u7ba1\u5bb6\u5a46\u9053\uff1a\u201c\u4f60\u51c6\u5907\u600e\u4e48\u529e\uff1f\u201d \u6d77\u5947\u9614\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u6761\u72d7\u82e5\u662f\u6761\u6b7b\u72d7\uff0c\u5c31\u597d\u50cf\u751f\u7c73\u5df2\u716e\u6210\u719f\u996d\u4e00\u6837\uff0c\u4ed6\u80fd\u628a\u6211 \u600e\u4e48\u6837\uff1f\u201d \u7ba1\u5bb6\u5a46\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5374\u4e0d\u77e5\u8fd9\u6761\u6d3b\u72d7\uff0c\u8981\u7b49\u5230\u65f6\u5019\u624d\u4f1a\u53d8\u6210\u6b7b\u72d7\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u5feb\u4e86\u3002\u201d \u7ba1\u5bb6\u5a46\u9053\uff1a\u201c\u660e\u5929\u4f60\u53bb\u6e9c\u72d7\uff1f\u201d \u6d77\u5947\u9614\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u597d\u50cf\u8fd8\u662f\u6211\u751f\u5e73\u7b2c\u4e00\u6b21\u505a\u8fd9\u79cd\u4e8b\u3002 \u7ba1\u5bb6\u5a46\u9053\uff1a\u201c\u662f\u4e0d\u662f\u6700\u540e\u4e00\u6b21\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u662f\u7684\uff0c\u7edd\u5bf9\u662f\u7684\u3002\u201d \u56db\u6708\u521d\u4e03\uff0c\u6674\u3002 \u6d77\u5947\u9614\u5df2\u7275\u7740\u72d7\u8d70\u4e86\u5f88\u8fdc\uff0c\u597d\u50cf\u8fd8\u6ca1\u6709\u56de\u5934\u7684\u610f\u601d\u3002 \u8868\u54e5\u8ddf\u5728\u540e\u9762\uff0c\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u51e0\u65f6\u53d8\u5f97\u8fd9\u4e48\u559c\u6b22\u8d70\u8def\u7684\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u521a\u624d\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u73b0\u5728\u4f60\u51c6\u5907\u8d70\u5230\u54ea\u91cc\u53bb\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u51fa\u57ce\u53bb\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u51fa\u57ce\u53bb\u5e72\u4ec0\u4e48\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u2014\u6761\u72d7\u6b7b\u5728\u9053\u4e0a\uff0c\u867d\u7136\u662f\u4ef6\u5f88\u5e73\u5e38\u7684\u4e8b\uff0c\u72d7\u76ae\u91cc\u82e5\u662f\u5ffd\u7136\u53d8\u51fa\u4e2a\u4eba \u6765\uff0c\u5c31\u5b8c\u5168\u662f\u53e6\u5916\u4e00\u4ef6\u4e8b\u4e86\u3002 \u8868\u54e5\u9053\uff1a\u201c\u8fd9\u79cd\u4e8b\u5f53\u7136\u662f\u7edd\u4e0d\u80fd\u8ba9\u522b\u4eba\u770b\u89c1\u7684\u3002 \u6d77\u5947\u9614\u9053\uff1a\u201c\u6240\u4ee5\u6211\u4eec\u8981\u51fa\u57ce\u53bb\u3002\u201d \u4ed6\u7d27\u7d27\u63e1\u7740\u7275\u72d7\u7684\u76ae\u5e26\uff0c\u8868\u54e5\u7684\u624b\u4e5f\u63e1\u4f4f\u4e86\u8863\u4e0b\u7684\u5251\u67c4\u3002 \u8fd9\u6761\u72d7\u4e0d\u4f46\u542c\u5f97\u61c2\u4eba\u8bdd\uff0c\u800c\u4e14\u8fd8\u662f\u4e2a\u6697\u5668\u9ad8\u624b\uff0c\u5982\u679c\u72d7\u6ca1\u6709\u6b7b\u5728\u4eba\u624b\u91cc\uff0c\u4eba\u53cd\u800c \u6b7b\u5728\u72d7\u624b\u91cc\u4e86\uff0c\u90a3\u624d\u771f\u7684\u662f\u7b11\u8bdd\u3002 \u8c01\u77e5\u8fd9\u6761\u72d7\u5c45\u7136\u8fde\u2014\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\u3002 \u8868\u54e5\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u72d7\u809a\u5b50\u91cc\u5728\u6253\u4ec0\u4e48\u9b3c\u4e3b\u610f\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u8fd9\u9644\u8fd1\u597d\u50cf\u5df2\u6ca1\u6709\u4eba\u4e86\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u7b80\u76f4\u8fde\u6761\u4eba\u5f71\u90fd\u6ca1\u6709\u3002\u201d \u6d77\u5947\u9614\u5ffd\u7136\u505c\u4e0b\u6765\uff0c\u770b\u7740\u8fd9\u6761\u72d7\uff0c\u53f9\u606f\u7740\u9053\uff1a\u201c\u72ac\u5144\u72ac\u5144\uff0c\u6211\u4eec\u4e5f\u66fe\u5728\u4e00\u8d77\u5403\u8fc7 \u996d\uff0c\u559d\u8fc7\u9152\uff0c\u603b\u7b97\u4e5f\u662f\u670b\u53cb\uff0c\u4f60\u82e5\u6709\u4ec0\u4e48\u9057\u8a00\u540e\u4e8b\uff0c\u4e5f\u4e0d\u59a8\u8bf4\u51fa\u6765\uff0c\u53ea\u8981\u6211\u4eec\u80fd\u505a \u7684\uff0c\u6211\u4eec6\u5b9a\u66ff\u4f60\u505a\u3002\u201d \u72d7\u5728\u6447\u5c3e\u5df4\uff0c\u201c\u6c6a\u6c6a\u201d\u7684\u76f4\u53eb\u3002 \u6d77\u5947\u9614\u9053\uff1a\u201c\u4f60\u6447\u5c3e\u5df4\u4e5f\u6ca1\u6709\u7528\uff0c\u6211\u4eec\u8fd8\u662f\u8981\u6740\u4e86\u4f60\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u53ef\u662f\u6211\u4fdd\u8bc1\u7edd\u4e0d\u4f1a\u628a\u4f60\u5356\u5230\u6302\u7740\u7f8a\u5934\u7684\u9999\u8089\u5e97\u6d77\u5947\u9614\u8fd8\u5728\u53f9\u7740\u6c14\uff0c\u918b \u94b5\u822c\u5927\u7684\u62f3\u5934\u5df2\u6325\u51fa\uff0c\u4e00\u62f3\u6253\u5728\u72d7\u5934\u4e0a\u3002\u62f3\u5934\u843d\u4e0b\uff0c\u7acb\u523b\u542c\u89c1\u4e86\u9aa8\u5934\u788e\u88c2\u7684\u58f0\u97f3\u3002 \u8fd9\u6761\u72d7\u72c2\u5420\u4e00\u58f0\uff0c\u5c45\u7136\u8fd8\u80fd\u6491\u8d77\u6765\uff0c\u8868\u54e5\u7684\u5251\u5374\u5df2\u523a\u5165\u4e86\u5b83\u7684\u8116\u5b50\u3002 \u9c9c\u8840\u98de\u6e85\uff0c\u6d77\u5947\u9614\u51cc\u7a7a\u63a0\u8d77\uff0c\u7b49\u4ed6\u843d\u4e0b\u6765\u65f6\uff0c\u6d3b\u72d7\u5c31\u5df2\u53d8\u6210\u4e86\u6b7b\u72d7\u3002 \u6d77\u5947\u9614\u677e\u4e86\u53e3\u6c14\uff0c\u7b11\u9053\uff1a\u201c\u770b\u6765\u6740\u72d7\u7684\u786e\u6bd4\u6740\u4eba\u8f7b\u677e\u5f97\u591a\u3002 \u8868\u54e5\u5374\u6c89\u7740\u8138\uff0c\u5ffd\u7136\u51b7\u7b11\u9053\uff1a\u201c\u53ea\u6015\u6211\u4eec\u6740\u7684\u771f\u662f\u6761\u72d7\u3002\u201d \u6d77\u5947\u9614\u5403\u4e86\u4e00\u60ca\uff0c\u7acb\u523b\u4fef\u4e0b\u8eab\u5b50\uff0c\u60f3\u5265\u5f00\u72d7\u76ae\u6765\u770b\u770b\u3002 \u72d7\u76ae\u91cc\u9762\u4e5f\u662f\u72d7\uff0c\u8fd9\u6761\u72d7\u7adf\u4e0d\u662f\u72ac\u5373\u541b\u3002 \u6d77\u5947\u9614\u8138\u8272\u53d8\u4e86\uff0c\u9053\uff1a\u201c\u6211\u660e\u660e\u770b\u89c1\u7684\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u770b\u89c1\u4ec0\u4e48\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u770b\u89c1\u72ac\u90ce\u541b\u94bb\u8fdb\u8fd9\u4e48\u6837\u4e00\u5f20\u72d7\u76ae\u91cc\u53bb\uff0c\u5c31\u53d8\u6210\u4e86\u8fd9\u4e48\u6837\u4e00\u6761\u72d7\u3002\u201d \u8868\u54e5\u51b7\u51b7\u9053\uff1a\u201c\u72d7\u6709\u5f88\u591a\u79cd\uff0c\u540c\u79cd\u7684\u72d7\u6837\u5b50\u90fd\u5dee\u4e0d\u591a\u7684\u3002\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u90a3\u4e48\u72ac\u90ce\u541b\u54ea\u91cc\u53bb\u4e86\uff1f\u8fd9\u6761\u72d7\u53c8\u662f\u600e\u4e48\u6765\u7684\uff1f\u201d \u8868\u54e5\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u95ee\u9646\u5c0f\u51e4\uff1f\u201d \u5395\u6240\u5916\u9762\u5c45\u7136\u53c8\u6709\u5165\u5728\u7b49\u7740\uff0c\u9646\u5c0f\u51e4\u521a\u8d70\u5230\u95e8\u53e3\uff0c\u8fde\u88e4\u5e26\u90fd\u6ca1\u6709\u7cfb\u597d\uff0c\u5c31\u770b\u89c1\u4e86 \u6d77\u5947\u9614\u3002 \u6d77\u5947\u9614\u7684\u6837\u5b50\uff0c\u770b\u6765\u5c31\u50cf\u662f\u5df2\u7ecf\u9cd6\u4e0d\u4f4f\u4e86\uff0c\u4e00\u6ce1\u5c4e\u5df2\u62c9\u5728\u88e4\u6863\u91cc\u3002 \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u54fa\u54fa\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u6211\u6bcf\u6b21\u65b9\u4fbf\u7684\u65f6\u5019\uff0c\u5916\u9762\u90fd\u6709\u4eba\u5728\u6392\u5bf9\uff0c\u96be \u9053\u5927\u5bb6\u90fd\u5403\u9519\u4e86\u836f\u3002\u90fd\u5728\u62c9\u809a\u5b50\uff1f\u201d \u6d77\u5947\u9614\u54ac\u7740\u7259\uff0c\u6068\u6068\u9053\uff1a\u201c\u6211\u4f8b\u6ca1\u6709\u5403\u9519\u836f\uff0c\u53ea\u4e0d\u8fc7\u6740\u9519\u4e86\u4eba\u3002\u201d \u9646\u5c0f\u51e4\u597d\u50cf\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u4f60\u6740\u4e86\u8c01\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u6211\u6740\u4e86\u4e00\u6761\u72d7\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u6740\u7684\u7a76\u7adf\u662f\u4eba\uff1f\u8fd8\u662f\u72d7\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u6211\u6740\u7684\u90a3\u6761\u72d7\u672c\u6765\u5e94\u8be5\u662f\u4e2a\u4eba\u7684\uff0c\u8c01\u77e5\u5b83\u7adf\u771f\u7684\u662f\u6761\u72d7\uff0c\u72d7\u76ae\u91cc\u9762 \u4e5f\u6ca1\u6709\u4eba\u3002\u201d \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u72d7\u5c31\u662f\u72d7\uff0c\u72d7\u76ae\u91cc\u9762\u5f53\u7136\u53ea\u6709\u72d7\u8089\u548c\u72d7\u9aa8\u5934\uff0c\u5f53\u7136\u4e0d\u4f1a \u6709\u4eba\u300f\u201d \u4ed6\u53f9\u606f\u7740\uff0c\u62cd\u4e86\u62cd\u6d77\u5947\u9614\u7684\u80a9\uff0c\u201c\u6700\u8fd1\u4f60\u2014\u5b9a\u592a\u7d2f\u4e86\uff0c\u82e5\u662f\u8fd8\u4e0d\u597d\u597d\u7684\u53bb\u4f11\u606f\u4f11 \u606f\uff0c\u8bf4\u4e0d\u5b9a\u771f\u4f1a\u53d1\u75af\u7684\u3002\u201d \u6d77\u5947\u9614\u770b\u6837\u5b50\u597d\u50cf\u771f\u7684\u8981\u88ab\u6c14\u75af\u5382\uff0c\u5ffd\u7136\u5927\u53eb\u9053\uff1a\u201c\u72ac\u90ce\u541b\u5462\uff1f\u201d \u9646\u5c0f\u51e4\u6de1\u6de1\u9053\uff1a\u201c\u4ed6\u65e2\u4e0d\u662f\u6211\u513f\u5b50\uff0c\u53c8\u4e0d\u662f\u6211\u7684\u7ba1\u5bb6\uff0c\u6211\u600e\u4e48\u77e5\u9053\u4ed6\u5728\u54ea\u91cc\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u53ef\u662f\u4e00\u5b9a\u8981\u5e26\u4ed6\u4e0b\u5c71\u6765\u7684\u5374\u662f\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u53ea\u4e0d\u8fc7\u53ea\u8981\u5e26\u6761\u72d7\u4e0b\u5c71\uff0c\u5e76\u6ca1\u6709\u8bf4\u8981\u5e26\u72ac\u90ce\u541b\u3002\u201d \u4ed6\u53c8\u62cd\u4e86\u62cd\u6d77\u5947\u9614\u7684\u80a9\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u867d\u7136\u6740\u4e86\u6211\u7684\u72d7\uff0c\u53ef\u662f\u6211\u5e76\u4e0d\u60f3\u8981\u4f60 \u507f\u547d\uff0c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u4e00\u4e2a\u597d\u7ba1\u5bb6\u603b\u6bd4\u4e00\u6761\u72d7\u6709\u7528\u5f97\u591a\uff0c\u4f55\u51b5\uff0c\u6211\u4e5f\u4e0d\u5fcd\u8ba9\u7ba1\u5bb6\u5a46\u505a\u5be1 \u5987\u3002\u201d \u6d77\u5947\u9614\u5df2\u6c14\u5f97\u8fde\u8bdd\u90fd\u8bf4\u4e0d\u4e86\u3002 \u9646\u5c0f\u51e4\u7ec8\u4e8e\u5df2\u7cfb\u597d\u88e4\u5e26\uff0c\u65bd\u65bd\u7136\u8d70\u4e86\uff0c\u8d70\u51fa\u51e0\u6b65\u53c8\u56de\u5934\uff0c\u5e26\u7740\u7b11\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4f60 \u4e00\u5b9a\u8981\u544a\u8bc9\u8001\u5200\u628a\u5b50\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u89c9\u5f97\u5f88\u6709\u8da3\u7684\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u91cd\u91cd\u7684\u8d4f\u4f60\u4e00\u6837\u4e1c\u897f\u3002\u201d \u4ed6\u7b11\u5f97\u5b9e\u5728\u6709\u70b9\u4e0d\u6000\u597d\u610f\uff0c\u201c\u4f60\u60f3\u4e0d\u60f3\u5f97\u51fa\u4ed6\u4f1a\u8d4f\u4f60\u6837\u4ec0\u4e48\u4e1c\u897f\u5462\uff1f\u201d \u6d77\u5947\u9614\u5df2\u60f3\u5230\u4e86\u3002 \u4e0d\u7ba1\u90a3\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u90fd\u4e00\u5b9a\u662f\u5f88\u91cd\u7684\uff0c\u5374\u4e0d\u77e5\u662f\u91cd\u91cd\u7684\u4e00\u62f3\uff1f\u8fd8\u662f\u91cd\u91cd\u7684\u4e00\u5200\u3002 \u6d77\u5947\u9614\u5ffd\u7136\u5927\u7b11\uff0c\u9053\uff1a\u201c\u6211\u603b\u7b97\u60f3\u901a\u4e86\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u60f3\u901a\u4e86\u4ec0\u4e48\uff1f\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u6211\u6740\u7684\u65e2\u7136\u662f\u6761\u72d7\uff0c\u6b7b\u7684\u5f53\u7136\u4e5f\u662f\u6761\u72d7\uff0c\u4e0d\u7ba1\u90a3\u662f\u6761\u4ec0\u4e48\u6837\u7684\u72d7\u90fd \u2014\u6837\uff0c\u53cd\u6b63\u90fd\u5df2\u662f\u6761\u6b7b\u72d7\u3002\u201d \u4ed6\u7728\u4e86\u7728\u773c\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u8fde\u4eba\u6b7b\u4e86\u90dd\u662f\u2014\u6837\u7684\uff0c\u4f55\u51b5\u72d7\u3002\u201d \u9646\u5c0f\u51e4\u4e5f\u5927\u7b11\uff0c\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4e2a\u4eba\u597d\u50cf\u771f\u7684\u662f\u60f3\u901a\u4e86\u3002\u201d \u56db\u6708\u521d\u516b\uff0c\u6674\u65f6\u591a\u4e91\u5076\u9635\u96e8\u3002 \u4eca\u5929\u7ba1\u5bb6\u5a46\u7c3f\u5b50\u4e0a\u7684\u8bb0\u8f7d\u5f88\u7b80\u5355\uff0c\u201c\u8d76\u8def\u56db\u767e\u91cc\uff0c\u72d7\u66b4\u6bd9\u3002\u201d \u56db\u6708\u521d\u4e5d\uff0c\u9634\u3002 \u6ca1\u6709\u96e8\uff0c\u53ea\u6709\u9634\u4e91\uff0c\u4e00\u5c42\u5c42\u539a\u539a\u7684\u9634\u4e91\u63a9\u4f4f\u4e86\u65e5\u8272\uff0c\u5929\u5c31\u9ed1\u5f97\u7279\u522b\u65e9\u3002 \u201c\u6211\u4eec\u600e\u4e48\u4f1a\u8d70\u5230\u8fd9\u91cc\u6765\u4e86\uff1f\u201d \u201c\u56e0\u4e3a\u8d76\u8f66\u7684\u6015\u9519\u8fc7\u5bbf\u5934\uff0c\u6240\u4ee5\u8981\u6284\u8fd1\u8def\u3002\u201d \u201c\u8fd9\u6761\u662f\u8fd1\u8def\uff1f\u201d \u201c\u672c\u6765\u5e94\u8be5\u662f\u7684\uff0c\u53ef\u662f\u73b0\u5728\u2026\u2026\u7ba1\u5bb6\u5a46\u53f9\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u73b0\u5728\u770b\u6765\u5374\u597d\u50cf\u662f \u8ff7\u4e86\u8def\u3002\u201d \u73b0\u5728\u672c\u6765\u5df2\u5230\u4e86\u5e94\u8be5\u5403\u996d\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u672c\u6765\u5df2\u5e94\u8be5\u6d17\u8fc7\u8138\uff0c\u6f31\u8fc7\u53e3\uff0c\u6362\u4e0a\u4e86\u5e72\u51c0 \u8212\u670d\u7684\u8863\u88f3\uff0c\u5750\u5728\u706f\u5149\u8f89\u714c\u7684\u996d\u9986\u91cc\u5403\u6b63\u83dc\u524d\u7684\u51b7\u76d8\u3002 \u53ef\u662f\u73b0\u5728\u4ed6\u4eec\u5374\u5728\u4e00\u4e2a\u5b8c\u5168\u964c\u751f\u7684\u5730\u65b9\u8ff7\u4e86\u8def\u3002 \u201c\u6211\u997f\u4e86\uff0c\u997f\u5f97\u8981\u547d\uff0c\u201c\u67f3\u9752\u9752\u663e\u7136\u5e76\u4e0d\u662f\u4e2a\u80fd\u5403\u82e6\u7684\u5973\u4eba\uff0c\u201c\u6211\u4e00\u5b9a\u8981\u5403\u70b9\u4e1c \u897f\uff0c\u6211\u7684\u80c3\u4e0d\u597d\u3002\u201d \u201c\u5047\u5982\u4f60\u771f\u7684\u2014\u5b9a\u8981\u5403\u70b9\u4e1c\u897f\uff0c\u5c31\u53ea\u6709\u50cf\u7f8a\u4e00\u6837\u5403\u8349\u3002\u201d \u67f3\u9752\u9752\u76b1\u8d77\u4e86\u7709\uff0c\u201c\u8f66\u4e0a\u96be\u9053\u8fde\u4e00\u70b9\u5403\u7684\u90fd\u6ca1\u6709\uff1f\u2019\u2019\u201c\u975e\u4f46\u6ca1\u6709\u5403\u7684\uff0c\u8fde\u6c34\u90fd\u6ca1 \u6709\u3002\u201d \u201c\u90a3\u6211\u4eec\u600e\u4e48\u529e\uff1f\u201d \u201c\u53ea\u6709\u4e00\u4e2a\u529e\u6cd5\u3002\u201d \u201c\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d \u201c\u997f\u7740\u3002\u201d \u67f3\u9752\u9752\u5ffd\u7136\u63a8\u5f00\u95e8\uff0c\u8df3\u4e0b\u8f66\uff0c\u201c\u6211\u5c31\u4e0d\u4fe1\u6ca1\u6709\u522b\u7684\u529e\u6cd5\uff0c\u6211\u53bb\u627e\u3002\u201d \u201c\u627e\u4ec0\u4e48\uff1f\u201d \u201c\u65e0\u8bba\u4ec0\u4e48\u6837\u7684\u5730\u65b9\u90fd\u6709\u4eba\u4f0e\u7684\uff0c\u8fd9\u9644\u8fd1\u4e00\u5b9a\u4e5f\u6709\u4eba\u5bb6\uff0c\u201c\u67f3\u9752\u9752\u8bf4\u5f97\u597d\u50cf\u5f88\u6709 \u628a\u63e1\uff0c\u5176\u5b9e\u5fc3\u91cc\u8fde\u4e00\u70b9\u628a\u63e1\u90fd\u6ca1\u6709\u3002 \u53ef\u662f\u5979\u80af\u53bb\u627e\u3002 \u56e0\u4e3a\u5979\u4e0d\u80fd\u5403\u82e6\uff0c\u4e0d\u80fd\u6328\u997f\u3002 \u65e0\u8bba\u4f60\u8981\u627e\u7684\u662f\u4ec0\u4e48\uff0c\u53ea\u6709\u80af\u53bb\u627e\u7684\u4eba\uff0c\u624d\u4f1a\u627e\u5f97\u5230\u3002 \u4e16\u4e0a\u672c\u5c31\u6709\u5f88\u591a\u4e8b\u90fd\u662f\u8fd9\u6837\u5b50\u7684\u2014\u7b2c\u4e00\u4e2a\u53d1\u660e\u8f66\u8f86\u7684\u4eba\uff0c\u4e00\u5b9a\u662f\u61d2\u5f97\u8d70\u8def\u7684\u4eba\uff0c \u5c31\u56e0\u4e3a\u4eba\u4eec\u4e0d\u613f\u5403\u82e6\uff0c\u6240\u4ee5\u4eba\u7c7b\u7684\u751f\u6d3b\u624d\u4f1a\u8fdb\u6b65\u3002 \u5979\u80af\u53bb\u627e\uff0c\u6240\u4ee5\u5979\u627e\u5230\u4e86\u3002 \u5c71\u5773\u540e\u7684\u5c71\u5761\u4e0b\uff0c\u5c45\u7136\u771f\u7684\u6709\u6237\u4eba\u5bb6\uff0c\u800c\u4e14\u662f\u5f88\u5927\u7684\u4e00\u6237\u4eba\u5bb6\u3002 \u4e8b\u5b9e\u4e0a\uff0c\u4f60\u65e0\u8bba\u5728\u4efb\u4f55\u5730\u65b9\u90fd\u5f88\u96be\u627e\u5230\u8fd9\u4e48\u5927\u4e00\u6237\u4eba\u5bb6\u3002 \u5728\u9ed1\u6697\u4e2d\u770b\u6765\uff0c\u5c71\u5761\u4e0a\u7684\u5c4b\u9876\u5c31\u50cf\u662f\u9634\u4e91\u822c\u4e00\u5c42\u5c42\u5806\u79ef\u7740\uff0c\u5bbd\u9614\u7684\u5927\u95e8\uff0c\u6700\u5c11\u53ef \u4ee5\u5bb9\u516d\u5339\u9a6c\u5e76\u9a70\u800c\u5165\u3002 \u53ef\u662f\u95e8\u4e0a\u7684\u6731\u6f06\u5df2\u5265\u843d\uff0c\u95e8\u4e5f\u662f\u7d27\u95ed\u7740\u7684\uff0c\u6700\u5947\u602a\u7684\u662f\uff0c\u8fd9\u4e48\u5927\u4e00\u6237\u4eba\u5bb6\uff0c\u7adf\u51e0 \u4e4e\u65e0\u5168\u770b\u4e0d\u89c1\u706f\u706b\u3002 \u636e\u8bf4\u5728\u4e00\u4e9b\u65e0\u4eba\u7684\u8352\u91ce\u4e2d\uff0c\u7ecf\u5e38\u4f1a\u6709\u9b3c\u5c4b\u51fa\u73b0\u7684\uff0c\u8fd9\u5730\u65b9\u96be\u9053\u5c31\u662f\u680b\u9b3c\u5c4b\uff1f \u201c\u5c31\u7b97\u771f\u7684\u662f\u9b3c\u5c4b\uff0c\u6211\u4e5f\u8981\u8fdb\u53bb\u770b\u770b\uff0c\u201c\u67f3\u9752\u9752\u53ea\u6015\u6328\u997f\uff0c\u4e0d\u6015\u9b3c\u3002 \u5979\u5df2\u7ecf\u5728\u6572\u95e8\uff0c\u5c06\u95e8\u4e0a\u7684\u94dc\u73af\u6572\u5f97\u6bd4\u6572\u9523\u8fd8\u54cd\uff0c\u95e8\u91cc\u5c45\u7136\u8fd8\u662f\u5b8c\u5168\u6ca1\u6709\u56de\u5e94\u3002 \u5979\u6b63\u51c6\u5907\u653e\u5f03\u7684\u65f6\u5019\uff0c\u95e8\u5374\u5ffd\u7136\u5f00\u4e86\uff0c\u5f00\u4e86\u4e00\u7ebf\uff0c\u4e00\u7ebf\u706f\u5149\u7167\u51fa\u6765\uff0c\u4e00\u4e2a\u4eba\u7ad9\u5728 \u706f\u5149\u540e\u7684\u9ed1\u6697\u4e2d\uff0c\u51b7\u51b7\u7684\u770b\u7740\u5979\u3002 \u9634\u68ee\u68ee\u7684\u706f\u5149\uff0c\u7167\u82b1\u4e86\u5979\u7684\u773c\u775b\uff0c\u7b49\u5230\u5979\u770b\u6e05\u8fd9\u4e2a\u4eba\u65f6\uff0c\u5c31\u518d\u4e5f\u4e0d\u6562\u770b\u7b2c\u4e8c\u773c\u3002 \u8fd9\u4e2a\u4eba\u5b9e\u5728\u4e0d\u50cf\u4e00\u4e2a\u4eba\uff0c\u5374\u4e5f\u4e0d\u50cf\u9b3c\uff0c\u82e5\u8bf4\u4ed6\u662f\u4eba\uff0c\u2014\u5b9a\u662f\u4e2a\u6ce5\u4eba\uff0c\u82e5\u8bf4\u4ed6\u662f\u4e2a \u9b3c\uff0c\u4e5f\u53ea\u80fd\u7b97\u662f\u4e2a\u7528\u6ce5\u5851\u6210\u7684\u9b3c\u3002 \u4ed6\u5168\u8eab\u4e0a\u4e0b\u90fd\u662f\u6ce5\uff0c\u8138\u4e0a\u3001\u9f3b\u5b50\u4e0a\u3001\u7709\u6bdb\u4e0a\uff0c\u751a\u81f3\u8fde\u5634\u91cc\u90fd\u597d\u50cf\u88ab\u6ce5\u585e\u4f4f\u3002 \u5e78\u597d\u4ed6\u8fd8\u4f1a\u7b11\u3002 \u770b\u89c1\u67f3\u9752\u9752\u8138\u4e0a\u7684\u8868\u60c5\uff0c\u4ed6\u5c31\u5ffd\u7136\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u7b11\u5f97\u8138\u4e0a\u7684\u5e72\u6ce5\u201c\u8e6c\u843d\u8e6c\u843d\u201d\u5f80 \u4e0b\u76f4\u6389\u3002 \u65e0\u8bba\u662f\u4eba\u662f\u9b3c\uff0c\u53ea\u8981\u8fd8\u4f1a\u7b11\uff0c\u770b\u6765\u5c31\u6bd4\u8f83\u6ca1\u6709\u90a3\u4e48\u53ef\u6015\u67f3\u9752\u9752\u7ec8\u4e8e\u58ee\u8d77\u80c6\u5b50\uff0c\u52c9 \u5f3a\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8ff7\u5382\u8def\u2026\u2026\u201d \u5979\u53ea\u8bf4\u4e86\u4e00\u53e5\uff0c\u8fd9\u4eba\u5c31\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff0c\u201c\u6211\u77e5\u9053\u4f60\u4eec\u8ff7\u4e86\u8def\uff0c\u82e5\u4e0d\u662f\u8ff7\u4e86\u8def\u7684 \u4eba\uff0c\u600e\u4e48\u4f1a\u8dd1\u5230\u8fd9\u9b3c\u5730\u65b9\u6765\u3002\u201d \u4ed6\u7b11\u5f97\u66f4\u6109\u5feb\uff0c\u201c\u53ef\u662f\u8001\u592a\u592a\u4f60\u7528\u4e0d\u7740\u5bb3\u6015\uff0c\u8fd9\u91cc\u867d\u7136\u662f\u4e2a\u9b3c\u5730\u65b9\uff0c\u6211\u5374\u4e0d\u662f \u9b3c\uff0c\u6211\u4e0d\u4f46\u662f\u4e2a\u4eba\uff0c\u800c\u4e14\u8fd8\u662f\u4e2a\u597d\u4eba\u3002\u201d \u67f3\u9752\u9752\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u597d\u4eba\u8eab\u4e0a\u600e\u4e48\u4f1a\u6709\u8fd9\u4e48\u591a\u6ce5\uff1f\u201d \u8fd9\u4eba\u9053\uff1a\u201c\u65e0\u8bba\u8c01\u6316\u4e86\u597d\u51e0\u5929\u86af\u8693\uff0c\u8eab\u4e0a\u90fd\u4f1a\u6709\u8fd9\u4e48\u591a\u6ce5\u7684\u3002\u201d \u67f3\u9752\u9752\u6014\u4e86\u6014\uff0c\u201c\u4f60\u5728\u6316\u86af\u8693\uff1f\u201d \u8fd9\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u6316\u4e86\u4e03\u767e\u516b\u5341\u4e09\u6761\u5927\u86af\u8693\u3002\u201d \u67f3\u9752\u9752\u66f4\u5403\u60ca\uff0c\u201c\u6316\u8fd9\u4e48\u591a\u86af\u8693\u5e72\u4ec0\u4e48\uff1f\u201d \u8fd9\u4eba\u9053\uff1a\u201c\u8fd9\u4e48\u591a\u8fd8\u4e0d\u591f\uff0c\u6211\u8fd8\u5f97\u518d\u6316\u4e03\u767e\u4e00\u4e03\u2014\u4e03\u6761\u624d\u591f\u6570\u3002 \u67f3\u9752\u9752\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u8fd9\u4eba\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u8ddf\u522b\u4eba\u6253\u8d4c\uff0c\u8c01\u8f93\u8c01\u5c31\u5f97\u6316\u4e00\u5343\u4e94\u767e\u6761\u86af\u8693\uff0c\u5c11\u4e00\u6761\u90fd\u4e0d \u884c\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u4f60\u8f93\u4e86\uff1f\u201d \u8fd9\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u73b0\u5728\u867d\u7136\u8fd8\u6ca1\u6709\u8f93\uff0c\u53ef\u662f\u6211\u81ea\u5df1\u77e5\u9053\u5df2\u7ecf\u8f93\u5b9a\u4e86\u3002\u201d \u67f3\u9752\u9752\u770b\u7740\u4ed6\uff0c\u773c\u775b\u5df1\u770b\u5f97\u53d1\u76f4\uff0c\u201c\u7528\u8fd9\u79cd\u6cd5\u5b50\u6765\u6253\u8d4c\u5012\u771f\u7279\u522b\uff0c\u8ddf\u4f60\u6253\u8d4c\u7684\u90a3 \u4e2a\u4eba\uff0c\u4e00\u5b9a\u662f\u4e2a\u602a\u4eba\u3002\u201d \u8fd9\u4eba\u9053\uff1a\u201c\u4e0d\u4f46\u662f\u4e2a\u602a\u4eba\uff0c\u800c\u4e14\u662f\u4e2a\u6df7\u86cb\uff0c\u4e0d\u4f46\u662f\u4e2a\u6df7\u86cb\uff0c\u800c\u4e14\u662f\u4e2a\u5927\u6df7\u86cb\u3002\u201d \u9646\u5c0f\u51e4\u4e00\u76f4\u8fdc\u8fdc\u7684\u7ad9\u7740\uff0c\u5ffd\u7136\u62a2\u7740\u9053\uff1a\u201c\u4e0d\u4f46\u662f\u4e2a\u5927\u6df7\u86cb\uff0c\u800c\u4e14\u662f\u7279\u522b\u5927\u7684\u4e00 \u4e2a\u3002\u201d \u8fd9\u4eba\u7acb\u523b\u540c\u610f\uff0c\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u82e5\u662f\u6df7\u86cb\uff0c\u4f60\u5462\uff1f\u201d \u8fd9\u4eba\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u597d\u50cf\u4e5f\u662f\u7684\u3002\u201d \u9646\u5c0f\u51e4\u8fd8\u60f3\u518d\u8bf4\u4ec0\u4e48\uff0c\u67f3\u9752\u9752\u5374\u5df2\u62a2\u7740\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u6df7\u86cb\uff0c\u4f60\u662f\u4e2a\u597d\u4eba\uff0c\u6211\u77e5\u9053 \u4f60\u2014\u5b9a\u80af\u8ba9\u6211\u4eec\u5728\u8fd9\u91cc\u501f\u5bbf\u4e00\u5bb5\u7684\u3002\u201d \u8fd9\u4eba\u9053\uff1a\u201c\u4f60\u60f3\u5728\u8fd9\u5730\u65b9\u4f50\u4e00\u665a\u4e0a\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u55ef\u3002\u201d \u8fd9\u4eba\u9053\uff1a\u201c\u4f60\u771f\u7684\u60f3\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u5f53\u7136\u662f\u771f\u7684\u3002\u201d \u8fd9\u4eba\u5403\u60ca\u7684\u770b\u7740\u5979\uff0c\u5c31\u597d\u50cf\u6bd4\u770b\u89c1\u4e00\u4e2a\u4eba\u5728\u70c2\u6ce5\u91cc\u6316\u86af\u8693\u8fd8\u5403\u60ca\u3002 \u67f3\u9752\u9752\u4e0d\u4f4f\u9053\uff1a\u201c\u6211\u4eec\u8ff7\u4e86\u8def\uff0c\u9644\u8fd1\u53c8\u6ca1\u6709\u522b\u7684\u4eba\u5bb6\uff0c\u6240\u4ee5\u6211\u4eec\u53ea\u6709\u4f4f\u5728\u8fd9\u91cc\uff0c \u8fd9\u96be\u9053\u662f\u4ef6\u5f88\u5947\u602a\u7684\u4e8b\uff1f\u201d \u8fd9\u5165\u70b9\u70b9\u5934\uff0c\u53c8\u6447\u6447\u5934\uff0c\u54fa\u54fa\u9053\uff1a\u201c\u4e0d\u5947\u602a\uff0c\u4e00\u70b9\u4e5f\u4e0d\u5947\u602a\u3002\u201d \u4ed6\u5634\u91cc\u867d\u7136\u5728\u8bf4\u4e0d\u5947\u602a\uff0c\u81ea\u5df1\u8138\u4e0a\u7684\u8868\u60c5\u5374\u5947\u602a\u5f97\u5f88\u3002 \u67f3\u9752\u9752\u53c8\u5fcd\u4e0d\u4f4f\u95ee\uff0c\u201c\u8fd9\u5730\u65b9\u96be\u9053\u6709\u9b3c\uff1f\u201d \u8fd9\u4eba\u9053\uff1a\u201c\u6ca1\u6709\uff0c\u4e00\u4e2a\u4e5f\u6ca1\u6709\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u80af\u4e0d\u80af\u8ba9\u6211\u4eec\u5728\u8fd9\u91cc\u4f4f\u4e0a\u4e00\u665a\u4e0a\uff1f\u201d \u8fd9\u4eba\u53c8\u7b11\u4e86\uff0c\u201c\u53ea\u8981\u4f60\u4eec\u771f\u7684\u613f\u610f\uff0c\u968f\u4fbf\u8981\u5728\u8fd9\u91cc\u4f4f\u591a\u4e45\u90fd\u6ca1\u6709\u5173\u7cfb\u3002\u201d \u4ed6\u8f6c\u8fc7\u8eab\uff0c\u8d70\u5165\u4e86\u8352\u51c9\u9634\u68ee\u7684\u5ead\u9662\uff0c\u5634\u91cc\u5583\u5583\u81ea\u8bed\uff0c\u4eff\u4f5b\u5728\u8bf4\uff0c\u201c\u6015\u53ea\u6015\u4f60\u4eec\u8fde \u534a\u4e2a\u65f6\u8fb0\u90fd\u803d\u4e0d\u4e0b\u53bb\uff0c\u56e0\u4e3a\u4ece\u6765\u4e5f\u6ca1\u6709\u4eba\u80fd\u5728\u8fd9\u91cc\u803d\u5f97\u4e0b\u53bb\u3002\u201d \u524d\u9762\u7684\u4e00\u91cd\u9662\u843d\u6709\u4e03\u95f4\u5c4b\u5b50\uff0c\u6bcf\u95f4\u5c4b\u5b50\u91cc\u90fd\u6709\u597d\u51e0\u76cf\u706f\u3002 \u706f\u91cc\u5c45\u7136\u8fd8\u6709\u6cb9\u3002 \u8fd9\u4e2a\u4eba\u5c45\u7136\u5c06\u6bcf\u95f4\u5c4b\u5b50\u91cc\u7684\u6bcf\u76cf\u706f\u90fd\u70b9\u4eae\u4e86\uff0c\u7136\u540e\u624d\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff1a\u201c\u65e0\u8bba\u4ec0\u4e48 \u6837\u7684\u9b3c\u5730\u65b9\uff0c\u53ea\u8981\u4e00\u70b9\u8d77\u706f\uff0c\u770b\u6765\u597d\u50cf\u7acb\u523b\u5c31\u4f1a\u53d8\u5f97\u597d\u591a\u4e86\u3002\u201d \u5176\u5b9e\u8fd9\u5730\u65b9\u672c\u6765\u5c31\u4e0d\u592a\u574f\uff0c\u867d\u7136\u5230\u5904\u90fd\u79ef\u7740\u539a\u539a\u7684\u4e00\u5c42\u7070\uff0c\u53ef\u662f\u534e\u4e3d\u6602\u8d35\u7684\u88c5\u78fa \u548c\u5bb6\u5177\u5e76\u6ca1\u6709\u7834\u70c2\uff0c\u4f9d\u7a00\u8fd8\u53ef\u4ee5\u60f3\u89c1\u5230\u5f53\u5e74\u7684\u98ce\u91c7\u3002 \u67f3\u9752\u9752\u8bd5\u63a2\u7740\u95ee\u9053\uff1a\u201c\u4f60\u521a\u624d\u662f\u4e0d\u662f\u5728\u8bf4\uff0c\u4ece\u6765\u4e5f\u6ca1\u6709\u4eba\u80fd\u5728\u8fd9\u91cc\u803d\u5f97\u4e0b\u53bb\uff1f\u201d \u8fd9\u4e2a\u4eba\u627f\u8ba4\u3002 \u67f3\u9752\u9752\u5f53\u7136\u8981\u95ee\uff0c\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u8fd9\u4eba\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u91cc\u6709\u6837\u4e1c\u897f\u4ece\u6765\u4e5f\u6ca1\u6709\u4eba\u80fd\u53d7\u5f97\u4e86\u3002\u201d \u67f3\u9752\u9752\u95ee\uff0c\u201c\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u5728\u54ea\u91cc\uff1f\u201d \u8fd9\u4eba\u968f\u624b\u4e00\u6307\uff0c\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u91ccao\u4ed6\u6307\u7740\u7684\u662f\u4e2a\u6c34\u6676\u76d2\u5b50\uff0c\u5c31\u6446\u5728\u5927\u5385\u6b63\u4e2d\u7684\u795e \u6848\u4e0a\u3002 \u78e8\u5f97\u975e\u5e38\u8584\u7684\u6c34\u6676\uff0c\u51e0\u4e4e\u5b8c\u5168\u662f\u900f\u660e\u7684\uff0c\u91cc\u9762\u6446\u7740\u7684\u4eff\u4f5b\u662f\u4e00\u74e3\u5df2\u67af\u840e\u4e86\u7684\u82b1 \u74e3\u3002 \u201c\u8fd9\u662f\u4ec0\u4e48\u82b1\uff1f\u201d \u201c\u8fd9\u4e0d\u662f\u82b1\uff0c\u4e5f\u4e0d\u662f\u4f60\u6240\u80fd\u60f3\u5f97\u5230\u7684\u4efb\u4f55\u4e1c\u897f\u3002\u201d \u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d \u201c\u8fd9\u662f\u4e00\u4e2a\u4eba\u7684\u773c\u775baU\u67f3\u9752\u9752\u7684\u773c\u775b\u5f20\u5927\u4e86\uff0c\u77b3\u5b54\u5374\u5728\u6536\u7f29\uff0c\u60c5\u4e0d\u81ea\u7981\u9000\u7f29\u4e86\u4e24 \u6b65\u3002 \u201c\u4ec0\u4e48\u4eba\u7684\u773c\u775b\uff1f\u201d \u201c\u4e00\u4e2a\u5973\u4eba\uff0c\u4e00\u4e2a\u5f88\u6709\u540d\u7684\u5973\u4eba\uff0c\u8fd9\u4e2a\u4ea5\u4eba\u6700\u6709\u540d\u7684\u5730\u65b9\uff0c\u5c31\u662f\u5979\u7684\u773c\u775b\u3002\u201d \u201c\u4e3a\u4ec0\u4e48\u6709\u540d\uff1f\u201d \u2018\u2018\u56e0\u4e3a\u5979\u662f\u795e\u773c\uff0c\u636e\u8bf4\u5979\u4e0d\u4f46\u80fd\u5728\u9ed1\u6697\u4e2d\u7ee3\u82b1\uff0c\u800c\u4e14\u8fd8\u80fd\u5728\u4e09\u5341\u6b65\u5916\u7528\u7ee3\u82b1\u9488 \u6253\u7a7f\u4e00\u53ea\u868a\u5b50\u7684\u5934ao\u201c\u4f60\u8bf4\u7684\u662f\u795e\u773c\u6c88\u4e09\u5a18\uff1f\u201d \u201c\u9664\u4e86\u5979\u8fd8\u6709\u8c01\uff1f\u201d \u201c\u662f\u8c01\u628a\u5979\u7684\u773c\u775b\u6446\u5728\u8fd9\u91cc\u7684\uff1f \u201c\u9664\u4e86\u5979\u7684\u6587\u592b\u8fd8\u6709\u8c01\uff1f\u201d \u201c\u5979\u7684\u4e08\u592b\u662f\u4e0d\u662f\u90a3\u4e2a\u2018\u7389\u6811\u5251\u5ba2\u2019\u53f6\u51cc\u98ce\uff1f\u201d \u201c\u662f\u7684\uff0c\u6c5f\u6e56\u4e2d\u4e5f\u53ea\u6709\u8fd9\u4e48\u6837\u4e00\u4e2a\u53f6\u51cc\u98ce\uff0c\u5e78\u597d\u53ea\u6709\u4e00\u4eba\u4f53\uff0c\uff0c\u67f3\u9752\u9752\u636e\u7d27\u4e86\u53cc \u624b\uff0c\u624b\u5fc3\u5df2\u6e7f\u4e86\u3002 \u5979\u662f\u4e0d\u662f\u4e5f\u77e5\u9053\u53f6\u51cc\u98ce\u548c\u8001\u5200\u628a\u5b50\u95f4\u7684\u6069\u6028\u7ea0\u7f20\uff1f\u4ed6\u4eec\u88ab\u5e26\u5230\u8fd9\u91cc\u6765\uff0c\u662f\u65e0\u610f\u95f4\u7684 \u5de7\u5408\uff1f\u8fd8\u662f\u51a5\u51a5\u4e2d\u6709\u4eba\u5728\u6545\u610f\u5b89\u6392\uff1f \u6316\u86af\u8693\u7684\u4eba\u4e00\u5f20\u8138\u5b8c\u5168\u88ab\u6ce5\u76d6\u7740\uff0c\u8c01\u4e5f\u770b\u4e0d\u51fa\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\u3002 \u53ef\u662f\u4ed6\u7684\u58f0\u97f3\u5df2\u6709\u4e9b\u5636\u54d1\uff0c\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u91cc\u4e00\u5171\u6709\u4e5d\u5341\u4e8c\u95f4\u5c4b\u5b50\uff0c\u6bcf\u95f4\u5c4b\u5b50\u91cc\u90fd \u6709\u8fd9\u4e48\u6837\u4e00\u4e2a\u6c34\u6676\u76d2\u5b50\u3002\u201d \u6bcf\u95f4\u5c4b\u5b50\u91cc\u90fd\u6709\uff1f \u67f3\u9752\u9752\u7acb\u523b\u51b2\u8fdb\u4e86\u7b2c\u4e8c\u95f4\u5c4b\u5b50\uff0c\u679c\u7136\u53c8\u770b\u89c1\u4e86\u4e00\u4e2a\u5b8c\u5168\u540c\u6837\u7684\u6c34\u6676\u76d2\u3002 \u76d2\u5b50\u91cc\u6446\u7740\u7684\uff0c\u8d6b\u7136\u7adf\u662f\u53ea\u5e72\u67af\u4e86\u7684\u8033\u6735\u3002 \u6316\u86af\u8693\u7684\u4eba\u5e7d\u7075\u822c\u8ddf\u5728\u5979\u8eab\u540e\uff0c\u201c\u6c88\u4e09\u5a18\u6b7b\u4e86\u540e\uff0c\u53f6\u51cc\u98ce\u5c31\u5c06\u5979\u5206\u6210\u4e86\u4e5d\u5341\u4e09 \u5757\u2026\u2026\u2026 \u67f3\u9752\u9752\u5fcd\u4e0d\u4f4f\u53eb\u4e86\u8d77\u6765\uff0c\u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8fd9\u4e48\u505a\uff1f\u201d \u6316\u86af\u8693\u7684\u4eba\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u592a\u7231\u5979\uff0c\u65f6\u65f6\u523b\u523b\u90fd\u60f3\u770b\u5230\u5979\uff0c\u65e0\u8bba\u8d70\u5230\u54ea \u91cc\u90fd\u60f3\u770b\u5230\u5979\uff0c\u90a3\u6015\u53ea\u80fd\u770b\u89c1\u4e00\u53ea\u773c\u775b\uff0c\u4e00\u4e2a\u8033\u6735\u4e5f\u597d\u3002\u201d \u67f3\u9752\u9752\u54ac\u7d27\u7259\uff0c\u51e0\u4e4e\u5df2\u5fcd\u4e0d\u4f4f\u8981\u5455\u5410\u3002 \u9646\u5c0f\u51e4\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u636e\u8bf4\u6c88\u4e8c\u5a18\u7684\u8868\u54e5\u5c31\u662f\u6b66\u5f53\u7684\u540d\u5251\u5ba2\u6728\u9053\u4eba\u3002\u201d \u6316\u86af\u8693\u7684\u4eba\u70b9\u70b9\u5934\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u636e\u8bf4\u4ed6\u4eec\u6210\u4eb2\uff0c\u5c31\u662f\u6728\u9053\u4eba\u505a\u7684\u5927\u5a92\u3002\u201d \u6316\u86af\u8693\u7684\u4eba\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53f6\u51cc\u98ce\u8fd9\u4e48\u6837\u505a\uff0c\u96be\u9053\u4e0d\u6015\u6728\u9053\u4eba\u5bf9\u4ed8\u4ed6\uff1f\u201d \u6316\u89c4\u5219\u7684\u4eba\u9053\uff1a\u201c\u6728\u9053\u4eba\u60f3\u5bf9\u4ed8\u4ed6\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u592a\u8fdf\u4e86\uff0c\u6c88\u4e09\u5a18\u6b7b\u4e86\u8fd8\u4e0d\u5230\u4e09\u4e2a \u6708\uff0c\u4ed6\u4e5f\u53d1\u4e86\u75af\uff0c\u81ea\u5df1\u4e00\u5934\u649e\u6b7b\u5728\u540e\u9762\u7684\u5047\u5c71\u4e0a\uff0c\u8111\u888b\u649e\u5f97\u7a00\u70c2\u3002\u201d \u4e00\u4e2a\u4eba\u82e5\u662f\u8fde\u8111\u888b\u90fd\u649e\u5f97\u7a00\u70c2\uff0c\u5f53\u7136\u5c31\u6ca1\u6709\u4eba\u80fd\u8ba4\u5f97\u51fa\u4ed6\u7684\u672c\u6765\u9762\u76ee\uff0c\u4e5f\u5c31\u6ca1\u6709 \u4eba\u80fd\u8bc1\u660e\u6b7b\u7684\u90a3\u4e2a\u4eba\u7a76\u7adf\u662f\u8c01\u4e86\u3002 \u67f3\u9752\u9752\u603b\u7b97\u5df2\u5598\u8fc7\u6c14\u6765\uff0c\u7acb\u523b\u95ee\u9053\uff1a\u201c\u4ed6\u6b7b\u4e86\u4e4b\u540e\uff0c\u522b\u4eba\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u628a\u8fd9\u4e9b\u76d2\u5b50 \u642c\u8d70\uff1f\u201d \u6316\u86af\u8693\u7684\u4eba\u9053\uff1a\u201c\u56e0\u4e3a\u60f3\u642c\u8fd9\u4e9b\u76d2\u5b50\u7684\u4eba\uff0c\u73b0\u5728\u90fd\u5df2\u7ecf\u8eba\u5728\u76d2\u5b50\u91cc\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u7684\u76d2\u5b50\uff1f\u201d \u6316\u86af\u8693\u7684\u4eba\u9053\uff1a\u201c\u4e00\u79cd\u957f\u957f\u7684\uff0c\u7528\u6728\u5934\u505a\u7684\uff0c\u4e13\u95e8\u88c5\u6b7b\u4eba\u7684\u76d2\u5b50\uff0c\u5927\u591a\u6570\u4eba\u6b7b\u4e86 \u540e\uff0c\u90fd\u8981\u88ab\u88c5\u5728\u8fd9\u79cd\u76d2\u5b50\u91cc\u3002\u201d \u67f3\u9752\u9752\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u81f3\u5c11\u603b\u6bd4\u88ab\u88c5\u5728\u8fd9\u79cd\u6c34\u6676\u76d2\u5b50\u91cc\u597d\u5f97\u591aoD\u6316\u86af\u8693\u7684\u4eba \u9053\uff1a\u201c\u53ea\u53ef\u60dc\u4e5f\u597d\u4e0d\u4e86\u592a\u591a\u3002\u201d \u67f3\u9752\u9752\u9053\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u6316\u86af\u8693\u7684\u4eba\u9053\uff1a\u201c\u56e0\u4e3a\u88ab\u4e00\u53cc\u9b3c\u624b\u6d3b\u6d3b\u634f\u6b7b\u7684\u6ecb\u5473\u5e76\u4e0d\u597d\u53d7\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u53ef\u662f\u4f60\u521a\u624d\u8fd8\u8bf4\u8fd9\u5730\u65b9\u8fde\u4e00\u4e2a\u9b3c\u90fd\u6ca1\u6709\u7684\u3002 \u6316\u8d29\u5219\u7684\u4eba\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u662f\u6ca1\u6709\u4e00\u4e2a\u9b3c\uff0c\u8fd9\u5730\u65b9\u81f3\u5c11\u6709\u56db\u5341\u4e5d\u4e2a\u9b3c\uff0c\u800c\u4e14\u90fd\u662f\u51a4 \u6b7b\u9b3c\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u8fd9\u5730\u65b9\u672c\u6765\u4e00\u5171\u6709\u591a\u5c11\u4eba\uff1f\u201d \u6316\u86af\u8693\u7684\u4eba\u9053\uff1a\u201c\u56db\u5341\u4e5d\u4e2a\u4eba\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u73b0\u5728\u8fd9\u4e9b\u4eba\u5df2\u5168\u90fd\u6b7b\u5149\u4e86\u3002\u201d \u6316\u86af\u8693\u7684\u4eba\u9053\uff1a\u201c\u5047\u5982\u6bcf\u5929\u90fd\u6709\u53ea\u773c\u775b\u5728\u6c34\u6676\u5323\u5b50\u91cc\u77aa\u7740\u4f60\uff0c\u4f60\u53d7\u4e0d\u53d7\u4e01\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u6211\u53d7\u4e0d\u4e86\uff0c\u6211\u4e00\u5b9a\u4f1a\u53d1\u75af\u3002\u201d \u6316\u86af\u8693\u7684\u5927\u9053\uff1a\u201c\u4f60\u53d7\u4e0d\u4e86\uff0c\u522b\u4eba\u4e5f\u4e00\u6837\u53d7\u4e0d\u4e86\uff0c\u6240\u4ee5\u6bcf\u4e2a\u4eba\u90fd\u60f3\u628a\u8fd9\u4e9b\u76d2\u5b50\u642c \u8d70\uff0c\u53ef\u662f\u65e0\u8bba\u4ec0\u4e48\u4eba\uff0c\u53ea\u8981\u4e00\u78b0\u8fd9\u4e9b\u76d2\u5b50\uff0c\u820c\u5934\u7acb\u523b\u5c31\u4f1a\u5410\u51fa\u534a\u5c3a\u957f\uff0c\u4e00\u8981\u773c\u7684\u529f\u592b \u5c31\u65ad\u4e86\u6c14\uff0c\u5c31\u50cf\u8fd9\u6837\u5b50oo\u4ed6\u81ea\u5df1\u4e5f\u628a\u820c\u5934\u4f38\u51fa\u6765\uff0c\u4f38\u5f97\u957f\u957f\u7684\uff0c\u4ed6\u8138\u4e0a\u5168\u662f\u9ed1\u6ce5\uff0c\u820c \u5934\u5374\u7ea2\u5982\u9c9c\u8840\uff0c\u53ea\u6709\u88ab\u6d3b\u6d3b\u627c\u6b7b\u7684\u4eba\uff0c\u624d\u4f1a\u53d8\u6210\u8fd9\u6837\u67f3\u9752\u9752\u7acb\u523b\u8f6c\u8fc7\u773c\uff0c\u4e0d\u6562\u518d\u770b\u4ed6 \u4e00\u773c\uff0c\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u6ca1\u6709\u52a8\u8fc7\u8fd9\u4e9b\u76d2\u5b50\uff1f\u201d \u6316\u86af\u8693\u7684\u4eba\u6447\u6447\u5934\uff0c\u53c8\u70b9\u70b9\u5934\uff0c\u4ed6\u820c\u5934\u8fd8\u662f\u4f38\u5f97\u957f\u957f\u7684\uff0c\u6839\u672c\u6ca1\u6cd5\u5b50\u8bf4\u8bdd\u3002 \u67f3\u9752\u9752\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u4eba\u5c82\u975e\u5df2\u6b7b\u5149\u4e86\uff0c\u4f60\u600e\u4e48\u8fd8\u6d3b\u7740\uff1f\u96be\u9053\u4f60\u4e0d\u662f\u4eba\uff1f\u201d \u6316\u86af\u8693\u7684\u4eba\u5ffd\u7136\u4ece\u6000\u91cc\u4f38\u51fa\u624b\uff0c\u5c06\u4e00\u6ee1\u628a\u9ed1\u9edd\u9edd\u7684\u4e1c\u897f\u5f80\u67f3\u9752\u9752\u629b\u4e86\u8fc7\u6765\uff0c\u8fd9\u4e9b \u4e1c\u897f\u7adf\u662f\u6d3b\u7684\uff0c\u53c8\u6e29\u53c8\u8f6f\u53c8\u6ed1\uff0c\u7adf\u662f\u6d3b\u751f\u751f\u7684\u86af\u8693\u3002 \u67f3\u9752\u9752\u60ca\u547c\u4e00\u58f0\uff0c\u51e0\u4e4e\u5413\u5f97\u6655\u4e86\u8fc7\u53bb\u3002 \u5979\u5e76\u4e0d\u662f\u90a3\u79cd\u5f88\u5bb9\u6613\u88ab\u5413\u6655\u7684\u5973\u4eba\uff0c\u53ef\u662f\u8fd9\u4e9b\u53c8\u6e7f\u53c8\u8f6f\u53c8\u6ed1\u7684\u86af\u8693\uff0c\u6709\u8c01\u80fd\u53d7\u5f97 \u4e86\u3002 \u7b49\u5979\u8eb2\u8fc7\u4e86\u8fd9\u4e9b\u86af\u8693\uff0c\u6316\u86af\u8693\u7684\u4eba\u7ade\u5df2\u4e0d\u89c1\u4e86\uff0c\u706f\u5149\u95ea\u4e86\u4e24\u95ea\uff0c\u5c4b\u5b50\u91cc\u7684\u706f\u4e5f\u5ffd \u7136\u7184\u706d\u3002 \u5979\u56de\u8fc7\u5934\uff0c\u9646\u5c0f\u51e4\u4ed6\u4eec\u5c45\u7136\u5168\u90fd\u4e0d\u5728\u8fd9\u5c4b\u5b50\u91cc\u3002 \u5e78\u597d\u9694\u58c1\u4e00\u95f4\u5c4b\u5b50\u91cc\u8fd8\u6709\u706f\uff0c\u5979\u51b2\u8fc7\u53bb\uff0c\u8fd9\u5c4b\u91cc\u7684\u706f\u4e5f\u706d\u4e86\u3002 \u518d\u524d\u9762\u7684\u4e00\u95f4\u5c4b\u91cc\u867d\u7136\u8fd8\u6709\u706f\uff0c\u53ef\u662f\u7b49\u5979\u51b2\u8fc7\u53bb\u65f6\uff0c\u706f\u5149\u4e5f\u7184\u706d\u3002 \u8fd9\u4e03\u95f4\u706f\u706b\u660e\u4eae\u7684\u5c4b\u5b50\uff0c\u5ffd\u7136\u4e4b\u95f4\uff0c\u5c31\u5df2\u53d8\u5f97\u4e00\u7247\u9ed1\u6697\u3002 \u5ffd\u7136\u4e4b\u95f4\uff0c\u5979\u4ec0\u4e48\u90fd\u5df2\u770b\u4e0d\u89c1\uff0c\u8fde\u81ea\u5df1\u4f38\u51fa\u53bb\u7684\u624b\u90fd\u5df2\u770b\u4e0d\u89c1\u3002 \u2014\u90a3\u53cc\u773c\u775b\u662f\u4e0d\u662f\u8fd8\u5728\u6c34\u6676\u76d2\u5b50\u91cc\u77aa\u7740\u5979\uff1f \u2014\u2014\u90a3\u56db\u5341\u4e5d\u4e2a\u820c\u5934\u5410\u5f97\u957f\u957f\u7684\u51a4\u6b7b\u9b3c\uff0c\u662f\u4e0d\u662f\u4e5f\u5728\u9ed1\u6697\u4e2d\u770b\u7740\u5979\uff1f \u5979\u770b\u4e0d\u89c1\u4ed6\u4eec\u3002 \u5979\u4e0d\u662f\u795e\u773c\u3002 \u2014\u2014\u90a3\u8be5\u6b7b\u7684\u9646\u5c0f\u51e4\u6b7b\u5230\u54ea\u91cc\u53bb\u4e86\uff1f \u201c\u8001\u5934\u5b50\uff0c\u6b7b\u8005\u5934\u5b50\uff0c\u59d3\u9646\u7684\uff0c\u4f60\u8fd8\u4e0d\u5feb\u51fa\u6765\uff1f\u201d \u5979\u5927\u558a\uff0c\u6ca1\u6709\u56de\u5e94\u3002 \u8fde\u4e00\u4e2a\u4eba\u7684\u56de\u7b54\u90fd\u6ca1\u6709\uff0c\u7ba1\u5bb6\u5a46\u3001\u94a9\u5b50\u3001\u8868\u54e5\uff0c\u4e5f\u5168\u90fd\u4e0d\u77e5\u6e9c\u5230\u54ea\u91cc\u53bb\u4e86\u3002 \u96be\u9053\u4ed6\u4eec\u4e5f\u5168\u90fd\u88ab\u90a3\u53cc\u770b\u4e0d\u89c1\u7684\u9b3c\u624b\u6d3b\u6d3b\u627c\u6b7b\uff1f \u96be\u9053\u8fd9\u6839\u672c\u5c31\u662f\u4e2a\u8981\u547d\u7684\u5708\u5957\uff1f \u5979\u60f3\u51b2\u51fa\u53bb\uff0c\u4e09\u6b21\u90fd\u649e\u5728\u5899\u4e0a\uff0c\u5979\u5168\u8eab\u90fd\u5df2\u88ab\u51b7\u6c57\u6e7f\u900f\u3002 \u6700\u540e\u4e00\u6b21\u8dcc\u5012\u65f6\uff0c\u5979\u7684\u817f\u5df2\u8f6f\u4e86\uff0c\u51e0\u4e4e\u8fde\u722c\u90fd\u722c\u4e0d\u8d77\u6765\u3002 \u9ed1\u6697\u4e2d\u5374\u5ffd\u7136\u6709\u53ea\u624b\u4f38\u4e86\u8fc7\u6765\uff0c\u62c9\u8d77\u4e86\u5979\u3002 \u2014\u662f\u4e0d\u662f\u9646\u5c0f\u51e4\uff1f \u4e0d\u662f\u3002 \u51b0\u51b7\u5e72\u67af\u7684\u624b\uff0c\u6307\u7532\u6700\u5c11\u6709\u4e00\u5bf8\u957f\u3002 \u5979\u5fcd\u4e0d\u4f4f\u53c8\u653e\u58f0\u5927\u547c\uff0c\u201c\u4f60\u662f\u8c01\uff1f\u201d \u201c\u4f60\u770b\u4e0d\u89c1\u6211\u7684\uff0c\u6211\u5374\u80fd\u770b\u89c1\u4f60\u3002\u201d\u9ed1\u6697\u4e2d\u6709\u4eba\u5728\u5403\u5403\u7684\u7b11\uff0c\u201c\u6211\u662f\u795e\u773c\u3002\u201d \u8fd9\u662f\u5973\u4eba\u7684\u58f0\u97f3\u3002 \u8fd9\u53ea\u624b\u96be\u9053\u662f\u4ece\u6c34\u6676\u76d2\u5b50\u91cc\u4f38\u51fa\u6765\u7684\uff1f \u7b11\u58f0\u8fd8\u6ca1\u6709\u505c\uff0c\u5979\u7528\u5c3d\u5168\u8eab\u529b\u6c14\u6478\u8fc7\u53bb\u3002 \u5979\u6251\u5382\u4e2a\u7a7a\u3002 \u90a3\u53ea\u51b0\u51b7\u5e72\u67af\u7684\u624b\uff0c\u5374\u53c8\u4ece\u5979\u80cc\u540e\u4f38\u4e86\u8fc7\u6765\uff0c\u8f7b\u629a\u7740\u5979\u7684\u54bd\u5589\u3002 \u5979\u5e76\u4e0d\u662f\u90a3\u79cd\u5f88\u5bb9\u6613\u5c31\u4f1a\u88ab\u5413\u6655\u7684\u5973\u4eba\uff0c\u53ef\u662f\u73b0\u5728\u5979\u5df2\u6655\u4e86\u8fc7\u53bb\u3002 \u56db\u6708\u521d\u5341\uff0c\u6674\u3002 \u67f3\u9752\u9752\u9192\u6765\u65f6\uff0c\u9633\u5149\u6b63\u7167\u5728\u7a97\u6237\u4e0a\u3002 \u7a97\u6237\u5728\u52a8\uff0c\u7a97\u5916\u7684\u6811\u6728\u4e5f\u5728\u52a8\u2014\u2014\u5c31\u50cf\u98de\u4e00\u6837\u7684\u5f80\u540e\u9000\u3002 \u5979\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u53c8\u5230\u4e86\u9a6c\u8f66\u4e0a\uff0c\u9646\u5c0f\u51e4\u6b63\u5750\u5728\u5979\u5bf9\u9762\uff0c\u7b11\u563b\u563b\u7684\u770b \u7740\u5979\u3002 \u5979\u54ac\u4e86\u54ac\u5634\u5507\uff0c\u5f88\u75bc\u3002 \u8fd9\u4e0d\u662f\u68a6\u3002 \u5979\u8df3\u4e86\u8d77\u6765\uff0c\u77aa\u7740\u9646\u5c0f\u51e4\u3002 \u9646\u5c0f\u51e4\u5fae\u7b11\u9053\uff1a\u201c\u65e9\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u65e9\uff1f\u73b0\u5728\u662f\u65e9\u4e0a\uff1f\u201d \u9646\u5c0f\u51e4\u7b11\u9053\uff1a\u201c\u5176\u5b9e\u4e5f\u4e0d\u7b97\u592a\u65e9\u4e86\uff0c\u6628\u5929\u665a\u4e0a\u4f60\u7761\u5f97\u7b80\u76f4\u50cf\u6b7b\u4eba\u4e00\u6837aH\u67f3\u9752\u9752\u54ac \u7740\u7259\uff0c\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u4e5f\u7761\u4e86\u4e00\u4e0b\u3002\u201d \u67f3\u9752\u9752\u5ffd\u7136\u8df3\u8d77\u6765\uff0c\u6251\u8fc7\u53bb\uff0c\u6251\u5728\u4ed6\u8eab\u4e0a\uff0c\u627c\u4f4f\u4e86\u4ed6\u7684\u8116\u5b50\uff0c\u72e0\u72e0\u9053\uff1a\u201c\u8bf4\u2018\u5feb \u8bf4\uff0c\u8fd9\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u6628\u5929\u665a\u4e0a\u7684\u4e8b\u3002\u201d \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u6b63\u60f3\u95ee\u4f60\uff0c\u4f60\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u597d\u597d\u7684\u4e3a\u4ec0\u4e48\u8981\u4e00\u5934\u649e\u5230\u5899 \u4e0a\u53bb\uff0c\u628a\u81ea\u5df1\u649e\u660f\uff1f\u201d \u67f3\u9752\u9752\u53eb\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u6211\u53c8\u6ca1\u6709\u75af\uff0c\u4e3a\u4ec0\u4e48\u8981\u649e\u81ea\u5df1\u7684\u5934\u2019\u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a \u201c\u8fde\u4f60\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\uff0c\u6211\u600e\u4e48\u4f1a\u77e5\u9053\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u6211\u95ee\u4f60\uff0c\u5c4b\u5b50\u91cc\u90a3\u4e9b\u706f\uff0c\u600e\u4e48\u4f1a\u5ffd\u7136\u4e00\u8d77\u706d\u4e86\u7684\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u706f\u91cc\u6ca1\u6709\u6cb9\u4e86\uff0c\u5f53\u7136\u4f1a\u706d\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u90a3\u4e2a\u6316\u86af\u8693\u7684\u4eba\u5462\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u706f\u706d\u4e86\uff0c\u4ed6\u5f53\u7136\u8981\u53bb\u627e\u706f\u6cb9\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u4ed6\u627e\u5230\u6ca1\u6709\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4ed6\u627e\u5230\u4e86\u706f\u6cb9\uff0c\u6211\u4eec\u624d\u80fd\u627e\u5230\u4f60\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u4ed6\u771f\u7684\u662f\u4e2a\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e0d\u4f46\u662f\u4eba\uff0c\u800c\u4e14\u8fd8\u662f\u4e2a\u597d\u4eba\uff0c\u4e0d\u4f46\u627e\u5230\u4e86\u706f\u6cb9\uff0c\u8fd8\u716e\u4e86\u4e00\u5927\u9505\u7ca5\uff0c \u6211\u4eec\u6bcf\u4e2a\u4eba\u90fd\u5403\u4e86\u597d\u51e0\u7897\u3002\u201d \u67f3\u9752\u9752\u6014\u4f0e\uff0c\u6014\u4e86\u534a\u5929\uff0c\u624d\u95ee\u9053\uff1a\u201c\u706f\u9ed1\u7684\u65f6\u5019\uff0c\u4f60\u4eec\u5728\u54ea\u91cc\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5728\u540e\u9762\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u6211\u5728\u524d\u9762\uff0c\u4f60\u4eec\u5230\u540e\u9762\u53bb\u5e72\u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u5728\u524d\u9762\u3002\u6211\u4eec\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u4e5f\u8981\u5728\u524d\u9762\uff1f\u6211\u4eec\u53c8\u4e0d\u662f\u4f60\u7684\u8ddf\u5c41\u866b\uff0c\u4e3a \u4ec0\u4e48\u4e0d\u80fd\u5230\u540e\u9762\u53bb\u770b\u770b\uff1f\u201d \u67f3\u9752\u9752\u5ffd\u53c8\u5927\u558a\uff0c\u201c\u7ba1\u5bb6\u7684\uff0c\u7ba1\u5bb6\u5a46\uff0c\u4e56\u513f\u5b50\uff0c\u4f60\u4eec\u5168\u8fdb\u6765\u3002\u201d \u8f66\u5b50\u505c\u4e0b\uff0c\u5979\u53eb\u7684\u4eba\u5168\u90fd\u8fc7\u6765\u4e86\uff0c\u5979\u5c06\u521a\u624d\u95ee\u9646\u5c0f\u51e4\u7684\u8bdd\u53c8\u95ee\u4e86\u4e00\u904d\uff0c\u4ed6\u4eec\u7684\u56de \u7b54\u4e5f\u4e00\u6837\u3002 \u4ed6\u4eec\u4e5f\u4e0d\u61c2\uff0c\u5979\u4e3a\u4ec0\u4e48\u597d\u597d\u7684\u8981\u628a\u81ea\u5df1\u4e00\u5934\u649e\u6655\u3002 \u67f3\u9752\u9752\u51e0\u4e4e\u53c8\u6c14\u5f97\u5feb\u6655\u8fc7\u53bb\u4e86\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u96be\u9053\u4f60\u4eec\u5168\u90fd\u6ca1\u6709\u770b\u89c1\u53ea\u624b\uff1f\u201d \u7ba1\u5bb6\u5a46\u9053\uff1a\u201c\u4ec0\u4e48\u624b\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u627c\u4f50\u6211\u8116\u5b50\u7684\u9b3c\u624b\u3002\u201d \u9646\u5c0f\u51e4\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u770b\u89c1\u4e86\u3002\u201d \u4ed6\u7b11\u5f97\u5f88\u795e\u79d8\uff0c\u201c\u4e0d\u4f46\u770b\u89c1\u4e86\uff0c\u800c\u4e14\u8fd8\u628a\u5b83\u5e26\u4e86\u56de\u6765\u3002\u201d \u67f3\u9752\u9752\u773c\u775b\u91cc\u7acb\u523b\u53d1\u4e86\u5149\uff0c\u201c\u5728\u54ea\u91cc\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5c31\u5728\u8fd9\u91cc\u3002\u201d\u4ed6\u5fae\u7b11\u7740\uff0c\u4ece\u8eab\u4e0a\u62ff\u51fa\u4e86\u4e00\u6bb5\u6302\u7a97\u5e18\u7684\u7ef3\u5b50\uff0c\u7ef3\u5b50\u4e0a \u8fd8\u5e26\u7740\u597d\u51e0\u4e2a\u2014\u5bf8\u957f\u7684\u94a9\u5b50\uff0c\u5c31\u50cf\u662f\u6307\u7532\u4e00\u6837\u7684\u94a9\u5b50\uff0c\u201c\u8fd9\u662f\u4e0d\u8fdb\u7f20\u5728\u4f60\u8116\u5b50\u4e0a\u7684\u9b3c \u624b\uff1f\u201d \u67f3\u9752\u9752\u8bf4\u4e0d\u51fa\u8bdd\u6765\u4e86\u3002 \u6d77\u5947\u9614\u5ffd\u7136\u5927\u7b11\uff0c\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5927\u540d\u9f0e\u9f0e\u7684\u6c5f\u5357\u5973\u4fa0\u67f3\u9752\u9752\uff0c\u5c45\u7136\u4f1a\u88ab\u4e00\u6bb5\u7ef3\u5b50 \u5413\u5f97\u6655\u8fc7\u53bb\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5176\u5b9e\u4f60\u5e94\u8be5\u60f3\u5f97\u5230\u7684\u3002\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u5979\u662f\u4e2a\u5973\u4eba\uff0c\u800c\u4e14\u5e74\u7eaa\u5df2\u4e0d\u7b97\u5c0fao\u4ed6\u53f9\u606f\u7740\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u5973\u4eba \u5230\u4e86\u5979\u8fd9\u79cd\u5e74\u7eaa\uff0c\u603b\u96be\u514d\u4f1a\u7591\u795e\u7591\u9b3c\u7684\u3002\u201d \u56db\u6708\u5341\u4e00\uff0c\u6674\u3002 \u9ec4\u660f\u3002 \u4ece\u6628\u5929\u65e9\u4e0a\u5230\u73b0\u5728\uff0c\u67f3\u9752\u9752\u8bf4\u7684\u8bdd\u52a0\u8d77\u6765\u8fd8\u6ca1\u6709\u5979\u5e73\u5e38\u4e00\u987f\u3002 \u5979\u7684\u8138\u8272\u4e5f\u5f88\u4e0d\u597d\u770b\uff0c\u4e0d\u77e5\u9053\u662f\u56e0\u4e3a\u60ca\u9b42\u72b9\u672a\u5b9a\uff1f\u8fd8\u662f\u56e0\u4e3a\u884c\u52a8\u7684\u65f6\u5019\u5df2\u7ecf\u5feb\u5230 \u4e86\u3002 \u73b0\u5728\u4ed6\u4eec\u8ddd\u79bb\u6b66\u5f53\u5df2\u53ea\u6709\u534a\u5929\u7684\u884c\u7a0b\uff0c\u8001\u5200\u628a\u5b50\u5374\u4e00\u76f4\u6ca1\u6709\u6d88\u606f\uff0c\u4e5f\u6ca1\u6709\u7ed9\u4ed6\u4eec \u6700\u540e\u7684\u6307\u793a\uff0c\u6240\u4ee5\u4e0d\u4f46\u5979\u53d8\u4e86\uff0c\u522b\u7684\u4eba\u4e5f\u96be\u514d\u6709\u70b9\u7d27\u5f20\u3002 \u8c01\u4e5f\u4e0d\u77e5\u9053\u8fd9\u6b21\u884c\u52a8\u4ed6\u4eec\u80fd\u6709\u591a\u5c11\u6210\u628a\u63e1\u3002 \u77f3\u9e64\u3001\u94c1\u80a9\u3001\u738b\u5341\u888b\u3001\u9ad8\u884c\u7a7a\u2026\u2026\u8fd9\u4e9b\u4eba\u51e0\u4e4e\u5df2\u53ef\u7b97\u662f\u6b66\u6797\u4e2d\u7684\u7cbe\u82f1\u3002 \u4f55\u51b5\uff0c\u9664\u4e86\u8fd9\u4e03\u4e2a\u4eba\u4e4b\u5916\uff0c\u8fd8\u4e0d\u77e5\u6709\u591a\u5c11\u9ad8\u624b\u4e5f\u5df2\u5230\u4e86\u2019\u6b66\u5f53\u5c71\u3002 \u201c\u4f60\u60f3\u897f\u95e8\u5439\u96ea\u4f1a\u4e0d\u4f1a\u53bb\uff1f \u201c\u4ed6\u53ef\u80fd\u4e0d\u4f1a\u53bb\u3002\u201d \u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u201c\u56e0\u4e3a\u4ed6\u5728\u627e\u9646\u5c0f\u51e4\uff0c\u4ed6\u7edd\u5bf9\u60f3\u4e0d\u5230\u9646\u5c0f\u51e4\u6562\u4e0a\u6b66\u5f53\u3002\u201d \u8bf4\u8fd9\u53e5\u8bdd\u7684\u4eba\u6b63\u662f\u9646\u5c0f\u51e4\u81ea\u5df1\u3002 \u4ed6\u8fd9\u4e48\u6837\u8bf4\uff0c\u4e5f\u8bb8\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u81ea\u5df1\u5fc3\u91cc\u5e0c\u671b\u5982\u6b64\u3002 \u9ec4\u660f\u65f6\u7684\u57ce\u5e02\u603b\u662f\u6700\u70ed\u95f9\u7684\uff0c\u4ed6\u4eec\u7684\u8f66\u9a6c\u6b63\u7a7f\u8fc7\u95f9\u5e02\u3002 \u201c\u5c31\u7b97\u897f\u95e8\u5439\u96ea\u4e0d\u4f1a\u53bb\uff0c\u6728\u9053\u4eba\u5374\u4e00\u5b9a\u4f1a\u5728\u90a3\u91cc\uff0c\u8fd1\u5e74\u6765\u4ed6\u867d\u7136\u5df2\u51e0\u4e4e\u5b8c\u5168\u9000 \u9690\uff0c\u53ef\u662f\u50cf\u518c\u7acb\u638c\u95e8\u8fd9\u79cd\u5927\u4e8b\uff0c\u4ed6\u603b\u4e0d\u80fd\u7f6e\u8eab\u4e8b\u5916\u7684ao\u201c\u5f53\u7136oo\u201c\u6728\u9053\u4eba\u82e5\u5230\u4e86\uff0c\u53e4 \u677e\u5c45\u58eb\u60f3\u5fc5\u4e5f\u4f1a\u53bb\uff0c\u5c31\u53ea\u8fd9\u4e24\u4e2a\u4eba\uff0c\u5df3\u4e0d\u662f\u5bb9\u6613\u5bf9\u4ed8\u7684\u3002\u201d \u201c\u6211\u60f3\u8001\u5200\u628a\u5b50\u4e00\u5b9a\u5df2\u6709\u4e86\u5bf9\u4ed8\u4ed6\u4eec\u7684\u6cd5\u5b50\uff0c\u5426\u5219\u4ed6\u4e3a\u4ec0\u4e48\u4e00\u76f4\u90fd\u6ca1\u6709\u628a\u8fd9\u4e24\u4e2a \u4eba\u5217\u5165\u8ba1\u5212\u91cc\uff1f\u201d \u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u73b0\u5728\u6211\u4eec\u90fd\u4e0d\u8be5\u60f3\u8fd9\u4ef6\u4e8b\u3002\u201d\u9646\u5c0f\u51e4\u53c8\u5f00\u5382\u53e3\u3002 \u201c\u6211\u4eec\u5e94\u8be5\u60f3\u4ec0\u4e48\uff1f\u201d \u201c\u60f3\u60f3\u5e94\u8be5\u5230\u54ea\u91cc\u5403\u996d\u53bb\u3002\u201d \u8868\u54e5\u3001\u7ba1\u5bb6\u5a46\u3001\u6d77\u5947\u9614\uff0c\u6b64\u523b\u5168\u90fd\u5728\u8f66\u4e0a\uff0c\u672c\u6765\u597d\u50cf\u90fd\u60f3\u8bf4\u8bdd\u7684\uff0c\u5374\u5ffd\u7136\u540c\u65f6\u95ed \u4e0a\u4e86\u5634\uff0c\u516d\u53ea\u773c\u775b\u4e00\u8d77\u76ef\u5728\u5bf9\u8857\u7684\u4e00\u5bb6\u9152\u697c\u95e8\u53e3\u3002 \u8f66\u9a6c\u8d70\u5f97\u5f88\u6162\uff0c\u5c31\u5728\u4ed6\u4eec\u7ecf\u8fc7\u65f6\uff0c\u6b63\u6709\u4e09\u4e2a\u4eba\u8d70\u5165\u4e86\u9152\u697c\u3002 \u4e00\u4e2a\u4eba\u8d64\u9762\u79c3\u9876\uff0c\u76ee\u5149\u707c\u707c\u5982\u9e70\uff0c\u4e00\u4e2a\u4eba\u9ad8\u5982\u7af9\u7aff\uff0c\u7626\u4e5f\u5982\u7af9\u7aff\uff0c\u8d70\u8d77\u8def\u6765\u4e00\u6447 \u4e09\u6643\uff0c\u597d\u50cf\u4e00\u9635\u98ce\u5c31\u80fd\u5c06\u4ed6\u5439\u5012\u3002 \u8fd8\u6709\u4e2a\u4eba\u6276\u7740\u8fd9\u4e24\u4eba\u7684\u80a9\uff0c\u4eff\u4f5b\u5df2\u6709\u4e86\u51e0\u5206\u9189\u6001\uff0c\u5374\u662f\u4e2a\u767d\u53d1\u82cd\u82cd\u7684\u9053\u4eba\u3002 \u8fd9\u4e09\u4e2a\u4eba\u9646\u5c0f\u51e4\u5168\u8ba4\u5f97\uff0c\u8868\u54e5\u3001\u7ba1\u5bb6\u5a46\u3001\u6d77\u5947\u9614\u4e5f\u5168\u90fd\u8ba4\u5f97\u3002 \u76ee\u5149\u5982\u9e70\u7684\uff0c\u6b63\u662f\u5341\u4e8c\u8fde\u73af\u575e\u603b\u74e2\u628a\u5b50\u201c\u9e70\u773c\u201d\u8001\u4e03\u3002 \u8fde\u8def\u90fd\u8d70\u4e0d\u7a33\u7684\uff0c\u5374\u662f\u4ee5\u8f7b\u529f\u540d\u52a8\u5927\u6c5f\u5357\u5317\u7684\u201c\u96c1\u8361\u5c71\u4e3b\u201d\u9ad8\u884c\u7a7a\u3002 \u90a3\u4e2a\u5df2\u559d\u5f97\u5dee\u4e0d\u591a\u4e86\u7684\u8001\u9053\u58eb\uff0c\u5c31\u6b63\u662f\u4ed6\u4eec\u521a\u521a\u8fd8\u5728\u8c08\u8d77\u7684\u6b66\u5f53\u540d\u5bbf\u6728\u9053\u4eba\u3002 \u8868\u54e5\u7684\u773c\u775b\u867d\u7136\u5728\u76ef\u7740\u4ed6\u4eec\uff0c\u5fc3\u91cc\u5374\u53ea\u5e0c\u671b\u8f66\u9a6c\u5feb\u70b9\u8d70\u8fc7\u53bb\u3002 \u8c01\u77e5\u9646\u5c0f\u51e4\u5374\u5ffd\u7136\u9053\uff1a\u201c\u53eb\u8f66\u5b50\u505c\u4e0b\u6765\u3002\u201d \u8868\u54e5\u5413\u4e86\u4e00\u8df3\uff0c\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4eec\u5c31\u8981\u5728\u8fd9\u5bb6\u9152\u697c\u5403\u996d\u3002\u201d \u8868\u54e5\u66f4\u5403\u60ca\uff0c\u201c\u4f60\u4e0d\u8ba4\u5f97\u90a3\u4e09\u4e2a\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u8ba4\u5f97\u4ed6\u4eec\uff0c\u53ef\u662f\u4ed6\u4eec\u5374\u4e0d\u8ba4\u5f97\u6211\u4e86\u3002\u201d \u8868\u54e5\u9053\uff1a\u201c\u4e07\u4e00\u4ed6\u4eec\u8ba4\u51fa\u6765\u4e86\u600e\u4e48\u529e\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u4eec\u73b0\u5728\u82e5\u80fd\u8ba4\u51fa\u6211\u4eec\uff0c\u5230\u4e86\u6b66\u5f53\u4e5f\u4e00\u6837\u8ba4\u5f97\u51fa\u3002\u201d \u8868\u54e5\u60f3\u4e86\u60f3\uff0c\u7ec8\u4e8e\u6709\u70b9\u660e\u767d\u4ed6\u7684\u610f\u601d\uff0c\u201c\u4f60\u662f\u60f3\u8bd5\u8bd5\u4ed6\u4eec\uff0c\u4e0d\u662f\u4e0d\u80fd\u8ba4\u5f97\u51fa\u6211\u4eec \u6765\uff1f\u201d \u9646\u5c0f\u51e4\u6de1\u6de1\u9053\uff1a\u201c\u53cd\u6b63\u6211\u4eec\u603b\u5f97\u8fd9\u4e48\u6837\u5192\u2014\u6b21\u9669\u7684\uff0c\u73b0\u5728\u88ab\u4ed6\u4eec\u8ba4\u51fa\u6765\uff0c\u81f3\u5c11\u603b \u6bd4\u5230\u4e86\u6b66\u5f53\u624d\u88ab\u8ba4\u51fa\u6765\u7684\u597d\u3002\u201d \u8fd9\u53e5\u8bdd\u521a\u8bf4\u5b8c\uff0c\u67f3\u9752\u9752\u5df2\u5728\u7528\u529b\u6572\u7740\u8f66\u53a2\uff0c\u5927\u58f0\u9053\uff1a\u201c\u505c\u8f66\u76f4\u5230\u8fd9\u65f6\u4e3a\u6b62\uff0c\u5927\u5bb6 \u663e\u7136\u90fd\u8ba4\u4e3a\u9646\u5c0f\u51e4\u8fd9\u60f3\u6cd5\u4e0d\u9519\uff0c\u6240\u4ee5\u6ca1\u6709\u4e00\u4e2a\u4eba\u53cd\u5bf9\u7684\u3002 \u56e0\u4e3a\u8fd9\u65f6\u4ed6\u4eec\u8fd8\u6ca1\u6709\u8d70\u4e0a\u9152\u697c\u3002 \u7b49\u4ed6\u4eec\u8d70\u4e0a\u53bb\u65f6\uff0c\u540e\u6094\u5c31\u5df2\u6765\u4e0d\u53ca\u4e86\uff0c\u6700\u540e\u6094\u7684\u2014\u4e2a\u4eba\uff0c\u5c31\u662f\u9646\u5c0f\u51e4\u3002 \u7b2c\u5341\u4e09\u7ae0\u3000\u4e00\u5b9a\u8981\u6210\u529f>> \u53e4\u9f99\u300a\u9646\u5c0f\u51e4\u7cfb\u5217\xb7\u5e7d\u7075\u5c71\u5e84\u300b \u7b2c\u5341\u4e09\u7ae0\u3000\u4e00\u5b9a\u8981\u6210\u529f \u8fd9\u5bb6\u9152\u697c\u7684\u88c5\u78fa\u5f88\u8003\u7a76\uff0c\u6c14\u6d3e\u4e5f\u5f88\u5927\uff0c\u53ef\u662f\u751f\u610f\u5e76\u4e0d\u592a\u597d\u3002 \u73b0\u5728\u867d\u7136\u6b63\u662f\u665a\u996d\u7684\u65f6\u5019\uff0c\u9152\u697c\u4e0a\u7684\u96c5\u5ea7\u5374\u53ea\u6709\u4e09\u684c\u5ba2\u4eba\u3002 \u9ad8\u884c\u7a7a\u4ed6\u4eec\u5e76\u4e0d\u662f\u4e09\u4e2a\u4eba\u6765\u7684\uff0c\u9152\u697c\u4e0a\u65e9\u5df2\u5148\u5230\u4e86\u4e00\u4e2a\u4eba\u5728\u7b49\u7740\u4ed6\u4eec\u3002 \u8fd9\u4eba\u9ad8\u5927\u5a01\u6b66\uff0c\u76f8\u8c8c\u5802\u5802\uff0c\u770b\u6c14\u6d3e\uff0c\u770b\u8863\u7740\uff0c\u90fd\u5e94\u8be5\u662f\u6b66\u6797\u4e2d\u7684\u540d\u4eba\u3002 \u53ef\u662f\u9646\u5c0f\u51e4\u5374\u504f\u504f\u4e0d\u8ba4\u5f97\u4ed6\uff0c\u751a\u81f3\u8fde\u89c1\u90fd\u6ca1\u6709\u89c1\u8fc7\u3002 \u6b66\u6797\u4e2d\u7684\u540d\u4eba\uff0c\u9646\u5c0f\u51e4\u6ca1\u6709\u89c1\u8fc7\u7684\u5e76\u4e0d\u591a\u3002 \u4eba\u6700\u591a\u7684\u4e00\u684c\uff0c\u4e5f\u662f\u9152\u559d\u5f97\u6700\u591a\u7684\u4e00\u684c\uff0c\u5ea7\u4e0a\u6709\u7537\u6709\u53c8\u3002 \u7537\u7684\u8863\u7740\u534e\u4e3d\uff0c\u770b\u6765\u4e0d\u662f\u4ece\u626c\u5dde\u90a3\u8fb9\u6765\u7684\u76d0\u5546\u5bcc\u8d3e\uff0c\u5c31\u662f\u5fae\u670d\u51fa\u6e38\u7684\u95f2\u5b98\u540d\u540f\uff0c \u5973\u7684\u59ff\u5bb9\u51b6\u8273\uff0c\u98ce\u6d41\u800c\u8f7b\u6311\uff0c\u65e0\u7591\u662f\u98ce\u5c18\u4e2d\u7684\u5973\u5b50\u3002 \u4eba\u6700\u5c11\u7684\u4e00\u684c\u53ea\u6709\u4e00\u4e2a\u4eba\u3002 \u4e00\u4e2a\u767d\u8863\u4eba\uff0c\u767d\u8863\u5982\u96ea\u3002 \u770b\u89c1\u8fd9\u4e2a\u4eba\uff0c\u9646\u5c0f\u51e4\u7684\u638c\u5fc3\u5c31\u6ccc\u51fa\u4e86\u51b7\u6c57\uff0c\u4ed6\u5b9e\u5728\u60f3\u4e0d\u5230\u4f1a\u5728\u8fd9\u91cc\u9047\u89c1\u8fd9\u4e2a\u4eba\uff0c \u5426\u5219\u5c31\u7b97\u6709\u4eba\u5728\u540e\u9762\u7528\u97ad\u5b50\u62bd\u4ed6\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u4f1a\u4e0a\u6765\u7684\u3002 \u65e2\u7136\u5df2\u4e0a\u4e86\u697c\uff0c\u518d\u4e0b\u53bb\u5c31\u6765\u4e0d\u53ca\u4e86\u3002 \u9646\u5c0f\u51e4\u53ea\u6709\u786c\u7740\u5934\u76ae\u627e\u4e86\u4e2a\u4f4d\u5b50\u5750\u4e0b\uff0c\u67f3\u9752\u9752\u51b7\u51b7\u7684\u770b\u7740\u4ed6\uff0c\u51e0\u4e4e\u53ef\u4ee5\u770b\u5f97\u89c1\u4e00 \u7c92\u7c92\u51b7\u6c57\u5df2\u900f\u8fc7\u4ed6\u8138\u4e0a\u7684\u4eba\u76ae\u9762\u5177\u5192\u4e86\u51fa\u6765\u3002 \u767d\u8863\u4eba\u5374\u8fde\u773c\u89d2\u90fd\u6ca1\u6709\u770b\u4ed6\u4eec\u3002 \u4ed6\u7684\u8138\u94c1\u9752\u3002 \u4ed6\u7684\u5251\u5c31\u5728\u684c\u4e0a\u3002 \u4ed6\u559d\u7684\u662f\u6c34\uff0c\u7eaf\u51c0\u7684\u767d\u6c34\uff0c\u4e0d\u662f\u9152\u3002 \u4ed6\u663e\u7136\u968f\u65f6\u968f\u5730\u90fd\u5728\u51c6\u5907\u6740\u4eba\u3002 \u6728\u9053\u4eba\u5728\u5411\u4ed6\u6253\u62db\u547c\uff0c\u4ed6\u4e5f\u50cf\u662f\u6ca1\u6709\u770b\u89c1\uff0c\u8fd9\u4f4d\u540d\u91cd\u6c5f\u6e56\u7684\u6b66\u5f53\u540d\u5bbf\uff0c\u7ade\u4eff\u4f5b\u6839 \u672c\u5c31\u6ca1\u6709\u88ab\u4ed6\u770b\u5728\u773c\u91cc\u3002 \u4f55\u4eba\u770b\u5728\u773c\u91cc\u3002 \u6728\u9053\u4eba\u5374\u7b11\u4e86\uff0c\u6447\u7740\u5934\u5583\u5583\u7b11\u9053\uff1a\u201c\u6211\u4e0d\u602a\u4ed6\uff0c\u968f\u4fbf\u4ed6\u600e\u4e48\u65e0\u793c\uff0c\u6211\u90fd\u4e0d\u602a \u4ed6\u3002\u201d \u90a3\u9ad8\u5927\u5a01\u6b66\u7684\u8001\u4eba\u5fcd\u4e0d\u4f4f\u95ee\uff0c\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u6728\u9053\u4eba\u9053\uff1a\u56e0\u4e3a\u4ed6\u662f\u897f\u95e8\u5439\u96ea\uff01\u201d \u5929\u4e0a\u5730\u4e0b\uff0c\u72ec\u4e00\u65e0\u4e8c\u7684\u897f\u95e8\u5439\u96eaI\u5929\u4e0a\u5730\u4e0b\uff0c\u72ec\u4e00\u65e0\u4e8c\u7684\u5251\uff01 \u53ea\u8981\u4ed6\u624b\u91cc\u8fd8\u6709\u5251\uff0c\u4ed6\u5c31\u6709\u6743\u4e0d\u5c06\u4efb\u4f55\u4eba\u770b\u5728\u773c\u91cc\uff01 \u4e5f\u8bb8\u4ed6\u73b0\u5728\u773c\u91cc\u53ea\u770b\u5f97\u89c1\u9646\u5c0f\u51e4\u4e00\u4e2a\u4eba\u3002 \u4ec7\u6068\u5c31\u50cf\u662f\u79cd\u5947\u5f02\u7684\u6bd2\u8349\uff0c\u867d\u7136\u80fd\u7248\u5bb3\u4eba\u7684\u5fc3\u7075\uff0c\u5374\u4e5f\u80fd\u5c06\u4e00\u4e2a\u4eba\u7684\u6f5c\u529b\u5168\u90e8\u53d1 \u6325\uff0c\u4f7f\u4ed6\u7684\u610f\u5fd7\u66f4\u575a\u5f3a\uff0c\u53cd\u5e94\u66f4\u654f\u9510\u3002 \u4f55\u51b5\uff0c\u8fd9\u79cd\u4e00\u5251\u523a\u51fa\uff0c\u4e0d\u5dee\u6beb\u5398\u7684\u5251\u58eb\uff0c\u672c\u5c31\u6709\u4e00\u53cc\u9e70\u96bc\u822c\u7684\u9510\u773c\u3002 \u73b0\u5728\u4ed6\u867d\u7136\u7edd\u5bf9\u60f3\u4e0d\u5230\u9646\u5c0f\u51e4\u5c31\u5728\u4ed6\u773c\u524d\uff0c\u4f46\u662f\u9646\u5c0f\u51e4\u53ea\u8981\u9732\u51fa\u4e00\u70b9\u7834\u9699\uff0c\u5c31\u7edd \u5bf9\u9003\u4e0d\u8fc7\u4ed6\u8fd9\u53cc\u9510\u773c\u3002 \u83dc\u5df2\u7ecf\u70b9\u597d\u4e86\uff0c\u5802\u500c\u6b63\u5728\u95ee\uff0c\u201c\u5ba2\u5b98\u4eec\u60f3\u559d\u70b9\u4ec0\u4e48\u9152\uff1f\u201d \u67f3\u9752\u9752\u7acb\u523b\u62a2\u7740\u9053\uff1a\u201c\u4eca\u5929\u6211\u4eec\u4e0d\u559d\u9152\uff0c\u4e00\u70b9\u90fd\u4e0d\u559d\u3002\u201d \u9152\u603b\u662f\u5bb9\u6613\u4ee4\u4eba\u9020\u6210\u758f\u5ffd\u7684\uff0c\u4efb\u4f55\u4e00\u70b9\u758f\u5ffd\uff0c\u90fd\u8db3\u4ee5\u81f4\u547d\u3002 \u53ef\u662f\u9152\u4e5f\u80fd\u4f7f\u4eba\u7684\u795e\u7ecf\u677e\u5f1b\uff0c\u5fc3\u60c5\u9547\u5b9a\u3002 \u9646\u5c0f\u51e4\u7b11\uff0c\u201c\u4eca\u5929\u6211\u4eec\u4e0d\u559d\u4e00\u70b9\u9152\uff0c\u6211\u4eec\u8981\u559d\u5f88\u591a\u3002\u201d \u4ed6\u5fae\u7b11\u7740\u62cd\u4e86\u62cd\u8868\u54e5\u7684\u80a9\uff0c\u201c\u4eca\u5929\u662f\u6211\u513f\u5b50\u7684\u751f\u65e5\uff0c\u5409\u65e5\u600e\u53ef\u65e0\u9152\uff0c\u4f60\u5148\u7ed9\u6211\u4eec \u6765\u4e00\u575b\u7af9\u65f6\u9752\u3002\u201d \u67f3\u9752\u9752\u72e0\u72e0\u7684\u76ef\u7740\u4ed6\uff0c\u4ed6\u4e5f\u597d\u50cf\u5b8c\u5168\u770b\u4e0d\u89c1\uff0c\u5fae\u7b11\u7740\u53c8\u9053\uff1a\u201c\u5929\u751f\u7537\u513f\uff0c\u4ee5\u9152\u4e3a \u547d\uff0c\u5987\u4eba\u4e4b\u8a00\uff0c\u614e\u4e0d\u53ef\u542c\uff0c\u6765\uff0c\u4f60\u4eec\u8001\u4e24\u53e3\u4e5f\u5750\u4e0b\u6765\u966a\u6211\u559d\u51e0\u676f\uff0c\u201c\u7ba1\u5bb6\u5a46\u548c\u6d77\u5947\u9614 \u4e5f\u53ea\u597d\u5750\u4e0b\uff0c\u6728\u9053\u4eba\u5df2\u5728\u90a3\u8fb9\u629a\u638c\u5927\u7b11\uff0c\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u2018\u5987\u4eba\u4e4b\u8a00\uff0c\u614e\u4e0d\u53ef\u542c\u2019\u542c\u6b64 \u4e00\u8a00\uff0c\u5df2\u5f53\u6d6e\u4e09\u5927\u767d\u3002\u201d \u9152\u6765\u7684\u771f\u597d\uff0c\u559d\u5f97\u66f4\u5feb\u3002 \u4e09\u676f\u4e0b\u809a\uff0c\u9646\u5c0f\u51e4\u795e\u60c5\u5c31\u81ea\u7136\u5f97\u591a\u4e86\uff0c\u773c\u775b\u91cc\u4e5f\u6709\u4e86\u5149\u3002 \u73b0\u5728\u4ed6\u603b\u7b97\u5df2\u8d70\u51fa\u4e86\u897f\u95e8\u5439\u96ea\u7684\u9634\u5f71\uff0c\u4eff\u4f5b\u6839\u672c\u5df2\u5fd8\u4e86\u9152\u697c\u4e0a\u8fd8\u6709\u8fd9\u4e48\u6837\u4e00\u4e2a \u4eba\u3002 \u897f\u95e8\u5439\u96ea\u5251\u950b\u822c\u9510\u5229\u7684\u76ee\u5149\uff0c\u5374\u5ffd\u7136\u76ef\u5230\u4ed6\u8eab\u4e0a\u3002 \u6728\u9053\u4eba\u4e5f\u5728\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u4e3e\u676f\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u4ee5\u9152\u4e3a\u547d\u7684\u670b\u53cb\uff0c\u53ef\u5bb9\u8001\u9053\u58eb\u656c\u4f60\u4e00 \u676f\uff1f\u201d \u9646\u5c0f\u51e4\u7b11\u9053\uff1a\u201c\u606d\u656c\u4e0d\u5982\u4ece\u547d\uff0c\u8001\u673d\u4e5f\u5f53\u56de\u656c\u9053\u957f\u4e09\u676f\u3002\u201d \u6728\u9053\u4eba\u5927\u7b11\uff0c\u5ffd\u7136\u8d70\u8fdb\u6765\uff0c\u773c\u775b\u91cc\u4e5f\u9732\u51fa\u5200\u950b\u822c\u7684\u5149\uff0c\u76ef\u7740\u9646\u5c0f\u51e4\uff0c\u9053\uff1a\u201c\u8d35 \u59d3\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u59d3\u718a\uff0c\u718a\u864e\u4e4b\u718a\u3002\u201d \u6728\u9053\u4eba\u9053\uff1a\u201c\u840d\u6c34\u76f8\u9022\uff0c\u672c\u4e0d\u8be5\u6253\u6270\u7684\uff0c\u53ea\u662f\u718a\u5144\u996e\u9152\u7684\u8c6a\u60c5\uff0c\u50cf\u6781\u4e86\u6211\u4e00\u4f4d\u670b \u53cb\u3002\u201d \u67f3\u9752\u9752\u5fc3\u5df2\u5728\u8df3\u4e86\uff0c\u9646\u5c0f\u51e4\u5c45\u7136\u8fd8\u662f\u7b11\u5f97\u5f88\u6109\u5feb\uff0c\u9053\uff1a\u201c\u9053\u957f\u8fd9\u4f4d\u670b\u53cb\u5728\u54ea\u91cc\uff1f\u201d \u6728\u9053\u4eba\u9053\uff1a\u201c\u8fdc\u5728\u5929\u8fb9\uff0c\u8fd1\u5728\u773c\u524d\u3002\u201d \u67f3\u9752\u9752\u4e00\u9897\u5fc3\u5df2\u51e0\u4e4e\u8df3\u51fa\u8154\u4e86\uff0c\u9646\u5c0f\u51e4\u676f\u4e2d\u7684\u9152\u4e5f\u51e0\u4e4e\u6e85\u4e86\u51fa\u6765\u3002 \u6728\u9053\u4eba\u5374\u53c8\u4ef0\u9762\u957f\u53f9\uff0c\u63a5\u7740\u9053\uff1a\u201c\u5929\u5fcc\u835a\u624d\uff0c\u6211\u8fd9\u4f4d\u670b\u53cb\u867d\u7136\u5df2\u8fdc\u53bb\u897f\u5929\uff0c\u53ef\u662f \u6b64\u95f4\u6709\u9152\uff0c\u53c8\u6709\u6545\u4eba\uff0c\u4ed6\u7684\u4e00\u7f15\u82f1\u9b42\uff0c\u8bf4\u4e0d\u5b9a\u53c8\u56de\u5230\u6211\u773c\u524d\u3002\u201d \u67f3\u9752\u9752\u677e\u4e86\u53e3\u6c14\uff0c\u9646\u5c0f\u51e4\u4e5f\u677e\u4e86\u53e3\u6c14\uff0c\u56e0\u4e3a\u4ed6\u4eec\u90fd\u6ca1\u6709\u53bb\u770b\u897f\u95e8\u5439\u96ea\u3002 \u897f\u95e8\u5439\u96ea\u82cd\u767d\u7684\u8138\u4f3c\u5df2\u767d\u5f97\u900f\u660e\uff0c\u4e00\u53ea\u624b\u5df2\u6276\u4e0a\u5251\u67c4\u3002 \u5ffd\u7136\u95f4\uff0c\u7a97\u5916\u54cd\u8d77\u201c\u545b\u201d\u7684\u4e00\u58f0\u9f99\u541f\u3002 \u53ea\u6709\u5229\u5251\u51fa\u9798\u65f6\uff0c\u624d\u4f1a\u6709\u8fd9\u79cd\u6e05\u4eae\u5982\u9f99\u541f\u822c\u7684\u54cd\u58f0\u3002 \u897f\u95e8\u5439\u96ea\u7684\u77b3\u5b54\u7acb\u523b\u6536\u7f29\u3002 \u5c31\u5728\u8fd9\u540c\u4e00\u5239\u90a3\u95f4\uff0c\u591c\u7a7a\u4e2d\u4eff\u4f5b\u5728\u5389\u7535\u4e00\u95ea\uff0c\u4e00\u9053\u5bd2\u5149\uff0c\u7a7f\u7a97\u800c\u5165\uff0c\u76f4\u523a\u897f\u95e8\u5439 \u96ea\u3002 \u897f\u95e8\u5439\u96ea\u7684\u5251\u5728\u684c\u4e0a\uff0c\u72b9\u672a\u51fa\u9798\uff0c\u5251\u9798\u65c1\u7684\u4e00\u53ea\u96f6\u6c34\u7684\u9152\u676f\u5374\u7a81\u7136\u5f39\u8d77\uff0c\u8fce\u4e0a\u4e86 \u5251\u5149\u3002 \u2018\u2018\u53ee\u201d\u7684\u4e00\u54cd\uff0c\u4e00\u53ea\u9152\u676f\u7adf\u788e\u6210\u4e86\u5343\u4e07\u7247\uff0c\u5e26\u7740\u5343\u4e07\u7c92\u6c34\u73e0\uff0c\u51b7\u96fe\u822c\u98de\u6563\u56db \u6fc0\u3002 \u5251\u5149\u4e0d\u89c1\u4e86\uff0c\u51b7\u96fe\u4e2d\u5374\u51fa\u73b0\u4e86\u4e00\u4e2a\u4eba\u3002 \u4e00\u4e2a\u9ed1\u8863\u4eba\uff0c\u8138\u4e0a\u4e5f\u8499\u7740\u5757\u9ed1\u5dfe\uff0c\u53ea\u9732\u51fa\u4e00\u53cc\u707c\u707c\u6709\u5149\u7684\u8116\u5b50\u3002 \u684c\u4e0a\u5df2\u6ca1\u6709\u5251\uff0c\u5251\u5df2\u5728\u624b\u3002 \u9ed1\u8863\u4eba\u76ef\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u62d4\u5251\u3002\u201d \u897f\u95e8\u5439\u96ea\u51b7\u51b7\u9053\uff1a\u201c\u4e03\u4e2a\u4eba\u5df2\u592a\u5c11\uff0c\u4f60\u4f55\u5fc5\u4e00\u5b9a\u8981\u6b7b\u3002\u201d \u9ed1\u8863\u4eba\u4e0d\u61c2\uff0c\u201c\u4e03\u4e2a\u4eba\uff1f\u201d \u897f\u95e8\u5439\u96ea\u9053\uff1a\u201c\u666e\u5929\u4e4b\u4e0b\uff0c\u914d\u7528\u5251\u7684\u4eba\uff0c\u8fde\u4f60\u53ea\u6709\u4e03\u4e2a\uff0c\u5b66\u5251\u5982\u6b64\uff0c\u5e76\u4e0d\u5bb9 \u6613\u3002\u201d \u4ed6\u6325\u4e86\u6325\u624b\uff0c\u201c\u4f60\u8d70\u5427\u3002\u201d \u9ed1\u8863\u4eba\u9053\uff1a\u201c\u4e0d\u8d70\u5c31\u6b7b\u3002\u201d \u897f\u95e8\u5439\u96ea\u9053\uff1a\u201c\u662f\u3002\u201d \u9ed1\u8863\u4eba\u51b7\u7b11\uff0c\u9053\uff1a\u201c\u6b7b\u7684\u53ea\u4f2f\u4e0d\u662f\u6211\uff0c\u662f\u4f60\u300f\u2019\u2019\u4ed6\u7684\u5251\u53c8\u98de\u8d77\u3002 \u6728\u9053\u4eba\u76b1\u8d77\u4e86\u7709\uff0c\u201c\u8fd9\u4e00\u5251\u5df2\u4e0d\u5728\u65f6\u5b64\u57ce\u7684\u5929\u5916\u98de\u4ed9\u4e4b\u4e0b\uff0c\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u53ea\u6709\u9646\u5c0f\u51e4\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u8c01\u3002 \u4ed6\u53c8\u60f3\u8d77\u4e86\u5e7d\u7075\u5c71\u5e84\u5916\u7684\u751f\u6b7b\u4ea4\u754c\u7ebf\u4e0a\uff0c\u90a3\u7a7f\u77f3\u800c\u5165\u7684\u4e00\u5251\u3002 \u77f3\u9e64\uff0c\u90a3\u4e2a\u6ca1\u6709\u8138\u7684\u4eba\u3002 \u4ed6\u672c\u6765\u5c31\u4e00\u5fc3\u60f3\u4e0e\u897f\u95e8\u5439\u96ea\u4e00\u8f83\u9ad8\u4f4e\u7684\u3002 \u53c8\u662f\u4e00\u58f0\u9f99\u541f\uff0c\u897f\u95e8\u5439\u96ea\u7684\u5251\u4e5f\u5df2\u51fa\u9798\u3002 \u6ca1\u6709\u4eba\u80fd\u5f62\u5bb9\u4ed6\u4eec\u4e24\u67c4\u5251\u7684\u53d8\u5316\u548c\u901f\u5ea6\u3002 \u6ca1\u6709\u4eba\u80fd\u5f62\u5bb9\u4ed6\u4eec\u8fd9\u4e00\u6218\u3002 \u5251\u6c14\u7eb5\u6a2a\uff0c\u9152\u697c\u4e0a\u6240\u6709\u7684\u676f\u76d8\u76cf\u7adf\u5168\u90fd\u7c89\u788e\uff0c\u5251\u98ce\u7834\u7a7a\uff0c\u903c\u5f97\u6bcf\u4e2a\u4eba\u547c\u5438\u90fd\u51e0\u4e4e \u505c\u987f\u3002 \u90a3\u56db\u4e2a\u8863\u7740\u534e\u4e3d\u7684\u8001\u4eba\uff0c\u5c45\u7136\u8fd8\u662f\u9762\u4e0d\u6539\u8272\uff0c\u966a\u4f34\u5728\u4ed6\u4eec\u8eab\u65c1\u7684\u5973\u5b69\u5b50\uff0c\u5374\u5df2\u7bf1 \u98de\u71d5\u6563\uff0c\u82b1\u5bb9\u5931\u8272\u4e86\u3002 \u5ffd\u7136\u95f4\uff0c\u4e00\u9053\u5251\u5149\u51b2\u5929\u98de\u8d77\uff0c\u9ed1\u8863\u4eba\u659c\u659c\u7a9c\u51fa\uff0c\u843d\u5728\u4ed6\u4eec\u684c\u4e0a\u3002 \u897f\u95e8\u5439\u96ea\u7684\u5251\u5149\u51cc\u7a7a\u4e0b\u51fb\uff0c\u4ed6\u5168\u8eab\u90fd\u5df2\u5728\u5251\u5149\u7b3c\u7f69\u4e0b\u3002 \u4ed6\u5df2\u5931\u5c3d\u5148\u673a\uff0c\u5df2\u9000\u65e0\u53ef\u9000\u3002 \u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\uff0c\u8fd9\u5757\u697c\u677f\u7adf\u5ffd\u7136\u51ed\u7a7a\u9677\u843d\u4e86\u4e0b\u53bb\u2014\u684c\u5343\u8ddf\u7740\u843d\u4e86\u4e0b\u53bb\uff0c\u684c\u4e0a\u7684\u9ed1 \u8863\u4eba\u843d\u4e86\u4e0b\u53bb\uff0c\u56db\u4e2a\u5b89\u5750\u4e0d\u52a8\u7684\u534e\u8863\u8001\u4eba\u4e5f\u843d\u4e86\u4e0b\u53bb\u3002 \u9152\u697c\u4e0a\u7adf\u5ffd\u7136\u9677\u843d\u4e86\u4e00\u4e2a\u5927\u6d1e\u3002\u5c31\u50cf\u662f\u5927\u5730\u5ffd\u7136\u5206\u88c2\u3002 \u897f\u95e8\u5439\u96ea\u7684\u5251\u5149\u5df2\u4ece\u6d1e\u4e0a\u5b83\u8fc7\uff0c\u8fd9\u53d8\u5316\u663e\u7136\u4e5f\u4e0d\u51fa\u4ed6\u610f\u6599\u4e4b\u5916\uff0c\u4ed6\u6b63\u60f3\u7a7f\u6d1e\u800c \u4e0b\uff0c\u8c01\u77e5\u8fd9\u5757\u697c\u677f\u7adf\u5ffd\u7136\u53c8\u98de\u4e86\u4e0a\u6765\uff0c\u201c\u5361\u64e6\u201d\u4e00\u58f0\uff0c\u6070\u5de7\u8865\u4e0a\u4e86\u8fd9\u4e2a\u6d1e\u3002 \u684c\u5b50\u8fd8\u5728\u8fd9\u5757\u697c\u677f\u4e0a\uff0c\u56db\u4e2a\u534e\u670d\u8001\u4eba\u4e5f\u8fd8\u662f\u52a8\u4e5f\u4e0d\u52a8\u7684\u5750\u5728\u90a3\u91cc\uff0c\u8fd9\u5757\u697c\u677f\u7adf\u50cf \u662f\u88ab\u4ed6\u4eec\u7528\u811a\u5e95\u5438\u4e0a\u6765\u7684\uff0c\u684c\u4e0a\u7684\u9ed1\u8863\u4eba\u5374\u5df2\u4e0d\u89c1\u4e86\uff01 \u5251\u5149\u4e5f\u4e0d\u89c1\u4e86\uff0c\u5251\u5df2\u4eba\u9798\u3002 \u897f\u95e8\u5439\u96ea\u51b7\u51b7\u7684\u770b\u7740\u4ed6\u4eec\uff0c\u51b7\u9177\u7684\u76ee\u5149\u4e2d\uff0c\u4e5f\u6709\u5b50\u60ca\u8be7\u4e4b\u610f\u3002 \u9ad8\u884c\u7a7a\u3001\u9e70\u773c\u8001\u4e03\u3001\u6728\u9053\u4eba\uff0c\u4e5f\u4e0d\u7981\u76f8\u987e\u5931\u8272\u3002 \u73b0\u5728\u4ed6\u4eec\u5f53\u7136\u90fd\u5df2\u770b\u51fa\u6765\uff0c\u8fd9\u56db\u4e2a\u534e\u670d\u8001\u4eba\u65e2\u4e0d\u662f\u8170\u7f20\u4e07\u8d2f\u7684\u76d0\u5546\u5bcc\u8d3e\uff0c\u4e5f\u4e0d\u662f \u5fae\u670d\u51fa\u6e38\u7684\u95f2\u5b98\u540d\u540f\uff0c\u800c\u662f\u529f\u529b\u6df1\u4e2d\u53ef\u6d4b\u7684\u6b66\u6797\u9ad8\u624b\u3002 \u4ed6\u4eec\u4ee5\u5185\u529b\u538b\u65ad\u518d\u4ee5\u5185\u529b\u5c06\u90a3\u5757\u697c\u677f\u5438\u4e0a\u6765\uff0c\u529f\u529b\u80fd\u5230\u8fbe\u8fd9\u4e00\u6b65\u7684\uff0c\u6b66\u6797\u4e2d\u6709\u51e0 \u4eba\uff1f \u897f\u95e8\u5439\u96ea\u5ffd\u7136\u9053\uff1a\u201c\u4e09\u4e2a\u4eba\u3002\u201d \u534e\u8863\u8001\u8005\u9759\u9759\u7684\u770b\u7740\u4ed6\uff0c\u7b49\u7740\u4ed6\u8bf4\u4e0b\u53bb\u3002 \u897f\u95e8\u5439\u96ea\u9053\uff1a\u201c\u80fd\u63a5\u4f4f\u6211\u56db\u5341\u4e5d\u5251\u7684\u4eba\uff0c\u53ea\u6709\u4e09\u4e2a\u4eba\u3002\u201d \u521a\u624d\u90a3\u7247\u8be5\u4e4b\u95f4\uff0c\u4ed6\u7adf\u5df2\u523a\u51fa\u4e86\u4e03\u4e03\u56db\u5341\u4e5d\u5251\u2606\u4ed6\u6740\u4eba\u7684\u786e\u4ece\u672a\u4f7f\u51fa\u8fc7\u56db\u5341\u4e5d \u5251\u3002 \u534e\u8863\u8001\u8005\u4e2d\u5e74\u7eaa\u6700\u957f\u7684\u4e00\u4e2a\u7ec8\u4e8e\u5f00\u53e3\uff0c\u9053\uff1a\u201c\u4f60\u770b\u4ed6\u662f\u5176\u4e2d\u54ea\u4e00\u4e2a\uff1f\u201d \u897f\u95e8\u5439\u96ea\u9053\uff1a\u201c\u90fd\u4e0d\u662f\u3002\u201d \u534e\u8863\u8001\u8005\u9053\uff1a\u201c\u54e6\uff1f\u201d \u897f\u95e8\u5439\u96ea\u51b7\u51b7\u9053\uff1a\u201c\u8fd9\u4e09\u4eba\u90fd\u5df2\u6709\u4e00\u6d3e\u5b97\u4e3b\u7684\u8eab\u4efd\uff0c\u7eb5\u7136\u8840\u6e85\u5251\u4e0b\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u9003 \u7684ao\u534e\u8863\u8001\u8005\u6de1\u6de1\u9053\uff1a\u201c\u90a3\u4e48\u4ed6\u5c31\u4e00\u5b9a\u662f\u7b2c\u56db\u4e2a\u4eba\u3002\u201d \u897f\u95e8\u5439\u96ea\u9053\uff1a\u201c\u6ca1\u6709\u7b2c\u56db\u4e2a\uff01\u201d \u534e\u8863\u8001\u8005\u9053\uff1a\u201c\u9601\u4e0b\u624b\u4e2d\u8fd8\u6709\u5251\uff0c\u4e3a\u4f55\u4e0d\u518d\u8bd5\u8bd5\uff0c\u6211\u4eec\u662f\u5426\u80fd\u63a5\u5f97\u4f4f\u9601\u4e0b\u7684\u56db\u5341 \u4e5d\u5251\uff1f\u201d \u897f\u95e8\u5439\u96ea\u9053\uff1a\u201c\u7eb5\u7136\u80fd\u63a5\u5f97\u4f4f\uff0c\u4f60\u4eec\u56db\u4eba\u6050\u6015\u6700\u591a\u4e5f\u53ea\u80fd\u5269\u4e0b\u4e09\u4e2a\u3002\u201d \u534e\u8863\u8001\u8005\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u897f\u95e8\u5439\u96ea\u95ed\u4e0a\u5634\u3002 \u8981\u5bf9\u4ed8\u8fd9\u56db\u4e2a\u4eba\uff0c\u4ed6\u7684\u786e\u6ca1\u6709\u628a\u63e1\u3002 \u534e\u8863\u8001\u4eba\u4eec\u4e5f\u95ed\u4e0a\u4e86\u5634\u3002 \u8981\u5bf9\u4ed8\u897f\u95e8\u5439\u96ea\uff0c\u4ed6\u4eec\u4e5f\u540c\u6837\u6ca1\u6709\u628a\u63e1\u3002 \u8ddf\u7740\u4ed6\u4eec\u6765\u7684\u56db\u4e2a\u8273\u88c5\u5c11\u5973\u4e2d\uff0c\u4e00\u4e2a\u7a7f\u7740\u7fe0\u7eff\u8f7b\u886b\u7684\u5ffd\u7136\u53eb\u4e86\u8d77\u6765\u3002 \u201c\u8205\u8205\u3002\u201d\u5979\u5927\u53eb\u7740\u51b2\u5411\u9646\u5c0f\u51e4\uff1a\u201c\u6211\u603b\u7b97\u627e\u5230\u4f60\u4e86\uff0c\u6211\u627e\u5f97\u4f60\u597d\u82e6\u3002\u201d \u9646\u5c0f\u51e4\u6014\u4f4f\u3002 \u4ed6\u4e00\u5411\u662f\u4e2a\u5149\u68cd\uff0c\u6807\u51c6\u7684\u5149\u68cd\uff0c\u53ef\u662f\u73b0\u5728\u4e0d\u4f46\u5ffd\u7136\u591a\u4e86\u4e2a\u513f\u5b50\u51fa\u6765\uff0c\u53c8\u5ffd\u7136\u505a\u4e86 \u522b\u4eba\u7684\u8205\u8205\u3002 \u8fd9\u5c11\u5973\u5df2\u8dea\u5012\u5728\u4ed6\u9762\u524d\uff0c\u6cea\u6d41\u6ee1\u9762\uff1a\u201c\u8205\u8205\u96be\u9053\u5df2\u4e0d\u8ba4\u5f97\u6211\u4e86\uff1f\u6211\u662f\u5c0f\u7fe0\uff0c\u4f60\u5ae1\u4eb2 \u7684\u5916\u7525\u5973\u5c0f\u7fe0\u3002\u201d \u9646\u5c0f\u51e4\u5ffd\u7136\u4e00\u628a\u6402\u4f4f\u5979\uff0c\u201c\u6211\u600e\u4e48\u4f1a\u4e0d\u8ba4\u5f97\u4f60\uff0c\u4f60\u7684\u5a18\u5462\uff1f\u201d \u5c0f\u7fe0\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u6ca1\u6cd5\u5b50\uff0c\u4ed6\u4eec\u2026\u2026\u4ed6\u4eec\u2026\u2026\u201d \u4e00\u53e5\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5df2\u653e\u58f0\u5927\u54ed\u4e86\u8d77\u6765\u3002 \u9646\u5c0f\u51e4\u5ffd\u7136\u8df3\u8d77\u6765\uff0c\u51b2\u5230\u534e\u8863\u8001\u4eba\u4eec\u7684\u9762\u524d\uff0c\u7834\u53e3\u5927\u9a82\uff0c\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8981\u6b3a\u8d1f\u5979\uff1f \u5426\u5219\u5979\u600e\u4e48\u4f1a\u54ed\u5f97\u5982\u6b64\u4f24\u5fc3\uff1f\u201d \u4ed6\u63ea\u4f4f\u4e00\u4e2a\u8001\u4eba\u7684\u8863\u895f\uff0c\u201c\u770b\u4f60\u4eec\u7684\u5e74\u7eaa\u6bd4\u6211\u8fd8\u5927\uff0c\u5374\u6765\u6b3a\u8d1f\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u4f60\u4eec \u662f\u4e0d\u662f\u4eba\uff1f\u6211\u8ddf\u4f60\u4eec\u62fc\u4e86\u3002 \u4ed6\u7528\u529b\u62c9\u8fd9\u8001\u4eba\uff0c\u5c0f\u7fe0\u4e5f\u8d76\u8fc7\u6765\uff0c\u5728\u540e\u9762\u62c9\u4ed6\uff0c\u5ffd\u7136\u95f4\uff0c\u201c\u54d7\u5566\u5566\u201d\u4e00\u58f0\u54cd\uff0c\u8fd9 \u5757\u697c\u677f\u53c8\u9677\u843d\u4e86\u4e0b\u53bb\uff0c\u4e09\u4e2a\u4eba\u8dcc\u4f5c\u4e00\u56e2\u3002 \u897f\u95e8\u5439\u96ea\u4f3c\u4e5f\u6014\u4f4f\u3002 \u521a\u624d\u4ed6\u9762\u5bf9\u7740\u7684\uff0c\u5f88\u53ef\u80fd\u5c31\u662f\u4ed6\u8fd9\u4e00\u751f\u4e2d\u6700\u53ef\u6015\u7684\u5bf9\u53ef\u662f\u73b0\u5728\u5ffd\u7136\u4e4b\u95f4\uff0c\u4ed6\u9762\u5bf9 \u7740\u7684\u5df2\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5927\u6d1e\u3002 \u4ed6\u53ea\u6709\u8d70\u3002 \u8d70\u8fc7\u6728\u9053\u4eba\u9762\u524d\u65f6\uff0c\u4ed6\u5ffd\u53c8\u505c\u4e0b\u6765\uff0c\u9053\uff1a\u201c\u4f60\u597d\u3002\u201d \u6728\u9053\u4eba\u4e5f\u6014\u4e86\u6014\uff0c\u5f00\u6000\u5927\u7b11\uff0c\u9053\uff1a\u201c\u597d\uff0c\u6211\u5f88\u597d\uff0c\u60f3\u4e0d\u5230\u4f60\u5c45\u7136\u8fd8\u8ba4\u5f97\u6211\u3002\u201d \u897f\u95e8\u5439\u96ea\u9053\uff1a\u201c\u53ef\u66fe\u89c1\u5230\u9646\u5c0f\u51e4\uff1f\u201d \u6728\u9053\u4eba\u4e0d\u7b11\u4e86\uff0c\u53f9\u606f\u7740\u9053\uff1a\u201c\u6211\u89c1\u4e0d\u7740\u4ed6\uff0c\u8c01\u90fd\u89c1\u4e0d\u7740\u4ed6\u897f\u95e8\u5439\u96ea\u51b7\u7b11\u3002 \u6728\u9053\u4eba\u8f6c\u5f00\u8bdd\u9898\uff0c\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u4e5f\u5230\u6b66\u5f53\u53bb\uff1f\u201d \u897f\u95e8\u5439\u96ea\u9053\uff1a\u201c\u4e0d\u53bb\u3002\u201d \u6728\u9053\u4eba\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u897f\u95e8\u5439\u96ea\u9053\uff1a\u201c\u6211\u6709\u5251\uff0c\u6b66\u5f53\u6709\u89e3\u5251\u5ca9\u3002\u201d \u6728\u9053\u4eba\u9053\uff1a\u201c\u4f60\u7684\u5251\u4ece\u4e0d\u80af\u89e3\uff1f\u201d \u897f\u95e8\u5439\u96ea\u9053\uff1a\u201c\u662f\u7684\u3002\u201d \u90a3\u9ad8\u5927\u5a01\u6b66\u7684\u8001\u4eba\u5ffd\u7136\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u6562\u5e26\u5251\u4e0a\u6b66\u5f53\uff1f\u201d \u897f\u95e8\u5439\u96ea\u51b7\u51b7\u9053\uff1a\u201c\u6211\u53ea\u6562\u6740\u4eba\uff0c\u53ea\u8981\u4f60\u518d\u8bf4\u4e00\u4e2a\u5b57\uff0c\u6211\u5c31\u6740\u4e86\u4f60\u3002\u201d \u6ca1\u6709\u4eba\u518d\u8bf4\u2014\u4e2a\u5b87\u3002 \u897f\u95e8\u5439\u96ea\u7684\u624b\u4e2d\u4ecd\u6709\u5251\u3002 \u4ed6\u5e26\u7740\u4ed6\u7684\u5251\uff0c\u5934\u4e5f\u4e0d\u56de\u7684\u8d70\u4e0b\u4e86\u697c\uff0c\u5934\u4e5f\u4e0d\u56de\u7684\u8d70\u4e86\u51fa\u53bb\u3002 \u9646\u5c0f\u51e4\u8fd8\u5728\u8ddf\u90a3\u4e9b\u534e\u8863\u8001\u4eba\u7ea0\u7f20\uff0c\u4ed6\u5374\u8fde\u770b\u90fd\u4e0d\u518d\u770b\u4ed6\u4eec\u4e00\u773c\u3002 \u95f9\u5e02\u706f\u706b\u4f9d\u65e7\u3002 \u770b\u7740\u4ed6\u8d70\u4e0a\u706f\u706b\u8f89\u714c\u7684\u957f\u8857\uff0c\u770b\u7740\u4ed6\u8d70\u8fdc\uff0c\u9ad8\u5927\u5a01\u6b66\u7684\u8001\u4eba\u624d\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a \u201c\u8fd9\u4e16\u4e0a\u96be\u9053\u771f\u7684\u53ea\u6709\u4e09\u4e2a\u4eba\u80fd\u63a5\u4f4f\u4ed6\u56db\u5341\u4e5d\u5251\uff1f\u201d \u6728\u9053\u4eba\u9053\uff1a\u201c\u771f\u7684\u3002\u201d \u8001\u4eba\u9053\uff1a\u201c\u6709\u6ca1\u6709\u80fd\u89e3\u4e0b\u4ed6\u7684\u5251\uff1f\u201d \u6728\u9053\u4eba\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u9ad8\u884c\u7a7a\u9053\uff1a\u201c\u96be\u9053\u4ed6\u771f\u7684\u5df2\u5929\u4e0b\u65e0\u6545\uff1f\u201d \u9ad8\u5927\u5a01\u6b66\u7684\u8001\u4eba\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4e5f\u8bb8\u6ca1\u6709\u4eba\u80fd\u89e3\u4e0b\u4ed6\u7684\u5251\uff0c\u4f46\u5374\u6709\u4e2a\u4eba\u80fd\u6740 \u4e86\u4ed6\u3002\u201d \u9ad8\u884c\u7a7a\u3001\u9e70\u773c\u8001\u4e03\uff0c\u540c\u65f6\u62a2\u7740\u95ee\u9053\uff1a\u201c\u8c01\uff1f\u201d \u9ad8\u5927\u5a01\u6b66\u7684\u8001\u4eba\u7b11\u5f97\u4eff\u4f5b\u5f88\u795e\u79d8\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u53ea\u8981\u4f60\u4eec\u6709\u8010\u5fc3\u7b49\u7740\uff0c\u8fd9\u4e2a\u4eba\u8fdf\u65e9 \u603b\u4f1a\u51fa\u73b0\u7684\u3002\u201d \u5ffd\u7136\u5c31\u53d1\u751f\u7684\u51b2\u7a81\uff0c\u53c8\u5ffd\u7136\u7ed3\u675f\uff0c\u522b\u7684\u4eba\u770b\u6765\u867d\u83ab\u540d\u5176\u5999\uff0c\u4ed6\u4eec\u81ea\u5df1\u5fc3\u91cc\u5374\u6709 \u6570\u3002 \u897f\u95e8\u5439\u96ea\u4e00\u8d70\uff0c\u9646\u5c0f\u51e4\u4e5f\u5c31\u8d70\u4e86\uff0c\u534e\u8863\u8001\u4eba\u4eec\u5f53\u7136\u4e0d\u4f1a\u963b\u62e6\u4ed6\uff0c\u5927\u5bb6\u90fd\u597d\u50cf\u6839\u672c \u6ca1\u6709\u53d1\u751f\u8fc7\u4efb\u4f55\u4e8b\u2014\u6837\u3002 \u73b0\u5728\u9646\u5c0f\u51e4\u53c8\u8212\u8212\u670d\u670d\u7684\u5750\u5230\u4ed6\u90a3\u8f86\u9a6c\u8f66\u4e0a\uff0c\u8f66\u9a6c\u53c8\u5f00\u59cb\u5f80\u524d\u8d70\u3002 \u4ed6\u90a3\u7a7f\u7740\u7fe0\u7eff\u8f7b\u886b\uff0c\u957f\u5f97\u695a\u695a\u52a8\u4eba\u7684\u5916\u7525\u5973\uff0c\u5c31\u5750\u5728\u4ed6\u5bf9\u9762\uff0c\u8138\u4e0a\u7684\u6cea\u75d5\u867d\u672a \u5e72\uff0c\u5374\u8fde\u4e00\u70b9\u60b2\u54c0\u7684\u8868\u60c5\u90fd\u6ca1\u6709\uff0c\u773c\u775b\u91cc\u751a\u81f3\u8fd8\u5e26\u7740\u7b11\u610f\uff0c\u4eff\u4f5b\u89c9\u5f97\u8fd9\u4ef6\u4e8b\u5f88\u6709\u8da3\u3002 \u9646\u5c0f\u51e4\u597d\u50cf\u4e5f\u89c9\u5f97\u8fd9\u4ef6\u4e8b\u5f88\u6709\u8da3\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u4f60\u662f\u6211\u5ae1\u4eb2\u7684\u5916\u7525\u5973\uff1f\u201d \u5c0f\u7fe0\u9053\uff1a\u201c\u55ef\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u5988\u5988\u5c31\u662f\u6211\u59b9\u59b9\uff1f\u201d \u5c0f\u7fe0\u9053\uff1a\u201c\u55ef\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u73b0\u5728\u5979\u5df2\u7ecf\u6b7b\u4e86\uff1f\u201d \u5c0f\u7fe0\u9053\uff1a\u201c\u55efoU\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u73b0\u5728\u4f60\u662f\u4e0d\u662f\u8981\u5e26\u6211\u4eec\u5230\u4f60\u5bb6\u53bb\uff1f\u201d \u5c0f\u7fe0\u9053\uff1a\u201c\u55ef\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u5bb6\u91cc\u8fd8\u6709\u4e9b\u4ec0\u4e48\u4eba\uff1f\u201d \u5c0f\u7fe0\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u8fd8\u6709\u4e9b\u4f60\u4e00\u5b9a\u4f1a\u559c\u6b22\u7684\u4eba\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u4f1a\u559c\u6b22\u4ec0\u4e48\u4eba\uff1f\u201d \u5c0f\u7fe0\u7728\u7740\u773c\u7b11\u9053\uff1a\u201c\u6211\u5f53\u7136\u77e5\u9053\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6709\u4e9b\u662f\u591a\u5c11\u4eba\uff1f\u201d \u5c0f\u7fe0\u9053\uff1a\u201c\u4e0d\u5c11\u3002\u201d \u5979\u4e5f\u7b11\u5f97\u5f88\u795e\u79d8\uff0c\u5ffd\u7136\u628a\u5934\u4f38\u5230\u7a97\u5916\uff0c\u5927\u58f0\u5429\u5490\u8d76\u8f66\u7684\uff0c\u201c\u4ece\u524d\u9762\u90a3\u6761\u5df7\u5b50\u5411\u5de6 \u8f6c\uff0c\u53f3\u8fb9\u7b2c\u4e09\u4e2a\u7ea2\u95e8\u5c31\u5230\u4e86\u3002\u201d \u94fa\u7740\u9752\u77f3\u677f\u7684\u5df7\u5b50\uff0c\u4e24\u8fb9\u9ad8\u5899\u5185\u4e00\u68f5\u68f5\u7ea2\u674f\u5f00\u5f97\u6b63\u597d\uff0c\u5899\u5185\u7684\u6625\u8272\u5df2\u6d53\u5f97\u8fde\u5173\u90fd \u5173\u4e0d\u4f4f\u4e86\u3002 \u53f3\u8fb9\u7b2c\u4e09\u4e2a\u7ea2\u95e8\u672c\u6765\u5c31\u662f\u5f00\u7740\u7684\uff0c\u95e8\u6963\u4e0a\u6302\u7740\u597d\u51e0\u76cf\u7c89\u7ea2\u8272\u7684\u5bab\u706f\u3002 \u5c0f\u7fe0\u4e00\u5b9a\u8fdb\u53bb\u5c31\u5927\u58f0\u7684\u558a\uff0c\u201c\u5927\u5bb6\u5feb\u51fa\u6765\uff0c\u6211\u4eec\u7684\u8205\u8205\u6765\u5979\u7684\u53eb\u58f0\u8fd8\u6ca1\u6709\u505c\uff0c\u9662 \u5b50\u91cc\u5c31\u6709\u5341\u4e03\u516b\u4e2a\u5973\u5b69\u5b50\u62e5\u4e86\u51fa\u6765\u3002 \u5979\u4eec\u90fd\u5f88\u5e74\u9752\uff0c\u5c31\u50cf\u662f\u71d5\u5b50\u822c\u8f7b\u76c8\u7f8e\u4e3d\uff0c\u53c8\u50cf\u662f\u9ebb\u96c0\u822c\u201c\u5431\u5431\u55b3\u55b3\u201d\u5435\u4e2a\u4e0d\u505c\u3002 \u5e74\u9752\u7684\u5973\u5b69\u5b50\u8c01\u4e0d\u559c\u6b22\u8205\u8205\u5462\uff1f \u5979\u4eec\u90fd\u62e5\u5230\u9646\u5c0f\u51e4\u8eab\u65c1\uff0c\u6709\u7684\u62c9\u624b\uff0c\u6709\u7684\u7275\u8863\u89d2\uff0c\u4e00\u4e2a\u4e2a\u90fd\u5728\u53eb\uff0c\u201c\u8205\u8205\u3002\u201d \u9646\u5c0f\u51e4\u53c8\u6014\u4f4f\uff0c\u201c\u5979\u4eec\u90fd\u662f\u6211\u7684\u5916\u7525\u5973\uff1f\u201d \u5c0f\u7fe0\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f60\u559c\u4e0d\u559c\u6b22\u5979\u4eec\uff1f\u201d \u9646\u5c0f\u51e4\u53ea\u6709\u627f\u8ba4\uff0c\u201c\u559c\u6b22\uff0c\u6bcf\u4e00\u4e2a\u90fd\u559c\u6b22\u3002\u201d \u5c0f\u7fe0\u7b11\u4e86\uff0c\u201c\u6211\u5c31\u77e5\u9053\u4f60\u4e00\u5b9a\u4f1a\u559c\u6b22\u5979\u4eec\u7684\u3002\u201d \u5979\u53c8\u53bb\u8b66\u544a\u90a3\u4e9b\u5973\u5b69\u5b50\uff0c\u201c\u53ef\u662f\u4f60\u4eec\u5374\u90fd\u5f97\u5c0f\u5fc3\u70b9\uff0c\u6211\u4eec\u8fd9\u4e2a\u8205\u8205\u4ec0\u4e48\u90fd\u597d\uff0c\u5c31 \u662f\u6709\u70b9\u4e0d\u592a\u8001\u5b9e\uff0c\u62b1\u7740\u4f60\u7684\u65f6\u5019\uff0c\u7b80\u76f4\u8ba9\u4eba\u8fde\u6c14\u90fd\u5598\u4e0d\u8fc7\u6765\u3002\u201d \u5973\u5b69\u5b50\u4eec\u7b11\u5f97\u66f4\u5a07\uff0c\u5435\u5f97\u66f4\u5389\u5bb3\u4e86\uff0c\u201c\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u88ab\u4ed6\u62b1\u8fc7\uff1f\u201d \u201c\u8205\u8205\u4e0d\u516c\u5e73\uff0c\u62b1\u8fc7\u5979\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u62b1\u6211\uff1f\u201d \u201c\u6211\u4e5f\u8981\u8205\u8205\u62b1\u3002\u201d \u201c\u6211\u4e5f\u8981\u3002\u201d \u9646\u5c0f\u51e4\u5de6\u987e\u53f3\u76fc\uff0c\u5f88\u6709\u70b9\u60f3\u8981\u53bb\u5de6\u62e5\u6709\u62b1\u7684\u610f\u601d\uff0c\u67f3\u9752\u9752\u51b7\u773c\u65c1\u89c2\uff0c\u6b63\u51c6\u5907\u60f3\u4e2a \u6cd5\u5b50\u8ba9\u4ed6\u6e05\u9192\u6e05\u9192\uff0c\u83ab\u8981\u4e50\u6781\u751f\u60b2\u3002 \u8c01\u77e5\u5c0f\u7fe0\u7684\u52a8\u4f5c\u5c45\u7136\u6bd4\u5979\u8fd8\u5feb\uff0c\u5df2\u62c9\u4f4f\u9646\u5c0f\u51e4\u7684\u624b\uff0c\u51b2\u51fa\u4e86\u91cd\u56f4\u3002 \u5973\u5b69\u5b50\u4eec\u53c8\u5927\u53eb\uff0c\u201c\u4f60\u53eb\u6211\u4eec\u51fa\u6765\u7684\uff0c\u4e3a\u4ec0\u4e48\u53c8\u628a\u8205\u8205\u62c9\u8d70\uff1f\u4ed6\u53c8\u4e0d\u662f\u4f60\u4e00\u4e2a\u4eba\u7684 \u8205\u8205\u3002\u201d \u9646\u5c0f\u51e4\u7acb\u523b\u540c\u610f\uff0c\u201c\u65e2\u7136\u5927\u5bb6\u90fd\u662f\u6211\u7684\u5916\u7525\u5973\uff0c\u6211\u4e5f\u5e94\u8be5\u966a\u966a\u5979\u4eec\u624d\u662f\u3002\u201d \u5c0f\u7fe0\u4e0d\u7406\u4ed6\uff0c\u4e00\u76f4\u5c06\u4ed6\u62c9\u5165\u4e86\u540e\u9762\u7684\u957f\u5eca\uff0c\u624d\u677e\u5f00\u624b\uff0c\u4f3c\u7b11\u975e\u7b11\u7684\u7528\u773c\u89d2\u779f\u7740 \u4ed6\uff0c\u201c\u770b\u6765\u4f60\u7684\u91ce\u5fc3\u5012\u771f\u4e0d\u5c0f\uff0c\u90a3\u4e9b\u91ce\u4e2b\u5934\u90fd\u662f\u6bcd\u8001\u864e\uff0c\u4f60\u96be\u9053\u4e0d\u6015\u5979\u4eec\u62c6\u6563\u4f60\u7684\u8fd9 \u628a\u8001\u9aa8\u5934\uff1f\u201d \u8fd9\u5df2\u7ecf\u5f88\u4e0d\u50cf\u5916\u7525\u5973\u5bf9\u8205\u8205\u8bf4\u8bdd\u7684\u6837\u5b50\u4e86\uff0c\u5979\u7a76\u7adf\u662f\u4ec0\u4e48\u4eba\uff1f\u4e3a\u4ec0\u4e48\u8981\u8ba4\u9646\u5c0f\u51e4\u505a \u8205\u8205\uff1f\u628a\u9646\u5c0f\u51e4\u62c9\u5230\u8fd9\u91cc\u6765\u60f3\u5e72\u4ec0\u4e48\uff1f \u9646\u5c0f\u51e4\u7728\u4e86\u7728\u773c\uff0c\u6545\u610f\u95ee\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u60f3\u5355\u72ec\u8ddf\u6211\u5728\u4e00\u8d77\uff1f\u201d \u5c0f\u7fe0\u53c8\u7b11\u4e86\uff0c\u5403\u5403\u7684\u7b11\u7740\u9053\uff1a\u201c\u6211\u53ef\u6ca1\u6709\u8fd9\u4e48\u5927\u7684\u80c6\u5b50\uff0c\u521a\u624d\u4f60\u5c31\u5dee\u70b9\u628a\u6211\u5168\u8eab \u9aa8\u5934\u90fd\u62b1\u788e\u4e86\uff0c\u82e5\u662f\u5355\u72ec\u8ddf\u4f60\u5728\u4e00\u8d77\uff0c\u90a3\u8fd8\u5f97\u4e86\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6709\u65f6\u6211\u4e5f\u4f1a\u5f88\u6e29\u67d4\u7684\uff0c\u5c24\u5176\u662f\u5728\u65c1\u8fb9\u6ca1\u6709\u4eba\u7684\u65f6\u5019\u3002\u201d \u5c0f\u7fe0\u6545\u610f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u96be\u602a\u522b\u4eba\u8bf4\u4f60\u662f\u4e2a\u8001\u8272\u72fc\uff0c\u5c45\u7136\u8fde\u81ea\u5df1\u7684\u5916\u7525\u5973\u90fd\u8981 \u6253\u4e3b\u610f\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u662f\u8001\u8272\u72fc\uff1f\u201d \u5c0f\u7fe0\u9053\uff1a\u201c\u4e00\u4e2a\u4eba\u8bf4\u7684\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8c01\uff1f\u201d \u5c0f\u7fe0\u9053\uff1a\u201c\u5f53\u7136\u4e5f\u662f\u4e2a\u4f60\u2014\u5b9a\u4f1a\u5f88\u559c\u6b22\u7684\u4eba\uff0c\u6211\u4fdd\u8bc1\u53ea\u8981\u4f60\u2014\u770b\u89c1\u4ed6\uff0c\u7acb\u523b\u5c31\u4f1a \u5c06\u522b\u7684\u4eba\u5168\u90fd\u5fd8\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u773c\u775b\u53c8\u4eae\u4e86\uff0c\u7acb\u523b\u95ee\u9053\uff1a\u201c\u8fd9\u4e2a\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u5c0f\u7fe0\u6307\u4e86\u6307\u8d70\u5eca\u5c3d\u5934\u5916\u7684\u2014\u6247\u95e8\uff0c\u9053\uff1a\u201c\u4ed6\u5c31\u5728\u90a3\u5c4b\u91cc\u7b49\u7740\u4f60\uff0c\u5df2\u7b49\u4e86\u5f88\u4e45\u4e86\uff0c \u4f60\u8fd8\u4e0d\u5feb\u53bb\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u5c0f\u7fe0\u53c8\u5403\u5403\u7684\u7b11\u9053\uff1a\u201c\u6211\u8fd9\u4e2a\u7ea2\u5a18\u53ea\u7ba1\u9001\u4fe1\uff0c\u53ef\u4e0d\u7ba1\u5e26\u4eba\u8fdb\u6d1e\u623f\u3002 \u957f\u5eca\u91cc\u4e5f\u6302\u7740\u597d\u51e0\u76cf\u7c89\u7ea2\u8272\u7684\u5bab\u706f\uff0c\u706f\u5149\u6bd4\u6708\u8272\u66f4\u6e29\u67d4\u3002 \u90a3\u4e9b\u91ce\u4e2b\u5934\u5c45\u7136\u6ca1\u6709\u8ffd\u8fdb\u6765\uff0c\u67f3\u9752\u9752\u5c45\u7136\u4e5f\u6ca1\u6709\u8ffd\u8fdb\u6765\u3002 \u95e8\u662f\u865a\u63a9\u7740\u7684\u3002 \u95e8\u91cc\u9759\u6084\u6084\u7684\u542c\u4e0d\u89c1\u4eba\u58f0\u3002 \u7a76\u7adf\u662f\u8c01\u5728\u91cc\u9762\u7b49\u7740\u4ed6\uff1f\u91cc\u9762\u662f\u4e2a\u6e29\u67d4\u9677\u9631\uff1f\u8fd8\u662f\u4e2a\u6740\u4eba\u7684\u9677\u9631\uff1f \u9646\u5c0f\u51e4\u6b63\u5728\u8fdf\u7591\u7740\uff0c\u5c0f\u7fe0\u5df2\u5728\u540e\u9762\u7528\u529b\u63a8\u4e86\u4ed6\u4e00\u628a\uff0c\u5c06\u4ed6\u63a8\u8fdb\u8fd9\u6247\u95e8\u3002 \u5c4b\u91cc\u7684\u706f\u5149\u66f4\u6e29\u67d4\uff0c\u9526\u5e10\u4f4e\u5782\uff0c\u73e0\u5e18\u6447\u66f3\uff0c\u770b\u6765\u7adf\u771f\u6709\u51e0\u5206\u50cf\u662f\u6d1e\u623f\u7684\u5149\u666f\u3002 \u73b0\u5728\u65b0\u90ce\u5df2\u8fdb\u4e86\u6d1e\u623f\uff0c\u65b0\u5a18\u5b50\u5462\uff1f \u5e10\u5b50\u91cc\u4e5f\u5bc2\u65e0\u4eba\u58f0\uff0c\u597d\u50cf\u5e76\u6ca1\u6709\u4eba\uff0c\u684c\u4e0a\u5374\u6446\u7740\u51e0\u6837\u83dc\u4e00\u58f6\u9152\u3002 \u83dc\u90fd\u662f\u9646\u5c0f\u51e4\u6700\u559c\u6b22\u5403\u7684\uff0c\u9152\u4e5f\u662f\u6700\u5408\u4ed6\u7684\u53e3\u5473\u7684\u7af9\u53f6\u9752\u8fd9\u4e2a\u4eba\u65e0\u7591\u8ba4\u5f97\u4ed6\uff0c\u800c \u4e14\u8fd8\u5f88\u4e86\u89e3\u4ed6\u3002 \u662f\u4e0d\u662f\u65f6\u7075\u5df2\u8d76\u5230\u524d\u9762\u6765\u4e86\uff0c\u6545\u610f\u8981\u8ba9\u4ed6\u5413\u4e00\u8df3\uff1f \u82e5\u4e0d\u662f\u53f6\u7075\uff0c\u8fd8\u6709\u8c01\u77e5\u9053\u4ed6\u5c31\u662f\u9646\u5c0f\u51e4\uff1f \u4ed6\u5c06\u81ea\u5df2\u8ba4\u5f97\u7684\u6bcf\u4e2a\u5973\u4eba\u90fd\u60f3\u4e86\u4e00\u904d\uff0c\u89c9\u5f97\u90fd\u4e0d\u53ef\u80fd\u3002 \u4e8e\u662f\u4ed6\u7d22\u6027\u4e0d\u60f3\u4e86\uff0c\u6b63\u51c6\u5907\u5750\u4e0b\u5c06\u521a\u624d\u8fd8\u6ca1\u6709\u5403\u5b8c\u7684\u665a\u996d\u627e\u8865\u56de\u6765\uff0c\u5e10\u5b50\u91cc\u5ffd\u7136 \u6709\u4eba\u9053\uff1a\u201c\u4eca\u5929\u4f60\u4e5f\u4e0d\u59a8\u5f00\u6000\u7545\u996e\uff0c\u65e0\u8bba\u60f3\u8981\u8c01\u966a\u4f60\u559d\u90fd\u884c\uff0c\u5c31\u7b97\u559d\u9189\u4e86\u4e5f\u65e0\u59a8\uff0c\u660e \u5929\u6211\u4eec\u6ca1\u6709\u4e8b\u3002\u201d \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u521a\u624d\u90a3\u4e9b\u7c89\u7ea2\u8272\u7684\u5e7b\u60f3\uff0c\u4e00\u4e0b\u5b50\u5168\u90fd\u53d8\u6210\u4e86\u7070\u8272\u76846\u7070\u6734\u6734\u7684 \u8863\u670d\uff0c\u7070\u6734\u6734\u7684\u58f0\u97f3\u3002 \u8fd9\u662f\u8001\u5200\u628a\u5b50\u7684\u58f0\u97f3\u3002 \u9646\u5c0f\u51e4\u53f9\u606f\u7740\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u660e\u660e\u6709\u5f88\u591a\u6cd5\u5b50\u53ef\u4ee5\u8ddf\u6211\u89c1\u9762\uff0c\u4e3a\u4ec0\u4e48\u504f\u504f\u8981\u6211\u7a7a \u6b22\u559c\u4e00\u573a\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u73b0\u5728\u8ddf\u4f60\u8bf4\u7684\u8bdd\uff0c\u7edd\u4e0d\u80fd\u8ba9\u7b2c\u4e8c\u4e2a\u4eba\u542c\u89c1\u3002\u201d \u4ed6\u7684\u4eba\u7ec8\u4e8e\u51fa\u73b0\u4e86\uff0c\u7a7f\u7684\u679c\u7136\u8fd8\u662f\u90a3\u5957\u7070\u6734\u6734\u7684\u8863\u88f3\uff0c\u5934\u4e0a\u5f53\u7136\u4e5f\u8fd8\u662f\u6234\u7740\u90a3\u9876 \u7bd3\u5b50\u822c\u7684\u7af9\u7b20\uff0c\u8ddf\u8fd9\u5730\u65b9\u5b9e\u5728\u4e00\u70b9\u90fd\u4e0d\u76f8\u914d\u3002 \u9646\u5c0f\u51e4\u8fde\u9152\u90fd\u5df2\u559d\u4e0d\u4e0b\u53bb\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u51c6\u5907\u628a\u6211\u9a82\u5f97\u72d7\u8840\u6dcb\u5934\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u521a\u624d\u4f60\u505a\u7684\u4e8b\u786e\u5b9e\u5f88\u9669\uff0c\u82e5\u4e0d\u662f\u6211\u65e9\u5df2\u6709\u4e86\u5b89\u6392\uff0c\u4e0d\u4f46\u6728\u9053\u4eba\u5f88\u53ef \u80fd\u8ba4\u51fa\u4f60\uff0c\u897f\u95e8\u5439\u96ea\u53ea\u4f2f\u4e5f\u8ba4\u51fa\u4e86\u4f60\u3002\u201d \u4ed6\u7684\u58f0\u97f3\u5c45\u7136\u5f88\u548c\u7f13\uff0c\u201c\u53ef\u662f\u73b0\u5728\u4e8b\u60c5\u603b\u7b97\u5df2\u8fc7\u53bb\uff0c\u603b\u7b97\u6ca1\u6709\u5f71\u54cd\u5927\u5c40\u3002\u201d \u9646\u5c0f\u51e4\u5374\u5fcd\u4e0d\u4f4f\u8981\u95ee\uff0c\u201c\u521a\u624d\u7684\u4e8b\u4f60\u5df2\u5168\u90fd\u77e5\u9053\uff1f\u96be\u9053\u521a\u624d\u4f60\u4e5f\u5728\u90a3\u91cc\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u6211\u4e0d\u5728\uff0c\u53ef\u662f\u6211\u77e5\u9053\u3002\u201d \u9646\u5c0f\u51e4\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u6700\u4f69\u670d\u4f60\u7684\u4e00\u70b9\uff0c\u5012\u5e76\u4e0d\u662f\u56e0\u4e3a\u4f60\u4ec0\u4e48\u4e8b\u90fd\u77e5 \u9053\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u6700\u4f69\u670d\u7684\u662f\u54ea\u4e00\u70b9\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u5c45\u7136\u60f3\u5f97\u51fa\u8981\u65e0\u864e\u65e0\u8c79\u90a3\u4e9b\u8001\u548c\u5c1a\u5e26\u7740\u5973\u4eba\u53bb\u559d\u9152\uff0c\u5c31\u51ed\u8fd9\u4e00 \u70b9\uff0c\u6211\u60f3\u4e0d\u4f69\u670d\u4f60\u90fd\u4e0d\u884c\u3002\u201d \u62bc\u5993\u51b6\u8fb9\u7684\u5927\u7237\u4eec\uff0c\u7adf\u662f\u6614\u65e5\u7684\u5c11\u6797\u9ad8\u50e7\uff0c\u8fd9\u79cd\u4e8b\u9664\u4e86\u8001\u5200\u628a\u5b50\u5916\uff0c\u6709\u8c01\u80fd\u60f3\u5f97 \u5230\uff1f \u6240\u4ee5\u897f\u95e8\u5439\u96ea\u4ed6\u4eec\u7eb5\u7136\u89c9\u5f97\u4ed6\u4eec\u6b66\u529f\u884c\u8ff9\u53ef\u7591\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u6000\u7591\u5230\u4ed6\u4eec\u5c31\u662f\u6b7b\u800c\u590d \u6d3b\u7684\u65e0\u864e\u5144\u5f1f\u3002 \u6c5f\u6e56\u4e4b\u4e2d\uff0c\u672c\u5c31\u6709\u5f88\u591a\u8eab\u6000\u7edd\u6280\uff0c\u6df1\u85cf\u4e0d\u9732\u7684\u98ce\u5c18\u5f02\u4eba\u3002 \u8001\u5200\u628a\u5b50\u6de1\u6de1\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u522b\u4eba\u60f3\u4e0d\u5230\uff0c\u6240\u4ee5\u8fd9\u4ef6\u4e8b\u624d\u4e0d\u81f4\u5f71\u54cd\u5927\u5c40\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ef\u662f\u7b49\u5230\u56db\u6708\u5341\u4e09\u90a3\u4e00\u5929\uff0c\u4ed6\u4eec\u53c8\u5728\u6b66\u5f53\u51fa\u73b0\u65f6\u2026\u2026\u201d \u8005\u5200\u628a\u5b50\u9053\uff1a\u201c\u90a3\u65f6\u4ed6\u4eec\u5df2\u53d8\u6210\u4e86\u4e0a\u5c71\u968f\u559c\u7684\u8fdc\u65b9\u9053\u58eb\uff0c\u6ca1\u6709\u4eba\u4f1a\u6ce8\u610f\u4ed6\u4eec \u7684\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u5462\uff1f\u90a3\u5929\u6211\u53d8\u6210\u4e86\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u662f\u4e2a\u706b\u5de5\u9053\u4eba\uff0c\u968f\u65f6\u90fd\u5f97\u5728\u5927\u6bbf\u4e2d\u4f8d\u5949\u6765\u81ea\u56db\u65b9\u7684\u8d35\u5ba2\u3002\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u5012\u771f\u662f\u4e2a\u597d\u5dee\u4e8b\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u90a3\u4e00\u5929\u6b66\u5f53\u5c71\u4e0a\u51a0\u76d6\u4e91\u96c6\uff0c\u7edd\u5bf9\u4e5f\u6ca1\u6709\u4eba\u4f1a\u6ce8\u610f\u5230\u4e00\u4e2a\u706b\u5de5\u9053\u58eb \u7684\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u771f\u6b63\u7684\u5dee\u4e8b\u662f\u4ec0\u4e48\uff1f\u662f\u5bf9\u4ed8\u77f3\u96c1\uff1f\u8fd8\u662f\u5bf9\u4ed8\u6728\u9053\u4eba\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u90fd\u4e0d\u662f\uff0c\u6211\u65e9\u5df2\u6709\u4e86\u5bf9\u4ed8\u4ed6\u4eec\u7684\u4eba\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u90a3\u4e48\u6211\u5462\uff1f\u4f60\u627e\u6211\u6765\uff0c\u603b\u4e0d\u4f1a\u662f\u7279\u5730\u8981\u6211\u53bb\u4f3a\u5019\u90a3\u4e9b\u5ba2\u4eba\u7684oD\u8001\u5200 \u628a\u5b50\u9053\uff1a\u201c\u4f60\u5f53\u7136\u8fd8\u6709\u522b\u7684\u4e8b\u8981\u505a\uff0c\u8fd9\u8ba1\u5212\u7684\u6210\u8d25\u5173\u952e\uff0c\u5c31\u5728\u4f60\u8eab\u4e0a\u3002\u201d \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u559d\u4e86\u676f\u9152\uff0c\u60f3\u5230\u81ea\u5df1\u80a9\u4e0a\u7adf\u8d1f\u7740\u8fd9\u4e48\u5927\u7684\u8d23\u4efb\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u53c8\u559d\u4e86\u4e00 \u676f\u3002 \u4ed6\u5b9e\u5728\u6709\uff0c\u70b9\u7d27\u5f20\u3002 \u8001\u5200\u628a\u5b50\u5c45\u7136\u4e5f\u5012\u4e86\u676f\u9152\uff0c\u6d45\u6d45\u555c\u7f00\u4e86\u4e00\u53e3\uff0c\u624d\u7f13\u7f13\u9053\uff1a\uff0e\u201c\u6211\u8981\u4f60\u505a\u7684\u4e8b\u5e76\u4e0d \u662f\u6740\u4eba\uff0c\u6211\u53ea\u4e0d\u8fc7\u8981\u4f60\u53bb\u9999\u6211\u62ff\u4e00\u4e2a\u5e10\u7c3f\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8c01\u7684\u5e10\u7c3f\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u672c\u6765\u662f\u6885\u771f\u4eba\u7684\uff0c\u4ed6\u6b7b\u4e86\u4e4b\u540e\uff0c\u5c31\u4f20\u5230\u77f3\u96c1\u624b\u91cc\u3002\u201d \u9646\u5c0f\u51e4\u60f3\u4e0d\u901a\uff0c\u201c\u5802\u5802\u7684\u6b66\u5f53\u638c\u95e8\uff0c\u96be\u9053\u4e5f\u81ea\u5df1\u8bb0\u5e10\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u6bcf\u4e00\u7b14\u5e10\u90fd\u662f\u4ed6\u4eec\u4eb2\u624b\u8bb0\u4e0b\u7684\u3002\u201d \u9646\u5c0f\u51e4\u8bd5\u63a2\u7740\u9053\uff1a\u201c\u5e10\u4e0a\u8bb0\u7740\u7684\u5f53\u7136\u4e0d\u662f\u67f4\u7c73\u6cb9\u76d0\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4e0d\u662f\u3002\u201d \u9646\u5c0f\u51e4\u66f4\u597d\u5947\uff0c\u201c\u4e0a\u9762\u8bb0\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\uff1f\u201d \u8001\u5200\u628a\u5b50\u5c45\u7136\u5c06\u676f\u4e2d\u9152\u4e00\u996e\u800c\u5c3d\uff0c\u624d\u6c89\u58f0\u9053\uff1a\u201c\u5e10\u4e0a\u8bb0\u7684\u662f\u5e72\u5e72\u767e\u767e\u4eba\u7684\u8eab\u5bb6\u6027 \u547d\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u662f\u54ea\u4e9b\u4eba\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u90fd\u662f\u4e9b\u6709\u8eab\u4efd\u7684\u4eba\uff0c\u6709\u540d\u7684\u4eba\uff0c\u6709\u94b1\u7684\u9646\u5c0f\u51e4\u66f4\u4e0d\u61c2\uff0c\u201c\u4ed6\u4eec\u7684 \u8eab\u5bb6\u6027\u547d\u548c\u77f3\u96c1\u7684\u5e10\u7c3f\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u8005\u5200\u628a\u5b50\u9053\uff1a\u201c\u8fd9\u672c\u5e10\u7c3f\u4e0a\u8bb0\u7740\u7684\uff0c\u5c31\u662f\u8fd9\u4e9b\u4eba\u7684\u9690\u79c1\u548c\u79d8\u5bc6oo\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u89c1 \u4e0d\u5f97\u4eba\u7684\u79d8\u5bc6\uff1f\u201d \u8001\u5200\u628a\u5b50\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u77f3\u96c1\u82e5\u662f\u5c06\u8fd9\u4e9b\u79d8\u5bc6\u516c\u5f00\u4e86\uff0c\u8fd9\u4e9b\u4eba\u975e\u4f46\u4ece\u6b64\u4e0d\u80fd\u7acb\u8db3 \u4e8e\u6c5f\u6e56\uff0c\u53ea\u6015\u7acb\u523b\u5c31\u8981\u8eab\u8d25\u540d\u88c2\uff0c\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\u3002\u201d \u9646\u5c0f\u51e4\u957f\u957f\u5438\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5802\u5802\u7684\u6b66\u5f53\u638c\u95e8\uff0c\u603b\u4e0d\u8be5\u505a\u51fa\u80c1\u4eba\u9690\u79c1\u7684\u4e8b\u3002\u201d \u8001\u5200\u628a\u5b50\u51b7\u51b7\u9053\uff1a\u201c\u4ed6\u4eec\u7684\u786e\u4e0d\u8be5\u505a\u7684\uff0c\u53ef\u662f\u4ed6\u4eec\u504f\u504f\u505a\u4e86\u51fa\u6765\u3002\u201d \u4ed6\u7684\u58f0\u97f3\u91cc\u5ffd\u7136\u5145\u6ee1\u6028\u6bd2\uff0c\u201c\u82e5\u4e0d\u662f\u56e0\u4e3a\u4ed6\u4eec\u603b\u662f\u4ee5\u522b\u4eba\u7684\u9690\u79c1\u4f5c\u4e3a\u8981\u80c1\u4e4b\u624b \u6bb5\uff0c\u77f3\u9e64\u600e\u4e48\u4f1a\u5728\u63a5\u638c\u6b66\u5f53\u95e8\u6237\u7684\u524d\u5915\u81ea\u6bc1\u9762\u76ee\uff1f\u987e\u98de\u4e91\u3001\u9ad8\u6d9b\u3001\u67f3\u9752\u9752\u3001\u949f\u65e0\u9aa8\uff0c\u8fd9 \u4e9b\u4eba\u7684\u79d8\u5bc6\uff0c\u53c8\u600e\u4e48\u4f1a\u88ab\u4eba\u77e5\u9053\uff1f\u201d \u9646\u5c0f\u51e4\u53c8\u4e0d\u7981\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u8fd9\u4e9b\u79d8\u5bc6\u90fd\u662f\u6885\u771f\u4eba\u548c\u77f3\u96c1\u8bf4\u51fa\u6765\u7684aU\u8001\u5200\u628a\u5b50 \u6068\u6068\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4eec\u8981\u80c1\u4e0d\u9042\uff0c\u4ed6\u4eec\u5c31\u4e00\u5b9a\u8981\u5c06\u8fd9\u4eba\u7f6e\u4e4b\u4e8e\u6b7b\u5730\uff0c\u5c31\u7b97\u8fd9\u4eba\u5df2\u6d17\u5fc3\u9769 \u9762\uff0c\u60f3\u91cd\u65b0\u505a\u4eba\uff0c\u4e5f\u5df2\u7edd\u65e0\u673a\u4f1a\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ef\u662f\u4f60\u7ed9\u4e86\u4ed6\u4eec\u4e00\u4e2a\u673a\u4f1a\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u6211\u53ea\u7ed9\u4e86\u4ed6\u4eec\u4e00\u6b21\u673a\u4f1a\uff0c\u4e0d\u662f\u4e00\u4e2a\u673a\u4f1a\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u90a3\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u60f3\u91cd\u65b0\u505a\u4eba\uff0c\u4e0d\u662f\u505a\u6b7b\u4eba\u3002\u201d \u2014\u6d3b\u5728\u5e7d\u7075\u5c71\u5e84\u4e2d\u7684\u4eba\uff0c\u548c\u6b7b\u53c8\u6709\u4ec0\u4e48\u5206\u522b\uff1f \u2014\u2014\u53ea\u6709\u6bc1\u4e86\u90a3\u5e10\u7c3f\uff0c\u4ed6\u4eec\u624d\u771f\u6b63\u6709\u91cd\u65b0\u505a\u4eba\u7684\u673a\u4f1a\u3002 \u8001\u5200\u628a\u5b50\u63e1\u7d27\u53cc\u624b\uff0c\u9053\uff1a\u201c\u8fd9\u624d\u662f\u6211\u8fd9\u6b21\u884c\u52a8\u7684\u6700\u5927\u76ee\u7684\uff0c\u6211\u4eec\u53ea\u8bb8\u6210\u529f\uff0c\u4e0d\u8bb8 \u5931\u8d25\uff01 \u201c\u535c\u201d\u7684\u2014\u58f0\uff0c\u9152\u676f\u5728\u4ed6\u638c\u4e2d\u7c89\u788e\uff0c\u4e00\u4e1d\u9c9c\u8840\u4ece\u6307\u7f1d\u95f4\u6d41\u4e86\u51fa\u6765\u3002 \u9646\u5c0f\u51e4\u770b\u7740\u8fd9\u4e00\u4e1d\u9c9c\u7ea2\u7684\u8840\uff0c\u5ffd\u7136\u53d8\u5f97\u6c89\u9ed8\u4e86\u8d77\u6765\u3002 \u56e0\u4e3a\u4ed6\u5fc3\u91cc\u6b63\u5728\u95ee\u81ea\u5df1\u8001\u5200\u628a\u5b50\u8fd9\u4ef6\u4e8b\uff0c\u662f\u4e0d\u662f\u505a\u5f97\u6b63\u786e\uff1f \u5982\u679c\u662f\u6b63\u786e\u7684\uff0c\u4e00\u4e2a\u6b63\u76f4\u7684\u4eba\uff0c\u662f\u4e0d\u662f\u5c31\u5e94\u8be5\u5168\u529b\u5e2e\u52a9\u4ed6\u5b8c\u6210\u8fd9\u4ef6\u4e8b\uff1f \u6b66\u5f53\u662f\u540d\u95e8\u6b63\u5b97\uff0c\u6885\u771f\u4eba\u548c\u77f3\u96c1\u4e00\u5411\u53d7\u4eba\u5c0a\u656c\uff0c\u4ed6\u4ece\u672a\u6000\u7591\u8fc7\u4ed6\u4eec\u7684\u4eba\u683c\u3002 \u53ef\u662f\u73b0\u5728\u6c60\u5bf9\u6240\u6709\u7684\u4e8b\u90fd\u5df2\u5fc5\u987b\u91cd\u65b0\u4f30\u8ba1\u3002 \u8001\u5200\u628a\u5b50\u76ef\u7740\u4ed6\uff0c\u4eff\u4f5b\u60f3\u770b\u51fa\u4ed6\u5fc3\u5e95\u6700\u6df1\u5904\u5728\u60f3\u4ec0\u4e48\u3002 \u9646\u5c0f\u51e4\u7a76\u7adf\u5728\u60f3\u4ec0\u4e48\uff1f\u8c01\u77e5\u9053\uff1f \u8001\u5200\u628a\u5b50\u7f13\u7f13\u9053\uff1a\u201c\u6211\u5f88\u4e86\u89e3\uff0c\u4f60\u82e5\u4e0d\u662f\u771f\u7684\u613f\u610f\u53bb\u505a\u4e00\u4ef6\u4e8b\uff0c\u8c01\u4e5f\u6ca1\u6cd5\u5b50\u52c9\u5f3a \u4f60\uff0c\u6240\u4ee5\u6211\u4e00\u5b9a\u8981\u4f60\u4e86\u89e3\u8fd9\u4ef6\u4e8b\u7684\u771f\u8c61\u3002\u201d \u9646\u5c0f\u51e4\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u65e2\u7136\u4f60\u7684\u76ee\u7684\u662f\u4e3a\u4e86\u6551\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u6740\u4eba\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u6211\u8981\u6740\u7684\uff0c\u53ea\u662f\u4e00\u4e9b\u975e\u6740\u4e0d\u53ef\u7684\u4eba\uff01\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u738b\u5341\u888b\u3001\u9ad8\u884c\u7a7a\u3001\u6c34\u4e0a\u98de\uff0c\u8fd9\u4e9b\u4eba\u90fd\u975e\u6740\u4e0d\u53ef\uff1f\u201d \u8001\u5200\u628a\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u6211\u95ee\u4f60\uff0c\u53ea\u51ed\u6885\u771f\u4eba\u548c\u77f3\u96c1\u7684\u4eb2\u4fe1\u5f1f\u5b50\uff0c\u600e\u4e48\u67e5\u5f97\u51fa\u90a3\u4e48\u591a \u4eba\u7684\u9690\u79c1\u79d8\u5bc6\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u96be\u9053\u4f60\u8981\u6740\u7684\u8fd9\u4e9b\u4eba\uff0c\u90fd\u662f\u4ed6\u4eec\u7684\u5bc6\u63a2\uff1f\u201d \u8001\u5200\u628a\u5b50\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u8fd9\u4e9b\u4eba\u672c\u8eab\u4e5f\u6709\u9690\u79c1\u88ab\u4ed6\u4eec\u634f\u5728\u624b\u91ccoo\u9646\u5c0f\u51e4\u4e5f\u63e1 \u7d27\u4e86\u53cc\u624b\uff0c\u7ec8\u4e25\u95ee\u9053\uff1a\u201c\u90a3\u672c\u5e10\u7c3f\u5728\u54ea\u91cc\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u5c31\u5728\u77f3\u96c1\u5934\u4e0a\u6234\u7740\u7684\u9053\u51a0\u91cc\u3002\u201d \u9646\u5c0f\u51e4\u7684\u5fc3\u6c89\u4e86\u4e0b\u53bb\u3002 \u6b66\u5f53\u77f3\u96c1\u5c11\u5e74\u65f6\u5c31\u5df2\u662f\u6c5f\u6e56\u4e2d\u6781\u8d1f\u76db\u540d\u7684\u5251\u5ba2\uff0c\u8fd1\u5e74\u6765\u529f\u529b\u4fee\u4e3a\u66f4\u6709\u7cbe\u8fdb\uff0c\u5e73\u65f6 \u867d\u7136\u7edd\u5c11\u51fa\u624b\uff0c\u636e\u4e00\u822c\u4f30\u8ba1\uff0c\u4ed6\u7684\u5251\u6cd5\u5df2\u5728\u6728\u9053\u4eba\u4e4b\u4e0a\u3002 \u897f\u95e8\u5439\u96ea\u8bf4\u7684\u4e09\u4e2a\u4eba\u5176\u4e2d\u65e0\u7591\u5c31\u6709\u4ed6\u3002 \u6b66\u5f53\u638c\u95e8\u7684\u9053\u51a0\uff0c\u4e0d\u4f46\u8c61\u5f81\u7740\u6b66\u5f53\u4e00\u6d3e\u7684\u5c0a\u4e25\uff0c\u672c\u8eab\u5c31\u5df2\u662f\u65e0\u4ef7\u4e4b\u5b9d\uff0c\u4f55\u51b5\u9053\u51a0 \u4e2d\u8fd8\u85cf\u7740\u6709\u90a3\u4e48\u5927\u7684\u79d8\u5bc6\uff1f \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u6211\u4e5f\u77e5\u9053\u8981\u4ece\u4ed6\u5934\u4e0a\u6458\u4e0b\u90a3\u9876\u9053\u51a0\u6765\u5e76\u4e0d\u5bb9\u6613\u3002 \u90a3\u53c8\u5c82\u53ea\u662f\u4e0d\u5bb9\u6613\uff0c\u90a3\u7b80\u76f4\u96be\u5982\u767b\u5929\u6458\u6708\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u4eec\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u5728\u4ed6\u6234\u7740\u9053\u51a0\u65f6\u52a8\u624b\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\u90a3\u662f\u6211\u4eec\u552f\u4e00\u7684\u673a\u4f1aqH\u4ed6\u6709\u5f88\u5145\u8db3\u7684\u7406\u7531\u89e3\u91ca\uff0c\u201c\u56e0\u4e3a\u9664\u4e86 \u4ed6\u81ea\u5df1\u4e4b\u5916\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u4ed6\u5e73\u65f6\u5c06\u8fd9\u9876\u9053\u51a0\u85cf\u5728\u54ea\u91cc\u3002\u201d \u9646\u5c0f\u51e4\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u6211\u505a\u4e0d\u5230\u3002\u201d \u90a3\u4e00\u5929\u6b66\u5f53\u9053\u89c2\u7684\u5927\u6bbf\u4e2d\uff0c\u706f\u706b\u901a\u660e\uff0c\u9ad8\u624b\u5982\u4e91\uff0c\u8981\u5728\u4f17\u76ee\u77aa\u77aa\u4e4b\u4e0b\u4ece\u6b66\u5f53\u638c\u6559 \u771f\u4eba\u7684\u5934\u4e0a\u6458\u4e0b\u4ed6\u7684\u9053\u51a0\u6765\uff0c\u8fd9\u79cd\u4e8b\u6709\u8c01\u80fd\u505a\u5f97\u5230\uff01 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u53ea\u6709\u4f60\uff0c\u4f60\u4e00\u5b9a\u80fd\u505a\u5230\uff01\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u5c31\u7b97\u6211\u80fd\u6458\u4e0b\u6765\uff0c\u4e5f\u7edd\u5bf9\u6ca1\u6cd5\u5b50\u5e26\u7740\u5b83\u5728\u4f17\u76ee\u77aa\u77aa\u4e4b\u4e0b\u9003\u51fa\u53bb\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4e0d\u662f\u5728\u4f17\u76ee\u668c\u668c\u4e4b\u4e0b\uff0c\u4f60\u51fa\u624b\u65f6\uff0c\u6ca1\u6709\u4eba\u80fd\u770b\u5f97\u89c1\u4f60\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u770b\u4e0d\u89c1\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\u90a3\u65f6\u5927\u6bbf\u5185\u5916\u4e03\u5341\u4e8c\u76cf\u957f\u660e\u706f\u4e00\u5b9a\u4f1a\u540c\u65f6\u7184\u706d\u3002\u201d \u2014\u706f\u91cc\u7684\u6cb9\u5e72\u4e86\uff0c\u706f\u81ea\u7136\u4f1a\u7184\u706d\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u6211\u4eec\u81f3\u5c11\u5df2\u8bd5\u9a8c\u7740\u516b\u767e\u6b21\uff0c\u7b97\u51c6\u4e86\u706f\u91cc\u82e5\u53ea\u6709\u4e00\u4e24\u4e09\u94b1\u6cb9\uff0c\u5c31\u4e00 \u5b9a\u4f1a\u5728\u4ed6\u5ba3\u5e03\u7ee7\u627f\u4eba\u7684\u65f6\u5019\u71c3\u5c3d\uff0c\u6211\u4eec\u5728\u6b66\u5f53\u7684\u5185\u7ebf\uff0c\u5230\u65f6\u4e00\u5b9a\u4f1a\u4f7f\u6bcf\u76cf\u706f\u91cc\u7684\u6cb9\u90fd \u53ea\u6709\u4e00\u4e24\u4e09\u94b1ao\u8fd9\u8ba1\u5212\u5b9e\u5728\u5468\u5bc6\uff0c\u201c\u53ef\u662f\u5927\u6bbf\u4e2d\u4e00\u5b9a\u8fd8\u6709\u70b9\u71c3\u7684\u8721\u70db\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u8fd9\u4e00\u70b9\u7531\u82b1\u9b41\u8d1f\u8d23\uff0c\u4ed6\u6ee1\u5929\u82b1\u96e8\u7684\u6697\u5668\u624b\u6cd5\uff0c\u5df2\u65e0\u4eba\u80fd\u53ca\u3002\u201d \u73b0\u5728\u8fd9\u8ba1\u5212\u51e0\u4e4e\u5df2\u5929\u8863\u65e0\u7f1d\u3002 \u706f\u706d\u65f6\u5927\u6bbf\u4e2d\u9aa4\u7136\u9ed1\u6697\uff0c\u5927\u5bb6\u5fc5\u5b9a\u96be\u514d\u60ca\u60f6\uff0c\u5c31\u5728\u8fd9\u7247\u523b\u95f4\uff0c\u9646\u5c0f\u51e4\u51fa\u624b\u593a\u9053 \u51a0\uff0c\u77f3\u9e64\u6740\u77f3\u96c1\uff0c\u65e0\u864e\u5144\u5f1f\u6740\u94c1\u80a9\uff0c\u8868\u54e5\u6740\u5c0f\u987e\u9053\u4eba\uff0c\u7ba1\u5bb6\u5a46\u6740\u9e70\u773c\u8001\u4e03\uff0c\u6d77\u5947\u9614\u6740 \u6c34\u4e0a\u98de\uff0c\u5173\u5929\u6b66\u6740\u9ad8\u884c\u7a7a\uff0c\u675c\u94c1\u5fc3\u6740\u738b\u5341\u888b\uff01 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u65e0\u8bba\u4ed6\u4eec\u662f\u5426\u80fd\u5f97\u624b\uff0c\u7b49\u5230\u706f\u706b\u518d\u4eae\u8d77\u65f6\uff0c\u4ed6\u4eec\u5c31\u90fd\u5df2\u5168\u8eab\u800c \u9000\u3002\u201d \u53ea\u8981\u4e00\u51fb\u4e0d\u4e2d\uff0c\u5c31\u5168\u8eab\u800c\u9000\uff01 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u4e5f\u4e00\u6837\uff0c\u7eb5\u7136\u9053\u51a0\u4e0d\u80fd\u5f97\u624b\uff0c\u4f60\u4e5f\u4e00\u5b9a\u8981\u8d70\uff0c\u56e0\u4e3a\u5728\u90a3\u79cd\u60c5\u51b5 \u4e0b\uff0c\u65e0\u8bba\u4efb\u4f55\u4eba\u90fd\u7edd\u6ca1\u6709\u7b2c\u4e8c\u6b21\u51fa\u624b\u7684\u673a\u4f1a\u3002\u201d \u4ed6\u53c8\u8865\u5145\u7740\u9053\uff1a\u201c\u65e0\u8bba\u4f60\u662f\u5426\u5f97\u624b\uff0c\u90fd\u8981\u7acb\u523b\u8d76\u56de\u8fd9\u91cc\u6765\uff0c\u706f\u4eae\u4e4b\u540e\uff0c\u5927\u5bb6\u90fd\u4e00 \u5b9a\u53ea\u4f1a\u53bb\u7167\u987e\u5df2\u8d1f\u4e86\u4f24\u7684\u53cb\u4f34\u540c\u95e8\uff0c\u8c01\u90fd\u4e0d\u4f1a\u6ce8\u610f\u5230\u5927\u6bbf\u4e2d\u5df2\u5c11\u4e86\u4e9b\u4ec0\u4e48\u4eba\uff0c\u66f4\u4e0d\u4f1a \u6709\u4eba\u8ffd\u8e2a\u201d \u4f55\u51b5\u90a3\u65f6\u6839\u672c\u8fd8\u6ca1\u6709\u4eba\u77e5\u9053\u8fd9\u4ef6\u4e8b\u7a76\u7adf\u662f\u600e\u4e48\u4f1a\u53d1\u751f\u7684\u9646\u5c0f\u51e4\u53c8\u4e0d\u7981\u957f\u957f\u53f9\u4e86\u53e3 \u6c14\u9053\uff1a\u201c\u6211\u4f69\u670d\u4f60JU\u4ed6\u8fd9\u2014\u751f\u4e2d\uff0c\u4e5f\u4e0d\u77e5\u63d2\u624b\u8fc7\u591a\u5c11\u4ef6\u9634\u8c0b\uff0c\u7edd\u6ca1\u6709\u4efb\u4f55\u2014\u6b21\u80fd\u6bd4\u5f97 \u4e0a\u8fd9\u4e00\u6b21\u3002 \u8fd9\u8ba1\u5212\u51e0\u4e4e\u5df2\u5b8c\u5168\u65e0\u61c8\u53ef\u51fb\u3002 \u53ef\u662f\u4ed6\u8fd8\u6709\u51e0\u70b9\u8981\u95ee\uff0c\u201c\u6211\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u5148\u6740\u4e86\u77f3\u96c1\uff0c\u518d\u53d6\u4ed6\u9876\u4e0a\u7684\u9053\u51a0\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4eec\u6ca1\u6709\u4e00\u51fb\u5c31\u80fd\u547d\u4e2d\u7684\u628a\u63e1J\u2019\u2019\u8fd9\u4ef6\u4e8b\u5374\u53ea\u8bb8\u6210\u529f\uff0c\u4e0d\u8bb8 \u5931\u8d25\uff01\u8fd9\u4ef6\u4e8b\u7684\u786e\u5df2\u8017\u5c3d\u4e86\u4ed6\u2014\u751f\u5fc3\u8840\u3002 \u9646\u5c0f\u51e4\u53c8\u95ee\uff0c\u201c\u82e5\u6ca1\u6709\u6211\uff0c\u6211\u7684\u5dee\u4f7f\u8c01\u505a\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u53f6\u96ea\uff01\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u4f1a\u662f\u5979\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u5979\u7684\u8f7b\u529f\u6781\u9ad8\uff0c\u53c8\u662f\u5929\u751f\u7684\u591c\u773c\uff0c\u5728\u77f3\u96c1\u9aa4\u51fa\u4e0d\u610f\u4e4b\u4e0b\uff0c\u5979\u81f3\u5c11 \u6709\u4e03\u516b\u6210\u5f97\u624b\u7684\u673a\u4f1a\u3002\uff0c\u2019\u4ed6\u5ffd\u7136\u7528\u529b\u63e1\u4f4f\u4e86\u9646\u5c0f\u51e4\u7684\u624b\uff0c\u201c\u4f60\u5374\u6709\u4e5d\u6210\u673a\u4f1a\uff0c\u751a\u81f3 \u8fd8\u4e0d\u6b62\u4e5d\u6210\uff0c\u6211\u77e5\u9053\u4f60\u4e5f\u6709\u5728\u9ed1\u6697\u4e2d\u660e\u5bdf\u79cb\u6beb\u7684\u672c\u4e8b\uff0c\u800c\u4e14\u4f60\u8fd8\u6709\u8fd9\u4e00\u53ea\u5929\u4e0b\u65e0\u53cc\u7684 \u624b\uff01\u201d \u4ed6\u63e1\u7740\u8fd9\u53ea\u624b\uff0c\u5c31\u597d\u50cf\u5728\u63e1\u7740\u4ef6\u65e0\u4ef7\u7684\u73cd\u5b9d\u3002 \u9646\u5c0f\u51e4\u5374\u5728\u770b\u7740\u4ed6\u7684\u624b\u3002 \u4ed6\u7684\u624b\u7626\u524a\u3001\u7a33\u5b9a\u3001\u5e72\u71e5\uff0c\u624b\u6307\u957f\u800c\u6709\u529b\u3002 \u82e5\u662f\u63e1\u4f4f\u4e86\u4e00\u67c4\u5408\u624b\u7684\u5251\uff0c\u8fd9\u53ea\u624b\u662f\u4e0d\u662f\u6bd4\u897f\u95e8\u5439\u96ea\u7684\u624b\u66f4\u53ef\u6015\uff1f \u8fd9\u4e2a\u4eba\u7a76\u7adf\u662f\u8c01\uff1f \u73b0\u5728\u9646\u5c0f\u51e4\u82e5\u662f\u53cd\u8155\u62ff\u4f4f\u4ed6\u7684\u8109\u95e8\uff0c\u6458\u4e0b\u4ed6\u5934\u4e0a\u7684\u7af9\u7b7e\uff0c\u7acb\u523b\u5c31\u53ef\u4ee5\u77e5\u9053\u4ed6\u662f\u8c01 \u4e86\u3002 \u6210\u529f\u673a\u4f1a\u5c31\u7b97\u4e0d\u5927\uff0c\u81f3\u5c11\u4e5f\u8be5\u8bd5\u4e00\u8bd5\u3002 \u4f46\u662f\u9646\u5c0f\u51e4\u6ca1\u6709\u8bd5\u3002 \u8fd9\u4f7f\u5f97\u4ed6\u5bf9\u81ea\u5df1\u5f88\u6124\u6012\uff0c\u5ffd\u7136\u5927\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u96be\u9053\u4ece\u6765\u90fd\u6ca1\u6709\u60f3\u5230\u8fc7\u5979\u7684\u6b7b\u6d3b\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u8c01\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u662f\u4f60\u7684\u5973\u513f\uff0c\u53f6\u96ea\u3002\u201d \u8001\u5200\u628a\u5b50\u6de1\u6de1\u9053\uff1a\u201c\u60f3\u4e86\u4e5f\u6ca1\u6709\u7528\u7684\u4e8b\uff0c\u53c8\u4f55\u5fc5\u53bb\u60f3\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u5979\u7684\u6bcd\u4eb2\u6b7b\u4e86\u4e4b\u540e\u8fd8\u88ab\u2026\u2026\u201d \u8001\u5200\u628a\u5b50\u7acb\u523b\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u76ee\u5149\u5200\u950b\u822c\u5728\u7af9\u7b20\u540e\u6012\u89c6\u7740\u4ed6\uff0c\u201c\u4f60\u53ef\u4ee5\u8981\u6211\u66ff\u4f60 \u505a\u4efb\u4f55\u4e8b\uff0c\u4f46\u662f\u4f60\u4ee5\u540e\u5343\u4e07\u4e0d\u8981\u5728\u6211\u9762\u524d\u518d\u63d0\u8d77\u8fd9\u4e2a\u5973\u4eba\u3002\u201d \u4e3a\u4ec0\u4e48\uff1f \u6c88\u4e09\u5a18\u662f\u53f6\u51cc\u98ce\u7684\u59bb\u5b50\uff0c\u5374\u4e3a\u4ed6\u751f\u4e86\u4e00\u4e2a\u5973\u513f\uff0c\u5979\u5bf9\u4e0d\u8d77\u7684\u662f\u53f6\u51cc\u98ce\uff0c\u5e76\u4e0d\u662f \u4ed6\u3002 \u2014\u4ed6\u4e3a\u4ec0\u4e48\u5982\u6b64\u6068\u5979\uff1f \u9646\u5c0f\u51e4\u60f3\u4e0d\u901a\uff0c\u60f3\u4e86\u5f88\u4e45\u90fd\u60f3\u4e0d\u901a\u3002 \u8001\u5200\u628a\u5b50\u7684\u6124\u6012\u5f88\u5feb\u5c31\u88ab\u6291\u5236\uff0c\u201c\u660e\u5929\u767d\u5929\u6ca1\u6709\u4e8b\uff0c\u968f\u4fbf\u4f60\u60f3\u5e72\u4ec0\u4e48\u90fd\u65e0\u59a8\uff0c\u540e \u5929\u51cc\u6668\u4e4b\u524d\uff0c\u6211\u4f1a\u5b89\u6392\u4f60\u5230\u6b66\u5f53\u53bb\u3002\u201d \u4ed6\u7ad9\u8d77\u6765\uff0c\u663e\u7136\u5df2\u51c6\u5907\u7ed3\u675f\u8fd9\u6b21\u8c08\u8bdd\uff1a\u201c\u90a3\u91cc\u9999\u706b\u9053\u4eba\u7684\u603b\u7ba1\u53eb\u5f6d\u957f\u51c0\uff0c\u4f60\u5230\u4e86 \u540e\u5c71\uff0c\u65e0\u8bba\u4ec0\u4e48\u4e8b\u4ed6\u90fd\u4f1a\u66ff\u4f60\u5b89\u6392\u7684\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u7136\u540e\u5462\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u7136\u540e\u4f60\u5c31\u53ea\u6709\u5728\u90a3\u91cc\u7b49\u7740\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u7b49\u706f\u706d\u7684\u65f6\u5019\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u7b49\u706f\u706d\u7684\u65f6\u5019\uff1f\u201d \u4ed6\u8d70\u51fa\u53bb\uff0c\u53c8\u56de\u8fc7\u5934\uff0c\u201c\u4ece\u73b0\u5728\u5f00\u59cb\uff0c\u4f60\u5c31\u5b8c\u5168\u5355\u72ec\u884c\u52a8\uff0c\u7528\u4e0d\u7740\u518d\u8ddf\u4efb\u4f55\u4eba\u8fde \u7edc\uff0c\u4e5f\u4e0d\u4f1a\u518d\u6709\u4eba\u6765\u627e\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u4ece\u73b0\u5728\u5f00\u59cb\uff0c\u8fde\u6211\u7684\u8001\u5a46\u513f\u5b50\u6211\u90fd\u5df2\u89c1\u4e0d\u5230\u4e86\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f46\u662f\u4f60\u4e0d\u4f1a\u5bc2\u5bde\u7684\uff0c\u4f60\u8fd8\u6709\u5f88\u591a\u5916\u7525\u5973\u3002\u201d \u7b2c\u5341\u56db\u7ae0\u3000\u98ce\u6d41\u8205\u8205>> \u53e4\u9f99\u300a\u9646\u5c0f\u51e4\u7cfb\u5217\xb7\u5e7d\u7075\u5c71\u5e84\u300b \u7b2c\u5341\u56db\u7ae0\u3000\u98ce\u6d41\u8205\u8205 \u56db\u6708\u5341\u4e09\uff0c\u9ece\u660e\u524d\uff0c\u6b66\u5f53\u540e\u5c71\u4e00\u7247\u9ed1\u6697\uff0c\u8fc7\u4e86\u534a\u5c71\u540e\uff0c\u98ce\u4e2d\u5c31\u5df2\u6709\u4e86\u5bd2\u610f\u3002 \u9759\u591c\u7a7a\u5c71\uff0c\u2014\u7f15\u7f15\u767d\u70df\u4ece\u8db3\u4e0b\u5347\u8d77\uff0c\u4e5f\u4e0d\u77e5\u662f\u4e91\uff1f\u8fd8\u662f\u96fe\uff1f \u8fdc\u8fdc\u770b\u8fc7\u53bb\uff0c\u4f9d\u7a00\u5df2\u53ef\u770b\u89c1\u90a3\u53e4\u8001\u9053\u89c2\u5e84\u4e25\u5dcd\u5ce8\u7684\u5f71\u5230\u4e86\u8fd9\u91cc\uff0c\u5e26\u8def\u7684\u4eba\u5c31\u8d70 \u4e86\uff0c\u201c\u4f60\u5728\u8fd9\u91cc\u7b49\u7740\uff0c\u5f88\u5feb\u5c31\u4f1a\u6709\u4eba\u6765\u63a5\u5e94\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u5e76\u6ca1\u6709\u591a\u95ee\uff0c\u4e5f\u4e0d\u60f3\u77e5\u9053\u8fd9\u4e2a\u4eba\u662f\u8c01\uff0c\u4eca\u5929\u867d\u7136\u662f\u4e2a\u5927\u65e5\u5b50\uff0c\u4ed6\u7684\u7cbe\u795e\u5e76 \u4e0d\u592a\u597d\u3002 \u4ed6\u7684\u5916\u7525\u5973\u5b9e\u5728\u592a\u591a\u3002 \u5e78\u597d\u4ed6\u5e76\u6ca1\u6709\u7b49\u591a\u4e45\uff0c\u9ed1\u6697\u4e2d\u5c31\u6709\u4eba\u538b\u4f4e\u4e86\u58f0\u97f3\u5728\u95ee\uff1a\u201c\u4f60\u6765\u5e72\u4ec0\u4e48\u7684\uff1f\u201d \u8fd9\u662f\u4ed6\u4eec\u7ea6\u5b9a\u7684\u6697\u53f7\uff0c\u56de\u7b54\u5e94\u8be5\u662f\u6765\u627e\u8c46\u5b50\uff0c\u5341\u4e09\u9897\u8c46\u9ed1\u6697\u4e2d\u679c\u7136\u7acb\u523b\u51fa\u73b0\u4e86\u4e00 \u4e2a\u4eba\uff0c\u9646\u5c0f\u51e4\u518d\u95ee\uff0c\u201c\u4f60\u662f\u8c01\uff1f\u201d \u201c\u5f6d\u957f\u51c0\u3002\u201d \u5f6d\u957f\u51c0\u770b\u6765\u7adf\u771f\u7684\u6709\u70b9\u50cf\u662f\u4e2a\u8c46\u5b50\uff0c\u5706\u5706\u7684\uff0c\u5c0f\u5c0f\u7684\uff0c\u773c\u775b\u5f88\u4eae\uff0c\u52a8\u4f5c\u5f88\u7075\u654f\uff0c \u5f88\u5feb\u7684\u6253\u91cf\u4e86\u9646\u5c0f\u51e4\u4e24\u773c\uff0c\u5c31\u677f\u7740\u8138\u9053\uff1a\u201c\u4f60\u559d\u8fc7\u9152\u3002\u201d \u9646\u5c0f\u51e4\u5f53\u7136\u559d\u8fc7\u9152\uff0c\u559d\u7684\u8fd8\u4e0d\u5c11\u3002 \u5f6d\u957f\u51c0\u9053\uff1a\u201c\u8fd9\u91cc\u4e0d\u51c6\u559d\u9152\uff0c\u4e0d\u51c6\u8bf4\u7c97\u8bdd\uff0c\u4e0d\u51c6\u770b\u5973\u4eba\uff0c\u8d70\u8def\u4e0d\u51c6\u592a\u5feb\uff0c\u8bf4\u8bdd\u4e0d \u51c6\u592a\u664c\u3002\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\uff0c\u201c\u8fd9\u91cc\u51c6\u4e0d\u51c6\u653e\u5c41\uff1f\u201d \u5f6d\u957f\u51c0\u6c89\u4e0b\u8138\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u4f60\u4ee5\u524d\u662f\u5e72\u4ec0\u4e48\u7684\uff0c\u6211\u4e5f\u4e0d\u60f3\u77e5\u9053\uff0c\u5230\u4e86\u8fd9 \u91cc\uff0c\u4f60\u5c31\u5f97\u5b88\u8fd9\u91cc\u7684\u89c4\u77e9\u3002\u201d \u9646\u5c0f\u51e4\u4e0d\u7b11\u4e86\uff0c\u4e5f\u5df2\u7b11\u4e0d\u51fa\u3002 \u4ed6\u77e5\u9053\u4ed6\u53c8\u9047\u89c1\u4e86\u4e00\u4e2a\u5f88\u96be\u5bf9\u4ed8\u7684\u4eba\u3002 \u5f6d\u957f\u51c0\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4ef6\u4e8b\u4f60\u6700\u597d\u4e5f\u8bb0\u4f4f\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u5f6d\u957f\u51c0\u9053\uff1a\u201c\u5230\u4e86\u5c71\u4e0a\uff0c\u4f60\u5c31\u53bb\u8499\u5934\u5927\u7761\uff0c\u5343\u4e07\u4e0d\u8981\u8ddf\u522b\u4eba\u6253\u4ea4\u9053\uff0c\u4e07\u4e00\u6709\u4eba\u95ee \u8d77\u4f60\uff0c\u4f60\u5c31\u8bf4\u662f\u6211\u627e\u4f60\u6765\u5e2e\u5fd9\u7684\u3002\u201d \u4ed6\u60f3\u4e86\u60f3\uff0c\u53c8\u9053\uff1a\u201c\u6211\u7684\u5e08\u5f1f\u957f\u6e05\u662f\u4e2a\u5f88\u5389\u5bb3\u7684\u4eba\uff0c\u4e07\u4e00\u4f60\u9047\u4e0a\u4ed6\uff0c\u8bf4\u8bdd\u66f4\u8981\u5c0f \u5fc3\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u4f1a\u5f88\u5c0f\u5fc3\uff0c\u5f88\u5c0f\u5fc3\u7684\u3002\u201d \u5f6d\u957f\u51c0\u9053\uff1a\u201c\u597d\uff0c\u4f60\u8ddf\u6211\u6765\u3002\u201d \u4ed6\u4e0d\u4f46\u52a8\u4f5c\u7075\u654f\uff0c\u8f7b\u529f\u4e5f\u5f88\u4e0d\u9519\u3002 \u9646\u5c0f\u51e4\u5b9e\u5728\u6ca1\u60f3\u5230\u4e00\u4e2a\u706b\u5de5\u9053\u4eba\u7684\u603b\u7ba1\uff0c\u7adf\u6709\u8fd9\u4e48\u597d\u7684\u8eab\u624b\u3002 \u5f6d\u957f\u51c0\u5374\u66f4\u610f\u5916\uff0c\u9646\u5c0f\u51e4\u5c45\u7136\u80fd\u8ddf\u5f97\u4e0a\u4ed6\uff0c\u65e0\u8bba\u4ed6\u591a\u5feb\uff0c\u9646\u5c0f\u51e4\u59cb\u7ec8\u90fd\u80fd\u8ddf\u4ed6\u4fdd \u6301\u7740\u540c\u6837\u7684\u8ddd\u79bb\u3002 \u8001\u5200\u628a\u5b50\u663e\u7136\u6ca1\u6709\u5c06\u9646\u5c0f\u51e4\u7684\u6765\u5386\u8eab\u5206\u544a\u8bc9\u4ed6\u3002 \u9664\u4e86\u8001\u5200\u628a\u5b50\u81ea\u5df1\u4e4b\u5916\uff0c\u6bcf\u4e2a\u4eba\u77e5\u9053\u7684\u597d\u50cf\u90fd\u4e0d\u592a\u591a\u3002 \u6240\u4ee5\u5176\u4e2d\u5c31\u7b97\u6709\u4e00\u4e24\u4e2a\u4eba\u5931\u4e86\u98ce\uff0c\u4e5f\u4e0d\u81f4\u4e8e\u5f71\u54cd\u6574\u4e2a\u8ba1\u5212\u3002 \u5929\u8fd8\u6ca1\u6709\u4eae\uff0c\u540e\u5c71\u7684\u9999\u79ef\u53a8\u91cc\u5df2\u6709\u4eba\u5f00\u59cb\u5de5\u4f5c\uff0c\u6dd8\u7c73\u3001\u751f\u706b\u3001\u6d17\u83dc\u3001\u71ac\u7ca5\uff0c\u6bcf\u4e2a \u4eba\u90fd\u5728\u9ed8\u9ed8\u7684\u505a\u81ea\u5df1\u7684\u4e8b\uff0c\u5f88\u5c11\u6709\u4eba\u5f00\u53e3\u8bf4\u8bdd\u7684\u3002 \u8fd9\u4f4d\u5f6d\u603b\u7ba1\u5bf9\u4ed6\u5c5e\u4e0b\u7684\u706b\u5de5\u9053\u4eba\u8ba2I\uff0c\u60f3\u5fc5\u6bd4\u5bf9\u9646\u5c0f\u51e4\u66f4\u4e0d\u5ba2\u6c14\u3002 \u9999\u79ef\u53a8\u540e\u9762\uff0c\u6709\u4e24\u6392\u6728\u5c4b\uff0c\u6700\u65c1\u8fb9\u7684\u4e00\u95f4\uff0c\u5c4b\u91cc\u5806\u7740\u4e00\u7bd3\u7bd3\u8fd8\u6ca1\u6709\u5b8c\u5168\u6652\u5e72\u7684\u8106 \u841d\uff1e\uff0c\u5c4b\u89d2\u6446\u7740\u5f20\u7834\u65e7\u7684\u7af9\u5e8a\u3002 \u5f6d\u957f\u51c0\u9053\uff1a\u201c\u4f60\u5c31\u7761\u5728\u8fd9\u91cc\u3002\u201d \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u8981\u95ee\uff0c\u201c\u7761\u5230\u4ec0\u4e48\u65f6\u5019\uff1f\u201d \u5f6d\u957f\u51c0\u9053\uff1a\u201c\u7761\u5230\u6211\u6765\u627e\u4f60\u7684\u65f6\u5019\uff0c\u53cd\u6b63\u8fd9\u91cc\u6709\u5403\u7684\u3002\u201d \u9646\u5c0f\u51e4\u5148\u5403\u4e86\u4e00\u60ca\uff0c\u201c\u5486\u8fd9\u4e9b\u8155\u841d\uff1e\uff1f\u201d \u5f6d\u51c0\u51b7\u51b7\u9053\uff1a\u201c\u814c\u841d\u535c\u4e5f\u662f\u4eba\u5403\u7684\u3002\u201d \u9646\u5c0f\u51e4\u4e86\u53e3\u6c14\uff0c\u82e6\u7b11\u7740\u5583\u5583\u9053\uff1a\u201c\u6211\u53ea\u6015\u841d\u535c\u5403\u591a\u4e86\u4f1a\u653e\u5c41\u3002\u201d \u5f6d\u957f\u51c0\u9053\uff1a\u201c\u4f60\u53ef\u4ee5\u4e0d\u5403\uff0c\u5c31\u7b97\u997f\u4e00\u5929\uff0c\u4e5f\u997f\u4e0d\u6b7b\u4eba\u7684\u3002\u201d \u4ed6\u5df2\u51c6\u5907\u8d70\u4e86\uff0c\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u4e0d\u660e\u767d\u4e8b\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ea\u6709\u4e00\u4ef6\u4e8b\u3002\u201d \u5f6d\u957f\u51c0\u9053\uff1a\u201c\u4f60\u8bf4\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u53ea\u5947\u602a\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u6539\u884c\u505a\u7262\u5934\u53bbZ\u201d \u95ee\u5b8c\u4e86\u4ed6\u5c31\u5f80\u7af9\u5e8a\u4e0a\u4e00\u8eba\uff0c\u7528\u8584\u88ab\u76d6\u4f4f\u4e86\u5934\uff0c\u6b7b\u4eba\u4e5f\u4e0d\u7ba1\u4e86\u3002 \u53ea\u542c\u623f\u95e8\u201c\u7830\u201d\u7684\u4e00\u58f0\u54cd\uff0c\u5f6d\u957f\u51c0\u53ea\u6709\u628a\u6c14\u51fa\u5728\u8fd9\u6247\u6728\u677f\u95e8\u4e0a\u3002 \u9646\u5c0f\u51e4\u7b11\u4e86\u3002 \u5bf9\u4ed8\u8fd9\u79cd\u4eba\uff0c\u4f60\u53ea\u6709\u60f3\u6cd5\u5b50\u6c14\u6c14\u4ed6\uff0c\u53ea\u8981\u6709\u4e00\u70b9\u673a\u4f1a\u80fd\u8ba9\u4ed6\u751f\u6c14\uff0c\u5c31\u5343\u4e07\u4e0d\u8981\u9519 \u8fc7\uff0c\u6700\u597d\u80fd\u8ba9\u4ed6\u6c14\u5f97\u534a\u6b7b\u3002 \u53ef\u662f\u8fd9\u5e8a\u68c9\u88ab\u5374\u5df2\u5148\u628a\u9646\u5c0f\u51e4\u81ed\u5f97\u534a\u6b7b\uff0c\u4ed6\u4f38\u51fa\u5934\u6765\u60f3\u900f\u53e3\u6c14\uff0c\u814c\u841d\u535c\u7684\u6c14\u5473\u4e5f \u5e76\u4e0d\u6bd4\u8fd9\u5e8a\u88ab\u597d\u591a\u5c11\uff0c\u53ea\u6709\u9f3b\u5b50\u4e0d\u901a\u7684\u4eba\uff0c\u4e5f\u8bb8\u8fd8\u80fd\u5728\u8fd9\u91cc\u7761\u5f97\u7740\u3002 \u4e1c\u65b9\u7684\u66d9\u8272\uff0c\u5df2\u5c06\u7a97\u7eb8\u67d3\u767d\uff0c\u7136\u540e\u9633\u5149\u5c31\u7167\u4e0a\u4e86\u7a97\u68cd\u3002 \u4ed6\u773c\u7741\u7741\u7684\u770b\u7740\u5c4b\u91cc\u8fd9\u6247\u552f\u4e00\u7684\u7a97\u6237\uff0c\u53eb\u4ed6\u5c31\u8fd9\u4e48\u6837\u8eba\u5728\u8fd9\u91cc\uff0c\u518d\u773c\u7741\u7741\u7684\u7b49\u7740 \u592a\u9633\u843d\u4e0b\u53bb\uff0c\u90a3\u7b80\u76f4\u8981\u4ed6\u7684\u547d\u3002 \u4f55\u51b5\u4ed6\u73b0\u5728\u809a\u5b50\u53c8\u997f\u5f97\u8981\u547d\uff0c\u8981\u4ed6\u5403\u814c\u841d\u535c\uff0c\u66f4\u8981\u4ed6\u7684\u547d\u3002 \u6709\u4e86\u8fd9\u4e48\u591a\u8981\u547d\u7684\u4e8b\uff0c\u4ed6\u5982\u679c\u8fd8\u80fd\u803d\u5f97\u4e0b\u53bb\uff0c\u4ed6\u5c31\u4e0d\u662f\u9646\u5c0f\u51e4\u4e86\u3002 \u5c31\u7b97\u5f6d\u957f\u51c0\u8bf4\u7684\u8bdd\u662f\u5723\u65e8\uff0c\u9646\u5c0f\u51e4\u4e5f\u4e0d\u7ba1\u7684\uff0c\u597d\u6b79\u4e5f\u5f97\u5148\u5230\u53a8\u623f\u91cc\u627e\u70b9\u4e1c\u897f\u5403\u3002 \u5c71\u4e0a\u65e2\u7136\u6765\u4e86\u8fd9\u4e48\u591a\u8d35\u5bbe\uff0c\u9999\u79ef\u53a8\u623f\u91cc\u5f53\u7136\u5c11\u4e0d\u4e86\u6709\u4e9b\u51ac\u83c7\u9999\u83cc\u4e4b\u7c7b\u7684\u4e0a\u7d22\u3002 \u4ed6\u867d\u7136\u5b81\u53ef\u5403\u5927\u9c7c\u5927\u8089\uff0c\u53ef\u662f\u5076\u5c14\u5403\u4e00\u6b21\u7d20\uff0c\u4ed6\u4e5f\u4e0d\u53cd\u5bf9\u3002 \u4ed6\u53ea\u4e0d\u8fc7\u53cd\u5bf9\u6328\u997f\u3002 \u4ed6\u8ba4\u4e3a\u6bcf\u4e2a\u4eba\u90fd\u5e94\u8be5\u6709\u514d\u4e8e\u9965\u997f\u56f0\u4e4f\u7684\u81ea\u7531\u3002 \u592a\u9633\u5df2\u5347\u5f97\u5f88\u9ad8\uff0c\u9999\u79ef\u53a8\u91cc\u7684\u4eba\u6b63\u5728\u5c06\u7ca5\u83dc\u70b9\u5fc3\u653e\u8fdb\u4e00\u4e2a\u4e2a\u6d82\u7740\u7ea2\u6f06\u7684\u98df\u76d2\u91cc\uff0c \u518d\u5206\u522b\u9001\u51fa\u53bb\u3002 \u65e9\u70b9\u867d\u7136\u7b80\u5355\u4e9b\uff0c\u7d20\u83dc\u8fd8\u662f\u505a\u5f97\u5f88\u7cbe\u81f4\uff0c\u663e\u7136\u662f\u9001\u7ed9\u8d35\u5ba2\u4eec\u5403\u7684\u3002 \u9646\u5c0f\u51e4\u6b63\u51c6\u5907\u60f3\u6cd5\u5b50\u5f04\u4e2a\u98df\u76d2\uff0c\u5e26\u56de\u4ed6\u90a3\u5c0f\u5c4b\u53bb\u4eab\u53d7\uff0c\u7a81\u7136\u542c\u4e00\u4e2a\u4eba\u5927\u58f0\u9053\uff1a \u201c\u4f60\u8fc7\u6765\u3002\u201d \u8bf4\u8bdd\u7684\u4eba\u662f\u4e2a\u4e2d\u5e74\u9053\u58eb\uff0c\u9634\u6c89\u6c89\u7684\u4e00\u5f20\u9a6c\u8138\uff0c\u770b\u6837\u5b50\u5c31\u5f88\u4e0d\u8ba8\u4eba\u559c\u6b22\u3002 \u9646\u5c0f\u51e4\u4e1c\u770b\u770b\uff0c\u897f\u770b\u770b\uff0c\u524d\u770b\u770b\uff0c\u540e\u770b\u770b\uff0c\u524d\u540e\u5de6\u53f3\u90fd\u6ca1\u6709\u522b\u7684\u4eba\u3002 \u8fd9\u9a6c\u8138\u9053\u58eb\u53eb\u7684\u5c31\u662f\u4ed6\u3002 \u4ed6\u53ea\u6709\u8d70\u8fc7\u53bb\u3002 \u4e34\u65f6\u88ab\u627e\u6765\u5e2e\u5fd9\u7684\u706b\u5de5\u9053\u4eba\u597d\u50cf\u4e0d\u6b62\u4ed6\u4e00\u4e2a\uff0c\u8fd9\u9053\u58eb\u5e76\u6ca1\u6709\u76d8\u95ee\u4ed6\u7684\u6765\u5386\uff0c\u53ea\u4e0d \u8fc7\u8981\u4ed6\u628a6\u4e2a\u6700\u5927\u7684\u98df\u76d2\u9001\u5230\u201c\u542c\u7af9\u5c0f\u9662\u201d\u53bb\uff0c\u800c\u4e14\u8981\u8d76\u5feb\u9001\u53bb\u3002 \u9646\u5c0f\u51e4\u63d0\u8d77\u98df\u76d2\u5c31\u8d70\uff0c\u4ed6\u770b\u89c1\u6446\u8fdb\u997f\u76d2\u91cc\u7684\u662f\u4e00\u789f\u83cc\u6cb9\u70c2\u7b0b\uff0c\u4e00\u789f\u6241\u5c16\u6bdb\u8c46\uff0c\u4e00 \u789f\u51ac\u83c7\u8150\uff0c\u4e00\u789f\u7f57\u6c49\u4e0a\u658b\uff0c\u8fd8\u6709\u2014\u5927\u9505\u9999\u55b7\u55b7\u7684\u7cb3\u7c73\u7ca5\u3002 \u8fd9\u4e9b\u4e1c\u897f\u5f88\u5408\u4ed6\u7684\u5473\u53e3\uff0c\u4ed6\u5b9e\u5728\u5f88\u60f3\u5148\u5403\u4e86\u518d\u8bf4\u3002 \u5982\u679c\u4ed6\u771f\u7684\u8fd9\u4e48\u6837\u505a\u4e86\uff0c\u4ed6\u4e5f\u4e0d\u662f\u9646\u5c0f\u51e4\u4e86\u3002 \u9646\u5c0f\u51e4\u505a\u4e8b\uff0c\u5e76\u4e0d\u662f\u5b8c\u5168\u6ca1\u6709\u5206\u5bf8\u7684\uff0c\u4ed6\u5e76\u4e0d\u60f3\u8bef\u4e86\u5927\u4e8b\uff0c\u8fd9\u98df\u76d2\u91cc\u7684\u83dc\u65e2\u7136\u7279 \u522b\u7cbe\u81f4\uff0c\u4f0e\u5728\u542c\u7af9\u5c0f\u9662\u91cc\u7684\u5f53\u7136\u662f\u7279\u522b\u7684\u8d35\u5ba2\u3002 \u73b0\u5728\u552f\u4e00\u7684\u95ee\u9898\u662f\uff0c\u4ed6\u6839\u672c\u4e0d\u77e5\u9053\u542c\u7af9\u5c0f\u9662\u5728\u54ea\u91cc\u3002 \u4ed6\u6b63\u60f3\u627e\u4e2a\u6837\u5b50\u6bd4\u8f83\u548c\u6c14\u7684\u4eba\u95ee\u95ee\uff0c\u5374\u770b\u89c1\u4e86\u4e2a\u6837\u5b50\u6700\u4e0d\u548c\u6c14\u7684\u4eba\u3002 \u5f6d\u957f\u51c0\u6b63\u5728\u51b7\u51b7\u7684\u76ef\u7740\u4ed6\uff0c\u5ffd\u7136\u538b\u4f4e\u58f0\u97f3\u95ee\uff0c\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u542c\u7af9\u5c0f\u9662\u91cc\u4f4f\u7684\u662f\u4ec0 \u4e48\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\u6447\u6447\u5934\u3002 \u5f6d\u957f\u51c0\u9053\uff1a\u201c\u662f\u5c11\u6797\u94c1\u80a9\u3002\u201d \u9646\u5c0f\u51e4\u624b\u5fc3\u597d\u50cf\u8863\u5f00\u59cb\u5192\u6c57\u3002 \u4ed6\u8ba4\u5f97\u94c1\u80a9\uff0c\u8fd9\u8001\u548c\u5c1a\u4e0d\u4f46\u6709\u4e00\u53cc\u9510\u773c\uff0c\u51fa\u5bb6\u524d\u8fd8\u662f\u4e2a\u540d\u6355\uff0c\u9ed1\u9053\u4e0a\u7684\u52fe\u5f53\uff0c\u4ed6 \u6ca1\u6709\u4e00\u6837\u4e0d\u7cbe\u7684\uff0c\u6700\u7cbe\u7684\u636e\u8bf4\u5c31\u662f\u6613\u5bb9\uff0c\u8fde\u6614\u5e74\u6c5f\u6e56\u4e2d\u7684\u7b2c\u4e00\u53f7\u98de\u8d3c\u201c\u5343\u9762\u4eba\uff3c\u90fd\u683d \u5728\u4ed6\u624b\u91cc\u3002 \u5f6d\u957f\u51c0\u51b7\u51b7\u9053\uff1a\u4ed6\u82e5\u770b\u51fa\u4f60\u6613\u5bb9\u6539\u626e\u8fc7\uff0c\u4f60\u5c31\u5b8c\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u6211\u80fd\u4e0d\u80fd\u4e0d\u53bb\uff1f\u201d \u5f6d\u957f\u51c0\u9053\uff1a\u201c\u4e0d\u80fd\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u5f6d\u957f\u51c0\u9053\uff1a\u201c\u56e0\u4e3a\u6d3e\u7ed9\u4f60\u8fd9\u4ef6\u5dee\u4f7f\u7684\u4eba\uff0c\u5c31\u662f\u5b8b\u957f\u6e05\uff0c\u4ed6\u5df2\u7ecf\u5728\u6ce8\u610f\u4f60\u3002 \u5e78\u597d\u542c\u7af9\u5c0f\u9662\u5e76\u4e0d\u96be\u627e\uff0c\u4f9d\u7167\u5f6d\u957f\u51c0\u7684\u6307\u793a\u8d70\u8fc7\u6761\u788e\u77f3\u5c0f\u5f84\uff0c\u5c31\u53ef\u4ee5\u770b\u89c1\u4e00\u7247\u9752 \u7fe0\u7684\u7af9\u6797\u3002 \u4ed6\u8d70\u8fc7\u53bb\u7684\u65f6\u5019\uff0c\u6709\u4e2a\u4eba\u6b63\u8d70\u5728\u4ed6\u524d\u9762\uff0c\u4e00\u8eab\u84dd\u5e03\u8863\u670d\u5df2\u6d17\u5f97\u53d1\u767d\uff0c\u8fd8\u6253\u7740\u535c\u4e03 \u516b\u4e2a\u5927\u8865\u9489\u3002 \u4ed6\u8ba4\u5f97\u8fd9\u4e2a\u4eba\uff0c\u7528\u4e0d\u7740\u770b\u5230\u8fd9\u4e2a\u4eba\u7684\u8138\uff0c\u5c31\u53ef\u4ee5\u8ba4\u5f97\u51fa\u3002 \u4e10\u5e2e\u7684\u89c4\u77e9\u6700\u5927\uff0c\u4e10\u5e2e\u5f1f\u5b50\u80cc\u540e\u80cc\u7740\u7684\u9ebb\u888b\uff0c\u53eb\u505a\u54c1\u7ea7\u888b\u3002 \u4f60\u82e5\u6709\u4e86\u4e03\u888b\u5f1f\u5b50\u7684\u8eab\u5206\uff0c\u5c31\u5f97\u80cc\u4e03\u53e3\u9ebb\u888b\uff0c\u591a\u4e00\u53e3\u90fd\u4e0d\u884c\uff0c\u5c11\u4e00\u53e3\u4e5f\u4e0d\u884c\uff0c\u7b80 \u76f4\u6bd4\u671d\u5ef7\u547d\u5b98\u7684\u54c1\u7ea7\u5206\u5f97\u8fd8\u4e25\u3002 \u4e03\u888b\u5f1f\u5b50\u5df2\u662f\u4e10\u5e2e\u4e2d\u7684\u6267\u4e8b\u957f\u8001\uff0c\u5e2e\u4e3b\u624d\u6709\u8d44\u683c\u80cc\u4e5d\u53e3\u9ebb\u888b\u3002 \u8d70\u5728\u9646\u5c0f\u51e4\u524d\u9762\u7684\u8fd9\u4e2a\u4eba\uff0c\u80cc\u540e\u7684\u9ebb\u888b\u7adf\u6709\u5341\u53e3\u3002 \u4e10\u5e2e\u5efa\u7acb\u6570\u767e\u5e74\u6765\uff0c\u8fd9\u662f\u552f\u4e00\u7684\u4f8b\u5916\uff0c\u56e0\u4e3a\u8fd9\u4e2a\u4eba\u66ff\u4e10\u5e2e\u7acb\u7684\u529f\u52cb\u5b9e\u5728\u592a\u5927\uff0c\u5374 \u53c8\u504f\u504f\u529f\u6210\u8eab\u9000\uff0c\u8fde\u5e2e\u4e3b\u90fd\u4e0d\u80af\u505a\u3002 \u4e3a\u4e86\u8868\u793a\u5bf9\u4ed6\u7684\u5c0a\u656c\u548c\u611f\u6fc0\uff0c\u4e10\u5e2e\u4e0a\u4e0a\u4e0b\u4e0b\u6570\u5343\u5f1f\u5b50\uff0c\u6bcf\u4e2a\u4eba\u90fd\u5c06\u81ea\u5df1\u7684\u9ebb\u888b\u526a \u4e0b\u4e00\u5c0f\u5757\uff0c\u8fde\u7f00\u6210\u4e00\u4e2a\u9001\u7ed9\u4ed6\uff0c\u8c61\u5f81\u4ed6\u7684\u5c0a\u8363\u6743\u8d35\u3002 \u8fd9\u4e2a\u4eba\u5c31\u662f\u738b\u5341\u888b\u3002 \u9646\u5c0f\u51e4\u4f4e\u4e0b\u4e86\u5934\uff0c\u6545\u610f\u6162\u6162\u7684\u5b9a\u3002 \u738b\u5341\u888b\u4eca\u5e74\u5df2\u8fd1\u516b\u5341\uff0c\u5df2\u662f\u4e2a\u8001\u5f97\u4e0d\u80fd\u518d\u8001\u7684\u8001\u6c5f\u6e56\uff0c\u6c5f\u6e56\u4e2d\u7684\u4e8b\uff0c\u80fd\u7792\u8fc7\u4ed6\u7684 \u5df2\u4e0d\u591a\u3002 \u9646\u5c0f\u51e4\u5b9e\u5728\u4e0d\u613f\u88ab\u4ed6\u770b\u89c1\uff0c\u5374\u53c8\u504f\u504f\u8eb2\u4e0d\u4e86\uff0c\u4ed6\u663e\u7136\u4e5f\u662f\u5230\u542c\u7af9\u5c0f\u9662\u4e2d\u53bb\u7684\uff0c\u6709 \u5f88\u591a\u670b\u53cb\u5df2\u7ecf\u5728\u90a3\u91cc\u7b49\u7740\u4ed6\uff0c\u4ed6\u7684\u670b\u53cb\u90fd\u662f\u8eab\u5206\u6781\u9ad8\u7684\u6b66\u6797\u540d\u4eba\u3002 \u6728\u9053\u4eba\u3001\u9ad8\u884c\u7a7a\u548c\u9e70\u773c\u8001\u4e03\u90fd\u5728\uff0c\u8fd8\u6709\u90a3\u9ad8\u5927\u5a01\u731b\u7684\u8001\u4eba\u3002 \u2014\u8fd9\u4eba\u7a76\u7adf\u662f\u4ec0\u4e48\u8eab\u5206\uff1f \u4e00\u4e2a\u4fee\u9970\u6574\u6d01\uff0c\u767d\u9762\u5fae\u987b\u7684\u4e2d\u5e74\u9053\u8005\uff0c\u6b63\u662f\u5df4\u5c71\u5c0f\u987e\u3002 \u4e00\u4e2a\u8863\u7740\u6734\u7d20\uff0c\u6001\u5ea6\u606c\u9759\uff0c\u6c38\u8fdc\u90fd\u5bf9\u751f\u547d\u5145\u6ee1\u4e86\u4fe1\u5fc3\u548c\u7231\u5fc3\u7684\u5e74\u9752\u4eba\uff0c\u5374\u662f\u4e45\u8fdd \u4e86\u7684\u82b1\u6ee1\u697c\u3002 \u4ed6\u867d\u7136\u4e0d\u80fd\u7528\u773c\u775b\u53bb\u770b\uff0c\u53ef\u662f\u4ed6\u80fd\u7528\u5fc3\u53bb\u770b\uff0c\u53bb\u4e86\u89e3\uff0c\u53bb\u540c\u60c5\uff0c\u53bb\u5173\u6000\u522b\u4eba\u3002 \u6240\u4ee5\u4ed6\u7684\u751f\u547d\u6c38\u8fdc\u662f\u5145\u5b9e\u7684\u3002 \u9646\u5c0f\u51e4\u6bcf\u6b21\u770b\u89c1\u4ed6\u7684\u65f6\u5019\uff0c\u5fc3\u91cc\u90fd\u4f1a\u6d8c\u8d77\u4e00\u9635\u8bf4\u4e0d\u51fa\u7684\u6e29\u6696\u3002 \u90a3\u4e0d\u4ec5\u662f\u53cb\u60c5\uff0c\u8fd8\u6709\u79cd\u53d1\u81ea\u5185\u5fc3\u7684\u5c0a\u656c\u3002 \u4e91\u623f\u4e2d\u7cbe\u96c5\u5e7d\u9759\uff0c\u9646\u5c0f\u51e4\u8fdb\u53bb\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u6b63\u5728\u8c08\u8bba\u6728\u9053\u4eba\u90a3\u5929\u5728\u9152\u697c\u4e0a\u770b\u89c1\u7684 \u4e8b\u3002 \u5bf9\u8fd9\u4e2a\u8bdd\u9898\u9646\u5c0f\u51e4\u65e0\u7591\u4e5f\u5f88\u6709\u5174\u8da3\uff0c\u6545\u610f\u5c06\u6bcf\u4ef6\u4e8b\u90fd\u505a\u5f97\u5f88\u6162\uff0c\u5c3d\u91cf\u4e0d\u8ba9\u81ea\u5df1\u7684 \u8138\u53bb\u5bf9\u7740\u8fd9\u4e9b\u4eba\u3002 \u4ed6\u4eec\u5bf9\u4ed6\u5374\u5b8c\u5168\u6ca1\u6709\u6ce8\u610f\uff0c\u8c08\u8bdd\u5e76\u6ca1\u6709\u505c\u987f\u3002 \u201c\u897f\u95e8\u5439\u96ea\u8bf4\u7684\u662f\u771f\u8bdd\uff0c\u201c\u6728\u9053\u4eba\u7684\u5224\u65ad\u4e00\u5411\u90fd\u5f88\u53d7\u91cd\u89c6\uff0c\u201c\u80fd\u63a5\u5f97\u4f4f\u4ed6\u90a3\u4e00\u8f6e \u5feb\u653b\u7684\uff0c\u7edd\u4e0d\u4f1a\u8d85\u51fa\u4e94\u4e2a\u4eba\u3002\u201d \u201c\u4f60\u4e5f\u770b\u4e0d\u51fa\u90a3\u9ed1\u8863\u8499\u9762\u5251\u5ba2\u6765\u5386\uff1f\u201d\u95ee\u8bdd\u7684\u662f\u5df4\u5c71\u5c0f\u987e\u3002 \u4ed6\u81ea\u5df1\u4e5f\u662f\u5251\u6cd5\u540d\u5bb6\uff0c\u5bb6\u4f20\u4e03\u4e03\u56db\u5341\u4e5d\u624b\u56de\u98ce\u821e\u67f3\u5251\uff0c\u4e0e\u6b66\u5f53\u7684\u4e24\u4eea\u795e\u5251\uff0c\u6606\u4ed1 \u7684\u98de\u9f99\u5927\u4e5d\u5f0f\uff0c\u5e76\u79f0\u4e3a\u7384\u95e8\u4e09\u5927\u5251\u6cd5\u3002 \u201c\u90a3\u4eba\u7684\u51fa\u624b\u8f7b\u7075\u8001\u7ec3\uff0c\u529f\u529b\u6781\u6df1\uff0c\u51e0\u4e4e\u5df2\u4e0d\u5728\u6614\u5e74\u8001\u987e\u4e4b\u4e0b\u3002\u201d\u6728\u9053\u4eba\u76ee\u4e2d\u5e26 \u7740\u6df1\u601d\u4e4b\u8272\uff0c\u201c\u6700\u5947\u602a\u7684\u662f\uff0c\u4ed6\u7528\u7684\u7adf\u4eff\u4f5b\u662f\u6b66\u5f53\u5251\u6cd5\uff0c\u5374\u53c8\u6bd4\u6b66\u5f53\u5251\u6cd5\u66f4\u950b\u9510\u6bd2\u8fa3 qH\u201c\u4f60\u770b\u4ed6\u6bd4\u4f60\u600e\u4e48\u4eec\uff1f\u201d\u8fd9\u6b21\u95ee\u8bdd\u7684\u662f\u738b\u5341\u888b\uff0c\u53ea\u6709\u4ed6\u624d\u80fd\u95ee\u51fa\u8fd9\u79cd\u8bdd\u3002 \u6728\u9053\u4eba\u7b11\u4e86\u7b11\uff0c\u201c\u6211\u8fd9\u53cc\u624b\u81f3\u5c11\u5df2\u6709\u5341\u5e74\u672a\u66fe\u63e1\u5251\u4e86\u3002\u201d \u201c\u4f60\u7684\u624b\u4e0d\u4f1a\u75d2\uff1f\u201d \u201c\u624b\u75d2\u7684\u65f6\u5019\u6211\u5c31\u53bb\u62ff\u68cb\u5b50\u9152\u676f\u3002\u2019\u6728\u9053\u4eba\u7b11\u9053\uff1a\u201c\u2018\u90a3\u4e0d\u4f46\u6bd4\u63e1\u5251\u8f7b\u677e\u6109\u5feb\uff0c \u800c\u4e14\u4e5f\u5b89\u5168\u5f97\u591a\u3002\u201d \u201c\u6240\u4ee5\u90a3\u5929\u4f60\u5c31\u4e00\u76f4\u5728\u8896\u624b\u65c1\u89c2aU\u201c\u6211\u53ea\u80fd\u8896\u624b\u65c1\u89c2\uff0c\u6211\u624b\u91cc\u4e0d\u4f46\u6709\u9152\u676f\uff0c\u8fd8\u63d0 \u7740\u4e2a\u9152\u58f6\u3002\u201d \u201c\u4f60\u8bf4\u7684\u90a3\u4f4d\u4ee5\u9152\u4e3a\u547d\u7684\u670b\u53cb\u662f\u8c01\uff1f\u201d \u201c\u90a3\u4eba\u636e\u8bf4\u662f\u4e2a\u544a\u8001\u8fd8\u4e61\u7684\u4eac\u5b98\uff0c\u6211\u770b\u4ed6\u5374\u6709\u70b9\u53ef\u7591\u3002\u201d \u9e70\u773c\u8001\u4e03\u62a2\u7740\u8bf4\u3002 \u201c\u53ef\u7591\uff1f\u201d \u201c\u4ed6\u867d\u7136\u5c3d\u91cf\u4f5c\u51fa\u8001\u8fc8\u7792\u76ef\u7684\u6837\u5b50\uff0c\u5176\u5b9e\u811a\u4e0b\u7684\u529f\u592b\u5374\u5f88\u4e0d\u5f31\uff0c\u4e00\u8e6c\u4ece\u697c\u4e0a\u8dcc\u4e0b \u53bb\uff0c\u5c45\u7136\u8fde\u4e00\u70b9\u4e8b\u90fd\u6ca1\u6709\uff0c\u770b\u4ed6\u7684\u6837\u5b50\uff0c\u5c31\u50cf\u662f\u6211\u4eec\u4e00\u4e2a\u719f\u4eba\u3002\u201d \u542c\u5230\u8fd9\u91cc\uff0c\u9646\u5c0f\u51e4\u7684\u4e00\u9897\u5fc3\u51e0\u4e4e\u5df2\u8df3\u51fa\u8154\u5b50\uff0c\u53ea\u60f3\u8d76\u7d27\u5f00\u6e9c\u3002 \u201c\u4f60\u770b\u4ed6\u50cf\u8c01\uff1f\u201d \u201c\u53f8\u7a7a\u6458\u661f\u3002\u201d \u9646\u5c0f\u51e4\u7acb\u523b\u677e\u4e86\u53e3\u6c14\uff0c\u53c8\u4e0d\u60f3\u8d70\u4e86\u3002 \u4ed6\u4eec\u53c8\u5f00\u59cb\u5728\u8c08\u8bba\u90a3\u56db\u4e2a\u884c\u8ff9\u6700\u795e\u79d8\u7684\u8001\u5934\u5b50\u3002 \u201c\u90a3\u56db\u4eba\u975e\u4f46\u529f\u529b\u90fd\u6781\u6df1\uff0c\u800c\u4e14\u8def\u6570\u4e5f\u5f88\u63a5\u8fd1\uff0c\u201c\u6728\u9053\u4eba\u82e6\u7b11\u9053\uff1a\u201c\u50cf\u90a3\u6837\u7684 \u4eba\uff0c\u4e00\u4e2a\u5df2\u5f88\u96be\u627e\uff0c\u90a3\u5929\u5374\u5ffd\u7136\u540c\u65f6\u51fa\u73b0\u4e86\u56db\u4e2a\uff0c\u7b80\u76f4\u5c31\u50cf\u662f\u5ffd\u7136\u4ece\u5929\u4e0a\u6389\u4e0b\u6765 \u7684\u3002\u201d \u9ad8\u884c\u7a7a\u6c89\u541f\u7740\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u66f4\u5947\u602a\u7684\u662f\uff0c\u4ed6\u4eec\u7684\u795e\u60c5\u4e3e\u52a8\u770b\u6765\u90fd\u5dee\u4e0d\u591a\uff0c\u5c31\u8fde\u9762 \u8c8c\u597d\u50cf\u90fd\u6709\u70b9\u76f8\u4f3c\uff0c\u5c31\u597d\u50cf\u662f\u5144\u5f1f\u3002\u201d \u201c\u5144\u5f1f\uff1f\u201d\u94c1\u80a9\u76b1\u4e86\u76b1\u7709\uff0c\u201c\u50cf\u8fd9\u6837\u7684\u5144\u5f1f\uff0c\u6211\u53ea\u77e5\u9053\u2026\u2026\u201d \u4ed6\u6ca1\u6709\u8bf4\u4e0b\u53bb\uff0c\u4ed6\u4e00\u5411\u4e0d\u662f\u4e2a\u8f7b\u6613\u4e0b\u5224\u65ad\u7684\u4eba\uff0c\u4ee5\u4ed6\u7684\u8eab\u5206\u5730\u4f4d\uff0c\u4e5f\u4e0d\u80fd\u8f7b\u6613\u4e0b \u5224\u65ad\u3002 \u53ef\u662f\u5728\u5ea7\u7684\u8fd9\u4e9b\u8001\u6c5f\u6e56\u4eec\uff0c\u663e\u8def\u5df1\u542c\u51fa\u4e86\u4ed6\u7684\u610f\u601d\uff0c\u201c\u4f60\u8bf4\u7684\u662f\u864e\u8c79\u5144\u5f1f\uff1f\u201d \u94c1\u80a9\u6ca1\u6709\u627f\u8ba4\uff0c\u4e5f\u6ca1\u6709\u5426\u8ba4\u3002 \u6728\u9053\u4eba\u53c8\u7b11\u4e86\uff0c\u201c\u5c31\u7b97\u4ed6\u4eec\u8fd8\u5728\u4eba\u4e16\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u5e26\u7740\u2018\u6ee1\u7fe0\u697c\u2019\u7684\u59d1\u5a18\u53bb\u559d\u9152 \u7684\u3002\u201d \u201c\u6ee1\u7fe0\u697c\u7684\u59d1\u5a18\uff1f\u201d\u738b\u5341\u888b\u62a2\u7740\u9053\uff1a\u201c\u4f60\u5bf9\u8fd9\u79cd\u4e8b\u597d\u50cf\u86ee\u5185\u884c\u7684\uff0c\u4f60\u662f\u4e0d\u662f\u4e5f\u53bb\u8fc7 \u6ee1\u7fe0\u697c\uff1f\u201d\u201c\u6211\u5f53\u7136\u53bb\u8fc7\uff0c\u201c\u6728\u9053\u4eba\u60a0\u7136\u800c\u7b11\uff0c\u201c\u53ea\u8981\u6709\u9152\u559d\uff0c\u4ec0\u4e48\u5730\u65b9\u6211\u90fd\u53bbao\u738b \u5341\u888b\u4e5f\u5927\u7b11\uff0c\u201c\u8fd9\u8001\u9053\u8bf4\u8bdd\u7684\u53e3\u6c14\uff0c\u7b80\u76f4\u5c31\u8ddf\u9646\u5c0f\u51e4\u4e00\u6a21\u4e00\u6837\u3002\u201d \u8bdd\u9898\u597d\u50cf\u5df2\u5c06\u8f6c\u5230\u9646\u5c0f\u51e4\u8eab\u4e0a\u3002 \u9646\u5c0f\u51e4\u53c8\u51c6\u5907\u5f00\u6e9c\u3002 \u9e70\u773c\u8001\u4e03\u5ffd\u7136\u9053\uff1a\u201c\u8fd8\u6709\u4ef6\u4e8b\u6211\u66f4\u60f3\u4e0d\u901a\u3002\u201d \u6728\u9053\u4eba\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d \u9e70\u773c\u8005\u4e03\u9053\uff1a\u201c\u4e00\u4e2a\u544a\u8001\u8fd8\u4e61\u7684\u4eac\u5b98\uff0c\u600e\u4e48\u4f1a\u5ffd\u7136\u53d8\u6210\u4e86\u706b\u5de5\u9053\u58eb\uff1f\u201d \u9646\u5c0f\u51e4\u624b\u811a\u51b0\u51b7\uff0c\u518d\u60f3\u8d70\u5df2\u592a\u8fdf\u3002 \u9e70\u773c\u8001\u4e03\u5df2\u98de\u8eab\u800c\u8d77\uff0c\u6321\u4f4f\u4e86\u4ed6\u7684\u53bb\u8def\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u4e0d\u80fd\u8d70\u3002 \u9646\u5c0f\u51e4\u597d\u50cf\u5f88\u5403\u60ca\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u8d70\uff1f\u201d \u9e70\u773c\u8001\u4e03\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u60f3\u4e0d\u901a\u7684\u8fd9\u4ef6\u4e8b\uff0c\u53ea\u6709\u4f60\u80fd\u544a\u8bc9\u6211\u3002\u201d \u9ad8\u884c\u7a7a\u4e5f\u8df3\u4e86\u8d77\u6765\uff0c\u201c\u4e0d\u9519\uff0c\u4ed6\u5c31\u662f\u90a3\u4f4d\u4ee5\u9152\u4e3a\u547d\u7684\u670b\u53cb\uff0c\u4ed6\u600e\u4e48\u4f1a\u5230\u8fd9\u91cc\u6765 \u7684\uff1f\u201d \u5e7d\u96c5\u7684\u4e91\u623f\uff0c\u5ffd\u7136\u5145\u6ee1\u6740\u6c14\u3002 \u65e0\u8bba\u8c01\u505a\u4e86\u5341\u4e8c\u8fde\u73af\u575e\u7684\u603b\u74e2\u628a\u5b50\uff0c\u4e00\u4e2a\u6708\u4e2d\u603b\u96be\u514d\u8981\u6740\u4e09\u4e94\u4e2a\u4eba\u7684\u3002 \u9ad8\u884c\u7a7a\u9634\u9e33\u51b7\u9177\uff0c\u4e5f\u662f\u6c5f\u6e56\u4e2d\u6709\u540d\u7684\u5389\u5bb3\u4eba\u7269\u3002 \u53ea\u8981\u4ed6\u4eec\u4e00\u5f00\u59cb\u884c\u52a8\uff0c\u5c31\u6709\u6740\u673a\u3002 \u4ed6\u4eec\u4e00\u524d\u4e00\u540e\uff0c\u5df2\u5b8c\u5168\u5c01\u6b7b\u4e86\u9646\u5c0f\u51e4\u7684\u9000\u8def\uff0c\u9646\u5c0f\u51e4\u5c31\u7b97\u80fd\u957f\u51fa\u5341\u5bf9\u7fc5\u8180\u6765\uff0c\u4e5f \u5f88\u96be\u4ece\u8fd9\u5c4b\u91cc\u98de\u51fa\u53bb\u3002 \u53ea\u4e0d\u8fc7\u4e16\u4e0a\u5047\u5982\u8fd8\u6709\u4e00\u4e2a\u4eba\u80fd\u4ece\u8fd9\u5c4b\u91cc\u9003\u51fa\u53bb\uff0c\u8fd9\u4e2a\u4eba\u4e00\u5b9a\u5c31\u662f\u9646\u5c0f\u51e4\u3002 \u4ed6\u5ffd\u7136\u5927\u7b11\uff0c\u201c\u6211\u597d\u50cf\u8f93\u4e86\u3002\u201d \u9e70\u773c\u8001\u4e03\u9053\uff1a\u201c\u4f60\u8f93\u5b9a\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u7b11\u9053\uff1a\u201c\u6211\u5e73\u751f\u8ddf\u522b\u4eba\u6253\u8d4c\u4e0d\u4e0b\u516b\u767e\u6b21\uff0c\u8fd9\u4e00\u6b21\u8f93\u5f97\u6700\u60e8ao\u9e70\u773c\u8001\u4e03\u9053\uff1a \u201c\u6253\u8d4c\uff1f\u8d4c\u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6709\u4e2a\u4eba\u8ddf\u6211\u8d4c\uff0c\u53ea\u8981\u6211\u80fd\u5728\u8fd9\u5c4b\u91cc\u803d\u4e00\u76cf\u8336\u529f\u592b\uff0c\u8fd8\u6ca1\u6709\u88ab\u4eba\u8ba4\u51fa \u6765\uff0c\u4ed6\u5c31\u8f93\u7ed9\u6211\u4e00\u987f\u597d\u9152\uff0c\u5426\u5219\u4ed6\u4ece\u6b64\u90fd\u8981\u53eb\u6211\u6df7\u86cb\u3002\u201d \u9e70\u773c\u8001\u4e03\u51b7\u7b11\u3002 \u4ed6\u6839\u672c\u4e0d\u4fe1\u8fd9\u4e00\u5957\uff0c\u5374\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u95ee\uff0c\u201c\u8ddf\u4f60\u6253\u8d4c\u7684\u8fd9\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u81ea\u5df1\u5f53\u7136\u4e5f\u662f\u4e2a\u6df7\u86cb\uff0c\u800c\u4e14\u662f\u4e2a\u7279\u5927\u53f7\u7684\u6df7\u86cbao\u9e70\u773c\u8001\u4e03\u9053\uff1a \u201c\u8c01\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u9646\u5c0f\u51e4\u3002\u201d \u8fd9\u540d\u5b57\u8bf4\u51fa\u6765\uff0c\u5927\u5bb6\u90fd\u4e0d\u7981\u8038\u7136\u52a8\u5bb9\uff0c\u201c\u4ed6\u8fd8\u6ca1\u6709\u6b7b\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6b7b\u4eba\u600e\u4e48\u4f1a\u6253\u8d4c\uff1f\u201d \u9e70\u773c\u8001\u4e03\u9053\uff1a\u201c\u4ed6\u7684\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u9646\u5c0f\u51e4\u62ac\u8d77\u5934\uff0c\u5411\u5bf9\u9762\u7684\u7a97\u6237\u62db\u4e86\u62db\u624b\uff0c\u9053\uff1a\u201c\u4f60\u8fd8\u4e0d\u8fdb\u6765\uff1f\u201d \u5927\u5bb6\u5f53\u7136\u90fd\u5fcd\u4e0d\u4f4f\u8981\u671d\u90a3\u8fb9\u53bb\u770b\uff0c\u4ed6\u81ea\u5df1\u5374\u4e58\u673a\u4ece\u53e6\u4e00\u8fb9\u6e9c\u4e86\u3002 \u4e24\u8fb9\u7a97\u5b50\u90fd\u662f\u5f00\u7740\u7684\uff0c\u4ed6\u7bad\u4e00\u822c\u8e7f\u4e86\u51fa\u53bb\uff0c\u4e00\u811a\u8e39\u5728\u5c4b\u6a90\u4e0a\u3002 \u5c4b\u6a90\u584c\u4e0b\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u53c8\u5df2\u501f\u529b\u63a0\u51fa\u4e94\u4e08\u3002 \u540e\u9762\u6709\u4eba\u5728\u547c\u559d\uff0c\u6bcf\u4e2a\u4eba\u7684\u8f7b\u529f\u90fd\u5f88\u4e0d\u9519\uff0c\u5012\u584c\u7684\u5c4b\u6a90\u867d\u7136\u80fd\u963b\u62e6\u4ed6\u4eec\u4e00\u4e0b\u5b50\uff0c \u4ed6\u4eec\u8fd8\u662f\u5f88\u5feb\u5c31\u4f1a\u8ffd\u51fa\u6765\u7684\u3002 \u9646\u5c0f\u51e4\u8fde\u770b\u90fd\u4e0d\u6562\u56de\u5934\u53bb\u770b\u3002 \u9053\u89c2\u7684\u5efa\u7b51\u53e4\u8001\u9ad8\u5927\u800c\u7a7a\u9614\uff0c\u867d\u7136\u6709\u5f88\u591a\u85cf\u8eab\u4e4b\u5904\uff0c\u4ed6\u5374\u4e0d\u6562\u5192\u9669\u3002 \u4eca\u5929\u5df2\u662f\u5341\u4e09\u3002\u8be5\u5230\u7684\u4eba\u5df2\u5168\u90fd\u5230\u4e86\uff0c\u5230\u7684\u4eba\u90fd\u662f\u9ad8\u65e0\u8bba\u4ed6\u85cf\u5728\u54ea\u91cc\uff0c\u90fd\u53ef\u80fd\u88ab \u4eba\u627e\u5230\uff0c\u65e0\u8bba\u88ab\u8c01\u627e\u5230\uff0c\u8981\u60f3\u8131\u8eab\u90fd\u5f88\u96be\u3002 \u4ed6\u5f53\u7136\u4e5f\u4e0d\u80fd\u9003\u4e0b\u5c71\u53bb\uff0c\u4eca\u5929\u7684\u4e8b\uff0c\u4ed6\u65e2\u4e0d\u80fd\u9519\u8fc7\uff0c\u4e5f\u4e0d\u613f\u9519\u8fc7\u3002 \u4e09\u4e94\u4e2a\u8d77\u843d\u540e\uff0c\u5bf9\u9762\u5df2\u6709\u4eba\u4e0a\u4e86\u5c4b\u810a\uff0c\u540e\u9762\u5f53\u7136\u4e5f\u5df2\u6709\u4eba\u8ffd\u4e86\u8fc7\u6765\u3002 \u63a5\u7740\uff0c\u5de6\u53f3\u4e24\u8fb9\u4e5f\u51fa\u73b0\u4e86\u4eba\u5f71\uff0c\u524d\u540e\u5de6\u53f3\u56db\u8def\u5305\u6284\uff0c\u4ed6\u51e0\u4e4e\u5df2\u65e0\u8def\u53ef\u8d70\u3002 \u4ed6\u53ea\u6709\u5f80\u4e0b\u8df3\u3002 \u4e0b\u9762\u7684\u4eba\u4eff\u4f5b\u66f4\u591a\uff0c\u56db\u9762\u516b\u65b9\u90fd\u5df2\u54cd\u8d77\u4e86\u811a\u6b65\u58f0\u3002 \u4ed6\u8f6c\u8fc7\u4e24\u4e09\u4e2a\u5c4b\u89d2\uff0c\u5ffd\u7136\u53d1\u73b0\u524d\u9762\u6709\u4e2a\u4eba\u5728\u51b7\u51b7\u7684\u770b\u7740\u4ed6\uff0c\u9a6c\u8138\u4e0a\u5168\u65e0\u8868\u60c5\uff0c\u7adf \u662f\u5f6d\u957f\u51c0\u7684\u5e08\u5f1f\uff0c\u706b\u5de5\u9053\u4eba\u7684\u526f\u603b\u7ba1\u957f\u6e05\u3002 \u9646\u5c0f\u51e4\u5403\u4e86\u4e00\u60ca\uff0c\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u4f60\u597dqo\u957f\u6e05\u51b7\u51b7\u9053\uff1a\u201c\u6211\u4e0d\u597d\uff0c\u4f60\u66f4\u4e0d\u597d\uff0c\u6211\u53ea \u8981\u5927\u53eb\u4e00\u58f0\uff0c\u6240\u6709\u7684\u4eba\u90fd\u4f1a\u8d76\u5230\u8fd9\u91cc\u6765\uff0c\u5c31\u7b97\u4f60\u80fd\u4e00\u4e0b\u5b50\u6253\u5012\u6211\uff0c\u4e5f\u6ca1\u6709\u7528\u2606\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u600e\u4e48\u6837\uff1f\u201d \u957f\u6e05\u9053\uff1a\u201c\u6211\u53ea\u60f3\u8ba9\u4f60\u660e\u767d\u8fd9\u4e00\u70b9\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u5df2\u7ecf\u660e\u767d\u4e86\u3002\u201d \u957f\u6e05\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5c31\u6700\u597d\u8ba9\u6211\u628a\u4f60\u6293\u4f4f\uff0c\u4ee5\u540e\u5bf9\u4f60\u4e5f\u6709\u597d\u5904\u3002\u201d \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u597d\u5427\uff0c\u53cd\u6b63\u6211\u8fdf\u65e9\u603b\u662f\u9003\u4e0d\u4e86\u7684\uff0c\u5230\u4e0d\u5982\u7d22\u6027\u4e70\u4e2a\u4ea4\u60c5 \u7ed9\u4f60\u3002\u201d \u957f\u6e05\u773c\u775b\u4eae\u4e86\uff0c\u4e00\u4e2a\u7bad\u6b65\u7a9c\u8fc7\u6765\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u4e0b\u624b\u8f7b\u2018\u70b9\u597d\u4e0d\u597d\uff1f\u201d \u957f\u6e05\u9053\uff1a\u201c\u597d\u3002\u201d \u8fd9\u4e2a\u5b57\u662f\u5f00\u53e3\u97f3\uff0c\u4ed6\u53ea\u8bf4\u51fa\u8fd9\u4e00\u4e2a\u5b87\uff0c\u5df2\u6709\u6837\u4e1c\u897f\u585e\u5165\u4ed6\u5634\u91cc\uff0c\u4ed6\u6325\u62f3\u8fce\u51fb\uff0c\u80c1 \u4e0b\u7684\u7a74\u9053\u4e5f\u5df2\u88ab\u70b9\u4f50\u3002 \u9646\u5c0f\u51e4\u5df2\u8f6c\u8fc7\u524d\u9762\u7684\u5c4b\u89d2\uff0c\u4ed6\u53ea\u6709\u773c\u7741\u7741\u7684\u770b\u7740\u3002 \u53ef\u662f\u4ed6\u77e5\u9053\u9646\u5c0f\u51e4\u8fd8\u662f\u9003\u4e0d\u4e86\u7684\uff0c\u56e0\u4e3a\u518d\u5f80\u524d\u8f6c\uff0c\u5c31\u662f\u5927\u6bbf\u3002 \u5f53\u4eca\u6b66\u5f53\u7684\u638c\u95e8\u4eba\uff0c\u6b63\u5728\u5927\u6bbf\u91cc\u3002 \u5927\u6bbf\u524d\u662f\u4e2a\u7a7a\u65f7\u5bbd\u9614\u7684\u9662\u5b50\uff0c\u8c01\u4e5f\u6ca1\u6cd5\u5b50\u85cf\u8eab\uff0c\u5927\u6bbf\u91cc\u5149\u7ebf\u9634\u9eef\uff0c\u9999\u70df\u7ee6\u7ed5\uff0c\u4eba \u4e16\u95f4\u6240\u6709\u7684\u7ea0\u7eb7\u70e6\u607c\uff0c\u90fd\u5df2\u88ab\u9694\u7edd\u5728\u95e8\u69db\u5916\u3002 \u9646\u5c0f\u51e4\u7adf\u7a9c\u4e86\u8fdb\u53bb\u3002 \u4ed6\u663e\u7136\u65e9\u5df2\u51c6\u5907\u85cf\u8eab\u5728\u8fd9\u91cc\u3002 \u4ed6\u77e5\u9053\u4eba\u4eec\u5fc3\u91cc\u90fd\u6709\u4e2a\u76f2\u70b9\uff0c\u85cf\u8eab\u5728\u6700\u660e\u663e\u7684\u5730\u65b9\uff0c\u53cd\u800c\u8d8a\u4e0d\u5bb9\u6613\u88ab\u627e\u5230\u3002 \u73b0\u5728\u65e9\u8bfe\u7684\u65f6\u5019\u5df2\u8fc7\uff0c\u5927\u6bbf\u4e2d\u5c31\u8fd8\u6709\u4eba\uff0c\u4e5f\u5e94\u8be5\u88ab\u521a\u624d\u7684\u547c\u559d\u60ca\u52a8\u3002 \u4ed6\u5b9e\u5728\u60f3\u4e0d\u5230\u91cc\u9762\u5c45\u7136\u8fd8\u6709\u4eba\u3002 \u4e00\u4e2a\u957f\u8eab\u7389\u7acb\u7684\u9053\u4eba\uff0c\u9ed8\u9ed8\u7684\u7ad9\u5728\u795e\u6848\u524d\uff0c\u4e5f\u4e0d\u77e5\u662f\u5728\u4e3a\u4eba\u7c7b\u7948\u6c42\u5e73\u5b89\uff0c\u8fd8\u662f\u5728 \u9759\u601d\u7740\u81ea\u5df1\u7684\u8fc7\u9519\u3002 \u4ed6\u9762\u524d\u7684\u795e\u6848\u4e0a\uff0c\u6446\u7740\u4e00\u67c4\u5251\u3002 \u4e00\u67c4\u8c61\u5f81\u7740\u5c0a\u8363\u548c\u6743\u529b\u7684\u4e03\u661f\u5b9d\u5251\u3002 \u8fd9\u4e2a\u4eba\u7adf\u662f\u77f3\u96c1\u3002 \u9646\u5c0f\u51e4\u66f4\u5403\u60ca\uff0c\u811a\u5c16\u70b9\u5730\uff0c\u8eab\u5b50\u7acb\u523b\u8e7f\u8d77\u3002 \u5927\u6bbf\u4e0a\u7684\u6a2a\u6881\u79bb\u5730\u5341\u4e08\uff01 \u6ca1\u6709\u5927\u80fd\u4e00\u63a0\u5341\u6587\u3002 \u4ed6\u8eab\u5b50\u8e7f\u8d77\uff0c\u5de6\u8db3\u8db3\u5c16\u5728\u53f3\u8db3\u8db3\u80cc\u4e0a\u4e00\u70b9\uff0c\u7adf\u65bd\u5c55\u51fa\u6b66\u6797\u4e45\u5df2\u7edd\u4f20\u7684\u201c\u68af\u4e91\u8e2a\u201d \u7edd\u9876\u8f7b\u529f\u3002 \u4ed6\u5c45\u7136\u63a0\u4e0a\u4e86\u6a2a\u6881\u3002 \u77f3\u96c1\u8fd8\u662f\u9ed8\u9ed8\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u4eff\u4f5b\u5df2\u795e\u6e38\u7269\u5916\u3002 \u9646\u5c0f\u51e4\u521a\u677e\u4e86\u53e3\u6c14\uff0c\u738b\u5341\u888b\u3001\u9ad8\u884c\u7a7a\u3001\u9e70\u773c\u8001\u4e03\u3001\u5df4\u5c71\u5c0f\u987e\u90fd\u5df2\u95ef\u4e86\u8fdb\u6765\u3002 \u201c\u521a\u624d\u6709\u6ca1\u6709\u4eba\u8fdb\u6765\u8fc7\uff1f\u201d \u77f3\u96c1\u6162\u6162\u7684\u8f6c\u8fc7\u8eab\uff0c\u9053\uff1a\u201c\u6709\u3002\u201d \u8fd9\u4e2a\u201c\u6709\u201d\u5b57\u542c\u5728\u9646\u5c0f\u51e4\u8033\u91cc\u51e0\u4e4e\u5c31\u50cf\u662f\u7f6a\u72af\u542c\u89c1\u4e86\u4ed6\u5df2\u88ab\u5224\u51b3\u6b7b\u5211\u3002 \u201c\u4eba\u5728\u54ea\u91cc\uff1f\u201d \u201c\u5c31\u5728\u8fd9\u91cc\uff0c\u201c\u77f3\u96c1\u5fae\u7b11\u7740\uff0c\u201c\u6211\u5c31\u662f\u521a\u624d\u8fdb\u6765\u7684ao\u4eba\u90fd\u5df2\u8d70\u4e86\uff0c\u8fde\u77f3\u96c1\u90fd\u8d70 \u4e86\u3002 \u5982\u679c\u6b66\u5f53\u7684\u638c\u95e8\u4eba\u8bf4\u8fd9\u91cc\u6ca1\u6709\u4eba\u6765\u8fc7\uff0c\u90a3\u4e48\u5c31\u7b97\u6709\u4eba\u770b\u89c1\u9646\u5c0f\u51e4\u5728\u8fd9\u91cc\uff0c\u4e5f\u4e00\u5b9a \u8ba4\u4e3a\u81ea\u5df1\u770b\u9519\u4e86\u3002 \u6709\u5f88\u591a\u4eba\u90fd\u8ba4\u4e3a\u6b66\u5f53\u638c\u95e8\u8bf4\u7684\u8bdd\uff0c\u751a\u81f3\u6bd4\u81ea\u5df1\u7684\u773c\u775b\u8fd8\u53ef\u9760\u3002 \u77f3\u96c1\u5f53\u7136\u7edd\u4e0d\u4f1a\u8bf4\u8c0e\u7684\uff0c\u4ee5\u4ed6\u7684\u8033\u76ee\uff0c\u96be\u9053\u771f\u4e0d\u77e5\u9053\u6709\u4eba\u8fdb\u6765\u8fc7\uff1f \u9646\u5c0f\u51e4\u5ffd\u7136\u60f3\u8d77\u4e86\u5b69\u5b50\u4eec\u6349\u8ff7\u85cf\u7684\u6e38\u620f\u3002 \u4e00\u4e2a\u5b69\u5b50\u8eb2\u5728\u53d4\u53d4\u6905\u5b50\u80cc\u540e\uff0c\u53e6\u4e00\u4e2a\u5b69\u5b50\u6765\u627e\uff0c\u53d4\u53d4\u603b\u662f\u4f1a\u8bf4\uff0c\u201c\u8fd9\u91cc\u6ca1\u6709 \u4eba\u3002\u201d \u77f3\u96c1\u5e76\u4e0d\u662f\u4ed6\u7684\u53d4\u53d4\uff0c\u4e3a\u4ec0\u4e48\u8981\u66ff\u4ed6\u63a9\u62a4\uff1f \u9646\u5c0f\u51e4\u6ca1\u6709\u53bb\u60f3\u3002 \u6a2a\u6881\u4e0a\u7070\u5c18\u79ef\u5f97\u5f88\u539a\uff0c\u4ed6\u8fd8\u662f\u8eba\u4e86\u4e0b\u53bb\uff0c\u5e0c\u671b\u80fd\u7761\u4e00\u73b0\u5728\u4ed6\u5df2\u7edd\u4e0d\u80fd\u518d\u9732\u9762\u4e86\uff0c \u53ea\u8981\u5728\u8fd9\u91cc\u7b49\uff0c\u201c\u7b49\u706f\u706b\u7684\u65f6\u5019qo\uff0e\u7b49\u5230\u90a3\u4e00\u77ac\u5230\u6765\uff0c\u4ed6\u5728\u6a2a\u6881\u4e0a\u8fd8\u662f\u540c\u6837\u53ef\u4ee5\u51fa \u624b\u3002 \u6240\u4ee5\u4ed6\u624d\u4f1a\u9009\u62e9\u8fd9\u5730\u65b9\u85cf\u8eab\uff0c\u8fd9\u91cc\u81f3\u5c11\u6ca1\u6709\u814c\u841d\u535c\u7684\u81ed\u53ea\u53ef\u60dc\u4ed6\u8fd8\u662f\u7761\u4e0d\u7740\u3002 \u4ed6\u4f2f\u6389\u4e0b\u53bb\u3002 \u4e0d\u4f46\u6015\u4eba\u6389\u4e0b\u53bb\uff0c\u4e5f\u6015\u6881\u4e0a\u7684\u7070\u5c18\u6389\u4e0b\u53bb\uff0c\u4ed6\u7b80\u76f4\u8fde\u52a8\u90fd\u4e0d\u6562\u52a8\u3002 \u7b49\u5230\u4ed6\u60f3\u5230\u997f\u7684\u65f6\u5019\uff0c\u5c31\u5f00\u59cb\u540e\u6094\u4e86\uff0c\u540e\u6094\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4e0d\u8001\u8001\u5b9e\u5b9e\u7684\u803d\u5728\u90a3\u5c4b\u5b50 \u91cc\uff1f\u814c\u841d\u535c\u3001\u5473\u9053\u5176\u5b9e\u5e76\u6ca1\u6709\u4ed6\u60f3\u50cf\u4e2d\u90a3\u4e48\u81ed\u7684\u3002 \u8fd9\u65f6\u5927\u6bbf\u4e2d\u53c8\u6709\u5f88\u591a\u4eba\u8fdb\u6765\uff0c\u6253\u626b\u6bbf\u5802\uff0c\u5b89\u6392\u5750\u6905\uff0c\u8fd8\u6709\u4eba\u5728\u95ee\uff0c\u201c\u8c01\u662f\u7ba1\u706f\u6cb9 \u7684\uff1f\u201d \u201c\u662f\u5f1f\u5b50\u957f\u614e\u3002\u201d \u201c\u706f\u91cc\u7684\u6cb9\u52a0\u6ee1\u4e86\u6ca1\u6709\uff1f\u201d\u201c\u52a0\u6ee1\u4e86\uff0c\u4eca\u5929\u6e05\u65e9\uff0c\u5f1f\u5b50\u5c31\u5df2\u68c0\u67e5\u8fc7\u4e00\u904d\u3002\u201d \u95ee\u8bdd\u7684\u4eba\u663e\u7136\u5df2\u5f88\u6ee1\u610f\uff0c\u957f\u614e\u505a\u4e8b\u60f3\u5fc5\u4e00\u5411\u90fd\u5f88\u8c28\u614e\u3002 \u5947\u602a\u7684\u662f\uff0c\u6b66\u5f53\u5f1f\u5b50\u600e\u4e48\u4f1a\u88ab\u8001\u5200\u628a\u5b50\u6536\u4e70\u4e86\u7684\uff1f\u4ed6\u5bf9f\u6b66\u5f53\u7684\u60c5\u51b5\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u5982 \u6b64\u719f\u6089\uff1f \u9646\u5c0f\u51e4\u4e5f\u6ca1\u6709\u53bb\u60f3\u3002 \u6700\u8fd1\u4ed6\u597d\u50cf\u4e00\u76f4\u90fd\u4e0d\u613f\u610f\u52a8\u8111\u7b4b\u53bb\u60f3\u4efb\u4f55\u4e8b\u3002 \u6253\u626b\u7684\u4eba\u5927\u591a\u90fd\u8d70\u4e86\uff0c\u53ea\u7559\u4e0b\u51e0\u4e2a\u4eba\u5728\u5927\u6bbf\u91cc\u770b\u5b88\u7167\u987e\u3002 \u53c8\u8fc7\u4e86\u5f88\u4e45\uff0c\u9646\u5c0f\u51e4\u5c31\u542c\u89c1\u4ed6\u4eec\u5728\u7a83\u7a83\u79c1\u8bae\uff0c\u8bae\u8bba\u4e00\u6b63\u662f\u90a3\u4e2a\u5047\u626e\u6210\u706b\u5de5\u9053\u4eba\u7684 \u201c\u5978\u7ec6\u201d\u3002 \u201c\u6211\u5b9e\u5728\u60f3\u4e0d\u901a\uff0c\u8fd9\u91cc\u53c8\u6ca1\u6709\u4ec0\u4e48\u79d8\u5bc6\uff0c\u600e\u4e48\u4f1a\u6709\u5978\u7ec6\u6765\uff1fD\u201c\u4e5f\u8bb8\u4ed6\u662f\u60f3\u6765\u5077\u4e1c \u897f\u7684\u3002\u201d \u201c\u5077\u6211\u4eec\u8fd9\u4e9b\u7a77\u9053\u58eb\uff1f\u201d \u201c\u83ab\u5fd8\u8bb0\u8fd9\u4e24\u5929\u5c71\u4e0a\u6765\u7684\u90fd\u662f\u8d35\u5ba2\u3002\u201d \u201c\u4e5f\u8bb8\u4ed6\u65e2\u4e0d\u662f\u5c0f\u5077\uff0c\u4e5f\u4e0d\u662f\u5978\u7ec6\u3002\u201d \u201c\u662f\u4ec0\u4e48\uff1f\u201d \u201c\u662f\u523a\u5ba2\uff01\u6765\u523a\u90a3\u4e9b\u8d35\u5ba2\u7684\u3002\u201d \u201c\u73b0\u5728\u6211\u4eec\u8fd8\u6ca1\u6709\u6293\u4f4f\u4ed6\uff1f\u201d \u201c\u8fd8\u6ca1\u6709\u3002 \u201c\u6211\u60f3\u4ed6\u73b0\u5728\u4e00\u5b9a\u65e9\u5c31\u4e0b\u5c71\u53bb\u4e86\uff0c\u4ed6\u53c8\u4e0d\u662f\u5446\u5b50\uff0c\u600e\u4e48\u4f1a\u7559\u5728\u5c71\u4e0a\u7b49\u6b7b\u3002\u201d \u201c\u5012\u9709\u7684\u662f\u957f\u51c0\uff0c\u636e\u8bf4\u90a3\u4e2a\u4eba\u662f\u4ed6\u5e26\u4e0a\u5c71\u6765\u7684\uff0c\u73b0\u5728\u5341\u4e8c\u8fde\u73af\u575e\u7684\u603b\u74e2\u628a\u5b50\u6b63\u5728 \u4eb2\u81ea\u8ffd\u95ee\u4ed6\u7684\u53e3\u4f9b\u3002\u201d \u636e\u8bf4\u9e70\u773c\u8001\u4e03\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u522b\u6709\u4e00\u529f\uff0c\u5728\u4ed6\u7684\u624b\u4e0b\uff0c\u8fde\u6b7b\u4eba\u4e5f\u6ca1\u6cd5\u5b50\u4e0d\u5f00\u53e3\u3002 \u957f\u51c0\u4f1a\u4e0d\u4f1a\u5c06\u8fd9\u79d8\u5bc6\u62db\u4f9b\u51fa\u6765\uff1f \u4ed6\u77e5\u9053\u7684\u7a76\u7adf\u6709\u591a\u5c11\uff1f \u9646\u5c0f\u51e4\u6b63\u5f00\u59cb\u62c5\u5fc3\uff0c\u5ffd\u7136\u53c8\u542c\u89c1\u811a\u6b65\u58f0\u54cd\uff0c\u4e24\u4e2a\u4eba\u5598\u606f\u7740\u8d70\u8fdb\u6765\uff0c\u8bf4\u51fa\u4ef6\u60ca\u4eba\u7684 \u6d88\u606f\uff0c\u201c\u5f6d\u957f\u51c0\u6b7b\u4e86\u3002\u201d \u201c\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u201c\u4e8c\u5e08\u53d4\u4ed6\u4eec\u6b63\u5728\u95ee\u4ed6\u7684\u53e3\u4f9b\u65f6\uff0c\u5916\u9762\u5ffd\u7136\u98de\u8fdb\u4e86\u4e00\u6839\u7af9\u7aff\uff0c\u6d3b\u6d3b\u7684\u628a\u4ed6\u9489\u6b7b\u5728 \u6905\u5b50\u4e0a\u3002\u201d \u201c\u51f6\u624b\u6293\u4f50\u4e86\u6ca1\u6709\uff1f\u201d \u201c\u6ca1\u6709\uff0c\u592a\u5e08\u7956\u5df2\u7ecf\u5e26\u7740\u4e8c\u5e08\u53d4\u4ed6\u4eec\u8ffd\u4e0b\u53bb\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u8fd9\u7ed3\u679c\u4ed6\u5e76\u4e0d\u610f\u5916\uff0c\u6740\u4eba\u706d\u53e3\uff0c\u672c\u5c31\u662f\u4ed6\u4eec\u7684\u4e00\u8d2f\u4f5c\u98ce\u3002 \u53ea\u4e0d\u8fc7\u7528\u4e00\u6839\u7af9\u7aff\u5c31\u80fd\u5c06\u4eba\u6d3b\u6d3b\u9489\u6b7b\u5728\u6905\u5b50\u4e0a\u7684\u4eba\u5e76\u4e0d\u591a\uff0c\u5c31\u8fde\u8868\u54e5\u548c\u7ba1\u5bb6\u5a46\u4ed6 \u4eec\u90fd\u7edd\u6ca1\u6709\u8fd9\u4e48\u6df1\u7684\u529f\u529b\u3002 \u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u8fd8\u6709\u8c01\u4e5f\u5df2\u6f5c\u5165\u4e86\u6b66\u5f53\uff1f \u65e0\u864e\u5144\u5f1f\u548c\u77f3\u9e64\u7edd\u4e0d\u6562\u8fd9\u4e48\u65e9\u5c31\u4e0a\u5c71\uff0c\u6765\u7684\u96be\u9053\u662f\u8001\u5200\u628a\u5b50\uff1f \u4ed6\u662f\u7528\u4ec0\u4e48\u8eab\u5206\u505a\u63a9\u62a4\u7684\uff1f\u96be\u9053\u4ed6\u4e5f\u626e\u6210\u4e86\u4e2a\u706b\u5de5\u9053\u4eba\uff1f \u4e0b\u9762\u5ffd\u7136\u53c8\u6709\u4eba\u95ee\uff0c\u201c\u957f\u51c0\u6b7b\u4e86\uff0c\u8ddf\u6211\u4eec\u53c8\u6ca1\u4ec0\u4e48\u5173\u7cfb\uff0c\u4f60\u4f55\u5fc5\u6025\u7740\u8d76\u6765\u62a5\u6d88 \u606f\uff1f\u201d \u201c\u8ddf\u4f60\u867d\u7136\u6ca1\u5173\u7cfb\uff0c\u8ddf\u957f\u614e\u5e08\u5144\u5374\u6709\u5173\u7cfb\u2026\u2026\u201d \u201c\u6211\u660e\u767d\u4e86\uff0c\u201c\u53e6\u5916\u4e00\u4e2a\u4eba\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u201c\u957f\u51c0\u6b7bf\uff0c\u957f\u6e05\u4e5f\u53d7\u4e86\u7f5a\uff0c\u957f\u614e\u5e08\u5144 \u5f53\u7136\u5c31\u53d8\u6210\u4e86\u6211\u4eec\u7684\u603b\u7ba1\uff0c\u4f60\u662f\u8d76\u6765\u62a5\u559c\u7684\u3002\u201d \u770b\u6765\u8fd9\u4e9b\u706b\u5de5\u9053\u4eba\u4eec\u7684\u516d\u6839\u5e76\u4e0d\u6e05\u51c0\uff0c\u4e5f\u4e00\u6837\u4f1a\u4e89\u6743\u593a\u5229\u3002 \u9646\u5c0f\u51e4\u5fc3\u91cc\u6b63\u5728\u53f9\u606f\uff0c\u5ffd\u7136\u542c\u5230\u4e00\u9635\u5c16\u9510\u5947\u5f02\u7684\u58f0\u97f3\u4ece\u5916\u9762\u7737\u4e86\u8fdb\u6765\u3002 \u8fde\u4ed6\u90fd\u542c\u4e0d\u51fa\u8fd9\u662f\u4ec0\u4e48\u58f0\u97f3\uff0c\u53ea\u89c9\u5f97\u8033\u6735\u88ab\u523a\u5f97\u5f88\u96be\u53d7\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5927\u6bbf\u91cc\u5df2 \u54cd\u8d77\u4e00\u8fde\u4e32\u77ed\u4fc3\u51c4\u5389\u7684\u60e8\u547c\u58f0\uff0c\u201c\u662f\u4f60\u2026\u2026\u201d \u4e00\u53e5\u8bdd\u672b\u8bf4\u5b8c\u6240\u6709\u7684\u58f0\u97f3\u53c8\u7a81\u7136\u65ad\u7edd\u3002 \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u6084\u6084\u7684\u4f38\u51fa\u5934\u53bb\u770b\u4e86\u4e00\u773c\uff0c\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u624b\u8db3\u5df2\u51b0\u51b7\u3002 \u5927\u6bbf\u91cc\u672c\u6765\u6709\u4e5d\u4e2a\u4eba\uff0c\u4e5d\u4e2a\u6d3b\u751f\u751f\u7684\u4eba\uff0c\u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4e5d\u4e2a\u4eba\u90fd\u5df2\u6b7b\u4e86\u3002 \u4e5d\u4e2a\u4eba\u7684\u54bd\u5589\u90fd\u5df2\u88ab\u5272\u65ad\uff0c\u770b\u6765\u65e0\u7591\u90fd\u662f\u6b7b\u5728\u5251\u950b\u4e0b\u7684\u3002 \u4e00\u5251\u5c31\u5df2\u81f4\u547d\uff01 \u6b66\u5f53\u7684\u5f1f\u5b50\u4eec\u6b66\u529f\u591a\u5c11\u603b\u6709\u4e9b\u6839\u57fa\uff0c\u5374\u5728\u4e00\u77ac\u95f4\u5c31\u5df2\u88ab\u4eba\u6740\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u521a\u624d\u90a3\u5947\u5f02\u5c16\u9510\u7684\u58f0\u97f3\uff0c\u7adf\u662f\u5251\u950b\u7834\u7a7a\u58f0\u3002 \u597d\u5feb\u7684\u5251\uff01\u597d\u72e0\u7684\u5251\u300f\u5c31\u8fde\u7eb5\u6a2a\u5929\u4e0b\u7684\u897f\u95e8\u5439\u96ea\u90fd\u672a\u5fc5\u80fd\u6bd4\u5f97\u4e0a\uff01 \u51f6\u624b\u662f\u51c6\uff1f \u4ed6\u4e3a\u4ec0\u4e48\u8981\u6740\u8fd9\u4e9b\u65e0\u8db3\u8f7b\u91cd\u7684\u706b\u8981\u9053\u4eba\uff1f \u201c\u662f\u4e3a\u4e86\u957f\u614e\u3002\u201d\u9646\u5c0f\u51e4\u5ffd\u7136\u660e\u767d\uff0c\u201c\u4ed6\u7b97\u51c6\u4e86\u957f\u51c0\u4e00\u6b7b\uff0c\u522b\u4eba\u4e00\u5b9a\u4f1a\u627e\u957f\u614e\u95ee \u8bdd\uff0c\u6240\u4ee5\u5148\u8d76\u6765\u6740\u4e86\u957f\u614e\u706d\u53e3\u3002\u201d \u6740\u957f\u51c0\u7684\u51f6\u624b\u5f53\u7136\u4e5f\u662f\u4ed6\uff01 \u8fd9\u4e2a\u4eba\u7adf\u80fd\u5728\u6b66\u5f53\u7684\u6839\u672c\u91cd\u5730\u5185\u6765\u53bb\u81ea\u5982\uff0c\u968f\u610f\u6740\u4eba\uff0c\u4ed6\u7a76\u7adf\u662f\u4ec0\u4e48\u8eab\u5206\uff1f \u201c\u662f\u4f60\u3002\u3002\u3002\u4e00\u4e00\u201d \u957f\u614e\u4e34\u6b7b\u524d\u8fd8\u8bf4\u51fa\u4e86\u8fd9\u4e24\u4e2a\u5b57\uff0c\u663e\u7136\u662f\u8ba4\u5f97\u8fd9\u4e2a\u4eba\u7684\uff0c\u5374\u4e5f\u60f3\u4e0d\u5230\u8fd9\u4e2a\u4eba\u4f1a\u662f\u6740 \u4eba\u7684\u51f6\u624b\u3002 \u9646\u5c0f\u51e4\u53c8\u4e2d\u7981\u5f00\u59cb\u540e\u6094\uff0c\u521a\u624d\u54cd\u58f0\u2014\u8d77\uff0c\u4ed6\u5c31\u8be5\u4f38\u51fa\u6765\u770b\u770b\u7684\u3002 \u4e5f\u8bb8\u8fd9\u5c31\u662f\u4ed6\u552f\u4e00\u80fd\u770b\u5230\u8fd9\u4eba\u771f\u9762\u76ee\u7684\u673a\u4f1a\u2026\u826f\u673a\u4e00\u5931\uff0c\u53ea\u6015\u5c31\u6c38\u4e0d\u518d\u6765\u4e86\u3002 \u6b7b\u4eba\u5df2\u4e0d\u4f1a\u5f00\u53e3\u3002 \u65e0\u8bba\u9e70\u773c\u8001\u4e03\u7684\u5206\u7b4b\u9519\u9aa8\u624b\u591a\u5389\u5bb3\uff0c\u6b7b\u4eba\u4e5f\u4e0d\u4f1a\u5f00\u53e3\u3002 \u6240\u4ee5\u8ba1\u5212\u4e00\u5b9a\u8fd8\u662f\u7167\u5e38\u8fdb\u884c\u3002 \u6240\u4ee5\u9646\u5c0f\u51e4\u8fd8\u662f\u53ea\u6709\u7b49\uff0c\u7b49\u5929\u9ed1\uff0c\u7b49\u706f\u4eae\uff0c\u518d\u7b49\u706f\u706d\u3002 \u7b49\u5f85\u7684\u6ecb\u6627\u5b9e\u5728\u4e0d\u597d\u53d7\u3002 \u7b2c\u5341\u4e94\u7ae0\u3000\u5de7\u8ba1\u51fa\u91cd\u56f4>> \u53e4\u9f99\u300a\u9646\u5c0f\u51e4\u7cfb\u5217\xb7\u5e7d\u7075\u5c71\u5e84\u300b \u7b2c\u5341\u4e94\u7ae0\u3000\u5de7\u8ba1\u51fa\u91cd\u56f4 \u56db\u6708\u5341\u4e8c\uff0c\u9ec4\u660f\u3002\u5929\u6e10\u6e10\u9ed1\u4e86\uff0c\u5927\u6bbf\u91cc\u706f\u706b\u5df2\u71c3\u8d77\u3002 \u6a2a\u6881\u4e0a\u5374\u8fd8\u662f\u5f88\u9634\u6697\uff0c\u9633\u5149\u7167\u4e0d\u5230\u8fd9\u91cc\uff0c\u706f\u706b\u4e5f\u7167\u4e0d\u5230\uff0c\u4e16\u4e0a\u672c\u5c31\u6709\u5f88\u591a\u5730\u65b9\u662f\u6c38\u8fdc\u90fd \u4e0d\u6ca1\u6709\u5149\u660e\u7684\u3002 \u6709\u4e9b\u4eba\u4e5f\u4e00\u6837\u3002 \u96be\u9053\u9646\u5c0f\u51e4\u5df2\u53d8\u6210\u4e86\u8fd9\u79cd\u4eba\uff0c\u4ed6\u8fd9\u4e00\u751f\u96be\u9053\u5df2\u6ca1\u6709\u51fa\u5934\u7684\u673a\u4f1a\uff0c\u53ea\u80fd\u50cf\u8001\u9f20\u822c\u8eb2\u5728\u9ed1\u6697 \u4e2d\uff0c\u8eb2\u907f\u7740\u897f\u95e8\u5439\u96ea\u3002 \u4e5f\u8bb8\u4ed6\u8fd8\u6709\u673a\u4f1a\uff0c\u4e5f\u8bb8\u8fd9\u6b21\u884c\u52a8\u5c31\u662f\u4ed6\u552f\u4e00\u7684\u673a\u4f1a\uff0c\u6240\u4ee5\u4ed6\u7edd\u4e0d\u80fd\u5931\u624b\u3002 \u53ef\u662f\u4ed6\u5e76\u6ca1\u6709\u628a\u63e1\u3002 \u8c01\u80fd\u6709\u628a\u63e1\u4ece\u77f3\u96c1\u5934\u4e0a\u6458\u4e0b\u90a3\u9876\u9053\u51a0\u6765\uff1f\u4ed6\u8fd9\u4e00\u4e2a\u4eba\u90fd\u60f3\u4e0d\u51fa\u3002 \u5927\u6bbf\u91cc\u53c8\u54cd\u8d77\u4e86\u811a\u6b65\u58f0\uff0c\u8d70\u5728\u6700\u524d\u9762\u7684\u4e00\u4e2a\u4eba\u811a\u6b65\u867d\u7136\u8d70\u5f97\u5f88\u91cd\uff0c\u811a\u6b65\u58f0\u5374\u8fd8\u662f\u5f88\u8f7b\u3002 \u56e0\u4e3a\u4ed6\u5168\u8eab\u7684\u6c14\u8109\u8840\u6db2\u90fd\u5df2\u8d2f\u901a\uff0c\u4ed6\u867d\u7136\u4e5f\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u5374\u5df2\u548c\u522b\u4eba\u4e0d\u540c\u3002 \u4ed6\u8eab\u5b50\u91cc\u5df2\u6ca1\u6709\u6e23\u6ed3\u3002 \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u5c06\u773c\u775b\u8d34\u7740\u6a2a\u6881\uff0c\u5077\u5077\u7684\u5f80\u4e0b\u770b\uff0c\u4e00\u884c\u7d2b\u8863\u7384\u51a0\u7684\u9053\u4eba\u9c7c\u8d2f\u8d70\u4eba\u5927\u6bbf\uff0c\u8d70 \u5728\u6700\u524d\u9762\u7684\u4eba\uff0c\u7adf\u662f\u6728\u9053\u4eba\u3002 \u4ed6\u548c\u6728\u9053\u4eba\u76f8\u4ea4\u591a\u5e74\uff0c\u76f4\u5230\u6b64\u523b\uff0c\u624d\u77e5\u9053\u8fd9\u4f4d\u6b66\u5f53\u540d\u5bbf\u7684\u529f\u529b\uff0c\u6bd4\u4efb\u4f55\u4eba\u60f3\u50cf\u4e2d\u90fd\u8981\u9ad8 \u5f97\u591a\u3002 \u77f3\u96c1\u8fd8\u6ca1\u6709\u6765\uff0c\u4e3b\u4f4d\u4e0a\u7684\u7b2c\u4e00\u5f20\u4ea4\u6905\u662f\u7a7a\u7740\u7684\uff0c\u6728\u9053\u4eba\u5374\u53ea\u80fd\u5750\u5728\u7b2c\u4e8c\u5f20\u6905\u5b50\u4e0a\u3002 \u867d\u7136\u4ed6\u5fb7\u9ad8\u671b\u91cd\uff0c\u8f88\u5206\u6781\u5c0a\uff0c\u53ef\u662f\u6709\u638c\u95e8\u4eba\u5728\u65f6\uff0c\u4ed6\u8fd8\u662f\u8981\u9000\u5c45\u5176\u6b21\u3002 \u8fd9\u662f\u6b66\u5f53\u7684\u89c4\u77e9\uff0c\u4e5f\u662f\u6c5f\u6e56\u4e2d\u7684\u89c4\u77e9\uff0c\u65e0\u8bba\u8c01\u90fd\u4e0d\u80fd\u6539\u5efa\u53e3 \u5927\u5385\u91cc\u706f\u706b\u8f89\u714c\uff0c\u5916\u9762\u6709\u949f\u58f0\u54cd\u8d77\uff0c\u6728\u9053\u4eba\u964d\u9636\u8fce\u5bbe\uff0c\u5ba2\u4eba\u4eec\u4e5f\u9646\u7eed\u6765\u4e86\u3002 \u6bcf\u4e2a\u4eba\u7684\u6001\u5ea6\u90fd\u5f88\u4e25\u8083\uff0c\u9e70\u773c\u8001\u4e03\u4ed6\u4eec\u7684\u795e\u60c5\u66f4\u51dd\u91cd\uff0c\u663e\u7136\u8fd8\u4e0d\u80fd\u5fd8\u8bb0\u4eca\u5929\u767d\u5929\u53d1\u751f\u7684 \u4e8b\u3002 \u90a3\u9ad8\u5927\u5a01\u731b\u7684\u8001\u4eba\u4e5f\u5230\u4e86\uff0c\u5750\u4f4d\u5c45\u7136\u8fd8\u5728\u5341\u4e8c\u8fde\u73af\u575e\u7684\u2018\u603b\u74e2\u628a\u5b50\u4e4b\u4e0a\u3002 \u4ed6\u53c8\u662f\u4ec0\u4e48\u8eab\u5206\uff1f\u4e3a\u4ec0\u4e48\u4ece\u6765\u4e0d\u5728\u6c5f\u6e56\u4e2d\u9732\u9762\uff1f\u6b64\u523b\u4e3a\u4ec0\u4e48\u53c8\u5ffd\u7136\u9732\u9762\u4e86\u3002 \u9646\u5c0f\u51e4\u4e00\u76f4\u76ef\u7740\u4ed6\uff0c\u5fc3\u91cc\u603b\u89c9\u5f97\u81ea\u5df1\u5e94\u8be5\u8ba4\u5f97\u8fd9\u4e2a\u4eba\uff0c\u5374\u53c8\u504f\u504f\u4e0d\u8ba4\u5f97\uff0c \u5927\u6bbf\u4e2d\u6446\u7684\u6905\u5b50\u5e76\u4e0d\u591a\uff0c\u591f\u8d44\u683c\u5728\u8fd9\u91cc\u6709\u5750\u4f4d\u7684\u4eba\u5e76\u4e0d\u591a\u3002 \u5ba2\u4eba\u4eec\u6765\u7684\u5374\u4e0d\u5c11\uff0c\u6ca1\u6709\u5750\u4f4d\u7684\u53ea\u6709\u7ad9\u7740\u3002 \u94c1\u80a9\u3001\u77f3\u96c1\u3001\u738b\u5341\u888b\u3001\u6c34\u4e0a\u98de\u3001\u9ad8\u884c\u7a7a\u3001\u5df4\u5c71\u5c0f\u987e\u3001\u9e70\u773c\u8001\u4e03\uff0c\u4ed6\u4eec\u8eab\u540e\u90fd\u6709\u4eba\u7ad9\u7740\uff0c \u6bcf\u4e2a\u4eba\u90fd\u53ef\u80fd\u5c31\u662f\u5728\u7b49\u7740\u8981\u4ed6\u4eec\u547d\u7684\u3001 \u8fd9\u4e9b\u4eba\u4e4b\u4e2d\uff0c\u6709\u54ea\u4e9b\u662f\u5df2\u6b7b\u8fc7\u4e00\u6b21\u53c8\u590d\u6d3b\u4e86\u7684\uff1f\u8c01\u662f\u675c\u94c1\u5fc3\uff1f\u8c01\u662f\u5173\u5929\u6b66\uff1f\u8c01\u662f\u5a04\u8001\u592a\u592a\uff1f \u9646\u5c0f\u51e4\u6b63\u5728\u627e\u3002 \u4ed6\u4eec\u6613\u5bb9\u6539\u626e\u8fc7\u4e4b\u540e\u7684\u9762\u8c8c\uff0c\u9664\u4e86\u8001\u5200\u628a\u5b50\u548c\u72ac\u90ce\u541b\u5916\uff0c\u53ea\u6709\u9646\u5c0f\u51e4\u77e5\u9053\u3002 \u72ac\u90ce\u541b\u5df2\u5c06\u4ed6\u4eec\u6bcf\u4e2a\u4eba\u6613\u5bb9\u540e\u7684\u6837\u5b50\u90fd\u753b\u51fa\u6765\u4ea4\u7ed9\u4e86\u9646\u5c0f\u98ce\u4e00\u5728\u7b2c\u4e00\u6d41\u7684\u5ba2\u6808\u91cc\uff0c\u5395\u6240 \u603b\u662f\u76f8\u5f53\u5927\u7684\uff0c\u9664\u4e86\u65b9\u4fbf\u5916\uff0c\u8fd8\u53ef\u4ee5\u505a\u5f88\u591a\u4e8b\u3002 \u6d77\u5947\u9614\u6740\u7684\u90a3\u6761\u72d7\uff0c\u65e2\u7136\u771f\u662f\u6761\u72d7\uff0c\u72ac\u90ce\u541b\u5230\u54ea\u91cc\u53bb \u8fd9\u79d8\u5bc6\u662f\u4e0d\u662f\u4e5f\u53ea\u6709\u9646\u5c0f\u51e4\u77e5\u9053\uff1f \u4ed6\u5f88\u5feb\u5c31\u627e\u5230\u4e86\u4ed6\u4eec\uff0c\u751a\u81f3\u8fde\u90a3\u4e2a\u6ca1\u6709\u8106\u7684\u77f3\u9e64\uff0c\u73b0\u5728\u90a3\u5df2\u6709\u4e86\u5f20\u8138\u3002 \u4ed6\u4eec\u663e\u7136\u90fd\u5728\u7d27\u7d27\u76ef\u7740\u81ea\u5df1\u7684\u76ee\u6807\uff0c\u53ea\u7b49\u706f\u4e00\u706d\uff0c\u5c31\u7a9c\u8fc7\u53bb\u51fa\u624b\u2026 \u552f\u2018\u6ca1\u6709\u5bf9\u4ed8\u7684\uff0c\u597d\u50cf\u53ea\u6709\u6728\u9053\u4eba\uff0c\u662f\u4e0d\u662f\u56e0\u4e3a\u4ed6\u4e45\u5df2\u4e0d\u95ee\u6c5f\u6e56\u4e2d\u7684\u4e8b\uff0c\u8001\u5200\u628a\u5b50\u6839\u672c \u5c31\u6ca1\u6709\u5c06\u4ed6\u5f53\u505a\u76ee\u6807\u3002 \u9646\u5c0f\u51e4\u6ca1\u6709\u518d\u60f3\u4e0b\u53bb\uff0c\u56e0\u4e3a\u8fd9\u65f6\u5019\u4ed6\u81ea\u5df1\u7684\u76ee\u6807\u4e5f\u51fa\u89c2 \u6234\u7740\u7d2b\u91d1\u9053\u51a0\u7684\u6b66\u5f53\u638c\u95e8\u771f\u4eba\uff0c\u5df2\u5728\u56db\u4e2a\u624b\u6267\u6cd5\u5668\u7684\u9053\u7231\u62a4\u536b\u4e2d\uff0c\u6162\u6162\u7684\u8d70\u4e86\u51fa\u6765\u3002 \u8fd9\u4f4d\u540d\u91cd\u5f53\u4ee3\u7684\u77f3\u96c1\u9053\u957f\uff0c\u4e0d\u4f46\u4fee\u4e3a\u529f\u6df1\uff0c\u5c11\u5e74\u65f6\u4e5f\u66fe\u6597\u7ecf\u4e07\u6218\uff0c\u4ed6\u7684\u5251\u6cd5\u3001\u5185\u529b\u548c\u4fee \u517b\uff0c\u90fd\u5df2\u5f88\u5c11\u6709\u4eba\u80fd\u6bd4\u5f97\u4e0a\u3002 \u53ef\u662f\u73b0\u5728\u770b\u6765\u7adf\u4f3c\u5f88\u75b2\u5026\uff0c\u5f88\u8870\u8001\uff0c\u751a\u81f3\u8fd8\u6709\u70b9\u7d27\u5f20\u3002 \u77f3\u96c1\u7684\u786e\u6709\u70b9\u7d27\u5f20\u3002 \u8fd9\u4e48\u591a\u4f73\u5bbe\u8d35\u5ba2\uff0c\u4ed6\u867d\u7136\u4e0d\u80fd\u4e0d\u4ee5\u7b11\u8138\u8fce\u4eba\uff0c\u53ef\u662f\u5fc3\u91cc\u5374\u89c9\u5f97\u7d27\u5f20\u800c\u70e6\u8e81\u3002 \u8fd1\u5341\u5e74\u6765\uff0c\u4ed6\u5df2\u5f88\u5c11\u4f1a\u53d1\u751f\u8fd9\u79cd\u73b0\u8c61\u3002 \u4eca\u5929\u4ed6\u5fc3\u91cc\u4eff\u4f5b\u6709\u79cd\u4e0d\u7965\u7684\u9884\u611f\uff0c\u77e5\u9053\u4e00\u5b9a\u4f1a\u6709\u4e9b\u4e0d\u5e78\u7684\u4e8b\u53d1\u751f\u3002\u201c\u4e5f\u8bb8\u6211\u7684\u786e\u5df2\u5e94\u8be5 \u9000\u4f11\u4e86\u3002\u4ed6\u5728\u5fc3\u91cc\u60f3\uff1a\u201c\u53bb\u627e\u4e2a\u5b89\u9759\u504f\u50fb\u7684\u5730\u65b9\uff0c\u76ca\u4e24\u95f4\u5c0f\u6728\u5c4b\uff0c\u4ece\u6b64\u4e0d\u518d\u95ee\u6c5f\u6e56\u4e2d\u7684\u662f\u975e\uff0c \u4e5f\u4e0d\u518d\u89c1\u6c5f\u6e56\u4e2d\u7684\u4eba\u3002\u201d \u53ea\u53ef\u60dc\u5230\u73b0\u5728\u4e3a\u6b62\uff0c\u8fd9\u4e9b\u8fd8\u90fd\u662f\u5e7b\u60f3\uff0c\u4ee5\u540e\u662f\u4e0d\u662f\u771f\u7684\u80fd\u53ca\u65f6\u4ece\u6c5f\u6e56\u4e0a\u7684\u662f\u975e\u6069\u6028\u4e2d\u5168 \u8eab\u800c\u9000\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u6ca1\u6709\u628a\u63e1\u3002\u82e5\u4e0d\u80fd\u628a\u63e1\u65f6\u673a\uff0c\u5f88\u53ef\u80fd\u5c31\u5df2\u592a\u8fdf\u3002 \u6bcf\u5f53\u4ed6\u7d27\u5f20\u75b2\u5026\u65f6\uff0c\u4ed6\u5c31\u4f1a\u89c9\u5f97\u540e\u9888\u50f5\u786c\uff0c\u504f\u5934\u75db\u7684\u8001\u6bdb\u75c5\u4e5f\u4f1a\u53d1\u4f5c\u3002 \u5c24\u5176\u73b0\u5728\uff0c\u4ed6\u8fd8\u6234\u7740\u9876\u5206\u91cf\u5f88\u91cd\u7684\u7d2b\u91d1\u9053\u51a0\uff0c\u5c31\u50cf\u662f\u9505\u76d6\u822c\u538b\u5728\u4ed6\u5934\u4e0a\u3002 \u4f73\u5bbe\u8d35\u6765\u8fce\u63a5\u4ed6\u3002 \u867d\u7136\u4ed6\u77e5\u9053\u4ed6\u4eec\u5c0a\u656c\u4ed6\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u662f\u6b66\u5f53\u7684\u638c\u95e8\u3002 \u867d\u7136\u4ed6\u5e76\u4e0d\u5b8c\u5168\u559c\u6b22\u8fd9\u4e9b\u4eba\uff0c\u5374\u8fd8\u662f\u4e0d\u80fd\u4e0d\u6446\u51fa\u6700\u52a8\u4eba\u7684\u7b11\u5bb9\uff0c\u5411\u4ed6\u4eec\u62db\u547c\u7b54\u793c\u3002 \u8fd9\u5c82\u975e\u4e5f\u50cf\u505a\u620f\u4e00\u6837\uff1f \u2014\u4f60\u65e2\u7136\u5df2\u88ab\u6d3e\u4e0a\u8fd9\u89d2\u8272\uff0c\u4e0d\u7ba1\u4f60\u8116\u5b50\u518d\u786c\uff0c\u5934\u518d\u75bc\uff0c\u90fd\u5f97\u597d\u597d\u7684\u6f14\u4e0b\u53bb\u3002 \u5927\u6bbf\u91cc\u706f\u706b\u8f89\u714c\u3002 \u5728\u706f\u5149\u4e0b\u770b\u6765\uff0c\u94c1\u80a9\u548c\u738b\u5341\u888b\u65e0\u7591\u90fd\u6bd4\u4ed6\u66f4\u75b2\u5026\uff0c\u66f4\u8870\u8001\u3002 \u5176\u5b9e\u4ed6\u4eec\u90fd\u65e9\u5df3\u5e94\u8be5\u9000\u4f11\u5f52\u9690\u4e86\uff0c\u6839\u672c\u4e0d\u5fc5\u5230\u8fd9\u91cc\u6765\u7684\u3002 \u4ed6\u5e76\u4e0d\u60f3\u89c1\u5230\u4ed6\u4eec\uff0c\u5c24\u5176\u662f\u738b\u5341\u888b\uff1a\u201c\u660e\u660e\u662f\u4e2a\u5fc3\u80f8\u72ed\u7a84\uff0c\u542b\u6bd7\u5fc5\u62a5\u7684\u4eba\uff0c\u5374\u504f\u504f\u8981\u4f5c\u51fa \u6e38\u620f\u98ce\u5c18\uff0c\u73a9\u4e16\u4e0d\u606d\u7684\u6837\u5b50\u201d \u8fd8\u6709\u90a3\u603b\u662f\u559c\u6b22\u7167\u955c\u5b50\u7684\u5df4\u5c71\u5c0f\u987e\uff0c\u4ed6\u5b9e\u5728\u5e94\u8be5\u53bb\u5f00\u5993\u9662\u7684\uff0c\u4e3a\u4ec0\u4e48\u504f\u504f\u8981\u51fa\u5bb6\uff1f \u4e16\u754c\u4e0a\u4e3a\u4ec0\u4e48\u6709\u8fd9\u8bb8\u591a\u4eba\u90fd\u4e0d\u80fd\u53bb\u505a\u81ea\u5df1\u771f\u6b63\u60f3\u505a\u7684\u4e8b\uff1f \u5178\u793c\u5df2\u5f00\u59cb\u8fdb\u884c\uff0c\u6bcf\u4e00\u4e2a\u7a0b\u5e8f\u90fd\u662f\u77f3\u96c1\u5df2\u4e0d\u77e5\u505a\u8fc7\u591a\u5c11\u6b21\u7684\uff0c\u8bf4\u7684\u90a3\u4e9b\u8bdd\uff0c\u4e5f\u5168\u90fd\u4e0d\u77e5 \u662f\u4ed6\u5df2\u8bf4\u8fc7\u591a\u5c11\u6b21\u7684\u3002 \u65e0\u8bba\u4ed6\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\uff0c\u90fd\u7edd\u4e0d\u53bf\u51fa\u4e00\u70b9\u9519\u8bef\uff0c\u6bcf\u4ef6\u4e8b\u90fd\u597d\u50cf\u8fdb\u884c\u5f97\u5f88\u987a\u5229\u3002 \u63a5\u7740\u4ed6\u5c31\u8981\u5ba3\u5e03\u4ed6\u7ee7\u627f\u4eba\u7684\u59d3\u540d\u4e86\u3002 \u4ed6\u7528\u773c\u89d2\u770b\u7740\u4ed6\u4eec\u4e0b\u51e0\u4e2a\u6700\u91cd\u8981\u7684\u5f1f\u5b50\uff0c\u8d8a\u6709\u5e0c\u671b\u7684\uff0c\u5c31\u663e\u5f97\u8d8a\u7d27\u5f20\u3002 \u5047\u5982\u4ed6\u5ba3\u5e03\u7684\u59d3\u540d\u5e76\u4e0d\u662f\u8fd9\u51e0\u4e2a\u4eba\uff0c\u4ed6\u4eec\u4f1a\u76f4\u4ec0\u4e48\u8868\u60c5\uff1f\u522b\u4eba\u4f1a\u6709\u4ec0\u4e48\u53cd\u5e94\uff1f \u90a3\u4e00\u5b9a\u5f88\u6709\u8da3\uff1f \u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u4ed6\u5634\u89d2\u4e0d\u7981\u9732\u51fa\u4e86\u7b11\u610f\uff0c\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u7b11\u51fa\u6765\u3002 \u53ef\u662f\u4ed6\u5f88\u5feb\u5c31\u6291\u5236\u4e86\u81ea\u5df1\uff0c\u6b63\u51c6\u5907\u8fdb\u884c\u4eea\u5f0f\u4e2d\u6700\u91cd\u8981\u7684\u2014\u8282\u201d \u5c31\u5728\u8fd9\u65f6\uff0c\u5927\u6bbf\u91cc\u6709\u76cf\u6c34\u4e0d\u7184\u706d\u7684\u957f\u660e\u706f\uff0c\u7adf\u5ffd\u7136\u706d \u4ed6\u5fc3\u91cc\u7acb\u523b\u751f\u51fa\u8b66\u5146\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u90a3\u4e0d\u7965\u7684\u9884\u611f\u5df2\u5c06\u7075\u9a8c\u3002 \u51e0\u4e4e\u5c31\u5728\u8fd9\u540c\u4e00\u5239\u90a3\u95f4\uff0c\u5927\u6bbf\u5185\u5916\u7684\u4e03\u5341\u4e8c\u76cf\u957f\u660e\u706f\uff0c\u7adf\u7a81\u7136\u5168\u90fd\u7184\u706d\u3002 \u51e0\u7f15\u6025\u9510\u7684\u98ce\u58f0\u54cd\u8fc7\uff0c\u795e\u9f9b\u9999\u6848\u4e0a\u7684\u70db\u706b\u4e5f\u88ab\u51fb\u706d\u3002 \u706f\u706b\u8f89\u714c\u7684\u5927\u6bbf\uff0c\u7adf\u7a81\u7136\u53d8\u5f97\u4e00\u7247\u9ed1\u6697\u3002 \u9ed1\u6697\u4e2d\u7a81\u7136\u54cd\u8d77\u4e00\u8fde\u4e32\u60e8\u547c\uff0c\u4e00\u9053\u66f4\u5f3a\u9510\u7684\u98ce\u58f0\uff0c\u4ece\u5927\u6bbf\u6a2a\u6881\u4e0a\u5f80\u4ed6\u5934\u9876\u5439\u4e86\u8fc7\u6765\uff0c\u5439 \u52a8\u4e86\u4ed6\u7684\u9053\u51a0\uff0c\u7adf\u4eff\u4f5b\u662f\u591c\u884c\u4eba\u7684\u8863\u5feb\u5e26\u98ce\u58f0\u3002 \u4ed6\u4f38\u624b\u53bb\u6276\u9053\u51a0\u65f6\uff0c\u9053\u51a0\u5df2\u4e0d\u89c1\u4e86\u3002 \u201c\u545b\u201d\u7684\u4e00\u54cd\uff0c\u4ed6\u8170\u4e0a\u7684\u4e03\u661f\u5251\u4e5f\u5df2\u51fa\u9798\uff0c\u5374\u4e0d\u662f\u4ed6\u81ea\u5df1\u62d4\u51fa\u6765\u7684\u3002 \u4ed6\u8eab\u5b50\u7acb\u523b\u63a0\u8d77\uff0c\u53ea\u89c9\u5f97\u80c1\u4e0b\u808b\u9aa8\u95f4\u4e00\u9635\u51b0\u51b7\uff0c\u4eff\u4f5b\u88ab\u5251\u950b\u5212\u8fc7\u3002 \u8fd9\u4ef6\u4e8b\u51e0\u4e4e\u4e5f\u5168\u90fd\u662f\u5728\u540c\u4e00\u5239\u90a3\u95f4\u53d1\u751f\u7684\u3002 \u5927\u591a\u6570\u4eba\u6839\u672c\u8fd8\u4e0d\u77e5\u9053\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u5f53\u7136\u66f4\u4e0d\u77e5\u9053\u5e94\u8be5\u600e\u4e48\u6837\u5e94\u53d8\u3002 \u90a3\u4e9b\uff0c\u4f7f\u5f97\u8fd9\u7a81\u6765\u7684\u53d8\u5316\u663e\u5f97\u66f4\u8be1\u79d8\u6050\u6016\u3002 \u60e8\u547c\u58f0\u4e2d\uff0c\u7adf\u4f3c\u8fd8\u6709\u94c1\u80a9\u548c\u738b\u5341\u888b\u8fd9\u4e9b\u7edd\u9876\u9ad8\u624b\u7684\u58f0\u97f3\uff0c\u7136\u540e\u5c31\u542c\u89c1\u6728\u9053\uff0c\u4eba\u5728\u547c\u559d\u3002\u201d \u8c01\u6709\u706b\u6298\u5b50\uff1f\u5feb\u71c3\u706f\u3002\u201d \u4ed6\u7684\u58f0\u97f3\u5c45\u7136\u8fd8\u5f88\u9547\u5b9a\uff0c\u4f46\u77f3\u96c1\u5374\u542c\u5f97\u51fa\u5176\u4e2d\u4e5f\u5e26\u7740\u75db\u82e6\u4e4b\u610f\u3002 \u96be\u9053\u4ed6\u4e5f\u53d7\u4e86\u4f24\u3002 \u867d\u7136\u53ea\u4e0d\u8fc7\u662f\u77ed\u77ed\u7684\u4e00\u77ac\u65f6\u5149\uff0c\u53ef\u662f\u5728\u6bcf\u4e2a\u4eba\u611f\u89c9\u4e2d\uff0c\u90fd\u597d\u50cf\u5f88\u957f\u3002 \u8fd9\u2014\u77ac\u95f4\u53d1\u751f\u7684\u4e8b\uff0c\u66f4\u662f\u6bcf\u4e2a\u4eba\u90fd\u6c34\u8fdc\u5fd8\u4e0d\u4e86\u7684\u3002 \u706f\u7ec8\u4e8e\u4eae\u4e86\uff0c\u5927\u5bb6\u5374\u66f4\u5403\u60ca\uff0c\u66f4\u6050\u60e7\u3002 \u8c01\u4e5f\u4e0d\u80fd\u76f8\u4fe1\u81ea\u5df1\u773c\u775b\u91cc\u770b\u89c1\u7684\u4e8b\uff0c\u8fd9\u4e9b\u4e8b\u5374\u504f\u504f\u662f\u771f\u7684 \u94c1\u80a9\uff0c\u738b\u5341\u888b\u3001\u5df4\u5c71\u5c0f\u987e\u3001\u6c34\u4e0a\u98de\u3001\u9ad8\u884c\u7a7a\u3001\u9e70\u773c\u8001\u4e03\uff0c\u8fd8\u6709\u6b66\u5f53\u95e8\u4e0b\u51e0\u4e2a\u6700\u91cd\u8981\u5f1f \u5b50\uff0c\u7adf\u90fd\u5df2\u5012\u4e86\u4e0b\u53bb\uff0c\u5012\u5728\u8840\u6cca\u66f0\u4e60 \u738b\u5341\u888b\u8170\u4e0a\u751a\u81f3\u8fd8\u63d2\u7740\u4e00\u628a\u5251\uff0c\u5251\u950b\u5df2\u76f4\u523a\u5165\u4ed6\u8981\u5bb3\u91cc\uff0c\u53ea\u7559\u4e86\u2014\u622a\u5251\u67c4\u3002 \u6728\u9053\u4eba\u8eab\u4e0a\u4e5f\u5e26\u7740\u76bf\u8ff9\uff0c\u867d\u7136\u4e5f\u53d7\u4e86\u4f24\uff0c\u5374\u8fd8\u662f\u6700\u9547\u5b9a\u3002 \u201c\u51f6\u624b\u4e00\u5b9a\u8fd8\u5728\u8fd9\u91cc\uff0c\u771f\u76f8\u672b\u660e\u4e4b\u524d\uff0c\u5927\u5bb6\u6700\u597d\u5168\u90fd\u7559\u4e0b\u6765\u3002\u201d \u4e8b\u53d8\u975e\u5e38\uff0c\u4ed6\u7684\u53e3\u6c14\u4e5f\u53d8\u5f97\u5f88\u4e25\u8083\uff1a\u201c\u65e0\u8bba\u8c01\u53ea\u8981\u8d70\u51fa\u8fd9\u5927\u6bbf\u4e00\u6b65\uff0c\u90fd\u4e0d\u80fd\u6d17\u8131\u51f6\u624b\u7684\u5acc \u7591\uff0c\u90a3\u5c31\u4f11\u602a\u672c\u95e8\u5b50\u5f1f\uff0c\u8981\u5bf9\u8d35\u5ba2\u65e0\u793c\u4e86\u3002\u201d \u6ca1\u6709\u4eba\u6562\u8d70\uff0c\u6ca1\u6709\u4eba\u6562\u52a8\u3002 \u8fd9\u4ef6\u4e8b\u5b9e\u5728\u592a\u4e25\u91cd\uff0c\u8c01\u4e5f\u4e0d\u613f\u610f\u6cbe\u4e0a\u4e00\u70b9\u5acc\u7591\u3002 \u5947\u602a\u7684\u662f\uff0c\u7559\u5728\u5927\u6bbf\u91cc\u7684\u4eba\uff0c\u8eab\u4e0a\u90fd\u6ca1\u6709\u5175\u5203\uff0c\u6740\u4eba\u7684\u5200\u5251\u90fd\u54ea\u91cc\u6765\u7684\uff1f\u5230\u54ea\u91cc\u53bb\u4e86\uff1f \u77f3\u96c1\u4f24\u5f97\u867d\u4e0d\u91cd\uff0c\u5374\u663e\u5f97\u6bd4\u522b\u4eba\u66f4\u60b2\u54c0\u3001\u6124\u6012\u3001\u6cae\u4e27\u3002 \u6728\u9053\u4eba\u538b\u4f4e\u58f0\u97f3\uff0c\u9053\uff1a\u201c\u51f6\u624b\u7edd\u4e0d\u6b62\u4e00\u4e2a\u4eba\uff0c\u4ed6\u4eec\u4e00\u51fb\u5f97\u624b\uff0c\u5f88\u53ef\u80fd\u5df2\u4e58\u7740\u521a\u624d\u9ed1\u6697\u65f6\u5168 \u8eab\u800c\u9000\uff0c\u4f46\u5374\u4e0d\u53ef\u80fd\u5df2\u5168\u90fd\u9000\u4e86\u6b66\u5f53\u3002\u201d \u77f3\u96c1\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u65e2\u7136\u5927\u5bb6\u90fd\u5f97\u7559\u5728\u5927\u6bbf\u91cc\uff0c\u8c01\u53bb\u8ffd\u4ed6\u4eec\uff1f\u201d \u6728\u9053\u4eba\u9053\uff1a\u201c\u6211\u53bbaH \u4ed6\u770b\u4e86\u770b\u56db\u4e0b\u5f85\u547d\u7684\u6b66\u5f53\u5f1f\u5b50\uff1a\u201c\u6211\u8fd8\u5f97\u5e26\u51e0\u4e2a\u5f97\u529b\u7684\u4eba\u201d\u3002\u77f3\u96c1\u9053\uff1a\u201c\u672c\u95e8\u5f1f\u5b50\uff0c\u4f46 \u51ed\u5e08\u53d4\u8c03\u6d3e\u3002\u201d \u6728\u9053\u4eba\u7acb\u523b\u5c31\u8d70\u4e86\uff0c\u5e26\u8d70\u4e86\u5341\u4e2a\u4eba\uff0c\u5f53\u7136\u5168\u90fd\u662f\u6b66\u5f53\u95e8\u4e0b\u7684\u7cbe\u82f1\u3002 \u770b\u7740\u4ed6\u5306\u5306\u800c\u53bb\uff0c\u77f3\u96c1\u773c\u775b\u91cc\u5ffd\u7136\u9732\u51fa\u79cd\u5f88\u5947\u602a\u7684\u8868\u60c5\u3002 \u90a3\u9ad8\u5927\u5a01\u731b\u7684\u8001\u4eba\u5df2\u6084\u6084\u5230\u4e86\u4ed6\u8eab\u540e\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u679c\u7136\u5982\u6b64\u3002\u201d \u77f3\u96c1\u70b9\u70b9\u5934\uff0c\u5ffd\u7136\u632f\u4f5c\u8d77\u7cbe\u795e\uff0c\u9053\uff1a\u201c\u4e8b\u53d8\u975e\u5e38\uff0c\u53ea\u5f97\u59d4\u66f2\u5404\u4f4d\u5728\u6b64\u7a0d\u5019\uff0c\u65e0\u57a2\u5148\u5e26\u9886\u4f60 \u95e8\u4e0b\u5f1f\u5b50\uff0c\u5c06\u6b7b\u96be\u7684\u524d\u8f88\u4eec\u62fe\u5230\u542c\u7af9\u9662\u53bb\uff0c\u65e0\u955c\u3001\u65e0\u8272\u5e26\u9886\u5f1f\u5b50\u53bb\u5de1\u89c6\u5404\u5730\uff0c\u53ea\u8981\u53d1\u73b0\u2014\u4ef6 \u5175\u5203\uff0c\u5c31\u62a5\u4e0a\u6765\u3002\u201d\u9ad8\u5927\u5a01\u731b\u7684\u8001\u4eba\u9053\uff1a\u201c\u4f60\u6700\u597d\u8ba9\u4ed6\u4eec\u5148\u5ac2\u5ac2\u6211\u3002\u201d \u77f3\u96c1\u82e6\u7b11\u9053\uff1a\u201c\u4f60\u82e5\u8981\u6740\u4eba\uff0c\u53c8\u4f55\u5fc5\u7528\u5200\u5251\uff1f\u201d\u8001\u4eba\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4e5f\u60f3\u966a\u4f60\u5e08\u53d4\u8ffd\u51f6\u624b\u53bb\u3002\u201d \u77f3\u96c1\u9053\uff1a\u201c\u8bf7\u3002\u201d \u8001\u4eba\u62f1\u4e86\u62f1\u624b\uff0c\u4e00\u6325\u8170\uff0c\u5c31\u5df2\u7bad\u4e00\u822c\u7a9c\u51fa\u3002 \u7fa4\u8c6a\u4e2d\u7acb\u523b\u6709\u4eba\u4e0d\u6ee1\uff1a\u201c\u6211\u4eec\u4e0d\u80fd\u8d70\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u80fd\u8d70\uff1f\u201d \u201c\u56e0\u4e3a\u4ed6\u7684\u8eab\u5206\u548c\u522b\u4eba\u4e0d\u540c\u3002\u201d \u201c\u4ed6\u662f\u8c01\uff1f\u201d \u201c\u4ed6\u5c31\u662f\u90a3\u2026\u2026\u201d \u4e00\u58f0\u9a9a\u52a8\uff0c\u6df9\u6ca1\u4e86\u8fd9\u4eba\u7684\u58f0\u97f3\uff0c\u4e24\u4e2a\u7d2b\u8863\u9053\u4eba\u5927\u6b65\u5954\u5165\uff0c\u624b\u91cc\u68d2\u7740\u67c4\u957f\u5251\uff0c\u8d6b\u7136\u7adf\u662f\u6b66 \u5f53\u638c\u95e8\u4eba\u7684\u4e03\u661f\u5251\u3002 \u53ef\u662f\u4ed6\u4f69\u5e26\u7684\u53e6\u4e00\u4ef6\u5b9d\u7269\u7d2b\u91d1\u51a0\uff0c\u5374\u5df2\u5982\u9ec4\u9e64\u98de\u53bb\uff0c\u4e0d\u89c1\u5f71\u8e2a\u4e86\u3002 \u56db\u6708\u5341\u4e09\uff0c\u5348\u591c\u3002 \u591c\u51c9\u5982\u6c34\u3002 \u6b64\u65f6\u6b64\u523b\uff0c\u53ea\u6709\u4e00\u4e2a\u4eba\u77e5\u9053\u7d2b\u91d1\u51a0\u5728\u54ea\u91cc\uff0c\u8fd9\u4e2a\u4eba\u5f53\u7136\u5c31\u662f\u9646\u5c0f\u51e4\u3002 \u4ed6\u4e5f\u4e0d\u77e5\u4ece\u90a3\u91cc\u4e70\u4e86\u9876\u7279\u5927\u53f7\u7684\u8303\u9633\u6be1\u7b20\u6234\u5728\u5934\u4e0a\uff0c\u906e\u4f4f\u4e86\u4ed6\u5927\u534a\u8fb9\u8138\u3002 \u7d2b\u91d1\u51a0\u5c31\u5728\u4ed6\u5934\u4e0a\uff0c\u4e5f\u88ab\u6be1\u7b20\u76d6\u4f4f\u4e86\u3002 \u8fd9\u662f\u4ed6\u7528\u4ed6\u90a3\u4e24\u6839\u65e0\u4ef7\u7684\u624b\u6307\u4ece\u77f3\u96c1\u5934\u4e0a\u6458\u4e0b\u6765\u7684\uff0c\u4ed6\u603b\u7b97\u53c8\u6ca1\u6709\u5931\u624b\u3002 \u53ef\u662f\u5c31\u5728\u4ed6\u521a\u624d\u51fa\u624b\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u4ed6\u5168\u8eab\u7684\u8863\u886b\u90fd\u5df2\u6e7f\u900f\u3002 \u4ed6\u77e5\u9053\u8fd9\u6b21\u884c\u52a8\u5df2\u5b8c\u5168\u6210\u529f\uff0c\u63a0\u51fa\u5927\u6bbf\u65f6\uff0c\u4ed6\u5c31\u542c\u89c1\u94c1\u80a9\u4ed6\u4eec\u7684\u60e8\u547c\u58f0\u3002 \u73b0\u5728\u4ed6\u8eab\u4e0a\u8863\u670d\u65e9\u5df2\u5e72\u4e86\u3002\u4ed6\u5df2\u5728\u9644\u8fd1\u7684\u6697\u5df7\u4e2d\u515c\u4e86\u597d\u51e0\u4e2a\u5708\u5b50\uff0c\u786e\u5b9a\u4e86\u540e\u9762\u7edd\u6ca1\u6709\u8ddf \u8e2a\u4eba\uff0c\u7136\u540e\u624d\u4ece\u540e\u9662\u7684\u89d2\u95e8\u6e9c\u5165\u6ee1\u7fe0\u697c\u3002 \u540e\u56ed\u4e2d\u9759\u6084\u6084\u7684\uff0c\u542c\u4e0d\u89c1\u4eba\u58f0\uff0c\u4e5f\u770b\u4e0d\u89c1\u706f\u5149\u3002 \u201c\u90a3\u4e9b\u4eba\u96be\u9053\u8fd8\u6ca1\u6709\u56de\u6765\u201d\uff1f \u4ed6\u6b63\u60f3\u627e\u4e2a\u4eba\u95ee\u95ee\uff0c\u5ffd\u7136\u542c\u89c1\u516d\u89d2\u4ead\u7554\u7684\u82b1\u4e1b\u91cc\u6709\u4eba\u8f7b\u8f7b\u9053\uff1a\u201c\u5728\u8fd9\u91ccoo \u8fd9\u662f\u67f3\u9752\u9752\u7684\u58f0\u97f3\u3002 \u770b\u89c1\u9646\u5c0f\u51e4\u7684\u65f6\u5019\uff0c\u5979\u7684\u8868\u60c5\u5f88\u5947\u602a\uff0c\u53c8\u50cf\u662f\u60ca\u8bb6\uff0c\u53c8\u50cf\u662f\u6b22\u559c\uff1a\u201c\u4f60\u4e5f\u5f97\u624b\u4e86\uff1f\u201d \u9646\u5c0f\u51e4\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u522b\u4eba\u5462\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u5927\u5bb6\u5dee\u4e0d\u591a\u90fd\u5df2\u56de\u6765\u4e86\uff0c\u90fd\u5728\u7b49\u8001\u5200\u628a\u5b50\u3002\u201d \u5979\u54ac\u7740\u5634\u5507\uff0c\u7528\u773c\u89d2\u779f\u7740\u9646\u5c0f\u51e4\uff1a\u201c\u53ef\u662f\u6211\u771f\u60f3\u4e0d\u5230\u8fd9\u6b21\u771f\u4f1a\u6210\u529f\u7684\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u60f3\u4e0d\u5230\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u603b\u6709\u70b9\u7591\u5fc3\u4f60\uff0c\u5c24\u5176\u662f\u72ac\u90ce\u541b\u7684\u90a3\u4ef6\u4e8b\uff0c\u8fd8\u6709\u90a3\u4e2a\u66ff\u4f60\u6e9c\u72d7\u53bb\u7684\u5802 \u500c\uff0c\u53f6\u5bb6\u90a3\u4e2a\u6316\u86af\u8693\u7684\u4eba \u9646\u5c0f\u51e4\u7b11\u4e86\uff1a\u201c\u8fd9\u53ea\u80fd\u8bc1\u660e\u4e00\u4ef6\u4e8b\uff0c\u8bc1\u660e\u4f60\u7684\u7591\u5fc3\u75c5\u81f3\u5c11\u6bd4\u522b\u4eba\u5927\u5341\u500d\u3002\u201d \u67f3\u9752\u9752\u4e5f\u7b11\u4e86\uff0c\u521a\u62c9\u8d77\u4ed6\u7684\u624b\uff0c\u82b1\u4e1b\u91cc\u5ffd\u7136\u6709\u9053\u706f\u5149\u5c04\u51fa\u6765\u3002 \u5c0f\u7fe0\u6b63\u5728\u706f\u5149\u540e\u77aa\u7740\u4ed6\u4eec\uff1a\u201c\u597d\u5440\uff0c\u5927\u5bb6\u90fd\u5728\u4e0b\u9762\u7b49\uff0c\u4f60\u4eec\u5374\u8eb2\u5728\u8fd9\u91cc\u62c9\u7740\u624b\u8bf4\u6084\u6084\u8bdd \u9646\u5c0f\u51e4\u76f4\u5230\u73b0\u5728\u624d\u77e5\u9053\uff0c\u4ed6\u4eec\u805a\u4f1a\u7684\u79d8\u5ba4\uff0c\u7adf\u662f\u5728\u8fd9\u4e00\u4e1b\u6708\u5b63\u82b1\u4e0b\u3002 \u8fd9\u8ba1\u5212\u7684\u6bcf\u4e00\u4e2a\u7ec6\u8282\u867d\u7136\u65e9\u5c31\u5168\u90fd\u5b89\u6392\u597d\u4e86\uff0c\u53ef\u662f\u4e0d\u5230\u6700\u540e\u5173\u5934\uff0c\u9664\u4e86\u8001\u5200\u628a\u5b50\u5916\uff0c\u8fd8 \u662f\u6ca1\u6709\u522b\u4eba\u80fd\u5b8c\u5168\u77e5\u9053\u3002 \u76f4\u5230\u73b0\u5728\uff0c\u8fd8\u662f\u6ca1\u6709\u4eba\u80fd\u770b\u89c1\u4ed6\u771f\u9762\u76ee\uff1a\u201c\u53ef\u662f\u4ed6\u2014\u5b9a\u5f88\u5feb\u5c31\u4f1a\u6765\u4e86\u3002\u201d \u5bbd\u5927\u7684\u5730\u5ba4\uff0c\u901a\u98ce\u7684\u8bbe\u5907\u826f\u597d\uff0c\u5927\u5bb6\u7684\u547c\u5438\u5374\u8fd8\u662f\u5f88\u6025\u4fc3\u3002 \u53c2\u52a0\u8fd9\u6b21\u884c\u52a8\u7684\u4eba\uff0c\u73b0\u5728\u90fd\u5df2\u5230\u9f50\uff0c\u7adf\u5b8c\u5168\u6ca1\u6709\u610f\u5916\u7684\u5dee\u9519\uff0c\u4e5f\u6ca1\u6709\u4f24\u635f\u3002 \u53ea\u662f\u5f53\u65f6\u8fd9\u4e00\u77ac\u95f4\u7684\u7d27\u5f20\u548c\u523a\u6fc0\uff0c\u5374\u7edd\u4e0d\u662f\u5f88\u5feb\u5c31\u4f1a\u5e73\u9759\u7684\uff0c\u5927\u5bb6\u8fd8\u662f\u663e\u5f97\u5f88\u5174\u594b\uff0c\u51e0 \u4e4e\u6ca1\u6709\u4eba\u5f00\u53e3\u8bf4\u8bdd\u7684\u3002 \u6709\u4e9b\u4eba\u8863\u895f\u4e0a\u5e26\u7740\u8840\uff0c\u60f3\u5fc5\u662f\u56e0\u4e3a\u51fa\u624b\u65f6\u592a\u7528\u529b\uff0c\u523a\u5f97\u592a\u731b\uff0c\u6709\u7684\u4eba\u751a\u81f3\u8fde\u8138\u4e0a\u90fd\u88ab\u6e85 \u7740\u4e86\u76bf\u8ff9\u3002 \u4ed6\u4eec\u672c\u8be5\u9ad8\u5174\u4e9b\u7684\uff0c\u56e0\u4e3a\u4ed6\u4eec\u4eca\u5929\u665a\u4e0a\u505a\u7684\u4e8b\uff0c\u65e0\u7591\u5fc5\u5c06\u4f1a\u6539\u53d8\u5929\u4e0b\u6b66\u6797\u7684\u5386\u53f2\u548c\u547d \u8fd0\u3002 \u201c\u8fd9\u91cc\u4e3a\u4ec0\u4e48\u6ca1\u6709\u9152\uff1f\u5927\u529f\u5df2\u544a\u6210\u4e86\uff0c\u6211\u4eec\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u80fd\u559d\u4e24\u676f\u5e86\u795d\u5e86\u795d\uff1f\u201d \u201c\u56e0\u4e3a\u8001\u5200\u628a\u5b50\u8fd8\u6ca1\u6709\u56de\u6765\u3002\u201d \u201c\u4ed6\u4e3a\u4ec0\u4e48\u8fd8\u6ca1\u6709\u56de\u6765\uff1f\u201d \u201c\u56e0\u4e3a\u4ed6\u8fd8\u6709\u5f88\u591a\u4e8b\u8981\u505a\u3002\u201d\u58f0\u97f3\u6765\u81ea\u5730\u5ba4\u5916\uff1a\u201c\u4ed6\u8fd8\u8981\u66ff\u4f60\u4eec\u963b\u6321\u8ffd\u5175\uff0c\u6e05\u70b9\u6218\u679cu\u201d \u8001\u5200\u628a\u5b50\u7ec8\u4e8e\u51fa\u73b0\u4e86\uff0c\u6218\u679c\u65e0\u7591\u5f88\u8f89\u714c\uff0c\u8fde\u4ed6\u7684\u58f0\u97f3\u90fd\u2019\u5df2\u56e0\u5174\u594b\u800c\u663e\u5f97\u6709\u4e9b\u5636\u54d1\u3002 \u7136\u540e\u4ed6\u5c31\u6b63\u5f0f\u5ba3\u5e03\uff1a\u201c\u4e00\u51fb\u547d\u4e2d\uff0c\u5143\u51f6\u5c3d\u8bdb\uff0c\u5929\u96f7\u884c\u52a8\uff0c\u5b8c\u5168\u6210\u529fJ\u201d \u614e\u91cd\u5468\u5bc6\u7684\u8ba1\u5212\uff0c\u8fc5\u901f\u51c6\u786e\u7684\u884c\u52a8\uff0c\u53ea\u8981\u80fd\u505a\u5230\u8fd9\u4e24\u70b9\uff0c\u65e0\u8bba\u4ec0\u4e48\u4e8b\u90fd\u4f1a\u6210\u529f\u7684\u3002 \u4f46\u662f\u8001\u5200\u628a\u5b50\u5374\u597d\u50cf\u5fd8\u8bb0\u4e86\u4e00\u4ef6\u4e8b\u3002 \u4ed6\u5e76\u6ca1\u6709\u95ee\u9646\u5c0f\u51e4\u662f\u5426\u5f97\u624b\uff0c\u600e\u4e48\u4f1a\u77e5\u9053\u8fd9\u6b21\u884c\u52a8\u5df2\u5b8c\u5168\u6210\u529f\uff1f\u9664\u975e\u706f\u4eae\u540e\u4ed6\u8fd8\u5728\u5927\u6bbf \u91cc\uff0c\u5df2\u770b\u89c1\u7d2b\u91d1\u51a0\u4e0d\u5728\u77f3\u96c1\u5934\u4e0a\u3002 \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u5fd8\u4e86\u5411\u6211\u8981\u6837\u4e1c\u897f\uff1f\u201d \u4ed6\u5ffd\u7136\u6458\u4e0b\u6be1\u7b20\uff0c\u7d2b\u91d1\u51a0\u7acb\u523b\u5728\u706f\u4e0b\u6563\u53d1\u51fa\u8f89\u714c\u7f8e\u4e3d\u7684\u5149\u5f69\u3002 \u8001\u5200\u628a\u5b50\u5374\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u6025\u3002\u201d \u9646\u5c0f\u51e4\u7b11\u4e86\uff1a\u201c\u4f60\u5f53\u7136\u4e0d\u6025\uff0c\u56e0\u4e3a\u4f60\u8981\u7684\u672c\u5c31\u4e0d\u662f\u8fd9\u9876\u7d2b\u91d1\u51a0\uff0c\u800c\u662f\u90a3\u628a\u4e03\u661f\u5251\u3002\u201d \u8fd9\u4e9b\u8bdd\u4ed6\u672c\u4e0d\u60f3\u8bf4\u7684\uff0c\u5374\u5ffd\u7136\u6709\u4e86\u79cd\u5fcd\u4e0d\u4f4f\u8981\u8bf4\u51fa\u6765\u7684\u51b2\u52a8\uff1a\u201c\u6211\u53bb\u6458\u7d2b\u91d1\u51a0\u65f6\uff0c\u77f3\u96c1\u4e00 \u5b9a\u4f1a\u4f38\u624b\u5230\u5934\u4e0a\u53bb\u6276\uff0c\u4f60\u624d\u6709\u673a\u4f1a\u593a\u4ed6\u8170\u4e0b\u7684\u5251\u3002\u201d \u8001\u5200\u628a\u5b50\u51b7\u51b7\u7684\u770b\u7740\u4ed6\uff0c\u7b49\u7740\u4ed6\u8bf4\u4e0b\u53bb\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u90a3\u79d8\u5bc6\u867d\u7136\u4e00\u76f4\u90fd\u5728\u5251\u67c4\u91cc\uff0c\u77f3\u96c1\u5374\u4ece\u6765\u6ca1\u6709\u7528\u5b83\u8981\u631f\u8fc7\u4efb\u4f55\u4eba\uff0c\u4f46\u4f60\u5374\u8fd8 \u662f\u4e0d\u653e\u5fc3\uff0c\u56e0\u4e3a\u90a3\u5176\u4e2d\u6700\u5927\u7684\u4e00\u4e2a\u79d8\u5bc6\uff0c\u5c31\u662f\u4f60\u7684\u79d8\u5bc6\uff0c\u6240\u4ee5\u4f60\u4e00\u5b9a\u8981\u4eb2\u624b\u593a\u4ed6\u7684\u5251\uff0c\u7edd\u4e0d \u8ba9\u8fd9\u79d8\u5bc6\u518d\u7ecf\u8fc7\u7b2c\u4e8c\u4e2a\u4eba\u7684\u624b\u3002\u201d \u8001\u5200\u628a\u5b50\u5c45\u7136\u5e76\u4e0d\u5426\u8ba4\uff1a\u201c\u53ef\u662f\u4ed6\u7684\u624b\u4e00\u76f4\u90fd\u6276\u5728\u5251\u67c4\u4e0a\uff0c\u6240\u4ee5\u6211\u624d\u7528\u5f97\u7740\u4f60\uff0c\u4ee5\u540e\u4ed6\u4e00 \u5b9a\u4f1a\u8ba4\u4e3a\u8fd9\u6b21\u884c\u52a8\u7684\u4e3b\u8c0b\u5c31\u662f\u4f60\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u521a\u624d\u51fa\u624b\u65f6\uff0c\u4e00\u5b9a\u5f88\u7528\u529b\uff0c\u7d2b\u91d1\u51a0\u4e0a\u4e00\u5b9a\u5df2\u88ab\u4f60\u634f\u51fa\u4e86\u4e24\u4e2a\u6307\u75d5\uff0c \u80fd\u7528\u4e24\u6839\u624b\u6307\u6458\u4e0b\u4ed6\u7740\u4e0a\u7684\u9053\u51a0\u7684\u4eba\uff0c\u9664\u4e86\u9646\u5c0f\u51e4\u5916\uff0c\u4e16\u4e0a\u53ea\u6015\u8fd8\u6ca1\u6709\u7b2c\u4e8c\u4e2a\uff0c\u8fd9\u5c31\u662f\u6700\u597d \u7684\u8bc1\u636e\u3002\u201d \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e0d\u4f46\u8981\u6211\u53bb\u5206\u6563\u4ed6\u7684\u6ce8\u610f\u529b\uff0c\u8fd8\u8981\u6211\u53bb\u66ff\u4f60\u80cc\u9ed1\u9505\u3002\u201d\u8001 \u5200\u628a\u5b50\u9053\uff1a\u201c\u8fd9\u5c31\u53eb\u4e00\u77f3\u4e8c\u9e1f\u4e4b\u8ba1\u3002\u201d \u8fd9\u4e00\u70b9\u624d\u662f\u6574\u4e2a\u8ba1\u5212\u4e2d\u6700\u540e\u7684\u5173\u952e\uff0c\u9646\u5c0f\u51e4\u76f4\u5230\u73b0\u5728\u624d\u5b8c\u5168\u660e\u767d\u3002 \u4ed6\u53ea\u6709\u82e6\u7b11\uff1a\u201c\u4f46\u6211\u8fd8\u662f\u4e0d\u660e\u767d\uff0c\u4f60\u65e2\u7136\u5df2\u593a\u4e0b\u4ed6\u7684\u5251\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u7d22\u6027\u6740\u4e86\u4ed6\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u53cd\u6b63\u5df2\u6d3b\u4e0d\u957f\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u5403\u60ca\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u5df2\u5f97\u4e86\u7edd\u75c7\uff0c\u4ed6\u7684\u5bff\u547d\u6700\u591a\u5df2\u53ea\u6709\u4e24\u4e09\u4e2a\u6708aD \u9646\u5c0f\u51e4\u53f9\u9053\uff1a\u201c\u8fd9\u5c31\u96be\u602a\u4ed6\u6025\u7740\u8981\u63d0\u524d\u518c\u7acb\u7ee7\u627f\u4ed6\u7684\u4eba \u8001\u5200\u628a\u5b50\u51b7\u51b7\u9053\uff1a\u201c\u53ea\u53ef\u60dc\u73b0\u5728\u80fd\u591f\u62c5\u5f53\u91cd\u4efb\u7684\u6b66\u5f53\u5f1f\u5b50\uff0c\u90fd\u5df2\u6b7b\u5728\u6211\u4eec\u624b\u91cc\u3002\u201d \u9646\u5c0f\u51e4\u76ef\u7740\u4ed6\uff0c\u9053\uff1a\u201c\u6240\u4ee5\u4ed6\u73b0\u5728\u53ea\u80fd\u5c06\u638c\u95e8\u4e4b\u4f4d\u4f20\u7ed9\u4f60\u3002\u201d \u8001\u5200\u628a\u5b50\u7684\u624b\u7a81\u7136\u63e1\u7d27\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u662f\u4e2a\u806a\u660e\u4eba\uff0c\u8fd9\u4e9b\u8bdd\u4f60\u672c\u4e0d\u8be5\u8bf4\u51fa\u6765\u7684\u3002\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u201c\u53ea\u53ef\u60dc\u6211\u5fcd\u4e0d\u4f4f\u8981\u8bf4\u3002\u201d \u8001\u5200\u628a\u5b50\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u5a04\u91d1\u6c0f\u3001\u5173\u5929\u6b66\u3001\u675c\u94c1\u5fc3\uff0c\u9ad8\u6d9b\u3001\u6d77\u5947\u9614\u3001\u987e\u98de\u4e91\u3002\u201d \u4ed6\u53eb\u51fa\u4e00\u4e2a\u4eba\u7684\u540d\u5b57\uff0c\u8fd9\u4e2a\u4eba\u7acb\u523b\u5c31\u7ad9\u4e86\u51fa\u6765\uff0c\u77aa\u7740\u9646\u5c0f\u98ce\u3002 \u8001\u5200\u628a\u5b50\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u516d\u4e2a\u4eba\u80fd\u4e0d\u80fd\u5236\u5f97\u4f4f\u4f60\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ea\u8981\u4e24\u4e09\u4e2a\u5c31\u8db3\u591f\u4e86\uff01 \u8001\u5200\u628a\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u96be\u9053\u8fd8\u8981\u4ed6\u4eec\u51fa\u624b\uff1f\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u4e0d\u60f3\u8981\u4ed6\u4eec\u51fa\u624b\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u90a3\u672b\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u675f\u624b\u5c31\u7f1a\uff1f\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u51fa\u624b \u7684\u3002 \u8001\u5200\u628a\u5b50\u5389\u58f0\u9053\uff1a\u201c\u62ff\u4e0b\u4ed6\uff01 \u4ed6\u53eb\u7684\u58f0\u97f3\u867d\u5927\uff0c\u8fd9\u516d\u4e2a\u4eba\u5374\u597d\u50cf\u5ffd\u7136\u53d8\u6210\u4e86\u804b\u5b50\uff0c\u8fde\u52a8\u90fd\u4e0d\u52a8\u3002 \u8001\u5200\u628a\u5b50\u77b3\u5b54\u6536\u7f29\uff0c\u9646\u5c0f\u51e4\u5374\u7b11\u4e86\u3002\u4ed6\u5fae\u7b11\u7740\u9053\uff1a\u201c\u73b0\u5728\u4ed6\u4eec\u82e5\u662f\u51fa\u624b\uff0c\u53ea\u4f1a\u53bb\u62ff\u4e00\u4e2a\u4eba \u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u8c01\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u3002\u201d \u516d\u4e2a\u4eba\u679c\u7136\u540c\u65f6\u8f6c\u8eab\uff0c\u9762\u5bf9\u7740\u8001\u5200\u628a\u5b50\uff0c\u540c\u65f6\u9053\uff1a\u201c\u4f60\u96be\u9053\u8fd8\u8981\u7b49\u6211\u4eec\u51fa\u624b\uff1f\u201d \u8001\u5200\u628a\u5b50\u5168\u8eab\u50f5\u786c\uff1a\u201c\u82e5\u6ca1\u6709\u6211\uff0c\u73b0\u5728\u4f60\u4eec\u8fde\u5c38\u9aa8\u90fd\u5df2\u70c2\u5149\u4e86\uff0c\u4f60\u4eec\u7adf\u6562\u80cc\u53db\u6211\u201d \u9646\u5c0f\u51e4\u62a2\u7740\u9053\uff1a\u201c\u4ed6\u4eec\u5e76\u4e0d\u60f3\u80cc\u53db\u4f60\uff0c\u53ea\u602a\u4f60\u81ea\u5df1\u505a\u9519\u4e86\u4e8b\u3002\u201d \u5730\u5ba4\u4e2d\u5c45\u7136\u4e00\u76f4\u90fd\u5f88\u5b89\u9759\uff0c\u9664\u4e86\u67f3\u9752\u9752\u548c\u5c0f\u7fe0\u5916\uff0c\u6bcf\u4e2a\u4eba\u90fd\u663e\u5f97\u51fa\u5947\u7684\u9547\u5b9a\uff0c\u8fd9\u4e9b\u60ca\u4eba \u7684\u53d8\u5316\uff0c\u7adf\u4f3c\u65e9\u5c31\u5728\u4ed6\u4eec\u610f\u6599\u4e4b\u4e2d\u3002 \u96be\u9053\u8fd9\u4e9b\u4eba\u5df2\u5168\u90fd\u80cc\u53db\u4e86\u4ed6\uff1f \u8001\u5200\u628a\u5b50\u7684\u624b\u63e1\u5f97\u66f4\u7d27\uff0c\u9053\uff1a\u201c\u6211\u505a\u4e86\u4ec0\u4e48\u4e8b\uff1f\u2019\u2019 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u7684\u8ba1\u5212\u867d\u5468\u5bc6\u5de7\u5999\uff0c\u5374\u6709\u4e2a\u81f4\u547d\u7684\u6f0f\u6d1e\u3002 \u8001\u5200\u628a\u5b50\u4e0d\u4fe1\u3002 \u4ed6\u7684\u786e\u65e0\u6cd5\u76f8\u4fe1\uff0c\u8fd9\u8ba1\u5212\u4ed6\u5df2\u53cd\u590d\u601d\u8651\u8fc7\u65e0\u6570\u6b21\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u8fd9\u8ba1\u5212\u4e2d\u6700\u5de7\u5999\u7684\u4e00\u70b9\uff0c\u5c31\u662f\u4f60\u6d3e\u51fa\u6765\u53c2\u52a0\u8fd9\u6b21\u884c\u52a8\u7684\u672c\u90fd\u662f\u6b7b\u4eba\uff0c\u4f60\u518d\u5c06 \u4ed6\u4eec\u6539\u626e\u6210\u53e6\u5916\u4e00\u4e2a\u6839\u672c\u4e0d\u5b58\u5728\u7684\u4eba\uff0c\u6c5f\u6e56\u4e2d\u5f53\u7136\u6ca1\u6709\u4eba\u4f1a\u6ce8\u610f\u4ed6\u4eec\u7684\u884c\u52a8\u3002 \u4ed6\u7b11\u4e86\u7b11\uff1a\u201c\u53ea\u53ef\u60dc\u8fd9\u4e00\u70b9\u504f\u504f\u4e5f\u5c31\u662f\u4f60\u8ba1\u5212\u4e2d\u6700\u5927\u7684\u6f0f\u6d1e\u3002\u201d \u8001\u5200\u628a\u5b50\u4e0d\u61c2\u3002 \u8fd9\u4e9b\u8bdd\u7684\u786e\u5e76\u4e0d\u662f\u5f88\u5bb9\u6613\u5c31\u80fd\u8ba9\u4eba\u542c\u61c2\u7684\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u82e5\u5c06\u9ad8\u6d9b\u626e\u6210\u6c34\u4e0a\u98de\uff0c\u72ac\u90ce\u541b\u7684\u6613\u5bb9\u672f\u7eb5\u7136\u5999\u7edd\u5929\u4e0b\uff0c\u8fd8\u662f\u6709\u4eba\u4f1a\u8ba4\u51fa\u4ed6 \u6765\u7684\uff0c\u81f3\u5c11\u6c34\u4e0a\u98de\u7684\u670b\u53cb\u548c\u4eb2\u4eba\u603b\u80fd\u8ba4\u5f97\u51fa\u3002\u201d \u4ed6\u62cd\u4e86\u62cd\u201c\u7ba1\u5bb6\u5a46\u201d\u7684\u80a9\uff1a\u201c\u53ef\u662f\u4f60\u5c06\u4ed6\u626e\u6210\u4e86\u8fd9\u6837\u5b50\uff0c\u4e16\u4e0a\u6839\u672c\u5c31\u6ca1\u6709\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\u5b58 \u5728\uff0c\u5f53\u7136\u4e5f\u5c31\u6ca1\u6709\u4eba\u80fd\u8ba4\u5f97\u51fa\u4ed6\u3002\u201d \u8fd9\u4e9b\u8bdd\u8bf4\u5f97\u5c31\u6bd4\u8f83\u5bb9\u6613\u8ba9\u4eba\u542c\u61c2\u4e86\u3002\u8001\u5200\u628a\u5b50\u5f53\u7136\u4e5f\u61c2\uff0c\u8fd9\u672c\u662f\u4ed6\u8ba1\u5212\u4e2d\u6700\u57fa\u672c\u7684\u4e00\u4e2a \u73af\u8282\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ef\u662f\u4f60\u5ffd\u7565\u4e86\u4e00\u70b9\u3002\u201d \u8001\u5200\u628a\u5b50\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u9646\u5c0f\u51e4\u53c8\u6307\u4e86\u6307\u201c\u7ba1\u5bb6\u5a46\u201d\u7684\u8138\uff1a\u201c\u9ad8\u6d9b\u80fd\u626e\u6210\u8fd9\u6837\u5b50\uff0c\u522b\u4eba\u5f53\u7136\u4e5f\u80fd\u626e\u6210\u8fd9\u6837\u5b50\u3002\u201d \u8001\u5200\u628a\u5b50\u627f\u8ba4\u3002 \u53ea\u8981\u6709\u4e00\u5f20\u5236\u4f5c\u7cbe\u5de7\u7684\u4eba\u76ae\u9762\u5177\uff0c\u518d\u52a0\u4e0a\u4e00\u4e2a\u6613\u5bb9\u597d\u624b\uff0c\u4efb\u4f55\u4eba\u90fd\u80fd\u626e\u6210\u8fd9\u6837\u5b50\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u9ad8\u6d9b\u626e\u6210\u8fd9\u6837\u5b50\u540e\uff0c\u6ca1\u6709\u4eba\u80fd\u8ba4\u5f97\u51fa\u4ed6\uff0c\u522b\u4eba\u626e\u6210\u8fd9\u6837\u5b50\uff0c\u5f53\u7136\u4e5f\u6ca1\u6709\u4eba\u80fd \u8ba4\u5f97\u51fa\u3002\u201d \u56e0\u4e3a\u4e16\u4e0a\u6839\u672c\u5c31\u6ca1\u6709\u8fd9\u4e48\u6837\u4e00\u4e2a\u4eba\u5b58\u5728\uff0c\u6240\u4ee5\u4e5f\u6ca1\u6709\u4eba\u4f1a\u53bb\u6ce8\u610f\u4ed6\uff0c\u8fde\u8001\u5200\u628a\u5b50\u90fd\u4e0d\u4f8b \u5916\u3002 \u8001\u5200\u628a\u5b50\u7684\u624b\u7a81\u7136\u5f00\u59cb\u53d1\u6296\uff0c\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4e2a\u4eba\u5df2\u4e0d\u662f\u9ad8\u6d9b\uff1f\u201d \u9646\u5c0f\u51e4\u7b11\u9053\uff1a\u201c\u4f60\u603b\u7b97\u660e\u767d\u6211\u7684\u610f\u601d\u4e86aH \u8fd9\u201c\u7ba1\u5bb6\u5a46\u201d\u4e5f\u7b11\u4e86\u7b11\uff0c\u7528\u529b\u6495\u4e0b\u8138\u4e0a\u4e00\u5f20\u4eba\u76ae\u9762\u5177\uff0c\u7adf\u662f\u4e2a\u5e74\u7eaa\u5e76\u4e0d\u592a\u5927\u7684\u5973\u4eba\u3002 \u8fd9\u4e2a\u4eba\u5f53\u7136\u4e0d\u662f\u9ad8\u6d9b\u3002 \u9646\u5c0f\u51e4\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u5c31\u662f\u6614\u5e74\u516c\u5b59\u5927\u5a18\u7684\u597d\u59d0\u59b9\uff0c\u4e5f\u662f\u6211\u7684\u597d\u670b\u53cb\uff0c\u6211\u4e00\u65f6\u627e\u4e0d\u5230\u9ad8 \u6d9b\u90a3\u6837\u4e0d\u7537\u4e0d\u5973\u7684\u7ba1\u5bb6\u5a46\uff0c\u53ea\u597d\u627e\u5979\u6765\u5e2e\u5fd9\u4e86\u3002\u201d \u8001\u5200\u628a\u5b50\u6014\u4f4f\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u80fd\u5c06\u9ad8\u6d9b\u626e\u6210\u8fd9\u6837\u5b50\uff0c\u6211\u5f53\u7136\u4e5f\u80fd\u8bf7\u4eba\u5c06\u5979\u626e\u6210\u8fd9\u6837\u5b50\u3002\u201d \u8001\u5200\u628a\u5b50\u6068\u6068\u9053\uff1a\u201c\u662f\u4e0d\u662f\u72ac\u90ce\u541b\u51fa\u5356\u4e86\u6211\uff1f\u201d \u9646\u5c0f\u51e4\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u56e0\u4e3a\u4ed6\u4e5f\u662f\u4eba\uff0c\u5e76\u4e0d\u662f\u72d7\uff0c\u8fde\u72d7\u88ab\u903c\u6025\u4e86\u4e5f\u4f1a\u8df3\u5899\uff0c\u4f55\u51b5\u4eba\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4ed6\u8fd8\u6ca1\u6709\u6b7b\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u82e5\u6b7b\u4e86\uff0c\u6211\u4eec\u600e\u4e48\u80fd\u5c06\u8fd9\u4f4d\u59d1\u5a18\u626e\u5f97\u548c\u90a3\u7ba1\u5bb6\u5a46\u4e00\u6a21\u4e00\u6837\uff0c\u8fde\u4f60\u90fd\u770b\u4e0d \u51fa\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u8fd9\u5f20\u9762\u5177\u4e5f\u662f\u9ad8\u6d9b\u8138\u4e0a\u7684\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u662f\u4ece\u4ed6\u8138\u4e0a\u5265\u4e0b\u6765\u7684\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u9ad8\u6d9b\u5462\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u7ba1\u7684\u4e8b\u592a\u591a\u4e86\uff0c\u5df2\u7ecf\u5e94\u8be5\u4f11\u606f\u4f11\u606f\u3002\u201d \u67f3\u9752\u9752\u5ffd\u7136\u9053\uff1a\u201c\u5c31\u662f\u90a3\u5929\u665a\u4e0a\uff0c\u5728\u53f6\u51cc\u98ce\u7684\u5c71\u5e84\u91cc\uff0c\u4f60\u505a\u7684\u624b\u811a\u3002\u201d \u73b0\u5728\u5979\u624d\u60f3\u5230\uff0c\u90a3\u5929\u665a\u4e0a\u706f\u706d\u4e86\u7684\u65f6\u5019\uff0c\u4e3a\u4ec0\u4e48\u627e\u4e0d\u5230\u4ed6\u4eec\u7684\u4eba\u4e86\u3002 \u9646\u5c0f\u51e4\u5c06\u9ad8\u6d9b\u3001\u987e\u98de\u4e91\u3001\u6d77\u5947\u9614\u5236\u4f4f\uff0c\u5c06\u53e6\u5916\u4e09\u4e2a\u4eba\u6539\u626e\u6210\u4ed6\u4eec\u7684\u6837\u5b50\uff0c\u800c\u4e14\u662f\u7528\u540c\u4e00 \u5f20\u4eba\u76ae\u9762\u5177\uff0c\u7ecf\u540c\u2014\u4eba\u7684\u624b\u6539\u626e\u7684\u3002 \u67f3\u9752\u9752\u9053\uff1a\u201c\u90a3\u5929\u72ac\u90ce\u541b\u4e5f\u5728\uff1f\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u4e00\u76f4\u90fd\u5728\u90a3\u91cc\u7b49\u7740\u3002\u201d \u4ed6\u5fae\u7b11\u7740\u9053\uff1a\u201c\u6211\u4eec\u4e0b\u5c71\u7684\u7b2c\u4e8c\u5929\uff0c\u6211\u5df2\u53eb\u4eba\u627e\u4e86\u6761\u540c\u6837\u7684\u72d7\u6765\uff0c\u4e58\u7740\u6e9c\u72d7\u7684\u65f6\u5019\uff0c\u5c06\u4ed6 \u6389\u4e86\u5305\u3002\u201d \u72d7\u7684\u6837\u5b50\u90fd\u5dee\u4e0d\u591a\u7684\uff0c\u9664\u4e86\u5f88\u4eb2\u8fd1\u5b83\u7684\u4eba\u4e4b\u5916\uff0c\u5f53\u7136\u66f4\u4e0d\u4f1a\u6709\u4eba\u80fd\u5206\u8fa8\u5f97\u51fa\u3002 \u67f3\u9752\u9752\u53f9\u9053\uff1a\u201c\u6211\u65e9\u5c31\u89c9\u5f97\u66ff\u4f60\u6e9c\u72d7\u7684\u90a3\u4e2a\u5802\u500c\u53ef\u7591\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u7b11\u9053\uff1a\u201c\u4f60\u7684\u7591\u5fc3\u75c5\u4e00\u5411\u5f88\u91cdo)\u2019 \u67f3\u9752\u9752\u9053\uff1a\u201c\u90a3\u4e2a\u6316\u86af\u8693\u7684\u4eba\u5462\uff1f\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ed6\u5c31\u662f\u90a3\u4e2a\u66ff\u6211\u6e9c\u72d7\u7684\u5802\u500c\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u4ed6\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53f8\u7a7a\u6458\u661f\uff01\u201d \u5f53\u7136\u662f\u53f8\u7a7a\u6458\u661f\u3002 \u8fd9\u540d\u6ee1\u5929\u4e0b\u7684\u72ec\u884c\u4fa0\u76d7\uff0c\u4e0d\u4f46\u8f7b\u529f\u9ad8\u7edd\uff0c\u673a\u667a\u8fc7\u4eba\uff0c\u800c\u4e14\u4ed6\u81ea\u5df1\u662f\u4e2a\u6613\u5bb9\u597d\u624b\u3002 \u67f3\u9752\u9752\u9053\uff1a\u201c\u96be\u9053\u8fd9\u91cc\u6240\u6709\u7684\u4eba\u90fd\u5df2\u4e0d\u662f\u539f\u6765\u90a3\u4e2a\u4eba\u4e86oo \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ea\u6709\u4e24\u4e2a\u4eba\u8fd8\u662f\u7684\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u54ea\u4e24\u4e2a\uff1f\u201d\u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e00\u4e2a\u6211\uff0c\u4e00\u4e2a\u4f60\u3002\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u90a3\u5929\u4f60\u4eec\u4e3a\u4ec0\u4e48\u6ca1\u6709\u5bf9\u6211\u4e0b\u624b\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u548c\u8001\u5200\u628a\u5b50\u592a\u63a5\u8fd1\uff0c\u6211\u4eec\u6015\u4ed6\u770b\u51fa\u7834\u7efd\u6765\u2026\u2026\u201d \u67f3\u9752\u9752\u54ac\u7740\u7259\uff0c\u5ffd\u7136\u4e00\u62f3\u5f80\u4ed6\u7684\u9f3b\u5b50\u4e0a\u6253\u4e86\u8fc7\u53bb\u3002 \u9646\u5c0f\u51e4\u6ca1\u6709\u95ea\u907f\uff0c\u5979\u4e5f\u6ca1\u6709\u6253\u7740\u3002 \u5979\u7684\u624b\u5f88\u5feb\u5c31\u5f7c\u4eba\u62c9\u4f4f\uff0c\u53ef\u662f\u5979\u7684\u773c\u775b\u5374\u8fd8\u5728\u72e0\u72e0\u7684\u77aa\u7740\u9646\u5c0f\u51e4\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6211\u53ea\u60f3\u8981\u4f60 \u660e\u767d\u4e00\u4ef6\u4e8b\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u2018\u4ec0\u4e48\u4e8b\uff1f\u201d \u67f3\u9752\u9752\u9053\uff1a\u201c\u73b0\u5728\u552f\u4e00\u8ddf\u6211\u6700\u63a5\u8fd1\u7684\u4eba\u5c31\u662f\u4f60\u300f\u201d \u9646\u5c0f\u51e4\u5fc3\u91cc\u6709\u70b9\u9178\uff0c\u4e5f\u6709\u70b9\u75bc\u3002 \u53ef\u662f\u4e00\u4e2a\u4eba\u82e5\u662f\u8981\u505a\u4e00\u4ef6\u5bf9\u5f88\u591a\u4eba\u90fd\u6709\u597d\u5904\u7684\u4e8b\uff0c\u603b\u4e0d\u80fd\u4e0d\u727a\u7272\u4e00\u70b9\u7684\u3002 \u4ed6\u5c3d\u91cf\u88c5\u4f5c\u6ca1\u6709\u770b\u89c1\u5979\u773c\u4e2d\u7684\u6cea\u75d5\uff0c\u5c3d\u91cf\u4e0d\u53bb\u60f3\u8fd9\u4ef6\u4e8b\u3002 \u5c31\u7b97\u8981\u5fcf\u4fae\u6d41\u6cea\uff0c\u4e5f\u53ef\u4ee5\u7b49\u5230\u660e\u5929\uff0c\u73b0\u5728\u8fd8\u6709\u5f88\u591a\u522b\u7684\u4e8b\u8981\u505a\u3002 \u56e0\u4e3a\u4ed6\u662f\u9646\u5c0f\u51e4\u3002 \u6709\u4eba\u62e8\u4eae\u4e86\u706f\u5149\uff0c\u5730\u5ba4\u4e2d\u66f4\u660e\u4eae\u3002 \u8001\u5200\u628a\u5b50\u53cd\u800c\u9547\u5b9a\u4e86\u4e0b\u6765\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u65e2\u7136\u65e9\u5df2\u63a7\u5236\u4e86\u5c40\u9762\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u8981\u6309\u7167\u6211\u7684\u8ba1 \u5212\u884c\u4e8b\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4eec\u8fd8\u4e0d\u77e5\u9053\u8001\u5200\u628a\u5b50\u7a76\u7adf\u662f\u8c01\uff0c\u6240\u4ee5\u2014\u5b9a\u8981\u8bf1\u4f60\u5165\u5f40\u3002 \u8fd9\u624d\u662f\u4ed6\u6574\u4e2a\u8ba1\u5212\u7684\u5173\u8282\uff0c\u76f4\u5230\u73b0\u5728\uff0c\u4ed6\u8fd8\u6ca1\u6709\u770b\u89c1\u8001\u5200\u628a\u5b50\u7684\u771f\u9762\u76ee\u3002 \u8fd8\u6ca1\u6709\u4eba\u770b\u89c1\u8fc7\u3002 \u8001\u5200\u628a\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u73b0\u5728\u4f60\u4eec\u603b\u7b97\u5f88\u5feb\u5c31\u53ef\u4ee5\u77e5\u9053\u6211\u662f\u8c01\u4e86\uff0c\u53ea\u53ef\u60dc\u94c1\u80a9\u4ed6\u4eec\u5df2\u6c38\u8fdc\u65e0\u6cd5 \u77e5\u9053\uff1a\u201c \u9646\u5c0f\u51e4\u5ffd\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u771f\u7684\u4ee5\u4e3a\u4ed6\u4eec\u5df2\u5168\u90fd\u6b7b\u4e86\uff1f\u4f60\u770b\u770b\u8fd9\u4e9b\u4eba\u662f\u8c01\uff1f \u5730\u5ba4\u7684\u4eba\u53e3\u5ffd\u7136\u6253\u5f00\uff0c\u4e00\u884c\u4eba\u6162\u6162\u7684\u8d70\u4e0b\u6765\uff0c\u6b63\u662f\u521a\u624d\u5df2\u5012\u5728\u8840\u6cca\u4e2d\u7684\u94c1\u80a9\u3001\u738b\u5341\u888b\u3001 \u9ad8\u884c\u7a7a\u3001\u6c34\u4e0a\u98de\u3001\u5df4\u5c71\u5c0f\u987e\u3001\u9e70\u773c\u8001\u4e03\uff0c\u548c\u6b66\u5f53\u5f1f\u5b50\u4e2d\u4e94\u5927\u9ad8\u624b\u3002 \u90a3\u9ad8\u5927\u5a01\u731b\u7684\u8001\u4eba\u5c45\u7136\u4e5f\u5728\u5176\u4e2d\u3002 \u77f3\u96c1\u8d70\u5728\u6700\u540e\u3002 \u4ed6\u521a\u8d70\u4e0b\u6765\uff0c\u5730\u5ba4\u7684\u95e8\u8fd8\u5f00\u7740\u3002 \u9646\u5c0f\u51e4\u6b63\u5728\u8bf4\uff1a\u201c\u6709\u4e86\u738b\u8001\u524d\u8f88\u3001\u53f8\u7a7a\u6458\u661f\u548c\u72ac\u90ce\u541b\u8fd9\u6837\u7684\u6613\u5bb9\u597d\u624b\uff0c\u8981\u5047\u6b7b\u5f53\u7136\u5e76\u4e0d\u662f \u4ef6\u5f88\u56f0\u96be\u7684\u4e8b\uff0c\u4f55\u51b5\u2026\u2026\u201d \u4ed6\u7684\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u8001\u5200\u628a\u5b50\u7a81\u7136\u8e7f\u8d77\uff0c\u7bad\u4e00\u822c\u8e7f\u4e86\u51fa \u4ed6\u638c\u4e2d\u5df2\u6709\u5251\uff0c\u51fa\u4e86\u9798\u7684\u5251\u3002 \u4ed6\u7684\u4eba\u4e0e\u5251\u4f3c\u5df2\u5408\u4e3a\u4e00\u4f53\uff0c\u95ea\u7535\u822c\u51fb\u5411\u77f3\u96c1\u3002 \u77f3\u96c1\u4e5f\u6709\u5251\u3002 \u5251\u67c4\u4e2d\u7684\u79d8\u5bc6\u88ab\u53d6\u51fa\uff0c\u4e03\u661f\u5251\u53c8\u91cd\u56de\u4ed6\u624b\u91cc\u3002 \u4ed6\u60f3\u62d4\u5251\uff0c\u53ef\u662f\u808b\u4e0b\u5ffd\u7136\u4e00\u9635\u523a\u75db\uff0c\u65b0\u4f24\u548c\u65e7\u75be\u540c\u65f6\u53d1\u4f5c\u3002 \u8001\u5200\u628a\u5b50\u7684\u5251\u5df2\u5728\u4ed6\u54bd\u5589\uff0c\u4eba\u5df2\u5230\u4e86\u4ed6\u80cc\u540e\uff0c\u7528\u4e00\u53ea\u624b\u62d7\u4f4f\u4ed6\u7684\u81c2\uff1a\u201c\u4f60\u4eec\u8c01\u6562\u52a8\uff0c\u6211\u5c31 \u6740\u4e86\u4ed6\uff01\u201d \u6ca1\u6709\u4eba\u6562\u52a8\u3002 \u867d\u7136\u4ed6\u5df2\u6709\u4e86\u7edd\u75c7\uff0c\u8fd8\u662f\u6ca1\u6709\u4eba\u80fd\u773c\u770b\u7740\u6b66\u5f53\u7684\u638c\u95e8\u4eba\uff0c\u8fd9\u5fe0\u539a\u6b63\u76f4\u7684\u957f\u8005\u6b7b\u5728\u5251\u4e0b\u3002 \u6240\u4ee5\u5927\u5bb6\u53ea\u6709\u773c\u770b\u7740\u8001\u5200\u628a\u5b50\u5f80\u540e\u9000\u3002 \u8001\u5200\u628a\u5b50\u51b7\u7b11\u9053\uff1a\u201c\u6211\u7684\u8ba1\u5212\u867d\u672a\u6210\u529f\uff0c\u4f60\u4eec\u7684\u8ba1\u5212\u770b\u6765\u4e5f\u529f\u4e8f\u4e00\u8489\u3002\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u9053\uff1a\u201c\u6211\u4eec\u82e5\u7b54\u5e94\u8ba9\u4f60\u8d70\uff0c\u4f60\u80fd\u4e0d\u80fd\u8ba9\u6211\u4eec\u770b\u770b\u4f60\u7684\u771f\u9762\u76ee\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4e0d\u80fd\u3002\u201d \u4ed6\u5927\u7b11\uff0c\u53c8\u9053\uff1a\u201c\u6c38\u8fdc\u6ca1\u6709\u4eba\u518d\u80fd\u770b\u89c1\u6211\u7684\u771f\u9762\u76ee\uff0c\u6c38\u8fdc\u6ca1\u6709\u2026\u2026\u201d \u7b11\u58f0\u7a81\u7136\u505c\u987f\u3002 \u4ed6\u7684\u4eba\u7a81\u7136\u5411\u524d\u683d\u5012\uff0c\u6eda\u5385\u4e03\u516b\u7ea7\u77f3\u9636\uff0c\u4ec6\u5012\u5728\u5730\u4e0a\uff0c\u80cc\u540e\u9c9c\u8840\u6cc9\u6c34\u822c\u6d8c\u51fa\u3002 \u4ed6\u7684\u7af9\u7b20\u4e5f\u6eda\u4e86\u51fa\u53bb\u3002 \u4e00\u4e2a\u4eba\u6162\u6162\u7684\u4ece\u77f3\u9636\u4e0a\u8d70\u4e0b\u6765\uff0c\u624b\u91cc\u4e00\u67c4\u957f\u5251\uff0c\u5251\u5c16\u8fd8\u5728\u6ef4\u7740\u8840\u3002 \u9646\u5c0f\u51e4\u8138\u8272\u5ffd\u7136\u53d8\u4e86\u3002 \u82e5\u4e0d\u662f\u56e0\u4e3a\u4ed6\u8138\u4e0a\u8fd8\u6709\u9762\u5177\uff0c\u5927\u5bb6\u2014\u2014\u5b9a\u4f1a\u5927\u5403\u4e00\u60ca\u7684\u3002 \u56e0\u4e3a\u4ed6\u8138\u8272\u5b9e\u5728\u53d8\u5f97\u592a\u53ef\u6015\u3002 \u7b2c\u5341\u516d\u7ae0\u3000\u68cb\u9ad8\u4e00\u7740>> \u53e4\u9f99\u300a\u9646\u5c0f\u51e4\u7cfb\u5217\xb7\u5e7d\u7075\u5c71\u5e84\u300b \u7b2c\u5341\u516d\u7ae0\u3000\u68cb\u9ad8\u4e00\u7740 \u6700\u540e\u4ece\u77f3\u9636\u4e0a\u8d70\u4e0b\u6765\u7684\uff0c\u5e76\u4e0d\u662f\u897f\u95e8\u5439\u96ea\uff0c\u662f\u6728\u9053\u4eba\u3002\u4ed6\u624d\u771f\u6b63\u662f\u8d70\u5728\u6700\u540e\u9762\u7684\u2014\u4e2a\uff0c \u8001\u5200\u628a\u5b50\u5374\u663e\u7136\u60f3\u4e0d\u5230\u77f3\u96c1\u8eab\u540e\u8fd8\u6709\u4eba\u5728\uff0c\u87b3\u8782\u6355\u8749\uff0c\u9ec4\u96c0\u5728\u540e\uff0c\u4e16\u4e0a\u5c82\u975e\u672c\u5c31\u6709\u5f88\u591a\u4e8b\u90fd \u662f\u8fd9\u6837\u5b50\u7684\u3002 \u9646\u5c0f\u51e4\u7adf\u4f3c\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4f1a\u6765\uff0c\u5403\u60ca\u7684\u770b\u7740\u4ed6\uff0c\u518d\u770b\u770b\u5012\u5728\u8840\u6cca\u4e2d\u7684\u8001\u5200\u628a\u5b50\uff0c\u5ffd\u7136\uff1a\u201c \u4f60\u4e3a\u4ec0\u4e48\u6740\u4e86\u4ed6\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u7559\u4e0b\u6d3b\u53e3\uff1f\u201d \u6728\u9053\u4eba\uff1a\u201c\u4ed6\u7684\u79d8\u5bc6\u6211\u4eec\u65e9\u5df2\u77e5\u9053\uff0c\u5c31\u7b97\u518d\u95ee\uff0c\u4e5f\u95ee\u4e0d\u51fa\u4ec0\u4e48\u6765\uff0c\u6211\u51fa\u624b\u867d\u91cd\u4e86\u4e9b\uff0c\u5374 \u7edd\u4e86\u540e\u60a3\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ef\u662f\u6211\u4eec\u8fd8\u6ca1\u6709\u89c1\u8fc7\u4ed6\u7684\u771f\u9762\u76ee\u3002\u201d \u6728\u9053\u4eba\u7b11\u4e86\u7b11\uff0c\uff1a\u201c\u4eba\u6b7b\u4e86\u4e4b\u540e\uff0c\u8fd8\u662f\u4e00\u6837\u80fd\u770b\u5f97\u51fa\u4ed6\u672c\u6765\u9762\u76ee\u7684\u3002\u201d \u9646\u5c0f\u51e4\u6014\u4e86\u6014\uff0c\u4e5f\u7b11\u4e86\uff1a\u201c\u8fd9\u51e0\u5929\u6211\u5b9e\u5728\u592a\u7d2f\uff0c\u8fde\u5934\u90fd\u7d2f\u6655\u4e86\u3002\u201d \u6728\u9053\u4eba\u7b11\uff1a\u201c\u6bcf\u4e2a\u4eba\u90fd\u6709\u6655\u5934\u7684\u65f6\u5019\uff0c\u6015\u53ea\u6015\u6ca1\u6709\u5934\u53ef\u6655\u3002 \u6bcf\u4e2a\u4eba\u6b7b\u4e86\u4e4b\u540e\uff0c\u90fd\u4e00\u6837\u80fd\u770b\u5f97\u51fa\u4ed6\u672c\u6765\u7684\u9762\u76ee\u3002 \u6015\u53ea\u6015\u4ed6\u672c\u6765\u6839\u672c\u6ca1\u6709\u9762\u76ee\u3002 \u9646\u5c0f\u51e4\u7ffb\u8fc7\u8001\u5200\u628a\u5b50\u7684\u8138\uff0c\u53c8\u6014\u4f4f\u3002 \u4ed6\u770b\u89c1\u7684\u7adf\u662f\u4e00\u5f20\u6ca1\u6709\u8138\u7684\u8138\uff0c\u9ed1\u6d1e\u822c\u7684\u773c\u775b\u91cc\u5374\u5e26\u7740\u8bf4\u4e0d\u51fa\u6298\u8ba5\u6d88\uff0c\u4eff\u4f5b\u8fd8\u5728\u8bf4\uff1a\u201c\u6c38 \u8fdc\u6ca1\u6709\u4eba\u518d\u80fd\u770b\u89c1\u6211\u7684\u9762\u76ee\uff0c\u6c38\u8fdc\u6ca1\u6709\u2026\u2026\u201d \u6bcf\u4e2a\u4eba\u90fd\u6014\u4f4f\uff0c\u8fde\u67f3\u9752\u9752\u90fd\u6014\u4f4f\u3002 \u77f3\u96c1\u5374\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\uff1a\u201c\u4ed6\u867d\u7136\u6ca1\u6709\u8138\uff0c\u6211\u4e5f\u8ba4\u5f97\u51fa\u4ed6\u3002\u201d \u6728\u9053\u4eba\u9eef\u7136\uff1a\u201c\u4f60\u5f53\u7136\u8ba4\u5f97\u51fa\uff0c\u6211\u4e5f\u8ba4\u5f97\u51fa\u3002\u201d \u4ed6\u62ac\u8d77\u5934\uff0c\u770b\u6765\u4eff\u4f5b\u66f4\u8870\u8001\uff0c\u201c\u8fd9\u4e2a\u4eba\u5c31\u662f\u672c\u95e8\u7684\u53db\u5f92\u77f3\u9e64\u3002\u201d \u201c\u4e0d\u5bf9\u3002\u201d\u9646\u5c0f\u51e4\u8bf4\uff1a\u201c\u4e0d\u662f\u77f3\u9e64\u3002\u201d \u4ed6\u7684\u53e3\u6c14\u5f88\u575a\u51b3\uff0c\u5f88\u6709\u81ea\u4fe1\uff0c\u5bf9\u4ed6\u8bf4\u7684\u8fd9\u4ef6\u4e8b\uff0c\u663e\u5f97\u6781\u6709\u628a\u63e1\u3002 \u6ca1\u6709\u628a\u63e1\u7684\u8bdd\uff0c\u4ed6\u7edd\u4e0d\u4f1a\u5bf9\u5c4b\u5b50\u91cc\u8fd9\u4e9b\u4eba\u8bf4\u3002 \u8fd9\u662f\u95f4\u9ad8\u96c5\u5b89\u9759\u7684\u4e66\u623f\uff0c\u5728\u4e00\u4e2a\u7edd\u5bf9\u5b89\u5168\u9690\u79d8\u7684\u5730\u65b9\u3002 \u65e0\u8bba\u8c01\u8981\u8fdb\u5165\u8fd9\u95f4\u4e66\u623f\uff0c\u90fd\u5fc5\u9700\u5148\u901a\u8fc7\u4e03\u9053\u9632\u5b88\u4e25\u5bc6\u7684\u95e8\u6237\u3002 \u9632\u5b88\u5728\u5916\u9762\u7684\u4eba\uff0c\u51e0\u4e4e\u6bcf\u4e00\u4e2a\u90fd\u662f\u6b66\u6797\u4e2d\u7684\u2014\u6d41\u9ad8\u624b\uff0c\u5176\u4e2d\u5305\u62ec\u4e86\u6b66\u5f53\u3001\u5c11\u6797\u3001\u96c1\u8361\u548c \u5df4\u5c71\u95e8\u4e0b\u6700\u4f18\u79c0\u7684\u5f1f\u5b50\uff0c\u8fd8\u6709\u957f\u6c5f\u6c34\u5be8\u7b14\u5341\u4e8c\u8fde\u73af\u575e\u4e2d\u6700\u7cbe\u660e\u5e72\u7ec3\u7684\u51e0\u4f4d\u8235\u4e3b\u3002 \u6ca1\u6709\u5f97\u5230\u5c4b\u5b50\u91cc\u8fd9\u4e9b\u4eba\u7684\u5141\u8bb8\uff0c\u7edd\u5bf9\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd\u95ef\u8fdb\u6765\u3002 \u4ed6\u4eec\u5728\u8fd9\u91cc\u8bf4\u7684\u8bdd\uff0c\u4e5f\u7edd\u5bf9\u4e0d\u4f1a\u6709\u4e00\u70b9\u98ce\u58f0\u8d70\u6f0f\u51fa\u53bb\u3002 \u4ed6\u4eec\u5c06\u8fd9\u4e2a\u5730\u65b9\u53eb\u505a\u201c\u9e70\u5de2\u201d\u8fd9\u6b21\u5bf9\u4ed8\u201c\u5e7d\u7075\u5c71\u5e84\u201d\u7684\u8ba1\u5212\uff0c\u5c31\u662f\u4ed6\u4eec\u4e09\u4e2a\u6708\u4ee5\u524d\u5728 \u201c\u9e70\u5de2\u201d\u4e2d\u51b3\u5b9a\u7684\u3002 \u8fd9\u662f\u4e2a\u7edd\u3002 \u8ba1\u5212\u4e2d\u7684\u7b2c\u4e00\u6b65\uff0c\u5c31\u662f\u5148\u8bf4\u670d\u897f\u95e8\u5439\u96ea\uff0c\u9020\u6210\u4ed6\u548c\u9646\u5c0f\u98ce\u4e4b\u95f4\u7684\u51b2\u7a81\u4ec7\u6068\uff0c\u8ba9\u6c5f\u6e56\u4e2d\u7684 \u4eba\uff0c\u90fd\u4ee5\u4e3a\u4ed6\u975e\u6740\u9646\u5c0f\u51e4\u4e0d\u53ef\u3002 \u8fd9\u672c\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\uff0c\u897f\u95e8\u5439\u96ea\u7edd\u4e0d\u662f\u4e2a\u5bb9\u6613\u88ab\u6253\u52a8\u7684 \u8c01\u77e5\u8fd9\u4e00\u6b21\u897f\u95e8\u5439\u96ea\u5c45\u7136\u6ca1\u6709\u62d2\u7edd\uff0c\u4ed6\u663e\u7136\u89c9\u5f97\u80fd\u8ffd\u6740\u9646\u5c0f\u51e4\u662f\u4ef6\u5f88\u6709\u8da3\u7684\u4e8b\uff0c\u6240\u4ee5\u4ed6 \u552f\u4e00\u7684\u6761\u4ef6\u662f \u201c\u4f60\u4e00\u5b9a\u8981\u771f\u7684\u9003\uff0c\u56e0\u4e3a\u6211\u662f\u771f\u7684\u8ffd\uff0c\u4f60\u82e5\u88ab\u6211\u8ffd\u4e0a\uff0c\u6211\u4e5f\u8bb8\u5c31\u4f1a\u771f\u7684\u6740\u4e86\u4f60\u3002\u201d \u6240\u4ee5\u9646\u5c0f\u51e4\u5728\u9003\u4ea1\u7684\u65f6\u5019\uff0c\u7684\u786e\u968f\u65f6\u90fd\u5728\u634f\u7740\u628a\u51b7\u6c57\u3002 \u8ba1\u5212\u4e2d\u7684\u7b2c\u4e8c\u6b65\uff0c\u5c31\u662f\u5b89\u6392\u9646\u5c0f\u51e4\u9003\u4ea1\u7684\u8def\u7ebf\uff0c\u4e00\u5b9a\u8981\u8ba9\u4ed6\u80fd\u5728\u65e0\u610f\u95f4\u548c\u201c\u5e7d\u7075\u5c71\u5e84\u201d \u4e2d\u7684\u4eba\u63a5\u89e6\uff0c\u800c\u4e0d\u88ab\u6000\u7591\u3002 \u5728\u9003\u4ea1\u8fc7\u7a0b\u4e2d\uff0c\u4ed6\u8fd8\u5f97\u81ea\u5df1\u72ec\u529b\u53bb\u5e94\u4ed8\u4e00\u5207\u56f0\u96be\uff0c\u7edd\u4e0d\u80fd\u548c\u4efb\u4f55\u4eba\u63a5\u89e6\u3002 \u9646\u5c0f\u51e4\u662f\u4e0d\u662f\u771f\u7684\u80fd\u6df7\u5165\u5e7d\u7075\u5c71\u5e84\uff0c\u4ed6\u4eec\u5e76\u6ca1\u6709\u628a\u63e1\u3002 \u53ef\u662f\u4ed6\u613f\u610f\u5192\u8fd9\u4e2a\u9669\u3002 \u4ed6\u4eec\u5bf9\u4e8e\u201c\u5e7d\u7075\u5c71\u5e84\u201d\u8fd9\u4e2a\u7ec4\u7ec7\u5df2\u77e5\u9053\u4e86\u5f88\u4e45\uff0c\u5374\u4e00\u76f4\u90fd\u6293\u4e0d\u5230\u4e00\u70b9\u7ebf\u7d22\uff0c\u53ea\u4e0d\u8fc7\u4ece\u2014 \u4e2a\u5782\u6b7b\u7684\u964c\u751f\u4eba\u53e3\u4e2d\uff0c\u77e5\u9053\u8fd9\u7ec4\u7ec7\u6700\u8fd1\u5c31\u8981\u505a\u4e00\u4ef6\u60ca\u5929\u52a8\u5730\u7684\u5927\u4e8b\u3002 \u6240\u4ee5\u4ed6\u4eec\u4e5f\u975e\u5f00\u59cb\u884c\u52a8\u4e0d\u53ef\u3002 \u56e0\u4e3a\u4ed6\u4eec\u5df2\u67e5\u51fa\u8fd9\u4e2a\u5782\u6b7b\u7684\u964c\u751f\u4eba\uff0c\u7adf\u662f\u591a\u5e74\u524d\u5c31\u5df2\u5e94\u8be5\u6b7b\u5728\u897f\u95e8\u5439\u96ea\u5251\u4e0b\u7684\u987e\u98de\u4e91\u3002 \u4ed6\u4ece\u5e7d\u7075\u5c71\u5e84\u9003\u51fa\u6765\uff0c\u88ab\u77f3\u9e64\u903c\u5165\u4e86\u4e07\u4e08\u6df1\u5851\uff0c\u867d\u7136\u4fa5\u5e78\u6ca1\u6709\u6b7b\uff0c\u4e24\u6761\u817f\u5374\u5df2\u65ad\u4e86\uff0c\u53ea \u51ed\u7740\u4e00\u53cc\u624b\u548c\u4e00\u80a1\u575a\u5f3a\u7684\u610f\u5fd7\uff0c\u5728\u7edd\u8c37\u4e2d\u722c\u4e86\u4e94\u5929\u56db\u591c\uff0c\u624d\u9047\u89c1\u4e00\u4e2a\u6df1\u5c71\u4e2d\u91c7\u836f\u7684\u9053 \u8fd9\u9053\u58eb\u6b63\u662f\u6b66\u5f53\u5f1f\u5b50\uff0c\u4ed6\u603b\u7b97\u80fd\u6d3b\u7740\u8bf4\u51fa\u4e86\u5e7d\u7075\u5c71\u5e84\u7684\u79d8\u5bc6\u3002 \u53ea\u53ef\u60dc\u4ed6\u77e5\u9053\u7684\u4e5f\u5e76\u4e0d\u591a\uff0c\u800c\u4e14\u5df2\u53ea\u5269\u4e0b\u6700\u540e\u4e00\u53e3\u6c14\u3002 \u6240\u4ee5\u9646\u5c0f\u51e4\u4ece\u4e00\u5f00\u59cb\u5c31\u5df2\u77e5\u9053\u201c\u8868\u54e5\u201d\u5e76\u4e0d\u662f\u987e\u98de\u4e91\u3002 \u6700\u5148\u5f00\u59cb\u7b56\u5212\u8fd9\u4ef6\u4e8b\u7684\u662f\u6b66\u5f53\u77f3\u96c1\uff0c\u4ed6\u7b2c\u4e00\u4e2a\u627e\u7684\u4eba\u5c31\u662f\u9646\u5c0f\u51e4\u3002 \u5982\u679c\u4e16\u754c\u4e0a\u8fd8\u6709\u4e00\u4e2a\u4eba\u80fd\u5b8c\u6210\u8fd9\u6b21\u8270\u5de8\u7684\u4efb\u52a1\uff0c\u8fd9\u4e2a\u4eba\u65e0\u7591\u5c31\u662f\u9646\u5c0f\u51e4\u3002 \u53ef\u662f\u9646\u5c0f\u51e4\u5374\u77e5\u9053\uff0c\u5355\u51ed\u81ea\u5df1\u4e00\u4e2a\u4eba\u4e4b\u529b\uff0c\u662f\u7edd\u5bf9\u65e0\u6cd5\u6210\u529f\u7684\u3002 \u4ed6\u4e00\u5b9a\u8fd8\u8981\u627e\u51e0\u4e2a\u597d\u5e2e\u624b\uff0c\u4ed6\u8ba4\u4e3a\u5176\u4e2d\u4e0d\u80fd\u7f3a\u5c11\u7684\u5c31\u662f\u53f8\u7a7a\u6458\u661f\u3002 \u8981\u8bf4\u670d\u53f8\u7a7a\u6458\u661f\u7b80\u76f4\u6bd4\u8bf4\u670d\u897f\u95e8\u5439\u96ea\u8fd8\u56f0\u96be\uff0c\u5e78\u597d\u4ed6\u6709\u4e2a\u5f31\u70b9\u3002 \u4ed6\u597d\u8d4c\uff0c\u5c24\u5176\u559c\u6b22\u548c\u9646\u5c0f\u51e4\u8d4c\uff0c\u800c\u4e14\u968f\u4fbf\u9646\u5c0f\u51e4\u8d4c\u4ec0\u4e48\u90fd\u884c\u3002 \u6240\u4ee5\u9646\u5c0f\u51e4\u5c31\u8ddf\u4ed6\u8d4c\uff1a\u201c\u6211\u82e5\u4e0d\u6210\u529f\uff0c\u4f60\u5c31\u5f97\u66ff\u6211\u6316\u86af\u8693\u3002\u201d \u7b49\u5230\u53f8\u7a7a\u6458\u661f\u53d1\u73b0\u8fd9\u662f\u4e2a\u5708\u5957\uff0c\u540e\u6094\u5df2\u6765\u4e0d\u53ca\u4e86\uff0c\u4e3a\u4e86\u4e0d\u60f3\u8f93\uff0c\u4ed6\u53ea\u6709\u5168\u529b\u5e2e\u52a9\u9646\u5c0f\u51e4 \u5b8c\u6210\u8fd9\u4ef6\u4e8b\u3002 \u4ed6\u4e00\u5411\u662f\u4e2a\u8a00\u800c\u6709\u4fe1\u7684\u4eba\u3002 \u53ef\u662f\u4ed6\u4e5f\u575a\u6301\u8981\u627e\u4e00\u4e2a\u4e0d\u80fd\u7f3a\u5c11\u7684\u5e2e\u624b\uff0c\u4ed6\u8981\u9646\u5c0f\u51e4\u66ff\u4ed6\u627e\u82b1\u6ee1\u697c\u3002 \u82b1\u6ee1\u697c\u7684\u601d\u8651\u5468\u5bc6\uff0c\u65e0\u4eba\u80fd\u53ca\uff0c\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u4ed6\u770b\u4e0d\u89c1\uff0c\u6240\u4ee5\u601d\u60f3\u7684\u65f6\u95f4\u6bd4\u522b\u4eba\u591a\u3002 \u6700\u539f\u59cb\u7684\u8ba1\u5212\uff0c\u5c31\u662f\u4ed6\u4eec\u56db\u4e2a\u4eba\u5728\u201c\u9e70\u5de2\u201d\u4e2d\u51b3\u5b9a\u7684\u3002 \u4ed6\u4eec\u56db\u4e2a\u4eba\u7684\u529b\u91cf\u5f53\u7136\u4e0d\u8fd8\u4e0d\u591f\uff0c\u6240\u4ee5\u4ed6\u4eec\u53c8\u62c9\u5165\u4e86\u516d\u4e2a\u4eba\u3002 \u90a3\u5c31\u662f\u5c11\u6797\u94c1\u80a9\u3001\u4e10\u5e2e\u738b\u5341\u888b\u3001\u957f\u6c5f\u6c34\u4e0a\u98de\u3001\u96c1\u8361\u9ad8\u884c\u7a7a\uff0c\u5df4\u5c71\u5c0f\u987e\uff0c\u548c\u5341\u4e8c\u8fde\u73af\u575e\u7684 \u9e70\u773c\u8001\u4e03\u3002 \u56e0\u4e3a\u8fd9\u516d\u4e2a\u4eba\u95e8\u4e0b\u90fd\u6709\u4eba\u5728\u5e7d\u7075\u5c71\u5e84\u3002 \u4ed6\u4eec\u7684\u52bf\u529b\uff0c\u4e5f\u6b63\u597d\u5206\u5e03\u5728\u5e7d\u7075\u5c71\u5e84\u5230\u6b66\u5f53\u7684\u8def\u4e0a\u3002 \u6700\u91cd\u8981\u7684\u4e00\u70b9\u662f\uff0c\u4ed6\u4eec\u90fd\u662f\u7edd\u5bf9\u5b88\u53e3\u5982\u74f6\u7684\u4eba\uff0c\u7edd\u4e0d\u4f1a\u6cc4\u9732\u8fd9\u8ba1\u5212\u7684\u673a\u5bc6\u3002 \u4ece\u5916\u8868\u770b\u6765\uff0c\u8fd9\u53ea\u4e0d\u8fc7\u662f\u95f9\u5e02\u4e2d\u4e00\u680b\u5f88\u666e\u901a\u7684\u697c\u623f\uff0c\u662f\u7528\u9e70\u773c\u8001\u4e03\u95e8\u4e0b\u4e00\u4e2a\u5206\u8235\u8235\u4e3b\u7684 \u540d\u4e49\u4e70\u4e0b\u6765\u7684\uff0c\u7528\u697c\u4e0b\u7684\u4e09\u95f4\u95e8\u9762\uff0c\u5206\u522b\u5f00\u4e86\u4e00\u5bb6\u836f\u94fa\uff0c\u4e00\u5bb6\u9152\u8086\uff0c\u548c\u4e00\u5bb6\u68fa\u6750\u5e97\u3002 \u4e09\u5bb6\u5e97\u94fa\u4e2d\u7684\u4f19\u8ba1\uff0c\u5f53\u7136\u90fd\u662f\u4ed6\u4eec\u95e8\u4e0b\u6700\u5fe0\u57ce\u5e72\u7ec3\u7684\u5b50\u5f1f\u3002 \u77e5\u9053\u8fd9\u6b21\u8ba1\u5212\u7684\u4eba\uff0c\u5374\u53ea\u6709\u4ed6\u4eec\u5341\u4e2a\uff0c\u5176\u4f59\u7684\u4eba\uff0c\u53ea\u4e0d\u8fc7\u662f\u5949\u547d\u884c\u4e8b\u3002 \u73b0\u5728\u4ed6\u4eec\u5341\u4e2a\u4eba\u4e2d\u5df2\u5230\u4e86\u516b\u4eba\u3002 \u9646\u5c0f\u51e4\u770b\u7740\u4ed6\u4eec\uff0c\u5c06\u521a\u624d\u8bf4\u7684\u8bdd\u53c8\u91cd\u65b0\u5f3a\u8c03\u4e86\u4e00\u904d\uff1a\u201c\u4e0d\u662f\u77f3\u9e64\uff0c\u7edd\u4e0d\u662f\u3002\u201d \u77f3\u96c1\u6ca1\u6709\u6765\uff0c\u663e\u7136\u75c5\u5f97\u5f88\u4e25\u91cd\uff0c\u552f\u4e00\u89c1\u8fc7\u77f3\u9e64\u7684\u5c31\u662f\u94c1\u80a9\u3002 \u5f53\u5e74\u6b66\u5f53\u53e6\u7acb\u638c\u95e8\uff0c\u77f3\u9e64\u81ea\u6bc1\u9762\u76ee\u65f6\uff0c\u8fd9\u4f4d\u5c11\u6797\u9ad8\u50e7\u4e5f\u5728\u5ea7\u3002 \u4ed6\u770b\u89c1\u8fc7\u8fd9\u5f20\u6ca1\u6709\u8138\u7684\u8138\uff0c\u65e0\u8bba\u8c01\u53ea\u8981\u770b\u8fc7\u4e00\u773c\uff0c\u90fd\u6c38\u8fdc\u4e0d\u4f1a\u5fd8\u8bb0\u3002\u6240\u4ee5\u4ed6\u53cd\u5bf9\uff0f\u6211\u770b \u8fc7\u4ed6\u7684\u8138\uff0c\u4ed6\u7edd\u5bf9\u5c31\u662f\u77f3\u9e64\u3002\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u6b7b\u5728\u6728\u9053\u4eba\u5251\u4e0b\u7684\u5f53\u7136\u662f\u77f3\u9e64\uff0c\u77f3\u9e64\u5374\u4e0d\u662f\u8001\u5200\u628a\u5b50\uff0c\u7edd\u4e0d\u662f\uff1f\u201d \u53f8\u7a7a\u6458\u661f\u62a2\u7740\u9053\uff1a\u201c\u4f60\u600e\u4e48\u80fd\u786e\u5b9a\uff1f\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u8001\u5200\u628a\u5b50\u662f\u8c01\u3002\u201d \u53f8\u7a7a\u6458\u661f\uff1a\u201c\u662f\u8c01\uff1f\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u662f\u6728\u9053\u4eba\u3002\u201d \u53f8\u7a7a\u6458\u661f\u5403\u4e86\u4e00\u60ca\uff0c\u6bcf\u4e2a\u4eba\u90fd\u5403\u4e86\u4e00\u60ca\u3002 \u8fc7\u4e86\u5f88\u4e45\uff0c\u94c1\u80a9\u624d\u6162\u6162\u7684\u6447\u4e86\u6447\u5934\uff0c\uff1a\u201c\u4e0d\u5bf9\uff0c\u4e0d\u4f1a\u662f\u4ed6\u3002\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u94c1\u80a9\uff1a\u201c\u591a\u5e74\u524d\u4ed6\u5c31\u53ef\u4ee5\u505a\u6b66\u5f53\u638c\u95e8\u7684\uff0c\u4f46\u4ed6\u5374\u5c06\u638c\u95e8\u4eba\u7684\u4f4d\u5b50\u8ba9\u7ed9\u4e86\u4ed6\u5e08\u5f1f\u6885\u771f\u4eba\uff0c \u7531\u6b64\u53ef\u89c1\uff0c\u4ed6\u5bf9\u540d\u5229\u548c\u6743\u4f4d\u770b\u5f97\u5e76\u4e0d\u91cd\uff0c\u4ed6\u600e\u4e48\u4f1a\u505a\u8fd9\u79cd\u4e8b\uff1f\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u672c\u6765\u6211\u4e5f\u4e0d\u76f8\u4fe1\uff0c\u672c\u6765\u6211\u8fd8\u60f3\u5c06\u4ed6\u4e5f\u62c9\u5165\u9e70\u5de2\u6765\u3002\u201d \u94c1\u80a9\uff1a\u201c\u96be\u9053\u6709\u4eba\u53cd\u5bf9\uff1f\u201d \u9646\u5c0f\u51e4\u70b9\u70b9\u5934\uff0c\uff1a\u201c\u77f3\u96c1\u53cd\u5bf9\uff0c\u82b1\u6ee1\u697c\u4e5f\u4e0d\u8d5e\u6210\u3002\u201d \u94c1\u80a9\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u8fd9\u6b21\u4ed6\u95ee\u7684\u662f\u82b1\u6ee1\u697c\u3002 \u82b1\u6ee1\u697c\u8fdf\u7591\u7740\uff0c\u7f13\u7f13\uff1a\u201c\u5f53\u65f6\u6211\u5e76\u4e0d\u662f\u6000\u7591\u4ed6\uff0c\u53ea\u4e0d\u8fc7\u89c9\u5f97\u4ed6\u548c\u53e4\u677e\u5c45\u58eb\u592a\u63a5\u8fd1\uff0c\u5f88\u96be \u5bf9\u53e4\u677e\u4fdd\u5b88\u673a\u5bc6\u3002\u201d \u94e3\u80a9\uff1a\u201c\u4f60\u8ba4\u4e3a\u53e4\u677e\u53ef\u7591\uff1f\u201d \u82b1\u6ee1\u697c\uff1a\u201c\u4ed6\u7684\u6b66\u529f\u6781\u9ad8\uff0c\u53ef\u662f\u4ed6\u7684\u5e08\u627f\u548c\u6765\u5386\u5374\u4ece\u6765\u6ca1\u6709\u4eba\u77e5\uff1a\u201c \u94c1\u80a9\uff1a\u201c\u4ed6\u662f\u4e2a\u9690\u58eb\uff0c\u9690\u58eb\u4eec\u672c\u6765\u5c31\u901a\u5e38\u2019\u90fd\u662f\u8fd9\u6837\u5b50\u7684\u3002\u201d \u82b1\u6ee1\u697c\uff1a\u201c\u9690\u58eb\u5f52\u9690\u4e4b\u524d\uff0c\u4e5f\u603b\u8be5\u6709\u4e9b\u5f80\u4e8b\u7684\uff0c\u53ef\u662f\u4ed6\u6ca1\u6709\uff0c\u5c31\u597d\u50cf\u4e00\u751f\u51fa\u6765\u5c31\u662f\u4e2a\u9690 \u58eb\u3002\u201d \u94c1\u80a9\u6c89\u541f\uff1a\u201c\u77f3\u96c1\u4e3a\u4ec0\u4e48\u8981\u53cd\u5bf9\u6728\u9053\u4eba\uff1f\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u56e0\u4e3a\u4ed6\u77e5\u9053\u6728\u9053\u4eba\u5e76\u4e0d\u662f\u771f\u5fc3\u60c5\u613f\u8ba9\u7ed9\u6885\u771f\u4eba\u7684\u3002\u201d \u94c1\u80a9\u76b1\u7709\uff1a\u201c\u96be\u9053\u4ed6\u4e5f\u50cf\u77f3\u9e64\u4e00\u6837\uff0c\u662f\u56e0\u4e3a\u505a\u4e86\u4ef6\u6709\u8fdd\u6559\u89c4\u7684\u4e8b\uff0c\u6240\u4ee5\u624d\u88ab\u8feb\u8ba9\u4f4d\uff1f\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u60f3\u5fc5\u662f\u7684\u3002\u201d \u94c1\u80a9\uff1a\u201c\u4ed6\u505a\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u2018\u77f3\u96c1\u4e0d\u80af\u8bf4\u3002\u201d \u5bb6\u4e11\u4e0d\u53ef\u5916\u626c\uff0c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u6728\u9053\u4eba\u603b\u662f\u4ed6\u7684\u5e08\u53d4\uff0c\u53c8\u662f\u6b66\u5f53\u95e8\u4e0b\u7855\u679c\u4ec5\u5b58\u7684\u957f\u8001\u3002 \u9646\u5c0f\u51e4\uff1a\u201c\u77f3\u96c1\u867d\u7136\u4e0d\u80af\u8bf4\uff0c\u73b0\u5728\u6211\u5374\u8fd8\u662f\u5df2\u5927\u81f4\u731c\u51fa\u6765\u4e86\u3002\u201d \u5df4\u5c71\u5c0f\u987e\u4e5f\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u6728\u9053\u4eba\u5f53\u5e74\u7a76\u7adf\u505a\u4e86\u4ec0\u4e48\u8fdd\u80cc\u6559\u89c4\u7684\u4e8b\uff1f\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u4ed6\u4e0d\u4f46\u5728\u5916\u9762\u5a36\u4e86\u59bb\uff0c\u800c\u4e14\u8fd8\u751f\u4e86\u513f\u5973\u3002\u201d \u94c1\u80a9\u6c89\u4e0b\u8138\uff0c\uff1a\u201c\u4eba\u8a00\u4e0d\u53ef\u8f7b\u4fe1\uff0c\u6709\u5173\u4ed6\u4eba\u540d\u8282\u7684\u8bdd\uff0c\u65e2\u4e0d\u53ef\u8f7b\u6613\u542c\u4fe1\uff0c\u66f4\u4e0d\u53ef\u8f7b\u6613\u51fa \u53e3\u3002\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u662f\u3002\u201d \u53f8\u7a7a\u6458\u661f\u53c8\u62a2\u7740\u9053\uff1a\u201c\u53ef\u662f\u4ed6\u65e2\u7136\u5df2\u8bf4\u51fa\u53e3\uff0c\u5c31\u4e00\u5b9a\u6709\u628a\u63e1\u3002 \u94c1\u80a9\u9053\uff1a\u201c\u4e0d\u4f46\u8981\u6709\u628a\u63e1\uff0c\u8fd8\u5f97\u8981\u6709\u8bc1\u636e\u3002\u201d \u9646\u5c0f\u51e4\u6ca1\u6709\u8bc1\u6398\u3002 \u53ef\u662f\u4ed6\u7684\u5206\u6790\u548c\u5224\u65ad\uff0c\u5c31\u8fde\u94c1\u80a9\u5927\u5e08\u90fd\u4e0d\u80fd\u4e0d\u627f\u8ba4\u6781\u6709\u9053\u7406\u3002 \u6c88\u4e09\u5a18\u662f\u53f6\u51cc\u98ce\u7684\u59bb\u5b50\uff0c\u5374\u4e3a\u8001\u5200\u628a\u5b50\u751f\u4e86\u513f\u5973\uff0c\u5979\u5bf9\u4e0d\u8d77\u7684\u662f\u53f6\u51cc\u98ce\uff0c\u5e76\u4e0d\u662f\u4ed6\uff0c\u8001 \u5200\u628a\u5b50\u4e3a\u4ec0\u4e48\u53cd\u800c\u6068\u5979\uff1f\u800c\u4e14\u6740\u4e86\u65f6\u51cc\u98ce\u3002 \u56e0\u4e3a\u5200\u628a\u5b50\u6728\u9053\u4eba\uff0c\u5c31\u662f\u6c88\u4e09\u5a18\u7684\u8868\u54e5\uff0c\u4e5f\u5c31\u662f\u6c88\u4e09\u5a18\u771f\u6b63\u7684\u4e08\u592b\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6728\u9053\u4eba\u5f53\u65f6\u6b63\u5728\u76db\u5e74\uff0c\u6c88\u4e09\u5a18\u4e5f\u6b63\u662f\u8c46\u85d0\u5e74\u534e\uff0e\u2606\u2026\u3002\u201d \u5728\u94c1\u80a9\u5927\u5e08\u9762\u524d\uff0c\u4ed6\u8bf4\u5f97\u5f88\u542b\u84c4\uff0c\u53ef\u662f\u4ed6\u7684\u610f\u601d\u5374\u5f88\u660e\u663e\u3002 \u8fd9\u8868\u5144\u59b9\u4e24\u4eba\uff0c\u65e0\u7591\u6709\u4e86\u79c1\u60c5\uff0c\u600e\u5948\u6728\u9053\u4eba\u5f53\u65f6\u5df2\u662f\u6b66\u5f53\u7684\u5165\u95e8\u5f1f\u5b50\uff0c\u5f53\u7136\u4e0d\u80fd\u5149\u660e\u6b63 \u5927\u7684\u548c\u5979\u7ed3\u4e3a\u592b\u59bb\uff0f\u6240\u4ee5\u4ed6\u5c31\u60f3\u51fa\u4e86\u4e2a\u674e\u4ee3\u6843\u50f5\u4e4b\u8ba1\uff0c\u8ba9\u6c88\u4e09\u5a18\u5ac1\u7ed9\u53f6\u51cc\u98ce\uff0c\u505a\u4ed6\u5b50\u5973\u7684\u7236 \u4eb2\u3002\u201d \u201c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u9009\u4e0a\u53f6\u51cc\u98ce\uff1f\u201d \u201c\u56e0\u4e3a\u53f6\u51cc\u98ce\u4e5f\u66fe\u5728\u6b66\u5f53\u5b66\u8fc7\u5251\uff0c\u800c\u4e14\u662f\u4ed6\u4eb2\u81ea\u4f20\u6388\u7684\uff0c\u4e3a\u4e86\u6388\u4e1a\u7684\u6069\u5e08\uff0c\u505a\u5f1f\u5b50\u7684\u5f53 \u7136\u4e0d\u80fd\u4e0d\u727a\u7272\u3002\u201d \u4f46\u662f\u540e\u6765\u6728\u9053\u4eba\u8001\u4e86\uff0c\u53c8\u957f\u5e74\u4e91\u6e38\u5728\u5916\uff0c\u6c88\u4e09\u5a18\u7a7a\u95fa\u5bc2\u5bde\uff0c\u7adf\u5f04\u5047\u6210\u771f\uff0c\u548c\u53f6\u51cc\u98ce\u6709\u4e86 \u79c1\u60c5\u3002 \u7b49\u5230\u6728\u9053\u4eba\u53d1\u73b0\u5979\u53c8\u6709\u4e86\u672c\u4e0d\u8be5\u6709\u7684\u5973\u513f\uff0c\u4e5f\u5c31\u53d1\u73b0\u4e86\u4ed6\u4eec\u7684\u79c1\u60c5\uff0c\u5f53\u7136\u5bf9\u4ed6\u4eec\u6839\u4e4b\u5165 \u9aa8\u3002 \u201c\u4f46\u662f\u4ed6\u66f4\u6068\u6b66\u5f53\uff0c\u56e0\u4e3a\u4ed6\u7684\u5f1f\u5b50\u77f3\u9e64\uff0c\u4e5f\u906d\u53d7\u4e86\u4ed6\u540c\u6837\u7684\u547d\u8fd0\uff0c\u88ab\u8feb\u8ba9\u51fa\u4e86\u638c\u95e8\u4e4b\u4f4d \u3002\u201d \u4ed6\u672c\u6765\u5df2\u5c06\u5e0c\u671b\u5bc4\u6258\u5728\u77f3\u9e64\u8eab\u4e0a\uff0c\u73b0\u5728\u6240\u6709\u7684\u5e0c\u671b\u90fd\u6210\u4e86\u6ce1\u5f71\uff0c\u4ed6\u53ea\u6709\u522b\u8d70\u8e6c\u5f84\u3002 \u201c\u62a5\u590d\u201d\u548c\u201c\u6743\u529b\u201d\u8fd9\u4e24\u6837\u4e8b\uff0c\u5176\u4e2d\u65e0\u8bba\u54ea\u4e00\u6837\u90fd\u5df2\u4ee4\u4eba\u4e0d\u62e9\u624b\u6bb5\uff0c\u633a\u800c\u8d70\u9669\u3002 \u201c\u53ef\u662f\u8fd9\u8fd8\u4e0d\u8db3\u4ee5\u8bc1\u660e\u6728\u9053\u4eba\u5c31\u662f\u8001\u5200\u628a\u5b50\u3002\u201d \u201c\u6211\u8fd8\u53ef\u4ee5\u4e3e\u51fa\u51e0\u70b9\u4e8b\u5b9e\u8bc1\u660e\u3002\u201d \u5178\u793c\u8fdb\u884c\u65f6\uff0c\u53ea\u6709\u4ed6\u624d\u80fd\u63a5\u8fd1\u77f3\u96c1\uff0c\u4e5f\u53ea\u6709\u4ed6\u77e5\u9053\u5251\u67c4\u4e2d\u7684\u79d8\u5bc6\u3002 \u201c\u90a3\u79d8\u5bc6\u5f88\u53ef\u80fd\u5c31\u662f\u4ed6\u5f53\u5e74\u88ab\u8feb\u8ba9\u4f4d\u7684\u79d8\u5bc6\uff0c\u6240\u4ee5\u4ed6\u52bf\u5728\u5fc5\u5f97\u3002\u201d \u5bf9\u6b66\u5f53\u5185\u90e8\u7684\u60c5\u51b5\uff0c\u53ea\u6709\u4ed6\u6700\u719f\u6089\uff0c\u6240\u4ee5\u4ed6\u624d\u80fd\u5e03\u7f6e\u4e8b\u6210\u540e\u5b89\u5168\u64a4\u9000\u7684\u8def\u7ebf\uff0c\u800c\u4e14\u5c06\u7fa4 \u8c6a\u7559\u5728\u5927\u6bbf\u91cc\uff0c\u60f3\u8ffd\u90fd\u6ca1\u6cd5\u5b50\u53bb\u8ffd\u3002 \u957f\u51c0\u548c\u957f\u6e05\u90fd\u662f\u4ed6\u95e8\u4e0b\u7684\u76f4\u7cfb\u5b50\u5f1f\uff0c\u53ea\u6709\u4ed6\u624d\u80fd\u6536\u4e70\u4ed6\u4eec\u3002 \u77f3\u9e64\u4e00\u5411\u5b64\u50fb\u9a84\u50b2\uff0c\u4e5f\u53ea\u6709\u4ed6\u624d\u80fd\u6307\u6325\u547d\u4ee4\u3002 \u8fd9\u51e0\u70b9\u867d\u7136\u4e5f\u53ea\u4e0d\u8fc7\u662f\u63a8\u6d4b\uff0c\u5374\u5df2\u8db3\u591f\u63a5\u8fde\u6210\u4e00\u6761\u5f88\u5b8c\u6574\u7684\u7ebf\u7d22\u3002 \u4f55\u51b5\u9646\u5c0f\u51e4\u624b\u91cc\u8fd8\u63e1\u7740\u91cd\u8981\u7684\u4e00\u4e2a\u73af\u8282\uff0f\u6211\u867d\u7136\u65e9\u5c31\u77e5\u9053\u8868\u54e5\u4e0d\u662f\u987e\u98de\u4e91\uff0c\u5374\u4e00\u76f4\u770b\u4e0d \u51fa\u4ed6\u7684\u771f\u6b63\u6765\u5386\u3002\u201d \u94c1\u80a9\u5fcd\u4e0d\u4f4f\u95ee\uff0f\u73b0\u5728\u4f60\u5df2\u67e5\u51fa\u6765\uff1f\u201d \u9646\u5c0f\u51e4\u70b9\u70b9\u5934\uff0c\uff1a\u201c\u8868\u54e5\u5c31\u662f\u53e4\u677e\u3002\u201d \u8fd9\u53e5\u8bdd\u8bf4\u51fa\u6765\uff0c\u5927\u5bb6\u53c8\u662f\u4e00\u60ca\u3002 \u9646\u5c0f\u51e4\uff1a\u201c\u8fd1\u5e74\u6765\u6728\u9053\u4eba\u548c\u53e4\u677e\u4e00\u5411\u5f62\u5f71\u4e0d\u79bb\uff0c\u7ecf\u5e38\u7ed3\u4f34\u4e91\u6e38\uff0c\u800c\u4e14\u884c\u8e2a\u98d8\u5ffd\uff0c\u53ea\u56e0\u4e3a \u4ed6\u4eec\u7ecf\u5e38\u8981\u56de\u5e7d\u7075\u5c71\u5e84\u53bb\u3002\u201d \u5df4\u5c71\u5c0f\u987e\uff1a\u201c\u8fd9\u6b21\u6b66\u5f53\u76db\u4f1a\uff0c\u5927\u5bb6\u90fd\u4ee5\u4e3a\u53e4\u677e\u4e00\u5b9a\u4f1a\u5230\u7684\uff0c\u4ed6\u5374\u504f\u504f\u6ca1\u6709\u9732\u9762\u3002\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u90a3\u53ea\u56e0\u4e3a\u4ed6\u5df2\u88ab\u56da\u7981\u5728\u53f6\u6c0f\u5c71\u5e84\u7684\u5730\u7a96\u91cc\u3002\u201d \u94c1\u80a9\uff1a\u201c\u4f60\u6709\u8bc1\u636e\u80fd\u8bc1\u660e\u4ed6\u5c31\u662f\u53e4\u677e\uff1f\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u6211\u89c1\u8fc7\u4ed6\u7684\u624b\uff0c\u4ed6\u7684\u5251\u6cd5\u6781\u7cbe\uff0c\u800c\u4e14\u6e0a\u535a\uff0c\u548c\u53e4\u677e\u7684\u5251\u6cd5\u5f88\u63a5\u8fd1\uff0c\u4ed6\u7684\u8eab\u6750 \u548c\u8138\u578b\u66f4\u50cf\u53e4\u677e\uff0c\u53ea\u8981\u5728\u52a0\u4e00\u70b9\u80e1\u987b\uff0c\u6dfb\u51e0\u6839\u767d\u53d1\uff0c\u518d\u67d3\u9ec4\u4e00\u70b9\uff0c\u5c31\u5b8c\u5168\u548c\u53e4\u677e\u4e00\u6a21\u4e00\u6837 \u4e86\u3002\u201d \u53f8\u7a7a\u6458\u661f\uff1a\u201c\u96be\u602a\u6211\u603b\u89c9\u5f97\u53e4\u677e\u6709\u70b9\u9634\u9633\u602a\u6c14\u7684\u6837\u5b50\uff0c\u539f\u6765\u4ed6\u4e00\u76f4\u90fd\u6ca1\u6709\u4ee5\u771f\u9762\u76ee\u89c1\u4eba \u3002\u201d \u94c1\u80a9\u6c89\u601d\u7740\uff0c\u5ffd\u7136\uff1a\u201c\u8fd8\u6709\u4e00\u70b9\u6f0f\u6d1e\u3002\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u54ea\u4e00\u70b9\uff1f\u201d \u94c1\u80a9\uff1a\u201c\u5982\u679c\u6728\u9053\u4eba\u771f\u7684\u5c31\u662f\u8001\u5200\u628a\u5b50\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u4f9d\u7ea6\u5230\u6ee1\u7fe0\u697c\u53bb\u8ddf\u4f60\u4eec\u4f1a\u5408\uff1f\u2026 \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\uff1a\u201c\u90a3\u53ea\u56e0\u4e3a\u4ed6\u5df2\u77e5\u9053\u4e8b\u60c5\u6709\u4e86\u53d8\u5316\uff0c\u5df2\u6709\u4eba\u6cc4\u9732\u4e86\u6211\u4eec\u7684\u673a\u5bc6qo \u94c1\u80a9\uff1a\u201c\u662f\u8c01\u6cc4\u9732\u4e86\u673a\u5bc6\u3002\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\uff1a\u201c\u5f53\u7136\u662f\u5e73\u7a7a\u591a\u51fa\u6765\u7684\u90a3\u4e2a\u4eba\u3002\u201d \u591a\u51fa\u6765\u7684\u4eba\uff0c\u5f53\u7136\u5c31\u662f\u90a3\u9ad8\u5927\u5a01\u731b\u7684\u8001\u4eba\u3002 \u9646\u5c0f\u51e4\uff1a\u201c\u8fd9\u4ef6\u4e8b\u7edd\u4e0d\u80fd\u8ba9\u7b2c\u5341\u4e2a\u4eba\u77e5\u9053\u7684\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u8981\u591a\u5e26\u4e00\u4e2a\u4eba\u53bb\uff1f\u201d \u5df4\u5c71\u5c0f\u987e\u53cd\u95ee\uff1a\u201c\u4f60\u77e5\u9053\u90a3\u4e2a\u4eba\u662f\u8c01\uff1f\u201d \u9646\u5c0f\u51e4\u4e0d\u77e5\u9053\u3002 \u5df4\u5c71\u5c0f\u987e\uff1a\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u6709\u4e2a\u5e08\u53d4\uff0c\u662f\u6ec7\u8fb9\u82d7\u4eba\u5c71\u4e8c\u5341\u516d\u5cd2\u7684\u5cd2\u4e3b\uff0c\u4e5f\u662f\u4e16\u88ad\u7684\u571f \u53f8\u3002\u201d \u9646\u5c0f\u51e4\u5ffd\u7136\u8df3\u4e86\u8d77\u6765\uff0c\uff1a\u201c\u4f60\u8bf4\u7684\u662f\u9f99\u731b\u98de\u72ee\uff1f\u201d \u5df4\u5c71\u5c0f\u987e\u5fae\u7b11\uff1a\u201c\u4ed6\u8db3\u8ff9\u4e45\u672a\u5230\u4e2d\u539f\uff0c\u96be\u602a\u8fde\u4f60\u90fd\u4e0d\u8ba4\u5f97\u4ed6\u4e86\u3002\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u4f60\u4eec\u8ba9\u4ed6\u4e5f\u53c2\u4e0e\u4e86\u8fd9\u79d8\u5bc6\uff1f\u201d \u5df4\u5c71\u5c0f\u987e\uff1a\u201c\u4ed6\u4e16\u4ee3\u5750\u9547\u5929\u5357\uff0c\u8d35\u6bd4\u738b\u4faf\uff0c\u5bcc\u8d35\u5c0a\u8363\uff0c\u6c5f\u6e56\u4e2d\u65e0\u4eba\u80fd\u53ca\uff0c\u4f60\u60f3\u4ed6\u600e\u4e48\u4f1a \u51fa\u5356\u6211\u4eec\uff1f\u6cc4\u9732\u6211\u4eec\u7684\u673a\u5bc6\uff1f \u9646\u5c0f\u51e4\u95ed\u4e0a\u4e86\u5634\u3002 \u53ef\u662f\u4ed6\u7ec8\u4e8e\u5df2\u60f3\u8d77\u8fd9\u4e2a\u4eba\u662f\u8c01\u4e86\uff0c\u4e5f\u5df2\u60f3\u8d77\u81ea\u5df1\u4e3a\u4ec0\u4e48\uff0c\u603b\u89c9\u5f97\u89c1\u8fc7\u8fd9\u4e2a\u4eba\u3002 \u4ed6\u5ffd\u7136\u89c9\u5f97\u5634\u91cc\u53c8\u9178\u53c8\u82e6\uff0c\u5c31\u597d\u50cf\u5403\u4e86\u4e00\u5927\u9505\u81ed\u8089\u3002 \u94c1\u80a9\uff1a\u201c\u73b0\u5728\u6211\u4eec\u53ea\u6709\u4e00\u4e2a\u6cd5\u5b50\u80fd\u8bc1\u660e\u4f60\u7684\u63a8\u6d4b\u662f\u5426\u6b63\u786e\u3002\u201d \u5df4\u5c71\u5c0f\u987e\uff1a\u201c\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d\u94c1\u80a9\uff1a\u201c\u8981\u77f3\u96c1\u8bf4\u51fa\u5251\u67c4\u4e2d\u7684\u79d8\u5bc6\u3002\u201d \u6bcf\u4e2a\u4eba\u90fd\u540c\u610f\uff0f\u6728\u9053\u4eba\u8ba9\u4f4d\uff0c\u82e5\u771f\u662f\u4e3a\u4e86\u4ed6\u548c\u6c88\u4e09\u5a18\u7684\u79c1\u60c5\uff0c\u4e5f\u5c31\u8bc1\u660e\u4e86\u4ed6\u662f\u8001\u5200\u628a\u5b50 \u3002\u201d \u94c1\u80a9\uff1a\u201c\u77f3\u96c1\u867d\u7136\u4e0d\u613f\u6cc4\u9732\u4ed6\u672c\u95e8\u5c0a\u957f\u7684\u9690\u79c1\uff0c\uff0c\u53ef\u662f\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u4ed6\u5df2\u4e0d\u80fd\u4e0d\u8bf4\u3002\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u4ed6\u5df2\u56de\u6b66\u5f53\uff1f\u201d\u94e3\u80a9\uff1a\u201c\u5929\u8fd8\u6ca1\u6709\u4eae\u5c31\u5df2\u56de\u53bbqo \u9646\u5c0f\u51e4\uff1a\u201c\u6728\u9053\u4eba\u662f\u4e0d\u662f\u4e5f\u5728\u6b66\u5f53\uff1f\u201d \u94c1\u80a9\uff1a\u201c\u6211\u4eec\u4e5f\u60f3\u5230\u5f88\u53ef\u80fd\u4f1a\u6709\u4eba\u5bf9\u4ed6\u4e0d\u5229\uff0c\u6240\u4ee5\u7279\u5730\u8981\u738b\u5341\u888b\u966a\u4ed6\u56de\u53bb\u3002\u201d \u5df4\u5c71\u5c0f\u987e\uff1a\u201c\u90a3\u4e48\u6211\u4eec\u4e5f\u5e94\u8be5\u5c3d\u5feb\u8d76\u5230\u6b66\u5f53\u53bb\u95ee\u4e2a\u6e05\u695a\u3002 \u9646\u5c0f\u51e4\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\uff1a\u201c\u6211\u53ea\u5e0c\u671b\u73b0\u5728\u8d76\u53bb\u8fd8\u6765\u5f97\u53ca\u3002\u7a81\u542c\u95e8\u5916\u6709\u4eba\uff1a\u201c\u73b0\u5728\u5df2\u6765\u4e0d \u53ca\u4e86\u3002\u201d \u738b\u5341\u888b\u5148\u5750\u4e0b\u6765\uff0c\u64e6\u5e72\u4e86\u8138\u4e0a\u7684\u6c57\uff0c\u5598\u8fc7\u4e00\u53e3\u6c14\uff0c\u624d\u7f13\u7f13\uff1a\u201c\u6b66\u5f53\u7b2c\u5341\u4e09\u4ee3\u638c\u95e8\u4eba\u77f3 \u96c1\uff0c\u5df2\u4e8e\u56db\u6708\u5341\u56db\u5348\u65f6\u524d\u4e00\u523b\u4ed9\u901d\uff0c\u4eab\u5e74\u56db\u5341\u4e03\u3002\u201d \u6ca1\u6709\u4eba\u52a8\uff0c\u6ca1\u6709\u4eba\u5f00\u53e3\u3002 \u5927\u5bb6\u7684\u5fc3\u90fd\u5df2\u6c89\u4e86\u4e0b\u53bb\uff0c\u8fc7\u4e86\u5f88\u4e45\uff0c\u624d\u6709\u4eba\u95ee\uff0f\u4ed6\u600e\u4e48\u6b7b\u7684\uff1f\u201d \u738b\u5341\u888b\uff1a\u201c\u4ed6\u6709\u5bbf\u75be\uff0c\u800c\u4e14\u5f88\u4e25\u91cd\u3002\u201d \u94c1\u80a9\uff1a\u201c\u662f\u4ec0\u4e48\uff1f\u201d \u738b\u5341\u888b\uff1a\u201c\u75c5\u5728\u809d\u812f\u4e4b\u95f4\uff0c\u6728\u9053\u4eba\u65e9\u5df2\u770b\u51fa\u4ed6\u5bff\u547d\u6700\u591a\u5df2\u53ea\u5269\u4e0b\u767e\u65e5ao \u9646\u5c0f\u51e4\u52a8\u5bb9\uff1a\u201c\u6728\u9053\u4eba\u66ff\u4ed6\u770b\u8fc7\u75c5\uff1f\u201d\u738b\u5341\u888b\uff1a\u201c\u6728\u9053\u4eba\u7684\u533b\u9053\u9887\u7cbe\uff0c\u6211\u4e5f\u61c2\u5f97\u4e00\u70b9\u533b \u672f\u3002\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u4f60\u770b\u4ed6\u771f\u7684\u662f\u56e0\u65e7\u75c5\u53d1\u4f5c\u800c\u6b7b\u7684\uff1f\u201d \u738b\u5341\u888b\uff1a\u201c\u7edd\u65e0\u7591\u95ee\u3002\u201d \u9646\u5c0f\u51e4\u6162\u6162\u7684\u5750\u4e86\u4e0b\u53bb\uff0c\u7adf\u4eff\u4f5b\u8fde\u7ad9\u90fd\u5df2\u7ad9\u4e0d\u7a33\u4e86\u3002 \u94c1\u80a9\u7684\u8138\u8272\u4e5f\u5f88\u6c89\u91cd\uff0f\u4ed6\u6709\u6ca1\u6709\u7559\u4e0b\u9057\u547d\uff0c\u6307\u5b9a\u7ee7\u627f\u6b66\u5f53\u638c\u95e8\u7684\u4eba\uff1f\u201d \u738b\u5341\u888b\uff1a\u201c\u6211\u4eec\u672c\u6765\u4ee5\u4e3a\u4ed6\u4e00\u5b9a\u6709\u9057\u4e66\u7559\u4e0b\u7684\uff0c\u5374\u627e\u4e0d\u7740\u3002 \u94c1\u80a9\u7684\u8138\u8272\u66f4\u6c89\u91cd\u3002 \u4ed6\u6df1\u77e5\u6b66\u5f53\u7684\u5bb6\u6cd5\u95e8\u89c4\uff0c\u638c\u95e8\u4eba\u82e5\u662f\u56e0\u7279\u522b\u4e8b\u6545\u53bb\u4e16\uff0c\u672b\u53ca\u7559\u4e0b\u9057\u547d\uff0c\u638c\u95e8\u4e4b\u4f4d\uff0c\u5c31\u7531 \u95e8\u4e2d\u8f88\u4efd\u6700\u5c0a\u7684\u4eba\u63a5\u638c\u3002 \u6b66\u5f53\u95e8\u4e0b\u8f88\u4efd\u6700\u5c0a\u7684\uff0c\u5c31\u662f\u6728\u9053\u4eba\u3002 \u94c1\u80a9\u957f\u957f\u53f9\u606f\uff0c\uff1a\u201c\u60f3\u4e0d\u5230\u4e09\u5341\u5e74\u540e\uff0c\u4ed6\u8fd8\u662f\u505a\u4e86\u6b66\u5f53\u638c\u95e8\u3002\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\uff1a\u201c\u8fd9\u53ea\u6015\u65e9\u5df2\u5728\u4ed6\u610f\u6599\u4e4b\u4e2d\u3002\u201d \u4ed6\u4eec\u5fc3\u91cc\u90fd\u660e\u767d\uff0c\u73b0\u5728\u82e5\u6ca1\u6709\u786e\u5207\u7684\u8bc1\u636e\uff0c\u66f4\u4e0d\u80fd\u52a8\u4ed6 \u6b66\u5f53\u7684\u638c\u95e8\uff0c\u662f\u7edd\u4e0d\u5bb9\u4efb\u4f55\u4eba\u8f7b\u72af\u7684\u3002 \u73b0\u5728\u4ed6\u4eec\u8fde\u4e00\u70b9\u8bc1\u636e\u90fd\u6ca1\u6709\uff0c\u5c31\u7b97\u6728\u9053\u4eba\u771f\u662f\u8001\u5200\u628a\u5b50\uff0c\u4ed6\u4eec\u4e5f\u65e0\u80fd\u4e3a\u529b\u3002 \u738b\u5341\u888b\u9eef\u7136\uff1a\u201c\u77f3\u96c1\u81ea\u5df1\u867d\u7136\u4e5f\u77e5\u9053\u6b7b\u671f\u4e0d\u8fdc\uff0c\u5374\u8fd8\u662f\u60f3\u4e0d\u5230\u4f1a\u5982\u6b64\u7a81\u7136\u3002\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u4ed6\u4e34\u6b7b\u65f6\u96be\u9053\u8fde\u4e00\u53e5\u8bdd\u90fd\u6ca1\u6709\u8bf4\uff1f\u201d \u738b\u5341\u888b\uff1a\u201c\u53ea\u8bf4\u4e86\u4e00\u65ec\u3002\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u4ed6\u8bf4\u4ec0\u4e48\uff1f\u201d \u738b\u5341\u888b\uff1a\u201c\u4ed6\u8981\u6211\u544a\u8bc9\u4f60\uff0c\u4f60\u731c\u5f97\u4e0d\u9519\u3002\u201d \u9646\u5c0f\u51e4\u970d\u7136\u7ad9\u8d77\uff0c\u53c8\u6162\u6162\u5750\u4e0b\uff0c\u5583\u5583\uff1a\u201c\u6ca1\u6709\u7528\u4e86\uff0c\u5c31\u7b97\u6211\u731c\u5f97\u4e0d\u9519\uff0c\u4e5f\u6ca1\u6709\u7528\u4e86\u3002\u201d \u4ed6\u95ee\u8fc7\u77f3\u96c1\uff0c\u6728\u9053\u4eba\u5f53\u5e74\u662f\u4e0d\u662f\u56e0\u79c1\u60c5\u800c\u88ab\u8feb\u8ba9\u4f4d\u7684\u3002 \u77f3\u96c1\u6ca1\u6709\u8bf4\uff0c\u7b49\u5230\u8bf4\u7684\u65f6\u5019\u5df2\u592a\u8fdf\u3002 \u5251\u67c4\u4e2d\u7684\u79d8\u5bc6\uff0c\u73b0\u5728\u65e0\u7591\u5df2\u843d\u5165\u6728\u9053\u4eba\u624b\u91cc\uff0c\u4ed6\u4eec\u5df2\u62ff\u4e0d\u51fa\u8bc1\u636e\u3002 \u94c1\u80a9\uff1a\u201c\u4f60\u731c\u7684\u867d\u4e0d\u9519\uff0c\u5374\u505a\u9519\u4e86\u3002\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u9519\u5728\u54ea\u91cc\uff1f\u201d \u94c1\u80a9\uff1a\u201c\u4f60\u65e2\u7136\u77e5\u9053\u6709\u4eba\u8981\u593a\u5251\uff0c\u5c31\u4e0d\u8be5\u8ba9\u77f3\u96c1\u5c06\u90a3\u79d8\u5bc6\u7559\u5728\u5251\u67c4\u91cc\u3002\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u6211\u4eec\u8fd9\u6837\u505a\uff0c\u53ea\u4e0d\u8fc7\u56e0\u4e3a\u8981\u8bf1\u4ed6\u4f9d\u7ea6\u5230\u6ee1\u7fe0\u697c\u53bb\uff0c\u6211\u4eec\u624d\u80fd\u5f53\u9762\u63ed\u7a7f\u4ed6\u7684\u771f \u9762\u76ee\uff0c\u5251\u67c4\u4e2d\u7684\u79d8\u5bc6\u82e5\u4e0d\u662f\u539f\u4ef6\uff0c\u4ed6\u4e00\u5b9a\u770b\u5f97\u51fa\uff0c\u4e00\u5b9a\u4f1a\u7591\u5fc3ao \u4ed6\u53f9\u606f\u7740\uff0c\u53c8\uff1a\u201c\u5f53\u65f6\u6211\u4eec\u600e\u4e48\u60f3\u5f97\u5230\u6d88\u606f\u4f1a\u8d70\u6f0f\uff0c\u4ed6\u7adf\u5ffd\u7136\u6539\u53d8\u4e86\u4e3b\u610f\u3002\u201d \u94c1\u80a9\u53f9\uff1a\u201c\u65e0\u8bba\u4ed6\u662f\u8c01\uff0c\u90fd\u5b9e\u5728\u662f\u4e2a\u4e86\u4e0d\u8d77\u7684\u4eba\uff0c\u4ed6\u7684\u8ba1\u5212\u867d\u7136\u4e00\u8d25\u6d82\u5730\uff0c\u53ef\u662f\u5230\u6700\u540e \u5173\u5934\uff0c\u4ed6\u8fd8\u662f\u6ca1\u6709\u8d25\u3002\u201d \u5927\u5bb6\u9ed8\u9ed8\u7684\u5750\u7740\uff0c\u5fc3\u60c5\u90fd\u5f88\u6cae\u4e27\u3002 \u4ed6\u4eec\u7684\u8ba1\u5212\u867d\u7136\u5468\u5bc6\u5de7\u5999\uff0c\u60f3\u4e0d\u5230\u6700\u540e\u8fd8\u662f\u529f\u4e8f\u4e00\u7b56\u3002 \u5df4\u5c71\u5c0f\u987e\uff1a\u201c\u73b0\u5728\u6211\u4eec\u5bf9\u4ed6\u96be\u9053\u771f\u7684\u5df2\u5b8c\u5168\u65e0\u80fd\u4e3a\u529b\uff1f\u201d \u9646\u5c0f\u51e4\u6c89\u541f\u7740\uff0c\u7f13\u7f13\uff1a\u201c\u4e5f\u8bb8\u6211\u8fd8\u80fd\u60f3\u51fa\u4e00\u4e24\u4e2a\u6cd5\u5b50\u6765\u3002\u201d \u5df4\u5c71\u5c0f\u987e\uff1a\u201c\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u4f60\u5e08\u53d4\u662f\u4e0d\u662f\u4e5f\u5728\u6b66\u5f53\uff1f\u201d \u5df4\u5c71\u5c0f\u987e\uff1a\u201c\u4ed6\u4e0d\u5728\u3002\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u4f60\u77e5\u9053\u4ed6\u5728\u54ea\u91cc\uff1f\u201d \u5df4\u5c71\u5c0f\u987e\uff1a\u201c\u6211\u77e5\u9053\u5168\u798f\u697c\u7684\u4e3b\u4eba\u662f\u4ed6\u6614\u5e74\u7684\u65e7\u5c5e\uff0c\u7279\u5730\u5bb0\u4e86\u6253\u80a5\u725b\uff0c\u8bf7\u4ed6\u53bb\u5927\u5feb\u6735 \u9890\uff0c\u8fd9\u79cd\u4e8b\u4ed6\u662f\u7edd\u4e0d\u4f1a\u9519\u8fc7\u7684\u3002\u201d \u9646\u5c0f\u51e4\u773c\u775b\u91cc\u53d1\u51fa\u4e86\u5149\uff0c\uff1a\u201c\u4ed6\u559c\u6b22\u5403\u8089\uff1f\u201d \u5df4\u5c71\u5c0f\u987e\uff1a\u201c\u7b80\u76f4\u4e0d\u53ef\u4e00\u65e5\u65e0\u8089\u3002\u201d \u9646\u5c0f\u51e4\u9009\u4eba\u201c\u4ed6\u5403\u5f97\u591a\u4e0d\u591a\uff1f\u201d \u5df4\u5c71\u5c0f\u987e\uff1a\u201c\u591a\u5f97\u8981\u547d\u3002\u201d \u56db\u6708\u5bf8\u2018\u65e5\uff0c\u5348\u540e\u3002 \u5168\u798f\u697c\u7684\u95e8\u4e0a\u8d34\u7740\u5f20\u7ea2\u7eb8\uff0f\u5bb6\u6709\u8d35\u5ba2\uff0c\u6b47\u4e1a\u4e00\u65e5\u3002\u201d \u867d\u7136\u6b47\u4e1a\uff0c\u95e8\u677f\u5e76\u6ca1\u6709\u4e0a\u8d77\u6765\uff0c\u4e00\u8d70\u8fdb\u95e8\uff0c\u5c31\u53ef\u4ee5\u770b\u89c1\u5a01\u6b66\u9ad8\u5927\uff0c\u6c14\u541e\u5168\u725b\u7684\u9f99\u731b\u9f99\u98de \u72ee\u3002 \u4e09\u5f20\u684c\u5b50\u62fc\u8d77\u6765\uff0c\u6446\u7740\u4e00\u5927\u9505\u8089\u3002 \u4ed6\u5403\u8089\u4e0d\u559c\u6b22\u7cbe\u5207\u7ec6\u80fa\uff0c\u82b1\u6837\u7ffb\u65b0\uff0c\u8981\u5486\u8089\uff0c\u5c31\u5f97\u4e00\u5927\u5757\u4e00\u5927\u5757\u7684\u5403\u3002 \u501f\u5927\u7684\u5385\u5802\u91cc\uff0c\u53ea\u6709\u4e00\u4e2a\u5802\u60f0\u8fdc\u8fdc\u7684\u7ad9\u7740\u4f3a\u5019\uff0c\u8fde\u4e3b\u4eba\u90fd\u4e0d\u5728\u3002 \u4ed6\u5403\u8089\u7684\u65f6\u5019\uff0c\u4e0d\u559c\u6b22\u522b\u4eba\u6253\u6270\uff0c\u4e5f\u4e0d\u559c\u6b22\u8bf4\u8bdd\u3002 \u53ef\u662f\u4ed6\u5e76\u6ca1\u6709\u53eb\u4eba\u62e6\u963b\u9646\u5c0f\u51e4\u3002 \u9646\u5c0f\u51e4\u5c31\u5927\u6b65\u8d70\u8fc7\u53bb\uff0c\u642c\u4e86\u5f20\u6905\u5b50\uff0c\u5728\u4ed6\u5bf9\u9762\u5750\u4e0b\uff0c\u5fae\u7b11\uff1a\u201c\u4f60\u597d\u3002\u201d \u9f99\u731b\uff1a\u201c\u597d\u3002\u201d\u9646\u5c0f\u51e4\uff1a\u201c\u6211\u8ba4\u5f97\u4f60\uff0f\u9f99\u731b\uff1a\u201c\u6211\u4e5f\u8ba4\u5f97\u4f60\uff0c\u4f60\u662f\u9646\u5c0f\u51e4Jo\u9646\u5c0f\u51e4\u9053 \uff0f\u4f46\u6211\u5374\u4e0d\u8ba4\u5f97\u9f99\u731b\uff0c\u6211\u53ea\u8ba4\u5f97\u4f60\u3002\u201d \u9f99\u731b\u5927\u7b11\uff0f\u6211\u96be\u9053\u4e0d\u662f\u9f99\u731b\uff1f\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u4f60\u662f\u98de\u72ee\u571f\u53f8\uff0c\u96be\u9053\u5c31\u4e0d\u662f\u5403\u8089\u7684\u5c06\u519b\uff1f\u201d \u9f99\u731b\u4e0d\u7b11\u4e86\uff0c\u4e00\u53cc\u73af\u76ee\u4e2d\u7cbe\u5149\u66b4\u5c04\uff0c\u77aa\u7740\u9646\u5c0f\u51e4\u3002\u9646\u5c0f\u51e4\uff1a\u201c\u5c06\u519b\u5e76\u6ca1\u6709\u6b7b\uff0c\u5c06\u519b\u8fd8\u5728 \u5403\u8089\u3002\u201d \u9f99\u731b\uff1a\u201c\u8089\u597d\u5403\u3002\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u72ac\u90ce\u541b\u65e2\u7136\u80fd\u5c06\u4f60\u626e\u6210\u5c06\u519b\u7684\u6837\u5b50\uff0c\u5f53\u7136\u4e5f\u80fd\u5c06\u522b\u4eba\u626e\u6210\u90a3\u6837\u5b50\uff0c\u4f55\u51b5\u4eba\u6b7b \u4e86\u4e4b\u540e\uff0c\u6837\u5b50\u672c\u5c31\u5dee\u4e0d\u591a\u3002\u201d \u9f99\u731b\uff1a\u201c\u5c06\u519b\u4e3a\u4ec0\u4e48\u4f1a\u6b7b\uff1f\u201d\u9646\u5c0f\u51e4\uff1a\u201c\u56e0\u4e3a\u6211\u53bb\u4e86\u3002\u201d \u9f99\u731b\uff1a\u201c\u4f60\u53bb\u4e86\u5c06\u519b\u5c31\u8981\u6b7b\uff1f\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u5c06\u519b\u7684\u5173\u7cfb\u91cd\u5927\uff0c\u9664\u4e86\u8001\u5200\u628a\u5b50\u4e4b\u5916\uff0c\u7edd\u4e0d\u80fd\u8ba9\u4efb\u4f55\u4eba\u770b\u51fa\u4ed6\u7684\u9762\u76ee\uff0c\u65e9\u2014 \u70b9\u6b7b\uff0c\u603b\u6bd4\u8f83\u5b89\u5168\u4e9bao \u9f99\u731b\uff1a\u201c\u4e0d\u9519\uff0c\u6b7b\u4eba\u7684\u786e\u6700\u5b89\u5168\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u6ce8\u610f\u6b7b\u4eba\u3002\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u53ea\u53ef\u60dc\u6700\u8fd1\u6b7b\u4eba\u5e38\u5e38\u4f1a\u590d\u6d3b\u3002\u201d \u9f99\u731b\u7aef\u8d77\u4e86\u2014\u6753\u8089\uff0c\u5ffd\u7136\u95ee\uff0c\u4f60\u5403\u8089\uff1f\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u5403\u3002\u201d \u9f99\u731b\uff1a\u201c\u5403\u5f97\u591a\uff1f\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u591a\u3002\u201d \u9f99\u731b\uff1a\u201c\u597d\uff0c\u60a8\u5403\u3002\u201d \u4ed6\u5148\u5c06\u4e00\u6753\u8089\u5012\u5165\u5634\u91cc\uff0c\u5c31\u5c06\u6728\u6753\u9012\u7ed9\u4e86\u9646\u5c0f\u51e4\uff0f\u5feb\u5403\uff0c\u591a\u5403\uff0c\u8089\u597d\u5403\u3002\u201d \u9646\u5c0f\u51e4\u4e5f\u76db\u8d77\u2014\u6784\u8089\uff0f\u8089\u7684\u786e\u597d\u5403\uff0c\u597d\u5403\u5f97\u8981\u547d\uff0c\u5987\u53ef\u60dc\u6709\u65f6\u7adf\u771f\u4f1a\u8981\u4eba\u7684\u547d\u3002\u201d \u9f99\u731b\uff1a\u201c\u5c06\u519b\u5403\u8089\uff0c\u4f60\u4e5f\u5403\u8089\uff0c\u5927\u5bb6\u90fd\u5403\u8089\uff0c\u5403\u8089\u7684\u672a\u5fc5\u5c31\u662f\u5c06\u519b\u3002\u201d \u9646\u5c0f\u51e4\u627f\u8ba4\u3002 \u9f99\u731b\u773c\u775b\u91cc\u9732\u51fa\u79cd\u8be1\u6f0f\u7684\u7b11\u610f\uff0c\u5ffd\u7136\u538b\u4f4e\u58f0\u97f3\uff0c\uff1a\u201c\u6240\u4ee5\u4f60\u6c38\u8fdc\u4e5f\u6ca1\u6cd5\u5b50\u8bc1\u660e\u6211\u5c31\u662f\u5c06 \u519b\u3002\u201d \u4ed6\u53c8\u5927\u7b11\uff0f\u6240\u4ee5\u4f60\u53ea\u6709\u5403\u8089\u3002\u201d \u9646\u5c0f\u51e4\u60f3\u7b11\uff0c\u5374\u5df2\u7b11\u4e0d\u51fa\u3002 \u4ed6\u53ea\u6709\u5403\u8089\u3002 \u8089\u7684\u786e\u70b3\u5f97\u5f88\u9999\uff0c\u53ef\u662f\u4ed6\u521a\u5403\u4e86\u4e00\u53e3\uff0c\u8138\u8272\u5c31\u53d8\u4e86\u3002 \u9f99\u731b\u7b11\uff1a\u201c\u4eca\u5929\u4f60\u597d\u50cf\u5403\u5f97\u4e0d\u5feb\uff0c\u4e5f\u4e0d\u591a\u3002\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u4f60\u5403\u4e86\u591a\u5c11\uff1f\u201d \u9f99\u731b\uff1a\u201c\u5f88\u591a\uff0c\u591a\u5f97\u8981\u547d\uff0f\u9646\u5c0f\u51e4\u82e6\u7b11\uff1a\u201c\u8fd9\u6b21\u53ea\u4f2f\u662f\u771f\u7684\u8981\u547d\uff0f\u9f99\u731b\uff1a\u201c\u8981\u8c01\u7684\u547d\u3002\u201d \u9646\u5c0f\u51e4\uff1a\u201c\u4f60\u7684\u3002\u201d \u4ed6\u7684\u4eba\u5728\u684c\u4e0a\u8f7b\u8f7b\u4e00\u6309\uff0c\u4eba\u5df2\u63a0\u8fc7\u684c\u9762\uff0c\u95ea\u7535\u822c\u53bb\u70b9\u9f99\u731b\u5fc3\u8109\u9644\u8fd1\u7684\u7a74\u9053\u3002 \u53ea\u53ef\u60dc\u4ed6\u5fd8\u4e86\u4e2d\u95f4\u8fd8\u6709\u4e00\u9505\u8089\u3002 \u4e00\u9505\u8981\u547d\u7684\u8089\u3002 \u5c06\u519b\u7684\u52a8\u4f5c\u4e5f\u6781\u5feb\uff0c\u7a81\u7136\u6380\u8d77\u8fd9\u9505\u8089\uff0c\u8089\u6c41\u98de\u6e85\uff0c\u8fd8\u662f\u6eda\u70eb\u7684\u3002\u9646\u5c0f\u51e4\u53ea\u6709\u95ea\u907f\uff0c\u5927\u58f0 \uff1a\u201c\u5750\u7740\uff0c\u4e0d\u8981\u52a8\u3002\u201d \u9f99\u731b\u5f53\u7136\u4e0d\u4f1a\u542c\u4ed6\u7684\uff0c\u8eab\u5b50\u5df2\u63a0\u8d77\uff0c\u5f80\u5916\u9762\u7a9c\u4e86\u51fa\u53bb\u3002 \u4ed6\u4e0d\u4f46\u52a8\u4e86\uff0c\u800c\u4e14\u52a8\u5f97\u5f88\u5feb\uff0c\u5f88\u5267\u70c8\u3002 \u6240\u4ee5\u4e45\u5df2\u6f5c\u4f0f\u5728\u809a\u80a0\u80c3\u91cc\u7684\u6bd2\uff0c\u5ffd\u7136\u5c31\u653b\u5165\u4e86\u4ed6\u7684\u5fc3\u3002 \u4ed6\u7acb\u523b\u5012\u4e86\u4e0b\u53bb\u3002 \u9646\u5c0f\u51e4\uff1a\u201c\u8089\u91cc\u6709\u6bd2\uff0c\u4e00\u52a8\u5c31\u2026\u2026\uff01\u201d \u4ed6\u6ca1\u6709\u8bf4\u4e0b\u53bb\uff0c\u56e0\u4e3a\u4ed6\u770b\u5f97\u51fa\u9f99\u731b\u5df2\u542c\u4e0d\u89c1\u4ed6\u7684\u8bdd\u4e86\u3002 \u8fd9\u9505\u8089\u771f\u7684\u8981\u4e86\u4ed6\u7684\u547d\u3002 \u4ed6\u5012\u4e0b\u53bb\u65f6\uff0c\u8138\u5df2\u53d1\u9ed1\uff0c\u8138\u53d1\u9ed1\u65f6\uff0c\u5df2\u7ecf\u53d8\u6210\u4e86\u4e2a\u6b7b\u4eba\u3002 \u6b7b\u4eba\u65e2\u4e0d\u662f\u98de\u72ee\u571f\u53f8\uff0c\u4e5f\u4e0d\u662f\u5c06\u519b\u3002 \u6b7b\u4eba\u5c31\u662f\u6b7b\u4eba\u3002 \u8fd9\u9505\u8089\u662f\u8c01\u716e\u7684\uff1f \u8fd9\u91cc\u7684\u4e3b\u4eba\u662f\u8c01\u5462\uff1f \u8fdc\u8fdc\u7ad9\u5728\u4e00\u65c1\u4f3a\u5019\u7684\u5802\u500d\uff0c\u65e9\u5df3\u5413\u5446\u4e86\uff0c\u9646\u5c0f\u51e4\u4e00\u628a\u63ea\u4f4f\u4ed6\uff0f\u5e26\u6211\u5230\u53a8\u623f\u53bb\u3002\u201d \u716e\u8089\u7684\u4eba\u5f53\u7136\u5e94\u8be5\u5728\u53a8\u623f\u91cc\u3002 \u53ef\u662f\u53a8\u623f\u91cc\u5374\u53ea\u6709\u8089\uff0c\u6ca1\u6709\u4eba\u3002 \u7089\u5b50\u4e0a\u8fd8\u716e\u7740\u4e00\u5927\u9505\u8089\uff0c\u597d\u5927\u7684\u9505\uff0c\u7adf\u50cf\u662f\u6b66\u5f53\u5c71\u4e0a\uff0c\u9999\u79ef\u53a8\u91cc\u7684\u716e\u996d\u9505\uff0c\u91cc\u9762\u6ee1\u6ee1\u7684 \u4e00\u9505\u8089\uff0c\u8fd8\u6ca1\u6709\u5b8c\u5168\u716e\u719f\u3002 \u9646\u5c0f\u51e4\u8138\u8272\u53c8\u53d8\u4e86\uff0c\u7adf\u5fcd\u4e0d\u4f73\u5f00\u59cb\u5455\u5410\u3002 \u4ed6\u5ffd\u7136\u53d1\u73b0\u4e86\u4e00\u6837\u53ef\u6015\u7684\u4e8b\u96be\u9053\u8089\u5728\u9505\u91cc\uff0c\u4eba\u4e5f\u5728\u9505\u91cc\uff1f \u73b0\u5728\u8fd8\u80fd\u591f\u4e3a\u9646\u5c0f\u51e4\u4f5c\u8bc1\u7684\uff0c\u5f88\u53ef\u80fd\u53ea\u5269\u4e0b\u4e00\u4e2a\u4eba\u3002 \u4e0d\u7ba1\u4ed6\u662f\u8868\u54e5\u4e5f\u597d\uff0c\u662f\u53e4\u677e\u4e5f\u597d\uff0c\u9646\u5c0f\u51e4\u53ea\u5e0c\u671b\u4ed6\u8fd8\u662f\u4e2a\u6d3b\u4eba\u3002 \u73b0\u5728\u8fd9\u4e2a\u4eba\u5728\u54ea\u91cc\uff1f\u5e78\u597d\u53ea\u6709\u9646\u5c0f\u51e4\u81ea\u5df1\u77e5\u9053\u3002 \u53f6\u5bb6\u51cc\u5f53\u7136\u7edd\u4e0d\u662f\u4e2a\u5b89\u5168\u7684\u5730\u65b9\uff0c\u4ed6\u65e9\u5df2\u5c06\u8fd9\u4e2a\u4eba\u9001\u5230\u4e00\u4e2a\u4efb\u4f55\u4eba\u90fd\u60f3\u4e0d\u5230\u7684\u79d8\u5bc6\u6240\u5728 \u68cb\u5c40\u5df2\u5c06\u7ec8\u4e86\uff0c\u8fd9\u5df2\u662f\u4ed6\u6700\u540e\u4e00\u7740\u6740\u624b\uff0c\u4ed6\u5f53\u7136\u8981\u4e3a\u81ea\u5df1\u7559\u4e00\u70b9\u79d8\u5bc6 \u66ae\u6625\u7684\u4e0b\u5348\uff0c\u9633\u5149\u8fd8\u662f\u5f88\u707f\u70c2\uff0c\u4ed6\u6162\u6162\u7684\u8d70\u5728\u957f\u8857\u4e0a\uff0c\u597d\u50cf\u4e00\u70b9\u76ee\u7684\u90fd\u6ca1\u6709\u3002 \u8857\u9053\u4e24\u65c1\u6709\u5404\u5f0f\u5404\u6837\u7684\u5e97\u94fa\uff0c\u5e97\u94fa\u4e2d\u6709\u5404\u5f0f\u5404\u6837\u7684\u4eba\uff0c\u4ed6\u770b\u5f97\u89c1\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u770b\u5f97\u89c1 \u4ed6\uff0c\u4f46\u4ed6\u5374\u4e0d\u77e5\u9053\u90a3\u5176\u4e2d\u6709\u591a\u5c11\u5165\u662f\u5728\u5077\u5077\u7684\u76d1\u89c6\u7740\u4ed6\u3002 \u957f\u8857\u5c3d\u5934\uff0c\u5ffd\u7136\u6709\u8f86\u9a6c\u8f66\u6025\u9a70\u800c\u6765\uff0c\u51e0\u4e4e\u5c06\u4ed6\u649e\u5012\uff0c\u4eff\u4f5b\u6709\u4e2a\u4eba\u4ece\u8f66\u7a97\u91cc\u4f38\u51fa\u5934\u6765\u770b\u4e86 \u4ed6\u4e00\u773c\uff0c\u4eff\u4f5b\u6709\u53cc\u5f88\u660e\u4eae\u7684\u773c\u775b\u3002 \u5982\u679c\u4ed6\u4e5f\u80fd\u4ed4\u7ec6\u770b\u770b\uff0c\u4e00\u5b9a\u4f1a\u8ba4\u5f97\u8fd9\u4e2a\u4eba\u7684\uff0c\u53ea\u53ef\u60dc\u4ed6\u8981\u53bb\u770b\u7684\u65f6\u5019\uff0c\u8f66\u9a6c\u5df2\u53bb\u8fdc \u53ef\u662f\u76f4\u5230\u4ed6\u8d70\u51fa\u8fd9\u6761\u957f\u8857\u540e\uff0c\u4ed6\u5fc3\u91cc\u4eff\u4f5b\u8fd8\u5728\u60f3\u7740\u90a3\u53cc\u660e\u4eae\u7684\u773c\u775b\uff0c\u751a\u81f3\u8fd8\u56e0\u6b64\u89c9\u5f97\u4e0d \u5b89\u3002 \u4e00\u4e2a\u964c\u751f\u4eba\u7684\u5306\u5306\u4e00\u77a5\uff0c\u4e3a\u4ec0\u4e48\u5c31\u80fd\u8ba9\u4ed6\u63d0\u5fc3\u540a\u80c6\uff1f \u96be\u9053\u8fd9\u4e2a\u4eba\u5e76\u4e0d\u662f\u4e2a\u964c\u751f\u4eba\uff1f \u4ed6\u5c3d\u91cf\u4e0d\u518d\u53bb\u60f3\u8fd9\u4ef6\u4e8b\uff0c\u8d70\u8fc7\u8857\u89d2\u7684\u6c34\u679c\u644a\u65f6\uff0c\u4ed6\u4e70\u4e86\u4e24\u4e2a\u7281\uff0c\u4e00\u4e2a\u629b\u7ed9\u7684\u5b69\u5b50\uff0c\u4e00\u4e2a \u62ff\u5728\u624b\u91cc\u6162\u6162\u7684\u5543\u3002 \u73b0\u5728\u4ed6\u4e00\u5fc3\u53ea\u60f3\u6293\u4f4f\u6728\u9053\u4eba\u81f4\u547d\u7684\u8981\u5bb3\uff0c\u73b0\u5728\u6728\u9053\u4eba\u662f\u4e0d\u662f\u4e5f\u60f3\u6740\u4e86\u4ed6\uff1f \u521a\u624d\u90a3\u9505\u8981\u547d\u7684\u8089\uff0c\u4ed6\u867d\u7136\u53ea\u54ac\u4e86\u4e24\u53e3\u5c31\u5410\u51fa\u6765\uff0c\u6b64\u523b\u80c3\u91cc\u8fd8\u662f\u89c9\u5f97\u6709\u70b9\u4e0d\u8212\u670d\u3002 \u5e78\u597d\u8089\u91cc\u4e0b\u7684\u6bd2\u4efd\u91cf\u5e76\u4e0d\u91cd\uff0c\u4efd\u91cf\u592a\u91cd\uff0c\u5c31\u5bb9\u6613\u89c9\u5bdf\u3002 \u9f99\u731b\u5e76\u4e0d\u662f\u53cd\u5e94\u8fdf\u949d\u7684\u4eba\uff0c\u53ea\u4e0d\u8fc7\u8089\u5403\u5f97\u592a\u591a\u4e86\u4e9b\u3002 \u591a\u5f97\u8981\u547d\uff01 \u5982\u679c\u4ed6\u521a\u624d\u4e5f\u591a\u5403\u4e86\u51e0\u5757\u8089\uff0c\u6728\u9053\u4eba\u5c31\u771f\u7684\u5b8c\u5168\u7528\u4e0d\u7740\u518d\u62c5\u5fc3\u4efb\u4f55\u4e8b\u4e86\uff0c\u4ed6\u81ea\u5df1\u4e5f\u7528\u4e0d \u7740\u518d\u62c5\u5fc3\u4efb\u4f55\u4e8b\u4e86\u3002 \u521a\u624d\u8f66\u7a97\u91cc\u90a3\u4e2a\u4eba\u597d\u50cf\u662f\u4e2a\u5973\u4eba\uff0c\u62c9\u8f66\u7684\u9a6c\u5634\u91cc\u6709\u5f88\u6d53\u7684\u767d\u6cab\u5b50\uff0c\u597d\u50cf\u8d76\u4e86\u5f88\u8fdc\u7684\u8def\uff0c \u800c\u4e14\u8d76\u5f97\u5f88\u6025\u3002 \u5979\u662f\u8c01\uff1f\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f \u9646\u5c0f\u51e4\u867d\u7136\u5c3d\u91cf\u4e0d\u8ba9\u81ea\u5df1\u518d\u53bb\u60f3\u8fd9\u4ef6\u4e8b\uff0c\u5374\u504f\u504f\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u53bb\u60f3\u3002 \u4ed6\u5fc3\u91cc\u7adf\u4f3c\u6709\u79cd\u5f88\u5947\u602a\u7684\u9884\u611f\uff0c\u89c9\u5f97\u8fd9\u4e2a\u4eba\u5bf9\u4ed6\u5f88\u91cd \u8981\u3002 \u771f\u6b63\u5bf9\u4ed6\u91cd\u8981\u7684\u4eba\u5f53\u7136\u4e0d\u662f\u5979\uff0c\u662f\u53e4\u677e\u3002 \u90a3\u5929\u706f\u706d\u4e86\u7684\u65f6\u5019\uff0c\u662f\u4ed6\u4eb2\u81ea\u51fa\u624b\u5236\u4f4f\u4ed6\u7684\uff0c\u6d77\u5947\u9614\u548c\u9ad8\u6d9b\u88ab\u56da\u7981\u5728\u540e\u9762\u7684\u5730\u7a96\u91cc\u3002 \u4ece\u5e7d\u7075\u5c71\u5e84\u6765\u7684\u4eba\uff0c\u73b0\u5728\u90fd\u5df2\u88ab\u56da\u7981\u5728\u90a3\u5730\u7a96\u91cc\uff0c\u4e0b\u5c71\u7684\u7b2c\u4e00\u5929\uff0c\u9646\u5c0f\u51e4\u5c31\u5df2\u5c06\u8fd9\u4e9b\u5165 \u7684\u5bb9\u8c8c\u56fe\u5f62\u4ea4\u7ed9\u4e86\u90a3\u4e2a\u201c\u6e9c\u72d7\u7684\u5802\u500c\u201d\uff0c\u9e70\u5de2\u4e2d\u7684\u4eba\u7acb\u523b\u5206\u522b\u5f00\u59cb\u884c\u52a8\uff0c\u5c06\u4ed6\u4eec\u2014\u7f51\u6253\u5c3d\uff0c \u518d\u7531\u72ac\u90ce\u541b\u3001\u53f8\u7a7a\u6458\u661f\u548c\u738b\u5341\u888b\u5c06\u81ea\u5df1\u4eba\u6539\u626e\u6210\u4ed6\u4eec\u7684\u6837\u5b50\u3002 \u9646\u5c0f\u51e4\u5e76\u4e0d\u5341\u5206\u5173\u5fc3\u4ed6\u4eec\u7684\u6b7b\u6d3b\uff0c\u53cd\u6b63\u4ed6\u4eec\u4e5f\u7edd\u4e0d\u4f1a\u77e5\u9053\u201c\u8001\u5200\u628a\u5b50\u201d\u7684\u771f\u5b9e\u8eab\u5206\uff0c\u53cd \u6b63\u4ed6\u4eec\u90fd\u5df2\u662f\u65e9\u8be5\u6b7b\u4e86\u7684\u4eba\u3002 \u201c\u8868\u54e5\u201d\u5462\uff1f \u4ed6\u5c06\u8868\u54e5\u9001\u5230\u54ea\u91cc\u53bb\u4e86\uff1f\u662f\u7528\u4ec0\u4e48\u6cd5\u5b50\u9001\u8d70\u7684\uff1f\u4ed6\u597d\u50cf\u6839\u672c\u6ca1\u6709\u673a\u4f1a\u5e26\u8d70\u90a3\u4e48\u5927\u4e00\u4e2a\u6d3b \u4eba\u3002 \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u81ea\u5df1\u5bf9\u81ea\u5df1\u7b11\u4e86\u7b11\uff0c\u7a7f\u8fc7\u6761\u659c\u5df7\uff0c\u8d70\u56de\u5ba2\u6808\u2014\u5c31\u662f\u56db\u6708\u5341\u4e00\u90a3\u5929\uff0c\u4ed6\u4eec\u521a \u5230\u8fd9\u91cc\u7684\u65f6\u5019\uff0c\u6295\u5bbf\u7684\u90a3\u5bb6\u5ba2\u6808\u3002 \u4ed6\u4eec\u5378\u4e0b\u4e86\u884c\u674e\uff0c\u5b89\u987f\u4e86\u8f66\u9a6c\u540e\uff0c\u624d\u53bb\u559d\u9152\u7684\uff0c\u559d\u9152\u7684\u65f6\u5019\u624d\u9047\u89c1\u4ed6\u7684\u5916\u7525\u5973\uff0c\u624d\u5230\u4e86 \u6ee1\u7fe0\u56ed\uff0c\u8f66\u9a6c\u548c\u884c\u674e\u90fd\u8fd8\u7559\u5728\u5ba2\u6808\u91cc\uff0c\u4ece\u8def\u4e0a\u96c7\u6765\u7684\u8f66\u592b\uff0c\u8fd8\u5728\u7b49\u7740\u4ed6\u5f00\u53d1\u811a\u529b\u94b1\u3002 \u4ed6\u597d\u50cf\u5df2\u7ecf\u5fd8\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u597d\u50cf\u76f4\u5230\u73b0\u5728\u624d\u60f3\u8d77\u3002 \u7ed9\u4e86\u53cc\u500d\u7684\u8d4f\u94b1\uff0c\u4ed6\u597d\u50cf\u53c8\u89c9\u5f97\u6709\u70b9\u51a4\u6789\u4e86\uff0c\u6240\u4ee5\u53c8\u53eb\u4e2d\u592b\u5957\u4e0a\u9a6c\uff0f\u4eca\u5929\u7684\u5929\u6c14\u4e0d\u9519\uff0c \u6211\u60f3\u5230\u56db\u5904\u53bb\u901b\u901b\uff0c\u4f60\u518d\u66ff\u6211\u8d76\u6700\u540e\u4e00\u6b21\u8f66\uff0c\u6211\u8bf7\u4f60\u559d\u9152\u3002\u201d \u5929\u6c14\u771f\u4e0d\u9519\uff0c\u8d76\u8f66\u7684\u4eba\u548c\u62c9\u8f66\u7684\u9a6c\u90fd\u5df2\u517b\u8db3\u4e86\u7cbe\u795e\uff0c\u8d70\u5728\u8def\u4e0a\u4e5f\u7279\u522b\u6709\u52b2\u3002 \u8fd9\u91cc\u4e0d\u4f46\u662f\u53bb\u6b66\u5f53\u5fc5\u7ecf\u4e4b\u8def\uff0c\u4e5f\u662f\u8ddd\u79bb\u6b66\u5f53\u5c71\u53e3\u6700\u8fd1\u7684\u2014\u4e2a\u5e02\u9547\uff0c\u8d70\u51fa\u95f9\u5e02\u533a\u540e\uff0c\u6ee1\u773c \u9752\u7fe0\uff0c\u5929\u4e0b\u95fb\u540d\u7684\u6b66\u5f53\u5c71\u4eff\u4f5b\u5c31\u5728\u773c\u524d\u3002 \u4ed6\u4eec\u5728\u5c71\u9e93\u65c1\u2014\u4e2a\u6811\u6797\u8fb9\u505c\u4e0b\u6765\uff0c\u9646\u5c0f\u51e4\u624d\u60f3\u8d77\u5fd8\u8bb0\u5e26\u9152\u3002\u201c\u6211\u7b54\u5e94\u8fc7\u8bf7\u4f60\u559d\u9152\u7684\uff0f\u4ed6 \u53c8\u7ed9\u4e86\u8f66\u592b\u4e00\u952d\u94f6\u5b50\uff0f\u4f60\u53bb\u4e70\uff0c\u591a\u4e70\u4e00\u70b9\uff0c\u5269\u4e0b\u6765\u7684\u7ed9\u4f60\u3002\u201d \u8fd9\u91cc\u79bb\u5356\u9152\u7684\u5730\u65b9\u5f53\u7136\u4e0d\u8fd1\uff0c\u53ef\u662f\u770b\u5728\u94f6\u5b50\u7684\u4efd\u4e0a\uff0c\u8f66\u592b\u8fd8\u662f\u5174\u9ad8\u91c7\u70c8\u7684\u8d70\u4e86\u3002 \u73b0\u5728\u6b63\u662f\u9ec4\u660f\uff0c\u5915\u9633\u6ee1\u5929\uff0c\u665a\u971e\u8273\u4e3d\uff0c\u8fd9\u9053\u6559\u7684\u540d\u5c71\uff0c\u6b66\u6797\u7684\u602a\u5730\uff0c\u5728\u5915\u9633\u4e0b\u770b\u6765\uff0c\u4e5f \u5c31\u66f4\u7470\u4e3d\u96c4\u5947\u3002 \u53ea\u4e0d\u8fc7\u8fd9\u9644\u8fd1\u5e76\u6ca1\u6709\u4e0a\u5c71\u7684\u8def\uff0c\u8ddd\u79bb\u5c71\u4e0a\u7684\u9053\u89c2\u548c\u540d\u80dc\u53c8\u5f88\u8fdc\u3002 \u6240\u4ee5\u65e0\u8bba\u5f80\u54ea\u8fb9\u53bb\u770b\uff0c\u90fd\u770b\u4e0d\u89c1\u4e00\u4e2a\u4eba\uff0c\u9646\u5c0f\u51e4\u5ffd\u7136\u4e00\u5934\u94bb\u8fdb\u4e86\u8f66\u5e95\u3002 \u8f66\u5e95\u4e0b\u66f4\u6ca1\u6709\u4e1c\u897f\u53ef\u770b\u4e86\uff0c\u4ed6\u94bb\u8fdb\u53bb\u5e72\u4ec0\u4e48\uff1f\u96be\u9053\u60f3\u5728\u4e0b\u9762\u7761\u4e00\u89c9\uff1f \u53ef\u662f\u4ed6\u5e76\u6ca1\u6709\u95ed\u4e0a\u773c\u775b\uff0c\u53cd\u800c\u597d\u50cf\u5728\u54fa\u54fa\u81ea\u8bed\uff0f\u53ea\u4e0d\u8fc7\u997f\u4e86\u5b50\u4e24\u5929\uff0c\u65e0\u8bba\u4ec0\u4e48\u4eba\u90fd\u4e0d\u4f1a \u997f\u6b7b\u7684\uff0c\u4f55\u51b5\u9690\u58eb\u4eec\u901a\u5e38\u90fd\u5403\u5f97\u4e0d\u592a\u591a\u7684oo \u4ed6\u53c8\u597d\u50cf\u5e76\u4e0d\u662f\u5728\u54fa\u54fa\u81ea\u8bed\uff0c\u96be\u9053\u8f66\u5e95\u4e0b\u8fd8\u6709\u522b\u7684\u4eba\uff1f \u4eba\u5728\u54ea\u91cc\uff1f \u4ed6\u6572\u4e86\u6572\u8f66\u5e95\u7684\u6728\u677f\uff0c\u91cc\u9762\u7adf\u662f\u7a7a\u7684\uff0c\u8f66\u5e95\u5c45\u7136\u8fd8\u6709\u5939\u5c42\u3002\uff0c \u4eac\u5b98\u4eec\u544a\u8001\u8fd8\u4e61\uff0c\u5e26\u7684\u4e1c\u897f\u603b\u4e0d\u5c11\uff0c\u5f53\u7136\u8981\u96c7\u8f86\u7279\u522b\u5927\u7684\u8f66\uff0c\u8f66\u5e95\u82e5\u6709\u5939\u5c42\uff0c\u5f53\u7136\u4e5f\u4e0d \u5c0f\uff0c\u8981\u5c06\u2014\u4e2a\u4eba\u85cf\u5728\u91cc\u9762\uff0c\u5e76\u4e0d\u4ef6\u5f88\u56f0\u96be\u7684\u4e8b\u3002 \u90a3\u5929\u5728\u51cc\u98ce\u5c71\u5e84\u91cc\uff0c\u67f3\u9752\u9752\u8fd8\u6ca1\u6709\u9192\uff0c\u522b\u4eba\u6b63\u5fd9\u7740\u6613\u5bb9\u6539\u626e\u65f6\uff0c\u4ed6\u5df2\u5c06\u201c\u8868\u54e5\u201d\u85cf\u5728\u8fd9 \u91cc\u9762\u4e86\u3002 \u5c06\u4e00\u4e2a\u4eba\u70b9\u7740\u707e\u9053\uff0c\u5173\u5728\u8fd9\u79cd\u5730\u65b9\uff0c\u867d\u7136\u662f\u8650\u5f85\uff0c\u4f46\u662f\u4ed6\u8ba4\u4e3a\u6709\u4e9b\u4eba\u4e2d\u5c31\u5e94\u8be5\u53d7\u70b9\u7f6a \u7684\u3002 \u2018\u2018\u73b0\u5728\u4f60\u867d\u7136\u53d7\u7f6a\uff0c\u53ef\u662f\u53ea\u8981\u4f60\u80c3\u5e2e\u6211\u2014\u70b9\u5fd9\uff0c\u6211\u4fdd\u8bc1\u7edd\u4e2d\u518d\u96be\u4e3a\u4f60\uff0c\u4f60\u8fd8\u662f\u53ef\u4ee5\u53bb \u505a\u4f60\u7684\u9690\u571f\u2606\u3002\u201d \u4ed6\u5378\u4e0b\u4e86\u5939\u5c42\u7684\u6728\u677f\uff0c\u5c31\u6709\u2014\u4e2a\u4eba\u4ece\u91cc\u9762\u6389\u4e86\u4e0b\u6765\u3002 \u2014\u4e2a\u6d3b\u4eba\u3002 \u4f60\u7528\u4e0d\u7740\u68c0\u67e5\u4ed6\u7684\u8109\u640f\u547c\u5438\uff0c\u5c31\u53ef\u4ee5\u770b\u5f97\u51fa\u4ed6\u662f\u4e2a\u6d3b\u4eba\u3002 \u56e0\u4e3a\u4ed6\u6389\u4e0b\u6765\u7684\u65f6\u5019\uff0c\u5168\u8eab\u90fd\u5728\u52a8\uff0c\u52a8\u4f5c\u7684\u53d8\u5316\u8fd8\u5f88\u591a\uff0c \u8fd9\u4e2a\u4eba\u2014\u6389\u4e0b\u6765\uff0c\u91cc\u9762\u53c8\u6709\u4e2a\u4eba\u6389\u4e86\u4e0b\u6765\uff0c\u63a5\u7740\uff0c\u53c8\u6389\u4e0b\u4e86\u2014\u4e2a\u3002 \u9646\u5c0f\u51e4\u660e\u660e\u53ea\u85cf\u4e86\u4e00\u4e2a\u4eba\u5728\u91cc\u9762\uff0c\u600e\u4e48\u4f1a\u5ffd\u7136\u53d8\u6210\u4e86\u4e09\u4e2a\uff1f \u4e09\u4e2a\u4eba\u90fd\u662f\u6d3b\u7684\uff0c\u4e09\u4e2a\u4eba\u90fd\u5728\u52a8\uff0c\u52a8\u4f5c\u90fd\u5f88\u5feb\uff0c\u53d8\u5316\u90fd\u5f88\u591a\u3002 \u8f66\u5e95\u4e0b\u7684\u5730\u65b9\u672c\u4e0d\u5927\uff0c\u80fd\u6d3b\u52a8\u7684\u8303\u56f4\u66f4\u5c0f\uff0c\u9646\u5c0f\u51e4\u4e00\u4e2a\u4eba\u5728\u4e0b\u9762\uff0c\u5df2\u7ecf\u89c9\u5f97\u5f88\u5c40\u4fc3\uff0c\u4f55 \u51b5\u53c8\u591a\u4e86\u4e09\u4e2a\u4eba\u6324\u8fdb\u6765\u3002 \u4e00\u4e2a\u5b50\u4ed6\u5c31\u5df2\u7ecf\u8fde\u52a8\u90fd\u4e0d\u80fd\u52a8\u4e86\uff0c\u56e0\u4e3a\u8fd9\u4e09\u4e2a\u4eba\u5df2\u50cf\u662f\u4e09\u6761\u516b\u722a\u9c7c\uff0c\u538b\u5728\u4ed6\u8eab\u4e0a\uff0c\u7d27\u7d27 \u7684\u7f20\u4f4f\u4e86\u4ed6\uff0c\u4e94\u53ea\u624b\u540c\u65f6\u70b9\u5728\u4ed6\u7684\u7a74\u9053\u4e0a\u3002 \u4e09\u4e2a\u4eba\u4e3a\u4ec0\u4e48\u6709\u4e94\u53ea\u624b\uff1f\u662f\u4e0d\u662f\u56e0\u4e3a\u5176\u4e2d\u4e00\u4e2a\u4eba\u53ea\u6709\u4e00\u53ea\u624b\uff01 \u8fd9\u4e2a\u4e00\u53ea\u624b\u7684\u4eba\u96be\u9053\u662f\u6d77\u5947\u9614\uff1f \u9646\u5c0f\u51e4\u751a\u81f3\u8fde\u4ed6\u4eec\u7684\u8138\u90fd\u6ca1\u6709\u770b\u89c1\uff0c\u5c31\u5df2\u88ab\u63d0\u4e86\u8d77\u6765\uff0c\u91cd\u91cd\u7684\u6454\u5728\u8f66\u53a2\u91cc\uff0c\u5c31\u50cf\u662f\u4e00\u6761 \u6b7b\u9c7c\u88ab\u6454\u4eba\u4e86\u6cb9\u9505\u3002 \u7b2c\u5341\u4e03\u7ae0\u3000\u9047\u88ad\u906d\u4fd8\u864f>> \u53e4\u9f99\u300a\u9646\u5c0f\u51e4\u7cfb\u5217\xb7\u5e7d\u7075\u5c71\u5e84\u300b \u7b2c\u5341\u4e03\u7ae0\u3000\u9047\u88ad\u906d\u4fd8\u864f \u5065\u9a6c\u957f\u5636\uff0c\u5411\u524d\u6025\u5954\u3002 \u4e09\u4e2a\u4eba\u90fd\u5df2\u5750\u4e0b\u6765\uff0c\u51b7\u51b7\u7684\u770b\u7740\u9646\u5c0f\u51e4\uff0c\u4e00\u4e2a\u662f\u9ad8\u6d9b\uff0c\u4e00\u4e2a\u662f\u6d77\u5947\u9614\u3002 \u7b2c\u4e8c\u4e2a\u4eba\u5374\u4e0d\u662f\u8868\u54e5\uff0c\u662f\u6756\u94c1\u5fc3\u3002 \u8f66\u5e95\u7684\u5939\u5c42\u4e2d\u672c\u6765\u660e\u660e\u53ea\u6709\u8868\u54e5\u4e00\u4e2a\u4eba\u7684\uff0c\u73b0\u5728\u53cd\u800c\u504f\u504f\u5c11\u4e86\u4ed6\u4e00\u4e2a\u3002\u4ed6\u7684\u4eba\u5230\u54ea\u91cc\u53bb \u4e86\uff1f \u8fd9\u4e09\u4e2a\u4eba\u662f\u600e\u4e48\u6765\u7684\uff1f\u5728\u524d\u9762\u8d76\u8f66\u7684\u662f\u8c01\uff1f\u662f\u4e0d\u662f\u90a3\u4e2a\u672c\u6765\u5e94\u8be5\u5728\u4e70\u9152\u7684\u8f66\u592b\uff1f \u9646\u5c0f\u51e4\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u60f3\u8bf4\u8bdd\uff0c\u5374\u8bf4\u4e0d\u51fa\u3002 \u4ed6\u4eec\u70b9\u7a74\u7684\u624b\u6cd5\u5f88\u91cd\uff0c\u4ed6\u8138\u4e0a\u7684\u808c\u8089\u90fd\u5df2\u50f5\u786c\u9ebb\u6728\uff0c\u975e\u4f46\u8bf4\u4e0d\u51fa\u8bdd\uff0c\u8fde\u7b11\u90fd\u7b11\u4e0d\u51fa\u3002 \u4ed6\u4eec\u663e\u7136\u5e76\u4e0d\u60f3\u542c\u4ed6\u8bf4\u8bdd\uff0c\u4e5f\u4e0d\u60f3\u770b\u4ed6\u7b11\uff0c\u53ef\u662f\u7b49\u4ed6\u4eec\u8981\u4ed6\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u4ed6\u60f3\u4e0d\u8bf4\u90fd\u4e0d \u884c\u3002 \u675c\u94c1\u5fc3\u7684\u624b\u5f20\u5f00\uff0c\u53c8\u63e1\u7d27\uff0c\u6307\u8282\u53d1\u51fa\u4e00\u8fde\u4e32\u7206\u7af9\u822c\u7684\u54cd\u58f0\u3002 \u9ad8\u6d9b\u770b\u7740\u4ed6\u7684\u624b\uff0c\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u4f60\u505a\u5211\u5802\u7684\u5802\u4e3b\uff0c\u4e00\u5171\u505a\u4e86\u591a\u5c11\u5e74\uff1f\u201d \u675c\u94c1\u5fc3\u9053\uff1a\u201c\u5341\u4e5d\u5e74\u3002\u201d \u9ad8\u6d9b\u9053\uff1a\u201c\u5728\u4f60\u8fd9\u53cc\u624b\u4e0b\u9762\uff0c\u6709\u6ca1\u6709\u6562\u4e0d\u8bf4\u5b9e\u8bdd\u7684\uff1f\u201d \u675c\u94c1\u5fc3\u9053\uff1a\u201c\u6ca1\u6709\u3002\u201d \u9ad8\u6d9b\u9053\uff1a\u201c\u636e\u8bf4\u4f60\u672c\u6765\u6709\u5f88\u591a\u6b21\u673a\u4f1a\uff0c\u53ef\u4ee5\u505a\u603b\u98d8\u628a\u5b50\u7684\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u5e72\uff1f\u201d \u675c\u94c1\u5fc3\u9053\uff1a\u201c\u56e0\u4e3a\u5211\u5802\u6709\u8da3\u3002\u201d \u9ad8\u6d9b\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u559c\u6b22\u770b\u522b\u4eba\u53d7\u7f6a\uff1f\u201d\u675c\u94c1\u5fc3\u9053\uff1a\u201c\u4e0d\u9519\u3002\u201d \u9ad8\u6d9b\u7b11\u4e86\uff0c\u6d77\u5947\u9614\u4e5f\u7b11\u4e86\uff0c\u4e24\u4e2a\u4eba\u7684\u7b11\u58f0\u5c31\u50cf\u751f\u4e86\u9508\u7684\u94c1\u5668\u6469\u64e6\uff0c\u4ee4\u4eba\u542c\u5f97\u7259\u6839\u53d1\u8f6f\u3002 \u6d77\u5947\u9614\u7b11\u9053\uff1a\u201c\u6211\u5012\u771f\u60f3\u770b\u770b\u4ed6\u5f53\u5e74\u7684\u624b\u6bb5\u3002\u201d \u9ad8\u6d9b\u9053\uff1a\u201c\u4f60\u9a6c\u4e0a\u5c31\u4f1a\u770b\u5230\u7684\u3002\u201d \u9ad8\u6d9b\u70b9\u70b9\u5934\u3002 \u6d77\u5947\u9614\u9053\uff1a\u201c\u636e\u8bf4\u6614\u5e74\u4e09\u5341\u516d\u5be8\u91cc\u53db\u5f92\uff0c\u5b81\u53ef\u4e0b\u6cb9\u9505\uff0c\u4e5f\u4e0d\u613f\u8fdb\u4ed6\u7684\u5211\u5802\u3002\u201d \u9ad8\u6d9b\u9053\uff1a\u201c\u4e00\u70b9\u4e5f\u4e0d\u9519\u3002\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u4ed6\u662f\u4e0d\u662f\u6709\u5957\u5f88\u7279\u522b\u7684\u6cd5\u5b50\u5bf9\u4ed8\u53db\u5f92\uff1f\u201d \u9ad8\u6d9b\u9634\u4fa7\u4fa7\u7b11\u9053\uff1a\u201c\u4e0d\u4f46\u7279\u522b\uff0c\u800c\u4e14\u6709\u8da3\u3002\u201d \u9646\u5c0f\u51e4\u95ed\u4e0a\u773c\u775b\uff0c\u53ea\u6068\u4e0d\u5f97\u5c06\u8033\u6735\u4e5f\u585e\u4f4f\uff0c\u8fd9\u4e9b\u8bdd\u542c\u6765\u5b9e\u5728\u6bd4\u4eba\u5f88\u4e0d\u6109\u5feb\uff0c\u5374\u53c8\u504f\u504f\u4e0d \u662f\u5047\u8bdd\u3002 \u9ad8\u6d9b\u5ffd\u53c8\u50cf\u5531\u6b4c\u4e00\u6837\u5531\u7740\u9053\uff1a\u201c\u5c06\u5165\u5211\u5802\uff0c\u4f24\u5fc3\u65ad\u80a0\uff0c\u5165\u4e86\u5211\u5802\uff0c\u558a\u7239\u558a\u5a18\u3002\u201d \u6d77\u5947\u9614\u7728\u7740\u773c\uff0c\u6545\u610f\u95ee\u9053\uff1a\u201c\u51fa\u4e86\u5211\u5802\u5462\uff1f\u201d \u9ad8\u6d9b\u9053\uff1a\u201c\u51fa\u4e86\u5211\u5802\uff0c\u5df2\u89c1\u960e\u738b\u3002\u201d\u675c\u94c1\u5fc3\u51b7\u51b7\u9053\uff1a\u201c\u4eba\u4e86\u5211\u5802\uff0c\u5c31\u5df2\u5982\u89c1\u960e\u738b\u4e86\u3002\u201d \u9ad8\u6d9b\u9053\uff1a\u201c\u5211\u5802\u91cc\u4e5f\u6709\u960e\u738b\uff1f\u201d \u6756\u94c1\u5fc3\u9053\uff1a\u201c\u6211\u5c31\u662f\u960e\u738b\u3002\u201d \u8f66\u7a97\u5916\u5ffd\u7136\u53d8\u5f97\u4e00\u7247\u6f06\u9ed1\uff0c\u8fde\u661f\u5149\u6708\u8272\u90fd\u5df2\u770b\u4e0d\u89c1\uff0c\u8f66\u58f0\u9686\u9686\uff0c\u54cd\u5f97\u9707\u8033\uff0c\u9a6c\u8f66\u7adf\u4f3c\u5df2 \u9a76\u5165\u4e86\u4e00\u4e2a\u5e7d\u6df1\u7684\u5c71\u6d1e\uff0c\u5728\u6d1e\u4e2d\u53c8\u8d70\u4e86\u6bb5\u8def\u624d\u505c\u4e0b\u3002 \u9ad8\u6d9b\u957f\u957f\u5410\u51fa\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u5230\u4e86\u3002\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u8fd9\u91cc\u5c31\u662f\u9ed1\u5fc3\u8001\u675c\u7684\u5211\u5802\uff1f\u201d\u9ad8\u6d9b\u5403\u5403\u7684\u7b11\u9053\uff1a\u201c\u8fd9\u91cc\u4e5f\u5c31\u662f\u960e\u738b\u8001\u5b50\u7684 \u68ee\u7f57\u6bbf\u3002\u201d \u4ed6\u4eec\u53c8\u5c06\u9646\u5c0f\u51e4\u4ece\u8f66\u53a2\u91cc\u62ce\u4e86\u51fa\u6765\uff0c\u5c31\u50cf\u662f\u62ce\u7740\u53e3\u7834\u9ebb\u888b\u4e00\u6837\uff0c\u65e2\u4e0d\u5c0f\u5fc3\u3002\u4e5f\u4e0d\u5728\u4e4e\uff0c \u4e00\u4e0b\u4e88\u649e\u4e0a\u8f66\u95e8\uff0c\u4e00\u4e0b\u5b50\u53c8\u649e\u4e0a\u5c71\u58c1\uff0c\u649e\u5f97\u9646\u5c0f\u51e4\u8111\u888b\u53d1\u6655\uff0c\u8fde\u9aa8\u5934\u90fd\u5feb\u6563\u4e86\u3002 \u9ad8\u6d9b\u6545\u610f\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u624b\u91cc\u94a9\u7740\u7684\u662f\u4e2a\u6d3b\u4eba\uff0c\u4e0d\u662f\u7834\u9ebb\u888b\uff0c\u4f60\u600e\u4e48\u4e0d\u5c0f\u5fc3\u4e00 \u70b9\uff1f\u201d\u6d77\u5947\u9614\u9053\uff1a\u201c\u6211\u770b\u4e0d\u89c1\u3002\u201d \u8fd9\u5012\u4e5f\u4e0d\u662f\u5047\u8bdd\uff0c\u5c71\u6d1e\u91cc\u5b9e\u5728\u592a\u9ed1\uff0c\u7b80\u76f4\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\u3002 \u4ed6\u4eec\u53c8\u5f80\u524d\u8d70\u4e86\u4e00\u6bb5\uff0c\u8d8a\u8d70\u8d8a\u7a84\uff0c\u88ab\u649e\u7684\u673a\u4f1a\u4e5f\u66f4\u591a\u3002 \u73b0\u5728\u8fde\u9646\u5c0f\u51e4\u81ea\u5df1\u90fd\u89c9\u5f97\u81ea\u5df1\u5df2\u53d8\u5f97\u50cf\u662f\u53e3\u7834\u9ebb\u888b\u4e86\u3002 \u5e78\u597d\u5c31\u5728\u8fd9\u65f6\uff0c\u524d\u9762\u5c71\u58c1\u4e0a\u201c\u683c\u683c\u201d\u7684\u4f5c\u54cd\uff0c\u5ffd\u7136\u6709\u4e00\u5757\u7ffb\u4e86\u8d77\u6765\uff0c\u9732\u51fa\u4e2a\u6d1e\u7a74\uff0c\u91cc\u9762 \u5c45\u7136\u6709\u5149\u3002 \u4e0d\u4f46\u6709\u5149\uff0c\u8fd8\u6709\u684c\u6905\u3002 \u684c\u4e0a\u6446\u7740\u5bf9\u6b7b\u4eba\u7075\u5802\u91cc\u7528\u7684\u81ea\u8721\u70db\uff0c\u5df2\u7ecf\u88ab\u71c3\u6389\u4e86\u2014\u5927 \u70db\u706b\u95ea\u70c1\uff0c\u98ce\u662f\u4ece\u6d1e\u707e\u4e0a\u4e00\u6761\u88c2\u9699\u4e2d\u5439\u8fdb\u6765\u7684\uff0c\u5c31\u597d\u50cf\u7279\u5730\u4e3a\u8fd9\u91cc\u9020\u51fa\u7684\u901a\u98ce\u53e3\u3002 \u6d77\u5947\u9614\u968f\u968f\u4fbf\u4fbf\u7684\u5c06\u9646\u5c0f\u51e4\u5f80\u684c\u5b50\u524d\u9762\u4e00\u6454\uff0c\u53f9\u606f\u7740\u9053\uff1a\u201c\u8fd9\u771f\u662f\u4e2a\u597d\u5730\u65b9\u3002\u201d \u9ad8\u6d9b\u9053\uff1a\u201c\u5c31\u7b97\u6709\u5341\u4e07\u4eba\u5728\u9644\u8fd1\u627e\u4e0a\u4e8c\u5e74\u516d\u4e2a\u6708\uff0c\u4e5f\u2014\u5b9a\u627e\u4e0d\u5230\u8fd9\u91cc\u9762\u6765\u3002\u201d \u6d77\u5947\u9614\u7528\u94a9\u5b50\u6572\u4e86\u6572\u9646\u5c0f\u51e4\u7684\u5934\uff0c\u9053\uff1a\u201c\u82e5\u662f\u627e\u4e0d\u5230\uff0c\u8c01\u6765\u6551\u4ed6\uff1f\u201d \u9ad8\u6d9b\u7b11\u9053\uff1a\u201c\u4ed6\u5c31\u7b97\u771f\u7684\u558a\u7239\u53eb\u5a18\uff0c\u4e5f\u6ca1\u6709\u4eba\u4f1a\u6765\u6551\u4ed6\u7684\u3002\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u90a3\u4e48\u4ed6\u5c82\u975e\u5df2\u6b7b\u5b9a\u4e86\uff1f\u201d \u675c\u94c1\u5fc3\u9053\uff1a\u201c\u4ed6\u4e0d\u4f1a\u6b7b\u5f97\u592a\u5feb\u3002\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u675c\u94c1\u5fc3\u51b7\u51b7\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u4e00\u5b9a\u4f1a\u8ba9\u4ed6\u6162\u6162\u7684\u6b7b\uff0c\u5f88\u6162\u5f88\u6162\u3002\u201d \u6d77\u5947\u9614\u9053\uff1a\u201c\u4ed6\u60f3\u6b7b\u5feb\u4e00\u70b9\u90fd\u4e0d\u884c\uff1f\u201d \u675c\u94c1\u5fc3\u9053\uff1a\u201c\u4e0d\u884c\u3002\u201d \u6d77\u5947\u9614\u7b11\u4e86\uff0c\u53d1\u73b0\u9ad8\u6d9b\u6b63\u4f4e\u7740\u5934\uff0c\u597d\u50cf\u6b63\u5728\u7814\u7a76\u9646\u5c0f\u51e4\u8eab\u4f53\u7684\u6784\u9020\uff0c\u5c31\u95ee\u9053\uff1a\u201c\u82e5\u662f\u7531 \u4f60\u52a8\u624b\u4e86\u4f60\u51c6\u5907\u4ece\u54ea\u91cc\u5f00\u5200\uff1f\u201d \u9ad8\u6d9b\u62cd\u4e86\u62cd\u9646\u5c0f\u51e4\u7684\u624b\uff0c\u9053\uff1a\u201c\u5f53\u7136\u662f\u4ece\u8fd9\u4e24\u6839\u5b9d\u8d1d\u624b\u6307\u624b\uff0c\uff0c \u6d77\u5947\u9614\u9053\uff1a\u201c\u82e5\u662f\u6211\uff0c\u5c31\u5148\u62d4\u4ed6\u7684\u4e24\u6761\u7709\u6bdb\u3002\u201d \u9ad8\u6d9b\u9053\uff1a\u201c\u54ea\u4e24\u6761\uff1f\u201d\u6d3b\u5947\u9614\u9053\uff1a\u201c\u5f53\u7136\u662f\u957f\u5728\u5634\u4e0a\u7684\u90a3\u4e24\u6761\u3002\u201d \u4e24\u4e2a\u4eba\u8d8a\u8bf4\u8d8a\u5f97\u610f\uff0c\u5c31\u50cf\u662f\u5c60\u592b\u5728\u8c08\u8bba\u7740\u4e00\u6761\u5f85\u5bb0\u7684\u7f94 \u9646\u5c0f\u51e4\u4e00\u5411\u662f\u4e2a\u5f88\u770b\u5f97\u5f00\u7684\u4eba\uff0c\u4e5f\u5f88\u6c89\u5f97\u4f0e\u6c14\uff0c\u53ef\u662f\u73b0\u5728\u5fc3\u91cc\u7684\u6ecb\u5473\uff0c\u5374\u597d\u50cf\u6574\u4e2a\u4eba\u90fd \u5df2\u5728\u6cb9\u9505\u91cc\u3002 \u770b\u8d77\u6765\u4ed6\u7684\u786e\u5df2\u6beb\u65e0\u5e0c\u671b\uff0c\u80fd\u591f\u5feb\u70b9\u6b7b\uff0c\u5df2\u7ecf\u662f\u8fd0\u6c14\u3002 \u8c01\u77e5\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u5916\u9762\u7684\u9ed1\u6697\u4e2d\u7a81\u7136\u54cd\u8d77\u4e86\u2014\u58f0\u51b7\u7b11\u3002 \u201c\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u9ad8\u6d9b\u3001\u6d77\u5947\u9614\u3001\u6756\u94c1\u5fc3\uff0c\u4e8c\u4e2a\u4eba\u540c\u65f6\u7a9c\u4e86\u51fa\u53bb\u3002 \u4e8c\u4e2a\u4eba\u90fd\u662f\u6b66\u6797\u4e2d\u7684\u4e00\u6d41\u9ad8\u624b\uff0c\u4e0d\u4f46\u53cd\u5e94\u5feb\uff0c\u52a8\u4f5c\u5feb\uff0c\u800c\u4e14\u8eab\u7ecf\u767e\u6218\uff0c\u80fd\u6321\u5f97\u4f4f\u4ed6\u4eec\u8054 \u624b\u4e00\u51fb\u7684\u4eba\uff0c\u5e76\u6ca1\u6709\u51e0\u4e2a\u3002 \u5916\u9762\u6765\u7684\u4eff\u4f5b\u53ea\u6709\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4e2a\u4eba\u7b80\u76f4\u5c31\u50cf\u662f\u6765\u9001\u6b7b\u7684\u3002 \u4ed6\u4eec\u4e00\u7a9c\u51fa\u53bb\uff0c\u5c31\u91c7\u53d6\u4e86\u5305\u6284\u4e4b\u52bf\uff0c\u65e0\u8bba\u6765\u7684\u8fd9\u4eba\u662f\u8c01\uff0c\u4ed6\u4eec\u90fd\u7edd\u4e0d\u4f1a\u8ba9\u4ed6\u518d\u6d3b\u7740\u8d70\u51fa \u53bb\u3002 \u6d77\u5947\u9614\u8180\u608d\u51f6\u731b\uff0c\u624b\u4e0a\u7684\u94c1\u94a9\u91cd\u662f\u4ef6\u6781\u9738\u9053\u7684\u6b66\u5668\uff0c\u4ee5\u4e94\u4e01\u5f00\u5c71\u4e4b\u529b\uff0c\u62a2\u5728\u6700\u5148\u3002 \u675c\u94c1\u5fc3\u5355\u638c\u62a4\u80f8\uff0c\u53f3\u62f3\u5f00\u8def\uff0c\u7d27\u8d34\u5728\u4ed6\u8eab\u540e\u3002 \u53c8\u662f\u4e00\u58f0\u51b7\u7b11\uff0c\u9ed1\u6697\u4e2d\u7a81\u7136\u6709\u5251\u5149\u4e00\u95ea\uff0c\u5c31\u50cf\u662f\u96f7\u9706\u9707\u6012\uff0c\u95ea\u7535\u751f\u5a01\uff0c\u5374\u6bd4\u95ea\u7535\u66f4\u5feb\uff0c \u66f4\u53ef\u6015\u3002 \u53ea\u542c\u201c\u53ee\u201d\u7684\u4e00\u54cd\uff0c\u4e00\u67c4\u94c1\u94a9\u6253\u4e0a\u77f3\u58c1\uff0c\u706b\u661f\u56db\u6e85\uff0c\u94c1\u94a9\u4e0a\u8fd8\u5e26\u7740\u4e00\u6761\u65ad\u81c2\u3002 \u675c\u94c1\u5fc3\u5df2\u4ef0\u9762\u800c\u5012\uff0c\u4e00\u80a1\u9c9c\u8840\uff0c\u6cc9\u6c34\u822c\u4ece\u54bd\u5589\u95f4\u6d8c\u51fa\u3002 \u4e24\u4e2a\u4eba\u8fde\u60e8\u547c\u58f0\u90fd\u6ca1\u6709\u53d1\u51fa\uff0c\u5c31\u5df2\u6c14\u7edd\u3002 \u597d\u5feb\u7684\u5251\uff01 \u5251\u950b\u8fd8\u5728\u9ed1\u6697\u4e2d\u95ea\u7740\u5149\uff0c\u95ea\u52a8\u7684\u5251\u5149\u4e2d\uff0c\u4eff\u4f5b\u6709\u6761\u4eba\u5f71\u3002 \u9ad8\u6d9b\u770b\u89c1\u4e86\u8fd9\u4e2a\u4eba\uff0c\u4e00\u6b65\u6b65\u5411\u540e\u9000\u3002 \u4ed6\u7684\u8138\u5df2\u5b8c\u5168\u626d\u66f2\uff0c\u5c31\u597d\u50cf\u5ffd\u7136\u770b\u89c1\u4e86\u5389\u9b3c\u51fa\u73b0\uff1b\u9000\u51fa\u51e0\u6b65\uff0c\u4e00\u4ea4\u8dcc\u5728\u5730\u4e0a\uff0c\u773c\u6cea\u3001\u9f3b \u6d95\u3001\u53e3\u6c34\u3001\u5927\u5c0f\u4fbf\u4e00\u8d77\u6d41\u4e86\u51fa\u6765\uff0c\u6574\u4e2a\u4eba\u90fd\u8f6f\u6210\u4e86\u4e00\u644a\u6ce5\uff0c\u7adf\u6d3b\u6d3b\u88ab\u5413\u6b7b\u3002 \u8c01\u80fd\u8ba9\u4ed6\u6015\u5f97\u8fd9\u4e48\u5389\u5bb3\uff1f \u8c01\u80fd\u6709\u8fd9\u4e48\u5feb\u7684\u5251\uff1f \u897f\u95e8\u5439\u96ea\uff1f \u2014\u4e2a\u4eba\u6162\u6162\u4ece\u9ed1\u6697\u4e2d\u8d70\u51fa\u6765\uff0c\u7a7f\u7740\u8eab\u7070\u5e03\u957f\u888d\uff0c\u6234\u7740\u9876\u7bd3\u5b50\u822c\u7684\u7af9\u7b20\u3002 \u4e0d\u662f\u897f\u95e8\u5439\u96ea\uff0c\u662f\u8001\u5200\u628a\u5b50\uff01 \u9646\u5c0f\u51e4\u7684\u4eba\u521a\u4ece\u6cb9\u9505\u91cc\u635e\u51fa\u6765\uff0c\u53c8\u6389\u8fdb\u51b0\u7a96\u91cc\uff0c\u5168\u8eab\u90fd\u5df2\u51b0\u51b7\u3002 \u4ed6\u4e00\u5fc3\u60f3\u6293\u4f4f\u8fd9\u4e2a\u4eba\u7684\u81f4\u547d\u8981\u5bb3\uff0c\u8fd9\u4e2a\u4eba\u5f53\u7136\u4e5f\u60f3\u8981\u4ed6\u7684\u547d\uff01 \u5c31\u7b97\u4ed6\u5b81\u53ef\u8fdb\u6cb9\u9505\uff0c\u4e5f\u4e0d\u613f\u5165\u5211\u5802\uff0c\u53ef\u662f\u73b0\u5728\u4ed6\u5b81\u53ef\u8fdb\u5211\u5802\uff0c\u4e5f\u4e0d\u613f\u843d\u5165\u8001\u5200\u628a\u5b50\u624b \u91cc\u3002 \u8001\u5200\u628a\u4e8e\u7684\u58f0\u97f3\u5374\u5f88\u6e29\u548c\uff0c\u5c45\u7136\u5728\u95ee\uff1a\u201c\u4ed6\u4eec\u6709\u6ca1\u6709\u5bf9\u4f60\u65e0\u793c\uff1f\u201d \u9646\u5c0f\u51e4\u82e6\u7b11\u3002 \u521a\u624d\u88ab\u649e\u4e86\u90a3\u4e48\u591a\u4e0b\uff0c\u4ed6\u8840\u8109\u603b\u7b97\u88ab\u649e\u5f97\u6bd4\u8f83\u7545\u901a\u4e86\uff0c\u5df2\u7ecf\u80fd\u8bf4\u5f97\u51fa\u8bdd\u3002 \u53ef\u662f\u6b64\u65f6\u6b64\u523b\uff0c\u4ed6\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\uff1f \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u6211\u90fd\u4e0d\u80fd\u8ba9\u4f60\u53d7\u4ed6\u4eec\u7684\u59d4\u5c48\uff0c\u4ed6\u4eec\u8fd8\u4e0d\u914d\u3002\u201d \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u6211\u73b0\u5728\u624d\u77e5\u9053\uff0c\u4f60\u65e9\u5c31\u51c6\u5907\u5728\u4e8b\u6210\u4e4b\u540e\u6740\u4e86\u4ed6\u4eec\u7684\uff0c\u8001\u5200\u628a\u5b50\u5e76\u4e0d \u5426\u8ba4\uff0c\u9053\uff1a\u201c\u65a9\u5c3d\u6740\u7edd\uff0c\u8fde\u4e00\u4e2a\u90fd\u4e0d\u7559\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4e5f\u8bb8\u6ee1\u7fe0\u697c\u90a3\u5730\u7a96\uff0c\u672c\u6765\u5c31\u662f\u4ed6\u4eec\u7684\u846c\u8eab\u4e4b\u5730\u3002\u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u51cc\u98ce\u5c71\u5e84 \u7684\u5730\u7a96\u4e5f\u4e00\u6837aH \u6f6e\u6e7f\u9634\u6697\u7684\u5730\u7a96\uff0c\u547c\u53f7\u7740\u60f3\u9003\u547d\u7684\u4eba\uff0c\u8840\u8089\u6a21\u7cca\u7684\u5c38\u4f53\u3002 \u9646\u5c0f\u51e4\u5fcd\u4f4f\u4e86\u5455\u5410\uff0c\u9053\uff1a\u201c\u4ed6\u4eec\u672c\u5c31\u8981\u6b7b\u7684\uff0c\u867d\u7136\u6ca1\u6709\u6740\u6b7b\u94c1\u80a9\u90a3\u4e9b\u4eba\uff0c\u4f60\u7684\u8ba1\u5212\u8fd8\u662f \u6ca1\u6709\u5931\u8d25\u3002\u201d \u8001\u5200\u628a\u5b50\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u65e9\u5c31\u8bf4\u8fc7\uff0c\u6211\u7edd\u4e0d\u4f1a\u5931\u8d25\u3002\u201d \u9646\u5c0f\u51e4\u4e5f\u53ea\u6709\u627f\u8ba4\uff0c\u73b0\u5728\u770b\u8d77\u6765\uff0c\u6700\u540e\u7684\u80dc\u5229\u7684\u786e\u5c5e\u4e8e\u4ed6\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u8fd9\u5c31\u597d\u50cf\u653b\u57ce\u4e00\u6837\uff0c\u5c31\u7b97\u4f60\u5df2\u653b\u7834\u4e86\u4e5d\u9053\u57ce\uff0c\u5916\u9762\u867d\u7136\u5df2\u8840\u67d3\u6210\u6e20\uff0c\u6211 \u5374\u8fd8\u662f\u592a\u592a\u5e73\u5e73\u7684\u9ad8\u5367\u5728\u57ce\u91cc\u201d \u4ed6\u5fae\u7b11\u7740\u9053\uff1a\u201c\u56e0\u4e3a\u6211\u7684\u601d\u8651\u6bd4\u4f60\u66f4\u5468\u5bc6\uff0c\u4f60\u80fd\u653b\u7834\u4e5d\u9053\u57ce\uff0c\u6211\u5374\u65e9\u5df3\u5efa\u7acb\u4e86\u7b2c\u5341\u9053\uff0c \u5230\u4e86\u8fd9\u9053\u57ce\u5916\uff0c\u4f60\u5df2\u7cbe\u75b2\u529b\u7aed\uff0c\u5012\u4e0b\u53bb\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u7b97\u51c6\u4e86\u6211\u6ca1\u6cd5\u5b50\u63ed\u7a7f\u4f60\u7684\u771f\u9762\u76ee\uff1f\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u73b0\u5728\u4e16\u4e0a\u5df2\u6ca1\u6709\u4e00\u4e2a\u4eba\u80fd\u4e3a\u4f60\u4f5c\u8bc1\uff0c\u4f60\u8bf4\u7684\u8bdd\uff0c\u8fd8\u6709\u8c01\u76f8\u4fe1\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u8fd8\u6709\u4e00\u4e2a\u4eba\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u8c01\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u3002\u201d \u8001\u5200\u628a\u5b50\u5927\u7b11\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u53ea\u6709\u4f60\u81ea\u5df1\u77e5\u9053\u6211\u8bf4\u7684\u4e0d\u9519\uff0c\u6240\u4ee5\u4f60\u2014\u5b9a\u8981\u6740\u6211\u706d\u53e3\u3002\u201d \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u4f60\u5462\uff1f\u4f60\u81ea\u5df1\u662f\u4e0d\u662f\u5b8c\u5168\u7edd\u5bf9\u76f8\u4fe1\u4f60\u81ea\u5df1\u7684\u60f3\u6cd5\uff1f\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u2026\u2026 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u81ea\u5df1\u4e5f\u4e0d\u80fd\u7edd\u5bf9\u76f8\u4fe1\u7684\uff0c\u9664\u975e\u4f60\u80fd\u6458\u4e0b\u6211\u8fd9\u9876\u7af9\u7b20\u6765\uff0c\u4eb2\u773c\u770b \u89c1\u6211\u7684\u771f\u9762\u76ee\u3002\u201d \u9646\u5c0f\u51e4\u65e0\u6cd5\u5426\u8ba4\u3002 \u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u8fd8\u6709\u4ef6\u4e8b\u4f60\u9519\u4e86\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d\u8001\u5200\u628a\u5b50\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u60f3\u6740\u4f60\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u4e0d\u60f3\uff1f\u201d \u8001\u5200\u628a\u5b50\u53c8\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u6740\u4f60\uff1f\u4f60\u73b0\u5728\u8ddf\u6b7b\u4eba\u6709\u4ec0\u4e48\u4e24\u6837\uff1f\u201d \u4ed6\u5fae\u7b11\u7740\u8f6c\u8eab\uff0c\u65bd\u65bd\u7136\u8d70\u4e86\u51fa\u53bb\uff0f\u4e0d\u503c\u5f97\u6211\u6740\u7684\u4eba\uff0c\u6211\u7edd\u4e0d\u4f1a\u52a8\u624b\u7684\u3002\u201d \u9646\u5c0f\u51e4\u5fcd\u4e0d\u4f4f\u5927\u58f0\u9053\uff1a\u201c\u73b0\u5728\u4f60\u80fd\u4e0d\u80fd\u8ba9\u6211\u770b\u770b\u4f60\u7a76\u7adf\u662f\u8c01\uff1f\u201d \u8001\u5200\u628a\u5b50\u5934\u4e5f\u4e0d\u56de\uff0c\u9053\uff1a\u201c\u4e0d\u80fd\u3002\u201d \u70db\u5149\u95ea\u52a8\uff0c\u5df2\u5c06\u7184\u706d\u3002 \u8001\u5200\u628a\u5b50\u8d70\u4e86\uff0c\u5165\u53e3\u5916\u90a3\u5757\u5de8\u5927\u7684\u77f3\u58c1\uff0c\u4e5f\u5df2\u5bc6\u5bc6\u56ed\u8d77\u3002 \u5c31\u7b97\u9646\u5c0f\u51e4\u80fd\u591f\u81ea\u7531\u6d3b\u52a8\uff0c\u4e5f\u4e00\u5b9a\u6ca1\u6cd5\u5b50\u6d3b\u7740\u4ece\u8fd9\u91cc\u8d70\u51fa\u53bb\u3002 \u73b0\u5728\u8fd9\u5730\u65b9\u5c31\u597d\u50cf\u662f\u4e2a\u5bc6\u5c01\u7684\u7f50\u5b50\uff0c\u8fde\u4e00\u53ea\u82cd\u8747\u90fd\u98de\u4e0d\u51fa\u53bb\u3002 \u6211\u4e3a\u4ec0\u4e48\u8981\u6740\u4f60\uff0c\u73b0\u5728\u4f60\u8ddf\u4e00\u4e2a\u6b7b\u4eba\u6709\u4ec0\u4e48\u4e24\u6837\uff1f \u6ca1\u6709\u4e24\u6837\uff0c\u8fd9\u5bc6\u5c01\u7684\u7f50\u5b50\uff0c\u5c31\u662f\u4ed6\u7684\u575f\u5893\u3002 \u6bcf\u4e2a\u4eba\u8fdf\u65e9\u90fd\u8981\u8fdb\u575f\u5893\u7684\uff0c\u53ea\u4e0d\u8fc7\u6d3b\u751f\u751f\u7684\u5750\u5728\u575f\u5893\u91cc\u7b49\u6b7b\uff0c\u8fd8\u4e0d\u5982\u7d22\u6027\u65e9\u70b9\u6b7b\u4e86\u7684 \u597d\u3002 \u6700\u60b2\u54c0\u7684\u662f\u73b0\u5728\u4ed6\u8fde\u6b7b\u90fd\u6ca1\u6cd5\u5b50\u6b7b\u3002 \u70db\u6cea\u5df2\u5c06\u6d41\u5c3d\u4e86\uff0c\u4ed6\u7684\u751f\u547d\uff0c\u5c82\u975e\u4e5f\u6b63\u5982\u8fd9\u6839\u6b8b\u70db\uff01 \u76f4\u5230\u73b0\u5728\u4ed6\u624d\u53d1\u73b0\uff0c\u539f\u6765\u81ea\u5df1\u5e76\u4e0d\u662f\u4e2a\u65e0\u5f80\u4e0d\u80dc\uff0c\u65e0\u6240\u4e0d\u80fd\u7684\u8d85\u4eba\u3002 \u4ed6\u80fd\u4ece\u4ee5\u524d\u90a3\u4e9b\u5371\u673a\u4e2d\u8131\u8eab\uff0c\u4e5f\u8bb8\u53ea\u4e0d\u8fc7\u5168\u51ed\u4e00\u70b9\u8fd0\u6c14 \u53ef\u662f\u9047\u89c1\u8001\u5200\u628a\u5b50\u8fd9\u79cd\u53ef\u6015\u7684\u5bf9\u624b\u65f6\uff0c\u8fd0\u6c14\u5c31\u6ca1\u6709\u7528 \u6211\u77e5\u9053\u4f60\u81ea\u5df1\u4e5f\u4e0d\u80fd\u7edd\u5bf9\u76f8\u4fe1\u7684\uff0c\u9664\u975e\u4f60\u80fd\u4eb2\u773c\u770b\u89c1\u6211\u7684\u771f\u9762\u76ee\u3002 \u73b0\u5728\u4ed6\u6c38\u8fdc\u770b\u4e0d\u5230\u4e86\uff0c\u4ed6\u5df2\u53ea\u6709\u5e26\u7740\u8fd9\u7591\u95ee\u4e0b\u5730\u72f1\u53bb\u3002\u4e3a\u4ec0\u4e48\u8981\u4e0b\u5730\u72f1\uff1f \u8fde\u81ea\u5df1\u90fd\u4e0d\u80fd\u76f8\u4fe1\u81ea\u5df1\u7684\u4eba\uff0c\u4e0d\u4e0b\u5730\u72f1\u8fd8\u80fd\u5230\u54ea\u91cc\u53bb\uff1f \u70db\u5149\u706d\u4e86\uff0c\u4ed6\u5374\u8fd8\u6d3b\u7740\u3002 \u4e16\u4e0a\u552f\u4e00\u6bd4\u6d3b\u751f\u751f\u5750\u5728\u575f\u5893\u4e2d\u7b49\u6b7b\u66f4\u7cdf\u7684\u4e8b\uff0c\u5c31\u662f\u6d3b\u6d3b\u7684\u5750\u5728\u9ed1\u6697\u91cc\u7b49\u6b7b\u3002 \u4ed6\u60f3\u4e5f\u60f3\u8d77\u4e86\u5f88\u591a\u4eba\uff0c\u751a\u81f3\u8fd8\u60f3\u8d77\u4e86\u8f66\u7a97\u4e2d\u90a3\u53cc\u53d1\u4eae\u7684\u773c\u775b\u3002 \u6b64\u65f6\u6b64\u523b\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8fd8\u4f1a\u60f3\u5230\u5979\uff1f \u96be\u9053\u8fd9\u4e2a\u6709\u4e00\u53cc\u53d1\u4eae\u773c\u775b\u7684\u8fc7\u8def\u5973\u4eba\uff0c\u548c\u4ed6\u4e5f\u6709\u67d0\u79cd\u5947\u5f02\u800c\u795e\u79d8\u7684\u5173\u7cfb\uff1f \u79d8\u5ba4\u4e2d\u5ffd\u7136\u53d8\u5f97\u5f88\u95f7\u70ed\uff0c\u4ed6\u5df2\u5f00\u59cb\u6d41\u6c57\uff0c\u4e00\u7c92\u7c92\u6c57\u73e0\uff0c\u5c31\u50cf\u662f\u8682\u8681\u822c\u5728\u4ed6\u8138\u4e0a\u722c\u8fc7\u3002 \u4ed6\u5ffd\u7136\u53d1\u73b0\u81ea\u5df1\u7684\u624b\u5df2\u7ecf\u80fd\u52a8\u4e86\u3002 \u4f60\u6709\u53ea\u5929\u4e0b\u65e0\u53cc\u7684\u624b\uff0c\u4f60\u8fd9\u4e24\u6839\u624b\u6307\uff0c\u5c31\u662f\u65e0\u4ef7\u73cd \u6bcf\u4e2a\u4eba\u90fd\u8fd9\u4e48\u6837\u8bf4\uff0c\u53ef\u662f\u73b0\u5728\u4ed6\u8fd9\u4e24\u6839\u624b\u6307\u552f\u4e00\u80fd\u505a\u7684\u4e8b\uff0c\u5c31\u662f\u7528\u529b\u634f\u4e00\u6444\u4ed6\u81ea\u5df1\u7684 \u817f\uff0c\u8ba9\u4ed6\u6e05\u9192\u6e05\u9192\uff0c\u4e0d\u8981\u603b\u4ee5\u4e3a\u81ea\u5df1\u4e86\u4e0d\u8d77\u3002 \u53ea\u4e0d\u8fc7\u6e05\u9192\u4e86\u53cd\u800c\u66f4\u75db\u82e6\u3002 \u201c\u5982\u679c\u80fd\u7761\u7740\u591a\u597d\u3002\u201d \u4e00\u89c9\u9192\u6765\uff0c\u53d1\u73b0\u81ea\u5df1\u5df2\u7ecf\u5728\u5730\u72f1\u91cc\uff0c\u5c82\u975e\u4e5f\u75db\u5feb\u5f97\u5f88\u3002 \u4ed6\u7761\u4e0d\u7740\u3002 \u968f\u7740\u9ed1\u6697\u548c\u95f7\u70ed\u800c\u6765\u7684\uff0c\u662f\u75b2\u5026\u548c\u9965\u6e34\uff0c\u5c24\u5176\u662f\u6e34\u66f4\u96be\u5fcd\u53d7\u3002 \u8fd9\u79cd\u7f6a\u8981\u53d7\u5230\u4f55\u65f6\u4e3a\u6b62\uff1f \u5230\u6b7b\u4e3a\u6b62\uff01 \u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u6b7b\uff1f \u4ed6\u5ffd\u7136\u5927\u58f0\u5531\u8d77\u6b4c\u6765\uff0c\u5531\u7684\u8fd8\u662f\u90a3\u51e0\u6761\u513f\u6b4c\uff1a \u201c\u59b9\u59b9\u62b1\u7740\u6ce5\u5a03\u5a03\uff0c \u8981\u5230\u82b1\u56ed\u53bb\u770b\u82b1\u2026\u2026\u201d \u9ec4\u91d1\u822c\u7684\u7ae5\u5e74\uff0c\u751c\u871c\u7684\u5f80\u4e8b\uff0c\u5c31\u8fde\u5f80\u65e5\u7684\u75db\u82eb\uff0c\u73b0\u5728\u90fd\u5df1\u53d8\u5f97\u5f88\u751c\u871c\u3002 \u539f\u6765\u751f\u547d\u7adf\u662f\u5982\u6b64\u53ef\u7231\uff0c\u4eba\u4eec\u4e3a\u4ec0\u4e48\u504f\u504f\u603b\u662f\u8981\u7b49\u5230\u5782\u6b7b\u65f6\u624d\u77e5\u73cd\u60dc\uff1f \u5ffd\u7136\u95f4\uff0c\u9ed1\u6697\u4e2d\u53d1\u51fa\u201c\u683c\u201d\u7684\u2014\u58f0\u54cd\uff0c\u90a3\u5757\u5de8\u5927\u7684\u5c71\u58c1\u5ffd\u7136\u7ffb\u8d77\u3002 \u706f\u5149\u7167\u5165\uff0c\u4e00\u5927\u7fa4\u4eba\u6d8c\u4e86\u8fdb\u6765\uff0c\u5176\u4e2d\u6709\u94c1\u80a9\u3001\u6709\u738b\u5341\u888b\u3001\u6709\u82b1\u6ee1\u697c\uff0c\u8d70\u5728\u6700\u524d\u9762\u7684\u2014\u4e2a \u767d\u53d1\u8001\u9053\u3002\u8d6b\u7136\u7adf\u662f\u6728\u9053\u5165\uff01 \u5728\u5782\u6b7b\u65f6\u7a81\u7136\u83b7\u6551\uff0c\u4e2d\u662f\u6700\u503c\u5f97\u6b22\u559c\u97f5\u4e8b\uff0c\u9646\u5c0f\u51e4\u5374\u5ffd\u7136\u89c9\u5f97\u4e00\u9635\u6012\u6c14\u2014\u6d8c\uff0c\u7adf\u6c14\u5f97\u6655 \u4e86\u8fc7\u53bb\u3002 \u56db\u6708\u5341\u4e94\uff0c\u5348\u540e\u3002 \u5c06\u8fd1\u9ec4\u660f\uff0c\u4e91\u623f\u4e2d\u6e05\u51c9\u5b89\u9759\uff0c\u5916\u9762\u7af9\u58f0\u5982\u6d9b\uff0c\u6b63\u662f\u6b66\u5f53\u638c\u95e8\u63a5\u5f85\u8d35\u5bbe\u7684\u542c\u7af9\u5c0f\u9662\u3002 \u8fd9\u6b21\u6765\u7684\u8d35\u5bbe\u5c31\u662f\u9646\u5c0f\u51e4\u3002 \u4ed6\u52a8\u4e5f\u4e0d\u52a8\u7684\u8eba\u5728\u5e8a\u4e0a\uff0c\u770b\u7740\u5c4b\u9876\uff0c\u770b\u6765\u4e5f\u8ddf\u4e00\u4e2a\u6b7b\u4eba\u6ca1\u4ec0\u4e48\u5206\u522b\u3002 \u201c\u82e5\u4e0d\u662f\u6728\u771f\u4eba\u60f3\u8d77\u540e\u5c71\u6709\u90a3\u4e48\u6837\u4e00\u4e2a\u6d1e\u7a9f\uff0c\u8fd9\u6b21\u4f60\u5c31\u6b7b\u5b9a\u4e86\u3002\u201d \u8bf4\u8bdd\u7684\u662f\u94c1\u80a9\uff0c\u201c\u90a3\u672c\u662f\u6614\u5e74\u6b66\u5f53\u5f1f\u5b50\u53bb\u9762\u58c1\u601d\u8fc7\u7684\u5730\u65b9\uff0c\u73b0\u5728\u4ed6\u4eec\u7684\u95e8\u89c4\u5df2\u4e0d\u5982\u6614\u65e5 \u4e25\u5389\uff0c\u90a3\u5730\u65b9\u5df2\u6709\u5f88\u4e45\u6ca1\u6709\u4eba\u53bb\u8fc7\uff0c\u8fd9\u6b21\u4f60\u5b9e\u5728\u662f\u8fd0\u6c14\u3002\u201d \u8fd0\u6c14\uff1f\u89c1\u9b3c\u7684\u8fd0\u6c14\uff01 \u201c\u4f46\u662f\u4f60\u4e5f\u4e0d\u80fd\u5b8c\u5168\u611f\u6fc0\u8fd0\u6c14\uff0c\u5e26\u6211\u4eec\u5230\u90a3\u91cc\u53bb\u627e\u4f60\u7684\uff0c\u603b\u662f\u6728\u771f\u4eba\u3002\u201d \u8fd9\u4f4d\u5c11\u6797\u9ad8\u50e7\u8bf4\u5f97\u5f88\u542b\u84c4\uff0c\u610f\u601d\u5374\u5f88\u660e\u663e\u3002 \u4ed6\u663e\u7136\u5df2\u4e0d\u518d\u6000\u7591\u6728\u9053\u4eba\u5c31\u662f\u8001\u5200\u628a\u5b50\uff0f\u5426\u5219\u4ed6\u4e3a\u4ec0\u4e48\u8981\u5e26\u6211\u4eec\u53bb\u6551\u4f60\uff1f\u201d \u522b\u4eba\u7684\u60f3\u6cd5\u5f53\u7136\u4e5f\u4e00\u6837\uff0c\u8fd9\u9053\u7406\u672c\u5c31\u548c\u201c\u4e00\u52a0\u4e00\u7b49\u4e8e\u4e8c\u201d\u540c\u6837\u7b80\u5355\u3002 \u6240\u4ee5\u6728\u9053\u4eba\u5c31\u53d8\u6210\u4e86\u6728\u771f\u4eba\u3002 \u4f46\u662f\u9646\u5c0f\u51e4\u5fc3\u91cc\u5374\u660e\u767d\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\u3002 \u6728\u9053\u4eba\u82e5\u6740\u4e86\u4ed6\u706d\u53e3\uff0c\u5927\u5bb6\u5c31\u7b97\u627e\u4e0d\u51fa\u8bc1\u636e\uff0c\u5fc3\u91cc\u4e5f\u5fc5\u5b9a\u96be\u514d\u6000\u7591\u3002 \u4f46\u662f\u73b0\u5728\u4ed6\u6551\u4e86\u9646\u5c0f\u51e4\u3002 \u90a3\u4e0d\u4f46\u8bc1\u660e\u4ed6\u7edd\u4e0d\u662f\u8001\u5200\u628a\u5b50\uff0c\u800c\u4e14\u8fd8\u53ef\u4ee5\u6362\u5f97\u5927\u5bb6\u5bf9\u4ed6\u7684\u611f\u6fc0\u548c\u5c0a\u656c\u3002 \u9646\u5c0f\u51e4\u53ea\u6709\u627f\u8ba4\uff0c\u8fd9\u7684\u786e\u662f\u4ed6\u5e73\u751f\u6240\u77e5\u9053\u7684\u6700\u72e1\u9ee0\u7f1c\u5bc6\u7684\u8ba1\u5212\uff0c\u6728\u9053\u4eba\u7684\u786e\u662f\u4ed6\u5e73\u751f\u6240 \u9047\u89c1\u8fc7\u7684\u6700\u53ef\u6015\u7684\u5bf9\u624b\u3002 \u8fd9\u4ef6\u4e8b\u65e0\u7591\u4e5f\u662f\u4ed6\u5e73\u751f\u6700\u5927\u7684\u632b\u6298\uff0c\u73b0\u5728\u4ed6\u5df2\u53ea\u6709\u8ba4\u8f93\u3002 \u4ed6\u5fc3\u91cc\u867d\u7136\u5f88\u660e\u767d\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u5374\u4e0d\u80fd\u8bf4\u51fa\u6765\uff0c\u56e0\u4e3a\u4ed6\u5c31\u7b97\u8bf4\u51fa\u6765\uff0c\u4e5f\u6ca1\u6709\u4eba\u4f1a\u76f8 \u4fe1\u3002 \u4ed6\u53ea\u95ee\u8fc7\u4e00\u53e5\u8bdd\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u4f1a\u77e5\u9053\u6211\u5df2\u9047\u9669\u7684\u3002\u201d \u201c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u6211\u4eec\u77e5\u9053\u4f60\u7edd\u4e0d\u4f1a\u65e0\u7f18\u65e0\u6545\u5931\u8e2a\u7684\uff0c\u6211\u4eec\u53c8\u5728\u6b66\u5f53\u540e\u5c71\u4e00\u4e2a\u9669\u5761\u4e0b\uff0c \u627e\u5230\u4e86\u90a3\u8f86\u9a6c\u8f66\uff0c\u8f66\u4e0a\u8fd8\u7559\u7740\u4f60\u4e00\u4ef6\u5916\u8863\uff0c\u8863\u895f\u5df2\u88ab\u6495\u7834\uff0c\u4e0a\u9762\u8fd8\u6709\u5728\u6ce5\u571f\u4e0a\u6323\u624e\u8fc7\u7684\u75d5 \u8ff9\u3002\u201d \u8fd9\u51e0\u70b9\u5df2\u8db3\u591f\u8bc1\u660e\u4ed6\u5df2\u6709\u4e86\u5371\u9669\uff0c\u6240\u4ee5\u4ed6\u8fde\u4e00\u53e5\u8bdd\u90fd\u6ca1\u6709\u518d\u8bf4\u3002 \u66ae\u8272\u6e10\u4e34\uff0c\u5916\u9762\u5ffd\u7136\u54cd\u8d77\u4e86\u6e05\u60a6\u7684\u949f\u58f0\u3002 \u201c\u4eca\u5929\u662f\u6728\u771f\u4eba\u6b63\u5f0f\u5373\u4f4d\u7684\u5927\u5178\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4f60\u90fd\u5e94\u8be5\u53bb\u9053\u8d3a\u7684\u3002\u201d \u770b\u7740\u4e00\u4e2a\u672c\u8be5\u53d7\u5230\u60e9\u7f5a\u7684\u4eba\uff0c\u53cd\u800c\u83b7\u5f97\u4e86\u8363\u8000\u548c\u6743\u529b\u4e2d\u8fd9\u79cd\u4e8b\u5f53\u7136\u4e0d\u4f1a\u8ba9\u4eba\u89c9\u5f97\u5f88\u597d\u53d7 \u7684\u3002 \u4f46\u4ed6\u5374\u8fd8\u662f\u4e0d\u80fd\u4e0d\u53bb\u3002 \u4ed6\u4e0d\u613f\u9003\u907f\u3002 \u4ed6\u8981\u8ba9\u6728\u9053\u4eba\u77e5\u9053\uff0c\u8fd9\u6b21\u632b\u8d25\u7684\u7ecf\u9a8c\u867d\u60e8\u75db\uff0c\u5374\u5e76\u6ca1\u6709\u5c06\u4ed6\u51fb\u5012\u3002 \u5c31\u7b97\u4ed6\u5df2\u975e\u8ba4\u8f93\u90a3\u91cc\u8ba4\u8f93\u3002 \u7a97\u5916\u98ce\u5439\u7af9\u53f6\uff0c\u591c\u8272\u5ffd\u7136\u95f4\u5c31\u5df2\u7b3c\u7f69\u5927\u5730\u3002 \u5927\u6bbf\u91cc\u706f\u706b\u8f89\u714c\u3002 \u6234\u7740\u7d2b\u91d1\u51a0\uff0c\u4f69\u7740\u4e03\u661f\u5251\u7684\u6728\u771f\u4eba\uff0c\u5728\u706f\u5149\u4e0b\u770b\u6765\uff0c\u66f4\u663e\u5f97\u5c0a\u4e25\u9ad8\u8d35\u3002 \u6614\u65e5\u90a3\u6e38\u620f\u98ce\u5c18\uff0c\u843d\u62d3\u4e0d\u7f81\u7684\u6728\u9053\u4eba\u6839\u672c\u5df2\u4e0d\u5b58\u5728\u4e86\u3002 \u6b64\u523b\u7ad9\u5728\u8fd9\u91cc\u7684\uff0c\u662f\u6b66\u5f53\u7684\u7b2c\u5341\u56db\u4ee3\u638c\u95e8\u6559\u4e3b\u6728\u771f\u4eba\uff0c\u662f\u7edd\u4e0d\u5bb9\u4efb\u4f55\u4eba\u8f7b\u6162\u7684\u3002 \u9646\u5c0f\u51e4\u5fc3\u91cc\u544a\u8bc9\u81ea\u5df1\uff0c\u4e00\u5b9a\u8981\u8bb0\u4f4f\u8fd9\u4e00\u70b9\u3002 \u7136\u540e\u4ed6\u5c31\u6574\u8083\u8863\u51a0\uff0c\u5927\u6b65\u8d70\u4e0a\u53bb\uff0c\u957f\u63d6\u5230\u5730\uff1a\u201c\u606d\u559c\u9053\u957f\u8363\u767b\u5927\u4f4d\uff0c\u9646\u5c0f\u51e4\u7279\u6765\u8d3a \u559c\u3002\u201d \u6728\u771f\u4eba\u5fae\u7b11\uff0c\u6276\u4f4f\u4e86\u4ed6\u7684\u81c2\uff0c\u9053\uff1a\u201c\u9646\u5927\u4fa0\u5343\u4e07\u4e0d\u53ef\u591a\u793c\u3002\u201d \u9646\u5c0f\u51e4\u4e5f\u5728\u5fae\u7b11\uff0c\u9053\uff1a\u201c\u9053\u957f\u5386\u5c3d\u8270\u96be\uff0c\u7ec8\u4e8e\u5982\u613f\u4ee5\u507f\uff0c\u9646\u5c0f\u51e4\u5374\u8fd8\u662f\u9646\u5c0f\u51e4\uff0c\u4e0d\u662f\u9646 \u5927\u4fa0\u3002\u201d \u4ed6\u7684\u6001\u5ea6\u867d\u606d\u8c28\u5ba2\u6c14\uff0c\u8a00\u8bcd\u4e2d\u5374\u5e26\u7740\u5c16\u9488\u822c\u7684\u8ba5\u523a\u3002 \u5c24\u5176\u662f\u201c\u5982\u613f\u4ee5\u507f\u201d\u56db\u4e2a\u5b87\u3002 \u4ed6\u5fcd\u4e0d\u4f4f\u8fd8\u662f\u8981\u6728\u771f\u4eba\u77e5\u9053\uff0c\u4ed6\u867d\u7136\u8d25\u4e86\uff0c\u5374\u4e0d\u662f\u5446\u5b50 \u6728\u771f\u4eba\u9053\uff1a\u201c\u65e2\u7136\u9646\u5c0f\u51e4\u8fd8\u662f\u9646\u5c0f\u51e4\uff0c\u8001\u9053\u58eb\u4e5f\u4f9d\u65e7\u8fd8\u662f\u8001\u9053\u58eb\uff0c\u6240\u4ee5\u6211\u4eec\u8fd8\u662f\u670b\u53cb\uff0c \u662f\u4e0d\u662f\uff1f\u201d \u4ed6\u867d\u7136\u5728\u7b11\uff0c\u76ee\u5149\u4e2d\u4e5f\u9732\u51fa\u4e86\u5c16\u9488\u822c\u7684\u950b\u8292\u3002 \u9646\u5c0f\u51e4\u5ffd\u7136\u89c9\u5f97\u6709\u80a1\u4e0d\u53ef\u6297\u62d2\u7684\u529b\u91cf\uff0c\u4ece\u4ed6\u624b\u4e0a\u4f20\u4e86\u8fc7\u6765\u3002 \u5c31\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5c0a\u8d35\u8363\u534e\u7684\u6b66\u5f53\u638c\u95e8\u4e5f\u4e0d\u5b58\u5728\u4e86\uff0c\u53c8\u5df2\u53d8\u6210\u4e86\u9634\u9e33\u9ad8\u50b2\uff0c\u96c4\u624d\u5927\u7565\u7684\u4e00 \u4ee3\u67ad\u96c4\u8001\u5200\u628a\u5b50\uff0c\u4eff\u4f5b\u6545\u610f\u8981\u544a\u8bc9\u9646\u5c0f\u51e4\u201c\u6211\u5c31\u7b97\u8ba9\u4f60\u77e5\u9053\u6211\u662f\u8c01\u53c8\u4f55\u59a8\uff1f\u4f60\u53c8\u80fd\u62ff\u6211\u600e\u4e48 \u6837\uff1f\u201d \u4ed6\u53cc\u624b\u6276\u5728\u9646\u5c0f\u51e4\u80a9\u8098\u95f4\uff0c\u4e0a\u6258\u4e4b\u52bf\u5ffd\u7136\u53d8\u6210\u4e86\u4e0b\u538b\u4e4b\u529b\u3002 \u8fd9\u4e00\u538b\u5f88\u53ef\u80fd\u9020\u6210\u4e24\u79cd\u7ed3\u679c\u2014\u53cc\u81c2\u7684\u9aa8\u5934\u88ab\u538b\u65ad\uff0c\u6216\u8005\u662f\u88ab\u538b\u5f97\u8dea\u4e0b\u53bb\u3002 \u9646\u5c0f\u51e4\u5b81\u53ef\u65ad\u4e00\u767e\u6839\u9aa8\u5934\uff0c\u4e5f\u4e0d\u4f1a\u5728\u8fd9\u4e2a\u4eba\u9762\u524d\u4e0b\u8dea\u7684\u3002 \u5e78\u597d\u4ed6\u7684\u9aa8\u5934\u4e5f\u6ca1\u6709\u65ad\uff0c\u4ed6\u7684\u4e24\u81c2\u4e0a\u4e5f\u65e9\u5df2\u8d2f\u6ce8\u4e86\u771f\u529b\u3002 \u4ee5\u529b\u6297\u529b\uff0c\u529b\u5f31\u8005\u8d25\uff0c\u8fd9\u5176\u95f4\u5df2\u7edd\u65e0\u53d6\u5de7\u9000\u8ba9\u7684\u4f59\u5730\u3002 \u5236\u654c\u53d6\u80dc\u7684\u6b66\u529f\u4e5f\u6709\u5f88\u591a\u79cd\uff0c\u6709\u7684\u4ee5\u201c\u6c14\u201d\u80dc\uff0c\u6709\u7684\u4ee5\u201c\u529b\u2019\u80dc\uff0c\u6709\u7684\u4ee5\u201c\u52bf\u201d\u80dc\uff0c\u6709 \u7684\u4ee5\u201c\u5de7\u201d\u80dc\uff0c\u9646\u5c0f\u51e4\u7684\u6b66\u529f\u673a\u53d8\u8df3\u8131\uff0c\u4e0d\u53ef\u6349\u6478\uff0c\u672c\u6765\u662f\u5c5e\u4e8e\u6700\u540e\u4e00\u79cd\u3002 \u53ef\u662f\u73b0\u5728\u4ed6\u7684\u771f\u529b\u5df2\u53d1\uff0c\u5c31\u6b63\u5982\u7bad\u5728\u5f26\u4e0a\uff0c\u4eba\u5728\u864e\u80cc\uff0c\u518d\u60f3\u64a4\u56de\uff0c\u5df2\u6765\u4e0d\u53ca\u4e86\u3002 \u56e0\u4e3a\u5bf9\u65b9\u7684\u529b\u91cf\u5b9e\u5728\u592a\u5f3a\uff0c\u4ed6\u7684\u771f\u529b\u4e00\u64a4\uff0c\u5c31\u96be\u514d\u8981\u88ab\u538b\u5f97\u7c89\u8eab\u788e\u9aa8\u3002 \u201c\u535c\u201d\u7684\u4e00\u54cd\uff0c\u4ed6\u7ad9\u7740\u7684\u77f3\u677f\u5df2\u88ab\u538b\u788e\uff0c\u8138\u4e0a\u4e5f\u5df2\u6c81\u51fa\u8c46\u5927\u7684\u6c57\u73e0\u3002 \u7ad9\u5728\u4ed6\u4eec\u9644\u8fd1\u7684\u4eba\uff0c\u8138\u8272\u5df2\u53d8\uff0c\u5374\u53ea\u6709\u773c\u7741\u7741\u7684\u770b\u7740\u3002 \u4e24\u4e2a\u4eba\u7684\u529b\u91cf\u5df2\u5982\u9488\u950b\u76f8\u5bf9\uff0c\u82e5\u6709\u7b2c\u4e09\u8005\u63d2\u5165\uff0c\u529b\u91cf\u53ea\u8981\u6709\u4e00\u70b9\u504f\u5dee\uff0c\u5c31\u53ef\u80fd\u5bb3\u4e86\u4ed6\u4eec \u5176\u4e2d\u4e00\u4e2a\u4eba\uff0c\u4e5f\u53ef\u80fd\u88ab\u4ed6\u4eec\u53cd\u6fc0\u7684\u529b\u91cf\u6467\u6bc1\u3002 \u8c01\u4e5f\u4e0d\u6562\u5192\u8fd9\u79cd\u9669\u3002 \u5176\u5b9e\u9646\u5c0f\u51e4\u4e5f\u4e0d\u5fc5\u5192\u8fd9\u79cd\u9669\u7684\uff0c\u5728\u6728\u771f\u4eba\u529b\u91cf\u5c06\u53d1\u672a\u53d1\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u4ed6\u5df2\u611f\u89c9\u5230\uff0c\u672c\u6765 \u8fd8\u6709\u673a\u4f1a\u4ece\u5bb9\u64a4\u9000\u3002 \u53ef\u662f\u4ed6\u5df2\u9000\u4e86\u4e00\u6b21\uff0c\u4ed6\u4e0d\u613f\u518d\u9000\u3002 \u73b0\u5728\u4ed6\u53ea\u89c9\u547c\u5438\u6e10\u91cd\uff0c\u5fc3\u8df3\u52a0\u5feb\uff0c\u751a\u81f3\u8fde\u773c\u73e0\u90fd\u4f3c\u5df2\u6e10\u6e10\u51f8\u51fa\u3002 \u552f\u4e00\u8ba9\u4ed6\u652f\u6301\u4e0b\u53bb\u7684\u529b\u91cf\u662f\uff0c\u4ed6\u770b\u5f97\u51fa\u6728\u9053\u4eba\u4e5f\u5f88\u4e0d\u597d\u53d7\u3002 \u8fd9\u2014\u6218\u65e0\u8bba\u662f\u8c01\u80dc\uff0c\u90fd\u5fc5\u987b\u4ed8\u51fa\u60e8\u75db\u7684\u4ee3\u4ef7\uff0c\u6728\u9053\u5165\u672c\u6765\u4e5f\u4e0d\u5fc5\u8fd9\u4e48\u505a\u7684\u3002 \u4e5f\u8bb8\u4ed6\u60f3\u4e0d\u5230\u9646\u5c0f\u51e4\u4f1a\u6709\u8fd9\u79cd\u5b81\u6298\u4e0d\u5c48\u7684\u52c7\u6c14\uff0c\u4e5f\u8bb8\u4ed6\u73b0\u5728\u5df2\u5f00\u59cb\u540e\u6094\u3002 \u5c31\u5728\u8fd9\u65f6\uff0c\u5927\u6bbf\u5916\u5ffd\u7136\u6709\u4e2a\u5e74\u8f7b\u7684\u9053\u4eba\u5306\u5306\u5954\u4eba\uff0c\u795e\u8272\u663e\u5f97\u5f88\u7126\u6025\uff0c\u82e5\u6ca1\u6709\u6781\u4e25\u91cd\u7684\u4e8b \u53d1\u751f\uff0c\u4ed6\u7edd\u4e0d\u6562\u8fd9\u4e48\u6837\u95ef\u5165\u5927\u6bbf\u3002 \u6728\u771f\u4eba\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u6ed1\u51fa\u4e24\u6b65\uff0c\u9646\u5c0f\u51e4\u81c2\u4e0a\u7684\u5343\u65a4\u91cd\u62c5\u7adf\u4f3c\u5ffd\u7136\u65e0\u5f71\u65e0\u8e2a\uff0c\u8fd9\u4f7f\u5f97\u4ed6\u6574 \u4e2a\u4eba\u90fd\u50cf\u662f\u8981\u98de\u4e86\u8d77\u6765\u3002 \u4ed6\u5b9e\u5728\u60f3\u4e0d\u5230\u4ed6\u7684\u5bf9\u624b\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u8fd8\u80fd\u4ece\u5bb9\u64a4\u56de\u771f\u529b\uff0c\u770b\u6765\u8fd9\u2014\u6218\u4ed6\u53c8\u8d25\u4e86\u3002 \u4ed6\u8fd8\u6ca1\u6709\u5b8c\u5168\u5598\u8fc7\u6c14\u6765\uff0c\u6728\u771f\u4eba\u5df2\u80fd\u5f00\u53e3\u8bf4\u8bdd\uff0c\u6b63\u5728\u95ee\u90a3\u5e74\u8f7b\u7684\u5f1f\u5b50\uff0f\u4ec0\u4e48\u4e8b\uff1f\u201d \u201c\u897f\u95e8\u5439\u96ea\u6765\u4e86\uff01\u201d \u201c\u8d35\u5ba2\u5149\u4e34\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u8bf7\u4e0a\u3002\u201d\u201c\u4ed6\u4e00\u5b9a\u8981\u5e26\u5251\u4e0a\u5c71\uff0c\u5e74\u8f7b\u9053\u4eba\u7684\u624b\u8fd8\u5728\u53d1\u6296\uff0c\u5f1f\u5b50 \u4eec\u65e0\u80fd\u8981\u4ed6\u89e3\u5251\uff0c\u7559\u5b88\u5728\u89e3\u6c60\u5ca9\u7684\u5e08\u5144\u4eec\uff0c\u5df2\u5168\u90fd\u4f24\u5728\u4ed6\u5251\u4e0b\u201d \u8fd9\u7684\u786e\u662f\u4ef6\u5f88\u4e25\u91cd\u7684\u4e8b\uff0c\u6570\u767e\u5e74\u6765\uff0c\u4ece\u6765\u6ca1\u6709\u4eba\u6562\u8f7b\u72af\u6b66\u5f53\u3002 \u201c\u4ed6\u7684\u4eba\u5728\u90a3\u91cc\uff1f\u201d \u201c\u8fd8\u5728\u89e3\u5251\u6c60\u7554\uff0c\u516b\u5e08\u53d4\u6b63\u5728\u60f3\u6cd5\u5b50\u7a33\u4f4f\u4ed6\u3002\u201d \u6728\u771f\u4eba\u7684\u624b\u5df2\u63e1\u4f4f\u5251\u67c4\u3002 \u4ed6\u7684\u624b\u524a\u7626\u3001\u5e72\u71e5\u3001\u7a33\u5b9a\uff0c\u624b\u6307\u957f\u800c\u6709\u529b\u3002 \u82e5\u662f\u63e1\u4f4f\u4e86\u4e00\u67c4\u5408\u624b\u7684\u5251\uff0c\u8fd9\u53ea\u624b\u662f\u4e0d\u662f\u6bd4\u897f\u95e8\u5439\u96ea\u66f4\u53ef\u6015\uff1f \u4ed6\u5ffd\u7136\u5927\u6b65\u8d70\u4e86\u51fa\u53bb\u3002 \u770b\u7740\u4ed6\u8d70\u51fa\u53bb\uff0c\u9646\u5c0f\u51e4\u5fc3\u91cc\u5ffd\u7136\u6709\u4e86\u79cd\u8bf4\u4e0d\u51fa\u7684\u6050\u60e7\u3002 \u53ea\u6709\u4ed6\u770b\u89c1\u8fc7\u8fd9\u4e2a\u4eba\u7684\u5251\uff0c\u5982\u679c\u4e16\u4e0a\u8fd8\u6709\u4e00\u4e2a\u4eba\u80fd\u51fb\u8d25\u897f\u95e8\u5439\u96ea\uff0c\u65e0\u7591\u5c31\u662f\u8fd9\u4e2a\u4eba\u3002 \u89e3\u5251\u6c60\u4e0b\u7684\u6c34\uff0c\u7acb\u523b\u5c31\u8981\u88ab\u9c9c\u8840\u67d3\u7ea2\u4e86\u3002 \u662f\u8c01\u7684\u8840\uff1f \u9646\u5c0f\u51e4\u6ca1\u6709\u628a\u63e1\u80fd\u786e\u5b9a\uff0c\u4ed6\u7edd\u4e0d\u80fd\u518d\u8ba9\u897f\u95e8\u5439\u96ea\u6b7b\u5728\u8fd9\u56e2 \u4ed6\u4e00\u5b9a\u8981\u60f3\u6cd5\u5b50\u62e6\u963b\u8fd9\u4e00\u6218\u3002 \u6728\u9053\u4eba\u5df2\u7a7f\u8fc7\u5e7f\u9614\u7684\u9662\u5b50\uff0c\u8d70\u51fa\u4e86\u9053\u89c2\u7684\u5927\u95e8\uff0c\u9646\u5c0f\u51e4\u7acb\u523b\u4e5f\u8d76\u51fa\u53bb\u3002 \u9053\u89c2\u5916\u4f73\u6728\u8471\u83c7\uff0c\u6625\u8349\u5df2\u6df1\uff0c\u8349\u6728\u4e1b\u4e2d\uff0c\u4eff\u4f5b\u6709\u53cc\u53d1\u4eae\u7684\u773c\u775b\u3002 \u9646\u5c0f\u51e4\u7684\u5fc3\u4e00\u8df3\uff0c\u4e00\u4e2a\u7a7f\u7740\u767d\u9ebb\u5b5d\u670d\u7684\u4eba\uff0c\u5ffd\u7136\u4ece\u8349\u6728\u4e1b\u4e2d\u7a7f\u51fa\u6765\uff0c\u624b\u91cc\u63d0\u7740\u67c4\u51fa\u4e86\u9798 \u7684\u5251\uff0c\u4e00\u5251\u5411\u6728\u771f\u4eba\u7684\u5fc3\u53e3\u523a\u4e86\u8fc7\u53bb\u3002 \u6728\u771f\u4eba\u7684\u624b\u6b63\u63e1\u7740\u5251\u67c4\uff0c\u672c\u6765\u5f88\u5bb9\u6613\u5c31\u53ef\u4ee5\u62d4\u5251\u51fb\u8d25\u8fd9\u523a\u5ba2\uff0c\u5f88\u5bb9\u6613\u5c31\u53ef\u4ee5\u8981\u5979\u6b7b\u5728\u5251 \u4e0b\u3002 \u4f46\u662f\u4e5f\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff1f\u4ed6\u7684\u5251\u7adf\u6ca1\u6709\u62d4\u51fa\u6765\u3002 \u770b\u89c1\u8fd9\u7a7f\u7740\u767d\u9ebb\u5b5d\u670d\u7684\u5973\u5b50\uff0c\u4ed6\u7adf\u4f3c\u5ffd\u7136\u88ab\u9707\u60ca\u3002 \u5c31\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u8fd9\u767d\u8863\u5973\u5b50\u7684\u5251\uff0c\u5df2\u6bd2\u86c7\u822c\u523a\u5165\u4e86\u4ed6\u7684\u5fc3\u3002 \u4ed6\u8fd8\u6ca1\u6709\u5012\u4e0b\u53bb\uff0c\u8fd8\u5728\u5403\u60ca\u7684\u770b\u7740\u5979\uff0c\u597d\u50cf\u8fd8\u4e0d\u76f8\u4fe1\u8fd9\u662f\u771f\u7684\u3002 \u4ed6\u8138\u4e0a\u7684\u8868\u60c5\u4e0d\u4ec5\u662f\u60ca\u8bb6\uff0c\u8fd8\u5e26\u7740\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u60b2\u54c0\u548c\u75db\u82e6\u3002 \u201c\u4f60\u2026\u2026\u4f60\u6740\u4e86\u6211\uff1f\u201d \u201c\u4f60\u6740\u4e86\u6211\u7236\u4eb2\uff0c\u6211\u5f53\u7136\u8981\u6740\u4f60\u3002\u201d \u201c\u4f60\u7236\u4eb2\uff1f\u201d \u201c\u6211\u7236\u4eb2\u5c31\u662f\u6b7b\u5728\u4f60\u5251\u4e0b\u7684\u8001\u5200\u628a\u5b50\u3002\u201d \u6728\u771f\u4eba\u7684\u8138\u7a81\u7136\u626d\u66f2\uff0c\u8fd9\u53e5\u8bdd\u5c31\u50cf\u662f\u4e00\u6839\u9488\uff0c\u53c8\u523a\u5728\u4ed6\u5fc3\u4e0a\uff0c\u751a\u81f3\u6bd4\u90a3\u81f4\u547d\u7684\u4e00\u5251\u8fd8\u950b \u5229\u3002 \u4ed6\u8138\u4e0a\u5ffd\u7136\u9732\u51fa\u79cd\u65e0\u6cd5\u5f62\u5bb9\u7684\u6050\u60e7\u3002 \u90a3\u7edd\u4e0d\u662f\u6b7b\u7684\u6050\u60e7\u3002 \u4ed6\u6050\u60e7\uff0c\u53ea\u56e0\u4e3a\u5929\u5730\u95f4\u6240\u6709\u4e0d\u53ef\u601d\u8bae\uff0c\u4e0d\u53ef\u89e3\u91ca\u7684\u4e8b\uff0c\u5728\u8fd9\u4e00\u77ac\u95f4\u5ffd\u7136\u5168\u90fd\u6709\u4e86\u7b54\u6848\uff0c \u6240\u6709\u4ed6\u672c\u6765\u7edd\u4e0d\u76f8\u4fe1\u7684\u4e8b\uff0c\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u90fd\u5df2\u4ee4\u4ed6\u4e0d\u80fd\u4e0d\u4fe1\u3002 \u4ed6\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u5583\u5583\u9053\uff1a\u201c\u5f88\u597d\uff0c\u5f88\u597d\u2026\u2026\u201d \u51fa\u7684\u56db\u4e2a\u5b57\u3002 \u7136\u540e\u4ed6\u5c31\u5012\u4e86\u4e0b\u53bb\u3002 \u9646\u5c0f\u51e4\u770b\u7740\u90a3\u67c4\u5251\u523a\u5165\u4ed6\u7684\u5fc3\u810f\uff0c\u4e5f\u770b\u7740\u4ed6\u5012\u4e0b\u53bb\uff0c\u53ea\u89c9\u5f97\u5168\u8eab\u51b0\u51b7\uff0c\u8138\u4e0a\u4e5f\u9732\u51fa\u79cd\u65e0 \u6cd5\u5f62\u5bb9\u7684\u6050\u60e7\u3002 \u5929\u7f51\u6062\u6062\uff0c\u758f\u800c\u4e0d\u6f0f\u3002 \u51a5\u51a5\u4e2d\u7adf\u4eff\u4f5b\u771f\u7684\u6709\u79cd\u795e\u79d8\u7684\u529b\u91cf\uff0c\u5728\u4e3b\u5bb0\u7740\u4eba\u7c7b\u7684\u547d\u8fd0\uff0c\u7edd\u6ca1\u6709\u4efb\u4f55\u4e00\u4e2a\u5e94\u8be5\u53d7\u60e9\u7f5a \u7684\u4eba\uff0c\u80fd\u9003\u8fc7\u201c\u5b83\u201d\u7684\u5236\u88c1\u3002 \u8fd9\u79cd\u529b\u91cf\u867d\u7136\u662f\u770b\u4e0d\u89c1\uff0c\u6478\u4e0d\u5230\u7684\uff0c\u4f46\u662f\u6bcf\u4e2a\u4eba\u90fd\u968f\u65f6\u53ef\u4ee5\u611f\u89c9\u5230\u4ed6\u7684\u5b58\u5728\u3002 \u6728\u9053\u4eba\u7684\u6050\u60e7\uff0c\u5c31\u56e0\u4e3a\u5df2\u7ecf\u611f\u89c9\u5230\u5b83\u7684\u5b58\u5728\u3002 \u73b0\u5728\u9646\u5c0f\u51e4\u4e5f\u5df2\u611f\u89c9\u5230\uff0c\u53ea\u89c9\u5f97\u6ee1\u5fc3\u656c\u754f\uff0c\u51e0\u4e4e\u5fcd\u4e0d\u4f4f\u8981\u8dea\u4e0b\u53bb\uff0c\u8dea\u5728\u8fd9\u9ed1\u6697\u7684\u7a79\u82cd \u4e0b\u3002 \u522b\u7684\u4eba\u4e5f\u90fd\u88ab\u9707\u60ca\uff0c\u8fc7\u4e86\u5f88\u4e45\u4e4b\u540e\uff0c\u624d\u6709\u6b66\u5f53\u5f1f\u5b50\u51b2\u8fc7\u53bb\u56f4\u4f4f\u90a3\u767d\u8863\u523a\u5ba2\u3002 \u5979\u7acb\u523b\u5927\u559d\uff1a\u201c\u4f60\u4eec\u9000\u4e0b\u53bb\uff0c\u6211\u81ea\u5df1\u505a\u7684\u4e8b\uff0c\u6211\u81ea\u5df1\u4f1a\u89e3\u51b3\u3002\u201d \u5979\u82cd\u767d\u7684\u8138\u5728\u591c\u8272\u4e2d\u770b\u6765\u663e\u5f97\u65e0\u6bd4\u7f8e\u4e3d\u5e84\u4e25\uff0c\u5c31\u50cf\u662f\u590d\u4ec7\u7684\u5973\u795e\uff0f\u6211\u53eb\u53f6\u96ea\uff0c\u6211\u5c31\u662f\u8001 \u5200\u628a\u5b50\u7684\u5973\u513f\uff0c\u82e5\u6709\u4eba\u8ba4\u4e3a\u6211\u4e0d\u8be5\u66ff\u7236\u4eb2\u62a5\u4ec7\u7684\uff0c\u5c3d\u7ba1\u8fc7\u6765\u6740\u4e86\u6211\u3002 \u5979\u5ffd\u7136\u6495\u5f00\u8863\u895f\uff0c\u9732\u51fa\u6676\u83b9\u6d01\u767d\u7684\u80f8\u819b\u3002 \u53ef\u662f\u6ca1\u6709\u4eba\u8fc7\u53bb\u52a8\u624b\u3002 \u6bcf\u4e2a\u4eba\u90fd\u4f3c\u5df2\u88ab\u5979\u90a3\u79cd\u795e\u5723\u5e84\u4e25\u7684\u7f8e\u4e3d\u6240\u9707\u6151\uff0c\u5c24\u5176\u662f\u9646\u5c0f\u51e4\u3002 \u53ea\u6709\u4ed6\u77e5\u9053\u5979\u771f\u6b63\u7684\u7236\u4eb2\u662f\u8c01\uff0c\u56e0\u4e3a\uff0c \u201c\u6728\u9053\u4eba\u5c31\u662f\u8001\u5200\u628a\u5b50\u3002\u201d \u4ed6\u4e0d\u80fd\u8bf4\u3001\u4e0d\u5fcd\u8bf4\u3001\u4e5f\u4e0d\u613f\u8bf4\u4f55\u51b5\uff0c\u4ed6\u8bf4\u51fa\u6765\u4e5f\u6ca1\u6709\u4eba\u76f8\u4fe1\u3002 \u8fd9\u7ed3\u679c\u672c\u662f\u6728\u9053\u4eba\u81ea\u5df1\u9020\u6210\u7684\uff0c\u73b0\u5728\u4ed6\u5df2\u81ea\u98df\u6076\u679c\uff0c\u4ed6\u7684\u8ba1\u5212\u867d\u5468\u5bc6\uff0c\u5374\u60f3\u4e0d\u5230\u8fd8\u6709\u66f4 \u5468\u5bc6\u7684\u5929\u7f51\u5728\u7b49\u7740\u4ed6\uff01\u201c\u6211\u672c\u6765\u5df2\u8be5\u6b7b\u5728\u6cbc\u6cfd\u91cc\uff0c\u53ef\u662f\u6211\u6ca1\u6709\u6b7b\u3002\u201d \u5979\u662f\u4e2a\u730e\u8c79\u7684\u5973\u4eba\uff0c\u5979\u8fdc\u6bd4\u4efb\u4f55\u4eba\u90fd\u80fd\u5fcd\u8010\u75db\u82e6\u548c\u5371\u96be\uff0c\u5979\u65e9\u5df2\u5b66\u4f1a\u7b49\u5f85\uff0c\u6240\u4ee5\u624d\u80fd\u7b49 \u5230\u6700\u597d\u7684\u673a\u4f1a\u51fa\u624b\uff01 \u201c\u6211\u6ca1\u6709\u6b7b\uff0c\u53ea\u56e0\u4e3a\u8001\u5929\u8981\u7559\u7740\u6211\u6765\u590d\u4ec7\u3002\u201d\u5979\u7684\u58f0\u97f3\u51b7\u9759\u800c\u9547\u5b9a\uff0f\u73b0\u5728\u6211\u5fc3\u613f\u5df2\u4e86\uff0c \u6211\u4e0d\u4f1a\u7b49\u4f60\u4eec\u6765\u52a8\u624b\u7684\uff0c\u56e0\u4e3a \u76f4\u5230\u73b0\u5728\uff0c\u5979\u624d\u53bb\u770b\u9646\u5c0f\u51e4\uff0c\u773c\u775b\u91cc\u5e26\u7740\u79cd\u8c01\u90fd\u65e0\u6cd5\u89e3\u91ca\u7684\u8868\u60c5\uff0c\u65e2\u4e0d\u60b2\u4f24\uff0c\u4e5f\u6ca1\u6709\u75db \u82e6\uff0c\u53ef\u662f\u65e0\u8bba\u8c01\u770b\u89c1\u5979\u8fd9\u79cd\u8868\u60c5\uff0c\u5fc3\u90fd\u4f1a\u788e\u7684\u3002 \u9646\u5c0f\u51e4\u7684\u5fc3\u5df2\u788e\u4e86\u3002 \u5979\u5374\u5df2\u6602\u8d77\u5934\uff0c\u80fd\u518d\u770b\u4ed6\u4e00\u773c\uff0c\u4eff\u4f5b\u5c31\u5df2\u662f\u5979\u6700\u540e\u7684\u5fc3\u613f\u3002 \u73b0\u5728\u5979\u5fc3\u613f\u5df2\u4e86\uff0c\u5979\u7edd\u4e0d\u4f1a\u7b49\u522b\u4eba\u52a8\u624b\u3002 \u201c\u56e0\u4e3a\u6211\u8fd9\u4e00\u751f\u4e2d\uff0c\u53ea\u6709\u2014\u4e2a\u7537\u4eba\uff0c\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u8c01\u4e5f\u4e0d\u80fd\u78b0\u6211\uff01\u201d \u66f2\u7ec8\u4eba\u6563 \u5e94\u8be5\u6d41\u7684\u8840\u90fd\u5df2\u6d41\u5c3d\uff0c\u89e3\u5251\u5ca9\u4e0b\u7684\u6c60\u6c34\u4f9d\u65e7\u6e05\u6f88\uff0c\u6b66\u5f53\u5c71\u4e5f\u4f9d\u65e7\u5c79\u7acb\uff0c\u4f9d\u65e7\u662f\u4eba\u4eba\u4ef0\u6155 \u7684\u9053\u6559\u540d\u5c71\uff0c\u6b66\u6797\u5723\u5730\u3002 \u6539\u53d8\u7684\u53ea\u6709\u4eba\u3002\u7531\u751f\u800c\u6b7b\uff0c\u7531\u65b0\u800c\u8001\uff0c\u8fd9\u671f\u95f4\u8f6c\u53d8\u7684\u8fc7\u7a0b\uff0c\u6709\u65f6\u7adf\u6765\u5f97\u5982\u6b64\u7a81\u7136\u3002 \u6240\u6709\u7684\u60c5\u7231\u548c\u4ec7\u6068\uff0c\u6240\u6709\u7684\u6069\u6028\u548c\u79d8\u5bc6\uff0c\u73b0\u5728\u90fd\u5df2\u968f\u7740\u8fd9\u7a81\u6765\u7684\u8f6c\u53d8\u800c\u6c38\u8fdc\u57cb\u846c\uff0c\u57cb\u846c \u5728\u9646\u5c0f\u51e4\u5fc3\u5e95\u3002 \u73b0\u5728\u4ed6\u53ea\u60f3\u627e\u4e2a\u6ca1\u6709\u4eba\u7684\u5730\u65b9\uff0c\u9759\u9759\u7684\u8fc7\u4e00\u6bb5\u65e5\u5b50\uff0c\u8ba9\u90a3\u4e9b\u5df2\u7ecf\u57cb\u846c\u4e86\u7684\uff0c\u57cb\u5f97\u66f4\u6df1\u3002 \u4ed6\u4e58\u7740\u957f\u591c\u672a\u5c3d\u65f6\u4e0b\u5c71\uff0c\u5374\u4e0d\u77e5\u5c71\u4e0b\u8fd8\u6709\u4e2a\u4eba\u5728\u7b49\u7740\u4ed6\u3002 \u4e00\u4e2a\u4eba\u72ec\u7acb\u5728\u89e3\u5251\u5ca9\u4e0b\uff0c\u767d\u8863\u5982\u96ea\u3002 \u9646\u5c0f\u51e4\u6162\u6162\u7684\u8d70\u8fc7\u53bb\uff0f\u73b0\u5728\u5df2\u5230\u4e86\u66f2\u7ec8\u4eba\u6563\u7684\u65f6\u5019\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u8d70\uff1f\u201d\u897f\u95e8\u5439\u96ea\u9053\uff1a \u201c\u4eba\u867d\u5df2\u6563\uff0c\u66f2\u72b9\u672a\u7ec8\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u8fd8\u51c6\u5907\u5439\u4e00\u66f2\u4ec0\u4e48\uff1f\u201d \u897f\u95e8\u5439\u96ea\u9053\uff1a\u201c\u6211\u8ffd\u8e2a\u516b\u5343\u91cc\uff0c\u53ea\u4e3a\u4e86\u6740\u4e00\u4e2a\u4eba\uff0c\u73b0\u5728\u8fd9\u4e2a\u4eba\u8fd8\u6ca1\u6709\u6b7b\uff0c\u6211\u8fd8\u51c6\u5907\u5439\u4e00 \u66f2\u4e3a\u4ed6\u9001\u4e27\u7684\u6b7b\u8c03\uff0c\u7528\u6211\u7684\u5251\u3002\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8fd9\u4e2a\u4eba\u5c31\u662f\u6211\uff1f\u201d \u897f\u95e8\u5439\u96ea\u9053\uff1a\u201c\u662f\u4f60\uff01\u201d \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u4f60\u96be\u9053\u5fd8\u4e86\u4f60\u5e76\u4e0d\u662f\u771f\u7684\u8981\u6740\u6211\uff1f\u201d \u897f\u95e8\u5439\u96ea\u51b7\u51b7\u9053\uff1a\u201c\u6211\u53ea\u77e5\u9053\u6c5f\u6e56\u4e2d\u4eba\u4e00\u5411\u4e0d\u5206\u771f\u5047\uff0c\u4f60\u82e5\u6d3b\u7740\uff0c\u5c31\u662f\u6211\u7684\u803b\u8fb1\u3002\u201d \u9646\u5c0f\u51e4\u770b\u7740\u4ed6\uff0c\u5ffd\u7136\u7b11\u4e86\uff0f\u4f60\u662f\u4e0d\u662f\u60f3\u903c\u6211\u51fa\u624b\uff0c\u8bd5\u8bd5\u6211\u7a76\u7adf\u80fd\u4e0d\u80fd\u7834\u5f97\u4e86\u4f60\u7684\u5929\u4e0b\u65e0 \u53cc\u7684\u51fa\u624b\u4e00\u5251\uff1f\u201d \u897f\u95e8\u5439\u96ea\u5e76\u4e0d\u5426\u8ba4\u3002 \u9646\u5c0f\u51e4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5f88\u60f3\u77e5\u9053\u8fd9\u95ee\u9898\u7684\u7b54\u6848\uff0c\u6211\u4e5f\u77e5\u9053\u8fd9\u662f\u4f60\u7684\u597d\u673a\u4f1a\uff0c\u53ea\u53ef\u60dc\u4f60\u8fd8 \u662f\u8bd5\u4e0d\u51fa\u7684\u3002\u201d \u897f\u95e8\u5439\u96ea\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u9646\u5c0f\u51e4\u7684\u7b11\u5bb9\u75b2\u5026\u800c\u6194\u60b4\uff0c\u6de1\u6de1\u9053\uff1a\u201c\u53ea\u8981\u4f60\u7684\u5251\u51fa\u9798\uff0c\u4f60\u5c31\u77e5\u9053\u4e3a\u4ec0\u4e48\u4e86\uff0c\u73b0\u5728\u53c8\u4f55 \u5fc5\u95ee\uff1f\u201d \u96be\u9053\u4ed6\u5df2\u51c6\u5907\u62b5\u6297\u95ea\u907f\uff1f\u96be\u9053\u4ed6\u771f\u7684\u5df2\u5c06\u751f\u6b7b\u8363\u8fb1\u770b\u5f97\u6bd4\u89e3\u6c60\u5251\u4e0b\u7684\u4e00\u6cd3\u6e05\u6c34\u8fd8\u6de1\uff1f \u897f\u95e8\u5439\u96ea\u76ef\u7740\u4ed6\u770b\u4e86\u5f88\u4e45\uff0c\u6c60\u7554\u5df2\u6709\u96fe\u5347\u8d77\uff0c\u4ed6\u5ffd\u7136\u8f6c\u8eab\uff0c\u8d70\u5165\u96fe\u91cc\u3002 \u9646\u5c0f\u51e4\u5927\u58f0\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u51fa\u624b\uff1f\u201d \u897f\u95e8\u5439\u96ea\u5934\u4e5f\u4e0d\u56de\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u56e0\u4e3a\u4f60\u7684\u5fc3\u5df2\u7ecf\u6b7b\u4e86\uff0c\u4f60\u5df2\u7ecf\u662f\u4e2a\u6b7b\u4eba\u3002\u201d \u201c\u6211\u7684\u5fc3\u662f\u4e0d\u662f\u771f\u7684\u5df2\u6b7b\uff1f\u201d\u9646\u5c0f\u51e4\u5728\u95ee\u81ea\u5df1\uff0f\u6211\u662f\u4e0d\u662f\u771f\u7684\u5df2\u50cf\u6b7b\u4eba\u822c\u6beb\u65e0\u4f5c\u4e3a\uff1f\u201d \u8fd9\u95ee\u9898\u4e5f\u53ea\u6709\u4ed6\u81ea\u5df1\u77e5\u9053\u7b54\u6848\u3002 \u6668\u96fe\u51c4\u8ff7\uff0c\u4e1c\u65b9\u5374\u5df2\u6709\u4e86\u5149\u660e\uff0c\u4ed6\u5ffd\u7136\u633a\u8d77\u80f8\u819b\uff0c\u5927\u6b65\u8d70\u5411\u5149\u660e\u3002"}),showOnHomepage:!0}]}}]);